"use strict";(self.webpackChunkwebsite=self.webpackChunkwebsite||[]).push([[40780],{5953:(e,n,s)=>{s.r(n),s.d(n,{default:()=>a});var t=s(74848);const a=[{name:"\u701a\u6d77\u96c4\u98ce",content:(0,t.jsx)(t.Fragment,{children:"\u7b2c\u4e00\u56de\u3000\u5355\u9a91\u4e00\u5251\u6c5f\u6e56\u5ba2\u3000\u4e07\u91cc\u897f\u98ce\u701a\u6d77\u6c99 \u3000\u3000\u7d2b\u585e\u9ec4\u4e91\u671b\u773c\u906e\uff0c\u5f81\u9791\u672a\u89e3\u53c8\u5929\u6daf\uff0c\u53ef\u582a\u7eff\u9b13\u6597\u971c\u534e\u3002 \u3000\u3000\u5269\u6c34\u6b8b\u5c71\u601d\u6545\u56fd\uff0c\u8352\u6c99\u701a\u6d77\u4e4f\u4ed9\u69ce\uff0c\u8c6a\u60c5\u72b9\u5728\u83ab\u5174\u55df\u3002 \u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u2014\u2014\u8c03\u5bc4\u6d63\u6eaa\u6c99 \u3000\u3000\u6f20\u6f20\u9ec4\u6c99\uff0c\u9a84\u9633\u4f3c\u706b\u3002\u8fd9\u6b63\u662f\u8499\u53e4\u5e93\u91cc\u6208\u58c1\u4e0a\u6700\u708e\u70ed\u7684\u5b63\u8282\uff0c\u4e03\u6708\u91cc\u7684\u67d0\u4e00\u5929\u7684\u4e2d\u5348\u65f6 \u5019\u3002 \u3000\u3000\u5927\u5730\u90fd\u597d\u4f3c\u5598\u4e0d\u8fc7\u6c14\u6765\uff0c\u5728\u8fd9\u4e07\u91cc\u65e0\u57a0\u7684\u5927\u6c99\u6f20\u4e0a\uff0c\u4e00\u5207\u90fd\u597d\u4f3c\u9759\u6b62\u4e86\uff0c\u770b\u4e0d\u5230\u4ec0\u4e48\u6709 \u751f\u547d\u7684\u4e1c\u897f\uff0c\u53ea\u5076\u5c14\u53ef\u4ee5\u770b\u89c1\u6c99\u77f3\u5806\u4e2d\u7efd\u51fa\u7684\u51e0\u6839\u91ce\u8349\u3002\u53ef\u662f\u5c31\u8fde\u8fd9\u6c99\u6f20\u4e2d\u751f\u547d\u529b\u6700\u575a\u97e7\u7684 \u91ce\u8349\uff0c\u4e5f\u5df2\u7ecf\u6854\u840e\u7126\u9ec4\uff0c\u7eb5\u6709\u98ce\u6765\uff0c\u5b83\u4e5f\u4e0d\u4f1a\u8fce\u98ce\u8d77\u821e\u4e86\u3002 \u3000\u3000\u6ca1\u6709\u9759\u6b62\u7684\u53ea\u6709\u6d41\u6c99\u3002\u4e00\u9635\u72c2\u98ce\u8fc7\u540e\uff0c\u6d41\u6c99\u56db\u6563\uff0c\u4fe8\u82e5\u60ca\u6d9b\u3002\u6c99\u8ddf\u7740\u98ce\u79fb\u8d70\uff0c\u5c31\u50cf\u6c34\u5728 \u5730\u9762\u4e0a\u6d41\u8fc7\u4e00\u6837\u3002\u98ce\u6c99\u8d77\u5904\uff0c\u9633\u5149\u4e5f\u67d3\u6210\u4e86\u4e00\u7247\u9ec4\u3002\u9ec4\u6c99\u6f2b\u5929\u7684\u8ff7\u79bb\u70df\u96fe\u4e4b\u4e2d\uff0c\u7565\u7565\u5e26\u7740\u4e00 \u4e9b\u6de1\u7d2b\u7684\u8f7b\u84dd\u8272\uff0c\u4f7f\u4eba\u8fdc\u8fdc\u671b\u53bb\uff0c\u603b\u597d\u50cf\u90a3\u9065\u8fdc\u7684\u5730\u65b9\u662f\u4e00\u4e2a\u6d69\u701a\u7684\u7f8e\u4e3d\u7684\u6d77\u6d0b\u4e00\u6837\u3002\u53ef\u662f \u4efb\u662f\u773c\u775b\u518d\u597d\u7684\u4eba\uff0c\u4e5f\u770b\u4e0d\u6e05\u695a\u54ea\u513f\u662f\u5929\u5730\u76f8\u63a5\u7684\u5730\u65b9\uff01 \u3000\u3000\u8fd9\u771f\u662f\u5929\u5730\u95f4\u7f55\u6709\u7684\u5947\u89c2\uff0c\u53ea\u6709\u5728\u5927\u6208\u58c1\u624d\u80fd\u89c1\u7740\u7684\u5947\u666f\u3002 \u3000\u3000\u5728\u8fd9\u4e07\u91cc\u65e0\u57a0\u7684\u5927\u6c99\u6f20\u4e0a\uff0c\u5f53\u771f\u662f\u4e00\u5207\u90fd\u9759\u6b62\u4e86\u5417\uff1f\u5728\u8fd9\u4e03\u6708\u91cc\u7684\u4e2d\u5348\u65f6\u5019\u3002 \u3000\u3000\u4e0d\uff0c\u8fd9\u53ea\u662f\u5f80\u5e38\u7684\u60c5\u5f62\uff0c\u4f46\u4eca\u5929\u5374\u6709\u4f8b\u5916\u3002 \u3000\u3000\u9664\u4e86\u6d41\u6c99\u4e4b\u5916\uff0c\u8fd8\u6709\u4e00\u4e2a\u4eba\u3002\u4ed6\u662f\u4e00\u4e2a\u7ea6\u83ab\u4e8c\u5341\u4e09\u56db\u5c81\u7684\u7c97\u8c6a\u5c11\u5e74\uff0c\u9a91\u7740\u4e00\u5339\u9a6c\uff0c\u6b63\u5728 \u5e93\u91cc\u6208\u58c1\u4e0a\u5192\u7740\u98ce\u6c99\u524d\u884c\u3002 \u3000\u3000\u8fd9\u5c11\u5e74\u4ece\u6765\u6ca1\u6709\u8fc7\u5728\u6c99\u6f20\u65c5\u884c\u7684\u7ecf\u9a8c\uff0c\u6b64\u9645\u4ed6\u6b63\u9707\u60ca\u4e8e\u773c\u524d\u7684\u5947\u666f\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u524d\u4eba \u5f62\u5bb9\u2018\u701a\u6d77\u2019\uff08\u6c99\u6f20\uff09\u7684\u6d41\u6c99\u8bf4\u662f\uff1a\u2018\u79ef\u6cb3\u6210\u65f1\uff0c\u72b6\u5982\u60ca\u6d9b\u3002\u9047\u98ce\u5219\u6d41\uff0c\u4e4d\u805a\u4e4d\u6563\u3002\u2019\u5f62\u5bb9 \u5f97\u771f\u662f\u4e00\u70b9\u4e0d\u9519\u3002\u201d\u8fd9\u5c11\u5e74\u4eba\u662f\u4ece\u4e2d\u539f\u6765\u7684\uff0c\u4ed6\u66fe\u7ecf\u4e3a\u4e86\u8fd9\u6b21\u6df1\u5165\u6f20\u5317\u7684\u4e07\u91cc\u58ee\u6e38\u505a\u8fc7\u4e00\u4e9b \u51c6\u5907\u529f\u592b\uff0c\u8bfb\u8fc7\u4e00\u4e9b\u6709\u5173\u6c99\u6f20\u7684\u6e38\u8bb0\uff0c\u5b66\u8fc7\u8499\u53e4\u7684\u8bed\u8a00\u3002\u770b\u8fc7\u8bb8\u591a\u6709\u5173\u8499\u53e4\u5404\u4e2a\u90e8\u843d\u7684\u98ce\u571f \u4eba\u60c5\u7684\u8bb0\u8ff0\u3002\u4f46\u5982\u4eca\u8eab\u5386\u5176\u5883\uff0c\u4ed6\u53ea\u89c9\u5f97\u4e66\u4e0a\u6240\u63cf\u5199\u7684\u8fdc\u4e0d\u53ca\u4ed6\u6240\u89c1\u5230\u7684\u5341\u4e00\uff0c\u662f\u5982\u6b64\u7684\u96c4 \u5947\u800c\u53c8\u5982\u6b64\u7684\u4ee4\u4eba\u6035\u76ee\u60ca\u5fc3\uff01 \u3000\u3000\u8fd9\u5c11\u5e74\u53ea\u662f\u9707\u60ca\u4e8e\u773c\u524d\u7684\u5947\u666f\uff0c\u5374\u65e0\u5fc3\u6b23\u8d4f\u8fd9\u773c\u524d\u7684\u5947\u666f\uff0c\u5728\u8fd9\u6837\u708e\u70ed\u7684\u9633\u5149\u5e95\u4e0b\uff0c\u4ed6 \u7684\u6574\u4e2a\u8eab\u4f53\u5c31\u50cf\u4e00\u5757\u5757\u8981\u6eb6\u5316\u7684\u7f8a\u8102\uff0c\u4ed6\u9a91\u7684\u4e00\u5339\u5343\u4e2d\u9009\u4e00\u7684\u56fd\u5916\u826f\u9a79\uff0c\u4e5f\u5728\u53e3\u5410\u767d\u6cab\uff0c\u51e0 \u4e4e\u8d70\u4e0d\u52a8\u4e86\u3002\u800c\u4e14\u66f4\u8981\u547d\u7684\u662f\uff0c\u4ed6\u6240\u5e26\u7684\u5e72\u7cae\u548c\u98df\u6c34\u4e5f\u5feb\u8981\u7528\u5b8c\u4e86\u3002 \u3000\u3000\u8fd9\u5c11\u5e74\u5728\u9ec4\u6c99\u6f2b\u5929\u4e4b\u4e2d\uff0c\u5438\u4e86\u4e00\u53e3\u70ed\u98ce\uff0c\u53ea\u89c9\u8eab\u4f53\u5c31\u4f3c\u8981\u7740\u706b\u711a\u70e7\u4f3c\u7684\uff0c\u5341\u5206\u96be\u53d7\u3002\u4ed6 \u60f3\u559d\u4e00\u53e3\u6c34\uff0c\u4e00\u770b\u6c34\u58f6\u91cc\u7684\u6c34\uff0c\u5df2\u662f\u53ea\u5269\u4e0b\u8584\u8584\u7684\u4e00\u5708\u4e86\u3002\u8fd9\u4e2a\u5927\u6c99\u6f20\u4e0d\u77e5\u4f55\u65f6\u65b9\u80fd\u8d70\u51fa\uff0c \u4ed6\u82e6\u7b11\u4e00\u58f0\uff0c\u53ea\u597d\u53c8\u628a\u58f6\u76d6\u76d6\u4e0a\u3002 \u3000\u3000\u8fd9\u5c11\u5e74\u82e6\u7b11\u4e00\u58f0\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4eba\u751f\u9645\u9047\u4e4b\u5947\uff0c\u5f80\u5f80\u51fa\u4eba\u610f\u5916\u3002\u6211\u4ee5\u4e3a\u662f\u8981\u5230\u6c5f\u5357\u53bb \u7684\uff0c\u8c01\u77e5\u5374\u5230\u4e86\u6f20\u5317\u6765\uff01\u201d \u3000\u3000\u539f\u6765\u8fd9\u5c11\u5e74\u59d3\u674e\u540d\u601d\u5357\uff0c\u672c\u662f\u5c71\u4e1c\u6b66\u57ce\u4eba\u6c0f\uff0c\u8fd9\u6b21\u4ed6\u5230\u8499\u53e4\u6765\uff0c\u4e43\u662f\u5949\u4e86\u6bcd\u547d\u6765\u5bfb\u8bbf\u4ed6 \u7236\u4eb2\u7684\u3002 \u3000\u3000\u5c71\u4e1c\u6b66\u57ce\u65e9\u5df2\u6ca6\u9677\u5728\u91d1\u56fd\u7684\u7edf\u6cbb\u4e4b\u4e0b\u767e\u6709\u4f59\u5e74\uff0c\u5357\u5b8b\u504f\u5b89\u6c5f\u5357\u4e5f\u5dee\u4e0d\u591a\u6709\u4e00\u767e\u5e74\u4e86\u3002\u674e \u601d\u5357\u672c\u6765\u662f\u5c06\u95e8\u4e4b\u540e\uff0c\u4ed6\u7684\u66fe\u7956\u66fe\u5728\u97e9\u4e16\u5fe0\u624b\u4e0b\u5f53\u8fc7\u4e00\u4e2a\u4e2d\u7ea7\u519b\u5b98\uff0c\u5b8b\u5ba4\u5357\u8fc1\u4e4b\u9645\uff0c\u6ca1\u6709\u968f \u884c\uff0c\u4ece\u6b64\u4fbf\u4ee5\u52a1\u519c\u4e3a\u751f\u3002\u5230\u4e86\u4ed6\u7684\u7236\u4eb2\u674e\u5e0c\u6d69\u8fd9\u4e00\u4ee3\uff0c\u5bb6\u9053\u4e2d\u843d\uff0c\u548c\u4e00\u822c\u519c\u6237\uff0c\u5df2\u662f\u6ca1\u6709\u591a \u5927\u5206\u522b\u3002\u674e\u5bb6\u7684\u5bb6\u4f20\u6b66\u827a\uff0c\u4e5f\u56e0\u6570\u4ee3\u52a1\u519c\uff0c\u9010\u6e10\u5931\u4f20\uff0c\u4e00\u4ee3\u4e0d\u5982\u4e00\u4ee3\uff0c\u5230\u4e86\u674e\u5e0c\u6d69\uff0c\u53ea\u662f\u4f1a \u4e00\u4e9b\u5bfb\u5e38\u7684\u5f13\u9a6c\uff0c\u8c08\u4e0d\u5230\u6709\u4ec0\u4e48\u6b66\u529f\u4e86\u3002\u6240\u5e78\u4ed6\u77e5\u4e66\u8bc6\u5b57\uff0c\u5728\u4e61\u4e0b\u5f00\u4e86\u4e00\u95f4\u79c1\u587e\uff0c\u52a1\u519c\u800c\u517c \u6559\u5b66\uff0c\u52c9\u5f3a\u4e5f\u8fd8\u53ef\u4ee5\u6df7\u6df7\u65e5\u5b50\u3002 \u3000\u3000\u91d1\u7ae0\u5b8b\u6cf0\u548c\u516d\u5e74\uff08\u5b8b\u5b81\u5b97\u5f00\u79a7\u4e8c\u5e74\uff0c\u516c\u5143\u4e00\u4e8c0\u516d\u5e74\uff09\uff0c\u94c1\u6728\u771f\u7edf\u4e00\u8499\u53e4\uff0c\u53d7\u5404\u90e8\u843d\u63a8 \u6234\u4e3a\u201c\u6210\u5409\u601d\u6c57\u201d\uff0c\u5174\u5175\u4f10\u91d1\uff0c\u674e\u5e0c\u6d69\u88ab\u8499\u53e4\u5175\u63b3\u53bb\u4f5c\u592b\u5b50\u3002\u8fd9\u6b21\u6218\u4e89\uff0c\u56e0\u8499\u53e4\u6709\u4e8b\u4e8e\u897f\u590f \u800c\u79fb\u5175\u897f\u5411\uff0c\u5168\u56fd\u5f97\u4ee5\u82df\u5b89\uff0c\u4f46\u91d1\u56fd\u7684\u6218\u4e89\u7ed3\u675f\u4e86\uff0c\u674e\u5e0c\u6d69\u5374\u6ca1\u6709\u5f97\u5230\u91ca\u653e\uff0c\u63a8\u60f3\u53ef\u80fd\u662f\u8499 \u65b9\u7f3a\u4e4f\u4eba\u529b\u628a\u6c11\u592b\u90fd\u5e26\u56de\u53bb\u4e86\u3002 \u3000\u3000\u674e\u5e0c\u6d69\u88ab\u63b3\u4e4b\u65f6\uff0c\u674e\u601d\u5357\u53ea\u6709\u4e09\u5c81\uff0c\u4ed6\u7684\u8fd9\u4e2a\u540d\u5b57\uff0c\u662f\u4ed6\u7236\u4eb2\u53d6\u7684\u3002\u5b8b\u5ba4\u5357\u8fc1\uff0c\u4e2d\u539f\u7236 \u8001\uff0c\u9065\u76fc\u5f7c\u65d7\uff0c\u767e\u5e74\u6765\u4ecd\u662f\u5728\u5f02\u65cf\u7edf\u6cbb\u4e4b\u4e0b\uff0c\u674e\u5e0c\u6d69\u601d\u5ff5\u6545\u56fd\uff0c\u662f\u4ee5\u628a\u513f\u5b50\u540d\u4e3a\u201c\u601d\u5357\u201d\u3002 \u3000\u3000\u4e03\u5c81\u90a3\u5e74\uff0c\u674e\u601d\u5357\u6709\u4e86\u4e00\u4e2a\u5947\u9047\u3002\u4ed6\u7236\u4eb2\u7ae5\u5e74\u65f6\u4ee3\u7684\u4e00\u4e2a\u597d\u53cb\u5728\u5c11\u6797\u5bfa\u5b66\u6280\uff0c\u6b64\u65f6\u5df2\u7ecf \u6210\u4e3a\u4e00\u4e2a\u540d\u6ee1\u6c5f\u6e56\u7684\u5927\u4fa0\uff0c\u56de\u5230\u6545\u4e61\uff0c\u8bbf\u5bfb\u65e7\u53cb\uff0c\u5f97\u77e5\u674e\u5e0c\u6d69\u7684\u4e0d\u5e78\u906d\u9047\uff0c\u4e0d\u80dc\u53f9\u606f\u3002\u4ed6\u89c1 \u674e\u601d\u5357\u806a\u660e\u4f36\u4fd0\uff0c\u9042\u6536\u4e86\u8fd9\u4e2a\u6545\u4eba\u4e4b\u5b50\u505a\u4e86\u4ed6\u7684\u7b2c\u4e8c\u4e2a\u5f1f\u5b50\u3002 \u3000\u3000\u5306\u5306\u5341\u8f7d\u4e4b\u540e\uff0c\u674e\u601d\u5357\u5df2\u662f\u5b66\u6210\u51fa\u5e08\uff0c\u4ed6\u8bb0\u7740\u7236\u4eb2\u547d\u540d\u4e4b\u610f\uff0c\u51c6\u5907\u5f85\u6bcd\u4eb2\u767e\u5e74\u4e4b\u540e\uff0c\u4fbf \u5373\u6295\u5954\u6c5f\u5357\u3002\u5c45\u5bb6\u6570\u5e74\u4e4b\u4e2d\uff0c\u4ed6\u4e0e\u6297\u91d1\u5fd7\u58eb\u4e5f\u5e38\u6709\u6765\u5f80\u3002 \u3000\u3000\u5230\u4e86\u4eca\u5e74\u6625\u5929\uff0c\u6709\u4e00\u4e2a\u5f53\u5e74\u4e0e\u4ed6\u7236\u4eb2\u540c\u88ab\u4fd8\u5f80\u8499\u53e4\u7684\u6c11\u592b\u9003\u4e86\u56de\u6765\uff0c\u636e\u8bf4\u4ed6\u5728\u4e03\u516b\u5e74\u524d \u89c1\u8fc7\u4ed6\u7684\u7236\u4eb2\uff0c\u540e\u6765\u5c31\u4e0d\u77e5\u6d88\u606f\u4e86\u3002 \u3000\u3000\u674e\u601d\u5357\u7684\u6bcd\u4eb2\u5e74\u7eaa\u5df2\u8001\uff0c\u65e5\u5915\u601d\u5ff5\u4e08\u592b\uff0c\u56e0\u6b64\u5c31\u8981\u674e\u601d\u5357\u5230\u8499\u53e4\u5bfb\u7236\u3002\u5979\u600e\u60f3\u5f97\u5230\u674e\u601d \u5357\u5c1a\u672a\u5f97\u77e5\u7236\u4eb2\u7684\u4e0b\u843d\uff0c\u5c31\u5df2\u88ab\u56f0\u5728\u8fd9\u6c99\u6f20\u4e4b\u4e2d\u3002 \u3000\u3000\u674e\u601d\u5357\u54ac\u4e86\u54ac\u7259\uff0c\u54bd\u4e86\u54bd\u53e3\u6c34\uff0c\u7a0d\u7a0d\u6da6\u6e7f\u90a3\u5feb\u8981\u5192\u70df\u7684\u5589\u5499\uff0c\u6781\u529b\u5fcd\u53d7\u7740\u53e3\u6e34\u7684\u7126\u6e34\uff0c \u6b64\u91cc\u60f3\u9053\uff1a\u201c\u53ea\u8981\u6211\u8fd8\u6709\u4e00\u53e3\u6c14\u5728\uff0c\u6211\u5c31\u5f97\u7ee7\u7eed\u524d\u884c\uff01\u201d \u3000\u3000\u597d\u5728\u8fd9\u65f6\u72c2\u98ce\u5df2\u8fc7\uff0c\u6d41\u6c99\u6e10\u6e10\u9759\u6b62\uff0c\u867d\u7136\u4ecd\u662f\u9a84\u9633\u4f3c\u706b\uff0c\u5927\u5730\u5219\u5df2\u6062\u590d\u6674\u660e\uff0c\u547c\u5438\u4e5f\u6ca1 \u6709\u90a3\u4e48\u8270\u96be\u4e86\u3002 \u3000\u3000\u674e\u601d\u5357\u7c07\u9a6c\u7f13\u7f13\u800c\u884c\uff0c\u53ea\u76fc\u5728\u8fd9\u6c99\u6f20\u4e0a\u51fa\u73b0\u5947\u8ff9\uff0c\u53d1\u73b0\u7eff\u6d32\u3002\u4e0d\u6599\u7eff\u6d32\u672a\u66fe\u53d1\u73b0\uff0c\u5374\u5148 \u53d1\u73b0\u4e86\u4e00\u5806\u767d\u9aa8\u3002 \u3000\u3000\u90a3\u662f\u5728\u72c2\u98ce\u201c\u642c\u5f00\u201d\u4e86\u4e00\u4e2a\u6c99\u4e18\u4e4b\u540e\uff0c\u6240\u66b4\u9732\u51fa\u6765\u7684\u4e00\u5806\u767d\u9aa8\u3002\u56e0\u4e3a\u5b83\u662f\u57cb\u5728\u6c99\u4e18\u4e0b\u9762 \u7684\uff0c\u8fd8\u672a\u66fe\u7ed9\u72c2\u98ce\u5439\u5f97\u4e1c\u96f6\u897f\u6563\uff0c\u6392\u5217\u5f97\u867d\u7136\u51cc\u4e71\u65e0\u7ae0\uff0c\u5927\u4f53\u8fd8\u53ef\u4ee5\u770b\u5f97\u51fa\u662f\u4e24\u5177\u5c38\u4f53\u3002\u4e24 \u4e2a\u7834\u788e\u4e86\u7684\u5934\u9885\u4e0a\u8fd8\u8986\u76d6\u7740\u6d53\u5bc6\u7684\u9ed1\u53d1\u3002\u770b\u6765\u8fd9\u4e24\u5177\u5c38\u4f53\u751f\u524d\u5e94\u8be5\u662f\u4e2d\u5e74\u6c49\u5b50\u3002\u800c\u4e14\u662f\u6b7b\u4e86 \u8fd8\u6ca1\u591a\u4e45\u7684\uff0c\u6240\u4ee5\u5934\u53d1\u90fd\u672a\u8131\u843d\u3002 \u3000\u3000\u674e\u601d\u5357\u60f3\u4e86\u4e00\u60f3\uff0c\u4fbf\u660e\u767d\u5176\u4e2d\u7f18\u6545\u3002\u6c99\u6f20\u4e2d\u7684\u5140\u9e70\u662f\u559c\u98df\u8150\u8089\u7684\uff0c\u8fd9\u4e24\u4e2a\u4eba\u4e00\u6b7b\uff0c\u8eab\u4e0a \u7684\u76ae\u8089\u4fbf\u5df2\u7ed9\u5140\u9e70\u5544\u98df\u5c3d\u4e86\u3002 \u3000\u3000\u674e\u601d\u5357\u4e0d\u7981\u6bdb\u9aa8\u609a\u7136\uff0c\u77e5\u9053\u53ea\u8981\u81ea\u5df1\u4e00\u4e2a\u652f\u6491\u4e0d\u4f4f\uff0c\u5012\u4e86\u4e0b\u6765\uff0c\u4fbf\u5c06\u53d8\u6210\u8fd9\u6837\u7684\u4e00\u5806\u767d \u9aa8\uff01 \u3000\u3000\u674e\u601d\u5357\u6b63\u81ea\u89e6\u76ee\u60ca\u5fc3\uff0c\u5ffd\u542c\u5f97\u6781\u4e3a\u523a\u8033\u7684\u201c\u560e\u560e\u201d\u53eb\u58f0\uff0c\u62ac\u5934\u4e00\u770b\uff0c\u5934\u9876\u8986\u76d6\u7740\u4e00\u7247 \u201c\u9ed1\u4e91\u201d\uff0c\u5374\u539f\u6765\u662f\u4e00\u53ea\u7855\u5927\u65e0\u6bd4\u7684\u5140\u9e70\uff0c\u4e24\u53ea\u7fc5\u8180\u5f20\u5f00\u6765\u8db3\u6709\u4e00\u4e08\u591a\u957f\uff01\u4ed6\u521a\u521a\u770b\u5230\u88ab\u5140 \u9e70\u5544\u5c3d\u76ae\u8089\u7684\u767d\u9aa8\uff0c\u8fd9\u53ea\u98df\u4eba\u7684\u5140\u9e70\u5c31\u6765\u5230\u4e86\uff01 \u3000\u3000\u674e\u601d\u5357\u6012\u9053\uff1a\u201c\u597d\u5440\uff0c\u6211\u662f\u6d3b\u4eba\uff0c\u96be\u9053\u4f60\u4e5f\u6562\u98df\u6211\uff1f\u201d\u53ea\u89c1\u90a3\u53ea\u5140\u9e70\u4ece\u4ed6\u5934\u9876\u63a0\u8fc7\uff0c\u5411 \u4ed6\u524d\u9762\u7684\u4e00\u4e2a\u6c99\u4e18\u4fef\u51b2\u4e0b\u53bb\u3002 \u3000\u3000\u674e\u601d\u5357\u773c\u5149\u4e00\u77a5\uff0c\u5df2\u7ecf\u53d1\u73b0\u90a3\u91cc\u6709\u4e00\u4e2a\u4eba\uff01\u8138\u671d\u4e0b\u7684\u4fef\u5367\u5728\u6d41\u6c99\u4e4b\u4e0a\uff0c\u4e0d\u77e5\u662f\u6b7b\u4eba\u8fd8\u662f \u6d3b\u4eba\u3002\u4f46\u56db\u80a2\u65e0\u7f3a\uff0c\u7eb5\u662f\u6b7b\u4eba\uff0c\u81f3\u5c11\u4e5f\u662f\u4e00\u5177\u5b8c\u6574\u7684\u5c38\u4f53\u3002\u674e\u601d\u5357\u8fd9\u624d\u77e5\u9053\uff0c\u5140\u9e70\u662f\u8981\u53bb\u5544 \u98df\u90a3\u4e2a\u4eba\u7684\u3002 \u3000\u3000\u674e\u601d\u5357\u5927\u6012\uff0c\u5f20\u5f13\u642d\u7bad\uff0c\u201c\u55d6\u201d\u7684\u4e00\u7bad\u5c31\u5c04\u8fc7\u53bb\u3002\u5f13\u5982\u9739\u96f3\uff0c\u7bad\u4f3c\u6d41\u661f\uff0c\u6070\u597d\u5728\u90a3\u5140\u9e70 \u7684\u5de8\u722a\u5c06\u8981\u6293\u7740\u90a3\u4eba\u4e4b\u65f6\uff0c\u5c04\u4e2a\u6b63\u7740\uff01 \u3000\u3000\u674e\u601d\u5357\u867d\u7136\u751a\u4e3a\u75b2\u7d2f\uff0c\u6bd5\u7adf\u662f\u4e2a\u5177\u6709\u5185\u5bb6\u771f\u529b\u7684\u6b66\u529f\u9ad8\u624b\uff0c\u8fd9\u4e00\u7bad\u7684\u52b2\u9053\u975e\u6bd4\u5bfb\u5e38\uff0c\u90a3 \u53ea\u78e8\u76d8\u5927\u7684\u5140\u9e70\u5403\u4e86\u4ed6\u8fd9\u4e00\u7bad\uff0c\u75db\u5f97\u96be\u5f53\uff0c\u4e0d\u6562\u505c\u7559\uff0c\u5e26\u7740\u7bad\u5c31\u98de\u8d70\u4e86\u3002 \u3000\u3000\u674e\u601d\u5357\u8df3\u4e0b\u9a6c\u6765\uff0c\u8fc7\u53bb\u5bdf\u770b\uff0c\u628a\u90a3\u4eba\u7ffb\u8f6c\u8fc7\u6765\uff0c\u53ea\u89c1\u5934\u90e8\u8840\u8089\u6a21\u7cca\uff0c\u65e9\u5df2\u6b7b\u4e86\uff01\u674e\u601d\u5357 \u53f9\u4e86\u53e3\u6c14\uff0c\u5fc3\u9053\uff1a\u201c\u8d39\u4e86\u504c\u5927\u6c14\u529b\uff0c\u4e0d\u6599\u6551\u7684\u5374\u662f\u4e2a\u6b7b\u4eba\u3002\u60f3\u5fc5\u8fd9\u4eba\u4e00\u6b7b\uff0c\u5c31\u9047\u4e0a\u98ce\u6c99\u5c06\u4ed6 \u63a9\u57cb\uff0c\u5e78\u514d\u505a\u4e86\u5140\u9e70\u53e3\u4e2d\u7684\u98df\u7269\u3002\u4f46\u8fd9\u4eba\u662f\u4ec0\u4e48\u4eba\u5462\uff1f\u201d \u3000\u3000\u8fd9\u4eba\u7684\u8170\u95f4\u60ac\u6709\u4e00\u4e2a\u8349\u56ca\uff0c\u674e\u601d\u5357\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6211\u4e14\u770b\u770b\u4ed6\u6709\u4ec0\u4e48\u9057\u7269\uff0c\u8981\u662f\u80fd\u591f\u5f04\u6e05 \u695a\u4ed6\u7684\u8eab\u4efd\uff0c\u77e5\u9053\u4ed6\u662f\u54ea\u91cc\u4eba\u6c0f\uff0c\u5c06\u6765\u56de\u53bb\u7ed9\u4ed6\u7684\u5bb6\u4eba\u62a5\u4e2a\u8baf\u4e5f\u597d\u3002\u201d\u4e8e\u662f\u4fbf\u641c\u67e5\u8fd9\u4eba\u7684\u8349 \u56ca\u3002 \u3000\u3000\u674e\u601d\u5357\u628a\u888b\u5b50\u91cc\u7684\u4e1c\u897f\u62ff\u51fa\u6765\u4e00\u770b\uff0c\u4e0d\u89c9\u5927\u5403\u4e00\u60ca\u3002\u539f\u6765\u56ca\u4e2d\u6240\u6709\uff0c\u53ea\u662f\u4e09\u652f\u91d1\u9556\uff0c\u9ec4 \u91d1\u6240\u6253\u7684\u98de\u9556\uff0c\u5728\u6c5f\u6e56\u4e0a\u6781\u4e3a\u5c11\u89c1\uff0c\u9556\u7684\u5f62\u72b6\u4e5f\u5f88\u5947\u602a\uff0c\u90a3\u662f\u5c16\u7aef\u5f00\u53c9\u7684\u86c7\u5f62\u7684\u98de\u9556\u3002 \u3000\u3000\u4f46\u8fd9\u8fd8\u4e0d\u8db3\u4ee5\u4ee4\u674e\u601d\u5357\u5403\u60ca\uff0c\u4f7f\u5f97\u4ed6\u5403\u60ca\u7684\u662f\uff1a\u8fd9\u4e09\u652f\u91d1\u9556\u62ff\u5230\u624b\u4e2d\uff0c\u4fbf\u95fb\u5230\u4e00\u80a1\u8165 \u5473\u3002\u674e\u601d\u5357\u7acb\u5373\u77e5\u9053\uff0c\u8fd9\u662f\u6bd2\u9556\uff01\u6bd2\u9556\u4e5f\u8fd8\u4e0d\u8db3\u4e3a\u5947\uff0c\u4f46\u518d\u4ed4\u7ec6\u770b\u65f6\uff0c\u9556\u8eab\u4e0a\u8fd8\u523b\u6709\u56fe\u6848 \u7684\uff0c\u8fd9\u4e00\u7279\u6b8a\u7684\u6807\u5fd7\u53ef\u4f7f\u5f97\u674e\u601d\u5357\u5927\u5927\u5403\u60ca\u4e86\u3002 \u3000\u3000\u9556\u4e0a\u523b\u7740\u7684\u662f\u9f99\u5f62\u56fe\u6848\uff0c\u5177\u4f53\u800c\u5fae\uff0c\u6602\u5934\u821e\u722a\uff0c\u5341\u5206\u751f\u52a8\u3002\u674e\u601d\u5357\u5fc3\u4e2d\u95ea\u8fc7\u4e86\u4e09\u4e2a\u5b57\uff1a \u201c\u6bd2\u9f99\u9556\u201d\uff01 \u3000\u3000\u6bd2\u9f99\u9556\u7684\u4e3b\u4eba\u662f\u4e2a\u6c5f\u6e56\u4e0a\u9f0e\u9f0e\u6709\u540d\u7684\u72ec\u811a\u5927\u76d7\uff0c\u540d\u53eb\u5c60\u767e\u57ce\uff0c\u4eba\u79f0\u201c\u5180\u5317\u4eba\u9b54\u201d\uff01\u5355\u5355 \u770b\u4ed6\u7684\u59d3\u540d\u7ef0\u53f7\uff0c\u53ef\u80fd\u4f1a\u4ee5\u4e3a\u4ed6\u662f\u4e2a\u6740\u4eba\u4e0d\u7728\u773c\u7684\u6076\u9b54\uff0c\u5176\u5b9e\u4ed6\u5e73\u751f\u6740\u4eba\u867d\u591a\uff0c\u5374\u5e76\u975e\u6ee5\u6740 \u65e0\u8f9c\uff0c\u4ed6\u6740\u7684\u591a\u662f\u91d1\u56fd\u7684\u8d2a\u5b98\u6c61\u540f\uff0c\u5c24\u5176\u559c\u6b22\u6740\u6b8b\u5bb3\u767e\u59d3\u7684\u91d1\u56fd\u5e26\u5175\u7684\u519b\u5b98\uff0c\u67d0\u4e00\u5e74\u66fe\u5728\u5c71 \u4e1c\u7684\u4e03\u4e2a\u4e2d\u5c0f\u57ce\u5e02\uff0c\u6740\u4e86\u91d1\u56fd\u7684\u56db\u5341\u516b\u540d\u519b\u5b98\uff0c\u5176\u4e2d\u6709\u5341\u56db\u540d\u8fd8\u662f\u5b88\u5907\u548c\u603b\u5175\u7ea7\u7684\u9ad8\u7ea7\u519b \u5b98\u3002 \u3000\u3000\u91d1\u56fd\u7684\u5b98\u5e9c\u4e2d\u4eba\u95fb\u5176\u540d\u800c\u4e27\u80c6\uff0c\u4ed6\u672c\u6765\u53e6\u6709\u540d\u5b57\u7684\uff0c\u56e0\u4ed6\u59d3\u5c60\uff0c\u5728\u90a3\u5e74\u8fde\u6740\u4e03\u57ce\u7684\u519b\u5b98 \u4e4b\u540e\uff0c\u5c31\u6709\u4eba\u53eb\u4ed6\u505a\u5c60\u767e\u57ce\uff0c\u4ece\u6b64\u4ed6\u4e5f\u81ea\u79f0\u5c60\u767e\u57ce\u4e86\uff0c\u5f53\u65f6\u4ed6\u5e76\u66fe\u6709\u8c6a\u8bed\u8bf4\uff1a\u201c\u591a\u8499\u6c5f\u6e56\u4e0a \u7684\u670b\u53cb\u7ed9\u6211\u53d6\u4e86\u65b0\u540d\uff0c\u6211\u5fc5\u5b9a\u4e0d\u8d1f\u670b\u53cb\u7684\u671f\u671b\uff0c\u8981\u5c60\u5b83\u767e\u57ce\u7684\u91d1\u864f\u65b9\u7b97\u79f0\u5fc3\u3002\u201d\u81f3\u4e8e\u201c\u5180\u5317 \u4eba\u9b54\u201d\u5219\u662f\u91d1\u56fd\u7684\u6b66\u571f\u7ed9\u4ed6\u6240\u8d77\u7684\u7ef0\u53f7\u3002 \u3000\u3000\u674e\u601d\u5357\u4e0d\u77e5\u4ed6\u7a76\u7adf\u5c60\u4e86\u51e0\u57ce\uff0c\u4e0d\u8fc7\u60f3\u6765\u603b\u8fd8\u4e0d\u6ee1\u767e\u57ce\u4e4b\u6570\uff0c\u5982\u4eca\u5374\u5df2\u57cb\u9aa8\u6c99\u4e18\uff01 \u3000\u3000\u674e\u601d\u5357\u5bf9\u5c60\u767e\u57ce\u53ea\u662f\u95fb\u5176\u540d\u800c\u672a\u89c1\u8fc7\u9762\u7684\uff0c\u4e0d\u8fc7\u4ed6\u66fe\u542c\u5e08\u7236\u8be6\u7ec6\u8bf4\u8fc7\u5176\u4eba\u5176\u4e8b\uff0c\u8fd8\u7279\u522b \u63d0\u5230\u5c60\u767e\u57ce\u6240\u7528\u7684\u72ec\u95e8\u6697\u5668\u2014\u2014\u6bd2\u9f99\u9556\u3002\u8fd9\u662f\u5206\u91cf\u6700\u91cd\u800c\u53c8\u89c1\u8840\u5c01\u5589\u7684\u6bd2\u9556\uff0c\u53ea\u8981\u7ed9\u4ed6\u6253\u7740 \u4e00\u9556\uff0c\u6b66\u529f\u591a\u597d\uff0c\u4e5f\u662f\u5fc5\u6b7b\u65e0\u7591\u3002\u5f53\u5e74\u4ed6\u6740\u4e03\u57ce\u7684\u56db\u5341\u516b\u540d\u91d1\u56fd\u519b\u5b98\uff0c\u5176\u4e2d\u4e09\u5341\u516d\u540d\u4fbf\u662f\u4e27 \u5728\u4ed6\u8fd9\u6bd2\u9f99\u9556\u4e4b\u4e0b\uff01 \u3000\u3000\u674e\u601d\u5357\u624b\u4e2d\u62ff\u7740\u6bd2\u9f99\u6f02\uff0c\u5927\u60ca\u4e4b\u540e\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u4e00\u5b9a\u662f\u5c60\u767e\u57ce\u65e0\u7591\u4e86\uff0c\u4ed6\u8fd9\u4e48\u9ad8\u7684 \u6b66\u529f\uff0c\u5374\u662f\u8c01\u6709\u90a3\u4e48\u5927\u7684\u672c\u9886\u628a\u4ed6\u6740\u6b7b\u7684\uff1f\u201d \u3000\u3000\u5fc3\u5ff5\u672a\u5df2\uff0c\u5ffd\u542c\u5f97\u201c\u55d6\u201d\u7684\u4e00\u58f0\uff0c\u4e00\u652f\u54cd\u7bad\uff0c\u5df2\u662f\u5411\u4ed6\u5c04\u6765\uff01 \u3000\u3000\u8fd9\u652f\u54cd\u7bad\u6765\u5f97\u6025\u52b2\u4e4b\u6781\uff0c\u674e\u601d\u5357\u521a\u542c\u5f97\u201c\u545c\u201d\u7684\u4e00\u58f0\u54cd\uff0c\u5229\u7bad\u5df2\u662f\u5288\u80f8\u5c04\u5230\u3002\u674e\u601d\u5357\u6b63 \u5728\u4f4e\u5934\u770b\u90a3\u6bd2\u9f99\u9556\uff0c\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u5728\u8fd9\u6773\u65e0\u4eba\u70df\u7684\u6208\u58c1\u4e4b\u4e0a\u7a81\u906d\u6697\u7b97\uff0c\u51b7\u4e0d\u53ca\u9632\uff0c\u51e0\u4e4e\u7ed9\u90a3 \u679d\u7bad\u5c04\u7740\u3002 \u3000\u3000\u674e\u601d\u5357\u4e00\u4e2a\u201c\u71d5\u9752\u5341\u516b\u7ffb\u201d\u5c31\u5728\u6d6e\u6c99\u4e0a\u6253\u4e86\u51e0\u4e2a\u6eda\uff0c\u53d1\u7bad\u7684\u4eba\u54c8\u54c8\u5927\u7b11\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6 \u5feb\uff0c\u7b2c\u4e8c\u652f\u3001\u7b2c\u4e09\u652f\u54cd\u7bad\u53c8\u76f8\u7ee7\u5c04\u6765\u3002 \u3000\u3000\u54cd\u7bad\u672c\u6765\u542b\u6709\u8b66\u544a\u7684\u610f\u601d\u5728\u5185\u3002\u6c5f\u6e56\u4e0a\u7684\u4e60\u60ef\uff0c\u6562\u4e8e\u4f7f\u7528\u54cd\u7bad\u7684\u4eba\u4e00\u5b9a\u662f\u81ea\u4fe1\u6b66\u529f\u4e86 \u5f97\uff0c\u8fdc\u80dc\u5bf9\u65b9\uff0c\u6240\u4ee5\u624d\u7528\u54cd\u7bad\uff0c\u53eb\u5bf9\u65b9\u6709\u6240\u63d0\u9632\u3002\u800c\u4e14\u7b2c\u4e00\u652f\u54cd\u7bad\uff0c\u901a\u5e38\u4e5f\u4e0d\u4f1a\u662f\u5c04\u5411\u5bf9\u65b9 \u8981\u5bb3\u7684\u3002 \u3000\u3000\u53ef\u662f\u8fd9\u4e2a\u4eba\u7684\u54cd\u7bad\u6765\u5f97\u5feb\u5982\u95ea\u7535\uff0c\u800c\u4e14\u63a5\u8fde\u4e09\u652f\uff0c\u90fd\u662f\u5411\u7740\u674e\u601d\u5357\u7684\u8981\u5bb3\u4e4b\u5904\u5c04\u6765\uff0c\u8fd9 \u5c31\u5931\u6389\u4e86\u201c\u8b66\u544a\u201d\u7684\u610f\u4e49\uff0c\u8fd9\u4e2a\u4eba\u5206\u660e\u662f\u8981\u53d6\u4ed6\u7684\u6027\u547d\uff0c\u5374\u53c8\u6545\u4f5c\u8c6a\u8fc8\uff0c\u4f7f\u7528\u54cd\u7bad\uff0c\u663e\u5f97\u4ed6 \u201c\u4e0d\u662f\u201d\u6697\u7b97\u3002 \u3000\u3000\u674e\u601d\u5357\u5927\u6012\uff0c\u4e00\u4e2a\u201c\u9ca4\u9c7c\u6253\u633a\u201d\u7ffb\u8d77\u8eab\u6765\uff0c\u6b64\u65f6\u4ed6\u5df2\u6709\u4e86\u51c6\u5907\uff0c\u5bf9\u65b9\u7684\u8fde\u73e0\u7bad\u867d\u7136\u6765\u5f97 \u4ecd\u662f\u5341\u5206\u6025\u52b2\uff0c\u4ed6\u5df2\u662f\u53ef\u4ee5\u4ece\u5bb9\u5e94\u4ed8\u4e86\u3002\u4ed6\u7ffb\u8d77\u8eab\u6765\uff0c\u6b63\u597d\u8fce\u7740\u4e86\u90a3\u4e24\u679d\u7bad\uff0c\u5f53\u4e0b\u63d0\u5f13\u4e00 \u62e8\uff0c\u53ee\u94db\u4e24\u58f0\uff0c\u4e24\u652f\u54cd\u7bad\u843d\u5728\u5730\u4e0a\u3002 \u3000\u3000\u674e\u6069\u5357\u62ac\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u6765\u7684\u4e24\u9a91\u5feb\u9a6c\uff0c\u9a91\u5728\u9a6c\u4e0a\u7684\uff0c\u4e00\u4e2a\u662f\u7c97\u7709\u5927\u773c\u7684\u8499\u53e4\u6b66\u58eb\uff0c\u4e00\u4e2a \u662f\u9762\u8089\u6a2a\u751f\u3001\u62ab\u7740\u5927\u7ea2\u8888\u88df\u7684\u5587\u561b\u3002 \u3000\u3000\u90a3\u7c97\u7709\u5927\u773c\u7684\u6b66\u58eb\u6014\u4e86\u4e00\u6014\uff0c\u968f\u5373\u53c8\u7eb5\u58f0\u7b11\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u5012\u6709\u51e0\u5206\u672c\u4e8b\u3002\u4f60\u662f\u6765\u7ed9\u5c60 \u767e\u57ce\u6536\u5c38\u7684\u5417\uff1f\u4e0d\u7528\u8d39\u4e8b\u4e86\uff0c\u6211\u9001\u4f60\u5230\u9ec4\u6cc9\u53bb\u4e0e\u5c60\u767e\u57ce\u76f8\u4f1a\u5427\uff01\u201d \u3000\u3000\u90a3\u5587\u561b\u9053\uff1a\u201c\u5148\u95ee\u95ee\u4ed6\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u6b66\u571f\u8bf4\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u6765\u7ed9\u5c60\u767e\u57ce\u6536\u5c38\uff0c\u53c8\u662f\u6c49\u4eba\uff0c\u4e0d\u7528\u95ee\u51c6\u662f\u5c60\u767e\u57ce\u7684\u624b\u4e0b\u3002\u201d\u4e00\u9762\u8bf4 \u8bdd\uff0c\u4e00\u9762\u8df3\u4e0b\u9a6c\u6765\uff0c\u5411\u674e\u601d\u5357\u6251\u53bb\u3002\u6b66\u5b66\u9ad8\u624b\u5728\u6c99\u6f20\u4e0a\u548c\u654c\u4eba\u4ea4\u624b\uff0c\u5730\u6218\u8981\u6bd4\u9a6c\u6218\u6709\u5229\u5f97 \u591a\uff0c\u56e0\u4e3a\u6c99\u6f20\u4e0d\u6bd4\u5e73\u5730\uff0c\u8bf4\u4e0d\u5b9a\u4f1a\u8e0f\u7740\u6d6e\u6c99\uff0c\u4eba\u548c\u9a6c\u7684\u91cd\u91cf\u603b\u5728\u4e8c\u767e\u65a4\u5f00\u5916\uff0c\u8e0f\u7740\u6d6e\u6c99\u5750\u9a91 \u5c31\u4f1a\u5931\u9677\u3002\u4e00\u4e2a\u4eba\u5c31\u8f7b\u5f97\u591a\uff0c\u800c\u4e14\u53ef\u4ee5\u65bd\u5c55\u8f7b\u529f\uff0c\u6240\u4ee5\u8fd9\u4e2a\u8499\u53e4\u6b66\u58eb\u820d\u5f03\u5750\u9a91\uff0c\u8f7b\u8eab\u6765\u6597\u674e \u601d\u5357\u3002 \u3000\u3000\u674e\u601d\u5357\u656c\u4f69\u5c60\u767e\u57ce\u662f\u4e2a\u597d\u6c49\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6740\u5c60\u767e\u57ce\u7684\u4e00\u5b9a\u4e0d\u662f\u597d\u4eba\uff0c\u6597\u4e0d\u8fc7\u4e5f\u8981\u548c\u4ed6 \u4eec\u4e00\u6597\u3002\u201d \u3000\u3000\u5f85\u90a3\u8499\u53e4\u6b66\u58eb\u6765\u5230\u4e09\u4e08\u4e4b\u5185\uff0c\u674e\u601d\u5357\u9661\u5730\u4e00\u58f0\u5927\u559d\uff1a\u201c\u6765\u800c\u4e0d\u5f80\u975e\u793c\u4e5f\uff01\u201d\u624b\u4e2d\u7684\u4e09\u652f \u6bd2\u9f99\u9556\u4e00\u9f50\u6253\u51fa\uff01 \u3000\u3000\u674e\u601d\u5357\u4e00\u6765\u662f\u56e0\u4e3a\u8fd9\u4e2a\u6b66\u58eb\u7684\u624b\u6bb5\u592a\u8fc7\u72e0\uff1b\u4e8c\u6765\u5bf9\u65b9\u80fd\u591f\u6740\u5c60\u767e\u57ce\uff0c\u672c\u9886\u4e00\u5b9a\u5341\u5206\u4e86 \u5f97\uff0c\u82e5\u7136\u4e0d\u4e0b\u8fa3\u624b\uff0c\u53ea\u6015\u6027\u547d\u96be\u4fdd\u3002\u662f\u4ee5\u4ed6\u8feb\u4e0d\u5f97\u5df2\uff0c\u624d\u4f7f\u7528\u521a\u521a\u5230\u624b\u7684\u6bd2\u9f99\u9556\u3002 \u3000\u3000\u8fd9\u540d\u8499\u53e4\u6b66\u58eb\u6b3a\u8eab\u75be\u6251\uff0c\u4e5f\u5e76\u975e\u5bf9\u654c\u4eba\u6beb\u65e0\u9632\u5907\u7684\u3002\u4f46\u4e00\u6765\u56e0\u4e3a\u4ed6\u662f\u6210\u5409\u601d\u6c57\u5e10\u4e0b\u6709\u6570 \u7684\u597d\u624b\uff0c\u827a\u9ad8\u80c6\u5927\uff0c\u674e\u601d\u5357\u4e0d\u8fc7\u662f\u4e8c\u5341\u591a\u5c81\u7684\u5c0f\u4f19\u5b50\uff0c\u4ed6\u867d\u6709\u9632\u5907\uff0c\u4e5f\u8fd8\u4e0d\u600e\u6837\u628a\u674e\u601d\u5357\u653e \u5728\u773c\u5185\uff1b\u4e8c\u6765\u4ed6\u5e76\u4e0d\u77e5\u9053\u674e\u6069\u5357\u5df2\u7ecf\u53d6\u5f97\u4e86\u5c60\u767e\u57ce\u7684\u6bd2\u9f99\u9556\uff0c\u800c\u8fd9\u6bd2\u9556\u4e43\u662f\u89c1\u8840\u5c01\u5589\uff0c\u6bd2\u6027 \u6700\u5389\u5bb3\u7684\u6697\u5668\uff01 \u3000\u3000\u8ddd\u79bb\u53ea\u5728\u4e09\u4e08\u4e4b\u5185\uff0c\u800c\u4e14\u8fd9\u540d\u8499\u53e4\u6b66\u58eb\u7684\u8eab\u5f62\u8fd8\u662f\u5411\u4ed6\u6251\u6765\u7684\uff0c\u674e\u601d\u5357\u4e00\u624b\u4e09\u9556\uff0c\u5168\u90fd \u6253\u5411\u4ed6\u7684\u8981\u5bb3\uff0c\u9976\u4ed6\u6b66\u529f\u518d\u9ad8\uff0c\u4e5f\u662f\u96be\u4ee5\u8eb2\u907f\u7684\u4e86\uff01 \u3000\u3000\u674e\u601d\u5357\u7684\u4e09\u652f\u6bd2\u9f99\u9556\uff0c\u5206\u522b\u4ece\u4e09\u4e2a\u4e0d\u540c\u7684\u65b9\u4f4d\u6253\u53bb\uff0c\u4e00\u652f\u6253\u4ed6\u4e0a\u76d8\u989d\u89d2\u7684\u201c\u592a\u9633\u7a74\u201d\uff0c \u4e00\u652f\u6253\u4ed6\u4e2d\u76d8\u80f8\u53e3\u7684\u201c\u7487\u7391\u7a74\u201d\uff0c\u4e00\u652f\u6253\u4ed6\u4e0b\u76d8\u77f3\u819d\u76d6\u7684\u201c\u73af\u8df3\u7a74\u201d\uff0c\u8fd9\u4e09\u4e2a\u90e8\u4f4d\u5e76\u975e\u6210\u4e00 \u76f4\u7ebf\uff0c\u800c\u662f\u6392\u5217\u6210\u4e00\u4e2a\u4e0d\u89c4\u5219\u7684\u89d2\u5f62\u3002\u8fd9\u6837\u7684\u6253\u6cd5\uff0c\u96be\u5230\u4e86\u6781\u70b9\uff0c\u5bf9\u65b9\u5373\u4f7f\u662f\u4e00\u4e2a\u64c5\u63a5\u6697\u5668 \u7684\u9ad8\u624b\uff0c\u81f3\u5c11\u4e5f\u8981\u7ed9\u6253\u4e2d\u4e00\u652f\u3002 \u3000\u3000\u674e\u601d\u5357\u6ee1\u4ee5\u4e3a\u975e\u4e2d\u4e0d\u53ef\uff0c\u4e0d\u6599\u5c31\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u53ea\u542c\u5f97\u201c\u547c\u201d\u7684\u4e00\u58f0\uff0c\u7a81\u7136\u5c31\u50cf\u6709\u4e00\u7247\u9ec4 \u4e91\u5377\u6765\uff0c\u628a\u4ed6\u8fd9\u4e09\u652f\u6bd2\u9f99\u9556\u5168\u90e8\u5377\u4e86\u53bb\u3002\u539f\u6765\u662f\u90a3\u4e2a\u7ea2\u8863\u5587\u561b\u4ece\u9a6c\u80cc\u4e0a\u8df3\u8d77\uff0c\u4e00\u6280\u7bad\u4f3c\u7684\u5c04 \u6765\uff0c\u6070\u597d\u8d74\u5728\u90a3\u8499\u53e4\u6b66\u58eb\u7684\u524d\u9762\uff0c\u5927\u8896\u98de\u626c\uff0c\u628a\u4e09\u652f\u6bd2\u9f99\u9556\u4e00\u53e4\u8111\u513f\u7684\u6536\u5f52\u8896\u5e95\u3002 \u3000\u3000\u8fd9\u79cd\u63a5\u6697\u5668\u7684\u529f\u592b\u5f53\u771f\u662f\u51fa\u795e\u5165\u5316\uff0c\u674e\u601d\u5357\u975e\u552f\u89c1\u6240\u672a\u89c1\uff0c\u4e14\u662f\u95fb\u6240\u672a\u95fb\u3002 \u3000\u3000\u7ea2\u8863\u5587\u561b\u628a\u4e09\u652f\u6bd2\u9f99\u9556\u62c8\u8d77\u6765\u770b\u4e86\u4e00\u770b\uff0c\u4fbf\u653e\u5165\u56ca\u4e2d\uff0c\u7b11\u9053\uff1a\u201c\u5c60\u767e\u57ce\u7684\u6bd2\u9f99\u9556\u679c\u7136\u540d \u4e0d\u865a\u4f20\uff0c\u662f\u5929\u4e0b\u6700\u5389\u5bb3\u7684\u767e\u6bd2\u6697\u5668\uff0c\u53ef\u60dc\u4f60\u8fd9\u5c0f\u5b50\u4e0d\u4f1a\u4f7f\u7528\uff01\u201d \u3000\u3000\u542c\u4ed6\u7684\u53e3\u6c14\uff0c\u4f3c\u4e4e\u4ed6\u4ece\u524d\u8fd8\u672a\u89c1\u8fc7\u6bd2\u9f99\u9556\uff0c\u90a3\u4e48\uff0c\u5c60\u767e\u57ce\u662f\u4e0d\u662f\u4ed6\u6740\u7684\u5462\uff1f\u6291\u6216\u662f\u5728\u4ed6 \u4eec\u4ea4\u624b\u4e4b\u65f6\uff0c\u5c60\u767e\u57ce\u65e0\u6687\u5c04\u51fa\u6bd2\u9f99\u9556\u5c31\u7ed9\u4ed6\u6740\u4e86\u3002 \u3000\u3000\u4f46\u5728\u6b64\u65f6\u6b64\u5730\uff0c\u674e\u601d\u5357\u4e5f\u662f\u65e0\u6687\u63a8\u6572\u7684\u4e86\u3002\u674e\u601d\u5357\u5530\u7684\u62d4\u51fa\u5251\u6765\uff0c\u559d\u9053\uff1a\u201c\u5e76\u80a9\u5b50\u4e0a \u5427\uff01\u201d \u3000\u3000\u7ea2\u8863\u5587\u561b\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u7c73\u7c92\u4e4b\u73e0\uff0c\u4e5f\u653e\u5149\u534e\uff0c\u8001\u4f5b\u7237\u8fd8\u4e0d\u5c51\u4e0e\u4f60\u4ea4\u624b\u5462\u3002\u201d \u3000\u3000\u90a3\u8499\u53e4\u6b66\u58eb\u9669\u4e9b\u7ed9\u6bd2\u9f99\u9556\u6253\u4e2d\uff0c\u5413\u51fa\u4e86\u4e00\u8eab\u51b7\u6c57\uff0c\u6b64\u9645\u60ca\u9b42\u5df2\u5b9a\uff0c\u6012\u6c14\u9661\u751f\uff0c\u6251\u4e0a\u524d \u6765\uff0c\u5927\u559d\u9053\uff1a\u201c\u5c0f\u5b50\u4f11\u5f97\u7316\u72c2\uff0c\u770b\u6211\u53d6\u4f60\u6027\u547d\uff01\u201d \u3000\u3000\u7ea2\u8863\u5587\u561b\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u867d\u7136\u4e0d\u662f\u600e\u4e48\u4e86\uff0c\u4f46\u8d64\u8001\u6e29\uff0c\u4f60\u4e5f\u4e0d\u53ef\u8f7b\u654c\u4e86\u3002 \u3000\u3000\u8d64\u8001\u6e29\u4e00\u542c\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4f60\u770b\u4e0d\u8d77\u8fd9\u5c0f\u5b50\uff0c\u5374\u53eb\u6211\u4e0d\u53ef\u8f7b\u654c\uff0c\u8fd9\u5206\u660e\u4e5f\u662f\u770b\u4e0d\u8d77 \u6211\u3002\u201d\u4e3a\u4e86\u4e89\u4e00\u53e3\u6c14\uff0c\u4e8e\u662f\u4e00\u4e0a\u6765\uff0c\u4fbf\u5bf9\u674e\u601d\u5357\u8fde\u4e0b\u6740\u624b\u3002 \u3000\u3000\u674e\u601d\u5357\u4e00\u62db\u201c\u5f2f\u5f13\u5c04\u96d5\u201d\uff0c\u957f\u5251\u7b14\u76f4\u523a\u51fa\uff0c\u9661\u7136\u95f4\u4e00\u4e2a\u8f6c\u8eab\uff0c\u5de6\u7ffb\u53f3\u7ede\uff0c\u628a\u90a3\u6b66\u58eb\u5288\u6765 \u7684\u51f6\u608d\u7edd\u4f26\u7684\u8fde\u73af\u4e09\u5200\u5c3d\u90fd\u5316\u89e3\u3002\u800c\u4e14\u5251\u52bf\u672a\u8870\uff0c\u89e3\u62db\u4e4b\u540e\uff0c\u5251\u5c16\u4ecd\u662f\u5411\u524d\u523a\u53bb\u3002 \u3000\u3000\u8d64\u8001\u6e29\u5fc3\u5934\u4e00\u51db\uff0c\u6025\u5fd9\u4e00\u4e2a\u201c\u5927\u5f2f\u8170\uff0c\u659c\u63d2\u67f3\u201d\uff0c\u4fef\u8eab\u4e00\u65cb\uff0c\u6a2a\u5200\u62cd\u51fa\uff0c\u4ed6\u7684\u81c2\u529b\u672c\u6765 \u4e0d\u5f31\uff0c\u518d\u501f\u4e86\u8fd9\u4e00\u65cb\u8f6c\u4e4b\u52bf\uff0c\u52b2\u9053\u5df2\u5728\u674e\u601d\u5357\u7684\u8fd9\u4e00\u5251\u4e4b\u4e0a\uff0c\u201c\u94db\u201d\u7684\u4e00\u58f0\uff0c\u5200\u5251\u76f8\u4ea4\uff0c\u674e \u601d\u5357\u7684\u957f\u5251\u7ed9\u4ed6\u62cd\u5f00\u3002 \u3000\u3000\u674e\u601d\u5357\u4e5f\u7981\u4e0d\u4f4f\u5fc3\u5934\u4e00\u51db\uff0c\u60f3\u9053\uff1a\u201c\u8499\u53e4\u7684\u5200\u6cd5\u679c\u7136\u4e0e\u4e2d\u539f\u5927\u4e0d\u76f8\u540c\uff0c\u53e4\u602a\u4e4b\u6781\uff01\u201d\u8981 \u77e5\u674e\u601d\u5357\u5df2\u5f97\u5c11\u6797\u6d3e\u8fbe\u6469\u5251\u6cd5\u7684\u771f\u4f20\uff0c\u5728\u4ed6\u4f7f\u51fa\u90a3\u4e00\u62db\u6740\u624b\u8fd8\u51fb\u4e4b\u65f6\uff0c\u5b9e\u662f\u60f3\u4e0d\u5230\u8499\u53e4\u6b66\u58eb \u4f1a\u7528\u5982\u6b64\u53e4\u602a\u7684\u5200\u6cd5\u5316\u89e3\u4ed6\u7684\u3002 \u3000\u3000\u4e24\u4eba\u5200\u6765\u5251\u5f80\uff0c\u8f6c\u773c\u95f4\u5c31\u6597\u4e86\u4e09\u4e94\u5341\u62db\u3002\u674e\u601d\u5357\u53ea\u89c9\u5f97\u5589\u7126\u53e3\u6da9\uff0c\u76ee\u7729\u5934\u660f\uff0c\u5251\u62db\u4f7f \u51fa\uff0c\u5f80\u5f80\u529b\u4e0d\u4ece\u5fc3\u3002\u539f\u6765\u4ed6\u5728\u5e93\u91cc\u6208\u58c1\u4e0a\u56f0\u4e86\u4e24\u65e5\uff0c\u53ea\u7528\u5e72\u7cae\u5ea6\u65e5\uff0c\u6c34\u4e5f\u4e0d\u6562\u591a\u559d\uff0c\u5982\u4eca\u4e00 \u4e0e\u5f3a\u654c\u4ea4\u624b\uff0c\u521d\u65f6\u8fd8\u53ef\u4ee5\u652f\u6301\uff0c\u65f6\u95f4\u7a0d\u957f\uff0c\u5df2\u662f\u96be\u4ee5\u4e3a\u7ee7\u3002 \u3000\u3000\u8d64\u8001\u6e29\u5f97\u7406\u4e0d\u9976\u4eba\uff0c\u5200\u6cd5\u8d8a\u6765\u8d8a\u72e0\uff0c\u6a2a\u5288\u76f4\u65ab\uff0c\u6bcf\u4e00\u5200\u5288\u51fa\u53bb\uff0c\u90fd\u662f\u9690\u9690\u631f\u7740\u98ce\u96f7\u4e4b \u58f0\u3002 \u3000\u3000\u674e\u601d\u5357\u6539\u7528\u201c\u6e38\u8eab\u516b\u5366\u638c\u201d\u7684\u8f7b\u7075\u8eab\u6cd5\uff0c\u5251\u4e2d\u5939\u638c\uff0c\u4e0e\u8d64\u8001\u6e29\u6e38\u6597\u3002\u8d64\u8001\u6e29\u559d\u9053\uff1a\u201c\u4e00 \u5473\u8eb2\u95ea\uff0c\u4e0d\u6562\u4e0e\u6211\u89c1\u4e2a\u771f\u7ae0\uff0c\u7b97\u5f97\u4ec0\u4e48\u597d\u6c49\uff1f\u201d \u3000\u3000\u8bdd\u72b9\u672a\u4e86\uff0c\u674e\u601d\u5357\u9661\u5730\u559d\u9053\uff1a\u201c\u770b\u5251\uff01\u201d\u4e00\u62db\u201c\u5927\u6f20\u5b64\u70df\u201d\uff0c\u5251\u5149\u5982\u7ec3\uff0c\u500f\u5730\u91cc\u5c31\u6307\u5230 \u4e86\u8d64\u8001\u6e29\u7684\u80f8\u524d\uff0c\u8d64\u8001\u6e29\u659c\u8eab\u4e00\u7a9c\uff0c\u674e\u601d\u5357\u5982\u5f71\u968f\u5f62\u53c8\u8ffd\u4e86\u8fc7\u6765\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u4e5f\u53ea\u77e5\u8eb2 \u907f\uff0c\u4e0d\u6562\u4e0e\u6211\u89c1\u4e2a\u771f\u7ae0\u4e48\uff1f\u201d \u3000\u3000\u8d64\u8001\u6e29\u5927\u6012\uff0c\u4f7f\u4e86\u6d51\u8eab\u6c14\u529b\uff0c\u4e00\u5200\u5c31\u5288\u8fc7\u53bb\uff0c\u674e\u601d\u5357\u6545\u610f\u5356\u4e2a\u7834\u7efd\uff0c\u5012\u7eb5\u95ea\u5f00\uff0c\u8d64\u8001\u6e29 \u731b\u529b\u4e00\u8dc3\uff0c\u53c8\u662f\u4e00\u5200\uff0c\u6068\u4e0d\u5f97\u628a\u674e\u601d\u5357\u5288\u6210\u4e24\u534a\uff01 \u3000\u3000\u674e\u601d\u5357\u5ffd\u5730\u559d\u58f0\u201c\u5012\u4e5f\uff01\u201d\u8d64\u8001\u6e29\u201c\u54ce\u54df\u201d\u4e00\u58f0\uff0c\u679c\u7136\u5e94\u58f0\u800c\u5012\uff01\u539f\u6765\u8d64\u8001\u6e29\u7684\u8f7b\u529f\u4e0d \u53ca\u674e\u601d\u5357\uff0c\u674e\u601d\u5357\u5728\u6e38\u6597\u4e4b\u65f6\uff0c\u8bd5\u51fa\u4e86\u6709\u4e00\u5806\u4e43\u662f\u4e2d\u95f4\u9677\u7a7a\u7684\u6d6e\u6c99\uff0c\u4ed6\u5012\u7eb5\u8df3\u8fc7\u8fd9\u5806\u6d6e\u6c99\uff0c \u8d64\u8001\u6e29\u5374\u9677\u5165\u4e86\u6d6e\u6c99\u4e4b\u5185\u3002\u4ed6\u662f\u7528\u4e86\u5168\u8eab\u6c14\u529b\u7684\uff0c\u4e00\u8e0f\u7740\u4e86\u6d6e\u6c99\uff0c\u5927\u534a\u4e2a\u8eab\u5b50\u5df2\u662f\u9677\u4e86\u8fdb \u53bb\uff0c\u6025\u5207\u95f4\u54ea\u91cc\u8df3\u5f97\u51fa\u6765\uff1f \u3000\u3000\u773c\u770b\u53ea\u8981\u674e\u601d\u5357\u56de\u5251\u4e00\u6233\uff0c\u8d64\u8001\u6e29\u6027\u547d\u96be\u4fdd\uff0c\u90a3\u7ea2\u8863\u5587\u561b\u5df2\u662f\u6321\u5728\u674e\u601d\u5357\u9762\u524d\uff0c\u51b7\u51b7\u8bf4 \u9053\uff1a\u201c\u4f60\u83ab\u4ee5\u4e3a\u4f60\u7528\u72e1\u8ba1\u80dc\u4e00\u573a\uff0c\u5c31\u5f97\u610f\u4e86\u3002\u8001\u4f5b\u7237\u7a7a\u624b\u63a5\u4f60\u7684\u5251\uff0c\u53ea\u8981\u4f60\u8fc7\u5f97\u5341\u62db\uff0c\u8001\u4f5b \u7237\u7ed9\u4f60\u55d1\u5934\uff0c\u800c\u4e14\u606d\u9001\u4f60\u51fa\u8fd9\u6c99\u6f20\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u559d\u9053\uff1a\u201c\u8c01\u8981\u4f60\u8ba9\uff01\u201d\u5530\u7684\u4e00\u5251\u5c31\u523a\u8fc7\u53bb\uff0c\u6b64\u65f6\u4ed6\u7684\u6c14\u529b\u5df2\u5dee\u4e0d\u591a\u8017\u5c3d\uff0c\u5f62\u52bf\u51f6 \u9669\uff0c\u903c\u5f97\u4ed6\u5fc5\u987b\u901f\u6218\u901f\u51b3\uff0c\u662f\u4ee5\u4e00\u51fa\u624b\u4fbf\u662f\u201c\u8fbe\u9b54\u5251\u6cd5\u201d\u4e2d\u7684\u6740\u624b\u7edd\u62db\uff0c\u4e5f\u987e\u4e0d\u5f97\u5bf9\u65b9\u662f\u7a7a \u624b\u8fd8\u662f\u4f7f\u7528\u6b66\u5668\u4e86\u3002 \u3000\u3000\u90a3\u5587\u561b\u8bf4\u4e86\u53ea\u51ed\u4e00\u53cc\u8089\u638c\uff0c\u679c\u7136\u5c31\u662f\u7a7a\u624b\u5e94\u4ed8\u3002\u674e\u601d\u5357\u7684\u8fd9\u4e00\u5251\u6765\u5f97\u8fc5\u5982\u95ea\u7535\uff0c\u4ed6\u7adf\u7136 \u4e0d\u8eb2\u4e0d\u95ea\uff0c\u53ea\u542c\u5f97\u201c\u556a\u201d\u7684\u4e00\u58f0\uff0c\u674e\u601d\u5357\u7684\u9752\u94a2\u5251\u6b63\u662f\u7ed9\u4ed6\u5f39\u5f00\u3002\u8fd9\u4e00\u5f39\u62ff\u634f\u65f6\u5019\uff0c\u5f53\u771f\u662f \u5999\u5230\u6beb\u5dc5\uff01 \u3000\u3000\u674e\u601d\u5357\u864e\u53e3\u75bc\u75db\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u4e00\u4e2a\u201c\u98ce\u522e\u843d\u82b1\u201d\u7684\u8eab\u6cd5\uff0c\u8f6c\u5230\u90a3\u5587\u561b\u7684\u80cc\u540e\uff0c\u53c8\u662f\u4e00 \u5251\uff0c\u523a\u4ed6\u810a\u690e\u7684\u201c\u5927\u690e\u7a74\u201d\u3002 \u3000\u3000\u6b8a\u4e0d\u77e5\u674e\u601d\u5357\u56fa\u7136\u5403\u60ca\u975e\u5c0f\uff0c\u90a3\u5587\u561b\u4e5f\u662f\u4e0d\u7981\u5fc3\u5934\u4e00\u51db\uff0c\u201c\u8fd9\u5c0f\u5b50\u770b\u6765\u5df2\u662f\u7b4b\u75b2\u529b\u7aed\uff0c \u6211\u5c45\u7136\u8fd8\u4e0d\u80fd\u591f\u628a\u4ed6\u7684\u5251\u5f39\u51fa\u624b\u53bb\uff0c\u5012\u662f\u4e0d\u53ef\u5c0f\u89d1\u4e86\u3002\u201d \u3000\u3000\u90a3\u5587\u561b\u53cd\u624b\u4e00\u62c2\uff0c\u674e\u601d\u5357\u89c1\u8bc6\u8fc7\u4ed6\u7684\u672c\u9886\uff0c\u4e0d\u5bb9\u4ed6\u7684\u624b\u6307\u62c2\u5230\uff0c\u5251\u8d70\u8f7b\u7075\uff0c\u500f\u5730\u5c31\u65a9\u4ed6 \u53cc\u817f\u3002\u90a3\u5587\u561b\u4f7f\u7528\u201c\u62c2\u4e91\u624b\u201d\u7684\u529f\u592b\uff0c\u91cd\u5fc3\u653e\u5728\u4e0a\u76d8\uff0c\u4e0b\u76d8\u4e43\u662f\u201c\u7a7a\u95e8\u201d\uff0c\u674e\u601d\u5357\u672c\u9886\u867d\u4e0d \u53ca\u4ed6\uff0c\u4f46\u5bf9\u4e8e\u6b66\u5b66\u7684\u539f\u7406\u5374\u662f\u7814\u7a76\u6709\u7d20\uff0c\u662f\u4ee5\u80fd\u591f\u907f\u5b9e\u5c31\u865a\uff0c\u51fa\u624b\u5c31\u662f\u653b\u51fb\u5bf9\u65b9\u7684\u5f31\u70b9\u3002 \u3000\u3000\u90a3\u5587\u561b\u4e5f\u597d\u751f\u4e86\u5f97\uff0c\u80cc\u540e\u597d\u50cf\u957f\u7740\u773c\u775b\u4f3c\u7684\uff0c\u674e\u601d\u5357\u90a3\u91cc\u4e00\u4e2a\u53d8\u62db\uff0c\u4ed6\u4ea6\u5df2\u662f\u6b65\u6362\u8eab \u79fb\uff0c\u970d\u7684\u4e00\u4e2a\u201c\u9b41\u661f\u8e22\u6597\u201d\uff0c\u4ecd\u7136\u662f\u5bf9\u51c6\u4e86\u674e\u601d\u5357\u7684\u864e\u53e3\u8e22\u6765\u3002 \u3000\u3000\u674e\u601d\u5357\u5251\u950b\u4e00\u504f\uff0c\u907f\u5f00\u4e86\u4ed6\u7684\u978b\u5c16\uff0c\u5251\u950b\u671d\u7740\u4ed6\u817f\u7a9d\u201c\u9f20\u8df3\u7a74\u201d\u5c31\u523a\u3002\u867d\u7136\u4e0d\u8fc7\u6570\u5bf8\u4e4b \u5dee\uff0c\u4f46\u4ed6\u8fd9\u4e00\u907f\u4e00\u523a\u5374\u662f\u6b66\u5b66\u7684\u4e0a\u4e58\u529f\u592b\u3002\u9ad8\u624b\u6bd4\u62fc\uff0c\u6240\u4e89\u4e0d\u8fc7\u6beb\u5398\u4e4b\u5dee\uff0c\u5982\u6b64\u4e00\u6765\uff0c\u90a3\u5587 \u561b\u767b\u65f6\u53c8\u7ed9\u674e\u601d\u5357\u5236\u4e86\u673a\u5148\u3002 \u3000\u3000\u90a3\u5587\u561b\u786c\u751f\u751f\u7684\u628a\u8e22\u51fa\u53bb\u7684\u8fd9\u4e00\u811a\u7f29\u56de\uff0c\u5012\u7eb5\u51fa\u4e00\u4e08\u5f00\u5916\u3002\u674e\u601d\u5357\u8fde\u9047\u4e24\u6b21\u9669\u62db\uff0c\u4e5f\u662f \u5403\u60ca\u975e\u5c0f\uff0c\u4e00\u65f6\u95f4\u5012\u4e5f\u4e0d\u6562\u8d38\u7136\u8fdb\u51fb\u3002 \u3000\u3000\u90a3\u5587\u561b\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u7684\u5251\u6cd5\u597d\u4e0d\u6e9c\u6ed1\uff0c\u53ea\u6015\u5341\u62db\u4e4b\u5185\uff0c\u6211\u662f\u672a\u5fc5\u80dc\u5f97\u4ed6\u4e86\u3002\u201d\u6697 \u6697\u540e\u6094\u5148\u524d\u7684\u8bdd\u8bf4\u5f97\u592a\u6ee1\uff0c\u84e6\u5730\u5fc3\u5ff5\u4e00\u52a8\uff0c\u201c\u6709\u4e86\uff01\u201d\u628a\u8888\u88df\u8131\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u674e\u601d\u5357\u6b63\u8981\u63c9\u8eab\u518d\u4e0a\uff0c\u9661\u7136\u95f4\u53ea\u89c1\u4e00\u7247\u7ea2\u4e91\u5f53\u5934\u7f69\u4e0b\uff0c\u674e\u601d\u5357\u4e00\u5251\u523a\u53bb\uff0c\u201c\u55e4\u201d\u7684\u4e00\u58f0 \u8f7b\u54cd\uff0c\u5251\u5c16\u4ece\u8888\u88df\u4e0a\u6ed1\u8fc7\uff0c\u7adf\u662f\u672a\u80fd\u5c06\u4ed6\u7684\u8888\u88df\u523a\u7a7f\u3002\u5c31\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u53ea\u89c9\u52b2\u98ce\u6251\u9762\uff0c\u800c\u4e14 \u80f8\u53e3\u4e5f\u597d\u50cf\u6328\u4e86\u4e00\u9524\u4f3c\u7684\uff0c\u9690\u9690\u4f5c\u75db\uff0c\u674e\u601d\u5357\u8fde\u5fd9\u7eb5\u5f00\uff0c\u5e78\u597d\u6070\u6070\u6765\u5f97\u53ca\u907f\u5f00\u8888\u88df\u7f69\u4f53\u4e4b \u707e\u3002 \u3000\u3000\u90a3\u5587\u561b\u54c8\u54c8\u7b11\u9053\uff0c\u201c\u597d\u5c0f\u5b50\uff0c\u771f\u6709\u4f60\u7684\u3002\u8fd8\u6709\u516d\u62db\uff0c\u5c0f\u5fc3\u5e94\u4ed8\u4e86\u3002\u201d \u3000\u3000\u8fd9\u5587\u561b\u8bf4\u8fc7\u4e0d\u7528\u6b66\u5668\uff0c\u4f46\u8fd9\u88ad\u8888\u88df\u62ff\u5728\u4ed6\u7684\u624b\u4e0a\u5374\u662f\u4e00\u4ef6\u6781\u4e3a\u5389\u5bb3\u7684\u6b66\u5668\uff0c\u6296\u5f00\u6765\u4f3c\u662f \u4e00\u5f20\u5927\u7f51\uff0c\u6536\u675f\u8d77\u6765\u53c8\u4f3c\u4e00\u6839\u68cd\u5b50\uff0c\u7ecf\u8fc7\u4e86\u4ed6\u7684\u7384\u529f\u8fd0\u7528\uff0c\u78b0\u4e0a\u4e86\u674e\u601d\u5357\u7684\u9752\u94a2\u5251\uff0c\u7adf\u4f1a\u53d1 \u51fa\u94ff\u9535\u7684\u58f0\u54cd\u3002 \u3000\u3000\u674e\u601d\u5357\u594b\u529b\u62db\u67b6\uff0c\u4e0d\u8fc7\u6570\u62db\uff0c\u53ea\u89c9\u80f8\u4e2d\u6c14\u8840\u7ffb\u6d8c\uff0c\u4e94\u810f\u516d\u8151\u597d\u4f3c\u5c31\u8981\u7ffb\u8f6c\u4e00\u822c\u3002\u674e\u601d\u5357 \u6697\u7779\u53eb\u9000\uff0c\u201c\u4e0d\u5999\uff01\u201d\u4ed7\u7740\u8f7b\u529f\uff0c\u4e1c\u8eb2\u897f\u95ea\uff0c\u5e0c\u671b\u80fd\u591f\u5c06\u4ed6\u8bf1\u5230\u6d6e\u6c99\u4e4b\u4e0a\uff0c\u8d25\u4e2d\u6c42\u80dc\u3002 \u3000\u3000\u54ea\u77e5\u8fd9\u5587\u561b\u7684\u8f7b\u529f\u6bd4\u4ed6\u8fd8\u8981\u9ad8\u660e\uff0c\u51e0\u6b21\u8e0f\u7740\u4e86\u6d6e\u6c99\uff0c\u90fd\u662f\u4e00\u60ca\u5373\u8fc7\uff0c\u5982\u5f71\u968f\u5f62\u5730\u7d27\u7d27\u8ddf \u5728\u674e\u601d\u5357\u80cc\u540e\u3002 \u3000\u3000\u674e\u601d\u5357\u5168\u795e\u5e94\u654c\uff0c\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u5c11\u62db\u3002\u8fd9\u5587\u561b\u5374\u662f\u5fc3\u4e2d\u7126\u8e81\uff0c\u6697\u5730\u60f3\u9053\uff1a\u201c\u5e78\u597d\u8fd9\u91cc\u6ca1 \u6709\u5916\u4eba\uff0c\u8981\u4e0d\u7136\u7ed9\u4eba\u770b\u5230\u6211\u5728\u5341\u62db\u4e4b\u5185\u62ff\u4e0d\u4e0b\u8fd9\u4e2a\u5c0f\u5b50\uff0c\u6211\u7684\u9762\u4e0a\u8fd8\u6709\u4ec0\u4e48\u5149\u5f69\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u5b9e\u5728\u5df2\u662f\u75b2\u7d2f\u4e0d\u582a\uff0c\u5168\u4ed7\u7740\u4e00\u80a1\u6c14\u624d\u80fd\u591f\u52c9\u5f3a\u6253\u4e0b\u53bb\u7684\u3002\u5ffd\u5730\u4e00\u4e2a\u758f\u795e\uff0c\u4ed6\u60f3\u8bf1 \u5bf9\u65b9\u9677\u8db3\u6d6e\u6c99\uff0c\u4e0d\u6599\u81ea\u5df1\u4e00\u8dc3\u5374\u5dee\u4e86\u90a3\u4e48\u5c11\u8bb8\u6ca1\u6709\u8df3\u8fc7\u53bb\uff0c\u53cd\u800c\u9677\u5728\u6d6e\u6c99\u4e4b\u4e2d\u3002 \u3000\u3000\u90a3\u5587\u561b\u559d\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u8fd8\u60f3\u9003\u4e48\uff1f\u201d\u88df\u88df\u7f69\u4e0b\uff0c\u90a3\u80a1\u52b2\u98ce\u5148\u5c31\u4ee4\u5f97\u674e\u601d\u5357\u51e0\u4e4e\u7a92\u606f\u3002 \u3000\u3000\u674e\u601d\u5357\u6b63\u81ea\u5fc3\u91cc\u53eb\u9053\uff1a\u201c\u6211\u547d\u4f11\u77e3\uff01\u201d\u8ff7\u8ff7\u7cca\u7cca\u4e4b\u4e2d\u5ffd\u4f3c\u542c\u5f97\u201c\u5598\u201d\u7684\u4e00\u58f0\u54cd\uff0c\u90a3\u5587\u561b \u559d\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\uff1f\u201d\u968f\u5373\u6709\u4eba\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u8bf4\u8bdd\u7b97\u4e0d\u7b97\u6570\uff0c\u5df2\u7ecf\u662f\u7b2c\u5341\u4e94\u62db\u5566\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u60f3\u7741\u5f00\u773c\u775b\u6765\u770b\uff0c\u53cc\u773c\u5df2\u662f\u4e0d\u542c\u4f7f\u5524\uff0c\u7741\u4e0d\u5f00\u6765\u3002\u4f46\u4e5f\u8fd8\u9690\u9690\u611f\u89c9\u5f97\u597d\u50cf\u662f\u6709\u4eba \u5230\u6765\uff0c\u84e6\u5730\u91cc\u90a3\u5587\u561b\u5927\u53eb\u4e00\u58f0\uff0c\u968f\u5373\u542c\u5f97\u9a6c\u8e44\u58f0\u8fdc\u53bb\u3002\u674e\u601d\u5357\u77e5\u9053\u5b9a\u662f\u6709\u4eba\u6765\u6551\u81ea\u5df1\uff0c\u5982\u4eca \u5df2\u662f\u628a\u90a3\u5587\u561b\u6253\u8dd1\u4e86\u3002\u674e\u601d\u5357\u5168\u4ed7\u7740\u4e00\u80a1\u6c14\u52c9\u5f3a\u652f\u6491\uff0c\u6b64\u65f6\u77e5\u9053\u5371\u9669\u5df2\u8fc7\uff0c\u90a3\u53e3\u6c14\u4e00\u677e\uff0c\u4eba \u4e5f\u5c31\u767b\u65f6\u6655\u8fc7\u53bb\u4e86\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u674e\u601d\u5357\u5ffd\u89c9\u904d\u4f53\u6e05\u51c9\uff0c\u6726\u6726\u80e7\u80e7\u4e4b\u4e2d\u597d\u4f3c\u6709\u7518\u9716\u4e0b\u964d\uff0c\u6ef4\u5165\u4ed6\u7684\u53e3 \u4e2d\uff0c\u7126\u6e34\u6b62\u4e86\uff0c\u6709\u8bf4\u4e0d\u51fa\u7684\u8212\u670d\uff0c\u4eba\u4e5f\u5c31\u6e10\u6e10\u9192\u4e86\u8f6c\u6765\u3002 \u3000\u3000\u5f20\u5f00\u773c\u775b\u4e00\u770b\uff0c\u53ea\u89c1\u81ea\u5df1\u662f\u5728\u4e00\u4e2a\u5e10\u5e55\u4e4b\u4e2d\uff0c\u5e10\u5e55\u4e2d\u6709\u4e00\u4e2a\u5934\u6234\u5112\u51a0\uff0c\u7559\u7740\u4e09\u7edc\u957f\u987b\u7684 \u4e2d\u5e74\u6c49\u5b50\uff0c\u8fd8\u6709\u4e00\u4e2a\u7a7f\u7740\u6d45\u7eff\u8863\u88d9\u7684\u5999\u9f84\u5973\u5b50\uff0c\u4e24\u4eba\u90fd\u662f\u6c49\u4eba\u88c5\u675f\u3002\u90a3\u4e2a\u5c11\u5973\u6b63\u7ad9\u5728\u4ed6\u7684\u9762 \u524d\uff0c\u624b\u91cc\u62ff\u7740\u4e00\u6761\u6e7f\u900f\u7684\u6bdb\u5dfe\uff0c\u7ede\u90a3\u624b\u4e2d\uff0c\u628a\u6c34\u73e0\u4e00\u6ef4\u6ef4\u5730\u6ef4\u5728\u4ed6\u7684\u9762\u4e0a\uff0c\u6ef4\u8fdb\u4ed6\u7684\u53e3\u4e2d\u3002 \u3000\u3000\u6b64\u65f6\u5df2\u662f\u665a\u4e0a\uff0c\u6c99\u6f20\u4e0a\u65e5\u95f4\u548c\u665a\u4e0a\u7684\u6c14\u6e29\u76f8\u5dee\u5f88\u5927\uff0c\u674e\u601d\u5357\u662f\u5728\u708e\u70ed\u7684\u6c99\u6f20\u4e0a\u6655\u8fc7\u53bb \u7684\uff0c\u6b64\u65f6\u9192\u6765\u5b9b\u4f3c\u5230\u4e86\u6e05\u51c9\u7684\u5883\u754c\u3002\u4ed6\u5e72\u71e5\u7684\u5589\u5499\u6709\u4e86\u6c34\u73e0\u7684\u6ecb\u6da6\uff0c\u5c11\u5973\u6d45\u7eff\u8272\u7684\u8863\u88d9\u4e5f\u7ed9 \u4ed6\u4e00\u79cd\u6e05\u51c9\u7684\u611f\u89c9\u3002\u674e\u601d\u5357\u4e00\u9192\u8fc7\u6765\uff0c\u4e0d\u89c9\u4fbf\u662f\u7cbe\u795e\u4e00\u632f\u3002 \u3000\u3000\u90a3\u5c11\u5973\u89c1\u4ed6\u5f20\u5f00\u773c\u775b\uff0c\u7b11\u9053\uff1a\u201c\u597d\u4e86\uff0c\u7239\u7239\uff0c\u8fd9\u4f4d\u76f8\u516c\u5df2\u7ecf\u9192\u8fc7\u6765\u5566\uff01\u201d \u3000\u3000\u90a3\u4e2d\u5e74\u53cc\u5b50\u8d70\u4e86\u8fc7\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u4f60\u662f\u5c60\u767e\u57ce\u7684\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u5e10\u5e55\u7684\u4e00\u89d2\u5e76\u6392\u653e\u7740\u90a3\u4e09\u679a\u6bd2\u9f99\u9556\uff0c\u60f3\u5fc5\u662f\u8fd9\u4eba\u5728\u6253\u8dd1\u4e86\u90a3\u5587\u561b\u4e4b\u540e\u6361\u8d77\u6765\u7684\u3002\u5c60\u767e\u57ce \u7684\u5c38\u4f53\u60f3\u6765\u4e5f\u5f53\u7136\u662f\u7ed9\u4ed6\u53d1\u73b0\u4e86\u3002 \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u591a\u8c22\u4fa0\u58eb\u6551\u547d\u4e4b\u6069\u3002\u6211\u4e0e\u5c60\u767e\u57ce\u7d20\u4e0d\u76f8\u8bc6\uff0c\u4e0d\u8fc7\u66fe\u542c\u5f97\u5bb6\u5e08\u8bf4\u8fc7\u4ed6\u7684\u4e8b \u8ff9\uff0c\u77e5\u9053\u4ed6\u662f\u4e00\u4e2a\u597d\u6c49\u5b50\u3002\u201d \u3000\u3000\u90a3\u6c49\u5b50\u9053\uff1a\u201c\u4f60\u5e08\u7236\u662f\u8c01\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u8bf4\u4e86\u5e08\u7236\u7684\u540d\u5b57\uff0c\u90a3\u6c49\u5b50\u7b11\u9053\uff1a\u201c\u539f\u6765\u662f\u5c11\u6797\u6d3e\u8c37\u5927\u4fa0\u8c37\u5e73\u9633\u7684\u9ad8\u8db3\uff0c\u602a\u4e0d\u5f97\u672c \u9886\u8fd9\u4e48\u4e86\u5f97\uff01\u201d\u674e\u601d\u5357\u597d\u751f\u60ed\u6127\uff0c\u60f6\u7136\u8bf4\u9053\uff1a\u201c\u665a\u8f88\u5b66\u827a\u672a\u7cbe\uff0c\u82e5\u975e\u6069\u516c\u76f8\u6551\uff0c\u665a\u8f88\u5df2\u662f\u4e27 \u547d\u5728\u90a3\u51f6\u50e7\u624b\u4e0b\u3002\u201d \u3000\u3000\u90a3\u6c49\u5b50\u6b63\u8272\u8bf4\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u90a3\u4e24\u4e2a\u4eba\u662f\u4ec0\u4e48\u4eba\uff1f\u90a3\u4e2a\u5587\u561b\u662f\u8499\u53e4\u56fd\u5e08\u9f99\u8c61\u6cd5\u738b\u7684\u5927\u5f1f\u5b50 \u547c\u9ece\u5962\uff0c\u90a3\u4e2a\u6b66\u58eb\u5219\u662f\u6210\u5409\u601d\u6c57\u624b\u4e0b\u6709\u540d\u7684\u6b66\u58eb\u3002\u6210\u5409\u601d\u6c57\u624b\u4e0b\u6709\u5341\u4e8c\u4e2a\u672c\u9886\u6700\u9ad8\u7684\u6b66\u58eb\u53f7 \u79f0\u5341\u4e8c\u91d1\u521a\uff0c\u8fd9\u4eba\u540d\u53eb\u8d64\u8001\u6e29\uff0c\u5728\u5341\u4e8c\u91d1\u521a\u4e4b\u4e2d\u6392\u540d\u7b2c\u516b\u3002\u6211\u770b\u4f60\u7684\u5750\u9a91\u7d2f\u6210\u90a3\u4e2a\u6837\u5b50\uff0c\u60f3 \u6765\u4f60\u5df2\u662f\u88ab\u56f0\u5728\u6208\u58c1\u91cc\u6709\u51e0\u5929\u4e86\uff0c\u4f60\u5c45\u7136\u80fd\u591f\u6253\u8d25\u8d64\u8001\u6e29\uff0c\u8fd8\u80fd\u591f\u62b5\u6321\u547c\u9ece\u5962\u7684\u4e00\u5341\u4e94\u62db\uff0c \u8fd9\u6b63\u662f\u867d\u8d25\u72b9\u8363\uff0c\u8fd8\u7528\u5f97\u7740\u60ed\u6127\u4e48\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u542c\u4e86\u8fd9\u6c49\u5b50\u7684\u8d5e\u8bed\uff0c\u5fc3\u91cc\u5374\u662f\u6697\u6697\u5403\u60ca\uff0c\u60f3\u9053\uff1a\u201c\u539f\u6765\u90a3\u4e2a\u5587\u561b\u4e0d\u8fc7\u662f\u8499\u53e4\u56fd\u5e08 \u7684\u5f1f\u5b50\uff0c\u800c\u90a3\u4e2a\u6b66\u58eb\u5728\u5341\u4e8c\u91d1\u521a\u4e4b\u4e2d\u4e5f\u53ea\u80fd\u6392\u540d\u7b2c\u516b\u3002\u5982\u6b64\u770b\u6765\uff0c\u8499\u53e4\u5b9e\u662f\u5927\u6709\u80fd\u4eba\uff0c\u4e0d\u5728 \u4e2d\u539f\u4e4b\u4e0b\u5462\uff01\u201d \u3000\u3000\u90a3\u6c49\u5b50\u82e5\u6709\u6240\u601d\uff0c\u63a5\u7740\u95ee\u674e\u601d\u5357\u9053\uff1a\u201c\u5c60\u767e\u57ce\u662f\u7ed9\u8c01\u6740\u7684\uff0c\u4f60\u77e5\u9053\u4e48\uff1f\u4f9d\u6211\u770b\u6765\uff0c\u547c\u9ece \u5962\u7684\u6b66\u529f\u867d\u7136\u4e0d\u5f31\uff0c\u4f46\u51ed\u4ed6\u8fd9\u70b9\u672c\u9886\uff0c\u4e5f\u8fd8\u6740\u4e0d\u4e86\u5c60\u767e\u57ce\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u665a\u8f88\u4e0d\u77e5\uff0c\u4f46\u6069\u516c\u5374\u662f\u731c\u5f97\u4e0d\u9519\uff0c\u5c60\u767e\u57ce\u7684\u786e\u4e0d\u662f\u90a3\u4e24\u4eba\u6740\u7684\u3002\u56e0\u4e3a\u5728\u4ed6 \u548c\u6211\u4ea4\u624b\u4e4b\u524d\uff0c\u4ed6\u8fd8\u672a\u66fe\u89c1\u8fc7\u8fd9\u6bd2\u9f99\u9556\u3002\u201d\u5f53\u4e0b\u5c06\u53d1\u73b0\u5c60\u767e\u57ce\u5c38\u4f53\u7684\u7ecf\u8fc7\uff0c\u4ee5\u53ca\u548c\u90a3\u4e24\u4e2a\u4eba \u4ea4\u624b\u4e4b\u65f6\u6240\u542c\u5f97\u7684\u8a00\u8bed\u90fd\u5bf9\u8fd9\u4eba\u8bf4\u4e86\u3002 \u3000\u3000\u8fd9\u6c49\u5b50\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e0e\u5c60\u767e\u57ce\u66fe\u6709\u4e00\u9762\u4e4b\u7f18\uff0c\u867d\u7136\u76f8\u4ea4\u4e0d\u6df1\uff0c\u5f7c\u6b64\u90fd\u662f\u4e92\u76f8\u656c \u91cd\u7684\u3002\u6211\u542c\u8bf4\u4ed6\u6765\u5230\u4e86\u8499\u53e4\uff0c\u6b63\u60f3\u627e\u4ed6\uff0c\u4e0d\u6599\u4ed6\u5df2\u57cb\u9aa8\u8352\u6c99\u3002\u4e00\u4ee3\u82f1\u8c6a\uff0c\u4e27\u8eab\u5f02\u57df\uff0c\u5b9e\u662f\u4ee4 \u4eba\u53f9\u606f\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u4eba\u80fd\u591f\u6253\u8d25\u90a3\u4e2a\u5587\u561b\uff0c\u53c8\u662f\u5c60\u767e\u57ce\u7684\u670b\u53cb\uff0c\u60f3\u6765\u4e00\u5b9a\u662f\u4e2a\u5927\u6709\u6765 \u5934\u7684\u4eba\u7269\u3002\u201d\u4e8e\u662f\u606d\u606d\u656c\u656c\u5730\u95ee\u9053\uff1a\u201c\u5c0f\u53ef\u591a\u8499\u6551\u547d\u4e4b\u6069\uff0c\u4e0d\u6562\u8bf7\u6559\u6069\u516c\u9ad8\u59d3\u5927\u540d\u3002\u201d\u90a3\u6c49 \u5b50\u9053\uff1a\u201c\u60a3\u96be\u76f8\u52a9\uff0c\u6211\u8f88\u4fa0\u4e49\u9053\u4e4b\u6240\u5f53\u4e3a\uff0c\u4f55\u51b5\u54b1\u4eec\u90fd\u662f\u6c49\u4eba\u5462\u3002\u6211\u662f\u8944\u9633\u76c2\u5c11\u521a\uff0c\u5979\u662f\u5c0f \u5973\u5b5f\u660e\u971e\u3002\u201d\u674e\u601d\u5357\u542c\u4e86\u4ed6\u4eec\u7236\u5973\u7684\u540d\u5b57\uff0c\u4e0d\u89c9\u5927\u5403\u4e00\u60ca\u3002 \u3000\u3000\u539f\u6765\u8fd9\u5b5f\u5c11\u521a\u4e43\u662f\u540d\u9707\u6b66\u6797\u7684\u6c5f\u5357\u5251\u5ba2\uff0c\u674e\u601d\u5357\u66fe\u542c\u5f97\u5e08\u7236\u8bf4\u8fc7\u4ed6\u7684\u4e00\u4e2a\u6545\u4e8b\uff1a\u6709\u4e00\u5e74 \u5b5f\u5c11\u521a\u5077\u6e21\u957f\u6c5f\uff0c\u6f5c\u5165\u654c\u540e\uff0c\u60f3\u8981\u8054\u7edc\u4f0f\u725b\u5c71\u533a\u7684\u4e00\u652f\u4e49\u519b\uff0c\u4e0d\u5e78\u98ce\u58f0\u6cc4\u6f0f\uff0c\u91d1\u5ef7\u6d3e\u4e86\u4e94\u540d \u4e00\u7b49\u7684\u9ad8\u624b\u7f09\u6355\u4ed6\uff0c\u5728\u4f0f\u725b\u5c71\u4e0b\u76f8\u9047\uff0c\u4e00\u573a\u6076\u6218\uff0c\u5b5f\u5c11\u521a\u53ea\u51ed\u53cc\u638c\u4e00\u5251\uff0c\u5c3d\u6b7c\u91d1\u56fd\u4e94\u540d\u9ad8 \u624b\uff0c\u672c\u8eab\u4e5f\u53d7\u4e86\u91cd\u4f24\u3002 \u3000\u3000\u90a3\u662f\u5341\u5e74\u4e4b\u524d\u7684\u4e8b\u60c5\uff0c\u81ea\u6b64\u4e4b\u540e\uff0c\u5b5f\u5c11\u521a\u7684\u8e2a\u8ff9\u5c31\u518d\u4e5f\u6ca1\u6709\u5728\u91d1\u56fd\u7edf\u6cbb\u4e0b\u7684\u5730\u533a\u53d1\u73b0 \u4e86\u3002\u4e2d\u539f\u7684\u6b66\u6797\u4eba\u58eb\uff0c\u63e3\u6d4b\u7eb7\u7eb7\uff0c\u6709\u7684\u4ee5\u4e3a\u4ed6\u56e0\u91cd\u4f24\u96be\u6cbb\uff0c\u4e1a\u5df2\u8eab\u4ea1\uff1b\u6709\u7684\u4ee5\u4e3a\u4ed6\u5df2\u56de\u8f6c\u6c5f \u5357\uff0c\u7ecf\u8fc7\u4e86\u8fd9\u4e00\u6b21\u6b7b\u91cc\u9003\u751f\u7684\u5371\u9669\uff0c\u8c6a\u6c14\u975e\u590d\u5f53\u5e74\uff0c\u56e0\u6b64\u95ed\u95e8\u5c01\u5200\u4e86\u3002\u60f3\u4e0d\u5230\u5728\u5341\u5e74\u4e4b\u540e\uff0c \u674e\u601d\u5357\u5374\u5728\u8fd9\u5927\u6208\u58c1\u9047\u4e0a\u4e86\u4ed6\u3002 \u3000\u3000\u5b5f\u5c11\u521a\u4f3c\u662f\u731c\u5230\u4e86\u4ed6\u7684\u5fc3\u601d\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u5927\u7ea6\u66fe\u7ecf\u542c\u8fc7\u6211\u7684\u540d\u5b57\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u5b5f\u5927\u4fa0\u5f53\u5e74\u5728\u4f0f\u725b\u5c71\u4e0b\uff0c\u53cc\u638c\u4e00\u5251\uff0c\u5c3d\u6b7c\u91d1\u56fd\u4e94\u540d\u5927\u5185\u9ad8\u624b\uff0c\u5bb6\u5e08\u5bf9\u5b5f\u5927 \u4fa0\u4e5f\u662f\u5341\u5206\u4f69\u670d\u7684\u3002\u5bb6\u5e08\u5e38\u8bf4\uff0c\u4ed6\u53ea\u6068\u6ca1\u6709\u673a\u7f18\u5230\u6c5f\u5357\u53bb\u62dc\u8bbf\u5b5f\u5927\u4fa0\u3002\u665a\u8f88\u771f\u662f\u4f55\u5e78\u5982 \u4e4b\u3002\u201d \u3000\u3000\u5b5f\u5c11\u521a\u9053\uff1a\u201c\u6211\u5f53\u5e74\u8eab\u53d7\u91cd\u4f24\uff0c\u672c\u6765\u662f\u6d3b\u4e0d\u4e86\u7684\u3002\u5e78\u4e8f\u6709\u4e2a\u4eba\u6551\u4e86\u6211\uff0c\u8fd9\u4e2a\u4eba\u5c31\u662f\u5c60\u767e \u57ce\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u5982\u6b64\uff01\u201d\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u4ed6\u8d76\u6765\u6551\u6211\uff0c\u800c\u4e14\u6211\u4e00\u9192\u6765\uff0c\u4ed6\u5c31 \u95ee\u6211\u662f\u5c60\u767e\u57ce\u7684\u4ec0\u4e48\u4eba\u3002\u5c60\u767e\u57ce\u7684\u5c38\u9996\u7ed9\u6211\u53d1\u73b0\uff0c\u6211\u53c8\u6253\u51fa\u4e86\u5c60\u767e\u57ce\u7684\u72ec\u95e8\u6697\u5668\u6bd2\u9f99\u9556\uff0c\u5f53 \u65f6\u90a3\u5587\u561b\u4e5f\u4ee5\u4e3a\u6211\u662f\u5c60\u767e\u57ce\u7684\u5f1f\u5b50\uff0c\u60f3\u5fc5\u8fd9\u4f4d\u76c2\u5927\u4fa0\u4ea6\u66fe\u6709\u8fc7\u5982\u6b64\u6000\u7591\u3002\u201d \u3000\u3000\u5b5f\u5c11\u521a\u9053\uff1a\u201c\u6211\u5bf9\u4ee4\u5e08\u4e5f\u662f\u95fb\u540d\u5df2\u4e45\u7684\u4e86\uff0c\u53ef\u60dc\u6211\u8fd9\u6b21\u5306\u5306\u7ecf\u8fc7\u4e2d\u539f\uff0c\u672a\u80fd\u524d\u53bb\u62dc \u8bbf\u3002\u201d\u63a5\u7740\u6e20\u9053\uff1a\u201c\u4f60\u5728\u8fd9\u91cc\u9047\u89c1\u6211\uff0c\u6709\u70b9\u5947\u602a\u5427\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u524d\u8f88\u53ef\u662f\u5728\u8499\u53e4\u627e\u5bfb\u5c65\u5927\u4fa0\u7684\u4e48\uff1f\u201d \u3000\u3000\u5b5f\u5c11\u521a\u9053\uff1a\u201c\u6211\u662f\u77e5\u9053\u5730\u5df2\u7ecf\u6765\u4e86\u8499\u53e4\uff0c\u6211\u4e5f\u5e0c\u671b\u80fd\u591f\u5728\u8fd9\u91cc\u78b0\u89c1\u4ed6\uff0c\u4e0d\u8fc7\uff0c\u6211\u8fd9\u4e00\u6b21 \u5374\u5e76\u975e\u4e3a\u4ed6\u800c\u6765\u3002\u4f60\u662f\u6211\u8f88\u4e2d\u4eba\uff0c\u6211\u4e0d\u59a8\u5bf9\u4f60\u8bf4\u3002\u201d \u3000\u3000\u5728\u4ed6\u4eec\u8bf4\u8bdd\u7684\u65f6\u95f4\uff0c\u5b5f\u660e\u971e\u5df2\u7ecf\u716e\u597d\u4e00\u9505\u7a00\u996d\uff0c\u6b64\u65f6\u6367\u4e86\u4e0a\u6765\uff0c\u7b11\u9053\uff1a\u201c\u7239\uff0c\u5148\u8ba9\u5ba2\u4eba \u5403\u70b9\u4e1c\u897f\u518d\u8bf4\u3002\u674e\u5927\u54e5\uff0c\u4f60\u5728\u6076\u6597\u8fc7\u540e\uff0c\u75b2\u52b3\u8fc7\u751a\uff0c\u5403\u5e72\u786c\u7684\u4e1c\u897f\u6050\u6015\u4e0d\u5927\u9002\u5b9c\uff0c\u6240\u4ee5\u6211\u7ed9 \u4f60\u5f04\u4e86\u4e00\u70b9\u7a00\u996d\u3002\u6211\u4eec\u5df2\u7ecf\u5403\u8fc7\u4e86\uff0c\u4f60\u4e0d\u5fc5\u5ba2\u6c14\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u7b11\u9053\uff1a\u201c\u6211\u5728\u6c99\u6f20\u51e0\u5929\uff0c\u6bcf\u4e00\u5929\u81f3\u591a\u6562\u559d\u4e00\u5c0f\u676f\u7684\u6c34\uff0c\u5b9e\u5728\u6e34\u5f97\u96be\u53d7\uff0c\u5403\u7a00\u996d\u6700 \u597d\u4e0d\u8fc7\u3002\u201d\u5fc3\u91cc\u5f88\u662f\u611f\u6fc0\u5b5f\u660e\u971e\u7684\u4f53\u8d34\u3002 \u3000\u3000\u5e10\u5e55\u5916\u9762\u4f20\u6765\u9a6c\u5636\u4e4b\u58f0\uff0c\u674e\u601d\u5357\u542c\u5f97\u597d\u4f3c\u662f\u4ed6\u7684\u90a3\u5339\u201c\u4e00\u4e08\u9752\u201d\uff0c\u6b63\u8981\u8be2\u95ee\uff0c\u5b5f\u660e\u971e\u5df2 \u5728\u8bf4\u9053\uff1a\u201c\u4f60\u90a3\u5339\u5750\u9a91\uff0c\u6211\u4e5f\u7ed9\u4f60\u6551\u6d3b\u4e86\u3002\u9a6c\u662f\u597d\u9a6c\uff0c\u53ef\u60dc\u5927\u7ea6\u4e5f\u662f\u56e0\u4e3a\u51e0\u5929\u6ca1\u6709\u6c34\u559d\uff0c\u75b2 \u4e0d\u80fd\u5174\u3002\u73b0\u5728\u5b83\u6b63\u5728\u5916\u9762\u5403\u8349\uff0c\u6211\u51fa\u53bb\u770b\u770b\uff0c\u5f85\u5b83\u5403\u9971\u4e86\u8349\uff0c\u6211\u518d\u7275\u5b83\u53bb\u559d\u6c34\u3002\u79bb\u8fd9\u91cc\u4e0d \u8fdc\uff0c\u6709\u4e2a\u6c34\u6e90\u3002\u201d\u674e\u601d\u5357\u542c\u5f97\u5750\u9a91\u65e0\u6059\uff0c\u5927\u559c\u8fc7\u671b\uff0c\u518d\u6b21\u591a\u8c22\u4e86\u5b5f\u5bb6\u7236\u5973\u3002 \u3000\u3000\u5b5f\u5c11\u521a\u9053\uff1a\u201c\u971e\u513f\uff0c\u4f60\u628a\u9a6c\u513f\u7275\u53bb\u559d\u6c34\uff0c\u987a\u4fbf\u5e26\u4e00\u4e9b\u98df\u6c34\u56de\u6765\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u662f\u3002\u201d\u53d6\u4e0b\u6302\u5728\u5e10\u5e55\u4e0a\u7684\u4e00\u4e2a\u76ae\u888b\uff0c\u4fbf\u51fa\u53bb\u4e86\u3002\u6c99\u6f20\u4e0a\u7684\u8499\u53e4\u4eba\u90fd\u662f\u7528\u76ae \u888b\u76db\u6c34\u7684\uff0c\u5b5f\u5bb6\u7236\u5973\u5230\u4e86\u8499\u53e4\u5df2\u6709\u591a\u65e5\uff0c\u4e5f\u8ddf\u4ece\u4e86\u8499\u53e4\u4eba\u7684\u4e60\u60ef\u3002 \u3000\u3000\u674e\u601d\u5357\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u7528\u76ae\u888b\u76db\u6c34\u6bd4\u7528\u6c34\u58f6\u597d\u5f97\u591a\u4e86\uff0c\u53ef\u60dc\u6211\u6ca1\u6709\u9884\u5907\u3002\u8981\u662f\u6709\u8fd9\u4e48\u4e00\u4e2a \u5927\u76ae\u888b\uff0c\u5728\u6c99\u6f20\u91cc\u81f3\u5c11\u53ef\u4ee5\u591a\u71ac\u51e0\u5929\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u628a\u90a3\u9505\u7a00\u996d\u5403\u5f97\u5e72\u5e72\u51c0\u51c0\uff0c\u5b5f\u5c11\u521a\u7b11\u4e86\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u7cbe\u795e\u597d\u4e86\u70b9\u4e48\uff1f\u201d\u674e\u601d\u5357 \u9053\uff1a\u201c\u597d\u5f97\u591a\u4e86\u3002\u201d\u4e8e\u662f\u76c2\u5c11\u521a\u56de\u5230\u521a\u624d\u7684\u8bdd\u9898\uff0c\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u4f60\u5728\u91d1\u864f\u7edf\u6cbb\u7684\u5730\u533a\uff0c\u53ef\u66fe \u542c\u5230\u8fd9\u6837\u7684\u4e00\u4e2a\u98ce\u58f0\uff1a\u8499\u53e4\u8981\u548c\u5927\u5b8b\u8054\u76df\uff0c\u5939\u653b\u91d1\u56fd\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u8fd9\u51e0\u5e74\u665a\u8f88\u5bb6\u5c45\u4f8d\u5949\u8001\u6bcd\uff0c\u7a77\u4e61\u50fb\u58e4\uff0c\u542c\u4e0d\u5230\u4ec0\u4e48\u6d88\u606f\u3002\u6b64\u4e8b\u82e5\u7136\u5c5e\u5b9e\uff0c \u5012\u662f\u4e00\u4e2a\u559c\u8baf\u3002\u201d \u3000\u3000\u5b5f\u5c11\u521a\u76b1\u4e86\u76b1\u7709\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u770b\u6765\u6d88\u606f\u5012\u662f\u771f\u7684\uff0c\u4e0d\u8fc7\uff0c\u5374\u672a\u5fc5\u662f\u5927\u5b8b\u4e4b\u798f\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u5b5f\u5927\u4fa0\u53ef\u662f\u6050\u9632\u524d\u95e8\u62d2\u864e\uff0c\u540e\u95e8\u8fdb\u72fc\u3002\u201d \u3000\u3000\u5b5f\u5c11\u521a\u70b9\u4e86\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u6b63\u662f\u8fd9\u6837\u3002\u201d\u6b47\u4e86\u4e00\u6b47\uff0c\u559d\u4e86\u4e00\u53e3\u6c34\uff0c\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u6211\u6709\u4e2a \u670b\u53cb\uff0c\u662f\u5728\u5bab\u4e2d\u505a\u4f8d\u536b\u7684\uff0c\u636e\u8bf4\u8499\u53e4\u7684\u786e\u6d3e\u6709\u5bc6\u4f7f\u524d\u6765\u4e34\u5b89\uff0c\u51c6\u5907\u4e0e\u5b8b\u56fd\u5546\u8c08\u8054\u8179\u653b\u91d1\u4e4b \u4e8b\u3002\u4e34\u5b89\u671d\u8bae\u672a\u5b9a\uff0c\u5927\u81e3\u4e2d\u5206\u4e3a\u4e24\u6d3e\uff0c\u4e00\u6d3e\u6025\u529f\u8fd1\u5229\uff0c\u5f88\u60f3\u4fc3\u6210\u6b64\u4e8b\uff0c\u85c9\u8499\u53e4\u7684\u5175\u529b\u6062\u590d\u4e2d \u539f\uff1b\u4e00\u6d3e\u754f\u91d1\u5982\u864e\uff0c\u53ea\u6015\u6218\u4e8b\u5931\u5229\uff0c\u90a3\u65f6\u53cd\u906d\u4ea1\u56fd\u4e4b\u7978\u3002\u8fd9\u4e24\u6d3e\u4eba\u90fd\u6ca1\u6709\u60f3\u5230\u8981\u9760\u81ea\u5df1\u7684\u529b \u91cf\u6765\u6253\u8d25\u654c\u4eba\uff0c\u5f53\u771f\u662f\u4ee4\u4eba\u5174\u53f9\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u5b5f\u5927\u4fa0\u4e4b\u89c1\u5982\u4f55\uff1f\u201d \u3000\u3000\u5b5f\u5c11\u521a\u9053\uff1a\u201c\u8054\u8499\u53e4\u5939\u653b\u91d1\u56fd\uff0c\u6b64\u4e8b\u662f\u5426\u53ef\u884c\uff0c\u5728\u6c5f\u5357\u7684\u6b66\u6797\u4eba\u7269\u4e4b\u4e2d\uff0c\u4e5f\u662f\u8bae\u8bba\u7eb7 \u7eb7\uff0c\u83ab\u8877\u4e00\u662f\u3002\u8499\u53e4\u4e0e\u5728\u4e34\u5b89\u7acb\u56fd\u7684\u5927\u5b8b\u5730\u5317\u5929\u5357\uff0c\u76f8\u9694\u4f55\u6b62\u4e07\u91cc\uff0c\u6c5f\u5357\u8c6a\u6770\u90fd\u6ca1\u6709\u5230\u8fc7\u8499 \u53e4\uff0c\u5bf9\u8499\u53e4\u7684\u60c5\u5f62\u6beb\u65e0\u6240\u77e5\uff0c\u662f\u4ee5\u6b66\u6797\u670b\u53cb\u5728\u4e89\u8bba\u4e00\u756a\u4e4b\u540e\uff0c\u8ba4\u4e3a\u7a7a\u8bba\u65e0\u76ca\uff0c\u5fc5\u987b\u5230\u8499\u53e4\uff0c \u542c\u542c\u4ed6\u4eec\u7684\u8206\u60c5\uff0c\u770b\u770b\u4ed6\u4eec\u7684\u60c5\u51b5\uff0c\u7136\u540e\u624d\u80fd\u5224\u65ad\u8499\u53e4\u662f\u5426\u5177\u6709\u8bda\u610f\u3002\u8fd9\uff0c\u5c31\u662f\u6211\u8fd9\u6b21\u8981\u6765 \u8499\u53e4\u7684\u539f\u56e0\u4e86\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u8083\u7136\u8d77\u656c\uff0c\u8bf4\u9053\uff1a\u201c\u5b5f\u5927\u4fa0\u4e3a\u56fd\u8f9b\u52b3\uff0c\u95f4\u5173\u4e07\u91cc\uff0c\u6a2a\u7a7f\u5927\u6f20\uff0c\u6df1\u5165\u7a77\u8fb9\uff0c\u5f53\u771f\u662f \u4ee4\u4eba\u656c\u4f69\uff01\u201d \u3000\u3000\u5b5f\u5c11\u521a\u9053\uff1a\u201c\u6211\u521a\u8d70\u6c5f\u6e56\u7684\uff0c\u591a\u5403\u70b9\u82e6\uff0c\u6b63\u597d\u78e8\u7ec3\u81ea\u5df1\uff0c\u90a3\u4e5f\u7b97\u4ec0\u4e48\u3002\u5c0f\u5973\u660e\u971e\uff0c\u5979\u5bf9 \u8fd9\u91cc\u66b4\u70ed\u66b4\u5bd2\u7684\u6c14\u5019\u5f88\u4e0d\u4e60\u60ef\uff0c\u73b0\u5728\u4e5f\u53ef\u4ee5\u8d70\u4e86\u3002\u201d \u3000\u3000\u5b5f\u5c11\u521a\u53ea\u6709\u4e00\u4e2a\u5973\u513f\uff0c\u5f88\u662f\u75bc\u7231\uff0c\u4e00\u8bf4\u8d77\u6765\uff0c\u4e0d\u514d\u591a\u8bf4\u51e0\u53e5\u95f2\u8bdd\uff0c\u7b11\u9053\uff1a\u201c\u6211\u672c\u6765\u4e0d\u8981 \u5979\u6765\uff0c\u5979\u975e\u8981\u8ddf\u6211\u4e0d\u53ef\uff0c\u4fbf\u4e5f\u53ea\u597d\u5e26\u7740\u5979\u4e86\u3002\u5e78\u4e8f\u5979\u5012\u662f\u6ca1\u6709\u7ed9\u6211\u6dfb\u4e0a\u9ebb\u70e6\uff0c\u65c5\u9014\u4e0a\u7684\u8d77\u5c45 \u996e\u98df\uff0c\u53cd\u800c\u662f\u5979\u7167\u987e\u6211\u5462\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u8fd9\u662f\u5b5f\u5927\u4fa0\u7684\u597d\u798f\u6c14\uff0c\u6709\u4e00\u4e2a\u8fd9\u6837\u5b5d\u987a\u7684\u5973\u513f\u3002\u201d \u3000\u3000\u76c2\u5c11\u521a\u9053\uff1a\u201c\u4f60\u4e5f\u662f\u4e00\u4e2a\u5b5d\u5b50\u5440\uff0c\u4f60\u521a\u624d\u4e0d\u662f\u8bf4\uff0c\u4f60\u8fd9\u51e0\u5e74\u662f\u5728\u5bb6\u5c45\u4f8d\u5949\u8001\u6bcd\u5417\uff1f\u201d \u3000\u3000\u4e00\u4e2a\u5b5d\u5b50\uff0c\u4e00\u4e2a\u5b5d\u5973\u3002\u8bf4\u8005\u65e0\u5fc3\uff0c\u542c\u8005\u6709\u610f\uff0c\u674e\u601d\u5357\u957f\u4e86\u4e8c\u5341\u4e09\u5c81\uff0c\u4ece\u672a\u66fe\u4e0e\u5973\u5b50\u7ed3 \u4ea4\uff0c\u5b5f\u660e\u971e\u53ef\u7b97\u5f97\u662f\u4ed6\u7b2c\u4e00\u4e2a\u76f8\u8bc6\u7684\u5f02\u6027\u670b\u53cb\u3002\u5bf9\u4e8e\u8fd9\u6837\u4e00\u4e2a\u521a\u5065\u5a40\u5a1c\u6709\u4e4b\u7684\u4fa0\u5973\uff0c\u4ed6\u867d\u7136 \u4e0d\u6562\u7a0d\u6d89\u9050\u601d\uff0c\u5fc3\u91cc\u4e5f\u662f\u5341\u5206\u4f69\u670d\u7684\u3002\u6b64\u65f6\u4ed6\u6b63\u5728\u611f\u6fc0\u7740\u5b5f\u660e\u971e\u5bf9\u4ed6\u7684\u5173\u5fc3\uff0c\u542c\u4e86\u5b5f\u5c11\u521a\u7684 \u8bdd\uff0c\u4e0d\u89c9\u9762\u4e0a\u4e00\u70ed\uff0c\u8bf4\u9053\uff1a\u201c\u665a\u8f88\u600e\u4e48\u6bd4\u5f97\u6b62\u4ee4\u5a9b\uff1f\u5bf9\u5566\uff0c\u5b5f\u5927\u4fa0\u521a\u624d\u8bf4\u5230\u6b64\u6b21\u6765\u8499\u53e4\u63a2\u542c \u865a\u5b9e\uff0c\u4e0d\u77e5\u662f\u5426\u5df2\u6709\u6240\u5f97\uff1f\u201d\u4ed6\u559c\u6b22\u542c\u5b5f\u5c11\u521a\u8c08\u8bba\u4ed6\u7684\u5973\u513f\uff0c\u4f46\u53c8\u4e0d\u597d\u610f\u601d\u518d\u8c08\u4e0b\u53bb\uff0c\u4e8e\u662f \u5c31\u8d76\u5feb\u56de\u5230\u539f\u6765\u7684\u8bdd\u9898\u3002 \u3000\u3000\u5b5f\u5c11\u521a\u7b11\u9053\uff1a\u201c\u4f60\u77a7\u6211\u597d\u7cca\u6d82\uff0c\u6b63\u7ecf\u7684\u4e8b\u5fd8\u4e86\u8bf4\uff0c\u7ed5\u4e86\u51e0\u4e2a\u5f2f\uff0c\u4e5f\u4e0d\u77e5\u8bf4\u5230\u54ea\u91cc\u53bb \u4e86\u3002\u201d\u4ed6\u6574\u7406\u4e86\u4e00\u4e0b\u601d\u8def\uff0c\u559d\u4e86\u4e00\u53e3\u6c34\uff0c\u63a5\u4e0b\u53bb\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u7236\u5973\u5230\u4e86\u8499\u5360\uff0c\u5df2\u7ecf\u6709\u516b\u4e2a \u6708\u3002\u8fd9\u516b\u4e2a\u6708\u4e2d\uff0c\u6211\u4eec\u8d70\u4e86\u8bb8\u591a\u5730\u65b9\uff0c\u4e09\u6559\u4e5d\u6d41\u7684\u670b\u53cb\uff0c\u4e5f\u90fd\u89c1\u8fc7\u4e0d\u5c11\u3002\u6211\u4eec\u63a2\u542c\u6240\u5f97\uff0c\u6210 \u5409\u601d\u6c57\u6574\u519b\u7ecf\u6b66\uff0c\u4ee5\u4ed6\u7684\u5175\u529b\uff0c\u786e\u662f\u53ef\u4ee5\u72ec\u81ea\u6253\u8d25\u91d1\u56fd\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u4ed6\u4f55\u4ee5\u8981\u4e0e\u5927\u5b8b\u8054\u76df\uff1f\u201d \u3000\u3000\u5b5f\u5c11\u521a\u9053\uff1a\u201c\u4f9d\u6211\u731c\u60f3\uff0c\u4ed6\u4e00\u5b9a\u662f\u8981\u64cd\u5fc5\u80dc\u4e4b\u7b97\u624d\u80af\u8fdb\u5175\uff1b\u4e8c\u6765\u4ed6\u4e5f\u60f3\u6d88\u8017\u5b8b\u56fd\u7684\u5175 \u529b\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u6210\u5409\u601d\u6c57\u7684\u56fe\u8c0b\uff0c\u662f\u5728\u706d\u91d1\u4e4b\u540e\uff0c\u518d\u79fb\u5e08\u5357\u5411\u7684\u4e86\u3002\u201d \u3000\u3000\u5b5f\u5c11\u521a\u9053\uff1a\u201c\u4e00\u70b9\u4e0d\u9519\u3002\u6211\u770b\u5230\u597d\u51e0\u4e2a\u8ff9\u8c61\uff0c\u90fd\u53ef\u4ee5\u8bc1\u660e\u4ed6\u6709\u5148\u706d\u91d1\u3001\u540e\u706d\u6765\u7684\u56fe \u8c0b\u3002\u201d \u3000\u3000\u201c\u8499\u53e4\u5927\u90e8\u5206\u5730\u533a\u662f\u6c99\u6f20\uff0c\u4ed6\u4eec\u7684\u9a91\u5175\u6700\u4e3a\u9a81\u52c7\uff0c\u653b\u57ce\u63a0\u5730\uff0c\u9760\u7684\u5168\u662f\u9a91\u5175\u3002\u53ef\u662f\u8fd9\u4e00 \u4e24\u5e74\u6765\uff0c\u4ed6\u4eec\u5df2\u5728\u5f00\u59cb\u8bad\u7ec3\u6c34\u5e08\u4e86\u3002\u8499\u53e4\u7684\u51e0\u4e2a\u5927\u6e56\uff0c\u5982\u547c\u4f26\u6c60\u3001\u8d1d\u5c14\u6e56\u3001\u8fbe\u5c14\u6cca\u3001\u5e93\u5c14\u5bdf \u6c57\u6cca\u7b49\u7b49\uff0c\u90fd\u662f\u4ed6\u4eec\u8bad\u7ec3\u6c34\u5e08\u7684\u5904\u6240\u3002\u8fdb\u653b\u91d1\u56fd\uff0c\u53ef\u4ee5\u4ece\u9646\u8def\u5927\u4e3e\u5165\u4fb5\uff0c\u53ea\u9760\u9a91\u5175\u4fbf\u53ef\u6a2a\u884c \u65e0\u963b\u3002\u4ed6\u4eec\u8bad\u7ec3\u6c34\u5e08\uff0c\u663e\u7136\u4e0d\u662f\u5728\u4e8e\u5bf9\u4ed8\u91d1\u4eba\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u609a\u7136\u800c\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\u3002\u4ed6\u4eec\u82e5\u662f\u8981\u8fdb\u5175\u6c5f\u5357\uff0c\u5fc5\u987b\u6e21\u8fc7\u957f\u6c5f\u3002\u8fd9\u6c34\u5e08\u5f53\u7136\u662f \u8981\u7528\u6765\u4fb5\u5b8b\u7684\u4e86\u3002\u201d \u3000\u3000\u76c2\u5c11\u521a\u63a5\u4e0b\u53bb\u8bf4\u9053\uff1a\u201c\u5176\u6b21\uff0c\u8499\u53e4\u662f\u8bb8\u591a\u90e8\u843d\u7ed3\u5408\u800c\u6210\u7684\u56fd\u5bb6\uff0c\u90e8\u843d\u591a\uff0c\u79cd\u65cf\u4e5f\u591a\u3002\u6c49 \u4eba\u5728\u8499\u53e4\u4e5f\u6709\u4e0d\u5c11\u3002\u542c\u8bf4\u8499\u53e4\u4eba\uff0c\u5c24\u5176\u662f\u8499\u53e4\u7684\u8d35\u65cf\uff0c\u4ee5\u524d\u5bf9\u6c49\u4eba\u662f\u5f88\u8650\u5f85\u7684\uff0c\u8fd9\u4e24\u5e74\u5374\u597d \u4e86\u5f88\u591a\uff0c\u5728\u6210\u5409\u601d\u6c57\u7684\u5e10\u4e0b\u5c31\u7528\u4e86\u4e0d\u5c11\u6c49\u4eba\u3002\u4ed6\u4eec\u8981\u5b66\u6c49\u4eba\u7684\u8bfa\u8a00\uff0c\u8981\u719f\u6089\u6c5f\u5357\u7684\u98ce\u571f\u4eba\u60c5 \u548c\u5730\u7406\u5f62\u52bf\uff0c\u4e0d\u60dc\u62dc\u6c49\u4eba\u4e3a\u5e08\u5462\u3002\u4f60\u60f3\uff0c\u4ed6\u4eec\u8fd9\u6837\u505a\u662f\u4e3a\u4e86\u4ec0\u4e48\uff0c\u96be\u9053\u5f53\u771f\u662f\u5bf9\u6c49\u4eba\u597d\u8d77\u6765 \u4e48\uff1f\u8fd8\u4e0d\u662f\u4e3a\u4e86\u8981\u77e5\u5df1\u77e5\u5f7c\uff0c\u624d\u80fd\u767e\u6218\u767e\u80dc\u7684\u7f18\u6545\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u70b9\u4e86\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e5f\u542c\u8bf4\u6210\u5409\u601d\u6c57\u96c4\u624d\u5927\u7565\uff0c\u59d4\u5b9e\u4e0d\u53ef\u5c0f\u89d1\uff0c\u5982\u6b64\u770b\u6765\uff0c\u4ed6 \u8fd8\u5f53\u771f\u662f\u6df1\u8c0b\u8fdc\u8651\u54ea\uff01\u201d\u5fc3\u91cc\u5219\u5728\u60f3\u9053\uff1a\u201c\u8499\u53e4\u6539\u53d8\u5bf9\u5f85\u6c49\u4eba\u7684\u7b56\u7565\uff0c\u6211\u7684\u7239\u7239\u6216\u8bb8\u53ef\u4ee5\u5c11 \u53d7\u6298\u78e8\uff0c\u8fd8\u6d3b\u5728\u4eba\u95f4\u4e5f\u8bf4\u4e0d\u5b9a\u3002\u201d \u3000\u3000\u5b5f\u5c11\u521a\u8bb2\u5b8c\u4e86\u4ed6\u5728\u8499\u53e4\u7684\u6240\u89c1\u6240\u95fb\u4e4b\u540e\uff0c\u95ee\u674e\u601d\u5357\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u5728\u4e61\u4e0b\u8fd8\u4f4f\u5f97\u4e0b\u53bb \u5427\uff0c\u4e3a\u4ec0\u4e48\u4e5f\u8dd1\u5230\u8499\u53e4\u6765\uff1f\u201d\u5fc3\u60f3\uff0c\u4ed6\u82e5\u662f\u4e0d\u7518\u4e8e\u53d7\u91d1\u864f\u7684\u7edf\u6cbb\uff0c\u5e94\u8be5\u8dd1\u5230\u6c5f\u5357\u624d\u662f\u3002 \u3000\u3000\u674e\u601d\u5357\u8bf4\u51fa\u4e86\u4ed6\u8fdc\u6765\u5bfb\u7236\u7684\u539f\u56e0\u3002\u5b5f\u5c11\u521a\u9053\uff1a\u201c\u5bf9\u4e86\u3002\u6211\u4e5f\u77e5\u9053\u8499\u53e4\u5728\u4e8c\u5341\u5e74\u524d\uff0c\u66fe\u7ecf \u5174\u5175\u4fb5\u91d1\uff0c\u5728\u4e2d\u539f\u4fd8\u864f\u4e00\u6279\u6c49\u4eba\u8001\u767e\u59d3\u56de\u6765\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u8fd9\u6279\u4fd8\u864f\u7684\u4e0b\u843d\uff0c\u76c2\u5927\u4fa0\u53ef\u6709\u6240\u95fb\uff1f\u201d \u3000\u3000\u76c2\u5c11\u521a\u9053\uff1a\u201c\u6211\u542c\u8bf4\u5176\u4e2d\u6709\u4e00\u90e8\u5206\u662f\u7ed9\u4ed6\u4eec\u6d3e\u53bb\u5f00\u8352\u3002\u6c49\u4eba\u61c2\u5f97\u8015\u4f5c\uff0c\u8499\u53e4\u4eba\u5219\u53ea\u662f\u64c5 \u4e8e\u6e38\u7267\uff0c\u5bf9\u4e8e\u519c\u4e8b\uff0c\u975e\u5f97\u8bf7\u6559\u6c49\u4eba\u4e0d\u884c\u3002\u201d\u5f53\u4e0b\u8bf4\u4e86\u51e0\u4e2a\u6bd4\u8f83\u5927\u89c4\u6a21\u7684\u5f00\u57a6\u8352\u5730\u7684\u573a\u6240\uff0c\u674e \u601d\u5357\u8bb0\u5728\u5fc3\u4e0a\u3002 \u3000\u3000\u5b5f\u5c11\u521a\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u4e9b\u6709\u4e00\u6280\u4e4b\u957f\u7684\uff0c\u5f53\u4e86\u5de5\u5320\u4e0e\u5176\u4ed6\u67b6\u5dee\u3002\u4e5f\u6709\u5c11\u6570\u4eba\u53d7\u4e86\u5ef6\u63fd\uff0c\u5728 \u6210\u5409\u601d\u6c57\u7684\u5e10\u4e0b\u505a\u4e86\u5b98\u3002\u201d \u3000\u3000\u8bf4\u5230\u8fd9\u91cc\uff0c\u5b5f\u5c11\u521a\u60f3\u4e86\u4e00\u60f3\uff0c\u5ffd\u9053\uff1a\u201c\u4ee4\u5c0a\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u56e0\u4e3a\u4ed6\u7684\u7236\u4eb2\u5e76\u975e\u4ec0\u4e48\u8457\u540d\u4eba\u7269\uff0c\u6df7\u6742\u5728\u4e00\u5927\u5806\u4fd8\u864f\u4e4b\u4e2d\uff0c\u4ed6\u7684\u540d\u5b57\uff0c\u4ece\u6c5f\u5357\u6765 \u7684\u5b5f\u5c11\u521a\u60f3\u6765\u662f\u4e0d\u4f1a\u77e5\u9053\u7684\uff0c\u6240\u4ee5\u4e00\u76f4\u6ca1\u8bf4\u3002\u8fd9\u65f6\u542c\u5f97\u5b5f\u5c11\u521a\u95ee\u8d77\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8bd5\u4e00\u8bd5\u4e5f \u597d\uff0c\u8bf4\u4e0d\u8d70\u4ed6\u66fe\u7ecf\u542c\u8fc7\u4ec0\u4e48\u4eba\u8c08\u53ca\u6211\u7684\u7239\u7239\u3002\u201d\u4e8e\u662f\u5c31\u628a\u201c\u674e\u5e0c\u6d69\u201d\u8fd9\u4e2a\u540d\u5b57\u8bf4\u4e86\u51fa\u6765\uff0c\u8fd8 \u6015\u5b5f\u5c11\u521a\u542c\u4e0d\u6e05\u695a\uff0c\u62ff\u8d77\u7b77\u5b50\uff0c\u5728\u5730\u4e0a\u5199\u4e86\u4e09\u4e2a\u5de5\u7b14\u63e9\u4e66\u3002 \u3000\u3000\u5b5f\u5c11\u521a\u4f4e\u5934\u4e00\u770b\uff0c\u9762\u8272\u5fae\u5fae\u4e00\u53d8\uff0c\u4f46\u8f6c\u77ac\u4fbf\u5373\u6062\u590d\u5982\u5e38\uff0c\u4e0d\u8ba9\u674e\u601d\u5357\u770b\u51fa\uff0c\u6de1\u6de1\u8bf4\u9053\uff1a \u201c\u54e6\uff0c\u662f\u674e\u5e0c\u6d69\u5417\uff1f\u201d\u6b63\u662f\uff1a \u3000\u3000\u8a00\u8005\u65e0\u5fc3\u542c\u6709\u610f\uff0c\u9669\u6559\u78a7\u8840\u67d3\u9ec4\u6c99\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b\u2014\u2014\u7b2c\u4e8c\u56de\u3000\u7559\u4e66\u793a\u8b66\u7591\u771f\u5e7b\u3000\u6325\u5251\u8c01\u77e5\u662f\u53cb\u4ec7 \u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b \u7b2c\u4e8c\u56de\u3000\u7559\u4e66\u793a\u8b66\u7591\u771f\u5e7b\u3000\u6325\u5251\u8c01\u77e5\u662f\u53cb\u4ec7 \u3000\u3000\u674e\u601d\u5357\u559c\u9053\uff1a\u201c\u5b5f\u5927\u4fa0\u77e5\u9053\u5bb6\u7236\uff1f\u201d\u5b5f\u5c11\u521a\u9053\uff1a\u201c\u6211\u597d\u4f3c\u542c\u4eba\u8bf4\u8fc7\u8fd9\u4e2a\u540d\u5b57\u3002\u201d\u674e\u601d\u5357 \u6025\u5fd9\u95ee\u9053\uff1a\u201c\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u5b5f\u5c11\u521a\u60f3\u4e86\u4e00\u60f3\uff0c\u8bf4\u9053\uff1a\u201c\u662f\u548c\u4f60\u7239\u7239\u540c\u65f6\u88ab\u4fd8\u7684\u4e00\u4e2a\u6c49\u4eba\uff0c\u4e09\u5e74\u524d\u66fe\u7ecf\u548c\u4f60\u7684\u7239\u7239\u540c \u5728\u4e00\u4e2a\u5730\u65b9\u8015\u4f5c\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u5730\u65b9\uff1f\u201d \u3000\u3000\u201c\u897f\u90e8\u5e93\u4f26\u6c60\u7554\u7684\u6d77\u62c9\u5c14\u5c6f\u57a6\u533a\u3002\u4e2a\u591a\u6708\u524d\u6211\u5728\u5e93\u4f26\u6c60\u996e\u9a6c\uff0c\u5076\u7136\u78b0\u89c1\u8fd9\u4e2a\u4eba\uff0c\u548c\u4ed6\u804a \u5929\uff0c\u95ee\u4ed6\u5f00\u8352\u7684\u60c5\u5f62\u3002\u4ed6\u8bf4\u65e5\u5b50\u8fc7\u5f97\u5f88\u82e6\uff0c\u8bb8\u591a\u4eba\u90fd\u60f3\u9003\u8d70\u3002\u53ef\u662f\u6bcf\u4e00\u6b21\u9003\u8d70\u7684\u4eba\u90fd\u7ed9\u6293\u4e86 \u56de\u6765\uff0c\u6d3b\u751f\u751f\u5730\u6253\u6b7b\u4e86\u3002\u201d\u674e\u601d\u5357\u5403\u4e86\u4e00\u60ca\uff0c\u5931\u58f0\u53eb\u9053\uff1a\u201c\u6211\u7684\u7239\u7239\uff0c\u83ab\u975e\u4ed6\u3001\u4ed6\u2026\u2026\u201d\u76c2 \u5c11\u521a\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4e0d\uff0c\u4f60\u7239\u7239\u8fd9\u6837\u7684\u4eba\uff0c\u4ed6\u662f\u4e0d\u4f1a\u6b7b\u7684\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u653e\u4e0b\u4e86\u5fc3\u4e0a\u7684\u4e00\u5757\u77f3\u5934\uff0c\u7531\u4e8e\u4ed6\u7684\u5168\u526f\u7cbe\u795e\u90fd\u96c6\u4e2d\u5728\u60f3\u8981\u77e5\u9053\u4ed6\u7236\u4eb2\u7684\u751f\u6b7b\u4e4b\u8c1c \u7684\u8fd9\u4e2a\u95ee\u9898\u4e0a\uff0c\u662f\u4ee5\u6839\u672c\u5c31\u6ca1\u6709\u601d\u7d22\u5b5f\u5c11\u521a\u7684\u8bdd\u4e2d\u53e6\u6709\u542b\u610f\u3002 \u3000\u3000\u674e\u601d\u5357\u95ee\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u8fd9\u4e2a\u4eba\u4f55\u4ee5\u4f1a\u8c08\u8d77\u5bb6\u7236\uff1f\u201d \u3000\u3000\u5b5f\u5c11\u521a\u9053\uff1a\u201c\u7ecf\u8fc7\u51e0\u6b21\u8fd9\u6837\u7684\u4e8b\u60c5\u4e4b\u540e\uff0c\u60f3\u8981\u9003\u8dd1\u800c\u4e0d\u6562\u9003\u8dd1\u7684\u4eba\u5c31\u79d8\u5bc6\u96c6\u4f1a\uff0c\u5546\u91cf\u59a5 \u5584\u7684\u9003\u8dd1\u65b9\u6cd5\uff0c\u5927\u5bb6\u90fd\u8bf4\u5fc5\u987b\u5b66\u4f1a\u4e00\u70b9\u6b66\u827a\uff0c\u6709\u4eba\u77e5\u9053\u4f60\u7684\u7239\u7239\u662f\u5c06\u95e8\u4e4b\u540e\uff0c\u4e8e\u662f\u5c31\u592e\u6c42\u4ed6 \u6559\u5927\u4f19\u513f\u7684\u6b66\u827a\u3002\u6211\u5728\u5e93\u4f26\u6c60\u78b0\u4e0a\u7684\u90a3\u4e2a\u4eba\u4e5f\u66fe\u7ecf\u8ddf\u4f60\u7239\u7239\u5b66\u8fc7\u3002\u4ed6\u8fd8\u8bf4\uff0c\u4f60\u7684\u7239\u7239\u4e0d\u4f46\u61c2 \u5f97\u6b66\u827a\uff0c\u800c\u4e14\u9887\u901a\u6587\u58a8\uff0c\u53c8\u662f\u8015\u4f5c\u597d\u624b\uff0c\u56e0\u6b64\u8425\u5b98\u5f88\u91cd\u89c6\u4ed6\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u540e\u6765\u600e\u4f6f\uff1f\u201d\u5b5f\u5c11\u521a\u9053\uff1a\u201c\u540e\u6765\u4f60\u7684\u7239\u7239\u7ed9\u8c03\u5230\u53e6\u4e00\u4e2a\u5730\u65b9\uff0c\u8fd9\u4e2a\u4eba\u5c31\u4e0d \u77e5\u9053\u4ed6\u7684\u4e0b\u843d\u4e86\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u5927\u5931\u6240\u671b\uff0c\u8fd9\u4e2a\u6d88\u606f\u548c\u4ed6\u5bb6\u4e61\u9003\u56de\u6765\u7684\u90a3\u4e2a\u4eba\u6240\u8bf4\u7684\u5dee\u4e0d\u591a\uff0c\u4e0d\u8fc7\u6bd4\u8f83\u8be6\u7ec6\u4e00 \u4e9b\uff0c\u8fd8\u6709\u4e00\u4e2a\u4ed6\u7236\u4eb2\u4e09\u5e74\u524d\u66fe\u7ecf\u4f4f\u8fc7\u7684\u5c6f\u57a6\u533a\u7684\u5730\u5740\u3002\u674e\u601d\u5357\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u603b\u7b97\u5f97\u5230\u4e86\u4e00\u70b9 \u7ebf\u7d22\uff0c\u6211\u53ef\u4ee5\u5230\u90a3\u4e2a\u5730\u65b9\u518d\u884c\u67e5\u63a2\uff0c\u53ef\u662f\u7236\u4eb2\u7684\u751f\u6b7b\u4f9d\u7136\u672a\u77e5\uff0c\u5fc3\u4e2d\u4e0d\u514d\u7275\u6302\u3002 \u3000\u3000\u5b5f\u5c11\u521a\u9053\uff1a\u201c\u6211\u8bd5\u8fc7\u4f60\u7239\u7239\u6559\u8fc7\u7684\u90a3\u4e2a\u4eba\u7684\u6b66\u827a\uff0c\u5f88\u662f\u5e73\u5e38\u3002\u5065\u8eab\u662f\u53ef\u4ee5\u7684\uff0c\u78b0\u4e0a\u5f3a\u654c \u6050\u6015\u5c31\u4e0d\u7ba1\u7528\u4e86\u3002\u6055\u6211\u4fe1\u53e3\u96cc\u9ec4\uff0c\u770b\u6765\u4f60\u7239\u7239\u7684\u6b66\u827a\u662f\u8fdc\u4e0d\u5982\u4f60\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u5b8b\u5ba4\u5357\u6e21\u4e4b\u540e\uff0c\u6211\u5bb6\u4e16\u4ee3\u52a1\u519c\uff0c\u5bb6\u4f20\u7684\u6b66\u827a\u65e9\u5df2\u4e22\u8352\u4e86\uff0c\u6211\u7684\u672c\u9886\u90fd\u662f\u5e08 \u7236\u6559\u7684\u3002\u201d\u4e24\u4eba\u53c8\u8bf4\u4e86\u4e00\u4f1a\u8bdd\uff0c\u4e0d\u8fc7\u5b5f\u5c11\u521a\u7684\u6001\u5ea6\u5df2\u4e0d\u4f3c\u5148\u524d\u7684\u4eb2\u70ed\uff0c\u674e\u601d\u5357\u95ee\u4ed6\u5728\u8499\u53e4\u5404 \u5730\u6240\u89c1\u7684\u60c5\u5f62\uff0c\u4ed6\u662f\u95ee\u4e00\u53e5\u624d\u8bf4\u4e00\u53e5\u3002\u674e\u601d\u5357\u7cbe\u795e\u56f0\u5026\uff0c\u4e0d\u77e5\u4e0d\u89c9\u6253\u4e86\u4e00\u4e2a\u54c8\u6b20\u3002 \u3000\u3000\u5b5f\u5c11\u521a\u9053\uff1a\u201c\u4f60\u8fde\u65e5\u5954\u6ce2\uff0c\u53c8\u6076\u6597\u4e86\u4e00\u573a\uff0c\u8eab\u5b50\u56f0\u5026\uff0c\u5e94\u8be5\u7761\u4e86\u3002\u4f60\u7761\u5427\uff0c\u6211\u51fa\u53bb\u770b \u770b\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u8fd9\u624d\u60f3\u8d77\u5b5f\u660e\u971e\u51fa\u53bb\u5df2\u7ecf\u5f88\u4e45\u4e86\u3002 \u3000\u3000\u5b5f\u5c11\u521a\u51fa\u53bb\u627e\u5bfb\u5973\u513f\uff0c\u5e10\u5e55\u4e2d\u53ea\u7559\u4e0b\u4e86\u674e\u601d\u5357\u4e00\u4eba\u3002 \u3000\u3000\u674e\u601d\u5357\u672c\u6765\u7761\u610f\u6781\u6d53\uff0c\u4f46\u56e0\u4e3a\u4e0d\u89c1\u5b5f\u660e\u971e\u56de\u6765\u3001\u4e0d\u7981\u4e3a\u5979\u62c5\u5fc3\uff0c\u53cd\u800c\u7761\u4e0d\u7740\u4e86\u3002 \u3000\u3000\u201c\u96be\u9053\u5979\u662f\u906d\u9047\u4e86\u610f\u5916\uff1f\u201d\u201c\u5979\u662f\u5b5f\u5927\u4fa0\u7684\u5973\u513f\uff0c\u6b66\u529f\u4e00\u5b9a\u5f88\u662f\u4e86\u5f97\uff0c\u5373\u4f7f\u78b0\u4e0a\u90a3\u4e2a\u610f \u5916\u561b\uff0c\u60f3\u6765\u4e5f\u662f\u65e0\u59a8\u3002\u201d\u60f3\u662f\u8fd9\u6837\u60f3\uff0c\u4f46\u5b5f\u660e\u971e\u5c1a\u672a\u56de\u6765\uff0c\u4ed6\u603b\u662f\u653e\u5fc3\u4e0d\u4e0b\u3002 \u3000\u3000\u5927\u7ea6\u8fc7\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u4ecd\u7136\u4e0d\u89c1\u4ed6\u4eec\u7236\u5973\u56de\u6765\uff0c\u674e\u601d\u5357\u6309\u637a\u4e0d\u4f4f\uff0c\u4e8e\u662f\u4e5f\u8d70\u51fa\u5e10\u5e55\uff0c\u60f3\u53bb \u627e\u5bfb\u4ed6\u4eec\u3002 \u3000\u3000\u674e\u601d\u5357\u4e0d\u77e5\u9053\u90a3\u4e2a\u6c34\u6e90\u7684\u6240\u5728\uff0c\u5f80\u4f55\u5904\u627e\u5bfb\u5462\uff1f\u6b63\u81ea\u8e0c\u8e87\uff0c\u5ffd\u542c\u5f97\u8fdc\u5904\u4f3c\u4e4e\u6709\u4eba\u8bf4\u8bdd\uff0c \u8bf4\u7684\u4ec0\u4e48\uff0c\u5374\u662f\u542c\u4e0d\u6e05\u695a\u3002 \u3000\u3000\u674e\u601d\u5357\u66fe\u7ecf\u5b66\u8fc7\u4f0f\u5730\u542c\u58f0\u7684\u672c\u9886\uff0c\u6c99\u6f20\u7a7a\u65f7\uff0c\u6b64\u65f6\u53c8\u5df2\u662f\u4e07\u7c41\u4ff1\u5bc2\u7684\u4e09\u66f4\u65f6\u5206\uff0c\u4f0f\u5730\u542c \u58f0\uff0c\u53ef\u4ee5\u542c\u5f97\u89c1\u6570\u91cc\u4e4b\u5916\u7684\u58f0\u97f3\u3002\u674e\u601d\u5357\u672c\u6765\u662f\u5bb3\u6015\u6709\u4ec0\u4e48\u654c\u4eba\u6765\u7684\uff0c\u4e00\u542c\u4e4b\u4e0b\uff0c\u4e0d\u89c9\u53c8\u60ca \u53c8\u559c\uff0c\u4ed6\u542c\u5230\u7684\uff0c\u6b63\u662f\u4ed6\u6240\u6302\u5ff5\u7740\u7684\u5b5f\u660e\u971e\u7684\u58f0\u97f3\u3002 \u3000\u3000\u201c\u7239\u7239\uff0c\u4e0d\u53ef\uff01\u201d\u8fd9\u662f\u5979\u7528\u5c16\u9510\u7684\u5589\u97f3\u53eb\u51fa\u6765\u7684\u3002\u674e\u601d\u5357\u6ca1\u6709\u542c\u5230\u4ed6\u4eec\u524d\u534a\u6bb5\u7684\u8c08\u8bdd\uff0c \u5413\u4e86\u4e00\u8df3\uff0c\u4e0d\u77e5\u5b5f\u660e\u971e\u8bf4\u7684\u201c\u4e0d\u53ef\u201d\uff0c\u5230\u5e95\u662f\u6307\u4ec0\u4e48\u4e8b\u60c5\uff1f \u3000\u3000\u968f\u5373\u542c\u5f97\u5b5f\u5c11\u521a\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u8fd8\u662f\u6740\u4e86\u7684\u597d\uff01\u201d \u3000\u3000\u5b5f\u5c11\u521a\u8981\u6740\u8c01\u5462\uff1f\u770b\u6765\u4ed6\u4eec\u7236\u5973\u662f\u6b63\u5728\u8fa9\u8bba\u8981\u4e0d\u8981\u6740\u67d0\u4e00\u4e2a\u4eba\u7684\u95ee\u9898\uff0c\u674e\u601d\u5357\u89c9\u5f97\u6709\u70b9 \u5947\u602a\uff0c\u6740\u4eba\u662f\u4e00\u4ef6\u5927\u4e8b\uff0c\u4ed6\u4eec\u7236\u5973\u5e94\u8be5\u662f\u4e00\u81f4\u7684\u624d\u5bf9\uff0c\u4e3a\u4f55\u4e00\u4e2a\u8981\u6740\uff0c\u4e00\u4e2a\u5374\u4e0d\u8d5e\u540c\uff1f\u674e\u601d \u5357\u672c\u6765\u4e0d\u60f3\u5077\u542c\u4ed6\u4eec\u7236\u5973\u7684\u8c08\u8bdd\uff0c\u4f46\u597d\u5947\u5fc3\u91cd\uff0c\u5c31\u518d\u542c\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u201c\u7239\u7239\uff0c\u4f60\u4e0d\u662f\u8bf4\u8fc7\uff0c\u54b1\u4eec\u7684\u5b9d\u5251\u4e0d\u80fd\u6740\u65e0\u7f6a\u4e4b\u4eba\uff01\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u4f46\u50cf\u4ed6\u8fd9\u6837\u6709\u672c\u9886\u7684\u4eba\uff0c\u8981\u505a\u8d77\u574f\u4e8b\u6765\uff0c\u90a3\u5bb3\u5904\u5c31\u66f4\u5927\u4e86\uff01\u201d \u3000\u3000\u201c\u4ed6\u73b0\u5728\u53ef\u5e76\u6ca1\u6709\u505a\u8fc7\u4ec0\u4e48\u574f\u4e8b\u5440\uff01\u201d \u3000\u3000\u201c\u6211\u662f\u62c5\u5fc3\u4ed6\u5c06\u6765\u4e3a\u864e\u4f5c\u6005\uff01\u201d \u3000\u3000\u201c\u662f\u5426\u5982\u6b64\uff0c\u5c1a\u672a\u53ef\u77e5\u3002\u600e\u53ef\u4ee5\u73b0\u5728\u5c31\u5984\u52a0\u6740\u622e\uff1f\u201d \u3000\u3000\u201c\u971e\u513f\uff0c\u8fd9\u662f\u9632\u60a3\u672a\u7136\u3002\u6b64\u4e8b\u5173\u7cfb\u592a\u5927\uff0c\u54b1\u4eec\u4e0d\u5b9c\u6709\u5987\u4eba\u4e4b\u4ec1\u3002\u54b1\u4eec\u65e2\u7136\u4e0d\u80fd\u8001\u662f\u8ddf\u7740 \u4ed6\uff0c\u4e0d\u5982\u65e9\u65e9\u9664\u4e86\u8fd9\u4e2a\u540e\u60a3\u3002\u201d \u3000\u3000\u201c\u4f9d\u6211\u770b\u6765\uff0c\u4ed6\u51b3\u4e0d\u81f3\u4e8e\u4e3a\u864e\u4f5c\u6005\uff01\u201d \u3000\u3000\u201c\u4e0d\uff01\u6211\u770b\u4ed6\u5341\u4e5d\u662f\u4f1a\u540c\u6d41\u5408\u6c61\u3002\u4f60\u60f3\u2014\u2014\u201d\u5e95\u4e0b\u7684\u8bdd\u4e00\u4e2a\u5b57\u4e5f\u542c\u4e0d\u89c1\uff0c\u60f3\u5fc5\u662f\u76c2\u5c11\u521a \u5728\u5973\u513f\u7684\u8033\u6735\u8fb9\u6084\u58f0\u8bf4\u7684\u3002 \u3000\u3000\u8fc7\u4e86\u534a\u664c\uff0c\u53ea\u542c\u5f97\u5b5f\u660e\u971e\u53c8\u5728\u5c16\u58f0\u53eb\u4e86\u8d77\u6765\uff0c\u4ecd\u7136\u662f\u90a3\u8bdd\uff0c\u201c\u7239\u7239\uff0c\u4e0d\u53ef\uff01\u201d \u3000\u3000\u5728\u4ed6\u5973\u513f\u4e00\u518d\u575a\u6301\u4e4b\u540e\uff0c\u674e\u601d\u5357\u9690\u9690\u542c\u5f97\u5b5f\u5c11\u521a\u82e6\u7b11\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u5427\u3002\u4f60\u5f85\u6211\u4eca\u665a \u518d\u4ed4\u7ec6\u60f3\u60f3\u3002\u73b0\u5728\u4e0d\u8981\u518d\u8c08\u6b64\u4e8b\u4e86\uff0c\u4f60\u7684\u674e\u5927\u54e5\u4e5f\u8bb8\u8fd8\u6ca1\u6709\u7761\u7740\u5462\uff01\u201d \u3000\u3000\u201c\u7239\u7239\uff0c\u6211\u4e0d\u4f9d\u4f60\uff0c\u4f60\u600e\u4e48\u53ef\u4ee5\u8fd9\u6837\u53d6\u7b11\u5973\u513f\uff1f\u201d\u811a\u6b65\u58f0\u6e10\u6e10\u8d70\u5f97\u8fd1\u4e86\u3002 \u3000\u3000\u674e\u601d\u5357\u9762\u4e0a\u53d1\u70ed\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u96be\u9053\u660e\u971e\u5bf9\u6211\u6709\u70b9\u610f\u601d\uff0c\u7ed9\u5979\u7239\u7239\u77e5\u9053\u4e86\uff1f\u201d \u3000\u3000\u542c\u4e86\u8fd9\u4e48\u4e00\u5927\u6bb5\u5bf9\u8bdd\uff0c\u674e\u601d\u5357\u4ecd\u7136\u4e0d\u77e5\u9053\u5b5f\u5c11\u521a\u6240\u60f3\u8981\u6740\u7684\u4eba\u662f\u8c01\uff0c\u4e0d\u8fc7\uff0c\u4ed6\u5374\u77e5\u9053\u4e86 \u4e00\u70b9\uff0c\u5b5f\u5c11\u521a\u4e0d\u613f\u610f\u8ba9\u4ed6\u77e5\u9053\u3002 \u3000\u3000\u5077\u542c\u522b\u4eba\u7684\u79d8\u5bc6\u6b63\u662f\u6c5f\u6e56\u4e0a\u6700\u7981\u5fcc\u7684\u4e8b\u60c5\uff0c\u674e\u601d\u5357\u6697\u6697\u540e\u6094\uff1a\u201c\u5982\u679c\u7ed9\u4ed6\u4eec\u53d1\u73b0\u6211\u5728\u8fd9 \u91cc\u542c\u4ed6\u4eec\u7684\u8c08\u8bdd\uff0c\u4ed6\u4eec\u4e00\u5b9a\u4f1a\u628a\u6211\u5f53\u4f5c\u54c1\u884c\u4e0d\u7aef\u7684\u5c11\u5e74\u3002\u201d\u4e8e\u662f\u50cf\u5c0f\u5077\u4e00\u6837\uff0c\u6084\u6084\u722c\u5165\u5e10 \u5e55\uff0c\u5012\u5934\u4fbf\u7761\u3002\u5fc3\u4e2d\u6697\u6697\u5632\u7b11\u81ea\u5df1\uff1a\u201c\u602a\u4e0d\u5f97\u4eba\u5bb6\u8bf4\u662f\u4f5c\u8d3c\u5fc3\u865a\uff01\u674e\u601d\u5357\u5440\u674e\u601d\u5357\uff0c\u4f60\u672c\u6765 \u662f\u5b5f\u5927\u4fa0\u7684\u5ba2\u4eba\uff0c\u73b0\u5728\u5374\u53d8\u6210\u4e86\u722c\u4ed6\u5e10\u5e55\u7684\u5c0f\u5077\u4e86\u3002\u201d \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u5b5f\u5c11\u521a\u7236\u5973\u8d70\u5165\u5e10\u5e55\u3002\u5b5f\u5c11\u521a\u54b3\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u674e\u4e16\u5144\uff0c\u4f60\u7761\u4e86\u4e48\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u201c\u4f5c\u8d3c\u5fc3\u865a\u201d\uff0c\u672c\u6765\u4ecd\u60f3\u5047\u88c5\u719f\u7761\u7684\uff0c\u6015\u4ed6\u4e0d\u60ef\u4f5c\u4f2a\uff0c\u7ec8\u4e8e\u5e94\u4e86\u4e00\u58f0\uff0c\u5f53\u4f5c\u662f\u521a \u521a\u9192\u6765\u3002 \u3000\u3000\u4ed6\u672c\u6765\u7cbe\u795e\u56f0\u5026\uff0c\u5b5f\u5c11\u521a\u624b\u6267\u706b\u628a\uff0c\u706b\u5149\u4e0b\u89c1\u4ed6\u5f20\u5f00\u4e86\u6726\u80e7\u53cc\u773c\uff0c\u90a3\u6837\u5b50\u786e\u5b9e\u662f\u597d\u50cf\u521a \u9192\u8fc7\u6765\u3002 \u3000\u3000\u5b5f\u5c11\u521a\u653e\u4e0b\u4e86\u5fc3\uff0c\u60f3\u9053\uff1a\u201c\u6211\u4eec\u4e00\u8e0f\u4e0a\u8fd9\u4e2a\u571f\u4e18\uff0c\u5c31\u505c\u6b62\u8c08\u8bba\u90a3\u4ef6\u4e8b\u3002\u5373\u4f7f\u4ed6\u6ca1\u7761\u7740\uff0c \u9694\u7740\u8fd9\u4e48\u8fdc\uff0c\u4ed6\u4e5f\u4e0d\u4f1a\u542c\u89c1\u3002\u201d\u4ed6\u600e\u77e5\u9053\u674e\u601d\u5357\u66fe\u7ecf\u8d70\u51fa\u8fd9\u4e2a\u5e10\u5e55\uff0c\u800c\u4e14\u674e\u601d\u5357\u8fd8\u4f1a\u4f0f\u5730\u542c \u58f0\uff01 \u3000\u3000\u5b5f\u660e\u971e\u57cb\u6028\u9053\uff1a\u201c\u7239\uff0c\u4f60\u4e0d\u8be5\u5c06\u674e\u5927\u54e5\u53eb\u9192\u7684\uff0c\u4ed6\u7761\u5f97\u6b63\u9999\u5462\u3002\u201d\u4e00\u9762\u8bf4\u8bdd\uff0c\u4e00\u9762\u628a\u90a3 \u76db\u6c34\u7684\u76ae\u888b\u653e\u4e0b\u3002 \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u54e6\uff0c\u5b5f\u59d1\u5a18\uff0c\u4f60\u5df2\u7ecf\u6253\u6c34\u56de\u6765\u4e86\uff1f\u771f\u662f\u8f9b\u82e6\u4f60\u4e86\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u6211\u672c\u6765\u5e94\u8be5\u65e9\u5c31\u56de\u6765\u4e86\u7684\uff0c\u53ea\u56e0\u53d1\u73b0\u6709\u4e24\u4e2a\u4eba\u7ecf\u8fc7\uff0c\u4fbf\u6ce8\u89c6\u4ed6\u4eec\u7684\u884c \u8e2a\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u4e0d\u77e5\u662f\u53cb\u662f\u654c\uff0c\u8f7b\u529f\u597d\u751f\u4e86\u5f97\uff0c\u5728\u6c99\u6f20\u597d\u4f3c\u4e00\u6e9c\u70df\u7684\u5c31\u8fc7\u53bb\u4e86\u3002\u6211\u9690\u9690\u542c \u5f97\u5176\u4e2d\u4e00\u4eba\u63d0\u53ca\u2018\u6bd2\u9f99\u9556\u2019\u8fd9\u4e09\u4e2a\u5b57\u3002\u201d \u3000\u3000\u5b5f\u5c11\u521a\u9053\uff1a\u201c\u8bf4\u4e0d\u5b9a\u662f\u5c60\u767e\u57ce\u7684\u670b\u53cb\uff0c\u53ef\u60dc\u4f60\u6ca1\u6709\u62e6\u4f4f\u4ed6\u3002\u201d\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u5b5f\u5c11\u521a\u65e9\u542c\u5973 \u513f\u8bf4\u8fc7\uff0c\u53ea\u56e0\u4ed6\u53e6\u6709\u5fc3\u4e8b\uff0c\u8981\u4e0e\u5973\u513f\u5546\u91cf\uff0c\u5f53\u65f6\u6ca1\u6709\u8be6\u52a0\u76d8\u95f4\u3002 \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u90a3\u4e24\u4e2a\u4eba\u8dd1\u5f97\u98de\u5feb\uff0c\u6211\u600e\u4e48\u8ffd\u5f97\u4e0a\u4ed6\u4eec\uff1f\u4e0d\u8fc7\uff0c\u4ed6\u4eec\u7684\u76f8\u8c8c\u6211\u867d\u7136\u770b\u4e0d\u6e05 \u695a\uff0c\u5374\u4e5f\u53ef\u4ee5\u9690\u7ea6\u5206\u522b\u51fa\u6765\uff0c\u4e43\u662f\u4e00\u7537\u4e00\u5973\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u7684\u8f7b\u529f\u5b66\u5f97\u6700\u597d\uff0c\u867d\u7136\u4e0d\u53ca\u7236\u4eb2\uff0c\u5728\u6c5f\u6e56\u4e0a\u4ea6\u5df2\u662f\u5c11\u6709\u7684\u4e86\u3002\u5b5f\u5c11\u521a\u542c\u4e86\u5973\u513f\u8fd9 \u4e48\u8bf4\u6cd5\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u4e2b\u5934\u7d20\u6765\u5f88\u5c11\u4f69\u670d\u4eba\u7684\u3002\u5982\u6b64\u8bf4\u6765\uff0c\u90a3\u4e24\u4e2a\u4eba\u5012\u771f\u7684\u662f\u6b66\u676f\u9ad8\u624b \u4e86\u3002\u201d\u5fc3\u4e2d\u9887\u6709\u70b9\u60ca\u7591\u3002 \u3000\u3000\u674e\u601d\u5357\u5401\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u8fd8\u4ee5\u4e3a\u662f\u90a3\u4e2a\u5587\u561b\u9080\u4e86\u5e2e\u624b\uff0c\u518d\u6765\u5bfb\u4ec7\u4e8b\u5462\u3002\u82e5\u662f\u4e0d\u76f8\u5e72 \u7684\u4eba\uff0c\u90a3\u5c31\u7531\u4ed6\u53bb\u5427\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u6211\u5f53\u65f6\u5c31\u56e0\u4e3a\u4e0d\u77e5\u9053\u4ed6\u4eec\u662f\u4ec0\u4e48\u4eba\uff0c\u6240\u4ee5\u53ea\u597d\u6682\u65f6\u8eb2\u8d77\u6765\uff0c\u6ce8\u89c6\u4ed6\u4eec\u7684\u884c \u8e2a\u3002\u540e\u6765\u770b\u89c1\u4ed6\u4eec\u5e76\u4e0d\u662f\u671d\u7740\u54b1\u4eec\u5bbf\u8425\u7684\u65b9\u5411\u5954\u8dd1\uff0c\u8fd9\u624d\u653e\u5fc3\u3002\u4e0d\u4e45\uff0c\u7239\u7239\u4e5f\u5c31\u6765\u4e86\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u60f3\u9053\uff1a\u201c\u5979\u662f\u6015\u6211\u7761\u7740\u4e86\uff0c\u90a3\u4e24\u4e2a\u4eba\u6765\u5077\u88ad\u3002\u201d\u5fc3\u91cc\u5341\u5206\u611f\u6fc0\uff0c\u53c8\u60f3\uff1a\u201c\u5b5f\u5927\u4fa0 \u6240\u8981\u6740\u7684\u4eba\u83ab\u975e\u5c31\u662f\u90a3\u4e2a\u7537\u7684\uff1f\u201d\u4f46\u4ed4\u7ec6\u4e00\u60f3\uff0c\u8fd9\u4e00\u7537\u4e00\u5973\uff0c\u5b5f\u5927\u4fa0\u5e76\u6ca1\u6709\u89c1\u7740\u3002\u800c\u4ed6\u6240\u8981 \u6740\u7684\u90a3\u4e2a\u4eba\uff0c\u5219\u662f\u4ed6\u4eec\u7236\u5973\u90fd\u8ba4\u8bc6\u7684\uff0c\u8fd9\u53c8\u4e0d\u5bf9\u4e86\u3002 \u3000\u3000\u5c3d\u7ba1\u674e\u601d\u5357\u8fd8\u662f\u7591\u56e2\u83ab\u91ca\uff0c\u4f46\u5b5f\u660e\u971e\u5df2\u7ecf\u56de\u6765\uff0c\u4ed6\u7684\u5fc3\u91cc\u4e5f\u7528\u4e0d\u7740\u518d\u7275\u6302\u4e86\u3002\u5fc3\u60c5\u4e00 \u677e\uff0c\u5f88\u5feb\u5c31\u771f\u7684\u719f\u7761\u5982\u6ce5\u3002 \u3000\u3000\u8fd9\u4e00\u89c9\u7761\u5f97\u53c8\u9999\u53c8\u751c\uff0c\u68a6\u4e2d\u4eff\u4f5b\u8ddf\u4e86\u5b5f\u5bb6\u7236\u5973\uff0c\u5230\u4e86\u5c71\u6e29\u6c34\u8f6f\u7684\u6c5f\u5357\uff0c\u5728\u90a3\u4ed9\u5883\u4e00\u822c\u7684 \u5730\u65b9\u5b09\u620f\u3002\u5ffd\u7136\u5b5f\u660e\u971e\u53d8\u4e86\u8138\u4e0d\u7406\u4ed6\uff0c\u98ce\u4e5f\u4f3c\u5730\u8dd1\u5165\u6811\u6797\u3002 \u3000\u3000\u674e\u601d\u5357\u53eb\u9053\uff1a\u201c\u5b5f\u59d1\u5a18\uff0c\u5b5f\u59d1\u5a18\uff0c\u7b49\u7b49\u6211\u5440\uff01\u201d\u5b5f\u5c11\u521a\u4e00\u62f3\u6253\u5728\u4ed6\u7684\u80f8\u53e3\uff0c\u201c\u4f60\u8ffd\u6211\u7684 \u5973\u513f\u505a\u4ec0\u4e48\uff1f\u201d\u674e\u601d\u5357\u80f8\u53e3\u4e00\u75db\uff0c\u500f\u7136\u5c31\u9192\u4e86 \u3000\u3000\u7741\u773c\u4e00\u770b\uff0c\u4e0d\u7531\u5f97\u5403\u4e00\u60ca\uff0c\u5b5f\u5c11\u521a\u7236\u5973\u90fd\u4e0d\u89c1\u4e86\u3002\u4e0d\u4f46\u4e0d\u89c1\u4e86\u4eba\uff0c\u8fde\u5e10\u5e55\u4e5f\u6ca1\u6709\u4e86\u3002\u4ed6 \u662f\u7761\u5728\u4e00\u5757\u7a7a\u65f7\u7684\u8349\u5730\u4e0a\u3002 \u3000\u3000\u68a6\u5883\u4f9d\u7a00\u53ef\u8bb0\uff0c\u201c\u96be\u9053\u6211\u8fd8\u662f\u5728\u68a6\u91cc\u4e0d\u6210\uff1f\u201d\u674e\u601d\u5357\u5fc3\u60f3\u3002\u4f46\u4ed6\u8bd5\u54ac\u4e00\u54ac\u6307\u5934\uff0c\u5f88\u75db\uff0c \u8fd9\u5f53\u7136\u4e0d\u662f\u68a6\u4e86\u3002 \u3000\u3000\u674e\u601d\u5357\u5b9a\u4e86\u5b9a\u795e\uff0c\u5bdf\u89c6\u56db\u5468\uff0c\u5b5f\u5c11\u521a\u7236\u5973\u7684\u4e1c\u897f\u8fde\u90a3\u4e09\u652f\u6bd2\u9f99\u9556\u5728\u5185\u90fd\u5df2\u5e26\u8d70\uff0c\u53ea\u7559\u4e00 \u4e2a\u88c5\u6ee1\u6c34\u7684\u76ae\u888b\u3002 \u3000\u3000\u674e\u601d\u5357\u5185\u5fc3\u83ab\u89e3\uff1a\u201c\u4ed6\u4eec\u4e3a\u4ec0\u4e48\u4e0d\u7b49\u5f85\u6211\u9192\u6765\u5c31\u8d70\u4e86\u5462\uff1f\u201d\u4ed6\u77e5\u9053\u5b5f\u5c11\u521a\u662f\u8981\u56de\u8f6c\u6c5f \u5357\uff0c\u8fdf\u65e9\u8981\u5206\u624b\u7684\u3002\u4f46\u662f\u8fd9\u6837\u7684\u4e0d\u8f9e\u800c\u522b\uff0c\u5374\u672a\u514d\u592a\u8fc7\u4e8e\u4e0d\u8fd1\u4eba\u60c5\uff01 \u3000\u3000\u518d\u5f80\u8fdc\u5904\u4e00\u671b\uff0c\u674e\u601d\u5357\u53d1\u73b0\u4ed6\u7684\u90a3\u5339\u201c\u4e00\u4e08\u9752\u201d\u7cfb\u5728\u6811\u4e0a\uff0c\u4ed6\u7684\u5750\u9a91\u4e5f\u53d1\u73b0\u4e86\u4e3b\u4eba\uff0c\u58f0 \u58f0\u5636\u9e23\u3002\u674e\u601d\u5357\u7b11\u9053\uff1a\u201c\u4f60\u559d\u591f\u4e86\u6c34\uff0c\u5403\u9971\u4e86\uff0c\u7cbe\u795e\u6062\u590d\u4e86\u5566\uff01\u201d\u8d70\u8fc7\u53bb\u89e3\u5f00\u5ea7\u9a91\uff0c\u5ffd\u53c8\u53d1 \u73b0\u5730\u4e0a\u5199\u7684\u4e24\u884c\u5b57\uff0c\u674e\u601d\u5357\u4e00\u770b\uff0c\u53ef\u60ca\u5f97\u5446\u4e86\u3002 \u3000\u3000\u5f53\u4e2d\u4e00\u884c\uff0c\u5199\u7684\u516b\u4e2a\u5927\u5b57\uff0c\u201c\u4e3a\u864e\u4f5c\u6005\uff0c\u5fc5\u53d6\u4f60\u547d\u3002\u201d\u94c1\u5212\u9497\u94a9\uff0c\u521a\u52b2\u6709\u529b\uff0c\u60f3\u5fc5\u662f\u5b5f \u5c11\u521a\u7528\u5251\u5c16\u5728\u6c99\u5730\u4e0a\u5212\u51fa\u6765\u7684\u3002 \u3000\u3000\u65c1\u8fb9\u53e6\u6709\u4e00\u884c\u5c0f\u5b57\uff1a\u201c\u6c34\u888b\u7ed9\u4f60\uff0c\u671b\u4f60\u505a\u597d\u4eba\u3002\u201d\u4e66\u6cd5\u79c0\u4e3d\uff0c\u7b14\u81f4\u67d4\u5a9a\uff0c\u4e00\u770b\u4fbf\u77e5\u662f\u5973 \u5b50\u6240\u4e66\u3002\u4e0d\u7528\u8bf4\u5f53\u7136\u662f\u5b5f\u660e\u971e\u5199\u7684\u3002 \u3000\u3000\u674e\u601d\u5357\u5446\u82e5\u6728\u9e21\uff0c\u5bf9\u7740\u7740\u8fd9\u4e24\u884c\u5b57\u770b\u4e86\u53c8\u770b\uff0c\u51e0\u4e4e\u4e0d\u6562\u76f8\u4fe1\u81ea\u5df1\u7684\u773c\u775b\uff0c\u773c\u524d\u4e00\u7247\u8ff7 \u7cca\uff0c\u9ed1\u8499\u8499\u7684\uff0c\u90a3\u5341\u516b\u4e2a\u5927\u5b57\u5c0f\u5b57\u597d\u4f3c\u8fde\u6210\u4e86\u4e00\u5927\u7247\u4e4c\u4e91\uff0c\u538b\u5728\u4ed6\u7684\u5fc3\u4e0a\uff0c\u4ee4\u4ed6\u5982\u5760\u4e91\u96fe\u4e4b \u4e2d\uff01 \u3000\u3000\u8fc7\u534a\u664c\u674e\u601d\u5357\u624d\u6e05\u9192\u8fc7\u6765\uff0c\u8fd9\u624d\u660e\u767d\uff0c\u5b5f\u5c11\u521a\u8981\u6740\u7684\u90a3\u4e2a\u4eba\u7adf\u5c31\u662f\u4ed6\uff01 \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u4e3a\u4ec0\u4e48\uff1f\u4e3a\u4ec0\u4e48\uff1f\u201d\u4e00\u8fde\u4e32\u7684\u7591\u95ee\u90fd\u662f\u674e\u601d\u5357\u96be\u4ee5\u81ea\u89e3\u7684\u3002\u5b5f\u5c11\u521a\u65e2\u8981\u6740 \u4ed6\uff0c\u5f53\u521d\u53c8\u4f55\u5fc5\u6551\u4ed6\uff1f\u4ed6\u53c8\u6839\u636e\u4ec0\u4e48\u800c\u6562\u65ad\u5b9a\u4ed6\u4f1a\u4e3a\u864e\u4f5c\u6005\uff1f\u8fd8\u6709\u4ed6\u548c\u5b5f\u5c11\u521a\u7d20\u4e0d\u76f8\u8bc6\uff0c\u53ea \u662f\u521d\u6b21\u89c1\u9762\uff0c\u4f55\u4ee5\u5c31\u4f1a\u5f15\u8d77\u5b5f\u5c11\u521a\u90a3\u6837\u4e25\u91cd\u7684\u731c\u7591\uff1f \u3000\u3000\u8fd9\u4e00\u8fde\u4e32\u95ee\u9898\u53ea\u6709\u5b5f\u5c11\u521a\u624d\u80fd\u7ed9\u4ed6\u89e3\u7b54\uff0c\u53ef\u662f\u5b5f\u5c11\u521a\u5df2\u7ecf\u56de\u8f6c\u6c5f\u5357\uff0c\u5728\u8fd9\u5927\u6208\u58c1\u4e0a\u4ed6\u662f \u51b3\u8ba1\u8ffd\u4e0d\u4e0a\u4ed6\u4eec\u7684\u4e86\u3002\u4e5f\u8bb8\u8fd9\u4e00\u751f\u90fd\u6ca1\u6709\u673a\u4f1a\u518d\u89c1\u5230\u4ed6\u4eec\u7236\u5973\uff0c\u8fd9\u4e2a\u8c1c\u5e95\u4e5f\u5c31\u6c38\u8fdc\u65e0\u6cd5\u63ed \u5f00\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u4e5f\u6709\u4ee4\u5f97\u674e\u601d\u5357\u8db3\u4ee5\u81ea\u6170\u7684\uff0c\u90a3\u5c31\u662f\u5b5f\u660e\u971e\u8fd8\u76f8\u4fe1\u4ed6\u3002\u662f\u5979\u7684\u575a\u51b3\u53cd\u5bf9\uff0c\u4ed6\u624d\u4e0d \u81f4\u51a4\u6789\u4e27\u751f\u5728\u5979\u7239\u7239\u7684\u5251\u4e0b\uff0c\u73b0\u5728\u5979\u53c8\u628a\u4e00\u76ae\u888b\u7684\u6c34\u7559\u7ed9\u4ed6\uff0c\u6c34\uff0c\u5728\u522b\u7684\u5730\u65b9\u662f\u6beb\u4e0d\u503c\u94b1\u7684 \u4e1c\u897f\uff0c\u5728\u6c99\u6f20\u4e0a\u53ef\u662f\u6700\u5b9d\u8d35\u7684\u793c\u7269\uff0c\u662f\u751f\u547d\u4e4b\u6c34\u554a\uff01\u4ed6\u518d\u8bfb\u4e00\u904d\u5b5f\u660e\u971e\u7559\u7ed9\u4ed6\u7684\u90a3\u4e00\u884c\u5b57\uff0c \u5fc3\u4e2d\u6697\u81ea\u5411\u5979\u53d1\u8a93\uff1a\u201c\u5b5f\u59d1\u5a18\uff0c\u4f60\u653e\u5fc3\uff0c\u6211\u5c82\u80fd\u8f9c\u8d1f\u4e86\u4f60\u9001\u6211\u8fd9\u4efd\u793c\u7269\u7684\u76db\u60c5\uff1f\u6211\u4f1a\u6c38\u8fdc\u8bb0 \u7740\u4f60\u7684\u8bdd\u505a\u4e2a\u597d\u4eba\u7684\u3002\u201d\u4ed6\u559d\u4e86\u4e00\u5927\u53e3\u6c34\uff0c\u7518\u6cc9\u5165\u53e3\uff0c\u751c\u5728\u5fc3\u5934\uff0c\u5fc3\u4e2d\u7684\u70e6\u607c\u4e5f\u597d\u50cf\u7ed9\u7518\u6cc9 \u6d17\u6da4\u4e86\u3002 \u3000\u3000\u674e\u601d\u5357\u4f11\u606f\u4e86\u4e00\u665a\uff0c\u7cbe\u529b\u5df2\u7ecf\u6062\u590d\uff0c\u8de8\u4e0a\u5750\u9a91\uff0c\u518d\u5411\u897f\u884c\u3002\u4e0d\u5230\u534a\u5929\uff0c\u5c31\u8d70\u51fa\u4e86\u8fd9\u4e2a\u5927 \u6208\u58c1\u3002 \u3000\u3000\u539f\u6765\u5b5f\u5c11\u521a\u7236\u5973\u5bbf\u8425\u4e4b\u5904\uff0c\u662f\u9009\u62e9\u9644\u8fd1\u6709\u6c34\u8349\u7684\u5730\u65b9\u7684\uff0c\u8fd9\u5730\u65b9\u5df2\u7ecf\u662f\u63a5\u8fd1\u6208\u58c1\u7684\u8fb9\u7f18 \u4e86\u3002 \u3000\u3000\u51fa\u4e86\u6c99\u6f20\u53c8\u662f\u4e00\u756a\u666f\u8c61\uff0c\u8fdc\u8fdc\u671b\u53bb\uff0c\u7eff\u7eff\u7684\u4e00\u7247\u8349\uff0c\u4e00\u671b\u65e0\u9645\u7684\u7eff\u5230\u5929\u8fb9\u3002 \u3000\u3000\u674e\u601d\u5357\u60f3\u8d77\u4e86\u8bfb\u8fc7\u7684\u8bd7\uff1a\u201c\u5929\u82cd\u82cd\uff0c\u5730\u832b\u832b\uff0c\u98ce\u5439\u8349\u4f4e\u89c1\u725b\u7f8a\u3002\u201d\u65f6\u8282\u662f\u76db\u590f\u7684\u4e03\u6708\uff0c \u91ce\u8349\u6ca1\u6709\u4ed6\u60f3\u8c61\u7684\u5f97\u90a3\u4e48\u9ad8\uff0c\u5728\u8349\u539f\u4e0e\u6208\u58c1\u7684\u63a5\u58e4\u4e4b\u5904\uff0c\u4e5f\u672a\u66fe\u53d1\u73b0\u4e00\u7fa4\u7fa4\u7684\u725b\u7f8a\u3002\u4f46\u6208\u58c1 \u4e0a\u7684\u70ed\u98ce\u4e00\u9635\u9635\u5439\u5411\u8349\u539f\uff0c\u8349\u5728\u98ce\u4e2d\u8d77\u4f0f\u6446\u52a8\uff0c\u715e\u50cf\u662f\u6d77\u9762\u4e0a\u7684\u98ce\u6d6a\uff0c\u8fd9\u8349\u539f\u4e0a\u7684\u666f\u8c61\u4e5f\u662f \u6781\u5c3d\u58ee\u89c2\u4e4b\u81f3\u3002\u5c24\u5176\u674e\u601d\u5357\u662f\u4ece\u4e00\u7247\u9ec4\u6c99\u7684\u6208\u58c1\u4e0a\u8d70\u51fa\u6765\u7684\uff0c\u5982\u4eca\u770b\u5230\u5ef6\u5c55\u81f3\u5929\u8fb9\u7684\u4e00\u7247 \u7eff\uff0c\u5fc3\u60c5\u7684\u6109\u5feb\u81ea\u662f\u53ef\u60f3\u800c\u77e5\uff01 \u3000\u3000\u674e\u601d\u5357\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4e0d\u5230\u585e\u5916\uff0c\u4e0d\u77e5\u5929\u5730\u4e4b\u5927\u3002\u6b64\u8bdd\u5f53\u771f\u4e0d\u9519\u3002\u201d\u6b63\u5728\u89c2\u8d4f\u8349\u539f\u666f\u8272\uff0c \u5ffd\u542c\u5f97\u80cc\u540e\u9a6c\u94c3\u58f0\u54cd\uff0c\u98ce\u4e2d\u8fd8\u9001\u6765\u4e86\u4e24\u4e2a\u4eba\u8bf4\u8bdd\u7684\u58f0\u97f3\u3002 \u3000\u3000\u674e\u601d\u5357\u4fa7\u8033\u4e00\u542c\uff0c\u5148\u662f\u6240\u5f97\u4e00\u4e2a\u5973\u5b50\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u591a\u534a\u5c31\u662f\u524d\u9762\u8fd9\u5c0f\u5b50\u4e86\uff01\u201d\u968f\u5373\u542c \u5f97\u4e00\u4e2a\u7c97\u91cc\u7c97\u6c14\u7684\u7537\u5b50\u58f0\u97f3\uff0c\u53ea\u8bf4\u4e86\u4e00\u4e2a\u5b57\u201c\u8ffd\uff01\u201d\u90a3\u4e24\u9a91\u9a6c\u8fd8\u5728\u6570\u91cc\u5f00\u5916\uff0c\u8fdc\u8fdc\u671b\u53bb\uff0c\u8fd8 \u672a\u66fe\u770b\u5f97\u600e\u4e48\u6e05\u695a\uff0c\u53ef\u662f\u8fd9\u7537\u5b50\u7684\u4e00\u58f0\u65ad\u559d\uff0c\u5374\u5df2\u9707\u5f97\u674e\u601d\u5357\u7684\u8033\u9f13\u55e1\u55e1\u4f5c\u54cd\u3002 \u3000\u3000\u674e\u601d\u5357\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u4e24\u65e5\u4e4b\u95f4\uff0c\u7adf\u662f\u63a5\u8fde\u78b0\u7740\u6b66\u6797\u9ad8\u624b\u3002\u5374\u4e0d\u77e5\u4ed6\u4eec \u8981\u9053\u6211\u5e72\u4ec0\u4e48\uff1f\u201d\u5728\u8fd9\u8349\u539f\u4e0a\u7684\uff0c\u53ea\u6709\u674e\u601d\u5357\u4e00\u4eba\uff0c\u4e0d\u7528\u8bf4\u90a3\u4e2a\u5973\u5b50\u6240\u6307\u7684\u201c\u5c0f\u5b50\u201d\u5c31\u662f\u4ed6 \u4e86\u3002 \u3000\u3000\u90a3\u4e24\u4e2a\u4eba\u7684\u9a6c\u5feb\u5f97\u51fa\u5947\uff0c\u8f6c\u773c\u95f4\u5df2\u7ecf\u8ffd\u5230\u4e86\u674e\u601d\u5357\u7684\u80cc\u540e\u3002\u674e\u601d\u5357\u7d22\u6027\u505c\u4e0b\u6765\u7b49\u4ed6\u4eec\u3002 \u53ea\u89c1\u90a3\u4e2a\u7537\u7684\u7c97\u987b\u5982\u671d\uff0c\u76f8\u8c8c\u7c97\u8c6a\uff1b\u90a3\u4e2a\u5973\u7684\u5374\u662f\u4e2a\u59ff\u5bb9\u751a\u7f8e\u7684\u534a\u8001\u5f90\u5a18\u3002\u4e24\u4eba\u90fd\u662f\u6c49\u4eba\uff0c \u770b\u4ed6\u4eec\u5e76\u8f94\u800c\u884c\u7684\u4eb2\u5bc6\u60c5\u5f62\uff0c\u4f3c\u4e4e\u662f\u4e00\u5bf9\u592b\u5987\u3002 \u3000\u3000\u90a3\u4e2a\u7c97\u8c6a\u6c49\u5b50\u4e00\u5f00\u53e3\u5c31\u95ee\u9053\uff1a\u201c\u5c60\u767e\u57ce\u7684\u6bd2\u9f99\u9556\u662f\u4e0d\u662f\u5728\u4f60\u7684\u8eab\u4e0a\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u8bf7\u95ee\u5c0a\u9a7e\u4f55\u4eba\uff1f\u201d \u3000\u3000\u90a3\u7c97\u8c6a\u6c49\u5b50\u9053\uff1a\u201c\u4f60\u4e0d\u5fc5\u7ba1\u6211\u4eec\u662f\u8c01\uff0c\u6bd2\u9f99\u9556\u5feb\u5feb\u62ff\u6765\u7ed9\u6211\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u5fc3\u91cc\u6709\u70b9\u6c14\uff0c\u60f3\u9053\uff1a\u201c\u95ee\u4eba\u8ba8\u4e1c\u897f\u4e5f\u4e0d\u662f\u8fd9\u4e48\u8ba8\u6cd5\u3002\u201d\u5ffd\u5730\u5fc3\u5ff5\u4e00\u52a8\uff0c\u201c\u5b5f\u660e\u971e \u6628\u665a\u6240\u89c1\u7684\u90a3\u4e00\u7537\u4e00\u5973\uff0c\u83ab\u975e\u5c31\u662f\u4ed6\u4eec\uff1f\u201d \u3000\u3000\u5728\u585e\u5916\u96be\u5f97\u9047\u4e0a\u6c49\u4eba\uff0c\u674e\u601d\u5357\u53c8\u6709\u70b9\u597d\u5947\uff0c\u60f3\u8981\u77e5\u9053\u4ed6\u4eec\u7684\u6765\u5386\uff0c\u4e8e\u662f\u8bf4\u9053\uff1a\u201c\u4f60\u600e\u4e48 \u77e5\u9053\u6bd2\u9f99\u9556\u662f\u5728\u6211\u7684\u8eab\u4e0a\uff1f\u201d \u3000\u3000\u90a3\u7c97\u8c6a\u6c49\u5b50\u6012\u9053\uff1a\u201c\u6628\u65e5\u53ea\u6709\u4f60\u4ece\u90a3\u91cc\u7ecf\u8fc7\uff0c\u5c60\u767e\u57ce\u7684\u6bd2\u9f99\u9556\u5931\u4e86\uff0c\u8fd8\u4e0d\u662f\u4f60\u62ff\u662f\u8c01\uff1f \u54fc\uff0c\u8981\u4e0d\u662f\u6211\u770b\u4f60\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u51b3\u4e0d\u53ef\u80fd\u4f24\u5f97\u4e86\u5c60\u767e\u57ce\uff0c\u6211\u8fd8\u8981\u5f53\u4f60\u51f6\u624b\u529e\u5462\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6bd2\u9f99\u9556\u7684\u4e0b\u843d\u6211\u662f\u77e5\u9053\uff0c\u4e0d\u8fc7\u6211\u5012\u8981\u5148\u9a73\u4f60\u4e00\u9a73\u3002\u201d\u4e8e\u662f\u7b11\u9053\uff1a \u201c\u4f60\u8fd9\u8bf4\u8bdd\u53ef\u6709\u70b9\u6b20\u601d\u91cf\u4e86\uff0c\u7109\u77e5\u4e0d\u662f\u6740\u4ed6\u7684\u90a3\u4e2a\u4eba\u62ff\u53bb\u7684\u5462\uff1f\u201d \u3000\u3000\u90a3\u7c97\u8c6a\u6c49\u5b50\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u5929\u4e0b\u6709\u4f55\u4eba\u80fd\u591f\u6740\u5f97\u4e86\u5c60\u767e\u57ce\u81ea\u5df1\u8fd8\u4e0d\u53d7\u91cd\u4f24\u7684\uff1f \u6211\u4e5f\u67e5\u7a76\u8fc7\u5c60\u767e\u57ce\u7684\u6b7b\uff0c\u4ed6\u8eab\u4e0a\u6ca1\u6709\u5200\u5251\u4f24\u75d5\uff0c\u53ef\u77e5\u4e0d\u662f\u5f53\u573a\u8eab\u6b7b\uff0c\u800c\u662f\u53d7\u4e86\u5185\u4f24\uff0c\u60e8\u53d7\u6d41 \u6c99\u6240\u4e27\u3002\u90a3\u4eba\u6740\u4e86\u5c60\u767e\u57ce\uff0c\u81ea\u5df1\u4fdd\u5f97\u4f4f\u6027\u547d\u5df1\u662f\u4e07\u5e78\uff0c\u8fd8\u6562\u5e26\u7740\u91cd\u4f24\u53bb\u62ff\u5c60\u767e\u57ce\u7684\u6bd2\u9f99\u9556\uff1f \u4f60\u4e0d\u8981\u72e1\u8d56\u4e86\uff0c\u5feb\u5feb\u62ff\u51fa\u6765\u5427\uff01\u8001\u5b50\u6ca1\u5de5\u592b\u7b49\u4f60\uff01\u201d \u3000\u3000\u90a3\u5973\u5b50\u7b11\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4e0d\u8981\u5413\u7740\u4e86\u4ed6\uff0c\u5f85\u6211\u6765\u8bf4\u3002\u6211\u770b\u4f60\u4e5f\u662f\u4e00\u4f4d\u6b66\u6797\u4eba\u7269\uff0c\u4f60\u62ff\u4e86\u5c60 \u767e\u57ce\u7684\u6bd2\u9f99\u9556\uff0c\u60f3\u5fc5\u77e5\u9053\u5b83\u7684\u6765\u5386\u3002\u8fd9\u6697\u5668\u867d\u7136\u5389\u5bb3\uff0c\u5bf9\u4f60\u5374\u662f\u6beb\u65e0\u7528\u5904\u3002\u4f60\u7559\u5728\u8eab\u4e0a\uff0c\u53ea \u80fd\u662f\u4e00\u4e2a\u7978\u80ce\u3002\u6211\u529d\u4f60\u8fd8\u662f\u7ed9\u4e86\u6211\u4eec\u7684\u597d\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u7b11\u9053\uff1a\u201c\u597d\uff0c\u4f60\u4eec\u548c\u6211\u8bf4\u597d\u8bdd\uff0c\u6211\u4e5f\u5c31\u5bf9\u4f60\u8bf4\u5b9e\u8bdd\u4e86\u5427\u3002\u4e0d\u9519\uff0c\u6628\u65e5\u6211\u662f\u53d1\u73b0\u4e86 \u5c60\u767e\u57ce\u7684\u5c38\u4f53\uff0c\u6bd2\u9f99\u9556\u4e5f\u662f\u6211\u62ff\u8d70\u4e86\u7684\u3002\u4e0d\u8fc7\uff0c\u73b0\u5728\u5374\u4e0d\u662f\u5728\u6211\u7684\u8eab\u4e0a\u3002\u201d \u3000\u3000\u90a3\u7c97\u8c6a\u6c49\u5b50\u559d\u9053\uff1a\u201c\u90a3\u53c8\u662f\u8c01\u62ff\u8d70\u4e86\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u662f\u6c5f\u5357\u5927\u4fa0\u5b5f\u5c11\u521a\u62ff\u8d70\u4e86\u3002\u201d \u3000\u3000\u90a3\u7c97\u8c6a\u6c49\u5b50\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u548c\u5b5f\u5927\u4fa0\u76f8\u8bc6\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u8c08\u4e0d\u4e0a\u600e\u6837\u76f8\u8bc6\u3002\u4e0d\u8fc7\uff0c\u6628\u65e5\u4e5f\u662f\u4ed6\u6551\u4e86\u6211\u7684\u4e00\u6761\u6027\u547d\u3002\u201d\u5f53\u4e0b\uff0c\u5c06\u6628\u65e5 \u5728\u90a3\u6208\u58c1\u4e0a\u7684\u906d\u9047\u544a\u8bc9\u4e86\u8fd9\u5bf9\u592b\u5987\u3002\u7c97\u8c6a\u6c49\u5b50\u542c\u8bf4\u4ed6\u662f\u5b5f\u5c11\u521a\u6240\u6551\u7684\u4eba\uff0c\u4ece\u4ed6\u7684\u8bf4\u8bdd\u4e2d\u53c8\u77e5 \u9053\u4ed6\u662f\u5c11\u6797\u6d3e\u4fa0\u5ba2\u8c37\u5e73\u9633\u7684\u5f1f\u5b50\uff0c\u795e\u8272\u767b\u65f6\u6539\u53d8\uff0c\u548c\u6c14\u8bb8\u591a\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u4f60\u7684\u6765\u5386\uff0c\u591a \u6709\u5f97\u7f6a\u4e86\u3002\u6211\u59d3\u5b8b\uff0c\u540d\u94c1\u8f6e\u3002\u5979\u662f\u6211\u7684\u6d51\u5bb6\uff0c\u5728\u6c5f\u6e56\u4e0a\u4e5f\u6709\u70b9\u5c0f\u5c0f\u7684\u540d\u6c14\u2014\u2014\u201d\u90a3\u5973\u7684\u7b11 \u9053\uff1a\u201c\u4e0d\u5fc5\u4f60\u66ff\u6211\u5439\u725b\uff0c\u6211\u662f\u5c71\u4e1c\u5386\u57ce\u7684\u67f3\u4e09\u5a18\uff0c\u6216\u8bb8\u4f60\u542c\u8fc7\u6211\u4eec\u592b\u5987\u7684\u540d\u5b57\u3002\u201d \u3000\u3000\u539f\u6765\u5b8b\u94c1\u8f6e\u592b\u59bb\u90fd\u662f\u5c71\u4e1c\u8457\u540d\u7684\u5927\u76d7\uff0c\u67f3\u4e09\u5a18\u5c24\u5176\u8457\u540d\uff0c\u88ab\u4eba\u79f0\u4e3a\u6740\u4eba\u4e0d\u7728\u773c\u7684\u5973\u9b54 \u5934\u3002\u4e0d\u8fc7\u5979\u4e5f\u662f\u50cf\u5c60\u767e\u57ce\u4e00\u6837\u6740\u7684\u4e0d\u662f\u571f\u8c6a\u52a3\u7ec5\u5c31\u662f\u91d1\u56fd\u7684\u8d2a\u5b98\u3002 \u3000\u3000\u674e\u601d\u5357\u9053\u4e86\u4e00\u58f0\uff1a\u201c\u4e45\u4ef0\uff01\u201d\u95ee\u9053\uff1a\u201c\u4e24\u4f4d\u60f3\u5fc5\u548c\u5b5f\u5927\u4fa0\u76f8\u8bc6\uff1f\u201d \u3000\u3000\u5b8b\u94c1\u8f6e\u9053\uff1a\u201c\u5c60\u767e\u57ce\u662f\u6211\u4eec\u74e2\u628a\u5b50\uff08\u5f3a\u76d7\u5934\u5b50\uff09\uff0c\u5f53\u5e74\u5b5f\u5927\u4fa0\u5728\u6211\u4eec\u74e2\u628a\u5b50\u90a3\u513f\uff0c\u6211\u4eec \u4e5f\u66fe\u548c\u4ed6\u6709\u8fc7\u4e00\u9762\u4e4b\u7f18\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u90a3\u5c31\u597d\u529e\u4e86\u3002\u5b5f\u5927\u4fa0\u662f\u4eca\u671d\u8d70\u7684\uff0c\u8d70\u4e86\u4e0d\u8fc7\u534a\u5929\u3002\u4e24\u4f4d\u7684\u9a6c\u5feb\uff0c\u6599\u60f3\u5728\u672a \u51fa\u6208\u58c1\u4e4b\u524d\uff0c\u53ef\u4ee5\u8ffd\u5f97\u4e0a\u4ed6\u3002\u201d \u3000\u3000\u5b8b\u94c1\u8f6e\u9053\uff1a\u201c\u591a\u8c22\u6307\u70b9\u3002\u201d\u6b63\u8981\u56de\u5934\uff0c\u67f3\u4e09\u5a18\u5ffd\u9053\uff1a\u201c\u5927\u54e5\u4e14\u6162\uff01\u201d\u5b8b\u94c1\u8f6e\u6014\u4e86\u4e00\u6014\uff0c \u8bf4\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u67f3\u4e09\u5a18\u9053\uff1a\u201c\u6211\u8fd8\u6709\u4e00\u4e24\u4ef6\u4e8b\u60c5\u8981\u60f3\u8bf7\u6559\u8fd9\u4f4d\u674e\u76f8\u516c\u3002\u201d \u3000\u3000\u67f3\u4e09\u5a18\u8bf4\u5f97\u5f88\u5ba2\u6c14\uff0c\u674e\u601d\u5357\u5374\u662f\u611f\u5230\u6709\u70b9\u7a81\u5140\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u6562\u3002\u4e0d\u77e5\u67f3\u5973\u4fa0\u8981\u95ee\u7684\u662f\u4ec0 \u4e48\u4e8b\u60c5\uff1f\u201d \u3000\u3000\u67f3\u4e09\u5a18\u9053\uff1a\u201c\u90a3\u4e09\u652f\u6bd2\u9f99\u9556\u662f\u674e\u76f8\u516c\u8bf7\u5b5f\u5927\u4fa0\u5e26\u56de\u53bb\u7684\u5417\uff1f\u201d\u674e\u601d\u5357\u9053\uff1a\u201c\u5b5f\u5927\u4fa0\u548c\u6211 \u8bf4\u8fc7\u4ed6\u4e0e\u5c60\u767e\u57ce\u7684\u4ea4\u60c5\uff0c\u6211\u672c\u6765\u5c31\u60f3\u4ea4\u7ed9\u4ed6\u7684\u3002\u201d\u67f3\u4e09\u5a18\u9053\uff1a\u201c\u54e6\uff0c\u8fd9\u4e48\u8bf4\uff0c\u5c31\u662f\u4ed6\u8fd8\u6ca1\u6709 \u95ee\u8fc7\u4f60\u4e86\u3002\u201d \u3000\u3000\u67f3\u4e09\u5a18\u7591\u5fc3\u987f\u8d77\uff0c\u8bf4\u9053\uff1a\u201c\u5b5f\u5927\u4fa0\u4eca\u65e9\u8d70\u7684\u65f6\u5019\uff0c\u674e\u76f8\u516c\u60f3\u5fc5\u8fd8\u5728\u68a6\u4e2d\uff0c\u672a\u66fe\u77e5\u9053\uff1f\u201d \u3000\u3000\u8981\u77e5\u67f3\u4e09\u5a18\u662f\u4e2a\u7cbe\u7ec6\u7684\u4eba\uff0c\u5979\u76d8\u95ee\u4e86\u674e\u601d\u5357\uff0c\u5df2\u77e5\u90a3\u4e09\u652f\u6bd2\u9f99\u9556\u662f\u5b5f\u5c11\u521a\u4e0d\u544a\u800c\u53d6\u7684\uff0c \u636e\u6b64\u63a8\u60f3\uff0c\u4ed6\u5f53\u7136\u4e5f\u662f\u4e0d\u522b\u800c\u884c\u7684\u4e86\u3002\u5426\u5219\u4ee5\u5b5f\u5c11\u521a\u7684\u8eab\u4efd\uff0c\u4f55\u81f3\u4e8e\u62ff\u4e86\u5c0f\u8f88\u7684\u4e1c\u897f\uff0c\u5374\u4e0d \u544a\u8bc9\u4ed6\u3002 \u3000\u3000\u674e\u601d\u5357\u6b63\u662f\u4e3a\u6b64\u82e6\u607c\uff0c\u4e8e\u662f\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\u3002\u6211\u672c\u6765\u6709\u6869\u4e8b\u60c5\uff0c\u60f3\u5411\u5b5f\u5927\u4fa0\u8bf7\u6559\u7684\uff0c\u4ed6\u8d70 \u5f97\u5306\u5fd9\uff0c\u4ee5\u81f4\u6211\u5df2\u6ca1\u6709\u673a\u4f1a\u4e86\u3002\u201d \u3000\u3000\u5b8b\u94c1\u8f6e\u662f\u4e2a\u723d\u76f4\u3001\u6025\u8e81\u7684\u6027\u5b50\uff0c\u7acb\u5373\u8bf4\u9053\uff1a\u201c\u53cd\u6b63\u6211\u4eec\u662f\u8981\u53bb\u89c1\u5b5f\u5927\u4fa0\u7684\uff0c\u4f60\u6709\u4ec0\u4e48\u4e8b \u60c5\uff0c\u6211\u66ff\u4f60\u8f6c\u544a\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u5374\u662f\u4e2a\u8c28\u614e\u7684\u4eba\uff0c\u4e00\u6765\u4ed6\u4e0e\u8fd9\u5bf9\u592b\u59bb\u7d20\u4e0d\u76f8\u8bc6\uff0c\u4e0d\u4fbf\u4ea4\u6d45\u8a00\u6df1\uff1b\u4e8c\u6765\u4ed6\u8981\u8bf4\u4e5f\u4e0d \u77e5\u4ece\u54ea\u91cc\u8bf4\u8d77\uff0c\u603b\u4e0d\u6210\u5c31\u544a\u8bc9\u4ed6\u4eec\u5b5f\u5c11\u521a\u8981\u60f3\u6740\u4ed6\uff0c\u8fd9\u53ea\u6709\u5f92\u589e\u5bf9\u65b9\u7684\u7591\u731c\u800c\u5df2\u3002\u4e8e\u662f\u674e\u601d \u5357\u53e5\u659f\u5b57\u914c\u5730\u8bf4\u9053\uff1a\u201c\u4e0d\u6562\u6709\u52b3\u4e24\u4f4d\u3002\u6211\u53ea\u60f3\u8bf7\u4e24\u4f4d\u4ee3\u6211\u591a\u8c22\u5b5f\u5927\u4fa0\uff0c\u6211\u4e00\u5b9a\u4f1a\u4f9d\u4ece\u4ed6\u7684\u5429 \u5490\u3002\u53ea\u8981\u8fd9\u4e48\u8bf4\uff0c\u5b5f\u5927\u4fa0\u5c31\u660e\u767d\u4e86\u3002\u201d \u3000\u3000\u5b8b\u94c1\u8f6e\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u5c0f\u5b50\u5a46\u5a46\u5988\u5988\uff0c\u6beb\u4e0d\u723d\u5feb\u3002\u6562\u60c5\u4ed6\u8fd8\u76f8\u4fe1\u4e0d\u8fc7\u6211 \u54e9\uff01\u201d\u53ea\u56e0\u674e\u601d\u5357\u544a\u8bc9\u4e86\u4ed6\u4eec\u5173\u4e8e\u5b5f\u5927\u4fa0\u7684\u6d88\u606f\uff0c\u4e8e\u4ed6\u6709\u6307\u70b9\u4e4b\u6069\uff0c\u5b8b\u94c1\u8f6e\u8fd9\u624d\u6ca1\u6709\u53d1\u4f5c\uff0c \u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u597d\u5427\uff0c\u6211\u9886\u4e86\u4f60\u7684\u4eba\u60c5\uff0c\u4f60\u8981\u6211\u600e\u4e48\u8bf4\uff0c\u6211\u4e00\u53e5\u4e0d\u591a\u4e00\u53e5\u4e0d\u5c11\u5730\u66ff\u4f60\u4f20\u8bdd\u5c31 \u662f\u3002\u4e09\u5a18\uff0c\u8d70\u5427\uff01\u201d \u3000\u3000\u67f3\u4e09\u5a18\u7b11\u9053\uff1a\u201c\u4f60\u6025\u4ec0\u4e48\uff0c\u6211\u8fd8\u8981\u8bf7\u95ee\u674e\u76f8\u516c\u4e00\u6869\u4e8b\u60c5\u5462\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u610f\u6b32\u907f\u514d\u7ed9\u4ed6\u4eec\u7591\u731c\uff0c\u6b8a\u4e0d\u77e5\u67f3\u4e09\u5a18\u5df2\u7ecf\u8d77\u4e86\u7591\u731c\u3002\u201c\u8bf7\u95ee\u674e\u76f8\u516c\u8fdc\u6765\u8499\u53e4\uff0c\u4e3a \u4e86\u4f55\u4e8b\uff1f\u6055\u6211\u5192\u5473\uff0c\u53ef\u80af\u89c1\u544a\u67f3\u4e09\u5a18\u8bf4\u3002\u201d \u3000\u3000\u67f3\u4e09\u5a18\u8bf4\u5f97\u8fd9\u6837\u5ba2\u6c14\uff0c\u674e\u601d\u5357\u4e8e\u7406\u4e8e\u60c5\uff0c\u5374\u662f\u4e0d\u80fd\u4e0d\u7b54\uff0c\u5fc3\u60f3\uff1a\u201c\u4ed6\u4eec\u89c1\u4e86\u5b5f\u5927\u4fa0\uff0c\u53cd \u6b63\u4e5f\u4f1a\u77e5\u9053\u3002\u6211\u544a\u8bc9\u4ed6\u53c8\u6709\u4f55\u59a8\uff1f\u201d \u3000\u3000\u67f3\u4e09\u5a18\u542c\u8bf4\u4ed6\u662f\u5230\u8499\u53e4\u5bfb\u7236\uff0c\u7d27\u63a5\u7740\u53c8\u95ee\uff1a\u201c\u4ee4\u5c0a\u5927\u540d\uff0c\u80fd\u5426\u793a\u77e5\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u5bb6\u7236\u540d\u8bb3\uff0c\u662f\u4e0a\u5e0c\u4e0b\u6d69\u8fd9\u4e24\u4e2a\u5b57\u3002\u201d \u3000\u3000\u5b8b\u94c1\u8f6e\u4e00\u542c\u4ed6\u8bf4\u51fa\u674e\u5e0c\u6d69\u7684\u540d\u5b57\uff0c\u5ffd\u5730\u9762\u8272\u4e00\u53d8\uff0c\u559d\u9053\uff1a\u201c\u539f\u6765\u4f60\u662f\u674e\u5e0c\u6d69\u7684\u513f\u5b50\u3002 \u54a6\uff0c\u5b5f\u5c11\u521a\u4e3a\u4f55\u4e0d\u6740\u4f60\uff0c\u8fd9\u624d\u771f\u662f\u602a\u4e8b\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u5927\u5403\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u6211\u662f\u674e\u67d0\u4eba\u7684\u513f\u5b50\uff0c\u5b5f\u5927\u4fa0\u5c31\u8981\u6740\u6211\uff1f\u6211\u6b63\u4e3a\u6b64\u4e8b\u4e0d \u660e\uff0c\u4e24\u4f4d\u53ef\u5426\u89c1\u544a\uff1f\u201d\u4ed6\u6025\u4e8e\u6253\u7834\u8fd9\u4e2a\u846b\u82a6\uff0c\u4e0d\u5047\u601d\u7d22\u5c31\u53d1\u95ee\u4e86\u3002 \u3000\u3000\u67f3\u4e09\u5a18\u683c\u683c\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u5b5f\u5c11\u521a\u679c\u7136\u662f\u66fe\u7ecf\u60f3\u8fc7\u8981\u6740\u4f60\u7684\u4e86\u3002\u201d \u3000\u3000\u5b8b\u94c1\u8f6e\u84e6\u5730\u4e00\u58f0\u5927\u543c\uff0c\u559d\u9053\uff1a\u201c\u5b5f\u5c11\u521a\u4e0d\u6740\u4f60\uff0c\u6211\u6765\u6740\u4f60\uff01\u201d\u4ed6\u540d\u53eb\u201c\u94c1\u8f6e\u201d\uff0c\u4f7f\u7684\u4e5f \u662f\u4e00\u5bf9\u94c1\u8f6e\uff0c\u53cc\u8f6e\u7f69\u9876\uff0c\u731b\u5730\u5c31\u5411\u674e\u601d\u5357\u5f53\u5934\u7838\u4e0b\u3002 \u3000\u3000\u67f3\u4e09\u5a18\u51fa\u624b\u66f4\u5feb\uff0c\u201c\u547c\u201d\u7684\u4e00\u58f0\uff0c\u4e00\u6761\u8f6f\u97ad\u5df2\u7136\u5377\u5230\uff0c\u53eb\u9053\uff1a\u201c\u5927\u54e5\uff0c\u7559\u6d3b\u53e3\u3002\u201d\u5979\u5012 \u662f\u4e0d\u60f3\u6740\u674e\u601d\u5357\uff0c\u800c\u662f\u8981\u628a\u4ed6\u62d6\u4e0b\u9a6c\u6765\u3002 \u3000\u3000\u674e\u601d\u5357\u51b7\u4e0d\u9632\uff0c\u5927\u5403\u4e00\u60ca\u3002\u53ef\u662f\u4ed6\u6bd5\u7adf\u662f\u5c11\u6797\u6d3e\u6b66\u5b66\u540d\u5bb6\u7684\u9ad8\u8db3\uff0c\u5c31\u5728\u90a3\u95f4\u4e0d\u5bb9\u53d1\u4e4b \u9645\uff0c\u500f\u5730\u817e\u8eab\u98de\u8d77\uff0c\u5982\u7bad\u79bb\u5f26\uff0c\u201c\u98de\u201d\u51fa\u4e86\u4e09\u4e08\u5f00\u5916\uff0c\u4f46\u9976\u662f\u5982\u6b64\uff0c\u4ed6\u7684\u8863\u895f\u4ea6\u5df2\u88ab\u67f3\u4e09\u5a18 \u7684\u8f6f\u97ad\u6495\u53bb\u4e86\u4e00\u5e45\u3002 \u3000\u3000\u5b8b\u94c1\u8f6e\u7ffb\u8eab\u4e0b\u9a6c\uff0c\u8ddf\u8e2a\u6251\u5230\uff0c\u674e\u601d\u5357\u53eb\u9053\uff1a\u201c\u4f60\u8981\u6740\u6211\uff0c\u4e5f\u5f97\u2014\u2014\u201d\u5e95\u4e0b\u201c\u8ba9\u6211\u660e\u767d\u201d \u8fd9\u56db\u4e2a\u5b57\u672a\u66fe\u51fa\u53e3\uff0c\u5b8b\u94c1\u8f6e\u7684\u53cc\u8f6e\u631f\u7740\u52b2\u98ce\uff0c\u5df2\u7ecf\u662f\u6253\u4e86\u5230\u6765\u3002 \u3000\u3000\u674e\u601d\u5357\u662f\u5403\u8f6f\u4e0d\u5403\u786c\u7684\u813e\u6c14\uff0c\u800c\u4e14\u5b8b\u94c1\u8f6e\u7684\u53cc\u8f6e\u5df2\u7ecf\u6253\u5230\uff0c\u518d\u4e0d\u8fd8\u624b\uff0c\u53ea\u80fd\u6d3b\u6d3b\u7ed9\u4ed6\u6253 \u6b7b\uff0c\u674e\u601d\u5357\u65e0\u6687\u5206\u8fa9\uff0c\u62d4\u5251\u51fa\u9798\uff0c\u8fc5\u5373\u8fd8\u62db\u3002 \u3000\u3000\u5251\u5149\u4e00\u95ea\uff0c\u53ea\u542c\u5f97\u201c\u94db\u201d\u7684\u4e00\u58f0\uff0c\u706b\u82b1\u56db\u6e85\uff0c\u968f\u5373\u662f\u201c\u55e4\u201d\u7684\u4e00\u54cd\uff0c\u5b8b\u94c1\u8f6e\u5934\u4e0a\u7684\u76ae\u5e3d \u7a7f\u4e86\u4e00\u4e2a\u6d1e\u3002\u539f\u6765\u674e\u601d\u5357\u5148\u7528\u5251\u80cc\u6321\u4e00\u6321\u4ed6\u7684\u94c1\u8f6e\uff0c\u95ea\u7535\u822c\u5730\u628a\u5251\u4e00\u7ffb\uff0c\u523a\u7a7f\u4e86\u4ed6\u5934\u4e0a\u7684\u76ae \u5e3d\u3002 \u3000\u3000\u674e\u601d\u5357\u907f\u62db\u3001\u62d4\u5251\u3001\u8fce\u51fb\u3001\u53cd\u653b\uff0c\u56db\u4e2a\u52a8\u4f5c\uff0c\u4e00\u6c14\u5475\u6210\uff0c\u8fc5\u6377\u65e0\u6bd4\u3002\u8fd9\u8fd8\u662f\u4ed6\u624b\u4e0b\u7559 \u60c5\uff0c\u5426\u5219\u5b8b\u94c1\u8f6e\u7684\u5929\u7075\u76d6\u5373\u4f7f\u4e0d\u7ed9\u523a\u7a7f\uff0c\u4e00\u5c42\u5934\u76ae\u662f\u5b9a\u8981\u7ed9\u4ed6\u524a\u53bb\u7684\u4e86\u3002 \u3000\u3000\u5b8b\u94c1\u8f6e\u5934\u4e0a\u7684\u76ae\u5e3d\u7a7f\u4e86\u4e00\u4e2a\u6d1e\uff0c\u4ed6\u81ea\u5df1\u770b\u4e0d\u89c1\uff0c\u4e0d\u77e5\u6050\u60e7\uff0c\u4ed6\u7684\u59bb\u5b50\u67f3\u4e09\u5a18\u53ef\u662f\u5927\u5927\u5403 \u4e86\u4e00\u60ca\uff0c\u8fde\u5fd9\u4e5f\u8df3\u4e0b\u9a6c\u6765\u3002\u5979\u770b\u51fa\u4e86\u674e\u601d\u5357\u7684\u672c\u9886\u8fdc\u8fdc\u5728\u5979\u4e08\u592b\u4e4b\u4e0a\uff0c\u4e5f\u5c31\u987e\u4e0d\u5f97\u6c5f\u6e56\u4e0a\u6210 \u540d\u4eba\u7269\u7684\u8eab\u4efd\uff0c\u4e0a\u6765\u548c\u4e08\u592b\u5939\u653b\u4ed6\u4e86\u3002 \u3000\u3000\u5b8b\u94c1\u8f6e\u53eb\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u662f\u975e\u6740\u4e0d\u53ef\u7684\uff0c\u81ea\u4eec\u6ca1\u6709\u529f\u592b\u76d8\u95ee\u4ed6\uff0c\u4e5f\u65e0\u9700\u76d8\u95ee\u4ed6\uff0c\u5e72\u8106\u6740\u4e86 \u5c31\u7b97\u5427\uff01\u201d \u3000\u3000\u67f3\u4e09\u5a18\u9053\uff1a\u201c\u597d\u5427\uff0c\u4f9d\u4f60\u5c31\u662f\uff01\u201d\u4e00\u62db\u201c\u56de\u98ce\u626b\u67f3\u201d\u8fde\u73af\u4e09\u97ad\uff0c\u547c\u547c\u98ce\u54cd\uff0c\u5377\u8d77\u4e86\u4e00\u56e2 \u97ad\u5f71\uff0c\u970d\u5730\u626b\u6765\u3002 \u3000\u3000\u67f3\u4e09\u5a18\u7684\u8f6f\u97ad\u97ad\u6cd5\u7cbe\u5947\uff0c\u64c5\u80fd\u4ee5\u67d4\u514b\u521a\uff0c\u672c\u9886\u6bd4\u5979\u7684\u4e08\u592b\u9ad8\u660e\u5f97\u591a\uff0c\u5979\u4e00\u4e0a\u6765\uff0c\u767b\u65f6\u5c31 \u5927\u5360\u4e0a\u98ce\uff0c\u628a\u674e\u601d\u5357\u6253\u5f97\u5fd9\u4e8e\u62db\u67b6\uff0c\u66f4\u662f\u4e0d\u80fd\u5206\u5fc3\u8bf4\u8bdd\u4e86\u3002 \u3000\u3000\u674e\u601d\u5357\u5c55\u5f00\u4e86\u5c11\u6797\u6d3e\u5ae1\u4f20\u7684\u8fbe\u6469\u5251\u6cd5\uff0c\u6c89\u7740\u5e94\u4ed8\uff0c\u5e94\u4ed8\u4e86\u4e09\u4e94\u5341\u62db\u3002\u53ef\u662f\u67f3\u4e09\u5a18\u7684\u8fd9\u6761 \u8f6f\u97ad\u77eb\u82e5\u6e38\u9f99\uff0c\u6307\u4e1c\u6253\u897f\uff0c\u6307\u5357\u6253\u5317\uff0c\u5b9e\u5728\u96be\u4ee5\u5e94\u4ed8\u3002\u5b8b\u94c1\u8f6e\u7684\u62db\u6570\u867d\u7136\u8f83\u5f31\uff0c\u4f46\u4ed6\u7684\u6c14\u529b \u5374\u662f\u5f88\u5927\u3002 \u3000\u3000\u5018\u82e5\u662f\u4ed6\u548c\u674e\u601d\u5357\u867d\u6253\u72ec\u6597\uff0c\u674e\u601d\u5357\u4ed7\u7740\u5251\u672f\u8f7b\u7075\uff0c\u53ef\u4ee5\u201c\u4ee5\u5de7\u964d\u529b\u201d\uff0c\u4e0d\u5fc5\u600e\u4e48\u8d39\u529b \u5c31\u53ef\u4ee5\u5236\u4f0f\u4ed6\u3002\u4f46\u5982\u4eca\u4ed6\u662f\u548c\u67f3\u4e09\u5a18\u8054\u624b\u5939\u653b\u4e4b\u4e0b\uff0c\u67f3\u4e09\u5a18\u7684\u97ad\u6cd5\u6bd4\u674e\u601d\u5357\u7684\u5251\u6cd5\u8fd8\u8981\u8f7b\u7075 \u5de7\u5999\uff0c\u674e\u601d\u5357\u5168\u529b\u5e94\u4ed8\u67f3\u4e09\u5a18\u8fd8\u611f\u4e0d\u8db3\uff0c\u8fd9\u4e48\u4e00\u6765\uff0c\u4ed6\u7684\u6c14\u529b\u53ef\u5c31\u5927\u6709\u65bd\u5c55\u7684\u4f59\u5730\u4e86\u3002\u4ed6\u4ed7 \u7740\u68b0\u91cd\u529b\u6c89\uff0c\u4e00\u5bf9\u94c1\u8f6e\uff0c\u5728\u97ad\u5f71\u7ffb\u98de\u7684\u63a9\u62a4\u4e4b\u4e0b\uff0c\u51b2\u5411\u674e\u601d\u5357\u731b\u7838\u731b\u6253\u3002\u674e\u601d\u5357\u8fd8\u5f53\u771f\u4e0d\u6562 \u8ba9\u81ea\u5df1\u7684\u9752\u94a2\u5251\u7ed9\u4ed6\u78b0\u7740\u3002 \u3000\u3000\u8fc7\u4e86\u4e94\u5341\u62db\uff0c\u674e\u601d\u5357\u5df2\u662f\u5927\u6c57\u6dcb\u6dcb\uff0c\u6e10\u611f\u4e0d\u652f\uff0c\u67f3\u4e09\u5a18\u559d\u9053\uff1a\u201c\u5c0f\u5b50\uff0c\u629b\u5251\u5427\uff01\u8fd8\u8981\u987d \u6297\u4e48\uff1f\u201d\u8a00\u4e0b\u4e4b\u6025\uff0c\u674e\u601d\u5357\u629b\u5251\u6295\u964d\uff0c\u6216\u8bb8\u5979\u8fd8\u53ef\u4ee5\u9976\u4ed6\u4e00\u547d\u3002 \u3000\u3000\u674e\u601d\u5357\u8fd9\u67c4\u5b9d\u5251\u662f\u4ed6\u5e08\u7236\u4ece\u524d\u7684\u4f69\u5251\uff0c\u4f20\u5251\u4e4b\u65f6\uff0c\u674e\u601d\u5357\u66fe\u7ecf\u5411\u4ed6\u5e08\u7236\u7acb\u8fc7\u8a93\uff1a\u201c\u5251\u5728 \u4eba\u5728\uff0c\u5251\u4ea1\u4eba\u4ea1\uff01\u201d\u800c\u4e14\u7ed9\u4eba\u7f34\u68b0\uff0c\u4e43\u662f\u6b66\u6797\u4e2d\u8ba4\u4e3a\u6700\u662f\u5947\u803b\u5927\u8fb1\u7684\u4e8b\u60c5\uff0c\u674e\u601d\u5357\u5982\u4f55\u80af\u5f03 \u5251\u6295\u964d\u3002 \u3000\u3000\u674e\u601d\u5357\u559d\u9053\uff1a\u201c\u5927\u4e08\u592b\u5b81\u6b7b\u4e0d\u8fb1\uff01\u201d\u54ac\u7259\u82e6\u6218\u3002\u5b8b\u94c1\u8f6e\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u4e5f\u914d\u79f0\u5927\u4e08\u592b\uff01\u201d \u6b3a\u8eab\u62a2\u8fdb\uff0c\u627e\u4ed6\u7684\u9752\u94a2\u5251\u786c\u7838\u3002 \u3000\u3000\u674e\u601d\u5357\u5fc3\u5934\u706b\u8d77\uff0c\u4e00\u4e2a\u201c\u76d8\u9f99\u7ed5\u6b65\u201d\u907f\u5f00\uff0c\u6b63\u8981\u8fd8\u62db\uff0c\u7ed9\u4ed6\u4e00\u70b9\u5389\u5bb3\uff0c\u67f3\u4e09\u5a18\u7684\u8f6f\u97ad\u5df1 \u662f\u4e58\u9699\u6253\u4e86\u8fdb\u6765\uff0c\u201c\u5530\u201d\u7684\u4e00\u97ad\uff0c\u5728\u674e\u601d\u5357\u7684\u5de6\u81c2\u4e0a\u62bd\u4e86\u4e00\u9053\u8840\u75d5\uff01 \u3000\u3000\u67f3\u4e09\u5a18\u67d4\u58f0\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b50\u5012\u8fd8\u6709\u70b9\u786c\u6c14\uff0c\u600e\u4e48\uff0c\u8fd8\u4e0d\u670d\u4e48\uff1f\u201d\u674e\u601d\u5357\u559d\u9053\uff1a\u201c\u4e0d \u670d\uff01\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u84e6\u7684\u4e00\u97ad\uff0c\u67f3\u4e09\u5a18\u53c8\u628a\u4ed6\u7684\u53f3\u80a9\u6253\u51fa\u4e86\u4e00\u9053\u8840\u75d5\uff0c\u4ecd\u662f\u67d4\u58f0\u7b11\u9053\uff1a\u201c\u4e0d\u7528 \u5341\u97ad\uff0c\u4f60\u5c31\u8981\u6d51\u8eab\u7834\u788e\uff0c\u904d\u4f53\u9cde\u4f24\uff0c\u90a3\u65f6\u4f60\u518d\u6c42\u9976\uff0c\u53ef\u5c31\u8fdf\u4e86\uff01\u201d\u5979\u8c08\u7b11\u81ea\u5982\uff0c\u597d\u50cf\u548c\u4e00\u4e2a \u8001\u670b\u53cb\u8bf4\u8bdd\uff0c\u97ad\u6cd5\u53ef\u662f\u72e0\u8fa3\u65e0\u6bd4\uff0c\u62db\u62db\u90fd\u8574\u6740\u673a\u3002\u5979\u5728\u5c71\u4e1c\u9ed1\u9053\u4e0a\u7ed9\u4eba\u79f0\u4e3a\u6740\u4eba\u4e0d\u7728\u773c\u7684\u5973 \u9b54\u5934\uff0c\u56fa\u7136\u5979\u6240\u6740\u7684\u90fd\u662f\u654c\u4eba\u6216\u8005\u5979\u5fc3\u76ee\u4e2d\u8ba4\u4e3a\u662f\u654c\u4eba\u7684\u4eba\uff0c\u4f46\u8fd9\u4e2a\u7ef0\u53f7\u4e5f\u7684\u786e\u4e0d\u662f\u51a4\u6789\u5979 \u7684\u3002 \u3000\u3000\u674e\u601d\u5357\u5fcd\u7740\u75bc\u75db\uff0c\u5927\u53eb\u9053\uff1a\u201c\u4f60\u6740\u6b7b\u6211\uff0c\u6211\u4e5f\u4e0d\u670d\uff01\u54fc\uff0c\u6211\u53ea\u9053\u4f60\u4eec\u662f\u6c5f\u6e56\u597d\u6c49\uff0c\u8c01\u77e5 \u5982\u6b64\u4e0d\u5206\u7682\u767d\u2014\u2014\u201d\u6b64\u65f6\u4ed6\u5df2\u662f\u7b4b\u75b2\u529b\u7aed\uff0c\u8bf4\u8bdd\u7684\u58f0\u97f3\u7531\u5927\u800c\u5c0f\uff0c\u8bf4\u5230\u6700\u540e\u7684\u51e0\u4e2a\u5b57\uff0c\u5df2\u662f \u542b\u7cca\u4e0d\u6e05\u3002 \u3000\u3000\u67f3\u4e09\u5a18\u6014\u4e86\u4e00\u6014\uff0c\u7b2c\u4e09\u97ad\u6253\u4e86\u51fa\u53bb\uff0c\u5374\u505c\u5728\u7a7a\u4e2d\uff0c\u6b63\u60f3\u518d\u5411\u674e\u601d\u5357\u76d8\u95ee\uff0c\u5ffd\u542c\u5f97\u9a6c\u94c3\u58f0 \u54cd\uff0c\u8349\u539f\u4e0a\u53c8\u6765\u4e86\u4e24\u9a91\u3002 \u3000\u3000\u5b8b\u94c1\u8f6e\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u7684\u6551\u5175\u6765\u4e86\uff0c\u5feb\u5feb\u5c06\u4ed6\u51fb\u6bd9\uff01\u201d\u67f3\u4e09\u5a18\u94f6\u7259\u4e00\u54ac\uff0c\u5fc3\u9053\uff1a\u201c\u5b81\u53ef\u51a4 \u6789\u4e86\u4ed6\uff0c\u4e0d\u80fd\u8ba9\u4ed6\u8d70\u6389\u3002\u201d\u7b2c\u4e09\u97ad\u6253\u4e0b\uff0c\u8fd9\u4e00\u97ad\u6b63\u51fb\u7740\u674e\u601d\u5357\u7684\u5c0f\u8179\uff0c\u767b\u65f6\u628a\u4ed6\u6253\u5f97\u773c\u775b\u53d1 \u9ed1\uff0c\u5730\u8f6c\u5929\u65cb\u3002 \u3000\u3000\u674e\u601d\u5357\u5fc3\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u6211\u4e0d\u660e\u4e0d\u767d\u5730\u6b7b\u5728\u8fd9\u513f\uff01\u201d\u8c01\u77e5\u66f4\u60f3\u4e0d\u5230\u7684\u662f\uff0c\u6765\u7684\u679c\u7136\u662f\u4ed6\u7684 \u6551\u5175\uff01\u6b63\u662f\uff1a \u3000\u3000- \u3000\u3000\u4e0d\u6599\u98ce\u6ce2\u5e73\u5730\u8d77\uff0c\u51e0\u756a\u5947\u9047\u53cd\u7cca\u6d82\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b\u2014\u2014\u7b2c\u4e09\u56de\u3000\u7ef5\u5e10\u56e2\u5706\u4f24\u53d8\u8282\u3000\u6b8b\u7bc7\u672a\u7eed\u5fc6\u524d\u5c18 \u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b \u7b2c\u4e09\u56de\u3000\u7ef5\u5e10\u56e2\u5706\u4f24\u53d8\u8282\u3000\u6b8b\u7bc7\u672a\u7eed\u5fc6\u524d\u5c18 \u3000\u3000\u674e\u601d\u5357\u7ed9\u67f3\u4e09\u5a18\u63a5\u8fde\u6253\u4e86\u4e09\u97ad\uff0c\u6253\u5f97\u4ed6\u5934\u660f\u773c\u82b1\uff0c\u767e\u9ab8\u6b32\u6563\uff0c\u773c\u770b\u770b\u5b8b\u94c1\u8f6e\u7684\u53cc\u8f6e\u53c8\u5df2 \u63a8\u538b\u8fc7\u6765\uff0c\u674e\u601d\u5357\u6beb\u65e0\u62db\u67b6\u4e4b\u529b\uff0c\u552f\u6709\u95ed\u76ee\u5f85\u6b7b\uff0c\u54ea\u77e5\u5c31\u5728\u4ed6\u6447\u6447\u6b32\u5760\u4e4b\u9645\uff0c\u5ffd\u89c9\u8eab\u5b50\u4e00 \u8f7b\uff0c\u5df2\u662f\u7ed9\u4eba\u62b1\u4e86\u8d77\u6765\u3002\u539f\u6765\u90a3\u4e24\u4e2a\u6b66\u58eb\u6765\u5230\uff0c\u4e00\u4e2a\u7528\u94fe\u5b50\u9524\u78d5\u5f00\u4e86\u94c1\u8f6e\uff0c\u53e6\u4e00\u4e2a\u5c31\u628a\u4ed6\u6551 \u51fa\u4e86\u9669\u5883\u3002 \u3000\u3000\u9524\u8f6e\u78b0\u51fb\u706b\u82b1\u7535\u95ea\uff0c\u8f70\u82e5\u96f7\u9e23\u3002\u5b8b\u94c1\u8f6e\u864e\u53e3\u9178\u9ebb\uff0c\u4f4e\u5934\u4e00\u770b\uff0c\u5de6\u624b\u7684\u90a3\u53ea\u8f6e\u5b50\u5df2\u88ab\u6253\u7f3a \u4e00\u89d2\u3002\u5b8b\u94c1\u8f6e\u7d20\u6765\u4ee5\u6c14\u529b\u81ea\u8d1f\uff0c\u4e0d\u7981\u5927\u5403\u4e00\u60ca\u3002 \u3000\u3000\u67f3\u4e09\u5a18\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u53bb\u6740\u90a3\u5c0f\u8d3c\uff0c\u8fd9\u9791\u5b50\u4ea4\u7ed9\u6211\u5427\uff01\u201d\u4e00\u62db\u201c\u56de\u98ce\u626b\u67f3\u201d\uff0c\u8f6f\u97ad\u957f\u86c7 \u822c\u5730\u5377\u5c06\u8fc7\u53bb\u3002\u90a3\u8499\u53e4\u6b66\u58eb\u559d\u9053\uff1a\u201c\u597d\u72e0\u7684\u5a46\u5a18\uff01\u201d\u4ed6\u53f3\u624b\u62ff\u7684\u662f\u67c4\u94fe\u5b50\u9524\uff0c\u5de6\u624b\u63d0\u7684\u662f\u628a \u94c1\u80ce\u5f13\u3002\u94fe\u5b50\u9524\u5df2\u7528\u6765\u5bf9\u4ed8\u5b8b\u94c1\u8f6e\uff0c\u53ea\u5f97\u5c06\u90a3\u628a\u94c1\u80ce\u5f13\u7528\u4f5c\u5e94\u4ed8\u67f3\u4e09\u5a18\u7684\u5175\u5668\u3002 \u3000\u3000\u67f3\u4e09\u5a18\u7684\u97ad\u6cd5\u8f7b\u7075\u5947\u5de7\uff0c\u97ad\u68a2\u4e00\u7ed9\u62e8\u5f00\uff0c\u767b\u65f6\u53c8\u53d8\u4f5c\u4e86\u201c\u6bd2\u86c7\u5410\u4fe1\u201d\u7684\u62db\u6570\uff0c\u8f6f\u97ad\u6296\u5f97 \u7b14\u76f4\uff0c\u70b9\u90a3\u8499\u53e4\u6b66\u58eb\u7684\u7a74\u9053\u3002\u8499\u53e4\u6b66\u58eb\u559d\u9053\uff1a\u201c\u6765\u5f97\u597d\uff01\u201d\u4e3e\u8d77\u94c1\u5f13\uff0c\u4e00\u5957\u4e00\u62c9\uff0c\u201c\u535c\u201d\u7684 \u4e00\u58f0\u54cd\uff0c\u8f6f\u97ad\u5377\u4e0a\u4e86\u5f13\u5f26\u3002 \u3000\u3000\u8fd9\u662f\u8499\u53e4\u7279\u6709\u4e2d\u571f\u6240\u65e0\u7684\u2018\u91d1\u5f13\u5341\u516b\u6253\u201d\u7684\u62db\u6570\u3002\u8499\u53e4\u4eba\u957f\u4e8e\u9a91\u9a6c\u5c04\u7bad\uff0c\u5f13\u7bad\u662f\u4ed6\u4eec\u6700 \u5e38\u7528\u7684\u6b66\u5668\uff0c\u7bad\u56fa\u7136\u53ef\u4ee5\u5c04\u4eba\uff0c\u5f13\u4ea6\u53ef\u4ee5\u62ff\u6765\u5e94\u654c\u3002\u8fd9\u4e2a\u8499\u53e4\u6b66\u58eb\u5c24\u5176\u662f\u4e2a\u4e2d\u7fd8\u695a\uff0c\u628a\u201c\u91d1 \u5f13\u5341\u516b\u6253\u201d\u7684\u62db\u6570\u4f7f\u5f97\u53d8\u5e7b\u83ab\u6d4b\uff0c\u53e4\u602a\u4e4b\u6781\u3002 \u3000\u3000\u67f3\u4e09\u5a18\u4ece\u672a\u89c1\u8fc7\u8fd9\u79cd\u53e4\u602a\u7684\u62db\u6570\uff0c\u8f6f\u97ad\u4e00\u6536\uff0c\u60f3\u628a\u5bf9\u65b9\u7684\u5f13\u5f26\u62c9\u65ad\uff0c\u54ea\u77e5\u5f13\u5f26\u575a\u97e7\uff0c\u53cc \u65b9\u7528\u529b\u4e00\u62c9\uff0c\u5bf9\u65b9\u7684\u5f13\u5f26\u6ca1\u65ad\uff0c\u67f3\u4e09\u5a18\u7684\u8f6f\u97ad\u5374\u5df2\u7ed9\u62c9\u5f97\u50cf\u5d29\u7d27\u7684\u5f13\u5f26\u4e86\uff0c\u67f3\u4e09\u5a18\u6c14\u529b\u8fdc\u8fdc \u4e0d\u5982\u5bf9\u65b9\uff0c\u53cd\u800c\u7ed9\u90a3\u4e2a\u8499\u53e4\u6b66\u58eb\u5c06\u5979\u62d6\u524d\u51e0\u6b65\u3002 \u3000\u3000\u67f3\u4e09\u5a18\u8d81\u52bf\u5411\u524d\u4e00\u6251\uff0c\u4e00\u4e2a\u201c\u98ce\u522e\u843d\u82b1\u201d\u7684\u8eab\u6cd5\u628a\u8f6f\u97ad\u89e3\u5f00\uff0c\u770b\u6765\u4f3c\u4e4e\u5c31\u8981\u8dcc\u5012\uff0c\u5374\u662f \u4e00\u62db\u6781\u4e3a\u8f7b\u7075\u8fc5\u6377\u7684\u5b9c\u5b88\u5b9c\u653b\u7684\u62db\u6570\u3002\u90a3\u8499\u53e4\u6b66\u58eb\u4e5f\u4e0d\u7531\u5f97\u8d5e\u4e86\u4e00\u4e2a\u201c\u597d\u201d\u5b57\u3002 \u3000\u3000\u4e00\u4e2a\u201c\u597d\u201d\u5b57\u51fa\u53e3\uff0c\u8499\u53e4\u6b66\u58eb\u53f3\u624b\u7684\u94fe\u5b50\u9524\u53c8\u5df2\u5411\u7740\u5b8b\u94c1\u8f6e\u80cc\u5fc3\u51fb\u53bb\uff0c\u5b8b\u94c1\u8f6e\u6b63\u8981\u51b2\u8fc7 \u53bb\u5bf9\u4ed8\u6551\u51fa\u674e\u601d\u5357\u7684\u90a3\u4e2a\u6b66\u58eb\uff0c\u542c\u5f97\u80cc\u540e\u7684\u91cd\u5175\u5668\u631f\u98ce\u4e4b\u58f0\uff0c\u53ea\u5f97\u56de\u8eab\u62db\u67b6\u3002\u8fd9\u8499\u53e4\u6b66\u571f\u540c \u65f6\u5e94\u4ed8\u4ed6\u4eec\u592b\u59bb\uff0c\u529b\u9053\u4ecd\u662f\u5927\u5f97\u51fa\u5947\u3002\u53ea\u542c\u5f97\u201c\u94db\u201d\u7684\u4e00\u58f0\u54cd\uff0c\u5b8b\u94c1\u8f6e\u7684\u53f3\u624b\u90a3\u53ea\u94c1\u8f6e\u4e5f\u7f3a \u4e86\u4e00\u89d2\u3002 \u3000\u3000\u94fe\u5b50\u9524\u80fd\u6253\u5230\u4e09\u4e08\u5f00\u5916\uff0c\u5b8b\u94c1\u8f6e\u65e0\u6cd5\u6446\u8131\u654c\u4eba\u7684\u7ea0\u7f20\u8ffd\u51fb\uff0c\u53ea\u80fd\u773c\u7741\u7741\u5730\u770b\u7740\u53e6\u5916\u4e00\u4e2a \u8499\u53e4\u6b66\u58eb\u628a\u674e\u601d\u5357\u62b1\u4e86\u53bb\uff0c\u6c14\u6068\u6068\u5730\u5927\u9a82\uff1a\u201c\u6094\u4e0d\u8be5\u4e0d\u65e9\u4e9b\u6740\u4e86\u8fd9\u4e2a\u5c0f\u5b50\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u88ab\u90a3\u201c\u94db\u201d\u7684\u4e00\u58f0\u5de8\u54cd\uff0c\u4ece\u8ff7\u8ff7\u7cca\u7cca\u60ca\u9192\u8fc7\u6765\uff0c\u8fd9\u624d\u770b\u6e05\u695a\u4e86\u6551\u4ed6\u51fa\u6765\u7684\u90a3\u4e2a \u4eba\uff0c\u4e0d\u7981\u5927\u4e3a\u60ca\u5f02\uff0c\u51e0\u4e4e\u4ee5\u4e3a\u81ea\u5df1\u53c8\u662f\u7f6e\u8eab\u6076\u68a6\u4e4b\u4e2d\u3002 \u3000\u3000\u539f\u6765\u8fd9\u4e2a\u6b66\u58eb\u4e0d\u662f\u522b\u4eba\uff0c\u6b63\u662f\u6628\u65e5\u5728\u6c99\u6f20\u6240\u9047\u7684\u90a3\u4e2a\u8499\u53e4\u6b66\u58eb\u8d64\u8001\u6e29\u3002\u53ea\u4e0d\u8fc7\u9694\u4e86\u4e00 \u5927\uff0c\u6628\u5929\u8981\u6740\u4ed6\u7684\u8fd9\u4e2a\u8d64\u8001\u6e29\uff0c\u4eca\u5929\u7adf\u53d8\u4f5c\u4e86\u4ed6\u7684\u6551\u547d\u6069\u4eba\u3002 \u3000\u3000\u201c\u8fd9\u7a76\u7adf\u662f\u4e3a\u4e86\u4ec0\u4e48\uff1f\u4e3a\u4e86\u4ec0\u4e48\uff1f\u201d\u674e\u601d\u5357\u832b\u7136\u4e0d\u89e3\u3002\u4e0d\u8fc7\uff0c\u6709\u4e00\u70b9\u4ed6\u5df2\u7ecf\u662f\u660e\u767d\u4e86\uff1a \u67f3\u4e09\u5a18\u521a\u624d\u4e4b\u6240\u4ee5\u72e0\u4e0b\u6740\u624b\uff0c\u5b9a\u8981\u6740\u4ed6\uff0c\u4e00\u5b9a\u662f\u56e0\u4e3a\u65e9\u5df2\u6599\u5230\u8fd9\u4e24\u4e2a\u8499\u53e4\u6b66\u571f\u8981\u6765\u6551\u4ed6\u7684\u7f18 \u6545\u3002 \u3000\u3000\u201c\u602a\u4e0d\u5f97\u4ed6\u4eec\u4e00\u542c\u89c1\u9a6c\u94c3\u58f0\u54cd\uff0c\u5c31\u8bf4\u662f\u6211\u7684\u6551\u5175\u6765\u4e86\u3002\u679c\u7136\u771f\u662f\u6211\u7684\u6551\u5175\u3002\u201d\u674e\u601d\u5357\u6599 \u60f3\u5176\u4e2d\u5b9a\u6709\u8bef\u4f1a\uff0c\u4f46\u4e00\u6765\u4ed6\u5df2\u662f\u529b\u7aed\u7cbe\u75b2\uff0c\u8fde\u8bf4\u8bdd\u7684\u6c14\u529b\u90fd\u6ca1\u6709\u4e86\uff1b\u4e8c\u6765\u4ed6\u4e5f\u4e0d\u77e5\u4ece\u4f55\u8bf4 \u8d77\uff0c\u8fd9\u4e24\u4e2a\u8499\u53e4\u6b66\u58eb\u786e\u5b9e\u662f\u6551\u4e86\u4ed6\uff0c\u96be\u9053\u4ed6\u80fd\u591f\u5426\u8ba4\u4e0d\u662f\u4ed6\u7684\u201c\u6551\u5175\u201d\uff0c\u548c\u8d64\u8001\u6e29\u540c\u6765\u7684\u90a3 \u4e2a\u8499\u53e4\u6b66\u58eb\uff0c\u4ee5\u4e00\u654c\u4e8c\uff0c\u5140\u662f\u653b\u591a\u5b88\u5c11\u3002\u5b8b\u94c1\u8f6e\u592b\u5987\u6218\u4ed6\u4e0d\u4e0b\uff0c\u53c8\u6015\u7eed\u6709\u8ffd\u5175\uff0c\u4e0d\u6562\u604b\u6218\uff0c \u4e0a\u9a6c\u4fbf\u8dd1\u3002 \u3000\u3000\u90a3\u6b66\u58eb\u559d\u9053\uff1a\u201c\u5728\u91d1\u56fd\u5883\u5185\uff0c\u53ef\u4ee5\u4efb\u4f60\u6a2a\u884c\uff1b\u5230\u4e86\u8499\u53e4\uff0c\u5c82\u80fd\u8ba9\u4f60\u8981\u8d70\u4fbf\u8d70\uff1f\u201d\u5f20\u5f13\u642d \u7bad\uff0c\u55d6\u3001\u55d6\u3001\u55d6\u4e09\u679d\u8fde\u73e0\u7bad\u5c04\u51fa\u3002\u67f3\u4e09\u5a18\u8f6f\u97ad\u98de\u821e\uff0c\u6253\u843d\u4e86\u4e00\u679d\u3002\u5b8b\u94c1\u8f6e\u7684\u5175\u5668\u6c89\u91cd\uff0c\u5728\u9a6c \u80cc\u4e0a\u821e\u52a8\u8d77\u6765\uff0c\u5374\u662f\u4e0d\u80fd\u50cf\u67f3\u4e09\u5a18\u4e4b\u906e\u62e6\u5f97\u98ce\u96e8\u4e0d\u900f\uff0c\u7b2c\u4e8c\u679d\u7bad\u6070\u597d\u4ece\u4ed6\u7684\u8f6e\u5b50\u4e2d\u7a7a\u4e4b\u5904\u7a7f \u51fa\uff0c\u6b63\u4e2d\u4ed6\u7684\u80a9\u5934\uff0c\u8840\u6d41\u5982\u6ce8\u3002\u6b64\u65f6\u4ed6\u4eec\u7684\u8ddd\u79bb\u5df1\u5728\u767e\u6b65\u5f00\u5916\uff0c\u8fd9\u4e00\u679d\u7bad\u8fd8\u662f\u5c04\u5f97\u5982\u6b64\u4e4b \u51c6\uff0c\u5f53\u771f\u662f\u7f55\u89c1\u7f55\u95fb\u7684\u795e\u7bad\u529f\u592b\uff0c\u8fde\u674e\u601d\u5357\u4e5f\u4e0d\u7981\u4e3a\u4e4b\u77eb\u820c\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u53e4\u4ee3\u795e\u7bad\u624b\u517b\u7531 \u57fa\u7684\u2018\u767e\u6b65\u7a7f\u6768\u2019\u60f3\u6765\u4e5f\u4e0d\u8fc7\u5982\u662f\u3002\u201d \u3000\u3000\u5e78\u4e8f\u4ed6\u4eec\u7684\u5750\u9a91\u795e\u9a8f\uff0c\u7b2c\u4e09\u679d\u5c04\u5230\uff0c\u5df2\u662f\u843d\u5728\u5b8b\u94c1\u8f6e\u7684\u9a6c\u540e\u3002\u5b8b\u94c1\u8f6e\u6240\u4e2d\u7684\u90a3\u4e00\u7bad\u56e0\u4e3a \u662f\u5728\u767e\u6b65\u5f00\u5916\u5c04\u6765\uff0c\u5f3a\u5f29\u4e4b\u672a\uff0c\u867d\u4f24\u4e0d\u91cd\u3002 \u3000\u3000\u5b8b\u94c1\u8f6e\u6c14\u5f97\u5927\u9a82\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u4f60\u4f11\u8981\u5f97\u610f\uff0c\u56de\u53bb\u6211\u81ea\u4f1a\u627e\u4f60\u7684\u5e08\u7236\u7b97\u5e10\uff01\u5b5f\u5927\u4fa0\u4e0d\u6740 \u4f60\uff0c\u8c37\u5e73\u9633\u4e5f\u975e\u6740\u4f60\u4e0d\u53ef\uff01\u201d\u4ed6\u4e2d\u4e86\u90a3\u4e2a\u8499\u53e4\u6b66\u58eb\u7684\u7bad\uff0c\u5374\u62ff\u674e\u601d\u5357\u6765\u51fa\u6c14\uff0c\u8a00\u4e0b\u4e4b\u610f\uff0c\u5f53 \u7136\u662f\u8981\u56de\u53bb\u544a\u8bc9\u4ed6\u7684\u5e08\u7236\u8c37\u5e73\u9633\uff0c\u903c\u8c37\u5e73\u9633\u201c\u6e05\u7406\u95e8\u6237\u201d\u4e86\u3002\u4ed6\u5374\u54ea\u91cc\u77e5\u9053\uff0c\u674e\u601d\u5357\u6b64\u65f6\u6b63 \u662f\u6709\u82e6\u8bf4\u4e0d\u51fa\u6765\uff0c\u54ea\u91cc\u8fd8\u4f1a\u201c\u5f97\u610f\u201d\uff1f \u3000\u3000\u674e\u601d\u5357\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u7b14\u7cca\u6d82\u5e10\u600e\u5730\u7b97\u5230\u4e86\u6211\u7684\u5934\u4e0a\uff1f\u6069\u5e08\u662f\u77e5\u9053\u6211\u7684\u4e3a\u4eba\uff0c\u4f60\u82e5\u662f\u53bb \u7980\u544a\u4ed6\u8001\u4eba\u5bb6\uff0c\u6211\u662f\u6c42\u4e4b\u4e0d\u5f97\uff01\u201d\u60f3\u5230\u603b\u6709\u6c34\u843d\u77f3\u51fa\u4e4b\u65f6\uff0c\u5fc3\u4e2d\u624d\u7a0d\u7a0d\u5f00\u89e3\u3002\u4f46\u5e73\u767d\u53d7\u4e86\u4e00 \u987f\u75db\u9a82\uff0c\u6b32\u8fa9\u65e0\u4ece\uff0c\u4e5f\u552f\u6709\u82e6\u7b11\u800c\u5df2\u3002 \u3000\u3000\u8d64\u8001\u6e29\u7ed9\u4ed6\u559d\u4e86\u4e00\u53e3\u6c34\uff0c\u8bf4\u9053\uff1a\u201c\u6628\u65e5\u5bf9\u4f60\u591a\u6709\u5f97\u7f6a\uff0c\u4f60\u53ef\u522b\u8981\u89c1\u602a\u3002\u6211\u540d\u53eb\u8d64\u8001\u6e29\uff0c \u8fd9\u662f\u6211\u7684\u4e09\u54e5\u6728\u534e\u9ece\uff01\u6211\u4eec\u90fd\u662f\u6210\u5409\u6069\u6c57\u624b\u4e0b\u7684\u91d1\u5e10\u6b66\u58eb\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u66fe\u542c\u5f97\u5b5f\u5c11\u521a\u8bf4\u8fc7\uff0c\u8499\u53e4\u662f\u4e2a\u6e38\u7267\u6c11\u65cf\uff0c\u6210\u5409\u601d\u6c57\u7acb\u56fd\u4e4b\u59cb\uff0c\u5c1a\u672a\u5efa\u6709\u5bab\u6bbf\uff0c\u800c \u662f\u4f4f\u5728\u5e10\u5e55\u4e4b\u4e2d\uff0c\u7ecf\u5e38\u642c\u8fc1\uff0c\u79f0\u4e3a\u201c\u884c\u56fd\u201d\u3002\u5f53\u7136\uff0c\u4ed6\u7684\u5e10\u5e55\u8981\u6bd4\u5bfb\u5e38\u7684\u5e10\u5e55\u5b8f\u4f1f\u534e\u4e3d\uff0c\u662f \u6545\u53c8\u79f0\u4e3a\u201c\u91d1\u5e10\u201d\u3002 \u3000\u3000\u6210\u5409\u601d\u6c57\u7684\u968f\u8eab\u536b\u58eb\u79f0\u4e3a\u201c\u91d1\u5e10\u6b66\u58eb\u201d\uff0c\u91d1\u5e10\u6b66\u571f\u4e4b\u4e2d\u53c8\u6709\u5341\u4e8c\u4e2a\u672c\u9886\u6700\u5f3a\u7684\uff0c\u53f7\u79f0 \u201c\u5341\u4e8c\u91d1\u521a\u201d\u3002\u8d64\u8001\u6e29\u6392\u540d\u7b2c\u516b\uff0c\u4ed6\u79f0\u6728\u534e\u9ece\u4e3a\u201c\u4e09\u54e5\u201d\uff0c\u60f3\u5fc5\u8fd9\u6728\u534e\u9ece\u5728\u201c\u5341\u4e8c\u91d1\u521a\u201d\u4e4b \u4e2d\u662f\u6392\u540d\u7b2c\u4e09\u7684\u4e86\u3002 \u3000\u3000\u674e\u601d\u5357\u60ed\u590d\u4e86\u4e00\u70b9\u6c14\u529b\uff0c\u4fbf\u5411\u8d64\u8001\u6e29\u95ee\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u6740\u6211\uff1f\u201d \u3000\u3000\u8d64\u8001\u6e29\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6628\u65e5\u6211\u4ee5\u4e3a\u4f60\u662f\u5c60\u767e\u57ce\u7684\u624b\u4e0b\u55bd\u5175\uff0c\u5982\u4eca\u6211\u5df2\u77e5\u9053\u4e0d\u662f\uff0c\u6211\u4e3a\u4f55\u8fd8 \u8981\u6740\u4f60\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u4f60\u4e0d\u6740\u6211\uff0c\u4f46\u4e5f\u65e0\u987b\u6551\u6211\u5440\uff1f\u201d \u3000\u3000\u8d64\u8001\u6e29\u7b11\u9053\uff1a\u201c\u8fd9\u5012\u6709\u4e24\u4e2a\u539f\u56e0\u3002\u201d\u674e\u601d\u5357\u9053\uff1a\u201c\u54e6\uff0c\u4ec0\u4e48\u4e24\u4e2a\u539f\u56e0\uff0c\u5012\u8981\u8bf7\u6559\u3002\u201d \u3000\u3000\u8d64\u8001\u6e29\u9053\uff1a\u201c\u6211\u56fd\u5c06\u4e0e\u5b8b\u56fd\u8054\u76df\uff0c\u5939\u653b\u91d1\u56fd\u3002\u6211\u4eec\u7684\u5927\u6c57\u6b63\u8981\u793c\u8058\u6709\u672c\u4e8b\u7684\u6c49\u4eba\u3002\u91d1\u56fd \u662f\u54b1\u4eec\u5171\u540c\u7684\u654c\u4eba\uff0c\u90a3\u4e48\u54b1\u4eec\u4e16\u5c31\u662f\u670b\u53cb\u4e86\uff0c\u60f3\u5fc5\u4f60\u4f1a\u4e50\u610f\u5e2e\u52a9\u6211\u4eec\u5427\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u8fd9\u4e8b\u6682\u4e14\u4e0d\u8c08\uff0c\u7b2c\u4e8c\u4e2a\u539f\u56e0\u53c8\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u8d64\u8001\u6e29\u9053\uff1a\u201c\u4f60\u59d3\u674e\u540d\u601d\u5357\uff0c\u662f\u4e48\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d \u3000\u3000\u8d64\u8001\u6e29\u9053\uff1a\u201c\u4f60\u8fdb\u5165\u6211\u4eec\u56fd\u5883\uff0c\u672a\u66fe\u901a\u8fc7\u6208\u58c1\u4e4b\u65f6\uff0c\u5df2\u7ecf\u6709\u4eba\u62a5\u5230\u548c\u6797\uff08\u8499\u53e4\u7684\u4e24\u4e2a \u201c\u884c\u90fd\u201d\u4e4b\u4e00\uff0c\u53e6\u4e00\u4e2a\u884c\u90fd\u662f\u65a1\u96be\u6cb3\u6e90\u3002\u6210\u5409\u601d\u6c57\u4f4f\u5728\u548c\u6797\u7684\u65f6\u5019\u8f83\u591a\uff0c\u548c\u6797\u81f3\u5143\u592a\u5b97\u7a9d\u9614 \u53f0\u7684\u65f6\u5019\uff0c\u59cb\u5efa\u4e3a\u6b63\u5f0f\u90fd\u57ce\u3002\uff09\u6765\u4e86\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u5927\u4e3a\u8be7\u5f02\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u53ea\u662f\u4e00\u4e2a\u5fae\u4e0d\u8db3\u9053\u7684\u5c0f\u4eba\u7269\uff01\u60f3\u4e0d\u5230\u4f60\u4eec\u7adf\u7136\u5982\u6b64\u6ce8\u610f\u6211 \u7684\u884c\u8e2a\u3002\u201d \u3000\u3000\u6728\u534e\u9ece\u7b11\u9053\uff1a\u201c\u8fd9\u53ea\u662f\u4f60\u7684\u81ea\u8c26\u800c\u5df2\uff0c\u4efb\u4f55\u5916\u6765\u7684\u6c49\u4eba\u6211\u4eec\u90fd\u6ce8\u610f\u7684\uff0c\u4f55\u51b5\u662f\u4f60\u5462\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u6211\u4e0e\u522b\u4eba\u53c8\u6709\u4ec0\u4e48\u4e0d\u540c\uff1f\u201d \u3000\u3000\u6728\u534e\u9ece\u9053\uff1a\u201c\u5728\u6211\u4eec\u5927\u6c57\u7684\u624b\u4e0b\uff0c\u6709\u4e2a\u5f88\u53d7\u91cd\u7528\u7684\u6c49\u4eba\u5b98\u5458\uff0c\u4ed6\u60f3\u89c1\u4e00\u89c1\u4f60\u3002\u6628\u65e5\u6211\u7684 \u516b\u5f1f\u78b0\u4e0a\u4f60\uff0c\u56de\u6765\u548c\u6211\u4e00\u8bf4\uff0c\u6211\u5c31\u6000\u7591\u8fd9\u4eba\u662f\u4f60\u4e86\u3002\u56e0\u6b64\u6211\u4eec\u624d\u8d76\u5feb\u672a\u627e\u4f60\u7684\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u66f4\u4e3a\u5947\u602a\uff0c\u95ee\u9053\uff1a\u201c\u8fd9\u4e2a\u6c49\u4eba\u59d3\u751a\u540d\u8c01\uff1f\u201d \u3000\u3000\u6728\u534e\u9ece\u9053\uff1a\u201c\u4f60\u89c1\u4e86\u4ed6\uff0c\u81ea\u7136\u77e5\u9053\u3002\u201d\u770b\u6765\u662f\u90a3\u4eba\u6709\u8a00\u4ea4\u4ee3\u4ed6\u4eec\uff0c\u8bf7\u4ed6\u4eec\u4e0d\u8981\u5148\u8bf4\u7684 \u4e86\u3002 \u3000\u3000\u674e\u601d\u5357\u7591\u5fc3\u987f\u8d77\uff1a\u201c\u96be\u9053\u662f\u6211\u7684\u7239\u7239\uff1f\u4e0d\uff0c\u6211\u7684\u7236\u4eb2\u53d7\u4e86\u8499\u53e4\u9791\u5b50\u8fd9\u8bb8\u591a\u5e74\u7684\u6298\u78e8\uff0c\u4ed6 \u600e\u4f1a\u505a\u8499\u53e4\u4eba\u7684\u5b98\uff1f\u201d \u3000\u3000\u53ef\u662f\u674e\u601d\u5357\u65e2\u7136\u662f\u4e3a\u4e86\u627e\u5bfb\u7236\u4eb2\u6765\u4e86\uff0c\u5982\u4eca\u6709\u4e2a\u8bc6\u5f97\u4ed6\u7684\u4eba\u5728\u548c\u6797\uff0c\u5373\u4f7f\u4e0d\u662f\u4ed6\u7684\u7236 \u4eb2\uff0c\u60f3\u5fc5\u4e5f\u4f1a\u77e5\u9053\u4ed6\u7684\u4e0b\u843d\uff0c\u56e0\u6b64\u674e\u601d\u5357\u4e5f\u5c31\u7b54\u5e94\u4e86\u4e0e\u4ed6\u4eec\u540c\u5f80\u548c\u6797\uff0c\u4f1a\u89c1\u90a3\u4eba\u3002 \u3000\u3000\u5f53\u7136\uff0c\u674e\u601d\u5357\u5728\u4f5c\u51fa\u8fd9\u4e2a\u51b3\u5b9a\u4e4b\u65f6\uff0c\u4e5f\u662f\u66fe\u7ecf\u6709\u8fc7\u72b9\u8c6b\uff0c\u6709\u8fc7\u4e0d\u5b89\u3002 \u3000\u3000\u4ed6\u60f3\u8d77\u4e86\u5b5f\u5c11\u521a\u7684\u8b66\u544a\uff0c\u8fd9\u65f6\u4ed6\u4e5f\u660e\u767d\u4e86\u76c2\u5c11\u521a\u4e3a\u4ec0\u4e48\u8981\u6740\u4ed6\u7684\u539f\u56e0\u4e86\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a \u201c\u4e3a\u864e\u4f5c\u6005\uff0c\u6211\u5f53\u7136\u662f\u51b3\u4e0d\u4f1a\u7684\u3002\u8499\u53e4\u4e0e\u5927\u5b8b\u6b63\u5728\u5546\u8c08\u8054\u76df\u5bf9\u91d1\uff0c\u6700\u5c11\u76ee\u524d\u8fd8\u4e0d\u662f\u654c\u56fd\uff0c\u53ea \u8981\u6211\u81ea\u5df1\u628a\u6301\u5f97\u5b9a\uff0c\u53bb\u89c1\u89c1\u4ed6\u4eec\u7684\u5b98\u5458\uff0c\u751a\u81f3\u89c1\u89c1\u4ed6\u4eec\u7684\u5927\u6c57\uff0c\u90a3\u4e5f\u7b97\u4e0d\u4e86\u4ec0\u4e48\u3002\u201d\u4f46\u968f\u5373 \u53c8\u60f3\uff1a\u201c\u8499\u53e4\u541e\u91d1\u4e4b\u540e\uff0c\u7ee7\u800c\u5fc5\u5c06\u706d\u5b8b\uff0c\u8fd9\u662f\u6709\u8bc6\u4e4b\u58eb\uff0c\u90fd\u53ef\u4ee5\u6599\u5f97\u5230\u7684\u3002\u5982\u679c\u90a3\u4e2a\u4eba\u771f\u662f \u6211\u7684\u7239\u7239\uff0c\u6211\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u201c\u6211\u53ef\u4ee5\u529d\u4ed6\u548c\u6211\u5f03\u5b98\u6f5c\u9003\u3002\u5728\u8499\u53e4\u4e0e\u5927\u5b8b\u672a\u6210\u654c\u56fd\u4e4b\u524d\uff0c\u5148\u9003\u5230\u6c5f\u5357\u53bb\uff0c\u90a3\u5c31\u53ef\u4ee5\u4fdd \u5168\u6211\u7239\u7239\u7684\u540d\u8282\u4e86\u3002\u53ef\u662f\u7239\u7239\u80af\u542c\u6211\u7684\u8bdd\u5417\uff1f\u4ed6\u82e5\u7136\u771f\u662f\u505a\u4e86\u8499\u53e4\u4eba\u7684\u9ad8\u5b98\uff0c\u53c8\u90a3\u4e48\u5bb9\u6613\u9003 \u5f97\u8131\u5417\uff1f\u201d\u674e\u601d\u5357\u5fc3\u5982\u4e71\u9ebb\uff0c\u53ea\u597d\u4ecd\u7136\u7528\u6700\u521d\u7684\u90a3\u4e2a\u5ff5\u5934\u5b89\u6170\u81ea\u5df1\uff1a\u201c\u4e0d\u4f1a\u7684\uff0c\u4e0d\u4f1a\u7684\uff0c\u6211 \u7239\u7239\u88ab\u8499\u53e4\u9791\u5b50\u6240\u4fd8\uff0c\u53d7\u4e86\u4e00\u4e8c\u5341\u5e74\u7684\u82e6\u695a\uff0c\u4ed6\u600e\u4f1a\u8fd8\u505a\u8499\u53e4\u4eba\u7684\u5b98\uff1f\u201d \u3000\u3000\u8d64\u8001\u6e29\u627e\u6765\u4e86\u4e00\u5339\u9a86\u9a7c\uff0c\u8ba9\u7ed9\u674e\u601d\u5357\u4e58\u5750\u3002\u9a86\u9a7c\u4e16\u79f0\u201c\u6c99\u6f20\u4e4b\u821f\u201d\uff0c\u6b64\u65f6\u5df2\u7ecf\u8fc7\u4e86\u6c99\u6f20 \u5730\u5e26\uff0c\u4e0d\u8fc7\u5750\u5728\u9a86\u9a7c\u4e0a\u8fd8\u662f\u6bd4\u9a91\u9a6c\u8212\u670d\u5f97\u591a\uff0c\u674e\u601d\u5357\u53d7\u7684\u53ea\u662f\u5916\u4f24\uff0c\u6577\u4e0a\u4e86\u91d1\u521b\u836f\u4e4b\u540e\uff0c\u5728 \u9a86\u9a7c\u80cc\u4e0a\u8fc7\u4e86\u51e0\u5929\uff0c\u6e10\u6e10\u6062\u590d\u5982\u521d\u3002 \u3000\u3000\u8def\u4e0a\u6728\u534e\u9ece\u3001\u8d64\u8001\u6e29\u4e5f\u66fe\u4e0e\u4ed6\u8c08\u53ca\u5c60\u767e\u57ce\u4e4b\u4e8b\uff0c\u636e\u8bf4\u5c60\u767e\u57ce\u662f\u56e0\u4e3a\u5728\u91d1\u56fd\u7ad9\u4e0d\u4f4f\u8db3\uff0c\u8fd9 \u624d\u9003\u5230\u8499\u53e4\u7684\u3002\u91d1\u56fd\u6d3e\u9063\u4e86\u5341\u4e8c\u540d\u4e00\u6d41\u9ad8\u624b\u641c\u6355\u4ed6\uff0c\u4ed6\u5fc5\u987b\u907f\u4e00\u907f\u98ce\u5934\u3002 \u3000\u3000\u674e\u601d\u5357\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u65e2\u7136\u51c6\u5907\u4e0e\u91d1\u56fd\u6253\u4ed7\uff0c\u8fd9\u4e2a\u5c60\u767e\u57ce\u662f\u6297\u91d1\u7684\u597d\u6c49\uff0c\u4f60\u4eec\u4e3a\u4ec0\u4e48\u8981\u6740 \u4ed6\u5462\uff1f\u201d \u3000\u3000\u6728\u534e\u9ece\u7b11\u9053\uff1a\u201c\u4f60\u60f3\u5f97\u5230\u7684\uff0c\u6211\u4eec\u65e9\u5df2\u60f3\u5230\u4e86\u3002\u5927\u6c57\u66fe\u6d3e\u4eba\u627e\u8fc7\u4ed6\u5546\u8c08\uff0c\u4ed6\u4e0d\u80af\u4e3a\u6211\u4eec \u6240\u7528\u3002\u6070\u597d\u8bdd\u65f6\u91d1\u56fd\u53c8\u6709\u4f7f\u8005\u6765\uff0c\u613f\u610f\u5272\u8fb9\u5883\u7684\u4e24\u5ea7\u57ce\u5730\uff0c\u6362\u5c60\u767e\u57ce\u8fd9\u4e00\u4e2a\u4eba\u3002\u4e0d\u8fc7\u662f\u8981\u6d3b \u7684\u800c\u4e0d\u662f\u8981\u6b7b\u7684\uff0c\u82e5\u662f\u6b7b\u7684\uff0c\u5c31\u80fd\u6362\u4e00\u5ea7\u57ce\u6c60\u3002\u91d1\u4e3b\u7684\u610f\u601d\uff0c\u662f\u8981\u5728\u5f97\u5230\u4ed6\u7684\u53e3\u4f9b\u4e4b\u540e\uff0c\u624d \u6740\u4ed6\u706d\u53e3\uff0c\u6240\u4ee5\u6211\u4eec\u5c31\u975e\u641c\u6355\u4ed6\u4e0d\u53ef\u4e86\u3002\u201d \u3000\u3000\u8d64\u8001\u6e29\u9053\uff1a\u201c\u8fd9\u5c60\u767e\u57ce\u72e1\u733e\u5f97\u5f88\uff0c\u4ed6\u5728\u7b2c\u4e00\u6b21\u548c\u6211\u4eec\u4f1a\u8c08\u4e4b\u540e\uff0c\u5df2\u9884\u6599\u5230\u4ed6\u4e0d\u7b54\u5141\uff0c\u5b9a \u6709\u540e\u60a3\uff0c\u7559\u4ee5\u5728\u91d1\u56fd\u4f7f\u8005\u672a\u6765\u4e4b\u524d\uff0c\u4ed6\u5df2\u7ecf\u9690\u85cf\u8d77\u6765\u4e86\u3002\u6211\u4eec\u662f\u60f3\u6740\u4ed6\u6216\u6349\u4ed6\uff0c\u53ef\u662f\u5982\u4eca\u6740 \u4ed6\u7684\u90a3\u4e2a\u4eba\uff0c\u6211\u4eec\u5374\u8fd8\u672a\u77e5\u9053\u5462\u3002\u201d \u3000\u3000\u6728\u534e\u9ece\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u73b0\u5728\u6211\u4eec\u627e\u5230\u4e86\u4ed6\u7684\u5c38\u9996\uff0c\u4e5f\u603b\u53ef\u4ee5\u5e73\u767d\u5f97\u5230\u4e00\u5ea7\u57ce\u6c60\u4e86\u3002\u201d\u8bf4 \u7f62\uff0c\u54c8\u54c8\u5927\u7b11\u3002 \u3000\u3000\u674e\u601d\u5357\u542c\u4e86\u4ed6\u7684\u7b11\u58f0\uff0c\u5fc3\u4e2d\u4e0d\u5bd2\u800c\u6817\uff1a\u201c\u5982\u679c\u6211\u50cf\u5c60\u767e\u57ce\u4e00\u6837\uff0c\u4e0d\u4e3a\u4ed6\u4eec\u6240\u7528\uff0c\u60f3\u6765\u4ed6 \u4eec\u4e5f\u662f\u4e0d\u80af\u628a\u6211\u653e\u8fc7\u7684\u3002\u201d \u3000\u3000\u4e00\u961f\u9a91\u7740\u9a6c\u7684\u9752\u5e74\u4ece\u8def\u4e0a\u7ecf\u8fc7\uff0c\u9ad8\u58f0\u5531\u7740\u6b4c\uff0c\u6fc0\u6602\u6177\u6168\uff01\u4e2d\u5e26\u7740\u51e0\u5206\u60b2\u51c9\u7684\u60c5\u8c03\uff0c\u6b4c\u8c03 \u7684\u5927\u610f\u8bd1\u6210\u6c49\u6587\u662f\uff1a \u3000\u3000\u97ad\u513f\u6325\u52a8\u54cd\u56db\u65b9\uff0c \u3000\u3000\u5f2f\u5f13\u76d8\u9a6c\u901e\u8c6a\u5f3a\uff0c \u3000\u3000\u5927\u6c57\u5a01\u540d\u65e0\u6240\u626c\u3002 \u3000\u3000\u5927\u5c71\u5728\u4ed6\u811a\u4e0b\u4fef\u4f0f \u3000\u3000\u654c\u4eba\u6218\u7c9f\u5728\u4ed6\u8ddf\u524d\u3002 \u3000\u3000\u559d\u4e00\u53e3\u65a1\u96be\u6cb3\u7684\u6e05\u6c34\u5427\uff01 \u3000\u3000\u6211\u4eec\u8981\u968f\u5927\u6c57\u8fdc\u5f81\u53bb\u4e86\u2014\u2014 \u3000\u3000\u4e0d\u77e5\u4ec0\u4e48\u65f6\u5019\u624d\u80fd\u56de\u8f6c\u6545\u4e61\u3002 \u3000\u3000\u6728\u534e\u9ece\u9053\uff1a\u201c\u4ed6\u4eec\u662f\u5e94\u5f81\u5f80\u8fb9\u7586\u7684\u6218\u58eb\uff0c\u53ea\u7b49\u5927\u6c57\u7684\u547d\u4ee4\u4e00\u4e0b\uff0c\u5c31\u8981\u51fa\u5f81\u3002\u4f60\u77a7\uff0c\u6211\u4eec \u6709\u8fd9\u8bb8\u591a\u4f18\u79c0\u7684\u6218\u58eb\uff0c\u4f55\u6101\u91d1\u56fd\u4e0d\u5e73\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u5fc3\u60f3\uff1a\u201c\u6210\u5409\u601d\u6c57\u7edf\u4e00\u8499\u53e4\uff0c\u56fd\u5a01\u8fdc\u632f\uff0c\u8499\u53e4\u4eba\u4e2a\u4e2a\u4ee5\u4ed6\u4e3a\u8363\uff0c\u4ed6\u7684\u529f\u52b3\u81ea\u7136\u5f88 \u5927\u3002\u53ef\u662f\u4ed6\u82e5\u7136\u53ea\u77e5\u7a77\u5175\u9ee9\u6b66\uff0c\u4fb5\u7565\u522b\u4eba\u7684\u56fd\u5bb6\uff0c\u53ea\u6015\u4e5f\u4e0d\u662f\u8499\u53e4\u4e4b\u798f\u5462\uff01\u201d\u53c8\u60f3\uff1a\u201c\u8499\u91d1 \u4ea4\u5175\u5728\u5373\uff0c\u6211\u5230\u4e86\u548c\u6797\uff0c\u8fd8\u5f97\u65e9\u65e9\u60f3\u6cd5\u8131\u8eab\u624d\u662f\u3002\u201d \u3000\u3000\u540e\u9762\u662f\u4e00\u961f\u5987\u5973\uff0c\u6325\u821e\u7740\u624b\u5dfe\u4e0e\u6218\u58eb\u9001\u522b\u3002\u5176\u4e2d\u4e00\u4e2a\u5c11\u5973\uff0c\u957f\u5f97\u5341\u5206\u7f8e\u4e3d\uff0c\u62b1\u7740\u9a6c\u5934\u7434 \u5531\u9053\uff1a \u3000\u3000\u201c\u5927\u98ce\u5377\u8d77\u4e86\u9ec4\u6c99\uff0c \u3000\u3000\u5929\u8fb9\u7684\u5140\u9e70\u76d8\u65cb\u6b32\u4e0b\uff1b \u3000\u3000\u54e5\u5440\uff0c\u4f60\u5c31\u662f\u5929\u8fb9\u7684\u90a3\u53ea\u5140\u9e70\uff0c \u3000\u3000\u4f60\u867d\u7136\u4e0d\u6015\u98ce\u6c99\uff0c\u4f60\u4e5f\u4e0d\u8981\u4e0b\u6765\u5440\uff01 \u3000\u3000\u5927\u98ce\u5377\u8d77\u4e86\u9ec4\u6c99\uff0c \u3000\u3000\u5929\u8fb9\u7684\u5140\u9e70\u76d8\u65cb\u6b32\u4e0b\uff1b \u3000\u3000\u6211\u4e0d\u662f\u4e0d\u6015\u98ce\u6c99\u3002 \u3000\u3000\u59b9\u5440\uff0c\u6211\u662f\u4e3a\u4e86\u8981\u89c1\u4f60\u7684\u9762\uff0c \u3000\u3000\u6211\u8981\u4e58\u98ce\u6765\u627e\u4f60\u56de\u5bb6\uff01\u201d \u3000\u3000\u7434\u97f5\u60a0\u626c\uff0c\u6b4c\u5589\u5a49\u8f6c\uff0c\u674e\u601d\u5357\u4e0d\u7981\u4e5f\u542c\u5f97\u75f4\u3002\u8d64\u8001\u6e29\u542c\u5f97\u541e\u4e86\u541e\u53e3\u6c34\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u5973\u5b50 \u540d\u53eb\u5361\u6d1b\u4e1d\uff0c\u662f\u6211\u4eec\u8499\u53e4\u6709\u540d\u7684\u7f8e\u4eba\u513f\uff0c\u5374\u4e0d\u77e5\u5979\u9001\u7684\u662f\u8c01\uff1f\u53ef\u60dc\uff0c\u53ef\u60dc\uff01\u201d\u5e95\u4e0b\u6ca1\u6709\u8bf4\u51fa \u7684\u8bdd\uff0c\u81ea\u7136\u662f\u53ef\u60dc\u5979\u6709\u4e86\u610f\u4e2d\u4eba\u4e86\u3002 \u3000\u3000\u5728\u8def\u4e0a\u8fc7\u4e86\u4e03\u5929\uff0c\u7ec8\u4e8e\u5230\u4e86\u548c\u6797\u3002\u674e\u601d\u5357\u6000\u7740\u60f4\u60f4\u4e0d\u5b89\u7684\u5fc3\u60c5\uff0c\u7ed9\u4ed6\u4eec\u5e26\u5230\u4e00\u5ea7\u5e10\u5e55\u3002 \u3000\u3000\u8d64\u6728\u4e24\u4eba\u628a\u674e\u601d\u5357\u4ea4\u7ed9\u4e00\u4e2a\u6218\u58eb\uff0c\u5bf9\u90a3\u6218\u58eb\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u4e0d\u8fdb\u53bb\u4e86\uff0c\u4f60\u544a\u8bc9\u674e\u5927\u4eba\uff0c\u53eb \u4ed6\u660e\u65e5\u5230\u91d1\u5e10\u56de\u62a5\u3002\u201d\u536b\u571f\u5e94\u4e86\u4e00\u4e2a\u201c\u662f\u201d\u5b57\uff0c\u95ee\u9053\uff1a\u201c\u8fd9\u4f4d\u5c31\u662f\u674e\u516c\u5b50\u4e48\uff1f\u201d\u8d64\u8001\u6e29\u7b11 \u9053\uff1a\u201c\u4f60\u7684\u5c0f\u4e3b\u4eba\u5230\u4e86\uff0c\u4f60\u8fd8\u4e0d\u5c0f\u5fc3\u670d\u4f8d\uff1f\u201d \u3000\u3000\u90a3\u536b\u58eb\u5fd9\u628a\u674e\u601d\u5357\u6276\u4e0b\u9a86\u9a7c\uff0c\u606d\u606d\u656c\u656c\u8bf4\u9053\uff1a\u201c\u5c11\u7237\uff0c\u8bf7\uff01\u201d\u8fd9\u201c\u5c11\u7237\u201d\u4e8c\u5b57\u662f\u7528\u6c49\u8bed \u8bf4\u7684\uff0c\u751f\u786c\u5f97\u5f88\u3002\u6728\u534e\u9ece\u5728\u9a6c\u80cc\u4e0a\u56de\u8fc7\u5934\u6765\u7b11\u9053\uff1a\u201c\u4f60\u7684\u6c49\u8bdd\u5b66\u5f97\u5f88\u6709\u6210\u7ee9\u554a\uff01\u201d\u8f6c\u773c\u95f4\u4e0e \u8d64\u8001\u6e29\u5df2\u662f\u53bb\u5f97\u8fdc\u4e86\u3002 \u3000\u3000\u201c\u5c11\u7237\u201d\u8fd9\u4e2a\u79f0\u547c\u4f3c\u4e00\u67c4\u94c1\u9524\u5411\u4ed6\u7838\u4e0b\uff0c\u674e\u601d\u5357\u4e00\u9635\u7729\u6655\uff0c\u5fc3\u5934\u5267\u9707\uff0c\u60f3\u8981\u53d1\u95ee\uff1a\u201c\u4f60 \u5bb6\u4e3b\u4eba\u59d3\u751a\u540d\u8c01\uff1f\u201d\u820c\u5934\u5374\u4f3c\u50f5\u786c\u4e00\u822c\uff0c\u4e0d\u542c\u4f7f\u5524\u3002\u8ff7\u8ff7\u7cca\u7cca\u4e2d\u53ea\u542c\u5f97\u90a3\u536b\u571f\u8bf4\u9053\uff1a\u201c\u5c11 \u7237\uff0c\u4f60\u5750\u4e00\u4f1a\u513f\uff0c\u5927\u4eba\u5c31\u6765\u3002\u201d\u539f\u6765\u5df2\u7ecf\u5230\u4e86\u5185\u5e10\u3002 \u3000\u3000\u8fd9\u5e10\u5e55\u597d\u4f3c\u6c49\u4eba\u7684\u4eba\u5bb6\uff0c\u4e0d\u8fc7\u662f\u7528\u76ae\u9769\u6765\u4ee3\u66ff\u5899\u58c1\u800c\u5df2\uff0c\u5e10\u5e55\u91cc\u95f4\u9694\u6210\u4e00\u95f4\u95f4\u7684\u623f\u5b50\uff0c \u8fd9\u5ea7\u201c\u5185\u5e10\u201d\u4fbf\u4f3c\u6c49\u4eba\u5bcc\u8d35\u4eba\u5bb6\u7684\u5ba2\u5385\uff0c\u6709\u684c\u6905\u51e0\u6848\u7b49\u6446\u8bbe\uff0c\u6848\u4e0a\u711a\u7740\u4e00\u7089\u9999\u4ee5\u8f9f\u81bb\u8165\uff0c\u8fd9 \u662f\u4e0a\u597d\u7684\u6a80\u9999\uff0c\u80fd\u4ee4\u4eba\u5fc3\u795e\u5b81\u9759\u3002\u53ef\u662f\u674e\u601d\u5357\u5374\u662f\u5fc3\u4e71\u5982\u9ebb\uff0c\u4e0d\u80fd\u81ea\u5df1\u3002 \u3000\u3000\u674e\u601d\u5357\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u65e2\u6765\u4e4b\uff0c\u5219\u5b89\u4e4b\u3002\u4e14\u5f85\u6c34\u843d\u77f3\u51fa\u4e4b\u540e\uff0c\u5e94\u8be5\u5982\u4f55\u5e94\u4ed8\uff0c\u90a3\u65f6\u518d\u884c\u8bbe \u6cd5\u3002\u201d\u5f53\u4e0b\u95ed\u76ee\u517b\u795e\uff0c\u5728\u64c5\u9999\u7ee6\u7ed5\u4e4b\u4e2d\u6df1\u6df1\u5438\u4e86\u51e0\u53e3\u6c14\uff0c\u597d\u4e0d\u5bb9\u6613\u628a\u7d0a\u4e71\u7684\u5fc3\u60c5\u7a0d\u7a0d\u5e73\u9759\u4e0b \u6765\uff0c\u53ea\u542c\u5f97\u201c\u55d2\u55d2\u201d\u7684\u9a6c\u9774\u8e0f\u5730\u58f0\u97f3\uff0c\u90a3\u4f4d\u201c\u674e\u5927\u4eba\u201d\u5df2\u7ecf\u6765\u4e86\u3002 \u3000\u3000\u674e\u601d\u5357\u7741\u773c\u4e00\u770b\uff0c\u53ea\u89c1\u6765\u7684\u662f\u4e2a\u56db\u5341\u591a\u5c81\u7684\u4e2d\u5e74\u4eba\uff0c\u62ab\u7740\u72d0\u88ad\uff0c\u7a7f\u7684\u662f\u8499\u53e4\u670d\u9970\u3002\u674e\u601d \u5357\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5988\u5e38\u8bf4\u6211\u50cf\u7239\u7239\uff0c\u4f46\u8fd9\u4eba\u7684\u76f8\u8c8c\u5374\u4e0d\u50cf\u6211\u3002\u201d\u4ed6\u7236\u4eb2\u88ab\u4fd8\u90a3\u5e74\uff0c\u4ed6\u624d\u4e09\u5c81\uff0c \u7236\u4eb2\u5bb9\u8c8c\u5728\u4ed6\u8111\u6d77\u4e4b\u4e2d\u5df2\u662f\u6beb\u65e0\u5370\u8c61\u3002 \u3000\u3000\u674e\u601d\u5357\u5b9a\u4e86\u5b9a\u795e\uff0c\u5148\u4e0d\u628a\u4ed6\u5f53\u4f5c\u7236\u4eb2\u770b\u5f85\uff0c\u7ad9\u8d77\u6765\u95ee\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u8bf7\u95ee\u4f60\u628a\u6211\u627e\u6765\uff0c \u7a76\u7adf\u662f\u4e3a\u4e86\u4f55\u4e8b\uff1f\u201d \u3000\u3000\u90a3\u4eba\u772f\u7740\u53cc\u773c\u628a\u674e\u601d\u5357\u6253\u91cf\u4e86\u597d\u4e00\u4f1a\uff0c\u8fd9\u624d\u8bf4\u9053\uff1a\u201c\u4f60\u53eb\u505a\u674e\u601d\u5357\uff0c\u5c71\u4e1c\u6b66\u57ce\u4eba\u6c0f\uff0c\u662f \u4e0d\u662f\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d \u3000\u3000\u90a3\u4eba\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\uff1a\u201c\u5357\u513f\uff0c\u602a\u4e0d\u5f97\u4f60\u4e0d\u8ba4\u5f97\u6211\u4e86\uff0c\u6211\u79bb\u5bb6\u90a3\u5e74\uff0c\u4f60\u624d\u4e09\u5c81\uff0c\u6211\u4e5f\u60f3\u4e0d \u5230\u4f60\u957f\u5f97\u8fd9\u4e48\u9ad8\u5927\u4e86\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u76ee\u77aa\u53e3\u5446\uff0c\u4e00\u65f6\u95f4\u4e0d\u77e5\u8bf4\u4e9b\u4ec0\u4e48\u624d\u597d\u3002 \u3000\u3000\u90a3\u4eba\u62cd\u4e86\u62cd\u4ed6\u7684\u80a9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4eca\u5e74\u662f\u4e8c\u5341\u4e09\u5c81\u4e86\u5427\uff0c\u6211\u8bb0\u5f97\u4f60\u7684\u751f\u65e5\u662f\u4e03\u6708\u4e8c\u5341\u4e8c \u65e5\uff0c\u6070\u597d\u5c31\u662f\u660e\u5929\u3002\u60f3\u4e0d\u5230\u6211\u76fc\u4e86\u4f60\u4e8c\u5341\u5e74\uff0c\u5982\u4eca\u624d\u5f97\u9aa8\u8089\u76f8\u9022\u3002\u660e\u5929\u6211\u53ef\u4ee5\u4e3a\u4f60\u5e86\u795d\u4e8c\u5341 \u4e09\u5c81\u7684\u751f\u8fb0\u4e86\u3002\u4f60\u8fd8\u4e0d\u53eb\u6211\u4e00\u58f0\u7239\u7239\u4e48\uff1f\u201d \u3000\u3000\u8fd9\u201c\u674e\u5927\u4eba\u201d\u8bf4\u5f97\u51fa\u4ed6\u7684\u751f\u8fb0\uff0c\u674e\u601d\u5357\u662f\u4e0d\u80fd\u518d\u6709\u6000\u7591\u7684\u4e86\uff0c\u53ea\u597d\u7528\u6697\u54d1\u7684\u55d3\u97f3\uff0c\u53eb\u4e86 \u4e00\u58f0\uff1a\u201c\u7239\u7239\u3002\u201d \u3000\u3000\u674e\u5e0c\u6d69\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5b69\u513f\uff0c\u4f60\u60f3\u4e0d\u5230\u5728\u8fd9\u91cc\u89c1\u7740\u7239\u7239\uff0c\u7239\u7239\u53c8\u56fa\u7136\u505a\u4e86\u5b98\u5427\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u5fcd\u4e0d\u4f4f\u8bf4\u9053\uff1a\u201c\u662f\u5440\u3002\u6211\u4ee5\u4e3a\u4f60\u8fd8\u5728\u5e93\u4f26\u6c60\u7554\u7684\u6d77\u62c9\u5c14\u5c6f\u57a6\u533a\u5462\u3002\u6211\u672c\u6765\u60f3\u5230\u90a3 \u91cc\u627e\u4f60\u7684\uff0c\u542c\u8bf4\u4f60\u5728\u90a3\u91cc\u5403\u4e86\u4e00\u4e9b\u82e6\u5934\u3002\u201d \u3000\u3000\u674e\u5e0c\u6d69\u9762\u8272\u5fae\u53d8\uff0c\u8bf4\u9053\uff1a\u201c\u662f\u8c01\u544a\u8bc9\u4f60\u7684\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u6211\u5728\u8def\u4e0a\u9047\u5230\u4e00\u4f4d\u59d3\u5b5f\u7684\u670b\u53cb\uff0c\u4ed6\u66fe\u7ecf\u5230\u8fc7\u90a3\u4e00\u5e26\u5730\u65b9\uff0c\u662f\u4ed6\u544a\u8bc9\u6211 \u7684\u3002\u201d \u3000\u3000\u674e\u5e0c\u6d69\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u8fd9\u4f4d\u59d3\u5b5f\u7684\u670b\u53cb\uff0c\u662f\u4e0d\u662f\u4ece\u6c5f\u5357\u6765\u7684\u5251\u5ba2\u5b5f\u5c11\u521a\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d\u5fc3\u91cc\u6709\u70b9\u5947\u602a\uff1a\u201c\u7239\u7239\u4ee5\u524d\u5728\u5bb6\u52a1\u519c\uff0c\u4e0e\u6b66\u6797\u4eba\u7269\u6781\u5c11\u6765\u5f80\uff0c\u4e8c \u5341\u5e74\u524d\uff0c\u5b5f\u5c11\u521a\u4e5f\u8fd8\u672a\u66fe\u6210\u540d\u3002\u8fd9\u4e8c\u5341\u5e74\u6765\uff0c\u7239\u7239\u5728\u8499\u53e4\u4f5c\u4fd8\u864f\uff0c\u66f4\u662f\u4e0e\u5916\u95f4\u9694\u7edd\uff0c\u4ed6\u5374\u600e \u4e48\u77e5\u9053\u6c5f\u5357\u5251\u5ba2\u5b5f\u5c11\u521a\u7684\u540d\u5b57\uff1f\u201d \u3000\u3000\u674e\u5e0c\u6d69\u70b9\u4e86\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u662f\u5403\u4e86\u8bb8\u591a\u82e6\u5934\uff0c\u4f46\u90a3\u90fd\u662f\u8fc7\u53bb\u7684\u4e8b\u60c5\u4e86\u3002\u73b0\u5728\u6211 \u662f\u82e6\u5c3d\u7518\u6765\uff0c\u5927\u6c57\u5bf9\u6211\u5f88\u4e0d\u9519\u5462\uff0c\u4f60\u77a7\u77a7\u6211\u8fd9\u513f\u867d\u7136\u4f4f\u7684\u662f\u5e10\u5e55\uff0c\u6bd4\u5bb6\u91cc\u8981\u597d\u5f97\u591a\u5427\uff1f\u5bf9 \u5566\uff0c\u8bf4\u8d77\u54b1\u4eec\u7684\u5bb6\uff0c\u4f60\u5988\u8fd8\u5728\u5427\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u5988\u8fd8\u6d3b\u7740\uff0c\u53ea\u662f\u8fd9\u51e0\u5e74\u5e74\u7eaa\u5927\u4e86\uff0c\u8eab\u4f53\u53ef\u6ca1\u6709\u4ee5\u524d\u786c\u6717\u4e86\u3002\u5979\u6302\u5ff5\u7740\u7239\uff0c \u5934\u53d1\u4e5f\u65e9\u5df2\u767d\u4e86\u3002\u5979\u5e0c\u671b\u6211\u627e\u7740\u7239\u7239\uff0c\u5c31\u548c\u7239\u7239\u56de\u53bb\u3002\u201d \u3000\u3000\u674e\u5e0c\u6d69\u9053\uff1a\u201c\u6211\u73b0\u5728\u505a\u4e86\u8499\u53e4\u4eba\u7684\u5b98\uff0c\u600e\u80fd\u8f7b\u6613\u56de\u53bb\uff1f\u800c\u4e14\u56de\u53bb\u53c8\u6709\u4ec0\u4e48\u597d\uff1f\u4e0d\u5982\u5728\u8fd9 \u91cc\u53ef\u4ee5\u4eab\u53d7\u8363\u534e\uff0c\u6211\u770b\u4e0d\u5982\u7b49\u5230\u9002\u5f53\u7684\u65f6\u673a\uff0c\u518d\u63a5\u4f60\u5988\u51fa\u6765\u7684\u597d\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u5fcd\u7740\u773c\u6cea\uff0c\u8bf4\u9053\uff1a\u201c\u7239\uff0c\u5988\u8bf4\u6211\u8fd9\u540d\u5b57\u662f\u4f60\u53d6\u7684\uff0c\u4f60\u8fd8\u8bb0\u5f97\u547d\u540d\u4e4b\u610f\u4e48\uff1f\u201d \u3000\u3000\u674e\u5e0c\u6d69\u6014\u4e86\u4e00\u6014\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u7684\u540d\u5b57\u5f53\u7136\u662f\u6211\u53d6\u7684\uff0c\u4f46\u4f60\u63d0\u8d77\u8fd9\u4e2a\u5374\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u7239\uff0c\u4f60\u7ed9\u6211\u53d6\u8fd9\u2018\u601d\u5357\u2019\u4e8c\u5b57\u4e3a\u540d\uff0c\u662f\u8981\u6211\u4e0d\u8981\u5fd8\u8bb0\u6545\u56fd\uff0c\u601d\u5ff5\u6c5f\u5357\u3002\u662f \u4e0d\u662f\u8fd9\u4e2a\u610f\u601d\uff1f\u201d \u3000\u3000\u674e\u5e0c\u6d69\u9053\uff1a\u201c\u8fd9\u53c8\u600e\u6837\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u4f60\u8981\u6211\u4e0d\u5fd8\u6545\u56fd\uff0c\u96be\u9053\u4f60\u81ea\u5df1\u53cd\u800c\u5fd8\u4e86\uff1f\u201d \u3000\u3000\u674e\u5e0c\u6d69\u76b1\u4e86\u76b1\u7709\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u53d7\u6069\u6df1\u5904\u4fbf\u4e3a\u5bb6\uff0c\u6211\u662f\u4e0d\u60f3\u8d70\u4e86\u3002\u4f55\u51b5\u5c31\u662f\u60f3\u8d70\u4e5f\u8d70\u4e0d\u6210 \u7684\u3002\u5728\u6d77\u62c9\u5c14\u65f6\uff0c\u6211\u4f55\u5c1d\u6ca1\u6709\u60f3\u8fc7\u8981\u9003\uff0c\u90a3\u65f6\u6211\u8fd8\u6709\u8bb8\u591a\u540c\u4f34\u5408\u8c0b\u5462\u3002\u4f46\u7ed3\u679c\u600e\u6837\uff1f\u9003\u8dd1\u7684 \u4eba\u4e0d\u662f\u7ed9\u6349\u56de\u6765\u66f4\u53d7\u82e6\u695a\uff0c\u5c31\u662f\u5728\u9003\u8dd1\u4e4b\u65f6\u7ed9\u5c04\u6740\u4e86\u3002\u73b0\u5728\u6211\u505a\u4e86\u5b98\uff0c\u8eab\u8fb9\u90fd\u662f\u8499\u53e4\u536b\u58eb\uff0c \u522b\u8bf4\u9003\u8dd1\uff0c\u53ea\u8981\u7ed9\u4ed6\u4eec\u77e5\u9053\u6211\u6709\u8fd9\u4e2a\u5ff5\u5934\uff0c\u5c31\u6d3b\u4e0d\u6210\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u7239\u7239\u7684\u5904\u5883\u5b69\u513f\u77e5\u9053\uff0c\u9003\u8d70\u7684\u786e\u662f\u5f88\u56f0\u96be\uff0c\u4e0d\u8fc7\u53ea\u8981\u7239\u7239\u6709\u51b3\u5fc3\uff0c\u5c31\u662f\u5192 \u5929\u5927\u7684\u5371\u9669\uff0c\u54b1\u4eec\u4e5f\u5f97\u8bd5\u8bd5\u3002\u6211\u76f8\u4fe1\u4e5f\u603b\u53ef\u4ee5\u627e\u51fa\u4e2a\u529e\u6cd5\u9003\u8d70\u7684\u3002\u201d \u3000\u3000\u674e\u5e0c\u6d69\u5927\u4e0d\u4ee5\u4e3a\u7136\u7684\u795e\u6c14\uff0c\u6447\u4e86\u6447\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u503c\u5f97\u7528\u6027\u547d\u53bb\u640f\u4e00\u640f\u5417\uff1f\u6211\u5728\u8fd9\u671b\u4e5f\u5e76 \u6ca1\u6709\u4ec0\u4e48\u4e0d\u597d\uff0c\u4e0e\u5176\u56de\u53bb\u53d7\u91d1\u864f\u7684\u6b3a\u51cc\uff0c\u4e0d\u5982\u5728\u8fd9\u91cc\u505a\u8499\u53e4\u4eba\u7684\u5b98\u3002\u8bf4\u5b9e\u5728\u8bdd\uff0c\u8499\u53e4\u4eba\u867d\u7136 \u6b8b\u66b4\uff0c\u4f46\u5bf9\u5f85\u54b1\u4eec\u6c49\u4eba\uff0c\u603b\u8fd8\u662f\u8981\u6bd4\u91d1\u864f\u597d\u4e9b\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u7239\u7239\uff0c\u8499\u53e4\u5927\u6c57\u4e3a\u4ec0\u4e48\u8981\u7ed9\u4f60\u505a\u5b98\uff0c\u4f60\u53ef\u77e5\u9053\uff1f\u6700\u8fd1\u51e0\u5e74\uff0c\u4ed6\u53c8\u4e3a\u4ec0\u4e48\u6539 \u53d8\u4e86\u7b56\u7565\uff0c\u5bf9\u5f85\u6c49\u4eba\u6ca1\u6709\u4ee5\u524d\u7684\u6b8b\u9177\u4e86\uff1f\u201d \u3000\u3000\u674e\u5e0c\u6d69\u9053\uff1a\u201c\u542c\u4f60\u8fd9\u6837\u8bf4\uff0c\u4f60\u597d\u50cf\u61c2\u5f97\u8bb8\u591a\u7684\u795e\u6c14\uff0c\u4f60\u5012\u8bf4\u8bf4\u770b\uff0c\u4ed6\u4e3a\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u4ed6\u4e3a\u4e86\u8981\u8fdb\u5175\u4e2d\u539f\uff0c\u4e0d\u80fd\u4e0d\u5229\u7528\u6c49\u4eba\u3002\u201d \u3000\u3000\u674e\u5e0c\u6d69\u9053\uff1a\u201c\u662f\u5440\uff0c\u54b1\u4eec\u7684\u534a\u58c1\u6c5f\u5c71\u662f\u7ed9\u91d1\u864f\u4fb5\u5360\u7684\uff0c\u5982\u4eca\u8499\u53e4\u7ed9\u54b1\u4eec\u6062\u590d\u4e2d\u539f\uff0c\u8fd9\u4e0d \u6b63\u662f\u597d\u4e8b\u5417\uff1f\u6211\u544a\u8bc9\u4f60\u4e00\u4ef6\u4e8b\u5427\uff0c\u5357\u5b8b\u548c\u8499\u53e4\u8ba1\u5212\u8054\u76df\u653b\u91d1\uff0c\u5982\u4eca\u5df2\u662f\u4fe1\u4f7f\u5f80\u8fd8\uff0c\u5c06\u6709\u6210\u8bae \u4e86\u3002\u4f60\u82e5\u662f\u5fe0\u4e8e\u5927\u5b8b\uff0c\u6709\u5fd7\u62a5\u56fd\u7684\u8bdd\uff0c\u4e0d\u5fc5\u6295\u5954\u6c5f\u5357\uff0c\u5728\u8fd9\u91cc\u4e3a\u5927\u6c57\u505a\u4e8b\uff0c\u4e5f\u4e00\u6837\u4e3a\u56fd\u5c3d \u529b\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u7239\u7239\uff0c\u4f60\u662f\u53ea\u77e5\u5176\u4e00\uff0c\u4e0d\u77e5\u5176\u4e8c\u3002\u201d \u3000\u3000\u674e\u5e0c\u6d69\u602b\u7136\u4e0d\u60a6\uff0c\u201c\u54fc\u201d\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u4f60\u521a\u548c\u6211\u89c1\u9762\uff0c\u5c31\u8981\u6559\u8bad\u8d77\u8001\u5b50\u6765\u4e86\u3002\u201d\u674e\u601d\u5357 \u9053\uff1a\u201c\u5b69\u513f\u4e0d\u6562\uff0c\u4f46\u8bf7\u7239\u7239\u60f3\u60f3\uff0c\u8499\u53e4\u706d\u91d1\u4e4b\u540e\uff0c\u4ed6\u80af\u4e0d\u80af\u8ba9\u5927\u5b8b\u82df\u5b89\u6c5f\u5357\uff1f\u4f9d\u6211\u770b\u6765\uff0c\u4ed6 \u548c\u5927\u5b8b\u8054\u76df\uff0c\u6b63\u662f\u5404\u4e2a\u51fb\u7834\u4e4b\u8ba1\uff01\u201d \u3000\u3000\u674e\u5e0c\u6d69\u9053\uff0c\u201c\u90a3\u53ea\u662f\u4f60\u7684\u63a8\u6d4b\u800c\u5df2\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u53f8\u9a6c\u662d\u4e4b\u5fc3\uff0c\u8def\u4eba\u7686\u89c1\u3002\u7239\u7239\uff0c\u4f60\u82e5\u4e0d\u53ca\u65e9\u5f53\u673a\u7acb\u65ad\uff0c\u5f85\u5230\u8499\u53e4\u548c\u5927\u5b8b\u4e00 \u6210\u654c\u56fd\uff0c\u90a3\u65f6\u4f60\u3001\u4f60\u2026\u2026\u201d \u3000\u3000\u674e\u5e0c\u6d69\u9053\uff1a\u201c\u6211\u600e\u6837\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u54ac\u4e86\u54ac\u7259\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u65f6\u4f60\u5c31\u8981\u6210\u4e3a\u4eba\u6240\u553e\u5f03\u7684\u5356\u56fd\u6c42\u8363\u4e4b\u8f88\u4e86\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u662f\u62fc\u7740\u53d7\u7236\u4eb2\u7684\u53d1\u6012\u8bf4\u51fa\u8fd9\u53e5\u8bdd\u7684\uff0c\u54ea\u77e5\u8fd9\u53e5\u8bdd\u8bf4\u51fa\u4e4b\u540e\uff0c\u674e\u5e0c\u6d69\u7684\u6012\u8272\u5012\u7f13\u548c \u4e86\u4e9b\uff0c\u53ea\u89c1\u4ed6\u9893\u7136\u5750\u4e0b\uff0c\u9762\u4e0a\u4e00\u9635\u9752\u4e00\u9635\u7ea2\uff0c\u6700\u540e\u53d8\u4e3a\u82cd\u767d\u3002 \u3000\u3000\u674e\u601d\u5357\u53ea\u9053\u7236\u4eb2\u5df2\u7ed9\u4ed6\u8bf4\u52a8\uff0c\u6b8a\u4e0d\u77e5\u674e\u5e0c\u6d69\u5fc3\u91cc\u60f3\u7684\u5374\u662f\uff0c\u201c\u8fd9\u5c0f\u5b50\u5014\u5f3a\u5f97\u5f88\uff01\u53ea\u7528\u7236 \u4eb2\u7684\u5a01\u4e25\u53ea\u6015\u662f\u538b\u4e0d\u4e86\u4ed6\u4e86\uff0c\u5f97\u6539\u7528\u8f6f\u529f\u624d\u6210\u3002\u201d \u3000\u3000\u4e8e\u662f\u674e\u5e0c\u6d69\u53f9\u4e86\u53e3\u6c14\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u5b69\u513f\uff0c\u4f60\u8bf4\u7684\u8bdd\u4e5f\u672a\u5c1d\u6ca1\u6709\u9053\u7406\uff0c\u4e0d\u8fc7\u5179\u4e8b\u4f53\u5927\uff0c \u54b1\u4eec\u8fd8\u5f97\u4ece\u957f\u8ba1\u8bae\u624d\u597d\u3002\u4f60\u7684\u6b66\u529f\u5b66\u5f97\u600e\u4e48\u6837\uff1f\u6211\u8001\u4e86\uff0c\u5c06\u6765\u82e5\u8981\u9003\u8d70\uff0c\u8fd8\u5f97\u4f9d\u9760\u4f60\u5462\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u8bf4\u6765\u60ed\u6127\uff0c\u5bb6\u4f20\u7684\u6b66\u529f\u5b69\u513f\u56e0\u4e3a\u6ca1\u4eba\u6559\u5bfc\uff0c\u4e00\u76f4\u6ca1\u6709\u7ec3\u8fc7\u3002\u4e0d\u8fc7\uff0c\u5e78\u4e8f\u53e6 \u5f97\u540d\u5e08\uff0c\u5b66\u4e86\u4e00\u4e9b\u5c11\u6797\u7684\u6b66\u529f\u3002\u5b69\u513f\u6b63\u60f3\u5411\u7239\u7239\u7980\u544a\uff0c\u8fd9\u4f4d\u6069\u5e08\u2014\u2014\u201d \u3000\u3000\u674e\u5e0c\u6d69\u6025\u4e0d\u53ca\u5f85\uff0c\u62a2\u7740\u95ee\u9053\uff1a\u201c\u4f60\u7684\u5e08\u7236\u662f\u8c01\uff1f\u201d\u5fc3\u91cc\u6697\u6697\u6b22\u559c\uff1a\u201c\u5e78\u4e8f\u4ed6\u6ca1\u6709\u5b66\u8fc7\u5bb6 \u4f20\u7684\u529f\u592b\uff0c\u5426\u5219\u4ed6\u8981\u6211\u6559\uff0c\u6211\u53ef\u662f\u6559\u4e0d\u51fa\u6765\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u597d\u6559\u7239\u7239\u6b22\u559c\uff0c\u5b69\u513f\u7684\u6069\u5e08\u662f\u8c37\u5927\u4fa0\u3002\u516d\u5c81\u90a3\u5e74\uff0c\u6069\u5e08\u627e\u4e0a\u95e8\u6765\u6536\u6211\u4e3a\u5f92 \u7684\u3002\u8fd9\u90fd\u662f\u5168\u9760\u7239\u7239\u7684\u798f\u836b\u3002\u201d \u3000\u3000\u674e\u5e0c\u6d69\u9053\uff1a\u201c\u54ea\u4f4d\u8c37\u5927\u4fa0\uff1f\u4f55\u4ee5\u8bf4\u662f\u9760\u4e86\u6211\u7684\u798f\u836b\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u6069\u5e08\u5c0a\u53f7\u5e73\u9633\uff0c\u8c37\u5e73\u9633\u8c37\u5927\u4fa0\uff0c\u7239\u7239\u4f60\u8fd8\u8bb0\u5f97\u4e48\uff1f\u201d \u3000\u3000\u674e\u5e0c\u6d69\u832b\u7136\u9053\uff1a\u201c\u8c37\u5e73\u9633\uff0c\u4ed6\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u6014\u4e86\u4e00\u6014\uff0c\u8bf4\u9053\uff1a\u201c\u8c37\u6069\u5e08\u662f\u7239\u7239\u7684\u597d\u670b\u53cb\uff0c\u7239\u7239\u600e\u4e48\u5fd8\u8bb0\u4e86\uff1f\u8c37\u6069\u5e08\u5c31\u662f\u56e0\u4e3a \u542c\u5f97\u7239\u7239\u7684\u4e0d\u5e78\u6d88\u606f\uff0c\u7279\u5730\u6765\u63a2\u671b\u6211\u4eec\u7684\u3002\u201d \u3000\u3000\u674e\u5e0c\u6d69\u62cd\u4e86\u4e00\u62cd\u8111\u888b\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4f9d\u7a00\u60f3\u8d77\u6765\u4e86\uff0c\u5c11\u5e74\u65f6\u5019\uff0c\u6211\u662f\u6709\u8fc7\u4e00\u4f4d\u59d3\u8c37\u7684\u670b \u53cb\u3002\u5509\uff0c\u950b\u5ae1\u4f59\u751f\uff0c\u7ecf\u8fc7\u4e86\u4e8c\u5341\u5e74\u7684\u6d41\u79bb\u4e4b\u82e6\uff0c\u4ee5\u524d\u7684\u4e8b\u604d\u5982\u9694\u4e16\uff0c\u4ec0\u4e48\u90fd\u8bb0\u4e0d\u5f97\u4e86\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u5fc3\u91cc\u4e00\u9178\uff0c\u60f3\u9053\uff1a\u201c\u4e8c\u5341\u5e74\u7684\u4fd8\u864f\u751f\u6daf\uff0c\u7684\u786e\u662f\u4e0d\u5bb9\u6613\u5fcd\u53d7\u7684\u3002\u5982\u679c\u6362\u4e86\u6211\uff0c\u53ea \u6015\u4f1a\u8981\u53d1\u75af\u4e86\u3002\u4e0d\u8fc7\uff0c\u53d7\u4e86\u8fd9\u4e48\u591a\u82e6\u695a\uff0c\u5e94\u8be5\u66f4\u6068\u654c\u4eba\u624d\u5bf9\u3002\u5374\u4e0d\u77e5\u7239\u7239\u662f\u600e\u4e48\u4e2a\u60f3\u6cd5 \u7684\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u63a5\u4e0b\u53bb\u8bf4\u9053\uff1a\u201c\u6069\u5e08\u4e5f\u662f\u5f88\u60f3\u5ff5\u7239\u7239\uff0c\u5e0c\u671b\u7239\u7239\u80fd\u6a84\u56de \u3000\u3000\u674e\u5e0c\u6d69\u8bf4\u9053\uff1a\u201c\u4f60\u89c1\u8fc7\u6728\u534e\u9ece\u548c\u8d64\u8001\u6e29\u4e24\u4eba\u7684\u6b66\u529f\u4e86\uff0c\u662f\u4e48\uff1f\u4f60\u81ea\u95ee\u80fd\u591f\u80dc\u5f97\u8fc7\u4ed6\u4eec \u5417\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u8d64\u8001\u6e29\u5b69\u513f\u6216\u8005\u53ef\u4ee5\u5bf9\u4ed8\u5f97\u4e86\uff0c\u6728\u534e\u9ece\u7684\u6b66\u529f\u5b9e\u662f\u8fdc\u8fdc\u5728\u5b69\u513f\u4e4b\u4e0a\u3002\u201d \u3000\u3000\u674e\u5e0c\u6d69\u9053\uff1a\u201c\u5927\u6c57\u624b\u4e0b\u6709\u5341\u4e8c\u91d1\u521a\uff0c\u6728\u534e\u9ece\u4ec5\u6392\u540d\u7b2c\u4e09\uff0c\u7b2c\u4e00\u7b2c\u4e8c\u90a3\u4e24\u4e2a\u4eba\u66f4\u662f\u5389\u5bb3\uff01 \u4f60\u8fde\u6728\u534e\u9ece\u4e5f\u80dc\u4e0d\u4e86\uff0c\u600e\u80fd\u9003\u8d70\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u4fd7\u8bed\u8bf4\u6b7b\u91cc\u9003\u751f\uff0c\u5b69\u513f\u662f\u7518\u613f\u820d\u4e86\u6027\u547d\u4fdd\u62a4\u7239\u7239\u91cd\u5f52\u6545\u58eb\u7684\uff0c\u53ea\u4e0d\u77e5\u7239 \u7239\u2026\u2026\u201d \u3000\u3000\u674e\u5e0c\u6d69\u9053\uff1a\u201c\u6211\u4f55\u5c1d\u4e0d\u60f3\u56de\u53bb\uff0c\u4e0d\u8fc7\u4e0d\u662f\u50cf\u4f60\u8fd9\u6837\u9c81\u83bd\uff0c\u9003\u4e0d\u51fa\u53bb\uff0c\u820d\u4e86\u6027\u547d\u4e5f\u662f\u6789 \u7136\u3002\u6211\u5012\u53e6\u6709\u4e00\u4e2a\u4e3b\u610f\uff0c\u53ea\u662f\u8981\u4f60\u5fcd\u8010\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u5b69\u513f\u5e74\u8f7b\u8bc6\u6d45\uff0c\u8bf7\u7239\u7239\u6559\u5bfc\u3002\u201d\u5fc3\u91cc\u6697\u6697\u6b22\u559c\uff1a\u201c\u53ea\u8981\u7239\u7239\u613f\u610f\u60f3\u529e\u6cd5\uff0c \u90a3\u5c31\u597d\u4e86\uff01\u201d \u3000\u3000\u674e\u5e0c\u6d69\u559d\u4e86\u4e00\u53e3\u6d53\u8336\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u636e\u6211\u6240\u77e5\uff0c\u4e0d\u51fa\u4e09\u4e2a\u6708\uff0c\u5927\u6c57\u5c31\u8981\u51fa\u5175\u4f10\u91d1\u3002\u6211\u4e5f \u53ef\u80fd\u968f\u519b\u51fa\u5f81\u3002\u5728\u6218\u573a\u4e0a\u9003\u8dd1\u7684\u673a\u4f1a\u591a\u5f97\u591a\uff0c\u800c\u4e14\u662f\u5728\u54b1\u4eec\u6c49\u4eba\u7684\u5730\u65b9\uff0c\u8dd1\u4e86\u51fa\u53bb\u4e5f\u6709\u4eba\u7167 \u5e94\u3002\u4e0d\u6bd4\u8fd9\u91cc\uff0c\u9003\u51fa\u548c\u6797\uff0c\u8fd8\u8981\u8fdb\u6208\u58c1\uff0c\u884c\u8def\u7684\u8270\u96be\u90a3\u662f\u4e0d\u5fc5\u8bf4\u4e86\uff0c\u4e00\u8def\u4e0a\u8fd8\u5f97\u5728\u8499\u53e4\u4eba\u7684 \u773c\u775b\u76d1\u89c6\u4e4b\u4e0b\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u542c\u7236\u4eb2\u8bf4\u5f97\u6709\u7406\uff0c\u5fc3\u91cc\u5f88\u662f\u6b22\u559c\uff0c\u7b54\u9053\uff1a\u201c\u7239\u7239\u8ba1\u8651\u5468\u8be6\uff0c\u8fd9\u51e0\u4e2a\u6708\u7684\u529f\u592b\u5b69\u513f \u5f53\u7136\u662f\u53ef\u4ee5\u5fcd\u8010\u7684\u3002\u201d \u3000\u3000\u674e\u5e0c\u6d69\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4e48\u54b1\u4eec\u4eca\u665a\u5c31\u8c08\u5230\u8fd9\u91cc\u5427\uff0c\u4f60\u8fde\u65e5\u5954\u6ce2\uff0c\u4e5f\u8be5\u65e9\u4e9b\u7761\u4e86\u3002\u660e\u5929\u6211\u5e26 \u4f60\u53bb\u8c12\u89c1\u5927\u6c57\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u6210\u5409\u601d\u6c57\u8981\u89c1\u6211\u4e48\uff1f\u82e5\u662f\u53ef\u514d\u5c31\u514d\u4e86\u5427\u3002\u201d \u3000\u3000\u674e\u5e0c\u6d69\u9053\uff1a\u201c\u4f60\u662f\u6728\u534e\u9ece\u548c\u8d64\u8001\u6e29\u5e26\u56de\u6765\u7684\uff0c\u5927\u6c57\u5df2\u7ecf\u77e5\u9053\u8fd9\u4ef6\u4e8b\u60c5\u4e86\uff0c\u4ed6\u4eca\u5929\u8fd8\u95ee\u8d77 \u6211\u5462\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u76b1\u4e86\u76b1\u7709\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u90a3\u4e48\u6211\u53ea\u597d\u548c\u7239\u7239\u53bb\u4e86\u3002\u201d \u3000\u3000\u674e\u5e0c\u6d69\u4e5f\u76b1\u4e86\u76b1\u7709\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u53ef\u5f97\u63d0\u9192\u4f60\uff0c\u4f60\u5fc3\u91cc\u4e0d\u613f\u610f\u4e5f\u4e0d\u80fd\u9732\u51fa\u4e0d\u613f\u610f\u7684\u6837\u5b50 \u6765\u3002\u4f60\u8981\u77e5\u9053\uff0c\u4f60\u8fd8\u5fc5\u987b\u53d6\u5f97\u5927\u6c57\u7684\u4fe1\u4efb\uff0c\u4ee5\u540e\u624d\u80fd\u591f\u6709\u673a\u4f1a\u9003\u8dd1\uff0c\u4f60\u61c2\u4e0d\u61c2\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u970d\u7136\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u7239\u7239\u6559\u8bad\u5f97\u662f\u3002\u5b69\u513f\u7406\u4f1a\u3002\u201d \u3000\u3000\u674e\u5e0c\u6d69\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4e48\u4f60\u53bb\u7761\u5427\u3002\u660e\u5929\u6211\u518d\u628a\u5e94\u8be5\u6ce8\u610f\u7684\u4e8b\u60c5\u8be6\u7ec6\u5730\u544a\u8bc9\u4f60\u3002\u201d\u5f53\u4e0b\u628a \u4e00\u4e2a\u536b\u58eb\u53eb\u6765\uff0c\u5e26\u674e\u601d\u5357\u5230\u5916\u5e10\u7684\u4e00\u95f4\u5367\u623f\u3002\u539f\u6765\u674e\u5e0c\u6d69\u5728\u8499\u53e4\u5df2\u7ecf\u8ba8\u4e86\u59ec\u59be\uff0c\u6682\u65f6\u4e0d\u613f\u610f \u8ba9\u513f\u5b50\u77e5\u9053\u3002 \u3000\u3000\u674e\u601d\u5357\u867d\u7136\u75b2\u5026\uff0c\u4f46\u7ffb\u6765\u8986\u53bb\u7684\u5374\u662f\u7761\u4e0d\u7740\u89c9\u3002\u4ed6\u672c\u6765\u4ee5\u4e3a\u8981\u5386\u5c3d\u8270\u96be\u624d\u627e\u5f97\u7740\u7236\u4eb2 \u7684\uff0c\u60f3\u4e0d\u5230\u8fd9\u6837\u5bb9\u6613\u5c31\u89c1\u7740\u4e86\u3002\u4f46\u5728\u8fd9\u6837\u7684\u5883\u9047\u4e0b\u7236\u5b50\u76f8\u9022\uff0c\u5374\u53c8\u672a\u514d\u592a\u8fc7\u51fa\u4e4e\u4ed6\u7684\u610f\u6599\u4e4b \u5916\u3002 \u3000\u3000\u9ed1\u6697\u4e2d\u4f3c\u4e4e\u73b0\u51fa\u4e86\u516b\u4e2a\u5927\u5b57\uff1a\u201c\u4e3a\u864e\u4f5c\u6005\uff0c\u5fc5\u53d6\u4f60\u547d\uff01\u201d\u8fd9\u662f\u5b5f\u5c11\u521a\u5728\u6c99\u6f20\u4e0a\u7ed9\u4ed6\u7684\u7559 \u8a00\u3002\u674e\u601d\u5357\u5149\u660e\u78ca\u843d\uff0c\u81ea\u95ee\u51b3\u4e0d\u81f3\u4e8e\u201c\u4e3a\u864e\u4f5c\u6005\u201d\uff0c\u56e0\u6b64\u4ed6\u5012\u4e0d\u662f\u5bb3\u6015\u5b5f\u5c11\u521a\u53d6\u4ed6\u6027\u547d\uff0c\u800c \u662f\u4ece\u5b5f\u5c11\u521a\u7559\u4e66\u793a\u8b66\u7684\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u4e0d\u7531\u5f97\u4ed6\u4e0d\u60f3\u6df1\u4e86\u4e00\u5c42\u3002 \u3000\u3000\u201c\u5b5f\u5927\u4fa0\u66fe\u60f3\u6740\u6211\u4ee5\u9664\u2018\u540e\u60a3\u2019\uff0c\u4e34\u8d70\u53c8\u7559\u4e0b\u8fd9\u6837\u7684\u516b\u4e2a\u5927\u5b57\u3002\u4f9d\u6b64\u770b\u6765\uff0c\u53ea\u6015\u7239\u7239\u8fd8 \u4e0d\u662f\u88ab\u8feb\u505a\u5b98\u7684\u5462\uff0c\u5f88\u53ef\u80fd\u4ed6\u5df2\u7ecf\u662f\u52a9\u7ea3\u4e3a\u8650\uff0c\u505a\u8fc7\u4e00\u4e9b\u574f\u4e8b\u7684\u4e86\u3002\u8981\u4e0d\u7136\u5b5f\u5927\u4fa0\u4e0d\u4f1a\u5982\u6b64 \u6068\u4ed6\u3002\u8fd9\u2018\u4e3a\u864e\u4f5c\u6005\u2019\u56db\u5b57\uff0c\u4e00\u65b9\u9762\u56fa\u7136\u662f\u8b66\u544a\u6211\uff0c\u4e00\u65b9\u9762\u6050\u6015\u4e5f\u662f\u6307\u6211\u7684\u7239\u7239\u3002\u201d \u3000\u3000\u60f3\u81f3\u6b64\u5904\uff0c\u674e\u601d\u5357\u751a\u611f\u75db\u5fc3\uff0c\u4e0d\u7531\u5f97\u53c8\u60f3\u8d77\u4e86\u6bcd\u4eb2\u6240\u66fe\u544a\u8bc9\u4ed6\u7684\u8bb8\u591a\u6709\u5173\u7236\u4eb2\u7684\u4e8b\u60c5\u3002 \u3000\u3000\u674e\u601d\u5357\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5988\u5e38\u63d0\u9192\u6211\uff0c\u53eb\u6211\u5207\u4e0d\u53ef\u5fd8\u8bb0\u4e86\u7239\u7239\u7ed9\u6211\u547d\u540d\u4e4b\u610f\u3002\u5979\u8bf4\u7239\u7239\u867d\u7136 \u662f\u9690\u5c45\u52a1\u519c\uff0c\u4f46\u4ecd\u662f\u58ee\u5fc3\u672a\u5df2\uff0c\u65f6\u6000\u590d\u56fd\u4e4b\u601d\u7684\u3002\u4ed6\u4e0d\u4f46\u548c\u6297\u91d1\u7684\u5fd7\u58eb\u79d8\u5bc6\u5f80\u8fd8\uff0c\u800c\u4e14\u8fd8\u66fe \u7ecf\u8fdb\u884c\u8fc7\u4e00\u9879\u5de5\u4f5c\uff0c\u8981\u6ce8\u91ca\u4e00\u90e8\u5175\u4e66\uff0c\u732e\u7ed9\u4e00\u4f4d\u4e49\u519b\u9996\u9886\uff0c\u53ef\u60dc\u4e66\u672a\u7f16\u6210\uff0c\u6ce8\u91ca\u7684\u5de5\u4f5c\u624d\u521a \u521a\u5f00\u59cb\uff0c\u4ed6\u5c31\u7ed9\u8499\u53e4\u9791\u5b50\u63b3\u53bb\u4e86\u3002\u201d \u3000\u3000\u539f\u6765\u674e\u601d\u5357\u7684\u5148\u7956\u4e43\u662f\u5317\u5b8b\u540d\u5c06\u97e9\u4e16\u5fe0\u7684\u90e8\u4e0b\uff0c\u4ed6\u66fe\u628a\u97e9\u4e16\u5fe0\u7684\u884c\u519b\u7528\u5175\u4e4b\u9053\u8bb0\u5f55\u4e0b \u6765\uff0c\u5176\u4e2d\u5305\u62ec\u6709\u6bcf\u6b21\u6218\u5f79\u7684\u7ecf\u8fc7\uff0c\u97e9\u4e16\u5fe0\u4e34\u9635\u7684\u90e8\u7f72\uff0c\u53e3\u6388\u7684\u5175\u6cd5\uff0c\u5e73\u65f6\u7ec3\u5175\u7684\u6cd5\u5b50\uff0c\u7b49 \u7b49\u3002\u4f46\u8fd9\u4e9b\u8bb0\u8f7d\u90fd\u662f\u96f6\u6563\u7684\uff0c\u672a\u66fe\u7f16\u6210\u4e00\u672c\u6709\u7cfb\u7edf\u7684\u5175\u4e66\u3002\u8fd9\u4e9b\u96f6\u7bc7\u65ad\u7b80\uff0c\u4f20\u5230\u4e86\u4ed6\u7236\u4eb2\u674e \u5e0c\u6d69\u7684\u624b\u91cc\uff0c\u674e\u5e0c\u6d69\u624d\u53d1\u4e0b\u5b8f\u613f\uff0c\u8981\u7ee7\u627f\u5148\u4eba\u9057\u5fd7\uff0c\u7f16\u7e82\u6210\u4e66\uff0c\u5e76\u52a0\u6ce8\u91ca\u3001\u6f14\u7ece\u9610\u626c\u3002 \u3000\u3000\u674e\u601d\u5357\u60f3\u8d77\u4e86\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u4e0d\u7531\u5f97\u6697\u6697\u53f9\u4e86\u53e3\u6c14\uff0c\u5fc3\u9053\uff1a\u201c\u771f\u60f3\u4e0d\u5230\u4e00\u4e2a\u4eba\u4f1a\u53d8\u5f97\u8fd9\u6837\u5389 \u5bb3\uff0c\u5374\u4e0d\u77e5\u7239\u7239\u8fd8\u8bb0\u5f97\u8fd9\u4ef6\u4e8b\u5417\uff1f\u201d \u3000\u3000\u539f\u6765\u8fd9\u90e8\u672a\u5b8c\u6210\u7684\u5175\u4e66\uff0c\u674e\u601d\u5357\u5df2\u7ecf\u5e26\u6765\uff0c\u53ea\u56e0\u7236\u5b50\u521d\u4f1a\uff0c\u8981\u8bf4\u7684\u4e8b\u592a\u591a\uff0c\u672a\u6709\u65f6\u95f4\u8c08 \u5230\u8fd9\u4ef6\u4e8b\u60c5\u3002\u201c\u4e14\u5f85\u660e\u5929\u56de\u6765\u4e4b\u540e\uff0c\u6211\u518d\u95ee\u4e00\u5411\u4ed6\u3002\u201d\u674e\u601d\u5357\u5fc3\u60f3\u3002 \u3000\u3000\u674e\u601d\u5357\u53c8\u60f3\u8d77\u4ed6\u6bcd\u4eb2\u66fe\u7ecf\u544a\u8bc9\u4ed6\uff0c\u7239\u7239\u662f\u4e00\u4e2a\u5341\u5206\u6b63\u76f4\uff0c\u7518\u4e8e\u6de1\u6cca\u7684\u4eba\uff0c\u672c\u6765\u7239\u7239\u662f\u5c06 \u95e8\u4e4b\u540e\uff0c\u82e5\u679c\u6709\u5fc3\u5bcc\u8d35\uff0c\u5c3d\u53ef\u51fa\u4ed5\u91d1\u5ef7\uff0c\u4f46\u4ed6\u5374\u7518\u613f\u52a1\u519c\u4e3a\u751f\uff0c\u65e5\u5b50\u5b9e\u5728\u8fc7\u4e0d\u53bb\uff0c\u5c31\u517c\u6559\u8499 \u9986\uff0c\u5b81\u53ef\u6328\u82e6\uff0c\u4e5f\u4e0d\u613f\u8d2a\u56fe\u5bcc\u8d35\uff0c\u53ef\u89c1\u4ed6\u5fd7\u8da3\u7684\u4e00\u6591\u3002\u201c\u8c01\u77e5\u7239\u7239\u4e0d\u613f\u505a\u91d1\u864f\u7684\u5b98\uff0c\u5982\u4eca\u5374 \u505a\u4e86\u8499\u53e4\u7684\u5b98\u3002\u96be\u9053\u5f53\u771f\u662f\u5343\u53e4\u8270\u96be\u552f\u4e00\u6b7b\uff0c\u4ee5\u81f4\u50cf\u7239\u7239\u8fd9\u6837\u7684\u4eba\uff0c\u4e5f\u4f1a\u5728\u8499\u53e4\u9791\u5b50\u7684\u6deb\u5a01 \u4e4b\u4e0b\u53d8\u8282\u4e86\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u72ec\u81ea\u55df\u53f9\u4e86\u4e00\u4f1a\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u597d\u5728\u7239\u7239\u8ff7\u9014\u672a\u8fdc\uff0c\u5982\u4eca\u5df2\u662f\u542c\u4ece\u6211\u7684\u89c4\u529d\uff0c\u613f \u5f03\u5b98\u800c\u9003\u3002\u4ed6\u5728\u8499\u53e4\u4e8c\u5341\u5e74\uff0c\u719f\u6089\u8499\u53e4\u5185\u60c5\uff0c\u82e5\u679c\u91cd\u5f52\u6545\u56fd\uff0c\u5c06\u6765\u5927\u5b8b\u62b5\u6297\u8499\u53e4\u4fb5\u7565\u4e4b\u65f6\uff0c \u4ed6\u8fd9\u6837\u7684\u4eba\u6750\u5c31\u6b63\u7528\u5f97\u7740\u4e86\u3002\u53ea\u8981\u4ed6\u4eca\u540e\u5c3d\u5fe0\u62a5\u56fd\uff0c\u5373\u4f7f\u4ed6\u505a\u8fc7\u4e00\u4e9b\u574f\u4e8b\uff0c\u4e5f\u8db3\u53ef\u4ee5\u5c06\u529f\u8d4e \u7f6a\u3002\u201d\u674e\u601d\u5357\u53ea\u6709\u4ece\u597d\u5904\u7740\u60f3\uff0c\u81ea\u5bbd\u81ea\u89e3\uff0c\u5fc3\u4e2d\u5b89\u6170\u4e86\u8bb8\u591a\u3002 \u3000\u3000\u674e\u601d\u5357\u6b63\u81ea\u80e1\u601d\u4e71\u60f3\uff0c\u5ffd\u542c\u5f97\u4f3c\u6709\u8f7b\u5fae\u7684\u58f0\u606f\uff0c\u6089\u6089\u7d22\u7d22\uff0c\u4f3c\u662f\u6709\u4eba\u62c9\u5f00\u4ed6\u7684\u7bf7\u5e10\u3002\u674e \u601d\u5357\u5403\u4e86\u4e00\u60ca\uff0c\u8df3\u8d77\u6765\u6b63\u60f3\u559d\u95ee\uff0c\u53ea\u542c\u5f97\u90a3\u4e2a\u4eba\u5df2\u5728\u8bf4\u9053\uff1a\u201c\u5664\u58f0\uff0c\u6211\u4e0d\u662f\u6765\u5bb3\u4f60\u7684\u3002\u6211\u6709 \u8981\u7d27\u7684\u4e8b\u548c\u4f60\u8bf4\uff01\u201d\u58f0\u97f3\u6781\u5c0f\uff0c\u4f46\u5374\u542c\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u663e\u7136\u90a3\u4eba\u5177\u6709\u201c\u4f20\u97f3\u5165\u5bc6\u201d\u7684\u529f\u592b\u3002 \u3000\u3000\u674e\u601d\u5357\u60ca\u7591\u4e0d\u5b9a\uff0c\u4e00\u65f6\u4e4b\u95f4\u4e0d\u77e5\u662f\u58f0\u5f20\u7684\u597d\uff0c\u8fd8\u662f\u5148\u542c\u542c\u8fd9\u4eba\u8bf4\u4e9b\u4ec0\u4e48\u7684\u597d\u3002\u5c31\u5728\u6b64 \u65f6\uff0c\u731b\u542c\u5f97\u4e00\u58f0\u559d\u9053\uff1a\u201c\u6349\u523a\u5ba2\uff01\u201d\u5e10\u4e2d\u7684\u536b\u58eb\u5df2\u7ecf\u53d1\u73b0\u90a3\u4eba\u7684\u8e2a\u8ff9\u4e86\u3002 \u3000\u3000\u90a3\u4eba\u521a\u521a\u62c9\u5f00\u5e10\u5e55\uff0c\u4fbf\u542c\u5f97\u536b\u571f\u7684\u5450\u558a\uff0c\u884c\u85cf\u8d25\u9732\uff0c\u5f53\u7136\u662f\u4e0d\u80fd\u8fdb\u53bb\u548c\u674e\u601d\u5357\u5077\u4f1a\u7684 \u4e86\u3002\u4f46\u4ed6\u827a\u9ad8\u80c6\u5927\uff0c\u8fd8\u662f\u8d81\u7740\u536b\u58eb\u5c06\u5230\u672a\u5230\u7684\u8fd9\u4e00\u77ac\u95f4\uff0c\u638f\u51fa\u4e00\u56e2\u4e1c\u897f\uff0c\u628a\u624b\u4e00\u626c\uff0c\u63b7\u5165\u5e10 \u5e55\uff0c\u8fd9\u624d\u9000\u51fa\u3002 \u3000\u3000\u674e\u601d\u5357\u542c\u98ce\u8fa8\u5668\uff0c\u77e5\u9053\u4e0d\u662f\u950b\u5229\u7684\u6697\u5668\uff0c\u4fbf\u5373\u628a\u624b\u4e00\u626c\uff0c\u63a5\u4e86\u8fc7\u6765\uff0c\u843d\u5165\u638c\u5fc3\uff0c\u4e00\u634f\u4e4b \u4e0b\uff0c\u5df2\u77e5\u662f\u4e00\u56e2\u7eb8\u56e2\u3002 \u3000\u3000\u674e\u601d\u5357\u60ca\u7591\u4e0d\u5b9a\uff0c\u5148\u4e0d\u6253\u5f00\u6765\u770b\uff0c\u85cf\u597d\u7eb8\u56e2\uff0c\u8dd1\u51fa\u53bb\u770b\uff0c\u53ea\u89c1\u90a3\u4eba\u5df2\u4e0a\u4e86\u7bf7\u9876\uff0c\u4e24\u4e2a\u536b \u58eb\u4ea6\u5df2\u8ffd\u4e86\u4e0a\u53bb\uff0c\u548c\u4ed6\u5f00\u59cb\u4ea4\u624b\u4e86\u3002 \u3000\u3000\u674e\u5e0c\u6d69\u6240\u4f4f\u7684\u8fd9\u5ea7\u5e10\u5e55\u5360\u6709\u5341\u51e0\u95f4\u623f\u5b50\u4e4b\u5e7f\uff0c\u7bf7\u9876\u5e73\u5766\uff0c\u5728\u4e0a\u9762\u4ea4\u624b\uff0c\u5982\u540c\u5e73\u5730\u3002\u4e0d \u8fc7\uff0c\u8fd9\u5e10\u5e55\u867d\u7136\u662f\u725b\u76ae\u505a\u7684\uff0c\u8f83\u5e03\u5339\u575a\u97e7\uff0c\u80fd\u591f\u622a\u91cd\uff0c\u4f46\u6709\u4e09\u4e2a\u5927\u4eba\u5728\u4e0a\u9762\u8ffd\u9010\uff0c\u5e10\u7bf7\u5e76\u4e0d \u5012\u584c\uff0c\u5219\u8fd9\u4e09\u4e2a\u4eba\u7684\u8f7b\u529f\u4e5f\u5c31\u53ef\u60f3\u800c\u77e5\u4e86\u3002 \u3000\u3000\u674e\u601d\u5357\u63d0\u4e00\u53e3\u6c14\uff0c\u8ddf\u7740\u4e5f\u8df3\u4e0a\u53bb\u3002\u53ea\u89c1\u5251\u5f71\u5200\u5149\uff0c\u8000\u773c\u751f\u8f89\u3002\u674e\u601d\u5357\u4e0d\u7531\u5f97\u6697\u6697\u5403\u4e86\u4e00 \u60ca\uff0c\u4e0d\u4f46\u90a3\u8499\u9762\u7684\u201c\u523a\u5ba2\u201d\u5251\u6cd5\u9ad8\u5f3a\uff0c\u8fdc\u8fdc\u5728\u4ed6\u4e4b\u4e0a\uff0c\u90a3\u4e24\u4e2a\u536b\u58eb\u7684\u6b66\u529f\u4ea6\u975e\u6cdb\u6cdb\uff0c\u81f3\u5c11\u4e5f \u4e0d\u5728\u4ed6\u4e4b\u4e0b\u3002 \u3000\u3000\u90a3\u4e2a\u4eba\u4f3c\u662f\u65e0\u5fc3\u604b\u6218\uff0c\u84e6\u5730\u559d\u4e00\u58f0\uff1a\u201c\u7740\uff01\u201d\u4e00\u540d\u536b\u58eb\u5de6\u81c2\u7ed9\u4ed6\u7684\u5251\u950b\u5212\u5f00\u4e86\u4e00\u9053\u4f24 \u53e3\uff0c\u6eda\u4e0b\u5e10\u7bf7\uff0c\u90a3\u4eba\u8f6c\u8eab\u4fbf\u9003\u3002 \u3000\u3000\u7b2c\u4e8c\u4e2a\u536b\u58eb\u8ddf\u7740\u4e5f\u662f\u559d\u4e00\u58f0\u201c\u7740\uff01\u201d\u4e00\u626c\u624b\u4fbf\u662f\u4e09\u67c4\u98de\u5200\uff0c\u7535\u5c04\u800c\u51fa\u3002\u8fd9\u4e2a\u536b\u58eb\u662f\u4e2a\u6697 \u5668\u9ad8\u624b\uff0c\u6700\u540e\u90a3\u67c4\u98de\u5200\u540e\u53d1\u5148\u81f3\uff0c\u90a3\u4eba\u4e00\u6765\u662f\u65e0\u5fc3\u604b\u6218\uff0c\u53ea\u987e\u9003\u8dd1\uff1b\u4e8c\u6765\u90a3\u536b\u58eb\u7684\u98de\u5200\u53d1\u5f97 \u5982\u6b64\u5de7\u5999\uff0c\u4e5f\u662f\u4ed6\u59cb\u6599\u4e4b\u6240\u4e0d\u53ca\uff0c\u51b7\u4e0d\u53ca\u9632\u7ed9\u98de\u5200\u63d2\u5165\u80a9\u5934\uff0c\u679c\u7136\u5e94\u58f0\u800c\u5012\uff0c\u4f46\u8fd8\u662f\u5728\u7bf7\u9876 \u4e0a\u6253\u6eda\u3002 \u3000\u3000\u674e\u601d\u5357\u867d\u7136\u4e0d\u77e5\u9053\u6b64\u4eba\u662f\u8c01\uff0c\u4f46\u4ece\u4ed6\u5bf9\u81ea\u5df1\u7684\u4e3e\u52a8\u770b\u6765\uff0c\u663e\u7136\u662f\u53cb\u975e\u654c\u3002\u53ef\u662f\u5728\u674e\u601d\u5357 \u7684\u5904\u5883\uff0c\u53ea\u6709\u5e2e\u536b\u58eb\u6349\u62ff\u201c\u523a\u5ba2\u201d\uff0c\u51b3\u65e0\u5e2e\u201c\u523a\u5ba2\u201d\u201c\u62d2\u6355\u201d\u4e4b\u7406\u3002\u674e\u601d\u5357\u5373\u770b\u89c1\u536b\u58eb\u8ffd\u4e86 \u4e0a\u53bb\uff0c\u5fc3\u91cc\u5927\u4e3a\u7740\u6025\u3002 \u3000\u3000\u5c31\u5728\u6b64\u9645\uff0c\u731b\u542c\u5f97\u90a3\u523a\u5ba2\u5927\u559d\u4e00\u58f0\uff1a\u201c\u539f\u7269\u5949\u8fd8\uff01\u201d\u767d\u5149\u4e00\u95ea\uff0c\u9661\u7136\u95f4\u90a3\u67c4\u98de\u5200\u5df2\u662f\u98de \u4e86\u56de\u6765\u3002\u539f\u6765\u4ed6\u7adf\u7136\u4e0d\u987e\u75bc\u75db\uff0c\u628a\u63d2\u8fdb\u80a9\u5934\u7684\u98de\u5200\u62d4\u51fa\uff0c\u53cd\u6253\u56de\u6765\u3002 \u3000\u3000\u8fd9\u4e00\u4e0b\uff0c\u8fd9\u536b\u58eb\u53ef\u5c31\u66f4\u60e8\u4e86\uff0c\u98de\u5200\u6253\u4e86\u4e00\u4e2a\u76d8\u65cb\u98de\u8fc7\uff0c\u8fd9\u536b\u58eb\u542c\u98ce\u8fa8\u5411\uff0c\u4ee5\u4e3a\u8fd9\u67c4\u98de\u5200 \u662f\u4ece\u5de6\u9762\u98de\u6765\uff0c\u767e\u5fd9\u4e2d\u5411\u53f3\u9762\u8eb2\u95ea\uff0c\u98de\u5200\u4e00\u4e2a\u76d8\u65cb\uff0c\u6539\u4e86\u65b9\u5411\uff0c\u8fd9\u536b\u58eb\u7b49\u4e8e\u662f\u9001\u4e0a\u53bb\u53d7\u4ed6\u4e00 \u5200\uff0c\u98de\u5200\u63a0\u8fc7\uff0c\u5c06\u4ed6\u7684\u819d\u76d6\u8fde\u76ae\u5e26\u8089\uff0c\u524a\u53bb\u4e86\u4e00\u5927\u7247\u3002 \u3000\u3000\u674e\u601d\u5357\u8fde\u5fd9\u8fc7\u53bb\uff0c\u628a\u8fd9\u536b\u58eb\u62b1\u8d77\uff0c\u8df3\u4e0b\u5730\u6765\uff0c\u4ed6\u7684\u4e3b\u8981\u76ee\u7684\u5012\u4e0d\u662f\u5728\u4e8e\u8981\u6551\u8fd9\u4e2a\u536b\u58eb\uff0c \u800c\u662f\u53ef\u4ee5\u85c9\u6b64\u907f\u514d\u548c\u90a3\u201c\u523a\u5ba2\u201d\u4ea4\u624b\u3002\u5f85\u5230\u4ed6\u628a\u8fd9\u4e2a\u536b\u58eb\u62b1\u4e86\u4e0b\u6765\uff0c\u4f17\u4eba\u4ea6\u5df2\u8d76\u5230\u4e4b\u65f6\uff0c\u90a3 \u201c\u523a\u5ba2\u201d\u65e9\u5df2\u662f\u9e3f\u98de\u6773\u6773\u4e86\uff0c\u6b63\u662f\uff1a \u3000\u3000\u4e07\u91cc\u8fdc\u6765\u7518\u8ba4\u8d3c\uff1f\u98de\u5200\u7559\u5b57\u8d77\u7591\u4e91\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b\u2014\u2014\u7b2c\u56db\u56de\u3000\u6b32\u8c0b\u7b56\u9a6c\u56fe\u4e2d\u571f\u3000\u53ea\u8bc6\u5f2f\u5f13\u5c04\u5927\u96d5 \u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b \u7b2c\u56db\u56de\u3000\u6b32\u8c0b\u7b56\u9a6c\u56fe\u4e2d\u571f\u3000\u53ea\u8bc6\u5f2f\u5f13\u5c04\u5927\u96d5 \u3000\u3000\u90a3\u4e24\u4e2a\u536b\u58eb\u4f24\u5f97\u4e0d\u8f7b\uff0c\u8fd8\u5e78\u6ca1\u6709\u4f24\u7740\u9aa8\u5934\uff0c\u6577\u4e0a\u4e86\u91d1\u521b\u836f\uff0c\u5df2\u65e0\u5927\u788d\u3002\u674e\u5e0c\u6d69\u51fa\u6765\u67e5 \u95ee\uff0c\u90a3\u4e24\u4e2a\u536b\u571f\u62a5\u544a\u4e86\u53d1\u73b0\u201c\u523a\u5ba2\u201d\u7684\u7ecf\u8fc7\uff0c\u8bf4\u9053\uff1a\u201c\u90fd\u662f\u6211\u4eec\u65e0\u80fd\uff0c\u6349\u4e0d\u4f4f\u523a\u5ba2\uff0c\u8fd8\u8fde\u7d2f \u4e86\u516c\u5b50\u53d7\u60ca\u3002\u4e0d\u8fc7\uff0c\u6211\u4eec\u7136\u7740\u4e86\u90a3\u53ae\u6697\u7b97\uff0c\u90a3\u53ae\u4e5f\u7740\u4e86\u6211\u4eec\u7684\u4e00\u67c4\u98de\u5200\uff0c\u4f24\u5728\u80a9\u5934\uff0c\u51b3\u96be\u63a9 \u9970\u3002\u5728\u548c\u6797\u7684\u6c49\u4eba\u4e0d\u591a\uff0c\u76f8\u4fe1\u53ef\u4ee5\u67e5\u5f97\u4e2a\u6c34\u843d\u77f3\u51fa\u3002\u201d \u3000\u3000\u8fd9\u4e24\u4e2a\u536b\u58eb\u662f\u6210\u5409\u601d\u6c57\u6d3e\u6765\u7ed9\u674e\u5e0c\u6d69\u4f7f\u7528\u7684\uff0c\u540d\u4e49\u4e0a\u662f\u505a\u4ed6\u7684\u536b\u58eb\uff0c\u5b9e\u9645\u4e0a\u662f\u76d1\u89c6\u4ed6\uff0c \u674e\u5e0c\u6d69\u5f53\u7136\u77e5\u9053\u3002 \u3000\u3000\u674e\u5e0c\u6d69\u4e0d\u6562\u5f97\u7f6a\u4ed6\u4eec\uff0c\u548c\u989c\u60a6\u8272\u5730\u5b89\u6170\u4e86\u4ed6\u4eec\u51e0\u53e5\uff0c\u7acb\u5373\u53eb\u4eba\u5feb\u9a6c\u5411\u6728\u534e\u9ece\u62a5\u8baf\u3002\u6728\u534e \u9ece\u662f\u4e13\u53f8\u641c\u6355\u7f6a\u72af\u7684\u201c\u795e\u7ffc\u8425\u201d\u6b66\u58eb\u9996\u9886\uff0c\u201c\u795e\u7ffc\u8425\u201d\u6b66\u58eb\u6bcf\u4eba\u90fd\u6709\u4e09\u5339\u9a8f\u9a6c\uff0c\u957f\u9014\u8ffd\u654c\uff0c \u9a6c\u529b\u4e4f\u65f6\uff0c\u53ef\u4ee5\u66ff\u6362\uff0c\u4e00\u5929\u8d70\u4e2a\u4e09\u4e94\u767e\u91cc\uff0c\u662f\u6781\u5bfb\u5e38\u4e4b\u4e8b\uff0c\u6240\u4ee5\u53f7\u79f0\u201c\u795e\u7ffc\u201d \u3000\u3000\u674e\u5e0c\u6d69\u6d3e\u4eba\u5411\u6728\u534e\u9ece\u62a5\u8baf\uff0c\u6728\u534e\u9ece\u81ea\u4f1a\u51fa\u52a8\u201c\u795e\u7ffc\u8425\u201d\u7684\u6b66\u58eb\uff0c\u641c\u6355\u8fd9\u4e2a\u201c\u523a\u5ba2\u201d\u3002\u5373 \u4f7f\u8fd9\u4e2a\u201c\u8346\u5ba2\u201d\u9003\u51fa\u4e86\u548c\u6797\uff0c\u4e5f\u5f88\u96be\u9003\u51fa\u8ffd\u5175\u7684\u7f09\u6355\u3002 \u3000\u3000\u674e\u601d\u5357\u89c1\u7236\u4eb2\u5982\u6b64\u8bda\u60f6\u8bda\u6050\u5730\u8c04\u5a9a\u8499\u53e4\u6b66\u58eb\uff0c\u5fc3\u4e2d\u6781\u4e0d\u8212\u670d\uff0c\u60f3\u9053\uff1a\u201c\u5982\u6b64\u4e00\u6765\uff0c\u53c8\u4e0d \u77e5\u8981\u8fde\u7d2f\u591a\u5c11\u6c49\u4eba\u4e86\uff01\u201d \u3000\u3000\u674e\u5e0c\u6d69\u5904\u7406\u4e86\u8fd9\u4ef6\u4e8b\u4e4b\u540e\uff0c\u8fd9\u624d\u677e\u4e86\u53e3\u6c14\uff0c\u56de\u8fc7\u5934\u95ee\u674e\u601d\u5357\u9053\uff1a\u201c\u8fd9\u523a\u5ba2\u5728\u4f60\u7684\u5e10\u5e55\u524d \u9762\u53d1\u73b0\uff0c\u4f3c\u4e4e\u662f\u51b2\u7740\u4f60\u6765\u7684\uff0c\u4f9d\u4f60\u63a8\u6d4b\uff0c\u4ed6\u662f\u60f3\u523a\u6740\u4f60\u5462\uff0c\u8fd8\u662f\u53e6\u6709\u5176\u4ed6\u76ee\u7684\uff1f\u4ed6\u53ef\u66fe\u5bf9\u4f60 \u8bf4\u8fc7\u4ec0\u4e48\u8bdd\u4e48\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u672c\u6765\u8fd8\u6709\u70b9\u8e0c\u8e87\uff0c\u4e0d\u77e5\u662f\u8be5\u544a\u8bc9\u7236\u4eb2\u7684\u597d\uff0c\u8fd8\u662f\u7792\u7740\u4ed6\u597d\uff1f\u542c\u4e86\u4ed6\u8fd9\u756a\u8bf4\u8bdd\u4e4b \u540e\uff0c\u5fc3\u610f\u5df2\u51b3\uff1a\u201c\u6b64\u4e8b\u5b9a\u6709\u8e4a\u8df7\uff0c\u8fd8\u662f\u7792\u7740\u7239\u7239\u7684\u597d\u3002\u201d\u4e8e\u662f\u8bf4\u9053\uff1a\u201c\u6211\u542c\u5f97\u6349\u523a\u5ba2\u4e4b\u65f6\uff0c \u65b9\u624d\u60ca\u8d77\uff0c\u600e\u77e5\u4ed6\u6765\u610f\u5982\u4f55\uff1f\u201d\u8bdd\u4e2d\u5df2\u662f\u56de\u7b54\u4e86\u674e\u5e0c\u6d69\u7684\u95ee\u9898\uff0c\u8fd9\u201c\u523a\u5ba2\u201d\u672a\u66fe\u548c\u4ed6\u8bf4\u8fc7\u8bdd \u4e86\u3002 \u3000\u3000\u674e\u5e0c\u6d69\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u662f\u4e0d\u8ba4\u8bc6\u4ed6\u4e86\uff1f\u201d\u8bed\u6c14\u4e4b\u4e2d\u663e\u7136\u8fd8\u6709\u51e0\u5206\u731c\u7591\u3002 \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u8fd9\u523a\u5ba2\u8499\u7740\u9762\u5b54\uff0c\u6211\u4e5f\u4e0d\u77e5\u662f\u5426\u66fe\u7ecf\u89c1\u8fc7\u7684\u4eba\u3002\u770b\u90a3\u80cc\u5f71\uff0c\u5219\u4f3c\u4e4e\u662f\u4e0d\u76f8 \u8bc6\u7684\u3002\u6211\u4eca\u5929\u521a\u5230\uff0c\u6211\u770b\u4e0d\u4f1a\u662f\u4e3a\u4e86\u523a\u6740\u6211\u800c\u6765\u7684\u5427\u3002\u201d \u3000\u3000\u674e\u5e0c\u6d69\u6c89\u6c89\u4e0d\u8bed\uff0c\u53d7\u4e86\u98de\u5200\u524a\u819d\u7684\u90a3\u4e2a\u536b\u58eb\u5ffd\u9053\uff1a\u201c\u4e0d\u5bf9\uff0c\u4e0d\u5bf9\uff01\u627e\u770b\u8fd9\u4eba\u5c31\u6050\u6015\u6b63\u662f \u516c\u5b50\u7684\u4ec7\u5bb6\uff01\u201d \u3000\u3000\u674e\u5e0c\u6d69\u8fde\u5fd9\u95ee\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d \u3000\u3000\u90a3\u536b\u58eb\u8bf4\u9053\uff1a\u201c\u516c\u5b50\u4e0d\u662f\u66fe\u7ecf\u78b0\u8fc7\u5c60\u767e\u57ce\u7684\u624b\u4e0b\uff0c\u5728\u8349\u539f\u4e0a\u51e0\u4e4e\u4e27\u5728\u4ed6\u4eec\u624b\u91cc\u4e48\uff1f\u6211\u770b \u4eca\u665a\u7684\u8fd9\u4e2a\u523a\u5ba2\uff0c\u4e5f\u591a\u534a\u4f1a\u662f\u5c60\u767e\u57ce\u7684\u515a\u7fbd\u3002\u201d \u3000\u3000\u8fd9\u4e2a\u536b\u571f\u66fe\u5f97\u8fc7\u674e\u601d\u5357\u7684\u6551\u52a9\uff0c\u5f53\u7136\u51b3\u4e0d\u4f1a\u7591\u5fc3\u523a\u5ba2\u662f\u674e\u601d\u5357\u7684\u670b\u53cb\u3002\u6728\u534e\u9ece\u6253\u8dd1\u5b8b\u94c1 \u8f6e\u592b\u59bb\u6551\u4e86\u674e\u601d\u5357\u6027\u547d\u4e4b\u4e8b\uff01\u53c8\u662f\u77e5\u9053\u7684\uff0c\u56e0\u6b64\u6beb\u65e0\u6000\u7591\u5730\u5c31\u4f5c\u51fa\u4e86\u8fd9\u4e2a\u5224\u65ad\u3002 \u3000\u3000\u8fd9\u536b\u58eb\u8fd9\u4e48\u4e00\u8bf4\uff0c\u4e0d\u4f46\u662f\u7ed9\u674e\u601d\u5357\u4f5c\u4e86\u63a9\u62a4\uff0c\u674e\u5e0c\u6d69\u89e3\u9664\u4e86\u5fc3\u4e0a\u7684\u6000\u7591\uff0c\u8bf4\u9053\uff1a\u201c\u5357 \u513f\uff0c\u4f60\u548c\u5c60\u767e\u57ce\u7684\u624b\u4e0b\u7ed3\u4e86\u4ec7\uff0c\u4ee5\u540e\u5012\u8981\u591a\u52a0\u5c0f\u5fc3\u624d\u8db3\u3002\u5f53\u7136\uff0c\u6211\u4e5f\u4f1a\u52a0\u5f3a\u5b88\u536b\uff0c\u4fdd\u62a4\u4f60 \u7684\u3002\u73b0\u5728\u5929\u5feb\u4eae\u4e86\uff0c\u4f60\u56de\u53bb\u6b47\u4e00\u6b47\uff0c\u6362\u597d\u8863\u88f3\uff0c\u5c31\u548c\u6211\u53bb\u671d\u89c1\u5927\u6c57\u5427\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u5e94\u4e86\u4e00\u4e2a\u201c\u662f\u201d\u5b57\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u7239\u7239\u7684\u4e3b\u610f\u597d\u50cf\u8fd8\u6ca1\u6709\u5341\u5206\u62ff\u5b9a\uff0c\u6709\u5173\u5c60\u767e\u57ce \u624b\u4e0b\u5bf9\u6211\u7684\u8bef\u4f1a\uff0c\u6682\u65f6\u8fd8\u662f\u4e0d\u8981\u544a\u8bc9\u4ed6\u7684\u597d\uff0c\u514d\u5f97\u523a\u6fc0\u4e86\u4ed6\u3002\u201d \u3000\u3000\u56de\u5230\u81ea\u5df1\u7684\u5e10\u5e55\uff0c\u674e\u601d\u5357\u6084\u6084\u7684\u628a\u90a3\u4e2a\u7eb8\u56e2\u6253\u5f00\u6765\u770b\uff0c\u53ea\u89c1\u4e0a\u9762\u5199\u7740\u4e24\u884c\u5b57\u662f\uff1a\u201c\u6b32\u91ca \u7591\u56e2\uff0c\u53ef\u5230\u963f\u513f\u683c\u5c71\u677e\u98ce\u8c37\u4e2d\u67e5\u8bbf\u3002\u7edd\u79d8\uff0c\u5207\u83ab\u544a\u8bc9\u522b\u4eba\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u770b\u4e86\u8fd9\u6837\u83ab\u540d\u5176\u5999\u7684\u51e0\u53e5\u8bdd\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6211\u6709\u4ec0\u4e48\u7591\u56e2\uff1f\u8fd9\u4eba\u662f\u8c01\uff0c\u4ed6\u53c8\u600e\u77e5 \u6211\u6709\u7591\u56e2\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u8bfb\u8fc7\u8499\u53e4\u7684\u5730\u7406\uff0c\u77e5\u9053\u963f\u513f\u683c\u5c71\u662f\u5728\u5e93\u4f26\u6cb9\u5317\u9762\u7ea6\u4e8c\u4e09\u767e\u91cc\u7684\u4e00\u5ea7\u5927\u5c71\uff0c\u5fc3\u60f3\uff1a \u201c\u7239\u7239\u5728\u90a3\u4e00\u5e26\u505a\u8fc7\u82e6\u5de5\uff0c\u4e0d\u77e5\u4ed6\u53ef\u66fe\u5230\u8fc7\u90a3\u963f\u513f\u683c\u5c71\u7684\u677e\u98ce\u8c37\uff1f\u677e\u98ce\u8c37\u4e2d\u6709\u751a\u4eba\u5bb6\uff0c\u7239\u7239 \u5018\u82e5\u77e5\u9053\uff0c\u6211\u5c31\u53ef\u4ee5\u627e\u5f97\u7ebf\u7d22\u4e86\u3002\u201d\u4f46\u968f\u5373\u53c8\u60f3\uff1a\u201c\u8fd9\u4eba\u5192\u4e86\u6027\u547d\u4e4b\u9669\u7ed9\u6211\u9001\u6765\u8fd9\u4e2a\u7eb8\u56e2\uff0c \u544a\u8beb\u6211\u5207\u83ab\u544a\u8bc9\u522b\u4eba\uff0c\u60f3\u5fc5\u4e5f\u662f\u4e0d\u613f\u610f\u8ba9\u6211\u7239\u7239\u77e5\u9053\u7684\u4e86\u3002\u6211\u5c82\u80fd\u4e0d\u9075\u4ece\u4ed6\u7684\u5631\u5490\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u6b63\u60f3\u628a\u90a3\u5b57\u6761\u711a\u6bc1\uff0c\u5374\u5fc3\u5ff5\u4e00\u52a8\uff0c\u53c8\u4ed4\u7ec6\u770b\u4e86\u4e00\u904d\uff0c\u4e0d\u89c9\u5927\u4e3a\u5947\u602a\uff1a\u201c\u8fd9\u5b57\u8ff9\u597d \u50cf\u5f88\u719f\uff0c\u6211\u5728\u54ea\u513f\u89c1\u8fc7\u7684\u5462\uff1f\u201d \u3000\u3000\u60f3\u4e86\u4e00\u4f1a\uff0c\u674e\u601d\u5357\u5982\u6709\u6240\u89e6\uff0c\u8d76\u5feb\u628a\u4ed6\u8d34\u8eab\u6536\u85cf\u7684\u90a3\u672c\u4ed6\u7236\u4eb2\u6240\u6ce8\u91ca\u800c\u5c1a\u672a\u5b8c\u6210\u7684\u5175\u4e66 \u62ff\u51fa\u6765\uff0c\u5bf9\u7167\u7740\u770b\uff0c\u7eb8\u4e0a\u7684\u5b57\u8ff9\u548c\u4e66\u4e0a\u7684\u7b14\u8ff9\u751a\u4e3a\u76f8\u4f3c\uff0c\u4e0d\u8fc7\u524d\u8005\u5374\u662f\u82cd\u52b2\u4e00\u4e9b\uff0c\u53c8\u5927\u6982\u56e0 \u4e3a\u662f\u5306\u5306\u4e66\u5199\u6240\u81f3\uff0c\u5199\u5f97\u9887\u4e3a\u6f66\u8349\uff0c\u56e0\u6b64\u674e\u601d\u5357\u8fd8\u4e0d\u6562\u65ad\u5b9a\u662f\u5426\u51fa\u81ea\u4e00\u4eba\u7684\u624b\u7b14\u3002 \u3000\u3000\u674e\u601d\u5357\u60f3\u4e86\u53c8\u60f3\uff0c\u7ec8\u4e8e\u54d1\u7136\u5931\u7b11\uff0c\u60f3\u9053\uff1a\u201c\u5929\u4e0b\u5b57\u8ff9\u76f8\u4f3c\u7684\u4e5f\u5f88\u591a\u3002\u6211\u8fd9\u4e2a\u7239\u7239\u603b\u4e0d\u4f1a \u662f\u5047\u7684\u5427\uff1f\u5b5f\u5927\u4fa0\u8981\u60f3\u6740\u6211\uff0c\u4e0d\u5c31\u662f\u4e3a\u4e86\u7239\u7239\u505a\u4e86\u8499\u53e4\u4eba\u7684\u5b98\u7684\u7f18\u6545\u4e48\uff1f\u82e5\u679c\u6211\u7684\u7239\u7239\u662f\u853d \u533f\u5728\u963f\u513f\u683c\u5c71\uff0c\u5b5f\u5927\u4fa0\u4e5f\u5c31\u4e0d\u81f3\u4e8e\u8981\u6740\u6211\u4e86\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u518d\u60f3\u9053\uff1a\u201c\u8fd9\u4eba\u672c\u6765\u662f\u8981\u4eb2\u53e3\u544a\u8bc9\u6211\u4e00\u4ef6\u4e8b\u60c5\u7684\uff0c\u60f3\u5fc5\u4ed6\u4e5f\u6599\u5230\u53ef\u80fd\u6ca1\u6709\u673a\u4f1a\u548c \u6211\u8bf4\u8bdd\uff0c\u6240\u4ee5\u51c6\u5907\u4e86\u8fd9\u4e2a\u7eb8\u56e2\u3002\u4f46\u4ed6\u4e3a\u4ec0\u4e48\u4e0d\u591a\u5199\u4e00\u4e9b\uff0c\u81f3\u5c11\u4e5f\u5e94\u8be5\u8ba9\u6211\u77e5\u9053\u4ed6\u7684\u8eab\u4efd \u5440\uff01\u201d\u674e\u601d\u5357\u53c8\u56de\u5fc6\u4e00\u4e0b\u521a\u624d\u6240\u89c1\u7684\u90a3\u4eba\u7684\u5370\u8c61\uff0c\u867d\u7136\u662f\u8499\u4e86\u9762\uff0c\u4e5f\u53ef\u4ee5\u770b\u5f97\u51fa\u662f\u4e2a\u4e0d\u4f1a\u8d85 \u8fc7\u56db\u5341\u5c81\u7684\u4e2d\u5e74\u6c49\u5b50\uff0c\u800c\u4e14\u8fd9\u4e2a\u4eba\u7684\u6b66\u529f\u9887\u9ad8\uff0c\u51b3\u4e0d\u4f1a\u662f\u4ed6\u7236\u4eb2\u3002 \u3000\u3000\u674e\u601d\u5357\u767e\u601d\u4e0d\u89e3\uff0c\u53ea\u597d\u628a\u90a3\u7eb8\u6761\u711a\u5316\uff0c\u8ba9\u8fd9\u54d1\u8c1c\u6682\u65f6\u7559\u5728\u5fc3\u4e2d\u3002 \u3000\u3000\u674e\u601d\u5357\u751a\u662f\u5c0f\u5fc3\uff0c\u711a\u4e86\u7eb8\u6761\uff0c\u5c31\u628a\u7070\u70ec\u653e\u5165\u676f\u4e2d\uff0c\u548c\u6c34\u559d\u4e86\uff0c\u514d\u5f97\u7559\u4e0b\u75d5\u8ff9\uff0c\u521a\u6536\u62fe\u59a5 \u5f53\uff0c\u536b\u58eb\u5df2\u6765\u50ac\u4fc3\u3002\u674e\u601d\u5357\u5306\u5306\u6362\u8fc7\u8863\u88f3\uff0c\u4fbf\u4e0e\u7236\u4eb2\u540c\u5f80\u201c\u91d1\u5e10\u201d\uff0c\u8c12\u89c1\u6210\u5409\u601d\u6c57\u3002 \u3000\u3000\u8def\u4e0a\uff0c\u674e\u5e0c\u6d69\u5bf9\u513f\u5b50\u8bf4\u9053\uff1a\u201c\u5927\u6c57\u6700\u559c\u6b22\u6709\u672c\u9886\u7684\u5e74\u9752\u4eba\uff0c\u4f60\u82e5\u662f\u8ba8\u5f97\u4ed6\u7684\u559c\u6b22\uff0c\u54b1\u4eec \u4ee5\u540e\u884c\u4e8b\u5c31\u65b9\u4fbf\u4e86\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u662f\u3002\u4e0d\u8fc7\uff0c\u6211\u5374\u662f\u6700\u4e0d\u4f1a\u5949\u627f\u4eba\u7684\u3002\u201d \u3000\u3000\u674e\u5e0c\u6d69\u7b11\u9053\uff1a\u201c\u5927\u6c57\u7684\u813e\u6c14\u867d\u7136\u559c\u6b22\u5949\u627f\uff0c\u4f46\u4e5f\u662f\u8ba8\u538c\u62d9\u52a3\u8c04\u5a9a\u7684\u3002\u4ed6\u8d4f\u8bc6\u7684\u662f\u6709\u80c6\u8bc6 \u7684\u5c11\u5e74\u82f1\u96c4\uff0c\u4f60\u5bf9\u4ed8\u4ed6\u53ea\u8981\u4e0d\u5351\u4e0d\u4ea2\uff0c\u5c31\u53ef\u4ee5\u4e86\u3002\u5176\u5b9e\u5927\u6c57\u96c4\u624d\u5927\u7565\uff0c\u6b66\u529f\u76d6\u4e16\uff0c\u4f60\u5373\u4f7f\u4e0d \u613f\u610f\u4f9d\u9644\u4ed6\uff0c\u4e5f\u662f\u5e94\u8be5\u4f69\u670d\u4ed6\u7684\uff0c\u4eca\u65e5\u4e4b\u4f1a\uff0c\u5bf9\u4f60\u5173\u7cfb\u5f88\u5927\uff0c\u4f60\u5584\u81ea\u4e3a\u4e4b\u5427\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u5fc3\u60f3\uff1a\u201c\u7239\u7239\u8fd9\u4e0d\u662f\u6559\u6211\u62cd\u9a6c\u7684\u6280\u672f\u5417\uff1f\u201d\u4e0d\u7531\u5f97\u5fc3\u91cc\u5f88\u4e0d\u8212\u670d\uff0c\u4f46\u5374\u4e5f\u4e0d\u613f\u9876 \u649e\u7236\u4eb2\uff0c\u5f53\u4e0b\u9ed8\u4e0d\u4f5c\u58f0\u3002 \u3000\u3000\u674e\u5e0c\u6d69\u53c8\u9053\uff1a\u201c\u5927\u6c57\u6709\u56db\u4e2a\u513f\u5b50\uff0c\u957f\u5b50\u672f\u8d64\uff0c\u6b21\u5b50\u5bdf\u5408\u53f0\uff0c\u4e09\u5b50\u7a9d\u9614\u53f0\uff0c\u6700\u5c0f\u7684\u513f\u5b50\u62d6 \u96f7\u3002\u672f\u8d64\u7684\u6bcd\u4eb2\u66fe\u88ab\u5927\u6c57\u7684\u654c\u4eba\u8511\u91cc\u5409\u90e8\u6240\u4fd8\uff0c\u672f\u8d64\u662f\u5728\u91ca\u653e\u4e4b\u540e\u4ed6\u6bcd\u4eb2\u5728\u5f52\u8def\u4e2d\u751f\u7684\uff0c\u56e0 \u6b64\uff0c\u4ed6\u7684\u5144\u5f1f\u8bf4\u4ed6\u2018\u6765\u5386\u4e0d\u660e\u2019\uff0c\u90fd\u4e0d\u628a\u4ed6\u5f53\u4f5c\u957f\u5144\u770b\u5f85\u3002\u4ed6\u6027\u60c5\u9c81\u83bd\uff0c\u5927\u6c57\u4e5f\u4e0d\u5927\u559c\u6b22 \u4ed6\u3002\u6b21\u5b50\u5bdf\u5408\u53f0\u5f88\u4f1a\u6253\u4ed7\uff0c\u4f46\u5341\u5206\u8dcb\u6248\uff0c\u90e8\u4e0b\u53ea\u662f\u754f\u5a01\u800c\u4e0d\u6000\u5fb7\uff0c\u770b\u6765\u4e5f\u662f\u5f88\u96be\u7ee7\u627f\u6c57\u4f4d\u3002 \u4e09\u5b50\u7a9d\u9614\u53f0\u6027\u60c5\u5fe0\u539a\uff0c\u6700\u5f97\u90e8\u4e0b\u62e5\u6234\u3002\u4f46\u5927\u6c57\u6700\u559c\u6b22\u7684\u5219\u662f\u5c0f\u513f\u5b50\u62d6\u96f7\u3002\u770b\u6765\u5c06\u6765\u7ee7\u627f\u5927\u6c57 \u4e4b\u4f4d\u7684\uff0c\u4e0d\u662f\u7a9d\u9614\u53f0\u5c31\u662f\u62d6\u96f7\u4e86\u3002\u4ed6\u4eec\u7684\u5e74\u7eaa\u548c\u4f60\u76f8\u82e5\uff0c\u4f60\u5012\u4e0d\u59a8\u548c\u4ed6\u4eec\u7ed3\u7eb3\u7ed3\u7eb3\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u54b1\u4eec\u53c8\u4e0d\u6253\u7b97\u4e45\u4f4f\u8499\u53e4\uff0c\u4e5f\u7528\u4e0d\u7740\u8d39\u5fc3\u673a\u53bb\u5df4\u7ed3\u738b\u5b50\u3002\u201d \u3000\u3000\u674e\u5e0c\u6d69\u9053\uff1a\u201c\u8bdd\u4e0d\u662f\u8fd9\u6837\u8bf4\uff0c\u522b\u4eba\u77e5\u9053\u4f60\u662f\u738b\u5b50\u7684\u670b\u53cb\uff0c\u5bf9\u4f60\u603b\u6709\u597d\u5904\u3002\u6700\u5c11\u5bf9\u4f60\u7684\u76d1 \u89c6\u4e5f\u4f1a\u653e\u677e\u4e00\u4e9b\u3002\u201d \u3000\u3000\u674e\u5e0c\u6d69\u53c8\u9053\uff1a\u201c\u5927\u6c57\u8fd8\u6709\u4e09\u4e2a\u5973\u513f\u3002\u957f\u5973\u3001\u6b21\u5973\u90fd\u5df2\u5a5a\u914d\u3002\u4e09\u5973\u963f\u9791\u6d77\u522b\u59ec\uff0c\u8bb8\u5a5a\u7ed9\u6c6a \u53e4\u90e8\u914b\u957f\u7684\u513f\u5b50\u9547\u56fd\uff0c\u5c1a\u672a\u5b8c\u5a5a\u3002\u5927\u6c57\u5728\u4e09\u4e2a\u5973\u513f\u4e2d\uff0c\u4e5f\u662f\u559c\u6b22\u6700\u5c0f\u7684\u8fd9\u4e2a\u5973\u513f\u3002\u2018\u963f\u9791 \u6d77\u2019\u5728\u8499\u53e4\u8bdd\u7684\u610f\u601d\u662f\u2018\u660e\u6167\u2019\uff0c\u4f60\u53ef\u4ee5\u5c0a\u79f0\u5979\u4f5c\u660e\u6167\u516c\u4e3b\u3002\u201d \u3000\u3000\u6b63\u8bf4\u8bdd\uff0c\u5ffd\u89c1\u6210\u5409\u601d\u6c57\u624b\u4e0b\u7684\u201c\u91d1\u5e10\u6b66\u58eb\u201d\u8d64\u8001\u6e29\u9a91\u9a6c\u8dd1\u6765\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u606d\u559c\u4f60\u4eec\u7236 \u5b50\u9aa8\u8089\u56e2\u5706\u3002\u201d \u3000\u3000\u674e\u5e0c\u6d69\u8bf4\u9053\uff1a\u201c\u8fd9\u90fd\u662f\u9760\u4e86\u5c06\u519b\u7684\u6069\u60e0\u3002\u8981\u4e0d\u662f\u4f60\u4eec\u6551\u4e86\u4ed6\uff0c\u6211\u4eec\u7236\u5b50\u7109\u80fd\u5f97\u6709\u4eca \u65e5\uff1f\u201d \u3000\u3000\u8d64\u8001\u6e29\u54c8\u54c8\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5927\u6c57\u542c\u8bf4\u4ee4\u90ce\u5e74\u5c11\u82f1\u96c4\uff0c\u4ed6\u4e5f\u6b63\u60f3\u89c1\u4e00\u89c1\u4ee4\u90ce\u5462\u3002\u4f60\u4eec\u6765\u5f97 \u6b63\u597d\uff01\u201d \u3000\u3000\u674e\u5e0c\u6d69\u95ee\u9053\uff1a\u201c\u5927\u6c57\u5347\u5e10\u6ca1\u6709\uff1f\u201d \u3000\u3000\u8d64\u8001\u6e29\u9053\uff1a\u201c\u5927\u6c57\u4eca\u5929\u5174\u81f4\u5f88\u9ad8\uff0c\u65e9\u5c31\u5230\u80af\u7279\u5c71\u72e9\u730e\u53bb\u4e86\u3002\u6211\u77e5\u9053\u4f60\u4eec\u7236\u5b50\u4eca\u5929\u8981\u6765\u91d1 \u5e10\u8c12\u89c1\u5927\u6c57\uff0c\u6240\u4ee5\u5728\u8fd9\u91cc\u7b49\u5019\u4f60\u4eec\u3002\u597d\uff0c\u54b1\u4eec\u4e00\u540c\u53bb\u5427\u3002\u201d \u3000\u3000\u674e\u5e0c\u6d69\u559c\u9053\uff1a\u201c\u72ac\u5b50\u5f97\u5c06\u519b\u5f15\u89c1\uff0c\u90a3\u662f\u6700\u597d\u4e0d\u8fc7\u3002\u201d \u3000\u3000\u8d64\u8001\u6e29\u7b11\u9053\uff1a\u201c\u674e\u516c\u5b50\uff0c\u4eca\u5929\u53ef\u662f\u4f60\u5927\u663e\u8eab\u624b\u7684\u673a\u4f1a\u4e86\u3002\u6211\u8fd8\u8bb0\u5f97\u54b1\u4eec\u76f8\u9047\u90a3\u5929\uff0c\u4f60\u5728 \u6208\u58c1\u4e0a\u5c04\u4e00\u5934\u5140\u9e70\uff0c\u7bad\u6cd5\u4e4b\u5999\uff0c\u5f53\u771f\u662f\u4ee4\u4eba\u4f69\u670d\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u9762\u4e0a\u4e00\u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u53ef\u6ca1\u6709\u5c04\u4e0b\u90a3\u53ea\u5140\u9e70\u3002\u201d\u539f\u6765\u90a3\u65e5\u674e\u601d\u5357\u56e0\u4e3a\u6c14\u529b\u4e0d\u8db3\uff0c \u5c04\u7740\u4e86\u5140\u9e70\uff0c\u5374\u7ed9\u5b83\u5e26\u7bad\u98de\u8d70\u4e86\u3002\u8d64\u8001\u6e29\u5c31\u662f\u7531\u4e8e\u53d1\u73b0\u4e86\u90a3\u53ea\u5e26\u7bad\u5140\u9e70\uff0c\u4e00\u8def\u5bfb\u6765\uff0c\u78b0\u4e0a\u674e \u601d\u5357\u7684\u3002\u4e0d\u8fc7\u674e\u601d\u5357\u6b64\u9645\u4e4b\u6240\u4ee5\u89c9\u5f97\u7f9e\u6127\uff0c\u8fd8\u4e0d\u4ec5\u4ec5\u662f\u56e0\u4e3a\u5c04\u4e0d\u4e0b\u90a3\u53ea\u5140\u9e70\u7684\u539f\u6545\uff0c\u800c\u662f\u56e0 \u4e3a\u4ed6\u5f53\u65e5\u53d7\u8fb1\u4e8e\u8d64\u8001\u6e29\uff0c\u4eca\u65e5\u8fd8\u8981\u9760\u4ed6\u5f15\u89c1\u3002 \u3000\u3000\u8d64\u8001\u6e29\u600e\u77e5\u4ed6\u7684\u5fc3\u601d\uff0c\u514d\u4e0d\u4e86\u518d\u79f0\u8d5e\u4ed6\u51e0\u53e5\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u53ea\u5140\u9e70\u78e8\u76d8\u822c\u5927\uff0c\u7fc5\u8180\u50cf\u94c1\u4e00 \u822c\uff0c\u731b\u79bd\u4e2d\u6bd4\u5b83\u66f4\u5389\u5bb3\u7684\u5c31\u53ea\u6709\u5927\u96d5\u4e86\u3002\u4f60\u80fd\u591f\u4e00\u7bad\u5c04\u4f24\u4e86\u4e00\u5934\u5140\u9e70\uff0c\u5df2\u7ecf\u662f\u5f88\u4e0d\u9519\u4e86\u3002\u201d \u3000\u3000\u80af\u7279\u5c71\u662f\u548c\u6797\u9644\u8fd1\u7684\u4e00\u5ea7\u5927\u5c71\uff0c\u4ece\u6210\u5409\u601d\u6c57\u7684\u91d1\u5e10\u524d\u5f80\u4e0d\u8fc7\u5341\u91cc\u4e4b\u9065\uff0c\u6ca1\u6709\u591a\u4e45\u5c31\u5230 \u4e86\u3002\u674e\u5e0c\u6d69\u7236\u5b50\u8ddf\u7740\u8d64\u8001\u6e29\u4e0a\u5c71\uff0c\u53ea\u89c1\u6ee1\u5c71\u7684\u8499\u53e4\u6b66\u58eb\u6b63\u5728\u653e\u9e70\u7eb5\u72ac\uff0c\u8ffd\u730e\u91ce\u517d\uff0c\u674e\u601d\u5357\u5374 \u4e0d\u77e5\u54ea\u4e2a\u662f\u6210\u5409\u601d\u6c57\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u9690\u9690\u96f7\u9e23\uff0c\u674e\u601d\u5357\u5fc3\u9053\uff1a\u201c\u6674\u65e5\u5f53\u7a7a\uff0c\u6beb\u65e0\u53d8\u5929\u7684\u8ff9\u8c61\uff0c\u600e\u7684\u5ffd\u7136\u6253\u8d77\u96f7\u6765 \u4e86\uff1f\u201d\u62ac\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u5929\u8fb9\u4e24\u4e2a\u9ed1\u70b9\uff0c\u8d8a\u8fd1\u8d8a\u663e\uff0c\u6e10\u6e10\u770b\u5f97\u6e05\u695a\uff0c\u5374\u539f\u6765\u662f\u4e24\u53ea\u5927\u96d5\uff0c\u5927\u96d5 \u631f\u98ce\u98de\u884c\uff0c\u53d1\u51fa\u95f7\u96f7\u4f3c\u7684\u58f0\u54cd\u3002 \u3000\u3000\u674e\u601d\u5357\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u679c\u7136\u6bd4\u6211\u90a3\u65e5\u6240\u5c04\u7684\u5140\u9e70\u5927\u5f97\u591a\u3002\u201d\u90a3\u4e24\u53ea\u5927\u96d5\uff0c\u60f3\u662f\u56e0\u4e3a \u4e0b\u9762\u4eba\u9a6c\u55a7\u95f9\uff0c\u4e0d\u6562\u4f4e\u98de\uff0c\u76d8\u65cb\u5728\u767d\u4e91\u4e4b\u4e0b\u3002 \u3000\u3000\u4f17\u4eba\u6b63\u81ea\u4ef0\u9996\u800c\u89c2\uff0c\u5ffd\u89c1\u4e00\u4e2a\u7a7f\u7740\u91d1\u9ec4\u8272\u6218\u888d\u7684\u6b66\u571f\u7eb5\u9a6c\u51fa\u6765\uff0c\u5f20\u5f13\u642d\u7bad\uff0c\u53eb\u9053\uff1a\u201c\u6211 \u82e5\u80fd\u62a5\u5148\u4e16\u4e4b\u4ec7\uff0c\u626b\u5e73\u91d1\u56fd\uff0c\u7bad\u5230\u96d5\u843d\uff01\u201d \u3000\u3000\u5f13\u5982\u9739\u96f3\uff0c\u7bad\u4f3c\u6d41\u661f\uff0c\u5927\u96d5\u679c\u7136\u5e94\u58f0\u800c\u843d\uff0c\u800c\u4e14\u4e0d\u53ea\u4e00\u53ea\uff0c\u8fd9\u6b66\u58eb\u7adf\u662f\u4e00\u7bad\u53cc\u96d5\uff0c\u4e24\u53ea \u5927\u96d5\u90fd\u843d\u4e0b\u6765\u4e86\uff01 \u3000\u3000\u767b\u65f6\u6ee1\u5c71\u6b22\u547c\uff0c\u6b66\u58eb\u4eec\u9f50\u58f0\u6b4c\u9882\uff1a \u3000\u3000\u201c\u6211\u7684\u4e07\u4f17\u5723\u4e3b\u2014\u2014 \u3000\u3000\u6210\u5409\u601d\u6c57\uff01 \u3000\u3000\u4e0a\u5929\u8d50\u7ed9\u4f60\u8d85\u4eba\u529b\u6c14\uff0c \u3000\u3000\u767e\u6b65\u7a7f\u6768\u7684\u7bad\uff0c \u3000\u3000\u4f7f\u9003\u9038\u7684\u767e\u59d3\uff0c \u3000\u3000\u5c48\u819d\u6295\u964d\uff1b \u3000\u3000\u767e\u53d1\u767e\u4e2d\u7684\u7bad\uff0c \u3000\u3000\u4f7f\u6e83\u9003\u7684\u53db\u4f17\uff0c \u3000\u3000\u7f34\u68b0\u6295\u8bda\u3002\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u5927\u5403\u4e00\u60ca\uff0c\u65b9\u59cb\u77e5\u9053\u8fd9\u4e2a\u5f2f\u5f13\u5c04\u5927\u96d5\u7684\u6b66\u58eb\u5c31\u662f\u6210\u5409\u601d\u6c57\u3002\u674e\u601d\u5357\u5fc3\u91cc\u60f3\u9053\uff1a \u201c\u6210\u5409\u601d\u6c57\u679c\u7136\u4e0d\u6127\u662f\u4e00\u4ee3\u5929\u9a84\uff0c\u771f\u662f\u6709\u6c14\u541e\u5c71\u5cb3\u7684\u6c14\u6982\u3002\u522b\u7684\u4e0d\u8bf4\uff0c\u53ea\u8bf4\u4ed6\u8fd9\u5f2f\u5f13\u5c04\u96d5\u7684 \u672c\u4e8b\uff0c\u5f53\u4eca\u4e4b\u4e16\uff0c\u53ea\u6015\u5df2\u662f\u65e0\u4eba\u80fd\u53ca\u3002\u201d \u3000\u3000\u8bf8\u5c06\u62dc\u4f0f\u4e8e\u5730\uff0c\u5f00\u58f0\u8bf4\u9053\uff1a\u201c\u4e00\u7bad\u8d2f\u53cc\u96d5\uff0c\u4e0a\u5929\u5df2\u7ecf\u52a0\u500d\u7684\u7b54\u5141\u4e86\u5927\u6c57\u7684\u8bf7\u6c42\uff0c\u6b64\u53bb\u4e0d \u4f46\u4e16\u4ec7\u53ef\u62a5\uff0c\u91d1\u56fd\u53ef\u706d\uff0c\u53ea\u6015\u5929\u4e0b\u90fd\u8981\u5f52\u4e8e\u4e00\u7edf\u5462\uff01\u8bf7\u5927\u6c57\u523b\u65e5\u5174\u5e08\u3002\u201d \u3000\u3000\u8499\u53e4\u548c\u91d1\u56fd\u4e43\u662f\u4e16\u4ec7\uff0c\u6210\u5409\u601d\u6c57\u7684\u4f2f\u7956\u9600\u5df4\u8be5\u6c57\u5c31\u662f\u7ed9\u91d1\u4eba\u6349\u53bb\uff0c\u5c06\u4ed6\u9489\u5728\u6728\u9a74\u80cc\u4e0a\uff0c \u4ee4\u4ed6\u8f97\u8f6c\u60e8\u6bd9\u7684\u3002\u662f\u4ee5\u6210\u5409\u601d\u6c57\u4e00\u5411\u7528\u62a5\u4ec7\u4f5c\u4e3a\u53f7\u53ec\uff0c\u7edf\u4e00\u8499\u53e4\u8bf8\u90e8\u3002\u521a\u624d\u5c04\u96d5\u544a\u5929\u4e4b\u65f6\uff0c \u6240\u8bf4\u7684\u8a93\u8bcd\u4e5f\u662f\u706d\u91d1\u62a5\u4ec7\u3002 \u3000\u3000\u4f46\u8bf8\u5c06\u7684\u8bf7\u6c42\uff0c\u5219\u4e0d\u4ec5\u4ec5\u662f\u8981\u4ed6\u4eec\u7684\u5927\u6c57\u706d\u91d1\uff0c\u800c\u662f\u8981\u8ba8\u5e73\u5305\u62ec\u5b8b\u56fd\u3001\u897f\u590f\u3001\u82b1\u523a\u5b50\u6a21 \u7b49\u7b49\u56fd\u5bb6\u5728\u5185\u7684\u201c\u5929\u4e0b\u201d\u4e86\uff0c\u5176\u5b9e\u6210\u5409\u601d\u6c57\u7684\u96c4\u5fc3\u4e5f\u662f\u5fd7\u5728\u7edf\u4e00\u5929\u4e0b\uff0c\u8bf8\u5c06\u4e0d\u8fc7\u8fce\u5408\u4ed6\u7684\u610f \u601d\u800c\u5df2\uff01 \u3000\u3000\u674e\u601d\u5357\u542c\u4e86\u8fd9\u4e9b\u8a00\u8bed\uff0c\u51db\u7136\u6212\u60e7\uff0c\u4e0d\u89c9\u671b\u4e86\u4ed6\u7236\u4eb2\u4e00\u773c\uff0c\u5fc3\u9053\uff1a\u201c\u7239\u7239\uff0c\u4f60\u4ee5\u4e3a\u8499\u53e4\u4e0d \u4f1a\u4fb5\u5b8b\uff0c\u8fd9\u8be5\u9192\u6765\u4e86\u5427\uff1f\u201d\u674e\u5e0c\u6d69\u4e00\u770b\u4ed6\u7684\u773c\u795e\uff0c\u5df2\u7ecf\u77e5\u9053\u4ed6\u5fc3\u91cc\u8981\u8bf4\u4ec0\u4e48\uff0c\u5fd9\u628a\u7709\u5934\u4e00 \u76b1\uff0c\u793a\u610f\u53eb\u4ed6\u4e0d\u53ef\u80e1\u8a00\u3002 \u3000\u3000\u6210\u5409\u601d\u6c57\u54c8\u54c8\u4e00\u7b11\uff0c\u6307\u7740\u56db\u4e2a\u513f\u5b50\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u90fd\u8fc7\u6765\u3002\u201d \u3000\u3000\u672f\u8d64\u3001\u5bdf\u5408\u53f0\u3001\u7a9d\u9614\u53f0\u3001\u62d6\u96f7\u56db\u4eba\u9f50\u96c6\u7236\u4eb2\u8eab\u524d\uff0c\u6210\u5409\u601d\u6c57\u53d6\u51fa\u4e00\u675f\u7bad\u6746\uff0c\u8bf4\u9053\uff1a\u201c\u672f \u8d64\uff0c\u4f60\u628a\u5b83\u6298\u65ad\u3002\u201d\u6210\u5409\u601d\u6c57\u6240\u7528\u7684\u7bad\u662f\u7279\u5927\u53f7\u7684\u5305\u7740\u94c1\u76ae\u7684\u7bad\uff0c\u5341\u652f\u4e00\u675f\uff0c\u672f\u8d64\u7528\u5c3d\u6c14 \u529b\uff0c\u7eb9\u4e1d\u4e0d\u52a8\u3002\u672f\u8d64\u6da8\u7ea2\u4e86\u8138\uff0c\u8bf4\u9053\uff1a\u201c\u5b69\u513f\u6ca1\u6709\u7239\u7239\u795e\u529b\uff0c\u6298\u5b83\u4e0d\u65ad\u3002\u201d\u6210\u5409\u601d\u6c57\u9053\uff1a \u201c\u5bdf\u5408\u53f0\uff0c\u4f60\u8bd5\u8bd5\u3002\u201d\u4f9d\u6b21\u7a9d\u9614\u53f0\u3001\u62d6\u96f7\u90fd\u8bd5\u8fc7\u4e86\uff0c\u65e0\u4eba\u80fd\u6298\u65ad\u8fd9\u675f\u7bad\u6746\u3002 \u3000\u3000\u6210\u5409\u601d\u6c57\u628a\u8fd9\u675f\u7bad\u62c6\u5f00\uff0c\u8f6c\u773c\u95f4\u4e00\u679d\u679d\u90fd\u6298\u65ad\u4e86\u3002\u6210\u5409\u601d\u6c57\u9053\uff1a\u201c\u4f60\u4eec\u61c2\u5f97\u4e86\u5427\uff0c\u4f60\u4eec \u5408\u8d77\u6765\u5c31\u50cf\u8fd9\u675f\u7bad\u4e00\u6837\uff0c\u6ca1\u4eba\u80fd\u6298\u65ad\u4f60\u4eec\uff0c\u5206\u5f00\u6765\u5c31\u662f\u81ea\u53d6\u706d\u4ea1\u4e86\uff01\u201d\u539f\u6765\u6210\u5409\u601d\u6c57\u65e9\u5df2\u77e5 \u9053\u4ed6\u4eec\u5144\u5f1f\u4e0d\u548c\uff0c\u662f\u4ee5\u85c9\u6b64\u544a\u8beb\u3002 \u3000\u3000\u6210\u5409\u601d\u6c57\u53c8\u9053\uff1a\u201c\u4f60\u4eec\u90fd\u8981\u50cf\u5c04\u51fa\u7684\u7bad\u4e00\u6837\u6709\u529b\uff0c\u98de\u5feb\u5c04\u6740\u654c\u4eba\u3002\u603b\u6709\u4e00\u5929\uff0c\u5168\u4e16\u754c\u90fd \u662f\u54b1\u4eec\u8499\u53e4\u4eba\u7684\u7267\u573a\uff01\u201d \u3000\u3000\u8bf8\u5c06\u6b22\u58f0\u96f7\u52a8\uff0c\u5531\u8d77\u8499\u53e4\u6218\u6b4c\uff1a \u3000\u3000\u201c\u661f\u5929\u65cb\u8f6c\uff0c\u8bf8\u56fd\u4e89\u6218\u3002\u8fde\u4e0a\u5e8a\u94fa\u7761\u89c9\u7684\u5de5\u592b\u4e5f\u6ca1\u6709\uff0c \u3000\u3000\u4e92\u76f8\u62a2\u593a\u3001\u63b3\u63a0\u3002 \u3000\u3000\u4e16\u754c\u7ffb\u8f6c\uff0c\u8bf8\u56fd\u653b\u4f10\u3002 \u3000\u3000\u8fde\u8fdb\u88ab\u7a9d\u7761\u89c9\u7684\u5de5\u592b\u4e5f\u6ca1\u6709\uff0c \u3000\u3000\u4e92\u76f8\u4e89\u593a\u3001\u6740\u4f10\u3002 \u3000\u3000\u6ca1\u6709\u601d\u8003\u4f59\u6687\uff0c \u3000\u3000\u53ea\u6709\u5c3d\u529b\u884c\u4e8b\u3002 \u3000\u3000\u6ca1\u6709\u9003\u907f\u5730\u65b9\uff0c \u3000\u3000\u53ea\u6709\u51b2\u950b\u6253\u4ed7\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u542c\u4e86\u6218\u6b4c\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u6210\u5409\u601d\u6c57\u8981\u628a\u4e16\u754c\u53d8\u4f5c\u4ed6\u7684\u7267\u573a\uff0c\u91ce\u5fc3\u4e4b\u5927\uff0c\u786e\u662f\u524d\u65e0 \u53e4\u4eba\uff0c\u6050\u6015\u4e5f\u662f\u540e\u65e0\u6765\u8005\u7684\u4e86\u3002\u53ef\u60dc\u4ed6\u53ea\u8bc6\u5f2f\u5f13\u5c04\u5927\u96d5\uff0c\u8fd9\u9996\u8499\u53e4\u6218\u6b4c\u552f\u77e5\u5d07\u62dc\u6b66\u529b\uff0c\u7eb5\u7136 \u80fd\u591f\u65e0\u654c\u5929\u4e0b\uff0c\u53ea\u6015\u4e5f\u662f\u96be\u4ee5\u4e45\u957f\uff01\u201d \u3000\u3000\u8d64\u8001\u6e29\u9053\uff1a\u201c\u674e\u516c\u5b50\uff0c\u6211\u5148\u7ed9\u4f60\u7980\u62a5\u5927\u6c57\uff0c\u4f60\u8ddf\u7740\u6765\u3002\u201d \u3000\u3000\u674e\u5e0c\u6d69\u5728\u6210\u5409\u601d\u6c57\u5c04\u96d5\u4e4b\u65f6\uff0c\u8ddf\u968f\u8499\u53e4\u6b66\u58eb\u4fef\u4f0f\u6b4c\u9882\uff0c\u6b64\u65f6\u65b9\u624d\u7ad9\u8d77\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8981 \u8c12\u89c1\u5927\u6c57\uff0c\u8fd8\u4e0d\u8d76\u5feb\u4e0b\u9a6c\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u662f\u4e2a\u5e74\u8f7b\u7684\u6c49\u4eba\uff0c\u5728\u6ee1\u5c71\u8499\u53e4\u6b66\u571f\u4e4b\u4e2d\uff0c\u672c\u6765\u5c31\u5df2\u53d7\u4eba\u6ce8\u610f\uff0c\u6b64\u65f6\u9664\u4e86\u6210\u5409\u601d \u6c57\uff0c\u53c8\u53ea\u6709\u4ed6\u4e00\u4e2a\u4eba\u9a91\u5728\u9a6c\u4e0a\uff0c\u76ee\u6807\u5c31\u66f4\u663e\u9732\u4e86\u3002\u6210\u5409\u601d\u6c57\u542c\u4e86\u8d64\u8001\u6e29\u7684\u62a5\u544a\uff0c\u770b\u4e86\u674e\u601d\u5357 \u4e00\u773c\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u5012\u662f\u80c6\u6c14\u4e0d\u5c0f\uff01\u201d \u3000\u3000\u674e\u5e0c\u6d69\u6b63\u8981\u62c9\u513f\u5b50\u4e0b\u9a6c\uff0c\u6210\u5409\u601d\u6c57\u6447\u4e86\u6447\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u5fc5\u4e0b\u9a6c\uff0c\u4f60\u8fc7\u6765\u5427\uff01\u201d\u674e\u5e0c\u6d69 \u4e0d\u77e5\u4ed6\u7684\u7528\u610f\uff0c\u5fc3\u4e2d\u60f4\u60f4\u4e0d\u5b89\u3002 \u3000\u3000\u674e\u601d\u5357\u7b56\u9a6c\u8fc7\u53bb\uff0c\u6210\u5409\u601d\u6c57\u5728\u4e00\u4e2a\u536b\u58eb\u7684\u7bad\u888b\u4e2d\u53d6\u4e86\u4e00\u679d\u7bad\uff0c\u5f85\u674e\u601d\u5357\u5230\u4e86\u767e\u6b65\u8ddd\u79bb\u4e4b \u5185\uff0c\u5ffd\u7136\u8bf4\u9053\uff1a\u201c\u4f60\u5c0f\u5fc3\u4e86\uff0c\u63a5\u7bad\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u5927\u5403\u4e00\u60ca\uff0c\u53ea\u542c\u5f97\u9739\u96f3\u4e00\u58f0\uff0c\u90a3\u679d\u7bad\u5df2\u662f\u5c04\u6765\u3002\u674e\u601d\u5357\u8bc6\u5f97\u6210\u5409\u601d\u6c57\u7bad\u6cd5\u7684\u5389 \u5bb3\uff0c\u77e5\u9053\u65e0\u6cd5\u95ea\u907f\uff0c\u53ea\u597d\u4e5f\u662f\u4e00\u7bad\u5c04\u53bb\u3002 \u3000\u3000\u4e24\u679d\u7bad\u5728\u534a\u7a7a\u4e2d\u78b0\u4e2a\u6b63\u7740\uff0c\u674e\u601d\u5357\u90a3\u679d\u7bad\u5148\u6389\u4e0b\u6765\uff0c\u6210\u5409\u601d\u6c57\u90a3\u679d\u7bad\u4f59\u52bf\u672a\u8870\uff0c\u5230\u4e86\u674e \u601d\u5357\u7684\u9a6c\u524d\u5341\u6b65\u4e4b\u5185\u65b9\u624d\u6389\u4e0b\u3002\u4f17\u6b66\u571f\u4e0d\u7531\u5f97\u53c8\u662f\u5927\u58f0\u559d\u5f69\u3002\u8499\u53e4\u6b66\u58eb\u5d07\u62dc\u672c\u9886\u9ad8\u5f3a\u7684\u4eba\uff0c \u8fd9\u5f69\u58f0\u56fa\u7136\u662f\u4e3a\u4ed6\u4eec\u7684\u5927\u6c57\u800c\u53d1\uff0c\u4f46\u4e5f\u4e0d\u65e0\u4f69\u670d\u674e\u601d\u5357\u7684\u610f\u601d\u5728\u5185\u3002 \u3000\u3000\u6210\u5409\u601d\u6c57\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u542c\u8bf4\u4f60\u66fe\u5728\u6208\u58c1\u5c04\u843d\u5140\u9e70\uff0c\u7bad\u6cd5\u679c\u7136\u4e0d\u9519\u3002\u518d\u63a5\u4e00\u652f\uff01\u201d \u4ece\u53e6\u4e00\u4e2a\u6b66\u58eb\u7684\u7bad\u888b\u4e2d\u53d6\u51fa\u4e00\u652f\uff0c\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u8fd9\u679d\u7bad\u662f\u4e8c\u53f7\u94c1\u80ce\u5f13\u7684\u7bad\uff0c\u4f60\u53ef\u8981\u52a0\u500d\u5c0f\u5fc3 \u4e86\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u8fd9\u624d\u77e5\u9053\u6210\u5409\u601d\u6c57\u662f\u8981\u8bd5\u4ed6\u7684\u7bad\u6cd5\uff0c\u4ed6\u521a\u624d\u5c04\u843d\u6210\u5409\u601d\u6c57\u7684\u7b2c\u4e00\u679d\u7bad\u5df2\u662f\u7528\u4e86\u4e0d \u5c11\u6c14\u529b\uff0c\u4e24\u818a\u6b63\u81ea\u9178\u75db\uff0c\u5fc3\u9053\uff1a\u201c\u4e0d\u597d\uff0c\u8fd9\u679d\u7bad\u6050\u6015\u6211\u662f\u63a5\u4e0d\u4e0b\u3002\u201d\u4f46\u4e5f\u53ea\u597d\u5c3d\u529b\u800c\u4e3a\uff0c\u548c \u4ed6\u5bf9\u5c04\u3002 \u3000\u3000\u201c\u53ee\u201d\u7684\u4e00\u58f0\uff0c\u4e24\u679d\u7bad\u5728\u7a7a\u4e2d\u53c8\u662f\u6070\u597d\u78b0\u4e2a\u6b63\u7740\uff0c\u53ea\u89c1\u6570\u70b9\u706b\u661f\u98de\u6e85\uff0c\u674e\u601d\u5357\u7684\u7bad\u5934\u65ad \u6298\uff0c\u7acb\u5373\u5760\u5730\uff0c\u6210\u5409\u601d\u6c57\u90a3\u679d\u7bad\u4ecd\u7136\u98de\u6765\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u8e44\u58f0\u6025\u9aa4\uff0c\u4e00\u9a91\u9a6c\u4ece\u6797\u4e2d\u98de\u51fa\uff0c\u5f13\u5f26\u58f0\u54cd\uff0c\u4e00\u679d\u7bad\u659c\u523a\u5c04\u6765\uff0c\u6070\u597d\u628a\u6210\u5409\u601d\u6c57\u8fd9 \u679d\u7bad\u78b0\u843d\u3002\u5728\u9a6c\u4e0a\u53d1\u7bad\u662f\u4e2a\u660e\u7738\u7693\u9f7f\u7684\u5c11\u5973\u3002 \u3000\u3000\u5c11\u5973\u5c04\u843d\u4e86\u6210\u5409\u601d\u6c57\u7684\u7bad\uff0c\u53eb\u9053\uff1a\u201c\u7239\u7239\uff0c\u8fd9\u4e0d\u516c\u5e73\uff01\u201d \u3000\u3000\u6210\u5409\u601d\u6c57\u7b11\u9053\uff1a\u201c\u600e\u4e48\u4e0d\u516c\u5e73\u4e86\uff1f\u201d \u3000\u3000\u8fd9\u5c11\u5973\u9053\uff1a\u201c\u7239\u7239\uff0c\u4f60\u5e38\u5938\u53e3\u5bf9\u4efb\u4f55\u5f3a\u654c\u90fd\u53ea\u662f\u4e00\u7bad\uff0c\u4e00\u7bad\u672a\u4e2d\uff0c\u51b3\u4e0d\u518d\u5c04\u3002\u4e3a\u4ec0\u4e48\u5bf9 \u8fd9\u4e2a\u5e74\u8f7b\u5c0f\u4f19\u5b50\u4f60\u5374\u5c04\u4e86\u4e24\u7bad\uff1f\u201d \u3000\u3000\u6210\u5409\u601d\u6c57\u7b11\u9053\uff1a\u201c\u963f\u9791\u6d77\uff0c\u4f60\u9519\u4e86\u3002\u8fd9\u5c0f\u4f19\u5b50\u4e0d\u662f\u54b1\u4eec\u7684\u654c\u4eba\uff0c\u4ed6\u662f\u54b1\u4eec\u7684\u670b\u53cb\u3002\u6211\u542c \u5f97\u8d64\u8001\u6e29\u8bf4\uff0c\u4ed6\u66fe\u7ecf\u5728\u6208\u58c1\u5c04\u4f24\u5140\u9e70\uff0c\u6240\u4ee5\u8bd5\u8bd5\u4ed6\u7684\u7bad\u6cd5\uff0c\u4f60\u4e0d\u89c1\u6211\u7528\u7684\u53ea\u662f\u5bfb\u5e38\u7684\u5c0f\u53f7\u5f13 \u7bad\u5417\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u8fd9\u624d\u660e\u767d\u6210\u5409\u601d\u6c57\u4e3a\u4ec0\u4e48\u8981\u62ff\u6b66\u58eb\u7684\u7bad\uff0c\u539f\u6765\u662f\u4e0d\u60f3\u4f7f\u7528\u4ed6\u81ea\u5df1\u7684\u90a3\u79cd\u7279\u5927\u53f7\u7684 \u94c1\u7bad\u3002 \u3000\u3000\u674e\u601d\u5357\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6210\u5409\u601d\u6c57\u5018\u82e5\u7528\u4ed6\u81ea\u5df1\u7684\u7bad\uff0c\u6211\u53ea\u6015\u662f\u4e00\u679d\u4e5f\u63a5\u4e0d\u8d77\u3002\u201d\u6b64\u65f6\uff0c\u5c3d \u7ba1\u674e\u601d\u5357\u8fd8\u662f\u4e0d\u613f\u964d\u987a\uff0c\u4f46\u5bf9\u6210\u5409\u601d\u6c57\u7684\u7bad\u6cd5\u5374\u5df2\u662f\u5fc3\u60a6\u8bda\u670d\uff0c\u5bf9\u6210\u5409\u601d\u6c57\u7684\u8c6a\u6c14\uff0c\u4e5f\u4e0d\u80fd \u4e0d\u6709\u51e0\u5206\u5fc3\u6298\uff0c\u5f53\u4e0b\u8dc3\u4e0b\u9a6c\u6765\uff0c\u5411\u6210\u5409\u601d\u6c57\u884c\u8fc7\u4e86\u793c\uff0c\u8bf4\u9053\uff1a\u201c\u5927\u6c57\u795e\u7bad\uff0c\u5929\u4e0b\u65e0\u53cc\uff0c\u5c0f\u5b50 \u62dc\u773c\u3002\u201d\u53ef\u662f\u674e\u601d\u5357\u7684\u201c\u62dc\u201d\u670d\uff0c\u6240\u884c\u7684\u793c\u4e5f\u53ea\u662f\u957f\u63d6\u800c\u5df2\uff0c\u5e76\u6ca1\u50cf\u4ed6\u7236\u4eb2\u90a3\u6837\u4fef\u4f0f\u8dea\u62dc\u3002 \u3000\u3000\u6210\u5409\u601d\u6c57\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u80fd\u591f\u63a5\u5f97\u6211\u7684\u4e24\u7bad\uff0c\u4e5f\u5f88\u662f\u4e0d\u9519\u4e86\u3002\u8fd9\u662f\u6211\u7684\u5973\u513f\u963f\u9791\u6d77\uff0c\u5979 \u4e5f\u5f88\u559c\u6b22\u9a91\u9a6c\u5c04\u7bad\uff0c\u4f60\u4eec\u4eca\u5929\u53ef\u4ee5\u6709\u4f34\u4e86\u3002\u201d \u3000\u3000\u674e\u5e0c\u6d69\u542c\u5f97\u6210\u5409\u601d\u6c57\u79f0\u8d5e\u4ed6\u7684\u513f\u5b50\uff0c\u7709\u5f00\u773c\u7b11\u5730\u8d70\u8fc7\u6765\u8bf4\u9053\uff1a\u201c\u5357\u513f\uff0c\u4f60\u8fd8\u4e0d\u8c22\u8c22\u660e\u6167 \u516c\u4e3b\uff1f\u4e0d\u662f\u5979\u5e2e\u4e86\u4f60\u5fd9\uff0c\u4f60\u600e\u63a5\u5f97\u8d77\u5927\u6c57\u7684\u795e\u7bad\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u516c\u4e3b\u7684\u7bad\u6cd5\u9ad8\u660e\uff0c\u6211\u4e5f\u662f\u975e\u5e38\u4f69\u670d\u7684\u3002\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u5fc5\u8bf4\u5ba2\u5957\u7684\u8bdd\uff0c\u6211\u770b\u54b1\u4eec\u7684\u7bad\u6cd5\u6050\u6015\u6b63\u662f\u534a\u65a4\u516b\u4e24\u3002\u7b49\u4f1a\u513f\u6253 \u730e\uff0c\u6211\u548c\u4f60\u6bd4\u6bd4\uff0c\u770b\u662f\u8c01\u5c04\u7684\u91ce\u517d\u591a\uff0c\u597d\u5417\uff1f\u201d \u3000\u3000\u6210\u5409\u601d\u6c57\u9053\uff1a\u201c\u4f60\u4ece\u4e2d\u539f\u6765\uff0c\u4f60\u770b\u770b\u6211\u7684\u6b66\u571f\u6bd4\u91d1\u56fd\u7684\u600e\u6837\uff1f\u6211\u7684\u519b\u961f\u53ef\u4ee5\u8361\u5e73\u5929\u4e0b \u5417\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u5927\u6c57\u5175\u5f3a\u5c06\u52c7\uff0c\u8981\u6253\u8d25\u91d1\u56fd\u662f\u5bb9\u6613\u7684\uff0c\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u6210\u5409\u601d\u6c57\u9053\uff1a\u201c\u4e0d\u8fc7\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u6211\u4eec\u6c49\u4eba\u8bb2\u7a76\u7684\u662f\u4ee5\u5fb7\u670d\u4eba\uff0c\u4e0d\u662f\u4ee5\u529b\u670d\u4eba\u3002\u4ee5\u5fb7\u670d\u4eba\u8001\u662f\u738b\u8005\u4e4b\u5e08\uff0c\u4e0d \u987b\u591a\u4e8b\u6740\u4f10\uff0c\u5929\u4e0b\u81ea\u4f1a\u9f9b\u7136\u666f\u4ece\u3002\u8bf7\u5927\u6c57\u6574\u519b\u7ecf\u6b66\u4e4b\u9645\uff0c\u517c\u65bd\u4ec1\u4e49\u3002\u201d \u3000\u3000\u6210\u5409\u601d\u6c57\u6447\u4e86\u6447\u5934\uff0c\u5927\u7b11\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u4f60\u4eec\u6c49\u4eba\u7684\u6240\u8c13\u5112\u5bb6\u4e4b\u8bf4\u5427\uff1f\u563f\uff0c\u563f\uff0c\u8fd9\u4e9b\u8150\u5112 \u4e4b\u89c1\uff0c\u600e\u80fd\u4fe1\u5f97\uff01\u4e0d\u7528\u6b66\u529b\u600e\u80fd\u8ba8\u5e73\u5929\u4e0b\uff1f\u7a7a\u8c08\u4ec1\u4e49\uff0c\u8fd9\u4e0d\u662f\u5b69\u5b50\u7684\u8bf4\u8bdd\u5417\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u89c1\u8bdd\u4e0d\u6295\u673a\uff0c\u6b63\u5f85\u9000\u4e0b\uff0c\u6210\u5409\u601d\u6c57\u5374\u53c8\u628a\u4ed6\u53eb\u4e86\u56de\u6765\u3002 \u3000\u3000\u6210\u5409\u601d\u6c57\u8bf4\u9053\uff1a\u201c\u6211\u8d2c\u65a5\u4e86\u4f60\u4eec\u6c49\u4eba\u7684\u8150\u5112\u4e4b\u89c1\uff0c\u4f60\u5fc3\u91cc\u5f88\u4e0d\u8212\u670d\uff0c\u662f\u4e48\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u7b54\u9053\uff1a\u201c\u4e0d\u6562\u3002\u5404\u6709\u6240\u89c1\uff0c\u5c82\u80fd\u5c3d\u540c\u3002\u201d\u610f\u601d\u662f\u8bf4\uff1a\u4f60\u6709\u4f60\u7684\u770b\u6cd5\uff0c\u6211\u6709\u6211\u7684\u770b \u6cd5\u3002\u6211\u529d\u4f60\u4e0d\u542c\uff0c\u4f60\u4e5f\u4e0d\u5fc5\u52c9\u5f3a\u6211\u8ddf\u968f\u4f60\u7684\u4e3b\u5f20\u3002\u6210\u5409\u601d\u6c57\u7684\u624b\u4e0b\u89c1\u4ed6\u7b54\u5f97\u4ecd\u662f\u5982\u6b64\u5014\u5f3a\uff0c \u4e0d\u7981\u76f8\u987e\u5931\u8272\u3002 \u3000\u3000\u6210\u5409\u601d\u6c57\u54c8\u54c8\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u771f\u6709\u4f60\u7684\u3002\u4e0d\u8fc7\uff0c\u6211\u867d\u7136\u770b\u4e0d\u8d77\u4f60\u4eec\u6c49\u4eba\u4e2d\u7684\u8150 \u5112\uff01\u90a3\u4e9b\u771f\u6b63\u6709\u672c\u9886\u7684\u4eba\u6211\u8fd8\u662f\u5f88\u4f69\u670d\u7684\u3002\u542c\u8bf4\u4f60\u4eec\u53e4\u65f6\u6709\u4e2a\u5b59\u6b66\u5b50\uff0c\u66fe\u8457\u6709\u300a\u5b59\u5b50\u5175\u6cd5\u5341 \u4e09\u7bc7\u300b\uff0c\u8bb2\u7684\u884c\u519b\u7528\u5175\u4e4b\u9053\uff0c\u5f88\u662f\u4e0d\u9519\uff0c\u53ef\u60dc\u6211\u6ca1\u6709\u89c1\u8fc7\u8fd9\u672c\u4e66\u3002\u53c8\u542c\u8bf4\u4f60\u4eec\u767e\u5e74\u524d\u66fe\u51fa\u8fc7 \u5cb3\u98de\u3001\u97e9\u4e16\u5fe0\u4e24\u4f4d\u82f1\u96c4\uff0c\u628a\u91d1\u4eba\u6253\u5f97\u671b\u98ce\u800c\u9003\u3002\u5c24\u5176\u5cb3\u98de\uff0c\u91d1\u4eba\u66fe\u6709\u2018\u64bc\u5c71\u6613\uff0c\u64bc\u5cb3\u5bb6\u519b \u96be\u2019\u4e4b\u53f9\u3002\u4f46\u89c1\u4ed6\u4eec\u7684\u7528\u5175\u4e5f\u4e00\u5b9a\u662f\u5f88\u662f\u4e86\u5f97\u7684\u4e86\u3002\u4e0d\u77e5\u4ed6\u4eec\u53ef\u6709\u5175\u6cd5\u9057\u7559\u4e0b\u6765\u4e48\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u5fc3\u4e2d\u4e00\u52a8\uff0c\u60f3\u9053\uff1a\u201c\u96be\u9053\u7239\u7239\u66fe\u7ecf\u5bf9\u4ed6\u8bf4\u8fc7\u6211\u4eec\u7684\u7956\u5148\u662f\u97e9\u4e16\u5fe0\u7684\u90e8\u4e0b\uff1f\u4e5f\u66fe\u7ecf \u53c2\u52a0\u8fc7\u5cb3\u98de\u6240\u6307\u6325\u7684\u4f1a\u6218\u4e48\uff1f\u201d\u4ed6\u7236\u4eb2\u6240\u6ce8\u91ca\u7684\u90a3\u672c\u5175\u6cd5\u8fd8\u5728\u4ed6\u7684\u6000\u4e2d\uff0c\u674e\u601d\u5357\u5b9a\u4e86\u5b9a\u795e\uff0c \u7b54\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u6210\u5409\u601d\u6c57\u53c8\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u53ef\u66fe\u5b66\u8fc7\u5175\u6cd5\uff1f\u5982\u679c\u4f60\u5b66\u8fc7\u7684\u8bdd\uff0c\u4e0d\u59a8\u6765\u7ed9\u6211\u8bb2\u89e3\u8bb2\u89e3\u3002\u6211\u60f3 \u77e5\u9053\u4f60\u4eec\u6c49\u4eba\u662f\u600e\u4e48\u6837\u7528\u5175\u7684\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u6211\u662f\u4e00\u4e2a\u4e61\u4e0b\u5b69\u5b50\uff0c\u53ea\u8bfb\u8fc7\u51e0\u5e74\u4e66\uff0c\u8bc6\u5f97\u51e0\u4e2a\u5b57\u7f62\u4e86\u3002\u5175\u6cd5\u4e48\uff1f\u90a3\u6211\u53ef\u662f \u4e00\u7a8d\u4e0d\u901a\u3002\u201d \u3000\u3000\u6210\u5409\u601d\u6c57\u6447\u4e86\u6447\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u53ef\u60dc\uff0c\u53ef\u60dc\uff01\u4f60\u4eec\u5148\u4eba\u7684\u597d\u4e1c\u897f\u4f60\u6ca1\u6709\u5b66\u5230\uff0c\u53ea\u5b66\u4e86\u4e9b\u8150 \u5112\u4e4b\u89c1\u3002\u201d \u3000\u3000\u8bf8\u5c06\u8bf4\u9053\uff1a\u201c\u5927\u6c57\u7528\u5175\uff0c\u5929\u4e0b\u65e0\u654c\uff0c\u8fd8\u4f55\u987b\u5b66\u4ec0\u4e48\u6c49\u4eba\u7684\u5175\u6cd5\uff1f\u201d \u3000\u3000\u6210\u5409\u601d\u6c57\u6b63\u8272\u7b54\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u7528\u5175\u4e4b\u5999\uff0c\u5b58\u4e4e\u4e00\u5fc3\u3002\u4e0d\u8fc7\u6c49\u4eba\u7684\u4e24\u53e5\u8bdd\u2018\u53d6\u4eba\u4e4b\u957f\uff0c\u8865 \u5df1\u4e4b\u77ed\u2019\uff0c\u4e5f\u8fd8\u662f\u8bf4\u5f97\u5f88\u5bf9\u7684\u3002\u6211\u544a\u8bc9\u4f60\u4eec\uff0c\u51e1\u6709\u4f60\u4eec\u4e0d\u4f1a\u7684\u672c\u4e8b\uff0c\u4f60\u4eec\u90fd\u8981\u5b66\u3002\u8bb0\u4f4f \u4e86\uff01\u201d\u8bf8\u5c06\u78b0\u4e86\u4e00\u4e2a\u9489\u5b50\uff0c\u9f50\u58f0\u8bf4\u9053\uff1a\u201c\u662f\uff01\u201d \u3000\u3000\u6210\u5409\u601d\u6c57\u62db\u4e86\u62db\u624b\uff0c\u628a\u56db\u4e2a\u5c06\u9886\u62db\u5230\u4ed6\u7684\u8ddf\u524d\uff0c\u5bf9\u4e3a\u9996\u7684\u5ffd\u5fc5\u6765\u8bf4\u9053\uff1a\u201c\u4f60\u7ed9\u6211\u626d\u6389\u4e86 \u5f3a\u6881\u7684\u5934\uff0c\u4f60\u7ed9\u6454\u5f97\u529b\u58eb\u7684\u5c41\u80a1\u5750\u5730\u3002\u4f60\u548c\u8005\u9791\u7bfe\u3001\u54f2\u522b\u3001\u901f\u522b\u989d\u53f0\u5c31\u50cf\u662f\u6211\u7684\u5fe0\u5b9e\u731b\u72d7\u3002 \u6211\u8981\u4f60\u4eec\uff1a \u3000\u3000\u8bf4\u5230\u7684\u5730\u65b9\u5c31\u5230\uff0c \u3000\u3000\u53bb\u628a\u575a\u77f3\u7c89\u788e\uff1b \u3000\u3000\u8bf4\u653b\u7684\u5730\u65b9\u5c31\u653b \u3000\u3000\u53bb\u628a\u786c\u5ca9\u6363\u6bc1\uff1b \u3000\u3000\u628a\u9ad8\u5c71\u5288\u5f00\uff0c \u3000\u3000\u628a\u6df1\u6c34\u65ad\u6db8 \u3000\u3000\u8fd9\u6837\u52c7\u6562\u6740\u654c\uff01\u201d \u3000\u3000\u5ffd\u5fc5\u6765\u7b49\u56db\u4eba\u9f50\u58f0\u7b54\u9053\uff1a\u201c\u662f\u3002\u53ea\u8981\u5927\u6c57\u4e00\u58f0\u4eca\u4e0b\uff0c\u6211\u4eec\u4e00\u5b9a\u50cf\u4f60\u7684\u5fe0\u5b9e\u731b\u72d7\u4e00\u822c\u3002\u5954 \u5f80\u6307\u5b9a\u7684\u5730\u65b9\uff0c\u54ac\u556e\u654c\u4eba\uff0c\u6495\u788e\u654c\u4eba\u3002\u201d \u3000\u3000\u6210\u5409\u601d\u6c57\u5f88\u662f\u5f97\u610f\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u521a\u624d\u5df2\u7ecf\u542c\u5f97\u6211\u7684\u7977\u544a\u4e86\uff0c\u6211\u4e00\u7bad\u5c04\u843d\u53cc\u96d5\uff0c\u4e0a\u5929\u5df2 \u7ecf\u8bb8\u6211\u706d\u91d1\u4e86\u3002\u6211\u8981\u4f60\u4eec\u505a\u5148\u950b\uff0c\u901f\u5f80\u8fb9\u5883\uff0c\u9886\u5175\u51fa\u53d1\u3002\u6211\u5c06\u4eb2\u7387\u5927\u519b\u4f5c\u4f60\u4eec\u7684\u540e\u63f4\u3002\u4f60\u4eec \u73b0\u5728\u5c31\u53bb\u5427\uff01\u201d\u539f\u6765\u8499\u53e4\u5df1\u6709\u4e00\u90e8\u5206\u5f81\u8c03\u7684\u5175\u58eb\u805a\u96c6\u8fb9\u5883\uff0c\u53ea\u7b49\u6210\u5409\u601d\u6c57\u6d3e\u51fa\u7684\u5c06\u9886\u53bb\u6307 \u6325\u3002 \u3000\u3000\u6210\u5409\u601d\u6c57\u6d3e\u9063\u4e86\u5ffd\u5fc5\u6765\u7b49\u56db\u5c06\u4e4b\u540e\uff0c\u5bf9\u5176\u4ed6\u7684\u4eba\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u4e0d\u4e45\u4e5f\u5c31\u8981\u51fa\u5f81\u4e86\uff0c\u4eca\u5929 \u8ba9\u4f60\u4eec\u75db\u75db\u5feb\u5feb\u5730\u73a9\u4e00\u5929\u3002\u59d1\u4e14\u4f60\u4eec\u4e0d\u5fc5\u8ddf\u7740\u6211\u4e86\uff0c\u90fd\u53bb\u6253\u730e\u5427\uff01\u201d \u3000\u3000\u6728\u534e\u9ece\u8d70\u6765\u95ee\u674e\u601d\u5357\u9053\uff1a\u201c\u542c\u8bf4\u4f60\u6628\u665a\u78b0\u4e0a\u523a\u5ba2\uff0c\u662f\u5c60\u767e\u57ce\u7684\u515a\u7fbd\uff0c\u672c\u9886\u5f88\u662f\u5389\u5bb3\uff0c\u4f60 \u53d7\u60ca\u4e86\u5427\uff1f\u6211\u8fd8\u672a\u66fe\u5411\u4f60\u6170\u95ee\u5462\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u8bf4\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\u3002\u523a\u5ba2\u662f\u4ec0\u4e48\u4eba\u8fd8\u672a\u77e5\u9053\uff0c\u4ed6\u4eec\u8ba4\u4e3a\u662f\u5c60\u767e\u57ce\u7684\u515a\u7fbd\uff0c\u90a3\u4e5f\u53ea\u4e0d \u8fc7\u662f\u731c\u6d4b\u800c\u5df2\u3002\u201d \u3000\u3000\u6728\u534e\u9ece\u7b11\u9053\uff1a\u201c\u523a\u5ba2\u662f\u4ec0\u4e48\u4eba\uff0c\u5f88\u5feb\u5c31\u4f1a\u77e5\u9053\u7684\u3002\u6211\u63a5\u4e86\u4ee4\u5c0a\u7684\u62a5\u544a\uff0c\u4eca\u5929\u4e00\u65e9\u5df2\u7ecf\u6d3e \u51fa\u2018\u795e\u7ffc\u8425\u2019\u7684\u5341\u516b\u540d\u597d\u624b\u53bb\u8ffd\u6355\u4e86\u3002\u523a\u5ba2\u53d7\u4e86\u4f24\uff0c\u4e00\u5b9a\u8dd1\u4e0d\u6389\u7684\u3002\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u660e\u6167\u516c\u4e3b\u9a91\u9a6c\u8d70\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u8bf4\u5b8c\u4e86\u6ca1\u6709\uff1f\u7239\u7239\u53eb\u4ed6\u966a\u6211\u53bb\u6253\u730e\u5462\uff01\u201d \u6728\u534e\u9ece\u8fde\u5fd9\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u4e5f\u5e76\u6ca1\u6709\u4ec0\u4e48\u8981\u8c08\u7684\u3002\u597d\uff0c\u6211\u4e0d\u963b\u788d\u4f60\u4eec\u6253\u730e\u4e86\u3002\u201d\u6728\u534e\u9ece\u8bf4\u4e86\u4e4b \u540e\uff0c\u4fbf\u5373\u8d70\u5f00\uff0c\u53bb\u627e\u674e\u5e0c\u6d69\u8bf4\u8bdd\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u8bb0\u6302\u90a3\u4e2a\u53d7\u4f24\u7684\u523a\u5ba2\uff0c\u966a\u7740\u660e\u6167\u516c\u4e3b\u6253\u730e\uff0c\u5374\u662f\u5fc3\u795e\u4e0d\u5b89\u3002\u201c\u4f46\u613f\u90a3\u4eba\u80fd\u591f\u9003\u8131 \u2018\u795e\u7ffc\u8425\u2019\u7684\u8ffd\u6355\uff0c\u4e0d\u7136\u5012\u662f\u6211\u7d2f\u4e86\u4ed6\u4e86\u3002\u201d\u53c8\u60f3\uff1a\u201c\u5927\u6c57\u5df2\u7ecf\u6d3e\u51fa\u5148\u950b\uff0c\u5927\u519b\u4f10\u91d1\u5728\u5373\uff0c \u7239\u7239\u548c\u6211\u6050\u6015\u4e5f\u8981\u968f\u519b\u51fa\u53d1\u4e86\u3002\u6211\u5fc5\u987b\u8d76\u5feb\u5230\u963f\u513f\u683c\u5c71\u7684\u677e\u98ce\u8c37\u53bb\uff0c\u53ef\u662f\u5374\u600e\u751f\u627e\u5f97\u4e2a\u85c9\u53e3 \u5462\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u7531\u4e8e\u5fc3\u795e\u4e0d\u5c5e\uff0c\u7bad\u6cd5\u5927\u5931\u6c34\u51c6\uff0c\u597d\u51e0\u6b21\u78b0\u7740\u91ce\u517d\uff0c\u90fd\u5c04\u4e0d\u4e2d\u3002\u660e\u6167\u516c\u4e3b\u730e\u53d6\u7684\u91ce \u517d\u6bd4\u4ed6\u591a\u5f97\u591a\u3002 \u3000\u3000\u660e\u6167\u516c\u4e3b\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u5b58\u5fc3\u8ba9\u6211\u7684\u5462\uff1f\u8fd8\u662f\u6709\u7740\u4ec0\u4e48\u5fc3\u4e8b\uff1f\u6211\u53ef\u4e0d\u8981\u4f60\u8ba9\uff01\u4f46\u82e5\u662f\u4f60\u6709 \u4ec0\u4e48\u5fc3\u4e8b\uff0c\u5012\u4e0d\u59a8\u8bf4\u7ed9\u6211\u542c\u542c\uff0c\u6211\u603b\u53ef\u4ee5\u5e2e\u4f60\u7684\u5fd9\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u4e0d\u77e5\u5982\u4f55\u56de\u7b54\uff0c\u6b63\u5728\u780c\u8f9e\uff0c\u5ffd\u542c\u5f97\u91ce\u517d\u7684\u543c\u58f0\uff0c\u6811\u6797\u91cc\u7a81\u7136\u8e7f\u51fa\u4e00\u5934\u72ec\u89d2\u7280\uff0c\u6765 \u52bf\u51f6\u731b\uff0c\u4e00\u89c1\u6709\u4eba\uff0c\u4fbf\u5373\u6251\u6765\u3002 \u3000\u3000\u660e\u6167\u516c\u4e3b\u8fde\u5fd9\u4e00\u7bad\u5c04\u53bb\uff0c\u72ec\u89d2\u7280\u662f\u6bd4\u8001\u864e\u8fd8\u51f6\u7684\u731b\u517d\uff0c\u76ae\u575a\u8089\u539a\uff0c\u660e\u6167\u516c\u4e3b\u7684\u7bad\u867d\u7136\u5c04 \u4e2d\u4e86\u5b83\uff0c\u5374\u662f\u4f24\u5b83\u4e0d\u5f97\u3002\u72ec\u89d2\u7280\u53d1\u4e86\u6012\uff0c\u7acb\u5373\u7528\u5b83\u90a3\u6839\u5229\u5200\u4e00\u6837\u7684\u72ec\u89d2\u6765\u6233\u660e\u6167\u516c\u4e3b\u3002 \u3000\u3000\u660e\u6167\u516c\u4e3b\u867d\u7136\u65f6\u5e38\u6253\u730e\uff0c\u5982\u4e5f\u4ece\u672a\u78b0\u8fc7\u8fd9\u6837\u51f6\u6076\u7684\u7280\u725b\uff0c\u89c1\u5b83\u6251\u6765\uff0c\u5413\u5f97\u614c\u4e86\uff0c\u8fd9\u4e00\u77ac \u95f4\uff0c\u53cc\u817f\u7ade\u662f\u4e0d\u542c\u4f7f\u5524\uff0c\u773c\u770b\u7280\u725b\u7684\u5229\u89d2\u5c31\u8981\u6233\u5230\u80f8\u53e3\uff0c\u95ea\u907f\u90fd\u6765\u4e0d\u53ca\u4e86\u3002\u5c31\u5728\u8fd9\u95f4\u4e0d\u5bb9\u53d1 \u4e4b\u9645\uff0c\u9661\u7136\u95f4\u53ea\u89c9\u8eab\u5b50\u4e00\u8f7b\uff0c\u5c31\u4f3c\u817e\u4e91\u9a7e\u96fe\u822c\u5730\u98de\u8d77\u4e86\u3002 \u3000\u3000\u7280\u725b\u4e00\u58f0\u5927\u543c\uff0c\u5730\u52a8\u5c71\u6447\uff0c\u660e\u6167\u516c\u4e3b\u611f\u5230\u6709\u4eba\u5c06\u5979\u7d27\u7d27\u62b1\u4f4f\uff0c\u4e00\u53cc\u811a\u4e5f\u4f3c\u4e4e\u5df2\u8e0f\u7740\u4e86\u5b9e \u5730\u3002\u53ea\u542c\u5f97\u674e\u601d\u5357\u5728\u5979\u8033\u8fb9\u8bf4\u9053\uff1a\u201c\u516c\u4e3b\u4e0d\u7528\u5bb3\u6015\uff0c\u7280\u725b\u5df2\u7ecf\u6b7b\u4e86\u3002\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u7741\u773c\u4e00\u770b\uff0c\u53ea\u89c1\u674e\u601d\u5357\u5728\u5979\u8eab\u8fb9\uff0c\u4e00\u6761\u624b\u81c2\u8fd8\u5728\u534a\u62e5\u7740\u5979\uff0c\u90a3\u5934\u72ec\u89d2\u7280\u5219\u5df2\u5012 \u6bd9\u5728\u4e00\u5ea7\u5371\u5ca9\u4e4b\u4e0b\uff0c\u725b\u5934\u4e0a\u8fd8\u538b\u7740\u4e00\u5757\u5927\u77f3\u3002 \u3000\u3000\u539f\u6765\u674e\u601d\u5357\u5c31\u5728\u90a3\u95f4\u4e0d\u5bb9\u53d1\u4e4b\u9645\uff0c\u5c06\u660e\u6167\u516c\u4e3b\u4e00\u628a\u62b1\u4e86\u8d77\u6765\uff0c\u800c\u4e14\u51fa\u5251\u5982\u7535\uff0c\u523a\u778e\u4e86\u90a3 \u72ec\u89d2\u7280\u7684\u53cc\u773c\u3002\u5e78\u4e8f\u4ed6\u7684\u8f7b\u529f\u8d85\u5353\uff0c\u62b1\u7740\u4e2a\u4eba\uff0c\u8fd8\u80fd\u591f\u8dc3\u8d77\u4e00\u4e08\u591a\u9ad8\uff0c\u7280\u725b\u7684\u5229\u89d2\u51e0\u4e4e\u662f\u64e6 \u7740\u4ed6\u7684\u811a\u677f\u5e95\u51b2\u8fc7\u3002\u8fd9\u5934\u7280\u725b\u778e\u4e86\u53cc\u773c\uff0c\u53d1\u6012\u4e71\u649e\uff0c\u649e\u7740\u5ca9\u77f3\uff0c\u5934\u8111\u5f00\u82b1\uff0c\u8fd9\u624d\u5012\u6bd9\u3002 \u3000\u3000\u8499\u53e4\u4eba\u867d\u7136\u4e0d\u4f3c\u6c49\u4eba\u7684\u90a3\u6837\u8bb2\u7a76\u7537\u5973\u4e4b\u522b\uff0c\u4f46\u8eba\u5728\u4e00\u4e2a\u7537\u5b50\u7684\u6000\u4e2d\uff0c\u4e5f\u8fd8\u662f\u660e\u6167\u516c\u4e3b\u6709 \u751f\u4ee5\u6765\u7684\u7b2c\u4e00\u6b21\uff0c\u660e\u6167\u516c\u4e3b\u5b9a\u4e86\u5b9a\u795e\uff0c\u4e0d\u7531\u5f97\u53cc\u988a\u6655\u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u4f60\u6551\u4e86\u6211\u7684\u6027\u547d\u3002\u4f60 \u7684\u672c\u9886\u771f\u597d\uff0c\u4f60\u662f\u600e\u4e48\u6740\u4e86\u8fd9\u5934\u7280\u725b\u7684\uff1f\u7280\u725b\u7684\u89d2\u5f88\u6709\u7528\u5904\uff0c\u54b1\u4eec\u73b0\u5728\u53ef\u4ee5\u53bb\u5265\u5b83\u7684\u89d2 \u4e86\u3002\u201d\u8bdd\u4e2d\u4e4b\u610f\u4ea6\u5373\u662f\u63d0\u9192\u674e\u601d\u5357\uff0c\u53ef\u4ee5\u653e\u5f00\u5979\u4e86\u3002 \u3000\u3000\u674e\u601d\u5357\u521a\u624d\u6025\u4e8e\u6551\u4eba\uff0c\u6839\u672c\u5c31\u672a\u60f3\u5230\u8981\u907f\u5acc\u7591\uff0c\u6b64\u65f6\u84e6\u7136\u4e00\u7701\uff0c\u4e5f\u662f\u4e0d\u7531\u5f97\u6ee1\u9762\u901a\u7ea2\uff0c \u653e\u5f00\u4e86\u516c\u4e3b\uff0c\u8bb7\u8bb7\u8bf4\u9053\uff1a\u201c\u6211\u53ea\u662f\u4fa5\u5e78\u523a\u778e\u4e86\u5b83\u7684\u53cc\u773c\uff0c\u5b83\u81ea\u5df1\u649e\u5ca9\u6b7b\u6389\u7684\u3002\u201d \u3000\u3000\u516c\u4e3b\u7b11\u9053\uff1a\u201c\u4f60\u5012\u662f\u4e00\u4e2a\u6253\u730e\u7684\u5927\u884c\u5bb6\u5462\uff0c\u7280\u725b\u76ae\u7c97\u8089\u539a\uff0c\u82e5\u4e0d\u662f\u523a\u778e\u4e86\u5b83\u7684\u53cc\u773c\uff0c\u53ea \u6015\u4f60\u8fd9\u628a\u5b9d\u5251\u4e5f\u672a\u5fc5\u5c31\u6740\u5f97\u6b7b\u5b83\u3002\u201d \u3000\u3000\u6b63\u8bf4\u8bdd\u95f4\uff0c\u5ffd\u89c1\u6709\u51e0\u4e2a\u4eba\u4ece\u6811\u6797\u91cc\u98de\u8dd1\u51fa\u6765\uff0c\u8d70\u5728\u6700\u524d\u9762\u7684\u662f\u4e00\u4e2a\u62ab\u7740\u72d0\u88d8\u7684\u5c11\u5e74\uff0c\u540e \u9762\u8ddf\u7740\u968f\u4ece\uff0c\u8fd9\u51e0\u4e2a\u968f\u4ece\u6b63\u5728\u5411\u7740\u4ed6\u4eec\u6307\u6307\u70b9\u70b9\u5730\u548c\u90a3\u4e2a\u5c11\u5e74\u8bf4\u8bdd\u3002\u674e\u601d\u5357\u9690\u9690\u542c\u5f97\u5176\u4e2d\u4e00 \u4eba\u8bf4\u9053\uff1a\u201c\u963f\u9791\u6d77\u522b\u59ec\u662f\u5927\u6c57\u6700\u5ba0\u7231\u7684\u5973\u513f\uff0c\u738b\u5b50\u4f60\u53ef\u4e0d\u80fd\u5411\u5979\u53d1\u813e\u6c14\u554a\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u5f88\u662f\u5947\u602a\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u662f\u54ea\u91cc\u6765\u7684\u738b\u5b50\uff0c\u4e3a\u4ec0\u4e48\u4ed6\u89c1\u4e86\u516c\u4e3b\u8981\u53d1\u813e\u6c14\u5462\uff1f\u201d \u5fc3\u5ff5\u672a\u5df2\uff0c\u53ea\u89c1\u90a3\u5c11\u5e74\u5df2\u662f\u65cb\u98ce\u822c\u5730\u8dd1\u5230\u4e86\u4ed6\u7684\u9762\u524d\uff0c\u9661\u5730\u5c31\u4eae\u51fa\u4e86\u4e00\u67c4\u6708\u7259\u5f2f\u5200\uff0c\u5411\u4ed6\u5927 \u543c\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u4f60\u901e\u80fd\u6740\u4e86\u6211\u8981\u730e\u53d6\u7684\u7280\u725b\uff0c\u6211\u5012\u8981\u770b\u770b\u4f60\u6709\u4ec0\u4e48\u672c\u9886\uff1f\u201d\u4e0d\u7531\u5206\u8bf4\uff0c\u671d \u7740\u674e\u601d\u5357\u5c31\u662f\u4e00\u5200\u5288\u4e0b\u3002 \u3000\u3000\u539f\u6765\u8fd9\u5c11\u5e74\u5c31\u662f\u6c6a\u53e4\u90e8\u914b\u957f\u7684\u513f\u5b50\u9547\u56fd\uff0c\u4ed6\u6b63\u662f\u660e\u6167\u516c\u4e3b\u7684\u672a\u5a5a\u592b\u3002 \u3000\u3000\u9547\u56fd\u751f\u5f97\u9762\u5982\u9505\u5e95\uff0c\u4e24\u9f7f\u64a9\u7259\u9732\u51fa\u5507\u8fb9\uff0c\u76f8\u8c8c\u4e11\u964b\uff0c\u4f46\u6c14\u529b\u5374\u662f\u4e0d\u5c0f\u3002\u674e\u601d\u5357\u62d4\u5251\u62db \u67b6\uff0c\u6321\u4e86\u4e00\u62db\uff0c\u7ed9\u4ed6\u51b2\u5f97\u5012\u9000\u4e09\u6b65\u3002 \u3000\u3000\u660e\u6167\u516c\u4e3b\u9a82\u9053\uff1a\u201c\u54ea\u91cc\u6765\u7684\u4e11\u516b\u602a\uff0c\u6562\u5230\u8fd9\u91cc\u64a4\u91ce\u3002\u201d\u5979\u4e0d\u77e5\u9053\u8fd9\u4e2a\u201c\u4e11\u516b\u602a\u201d\u6b63\u662f\u5979 \u7684\u672a\u5a5a\u592b\uff0c\u62ff\u8d77\u5f13\u7bad\uff0c\u5c31\u8981\u5c04\u4ed6\u3002 \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u516c\u4e3b\u4e0d\u7528\u52a8\u624b\uff0c\u5f85\u6211\u548c\u4ed6\u7406\u8bba\u3002\u201d\u5530\u5530\u51e0\u5251\uff0c\u7cbe\u8292\u7535\u5c04\u3002\u5251\u5149\u8fc7\u5904\uff0c\u9547\u56fd \u6240\u88ab\u7684\u72d0\u88ad\u88ab\u524a\u53bb\u4e86\u4e00\u5e45\u3002\u9547\u56fd\u5927\u5403\u4e00\u60ca\uff0c\u4e0d\u7531\u5f97\u4e5f\u5012\u9000\u4e86\u4e09\u6b65\u3002 \u3000\u3000\u674e\u601d\u5357\u559d\u9053\uff1a\u201c\u4f60\u662f\u4ec0\u4e48\u4eba\uff1f\u8fd9\u7280\u725b\u53c8\u4e0d\u662f\u4f60\u517b\u7684\uff0c\u8c01\u6709\u672c\u9886\u8c01\u5c31\u53ef\u4ee5\u730e\u5b83\uff0c\u4f60\u600e\u80fd\u8fd9 \u6837\u4e0d\u8bb2\u9053\u7406\uff1f\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u89c1\u674e\u601d\u5357\u5360\u4e86\u4e0a\u98ce\uff0c\u5927\u4e3a\u9ad8\u5174\uff0c\u53eb\u9053\uff1a\u201c\u4e0d\u5fc5\u7ba1\u4ed6\u662f\u8c01\uff0c\u4f60\u7ed9\u6211\u63cd\u4ed6\u4e00\u987f\uff01\u201d \u3000\u3000\u9547\u56fd\u542c\u5f97\u672a\u5a5a\u59bb\u5982\u6b64\u8bf4\u8bdd\uff0c\u6c14\u5f97\u4e03\u7a8d\u751f\u70df\uff0c\u86ee\u6027\u4e00\u53d1\uff0c\u4e0d\u987e\u6b7b\u6d3b\u5730\u5c31\u4e71\u5288\u4e71\u65a9\u3002\u674e\u601d\u5357 \u4e0d\u7981\u4e5f\u52a8\u4e86\u706b\u6c14\uff0c\u5fc3\u9053\uff1a\u201c\u4e0d\u7ed9\u8fd9\u5c0f\u5b50\u4e00\u70b9\u6559\u8bad\uff0c\u4ed6\u4e5f\u4e0d\u77e5\u9053\u5389\u5bb3\u3002\u201d \u3000\u3000\u9547\u56fd\u53ea\u662f\u6709\u4e00\u8eab\u86ee\u529b\uff0c\u5200\u6cd5\u5374\u662f\u666e\u666e\u901a\u901a\uff0c\u600e\u6bd4\u5f97\u4e0a\u674e\u601d\u5357\u5c11\u6797\u6d3e\u5ae1\u4f20\u7684\u8fbe\u6469\u5251\u6cd5\u7684\u7cbe \u5999\uff0c\u4e0d\u8fc7\u6570\u62db\uff0c\u674e\u601d\u5357\u7528\u4e86\u4e00\u4e2a\u201c\u7c98\u201d\u5b57\u8bc0\uff0c\u5c06\u4ed6\u7684\u6708\u7259\u5200\u4e00\u7275\u4e00\u5e26\uff0c\u201c\u7c98\u201d\u51fa\u5916\u95e8\uff0c\u518d\u628a \u957f\u5251\u53ea\u662f\u8f7b\u8f7b\u4e00\u7ede\uff0c\u53ea\u542c\u5f97\u201c\u94db\u201d\u7684\u4e00\u58f0\uff0c\u9547\u56fd\u7684\u90a3\u53e3\u6708\u7259\u5200\u5df2\u662f\u8131\u624b\u98de\u51fa\u3002 \u3000\u3000\u5c31\u5728\u6b64\u65f6\uff0c\u53ea\u89c1\u8d64\u8001\u6e29\u98de\u9a91\u5954\u6765\uff0c\u5927\u53eb\u9053\uff1a\u201c\u4f4f\u624b\uff0c\u5927\u6c57\u6765\u5566\uff01\u201d \u3000\u3000\u9547\u56fd\u9ed1\u8138\u6cdb\u7ea2\uff0c\u62fe\u8d77\u4e86\u5200\uff0c\u6c14\u547c\u547c\u5730\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u4f60\u522b\u8dd1\u3002\u6211\u548c\u4f60\u5230\u5927\u6c57\u8ddf\u524d\u7406 \u8bba\u3002\u201d\u4ed6\u6253\u4e0d\u8fc7\u674e\u601d\u5357\uff0c\u6b64\u65f6\u624d\u8bf4\u8981\u548c\u4ed6\u201c\u7406\u8bba\u201d\u3002 \u3000\u3000\u6210\u5409\u601d\u9a91\u9a6c\u6765\u5230\uff0c\u559d\u9053\uff1a\u201c\u8c01\u5728\u8fd9\u91cc\u80e1\u95f9\u4e86\uff1f\u55ef\uff0c\u539f\u6765\u662f\u4f60\uff0c\u662f\u4f60\u7239\u7239\u53eb\u4f60\u6765\u7684\u5417\uff1f\u4f60 \u4eec\u5374\u600e\u4e48\u6253\u8d77\u6765\u4e86\uff1f\u201d\u7b2c\u4e8c\u53e5\u8bdd\u5411\u9547\u56fd\u53d1\u95ee\uff0c\u6700\u540e\u4e00\u53e5\u5374\u662f\u5411\u674e\u601d\u5357\u8bf4\u7684\u3002 \u3000\u3000\u660e\u6167\u516c\u4e3b\u4e0d\u5f85\u9547\u56fd\u7533\u8fa9\uff0c\u5148\u5c31\u62a2\u4e86\u4e0a\u53bb\u548c\u7236\u4eb2\u8bc9\u8bf4\uff1a\u201c\u8fd9\u5934\u72ec\u89d2\u7280\u51e0\u4e4e\u8981\u4e86\u6211\u7684\u6027\u547d\uff0c \u591a\u4e8f\u674e\u601d\u5357\u6551\u4e86\u6211\u3002\u4ed6\u6740\u4e86\u7280\u725b\uff0c\u4f46\u8fd9\u9ed1\u70ad\u5934\u5374\u8dd1\u6765\u5927\u53eb\u5927\u56b7\uff0c\u8bf4\u662f\u6211\u4eec\u730e\u4e86\u4ed6\u7684\u7280\u725b\uff0c\u56e0 \u6b64\u5c31\u8981\u6740\u674e\u601d\u5357\uff0c\u7239\u7239\uff0c\u4f60\u8bf4\u6709\u6ca1\u6709\u8fd9\u4e2a\u9053\u7406\u3002\u201d \u3000\u3000\u6210\u5409\u601d\u6c57\u7b11\u9053\uff1a\u201c\u963f\u9791\u6d77\uff0c\u4e0d\u53ef\u65e0\u793c\u3002\u4f60\u77e5\u9053\u4ed6\u662f\u8c01\uff1f\u4ed6\u662f\u4f60\u7684\u672a\u5a7f\u592b\u3002\u201d \u3000\u3000\u660e\u6167\u5927\u5403\u4e00\u60ca\uff0c\u53c8\u7f9e\u53c8\u607c\u5730\u558a\u9053\uff0c\u201c\u4ec0\u4e48\uff1f\u4ed6\u662f\u6211\u7684\u592b\u5a7f\uff1f\u6211\u624d\u4e0d\u5ac1\u8fd9\u9ed1\u70ad\u5934\u5462\uff01\u201d \u3000\u3000\u6210\u5409\u601d\u6c57\u53cc\u76ee\u4e00\u77aa\uff0c\u8bf4\u9053\uff1a\u201c\u90fd\u662f\u6211\u628a\u4f60\u5ba0\u574f\u4e86\uff0c\u6211\u8bf4\u7684\u8bdd\u5c45\u7136\u4e5f\u6562\u4e0d\u542c\u4e86\uff01\u8fd9\u6869\u5a5a\u4e8b \u662f\u6211\u4eb2\u53e3\u7b54\u5e94\u7684\uff0c\u5c82\u80fd\u5bb9\u4f60\u4e0d\u4f9d\uff1f\u4f60\u9000\u4e0b\u53bb\uff01\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u7a76\u7adf\u662f\u6709\u51e0\u5206\u6015\u5979\u7236\u4eb2\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5ac1\u4e0d\u5ac1\u662f\u6211\u7684\u4e8b\u3002\u4f46\u73b0\u5728\u7239\u7239\u6b63\u5728\u53d1 \u6c14\uff0c\u6211\u6682\u4e14\u5fcd\u5b83\u4e00\u5fcd\u3002\u201d \u3000\u3000\u660e\u6167\u867d\u7136\u9000\u4e0b\uff0c\u5fc3\u6709\u4e0d\u7518\uff0c\u4ecd\u7136\u8bf4\u9053\uff1a\u201c\u7239\u7239\uff0c\u4f60\u5e38\u5e38\u8bf4\u8d4f\u7f5a\u516c\u5e73\uff0c\u53ef\u4e0d\u80fd\u5b58\u79c1\u504f \u8892\u3002\u201d \u3000\u3000\u6210\u5409\u601d\u6c57\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\u6211\u8d4f\u7f5a\u4e0d\u516c\u5e73\uff1f\u674e\u601d\u5357\u8fc7\u6765\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u4e0a\u80fd\u884c\u4e86\u793c\uff0c\u6210\u5409\u601d\u6c57\u8bf4\u9053\uff1a\u201c\u4f60\u5c04\u6b7b\u7280\u725b\uff0c\u6551\u4e86\u6211\u7684\u5973\u513f\uff0c\u6211\u8fd9\u526f\u5f13\u7bad\u8e22\u7ed9 \u4f60\uff0c\u5c01\u4f60\u505a\u91d1\u5e10\u6b66\u571f\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u5927\u6c57\u7684\u8d4f\u8d50\uff0c\u6211\u4e0d\u6562\u53d7\u3002\u201d \u3000\u3000\u6210\u5409\u601d\u6c57\u6012\u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u4f60\u6562\u770b\u8f7b\u6211\u7684\u8d4f\u8d50\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u4e0d\u6562\uff0c\u4f46\u6211\u4e00\u65e0\u6218\u529f\uff0c\u4e8c\u65e0\u672c\u9886\uff0c\u91d1\u5e10\u6b66\u58eb\u7684\u5c01\u53f7\u6211\u600e\u6562\u539a\u989c\u627f\u53d7\uff1f\u201d \u3000\u3000\u6210\u5409\u601d\u6c57\u60f3\u4e86\u4e00\u60f3\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u5f88\u8c26\u865a\uff0c\u5b9e\u5728\u96be\u5f97\u3002\u597d\u5427\uff0c\u6211\u4e0d\u7ed9\u4f60\u5b9e\u804c\uff0c\u6682\u4e14\u5148\u7ed9\u4f60 \u4ee5\u91d1\u5e10\u6b66\u571f\u540c\u7b49\u5f85\u9047\uff0c\u5f85\u4f60\u6709\u4e86\u519b\u529f\uff0c\u518d\u5b9e\u6388\u4f60\u8fd9\u4e2a\u5c01\u53f7\uff0c\u4f60\u603b\u53ef\u4ee5\u63a5\u53d7\u4e86\u5427\u3002\u4f60\u7684\u7bad\u5c04\u5f97 \u5f88\u597d\uff0c\u8fd9\u526f\u5f13\u7bad\u6b63\u5408\u4f60\u7528\uff0c\u4f60\u5c31\u4e0d\u5fc5\u63a8\u8f9e\u4e86\u3002\u201d \u3000\u3000\u539f\u6765\u8fd9\u201c\u91d1\u5e10\u6b66\u571f\u201d\u7684\u5c01\u53f7\u662f\u6781\u5c0a\u8d35\u7684\uff0c\u53d7\u5c01\u91d1\u5e10\u6b66\u571f\u7684\u4eba\u90fd\u662f\u8ddf\u968f\u6210\u5409\u601d\u6c57\u8eab\u7ecf\u767e \u6218\u3001\u51fa\u6b7b\u5165\u751f\u7684\u4eba\uff0c\u800c\u4e14\u4e5f\u4ece\u6765\u6ca1\u6709\u6c49\u4eba\u5f97\u8fc7\uff1b\u6210\u5409\u601d\u6c57\u518d\u52a0\u8003\u8651\u4e4b\u540e\uff0c\u4e5f\u6015\u8bf8\u5c06\u4e0d\u670d\uff0c\u662f \u4ee5\u63a5\u7eb3\u4e86\u674e\u601d\u5357\u4e4b\u8bf7\uff0c\u5c06\u5b83\u64a4\u56de\u3002\u4ed6\u5374\u4e0d\u77e5\u674e\u601d\u5357\u5b9e\u662f\u4e0d\u613f\u5728\u4ed6\u624b\u4e0b\u4e3a\u5b98\u3002 \u3000\u3000\u5c01\u53f7\u867d\u7136\u64a4\u9500\uff0c\u4f46\u6210\u5409\u601d\u6c57\u4eb2\u53e3\u8bb8\u4ee5\u201c\u91d1\u5e10\u6b66\u571f\u201d\u7684\u540c\u7b49\u5f85\u9047\uff0c\u8fd9\u5373\u662f\u8bf4\u4ed6\u4e0d\u4f46\u53ef\u4ee5\u6709 \u540c\u6837\u7684\u4ff8\u7984\uff0c\u800c\u4e14\u4e5f\u5e94\u8be5\u53d7\u540c\u6837\u7684\u5c0a\u91cd\u4e86\uff0c\u4f55\u51b5\u6210\u5409\u601d\u6c57\u8fd8\u628a\u81ea\u7528\u7684\u5f13\u7bad\u8d4f\u8e22\u7ed9\u4ed6\uff0c\u8fd9\u66f4\u662f \u8499\u53e4\u6b66\u58eb\u90fd\u4ece\u672a\u5f97\u8fc7\u7684\u201c\u6b8a\u8363\u201d\u3002\u6210\u5409\u601d\u6c57\u7684\u624b\u4e0b\u5f53\u7136\u4f53\u4f1a\u5f97\u5230\u5927\u6c57\u7684\u610f\u601d\uff0c\u7eb7\u7eb7\u5411\u674e\u601d\u5357 \u9053\u8d3a\u3002\u674e\u601d\u5357\u53ea\u8981\u4e0d\u5728\u6210\u5409\u601d\u6c57\u7684\u624b\u4e0b\u5f53\u5dee\uff0c\u4e5f\u5c31\u613f\u610f\u63a5\u53d7\u4e86\u3002\u4ed6\u5bf9\u201c\u91d1\u5e10\u6b66\u58eb\u201d\u7684\u5c01\u53f7\u6beb \u4e0d\u5728\u4e4e\uff0c\u4f46\u5bf9\u6210\u5409\u601d\u6c57\u8fd9\u526f\u5f13\u7bad\u4ed6\u5374\u662f\u5341\u5206\u6b22\u559c\u7684\u3002 \u3000\u3000\u4f17\u4eba\u7eb7\u7eb7\u5411\u674e\u601d\u5357\u9053\u8d3a\uff0c\u6c6a\u53e4\u90e8\u7684\u9547\u56fd\u738b\u5b50\u51b7\u843d\u4e00\u65c1\uff0c\u5374\u662f\u5c34\u5c2c\u5f97\u5f88\u3002\u6210\u5409\u601d\u6c57\u8d4f\u8d50\u4e86 \u674e\u601d\u5357\u4e4b\u540e\uff0c\u5411\u4ed6\u62db\u4e00\u62db\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u73b0\u5728\u4f60\u8fc7\u6765\u5427\u3002\u201d\u9547\u56fd\u60f4\u60f4\u4e0d\u5b89\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a \u201c\u5927\u6c57\u8d4f\u8d50\u4e86\u8fd9\u5c0f\u5b50\uff0c\u5c82\u4e0d\u662f\u8981\u5904\u7f5a\u6211\u4e86\uff1f\u201d\u8d70\u5230\u6210\u5409\u601d\u6c57\u8ddf\u524d\uff0c\u7ea2\u7740\u8138\u8bf4\u9053\uff0c\u201c\u6211\u53ea\u662f\u4e0d \u670d\u6c14\u4ed6\uff0c\u8981\u548c\u4ed6\u6bd4\u6bd4\u672c\u9886\u800c\u5df2\uff0c\u5e76\u4e0d\u6562\u600e\u4e48\u6837\u7684\u3002\u201d \u3000\u3000\u6210\u5409\u601d\u6c57\u9762\u5b54\u4e00\u677f\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u5992\u5fcc\u522b\u4eba\u672c\u9886\u6bd4\u4f60\u597d\uff0c\u8fd9\u5c31\u4e0d\u5bf9\u4e86\u3002\u4e0d\u8fc7\uff0c\u597d\u5728\u4f60\u4eec\u90fd \u6ca1\u6709\u53d7\u4f24\uff0c\u5c11\u5e74\u4eba\u597d\u80dc\uff0c\u53ea\u6bd4\u6bd4\u6b66\u4e5f\u662f\u5bfb\u5e38\u4e4b\u4e8b\u3002\u4eca\u6b21\u6211\u4e0d\u7f5a\u4f60\uff0c\u4f60\u597d\u597d\u8ddf\u6211\u6253\u4ed7\uff0c\u5f85\u6253\u4e86 \u80dc\u4ed7\u56de\u6765\uff0c\u6211\u5c31\u628a\u6211\u7684\u5c0f\u5973\u513f\u7ed9\u4f60\u3002\u201d \u3000\u3000\u9547\u56fd\u8fd9\u6b21\u6765\u89c1\u6210\u5409\u601d\u6c57\u7684\u9762\uff0c\u6b63\u662f\u60f3\u8981\u8fce\u4eb2\u7684\uff0c\u5982\u4eca\u5f97\u5230\u4e86\u6210\u5409\u601d\u6c57\u4eb2\u53e3\u8bb8\u4e0b\u5a5a\u671f\uff0c\u5bf9 \u4ed6\u6765\u8bf4\uff0c\u8fd9\u53ef\u662f\u6bd4\u4ec0\u4e48\u8d4f\u8d50\u90fd\u66f4\u5b9d\u8d35\u7684\u3002\u6b63\u662f\uff1a \u3000\u3000\u559c\u6709\u4f73\u4eba\u9752\u773c\u8d4f\uff0c\u5374\u60ca\u701a\u6d77\u8d77\u98ce\u6ce2\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b\u2014\u2014\u7b2c\u4e94\u56de\u3000\u8981\u4ece\u5b57\u8ff9\u5206\u771f\u4f2a\u3000\u7ec6\u542c\u524d\u60c5\u8bc6\u53cb\u4ec7 \u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b \u7b2c\u4e94\u56de\u3000\u8981\u4ece\u5b57\u8ff9\u5206\u771f\u4f2a\u3000\u7ec6\u542c\u524d\u60c5\u8bc6\u53cb\u4ec7 \u3000\u3000\u9547\u56fd\u5927\u559c\u53e9\u8c22\uff0c\u8bf4\u9053\uff1a\u201c\u5927\u6c57\u6d2a\u6069\uff0c\u5c0f\u5a7f\u7c89\u8eab\u788e\u9aa8\u65e0\u4ee5\u4e3a\u62a5\u3002\u4f55\u65e5\u5174\u5e08\uff0c\u5c0f\u5a7f\u81ea\u5f53\u5e26\u9886 \u672c\u90e8\u4eba\u9a6c\uff0c\u6765\u6548\u524d\u9a71\u3002\u201d \u3000\u3000\u6210\u5409\u601d\u6c57\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u7528\u4e0d\u7740\u4f60\u6253\u524d\u950b\u4e86\uff0c\u4f60\u628a\u4eba\u9a6c\u5e26\u6765\uff0c\u548c\u6211\u4e00\u540c\u51fa\u53d1\u5427\u3002\u5927\u519b\u8d77 \u884c\uff0c\u5c31\u5728\u8fd9\u51e0\u5929\u4e86\u3002\u201d\u539f\u6765\u6c6a\u4e3b\u90e8\u4e43\u662f\u8499\u53e4\u7684\u4e00\u5927\u90e8\u843d\uff0c\u6210\u5409\u601d\u6c57\u628a\u6700\u5ba0\u7231\u7684\u5c0f\u5973\u5ac1\u7ed9\u9547 \u56fd\uff0c\u4e3a\u7684\u5c31\u662f\u8981\u7b3c\u7edc\u4ed6\u3002 \u3000\u3000\u9547\u56fd\u559c\u4e0d\u81ea\u80dc\uff0c\u8c22\u8fc7\u4e86\u6069\uff0c\u4e0a\u9a6c\u5c31\u8d70\u3002\u4ed6\u5f97\u4e86\u5927\u6c57\u7684\u5429\u5490\uff0c\u8feb\u4e0d\u53ca\u5f85\u5730\u8d76\u56de\u53bb\u8981\u628a\u4eba\u9a6c \u5e26\u6765\u3002 \u3000\u3000\u660e\u6167\u516c\u4e3b\u7ecf\u8fc7\u4e86\u8fd9\u4e48\u4e00\u95f9\uff0c\u5174\u8da3\u7d22\u7136\uff0c\u65e0\u5fc3\u6253\u730e\uff0c\u6084\u6084\u7684\u4e5f\u8d70\u4e86\u3002\u5979\u8981\u56de\u53bb\u9759\u9759\u5730\u60f3\uff0c \u60f3\u60f3\u6709\u4ec0\u4e48\u6cd5\u5b50\u53ef\u4ee5\u62d2\u5a5a\u3002 \u3000\u3000\u6210\u5409\u601d\u6c57\u7684\u624b\u4e0b\u8fd8\u5728\u7eb7\u7eb7\u5411\u674e\u601d\u5357\u9053\u8d3a\uff0c\u5ffd\u5730\u6709\u4e00\u4e2a\u5c11\u5e74\u6b66\u58eb\u63a8\u5f00\u4f17\u4eba\uff0c\u8d70\u5230\u674e\u601d\u5357\u9762 \u524d\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u6551\u4e86\u6211\u7684\u59b9\u5b50\uff0c\u6211\u5411\u4f60\u9053\u8c22\u3002\u201d\u4f38\u51fa\u624b\u638c\uff0c\u5411\u674e\u601d\u5357\u80a9\u8180\u4e00\u62cd\u3002 \u3000\u3000\u674e\u601d\u5357\u53ea\u9053\u4ed6\u662f\u5411\u81ea\u5df1\u8868\u793a\u4eb2\u70ed\uff0c\u4e0d\u4ee5\u4e3a\u610f\uff0c\u4e0d\u6599\u9661\u7136\u95f4\u53ea\u89c9\u8eab\u5b50\u4e00\u8f7b\uff0c\u5df2\u662f\u7ed9\u8fd9\u5c11\u5e74 \u6b66\u58eb\u6293\u4e86\u8d77\u6765\uff0c\u52a8\u5f39\u4e0d\u5f97\u3002 \u3000\u3000\u672c\u6765\u4ee5\u674e\u601d\u5357\u7684\u6b66\u529f\uff0c\u5373\u4f7f\u662f\u51fa\u5176\u4e0d\u610f\uff0c\u4e5f\u672a\u5fc5\u5c31\u80fd\u591f\u5c06\u4ed6\u4e00\u628a\u5c31\u6293\u4e86\u8d77\u6765\u3002\u5982\u4eca\u7adf\u7136 \u4e00\u4e2a\u7167\u9762\u5c31\u7ed9\u6765\u4eba\u5236\u670d\uff0c\u8fd9\u5374\u662f\u4f55\u6545\uff1f\u539f\u6765\u8499\u53e4\u6b66\u571f\u64c5\u957f\u6454\u8de4\uff0c\u8fd1\u8eab\u640f\u6597\uff0c\u662f\u4ed6\u4eec\u7684\u770b\u5bb6\u672c \u9886\u3002\u8fd9\u4e2a\u5c11\u5e74\u6b66\u58eb\u66f4\u662f\u8499\u53e4\u6b66\u58eb\u4e2d\u7684\u80fd\u624b\u3002\u83ab\u8bf4\u674e\u601d\u5357\u662f\u88ab\u4ed6\u51fa\u5176\u4e0d\u610f\uff0c\u5373\u4f7f\u6709\u6240\u9632\u5907\uff0c\u4e5f \u672a\u5fc5\u8eb2\u5f97\u8fc7\u4ed6\u8fd9\u4e00\u6293\u4e00\u62ff\u3002 \u3000\u3000\u8fd9\u5c11\u5e74\u6b66\u58eb\u6293\u8d77\u4e86\u674e\u601d\u5357\u5f80\u5730\u4e0a\u4fbf\u6454\uff0c\u6b8a\u4e0d\u77e5\u4ed6\u4e0d\u6454\u8fd8\u597d\uff0c\u4e00\u6454\u53cd\u800c\u7ed9\u4e86\u674e\u601d\u5357\u53cd\u8d25\u4e3a \u80dc\u7684\u673a\u4f1a\uff0c\u674e\u601d\u5357\u8eab\u5b50\u4e00\u9e5e\uff0c\u8131\u51fa\u4e86\u5bf9\u65b9\u7684\u638c\u63e1\u4e4b\u540e\uff0c\u8eab\u5b50\u672a\u66fe\u843d\u5730\uff0c\u5df2\u662f\u53cd\u624b\u6263\u7740\u4e86\u90a3\u5c11 \u5e74\u6b66\u58eb\u7684\u8109\u95e8\uff0c\u501f\u529b\u4f7f\u529b\uff0c\u4e00\u4e2a\u5927\u7ffb\u8eab\uff0c\u559d\u58f0\uff1a\u201c\u53bb\uff01\u201d\u4ed6\u81ea\u5df1\u5b89\u5b89\u7a33\u7a33\u5730\u7ad9\u7acb\u5730\u4e0a\uff0c\u8fd9\u5c11 \u5e74\u6b66\u58eb\u5374\u7ed9\u4ed6\u6454\u5012\u5730\u4e0a\u4e86\u3002 \u3000\u3000\u674e\u5e0c\u6d69\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u5931\u58f0\u53eb\u9053\uff1a\u201c\u4e0d\u53ef\u65e0\u793c\uff0c\u8fd9\u3001\u8fd9\u662f\u56db\u6bbf\u4e0b\uff01\u201d\u4f46\u8bdd\u672a\u8bf4\u5b8c\uff0c\u8fd9 \u4f4d\u201c\u56db\u6bbf\u4e0b\u201d\u65e9\u5df2\u7ed9\u674e\u601d\u5357\u6454\u8dcc\u3002 \u3000\u3000\u8fd9\u4f4d\u201c\u56db\u6bbf\u4e0b\u201d\u5c31\u662f\u6210\u5409\u601d\u6c57\u7684\u5e7c\u5b50\u62d6\u96f7\u3002\u4ed6\u7ed9\u6454\u4e86\u4e00\u8de4\uff0c\u5e76\u4e0d\u607c\u6012\uff0c\u8df3\u4e86\u8d77\u6765\uff0c\u53cd\u800c \u54c8\u54c8\u5927\u7b11\uff0c\u6293\u4f4f\u674e\u601d\u5357\u7684\u624b\u8bf4\u9053\uff1a\u201c\u601d\u5357\u5b89\u7b54\uff0c\u4f60\u7684\u8eab\u624b\u679c\u7136\u4e86\u5f97\uff0c\u65e0\u6127\u4e8e\u91d1\u5e10\u6b66\u58eb\u7684\u5c01 \u53f7\u3002\u4f60\u613f\u610f\u548c\u6211\u7ed3\u4ea4\u4e48\uff1f\u201d \u3000\u3000\u539f\u6765\u62d6\u96f7\u662f\u4e0d\u670d\u6c14\u7236\u4eb2\u5c01\u4ed6\u505a\u201c\u91d1\u5e10\u6b66\u58eb\u201d\uff0c\u662f\u4ee5\u6709\u610f\u8bd5\u4ed6\u4e00\u8bd5\u3002\u5982\u4eca\u8bd5\u8fc7\u4e4b\u540e\uff0c\u6b63\u6240 \u8c13\u201c\u4e0d\u6253\u4e0d\u6210\u76f8\u8bc6\u201d\uff0c\u53cd\u800c\u5bf9\u674e\u601d\u5357\u4f69\u670d\u4e86\u3002\u4ed6\u79f0\u547c\u674e\u601d\u5357\u505a\u201c\u5b89\u7b54\u201d\uff0c\u201c\u5b89\u7b54\u201d\u5c31\u662f\u8499\u53e4 \u8bdd\u201c\u597d\u670b\u53cb\u201d\u7684\u610f\u601d\u3002 \u3000\u3000\u6210\u5409\u601d\u6c57\u4e5f\u5fcd\u4e0d\u4f4f\u7b11\u9053\uff1a\u201c\u522b\u4eba\u8fd8\u672a\u66fe\u7b54\u5e94\u548c\u4f60\u505a\u5b89\u7b54\u5462\uff0c\u4f60\u5c31\u548c\u4eba\u5bb6\u5f00\u8d77\u73a9\u7b11\u6765 \u4e86\u3002\u201d\u539f\u6765\u8499\u53e4\u4eba\u7684\u4e60\u60ef\uff0c\u597d\u670b\u53cb\u89c1\u9762\uff0c\u5e38\u5e38\u662f\u7528\u6454\u8de4\u6765\u8868\u793a\u4eb2\u70ed\u7684\uff0c\u5f53\u7136\u8fd9\u6837\u7684\u8868\u793a\u201c\u4eb2 \u70ed\u201d\uff0c\u4e5f\u662f\u5e26\u6709\u5f00\u73a9\u7b11\u7684\u610f\u5473\u7684\u3002 \u3000\u3000\u674e\u601d\u5357\u5e76\u4e0d\u613f\u610f\u5949\u627f\u5927\u6c57\u7236\u5b50\uff0c\u4f46\u8fd9\u662f\u62d6\u96f7\u4e3b\u52a8\u7684\u8981\u7eb3\u4ea4\u4e8e\u4ed6\uff0c\u674e\u601d\u5357\u81ea\u662f\u4e0d\u4fbf\u62d2\u4eba\u4e8e \u5343\u91cc\u4e4b\u5916\u3002\u4ed6\u5bf9\u62d6\u96f7\u7684\u8c6a\u723d\u4e5f\u6709\u51e0\u5206\u6b22\u559c\uff0c\u4e8e\u662f\u8bf4\u9053\uff1a\u201c\u53ea\u6015\u6211\u4e00\u4ecb\u5c0f\u6c11\u9ad8\u6500\u4e0d\u8d77\u3002\u201d \u3000\u3000\u62d6\u96f7\u7b11\u9053\uff1a\u201c\u82f1\u96c4\u4e0d\u8bba\u51fa\u8eab\uff0c\u6211\u7239\u7239\u4ee5\u714e\u4e5f\u66fe\u505a\u8fc7\u6cf0\u8d64\u4e4c\u65cf\u7684\u4fd8\u864f\uff0c\u5982\u4eca\u4e0d\u662f\u5f53\u4e86\u5927\u6c57 \u4e86\uff1f\u201d\u62d6\u96f7\u8fd9\u4e48\u4e00\u8bf4\uff0c\u674e\u601d\u5357\u53ea\u597d\u4e0e\u4ed6\u63e1\u624b\u7f14\u4ea4\uff0c\u5f7c\u6b64\u4e92\u79f0\u201c\u5b89\u7b54\u201d\u3002 \u3000\u3000\u6253\u4e86\u4e00\u4f1a\u730e\uff0c\u65e5\u5f71\u897f\u659c\uff0c\u6210\u5409\u601d\u6c57\u7684\u624b\u4e0b\u6b66\u58eb\u7eb7\u7eb7\u56de\u6765\uff0c\u5448\u732e\u730e\u7269\uff0c\u6210\u5409\u601d\u6c57\u54c8\u54c8\u5927 \u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4eca\u65e5\u53ef\u4ee5\u5c3d\u6b22\u800c\u6563\u4e86\u3002\u518d\u8fc7\u51e0\u5929\uff0c\u54b1\u4eec\u5927\u519b\u51fa\u53d1\uff0c\u90a3\u5c31\u4e0d\u662f\u730e\u7269\u800c\u662f\u730e\u4eba \u5566\uff01\u201d \u3000\u3000\u56de\u5bb6\u8def\u4e0a\uff0c\u674e\u5e0c\u6d69\u63a9\u9970\u4e0d\u4e86\u5185\u5fc3\u7684\u9ad8\u5174\uff0c\u559c\u5b5c\u5b5c\u5730\u548c\u513f\u5b50\u8bf4\u9053\uff1a\u201c\u6211\u4eca\u5929\u771f\u662f\u66ff\u4f60\u634f\u4e86 \u4e00\u628a\u6c57\uff0c\u8c01\u77e5\u4f60\u5374\u662f\u56e0\u7978\u5f97\u798f\uff01\u660e\u6167\u516c\u4e3b\u975e\u4f46\u4e0d\u602a\u4f60\u6253\u4e86\u5979\u7684\u9a78\u9a6c\uff0c\u770b\u6765\u5979\u8fd8\u5f88\u6b22\u559c\u4f60 \u5462\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u602b\u7136\u4e0d\u60a6\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u53c8\u4e0d\u60f3\u5728\u8499\u53e4\u5927\u6c57\u7684\u624b\u4e0b\u8ba8\u5403\uff0c\u4ec0\u4e48\u738b\u5b50\u4e5f\u597d\uff0c\u516c\u4e3b\u4e5f \u597d\uff0c\u6211\u53ef\u4e0d\u5e0c\u7f55\u4ed6\u4eec\u7684\u6b22\u559c\uff01\u201d \u3000\u3000\u674e\u5e0c\u6d69\u6014\u4e86\u4e00\u6014\uff0c\u7709\u5934\u4e00\u51db\uff0c\u81ea\u601d\uff1a\u201c\u8fd9\u5c0f\u5b50\u60f3\u7684\u548c\u6211\u5b8c\u5168\u4e0d\u4e00\u6837\uff0c\u6211\u4e5f\u4e0d\u80fd\u5728\u4ed6\u7684\u9762 \u524d\u592a\u7740\u75d5\u8ff9\u4e86\u3002\u201d\u4e8e\u662f\u5f3a\u7b11\u8bf4\u9053\uff1a\u201c\u8bdd\u4e0d\u662f\u8fd9\u4e48\u8bf4\uff0c\u54b1\u4eec\u6bd5\u7adf\u8fd8\u662f\u5bc4\u4eba\u7bf1\u4e0b\uff0c\u5c82\u80fd\u4e0d\u8ba8\u597d\u4eba \u5bb6\uff1f\u4fd7\u8bed\u8bf4\u5c0f\u4e0d\u5fcd\u5219\u4e71\u5927\u8c0b\uff0c\u6211\u529d\u4f60\u8fd8\u662f\u628a\u4f60\u8fd9\u80a1\u725b\u813e\u6c14\u6536\u655b\u4e00\u4e9b\uff0c\u5f85\u54b1\u4eec\u9003\u51fa\u4e86\u8499\u53e4\uff0c\u90a3 \u65f6\u4f60\u559c\u6b22\u600e\u4e48\u6837\u53d1\u4f5c\u5c31\u600e\u4e48\u6837\u53d1\u4f5c\uff0c\u4e0d\u8fc7\u5230\u5934\u6765\u548c\u8499\u53e4\u4eba\u6253\u4ed7\uff0c\u6211\u4e5f\u4e0d\u4f1a\u7ba1\u4f60\u3002\u201d\u5f53\u7136\u8fd9\u4e0d \u662f\u674e\u5e0c\u6d69\u7684\u7531\u8877\u4e4b\u8a00\uff0c\u4ed6\u53ea\u662f\u60f3\u201c\u5b89\u629a\u201d\u4ed6\u7684\u513f\u5b50\u800c\u5df2\u3002 \u3000\u3000\u674e\u601d\u5357\u5fc3\u91cc\u5374\u4e0d\u7981\u72af\u7591\uff0c\u60f3\u9053\uff1a\u201c\u7239\u7239\u867d\u7136\u8bf4\u5f97\u597d\uff0c\u4f46\u7ec6\u5bdf\u4ed6\u4eca\u65e5\u7684\u8a00\u884c\uff0c\u6050\u6015\u4ed6\u8fd8\u662f \u4e0d\u80af\u820d\u8fd9\u5df2\u7ecf\u5230\u624b\u4e86\u7684\u8363\u534e\u5bcc\u8d35\uff0c\u672a\u5fc5\u5c31\u80af\u548c\u6211\u5192\u9669\u6f5c\u9003\u5462\uff01\u201d\u4e0d\u8fc7\u4ed6\u4e5f\u4e0d\u4fbf\u5c31\u8d28\u95ee\u4ed6\u7684\u7236 \u4eb2\uff0c\u53ea\u597d\u6de1\u6de1\u5730\u8bf4\u4e86\u4e00\u4e2a\u2018\u662f\u2019\u5b57\u3002\u201d \u3000\u3000\u56de\u5230\u5e10\u5e55\uff0c\u5403\u8fc7\u665a\u996d\uff0c\u674e\u601d\u5357\u6b63\u60f3\u7761\u89c9\uff0c\u4ed6\u7684\u7236\u4eb2\u5ffd\u7136\u8d70\u8fdb\u4ed6\u8fd9\u5ea7\u5e10\u5e55\uff0c\u6b64\u65f6\u5df1\u662f\u5dee\u4e0d \u591a\u4e8c\u66f4\u65f6\u5206\u4e86\u3002 \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u7239\uff0c\u4f60\u8fd8\u6ca1\u6709\u7761\uff1f\u201d \u3000\u3000\u674e\u5e0c\u6d69\u8bf4\u9053\uff1a\u201c\u6211\u6709\u8bdd\u548c\u4f60\u8bf4\uff0c\u662f\u4e0d\u80fd\u8ba9\u5916\u4eba\u77e5\u9053\u7684\uff0c\u521a\u624d\u5403\u996d\u4e4b\u65f6\uff0c\u6709\u4ed6\u4eec\u7684\u4eba\u5728\u65c1 \u670d\u4f8d\uff0c\u6240\u4ee5\u6211\u4e0d\u6562\u8bf4\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u53ef\u662f\u7239\u7239\u5df2\u60f3\u597d\u4e86\u9003\u8d70\u7684\u8ba1\u5212\uff1f\u201d \u3000\u3000\u674e\u5e0c\u6d69\u9053\uff1a\u201c\u6211\u6628\u665a\u4e0d\u662f\u548c\u4f60\u8bf4\u8fc7\u4e86\uff0c\u5f85\u54b1\u4eec\u968f\u519b\u5230\u4e86\u4e2d\u539f\uff01\u624d\u80fd\u89c1\u673a\u800c\u4e3a\uff0c\u4f60\u4e0d\u5fc5\u8fd9 \u6837\u5fc3\u6025\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u7eb3\u7f55\u95ee\u9053\uff1a\u201c\u90a3\u4e48\u7239\u7239\u6765\u6b64\uff0c\u53c8\u662f\u4e3a\u4e86\u4f55\u4e8b\uff1f\u201d \u3000\u3000\u674e\u5e0c\u6d69\u8bf4\u9053\uff1a\u201c\u6211\u60f3\u8d77\u4e86\u4e00\u6869\u5fc3\u4e8b\u3002\u201d \u3000\u3000\u201c\u7239\u7239\u60f3\u7684\u4ec0\u4e48\u5fc3\u4e8b\uff1f\u201d \u3000\u3000\u201c\u5927\u6c57\u4eca\u5929\u4e0d\u662f\u548c\u4f60\u8c08\u8d77\u5cb3\u98de\u548c\u97e9\u4e16\u5fe0\u7684\u5175\u6cd5\u4e48\uff1f\u54b1\u4eec\u7684\u5148\u7956\u662f\u97e9\u4e16\u5fe0\u7684\u90e8\u5c06\uff0c\u66fe\u6709\u8bb8 \u591a\u96f6\u7bc7\u65ad\u7b80\u9057\u7559\u4e0b\u6765\uff0c\u5176\u4e2d\u5305\u62ec\u6709\u97e9\u5c06\u519b\u4e34\u9635\u7684\u90e8\u7f72\uff0c\u53e3\u6388\u7684\u5175\u6cd5\uff0c\u5e73\u65f6\u7ec3\u5175\u7684\u6cd5\u5b50\u7b49\u7b49\u3002 \u6211\u5728\u5bb6\u4e61\u7684\u65f6\u5019\uff0c\u66fe\u7acb\u4e0b\u5fd7\u613f\uff0c\u8981\u628a\u5148\u4eba\u7684\u9057\u4f5c\uff0c\u7f16\u6210\u4e00\u90e8\u5b8c\u6574\u7684\u5175\u4e66\uff0c\u4e0d\u6599\u4e66\u672a\u7f16\u6210\uff0c\u6211 \u5df2\u4e0d\u5e78\u88ab\u4fd8\uff0c\u6d41\u843d\u5f02\u57df\u3002\u6b64\u613f\u803f\u803f\u5fc3\u4e2d\uff0c\u65e0\u65f6\u6216\u5fd8\u3002\u4e0d\u77e5\u6211\u8fd9\u4e00\u90e8\u672a\u66fe\u7f16\u6210\u7684\u5175\u4e66\uff0c\u4f60\u53ef\u6709 \u5e26\u6765\u4e48\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8bf4\u5012\u662f\u8bf4\u5f97\u5bf9\uff0c\u53ef\u60dc\u4eca\u65e5\u7684\u7239\u7239\u5df2\u4e0d\u662f\u5e74\u8f7b\u65f6\u5019\u7684\u7239\u7239\uff0c\u6211\u867d\u7136\u662f \u4f60\u7684\u513f\u5b50\uff0c\u4e5f\u4e0d\u80fd\u76f8\u4fe1\u4f60\u4e86\u3002\u201d \u3000\u3000\u201c\u7239\u7239\u95ee\u7684\u662f\u8fd9\u90e8\u5175\u4e66\u4e48\uff1f\u8fd9\u4ef6\u4e8b\u5988\u5012\u662f\u548c\u6211\u8bf4\u8fc7\uff0c\u53ef\u60dc\u8fd9\u90e8\u4e66\u5374\u662f\u5728\u5175\u8352\u9a6c\u4e71\u4e4b\u4e2d\u5931 \u4e86\u3002\u201d\u674e\u601d\u5357\u7b54\u9053\u3002\u674e\u601d\u5357\u751f\u5e73\u4ece\u6ca1\u6709\u8bf4\u8fc7\u8c0e\uff0c\u60f3\u4e0d\u5230\u7b2c\u4e00\u6b21\u8bf4\u8c0e\u7684\uff0c\u662f\u6b3a\u9a97\u7236\u4eb2\uff0c\u5fc3\u4e2d\u4e0d \u7981\u611f\u5230\u51e0\u5206\u5185\u6127\u3002 \u3000\u3000\u674e\u5e0c\u6d69\u4f55\u7b49\u8001\u7ec3\uff0c\u770b\u51fa\u513f\u5b50\u795e\u8272\u6709\u5f02\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u8981\u5984\u81ea\u63e3\u6d4b\uff0c\u6211\u4e0d\u662f\u60f3\u8981\u628a\u8fd9\u90e8\u5175 \u6cd5\u732e\u7ed9\u5927\u6c57\uff0c\u53ea\u56e0\u8fd9\u662f\u6211\u591a\u5e74\u7684\u5fc3\u8840\uff0c\u6211\u4e0d\u628a\u5b83\u7f16\u6210\uff0c\u8fd9\u5c31\u662f\u6211\u4e00\u751f\u7684\u9057\u61be\u4e86\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u6211\u61c2\u5f97\u7239\u7239\u7684\u5fc3\u4e8b\uff0c\u4f46\u8fd9\u4e00\u90e8\u4e66\u59d4\u5b9e\u662f\u5df2\u7ecf\u5931\u6389\uff0c\u4f60\u53eb\u6211\u54ea\u91cc\u627e\u5f97\u56de\u6765\u7ed9 \u4f60\uff1f\u201d \u3000\u3000\u674e\u5e0c\u6d69\u8fde\u8fde\u51e0\u58f0\u201c\u53ef\u60dc\u201d\uff0c\u63a5\u7740\u53c8\u9053\uff1a\u201c\u4f60\u5988\u77e5\u9053\u8fd9\u662f\u6211\u6700\u5b9d\u8d35\u7684\u4e1c\u897f\uff0c\u600e\u4e48\u4f1a\u8ba9\u5931\u6389 \u7684\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u4f60\u88ab\u4fd8\u4e4b\u540e\uff0c\u63a5\u8fde\u51e0\u5e74\u5175\u8352\u9a6c\u4e71\uff0c\u9003\u96be\u8981\u7d27\uff0c\u5988\u672a\u80fd\u7ed9\u4f60\u4fdd\u5168\uff0c\u4f60\u4e5f\u4e0d\u80fd \u602a\u5979\u3002\u4e0d\u8fc7\uff0c\u4e5f\u8bf4\u4e0d\u5b9a\u5988\u662f\u5fd8\u8bb0\u653e\u5728\u4ec0\u4e48\u5730\u65b9\u4e86\uff0c\u5f85\u54b1\u4eec\u56de\u5bb6\u4e2d\u4e4b\u540e\uff0c\u518d\u4ed4\u7ec6\u5730\u627e\u4e00\u627e\u3002\u201d \u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5982\u679c\u7239\u7239\u613f\u610f\u820d\u5f03\u8363\u534e\uff0c\u548c\u6211\u9003\u56de\u6545\u91cc\u7684\u8bdd\uff0c\u90a3\u65f6\u6211\u81ea\u7136\u4f1a\u628a\u8fd9\u90e8\u5175\u4e66\u2018\u627e\u2019 \u51fa\u6765\u7ed9\u4ed6\u3002\u201d \u3000\u3000\u674e\u5e0c\u6d69\u5c06\u4fe1\u5c06\u7591\uff0c\u4f46\u674e\u601d\u5357\u77e2\u53e3\u8bf4\u662f\u5931\u6389\uff0c\u4ed6\u4e5f\u6ca1\u6709\u529e\u6cd5\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u4e0d\u77e5\u662f\u4e0d\u662f \u8bf4\u8c0e\uff1f\u4e0d\u8fc7\uff0c\u53cd\u6b63\u6709\u7684\u662f\u65f6\u5019\uff0c\u6162\u6162\u518d\u54c4\u4ed6\u8bf4\u51fa\u6765\u3002\u9a97\u4e0d\u51fa\u53e3\u98ce\uff0c\u4e5f\u8fd8\u53ef\u4ee5\u518d\u4ece\u8eab\u4e2d\u641c \u4ed6\u3002\u201d\u5fc3\u610f\u5df2\u5b9a\uff0c\u8bf4\u9053\uff1a\u201c\u65e2\u7136\u5931\u6389\uff0c\u90a3\u4e5f\u65e0\u6cd5\u53ef\u60f3\u3002\u4f60\u4eca\u5929\u7d2f\u4e86\uff0c\u65e9\u70b9\u7761\u5427\u3002\u4e0d\u8981\u4e3a\u4e86\u6b64 \u4e8b\u96be\u8fc7\u3002\u201d \u3000\u3000\u4f46\u674e\u601d\u5357\u5fc3\u4e8b\u91cd\u91cd\uff0c\u8fd9\u4e00\u665a\u53c8\u662f\u8f97\u8f6c\u53cd\u4fa7\uff0c\u4e0d\u80fd\u5165\u5bd0\u3002\u4ed6\u60f3\u7684\u4e0d\u4f46\u662f\u8fd9\u90e8\u5175\u4e66\u7684\u4e8b\u60c5\uff0c \u66f4\u8feb\u5207\u8981\u89e3\u51b3\u7684\u95ee\u9898\u662f\uff1a\u6628\u665a\u90a3\u4e2a\u201c\u523a\u5ba2\u201d\u7559\u5b57\u7ed9\u4ed6\uff0c\u8981\u4ed6\u5230\u963f\u513f\u683c\u5c71\u677e\u98ce\u8c37\u4e2d\u67e5\u8bbf\uff0c\u4ed6\u4e0d \u660e\u767d\u8fd9\u4eba\u8981\u4ed6\u201c\u67e5\u8bbf\u201d\u7684\u662f\u4ec0\u4e48\uff0c\u4f46\u60f3\u6765\u603b\u662f\u548c\u4ed6\u6709\u5f88\u5927\u5173\u7cfb\u4e4b\u4e8b\u3002\u4ed6\u7236\u4eb2\u5c31\u8981\u968f\u519b\u51fa\u53d1 \u4e86\uff0c\u4ed6\u5982\u679c\u4e0d\u636e\u5b9e\u544a\u8bc9\u7239\u7239\uff0c\u53c8\u600e\u80fd\u591f\u79c1\u81ea\u53bb\u677e\u98ce\u8c37\u5462\uff1f\u5982\u679c\u544a\u8bc9\u7239\u7239\uff0c\u5982\u53c8\u8fdd\u80cc\u4e86\u90a3\u4eba\u7684 \u5429\u5490\uff0c\u800c\u4e14\u7ecf\u8fc7\u4e86\u8fd9\u4e24\u5929\u7684\u7236\u5b50\u76f8\u5904\uff0c\u4ed6\u4e5f\u89c9\u5f97\u8fd9\u4ef6\u79d8\u5bc6\u8fd8\u662f\u7792\u4f4f\u7239\u7239\u7684\u597d\u3002 \u3000\u3000\u5c06\u8fd1\u5929\u660e\u7684\u65f6\u5019\uff0c\u674e\u601d\u5357\u624d\u6726\u6726\u80e7\u80e7\u5730\u7761\u53bb\uff0c\u505a\u4e86\u4e00\u4e2a\u6682\u77ed\u7684\u68a6\uff0c\u68a6\u4e2d\u5230\u4e86\u963f\u513f\u683c\u5c71\uff0c \u7f6e\u8eab\u60ac\u5d16\u4e4b\u4e0a\uff0c\u5ffd\u5730\u6709\u4e2a\u4eba\u5728\u80cc\u540e\u63a8\u4ed6\uff0c\u674e\u601d\u5357\u5927\u5403\u4e00\u60ca\uff0c\u56de\u5934\u770b\u65f6\uff0c\u8fd9\u4e2a\u63a8\u4ed6\u7684\u4eba\u7adf\u662f\u4ed6 \u7684\u7236\u4eb2\uff0c\u674e\u601d\u5357\u7ad9\u7acb\u4e0d\u7a33\uff0c\u5760\u4e0b\u6df1\u8c37\uff0c\u4e0d\u7981\u5931\u58f0\u60ca\u547c\uff0c\u4e00\u53eb\u51fa\u58f0\uff0c\u9a6c\u4e0a\u5c31\u9192\u4e86\uff0c\u5e10\u5e55\u5916\u9762\uff0c \u6b64\u65f6\u5374\u6b63\u6709\u4eba\u53eb\u9053\uff1a\u201c\u674e\u516c\u5b50\uff0c\u674e\u516c\u5b50\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u5b9a\u4e86\u5b9a\u795e\uff0c\u63ed\u5f00\u5e10\u5e55\u4e00\u770b\uff0c\u5374\u539f\u6765\u662f\u90a3\u4e2a\u61c2\u5f97\u6c49\u8bed\u7684\u536b\u58eb\u53eb\u4ed6\u3002\u674e\u6069\u5357\u9053\uff1a\u201c\u4ec0 \u4e48\u4e8b\uff1f\u201d\u90a3\u536b\u571f\u9053\uff1a\u201c\u5927\u4eba\u8bf7\u4f60\u9a6c\u4e0a\u8fc7\u53bb\u3002\u201d\u536b\u58eb\u53e3\u4e2d\u7684\u201c\u5927\u4eba\u201d\u5f53\u7136\u5c31\u662f\u4ed6\u7684\u7236\u4eb2\u4e86\u3002 \u3000\u3000\u674e\u601d\u5357\u5306\u5306\u6d17\u8fc7\u4e86\u8138\uff0c\u8fc7\u53bb\u7ed9\u7236\u4eb2\u8bf7\u5b89\u3002\u674e\u5e0c\u6d69\u4e00\u8138\u5174\u594b\u7684\u795e\u60c5\uff0c\u6b63\u5728\u5e10\u4e2d\u8e31\u7740\u65b9\u6b65\uff0c \u4e00\u89c1\u513f\u5b50\u8fdb\u6765\uff0c\u9a6c\u4e0a\u5c31\u8bf4\uff1a\u201c\u4f60\u771f\u662f\u4ea4\u4e86\u597d\u8fd0\u4e86\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u83ab\u540d\u5176\u5999\uff0c\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\u597d\u8fd0\uff1f\u201d \u3000\u3000\u674e\u5e0c\u6d69\u7b11\u9053\uff1a\u201c\u660e\u6167\u516c\u4e3b\u6d3e\u4e86\u4eba\u6765\uff0c\u53eb\u4f60\u4eca\u5929\u53bb\u966a\u5979\u6253\u730e\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u6628\u5929\u6211\u624d\u966a\u5979\u6253\u730e\uff0c\u600e\u7684\u4eca\u5929\u53c8\u8981\u6211\u53bb\uff1f\u201d \u3000\u3000\u674e\u5e0c\u6d69\u7b11\u9053\uff1a\u201c\u8fd9\u4e0d\u6b63\u662f\u597d\u5f97\u5f88\u5417\uff1f\u5979\u4e00\u5929\u4e5f\u79bb\u4e0d\u5f00\u4f60\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u602b\u7136\u4e0d\u60a6\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u662f\u5802\u5802\u4e03\u5c3a\u7684\u5927\u6c49\u7537\u513f\uff0c\u5230\u8499\u53e4\u6765\uff0c\u53ef\u5e76\u4e0d\u662f\u4e3a\u4e86\u8981\u966a\u516c \u4e3b\u73a9\u800d\u3001\u89e3\u95f7\u7684\uff01\u201d \u3000\u3000\u674e\u5e0c\u6d69\u9762\u8272\u4e00\u6c89\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u7684\u725b\u813e\u6c14\u53c8\u6765\u4e86\uff01\u6211\u5bf9\u4f60\u8bf4\u8fc7\u591a\u5c11\u6b21\u4e86\uff0c\u54b1\u4eec\u662f\u5bc4\u4eba\u7bf1 \u4e0b\uff0c\u4e0d\u80fd\u4e0d\u8ba8\u597d\u4eba\u5bb6\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u52a8\u4e86\u6012\u6c14\uff0c\u6b63\u8981\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u4e0d\u53bb\uff01\u201d\u5ffd\u5730\u5fc3\u5ff5\u4e00\u52a8\uff0c\u8bdd\u5230\u53e3\u8fb9\uff0c\u541e\u4e86\u56de\u53bb\u3002 \u3000\u3000\u674e\u5e0c\u6d69\u770b\u51fa\u4e86\u513f\u5b50\u9762\u8272\u7684\u53d8\u5316\uff0c\u67d4\u58f0\u8bf4\u9053\uff1a\u201c\u5357\u513f\uff0c\u4f60\u60f3\u660e\u767d\u4e86\u5427\uff1f\u4f60\u8ba8\u5f97\u516c\u4e3b\u7684\u6b22 \u559c\uff0c\u5c06\u6765\u4f60\u8981\u9003\u8dd1\uff0c\u4e5f\u5bb9\u6613\u5f97\u591a\u554a\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u7239\u7239\u8bf4\u5f97\u4e0d\u9519\uff0c\u5b69\u513f\u613f\u53bb\u4e86\u3002\u4e0d\u8fc7\u2014\u2014\u201d \u3000\u3000\u201c\u4e0d\u8fc7\u4ec0\u4e48\u3002\u201d\u674e\u5e0c\u6d69\u8fde\u5fd9\u53d1\u95ee\u3002 \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u4eca\u5929\u662f\u6211\u5230\u5979\u7684\u5e10\u5e55\u53bb\uff0c\u7136\u540e\u624d\u548c\u5979\u4e00\u540c\u51fa\u53bb\u6253\u730e\u3002\u4e0d\u6bd4\u6628\u5929\u662f\u5076\u7136\u78b0 \u4e0a\u3002\u201d \u3000\u3000\u674e\u5e0c\u6d69\u9053\uff1a\u201c\u8fd9\u53c8\u600e\u6837\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u7167\u54b1\u4eec\u6c49\u4eba\u7684\u4e60\u60ef\uff0c\u7b2c\u4e00\u6b21\u5230\u4eba\u5bb6\u7684\u5bb6\u91cc\uff0c\u4f3c\u4e4e\u5e94\u8be5\u5e26\u70b9\u793c\u7269\u3002\u201d \u3000\u3000\u674e\u5e0c\u6d69\u5927\u4e3a\u9ad8\u5174\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u539f\u6765\u4f60\u662f\u4e3a\u4e86\u793c\u7269\u5fc3\u70e6\u5440\uff01\u5bf9\uff0c\u5bf9\uff0c\u662f\u5e94\u8be5\u9001\u70b9\u4e1c\u897f \u53bb\uff0c\u624d\u663e\u5f97\u54b1\u4eec\u6c49\u4eba\u77e5\u4e66\u8bc6\u793c\u3002\u8fd9\u4e2a\u5bb9\u6613\uff0c\u5bb9\u6613\u2014\u2014\u201d \u3000\u3000\u4ed6\u968f\u5373\u8bf4\u4e86\u4e24\u58f0\u201c\u5bb9\u6613\u201d\u8fd9\u624d\u84e6\u5730\u60f3\u8d77\u8981\u9001\u5f97\u5408\u9002\u7684\u793c\u7269\uff0c\u5374\u8fd8\u771f\u7684\u662f\u5f88\u4e0d\u5bb9\u6613\uff01 \u3000\u3000\u674e\u5e0c\u6d69\u6c89\u541f\u534a\u664c\uff0c\u8bf4\u9053\uff1a\u201c\u660e\u6167\u516c\u4e3b\u662f\u5927\u6c57\u6700\u5ba0\u7231\u7684\u5973\u513f\uff0c\u73cd\u5b9d\u73a9\u7269\u5979\u6709\u7684\u662f\u3002\u5979\u559c\u6b22 \u9a91\u9a6c\u5c04\u7bad\uff0c\u53ef\u60dc\u6025\u5207\u95f4\u53c8\u627e\u4e0d\u5230\u4e00\u5339\u540d\u9a6c\u9001\u7ed9\u5979\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u6211\u5012\u60f3\u8d77\u4e00\u6837\u793c\u7269\uff0c\u9001\u7ed9\u5979\uff0c\u5979\u4e00\u5b9a\u6b22\u559c\u3002\u201d \u3000\u3000\u674e\u5e0c\u6d69\u8fde\u5fd9\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\u4e1c\u897f\uff1f\u4f60\u544a\u8bc9\u6211\uff0c\u6211\u7acb\u5373\u5907\u529e\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u6628\u5929\u6211\u966a\u5979\u6253\u730e\uff0c\u4e0e\u5979\u95f2\u804a\uff0c\u5979\u8bf4\u5979\u6700\u8fd1\u5b66\u4e86\u6c49\u6587\uff0c\u5f88\u611f\u5174\u8da3\u3002\u201d \u3000\u3000\u674e\u5e0c\u6d69\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u4e5f\u66fe\u6559\u8fc7\u5979\u7684\u6c49\u6587\uff0c\u5979\u7684\u786e\u5f88\u7528\u529f\u3002\u4e0d\u8fc7\uff0c\u8fd9\u53ef\u4e0d\u80fd\u5f53\u4f5c\u793c\u7269 \u5440\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u5979\u548c\u6211\u8c08\u8d77\u4e2d\u56fd\u7684\u5b57\u753b\uff01\u5979\u8bf4\u5979\u5f88\u559c\u6b22\u3002\u53ef\u60dc\u5979\u7684\u7239\u7239\u5374\u662f\u4e0d\u6536\u85cf\u5b57\u753b \u7684\uff0c\u5979\u53ea\u89c1\u8fc7\u4e24\u5e45\uff0c\u5f88\u60f3\u591a\u6709\u51e0\u5f20\u5b57\u753b\u3002\u5979\u53c8\u8bf4\u867d\u7136\u5979\u4e0d\u61c2\u5f97\u54b1\u4eec\u6c49\u4eba\u7684\u8bd7\uff0c\u4f46\u5ff5\u8d77\u6765\u5f88\u597d \u542c\uff0c\u867d\u7136\u53ea\u662f\u4e00\u77e5\u534a\u89e3\uff0c\u4e5f\u7740\u8ff7\u4e86\uff01\u201d \u3000\u3000\u674e\u5e0c\u6d69\u9053\uff1a\u201c\u54e6\uff0c\u60f3\u4e0d\u5229\u5979\u8fd9\u6837\u98ce\u7a1a\uff0c\u53ea\u53ef\u60dc\u6211\u4e00\u5f20\u5b57\u753b\u90fd\u6ca1\u6709\u3002\u8499\u53e4\u662f\u6ca1\u6709\u4eba\u505a\u5b57\u753b \u751f\u610f\u7684\uff0c\u6025\u5207\u95f4\u54ea\u91cc\u53bb\u6c42\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u7239\u7239\uff0c\u4f60\u4e0d\u662f\u5728\u5bb6\u4e61\u6559\u8fc7\u8499\u9986\u7684\u5417\uff1f\u4e0d\u4f1a\u753b\u753b\uff0c\u603b\u4f1a\u5199\u5b57\u5440\u3002\u4f60\u5c31\u7ed9\u5979\u5199 \u4e00\u5f20\u6761\u5e45\u5427\uff01\u201d \u3000\u3000\u674e\u5e0c\u6d69\u82e6\u7b11\u9053\uff1a\u201c\u6211\u6709\u4e8c\u5341\u5e74\u6ca1\u52a8\u7b14\u4e86\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u53cd\u6b63\u5979\u4e5f\u4e0d\u662f\u9274\u8d4f\u5b57\u753b\u7684\u884c\u5bb6\uff0c\u9a6c\u9a6c\u864e\u864e\u8fc7\u5f97\u53bb\uff0c\u53ea\u8981\u5979\u89c9\u5f97\u597d\u770b\u5c31 \u884c\u3002\u201d \u3000\u3000\u674e\u5e0c\u6d69\u6c89\u541f\u4e0d\u8bed\uff0c\u674e\u601d\u5357\u53c8\u50ac\u4fc3\u4ed6\u9053\uff1a\u201c\u8fd9\u662f\u4f60\u4eb2\u624b\u5199\u7684\uff0c\u7531\u6211\u9001\u53bb\uff0c\u53ea\u662f\u8fd9\u4efd\u4eba\u60c5\uff0c \u5979\u4e5f\u4f1a\u611f\u6fc0\u7684\u4e86\u3002\u4f55\u51b5\uff0c\u7239\u7239\u4f60\u7684\u5b57\u4e5f\u5199\u5f97\u5e76\u4e0d\u574f\u5440\uff0c\u5988\u8bf4\uff0c\u4ece\u524d\u5728\u5bb6\u4e61\u7684\u65f6\u5019\uff0c\u6bcf\u9022\u8fc7 \u5e74\uff0c\u5de6\u90bb\u53f3\u91cc\u90fd\u662f\u6765\u6c42\u4f60\u7ed9\u4ed6\u4eec\u5199\u6625\u8054\u7684\u3002\u4f60\u5c31\u52c9\u4e3a\u5176\u96be\u5427\uff0c\u6211\u7ed9\u4f60\u78e8\u58a8\u3002\u201d \u3000\u3000\u674e\u5e0c\u6d69\u867d\u7136\u8bf4\u81ea\u5df1\u4e8c\u5341\u5e74\u6ca1\u6709\u201c\u52a8\u7b14\u201d\uff0c\u4f46\u4ed6\u6240\u8bf4\u7684\u201c\u52a8\u7b14\u201d\u7684\u610f\u601d\u662f\u6307\u6b63\u6b63\u7ecf\u7ecf\u5730\u7ed9 \u4eba\u5199\u5b57\uff0c\u5e76\u975e\u65e5\u5e38\u90fd\u4e0d\u201c\u52a8\u7b14\u201d\u7684\u3002\u4ed6\u7684\u5e10\u5e55\u91cc\u5c31\u6446\u8bbe\u6709\u6587\u623f\u56db\u5b9d\uff0c\u6837\u6837\u9f50\u5168\u3002 \u3000\u3000\u674e\u601d\u5357\u4e00\u9762\u78e8\u58a8\uff0c\u4e00\u9762\u8bf4\u9053\uff1a\u201c\u7239\uff0c\u4f60\u5feb\u60f3\u597d\u5199\u4e9b\u4ec0\u4e48\uff1f\u65f6\u5019\u4e0d\u65e9\uff0c\u6211\u53ef\u5f97\u8d77\u5feb\u53bb\u548c\u5979 \u6253\u730e\u4e86\u3002\u201d \u3000\u3000\u674e\u5e0c\u6d69\u65e0\u53ef\u5948\u4f55\uff0c\u53ea\u597d\u63d0\u8d77\u7b14\u6765\uff0c\u8bf4\u9053\uff0c\u201c\u597d\u5427\uff0c\u6211\u5c31\u968f\u4fbf\u5199\u4e00\u9996\u5510\u8bd7\u5427\u3002\u6b64\u9053\u8352\u5e9f\u5df2 \u4e45\uff0c\u53ea\u6015\u5199\u6765\u4e00\u5b9a\u4e0d\u4f3c\u65e7\u65f6\u4e86\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u7ed9\u4ed6\u6276\u7eb8\uff0c\u53ea\u89c1\u4ed6\u5199\u788d\u5f88\u662f\u8c28\u614e\uff0c\u5f53\u771f\u662f\u4e00\u7b14\u4e0d\u82df\uff0c\u800c\u4e14\u6bcf\u5199\u4e00\u4e2a\u5b57\uff0c\u5c31\u505c\u7b14\u60f3 \u4e00\u60f3\u3002\u597d\u50cf\u662f\u5728\u63e3\u6469\u4ec0\u4e48\u7684\u795e\u6c14\u3002\u597d\u5728\u4ed6\u5199\u7684\u53ea\u662f\u4e00\u9996\u4e03\u7edd\uff0c\u53ea\u6709\u4e8c\u5341\u516b\u4e2a\u5b57\uff0c\u7528\u4e0d\u5230\u4e00\u67f1 \u9999\u7684\u65f6\u523b\uff0c\u4e5f\u5c31\u5199\u5b8c\u4e86\u3002 \u3000\u3000\u8fd9\u9996\u7edd\u53e5\u662f\u5510\u671d\u8bd7\u4eba\u738b\u660c\u9f84\u6240\u4f5c\u7684\u56db\u9996\u201c\u4ece\u519b\u884c\u201d\u4e2d\u7684\u4e00\u9996\u3002\u8bd7\u9053\uff1a\u201c\u9752\u6d77\u957f\u4e91\u6697\u96ea \u5c71\uff0c\u5b64\u57ce\u9065\u671b\u7389\u95e8\u5173\uff0c\u9ec4\u6c99\u767e\u6218\u7a7f\u91d1\u7532\uff0c\u4e0d\u7834\u697c\u5170\u8a93\u4e0d\u8fd8\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u7559\u5fc3\u7ec6\u770b\uff0c\u674e\u5e0c\u6d69\u6240\u5199\u7684\u5b57\u8ff9\u548c\u90a3\u672c\u5175\u4e66\u4e0a\u6240\u6279\u6ce8\u7684\u5b57\u8ff9\u5012\u662f\u6709\u51e0\u5206\u76f8\u4f3c\uff0c\u4f46\u53ea \u662f\u201c\u5f62\u4f3c\u201d\u800c\u5df2\uff0c\u5e76\u975e\u201c\u795e\u4f3c\u201d\u3002\u5175\u4e66\u4e0a\u7684\u5b57\u7b14\u6cd5\u521a\u52b2\uff0c\u73b0\u5728\u6240\u5199\u7684\u8fd9\u4e00\u9996\u8bd7\uff0c\u867d\u7136\u523b\u610f\u6c42 \u5176\u201c\u7626\u786c\u201d\uff0c\u4f46\u4ecd\u7136\u63a9\u4e0d\u4e86\u90a3\u51e0\u5206\u67d4\u5a9a\u7684\u5473\u9053\u3002\u674e\u601d\u5357\u4e0d\u7981\u7591\u4e91\u9661\u8d77\uff0c\u6697\u81ea\u5bfb\u601d\uff1a\u201c\u7239\u7239\u867d \u8bf4\u662f\u9694\u4e86\u5341\u5e74\uff0c\u4f46\u7b14\u6cd5\u5982\u6027\u683c\uff0c\u4e0d\u5e94\u6539\u53d8\u5f97\u5982\u662f\u4e4b\u5927\u3002\u201d \u3000\u3000\u674e\u5e0c\u6d69\u63b7\u7b14\u7b11\u9053\uff1a\u201c\u4e45\u4e0d\u7ec3\u5b57\u90fd\u786c\u4e86\uff0c\u4e0d\u8fc7\uff0c\u5b57\u867d\u7136\u5199\u5f97\u4e0d\u597d\uff0c\u8fd9\u9996\u8bd7\u9009\u5f97\u4f3c\u4e4e\u8fd8\u7b97\u5f97 \u4f53\u3002\u4f60\u770b\u600e\u6837\uff1f\u201d \u3000\u3000\u8499\u53e4\u7684\u7586\u57df\u7531\u4e8e\u8fde\u5e74\u5f81\u6218\uff0c\u53d8\u5316\u5f88\u5927\uff0c\u4e0e\u91d1\u56fd\u7684\u754c\u7ebf\u4e5f\u4ece\u65e0\u6b63\u5f0f\u5212\u5206\u3002\u4e0d\u8fc7\uff0c\u5927\u4f53\u8bf4 \u6765\uff0c\u662f\u4ee5\u7389\u95e8\u5173\u4e3a\u754c\u3002\u7389\u95e8\u5173\u5916\uff0c\u5176\u65f6\u5df2\u662f\u8499\u5360\u7684\u52bf\u529b\u8303\u56f4\uff0c\u867d\u7136\u8fd8\u6709\u67d0\u4e9b\u91d1\u56fd\u7684\u5c5e\u90e8\uff0c\u91d1 \u5ef7\u8bbe\u6709\u5b98\u804c\u7559\u5b88\uff0c\u90a3\u4e5f\u53ea\u662f\u865a\u8854\u800c\u5df2\u3002 \u3000\u3000\u91d1\u56fd\u5728\u672a\u4fb5\u5360\u5317\u5b8b\u7684\u9886\u571f\u4e4b\u524d\u6709\u4e00\u4e2a\u519b\u4e8b\u91cd\u9547\u5728\u53e4\u4ee3\u9119\u5584\u56fd\u5883\u5185\uff0c\u9119\u5584\u65e7\u540d\u6a31\u5170\u3002 \u3000\u3000\u738b\u660c\u9f84\u8fd9\u9996\u201c\u4ece\u519b\u884c\u201d\uff0c\u8bd7\u4e2d\u8bf4\u7684\u201c\u5b64\u57ce\u9065\u671b\u7389\u95e8\u5173\u201d\u4e0e\u201c\u4e0d\u7834\u697c\u5170\u8a93\u4e0d\u8fd8\u201d\u8fd9\u4e24\u53e5\uff0c \u8bf4\u7684\u867d\u662f\u5510\u4f26\u5f81\u897f\u57df\u4e4b\u4e8b\uff0c\u4f46\u7528\u4f5c\u4eca\u5929\u8499\u53e4\u201c\u4f10\u91d1\u201d\u4e4b\u7528\uff0c\u4e5f\u8fd8\u53ef\u4ee5\u9002\u5408\u3002\u662f\u4ee5\u674e\u5e0c\u6d69\u7279\u610f \u5199\u8fd9\u9996\u8bd7\uff0c\u5e0c\u671b\u901a\u8fc7\u660e\u6167\u516c\u4e3b\u4e4b\u624b\u51fa\u6765\u8ba8\u597d\u6210\u5409\u601d\u6c57\uff0c\u5199\u4e86\u4e4b\u540e\uff0c\u5f88\u662f\u5f97\u610f\u3002\u5374\u53c8\u6015\u513f\u5b50\u4e0d \u61c2\uff0c\u662f\u4ee5\u95ee\u4ed6\u3002 \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u7239\u7239\u8fd9\u9996\u8bd7\u9009\u5f97\u5f88\u597d\uff0c\u4e0d\u8fc7\u5018\u82e5\u9009\u7684\u662f\u53e6\u5916\u4e00\u9996\uff0c\u5c31\u66f4\u597d\u4e86\u3002\u201d \u3000\u3000\u674e\u5e0c\u6d69\u9053\uff1a\u201c\u54ea\u4e00\u9996\uff1f\u201d\u5fc3\u91cc\u4e0d\u5927\u9ad8\u5174\uff0c\u60f3\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u5012\u662f\u5927\u8a00\u4e0d\u60ed\uff0c\u96be\u9053\u4ed6\u8fd8\u80fd\u591f \u4ece\u5510\u8bd7\u4e2d\u6311\u51fa\u4e00\u9996\u66f4\u9002\u5408\u7684\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u4e5f\u662f\u738b\u660c\u9f84\u5199\u7684\u2018\u4ece\u519b\u884c\u2019\uff0c\u7239\u7239\u9009\u7684\u662f\u7b2c\u4e8c\u9996\uff0c\u6211\u4ee5\u4e3a\u6700\u540e\u90a3\u9996\u66f4 \u597d\u3002\u201d\u4e8e\u662f\u5ff5\u9053\uff1a\u201c\u79e6\u65f6\u660e\u6708\u6c49\u65f6\u5173\uff0c\u4e07\u91cc\u957f\u5f81\u4eba\u672a\u8fd8\uff0c\u4f46\u4f7f\u9f99\u57ce\u98de\u5c06\u5728\uff0c\u4e0d\u6559\u80e1\u9a6c\u6e21\u9634 \u5c71\u3002\u201d \u3000\u3000\u8fd9\u662f\u6b4c\u9882\u6c49\u540d\u5c06\u9632\u5fa1\u5916\u654c\u7684\u3002\u8499\u53e4\u5982\u4eca\u201c\u4f10\u91d1\u201d\u52bf\u5c06\u6e21\u9634\u5c71\u800c\u5165\u4e2d\u539f\u3002\u674e\u601d\u5357\u5ff5\u8fd9\u9996 \u8bd7\uff0c\u81ea\u662f\u628a\u8499\u53e4\u7684\u9a91\u5175\u6bd4\u4f5c\u8bd7\u4e2d\u7684\u201c\u80e1\u9a6c\u201d\u4e86\u3002\u8fd8\u6709\u4e00\u5c42\uff0c\u674e\u5e0c\u6d69\u867d\u662f\u88ab\u4fd8\u800c\u81f3\u8499\u53e4\uff0c\u5e76\u975e \u201c\u957f\u5f81\u201d\uff0c\u4f46\u603b\u662f\u5728\u4e07\u91cc\u4e4b\u5916\u7684\u5f02\u57df\uff0c\u800c\u81f3\u4eca\u4e5f\u8fd8\u662f\u201c\u4eba\u672a\u8fd8\u201d\u3002\u56e0\u6b64\u8fd9\u9996\u8bd7\u4ece\u674e\u601d\u5357\u7684\u53e3 \u4e2d\u5ff5\u51fa\u6765\uff0c\u4e5f\u662f\u5bf9\u7236\u4eb2\u7684\u4e00\u79cd\u8bbd\u55bb\u3002 \u3000\u3000\u674e\u5e0c\u6d69\u4e0d\u89c9\u53d8\u4e86\u9762\u8272\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u61c2\u5f97\u4f60\u7684\u610f\u601d\uff0c\u6211\u662f\u8981\u56de\u53bb\u7684\u3002\u4ed6\u65e5\u82e5\u7136\u8499\u53e4\u4e0e\u5927\u5b8b \u4e3a\u654c\uff0c\u6211\u4e5f\u5e0c\u671b\u80fd\u591f\u505a\u4e00\u4e2a\u2018\u9f99\u57ce\u98de\u5c06\u2019\u3002\u4e0d\u8fc7\uff0c\u8fd9\u90fd\u662f\u4ee5\u540e\u7684\u8bdd\uff0c\u4eca\u65e5\u54b1\u4eec\u8fd8\u662f\u4e0d\u80fd\u5f97\u7f6a \u8499\u53e4\u4eba\u7684\u3002\u8fd8\u662f\u7528\u6211\u5199\u7684\u8fd9\u9996\u8bd7\u597d\uff0c\u4f60\u61c2\u4e0d\u61c2\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u7239\u7239\u7684\u610f\u601d\uff0c\u5b69\u513f\u61c2\u5f97\u3002\u6211\u4f1a\u628a\u4f60\u60f3\u8981\u8868\u8fbe\u7684\u610f\u601d\uff0c\u85c9\u8fd9\u9996\u8bd7\u5411\u516c\u4e3b\u8bb2\u89e3 \u7684\u3002\u201d \u3000\u3000\u674e\u5e0c\u6d69\u677e\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u4f60\u660e\u767d\u5c31\u597d\u3002\u90a3\u4f60\u8d76\u5feb\u6362\u8863\u88f3\u53bb\u5427\uff0c\u516c\u4e3b\u6050\u6015\u7b49\u5f97\u4e0d\u8010 \u70e6\u4e86\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u56de\u5230\u81ea\u5df1\u90a3\u5ea7\u7bf7\u5e10\uff0c\u6084\u6084\u628a\u7236\u4eb2\u6ce8\u91ca\u7684\u90a3\u672c\u5175\u4e66\u62ff\u51fa\u6765\uff0c\u548c\u674e\u5e0c\u6d69\u5219\u624d\u6240\u5199\u7684\u5b57 \u5bf9\u7167\u7740\u770b\uff0c\u8d8a\u770b\u8d8a\u89c9\u5f97\u7b14\u8ff9\u4e0d\u540c\u3002 \u3000\u3000\u674e\u601d\u5357\u6536\u4e86\u5175\u4e66\uff0c\u7591\u5fc3\u5927\u8d77\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u662f\u56e0\u4e3a\u9694\u4e86\u4e8c\u5341\u5e74\u800c\u81f4\u7b14\u8ff9\u4e0d\u540c\u5462\uff1f\u8fd8\u662f\u2014 \u2014\u201d\u4ed6\u5f00\u59cb\u6000\u7591\u8fd9\u4e2a\u674e\u5e0c\u6d69\u4e0d\u662f\u4ed6\u7684\u7236\u4eb2\u4e86\u3002\u53ef\u662f\uff0c\u201c\u4ed6\u82e5\u7136\u4e0d\u662f\u6211\u7684\u7239\u7239\uff0c\u53c8\u4f55\u4ee5\u77e5\u9053\u6211 \u7684\u751f\u8fb0\u516b\u5b57\uff1f\u7239\u7239\u6ce8\u91ca\u7684\u8fd9\u672c\u5175\u4e66\uff0c\u66f4\u662f\u4e00\u4e2a\u79d8\u5bc6\uff0c\u9664\u4e86\u6211\u4eec\u6bcd\u5b50\u4e4b\u5916\uff0c\u518d\u6ca1\u6709\u7b2c\u4e09\u4e2a\u4eba\u77e5 \u9053\u7684\u3002\u96be\u9053\u7239\u7239\u4f1a\u968f\u4fbf\u544a\u8bc9\u5916\u4eba\u4e48\uff1f\u201d\u6709\u8fd9\u4e24\u5c42\u539f\u56e0\uff0c\u56e0\u6b64\u4ed6\u867d\u6709\u6000\u7591\uff0c\u5374\u4ecd\u7136\u4e0d\u6562\u65ad\u5b9a\u8fd9 \u4e2a\u674e\u5e0c\u6d69\u5c31\u662f\u5047\u5192\u3002 \u3000\u3000\u4ed6\u84e6\u5730\u60f3\u8d77\u8499\u9762\u4eba\u7684\u7559\u5b57\uff0c\u90a3\u5f20\u5b57\u6761\u867d\u7136\u5f53\u65f6\u5c31\u70e7\u6bc1\u4e86\uff0c\u90a3\u4e24\u53e5\u8bdd\u4ed6\u8fd8\u662f\u7262\u7262\u8bb0\u5728\u5fc3\u4e2d \u7684\uff1a\u201c\u6b32\u91ca\u7591\u56e2\uff0c\u53ef\u5230\u963f\u513f\u683c\u5c71\u677e\u51e4\u8c37\u4e2d\u67e5\u8bbf\u3002\u201d\u4ed6\u4e00\u76f4\u4e3a\u8fd9\u4e24\u53e5\u83ab\u540d\u5176\u5999\u7684\u8bdd\u611f\u5230\u60f6\u60d1\uff1a \u201c\u6211\u6709\u4ec0\u4e48\u7591\u56e2\uff1f\u90a3\u4eba\u53c8\u600e\u77e5\u9053\u6211\u6709\u7591\u56e2\uff1f\u8981\u6211\u5230\u677e\u98ce\u8c37\u4e2d\u67e5\u8bbf\u4ec0\u4e48\uff1f\u201d\u73b0\u5728\u60f3\u4e86\u8d77\u6765\uff0c\u5ffd \u5730\u5982\u6709\u6240\u609f\uff1a\u201c\u6211\u5982\u4eca\u4e0d\u662f\u6b63\u6709\u7740\u7591\u56e2\u5417\uff1f\u53ea\u6015\u6211\u4eca\u5929\u6240\u8d77\u7684\u6000\u7591\uff0c\u65e9\u5df2\u662f\u5728\u90a3\u4eba\u610f\u6599\u4e4b\u4e2d \u4e86\u3002\u4e0d\u9519\uff0c\u6211\u662f\u5e94\u8be5\u5230\u677e\u98ce\u8c37\u4e2d\u67e5\u8bbf\u660e\u767d\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u5306\u5306\u6362\u4e86\u8863\u88f3\uff0c\u8d70\u51fa\u5e10\u5e55\uff0c\u53ea\u89c1\u4ed6\u7684\u7236\u4eb2\u65e9\u5df2\u5dee\u9063\u536b\u58eb\u628a\u4ed6\u90a3\u5339\u5750\u9a91\u7275\u6765\uff0c\u5728\u5916 \u9762\u7b49\u7740\u4ed6\u4e86\u3002 \u3000\u3000\u674e\u5e0c\u6d69\u8bf4\u9053\uff1a\u201c\u516c\u4e3b\u53ea\u662f\u8bf7\u4f60\u53bb\u966a\u5979\u6253\u730e\uff0c\u6211\u53ef\u4e0d\u65b9\u4fbf\u53eb\u536b\u58eb\u548c\u4f60\u540c\u53bb\u3002\u201d\u4e8e\u662f\u628a\u660e\u6167 \u516c\u4e3b\u90a3\u5ea7\u5e10\u5e55\u7684\u6240\u5728\u5730\u544a\u8bc9\u674e\u601d\u5357\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u81ea\u5df1\u53bb\u5427\uff0c\u82e5\u4e0d\u719f\u8def\uff0c\u968f\u4fbf\u95ee\u4e00\u4e2a\u4eba\u90fd\u4f1a\u77e5 \u9053\u7684\u3002\u201d\u674e\u601d\u5357\u6b63\u662f\u62c5\u5fe7\u536b\u58eb\u968f\u884c\uff0c\u542c\u4e86\u8fd9\u8bdd\uff0c\u5fc3\u91cc\u7684\u4e00\u5757\u77f3\u5934\u843d\u4e0b\u4e86\u5730\u3002 \u3000\u3000\u674e\u601d\u5357\u8de8\u4e0a\u5750\u9a91\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4eca\u665a\u4e0d\u5b9a\u4ec0\u4e48\u65f6\u5019\u56de\u6765\uff0c\u4f60\u4eec\u4e0d\u5fc5\u7b49\u6211\u5403\u665a\u996d\u4e86\u3002\u201d \u3000\u3000\u674e\u5e0c\u6d69\u7b11\u9053\uff1a\u201c\u4f60\u660e\u5927\u56de\u6765\u4e5f\u4e0d\u8981\u7d27\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u5feb\u9a6c\u75be\u9a70\uff0c\u4e0d\u662f\u8dd1\u53bb\u516c\u4e3b\u7684\u5e10\u5e55\u800c\u662f\u8dd1\u51fa\u548c\u6797\u3002\u548c\u6797\u662f\u8499\u53e4\u7684\u201c\u884c\u90fd\u201d\uff0c\u5f53\u65f6\u867d \u7136\u8fd8\u6ca1\u6709\u9ad8\u57ce\u6df1\u6c60\u7684\u5efa\u7b51\uff0c\u4f46\u5728\u9698\u53e3\u4e4b\u5904\uff0c\u4e5f\u6709\u5b98\u5175\u628a\u5b88\uff0c\u76d8\u67e5\u4ed6\u4eec\u8ba4\u4e3a\u53ef\u7591\u7684\u6765\u5f80\u884c\u4eba\uff0c \u5c24\u5176\u5bf9\u4e8e\u6c49\u4eba\u800d\u662f\u9632\u5f97\u4e25\u5bc6\u3002 \u3000\u3000\u4e8b\u6709\u51d1\u5de7\uff0c\u8fd9\u65e5\u5728\u9698\u53e3\u628a\u5b88\u9e4f\u5b98\u662f\u6628\u5929\u66fe\u53c2\u52a0\u6210\u5409\u601d\u6c57\u7684\u72e9\u730e\u7684\uff0c\u4ed6\u66fe\u4eb2\u773c\u89c1\u5230\u660e\u6167\u516c \u4e3b\u548c\u674e\u601d\u5357\u5e76\u8f94\u51fa\u730e\uff0c\u4e5f\u66fe\u4eb2\u773c\u89c1\u5230\u6210\u5409\u601d\u6c57\u628a\u5fa1\u7bad\u8d50\u7ed9\u674e\u601d\u5357\uff0c\u5e76\u8981\u4ed6\u505a\u201c\u91d1\u5e10\u6b66\u58eb\u201d\u3002 \u3000\u3000\u674e\u601d\u5357\u5927\u6a21\u5927\u6837\u5730\u8bf4\u9053\uff1a\u201c\u660e\u6167\u516c\u4e3b\u7ea6\u6211\u5230\u5317\u5c71\u6253\u732a\uff0c\u6211\u6765\u4e0d\u53ca\u5230\u5979\u7684\u5e10\u5e55\u548c\u5979\u4f1a\u5408\uff0c \u53ea\u597d\u5404\u76ee\u8d76\u53bb\u4e86\u3002\u4f60\u53ef\u6709\u89c1\u7740\u516c\u4e3b\u4e48\uff1f\u201d\u5f80\u5317\u5c71\u6253\u730e\uff0c\u6709\u51e0\u6761\u8def\u53ef\u8d70\uff0c\u8fd9\u4e2a\u9698\u53e3\u5219\u662f\u8ddd\u79bb\u516c \u4e3b\u7684\u4f4f\u5740\u8f83\u8fdc\u7684\u3002 \u3000\u3000\u628a\u5b88\u9698\u53e3\u7684\u90a3\u4e2a\u519b\u5b98\u8eac\u8170\u8bf4\u9053\uff1a\u201c\u4e09\u516c\u4e3b\u60f3\u5fc5\u4ece\u53e6\u4e00\u6761\u8def\u53bb\u4e86\u3002\u5c0f\u7684\u6ca1\u6709\u89c1\u5230\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4e48\u62dc\u6258\u4f60\u4ee3\u4e3a\u7559\u5fc3\uff0c\u5982\u679c\u4e09\u516c\u4e3b\u4ece\u8fd9\u91cc\u6765\uff0c\u8bf7\u4f60\u544a\u8bc9\u5979\uff0c\u6211\u5148\u8d70 \u4e86\u3002\u201d\u90a3\u519b\u5b98\u8fed\u58f0\u8bf4\u9053\uff1a\u201c\u662f\uff0c\u662f\uff01\u201d\u606d\u606d\u656c\u656c\u5730\u9001\u674e\u601d\u5357\u51fa\u4e86\u9698\u53e3\u3002 \u3000\u3000\u674e\u601d\u5357\u51fa\u4e86\u9698\u53e3\u5feb\u9a6c\u52a0\u97ad\uff0c\u517c\u7a0b\u8d76\u8def\u3002\u674e\u601d\u5357\u7a7f\u7684\u662f\u201c\u91d1\u5e10\u6b66\u58eb\u201d\u7684\u670d\u9970\uff0c\u9a6c\u4e0a\u53c8\u6813\u6709 \u6210\u5409\u601d\u6c57\u6240\u8e22\u7684\u5fa1\u5f13\uff0c\u8fd9\u5f20\u7279\u5927\u53f7\u7684\u94c1\u80ce\u5f13\uff0c\u8499\u53e4\u7684\u9ad8\u7ea7\u519b\u5b98\u90fd\u662f\u8ba4\u5f97\u7684\u3002\u662f\u4ee5\u4ed6\u5728\u8def\u4e0a\u867d \u7136\u4e5f\u66fe\u9047\u4e0a\u8499\u53e4\u51fa\u5f81\u7684\u5b98\u5175\uff0c\u5374\u662f\u65e0\u4eba\u6562\u5411\u4ed6\u76d8\u95ee\u3002 \u3000\u3000\u7b2c\u4e09\u65e5\u5df2\u5230\u4e86\u5e93\u4f26\u6c60\u5317\u7684\u8349\u539f\u5730\u5e26\uff0c\u8349\u539f\u4e0a\u5730\u65f7\u4eba\u7a00\uff0c\u5f80\u5f80\u8d70\u4e0a\u4e8c\u4e09\u5341\u91cc\uff0c\u624d\u78b0\u89c1\u4e00\u7fa4 \u7267\u4eba\uff0c\u5175\u58eb\u5219\u662f\u6ca1\u6709\u9047\u4e0a\u4e86\u3002\u674e\u601d\u5357\u8fdb\u4e86\u8349\u539f\uff0c\u5fc3\u60c5\u8f7b\u677e\u4e86\u8bb8\u591a\uff0c\u60f3\u9053\uff1a\u201c\u6210\u5409\u6069\u6c57\u5fd9\u4e8e\u8c03 \u5175\u9063\u5c06\uff0c\u8ff8\u72af\u4e2d\u539f\uff0c\u60f3\u6765\u65e0\u6687\u7406\u4f1a\u6211\u8fd9\u70b9\u5c0f\u4e8b\u3002\u5373\u4f7f\u7239\u7239\u8bf7\u5f97\u52a8\u6728\u534e\u9ece\u6d3e\u4eba\u8ffd\u6211\uff0c\u4ed6\u4eec\u4e5f\u672a \u5fc5\u77e5\u9053\u6211\u662f\u9003\u5411\u4f55\u65b9\u3002\u201d \u3000\u3000\u963f\u513f\u683c\u5c71\u5728\u5e93\u4f26\u6c60\u5317\u4e09\u767e\u91cc\u5916\uff0c\u8fd9\u662f\u674e\u601d\u5357\u65e9\u5df2\u77e5\u9053\u4e86\u7684\uff0c\u4f46\u677e\u98ce\u8c37\u662f\u5728\u5c71\u4e2d\u4f55\u5904\uff0c\u674e \u601d\u5357\u5374\u4e0d\u77e5\u9053\u3002\u4ed6\u6cbf\u9014\u5411\u7267\u4eba\u6253\u542c\uff0c\u7267\u4eba\u90fd\u4e0d\u77e5\u9053\u6709\u8fd9\u4e2a\u5730\u540d\u3002\u674e\u601d\u5357\u5fc3\u60f3\uff1a\u201c\u5c71\u4e2d\u5927\u7ea6\u603b \u4f1a\u6709\u730e\u4eba\u5427\uff1f\u5230\u4e86\u5c71\u91cc\u518d\u5411\u730e\u4eba\u6253\u542c\u4e5f\u8fd8\u4e0d\u8fdf\u3002\u201d \u3000\u3000\u7b2c\u56db\u65e5\u8fc7\u4e86\u8349\u539f\u5730\u5e26\uff0c\u8fdb\u5165\u5c71\u533a\uff0c\u5c71\u8def\u5d0e\u5c96\uff0c\u8d8a\u6765\u8d8a\u662f\u96be\u8d70\u3002\u8fd9\u4e00\u5929\u4e0d\u8fc7\u8d70\u4e86\u4e00\u767e\u591a \u91cc\uff0c\u4eba\u9a6c\u4ff1\u662f\u75b2\u7d2f\u4e0d\u582a\u3002 \u3000\u3000\u7b2c\u4e94\u65e5\u5230\u4e86\u963f\u513f\u683c\u5c71\u5c71\u811a\uff0c\u53ea\u89c1\u5c42\u5ce6\u53e0\u5d82\uff0c\u9ad8\u53ef\u77d7\u5929\uff0c\u9a91\u7740\u9a6c\u662f\u8dd1\u4e0d\u4e0a\u53bb\u7684\u4e86\u3002\u674e\u601d\u5357 \u7ed5\u7740\u5c71\u811a\u7b56\u9a6c\u7f13\u884c\uff0c\u597d\u4e0d\u5bb9\u6613\u624d\u627e\u5230\u4e00\u6761\u5165\u53e3\uff0c\u8c37\u53e3\u5f62\u5982\u5587\u53ed\uff0c\u4ec5\u5bb9\u4e00\u4eba\u4e00\u9a91\u901a\u8fc7\uff0c\u8fdb\u4e86\u5c71 \u8c37\uff0c\u9010\u6e10\u5f00\u6717\uff0c\u53ea\u89c1\u91ce\u8349\u9ad8\u903e\u4eba\u5934\uff0c\u5c71\u6eaa\u5982\u8109\u7edc\u822c\u5728\u8c37\u5e95\u6df7\u6dc6\u6d41\u8fc7\u3002\u674e\u601d\u5357\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9 \u91cc\u5012\u662f\u4e2a\u6c34\u8349\u80a5\u7f8e\u4e4b\u5730\uff0c\u53ef\u60dc\u6ca1\u6709\u57a6\u8352\u3002\u201d \u3000\u3000\u5fc3\u5ff5\u672a\u5df2\uff0c\u5ffd\u89c1\u5c71\u5761\u4e0a\u6709\u70e7\u7126\u7684\u91ce\u8349\uff0c\u674e\u601d\u5357\u7ec6\u5fc3\u89c2\u5bdf\uff0c\u6ce5\u571f\u4e5f\u6709\u7ffb\u8fc7\u7684\u8ff9\u8c61\u3002\u674e\u601d\u5357 \u662f\u5728\u519c\u6751\u957f\u5927\u7684\u5b69\u5b50\uff0c\u4e00\u89c1\u4fbf\u77e5\u8fd9\u662f\u6709\u4eba\u5f00\u57a6\u8fc7\u7684\u68af\u7530\uff0c\u5927\u7ea6\u56e0\u4e3a\u5f00\u8352\u6ca1\u6709\u6210\u529f\uff0c\u534a\u9014\u800c \u5e9f\u3002\u674e\u601d\u5357\u597d\u751f\u6b22\u559c\uff0c\u5bfb\u601d\uff1a\u201c\u65e2\u7136\u6709\u4eba\u66fe\u7ecf\u5728\u8fd9\u91cc\u5f00\u8352\u8fc7\uff0c\u5c71\u4e2d\u5b9a\u7136\u4f4f\u6709\u4eba\u5bb6\uff01\u201d \u3000\u3000\u4e0d\u6599\u5728\u8c37\u5e95\u8d70\u4e86\u5341\u591a\u91cc\uff0c\u4e00\u4e2a\u4eba\u4e5f\u6ca1\u9047\u4e0a\u3002\u4e24\u65c1\u7684\u6811\u6728\uff0c\u5927\u90fd\u662f\u6986\u6811\u548c\u5176\u4ed6\u6742\u6728\uff0c\u201c\u5982 \u679c\u5230\u4e86\u677e\u98ce\u8c37\u7684\u8bdd\uff0c\u4e24\u65c1\u4e00\u5b9a\u662f\u677e\u6797\u7684\u3002\u201d\u674e\u601d\u5357\u5fc3\u60f3\u3002 \u3000\u3000\u963f\u513f\u683c\u5c71\u7ef5\u5ef6\u6570\u767e\u91cc\uff0c\u4e5f\u8bb8\u677e\u98ce\u8c37\u662f\u5728\u91cd\u5ce6\u53e0\u5d0e\u4e4b\u95f4\uff0c\u4f46\u65e0\u4eba\u53ef\u4ee5\u6253\u542c\uff0c\u8981\u627e\u5230\u677e\u98ce\u8c37 \u90a3\u5c31\u65e0\u6b8a\u6d77\u5e95\u635e\u9488\u3002 \u3000\u3000\u674e\u601d\u5357\u6b63\u81ea\u611f\u5230\u5fc3\u7126\uff0c\u5ffd\u542c\u5f97\u9a6c\u94c3\u58f0\u54cd\uff0c\u674e\u601d\u5357\u4ee5\u4e3a\u662f\u5c71\u4e2d\u7684\u730e\u4eba\uff0c\u5927\u559c\u53eb\u9053\uff1a\u201c\u5582\uff0c \u4f60\u77e5\u9053\u677e\u98ce\u8c37\u5728\u2026\u2026\u201d\u56de\u5934\u4e00\u671b\uff0c\u5927\u559c\u53d8\u4e3a\u5927\u60ca\uff0c\u672a\u66fe\u8bf4\u51fa\u7684\u8bdd\u767b\u65f6\u5c31\u4f3c\u88ab\u51b7\u7a7a\u6c14\u51dd\u7ed3\u4e86\u3002 \u3000\u3000\u9a91\u9a6c\u8ffd\u5165\u8c37\u4e2d\u7684\u662f\u4e00\u4e2a\u8eab\u6750\u9b41\u4f1f\u7684\u8499\u53e4\u6b66\u58eb\uff0c\u674e\u601d\u5357\u5728\u53c2\u52a0\u72e9\u730e\u90a3\u65e5\u66fe\u7ecf\u89c1\u8fc7\u6b64\u4eba\uff0c\u77e5 \u9053\u4ed6\u662f\u201c\u91d1\u5e10\u6b66\u58eb\u201d\u4e2d\u540d\u5217\u7b2c\u4e8c\u7684\u795e\u7bad\u624b\u54f2\u522b\u3002\u7bad\u6cd5\u4e4b\u7cbe\uff0c\u4e0d\u5728\u6210\u5409\u601d\u6c57\u4e4b\u4e0b\u3002 \u3000\u3000\u54f2\u522b\u98de\u9a91\u8d76\u81f3\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u674e\u516c\u5b50\uff0c\u4e09\u516c\u4e3b\u7ea6\u4f60\u6253\u730e\u4f60\u4e0d\u53bb\uff0c\u5374\u5230\u8fd9\u8352\u5c71\u6765\u505a\u4ec0 \u4e48\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u6211\u53c8\u6ca1\u6709\u5411\u4f60\u4eec\u5356\u8eab\u6295\u9760\uff0c\u559c\u6b22\u5230\u54ea\u513f\u4fbf\u5230\u54ea\u513f\uff0c\u4f60\u7ba1\u5f97\u7740\u6211\u4e48\u3002\u201d \u3000\u3000\u54f2\u522b\u7b11\u5bb9\u4e00\u655b\uff0c\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u522b\u5fd8\u4e86\u4f60\u7684\u7236\u4eb2\u662f\u5728\u7ed9\u6211\u4eec\u7684\u5927\u6c57\u5f53\u5dee\uff0c\u6211\u5949\u4e86\u5927\u6c57\u4e4b \u547d\u517c\u53d7\u4ee4\u5c0a\u4e4b\u6258\u2018\u8bf7\u2019\u4f60\u56de\u53bb\uff01\u6b63\u7ba1\u5f97\u7740\u4f60\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u7684\u5750\u9a91\u867d\u662f\u9a8f\u9a6c\uff0c\u4f46\u5954\u9a70\u6570\u65e5\uff0c\u9a6c\u529b\u5df2\u4e4f\u3002\u54f2\u522b\u5219\u662f\u6bcf\u5230\u4e00\u4e2a\u9a7f\u7ad9\u5c31\u6362\u4e00\u6b21\u5750\u9a91 \u7684\uff0c\u56e0\u6b64\u674e\u601d\u5357\u867d\u7136\u5feb\u9a6c\u52a0\u97ad\uff0c\u4e24\u4eba\u7684\u8ddd\u79bb\u5374\u662f\u8d8a\u6765\u8d8a\u8fd1\u3002 \u3000\u3000\u674e\u601d\u5357\u559d\u9053\uff1a\u201c\u6211\u662f\u8a93\u6b7b\u4e0d\u56de\u548c\u6797\u7684\u4e86\uff0c\u4f60\u82e6\u82e6\u76f8\u903c\uff0c\u6211\u552f\u6709\u4e0e\u4f60\u4e00\u62fc\uff01\u201d\u62e8\u8f6c\u9a6c\u5934\uff0c \u76d8\u9a6c\u5f2f\u5f13\uff0c\u201c\u5530\u201d\u7684\u4e00\u7bad\u5c04\u51fa\u3002 \u3000\u3000\u54f2\u522b\u54c8\u54c8\u5927\u7b11\uff0c\u201c\u4f60\u8981\u4e0e\u6211\u6bd4\u7bad\uff1f\u597d\uff0c\u6211\u8ba9\u4f60\u4e09\u7bad\uff01\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u674e\u601d\u5357\u7684\u90a3\u679d\u7bad\u5df2\u662f \u5c04\u4e86\u5230\u6765\uff0c\u54f2\u522b\u4e3e\u5f13\u4e00\u62e8\uff0c\u628a\u8fd9\u679d\u7bad\u6253\u843d\u3002 \u3000\u3000\u674e\u601d\u5357\u65e0\u610f\u8981\u4ed6\u76f8\u8ba9\uff0c\u4f46\u674e\u601d\u5357\u5c04\u7684\u662f\u8fde\u73e0\u7bad\uff0c\u7bad\u5728\u5f26\u4e0a\uff0c\u5374\u662f\u4e0d\u5f97\u4e0d\u53d1\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3 \u65f6\u5feb\uff0c\u54f2\u522b\u521a\u521a\u6253\u843d\u4ed6\u7684\u7b2c\u4e00\u679d\u7bad\uff0c\u7b2c\u4e8c\u679d\u7bad\u53c8\u5df2\u662f\u6d41\u661f\u95ea\u7535\u822c\u5c04\u5230\u3002 \u3000\u3000\u674e\u601d\u5357\u7528\u7684\u662f\u6210\u5409\u601d\u6c57\u7684\u94c1\u80ce\u5f13\uff0c\u5f53\u771f\u662f\u5f3a\u5f13\u731b\u7bad\uff0c\u52b2\u529b\u5947\u5927\u3002\u54f2\u522b\u4ecd\u7136\u7528\u4ed6\u7684\u94c1\u80ce\u5f13 \u62e8\u7bad\uff0c\u53ea\u542c\u5f97\u201c\u5530\u201d\u7684\u4e00\u58f0\uff0c\u7bad\u867d\u6253\u843d\uff0c\u54f2\u522b\u7684\u8fd9\u5f20\u5927\u5f13\uff0c\u5374\u5df2\u7ed9\u8fd9\u4e00\u679d\u7bad\u5f53\u4e2d\u5288\u5f00\u3002 \u3000\u3000\u54f2\u522b\u53eb\u9053\uff1a\u201c\u597d\u7bad\u6cd5\uff01\u201d\u53e3\u672a\u5408\u62e2\uff0c\u7b2c\u4e09\u679d\u7bad\u53c8\u5230\u3002\u8fd9\u4e00\u7bad\u7adf\u7136\u5c04\u5165\u4e86\u54f2\u522b\u7684\u53e3\u4e2d\uff0c\u54f2 \u522b\u7c97\u58ee\u7684\u8eab\u8eaf\u767b\u65f6\u5012\u4e0b\uff0c\u4fef\u5367\u9a6c\u80cc\u3002 \u3000\u3000\u674e\u601d\u5357\u53ea\u9053\u5df2\u7ecf\u5c04\u6b7b\u4e86\u4ed6\uff0c\u8bf4\u9053\uff1a\u201c\u8c01\u53eb\u4f60\u82e6\u82e6\u76f8\u903c\uff0c\u8fd9\u53ef\u6028\u4e0d\u5f97\u6211\uff01\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u54f2 \u522b\u7a81\u7136\u5750\u8d77\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8fd9\u556e\u7c07\u6cd5\u4f60\u8fd8\u6ca1\u6709\u5b66\u8fc7\u5427\uff1f\u201d\u674e\u601d\u5357\u5c04\u8fdb\u4ed6\u53e3\u4e2d\u7684\u90a3\u679d\u7bad\uff0c\u4ed6\u65e9 \u5df2\u5410\u51fa\uff0c\u62ff\u5728\u624b\u4e0a\u4e86\u3002 \u3000\u3000\u5f13\u529b\u5982\u6b64\u5f3a\u52b2\u5c04\u51fa\u7684\u7bad\uff0c\u54f2\u522b\u5c45\u7136\u6562\u7528\u7259\u9f7f\u54ac\u7740\u7bad\u5934\uff0c\u800c\u4e14\u4e0d\u53d7\u635f\u4f24\uff0c\u4ed6\u8fd9\u201c\u556e\u7c07\u6cd5\u201d \u5f53\u771f\u662f\u795e\u4e4e\u5176\u6280\uff0c\u4e3e\u4e16\u65e0\u53cc\u3002\u674e\u601d\u5357\u5927\u5403\u4e00\u60ca\uff0c\u8fd9\u624d\u77e5\u9053\u54f2\u522b\u7684\u201c\u795e\u7bad\u624b\u201d\u786e\u662f\u540d\u4e0d\u865a\u4f20\uff0c \u5c1a\u5728\u4ed6\u4f30\u8ba1\u4e4b\u4e0a\u3002 \u3000\u3000\u54f2\u522b\u7b11\u9053\uff1a\u201c\u4f60\u7528\u7684\u5f13\u7bad\u8fdc\u80dc\u4e8e\u6211\uff0c\u81f3\u4e8e\u7bad\u6cd5\u561b\uff0c\u867d\u4e5f\u4e0d\u9519\uff0c\u5374\u672a\u5fc5\u5c31\u80dc\u5f97\u6211\u4e86\uff01\u8fd9\u7bad \u662f\u5927\u6c57\u8d50\u7ed9\u4f60\u7684\uff0c\u6211\u4e0d\u6562\u8981\uff0c\u8fd8\u7ed9\u4f60\u5427\u3002\u201d\u4ed6\u4e0d\u7528\u5f13\u5f26\uff0c\u968f\u624b\u5c31\u4ee5\u201c\u7529\u7bad\u6cd5\u201d\u5f39\u51fa\uff0c\u52b2\u9053\u4e4b \u5f3a\u7adf\u7136\u4e0d\u4e9a\u4e8e\u7528\u5f13\u53d1\u5c04\u3002\u674e\u601d\u5357\u9aa4\u5403\u4e00\u60ca\uff0c\u53ea\u987e\u62a4\u8eab\uff0c\u60f3\u4e0d\u5230\u8fd9\u679d\u7bad\u5374\u662f\u5c04\u4ed6\u7684\u9a6c\u3002\u201c\u535c\u201d \u7684\u4e00\u58f0\uff0c\u7bad\u7a7f\u9a6c\u8179\uff0c\u767b\u65f6\u628a\u674e\u601d\u5357\u629b\u843d\u3002 \u3000\u3000\u674e\u601d\u5357\u8dd1\u4e0a\u5c71\u5c97\uff0c\u5c45\u9ad8\u4e34\u4e0b\uff0c\u62a2\u5148\u5360\u4e86\u6709\u5229\u7684\u4f4d\u7f6e\u3002\u54f2\u522b\u7ffb\u8eab\u4e0b\u9a6c\uff0c\u7d27\u8ddf\u7740\u8dd1\u4e0a\u5c71\u5c97\uff0c \u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u7684\u7bad\u6cd5\u6211\u89c1\u8bc6\u8fc7\u4e86\uff0c\u597d\uff0c\u73b0\u5728\u518d\u9886\u6559\u9886\u6559\u4f60\u4eec\u6c49\u4eba\u7684\u5251\u6cd5\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u5c45\u9ad8\u4e34\u4e0b\uff0c\u5530\u5730\u4e00\u5251\u523a\u4e0b\u53bb\u3002\u54f2\u522b\u4e3e\u8d77\u76fe\u724c\u4e00\u6321\uff0c\u53f3\u624b\u7684\u957f\u5200\u659c\u5288\u4e0a\u6765\u3002\u5200\u5251\u76f8 \u4ea4\uff0c\u674e\u601d\u5357\u6c14\u529b\u4e0d\u53ca\u4ed6\u5927\uff0c\u7ed9\u4ed6\u7528\u529b\u4e00\u63a8\uff0c\u4e0d\u7531\u5f97\u5012\u9000\u4e00\u6b65\u3002 \u3000\u3000\u674e\u601d\u5357\u6025\u6025\u6362\u62db\uff0c\u62bd\u5251\u8fdb\u5251\uff0c\u523a\u4ed6\u8111\u95e8\u7684\u201c\u767e\u4f1a\u7a74\u201d\u3002\u8fd9\u4e00\u62db\u201c\u9e4f\u640f\u4e5d\u9704\u201d\u672c\u6765\u662f\u8fbe\u6469 \u5251\u6cd5\u4e2d\u4e00\u62db\u6781\u5389\u5bb3\u7684\u6740\u624b\uff0c\u4f46\u54f2\u522b\u4ecd\u7136\u662f\u7528\u521a\u624d\u7684\u6cd5\u5b50\u5bf9\u4ed8\u4ed6\uff0c\u76fe\u724c\u9ad8\u4e3e\uff0c\u62a4\u7740\u9876\u95e8\uff0c\u957f\u5200 \u5288\u51fa\uff0c\u5200\u6cd5\u770b\u6765\u7b28\u62d9\uff0c\u5176\u5b9e\u5374\u662f\u62d9\u4e2d\u85cf\u5de7\uff0c\u53ea\u8fd9\u4e48\u4e00\u6321\u4e00\u5288\uff0c\u53c8\u628a\u674e\u601d\u5357\u8fd9\u4e00\u62db\u7e41\u590d\u7cbe\u5947\u7684 \u6740\u624b\u89e3\u5f00\uff0c\u800c\u4e14\u53c8\u51b2\u4e0a\u4e86\u4e00\u6b65\u3002 \u3000\u3000\u51e0\u4e2a\u56de\u5408\u4e00\u8fc7\uff0c\u674e\u601d\u5357\u7ed9\u4ed6\u903c\u4e0a\u5c71\u5c97\u4e00\u5757\u8f83\u4e3a\u5e73\u5766\u7684\u5730\u65b9\uff0c\u674e\u601d\u5357\u6240\u5360\u7684\u5730\u5229\u5df1\u7ecf\u5931 \u6389\uff0c\u4e0d\u591a\u4e00\u4f1a\uff0c\u54f2\u522b\u4fbf\u62a2\u5f97\u4e86\u653b\u52bf\uff0c\u6740\u5f97\u674e\u601d\u5357\u53ea\u6709\u62db\u67b6\u4e4b\u529f\u3002 \u3000\u3000\u674e\u601d\u5357\u594b\u52c7\u529b\u6218\uff0c\u54f2\u522b\u7b11\u9053\uff1a\u201c\u597d\u4e2a\u5014\u5f3a\u7684\u5c0f\u5b50\uff0c\u602a\u4e0d\u5f97\u5927\u6c57\u559c\u6b22\u4f60\u3002\u4f60\u522b\u62c5\u5fc3\uff0c\u8ddf\u6211 \u56de\u53bb\u3002\u5927\u6c57\u8fd8\u662f\u4f1a\u597d\u597d\u770b\u5f85\u4f60\u7684\uff0c\u53ea\u8981\u4f60\u544a\u8bc9\u6211\u4eec\uff0c\u4f60\u5230\u8fd9\u963f\u513f\u683c\u5c71\u6765\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u54ac\u7d27\u7259\u6839\uff0c\u4e0d\u7406\u4e0d\u776c\uff0c\u8d81\u54f2\u522b\u8bf4\u8bdd\u7684\u65f6\u5019\uff0c\u5feb\u5251\u53cd\u653b\uff0c\u4e00\u62db\u201c\u9f99\u95e8\u4e09\u9f13\u6d6a\u201d\uff0c\u5251 \u82b1\u9519\u843d\uff0c\u8fde\u73af\u4e09\u5f0f\u523a\u51fa\uff0c\u53ea\u542c\u5f97\u201c\u53ee\u94db\u201d\u4e24\u58f0\uff0c\u524d\u4e24\u5251\u7ed9\u76fe\u724c\u6321\u4f4f\uff0c\u7b2c\u4e09\u5251\u7684\u5251\u5c16\u5374\u4ece\u76fe\u724c \u65c1\u8fb9\u7a7f\u51fa\uff0c\u628a\u54f2\u522b\u675f\u8170\u7684\u8170\u5e26\u524a\u65ad\u4e86 \u3000\u3000\u54f2\u522b\u6012\u9053\uff1a\u201c\u597d\uff0c\u4f60\u8fd9\u5c0f\u5b50\u5f53\u771f\u662f\u656c\u9152\u4e0d\u5403\u8981\u5403\u7f5a\u9152\u5440\uff01\u201d\u4e00\u53e5\u8bdd\u672a\u66fe\u8bf4\u5b8c\uff0c\u5c31\u5288\u51fa\u4e86 \u516d\u516d\u4e09\u5341\u516d\u5200\uff0c\u6bd4\u674e\u601d\u5357\u7684\u5251\u4f7f\u5f97\u66f4\u5feb\uff0c\u4ed6\u6709\u76fe\u724c\u62a4\u8eab\uff0c\u53ea\u653b\u4e0d\u5b88\uff0c\u5a01\u529b\u65e0\u5f62\u4e2d\u53c8\u5927\u4e86\u4e00 \u500d\u3002 \u3000\u3000\u6fc0\u6218\u4e2d\uff0c\u674e\u601d\u5357\u8e29\u7740\u4e00\u5757\u77f3\u5934\uff0c\u811a\u6b65\u4e00\u4e2a\u8dc4\u8e09\uff0c\u54f2\u522b\u4e00\u5200\u524a\u5c06\u8fc7\u53bb\uff0c\u559d\u9053\uff1a\u201c\u5c0f\u5b50\uff0c\u64a4 \u5251\uff01\u201d\u674e\u601d\u5357\u4f0f\u5012\u5730\u4e0a\uff0c\u4e00\u4e2a\u201c\u71d5\u9752\u5341\u516b\u7ffb\u201d\uff0c\u6eda\u51fa\u4e86\u4e09\u4e08\u5f00\u5916\uff0c\u907f\u5f00\u4ed6\u8fd9\u4e00\u5200\uff0c\u8fc5\u5373\u53c8\u8df3 \u8d77\u6765\uff0c\u559d\u9053\uff1a\u201c\u8981\u6211\u64a4\u5251\u6295\u964d\uff0c\u9664\u975e\u4f60\u628a\u6211\u6740\u4e86\uff01\u6211\u662f\u5251\u5728\u4eba\u5728\uff0c\u5251\u4ea1\u4eba\u4ea1\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u867d\u7136\u6ca1\u6709\u7ed9\u4ed6\u7684\u957f\u5200\u780d\u4f24\uff0c\u4f46\u5728\u5730\u4e0a\u90a3\u4e00\u6eda\uff0c\u624b\u811a\u90fd\u7ed9\u8346\u68d8\u523a\u5f97\u9c9c\u8840\u6dcb\u6f13\u3002\u54f2\u522b \u9053\uff1a\u201c\u597d\uff0c\u4f60\u4e0d\u6127\u662f\u4e2a\u5c11\u5e74\u597d\u6c49\u3002\u4f46\u4f60\u8981\u6253\u662f\u6253\u4e0d\u8fc7\u6211\u7684\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u624b\u4e0a\u7684\u9c9c\u8840\u67d3\u7ea2\u4e86\u5251\u67c4\uff0c\u4ecd\u662f\u7262\u7262\u63e1\u7d27\uff0c\u8bf4\u9053\uff1a\u201c\u6253\u4e0d\u8fc7\u4e5f\u8981\u6253\uff01\u4f60\u6765\u5427\uff01\u201d\u54f2 \u522b\u7b11\u900d\uff1a\u201c\u4f55\u5fc5\u518d\u6253\uff0c\u4f60\u4eec\u6c49\u4eba\u7684\u5251\u6cd5\u6211\u89c1\u8bc6\u8fc7\u4e86\uff0c\u4e5f\u4e0d\u8fc7\u5982\u6b64\u3002\u518d\u6253\u8fd8\u6709\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u6012\u9053\uff1a\u201c\u6211\u7684\u5251\u6cd5\u654c\u4f60\u4e0d\u4f4f\uff0c\u5e76\u4e0d\u7b49\u4e8e\u662f\u6c49\u4eba\u7684\u5251\u6cd5\u6bd4\u4e0d\u4e0a\u4f60\u4eec\u8499\u53e4\u7684\u5200\u6cd5\uff01\u800c \u4e14\uff0c\u6211\u4e5f\u8fd8\u672a\u66fe\u8f93\u7ed9\u4f60\u5462\uff0c\u4f60\u5c31\u6562\u5927\u8a00\u4e0d\u60ed\uff01\u201d \u3000\u3000\u5ffd\u542c\u5f97\u6709\u4eba\u63a5\u58f0\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u8499\u53e4\u9791\u5b50\u81ea\u4ee5\u4e3a\u4e86\u4e0d\u8d77\uff0c\u5728\u6211\u770b\u6765\uff0c\u53ea\u4e0d\u8fc7\u662f\u4e95\u5e95\u4e4b \u86d9\uff01\u201d \u3000\u3000\u54f2\u522b\u5b9a\u775b\u4e00\u770b\uff0c\u53ea\u89c1\u6811\u6797\u91cc\u8e7f\u51fa\u4e00\u4e2a\u6c49\u5b50\uff0c\u8eab\u6750\u6bd4\u666e\u901a\u4eba\u7565\u77ee\u4e00\u4e9b\uff0c\u867d\u7136\u8c8c\u4e0d\u60ca\u4eba\uff0c\u4f46 \u53cc\u7738\u70af\u70af\uff0c\u6781\u662f\u6709\u795e\u3002 \u3000\u3000\u54f2\u522b\u6781\u4e3a\u81ea\u8d1f\uff0c\u542c\u4e86\u8fd9\u4eba\u7684\u8bf4\u8bdd\uff0c\u4e0d\u7981\u5fc3\u5934\u706b\u8d77\uff0c\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u600e\u4e48\u662f\u4e95 \u86d9\u4e4b\u89c1\uff0c\u5012\u8981\u8bf7\u4f60\u8fd9\u4f4d\u2018\u9ad8\u660e\u2019\u6307\u6559\uff01\u201d \u3000\u3000\u90a3\u6c49\u5b50\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6c49\u4eba\u7684\u4e0a\u4e58\u5251\u6cd5\uff0c\u4f60\u6839\u672c\u6ca1\u6709\u89c1\u8fc7\u3002\u5c31\u662f\u8fd9\u4f4d\u674e\u516c\u5b50\u7684\u5251\u6cd5\uff0c\u5176\u5b9e \u4e5f\u8981\u6bd4\u4f60\u7684\u5200\u6cd5\u9ad8\u660e\uff0c\u4f60\u4e0d\u8fc7\u5360\u4e86\u6c14\u529b\u5927\u7684\u4fbf\u5b9c\u7f62\u4e86\u3002\u6211\u8bf4\u51e0\u62db\u7ed9\u4f60\u542c\u542c\uff0c\u4f8b\u5982\u521a\u624d\u4ed6\u4f7f\u7684 \u90a3\u62db\u2018\u674e\u5e7f\u5c04\u77f3\u2019\u82e5\u7136\u53cc\u65b9\u6c14\u529b\u76f8\u7b49\uff0c\u4f60\u7528\u2018\u4e8c\u90ce\u62c5\u5c71\u2019\u7684\u5200\u6cd5\u7109\u80fd\u5c01\u95ed\u5f97\u4f4f\uff1f\u53c8\u5982\u90a3\u62db \u2018\u9f99\u95e8\u4e09\u9f13\u6d6a\u2019\uff0c\u6700\u540e\u4e00\u5f0f\uff0c\u82e5\u4e0d\u662f\u4f60\u7684\u76fe\u724c\u5378\u53bb\u4e86\u524d\u4e24\u5f0f\u7684\u529b\u9053\uff0c\u4ed6\u7684\u5251\u5c16\u5c31\u51c6\u80fd\u6d1e\u7a7f\u4f60 \u7684\u5c0f\u8179\u3002\u53c8\u5982\u2026\u2026\u201d\u63a5\u8fde\u8bf4\u4e86\u51e0\u62db\uff0c\u679c\u7136\u8bf4\u5f97\u6781\u4e3a\u4e2d\u80af\u3002\u674e\u601d\u5357\u5728\u65c1\u8fb9\u542c\u5f97\u53c8\u662f\u4f69\u670d\uff0c\u53c8\u662f \u60ed\u6127\u3002 \u3000\u3000\u539f\u6765\u8fd9\u4eba\u8bf4\u7684\u53ea\u662f\u54f2\u522b\u7684\u7f3a\u70b9\uff0c\u800c\u674e\u601d\u5357\u81ea\u77e5\uff0c\u7531\u4e8e\u81ea\u5df1\u4e34\u654c\u7684\u7ecf\u9a8c\u4e0d\u8db3\uff0c\u4ed6\u521a\u624d\u6240\u4f7f \u7684\u8fbe\u6469\u5251\u6cd5\uff0c\u5176\u5b9e\u4e5f\u662f\u7834\u7efd\u751a\u591a\uff0c\u8fdc\u8fdc\u672a\u80fd\u53d1\u6325\u539f\u6765\u5251\u6cd5\u7684\u7cbe\u534e\u3002 \u3000\u3000\u54f2\u522b\u6012\u9053\uff1a\u201c\u53e3\u8bf4\u65e0\u51ed\uff0c\u52a8\u624b\u65b9\u77e5\u3002\u53ea\u8981\u4f60\u80fd\u5728\u767e\u62db\u4e4b\u5185\u80fd\u591f\u4e0e\u6211\u6253\u4e2a\u5e73\u624b\uff0c\u6211\u5c31\u5411\u4f60 \u8ba4\u8f93\uff0c\u514d\u5f97\u4f60\u53c8\u8bf4\u6211\u5360\u4e86\u6c14\u529b\u5927\u7684\u4fbf\u5b9c\u3002\u201d \u3000\u3000\u90a3\u6c49\u5b50\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u767e\u62db\u4e4b\u5185\uff0c\u4f60\u65e9\u5df2\u8f93\u4e86\uff0c\u4e0d\u4fe1\u4f60\u5c31\u8bd5\u8bd5\u3002\u201d \u3000\u3000\u54f2\u522b\u5927\u6012\uff0c\u76fe\u724c\u4e00\u4e3e\uff0c\u6a2a\u5200\u5c31\u5288\u51fa\u53bb\u3002\u90a3\u6c49\u5b50\u8eab\u8eaf\u4e00\u77ee\uff0c\u4e00\u62db\u201c\u94c1\u9501\u6a2a\u6c5f\u201d\uff0c\u5251\u5149\u5982 \u7ec3\uff0c\u524a\u4ed6\u53cc\u8db3\u3002 \u3000\u3000\u54f2\u522b\u8eab\u9ad8\u4e03\u5c3a\u6709\u591a\uff0c\u8fd9\u77ed\u5c0f\u7cbe\u608d\u7684\u6c49\u5b50\u653b\u4ed6\u4e0b\u76d8\uff0c\u6b63\u662f\u907f\u654c\u4e4b\u957f\uff0c\u653b\u654c\u4e4b\u77ed\u3002\u54f2\u522b\u5982\u679c \u8e72\u4e0b\u6765\u5c31\u4e0d\u6613\u4f7f\u529b\uff0c\u53ea\u80fd\u628a\u957f\u5200\u4e0b\u5782\uff0c\u6bcf\u4e00\u62db\u90fd\u662f\u5782\u76f4\u7684\u65ab\u51fa\u53bb\uff0c\u624d\u80fd\u907f\u514d\u7ed9\u5bf9\u65b9\u524a\u7740\u53cc \u817f\u3002\u4ed6\u7684\u76fe\u724c\u53ef\u62a4\u4e0a\u76d8\u3001\u4e2d\u76d8\uff0c\u5bf9\u4e0b\u76d8\u7684\u7167\u987e\u5374\u662f\u96be\u4ee5\u7075\u6d3b\uff0c\u5982\u6b64\u4e00\u6765\uff0c\u54f2\u522b\u7684\u6709\u5229\u6761\u4ef6\uff0c \u65e0\u5f62\u4e2d\u5927\u6253\u6298\u6263\uff0c\u679c\u7136\u7ed9\u90a3\u6c49\u5b50\u6740\u5f97\u5e94\u4ed8\u4e0d\u6687\uff0c\u4ec5\u80fd\u62db\u67b6\u3002 \u3000\u3000\u54f2\u522b\u6012\u9053\uff1a\u201c\u4f60\u8fd9\u7b97\u662f\u4ec0\u4e48\u6253\u6cd5\uff1f\u4f60\u6562\u633a\u8d77\u8170\u6765\u548c\u6211\u5149\u660e\u78ca\u843d\u7684\u4ea4\u624b\u4e48\uff1f\u201d\u90a3\u6c49\u5b50\u7b11 \u9053\uff1a\u201c\u7528\u5175\u4e4b\u9053\uff0c\u8d35\u5728\u4e34\u673a\u5e94\u53d8\uff0c\u6bd4\u6b66\u4e5f\u662f\u5982\u6b64\uff0c\u4f60\u7ba1\u6211\u662f\u4ec0\u4e48\u6253\u6cd5\uff0c\u53ea\u8981\u6253\u5f97\u8d62\u4f60\u5c31\u884c\u3002 \u4f60\u522b\u6025\uff0c\u6253\u5230\u6700\u540e\uff0c\u4f60\u5c31\u77e5\u9053\u662f\u8c01\u633a\u4e0d\u8d77\u8170\u6765\u3002\u201d \u3000\u3000\u6fc0\u6218\u4e2d\uff0c\u90a3\u6c49\u5b50\u6709\u4e00\u5251\u51e0\u4e4e\u523a\u7740\u54f2\u522b\u7684\u819d\u76d6\uff0c\u5374\u7ed9\u54f2\u522b\u7684\u5200\u5934\u55d1\u5f00\u3002\u674e\u601d\u5357\u53eb\u4e86\u4e00\u58f0 \u201c\u53ef\u60dc\u201d\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5ce8\u5d4b\u6d3e\u4e2d\u6709\u4e00\u5957\u626b\u53f6\u5200\u6cd5\uff0c\u6ce8\u91cd\u4e8e\u653b\u5bf9\u65b9\u7684\u4e0b\u9abc\uff0c\u6b64\u4eba\u7684\u5251\u6cd5\u4f3c\u4e4e \u662f\u4ece\u626b\u53f6\u5200\u6cd5\u53d8\u5316\u800c\u6765\uff0c\u83ab\u975e\u4ed6\u662f\u5ce8\u5d4b\u6d3e\u7684\u5f1f\u5b50\uff1f\u4ed6\u521a\u624d\u90a3\u62db\u82e5\u662f\u524a\u5f97\u518d\u4f4e\u4e09\u5206\uff0c\u51c6\u80fd\u5f97 \u624b\uff0c\u4ed6\u7684\u5251\u6cd5\u5982\u6b64\u9ad8\u660e\uff0c\u4e0d\u77e5\u4f55\u4ee5\u9519\u8fc7\u4e86\u8fd9\u4e2a\u673a\u4f1a\uff1f\u201d\u674e\u601d\u5357\u4e0d\u77e5\uff0c\u4fd7\u8bed\u8bf4\u201c\u65c1\u89c2\u8005\u6e05\u201d\uff0c \u6240\u4ee5\u8fd9\u6c49\u5b50\u770b\u5f97\u51fa\u4ed6\u7684\u7f3a\u70b9\uff0c\u4ed6\u4e5f\u770b\u5f97\u51fa\u8fd9\u6c49\u5b50\u7684\u758f\u6f0f\u4e4b\u5904\u3002 \u3000\u3000\u54f2\u522b\u7a7f\u7684\u662f\u4e00\u4ef6\u5bbd\u5927\u7684\u76ae\u888d\uff0c\u521a\u624d\u548c\u674e\u601d\u5357\u4ea4\u624b\u4e4b\u65f6\uff0c\u4ed6\u7684\u675f\u8170\u5e26\u5df2\u7ed9\u674e\u601d\u5357\u524a\u65ad\uff0c\u76ae \u888d\u677e\u5f00\uff0c\u5f71\u54cd\u4e86\u7eb5\u8dc3\u7684\u7075\u6d3b\u3002\u53ea\u56e0\u674e\u601d\u5357\u7684\u672c\u9886\u548c\u4ed6\u9887\u6709\u8ddd\u79bb\uff0c\u548c\u674e\u601d\u5357\u4ea4\u624b\u4e4b\u65f6\u8fd8\u4e0d\u600e\u6837 \u89c9\u5f97\uff0c\u5982\u4eca\u6362\u4e86\u4e00\u4e2a\u672c\u9886\u66f4\u5f3a\u7684\u5bf9\u624b\uff0c\u7eb5\u8dc3\u4e0d\u7075\uff0c\u53ef\u5c31\u8981\u5927\u5927\u7684\u5403\u4e8f\u4e86\u3002 \u3000\u3000\u8fd9\u6c49\u5b50\u548c\u4ed6\u7ed5\u8eab\u6e38\u6597\uff0c\u4e13\u653b\u4ed6\u7684\u4e0b\u76d8\uff0c\u4e0d\u4f46\u5251\u6cd5\u53e4\u602a\uff0c\u8eab\u6cd5\u4e5f\u77eb\u6377\u4e4b\u6781\u3002\u5728\u5c71\u5c97\u4e0a\u4ea4 \u624b\uff0c\u4e0d\u6bd4\u5e73\u5730\uff0c\u7eb5\u8dc3\u4e0d\u7075\uff0c\u5c31\u53ea\u6709\u6328\u6253\u7684\u4efd\u513f\u3002\u54f2\u522b\u7ed9\u4ed6\u6740\u5f97\u624b\u5fd9\u811a\u4e71\uff0c\u5fc3\u5934\u706b\u8d77\uff0c\u5927\u6012\u559d \u9053\uff1a\u201c\u4e0d\u662f\u4f60\u6b7b\uff0c\u4fbf\u662f\u6211\u4ea1\uff01\u201d\u4e3e\u8d77\u76fe\u724c\uff0c\u731b\u5730\u5c31\u7838\u4e0b\u53bb\u3002 \u3000\u3000\u4ed6\u6bd4\u8fd9\u6c49\u5b50\u9ad8\u4e8c\u5c3a\u6709\u591a\uff0c\u8981\u7528\u76fe\u724c\u6253\u8fd9\u6c49\u5b50\u7684\u5929\u7075\u76d6\u5fc5\u987b\u5f2f\u4e0b\u8170\u6765\u3002\u8fd9\u6c49\u5b50\u54c8\u54c8\u4e00\u7b11\uff0c \u731b\u5730\u559d\u9053\uff1a\u201c\u4e14\u53eb\u4f60\u770b\u770b\u662f\u8c01\u633a\u4e0d\u8d77\u8170\uff01\u201d\u8eab\u5f62\u4e00\u98d8\u4e00\u95ea\uff0c\u54f2\u522b\u7684\u76fe\u724c\u7838\u4e86\u4e2a\u7a7a\uff0c\u8bf4\u65f6\u8fdf\uff0c \u90a3\u65f6\u5feb\uff0c\u53ea\u89c9\u52b2\u98ce\u98d2\u7136\uff0c\u8fd9\u6c49\u5b50\u7684\u5251\u5c16\u5df2\u7ecf\u6307\u5230\u4e86\u4ed6\u7684\u5c0f\u8179\u3002\u54f2\u522b\u7528\u4e86\u4e00\u8bb0\u201c\u6302\u5200\u201d\u7684\u62db \u6570\uff0c\u5782\u76f4\u65ab\u51fa\uff0c\u8fd9\u6c49\u5b50\u4e0d\u8ba9\u4ed6\u7684\u957f\u5200\u78b0\u7740\uff0c\u5251\u70b3\u4e00\u649e\uff0c\u54f2\u522b\u8170\u672a\u633a\u8d77\uff0c\u5df2\u662f\u7ed9\u4ed6\u649e\u7740\u4e86\u80c1\u4e0b \u7684\u808b\u9aa8\u3002 \u3000\u3000\u539f\u6765\u54f2\u522b\u7684\u76fe\u724c\u662f\u7528\u6765\u62a4\u8eab\u7684\uff0c\u5982\u4eca\u62ff\u6765\u52a9\u653b\uff0c\u8170\u8179\u4e4b\u95f4\u5c31\u9732\u51fa\u4e86\u7a7a\u95e8\uff0c\u662f\u4ee5\u8fd9\u6c49\u5b50\u624d \u80fd\u4e00\u4e3e\u594f\u6548\u3002\u6b64\u65f6\u4ed6\u4eec\u6253\u4e86\u5927\u7ea6\u53ea\u6709\u516d\u4e03\u5341\u62db\uff0c\u8fd9\u6c49\u5b50\u8bf4\u8fc7\u767e\u62db\u4e4b\u5185\u4fbf\u8981\u80dc\u4ed6\uff0c\u679c\u7136\u5151\u73b0\u3002 \u3000\u3000\u8fd9\u6c49\u5b50\u7684\u5251\u67c4\u649e\u6b63\u54f2\u522b\u7684\u808b\u9aa8\uff0c\u7528\u7684\u53c8\u662f\u91cd\u624b\u6cd5\uff0c\u9976\u662f\u54f2\u522b\u94c1\u9aa8\u94dc\u76ae\uff0c\u4e5f\u7981\u53d7\u4e0d\u8d77\u3002\u54f2 \u522b\u5927\u543c\u4e00\u58f0\uff0c\u76fe\u724c\u98de\u51fa\uff0c\u4e00\u8de4\u8dcc\u5012\u5730\u4e0a\u3002 \u3000\u3000\u8fd9\u6c49\u5b50\u6b63\u8981\u518d\u8865\u4e00\u5251\uff0c\u523a\u4ed6\u7a74\u9053\uff0c\u76fe\u724c\u98de\u6765\uff0c\u4e0d\u80fd\u4e0d\u4fa7\u8eab\u4e00\u95ea\u3002\u54f2\u522b\u4e5f\u771f\u662f\u987d\u5f3a\uff0c\u4eba\u672a \u722c\u8d77\uff0c\u4e00\u4e2a\u201c\u864e\u5c3e\u811a\u201d\u5c31\u5012\u6491\u51fa\u53bb\u3002\u8fd9\u6c49\u5b50\u6599\u4e0d\u5230\u4ed6\u6709\u6b64\u4e00\u7740\u53cd\u6251\uff0c\u624b\u4e2d\u7684\u9752\u94a2\u5251\u7adf\u4e5f\u7ed9\u4ed6 \u8e22\u98de\u4e86\u3002 \u3000\u3000\u6c49\u5b50\u6012\u9053\uff1a\u201c\u597d\uff0c\u6211\u5c31\u548c\u4f60\u518d\u6bd4\u6bd4\u817f\u4e0a\u529f\u592b\uff01\u201d\u817e\u5730\u4e5f\u662f\u4e00\u811a\u8e22\u51fa\uff0c\u54f2\u522b\u672a\u66fe\u722c\u8d77\uff0c\u54ea \u95ea\u5f97\u5f00\uff0c\u9aa8\u788c\u788c\u5730\u76f4\u6eda\u4e0b\u4e86\u5c71\u5761\uff01\u6c49\u5b50\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u6211\u5360\u4e86\u4f60\u5148\u6253\u4e00\u573a\u7684\u4fbf\u5b9c\uff0c\u5c31\u8ba9\u4f60\u53bb \u5427\uff01\u201d\u54f2\u522b\u6eda\u4e0b\u5c71\u5761\u4e4b\u65f6\uff0c\u957f\u5200\u5df2\u7ecf\u8dcc\u843d\uff0c\u4ed6\u7684\u5f13\u7bad\u53c8\u65e9\u5df2\u88ab\u674e\u601d\u5357\u6bc1\u4e86\uff0c\u6b64\u65f6\u4ed6\u5f53\u771f\u662f\u624b \u65e0\u5bf8\u94c1\uff0c\u56e0\u6b64\uff0c\u4ed6\u867d\u7136\u8bf4\u8fc7\u8981\u4e0e\u5bf9\u65b9\u62fc\u4e2a\u4f60\u6b7b\u6211\u6d3b\u7684\u8bf4\u8bdd\uff0c\u6b64\u9645\u4e5f\u53ea\u80fd\u9003\u547d\u4e86\u3002 \u3000\u3000\u54f2\u522b\u7684\u5750\u9a91\u662f\u4e45\u7ecf\u8bad\u7ec3\u7684\u6218\u9a6c\uff0c\u4e3b\u4eba\u6eda\u4e0b\u5c71\u5761\uff0c\u5b83\u5c31\u7acb\u5373\u5230\u4e86\u4e3b\u4eba\u7684\u8eab\u8fb9\uff0c\u54f2\u522b\u6d51\u8eab\u4e0a \u4e0b\u88ab\u77f3\u7b0b\u3001\u8346\u68d8\u523a\u5f97\u9c9c\u8840\u6dcb\u6f13\uff0c\u53d7\u4f24\u6bd4\u674e\u601d\u5357\u66f4\u91cd\uff0c\u4f46\u4ecd\u7136\u80fd\u591f\u8df3\u4e0a\u9a6c\u80cc\uff0c\u9a70\u51fa\u5c71\u8c37\u3002\u8fd9\u6c49 \u5b50\u4e5f\u662f\u660e\u77e5\u8ffd\u4ed6\u4e0d\u4e0a\uff0c\u4e50\u5f97\u8bf4\u90a3\u51e0\u53e5\u98ce\u51c9\u8bdd\u7684\u3002 \u3000\u3000\u674e\u601d\u5357\u4e0a\u524d\u9053\u8c22\uff0c\u90a3\u6c49\u5b50\u7b11\u9053\uff1a\u201c\u6211\u4e5f\u8981\u591a\u8c22\u4f60\u5462\uff0c\u4e0d\u662f\u4f60\u5148\u8017\u4e86\u4ed6\u6c14\u529b\uff0c\u6211\u672a\u5fc5\u6253\u5f97 \u8fc7\u4ed6\u3002\u674e\u516c\u5b50\uff0c\u4f60\u5e94\u8be5\u77e5\u9053\u6211\u662f\u8c01\u5427\uff1f\u201d\u6b63\u662f\uff1a \u3000\u3000\u63a2\u6c42\u8eab\u4e16\u9690\uff0c\u8352\u8c37\u8bbf\u5947\u4eba\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b\u2014\u2014\u7b2c\u516d\u56de\u3000\u9aa8\u8089\u56e2\u5706\u5982\u9694\u4e16\u3000\u4eb2\u6069\u987b\u6170\u7f14\u826f\u7f18 \u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b \u7b2c\u516d\u56de\u3000\u9aa8\u8089\u56e2\u5706\u5982\u9694\u4e16\u3000\u4eb2\u6069\u987b\u6170\u7f14\u826f\u7f18 \u3000\u3000\u8fd9\u6c49\u5b50\u4e00\u9762\u8bf4\u8bdd\uff0c\u4e00\u9762\u8131\u4e86\u4e0a\u8863\uff0c\u53ea\u89c1\u4ed6\u53f3\u80a9\u6709\u4e2a\u94dc\u94b1\u822c\u5927\u5c0f\u7684\u4f24\u75a4\u3002 \u3000\u3000\u90a3\u665a\u7559\u5b57\u7ed9\u4ed6\u7684\u90a3\u4e2a\u523a\u5ba2\uff0c\u674e\u601d\u5357\u867d\u7136\u6ca1\u89c1\u7740\u4ed6\u7684\u5e90\u5c71\u771f\u9762\uff0c\u4f46\u4ed6\u7684\u8eab\u578b\u548c\u4ed6\u7684\u5251\u6cd5\u674e \u601d\u5357\u5219\u662f\u89c1\u5230\u4e86\u7684\u3002\u90a3\u201c\u523a\u5ba2\u201d\u90a3\u665a\u4e2d\u4e86\u536b\u571f\u7684\u4e00\u67c4\u98de\u5200\uff0c\u4f24\u7684\u6b63\u662f\u53f3\u80a9\u3002\u5982\u4eca\u8fd9\u6c49\u5b50\u9732\u51fa \u4e86\u4f24\u75a4\uff0c\u674e\u601d\u5357\u5f53\u7136\u662f\u66f4\u65e0\u6000\u7591\u4e86\u3002 \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u591a\u8c22\u4f60\u7684\u6307\u5f15\uff0c\u6211\u5982\u4eca\u5df2\u662f\u4f9d\u7ea6\u800c\u6765\uff0c\u4e0d\u77e5\u4f60\u662f\u4e3a\u4e86\u4f55\u4e8b\u7ea6\u6211\uff1f\u201d \u3000\u3000\u90a3\u53cc\u5b50\u7b11\u9053\uff1a\u201c\u4e0d\u662f\u6211\u7ea6\u4f60\uff0c\u662f\u677e\u98ce\u8c37\u4e2d\u6709\u4e00\u4e2a\u4eba\u60f3\u8981\u89c1\u4f60\uff0c\u6211\u4ee3\u4ed6\u8bf7\u4f60\u6765\u7684\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u90a3\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u6c49\u5b50\u7b11\u9053\uff1a\u201c\u4f60\u89c1\u4e86\u4ed6\u81ea\u7136\u77e5\u9053\u3002\u6211\u53ea\u60f3\u95ee\u4f60\uff0c\u4f60\u73b0\u5728\u662f\u4e0d\u662f\u5fc3\u91cc\u6709\u4e86\u7591\u56e2\u4e86\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u6b63\u662f\uff0c\u6240\u4ee5\u6211\u7279\u5730\u6765\u8bf7\u4f60\u6307\u70b9\u8ff7\u6d25\u3002\u201d \u3000\u3000\u90a3\u6c49\u5b50\u9053\uff1a\u201c\u4f60\u7684\u2018\u8ff7\u6d25\u2019\uff0c\u4e5f\u53ea\u6709\u90a3\u4e2a\u4eba\u80fd\u591f\u7ed9\u4f60\u6307\u70b9\u3002\u597d\uff0c\u4f60\u73b0\u5728\u5c31\u8ddf\u6211\u53bb\u5427\u3002\u54b1 \u4eec\u6162\u6162\u518d\u8bf4\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u8ddf\u7740\u4ed6\u8d70\uff0c\u8def\u4e0a\u8bf7\u6559\u4ed6\u7684\u59d3\u540d\uff0c\u59cb\u77e5\u8fd9\u4eba\u59d3\u626c\uff0c\u5355\u540d\u4e00\u4e2a\u201c\u6ed4\u201d\u5b57\u3002\u674e\u601d\u5357\u9053\uff1a \u201c\u6768\u5144\u4f7f\u7684\u597d\u4e00\u5957\u843d\u53f6\u5251\u6cd5\uff0c\u6562\u60c5\u6768\u5144\u662f\u5ce8\u5d4b\u95e8\u4e0b\uff1f\u201d \u3000\u3000\u6768\u6ed4\u7b11\u9053\uff1a\u201c\u679c\u7136\u7792\u4e0d\u8fc7\u516c\u5b50\u7684\u6cd5\u773c\uff0c\u5bb6\u5e08\u88f4\u53d4\u5ea6\u6b63\u662f\u5ce8\u5d4b\u6d3e\u7684\u638c\u95e8\u5927\u5f1f\u5b50\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u597d\u751f\u6b22\u559c\uff0c\u539f\u6765\u88f4\u53d4\u5ea6\u548c\u4ed6\u7684\u5e08\u7236\u8c37\u5e73\u9633\u4e43\u662f\u77e5\u4ea4\uff0c\u65e9\u5e74\u66fe\u6709\u201c\u6b66\u6797\u53cc\u79c0\u201d\u4e4b \u79f0\uff0c\u56e0\u4e3a\u4ed6\u4eec\u8eab\u4efd\u76f8\u540c\uff0c\u540c\u662f\u5c11\u6797\u3001\u5ce8\u5d4b\u7b2c\u4e8c\u4ee3\u4e2d\u6700\u6770\u51fa\u7684\u4eba\u7269\u3002 \u3000\u3000\u674e\u601d\u5357\u66fe\u542c\u5f97\u5e08\u7236\u8bf4\u8fc7\u88f4\u53d4\u5ea6\u7684\u4e8b\u8ff9\uff0c\u4e0d\u8fc7\u56e0\u4e3a\u5ce8\u5d4b\u5c71\u662f\u5728\u56db\u5ddd\uff0c\u5c5e\u4e8e\u5357\u5b8b\u7586\u57df\uff1b\u5d69\u5c71 \u5c11\u6797\u5bfa\u5728\u6cb3\u5357\uff0c\u5219\u662f\u5c5e\u4e8e\u91d1\u56fd\u7684\u7edf\u6cbb\u4e4b\u4e0b\u3002\u6240\u4ee5\u8c37\u5e73\u9633\u548c\u88f4\u53d4\u5ea6\u89c1\u9762\u65e0\u591a\u3002\u8fd1\u5341\u5e74\u6765\u7531\u4e8e\u91d1 \u5b8b\u4e24\u56fd\u7ecf\u5e38\u5904\u4e8e\u6218\u4e89\u7684\u72b6\u6001\u4e4b\u4e2d\uff0c\u4e24\u4eba\u5c31\u4e00\u76f4\u6ca1\u6709\u76f8\u89c1\u4e86\u3002\u4e0d\u8fc7\uff0c\u867d\u7136\u5e73\u751f\u89c1\u9762\u65e0\u591a\uff0c\u6d88\u606f \u53c8\u4e2d\u65ad\u4e86\u5341\u5e74\u4e4b\u4e45\uff0c\u4ed6\u4eec\u7684\u4ea4\u60c5\u4ecd\u7136\u4e0d\u662f\u666e\u901a\u4eba\u6240\u80fd\u76f8\u6bd4\u7684\u3002 \u3000\u3000\u674e\u601d\u5357\u8bf4\u4e86\u81ea\u5df1\u7684\u5e08\u627f\uff0c\u6768\u6ed4\u7b11\u9053\uff1a\u201c\u6211\u4e5f\u542c\u5f97\u5e08\u7236\u8bf4\u8fc7\uff0c\u8bf4\u662f\u8c37\u5927\u4fa0\u6536\u4e86\u4e00\u4e2a\u5f97\u610f\u7684 \u5f1f\u5b50\uff0c\u539f\u6765\u5c31\u662f\u4f60\u3002\u90a3\u65f6\u4f60\u5927\u7ea6\u5c1a\u672a\u51fa\u9053\uff0c\u628a\u8fd9\u6d88\u606f\u5e26\u6765\u7684\u4eba\u4e5f\u8fd8\u672a\u77e5\u9053\u4f60\u7684\u5927\u540d\u5462\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u6211\u5165\u95e8\u5f97\u8fdf\uff0c\u672a\u66fe\u62dc\u89c1\u8fc7\u88f4\u5927\u4fa0\uff0c\u60f3\u4e0d\u5230\u4eca\u65e5\u5f97\u89c1\u6768\u5144\u3002\u8fd9\u6837\u8bf4\uff0c\u54b1\u4eec\u66f4 \u4e0d\u662f\u5916\u4eba\u4e86\u3002\u5374\u4e0d\u77e5\u6768\u5144\u4f55\u4ee5\u5230\u4e86\u8499\u53e4\u3002\u201d \u3000\u3000\u6768\u6ed4\u8bf4\u9053\uff1a\u201c\u6211\u6765\u4e86\u5df2\u7ecf\u6709\u4e03\u5e74\u4e86\uff0c\u8bf4\u8d77\u6765\u4e00\u8a00\u96be\u5c3d\u2026\u2026\u55ef\uff0c\u677e\u98ce\u8c37\u5df2\u7ecf\u5230\u4e86\uff0c\u6211\u7684\u4e8b \u4ee5\u540e\u518d\u6162\u6162\u8bf4\u5427\u3002\u201d \u3000\u3000\u8fd9\u677e\u51e4\u8c37\u662f\u5728\u4e24\u5cf0\u5939\u5cd9\u4e4b\u95f4\u7684\u4e00\u6761\u5c71\u6c9f\uff0c\u5e76\u4e0d\u50cf\u4e00\u822c\u6240\u8c13\u7684\u5c71\u8c37\u662f\u5728\u5e95\u4e0b\u7684\u3002\u8fd9\u5c71\u6c9f\u957f \u7684\u90fd\u662f\u677e\u6811\uff0c\u51c9\u98ce\u4e60\u4e60\uff0c\u540d\u5b9e\u76f8\u526f\u3002\u98ce\u4e2d\u9001\u6765\u677e\u5b50\u7684\u6e05\u9999\uff0c\u4ee4\u4eba\u7cbe\u795e\u987f\u723d\u3002 \u3000\u3000\u674e\u6069\u5357\u9053\uff1a\u201c\u679c\u7136\u4e0d\u6127\u677e\u98ce\u8c37\u8fd9\u4e2a\u5609\u540d\u3002\u4f46\u8fd9\u6837\u5e7d\u50fb\u7684\u5730\u65b9\uff0c\u82e5\u975e\u6768\u5144\u5e26\u5f15\uff0c\u5c0f\u5f1f\u7109\u80fd \u627e\u5230\uff1f\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u5230\u4e86\u4e00\u4e2a\u7a91\u6d1e\u5916\u9762\u3002\u6768\u6ed4\u6084\u58f0\u8bf4\u9053\uff1a\u201c\u811a\u6b65\u653e\u8f7b\u4e9b\u3002\u201d\u674e\u601d\u5357\u5f2f\u4e0b\u8170\uff0c\u6000\u7740 \u51e0\u5206\u597d\u5947\u51e0\u5206\u60f4\u60f4\u4e0d\u5b89\u7684\u5fc3\u60c5\uff0c\u8ddf\u4ed6\u94bb\u8fdb\u7a91\u6d1e\u3002 \u3000\u3000\u7a91\u6d1e\u6d1e\u53e3\u72ed\u7a84\uff0c\u91cc\u9762\u5374\u5f88\u5bbd\u5e7f\u3002\u674e\u601d\u5357\u5b9a\u775b\u4e00\u77a7\uff0c\u53ea\u89c1\u6d1e\u4e2d\u5e03\u7f6e\u5f97\u50cf\u4e00\u95f4\u666e\u901a\u519c\u5bb6\u7684\u5367 \u5ba4\uff0c\u7528\u8349\u5806\u4f5c\u5e8a\u94fa\uff0c\u5367\u7740\u4e00\u8001\u4eba\uff0c\u5728\u8fd9\u8001\u4eba\u7684\u8eab\u8fb9\uff0c\u5750\u7740\u4e00\u4e2a\u5c11\u5973\u3002 \u3000\u3000\u8fd9\u5c11\u5973\u770b\u89c1\u4e00\u4e2a\u964c\u751f\u4eba\u8fdb\u6765\uff0c\u6709\u70b9\u60ca\u8be7\u3002\u6768\u6ed4\u9053\uff1a\u201c\u6211\u628a\u674e\u516c\u5b50\u63a5\u6765\u5566\u3002\u201d\u5c11\u5973\u671b\u4e86\u674e \u601d\u5357\u4e00\u773c\uff0c\u770b\u6765\u5df2\u662f\u660e\u767d\uff0c\u4f46\u5374\u6447\u4e86\u6447\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u75c5\u4eba\u521a\u521a\u7761\u7740\u4e86\uff0c\u522b\u5435\u9192\u4ed6\u3002\u201d \u3000\u3000\u90a3\u8001\u4eba\u5ffd\u5730\u5f20\u5f00\u4e86\u773c\uff0c\u8bf4\u9053\uff1a\u201c\u662f\u8c01\u6765\u4e86\uff1f\u201d\u539f\u6765\u4ed6\u4e45\u75c5\u4f53\u865a\uff0c\u521a\u624d\u53ea\u662f\u95ed\u76ee\u517b\u795e\u800c \u5df2\uff0c\u5e76\u672a\u719f\u7761\u3002 \u3000\u3000\u6768\u6ed4\u9053\uff1a\u201c\u597d\u6559\u8001\u4f2f\u559c\u6b22\uff0c\u6211\u628a\u4ee4\u90ce\u5e26\u6765\u4e86\uff01\u201d \u3000\u3000\u8fd9\u4e24\u53e5\u8bdd\u80dc\u4f3c\u7075\u4e39\uff0c\u90a3\u8001\u4eba\u53cc\u773c\u653e\u5149\uff0c\u970d\u5730\u5c31\u5750\u4e86\u8d77\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u8d70\u8fd1\u4e00\u4e9b\uff0c\u8ba9\u6211\u4ed4\u7ec6 \u770b\u770b\uff0c\u5f53\u771f\u662f\u6211\u7684\u5357\u513f\u4e48\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u65e9\u5df2\u731c\u5230\u8fd9\u8001\u4eba\u662f\u4ed6\u7236\u4eb2\uff0c\u4f46\u56e0\u4ed6\u53d7\u8fc7\u4e00\u6b21\u9a97\uff0c\u4e00\u65f6\u95f4\u8fd8\u4e0d\u6562\u5192\u5473\u76f8\u8ba4\u3002\u662f\u4ee5\u4ed6 \u867d\u7136\u8d70\u8fd1\u90a3\u8001\u4eba\u8eab\u8fb9\uff0c\u5374\u672a\u8dea\u4e0b\u78d5\u5934\u53eb\u7239\u3002 \u3000\u3000\u7a91\u6d1e\u4e2d\u5149\u7ebf\u5fae\u5f31\uff0c\u4f46\u674e\u601d\u5357\u662f\u7ec3\u8fc7\u6697\u5668\u7684\u4eba\uff0c\u76ee\u529b\u6bd4\u5e38\u4eba\u4e3a\u4f73\uff0c\u6b64\u65f6\u4ed6\u8fdb\u4e86\u7a91\u6d1e\u5df2\u6709\u4e00 \u4f1a\uff0c\u4e5f\u6e10\u6e10\u4e60\u60ef\u4e8e\u6d1e\u4e2d\u6697\u6de1\u7684\u5149\u7ebf\u4e86\u3002\u773c\u5149\u4e00\u77a5\uff0c\u53ea\u89c1\u5899\u4e0a\u6302\u6709\u4e00\u5f20\u7f8a\u76ae\u7eb8\uff0c\u7eb8\u4e0a\u6709\u5b57\uff0c\u4ed4 \u7ec6\u4e00\u770b\uff0c\u5199\u7684\u662f\u4e00\u9996\u5510\u8bd7\uff0c\u58a8\u6e0d\u72b9\u65b0\uff0c\u60f3\u662f\u4e0d\u4e45\u4e4b\u524d\u5199\u7684\u3002 \u3000\u3000\u8001\u4eba\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u7b49\u4e86\u4f60\u8bb8\u591a\u5929\uff0c\u4ee5\u4e3a\u4f60\u4e0d\u4f1a\u6765\u4e86\u3002\u8fd9\u4e24\u5929\u6211\u60f3\u5bb6\u60f3\u5f97\u5fc3\u70e6\uff0c \u5199\u4e86\u5510\u8bd7\u4eba\u5d14\u793c\u5c71\u8fd9\u9996\u601d\u5bb6\u4e4b\u8bd7\uff0c\u60f3\u4e0d\u5230\u4f60\u4eca\u5929\u5c31\u6765\u4e86\u3002\u4f60\u5988\u597d\u4e48\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u987e\u4e0d\u5f97\u56de\u7b54\uff0c\u5148\u770b\u8fd9\u4e00\u9996\u8bd7\uff0c\u8bd7\u9053\uff1a\u201c\u6c34\u6d41\u82b1\u8c22\u4e24\u65e0\u60c5\uff0c\u9001\u5c3d\u4e1c\u98ce\u8fc7\u695a\u57ce\uff0c\u8774\u8776 \u68a6\u4e2d\u5bb6\u4e07\u91cc\uff0c\u675c\u9e43\u679d\u4e0a\u6708\u4e09\u66f4\u3002\u6545\u56ed\u8bd7\u52a8\u7ecf\u5e74\u7eaa\uff0c\u534e\u53d1\u6625\u50ac\u4e24\u9b13\u751f\u3002\u81ea\u662f\u4e0d\u5f52\u5f52\u4fbf\u5f97\uff0c\u4e94\u6e56 \u70df\u666f\u6709\u8c01\u4e89\uff1f\u201d \u3000\u3000\u601d\u5bb6\u4e4b\u60c5\uff0c\u85c9\u8fd9\u9996\u8bd7\u8868\u9732\u65e0\u9057\u3002\u4f46\u674e\u601d\u5357\u7559\u610f\u7684\u5374\u4e0d\u662f\u8bd7\u672c\u8eab\uff0c\u800c\u662f\u5b57\u8ff9\uff0c\u4e00\u770b\u4e4b\u4e0b\uff0c \u679c\u7136\u548c\u4ed6\u6240\u719f\u8bc6\u7684\u4ed6\u7236\u4eb2\u7684\u7b14\u8ff9\u4e00\u6a21\u4e00 \u3000\u3000\u674e\u601d\u5357\u6cea\u54bd\u5fc3\u9178\uff0c\u8dea\u4e0b\u6765\u9053\uff1a\u201c\u4e0d\u5b5d\u513f\u6765\u8fdf\uff0c\u7d2f\u5f97\u7239\u7239\u53d7\u82e6\u4e86\u3002\u5988\u5988\u8eab\u4f53\u8fd8\u597d\uff0c\u53ea\u7b49\u7740 \u7239\u7239\u56de\u53bb\uff01\u201d \u3000\u3000\u8001\u4eba\u82e6\u7b11\u9053\uff1a\u201c\u6211\u53ea\u6015\u56de\u4e0d\u53bb\u4e86\uff0c\u89c1\u5f97\u7740\u4f60\u4e00\u9762\uff0c\u6211\u4e5f\u5df2\u7ecf\u5fc3\u8db3\u4e86\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u54bd\u4e0b\u773c\u6cea\uff0c\u8bf4\u9053\uff1a\u201c\u7239\uff0c\u4f60\u522b\u96be\u8fc7\uff0c\u4f60\u4f1a\u597d\u8d77\u6765\u7684\u3002\u4f60\u6b47\u6b47\u518d\u8bf4\u5427\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u529d\u4ed6\u7236\u4eb2\u4e0d\u8981\u96be\u8fc7\uff0c\u4ed6\u81ea\u5df1\u5fc3\u91cc\u5374\u662f\u96be\u8fc7\u4e4b\u6781\uff0c\u4ed6\u7236\u4eb2\u4e0d\u8fc7\u662f\u4e94\u5341\u5c81\u5de6\u53f3\u7684\u4eba\uff0c \u4e0d\u5e94\u8be5\u8870\u8001\u5f97\u6210\u8fd9\u4e2a\u6837\u5b50\u7684\u3002\u201c\u7239\u7239\u4e0d\u77e5\u53d7\u4e86\u591a\u5c11\u6298\u78e8\uff0c\u4ed6\u989d\u4e0a\u4e00\u6761\u6761\u7684\u76b1\u7eb9\u90fd\u662f\u8499\u53e4\u9791\u5b50 \u4f5c\u6076\u7684\u7f6a\u8bc1\uff01\u53ef\u6068\u6211\u5374\u53d7\u5978\u4eba\u6b3a\u9a97\uff0c\u51e0\u4e4e\u8bc6\u8d3c\u4f5c\u7236\u3002\u201d\u674e\u601d\u5357\u5fc3\u60f3\u3002 \u3000\u3000\u90a3\u5c11\u5973\u7aef\u6765\u4e86\u4e00\u7897\u836f\u8336\uff0c\u8bf4\u9053\uff1a\u201c\u7239\uff0c\u4f60\u559d\u4e86\u836f\u518d\u8bf4\u3002\u201d\u674e\u601d\u5357\u542c\u89c1\u8fd9\u5c11\u5973\u53eb\u4ed6\u7684\u7236\u4eb2 \u505a\u201c\u7239\u201d\uff0c\u6709\u70b9\u5947\u602a\uff0c\u4f46\u6b64\u65f6\u4ed6\u53ea\u8981\u77e5\u9053\u4ed6\u7236\u4eb2\u7684\u4e8b\u60c5\uff0c\u5bf9\u8fd9\u5c11\u5973\u7684\u8eab\u4efd\uff0c\u6682\u65f6\u65e0\u6687\u8be2\u95ee\u3002 \u3000\u3000\u8fd9\u7897\u836f\u8336\u662f\u6709\u4eba\u53c2\u7684\uff0c\u674e\u5e0c\u6d69\u559d\u4e86\u4e4b\u540e\uff0c\u7cbe\u795e\u597d\u4e86\u4e00\u4e9b\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u6ce8\u91ca\u7684\u90a3\u672c\u5175\u4e66\u4f60 \u5e26\u6765\u4e86\u6ca1\u6709\uff1f\u8fd9\u662f\u6211\u672a\u66fe\u5b8c\u6210\u7684\u5fc3\u613f\uff0c\u9664\u4e86\u4f60\u4eec\u6bcd\u5b50\u4e4b\u5916\uff0c\u6211\u4e00\u76f4\u8bb0\u6302\u7684\u5c31\u53ea\u662f\u8fd9\u672c\u4e66\u4e86\u3002 \u6211\u8fd8\u8bb0\u5f97\u8fd9\u672c\u4e66\u4e00\u5171\u6709\u4e00\u767e\u4e00\u5341\u4e8c\u9875\uff0c\u6211\u53ea\u6ce8\u91ca\u4e86\u516d\u5341\u516b\u9875\u3002\u4f60\u53ef\u66fe\u770b\u8fc7\u4e48\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u8fd9\u672c\u4e66\u5c31\u5728\u6211\u7684\u8eab\u4e0a\uff0c\u6211\u770b\u8fc7\u4e86\u3002\u524d\u534a\u90e8\u6709\u4f60\u7684\u6ce8\u91ca\uff0c\u6211\u770b\u5f97\u5f88\u660e\u767d\uff0c\u53ef \u60dc\u5230\u4e86\u6ca1\u6709\u6ce8\u91ca\u7684\u540e\u534a\u90e8\uff0c\u6211\u770b\u5f97\u4e0d\u5927\u61c2\u4e86\u3002\u201d \u3000\u3000\u674e\u5e0c\u6d69\u63a5\u8fc7\u513f\u5b50\u7ed9\u4ed6\u7684\u90a3\u4e00\u672c\u4e66\uff0c\u7ffb\u4e86\u4e00\u7ffb\uff0c\u773c\u4e2d\u53d1\u51fa\u559c\u60a6\u7684\u5149\u8292\uff0c\u4f46\u968f\u5373\u5374\u662f\u53f9\u53e3\u6c14 \u9053\uff1a\u201c\u6211\u6ca1\u6709\u7cbe\u529b\u7ee7\u7eed\u4e0b\u53bb\u4e86\uff0c\u4f60\u597d\u597d\u4fdd\u5b58\u5b83\uff0c\u5c06\u6765\u53ef\u4ee5\u66ff\u6211\u5b8c\u6210\u8fd9\u4efd\u6770\u4f5c\u3002\u55ef\uff0c\u6211\u771f\u62c5\u5fc3 \u4f60\u7ed9\u90a3\u4eba\u9a97\u53bb\u5462\uff0c\u73b0\u5728\u6211\u5b89\u5fc3\u4e86\u3002\u201d\u8bf4\u7f62\u628a\u4e66\u53c8\u4ea4\u56de\u7ed9\u674e\u601d\u5357\u3002 \u3000\u3000\u674e\u601d\u5357\u85cf\u597d\u5175\u4e66\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u4eba\u662f\u8c01\uff0c\u6211\u6b63\u60f3\u77e5\u9053\u3002\u201d \u3000\u3000\u674e\u5e0c\u6d69\u8bf4\u9053\uff1a\u201c\u6211\u77e5\u9053\u4ed6\u73b0\u5728\u662f\u5192\u7528\u6211\u7684\u540d\u5b57\u3002\u4ed6\u539f\u6765\u7684\u540d\u5b57\u53eb\u4f59\u4e00\u4e2d\uff0c\u662f\u6211\u5728\u4fd8\u864f\u8425 \u4e2d\u6700\u8981\u597d\u7684\u4e00\u4e2a\u670b\u53cb\u3002\u60f3\u4e0d\u5230\u8fd9\u4e2a\u6700\u597d\u7684\u670b\u53cb\uff0c\u540e\u6765\u4e5f\u5c31\u662f\u628a\u6211\u5bb3\u5f97\u6700\u60e8\u7684\u4eba\u3002\u201d\u8bf4\u81f3\u6b64 \u5904\uff0c\u8fde\u8fde\u54b3\u55fd\u3002 \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u7239\uff0c\u4f60\u6162\u6162\u5730\u8bf4\u3002\u5b69\u513f\u4f1a\u7ed9\u4f60\u62a5\u4ec7\u7684\uff01\u201d \u3000\u3000\u674e\u5e0c\u6d69\u9053\uff1a\u201c\u6211\u6068\u4e0d\u5f97\u4e00\u4e0b\u5b50\u90fd\u544a\u8bc9\u4f60\u3002\u597d\uff0c\u6162\u6162\u5730\u8bf4\u5427\u3002\u201d \u3000\u3000\u201c\u6211\u548c\u4ed6\u662f\u5728\u5e93\u4f26\u6c60\u5317\u57a6\u8352\u7684\u65f6\u5019\u7ed3\u8bc6\u7684\u3002\u57a6\u8352\u7684\u6c49\u4eba\u4fd8\u864f\u6709\u4e8c\u4e09\u5343\u4eba\u4e4b\u591a\uff0c\u8499\u5360\u9791\u5b50 \u4e0d\u8010\u70e6\u8bb0\u54b1\u4eec\u6c49\u4eba\u7684\u540d\u5b57\uff0c\u4ed6\u4eec\u7ed9\u4fd8\u864f\u7f16\u4e86\u53f7\u6570\uff0c\u6211\u662f\u516b\u767e\u4e03\u5341\u4e09\u53f7\uff0c\u8fd9\u4e2a\u4f59\u4e00\u4e2d\u662f\u516b\u767e\u4e03 \u5341\u56db\u53f7\uff0c\u56e0\u6b64\u767d\u5929\u6211\u4eec\u662f\u540c\u5728\u4e00\u4e2a\u5c0f\u961f\uff0c\u665a\u4e0a\u662f\u540c\u5bbf\u4e00\u4e2a\u8425\u623f\u3002\u4ed6\u8bfb\u8fc7\u4e66\uff0c\u4e5f\u4f1a\u4e00\u70b9\u6b66\u827a\uff0c \u56e0\u6b64\u6211\u548c\u4ed6\u6bd4\u8f83\u8c08\u5f97\u6765\uff0c\u65e5\u5b50\u4e00\u957f\uff0c\u81ea\u81ea\u7136\u7136\u5730\u5c31\u6210\u4e86\u597d\u670b\u53cb\u4e86\u3002\u8499\u53e4\u9791\u5b50\u53ea\u77e5\u6211\u662f\u516b\u767e\u4e03 \u5341\u4e09\u53f7\uff0c\u4ed6\u662f\u516b\u767e\u4e03\u5341\u56db\u53f7\u3002\u674e\u5e0c\u6d69\u548c\u4f59\u4e00\u4e2d\u8fd9\u4e24\u4e2a\u540d\u5b57\uff0c\u90a3\u65f6\u9791\u5b50\u4eec\u8fd8\u662f\u4e0d\u77e5\u9053\u7684\u3002\u201d \u3000\u3000\u674e\u5e0c\u6d69\u559d\u4e86\u4e00\u53e3\u53c2\u6c64\uff0c\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u57a6\u8352\u751f\u6d3b\uff0c\u82e6\u4e0d\u582a\u8a00\u3002\u4fd8\u864f\u8425\u4e2d\uff0c\u56fa\u7136\u4e5f\u6709\u8d2a\u751f\u6015 \u6b7b\u4e4b\u8f88\uff0c\u4f46\u66f4\u591a\u7684\u5374\u662f\u4e0d\u7518\u53d7\u9791\u5b50\u51cc\u8fb1\u4e4b\u4eba\u3002\u4e8e\u662f\u6211\u5c31\u79d8\u5bc6\u8054\u7edc\u4e86\u4e00\u73ed\u4eba\uff0c\u8ba1\u5212\u9003\u8d70\uff0c\u5176\u4e2d \u4e5f\u6709\u8fd9\u4f59\u4e00\u4e2d\u5728\u5185\u3002 \u3000\u3000\u201c\u6211\u548c\u4f59\u4e00\u4e2d\u7a0d\u4e3a\u61c2\u5f97\u4e00\u70b9\u6b66\u529f\uff0c\u88ab\u63a8\u4e3a\u9996\u9886\uff0c\u6211\u4eec\u51c6\u5907\u5206\u4e3a\u4e24\u6279\u9003\u8d70\uff0c\u7b2c\u4e00\u6279\u9003\u8d70\u6210 \u529f\uff0c\u7b2c\u4e8c\u6279\u8ddf\u7740\u4fbf\u9003\u3002\u56e0\u4e3a\u4eba\u6570\u5982\u679c\u592a\u591a\uff0c\u96be\u4ee5\u7792\u8fc7\u654c\u4eba\u8033\u76ee\u3002\u6240\u4ee5\u5fc5\u987b\u5206\u5f00\u884c\u52a8\u3002\u6211\u4eec\u7684 \u8ba1\u5212\u672c\u6765\u662f\u76f8\u5f53\u5468\u5bc6\u7684\uff0c\u9884\u6599\u7b2c\u4e00\u6279\u4e00\u9003\u51fa\u8425\u5730\uff0c\u53ef\u80fd\u4fbf\u7ed9\u9791\u5b50\u53d1\u73b0\uff0c\u5176\u65f6\u9791\u5b50\u5fc5\u5b9a\u8981\u62bd\u51fa \u5927\u6279\u4eba\u529b\u8ffd\u6355\uff0c\u7b2c\u4e8c\u6279\u8ddf\u7740\u4fbf\u9003\uff0c\u5c31\u5bb9\u6613\u591a\u4e86\u3002\u800c\u7b2c\u4e8c\u6279\u4e00\u9003\uff0c\u53c8\u53ef\u4ee5\u5f15\u5f97\u9791\u5b50\u5206\u5175\uff0c\u5148\u9003 \u7684\u4eba\uff0c\u4e5f\u53ef\u4ee5\u51cf\u8f7b\u538b\u529b\u3002 \u3000\u3000\u201c\u5f53\u7136\u4efb\u4f55\u5468\u5bc6\u7684\u8ba1\u5212\u90fd\u662f\u4e00\u5b9a\u8981\u6709\u5192\u9669\u7684\u6210\u5206\uff0c\u5148\u9003\u3001\u540e\u9003\uff0c\u90fd\u5f97\u62c5\u5f53\u98ce\u9669\u3002\u5f53\u665a\u62c8 \u7b79\u51b3\u5b9a\u9003\u8d70\u7684\u6b21\u5e8f\uff0c\u7ed3\u679c\u662f\u7531\u4f59\u4e00\u4e2d\u7387\u9886\u7b2c\u4e00\u6279\u5148\u9003\uff0c\u6211\u5219\u4f5c\u7b2c\u4e8c\u6279\u9996\u9886\u3002 \u3000\u3000\u201c\u51fa\u4e4e\u610f\u6599\u4e4b\u5916\uff0c\u7b2c\u4e00\u6279\u9003\u51fa\u8425\u5730\u4e4b\u540e\uff0c\u9791\u5b50\u53d1\u73b0\u4e86\uff0c\u5e76\u4e0d\u6d3e\u5175\u8ffd\u8d76\uff0c\u5374\u7acb\u5373\u5c01\u9501\u4e86\u51fa \u53e3\uff0c\u7b2c\u4e8c\u6279\u51c6\u5907\u9003\u8d70\u7684\u4eba\uff0c\u4e00\u4e2a\u4e5f\u9003\u4e0d\u51fa\u53bb\u3002 \u3000\u3000\u201c\u5148\u9003\u7684\u4eba\u672a\u8fc7\u5e93\u4f26\u6c60\uff0c\u8499\u53e4\u7684\u53e6\u4e00\u80a1\u9a91\u5175\u5df2\u7ecf\u5f00\u5230\u90a3\u91cc\u7b49\u5019\u4ed6\u4eec\u4e86\u3002\u7ed3\u679c\u7b2c\u4e00\u6279\u9003\u8d70 \u7684\u4eba\u7adf\u88ab\u654c\u4eba\u4e00\u7f51\u6253\u5c3d\uff0c\u6b7b\u7684\u6b7b\u4e86\uff0c\u4f24\u7684\u4f24\u4e86\uff0c\u4fa5\u5e78\u6ca1\u53d7\u4f24\u7684\u4e5f\u90fd\u7ed9\u6349\u4e86\u56de\u6765\uff0c\u4f59\u4e00\u4e2d\u5c31\u662f \u2018\u4fa5\u5e78\u2019\u6ca1\u4f24\uff0c\u88ab\u6349\u56de\u6765\u7684\u4fd8\u864f\u4e4b\u4e00\u3002 \u3000\u3000\u201c\u9791\u5b50\u626c\u8a00\u8981\u628a\u6349\u56de\u6765\u7684\u4eba\u5c3d\u6570\u5904\u65a9\uff0c\u9664\u975e\u4ed6\u4eec\u4f9b\u51fa\u4e3b\u8c0b\u4e4b\u4eba\u3002\u6211\u633a\u8eab\u800c\u51fa\uff0c\u76f4\u8ba4\u4e0d \u8bb3\u3002\u9791\u5b50\u7528\u9177\u5211\u8feb\u6211\u4f9b\u51fa\u540c\u515a\uff0c\u6211\u95ed\u53e3\u4e00\u5b57\u4e0d\u8bf4\uff0c\u7ed9\u4ed6\u4eec\u6253\u5f97\u6b7b\u53bb\u6d3b\u6765\u3002 \u3000\u3000\u201c\u9791\u5b50\u4ece\u6211\u7684\u53e3\u4e2d\u5f97\u4e0d\u5230\u534a\u70b9\u4e1c\u897f\uff0c\u4e8e\u662f\u628a\u6211\u56da\u7981\u8d77\u6765\u3002\u6211\u5df2\u7ecf\u4f24\u5f97\u4e0d\u80fd\u52a8\u5f39\uff0c\u4ed6\u4eec\u8ba4 \u4e3a\u6211\u662f\u51b3\u8ba1\u4e0d\u80fd\u9003\u8d70\u7684\u4e86\uff0c\u56e0\u6b64\u5e76\u65e0\u7279\u522b\u6d3e\u51fa\u770b\u5b88\u3002\u53ea\u4e0d\u8fc7\u6bcf\u9694\u4e00\u4e9b\u65e5\u5b50\u5c31\u6765\u97ad\u6253\u6211\u4e00\u987f\uff0c \u8981\u6211\u59cb\u7ec8\u5904\u4e8e\u534a\u6b7b\u4e0d\u6d3b\u7684\u72b6\u6001\u4e2d\uff0c\u4e5f\u5e0c\u671b\u6211\u88ab\u6253\u5f97\u4e0d\u80fd\u5fcd\u53d7\u4e4b\u65f6\uff0c\u4f1a\u5bf9\u4ed6\u4eec\u5c48\u670d\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u864e\u76ee\u6d41\u6cea\uff0c\u54ac\u7259\u8bf4\u9053\uff1a\u201c\u9791\u5b50\u7684\u624b\u6bb5\u5982\u6b64\u72e0\u8fa3\uff0c\u771f\u662f\u53ef\u6c14\uff0c\u53ef\u6068\uff0c\u53ef\u6740\uff01\u4e0d\u8fc7\uff0c \u4ed6\u4eec\u6ca1\u6709\u6d3e\u4eba\u7279\u522b\u770b\u5b88\uff0c\u53ea\u6015\u5176\u4e2d\u8fd8\u6709\u8be1\u8ba1\uff0c\u4e0d\u4e00\u5b9a\u662f\u56e0\u4e3a\u7239\u7239\u4f24\u91cd\u4e4b\u6545\u3002\u201d \u3000\u3000\u674e\u5e0c\u6d69\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u4f60\u6bd4\u6211\u806a\u660e\uff0c\u6211\u5f53\u65f6\u5374\u6ca1\u6709\u60f3\u5230\u8fd9\u4e00\u5c42\uff0c\u4ee5\u81f4\u53d7\u4e86\u5c0f\u4eba\u7684\u6697\u7b97\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u8fd9\u5c0f\u4eba\u4e00\u5b9a\u662f\u4f59\u4e00\u4e2d\u4e86\uff1f\u201d \u3000\u3000\u674e\u5e0c\u6d69\u9053\uff1a\u201c\u4e0d\u9519\u3002\u4ed6\u5077\u5077\u5730\u6765\u770b\u8fc7\u6211\u597d\u51e0\u6b21\uff0c\u6bcf\u4e00\u6b21\u90fd\u5e26\u4e86\u98df\u7269\u548c\u836f\u6765\uff0c\u8fd9\u4e9b\u836f\u867d\u4e0d \u80fd\u533b\u597d\u6211\u7684\u75c5\uff0c\u5374\u80fd\u4ee4\u6211\u82df\u5ef6\u6b8b\u5598\u3002\u5f53\u65f6\u6211\u5e76\u4e0d\u77e5\u9053\u4ed6\u7684\u5c45\u5fc3\uff0c\u5bf9\u4ed6\u8fd8\u662f\u5341\u5206\u611f\u6fc0\u7684\u3002\u4ed6\u6bcf \u6b21\u5230\u6765\uff0c\u53c8\u90fd\u662f\u4f5c\u51fa\u4e49\u6124\u586b\u81ba\u7684\u6837\u5b50\uff0c\u53e3\u53e3\u58f0\u58f0\u8bf4\u662f\u4ed6\u8981\u53bb\u81ea\u9996\uff0c\u597d\u51cf\u8f7b\u6211\u7684\u2018\u7f6a\u8d23\u2019\uff0c\u5426 \u5219\u8981\u6b7b\u4e5f\u4e00\u540c\u6b7b\u3002\u6211\u611f\u6fc0\u4ed6\u7684\u2018\u4e49\u6c14\u2019\uff0c\u8d39\u5c3d\u53e3\u820c\uff0c\u529d\u963b\u4e86\u4ed6\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u8fd9\u5978\u8d3c\u7684\u9a97\u672f\u5982\u6b64\u5de7\u5999\uff0c\u96be\u602a\u7239\u7239\u628a\u4ed6\u5f53\u4f5c\u4e86\u597d\u4eba\u3002\u7239\uff0c\u4f60\u662f\u4ec0\u4e48\u65f6\u5019\u624d \u8bc6\u7834\u4ed6\u7684\u771f\u9762\u76ee\u7684\uff1f\u201d \u3000\u3000\u674e\u5e0c\u6d69\u6b47\u4e86\u4e00\u4f1a\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u6b21\u9003\u4ea1\u4e8b\u60c5\u4e4b\u540e\uff0c\u5927\u7ea6\u8fc7\u4e86\u534a\u5e74\u5149\u666f\uff0c\u8499\u53e4\u9791\u5b50\u5bf9\u6c49\u4eba\u4fd8 \u864f\u7684\u6001\u5ea6\u5ffd\u7136\u6709\u4e86\u5927\u5927\u7684\u6539\u53d8\uff0c\u6253\u9a82\u8d8a\u6765\u8d8a\u5c11\uff0c\u5c0f\u6069\u5c0f\u60e0\u7684\u65bd\u4e0e\u5219\u8d8a\u6765\u8d8a\u591a\u3002\u770b\u5f97\u51fa\u8499\u53e4\u9791 \u5b50\u662f\u6709\u5fc3\u62c9\u62e2\u54b1\u4eec\u6c49\u4eba\u3002 \u3000\u3000\u201c\u4e0d\u4e45\uff0c\u4fd8\u864f\u8425\u7684\u62ef\u5b50\u5b98\u51fa\u4e86\u4e00\u5f20\u544a\u793a\uff0c\u8bf4\u662f\u51e1\u6709\u4e00\u6280\u4e4b\u957f\u7684\u4eba\uff0c\u613f\u610f\u7ed9\u4ed6\u4eec\u505a\u4e8b\u7684\u90fd \u53ef\u4ee5\u53bb\u767b\u8bb0\uff0c\u767b\u8bb0\u4e4b\u540e\uff0c\u7acb\u5373\u53ef\u4ee5\u4ece\u4fd8\u864f\u8425\u4e2d\u91ca\u51fa\uff0c\u9001\u5230\u548c\u6797\uff0c\u5206\u914d\u529f\u4f5c\u3002\u6709\u4e9b\u4eba\u53d7\u4e0d\u7740\u8bf1 \u60d1\uff0c\u8dd1\u53bb\u767b\u8bb0\uff0c\u4e5f\u679c\u7136\u5f97\u5230\u4e86\u91ca\u653e\u3002 \u3000\u3000\u201c\u9791\u5b50\u6539\u53d8\u653f\u7b56\u7684\u539f\u56e0\uff0c\u4e0d\u4e45\u6211\u4eec\u4e5f\u77e5\u9053\u4e86\uff0c\u539f\u6765\u8499\u53e4\u662f\u5728\u8ba1\u5212\u548c\u5357\u5b8b\u8054\u76df\u4f10\u91d1\uff0c\u5b83\u8981 \u5229\u7528\u54b1\u4eec\u6c49\u4eba\u3002 \u3000\u3000\u201c\u4f59\u4e00\u4e2d\u5e76\u6ca1\u6709\u8dd1\u53bb\u767b\u8bb0\u3002\u6211\u5219\u8fd8\u662f\u50cf\u5f80\u5e38\u4e00\u6837\uff0c\u4ecd\u7136\u662f\u7ed9\u9791\u5b50\u56da\u7981\uff0c\u5341\u5929\u516b\u5929\u5c31\u53d7\u4e00 \u987f\u97ad\u6253\u3002\u4ed6\u4eec\u5bf9\u5f85\u522b\u7684\u4fd8\u864f\u5ba2\u6c14\u4e86\uff0c\u5bf9\u6211\u53ef\u6ca1\u6709\u653e\u677e\u3002 \u3000\u3000\u201c\u6ca1\u6709\u653e\u677e\uff0c\u4f46\u4e5f\u6ca1\u6709\u52a0\u7d27\u770b\u7ba1\uff0c\u7531\u4e8e\u522b\u7684\u4fd8\u864f\u770b\u7ba1\u5f97\u6bd4\u8f83\u677e\u4e86\uff0c\u6709\u4e9b\u80c6\u5b50\u5927\u7684\u670b\u53cb\u4e5f \u5077\u5077\u5730\u6765\u770b\u6211\uff0c\u6211\u77e5\u9053\u591a\u4e86\u4e00\u4e9b\u5916\u95f4\u7684\u6d88\u606f\u3002\u6211\u53eb\u4ed6\u4eec\u63ed\u7834\u9791\u5b50\u7684\u9634\u8c0b\uff0c\u53eb\u540c\u4f34\u4e0d\u53ef\u4e0a\u5f53\u3002 \u542c\u6211\u529d\u544a\u7684\u90a3\u4e9b\u4eba\u4e4b\u4e2d\uff0c\u5f53\u7136\u4e5f\u5305\u62ec\u4e86\u4f59\u4e00\u4e2d\u5728\u5185\u3002 \u3000\u3000\u201c\u6709\u4e00\u5929\uff0c\u7a81\u7136\u6765\u4e86\u4e00\u4e2a\u6d88\u606f\uff0c\u9791\u5b50\u5728\u8fd9\u4e2a\u4fd8\u864f\u8425\u4e2d\u67e5\u8be2\uff0c\u67e5\u95ee\u6709\u6ca1\u6709\u674e\u5e0c\u6d69\u8fd9\u4e2a \u4eba\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u7239\u7239\uff0c\u4ed6\u4eec\u600e\u4e48\u77e5\u9053\u4f60\u7684\uff1f\u201d \u3000\u3000\u674e\u5e0c\u6d69\u9053\uff1a\u201c\u542c\u8bf4\u662f\u56e0\u4e3a\u6210\u5409\u601d\u6c57\u8981\u5ef6\u63fd\u4eba\u624d\uff0c\u6211\u4ee5\u524d\u5f85\u8fc7\u7684\u4fd8\u864f\u8425\u4e2d\u6709\u4eba\u544a\u5bc6\uff0c\u8bf4\u662f \u6709\u674e\u5e0c\u6d69\u8fd9\u4e48\u4e00\u4e2a\u4eba\uff0c\u662f\u5c06\u95e8\u4e4b\u5b50\uff0c\u5f88\u6709\u672c\u9886\uff0c\u6240\u4ee5\u6210\u5fd7\u601d\u6c57\u8981\u628a\u6211\u627e\u51fa\u6765\u7ed9\u4ed6\u505a\u4e8b\u3002\u201d \u3000\u3000\u201c\u6211\u8bf4\u8fc7\uff0c\u6c49\u4eba\u4fd8\u864f\u90fd\u662f\u7f16\u4e86\u53f7\u6570\u4e0d\u7528\u539f\u6765\u7684\u540d\u5b57\u7684\u3002\u6211\u4e5f\u4e0d\u613f\u610f\u522b\u4eba\u77e5\u9053\u6211\u7684\u540d\u5b57\uff0c \u56e0\u6b64\u5373\u4f7f\u540c\u662f\u4fd8\u864f\u8425\u4e2d\u7684\u96be\u53cb\uff0c\u77e5\u9053\u6211\u7684\u540d\u5b57\u4e5f\u53ea\u662f\u5ed6\u5ed6\u6570\u4eba\u3002\u4f59\u4e00\u4e2d\u662f\u5176\u4e2d\u4e4b\u4e00\u3002\u540e\u6765\u6211 \u53c8\u77e5\u9053\uff0c\u5728\u8fd9\u4e2a\u6d88\u606f\u53d1\u5e03\u4e4b\u540e\uff0c\u51e0\u4e2a\u77e5\u9053\u6211\u7684\u771f\u540d\u5b9e\u59d3\u7684\u4eba\uff0c\u51e0\u5929\u4e4b\u5185\uff0c\u4e00\u4e2a\u4e2a\u7684\u79bb\u5947\u66b4 \u6bd9\u3002\u4fd8\u864f\u8425\u4e2d\uff0c\u6b7b\u4eba\u4e4b\u4e8b\uff0c\u6781\u662f\u5bfb\u5e38\uff0c\u9791\u5b50\u4e5f\u6ca1\u6709\u67e5\u7a76\u3002\u6211\u5f53\u65f6\u4e5f\u4e0d\u77e5\u9053\uff0c\u53ea\u89c9\u5f97\u8fd9\u51e0\u4e2a\u670b \u53cb\u6ca1\u6709\u6765\u770b\u6211\uff0c\u6211\u6709\u70b9\u5947\u602a\u800c\u5df2\u3002 \u3000\u3000\u201c\u4f59\u4e00\u4e2d\u5f53\u7136\u6ca1\u6709\u6b7b\uff0c\u4ed6\u5bf9\u6211\u7684\u2018\u7167\u987e\u2019\u66f4\u5468\u5230\u4e86\u3002 \u3000\u3000\u6709\u4e00\u5929\u665a\u4e0a\uff0c\u4ed6\u5355\u72ec\u6765\u770b\u6211\u3001\u529d\u6211\uff0c\u8bf4\u662f\u65e2\u7136\u6709\u8fd9\u6837\u4e00\u4e2a\u673a\u4f1a\uff0c\u4f55\u4e0d\u627f\u8ba4\u81ea\u5df1\u7684\u8eab\u4efd\uff0c \u5047\u610f\u6295\u964d\uff0c\u5c11\u53d7\u75db\u82e6\uff1f\u517b\u597d\u4e86\u8eab\u4f53\uff0c\u90a3\u65f6\u9003\u8d70\u4e5f\u8fd8\u4e0d\u8fdf\u3002 \u3000\u3000\u201c\u6211\u5f53\u7136\u4e0d\u80af\u4f9d\u4ece\uff0c\u8d23\u5907\u4e86\u4ed6\u4e00\u987f\uff0c\u6211\u8bf4\u6211\u529d\u522b\u4eba\u4e0d\u53ef\u3002\u4e0a\u9791\u5b50\u7684\u5f53\uff0c\u6211\u53c8\u5c82\u53ef\u7ed9\u81ea\u5df1 \u627e\u4e2a\u85c9\u53e3\uff0c\u82df\u56fe\u6d3b\u547d\uff1f\u6211\u662f\u5b81\u53ef\u6b7b\uff1b\u4e5f\u4e0d\u80fd\u73b7\u6c61\u81ea\u5df1\u7684\u6c14\u8282\u7684\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u62cd\u638c\u9053\uff1a\u201c\u7239\u7239\u9a82\u5f97\u597d\uff0c\u4f59\u4e00\u4e2d\u8fd9\u53ae\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u674e\u5e0c\u6d69\u9053\uff1a\u201c\u4ed6\u54c8\u54c8\u5927\u7b11\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u6124\u7136\u8bf4\u9053\uff1a\u201c\u54fc\uff0c\u4ed6\u4e0d\u77e5\u7f9e\u803b\uff0c\u8fd8\u5728\u54c8\u54c8\u5927\u7b11\uff1f\u4f46\u8fd9\u4e5f\u597d\uff0c\u5982\u6b64\u4e00\u6765\uff0c\u7239\u7239\u4e0d\u5c31 \u662f\u53ef\u4ee5\u8bc6\u7a7f\u4ed6\u4e86\uff1f\u201d \u3000\u3000\u674e\u5e0c\u6d69\u9053\uff1a\u201c\u4e0d\uff0c\u6211\u88ab\u4ed6\u9a97\u5f97\u66f4\u60e8\u4e86\u3002\u4ed6\u7b11\u8fc7\u4e4b\u540e\uff0c\u8bf4\u9053\uff1a\u201c\u5e0c\u6d69\uff0c\u4f60\u771f\u4e0d\u6127\u662f\u4e2a\u94c1\u950b \u951a\u7684\u597d\u6c49\u5b50\uff0c\u8001\u5b9e\u8bf4\uff0c\u6211\u662f\u6015\u4f60\u7684\u5fc3\u4e0d\u575a\u3001\u5fd7\u4e0d\u521a\uff0c\u6240\u4ee5\u7279\u5730\u8bd5\u63a2\u4f60\u7684\u3002\u73b0\u5728\u6211\u53ef\u4ee5\u653e\u5fc3 \u4e86\u3002\u4f46\u6211\u4e0d\u80fd\u8ba9\u4f60\u6b7b\u53bb\uff0c\u73b0\u5728\u9791\u5b50\u4e3a\u4e86\u7b3c\u7edc\u54b1\u4eec\u6c49\u4eba\uff0c\u8b66\u536b\u6ca1\u6709\u4ece\u524d\u4e25\u5bc6\uff0c\u6211\u5df2\u7ecf\u63a2\u6e05\u695a\u4e00 \u6761\u8def\u7ebf\uff0c\u4ece\u8fd9\u6761\u8def\u7ebf\u9003\u8dd1\uff0c\u867d\u7136\u4e0d\u80fd\u8bf4\u662f\u5168\u65e0\u5371\u9669\uff0c\u4f46\u6210\u529f\u7684\u5e0c\u671b\u5374\u662f\u5f88\u5927\u3002\u201d\u53ef\u53f9\u6211\u7ed9\u4ed6 \u8fd9\u4e48\u4e00\u8bf4\uff0c\u7adf\u7136\u5b8c\u5168\u76f8\u4fe1\u4e86\u4ed6\u3002\u6211\u8003\u8651\u7684\u53ea\u662f\u6084\u8fde\u7d2f\u4e86\u4ed6\uff0c\u6839\u672c\u5c31\u6ca1\u6709\u60f3\u5230\u4ed6\u8981\u6211\u9003\u8d70\u662f\u5426 \u8fd8\u6709\u9634\u8c0b\u3002 \u3000\u3000\u201c\u4ed6\u62cd\u8d77\u80f8\u819b\uff0c\u53d1\u8a93\u4e0e\u6211\u540c\u751f\u5171\u6b7b\uff0c\u8fd8\u8d23\u5907\u6211\uff1a\u201c\u5e0c\u6d69\uff0c\u5c31\u53ea\u8bb8\u4f60\u6177\u6168\u6350\u8eaf\uff0c\u4e0d\u8bb8\u6211\u4ece \u5bb9\u5c31\u4e49\u4e48\uff1f\u65e2\u7136\u6700\u591a\u53ea\u662f\u4e00\u6b7b\uff0c\u53c8\u4f55\u4e0d\u5192\u9669\u4e00\u8bd5\uff0c\u8981\u662f\u9003\u5f97\u51fa\u53bb\uff0c\u7559\u4e0b\u6709\u7528\u4e4b\u8eab\uff0c\u5c82\u4e0d\u80dc\u4e8e \u65e0\u58f0\u606f\u7684\u6b7b\u5728\u4fd8\u864f\u8425\u91cc\uff1f\u201d \u3000\u3000\u201c\u4ed6\u8bf4\u5f97\u6177\u6168\u6fc0\u6602\uff0c\u6211\u5374\u4e0d\u8fc7\u4ed6\u7684\u597d\u610f\u3002\u53ea\u597d\u8ba9\u4ed6\u80cc\u6211\u9003\u8d70\u3002\u8fd9\u6b21\u9003\u8d70\uff0c\u679c\u7136\u5f88\u987a\u5229\u5730 \u5c31\u9003\u51fa\u4e86\u4fd8\u864f\u8425\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff0c\u201c\u4ed6\u4e0d\u5411\u5f6c\u5b50\u544a\u5bc6\uff0c\u5374\u8981\u548c\u4f60\u4e00\u540c\u9003\u8d70\uff0c\u4ed6\u7684\u76ee\u7684\u7a76\u7adf\u4f55\u5728\uff1f\u201d \u3000\u3000\u674e\u5e0c\u6d69\u9053\uff1a\u201c\u544a\u5bc6\u4ed6\u53ef\u80fd\u5f97\u5230\u4e00\u4e9b\u8d4f\u8d50\uff0c\u4f46\u597d\u5904\u5374\u6ca1\u6709\u7ee7\u7eed\u9a97\u6211\u4e4b\u5927\u3002\u4f60\u542c\u6211\u8bf4\u4e0b \u53bb\u3002\u201d \u3000\u3000\u518d\u559d\u4e86\u4e00\u53e3\u53c2\u6c64\uff0c\u674e\u5e0c\u6d69\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u6211\u53d7\u5211\u592a\u91cd\uff0c\u8eab\u4f53\u672c\u6765\u5df2\u7ecf\u662f\u5341\u5206\u865a\u5f31\u7684\u4e86\uff0c\u8ddf \u4ed6\u9003\u8fdb\u8352\u5c71\u91cc\u53bb\uff0c\u5403\u91ce\u83dc\u3001\u4f4f\u5c71\u6d1e\u3002\u6211\u7684\u75c5\u8d8a\u53d1\u91cd\u4e86\u3002\u4ed6\u5411\u6211\u62b1\u6b49\uff0c\u8bf4\u662f\u65e9\u77e5\u5982\u6b64\uff0c\u4e0d\u9003\u8fd8 \u597d\u3002\u6211\u8bf4\uff1a\u201c\u4e0d\uff01\u53ea\u8981\u4e0d\u662f\u6b7b\u5728\u654c\u4eba\u624b\u671b\uff0c\u5c31\u662f\u6b7b\u4e86\uff0c\u6211\u4e5f\u6b7b\u5f97\u7791\u76ee\uff01\u7684\u786e\uff0c\u90a3\u65f6\u6211\u7684\u8089\u4f53 \u867d\u7136\u53d7\u82e6\uff0c\u7cbe\u795e\u5374\u662f\u6bd4\u5728\u4fd8\u864f\u8425\u4e2d\u6109\u5feb\u591a\u4e86\u3002\u56e0\u6b64\uff0c\u6211\u662f\u5341\u5206\u611f\u6fc0\u4ed6\u7684\u3002 \u3000\u3000\u201c\u6211\u4e0e\u4ed6\u2018\u76f8\u4f9d\u4e3a\u547d\u2019\uff0c\u4ed6\u662f\u6211\u552f\u4e00\u7684\u670b\u53cb\uff0c\u65e5\u5915\u76f8\u5bf9\uff0c\u6211\u6709\u4ec0\u4e48\u8bdd\u4e5f\u53ea\u6709\u548c\u4ed6\u53bb\u8bf4\uff0c \u75c5\u4e2d\u601d\u5bb6\uff0c\u4e0d\u77e5\u4e0d\u89c9\uff0c\u6211\u628a\u5bb6\u4e2d\u7684\u60c5\u5f62\u90fd\u544a\u8bc9\u4e86\u4ed6\u3002 \u3000\u3000\u201c\u6211\u7684\u75c5\u8d8a\u6765\u8d8a\u91cd\uff0c\u6211\u81ea\u77e5\u79bb\u6b7b\u4e0d\u8fdc\uff0c\u6211\u867d\u8bf4\u6b7b\u53ef\u7791\u76ee\uff0c\u5fc3\u4e2d\u5374\u8fd8\u662f\u6709\u4e24\u4ef6\u4e8b\u60c5\u7275\u6302 \u7684\u3002\u7b2c\u4e00\u662f\u4f60\uff0c\u7b2c\u4e8c\u662f\u90a3\u672c\u6211\u672a\u7f16\u6210\u7684\u5175\u4e66\u3002 \u3000\u3000\u201c\u6211\u544a\u8bc9\u4ed6\uff0c\u6211\u88ab\u4fd8\u7684\u65f6\u5019\uff0c\u4f60\u53ea\u6709\u4e09\u5c81\uff0c\u5982\u679c\u5728\u6218\u4e71\u4e4b\u4e2d\uff0c\u4f60\u4eec\u6bcd\u5b50\u4fa5\u5e78\u4e0d\u6b7b\u7684\u8bdd\uff0c \u4f60\u73b0\u5728\u5e94\u8be5\u662f\u4e8c\u5341\u4e09\u5c81\u7684\u5c11\u5e74\u4e86\u3002\u56e0\u6b64\u6211\u2018\u62dc\u6258\u2019\u4ed6\uff0c\u5e0c\u671b\u4ed6\u80fd\u591f\u5230\u6211\u7684\u6545\u4e61\u53bb\u8d70\u4e00\u8d9f\uff0c\u627e \u5230\u4f60\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u82e6\u7b11\u9053\uff0c\u201c\u602a\u4e0d\u5f97\u4ed6\u77e5\u9053\u6211\u7684\u5e74\u5c81\u751f\u8fb0\u3002\u4ed6\u662f\u627e\u5230\u4e86\u6211\uff0c\u6211\u5374\u4e5f\u56e0\u6b64\u53d7\u4ed6\u9a97 \u4e86\u3002\u201d \u3000\u3000\u674e\u5e0c\u6d69\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u7b2c\u4e8c\u4ef6\u6211\u6240\u6302\u5fc3\u4e4b\u4e8b\u5c31\u662f\u8fd9\u90e8\u5175\u4e66\u3002\u6211\u544a\u8bc9\u4f59\u4e00\u4e2d\uff0c\u8bf7\u4ed6\u627e\u7740\u4f60\u4eec \u6bcd\u5b50\u4e4b\u540e\uff0c\u5411\u4f60\u4eec\u53d6\u8fd9\u672c\u5175\u4e66\u3002\u8fd9\u6b21\u4f60\u53d7\u4e86\u8fd9\u5978\u8d3c\u4e4b\u9a97\uff0c\u4ed6\u6709\u6ca1\u6709\u5411\u4f60\u7d22\u53d6\u5175\u4e66\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u7b2c\u4e8c\u5929\u665a\u4e0a\uff0c\u4ed6\u5c31\u60f3\u9a97\u53d6\u6211\u8fd9\u672c\u5175\u4e66\u4e86\u3002\u5f53\u65f6\uff0c\u6211\u8fd8\u672a\u77e5\u4ed6\u662f\u5047\u5192\u7684\uff0c\u53ef \u662f\u6211\u5bf9\u4ed6\u7684\u4e3a\u4eba\u5df2\u6709\u6000\u7591\uff0c\u6240\u4ee5\u6211\u5c31\u8c0e\u8a00\u642a\u585e\u8fc7\u53bb\u3002\u4fa5\u5e78\u6ca1\u6709\u4e0a\u4ed6\u7684\u5f53\u3002\u201d \u3000\u3000\u674e\u5e0c\u6d69\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u6211\u7684\u539f\u610f\u662f\u8981\u4ed6\u53d6\u4e86\u5175\u4e66\u4e4b\u540e\uff0c\u8bf7\u4ed6\u628a\u8fd9\u672c\u5175\u4e66\u643a\u5f80\u6c5f\u5357\uff0c\u732e\u7ed9\u4e00 \u4f4d\u771f\u6b63\u80af\u6297\u654c\u7684\u5c06\u9886\uff0c\u4ee5\u4e86\u6211\u7684\u5fc3\u613f\uff0c\u53ef\u601c\u6211\u7adf\u7136\u7cca\u6d82\u5230\u8fd9\u79cd\u7530\u5730\uff0c\u4e00\u70b9\u4e5f\u4e0d\u77e5\u9053\u4ed6\u6b63\u662f\u60f3 \u628a\u6211\u7684\u5175\u4e66\u7a88\u4e3a\u5df1\u6709\uff0c\u4ee5\u4fbf\u5411\u9791\u5b50\u7684\u5927\u6c57\u9080\u529f\u3002\u6211\u7adf\u7136\u628a\u8fd9\u4e2a\u79d8\u5bc6\u8ba9\u4ed6\u77e5\u9053\uff0c\u8fd8\u90d1\u91cd\u5730\u2018\u62dc \u6258\u2019\u4e86\u4ed6\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u867d\u7136\u77e5\u9053\u7236\u4eb2\u6ca1\u6709\u7ed9\u4f59\u4e00\u4e2d\u5bb3\u6b7b\uff0c\u542c\u5230\u8fd9\u91cc\uff0c\u4e5f\u4e0d\u7981\u5931\u58f0\u60ca\u547c\uff1a\u201c\u54ce\u5440\uff01\u7239\u4f60\u628a \u6240\u6709\u7684\u79d8\u5bc6\u90fd\u544a\u8bc9\u4e86\u4ed6\uff0c\u8fd9\u53ef\u771f\u662f\u5371\u9669\u6781\u4e86\uff01\u201d \u3000\u3000\u674e\u5e0c\u6d69\u53f9\u4e86\u53e3\u6c14\uff0c\u58f0\u97f3\u4f4e\u6c89\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u6599\u5f97\u4e00\u70b9\u4e0d\u9519\u3002\u4ed6\u5957\u53d6\u4e86\u6211\u7684\u5168\u90e8\u79d8\u5bc6\u4e4b\u540e\uff0c \u5ffd\u5730\u5c31\u9762\u8272\u4e00\u53d8\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5e0c\u6d69\uff0c\u53cd\u6b63\u4f60\u662f\u8981\u6b7b\u7684\u4e86\uff0c\u8fdf\u6b7b\u65e9\u6b7b\u90fd\u662f\u4e00\u6837\u3002\u6211\u6ca1\u6709\u529f\u592b \u5728\u8fd9\u8352\u5c71\u518d\u966a\u4f60\u53d7\u82e6\u4e86\uff0c\u4e0d\u5982\u65e9\u65e9\u9001\u4f60\u5f52\u897f\uff0c\u7ed9\u4f60\u4e00\u4e2a\u5927\u89e3\u8131\u5427\uff01\u201d\u8bf4\u7f62\uff0c\u53cc\u624b\u7d27\u7d27\u627c\u7740\u6211 \u7684\u5589\u5499\uff0c\u6211\u900f\u4e0d\u8fc7\u6c14\u6765\uff0c\u53ea\u542c\u5f97\u4ed6\u8fd8\u5728\u7b11\u9053\uff1a\u201c\u770b\u5728\u8001\u670b\u53cb\u7684\u4efd\u4e0a\uff0c\u6211\u8ba9\u4f60\u843d\u4e2a\u5168\u5c38\u3002\u4e5f\u7b97 \u5bf9\u5f97\u8d77\u4f60\u4e86\u3002\u201d \u3000\u3000\u201c\u8f6c\u773c\u95f4\u6211\u5df2\u662f\u6c14\u7edd\u8109\u505c\uff0c\u65ad\u4e86\u547c\u5438\uff0c\u4eba\u4e8b\u4e0d\u7701\u3002\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u6211\u8ff7\u8ff7\u7cca\u7cca\u4e2d\u542c\u5f97 \u6c99\u6c99\u58f0\u54cd\uff0c\u4e0d\u77e5\u600e\u7684\uff0c\u53c8\u4f3c\u6709\u4e86\u4e00\u70b9\u77e5\u89c9\u3002\u60f3\u6765\u4ed6\u662f\u4ee5\u4e3a\u6211\u65e9\u5df2\u65ad\u4e86\u6c14\uff0c\u6211\u2018\u4e34\u6b7b\u2019\u65f6\u90a3\u526f \u6124\u6068\u7684\u795e\u60c5\u4ee4\u5f97\u4ed6\u5bb3\u6015\uff0c\u4ed6\u624d\u677e\u5f00\u4e86\u624b\u7684\u3002 \u3000\u3000\u201c\u6211\u6062\u590d\u4e86\u4e00\u70b9\u77e5\u89c9\uff0c\u53d1\u89c9\u81ea\u5df1\u662f\u8eba\u5728\u4e00\u4e2a\u5751\u4e2d\uff0c\u4f59\u4e00\u4e2d\u8fd9\u5978\u8d3c\u6b63\u5728\u65c1\u8fb9\u94f2\u571f\uff0c\u4e0d\u7528\u8bf4 \u4ed6\u662f\u8981\u628a\u6211\u6d3b\u57cb\u7684\u4e86\u3002 \u3000\u3000\u201c\u6211\u53ea\u662f\u6062\u590d\u4e86\u4e00\u70b9\u77e5\u89c9\uff0c\u8eab\u5b50\u4e0d\u80fd\u52a8\u5f39\uff0c\u53eb\u4e5f\u53eb\u4e0d\u51fa\u58f0\uff0c\u4f46\u4e5f\u597d\u5728\u6211\u53eb\u4e0d\u51fa\u58f0\uff0c\u5982\u679c \u53eb\u51fa\u58f0\u6765\uff0c\u8fd9\u5978\u8d3c\u4e00\u5b9a\u628a\u6211\u6740\u4e86\u3002 \u3000\u3000\u201c\u8fd9\u5978\u8d3c\u4e00\u9762\u94f2\u4e0a\uff0c\u4e00\u9762\u8fd8\u5728\u5f97\u610f\u5730\u7b11\uff1a\u201c\u5e0c\u6d69\uff0c\u4f60\u6210\u5168\u6211\u7684\u5bcc\u8d35\u529f\u540d\uff0c\u6211\u7ed9\u4f60\u63a9\u57cb\u5c38 \u4f53\uff0c\u514d\u4f60\u505a\u4e86\u5140\u9e70\u7684\u98df\u7269\uff0c\u4f60\u4e5f\u5e94\u8be5\u611f\u6fc0\u6211\u4e86\u3002\u201d\u6211\u77e5\u9053\u4ed6\u63a9\u57cb\u6211\u7684\u5c38\u4f53\uff0c\u53ea\u662f\u4e0d\u60f3\u8ba9\u4eba\u53d1 \u73b0\u800c\u5df2\u3002\u4ed6\u6ca1\u6cd5\u5c06\u6211\u7684\u5c38\u4f53\u5b8c\u5168\u6bc1\u706d\uff0c\u53ea\u6709\u8fd9\u4e2a\u6cd5\u5b50\uff0c\u6d3b\u57cb\u4e86\u6211\uff0c\u628a\u58eb\u586b\u5e73\u3002\u8fd8\u6709\u8c01\u4eba\u77e5\u9053 \u8352\u5c71\u4e4b\u4e2d\u6709\u8fd9\u4e00\u5177\u7ed9\u4eba\u8c0b\u6740\u7684\u5c38\u4f53\uff1f\u201d \u3000\u3000\u201c\u6211\u6c14\u6068\u5f97\u4e0d\u5f97\u4e86\uff0c\u9a82\u53c8\u9a82\u4e0d\u51fa\u6765\uff0c\u53ea\u542c\u5f97\u6c99\u6c99\u4e4b\u58f0\uff0c\u4f59\u4e00\u4e2d\u4e00\u94f2\u4e00\u94f2\u5730\u628a\u6ce5\u571f\u94f2\u5728\u6211 \u7684\u8eab\u4e0a\uff0c\u6df9\u6ca1\u4e86\u6211\u7684\u624b\uff0c\u6df9\u6ca1\u4e86\u6211\u7684\u811a\uff0c\u6df9\u6ca1\u4e86\u6211\u7684\u5934\uff0c\u773c\u775b\u4e00\u7247\u6f06\u9ed1\uff0c\u4e0d\u89c1\u5929\u5df2\u6574\u4e2a\u4eba\u90fd \u5c01\u95ed\u5728\u6ce5\u571f\u4e4b\u4e2d\u4e86\u3002\u6c99\u7684\u4e00\u94f2\uff0c\u6c99\u7684\u53c8\u662f\u4e00\u94f2\u2026\u2026\u201d \u3000\u3000\u674e\u601d\u5357\u542c\u5f97\u6bdb\u9aa8\u609a\u7136\uff0c\u53eb\u9053\uff1a\u201c\u7239\uff0c\u4e0d\u8981\u518d\u8bf4\u4e0b\u53bb\u4e86\u3002\u201d \u3000\u3000\u674e\u5e0c\u6d69\u82e6\u7b11\u9053\uff1a\u201c\u4f60\u6015\u4e86\u4e48\u6211\u5df2\u7ecf\u662f\u6b7b\u8fc7\u4e00\u6b21\u7684\u4eba\u4e86\uff0c\u5982\u4eca\u5012\u662f\u4e0d\u89c9\u5f97\u6b7b\u7684\u53ef\u6015\u4e86\u3002\u4f60 \u4e0d\u6562\u542c\u4e0b\u53bb\uff0c\u6211\u5c31\u7b80\u7565\u4ed6\u8bf4\u5427\u3002\u201d \u3000\u3000\u90a3\u5c11\u5973\u628a\u5269\u4f59\u7684\u53c2\u6c64\u90fd\u5012\u4e86\u51fa\u6765\uff0c\u8ba9\u674e\u5e0c\u6d69\u559d\u4e86\uff0c\u8bf4\u9053\uff1a\u201c\u7239\uff0c\u4f60\u6b47\u6b47\u518d\u8bf4\u3002\u201d \u3000\u3000\u674e\u5e0c\u6d69\u7b11\u9053\uff1a\u201c\u73b0\u5728\u8981\u8bf4\u5230\u4f60\u4eec\u4e86\u3002\u6211\u8bf4\u4e86\u8fd9\u4e00\u6bb5\uff0c\u4ee5\u540e\u7684\u4e8b\u60c5\uff0c\u5c31\u53ef\u4ee5\u8ba9\u4f60\u4eec\u8bf4 \u4e86\u3002\u201d \u3000\u3000\u674e\u5e0c\u6d69\u559d\u4e86\u53c2\u6c64\uff0c\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u90a3\u65f6\u6211\u4ee5\u4e3a\u53cc\u811a\u5df2\u8e0f\u8fdb\u4e86\u9b3c\u95e8\u5173\uff0c\u6b63\u5728\u95ed\u76ee\u5f85\u6b7b\uff0c\u5ffd\u542c \u5f97\u6709\u8bf4\u8bdd\u7684\u58f0\u97f3\uff0c\u968f\u5373\u53c8\u542c\u5f97\u6709\u6742\u4e71\u7684\u811a\u6b65\u58f0\u3002\u540e\u6765\u6211\u624d\u77e5\u9053\uff0c\u4f59\u4e00\u4e2d\u8fd9\u5978\u8d3c\u770b\u89c1\u6709\u4eba\u8d70 \u6765\uff0c\u5927\u7ea6\u662f\u4ee5\u4e3a\u6211\u65e9\u5df2\u6b7b\u4e86\uff0c\u6050\u6015\u7ed9\u6765\u4eba\u53d1\u73b0\u4e86\u4ed6\u5e72\u7684\u52fe\u5f53\uff0c\u5f53\u573a\u5c06\u4ed6\u6293\u4f4f\uff0c\u4e8e\u662f\u4fbf\u614c\u614c\u5f20 \u5f20\u5730\u9003\u8dd1\u4e86\u3002\u201d \u3000\u3000\u201c\u5e78\u4e8f\u6211\u8fd8\u6709\u4e00\u53e3\u6c14\uff0c\u5728\u9b3c\u95e8\u5173\u4e0a\u7ed9\u4eba\u62c9\u4e86\u56de\u6765\u3002\u5357\u513f\uff0c\u4f60\u5e94\u8be5\u77e5\u9053\u6551\u6211\u7684\u4eba\u662f\u8c01\u4e86 \u5427\uff1f\u5c31\u662f\u4ed6\u4eec\u5144\u59b9\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u8fd9\u624d\u77e5\u9053\u8fd9\u5c11\u5973\u4e43\u662f\u6768\u6ed4\u59b9\u59b9\uff0c\u8fde\u5fd9\u8dea\u4e0b\u53bb\u78d5\u5934\uff0c\u591a\u8c22\u4ed6\u4eec\u6551\u7236\u4e4b\u6069\uff0c\u6768\u6ed4\u6258\u4f4f \u4ed6\u7684\u8eab\u5b50\uff0c\u4e0d\u8ba9\u819d\u5934\u7740\u5730\uff0c\u8bf4\u9053\uff1a\u201c\u4e16\u4e0a\u5c82\u6709\u89c1\u6b7b\u4e0d\u6551\u4e4b\u7406\uff0c\u6211\u53ea\u662f\u505a\u4e86\u6211\u5e94\u8be5\u505a\u7684\u4e8b\u60c5\u7f62 \u4e86\u3002\u4f55\u51b5\u6211\u7684\u59b9\u59b9\u5c31\u662f\u4f60\u7684\u59b9\u59b9\uff0c\u4f60\u8c22\u6211\u4e00\u58f0\uff0c\u6211\u8fd8\u53ef\u4ee5\u52c9\u5f3a\u53d7\u4e0b\uff0c\u4f60\u82e5\u5411\u6211\u7684\u59b9\u59b9\u78d5\u5934\uff0c \u5374\u53eb\u5979\u5982\u4f55\u53d7\u5f97\u8d77\uff1f\u5bf9\u5566\uff0c\u6211\u8fd8\u6ca1\u6709\u544a\u8bc9\u4f60\u5462\uff0c\u820d\u59b9\u5355\u540d\u4e00\u4e2a\u2018\u5a49\u2019\u5b57\uff0c\u591a\u8499\u8001\u6015\u770b\u5f97\u8d77 \u5979\uff0c\u6536\u4e86\u5979\u505a\u5e72\u5973\u513f\u3002\u6211\u53ef\u6ca1\u6709\u5979\u7684\u798f\u6c14\uff0c\u60f3\u8ba4\u5e72\u7239\uff0c\u8001\u4f2f\u4e5f\u4e0d\u80af\u7b54\u5e94\u3002\u201d \u3000\u3000\u674e\u5e0c\u6d69\u7b11\u9053\uff1a\u201c\u6211\u6536\u4e86\u4e00\u4e2a\u5e72\u5973\u513f\uff0c\u5df2\u7ecf\u662f\u6298\u4e86\u6211\u7684\u798f\u5206\u4e86\u3002\u201d\u539f\u6765\u6768\u6ed4\u7684\u5e74\u9f84\u6bd4\u59b9\u59b9 \u5927\u5341\u5c81\u6709\u591a\uff0c\u674e\u5e0c\u6d69\u53ef\u4ee5\u8ba4\u4ed6\u7684\u59b9\u59b9\u505a\u5e72\u5973\u513f\uff0c\u4f46\u82e5\u4e0e\u4ed6\u4ee5\u7236\u5b50\u76f8\u79f0\uff0c\u5728\u5e74\u9f84\u4e0a\u5219\u662f\u4e0d\u76f8\u79f0 \u7684\u3002 \u3000\u3000\u674e\u5e0c\u6d69\u8bf4\u4e86\u8fd9\u53e5\u8bdd\uff0c\u5ffd\u5730\u6b63\u8272\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u6551\u6211\uff0c\u56fa\u7136\u662f\u4f60\u4eec\u8ba4\u4e3a\u5f53\u4e3a\u4e4b\u4e8b\uff1b\u5357\u513f\u5411\u4f60 \u7684\u59b9\u59b9\u78d5\u5934\uff0c\u8fd9\u4e5f\u662f\u5e94\u8be5\u7684\uff01\u5357\u513f\uff0c\u6211\u544a\u8bc9\u4f60\uff0c\u6211\u80fd\u591f\u6d3b\u5230\u73b0\u5728\uff0c\u5168\u662f\u9760\u4f60\u7684\u5a49\u59b9\u3002\u8fd9\u534a\u5e74 \u6765\uff0c\u5979\u8863\u4e0d\u89e3\u5e26\u5730\u670d\u4f8d\u6211\uff0c\u6211\u8fd9\u4e2a\u5973\u513f\u5f53\u771f\u662f\u6bd4\u4eb2\u751f\u7684\u5973\u513f\u8fd8\u4eb2\uff01\u201d \u3000\u3000\u201c\u6ed4\u4f84\uff0c\u4f60\u4e0d\u8981\u963b\u62e6\u4ed6\u4e86\uff0c\u4ed6\u4e0d\u66ff\u6211\u78d5\u8fd9\u4e2a\u5934\uff0c\u6211\u7684\u5fc3\u4e5f\u4e0d\u80fd\u5b89\u7136\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u6323\u8131\u4e86\u6768\u6ed4\u7684\u624b\uff0c\u7acb\u5373\u8dea\u4e0b\u53bb\u7ed9\u6768\u5a49\u78d5\u5934\u3002\u6768\u5a49\u4e0d\u597d\u610f\u601d\u6276\u4ed6\u8d77\u6765\uff0c\u7f9e\u5f97\u6ee1\u9762\u901a \u7ea2\uff0c\u53ea\u597d\u4e5f\u8dea\u4e0b\u53bb\u7ed9\u674e\u601d\u5357\u78d5\u5934\u8fd8\u793c\u3002 \u3000\u3000\u674e\u5e0c\u6d69\u4e50\u5f97\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4e5f\u597d\uff0c\u96be\u5f97\u4f60\u4eec\u76f8\u656c\u5982\u5bbe\u3002\u4f60\u4eec\u5c31\u5728\u6211\u7684\u9762\u524d\u8ba4\u4e86\u5144\u59b9\u5427\uff0c\u4e5f \u597d\u53eb\u6211\u9ad8\u5174\u9ad8\u5174\u3002\u201d \u3000\u3000\u8fd9\u201c\u76f8\u656c\u5982\u5bbe\u201d\u56db\u5b57\uff0c\u6768\u6ed4\u8bfb\u4e66\u4e0d\u591a\uff0c\u8fd8\u4e0d\u611f\u5230\u523a\u8033\uff1b\u674e\u601d\u5357\u542c\u4e86\uff0c\u53ef\u662f\u751a\u611f\u5c34\u5c2c\uff0c\u970e \u65f6\u95f4\u8138\u90fd\u7ea2\u4e86\u3002\u8981\u77e5\u8fd9\u56db\u4e2a\u5b57\u662f\u53ea\u80fd\u7528\u5728\u592b\u5987\u4e4b\u95f4\u7684\uff0c\u5144\u59b9\u4e4b\u95f4\uff0c\u5c82\u80fd\u4e71\u7528\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u7ea2\u7740\u8138\u9053\uff1a\u201c\u591a\u8c22\u5a49\u59b9\u3002\u201d\u6768\u5a49\u9053\uff1a\u201c\u5357\u54e5\u6765\u4e86\uff0c\u8fd9\u53ef\u5c31\u597d\u4e86\u3002\u7239\u7239\u6700\u6302\u5ff5\u4f60\uff0c \u4f60\u8fd9\u4e00\u6765\uff0c\u80dc\u4e8e\u6cbb\u75c5\u7684\u7075\u4e39\uff0c\u7239\u7239\u5b9a\u53ef\u597d\u4e86\u3002\u201d\u674e\u601d\u5357\u9053\uff1a\u201c\u4f46\u613f\u5982\u6b64\u3002\u201d\u4ed6\u89c1\u6768\u5a49\u843d\u843d\u5927 \u65b9\uff0c\u81ea\u7136\u4e5f\u5c31\u6d88\u4e86\u7a98\u6001\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u7239\u7239\u75c5\u5f97\u7cca\u6d82\uff0c\u5076\u7136\u7528\u9519\u6210\u8bed\uff0c\u4ea6\u5c5e\u5bfb\u5e38\u3002\u6211\u82e5\u591a \u5fc3\uff0c\u53cd\u800c\u662f\u7740\u4e86\u75d5\u8ff9\u3002\u201d \u3000\u3000\u674e\u5e0c\u6d69\u5806\u6ee1\u7b11\u5bb9\uff0c\u8bf4\u900d\uff1a\u201c\u5982\u4eca\u6211\u53ea\u6709\u4e00\u6869\u5fc3\u613f\u8fd8\u672a\u66fe\u4e86\uff0c\u55ef\uff0c\u8fc7\u4e24\u5929\u518d\u548c\u4f60\u8bf4\u5427\u3002\u201d \u4ed6\u770b\u4e86\u770b\u513f\u5b50\uff0c\u53c8\u770b\u4e86\u770b\u6768\u5a49\uff0c\u5fc3\u4e2d\u82e5\u6709\u6240\u601d\uff0c\u795e\u60c5\u5fae\u9732\u5026\u6001\u3002 \u3000\u3000\u6768\u5a49\u8bf4\u9053\uff0c\u201c\u662f\u5440\u3001\u7239\uff0c\u4f60\u4eca\u5929\u8bf4\u4e86\u8bb8\u591a\u8bdd\uff0c\u4e5f\u8be5\u6b47\u6b47\u4e86\u3002\u201d \u3000\u3000\u674e\u5e0c\u6d69\u4e0d\u77e5\u662f\u7531\u4e8e\u592a\u8fc7\u75b2\u5026\u7684\u7f18\u6545\u8fd8\u662f\u56e0\u4e3a\u5fc3\u4e2d\u5df2\u65e0\u7275\u6302\uff0c\u95ed\u4e0a\u773c\u775b\uff0c\u679c\u7136\u4e0d\u4e45\u5c31\u7761\u7740 \u4e86\u3002 \u3000\u3000\u6768\u5a49\u4f4e\u58f0\u9053\uff1a\u201c\u7239\u7239\u5df2\u6709\u51e0\u665a\u6ca1\u6709\u597d\u7761\uff0c\u96be\u5f97\u4ed6\u8212\u8212\u670d\u670d\u5730\u7761\u4e00\u89c9\u3002\u54e5\u54e5\uff0c\u4eba\u53c2\u521a\u7528\u5b8c \u4e86\uff0c\u4f60\u518d\u53bb\u627e\u627e\u5427\u3002\u201d\u539f\u6765\u8fd9\u963f\u513f\u683c\u5c71\u76db\u4ea7\u91ce\u751f\u7684\u4eba\u53c2\uff0c\u674e\u5e0c\u6d69\u4e4b\u5f97\u4ee5\u82df\u5ef6\u6b8b\u5598\uff0c\u6d3b\u4e0b\u547d \u6765\uff0c\u90fd\u662f\u9760\u626c\u6ed4\u7ed9\u4ed6\u6398\u6765\u4e86\u91ce\u751f\u7684\u4eba\u53c2\u7eed\u547d\u4e4b\u529f\u3002 \u3000\u3000\u674e\u601d\u5357\u4e00\u6765\u662f\u89c9\u5f97\u4e0d\u597d\u610f\u601d\u548c\u6768\u5a49\u5355\u72ec\u76f8\u5904\uff0c\u4e8c\u6765\u4e5f\u6709\u4e9b\u4e8b\u60c5\u8981\u95ee\u6768\u6ed4\uff0c\u4e8e\u662f\u8bf4\u9053\uff1a \u201c\u5927\u54e5\uff0c\u6211\u548c\u4f60\u4e00\u540c\u53bb\u3002\u201d\u6768\u6ed4\u8bf4\u9053\uff1a\u201c\u597d\u5427\u6211\u627e\u4eba\u53c2\uff0c\u4f60\u5e2e\u6211\u62fe\u67f4\u8349\u3002\u201d \u3000\u3000\u5230\u4e86\u5bc6\u6797\u6df1\u5904\uff0c\u6768\u6ed4\u6ca1\u6709\u600e\u6837\u8d39\u529b\u5c31\u627e\u5230\u4e86\u4e00\u652f\u7c97\u5982\u513f\u81c2\u7684\u4eba\u53c2\uff0c\u7b11\u9053\uff1a\u201c\u5357\u5f1f\uff0c\u8fd9\u6b21 \u771f\u662f\u6258\u4f60\u7684\u798f\uff0c\u8fd9\u662f\u4e00\u652f\u8001\u5c71\u53c2\uff0c\u6211\u5165\u5c71\u4ee5\u6765\uff0c\u8fd8\u672a\u66fe\u5f97\u8fc7\u8fd9\u6837\u5927\u7684\u5462\u3002\u201d\u6398\u4e86\u4eba\u53c2\uff0c\u53c8\u6765 \u5e2e\u5fd9\u674e\u601d\u5357\u6346\u597d\u4e00\u5927\u6346\u7684\u67f4\u8349\u3002 \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u6768\u5927\u54e5\uff0c\u4f60\u7684\u5e08\u7236\u88f4\u5927\u4fa0\u662f\u5ce8\u5d4b\u6d3e\u7684\u638c\u95e8\u5f1f\u5b50\uff0c\u4f60\u5374\u600e\u4e48\u4f1a\u8dd1\u5230\u8499\u53e4\u6765\uff0c \u4f4f\u5728\u8fd9\u4e2a\u8352\u5c71\u4e4b\u4e0a\uff1f\u201d \u3000\u3000\u6768\u6ed4\u82e6\u7b11\u9053\uff1a\u201c\u8bf4\u6765\u8bdd\u957f\u3002\u6211\u548c\u4f60\u4e00\u6837\u662f\u5c06\u95e8\u4e4b\u540e\uff0c\u6211\u5bb6\u7684\u7b2c\u4e00\u4ee3\u7956\u5148\u5c31\u662f\u66fe\u7ecf\u8f85\u4f50\u592a \u5b97\u7687\u5e1d\u5f81\u8fbd\u3001\u4eba\u79f0\u2018\u6768\u4ee4\u516c\u2019\u7684\u6768\u7ee7\u4e1a\u3002\u201d \u3000\u3000\u674e\u6069\u5357\u53c8\u60ca\u53c8\u559c\uff0c\u8bf4\u9053\uff1a\u201c\u5927\u54e5\uff0c\u539f\u6765\u4f60\u662f\u6768\u5bb6\u5c06\u7684\u540e\u4eba\uff01\u201d\u6768\u5bb6\u5728\u5317\u5b8b\u4ee3\u51fa\u540d\u5c06\uff0c\u4ece \u6768\u7ee7\u4e1a\u5230\u6768\u5ef6\u662d\u3001\u6768\u6587\u5e7f\u7b49\u4eba\uff0c\u4e2a\u4e2a\u90fd\u66fe\u7edf\u7387\u91cd\u5175\uff0c\u9547\u5b88\u8fb9\u5173\uff0c\u4e3a\u671d\u5ef7\u62b5\u5fa1\u5916\u7978\uff0c\u4e8c\u767e\u5e74 \u6765\uff0c\u6c11\u95f4\u4e0d\u77e5\u6709\u591a\u5c11\u5173\u4e8e\u4ed6\u4eec\u7684\u4f20\u8bf4\u3002\u8bba\u8d77\u529f\u4e1a\u7684\u5f6a\u70b3\uff0c\u58f0\u5a01\u7684\u663e\u8d6b\uff0c\u674e\u601d\u5357\u8fd9\u4e00\u5bb6\u65cf\u662f\u8fdc \u8fdc\u4e0d\u80fd\u4e0e\u4e4b\u76f8\u6bd4\u7684\u3002 \u3000\u3000\u6768\u6ed4\u8bf4\u9053\uff1a\u201c\u81ea\u4ece\u5fbd\u3001\u94a6\u8499\u5c18\uff0c\u5b8b\u5ba4\u5357\u6e21\u4e4b\u540e\uff0c\u6211\u4eec\u8fd9\u4e00\u5bb6\u4eba\uff0c\u6709\u7684\u5728\u5317\u65b9\u57cb\u540d\u9690\u8ff9\u4e00 \u4e16\u6709\u4eba\u968f\u9ad8\u5b97\u5230\u4e86\u6c5f\u5357\u3002\u5148\u7956\u6ca1\u6709\u6e21\u6c5f\uff0c\u5230\u4e86\u6211\u7239\u7239\u8fd9\u4ee3\uff0c\u548c\u5357\u65b9\u7684\u5bb6\u4eba\u6d88\u606f\u9694\u7edd\u4e5f\u6709\u4e86\u51e0 \u5341\u5e74\u4e86\u3002 \u3000\u3000\u201c\u6211\u5341\u516b\u5c81\u90a3\u5e74\uff0c\u6709\u4eba\u77e5\u9053\u6211\u4eec\u662f\u6768\u5bb6\u7684\u540e\u4ee3\uff0c\u7239\u7239\u6050\u9632\u91d1\u864f\u52a0\u5bb3\uff0c\u628a\u6211\u7684\u7956\u6bcd\u548c\u5e7c\u59b9 \u5b89\u987f\u5728\u4e61\u4e0b\uff0c\u5e26\u4e86\u6211\u6295\u5954\u6c5f\u5357\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u4e0d\u80dc\u6b23\u7fa1\uff0c\u8bf4\u9053\uff1a\u201c\u5bb6\u7236\u7ed9\u6211\u547d\u540d\u2018\u601d\u5357\u2019\uff0c\u6211\u5982\u662f\u4e00\u76f4\u5230\u5982\u4eca\u8fd8\u672a\u66fe\u5230\u6c5f\u5357\uff0c \u62a5\u56fd\u65e0\u4ece\uff0c\u601d\u4e4b\u6709\u6127\uff01\u201d \u3000\u3000\u6768\u6ed4\u795e\u8272\u9eef\u7136\uff0c\u4f3c\u4e4e\u662f\u7ed9\u674e\u601d\u5357\u7684\u8bdd\u52fe\u8d77\u4e86\u6c89\u75db\u7684\u56de\u5fc6\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5230\u4e86\u6c5f\u5357\uff0c\u6700\u521d\u4f55 \u5c1d\u4e0d\u662f\u548c\u4f60\u4e00\u6837\u60f3\u6cd5\uff0c\u4ee5\u4e3a\u603b\u53ef\u4ee5\u4e3a\u56fd\u6548\u52b3\u4e86\u3002\u8c01\u77e5\u4e0d\u6d88\u591a\u4e45\uff0c\u6211\u8fd9\u9897\u706b\u70ed\u7684\u5fc3\uff0c\u5c31\u4e0d\u7531\u5f97 \u4e0d\u6e10\u6e10\u51b7\u5374\u4e86\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u60ca\u9053\uff1a\u201c\u8fd9\u5374\u6709\u4e3a\u4f55\uff1f\u201d \u3000\u3000\u6768\u6ed4\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u4f60\u542c\u8fc7\u8fd9\u9996\u8bd7\u5417\uff0c\u8fd9\u662f\u5728\u6c5f\u5357\u4f20\u8bf5\u4e00\u65f6\u7684\u8bd7\uff1a\u2018\u5c71\u5916\u9752\u5c71\u697c\u5916\u697c\uff0c\u897f \u6e56\u6b4c\u821e\u51e0\u65f6\u4f11\uff1f\u6696\u98ce\u718f\u5f97\u6e38\u4eba\u9189\uff0c\u76f4\u628a\u676d\u5dde\u4f5c\u5fed\u5dde\u3002\u2019\u5357\u5b8b\u541b\u81e3\uff0c\u803d\u4e8e\u9038\u4e50\uff0c\u65e9\u5df2\u5fd8\u8bb0\u4e86\u6ca6 \u9677\u5728\u91d1\u864f\u94c1\u9a91\u4e0b\u7684\u5927\u597d\u6cb3\u5c71\uff0c\u5fd8\u8bb0\u4e86\u6e34\u671b\u4e00\u89c1\u6545\u56fd\u65cf\u65d7\u7684\u4e2d\u539f\u7236\u8001\u4e86\u3002\u4ed6\u4eec\u628a\u676d\u5dde\u6539\u6210\u4e86 \u2018\u4e34\u5b89\u2019\uff0c\u4f60\u53ea\u4ece\u8fd9\u2018\u4e34\u5b89\u2019\u4e8c\u5b57\uff0c\u5c31\u4e0d\u96be\u60f3\u89c1\u4e00\u6591\uff0c\u6240\u8c13\u2018\u4e34\u5b89\u2019\uff0c\u5176\u5b9e\u4e5f\u5c31\u662f\u53ea\u56fe\u2018\u82df \u5b89\u2019\u800c\u5df2\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u96be\u9053\u6c5f\u5357\u5c31\u518d\u4e5f\u6ca1\u6709\u4e86\u50cf\u5cb3\u98de\u3001\u97e9\u4e16\u5fe0\u90a3\u6837\u7684\u6297\u654c\u5c06\u9886\u4e48\uff1f\u201d \u3000\u3000\u6768\u6ed4\u9053\uff1a\u201c\u6709\u662f\u6709\u7684\uff0c\u4f46\u53ef\u60dc\u7684\u662f\u4ed6\u4eec\u4e5f\u9003\u4e0d\u6389\u5cb3\u98de\u3001\u97e9\u4e16\u5fe0\u90a3\u6837\u7684\u547d\u8fd0\u3002\u4e0d\u662f\u906d\u5978\u81e3 \u9677\u5bb3\uff0c\u5c31\u662f\u88ab\u7687\u4e0a\u89e3\u9664\u5175\u67c4\uff0c\u7f6e\u6563\u6295\u95f2\uff01\u8001\u5f1f\u3002\u6211\u7ed9\u4f60\u8bf4\u4e00\u4e2a\u6545\u4e8b\u3002\u8fd9\u4e2a\u6545\u4e8b\uff0c\u4e5f\u6b63\u5c31\u662f\u6211 \u4eec\u7236\u5b50\u7684\u906d\u9047\u3002\u201d \u3000\u3000\u6768\u6ed4\u987f\u4e86\u4e00\u987f\uff0c\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u7236\u5b50\u5230\u4e86\u4e34\u5b89\uff0c\u5176\u65f6\u79e6\u6867\u5df2\u6b7b\u591a\u5e74\uff0c\u4f46\u5f53\u671d\u7684\u5bb0\u76f8\uff0c \u5374\u4ecd\u662f\u79e6\u6867\u665a\u5e74\u6240\u63d0\u62d4\u7684\u89c9\u7fbd\u9b4f\u826f\u81e3\u3002\u8fd9\u4e2a\u9b4f\u826f\u81e3\u540d\u4e3a\u2018\u826f\u81e3\u2019\uff0c\u5b9e\u662f\u5978\u81e3\u3002\u4ed6\u788d\u7740\u6211\u4eec\u6768 \u5bb6\u7684\u52cb\u671b\uff0c\u4e0d\u80fd\u4e0d\u5b89\u7f6e\u6211\u7684\u7239\u7239\u3002\u4f46\u6211\u7239\u7239\u4e0d\u80af\u732e\u5a9a\u4ed6\uff0c\u4e8e\u662f\u5f97\u4e86\u4e00\u4e2a\u4e94\u54c1\u2018\u7b7e\u4e8b\u2019\u4e4b\u804c\uff0c \u62e8\u5728\u6dee\u77f3\u4e00\u4e2a\u5c0f\u53bf\u7ed9\u6dee\u9633\u8282\u5ea6\u4f7f\u7ec3\u5175\uff0c\u50cf\u8fd9\u6837\u7684\u7ec3\u5175\u5b98\u5728\u4e00\u4e2a\u8282\u5ea6\u4f7f\u4e4b\u4e0b\u6709\u5341\u51e0\u4e2a\u4e4b\u591a\uff0c\u7ec3 \u6210\u7684\u5175\u6bcf\u5e74\u90fd\u8981\u4ea4\u51fa\u53bb\u7684\uff0c\u4ea6\u5373\u662f\u8bf4\uff0c\u8d1f\u8d23\u7ec3\u5175\u4e4b\u4eba\u5e76\u65e0\u5175\u6743\uff0c\u4ed6\u53ea\u662f\u4e3a\u4eba\u4f5c\u5ac1\u800c\u5df2\u3002 \u3000\u3000\u201c\u672c\u6765\u5018\u82e5\u6240\u7ec3\u7684\u5175\u7528\u4e8e\u6297\u654c\u90a3\u4e5f\u5f88\u597d\uff0c\u6211\u7684\u7239\u7239\u5e76\u975e\u4e89\u6743\u593a\u5229\u4e4b\u4eba\u3002\u4f46\u7ed3\u679c\u7ecf\u4ed6\u7684\u624b \u6240\u7ec3\u6210\u7684\u7cbe\u9510\u4e4b\u5e08\uff0c\u5c3d\u90fd\u7528\u4e8e\u2018\u88ad\u532a\u2019\uff0c\u800c\u6240\u8c13\u2018\u532a\u2019\uff0c\u53c8\u53ea\u662f\u4e00\u4e9b\u65e0\u4ee5\u4e3a\u751f\uff0c\u4e0d\u582a\u66b4\u653f\uff0c \u903c\u5f97\u2018\u94e4\u800c\u8d70\u9669\u2019\u7684\u767e\u59d3\uff01 \u3000\u3000\u201c\u8fd9\u6837\u8fc7\u4e86\u51e0\u5e74\uff0c\u7239\u7239\u7070\u5fc3\u6781\u4e86\u3002\u56e0\u6b64\u4ed6\u4e0d\u8981\u6211\u5728\u519b\u4e2d\u4efb\u804c\uff0c\u8981\u6211\u591a\u5b66\u4e9b\u672c\u9886\uff0c\u5e0c\u671b\u671d \u653f\u66f4\u65b0\uff0c\u5f85\u65f6\u800c\u7528\u3002\u88f4\u5927\u4fa0\u548c\u6211\u7239\u7239\u4ea4\u597d\uff0c\u4e8e\u662f\u6536\u4e86\u6211\u505a\u5ce8\u5d4b\u6d3e\u7b2c\u4e09\u4ee3\u5f1f\u5b50\u3002 \u3000\u3000\u201c\u65f6\u5149\u6d41\u5931\uff0c\u6211\u4eec\u5230\u4e86\u6c5f\u5357\uff0c\u4e0d\u77e5\u4e0d\u89c9\u5df2\u662f\u5341\u5e74\u6709\u591a\uff0c\u8fd9\u4e00\u5e74\u91d1\u4e3b\u5b8c\u989c\u4eae\u8981\u2018\u7acb\u9a6c\u5434\u5c71 \u7b2c\u4e00\u5cf0\u2019\uff0c\u4eb2\u81ea\u9886\u5175\uff0c\u8981\u8ba8\u5e73\u6c5f\u5357\u3002\u6ee1\u671d\u6587\u6b66\uff0c\u90fd\u4f5c\u6295\u964d\u7684\u6253\u7b97\uff0c\u6562\u4e8e\u7edf\u5175\u6297\u6218\u7684\uff0c\u53ea\u6709\u865e \u5141\u6587\u4e00\u4eba\u3002\u865e\u5141\u6587\u5f53\u65f6\u53ea\u662f\u4e00\u4e2a\u4e2d\u7ea7\u5c06\u9886\uff0c\u6709\u5175\u4e0d\u8fc7\u4e07\u4eba\u3002\u800c\u5b8c\u989c\u4eae\u7684\u5927\u519b\u53f7\u79f0\u767e\u4e07\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u8fd9\u4f4d\u865e\u5141\u6587\u53ef\u662f\u5728\u91c7\u77f3\u77fe\u5927\u8d25\u91d1\u5175\u7684\u865e\u5143\u5e05\uff1f\u201d \u3000\u3000\u626c\u6ed4\u9053\uff1a\u201c\u4e0d\u9519\u3002\u4f60\u4eec\u5728\u6ca6\u9677\u533a\u7684\u4e5f\u77e5\u9053\u4e86\uff1f\u201d \u3000\u3000\u674e\u601d\u752b\u9053\uff1a\u201c\u8fd9\u4e00\u573a\u60ca\u5929\u52a8\u5730\u7684\u5927\u6218\uff0c\u6211\u4eec\u600e\u80fd\u4e0d\u77e5\uff1f\u91d1\u864f\u867d\u7136\u6781\u529b\u63a9\u7792\u6218\u8d25\u7684\u6d88\u606f\uff0c \u4f46\u6c11\u95f4\u5374\u662f\u5954\u8d70\u76f8\u544a\uff0c\u4eba\u4eba\u90fd\u77e5\u9053\u5b8c\u989c\u4eae\u7684\u767e\u4e07\u5927\u519b\u5728\u91c7\u77f3\u77fe\u5168\u519b\u8986\u6ca1\u3002\u4f46\u6211\u4eec\u5374\u4e0d\u77e5\u9053\u865e \u5143\u5e05\u53ea\u6709\u8fd9\u4e48\u4e00\u70b9\u5175\uff0c\u4e00\u5200\u65b0\u5175\u5bf9\u767e\u4e07\u4e45\u5386\u6c99\u573a\u7684\u5f3a\u864f\uff0c\u2018\u4ee5\u4e00\u5f53\u767e\u2019\u8fd8\u4e0d\u8db3\u4ee5\u5f62\u5bb9\u53cc\u65b9\u7684 \u5f3a\u5f31\u60ac\u6b8a\uff0c\u8fd9\u4e2a\u4e00\u4ed7\u4e0d\u77e5\u662f\u5982\u4f55\u6253\u6cd5\uff1f\u201d \u3000\u3000\u6768\u6ed4\u9053\uff1a\u201c\u4f9d\u9760\u8001\u767e\u59d3\u561b\uff01\u865e\u5141\u6587\u867d\u7136\u53ea\u6709\u4e00\u4e07\u65b0\u5175\uff0c\u4f46\u6218\u4e8b\u4e00\u8d77\uff0c\u5404\u65b9\u6c11\u519b\u90fd\u6765\u52a9 \u6218\uff0c\u6c5f\u5317\u7684\u4e49\u519b\u4e5f\u5927\u4e3e\u54cd\u5e94\uff0c\u622a\u65ad\u91d1\u864f\u8fd0\u7cae\u7684\u9053\u8def\u3002\u8fd9\u6837\u4e00\u6765\uff0c\u5b8c\u989c\u4eae\u7684\u767e\u4e07\u5927\u519b\u53cd\u800c\u9677\u5165 \u767e\u59d3\u7684\u5305\u56f4\u4e4b\u4e2d\uff0c\u5c31\u50cf\u74ee\u4e2d\u6349\u9cd6\u4e00\u6837\uff0c\u53eb\u4ed6\u4eec\u4e00\u4e2a\u4e5f\u9003\u4e0d\u6389\u3002 \u3000\u3000\u201c\u8fd9\u6b21\u91c7\u77f3\u77fe\u4e4b\u6218\uff0c\u6211\u7684\u7239\u7239\u4e5f\u5c3d\u4e86\u4ed6\u7684\u4e00\u4efd\u529b\u91cf\u3002\u5f53\u65f6\u4ed6\u521a\u597d\u6709\u4e09\u5343\u540d\u4e1a\u5df2\u8bad\u7ec3\u671f\u6ee1 \u7684\u65b0\u5175\uff0c\u672c\u6765\u8981\u62e8\u7ed9\u6dee\u9633\u8282\u5ea6\u4f7f\u62ff\u53bb\u2018\u88ad\u532a\u2019\u7684\uff0c\u4ed6\u770b\u5230\u4e86\u865e\u5143\u5e08\u53f7\u53ec\u767e\u59d3\u6297\u91d1\u7684\u6a84\u6587\uff0c\u5c31 \u628a\u8fd9\u652f\u65b0\u5175\u5f00\u5230\u91c7\u77f3\u77fe\u53bb\u4e86\u3002\u201d \u3000\u3000\u674e\u6069\u5357\u9053\uff1a\u201c\u8fd9\u4e0d\u662f\u8fdd\u6297\u4e86\u671d\u5ef7\u7684\u547d\u4ee4\u5417\uff1f\u201d \u3000\u3000\u6768\u6ed4\u9053\uff1a\u201c\u5f53\u65f6\u6b63\u662f\u6218\u4e8b\u6700\u5403\u7d27\u7684\u65f6\u5019\uff0c\u6253\u8d25\u91d1\u864f\u8981\u7d27\uff0c\u7239\u7239\u65e9\u5df2\u662f\u628a\u4e2a\u4eba\u8363\u8fb1\u3001\u751a\u81f3 \u662f\u8fde\u751f\u6b7b\u4e5f\u7f6e\u4e4b\u5ea6\u5916\u4e86\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u62cd\u638c\u8d5e\u9053\uff1a\u201c\u597d\uff0c\u8fd9\u624d\u662f\u5927\u82f1\u96c4\u5927\u8c6a\u6770\u7684\u895f\u6000\uff01\u201d \u3000\u3000\u6768\u6ed4\u9053\uff1a\u201c\u4e0d\uff0c\u6211\u7239\u7239\u8bf4\uff1a\u2018\u5929\u4e0b\u5174\u4ea1\uff0c\u5339\u592b\u6709\u8d23\u3002\u4ed6\u505a\u7684\u53ea\u662f\u4e00\u4e2a\u666e\u901a\u8001\u767e\u59d3\u6240\u5e94\u8be5 \u505a\u7684\u4e8b\u60c5\u3002\u2019\u201d \u3000\u3000\u674e\u601d\u5357\u70b9\u4e86\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5982\u679c\u662f\u6362\u4e0a\u4e86\u6211\uff0c\u6211\u4e5f\u4f1a\u8fd9\u6837\u505a\u7684\u3002\u201d \u3000\u3000\u8ddf\u7740\u53c8\u7709\u98de\u8272\u821e\u5730\u8bf4\u9053\uff1a\u201c\u8fd9\u4e00\u4ed7\u6253\u5f97\u6f02\u4eae\u6781\u4e86\u3002\u6709\u4e2a\u7b11\u8bdd\uff0c\u4e5f\u8bb8\u4f60\u8fd8\u4e0d\u77e5\u9053\u5462\u3002\u6211\u4eec \u5728\u6ca6\u9677\u533a\u7684\u767e\u59d3\uff0c\u5927\u5bb6\u90fd\u628a\u5b8c\u989c\u4eae\u53eb\u505a\u2018\u5b8c\u989c\u6697\u2019\u3002\u201d \u3000\u3000\u6768\u6ed4\u7b11\u9053\uff1a\u201c\u6709\u8fd9\u4e48\u4e00\u4e2a\u8bf4\u6cd5\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u636e\u8bf4\u5b8c\u989c\u4eae\u5728\u51fa\u5175\u4e4b\u65f6\uff0c\u66fe\u505a\u4e86\u4e00\u9996\u8bd7\uff0c\u8bd7\u9053\uff1a\u2018\u4e07\u91cc\u8f66\u4e66\u5c3d\u6df7\u540c\uff0c\u6c5f\u5357 \u5c82\u6709\u522b\u7586\u5c01\u3002\u63d0\u5175\u767e\u4e07\u897f\u6e56\u4e0a\uff0c\u7acb\u9a6c\u5434\u5c71\u7b2c\u4e00\u5cf0\u3002\u2019\u4ed6\u4ee5\u4e3a\u4ed6\u7684\u767e\u4e07\u5927\u519b\uff0c\u6295\u97ad\u8db3\u53ef\u65ad\u6d41\uff0c \u4e00\u5b9a\u53ef\u4ee5\u541e\u5e76\u6c5f\u5357\u3001\u7edf\u4e00\u5929\u4e0b\u7684\u4e86\uff0c\u54ea\u77e5\u8eab\u8d25\u540d\u88c2\uff0c\u4e0d\u4f46\u5168\u519b\u8986\u706d\uff0c\u4ed6\u81ea\u8eab\u5728\u9003\u547d\u9014\u4e2d\u4e5f\u7ed9 \u4e71\u519b\u6740\u4e86\u3002\u6240\u4ee5\u767e\u59d3\u8bf4\u4ed6\u6beb\u65e0\u81ea\u77e5\u4e4b\u660e\uff0c\u4e0d\u914d\u79f0\u2018\u4eae\u2019\uff0c\u53ea\u80fd\u53eb\u505a\u2018\u5b8c\u989c\u6697\u2019\u3002 \u3000\u3000\u201c\u4f46\u662f\u5317\u65b9\u7684\u767e\u59d3\u4e5f\u5f88\u5931\u671b\u53c8\u5f88\u5947\u602a\uff0c\u4e3a\u4ec0\u4e48\u865e\u5143\u5e05\u5728\u5927\u8d25\u91d1\u5175\u4e4b\u540e\uff0c\u4e0d\u4e58\u80dc\u6536\u590d\u5931 \u5730\u3002\u201d \u3000\u3000\u6768\u6ed4\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u8fd9\u662f\u56e0\u4e3a\u4e34\u5b89\u5c0f\u671d\u5ef7\u7684\u7687\u5e1d\u4e5f\u662f\u4e00\u4e2a\u660f\u541b\uff01\u201d\u5341\u4e8c\u9053\u91d1\u724c\u7684\u60b2\u5267\u5728\u865e \u5143\u5e05\u8eab\u4e0a\u91cd\u6f14\u4e86\uff01\u201d \u3000\u3000\u201c\u5341\u4e8c\u9053\u91d1\u724c\u201d\u8bf4\u7684\u662f\u5cb3\u98de\u7684\u6545\u4e8b\u3002\u5f53\u5e74\u5cb3\u98de\u5927\u7834\u91d1\u5175\u4e8e\u4e8c\u6731\u4ed9\u9547\uff0c\u6b63\u62df\u76f4\u6363\u9ec4\u9f99\uff0c\u5374 \u88ab\u5b8b\u9ad8\u5b97\u8fde\u53d1\u5341\u4e8c\u9053\u91d1\u724c\u53ec\u56de\uff0c\u5176\u540e\u5c31\u7ed9\u79e6\u6867\u4ee5\u201c\u83ab\u987b\u6709\u201d\u7684\u4e09\u5b57\u51a4\u72f1\u5bb3\u6b7b\u4e86\u3002 \u3000\u3000\u674e\u601d\u5357\u542c\u4e86\u8fd9\u8bdd\uff0c\u5927\u5403\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u865e\u5143\u5e05\u4e5f\u7ed9\u5978\u81e3\u5bb3\u6b7b\u4e86\u4e48\uff1f\u201d \u3000\u3000\u6768\u6ed4\u8bf4\u9053\uff1a\u201c\u865e\u5143\u5e05\u7684\u2018\u547d\u8fd0\u2019\uff0c\u8f83\u4e3a\u597d\u4e9b\uff0c\u8fd9\u4e5f\u662f\u56e0\u4e3a\u9b4f\u826f\u81e3\u788d\u4e8e\u6e05\u8bae\uff0c\u4e0d\u6562\u628a\u4ed6\u6253 \u4e0b\u51a4\u72f1\u7684\u7f18\u6545\u3002\u79e6\u6867\u6b7b\u540e\u6570\u5341\u5e74\uff0c\u5140\u4eca\u4ecd\u662f\u53d7\u4eba\u553e\u9a82\uff0c\u9b4f\u826f\u81e3\u4e0d\u80fd\u4e0d\u6709\u4e9b\u513f\u987e\u5fcc\u3002\u6545\u6b64\u4ed6\u53ea \u662f\u5047\u501f\u541b\u547d\uff01\u628a\u865e\u5141\u6587\u53ec\u56de\uff0c\u660e\u5347\u6697\u964d\uff0c\u8ba9\u4ed6\u505a\u4e2a\u4eac\u5b98\uff0c\u5265\u593a\u4e86\u4ed6\u7684\u5175\u6743\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u6168\u53f9\u826f\u4e45\uff0c\u8bf4\u9053\uff1a\u201c\u9677\u5bb3\u5fe0\u826f\uff0c\u53e4\u4eca\u5982\u51fa\u4e00\u8f99\u3002\u4f46\u4e0d\u77e5\u4ee4\u5c0a\u53c8\u5982\u4f55\u4e86\uff1f\u201d \u3000\u3000\u6768\u6ed4\u53cc\u76ee\u8574\u6cea\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u7684\u7239\u7239\u540d\u4f4d\u4e0d\u53ca\u865e\u5143\u5e05\uff0c\u9b4f\u826f\u81e3\u5bf9\u4ed8\u4ed6\u7684\u624b\u6bb5\u5c31\u72e0\u8fa3\u5f97\u591a\u3002 \u4ed6\u52a0\u7ed9\u4e86\u6211\u7684\u7239\u7239\u4e24\u6761\u5927\u7f6a\uff1a\u4e00\u662f\u64c5\u81ea\u8c03\u5175\uff0c\u4e8c\u662f\u79c1\u53d6\u5b98\u7cae\u3002\u6211\u7239\u7239\u90a3\u6b21\u5e26\u5175\u5230\u91c7\u77f3\u808c\u6253 \u4ed7\uff0c\u671d\u5ef7\u662f\u6ca1\u6709\u7cae\u8349\u53d1\u7ed9\u7684\uff0c\u53ea\u597d\u5728\u7ecf\u8fc7\u7684\u5dde\u53bf\uff0c\u501f\u7528\u516c\u62a5\u3002\u4ee5\u4e3a\u4e8b\u6025\u4ece\u6743\uff0c\u671d\u5ef7\u53ef\u4ee5\u8ffd \u8ba4\u3002\u54ea\u77e5\u8fc7\u540e\u9b4f\u826f\u81e3\u975e\u4f46\u4e0d\u51c6\u62a5\u9500\uff0c\u53cd\u800c\u7ed9\u6211\u7239\u7239\u52a0\u4e86\u7f6a\u72b6\uff0c\u8981\u4ed6\u8d54\u507f\u3002 \u3000\u3000\u672c\u6765\u6211\u7684\u7239\u7239\u867d\u662f\u64c5\u81ea\u8c03\u5175\uff0c\u4f46\u4ed6\u6253\u4e86\u80dc\u4ed7\uff0c\u8fd8\u662f\u53ef\u4ee5\u5c06\u529f\u8d4e\u7f6a\u3002\u7136\u8981\u4ed6\u79c1\u4eba\u8d54\u51fa\u8fd9\u8bb8 \u591a\u5b98\u7cae\uff0c\u5c31\u662f\u8981\u4ed6\u7684\u547d\u4e5f\u8d54\u4e0d\u4e86\uff09 \u3000\u3000\u201c\u6211\u7239\u7239\u53d7\u4e0d\u8fc7\u7262\u72f1\u7684\u6298\u78e8\uff0c\u7ec8\u4e8e\u5728\u72f1\u4e2d\u81ea\u5c3d\uff0c\u4e34\u6b7b\u4e4b\u524d\uff0c\u5199\u4e86\u4e00\u5c01\u8fe2\u4e66\uff0c\u6258\u4e00\u4e2a\u72f1\u5352 \u5e26\u51fa\u6765\u7ed9\u6211\uff0c\u53eb\u6211\u7acb\u5373\u56de\u5317\u65b9\u7684\u8001\u5bb6\uff0c\u4e00\u6765\u53ef\u4ee5\u4f8d\u6cf0\u8001\u6bcd\uff0c\u7167\u987e\u5e7c\u59b9\uff1b\u4e8c\u6765\u5728\u91d1\u864f\u7edf\u6cbb\u4e4b \u4e0b\uff0c\u4e00\u6837\u53ef\u4ee5\u4e3a\u56fd\u5c3d\u529b\uff0c\u5982\u679c\u80fd\u591f\u7ec4\u6210\u4e00\u652f\u4e49\u519b\uff0c\u5728\u654c\u4eba\u7684\u540e\u65b9\u6253\u4ed7\uff0c\u6bd4\u8d77\u5728\u8fd9\u91cc\u53d7\u5978\u81e3\u7684 \u94b3\u5236\u7528\u535c\u8fd8\u8981\u75db\u5feb\u5f97\u591a\u3002\u53e6\u6709\u4e00\u4e2a\u539f\u56e0\uff0c\u6211\u7239\u7239\u6ca1\u6709\u8bf4\u51fa\u6765\u7684\uff0c\u662f\u4ed6\u6015\u9b4f\u826f\u81e3\u6284\u5bb6\uff0c\u8fde\u7d2f\u4e8e \u6211\u3002 \u3000\u3000\u201c\u90a3\u72f1\u5352\u662f\u4e2a\u597d\u4eba\uff0c\u4ed6\u628a\u6211\u7239\u7239\u7684\u9053\u4e66\u548c\u5e73\u65e5\u5bf9\u4ed6\u6240\u8bf4\u7684\u8a00\u8bed\u90fd\u544a\u8bc9\u4e86\u6211\uff0c\u53c8\u8d44\u52a9\u76d8\u7f20 \u4e0e\u6211\uff0c\u5e2e\u6211\u5077\u6e21\u957f\u6c5f\u3002\u6211\u95ee\u4ed6\u7684\u540d\u5b57\u4ed6\u90fd\u4e0d\u80af\u8bf4\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u53f9\u9053\uff1a\u201c\u4ed7\u4e49\u6bcf\u591a\u5c60\u72d7\u8f88\uff01\u79e6\u6867\u3001\u9b4f\u826f\u8214\u8fd9\u4e9b\u5978\u81e3\u53ef\u6740\u53ef\u6068\uff0c\u8fd9\u4e2a\u65e0\u540d\u7684\u72f1\u5352\u5374 \u662f\u53ef\u94a6\u65ec\u656c\u4e86\uff01\u6768\u5927\u54e5\uff0c\u4f60\u65e2\u662f\u56de\u4e61\u4e0e\u5bb6\u4eba\u56e2\u805a\uff0c\u540e\u6765\u53c8\u600e\u4e48\u5230\u4e86\u8499\u53e4\u6765\u7684\uff1f\u201d \u3000\u3000\u6768\u6ed4\u8bf4\u9053\uff1a\u201c\u6211\u79bb\u5bb6\u5341\u8f7d\uff0c\u5bb6\u4e2d\u7684\u53d8\u5316\u5df2\u7ecf\u5f88\u5927\u3002\u6bcd\u4eb2\u5e74\u8001\u591a\u75c5\uff0c\u59b9\u59b9\u5c1a\u672a\u6210\u4eba\uff0c\u4ec5\u4f59 \u7684\u4e00\u4e9b\u7956\u4e1a\u4e5f\u90fd\u5356\u5149\u5403\u5c3d\u4e86\uff0c\u8fd8\u5e78\u6211\u56de\u5bb6\u5f97\u65e9\uff0c\u5f97\u89c1\u6bcd\u4eb2\u4e00\u9762\u3002 \u3000\u3000\u201c\u6bcd\u4eb2\u6b7b\u540e\uff0c\u65e5\u5b50\u66f4\u662f\u96be\u8fc7\u3002\u8fd9\u8fd8\u4e0d\u7b97\u4ec0\u4e48\uff0c\u66f4\u7cdf\u7cd5\u7684\u662f\u91d1\u864f\u77e5\u9053\u6211\u4ece\u6c5f\u5357\u56de\u6765\uff0c\u4ece\u5927 \u90fd\u884c\u6587\u5230\u6211\u6240\u5c5e\u7684\u90a3\u4e2a\u5730\u65b9\uff0c\u8981\u5730\u65b9\u5b98\u628a\u6211\u902e\u6355\u9001\u4eac\uff0c\u6709\u516c\u95e8\u4e2d\u7684\u670b\u53cb\u9001\u4fe1\u7ed9\u6211\uff0c\u903c\u5f97\u6211\u53ea \u597d\u5e26\u4e86\u59b9\u59b9\u9003\u4ea1\uff0c\u6c5f\u5357\u53bb\u4e0d\u6210\uff0c\u91d1\u56fd\u5883\u5185\u53c8\u4e0d\u80fd\u7acb\u8db3\u3002\u56e0\u6b64\u6700\u540e\u53ea\u80fd\u9003\u5230\u4e86\u8499\u53e4\u6765\u4e86\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u8499\u53e4\u9791\u5b50\u6ca1\u6709\u53d1\u73b0\u4f60\u7684\u8eab\u4efd\uff1f\u201d \u3000\u3000\u6768\u6ed4\u9053\uff1a\u201c\u6211\u4eec\u5144\u59b9\u662f\u4e03\u5e74\u524d\u6765\u5230\u8fd9\u91cc\u7684\u3002\u90a3\u65f6\u6210\u5409\u601d\u6c57\u8fd8\u6ca1\u6709\u5b8c\u5168\u7edf\u4e00\u8499\u53e4\uff0c\u90e8\u843d\u4e4b \u95f4\uff0c\u5404\u81ea\u4e3a\u653f\uff0c\u6709\u4e9b\u8352\u51c9\u7684\u5730\u65b9\uff0c\u6839\u672c\u5c31\u6ca1\u4eba\u7ba1\u3002\u6709\u4e9b\u90e8\u843d\uff0c\u4e5f\u6b22\u8fce\u6c49\u4eba\u7ed9\u4ed6\u4eec\u5f00\u8352\u3002\u5934\u4e09 \u5e74\u4e0d\u7528\u4ea4\u79df\uff0c\u6211\u4eec\u5144\u59b9\u5c31\u5728\u963f\u513f\u683c\u5c71\u5c71\u53e3\uff0c\u548c\u8bb8\u591a\u5404\u5730\u6765\u7684\u6d41\u6c11\u5f00\u8352\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u5c71\u8c37\u5165\u53e3\u4e4b\u5904\uff0c\u90a3\u4e24\u9762\u5c71\u5761\u4e0a\u7684\u68af\u7530\uff0c\u5c31\u662f\u4f60\u4eec\u5f00\u8352\u7684\u6210\u7ee9\u3002\u4f46 \u4f55\u4ee5\u73b0\u5728\u53c8\u662f\u91ce\u8349\u4e1b\u751f\u4e86\u5462\uff1f\u201d \u3000\u3000\u6768\u6ed4\u9053\uff1a\u201c\u8bf4\u6765\u6c14\u4eba\uff0c\u6211\u4eec\u8f9b\u8f9b\u82e6\u82e6\u5730\u5f00\u8352\uff0c\u5934\u4e09\u5e74\u662f\u6ca1\u6709\u4ec0\u4e48\u6536\u6210\u7684\uff0c\u4e00\u5230\u6709\u4e86\u6536 \u6210\uff0c\u90a3\u4e9b\u8499\u53e4\u7684\u738b\u516c\u5c31\u8981\u6765\u9738\u5360\u6211\u4eec\u7684\u571f\u5730\u4e86\uff0c\u7eb3\u79df\u4e4b\u540e\u8fd8\u4e0d\u591f\u53e3\u7cae\u3002\u4e0d\u7eb3\u79df\u4e48\uff0c\u5c31\u4e0d\u8bb8 \u8015\u3002\u8fd9\u8fd8\u4e0d\u7b97\uff0c\u66f4\u8981\u547d\u7684\u662f\uff0c\u6b64\u65f6\u6210\u5409\u601d\u6c57\u5df2\u7edf\u4e00\u8499\u53e4\u3002\u8bf4\u51e1\u5728\u8499\u53e4\u5883\u5185\uff0c\u4e0d\u8bba\u662f\u6c49\u4eba\u3001\u91d1 \u4eba\u3001\u897f\u57df\u5404\u56fd\u4eba\uff0c\u90fd\u662f\u4ed6\u7684\u5b50\u6c11\uff0c\u8981\u670d\u5175\u5f79\u3002 \u3000\u3000\u201c\u8fd9\u4e48\u4e00\u6765\uff0c\u8c01\u8fd8\u613f\u610f\u7ed9\u4ed6\u8015\u7530\uff1f\u6709\u7684\u518d\u9003\u4ea1\u4ed6\u65b9\uff1b\u6709\u7684\u5c31\u5728\u8349\u539f\u4e0a\u6d41\u6d6a\uff0c\u4e1c\u8eb2\u897f\u907f\u7684 \u9760\u505a\u77ed\u5ea6\u65e5\uff1b\u6709\u7684\u5df2\u8fc7\u4e86\u670d\u5175\u5f79\u9f84\u7684\u6539\u884c\u505a\u5de5\u5320\u7cca\u53e3\uff1b\u8fd8\u6709\u8dd1\u4e0d\u6389\u7684\u9752\u5e74\u3001\u58ee\u6c49\u7ed9\u6293\u4e86\u53bb\u5f53 \u9a6c\u592b\u3002\u6211\u4eec\u5144\u59b9\u907f\u5165\u6df1\u5c71\u6253\u730e\u5ea6\u65e5\u3002\u8fd9\u963f\u513f\u683c\u5c71\u7ef5\u4e98\u6570\u767e\u91cc\uff0c\u5c71\u53e3\u4ee5\u524d\u8fd8\u7565\u6709\u4eba\u5bb6\uff0c\u5230\u4e86\u6df1 \u5c71\u5bc6\u6797\u4e4b\u5904\uff0c\u90a3\u5c31\u53ea\u6709\u4e0e\u9e1f\u9c81\u540c\u7fa4\u4e86\u3002\u4e0d\u8fc7\uff0c\u867d\u7136\u5bc2\u5bde\uff0c\u5374\u662f\u6bd4\u8015\u7530\u81ea\u5728\u5f97\u591a\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u6b64\u5730\u4e0e\u4fd8\u864f\u5c6f\u6b96\u533a\u76f8\u53bb\u4e0d\u8fdc\uff0c\u4f60\u6709\u6ca1\u6709\u89c1\u8fc7\u90a3\u4e9b\u4fd8\u864f\uff1f\u201d \u3000\u3000\u6768\u6ed4\u9053\uff1a\u201c\u4ed6\u4eec\u8015\u4f5c\u4e4b\u65f6\uff0c\u662f\u6709\u8499\u53e4\u9791\u5b50\u5728\u65c1\u76d1\u89c6\u7684\u3002\u6211\u4eec\u89c1\u662f\u89c1\u8fc7\uff0c\u4f46\u4e0d\u80fd\u4e0e\u4ed6\u4eec\u4ea4 \u8c08\uff0c\u4e0d\u8fc7\uff0c\u4ed6\u4eec\u5148\u540e\u51e0\u6b21\u95ef\u8425\u9003\u4ea1\uff0c\u8840\u6597\u9791\u5b50\u4e4b\u4e8b\uff0c\u6211\u4eec\u4e5f\u6709\u8033\u95fb\u3002\u90a3\u65f6\u6211\u4eec\u867d\u4e0d\u77e5\u9002\u4ee4\u5c0a \u7684\u5927\u540d\uff0c\u4f46\u5df2\u77e5\u9053\u4ed6\u662f\u4fd8\u864f\u8425\u4e2d\u6700\u53d7\u7231\u6234\u7684\u8001\u82f1\u96c4\u4e86\u3002\u6709\u4e00\u4e2a\u4fa5\u5e78\u9003\u5f97\u51fa\u6765\u7684\u4fd8\u864f\uff0c\u66fe\u548c\u6211 \u8be6\u8c08\u8fc7\u4ee4\u5c0a\u5728\u4fd8\u864f\u8425\u4e2d\u7684\u6545\u4e8b\u3002\u56e0\u6b64\uff0c\u5728\u6211\u4eec\u6551\u4e86\u4ee4\u5c0a\u4e4b\u540e\uff0c\u4e00\u8bf4\u8d77\u6765\uff0c\u5c31\u77e5\u9053\u4ed6\u662f\u8c01 \u4e86\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u5fc3\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u4ed6\u4eec\u5982\u6b64\u6089\u5fc3\u8c03\u62a4\u6211\u7684\u7239\u7239\u3002\u201d \u3000\u3000\u6768\u6ed4\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u4ee4\u5c0a\u5728\u677e\u98ce\u8c37\u517b\u75c5\u671f\u95f4\uff0c\u6211\u53bb\u8fc7\u51e0\u6b21\u548c\u6797\u63a2\u542c\u6d88\u606f\u3002\u4f59\u4e00\u4e2d\u5192\u5145\u4f60\u7684 \u7239\u7239\uff0c\u505a\u4e86\u9791\u5b50\u7684\u5927\u5b98\uff0c\u6211\u65e9\u5df2\u77e5\u9053\u4e86\u3002\u53ef\u662f\u90a3\u4e00\u665a\u6211\u5374\u4e0d\u6562\u660e\u767d\u5730\u544a\u8bc9\u4f60\uff0c\u4e3a\u7684\u662f\u6015\u4f60\u77e5 \u9053\u4e4b\u540e\uff0c\u5fcd\u8010\u4e0d\u4f4f\uff0c\u5c31\u8981\u62a5\u4ec7\uff0c\u90a3\u5c31\u5b9a\u906d\u4f59\u4e00\u4e2d\u7684\u6bd2\u624b\u4e86\uff0c\u56e0\u6b64\uff0c\u6211\u53ea\u80fd\u6545\u5e03\u7591\u9635\u5f15\u4f60\u5230\u8fd9 \u91cc\u6765\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u6211\u61c2\u5f97\u5927\u54e5\u7684\u82e6\u5fc3\uff0c\u4e0d\u8fc7\u8fd9\u4e2a\u4ec7\u6211\u4ee5\u540e\u603b\u662f\u8981\u62a5\u7684\u3002\u201d \u3000\u3000\u6768\u6ed4\u9053\uff1a\u201c\u8fd9\u4e2a\u5f53\u7136\uff0c\u83ab\u8bf4\u4f60\u8981\u4e3a\u7236\u62a5\u4ec7\uff0c\u5c31\u662f\u6ca1\u6709\u79c1\u4ec7\uff0c\u8fd9\u53ae\u4e3a\u864e\u4f5c\u6005\uff0c\u6211\u4eec\u4e5f\u662f\u975e \u6740\u4ed6\u4e0d\u53ef\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u542c\u4e86\u8fd9\u201c\u4e3a\u864e\u4f5c\u6005\u201d\u56db\u5b57\uff0c\u4e0d\u7531\u5f97\u53c8\u60f3\u8d77\u4e86\u5b5f\u5927\u4fa0\u5bf9\u4ed6\u7684\u8bef\u4f1a\u6765\u3002 \u3000\u3000\u5b5f\u5c11\u521a\u90a3\u65e5\u7ed9\u4ed6\u7684\u201c\u7559\u8a00\u201d\uff0c\u6b63\u662f\u201c\u4e3a\u864e\u4f5c\u6005\uff0c\u5fc5\u53d6\u4f60\u547d\u201d\u8fd9\u516b\u4e2a\u5927\u5b57\u3002\u674e\u601d\u5357\u4e0d\u7981\u5fc3 \u4e2d\u82e6\u7b11\uff0c\u201c\u4f59\u4e00\u4e2d\u8fd9\u5978\u8d3c\u5bb3\u5f97\u6211\u7236\u5b50\u597d\u60e8\uff0c\u6211\u7239\u7239\u8499\u4e86\u4e0d\u767d\u4e4b\u51a4\uff0c\u8fde\u7d2f\u6211\u4e5f\u51e0\u4e4e\u4e27\u5728\u5b5f\u5927\u4fa0 \u7684\u5251\u4e0b\u3002\u201d \u3000\u3000\u4ed6\u611f\u5230\u51a4\u5c48\uff0c\u4f46\u4e5f\u611f\u5230\u4e86\u201c\u82e6\u5c3d\u7518\u6765\u201d\u7684\u559c\u60a6\u3002\u201c\u73b0\u5728\u53ef\u597d\u4e86\uff0c\u771f\u76f8\u5df2\u7ecf\u5927\u767d\uff0c\u6211\u53ef\u4ee5 \u548c\u5b5f\u5927\u4fa0\u8bf4\u4e2a\u6e05\u695a\u4e86\u3002\u53ea\u4e0d\u77e5\u4ec0\u4e48\u65f6\u5019\u624d\u53ef\u4ee5\u548c\u4ed6\u518d\u89c1\uff1f\u201d \u3000\u3000\u56de\u5fc6\u50cf\u4e00\u676f\u82e6\u9152\uff0c\u5473\u9053\u867d\u4e0d\u597d\u53d7\uff0c\u5374\u4e5f\u4ee4\u4ed6\u5fc3\u5934\u5174\u594b\u3002\u5b5f\u660e\u971e\u7684\u5f71\u5b50\u66ff\u4ee3\u4e86\u5979\u7684\u7236\u4eb2\uff0c \u5ffd\u5730\u5728\u4ed6\u8111\u6d77\u4e4b\u4e2d\u6d6e\u73b0\uff0c\u90a3\u665a\u5b5f\u5c11\u521a\u672c\u6765\u8981\u6740\u4ed6\u7684\uff0c\u5168\u9760\u5b5f\u660e\u971e\u7ed9\u4ed6\u8bf4\u60c5\uff0c\u5b5f\u5c11\u521a\u624d\u6539\u4e3a \u201c\u7559\u4e66\u793a\u8b66\u201d\u3002\u674e\u601d\u5357\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6211\u4e0e\u5b5f\u59d1\u5a18\u53ea\u662f\u4e00\u9762\u4e4b\u4ea4\uff0c\u96be\u5f97\u5979\u80af\u4fe1\u6211\u3002\u5982\u4eca\u771f\u76f8\u5927 \u767d\uff0c\u6211\u4e5f\u53ef\u4ee5\u544a\u6170\u5979\u4e86\u3002\u201d \u3000\u3000\u60f3\u81f3\u6b64\u5904\uff0c\u674e\u601d\u5357\u84e6\u5730\u4e00\u7701\uff0c\u604d\u7136\u81ea\u609f\uff0c\u539f\u6765\u4ed6\u6240\u60f3\u8981\u518d\u89c1\u7684\u4eba\uff0c\u5b5f\u5927\u4fa0\u8fd8\u5728\u5176\u6b21\uff0c\u6700 \u7d27\u8981\u7684\u8fd8\u662f\u5b5f\u660e\u971e\uff01\u4ed6\u5934\u4e00\u6b21\u53d1\u89c9\u81ea\u5df1\u5fc3\u5e95\u7684\u79d8\u5bc6\uff0c\u8138\u4e0a\u4e0d\u7981\u5fae\u5fae\u53d1\u70ed\u3002 \u3000\u3000\u6768\u6ed4\u9053\uff1a\u201c\u5357\u5f1f\uff0c\u4f60\u5728\u60f3\u4e9b\u4ec0\u4e48\uff1f\u201d\u674e\u601d\u5357\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\u3002\u5929\u5feb\u9ed1\u4e86\uff0c\u54b1\u4eec\u5feb\u4e9b\u8d70 \u5427\u3002\u201d \u3000\u3000\u6768\u6ed4\u7b11\u9053\uff1a\u201c\u62d0\u4e00\u4e2a\u5f2f\u5c31\u5230\u4e86\uff0c\u4f60\u8bb0\u4e0d\u5f97\u8def\u4e48\uff1f\u201d\u674e\u601d\u5357\u9762\u4e0a\u4e00\u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u5c71\u4e0a\u7684\u8def \u5cf0\u56de\u8def\u8f6c\uff0c\u786e\u662f\u4e0d\u6613\u8bb0\u8ba4\u3002\u201d\u674e\u601d\u5357\u94bb\u8fdb\u7a91\u6d1e\uff0c\u653e\u4e0b\u67f4\u8349\uff0c\u559c\u5b5c\u5b5c\u5730\u8bf4\u9053\uff1a\u201c\u7239\u7239\u7761\u9192\u4e86 \u4e48\uff1f\u7239\uff0c\u4f60\u77a7\uff0c\u6768\u5927\u54e5\u7ed9\u4f60\u627e\u6765\u4e86\u4e00\u652f\u53c8\u7c97\u53c8\u5927\u7684\u8001\u5c71\u53c2\uff01\u201d \u3000\u3000\u6768\u5a49\u5df2\u7ecf\u5728\u6d1e\u4e2d\u71c3\u8d77\u81ea\u5236\u7684\u6cb9\u70db\uff0c\u70db\u5149\u6447\u66f3\u4e4b\u4e2d\uff0c\u53ea\u89c1\u6768\u5a49\u7709\u5fc3\u6df1\u9501\uff0c\u8138\u4e0a\u4f3c\u6709\u6cea\u75d5\u3002 \u674e\u5e0c\u6d69\u7684\u8138\u8272\u5728\u65e5\u95f4\u672c\u662f\u82cd\u767d\u5982\u7eb8\u7684\uff0c\u6b64\u9645\u5728\u70db\u5149\u6620\u7167\u4e0b\uff0c\u5374\u5448\u73b0\u7740\u4e00\u7247\u5947\u5f02\u7684\u7ea2\u5149\u3002\u674e\u601d \u5357\u7a81\u7136\u611f\u5230\u7a7a\u6c14\u51b7\u5f97\u4f3c\u4e4e\u51dd\u7ed3\uff0c\u7b11\u5bb9\u4e5f\u5728\u4ed6\u7684\u8138\u4e0a\u51dd\u56fa\u4e86\u3002 \u3000\u3000\u674e\u5e0c\u6d69\u5f20\u5f00\u773c\u775b\uff0c\u54b3\u4e86\u4e00\u58f0\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u8d24\u4f84\uff0c\u4f60\u4e0d\u5fc5\u4e3a\u6211\u8d39\u795e\u53bb\u627e\u4eba\u53c2\u5566\uff0c\u6211\u7528\u4e0d\u7740 \u4e86\u3002\u5357\u513f\uff0c\u4f60\u8fc7\u6765\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u7239\uff0c\u4f60\u4e0d\u8981\u80e1\u601d\u4e71\u60f3\u3002\u4f60\u7684\u6c14\u8272\u6bd4\u521a\u624d\u597d\u591a\u4e86\u3002\u201d \u3000\u3000\u674e\u5e0c\u6d69\u9053\uff1a\u201c\u6211\u77e5\u9053\u6211\u8fd9\u662f\u56de\u5149\u53cd\u7167\uff0c\u8d81\u6211\u73b0\u5728\u8fd8\u6709\u7cbe\u795e\uff0c\u6211\u5f97\u8d76\u5feb\u548c\u4f60\u8bf4\u4e00\u4ef6\u7d27\u8981\u7684 \u4e8b\u60c5\uff0c\u4ee5\u4e86\u6211\u7684\u5fc3\u613f\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u7239\uff0c\u4f60\u4e0d\u4f1a\u6b7b\u7684\uff01\u4f60\u3001\u4f60\u4e0d\u8981\u8fd9\u4e48\u60f3\uff01\u201d \u3000\u3000\u674e\u5e0c\u6d69\u5fae\u5fae\u4e00\u7b11\uff0c\u795e\u60c5\u5341\u5206\u5b89\u8be6\u5730\u8bf4\u9053\uff1a\u201c\u5357\u513f\uff0c\u4f60\u4e0d\u8981\u96be\u8fc7\u3002\u6b7b\u6709\u4ec0\u4e48\u53ef\u6015\uff1f\u6211\u80fd\u591f \u89c1\u7740\u4e86\u4f60\u624d\u6b7b\uff0c\u6bd4\u6211\u7ed9\u4f59\u4e00\u4e2d\u6d3b\u57cb\u800c\u6b7b\uff0c\u90a3\u5df2\u7ecf\u662f\u597d\u5f97\u591a\u4e86\u3002\u6211\u73b0\u5728\u5fc3\u91cc\u5f88\u9ad8\u5174\uff0c\u5f88\u9ad8\u5174\uff0c \u771f\u7684\uff0c\u6211\u4e00\u70b9\u4e5f\u6ca1\u6709\u9057\u61be\u4e86\u3002\u4e0d\u8fc7\uff0c\u5c31\u53ea\u6709\u4e00\u4e2a\u5fc3\u613f\uff0c\u4f60\u3001\u4f60\u4e0d\u8981\u6d41\u6cea\uff0c\u8d76\u5feb\u5b9a\u4e0b\u795e\u6765\uff0c\u542c \u6211\u8bf4\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u662f\uff01\u7239\u7239\uff0c\u4f60\u8bf4\u5427\u3002\u4f60\u6709\u4ec0\u4e48\u5fc3\u613f\uff0c\u5b69\u513f\u4e00\u5b9a\u66ff\u4f60\u529e\u5230\u3002\u201d \u3000\u3000\u674e\u5e0c\u6d69\u6478\u4e86\u6478\u513f\u5b50\u7684\u9762\u5b54\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u79bb\u5bb6\u7684\u65f6\u5019\uff0c\u4f60\u624d\u53ea\u6709\u4e09\u5c81\u3002\u6643\u773c\u8fc7\u4e86\u4e8c\u5341\u5e74\uff0c \u4f60\u4eca\u5e74\u5df2\u662f\u4e8c\u5341\u4e09\u5c81\u4e86\u3002\u4f60\u5988\u7ed9\u4f60\u5b9a\u4e86\u4eb2\u6ca1\u6709\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u5fc3\u5934\u9e7f\u649e\uff0c\u6da8\u7ea2\u4e86\u8138\uff0c\u8bf4\u9053\uff1a\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u674e\u5e0c\u6d69\u9762\u9732\u7b11\u5bb9\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff01\u90a3\u6211\u5c31\u8d81\u7740\u53cc\u773c\u672a\u95ed\u4e4b\u65f6\uff0c\u7ed9\u4f60\u529e\u4e86\u8fd9\u4ef6\u4e8b\u5427\u3002\u4f60\u7684\u5a49 \u59b9\u670d\u4f8d\u4e86\u6211\u5927\u534a\u5e74\uff0c\u6211\u662f\u6ca1\u6cd5\u62a5\u7b54\u5979\u7684\uff0c\u4f60\u5fc5\u987b\u66ff\u6211\u597d\u597d\u7684\u62a5\u7b54\u5979\u3002\u4f60\u660e\u767d\u6211\u7684\u610f\u601d\u5417\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u7239\u7239\u653e\u5fc3\uff0c\u5a49\u59b9\u7684\u5927\u6069\u6211\u6c38\u4e16\u4e0d\u5fd8\uff0c\u6211\u4e00\u5b9a\u628a\u5979\u5f53\u4f5c\u4eb2\u59b9\u5b50\u770b\u5f85\u3002\u201d \u3000\u3000\u674e\u5e0c\u6d69\u9053\uff1a\u201c\u5509\uff0c\u4f60\u8fd9\u50bb\u5b69\u5b50\uff0c\u4f60\u8fd8\u4e0d\u61c2\u7239\u7239\u7684\u610f\u601d\u5417\uff1f\u6211\u662f\u8981\u5979\u505a\u4f60\u7684\u5ab3\u5987\u513f\uff0c\u4e0d\u662f \u8981\u5979\u505a\u4f60\u7684\u59b9\u5b50\uff01\u5f02\u59d3\u5144\u59b9\u867d\u4e5f\u662f\u4eb2\uff0c\u600e\u53ca\u5f97\u4e0a\u592b\u5987\u4e4b\u4eb2\uff1f\u6211\u662f\u8981\u628a\u4f60\u4eec\u7684\u5173\u7cfb\u66f4\u8fdb\u4e00\u5c42\uff0c \u8fd9\u624d\u80fd\u591f\u62a5\u7b54\u4f60\u5a49\u59b9\u7684\u60c5\u4e49\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u4f4e\u4e0b\u4e86\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u8fd9\u4e2a\u2014\u2014\u5144\u59b9\u6050\u6015\uff0c\u6050\u6015\u2014\u2014\u201d \u3000\u3000\u674e\u5e0c\u6d69\u6120\u9053\uff1a\u201c\u4ec0\u4e48\u8fd9\u4e2a\u90a3\u4e2a\uff0c\u6211\u5df2\u7ecf\u8bf4\u5f97\u5341\u5206\u6e05\u695a\u4e86\uff0c\u5f02\u59d3\u5144\u59b9\u6709\u4ec0\u4e48\u4e0d\u53ef\u6210\u4eb2\uff1f\u8fd9 \u662f\u6211\u552f\u4e00\u672a\u4e86\u7684\u5fc3\u613f\uff0c\u6211\u8981\u4f60\u4eec\u5728\u6211\u7684\u9762\u524d\u8ba2\u4e86\u4eb2\uff0c\u6211\u624d\u80fd\u591f\u7791\u76ee\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u5b69\u513f\u7acb\u8a93\u66ff\u7239\u7239\u62a5\u4ec7\uff0c\u6211\u662f\u51c6\u5907\u8c41\u4e86\u6027\u547d\u53bb\u523a\u6740\u4ec7\u4eba\u7684\uff0c\u662f\u5426\u80fd\u591f\u6d3b\u7740\u56de \u6765\u8fd8\u8bf4\u4e0d\u5b9a\u3002\u5c82\u80fd\u62d6\u7d2f\u5a49\u59b9\uff1f\u201d \u3000\u3000\u6768\u6ed4\u9053\uff1a\u201c\u4f60\u8fd9\u5c31\u8bf4\u5f97\u4e0d\u5bf9\u4e86\uff01\u4f60\u5373\u4f7f\u4e0d\u662f\u6211\u7684\u59b9\u592b\uff0c\u6211\u4eec\u5144\u59b9\u4e5f\u8981\u5e2e\u4f60\u62a5\u8fd9\u4e2a\u4ec7 \u7684\uff01\u201d \u3000\u3000\u674e\u5e0c\u6d69\u9053\uff1a\u201c\u6ed4\u4f84\uff0c\u8fd9\u662f\u4f60\u7684\u4e49\u4fa0\u5fc3\u80a0\uff0c\u6211\u5f88\u611f\u6fc0\u3002\u4f46\u5728\u6211\u6765\u8bf4\uff0c\u6211\u53d7\u4e86\u4f60\u4eec\u5144\u59b9\u7684\u6069 \u60e0\u5df2\u7ecf\u592a\u591a\uff0c\u5982\u679c\u4ed6\u4eec\u4e0d\u662f\u7ed3\u4e3a\u592b\u5987\uff0c\u4f60\u59b9\u59b9\u4e3a\u6211\u820d\u547d\u62a5\u4ec7\uff0c\u8fd9\u6069\u4e49\u6211\u5c31\u53d7\u4e0d\u8d77\u4e86\u3002\u201d \u3000\u3000\u6768\u6ed4\u9053\uff1a\u201c\u6211\u770b\u5357\u5f1f\u4f3c\u6709\u4e3a\u96be\u4e4b\u8272\uff0c\u53ea\u6015\u5357\u5f1f\u662f\u5acc\u6211\u7684\u59b9\u59b9\u914d\u4ed6\u4e0d\u8d77\uff01\u201d \u3000\u3000\u8bdd\u8bf4\u5230\u8fd9\u4e2a\u5730\u6b65\uff0c\u674e\u601d\u5357\u8fd8\u600e\u80fd\u591f\u63a8\u8f9e\uff1f\u5f53\u4e0b\u53ea\u597d\u60f6\u7136\u8bf4\u9053\uff1a\u201c\u6768\u5927\u54e5\uff0c\u4f60\u8fd9\u8bdd\u98a0\u5012\u8fc7 \u6765\u8bf4\u624d\u5bf9\uff0c\u662f\u6211\u6015\u914d\u4e0d\u8d77\u5a49\u59b9\u3002\u201d \u3000\u3000\u674e\u5e0c\u6d69\u8fd9\u624d\u7b11\u9053\uff1a\u201c\u601d\u5357\uff0c\u4f60\u8fd9\u6837\u8bf4\u5c31\u5bf9\u4e86\u3002\u8bf4\u771f\u7684\uff0c\u6211\u4e5f\u66fe\u6709\u6b64\u987e\u8651\u5462\uff01\u4f60\u5a49\u59b9\u7684\u4eba \u54c1\u6b66\u529f\uff0c\u6211\u6240\u6df1\u77e5\u3002\u8981\u627e\u4e00\u4e2a\u8fd9\u6837\u7684\u5dfe\u5e3c\u987b\u7709\uff0c\u53ea\u6015\u4f60\u6253\u4e86\u706f\u7b3c\u90fd\u96be\u518d\u627e\u4e00\u4e2a\u4e86\u3002\u597d\u5728\u6211\u521a \u624d\u95ee\u8fc7\u4f60\u7684\u5a49\u59b9\uff0c\u5979\u6ca1\u6709\u5acc\u5f03\u4f60\uff0c\u6211\u624d\u653e\u4e0b\u4e86\u5fc3\u4e0a\u7684\u77f3\u5934\u3002\u201d \u3000\u3000\u6768\u5a49\u6ee1\u9762\u901a\u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u7239\uff0c\u4f60\u2026\u2026\u201d \u3000\u3000\u674e\u5e0c\u6d69\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u90fd\u4e0d\u8981\u5bb3\u7f9e\u4e86\uff0c\u5982\u4eca\u65e2\u7136\u662f\u4f60\u4eec\u5f7c\u6b64\u90fd\u60c5\u613f\u4e86\uff0c\u8d81\u6211\u8fd8\u6709\u53e3 \u6c14\uff0c\u4f60\u4eec\u5c31\u5728\u6211\u7684\u9762\u524d\u4ea4\u62dc\u6210\u4eb2\u5427\uff01\u201d \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u60a3\u96be\u4e4b\u4ea4\u60c5\u4e49\u539a\uff0c\u76f8\u9022\u840d\u6c34\u7f14\u826f\u7f18\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b\u2014\u2014\u7b2c\u4e03\u56de\u3000\u53e0\u9f13\u6e05\u7b33\u7a7a\u5f15\u5251\u3000\u843d\u82b1\u98de\u7d6e\u603b\u65e0\u5fc3 \u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b \u7b2c\u4e03\u56de\u3000\u53e0\u9f13\u6e05\u7b33\u7a7a\u5f15\u5251\u3000\u843d\u82b1\u98de\u7d6e\u603b\u65e0\u5fc3 \u3000\u3000\u674e\u601d\u5357\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u7239\uff0c\u5f85\u4f60\u8001\u4eba\u5bb6\u75c5\u597d\u4e86\uff0c\u90a3\u3001\u90a3\u65f6\u4e5f\u8fd8\u4e0d\u8fdf\uff0c\u4f55\u5fc5\u8fd9\u6837\u5306 \u4fc3\uff1f\u201d \u3000\u3000\u674e\u5e0c\u6d69\u9053\uff1a\u201c\u6211\u8fd9\u75c5\u54ea\u8fd8\u597d\u5f97\u4e86\uff1f\u6211\u8981\u4eb2\u773c\u89c1\u5230\u4f60\u4eec\u5728\u6211\u9762\u524d\u7ed3\u4e3a\u592b\u5987\uff0c\u6211\u624d\u53bb\u5f97\u5b89 \u7136\u3002\u5f53\u7136\u5a5a\u59fb\u5927\u4e8b\uff0c\u4e0d\u5b9c\u8349\u7387\uff0c\u4f60\u4eec\u53ef\u4ee5\u5f85\u6211\u53bb\u4e16\u4e4b\u540e\uff0c\u62e9\u65e5\u5b8c\u5a5a\u3002\u4e0d\u8fc7\uff0c\u4e5f\u4e0d\u5fc5\u62d6\u5ef6\u592a \u4e45\u3002\u6211\u7684\u610f\u601d\u662f\u65e0\u987b\u4e25\u683c\u9075\u5b88\u53e4\u793c\uff0c\u6700\u597d\u662f\u4f60\u4eec\u56de\u5bb6\u4e4b\u65e5\uff0c\u7980\u660e\u4f60\u7684\u6bcd\u4eb2\uff0c\u4fbf\u5373\u5b8c\u5a5a\u3002\u201d\u53e4 \u793c\u662f\u8981\u5b88\u4e09\u5e74\u4e4b\u4e27\u7684\uff0c\u674e\u5e0c\u6d69\u8bdd\u4e2d\u4e4b\u610f\uff0c\u5373\u662f\u8981\u4ed6\u4eec\u4e0d\u5fc5\u7b49\u5f85\u4e09\u5e74\u3002 \u3000\u3000\u674e\u601d\u5357\u8fd9\u624d\u5f04\u61c2\u4e86\u7236\u4eb2\u7684\u610f\u601d\u3002\u539f\u6765\u4ed6\u7236\u4eb2\u6240\u8bf4\u7684\u201c\u4ea4\u62dc\u6210\u4eb2\u201d\uff0c\u5176\u5b9e\u53ea\u662f\u4e3e\u884c\u4e00\u79cd\u786e \u5b9a\u592b\u5987\u540d\u5206\u7684\u201c\u8ba2\u5a5a\u4eea\u5f0f\u201d\u800c\u5df2\u3002 \u3000\u3000\u674e\u601d\u5357\u5fc3\u5934\u7a0d\u677e\uff0c\u60f3\u9053\uff1a\u201c\u7239\u7239\u6267\u610f\u5982\u6b64\uff0c\u6211\u53ea\u597d\u7b54\u5e94\u4e0b\u6765\u518d\u8bf4\u4e86\u3002\u4f46\u613f\u535a\u5f97\u4ed6\u5fc3\u4e2d\u9ad8 \u5174\uff0c\u8bf4\u4e0d\u5b9a\u8fd8\u53ef\u4ee5\u633d\u6551\u4ed6\u7684\u6c89\u75b4\u3002\u201d \u3000\u3000\u4e8e\u662f\u8fd9\u5bf9\u521a\u521a\u76f8\u8bc6\u7684\u201c\u65b0\u4eba\u201d\uff0c\u4fbf\u5728\u674e\u5e0c\u6d69\u75c5\u584c\u4e4b\u65c1\uff0c\u76f8\u4e92\u62dc\u4e86\u4e09\u62dc\uff0c\u7b97\u662f\u5b8c\u6210\u4e86\u8ba2\u5a5a \u7684\u4eea\u5f0f\uff0c\u6768\u5a49\u56fa\u7136\u65e0\u9650\u5a07\u7f9e\uff0c\u674e\u601d\u5357\u4e5f\u662f\u9762\u7ea2\u8fc7\u8033\u3002 \u3000\u3000\u6700\u9ad8\u5174\u7684\u662f\u674e\u5e0c\u6d69\uff0c\u5728\u4ed6\u513f\u5b50\u4e0e\u6768\u5a49\u4ea4\u62dc\u4e4b\u540e\u53c8\u7ed9\u4ed6\u78d5\u5934\u4e4b\u65f6\uff0c\u4e50\u5f97\u54c8\u54c8\u5927\u7b11\uff0c\u4e0d\u6599\u7b11 \u58f0\u6e10\u6765\u6e10\u5f31\uff0c\u5f85\u5230\u674e\u601d\u5357\u5927\u5403\u4e00\u60ca\uff0c\u8d77\u6765\u63a2\u89c6\u4e4b\u65f6\uff0c\u674e\u5e0c\u6d69\u7b11\u58f0\u5df2\u7edd\uff0c\u547c\u5438\u4ea6\u5df2\u65ad\u4e86\u3002\u539f\u6765 \u53d7\u5211\u592a\u91cd\uff0c\u8eab\u4f53\u865a\u5f31\u4e0d\u582a\uff0c\u6362\u4e86\u522b\u4eba\uff0c\u65e9\u5df2\u5e94\u8be5\u6b7b\u4e86\u3002\u4ed6\u4e4b\u6240\u4ee5\u80fd\u591f\u6d3b\u5230\u4eca\u65e5\uff0c\u5168\u51ed\u7740\u4e00\u70b9 \u5e0c\u671b\uff0c\u5e0c\u671b\u4ed6\u7684\u513f\u5b50\u80fd\u8d76\u5f97\u6765\u4e0e\u4ed6\u76f8\u4f1a\u3002\u8fd9\u70b9\u5e0c\u671b\u9f13\u821e\u4e86\u4ed6\u6c42\u751f\u7684\u610f\u5fd7\uff0c\u8fd9\u624d\u80fd\u591f\u52c9\u5f3a\u652f\u6491 \u7684\u3002\u5982\u4eca\u5fc3\u613f\u5df2\u4e86\uff0c\u7cbe\u795e\u7684\u529b\u91cf\u4e00\u677e\u61c8\u4e0b\u6765\uff0c\u4fbf\u5728\u7b11\u58f0\u4e2d\u901d\u4e16\u4e86\u3002 \u3000\u3000\u674e\u601d\u5357\u7ecf\u8fc7\u4e86\u4e07\u82e6\u5343\u8f9b\uff0c\u624d\u627e\u5f97\u5230\u7236\u4eb2\uff0c\u4e3a\u4e86\u60f3\u4f7f\u7236\u4eb2\u9ad8\u5174\uff0c\u53c8\u4e0d\u60dc\u8fdd\u80cc\u81ea\u5df1\u7684\u5fc3\u610f\u4e0e \u6768\u5a49\u5b9a\u4eb2\uff0c\u4e0d\u6599\u4ecd\u662f\u633d\u6551\u4e0d\u4e86\u7236\u4eb2\u7684\u6027\u547d\uff0c\u54c0\u75db\u81ea\u662f\u53ef\u60f3\u800c\u77e5\u3002 \u3000\u3000\u6768\u6ed4\u529d\u6170\u4ed6\u9053\uff1a\u201c\u674e\u8001\u4f2f\u542b\u7b11\u800c\u901d\uff0c\u4ed6\u8001\u4eba\u5bb6\u662f\u53bb\u5f97\u5b89\u4e50\u7684\u3002\u4f60\u4e5f\u65e0\u987b\u592a\u8fc7\u60b2\u4f24\u4e86\u3002\u54b1 \u4eec\u73b0\u5728\u8fd8\u662f\u5728\u864e\u53e3\u4e4b\u4e2d\uff0c\u8fd8\u662f\u5feb\u5feb\u7ed9\u4ed6\u8001\u4eba\u5bb6\u529e\u4e86\u540e\u4e8b\u8981\u7d27\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u970d\u7136\u4e00\u7701\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\u3002\u54f2\u522b\u5df2\u7ecf\u77e5\u9053\u6211\u662f\u6765\u677e\u98ce\u8c37\u627e\u6211\u7239\u7239\u7684\u4e86\uff0c\u4ed6\u56de\u53bb\u4e4b \u540e\uff0c\u4e00\u5b9a\u8fd8\u4f1a\u518d\u6765\u3002\u54b1\u4eec\u662f\u8be5\u65e9\u4e9b\u7ed9\u7239\u7239\u4e0b\u846c\u3002\u51fa\u5c71\u4e4b\u540e\uff0c\u518d\u8bbe\u6cd5\u66ff\u7239\u7239\u62a5\u4ec7\u3002\u201d \u3000\u3000\u6768\u6ed4\u4e0a\u5c71\u4f10\u6728\uff0c\u505a\u4e86\u4e00\u526f\u68fa\u6750\uff0c\u6309\u7167\u6c49\u4eba\u7684\u4e27\u793c\uff0c\u7ed9\u674e\u5e0c\u6d69\u7b51\u575f\u4e0b\u846c\u3002\u5728\u6768\u6ed4\u5916\u51fa\u4e4b \u65f6\uff0c\u674e\u601d\u5357\u548c\u6768\u5a49\u7559\u5728\u7a91\u6d1e\uff0c\u5b88\u7740\u674e\u5e0c\u6d69\u7684\u9057\u4f53\uff0c\u53ef\u662f\u4ed6\u4eec\u4e24\u4eba\u4e5f\u627e\u4e0d\u7740\u4ec0\u4e48\u8bdd\u8bf4\uff0c\u53ea\u662f\u5404 \u81ea\u54c0\u54c0\u75db\u54ed\u3002 \u3000\u3000\u7b2c\u4e00\u65e5\u575f\u5df2\u7b51\u597d\uff0c\u6768\u5a49\u5144\u59b9\u6536\u62fe\u4e86\u5fc5\u987b\u643a\u5e26\u7684\u7b80\u5355\u884c\u674e\uff0c\u4fbf\u5373\u79bb\u5f00\u4e86\u8fd9\u4e2a\u4ed6\u4eec\u4f4f\u4e86\u51e0\u5e74 \u7684\u7a91\u6d1e\u3002 \u3000\u3000\u4e0b\u5c71\u4e4b\u524d\uff0c\u4e09\u4eba\u5148\u5230\u674e\u5e0c\u6d69\u7684\u575f\u524d\u4e0a\u9999\u544a\u8f9e\u3002\u6ca1\u6709\u73b0\u6210\u7684\u9999\u70db\uff0c\u53ea\u80fd\u64ae\u571f\u4e3a\u9999\u3002\u6768\u6ed4\u89c1 \u674e\u601d\u5357\u54c0\u75db\u5df2\u7ecf\u7a0d\u51cf\uff0c\u6709\u5fc3\u8ba9\u4ed6\u548c\u59b9\u59b9\u5355\u72ec\u76f8\u5904\u7247\u65f6\u3002 \u3000\u3000\u6768\u6ed4\u8bf4\u9053\uff1a\u201c\u5c31\u5dee\u4e00\u5757\u5893\u5a62\u4e86\uff0c\u5f85\u6211\u53bb\u627e\u5757\u5408\u7528\u7684\u77f3\u5934\uff0c\u7528\u5251\u523b\u5b57\uff0c\u6743\u5f53\u5893\u7891\u5427\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u64ae\u571f\u4e3a\u9999\uff0c\u5728\u7236\u4eb2\u5893\u524d\u8dea\u5012\uff0c\u78d5\u4e86\u4e09\u4e2a\u54cd\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u7239\u7239\u5728\u5929\u4e4b\u7075\u4fdd\u4f51\uff0c\u4fdd\u4f51 \u5b69\u513f\u624b\u5203\u4ec7\u4eba\u3002\u201d\u6768\u5a49\u8dea\u5728\u4ed6\u7684\u8eab\u540e\uff0c\u4e5f\u78d5\u4e86\u4e2a\u54cd\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u6c42\u7239\u7239\u4fdd\u4f51\uff0c\u4fdd\u4f51\u6211\u4eec\u5e73\u5b89 \u5230\u5bb6\u3002\u201d\u5979\u8bf4\u7684\u4e0d\u662f\u201c\u6211\u201d\u800c\u662f\u201c\u6211\u4eec\u201d\uff0c\u663e\u7136\u5979\u6240\u8bf4\u7684\u8fd9\u4e2a\u201c\u5bb6\u201d\uff0c\u4e5f\u662f\u6307\u674e\u601d\u5357\u7684\u5bb6 \u4e86\u3002 \u3000\u3000\u674e\u601d\u5357\u4e0d\u7981\u6709\u51e0\u5206\u60ed\u6127\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u6211\u548c\u5979\u5df2\u7ecf\u6709\u4e86\u592b\u59bb\u540d\u5206\uff0c\u592b\u59bb\u540c\u5c5e\u4e00\u4f53\uff0c\u5979\u7977 \u544a\u4e4b\u65f6\u6ca1\u6709\u5fd8\u8bb0\u6211\uff0c\u6211\u5374\u5fd8\u8bb0\u4e86\u5979\u4e86\uff01 \u3000\u3000\u4e24\u4eba\u7ad9\u4e86\u8d77\u6765\uff0c\u76ee\u5149\u76f8\u63a5\uff0c\u674e\u601d\u5357\u6709\u70b9\u5185\u759a\u4e8e\u5fc3\uff0c\u8bf4\u9053\uff1a\u201c\u5a49\u59b9\uff0c\u6b64\u6b21\u56de\u5bb6\uff0c\u8fe2\u8fe2\u4e07 \u91cc\uff0c\u524d\u9014\u8270\u9669\u5b9a\u591a\u3002\u6210\u5409\u601d\u6c57\u5df2\u7ecf\u4e0b\u4ee4\u4f10\u91d1\uff0c\u6211\u7684\u5bb6\u4e61\u53c8\u6b63\u662f\u5175\u5bb6\u5fc5\u4e89\u4e4b\u5730\uff0c\u4f60\u8ddf\u6211\u56de\u53bb\uff0c \u6211\u7d2f\u4f60\u53d7\u82e6\uff0c\u751a\u81f3\u8fd8\u53ef\u80fd\u7d2f\u4f60\u966a\u6211\u9001\u547d\uff0c\u6211\u3001\u6211\u5b9e\u5728\u8fc7\u610f\u4e0d\u53bb\u3002\u201d \u3000\u3000\u6768\u5a49\u6014\u4e86\u4e00\u6014\uff0c\u8bf4\u9053\uff1a\u201c\u65e2\u5df2\u7ed3\u4e3a\u592b\u5987\uff0c\u7406\u8be5\u7518\u82e6\u540c\u5c1d\uff0c\u751f\u6b7b\u4e0e\u5171\u3002\u4f60\u3001\u4f60\u4e3a\u4f55\u8fd8\u8bf4\u8fd9 \u6837\u7684\u8bdd\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u6ee1\u8138\u901a\u7ea2\uff0c\u4e00\u65f6\u95f4\u4e0d\u77e5\u5982\u4f55\u56de\u7b54\u624d\u597d\u3002\u6768\u5a49\u5ffd\u5730\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u4e0d\u5fcd\u7239 \u7239\u96be\u8fc7\uff0c\u624d\u59d4\u5c48\u81ea\u5df1\uff0c\u987a\u4ece\u4ed6\u8001\u4eba\u5bb6\u7684\u610f\u601d\u5427\uff1f\u8fd9\u6b21\u5a5a\u4e8b\uff0c\u672c\u6765\u6765\u5f97\u7a81\u7136\uff0c\u4f60\u82e5\u662f\u540e\u6094\uff0c\u73b0 \u5728\u8fd8\u6765\u5f97\u53ca\u3002\u4e0d\u8fc7\uff0c\u54b1\u4eec\u53ef\u5f97\u5546\u91cf\u597d\u4e86\u4e00\u756a\u8bf4\u8bdd\uff0c\u600e\u4e48\u6837\u548c\u6211\u54e5\u54e5\u6765\u8bf4\u3002\u6211\u4eec\u6768\u5bb6\u5386\u4ee3\u65e0\u518d \u5a5a\u4e4b\u5973\uff0c\u6211\u7684\u54e5\u54e5\u6027\u60c5\u662f\u5f88\u56fa\u6267\u7684\u3002\u201d \u3000\u3000\u5f26\u5916\u4e4b\u97f3\uff0c\u6768\u5a49\u5176\u5b9e\u662f\u4e0d\u60f3\u89e3\u9664\u8fd9\u4e2a\u5a5a\u7ea6\u3002\u8fd9\u4e5f\u602a\u4e0d\u5f97\u5979\uff0c\u8981\u77e5\u53e4\u4ee3\u7684\u793e\u4f1a\u98ce\u6c14\uff0c\u5bf9\u793c \u6cd5\u6700\u4e3a\u91cd\u89c6\uff0c\u5c24\u5176\u662f\u5b98\u5ba6\u4eba\u5bb6\uff0c\u65e0\u4e0d\u4ee5\u5bb6\u6709\u518d\u5a5a\u4e4b\u5973\u4e3a\u803b\u3002\u6768\u5a49\u5144\u59b9\u662f\u91d1\u5200\u6768\u4ee4\u516c\u7684\u540e\u4eba\uff0c \u867d\u7136\u4ed6\u4eec\u8fd9\u4e00\u5bb6\u65cf\u65e9\u5df2\u5206\u6563\u5404\u65b9\uff0c\u5bb6\u9053\u4ea6\u65e9\u5df2\u4e2d\u843d\uff0c\u4f46\u540d\u95e8\u5927\u65cf\u7684\u95e8\u98ce\u8fd8\u662f\u4e0d\u5bb9\u540e\u4eba\u201c\u73b7 \u6c61\u201d\u7684\u3002 \u3000\u3000\u6768\u5a49\u89c1\u674e\u601d\u5357\u8bb8\u4e45\u4e0d\u53d1\u4e00\u8a00\uff0c\u5fc3\u91cc\u66f4\u4e3a\u96be\u8fc7\uff0c\u5fcd\u7740\u6cea\u53c8\u518d\u8bf4\u9053\uff1a\u201c\u5357\u54e5\uff0c\u60f3\u5fc5\u4f60\u662f\u53e6\u6709 \u5fc3\u4e0a\u4e4b\u4eba\uff0c\u4f60\u4e0d\u5fc5\u987e\u5168\u6211\u7684\u9762\u5b50\uff0c\u4e5f\u7528\u4e0d\u7740\u5411\u6211\u54e5\u54e5\u4ea4\u4ee3\u4e86\u3002\u8d81\u4ed6\u672a\u66fe\u56de\u6765\uff0c\u4f60\u5148\u8d70\u5427\u3002\u6211 \u4f1a\u548c\u4ed6\u8bf4\u7684\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u597d\u751f\u4e3a\u96be\uff0c\u4ed6\u5bf9\u793c\u6cd5\u5012\u662f\u6ca1\u6709\u6768\u5bb6\u5144\u59b9\u90a3\u6837\u91cd\u89c6\uff0c\u4f46\u4ed6\u53c8\u600e\u5fcd\u4f24\u4e86\u4e00\u4e2a\u5c11\u5973\u7684\u81ea \u5c0a\uff0c\u800c\u4e14\u8fd9\u4e2a\u5c11\u5973\u8fd8\u662f\u4ed6\u7236\u4eb2\u7684\u6069\u4eba\uff1f\u4e0d\u9519\uff0c\u4ed6\u662f\u53e6\u6709\u5fc3\u4e0a\u4e4b\u4eba\uff0c\u4f46\u4ed6\u4e0e\u5b5f\u660e\u971e\u4e5f\u4e0d\u8fc7\u53ea\u662f \u4e00\u9762\u4e4b\u4ea4\uff0c\u8fde\u534a\u53e5\u60c5\u8bdd\u90fd\u6ca1\u6709\u8c08\u8fc7\u7684\uff0c\u4ed6\u7684\u5fc3\u4e0a\u6709\u5979\uff0c\u5374\u4e0d\u77e5\u5b5f\u660e\u971e\u5fc3\u4e2d\u6709\u6ca1\u6709\u4ed6\uff1f \u3000\u3000\u674e\u601d\u5357\u6709\u51e0\u5206\u4e3a\u4e86\u611f\u6069\uff0c\u6709\u513f\u5206\u4e3a\u4e86\u5185\u759a\uff0c\u8fd8\u6709\u51e0\u5206\u662f\u4e3a\u4e86\u4e0d\u5fcd\u4f24\u5bb3\u6768\u5a49\u7684\u81ea\u5c0a\uff0c\u7ec8\u4e8e \u60f6\u7136\u8bf4\u9053\uff1a\u201c\u5a49\u59b9\uff0c\u4f60\u8bef\u4f1a\u4e86\uff0c\u6211\u53ea\u662f\u81ea\u60ed\u5f62\u79fd\uff0c\u9ad8\u6500\u4e0d\u8d77\uff0c\u53c8\u6015\u8fde\u7d2f\u4f60\u4e86\uff0c\u6240\u4ee5\u3001\u6240\u4ee5\u624d \u8bf4\u51fa\u4e86\u5fc3\u8179\u4e4b\u8a00\u3002\u8bf4\u9519\u4e86\u8bdd\uff0c\u4f60\u522b\u4ecb\u610f\u3002\u201d \u3000\u3000\u6768\u5a49\u7f13\u7f13\u62ac\u8d77\u5934\u6765\uff0c\u6f06\u9ed1\u7684\u53cc\u7738\u5e73\u6dfb\u4e86\u51e0\u5206\u5149\u5f69\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u6211\u90fd\u662f\u5728\u6218\u4e71\u4e2d\u53d7\u8fc7 \u82e6\u96be\u7684\u5b64\u513f\u3002\u50cf\u4f60\u4e00\u6837\uff0c\u6211\u4e5f\u662f\u4e09\u5c81\u90a3\u5e74\u7236\u5973\u751f\u79bb\u7684\u3002\u6211\u4eec\u7684\u7236\u4eb2\u90fd\u662f\u53d7\u5978\u4eba\u9677\u5bb3\u3002\u8bf4\u8d77 \u6765\uff0c\u4f60\u6bd4\u6211\u8fd8\u2018\u5e78\u8fd0\u2019\u4e00\u4e9b\uff0c\u4f60\u603b\u7b97\u89c1\u5f97\u7740\u7239\u7239\u4e00\u9762\uff0c\u6211\u5374\u8fde\u7239\u7239\u7684\u575f\u5893\u90fd\u4e0d\u77e5\u9053\u3002\u4f46\u60f3\u4e0d \u5230\u7684\u662f\uff0c\u54b1\u4eec\u4e24\u4e2a\u547d\u8fd0\u76f8\u540c\u7684\u5b69\u5b50\uff0c\u5730\u5317\u5929\u5357\uff0c\u5982\u4eca\u7adf\u4f1a\u805a\u5728\u4e00\u8d77\uff0c\u5171\u7ed3\u4e1d\u841d\u3002\u53ea\u8981\u4f60\u4e0d\u5acc \u5f03\u6211\uff0c\u54b1\u4eec\u5c31\u662f\u4ee5\u540e\u906d\u53d7\u66f4\u591a\u7684\u82e6\u96be\uff0c\u90a3\u53c8\u7b97\u5f97\u4e86\u4ec0\u4e48\uff01\u201d \u3000\u3000\u6bcf\u4e00\u4e2a\u5b57\u90fd\u597d\u50cf\u662f\u4ece\u6768\u5a49\u7684\u80ba\u8151\u4e2d\u638f\u51fa\uff0c\u62e8\u52a8\u4e86\u5bf9\u65b9\u7684\u5fc3\u5f26\u3002\u674e\u601d\u5357\u4e0d\u7531\u5f97\u5927\u4e3a\u611f\u52a8\uff0c \u4e0d\u77e5\u4e0d\u89c9\u5730\u5c31\u628a\u6768\u5a49\u63fd\u5165\u6000\u4e2d\uff0c\u66ff\u5979\u62ed\u53bb\u4e86\u773c\u89d2\u7684\u6cea\u73e0\uff0c\u8bf4\u9053\uff1a\u201c\u5a49\u59b9\uff0c\u4f60\u8bf4\u5f97\u4e0d\u9519\uff0c\u54b1\u4eec \u6b63\u662f\u540c\u547d\u9e33\u9e2f\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u7684\u5f71\u5b50\u597d\u50cf\u662f\u7ed9\u6768\u5a49\u7684\u6cea\u73e0\u7194\u5316\u4e86\u3002\u5728\u674e\u601d\u5357\u5410\u51fa\u201c\u540c\u547d\u9e33\u9e2f\u201d\u56db\u5b57\u4e4b\u65f6\uff0c\u773c\u524d \u552f\u89c1\u6a21\u7cca\u7684\u6cea\u5f71\u3002\u4ed6\u611f\u5230\u6768\u5a49\u5fc3\u623f\u7684\u8df3\u52a8\uff0c\u4ed6\u611f\u5230\u81ea\u5df1\u6709\u8d23\u4efb\u8981\u4fdd\u62a4\u8fd9\u4e2a\u4e0e\u4ed6\u547d\u8fd0\u76f8\u540c\u7684\u5c11 \u5973\u3002\u6cea\u5149\u6447\u66f3\u4e4b\u4e2d\uff0c\u5b5f\u660e\u971e\u7684\u5f71\u5b50\u6de1\u4e86\u3001\u9690\u6ca1\u4e86\u3002 \u3000\u3000\u53ef\u662f\u5b5f\u660e\u971e\u7684\u5f71\u5b50\u5f53\u771f\u5c31\u5728\u4ed6\u7684\u5fc3\u4e2d\u6d88\u901d\u4e86\u4e48\uff1f\u674e\u601d\u5357\u6ca1\u6709\u60f3\u8fc7\u4e5f\u4e0d\u6562\u60f3\u3002\u5982\u679c\u6709\u4eba\u90a3 \u6837\u95ee\u4ed6\uff0c\u6050\u6015\u4ed6\u81ea\u5df1\u4e5f\u4e0d\u77e5\u9053\uff0c\u6b63\u5982\u4ed6\u4e0d\u77e5\u9053\u4ed6\u5bf9\u6768\u5a49\u7684\u8fd9\u4efd\u611f\u60c5\uff0c\u7a76\u7adf\u662f\u7231\u60dc\uff1f\u662f\u601c\u60af\uff1f \u8fd8\u662f\u540c\u60c5\uff1f \u3000\u3000\u50cf\u662f\u7518\u9732\u6ecb\u6da6\u4e86\u67af\u8349\uff0c\u6768\u5a49\u6ee1\u662f\u6cea\u75d5\u7684\u8138\u4e0a\u6cdb\u51fa\u4e86\u4e00\u7247\u7ea2\u6655\uff0c\u5979\u8f7b\u8f7b\u5730\u63a8\u5f00\u4e86\u674e\u601d\u5357\uff0c \u8bf4\u9053\uff1a\u201c\u54e5\u54e5\u5c31\u8981\u56de\u6765\u4e86\uff0c\u7ed9\u4ed6\u77a7\u89c1\u4e86\u53ef\u4e0d\u597d\u610f\u601d\u3002\u201d\u674e\u601d\u5357\u8bb7\u8bb7\u5730\u8bf4\u9053\uff1a\u201c\u662f\u5440\uff0c\u5927\u54e5\u4e0d \u8fc7\u662f\u53bb\u627e\u4e00\u5757\u77f3\u5934\uff0c\u600e\u7684\u53bb\u4e86\u8fd9\u8bb8\u4e45\u8fd8\u4e0d\u89c1\u56de\u6765\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u6b63\u60f3\u53bb\u627e\u6768\u6ed4\uff0c\u5ffd\u542c\u5f97\u201c\u545c\u201d\u7684\u4e00\u58f0\uff0c\u5212\u7834\u4e86\u7a7a\u5c71\u7684\u9759\u5bc2\uff0c\u4e00\u542c\u5c31\u77e5\u662f\u54cd\u7bad\u7684\u58f0 \u97f3\u3002\u6768\u5a49\u6014\u4e86\u4e00\u6014\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u54e5\u54e5\u7528\u7684\u4e0d\u662f\u54cd\u7bad\u3002\u201d \u3000\u3000\u8bdd\u72b9\u672a\u4e86\uff0c\u6768\u6ed4\u7684\u58f0\u97f3\u5df2\u662f\u8fdc\u8fdc\u4f20\u6765\uff0c\u53ea\u542c\u5f97\u4ed6\u7eb5\u58f0\u7b11\u9053\uff1a\u201c\u54c8\u54c8\uff0c\u4f60\u4eec\u4ee5\u4e3a\u674e\u516c\u5b50\u8fd8 \u4f1a\u5728\u8fd9\u8352\u5c71\u91ce\u5cad\u4e4b\u4e2d\u7b49\u5019\u4f60\u4eec\u6765\u6349\u5417\uff1f\u4ed6\u65e9\u5df2\u8d70\u4e86\uff0c\u4f60\u5230\u6c5f\u5357\u53bb\u8ffd\u6355\u4ed6\u5427\uff01\u8fd9\u91cc\u5c31\u53ea\u662f\u6211\u4e00 \u4e2a\u4eba\uff0c\u4f60\u4eec\u90fd\u51b2\u7740\u6211\u6765\u597d\u4e86\uff01\u201d\u663e\u7136\u6768\u6ed4\u662f\u78b0\u4e0a\u4e86\u654c\u4eba\uff0c\u6709\u610f\u8fd9\u4e48\u8bf4\uff0c\u597d\u8ba9\u674e\u601d\u5357\u548c\u4ed6\u7684\u59b9 \u59b9\u542c\u89c1\uff0c\u8d76\u5feb\u9003\u8d70\u7684\u3002 \u3000\u3000\u674e\u601d\u5357\u5927\u5403\u4e00\u60ca\uff0c\u8df3\u8d77\u6765\u9053\uff1a\u201c\u4e0d\u597d\uff0c\u5927\u54e5\u78b0\u4e0a\u4e86\u5f3a\u654c\u4e86\u3002\u8fd9\u652f\u54cd\u7bad\u4e00\u5b9a\u662f\u54f2\u522b\u5c04\u7684\uff01 \u54b1\u4eec\u5feb\u53bb\uff0c\u5feb\u53bb\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u662f\u77e5\u9053\u54f2\u522b\u7684\u672c\u9886\u7684\uff0c\u90a3\u65e5\u6768\u6ed4\u867d\u7136\u662f\u80dc\u4e86\u4ed6\uff0c\u5374\u4e5f\u662f\u80dc\u5f97\u5341\u5206\u4fa5\u5e78\u3002\u4f30\u91cf\u8fd9\u6b21 \u54f2\u522b\u91cd\u6765\uff0c\u5f53\u7136\u7edd\u4e0d\u6b62\u4ed6\u4e00\u4e2a\u4eba\uff0c\u674e\u601d\u5357\u7109\u5f97\u4e0d\u5927\u4e3a\u7740\u6025\uff1f\u5c3d\u7ba1\u6768\u6ed4\u626c\u58f0\u793a\u8b66\uff0c\u4ed6\u53c8\u5c82\u80fd\u72ec \u81ea\u9003\u751f\uff1f\u674e\u601d\u5357\u5982\u98de\u8dd1\u53bb\uff0c\u6768\u5a49\u7d27\u7d27\u5730\u8ddf\u5728\u4ed6\u7684\u540e\u9762\uff0c\u8dd1\u5230\u5c71\u8170\uff0c\u53ea\u89c1\u6768\u6ed4\u548c\u54f2\u522b\u5df2\u7ecf\u6253\u8d77 \u6765\u4e86\u3002 \u3000\u3000\u548c\u54f2\u522b\u4e00\u540c\u6765\u7684\u8fd8\u6709\u4e09\u4e2a\u4eba\uff0c\u4e24\u4e2a\u5587\u561b\uff0c\u4e00\u4e2a\u6b66\u571f\u3002\u674e\u601d\u5357\u8ba4\u5f97\u90a3\u4e2a\u6b66\u58eb\u5c31\u662f\u5728\u6208\u58c1\u4e0a \u548c\u4ed6\u4ea4\u8fc7\u624b\u7684\u90a3\u4e2a\u8d64\u8001\u6e29\uff0c\u90a3\u4e2a\u9ec4\u8863\u5587\u561b\u662f\u7ed9\u5b5f\u5c11\u521a\u90a3\u65e5\u5413\u8dd1\u7684\u547c\u9ece\u5962\uff0c\u8fd8\u6709\u4e00\u4e2a\u9ed1\u8863\u5587\u561b \u5219\u662f\u4e2a\u964c\u751f\u9762\u5b54\u3002 \u3000\u3000\u54f2\u522b\u5de6\u624b\u62ff\u7740\u4e00\u628a\u94c1\u80ce\u5f13\uff0c\u53f3\u624b\u62ff\u7740\u4e00\u67c4\u6708\u7259\u5f2f\u5200\uff0c\u548c\u6768\u6ed4\u6253\u5f97\u5341\u5206\u6fc0\u70c8\u3002\u6768\u6ed4\u90a3\u65e5\u80dc \u4ed6\uff0c\u7528\u7684\u662f\u201c\u626b\u53f6\u5251\u6cd5\u201d\uff0c\u4e13\u653b\u54f2\u522b\u7684\u4e0b\u76d8\u3002\u54f2\u522b\u8eab\u6750\u9ad8\u5927\uff0c\u4e0b\u76d8\u4e0d\u7a33\uff0c\u662f\u4e2a\u5f31\u70b9\u3002 \u3000\u3000\u6b64\u6b21\u54f2\u522b\u91cd\u6765\uff0c\u5927\u7ea6\u662f\u5df2\u7ecf\u5438\u53d6\u4e86\u90a3\u65e5\u5931\u8d25\u7684\u6559\u8bad\uff0c\u4ee5\u6708\u7259\u5f2f\u5200\u7167\u987e\u4e09\u8def\uff0c\u53e6\u5916\u7528\u4e00\u628a \u94c1\u80ce\u5f13\u4f7f\u51fa\u8499\u53e4\u6b66\u58eb\u7279\u957f\u7684\u201c\u91d1\u5f13\u5341\u516b\u6253\u201d\u62db\u6570\uff0c\u653b\u51fb\u6768\u6ed4\u7684\u4e0a\u4e09\u8def\u3002\u653b\u5b88\u517c\u65bd\uff0c\u6768\u6ed4\u53ef\u5c31 \u5360\u4e0d\u5230\u534a\u70b9\u4fbf\u5b9c\u4e86\u3002 \u3000\u3000\u54f2\u522b\u4e00\u9762\u6253\u4e00\u9762\u53eb\u9053\uff1a\u201c\u4e0d\u8981\u76f8\u4fe1\u4ed6\u7684\u9b3c\u8bdd\uff0c\u4f60\u4eec\u5feb\u53bb\u677e\u98ce\u8c37\u641c\u7d22\u3002\u674e\u601d\u5357\u8fd9\u5c0f\u5b50\u4e00\u5b9a \u8fd8\u5728\u90a3\u513f\u3002\u201d \u3000\u3000\u54f2\u522b\u8bdd\u58f0\u672a\u4e86\uff0c\u674e\u601d\u5357\u5df2\u662f\u73b0\u51fa\u8eab\u5f62\uff0c\u559d\u9053\uff1a\u201c\u6211\u5c31\u5728\u8fd9\u513f\uff0c\u7528\u4e0d\u7740\u4f60\u4eec\u8d39\u795e\u641c\u7d22 \u4e86\uff01\u201d \u3000\u3000\u6768\u6ed4\u5927\u60ca\u9053\uff1a\u201c\u5357\u5f1f\uff0c\u4f60\u80a9\u4e0a\u7684\u62c5\u5b50\u91cd\uff0c\u548c\u5a49\u59b9\u5feb\u9003\u5427\u3002\u7559\u5f97\u9752\u5c71\u5728\uff0c\u4e0d\u6015\u6ca1\u67f4\u70e7\uff01\u201d \u674e\u601d\u5357\u9053\uff1a\u201c\u4e0d\uff0c\u54b1\u4eec\u8981\u6b7b\u4e5f\u6b7b\u5728\u4e00\u8d77\uff0c\u672a\u5fc5\u8981\u9003\u8dd1\u7684\u5c31\u5b9a\u662f\u54b1\u4eec\u3002\u201d \u3000\u3000\u547c\u9ece\u5962\u7b11\u9053\uff1a\u201c\u597d\uff0c\u4f60\u8fd9\u5c0f\u5b50\u5012\u5f88\u6709\u4e49\u6c14\uff0c\u53ea\u662f\u5374\u672a\u514d\u592a\u81ea\u4e0d\u91cf\u529b\u4e86\u3002\u597d\u5427\uff0c\u4f60\u8981\u6c42\u6b7b \u90a3\u8fd8\u4e0d\u5bb9\u6613\uff0c\u4f5b\u7237\u6211\u5c31\u6210\u5168\u4f60\u5427\uff01\u201d\u547c\u9ece\u5962\u90a3\u65e5\u5728\u6208\u58c1\u4e0a\u548c\u674e\u601d\u5357\u4ea4\u624b\uff0c\u82e5\u4e0d\u662f\u5b5f\u5c11\u521a\u53ca\u65f6 \u8d76\u5230\uff0c\u4ed6\u5c31\u53ef\u4ee5\u628a\u674e\u601d\u5357\u6d3b\u64d2\u7684\u3002\u6545\u6b64\u547c\u9ece\u5962\u4e00\u70b9\u4e5f\u4e0d\u628a\u674e\u601d\u5357\u653e\u5728\u5fc3\u4e0a\uff0c\u8131\u4e0b\u8888\u88df\uff0c\u5c31\u50cf \u4e00\u53ea\u6469\u4e91\u5927\u9e1f\u4f3c\u7684\u5411\u674e\u601d\u5357\u6251\u6765\u3002 \u3000\u3000\u8d64\u8001\u6e29\u602a\u58f0\u7b11\u9053\uff1a\u201c\u54c8\u54c8\uff0c\u8fd8\u6709\u4e00\u4e2a\u96cc\u513f\uff0c\u8fd9\u96cc\u513f\u957f\u5f97\u5f88\u4e0d\u9519\uff0c\u7559\u7ed9\u6211\u5427\uff01\u201d\u6768\u5a49\u5927 \u6012\uff0c\u54ac\u7d27\u94f6\u7259\uff0c\u8bdd\u4e0d\u8bf4\uff0c\u8fce\u4e0a\u4e86\u8d64\u8001\u6e29\uff0c\u201c\u5530\u201d\u7684\u5c31\u662f\u4e00\u5251\u3002 \u3000\u3000\u547c\u9ece\u5962\u7684\u8888\u88df\u5f53\u5934\u7f69\u4e0b\uff0c\u674e\u601d\u5357\u4e00\u62db\u201c\u4e3e\u706b\u64a9\u5929\u201d\uff0c\u5251\u5c16\u4e00\u6311\uff0c\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u8888\u88df\u7a7f \u4e86\u4e00\u4e2a\u6d1e\uff0c\u547c\u9ece\u5962\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u7684\u529f\u592b\u600e\u7684\u7a81\u7136\u597d\u8d77\u6765\u4e86\uff1f\u201d\u5fd9\u628a\u8888\u88df\u4e00\u62e7\uff0c \u9ed8\u8fd0\u7384\u529f\uff0c\u805a\u6210\u4e00\u675f\uff0c\u5f53\u68cd\u6765\u4f7f\uff0c\u8fd9\u624d\u89e3\u5f00\u4e86\u674e\u601d\u5357\u8fde\u73af\u6025\u88ad\u7684\u4e09\u62db\u3002 \u3000\u3000\u547c\u9ece\u5962\u4e0d\u77e5\uff0c\u5e76\u975e\u674e\u601d\u5357\u7684\u6b66\u529f\u7a81\u98de\u731b\u8fdb\uff0c\u800c\u662f\u524d\u540e\u7684\u60c5\u51b5\u4e0d\u540c\u3002\u90a3\u65e5\u674e\u601d\u5357\u5728\u6c99\u6f20\u4e2d \u53d7\u56f0\uff0c\u53c8\u9965\u53c8\u6e34\uff0c\u800c\u4e14\u8fd8\u662f\u548c\u8d64\u8001\u6e29\u5148\u6253\u4e86\u4e00\u573a\u7684\uff0c\u5f85\u5230\u4e0e\u547c\u9ece\u5962\u4ea4\u624b\u4e4b\u65f6\uff0c\u65e9\u5df2\u7d2f\u5f97\u6709\u6c14 \u6ca1\u529b\u4e86\uff0c\u5982\u4eca\u4ed6\u5219\u662f\u7cbe\u529b\u5145\u6c9b\uff0c\u8a93\u62a5\u524d\u4ec7\uff0c\u4e00\u4e0a\u6765\u5c31\u5f3a\u653b\u731b\u6251\uff0c\u9510\u4e0d\u53ef\u5f53\u3002\u6545\u800c\u7167\u9762\u4e00\u62db\uff0c \u547c\u9ece\u5962\u5c31\u9669\u4e9b\u5403\u4e86\u4ed6\u7684\u4e8f\u3002 \u3000\u3000\u8d64\u8001\u6e29\u89c1\u6768\u5a49\u4e0d\u8fc7\u662f\u4e2a\u5999\u9f84\u5973\u5b50\uff0c\u54ea\u91cc\u5c06\u5979\u653e\u5728\u5fc3\u4e0a\uff1f\u4ed6\u5fc3\u4e2d\u76d8\u7b97\u7684\u53ea\u662f\u5982\u4f55\u624b\u5230\u62ff \u6765\uff0c\u6700\u597d\u662f\u4e0d\u8ba9\u6768\u5a49\u53d7\u4f24\uff0c\u4fdd\u5168\u5979\u7684\u82b1\u5bb9\u6708\u8c8c\uff0c\u597d\u62ff\u53bb\u732e\u7ed9\u6210\u5409\u601d\u6c57\u9080\u529f\u3002 \u3000\u3000\u54ea\u77e5\u6768\u5a49\u6c14\u529b\u867d\u7136\u4e0d\u8db3\uff0c\u8eab\u6cd5\u5374\u662f\u5feb\u5982\u95ea\u7535\uff0c\u8d64\u8001\u6e29\u7b11\u58f0\u672a\u4e86\uff0c\u9661\u7136\u95f4\u53ea\u89c9\u51b7\u6c14\u68ee\u68ee\uff0c \u5bd2\u5149\u8000\u773c\uff0c\u6768\u5a49\u5df2\u662f\u201c\u94db\u201d\u7684\u4e00\u5251\uff0c\u5251\u950b\u76f4\u6307\u5230\u4e86\u4ed6\u7684\u54bd\u5589\uff01 \u3000\u3000\u8d64\u8001\u6e29\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u8fd8\u5e78\u4ed6\u6234\u6709\u5934\u76d4\uff0c\u767e\u5fd9\u4e2d\u4e00\u4e2a\u85cf\u5934\u7f29\u9888\uff0c\u53ea\u542c\u5f97\u201c\u94db\u201d\u7684\u4e00 \u58f0\uff0c\u5934\u76d4\u66ff\u4ed6\u6863\u4e86\u4e00\u5251\uff0c\u8d64\u8001\u6e29\u8fd9\u624d\u5f97\u4ee5\u5e78\u514d\u7a7f\u5589\u5256\u8111\u4e4b\u707e\u3002\u4f46\u8111\u888b\u53d7\u4e86\u9707\u8361\uff0c\u5934\u75db\u6b32\u88c2\uff0c \u4ea6\u5df2\u662f\u4ee4\u4ed6\u5341\u5206\u96be\u53d7\u7684\u4e86\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u8d64\u8001\u6e29\u7a76\u7adf\u662f\u6210\u5409\u601d\u6c57\u624b\u4e0b\u6709\u6570\u7684\u201c\u91d1\u5e10\u6b66\u58eb\u201d\u4e4b\u4e00\uff0c\u5403\u4e86\u4e2a\u4e8f\u4e4b\u540e\uff0c\u8f7b\u654c\u4e4b\u5fc3 \u4e00\u53bb\uff0c\u6768\u5a49\u60f3\u8981\u518d\u80dc\u4e00\u62db\uff0c\u53ef\u5c31\u6ca1\u6709\u90a3\u4e48\u5bb9\u6613\u4e86\u3002\u8d64\u8001\u6e29\u7684\u6c14\u529b\u6bd4\u6768\u5a49\u5927\u5f97\u591a\uff0c\u6768\u5a49\u4e0d\u6562\u548c \u4ed6\u786c\u78b0\uff0c\u53ea\u80fd\u4ed7\u7740\u8f7b\u7075\u7684\u5251\u6cd5\u6270\u654c\uff0c\u7a0d\u5fae\u5360\u5f97\u4e00\u70b9\u4e0a\u98ce\u3002 \u3000\u3000\u6b64\u65f6\u54f2\u522b\u8fd9\u8fb9\u8fd8\u6709\u4e00\u4e2a\u9ed1\u8863\u5587\u561b\u5c1a\u672a\u52a0\u5165\u6218\u56e2\uff0c\u4ed6\u5728\u4e00\u8fb9\u89c2\u6218\uff0c\u6ce8\u610f\u7684\u91cd\u5fc3\u653e\u5728\u674e\u601d\u5357 \u8eab\u4e0a\uff0c\u770b\u4e86\u5341\u6570\u62db\uff0c\u89c1\u547c\u9ece\u5962\u6218\u674e\u601d\u5357\u4e0d\u4e0b\uff0c\u9042\u63d0\u8d77\u4e86\u7985\u6756\uff0c\u7b11\u9053\uff1a\u201c\u674e\u516c\u5b50\uff0c\u4e0d\u662f\u6211\u4eec\u501a \u591a\u4e3a\u80dc\uff0c\u53ea\u56e0\u5927\u6c57\u6709\u547d\uff0c\u8981\u6211\u4eec\u2018\u8bf7\u2019\u4f60\u56de\u53bb\uff0c\u4f60\u6297\u547d\u4e0d\u4ece\uff0c\u6ca1\u5948\u4f55\u6211\u4eec\u53ea\u597d\u5f97\u7f6a\u4f60\u4e86\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u6012\u9053\uff1a\u201c\u6211\u672c\u6765\u5c31\u4e0d\u51c6\u5907\u6d3b\u7740\u56de\u53bb\uff0c\u4f60\u4e0d\u5fc5\u5047\u60fa\u60fa\u4e86\uff0c\u95f2\u8bdd\u5c11\u8bf4\uff0c\u8981\u6765\u5c31\u6765\uff01\u201d \u9ed1\u8863\u5587\u561b\u54c8\u54c8\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u674e\u516c\u5b50\u771f\u662f\u4e2a\u723d\u5feb\u4eba\uff0c\u597d\uff0c\u770b\u5728\u4f60\u8fd9\u4e2a\u8c6a\u723d\u7684\u4efd\u4e0a\uff0c\u6211\u5012\u4e5f\u672a \u5c1d\u4e0d\u53ef\u9976\u4f60\u4e00\u547d\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u5927\u6012\u9053\uff1a\u201c\u8c01\u8981\u4f60\u2014\u2014\u201d\u4e00\u4e2a\u201c\u9976\u201d\u5b57\u672a\u66fe\u51fa\u53e3\uff0c\u9ed1\u8863\u5587\u561b\u7684\u7985\u6756\u5df2\u7ecf\u6253\u4e86\u5230 \u6765\u3002\u674e\u601d\u5357\u53cd\u624b\u4e00\u5251\uff0c\u53ea\u542c\u5f97\u201c\u5496\u201d\u7684\u4e00\u58f0\uff0c\u706b\u82b1\u98de\u6e85\uff0c\u674e\u601d\u5357\u864e\u53e3\u9178\u9ebb\uff0c\u5b9d\u5251\u51e0\u4e4e\u638c\u63e1\u4e0d \u7262\u3002\u547c\u9ece\u5962\u4e58\u673a\u53cd\u51fb\uff0c\u8888\u88df\u75be\u5377\u8fc7\u6765\uff0c\u674e\u601d\u5357\u811a\u5c16\u4e00\u70b9\uff0c\u4fbf\u51fa\u201c\u9ec4\u9e66\u51b2\u9704\u201d\u7684\u8d85\u5353\u8f7b\u529f\uff0c\u8eab \u5f62\u5e73\u5730\u62d4\u8d77\uff0c\u8888\u88df\u4ece\u4ed6\u811a\u4e0b\u63a0\u8fc7\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u90a3\u9ed1\u8863\u5587\u561b\u7985\u6756\u4e00\u4e3e\uff0c\u53c8\u662f\u4e00\u62db\u201c\u4e3e\u706b\u64a9\u5929\u201d\uff0c\u4e0a\u6233\u674e\u601d\u5357\u7684\u5c0f\u8179\u3002 \u674e\u601d\u5357\u4eba\u5728\u534a\u7a7a\uff0c\u65e0\u53ef\u95ea\u907f\uff0c\u4eba\u6025\u751f\u667a\uff0c\u534a\u7a7a\u84e6\u5730\u4e00\u4e2a\u9e5e\u5b50\u7ffb\u8eab\uff0c\u5251\u5c16\u8f7b\u8f7b\u5728\u90a3\u9ed1\u8863\u5587\u561b\u7684 \u6756\u5934\u4e0a\u4e00\u70b9\uff0c\u501f\u4e86\u4ed6\u90a3\u7985\u6756\u7684\u4e00\u6325\u4e4b\u529b\uff0c\u8eab\u4f3c\u79bb\u5f26\u4e4b\u7bad\uff0c\u7eb5\u51fa\u4e86\u4e09\u4e08\u5f00\u5916\u3002 \u3000\u3000\u8fd9\u4e00\u62db\u7528\u5f97\u60ca\u9669\u7edd\u4f26\uff0c\u9ed1\u8863\u5587\u561b\u4e5f\u4e0d\u7531\u5f97\u6697\u6697\u4f69\u670d\u3002\u547c\u9ece\u5962\u6050\u6015\u4ed6\u4e0e\u6768\u6ed4\u4f1a\u5408\uff0c\u62a2\u5148\u62e6 \u5728\u4ed6\u4eec\u4e8c\u4eba\u4e4b\u95f4\uff0c\u9ed1\u8863\u5587\u561b\u5982\u5f71\u968f\u5f62\uff0c\u7acb\u5373\u8ddf\u8e2a\u9053\u5230\u3002\u674e\u601d\u5357\u5df2\u77e5\u4ed6\u7684\u529f\u529b\u8fd8\u5728\u547c\u9ece\u5962\u4e4b \u4e0a\uff0c\u53ea\u80fd\u667a\u53d6\uff0c\u4e0d\u80fd\u529b\u654c\u3002\u5f53\u4e0b\u6539\u7528\u4ee5\u67d4\u5145\u521a\u7684\u5251\u6cd5\uff0c\u548c\u4ed6\u518d\u6597\u3002\u547c\u9ece\u5962\u5835\u622a\u6210\u529f\uff0c\u56de\u8fc7\u8eab \u6765\uff0c\u518d\u4e0e\u9ed1\u8863\u5587\u561b\u8054\u624b\uff0c\u5de6\u53f3\u5939\u653b\u3002\u674e\u601d\u5357\u5bf9\u4ed8\u4e00\u4e2a\u9ed1\u8863\u5587\u561b\u5df2\u611f\u5403\u529b\uff0c\u547c\u9ece\u5962\u7684\u672c\u9886\u5e76\u4e0d \u8f93\u4e8e\u674e\u601d\u5357\uff0c\u4ed6\u4e00\u4e0a\u6765\uff0c\u674e\u601d\u5357\u53ef\u5c31\u5e94\u4ed8\u4e3a\u96be\u4e86\u3002 \u3000\u3000\u6768\u6ed4\u5144\u59b9\u770b\u89c1\u674e\u601d\u5357\u5904\u5883\u5371\u6025\uff0c\u90fd\u60f3\u8fc7\u53bb\u4e0e\u4ed6\u4f1a\u5408\u3002\u6768\u6ed4\u9996\u5148\u53d1\u52a8\u653b\u52bf\uff0c\u5411\u54f2\u522b\u731b\u653b\u3002 \u54f2\u522b\u7b11\u9053\uff1a\u201c\u4f60\u7559\u70b9\u529b\u6c14\uff0c\u8fd8\u53ef\u4ee5\u4e0e\u6211\u591a\u6253\u4e00\u4f1a\u3002\u201d\u6768\u6ed4\u66b4\u98ce\u9aa4\u96e8\u822c\u5730\u75be\u653b\u4e86\u4e8c\u4e09\u5341\u62db\uff0c\u54f2 \u522b\u5bf8\u6b65\u4e0d\u8ba9\uff0c\u6ca1\u6709\u591a\u4e45\uff0c\u6768\u6ed4\u5df2\u662f\u5927\u6c57\u6dcb\u6f13\uff0c\u54f2\u522b\u4e58\u673a\u53cd\u653b\uff0c\u53cd\u800c\u628a\u4ed6\u903c\u9000\u4e86\u51e0\u6b65\u3002 \u3000\u3000\u539f\u6765\u6768\u6ed4\u4e0e\u54f2\u522b\u4e43\u662f\u5404\u6709\u5343\u79cb\uff0c\u54f2\u522b\u80dc\u5728\u6c14\u529b\u6c89\u96c4\uff0c\u800c\u6768\u6ed4\u5219\u80dc\u5728\u62db\u6570\u7cbe\u5999\u3002\u6768\u6ed4\u672c\u6765 \u5e94\u8be5\u51dd\u795e\u9759\u6c14\uff0c\u5bfb\u89c5\u5bf9\u65b9\u7684\u7834\u7efd\uff0c\u65b9\u6709\u53ef\u80dc\u4e4b\u673a\u7684\uff0c\u5982\u4eca\u4ed6\u4ee5\u5df1\u4e4b\u77ed\uff0c\u653b\u654c\u4e4b\u957f\uff0c\u5f53\u7136\u5c31\u96be \u514d\u5403\u4e8f\u4e86\u3002\u597d\u5728\u4ed6\u7684\u626b\u53f6\u5251\u6cd5\u53d8\u5316\u7e41\u590d\uff0c\u653b\u5b88\u54b8\u5b9c\uff0c\u54f2\u522b\u5c1a\u672a\u6478\u5f97\u900f\u5176\u4e2d\u7684\u5965\u5999\uff0c\u53ea\u80fd\u62b5 \u5fa1\uff0c\u4e0d\u80fd\u7834\u89e3\u3002\u54f2\u522b\u662f\u66fe\u7ecf\u5403\u8fc7\u4ed6\u8fd9\u5957\u5251\u6cd5\u7684\u4e8f\u7684\uff0c\u5fc3\u4e2d\u4e0d\u65e0\u987e\u5fcc\uff0c\u662f\u4ee5\u867d\u7136\u7a0d\u5360\u4e0a\u98ce\uff0c\u5374 \u662f\u4e0d\u6562\u8fc7\u5206\u7d27\u903c\u3002 \u3000\u3000\u6768\u6ed4\u88ab\u54f2\u522b\u5835\u4f4f\uff0c\u6768\u5a49\u5374\u6446\u8131\u4e86\u8d64\u8001\u6e29\u7684\u7f20\u7ed5\u3002\u8d64\u8001\u6e29\u5728\u201c\u91d1\u5e10\u6b66\u58eb\u201d\u4e2d\u540d\u5217\u7b2c\u516b\uff0c\u8fdc \u4e0d\u5982\u54f2\u522b\u7684\u672c\u9886\u9ad8\u5f3a\u3002\u800c\u6768\u5a49\u7684\u672c\u9886\u5219\u548c\u54e5\u54e5\u5dee\u4e0d\u4e86\u591a\u5c11\uff0c\u6545\u6b64\u54f2\u522b\u53ef\u4ee5\u4e0e\u5979\u54e5\u54e5\u6253\u6210\u5e73 \u624b\uff0c\u8d64\u8001\u6e29\u5374\u6321\u4e0d\u4f4f\u5979\u3002 \u3000\u3000\u6768\u5a49\u8d70\u5251\u8f7b\u7075\uff0c\u628a\u8d64\u8001\u6e29\u903c\u5f97\u56de\u5200\u62db\u67b6\u3002\u6768\u5a49\u4e00\u4e2a\u201c\u71d5\u5b50\u7a7f\u5e18\u201d\uff0c\u201c\u547c\u201d\u7684\u4e00\u58f0\uff0c\u5c31\u4ece \u4ed6\u7684\u5934\u9876\u201c\u98de\u201d\u8fc7\u53bb\u4e86\u3002 \u3000\u3000\u8d64\u8001\u6e29\u6c14\u5f97\u54c7\u54c7\u5927\u53eb\uff1a\u201c\u6666\u6c14\uff0c\u6666\u6c14\uff01\u201d\u8f6c\u8eab\u8ffd\u8d76\u3002\u8bf4\u65f6\u8fdf\u3001\u90a3\u65f6\u5feb\uff0c\u53ea\u89c1\u6768\u5a49\u5728\u534a\u7a7a \u4e2d\u4e00\u4e2a\u201c\u9e5e\u5b50\u7ffb\u8eab\u201d\uff0c\u5251\u5149\u5982\u7ec3\uff0c\u65e9\u5df2\u5411\u547c\u9ece\u5962\u5f53\u5934\u523a\u4e0b\uff01 \u3000\u3000\u547c\u9ece\u5962\u8888\u88df\u4e00\u6296\uff0c\u5c31\u50cf\u4e00\u7247\u7ea2\u4e91\uff0c\u76d6\u7740\u4e86\u4ed6\u7684\u5934\u9876\u3002\u6768\u5a49\u4e00\u5251\u523a\u7740\u8888\u88df\uff0c\u53ea\u542c\u5f97\u201c\u55e4\u201d \u7684\u4e00\u58f0\uff0c\u8888\u88df\u7a7f\u4e86\u4e00\u4e2a\u6d1e\uff0c\u6768\u5a49\u4e5f\u88ab\u4ed6\u8888\u88df\u6247\u8d77\u7684\u98ce\u529b\uff0c\u8361\u8fc7\u4e00\u8fb9\uff0c\u4e0d\u8fc7\u5374\u6ca1\u6709\u7ed9\u4ed6\u7684\u8888\u88df \u5377\u4f4f\uff0c\u6768\u5a49\u8f7b\u8f7b\u5de7\u5de7\u5730\u843d\u4e0b\u5730\u6765\uff0c\u6070\u597d\u843d\u5728\u674e\u601d\u5357\u7684\u8eab\u8fb9\u3002\u4e24\u4eba\u4fbf\u5373\u5e76\u80a9\u4f5c\u6218\u3002 \u3000\u3000\u674e\u601d\u5357\u89c1\u6768\u5a49\u5192\u9669\u6765\u63f4\uff0c\u5fc3\u4e2d\u5341\u5206\u611f\u6fc0\u3002\u5f53\u4e0b\u7cbe\u795e\u500d\u632f\uff0c\u767b\u65f6\u628a\u5f62\u52bf\u626d\u8f6c\u8fc7\u6765\u3002 \u3000\u3000\u53ef\u60dc\u4ed6\u4eec\u53ea\u80fd\u53d6\u5f97\u77ed\u6682\u7684\u4f18\u52bf\uff0c\u8d64\u8001\u6e29\u4e00\u8d76\u5230\uff0c\u4ed6\u4eec\u5728\u4f17\u5be1\u4e0d\u654c\u7684\u5f62\u52bf\u4e4b\u4e0b\uff0c\u53c8\u4e0d\u80fd\u4e0d \u5c48\u5904\u4e0b\u98ce\u4e86\u3002\u8d64\u8001\u6e29\u7684\u672c\u9886\u867d\u4e0d\u5f88\u9ad8\uff0c\u4f46\u4e5f\u5177\u6709\u5a01\u80c1\u7684\u529b\u91cf\u3002\u9ed1\u8863\u5587\u561b\u4e0e\u547c\u9ece\u5962\u5374\u662f\u4e00\u6d41\u9ad8 \u624b\uff0c\u674e\u6768\u4e8c\u4eba\u8054\u624b\uff0c\u5bf9\u4ed8\u4ed6\u4eec\uff0c\u4e45\u6218\u4e0b\u53bb\uff0c\u4e5f\u8fd8\u662f\u8981\u5403\u4e8f\u7684\u3002\u5982\u4eca\u52a0\u4e0a\u4e00\u4e2a\u8d64\u8001\u6e29\uff0c\u53cc\u65b9\u7684 \u529b\u91cf\u9a6c\u4e0a\u5c31\u8d77\u4e86\u53d8\u5316\u3002 \u3000\u3000\u674e\u601d\u5357\u4e0e\u6768\u5a49\u80cc\u9760\u7740\u80cc\uff0c\u594b\u52c7\u529b\u6218\uff0c\u8f6c\u773c\u53c8\u6597\u4e86\u6570\u5341\u62db\u3002\u674e\u601d\u5357\u5927\u6c57\u6dcb\u6ee1\uff0c\u8fd8\u53ef\u652f\u6301\uff0c \u6768\u5a49\u6bd5\u7adf\u662f\u4e2a\u5f31\u8d28\u5973\u5b50\uff0c\u6c14\u529b\u6709\u9650\uff0c\u63a5\u8fde\u82e6\u6597\u4e24\u573a\uff0c\u7981\u4e0d\u4f4f\u5a07\u5598\u5401\u5401\uff0c\u5251\u62db\u4f7f\u51fa\uff0c\u5df2\u662f\u529b\u4e0d \u4ece\u5fc3\u3002 \u3000\u3000\u674e\u601d\u5357\u5fc3\u91cc\u4e00\u9178\uff0c\u8bf4\u9053\uff1a\u201c\u5a49\u59b9\uff0c\u6211\u7d2f\u4e86\u4f60\u4e86\u3002\u201d\u6768\u5a49\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u8bf4\u8fc7\u54b1\u4eec\u662f\u540c\u547d \u9e33\u9e2f\u5417\uff1f\u5982\u4eca\u4f60\u53c8\u8bf4\u8fd9\u6837\u7684\u8bdd\uff0c\u90a3\u4e0d\u662f\u628a\u6211\u5f53\u4f5c\u5916\u4eba\u4e86\uff1f\u201d\u6768\u5a49\u8fd9\u4e00\u628a\u751f\u6b7b\u7f6e\u4e4b\u5ea6\u5916\u7684\u6001 \u5ea6\uff0c\u4f7f\u5f97\u674e\u601d\u5357\u53d7\u5230\u5f88\u5927\u7684\u9f13\u821e\u3002 \u3000\u3000\u8d64\u8001\u6e29\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u7684\u60c5\u8bdd\u7559\u5230\u548c\u6797\u518d\u8bf4\u5427\u3002\u73b0\u5728\u4f60\u4eec\u53ea\u6709\u4e56\u4e56\u7684\u675f\u624b\u5c31\u64d2\uff0c\u5426\u5219\u5c31 \u5f53\u771f\u53ea\u80fd\u5230\u9ec4\u6cc9\u8def\u4e0a\u53bb\u505a\u540c\u547d\u9e33\u9e2f\u4e86\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u5927\u6012\uff0c\u84e6\u5730\u53cd\u624b\u4e00\u5251\uff0c\u8d64\u8001\u6e29\u9762\u5411\u6768\u5a49\uff0c\u60f3\u4e0d\u5230\u674e\u601d\u5357\u8fd9\u4e00\u62db\u53cd\u624b\u5251\u6765\u5f97\u5982\u6b64\u7a81 \u7136\uff0c\u5de6\u81c2\u7ed9\u5251\u950b\u5272\u5f00\u4e86\u4e00\u9053\u4e09\u5bf8\u591a\u957f\u7684\u4f24\u53e3\uff0c\u75db\u5f97\u4ed6\u5927\u53eb\uff0c\u8fde\u5fd9\u540e\u9000\u3002 \u3000\u3000\u53ef\u662f\u201c\u87b3\u8782\u6355\u8749\uff0c\u9ec4\u96c0\u5728\u540e\u201d\uff0c\u5c31\u5728\u674e\u601d\u5357\u5411\u8d64\u8001\u8e22\u75db\u4e0b\u6740\u624b\u4e4b\u9645\uff0c\u9ed1\u8863\u6b66\u58eb\u7684\u7985\u6756\u4e5f \u5728\u540c\u4e00\u65f6\u95f4\u5411\u674e\u601d\u5357\u7684\u80cc\u5fc3\u731b\u6363\u3002\u6768\u5a49\u5931\u58f0\u60ca\u547c\uff0c\u767e\u5fd9\u4e2d\u8fde\u5fd9\u62bd\u5251\u66ff\u674e\u601d\u5357\u62db\u67b6\u3002\u6768\u5a49\u529b \u5f31\uff0c\u5251\u6756\u76f8\u4ea4\uff0c\u201c\u94db\u201d\u7684\u4e00\u58f0\uff0c\u624b\u4e2d\u7684\u9752\u94a2\u5251\u98de\u4e0a\u4e86\u534a\u7a7a\uff0c\u864e\u53e3\u4ea6\u5df2\u8ff8\u88c2\u6d41\u8840\u3002\u674e\u601d\u5357\u56de\u8eab \u4e00\u62db\u201c\u6a2a\u4e91\u65ad\u5cf0\u201d\uff0c\u957f\u5251\u8361\u5f00\u4e86\u9ed1\u8863\u5587\u561b\u7684\u7985\u6756\uff0c\u968f\u5373\u628a\u4ed6\u7684\u5251\u4ea4\u7ed9\u6768\u5a49\uff0c\u8bf4\u9053\uff1a\u201c\u5a49\u59b9\uff0c \u4f60\u7528\u6211\u8fd9\u628a\u5251\uff01\u201d\u5728\u8fd9\u6fc0\u6218\u5f53\u4e2d\uff0c\u4e0d\u5bb9\u6768\u5a49\u6709\u4e1d\u6beb\u7684\u8fdf\u7591\uff0c\u53ea\u80fd\u628a\u674e\u601d\u5357\u7684\u5251\u63a5\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u674e\u601d\u5357\u6539\u7528\u5c11\u6797\u6d3e\u5ae1\u4f20\u7684\u201c\u822c\u82e5\u638c\u201d\u6cd5\uff0c\u731b\u6253\u547c\u9ece\u5962\u4e0e\u90a3\u9ed1\u8863\u6b66\u58eb\u3002\u822c\u82e5\u638c\u80fd\u4f24\u5947\u7ecf\u516b \u8109\uff0c\u9ed1\u8863\u6b66\u58eb\u662f\u4e2a\u8bc6\u8d27\u7684\u4eba\uff0c\u89c1\u674e\u601d\u5357\u62fc\u6b7b\u731b\u6253\uff0c\u5012\u662f\u4e0d\u80fd\u4e0d\u6709\u4e9b\u987e\u5fcc\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5927\u6c57 \u7684\u547d\u4ee4\u867d\u7136\u662f\u8bf4\u8feb\u4e0d\u5f97\u5df2\u53ef\u4ee5\u6740\u4ed6\uff0c\u4f46\u7a76\u7adf\u8fd8\u662f\u628a\u4ed6\u751f\u64d2\u7684\u3002\u4f55\u51b5\u8fd9\u5c0f\u5b50\u8fd8\u6709\u4e00\u4e2a\u9760\u5c71\uff0c\u6211 \u6740\u4e86\u4ed6\uff0c\u5927\u6c57\u7eb5\u7136\u4e0d\u52a0\u602a\u8d23\uff0c\u4e09\u516c\u4e3b\u5374\u662f\u4e00\u5b9a\u8981\u6068\u6211\u7684\u4e86\u3002\u201d \u3000\u3000\u539f\u6765\u5728\u8fd9\u9ed1\u8863\u6b66\u58eb\u4e34\u884c\u4e4b\u9645\uff0c\u660e\u6167\u516c\u4e3b\u66fe\u7ecf\u90d1\u91cd\u5730\u5411\u4ed6\u8bf7\u6258\uff0c\u5e0c\u671b\u4ed6\u624b\u4e0b\u7559\u60c5\uff0c\u4e0d\u53ef\u4f24 \u4e86\u674e\u601d\u5357\u7684\u6027\u547d\u7684\u3002 \u3000\u3000\u4e00\u6765\u662f\u7531\u4e8e\u674e\u601d\u5357\u7684\u62fc\u6b7b\u731b\u640f\uff1b\u4e8c\u6765\u662f\u788d\u7740\u660e\u6167\u516c\u4e3b\u7684\u60c5\u9762\uff0c\u9ed1\u8863\u6b66\u58eb\u5fc3\u60f3\uff1a\u201c\u6211\u4f55\u5fc5 \u4e0e\u4ed6\u62fc\u547d\uff0c\u5f85\u4ed6\u6c14\u529b\u8017\u5c3d\uff0c\u624b\u5230\u64d2\u6765\uff0c\u5c82\u4e0d\u66f4\u5999\uff1f\u201d\u9ed1\u8863\u6b66\u58eb\u8fd9\u4e48\u4e00\u60f3\uff0c\u53cd\u800c\u8f6c\u653b\u4e3a\u5b88\uff0c\u674e \u601d\u5357\u5f62\u52bf\u7a0d\u5fae\u597d\u8f6c\uff0c\u4f46\u5df2\u662f\u9677\u4e8e\u82e6\u6597\u4e4b\u4e2d\u3002 \u3000\u3000\u8d64\u8001\u6e29\u88f9\u597d\u4e86\u4f24\uff0c\u6012\u4e0d\u53ef\u904f\uff0c\u63d0\u5200\u590d\u4e0a\uff0c\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b50\uff0c\u6b7b\u5230\u4e34\u5934\uff0c\u8fd8\u6562\u901e\u51f6\uff0c\u6211 \u975e\u6bd9\u4e86\u4f60\u8fd9\u5c0f\u5b50\u4e0d\u53ef\uff01\u201d \u3000\u3000\u539f\u6765\u8d64\u8001\u6e29\u4e0e\u54f2\u522b\u662f\u201c\u91d1\u5e10\u6b66\u571f\u201d\u7684\u8eab\u4efd\uff0c\u660e\u6167\u516c\u4e3b\u4e0d\u4fbf\u5230\u5979\u7236\u4eb2\u7684\u201c\u91d1\u5e10\u201d\uff0c\u5f53\u7740\u4e00 \u4f17\u6b66\u58eb\u7684\u9762\u4e3a\u674e\u601d\u5357\u8bf4\u60c5\uff0c\u6545\u800c\u53ea\u6709\u4e0e\u5979\u6bd4\u8f83\u63a5\u8fd1\u7684\u9ed1\u8863\u5587\u561b\u53d7\u4e86\u5979\u7684\u8bf7\u6258\u3002 \u3000\u3000\u9ed1\u8863\u5587\u561b\u5411\u8d64\u8001\u6e29\u8fde\u6253\u773c\u8272\uff0c\u8d64\u8001\u6e29\u5140\u662f\u4e0d\u609f\uff0c\u9ed1\u8863\u5587\u561b\u903c\u5f97\u8bf4\u9053\uff1a\u201c\u6d3b\u6349\u8fd9\u4e2a\u5c0f\u5b50\u529f \u52b3\u66f4\u5927\uff0c\u4f60\u82e5\u6c14\u6068\u4e0d\u8fc7\uff0c\u780d\u4ed6\u4e00\u5200\uff0c\u4e5f\u5c31\u662f\u4e86\u3002\u201d \u3000\u3000\u8d64\u8001\u6e29\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u4f60\u662f\u4e0d\u8bb8\u6211\u6740\u4ed6\uff01\u597d\u5427\uff0c\u6211\u542c\u4f60\u7684\u8bdd\uff0c\u53ea\u780d\u4ed6\u4e24\u5200\uff0c\u4e00\u5200\u7b97\u4f5c\u5229 \u606f\u3002\u201d\u6768\u5a49\u5fc3\u5934\u706b\u8d77\uff0c\u65a5\u9053\uff1a\u201c\u4f60\u8981\u780d\u4ed6\u4e24\u5200\uff0c\u4f60\u5148\u9886\u6211\u4e00\u5251\uff01\u201d\u6b64\u65f6\u9ed1\u8863\u6b66\u58eb\u4e0e\u547c\u9ece\u5962\u90fd \u662f\u6b63\u5728\u5bf9\u4ed8\u674e\u601d\u5357\uff0c\u6768\u5a49\u4e00\u54ac\u94f6\u7259\uff0c\u4f7f\u8db3\u4e86\u6c14\u529b\u5411\u7740\u8d64\u8001\u6e29\u5530\u5730\u4fbf\u662f\u4e00\u5251\uff01 \u3000\u3000\u8d64\u8001\u6e29\u5de6\u81c2\u53d7\u4f24\uff0c\u52a8\u4f5c\u4e0d\u7075\uff0c\u4ed6\u53c8\u6599\u4e0d\u5230\u6768\u5a49\u7a81\u7136\u548c\u4ed6\u62fc\u547d\uff0c\u6768\u5a49\u8fd9\u4e00\u5251\u53c8\u72e0\u53c8\u5feb\uff0c \u201c\u5657\u201d\u7684\u4e00\u58f0\u54cd\uff0c\u5251\u5c16\u5df2\u662f\u523a\u5165\u4e86\u4ed6\u7684\u80f8\u819b\u3002\u8d64\u8001\u6e29\u4e00\u58f0\u63ba\u547c\uff0c\u5012\u9000\u51e0\u6b65\uff0c\u4f4e\u5934\u4e00\u770b\uff0c\u53ea\u89c1 \u80f8\u53e3\u5f00\u4e86\u4e2a\u6d1e\uff0c\u8840\u6d41\u5982\u6ce8\u3002\u8d64\u8001\u6e29\u53eb\u9053\uff1a\u201c\u6211\u8981\u6b7b\u5566\uff01\u201d\u811a\u4e00\u8f6f\u5c31\u5012\u4e0b\u5730\u4e86\u3002 \u3000\u3000\u6768\u5a49\u7531\u4e8e\u6c14\u529b\u4e0d\u8db3\uff0c\u8fd9\u4e00\u5251\u5176\u5b9e\u5c1a\u672a\u523a\u7740\u4ed6\u7684\u5fc3\u623f\uff0c\u53ea\u662f\u523a\u7a7f\u4e86\u4ed6\u80f8\u53e3\u7684\u4e00\u56e2\u80a5\u8089\u800c \u5df2\u3002\u54f2\u522b\u662f\u4e2a\u6709\u7ecf\u9a8c\u7684\u6218\u58eb\uff0c\u542c\u4e86\u8d64\u8001\u6e29\u7684\u53eb\u558a\uff0c\u76b1\u8d77\u7709\u5934\u8bf4\u9053\uff1a\u201c\u6302\u70b9\u5f69\u7b97\u5f97\u4e86\u4ec0\u4e48\uff0c\u5927 \u547c\u5c0f\u53eb\u5730\u4f5c\u51fa\u8fd9\u7b49\u8113\u5305\u76f8\u6765\uff0c\u4e0d\u5bb3\u7f9e\u4e48\uff1f\u4f60\u6b7b\u4e0d\u4e86\u7684\uff0c\u5feb\u5feb\u6577\u4e0a\u91d1\u521b\u836f\u5427\u3002\u4f60\u82e5\u662f\u5bb3\u6015\uff0c\u4f60 \u5148\u56de\u53bb\u3002\u201d \u3000\u3000\u8d64\u8001\u6e29\u8fd9\u65f6\u4e5f\u53d1\u89c9\u4e86\u5e76\u975e\u81f4\u547d\u4e4b\u4f24\uff0c\u4f46\u770b\u7740\u9c9c\u8840\u6c69\u6c69\u6d41\u51fa\uff0c\u5fc3\u91cc\u4e5f\u7684\u786e\u6709\u51e0\u5206\u5bb3\u6015\u3002\u5f53 \u4e0b\u8fde\u5fd9\u6577\u4e0a\u4e86\u91d1\u521b\u836f\uff0c\u4ecd\u662f\u89c9\u5f97\u75bc\u75db\u4e0d\u6b62\u3002 \u3000\u3000\u8d64\u8001\u6e29\u4f24\u4e0a\u52a0\u4f24\uff0c\u4e0d\u6562\u518d\u6218\uff0c\u6c14\u6124\u4e0d\u8fc7\uff0c\u5636\u58f0\u53eb\u9053\uff1a\u201c\u8fd9\u96cc\u513f\u4e0d\u8bc6\u62ac\u4e3e\uff0c\u6211\u5012\u6709\u601c\u9999\u60dc \u7389\u4e4b\u5fc3\uff0c\u5979\u7adf\u7136\u8981\u53d6\u6211\u7684\u6027\u547d\u3002\u4f60\u4eec\u7ed9\u6211\u628a\u8fd9\u96cc\u513f\u6740\u4e86\u5427\uff01\u201d \u3000\u3000\u9ed1\u8863\u5587\u561b\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u4e2a\u96cc\u513f\u4f3c\u4e4e\u662f\u8fd9\u5c0f\u5b50\u7684\u60c5\u4eba\uff0c\u4e09\u516c\u4e3b\u4e00\u5b9a\u4e0d\u4f1a\u6b22\u559c\u89c1\u5230\u5979 \u7684\u3002\u201d\u4e8e\u662f\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u6211\u66ff\u4f60\u62a5\u8fd9\u4e00\u5251\u4e4b\u4ec7\u4fbf\u662f\uff0c\u4f60\u56de\u53bb\u5427\u3002\u201d \u3000\u3000\u9ed1\u8863\u5587\u561b\u9661\u7136\u6539\u53d8\u6218\u672f\uff0c\u5411\u6768\u5a49\u6025\u653b\u3002\u6768\u5a49\u523a\u4f24\u4e86\u8d64\u8001\u6e29\u4e4b\u540e\uff0c\u6c14\u529b\u5dee\u4e0d\u591a\u5df2\u7ecf\u8017\u5c3d\uff0c \u674e\u601d\u5357\u820d\u4e86\u6027\u547d\u4e3a\u5979\u9632\u62a4\uff0c\u4ecd\u662f\u906e\u62e6\u4e0d\u4f4f\uff0c\u4e0d\u6d88\u7247\u523b\uff0c\u4e24\u4eba\u90fd\u662f\u9891\u9891\u906d\u9047\u9669\u62db\uff0c\u9669\u8c61\u73af\u751f\u3002 \u3000\u3000\u6768\u6ed4\u53c8\u60ca\u53c8\u6012\uff0c\u731b\u5730\u4e00\u58f0\u5927\u559d\uff1a\u201c\u4e0d\u662f\u4f60\u6b7b\uff0c\u4fbf\u662f\u6211\u4ea1\uff01\u201d\u4e0d\u987e\u4e00\u5207\uff0c\u8fd0\u5251\u5982\u98ce\uff0c\u5411\u54f2 \u522b\u76f4\u51b2\u8fc7\u53bb\u3002\u54f2\u522b\u6c14\u529b\u867d\u7136\u6bd4\u4ed6\u5927\uff0c\u4f46\u89c1\u4e86\u4ed6\u8fd9\u6837\u51f6\u72e0\u7684\u6253\u6cd5\uff0c\u4e5f\u662f\u4e0d\u7531\u5f97\u5403\u4e86\u4e00\u60ca\uff0c\u8fde\u5fd9 \u95ea\u907f\u3002\u8bf4\u65f6\u8fdf\uff0c\u54f2\u522b\u53cd\u624b\u4e00\u5200\uff0c\u6ca1\u6709\u5288\u7740\u6768\u6ed4\uff0c\u6768\u6ed4\u5df2\u7ecf\u51b2\u8fc7\u53bb\u4e86\u3002 \u3000\u3000\u9ed1\u8863\u5587\u561b\u6b63\u5728\u7528\u5230\u4e00\u62db\u201c\u6cf0\u5c71\u538b\u9876\u201d\uff0c\u7985\u6756\u9ad8\u4e3e\uff0c\u5411\u7740\u6768\u5a49\u7684\u5929\u7075\u76d6\u6253\u4e0b\u3002\u6768\u6ed4\u6012\u543c\uff1a \u201c\u79c3\u9a74\u4f11\u5f97\u4f24\u5bb3\u6211\u59b9\uff01\u201d\u7985\u6756\u76f8\u4ea4\uff0c\u201c\u54d7\u201d\u7684\u4e00\u58f0\uff0c\u706b\u661f\u84ec\u98de\uff0c\u9ed1\u8863\u5587\u561b\u864e\u53e3\u8ff8\u88c2\uff0c\u6768\u6ed4\u5251 \u950b\u4e00\u5212\uff0c\u5272\u7834\u4e86\u4ed6\u7684\u8888\u88df\uff0c\u6b63\u8981\u4f7f\u52b2\u63d2\u5165\u4ed6\u7684\u5c0f\u8179\uff0c\u5ffd\u89c9\u80cc\u540e\u91d1\u5203\u5288\u98ce\u4e4b\u58f0\uff0c\u6768\u6ed4\u62db\u6570\u5df2\u7ecf \u4f7f\u8001\uff0c\u96be\u4ee5\u56de\u5251\u907f\u62e6\uff0c\u867d\u7136\u95ea\u8eb2\u5f97\u5feb\uff0c\u80cc\u810a\u4ea6\u5df2\u7740\u4e86\u54f2\u522b\u7684\u4e00\u5200\u3002\u4e0d\u8fc7\u4e5f\u5e78\u4e8f\u4ed6\u95ea\u8eb2\u5f97\u5feb\uff0c \u810a\u9aa8\u867d\u7ed9\u5200\u950b\u5272\u88c2\uff0c\u8fd8\u4e0d\u662f\u81f4\u547d\u4e4b\u4f24\u3002 \u3000\u3000\u53ef\u662f\u90a3\u9ed1\u8863\u5587\u561b\u7684\u6b66\u529f\u4e0d\u5728\u6768\u6ed4\u4e4b\u4e0b\uff0c\u4ed6\u9003\u8fc7\u4e86\u6768\u6ed4\u7684\u90a3\u4e00\u5251\u7a7f\u5fc3\u523a\u8179\u4e4b\u707e\uff0c\u7acb\u5373\u4e58\u673a \u53cd\u51fb\uff1b\u8fd9\u4e00\u6756\u6253\u6765\uff0c\u53ef\u5c31\u9053\u6210\u4e86\u6768\u6ed4\u7684\u81f4\u547d\u4e4b\u4f24\u4e86\u3002\u6768\u6ed4\u7684\u80f8\u819b\u7ed9\u4ed6\u7684\u7985\u6756\u91cd\u91cd\u4e00\u51fb\uff0c \u201c\u54c7\u201d\u7684\u4e00\u53e3\u9c9c\u8840\u55b7\u4e86\u51fa\u6765\uff0c\u5012\u7eb5\u51fa\u4e8c\u4e08\u5f00\u5916\uff0c \u3000\u3000\u9ed1\u8863\u5587\u561b\u54c8\u54c8\u5927\u7b11\uff0c\u8fc8\u4e0a\u4e24\u6b65\uff0c\u201c\u547c\u201d\u7684\u53c8\u662f\u4e00\u6756\u5411\u524d\u6311\u53bb\u3002\u4ed6\u8fd9\u6839\u7985\u6756\u6709\u516b\u5c3a\u591a\u957f\uff0c \u6768\u6ed4\u5012\u7eb5\u51fa\u4e8c\u4e08\u5f00\u5916\uff0c\u811a\u5c16\u521a\u521a\u7740\u5730\uff0c\u9ed1\u8863\u5587\u561b\u7684\u7985\u6756\u5df2\u7ecf\u6253\u5230\u6765\u4e86\u3002 \u3000\u3000\u547c\u9ece\u5962\u821e\u8d77\u8888\u88df\uff0c\u6070\u5982\u4e00\u9762\u5177\u6709\u5f39\u6027\u7684\u5899\u58c1\uff0c\u62e6\u4f4f\u4e86\u8981\u8dd1\u8fc7\u53bb\u63f4\u6551\u6768\u6ed4\u7684\u674e\u601d\u5357\uff0c\u773c\u770b \u9ed1\u8863\u5587\u561b\u8fd9\u4e00\u6756\u6253\u4e0b\uff0c\u5c31\u53ef\u4ee5\u53d6\u4e86\u6768\u6ed4\u7684\u6027\u547d\uff0c\u6768\u6ed4\u5ffd\u5730\u4e00\u58f0\u5927\u559d\uff1a\u201c\u4e0d\u662f\u4f60\u6b7b\uff0c\u4fbf\u662f\u6211 \u4ea1\uff01\u201d\u6325\u81c2\u4e00\u683c\uff0c\u53cd\u624b\u4e00\u62ff\uff0c\u500f\u5730\u5c31\u6293\u7740\u4e86\u6756\u5934\u3002 \u3000\u3000\u53ea\u542c\u5f97\u201c\u5580\u5587\u201d\u4e00\u58f0\uff0c\u6768\u6ed4\u81c2\u9aa8\u6298\u65ad\uff0c\u53ef\u662f\u4ed6\u53f3\u624b\u7684\u957f\u5251\u4ea6\u5df2\u5728\u6b64\u65f6\u5316\u4f5c\u4e86\u4e00\u9053\u94f6\u8679\uff0c \u95ea\u7535\u822c\u5730\u5411\u9ed1\u8863\u5587\u561b\u98de\u53bb\u3002 \u3000\u3000\u9ed1\u8863\u5587\u561b\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u6768\u6ed4\u5728\u91cd\u4f24\u4e4b\u540e\uff0c\u5c45\u7136\u8fd8\u80fd\u591f\u4f7f\u7528\u8fd9\u6837\u51f6\u72e0\u7684\u62fc\u547d\u6253\u6cd5\u3002\u4ed6\u7684\u7985 \u6756\u867d\u7136\u6253\u65ad\u4e86\u6768\u6ed4\u7684\u4e00\u6761\u624b\u81c2\uff0c\u4f46\u7ed9\u6768\u6ed4\u6321\u4e86\u4e00\u6321\uff0c\u5df2\u662f\u6765\u4e0d\u53ca\u6536\u6756\u906e\u62e6\u4e86\u3002\u6768\u6ed4\u7684\u957f\u5251\u63b7 \u6765\uff0c\u9ed1\u8863\u5587\u561b\u80f8\u524d\u95e8\u6237\u5927\u5f00\uff0c\u5168\u65e0\u9632\u5fa1\uff0c\u53ea\u542c\u5f97\u201c\u6ce2\u201d\u7684\u4e00\u58f0\uff0c\u957f\u5251\u5df2\u662f\u63d2\u5165\u4e86\u4ed6\u7684\u80f8\u819b\uff01 \u3000\u3000\u6768\u6ed4\u8fd9\u4e00\u5251\u662f\u4ed6\u6bd5\u751f\u529f\u529b\u4e4b\u6240\u805a\uff0c\u4ece\u9ed1\u8863\u5587\u561b\u7684\u524d\u5fc3\u63d2\u5165\uff0c\u540e\u5fc3\u7a7f\u51fa\u3002\u9ed1\u8863\u5587\u561b\u4e00\u58f0\u60e8 \u547c\uff0c\u767b\u65f6\u53d8\u4f5c\u4e86\u8840\u4eba\uff0c\u5012\u4e86\u4e0b\u53bb\uff0c\u4e00\u547d\u545c\u547c\uff01 \u3000\u3000\u6768\u6ed4\u63b7\u51fa\u4e86\u4e00\u5251\uff0c\u811a\u6b65\u4ea6\u5df2\u7ad9\u7acb\u4e0d\u7a33\uff0c\u547c\u9ece\u5962\u7684\u8888\u88df\u970d\u5730\u5377\u6765\uff0c\u6768\u6ed4\u559d\u9053\uff1a\u201c\u6211\u5df2\u591f\u4e86 \u672c\u4e86\uff0c\u518d\u6740\u4e00\u4e2a\uff0c\u5c31\u662f\u5229\u606f\uff01\u201d\u5355\u81c2\u6293\u7740\u4e86\u8888\u88df\uff0c\u4fbf\u5c3d\u5e73\u751f\u7684\u6c14\u529b\uff0c\u5728\u5730\u4e0a\u4e00\u4e2a\u6253\u6eda\uff0c\u628a\u547c \u9ece\u5962\u7684\u8888\u88df\u626f\u8131\u4e86\u624b\u3002 \u3000\u3000\u547c\u9ece\u5962\u8888\u88df\u8131\u624b\uff0c\u6768\u5a49\u4e00\u5251\u523a\u5230\u4ed6\u7684\u80cc\u5fc3\uff0c\u674e\u601d\u5357\u4e00\u5251\u53c8\u523a\u5230\u4e86\u4ed6\u7684\u80f8\u53e3\uff0c\u53cc\u5251\u5f00\u4e0b\uff0c \u524d\u540e\u5939\u653b\uff0c\u5728\u547c\u9ece\u5962\u7684\u8eab\u4e0a\u62a5\u4e86\u4e24\u4e2a\u900f\u660e\u7684\u7a9f\u5708\uff0c\u547c\u9ece\u5962\u6b7b\u7684\u6bd4\u9ed1\u8863\u5587\u561b\u66f4\u60e8\uff0c\u53eb\u90fd\u53eb\u4e0d\u51fa \u6765\u3002 \u3000\u3000\u8fd9\u51e0\u62db\u6027\u547d\u76f8\u640f\uff0c\u5f53\u771f\u662f\u63ba\u70c8\u4e4b\u6781\uff0c\u51e0\u65b9\u9762\u7684\u52a8\u4f5c\u90fd\u662f\u5feb\u5230\u4e86\u6781\u70b9\uff0c\u6768\u6ed4\u5728\u5730\u4e0a\u6253\u4e86\u4e2a \u6eda\uff0c\u521a\u521a\u7ad9\u8d77\uff0c\u53ea\u89c9\u54bd\u5589\u9aa4\u7136\u7d27\u675f\uff0c\u539f\u6765\u662f\u54f2\u522b\u7684\u94c1\u80ce\u5f13\u5df2\u7ecf\u5957\u4e0a\u4e86\u4ed6\u7684\u8116\u5b50\u3002 \u3000\u3000\u8fd9\u662f\u201c\u91d1\u5f13\u5341\u516b\u6253\u201d\u4e2d\u4e00\u62db\u6700\u5389\u5bb3\u7684\u6740\u624b\uff0c\u5f13\u5f26\u662f\u575a\u97e7\u7684\u725b\u7b4b\u505a\u7684\uff0c\u52d2\u7740\u4e86\u54bd\u5589\uff0c\u53ea\u987b \u7528\u529b\u4e00\u62c9\uff0c\u5c31\u7acb\u5373\u53ef\u4ee5\u4ee4\u4eba\u6c14\u7edd\u8eab\u4ea1\u3002 \u3000\u3000\u5728\u8fd9\u6027\u547d\u4fc4\u9877\u4e4b\u9645\uff0c\u6768\u6ed4\u5f20\u53e3\u4e00\u54ac\uff0c\u54ac\u7740\u5f13\u5f26\uff0c\u5411\u524d\u4fef\u8dcc\uff0c\u54f2\u522b\u7ed9\u4ed6\u4e00\u62c9\uff0c\u91cd\u5fc3\u4e0d\u7a33\uff0c \u6b63\u8981\u4f7f\u52b2\u52e4\u6b7b\u6768\u6ed4\uff0c\u674e\u601d\u5357\u3001\u6768\u5a49\u53cc\u5251\u9f50\u5230\uff0c\u54f2\u522b\u53ea\u6709\u4e00\u53e3\u6708\u7259\u5200\u5e94\u654c\uff0c\u91cd\u5fc3\u4e0d\u7a33\uff0c\u53c8\u4e0d\u6613 \u4f7f\u529b\uff0c\u53ea\u6240\u5f97\u201c\u94db\u201d\u7684\u4e00\u58f0\uff0c\u201c\u5530\u201d\u7684\u4e00\u54cd\uff0c\u54f2\u522b\u7684\u5f2f\u5200\u7ed9\u674e\u601d\u5357\u6253\u843d\uff0c\u53f3\u80a9\u53c8\u4e2d\u4e86\u6768\u5a49\u7684 \u4e00\u5251\uff0c\u6768\u5a49\u6c14\u529b\u867d\u5f31\uff0c\u4f46\u8fd9\u4e00\u5251\u662f\u7528\u5c3d\u6c14\u529b\u523a\u51fa\u7684\uff0c\u54f2\u522b\u4f24\u5f97\u59d4\u5b9e\u4e0d\u8f7b\u3002 \u3000\u3000\u54f2\u522b\u4e00\u58f0\u5927\u543c\uff0c\u5f03\u5f13\u800c\u9003\u3002\u9ed1\u8863\u5587\u561b\u4e0e\u547c\u9ece\u5962\u90fd\u5df2\u6b7b\u4e86\uff0c\u53ea\u5269\u4e0b\u4ed6\u4e00\u4e2a\u4eba\uff0c\u9976\u4ed6\u672c\u9886\u518d \u9ad8\uff0c\u4ea6\u5df2\u662f\u4e0d\u6562\u518d\u6253\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u674e\u601d\u5357\u4e0e\u6768\u5a49\u5fd9\u628a\u6768\u6ed4\u6276\u4f4f\uff0c\u6768\u6ed4\u53eb\u9053\uff1a\u201c\u51b3\u53bb\u8ffd\u6740\u54f2\u522b\uff0c\u4e0d\u80fd\u8ba9\u4ed6\u8dd1\u4e86\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u7684\u8eab\u4f53\u8981\u7d27\uff0c\u4f60\u600e\u4e48\u6837\u4e86\uff1f\u201d\u6768\u7a3b\u5598\u7740\u6c14\u9053\uff1a\u201c\u522b\u987e\u6211\uff0c\u8fd8\u662f\u5148 \u6740\u54f2\u522b\u8981\u7d27\uff01\u201d \u3000\u3000\u6768\u6ed4\u5589\u5499\u7ed9\u54f2\u522b\u7684\u5f13\u5f26\u9791\u7834\uff0c\u80f8\u53e3\u53c8\u7ed9\u9ed1\u8863\u5587\u561b\u7684\u7985\u6756\u6253\u4f24\uff0c\u4e24\u5904\u90fd\u662f\u4f24\u5f97\u5f88\u91cd\uff0c\u5c24\u5176 \u662f\u80f8\u53e3\u4f24\u5904\uff0c\u8840\u6db2\u5927\u91cf\u6d41\u51fa\uff0c\u8863\u88f3\u90fd\u67d3\u7ea2\u4e86\u3002 \u3000\u3000\u6768\u6ed4\u8eab\u53d7\u91cd\u4f24\uff0c\u674e\u601d\u5357\u3001\u6768\u5a49\u5c82\u80fd\u629b\u4e0b\u4e86\u4ed6\uff1f\u6768\u5a49\u8fde\u5fd9\u7ed9\u4ed6\u6577\u4e0a\u91d1\u521b\u836f\uff0c\u674e\u601d\u5357\u4e00\u9762\u7ed9 \u4ed6\u5305\u88f9\u4f24\u53e3\uff0c\u4e00\u9762\u8bf4\u9053\uff1a\u201c\u56db\u4e2a\u654c\u4eba\uff0c\u4e8c\u6b7b\u4e8c\u4f24\u3002\u6768\u5927\u54e5\uff0c\u54b1\u4eec\u8fd9\u4e00\u573a\u4ed7\u662f\u5927\u80dc\u4e86\uff01\u54f2\u522b\u7ed9 \u5a49\u59b9\u523a\u4e86\u4e00\u5251\uff0c\u4f24\u5f97\u6bd4\u8d64\u8001\u6e29\u8fd8\u91cd\u5462\u3002\u5373\u4f7f\u4ed6\u4eec\u56de\u8f6c\u548c\u6797\u518d\u8bf7\u63f4\u5175\uff0c\u6765\u56de\u81f3\u5c11\u4e5f\u8981\u56db\u4e94\u5929 \u4e86\uff01\u201d \u3000\u3000\u6768\u6ed4\u82e6\u7b11\u9053\uff1a\u201c\u8bdd\u867d\u5982\u6b64\uff0c\u4f60\u4eec\u8fd8\u662f\u65e9\u65e9\u79bb\u5f00\u9669\u5730\u7684\u597d\u3002\u6211\u4e0d\u6210\u5566\uff0c\u5a49\u59b9\uff0c\u4f60\u522b\u6d6a\u8d39\u91d1 \u521b\u836f\u4e86\uff01\u201d\u58f0\u97f3\u662f\u8d8a\u6765\u8d8a\u4f4e\u6c89\uff0c\u9762\u8272\u4e5f\u662f\u8d8a\u6765\u8d8a\u53d8\u5f97\u60e8\u767d\u3002 \u3000\u3000\u6768\u5a49\u5413\u5f97\u614c\u4e86\uff0c\u53eb\u9053\uff1a\u201c\u54e5\u54e5\uff0c\u4f60\u4e0d\u80fd\u6b7b\uff01\u201d \u3000\u3000\u6768\u6ed4\u7b11\u9053\uff1a\u201c\u50bb\u59b9\u5b50\uff0c\u4eba\u603b\u662f\u6709\u4e00\u6b7b\u7684\u3002\u6211\u4eca\u5929\u62fc\u6389\u4e86\u4e24\u4e2a\u654c\u4eba\uff0c\u6b7b\u4e5f\u503c\u5f97\u4e86\u3002\u5357\u5f1f\uff0c \u4ee5\u540e\u8981\u5168\u9760\u4f60\u7167\u987e\u6211\u7684\u59b9\u59b9\u5566\uff0c\u4f60\u4eec\u7684\u8d23\u4efb\u91cd\u5927\uff0c\u6211\u6b7b\u4e4b\u540e\uff0c\u4f60\u4eec\u4e0d\u5fc5\u591a\u8d39\u7cbe\u795e\u4e3a\u6211\u6599\u7406\u540e \u4e8b\u3002\u4f60\u4eec\u8981\u8d76\u5feb\u3001\u8d76\u5feb\u9003\u51fa\u8499\u53e4\uff01\u56fd\u6068\u5bb6\u4ec7\uff0c\u90fd\u8981\u7b49\u5f85\u4f60\u4eec\u53bb\u62a5\u5462\uff01\u5bf9\u4e0d\u4f4f\uff0c\u5357\u5f1f\uff0c\u6211\u628a\u91cd \u62c5\u4ea4\u4e86\u7ed9\u4f60\uff0c\u6211\u53ef\u8981\u5148\u8d70\u5566\uff01\u201d \u3000\u3000\u6768\u6ed4\u4e00\u53e3\u6c14\u8bf4\u4e86\u8fd9\u8bb8\u591a\u8bdd\uff0c\u529b\u7aed\u58f0\u5636\uff0c\u4ea4\u4ee3\u5b8c\u6bd5\uff0c\u53cc\u773c\u95ed\u62e2\uff0c\u7ec8\u4e8e\u5728\u674e\u601d\u5357\u7684\u6000\u4e2d\u6b7b \u4e86\u3002 \u3000\u3000\u4e24\u65e5\u4e4b\u95f4\uff0c\u6b7b\u4e86\u674e\u601d\u5357\u7684\u7236\u4eb2\uff0c\u53c8\u6b7b\u4e86\u6768\u5a49\u7684\u54e5\u54e5\u3002\u674e\u3001\u6768\u4e8c\u4eba\u6b32\u54ed\u65e0\u6cea\uff0c\u5fc3\u4e2d\u75db\u5982\u5200 \u7ede\u3002 \u3000\u3000\u65e5\u5f71\u6e10\u6e10\u504f\u897f\uff0c\u674e\u601d\u5357\u5f3a\u5fcd\u54c0\u75db\uff0c\u8bf4\u9053\uff1a\u201c\u6b7b\u8005\u4e0d\u80fd\u590d\u751f\uff0c\u5a49\u59b9\uff0c\u54b1\u4eec\u8fd8\u662f\u4f9d\u7167\u5927\u54e5\u7684 \u5429\u5490\u4e3a\u662f\u3002\u201d \u3000\u3000\u6768\u5a49\u9ed8\u4e0d\u4f5c\u58f0\uff0c\u548c\u674e\u601d\u5357\u4e00\u540c\u7528\u5251\u6316\u571f\u3002\u674e\u601d\u5357\u60f3\u5230\u6628\u65e5\u6768\u6ed4\u548c\u4ed6\u5236\u4f5c\u68fa\u6750\u57cb\u846c\u4ed6\u7684\u7236 \u4eb2\uff0c\u5982\u4eca\u5374\u8f6e\u5230\u4ed6\u6765\u57cb\u846c\u6768\u6ed4\uff0c\u8fde\u4e00\u5177\u8584\u68fa\u90fd\u6ca1\u6709\uff0c\u5fc3\u91cc\u5341\u5206\u96be\u8fc7\uff0c\u9ed8\u7977\u9053\uff1a\u201c\u6768\u5927\u54e5\uff0c\u4f60 \u5148\u5b89\u6b47\uff0c\u4ed6\u65e5\u91cd\u6765\uff0c\u6211\u518d\u7ed9\u4f60\u8fc1\u846c\u3002\u201d \u3000\u3000\u846c\u4e86\u6768\u6ed4\uff0c\u674e\u601d\u5357\u9053\uff1a\u201c\u5a49\u59b9\uff0c\u65e5\u5934\u5df2\u7ecf\u8fc7\u5348\uff0c\u8fd8\u6709\u4e24\u4e2a\u65f6\u8fb0\u5c31\u5929\u9ed1\u4e86\u3002\u4e0d\u8fc7\uff0c\u54b1\u4eec\u8fd8 \u662f\u5148\u79bb\u5f00\u8fd9\u91cc\u5427\uff0c\u80fd\u8d70\u5f97\u591a\u5c11\u8def\u5c31\u662f\u591a\u5c11\u3002\u201d \u3000\u3000\u6768\u5a49\u4ecd\u662f\u4e0d\u53d1\u4e00\u8a00\uff0c\u53ea\u662f\u9ed8\u9ed8\u7684\u80cc\u4e0a\u884c\u56ca\uff0c\u8ddf\u674e\u601d\u5357\u8d70\u3002\u674e\u601d\u5357\u77e5\u9053\u6768\u5a49\u6240\u611f\u53d7\u7684\u54c0\u75db \u6bd4\u4ed6\u66f4\u751a\uff0c\u53ef\u53c8\u60f3\u4e0d\u51fa\u4ec0\u4e48\u8bf4\u8bdd\u6765\u5b89\u6170\u5979\u3002 \u3000\u3000\u6768\u5a49\u8d70\u5f97\u5f88\u5feb\uff0c\u4f46\u5374\u4f3c\u51fa\u4e8e\u4e00\u79cd\u672c\u80fd\u4f3c\u5730\u79fb\u52a8\u811a\u6b65\uff0c\u53ea\u662f\u4e00\u80a1\u52b2\u5730\u5f80\u524d\u8d70\uff0c\u4e5f\u4e0d\u77e5\u9053\u62e9 \u8def\u800c\u884c\uff0c\u8d70\u4e0d\u591a\u4e45\uff0c\u5c31\u7ed9\u8346\u68d8\u52fe\u7834\u5979\u7684\u8863\u88f3\u3002\u674e\u601d\u5357\u89c1\u5979\u5982\u6b64\uff0c\u5f88\u662f\u62c5\u5fc3\uff0c\u4e8e\u662f\u8ba9\u5979\u8d70\u5728\u524d \u5934\uff0c\u6bcf\u5230\u9669\u5cfb\u4e4b\u5904\u548c\u6709\u8346\u68d8\u7684\u5730\u65b9\u5c31\u6400\u6276\u5979\uff0c\u6768\u5a49\u4ecd\u7136\u662f\u9ed8\u4e0d\u4f5c\u58f0\uff0c\u4e5f\u6ca1\u6709\u5411\u4ed6\u9053\u8c22\u3002 \u3000\u3000\u4e24\u4eba\u9ed8\u9ed8\u524d\u884c\uff0c\u4e0d\u77e5\u4e0d\u89c9\u4e4b\u95f4\uff0c\u5df2\u662f\u7ea2\u65e5\u897f\u6c89\uff0c\u4f59\u971e\u6563\u7ed5\u7684\u9ec4\u660f\u65f6\u5206\u4e86\uff0c\u674e\u601d\u5357\u6e10\u6e10\u611f \u5230\u4e86\u75b2\u5026\uff0c\u4ed6\u634f\u7740\u6768\u5a49\u7684\u624b\u5fc3\uff0c\u6768\u5a49\u7684\u624b\u5fc3\u5728\u6c81\u6c57\uff0c\u663e\u7136\u4e5f\u662f\u75b2\u7d2f\u4e0d\u582a\u3002 \u3000\u3000\u6b64\u65f6\u4ed6\u4eec\u6b63\u5728\u8d70\u5230\u8c37\u5e95\u7684\u4e00\u5757\u5e73\u5730\uff0c\u5c71\u4e0a\u6709\u4e00\u9053\u7011\u5e03\u503e\u6cfb\u800c\u4e0b\uff0c\u5728\u8c37\u5e95\u6c47\u6210\u4e86\u4e00\u6761\u6eaa \u6d41\uff0c\u6c34\u77f3\u4e92\u6fc0\uff0c\u6df7\u6dc6\u6dd9\u6dd9\uff0c\u6c34\u82b1\u56db\u6e85\uff0c\u6709\u5982\u73e0\u7389\u7eb7\u98de\u3002\u5c71\u5761\u4e0a\u3001\u6e05\u6eaa\u8fb9\uff0c\u6709\u65e0\u6570\u4e0d\u77e5\u540d\u7684\u91ce \u82b1\uff0c\u665a\u98ce\u5439\u6765\uff0c\u82b1\u9999\u6251\u9f3b\u3002 \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u5929\u5feb\u9ed1\u4e86\uff0c\u54b1\u4eec\u5c31\u5728\u8fd9\u91cc\u6b47\u4e0b\u6765\u5427\u3002\u201d\u6768\u5a49\u6ca1\u6709\u8bf4\u8bdd\uff0c\u8ddf\u7740\u4ed6\u5750\u4e0b\u6765\u3002\u674e \u601d\u5357\u9053\uff1a\u201c\u4f60\u997f\u4e86\u5427\uff1f\u5148\u5403\u4e00\u70b9\u5e72\u7cae\uff0c\u6211\u518d\u53bb\u66ff\u4f60\u627e\u70b9\u98df\u7269\u3002\u201d \u3000\u3000\u6768\u5a49\u6447\u4e86\u6447\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u997f\u3002\u201d\u674e\u601d\u5357\u9053\uff1a\u201c\u4f60\u4e00\u5b9a\u5f88\u7d2f\u4e86\u3002\u90a3\u4e48\uff0c\u4f60\u62b9\u4e00\u628a\u8138\uff0c\u5148 \u7761\u4e00\u89c9\u3002\u201d\u6768\u5a49\u53c8\u6447\u4e86\u6447\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u7d2f\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u96be\u8fc7\u4e4b\u6781\uff0c\u5fcd\u4e0d\u4f4f\u54bd\u6cea\u8bf4\u9053\uff1a\u201c\u4f60\u75db\u75db\u5feb\u5feb\u54ed\u4e00\u573a\u5427\uff01\u201d\u6768\u5a49\u9053\uff1a\u201c\u6211\u54ed\u4e0d \u51fa\uff01\u201d \u3000\u3000\u6768\u5a49\u6ca1\u6709\u54ed\uff0c\u674e\u601d\u5357\u81ea\u5df1\u5148\u6d41\u6cea\u4e86\u3002\u4ed6\u6b63\u4e0d\u77e5\u5982\u4f55\u5b89\u6170\u6768\u5a49\u624d\u597d\uff0c\u53ea\u89c1\u6768\u5a49\u628a\u4e00\u6735\u6735\u7684 \u91ce\u82b1\u629b\u4e0b\u6eaa\u6d41\uff0c\u4e00\u7247\u7247\u7684\u82b1\u74e3\u5728\u6c34\u4e2d\u98d8\u6563\u3002\u82e5\u5728\u65c1\u4eba\u770b\u6765\uff0c\u53ea\u900d\u662f\u4e00\u4e2a\u5929\u771f\u7684\u5c11\u5973\u5728\u5f04\u82b1\u620f \u6c34\uff0c\u53ea\u6709\u674e\u601d\u5357\u6df1\u6df1\u5730\u611f\u5230\u5979\u5fc3\u5e95\u7684\u54c0\u4f24\u3002 \u3000\u3000\u674e\u601d\u5357\u611f\u67d3\u4e86\u5979\u7684\u6101\u7eea\uff0c\u4e0d\u77e5\u600e\u7684\uff0c\u5ffd\u5730\u60f3\u8d77\u4e86\u201c\u82b1\u81ea\u98d8\u96f6\u6c34\u81ea\u6d41\u201d\uff0c\u201c\u843d\u82b1\u6d41\u6c34\u4e24\u65e0 \u60c5\u201d\u7b49\u7b49\u5c0f\u65f6\u5019\u8bfb\u8fc7\u7684\u8bd7\u53e5\uff0c\u8fd9\u4e9b\u5e7d\u6028\u51c4\u6e05\u7684\u8bd7\u53e5\uff0c\u4ed6\u672c\u6765\u662f\u4e0d\u600e\u4e48\u559c\u6b22\u7684\uff0c\u5982\u4eca\u5374\u7a81\u7136\u4ece \u5fc3\u4e2d\u6d41\u51fa\u6765\u4e86\u3002 \u3000\u3000\u201c\u4eba\u751f\u906d\u9047\u771f\u662f\u96be\u6d4b\uff0c\u4e09\u5929\u524d\u6211\u548c\u5a49\u59b9\u8fd8\u672a\u66fe\u76f8\u8bc6\uff0c\u5982\u4eca\u5374\u53d8\u6210\u4e86\u76f8\u4f9d\u4e3a\u547d\u7684\u592b\u59bb\u3002\u201d \u674e\u601d\u5357\u5fc3\u60f3\u3002 \u3000\u3000\u8fd9\u5239\u90a3\u95f4\uff0c\u4ed6\u5ffd\u5730\u611f\u5230\u5bf9\u6768\u5a49\u4f3c\u4e4e\u6709\u4e86\u4e00\u4efd\u771f\u60c5\uff0c\u4f46\u4e5f\u662f\u5728\u8fd9\u77ac\u95f4\uff0c\u5b5f\u660e\u971e\u90a3\u5982\u82b1\u7684\u7b11 \u9765\u4e5f\u4f3c\u5728\u6e05\u6eaa\u4e2d\u9690\u73b0\u3002\u674e\u601d\u5357\u6697\u81ea\u75db\u8d23\uff1a\u201c\u5509\uff0c\u6211\u600e\u4e48\u8fd8\u5fd8\u4e0d\u4e86\u5979\uff1f\u201d\u5fc3\u4e2d\u6df7\u4e71\uff0c\u4e0d\u81ea\u89c9\u7684 \u4e5f\u63c9\u788e\u4e86\u4e00\u6735\u91ce\u82b1\uff0c\u629b\u5165\u6eaa\u4e2d\u3002\u674e\u601d\u5357\u770b\u7740\u6c34\u9762\u8361\u8d77\u7684\u6d9f\u6f2a\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u6211\u4e0e\u5b5f\u660e\u971e\u7684\u4e00 \u756a\u9047\u5408\uff0c\u5927\u7ea6\u4e5f\u5c31\u662f\u8fd9\u4e48\u6837\u4e86\uff01\u201d \u3000\u3000\u6768\u5a49\u5ffd\u5730\u62ac\u8d77\u5934\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u5357\u54e5\uff0c\u4f60\u5728\u60f3\u4e9b\u4ec0\u4e48\uff1f\u201d\u674e\u601d\u5357\u9053\uff1a\u201c\u4f60\u5148\u8bf4\u4f60\u5728\u60f3\u4e9b\u4ec0 \u4e48\uff1f\u201d\u6768\u5a49\u9053\uff1a\u201c\u6211\u662f\u5728\u60f3\uff0c\u4ece\u4eca\u4e4b\u540e\uff0c\u53ea\u6709\u4f60\u662f\u6211\u7684\u4eb2\u4eba\u4e86\uff01\u8fd9\u4e0d\u662f\u5f88\u5947\u602a\u4e48\uff0c\u4e09\u5929\u4e4b \u524d\uff0c\u6211\u4eec\u8fd8\u672a\u76f8\u8bc6\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u6211\u6700\u521d\u4e5f\u662f\u8fd9\u6837\u60f3\uff0c\u4f46\u518d\u60f3\u4e00\u60f3\uff0c\u6211\u4e5f\u4e0d\u89c9\u5f97\u5947\u602a\u4e86\uff0c\u8fd9\u662f\u76f8\u540c\u7684\u9645\u9047\u628a \u54b1\u4eec\u7684\u547d\u8fd0\u8054\u7ed3\u5728\u4e00\u8d77\u7684\u3002\u201d\u4e24\u4eba\u4e92\u5410\u771f\u60c5\uff0c\u4e0d\u77e5\u4e0d\u89c9\u5730\u62e5\u62b1\u8d77\u6765\uff0c\u6768\u5a49\u8fd9\u624d\u80fd\u591f\u75db\u75db\u5feb\u5feb \u5730\u54ed\u51fa\u6765\u4e86\u3002\u6b63\u662f\uff1a \u3000\u3000\u76f8\u60dc\u76f8\u601c\u540c\u547d\u9e1f\uff0c\u4ea6\u60b2\u4ea6\u559c\u5c0f\u592b\u59bb\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b\u2014\u2014\u7b2c\u516b\u56de\u3000\u516c\u4e3b\u6709\u60c5\u7a7a\u60f9\u6068\u3000\u8944\u738b\u65e0\u68a6\u5404\u5206\u98de \u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b \u7b2c\u516b\u56de\u3000\u516c\u4e3b\u6709\u60c5\u7a7a\u60f9\u6068\u3000\u8944\u738b\u65e0\u68a6\u5404\u5206\u98de \u3000\u3000\u674e\u601d\u5357\u89c1\u6768\u5a49\u54ed\u5f97\u51fa\u6765\uff0c\u5fc3\u4e0a\u7684\u4e00\u5757\u77f3\u5934\u65b9\u624d\u653e\u4e0b\u3002\u8981\u77e5\u4e00\u4e2a\u4eba\u906d\u53d7\u5de8\u5927\u7684\u4e0d\u5e78\uff0c\u6700\u6015 \u662f\u628a\u6df1\u6c89\u7684\u54c0\u75db\u90c1\u79ef\u5fc3\u4e2d\uff0c\u54ed\u5f97\u51fa\u6765\uff0c\u53cd\u800c\u597d\u4e86\u3002 \u3000\u3000\u679c\u7136\u6768\u5a49\u5728\u5927\u54ed\u4e00\u573a\u4e4b\u540e\uff0c\u795e\u60c5\u663e\u5f97\u75b2\u5026\u4e0d\u582a\uff0c\u4e5f\u77e5\u9053\u997f\u4e86\u3002\u5979\u6076\u6597\u534a\u5929\uff0c\u53c8\u8dd1\u4e86\u8fd9\u8bb8 \u591a\u8def\uff0c\u75b2\u5026\u3001\u9965\u997f\u4e43\u662f\u6b63\u5e38\u7684\u73b0\u8c61\uff0c\u5018\u82e5\u4e00\u76f4\u9ebb\u6728\u65e0\u77e5\uff0c\u90a3\u5c31\u5341\u5206\u4e0d\u5999\u4e86\u3002 \u3000\u3000\u674e\u601d\u5357\u8bf4\u9041\uff1a\u201c\u5a49\u59b9\uff0c\u4f60\u8981\u8bb0\u4f4f\u54e5\u54e5\u7684\u5429\u5490\uff1a\u7559\u5f97\u9752\u5c71\u5728\uff0c\u4e0d\u6015\u6ca1\u67f4\u70e7\u3002\u4f60\u603b\u5f97\u5403\u70b9\u4e1c \u897f\uff0c\u624d\u80fd\u957f\u6c14\u8d70\u8def\u5440\u3002\u8fd9\u5c71\u6eaa\u91cc\u6709\u9c7c\uff0c\u6211\u6765\u6349\u4e24\u6761\u9c7c\u5427\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u8df3\u8fdb\u6c34\u91cc\uff0c\u8bb8\u4e45\u624d\u6349\u5230\u4e86\u4e24\u6761\u94f6\u767d\u8272\u7684\u5c0f\u9c7c\uff0c\u7b11\u9053\uff1a\u201c\u5e73\u65f6\u770b\u6e14\u7fc1\u6355\u9c7c\uff0c\u5bb9\u6613\u5f97 \u5f88\uff0c\u5230\u81ea\u5df1\u53bb\u505a\uff0c\u624d\u77e5\u9053\u8270\u96be\u3002\u53ef\u77e5\u5de5\u591a\u827a\u719f\u8fd9\u53e5\u4fd7\u8bdd\uff0c\u7684\u786e\u662f\u5f88\u6709\u9053\u7406\u3002\u4e0d\u8fc7\uff0c\u8fd9\u4e24\u6761\u9c7c \u513f\u867d\u5c0f\uff0c\u603b\u6bd4\u53ea\u5403\u5e72\u7cae\u597d\u4e9b\u3002\u201d \u3000\u3000\u6768\u5a49\u5df2\u7ecf\u751f\u8d77\u4e86\u706b\uff0c\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u7684\u98df\u91cf\u4e0d\u5927\uff0c\u8fd9\u4e24\u6761\u9c7c\u513f\u4e5f\u4e0d\u7b97\u5c0f\u4e86\u3002\u6765\uff0c\u6211\u7ed9\u4f60\u505a \u70e4\u9c7c\u5403\u3002\u5440\uff0c\u4f60\u6e7f\u6dcb\u6dcb\u7684\uff0c\u4e5f\u5e94\u8be5\u6765\u70e4\u70e4\u706b\u5566\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u4e00\u9762\u70e4\u706b\uff0c\u4e00\u9762\u770b\u7740\u6768\u5a49\u70e4\u9c7c\uff0c\u53ea\u89c9\u8eab\u4e0a\u6696\u70d8\u70d8\u7684\u597d\u4e0d\u8212\u670d\u3002\u4e0d\u53ea\u662f\u8eab\u4f53\u89c9\u5f97\u6e29 \u6696\uff0c\u5fc3\u4e2d\u5c24\u5176\u89c9\u5f97\u6e29\u6696\uff0c\u597d\u50cf\u662f\u6768\u5a49\u70b9\u8d77\u7684\u706b\u5c06\u4ed6\u7684\u4e00\u9897\u5fc3\u4e5f\u71c3\u70e7\u8d77\u6765\u4e86\u3002 \u3000\u3000\u674e\u601d\u5357\u770b\u7740\u6768\u5a49\u88ab\u706b\u6620\u7ea2\u7684\u8138\u6b63\u81ea\u51fa\u795e\uff0c\u5ffd\u89c1\u6768\u5a49\u9762\u8272\u4e00\u53d8\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u5357\u54e5\uff0c\u4f60 \u542c\uff0c\u597d\u50cf\u662f\u6709\u4eba\u6765\u4e86\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u84e6\u7136\u4e00\u60ca\uff0c\u4ece\u6c89\u601d\u4e2d\u9192\u6765\uff0c\u53ea\u542c\u5f97\u5c71\u8c37\u91cc\u679c\u7136\u662f\u9690\u9690\u6709\u7740\u201c\u5f97\u5f97\u201d\u7684\u8e44\u58f0\uff0c\u800c\u4e14 \u6765\u7684\u4e0d\u6b62\u4e00\u9a91\u3002 \u3000\u3000\u674e\u601d\u5357\u5fd9\u9053\uff1a\u201c\u5feb\u628a\u706b\u5f04\u7184\uff0c\u627e\u4e2a\u5730\u65b9\u8eb2\u8eb2\uff01\u201d\u8981\u77e5\u4ed6\u4eec\u6b64\u523b\u90fd\u662f\u53c8\u997f\u53c8\u7d2f\uff0c\u5018\u82e5\u6765\u7684 \u4e43\u662f\u654c\u4eba\uff0c\u4ed6\u4eec\u5df2\u662f\u65e0\u529b\u518d\u6597\u4e00\u573a\u4e86\u3002 \u3000\u3000\u8bdd\u72b9\u672a\u4e86\uff0c\u4e09\u4e2a\u4eba\u516d\u5339\u9a6c\u5df2\u5728\u8c37\u4e2d\u51fa\u73b0\u3002\u6768\u5a49\u62ac\u773c\u671b\u53bb\uff0c\u5927\u4e3a\u5947\u602a\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e09\u4e2a\u4eba \u90fd\u662f\u5973\u5b50\uff0c\u5927\u7ea6\u4e0d\u4f1a\u662f\u6765\u8ffd\u6355\u54b1\u4eec\u7684\u5427\uff1f\u4f46\u4e09\u66f4\u534a\u591c\uff0c\u4e09\u4e2a\u5973\u5b50\uff0c\u5374\u5230\u8352\u5c71\u5e7d\u8c37\u6765\u4f5c\u4ec0\u4e48\uff1f \u54a6\uff0c\u5357\u54e5\uff0c\u4f60\u3001\u4f60\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6765\u7684\u8fd9\u4e09\u4e2a\u5973\u5b50\uff0c\u4e0d\u662f\u522b\u4eba\uff0c\u6b63\u662f\u660e\u6167\u516c\u4e3b\u548c\u5979\u7684\u4f8d\u5973\u3002\u539f\u6765\u660e\u6167\u516c\u4e3b\u867d\u7136\u4ea4\u4ee3\u4e86\u90a3\u9ed1 \u8863\u5587\u561b\uff0c\u4ecd\u662f\u653e\u5fc3\u4e0d\u4e0b\u3002\u5979\u505a\u4e8b\u672c\u6765\u4efb\u6027\uff0c\u5fcd\u53d7\u4e0d\u4e86\u90a3\u4efd\u5750\u5728\u5bb6\u4e2d\u7b49\u5019\u6d88\u606f\u7684\u7126\u6025\uff0c\u7ec8\u4e8e\u81ea \u5df1\u4e5f\u8d76\u6765\u4e86\u3002\u5979\u6015\u8ffd\u8d76\u4e0d\u4e0a\uff0c\u548c\u5979\u7684\u4f8d\u5973\u6bcf\u4eba\u90fd\u5e26\u4e86\u4e00\u5339\u7a7a\u9a91\uff0c\u5728\u8def\u4e0a\u8f6e\u6d41\u66ff\u6362\u3002\u672c\u6765\u9ed1\u591c \u8352\u5c71\uff01\u5979\u4eec\u662f\u5f88\u96be\u627e\u5f97\u7740\u674e\u601d\u5357\u7684\uff0c\u65e0\u5de7\u4e0d\u5de7\uff0c\u6768\u5a49\u6070\u5de7\u5728\u8fd9\u4e2a\u65f6\u5019\u70e7\u8d77\u4e86\u4e00\u5806\u706b\uff0c\u8fd9\u5c31\u628a \u5979\u4eec\u5f15\u6765\u4e86\u3002 \u3000\u3000\u674e\u601d\u5357\u89c1\u662f\u660e\u6167\u516c\u4e3b\uff0c\u5fc3\u4e2d\u867d\u7136\u60ca\u7591\u4e0d\u5b9a\uff0c\u5374\u4e5f\u5b58\u7740\u4e00\u7ebf\u5e0c\u671b\uff0c\u65e2\u7136\u8eb2\u907f\u4e0d\u5f00\uff0c\u7d22\u6027\u5c31 \u8fce\u4e0a\u524d\u53bb\u51fa\u58f0\u8bf4\u9053\uff1a\u201c\u4e0d\u6562\u6709\u52b3\u516c\u4e3b\u8fdc\u6765\u3002\u4e00\u4eba\u4f5c\u4e8b\u4e00\u4eba\u5f53\uff0c\u6211\u674e\u601d\u5357\u4efb\u51ed\u516c\u4e3b\u5904\u7f6e\u3002\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u9762\u5e26\u5bd2\u971c\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u90a3\u5929\u4e3a\u4f55\u5931\u7ea6\uff1f\u6211\u7239\u7239\u5f85\u4f60\u4e0d\u8584\uff0c\u4f60\u53c8\u4e3a\u4f55\u79c1\u81ea\u9003 \u51fa\u548c\u6797\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u7b2c\u4e00\u3001\u6211\u90a3\u5929\u672c\u6765\u5c31\u6ca1\u6709\u7b54\u5e94\u4f60\u7684\u7ea6\u4f1a\uff0c\u662f\u4f59\u4e00\u4e2d\u64c5\u81ea\u66ff\u6211\u4f5c\u4e3b\u7684\uff0c\u4f60\u8981 \u8ffd\u7a76\u5931\u7ea6\u4e4b\u8d23\uff0c\u53ea\u80fd\u53bb\u8d23\u95ee\u4f59\u4e00\u4e2d\u3002\u7b2c\u4e8c\u3001\u6211\u5e76\u6ca1\u6709\u53d7\u4f60\u7239\u7239\u7684\u5b98\u804c\uff0c\u6211\u662f\u6c49\u4eba\uff0c\u4e5f\u4e0d\u80fd\u5728 \u4f60\u4eec\u8499\u53e4\u4f4f\u4e00\u8f88\u5b50\uff0c\u6211\u5149\u660e\u6b63\u5927\u5730\u56de\u5bb6\uff0c\u53c8\u600e\u80fd\u8bf4\u662f\u79c1\u9003\uff1f\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u8be7\u9053\uff1a\u201c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u8c01\u662f\u4f59\u4e00\u4e2d\uff1f\u4f60\u6ca1\u6709\u505a\u5b98\uff0c\u4f60\u7684\u7239\u7239\u53ef\u662f\u505a\u6211\u4eec\u8499\u53e4\u4eba\u7684 \u5b98\u3002\u4f60\u7684\u5bb6\u5c31\u5728\u548c\u6797\uff0c\u4f60\u53c8\u8981\u56de\u5230\u54ea\u4e00\u4e2a\u5bb6\u53bb\uff1f\u4f60\u8fd9\u756a\u8bf4\u8bdd\u7b80\u76f4\u662f\u6f0f\u6d1e\u767e\u51fa\uff0c\u9a97\u5b69\u5b50\u4e5f\u9a97\u4e0d \u8fc7\u53bb\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u516c\u4e3b\u6709\u6240\u4e0d\u77e5\uff0c\u6b64\u4e8b\u8bf4\u6765\u8bdd\u957f\uff0c\u8bf7\u5bb9\u674e\u67d0\u8be6\u7980\u3002\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u663e\u51fa\u5f88\u4e0d\u8010\u70e6\u7684\u795e\u6c14\uff0c\u6325\u4e00\u6325\u624b\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u5fc5\u8d39\u795e\u7f16\u9020\u8c0e\u8a00\u4e86\uff0c\u54fc\uff0c \u4f60\u4e0d\u8bf4\u6211\u4e5f\u660e\u767d\uff0c\u4f60\u4e0d\u8d74\u6211\u7684\u7ea6\u4f1a\uff0c\u539f\u6765\u662f\u8dd1\u5230\u8fd9\u91cc\u548c\u8fd9\u4e2a\u5c0f\u5996\u7cbe\u5e7d\u4f1a\u3002\u8fd9\u5c0f\u5996\u7cbe\u662f\u4f60\u7684\u4ec0 \u4e48\u4eba\uff1f\u8bf4\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u864e\u76ee\u4e00\u8f69\uff0c\u4ea2\u58f0\u8bf4\u9053\uff1a\u201c\u8981\u6740\u8981\u5250\uff0c\u6211\u674e\u601d\u5357\u968f\u4f60\u7684\u4fbf\uff0c\u4f60\u53ef\u4e0d\u80fd\u4fae\u8fb1\u6211\u7684\u59bb \u5b50\uff01\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u6014\u4e86\u4e00\u6014\uff0c\u8bf4\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u4f60\u8bf4\u8fd9\u3001\u8fd9\u5973\u4eba\u662f\u4f60\u7684\u59bb\u5b50\uff1f\u201d \u3000\u3000\u6768\u5a49\u4e00\u58f0\u51b7\u7b11\uff0c\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u6211\u548c\u674e\u516c\u5b50\u662f\u5149\u660e\u6b63\u5927\u7684\u592b\u59bb\uff0c\u8981\u5077\u6c49\u5b50\u7684\u5c0f\u5996\u7cbe\u4e5f\u8bb8\u662f \u4f1a\u6709\u7684\uff0c\u4f46\u4e0d\u662f\u6211\uff01\u201d\u6768\u5a49\u6c14\u6124\u4e0d\u8fc7\uff0c\u7ed5\u4e86\u4e2a\u5f2f\u513f\uff0c\u72e0\u72e0\u5730\u523a\u4e86\u660e\u6167\u516c\u4e3b\u4e00\u4e0b\u3002 \u3000\u3000\u660e\u6167\u516c\u4e3b\u5ffd\u5730\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u674e\u601d\u5357\uff0c\u4f60\u5728\u6211\u7684\u9762\u524d\u5c45\u7136\u6562\u6492\u8fd9\u6837\u7684\u5f25\u5929\u5927\u8c0e\uff01\u90a3\u65e5\u72e9\u730e \u4e4b\u65f6\uff0c\u4f60\u7239\u7239\u4eb2\u53e3\u5bf9\u6211\u8bf4\u8fc7\uff0c\u4f60\u8fd8\u6ca1\u6709\u5b9a\u4eb2\uff0c\u4f60\u54ea\u91cc\u6765\u7684\u8fd9\u4e2a\u59bb\u5b50\uff1f\u201d\u660e\u6167\u516c\u4e3b\u81ea\u4ee5\u4e3a\u62c6\u7a7f \u4e86\u674e\u601d\u5357\u7684\u8c0e\u8bdd\uff0c\u5f88\u662f\u5f97\u610f\uff0c\u5bf9\u6768\u5a49\u7684\u8ba5\u523a\uff0c\u4e5f\u4e0d\u653e\u5728\u5fc3\u4e0a\u4e86\u3002 \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u8fd9\u4f4d\u6768\u59d1\u5a18\u662f\u6211\u7239\u7239\u7ed9\u6211\u5a36\u7684\u59bb\u5b50\uff01\u6211\u7684\u7239\u7239\u662f\u674e\u5e0c\u6d69\uff0c\u5728\u548c\u6797\u5047\u5192\u6211\u7239 \u7239\u4e4b\u540d\u7684\u90a3\u4e2a\u4eba\u662f\u4f59\u4e00\u4e2d\u3002\u4f60\u660e\u767d\u4e86\u4e48\uff1f\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u5446\u82e5\u6728\u9e21\uff0c\u8fc7\u4e86\u597d\u4e00\u4f1a\u624d\u4ece\u660f\u4e71\u4e2d\u7a0d\u7a0d\u6e05\u9192\u8fc7\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u54e6\uff0c\u4f60\u8bf4\u7684\u90a3\u4e2a\u4f59 \u4e00\u4e2d\u539f\u6765\u7adf\u662f\u5047\u5192\u4f60\u7239\u7239\u4e4b\u540d\uff0c\u5728\u6211\u7236\u4eb2\u624b\u4e0b\u505a\u5b98\u7684\u4e48\uff1f\u8fd9\u662f\u600e\u4e48\u4e00\u56de\u4e8b\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u4f59\u4e00\u4e2d\u548c\u6211\u7239\u7239\u672c\u6765\u662f\u540c\u5728\u4e00\u4e2a\u4fd8\u864f\u8425\u7684\uff0c\u4ed6\u77e5\u9053\u4f60\u4eec\u8981\u6211\u7239\u7239\u51fa\u53bb\u505a \u5bab\uff0c\u9042\u6627\u4e86\u5929\u826f\uff0c\u8c0b\u5bb3\u6211\u7684\u7239\u7239\uff0c\u5192\u540d\u9876\u66ff\uff01\u4ed6\u662f\u6211\u7684\u6740\u7236\u4ec7\u4eba\uff0c\u53ea\u662f\u6211\u521d\u5230\u548c\u6797\u4e4b\u65f6\uff0c\u8fd8 \u4e0d\u77e5\u9053\u7f62\u4e86\u3002\u201d\u5f53\u4e0b\u5c06\u4e8b\u60c5\u7684\u7ecf\u8fc7\uff0c\u7b80\u5355\u5730\u544a\u8bc9\u4e86\u660e\u6167\u516c\u4e3b\u3002 \u3000\u3000\u660e\u6167\u516c\u4e3b\u53c8\u60ca\u53c8\u6025\uff0c\u534a\u664c\u8bf4\u9053\uff1a\u201c\u6709\u8fd9\u6837\u7684\u602a\u4e8b\uff1f\u597d\uff0c\u6211\u56de\u53bb\u544a\u8bc9\u7239\u7239\uff0c\u66ff\u4f60\u4f38\u51a4\u5c31 \u662f\u3002\u53ef\u662f\uff0c\u4f60\u5374\u4e0d\u8be5\u95ef\u4e0b\u4e86\u5927\u7978\uff0c\u73b0\u5728\u4f60\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u8fd9\u53e5\u8bdd\u4f3c\u4e4e\u662f\u5e94\u8be5\u7531\u6211\u6765\u8bf4\uff0c\u8bf7\u95ee\u516c\u4e3b\u5f85\u8981\u5982\u4f55\uff1f\u6211\u4eec\u592b\u59bb\u662f\u8981\u56de\u56fd\u7684\uff0c \u516c\u4e3b\u82e5\u80af\u9ad8\u62ac\u8d35\u624b\uff0c\u6211\u4eec\u6c38\u4e16\u611f\u6fc0\u4f60\u7684\u5927\u6069\u3002\u516c\u4e3b\u82e5\u662f\u4e0d\u80af\u653e\u8fc7\u6211\u4eec\uff0c\u90a3\u5c31\u7531\u6211\u4e00\u4eba\u627f\u62c5 \u5427\u3002\u4e0d\u8fc7\uff0c\u548c\u6797\u6211\u662f\u51b3\u4e0d\u56de\u53bb\u7684\u4e86\uff0c\u516c\u4e3b\u82e5\u7136\u5b9a\u8981\u62ff\u6211\u56de\u53bb\uff0c\u5c31\u8bf7\u628a\u6211\u7684\u9996\u7ea7\u5e26\u56de\u53bb\u5427\uff01\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u5982\u4eca\u5c31\u662f\u4f60\u60f3\u56de\u548c\u6797\u4e5f\u4e0d\u6210\u4e86\u3002\u4f60\u6700\u4e0d\u8be5\u6740\u4e86\u6211\u4eec\u7684\u4e24\u4e2a \u5587\u561b\uff0c\u53c8\u4f24\u4e86\u6211\u4eec\u4e24\u4e2a\u91d1\u5e10\u6b66\u58eb\u3002\u5509\uff0c\u53ea\u6015\u662f\u6211\u4e5f\u96be\u4ee5\u5e87\u62a4\u4f60\u4e86\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u4ed6\u4eec\u5df2\u7ecf\u6740\u4e86\u6211\u7684\u5927\u8205\uff0c\u5018\u82e5\u6211\u4e0d\u6740\u4f24\u4ed6\u4eec\uff0c\u6211\u4eec\u592b\u59bb\u5982\u4eca\u5c82\u80fd\u6d3b\u7740\u548c\u4f60 \u8bf4\u8bdd\uff01\u4f46\u6211\u77e5\u9053\u4f60\u4eec\u7684\u4eba\u662f\u4e0d\u4f1a\u548c\u6211\u8bb2\u9053\u7406\u7684\uff0c\u6211\u4e5f\u4e0d\u60f3\u8bf7\u6c42\u516c\u4e3b\u7684\u5e87\u62a4\uff0c\u53ea\u662f\u6b64\u4e8b\u4e0e\u6211\u59bb \u5b50\u65e0\u5173\uff0c\u4f60\u628a\u6211\u7684\u9996\u7ea7\u5e26\u56de\u53bb\u4ea4\u7ed9\u4f60\u7684\u7239\u7239\uff0c\u4e5f\u603b\u53ef\u4ee5\u4ea4\u4ee3\u5f97\u8fc7\u53bb\u4e86\u5427\uff01\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u9053\uff1a\u201c\u4f60\u83ab\u8981\u8bf4\u8fd9\u4e9b\u6124\u6fc0\u7684\u8bdd\uff0c\u6211\u5e76\u4e0d\u60f3\u4f24\u5bb3\u4f60\u3002\u4f60\u5bb9\u6211\u518d\u60f3\u4e00\u60f3\uff0c\u770b\u770b\u6709\u4ec0 \u4e48\u4e24\u5168\u4e4b\u7b56\u3002\u201d \u3000\u3000\u8fc7\u4e86\u534a\u664c\uff0c\u660e\u6167\u516c\u4e3b\u82e6\u7b11\u8bf4\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u60f3\u62c6\u6563\u4f60\u4eec\u592b\u59bb\uff0c\u4f46\u6211\u4ee5\u4e3a\u8fd8\u662f\u6768\u59d1\u5a18\u8ddf\u6211\u56de \u548c\u6797\u7684\u597d\uff01\u4f60\u8981\u77e5\u9053\u4f60\u7684\u4e08\u592b\u662f\u4e0d\u80fd\u843d\u5728\u6211\u7239\u7239\u4e4b\u624b\u7684\uff0c\u4f46\u4f60\u8ddf\u6211\u56de\u53bb\uff0c\u5373\u4f7f\u4e0d\u80fd\u514d\u4e8e\u53d7 \u7f5a\uff0c\u81f3\u5c11\u53ef\u9003\u4e00\u6b7b\u3002\u6211\u53ef\u4ee5\u6536\u4f60\u505a\u4f8d\u5973\u3002\u7167\u6211\u4eec\u8499\u53e4\u7684\u6cd5\u5f8b\uff0c\u4f60\u53ea\u662f\u4e2a\u2018\u4ece\u72af\u2019\uff0c\u6211\u6536\u4e86\u4f60 \u505a\u4f8d\u5973\uff0c\u5c31\u6ca1\u6709\u4eba\u6562\u52a0\u5bb3\u4f60\u4e86\uff01\u201d \u3000\u3000\u6768\u5a49\u4e00\u54ac\u7259\u6839\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u53ea\u8981\u4f60\u653e\u8fc7\u6211\u7684\u4e08\u592b\uff0c\u6211\u968f\u4f60\u53bb\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u4e0d\uff0c\u6211\u5df2\u7ecf\u5bb3\u4e86\u4f60\u7684\u54e5\u54e5\uff0c\u51b3\u4e0d\u80fd\u518d\u5bb3\u4f60\u4e86\uff0c\u4e00\u5b9a\u8981\u53bb\u7684\u8bdd\uff0c\u6211\u53bb\uff01\u5927\u4e0d \u4e86\u662f\u4e00\u6b7b\u800c\u5df2\uff01\u201d \u3000\u3000\u6768\u5a49\u62b1\u7740\u674e\u601d\u5357\u54ed\u9053\uff1a\u201c\u4f60\u8fd8\u4e0d\u61c2\u5f97\u516c\u4e3b\u7684\u610f\u601d\uff0c\u5979\u662f\u8981\u4f60\u6d3b\uff0c\u8981\u6211\u548c\u4f60\u5206\u79bb\uff01\u201d\u8fd9\u51e0 \u53e5\u8bdd\u6768\u5a49\u662f\u7528\u5bb6\u4e61\u8bdd\u8bf4\u7684\uff0c\u58f0\u97f3\u545c\u54bd\uff0c\u8bf4\u5f97\u53c8\u5feb\uff0c\u660e\u6167\u516c\u4e3b\u867d\u7136\u5b66\u8fc7\u4e00\u4e9b\u6c49\u8bed\uff0c\u5374\u542c\u4e0d\u61c2\u5979 \u8bf4\u4ec0\u4e48\u3002 \u3000\u3000\u660e\u6167\u516c\u4e3b\u867d\u7136\u542c\u4e0d\u61c2\u6768\u5a49\u7684\u8bdd\uff0c\u4f46\u89c1\u5979\u4e0e\u674e\u601d\u5357\u96be\u820d\u96be\u5206\u7684\u6837\u5b50\uff0c\u5fc3\u4e2d\u5374\u4e0d\u7531\u5f97\u53c8\u662f\u5992 \u5fcc\uff0c\u53c8\u662f\u4f24\u5fc3\u3002\u5728\u4f24\u5fc3\u5992\u5fcc\u4e4b\u4e2d\uff0c\u53c8\u4e0d\u80fd\u4e0d\u611f\u5230\u51e0\u5206\u5185\u759a\u3002 \u3000\u3000\u8fd9\u5239\u90a3\u95f4\u660e\u6167\u516c\u4e3b\u8f6c\u4e86\u597d\u51e0\u4e2a\u5ff5\u5934\uff0c\u5584\u6076\u4ea4\u6218\u4e8e\u5fc3\uff0c\u7ec8\u4e8e\u5584\u826f\u7684\u4e00\u9762\u5360\u4e86\u4e0a\u98ce\uff0c\u5fc3\u91cc\u60f3 \u9053\uff1a\u201c\u6211\u7eb5\u7136\u80fd\u591f\u4f7f\u4ed6\u592b\u59bb\u5206\u5f00\uff0c\u4ed6\u7684\u4e00\u9897\u5fc3\u4e5f\u603b\u662f\u5728\u4ed6\u59bb\u5b50\u8eab\u4e0a\uff0c\u6211\u6298\u78e8\u4ed6\u7684\u59bb\u5b50\u53c8\u6709\u4f55 \u7528\uff1f\u6211\u82e5\u662f\u771f\u7684\u6b22\u559c\u4ed6\uff0c\u5c31\u5e94\u8be5\u8ba9\u4ed6\u5feb\u4e50\u3002\u201d \u3000\u3000\u601d\u5ff5\u53ca\u6b64\uff0c\u5fc3\u610f\u7acb\u51b3\u3002\u660e\u6167\u516c\u4e3b\u538b\u4e0b\u4e86\u5fc3\u4e2d\u7684\u5992\u706b\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u5427\uff0c\u4f60\u4eec\u4e0d\u7528\u54ed\u54ed\u557c\u557c \u4e86\uff01\u6211\u62fc\u7740\u53d7\u7239\u7239\u9063\u8d23\uff0c\u8ba9\u4f60\u4eec\u8d70\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u559c\u51fa\u671b\u5916\uff0c\u62c9\u4e86\u6768\u5a49\uff0c\u5411\u516c\u4e3b\u884c\u4e86\u4e00\u793c\uff0c\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u516c\u4e3b\uff0c\u90a3\u4e48\u6211\u4eec\u8d70\u5566\uff01\u201d \u660e\u6167\u516c\u4e3b\u5ffd\u9053\uff1a\u201c\u4e14\u6162\uff01\u201d\u674e\u601d\u5357\u53ea\u9053\u516c\u4e3b\u6539\u4e86\u4e3b\u610f\uff0c\u767b\u65f6\u5446\u4e86\u3002 \u3000\u3000\u660e\u6167\u516c\u4e3b\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u7684\u672c\u9886\u867d\u7136\u4e0d\u9519\uff0c\u4f46\u8981\u60f3\u9003\u51fa\u8499\u53e4\uff0c\u6050\u6015\u8fd8\u662f\u4e0d\u5f88\u5bb9 \u6613\u5427\u3002\u54f2\u522b\u4e00\u56de\u548c\u6797\uff0c\u5fc5\u5b9a\u77e5\u4f1a\u795e\u7ffc\u8425\u7edf\u9886\u6728\u534e\u9ece\u7eed\u6d3e\u8ffd\u5175\u7f09\u6355\u4f60\u4eec\u3002\u800c\u4e14\u4f60\u4eec\u53c8\u662f\u6c49\u4eba\uff0c \u5404\u5730\u5173\u5361\u5373\u4f7f\u5c1a\u672a\u63a5\u83b7\u547d\u4ee4\uff0c\u5bf9\u6c49\u4eba\u4e5f\u5fc5\u5b9a\u4e25\u52a0\u76d8\u95ee\uff0c\u4e0d\u4f1a\u8f7b\u6613\u653e\u884c\u7684\u3002\u91cd\u91cd\u5173\u53e3\uff0c\u4f60\u4eec\u8fc7 \u5f97\u4e86\u4e00\u5173\uff0c\u4e5f\u8fc7\u4e0d\u4e86\u7b2c\u4e8c\u5173\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u53cc\u7709\u4e00\u7ad6\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e9b\u56f0\u96be\uff0c\u65e9\u5c31\u5728\u6211\u610f\u6599\u4e4b\u4e2d\u3002\u6211\u62fc\u7740\u8c41\u4e86\u6027\u547d\uff0c\u4e5f\u987e\u8651\u4e0d \u4e86\u90a3\u8bb8\u591a\u4e86\u3002\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u9053\uff1a\u201c\u6551\u4eba\u987b\u6551\u5f7b\uff0c\u9001\u4f5b\u9001\u5230\u897f\uff0c\u6211\u7d22\u6027\u6210\u5168\u4f60\u4eec\u5230\u5e95\u3002\u8fd9\u9762\u91d1\u724c\u4f60\u62ff\u53bb\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u63a5\u8fc7\u91d1\u724c\u4e00\u770b\uff0c\u53ea\u89c1\u4e0a\u9762\u96d5\u6709\u4e00\u5934\u5140\u9e70\uff0c\u795e\u6001\u5a01\u731b\uff0c\u6829\u6829\u5982\u751f\u3002\u65c1\u8fb9\u6709\u4e24\u884c\u8499\u53e4 \u6587\u5b57\uff0c\u674e\u601d\u5357\u53ea\u8bc6\u5f97\u201c\u91d1\u5e10\u201d\u4e8c\u5b57\u3002 \u3000\u3000\u660e\u6167\u516c\u4e3b\u9053\uff1a\u201c\u8fd9\u662f\u6211\u7239\u7239\u7684\u91d1\u5e10\u4ee4\u724c\uff0c\u4f60\u6709\u4e86\u8fd9\u9762\u91d1\u813e\uff0c\u5404\u5904\u5173\u5361\u5c31\u4e0d\u6562\u4e3a\u96be\u4f60\u4e86\u3002 \u53ef\u662f\u53ea\u6709\u91d1\u724c\u6ca1\u6709\u5750\u9a91\u8fd8\u662f\u4e0d\u884c\uff0c\u56e0\u4e3a\u4f60\u4eec\u5fc5\u987b\u8d76\u5728\u7f09\u6355\u6587\u4e66\u5c1a\u672a\u9001\u5230\u5404\u5904\u5173\u5361\u4e4b\u524d\u9003\u51fa \u53bb\u3002\u6b64\u5916\u4f60\u4eec\u8fd8\u5f97\u63d0\u9632\u7ed9\u91d1\u5e10\u6b66\u571f\u8ffd\u4e0a\uff0c\u8fd9\u4e9b\u4eba\u5949\u4e86\u6728\u534e\u9ece\u4e4b\u547d\u6355\u4f60\uff0c\u53ef\u4ee5\u4e0d\u7406\u8fd9\u9762\u91d1 \u724c\u3002\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u548c\u5979\u7684\u4e24\u4e2a\u4f8d\u5973\u90fd\u662f\u5e26\u6709\u4e00\u5339\u7a7a\u9a91\u7684\uff0c\u8bf4\u81f3\u6b64\u5904\uff0c\u660e\u6167\u516c\u4e3b\u6325\u4e00\u6325\u624b\uff0c\u628a\u4e09\u5339 \u9a8f\u9a6c\u653e\u8fc7\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e09\u5339\u9a6c\u662f\u5343\u4e2d\u9009\u4e00\u7684\u826f\u9a79\uff0c\u4f60\u4eec\u53ef\u4ee5\u66ff\u6362\u4e58\u5750\uff0c\u7528\u4e0d\u4e86\u5341\u5929\u5c31\u53ef\u4ee5 \u5230\u8fb9\u5883\u4e86\uff0c\u54f2\u522b\u53d7\u4f24\u9887\u91cd\uff0c\u5373\u4f7f\u4ed6\u56de\u5f97\u4e86\u548c\u6797\uff0c\u81f3\u5c11\u4e5f\u662f\u56db\u4e94\u5929\u4e4b\u540e\u7684\u4e8b\u60c5\u3002\u5230\u4e86\u548c\u6797\uff0c\u518d \u53d1\u51fa\u7f09\u6355\u6587\u4e66\uff0c\u90a3\u5c31\u8981\u8fdf\u4f60\u4eec\u5341\u5929\u516b\u5929\u4e86\u3002\u4e0d\u8fc7\uff0c\u4f60\u4eec\u4e5f\u4e0d\u80fd\u56e0\u6b64\u677e\u61c8\u5927\u610f\uff0c\u65e2\u7136\u8981\u9003\uff0c\u90a3 \u5c31\u8d8a\u5feb\u8d8a\u597d\u3002\u597d\uff0c\u4f46\u613f\u4f60\u4eec\u5e73\u5b89\u8131\u9669\uff0c\u4ee5\u540e\u6211\u4e5f\u4e0d\u5e0c\u671b\u518d\u89c1\u5230\u4f60\u4eec\u4e86\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u60f3\u4e0d\u5230\u516c\u4e3b\u7ed9\u4ed6\u8bbe\u60f3\u5f97\u8fd9\u6837\u5468\u5230\uff0c\u6768\u5a49\u66f4\u662f\u5927\u611f\u610f\u5916\uff0c\u60f3\u8d77\u521a\u624d\u8fd8\u5728\u5fc3\u91cc\u9a82\u5979\uff0c \u4e0d\u89c9\u751a\u662f\u5c34\u5c2c\uff0c\u6b63\u8981\u4e0a\u524d\u9053\u8c22\uff0c\u660e\u6167\u516c\u4e3b\u548c\u5979\u7684\u4e24\u4e2a\u4f8d\u5973\u65e9\u5df2\u62e8\u8f6c\u9a6c\u5934\uff0c\u4e00\u9635\u98ce\u5730\u8dd1\u4e86\u3002 \u3000\u3000\u6768\u5a49\u5446\u4e86\u534a\u664c\uff0c\u8bf4\u9053\uff1a\u201c\u5357\u54e5\uff0c\u8fd9\u4f4d\u660e\u6167\u516c\u4e3b\u5bf9\u4f60\u5f88\u4e0d\u9519\u554a\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u9762\u4e0a\u4e00\u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u53ea\u966a\u5979\u6253\u4e00\u6b21\u730e\uff0c\u8c08\u4e0d\u4e0a\u4ec0\u4e48\u4ea4\u60c5\uff0c\u4f60\u53ef\u83ab\u8981\u8bef\u4f1a\u3002\u201d \u3000\u3000\u6768\u5a49\u7b11\u9053\uff1a\u201c\u4f60\u548c\u5979\u76f8\u8bc6\u5728\u524d\uff0c\u5c31\u662f\u6709\u751a\u4ea4\u60c5\u6211\u4e5f\u4e0d\u80fd\u602a\u4f60\u3002\u4f55\u51b5\uff0c\u5979\u5f85\u4f60\u56fa\u7136\u4e0d\u9519\uff0c \u4f60\u5f85\u6211\u66f4\u4e0d\u9519\u5440\uff01\u8981\u4e0d\u662f\u4f60\u521a\u624d\u4ee5\u6b7b\u76f8\u80c1\uff0c\u5979\u6050\u6015\u8fd8\u4e0d\u80af\u653e\u8fc7\u6211\u5462\u3002\u5357\u54e5\uff0c\u6211\u662f\u771f\u7684\u611f\u6fc0 \u4f60\uff0c\u4f60\u4e5f\u83ab\u8981\u8bef\u4f1a\u6211\u662f\u8bf4\u7684\u53cd\u8bdd\u3002\u201d \u3000\u3000\u8fd9\u662f\u6768\u5a49\u4e09\u5929\u6765\u7b2c\u4e00\u6b21\u73b0\u51fa\u7684\u7b11\u5bb9\uff0c\u8fd9\u7b11\u5bb9\u51fa\u73b0\u5728\u6768\u5a49\u7684\u9762\u4e0a\uff0c\u5374\u626b\u53bb\u4e86\u674e\u601d\u5357\u5fc3\u4e0a\u7684 \u9634\u9709\u3002 \u3000\u3000\u6768\u5a49\u95fb\u5f97\u4e00\u80a1\u523a\u9f3b\u7684\u6c14\u5473\uff0c\u56de\u5934\u4e00\u770b\uff0c\u7b11\u9053\uff1a\u201c\u54ce\u5440\uff0c\u8fd9\u4e24\u5c3e\u9c7c\u513f\u5df2\u7ecf\u70e4\u7126\u4e86\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u63a5\u8fc7\u4e00\u6761\u70e4\u9c7c\uff0c\u7eb3\u5165\u53e3\u4e2d\u5927\u56bc\uff0c\u628a\u9c7c\u9aa8\u90fd\u541e\u54bd\u4e86\uff0c\u8bf4\u9053\uff1a\u201c\u6ecb\u5473\u597d\u5f97\u5f88\u5440\uff0c\u51fa\u751f \u4ee5\u6765\u4ece\u672a\u5403\u8fc7\u8fd9\u6837\u597d\u7684\u4e1c\u897f\uff0c\u201d\u6768\u5a49\u9053\uff1a\u201c\u6211\u4e0d\u4fe1\u3002\u201d\u54ac\u4e86\u4e00\u53e3\uff0c\u4e0d\u7531\u5f97\u76b1\u8d77\u4e86\u7709\u5934\u3002\u8bf4 \u9053\uff1a\u201c\u53c8\u7126\u53c8\u82e6\uff0c\u597d\u50cf\u4e00\u5757\u6728\u70ad\u4f3c\u7684\uff0c\u4e8f\u4f60\u5403\u5f97\u4e0b\u53bb\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u7b11\u9053\uff1a\u201c\u6211\u5df2\u7ecf\u89c9\u5f97\u5f88\u597d\u5403\u4e86\uff1b\u6211\u4eec\u5bb6\u4e61\u7684\u98ce\u4fd7\uff0c\u65b0\u5a18\u5b50\u5a5a\u540e\u4e09\u65e5\uff0c\u5c31\u8981\u4e0b\u53a8\u6d17 \u624b\u4f5c\u7fb9\u6c64\uff0c\u4f5c\u7fb9\u6c64\u7528\u7684\u5fc5\u5b9a\u662f\u9ca4\u9c7c\u3002\u5982\u4eca\u4f60\u53ea\u7528\u666e\u666e\u901a\u901a\u7684\u4e24\u5c3e\u767d\u9cde\u9c7c\uff0c\u53c8\u4e0d\u662f\u4f5c\u7684\u7fb9\u6c64\uff0c \u70e4\u51fa\u6765\u7684\u5473\u9053\u5374\u6bd4\u9ca4\u9c7c\u8fd8\u597d\u5403\uff0c\u6211\u5c82\u80fd\u4e0d\u5927\u5927\u5730\u5938\u8d5e\u4f60\u5462\uff01\u4ee5\u540e\u4f60\u5c31\u662f\u5929\u5929\u505a\u8fd9\u6837\u7684\u70e4\u9c7c\u7ed9 \u6211\u5403\uff0c\u6211\u4e5f\u5fc3\u6ee1\u610f\u8db3\u4e86\uff01\u201d \u3000\u3000\u6768\u5a49\u7f9e\u5f97\u6ee1\u9762\u901a\u7ea2\uff0c\u55d4\u9053\uff1a\u201c\u539f\u6765\u4f60\u662f\u54c4\u6211\u6b22\u559c\u7684\u3002\u597d\uff0c\u4ee5\u540e\u53ef\u6709\u5f97\u4f60\u5403\u82e6\u5462\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u53ea\u8981\u662f\u4f60\u4eb2\u624b\u505a\u7684\u83dc\u5f0f\uff0c\u82e6\u7684\u5403\u8fdb\u53e3\u91cc\u4e5f\u4f1a\u53d8\u751c\uff0c\u4f60\u5feb\u5403\u5427\uff0c\u8bf4\u6b63\u7ecf\u7684\uff0c \u5403\u9971\u4e86\u4e5f\u597d\u957f\u6c14\u529b\u5440\u3002\u201d \u3000\u3000\u6768\u5a49\u70b9\u4e86\u70b9\u5934\uff0c\u8bf4\u9053\uff0c\u201c\u4e0d\u9519\uff0c\u54b1\u4eec\u53ef\u4e0d\u80fd\u8f9c\u8d1f\u4e86\u660e\u6167\u516c\u4e3b\u8d60\u9001\u826f\u9a79\u7684\u7f8e\u610f\u3002\u201d\u6768\u5a49\u672c \u6765\u662f\u75b2\u5026\u4e0d\u582a\u7684\uff0c\u6b64\u65f6\u5fc3\u5934\u4e4b\u7ed3\u5df2\u7ecf\u89e3\u5f00\uff0c\u9003\u751f\u53c8\u6709\u4e86\u5e0c\u671b\uff0c\u4e0d\u77e5\u4e0d\u89c9\u7cbe\u795e\u597d\u4e86\u8d77\u6765\uff0c\u4e8e\u662f \u5728\u9971\u9910\u4e4b\u540e\uff0c\u4e24\u4eba\u5c31\u8de8\u4e0a\u5750\u9a91\uff0c\u8fde\u591c\u8d76\u8def\u3002 \u3000\u3000\u660e\u6167\u516c\u4e3b\u7ed9\u4ed6\u4eec\u7684\u8fd9\u9762\u201c\u91d1\u5e10\u4ee4\u724c\u201d\u679c\u7136\u5927\u6709\u7528\u5904\uff0c\u6cbf\u9014\u7ecf\u8fc7\u7684\u5173\u5361\uff0c\u4e00\u89c1\u91d1\u724c\uff0c\u65e0\u4e0d \u6bb7\u52e4\u4f8d\u5019\uff0c\u975e\u4f46\u6ca1\u6709\u4e3a\u96be\uff0c\u800c\u4e14\u8fd8\u9001\u7ed9\u4ed6\u4eec\u7cae\u98df\u548c\u6c99\u6f20\u4e0a\u9700\u7528\u7684\u7bf7\u5e10\u7b49\u7b49\u7528\u5177\u3002 \u3000\u3000\u8fd9\u4e00\u5929\u4ed6\u4eec\u8fdb\u5165\u4e86\u5927\u6208\u58c1\uff0c\u6768\u5a49\u70ed\u5f97\u5598\u4e0d\u8fc7\u6c14\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u90a3\u5e74\u548c\u54e5\u54e5\u6765\u7684\u65f6\u5019\u662f\u51ac \u5929\uff0c\u522b\u4eba\u8bf4\u6c99\u6f20\u600e\u4e48\u708e\u70ed\uff0c\u8bf4\u662f\u9e21\u86cb\u57cb\u5728\u6c99\u91cc\u4e00\u4f1a\u513f\u5c31\u4f1a\u70e4\u719f\uff0c\u6211\u8fd8\u4e0d\u76f8\u4fe1\u5462\u3002\u73b0\u5728\u624d\u77e5\u9053 \u662f\u771f\u7684\u5982\u6b64\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u73b0\u5728\u662f\u4e5d\u6708\u5929\u65f6\uff0c\u5df2\u7ecf\u597d\u5f97\u591a\u4e86\u3002\u6211\u6765\u7684\u65f6\u5019\u6b63\u662f\u9a84\u9633\u5982\u706b\u7684\u4e03\u6708\uff0c\u90a3\u624d \u771f\u53eb\u70ed\u5f97\u96be\u53d7\u5462\uff01\u5c82\u6b62\u9e21\u86cb\u53ef\u4ee5\u70e4\u719f\uff0c\u4eba\u4e5f\u50cf\u653e\u5728\u84b8\u7b3c\u4f3c\u7684\u8981\u7ed9\u5b83\u70e4\u719f\u4e86\u3002\u201d \u3000\u3000\u8e0f\u5165\u4e86\u5927\u6208\u58c1\uff0c\u674e\u601d\u5357\u4e0d\u7531\u5f97\u60f3\u8d77\u4e86\u6765\u65f6\u7684\u5947\u9047\u3002\u4ed6\u66fe\u5728\u8fd9\u6208\u58c1\u4e0a\u53d1\u73b0\u5c60\u767e\u57ce\u7684\u5c38\u9ab8\uff0c \u4ed6\u66fe\u5728\u8fd9\u6208\u58c1\u4e0a\u5de7\u9047\u5b5f\u5c11\u521a\u7236\u5973\uff0c\u5f80\u4e8b\u4e00\u5e55\u5e55\u7684\u7ffb\u8fc7\u5fc3\u5934\uff0c\u5b5f\u660e\u971e\u7684\u5f71\u5b50\u4e0d\u77e5\u4e0d\u89c9\u7684\u53c8\u5728\u4ed6 \u5fc3\u5934\u51fa\u73b0\u4e86\u3002 \u3000\u3000\u674e\u601d\u5357\u4e5f\u66fe\u597d\u51e0\u6b21\u7684\u60f3\u628a\u5b5f\u5c11\u521a\u7236\u5973\u7684\u4e8b\u60c5\u544a\u8bc9\u6768\u5a49\uff0c\u4f46\u53c8\u6015\u6768\u5a49\u591a\u7591\uff0c\u51e0\u6b21\u60f3\u8bf4\u90fd\u6ca1 \u6709\u8bf4\u3002\u73b0\u5728\u4ed6\u65e7\u5730\u91cd\u6e38\uff0c\u5f80\u4e8b\u7684\u56de\u5fc6\u53d8\u6210\u4e86\u4ed6\u7cbe\u795e\u7684\u8d1f\u62c5\uff0c\u4ed6\u7a81\u7136\u60f3\u8d77\u6768\u5a49\u8bf4\u8fc7\u7684\u4e00\u53e5\u8bf4 \u8bdd\uff0c\u90a3\u665a\u5728\u660e\u6167\u516c\u4e3b\u8d70\u540e\uff0c\u6768\u5a49\u66fe\u7ecf\u8bf4\u9053\uff1a\u201c\u4f60\u548c\u5979\u76f8\u8bc6\u5728\u524d\uff0c\u5c31\u662f\u6709\u751a\u4ea4\u60c5\u6211\u4e5f\u4e0d\u80fd\u602a \u4f60\u3002\u201d \u3000\u3000\u674e\u6069\u5357\u5fc3\u91cc\u81ea\u601d\uff1a\u201c\u6211\u4e0e\u5b5f\u660e\u971e\u5176\u5b9e\u4e5f\u4e0d\u8fc7\u662f\u4e00\u9762\u4e4b\u4ea4\uff0c\u6211\u4e3a\u4ec0\u4e48\u6015\u548c\u5a49\u59b9\u8bf4\u5462\uff1f\u201d\u60f3 \u81f3\u6b64\u5904\uff0c\u4ed6\u4fbf\u4f3c\u201c\u4f5c\u8d3c\u5fc3\u865a\u201d\u4f3c\u7684\uff0c\u9762\u4e0a\u7ea2\u4e86\u8d77\u6765\u3002 \u3000\u3000\u6768\u5a49\u4e45\u4e45\u4e0d\u89c1\u674e\u601d\u5357\u8bf4\u8bdd\uff0c\u56de\u5934\u4e00\u770b\uff0c\u95ee\u9053\uff1a\u201c\u5357\u54e5\uff0c\u4f60\u5728\u60f3\u4e9b\u4ec0\u4e48\uff1f\u54a6\uff0c\u4f60\u597d\u50cf\u662f\u53d1 \u70e7\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\uff0c\u70ed\u662f\u70ed\u5f97\u5389\u5bb3\uff0c\u6211\u8fd8\u62b5\u53d7\u5f97\u4e86\u7684\uff0c\u4f60\u522b\u62c5\u5fc3\u3002\u201d\u5e78\u597d\u4ed6\u4eec\u662f\u5728\u9177 \u70ed\u7684\u6c99\u6f20\u4e4b\u4e2d\uff0c\u674e\u601d\u5357\u8fd9\u4e48\u4e00\u8bf4\uff0c\u8f7b\u8f7b\u5730\u5c31\u63a9\u9970\u8fc7\u53bb\u4e86\uff0c\u53ef\u662f\u7531\u4e8e\u4ed6\u4e0d\u60ef\u8bf4\u8c0e\uff0c\u8138\u4e0a\u5c31\u66f4\u662f \u7ea2\u4e86\u3002 \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u5357\u54e5\uff0c\u4f60\u522b\u901e\u80fd\uff0c\u8eab\u4f53\u5f53\u771f\u662f\u53d7\u5f97\u4f4f\u624d\u597d\uff1b\u5728\u6c99\u6f20\u4e2d\u751f\u75c5\uff0c\u53ef\u4e0d\u662f\u5f53\u800d\u7684 \u554a\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u4e0d\u8981\u7d27\u7684\uff0c\u73b0\u5728\u5df2\u662f\u65e5\u5934\u504f\u897f\uff0c\u8fc7\u4e00\u4f1a\u5c31\u6709\u665a\u98ce\u6765\u4e86\u3002\u6211\u7ed9\u4f60\u8bf4\u4e2a\u6545\u4e8b \u5427\u3002\u201d \u3000\u3000\u6768\u5a49\u7b11\u9053\uff1a\u201c\u70ed\u5f97\u8fd9\u6837\u5389\u5bb3\uff0c\u4e8f\u4f60\u8fd8\u6709\u5174\u81f4\u8bf4\u6545\u4e8b\u3002\u4e5f\u597d\uff0c\u4f60\u8bf4\u6545\u4e8b\uff0c\u6211\u542c\u6545\u4e8b\uff0c\u4e5f\u8bb8 \u5c31\u4f1a\u5fd8\u5374\u70ed\u4e86\u3002\u4f60\u8bf4\u5427\uff0c\u4ec0\u4e48\u6545\u4e8b\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u662f\u6211\u5728\u8fd9\u6c99\u6f20\u4e0a\u906d\u9047\u7684\u4e8b\u3002\u201d\u6b63\u8981\u539f\u539f\u672c\u672c\u5730\u544a\u8bc9\u6768\u5a49\uff0c\u6768\u5a49\u5ffd\u5730\u53eb\u9053\uff1a \u201c\u5357\u54e5\uff0c\u4f60\u77a7\u90a3\u8fb9\uff0c\u6709\u4e00\u7247\u7eff\uff0c\u54c8\uff0c\u662f\u6811\u6728\u54e9\uff01\u5feb\u6765\uff0c\u5feb\u6765\uff01\u4f60\u7684\u6545\u4e8b\u7559\u5230\u90a3\u513f\u518d\u8bf4\u5427\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u62ac\u5934\u4e00\u770b\uff0c\u5374\u539f\u6765\u5c31\u662f\u4ed6\u548c\u5b5f\u5c11\u521a\u7236\u5973\u66fe\u7ecf\u5728\u90a3\u91cc\u4f4f\u8fc7\u4e00\u665a\u7684\u90a3\u5ea7\u5c0f\u5c71\u3002\u90a3\u65e5\u4ed6 \u7ed9\u547c\u9ece\u5962\u6253\u6655\uff0c\u4e00\u9192\u6765\u5c31\u662f\u5728\u5b5f\u5bb6\u7236\u5973\u7684\u5e10\u5e55\u4e4b\u4e2d\u3002\u90a3\u665a\uff0c\u4ed6\u66fe\u65e0\u610f\u4e2d\u5077\u542c\u4e86\u4ed6\u4eec\u7236\u5973\u7684\u8c08 \u8bdd\uff0c\u7b2c\u4e8c\u5929\u4e00\u65e9\uff0c\u4ed6\u4eec\u7236\u5973\u4e0d\u8f9e\u800c\u884c\uff0c\u5b5f\u5c11\u521a\u66fe\u5728\u5730\u4e0a\u7559\u4e0b\u201c\u4e3a\u864e\u4f5c\u6005\uff0c\u5fc5\u53d6\u4f60\u547d\u201d\u516b\u4e2a\u5927 \u5b57\u8b66\u544a\u4ed6\uff0c\u2026\u2026\u8fd9\u8bb8\u591a\u4e8b\u60c5\uff0c\u90fd\u662f\u53d1\u751f\u5728\u8fd9\u5ea7\u5c0f\u5c71\u4e0a\u7684\u3002 \u3000\u3000\u5f80\u4e8b\u5386\u5386\uff0c\u5982\u5728\u76ee\u524d\u3002\u674e\u601d\u5357\u6309\u4e0b\u4e00\u9897\u8df3\u52a8\u7684\u5fc3\uff0c\u8ddf\u7740\u6768\u5a49\u8dd1\u3002\u6768\u5a49\u6df1\u6df1\u5438\u4e86\u53e3\u6c14\uff0c\u8bf4 \u9053\uff1a\u201c\u6709\u4e00\u80a1\u6d77\u98ce\u7684\u5473\u9053\uff0c\u6211\u731c\u8fd9\u5c71\u4e0a\u53ef\u80fd\u8fd8\u6709\u7518\u6cc9\u3002\u201d\u674e\u601d\u5357\u7b11\u9053\uff1a\u201c\u4e0d\u7528\u731c\uff0c\u8fd9\u5c71\u4e0a\u662f \u6709\u7518\u6cc9\u3002\u201d\u6768\u5a49\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d\u674e\u601d\u5357\u9053\uff1a\u201c\u6211\u66fe\u7ecf\u5728\u8fd9\u5c71\u4e0a\u76db\u8fc7\u4e24\u5927\u76ae\u888b\u7684\u6c34\u3002\u201d \u6b64\u8a00\u4e00\u51fa\uff0c\u4e0d\u89c9\u53c8\u662f\u9762\u4e0a\u4e00\u7ea2\uff0c\u56e0\u4e3a\u90a3\u4e24\u5927\u76ae\u888b\u7684\u6c34\uff0c\u5176\u5b9e\u662f\u5b5f\u660e\u971e\u7559\u7ed9\u4ed6\u7684\u3002 \u3000\u3000\u6768\u5a49\u6b63\u4e3a\u53d1\u73b0\u6c99\u6f20\u7684\u7eff\u6d32\u800c\u9ad8\u5174\uff0c\u6839\u672c\u5c31\u6ca1\u6709\u6ce8\u610f\u674e\u601d\u5357\u7684\u9762\u8272\uff0c\u5f53\u4e0b\u5174\u51b2\u51b2\u5730\u8bf4\u9053\uff1a \u201c\u54b1\u4eec\u6c34\u56ca\u91cc\u7684\u6c34\u6240\u5269\u65e0\u591a\uff0c\u8fd9\u53ef\u662f\u518d\u597d\u4e0d\u8fc7\u4e86\u3002\u5357\u54e5\uff0c\u5230\u90a3\u6797\u5b50\u91cc\uff0c\u4f60\u5148\u597d\u597d\u5730\u7761\u4e00\u89c9\uff0c \u6691\u6c14\u9000\u4e86\uff0c\u518d\u7ed9\u6211\u8bb2\u6545\u4e8b\u3002\u201d \u3000\u3000\u6768\u5a49\u4f53\u8d34\u5165\u5fae\uff0c\u674e\u601d\u5357\u5fc3\u91cc\u53c8\u662f\u611f\u6fc0\uff0c\u53c8\u662f\u60ed\u6127\uff0c\u60f3\u9053\uff1a\u201c\u6211\u4e00\u5b9a\u8981\u628a\u6211\u7684\u8fc7\u53bb\u90fd\u544a\u8bc9 \u5979\uff0c\u4e00\u70b9\u513f\u4e5f\u4e0d\u9690\u7792\u3002\u5979\u3001\u5979\u5bf9\u6211\u5b9e\u5728\u662f\u592a\u597d\u4e86\uff0c\u6211\u5c82\u80fd\u5fc3\u91cc\u8fd8\u6709\u522b\u4eba\uff1f\u201d \u3000\u3000\u8d70\u5230\u5c71\u811a\uff0c\u53ea\u89c1\u6811\u6797\u91cc\u9690\u9690\u9732\u51fa\u7eff\u8272\u7684\u5e10\u5e55\uff0c\u6768\u5a49\u9053\uff1a\u201c\u54a6\uff0c\u8fd8\u6709\u4eba\u5728\u8fd9\u91cc\u5462\u3002\u4ed6\u4eec\u5148 \u6765\u662f\u4e3b\uff0c\u54b1\u4eec\u5e94\u8be5\u548c\u4ed6\u4eec\u6253\u4e2a\u62db\u547c\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u5fc3\u4e2d\u4e00\u52a8\uff0c\u60f3\u9053\uff1a\u201c\u600e\u7684\u8fd9\u6837\u5de7\uff0c\u90a3\u65e5\u6709\u5b5f\u5c11\u521a\u7236\u5973\u5728\u8fd9\u91cc\u5b89\u4e0b\u5e10\u5e55\uff0c\u73b0\u5728\u53c8\u6709 \u4eba\u5728\u8fd9\u91cc\uff0c\u5374\u4e0d\u77e5\u662f\u4ec0\u4e48\u4eba\u3002\u201d\u5fc3\u5ff5\u672a\u5df2\uff0c\u5e10\u5e55\u4e2d\u4eba\u5df2\u7ecf\u95fb\u58f0\u51fa\u73b0\uff0c\u53cc\u65b9\u6253\u4e86\u4e2a\u7167\u9762\uff0c\u4e0d\u7531 \u5f97\u90fd\u662f\u5927\u5403\u4e00\u60ca\uff01 \u3000\u3000\u4ece\u5e10\u5e55\u4e2d\u51fa\u6765\u7684\u4e00\u7537\u4e00\u5973\uff0c\u4e0d\u662f\u522b\u4eba\uff0c\u6b63\u662f\u5b8b\u94c1\u8f6e\u548c\u67f3\u4e09\u5a18\u8fd9\u5bf9\u592b\u5987\u3002 \u3000\u3000\u4ed6\u4eec\u592b\u59bb\u4e8c\u4eba\u672c\u662f\u5c60\u767e\u57ce\u7684\u90e8\u5c5e\uff0c\u90a3\u65e5\u674e\u601d\u5357\u5728\u8349\u539f\u4e0a\u78b0\u89c1\u4ed6\u4eec\uff0c\u4ed6\u4eec\u4e5f\u50cf\u5b5f\u5c11\u521a\u4e00 \u6837\uff0c\u4ee5\u4e3a\u5728\u548c\u6797\u505a\u8499\u53e4\u4eba\u7684\u5b98\u7684\u4f59\u4e00\u4e2d\u662f\u674e\u601d\u5357\u7684\u7236\u4eb2\uff0c\u4e0d\u5206\u7682\u767d\uff0c\u5c31\u548c\u674e\u601d\u5357\u6253\u4e86\u8d77\u6765\u3002 \u3000\u3000\u6070\u503c\u6210\u5409\u601d\u6c57\u7684\u201c\u91d1\u5e10\u6b66\u58eb\u201d\u6728\u534e\u9ece\u4e0e\u8d64\u8001\u6e29\u6765\u5230\uff0c\u6728\u534e\u9ece\u4e00\u7bad\u5c04\u4f24\u4e86\u5b8b\u94c1\u8f6e\uff0c\u5413\u9000\u4e86 \u67f3\u4e09\u5a18\uff0c\u628a\u674e\u601d\u5357\u6551\u53bb\u3002\u56e0\u6b64\u8bef\u4f1a\u8d8a\u6765\u8d8a\u6df1\uff0c\u5728\u6765\u94c1\u8f6e\u7684\u5fc3\u76ee\u4e2d\uff0c\u65e9\u5df2\u628a\u674e\u601d\u5357\u5f53\u4f5c\u5356\u56fd\u6c42 \u8363\u7684\u6b66\u6797\u8d25\u7c7b\u4e86\u3002 \u3000\u3000\u5b8b\u94c1\u8f6e\u672c\u6765\u5c31\u662f\u4e00\u4e2a\u6027\u60c5\u975e\u5e38\u66b4\u8e81\u7684\u4eba\uff0c\u4e00\u89c1\u4e86\u674e\u601d\u5357\uff0c\u4e0d\u7531\u5f97\u65e0\u540d\u706b\u8d77\u4e09\u5343\u4e08\uff0c\u767b\u65f6 \u51b2\u4e0a\u524d\u53bb\uff0c\u5927\u6012\u559d\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u8001\u5b50\u6b63\u8981\u627e\u4f60\u7b97\u5e10\uff01\u201d\u53cc\u8f6e\u9ad8\u4e3e\uff0c\u4e00\u62db\u201c\u65cb\u8f6c\u4e7e\u5764\u201d\uff0c\u53f3 \u8f6e\u5de6\u63a8\uff0c\u5de6\u8f6e\u53f3\u538b\uff0c\u5411\u674e\u601d\u5357\u731b\u6253\u3002 \u3000\u3000\u674e\u601d\u5357\u4e00\u4e2a\u5012\u7eb5\uff0c\u53eb\u9053\uff1a\u201c\u5b8b\u5927\u54e5\u2026\u2026\u201d\u5e95\u4e0b\u7684\u8bdd\u5c1a\u672a\u8bf4\u51fa\uff0c\u5b8b\u94c1\u8f6e\u7684\u53cc\u8f6e\u53c8\u5df2\u7838\u5230\uff0c \u9a82\u9053\uff1a\u201c\u4e0d\u8981\u8138\uff0c\u8c01\u4e0e\u4f60\u79f0\u5144\u9053\u5f1f\uff1f\u201d \u3000\u3000\u5b8b\u94c1\u8f6e\u9891\u65bd\u6740\u624b\uff0c\u653b\u52bf\u51cc\u5389\uff0c\u674e\u601d\u5357\u4e0d\u5f97\u4e0d\u62d4\u5251\u62db\u67b6\u3002\u89e3\u4e86\u4e00\u62db\uff0c\u7f13\u8fc7\u53e3\u6c14\uff0c\u53eb\u9053\uff0c \u201c\u4e14\u6162\u52a8\u624b\uff0c\u6211\u6709\u8bdd\u8bf4\uff01\u201d \u3000\u3000\u5b8b\u94c1\u8f6e\u4e0d\u7406\u674e\u601d\u5357\u7684\u547c\u53eb\uff0c\u201c\u65cb\u8f6c\u4e7e\u5764\u201c\uff0c\u201c\u96f7\u7535\u4ea4\u8f70\u201d\uff0c\u201c\u6cf0\u5c71\u538b\u9876\u201d\uff0c\u201c\u4e94\u65a7\u5f00 \u5c71\u201d\u2026\u2026\u4e00\u62db\u7d27\u63a5\u4e00\u62db\uff0c\u62db\u62db\u90fd\u662f\u6740\u624b\u3002 \u3000\u3000\u67f3\u4e09\u5a18\u89c1\u674e\u601d\u5357\u7740\u7740\u9000\u8ba9\u3001\u5012\u662f\u6709\u70b9\u8d77\u7591\uff0c\u4e00\u65f6\u8e0c\u8e87\u83ab\u51b3\uff0c\u8bf4\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u5c31\u542c\u542c\u4ed6 \u8bf4\u4e9b\u4ec0\u4e48\u5427\u3002\u201d\u5b8b\u94c1\u8f6e\u9053\uff1b\u201c\u90a3\u65e5\u4e4b\u4e8b\uff0c\u4f60\u662f\u4eb2\u773c\u89c1\u7684\u3002\u8499\u53e4\u9791\u5b50\u96be\u9053\u4f1a\u65e0\u7f18\u65e0\u6545\u5730\u6551\u4ed6\uff0c \u6211\u7ed9\u9791\u5b50\u5c04\u7684\u90a3\u4e00\u7bad\uff0c\u75ae\u75a4\u8fd8\u672a\u8131\u5462\uff0c\u4f60\u8fd8\u8981\u542c\u4ed6\u7684\u82b1\u8a00\u5de7\u8bed\uff1f\u201d \u3000\u3000\u67f3\u4e09\u5a18\u9053\uff1a\u201c\u53ef\u662f\u5b5f\u59d1\u5a18\u2026\u2026\u201d\u5b8b\u94c1\u8f6e\u4e00\u9762\u653b\u51fb\uff0c\u4e00\u9762\u8bf4\u9053\uff1a\u201c\u5b5f\u59d1\u5a18\u770b\u4e0a\u8fd9\u4e2a\u5c0f\u5b50\uff0c \u4f60\u4e5f\u4fe1\u5979\u7684\u8bdd\uff1f\u5b5f\u5927\u4fa0\u90fd\u540e\u6094\u90a3\u65e5\u653e\u8d70\u8fd9\u5c0f\u5b50\u5462\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u53eb\u9053\uff1a\u201c\u4f60\u89c1\u8fc7\u5b5f\u5927\u4fa0\u4e86\uff1f\u6211\u6b63\u662f\u6709\u51a4\u60c5\u8981\u5411\u4ed6\u8001\u4eba\u5bb6\u7980\u544a\u3002\u4f60\u80fd\u4e0d\u80fd\u6682\u4e14\u4f4f \u624b\uff1f\u201d \u3000\u3000\u5b8b\u94c1\u8f6e\u559d\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u89c1\u8fc7\u5b5f\u5927\u4fa0\u4e86\u3002\u5b5f\u5927\u4fa0\u53eb\u6211\u6740\u4f60\uff01\u201d\u4ed6\u53d7\u4e86\u6728\u534e\u9ece\u90a3\u4e00\u7bad\uff0c\u597d \u4e86\u75ae\u75a4\uff0c\u5fd8\u4e0d\u4e86\u75db\uff0c\u6740\u5f97\u5174\u8d77\uff0c\u54ea\u5bb9\u674e\u601d\u5357\u5206\u8fa9\uff0c\u975e\u4f46\u4e0d\u4f4f\u624b\uff0c\u53cd\u800c\u662f\u653b\u5f97\u66f4\u731b\u4e86\uff01 \u3000\u3000\u6768\u5a49\u5446\u5728\u4e00\u8fb9\uff0c\u7591\u56e2\u83ab\u91ca\uff1a\u201c\u4ed6\u4eec\u8bf4\u7684\u8fd9\u4f4d\u5b5f\u59d1\u5a18\u662f\u8c01\u5462\u3002\u4e3a\u4ec0\u4e48\u5357\u54e5\u4ece\u6ca1\u6709\u548c\u6211\u63d0 \u8fc7\uff1f\u201d \u3000\u3000\u53ef\u662f\u6768\u5a49\u867d\u7136\u5fc3\u6709\u6240\u7591\uff0c\u4f46\u4ece\u5b8b\u94c1\u8f6e\u592b\u59bb\u7684\u53e3\u6c14\u4e2d\uff0c\u4e5f\u5df2\u9690\u7ea6\u731c\u5230\u4e86\u4ed6\u4eec\u8981\u6740\u674e\u601d\u5357\u7684 \u75c7\u7ed3\u6240\u5728\uff0c\u8fde\u5fd9\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u4e00\u5b9a\u662f\u8bef\u4f1a\u4e86\uff0c\u5357\u54e5\u662f\u597d\u4eba\uff0c\u4ed6\u662f\u4ece\u548c\u6797\u9003\u62d9\u6765\u7684\uff0c\u51b3\u4e0d\u662f\u5982 \u4f60\u4eec\u6240\u60f3\u8c61\u7684\u90a3\u6837\u662f\u548c\u9791\u5b50\u4e00\u8def\u7684\uff01\u201d \u3000\u3000\u67f3\u4e09\u5a18\u201c\u5657\u55e4\u201d\u4e00\u7b11\uff0c\u8bf4\u901e\uff1a\u201c\u5357\u54e5\uff0c\u5357\u54e5\uff0c\u53eb\u5f97\u597d\u4eb2\u70ed\u54df\uff01\u4f60\u662f\u4ed6\u7684\u4ec0\u4e48\u4eba\uff1f\u201d\u6768\u5a49 \u89c1\u5979\u95ee\u5f97\u8fd9\u6837\u65e0\u793c\uff0c\u4e0d\u7531\u5f97\u674f\u8138\u901a\u7ea2\uff0c\u67f3\u7709\u5012\u7ad6\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6211\u662f\u4ed6\u7684\u59bb\u5b50\uff0c\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u67f3\u4e09\u5a18\u6014\u4e86\u4e00\u6014\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u4f60\u662f\u4e0d\u77e5\u4ed6\u7684\u5e95\u7ec6\uff0c\u88ab\u4ed6\u9a97\u4e0a\u4e86\u624b\u7684\u5973\u5b50\uff0c\u602a \u4e0d\u5f97\u4f60\u8bf4\u4ed6\u662f\u597d\u4eba\u4e86\u3002\u54c8\u54c8\uff0c\u73b0\u5728\u53ef\u4e0d\u7528\u76d8\u95ee\u8fd9\u4e2a\u5c0f\u5b50\u4e86\uff0c\u4f60\u8bf4\u4ed6\u662f\u2018\u597d\u4eba\u2019\uff0c\u8fd9\u5c31\u6070\u6070\u8bc1 \u660e\u4e86\u4ed6\u662f\u574f\u4eba\u4e86\uff01\u201d \u3000\u3000\u6768\u5a49\u53c8\u60ca\u53c8\u607c\u53c8\u6c14\u53c8\u7591\uff0c\u65a5\u9053\uff1a\u201c\u4f60\u80e1\u8bf4\u4ec0\u4e48\uff1f\u201d\u67f3\u4e09\u5a18\u9053\uff1a\u201c\u4f60\u4e0a\u4e86\u4ed6\u7684\u5f53\u4e5f\u4e0d\u77e5 \u9053\uff0c\u6211\u4e0d\u602a\u4f60\u3002\u4f46\u8fd9\u8d1f\u5fc3\u7684\u5c0f\u5b50\uff0c\u6211\u5374\u662f\u975e\u8981\u4e25\u60e9\u4ed6\u4e0d\u53ef\uff01\u201d\u5530\u7684\u4e00\u97ad\uff0c\u5c31\u5411\u674e\u601d\u5357\u6253\u53bb\uff01 \u3000\u3000\u8fd9\u4e00\u97ad\u6765\u5f97\u5feb\u5982\u95ea\u7535\uff0c\u674e\u601d\u5357\u6b63\u81ea\u53eb\u9053\uff1a\u201c\u5a49\u59b9\uff0c\u522b\u542c\u5979\u7684\u8bdd\uff0c\u6211\u4f1a\u544a\u8bc9\u4f60\u7684\u3002\u201d\u8bdd\u72b9 \u672a\u4e86\uff0c\u5df2\u662f\u88ab\u67f3\u4e09\u5a18\u72e0\u72e0\u5730\u62bd\u4e86\u4e00\u97ad\uff0c\u4e0a\u8863\u5316\u6210\u4e86\u7247\u7247\u8774\u8776\uff0c\u80cc\u4e0a\u8d77\u4e86\u4e00\u9053\u6df1\u7d2b\u8272\u7684\u8840\u75d5\u3002 \u3000\u3000\u539f\u6765\u90a3\u65e5\u5b8b\u94c1\u8f6e\u592b\u5987\u8d1f\u4f24\u800c\u9003\uff0c\u4ed7\u7740\u9a6c\u5feb\uff0c\u7b2c\u4e8c\u65e5\u5c31\u8ffd\u4e0a\u4e86\u5b5f\u5c11\u521a\u3002\u5b5f\u5c11\u521a\u542c\u4e86\u4ed6\u4eec\u7684 \u6295\u8bc9\uff0c\u8ba4\u4e3a\u674e\u601d\u5357\u7236\u5b50\u90fd\u5df2\u6295\u9760\u8499\u53e4\u65e0\u7591\uff0c\u540e\u6094\u6ca1\u6709\u5c06\u674e\u601d\u5357\u6740\u6389\u3002\u4f46\u4ed6\u7684\u5973\u513f\u5b5f\u660e\u971e\u4f9d\u7136 \u4e3a\u674e\u601d\u5357\u8fa9\u62a4\uff0c\u4e0d\u76f8\u4fe1\u4ed6\u662f\u574f\u4eba\u3002 \u3000\u3000\u5728\u5b5f\u660e\u971e\u7684\u60f3\u6cd5\u662f\u8ba4\u4e3a\u771f\u76f8\u672a\u660e\uff0c\u4e0d\u5e94\u968f\u4fbf\u51a4\u6789\u4e00\u4e2a\u4eba\u3002\u5979\u662f\u4e2a\u723d\u6717\u65e0\u90aa\u7684\u5c11\u5973\uff0c\u6839\u672c \u5c31\u6ca1\u6709\u60f3\u5230\u9700\u8981\u201c\u907f\u5acc\u201d\uff0c\u5c31\u66ff\u4e00\u4e2a\u53ea\u89c1\u8fc7\u4e00\u9762\u7684\u964c\u751f\u7537\u5b50\u8fa9\u62a4\u4e86\u3002\u4f46\u5979\u7684\u8fa9\u62a4\uff0c\u5374\u4e5f\u63d0\u4e0d \u51fa\u6709\u529b\u7684\u8bc1\u636e\u4f5c\u4e3a\u652f\u6301\uff0c\u56e0\u6b64\uff0c\u5728\u67f3\u4e09\u5a18\u60f3\u6765\uff0c\u5219\u8ba4\u4e3a\u5979\u662f\u4e3a\u4e86\u513f\u5973\u79c1\u60c5\u66ff\u674e\u601d\u5357\u201c\u66f2 \u8fa9\u201d\u3002 \u3000\u3000\u539f\u6765\u94c1\u8f6e\u592b\u5987\u662f\u5c60\u767e\u57ce\u7684\u624b\u4e0b\uff0c\u5c60\u767e\u57ce\u6b7b\u5f97\u4e0d\u660e\u4e0d\u767d\uff0c\u8fde\u4ec7\u4eba\u662f\u8c01\uff0c\u90fd\u4e0d\u77e5\u9053\u3002\u4ed6\u4eec\u592b \u59bb\u660e\u77e5\u6740\u6b7b\u5c60\u767e\u57ce\u7684\u4eba\u5fc5\u5b9a\u662f\u6b66\u529f\u9ad8\u660e\u4e4b\u58eb\uff0c\u53ea\u51ed\u4ed6\u4eec\u592b\u59bb\u51b3\u4e0d\u80fd\u4e3a\u8235\u4e3b\u62a5\u4ec7\uff0c\u4f46\u81f3\u5c11\u4e5f\u8981 \u67e5\u660e\u6740\u4ed6\u4eec\u8235\u4e3b\u7684\u4ec7\u4eba\u662f\u8c01\uff0c\u624d\u80fd\u56de\u53bb\u5411\u5f1f\u5144\u4eec\u4ea4\u4ee3\uff0c\u56e0\u6b64\u51b3\u610f\u7559\u5728\u8499\u53e4\u7ee7\u7eed\u4fa6\u5bdf\u3002\u5b5f\u5c11\u521a \u5219\u8981\u8d76\u56de\u6c5f\u5357\u62a5\u544a\u8499\u53e4\u4e4b\u884c\u6253\u542c\u5230\u7684\u865a\u5b9e\uff0c\u5bf9\u674e\u601d\u5357\u7684\u4e8b\u60c5\uff0c\u4ed6\u53ea\u80fd\u76f8\u4fe1\u5b8b\u94c1\u8f6e\u7684\u8bf4\u6cd5\uff0c\u81ea \u5df1\u5374\u662f\u65e0\u6687\u53bb\u67e5\u7a76\u4e2a\u6c34\u843d\u77f3\u51fa\u4e86\u3002 \u3000\u3000\u67f3\u4e09\u5a18\u7684\u4e08\u592b\u5bf9\u5979\u662f\u767e\u4f9d\u767e\u987a\u7684\uff0c\u5728\u5979\u7684\u5fc3\u76ee\u4e4b\u4e2d\uff0c\u5929\u4e0b\u7684\u7537\u5b50\u90fd\u5e94\u8bfb\u50cf\u5979\u4e08\u592b\u4e00\u6837\u7528 \u60c5\u4e13\u4e00\u624d\u5bf9\uff0c\u5979\u751f\u5e73\u6700\u6068\u7684\u5c31\u662f\u8d1f\u5fc3\u7537\u5b50\u3002\u65e2\u7136\u5979\u65e9\u5df2\u8ba4\u5b9a\u4e86\u674e\u601d\u5357\u662f\u5b5f\u660e\u971e\u7684\u60c5\u4eba\uff0c\u6545\u6b64 \u4e00\u542c\u8bf4\u674e\u601d\u5357\u53c8\u53e6\u6709\u59bb\u5b50\uff0c\u5c31\u4e0d\u7531\u5f97\u5927\u4e3a\u607c\u6012\uff0c\u6068\u4e0d\u5f97\u628a\u674e\u601d\u5357\u75db\u6253\u51e0\u5341\u767e\u97ad\uff01 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u67f3\u4e09\u5a18\u6253\u4e86\u4e00\u97ad\uff0c\u8ddf\u7740\u53c8\u662f\u4e00\u97ad\uff0c\u559d\u9053\uff1a\u201c\u8fd9\u4e00\u97ad\u6211\u662f\u66ff\u5b5f\u59d1\u5a18\u60e9\u7f5a \u4f60\u7684\uff01\u201d\u674e\u601d\u5357\u5df2\u7ecf\u88ab\u5979\u72e0\u72e0\u6253\u4e86\u4e00\u97ad\uff0c\u8fd9\u4e00\u97ad\u82e5\u518d\u6253\u7740\uff0c\u53ea\u6015\u4e0d\u6b7b\u4e5f\u53d7\u91cd\u4f24\u3002\u6768\u5a49\u5927\u60ca\u5931 \u8272\uff0c\u8fde\u5fd9\u51fa\u5251\u523a\u67f3\u4e09\u5a18\u7684\u80cc\u5fc3\u3002 \u3000\u3000\u8fd9\u4e00\u62db\u662f\u653b\u654c\u4e4b\u6240\u5fc5\u6551\uff0c\u67f3\u4e09\u5a18\u542c\u5f97\u80cc\u540e\u91d1\u5203\u5288\u98ce\u4e4b\u58f0\uff0c\u8fde\u5fd9\u4e00\u6446\u67f3\u8170\uff0c\u91d1\u83b2\u6b65\u6362\uff0c\u6768 \u5a49\u4e00\u5251\u523a\u7a7a\uff0c\u67f3\u4e09\u5a18\u5df2\u662f\u95ea\u8fc7\u4e00\u65c1\u3002\u5982\u6b64\u4e00\u6765\uff0c\u67f3\u4e09\u5a18\u6253\u674e\u601d\u5357\u7684\u90a3\u4e00\u97ad\uff0c\u4e5f\u5c31\u6ca1\u6709\u6253\u7740\u4ed6 \u7684\u8eab\u4f53\uff0c\u53ea\u662f\u628a\u4ed6\u7684\u8863\u88f3\u53c8\u6495\u53bb\u4e86\u4e00\u5e45\u3002 \u3000\u3000\u6768\u5a49\u5e76\u65e0\u6740\u4f24\u67f3\u4e09\u5a18\u4e4b\u5fc3\uff0c\u4f46\u5374\u6c14\u6068\u5979\u90a3\u6837\u72e0\u72e0\u5730\u97ad\u6253\u674e\u601d\u5357\uff0c\u662f\u4ee5\u4e00\u5251\u523a\u7a7a\uff0c\u8ddf\u8e2a\u5373 \u4e0a\uff0c\u4f7f\u51fa\u4e86\u4e00\u6d3e\u8fdb\u624b\u7684\u62db\u6570\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5373\u4f7f\u5931\u624b\u4f24\u4e86\u5979\uff0c\u6211\u4e5f\u987e\u4e0d\u5f97\u90a3\u8bb8\u591a\u4e86\uff0c\u8c01\u53eb\u5979 \u8981\u6740\u6211\u7684\u5357\u54e5\u3002\u201d \u3000\u3000\u67f3\u4e09\u5a18\u7ed9\u5979\u75be\u653b\u4e86\u5341\u6570\u62db\uff0c\u5fc3\u5934\u706b\u8d77\uff0c\u9a82\u9053\uff1a\u201c\u4f60\u8fd9\u81ed\u4e2b\u5934\uff0c\u771f\u662f\u4e0d\u77e5\u597d\u6b79\uff0c\u6211\u7ed9\u4f60\u51fa \u6c14\uff0c\u4f60\u53cd\u6765\u6253\u6211\uff01\u201d\u94f6\u97ad\u4e00\u6296\uff0c\u4f7f\u51fa\u4e86\u201c\u56de\u98ce\u626b\u67f3\u201d\u7684\u7edd\u6280\uff0c\u53ea\u542c\u788d\u547c\u547c\u98ce\u54cd\uff0c\u5377\u8d77\u4e86\u4e00\u56e2 \u97ad\u5f71\uff0c\u970d\u5730\u6253\u6765\u3002 \u3000\u3000\u6768\u5a49\u8fd0\u5251\u6311\u5979\u7684\u97ad\u68a2\uff0c\u51e0\u4e4e\u7ed9\u5979\u7684\u957f\u97ad\u7f20\u4e0a\u4e86\u624b\u8155\u3002\u5e78\u800c\u6768\u5a49\u8f7b\u529f\u4e0d\u5f31\uff0c\u8fc5\u901f\u6362\u62db\uff0c\u9752 \u94a2\u5251\u8fd9\u624d\u6ca1\u6709\u7ed9\u5979\u5377\u51fa\u4e86\u624b\u3002 \u3000\u3000\u4f46\u6768\u5a49\u7684\u672c\u9886\u867d\u7136\u4e0d\u5f31\uff0c\u67f3\u4e09\u5a18\u5374\u6bd4\u5979\u66f4\u80dc\u4e00\u7b79\uff0c\u4e34\u654c\u7684\u7ecf\u9a8c\u4e5f\u8981\u6bd4\u6768\u5a49\u4e30\u5bcc\u3002\u6768\u5a49\u521d \u65f6\u8fd8\u6015\u8bef\u4f24\u4e86\u5979\uff0c\u6b64\u65f6\u8fde\u62db\u67b6\u4e5f\u611f\u4e3a\u96be\uff0c\u767e\u5fd9\u4e2d\u53ea\u597d\u53eb\u9053\uff1a\u201c\u5357\u54e5\uff0c\u4f60\u8bf4\u8bdd\u5440\uff01\u201d\u8bf4\u8bdd\u7a0d\u7a0d \u5206\u5fc3\uff0c\u53c8\u9669\u4e9b\u7740\u4e86\u67f3\u4e09\u5a18\u7684\u4e00\u97ad\u3002\u201d \u3000\u3000\u5e78\u800c\u674e\u601d\u5357\u7684\u672c\u9886\u5728\u5b8b\u94c1\u8f6e\u4e4b\u4e0a\uff0c\u521d\u65f6\u4ed6\u4e3a\u4e86\u907f\u514d\u8bef\u4f1a\u52a0\u6df1\uff0c\u7740\u7740\u5fcd\u8ba9\uff0c\u7ed9\u5b8b\u94c1\u8f6e\u5f3a\u653b \u731b\u6253\uff0c\u903c\u5f97\u900f\u4e0d\u8fc7\u6c14\u6765\uff0c\u6b64\u65f6\u4ed6\u7740\u4e86\u4e00\u97ad\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6211\u82e5\u662f\u4e0d\u628a\u4ed6\u6740\u9000\uff0c\u600e\u80fd\u5206\u8fa9\uff1f\u201d \u4e8e\u662f\u4e00\u54ac\u7259\u6839\uff0c\u5fcd\u7740\u75bc\u75db\uff0c\u53cd\u5b88\u4e3a\u653b\u3002 \u3000\u3000\u5b8b\u94c1\u8f6e\u6012\u9053\uff1a\u201c\u597d\u5440\uff0c\u4f60\u8fd9\u5c0f\u5b50\uff0c\u4eca\u65e5\u4e0d\u662f\u4f60\u6b7b\uff0c\u4fbf\u662f\u6211\u4ea1\uff01\u201d\u8bdd\u867d\u5982\u6b64\uff0c\u4f46\u6709\u5fc3\u65e0 \u529b\uff0c\u8981\u60f3\u62fc\u547d\uff0c\u53cc\u8f6e\u4e5f\u6253\u4e0d\u7740\u674e\u601d\u5357\u3002\u4f46\u674e\u601d\u5357\u5728\u4ed6\u72c2\u653b\u4e4b\u4e0b\uff0c\u53c8\u4e0d\u80fd\u4f24\u4ed6\uff0c\u5e94\u4ed8\u5f97\u4e5f\u662f\u751a \u4e3a\u5403\u529b\u4e86\u3002 \u3000\u3000\u674e\u601d\u5357\u5b9e\u5728\u6c14\u4ed6\u4e0d\u8fc7\uff0c\u5fcd\u4e0d\u4f4f\u9a82\u9053\uff1a\u201c\u4f60\u8fd9\u8822\u6750\uff0c\u8c01\u548c\u4f60\u62fc\u547d\uff1f\u4f46\u4f60\u4e0d\u9000\uff0c\u6211\u7684\u5251\u53ef\u6ca1 \u957f\u7740\u773c\u775b\uff0c\u4f60\u6b7b\u4e86\u53ef\u522b\u6028\u6211\uff01\u201d\u5530\u5530\u5530\u4f7f\u51fa\u51cc\u5389\u975e\u5e38\u7684\u8fde\u73af\u593a\u547d\u5251\u6cd5\uff0c\u5b8b\u94c1\u8f6e\u77e5\u9053\u5389\u5bb3\uff0c\u6697 \u81ea\u60f3\u9053\uff1a\u201c\u539f\u6765\u8fd9\u5c0f\u5b50\u7684\u672c\u9886\u5982\u6b64\u4e86\u5f97\uff0c\u96be\u9053\u4ed6\u521a\u624d\u771f\u662f\u624b\u4e0b\u7559\u60c5\uff1f\u201d\u5fc3\u4e2d\u60ca\u7591\u4e0d\u5b9a\uff0c\u4e8e\u662f \u6682\u65f6\u653e\u5f03\u4e86\u201c\u62fc\u547d\u201d\u7684\u5ff5\u5934\uff0c\u8fde\u8fde\u540e\u9000\u3002 \u3000\u3000\u674e\u601d\u5357\u7f13\u8fc7\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u4eec\u4e3a\u4ec0\u4e48\u8981\u6740\u6211\uff0c\u4f60\u4eec\u4e00\u5b9a\u4ee5\u4e3a\u5728\u548c\u6797\u505a\u9791\u5b50\u7684\u5b98 \u7684\u90a3\u4e2a\u5978\u8d3c\u662f\u6211\u7684\u7236\u4eb2\u4e86\u3002\u6211\u544a\u8bc9\u4f60\uff0c\u90a3\u4e2a\u5978\u8d3c\u7684\u771f\u59d3\u540d\u662f\u4f59\u4e00\u4e2d\uff0c\u4ed6\u662f\u5192\u4e86\u6211\u7239\u7684\u540d\u5b57\u53bb \u535a\u53d6\u5bcc\u8d35\u529f\u540d\u7684\u3002\u6211\u7684\u7239\u7239\u662f\u9876\u5929\u7acb\u5730\u7684\u597d\u6c49\uff0c\u4ed6\u5df2\u7ecf\u7ed9\u4f59\u4e00\u4e2d\u5bb3\u6b7b\u4e86\u3002\u6211\u8bf4\u7684\u90fd\u662f\u771f\u8bdd\uff0c \u54fc\uff0c\u4f60\u4e3a\u4ec0\u4e48\u8fd8\u4e0d\u76f8\u4fe1\uff1f\u201d\u539f\u6765\u6765\u94c1\u8f6e\u542c\u4e86\u4ed6\u7684\u8bdd\uff0c\u867d\u7136\u60ca\u7591\u4e0d\u5b9a\uff0c\u5374\u8fd8\u4e0d\u6562\u5b8c\u5168\u76f8\u4fe1\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u5ffd\u89c1\u5c71\u4e0b\u5c18\u5934\u5927\u8d77\uff0c\u8e44\u58f0\u6025\u9aa4\uff0c\u5b9b\u5982\u66b4\u96e8\u3002\u5b8b\u94c1\u8f6e\u9762\u8272\u500f\u53d8\uff0c\u559d\u9053\uff1a\u201c\u8fd9\u4e9b \u662f\u4ec0\u4e48\u4eba\uff1f\u201d\u674e\u601d\u5357\u4e00\u6123\u9053\uff1a\u201c\u6211\u600e\u4e48\u77e5\u9053\uff1f\u201d\u56de\u5934\u671b\u65f6\uff0c\u53ea\u89c1\u4e00\u9a91\u9a6c\u5df2\u7ecf\u4e0a\u4e86\u5c71\u5c97\u3002\u5176\u4e2d \u6709\u4e24\u540d\u6b66\u58eb\uff0c\u674e\u601d\u5357\u4f9d\u7a00\u8bb0\u5f97\u662f\u5728\u72e9\u730e\u90a3\u5929\u89c1\u8fc7\u7684\uff0c\u5374\u53eb\u4e0d\u51fa\u4ed6\u4eec\u7684\u540d\u5b57\u3002 \u3000\u3000\u8fd9\u4e24\u4e2a\u8499\u53e4\u6b66\u571f\u53d1\u73b0\u4e86\u674e\u601d\u5357\u5728\u5c71\u4e0a\u548c\u5b8b\u94c1\u8f6e\u4ea4\u624b\uff0c\u9887\u662f\u60ca\u5947\uff0c\u9f50\u58f0\u53eb\u9053\uff1a\u201c\u674e\u516c\u5b50\uff0c \u4f60\u4e5f\u6765\u4e86\uff01\u201d\u201c\u54ce\u5440\uff0c\u8d3c\u4eba\u8981\u9003\uff0c\u5feb\u8ffd\u4e0a\u53bb\uff01\u201d \u3000\u3000\u539f\u6765\u8fd9\u4e24\u4e2a\u8499\u53e4\u6b66\u571f\u4e43\u662f\u6728\u534e\u9ece\u7684\u624b\u4e0b\uff0c\u5728\u72e9\u730e\u7684\u7b2c\u4e8c\u5929\uff0c\u6728\u534e\u9ece\u6d3e\u9063\u4ed6\u4eec\u548c\u795e\u7ffc\u8425\u7684 \u53e6\u4e94\u540d\u6b66\u571f\uff0c\u4e00\u5171\u4e03\u4eba\u53bb\u641c\u67e5\u5c60\u767e\u57ce\u7684\u515a\u7fbd\u3002\u4ed6\u4eec\u77e5\u9053\u674e\u601d\u5357\u751a\u5f97\u4ed6\u4eec\u5927\u6c57\u7684\u6069\u5ba0\uff0c\u5374\u4e0d\u77e5 \u9053\u674e\u601d\u5357\u540e\u6765\u9003\u51fa\u548c\u6797\uff0c\u4f24\u4e86\u54f2\u522b\u7b49\u7b49\u60c5\u4e8b\u3002\u5982\u4eca\u4ed6\u4eec\u770b\u89c1\u674e\u601d\u5357\u548c\u5b8b\u94c1\u8f6e\u4ea4\u624b\uff0c\u53ea\u9053\u674e\u601d \u5357\u5df2\u7ecf\u6388\u53d7\u4e86\u201c\u91d1\u5e10\u6b66\u58eb\u201d\u7684\u5c01\u53f7\uff0c\u5949\u547d\u6765\u641c\u6355\u7591\u72af\u7684\u3002 \u3000\u3000\u5b8b\u94c1\u8f6e\u53c8\u60ca\u53c8\u6012\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u7f16\u5f97\u597d\u4e00\u5957\u8c0e\u8bdd\uff0c\u73b0\u5728\u4f60\u8fd8\u6709\u4ec0\u4e48\u8bdd\u8bf4\uff1f\u8001\u5b50\u4e0e \u4f60\u62fc\u4e86\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u865a\u6643\u4e00\u62db\uff0c\u56de\u8eab\u4fbf\u8dd1\uff0c\u90a3\u4e24\u4e2a\u6b66\u58eb\u8fde\u5fd9\u98de\u5954\u4e0a\u53bb\uff0c\u53eb\u9053\uff0c\u201c\u516c\u5b50\u522b\u614c\uff0c\u6211\u6765\u52a9 \u4f60\uff01\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u674e\u601d\u5357\u7a81\u7136\u5530\u5530\u4e24\u5251\uff0c\u75be\u5982\u95ea\u7535\uff0c\u8fd9\u4e24\u4e2a\u6b66\u58eb\u672c\u9886\u5176\u5b9e\u4e0d\u5dee\uff0c\u4f46\u5374\u505a\u68a6\u4e5f \u60f3\u4e0d\u5230\u674e\u601d\u5357\u4f1a\u7a81\u7136\u5411\u4ed6\u4eec\u65bd\u5c55\u6740\u624b\uff0c\u51b7\u4e0d\u53ca\u9632\uff0c\u53ea\u89c1\u5251\u5149\u8fc7\u5904\uff0c\u8840\u82b1\u98de\u6e85\uff0c\u4e00\u4e2a\u7ed9\u523a\u7a7f\u4e86 \u5589\u5499\uff0c\u767b\u65f6\u6b7b\u6389\uff0c\u4e00\u4e2a\u7ed9\u524a\u65ad\u4e24\u6839\u808b\u9aa8\uff0c\u53d7\u4f24\u4ea6\u662f\u4e0d\u8f7b\uff01 \u3000\u3000\u5b8b\u94c1\u8f6e\u53cc\u8f6e\u6b63\u8981\u671d\u674e\u601d\u5357\u5288\u4e0b\uff0c\u5e78\u800c\u674e\u601d\u5357\u51fa\u624b\u5f97\u5feb\uff0c\u5df2\u5148\u6740\u4f24\u4e86\u4e24\u540d\u8499\u53e4\u6b66\u58eb\uff0c\u7528\u4e8b \u5b9e\u8bc1\u660e\u4e86\u4ed6\u7684\u53d7\u51a4\u3002\u5b8b\u94c1\u8f6e\u5927\u5403\u4e00\u60ca\uff0c\u8fde\u5fd9\u628a\u53cc\u8f6e\u715e\u4f4f\u3002\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u90a3\u4e94\u540d\u536b\u58eb\u5df2\u7ecf \u4e00\u62e5\u800c\u4e0a\uff0c\u5305\u56f4\u4e86\u4ed6\u4eec\u3002 \u3000\u3000\u674e\u601d\u5357\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u5feb\u8dd1\uff0c\u8fd9\u51e0\u4e2a\u4eba\u6211\u4eec\u592b\u59bb\u5bf9\u4ed8\u5f97\u4e86\u3002\u201d\u5b8b\u94c1\u8f6e\u53eb\u9053\uff1a\u201c\u6211\u3001\u6211\u8be5 \u6b7b\u3002\u201d\u500f\u5730\u4e00\u58f0\u5927\u543c\uff0c\u53cc\u8f6e\u76d8\u65cb\u98de\u821e\uff0c\u51f6\u795e\u6076\u715e\u822c\u5730\u5411\u90a3\u540d\u6b66\u58eb\u6253\u53bb\u3002\u90a3\u540d\u6b66\u58eb\u4f7f\u94c1\u67aa\u4e00 \u67b6\uff0c\u201c\u94db\u201d\u7684\u4e00\u58f0\uff0c\u94c1\u67aa\u53d8\u66f2\uff0c\u864e\u53e3\u6d41\u8840\uff0c\u4f46\u5b8b\u94c1\u8f6e\u7684\u80a9\u8180\u4e5f\u7ed9\u4ed6\u7684\u67aa\u5c16\u6843\u7834\u4e86\u4e00\u5c42\u76ae\u8089\u3002 \u5b8b\u94c1\u8f6e\u4e0d\u987e\u75bc\u75db\uff0c\u53cc\u8f6e\u4e00\u9f50\u538b\u4e0b\uff0c\u628a\u90a3\u540d\u6b66\u58eb\u6253\u6210\u4e86\u4e00\u56e2\u8089\u997c\u3002 \u3000\u3000\u67f3\u4e09\u5a18\u3001\u6768\u5a49\u540c\u65f6\u6765\u5230\u3002\u67f3\u4e09\u5a18\u957f\u97ad\u4e00\u5377\uff0c\u628a\u4e00\u540d\u6b66\u58eb\u56f4\u8170\u5377\u4e86\u8fc7\u6765\uff0c\u731b\u5730\u4e00\u6325\uff0c\u53c8\u628a \u4ed6\u6454\u4e86\u51fa\u53bb\uff0c\u78b0\u7ffb\u4e86\u53e6\u4e00\u4e2a\u6b66\u58eb\u3002\u6768\u5a49\u8ddf\u7740\u8865\u4e0a\u4e24\u5251\uff0c\u6740\u4e86\u4ed6\u4eec\uff0c\u7d27\u63a5\u7740\u4e00\u62db\u201c\u7389\u5973\u6295 \u68ad\u201d\uff0c\u53c8\u628a\u808b\u9aa8\u53d7\u4f24\u7684\u90a3\u540d\u6b66\u58eb\u6740\u4e86\u3002 \u3000\u3000\u4e03\u540d\u6b66\u58eb\u6b7b\u4e86\u4e94\u540d\uff0c\u5269\u4e0b\u7684\u4e24\u4e2a\u6b66\u58eb\u53ea\u6068\u7239\u5a18\u751f\u5c11\u4e86\u4e24\u6761\u817f\uff0c\u8fde\u5fd9\u9003\u8dd1\u3002\u674e\u601d\u5357\u548c\u6768\u5a49 \u7d27\u8ffd\u4e0d\u820d\uff0c\u90a3\u4e24\u4eba\u672a\u66fe\u9003\u5230\u5c71\u4e0b\uff0c\u4fbf\u7ed9\u4ed6\u4eec\u8ffd\u4e0a\u3002 \u3000\u3000\u5b8b\u94c1\u8f6e\u9053\uff1a\u201c\u4e09\u5a18\uff0c\u770b\u6765\u54b1\u4eec\u51a4\u6789\u4ed6\u4e86\uff0c\u600e\u4e48\u6837\u597d\u5462\uff1f\u201d\u67f3\u4e09\u5a18\u5148\u4e0d\u4f5c\u58f0\uff0c\u4e00\u626c\u624b\u5374\u628a \u4e24\u67c4\u6bd2\u9f99\u9525\u6253\u4e86\u4e0b\u53bb\u3002\u6b63\u662f\uff1a \u3000\u3000\u7eb5\u6709\u6d6e\u4e91\u80fd\u63a9\u65e5\uff0c\u9634\u9706\u8fc7\u540e\u662f\u6e05\u660e\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b\u2014\u2014\u7b2c\u4e5d\u56de\u3000\u5fcd\u542c\u60b2\u6b4c\u7ea2\u8896\u6e7f\u3000\u6301\u4e3a\u4fe1\u7269\u7ee3\u5dfe\u9999 \u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b \u7b2c\u4e5d\u56de\u3000\u5fcd\u542c\u60b2\u6b4c\u7ea2\u8896\u6e7f\u3000\u6301\u4e3a\u4fe1\u7269\u7ee3\u5dfe\u9999 \u3000\u3000\u8fd9\u4e24\u67c4\u6bd2\u9f99\u9525\u6253\u5f97\u6781\u51c6\uff0c\u53ea\u542c\u5f97\u4e24\u58f0\u88c2\u4eba\u5fc3\u80ba\u7684\u5389\u53eb\uff0c\u90a3\u4e24\u4e2a\u6b66\u58eb\u5df2\u662f\u5404\u81ea\u4e2d\u4e86\u4e00\u67c4\u98de \u9525\uff0c\u9aa8\u788c\u788c\u5730\u6eda\u4e0b\u5c71\u5761\u4e86\u3002 \u3000\u3000\u67f3\u4e09\u5a18\u66ff\u674e\u601d\u5357\u6740\u4e86\u90a3\u4e24\u4e2a\u6b66\u571f\uff0c\u4fbf\u5373\u626c\u58f0\u8bf4\u9053\uff1a\u201c\u6c5f\u6e56\u4e0a\u8bb2\u7a76\u7684\u662f\u6069\u6028\u5206\u660e\uff0c\u4f60\u4eca\u65e5 \u62d4\u5251\u76f8\u52a9\u4e4b\u5fb7\uff0c\u6211\u4eec\u592b\u59bb\u662f\u4e0d\u4f1a\u5fd8\u8bb0\u4f60\u7684\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u7b11\u9053\uff1a\u201c\u8fd9\u4f19\u8d3c\u4eba\u662f\u54b1\u4eec\u5408\u529b\u6740\u7684\uff0c\u8c01\u4e5f\u4e0d\u7528\u9886\u8c01\u7684\u60c5\u3002\u62a5\u7b54\u662f\u65e0\u9700\u4e86\uff0c\u4f46\u613f\u4f60 \u4eec\u5bf9\u6211\u4e0d\u518d\u8bef\u4f1a\u5c31\u597d\u3002\u201d \u3000\u3000\u67f3\u4e09\u5a18\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u4e8b\u60c5\u662f\u771f\u662f\u5047\uff0c\u603b\u6709\u6c34\u843d\u77f3\u51fa\u4e4b\u65f6\uff0c\u5018\u82e5\u6211\u4eec\u67e5\u660e \u4e86\u771f\u76f8\uff0c\u679c\u7136\u5982\u4f60\u6240\u8a00\u7684\u8bdd\uff0c\u6211\u4e00\u5b9a\u548c\u6211\u7684\u5f53\u5bb6\u6765\u627e\u4f60\u8d54\u7f6a\uff0c\u4f46\u6211\u67f3\u4e09\u5a18\u5e73\u751f\u6700\u6068\u8d1f\u5fc3\u6c49 \u5b50\u3002\u5373\u4f7f\u5f53\u771f\u662f\u51a4\u6789\u4e86\u4f60\uff0c\u8d4e\u7f6a\u4e4b\u540e\uff0c\u6211\u4e5f\u8fd8\u8981\u6253\u4f60\u4e09\u97ad\uff01\u201d\u67f3\u4e09\u5a18\u8bf4\u4e86\u8fd9\u756a\u8bf4\u8bdd\uff0c\u592b\u59bb\u4fe9 \u8de8\u4e0a\u5750\u9a91\u4ece\u5c71\u7684\u90a3\u8fb9\u8d70\u4e86\u3002 \u3000\u3000\u542c\u67f3\u4e09\u5a18\u8fd9\u756a\u8a00\u8bed\uff0c\u5979\u5df2\u6709\u4e03\u516b\u5206\u76f8\u4fe1\u674e\u601d\u5357\u7684\u8bf4\u8bdd\uff0c\u4e1a\u5df2\u51c6\u5907\u5411\u4ed6\u8d54\u7f6a\u7684\u4e86\u3002\u53ef\u662f\u5979 \u5bf9\u674e\u601d\u5357\u4e0e\u5b5f\u660e\u971e\u4e4b\u4e8b\uff0c\u4ecd\u662f\u6709\u7740\u6df1\u6df1\u7684\u8bef\u4f1a\uff0c\u8ba4\u5b9a\u4e86\u4ed6\u662f\u201c\u8d1f\u5fc3\u6c49\u5b50\u201d\uff0c\u56e0\u6b64\u8981\u4e3a\u5b5f\u660e\u971e \u4ee3\u62b1\u4e0d\u5e73\u3002\u674e\u601d\u5357\u542c\u4e86\u5979\u8fd9\u756a\u8bf4\u8bdd\uff0c\u4e0d\u7531\u5f97\u557c\u7b11\u7686\u975e\u3002 \u3000\u3000\u5b8b\u94c1\u8f6e\u592b\u59bb\u8d70\u540e\uff0c\u6768\u5a49\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u5357\u54e5\uff0c\u90a3\u4f4d\u5b5f\u59d1\u5a18\u662f\u3001\u662f\u600e\u4e48\u4e00\u56de\u4e8b\u60c5\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u6211\u521a\u624d\u60f3\u8981\u544a\u8bc9\u4f60\u5c31\u6b63\u662f\u8fd9\u4ef6\u4e8b\u60c5\u3002\u4ed6\u4eec\u7236\u5973\u4e00\u4e8e\u6211\u66fe\u6709\u6551\u547d\u4e4b\u6069\uff0c\u53ef\u662f \u6211\u548c\u5979\u53ea\u4e0d\u8fc7\u4e00\u9762\u4e4b\u4ea4\uff0c\u6839\u672c\u5c31\u8c08\u4e0d\u4e0a\u4ec0\u4e48\u2018\u8d1f\u5fc3\u2019\u4e0d\u2018\u8d1f\u5fc3\u2019\u3002\u90a3\u5a46\u5a18\u75af\u75af\u766b\u766b\u5730\u80e1\u8a00\u4e71 \u8bed\uff0c\u4f60\u53ef\u4e0d\u8981\u5fc3\u91cc\u5b58\u4e86\u82a5\u8482\u3002\u201d \u3000\u3000\u5f53\u4e0b\u674e\u601d\u5357\u628a\u90a3\u65e5\u4e0e\u5b5f\u5c11\u521a\u3001\u5b5f\u660e\u971e\u7236\u5973\u76f8\u9047\u7684\u60c5\u8282\uff0c\u4e00\u4e00\u544a\u8bc9\u4e86\u6768\u5a49\uff0c\u4f46\u5374\u7792\u8fc7\u4e86\u5b5f \u660e\u971e\u5728\u6c99\u4e0a\u7559\u5b57\u529d\u52c9\u4e8e\u4ed6\u4e4b\u4e8b\u3002 \u3000\u3000\u6768\u5a49\u7b11\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u8fd9\u4f4d\u5b5f\u59d1\u5a18\u4e5f\u7b97\u5f97\u662f\u4f60\u7684\u7ea2\u989c\u77e5\u5df1\u5462\uff01\u96be\u5f97\u5979\u53ea\u89c1\u4f60\u4e00\u9762\uff0c\u5c31 \u5982\u6b64\u76f8\u4fe1\u4f60\uff0c\u5979\u7684\u7236\u4eb2\u4ece\u547c\u9ece\u5962\u624b\u4e0b\u6551\u4e86\u4f60\u7684\u4e00\u547d\uff1b\u5979\u5219\u4ece\u5979\u7236\u4eb2\u7684\u5251\u4e0b\u6551\u4e86\u4f60\u7684\u4e00\u547d\u3002\u4f60 \u662f\u5e94\u8be5\u597d\u597d\u5730\u591a\u8c22\u8fd9\u4f4d\u7ea2\u989c\u77e5\u5df1\u624d\u5bf9\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u6da8\u7ea2\u4e86\u8138\uff0c\u8bf4\u9053\uff1a\u201c\u5a49\u59b9\uff0c\u4f60\u4e0d\u8981\u53d6\u7b11\u6211\u597d\u4e0d\u597d\uff1f\u54b1\u4eec\u5982\u4eca\u662f\u76f8\u4f9d\u4e3a\u547d\uff0c\u592b\u59bb\u7684 \u60c5\u8c0a\u662f\u7528\u9c9c\u8840\u917f\u6210\u7684\uff0c\u8fd9\u6bd4\u4efb\u4f55\u77e5\u5df1\u4e4b\u60c5\u90fd\u8981\u6d53\u4e86\u5341\u500d\u767e\u500d\u5427\uff01\u96be\u9053\u4f60\u3001\u4f60\u8fd8\u4e0d\u76f8\u4fe1\u6211 \u4e48\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u8bf4\u5f97\u6781\u662f\u52a8\u60c5\uff0c\u542c\u5f97\u51fa\u662f\u4ece\u80ba\u8151\u4e2d\u638f\u51fa\u6765\u7684\u8bf4\u8bdd\uff0c\u6768\u5a49\u5fc3\u91cc\u53c8\u9178\u53c8\u751c\uff0c\u62b9\u4e86\u62b9\u773c \u89d2\u7684\u6cea\u73e0\uff0c\u7b11\u9053\uff1a\u201c\u5357\u54e5\uff0c\u6211\u53ea\u4e0d\u8fc7\u8bf4\u4e86\u51e0\u53e5\uff0c\u4f60\u5c31\u6025\u6210\u8fd9\u4f6f\u3002\u5176\u5b9e\u6211\u8bf4\u7684\u4e5f\u662f\u6b63\u7ecf\u8bdd\uff0c\u4eba \u5bb6\u4e8e\u4f60\u6709\u6069\uff0c\u6211\u4e5f\u8be5\u591a\u8c22\u5979\u7684\u3002\u597d\u4e86\uff0c\u597d\u4e86\uff0c\u4f60\u653e\u5fc3\u5427\uff0c\u4f60\u4f8d\u6211\u597d\uff0c\u96be\u9053\u6211\u8fd8\u4e0d\u77e5\u9053\u5417\uff1f\u67f3 \u4e09\u5a18\u90a3\u4e9b\u98ce\u8a00\u98ce\u8bed\uff0c\u6211\u53ea\u5f53\u6ca1\u6709\u542c\u8fc7\u3002\u201d\u8bdd\u867d\u5982\u6b64\uff0c\u6768\u5a49\u7684\u5fc3\u4e0a\u5374\u4e5f\u96be\u514d\u6709\u4e86\u4e00\u4e1d\u9634\u5f71\u3002 \u3000\u3000\u5f53\u4e00\u4e2a\u5973\u5b69\u5b50\u5f00\u59cb\u9677\u5165\u7231\u60c5\u7684\u65f6\u5019\uff0c\u5979\u5bf9\u5979\u6240\u7231\u7684\u4eba\uff0c\u611f\u89c9\u662f\u975e\u5e38\u654f\u9510\u3001\u975e\u5e38\u5fae\u5999\u7684\uff0c \u54ea\u6015\u53ea\u662f\u4e00\u4e2a\u773c\u8272\uff0c\u4e00\u53e5\u8bf4\u8bdd\uff0c\u751a\u6216\u53ea\u662f\u51ed\u7740\u76f4\u89c9\uff0c\u5979\u90fd\u53ef\u80fd\u7aa5\u63a2\u51fa\u4ed6\u5185\u5fc3\u7684\u79d8\u5bc6\u3002 \u3000\u3000\u6768\u5a49\u5982\u4eca\u5df2\u7ecf\u77e5\u9053\uff0c\u5728\u5979\u4e4b\u524d\uff0c\u5979\u7684\u4e08\u592b\u66fe\u7ecf\u548c\u4e24\u4e2a\u5973\u5b50\u6709\u8fc7\u9887\u4e0d\u5e73\u51e1\u7684\u4ea4\u60c5\uff0c\u4e00\u4e2a\u662f \u660e\u6167\u516c\u4e3b\uff0c\u4e00\u4e2a\u662f\u5b5f\u660e\u971e\u3002\u660e\u6167\u516c\u4e3b\u662f\u5979\u89c1\u8fc7\u7684\uff0c\u90a3\u4e00\u665a\u7684\u5e7d\u8c37\u4e4b\u4f1a\uff0c\u660e\u6167\u516c\u4e3b\u5bf9\u5979\u4e08\u592b\u7684 \u7231\u610f\u8868\u9732\u65e0\u9057\u3002\u6768\u5a49\u5168\u90fd\u770b\u5728\u773c\u91cc\uff0c\u53ef\u662f\u4e8b\u60c5\u4e00\u8fc7\uff0c\u6768\u5a49\u5bf9\u5979\u5374\u662f\u4e1d\u6beb\u4e5f\u6ca1\u6709\u5992\u5fcc\u4e4b\u611f\uff0c\u76f8 \u53cd\uff0c\u5979\u4ece\u672a\u89c1\u8fc7\uff0c\u4e5f\u4e0d\u77e5\u9053\u662f\u5426\u5bf9\u5979\u4e08\u592b\u6709\u7740\u60c5\u610f\u7684\u5b5f\u660e\u971e\uff0c\u5374\u5728\u5979\u7684\u5fc3\u5934\u62b9\u4e0b\u4e86\u9634\u5f71\uff0c\u5f15 \u8d77\u4e86\u5979\u7684\u7591\u60e7\u3002\u8fd9\u662f\u4e3a\u4ec0\u4e48\u5462\uff1f \u3000\u3000\u56e0\u4e3a\u6768\u5a49\u53ef\u4ee5\u6e05\u5f7b\u5730\u611f\u89c9\u5f97\u5230\uff0c\u8fd9\u4e24\u4e2a\u5973\u5b50\u5728\u674e\u601d\u5357\u5fc3\u4e2d\u6240\u5360\u7684\u4f4d\u7f6e\u5927\u4e0d\u76f8\u540c\uff01\u674e\u601d\u5357 \u5bf9\u660e\u6167\u516c\u4e3b\u7684\u4e8b\u60c5\u53ef\u4ee5\u5766\u7136\u7f6e\u4e4b\uff0c\u5411\u5979\u89e3\u91ca\u4e4b\u65f6\u4e5f\u4e1d\u6beb\u4e0d\u542b\u6127\u4f5c\u4e4b\u8272\u3002\u4f46\u662f\u5bf9\u4e8e\u5b5f\u660e\u971e\uff0c\u4ed6 \u5374\u8981\u6025\u4e8e\u5206\u8fa9\u3001\u597d\u50cf\u751f\u6015\u6768\u5a49\u8d77\u7591\u3002\u53ea\u4ece\u8fd9\u4e00\u70b9\uff0c\u6768\u5a49\u5df2\u7ecf\u53ef\u4ee5\u4f53\u4f1a\u5f97\u5230\uff0c\u5373\u4f7f\u4ed6\u4eec\u8fd8\u4e0d\u662f \u60c5\u4fa3\uff0c\u81f3\u5c11\u5728\u674e\u601d\u5357\u7684\u5fc3\u91cc\u5bf9\u5b5f\u660e\u971e\u5df2\u662f\u65e9\u5c31\u6709\u4e86\u5f02\u6837\u7684\u611f\u60c5\uff01 \u3000\u3000\u6768\u5a49\u4e0d\u7981\u60f3\u5230\u90a3\u665a\u674e\u5e0c\u6d69\u4e34\u7ec8\u4e4b\u65f6\uff0c\u8981\u513f\u5b50\u5a36\u5979\u4e3a\u59bb\u4e4b\u4e8b\u3002\u201c\u90a3\u65f6\uff0c\u5357\u54e5\u518d\u4e09\u63a8\u642a\uff0c\u867d \u7136\u4ed6\u540e\u6765\u8bf4\u51fa\u7406\u7531\uff0c\u8bf4\u662f\u4e0d\u613f\u8fde\u7d2f\u4e8e\u6211\uff0c\u4f46\u600e\u77e5\u4ed6\u4e0d\u662f\u4e3a\u7740\u8fd9\u4f4d\u5b5f\u59d1\u5a18\u7684\u7f18\u6545\u5462\uff1f\u201d\u518d\u53c8\u60f3 \u9053\uff1a\u201c\u5b5f\u5c11\u521a\u662f\u5929\u4e0b\u77e5\u540d\u7684\u5927\u4fa0\uff0c\u5357\u54e5\u66fe\u53d7\u8fc7\u4ed6\u4eec\u7236\u5973\u6551\u547d\u4e4b\u6069\uff0c\u8fd9\u4ef6\u4e8b\u4e3a\u4f55\u8fdf\u81f3\u4eca\u5929\u624d\u548c \u6211\u8bf4\uff1f\u201d\u5fc3\u4e2d\u7684\u7591\u8651\u66f4\u52a0\u91cd\u4e86\u3002 \u3000\u3000\u8fd9\u4e5f\u602a\u4e0d\u5f97\u6768\u5a49\u591a\u7591\u5584\u5992\uff0c\u5176\u5b9e\u6768\u5a49\u5e76\u4e0d\u662f\u6c14\u91cf\u72ed\u7a84\u7684\u5973\u4eba\uff0c\u53ea\u56e0\u5979\u76ee\u524d\u7684\u5904\u5883\uff0c\u9664\u4e86 \u674e\u601d\u5357\u4e4b\u5916\uff0c\u5979\u5df2\u662f\u6ca1\u6709\u4e00\u4e2a\u4eb2\u4eba\u3002\u4ed6\u4eec\u7684\u547d\u8fd0\u65e9\u5df2\u8054\u7ed3\u5728\u4e00\u8d77\uff0c\u800c\u4e14\u5979\u4e5f\u6df1\u6df1\u5730\u7231\u4e0a\u674e\u601d \u5357\u4e86\uff0c\u5982\u4eca\u5979\u77e5\u9053\u674e\u601d\u5357\u5fc3\u4e2d\u8fd8\u6709\u53e6\u4e00\u4e2a\u5973\u5b50\uff0c\u5979\u7684\u5fc3\u5934\u53c8\u600e\u80fd\u4e0d\u8499\u4e0a\u4e00\u5c42\u9634\u5f71\u3002 \u3000\u3000\u4e0d\u8fc7\u6768\u5a49\u867d\u662f\u6709\u6240\u7591\u8651\uff0c\u4f46\u5979\u4e5f\u611f\u89c9\u5f97\u5230\uff0c\u674e\u601d\u5357\u5bf9\u5979\u662f\u65e5\u76ca\u4eb2\u8fd1\uff0c\u8d77\u521d\u6216\u8bb8\u6709\u70b9\u52c9 \u5f3a\uff0c\u73b0\u5728\u5219\u7684\u786e\u662f\u628a\u5979\u5f53\u4f5c\u672a\u5a5a\u59bb\u5b50\u770b\u5f85\u4e86\u3002\u201c\u5357\u54e5\u5bf9\u6211\u51b3\u975e\u5047\u6025\u865a\u60c5\uff01\u201d\u8fd9\u4e00\u70b9\u6768\u5a49\u4e5f\u662f \u53ef\u4ee5\u80af\u5b9a\u7684\u3002 \u3000\u3000\u6768\u5a49\u5ffd\u5730\u6709\u4e86\u4e2a\u5947\u602a\u7684\u60f3\u6cd5\uff1a\u201c\u4f46\u613f\u5357\u54e5\u65e9\u65e5\u548c\u90a3\u4f4d\u5b5f\u59d1\u5a18\u76f8\u89c1\uff0c\u90a3\u65f6\u6211\u5c31\u77e5\u9053\u4ed6\u771f\u6b63 \u7231\u7684\u662f\u8c01\u4e86\u3002\u6211\u53ef\u4e0d\u613f\u53ea\u662f\u51ed\u7740\u592b\u59bb\u7684\u540d\u5206\u628a\u4ed6\u7f1a\u7740\uff0c\u4ee4\u4ed6\u75db\u82e6\u4e00\u751f\u3002\u201d\u6768\u5a49\u662f\u5927\u5bb6\u95fa\u79c0\uff0c \u5c3d\u7ba1\u4ed6\u4eec\u4e8c\u4eba\u540d\u5206\u5df2\u8d70\uff0c\u5979\u5bf9\u674e\u601d\u5357\u7684\u611f\u60c5\u8fd8\u662f\u76f8\u5f53\u542b\u84c4\u7684\uff0c\u5979\u7684\u8fd9\u4e9b\u60f3\u6cd5\uff0c\u5f53\u7136\u4e5f\u4e0d\u4f1a\u5411 \u674e\u601d\u5357\u5410\u9732\u3002\u4ed6\u4eec\u8fd8\u672a\u8d70\u51fa\u8499\u53e4\u56fd\u5883\uff0c\u968f\u65f6\u8fd8\u53ef\u80fd\u6709\u8ffd\u5175\u6765\u5230\uff0c\u4e8e\u662f\u5728\u4ed6\u4eec\u6740\u4e86\u90a3\u4e03\u4e2a\u795e\u7ffc \u8425\u7684\u6b66\u58eb\u4e4b\u540e\uff0c\u4fbf\u5373\u7ee7\u7eed\u8d77\u8def\u3002\u4e00\u8def\u4e0a\u674e\u601d\u5357\u5bf9\u5979\u7167\u987e\u5f97\u5341\u5206\u5468\u5230\uff0c\u5728\u4ed6\u7684\u9f13\u821e\u4e0e\u7167\u6599\u4e4b \u4e0b\uff0c\u6768\u5a49\u624d\u6709\u52c7\u6c14\u8d70\u8fc7\u4e86\u5e7f\u9614\u7684\u6208\u58c1\uff0c\u5979\u5fc3\u5934\u7684\u90a3\u62b9\u9634\u5f71\uff0c\u4e5f\u597d\u50cf\u7ed9\u6208\u58c1\u4e0a\u71c3\u70e7\u7684\u592a\u9633\u70e7\u7740 \u4e86\u3002 \u3000\u3000\u8fd9\u4e00\u65e5\u4ed6\u4eec\u5728\u8349\u539f\u4e0a\u7b56\u9a6c\u800c\u884c\uff0c\u53d1\u73b0\u5730\u4e0a\u6709\u65e0\u6570\u51cc\u4e71\u7684\u9a6c\u8e44\u8db3\u5370\uff0c\u674e\u601d\u5357\u5fc3\u5934\u4e00\u51db\uff0c\u8bf4 \u9053\uff1a\u201c\u770b\u8fd9\u60c5\u5f62\uff0c\u8499\u53e4\u7684\u5927\u519b\u8fc7\u4e86\u672a\u4e45\u3002\u53ef\u80fd\u5c31\u662f\u6628\u5929\u7ecf\u8fc7\u8fd9\u91cc\u7684\u3002\u201d \u3000\u3000\u6768\u5a49\u8bf4\u9053\uff1a\u201c\u6210\u5409\u601d\u6c57\u4e0d\u662f\u8fd8\u5728\u548c\u6797\u4e48\uff1f\u201d\u674e\u601d\u5357\u9053\uff1a\u201c\u65e9\u5728\u4e00\u4e2a\u591a\u6708\u4e4b\u524d\uff0c\u6210\u5409\u601d\u6c57 \u5df2\u7ecf\u4e0b\u4ee4\u4f10\u91d1\uff0c\u4ece\u8499\u53e4\u5404\u90e8\u5f81\u8c03\u6765\u7684\u5175\u58eb\uff0c\u4e5f\u90fd\u7eb7\u7eb7\u5230\u8fb9\u5883\u805a\u96c6\u4e86\u3002\u8fd9\u60f3\u5fc5\u662f\u4ed6\u4eec\u8fdb\u4fb5\u91d1\u56fd \u7684\u5148\u5934\u90e8\u961f\uff0c\u54b1\u4eec\u7684\u9a6c\u5feb\uff0c\u5728\u8fd9\u91cc\u8d76\u4e0a\u4e86\u3002\u4e00\u8def\u4e0a\u6ca1\u89c1\u8e44\u75d5\uff0c\u90a3\u662f\u56e0\u4e3a\u5728\u6c99\u6f20\u4e0a\u7559\u4e0b\u7684\u9a6c\u8e44 \u8db3\u5370\uff0c\u5f88\u5feb\u5c31\u7ed9\u98ce\u6c99\u63a9\u6ca1\u7684\u7f18\u6545\u3002\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u660e\u6167\u516c\u4e3b\u90a3\u9762\u91d1\u724c\uff0c\u4e0d\u77e5\u80fd\u5426\u5728\u519b\u4e2d\u901a\u884c\u65e0\u963b\uff1f\u201d \u3000\u3000\u674e\u6069\u5357\u9053\uff1a\u201c\u5018\u82e5\u78b0\u4e0a\u5927\u519b\uff0c\u53ea\u6015\u603b\u662f\u6709\u4e9b\u4e0d\u4fbf\u7684\u4e86\u3002\u201d\u8981\u77e5\u519b\u4e2d\u7684\u5c06\u9886\u4e0d\u6bd4\u5173\u5361\u7684\u54e8 \u5b98\uff0c\u54e8\u5b98\u54c1\u4f4d\u5351\u5fae\uff0c\u4e00\u89c1\u91d1\u5e10\u4ee4\u724c\uff0c\u591a\u534a\u662f\u7ed9\u5413\u5f97\u95ee\u4e5f\u4e0d\u6562\u591a\u95f4\uff0c\u519b\u4e2d\u7684\u5c06\u9886\u5374\u662f\u5728\u6210\u5409\u601d \u6c57\u9762\u524d\u8bf4\u5f97\u8bdd\u7684\u4eba\uff0c\u4ed6\u4eec\u5f53\u7136\u77e5\u9053\u8fd9\u91d1\u5e10\u4ee4\u724c\u662f\u4e0d\u4f1a\u8f7b\u6613\u7ed9\u4e00\u4e2a\u6c49\u4eba\u7684\u3002\u5373\u4f7f\u4ed6\u4eec\u5728\u672a\u660e\u5e95 \u7ec6\u4e4b\u524d\uff0c\u4e0d\u6562\u600e\u6837\uff0c\u4f46\u81f3\u5c11\u4e5f\u4f1a\u52a0\u4ee5\u76d8\u67e5\uff0c\u751a\u6216\u501f\u6545\u7559\u96be\uff0c\u4ee5\u5f85\u771f\u76f8\u67e5\u660e\u518d\u884c\u5904\u7f6e\u7684\u4e86\u3002\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u90a3\u4e48\u54b1\u4eec\u600e\u529e\uff1f\u662f\u627e\u4e2a\u5730\u65b9\u8eb2\u51e0\u5929\uff0c\u7b49\u5f85\u5927\u519b\u53bb\u5f97\u8fdc\u4e86\u54b1\u4eec\u518d\u8d70\u5462\uff1f\u8fd8\u662f\u73b0 \u5728\u5c31\u5192\u9669\u524d\u884c\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u73b0\u5728\u867d\u7136\u51fa\u4e86\u8499\u53e4\u56fd\u5883\uff0c\u4f46\u8fd8\u662f\u672a\u8131\u4ed6\u4eec\u7684\u52bf\u529b\u8303\u56f4\u3002\u5018\u82e5\u505c\u4e0b\uff0c\u53ea\u6015\u8ffd \u5175\u6765\u5230\uff0c\u7a77\u52a0\u641c\u7d22\uff0c\u96be\u4ee5\u9690\u85cf\uff01\u6768\u5a49\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u5c31\u53ea\u6709\u5192\u9669\u524d\u884c\u4e86\u3002\u201d\u674e\u601d\u5357\u9053\uff1a\u201c\u4e14\u5f85 \u6211\u4ed4\u7ec6\u60f3\u60f3\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u719f\u6089\u5730\u7406\uff0c\u77e5\u9053\u8fd9\u4e2a\u8349\u539f\u4e43\u662f\u201c\u4e0d\u7ba1\u201d\u5730\u5e26\uff0c\u5411\u5357\u8d70\u8fdb\u91d1\u56fd\u56fd\u5883\uff0c\u5411\u897f\u8d70\u4e43\u662f\u897f \u590f\u7684\u5c5e\u58eb\u3002\u897f\u590f\u662f\u897f\u5317\u7684\u4e00\u4e2a\u5c0f\u56fd\uff0c\u5360\u636e\u4eca\u9655\u5317\u5b89\u585e\u4ee5\u5317\u81f3\u5b81\u590f\u4e00\u5e26\uff0c\u7ed5\u9053\u9655\u5317\u7684\uff0c\u53ef\u4ee5\u8fdb \u5165\u91d1\u56fd\u7684\u5c71\u897f\uff0c\u4ece\u5927\u540c\u800c\u76f4\u901a\u5927\u90fd\uff08\u4eca\u5317\u4eac\uff09\u3002 \u3000\u3000\u674e\u601d\u5357\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u53d6\u9053\u897f\u590f\uff0c\u8def\u7a0b\u8f83\u957f\uff0c\u4f46\u8f83\u5b89\u5168\u3002\u4e0d\u8fc7\u8ddf\u7740\u8499\u53e4\u5927\u519b\u6240\u8d70\u7684\u8def\u7ebf\uff0c \u5374\u53ef\u4ee5\u6709\u62a5\u4ec7\u7684\u673a\u4f1a\u3002\u8fd9\u6b21\u8499\u53e4\u4f10\u91d1\uff0c\u8981\u5229\u7528\u6295\u987a\u4ed6\u4eec\u7684\u6c49\u4eba\uff0c\u4f59\u4e00\u4e2d\u8fd9\u4fbf\u662f\u5728\u519b\u4e2d\u7684\u3002\u4e0d \u8fc7\u96be\u5c31\u96be\u5728\u600e\u6837\u53ef\u4ee5\u907f\u514d\u548c\u4ed6\u4eec\u7684\u5927\u519b\u78b0\u4e0a\uff0c\u800c\u53c8\u627e\u5f97\u5230\u884c\u523a\u7684\u673a\u4f1a\u3002 \u3000\u3000\u6b63\u5728\u8e0c\u8e87\u672a\u51b3\uff0c\u5ffd\u542c\u5f97\u8fdc\u5904\u6709\u7434\u58f0\u9690\u9690\u4f20\u6765\uff0c\u5982\u6028\u5982\u6155\uff0c\u5982\u6ce3\u5982\u8bc9\uff0c\u5982\u5b64\u821f\u4e4b\u9047\u60ca\u6d9b\uff0c \u5982\u68a8\u82b1\u4e4b\u906d\u66b4\u96e8\u3002\u8f9b\u9178\u51c4\u695a\u4e4b\u4e2d\uff0c\u517c\u6709\u60ca\u60f6\u6124\u6168\u4e4b\u6025\u3002\u6768\u5a49\u53d7\u7434\u97f3\u7684\u7275\u5f15\uff0c\u4e0d\u77e5\u4e0d\u89c9\u7eb5\u9a6c\u897f \u884c\uff0c\u674e\u601d\u5357\u7684\u601d\u8def\u4e5f\u88ab\u6253\u65ad\uff0c\u53ea\u89c9\u6bcf\u4e00\u4e2a\u97f3\u7b26\u90fd\u597d\u50cf\u6572\u52a8\u4e86\u4ed6\u7684\u5fc3\u5f26\u3002 \u3000\u3000\u6768\u5a49\u5ffd\u5730\u56de\u5934\u8bf4\u9053\uff1a\u201c\u6211\u542c\u8fc7\u5979\u5f39\u594f\u7684\u9a6c\u5934\u7434\uff0c\u8fd9\u4e00\u5b9a\u662f\u8349\u539f\u4e0a\u6700\u8457\u540d\u7684\u90a3\u4e2a\u5973\u6b4c\u624b\u5361 \u6d1b\u4e1d\u7684\u7434\u58f0\uff01\u5357\u54e5\uff0c\u4f60\u542c\u5979\u7434\u58f0\u5982\u6b64\u51c4\u695a\uff0c\u53ea\u6015\u662f\u6b63\u5728\u906d\u9047\u4ec0\u4e48\u4e0d\u5e78\u4e4b\u4e8b\u4e86\uff01\u201d\u674e\u601d\u5357\u89c9\u5f97 \u201c\u5361\u6d1b\u4e1d\u201d\u8fd9\u4e2a\u540d\u5b57\u597d\u719f\uff0c\u4f46\u4e00\u65f6\u5374\u60f3\u4e0d\u8d77\u662f\u8c01\uff1f \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4e48\u54b1\u4eec\u5c31\u53bb\u770b\u770b\uff01\u201d\u7434\u58f0\u54c0\u6028\u7f20\u7ef5\uff0c\u6781\u662f\u4ee4\u4eba\u4f24\u611f\u3002\u4e0d\u4f46\u7cbe\u4e8e\u97f3\u5f8b \u7684\u6768\u5a49\u8981\u53bb\u4e00\u770b\u7a76\u7adf\uff0c\u8fde\u674e\u601d\u5357\u4e5f\u5728\u4e0d\u77e5\u4e0d\u89c9\u95f4\u53d7\u5b83\u7684\u7275\u5f15\u4e86\u3002 \u3000\u3000\u50ac\u9a6c\u8def\u4e86\u4e00\u7a0b\uff0c\u53ea\u542c\u5f97\u90a3\u5c11\u5973\u4e00\u9762\u5f39\u7434\u4e00\u9762\u5531\u9053\uff1a \u3000\u3000\u201c\u5927\u98ce\u5377\u8d77\u4e86\u9ec4\u6c99\uff0c \u3000\u3000\u5929\u8fb9\u7684\u5140\u9e70\u76d8\u65cb\u6b32\u4e0b\uff0c \u3000\u3000\u54e5\u5440\uff0c\u4f60\u5c31\u662f\u5929\u8fb9\u90a3\u53ea\u5140\u9e70 \u3000\u3000\u4f60\u867d\u7136\u4e0d\u6015\u98ce\u6c99\uff0c\u4e5f\u4e0d\u8981\u4e0b\u6765\u5440\uff01 \u3000\u3000\u8fd9\u652f\u6b4c\u662f\u674e\u601d\u5357\u66fe\u7ecf\u542c\u8fc7\u7684\uff0c\u674e\u601d\u5357\u84e6\u5730\u60f3\u4e86\u8d77\u6765\uff0c\u90a3\u65e5\u6728\u534e\u9ece\u548c\u8d64\u8001\u761f\u8fce\u63a5\u4ed6\u524d\u5f80\u548c \u6797\uff0c\u5728\u9014\u4e2d\u78b0\u5230\u4e00\u961f\u5c11\u5973\u9001\u5979\u4eec\u7684\u60c5\u90ce\u51fa\u5f81\uff0c\u5176\u4e2d\u4e00\u4e2a\u6700\u7f8e\u7684\u5c11\u5973\u5f39\u8d77\u9a6c\u5934\u7434\uff0c\u5531\u7684\u5c31\u662f\u8fd9 \u4e00\u652f\u6b4c\u3002\u5f53\u65f6\u8d64\u8001\u6e29\u5bf9\u8fd9\u5c11\u5973\u5782\u6d8e\u4e0d\u5df2\uff0c\u66fe\u7ecf\u544a\u8bc9\u674e\u601d\u5357\u8bf4\u8fd9\u5c11\u5973\u4e43\u662f\u8499\u53e4\u8457\u540d\u7684\u7f8e\u4eba\u5361\u6d1b \u4e1d\u3002 \u3000\u3000\u674e\u601d\u5357\u548c\u6768\u5a49\u7684\u9a6c\u8dd1\u5f97\u5feb\uff0c\u6b64\u65f6\u5df2\u7ecf\u770b\u5f97\u89c1\u524d\u9762\u7684\u4eba\u4e86\u3002\u53ea\u89c1\u524d\u9762\u5171\u662f\u4e09\u9a91\uff0c\u4e24\u4e2a\u6b66\u58eb \u4e00\u524d\u4e00\u540e\uff0c\u628a\u4e00\u4e2a\u62b1\u7740\u9a6c\u5934\u7434\u7684\u5c11\u5973\u5939\u5728\u4e2d\u95f4\uff0c\u8fd9\u5c11\u5973\u679c\u7136\u5c31\u662f\u674e\u601d\u5357\u90a3\u65e5\u66fe\u7ecf\u89c1\u8fc7\u7684\u90a3\u4e2a \u5361\u6d1b\u4e1d\u3002 \u3000\u3000\u540e\u9762\u90a3\u4e2a\u7f09\u8863\u6b66\u58eb\u4fa7\u5730\u865a\u6253\u4e00\u97ad\uff0c\u72de\u7b11\u9053\uff1a\u201c\u4f60\u7684\u60c5\u90ce\u5c31\u662f\u5140\u9e70\uff0c\u4ed6\u4e5f\u4e0d\u80fd\u6551\u4f60\uff01\u201d\u770b \u8fd9\u60c5\u5f62\uff0c\u5361\u6d1b\u4e1d\u5df2\u7ecf\u53d8\u6210\u4e86\u4ed6\u4eec\u7684\u4fd8\u864f\uff0c\u4e0d\u8fc7\u7a0d\u53d7\u4f18\u5f85\uff0c\u672a\u6709\u52a0\u7f1a\uff0c\u4ecd\u7136\u8ba9\u5979\u9a91\u9a6c\u800c\u5df2\u3002\u8fd9 \u4e24\u4e2a\u6b66\u58eb\u5c06\u5979\u5939\u5728\u4e2d\u95f4\uff0c\u5f53\u7136\u662f\u9632\u5979\u9003\u8d70\u7684\u4e86\u3002 \u3000\u3000\u90a3\u4e24\u4e2a\u6b66\u571f\u542c\u5f97\u540e\u9762\u9a6c\u94c3\u58f0\u54cd\uff0c\u56de\u8fc7\u5934\u6765\uff1b\u5361\u6d1b\u4e1d\u4e5f\u505c\u4e86\u6b4c\u5531\uff0c\u56de\u5934\u89c2\u770b\u3002\u90a3\u65e5\u674e\u601d\u5357 \u662f\u548c\u8d64\u8001\u6e29\u3001\u6728\u534e\u9ece\u5728\u4e00\u8d77\u7684\uff0c\u4ed6\u662f\u4e2a\u6c49\u4eba\uff0c\u5bb9\u6613\u8bb0\u8ba4\u3002\u5361\u6d1b\u4e1d\u4e00\u770b\u5c31\u8ba4\u5f97\u4e86\u4ed6\uff0c\u5fc3\u91cc\u60f3 \u9053\uff1a\u201c\u4ed6\u662f\u6211\u56fd\u91d1\u5e10\u6b66\u571f\u7684\u670b\u53cb\uff0c\u60f3\u6765\u5e94\u8be5\u6551\u6211\uff01\u201d \u3000\u3000\u540e\u9762\u90a3\u4e2a\u9ed1\u8863\u6b66\u58eb\u770b\u89c1\u4e86\u6768\u5a49\uff0c\u4e0d\u89c9\u7741\u5927\u4e86\u773c\u775b\uff0c\u5495\u5495\u8113\u5450\u5730\u8bf4\u9053\uff1a\u201c\u54c8\uff0c\u8fd9\u4e2a\u6c49\u4eba\u96cc \u513f\u6bd4\u8499\u53e4\u7684\u7f8e\u4eba\u8fd8\u66f4\u6f02\u4eae\uff0c\u54c8\uff0c\u54b1\u4eec\u53ef\u662f\u8273\u798f\u4e0d\u6d45\uff01\u5582\uff01\u5927\u54e5\uff0c\u4f9d\u6211\u8bf4\uff0c\u62a2\u4e86\u5979\u5427\u3002\u54b1\u4eec\u4e00 \u4eba\u4e00\u4e2a\uff0c\u6b63\u597d\u5408\u9002\uff01\u201d \u3000\u3000\u8fd9\u9ed1\u8863\u6b66\u571f\u8bf4\u7684\u662f\u897f\u590f\u5317\u90e8\u7684\u571f\u8bdd\uff0c\u674e\u601d\u5357\u542c\u4e0d\u61c2\u4ed6\u8bf4\u4ec0\u4e48\u3002\u6768\u5a49\u5728\u8499\u53e4\u4f4f\u4e86\u4e03\u5e74\uff0c\u8499 \u53e4\u5e38\u6709\u897f\u590f\u7684\u7267\u6c11\u6765\u5f80\uff0c\u56e0\u6b64\u6768\u5a49\u5bf9\u897f\u590f\u7684\u571f\u8bdd\u61c2\u5f97\u4e00\u4e9b\uff0c\u8fd9\u9ed1\u8863\u6b66\u58eb\u8bf4\u7684\u6c61\u8a00\u79fd\u8bed\uff0c\u5979\u867d \u7136\u542c\u4e0d\u5168\u61c2\uff0c\u4ea6\u5df2\u77e5\u5176\u5927\u610f\uff0c\u662f\u8981\u62a2\u5979\u3002 \u3000\u3000\u6768\u5a49\u5927\u6012\uff0c\u6b63\u8981\u559d\u9a82\uff0c\u5361\u6d1b\u4e1d\u5df2\u7ecf\u53eb\u9053\uff1a\u201c\u6551\u547d\uff0c\u6551\u547d\uff01\u201d\u540e\u9762\u7684\u90a3\u4e2a\u9ed1\u8863\u4e00\u97ad\u6253\u53bb\uff0c \u6768\u5a49\u7684\u4e24\u67c4\u5315\u9996\u548c\u674e\u601d\u5357\u7684\u516d\u679a\u94dc\u94b1\u4ea6\u5df2\u540c\u65f6\u51fa\u624b\uff01 \u3000\u3000\u201c\u9ed1\u8863\u6b66\u58eb\u8eab\u624b\u59d4\u5b9e\u4e0d\u5f31\uff0c\u56de\u97ad\u4e00\u5377\uff0c\u201c\u556a\u201d\u7684\u4e00\u58f0\u5c31\u628a\u6768\u5a49\u90a3\u652f\u5315\u9996\u6253\u843d\uff0c\u72de\u7b11\u9053\uff1a \u201c\u597d\u4e2a\u4e2b\u5934\uff0c\u6211\u8fd8\u672a\u66fe\u52a8\u624b\uff0c\u4f60\u5c31\u52a8\u624b\u4e86\uff01\u201d\u7b11\u58f0\u672a\u7edd\uff0c\u5ffd\u5730\u201c\u54ce\u54df\u201d\u4e00\u58f0\u957f\u97ad\u5760\u5730\u3002\u539f\u6765 \u662f\u674e\u601d\u5357\u6253\u5411\u4ed6\u7684\u90a3\u4e09\u679a\u94b1\u9556\uff0c\u6709\u4e24\u679a\u6253\u7740\u4e86\u4ed6\u7684\u7a74\u9053\uff0c\u4e00\u679a\u6253\u7740\u4e86\u4ed6\u7684\u864e\u53e3\u3002 \u3000\u3000\u674e\u601d\u5357\u7684\u201c\u94b1\u9556\u201d\u662f\u7528\u666e\u901a\u7684\u94dc\u94b1\u628a\u8fb9\u7f18\u78e8\u5f97\u950b\u5229\u4e4b\u540e\uff0c\u5f53\u4f5c\u6697\u5668\u7684\u3002\u94dc\u94b1\u7684\u5206\u91cf\u6bd4\u5315 \u9996\u8f7b\uff0c\u6253\u51fa\u6765\u5e76\u65e0\u631f\u98ce\u4e4b\u58f0\uff0c\u7ea2\u8863\u6b66\u58eb\u987e\u5f97\u53ca\u62e8\u6253\u5315\u9996\uff0c\u8fd9\u4e09\u679a\u94b1\u9556\u4ed6\u5c31\u95ea\u907f\u4e0d\u5f00\u4e86\u3002\u94b1\u9556 \u8fb9\u7f18\u950b\u5229\uff0c\u9ed1\u8863\u6b66\u58eb\u7684\u624b\u8155\u88ab\u94b1\u9556\u5272\u7834\uff0c\u6545\u6b64\u624b\u4e2d\u7684\u957f\u97ad\u5148\u8dcc\u4e0b\u6765\uff0c\u8ddf\u7740\u4ed6\u81ea\u5df1\u4e5f\u662f\u4e00\u4e2a\u5012 \u683d\u8471\uff0c\u4ece\u9a6c\u80cc\u4e0a\u6eda\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u6768\u5a49\u7684\u4e24\u652f\u5315\u9996\u548c\u674e\u601d\u5357\u7684\u516d\u679a\u94b1\u9556\u90fd\u662f\u5206\u522b\u5411\u7740\u4e24\u4e2a\u9ed1\u8863\u6b66\u58eb\u6253\u53bb\u7684\uff0c\u524d\u9762\u7684\u90a3\u4e2a\u9ed1 \u8863\u6b66\u58eb\u6b66\u529f\u66f4\u9ad8\uff0c\u53ea\u89c1\u4ed6\u628a\u624b\u4e00\u62db\uff0c\u5c31\u628a\u6768\u5a49\u7684\u5315\u9996\u63a5\u5230\u624b\u4e2d\uff0c\u5315\u9996\u4e00\u5212\uff0c\u5de6\u624b\u4e2d\u6307\u75be\u5f39\u3002 \u950c\u94b5\u4e24\u58f0\uff0c\u674e\u601d\u5357\u6253\u5411\u4ed6\u53cc\u80c1\u7684\u4e24\u6545\u94b1\u9556\u7ed9\u4ed6\u5f39\u843d\uff0c\u6253\u4ed6\u80f8\u53e3\u201c\u6a80\u4e2d\u7a74\u201d\u7684\u90a3\u652f\u94b1\u9556\u5219\u7ed9\u5315 \u9996\u5f53\u4e2d\u5256\u5f00\uff01 \u3000\u3000\u8fd9\u9ed1\u8863\u6b66\u58eb\u559d\u9053\uff1a\u201c\u539f\u7269\u5949\u8fd8\uff01\u201d\u5315\u9996\u53cd\u63b7\u56de\u6765\uff0c\u6768\u5a49\u62d4\u5251\u4e00\u6321\uff0c\u201c\u54e8\u201d\u7684\u4e00\u58f0\uff0c\u706b\u82b1 \u56db\u6e85\u3002\u8fd9\u9ed1\u8863\u6b66\u58eb\u63b7\u56de\u6765\u7684\u529b\u9053\u6bd4\u5979\u5927\u5f97\u591a\uff0c\u5315\u9996\u4f59\u529b\u672a\u8870\uff0c\u7adf\u81ea\u5411\u7740\u5361\u6d1b\u4e1d\u98de\u53bb\u3002\u4f3c\u4e4e\u4ed6 \u65e9\u5df2\u6599\u5230\u4e86\u6709\u6b64\u7ed3\u679c\uff0c\u5728\u628a\u8fd9\u67c4\u5315\u9996\u53cd\u63b7\u56de\u6765\u4e4b\u65f6\uff0c\u4fbf\u5df2\u7528\u4e86\u5de7\u5999\u7684\u624b\u6cd5\uff0c\u8ba1\u7b97\u5f97\u5341\u5206\u51c6\u786e \u7684\u4e86\u3002 \u3000\u3000\u6768\u5a49\u5927\u5403\u4e00\u60ca\uff0c\u8fde\u5fd9\u65bd\u5c55\u8d85\u5999\u7684\u8f7b\u529f\uff0c\u5728\u9a6c\u80cc\u4e0a\u5c31\u4f3c\u4e00\u652f\u7bad\u5730\u98de\u51fa\u53bb\uff0c\u4eba\u5728\u534a\u7a7a\uff0c\u4f38\u624b \u4e00\u6293\uff0c\u5df2\u662f\u628a\u5361\u6d1b\u4e1d\u6293\u4e86\u8d77\u6765\u3002\u53ea\u89c1\u5200\u5149\u4e00\u95ea\uff0c\u5361\u7edc\u4e1d\u7684\u90a3\u5339\u5750\u9a91\u56db\u819d\u5c48\u5730\uff0c\u5df2\u662f\u7ed9\u8fd9\u67c4\u98de \u5200\u65a9\u65ad\u4e86\u4e00\u6761\u817f\u3002\u539f\u6765\u8fd9\u4e2a\u9ed1\u8863\u6b66\u58eb\u7528\u610f\u5e76\u4e0d\u5728\u4e8e\u6740\u5361\u6d1b\u4e1d\uff0c\u800c\u662f\u8981\u4f24\u5979\u5750\u9a91\uff0c\u9632\u5979\u9003\u8d70 \u7684\u3002 \u3000\u3000\u6768\u5a49\u628a\u5361\u6d1b\u4e1d\u653e\u5728\u8def\u8fb9\u7684\u4e00\u68f5\u6811\u4e0b\uff0c\u5b89\u6170\u5979\u9053\uff1a\u201c\u522b\u6015\uff0c\u522b\u6015\uff0c\u6709\u6211\u5728\u6b64\uff0c\u51b3\u4e0d\u80fd\u8ba9\u4ed6 \u4eec\u4f24\u4f60\u4e00\u4e1d\u6beb\u53d1\u3002\u201d\u5361\u6d1b\u4e1d\u82b1\u5bb9\u5931\u8272\uff0c\u4f0f\u5728\u6768\u5a49\u6000\u4e2d\uff0c\u4e45\u4e45\u8bf4\u4e0d\u51fa\u8bdd\u3002 \u3000\u3000\u6b64\u65f6\u674e\u601d\u5357\u548c\u524d\u9762\u90a3\u4e2a\u9ed1\u8863\u6b66\u571f\u5df2\u7ecf\u4ea4\u4e0a\u4e86\u624b\u3002\u9ed1\u8863\u6b66\u58eb\u7528\u7684\u662f\u4e00\u67c4\u6708\u7259\u5f2f\u5200\uff0c\u9a70\u9a6c\u8fc7 \u6765\uff0c\u5f2f\u5200\u6a2a\u5288\uff0c\u4f0a\u5982\u4e00\u9053\u7535\u5149\u6a2a\u8fc7\u7a7a\u9645\u3002 \u3000\u3000\u674e\u601d\u5357\u5fc3\u4e2d\u4e00\u51db\uff1a\u201c\u8fd9\u53ae\u6c34\u9886\u5012\u662f\u4e0d\u5f31\uff01\u201d\u9752\u94a2\u5251\u626c\u7a7a\u4e00\u5251\u8fd8\u4e86\u4e00\u62db\u201c\u674e\u5e7f\u5c04\u77f3\u201d\u3002\u9a6c \u4e0a\u4ea4\u950b\uff0c\u8bb2\u7a76\u7684\u662f\u62db\u6570\u8fc5\u6377\uff0c\u5200\u5251\u76f8\u4ea4\uff0c\u674e\u601d\u5357\u4e0d\u5f85\u4ed6\u7684\u6c14\u529b\u4f7f\u8db3\uff0c\u5251\u950b\u4e00\u63a0\uff0c\u8fc5\u5373\u53d8\u62db\u5c31 \u523a\u4ed6\u7684\u819d\u76d6\u3002\u8c82\u8863\u6b66\u571f\u6c89\u4e0b\u5200\u80cc\u4e00\u62cd\uff0c\u559d\u9053\uff1a\u201c\u597d\u5251\u6cd5\uff01\u201d\u4e24\u5339\u9a6c\u5df2\u7ecf\u5206\u5f00\u3002 \u3000\u3000\u7167\u9762\u4e24\u62db\uff0c\u4e0d\u5206\u80dc\u8d1f\u3002\u674e\u601d\u5357\u9a6c\u5feb\uff0c\u659c\u523a\u7a9c\u51fa\uff0c\u5c06\u4ed6\u622a\u4f4f\u3002\u8fd9\u9ed1\u8863\u6b66\u58eb\u6c14\u529b\u5145\u6c9b\uff0c\u5200\u6cd5 \u4e5f\u5f88\u5389\u5bb3\uff0c\u53cc\u9a6c\u76d8\u65cb\uff0c\u6076\u6597\u4e86\u5341\u51e0\u4e2a\u56de\u5408\uff0c\u674e\u601d\u5357\u7adf\u7136\u5360\u4e0d\u5230\u4ed6\u4e1d\u6beb\u4fbf\u5b9c\u3002 \u3000\u3000\u6768\u5a49\u628a\u5361\u6d1b\u4e1d\u653e\u4e0b\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8eb2\u5728\u8fd9\u68f5\u5927\u6811\u540e\u9762\uff0c\u4e0d\u8981\u4f38\u51fa\u5934\u6765\uff0c\u6211\u53bb\u7ed9\u4f60\u628a\u8fd9\u5f3a\u76d7 \u6740\u6389\u3002\u201d\u6b64\u65f6\u674e\u601d\u5357\u5df2\u622a\u4f4f\u90a3\u9ed1\u8863\u6b66\u4e0a\u5728\u5c71\u8fb9\u53ae\u6740\uff0c\u8ddd\u79bb\u5361\u6d1b\u4e1d\u7684\u85cf\u8eab\u4e4b\u5904\u6709\u5341\u6570\u4e08\u4e4b\u9065\uff0c \u4e00\u822c\u6697\u5668\u6253\u4e0d\u5230\u8fd9\u4e48\u8fdc\uff0c\u5373\u4f7f\u4e07\u4e00\u6253\u4e86\u5230\u6765\uff0c\u6709\u5927\u6811\u906e\u6321\uff0c\u4ea6\u53ef\u65e0\u59a8\u3002 \u3000\u3000\u6768\u5a49\u8de8\u4e0a\u5750\u9a91\uff0c\u62d4\u5251\u51fa\u9798\uff0c\u7acb\u5373\u5411\u90a3\u9ed1\u8863\u6b66\u571f\u51b2\u523a\uff0c\u6768\u5a49\u867d\u7136\u6c14\u529b\u8f83\u5f31\uff0c\u4f46\u5251\u672f\u5341\u5206\u7cbe \u5999\uff0c\u592b\u59bb\u8054\u624b\uff0c\u53cc\u9a6c\u76d8\u65cb\uff0c\u6740\u5f97\u90a3\u9ed1\u8863\u6b66\u571f\u4e1c\u7a9c\u897f\u8eb2\uff0c\u53ea\u6709\u62db\u67b6\u4e4b\u529f\u3002 \u3000\u3000\u5361\u6d1b\u4e1d\u8eb2\u5728\u5927\u6811\u540e\u9762\uff0c\u53ea\u542c\u5f97\u91d1\u94c1\u4ea4\u9e23\u4e4b\u58f0\u9707\u5f97\u5979\u8033\u9f13\u55e1\u55e1\u4f5c\u54cd\uff0c\u5374\u4e0d\u77e5\u53cc\u65b9\u80dc\u8d1f\u5982 \u4f55\uff0c\u5fc3\u5934\u535c\u535c\u4e71\u8df3\uff0c\u53c8\u4e0d\u6562\u63a2\u51fa\u5934\u53bb\u5077\u770b\u3002\u6b63\u5728\u63d0\u5fc3\u540a\u80c6\u4e4b\u9645\uff0c\u5076\u7136\u773c\u5149\u4e00\u77a5\uff0c\u5ffd\u89c1\u90a3\u53d7\u4e86 \u4f24\u7684\u6b66\u571f\uff0c\u5728\u4e71\u8349\u4e1b\u4e2d\u8815\u8815\u800c\u52a8\uff0c\u597d\u50cf\u5c31\u8981\u5411\u5979\u722c\u6765\u3002\u5361\u6d1b\u4e1d\u672c\u6765\u5df2\u7ecf\u662f\u60ca\u5f13\u4e4b\u9e1f\u7684\u4e86\uff0c\u600e \u7ecf\u5f97\u518d\u53d7\u60ca\u5413\uff0c\u4e0d\u89c9\u5931\u58f0\u60ca\u547c\u3002 \u3000\u3000\u539f\u6765\u8fd9\u4e2a\u6b66\u58eb\u6709\u81ea\u89e3\u7a74\u9053\u4e4b\u80fd\uff0c\u4ed6\u7ed9\u674e\u601d\u5357\u7684\u94b1\u9556\u6253\u7740\u4e24\u5904\u7a74\u9053\uff0c\u6b64\u65f6\u521a\u521a\u901a\u89e3\uff0c\u4f46\u4ecd \u7136\u4f7f\u4e0d\u51fa\u6c14\u529b\uff0c\u6545\u6b64\u53ea\u80fd\u722c\u884c\u3002\u5361\u6d1b\u4e1d\u5018\u82e5\u80c6\u5927\u7684\u8bdd\uff0c\u53ea\u8981\u7528\u4e00\u5757\u77f3\u5934\u5c31\u53ef\u4ee5\u628a\u4ed6\u6253\u6b7b\uff0c\u5b9e \u5728\u7528\u4e0d\u7740\u6015\u4ed6\u7684\u3002 \u3000\u3000\u674e\u601d\u5357\u6b63\u4f7f\u5230\u4e00\u62db\u201c\u4e91\u9ebe\u4e09\u821e\u201d\uff0c\u5251\u5149\u5377\u8d77\u4e09\u9053\u94f6\u5708\uff0c\u5411\u90a3\u9ed1\u8863\u6b66\u58eb\u51b2\u523a\u3002\u773c\u770b\u9ed1\u8863\u6b66 \u58eb\u5df2\u662f\u96be\u4ee5\u906e\u62e6\uff0c\u5c31\u5728\u6b64\u65f6\uff0c\u6768\u5a49\u542c\u5f97\u5361\u6d1b\u4e1d\u7684\u5c16\u53eb\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u4e0d\u77e5\u5979\u51fa\u4e86\u4ec0\u4e48\u4e8b\u60c5\uff0c\u4e0d \u7531\u5f97\u56de\u5934\u4e00\u770b\u3002\u9ed1\u8863\u6b66\u58eb\u9a91\u672f\u7cbe\u5999\uff0c\u6293\u7d27\u673a\u4f1a\uff0c\u7acb\u5373\u4ece\u6768\u5a49\u8eab\u8fb9\u7a9c\u51fa\uff0c\u6768\u5a49\u84e6\u7136\u60ca\u89c9\uff0c\u53cd\u624b \u4e00\u5251\uff0c\u9ed1\u8863\u6b66\u58eb\u7528\u4e86\u4e2a\u201c\u6697\u91cc\u85cf\u8eab\u201d\uff0c\u6768\u5a49\u4e00\u5251\u65a9\u7740\u9a6c\u8e44\uff0c\u5251\u5c16\u5212\u8fc7\uff0c\u4f24\u4e86\u4ed6\u7684\u5c0f\u817f\uff0c\u53ef\u662f \u8fd9\u9ed1\u8863\u6b66\u58eb\u4ea6\u5df2\u8131\u51fa\u4e86\u4ed6\u4eec\u7684\u5305\u56f4\u4e86\u3002 \u3000\u3000\u674e\u601d\u5357\u6050\u9632\u4ed6\u8dd1\u53bb\u4f24\u5bb3\u5361\u6d1b\u4e1d\uff0c\u8fde\u5fd9\u98de\u9a91\u9632\u62a4\uff0c\u6b8a\u4e0d\u77e5\u8fd9\u9ed1\u8863\u6b66\u58eb\u65e9\u5df2\u4e0d\u6562\u604b\u6218\uff0c\u4ed6\u4e0d \u662f\u5411\u5361\u6d1b\u4e1d\u85cf\u8eab\u4e4b\u5904\u9a70\u53bb\uff0c\u800c\u662f\u8dd1\u53bb\u6551\u4ed6\u7684\u540c\u4f34\u7684\u3002 \u3000\u3000\u53d7\u4e86\u4f24\u7684\u90a3\u4e2a\u6b66\u58eb\u4f0f\u5728\u8def\u65c1\u7684\u4e71\u8349\u4e4b\u4e2d\uff0c\u4eba\u8fd8\u672a\u80fd\u7ad9\u7acb\uff0c\u53ea\u80fd\u4e3e\u8d77\u4e00\u53ea\u624b\uff0c\u9ed1\u8863\u6b66\u58eb\u9a70 \u9a6c\u5230\u4ed6\u8eab\u8fb9\uff0c\u9a6c\u97ad\u4e00\u6325\uff0c\u559d\u9053\uff1a\u201c\u6293\u4f4f\uff01\u201d\u53d7\u4f24\u6b66\u58eb\u4f7f\u51fa\u4e86\u5403\u5976\u7684\u6c14\u529b\uff0c\u7d27\u7d27\u6293\u7740\u4ed6\u7684\u8fd9\u6839 \u9a6c\u97ad\uff0c\u9ed1\u8863\u6b66\u58eb\u5c06\u4ed6\u63d0\u4e0a\u9a6c\u80cc\uff0c\u9a6c\u4e0d\u505c\u8e44\uff0c\u8f6c\u773c\u95f4\u5df2\u662f\u53bb\u5f97\u8fdc\u4e86\u3002 \u3000\u3000\u6768\u5a49\u628a\u5361\u6d1b\u4e1d\u6276\u7a33\uff0c\u8bf4\u9017\uff1a\u201c\u4f60\u6ca1\u4e8b\u4e48\uff1f\u5f3a\u76d7\u5df2\u7ecf\u8d76\u8dd1\u4e86\u3002\u201d\u5361\u6d1b\u4e1d\u9053\uff1a\u201c\u6ca1\u4e8b\uff0c\u591a\u8c22 \u4f60\u4eec\u3002\u201d \u3000\u3000\u6768\u5a49\u6c14\u6068\u672a\u5df2\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u5f3a\u76d7\u751a\u662f\u53ef\u6076\uff0c\u6211\u672c\u6765\u8981\u7ed9\u4f60\u6740\u6389\u4ed6\u4eec\u7684\uff0c\u53ef\u60dc\u8fd8\u662f\u7ed9 \u4ed6\u4eec\u8d70\u4e86\u3002\u201d\u5361\u6d1b\u4e1d\u9053\uff1a\u201c\u6211\u4f46\u6c42\u8131\u8eab\uff0c\u5df1\u662f\u4e07\u5e78\u3002\u6076\u4eba\u81ea\u6709\u6076\u62a5\uff0c\u6211\u4e5f\u4e0d\u60f3\u62a5\u4ec7\u4e86\u3002\u201d \u3000\u3000\u6768\u5a49\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u4f60\u7684\u7f8e\u5999\u7434\u58f0\u628a\u6211\u5f15\u6765\uff0c\u8fd9\u4e5f\u662f\u54b1\u4eec\u6709\u7f18\u3002\u201d\u674e\u601d\u5357\u9053\uff1a\u201c\u90a3\u65e5\u6211\u89c1 \u4f60\u9001\u4eba\u51fa\u5f81\uff0c\u5374\u600e\u4e48\u5230\u4e86\u8fd9\u91cc\u6765\uff1f\u8fd9\u4e24\u4e2a\u6b66\u58eb\u53c8\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u5361\u6d1b\u4e1d\u988a\u6655\u5a07\u7ea2\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u820d\u4e0d\u5f97\u6211\u7684\u963f\u76d6\uff0c\u6240\u4ee5\u6211\u5c31\u8ddf\u7740\u968f\u519b\u7684\u672c\u56fd\u5546\u961f\u8d70\uff0c \u4e00\u76f4\u9001\u5230\u4e86\u9f99\u6c99\u5806\u3002\u201d\u201c\u9f99\u6c99\u5806\u201d\u5df2\u662f\u6df1\u5165\u91d1\u56fd\u56fd\u5883\u4e4b\u5185\u4e00\u767e\u591a\u91cc\u7684\u5730\u65b9\uff0c\u90a3\u91cc\u5730\u5f62\u9669\u5cfb\uff0c \u6c34\u8349\u4e30\u9976\uff0c\u662f\u4e00\u4e2a\u5f88\u597d\u7684\u5c6f\u5175\u4e4b\u5730\u3002 \u3000\u3000\u6768\u5a49\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u795d\u82f1\u53f0\u9001\u6881\u5c71\u4f2f\uff0c\u4e0d\u8fc7\u9001\u4e86\u5341\u516b\u91cc\u8def\uff0c\u5343\u53e4\u4f20\u4e3a\u4f73\u8bdd\uff0c\u5361\u6d1b\u4e1d\u9001\u5979\u7684 \u60c5\u90ce\uff0c\u7adf\u7136\u4e0d\u60dc\u5ea6\u6c99\u6f20\uff0c\u8d8a\u8349\u539f\uff0c\u8dcb\u6d89\u4f55\u6b62\u5343\u91cc\uff1f\u771f\u662f\u4e2a\u591a\u60c5\u7684\u59d1\u5a18\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u5374\u60f3\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u91d1\u56fd\u7684\u8fb9\u5883\u5b88\u519b\u7adf\u662f\u5168\u65e0\u62b5\u6297\uff0c\u8fde\u9f99\u6c99\u5806\u8fd9\u6837\u9669\u8981\u7684\u5730\u65b9\u90fd\u4e0d \u8bbe\u9632\uff0c\u5c31\u7ed9\u8499\u53e4\u5175\u5360\u9886\u4e86\u3002\u53ef\u662f\u8499\u53e4\u9a91\u5175\u4e00\u5411\u8bb2\u7a76\u7528\u5175\u795e\u901f\u7684\uff0c\u4f55\u4ee5\u5728\u593a\u4e86\u582a\u79f0\u5929\u9669\u7684\u9f99\u6c99 \u5806\u4e4b\u540e\uff0c\u4e0d\u4e58\u80dc\u8ffd\u51fb\uff0c\u957f\u9a71\u76f4\u5165\uff0c\u53cd\u800c\u5728\u9f99\u6c99\u5806\u6b47\u4e0b\u6765\u5462\u3002\u201d \u3000\u3000\u5361\u6d1b\u4e1d\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u5230\u4e86\u9f99\u6c99\u5806\uff0c\u963f\u76d6\u529d\u6211\u56de\u53bb\u3002\u4ed6\u8bf4\u518d\u8fc7\u51e0\u5929\uff0c\u5c31\u8981\u6b63\u5f0f\u6253\u4ed7\u4e86\u3002\u4e00 \u5230\u4e86\u6253\u4ed7\u7684\u65f6\u5019\uff0c\u8fde\u5546\u961f\u4e5f\u4e0d\u80fd\u8fdb\u5165\u8425\u5730\uff0c\u5987\u5973\u662f\u66f4\u4e0d\u80fd\u968f\u519b\u7684\u4e86\u3002\u65e2\u5df2\u4e0d\u80fd\u89c1\u9762\uff0c\u4f55\u82e6\u5192 \u7740\u5371\u9669\u8ddf\u5728\u519b\u961f\u7684\u540e\u5934\u3002\u4f46\u6211\u4e0d\u80af\u542c\u4ed6\u7684\u8bf4\u8bdd\u3002\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u4ed6\u8bf4\u7684\u5f88\u6709\u9053\u7406\u5440\uff0c\u4f60\u4e3a\u4f55\u4e0d\u542c\u4ed6\u7684\uff1f\u201d \u3000\u3000\u5361\u6d1b\u4e1d\u9053\uff1a\u201c\u59d0\u59d0\uff0c\u4f60\u662f\u6211\u7684\u6551\u547d\u601d\u4eba\uff0c\u6211\u4e0d\u6015\u8bf4\u7ed9\u4f60\u542c\u3002\u6211\u3001\u6211\u662f\u6000\u7740\u79c1\u5fc3\uff0c\u6211\u60f3\u529d \u4ed6\u9003\u8d70\u3002\u6211\u8bf4\uff0c\u54b1\u4eec\u5728\u8349\u539f\u4e0a\u7267\u7f8a\uff0c\u65e5\u5b50\u8fc7\u5f97\u597d\u597d\u7684\uff0c\u4e3a\u4ec0\u4e48\u8981\u8dd1\u5230\u4eba\u5bb6\u7684\u5730\u65b9\u6253\u4ed7\u3001\u62a2 \u52ab\u3001\u6740\u4eba\uff1f\u4e2d\u56fd\u7684\u5730\u65b9\u53c8\u8fd9\u6837\u5927\uff0c\u6211\u867d\u7136\u4e0d\u77e5\u9053\u5b8b\u56fd\u5728\u4ec0\u4e48\u5730\u65b9\uff0c\u4f46\u542c\u8bf4\u79bb\u6211\u4eec\u8499\u53e4\u662f\u6709\u597d \u51e0\u4e07\u91cc\u8def\u7684\uff0c\u5927\u6c57\u8981\u541e\u91d1\u706d\u5b8b\uff0c\u90a3\u4e48\u4f60\u4eec\u4ec0\u4e48\u65f6\u5019\u624d\u80fd\u56de\u6765\uff1f\u8bf4\u4e0d\u5b9a\u6211\u4eec\u8fd9\u4e00\u751f\u90fd\u4e0d\u80fd\u89c1\u9762 \u4e86\uff01\u201d \u3000\u3000\u6768\u5a49\u70b9\u4e86\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8bf4\u5f97\u66f4\u6709\u9053\u7406\u3002\u8001\u767e\u59d3\u90fd\u662f\u613f\u610f\u8fc7\u592a\u5e73\u65e5\u5b50\u7684\uff0c\u8981\u6253\u4ed7\u7684\u53ea \u662f\u5927\u6c57\u548c\u5c06\u519b\u4eec\uff0c\u6216\u8005\u518d\u52a0\u4e0a\u4e00\u4e9b\u60f3\u53d1\u8d22\u7684\u5546\u4eba\u800c\u5df2\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u4e5f\u9053\uff1a\u201c\u4f60\u529d\u5f97\u5f88\u5bf9\u3002\u6211\u4eec\u6c49\u4eba\u4e5f\u662f\u548c\u4f60\u4e00\u6837\uff0c\u90fd\u662f\u4e0d\u613f\u610f\u6253\u4ed7\u7684\uff0c\u53ef\u662f\u4f60\u4eec\u82e5 \u662f\u6253\u5230\u4e86\u6211\u4eec\u7684\u5730\u65b9\uff0c\u6211\u4eec\u5c31\u4e00\u5b9a\u8981\u548c\u4f60\u4eec\u62fc\u547d\u4e86\u3002\u90a3\u65f6\u6211\u4eec\u7684\u767e\u59d3\u56fa\u7136\u4e0d\u514d\u8981\u7ed9\u4f60\u4eec\u6ee5 \u6740\uff0c\u4f60\u4eec\u7684\u5175\u58eb\u4e5f\u96be\u514d\u8981\u57cb\u9aa8\u5f02\u4e61\uff01\u4e24\u56fd\u90fd\u4e0d\u77e5\u8981\u6dfb\u4e86\u591a\u5c11\u5be1\u5987\u5b64\u513f\uff01\u6240\u4ee5\u4f60\u4eec\u7684\u5927\u6c57\u53d1\u52a8 \u8fd9\u573a\u6218\u4e89\uff0c\u5b9e\u5728\u662f\u4e2a\u7f6a\u6076\u3002\u4f60\u529d\u963f\u76d6\uff0c\u529d\u5f97\u975e\u5e38\u6709\u7406\uff0c\u4e00\u70b9\u4e0d\u662f\u79c1\u5fc3\u3002\u201d \u3000\u3000\u5361\u6d1b\u4e1d\u5927\u4e3a\u6b22\u559c\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u53ea\u9053\u4f60\u662f\u5e2e\u6211\u4eec\u7684\u5927\u6c57\u7684\uff0c\u539f\u6765\u4f60\u4e5f\u4e0d\u8d5e\u6210\u4ed6\u6253\u8fd9\u4e2a\u4ed7\uff0c \u5509\uff0c\u53ea\u662f\u53ef\u60dc\u963f\u76d6\u5374\u4e0d\u80af\u542c\u6211\u7684\u8bdd\u3002\u540e\u6765\uff0c\u540e\u6765\u2026\u2026\u201d\u8bf4\u81f3\u6b64\u5904\uff0c\u795e\u8272\u9eef\u7136\uff0c\u4f3c\u4e4e\u6b63\u5728\u60f3\u8d77 \u4e00\u4ef6\u4e0d\u5e78\u4e4b\u4e8b\u3002 \u3000\u3000\u6768\u5a49\u628a\u6c34\u56ca\u9012\u7ed9\u5979\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u559d\u4e00\u53e3\u6c34\uff0c\u6162\u6162\u5730\u8bf4\uff0c\u540e\u6765\u600e\u6837\uff1f\u201d \u3000\u3000\u5361\u6d1b\u4e1d\u559d\u4e86\u6c34\uff0c\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u963f\u76d6\u4e00\u5411\u542c\u6211\u7684\u8bdd\uff0c\u4ed6\u4e5f\u820d\u4e0d\u5f97\u4e22\u4e0b\u6211\u3002\u4f46\u8fd9\u6b21\u6211\u600e\u6837\u529d \u4ed6\uff0c\u4ed6\u90fd\u4e0d\u542c\u3002\u4ed6\u8bf4\u4ed6\u4e0d\u60f3\u6740\u4eba\uff0c\u4f46\u4e5f\u4e0d\u613f\u53d7\u4eba\u803b\u7b11\u3002\u4ed6\u662f\u8349\u539f\u4e0a\u51fa\u540d\u7684\u52c7\u6562\u730e\u4eba\uff01\u4eba\u5bb6\u79f0 \u4ed6\u4e3a\u8349\u539f\u4e4b\u9e70\u3002\u4ed6\u8bf4\u4eba\u7684\u540d\u513f\uff0c\u6811\u7684\u5f71\u513f\uff0c\u906e\u63a9\u4e5f\u906e\u63a9\u4e0d\u4e86\u7684\u3002\u5927\u6811\u6728\u7ecf\u5200\u65a7\u4e0d\u4f1a\u5012\u4e0b\uff0c\u5140 \u9e70\u5e26\u4e86\u7bad\u8fd8\u8981\u98de\u7fd4\uff0c\u6218\u58eb\u7684\u5fc3\u623f\u4e0d\u7ed9\u654c\u624b\u7684\u767e\u59d3\u3002\u201d \u3000\u3000\u8499\u53e4\u6c11\u98ce\u5c1a\u6b66\uff0c\u4e00\u822c\u9752\u5e74\u53d7\u4e86\u4f20\u7edf\u7684\u6240\u8c13\u201c\u6b66\u58eb\u7cbe\u795e\u201d\u7684\u85b0\u9676\uff0c\u5dee\u4e0d\u591a\u90fd\u6709\u8fd9\u6837\u7684\u89c2 \u5ff5\uff0c\u8fd9\u4e5f\u4e0d\u8db3\u4e3a\u602a\u3002\u6768\u5a49\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5979\u7684\u963f\u76d6\u867d\u7136\u7cca\u6d82\uff0c\u4e5f\u8fd8\u662f\u4e2a\u6bd4\u8f83\u6b63\u76f4\u7684\u6c49\u5b50\u3002\u4f46\u613f \u4ed6\u5c06\u6765\u80fd\u591f\u9192\u609f\u8fc7\u6765\uff0c\u4e0d\u8d1f\u5361\u6d1b\u4e1d\u7231\u4ed6\u7684\u5fc3\u610f\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u5e0c\u671b\u4ece\u5361\u6d1b\u4e1d\u7684\u53e3\u4e2d\u6253\u542c\u5230\u4e00\u4e9b\u6d88\u606f\uff0c\u95ee\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u4f60\u4eec\u662f\u5728\u9f99\u6c99\u5806\u5206\u624b\u7684 \u4e86\uff1f\u963f\u76d6\u53ef\u66fe\u544a\u8bc9\u4f60\u4ed6\u8981\u5230\u4ec0\u4e48\u5730\u65b9\u6253\u4ed7\uff1f\u201d \u3000\u3000\u5361\u6d1b\u4e1d\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5c31\u662f\u62c5\u5fc3\u8fd9\u4e2a\uff0c\u4e0d\u77e5\u9053\u8fd9\u4e2a\u4ed7\u8981\u6253\u5230\u4ec0\u4e48\u65f6\u5019\u4e86\u7ed3\uff1f\u53ef\u662f \u963f\u76d6\u8bf4\uff0c\u591a\u5219\u4e00\u5e74\uff0c\u5c11\u5219\u534a\u622a\uff0c\u53ea\u8981\u4ed6\u4e0d\u662f\u6b7b\u5728\u6218\u573a\uff0c\u4ed6\u4e00\u5b9a\u4f1a\u56de\u6765\u770b\u6211\u7684\uff0c\u53eb\u6211\u5b89\u5fc3\u7b49 \u4ed6\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u54e6\uff0c\u542c\u8fd9\u53e3\u6c14\uff0c\u963f\u76d6\u662f\u8ba4\u4e3a\u8fd9\u4e2a\u4ed7\u6700\u591a\u6253\u4e00\u5e74\u4fbf\u53ef\u4ee5\u7ed3\u675f\u4e86\uff1f\u4f60\u4eec\u7684\u5927\u6c57 \u4e0d\u662f\u5938\u4e0b\u6d77\u53e3\u8981\u541e\u91d1\u706d\u5b8b\u7684\u5417\uff1f\u54ea\u6709\u8fd9\u6837\u5bb9\u6613\u4fbf\u53ef\u7ed3\u675f\uff1f\u201d \u3000\u3000\u5361\u6d1b\u4e1d\u9053\uff1a\u201c\u662f\u5440\uff01\u6211\u5f53\u65f6\u5c31\u8fd9\u6837\u548c\u4ed6\u8bf4\uff0c\u4e2d\u56fd\u5730\u65b9\u8fd9\u4e48\u5927\uff0c\u542c\u4eba\u5bb6\u8bf4\u8d70\u5230\u4e2d\u56fd\u7687\u5e1d\u6240 \u4f4f\u7684\u5730\u65b9\uff0c\u603b\u6709\u51e0\u4e07\u91cc\u8def\u5462\uff01\u9a91\u7740\u9a6c\u98de\u8dd1\u53ea\u6015\u4e5f\u8981\u8dd1\u4e00\u5e74\uff0c\u53ef\u662f\u963f\u76d6\u5374\u597d\u50cf\u5341\u5206\u81ea\u4fe1\uff0c\u8bf4\u662f \u7528\u4e0d\u4e86\u4e00\u5e74\uff0c\u4e00\u5b9a\u4f1a\u56de\u6765\u770b\u6211\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u5fc3\u5934\u4e00\u52a8\uff0c\u60f3\u9053\uff1a\u201c\u83ab\u975e\u662f\u6210\u5409\u601d\u6c57\u6539\u53d8\u4e86\u4f5c\u6218\u7684\u8ba1\u5212\uff1f\u8fd8\u662f\u4f5c\u9010\u6b65\u8695\u98df\u7684\u6253 \u7b97\uff0c\u6253\u5230\u5927\u90fd\uff08\u91d1\u4eac\uff09\u4fbf\u5373\u6536\u5175\uff1f\u201d \u3000\u3000\u5361\u6d1b\u4e1d\u63a5\u4e0b\u53bb\u8bf4\u9053\uff1a\u201c\u963f\u76d6\u4ece\u6765\u6ca1\u6709\u9a97\u8fc7\u6211\uff0c\u4f46\u6211\u53ef\u4e0d\u6562\u76f8\u4fe1\u4ed6\u7684\u8bf4\u8bdd\uff0c\u6211\u60f3\u4ed6\u4e00\u5b9a\u662f \u54c4\u6211\u6b22\u559c\u7684\u3002\u6211\u4e0d\u613f\u610f\u5c31\u548c\u4ed6\u5206\u624b\uff0c\u5927\u519b\u9a7b\u624e\u5728\u9f99\u6c99\u5806\uff0c\u800c\u4e14\u8fd8\u5728\u4fee\u5efa\u5821\u5792\uff0c\u963f\u76d6\u867d\u7136\u6ca1\u6709 \u544a\u8bc9\u6211\uff0c\u4f46\u5546\u961f\u7684\u4eba\u90fd\u8bf4\uff0c\u770b\u8fd9\u60c5\u5f62\uff0c\u5927\u519b\u6050\u6015\u4e0d\u4f1a\u5728\u77ed\u671f\u95f4\u51fa\u53d1\u6253\u4ed7\u3002\u4e8e\u662f\u6211\u8bf4\uff0c\u963f\u76d6\uff0c \u4f60\u4e0d\u8981\u8d76\u6211\u8dd1\uff0c\u4f60\u4e0d\u8bb8\u6211\u8ddf\u7740\u519b\u961f\uff0c\u90a3\u4e48\uff0c\u6700\u5c11\u6211\u4e5f\u8981\u7b49\u5230\u4f60\u4eec\u771f\u7684\u662f\u51fa\u53d1\u6253\u4ed7\u4e86\uff0c\u6211\u624d\u56de \u53bb\u3002\u201d \u3000\u3000\u6768\u5a49\u7b11\u9053\uff1a\u201c\u5361\u6d1b\u4e1d\uff0c\u4f60\u5bf9\u4ed6\u5982\u6b64\u6df1\u60c5\uff0c\u4f60\u7684\u963f\u76d6\u4e00\u5b9a\u4e3a\u4f60\u611f\u52a8\u4e86\u3002\u201d \u3000\u3000\u5361\u6d1b\u4e1d\u9053\uff1a\u201c\u4ed6\u624d\u4e0d\u5462\uff01\u4e0d\u8fc7\uff0c\u4ed6\u867d\u7136\u4e0d\u80af\u548c\u6211\u79c1\u9003\uff0c\u529d\u6211\u56de\u53bb\uff0c\u5012\u662f\u4e3a\u6211\u7740\u60f3\u7684\u3002\u6211 \u4e0d\u542c\u4ed6\u7684\u8bdd\uff0c\u53d7\u4e86\u4e00\u573a\u707e\u96be\uff0c\u9669\u4e9b\u8131\u4e0d\u4e86\u8eab\u3002\u674e\u516c\u5b50\uff0c\u8bf4\u8d77\u8fd9\u573a\u707e\u96be\uff0c\u6211\u5e94\u8be5\u544a\u8bc9\u4f60\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u6014\u4e86\u4e00\u6014\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u5361\u6d1b\u4e1d\u9053\uff1a\u201c\u7ed9\u6211\u53d7\u8fd9\u573a\u707e\u96be\u7684\u90a3\u4e2a\u4eba\uff0c\u8bf4\u8d77\u6765\u4f60\u53ef \u80fd\u8ba4\u8bc6\uff0c\u4ed6\u4f3c\u4e4e\u5f88\u607c\u6068\u4f60\u5462\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u8be7\u9053\uff1a\u201c\u54e6\uff0c\u90a3\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u5361\u6d1b\u4e1d\u9053\uff1a\u201c\u662f\u660e\u6167\u516c\u4e3b\u7684\u9a78\u9a6c\u9547\u56fd\u738b\u4e8e\u3002\u674e\u516c\u5b50\uff0c\u4f60\u8ba4\u8bc6\u4ed6\u5417\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u4e2a\u4eba\u6211\u662f\u8ba4\u8bc6\u7684\uff0c\u4ed6\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u5361\u6d1b\u4e1d\u9053\uff1a\u201c\u4ed6\u662f\u9f99\u6c99\u5806\u9a7b\u519b\u7684\u5143\u5e05\u3002\u963f\u76d6\u90a3\u5929\u529d\u6211\u56de\u53bb\uff0c\u6211\u4e0d\u80af\u542c\uff0c\u4ed6\u5c31\u66fe\u7ecf\u8b66\u544a\u8fc7 \u6211\u7684\u4e86\u3002\u4ed6\u8bf4\u8fd9\u9547\u56fd\u738b\u5b50\u6b8b\u66b4\u800c\u53c8\u597d\u8272\uff0c\u82e5\u7136\u7ed9\u4ed6\u53d1\u89c9\u6211\u5728\u519b\u8425\u4e4b\u4e2d\u8d70\u52a8\uff0c\u53ea\u6015\u4f1a\u6709\u4e0d\u6d4b\u4e4b \u7978\uff0c\u679c\u7136\u5c31\u5728\u90a3\u5929\u51fa\u4e86\u4e8b\u3002\u201d \u3000\u3000\u6768\u5a49\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u4ed6\u662f\u4e00\u519b\u4e3b\u5e05\uff0c\u96be\u9053\u6562\u516c\u7136\u62a2\u4e86\u4f60\u4e48\uff1f\u201d \u3000\u3000\u5361\u6d1b\u4e1d\u9053\uff1a\u201c\u4ed6\u6043\u7740\u662f\u9a78\u9a6c\u8eab\u4efd\uff0c\u9664\u4e86\u6015\u5927\u6c57\u548c\u4ed6\u7684\u672a\u5a5a\u59bb\u5b50\u4e4b\u5916\uff0c\u4ed6\u8fd8\u6015\u8c01\uff1f\u519b\u4e2d\u7684 \u7eaa\u5f8b\u662f\u4ed6\u7528\u6765\u7ea6\u675f\u522b\u4eba\u7684\uff0c\u53ef\u7ba1\u4e0d\u5230\u4ed6\u7684\u8eab\u4e0a\u3002 \u3000\u3000\u201c\u90a3\u5929\u6211\u5728\u963f\u76d6\u7684\u8425\u5e10\u51fa\u6765\uff0c\u6070\u5de7\u78b0\u4e0a\u4e86\u4ed6\u51fa\u6765\u5de1\u8425\uff0c\u4ed6\u767b\u65f6\u677f\u8d77\u9762\u5b54\uff0c\u8bf4\u6211\u64c5\u5165\u519b \u8425\uff0c\u5c31\u53eb\u4eba\u628a\u6211\u6293\u8d77\u6765\u4e86\u3002\u5176\u5b9e\u6309\u7167\u6211\u4eec\u8499\u53e4\u7684\u98ce\u4fd7\u3002\u6218\u58eb\u53ea\u8981\u4e0d\u662f\u5728\u6253\u4ed7\u7684\u65f6\u5019\uff0c\u662f\u53ef\u4ee5 \u548c\u4eb2\u4eba\u4f1a\u89c1\u7684\u3002\u4f46\u662f\u4ed6\u86ee\u4e0d\u8bb2\u7406\uff0c\u6211\u5374\u600e\u80fd\u548c\u4ed6\u5206\u8fa9\u3002\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u540e\u6765\u4f60\u600e\u6837\u8131\u8eab\uff1f\u201d \u3000\u3000\u5361\u6d1b\u4e1d\u9053\uff1a\u201c\u6211\u88ab\u56da\u5728\u4ed6\u7684\u5e10\u5e55\u91cc\uff0c\u5927\u7ea6\u8fc7\u4e86\u4e00\u4e2a\u65f6\u8fb0\uff0c\u4ed6\u5c31\u56de\u6765\u5ba1\u6211\u4e86\u3002\u4ed6\u8bf4\u662f\u5ba1 \u6211\uff0c\u53ef\u662f\u5374\u4e0d\u8ba9\u6211\u8bf4\u8bdd\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u4ed6\u8bf4\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5361\u6d1b\u4e1d\u9053\uff1a\u201c\u8bf4\u4e5f\u597d\u7b11\uff0c\u4ed6\u4e00\u56de\u6765\uff0c\u7acb\u5373\u5c31\u6362\u4e86\u4e00\u526f\u9762\u5b54\uff0c\u4e0d\u80fd\u4e0d\u505a\u4e2a\u6837\u5b50\u6293\u4f60\u3002\u4f60\u559c \u6b22\u7559\u5728\u519b\u4e2d\u662f\u4e0d\u662f\uff1f\u6211\u53ef\u4ee5\u9042\u4f60\u5fc3\u613f\u7684\u3002\u4f60\u505a\u6211\u7684\u4f8d\u5973\uff0c\u522b\u4eba\u5c31\u4e0d\u6562\u8d76\u4f60\u8d70\u4e86\u3002\u6253\u4ed7\u7684\u65f6 \u5019\uff0c\u6211\u4e5f\u53ef\u4ee5\u5e26\u7740\u4f60\u8d70\u554a\u3002\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\u505a\u4ed6\u7684\u4f8d\u5973\uff0c\u4e00\u5b9a\u6ca1\u6709\u597d\u4e8b\uff0c\u6211\u575a\u51b3\u4e0d\u7b54\u5e94\u3002\u4ed6\u53d1\u4e86\u6012\uff0c\u8bf4\u662f\u4e0d\u7b54\u5e94\u5c31\u8981\u63a5\u53d7 \u4ec0\u4e48\u519b\u89c4\u53d7\u5211\u3002\u6211\u62fc\u7740\u53d7\u4ed6\u5211\u7f5a\u3001\u7adf\u60f3\u4e0d\u5230\u7684\u5374\u6709\u4e00\u4e2a\u6551\u661f\u6765\u5230\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u90a3\u6551\u661f\u53c8\u662f\u8c01\u3002\u201d \u3000\u3000\u5361\u6d1b\u4e1d\u7b11\u9053\uff1a\u201c\u6b63\u662f\u4ed6\u7684\u672a\u5a5a\u59bb\u5b50\u660e\u6167\u516c\u4e3b\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u5947\u9053\uff1a\u201c\u660e\u6167\u516c\u4e3b\u4e5f\u5230\u4e86\u9f99\u6c99\u5806\u4e86\uff1f\u201d\u5fc3\u60f3\uff1a\u201c\u660e\u6167\u516c\u4e3b\u5bf9\u8fd9\u4e2a\u672a\u5a5a\u592b\u538c\u6076\u975e \u5e38\uff0c\u600e\u7684\u80af\u53bb\u4f1a\u4ed6\uff1f\u800c\u4e14\u5979\u90a3\u665a\u548c\u6211\u5206\u624b\uff0c\u8bf4\u662f\u8981\u56de\u8f6c\u548c\u6797\u7684\uff0c\u53c8\u600e\u80fd\u8fd9\u6837\u5feb\u5c31\u5230\u4e86\u9f99\u6284 \u5806\u3002\u201d \u3000\u3000\u5361\u6d1b\u4e1d\u9053\uff1a\u201c\u660e\u6167\u516c\u4e3b\u5c1a\u672a\u4eb2\u6765\uff0c\u53ea\u662f\u5979\u5c06\u8981\u5230\u6765\u7684\u6d88\u606f\uff0c\u5c31\u628a\u8fd9\u9547\u56fd\u738b\u5b50\u5413\u4f4f\u4e86\u3002\u201d \u3000\u3000\u5361\u6d1b\u4e1d\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u8fdb\u6765\u62a5\u544a\u8fd9\u4e2a\u6d88\u606f\u662f\u4ed6\u7684\u2018\u8d3a\u5974\u2019\uff08\u76f8\u5f53\u4e8e\u53c2\u8c0b\u4e4b\u7c7b\u7684\u201c\u5e08 \u7237\u201d\uff09\uff0c\u4ed6\u8fd8\u544a\u8bc9\u4ed6\uff0c\u6211\u662f\u963f\u76d6\u7684\u672a\u5a5a\u59bb\u5b50\uff0c\u4ed6\u624d\u4e0d\u5f97\u5df2\u5c06\u6211\u653e\u4e86\u3002\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u90a3\u4e2a\u2018\u8d3a\u5974\u2019\u8bf4\u4e9b\u4ec0\u4e48\uff1f\u201d\u5979\u5bf9\u4e8e\u6709\u5173\u660e\u6167\u516c\u4e3b\u7684\u6d88\u606f\u9887\u611f\u5174\u8da3\uff0c\u662f\u4ee5\u4e0d\u538c \u5176\u70e6\u5730\u7ec6\u95ee\u3002 \u3000\u3000\u5361\u6d1b\u4e1d\u9053\uff1a\u201c\u4ed6\u8bf4\u5df2\u7ecf\u63a5\u5230\u540e\u65b9\u7684\u5feb\u9a6c\u9a70\u62a5\uff0c\u8bf4\u662f\u5927\u6c57\u7684\u5927\u519b\u5df2\u7ecf\u4ece\u548c\u6797\u5f00\u51fa\uff0c\u660e\u6167\u516c \u4e3b\u968f\u884c\u3002\u800c\u4e14\u660e\u6167\u516c\u4e3b\u6240\u7387\u9886\u7684\u4e00\u652f\u5973\u5175\u8fd8\u662f\u5148\u884c\u5462\uff0c\u636e\u8bf4\u5018\u82e5\u660e\u5929\u4e0d\u6765\uff0c\u540e\u5929\u4e5f\u4f1a\u6765\u5230\u9f99 \u6c99\u5806\u7684\u3002\u201d \u3000\u3000\u6768\u5a49\u7b11\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u90a3\u9547\u56fd\u738b\u5b50\u95fb\u4e4b\u4e27\u80c6\u4e86\u3002\u201d \u3000\u3000\u5361\u6d1b\u4e1d\u9053\uff1a\u201c\u90a3\u4e2a\u2018\u8d3a\u5974\u2019\u4e8e\u662f\u4e58\u673a\u529d\u544a\u4ed6\u7684\u4e3b\u5e05\u3002\u4ed6\u8bf4\uff1a\u6b64\u4e8b\u82e5\u662f\u7ed9\u660e\u6167\u516c\u4e3b\u77e5\u9053\uff0c \u53ea\u6015\u5143\u5e05\u6709\u6240\u4e0d\u4fbf\uff1b\u4e8c\u6765\u8fd9\u5973\u5b50\u7684\u672a\u5a5a\u592b\u662f\u963f\u76d6\uff0c\u963f\u76d6\u662f\u6709\u540d\u7684\u6b66\u571f\uff0c\u5143\u5e05\u82e5\u662f\u5f3a\u593a\u4e86\u4ed6\u7684 \u672a\u5a5a\u59bb\uff0c\u53ea\u6015\u519b\u4e2d\u5c06\u58eb\u4e0d\u670d\uff0c\u5018\u82e5\u6fc0\u8d77\u54d7\u53d8\uff0c\u8fd9\u5c31\u5bf9\u5143\u5e05\u66f4\u4e0d\u5229\u4e86\u3002\u90a3\u4e11\u516b\u602a\u542c\u4e86\u4ed6\u7684\u2018\u8d3a \u5974\u2019\u8fd9\u756a\u8bf4\u8bdd\uff0c\u5413\u5f97\u9762\u4e0a\u4e00\u9635\u9752\u4e00\u9635\u7ea2\uff0c\u4e0d\u5f97\u5df2\u624d\u628a\u6211\u653e\u4e86\u3002\u540e\u6765\u90a3\u2018\u8d3a\u5974\u2019\u8fd8\u4eb2\u81ea\u9001\u6211\u51fa \u8425\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u8fd9\u2018\u8d3a\u5974\u2019\u5012\u662f\u4e2a\u597d\u4eba\u3002\u201d \u3000\u3000\u5361\u6d1b\u4e1d\u9053\uff1a\u201c\u4ed6\u662f\u963f\u76d6\u7684\u597d\u670b\u53cb\uff0c\u4e0d\u8fc7\u90a3\u4e11\u516b\u602a\u4e0d\u77e5\u9053\u7f62\u4e86\u3002\u51fa\u8425\u4e4b\u65f6\u4ed6\u68a2\u6084\u544a\u8bc9\u6211\uff0c \u8bf4\u662f\u963f\u76d6\u5df2\u7ecf\u77e5\u9053\u8fd9\u4ef6\u4e8b\uff0c\u662f\u963f\u76d6\u592e\u6c42\u4ed6\u6765\u6253\u6551\u6211\u7684\uff0c\u4ed6\u6709\u610f\u5938\u5f20\uff0c\u5176\u5b9e\u660e\u6167\u516c\u4e3b\u7684\u8fd9\u652f\u5973 \u5175\u8fd8\u5728\u6c99\u6f20\u4e4b\u4e2d\uff0c\u7edd\u4e0d\u53ef\u80fd\u5728\u660e\u540e\u5929\u5c31\u5230\u8fbe\u9f99\u6c99\u5806\u7684\u3002\u201d \u3000\u3000\u6768\u5a49\u5ffd\u9053\uff0c\u201c\u8fd9\u4e2a\u9547\u56fd\u738b\u5b50\u751f\u5f97\u5f88\u4e11\u964b\u5417\uff1f\u4f60\u53eb\u4ed6\u4e11\u516b\u602a\u3002\u201d \u3000\u3000\u5361\u6d1b\u4e1d\u9053\uff1a\u201c\u9762\u5982\u9505\u5e95\uff0c\u4e24\u9f7f\u64a9\u7259\uff0c\u6d3b\u50cf\u4e00\u5934\u9ed1\u718a\u3002\u201d \u3000\u3000\u6768\u5a49\u53f9\u9053\uff1a\u201c\u660e\u6167\u516c\u4e3b\u4e5f\u771f\u53ef\u601c\uff0c\u5979\u90a3\u6837\u82b1\u6735\u4f3c\u7684\u7f8e\u4eba\u513f\uff0c\u5ac1\u7684\u4e08\u592b\u5374\u662f\u4e2a\u4e11\u516b\u602a\u3002\u4e11 \u516b\u602a\u8fd8\u4e0d\u6253\u7d27\uff0c\u884c\u4e3a\u53c8\u662f\u90a3\u6837\u7684\u5351\u9119\u9f8c\u9f8a\u3002\u602a\u4e0d\u5f97\u5979\u8981\u53e6\u9009\u610f\u4e2d\u4eba\u4e86\u3002\u201d\u8bf4\u7f62\u5bf9\u674e\u601d\u5357\u5fae\u5fae \u4e00\u7b11\u3002 \u3000\u3000\u5361\u6d1b\u4e1d\u4e0d\u77e5\u674e\u601d\u5357\u4e0e\u660e\u6167\u516c\u4e3b\u4e4b\u95f4\u7684\u4e8b\u60c5\uff0c\u63d2\u53e3\u8bf4\u9053\uff1a\u201c\u662f\u5440\uff0c\u53bb\u5e74\u516c\u4e3b\u8bb8\u8d60\u90a3\u4e11\u516b\u602a \u7684\u6d88\u606f\u4f20\u51fa\uff0c\u6211\u4eec\u8349\u539f\u4e0a\u7684\u5973\u5b50\u4e5f\u90fd\u4e3a\u5979\u62b1\u5c48\u5462\u3002\u4eba\u4eba\u90fd\u8bf4\u8fd9\u662f\u4e00\u6735\u9c9c\u82b1\u63d2\u5728\u725b\u7caa\u4e0a\u3002\u201d\u674e \u601d\u5357\u9762\u4e0a\u4e00\u7ea2\uff0c\u626d\u8f6c\u8bdd\u9898\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u521a\u624d\u8bf4\uff0c\u8fd9\u9547\u56fd\u738b\u5b50\u5f88\u607c\u6068\u6211\uff0c\u4f60\u662f\u600e\u4e48\u77e5\u9053\u7684\uff1f\u201d \u3000\u3000\u5361\u6d1b\u4e1d\u9053\uff1a\u201c\u6211\u5fd8\u4e86\u544a\u8bc9\u4f60\u4e00\u4ef6\u4e8b\u60c5\uff0c\u5f53\u90a3\u4e11\u516b\u602a\u7684\u2018\u8d3a\u5974\u2019\u4e3a\u6211\u8bf4\u60c5\u4e4b\u540e\uff0c\u4ed6\u8fd8\u53e6\u6709 \u4e00\u4ef6\u516c\u4e8b\u7980\u544a\u90a3\u4e11\u516b\u602a\uff0c\u5f53\u65f6\u6211\u8fd8\u6ca1\u6709\u51fa\u8425\uff0c\u90fd\u7ed9\u6211\u542c\u89c1\u4e86\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u4ec0\u4e48\u516c\u4e8b\uff0c\u662f\u548c\u6211\u6709\u5173\u7684\u4e48\uff1f\u201d \u3000\u3000\u5361\u6cbb\u4e1d\u9053\uff1a\u201c\u6b63\u662f\uff0c\u90a3\u2018\u8d3a\u5974\u2019\u62ff\u51fa\u4e00\u5f20\u753b\u56fe\uff0c\u56fe\u4e0a\u7684\u90a3\u4e2a\u4eba\u50cf\uff0c\u6b63\u662f\u4f60\u674e\u516c\u5b50\u3002\u6211\u90a3 \u5929\u89c1\u8fc7\u4f60\u548c\u4e24\u4e2a\u91d1\u5e10\u6b66\u58eb\u5728\u4e00\u8d77\u7684\uff0c\u6240\u4ee5\u4e00\u89c1\u5c31\u8ba4\u5f97\u4e86\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u5403\u4e86\u4e00\u60ca\uff0c\u8fde\u5fd9\u95ee\u9053\uff1a\u201c\u4ed6\u62ff\u6bd4\u8fd9\u5f20\u753b\u56fe\uff0c\u600e\u4e48\u8bf4\uff1f\u201d \u3000\u3000\u5361\u6d1b\u4e1d\u9053\uff1a\u201c\u90a3\u2018\u8d3a\u5974\u2019\u8bf4\uff0c\u56fe\u4e0a\u8fd9\u4e2a\u4eba\u662f\u5927\u6c57\u6240\u8981\u7684\u4eba\uff0c\u8981\u90a3\u4e11\u516b\u602a\u7559\u5fc3\uff0c\u82e5\u662f\u53d1\u73b0 \u6b64\u4eba\uff0c\u52a1\u5fc5\u628a\u4ed6\u6349\u4f4f\uff1b\u4ed6\u8fd8\u8bf4\u5230\u6709\u4e00\u4e2a\u505a\u5b98\u7684\u6c49\u4eba\uff0c\u6709\u4e00\u6279\u8d35\u91cd\u7684\u793c\u7269\u8981\u9001\u7ed9\u4e11\u516b\u602a\uff0c\u4e3a\u7684 \u4e5f\u662f\u8981\u4ed6\u5e2e\u5fd9\u8fd9\u4ef6\u4e8b\u60c5\u3002\u90a3\u6c49\u4eba\u7684\u540d\u5b57\uff0c\u4ed6\u548c\u90a3\u4e11\u516b\u602a\u662f\u54ac\u7740\u8033\u6735\u8bf4\u7684\uff0c\u6211\u6ca1\u542c\u89c1\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u60f3\u5fc5\u662f\u4f59\u4e00\u4e2d\u8fd9\u53ae\uff0c\u5728\u6211\u90a3\u65e5\u9003\u51fa\u548c\u6797\u4e4b\u540e\uff0c\u4fbf\u7acb\u5373\u8bf7\u6c42\u6210\u5409\u601d\u6c57 \u4e0b\u7f09\u6355\u4ee4\u4e86\u3002\u6240\u4ee5\u65e0\u987b\u7b49\u5f85\u5728\u963f\u513f\u683c\u5c71\u53d7\u4e86\u4f24\u7684\u90a3\u4e2a\u54f2\u522b\u56de\u5230\u548c\u6797\uff0c\u4ed6\u5df2\u5148\u5411\u9547\u56fd\u738b\u5b50\u6697\u901a \u6d88\u606f\u3002\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u5927\u6c57\u5df2\u7136\u5429\u5490\u4e0b\u6765\uff0c\u8981\u90a3\u4e11\u516b\u602a\u6263\u4eba\u7684\u4e86\u3002\u96be\u9053\u90a3\u4e2a\u505a\u5b98\u6c49\u4eba\u8fd8\u6015\u4ed6\u4e0d\u9075\u5927 \u6c57\u4e4b\u547d\uff0c\u8981\u53e6\u5916\u9001\u793c\u8bf7\u6258\uff1f\u201d \u3000\u3000\u5361\u6d1b\u4e1d\u9053\uff1a\u201c\u90a3\u4e2a\u6c49\u4eba\u8981\u6c42\u90a3\u4e11\u516b\u602a\u4e00\u4e8b\uff0c\u5018\u82e5\u674e\u516c\u5b50\u5df2\u7ecf\u7ed9\u4ed6\u6263\u7559\u7684\u8bdd\uff0c\u4ed6\u6c42\u90a3\u4e11\u516b \u602a\u5728\u628a\u4f60\u9001\u7ed9\u5927\u6c57\u4e4b\u524d\uff0c\u5148\u8ba9\u4ed6\u89c1\u4e00\u89c1\u4f60\u3002\u674e\u516c\u5b50\uff0c\u90a3\u6c49\u4eba\u662f\u4f60\u7684\u4ec0\u4e48\u4eba\uff1f\u4f60\u4e0d\u662f\u6765\u5e2e\u5fd9\u6211 \u4eec\u5927\u6c57\u7684\u5417\uff1f\u4f55\u4ee5\u53c8\u5077\u5077\u8dd1\u51fa\u4e86\u548c\u6797\u5462\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u90a3\u6c49\u4eba\u662f\u6211\u7684\u4ec7\u5bb6\uff0c\u4ed6\u8981\u9677\u5bb3\u6211\u7684\u3002\u201d \u3000\u3000\u5361\u6d1b\u4e1d\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u65e2\u662f\u8fd9\u6837\uff0c\u674e\u516c\u5b50\uff0c\u4f60\u4eec\u53ef\u4e0d\u80fd\u5f80\u5357\u8d70\u4e86\u3002\u201d\u5f80\u5357\u8d70\u4e43\u662f\u91d1 \u56fd\u56fd\u5883\uff0c\u9547\u56fd\u738b\u5b50\u7edf\u7387\u7684\u5927\u519b\u6b63\u5728\u5b88\u7740\u9f99\u6c99\u5806\u3002 \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u591a\u8c22\u4f60\u7684\u6307\u70b9\u3002\u201d\u3002 \u3000\u3000\u5361\u6d1b\u4e1d\u9053\uff1a\u201c\u674e\u516c\u5b50\uff0c\u4f60\u592a\u5ba2\u6c14\u4e86\uff0c\u4f60\u662f\u6211\u7684\u6551\u547d\u6069\u4eba\uff0c\u6211\u90fd\u672a\u66fe\u591a\u8c22\u4f60\u5462\u3002\u201d\u63a5\u7740\u544a \u8bc9\u674e\u601d\u5357\uff0c\u5979\u662f\u5982\u4f55\u78b0\u4e0a\u90a3\u4e24\u4e2a\u897f\u590f\u6b66\u58eb\u4e4b\u4e8b\u3002\u201c\u6211\u79bb\u5f00\u9f99\u6c99\u5806\uff0c\u4eca\u5929\u4e00\u65e9\uff0c\u521a\u521a\u8d70\u5230\u4e0e\u897f \u590f\u76f8\u90bb\u7684\u8fb9\u5883\uff0c\u5c31\u78b0\u4e0a\u4e86\u90a3\u4e24\u4e2a\u5f3a\u76d7\uff0c\u4ed6\u4eec\u662f\u4ece\u540e\u9762\u8ffd\u6765\u7684\uff0c\u4e00\u89c1\u6211\u5c31\u628a\u6211\u6293\u8d77\u6765\uff0c\u5411\u6211\u523a \u63a2\u519b\u60c5\uff0c\u6211\u4e0d\u80af\u8bf4\uff0c\u4ed6\u4eec\u5c31\u628a\u6211\u62bc\u5230\u897f\u590f\u7684\u5730\u65b9\u6765\u4e86\u3002\u5e78\u597d\u78b0\u4e0a\u4e86\u4f60\u4eec\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u897f\u590f\u6b66\u571f\u662f\u4ece\u5979\u7684\u540e\u9762\u8ffd\u6765\uff0c\u8fd9\u4e48\u8bf4\uff0c\u8fd9\u4e24\u4e2a\u4eba\u662f\u5df2\u7ecf\u8fdb\u5165 \u91d1\u56fd\u7684\u56fd\u5883\u7684\u4e86\uff0c\u60f3\u5fc5\u4ed6\u4eec\u80c6\u5c0f\uff0c\u77e5\u9053\u8499\u53e4\u7684\u5927\u519b\u5728\u9f99\u6c99\u5806\uff0c\u4e0d\u6562\u9760\u8fd1\uff0c\u5c31\u56de\u6765\u4e86\u3002\u53ef\u662f\u897f \u590f\u4e3a\u4ec0\u4e48\u8981\u6d3e\u4eba\u523a\u63a2\u519b\u60c5\uff0c\u96be\u9053\u4ed6\u4eec\u662f\u542c\u5230\u4ec0\u4e48\u98ce\u58f0\uff0c\u63d0\u9632\u8499\u53e4\u5927\u519b\u4f1a\u79fb\u5e08\u897f\u5411\u4e48\uff1f\u201d \u3000\u3000\u201c\u8fd8\u662f\u8d76\u5feb\u56de\u53bb\u5427\u3002\u8fd9\u5339\u9a6c\u9001\u7ed9\u4f60\uff0c\u6055\u6211\u4eec\u4e0d\u9001\u4f60\u4e86\u3002\u201d\u897f\u590f\u662f\u5f31\u56fd\uff0c\u6768\u5a49\u6599\u60f3\u90a3\u4e24\u4e2a \u897f\u590f\u6b66\u58eb\uff0c\u5373\u4f7f\u53bb\u800c\u590d\u56de\uff0c\u4e5f\u7edd\u4e0d\u6562\u8ffd\u5165\u8499\u53e4\u5883\u5185\uff0c\u662f\u4ee5\u653e\u5fc3\u8ba9\u5361\u6d1b\u4e1d\u5355\u72ec\u56de\u53bb\u3002 \u3000\u3000\u5361\u6d1b\u4e1d\u7684\u5750\u9a91\u5df2\u7ecf\u7ed9\u90a3\u9ed1\u8863\u6b66\u58eb\u7528\u98de\u5200\u4f24\u4e86\u4e00\u817f\uff0c\u4e0d\u80fd\u518d\u7528\uff0c\u6545\u6b64\u6768\u5a49\u9001\u5979\u4e00\u5339\u9a8f\u9a6c\u3002 \u8fd9\u672c\u6765\u662f\u660e\u6167\u516c\u4e3b\u9001\u7ed9\u674e\u601d\u5357\u7684\uff0c\u5979\u4e00\u5171\u9001\u4e86\u4e09\u5339\u9a8f\u9a6c\uff0c\u5982\u4eca\u5df2\u8fc7\u4e86\u6c99\u6f20\uff0c\u7528\u4e0d\u7740\u7a7a\u9a91\u66ff \u6362\uff0c\u591a\u51fa\u6765\u7684\u4e00\u5339\u9a6c\uff0c\u6b63\u597d\u8ba9\u7ed9\u5361\u6d1b\u4e1d\u4e58\u5750\u3002 \u3000\u3000\u5361\u6d1b\u4e1d\u9053\u4e86\u4e2a\u8c22\uff0c\u6b63\u8981\u4e0a\u9a6c\uff0c\u5ffd\u5730\u597d\u4f3c\u60f3\u8d77\u4e00\u4e8b\uff0c\u8bf4\u9053\uff1a\u201c\u59d0\u59d0\uff0c\u6211\u4e5f\u6709\u4e00\u70b9\u5c0f\u5c0f\u7684\u793c \u7269\u7ed9\u4f60\u3002\u201d\u6768\u5a49\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u7528\u5ba2\u6c14\uff0c\u6211\u9886\u4f60\u7684\u60c5\u5c31\u662f\uff0c\u4f60\u7559\u7740\u81ea\u5df1\u7528\u5427\u3002\u201d \u3000\u3000\u6768\u5a49\u4ee5\u4e3a\u5979\u662f\u8981\u8d60\u9001\u4ec0\u4e48\u7528\u54c1\uff0c\u53ea\u89c1\u5361\u6d1b\u4e1d\u62ff\u51fa\u7684\u5374\u662f\u4e00\u6761\u624b\u5e15\u3002\u5361\u6d1b\u4e1d\u7b11\u9053\uff1a\u201c\u4f60\u4eec \u6c49\u4eba\u6709\u53e5\u8bf4\u8bdd\uff0c\u53eb\u505a\u7269\u8f7b\u60c5\u4e49\u91cd\uff0c\u8fd9\u6761\u624b\u5e15\u867d\u4e0d\u503c\u94b1\uff0c\u5374\u662f\u6211\u81ea\u5df1\u7ee3\u7684\u3002\u59d0\u59d0\uff0c\u8bf7\u4f60\u6536\u4e0b \u6765\uff0c\u6211\u8fd8\u6709\u4e8b\u60c5\u62dc\u6258\u4f60\u5462\u3002\u201d \u3000\u3000\u6768\u5a49\u63a5\u8fc7\u6765\u4e00\u770b\uff0c\u53ea\u89c1\u624b\u5e15\u4e0a\u7ee3\u7740\u4e00\u53ea\u5140\u9e70\uff0c\u5c55\u7fc5\u9ad8\u98de\uff0c\u795e\u6001\u751f\u52a8\u3002\u6768\u5a49\u7b11\u9053\uff1a\u201c\u7ee3\u5f97 \u771f\u597d\u554a\uff01\u4f60\u7684\u963f\u76d6\u4eba\u79f0\u8349\u539f\u4e4b\u9e70\uff0c\u8fd9\u6761\u624b\u5e15\uff0c\u4f60\u5e94\u8be5\u9001\u7ed9\u4ed6\u624d\u5bf9\u3002\u201d \u3000\u3000\u5361\u6d1b\u4e1d\u9762\u4e0a\u4e00\u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5df2\u7ecf\u9001\u4e86\u540c\u6837\u7684\u4e00\u6761\u624b\u5e15\u7ed9\u4ed6\u4e86\u3002\u8fd9\u6761\u4f60\u7559\u4e0b\uff0c\u4ee5\u540e\u82e5\u6709 \u673a\u4f1a\u78b0\u4e0a\u963f\u76d6\uff0c\u8fd9\u6761\u624b\u5e15\u53ef\u4ee5\u4f5c\u4e3a\u51ed\u8bc1\u3002\u4f60\u4eec\u5018\u82e5\u6709\u4ec0\u4e48\u4e8b\u60c5\u9700\u8981\u963f\u76d6\u53bb\u505a\uff0c\u4ed6\u4e00\u5b9a\u4f1a\u7ed9\u4f60 \u4eec\u5e2e\u5fd9\u3002\u201d \u3000\u3000\u6768\u5a49\u8fd9\u624d\u77e5\u9053\u5361\u6d1b\u4e1d\u9001\u624b\u5e15\u4e4b\u610f\uff0c\u7b11\u9053\uff1a\u201c\u597d\uff0c\u4f46\u613f\u6211\u4eec\u89c1\u5f97\u7740\u4f60\u7684\u963f\u76d6\uff0c\u6211\u4f1a\u628a\u4f60\u7684 \u6d88\u606f\u544a\u8bc9\u4ed6\u7684\u3002\u201d \u3000\u3000\u5361\u6d1b\u4e1d\u8d70\u540e\uff0c\u6768\u5a49\u7b11\u9053\uff1a\u201c\u5357\u54e5\u4f60\u8fd9\u6b21\u53ef\u662f\u53d7\u4e86\u65e0\u5984\u4e4b\u707e\u4e86\u3002\u201d\u674e\u601d\u5357\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a \u201c\u4ec0\u4e48\u65e0\u5984\u4e4b\u707e\uff1f\u201d\u6768\u5a49\u7b11\u9053\uff1a\u201c\u660e\u6167\u516c\u4e3b\u7684\u9a78\u9a6c\uff0c\u65e0\u7aef\u7aef\u5730\u5403\u4f60\u7684\u918b\uff0c\u5982\u4eca\u6ca1\u529e\u6cd5\uff0c\u4e3a\u4e86 \u907f\u514d\u4f60\u53d7\u65e0\u5984\u4e4b\u707e\uff0c\u54b1\u4eec\u53ea\u597d\u53d6\u9053\u897f\u590f\u56de\u56fd\u3002\u201d \u3000\u3000\u4e24\u4eba\u7eed\u5411\u897f\u884c\uff0c\u4e0d\u591a\u4e00\u4f1a\uff0c\u5929\u8272\u5df1\u665a\uff0c\u523a\u9aa8\u7684\u5bd2\u98ce\u9635\u9635\u5439\u6765\uff0c\u6768\u5a49\u611f\u5230\u4e86\u51e0\u5206\u5bd2\u610f\uff0c\u8bf4 \u9053\uff1a\u201c\u8fd9\u91cc\u7684\u5929\u6c14\u53d8\u5f97\u771f\u5feb\uff0c\u4e2d\u5348\u8fd8\u662f\u6c57\u6d41\u5939\u80cc\uff0c\u867d\u7136\u4e0d\u4f3c\u6c99\u6f20\u7684\u9177\u70ed\uff0c\u4e5f\u662f\u53eb\u4eba\u96be\u53d7\uff0c\u60f3 \u4e0d\u5230\u4e00\u5230\u508d\u665a\uff0c\u5c31\u4f3c\u54b1\u4eec\u5bb6\u4e61\u8981\u843d\u96ea\u7684\u5929\u6c14\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u897f\u5317\u8349\u539f\u7684\u6c14\u5019\u5c31\u662f\u8fd9\u6837\u7684\u3002\u8fd9\u91cc\u6709\u4e24\u53e5\u8c1a\u8bed\uff0c\u53eb\u505a\u2018\u671d\u7a7f\u76ae\u8884\u5348\u7a7f\u7eb1\uff0c \u665a\u4e0a\u62b1\u7740\u706b\u7089\u5403\u897f\u74dc\u3002\u2019\u7b49\u4e0b\u5929\u9ed1\u4e86\u8fd8\u8981\u51b7\u5462\uff01\u201d\u6768\u5a49\u9053\uff1a\u201c\u4f60\u53c8\u6ca1\u6709\u6765\u8fc7\uff0c\u4f60\u600e\u4e48\u77e5\u9053 \u7684\uff1f\u201d\u674e\u601d\u5357\u9053\uff1a\u201c\u6211\u5728\u4e66\u4e0a\u8bfb\u8fc7\u7684\u3002\u201d\u6768\u5a49\u9053\uff1a\u201c\u4fd7\u8bed\u8bf4\uff0c\u79c0\u624d\u4e0d\u51fa\u95e8\uff0c\u80fd\u77e5\u5929\u4e0b\u4e8b\u3002\u771f \u662f\u4e0d\u9519\u3002\u201d\u674e\u601d\u5357\u7b11\u9053\uff1a\u201c\u6211\u53c8\u4e0d\u662f\u79c0\u624d\u3002\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u4f60\u8bfb\u7684\u4e66\u53ef\u662f\u4e0d\u5c11\u5440\uff0c\u6211\u4eec\u4e61\u4e0b\u7684\u79c0\u624d\u53ea\u77e5\u9053\u6367\u7740\u4e66\u672c\u8bb2\u9053\uff0c\u8bf4\u8d77\u6765\u8fd8\u8fdc\u8fdc \u6bd4\u4e0d\u4e0a\u4f60\u5462\u3002\u201d\u674e\u601d\u5357\u7b11\u9053\uff1a\u201c\u591a\u627f\u5938\u5956\uff0c\u6211\u4e0d\u8fc7\u559c\u6b22\u8bfb\u8bfb\u6742\u4e66\u800c\u5df2\u3002\u201d \u3000\u3000\u6768\u5a49\u60a0\u7136\u6c89\u601d\uff0c\u534a\u664c\u8bf4\u9053\uff1a\u201c\u6211\u5c0f\u65f6\u5019\uff0c\u6bcd\u4eb2\u6559\u6211\u8bfb\u4e66\u3002\u6211\u5bb6\u867d\u7136\u6bcf\u4e00\u4ee3\u90fd\u662f\u519b\u4eba\uff0c\u5bb6 \u91cc\u7684\u85cf\u4e66\u5374\u4e5f\u4e0d\u5c11\u3002\u53ef\u60dc\u5230\u4e86\u8499\u53e4\u4e4b\u540e\uff0c\u591a\u5e74\u6765\u5df1\u662f\u6ca1\u6709\u78b0\u8fc7\u4e66\u672c\u4e86\u3002\u54b1\u4eec\u5982\u679c\u56de\u5230\u6545\u4e61\uff0c \u4f60\u628a\u4f60\u7684\u6bcd\u4eb2\u63a5\u5230\u6211\u7684\u5bb6\u91cc\u6765\uff0c\u54b1\u4eec\u5728\u4e0b\u96ea\u7684\u591c\u665a\uff0c\u95ed\u95e8\u8bfb\u4e66\uff0c\u4f60\u8bfb\u5f97\u75b2\u5026\u4e86\uff0c\u6211\u6696\u9152\u7ed9\u4f60 \u559d\u3002\u4f60\u8bf4\u597d\u4e0d\u597d\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u4f60\u60f3\u5f97\u5f88\u7f8e\uff0c\u53ea\u4e0d\u77e5\u4ec0\u4e48\u65f6\u5019\u624d\u80fd\u591f\u8fc7\u8fd9\u6837\u592a\u5e73\u7684\u65e5\u5b50\uff1f\u55ef\uff0c\u54b1\u4eec\u8fd8\u662f\u627e \u4e00\u5bb6\u4eba\u6295\u5bbf\u5427\uff01\u522b\u505a\u96ea\u591c\u5173\u95e8\u8bfb\u4e66\u7684\u7f8e\u68a6\u4e86\u3002\u201d \u3000\u3000\u8fd9\u4e00\u4e2a\u6708\u6765\uff0c\u4ed6\u4eec\u5ea6\u6c99\u6f20\uff0c\u8fc7\u8349\u539f\uff01\u4ece\u6ca1\u9047\u4e0a\u4eba\u5bb6\uff0c\u5230\u4e86\u665a\u4e0a\uff0c\u5c31\u642d\u8d77\u5e10\u7bf7\u8fc7\u591c\uff0c\u8fd9\u6837 \u7684\u751f\u6d3b\u4e5f\u8fc7\u60ef\u4e86\u3002\u4e0d\u8fc7\u73b0\u5728\u5230\u4e86\u6709\u4eba\u5bb6\u7684\u5730\u65b9\uff0c\u80fd\u591f\u5b89\u5b89\u9038\u9038\u5730\u7761\u4e00\u89c9\u5c82\u4e0d\u66f4\u597d\uff1f\u800c\u4e14\u4ed6\u4eec \u73b0\u5728\u5df2\u662f\u8e0f\u5165\u4e86\u897f\u590f\u7684\u56fd\u5883\uff0c\u201c\u5165\u5883\u95ee\u4fd7\u201d\uff0c\u4ed6\u4eec\u4e5f\u5e0c\u671b\u80fd\u591f\u627e\u4e2a\u4eba\u8c08\u8bdd\uff0c\u4e8e\u662f\u8d81\u7740\u5929\u8fd8\u672a \u9ed1\uff0c\u627e\u7740\u4e00\u5bb6\u4eba\u5bb6\uff0c\u4fbf\u53bb\u62cd\u95e8\u3002 \u3000\u3000\u4e0d\u6599\u8fd9\u5bb6\u4eba\u5bb6\u5374\u662f\u6ca1\u6709\u4eba\u7684\uff0c\u674e\u601d\u5357\u6572\u95e8\u6572\u4e86\u8bb8\u4e45\uff0c\u4e0d\u89c1\u6709\u4eba\u7b54\u5e94\uff0c\u4ece\u95e8\u7f1d\u5f20\u671b\u8fdb\u53bb\uff0c \u8fd9\u624d\u53d1\u73b0\u91cc\u9762\u56db\u58c1\u7a7a\u7a7a\uff0c\u522b\u8bf4\u662f\u4eba\uff0c\u8fde\u9e21\u72ac\u4e5f\u6ca1\u4e00\u53ea\u3002 \u3000\u3000\u8fd9\u4e2a\u5730\u65b9\u662f\u4e2a\u7a77\u4e61\u50fb\u58e4\uff0c\u4eba\u70df\u7a00\u5c11\uff0c\u627e\u4e86\u8bb8\u4e45\uff0c\u624d\u627e\u5230\u7b2c\u4e8c\u5bb6\u4eba\u5bb6\uff0c\u62cd\u95e8\u4e4b\u540e\uff0c\u4f9d\u7136\u6ca1 \u6709\u4eba\u5e94\u58f0\u3002\u6768\u5a49\u9053\uff1a\u201c\u5947\u602a\uff0c\u600e\u4e48\u90fd\u662f\u6ca1\u6709\u4eba\u7684\u3002\u201d\u5176\u540e\u53c8\u627e\u4e86\u51e0\u5bb6\u4eba\u5bb6\uff0c\u90fd\u662f\u50cf\u7b2c\u4e00\u5bb6\u4e00 \u6837\uff0c\u5c4b\u5185\u65e0\u4eba\uff0c\u552f\u4f59\u56db\u58c1\u3002\u6b64\u65f6\u5df2\u662f\u591c\u6155\u4f4e\u5782\uff0c\u8fc7\u4e86\u521d\u66f4\u4e86\u3002 \u3000\u3000\u6768\u5a49\u6253\u4e86\u4e00\u4e2a\u963f\u568f\uff0c\u8bf4\u9053\uff1a\u201c\u5357\u54e5\uff0c\u4f60\u8bf4\u5f97\u4e0d\u9519\uff0c\u5929\u6c14\u679c\u7136\u662f\u8d8a\u6765\u8d8a\u51b7\u4e86\u3002\u201d\u674e\u601d\u5357 \u9053\uff1a\u201c\u5c4b\u5185\u65e2\u7136\u6ca1\u4eba\uff0c\u54b1\u4eec\u4e0d\u5982\u5c31\u8fdb\u53bb\u4f4f\u4e00\u665a\u5427\u3002\u603b\u6bd4\u4f4f\u5e10\u84ec\u597d\u4e9b\uff0c\u6709\u539a\u5b9e\u7684\u5899\u58c1\u53ef\u4ee5\u6321 \u98ce\u3002\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u4e0d\u597d\uff0c\u4e0d\u5f97\u4e3b\u4eba\u5141\u8bb8\uff0c\u54b1\u4eec\u600e\u597d\u968f\u4fbf\u4f4f\u4eba\u5bb6\u7684\u623f\u5b50\uff1f\u800c\u4e14\u4e07\u4e00\u6709\u4eba\u56de\u6765\uff0c\u90a3 \u5c31\u66f4\u5c34\u5c2c\u4e86\u3002\u201d\u5979\u662f\u540d\u95e8\u4e4b\u5973\uff0c\u81ea\u5e7c\u53d7\u7236\u6bcd\u7684\u85b0\u9676\uff0c\u867d\u7136\u662f\u5728\u8352\u5c71\u8fc7\u4e86\u51e0\u5e74\uff0c\u5bf9\u4e8e\u5b88\u201c\u793c\u201d \u5b88\u201c\u6cd5\u201d\u8fd8\u662f\u76f8\u5f53\u91cd\u89c6\u7684\u3002 \u3000\u3000\u674e\u601d\u5357\u7b11\u9053\uff1a\u201c\u6211\u53ea\u662f\u6015\u4f60\u7740\u5bd2\u3002\u597d\u5427\uff0c\u4f60\u65e2\u7136\u62d8\u6ce5\u793c\u6cd5\uff0c\u90a3\u54b1\u4eec\u5c31\u627e\u7740\u5730\u65b9\u642d\u5e10\u7bf7 \u5427\u3002\u5176\u5b9e\u5728\u6c5f\u6e56\u95ef\u8361\uff0c\u662f\u65e0\u987b\u90a3\u4e48\u56fa\u6267\u7684\u3002\u201d \u3000\u3000\u6768\u5a49\u7b11\u9053\uff1a\u201c\u4e0d\u5f97\u5df2\u65f6\u81ea\u662f\u4e0d\u59a8\u968f\u4fbf\u4e00\u4e9b\uff0c\u4f46\u73b0\u5728\u54b1\u4eec\u4e0d\u67f1\u623f\u5b50\uff0c\u90a3\u4e5f\u7b97\u4e0d\u4e86\u4ec0\u4e48\u5440\u3002 \u53ea\u4e0d\u8fc7\u6ca1\u6709\u90a3\u4e48\u8212\u670d\u7f62\u4e86\u3002\u201d \u3000\u3000\u5929\u4e0a\u5f64\u4e91\u56db\u5e03\uff0c\u591c\u98ce\u547c\u547c\uff0c\u5e76\u6ca1\u6709\u4e0b\u96ea\uff0c\u5374\u6bd4\u4e0b\u96ea\u7684\u5929\u6c14\u66f4\u51b7\u3002\u674e\u601d\u5357\u548c\u6768\u5a49\u60f3\u627e\u4e00\u4e2a \u53ef\u4ee5\u907f\u98ce\u7684\u5730\u65b9\u642d\u5e10\u7bf7\uff0c\u4e00\u76f4\u6ca1\u6709\u627e\u4e2a\u5408\u9002\u7684\u5730\u65b9\u3002\u674e\u601d\u5357\u89c1\u6768\u5a49\u51b7\u63ed\u53d1\u6296\uff0c\u597d\u751f\u601c\u60dc\uff0c\u8bf4 \u9053\uff1a\u201c\u8fd9\u6837\u7684\u5929\u6c14\uff0c\u53ea\u6015\u8fd8\u4f1a\u4e0b\u96e8\u5462\u3002\u8fd8\u662f\u627e\u4e2a\u4eba\u5bb6\u7684\u597d\u3002\u201d \u3000\u3000\u6768\u5a49\u773c\u5149\u4e00\u77a5\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u770b\uff0c\u90a3\u91cc\u6709\u706b\u5149\u900f\u51fa\uff0c\u53ef\u80fd\u662f\u6709\u4eba\u7684\u4eba\u5bb6\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u559c\u51fa\u671b\u5916\uff0c\u6025\u6025\u5fd9\u5fd9\u5411\u90a3\u5bb6\u4eba\u5bb6\u8dd1\u53bb\u3002\u53ea\u89c1\u7bf1\u7b06\u865a\u63a9\uff0c\u91cc\u9762\u70e7\u7740\u4e00\u5806\u67f4\u706b\uff0c\u5374\u4e0d \u89c1\u6709\u4eba\u3002\u674e\u601d\u5357\u8fdb\u53bb\u4e00\u770b\uff0c\u5374\u539f\u6765\u662f\u4e2a\u78e8\u623f\uff0c\u91cc\u9762\u6709\u98ce\u8f66\u3001\u6709\u77f3\u78e8\uff0c\u8fd8\u5806\u6709\u8bb8\u591a\u67f4\u8349\uff0c\u53ea\u662f \u6ca1\u6709\u4eba\u3002\u6b63\u662f\uff1a \u3000\u3000\u5bd2\u591c\u8352\u6751\u6c42\u4e00\u5bbf\uff0c\u8c01\u77e5\u6a2a\u7978\u53c8\u98de\u6765\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b\u2014\u2014\u7b2c\u5341\u56de\u3000\u8352\u6751\u5348\u591c\u60ca\u5947\u53d8\u3000\u5de7\u8ba1\u91d1\u724c\u9000\u654c\u9a91 \u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b \u7b2c\u5341\u56de\u3000\u8352\u6751\u5348\u591c\u60ca\u5947\u53d8\u3000\u5de7\u8ba1\u91d1\u724c\u9000\u654c\u9a91 \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u5947\u602a\uff0c\u706b\u672a\u7184\u706d\uff0c\u4eba\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u201d\u6768\u5a49\u8bf4\u9053\uff1a\u201c\u5c4b\u91cc\u5806\u6ee1\u67f4\u8349\uff0c\u6ca1\u4eba\u770b \u5b88\uff0c\u8fd9\u4e00\u5806\u706b\u53ef\u662f\u5f88\u5bb9\u6613\u60f9\u8d77\u706b\u707e\u554a\uff01\u201d\u674e\u601d\u5357\u7b11\u9053\uff1a\u201c\u90a3\u54b1\u4eec\u5c31\u66ff\u4ed6\u770b\u5b88\u5427\u3002\u201d \u3000\u3000\u5728\u897f\u5317\u7684\u4e00\u4e9b\u5c0f\u6751\u5e84\uff0c\u5f80\u5f80\u4e00\u4e2a\u6751\u5b50\u91cc\u53ea\u6709\u4e00\u4e2a\u78e8\u623f\uff0c\u5c5e\u4e8e\u6751\u6c11\u516c\u6709\uff0c\u4f9d\u7167\u5f53\u5730\u7684\u98ce \u4fd7\uff0c\u5f02\u4e61\u4eba\u5728\u78e8\u623f\u501f\u5bbf\uff0c\u662f\u65e0\u987b\u8bf7\u51c6\u4e3b\u4eba\u7684\u3002\u6768\u5a49\u6b63\u81ea\u51b7\u5f97\u96be\u53d7\uff0c\u89c1\u4e86\u8fd9\u4e00\u5806\u706b\uff0c\u4e5f\u5c31\u4e50\u5f97 \u5750\u4e0b\u6765\u4e86\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u53cd\u6b63\u8fd9\u78e8\u623f\u91cc\u6709\u4eba\uff0c\u5f85\u90a3\u4eba\u56de\u6765\uff0c\u6211\u4eec\u5411\u4ed6\u89e3\u91ca\uff0c\u60f3\u5fc5\u4ed6\u4e5f\u4e0d\u4f1a\u602a \u8d23\u6211\u4eec\u64c5\u5165\u78e8\u623f\u7684\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u628a\u6c34\u58f6\u653e\u5728\u706b\u4e0a\uff0c\u628a\u4e00\u58f6\u51b0\u51b7\u7684\u6c34\u716e\u6cb8\uff0c\u7b11\u9053\uff1a\u201c\u5bd2\u591c\u5ba2\u6765\u8336\u5f53\u9152\uff0c\u54b1\u4eec\u8fde\u8336\u53f6 \u4e5f\u6ca1\u6709\uff0c\u53ea\u597d\u7528\u6c34\u6765\u5f53\u9152\u4e86\u3002\u8bf7\u554a\uff0c\u8bf7\u554a\uff01\u201d\u6768\u5a49\u7b11\u9053\uff1a\u201c\u6211\u53ef\u4e0d\u662f\u5ba2\u4eba\u3002\u201d\u559d\u4e0b\u51e0\u53e3\u5f00 \u6c34\uff0c\u9001\u4e0b\u5e72\u7cae\uff0c\u6d51\u8eab\u6696\u70d8\u70d8\u7684\u597d\u4e0d\u8212\u670d\u3002 \u3000\u3000\u6768\u5a49\u6253\u4e86\u4e2a\u54c8\u6b20\uff0c\u8bf4\u9053\uff0c\u201c\u6211\u53ef\u771f\u662f\u6709\u70b9\u60f3\u7761\u4e86\u3002\u201d\u674e\u601d\u5357\u9053\uff1a\u201c\u90a3\u5c31\u7761\u5427\u3002\u6211\u66ff\u4f60\u5b88 \u591c\u3002\u201d\u6768\u5a49\u9053\uff1a\u201c\u90a3\u4eba\u8fd8\u6ca1\u56de\u6765\uff0c\u6211\u7761\u7740\u4e86\u4e0d\u597d\u770b\u3002\u5357\u54e5\uff0c\u4f60\u627e\u70b9\u6709\u8da3\u7684\u4e8b\u60c5\u548c\u6211\u8bf4\uff0c\u6211\u5c31 \u4e0d\u60f3\u7761\u4e86\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u7b11\u9053\uff1a\u201c\u6211\u77e5\u9053\u7684\u53ea\u662f\u4e00\u4e9b\u6c5f\u6e56\u4e0a\u780d\u780d\u6740\u6740\u7684\u4e8b\u60c5\uff0c\u8bf4\u8d77\u6765\u53ef\u5e76\u4e0d\u600e\u6837\u6709\u8da3\u3002\u53ef \u60dc\u6211\u53c8\u4e0d\u4f1a\u8bf4\u6545\u4e8b\uff0c\u54a6\uff0c\u5947\u602a\uff0c\u90a3\u4eba\u600e\u6837\u8fd8\u672a\u56de\u6765\uff1f\u201d \u3000\u3000\u4e00\u9635\u51b7\u98ce\u5439\u8fdb\u78e8\u623f\uff0c\u53ea\u89c1\u5916\u9762\u5df2\u5728\u98d8\u7740\u9e45\u6bdb\u822c\u7684\u96ea\u82b1\u3002\u6768\u5a49\u9053\uff1a\u201c\u8fd9\u91cc\u7684\u5929\u6c14\u771f\u53e4\u602a\uff0c \u679c\u7136\u4e0b\u8d77\u96ea\u6765\u3002\u201d\u674e\u601d\u5357\u7b11\u9053\uff1a\u201c\u671d\u7a7f\u68c9\u8884\u5348\u7a7f\u7eb1\uff0c\u665a\u4e0a\u62b1\u7740\u706b\u7089\u5403\u897f\u74dc\u3002\u73b0\u5728\u706b\u662f\u6709\u4e86\uff0c \u5c31\u53ef\u60dc\u6ca1\u6709\u897f\u74dc\u3002\u201d\u6768\u5a49\u9053\uff1a\u201c\u90a3\u5c31\u559d\u6c34\u5427\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6c34\u53ef\u5f53\u9152\uff0c\u4e5f\u53ef\u5f53\u897f\u74dc\u3002\u4f46\u4e5f\u522b\u559d\u5f97\u592a\u591a\u4e86\uff0c\u660e\u5929\u8def\u4e0a\u8fd8\u8981\u559d \u5462\uff0c\u54b1\u4eec\u4eba\u751f\u8def\u4e0d\u719f\uff0c\u4ed3\u5352\u4e4b\u95f4\uff0c\u672a\u5fc5\u627e\u5f97\u7740\u6c34\u6e90\u3002\u201d\u6768\u5a49\u9053\uff1a\u201c\u6015\u4ec0\u4e48\uff0c\u4f60\u770b\u96ea\u8d8a\u4e0b\u8d8a\u5927 \u4e86\uff0c\u559d\u5e72\u4e86\u6c34\u56ca\u7684\u6c34\uff0c\u54b1\u4eec\u8fd8\u53ef\u4ee5\u559d\u96ea\u6c34\u3002\u201d \u3000\u3000\u4e24\u4eba\u6709\u4e00\u642d\u6ca1\u4e00\u642d\u7684\u95f2\u804a\uff0c\u5fc3\u91cc\u53ef\u90fd\u662f\u60f3\u7740\u540c\u4e00\u7684\u95ee\u9898\uff1a\u201c\u5929\u4e0a\u4e0b\u7740\u8fd9\u4e48\u5927\u7684\u96ea\uff0c\u90a3\u4eba \u4e3a\u4ec0\u4e48\u8fd8\u4e0d\u56de\u6765\u70e4\u706b\u3002\u201d \u3000\u3000\u6768\u5a49\u5ffd\u9053\uff1a\u201c\u6211\u5012\u60f3\u8d77\u4e86\u4e00\u4ef6\u6709\u8da3\u7684\u4e8b\u60c5\u3002\u201d\u674e\u601d\u5357\u9053\uff1a\u201c\u8bf4\u6765\u542c\u542c\u3002\u201d\u6768\u5a49\u9053\uff1a\u201c\u5361 \u6d1b\u4e1d\u628a\u90a3\u9547\u56fd\u738b\u5b50\u5f62\u5bb9\u4f5c\u4e11\u516b\u602a\uff0c\u660e\u6167\u516c\u4e3b\u90a3\u6837\u53c8\u7f8e\u8c8c\u53c8\u5a07\u7eb5\u7684\u5973\u5b50\uff0c\u600e\u80af\u5ac1\u7ed9\u4ed6\uff1f\u201d\u674e\u601d \u5357\u9053\uff1a\u201c\u8fd9\u6709\u4ec0\u4e48\u597d\u7b11\uff1f\u201d\u505c\u4e86\u4e00\u4f1a\u8bf4\u9053\uff1a\u201c\u5979\u662f\u8feb\u4e8e\u7236\u547d\uff0c\u4e0d\u60f3\u5ac1\u53ea\u6015\u4e5f\u5f97\u8981\u5ac1\u3002\u201d\u6768\u5a49 \u9053\uff1a\u201c\u662f\u5440\u3002\u6240\u4ee5\u6211\u62c5\u5fc3\u5979\u82e5\u5728\u6d1e\u623f\u4e4b\u591c\u53d1\u8d77\u4e86\u813e\u6c14\u6765\uff0c\u53ef\u4e0d\u662f\u5927\u5927\u7684\u7b11\u8bdd\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9ed8\u7136\u4e0d\u8bed\uff0c\u6768\u5a49\u4f3c\u4e4e\u4e5f\u89c9\u5f97\u8fd9\u4e2a\u201c\u7b11\u8bdd\u201d\u5e76\u4e0d\u597d\u7b11\uff0c\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u660e\u6167\u516c\u4e3b\u5176 \u5b9e\u4e5f\u5f88\u53ef\u601c\uff0c\u4e08\u592b\u76f8\u8c8c\u4e11\u90a3\u8fd8\u4e0d\u8981\u7d27\uff0c\u504f\u504f\u8fd9\u4e2a\u9547\u56fd\u738b\u5b50\u53c8\u662f\u4e2a\u9119\u592b\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u52c9\u5f3a\u7b11\u9053\uff1a\u201c\u4f60\u8981\u8bf4\u6709\u8da3\u7684\u4e8b\u60c5\uff0c\u4f60\u81ea\u5df1\u5374\u53f9\u8d77\u6c14\u6765\u4e86\u3002\u201d \u3000\u3000\u6768\u5a49\u7b11\u9053\uff1a\u201c\u5357\u54e5\uff0c\u6210\u5409\u601d\u6c57\u5bf9\u4f60\u53ef\u662f\u5f88\u4e0d\u9519\u554a\u3002\u4ed6\u9001\u4f60\u8fd9\u628a\u4ed6\u81ea\u7528\u7684\u94c1\u80ce\u5f13\uff0c\u8fd8\u8981\u5c01 \u4f60\u4f5c\u91d1\u5e10\u6b66\u58eb\uff0c\u82e5\u662f\u90a3\u9547\u56fd\u738b\u5b50\u9635\u4ea1\uff0c\u5357\u54e5\uff0c\u4f60\u5c31\u5927\u6709\u505a\u9a78\u9a6c\u7684\u5e0c\u671b\u4e86\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u4f6f\u6012\u8bf4\u9053\uff1a\u201c\u4f60\u600e\u4e48\u80e1\u626f\u4e00\u901a\uff0c\u626f\u5230\u6211\u7684\u8eab\u4e0a\u6765\u4e86\u3002\u597d\uff0c\u770b\u6211\u4e0d\u6495\u7834\u4f60\u8fd9\u5f20\u5c0f \u5634\uff1f\u201d \u3000\u3000\u6768\u5a49\u7b11\u5f97\u5982\u82b1\u679d\u4e71\u98a4\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u6015\u75d2\uff0c\u522b\u6765\u6405\u6211\u3002\u4f60\u4e0d\u662f\u8bf4\u660e\u6167\u516c\u4e3b\u5ac1\u7ed9\u90a3\u4e11\u516b\u602a\u662f \u6ca1\u8da3\u4e4b\u4e8b\u5417\uff1f\u6211\u4e0d\u8fc7\u60f3\u628a\u6ca1\u8da3\u7684\u60b2\u5267\u53d8\u6210\u6709\u8da3\u7684\u559c\u5267\u7f62\u4e86\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u5475\u4e86\u5475\u624b\u6307\uff0c\u4f5c\u52bf\u8bf4\u9053\uff1a\u201c\u8fd8\u8bf4\u8fd8\u8bf4\uff1f\u597d\uff0c\u6211\u975e\u5f97\u53eb\u4f60\u8ba8\u9976\u4e0d\u53ef\uff01\u201d\u4f5c\u52bf\u6293\u4e0b\uff0c \u5374\u5ffd\u5730\u505c\u624b\u4e0d\u52a8\uff0c\u8138\u4e0a\u4f3c\u4e4e\u9732\u51fa\u8be7\u5f02\u7684\u795e\u8272\u3002 \u3000\u3000\u6768\u5a49\u6014\u4e86\u4e00\u6014\uff0c\u8bf4\u9053\uff1a\u201c\u5357\u54e5\uff0c\u4f60\u2014\u2014\u201d\u674e\u601d\u5357\u5411\u5979\u6253\u4e86\u4e00\u4e2a\u773c\u8272\uff0c\u201c\u5947\u602a\uff0c\u6211\u521a\u624d\u597d \u4f3c\u542c\u5230\u4ec0\u4e48\u58f0\u97f3\uff0c\u600e\u4e48\u73b0\u5728\u53c8\u4e0d\u95fb\u4e86\u3002\u8fd9\u662f\u98ce\u5439\u74e6\u7247\u5f15\u8d77\u7684\u5e7b\u89c9\u5462\uff1f\u8fd8\u662f\u5c4b\u5b50\u91cc\u6709\u8017\u5b50\u8d70 \u52a8\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u6e38\u76ee\u56db\u987e\uff0c\u5ffd\u7136\u53d1\u73b0\u5730\u4e0a\u6709\u51e0\u70b9\u6de1\u6de1\u7684\u8840\u6e0d\u3002\u8fd9\u51e0\u70b9\u8840\u6e0d\u672c\u6765\u662f\u7ed9\u79be\u8349\u63a9\u76d6\u770b \u7684\uff0c\u674e\u601d\u5357\u521a\u521a\u62bd\u4e86\u4e00\u628a\u79be\u8349\u52a0\u706b\uff0c\u8fd9\u624d\u663e\u9732\u51fa\u6765\u3002 \u3000\u3000\u674e\u601d\u5357\u7591\u5fc3\u987f\u8d77\uff0c\u6b63\u8981\u642c\u52a8\u67f4\u8349\uff0c\u770b\u4e2a\u660e\u767d\uff0c\u5ffd\u542c\u5f97\u8e44\u58f0\u9aa4\u81f3\uff0c\u6765\u5f97\u6709\u5982\u66b4\u98ce\u6025\u96e8\u3002\u6768 \u5a49\u53d8\u4e86\u9762\u8272\uff0c\u6084\u58f0\u8bf4\u9053\uff1a\u201c\u6562\u60c5\u662f\u90a3\u4e24\u4e2a\u897f\u590f\u6b66\u571f\u7387\u4f17\u5bfb\u4ec7\uff1f\u5357\u54e5\uff0c\u54b1\u4eec\u600e\u4e48\u529e\uff1f\u662f\u8eb2\u5462\u8fd8 \u662f\u6253\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u4e14\u770b\u770b\u4ed6\u4eec\u6765\u610f\u5982\u4f55\uff0c\u8bf4\u4e0d\u5b9a\u53ea\u662f\u8def\u8fc7\u3002\u201d \u3000\u3000\u8bdd\u72b9\u672a\u4e86\uff0c\u53ea\u542c\u5f97\u8e44\u58f0\u7adf\u7136\u800c\u6b62\uff0c\u6b63\u662f\u505c\u5728\u8fd9\u5ea7\u78e8\u623f\u4e4b\u524d\uff0c\u542c\u5f97\u51fa\u6709\u4e94\u516d\u9a91\u4e4b\u591a\u3002 \u3000\u3000\u8fd9\u4f19\u4eba\u5df2\u7ecf\u53d1\u73b0\u4e86\u4ed6\u4eec\u7cfb\u5728\u7bf1\u7b06\u4e4b\u5185\u7684\u5750\u9a91\uff0c\u6709\u4e00\u4e2a\u8bf4\u9053\uff1a\u201c\u54c8\uff0c\u8fd9\u4e24\u5339\u9a6c\u5012\u662f\u5f88\u4e0d\u9519 \u5440\u3002\u201d\u6709\u4e00\u4e2a\u9053\uff1a\u201c\u78e8\u623f\u91cc\u6709\u706b\u5149\uff0c\u8fd9\u4e24\u5339\u9a6c\u7684\u4e3b\u4eba\u5b9a\u5728\u91cc\u9762\uff0c\u54b1\u4eec\u8fdb\u53bb\u95ee\u95ee\u3002\u201d\u6768\u5a49\u5728\u8499 \u53e4\u591a\u5e74\uff0c\u8fd9\u4e24\u4e2a\u4eba\u8bf4\u7684\u867d\u7136\u90fd\u662f\u897f\u590f\u65b9\u8a00\uff0c\u5979\u5374\u542c\u5f97\u51fa\u540e\u9762\u90a3\u4e2a\u4eba\u7684\u8bf4\u8bdd\u5e26\u7740\u8499\u53e4\u53e3\u97f3\u3002 \u3000\u3000\u6768\u5a49\u597d\u751f\u7eb3\u7f55\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8499\u53e4\u548c\u897f\u590f\u4e5f\u662f\u654c\u56fd\uff0c\u600e\u7684\u8fd9\u4e24\u4e2a\u7684\u6b66\u58eb\u5374\u6df7\u5728\u4e00\u8d77\uff1f\u201d \u5fc3\u5ff5\u672a\u5df2\uff0c\u90a3\u4e9b\u4eba\u5df2\u7ecf\u63a8\u5f00\u90a3\u4e24\u6247\u865a\u63a9\u7684\u6728\u95e8\uff0c\u8d70\u8fdb\u6765\u4e86\u3002\u8fd9\u4e9b\u4eba\u7a7f\u7684\u867d\u662f\u6b66\u58eb\u670d\u9970\uff0c\u4ed6\u4eec \u65e5\u95f4\u9047\u89c1\u7684\u90a3\u4e24\u4e2a\u897f\u590f\u6b66\u58eb\u5374\u5e76\u4e0d\u5728\u5185\uff0c\u674e\u601d\u5357\u7a0d\u7a0d\u653e\u70b9\u5fc3\u3002 \u3000\u3000\u90a3\u4e2a\u5e26\u7740\u8499\u53e4\u53e3\u97f3\u7684\u6b66\u58eb\u559d\u95ee\uff1a\u201c\u4f60\u4eec\u662f\u4ec0\u4e48\u4eba\uff1f\u54ea\u91cc\u6765\u7684\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u7b54\u9053\uff1a\u201c\u6211\u4eec\u5144\u59b9\u662f\u5728\u9f99\u6c99\u5806\u9644\u8fd1\u5c45\u4f4f\u7684\u6c49\u4eba\uff0c\u907f\u96be\u6765\u7684\u3002\u201d \u3000\u3000\u674e\u3001\u6768\u4e8c\u4eba\u8fd9\u4e00\u4e2a\u6708\u6765\u4ec6\u4ec6\u98ce\u5c18\uff0c\u8863\u88f3\u65e9\u5df2\u853d\u65e7\uff0c\u8eab\u4e0a\u6ee1\u662f\u98ce\u6c99\u7684\u75d5\u8ff9\uff0c\u770b\u6765\u5012\u662f\u5f88\u50cf \u96be\u6c11\u3002\u4f46\u6768\u5a49\u7684\u5929\u751f\u4e3d\u8d28\uff0c\u5374\u4f9d\u7136\u662f\u63a9\u76d6\u4e0d\u4f4f\u3002 \u3000\u3000\u90a3\u4e2a\u8bf4\u8bdd\u5e26\u6709\u8499\u53e4\u53e3\u97f3\u7684\u6b66\u58eb\u534a\u4fe1\u534a\u7591\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u5f53\u771f\u662f\u4ece\u9f99\u6c99\u5806\u9003\u96be\u6765\u7684\u5417\uff1f\u6709 \u6ca1\u6709\u78b0\u4e0a\u8499\u53e4\u5927\u519b\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u6211\u4eec\u95fb\u98ce\u800c\u9003\uff0c\u4fa5\u5e78\u6ca1\u6709\u78b0\u4e0a\u3002\u201d\u8bf4\u81f3\u6b64\u5904\uff0c\u6768\u5a49\u6084\u6084\u5730\u78b0\u4e86\u4ed6\u4e00\u4e0b\u3002\u674e \u601d\u5357\u970d\u7136\u4e00\u7701\uff0c\u8fd9\u624d\u542c\u51fa\u4e86\u8fd9\u4e2a\u6b66\u58eb\u7684\u8499\u53e4\u53e3\u97f3\uff0c\u540c\u65f6\u4e5f\u5c31\u660e\u767d\u4e86\u6768\u5a49\u7684\u793a\u610f\uff0c\u662f\u53eb\u4ed6\u4e0d\u53ef \u8bf4\u8499\u53e4\u5175\u7684\u574f\u8bdd\u3002 \u3000\u3000\u90a3\u6b66\u58eb\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u8499\u53e4\u58eb\u5175\u4e5f\u4e0d\u662f\u5403\u4eba\u7684\u9b54\u9b3c\uff0c\u4f60\u4eec\u5c31\u90a3\u6837\u5bb3\u6015\uff1f\u54fc\uff0c\u6211 \u770b\u4f60\u4eec\u5e76\u4e0d\u50cf\u96be\u6c11\uff01\u201d \u3000\u3000\u4e00\u4e2a\u897f\u590f\u6b66\u58eb\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\u3002\u96be\u6c11\u54ea\u80fd\u6709\u8fd9\u6837\u597d\u7684\u5750\u9a91\uff1f\u90a3\u4e24\u5339\u5750\u9a91\u6211\u4e00\u770b\u5c31\u77e5\u9053\u662f\u5927 \u5b9b\u51fa\u4ea7\u7684\u540d\u9a6c\uff0c\u5927\u5b9b\u9a6c\u81f3\u5c11\u4e5f\u8981\u503c\u51e0\u767e\u4e24\u94f6\u5b50\u4e00\u5339\u3002\u201d \u3000\u3000\u53e6\u4e00\u4e2a\u897f\u590f\u6b66\u4e0a\u9053\uff1a\u201c\u8fd9\u96cc\u513f\u4e5f\u957f\u5f97\u5f88\u4e0d\u9519\u5440\uff0c\u628a\u5979\u5e26\u56de\u53bb\u5427\u3002\u201d \u3000\u3000\u7b2c\u4e09\u4e2a\u897f\u590f\u6b66\u58eb\u8bf4\u9053\uff1a\u201c\u5bf9\u3002\u8fd9\u4e24\u5144\u59b9\u4e5f\u4e0d\u77e5\u662f\u771f\u7684\u96be\u6c11\u8fd8\u662f\u5047\u7684\u96be\u6c11\uff1f\u4f46\u603b\u4e4b\u662f\u5f62\u8ff9 \u53ef\u7591\u7684\u4e86\u3002\u5e26\u56de\u53bb\u76d8\u95ee\u51c6\u6ca1\u9519\u3002\u201d\u8fd9\u4e2a\u6b66\u58eb\u6bd4\u4ed6\u7684\u540c\u4f34\u5978\u6ed1\uff0c\u867d\u7136\u540c\u6837\u662f\u60f3\u5f3a\u62a2\u6c11\u5973\uff0c\u4ed6\u5374 \u8fd8\u8981\u627e\u4e2a\u85c9\u53e3\uff0c\u4ee5\u514d\u5728\u8fd9\u8499\u53e4\u6b66\u58eb\u9762\u524d\uff0c\u5931\u4e86\u8eab\u4efd\u3002 \u3000\u3000\u8499\u53e4\u6b66\u571f\u5ffd\u5730\u559d\u9053\uff1a\u201c\u4e14\u6162\uff01\u201d\u4ed6\u4f3c\u4e4e\u662f\u8fd9\u4e00\u4f19\u6b66\u58eb\u9996\u9886\uff0c\u4e00\u559d\u4e4b\u4e0b\uff0c\u90a3\u51e0\u4e2a\u897f\u590f\u6b66\u58eb \u6115\u7136\u540e\u9000\u3002 \u3000\u3000\u8fd9\u8499\u53e4\u6b66\u58eb\u6362\u4e86\u4e00\u526f\u53e3\u543b\uff0c\u6307\u7740\u90a3\u5f20\u94c1\u5236\u5f13\u8bf4\uff1a\u201c\u8fd9\u5f20\u5f13\u662f\u4f60\u7684\u5417\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u5fc3\u4e2d\u4e00\u52a8\uff0c\u60f3\u9053\uff1a\u201c\u4ed6\u4e00\u5b9a\u89c1\u8fc7\u6210\u5409\u601d\u6c57\u8fd9\u5f20\u94c1\u5236\u5f13\u7684\uff0c\u53ef\u662f\uff0c\u4ed6\u5e76\u4e0d\u77e5\u9053\u6211\u662f \u8c01\uff0c\u53ef\u77e5\u4ed6\u5fc5\u7136\u4e0d\u662f\u54f2\u522b\u6d3e\u6765\u7f09\u6355\u6211\u7684\u4e86\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u8fc5\u901f\u5730\u5224\u65ad\u4e86\u654c\u60c5\uff0c\u8bf4\u9053\uff1a\u201c\u662f\u4e00\u4f4d\u670b\u53cb\u9001\u7ed9\u6211\u7684\uff01\u201d \u3000\u3000\u8499\u53e4\u6b66\u58eb\u66f4\u662f\u5403\u60ca\uff0c\u8bb7\u8bb7\u8bf4\u9053\uff1a\u201c\u4f60\u8fd9\u4f4d\u670b\u53cb\u662f\u4ec0\u4e48\u4eba\uff0c\u4ed6\u600e\u4f1a\u9001\u7ed9\u4f60\u8fd9\u5f20\u5f13\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u8fd9\u4f4d\u670b\u53cb\u662f\u51e0\u4e2a\u6708\u524d\u6211\u5728\u548c\u6797\u7ed3\u8bc6\u7684\u3002\u4ed6\u7684\u8eab\u4efd\u6211\u53ef\u662f\u4e0d\u4fbf\u8bf4\uff0c\u627f\u4ed6\u770b\u5f97 \u8d77\u6211\uff0c\u9001\u4e86\u6211\u8fd9\u526f\u4ed6\u81ea\u7528\u7684\u5f13\u7bad\uff0c\u8fd8\u9001\u4e86\u6211\u4e00\u9762\u91d1\u724c\u3002\u201d \u3000\u3000\u8499\u53e4\u6b66\u58eb\u5fd9\u9053\uff1a\u201c\u91d1\u813e\u5728\u54ea\u91cc\uff0c\u53ef\u4e0d\u53ef\u4ee5\u7ed9\u6211\u770b\u770b\u3002\u201d\u201c\u674e\u601d\u5357\u51b3\u5fc3\u5192\u4e00\u5192\u9669\uff0c\u5fc3\u60f3\uff1a \u201c\u4ed6\u82e5\u4e0d\u77e5\u6211\u662f\u2018\u9003\u72af\u2019\u8eab\u4efd\uff0c\u89c1\u4e86\u8fd9\u9762\u91d1\u724c\uff0c\u51b3\u4e0d\u6562\u5c06\u6211\u96be\u4e3a\u3002\u201d\u4e8e\u662f\u638f\u51fa\u91d1\u724c\uff0c\u8bf4\u9053\uff1a \u201c\u4f60\u770b\u770b\u662f\u53ef\u4ee5\u7684\uff0c\u4f46\u4f60\u53ef\u4e0d\u80fd\u8ba9\u5916\u4eba\u77e5\u9053\u6211\u6709\u8fd9\u9762\u91d1\u724c\u3002\u201d\u8bdd\u4e2d\u4e4b\u610f\uff0c\u6697\u793a\u4ed6\u5df2\u7ecf\u77e5\u9053\u4e86 \u5bf9\u65b9\u7684\u8eab\u4efd\u662f\u8499\u53e4\u7684\u6b66\u58eb\u4e86\u3002 \u3000\u3000\u8499\u53e4\u6b66\u58eb\u5927\u60ca\u5931\u8272\uff0c\u8fde\u5fd9\u8bf4\u9053\uff1a\u201c\u6211\u660e\u767d\u4e86\uff0c\u4f60\u6536\u8d77\u6765\u5427\u3002\u4f60\u6211\u5fc3\u7167\u4e0d\u5ba3\uff0c\u6211\u4e0d\u4f1a\u6cc4\u6f0f \u4f60\u7684\u79d8\u5bc6\uff0c\u4f60\u4e5f\u4e0d\u8981\u628a\u4eca\u665a\u78b0\u89c1\u6211\u7684\u4e8b\u60c5\u8bf4\u51fa\u53bb\u3002\u6211\u60f3\uff0c\u4f60\u3001\u4f60\u8be5\u61c2\u5f97\u5427\uff01\u201d \u3000\u3000\u539f\u6765\u8fd9\u4e2a\u8499\u53e4\u6b66\u58eb\u4e43\u662f\u8d1f\u6709\u79d8\u5bc6\u7684\u4efb\u52a1\uff0c\u6f5c\u5165\u897f\u590f\uff0c\u56fe\u8c0b\u5927\u4e8b\uff0c\u517c\u4e14\u8981\u8ffd\u6355\u4e00\u4e2a\u4eba\u7684\u3002 \u8fd9\u51e0\u4e2a\u897f\u590f\u6b66\u58eb\u65e9\u5c31\u53d7\u4e86\u8499\u53e4\u7684\u6536\u4e70\uff0c\u662f\u4ed6\u7684\u5185\u5e94\u3002 \u3000\u3000\u8fd9\u4e2a\u8499\u53e4\u6b66\u58eb\u89c1\u4e86\u201c\u91d1\u5e10\u4ee4\u724c\u201d\uff0c\u4ee5\u4e3a\u674e\u601d\u5357\u4e5f\u662f\u548c\u4ed6\u4e00\u6837\uff0c\u662f\u8d1f\u6709\u79d8\u5bc6\u4efb\u52a1\u7684\uff0c\u4f46\u4efb \u52a1\u4e0d\u540c\uff0c\u5fc5\u987b\u5404\u5b88\u79d8\u5bc6\uff0c\u5373\u4f7f\u660e\u77e5\u662f\u81ea\u5df1\u4eba\uff0c\u4e5f\u4e0d\u80fd\u6253\u63a2\u3002\u4f46\u867d\u7136\u4e0d\u80fd\u6253\u63a2\uff0c\u5728\u77e5\u9053\u4e86\u662f \u201c\u81ea\u5df1\u4eba\u201d\u4e4b\u540e\uff0c\u5219\u5fc5\u987b\u4e92\u76f8\u534f\u52a9\u3002 \u3000\u3000\u8499\u53e4\u6b66\u571f\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6b64\u4eba\u6709\u91d1\u5e10\u4ee4\u724c\uff0c\u8fd8\u6709\u5927\u6c57\u6240\u8e22\u7684\u5f13\u7bad\uff0c\u4e00\u5b9a\u662f\u5927\u6c57\u4eb2\u81ea\u6d3e\u9063\u7684 \u4e86\u3002\u4ed6\u662f\u53d7\u547d\u4e8e\u795e\u7ffc\u8425\u7edf\u9886\u6728\u534e\u9ece\u7684\uff0c\u6210\u5409\u601d\u6c57\u4eb2\u81ea\u6d3e\u9063\uff0c\u800c\u4e14\u6301\u6709\u201c\u91d1\u5e10\u4ee4\u724c\u201d\u7684\u4eba\uff0c\u5730 \u4f4d\u5f53\u7136\u662f\u8fdc\u8fdc\u5728\u4ed6\u4e4b\u4e0a\uff0c\u6545\u6b64\u4ed6\u5bf9\u674e\u601d\u5357\u81ea\u662f\u4e0d\u80fd\u4e0d\u5fc5\u606d\u5fc5\u656c\u3002 \u3000\u3000\u90a3\u51e0\u4e2a\u897f\u590f\u6b66\u58eb\u4e0d\u8bc6\u91d1\u724c\uff0c\u89c1\u4ed6\u4eec\u7684\u9996\u9886\u7a81\u7136\u5bf9\u674e\u3001\u6768\u4e8c\u4eba\u8f9e\u8272\u8c26\u606d\uff0c\u4e0d\u7981\u90fd\u662f\u5927\u4e3a\u8be7 \u5f02\u3002\u5176\u4e2d\u4e00\u4eba\u50bb\u91cc\u50bb\u6c14\uff0c\u8111\u7b4b\u4e00\u4e0b\u5b50\u8f6c\u4e0d\u8fc7\u6765\uff0c\u8fd8\u5728\u5495\u5495\u54dd\u54dd\u5730\u8bf4\u9053\uff1a\u201c\u54a6\uff0c\u9ec4\u6a59\u6a59\u7684\uff0c\u53ea \u6015\u5f53\u771f\u662f\u91d1\u5b50\u5462\uff01\u65e2\u7136\u8bf4\u662f\u96be\u6c11\uff0c\u5374\u53c8\u6709\u5982\u6b64\u9614\u7ef0\u7684\u670b\u53cb\u9001\u7ed9\u4ed6\u91d1\u724c\uff0c\u8fd9\u5c0f\u5b50\u4e00\u5b9a\u4e0d\u662f\u4ec0\u4e48 \u597d\u8def\u9053\u3002\u201d \u3000\u3000\u8499\u53e4\u6b66\u58eb\u53cc\u773c\u4e00\u77aa\uff0c\u559d\u9053\uff1a\u201c\u4f60\u4eec\u6709\u773c\u65e0\u73e0\uff0c\u5f97\u7f6a\u4e86\u8d35\u4eba\uff0c\u8fd8\u4e0d\u5feb\u5feb\u8d54\u793c\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u7b11\u9053\uff1a\u201c\u4e0d\u77e5\u4e0d\u7f6a\uff0c\u4f60\u4e5f\u4e0d\u5fc5\u8d23\u602a\u4ed6\u4eec\u4e86\u3002\u6ca1\u4ec0\u4e48\u4e8b\uff0c\u6700\u597d\u4f60\u53eb\u4ed6\u4eec\u8d70\u5427\uff0c\u6211\u8fd8 \u8981\u7761\u89c9\u5462\u3002\u201d \u3000\u3000\u8499\u53e4\u6b66\u58eb\u5fd9\u4e0d\u8fed\u5730\u8bf4\u9053\uff1a\u201c\u662f\uff0c\u662f\u3002\u201d\u628a\u624b\u4e00\u6325\uff0c\u559d\u9053\uff1a\u201c\u4f60\u4eec\u90fd\u7ed9\u6211\u8d70\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u6015\u4ed6\u770b\u89c1\u5730\u4e0a\u7684\u8840\u6e0d\uff0c\u634e\u6084\u5730\u8eab\u5b50\u4e00\u632a\uff0c\u501a\u7740\u8349\u5806\uff0c\u63a9\u76d6\u4e86\u90a3\u51e0\u70b9\u8840\u6e0d\uff0c\u8bf4\u9053\uff1a \u201c\u6055\u6211\u4e0d\u9001\u4e86\u3002\u201d \u3000\u3000\u6709\u4e2a\u6bd4\u8f83\u7cbe\u7ec6\u7684\u897f\u590f\u6b66\u58eb\u8d77\u4e86\u4e00\u70b9\u7591\u5fc3\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u95f4\u78e8\u623f\u5730\u65b9\u5012\u662f\u4e0d\u5c0f\u554a\u3002\u4e0d\u77e5\u8fd9\u4f4d \u8d35\u4eba\u662f\u4ec0\u4e48\u65f6\u5019\u6765\u7684\uff1f\u6765\u7684\u65f6\u5019\uff0c\u5c4b\u5b50\u91cc\u6709\u6ca1\u6709\u4eba\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u8981\u76d8\u95ee\u6211\u4e48\uff1f\u4e0d\u9519\uff0c\u6211\u662f\u6536\u85cf\u4e86\u4e00\u4e2a\u4eba\u5728\u8fd9\u91cc\uff0c\u4f60\u6765\u641c\u5427\uff01\u201d \u3000\u3000\u8499\u53e4\u6b66\u58eb\u65a5\u9053\uff1a\u201c\u4f60\u522b\u7f57\u55e6\uff0c\u7ed9\u6211\u6eda\u5f00\uff01\u201d\u897f\u590f\u7684\u51e0\u4e2a\u6b66\u58eb\u90fd\u8d70\u4e86\u51fa\u53bb\u4e4b\u540e\uff0c\u8499\u53e4\u6b66\u58eb \u8d54\u7b11\u8bf4\u9053\uff1a\u201c\u897f\u590f\u9791\u5b50\uff0c\u4e0d\u61c2\u793c\u8c8c\uff0c\u4f60\u8001\u54e5\u53ef\u522b\u89c1\u602a\u3002\u4e0d\u8fc7\uff0c\u6211\u4eec\u7684\u786e\u662f\u8981\u7f09\u4e00\u4e2a\u7d27\u8981\u7684 \u4eba\uff0c\u8fd9\u4eba\u7ea6\u6709\u4e09\u5341\u5c81\u5e74\u7eaa\uff0c\u5de6\u988a\u6709\u4e00\u9053\u4e09\u5bf8\u957f\u7684\u5200\u75d5\uff0c\u8001\u54e5\u5018\u82e5\u78b0\u89c1\u6b64\u4eba\uff0c\u8fd8\u671b\u4f60\u52a9\u4e00\u81c2\u4e4b \u529b\uff0c\u5c06\u4ed6\u62ff\u4e0b\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u597d\uff0c\u6211\u66ff\u4f60\u7559\u5fc3\u5c31\u662f\u3002\u201d \u3000\u3000\u8499\u53e4\u6b66\u58eb\u53bb\u540e\uff0c\u6768\u5a49\u7b11\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u8fd9\u9762\u91d1\u724c\u5230\u4e86\u897f\u590f\u4e5f\u8fd8\u5927\u6709\u7528\u5904\uff0c\u5c45\u7136\u628a\u4ed6\u4eec\u5413\u9000 \u4e86\u3002\u4f46\u77a7\u4f60\u521a\u624d\u7684\u795e\u6c14\uff0c\u5374\u4f3c\u8fd9\u5c4b\u5b50\u91cc\u5f53\u771f\u662f\u85cf\u6709\u4eba\u4f3c\u7684\u3002\u8fde\u6211\u90fd\u5728\u6000\u7591\u4f60\u662f\u5728\u4f7f\u7528\u5175\u6cd5\u4e0a \u865a\u865a\u5b9e\u5b9e\u7684\u6218\u7565\u5462\u3002\u201d\u539f\u6765\u6768\u5a49\u5e76\u672a\u53d1\u89c9\u5730\u4e0a\u7684\u8840\u6e0d\uff0c\u5979\u5012\u4e0d\u662f\u771f\u7684\u6000\u7591\u5c4b\u5185\u6709\u4eba\u3002 \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u670b\u53cb\u3001\u8bf7\u51fa\u6765\u5427\uff01\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u53ea\u542c\u5f97\u201c\u54e9\u201d\u7684\u4e00\u58f0\uff0c\u4eba\u672a\u51fa\u6765\uff0c\u4e00\u652f\u98de \u9556\u5148\u51fa\u6765\u4e86\u3002 \u3000\u3000\u5e78\u800c\u674e\u601d\u5357\u65e9\u6709\u9632\u5907\uff0c\u8f7b\u8f7b\u4e00\u63a8\uff0c\u4f7f\u4e86\u4e2a\u52b2\uff0c\u628a\u6768\u5a49\u63a8\u8fc7\u4e00\u65c1\u3002\u90a3\u652f\u98de\u9556\u4ece\u6768\u5a49\u9b13\u8fb9\u98de \u8fc7\uff0c\u6768\u5a49\u55c5\u5f97\u4e00\u80a1\u6d53\u70c8\u7684\u8165\u6c14\uff0c\u8fd9\u662f\u4e00\u652f\u6dec\u4e86\u5267\u6bd2\u7684\u98de\u9556\uff01 \u3000\u3000\u6768\u5a49\u60ca\u9b42\u672a\u8d70\uff0c\u56de\u8fc7\u5934\u6765\uff0c\u53ea\u89c1\u8349\u5806\u91cc\u94bb\u51fa\u4e00\u4e2a\u4eba\u6765\uff0c\u6768\u5a49\u53eb\u9053\uff1a\u201c\u8fd9\u5c4b\u5b50\u91cc\u5f53\u771f\u6709 \u4eba\uff01\u201d\u9632\u4ed6\u518d\u4e0b\u6bd2\u624b\uff0c\u8fde\u5fd9\u62d4\u5251\u6307\u7740\u4ed6\u7684\u54bd\u5589\uff0c\u559d\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u201d \u3000\u3000\u90a3\u4eba\u5636\u58f0\u8bf4\u9053\uff1a\u201c\u4fbf\u5b9c\u4e86\u4f60\u8fd9\u4e24\u4e2a\u5c0f\u8d3c\uff0c\u4f60\u628a\u6211\u6740\u4e86\u9886\u529f\u5c31\u662f\uff0c\u591a\u95ee\u4ec0\u4e48\uff1f\u201d\u6768\u5a49\u8fd9\u65f6 \u65b9\u59cb\u5b9a\u4e0b\u795e\u6765\uff0c\u4ed4\u7ec6\u4e00\u770b\uff0c\u53ea\u89c1\u8fd9\u4eba\u6d51\u8eab\u8840\u6c61\uff0c\u5927\u7ea6\u6709\u4e09\u5341\u5c81\u5de6\u53f3\u5e74\u7eaa\uff0c\u5de6\u988a\u6709\u4e00\u9053\u4e09\u5bf8\u591a \u957f\u7684\u5200\u75a4\u3002 \u3000\u3000\u6768\u5a49\u604d\u7136\u5927\u609f\uff0c\u6536\u56de\u5b9d\u5251\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u5c31\u662f\u521a\u624d\u90a3\u4e9b\u4eba\u6240\u8981\u6349\u62ff\u7684\u9003\u72af\u5427\uff1f\u6211\u4eec\u6551\u4e86\u4f60 \u7684\u6027\u547d\uff0c\u4f60\u4e3a\u4f55\u6069\u5c06\u4ec7\u62a5\uff1f\u201d \u3000\u3000\u90a3\u4eba\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u548c\u90a3\u4e9b\u4eba\u90fd\u662f\u4e00\u4e18\u4e4b\u8c89\uff0c\u4f60\u5f53\u6211\u4e0d\u77e5\u9053\u5417\uff1f\u5927\u4e08\u592b\u6b7b\u5219\u6b7b\u8033\uff0c\u5c82\u80fd \u53d7\u8fb1\uff1f\u4f60\u4eec\u5e72\u8106\u628a\u6211\u4e00\u5251\u6740\u4e86\uff0c\u6211\u9886\u4f60\u4eec\u7684\u60c5\u3002\u4f60\u4eec\u82e5\u662f\u60f3\u628a\u6211\u62ff\u53bb\u732e\u7ed9\u6210\u5409\u601d\u6c57\uff0c\u563f\uff0c \u563f\uff0c\u90a3\u5c31\u6765\u5427\uff01\u5927\u4e0d\u4e86\u54b1\u4eec\u540c\u5f52\u4e8e\u5c3d\uff01\u201d\u4ed6\u624b\u4e2d\u634f\u7740\u4e00\u652f\u5149\u534e\u707f\u70c2\u7684\u91d1\u9556\uff0c\u548c\u521a\u624d\u6240\u53d1\u7684\u90a3 \u652f\u9556\u4e00\u6837\uff0c\u53d1\u51fa\u8165\u6c14\uff0c\u663e\u7136\u662f\u6253\u5b9a\u4e86\u4e3b\u610f\uff0c\u4e0d\u8bb8\u674e\u3001\u6768\u8fd1\u8eab\u3002 \u3000\u3000\u674e\u601d\u5357\u770b\u51fa\u8fd9\u4eba\u6240\u7528\u7684\u6697\u5668\u548c\u5c60\u767e\u57ce\u7684\u72ec\u95e8\u6697\u5668\u201c\u6bd2\u9f99\u9556\u201d\u4e00\u5f0f\u4e00\u6837\uff0c\u5fc3\u91cc\u9887\u4e3a\u8be7\u5f02\uff0c \u4e8e\u662f\u7b11\u9053\uff1a\u201c\u670b\u53cb\uff0c\u4f60\u8bef\u4f1a\u4e86\u3002\u6211\u65e9\u5df2\u77e5\u9053\u4f60\u8eb2\u5728\u8fd9\u513f\uff0c\u82e5\u662f\u60f3\u8981\u51fa\u5356\u4f60\uff0c\u4f60\u521a\u624d\u8fd8\u9003\u5f97\u8fc7 \u5417\uff1f\u8fd9\u91cc\u4e0d\u662f\u4e45\u7559\u4e4b\u5730\uff0c\u4f60\u5feb\u8bf4\u5b9e\u8bdd\uff0c\u4f60\u662f\u5c60\u767e\u57ce\u7684\u4ec0\u4e48\u4eba\uff1f\u5b8b\u94c1\u8f6e\u592b\u5987\u4f60\u53ef\u8ba4\u5f97\uff1f\u201d \u3000\u3000\u90a3\u4eba\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u53c8\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u522b\u60f3\u82b1\u8a00\u5de7\u8bed\uff0c\u5957\u6211\u53e3\u4f9b\uff01\u4f60\u90a3\u9762\u91d1\u724c\uff0c\u6211\u5df2\u7ecf\u77a7 \u89c1\u5566\uff01\u201d \u3000\u3000\u539f\u6765\u8fd9\u4eba\u8eb2\u5728\u8349\u5806\u91cc\u9762\uff0c\u674e\u601d\u5357\u548c\u6768\u5a49\u7684\u8bf4\u8bdd\u4ed6\u90fd\u542c\u89c1\u4e86\u3002\u4ed6\u542c\u89c1\u6768\u5a49\u63d0\u8d77\u660e\u6167\u516c\u4e3b\u7684 \u4e8b\u60c5\uff0c\u53c8\u542c\u8bf4\u6210\u5409\u601d\u6c57\u8d50\u4ed6\u5f13\u7bad\uff0c\u540e\u6765\u53c8\u89c1\u4e86\u90a3\u9762\u91d1\u724c\uff0c\u81ea\u662f\u4e0d\u80fd\u65e0\u7591\u3002\u4ed6\u628a\u674e\u601d\u5357\u5f53\u4f5c\u4e86 \u5356\u8eab\u6295\u9760\u8499\u53e4\u9791\u5b50\u7684\u9b54\u72ac\u4e4b\u8f88\uff0c\u56e0\u6b64\uff0c\u4e0d\u8bba\u674e\u601d\u5357\u5982\u4f55\u5206\u8fa9\uff0c\u4ed6\u90fd\u4e0d\u518d\u76f8\u4fe1\u7684\u4e86\u3002 \u3000\u3000\u674e\u601d\u5357\u6b63\u81ea\u65e0\u8ba1\uff0c\u5ffd\u542c\u5f97\u53c8\u662f\u4e00\u9635\u6025\u9aa4\u7684\u8e44\u58f0\uff0c\u5411\u7740\u8fd9\u4e2a\u78e8\u623f\u5954\u6765\u3002\u6768\u5a49\u60ca\u9053\uff1a\u201c\u96be\u9053 \u90a3\u4e2a\u7315\u5b50\u4fe1\u4f60\u4e0d\u8fc7\uff0c\u53bb\u800c\u590d\u6765\uff1f\u201d\u674e\u601d\u5357\u9053\uff1a\u201c\u4e0d\u662f\u521a\u624d\u90a3\u4e00\u62e8\uff0c\u8fd9\u6b21\u6765\u7684\u53ea\u662f\u4e09\u9a91\u3002\u201d \u3000\u3000\u90a3\u4eba\u51b7\u7b11\u9053\uff1a\u201c\u56fe\u7a77\u5315\u73b0\u4e86\u5427\uff1f\u597d\u5566\uff0c\u770b\u4f60\u8fd8\u8bf4\u4ec0\u4e48\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u4f60\u522b\u614c\uff0c\u5feb\u8eb2\u8d77\u6765\uff0c\u6211\u7ed9\u4f60\u5e94\u4ed8\u3002\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u4e09\u9a91\u5feb\u9a6c\u5df2\u7ecf\u6765\u5230\uff0c\u5927\u53eb \u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u5728\u8fd9\u91cc\u4e86\uff01\u201d \u3000\u3000\u53ea\u89c1\u6765\u7684\u8fd9\u4e09\u4e2a\u4eba\uff0c\u4e00\u4e2a\u662f\u9762\u8089\u6a2a\u751f\u7684\u5927\u6c49\uff0c\u4e00\u4e2a\u662f\u62ab\u7740\u8888\u88df\u7684\u5587\u561b\uff0c\u8fd8\u6709\u4e00\u4e2a\u662f\u4e94\u77ed \u8eab\u6750\u7684\u6c49\u5b50\uff0c\u4ece\u4ed6\u7684\u5f62\u8c8c\u548c\u670d\u9970\u53ef\u4ee5\u5224\u65ad\u4ed6\u5b9a\u662f\u6c49\u4eba\u3002 \u3000\u3000\u9762\u8089\u6a2a\u751f\u7684\u90a3\u5927\u6c49\u53eb\u9053\uff1a\u201c\u54c8\uff0c\u6211\u770b\u89c1\u90a3\u5c0f\u8d3c\u5566\uff01\u201d\u539f\u6765\u5c4b\u5185\u90a3\u53d7\u4f24\u7684\u6c49\u5b50\u8fd8\u672a\u6765\u5f97\u53ca \u722c\u5165\u8349\u5806\uff0c\u706b\u5149\u718a\u718a\u4e4b\u4e0b\uff0c\u7ed9\u4ed6\u77a7\u89c1\u4e86\u4fa7\u5f71\u3002 \u3000\u3000\u674e\u601d\u5357\u65e9\u5df2\u51fa\u4e86\u9662\u5b50\uff0c\u6b64\u65f6\u5728\u6697\u5904\u7a81\u7136\u73b0\u8eab\uff0c\u5b88\u7740\u6a90\u9636\uff0c\u90a3\u5927\u6c49\u7ed9\u4ed6\u5413\u4e86\u4e00\u8df3\uff0c\u5927\u6012\u559d \u9053\uff1a\u201c\u4f60\u662f\u4ec0\u4e48\u4eba\uff1f\u7ed9\u6211\u6eda\u5f00\uff01\u201d\u4ed6\u8bf4\u7684\u662f\u7684\u897f\u590f\u571f\u8bdd\uff0c\u53e3\u9f7f\u53c8\u4e0d\u4f36\u4fd0\uff0c\u674e\u601d\u5357\u4e00\u4e2a\u5b57\u4e5f\u542c \u4e0d\u61c2\u3002 \u3000\u3000\u8eab\u6750\u77ee\u5c0f\u7684\u90a3\u4e2a\u6c49\u5b50\u770b\u89c1\u674e\u601d\u5357\u662f\u4e2a\u6c49\u4eba\uff0c\u6014\u4e86\u4e00\u6014\uff0c\u53eb\u9053\uff1a\u201c\u4e14\u6162\uff0c\u4f60\u662f\u54ea\u6761\u7ebf\u4e0a\u7684 \u670b\u53cb\uff1f\u201d\u8fd9\u4eba\u662f\u6c5f\u6e56\u4e0a\u7684\u5927\u884c\u5bb6\uff0c\u4ed6\u6599\u60f3\u674e\u601d\u5357\u5fc5\u6709\u6765\u5386\uff0c\u6545\u6b64\u8981\u90a3\u897f\u590f\u6c49\u5b50\u6162\u4e9b\u52a8\u624b\uff0c\u4ee5 \u4fbf\u4ed6\u8fdb\u884c\u76d8\u95f4\u3002\u90a3\u897f\u590f\u6c49\u5b50\u5e76\u4e0d\u542c\u4ed6\u7684\u8bdd\uff0c\u6c14\u547c\u547c\u5730\u5df2\u662f\u5411\u7740\u674e\u601d\u5357\u5954\u53bb\u3002 \u3000\u3000\u6768\u5a49\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u662f\u8fd9\u78e8\u623f\u7684\u4e3b\u4eba\u4e48\uff0c\u4e3a\u4ec0\u4e48\u6211\u4eec\u5c31\u8981\u907f\u5f00\u8ba9\u4f60\uff1f\u201d\u6768\u5a49\u6bd4\u674e\u601d\u5357\u77ee\u534a \u4e2a\u5934\uff0c\u521a\u624d\u5979\u7ad9\u5728\u674e\u601d\u5357\u80cc\u540e\uff0c\u9ed1\u591c\u4e4b\u4e2d\uff0c\u90a3\u897f\u590f\u6c49\u5b50\u53ea\u770b\u5230\u674e\u601d\u5357\uff0c\u8fd8\u6ca1\u6709\u6ce8\u610f\u53ca\u5979\u3002 \u3000\u3000\u6b64\u65f6\u4ed6\u770b\u89c1\u4e86\u6768\u5a49\u6e05\u4e3d\u7684\u59ff\u5bb9\uff0c\u4e0d\u89c9\u7741\u5927\u4e86\u773c\u775b\uff0c\u8fde\u5fd9\u628a\u8eab\u5b50\u4e00\u4fa7\uff0c\u5360\u4e86\u9002\u5f53\u7684\u4f4d\u7f6e\uff0c \u501f\u7740\u5c4b\u5185\u900f\u51fa\u7684\u706b\u5149\uff0c\u628a\u6768\u5a49\u770b\u4e86\u4e2a\u9971\uff0c\u8bf4\u8bdd\u7684\u58f0\u97f3\u4e5f\u67d4\u548c\u4e86\uff1a\u201c\u5c0f\u5a18\u5b50\uff0c\u4f60\u522b\u5bb3\u6015\uff0c\u6211\u4eec \u662f\u6765\u6349\u8fd9\u4e2a\u5c0f\u8d3c\u7684\uff0c\u4e0e\u4f60\u65e0\u5173\uff0c\u4f60\u8eb2\u8fc7\u4e00\u8fb9\uff0c\u5c31\u6ca1\u4e8b\u4e86\u3002\u201d\u8eab\u6750\u77ee\u5c0f\u7684\u90a3\u6c49\u5b50\u6697\u6697\u9a82\u58f0\uff1a \u201c\u597d\u7cca\u6d82\uff01\u8fd9\u4e24\u4eba\u548c\u90a3\u59d3\u9f99\u7684\u5c0f\u5b50\u540c\u5728\u4e00\u8d77\uff0c\u5c82\u80fd\u65e0\u5173\uff1f\u201d\u6b8a\u4e0d\u77e5\u8fd9\u897f\u590f\u6c49\u5b50\u56e0\u4e3a\u662f\u751f\u957f\u5728 \u86ee\u8352\u4e4b\u5730\uff0c\u51e0\u66fe\u89c1\u8fc7\u8fd9\u6837\u6807\u81f4\u7684\u59d1\u5a18\uff0c\u4ed6\u4e0d\u662f\u7cca\u6d82\uff0c\u800c\u662f\u597d\u8272\u3002 \u3000\u3000\u90a3\u62ab\u7740\u5927\u7ea2\u88df\u88df\u7684\u5587\u561b\u4e00\u76f4\u6ca1\u6709\u4f5c\u58f0\uff0c\u6b64\u65f6\u5ffd\u5730\u8d70\u5230\u674e\u601d\u5357\u9762\u524d\uff0c\u7528\u751f\u786c\u7684\u6c49\u8bed\u8bf4\u9053\uff1a \u201c\u4f60\u53ef\u662f\u4ece\u548c\u6797\u9003\u51fa\u6765\u7684\u674e\u601d\u5357\u5417\uff1f\u201d\u8bf4\u7684\u662f\u6c49\u8bdd\uff0c\u5374\u542c\u5f97\u51fa\u4ed6\u662f\u8499\u53e4\u4eba\u3002 \u3000\u3000\u674e\u601d\u5357\u5403\u4e86\u4e00\u60ca\uff0c\u8fd9\u4e2a\u8499\u53e4\u5587\u561b\u662f\u4ed6\u4ece\u672a\u89c1\u8fc7\u7684\uff0c\u5374\u4e00\u89c1\u9762\u5c31\u8bf4\u51fa\u4e86\u4ed6\u7684\u540d\u5b57\u3002\u674e\u601d\u5357 \u4e00\u60ca\u4e4b\u540e\uff0c\u968f\u5373\u7701\u609f\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u662f\u4e86\uff0c\u4ed6\u4e00\u5b9a\u662f\u4ece\u9f99\u6c99\u5806\u6765\u7684\uff0c\u4ed6\u5df2\u7ecf\u5728\u9547\u56fd\u738b\u5b50\u90a3\u91cc \u89c1\u8fc7\u6211\u7684\u753b\u50cf\u3002\u201d \u3000\u3000\u8eab\u4efd\u5df1\u7136\u63ed\u7834\uff0c\u91d1\u724c\u81ea\u662f\u4e0d\u80fd\u518d\u4f5c\u62a4\u7b26\uff0c\u674e\u601d\u5357\u7d22\u6027\u4e5f\u5c31\u4e0d\u52a0\u9690\u7792\uff0c\u50b2\u7136\u8bf4\u9053\uff1a\u201c\u4e0d \u9519\uff0c\u6211\u5c31\u662f\u674e\u601d\u5357\uff0c\u6211\u4e0d\u559c\u6b22\u4f4f\u5728\u548c\u6797\uff0c\u5f80\u54ea\u513f\u662f\u6211\u7684\u4e8b\uff0c\u4f60\u5f85\u600e\u6837\u3002\u201d \u3000\u3000\u7ea2\u8863\u5587\u561b\u54c8\u54c8\u5927\u7b11\uff1a\u201c\u4f60\u4e0d\u559c\u6b22\u4f4f\u548c\u6797\uff0c\u5927\u6c57\u53ef\u662f\u8981\u4f60\u56de\u53bb\u54e9\uff01\u54c8\u54c8\uff0c\u4eca\u665a\u54b1\u4eec\u53ef\u662f\u4ea4 \u4e86\u597d\u8fd0\u9053\u4e86\uff0c\u8fd9\u5c0f\u5b50\u662f\u5927\u6c57\u8981\u7684\u4eba\uff0c\u53ea\u6015\u6bd4\u90a3\u59d3\u96f7\u7684\u5c0f\u8d3c\u8fd8\u66f4\u91cd\u8981\u5462\uff01\u8fd9\u5c0f\u5b50\u4ea4\u7ed9\u6211\uff0c\u4f60\u4eec \u8fdb\u53bb\u6349\u90a3\u5c0f\u8d3c\uff01\u201d\u8bf4\u7f62\uff0c\u4e00\u6296\u4e5d\u73af\u9521\u6756\uff0c\u5c31\u5411\u674e\u601d\u5357\u80c1\u4e0b\u70b9\u53bb\u3002 \u3000\u3000\u9661\u7136\u95f4\uff0c\u53ea\u89c1\u5251\u5149\u4e00\u95ea\uff0c\u53ee\u53ee\u94db\u94db\u4e4b\u58f0\u4e0d\u7edd\u4e8e\u8033\uff0c\u5b9b\u5982\u54cd\u8d77\u4e86\u4e00\u7247\u94f6\u94c3\u3002\u7ea2\u8863\u5587\u561b\u7684\u4e5d \u73af\u9521\u6756\u7ed9\u674e\u601d\u5357\u4e00\u5251\u6311\u5f00\uff0c\u4f3c\u4e4e\u9887\u611f\u610f\u5916\uff0c\u5fae\u201c\u54a6\u201d\u4e00\u58f0\uff0c\u9521\u6756\u4e00\u6296\uff0c\u4e00\u62db\u201c\u4e4c\u9f99\u6446\u5c3e\u201d\u6a2a \u626b\u674e\u601d\u5357\u53cc\u817f\uff0c\u674e\u601d\u5357\u62ac\u8db3\u4e00\u8000\uff0c\u5530\u7684\u4e00\u5251\uff0c\u4fbf\u6307\u5230\u4e86\u7ea2\u8863\u5587\u561b\u7684\u54bd\u5589\u3002\u8fd9\u4e00\u62db\u4f7f\u5f97\u51f6\u9669\u4e4b \u6781\uff0c\u5018\u82e5\u674e\u601d\u5357\u7684\u8db3\u529b\u8e0f\u4e0d\u7a33\u4ed6\u7684\u9521\u6756\uff0c\u767b\u65f6\u4fbf\u8981\u6298\u817f\u6b8b\u80a2\uff1b\u7ea2\u8863\u5587\u561b\u5018\u82e5\u95ea\u907f\u4e0d\u5f00\uff0c\u66f4\u6709 \u5229\u5251\u7a7f\u5589\u4e4b\u7978\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u751f\u6b7b\u4fc4\u503e\u4e4b\u95f4\uff0c\u53cc\u65b9\u90fd\u663e\u51fa\u4e86\u975e\u51e1\u7684\u672c\u9886\uff0c\u7ea2\u8863\u5587\u561b\u8eab\u5f62\u540e\u4ef0\uff0c\u559d\u58f0\uff1a\u201c\u5012\uff01\u201d \u9521\u6756\u4e00\u6311\uff0c\u53ea\u89c1\u674e\u601d\u5357\u817e\u8eab\u800c\u8d77\uff0c\u4ed6\u4e0d\u662f\u5012\u4e0b\uff0c\u53cd\u800c\u662f\u8df3\u8d77\u6765\u4e86\u3002 \u3000\u3000\u539f\u6765\u7ea2\u8863\u5587\u561b\u662f\u8981\u628a\u674e\u601d\u5357\u6311\u7ffb\u7684\uff0c\u674e\u601d\u5357\u8f7b\u529f\u8d85\u5353\uff0c\u5374\u501f\u4ed6\u9521\u4ed7\u6311\u8d77\u4e4b\u529b\uff0c\u4f7f\u51fa\u4e86 \u201c\u71d5\u5b50\u94bb\u4e91\u201d\u7684\u4e0a\u4e58\u8f7b\u529f\u3002\u7ea2\u8863\u5587\u561b\u867d\u7136\u907f\u5f00\u4e86\u4ed6\u7a7f\u5589\u7684\u4e00\u5251\uff0c\u5934\u4e0a\u7684\u516b\u89d2\u50e7\u5e3d\uff0c\u5374\u5df2\u7ed9\u5251 \u5c16\u6311\u5f00\uff0c\u8dcc\u843d\u5730\u4e0a\u3002\u867d\u65e0\u4f24\u635f\uff0c\u8fd9\u4e00\u62db\u81f3\u5c11\u4e5f\u662f\u8f93\u4e86\u534a\u62db\u4e86\u3002\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u674e\u601d\u5357\u5728\u534a\u7a7a\u4e2d\u4e00\u4e2a\u201c\u9e5e\u5b50\u7ffb\u8eab\u201d\uff0c\u51cc\u7a7a\u4e0b\u51fb\u3002\u7ea2\u8863\u5587\u561b\u6a2a\u6756\u4e00\u6321\uff0c \u674e\u601d\u5357\u7684\u957f\u5251\u5728\u9521\u6756\u4e0a\u4e00\u62cd\u4e00\u6309\uff0c\u518d\u4e00\u4e2a\u7ffb\u8eab\uff0c\u5b89\u5b89\u7a33\u7a33\u7684\u7740\u5730\uff0c\u9521\u6756\u4e0a\u7684\u4e5d\u4e2a\u94dc\u73af\uff0c\u54cd\u4e2a \u4e0d\u505c\u3002 \u3000\u3000\u90a3\u4e2a\u6c49\u4eba\u89c1\u4e86\u674e\u601d\u5357\u8fd9\u4e09\u62db\u5251\u6cd5\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u539f\u6765\u662f\u8c37\u5e73\u9633\u7684\u9ad8\u5f92\uff0c\u597d\uff0c\u6211\u6765 \u4f1a\u4f1a\u4f60\u7684\u8fbe\u6469\u5251\u6cd5\u3002\u201d \u3000\u3000\u8fd9\u4eba\u8eab\u6750\u77ee\u5c0f\uff0c\u5341\u5206\u5a07\u6377\uff0c\u58f0\u5230\u4eba\u5230\uff0c\u4f7f\u7684\u662f\u4e00\u53cc\u5224\u5b98\u7b14\uff0c\u5728\u5251\u5149\u6756\u5f71\u4e4b\u4e2d\uff0c\u500f\u5730\u4fbf\u6251 \u4e86\u8fdb\u6765\u3002\u53cc\u7b14\u4e00\u5206\uff0c\u5de6\u70b9\u201c\u671f\u95e8\u201d\u53f3\u70b9\u201c\u8840\u6d77\u201d\uff0c\u8fd9\u4e24\u4e2a\u7a74\u9053\uff0c\u90fd\u662f\u4eba\u8eab\u7684\u6b7b\u7a74\u3002\u674e\u601d\u5357\u559d \u9053\uff1a\u201c\u597d\u72e0\u6bd2\u7684\u70b9\u7a74\u529f\u592b\uff01\u201d\u8eab\u5f62\u4e00\u4e2a\u76d8\u65cb\uff0c\u4f7f\u51fa\u201c\u62bd\u6492\u8fde\u73af\u201d\u7684\u5251\u6cd5\uff0c\u6311\u5f00\u4e86\u7ea2\u8863\u5587\u561b\u7684 \u9521\u6756\uff0c\u53c8\u67b6\u4f4f\u4e86\u8fd9\u6c49\u5b50\u7684\u5224\u5b98\u7b14\uff0c\u8fd9\u6c49\u5b50\u4e5f\u4e0d\u5f97\u4e0d\u7531\u8877\u4f69\u670d\uff0c\u8d5e\u4e86\u4e00\u58f0\uff1a\u201c\u597d\u529f\u592b\u3002\u201d \u3000\u3000\u6768\u5a49\u6b63\u8981\u6765\u52a9\u674e\u601d\u5357\u4e00\u5288\u4e4b\u529b\uff0c\u90a3\u4e2a\u9762\u8089\u6a2a\u751f\u7684\u897f\u590f\u6c49\u5b50\u5df2\u8dd1\u4e0a\u4e86\u51e0\u7ea7\u77f3\u7ea7\uff0c\u5f85\u8981\u8fdb\u5c4b \u62ff\u4eba\u4e86\u3002\u674e\u601d\u5357\u9053\uff1a\u201c\u4e0d\u53ef\u8ba9\u4ed6\u8fdb\u53bb\u3002\u201d\u6768\u5a49\u4eae\u5251\u51fa\u9798\uff0c\u559d\u9053\uff1a\u201c\u7ed9\u6211\u6eda\u4e0b\uff01\u201d\u8fd9\u6c49\u5b50\u6700\u521d \u8fd8\u4e0d\u4ee5\u4e3a\u7136\uff0c\u8272\u8ff7\u8ff7\u5730\u7b11\u9053\uff1a\u201c\u5c0f\u5a18\u5b50\uff0c\u600e\u7684\u8fd9\u6837\u51f6\u554a\uff01\u201d\u4ed6\u6015\u4f24\u4e86\u6768\u5a49\uff0c\u60f3\u8981\u7a7a\u624b\u593a\u5979\u7684 \u5251\uff0c\u8499\u53e4\u897f\u590f\u7684\u6b66\u58eb\u5927\u90fd\u64c5\u957f\u2018\u6454\u89d2\u2019\u4e4b\u6280\uff0c\u53e6\u6709\u4e00\u5957\u529f\u592b\uff0c\u4e2a\u4e2d\u9ad8\u624b\uff0c\u7528\u6454\u89d2\u7684\u6280\u5de7\u6765\u593a \u654c\u4eba\u5175\u5668\uff0c\u7edd\u4e0d\u4e9a\u4e8e\u4e2d\u539f\u6b66\u5b66\u7684\u201c\u7a7a\u624b\u5165\u767d\u5203\u201d\u7684\u529f\u592b\u3002 \u3000\u3000\u8fd9\u897f\u590f\u6c49\u5b50\u4ee5\u4e3a\u6768\u5a49\u662f\u4e2a\u5e74\u7eaa\u8f7b\u8f7b\u7684\u5c0f\u59d1\u5a18\uff0c\u80fd\u6709\u591a\u5927\u672c\u9886\uff1f\u4e0d\u6599\u6768\u5a49\u7684\u5251\u6cd5\u5374\u662f\u7531\u5979 \u54e5\u54e5\u6240\u6388\u7684\u5ce8\u5d4b\u6d3e\u7684\u5ae1\u4f20\u5251\u6cd5\u3002\u5ce8\u5d4b\u6d3e\u7684\u5251\u6cd5\u4ee5\u8f7b\u7075\u5947\u6ce5\u89c1\u957f\uff0c\u6768\u5a49\u5251\u950b\u4e00\u504f\uff0c\u9661\u7136\u95f4\u4ece\u90a3 \u6c49\u5b50\u610f\u60f3\u4e0d\u5230\u7684\u65b9\u4f4d\u524a\u6765\uff0c\u5251\u5149\u4e00\u95ea\uff0c\u524a\u4e0b\u4e86\u90a3\u6c49\u5b50\u7684\u534a\u622a\u5c0f\u6307\u5934\u3002 \u3000\u3000\u8fd9\u897f\u590f\u6c49\u5b50\u75db\u5f97\u54c7\u54c7\u5927\u53eb\uff0c\u53c8\u60ca\u53c8\u6012\uff0c\u90a3\u6c49\u4eba\u5374\u7b11\u9053\uff1a\u201c\u4f60\u6682\u4e14\u6536\u8d77\u601c\u9999\u60dc\u7389\u4e4b\u5fc3\uff0c\u628a \u8fd9\u96cc\u513f\u5f04\u5230\u4e86\u624b\u518d\u8bf4\u5427\uff01\u201d \u3000\u3000\u897f\u590f\u6c49\u5b50\u4e00\u58f0\u6012\u543c\uff0c\u559d\u9053\uff1a\u201c\u597d\u6cfc\u8fa3\u7684\u5c0f\u5996\u7cbe\uff0c\u770b\u4f60\u9003\u5f97\u51fa\u9003\u4e0d\u51fa\u8001\u5b50\u7684\u638c\u5fc3\uff01\u201d\u62bd\u51fa \u8170\u5200\uff0c\u821e\u8d77\u4e86\u4e00\u5708\u94f6\u8679\uff0c\u5200\u4e2d\u5939\u638c\uff0c\u5411\u6768\u5a49\u731b\u6251\u3002\u770b\u6765\u4ed6\u5df2\u662f\u63a5\u53d7\u4e86\u540c\u4f34\u7684\u610f\u89c1\uff0c\u5373\u4f7f\u65a9\u4f24 \u6768\u5a49\uff0c\u4e5f\u662f\u5728\u6240\u4e0d\u60dc\u7684\u4e86\u3002 \u3000\u3000\u6768\u5a49\u51b7\u7b11\u9053\uff1a\u201c\u53eb\u4f60\u77e5\u9053\u59d1\u5a18\u7684\u5389\u5bb3\uff01\u201d\u9752\u94a2\u5251\u626c\u7a7a\u4e00\u95ea\uff0c\u4f7f\u51fa\u4ee5\u5de7\u964d\u529b\u7684\u624b\u6cd5\uff0c\u5251\u5c16 \u8f7b\u8f7b\u4e00\u6311\uff0c\u628a\u654c\u4eba\u7684\u539a\u80cc\u6251\u5200\u6311\u5f00\uff0c\u5251\u950b\u500f\u5730\u5c31\u5411\u4ed6\u624b\u8155\u524a\u4e0b\u3002 \u3000\u3000\u897f\u590f\u6c49\u5b50\u8d77\u521d\u7a7a\u624b\u5bf9\u654c\uff0c\u7ed9\u6768\u5a49\u6240\u4f24\uff0c\u8fd8\u53ea\u9053\u662f\u81ea\u5df1\u5927\u610f\u6240\u81f4\uff0c\u4e0d\u600e\u8fd9\u4e00\u4e2a\u7f8e\u8c8c\u7684\u5c0f\u59d1 \u5a18\u6709\u4ec0\u4e48\u771f\u5b9e\u7684\u672c\u9886\uff0c\u6b64\u65f6\u65b9\u59cb\u77e5\u9053\u5389\u5bb3\u3002\u767e\u5fd9\u4e2d\u6025\u6025\u4e00\u4e2a\u201c\u5927\u5f2f\u8170\uff0c\u659c\u63d2\u67f3\u201d\uff0c\u786c\u751f\u751f\u5730 \u628a\u8170\u8eaf\u4e00\u62d7\uff0c\u98de\u811a\u6765\u8e4b\u6768\u5a49\u7684\u5251\uff0c\u53ea\u542c\u5f97\u201c\u55e4\u201d\u7684\u4e00\u58f0\u8f7b\u54cd\uff0c\u8fd9\u897f\u590f\u6c49\u5b50\u7684\u62a4\u624b\u76ae\u5957\u5df2\u7ed9\u5251 \u5c16\u5212\u7a7f\u3002\u4f46\u6768\u5a49\u89c1\u4ed6\u8fd9\u4e00\u811a\u8e22\u5f97\u51f6\u731b\uff0c\u4e0d\u613f\u786c\u62fc\uff0c\u4e5f\u53ea\u597d\u6682\u907f\u5176\u950b\u3002 \u3000\u3000\u53cc\u65b9\u7531\u5408\u800c\u5206\uff0c\u53c8\u518d\u7531\u5206\u800c\u5408\u3002\u897f\u590f\u6c49\u5b50\u5403\u4e86\u4e24\u6b21\u4e8f\uff0c\u54ea\u91cc\u8fd8\u6562\u6709\u4e1d\u6beb\u8f7b\u654c\uff0c\u6768\u5a49\u77e5\u9053 \u5bf9\u65b9\u7684\u6c14\u529b\u6bd4\u5979\u5927\u5f97\u591a\uff0c\u4e5f\u662f\u4e0d\u6562\u7c97\u5fc3\u5927\u610f\u3002\u4e00\u4e2a\u80dc\u5728\u6c14\u529b\u5145\u6c9b\uff0c\u4e00\u4e2a\u80dc\u5728\u5251\u6cd5\u8f7b\u7075\uff0c\u5404\u6709 \u6240\u957f\uff0c\u672c\u6765\u662f\u96be\u5206\u9ad8\u4e0b\u7684\uff0c\u4f46\u56e0\u8fd9\u897f\u590f\u6c49\u5b50\u4e00\u4e0a\u6765\u5c31\u5403\u4e86\u4e8f\uff0c\u4e00\u53ea\u6307\u5934\u88ab\u524a\u4e86\u53bb\uff0c\u867d\u65e0\u5927 \u788d\uff0c\u4f7f\u5200\u4ea6\u662f\u7a0d\u6709\u4e0d\u4fbf\uff0c\u4e0d\u8fc7\u6570\u62db\uff0c\u7ed9\u6768\u5a49\u6740\u5f97\u624b\u5fd9\u811a\u4e71\u3002 \u3000\u3000\u674e\u601d\u5357\u90a3\u8fb9\u4ee5\u4e00\u654c\u4e8c\uff0c\u5374\u662f\u9669\u8c61\u73af\u751f\u3002\u90a3\u8eab\u6750\u77ee\u5c0f\u7684\u6c49\u4eba\u8eab\u624b\u5341\u5206\u77eb\u6377\uff0c\u4e00\u5bf9\u5224\u5b98\u7b14\u76d8 \u65cb\u98de\u821e\uff0c\u5c45\u7136\u5728\u5251\u5149\u5ba0\u7f69\u4e4b\u4e0b\uff0c\u5c61\u6b21\u4e58\u6687\u62b5\u9699\uff0c\u6b3a\u5230\u674e\u601d\u5357\u8eab\u524d\uff0c\u8fd1\u8eab\u640f\u51fb\uff0c\u6b66\u5b66\u6709\u4e91\uff1a \u201c\u4e00\u5bf8\u77ed\uff0c\u4e00\u5bf8\u9669\u3002\u201d\u4ed6\u8fd9\u5bf9\u5224\u5b98\u7b14\u53ea\u6709\u4e8c\u5c3a\u516b\u5bf8\uff0c\u5374\u6bd4\u90a3\u7ea2\u8863\u5587\u561b\u4e03\u5c3a\u591a\u957f\u7684\u4e5d\u73af\u9521\u6756\u66f4 \u96be\u5bf9\u4ed8\uff01\u8fd1\u8eab\u640f\u6597\uff0c\u6bcf\u4e00\u62db\u90fd\u662f\u6307\u5411\u674e\u601d\u5357\u7684\u8981\u5bb3\u7a74\u9053\uff0c\u4efb\u8c01\u7a0d\u6709\u4e0d\u614e\uff0c\u90fd\u6709\u8840\u6e85\u5c18\u57c3\u4e4b \u9669\u3002 \u3000\u3000\u90a3\u7ea2\u8863\u5587\u561b\u6240\u4f7f\u7684\u62db\u6570\u6ca1\u6709\u8fd9\u6c49\u5b50\u7684\u72e0\u8fa3\uff0c\u4f46\u5374\u4e5f\u53e6\u6709\u4e00\u529f\u3002\u6597\u5230\u7d27\u5904\uff0c\u53ea\u542c\u5f97\u4ed6\u6756\u4e0a \u90a3\u4e5d\u4e2a\u94dc\u73af\u54cd\u4e2a\u4e0d\u505c\uff0c\u94c3\u58f0\u6742\u4e71\u65e0\u7ae0\uff0c\u6270\u5f97\u674e\u601d\u5357\u5fc3\u795e\u70e6\u4e71\uff0c\u597d\u51e0\u6b21\u9669\u4e9b\u7ed9\u4ed6\u6253\u7740\u3002 \u3000\u3000\u6768\u5a49\u770b\u89c1\u674e\u601d\u5357\u5f62\u52bf\u4e0d\u5999\uff0c\u94f6\u7259\u4e00\u54ac\uff0c\u5251\u8d70\u8f7b\u7075\uff0c\u95ea\u8fc7\u90a3\u897f\u590f\u6c49\u5b50\u7684\u5200\u950b\uff0c\u500f\u5730\u4e00\u4e2a \u201c\u9e5e\u5b50\u7ffb\u8eab\u201d\uff0c\u638c\u4e2d\u5251\u201c\u5012\u6253\u91d1\u949f\u201d\uff0c\u201c\u4e09\u73af\u5957\u6708\u201d\uff0c\u201c\u7389\u5973\u6295\u68ad\u201d\uff0c\u4e00\u8fde\u4e09\u62db\u8fc5\u6377\u65e0\u6bd4\u51cc \u5389\u975e\u5e38\u7684\u5251\u6cd5\uff0c\u523a\u54bd\u5589\uff0c\u6302\u4e24\u80a9\uff0c\u5176\u75be\u5982\u98ce\uff0c\u5176\u9510\u5982\u7bad\uff01\u6740\u5f97\u90a3\u897f\u590f\u6c49\u5b50\u62db\u67b6\u4e0d\u4f4f\uff0c\u8fde\u8fde\u540e \u9000\u3002\u6768\u5a49\u5c06\u4ed6\u8feb\u4e0b\u4e86\u5341\u51e0\u7ea7\u77f3\u9636\uff0c\u548c\u674e\u601d\u5357\u5df2\u662f\u4f1a\u5408\u4e00\u8d77\u3002 \u3000\u3000\u674e\u3001\u6768\u4e24\u53e3\u5b50\u4ee5\u4e8c\u654c\u4e09\uff0c\u5f62\u52bf\u8f83\u4e3a\u597d\u8f6c\u3002\u4f46\u8fd8\u662f\u5904\u5728\u4e0b\u98ce\u3002\u6768\u5a49\u529b\u5f31\uff0c\u6076\u6597\u4e86\u6570\u5341\u62db\u4e4b \u540e\uff0c\u7981\u4e0d\u4f4f\u988a\u6302\u6c57\u73e0\uff0c\u5a07\u5598\u8f7b\u5401\u3002\u897f\u590f\u6c49\u5b50\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u6740\u90a3\u7537\u7684\uff0c\u8fd9\u96cc\u513f\u7559\u4e0b\uff01\u201d \u3000\u3000\u90a3\u6c49\u4eba\u7b11\u9053\uff1a\u201c\u54c8\u56fe\u4e0a\u4eba\u65e9\u5df2\u582a\u7834\u8272\u7a7a\uff0c\u6211\u4e5f\u4e0d\u4f1a\u548c\u4f60\u6765\u62a2\u3002\u4f60\u6025\u4ec0\u4e48\uff0c\u662f\u4f60\u7684\u603b\u662f\u4f60 \u7684\u3002\u201d\u4e24\u4eba\u4e00\u5439\u4e00\u5531\uff0c\u7b80\u76f4\u662f\u628a\u6768\u5a49\u770b\u4f5c\u56ca\u4e2d\u4e4b\u7269\uff0c\u6768\u5a49\u6c14\u5f97\u80ba\u90fd\u70b8\u4e86\u3002\u9ad8\u624b\u6bd4\u6597\uff0c\u6700\u5fcc\u6c89 \u4e0d\u4f4f\u6c14\uff0c\u6768\u5a49\u75be\u98ce\u66b4\u96e8\u822c\u5730\u523a\u4e86\u4e00\u5341\u4e09\u5251\uff0c\u90fd\u7ed9\u90a3\u6c49\u4eba\u7684\u53cc\u7b14\u4e00\u4e00\u67b6\u5f00\uff0c\u90a3\u897f\u590f\u6c49\u5b50\u4e58\u673a\u53c8 \u65bd\u5c55\u4ed6\u7684\u201c\u6454\u89d2\u201d\u672c\u9886\uff0c\u6768\u5a49\u9669\u9669\u7ed9\u4ed6\u6293\u7740\u3002\u674e\u601d\u5357\u4e00\u62db\u201c\u5de6\u53f3\u5f00\u5f13\u201d\uff0c\u6311\u5f00\u7ea2\u8863\u5587\u561b\u7684\u4e5d \u73af\u9521\u6756\uff0c\u5251\u67c4\u4e00\u649e\uff0c\u628a\u90a3\u897f\u590f\u6c49\u5b50\u903c\u9000\u4e09\u6b65\uff0c\u8bf4\u9053\uff1a\u201c\u72d7\u5634\u91cc\u4e0d\u957f\u8c61\u7259\uff0c\u522b\u7406\u4ed6\u4eec\uff01\u201d\u6768\u5a49 \u542c\u674e\u601d\u5357\u4e4b\u529d\uff0c\u6c89\u4f4f\u4e86\u6c14\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u62fc\u5f97\u4e00\u4e2a\u5c31\u662f\u4e00\u4e2a\uff0c\u5018\u82e5\u662f\u62fc\u4e0d\u8fc7\uff0c\u6211\u5c31\u81ea\u5df1\u4e86 \u7ed3\u3002\u51b3\u4e0d\u843d\u5728\u4ed6\u4eec\u7684\u624b\u4e2d\u3002\u201d \u3000\u3000\u773c\u770b\u5f62\u52bf\u8d8a\u6765\u8d8a\u9669\uff0c\u5ffd\u542c\u5f97\u6089\u6089\u7d22\u7d22\u7684\u58f0\u97f3\uff0c\u674e\u601d\u5357\u62bd\u773c\u4e00\u770b\uff0c\u5374\u539f\u6765\u662f\u90a3\u4e2a\u53d7\u4f24\u7684\u6c49 \u5b50\u4ece\u78e8\u623f\u91cc\u722c\u51fa\u6765\u3002\u674e\u601d\u5357\u5927\u5403\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u4f60\u4f24\u5f97\u8fd9\u4e48\u91cd\uff0c\u722c\u51fa\u6765\u4e0d\u662f\u767d\u767d\u9001\u6b7b\u5417\uff1f\u201d \u6ca1\u6709\u529e\u6cd5\uff0c\u53ea\u597d\u62fc\u547d\u62a2\u653b\uff0c\u5e0c\u671b\u7eca\u5f97\u4f4f\u4e09\u4e2a\u654c\u624b\uff0c\u4e0d\u8ba9\u4ed6\u4eec\u62bd\u51fa\u8eab\u5b50\uff0c\u8dd1\u53bb\u62ff\u4eba\u3002 \u3000\u3000\u53d7\u4f24\u7684\u90a3\u6c49\u5b50\u4e00\u7ea7\u7ea7\u5730\u722c\u4e0b\u4e86\u77f3\u9636\uff0c\u9c9c\u8840\u4e00\u70b9\u70b9\u5730\u6ef4\u5728\u5730\u4e0a\uff0c\u7ec8\u4e8e\u7ed9\u4ed6\u722c\u5230\u4e86\u95e8\u53e3\u3002\u7ea2 \u8863\u5587\u561b\u5728\u674e\u601d\u5357\u51cc\u5389\u7684\u5251\u6cd5\u5a01\u80c1\u4e4b\u4e0b\uff0c\u4e0d\u6562\u8f6c\u8eab\uff0c\u6025\u5f97\u5927\u53eb\u9053\uff1a\u201c\u4e0d\u80fd\u7ed9\u8fd9\u5c0f\u8d3c\u9003\u8dd1\uff01\u201d\u90a3 \u6c49\u4eba\u9053\uff1a\u201c\u5927\u5e08\u4e0d\u7528\u62c5\u5fc3\uff0c\u6211\u53bb\u628a\u4ed6\u62ff\u6765\uff01\u201d\u53cc\u7b14\u4e00\u8d77\uff0c\u6307\u4e1c\u6253\u897f\uff0c\u770b\u4f3c\u70b9\u5411\u674e\u601d\u5357\u7684\u524d \u80f8\uff0c\u7b14\u5cf0\u5ffd\u5730\u4e00\u8f6c\uff0c\u5374\u5411\u6768\u5a49\u51b2\u4e86\u8fc7\u53bb\uff0c\u6768\u5a49\u906e\u62e6\u4e0d\u4f4f\uff0c\u8eab\u5f62\u5fae\u4fa7\uff0c\u90a3\u6c49\u5b50\u51b2\u5f00\u4e86\u7f3a\u53e3\uff0c\u500f \u5730\u5c31\u4ece\u6768\u5a49\u8eab\u8fb9\u63a0\u8fc7\u3002 \u3000\u3000\u8fd9\u4eba\u4ee5\u4e3a\u90a3\u59d3\u9f99\u7684\u6c49\u5b50\u5df2\u53d7\u91cd\u4f24\uff0c\u53ea\u8981\u81ea\u5df1\u4e00\u5230\uff0c\u8fd8\u4e0d\u662f\u624b\u5230\u62ff\u6765\uff1f\u64d2\u4eba\u4e4b\u540e\uff0c\u56de\u8fc7\u5934 \u6765\uff0c\u8fd8\u53ef\u4ee5\u518d\u64d2\u6768\u5a49\uff0c\u6700\u591a\u4e0d\u8fc7\u803d\u6401\u7247\u523b\uff0c\u6599\u60f3\u5728\u8fd9\u7247\u523b\u4e4b\u95f4\uff0c\u81ea\u5df1\u8fd9\u4e24\u4e2a\u540c\u4f34\u603b\u4e0d\u81f3\u4e8e\u5c31 \u7ed9\u5bf9\u65b9\u4f24\u4e86\u3002 \u3000\u3000\u8fd9\u4eba\u662f\u4e2a\u6c5f\u6e56\u4e0a\u7684\u5927\u884c\u5bb6\uff0c\u660e\u77e5\u5bf9\u65b9\u6beb\u65e0\u53cd\u6251\u4e4b\u529b\uff0c\u8fd8\u662f\u4e0d\u6562\u5927\u610f\u3002\u5f53\u4e0b\u628a\u4e00\u652f\u5224\u5b98\u7b14 \u6536\u8d77\uff0c\u53e6\u4e00\u652f\u7b14\u4ecd\u7136\u6301\u5728\u624b\u4e2d\uff0c\u8dd1\u5230\u90a3\u6c49\u5b50\u7684\u8eab\u8fb9\uff0c\u4e00\u6293\u5c31\u5411\u4ed6\u6293\u4e0b\u3002\u4ed6\u89c1\u8fd9\u6c49\u5b50\u4f24\u5f97\u91cd\uff0c \u6015\u7528\u5224\u5b98\u7b14\u4f1a\u628a\u4ed6\u6233\u6b7b\u3002 \u3000\u3000\u4ed6\u5df2\u7ecf\u662f\u6709\u6240\u9632\u5907\u7684\u4e86\uff0c\u4e0d\u6599\u8fd8\u662f\u7740\u4e86\u90a3\u6c49\u5b50\u7684\u9053\u513f\u3002\u90a3\u6c49\u5b50\u624b\u4e2d\u6263\u7740\u4e00\u679a\u5c0f\u5c0f\u7684\u6bd2\u9f99 \u9556\uff0c\u53cc\u638c\u76f8\u4ea4\uff0c\u201c\u5657\u201d\u7684\u4e00\u58f0\uff0c\u6bd2\u9f99\u9556\u7684\u9556\u5c16\u5df2\u662f\u523a\u7834\u4e86\u90a3\u6c49\u5b50\u7684\u624b\u5fc3\u3002\u90a3\u6c49\u5b50\u7684\u53d7\u4f24\u867d\u7136 \u4e0d\u5047\uff0c\u4f46\u4f24\u5f97\u90a3\u6837\u6c89\u91cd\u5374\u662f\u88c5\u51fa\u6765\u7684\u3002 \u3000\u3000\u90a3\u6c49\u4eba\u5927\u543c\u4e00\u58f0\uff0c\u5de6\u624b\u5224\u5b98\u7b14\u63d2\u4e86\u4e0b\u53bb\uff0c\u53ef\u60dc\u5df2\u7ecf\u8fdf\u4e86\u3002\u8981\u662f\u4ed6\u5148\u7528\u5224\u5b98\u7b14\u70b9\u5bf9\u65b9\u7a74 \u9063\uff0c\u90a3\u53d7\u4f24\u7684\u6c49\u5b50\u51b3\u9003\u4e0d\u4e86\u3002\u5982\u4eca\u624d\u7528\u5224\u5b98\u7b14\uff0c\u4e00\u7b14\u521a\u521a\u6233\u51fa\uff0c\u53cc\u773c\u5df2\u662f\u660f\u82b1\u3002\u539f\u6765\u90a3\u201c\u6bd2 \u9f99\u9556\u201d\u4e43\u662f\u89c1\u8840\u5c01\u5589\u7684\u6697\u5668\uff0c\u4e2d\u6bd2\u4e4b\u540e\uff0c\u53d1\u4f5c\u5f97\u975e\u5e38\u4e4b\u5feb\uff0c\u8fd9\u4e00\u6233\u5931\u4e86\u51c6\u5934\uff0c\u53d7\u4f24\u90a3\u6c49\u5b50\u4e00 \u4e2a\u201c\u61d2\u9a74\u6253\u6eda\u201d\uff0c\u4f7f\u5c3d\u6b8b\u4f59\u6c14\u529b\uff0c\u6eda\u8fc7\u4e86\u4e00\u8fb9\u3002 \u3000\u3000\u90a3\u6c49\u4eba\u811a\u6b65\u8e09\u8dc4\uff0c\u5224\u5b98\u7b14\u5931\u624b\u5760\u5730\u3002\u53d7\u4f24\u90a3\u6c49\u5b50\u62fe\u8d77\u4e86\u5224\u5b98\u7b14\uff0c\u559d\u9053\uff1a\u201c\u539f\u7269\u5949\u8fd8\uff01\u201d \u98de\u7b14\u4e00\u63b7\uff0c\u90a3\u6c49\u4eba\u5e94\u58f0\u800c\u5012\u3002 \u3000\u3000\u53d7\u4f24\u90a3\u6c49\u5b50\u722c\u5230\u4ed6\u7684\u8eab\u8fb9\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u60f3\u5bb3\u6211\uff0c\u73b0\u5728\u4f60\u53ef\u77e5\u9053\u6bd2\u9f99\u9556\u7684\u6ecb\u5473\u4e86\u5427\uff1f\u600e \u4e48\u6837\uff0c\u597d\u4e0d\u597d\u5c1d\uff1f\u201d \u3000\u3000\u90a3\u6c49\u4eba\u4e2d\u4e86\u6bd2\u9f99\u9556\uff0c\u6b64\u65f6\u5df2\u662f\u5267\u6bd2\u5927\u53d1\uff0c\u8eab\u4f53\u5185\u5c31\u597d\u4f3c\u6709\u5343\u767e\u6761\u5c0f\u86c7\uff0c\u5728\u4ed6\u4e94\u810f\u516d\u8151\u4e2d \u4e71\u54ac\u4e71\u94bb\u3002\u90a3\u6c49\u4eba\u6d51\u8eab\u51b7\u6c57\uff0c\u54ac\u7740\u7259\u53eb\u9053\uff1a\u201c\u9f99\u7237\uff0c\u4f60\u505a\u505a\u597d\u4e8b\uff0c\u4e00\u5200\u628a\u6211\u6740\u4e86\u5427\uff01\u201d \u3000\u3000\u59d3\u9f99\u90a3\u6c49\u5b50\u9053\uff1a\u201c\u6740\u4f60\uff1f\u6709\u90a3\u4e48\u4fbf\u5b9c\u7684\u4e8b\u4e86\u4f60\u662f\u4e0d\u662f\u9633\u5929\u96f7\u7684\u95e8\u4e0b\u67b6\u5b50\uff0c\u4f60\u90a3\u8d3c\u5e08\u7236 \u5462\uff1f\u5feb\u8bf4\uff01\u201d \u3000\u3000\u90a3\u6c49\u4eba\u9053\uff1a\u201c\u6211\u7684\u5e08\u7236\u65e9\u5df2\u56de\u5230\u5927\u90fd\u517b\u4f24\u4e86\u3002\u201d\u59d3\u9f99\u7684\u6c49\u5b50\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u539f \u6765\u4f60\u4eec\u5e08\u5f92\u65e9\u5df2\u52fe\u7ed3\u91d1\u864f\uff0c\u5982\u4eca\u53c8\u6765\u79c1\u901a\u8499\u53e4\u9791\u5b50\u3002\u201d \u3000\u3000\u90a3\u6c49\u4eba\u53eb\u9053\uff1a\u201c\u51a4\u6709\u5934\uff0c\u503a\u6709\u4e3b\u3002\u4f60\u8981\u62a5\u4ec7\uff0c\u6709\u80c6\u7684\u53bb\u5927\u90fd\u627e\u6211\u5e08\u7236\u3002\u5374\u4f55\u82e6\u5c06\u6211\u6298 \u78e8\uff1f\u6c42\u6c42\u4f60\uff0c\u5feb\u5feb\u628a\u6211\u6740\u4e86\uff01\u201d \u3000\u3000\u59d3\u5c24\u7684\u6c49\u5b50\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u6025\u4ec0\u4e48\uff1f\u4f60\u8fd8\u6709\u534a\u4e2a\u65f6\u8fb0\uff0c\u5f85\u5230\u6bd2\u6c14\u4fb5\u5165\u5fc3\u623f\u624d\u201c\u4f1a\u65ad\u6c14\u3002\u201d \u90a3\u6c49\u5b50\u547b\u541f\u9053\uff1a\u201c\u6211\u3001\u6211\u53ef\u662f\u53d7\u4e0d\u4e86\u5566\uff01\u201d \u3000\u3000\u59d3\u9f99\u7684\u6c49\u5b50\u9053\uff1a\u201c\u5bb3\u6211\u5e08\u7236\u7684\u9664\u4e86\u9633\u8001\u8d3c\u4e4b\u5916\uff0c\u8fd8\u6709\u4f55\u4eba\uff1f\u6211\u4eec\u5e2e\u4e2d\uff0c\u8c01\u662f\u4f60\u4eec\u7684\u5185 \u5e94\uff0c\u4f60\u4e00\u62db\u51fa\u6765\uff0c\u6211\u5c31\u7ed9\u4f60\u4e00\u4e2a\u723d\u5feb\uff01\u201d\u201d \u3000\u3000\u4ed6\u4ee5\u4e3a\u8fd9\u4eba\u53d7\u4e0d\u4e86\u6298\u78e8\uff0c\u5b9a\u7136\u5982\u5b9e\u62db\u4f9b\uff0c\u4e0d\u6599\u8fd9\u6c49\u4eba\u5ffd\u5730\u5389\u58f0\u53eb\u9053\uff1a\u201c\u6211\u53cd\u6b63\u662f\u6d3b\u4e0d \u4e86\uff0c\u4f60\u8fd8\u80fd\u591f\u5c06\u6211\u600e\u6837\uff1f\u54fc\uff0c\u59d3\u9f99\u7684\u5c0f\u5b50\uff0c\u7b97\u4f60\u591f\u72e0\uff0c\u4f46\u8001\u5b50\u5f52\u5929\u4e4b\u540e\uff0c\u8c05\u4f60\u8fd9\u5c0f\u5b50\u4e5f\u6d3b\u4e0d \u4e86\u591a\u957f\uff01\u201d\u84d1\u5730\u4e00\u53e3\u9c9c\u8840\u55b7\u4e86\u51fa\u6765\uff0c\u55b7\u5f97\u90a3\u59d3\u9f99\u7684\u6c49\u5b50\u6ee1\u5934\u6ee1\u9762\u3002\u539f\u6765\u4ed6\u4e0d\u613f\u591a\u53d7\u71ac\u714e\uff0c\u4e8e \u662f\u62fc\u53d7\u4e00\u65f6\u4e4b\u82e6\uff0c\u54ac\u65ad\u820c\u5c16\uff0c\u81ea\u6563\u529f\u529b\uff0c\u529f\u529b\u4e00\u6563\uff0c\u6bd2\u6c14\u6563\u53d1\u66f4\u5feb\uff0c\u767b\u65f6\u4e03\u7a8d\u6d41\u8840\u800c\u4ea1\u3002 \u3000\u3000\u90a3\u53e3\u8840\u662f\u6709\u6bd2\u7684\uff0c\u55b7\u4e86\u8fd9\u59d3\u9f99\u7684\u6c49\u5b50\u4e00\u9762\uff0c\u867d\u7136\u4e0d\u81f4\u4fbf\u6709\u6027\u547d\u4e4b\u5371\uff0c\u4f46\u56e0\u4ed6\u672c\u6765\u5df2\u53d7\u91cd \u4f24\uff0c\u518d\u7ed9\u8fd9\u6bd2\u8840\u4e00\u55b7\uff0c\u8165\u81ed\u4e4b\u6c14\u6251\u9f3b\uff0c\u4ed6\u53c8\u4e0d\u80fd\u95ed\u4e86\u547c\u5438\uff0c\u56e0\u6b64\u4e5f\u662f\u6781\u4e3a\u96be\u53d7\uff0c\u51e0\u4e4e\u5c31\u8981\u7a92 \u606f\u3002\u8fd9\u6c49\u5b50\u5f3a\u81ea\u652f\u6301\uff0c\u5438\u4e86\u4e00\u53e3\u8165\u6c14\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u4f46\u76fc\u5e08\u59b9\u5feb\u70b9\u5230\u6765\uff01\u201d \u3000\u3000\u5f53\u8fd9\u6c49\u5b50\u5411\u90a3\u6c49\u4eba\u8feb\u95ee\u53e3\u4f9b\u7684\u65f6\u5019\uff0c\u7ea2\u8863\u5587\u561b\u548c\u90a3\u897f\u590f\u6b66\u58eb\u8981\u60f3\u8fc7\u6765\u63f4\u6551\uff0c\u53ef\u662f\u7ed9\u674e\u601d \u5357\u3001\u6768\u5a49\u4e24\u53e3\u957f\u5251\u8feb\u4f4f\uff0c\u81ea\u987e\u4e0d\u6687\uff0c\u54ea\u91cc\u80fd\u591f\u62bd\u51fa\u8eab\u5b50\u3002 \u3000\u3000\u6768\u5a49\u6068\u90a3\u897f\u590f\u6c49\u5b50\u5bf9\u5979\u65e0\u793c\uff0c\u624b\u4e2d\u7684\u9752\u94a2\u5251\u72c2\u98ce\u66b4\u96e8\u822c\u5730\u5411\u4ed6\u653b\u53bb\uff0c\u84e6\u5730\u559d\u9053\uff1a \u201c\u7740\uff01\u201d\u537f\u7684\u4e00\u5251\uff0c\u7a7f\u8fc7\u4e86\u90a3\u897f\u590f\u6c49\u5b50\u7684\u5589\u5499\uff0c\u62bd\u51fa\u5251\u6765\uff0c\u90a3\u53e3\u5251\u90fd\u67d3\u5f97\u901a\u7ea2\u4e86\u3002 \u3000\u3000\u7ea2\u8863\u5587\u561b\u89c1\u540c\u4f34\u88ab\u6740\uff0c\u8033\u8fb9\u84e6\u5730\u53c8\u542c\u5f97\u90a3\u6c49\u4eba\u7684\u5389\u547c\uff0c\u767e\u5fd9\u4e2d\u56de\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u90a3\u6c49\u4eba\u4ea6 \u5df2\u6b7b\u6389\uff0c\u7ea2\u8863\u5587\u561b\u72ec\u529b\u96be\u652f\uff0c\u5fc3\u80c6\u4ff1\u88c2\u3002\u706b\u5149\u6620\u7167\u4e4b\u4e0b\uff0c\u6768\u5a49\u90a3\u628a\u67d3\u5f97\u901a\u7ea2\u7684\u957f\u5251\u53c8\u523a\u4e86\u8fc7 \u6765\uff0c\u7ea2\u8863\u5587\u561b\u5413\u5f97\u9b42\u98de\u9b44\u6563\uff01 \u3000\u3000\u95ea\u7740\u8840\u5149\u7684\u5b9d\u5251\u9a87\u4eba\u5fc3\u9b44\uff0c\u7ea2\u8863\u5587\u561b\u6b63\u5728\u62b5\u5fa1\u674e\u601d\u5357\u7684\u8fde\u73af\u653b\u52bf\uff0c\u773c\u770b\u8fd9\u4e00\u628a\u901a\u7ea2\u7684\u5b9d \u5251\u523a\u4e86\u8fc7\u6765\uff0c\u5251\u52bf\u51cc\u5389\uff0c\u65e0\u53ef\u62b5\u6321\uff0c\u4e0d\u7531\u5f97\u9b44\u6563\u9b42\u98de\uff0c\u5927\u53eb\u4e00\u58f0\uff1a\u201c\u543e\u547d\u4f11\u77e3\uff01\u201d \u3000\u3000\u4f46\u5b66\u6b66\u4e4b\u4eba\uff0c\u5230\u4e86\u751f\u6b7b\u5173\u5934\uff0c\u867d\u7136\u660e\u77e5\u62b5\u6321\u4e0d\u4f4f\uff0c\u51fa\u4e8e\u6c42\u751f\u7684\u672c\u80fd\uff0c\u4e5f\u8fd8\u662f\u8981\u5c3d\u529b\u6323\u624e \u7684\uff0c\u7ea2\u8863\u5587\u561b\u7684\u4e5d\u73af\u9521\u6756\u67b6\u7740\u674e\u601d\u5357\u7684\u957f\u5251\uff0c\u62bd\u4e0d\u51fa\u5175\u5668\uff0c\u4fbf\u98de\u8d77\u817f\u6765\uff0c\u8e22\u6768\u5a49\u8fd9\u628a\u8840\u5251\u3002 \u3000\u3000\u4ee5\u6768\u5a49\u7684\u5251\u52bf\u529b\u9053\uff0c\u8fd9\u4e00\u5251\u672c\u6765\u53ef\u4ee5\u524a\u65ad\u4ed6\u7684\u4e00\u6761\u817f\uff0c\u7ea2\u8863\u5587\u561b\u81ea\u5df1\u4e5f\u4e0d\u6562\u5b58\u7740\u4fa5\u5e78\u7684 \u5ff5\u5934\uff0c\u4e0d\u6599\u4e00\u8e22\u4e4b\u4e0b\uff0c\u53ea\u542c\u5f97\u201c\u94db\u201d\u7684\u4e00\u58f0\uff0c\u6768\u5a49\u8fd9\u628a\u8840\u5251\u7adf\u7136\u7ed9\u4ed6\u8e22\u843d\uff0c\u5927\u51fa\u7ea2\u8863\u5587\u561b\u610f \u6599\u4e4b\u5916\uff01 \u3000\u3000\u539f\u6765\u8fd9\u662f\u6768\u5a49\u6709\u751f\u4ee5\u6765\u7b2c\u4e00\u6b21\u4eb2\u624b\u6740\u4eba\uff0c\u5728\u5979\u4e00\u5251\u523a\u8fdb\u90a3\u897f\u590f\u6c49\u5b50\u5589\u5499\u4e4b\u65f6\uff0c\u7531\u4e8e\u6c14\u6012 \u4ea4\u52a0\uff0c\u8fd8\u4e0d\u89c9\u600e\u4e48\u3002\u6740\u4eba\u4e4b\u540e\uff0c\u62d4\u51fa\u5251\u6765\uff0c\u8840\u5149\u6620\u5165\u773c\u5e18\uff0c\u65b9\u59cb\u5413\u5f97\u614c\u4e86\u3002\u8ddf\u7740\u5411\u7ea2\u8863\u5587\u561b \u523a\u51fa\u7684\u90a3\u4e00\u5251\uff0c\u4e0d\u7531\u5f97\u624b\u4e5f\u53d1\u8f6f\uff0c\u770b\u4f3c\u51cc\u5389\uff0c\u5b9e\u662f\u65e0\u529b\u3002 \u3000\u3000\u7ea2\u8863\u5587\u561b\u53c8\u60ca\u53c8\u559c\uff0c\u8fde\u5fd9\u5411\u6768\u5a49\u51b2\u8fc7\u53bb\uff0c\u6768\u5a49\u8dcc\u843d\u5b9d\u5251\uff0c\u970d\u7136\u4e00\u7701\uff0c\u95ea\u8fc7\u4e00\u8fb9\u3002\u674e\u601d\u5357 \u559d\u9053\uff1a\u201c\u54ea\u91cc\u8d70\uff1f\u201d\u7ea2\u8863\u5587\u561b\u4e00\u6296\u4e5d\u73af\u9521\u6756\uff0c\u54e8\u54e8\u8fde\u58f0\uff0c\u4e5d\u4e2a\u94dc\u73af\uff0c\u4e00\u9f50\u98de\u51fa\u3002\u539f\u6765\u4ed6\u4ed7\u4e0a \u7684\u94dc\u73af\u4e5f\u662f\u53ef\u4ee5\u5f53\u4f5c\u6697\u5668\u4f7f\u7528\u7684\uff0c\u5e73\u65f6\u51b3\u4e0d\u8f7b\u6613\u4f7f\u7528\uff0c\u6b64\u65f6\u4e3a\u4e86\u6551\u547d\uff0c\u53ea\u597d\u4f7f\u51fa\u6700\u540e\u4e00\u62db\u3002 \u3000\u3000\u6768\u5a49\u8dcc\u4e86\u5b9d\u5251\uff0c\u674e\u601d\u5357\u6050\u5979\u7a7a\u624b\u65e0\u6cd5\u62db\u67b6\uff0c\u5f53\u4e0b\u5fd9\u821e\u8d77\u957f\u5251\uff0c\u5251\u5149\u5316\u4f5c\u4e86\u4e00\u9053\u5149\u8f6e\uff0c\u4fdd \u62a4\u6768\u5a49\u3002\u53ea\u542c\u5f97\u54e8\u54e8\u4e4b\u58f0\uff0c\u4e0d\u7edd\u4e8e\u8033\uff0c\u4e5d\u4e2a\u94dc\u73af\uff0c\u90fd\u7ed9\u674e\u601d\u5357\u6253\u843d\u3002\u4f46\u90a3\u7ea2\u8863\u5587\u561b\u4e5f\u9003\u51fa\u53bb \u4e86\u3002 \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u5a49\u59b9\uff0c\u4f60\u600e\u4e48\u4e86\uff1f\u201d\u6768\u5a49\u62fe\u8d77\u5b9d\u5251\uff0c\u63e9\u62b9\u5e72\u51c0\u5251\u4e0a\u7684\u8840\u6e0d\uff0c\u8bf4\u9053\uff1a\u201c\u6ca1\u4ec0 \u4e48\uff0c\u53ea\u662f\u6211\u521d\u6b21\u6740\u4eba\uff0c\u6709\u70b9\u5fc3\u614c\u3002\u201d\u674e\u601d\u5357\u7b11\u9053\uff1a\u201c\u4f60\u53ea\u8981\u8fd9\u4e48\u60f3\uff1a\u6211\u4e0d\u6740\u4ed6\uff0c\u4ed6\u5c31\u6740\u6211\uff0c \u90a3\u4f60\u5c31\u4e0d\u4f1a\u5bb3\u6015\u4e86\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u6276\u8d77\u90a3\u53d7\u4f24\u7684\u6c49\u5b50\uff0c\u7ed9\u4ed6\u6b62\u8840\u6577\u4f24\u3002\u90a3\u6c49\u5b50\u9053\uff1a\u201c\u670b\u53cb\uff0c\u4f60\u4e0d\u5fc5\u8d39\u795e\u4e86\uff0c\u6211\u4e0d\u6210 \u5566\uff01\u53ea\u662f\uff0c\u53ea\u662f\u2014\u2014\u201d\u770b\u6765\u4ed6\u662f\u6709\u4e8b\u4ea4\u6258\uff0c\u4f46\u6c14\u8870\u529b\u7aed\uff0c\u8bf4\u8bdd\u5df2\u662f\u6296\u4e0d\u6210\u58f0\u3002 \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u5a49\u59b9\uff0c\u54b1\u4eec\u8fd8\u6709\u534a\u652f\u4eba\u53c2\uff0c\u8bf7\u4f60\u62ff\u51fa\u6765\uff0c\u524a\u6210\u4e00\u7247\u7247\u3002\u201d\u6768\u5a49\u9053\uff1a \u201c\u662f\uff01\u201d\u524a\u4e0b\u4e86\u5341\u591a\u7247\u4eba\u53c2\uff0c\u4e00\u7247\u7247\u7684\u7eb3\u5165\u90a3\u6c49\u5b50\u53e3\u4e2d\u3002\u8fd9\u662f\u91ce\u5c71\u4eba\u53c2\uff0c\u4fdd\u6c14\u57f9\u5143\uff0c\u529f\u6548\u6700 \u4f73\u3002\u674e\u601d\u5357\u8981\u6768\u5a49\u628a\u5b83\u524a\u6210\u788e\u7247\uff0c\u90a3\u662f\u5229\u4e8e\u8fd9\u6c49\u5b50\u5bb9\u6613\u5480\u56bc\u541e\u670d\u4e4b\u6545\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u8fd9\u6c49\u5b50\u7cbe\u795e\u7a0d\u632f\uff0c\u8bf4\u9053\uff1a\u201c\u9f99\u521a\u591a\u8c22\u6551\u547d\u4e4b\u6069\u3002\u539f\u6765\u4f60\u5c31\u662f\u8c37\u5e73\u9633\u7684\u5f1f\u5b50\u674e \u601d\u5357\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u73b0\u5728\u4fe1\u5f97\u6211\u4e86\u5427\uff1f\u4f60\u8fd9\u6837\u95ee\uff0c\u6562\u60c5\u662f\u542c\u8c01\u8bf4\u8fc7\u6211\u7684\u540d\u5b57\uff1f\u201d \u3000\u3000\u9f99\u521a\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u5b5f\u59d1\u5a18\u8bf4\u4f60\u662f\u597d\u4eba\uff0c\u679c\u7136\u4e0d\u9519\u3002\u5b5f\u5927\u4fa0\u5374\u662f\u8bef\u4f1a\u4f60\u4e86\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u53c8\u60ca\u53c8\u559c\uff0c\u9053\uff1a\u201c\u4f60\u89c1\u7740\u4e86\u5b5f\u5927\u4fa0\u4e86\uff1f\u201d \u3000\u3000\u9f99\u521a\u9053\uff1a\u201c\u6b63\u662f\u3002\u5b5f\u5927\u4fa0\u7236\u5973\u4ece\u8499\u53e4\u56de\u6765\uff0c\u66fe\u7279\u5730\u6765\u5230\u6211\u4eec\u5c71\u5be8\u62a5\u8baf\uff0c\u4ea4\u51fa\u4e86\u6bd2\u9f99\u9556\uff0c \u6211\u624d\u77e5\u9053\u5e08\u7236\u4ed6\u3001\u4ed6\u8001\u4eba\u5bb6\u5df2\u7ecf\u4e0d\u5e78\u88ab\u4eba\u5bb3\u6b7b\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u4f60\u662f\u5c60\u5927\u4fa0\u5c60\u767e\u57ce\u7684\u5f1f\u5b50\u3002\u602a\u4e0d\u5f97\u4f60\u4f1a\u4f7f\u6bd2\u9f99\u9556\u3002\u201d \u3000\u3000\u6768\u5a49\u63d2\u53e3\u9053\uff1a\u201c\u90a3\u4f4d\u5b5f\u5927\u4fa0\u5462\uff1f\u201d\u5979\u53e3\u91cc\u95ee\u7684\u662f\u5b5f\u5927\u4fa0\uff0c\u5176\u5b9e\u662f\u60f3\u77e5\u9053\u5b5f\u660e\u971e\u7684\u6d88\u606f\u3002 \u3000\u3000\u9f99\u521a\u9053\uff1a\u201c\u5b5f\u5927\u4fa0\u6709\u8981\u7d27\u7684\u4e8b\u60c5\uff0c\u5fc5\u987b\u8d76\u56de\u6c5f\u5357\uff0c\u6240\u4ee5\u53ea\u5728\u6211\u4eec\u7684\u5be8\u5b50\u91cc\u4f4f\u5bbf\u4e00\u5bb5\uff0c\u7b2c \u4e8c\u5929\u5c31\u72ec\u81ea\u56de\u53bb\u4e86\u3002\u201d \u3000\u3000\u6768\u5a49\u542c\u4e86\u201c\u72ec\u81ea\u201d\u4e8c\u5b57\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5b5f\u660e\u971e\u5462\uff1f\u201d\u4f46\u662f\u5979\u6015\u592a\u7740\u75d5\u8ff9\uff0c\u8fd9\u4e00\u53e5\u8bdd\u5728\u5979 \u820c\u5c16\u6253\u6eda\uff0c\u5374\u8fd8\u6ca1\u6709\u95ee\u51fa\u6765\u3002 \u3000\u3000\u6b63\u5728\u5979\u60f3\u95ee\u672a\u95ee\u4e4b\u9645\uff0c\u674e\u601d\u5357\u5df2\u5148\u95ee\u9053\uff1a\u201c\u9f99\u5144\uff0c\u90a3\u4e48\u4f60\u6b64\u6b21\u60f3\u5fc5\u662f\u4e3a\u4ee4\u5e08\u62a5\u4ec7\u800c\u6765\u7684 \u4e86\u3002\u4f46\u53ea\u4f60\u4e00\u4e2a\u4eba\u4e48\uff1f\u201d\u8981\u77e5\u5c60\u767e\u57ce\u4e43\u5317\u65b9\u7684\u7eff\u6797\u4e4b\u96c4\uff0c\u95e8\u4eba\u5f1f\u5b50\u4eb2\u53cb\u4e0b\u5c5e\uff0c\u672c\u9886\u9ad8\u5f3a\u7684\u4e0d \u77e5\u591a\u5c11\u3002\u662f\u4ee5\u674e\u601d\u5357\u6599\u60f3\u82e5\u4e3a\u5c60\u767e\u57ce\u62a5\u4ec7\uff0c\u4e0d\u5e94\u53ea\u662f\u9f99\u521a\u4e00\u4eba\u3002 \u3000\u3000\u9f99\u521a\u9053\uff1a\u201c\u5b5f\u5927\u4fa0\u6765\u62a5\u8baf\u4e4b\u65f6\uff0c\u53ea\u6709\u6211\u548c\u4e00\u4f4d\u5e08\u5f1f\u7559\u5728\u5c71\u5be8\uff0c\u53e6\u5916\u51e0\u4f4d\u540c\u95e8\u90fd\u5206\u522b\u5230\u5404 \u5730\u529e\u4e8b\u53bb\u4e86\u3002\u6211\u4eec\u8feb\u4e0d\u53ca\u5f85\uff0c\u662f\u4ee5\u53ea\u597d\u4e00\u9762\u6d3e\u4eba\u5411\u540c\u95e8\u62a5\u8baf\uff0c\u6211\u4eec\u7b2c\u4e8c\u5929\u5c31\u7acb\u5373\u4e0b\u5c71\uff0c\u6211\u4eec \u672c\u6765\u5171\u6709\u56db\u4e2a\u4eba\u7684\uff0c\u6211\u548c\u56db\u5e08\u5f1f\u548c\u4e24\u4f4d\u5c71\u5be8\u7684\u5934\u9886\uff0c\u5509\uff0c\u4f46\u73b0\u5728\u5374\u53ea\u5269\u4e0b\u6211\u4e00\u4e2a\u4eba\u5566\uff01\u201d\u8bf4 \u81f3\u6b64\u5904\uff0c\u9762\u5982\u91d1\u7eb8\u3002 \u3000\u3000\u674e\u601d\u5357\u731c\u60f3\u90a3\u51e0\u4e2a\u4eba\u4e00\u8d77\u662f\u5728\u8def\u4e0a\u906d\u4e86\u4e0d\u5e78\uff0c\u4e0d\u613f\u5f15\u8d77\u9f99\u521a\u4f24\u5fc3\uff0c\u4e8e\u662f\u628a\u6c34\u58f6\u9012\u5230\u9f99\u521a \u53e3\u8fb9\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u559d\u4e00\u53e3\u6c34\uff0c\u6162\u6162\u518d\u8bf4\u3002\u201d \u3000\u3000\u9f99\u521a\u559d\u4e86\u4e00\u53e3\u6c34\uff0c\u7ee7\u7eed\u9053\uff1a\u201c\u6211\u4eec\u5f97\u77e5\u5669\u8017\uff0c\u51b3\u610f\u4e3a\u5e08\u7236\u62a5\u4ec7\u3002\u4e0d\u6599\u4ec7\u4eba\u662f\u8c01\u8fd8\u672a\u77e5 \u9053\uff0c\u6211\u4eec\u5c31\u7ed9\u4ed6\u7684\u4eba\u7f00\u4e0a\u4e86\u3002\u674e\u516c\u5b50\uff0c\u8981\u4e0d\u662f\u4f60\u521a\u624d\u51fa\u624b\u76f8\u52a9\uff0c\u6b64\u523b\u6211\u6050\u6015\u4e5f\u4e0d\u80fd\u548c\u4f60\u8bf4\u8bdd \u4e86\u3002\u8bf7\u6055\u6211\u4e0d\u80fd\u5411\u4f60\u884c\u793c\u3002\u4f60\u7684\u5927\u6069\uff0c\u6211\u53ea\u597d\u5f85\u6765\u751f\u518d\u62a5\u5566\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u4f60\u522b\u62c5\u5fc3\uff0c\u4f60\u4f1a\u597d\u8d77\u6765\u7684\u3002\u5148\u627e\u4e2a\u5730\u65b9\u517b\u4f24\uff0c\u6211\u518d\u66ff\u4f60\u8bbe\u6cd5\u5bfb\u533b\u3002\u201d\u674e\u601d \u5357\u5fc3\u60f3\u6709\u90a3\u534a\u652f\u91ce\u5c71\u4eba\u53c2\uff0c\u6700\u5c11\u53ef\u4ee5\u4fdd\u5f97\u4f4f\u9f99\u521a\u4e24\u65e5\u7684\u6027\u547d\u3002 \u3000\u3000\u9f99\u521a\u82e6\u7b11\u9053\uff1a\u201c\u6211\u7684\u4f24\u6211\u81ea\u5df1\u77e5\u9053\uff0c\u8d81\u6211\u8fd8\u6709\u4e00\u53e3\u6c14\uff0c\u4f60\u8ba9\u6211\u628a\u8981\u8bf4\u7684\u90fd\u544a\u8bc9\u4f60\u5427\u3002\u201d \u3000\u3000\u9f99\u521a\u62d2\u7edd\u4e0a\u8def\uff0c\u674e\u601d\u5357\u53ea\u597d\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4f60\u5c31\u5728\u8fd9\u91cc\u518d\u6b47\u4e00\u4f1a\u513f\u3002\u6162\u6162\u8bf4\u5427\u3002\u201d\u5fc3\u91cc \u4e00\u9762\u76d8\u7b97\uff0c\u8981\u5982\u4f55\u529d\u8bf4\uff0c\u624d\u80fd\u9f13\u821e\u8d77\u9f99\u521a\u6c42\u751f\u7684\u610f\u5fd7\uff0c\u8ddf\u4ed6\u79bb\u5f00\u3002 \u3000\u3000\u9f99\u521a\u9053\uff1a\u201c\u6211\u4eec\u5728\u8def\u4e0a\u63a5\u8fde\u548c\u4ec7\u5bb6\u6597\u4e86\u51e0\u573a\uff0c\u56db\u5e08\u5f1f\u548c\u90a3\u4e24\u4f4d\u5934\u9886\u90fd\u4e0d\u5e78\u6b7b\u4e86\u3002\u6211\u4e5f\u53d7 \u4e86\u91cd\u4f24\uff0c\u4e0d\u8fc7\uff0c\u603b\u7b97\u7ed9\u6211\u63a2\u51fa\u4e86\u4ec7\u4eba\u7684\u540d\u5b57\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u6026\u7136\u5fc3\u8df3\uff0c\u95ee\u9053\uff1a\u201c\u90a3\u4eba\u662f\u8c01\uff1f\u201d\u8981\u77e5\u6740\u5f97\u4e86\u5c60\u767e\u57ce\u7684\u81ea\u662f\u975e\u540c\u5c0f\u53ef\u7684\u4eba\u7269\uff0c\u674e \u601d\u5357\u5f53\u7136\u4e5f\u60f3\u77e5\u9053\u8fd9\u4e2a\u795e\u79d8\u4eba\u7269\u662f\u8c01\u4e86 \u3000\u3000\u8c1c\u5e95\u63ed\u5f00\uff0c\u9f99\u521a\u54ac\u7740\u7259\u9f7f\uff0c\u4e00\u4e2a\u5b57\u4e00\u4e2a\u5b57\u5730\u5410\u51fa\u6765\u9053\uff1a\u201c\u662f\u9633\u5929\u96f7\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u5fc3\u4e2d\u4e00\u52a8\uff0c\u6c89\u541f\u81ea\u8bed\uff1a\u201c\u9633\u5929\u96f7\uff1f\u8fd9\u540d\u5b57\u6211\u4f3c\u4e4e\u542c\u8c01\u8bf4\u8fc7\uff01\u201d\u84e6\u5730\u60f3\u8d77\u4e86\u4e00\u4ef6\u4e8b \u60c5\u3002 \u3000\u3000\u5341\u4e8c\u5e74\u524d\uff0c\u4ed6\u521a\u5165\u5e08\u95e8\u672a\u4e45\uff0c\u6709\u4e00\u665a\uff0c\u534a\u591c\u4e09\u66f4\uff0c\u6709\u51e0\u4e2a\u4eba\u5306\u5306\u6765\u627e\u4ed6\u7684\u5e08\u7236\uff0c\u5e08\u7236\u548c \u90a3\u4e9b\u4eba\u7acb\u5373\u5c31\u8d70\uff0c\u4e34\u884c\u65f6\u53eb\u4ed6\u5c0f\u5fc3\u770b\u5b88\u95e8\u6237\uff0c\u8bf4\u662f\u4e09\u4e24\u65e0\u5c31\u53ef\u56de\u6765\u3002\u4e0d\u6599\u4e00\u53bb\uff0c\u53bb\u4e86\u4e03\u5929\uff0c \u65b9\u59cb\u56de\u6765\u3002\u56de\u6765\u7684\u65f6\u5019\u989c\u5bb9\u5026\u6020\uff0c\u8eab\u4e0a\u8fd8\u5e26\u7740\u8840\uff01 \u3000\u3000\u674e\u601d\u5357\u60ca\u95ee\u7f18\u6545\uff0c\u8fd9\u624d\u77e5\u9053\uff0c\u90a3\u5929\u665a\u4e0a\uff0c\u5e08\u7236\u662f\u88ab\u90a3\u4e9b\u4eba\u62c9\u53bb\u53c2\u52a0\u56f4\u653b\u4e00\u4e2a\u5927\u9b54\u5934\u7684\u3002 \u5e08\u7236\u8bf4\uff0c\u8fd9\u4e2a\u5927\u9b54\u5934\u662f\u4ece\u5317\u65b9\u6765\u7684\uff0c\u6765\u5230\u6c5f\u5357\uff0c\u505a\u4e86\u51e0\u4ef6\u4f24\u5929\u5bb3\u7406\u7684\u6848\u5b50\uff0c\u6740\u4e86\u597d\u51e0\u4e2a\u8457\u540d \u7684\u6b66\u6797\u4eba\u7269\u3002\u6c5f\u5357\u7684\u4fa0\u4e49\u9053\u4e8e\u662f\u8054\u5408\u8d77\u6765\uff0c\u8a93\u6b7c\u8fd9\u4e2a\u9b54\u5934\uff0c\u4e0d\u6599\u4e00\u6218\u4e4b\u4e0b\uff0c\u90a3\u9b54\u5934\u867d\u7136\u53d7 \u4f24\uff0c\u5374\u4f9d\u7136\u7ed9\u4ed6\u8131\u9003\uff0c\u800c\u6c5f\u5357\u7684\u4fa0\u4e49\u9053\uff0c\u8f7b\u4f24\u91cd\u4f24\u7684\u4e5f\u6709\u5341\u51e0\u4eba\u4e4b\u591a\u3002 \u3000\u3000\u8fd9\u4e2a\u5927\u9b54\u5934\u7684\u540d\u5b57\u5c31\u662f\u9633\u5929\u96f7\u3002 \u3000\u3000\u674e\u601d\u5357\u8bf4\u51fa\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u9f99\u521a\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5c31\u662f\u8fd9\u4e2a\u9633\u5929\u96f7\uff0c\u90a3\u6b21\u4ed6\u8dd1\u5230\u6c5f\u5357\uff0c\u7ed9\u4ee4\u5e08 \u7528\u5927\u529b\u91d1\u521a\u638c\u6253\u4f24\uff0c\u9003\u56de\u6765\u540e\uff0c\u5c31\u6b64\u6c89\u5bc2\uff0c\u4e00\u6643\u8fc7\u4e86\u5341\u51e0\u5e74\uff0c\u6c5f\u6e56\u4e0a\u65e0\u4eba\u53d1\u73b0\u4ed6\u7684\u8e2a\u8ff9\u3002\u6709 \u4eba\u8bf4\u4ed6\u662f\u4f24\u91cd\u800c\u6b7b\uff0c\u6709\u4eba\u8bf4\u4ed6\u662f\u9690\u59d3\u57cb\u540d\uff0c\u82e6\u7ec3\u4e86\u4e00\u79cd\u6b79\u6bd2\u7684\u529f\u592b\uff0c\u51c6\u5907\u7ec3\u6210\u4e4b\u540e\uff0c\u518d\u5230\u6c5f \u5357\uff0c\u4e0d\u4f46\u8981\u62a5\u4e00\u638c\u4e4b\u4ec7\uff0c\u8fd8\u8981\u505a\u5929\u4e0b\u6b66\u6797\u7684\u76df\u4e3b\u3002\u771f\u76f8\u5982\u4f55\uff1f\u65e0\u4eba\u80fd\u52a0\u4ee5\u8bc1\u5b9e\u3002 \u3000\u3000\u201c\u5982\u4eca\u6211\u624d\u77e5\u9053\uff0c\u539f\u6765\u8fd9\u5927\u9b54\u5934\u5f53\u771f\u662f\u8fd8\u6d3b\u5728\u4eba\u95f4\uff0c\u4ed6\u4e0d\u662f\u5728\u6df1\u5c71\u9690\u59d3\u57cb\u540d\uff0c\u800c\u662f\u53d7\u4e86 \u91d1\u864f\u4e4b\u8058\uff0c\u8fdb\u4e86\u91d1\u5bab\u5145\u5f53\u4e86\u5927\u5185\u9ad8\u624b\u3002 \u3000\u3000\u201c\u6211\u4eec\u8fd9\u6b21\u6765\u4e3a\u5e08\u7236\u5bfb\u4ec7\uff0c\u6cbf\u9014\u78b0\u89c1\u7684\u654c\u4eba\uff0c\u5c31\u662f\u8fd9\u5927\u9b54\u5934\u9633\u5929\u96f7\u7684\u515a\u7fbd\uff0c\u5176\u4e2d\u6709\u4ed6\u7684 \u5f1f\u5b50\uff0c\u6709\u91d1\u56fd\u7684\u6b66\u58eb\uff0c\u751a\u81f3\u8fd8\u6709\u8499\u53e4\u7684\u6b66\u58eb\u3002\u91d1\u3001\u8499\u672c\u662f\u654c\u56fd\uff0c\u4f55\u4ee5\u6709\u8499\u53e4\u7684\u6b66\u58eb\u5728\u5185\uff0c\u8fd9 \u4e2a\u6211\u4e5f\u662f\u5f04\u4e0d\u660e\u767d\uff0c\u8bf4\u4e0d\u5b9a\u8fd9\u9633\u5929\u96f7\u6697\u4e2d\u8fd8\u548c\u8499\u53e4\u9791\u5b50\u79c1\u901a\uff0c\u8981\u505a\u2018\u770b\u98ce\u9a76\u8235\u2019\u7684\u6240\u8c13\u2018\u4fca \u6770\u2019\u3002 \u3000\u3000\u201c\u9633\u5929\u96f7\u548c\u4ed6\u7684\u4e24\u4e2a\u5f97\u529b\u52a9\u624b\uff0c\u5728\u6208\u58c1\u9047\u4e0a\u6211\u7684\u5e08\u7236\uff0c\u4e00\u573a\u5267\u6597\uff0c\u9633\u5929\u96f7\u7684\u4e24\u4e2a\u52a9\u624b\u7ed9 \u6211\u5e08\u7236\u6253\u6b7b\uff0c\u4ed6\u672c\u4eba\u4e5f\u53d7\u4e86\u91cd\u4f24\uff0c\u5982\u4eca\u5df2\u7ecf\u9003\u56de\u5927\u90fd\u517b\u4f24\u3002\u53ef\u601c\u6211\u7684\u5e08\u7236\u5b64\u8eab\u4e00\u4eba\uff0c\u53d7\u4f24\u4e4b \u540e\uff0c\u65e0\u4eba\u6599\u7406\uff0c\u5012\u6bd9\u6c99\u6f20\u3002 \u3000\u3000\u201c\u8fd9\u4e9b\u4e8b\u662f\u6211\u5728\u524d\u4e24\u5929\u6349\u5230\u4e00\u4e2a\u4fd8\u864f\uff0c\u5ba1\u95ee\u51fa\u6765\u7684\uff0c\u4e0d\u8fc7\u8fd8\u6709\u4e9b\u7ec6\u8282\u5c1a\u672a\u77e5\u9053\u3002\u521a\u624d\u6211 \u4ece\u8fd9\u4e2a\u6c49\u5b50\u53e3\u4e2d\uff0c\u624d\u95ee\u51fa\u5168\u76d8\u771f\u76f8\u3002\u8fd9\u4e2a\u6c49\u5b50\u540d\u5524\u8363\u5f69\uff0c\u662f\u9633\u5929\u96f7\u7684\u5f1f\u5b50\u3002\u201d \u3000\u3000\u9f99\u521a\u6c14\u5598\u5401\u5401\uff0c\u8bf4\u8bdd\u7684\u58f0\u97f3\u8d8a\u6765\u8d8a\u5fae\u5f31\uff0c\u6e10\u6e10\u51e0\u4e4e\u542c\u5f97\u4e0d\u5927\u6e05\u695a\u4e86\u3002\u674e\u601d\u5357\u6697\u6697\u5403\u60ca\uff0c \u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u600e\u7684\u4ed6\u541e\u670d\u4e86\u534a\u652f\u91ce\u5c71\u4eba\u53c2\u8fd8\u662f\u4e0d\u6d4e\u4e8b\u3002\u201d\u4e8e\u662f\u8bf4\u9053\uff1a\u201c\u9f99\u5927\u54e5\uff0c\u4f60\u6709\u8bdd\u4ee5\u540e \u6162\u6162\u8bf4\u5427\u3002\u54b1\u4eec\u8fd8\u662f\u5148\u627e\u4e2a\u5730\u65b9\u7ed9\u4f11\u517b\u4f24\u7684\u597d\u3002\u201d \u3000\u3000\u9f99\u521a\u62ac\u5934\u4e00\u770b\u5929\u8272\uff0c\u4e1c\u65b9\u5df2\u9732\u51fa\u9c7c\u809a\u767d\uff0c\u9f99\u521a\u5ffd\u5730\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u8981\u7b49\u5f85\u7684\u4eba\uff0c \u53ea\u6015\u662f\u7b49\u4e0d\u5230\u4ed6\u4eec\u6765\u4e86\u3002\u674e\u516c\u5b50\uff0c\u6211\u62dc\u6258\u4f60\u4e24\u4ef6\u4e8b\u60c5\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u4f60\u662f\u7ea6\u4e86\u4ed6\u4eec\u5728\u6b64\u89c1\u9762\u7684\u4e48\uff1f\u5148\u627e\u4e2a\u6bd4\u8f83\u9690\u853d\u7684\u5730\u65b9\u4f60\u8eb2\u4e00\u8eb2\uff0c\u6211\u5728\u8fd9\u91cc \u7b49\u4ed6\u4eec\u597d\u4e0d\u597d\uff1f\u4f60\u4e0d\u8981\u80e1\u601d\u4e71\u60f3\uff0c\u4f60\u4f1a\u6d3b\u7740\u89c1\u4ed6\u4eec\u7684\u3002\u201d \u3000\u3000\u9f99\u521a\u9053\uff1a\u201c\u4e0d\uff0c\u4f60\u4e0d\u77e5\u9053\uff0c\u6211\u662f\u7b49\u4e0d\u53ca\u7684\u4e86\uff0c\u8fd9\u4e24\u4ef6\u4e8b\u60c5\u5f88\u7d27\u8981\uff0c\u6211\u5fc5\u987b\u8d81\u7740\u73b0\u5728\u8fd8\u6709 \u53e3\u6c14\uff0c\u8d76\u5feb\u5bf9\u4f60\u8bf4\u4e86\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u4e0d\u76f8\u4fe1\u9f99\u521a\u5c31\u6709\u6027\u547d\u4e4b\u5fe7\uff0c\u4f46\u4ed6\u65e2\u7136\u8bf4\u5f97\u5982\u6b64\u4e25\u91cd\uff0c\u674e\u601d\u5357\u4e3a\u4e86\u4f7f\u4ed6\u5b89\u5fc3\uff0c\u53ea\u597d \u8bf4\u9053\uff1a\u201c\u597d\u5427\uff0c\u4f60\u5148\u544a\u8bc9\u6211\u4e5f\u597d\u3002\u4f60\u7b49\u5f85\u7684\u662f\u4ec0\u4e48\u4eba\uff0c\u6211\u600e\u6837\u548c\u4ed6\u4eec\u8054\u7edc\uff1f\u201d \u3000\u3000\u9f99\u521a\u9053\uff1a\u201c\u5176\u4e2d\u4e00\u4e2a\u662f\u4f60\u8ba4\u8bc6\u7684\uff0c\u5979\u3001\u5979\u5c31\u662f\u5b5f\u5927\u4fa0\u7684\u5973\u513f\u5b5f\u660e\u971e\uff01\u201d\u6b64\u8a00\u4e00\u51fa\uff0c\u674e\u601d \u5357\u548c\u6768\u5a49\u90fd\u4e0d\u7981\u201c\u554a\uff01\u201d\u7684\u4e00\u58f0\u53eb\u4e86\u51fa\u6765\uff0c\u9f99\u521a\u7ea6\u4f1a\u7684\u4eba\u7adf\u7136\u662f\u5b5f\u660e\u971e\uff0c\u6b64\u4e8b\u5927\u51fa\u4ed6\u4eec\u610f\u6599 \u4e4b\u5916\u3002 \u3000\u3000\u9f99\u521a\u9053\uff1a\u201c\u6211\u4e00\u8def\u7559\u4e0b\u6807\u8bb0\uff0c\u4ed6\u4eec\u4f1a\u8ddf\u7740\u8fd9\u4e9b\u6807\u8bb0\u627e\u5230\u8fd9\u91cc\u6765\u7684\u3002\u7b2c\u4e00\u4ef6\u4e8b\uff0c\u8bf7\u4f60\u544a\u8bc9 \u4ed6\u4eec\uff0c\u6740\u6211\u5e08\u7236\u7684\u4ec7\u4eba\u662f\u9633\u5929\u96f7\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u597d\u3002\u7b2c\u4e8c\u4ef6\u5462\uff1f\u201d \u3000\u3000\u9f99\u521a\u5598\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u3001\u8fd9\u7b2c\u4e8c\u4ef6\u4e8b\u2026\u2026\u5509\uff0c\u6211\u8be5\u600e\u4e48\u8bf4\u5462\uff1f\u2026\u2026\u201d\u4f3c\u4e4e\u6709\u4ec0\u4e48\u96be \u8a00\u4e4b\u9690\uff0c\u6b32\u8bf4\u8fd8\u4f11\u3002\u5c31\u5728\u6b64\u65f6\uff0c\u5ffd\u542c\u5f97\u6025\u9aa4\u7684\u8e44\u58f0\uff0c\u5df2\u662f\u9690\u9690\u4f20\u6765\u3002 \u3000\u3000\u6768\u5a49\u5931\u58f0\u53eb\u9053\uff1a\u201c\u4f60\u770b\uff0c\u6765\u7684\u662f\u4e0d\u662f\u5b5f\u59d1\u5a18\uff1f\u201d \u3000\u3000\u9f99\u521a\u60ca\u559c\u4ea4\u96c6\uff0c\u53eb\u4e86\u4e00\u58f0\uff1a\u201c\u5440\uff0c\u771f\u7684\u662f\u5979\u6765\u4e86\uff01\u201d\u4e5f\u4e0d\u77e5\u662f\u7531\u4e8e\u6bd2\u4f24\u53d1\u4f5c\uff0c\u8fd8\u662f\u53d7\u4e86 \u7a81\u5982\u5176\u6765\u7684\u60ca\u559c\u523a\u6fc0\uff0c\u4e00\u53e3\u6c14\u8f6c\u4e0d\u8fc7\u6765\uff0c\u53cc\u773c\u5ffd\u5730\u7ffb\u767d\uff0c\u6655\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u674e\u601d\u5357\u5927\u5403\u4e00\u60ca\uff0c\u6293\u7740\u9f99\u521a\u53cc\u80a9\u6447\u9053\uff1a\u201c\u9f99\u5144\uff0c\u9192\u9192\uff01\u201d\u6b63\u8981\u8bbe\u6cd5\u6551\u6cbb\uff0c\u90a3\u9a91\u9a6c\u5df2\u7ecf\u6765 \u5230\u3002 \u3000\u3000\u90a3\u9a91\u5728\u9a6c\u4e0a\u7684\u662f\u4e2a\u7ea2\u8863\u5973\u5b50\uff0c\u674e\u601d\u5357\u56de\u5934\u4e00\u770b\uff0c\u8fd9\u4e2a\u5973\u5b50\u4ed6\u5e76\u4e0d\u8ba4\u8bc6\uff0c\u5fc3\u91cc\u597d\u751f\u8be7\u5f02\uff0c \u60f3\u9053\uff1a\u201c\u600e\u7684\u9f99\u521a\u53c8\u8bf4\u662f\u5b5f\u660e\u971e\uff1f\u55ef\uff0c\u83ab\u975e\u8fd9\u4e2a\u5973\u5b50\u53ea\u662f\u4e00\u4e2a\u4e0d\u76f8\u5e72\u7684\u8fc7\u8def\u4eba\u3002\u201d \u3000\u3000\u6768\u5a49\u4ece\u672a\u89c1\u8fc7\u5b5f\u660e\u971e\uff0c\u5374\u4ee5\u4e3a\u6765\u7684\u662f\u5979\uff0c\u4e0d\u7531\u5f97\u5fc3\u4e71\u5982\u9ebb\uff0c\u832b\u7136\u5730\u8fce\u4e0a\u524d\u53bb\u3002\u90a3\u7ea2\u8863\u5973 \u5b50\u5ffd\u5730\u559d\u9053\uff1a\u201c\u597d\u5440\uff0c\u4f60\u4eec\u80c6\u6562\u5bb3\u4e86\u6211\u7684\u5e08\u5144\uff01\u201d\u628a\u624b\u4e00\u626c\uff0c\u4e00\u67c4\u98de\u9556\u500f\u7684\u98de\u4e86\u51fa\u6765\uff0c\u5411\u6768 \u5a49\u5f53\u5934\u6293\u4e0b\u3002\u6b63\u662f\uff1a \u3000\u3000\u585e\u5916\u53c8\u9022\u5947\u5973\u5b50\uff0c\u5929\u6daf\u540c\u662f\u4e71\u79bb\u4eba\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b\u2014\u2014\u7b2c\u5341\u4e00\u56de\u3000\u76f8\u89c1\u4e89\u5982\u4ecd\u4e0d\u89c1\u3000\u591a\u60c5\u5374\u4f3c\u603b\u65e0\u60c5 \u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b \u7b2c\u5341\u4e00\u56de\u3000\u76f8\u89c1\u4e89\u5982\u4ecd\u4e0d\u89c1\u3000\u591a\u60c5\u5374\u4f3c\u603b\u65e0\u60c5 \u3000\u3000\u6768\u5a49\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u201c\u5b5f\u660e\u971e\u201d\u7a81\u7136\u5bf9\u5979\u88ad\u51fb\uff0c\u5927\u60ca\u4e4b\u4e0b\uff0c\u53ea\u597d\u548c\u8863\u4e00\u6eda\uff0c\u201c\u547c\u201d\u7684\u4e00 \u58f0\uff0c\u98de\u722a\u4ece\u5979\u5934\u9876\u63a0\u8fc7\u3002\u6768\u5a49\u7528\u7684\u662f\u201c\u71d5\u9752\u5341\u516b\u7ffb\u201d\u7684\u8eab\u6cd5\uff0c\u5728\u5730\u4e0a\u63a5\u8fde\u6253\u4e86\u51e0\u4e2a\u6eda\u624d\u907f\u5f00 \u7684\u3002\u7537\u4eba\u5728\u5730\u4e0a\u6253\u6eda\u8fd8\u4e0d\u600e\u4e48\uff0c\u4e00\u4e2a\u5c11\u5973\u88ab\u8feb\u4f7f\u7528\u8fd9\u79cd\u8eab\u6cd5\uff0c\u53ef\u5c31\u663e\u5f97\u5341\u5206\u72fc\u72c8\u4e86\u3002 \u3000\u3000\u6768\u5a49\u5927\u6012\uff0c\u4e00\u4e2a\u201c\u9ca4\u9c7c\u6253\u633a\u201d\u8df3\u8d77\uff0c\u5b9d\u5251\u5df2\u662f\u51fa\u9798\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u90a3\u7ea2\u8863\u5c11\u5973\u7684\u98de \u722a\u53c8\u5df2\u6765\u5230\u3002 \u3000\u3000\u6768\u5a49\u559d\u9053\uff1a\u201c\u5b5f\u660e\u971e\uff0c\u4f60\u600e\u53ef\u8fd9\u6837\u4e0d\u8bb2\u9053\u7406\uff01\u201d\u201c\u94db\u201d\u7684\u4e00\u58f0\uff0c\u5b9d\u5251\u524a\u51fa\uff0c\u7ea2\u8863\u5c11\u5973\u7684 \u98de\u722a\u635f\u4e86\u4e00\u4e2a\u7f3a\u53e3\uff0c\u706b\u661f\u84ec\u98de\u3002\u7ea2\u8863\u5c11\u5973\u4e5f\u4e0d\u7981\u5403\u4e86\u4e00\u60ca\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u8bc6\u5f97\u5b5f\u660e\u971e\u4e48\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u6025\u5fd9\u5728\u9f99\u521a\u7684\u4eba\u4e2d\u4e00\u6390\uff0c\u8fd9\u662f\u6025\u6551\u7684\u65b9\u6cd5\uff0c\u9f99\u521a\u9192\u4e86\u8fc7\u6765\uff0c\u53eb\u9053\uff1a\u201c\u5e08\u59b9\uff0c\u4f4f \u624b\uff0c\u4ed6\u4eec\u662f\u6211\u7684\u6069\u4eba\uff01\u201d \u3000\u3000\u7ea2\u8863\u5c11\u5973\u8fd9\u624d\u77e5\u9053\u8bef\u4f1a\uff0c\u6536\u4e86\u98de\u722a\uff0c\u5411\u6768\u5a49\u8d54\u4e86\u4e2a\u793c\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5728\u8def\u4e0a\u5f97\u77e5\u5e08\u5144\u906d\u53d7 \u56f4\u653b\u7684\u6d88\u606f\uff0c\u8d76\u5230\u8fd9\u513f\uff0c\u770b\u89c1\u5e08\u5144\u8fd9\u4e2a\u6837\u5b50\uff0c\u53ea\u4ee5\u4e3a\u4ed6\u5df2\u662f\u906d\u4e86\u4f60\u4eec\u7684\u6bd2\u624b\u3002\u59d0\u59d0\uff0c\u4f60\u83ab\u89c1 \u602a\u3002\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u597d\u5728\u6211\u4e5f\u5e76\u6ca1\u53d7\u4f24\uff0c\u4f60\u5feb\u53bb\u770b\u4f60\u7684\u5e08\u5144\u5427\u3002\u201d\u5fc3\u91cc\u53ef\u662f\u5f88\u4e0d\u9ad8\u5174\uff0c\u60f3\u9053\uff1a \u201c\u7eb5\u662f\u51fa\u4e8e\u8bef\u4f1a\uff0c\u4f60\u4e5f\u4e0d\u8be5\u4e0d\u95ee\u9752\u7ea2\u7682\u767d\u3002\u201d \u3000\u3000\u6b8a\u4e0d\u77e5\u8fd9\u4e2a\u7ea2\u8863\u5973\u5b50\u4e43\u662f\u81ea\u5c0f\u5728\u7eff\u6797\u4e2d\u957f\u5927\u7684\uff0c\u6027\u683c\u548c\u6768\u5a49\u81ea\u662f\u5927\u4e0d\u76f8\u540c\u3002\u5979\u505a\u4e8b\u7d20\u6765 \u5f53\u673a\u7acb\u65ad\uff0c\u7531\u4e8e\u8bef\u4f1a\u674e\u3001\u6768\u662f\u6740\u5bb3\u5979\u5e08\u5144\u7684\u654c\u4eba\uff0c\u662f\u4ee5\u5979\u5fc5\u987b\u5148\u6293\u4f4f\u4e00\u4e2a\u4eba\uff0c\u4ee5\u4fbf\u5404\u4e2a\u51fb \u7834\u3002\u5982\u679c\u6362\u4e86\u674e\u601d\u5357\uff0c\u8bbe\u8eab\u5904\u5730\uff0c\u4e5f\u4f1a\u8fd9\u6837\u505a\u7684\u3002 \u3000\u3000\u9f99\u521a\u7f13\u8fc7\u53e3\u6c14\uff0c\u5174\u594b\u7684\u60c5\u7eea\u652f\u6301\u7740\u4ed6\uff0c\u8bf4\u8bdd\u7684\u58f0\u97f3\u4e5f\u6bd4\u8f83\u54cd\u4eae\u4e86\uff1a\u201c\u8fd9\u4f4d\u662f\u674e\u516c\u5b50\u674e\u601d \u5357\u3002\u201d\u7ea2\u8863\u5c11\u5973\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4f60\u5c31\u662f\u674e\u601d\u5357\uff1f\u201d\u674e\u601d\u5357\u9053\uff1a\u201c\u59d1\u5a18\u60f3\u5fc5\u4e5f\u662f\u4ece\u5b5f\u5927\u4fa0\u90a3 \u513f\u542c\u8fc7\u6211\u7684\u540d\u5b57\u5427\uff1f\u4e0d\u9519\uff0c\u6211\u5c31\u662f\u674e\u601d\u5357\uff0c\u4f46\u5374\u4e0d\u662f\u2018\u4e3a\u864e\u4f5c\u6005\u2019\u7684\u674e\u601d\u5357\u3002\u201d \u3000\u3000\u9f99\u521a\u9053\uff1a\u201c\u8d77\u521d\u6211\u4e5f\u9887\u6709\u8bef\u4f1a\uff0c\u5982\u4eca\u65b9\u624d\u660e\u767d\u674e\u516c\u5b50\u786e\u5b9e\u662f\u4e2a\u597d\u4eba\u3002\u201d\u7ea2\u8863\u5973\u5b50\u70b9\u4e00\u70b9 \u5934\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u8bf4\u6211\u4e5f\u77e5\u9053\uff0c\u5b5f\u59d1\u5a18\u76f8\u4fe1\u5f97\u8fc7\u7684\u4eba\u600e\u4f1a\u4e0d\u662f\u597d\u4eba\uff1f\u201d \u3000\u3000\u9f99\u521a\u6389\u8fc7\u5934\u6765\u770b\u7740\u6768\u5a49\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u2014\u2014\u201d\u60f3\u8981\u4ecb\u7ecd\u6768\u5a49\uff0c\u4f46\u6768\u5a49\u5e76\u672a\u548c\u4ed6\u901a\u8fc7\u59d3 \u540d\uff0c\u9f99\u521a\u8bf4\u5230\u4e00\u534a\uff0c\u8fd9\u624d\u84e6\u7136\u7701\u8d77\u81ea\u5df1\u4e5f\u8fd8\u672a\u77e5\u9053\u5979\u7684\u540d\u5b57\u3002 \u3000\u3000\u6768\u5a49\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u6211\u59d3\u6768\uff0c\u5355\u540d\u4e00\u4e2a\u5a49\u5b57\u3002\u201d\u7ea2\u8863\u5973\u5b50\u53c8\u662f\u4e00\u6014\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u539f\u6765\u4ed6 \u4eec\u4e0d\u662f\u5144\u59b9\u3002\u201d \u3000\u3000\u6768\u5a49\u4ece\u5979\u60ca\u6115\u7684\u9762\u8272\u53ef\u4ee5\u731c\u60f3\u5f97\u5230\u5979\u5728\u60f3\u4e9b\u4ec0\u4e48\uff0c\u5fc3\u91cc\u4e0d\u89c9\u6709\u4e86\u51e0\u5206\u9178\u6e9c\u6e9c\u7684\u5473\u9053\uff0c\u6697 \u81ea\u5bfb\u601d\uff1a\u201c\u5b5f\u660e\u971e\u4e0d\u77e5\u548c\u5979\u8bf4\u4e86\u4e9b\u4ec0\u4e48\uff1f\u5979\u4e00\u5b9a\u4ee5\u4e3a\u5357\u54e5\u662f\u5b5f\u660e\u971e\u7684\u60c5\u4eba\uff0c\u800c\u6211\u5374\u662f\u63d2\u5728\u4ed6 \u4eec\u4e4b\u4e2d\u62a2\u4e86\u5357\u54e5\u7684\u574f\u4eba\u4e86\u3002\u201d\u6768\u5a49\u81ea\u5df1\u89c9\u5f97\u662f\u53d7\u4e86\u59d4\u5c48\uff0c\u867d\u7136\u6ca1\u6709\u53d1\u4f5c\uff0c\u4f46\u5bf9\u8fd9\u7ea2\u8863\u5973\u5b50\u5374 \u662f\u4e0d\u77e5\u4e0d\u89c9\u5730\u66f4\u51b7\u6de1\u4e86\u3002 \u3000\u3000\u7ea2\u8863\u5c11\u5973\u4e0d\u77e5\u662f\u5426\u89c9\u5bdf\uff0c\u4f46\u5374\u4f3c\u6beb\u4e0d\u5728\u610f\uff0c\u53eb\u4e86\u4e00\u58f0\u201c\u6768\u59d0\u59d0\u201d\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u59b9\u59d3\u5c60\uff0c \u5355\u540d\u4e00\u4e2a\u51e4\u5b57\u3002\u591a\u8c22\u4f60\u4eec\u6551\u4e86\u6211\u7684\u5e08\u5144\u3002\u201d \u3000\u3000\u9f99\u521a\u9053\uff1a\u201c\u6211\u7684\u5e08\u7236\u5c31\u662f\u5979\u7684\u7239\u7239\u3002\u201d\u674e\u601d\u5357\u8fd9\u624d\u77e5\u9053\uff0c\u539f\u6765\u8fd9\u4e2a\u7ea2\u8863\u5c11\u5973\u6b63\u662f\u5c60\u767e\u57ce \u7684\u5973\u513f\uff0c\u5fc3\u60f3\uff1a\u201c\u602a\u4e0d\u5f97\u5979\u6709\u8fd9\u6837\u597d\u7684\u672c\u9886\u3002\u201d \u3000\u3000\u5c60\u51e4\u65e0\u6687\u548c\u674e\u3001\u6768\u4e8c\u4eba\u53d9\u8bdd\uff0c\u4e92\u901a\u59d3\u540d\u4e4b\u540e\uff0c\u5c31\u8d70\u5230\u5e08\u5144\u8eab\u8fb9\uff0c\u8bf4\u9053\uff1a\u201c\u4e8c\u5e08\u54e5\uff0c\u4f60\u4f24 \u5f97\u5982\u4f55\uff1f\u6211\u7ed9\u4f60\u770b\u770b\u3002\u201d \u3000\u3000\u9f99\u521a\u82e6\u7b11\u9053\uff1a\u201c\u5e08\u59b9\uff0c\u4f60\u4e0d\u5fc5\u8d39\u795e\u4e86\uff0c\u6211\u4e0d\u6210\u5566\u3002\u6740\u4f60\u7239\u7239\u7684\u4ec7\u4eba\u662f\u9633\u5929\u96f7\u3002\u201d \u3000\u3000\u5c60\u51e4\u9053\uff1a\u201c\u662f\u8c01\u4f24\u4e86\u4f60\u7684\uff0c\u6211\u7ed9\u4f60\u62a5\u4ec7\uff01\u201d \u3000\u3000\u9f99\u521a\u9053\uff1a\u201c\u6211\u5df2\u7ecf\u4eb2\u624b\u62a5\u4ec7\u4e86\u3002\u201d\u6307\u4e00\u6307\u8363\u5f69\u7684\u5c38\u4f53\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8ba4\u5f97\u4ed6\u5427\uff1f\u4ed6\u662f\u9633\u5929 \u96f7\u7684\u5927\u5f1f\u5b50\uff0c\u6211\u5df2\u7ecf\u7528\u5e08\u7236\u7684\u6bd2\u9f99\u9556\u628a\u4ed6\u6740\u4e86\u3002\u201d \u3000\u3000\u6768\u5a49\u662f\u4e2a\u7ec6\u5fc3\u7684\u4eba\uff0c\u53d1\u73b0\u9f99\u521a\u7684\u8bf4\u8bdd\u6709\u5f88\u5927\u7684\u7834\u7efd\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4ed6\u662f\u5728\u53d7\u4e86\u91cd\u4f24\u4e4b \u540e\uff0c\u624d\u78b0\u4e0a\u8363\u5f69\u7684\u3002\u8d77\u521d\u4ed6\u8fd8\u4e0d\u77e5\u9053\u4ed6\u662f\u8c01\u5462\uff0c\u662f\u8fd9\u59d3\u8363\u7684\u548c\u6211\u4eec\u4ea4\u624b\u4e4b\u540e\uff0c\u4ed6\u624d\u770b\u51fa\u4ed6\u7684 \u5bb6\u6570\u6765\u5386\u3002\u5947\u602a\uff0c\u4ed6\u4e3a\u4ec0\u4e48\u8981\u5bf9\u5e08\u59b9\u8bf4\u8c0e\uff1f\u201d \u3000\u3000\u5fc3\u5ff5\u672a\u5df2\uff0c\u53ea\u542c\u5f97\u5c60\u51e4\u5df2\u5728\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u76f8\u4fe1\uff0c\u8363\u5f69\u6709\u591a\u5927\u7684\u672c\u9886\uff0c\u600e\u80fd\u7ed9\u4f60\u4ee5\u81f4\u547d\u4e4b \u4f24\uff1f\u201d \u3000\u3000\u9f99\u521a\u9053\uff1a\u201c\u6211\u662f\u7ed9\u4ed6\u4eec\u56f4\u653b\u7684\u3002\u201d \u3000\u3000\u5c60\u51e4\u534a\u4fe1\u534a\u7591\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u7ed9\u4f60\u6551\u6cbb\uff0c\u6211\u5e26\u6709\u4e13\u6cbb\u5185\u4f24\u7684\u5c0f\u8fd8\u4e39\u3002\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u9645\u5c60\u51e4\u7684\u4e00\u53ea\u624b\u5df2\u7ecf\u642d\u4e0a\u4e86\u9f99\u521a\u7684\u8109\u95e8\uff0c\u7ed9\u4ed6\u7ec6\u5bdf\u4f24\u52bf\u3002\u9f99\u521a\u6323\u624e\u7740\u8bf4\u9053\uff1a\u201c\u4f24 \u662f\u533b\u4e0d\u597d\u7684\uff0c\u4f60\u3001\u4f60\u2014\u2014\u201d \u3000\u3000\u5c60\u51e4\u9053\uff1a\u201c\u533b\u4e0d\u597d\u6211\u4e5f\u8981\u770b\uff0c\u6211\u4e00\u5b9a\u8981\u77e5\u9053\u8c01\u662f\u4f60\u7684\u4ec7\u4eba\uff01\u201d\u9f99\u521a\u4e0d\u60f3\u7ed9\u5979\u68c0\u67e5\u4f24\u52bf\uff0c \u5c60\u51e4\u5374\u662f\u975e\u770b\u4e0d\u53ef\uff0c\u800c\u4e14\u9762\u4e0a\u9732\u51fa\u975e\u5e38\u53e4\u602a\u7684\u795e\u8272\uff0c\u4f3c\u662f\u60f6\u60d1\uff0c\u53c8\u4f3c\u60ca\u614c\uff0c\u60ca\u614c\u60f6\u60d1\u4e4b\u4e2d\u8fd8 \u5e26\u7740\u51e0\u5206\u5fe7\u6124\u3002\u6b64\u65f6\u8fde\u674e\u601d\u5357\u4e5f\u662f\u5927\u4e3a\u60ca\u6115\uff0c\u9690\u9690\u611f\u5230\u4e8b\u60c5\u8d70\u6709\u8e4a\u8df7\u4e86\u3002 \u3000\u3000\u9f99\u521a\u6323\u624e\u4e0d\u8fc7\uff0c\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u8fd9\u4ec7\u662f\u4e0d\u80fd\u62a5\u7684\uff0c\u5e08\u59b9\uff0c\u4f60\u5fd8\u8bb0\u4e86\u8fd9\u4ef6\u4e8b\u5427\uff01\u201d \u3000\u3000\u5c60\u51e4\u9762\u8272\u554a\u5730\u53d8\u5f97\u82cd\u767d\u5982\u7eb8\uff0c\u53eb\u9053\uff1a\u201c\u539f\u6765\u662f\u6211\u7684\u54e5\u54e5\u7ed9\u4f60\u4ee5\u81f4\u547d\u4e4b\u4f24\uff01\u4ed6\u662f\u5728\u534a\u4e2a\u6708 \u524d\u7528\u6bd2\u638c\u4f24\u4e86\u4f60\u7684\uff0c\u6b64\u65f6\u65b9\u59cb\u53d1\u4f5c\uff01\u4ed6\u3001\u4ed6\u4e3a\u4ec0\u4e48\u8981\u5bf9\u4f60\u4e0b\u6b64\u6bd2\u624b\uff01\u201d \u3000\u3000\u9f99\u521a\u82e6\u7b11\u9053\uff1a\u201c\u9664\u975e\u662f\u4f60\u7239\u7239\u590d\u751f\uff0c\u5929\u4e0b\u65e0\u4eba\u80fd\u591f\u89e3\u4f60\u54e5\u54e5\u7684\u6bd2\u638c\uff0c\u6240\u4ee5\uff0c\u4f60\u662f\u4e0d\u5fc5\u8d39 \u795e\u4e86\u3002\u6211\u8eab\u4e0a\u6709\u4e00\u5c01\u4fe1\uff0c\u662f\u4e09\u5e08\u5f1f\u7ed9\u4f60\u7684\u3002\u4f60\u62ff\u53bb\u5427\u3002\u201d\u4ed6\u5e76\u6ca1\u6709\u56de\u7b54\u5e08\u59b9\u7684\u95ee\u9898\uff0c\u4f46\u5c60\u51e4 \u542c\u4ed6\u8fd9\u4e48\u4e00\u8bf4\uff0c\u5fc3\u4e2d\u5df2\u662f\u660e\u767d\u3002 \u3000\u3000\u5c60\u51e4\u63a5\u8fc7\u4e86\u90a3\u5c01\u4fe1\uff0c\u624b\u6307\u98a4\u6296\uff0c\u773c\u89d2\u6302\u7740\u6cea\u73e0\uff0c\u8bf4\u9053\uff1a\u201c\u4e8c\u5e08\u54e5\uff0c\u8fd9\u90fd\u662f\u6211\u4eec\u8fde\u7d2f\u4e86 \u4f60\u3002\u5509\uff0c\u4f60\u53d7\u4e86\u51a4\u6789\uff0c\u4e3a\u4f55\u5374\u4e0d\u5206\u8fa9\u3002\u201d \u3000\u3000\u9f99\u521a\u60e8\u767d\u7684\u9762\u4e0a\u7efd\u51fa\u4e00\u4e1d\u5fae\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u4ee5\u4e3a\u6211\u662f\u4ee3\u4eba\u53d7\u8fc7\u3002\u5176\u5b9e\uff0c\u8fd9\u4e5f\u4e0d\u662f\u4f60\u4eec \u7684\u8fc7\u9519\u3002\u6211\u80fd\u591f\u4e3a\u4f60\u4eec\u5c3d\u4e00\u70b9\u529b\uff0c\u6211\u5f88\u9ad8\u5174\u3002\u53ea\u8981\u4f60\u660e\u767d\u6211\u7684\u5fc3\u4e8b\u3002\u201d \u3000\u3000\u5c60\u51e4\u9053\uff1a\u201c\u6211\u660e\u767d\u7684\u3002\u201d\u63e1\u7740\u9f99\u521a\u7684\u624b\u8bf4\u9053\uff1a\u201c\u4e8c\u5e08\u54e5\uff0c\u6211\u4f1a\u611f\u6fc0\u4f60\u4e00\u8f88\u5b50\u3002\u4f60\u6709\u4ec0\u4e48 \u672a\u4e86\u4e4b\u4e8b\u8981\u4ea4\u4ee3\u4e48\uff1f\u201d \u3000\u3000\u9f99\u521a\u9053\uff1a\u201c\u4f60\u4e0d\u8981\u6068\u4f60\u54e5\u54e5\uff0c\u4f46\u5374\u8981\u63d0\u9632\u4ed6\u3002\u6211\u6b7b\u4e86\u4e4b\u540e\uff0c\u8bf7\u4f60\u628a\u6211\u7684\u9aa8\u7070\u5e26\u56de\u53bb\uff0c\u6211 \u4e0d\u613f\u610f\u57cb\u9aa8\u5f02\u4e61\u3002\u5e08\u59b9\uff0c\u4f60\u653e\u5fc3\uff0c\u77f3\u5e08\u5f1f\u4f1a\u56de\u5230\u4f60\u7684\u8eab\u8fb9\u7684\u3002\u201d\u8bf4\u5230\u540e\u9762\u4e24\u53e5\uff0c\u58f0\u97f3\u5df2\u662f\u5f31 \u4e0d\u53ef\u95fb\uff0c\u5c60\u51e4\u628a\u8033\u6735\u51d1\u5230\u4ed6\u7684\u5507\u8fb9\u624d\u542c\u5f97\u6e05\u695a\u3002 \u3000\u3000\u5c60\u51e4\u611f\u5230\u4ed6\u7684\u5634\u5507\u51b0\u51b7\uff0c\u4e00\u89e6\u4ed6\u7684\u9f3b\u7aef\u5df2\u662f\u6ca1\u6709\u4e86\u6c14\u606f\u3002\u5c60\u51e4\u7f13\u7f13\u5730\u628a\u9f99\u521a\u7684\u5c38\u4f53\u653e\u4e86 \u4e0b\u6765\uff0c\u6325\u4e00\u6325\u624b\uff0c\u793a\u610f\u674e\u3001\u6768\u4e8c\u4eba\u8d70\u5f00\u3002\u674e\u601d\u5357\u548c\u6768\u5a49\u4e0d\u77e5\u9053\u4ed6\u4eec\u7684\u9690\u60c5\uff0c\u60f3\u529d\u6170\u5979\u4e5f\u65e0\u4ece \u529d\u8d77\u3002 \u3000\u3000\u5c60\u51e4\u5012\u6ca1\u6709\u53f7\u9676\u5927\u54ed\uff0c\u53ea\u89c1\u5979\u54ac\u7740\u5634\u5507\uff0c\u63a2\u624b\u5165\u6697\u5668\u56ca\u4e2d\uff0c\u7a81\u7136\u628a\u624b\u4e00\u626c\uff0c\u4e00\u9897\u5c0f\u5c0f\u7684 \u5f39\u5b50\u6253\u4e86\u51fa\u6765\uff0c\u78b0\u7740\u9f99\u521a\u7684\u5c38\u4f53\uff0c\u201c\u6ce2\u201d\u7684\u4e00\u58f0\uff0c\u5f39\u5b50\u88c2\u5f00\uff0c\u706b\u5149\u55b7\u51fa\uff0c\u8f6c\u773c\u95f4\u8eab\u4f53\u5df2\u662f\u7740 \u706b\u711a\u70e7\uff0c\u5316\u4e3a\u7070\u70ec\uff01\u539f\u6765\u8fd9\u662f\u5c60\u5bb6\u72ec\u95e8\u6697\u5668\u4e4b\u4e00\uff0c\u540d\u4e3a\u201c\u706b\u9f99\u73e0\u201d\uff0c\u662f\u7528\u731b\u70c8\u6613\u71c3\u7684\u836f\u5242\u6df7 \u548c\u4e86\u786b\u78fa\u7c89\u5236\u6210\uff0c\u80fd\u53d1\u70c8\u7130\uff0c\u9732\u98ce\u5373\u71c3\u3002 \u3000\u3000\u6768\u5a49\u5413\u4e86\u4e00\u8df3\uff0c\u8f6c\u8fc7\u4e86\u5934\uff0c\u4e0d\u6562\u89c2\u770b\u3002\u5f53\u65f6\u6c49\u4eba\u7684\u98ce\u4fd7\u4e60\u60ef\uff0c\u6b7b\u4eba\u662f\u5fc5\u5b9a\u571f\u846c\u7684\uff0c\u706b\u846c \u4e4b\u4e8b\uff0c\u6768\u5a49\u8fd8\u4ece\u672a\u89c1\u8fc7\u3002\u4f46\u5c60\u51e4\u5374\u4f3c\u5e76\u4e0d\u5728\u4e4e\u706b\u846c\u5979\u7684\u5e08\u5144\u3002 \u3000\u3000\u5c60\u51e4\u8fd9\u624d\u628a\u90a3\u5c01\u4fe1\u62c6\u5f00\u4e86\uff0c\u542b\u7740\u6cea\u770b\u4e86\u4e00\u904d\u81ea\u8a00\u81ea\u8bed\u5730\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u4e8c\u5e08\u54e5\uff0c\u53ef\u601c\u4f60\u62c5 \u4e86\u865a\u540d\uff0c\u6211\u662f\u5bf9\u4e0d\u8d77\u4f60\u4e86\u3002\u201d \u3000\u3000\u70c8\u7130\u718a\u718a\u4e4b\u4e2d\uff0c\u9f99\u521a\u7684\u5c38\u4f53\u5316\u4e3a\u7070\u70ec\u3002\u5f85\u5230\u706b\u5149\u5ab3\u706d\uff0c\u5c60\u51e4\u817e\u51fa\u4e86\u6697\u5668\u56ca\uff0c\u88c5\u4e86\u9f99\u521a\u7684 \u9aa8\u7070\uff0c\u6302\u5728\u9a6c\u65c1\uff0c\u8de8\u4e0a\u9a6c\u80cc\u3002 \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u5c60\u5973\u4fa0\uff0c\u4f60\u8d70\u4e86\u4e48\uff1f\u4f60\u7239\u7239\u7684\u4e24\u4e2a\u624b\u4e0b\u5b8b\u94c1\u8f6e\u3001\u67f3\u4e09\u5a18\u592b\u5987\u73b0\u5728\u8499\u53e4\u3002\u201d \u3000\u3000\u5c60\u51e4\u9053\uff1a\u201c\u591a\u8c22\u4f60\u7ed9\u6211\u62a5\u8baf\uff0c\u4e0d\u8fc7\uff0c\u6211\u4eec\u4e0d\u51c6\u5907\u5230\u8499\u53e4\u53bb\u4e86\u3002\u5bf9\u5566\uff0c\u6709\u4e00\u4ef6\u4e8b\u6211\u5e94\u8be5\u544a \u8bc9\u4f60\u3002\u201d\u8bf4\u81f3\u6b64\u5904\uff0c\u7a81\u7136\u505c\u4e86\u4e0b\u6765\uff0c\u671b\u4e86\u4e00\u671b\u6768\u5a49\u3002 \u3000\u3000\u674e\u601d\u5357\u731c\u60f3\u5f97\u5230\u5979\u8981\u8bf4\u4e9b\u4ec0\u4e48\uff0c\u5fc3\u5934\u201c\u535c\u535c\u201d\u4e71\u8df3\uff0c\u5fc3\u5ff5\u672a\u5df2\u3002\u679c\u7136\u4fbf\u542c\u5f97\u5c60\u51e4\u8bf4\u9053\uff1a \u201c\u5b5f\u660e\u971e\u5728\u6211\u90a3\u513f\uff0c\u79bb\u6b64\u4e0d\u8fdc\u3002\u4f60\u82e5\u662f\u60f3\u8981\u89c1\u5979\uff0c\u53ef\u4ee5\u548c\u6211\u4e00\u9053\u53bb\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u867d\u7136\u548c\u6768\u5a49\u8ba2\u4e86\u5a5a\uff0c\u4f46\u5bf9\u5b5f\u660e\u971e\u603b\u8fd8\u662f\u6709\u7740\u77e5\u5df1\u4e4b\u611f\uff0c\u4e5f\u662f\u4e00\u76f4\u672a\u80fd\u5fd8\u6000\u5979\u7684\u3002 \u4f46\u6b64\u9645\uff0c\u5c60\u51e4\u7a81\u7136\u9080\u4ed6\u53bb\u89c1\u5b5f\u660e\u971e\uff0c\u5374\u662f\u53eb\u4ed6\u4e3a\u96be\u4e86\u3002 \u3000\u3000\u4e00\u6765\u56e0\u4e3a\u5c60\u51e4\u5e76\u6ca1\u9080\u8bf7\u6768\u5a49\uff0c\u674e\u601d\u5357\u600e\u80fd\u629b\u4e0b\u6768\u5a49\uff0c\u72ec\u81ea\u53bb\u4f1a\u5b5f\u660e\u971e\uff1f\u4e8c\u6765\u674e\u601d\u5357\u53c8\u5df2 \u7ecf\u8ba2\u4e86\u5a5a\uff0c\u4ed6\u6697\u81ea\u601d\u91cf\uff0c\u5728\u8fd9\u6837\u7684\u60c5\u5f62\u4e4b\u4e0b\uff0c\u4e0e\u5b5f\u660e\u971e\u4e5f\u662f\u201c\u76f8\u89c1\u4e89\u5982\u4e0d\u89c1\u201d\u7684\u4e86\u3002\u5f53\u7136\uff0c \u4ed6\u89c1\u4e86\u5b5f\u660e\u971e\uff0c\u53ef\u4ee5\u89e3\u91ca\u6709\u5173\u4e8e\u4ed6\u7236\u4eb2\u7684\u8bef\u4f1a\u3002\u4f46\u5373\u4f7f\u4ed6\u4e0d\u4eb2\u81ea\u89e3\u91ca\uff0c\u5c60\u51e4\u4e5f\u4f1a\u548c\u5979\u8bf4\u53ca\u4ed6 \u6551\u9f99\u521a\u4e4b\u4e8b\u7684\uff0c\u53ea\u51ed\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u5c31\u53ef\u4ee5\u8bc1\u660e\u4ed6\u6ca1\u6709\u8f9c\u8d1f\u5b5f\u660e\u971e\u7684\u671f\u671b\u4e86\u3002\u96be\u9053\u5b5f\u660e\u971e\u8fd8\u4f1a\u4e0d \u76f8\u4fe1\u4ed6\u662f\u597d\u4eba\u5417\uff1f\u4f55\u51b5\u5979\u4eec\u5c06\u6765\u89c1\u4e86\u5b8b\u94c1\u8f6e\u592b\u5987\uff0c\u771f\u76f8\u4e5f\u5b9a\u80fd\u5927\u767d\u3002 \u3000\u3000\u201c\u7ea2\u989c\u77e5\u5df1\u957f\u76f8\u5fc6\uff0c\u4e0d\u843d\u8a00\u8be0\u4ea6\u5927\u4f73\u3002\u5b5f\u660e\u971e\u662f\u5973\u4e2d\u82f1\u6770\uff0c\u6211\u548c\u5979\u4e5f\u7b97\u5f97\u662f\u541b\u5b50\u4e4b\u4ea4\u3002 \u541b\u5b50\u4e4b\u4ea4\u6de1\u5982\u6c34\uff0c\u672c\u6765\u5c31\u4e0d\u5fc5\u7528\u8a00\u8bed\u8868\u8fbe\u51fa\u6765\u3002\u6211\u53c8\u4f55\u987b\u591a\u6b64\u4e00\u884c\u3002\u201d\u674e\u601d\u5357\u5fc3\u60f3\u3002 \u3000\u3000\u5c60\u51e4\u4e0d\u77e5\u674e\u601d\u5357\u5fc3\u60c5\u7684\u7d0a\u4e71\uff0c\u89c1\u4ed6\u8bb7\u8bb7\u4e0d\u8bed\uff0c\u5f88\u662f\u4e0d\u8010\u70e6\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\u600e\u7684\u5982 \u6b64\u5a46\u5a46\u5988\u5988\uff1f\u201d\u7981\u4e0d\u4f4f\u53cc\u7709\u5fae\u8e59\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u600e\u4e48\u6837\uff0c\u7a76\u7adf\u662f\u53bb\u4e5f\u4e0d\u53bb\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9762\u4e0a\u4e00\u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u6025\u4e8e\u56de\u56fd\uff0c\u7559\u5728\u8fd9\u91cc\uff0c\u4e5f\u5e2e\u4e0d\u4e86\u4f60\u4eec\u4ec0\u4e48\u5fd9\u3002\u8fd8\u662f\u8bf7\u4f60 \u5728\u5b5f\u59d1\u5a18\u9762\u524d\uff0c\u4ee3\u6211\u9053\u4e2a\u6b49\u5427\u3002\u201d \u3000\u3000\u5c65\u51e4\u5927\u4e0d\u9ad8\u5174\uff0c\u8bf4\u9053\uff1a\u201c\u5e76\u4e0d\u662f\u5b5f\u59d0\u59d0\u8981\u89c1\u4f60\uff0c\u662f\u6211\u56e0\u4e3a\u77e5\u9053\u4f60\u4eec\u76f8\u8bc6\uff0c\u6240\u4ee5\u624d\u95ee\u4f60\u8981 \u4e0d\u8981\u89c1\u5979\u7684\u3002\u4f60\u65e2\u7136\u4e0d\u8981\u53bb\uff0c\u90a3\u5c31\u7b97\u4e86\u3002\u6709\u4ec0\u4e48\u9053\u6b49\u4e0d\u9053\u6b49\u7684\uff1f\u6211\u7239\u7239\u7684\u4ec7\uff0c\u6211\u81ea\u5df1\u4f1a\u62a5\uff0c \u5f53\u7136\u4e5f\u7528\u4e0d\u7740\u4f60\u6765\u5e2e\u5fd9\uff01\u201d\u8bf4\u7f62\uff0c\u201c\u5530\u201d\u7684\u865a\u6253\u4e00\u97ad\uff0c\u80ef\u4e0b\u7684\u6843\u82b1\u9a6c\u5c55\u5f00\u56db\u8e44\uff0c\u7edd\u5c18\u800c\u53bb\u3002 \u3000\u3000\u674e\u601d\u5357\u5403\u5979\u4e00\u987f\u6392\u63ce\uff0c\u6ee1\u9762\u901a\u7ea2\uff0c\u5f3a\u7b11\u8bf4\u9053\uff1a\u201c\u6211\u56fa\u7136\u662f\u4e0d\u4f1a\u8bf4\u8bdd\uff0c\u8fd9\u4f4d\u5c60\u59d1\u5a18\u7684\u813e\u6c14 \u4e5f\u662f\u771f\u96be\u4f3a\u5019\u3002\u201d \u3000\u3000\u6768\u5a49\u7b11\u9053\uff1a\u201c\u5979\u7239\u7239\u53f7\u79f0\u5180\u5317\u4eba\u9b54\u5c60\u767e\u57ce\uff0c\u5927\u9b54\u5934\u7684\u5973\u513f\u561b\uff0c\u813e\u6c14\u5f53\u7136\u662f\u4e0d\u548c\u5e38\u4eba\u4e00\u6837 \u7684\u4e86\u3002\u4e0d\u8fc7\uff0c\u597d\u5728\u4f60\u53c8\u4e0d\u5fc5\u505a\u5979\u7684\u4e08\u592b\uff0c\u5979\u96be\u4e8e\u4f3a\u5019\u4e5f\u7f62\uff0c\u5bb9\u6613\u4f3a\u5019\u4e5f\u7f62\uff0c\u4f60\u4e5f\u7528\u4e0d\u7740\u62c5\u5fc3 \u7684\u4e86\u3002\u597d\u5566\uff0c\u5929\u5df2\u5927\u4eae\uff0c\u54b1\u4eec\u4e5f\u8be5\u8d70\u4e86\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u7b11\u9053\uff1a\u201c\u6211\u53c8\u8bf4\u9519\u8bdd\u4e86\uff0c\u597d\uff0c\u8d70\u5427\u3002\u201d \u3000\u3000\u4e24\u4eba\u5e76\u8f94\u540c\u884c\uff0c\u6768\u5a49\u4e45\u4e45\u4e0d\u8bed\u3002\u674e\u601d\u5357\u642d\u8baa\u9053\uff1a\u201c\u771f\u60f3\u4e0d\u5230\u9f99\u521a\u662f\u7ed9\u4ed6\u7684\u5e08\u5144\u6740\u7684\uff0c\u5374 \u4e0d\u77e5\u662f\u4f55\u7f18\u6545\uff1f\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u5c60\u767e\u57ce\u7684\u513f\u5973\u548c\u95e8\u4e0b\u5f1f\u5b50\uff0c\u5c11\u4e0d\u514d\u90fd\u5e26\u7740\u4e00\u70b9\u90aa\u6c14\u3002\u4ed6\u4eec\u7684\u95e8\u6237\u7ea0\u7eb7\uff0c\u54b1\u4eec \u4e0d\u5fc5\u7406\u4f1a\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u7b11\u9053\uff1a\u201c\u6211\u53ea\u662f\u968f\u4fbf\u8bf4\u8bf4\u800c\u5df2\uff0c\u8c01\u8981\u53bb\u7406\u4f1a\u4ed6\u4eec\u7684\u4e8b\u60c5\u3002\u4e0d\u8fc7\uff0c\u5c60\u767e\u57ce\u867d\u7136\u662f\u6709 \u9b54\u5934\u4e4b\u79f0\uff0c\u5012\u4e5f\u4e0d\u662f\u90aa\u6d3e\u4eba\u7269\u3002\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u6211\u77e5\u9053\u91d1\u56fd\u7684\u8d2a\u5b98\u4e27\u5728\u4ed6\u624b\u91cc\u7684\u4e0d\u5c11\u3002\u4f46\u4ed6\u559c\u6012\u968f\u5fc3\uff0c\u51fa\u624b\u72e0\u8fa3\uff0c\u548c\u6b63\u6d3e\u7684 \u4fa0\u4e49\u4eba\u7269\uff0c\u7a76\u7adf\u4e5f\u8fd8\u4e0d\u662f\u5b8c\u5168\u4e00\u6837\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u77e5\u9053\u6768\u5a49\u6000\u6709\u5fc3\u4e8b\uff0c\u4f46\u4ed6\u4e0d\u613f\u6311\u8d77\u8bdd\u9898\uff0c\u53ea\u597d\u627e\u4e9b\u4e0d\u75db\u4e0d\u75d2\u7684\u95f2\u8bdd\u6765\u8bf4\u3002 \u3000\u3000\u6768\u5a49\u7ec8\u4e8e\u5fcd\u8010\u4e0d\u4f4f\uff0c\u8bf4\u9053\uff1a\u201c\u5357\u54e5\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u53bb\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u4f60\u662f\u8bf4\u2026\u2026\u201d\u6768\u5a49\u7b11\u9053\uff1a\u201c\u4f60\u8fd8\u88c5\u4ec0\u4e48\u7cca\u6d82\uff1f\u6211\u8bf4\u7684\u5f53\u7136\u662f\u90a3\u4f4d\u5b5f\u59d1\u5a18\u3002 \u4eba\u5bb6\u5bf9\u4f60\u5ff5\u5ff5\u4e0d\u5fd8\uff0c\u96be\u9053\u4f60\u5c31\u628a\u5979\u5fd8\u4e86\uff1f\u201d\u8bf4\u8bdd\u867d\u7136\u5e26\u7b11\uff0c\u7b11\u5f97\u4e0d\u662f\u5f88\u81ea\u7136\u3002 \u3000\u3000\u674e\u601d\u5357\u9762\u4e0a\u4e00\u7ea2\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u5a49\u59b9\uff0c\u4f60\u5fc3\u4e0a\u7684\u7ed3\u8fd8\u6ca1\u89e3\u5f00\uff1f\u96be\u9053\u5f53\u771f\u8981\u6211\u638f\u5fc3\u51fa\u6765\uff1f\u201d \u3000\u3000\u6768\u5a49\u5550\u4e86\u4e00\u53e3\u8bf4\u9053\uff1a\u201c\u4f60\u628a\u6211\u770b\u4f5c\u4ec0\u4e48\u4eba\uff1f\u6211\u662f\u918b\u5a18\u5b50\u4e48\uff1f\u5b5f\u660e\u971e\u4e0e\u4f60\u76f8\u8bc6\u5728\u524d\uff0c\u4e8e\u4f60 \u53c8\u6709\u6551\u547d\u4e4b\u6069\uff0c\u4f60\u53bb\u770b\u5979\uff0c\u96be\u9053\u4e0d\u8be5\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u4e0d\u662f\u4e0d\u8be5\u3002\u4f46\u4f60\u522b\u5fd8\u4e86\uff0c\u54b1\u4eec\u8fd8\u662f\u521a\u521a\u6446\u8131\u8ffd\u5175\uff0c\u5c1a\u672a\u8131\u51fa\u9669\u5883\uff0c\u6211\u6487\u4e0b \u4f60\uff0c\u53c8\u600e\u80fd\u653e\u5fc3\uff1f\u201d \u3000\u3000\u8fd9\u51e0\u53e5\u8bdd\u8bf4\u5f97\u5341\u5206\u8bda\u6073\uff0c\u6768\u5a49\u5fc3\u91cc\u867d\u7136\u4ecd\u6709\u51e0\u5206\u9178\u6e9c\u6e9c\u7684\u5473\u9053\uff0c\u5374\u4e5f\u4e0d\u7ecf\u53d7\u4e86\u4ed6\u7684\u611f \u52a8\uff0c\u4f4e\u4e0b\u4e86\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u5357\u54e5\uff0c\u6211\u77e5\u9053\u4f60\u662f\u4e3a\u4e86\u6211\u7684\u7f18\u6545\u4e0d\u53bb\u770b\u5979\uff0c\u4f46\u6211\u4e5f\u77e5\u9053\u4f60\u662f\u60f3\u89c1\u5979 \u7684\u3002\u6211\u4e0d\u613f\u610f\u4f60\u7559\u6709\u9057\u61be\uff0c\u66f4\u4e0d\u613f\u610f\u7ed9\u4f60\u90a3\u4f4d\u5b5f\u59d1\u5a18\u8bef\u4f1a\uff0c\u4ee5\u4e3a\u662f\u6211\u6c14\u91cf\u72ed\u7a84\uff0c\u4e0d\u8bb8\u4f60\u53bb\uff0c \u5bf9\u5566\uff0c\u5c60\u51e4\u521a\u624d\u66fe\u8bf4\u5979\u4eec\u6240\u5728\u4e4b\u5904\u79bb\u6b64\u4e0d\u8fdc\uff0c\u4e0d\u5982\u4f60\u5c31\u53bb\u627e\u5979\u3002\u6211\u53ef\u4ee5\u5728\u4e00\u4e2a\u7ea6\u5b9a\u7684\u5730\u65b9\u7b49 \u4f60\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u53ea\u8981\u54b1\u4eec\u7684\u5fc3\u91cc\u6ca1\u6709\u82a5\u8482\uff0c\u65c1\u4eba\u7684\u95f2\u8bdd\u7b97\u5f97\u4e86\u4ec0\u4e48\uff1f\u4e0d\u9519\uff0c\u6211\u662f\u6b20\u4e86\u5b5f\u660e \u971e\u7684\u601d\u60c5\uff0c\u5e94\u8be5\u5411\u5979\u9053\u8c22\u3002\u4f46\u8fd9\u5374\u4e0d\u662f\u4ec0\u4e48\u5fc5\u987b\u7acb\u523b\u53bb\u529e\u7684\u5927\u4e8b\u3002\u5f7c\u6b64\u90fd\u662f\u6c5f\u6e56\u513f\u5973\uff0c\u540c\u9053 \u4e2d\u4eba\uff0c\u60f3\u6765\u5b5f\u660e\u971e\u4e5f\u4e0d\u4f1a\u602a\u6211\u5931\u793c\u7684\u3002\u518d\u8bf4\u53cb\u60c5\u56fa\u7136\u7d27\u8981\uff0c\u603b\u6bd4\u4e0d\u4e0a\u592b\u5987\u4e4b\u60c5\u3002\u5a49\u59b9\uff0c\u6211\u7d2f \u4f60\u4e00\u8def\u966a\u6211\u62c5\u60ca\u53d7\u9669\uff0c\u4f46\u76fc\u548c\u4f60\u65e9\u70b9\u56de\u5230\u5bb6\u4e61\u6211\u624d\u80fd\u653e\u5f97\u4e0b\u5fc3\uff0c\u55ef\uff0c\u54b1\u4eec\u8fd8\u662f\u5feb\u70b9\u8d76\u8def \u5427\u3002\u201d \u3000\u3000\u6768\u5a49\u773c\u4e2d\u542b\u7740\u6cea\u6c34\uff0c\u7b11\u9053\uff1a\u201c\u6211\u4e0d\u8fc7\u95ee\u4f60\u4e00\u53e5\uff0c\u4f60\u8bf4\u4e86\u4e00\u5927\u8f66\u5b50\u7684\u8bdd\u3002\u597d\u5566\uff0c\u4e0d\u53bb\u5c31\u4e0d \u53bb\uff0c\u4e5f\u4e0d\u7528\u591a\u89e3\u91ca\u4e86\u3002\u4f60\u5bf9\u6211\u597d\uff0c\u6211\u5fc3\u91cc\u660e\u767d\uff0c\u96be\u9053\u6211\u8fd8\u4e0d\u76f8\u4fe1\u4f60\u5417\uff1f\u201d \u3000\u3000\u8bdd\u867d\u5982\u6b64\uff0c\u4f46\u8981\u8bf4\u6768\u5a49\u5fc3\u91cc\u6beb\u65e0\u82a5\u8482\uff0c\u5374\u8fd8\u672a\u80fd\u3002\u4e0d\u9519\uff0c\u5979\u662f\u4fe1\u5f97\u8fc7\u674e\u601d\u5357\uff0c\u4f46\u5979\u4e5f\u9690 \u9690\u611f\u89c9\u5f97\u5230\uff0c\u674e\u601d\u5357\u5bf9\u5b5f\u660e\u971e\u90a3\u6bb5\u611f\u60c5\uff0c\u5373\u4f7f\u6ca1\u6df7\u6709\u6742\u5ff5\uff0c\u4f46\u5374\u4e5f\u672a\u80fd\u505a\u5230\u201c\u541b\u5b50\u5766\u8361\u8361\u201d \u7684\u80f8\u6000\u3002\u201c\u8981\u4e0d\u662f\u4ed6\u8fd8\u6709\u4e00\u70b9\u513f\u5fc3\u75c5\uff0c\u4ed6\u5c31\u4e0d\u5fc5\u56de\u907f\u5b5f\u660e\u971e\u4e86\u3002\u201d\u6768\u5a49\u5fc3\u60f3\u3002 \u3000\u3000\u674e\u601d\u5357\u5012\u662f\u6709\u70b9\u5bb3\u6015\u5728\u8def\u4e0a\u78b0\u89c1\u5b5f\u660e\u971e\uff0c\u4e8e\u662f\u4e00\u8def\u7eb5\u9a6c\u75be\u9a70\uff0c\u6768\u5a49\u8ddf\u4ed6\u4e0d\u4e0a\uff0c\u7b11\u9053\uff1a \u201c\u8dd1\u8fd9\u6837\u5feb\u5e72\u5417\uff1f\u4f60\u4e0d\u53bb\u770b\u5979\uff0c\u96be\u9053\u6015\u5979\u8ffd\u6765\u770b\u4f60\uff1f\u55ef\uff0c\u5357\u54e5\uff0c\u6211\u5012\u662f\u66ff\u4f60\u6709\u70b9\u53ef\u60dc\uff0c\u5b5f\u59d1 \u5a18\u5c31\u5728\u9644\u8fd1\uff0c\u4f60\u4e00\u9635\u5feb\u9a6c\u8def\u8fc7\u53bb\uff0c\u628a\u5979\u7529\u5728\u8111\u540e\uff0c\u4ee5\u540e\u53ef\u5c31\u4e0d\u77e5\u4ec0\u4e48\u65f6\u5019\u624d\u80fd\u89c1\u9762\u4e86\u3002\u96be\u9053 \u4f60\u5c31\u6ca1\u6709\u2018\u54ab\u5c3a\u5929\u6daf\u2019\u4e4b\u611f\u4e48\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u82e6\u7b11\u9053\uff1a\u201c\u5a49\u59b9\uff0c\u4f60\u53c8\u6765\u4e86\u3002\u201d\u6768\u5a49\u7b11\u9053\uff1a\u201c\u548c\u4f60\u8bf4\u7b11\u7684\uff0c\u4f60\u7740\u6025\u4ec0\u4e48\uff1f\u8d70\u5427\uff0c \u4f46\u5374\u4e0d\u5fc5\u5feb\u9a6c\u52a0\u97ad\u4e86\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u867d\u7136\u51b3\u5b9a\u4e86\u4e0d\u53bb\u4e0e\u5b5f\u660e\u971e\u76f8\u4f1a\uff0c\u4f46\u5374\u4e5f\u662f\u7ed9\u6768\u5a49\u8bf4\u4e2d\u4e86\u5fc3\u4e8b\uff0c\u84e6\u5730\u60f3\u8d77\u5c0f\u65f6\u5019\u8bfb \u8fc7\u7684\u4e00\u9996\u8bd7\uff1a\u201c\u4eba\u751f\u5230\u5904\u77e5\u4f55\u4f3c\uff1f\u77e5\u662f\u98de\u9e3f\u8e0f\u96ea\u6ce5\u3002\u6ce5\u4e0a\u5076\u7136\u7559\u6307\u722a\uff0c\u9e3f\u98de\u54ea\u590d\u8bb0\u4e1c\u897f\uff1f\u201d \u60f3\u8d77\u6768\u5a49\u8bf4\u7684\u201c\u54ab\u5c3a\u5929\u6daf\u201d\u56db\u5b57\uff0c\u4e0d\u89c9\u4e00\u7247\u60d8\u7136\u3002 \u3000\u3000\u674e\u601d\u5357\u5728\u8fd9\u91cc\u6005\u6005\u60d8\u60d8\uff0c\u53e6\u4e00\u6761\u8def\u4e0a\uff0c\u5c60\u51e4\u4e5f\u6b63\u5728\u4e3a\u7740\u53cb\u8c0a\u4e0e\u7231\u60c5\u800c\u611f\u4f24\u3002 \u3000\u3000\u80ef\u4e0b\u9a6c\u513f\u98de\u8dd1\uff0c\u5fc3\u4e2d\u65e7\u4e8b\u91cd\u7ffb\u3002\u8bb0\u5fc6\u8dd1\u5f97\u5f88\u8fdc\uff0c\u6bd4\u9a6c\u5feb\u5f97\u591a\u4e86\u3002\u5b83\u8dd1\u8fc7\u4e86\u4e07\u6c34\u5343\u5c71\uff0c\u8dd1 \u8fc7\u4e86\u5341\u51e0\u5e74\u5c81\u6708\uff0c\u56de\u5230\u4e86\u5c60\u51e4\u7684\u513f\u65f6\uff0c\u56de\u5230\u4e86\u5c60\u51e4\u7684\u6545\u56ed\u3002 \u3000\u3000\u5c60\u51e4\u6e05\u695a\u8bb0\u5f97\uff0c\u4e8c\u5e08\u5144\u9f99\u521a\u662f\u5979\u4e03\u5c81\u90a3\u5e74\u6765\u7684\uff0c\u4e09\u5e08\u5144\u77f3\u749e\u662f\u5979\u4e5d\u5c81\u90a3\u5e74\u6765\u7684\uff0c\u4ed6\u4eec\u4e09 \u4eba\u81ea\u5c0f\u5c31\u5728\u4e00\u8d77\u6e38\u73a9\u3001\u7ec3\u6b66\uff0c\u5f62\u5f71\u4e0d\u79bb\u3002\u4e24\u4e2a\u5e08\u5144\u5bf9\u5979\u90fd\u5f88\u597d\uff0c\u5979\u5bf9\u4e24\u4e2a\u5e08\u5144\u4e5f\u662f\u4e00\u6837\u3002\u4f46 \u5728\u6e10\u6e10\u957f\u5927\u4e4b\u540e\uff0c\u5728\u5979\u7684\u5c0f\u5c0f\u5fc3\u7075\u4e4b\u4e2d\uff0c\u5bf9\u4e09\u5e08\u5144\u7684\u611f\u89c9\u5c31\u597d\u4f3c\u6709\u4e9b\u4e0d\u540c\u4e86\u3002 \u3000\u3000\u9f99\u521a\u5e74\u7eaa\u6bd4\u5979\u5927\u4e03\u5c81\uff0c\u77f3\u749e\u5219\u4ec5\u4ec5\u6bd4\u5979\u5927\u4e24\u5c81\uff0c\u4e24\u4e2a\u5e74\u7eaa\u6bd4\u8f83\u63a5\u8fd1\uff0c\u5927\u5bb6\u540c\u5728\u4e00\u8d77\u6e38\u73a9 \u7684\u65f6\u5019\uff0c\u5979\u548c\u77f3\u749e\u4e0d\u77e5\u4e0d\u89c9\u5730\u4e5f\u4f3c\u4e4e\u4eb2\u8fd1\u4e00\u4e9b\uff0c\u4f46\u4e89\u5435\u5374\u4e5f\u8f83\u591a\u3002\u5979\u548c\u4e8c\u5e08\u5144\u9f99\u521a\u5374\u662f\u4ece\u672a \u5435\u8fc7\u5634\u7684\u3002\u9f99\u521a\u597d\u50cf\u5979\u7684\u957f\u5144\u4e00\u6837\uff0c\u603b\u662f\u8ba9\u7740\u5979\u3002 \u3000\u3000\u4e24\u4e2a\u5e08\u5144\u5bf9\u5979\u90fd\u5f88\u597d\uff0c\u5979\u7684\u4eb2\u54e5\u54e5\u53cd\u800c\u662f\u548c\u5979\u5408\u4e0d\u6765\u3002\u5979\u7684\u54e5\u54e5\u540d\u53eb\u5c60\u9f99\uff0c\u4e0e\u9f99\u521a\u540c \u5e74\uff0c\u81ea\u5c0f\u8ddf\u968f\u7236\u4eb2\uff0c\u6bd4\u9f99\u521a\u65e9\u51e0\u5e74\u7ec3\u6210\u6b66\u529f\uff0c\u4eba\u53c8\u806a\u660e\u80fd\u5e72\uff0c\u56e0\u6b64\u5728\u5341\u516b\u5c81\u90a3\u5e74\u4fbf\u5f00\u59cb\u51fa\u9053 \u4e86\u3002 \u3000\u3000\u5c60\u9f99\u51fa\u9053\u5f97\u65e9\uff0c\u5728\u4ed6\u7684\u59b9\u59b9\u8fd8\u5728\u548c\u4e24\u4e2a\u5e08\u5144\u7ec3\u6b66\u7684\u65f6\u5019\uff0c\u4ed6\u5df2\u7ecf\u5728\u6c5f\u6e56\u4e0a\u95ef\u51fa\u4e86\u540d\u5934\uff0c \u4ea4\u7ed3\u4e86\u8bb8\u591a\u670b\u53cb\u3002\u4ed6\u7684\u670b\u53cb\u4e09\u6559\u4e5d\u6d41\u90fd\u6709\uff0c\u6709\u51e0\u4e2a\u4f5c\u98ce\u5f88\u4e0d\u6b63\u6d3e\uff0c\u4ed6\u66fe\u7ecf\u5e26\u8fc7\u4ed6\u7684\u4e00\u4e9b\u670b\u53cb \u56de\u5bb6\uff0c\u5c60\u51e4\u77a7\u7740\u5c31\u4e0d\u987a\u773c\u3002\u5c60\u9f99\u4e00\u5411\u4e5f\u4e0d\u7406\u776c\u59b9\u59b9\u3002 \u3000\u3000\u56e0\u6b64\u5728\u5c60\u51e4\u7684\u5fc3\u91cc\uff0c\u5012\u4f3c\u4e4e\u89c9\u5f97\u9f99\u521a\u66f4\u50cf\u5979\u7684\u957f\u5144\u3002\u81f3\u4e8e\u77f3\u749e\uff0c\u6709\u65f6\u5019\u5979\u89c9\u5f97\u4ed6\u50cf\u54e5 \u54e5\uff0c\u5904\u5904\u7167\u6599\u5979\uff1b\u6709\u65f6\u5019\u53c8\u89c9\u4ed6\u50cf\u5f1f\u5f1f\uff0c\u8fd8\u9700\u8981\u5979\u7684\u7231\u62a4\u3002\u8fd9\u4efd\u5947\u7279\u7684\u611f\u60c5\uff0c\u540e\u6765\u5f85\u5979\u61c2\u5f97 \u4eba\u4e8b\u4e4b\u540e\u60f3\u8d77\u6765\uff0c\u4e5f\u8fd8\u662f\u89c9\u5f97\u83ab\u540d\u5176\u5999\u3002 \u3000\u3000\u5c60\u9f99\u7684\u7236\u4eb2\u5c60\u767e\u57ce\u5f88\u4ee5\u513f\u5b50\u7684\u6ee5\u4ea4\u4e3a\u8651\uff0c\u4f46\u4e00\u6765\u513f\u5b50\u5df2\u7ecf\u957f\u5927\uff0c\u4e8c\u6765\u5c60\u767e\u57ce\u4e5f\u662f\u7ecf\u5e38\u4e0d \u5728\u5bb6\u7684\uff0c\u53ea\u597d\u7531\u4ed6\u53bb\u4e86\u3002 \u3000\u3000\u65e5\u5b50\u4e00\u5929\u5929\u8fc7\u53bb\uff0c\u5c60\u51e4\u4ece\u4e00\u4e2a\u62d6\u7740\u9f3b\u6d95\u7684\u5c0f\u59d1\u5a18\u957f\u6210\u4e86\u4e00\u4e2a\u660e\u8273\u52a8\u4eba\u7684\u5c11\u5973\uff0c\u5979\u6bcd\u4eb2\u5f00 \u59cb\u4e3a\u5979\u7684\u5a5a\u4e8b\u601d\u91cf\u4e86\u3002\u6bcd\u4eb2\u66fe\u7ecf\u4e0d\u53ea\u4e00\u6b21\u5730\u5077\u5077\u95ee\u8fc7\u5979\uff0c\u5728\u4e24\u4e2a\u5e08\u5144\u4e4b\u4e2d\u5979\u66f4\u559c\u6b22\u7684\u662f\u54ea\u4e00 \u4e2a\uff0c\u6bcf\u6b21\u6bcd\u4eb2\u8fd9\u6837\u95ee\u5979\uff0c\u5c60\u51e4\u603b\u662f\u7ea2\u7740\u8138\u56de\u7b54\uff1a\u201c\u6211\u4e0d\u77e5\u9053\u3002\u201d\u6216\u8005\u662f\u8bf4\uff1a\u201c\u6211\u5bf9\u4e24\u4f4d\u5e08\u5144 \u90fd\u662f\u4e00\u6837\u3002\u201d\u5176\u5b9e\u5979\u81ea\u5df1\u5fc3\u91cc\u77e5\u9053\u662f\u5e76\u4e0d\u4e00\u6837\u7684\uff01 \u3000\u3000\u9f99\u521a\u8001\u6210\u4e14\u517c\u5e72\u7ec3\uff0c\u77f3\u749e\u7eaf\u539a\u800c\u53c8\u806a\u660e\uff0c\u5c60\u592b\u4eba\u5411\u6765\u5bf9\u4ed6\u4eec\u4e5f\u662f\u4e00\u89c6\u540c\u4ec1\uff0c\u96be\u5206\u8f69\u8f7b \u7684\u3002\u5979\u60f3\u5728\u8fd9\u4e24\u4e2a\u5f92\u5f1f\u4e4b\u4e2d\uff0c\u6311\u9009\u4e00\u4e2a\u4f5c\u5979\u5973\u5a7f\uff0c\u4f46\u56e0\u5973\u513f\u8fdf\u8fdf\u4e0d\u80af\u8868\u793a\u6001\u5ea6\uff0c\u5c60\u592b\u4eba\u59d4\u51b3 \u4e0d\u4e0b\uff0c\u5a5a\u59fb\u4e4b\u8bae\u53ea\u597d\u6682\u4e14\u62d6\u5ef6\u3002\u5979\u6253\u7b97\u5f85\u4e08\u592b\u56de\u6765\uff0c\u624d\u4f5c\u6700\u540e\u7684\u5b9a\u593a\u3002 \u3000\u3000\u5c60\u767e\u57ce\u4e34\u884c\u4e4b\u65f6\uff0c\u66fe\u7ecf\u548c\u59bb\u5b50\u8bf4\u8fc7\uff1a\u6b64\u53bb\u8499\u53e4\uff0c\u5feb\u5219\u4e09\u6708\uff1b\u8fdf\u5219\u534a\u5e74\uff0c\u5c31\u4f1a\u56de\u6765\u3002\u4e0d\u6599 \u4e09\u4e2a\u6708\u8fc7\u53bb\u4e86\uff0c\u534a\u5e74\u4e5f\u8fc7\u53bb\u4e86\uff0c\u534a\u5e74\u53c8\u52a0\u534a\u5e74\uff0c\u4e00\u5e74\u90fd\u8fc7\u53bb\u4e86\uff0c\u5979\u7684\u4e08\u592b\u8fd8\u662f\u4e0d\u89c1\u56de\u6765\uff01\u6c34 \u8fdc\u5c71\u9065\uff0c\u5409\u51f6\u96be\u6d4b\u3002\u5c60\u592b\u4eba\u9690\u7ea6\u542c\u5230\u98ce\u58f0\uff0c\u8bf4\u662f\u5979\u4e08\u592b\u5728\u8499\u53e4\u5df2\u906d\u4e0d\u5e78\uff0c\u53ea\u662f\u8fd8\u672a\u80fd\u8bc1\u5b9e\u800c \u5df2\u3002\u5c60\u592b\u4eba\u5fe7\u6025\u6210\u75c5\uff0c\u5728\u8fd9\u6837\u60c5\u5f62\u4e4b\u4e0b\uff0c\u5f53\u7136\u66f4\u662f\u65e0\u5fc3\u8fdb\u884c\u5973\u513f\u7684\u5a5a\u4e8b\u4e86\u3002 \u3000\u3000\u6bcd\u4eb2\u8fd9\u8fb9\u51b7\u6de1\u4e0b\u6765\uff0c\u5c60\u51e4\u7684\u54e5\u54e5\u5374\u6765\u5173\u5fc3\u59b9\u59b9\u7684\u5a5a\u4e8b\u4e86\u3002\u5c60\u9f99\u6709\u4e2a\u670b\u53cb\uff0c\u540d\u5524\u6df3\u4e8e\u8191\uff0c \u4e09\u5e74\u4e4b\u524d\uff0c\u66fe\u7ecf\u548c\u5c60\u9f99\u6765\u8fc7\u4e00\u6b21\u3002\u6df3\u4e8e\u8191\u7684\u7236\u4eb2\u6df3\u4e8e\u5468\u662f\u9ed1\u9053\u4e0a\u7684\u8457\u540d\u7684\u4eba\u7269\uff0c\u58f0\u540d\u4ec5\u6b21\u4e8e \u5c60\u767e\u57ce\uff0c\u4f46\u4e24\u4eba\u7684\u4f5c\u98ce\u5374\u9887\u6709\u4e0d\u540c\u3002\u6df3\u4e8e\u5468\u4e0d\u4f46\u624b\u8fa3\uff0c\u800c\u4e14\u5fc3\u9ed1\uff0c\u4ed6\u5bf9\u9ed1\u9053\u767d\u9053\u5168\u4e0d\u5356\u5e10\uff0c \u6ca1\u6709\u4e00\u5b9a\u7684\u670b\u53cb\uff0c\u4e5f\u6ca1\u6709\u4e00\u5b9a\u7684\u654c\u4eba\uff0c\u552f\u5229\u662f\u89c6\uff0c\u597d\u6076\u968f\u5fc3\u3002\u6df3\u4e8e\u8191\u201c\u9752\u51fa\u4e8e\u84dd\u201d\uff0c\u5728\u6c5f\u6e56 \u4e0a\u7684\u58f0\u540d\u6bd4\u4ed6\u7236\u4eb2\u66f4\u574f\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u5c60\u767e\u57ce\u548c\u6df3\u4e8e\u5468\u867d\u7136\u5f88\u5c11\u6765\u5f80\uff0c\u4e5f\u6ca1\u6709\u8fc7\u516c\u5f00\u7684\u51b2\u7a81\u3002\u6240\u4ee5\u90a3\u6b21\u6df3\u4e8e\u5468\u7684\u513f\u5b50\u5230 \u4ed6\u5bb6\u91cc\uff0c\u4ed6\u8fd8\u662f\u628a\u4ed6\u5f53\u4f5c\u4e00\u4e2a\u201c\u4e16\u4f84\u201d\u62db\u5f85\u3002\u6df3\u4e8e\u8191\u662f\u4e2a\u806a\u660e\u4eba\uff0c\u77e5\u9053\u8fd9\u4e2a\u201c\u4e16\u4f2f\u201d\u4e0d\u5f88\u559c \u6b22\u4ed6\uff0c\u6765\u4e86\u4e00\u6b21\u5c31\u4e0d\u518d\u6765\u4e86\u3002 \u3000\u3000\u4e0d\u77e5\u4e0d\u89c9\u8fc7\u4e86\u4e09\u5e74\uff0c\u5c60\u51e4\u56e0\u4e3a\u4ece\u672a\u628a\u8fd9\u6df3\u4e8e\u8191\u653e\u5728\u5fc3\u4e0a\uff0c\u5dee\u4e0d\u591a\u90fd\u5df1\u5fd8\u8bb0\u4ed6\u4e86\u3002\u4e0d\u6599\u5c31 \u5728\u5979\u7236\u4eb2\u7684\u6b7b\u8baf\u8bc1\u5b9e\u7684\u524d\u4e24\u5929\uff0c\u5979\u7684\u54e5\u54e5\u5c60\u9f99\u5ffd\u7136\u53c8\u548c\u8fd9\u4e2a\u6df3\u4e8e\u8191\u4e00\u540c\u56de\u5bb6\u3002 \u3000\u3000\u5c60\u9f99\u8fd9\u6b21\u56de\u6765\uff0c\u5bf9\u59b9\u59b9\u7684\u6001\u5ea6\u5927\u5927\u4e0d\u540c\uff0c\u62c9\u7740\u59b9\u59b9\uff0c\u95ee\u957f\u95ee\u77ed\uff0c\u9001\u5979\u4e00\u4e9b\u660e\u73e0\uff0c\u8fd8\u6709\u4e00 \u5bf9\u7389\u7c2a\uff0c\u8fd9\u4e24\u6837\u793c\u7269\uff0c\u90fd\u662f\u4ef7\u503c\u8fde\u57ce\u7684\u73cd\u5b9d\u3002\u5c60\u51e4\u5e76\u975e\u770b\u91cd\u793c\u7269\uff0c\u4f46\u5374\u5f88\u9ad8\u5174\u54e5\u54e5\u5bf9\u5979\u7684\u5173 \u6000\uff0c\u56e0\u6b64\u4e5f\u5c31\u6536\u4e0b\u4e86\u3002 \u3000\u3000\u5c60\u9f99\u8bf4\u6765\u8bf4\u53bb\uff0c\u6e10\u6e10\u5c31\u8bf4\u5230\u6df3\u4e8e\u8191\u8eab\u4e0a\uff0c\u5927\u5927\u4e3a\u4ed6\u5439\u5618\u3002\u8bf4\u5230\u540e\u6765\uff0c\u56fe\u7a77\u5315\u89c1\uff0c\u7adf\u662f\u8981 \u4e3a\u6df3\u4e8e\u8191\u505a\u5a92\u3002\u5c60\u51e4\u5f53\u7136\u4e0d\u80af\u7b54\u5e94\uff0c\u5144\u59b9\u4e89\u5435\u8d77\u6765\u3002 \u3000\u3000\u5144\u59b9\u4e89\u5435\uff0c\u60ca\u52a8\u4e86\u540e\u5802\u7684\u6bcd\u4eb2\u3002\u5c60\u592b\u4eba\u6276\u75c5\u51fa\u6765\uff0c\u95ee\u660e\u6240\u4ee5\uff0c\u4e5f\u662f\u4e0d\u503c\u513f\u5b50\u6240\u4e3a\uff0c\u72e0\u72e0 \u5730\u6570\u8bf4\u4e86\u5c60\u9f99\u4e00\u987f\u3002\u8bf4\u5730\u4e0d\u8be5\u5f3a\u903c\u59b9\u59b9\uff0c\u5c24\u5176\u4e0d\u8be5\u5728\u7236\u4eb2\u751f\u6b7b\u672a\u535c\u4e4b\u9645\uff0c\u56de\u5bb6\u60f9\u662f\u751f\u975e\u3002 \u3000\u3000\u5c60\u9f99\u8001\u7f9e\u6210\u6012\uff0c\u7adf\u7136\u548c\u6bcd\u4eb2\u9876\u649e\u8d77\u6765\uff0c\u4ed6\u8bf4\u7236\u4eb2\u4e0d\u77e5\u4f55\u65f6\u56de\u6765\uff0c\u5018\u82e5\u5341\u5e74\u516b\u5e74\u4e0d\u56de\u6765\uff0c \u96be\u9053\u59b9\u59b9\u4e5f\u4e0d\u51fa\u5ac1\uff1f\u4ed6\u66ff\u59b9\u59b9\u4e3b\u5a5a\uff0c\u53c8\u7109\u80fd\u8bf4\u662f\u60f9\u662f\u751f\u975e\uff1f \u3000\u3000\u5c60\u592b\u4eba\u53ea\u6709\u8fd9\u4e00\u4e2a\u513f\u5b50\uff0c\u81ea\u5c0f\u5c31\u628a\u4ed6\u5ba0\u60ef\u4e86\u7684\u3002\u5c60\u9f99\u751f\u5e73\u53ea\u6015\u7236\u4eb2\uff0c\u6bcd\u4eb2\u53ef\u7ba1\u4ed6\u4e0d\u4e86\u3002 \u4e0d\u8fc7\uff0c\u5c60\u9f99\u4ee5\u5f80\u867d\u7136\u4e5f\u662f\u7ecf\u5e38\u4e0d\u542c\u6bcd\u4eb2\u7684\u8bdd\uff0c\u4f46\u50cf\u4eca\u5929\u8fd9\u6837\u7684\u9876\u649e\u4ed6\u7684\u6bcd\u4eb2\uff0c\u8fc7\u53bb\u5374\u8fd8\u662f\u672a \u66fe\u6709\u8fc7\u7684\u3002 \u3000\u3000\u5c60\u51e4\u5fc3\u91cc\u9635\u9635\u7ede\u75db\uff0c\u90a3\u4e00\u65e5\u5435\u95f9\u7684\u60c5\u666f\uff0c\u5982\u5728\u773c\u524d\u3002 \u3000\u3000\u6bcd\u4eb2\u6c14\u9ec4\u4e86\u9762\uff0c\u9a82\u9053\uff1a\u201c\u4f60\u7239\u751f\u6b7b\u672a\u535c\uff0c\u5373\u4f7f\u4f60\u7239\u6b7b\u4e86\uff0c\u4e5f\u8fd8\u6709\u6211\u5462\u3002\u8f6e\u4e0d\u5230\u4f60\u4f5c \u4e3b\uff01\u201d \u3000\u3000\u54e5\u54e5\u89c1\u6bcd\u4eb2\u52a8\u4e86\u6c14\uff0c\u521d\u65f6\u5012\u4e5f\u4e0d\u6562\u53cd\u5507\u76f8\u8ba5\uff0c\u4f46\u4ed6\u72e1\u733e\u5f97\u5f88\uff0c\u5374\u7528\u8bd5\u63a2\u7684\u53e3\u543b\u8bf4\u9053\uff1a \u201c\u6211\u4e5f\u4e0d\u8fc7\u662f\u4e3a\u4e86\u59b9\u59b9\u7684\u597d\uff0c\u4fd7\u8bed\u8bf4\uff1a\u2018\u5973\u5927\u4e0d\u4e2d\u7559\u2019\uff0c\u8fdf\u65e9\u603b\u662f\u8981\u628a\u5979\u5ac1\u51fa\u53bb\u7684\u3002\u5988\uff0c\u4f60 \u82e5\u662f\u7ed9\u5979\u627e\u5f97\u597d\u7684\u5a46\u5bb6\uff0c\u6211\u8fd9\u4e2a\u505a\u54e5\u54e5\u7684\u4e5f\u53ef\u4ee5\u5c11\u64cd\u5fc3\u4e8b\u3002\u5c31\u4e0d\u77e5\u4f60\u5fc3\u76ee\u91cc\u6709\u4e86\u597d\u7684\u4eba\u5bb6\u6ca1 \u6709\uff1f\u201d \u3000\u3000\u6bcd\u4eb2\u7ed9\u54e5\u54e5\u7684\u51e0\u53e5\u597d\u8bdd\u4e00\u8bf4\uff0c\u4e0d\u89c9\u5c31\u9732\u51fa\u4e86\u53e3\u98ce\uff1a\u201c\u653e\u5728\u773c\u524d\u7684\u5979\u7684\u4e24\u4e2a\u5e08\u5144\uff0c\u5c31\u90fd\u662f \u597d\u4eba\u5bb6\u7684\u5b50\u5f1f\u3002\u4e0d\u7ba1\u662f\u9f99\u521a\u6216\u8005\u77f3\u749e\uff0c\u54ea\u4e00\u4e2a\u90fd\u8981\u6bd4\u4f60\u7684\u90a3\u4f4d\u670b\u53cb\u5f3a\u5f97\u591a\uff01\u201d \u3000\u3000\u54e5\u54e5\u7eb5\u58f0\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5988\uff0c\u4f60\u6709\u8bb8\u591a\u5e74\u672b\u51fa\u8fc7\u5bb6\u95e8\u4e86\u5427\uff0c\u602a\u4e0d\u5f97\u4f60\u8fd9\u6837\u7cca\u6d82\uff01\u4f60\u53ef\u77e5 \u9053\u6df3\u4e8e\u8191\u5728\u6c5f\u6e56\u4e0a\u6709\u591a\u5927\u7684\u58f0\u540d\uff1f\u4f60\u53ef\u77e5\u9053\u4ed6\u7684\u6b66\u529f\u5df2\u7ecf\u5c3d\u5f97\u5bb6\u4f20\uff0c\u751a\u81f3\u5f3a\u7239\u80dc\u7956\uff1f\u4f60\u53ef\u77e5 \u9053\u4ed6\u8d70\u904d\u5927\u6c5f\u5357\u5317\uff0c\u8bb8\u591a\u6b66\u6797\u4e2d\u7684\u6210\u540d\u4eba\u7269\u90fd\u6298\u5728\u4ed6\u7684\u624b\u91cc\uff1f\u563f\uff0c\u563f\uff0c\u4f60\u8981\u662f\u4e0d\u76f8\u4fe1\u7684\u8bdd\uff0c \u4f60\u53ef\u4ee5\u53eb\u9f99\u3001\u77f3\u4e24\u4f4d\u5e08\u5f1f\u548c\u4ed6\u8bd5\u8bd5\uff01\u4f60\u628a\u4f60\u8fd9\u4e24\u4e2a\u5f92\u5f1f\u5f53\u4f5c\u5b9d\u8d1d\uff0c\u5728\u6211\u770b\u6765\uff0c\u4ed6\u6bd4\u6df3\u4e8e\u8191\u7684 \u4e00\u6839\u5c0f\u6307\u5934\u90fd\u6bd4\u4e0d\u4e0a\u5462\uff0c\u5988\uff0c\u4e0d\u662f\u6211\u8bf4\u4f60\uff0c\u8bd5\u8fc7\u4e4b\u540e\uff0c\u4f60\u5c31\u77e5\u9053\u4f60\u8fd9\u662f\u4e95\u86d9\u4e4b\u89c1\u4e86\uff01\u201d \u3000\u3000\u6bcd\u4eb2\u6c14\u788d\u53cc\u773c\u7ffb\u767d\uff0c\u9a82\u9053\uff1a\u201c\u4f60\u8ba5\u7b11\u6211\u89c1\u95fb\u4e0d\u5e7f\uff0c\u4e0d\u9519\uff0c\u6211\u662f\u89c1\u95fb\u4e0d\u5e7f\uff0c\u4f46\u6211\u5374\u77e5\u9053\u6df3 \u4e8e\u5468\u3001\u6df3\u4e8e\u8191\u4e24\u7236\u5b50\u90fd\u662f\u540c\u4e00\u4e2a\u6a21\u578b\u94f8\u51fa\u6765\u7684\uff0c\u5728\u6c5f\u6e56\u4e0a\u58f0\u540d\u72fc\u85c9\uff0c\u4e0d\u662f\u4e3a\u4e86\u4f60\u7684\u7f18\u6545\uff0c\u6211 \u8fd8\u4e0d\u4f1a\u62db\u5f85\u4ed6\u5462\uff01\u6b66\u529f\u518d\u597d\u4e5f\u6ca1\u6709\u7528\uff0c\u6700\u7d27\u8981\u7684\u662f\u884c\u4e3a\u6b63\u6d3e\u3002\u6211\u7684\u5973\u513f\u51b3\u4e0d\u80fd\u5ac1\u7ed9\u6df3\u4e8e\u8191\u8fd9 \u4e00\u79cd\u4eba\uff01\u201d \u3000\u3000\u54e5\u54e5\u5c45\u7136\u8fd8\u5728\u51b7\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u62db\u4eba\u5fcc\u662f\u5eb8\u624d\uff0c\u6211\u548c\u4ed6\u662f\u591a\u5e74\u7684\u597d\u670b\u53cb\uff0c\u5982\u679c\u4ed6\u4e0d\u6b63 \u6d3e\uff0c\u6211\u8fd8\u80fd\u548c\u4ed6\u7ed3\u4ea4\uff1f\u201d \u3000\u3000\u6bcd\u4eb2\u6c14\u5f97\u5598\u7740\u6c14\u9a82\uff1a\u201c\u4f60\u8fd9\u662f\u8fd1\u6731\u8005\u4ea6\uff0c\u8fd1\u58a8\u8005\u9ed1\uff01\u4f60\u518d\u8bf4\u6211\u5c31\u628a\u4f60\u548c\u4f60\u7684\u597d\u670b\u53cb\u90fd\u8d76 \u51fa\u53bb\uff01\u201d \u3000\u3000\u5c60\u51e4\u63d2\u4e0d\u8fdb\u53e3\u53bb\uff0c\u4f46\u4e5f\u6c14\u5f97\u80ba\u90fd\u70b8\u4e86\uff0c\u6b63\u8981\u6307\u65a5\u5979\u7684\u54e5\u54e5\uff0c\u5c60\u9f99\u5374\u5ffd\u5730\u5728\u5979\u7684\u8eab\u4e0a\u505a\u8d77 \u201c\u6587\u7ae0\u201d\u6765\uff1a\u201c\u5988\uff0c\u4f60\u4e0d\u559c\u6b22\u8fd9\u4e2a\u6df3\u4e8e\u8191\uff0c\u59b9\u59b9\u53ef\u6536\u4e86\u4ed6\u7684\u8058\u793c\u5462\uff01\u201d \u3000\u3000\u5c60\u51e4\u4e00\u65f6\u95f4\u5c1a\u672a\u660e\u767d\uff0c\u5927\u6012\u8df3\u8d77\uff0c\u9a82\u9053\uff1a\u201c\u80e1\u8bf4\u516b\u9053\uff0c\u6211\u6536\u4e86\u4ed6\u4ec0\u4e48\u8058\u793c\uff1f\u201d \u3000\u3000\u5ffd\u542c\u5f97\u201c\u5495\u549a\u201d\u4e00\u58f0\uff0c\u6bcd\u4eb2\u53eb\u9053\uff1a\u201c\u755c\u7272\uff0c\u4f60\u7ed9\u6211\u6eda\uff01\u201d\u6bcd\u4eb2\u7684\u624b\u6756\u4e00\u6454\uff0c\u8dcc\u5012\u5730\u4e0a\u3002 \u3000\u3000\u5c60\u51e4\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u5931\u58f0\u53eb\u9053\uff1a\u201c\u5988\u7ed9\u4f60\u6c14\u6b7b\u5566\uff01\u201d\u5fd9\u628a\u6bcd\u4eb2\u6276\u4e86\u8d77\u6765\uff0c\u56de\u5934\u5f85\u4e0e\u54e5 \u54e5\u7b97\u5e10\uff0c\u5374\u5df2\u4e0d\u89c1\u4e86\u5c60\u9f99\u7684\u4eba\u5f71\u3002 \u3000\u3000\u5a62\u4ec6\u95fb\u58f0\u8d76\u6765\uff0c\u4e03\u624b\u516b\u811a\u5730\u614c\u5fd9\u65bd\u6551\u3002\u5e78\u4e8f\u5c60\u592b\u4eba\u53ea\u662f\u4e00\u65f6\u8f6c\u4e0d\u8fc7\u6c14\u6765\uff0c\u4ee5\u81f4\u6655\u5012\u7684\uff0c \u4e0d\u4e45\u4e5f\u5c31\u9192\u4e86\u3002 \u3000\u3000\u5c60\u592b\u4eba\u9192\u8fc7\u6765\uff0c\u6c14\u8fd8\u672a\u8fc7\uff0c\u4e00\u7741\u5f00\u773c\u4fbf\u5373\u559d\u95ee\uff1a\u201c\u90a3\u5b7d\u755c\u5462\uff1f\u201d\u5a62\u4ec6\u4eec\u9762\u9762\u76f8\u89d1\uff0c\u4e0d\u6562 \u56de\u7b54\u3002 \u3000\u3000\u5c60\u592b\u4eba\u9053\uff1a\u201c\u628a\u62d0\u6756\u7ed9\u6211\u62ff\u6765\uff01\u201d\u5c60\u51e4\u9053\uff1a\u201c\u5988\uff0c\u4f60\u8eab\u4f53\u8981\u7d27\u3002\u72af\u4e0d\u7740\u4e3a\u54e5\u54e5\u751f\u6c14\u3002\u201d \u3000\u3000\u5c60\u592b\u4eba\u91cd\u590d\u9053\uff1a\u201c\u62ff\u6765\uff0c\u62d0\u6756\u62ff\u6765\uff01\u201d\u5c60\u51e4\u9053\uff1a\u201c\u5988\uff0c\u4f60\u8981\u62d0\u6756\u505a\u4ec0\u4e48\uff1f\u6211\u6276\u4f60\u4e0a\u5e8a\u6b47 \u606f\u5427\u3002\u201d\u5c60\u592b\u4eba\u9053\uff1a\u201c\u6211\u627e\u90a3\u5b7d\u755c\u53bb\uff0c\u6211\u975e\u72e0\u72e0\u5730\u6559\u8bad\u4ed6\u4e00\u987f\u4e0d\u53ef\uff01\u201d \u3000\u3000\u5c60\u51e4\u5fc3\u60f3\uff1a\u201c\u4e5f\u53ea\u6709\u628a\u54e5\u54e5\u627e\u6765\uff0c\u8981\u4ed6\u5411\u6bcd\u4eb2\u8d54\u7f6a\uff0c\u624d\u80fd\u6d88\u5f97\u5979\u5fc3\u5934\u4e4b\u6c14\u3002\u201d\u4e8e\u662f\u8bf4 \u9053\uff1a\u201c\u8682\uff0c\u4f60\u5148\u6b47\u6b47\uff0c\u6211\u8fd9\u5c31\u53bb\u628a\u54e5\u54e5\u5524\u6765\u3002\u201d \u3000\u3000\u5c60\u51e4\u628a\u6bcd\u4eb2\u6276\u5165\u5367\u623f\uff0c\u51fa\u6765\u95ee\u5a62\u4ec6\u9053\uff1a\u201c\u4f60\u4eec\u6709\u8c01\u770b\u89c1\u6211\u7684\u54e5\u54e5\u6ca1\u6709\uff1f\u53ef\u77e5\u4ed6\u8eb2\u5728\u54ea \u513f\uff1f\u201d \u3000\u3000\u4e00\u4e2a\u5c0f\u4e2b\u5934\u6084\u6084\u8bf4\u9053\uff1a\u201c\u5c0f\u59d0\uff0c\u521a\u624d\u6211\u4e0d\u6562\u8bf4\uff0c\u73b0\u5728\u662f\u4e0d\u80fd\u4e0d\u8bf4\u4e86\uff0c\u5c11\u7237\u4ed6\u3001\u4ed6\u548c\u90a3\u4f4d \u6df3\u4e8e\u516c\u5b50\u2026\u2026\u201d\u5c60\u51e4\u9053\uff1a\u201c\u600e\u4e48\u6837\uff1f\u201d\u5c0f\u4e2b\u5934\u9053\uff1a\u201c\u4ed6\u4eec\u4e24\u4eba\u5728\u524d\u5c71\u90a3\u5757\u8349\u576a\u4e0e\u9f99\u7237\u548c\u77f3\u7237 \u6bd4\u6b66\u3002\u201d \u3000\u3000\u539f\u6765\u5c60\u9f99\u5728\u95ef\u4e86\u7978\u4e4b\u540e\uff0c\u8d77\u521d\u5fc3\u91cc\u8fd8\u662f\u6709\u70b9\u62c5\u60ca\u5bb3\u6015\uff0c\u6e9c\u51fa\u53bb\u8eb2\u5728\u7a97\u5916\u5077\u770b\uff0c\u540e\u6765\u770b\u89c1 \u6bcd\u4eb2\u9192\u8f6c\uff0c\u77e5\u9053\u5979\u6b7b\u4e0d\u4e86\uff0c\u5fc3\u91cc\u6076\u5ff5\u53c8\u751f\uff0c\u4e00\u4e0d\u505a\u4e8c\u4e0d\u4f11\uff0c\u4e3a\u4e86\u7ed9\u6df3\u4e8e\u8191\u6e05\u9664\u201c\u969c\u788d\u201d\uff0c\u4e0d \u60dc\u4e0e\u5916\u4eba\u8054\u624b\uff0c\u60f3\u4ee5\u201c\u6bd4\u6b66\u201d\u4e3a\u540d\uff0c\u903c\u8d70\u4e24\u4e2a\u5e08\u5f1f\u3002 \u3000\u3000\u5c60\u51e4\u5927\u60ca\u9053\uff1a\u201c\u4ed6\u4eec\u52a8\u624b\u4e86\u6ca1\u6709\uff1f\u201d\u90a3\u5c0f\u4e2b\u5934\u9053\uff1a\u201c\u6211\u6765\u7684\u65f6\u5019\uff0c\u7ecf\u8fc7\u90a3\u513f\uff0c\u770b\u89c1\u5c11\u7237 \u6b63\u5728\u628a\u9f99\u7237\u63a8\u4e0a\u524d\u53bb\u3002\u9f99\u7237\u597d\u50cf\u4e0d\u613f\u6bd4\u6b66\uff0c\u5c11\u7237\u5374\u975e\u903c\u4ed6\u6bd4\u6b66\u4e0d\u53ef\u3002\u5f53\u65f6\u5c1a\u672a\u52a8\u624b\uff0c\u540e\u6765\u600e \u4e48\u6837\u6211\u5c31\u4e0d\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u5c60\u51e4\u65e0\u6687\u7ec6\u95ee\uff0c\u8fde\u5fd9\u8dd1\u51fa\u8349\u576a\uff0c\u53ea\u89c1\u6df3\u4e8e\u8191\u4f7f\u4e00\u5bf9\u864e\u5934\u94a9\uff0c\u5df2\u662f\u548c\u9f99\u521a\u7684\u4e00\u67c4\u957f\u5251\u6253\u5f97 \u5341\u5206\u70bd\u70c8\u3002\u53ef\u662f\u8349\u576a\u4e0a\u4e5f\u53ea\u6709\u4ed6\u4eec\u4e00\u5bf9\u53ae\u6740\uff0c\u5374\u4e0d\u89c1\u5c60\u9f99\u548c\u77f3\u749e\u3002 \u3000\u3000\u864e\u5934\u94a9\u5584\u80fd\u514b\u5236\u5200\u5251\uff0c\u5728\u5175\u5668\u4e0a\u6df3\u4e8e\u8191\u5148\u5360\u4e86\u4fbf\u5b9c\uff0c\u9f99\u521a\u6c89\u7740\u5e94\u4ed8\uff0c\u5140\u662f\u7ed9\u4ed6\u903c\u5f97\u53ea\u6709 \u62db\u67b6\u4e4b\u529f\uff0c\u6beb\u65e0\u8fd8\u624b\u4e4b\u529b\u3002 \u3000\u3000\u6df3\u4e8e\u8191\u80cc\u5411\u5c60\u51e4\uff0c\u4e0d\u77e5\u5c60\u51e4\u5df2\u7ecf\u6765\u5230\u3002\u4ed6\u5360\u4e86\u4e0a\u98ce\uff0c\u5f97\u610f\u6d0b\u6d0b\uff0c\u5927\u8086\u8f7b\u8584\uff0c\u563f\u563f\u5730\u7b11 \u9053\uff1a\u201c\u9f99\u521a\uff0c\u602a\u4e0d\u5f97\u4f60\u7684\u5e08\u5144\u8bf4\u4f60\u662f\u8d56\u86e4\u87c6\u60f3\u5403\u5929\u9e45\u8089\uff0c\u539f\u6765\u4f60\u679c\u7136\u662f\u53ea\u6709\u8fd9\u4e48\u4e00\u70b9\u529f\u592b\uff0c \u4f60\u7684\u5e08\u5144\u672c\u6765\u8981\u6211\u60e9\u7f5a\u4f60\u7684\uff0c\u4f46\u54b1\u4eec\u5c31\u8981\u662f\u4e00\u5bb6\u4eba\u4e86\uff0c\u6211\u4e5f\u4e0d\u80fd\u5c06\u4f60\u96be\u4e3a\uff0c\u53ea\u8981\u4f60\u7ed9\u6211\u78d5\u5934 \u8ba4\u8f93\uff0c\u4ece\u4eca\u4e4b\u540e\uff0c\u4e0d\u8bb8\u4f60\u518d\u4eb2\u8fd1\u5e08\u59b9\uff0c\u4f60\u7b54\u5e94\u4e0b\u6765\uff0c\u6211\u5c31\u9976\u4f60\u3002\u201d \u3000\u3000\u6df3\u4e8e\u8191\u4e0d\u4f4f\u53e3\u5bf9\u9f99\u521a\u51b7\u5632\u70ed\u8bbd\uff0c\u624b\u4e0a\u7684\u653b\u52bf\u4e5f\u662f\u8fde\u7ef5\u4e0d\u65ad\uff0c\u8d8a\u53d1\u51cc\u5389\u3002\u94a9\u5149\u970d\u970d\u4e4b\u4e2d\uff0c \u53ea\u542c\u5f97\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u9f99\u521a\u7684\u8863\u88f3\u7ed9\u4ed6\u53f3\u624b\u7684\u864e\u5934\u94a9\u6495\u53bb\u4e86\u4e00\u5e45\u3002\u9f99\u521a\u8eab\u8eaf\u4e00\u77ee\uff0c\u4e00\u62db\u201c\u4e3e \u706b\u71ce\u5929\u201d\uff0c\u957f\u5251\u5411\u4e0a\u4e00\u62e8\uff0c\u8361\u5f00\u4e86\u6df3\u4e8e\u8191\u5de6\u624b\u7684\u864e\u5934\u94a9\uff0c\u65a5\u9053\uff1a\u201c\u4f60\u6740\u4e86\u6211\u4e0d\u6253\u7d27\uff0c\u6211\u53ef\u4e0d \u8bb8\u4f60\u6c61\u8511\u6211\u7684\u5e08\u59b9\uff01\u201d \u3000\u3000\u6df3\u4e8e\u8191\u54c8\u54c8\u5927\u7b11\uff0c\u201c\u4f60\u4ee5\u4e3a\u6211\u662f\u80e1\u8bf4\u516b\u9053\u4e48\uff1f\u563f\uff0c\u563f\uff0c\u4f60\u7684\u5e08\u59b9\u90fd\u5df2\u7ecf\u6536\u4e86\u6211\u7684\u793c\u7269 \u4e86\uff01\u5979\u7684\u4eb2\u54e5\u54e5\u90fd\u4e3a\u8fd9\u95e8\u4eb2\u4e8b\u9ad8\u5174\uff0c\u4f60\u5374\u5c45\u7136\u6562\u7528\u2018\u6c61\u8511\u2019\u4e8c\u5b57\uff01\u201d \u3000\u3000\u5c60\u51e4\u6309\u637a\u4e0d\u4f4f\uff0c\u4e00\u8dc3\u800c\u4e0a\uff0c\u559d\u9053\uff1a\u201c\u4f4f\u5634\uff01\u201d\u6df3\u4e8e\u8191\u6115\u7136\u56de\u987e\uff0c\u53ea\u89c1\u5c60\u51e4\u5df2\u662f\u674f\u773c\u5706\u7741 \u5730\u7ad9\u5728\u4ed6\u7684\u9762\u524d\u3002 \u3000\u3000\u6df3\u4e8e\u8191\u6ee1\u9762\u901a\u7ea2\uff0c\u53cc\u94a9\u4e00\u526a\uff0c\u5c06\u9f99\u521a\u903c\u9000\uff0c\u563b\u76ae\u7b11\u8138\u5730\u8bf4\u9053\uff1a\u201c\u6211\u8fd9\u662f\u548c\u9f99\u5144\u95f9\u7740\u73a9 \u7684\u3002\u201d \u3000\u3000\u5c60\u51e4\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u677f\u8d77\u8138\u8bf4\u9053\uff1a\u201c\u95f9\u7740\u73a9\u7684\uff1f\u95f9\u7740\u73a9\u7684\u662f\u8fd9\u6837\u6253\u6cd5\u5417\uff1f\u54fc\uff0c\u4f60\u521a\u624d\u8bf4 \u4e86\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6df3\u4e8e\u8191\u5c34\u5c2c\u4e4b\u6781\uff0c\u8d54\u7b11\u8bf4\u9053\uff1a\u201c\u6ca1\u3001\u6ca1\u4ec0\u4e48\u3002\u55ef\uff0c\u5c60\u59d1\u5a18\uff0c\u6211\u6258\u4ee4\u5144\u9001\u7ed9\u4f60\u7684\u660e\u73e0\u548c\u7389 \u7c2a\u4e0d\u77e5\u53ef\u5408\u4f60\u7684\u5fc3\u610f\uff1f\u201d\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u96be\u9053\u5c60\u9f99\u8fd8\u6ca1\u6709\u548c\u5979\u8bf4\u597d\uff0c\u600e\u7684\u5979\u5982\u6b64\u6cfc\u8fa3\uff0c\u4e00\u70b9\u4e0d \u987e\u989c\u9762\uff0c\u7adf\u7136\u660e\u5200\u4eae\u6240\u5730\u8fd9\u6837\u95ee\u6211\uff1f\u201d\u4ed6\u54ea\u91cc\u77e5\u9053\uff0c\u5c60\u51e4\u53ef\u5e76\u4e0d\u662f\u201c\u5a34\u9759\u201d\u754f\u7f9e\u7684\u5c0f\u59d0\uff0c\u800c \u662f\u4e00\u4e2a\u6027\u60c5\u521a\u70c8\uff0c\u9976\u6709\u7236\u98ce\u7684\u5dfe\u5e3c\u82f1\u96c4\uff0c\u201c\u6cfc\u8fa3\u201d\u7684\u8fd8\u5728\u540e\u5934\u5462\u3002 \u3000\u3000\u6df3\u4e8e\u8191\u8bdd\u72b9\u672a\u4e86\uff0c\u53ea\u89c1\u5c60\u51e4\u628a\u624b\u4e00\u573a\uff0c\u90a3\u4e32\u660e\u73e0\u5df2\u662f\u5288\u9762\u63b7\u6765\u3002\u6df3\u4e8e\u8191\u60ca\u9053\uff1a\u201c\u5c60\u59d1 \u5a18\uff0c\u4f60\u2014\u2014\u201d\u521a\u8bf4\u5f97\u4e00\u4e2a\u201c\u4f60\u201d\u5b57\uff0c\u90a3\u5bf9\u7389\u7c2a\u4e5f\u4f3c\u7bad\u4e00\u822c\u7684\u5c04\u8fc7\u6765\u4e86\uff01 \u3000\u3000\u8fd9\u4e32\u660e\u73e0\u548c\u8fd9\u5bf9\u7389\u7c2a\u4e43\u662f\u6df3\u4e8e\u8191\u8d39\u4e86\u8bb8\u591a\u5fc3\u8840\u624d\u80fd\u5230\u624b\u7684\u5b9d\u7269\uff0c\u5982\u4eca\u7ed9\u5c60\u51e4\u5f53\u4f5c\u5783\u573e\u4e00 \u822c\u7684\u629b\u63b7\uff0c\u4ee4\u4ed6\u53c8\u662f\u5403\u60ca\uff0c\u53c8\u662f\u5fc3\u75db\uff01 \u3000\u3000\u5403\u60ca\u3001\u5fc3\u75db\u4e5f\u8fd8\u7f62\u4e86\uff0c\u6df3\u4e8e\u8191\u8fd8\u5f97\u63d0\u9632\u7ed9\u5979\u6253\u4f24\u3002\u539f\u6765\u5c60\u51e4\u662f\u7528\u201c\u5929\u5973\u6563\u82b1\u201d\u7684\u6253\u201c\u6697 \u5668\u201d\u624b\u6cd5\uff0c\u628a\u4e32\u73e0\u7684\u7ebf\u626f\u65ad\u4e86\uff0c\u8fd9\u4e32\u73e0\u5171\u662f\u4e09\u5341\u516d\u9897\u53c8\u5706\u53c8\u5927\u7684\u660e\u73e0\uff0c\u53d8\u4f5c\u4e86\u4e09\u5341\u516d\u9897\u6253\u7a74 \u7684\u6697\u5668\uff0c\u6bcf\u4e00\u9897\u660e\u73e0\u90fd\u662f\u6253\u5411\u4ed6\u7684\u7a74\u9053\u3002 \u3000\u3000\u6df3\u4e8e\u8191\u4e00\u9762\u95ed\u4e86\u7a74\u9053\uff0c\u4e00\u9762\u817e\u51fa\u4e00\u53ea\u624b\u6765\uff0c\u65bd\u5c55\u63a5\u6697\u5668\u7684\u624b\u6cd5\uff0c\u5e0c\u671b\u591a\u6536\u56de\u51e0\u9897\u3002\u6b63\u5728 \u624b\u5fd9\u811a\u4e71\uff0c\u7389\u7c2a\u53c8\u5df2\u5c04\u5230\uff0c\u8fd9\u5bf9\u7389\u7c2a\u662f\u5c60\u51e4\u5f53\u4f5c\u8896\u7bad\u5c04\u51fa\u7684\uff0c\u52b2\u529b\u66f4\u5927\u3002\u6df3\u4e8e\u8191\u65e0\u53ef\u5948\u4f55\uff0c \u53ea\u597d\u7528\u864e\u5934\u94a9\u906e\u62e6\uff0c\u201c\u94db\u201d\u7684\u4e00\u58f0\u54cd\uff0c\u90a3\u5bf9\u7389\u7c2a\u78b0\u7740\u4e86\u4ed6\u7684\u7cbe\u94a2\u6240\u94f8\u7684\u864e\u5934\u94a9\uff0c\u65ad\u4e3a\u56db\u6bb5\u3002 \u6df3\u4e8e\u8191\u5fd9\u4e8e\u906e\u62e6\uff0c\u8eab\u4e0a\u6709\u4e09\u5904\u7a74\u9053\u7ed9\u660e\u73e0\u6253\u7740\uff0c\u867d\u7136\u662f\u95ed\u4e86\u7a74\u9053\uff0c\u4e5f\u662f\u75db\u5f97\u96be\u53d7\uff01 \u3000\u3000\u5c60\u51e4\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u8bdd\u6211\u90fd\u542c\u89c1\u4e86\uff0c\u6211\u8bf4\u4f60\u624d\u662f\u765e\u86e4\u87c6\u60f3\u5403\u5929\u9e45\u8089\uff01\u6211\u90a3\u4e0d\u6210\u6750\u7684\u54e5 \u54e5\u6536\u4e86\u4f60\u7684\u4e1c\u897f\uff0c\u73b0\u5728\u6211\u90fd\u8fd8\u7ed9\u4f60\u4e86\uff0c\u4f60\u7ed9\u6211\u6eda\uff01\u6eda\uff01\u201d \u3000\u3000\u6df3\u4e8e\u8191\u5e73\u7d20\u98ce\u6d41\u81ea\u8d1f\uff0c\u51e0\u66fe\u53d7\u8fc7\u5982\u6b64\u96be\u582a\uff0c\u4e00\u65f6\u95f4\u7adf\u4e0d\u77e5\u8bf4\u4e9b\u4ec0\u4e48\u8bdd\u597d\uff0c\u9762\u4e0a\u4e00\u9635\u9752\u4e00 \u9635\u7ea2\uff0c\u6068\u4e0d\u5f97\u6709\u4e2a\u5730\u6d1e\u94bb\u4e0b\u53bb\u3002 \u3000\u3000\u5c60\u51e4\u559d\u9053\uff0c\u201c\u4f60\u8d70\u4e0d\u8d70\uff1f\u201d\u6df3\u4e8e\u8191\u607c\u7f9e\u6210\u6012\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u6211\u662f\u4f60\u54e5\u54e5\u8bf7\u6765\u7684\uff0c\u6211\u504f\u4e0d \u8d70\uff0c\u4f60\u600e\u4e48\u6837\uff1f\u201d\u5c60\u51e4\u9053\uff1a\u201c\u6211\u54e5\u54e5\u8ba4\u5f97\u4f60\uff0c\u6211\u8ba4\u4e0d\u5f97\u4f60\u3002\u6211\u8ba4\u5f97\u4f60\uff0c\u6211\u7684\u5251\u8ba4\u4e0d\u5f97\u4f60\uff01\u4f60 \u6709\u672c\u9886\u5c31\u8d56\u5728\u8fd9\u513f\u5427\uff0c\u770b\u5251\uff01\u201d \u3000\u3000\u6df3\u4e8e\u8191\u6c14\u5f97\u53cc\u773c\u55b7\u706b\uff0c\u5934\u9762\u9752\u7b4b\u66b4\u9732\uff0c\u5927\u6012\u9053\uff1a\u201c\u81ed\u4e2b\u5934\uff0c\u4e0d\u8bc6\u62ac\u4e3e\uff01\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u5c60 \u51e4\u5df2\u662f\u5530\u5730\u4e00\u5251\u523a\u5230\u4ed6\u7684\u9762\u95e8\uff0c\u6df3\u4e8e\u8191\u970d\u5730\u4e00\u4e2a\u201c\u51e4\u70b9\u5934\u201d\uff0c\u8fd8\u4e86\u4e00\u62db\u201c\u817e\u86df\u8d77\u51e4\u201d\uff0c\u53cc\u94a9 \u76d8\u65cb\u98de\u821e\uff0c\u53cd\u9501\u5c60\u51e4\u7684\u5251\u950b\uff0c\u94a9\u5c16\u53c8\u523a\u5411\u5979\u80c1\u4e0b\u7684\u201c\u6108\u6c14\u7a74\u201d\u3002 \u3000\u3000\u9f99\u521a\u66fe\u7ecf\u5728\u8fd9\u4e00\u62db\u5403\u8fc7\u4e8f\uff0c\u53eb\u9053\uff1a\u201c\u5e08\u59b9\uff0c\u5c0f\u5fc3\uff01\u201d\u5c60\u51e4\u7b11\u9053\uff1a\u201c\u4f60\u653e\u5fc3\uff0c\u4ed6\u8fd9\u70b9\u73a9\u827a \u5413\u4e0d\u5012\u6211\uff01\u201d\u9752\u94a2\u5251\u75be\u523a\u8fc7\u53bb\uff0c\u4f7f\u51fa\u4e86\u4e00\u62db\u201c\u5927\u6f20\u5b64\u70df\u201d\uff0c\u5176\u76f4\u5982\u77e2\uff0c\u6df3\u4e8e\u8191\u7684\u53cc\u94a9\u5c1a\u672a\u9501 \u7740\u5979\u7684\u5251\u950b\uff0c\u5979\u7684\u5251\u950b\u5df2\u7ecf\u6307\u5230\u6df3\u4e8e\u8191\u7684\u80f8\u53e3\u3002\u6df3\u4e8e\u8191\u62db\u6570\u4f7f\u8001\uff0c\u6025\u5207\u95f4\u96be\u4ee5\u6492\u56de\u53cc\u94a9\u62db \u67b6\uff0c\u53ea\u597d\u6025\u6538\u540e\u9000\u3002 \u3000\u3000\u539f\u6765\u5c60\u51e4\u4e5f\u662f\u4e0d\u8bc6\u5982\u4f55\u7834\u89e3\u6df3\u4e8e\u8191\u8fd9\u62db\u201c\u817e\u86df\u8d77\u51e4\u201d\u7684\uff0c\u4f46\u5979\u806a\u660e\u7edd\u9876\uff0c\u9f99\u521a\u5728\u8fd9\u4e00\u62db \u4e0a\u5403\u4e86\u4e8f\uff0c\u5979\u770b\u5728\u773c\u4e2d\uff0c\u80f8\u4e2d\u5df2\u6709\u6210\u7af9\u3002\u4e8e\u662f\u5728\u4ea4\u624b\u4e4b\u65f6\uff0c\u4fbf\u91c7\u53d6\u4ee5\u5feb\u6253\u6162\u7684\u65b9\u6cd5\uff0c\u5236\u654c\u673a \u5148\uff0c\u4e0d\u6c42\u7834\u89e3\u654c\u62db\uff0c\u5374\u81ea\u7136\u5c31\u7834\u89e3\u4e86\u654c\u62db\u3002 \u3000\u3000\u6df3\u4e8e\u8191\u7684\u672c\u9886\u672c\u6765\u9ad8\u51fa\u5c60\u51e4\u8bb8\u591a\uff0c\u4f46\u4e00\u6765\u56e0\u4e3a\u7ed9\u5c60\u51e4\u5148\u7528\u73cd\u73e0\u6253\u7740\u4ed6\u7684\u7a74\u9053\uff0c\u529f\u529b\u4e1a\u5df2 \u51cf\u4e86\u51e0\u5206\uff1b\u4e8c\u6765\u4ed6\u53c8\u6b63\u5728\u7ed9\u5c60\u51e4\u6c14\u5f97\u4e03\u7a8d\u751f\u70df\uff0c\u9ad8\u624b\u6bd4\u6597\u5c82\u5bb9\u5fc3\u6d6e\u6c14\u8e81\uff1f\u4e09\u6765\u5c60\u51e4\u521a\u624d\u51b7\u773c \u65c1\u89c2\uff0c\u5927\u81f4\u5df2\u6478\u5230\u4ed6\u7684\u5bb6\u6570\uff0c\u6536\u5230\u4e86\u77e5\u5df1\u77e5\u5f7c\u7684\u529f\u6548\u3002\u6df3\u4e8e\u8191\u4e00\u51fa\u62db\u5c31\u53d7\u5979\u7684\u63a3\u5236\u3002\u6709\u8fd9\u4e09 \u4e2a\u539f\u56e0\uff0c\u6df3\u4e8e\u8191\u81ea\u662f\u96be\u9003\u4e00\u8d25\u3002\u4e0d\u8fc7\u5341\u4f59\u62db\uff0c\u53ea\u542c\u5f97\u5c60\u51e4\u559d\u9053\uff1a\u201c\u7740\uff01\u201d\u5251\u5149\u8fc7\u53bb\uff0c\u6df3\u4e8e\u8191 \u8863\u88f3\u67d3\u8840\uff0c\u4e00\u7247\u6bb7\u7ea2\uff0c\u80a9\u4e0a\u5df2\u662f\u7ed9\u5212\u5f00\u4e86\u4e09\u5bf8\u591a\u957f\u7684\u4f24\u53e3\u3002 \u3000\u3000\u6df3\u4e8e\u8191\u4e00\u4e2a\u5012\u7eb5\uff0c\u8df3\u51fa\u6570\u4e08\u5f00\u5916\uff0c\u66b4\u6012\u5982\u96f7\u5730\u559d\u9053\uff1a\u201c\u597d\u5440\uff0c\u6211\u6df3\u4e8e\u8191\u4eca\u751f\u4e0d\u628a\u4f60\u8fd9\u81ed \u4e2b\u5934\u5f04\u5230\u624b\uff0c\u8a93\u4e0d\u4e3a\u4eba\uff01\u201d\u53e3\u4e2d\u5728\u9a82\u3001\u811a\u5e95\u5374\u5df2\u62b9\u4e86\u6cb9\u98de\u8dd1\u3002 \u3000\u3000\u5c60\u51e4\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u672c\u6765\u5c31\u4e0d\u662f\u4eba\uff01\u201d\u6c14\u6012\u4ea4\u52a0\uff0c\u8fd8\u60f3\u8ffd\u4e0b\u53bb\u518d\u7ed9\u4ed6\u4e00\u5251\uff0c\u9f99\u521a\u8bf4\u9053\uff1a \u201c\u5e08\u59b9\uff0c\u4f55\u5fc5\u548c\u8fd9\u6837\u7684\u9f8c\u9f8a\u5c0f\u4eba\u751f\u6c14\uff0c\u7531\u4ed6\u53bb\u5427\u3002\u201d \u3000\u3000\u5c60\u51e4\u970d\u7136\u4e00\u7701\uff0c\u63d2\u5251\u4eba\u9798\uff0c\u8bf4\u9053\uff1a\u201c\u77f3\u5e08\u54e5\u5462\uff1f\u201d\u9f99\u521a\u9053\uff1a\u201c\u8ddf\u5927\u5e08\u54e5\u8d70\u4e86\u3002\u201d\u5c60\u51e4\u5403 \u60ca\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u4ed6\u4e0d\u662f\u548c\u4f60\u5728\u4e00\u8d77\u7684\u5417\uff0c\u600e\u7684\u4f1a\u8ddf\u54e5\u54e5\u8d70\u4e86\uff1f\u201d \u3000\u3000\u9f99\u521a\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u77e5\u9053\uff0c\u6211\u7ed9\u8fd9\u53ae\u903c\u6211\u6bd4\u6b66\uff0c\u5374\u4e0d\u77e5\u5927\u5e08\u54e5\u548c\u4ed6\u8bf4\u4e86\u4e9b\u4ec0\u4e48\u8bdd\uff0c\u4ed6\u4eec\u4e24 \u4eba\u5c31\u5411\u540e\u5c71\u8d70\u4e86\u3002\u201d\u5c60\u51e4\u5fc3\u4e2d\u60f4\u60f4\u4e0d\u5b89\uff0c\u8fde\u5fd9\u8bf4\u9053\uff0c\u201c\u54b1\u4eec\u5feb\u5230\u540e\u5c71\u770b\u53bb\u3002\u201d \u3000\u3000\u5c60\u51e4\u62c5\u5fe7\u7684\u662f\uff1a\u5979\u7684\u54e5\u54e5\u5fc3\u72e0\u624b\u8fa3\uff0c\u4ece\u4eca\u65e5\u4e4b\u4e8b\u770b\u6765\uff0c\u4ed6\u5df2\u662f\u53ea\u56fe\u5df4\u7ed3\u5916\u4eba\uff0c\u4e1d\u6beb\u4e0d\u987e \u540c\u95e8\u7684\u60c5\u4e49\u4e86\u3002\u4ed6\u628a\u77f3\u749e\u62c9\u5f00\uff0c\u4e0d\u95ee\u53ef\u77e5\uff0c\u5b9a\u662f\u4e0d\u6000\u597d\u610f\u3002\u800c\u77f3\u749e\u7684\u6027\u60c5\u53c8\u662f\u76f8\u5f53\u5014\u5f3a\u7684\uff0c \u5c60\u51e4\u53ea\u6015\u4ed6\u4eec\u4e24\u4eba\u4e00\u8a00\u4e0d\u5408\uff0c\u5979\u7684\u54e5\u54e5\u4f1a\u4e0b\u6bd2\u624b\u3002 \u3000\u3000\u5c60\u51e4\u98de\u5feb\u7684\u5411\u540e\u5c71\u8dd1\u53bb\uff0c\u4e00\u9762\u8dd1\u4e00\u9762\u53eb\uff1a\u201c\u4e09\u5e05\u54e5\uff0c\u4e09\u5e08\u54e5\uff01\u201d\u5fe7\u6025\u4e4b\u60c5\uff0c\u8868\u9732\u65e0\u9057\uff01 \u9f99\u521a\u5f53\u7136\u4e5f\u662f\u4e3a\u77f3\u749e\u62c5\u5fe7\u7684\uff0c\u53ef\u662f\u5c60\u51e4\u60ca\u60f6\u5730\u53eb\u558a\uff0c\u5374\u4e5f\u62d4\u52a8\u4e86\u4ed6\u7684\u5fc3\u5f26\uff0c\u4ee4\u4ed6\u832b\u7136\u82e5\u5931\uff0c \u968f\u5373\u604d\u7136\u5927\u609f\uff1a\u201c\u5c0f\u5e08\u59b9\u559c\u6b22\u7684\u662f\u77f3\u5e08\u5f1f\u3002\u5509\uff0c\u5176\u5b9e\u6211\u4e5f\u5e94\u8be5\u65e9\u5c31\u660e\u767d\u7684\u4e86\u3002\u201d \u3000\u3000\u8dd1\u5230\u540e\u5c71\uff0c\u53ea\u89c1\u77f3\u749e\u5df2\u5728\u5411\u4ed6\u4eec\u8d70\u6765\uff0c\u4e00\u5f20\u672c\u6765\u662f\u82f1\u6c14\u52c3\u52c3\u7684\u9762\u5e9e\u597d\u50cf\u88ab\u62b9\u4e86\u7070\u4f3c\u7684\uff0c \u53d8\u5f97\u5341\u5206\u9893\u4e27\u3002\u5c60\u51e4\u5403\u4e86\u4e00\u60ca\uff0c\u8fde\u5fd9\u95ee\u9053\uff1a\u201c\u77f3\u5e08\u54e5\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u53ef\u662f\u53d7\u3001\u53d7\u4e86\u4f24\u4e86\uff1f\u201d \u3000\u3000\u77f3\u749e\u7b11\u4e86\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\uff0c\u4f60\u77a7\u6211\u4e0d\u662f\u597d\u597d\u7684\u5417\uff1f\u597d\u5728\u4e8c\u5e08\u54e5\u4e0d\u662f\u5916\u4eba\uff0c\u8981\u4e0d\u7136 \u4f60\u8fd9\u6837\u5927\u60ca\u5c0f\u602a\uff0c\u5c82\u4e0d\u6559\u4eba\u7b11\u8bdd\uff1f\u201d\u8bf4\u7f62\u8fd8\u6709\u610f\u5730\u4f38\u4e86\u4f38\u62f3\uff0c\u8e22\u4e86\u8e22\u817f\uff0c\u8bc1\u660e\u81ea\u5df1\u5e76\u6ca1\u6709\u53d7 \u4f24\u3002\u5c60\u51e4\u8fd9\u624d\u653e\u4e0b\u4e86\u5fc3\u4e0a\u7684\u4e00\u5757\u77f3\u5934\u3002\u4f46\u5c60\u51e4\u4e5f\u89c9\u5bdf\u5f97\u5230\uff0c\u77f3\u749e\u9762\u4e0a\u7684\u7b11\u5bb9\uff0c\u5b9e\u5728\u662f\u7b11\u5f97\u5341 \u5206\u52c9\u5f3a\u3002 \u3000\u3000\u5c60\u51e4\u55d4\u9053\uff0c\u201c\u4eba\u5bb6\u5173\u5fc3\u4f60\u5012\u662f\u5173\u5fc3\u9519\u4e86\uff1f\u597d\u5566\uff0c\u4ee5\u540e\u6211\u4e5f\u4e0d\u6562\u518d\u7406\u4f60\u5566\u3002\u201d\u77f3\u749e\u53f9\u4e86\u53e3 \u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u672c\u6765\u4f60\u5c31\u4e0d\u8be5\u7406\u6211\u7684\u3002\u201d\u5c60\u51e4\u6014\u4e86\u4e00\u6014\uff0c\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u6211\u7684\u54e5\u54e5\u5462\uff1f\u4ed6\u548c\u4f60 \u8bf4\u4e86\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u77f3\u749e\u9053\uff1a\u201c\u5927\u5e08\u54e5\u548c\u6df3\u4e8e\u8191\u8fd9\u53ae\u4e0b\u5c71\u53bb\u4e86\uff0c\u4ed6\u5df2\u7ecf\u77e5\u9053\u4e86\u6df3\u4e8e\u8191\u53d7\u4e86\u4f60\u7684\u4f24\u3002\u201d\u5c60\u51e4\u6068 \u6068\u8bf4\u9053\uff1a\u201c\u54e5\u54e5\u771f\u662f\u4e0d\u8be5\uff0c\u5988\u51e0\u4e4e\u7ed9\u4ed6\u6c14\u6b7b\u4e86\uff0c\u4ed6\u4e5f\u4e0d\u56de\u53bb\u8d54\u7f6a\uff0c\u4e5f\u4e0d\u77e5\u4ed6\u7740\u4e86\u6df3\u4e8e\u8191\u7684\u4ec0 \u4e48\u8ff7\uff0c\u4ea4\u4e0a\u4e86\u8fd9\u6837\u4e00\u4e2a\u4e0b\u4e09\u6d41\u7684\u670b\u53cb\uff0c\u8fde\u5988\u5988\u90fd\u4e0d\u8981\u4e86\u3002\u4f46\u4ed6\u7a76\u7adf\u548c\u4f60\u8bf4\u4e86\u4e9b\u4ec0\u4e48\uff0c\u4f60\u8fd8\u672a \u66fe\u544a\u8bc9\u6211\u5462\u3002\u201d \u3000\u3000\u77f3\u749e\u8bb7\u8bb7\u8bf4\u9053\uff1a\u201c\u6ca1\u8bf4\u4ec0\u4e48\u3002\u201d\u5c60\u51e4\u9053\uff1a\u201c\u6211\u4e0d\u76f8\u4fe1\u3002\u4f60\u4eec\u53bb\u4e86\u8fd9\u8bb8\u591a\u65f6\u5019\uff0c\u8bf4\u7684\u8bdd\u8fd8 \u4f1a\u5c11\u4e48\uff1f\u201d\u77f3\u749e\u82e6\u7b11\u9053\uff1a\u201c\u5e08\u59b9\uff0c\u4f60\u4e0d\u8981\u95ee\u4e86\u3002\u4f60\u54e5\u54e5\u4f1a\u8bf4\u4e9b\u4ec0\u4e48\u8bdd\uff0c\u4f60\u731c\u4e5f\u5e94\u8be5\u731c\u5f97\u5230 \u7684\u3002\u201d \u3000\u3000\u5c60\u51e4\u5fc3\u4e2d\u4e00\u52a8\uff0c\u4e0d\u7531\u5f97\u674f\u8138\u6cdb\u7ea2\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u54e5\u54e5\u4e00\u5b9a\u662f\u76d8\u95ee\u4ed6\u548c\u6211\u7684\u79c1\u60c5\u4e86\u3002\u5374\u4e0d \u77e5\u8fd9\u50bb\u5c0f\u5b50\u5982\u4f55\u56de\u7b54\uff1f\u201d\u5c60\u51e4\u788d\u7740\u9f99\u521a\u5728\u65c1\uff0c\u4e0d\u597d\u610f\u601d\u518d\u95ee\u4e0b\u53bb\u3002 \u3000\u3000\u5c60\u592b\u4eba\u5f97\u77e5\u513f\u5b50\u5df2\u7ecf\u548c\u6df3\u4e8e\u8191\u4e0b\u5c71\u7684\u6d88\u606f\uff0c\u5c11\u4e0d\u514d\u53c8\u751f\u4e86\u4e00\u573a\u5927\u6c14\uff0c\u5f53\u771f\u5c31\u75c5\u8d77\u6765\u4e86\u3002 \u5c60\u51e4\u6574\u665a\u670d\u4f8d\u6bcd\u4eb2\uff0c\u987e\u4e0d\u5f97\u79c1\u4e0b\u627e\u77f3\u749e\u8bf4\u8bdd\u3002\u5979\u672c\u6765\u51c6\u5907\u7b2c\u4e8c\u5929\u53bb\u627e\u77f3\u749e\u7684\uff0c\u4e0d\u6599\u7b2c\u4e8c\u5929\u5df2 \u662f\u627e\u4e0d\u7740\u77f3\u749e\u4e86\u3002\u77f3\u749e\u5bf9\u8c01\u4e5f\u6ca1\u6709\u8bf4\uff0c\u4e5f\u6ca1\u6709\u7559\u4e0b\u7247\u7eb8\u53ea\u5b57\uff0c\u7adf\u7136\u5c31\u8fd9\u6837\u5730\u6084\u6084\u8d70\u4e86\u3002\u76f4\u5230 \u4eca\u5929\uff0c\u5979\u624d\u4ece\u9f99\u521a\u7684\u53e3\u4e2d\uff0c\u542c\u4e86\u77f3\u749e\u7684\u6d88\u606f\u3002\u6b63\u662f\uff1a \u3000\u3000\u820d\u5df1\u4e3a\u4eba\u60c5\u4e49\u91cd\uff0c\u9e33\u9e2f\u4e24\u5730\u4f1a\u4f55\u65f6\uff1f \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b\u2014\u2014\u7b2c\u5341\u4e8c\u56de\u3000\u6545\u56fd\u8def\u9065\u5f52\u68a6\u6e3a\u3000\u5929\u6daf\u4eba\u9694\u5ba2\u9b42\u6d88 \u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b \u7b2c\u5341\u4e8c\u56de\u3000\u6545\u56fd\u8def\u9065\u5f52\u68a6\u6e3a\u3000\u5929\u6daf\u4eba\u9694\u5ba2\u9b42\u6d88 \u3000\u3000\u5f80\u4e8b\u4e00\u5e55\u5e55\u7684\u4ece\u5fc3\u5934\u7ffb\u8fc7\uff0c\u5c60\u51e4\u624b\u6309\u6302\u5728\u9a6c\u9791\u4e0a\u7684\u9aa8\u7070\u888b\uff0c\u773c\u6cea\u76c8\u7736\uff0c\u51c4\u6006\u6b32\u7edd\u3002\u5979\u5728 \u706b\u5316\u9f99\u521a\u7684\u65f6\u5019\u6ca1\u6709\u6389\u4e0b\u4e00\u6ef4\u773c\u6cea\uff0c\u5982\u4eca\u5374\u662f\u4e0d\u7531\u5f97\u54ed\u51fa\u6765\u4e86\u3002\u5979\u77e5\u9053\u9f99\u521a\u662f\u4e3a\u4e86\u6210\u5168\u5979\u548c \u77f3\u749e\uff0c\u662f\u4ee5\u7518\u613f\u53d7\u5979\u54e5\u54e5\u4e00\u638c\u800c\u4e0d\u52a0\u8fa9\u767d\u7684\u3002 \u3000\u3000\u5c60\u51e4\u53ef\u4ee5\u60f3\u8c61\u5f97\u5230\uff0c\u9f99\u521a\u5728\u53d7\u5979\u54e5\u54e5\u6bd2\u638c\u4e4b\u65f6\uff0c\u5fc3\u4e2d\u662f\u5982\u4f55\u7684\u60b2\u82e6\u3002\u4ed6\u660e\u660e\u77e5\u9053\u5979\u548c\u77f3 \u749e\u76f8\u7231\uff0c\u4f46\u4e3a\u4e86\u6d17\u8131\u77f3\u749e\u7684\u201c\u5acc\u7591\u201d\uff0c\u5728\u4ed6\u53d7\u5230\u54e5\u54e5\u62f7\u95ee\u4e4b\u65f6\uff0c\u5374\u4e0d\u5f97\u4e0d\u62c5\u4e86\u865a\u540d\uff0c\u76f4\u8ba4\u4e0d \u8bb3\uff0c\u5e0c\u671b\u9a97\u8fc7\u54e5\u54e5\uff0c\u597d\u53eb\u4ed6\u653e\u677e\u77f3\u749e\u3002\u201c\u4e8c\u5e08\u54e5\u5440\u4e8c\u5e08\u54e5\uff0c\u4f60\u771f\u662f\u7528\u5fc3\u826f\u82e6\uff01\u4f46\u4f60\u8fd9\u4efd\u6df1 \u60c5\uff0c\u6211\u4eca\u751f\u5374\u662f\u4e0d\u80fd\u62a5\u7b54\u7684\u4e86\uff01\u201d \u3000\u3000\u5c60\u51e4\u54ed\u4e86\u4e00\u4f1a\uff0c\u53c8\u62ff\u51fa\u77f3\u749e\u90a3\u5c01\u4fe1\u518d\u770b\u4e00\u904d\u3002\u8747\u5934\u5c0f\u6977\u5bc6\u5bc6\u9ebb\u9ebb\u5730\u5199\u4e86\u4e00\u5927\u5f20\u3002\u7b2c\u4e00\u6bb5 \u8bf4\u4ed6\u4e0d\u613f\u5c60\u51e4\u5144\u59b9\u5931\u548c\uff0c\u662f\u4ee5\u90a3\u65e5\u53d7\u4e86\u5979\u7684\u54e5\u54e5\u8d23\u5907\u4e4b\u540e\uff0c\u5b81\u53ef\u6084\u6084\u79bb\u5f00\u3002\u7b2c\u4e8c\u6bb5\u8bf4\u4ed6\u77e5 \u9053\uff0c\u4e8c\u5e08\u54e5\u82e6\u604b\u5e08\u59b9\uff0c\u5373\u4f7f\u5c60\u9f99\u4e0d\u8d23\u5907\u4ed6\uff0c\u4ed6\u4e5f\u65e9\u6709\u9000\u8ba9\u4e4b\u610f\u3002\u7b2c\u4e09\u6bb5\u5ffd\u5730\u7b14\u950b\u4e00\u8f6c\uff0c\u8fd9\u624d \u8bf4\u5230\u4ed6\u9047\u89c1\u4e86\u9f99\u521a\u4e4b\u4e8b\uff0c\u4ed6\u8bf4\u542c\u4e86\u9f99\u521a\u51fa\u81ea\u80ba\u8151\u4e4b\u8a00\uff0c\u8fd9\u624d\u77e5\u9053\u5e08\u59b9\u771f\u7231\u7684\u662f\u4ed6\uff0c\u540c\u65f6\u53c8\u8bf4 \u9f99\u521a\u5df2\u7ecf\u544a\u8bc9\u4ed6\u5e08\u7236\u7684\u6b7b\u8baf\uff0c\u6740\u5e08\u7684\u4ec7\u4eba\u662f\u9633\u5929\u96f7\u4ed6\u4e5f\u77e5\u9053\u4e86\uff0c\u4ed6\u8bf4\u4ed6\u4e00\u5b9a\u4f1a\u56de\u6765\u5e2e\u5c60\u51e4\u62a5 \u4ec7\u3002\u4fe1\u4e2d\u867d\u7136\u6ca1\u6709\u8868\u660e\u662f\u5426\u6539\u53d8\u521d\u8877\uff0c\u4e0e\u5c60\u51e4\u91cd\u4fee\u65e7\u597d\uff0c\u4f46\u4ed6\u65e2\u7136\u613f\u610f\u56de\u6765\uff0c\u90a3\u4e5f\u53ef\u4ee5\u4e0d\u8a00 \u800c\u55bb\u4e86\u3002 \u3000\u3000\u5c60\u51e4\u8bfb\u4e86\u4e00\u904d\u53c8\u4e00\u904d\uff0c\u773c\u6cea\u4e0d\u7981\u53c8\u6dcc\u51fa\u6765\uff0c\u60f3\u9053\uff1a\u201c\u77f3\u749e\uff0c\u4f60\u771f\u662f\u4e2a\u50bb\u5c0f\u5b50\uff0c\u4e8c\u4eba\u76f8 \u60a6\uff0c\u5c82\u662f\u7b2c\u4e09\u8005\u53ef\u4ee5\u66ff\u5f97\u4e86\u7684\uff1f\u6211\u90a3\u72e0\u6bd2\u7684\u54e5\u54e5\u4e5f\u4e0d\u77e5\u548c\u4ed6\u8bf4\u4e86\u4e9b\u4ec0\u4e48\u5e26\u523a\u7684\u8bdd\uff0c\u4f24\u4e86\u4ed6\u7684 \u5fc3\uff1f\u8981\u4e0d\u7136\u4ee5\u4ed6\u5014\u5f3a\u7684\u6027\u683c\uff0c\u662f\u7edd\u4e0d\u4f1a\u5bf9\u6211\u54e5\u54e5\u5c48\u670d\u7684\u3002\u201d \u3000\u3000\u60f3\u5230\u5979\u7684\u54e5\u54e5\uff0c\u5c60\u51e4\u53c8\u6c14\u53c8\u6068\uff0c\u54e5\u54e5\u903c\u8d70\u4e86\u5979\u7684\u5fc3\u4e0a\u4eba\uff0c\u8fd9\u8fd8\u4e0d\u7b97\uff0c\u5982\u4eca\u53c8\u6740\u4e86\u5979\u4e00\u5411 \u5f53\u4f5c\u5144\u957f\u7684\u9f99\u521a\u3002\u201c\u4e8c\u5e08\u54e5\uff0c\u4f60\u867d\u7136\u4e0d\u8981\u6211\u62a5\u4ec7\uff0c\u4f46\u6211\u5374\u662f\u4e00\u5b9a\u4e0d\u80fd\u518d\u8ba4\u4ed6\u4f5c\u54e5\u54e5\u7684\u4e86\u3002\u201d \u5c60\u51e4\u5fc3\u60f3\u3002 \u3000\u3000\u4e0d\u77e5\u4e0d\u89c9\u5df2\u662f\u65e5\u5f71\u897f\u659c\uff0c\u5c60\u51e4\u56de\u5230\u4ed6\u4eec\u4e34\u65f6\u624e\u8425\u7684\u5730\u65b9\u6765\u4e86\u3002 \u3000\u3000\u90a3\u65e5\u5b5f\u5c11\u521a\u7236\u5973\u5230\u4ed6\u4eec\u7684\u5c71\u5be8\u62a5\u8baf\uff0c\u9f99\u521a\u7559\u5b88\u5728\u5bb6\uff0c\u5c60\u51e4\u51fa\u53bb\u66ff\u6bcd\u4eb2\u5ef6\u533b\u672a\u56de\uff0c\u9f99\u521a\u548c \u4e00\u4e2a\u5e08\u4f2f\u3001\u4e24\u4e2a\u5934\u9886\u5148\u884c\u51fa\u53d1\uff0c\u6253\u63a2\u4ec7\u4eba\u3002\u7b2c\u4e8c\u65e5\u5c60\u51e4\u56de\u6765\uff0c\u7ed9\u6bcd\u4eb2\u5403\u4e86\u836f\uff0c\u53c8\u5f85\u5979\u6bcd\u4eb2\u7684 \u75c5\u51b5\u7a0d\u6709\u597d\u8f6c\u4e4b\u540e\uff0c\u8fd9\u624d\u5927\u4e3e\u7387\u9886\u5404\u5730\u8d76\u56de\u5c71\u5be8\u7684\u5934\u9886\u524d\u5f80\u8499\u53e4\u5bfb\u4ec7\uff0c\u4e00\u8def\u4e0a\u4ed6\u4eec\u8ffd\u8e2a\u9f99\u521a \u6240\u7559\u7684\u6807\u8bb0\uff0c\u5230\u4e86\u897f\u590f\uff0c\u53e6\u5916\u6d3e\u4e00\u652f\u4eba\u9a6c\u5230\u8499\u53e4\u53bb\u4f1a\u5b8b\u94c1\u8f6e\u3002 \u3000\u3000\u4ed6\u4eec\u5728\u4e00\u4e2a\u540d\u53eb\u8774\u8776\u8c37\u7684\u5730\u65b9\u5b89\u4e0b\u8425\u5e10\uff0c\u5c60\u51e4\u4e0e\u51e0\u4e2a\u5934\u76ee\u5206\u5934\u53bb\u627e\u9f99\u521a\u3002\u5b5f\u660e\u971e\u548c\u53e6\u5916 \u51e0\u4e2a\u5934\u76ee\u5728\u8774\u8776\u8c37\u7559\u5b88\u3002 \u3000\u3000\u5c60\u51e4\u7b56\u9a6c\u8d70\u8fdb\u8774\u8776\u8c37\uff0c\u5fc3\u91cc\u4e0d\u7981\u60f3\u9053\uff1a\u201c\u5b5f\u59d0\u59d0\u6628\u5929\u624d\u548c\u6211\u8bf4\u8d77\u674e\u601d\u5357\u7684\u4e8b\u60c5\uff0c\u5979\u4e00\u5b9a \u60f3\u4e0d\u5230\u6211\u4eca\u5929\u5c31\u78b0\u4e0a\u4e86\u4ed6\u4e86\u3002\u201d \u3000\u3000\u5c60\u51e4\u4e0e\u5b5f\u660e\u971e\u662f\u81ea\u5c0f\u76f8\u8bc6\u7684\uff0c\u867d\u7136\u5979\u4eec\u4e8c\u4eba\u4e00\u5728\u6c5f\u5357\u4e00\u5728\u6c5f\u5317\uff0c\u89c1\u9762\u7684\u6b21\u6570\u5e76\u4e0d\u591a\uff0c\u4f46 \u5374\u662f\u5f7c\u6b64\u77e5\u5fc3\uff0c\u60c5\u540c\u59d0\u59b9\uff0c\u4ece\u5b5f\u660e\u971e\u8fd9\u6b21\u613f\u610f\u72ec\u81ea\u7559\u4e0b\u6765\uff0c\u5e2e\u5fd9\u5c60\u51e4\u62a5\u4ec7\uff0c\u5c31\u53ef\u4ee5\u89c1\u5f97\u5979\u4eec \u4ea4\u60c5\u7684\u4e00\u6591\u4e86\u3002 \u3000\u3000\u5c60\u51e4\u60f3\u8d77\u4e86\u5b5f\u660e\u971e\u5bf9\u5979\u7684\u60c5\u4e49\uff0c\u4e0d\u7531\u5f97\u5fc3\u4e2d\u6168\u53f9\uff1a\u201c\u5b5f\u59d0\u59d0\u8fd9\u6837\u70ed\u5fc3\u80a0\u7684\u4eba\uff0c\u5374\u504f\u504f\u78b0 \u4e0a\u4e86\u4e00\u4e2a\u51b7\u9762\u65e0\u60c5\u7684\u5c0f\u5b50\u3002\u201d\u89c9\u5f97\u82e5\u662f\u6bd4\u8d77\u4e86\u5b5f\u660e\u971e\u6765\uff0c\u81ea\u5df1\u5df2\u7ecf\u662f\u201c\u5e78\u798f\u201d\u591a\u4e86\u3002 \u3000\u3000\u201c\u77f3\u5e08\u54e5\u867d\u7136\u662f\u8d1f\u6c14\u79bb\u5f00\u4e86\u6211\uff0c\u4f46\u4ed6\u548c\u6211\u662f\u771f\u5fc3\u76f8\u7231\u7684\uff0c\u9f99\u5e08\u54e5\u4e3a\u6211\u727a\u7272\uff0c\u5bf9\u6211\u7684\u53cb\u8c0a \u66f4\u662f\u4ee4\u4eba\u611f\u52a8\u3002\u4eba\u751f\u5f97\u4e00\u77e5\u5df1\u8db3\u4ee5\u65e0\u61be\uff0c\u8001\u5929\u7237\u5bf9\u6211\u603b\u7b97\u662f\u4e0d\u8584\u4e86\u3002\u53ef\u60dc\u5b5f\u59d0\u59d0\u6ca1\u6709\u6211\u8fd9\u6837 \u5e78\u8fd0\uff0c\u90a3\u59d3\u674e\u7684\u5c0f\u5b50\u53ea\u8981\u6709\u6211\u8fd9\u4e24\u4e2a\u5e08\u54e5\u4e00\u534a\u90a3\u6837\u597d\uff0c\u6211\u5c31\u7528\u4e0d\u7740\u4e3a\u5979\u62c5\u5fe7\u4e86\u3002\u201d\u5c60\u51e4\u5fc3 \u60f3\u3002 \u3000\u3000\u5c60\u51e4\u6b63\u81ea\u80e1\u601d\u4e71\u60f3\uff0c\u5ffd\u89c1\u6797\u5b50\u91cc\u94bb\u51fa\u4e00\u4e2a\u4eba\u6765\uff0c\u7b11\u9053\uff1a\u201c\u600e\u7684\u8fd9\u6837\u665a\u624d\u56de\u6765\uff1f\u6211\u6b63\u60f3\u51fa \u53bb\u627e\u4f60\u5462\u3002\u201d\u8fd9\u4e2a\u4eba\u6b63\u662f\u5b5f\u660e\u971e\u3002 \u3000\u3000\u5c60\u51e4\u9053\uff1a\u201c\u5e78\u4e8f\u4f60\u6ca1\u6709\u53bb\u3002\u201d\u5b5f\u660e\u971e\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u773c\u5149\u4e00\u77a5\uff0c\u53d1\u89c9\u5c60\u51e4\u9762\u6709\u6cea\u75d5\uff0c \u5b5f\u660e\u971e\u5403\u4e86\u4e00\u60ca\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u600e\u4e48\u5566\uff1f\u9f99\u521a\u5462\uff0c\u89c1\u7740\u4e86\u6ca1\u6709\uff1f\u201d \u3000\u3000\u5c60\u51e4\u9053\uff1a\u201c\u9f99\u5e08\u54e5\u6b7b\u4e86\u3002\u201d\u5b5f\u660e\u971e\u9053\uff1a\u201c\u8c01\u6740\u6b7b\u4ed6\u7684\uff1f\u201d\u5c60\u51e4\u6728\u7136\u8bf4\u9053\uff1a\u201c\u6211\u7684\u54e5 \u54e5\u3002\u201d\u5b5f\u660e\u971e\u77e5\u9053\u5979\u4eec\u5144\u59b9\u53cd\u8138\u7684\u90a3\u4ef6\u4e8b\u60c5\uff0c\u5f53\u4e0b\u4e5f\u5c31\u731c\u5230\u4e86\u5c60\u9f99\u8981\u6740\u9f99\u521a\u7684\u539f\u6545\uff0c\u5fc3\u91cc\u60f3 \u9053\uff1a\u201c\u602a\u4e0d\u5f97\u5979\u8fd9\u6837\u4f24\u5fc3\uff0c\u8fd9\u4e2a\u4ec7\u53ef\u662f\u4e0d\u80fd\u7531\u5979\u62a5\u4e86\u3002\u201d \u3000\u3000\u5c60\u51e4\u9053\uff1a\u201c\u6740\u6211\u7239\u7239\u7684\u4ec7\u4eba\u662f\u9633\u5929\u96f7\uff0c\u4e8c\u5e08\u54e5\u5df2\u7ecf\u6253\u542c\u5230\u4e86\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u597d\uff0c\u9f99\u521a\u7684\u4ec7\u6211\u4e0d\u80fd\u66ff\u4ed6\u62a5\uff0c\u4f60\u7239\u7239\u7684\u4ec7\u6211\u4e00\u5b9a\u8981\u5e2e\u5fd9\u4f60\u62a5\u7684\uff0c\u6211\u6253\u4e0d\u8fc7 \u9633\u5929\u96f7\uff0c\u6211\u8fd8\u53ef\u4ee5\u8bf7\u6211\u7684\u7239\u7239\u51fa\u6765\u3002\u201d\u5c60\u51e4\u62b9\u5e72\u4e86\u773c\u6cea\uff0c\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u59d0\u59d0\u3002\u9f99\u521a\u4e4b\u4e8b\u6211\u4f1a \u544a\u8bc9\u5988\u7684\uff0c\u5373\u4f7f\u4e0d\u8981\u54e5\u54e5\u507f\u547d\uff0c\u81f3\u5c11\u4e5f\u8981\u5e9f\u53bb\u4ed6\u7684\u6b66\u529f\u3002\u201d \u3000\u3000\u5c60\u51e4\u4e0b\u4e86\u9a6c\u4e0e\u5b5f\u660e\u971e\u5e76\u80a9\u540c\u884c\uff0c\u6b64\u65f6\u5df2\u662f\u6708\u4e0a\u68a2\u5934\uff0c\u5c71\u9876\u4e0a\u79ef\u96ea\u7691\u7691\uff0c\u96ea\u6708\u4ea4\u8f89\uff0c\u5c31\u50cf \u94f6\u5149\u6cfb\u5730\u4e00\u822c\uff0c\u5c60\u51e4\u597d\u50cf\u51fa\u4e86\u795e\uff0c\u4e45\u4e45\u6ca1\u6709\u8bf4\u8bdd\uff0c\u5b5f\u660e\u971e\u6709\u70b9\u8be7\u5f02\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5c60\u51e4\u7d20\u6765 \u662f\u85cf\u4e0d\u4f4f\u8bdd\u7684\uff0c\u4e3a\u4ec0\u4e48\u76f4\u5230\u73b0\u5728\uff0c\u5979\u8fd8\u6ca1\u6709\u544a\u8bc9\u6211\u5979\u4eca\u65e5\u7684\u906d\u9047\uff0c\u5373\u4f7f\u5979\u7684\u60b2\u4f24\u672a\u8fc7\uff0c\u4e5f\u8be5 \u5411\u6211\u503e\u5410\u5440\u3002\u201d\u6b63\u8981\u95ee\u5979\uff0c\u5c60\u51e4\u5ffd\u5730\u6253\u4e86\u4e00\u4e2a\u5bd2\u5664\uff0c\u5583\u5583\u8bf4\u9053\uff1a\u201c\u597d\u51b7\uff0c\u597d\u51b7\uff01\u201d\u5b5f\u660e\u971e\u8131 \u4e0b\u62ab\u98ce\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u91cc\u7684\u6c14\u5019\u65e9\u665a\u5dee\u522b\u5f88\u5927\uff0c\u5c0f\u5fc3\uff0c\u522b\u7740\u4e86\u5bd2\u3002\u201d\u5c60\u51e4\u63a8\u5f00\u4e86\u5979\u9012\u8fc7\u6765\u7684\u62ab \u98ce\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u662f\u5fc3\u4e2d\u5bd2\u51b7\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u6014\u4e86\u4e00\u6014\uff0c\u8bf4\u9053\uff1a\u201c\u5bf9\u5566\uff0c\u521a\u624d\u4f60\u8bf4\uff0c\u5e78\u4e8f\u6211\u6ca1\u6709\u53bb\uff01\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5c60\u51e4\u9053\uff1a\u201c\u4f60\u731c\u6211\u4eca\u5929\u78b0\u7740\u4e86\u4ec0\u4e48\u4eba\uff1f\u201d\u5979\u672c\u6765\u4e0d\u60f3\u8bf4\u7684\uff0c\u7ec8\u4e8e\u8fd8\u662f\u5fcd\u4e0d\u4f4f\u8981\u8bf4\u4e86\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u8bf4\uff0c\u6211\u600e\u4e48\u77e5\u9053\uff1f\u201d \u3000\u3000\u5c60\u51e4\u9053\uff1a\u201c\u6211\u627e\u7740\u4e8c\u5e08\u54e5\u7684\u65f6\u5019\uff0c\u6b63\u6709\u4e24\u4e2a\u670b\u53cb\u7167\u6599\u7740\u4ed6\u3002\u539f\u6765\u4e8c\u5e08\u54e5\u53d7\u4f24\u4e4b\u540e\uff0c\u4e0d\u5e78 \u53c8\u78b0\u4e0a\u8ffd\u5175\uff0c\u5e78\u4e8f\u5f97\u8fd9\u4e24\u4eba\u62d4\u5200\u76f8\u52a9\uff0c\u53c8\u7ed9\u4ed6\u4eba\u53c2\u7eed\u547d\uff0c\u4e8c\u5e08\u54e5\u8fd9\u624d\u7b49\u5f97\u5230\u548c\u6211\u89c1\u6700\u540e\u4e00\u9762 \u7684\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u8fd9\u4e24\u4f4d\u70ed\u5fc3\u7684\u670b\u53cb\u771f\u662f\u96be\u5f97\uff0c\u60f3\u5fc5\u662f\u79f0\u4f60\u76f8\u8bc6\u7684\u4e86\uff1f\u201d \u3000\u3000\u5c60\u51e4\u7b11\u9053\uff1a\u201c\u548c\u6211\u5e76\u4e0d\u76f8\u8bc6\uff0c\u5012\u662f\u548c\u4f60\u76f8\u8bc6\u7684\u3002\u4e0d\uff0c\u53ea\u6709\u4e00\u4e2a\u662f\u548c\u4f60\u76f8\u8bc6\uff0c\u53e6\u4e00\u4e2a\u60f3\u5fc5 \u4f60\u4e5f\u672a\u66fe\u89c1\u8fc7\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u8be7\u9053\uff1a\u201c\u548c\u6211\u76f8\u8bc6\u7684\u90a3\u4e2a\u662f\u8c01\uff1f\u4f60\u4e0d\u8981\u6545\u5f04\u7384\u865a\u4e86\uff0c\u5feb\u70b9\u63ed\u5f00\u95f7\u846b\u82a6\u5427\u3002\u201d \u3000\u3000\u5c60\u51e4\u9053\uff1a\u201c\u90a3\u4eba\u5c31\u662f\u53d7\u8fc7\u4f60\u6069\u60e0\u7684\u90a3\u4f4d\u674e\u516c\u5b50\uff0c\u674e\u601d\u5357\uff01\u201d \u3000\u3000\u5b5f\u660e\u971e\u53c8\u60ca\u53c8\u559c\uff0c\u8bf4\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u662f\u4ed6\uff01\u4ed6\u600e\u4e48\u4e5f\u5230\u897f\u590f\u6765\u4e86\uff1f\u8fd8\u6709\u4e00\u4e2a\u5462\uff1f\u201d \u3000\u3000\u5c60\u51e4\u9053\uff1a\u201c\u662f\u4e2a\u5973\u5b50\uff0c\u521d\u65f6\u6211\u8fd8\u4ee5\u4e3a\u662f\u4ed6\u7684\u59d0\u59d0\uff0c\u540e\u6765\u95ee\u4e86\u59d3\u540d\uff0c\u624d\u77e5\u9053\u662f\u59d3\u6768\u540d\u5a49\u3002 \u8fd9\u4f4d\u6768\u59d1\u5a18\u795e\u60c5\u50b2\u5cb8\uff0c\u5bf9\u6211\u4e00\u76f4\u662f\u7231\u7406\u4e0d\u7406\u7684\u6837\u5b50\uff0c\u5374\u4e0d\u77e5\u5979\u662f\u674e\u601d\u5357\u7684\u4ec0\u4e48\u4eba\u3002\u201d\u5176\u5b9e\u6768 \u5a49\u5e76\u6ca1\u6709\u5c60\u51e4\u8bf4\u7684\u90a3\u6837\u4ee4\u5979\u96be\u582a\uff0c\u53ea\u56e0\u5c60\u51e4\u5bf9\u5979\u6b8a\u65e0\u597d\u611f\uff0c\u8fd9\u5c31\u4e0d\u514d\u5938\u5927\u5176\u8bcd\u3002 \u3000\u3000\u5b5f\u660e\u971e\u5446\u4e86\u4e00\u5446\uff0c\u52c9\u5f3a\u7b11\u9053\uff1a\u201c\u4f55\u5fc5\u7ba1\u5979\u662f\u4ec0\u4e48\u4eba\u3002\u4f46\u4f60\u6709\u6ca1\u6709\u544a\u8bc9\u674e\u601d\u5357\u6211\u5728\u8fd9 \u513f\uff1f\u201d \u3000\u3000\u5c60\u51e4\u9053\uff1a\u201c\u8bf4\u5566\uff0c\u6211\u8fd8\u8bf7\u4ed6\u6765\u770b\u4f60\u5462\uff01\u53ef\u662f\u2014\u2014\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u4f60\u771f\u662f\u591a\u4e8b\u3002\u53ef\u662f\u53c8\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u5c60\u51e4\u9053\uff1a\u201c\u4ed6\u4e0d\u80af\u6765\u3002\u770b\u795e\u6c14\u597d\u50cf\u662f\u4e0d\u9ad8\u5174\u6211\u4e0d\u8fde\u540c\u9080\u8bf7\u90a3\u4f4d\u6768\u59d1\u5a18\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u4e0d\u6765\u5c31\u7b97\u4e86\u3002\u6211\u4e5f\u4e0d\u662f\u975e\u89c1\u4ed6\u4e0d\u53ef\u3002\u201d \u3000\u3000\u5c60\u51e4\u9053\uff1a\u201c\u4f60\u4e8e\u4ed6\u6709\u6551\u547d\u4e4b\u6069\uff0c\u5982\u4eca\u8fd1\u5728\u54ab\u5c3a\uff0c\u4ed6\u5374\u4e0d\u6765\u89c1\u4f60\uff0c\u4f60\u4e0d\u602a\u4ed6\uff0c\u6211\u4e5f\u4e3a\u4f60\u611f \u5230\u4e0d\u503c\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u4f60\u8bf4\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u6211\u6551\u4ed6\u662f\u56e0\u4e3a\u6211\u76f8\u4fe1\u4ed6\u662f\u597d\u4eba\uff0c\u96be\u9053\u6211\u662f\u671b\u4ed6\u62a5\u7b54\u7684 \u5417\uff1f\u201d \u3000\u3000\u5c60\u51e4\u9053\uff1a\u201c\u59d0\u59d0\uff0c\u4f60\u77e5\u9053\u6211\u662f\u5fc3\u76f4\u53e3\u5feb\uff0c\u6709\u597d\u8bf4\u597d\uff0c\u6709\u574f\u8bf4\u574f\u7684\u3002\u4f60\u7684\u773c\u5149\u4e0d\u9519\uff0c\u674e\u601d \u5357\u786e\u662f\u53d7\u4e86\u51a4\u6789\u7684\u3002\u539f\u6765\u5728\u8499\u53e4\u505a\u5b98\u7684\u90a3\u4e2a\u5e76\u4e0d\u662f\u4ed6\u7236\u4eb2\uff0c\u90a3\u662f\u4e00\u4e2a\u5047\u5192\u4ed6\u7236\u4eb2\u540d\u5b57\u7684\u5978 \u4eba\u3002\u201d\u5f53\u4e0b\u5c06\u674e\u601d\u5357\u544a\u8bc9\u5979\u7684\u8bdd\u8f6c\u544a\u5b5f\u660e\u971e\u3002 \u3000\u3000\u5b5f\u660e\u971e\u751a\u611f\u5b89\u6170\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u6211\u5c31\u653e\u5fc3\u4e86\u3002\u5e78\u4e8f\u6211\u90a3\u665a\u529d\u963b\u7239\u7239\uff0c\u8981\u4e0d\u7136\u53ef\u771f\u662f\u6740\u9519\u597d \u4eba\u3002\u201d \u3000\u3000\u5c60\u51e4\u9053\uff1a\u201c\u53ef\u662f\u4ed6\u65e0\u60c5\u65e0\u4e49\uff0c\u6211\u5374\u53c8\u4e0d\u80fd\u4e0d\u8bf4\u4ed6\u7684\u574f\u8bdd\u4e86\uff01\u201d\u5b5f\u660e\u971e\u5567\u9053\uff1a\u201c\u6211\u4e0e\u4ed6\u4e0d \u8fc7\u662f\u840d\u6c34\u76f8\u9022\uff0c\u53c8\u4f55\u987b\u4ed6\u5bf9\u6211\u6709\u60c5\u6709\u4e49\uff1f\u4f60\u8d8a\u8bf4\u8d8a\u4e0d\u50cf\u8bdd\u5566\uff0c\u4f60\u518d\u8bf4\u6211\u5c31\u4e0d\u7406\u4f60\u4e86\uff01\u201d \u3000\u3000\u5b5f\u660e\u971e\u6325\u4e00\u6325\u624b\uff0c\u597d\u50cf\u8981\u628a\u8fd9\u4ef6\u4ee4\u5979\u4e0d\u6109\u5feb\u7684\u4e8b\u60c5\u6325\u8d70\u4f3c\u7684\uff0c\u53ef\u662f\u4e0d\u77e5\u600e\u7684\uff0c\u674e\u601d\u5357\u7684 \u5f71\u5b50\u5374\u597d\u4f3c\u4e00\u4e2a\u4e0d\u8bb2\u7406\u7684\u5ba2\u4eba\uff0c\u786c\u8981\u5360\u636e\u5979\u7684\u5fc3\u623f\uff0c\u4e0d\u80af\u8d70\u5f00\u3002\u201c\u53ef\u60dc\u5c60\u51e4\u6ca1\u6709\u9080\u8bf7\u90a3\u4f4d\u6768 \u59d1\u5a18\uff0c\u8fde\u4ed6\u90fd\u89c1\u4e0d\u7740\u4e86\u3002\u89c1\u4e0d\u7740\u4e0d\u6253\u7d27\uff0c\u53ea\u6015\u674e\u601d\u5357\u4ee5\u4e3a\u662f\u6211\u7684\u4e3b\u610f\uff0c\u5012\u53eb\u4ed6\u4eec\u7b11\u6211\u6c14\u91cf\u72ed \u7a84\u4e86\u3002\u201d\u60f3\u81f3\u6b64\u5904\uff0c\u4e0d\u7981\u53cc\u988a\u6655\u7ea2\u3002\u5b5f\u660e\u971e\u7b2c\u4e00\u6b21\u53d1\u73b0\u4e86\u81ea\u5df1\u5fc3\u5e95\u7684\u79d8\u5bc6\uff0c\u539f\u6765\u81ea\u5df1\u662f\u8fd9\u6837 \u7684\u6e34\u671b\u518d\u89c1\u674e\u601d\u5357\u3002 \u3000\u3000\u5c60\u51e4\u7b11\u9053\uff1a\u201c\u597d\uff0c\u4f60\u4e0d\u8bb8\u8bf4\uff0c\u6211\u5c31\u4e0d\u8bf4\u5566\u3002\u5176\u5b9e\u4f60\u82e5\u662f\u5fc3\u65e0\u5c18\u57a2\uff0c\u53c8\u6015\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5b5f\u660e\u971e\u597d\u4f3c\u53d7\u4e86\u5f53\u5934\u4e00\u68d2\uff0c\u5979\u672c\u6765\u662f\u60f3\u88c5\u4f5c\u53d1\u6c14\u7684\uff0c\u4f46\u8f6c\u5ff5\u4e00\u60f3\uff0c\u5c60\u51e4\u8bf4\u7684\u8fd9\u53e5\u8bdd\u786e\u662f \u6293\u7740\u4e86\u5979\u7684\u75d2\u5904\uff0c\u5979\u60f3\u5047\u88c5\u53d1\u6c14\u4e5f\u5047\u88c5\u4e0d\u6765\u4e86\u3002 \u3000\u3000\u5b5f\u660e\u971e\u731b\u7136\u4e00\u7701\uff0c\u7b11\u9053\uff1a\u201c\u7ed9\u4f60\u7f20\u4e03\u5939\u516b\u5730\u80e1\u626f\u4e00\u901a\uff0c\u6211\u51e0\u4e4e\u628a\u4e00\u4ef6\u7d27\u8981\u7684\u4e8b\u60c5\u5fd8 \u4e86\u3002\u201d \u3000\u3000\u5c60\u51e4\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\u60c5\uff0c\u5927\u60ca\u5c0f\u602a\uff1f\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u6211\u8bf4\u51fa\u6765\uff0c\u53ea\u6015\u4f60\u975e\u5f97\u5f53\u771f\u7684\u5927\u60ca\u5c0f\u602a\u4e0d\u53ef\u3002\u201d \u3000\u3000\u5c60\u51e4\u9053\uff1a\u201c\u6211\u4e0d\u76f8\u4fe1\u3002\u4f60\u5feb\u8bf4\u5427\uff01\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u4f60\u4eca\u5929\u78b0\u5230\u4e86\u4e24\u4e2a\u610f\u5916\u7684\u4eba\uff0c\u8d75\u8d76\u9a74\u4ed6\u4eec\u4e5f\u78b0\u5230\u4e86\u4e24\u4e2a\u610f\u5916\u7684\u4eba\u3002\u201d\u8d75\u8d76 \u9a74\u662f\u5c60\u51e4\u624b\u4e0b\u7684\u4e00\u4e2a\u5934\u76ee\uff0c\u4eca\u5929\u4e00\u65e9\uff0c\u548c\u53e6\u4e00\u62e8\u4eba\u53bb\u627e\u5bfb\u9f99\u521a\u7684\u3002 \u3000\u3000\u5c60\u51e4\u9053\uff1a\u201c\u4e24\u4e2a\u4ec0\u4e48\u4eba\uff1f\u201d\u5b5f\u660e\u971e\u9053\uff1a\u201c\u5176\u4e2d\u4e00\u4e2a\u662f\u548c\u4f60\u76f8\u8bc6\u7684\uff0c\u53e6\u5916\u4e00\u4e2a\uff0c\u627e\u5148\u4e0d \u8bf4\uff0c\u8ba9\u4f60\u731c\u731c\u3002\u201d \u3000\u3000\u5c60\u51e4\u9053\uff1a\u201c\u6211\u8ba4\u8bc6\u7684\u90a3\u4e2a\u662f\u8c01\uff1f\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u662f\u6df3\u4e8e\u8191\u3002\u4f60\u60f3\u4e0d\u5230\u5427\uff0c\u4ed6\u8ffd\u4f60\u8ffd\u5230\u8fd9\u513f\u6765\u4e86\uff01\u201d \u3000\u3000\u5c60\u51e4\u9762\u8272\u4e00\u6c89\uff0c\u8bf4\u9053\uff1a\u201c\u539f\u6765\u662f\u8fd9\u81ed\u8d3c\u3002\u53e6\u5916\u4e00\u4e2a\u4f60\u4e0d\u8bf4\u6211\u4e5f\u77e5\u9053\u4e86\uff0c\u4e00\u5b9a\u662f\u6211\u90a3\u4e0d\u6210 \u6750\u7684\u54e5\u54e5\uff01\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u4e0d\u662f\u3002\u662f\u4e00\u4e2a\u7ea2\u8863\u5587\u561b\uff0c\u4f60\u60f3\u4e0d\u5230\u5427\uff1f\u201d \u3000\u3000\u5c60\u51e4\u6014\u4e86\u4e00\u6014\uff0c\u8bf4\u9053\uff1a\u201c\u897f\u590f\u4e0e\u8499\u53e4\u76f8\u90bb\uff0c\u6210\u5409\u601d\u6c57\u624b\u4e0b\u6709\u73ed\u7cbe\u901a\u6b66\u827a\u7684\u5587\u561b\uff0c\u8fd9\u7ea2\u8863 \u5587\u561b\u5b9a\u7136\u662f\u4ece\u8499\u53e4\u6765\u7684\u3002\u54fc\uff0c\u5982\u6b64\u8bf4\u6765\uff0c\u6df3\u4e8e\u8191\u8fd9\u81ed\u8d3c\u4e5f\u662f\u65e9\u5df2\u548c\u8499\u53e4\u9791\u5b50\u6709\u52fe\u7ed3\u7684\u4e86\uff0c\u8fd9 \u81ed\u8d3c\u6211\u975e\u548c\u4ed6\u7b97\u5e10\u4e0d\u53ef\uff01\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u4f60\u4e0d\u627e\u4ed6\u7b97\uff0c\u4ed6\u4e5f\u8981\u627e\u4f60\u7b97\u5e10\u5462\u3002\u201d \u3000\u3000\u5c60\u51e4\u6012\u9053\uff1a\u201c\u4ed6\u600e\u4e48\u8bf4\uff1f\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u8d75\u8d76\u9a74\u8fd9\u4e00\u62e8\u4eba\u78b0\u5230\u4e86\u4ed6\uff0c\u4ed6\u8bf4\u4ed6\u5df2\u7ecf\u77e5\u9053\u4e86\u4ee4\u5c0a\u88ab\u5bb3\u7684\u6d88\u606f\uff0c\u8981\u6765\u5b89\u6170 \u4f60\u5462\u3002\u201d \u3000\u3000\u5c60\u51e4\u9053\uff1a\u201c\u54fc\uff0c\u8ba9\u4ed6\u6765\u5427\u3002\u627e\u4e0d\u628a\u4ed6\u5316\u9aa8\u626c\u7070\u624d\u602a\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u6df3\u4e8e\u8191\u5411\u8d75\u8d76\u9a74\u8ffd\u67e5\u4f60\u7684\u4f4f\u5740\uff0c\u8d75\u8d76\u9a74\u548c\u4f60\u7684\u60f3\u6cd5\u4e0d\u4e00\u6837\uff0c\u4ed6\u4e5f\u4e0d\u613f\u610f\u4f60 \u591a\u60f9\u9ebb\u70e6\uff0c\u6b63\u56e0\u4e3a\u8d75\u8d76\u9a74\u77e5\u9053\u6df3\u4e8e\u8191\u548c\u8499\u53e4\u9791\u5b50\u5df2\u6709\u52fe\u7ed3\uff0c\u6240\u4ee5\u4ed6\u89c9\u5f97\u72af\u4e0d\u7740\u5728\u8fd9\u4e2a\u65f6\u5019\u53bb \u62db\u60f9\u4ed6\u3002\u201d \u3000\u3000\u5c60\u51e4\u6012\u6c14\u7a0d\u5e73\u4e4b\u540e\uff0c\u60f3\u4e86\u60f3\uff0c\u8bf4\u9053\uff1a\u201c\u627e\u660e\u767d\u4ed6\u7684\u610f\u601d\uff0c\u4ed6\u662f\u6015\u4f17\u5be1\u4e0d\u654c\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8981\u77e5\u4f60\u4eec\u8fd9\u6b21\u662f\u4e3a\u4e86\u5bfb\u89c5\u4ec7\u4eba\u6765\u7684\uff0c\u6c5f\u6e56\u4e0a\u5bfb\u4ec7\u62a5\u590d\u4e4b\u4e8b\uff0c\u672c\u5c5e\u5bfb \u5e38\uff0c\u4f46\u82e5\u662f\u548c\u5916\u654c\u4f5c\u5bf9\uff0c\u8fd9\u6027\u8d28\u5c31\u5b8c\u5168\u4e0d\u4e00\u6837\u4e86\u3002\u4e0d\u662f\u8bf4\u6211\u4eec\u4e0d\u8be5\u62b5\u6297\u8499\u53e4\u9791\u5b50\uff0c\u800c\u662f\u65f6\u5730 \u4e0d\u5b9c\uff01\u8fd9\u91cc\u6bd5\u7adf\u662f\u4ed6\u4eec\u7684\u5730\u65b9\uff0c\u4ee5\u54b1\u4eec\u8fd9\u70b9\u529b\u91cf\uff0c\u5b9e\u662f\u96be\u4ee5\u548c\u4ed6\u4eec\u8f83\u91cf\u3002\u9664\u975e\u662f\u4e8b\u60c5\u903c\u5230\u5934 \u4e0a\uff0c\u90a3\u53c8\u53e6\u5f53\u522b\u8bba\u3002\u201d \u3000\u3000\u5c60\u51e4\u9053\uff1a\u201c\u8d75\u8d76\u9a74\u4e00\u5411\u8001\u6210\u6301\u91cd\uff0c\u602a\u4e0d\u5f97\u4ed6\u6709\u5982\u6b64\u60f3\u6cd5\u3002\u4e0d\u8fc7\u4f9d\u6211\u770b\u6765\uff0c\u6df3\u4e8e\u8191\u65e2\u662f\u6709 \u5fc3\u6765\u627e\u6211\u7684\u9ebb\u70e6\uff0c\u4ed6\u548c\u8499\u53e4\u9791\u5b50\u53c8\u6709\u4e86\u52fe\u7ed3\uff0c\u54b1\u4eec\u8981\u8eb2\u907f\u6050\u6015\u4e5f\u662f\u8eb2\u907f\u4e0d\u4e86\u7684\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u8fd9\u70b9\u5012\u662f\u7ed9\u4f60\u6599\u7740\u4e86\u3002\u6df3\u4e8e\u8191\u679c\u7136\u4e0d\u80af\u653e\u8fc7\u4ed6\u4eec\uff0c\u8d75\u8d76\u9a74\u4ed6\u4eec\u5f53\u7136\u4e5f\u4e0d\u613f \u900f\u9732\u4f60\u7684\u4f4f\u5904\uff0c\u7ed3\u679c\u53ea\u6709\u5927\u6253\u4e00\u573a\u3002\u8d75\u8d76\u9a74\u548c\u53e6\u5916\u4e24\u4e2a\u5934\u76ee\u53d7\u4e86\u70b9\u4f24\uff0c\u9003\u4e86\u56de\u6765\u3002\u636e\u8d75\u8d76\u9a74 \u8bf4\uff0c\u5e78\u4e8f\u90a3\u4e2a\u7ea2\u8863\u5587\u561b\u6ca1\u6709\u52a8\u624b\uff0c\u8981\u4e0d\u7136\u53ea\u6015\u8d25\u5f97\u66f4\u60e8\u3002\u201d \u3000\u3000\u5c60\u51e4\u5403\u4e86\u4e00\u60ca\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u4e2a\u5934\u76ee\u5462\uff1f\u201d \u3000\u3000\u76c2\u660e\u971e\u9053\uff1a\u201c\u7ed9\u6df3\u4e8e\u8191\u64d2\u4e86\u3002\u201d \u3000\u3000\u5c60\u51e4\u6012\u9053\uff1a\u201c\u4ed6\u6349\u4e86\u6211\u4eec\u7684\u4eba\uff0c\u96be\u9053\u6211\u8fd8\u8981\u8eb2\u5f00\u4ed6\u5417\uff1f\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u6df3\u4e8e\u8191\u6349\u4e86\u8fd9\u4e2a\u5934\u76ee\uff0c\u4e0d\u95ee\u53ef\u77e5\uff0c\u5f53\u7136\u662f\u8981\u95ee\u4ed6\u7684\u53e3\u4f9b\uff0c\u6216\u8005\u662f\u8981\u4ed6\u5e26\u8def \u7684\u4e86\uff0c\u6b64\u4e8b\u53ef\u4e0d\u80fd\u610f\u6c14\u7528\u4e8b\uff0c\u6211\u4eec\u6b63\u662f\u8981\u7b49\u4f60\u56de\u6765\u5546\u91cf\u5bf9\u7b56\u3002\u4f60\u5230\u73b0\u5728\u624d\u56de\u6765\uff0c\u628a\u6211\u4eec\u6025\u6b7b \u4e86\uff0c\u56e0\u6b64\u6211\u624d\u51fa\u6765\u627e\u4f60\u7684\u3002\u201d \u3000\u3000\u5c60\u51e4\u9053\uff1a\u201c\u4f9d\u4f60\u4e4b\u89c1\u5982\u4f55\uff1f\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u8d75\u8d76\u9a74\u7684\u610f\u601d\u662f\uff1a\u541b\u5b50\u62a5\u4ec7\uff0c\u4e09\u5e74\u672a\u665a\u3002\u201d \u3000\u3000\u5c60\u51e4\u9053\uff1a\u201c\u6211\u77e5\u9053\u8d75\u8d76\u9a74\u662f\u6015\u6211\u5403\u4e8f\uff0c\u4f46\u4f60\u7684\u610f\u601d\u53c8\u662f\u600e\u6837\uff0c\u4f60\u8fd8\u6ca1\u6709\u8bf4\u5462\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u5982\u4eca\u9f99\u521a\u5df2\u6b7b\uff0c\u5b8b\u94c1\u8f6e\u592b\u5987\u672a\u66fe\u4f1a\u5408\uff0c\u8d75\u8d76\u9a74\u53c8\u53d7\u4e86\u4f24\uff0c\u6df3\u4e8e\u8191\u82e5\u662f\u8981\u6765 \u7684\u8bdd\uff0c\u5b9a\u662f\u6709\u5907\u800c\u6765\uff0c\u51b3\u4e0d\u4f1a\u53ea\u548c\u4e00\u4e24\u4e2a\u5e2e\u624b\u6765\u7684\uff0c\u4f9d\u6211\u4e4b\u89c1\uff0c\u8fd8\u662f\u4e0d\u5b9c\u786c\u62fc\u3002\u201d \u3000\u3000\u5c60\u51e4\u6c89\u541f\u4e0d\u8bed\uff0c\u5b5f\u660e\u971e\u53c8\u9053\uff1a\u201c\u6740\u5bb3\u4f2f\u7236\u7684\u4ec7\u4eba\uff0c\u73b0\u5728\u5df2\u7ecf\u77e5\u9053\u662f\u9633\u5929\u96f7\u4e86\uff0c\u9633\u5929\u96f7\u6295 \u9760\u91d1\u864f\uff0c\u60f3\u5fc5\u73b0\u5728\u662f\u5728\u5927\u90fd\uff1f\u201d \u3000\u3000\u5c60\u51e4\u9053\uff1a\u201c\u4e0d\u9519\u3002\u636e\u4e8c\u5e08\u54e5\u5f97\u5230\u7684\u6d88\u606f\uff0c\u9633\u5929\u96f7\u6740\u4e86\u6211\u7684\u7239\u7239\uff0c\u4ed6\u81ea\u8eab\u4e5f\u53d7\u4e86\u91cd\u4f24\uff0c\u5982 \u4eca\u6b63\u5728\u5927\u90fd\u517b\u4f24\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u56e0\u6b64\u6211\u770b\u8fd8\u662f\u5148\u56de\u53bb\u7684\u597d\u3002\u62a5\u4e86\u4f60\u7239\u7239\u7684\u4ec7\uff0c\u518d\u4e0e\u6df3\u4e8e\u8191\u7b97\u5e10\u3002\u5982\u4eca\u8499\u53e4 \u6b63\u5728\u5174\u5175\u5165\u4fb5\u91d1\u56fd\uff0c\u54b1\u4eec\u4e5f\u4e0d\u6101\u6ca1\u6709\u548c\u8499\u53e4\u9791\u5b50\u4ea4\u624b\u7684\u673a\u4f1a\u3002\u88ab\u6349\u53bb\u7684\u90a3\u4e2a\u5934\u76ee\uff0c\u6df3\u4e8e\u8191\u770b \u5728\u4f60\u54e5\u54e5\u7684\u4efd\u4e0a\uff0c\u4e5f\u672a\u5fc5\u5c31\u4f1a\u6740\u4e86\u4ed6\u7684\u3002\u201d \u3000\u3000\u5c60\u51e4\u9053\uff1a\u201c\u65e2\u7136\u4f60\u4eec\u90fd\u662f\u8fd9\u6837\u4e3b\u5f20\uff0c\u90a3\u4e48\u4eca\u5929\u665a\u4e0a\u5148\u642c\u4e2a\u5730\u65b9\uff0c\u660e\u5929\u4e00\u65e9\u5c31\u8d70\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u4f60\u7d2f\u4e0d\u7d2f\uff1f\u201d\u5c60\u51e4\u9053\uff1a\u201c\u6211\u5012\u4e0d\u7d2f\uff0c\u5c31\u53ea\u4e0d\u77e5\u8d75\u8d76\u9a74\u4ed6\u4eec\u4f24\u5f97\u5982\u4f55\uff1f\u201d\u5b5f \u660e\u971e\u9053\uff1a\u201c\u4e0d\u7b97\u5f88\u91cd\uff0c\u8dd1\u4e0d\u52a8\uff0c\u9a91\u9a6c\u603b\u53ef\u4ee5\u7684\u3002\u201d\u5c60\u51e4\u9053\uff1a\u201c\u597d\u5427\uff0c\u90a3\u4e48\u4e8b\u4e0d\u5b9c\u8fdf\uff0c\u54b1\u4eec\u9a6c \u4e0a\u5c31\u8d70\u3002\u201d \u3000\u3000\u4ece\u8c37\u53e3\u5230\u4ed6\u4eec\u624e\u8425\u4e4b\u5904\u7ea6\u6709\u516d\u4e03\u91cc\u8def\uff0c\u4e8c\u4eba\u5feb\u9a6c\u52a0\u97ad\uff0c\u4e0d\u9700\u591a\u4e45\uff0c\u5df2\u662f\u6df1\u5165\u5e7d\u8c37\uff0c\u5b89\u5728 \u5c71\u5761\u4e0a\u7684\u5e10\u5e55\u4e5f\u5df2\u7ecf\u53ef\u4ee5\u770b\u5f97\u89c1\u4e86\u3002 \u3000\u3000\u5c60\u51e4\u5ffd\u5730\u5fc3\u4e2d\u4e00\u52a8\uff0c\u201c\u600e\u7684\u4ed6\u4eec\u8fd9\u6837\u7c97\u5fc3\u5927\u610f\uff0c\u7adf\u7136\u6ca1\u6709\u6d3e\u4eba\u653e\u54e8\uff1f\u201d\u8981\u77e5\u5c60\u51e4\u867d\u7136\u6027 \u6025\uff0c\u5374\u4e5f\u4e0d\u662f\u9c81\u83bd\u4e4b\u4eba\uff0c\u5979\u5728\u7eff\u6797\u957f\u5927\uff0c\u591a\u5c11\u6709\u70b9\u6c5f\u6e56\u7ecf\u9a8c\uff0c\u4e0d\u89c1\u6709\u4eba\u8fce\u63a5\uff0c\u4e0d\u514d\u8d77\u4e86\u7591 \u5fc3\u3002\u5fc3\u60f3\uff1a\u8fd9\u73ed\u4eba\u90fd\u662f\u8ddf\u968f\u5979\u7236\u4eb2\u591a\u5e74\u7684\u90e8\u5c5e\uff0c\u6309\u8bf4\u51b3\u4e0d\u4f1a\u8fd9\u6837\u7cca\u6d82\uff0c\u8fde\u653e\u54e8\u628a\u98ce\u7684\u4eba\u90fd\u6ca1 \u4e00\u4e2a\uff1f\u800c\u4e14\u5979\u662f\u4e00\u8def\u75be\u9a70\u56de\u6765\u7684\uff0c\u5373\u4f7f\u6ca1\u4eba\u653e\u54e8\uff0c\u542c\u5f97\u9a6c\u94c3\u58f0\u54cd\uff0c\u4e5f\u8be5\u6709\u4eba\u51fa\u6765\u770b\u4e86\u3002 \u3000\u3000\u5fc3\u5ff5\u672a\u5df2\uff0c\u5ffd\u542c\u5f97\u8349\u4e1b\u91cc\u6709\u4eba\u5c16\u58f0\u53eb\u9053\uff1a\u201c\u524d\u9762\u6709\u57cb\u4f0f\uff0c\u54b1\u4eec\u7684\u8425\u5730\u5df2\u7ecf\u7ed9\u654c\u4eba\u5360 \u4e86\uff01\u201d\u5c60\u51e4\u5927\u5403\u4e00\u60ca\uff0c\u62ac\u5934\u770b\u65f6\uff0c\u53ea\u89c1\u4e00\u4e2a\u8840\u6dcb\u6dcb\u7684\u6c49\u5b50\u4ece\u8349\u4e1b\u4e2d\u722c\u51fa\u6765\uff0c\u53ef\u4e0d\u6b63\u662f\u8d75\u8d76 \u9a74\uff01 \u3000\u3000\u5c60\u51e4\u5931\u58f0\u53eb\u9053\uff1a\u201c\u8d75\u5927\u53d4\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u55d6\u7684\u4e00\u652f\u51b7\u7bad\u4ece\u8349\u4e1b\u91cc\u5c04\u8fc7\u6765\uff0c\u4e00 \u7bad\u7a7f\u5589\uff0c\u767b\u65f6\u628a\u8d75\u8d76\u9a74\u5c04\u6bd9\u3002 \u3000\u3000\u8f6c\u773c\u95f4\uff0c\u4e71\u8349\u4e1b\u4e2d\uff0c\u5ca9\u77f3\u540e\u9762\uff0c\u4f0f\u5175\u5c3d\u73b0\uff0c\u5f71\u7ef0\u7ef0\u7684\u4e5f\u4e0d\u77e5\u6709\u591a\u5c11\u4eba\uff0c\u6700\u524d\u9762\u7684\u90a3\u4e2a\u4eba \u6b63\u662f\u6df3\u4e8e\u8191\u3002 \u3000\u3000\u539f\u6765\u5728\u5b5f\u660e\u971e\u51fa\u53bb\u5bfb\u627e\u5c60\u51e4\u7684\u65f6\u5019\uff0c\u6df3\u4e8e\u8191\u65e9\u5df2\u5e26\u4e86\u4eba\u5077\u5077\u5730\u4ece\u53e6\u4e00\u9762\u4e0a\u4e86\u5c71\u3002\u6df3\u4e8e\u8191 \u7684\u6b66\u529f\u8fdc\u5728\u7559\u5b88\u7684\u8fd9\u73ed\u5934\u76ee\u4e4b\u4e0a\uff0c\u5e26\u6765\u7684\u4eba\u4e5f\u90fd\u662f\u527d\u608d\u5f02\u5e38\u7684\u8499\u53e4\u6b66\u58eb\u4ee5\u53ca\u9ed1\u9053\u4eba\u7269\uff0c\u5c71\u4e0a \u7684\u54e8\u536b\u521a\u521a\u53d1\u73b0\uff0c\u4fbf\u7ed9\u4ed6\u4eec\u5c04\u6740\u3002\u8425\u5e10\u4e2d\u7684\u4e03\u516b\u4e2a\u5934\u76ee\uff0c\u9664\u4e86\u6b66\u529f\u8f83\u5f3a\u7684\u8d75\u8d76\u9a74\u5e26\u4f24\u9003\u8d70\u4e4b \u5916\uff0c\u4e5f\u90fd\u5c3d\u6570\u88ab\u6b7c\u3002 \u3000\u3000\u6df3\u4e8e\u8191\u8fd9\u4e9b\u4eba\u6b63\u5728\u641c\u7d22\u8d75\u8d76\u9a74\uff0c\u542c\u5f97\u9a6c\u94c3\u58f0\u54cd\uff0c\u77e5\u9053\u5c60\u51e4\u56de\u6765\uff0c\u6df3\u4e8e\u8191\u51b3\u610f\u8981\u6d3b\u64d2\u5c60 \u51e4\uff0c\u4e8e\u662f\u53eb\u8fd9\u4e9b\u4eba\u6563\u5f00\u57cb\u4f0f\uff0c\u51c6\u5907\u597d\u7eca\u9a6c\u7d22\uff0c\u8fd8\u6398\u4e86\u51e0\u4e2a\u9677\u9631\uff0c\u53ea\u7b49\u5c60\u51e4\u524d\u6765\u4e0a\u5f53\u3002\u5018\u4e0d\u662f \u8d75\u8d76\u9a74\u4e0d\u987e\u6027\u547d\u51fa\u58f0\u53eb\u558a\uff0c\u5c60\u51e4\u518d\u8dd1\u8fc7\u53bb\uff0c\u524d\u9762\u5c31\u662f\u4e00\u4e2a\u9677\u9631\u3002 \u3000\u3000\u6df3\u4e8e\u8191\u7684\u6697\u7b97\u867d\u4e0d\u6210\u529f\uff0c\u81ea\u5fd6\u4ea6\u5df2\u662f\u7a33\u64cd\u80dc\u7b97\uff0c\u5f53\u4e0b\u5f97\u610f\u6d0b\u6d0b\u5730\u7b11\u9053\uff1a\u201c\u51e4\u59d1\u5a18\uff0c\u597d\u4e0d \u5bb9\u6613\u7b49\u7740\u4f60\u56de\u6765\u4e86\u3002\u4f60\u8981\u62a5\u4ec7\uff0c\u9760\u4f60\u8fd9\u4e9b\u624b\u4e0b\u4eba\u662f\u6ca1\u7528\u7684\uff0c\u53ea\u8981\u4f60\u7b54\u5e94\u5ac1\u6211\uff0c\u6211\u5012\u53ef\u4ee5\u66ff\u4f60 \u62a5\u4ec7\u3002\u201d \u3000\u3000\u5c60\u51e4\u6c89\u58f0\u9053\uff1a\u201c\u4f60\u8fc7\u6765\uff01\u201d\u6df3\u4e8e\u8191\u7b11\u9053\uff1a\u201c\u5a18\u5b50\u6709\u547d\uff0c\u5c82\u6562\u4e0d\u4ece\uff1f\u563f\u563f\uff0c\u4ece\u524d\u6211\u5728\u4f60 \u5bb6\uff0c\u4f60\u8d76\u6211\u8d70\u3002\u5982\u4eca\u6211\u8fd8\u6015\u4f60\u8d76\u6211\u4e0d\u6210\uff0c\u6211\u53ea\u662f\u6015\u4f60\u8d70\u4e86\u3002\u201d\u5927\u6447\u5927\u6446\u5730\u8d70\u5230\u5c60\u51e4\u8eab\u8fb9\uff0c\u8bf4 \u9053\uff1a\u201c\u6211\u6765\u5566\uff0c\u4f60\u8981\u4ec0\u4e48\uff0c\u8bf7\u5429\u5490\u5427\uff01\u201d\u5c60\u51e4\u9053\uff1a\u201c\u6211\u8981\u4f60\u7684\u6027\u547d\uff01\u201d\u5530\u5730\u4e00\u5251\u5c31\u523a\u8fc7\u53bb\u3002 \u3000\u3000\u6df3\u4e8e\u8191\u51b7\u7b11\u9053\uff1a\u201c\u81ed\u4e2b\u5934\uff0c\u4f60\u8981\u8c0b\u6740\u4eb2\u592b\uff1f\u201d\u94a9\u5149\u4e00\u95ea\uff0c\u5df2\u662f\u9501\u4f4f\u4e86\u5c60\u51e4\u7684\u5251\u5c16\u3002\u539f\u6765 \u6df3\u4e8e\u8191\u65e9\u5df2\u77e5\u9053\u5c60\u51e4\u6027\u5b50\u5014\u5f3a\uff0c\u51b3\u4e0d\u4f1a\u6beb\u65e0\u62b5\u6297\u5730\u5c31\u4f9d\u4ece\u4ed6\uff0c\u662f\u4ee5\u5c60\u51e4\u8fd9\u4e00\u62db\u867d\u7136\u51fa\u624b\u5947 \u5feb\uff0c\u5374\u65e9\u5df2\u5728\u4ed6\u610f\u6599\u4e4b\u4e2d\u3002 \u3000\u3000\u6df3\u4e8e\u8191\u559d\u9053\uff1a\u201c\u7ed9\u6211\u6eda\u4e0b\u9a6c\u6765\uff01\u201d\u5de6\u94a9\u4e00\u9001\uff0c\u523a\u8fd1\u9a6c\u8179\u3002\u5c60\u51e4\u811a\u8e29\u8e0f\u9519\uff0c\u767e\u5fd9\u4e2d\u4f7f\u51fa \u201c\u71d5\u5b50\u7a7f\u5e18\u201d\u7684\u8d85\u5353\u8f7b\u529f\uff0c\u659c\u98de\u51fa\u6570\u4e08\u5f00\u5916\uff0c\u6df3\u4e8e\u8191\u5355\u81c2\u4e4b\u529b\u593a\u4e0d\u4e86\u5979\u7684\u5251\uff0c\u94a9\u4e0a\u7684\u6708\u7259\u65ad \u4e86\u4e24\u9f7f\u3002\u8fd9\u7167\u9762\u4e00\u62db\u53ef\u8bf4\u662f\u5f7c\u6b64\u90fd\u5360\u4e0d\u5230\u4fbf\u5b9c\uff0c\u4f46\u5c60\u51e4\u7684\u5750\u9a91\u5374\u5df2\u7ed9\u6df3\u4e8e\u8191\u6740\u4e86\u3002 \u3000\u3000\u5c60\u51e4\u7acb\u8db3\u672a\u7a33\uff0c\u6df3\u4e8e\u8191\u53c8\u5df2\u653b\u4e86\u4e0a\u6765\u3002\u5c60\u51e4\u5389\u58f0\u559d\u9053\uff1a\u201c\u4eca\u65e5\u4e0d\u662f\u4f60\u6b7b\u4fbf\u662f\u6211\u4ea1\uff01\u201d\u53cd \u624b\u8fde\u73af\u4e09\u5251\uff0c\u62db\u62db\u90fd\u662f\u6307\u5411\u6df3\u4e8e\u8191\u7684\u8981\u5bb3\uff0c\u5c31\u50cf\u80cc\u540e\u957f\u7740\u773c\u775b\u4f3c\u7684\uff0c\u6df3\u4e8e\u8191\u5fc3\u5934\u4e00\u51db\uff1a\u201c\u8fd9 \u4e2b\u5934\u7684\u5251\u6cd5\u4f3c\u4e4e\u8fd8\u5728\u5979\u54e5\u54e5\u4e4b\u4e0a\uff0c\u5012\u662f\u4e0d\u53ef\u8f7b\u654c\u4e86\u3002\u201d \u3000\u3000\u6df3\u4e8e\u8191\u90a3\u6b21\u8d25\u5728\u5c60\u51e4\u4e4b\u624b\uff0c\u8d25\u5f97\u751a\u611f\u4e0d\u503c\uff0c\u90a3\u6b21\u4ed6\u662f\u5ba2\u4eba\u8eab\u4efd\uff0c\u60f3\u4e0d\u5230\u5c60\u51e4\u4f1a\u4ee4\u4ed6\u90a3\u6837 \u96be\u582a\u7684\u3002\u5728\u5c60\u51e4\u5bb6\u4e2d\uff0c\u4ed6\u4e0d\u80fd\u4e0d\u6709\u6240\u987e\u5fcc\u3002\u800c\u4e14\u5c60\u51e4\u4e00\u4e0a\u6765\u53c8\u5148\u6253\u7740\u4e86\u4ed6\u7684\u7a74\u9053\u3002\u6709\u8fd9\u4e09\u91cd \u7f18\u6545\uff0c\u6df3\u4e8e\u8191\u5f53\u7136\u662f\u8d25\u5f97\u6781\u4e0d\u7518\u5fc3\u4e86\u3002 \u3000\u3000\u4eca\u65e5\u518d\u6218\uff0c\u6df3\u4e8e\u8191\u65e9\u5df2\u6709\u4e86\u51c6\u5907\uff0c\u4f46\u5728\u5f00\u9996\u5341\u62db\u4e4b\u5185\uff0c\u4ecd\u662f\u5360\u4e0d\u5230\u5c60\u51e4\u534a\u70b9\u4fbf\u5b9c\uff0c\u6df3\u4e8e \u8191\u8fd9\u624d\u77e5\u9053\u5c60\u51e4\u786e\u662f\u6709\u51e0\u5206\u771f\u5b9e\u7684\u672c\u9886\uff0c\u90a3\u65e5\u80dc\u4ed6\uff0c\u5e76\u975e\u5168\u51ed\u53d6\u5de7\u3002 \u3000\u3000\u5c60\u51e4\u6076\u6597\u6df3\u4e8e\u8191\u4e4b\u65f6\uff0c\u5b5f\u660e\u971e\u548c\u90a3\u7ea2\u8863\u5587\u561b\u4e5f\u5df2\u4ea4\u4e0a\u4e86\u624b\u3002 \u3000\u3000\u7ea2\u8863\u5587\u561b\u624b\u4e0b\u7684\u8499\u53e4\u6b66\u58eb\u4e71\u7bad\u5c04\u6765\uff0c\u5b5f\u660e\u971e\u62a4\u5f97\u4e86\u4eba\uff0c\u62a4\u4e0d\u4e86\u9a6c\uff0c\u7d22\u6027\u8df3\u4e0b\u9a6c\u6765\uff0c\u6325\u5251 \u6740\u4eba\u4eba\u4e1b\u3002 \u3000\u3000\u5b5f\u660e\u971e\u7684\u5251\u6cd5\u66f4\u4e3a\u8f9b\u8fa3\uff0c\u53ea\u89c1\u5979\u8eab\u5b50\u6ef4\u6e9c\u6e9c\u5730\u4e00\u8f6c\uff0c\u5251\u5149\u8fc7\u5904\uff0c\u4e09\u6761\u5927\u6c49\u5df2\u662f\u9aa8\u788c\u788c\u5730 \u6eda\u4e0b\u4e86\u5c71\u5761\u3002\u539f\u6765\u5b5f\u660e\u971e\u4e00\u51fa\u624b\u4fbf\u662f\u523a\u5411\u5bf9\u65b9\u7684\u5173\u8282\u8981\u5bb3\uff0c\u867d\u65e0\u6027\u547d\u4e4b\u5fe7\uff0c\u4e2d\u4e86\u4e00\u5251\uff0c\u4fbf\u975e \u5012\u4e0b\u4e0d\u53ef\u3002 \u3000\u3000\u7ea2\u8863\u5587\u561b\u5927\u6012\uff0c\u4e5d\u73af\u9521\u6756\u4e00\u6296\uff0c\u62e6\u4f4f\u5b5f\u660e\u971e\u4fbf\u626b\uff0c\u5251\u6756\u76f8\u4ea4\uff0c\u6756\u4e0a\u7684\u4e5d\u4e2a\u94dc\u73af\u53ee\u53ee\u94db\u94db \u7684\u54cd\u4e2a\u4e0d\u505c\u3002 \u3000\u3000\u5b5f\u660e\u971e\u51b7\u7b11\u9053\uff1a\u201c\u8fd9\u7b97\u4ec0\u4e48\u73a9\u610f\uff01\u201d\u9752\u94a2\u5251\u626c\u7a7a\u4e00\u95ea\uff0c\u6d12\u4e0b\u4e86\u70b9\u70b9\u5bd2\u661f\u3002\u7ea2\u8863\u5587\u561b\u4e0d\u77e5 \u5979\u7684\u5251\u4ece\u4f55\u65b9\u653b\u6765\uff0c\u53ea\u597d\u628a\u4e5d\u73af\u9521\u6756\u821e\u6210\u4e86\u4e00\u9053\u5706\u5708\uff0c\u94dc\u73af\u6fc0\u8361\uff0c\u53ee\u94db\u4e4b\u58f0\u66f4\u662f\u54cd\u5f97\u5bc6\u4e86\u3002 \u3000\u3000\u5b5f\u660e\u971e\u521d\u65f6\u5e76\u4e0d\u628a\u94dc\u73af\u7684\u58f0\u97f3\u653e\u5728\u5fc3\u4e0a\uff0c\u54ea\u77e5\u542c\u5f97\u591a\u4e86\uff0c\u7adf\u662f\u4e0d\u7531\u81ea\u5df1\u5730\u5fc3\u4e2d\u70e6\u8e81\u8d77 \u6765\u3002\u539f\u6765\u8fd9\u4e00\u94dc\u94a2\u73af\u53d1\u51fa\u7684\u58f0\u54cd\uff0c\u6784\u6210\u4e86\u8282\u62cd\u6781\u5176\u51cc\u4e71\u7684\u566a\u97f3\uff0c\u8db3\u4ee5\u6270\u4eba\u5fc3\u795e\uff0c\u6b63\u662f\u7ea2\u8863\u5587 \u561b\u5236\u654c\u7684\u4e00\u4ef6\u201c\u6cd5\u5b9d\u201d\u3002\u6fc0\u6218\u4e2d\u5b5f\u660e\u971e\u5fc3\u795e\u7a0d\u5206\uff0c\u9669\u4e9b\u7ed9\u7ea2\u8863\u5587\u561b\u7684\u9521\u6756\u626b\u7740\u3002 \u3000\u3000\u5e78\u800c\u5b5f\u660e\u971e\u8f7b\u529f\u8d85\u7edd\uff0c\u5251\u5c16\u8f7b\u8f7b\u4e00\u70b9\u6756\u5934\uff0c\u501f\u7740\u5bf9\u65b9\u8fd9\u80a1\u529b\u9053\uff0c\u817e\u8eab\u8dc3\u8d77\uff0c\u4e00\u4e2a\u201c\u9e5e\u5b50 \u7ffb\u8eab\u201d\uff0c\u4ece\u95f4\u4e0d\u5bb9\u53d1\u4e4b\u9645\uff0c\u907f\u8fc7\u4e86\u8fd9\u4e00\u9669\u62db\u3002 \u3000\u3000\u4e00\u4e2a\u624b\u62a1\u5927\u5200\u7684\u6c49\u5b50\u548c\u4e00\u4e2a\u624b\u6301\u957f\u77db\u7684\u6b66\u571f\u4e24\u7ffc\u6284\u6765\uff0c\u9632\u5907\u5b5f\u660e\u971e\u9003\u8d70\u3002\u524d\u8005\u662f\u6df3\u4e8e\u8191 \u9080\u6765\u7684\u9ed1\u9053\u4eba\u7269\uff0c\u540e\u8005\u662f\u8499\u53e4\u201c\u795e\u7ffc\u8425\u201d\u7684\u4e00\u4e2a\u6b66\u4e0a\u5934\u76ee\uff0c\u8fd9\u4e24\u4eba\u7684\u6b66\u529f\u867d\u4e0d\u662f\u592a\u5f3a\uff0c\u5b5f\u660e \u971e\u60f3\u8981\u5728\u4e09\u62db\u4e24\u5f0f\u4e4b\u5185\u5c06\u4ed6\u4eec\u51fb\u523d\u5374\u662f\u4e0d\u80fd\uff0c\u7ea2\u8863\u5587\u561b\u4e00\u6296\u4e5d\u73af\u9521\u6756\uff0c\u53c8\u8ffd\u6765\u4e86\u3002 \u3000\u3000\u5b5f\u660e\u971e\u4ee5\u4e00\u654c\u4e09\uff0c\u53c8\u5fc5\u987b\u9547\u6151\u5fc3\u795e\uff0c\u65b9\u80fd\u4e0d\u53d7\u73af\u58f0\u6240\u6270\uff0c\u65f6\u95f4\u4e00\u957f\uff0c\u5f53\u7136\u662f\u8d8a\u6765\u8d8a\u611f\u5403 \u529b\u3002\u597d\u5728\u90a3\u7ea2\u8863\u5587\u561b\u4e5f\u597d\u4f3c\u6709\u70b9\u6c14\u529b\u4e0d\u7ee7\u7684\u6837\u5b50\uff0c\u5b5f\u660e\u971e\u8fd9\u624d\u53ef\u4ee5\u52c9\u5f3a\u652f\u6301\u3002 \u3000\u3000\u5c60\u51e4\u7684\u5f62\u52bf\u6bd4\u5b5f\u660e\u971e\u8fd8\u66f4\u9669\u6076\u3002\u8d77\u521d\u5979\u867d\u7136\u53ef\u4ee5\u4e0e\u6df3\u4e8e\u8191\u6253\u6210\u5e73\u624b\uff0c\u4f46\u6df3\u4e8e\u8191\u7684\u771f\u5b9e\u672c \u9886\u6bd5\u7adf\u5728\u5979\u4e4b\u4e0a\uff0c\u6597\u5230\u4e86\u4e94\u5341\u62db\u4e4b\u540e\uff0c\u5c60\u51e4\u7684\u5251\u6cd5\u5df2\u662f\u6e10\u6e10\u4e3a\u4ed6\u719f\u6089\uff0c\u6df3\u4e8e\u8191\u53cc\u94a7\u98de\u821e\uff0c\u4fe8 \u5982\u4e24\u9053\u94f6\u86c7\uff0c\u7d27\u7d27\u7684\u201c\u556e\u4f4f\u201d\u5c60\u51e4\u7684\u957f\u5251\uff0c\u6740\u5f97\u5c60\u51e4\u53ea\u6709\u62db\u67b6\u4e4b\u529f\uff0c\u6beb\u65e0\u8fd8\u624b\u4e4b\u529b\u3002 \u3000\u3000\u6b63\u5728\u5403\u7d27\uff0c\u5ffd\u542c\u5f97\u4e00\u7247\u8e44\u58f0\uff0c\u9707\u64bc\u8352\u8c37\uff0c\u6765\u52bf\u4e4b\u75be\uff0c\u4fe8\u5982\u66b4\u98ce\u9aa4\u96e8\u3002\u6df3\u4e8e\u8191\u7b11\u9053\uff1a\u201c\u4ed6 \u4eec\u8fd9\u4e2a\u65f6\u5019\u624d\u6765\uff0c\u8fd9\u573a\u620f\u90fd\u5df2\u7ecf\u715e\u79d1\u4e86\u3002\u563f\uff0c\u563f\uff0c\u65e9\u77e5\u5c60\u767e\u57ce\u624b\u4e0b\u5982\u6b64\u4e0d\u6d4e\uff0c\u54b1\u4eec\u5b9e\u5728\u662f\u65e0 \u987b\u77e5\u4f1a\u4ed6\u4eec\u6765\u7684\u3002\u201d \u3000\u3000\u6df3\u4e8e\u8191\u9080\u6765\u7684\u4e24\u4e2a\u670b\u53cb\u7b11\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u4e5f\u8fd8\u662f\u5feb\u70b9\u628a\u8fd9\u4e24\u4e2a\u96cc\u513f\u62ff\u4e0b\u7684\u597d\uff0c\u514d\u5f97\u4ed6\u4eec\u6765 \u4e86\u5206\u529f\u3002\u201d\u8fd9\u4e24\u4eba\u62bd\u51fa\u5175\u5668\uff0c\u4e00\u4e2a\u5954\u5411\u5c60\u51e4\uff0c\u4e00\u4e2a\u5954\u5411\u5b5f\u660e\u971e\u3002\u6df3\u4e8e\u8191\u53eb\u9053\uff1a\u201c\u5c0f\u5fc3\uff0c\u4f60\u53ef \u522b\u628a\u6211\u7684\u65b0\u5a18\u5b50\u5f04\u4f24\u4e86\u3002\u201d \u3000\u3000\u5c60\u3001\u5b5f\u4e8c\u4eba\u6c14\u5f97\u53d1\u6296\uff0c\u5fc3\u4e2d\u4ff1\u662f\u60f3\u9053\uff1a\u201c\u6740\u5f97\u4e00\u4e2a\u5c31\u662f\u4e00\u4e2a\uff0c\u6740\u4e0d\u4e86\u5b81\u53ef\u81ea\u5c3d\u800c\u4ea1\u3002\u65e0 \u8bba\u5982\u4f55\u7edd\u4e0d\u80fd\u843d\u5728\u4ed6\u4eec\u624b\u4e0a\u3002\u201d \u3000\u3000\u5979\u4eec\u90fd\u4ee5\u4e3a\u6765\u7684\u4e43\u662f\u654c\u4eba\uff0c\u6b8a\u4e0d\u77e5\u5728\u654c\u4eba\u4e4b\u4e2d\uff0c\u5979\u4eec\u7684\u6551\u661f\u4e5f\u8ddf\u7740\u6765\u4e86\u3002 \u3000\u3000\u4e14\u8bf4\u674e\u601d\u5357\u548c\u6768\u5a49\u4e00\u8def\u5feb\u9a6c\u75be\u9a70\uff0c\u4e0d\u77e5\u4e0d\u89c9\u5df2\u662f\u65e5\u5934\u8fc7\u5348\uff0c\u79bb\u5f00\u90a3\u5ea7\u78e8\u623f\uff0c\u4f30\u8ba1\u81f3\u5c11\u4e5f \u5728\u4e94\u5341\u91cc\u5f00\u5916\u4e86\u3002\u5c60\u51e4\u66fe\u7ecf\u544a\u8bc9\u674e\u601d\u5357\uff0c\u8bf4\u662f\u5979\u4eec\u4f4f\u5728\u201c\u9644\u8fd1\u201d\u8774\u8776\u8c37\uff0c\u674e\u601d\u5357\u53ea\u9053\u65e9\u5df2\u8fc7 \u53bb\u4e86\uff0c\u5374\u4e0d\u77e5\u8774\u8776\u8c37\u8fd8\u5728\u524d\u9762\uff0c\u4e0d\u8fc7\u5374\u5e76\u4e0d\u662f\u5728\u8fd9\u4e00\u6761\u8def\u4e0a\u3002 \u3000\u3000\u674e\u601d\u5357\u9ed8\u5ff5\u5c0f\u65f6\u5019\u8bfb\u8fc7\u7684\u4e00\u9996\u8bd7\uff1a\u201c\u4eba\u5fc3\u5230\u5904\u77e5\u4f55\u4f3c\uff1f\u77e5\u662f\u98de\u9e3f\u8e0f\u96ea\u6ce5\u3002\u6ce5\u4e0a\u5076\u7136\u65ad\u6307 \u722a\uff0c\u9e3f\u98de\u90a3\u590d\u8ba1\u4e1c\u897f\uff1f\u201d\u60f3\u8d77\u5b5f\u660e\u971e\u7684\u77e5\u5df1\u4e4b\u60c5\uff0c\u5c3d\u7ba1\u662f\u5fc3\u5c24\u522b\u5ff5\uff0c\u4e5f\u81ea\u4e0d\u7981\u60d8\u7136\u3002 \u3000\u3000\u6b63\u5728\u6005\u6005\u60d8\u60d8\u4e4b\u9645\uff0c\u4ed6\u4eec\u521a\u597d\u5728\u5230\u4e00\u5c94\u8def\u4e0a\u3002\u5ffd\u89c1\u524d\u557c\u6765\u4e86\u4e00\u961f\u4eba\u9a6c\uff0c\u5171\u6709\u4eba\u4e03\u9a91\u4e4b \u591a\uff0c\u6b63\u662f\u6628\u665a\u7b2c\u4e00\u6279\u51c6\u5230\u78e8\u80a9\u641c\u7d22\u7684\u90a3\u961f\u4eba\u9a6c\u3002 \u3000\u3000\u4e3a\u9996\u7684\u90a3\u4e2a\u8499\u53e4\u6b66\u58eb\u7eb5\u9a6c\u8fc7\u6765\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u674e\u516c\u5b50\uff0c\u4f60\u6765\u5f97\u6b63\u597d\uff01\u201d \u3000\u3000\u6628\u665a\u4e0a\u8fd9\u6279\u4eba\u641c\u7d22\u78e8\u623f\u4e4b\u65f6\uff0c\u674e\u601d\u5357\u56e0\u4e3a\u6709\u6210\u5927\u5fe0\u8ba1\u6240\u8d50\u7ed9\u5730\u7684\u94c1\u80ce\u5f13\uff0c\u53c8\u6709\u660e\u6167\u516c\u4e3b \u6240\u8d60\u7684\u201c\u91d1\u5e10\u4ee4\u201d\uff0c\u8fd9\u4e2a\u8499\u5360\u6b66\u58eb\u731c\u4e0d\u900f\u4ed6\u7684\u8eab\u4efd\uff0c\u4ee5\u4e3a\u4ed6\u662f\u5949\u6210\u5409\u601d\u6c57\u4e4b\u547d\uff0c\u5230\u897f\u590f\u6765\u8d1f \u6709\u79d8\u5bc6\u4efb\u52a1\u7684\uff0c\u662f\u4ee5\u5bf9\u4ed6\u751a\u4e3a\u5c0a\u656c\uff0c\u6beb\u4e0d\u7559\u96be\u3002\u4f46\u5982\u4eca\u674e\u601d\u5357\u78b0\u4e0a\u4e86\u4ed6\uff0c\u5374\u662f\u4e0d\u7981\u6709\u70b9\u60f4\u60f4 \u4e0d\u5b89\u4e86\u3002\u201c\u4e0d\u77e5\u4ed6\u77e5\u9053\u4e86\u6211\u7684\u8eab\u4efd\u6ca1\u6709\uff1f\u59d1\u4e14\u6577\u884d\u4ed6\u51e0\u53e5\u518d\u8bf4\uff0c\u8981\u662f\u6df7\u4e0d\u8fc7\u53bb\u7684\u8bdd\uff0c\u90a3\u4e5f\u53ea \u597d\u62fc\u4e86\u3002\u201d\u674e\u601d\u5357\u5fc3\u60f3\u3002\u4e00\u5fc3\u5ff5\u672a\u5df2\uff0c\u8499\u53e4\u6b66\u58eb\u5df2\u662f\u5728\u674e\u601d\u5357\u9762\u524d\u52d2\u4f4f\u5750\u9a91\uff0c\u4e00\u526f\u6025\u7740\u7b49\u5f85 \u674e\u601d\u5357\u56de\u7b54\u7684\u795e\u60c5\u3002\u674e\u601d\u5357\u8a00\u4e0d\u7531\u8877\u5730\u7b11\u9053\uff1a\u201c\u771f\u662f\u5de7\u9047\uff0c\u4f60\u6709\u4ec0\u4e48\u4e8b\u4e48\uff1f\u201d \u3000\u3000\u8499\u5360\u6b66\u58eb\u7b11\u9053\uff1a\u201c\u597d\u6559\u4f60\u6b22\u559c\uff0c\u6211\u8981\u641c\u6355\u7684\u4eba\uff0c\u5982\u4eca\u5374\u5df2\u7ecf\u77e5\u9053\u4e0b\u843d\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u77e5\u9053\u4ed6\u8981\u641c\u6355\u7684\u4eba\u4e43\u662f\u9f99\u521a\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u89c1\u4f60\u7684\u9b3c\uff0c\u9f99\u521a\u65e9\u5df2\u5316\u4e86\u9aa8\u7070\u5566\u3002\u201d \u53ef\u662f\u53c8\u4e0d\u514d\u6709\u70b9\u60ca\u7591\uff1a\u201c\u83ab\u975e\u4ed6\u6628\u665a\u5df2\u7ecf\u770b\u51fa\u4e86\u4ec0\u4e48\u7834\u7efd\uff0c\u5982\u4eca\u6765\u8bd5\u8bd5\u6211\u7684\u53e3\u98ce\uff1f\u201d\u5f53\u4e0b\u52c9 \u5f3a\u7b11\u9053\uff1a\u201c\u90a3\u5f88\u597d\u5440\uff0c\u4f60\u53ef\u4ee5\u4ea4\u5dee\u4e86\u3002\u201d \u3000\u3000\u8499\u53e4\u6b66\u4e0a\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u53ef\u5f97\u4f60\u5e2e\u5e2e\u5fd9\u4e86\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u5bf9\u4e0d\u4f4f\uff0c\u6211\u4eec\u53ef\u662f\u53e6\u6709\u8981\u4e8b\u5728\u8eab\u3002\u201d \u3000\u3000\u8499\u53e4\u6b66\u58eb\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u6709\u4e8b\u3002\u4f46\u4f60\u53ef\u77e5\u9053\u90a3\u59d3\u9f99\u7684\u5c0f\u5b50\u662f\u4ec0\u4e48\u4eba\u5417\uff1f\u54c8\u54c8\uff0c\u4ed6\u53ef\u662f\u5c60 \u767e\u57ce\u7684\u5f1f\u5b50\u5440\u3002\u8fd8\u6709\u66f4\u4ee4\u4eba\u6b22\u559c\u7684\u6d88\u606f\u5462\uff0c\u4f60\u8981\u4e0d\u8981\u542c\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u627e\u7740\u8fd9\u5c0f\u5b50\u4e86\uff1f\u201d\u5f53\u7136\u4ed6\u77e5\u9053\u8499\u53e4\u6b66\u58eb\u6c38\u8fdc\u627e\u4e0d\u7740\u9f99\u521a \u7684\uff0c\u56e0\u4e3a\u9f99\u521a\u5df2\u7ecf\u6b7b\u4e86\u3002\u4ed6\u53ea\u662f\u60f3\u8bd5\u63a2\u4ed6\u8fd8\u77e5\u9053\u591a\u5c11\u4e8b\u60c5\u800c\u5df2\u3002 \u3000\u3000\u8499\u53e4\u6b66\u58eb\u7b11\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u5012\u6ca1\u627e\u7740\uff0c\u4f46\u5c60\u767e\u57ce\u7684\u624b\u4e0b\u6709\u4e00\u6279\u4eba\u6765\u5230\u4e86\u897f\u590f\uff0c\u4ed6\u4eec\u7684\u4e0b\u843d \u6211\u521a\u624d\u77e5\u9053\uff0c\u5c31\u5728\u79bb\u6b64\u4e0d\u8fdc\u7684\u8774\u8776\u8c37\uff0c\u8fd9\u5c31\u662f\u6211\u8981\u6c42\u4f60\u5e2e\u5fd9\u7684\u539f\u56e0\u4e86\u3002\u201d \u3000\u3000\u8499\u53e4\u6b66\u58eb\u4f3c\u4e4e\u662f\u6015\u674e\u601d\u5357\u4e0d\u80af\u7b54\u5e94\uff0c\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u96be\u5f97\u5c06\u8fd9\u73ed\u4eba\u4e00\u7f51\u6253\u5c3d\uff0c\u8fd9\u53ef\u662f\u4e2a\u5927 \u5927\u7684\u529f\u52b3\u5462\uff01\u542c\u8bf4\u5c60\u767e\u57ce\u7684\u5973\u513f\u4e5f\u5728\u5176\u4e2d\uff0c\u53e6\u5916\u8fd8\u6709\u4e2a\u5341\u5206\u7f8e\u8c8c\u7684\u6c49\u4eba\u59d1\u5a18\u3002\u53ea\u8981\u6349\u5230\u8fd9\u4e24 \u4e2a\u5973\u5b50\uff0c\u732e\u7ed9\u5927\u6c57\uff0c\u5c31\u5df2\u80dc\u4e8e\u7ed9\u5927\u6c57\u6253\u4e0b\u4e00\u5ea7\u57ce\u6c60\uff01\u59d3\u9f99\u90a3\u5c0f\u5b50\u662f\u8fd9\u4e9b\u4eba\u4e00\u4f19\uff0c\u60f3\u5fc5\u4e5f\u662f\u5728 \u8774\u8776\u8c37\u3002\u4f46\u5982\u4eca\u6211\u4e0d\u5728\u4e4e\u4ed6\u4e86\uff0c\u5c60\u767e\u57ce\u7684\u5973\u513f\u6bd4\u4ed6\u7d27\u8981\u5f97\u591a\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u672c\u6765\u662f\u60f3\u6577\u884d\u4ed6\u51e0\u53e5\u7684\uff0c\u73b0\u5728\u53ef\u4e0d\u80fd\u4e0d\u8ba4\u771f\u8003\u8651\u4e86\uff0c\u201c\u53bb\u5462\u8fd8\u662f\u4e0d\u53bb\uff1f\u201d \u3000\u3000\u8499\u53e4\u6b66\u58eb\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u4f60\u4e5f\u8bb8\u6709\u5f88\u7d27\u8981\u7684\u4e8b\u60c5\uff0c\u4f46\u8fd9\u4ef6\u4e8b\u66f4\u662f\u5341\u5206\u7d27\u8981\uff01\u5c60\u767e\u57ce\u624b\u4e0b\u6b66 \u529f\u5fc5\u5b9a\u4e0d\u51e1\uff0c\u6211\u4eec\u5df2\u7ecf\u6709\u4eba\u5148\u5230\u8774\u8776\u8c37\u53bb\uff0c\u4f46\u8fd8\u6015\u4e0d\u80fd\u5c06\u4ed6\u4eec\u4e00\u7f51\u6253\u5c3d\u3002\u53cd\u6b63\u8774\u8776\u8c37\u79bb\u6b64\u4e0d \u8fdc\uff0c\u4f60\u4f55\u59a8\u53bb\u8d81\u8d81\u70ed\u95f9\uff0c\u4e5f\u4e50\u5f97\u5206\u70b9\u529f\u52b3\uff01\u201d\u6b63\u662f\uff1a \u3000\u3000\u65e7\u68a6\u5c18\u5c01\u4f11\u518d\u542f\uff0c\u6545\u4eba\u76f8\u89c1\u4e5f\u4f24\u60c5\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b\u2014\u2014\u7b2c\u5341\u4e09\u56de\u3000\u826f\u53cb\u76f8\u9022\u5f92\u6005\u6005\u3000\u592b\u59bb\u79bb\u6563\u6068\u7ef5\u7ef5 \u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b \u7b2c\u5341\u4e09\u56de\u3000\u826f\u53cb\u76f8\u9022\u5f92\u6005\u6005\u3000\u592b\u59bb\u79bb\u6563\u6068\u7ef5\u7ef5 \u3000\u3000\u674e\u601d\u5357\u672a\u66fe\u56de\u7b54\uff0c\u6768\u5a49\u5df2\u5148\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u90fd\u662f\u7ed9\u5927\u6c57\u529e\u4e8b\u7684\uff0c\u7406\u5e94\u5f7c\u6b64\u5e2e\u5fd9\u3002\u5c06\u519b\uff0c\u4f60 \u8bf4\u8fd9\u6837\u7684\u8bdd\uff0c\u90a3\u662f\u592a\u89c1\u5916\u4e86\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u5176\u5b9e\u5df2\u662f\u6025\u7740\u8981\u53bb\u6551\u63f4\u5b5f\u660e\u971e\u7684\u4e86\uff0c\u53ea\u662f\u6015\u6768\u5a49\u591a\u5fc3\u800c\u5df2\uff0c\u542c\u4e86\u6768\u5a49\u8fd9\u4e48\u8bf4\uff0c\u9a6c \u4e0a\u987a\u6c34\u63a8\u821f\u5730\u8bf4\u9053\uff1a\u201c\u662f\u5440\uff0c\u5927\u5bb6\u90fd\u662f\u81ea\u5df1\u4eba\uff0c\u4f55\u987b\u8bf4\u5230\u916c\u52b3\u4e8c\u5b57\uff1f\u4f60\u53eb\u6211\u4eec\u53bb\u5206\u4eab\u529f\u52b3\uff0c \u6211\u4eec\u5012\u4e0d\u597d\u610f\u601d\u53bb\u4e86\u3002\u201d \u3000\u3000\u8499\u53e4\u6b66\u571f\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u96be\u5f97\u4e24\u4f4d\u5982\u6b64\u9ad8\u5174\uff0c\u6211\u5012\u662f\u8bf4\u9519\u8bdd\u4e86\u3002\u597d\uff0c\u8fd9\u5c31\u53bb\u5427\u3002\u201d \u3000\u3000\u4e14\u8bf4\u5c60\u51e4\u3001\u5b5f\u660e\u971e\u4e8c\u4eba\u6b63\u5728\u5403\u7d27\uff0c\u5ffd\u89c1\u65b0\u6765\u7684\u8fd9\u961f\u654c\u4eba\u4e4b\u4e2d\uff0c\u7adf\u6709\u674e\u601d\u5357\u548c\u6768\u5a49\u5728\u5185\uff0c \u90fd\u662f\u5927\u4e3a\u60ca\u8be7\u3002\u5c24\u5176\u662f\u5b5f\u660e\u971e\uff0c\u5979\u4ee5\u4e3a\u662f\u89c1\u4e0d\u5230\u674e\u601d\u5357\u7684\u4e86\uff0c\u60f3\u4e0d\u5230\u674e\u601d\u5357\u7a81\u5982\u5176\u6765\uff0c\u5b5f\u660e \u971e\u4e0d\u7531\u5f97\u60f4\u60f4\u4e0d\u5b89\uff0c\u6697\u81ea\u5bfb\u601d\uff1a\u201c\u96be\u9053\u674e\u601d\u5357\u5f53\u771f\u5df2\u7ecf\u53d8\u8282\uff0c\u4ed6\u8bf4\u7ed9\u9f99\u521a\u542c\u7684\u90a3\u4e9b\u8bdd\uff0c\u53ea\u662f \u60f3\u54c4\u9a97\u6211\u4eec\u7684\u4e48\uff1f\u4e0e\u4ed6\u540c\u6765\u7684\u8fd9\u4e2a\u5973\u5b50\u60f3\u5fc5\u5c31\u662f\u51e4\u59d0\u8bf4\u7684\u90a3\u4e2a\u6768\u5a49\u4e86\uff0c\u4e14\u770b\u4ed6\u4eec\u600e\u6837\uff1f\u201d \u3000\u3000\u6b64\u65f6\uff0c\u8fd9\u4e00\u961f\u4eba\u5df2\u7ecf\u7eb7\u7eb7\u8df3\u4e0b\u5750\u9a91\uff0c\u8dd1\u4e0a\u5c71\u5761\uff0c\u51c6\u5907\u6d3b\u6349\u5c60\u3001\u5b5f\u4e8c\u5973\u3002\u90a3\u7ea2\u8863\u5587\u561b\u4ea6\u5df2 \u770b\u89c1\u4e86\u674e\u6069\u5357\u4e86\u3002 \u3000\u3000\u8fd9\u7ea2\u8863\u5587\u561b\u6b63\u662f\u548c\u8363\u5f69\u53ca\u90a3\u897f\u590f\u6c49\u5b50\u4e00\u540c\u53bb\u641c\u7d22\u78e8\u623f\u7684\u90a3\u4e2a\u5587\u561b\u3002\u6628\u665a\u90a3\u4e00\u4f4d\u4ed6\u7684\u4e24\u4e2a \u540c\u4f34\u90fd\u7ed9\u6740\u6b7b\uff0c\u4ed6\u81ea\u5df1\u4e5f\u7ed9\u674e\u601d\u5357\u523a\u4f24\uff0c\u4fa5\u5e78\u9003\u4e86\u51fa\u6765\uff0c\u540e\u6765\u624d\u78b0\u4e0a\u6df3\u4e8e\u8191\u7684\u3002 \u3000\u3000\u7ea2\u8863\u5587\u561b\u4e00\u773c\u77a5\u89c1\u674e\u601d\u5357\uff0c\u8fd9\u4e00\u60ca\u5f53\u771f\u662f\u975e\u540c\u5c0f\u53ef\uff0c\u8fde\u5fd9\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u4e2d\u95f4\u6709\u5978\u7ec6\uff0c\u6709 \u5978\u7ec6\uff01\u201d \u3000\u3000\u90a3\u8499\u53e4\u6b66\u58eb\u4e86\u83ab\u540d\u5176\u5999\uff0c\u53eb\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u4f60\u8bf4\u8c01\u662f\u2014\u2014\u201d\u4ed6\u8fd8\u4e0d\u600e\u4e48\u76f8\u4fe1\uff0c\u6b63\u8981\u67e5\u95ee\uff0c \u201c\u5978\u7ec6\u201d\u4e8c\u5b57\u5c1a\u672a\u8bf4\u51fa\u53e3\u6765\uff0c\u9661\u7136\u53ea\u89c9\u540e\u5fc3\u4e00\u51c9\uff0c\u674e\u601d\u5357\u5df2\u662f\u5230\u4e86\u4ed6\u7684\u80cc\u540e\uff0c\u4ee5\u8fc5\u96f7\u4e0d\u53ca\u63a9 \u8033\u7684\u624b\u6cd5\uff0c\u4e00\u5251\u4ece\u4ed6\u540e\u5fc3\u523a\u5165\uff0c\u524d\u5fc3\u7a7f\u51fa\uff01\u4e0e\u6b64\u540c\u65f6\uff0c\u6768\u5a49\u4e5f\u98de\u5feb\u5730\u52a8\u624b\uff0c\u5979\u7684\u5251\u6cd5\u6bd4\u674e\u601d \u5357\u66f4\u72e0\u66f4\u51c6\uff0c\u5530\u5530\u4e24\u5251\uff0c\u523a\u4f24\u4e86\u4e24\u4e2a\u897f\u590f\u6b66\u58eb\u7684\u5173\u8282\u8981\u5bb3\uff0c\u90a3\u4e24\u4e2a\u6c49\u5b50\u9aa8\u788c\u788c\u5730\u6eda\u4e0b\u4e86\u5c71 \u5761\u3002 \u3000\u3000\u674e\u601d\u5357\u62bd\u51fa\u5b9d\u5251\uff0c\u559d\u9053\uff1a\u201c\u6211\u662f\u5927\u6c49\u7537\u513f\uff0c\u5c82\u80fd\u529b\u864e\u4f5c\u6005\uff1f\u8bf4\u7ed9\u4f60\u542c\uff0c\u4e5f\u597d\u53eb\u4f60\u6b7b\u5f97\u660e \u767d\uff01\u201d\u90a3\u8499\u53e4\u6b66\u58eb\u53cc\u773c\u7ffb\u767d\uff0c\u5927\u53eb\u4e00\u58f0\uff0c\u6c14\u7edd\u800c\u4ea1\u3002 \u3000\u3000\u8fd9\u4e00\u961f\u4eba\u4e4b\u4e2d\uff0c\u4ee5\u90a3\u8499\u53e4\u6b66\u58eb\u672c\u9886\u6700\u5f3a\uff0c\u5176\u6b21\u5c31\u662f\u90a3\u4e24\u4e2a\u897f\u590f\u6b66\u58eb\uff0c\u8fd9\u4e09\u4e2a\u4eba\u4e00\u6b7b\u4e24 \u4f24\uff0c\u53e6\u5916\u56db\u4e2a\u4eba\u614c\u4e0d\u8fed\u5730\u9003\u8dd1\u3002 \u3000\u3000\u6768\u5a49\u53eb\u9053\uff1a\u201c\u5c60\u59d0\u59d0\u4e0d\u8981\u7740\u614c\uff0c\u6211\u6765\u5e2e\u4f60\uff01\u201d\u539f\u6765\u5979\u662f\u6709\u5fc3\u8ba9\u674e\u601d\u5357\u53bb\u52a9\u5b5f\u660e\u971e\uff0c\u6545\u800c \u81ea\u5df1\u62a2\u5148\u6765\u52a0\u5165\u5c60\u51e4\u8fd9\u8fb9\u3002 \u3000\u3000\u6df3\u4e8e\u8191\u542c\u5f97\u80cc\u540e\u91d1\u5203\u5288\u98ce\u4e4b\u58f0\uff0c\u53cd\u624b\u4e00\u5251\uff0c\u8361\u5f00\u6768\u5a49\u7684\u5251\u62db\u3002\u5c60\u51e4\u817e\u51fa\u624b\u6765\uff0c\u5012\u7eb5\u4e09 \u6b65\uff0c\u559d\u4e00\u58f0\uff1a\u201c\u6253\uff01\u201d\u4e09\u652f\u6bd2\u9f99\u9556\uff0c\u95ea\u7535\u822c\u5c04\u51fa\u3002 \u3000\u3000\u4e09\u652f\u98de\u9556\u6253\u51fa\uff0c\u53d8\u5316\u5404\u4e2a\u4e0d\u540c\u3002 \u3000\u3000\u4f7f\u9752\u94dc\u68d2\u7684\u90a3\u4e2a\u6c49\u5b50\uff0c\u6c14\u529b\u9887\u5927\uff0c\u8df3\u8dc3\u7684\u529f\u592b\u5374\u662f\u4e0d\u751a\u7075\u6d3b\u3002\u4ed6\u628a\u9752\u94dc\u68d2\u4e00\u6321\uff0c\u6ca1\u6709\u6321 \u7740\u3002\u5f85\u8981\u8f6c\u8eab\u95ea\u907f\uff0c\u54ea\u91cc\u8fd8\u6765\u5f97\u53ca\uff0c\u53ea\u89c9\u80c1\u4e0b\u4e00\u9ebb\uff0c\u8170\u90e8\u5df2\u662f\u7740\u4e86\u98de\u9556\uff0c\u6643\u4e86\u4e24\u6643\uff0c\u767b\u65f6\u5c31 \u5012\u4e0b\u53bb\u4e86\u3002\u201d \u3000\u3000\u4f7f\u6708\u7259\u5f2f\u5200\u7684\u90a3\u4e2a\u6c49\u5b50\uff0c\u5200\u6cd5\u4f7f\u5f97\u6cfc\u98ce\u4e5f\u4f3c\uff0c\u970d\u5730\u4e00\u4e2a\u201c\u51e4\u70b9\u5934\u201d\uff0c\u957f\u5200\u4e00\u4e3e\uff0c\u4e00\u62db \u201c\u4e3e\u706b\u71ce\u5929\u201d\uff0c\u201c\u545c\u201d\u7684\u4e00\u58f0\uff0c\u628a\u98de\u9556\u6253\u843d\u3002\u4f46\u867d\u7136\u6253\u843d\uff0c\u53ea\u56e0\u52b2\u529b\u8f83\u5f31\uff0c\u90a3\u652f\u98de\u9556\u51e0\u4e4e\u662f \u8d34\u7740\u4ed6\u7684\u5934\u76ae\u64e6\u8fc7\u3002\u90a3\u6c49\u5b50\u95fb\u5f97\u4e00\u80a1\u6de1\u6de1\u7684\u8165\u5473\uff0c\u9a87\u7136\u53eb\u9053\uff1a\u201c\u6bd2\u9f99\u8198\uff01\u201d \u3000\u3000\u5c60\u51e4\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u5012\u8bc6\u8d27\uff01\u201d\u7b2c\u4e09\u652f\u9556\u65e9\u5df2\u51fa\u624b\uff0c\u76f4\u53d6\u6df3\u4e8e\u8191\u7684\u54bd\u5589\u3002\u6df3\u4e8e\u8191\u597d\u751f \u4e86\u5f97\uff0c\u5de6\u624b\u94a9\u4e00\u62c9\uff0c\u53f3\u624b\u94a9\u4e00\u94db\uff0c\u8fd9\u652f\u98de\u9556\u767b\u65f6\u6539\u4e86\u65b9\u5411\uff0c\u5411\u6768\u5a49\u98de\u53bb\u3002 \u3000\u3000\u6768\u5a49\u8fd0\u5251\u4e00\u6311\uff0c\u98de\u9556\u65b9\u5411\u518d\u8f6c\uff0c\u4f7f\u6708\u7259\u5f2f\u5200\u7684\u90a3\u4e2a\u6c49\u5b50\u60ca\u9b42\u672a\u5b9a\uff0c\u98de\u9556\u53c8\u5c04\u5230\u4e86\u4ed6\u7684\u9762 \u95e8\uff0c\u8fd9\u6b21\u4ed6\u53ef\u95ea\u8eb2\u4e0d\u5f00\u4e86\uff0c\u201c\u535c\u201d\u7684\u4e00\u58f0\uff0c\u98de\u9556\u63d2\u5230\u4e86\u4ed6\u7684\u80a9\u5934\uff0c\u201c\u6bd2\u9f99\u9556\u201d\u89c1\u8840\u5c01\u5589\uff0c\u8fd9 \u6c49\u5b50\u5589\u5934\u54af\u54af\u4f5c\u54cd\uff0c\u8fd8\u672a\u53eb\u5f97\u51fa\u58f0\uff0c\u5df2\u662f\u4e03\u7a8d\u6d41\u8840\uff0c\u5012\u5730\u800c\u4ea1\u3002 \u3000\u3000\u6df3\u4e8e\u8191\u53cc\u94a9\u98de\u821e\uff0c\u62a4\u7740\u5168\u8eab\uff0c\u53eb\u9053\uff1a\u201c\u7f20\u7740\u5979\uff0c\u4e0d\u80fd\u8ba9\u5979\u653e\u6697\u5668\uff01\u201d\u6df3\u4e8e\u8191\u8fd9\u8fb9\u8fd8\u6709\u516d \u4e03\u4e2a\u4eba\uff0c\u4ece\u5e10\u7bf7\u91cc\u90fd\u8df3\u51fa\u6765\uff0c\u8896\u7bad\u3001\u98de\u8757\u77f3\u3001\u94c1\u76d4\u6851\u2026\u2026\u5404\u79cd\u6697\u5668\u7eb7\u98de\uff0c\u51b0\u96f9\u822c\u7684\u5411\u5c60\u51e4\u5c04 \u53bb\uff0c\u5c60\u51e4\u6325\u5251\u906e\u62e6\uff0c\u5077\u7a7a\u53d1\u51fa\u6bd2\u9f99\u9556\u8fd8\u51fb\u3002 \u3000\u3000\u53cc\u65b9\u4ee5\u6697\u5668\u4ea4\u950b\uff0c\u5c60\u51e4\u8eab\u6cd5\u8f7b\u7075\uff0c\u5251\u6cd5\u7cbe\u5999\uff0c\u51b0\u96f9\u822c\u7684\u6697\u5668\u8fde\u5979\u7684\u8863\u89d2\u90fd\u6cbe\u4e0d\u7740\uff0c\u5bf9\u65b9 \u53c8\u7ed9\u5979\u7684\u6bd2\u9f99\u9556\u6740\u4e86\u4e24\u4e2a\u3002\u53ef\u662f\u4f59\u4e0b\u7684\u4e94\u4e2a\u4eba\u6bd5\u7adf\u662f\u653b\u5230\u4e86\u5979\u7684\u8eab\u65c1\uff0c\u4e00\u5230\u8fd1\u8eab\u640f\u6597\uff0c\u5c60\u51e4 \u7684\u6697\u5668\u5df2\u662f\u4e0d\u80fd\u518d\u53d1\u3002 \u3000\u3000\u6768\u5a49\u66ff\u5c60\u51e4\u89e3\u56f4\u4e4b\u9645\uff0c\u674e\u601d\u5357\u4ea6\u5df2\u5230\u4e86\u5b5f\u660e\u971e\u8fd9\u8fb9\u3002\u56f4\u653b\u5b5f\u660e\u971e\u7684\u5171\u662f\u4e09\u4eba\uff1a\u7ea2\u8863\u5587 \u561b\u3001\u4e00\u4e2a\u4f7f\u5927\u6240\u5200\u7684\u6c49\u5b50\u3001\u548c\u4e00\u4e2a\u4f7f\u957f\u77db\u7684\u8499\u53e4\u6b66\u58eb\uff0c\u524d\u8005\u662f\u6df3\u4e8e\u8191\u9080\u6765\u7684\u9ed1\u9053\u4eba\u7269\uff0c\u540e\u8005 \u662f\u6728\u534e\u9ece\u624b\u4e0b\u201c\u795e\u7ffc\u8425\u201d\u7684\u5934\u76ee\uff0c\u6b66\u529f\u90fd\u5f88\u4e0d\u5f31\u3002 \u3000\u3000\u674e\u601d\u5357\u6765\u5f97\u6b63\u662f\u53ca\u65f6\uff0c\u5530\u7684\u4e00\u5251\uff0c\u62e8\u5f00\u4e86\u90a3\u6b66\u58eb\u7684\u957f\u77db\uff0c\u8fc5\u5373\u5251\u950b\u4e00\u8f6c\uff0c\u653b\u5f97\u90a3\u4f7f\u5927\u5200 \u7684\u6c49\u5b50\u8fde\u5fd9\u6551\u62db\u3002\u5b5f\u660e\u971e\u6240\u53d7\u7684\u538b\u529b\u5927\u5927\u51cf\u8f7b\uff0c\u7cbe\u795e\u9661\u632f\uff0c\u8fd0\u5251\u5982\u98ce\uff0c\u654c\u4f4f\u4e86\u7ea2\u8863\u5587\u561b\u7684\u4e5d \u73af\u9521\u6756\u3002 \u3000\u3000\u674e\u601d\u5357\u662f\u4e2a\u6b66\u5b66\u7684\u5927\u884c\u5bb6\uff0c\u548c\u90a3\u4f7f\u5927\u5200\u7684\u6c49\u5b50\u8fc7\u4e86\u4e00\u62db\uff0c\u5df2\u77e5\u4ed6\u7684\u4e0b\u76d8\u529f\u592b\u751a\u4e3a\u575a\u56fa\uff0c \u5fc5\u987b\u7528\u201c\u4ee5\u5de7\u964d\u529b\u201d\u7684\u6253\u6cd5\u65b9\u80fd\u80dc\u4ed6\u3002\u5f53\u4e86\u4e0d\u5bb9\u4ed6\u518d\u62a2\u653b\u52bf\uff0c\u7acb\u5373\u659c\u8eab\u4e0a\u6b65\uff0c\u53f3\u624b\u4e3e\u5251\u5148\u8361 \u5f00\u8499\u53e4\u6b66\u58eb\u7684\u957f\u77db\uff0c\u5de6\u638c\u4ece\u5251\u5e95\u7a7f\u51fa\uff0c\u5411\u90a3\u4f7f\u5927\u5200\u7684\u6c49\u5b50\u8155\u4e0b\u4e00\u64a9\uff0c\u4f7f\u5200\u7684\u6c49\u5b50\u6012\u9053\uff1a\u201c\u5c0f \u5b50\u80c6\u6562\u6b3a\u6211\u3002\u201d\u957f\u5200\u659c\u5288\u4e0b\u6765\u3002\u674e\u601d\u5357\u8eab\u8eaf\u4e00\u77ee\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u5251\u67c4\u5df2\u662f\u649e\u5f00\u4ed6\u7684\u957f \u5200\uff0c\u90a3\u6c49\u5b50\u8170\u80c1\u4e00\u9ebb\uff0c\u7ed9\u674e\u601d\u5357\u70b9\u4e2d\u4e86\u7a74\u9053\u3002 \u3000\u3000\u4f7f\u957f\u77db\u7684\u8499\u53e4\u6b66\u58eb\u6b63\u81ea\u5411\u674e\u601d\u5357\u523a\u6765\uff0c\u674e\u601d\u5357\u8f7b\u8f7b\u4e00\u95ea\uff0c\u95ea\u5230\u4e86\u4f7f\u5927\u5200\u7684\u6c49\u5b50\u80cc\u540e\u3002\u8fd9 \u6c49\u5b50\u7ed9\u674e\u601d\u5357\u70b9\u7740\u4e86\u7a74\u9053\uff0c\u6d51\u8eab\u9ebb\u6728\uff0c\u4e0d\u80fd\u52a8\u5f39\uff0c\u4f46\u5374\u8fd8\u672a\u66fe\u5012\u4e0b\u3002\u5c31\u4f3c\u7740\u4e86\u5b9a\u8eab\u6cd5\u4f3c\u7684\uff0c \u6070\u6070\u7ed9\u674e\u601d\u5357\u62ff\u6765\u5f53\u4f5c\u76fe\u724c\u3002 \u3000\u3000\u8499\u53e4\u6b66\u571f\u4e0d\u77e5\u4ed6\u5df2\u7740\u4e86\u9053\u513f\uff0c\u5f85\u5230\u957f\u77db\u523a\u51fa\uff0c\u89c1\u4ed6\u52a8\u4e5f\u4e0d\u52a8\uff0c\u65b9\u59cb\u53d1\u89c9\u6709\u5f02\u3002\u4ed6\u4eec\u4e8c\u4eba \u7684\u62db\u6570\u672c\u6765\u662f\u4e92\u76f8\u914d\u5408\u7684\u3002\u8499\u53e4\u6b66\u58eb\u6b63\u9762\u523a\u624e\uff0c\u8fd9\u6c49\u5b50\u5c31\u8be5\u4fa7\u8eab\u659c\u5288\uff0c\u4e0e\u4ed6\u914d\u5408\uff0c\u6765\u653b\u654c\u4eba \u7684\u3002 \u3000\u3000\u8499\u53e4\u6b66\u58eb\u5927\u5403\u4e00\u60ca\uff0c\u7f29\u624b\u4e0d\u53ca\uff0c\u957f\u77db\u4e00\u6311\uff0c\u767b\u65f6\u628a\u81ea\u5df1\u7684\u540c\u4f34\u50cf\u7a3b\u8349\u4eba\u4f3c\u7684\u6311\u4e0a\u4e86\u534a \u7a7a\u3002\u8499\u53e4\u6b66\u58eb\u76ee\u77aa\u53e3\u5446\uff0c\u53c8\u60ca\u53c8\u6012\uff0c\u8feb\u5207\u95f4\u8fd8\u672a\u66fe\u9a82\u5f97\u51fa\u58f0\uff0c\u674e\u601d\u5357\u5df2\u662f\u5530\u7684\u4e00\u5251\uff0c\u523a\u5165\u4e86 \u4ed6\u7684\u5c0f\u8179\u3002\u674e\u6069\u5357\u559d\u9053\uff1a\u201c\u4f60\u4eec\u5230\u9ec4\u6cc9\u8def\u4e0a\u4f5c\u4f34\u53bb\u5427\uff01\u201d\u62bd\u51fa\u5251\u6765\uff0c\u8fd9\u4e2a\u8499\u53e4\u6b66\u58eb\u4e5f\u5c31\u5e94\u58f0 \u5012\u5730\u4e86\u3002 \u3000\u3000\u7ea2\u8863\u5587\u561b\u89c1\u674e\u601d\u5357\u6765\u5230\uff0c\u80c6\u6c14\u5df2\u602f\uff0c\u6b64\u65f6\u4ed6\u7684\u4e24\u4e2a\u52a9\u624b\u53c8\u5df2\u7ed9\u674e\u601d\u5357\u6240\u6740\uff0c\u7ea2\u8863\u5587\u561b\u66f4 \u662f\u5fc3\u614c\uff0c\u8fd9\u5239\u90a3\u95f4\u4e0d\u7531\u5f97\u6756\u6cd5\u5927\u4e71\u3002\u5b5f\u660e\u971e\u4e58\u52bf\u75be\u653b\uff0c\u5251\u6c14\u5982\u8679\uff0c\u4e00\u62db\u201c\u7389\u5973\u6295\u68ad\u201d\uff0c\u5251\u5149 \u95ea\u5904\uff0c\u8840\u5149\u98de\u6e85\uff0c\u7ea2\u8863\u5587\u561b\u7684\u53f3\u80a9\u5df2\u662f\u7ed9\u5979\u5212\u5f00\u4e86\u4e00\u9053\u4e94\u5bf8\u591a\u957f\u7684\u4f24\u53e3\u3002 \u3000\u3000\u7ea2\u8863\u5587\u561b\u4e00\u58f0\u6012\u543c\uff0c\u56de\u8eab\u4fbf\u9003\u3002\u674e\u601d\u5357\u559d\u9053\uff1a\u201c\u5f80\u54ea\u91cc\u8dd1\uff1f\u201d\u7ea2\u8863\u5587\u561b\u4e00\u6296\u4e5d\u73af\u9521\u679a\uff0c \u679a\u4e0a\u7684\u4e5d\u4e2a\u94dc\u73af\u90fd\u98de\u4e86\u8d77\u6765\uff0c\u5411\u674e\u3001\u5b5f\u4e8c\u4eba\u6253\u53bb\u3002\u539f\u6765\u4ed6\u7684\u94dc\u73af\u4e0d\u4f46\u53ef\u4ee5\u6270\u4eba\u5fc3\u795e\uff0c\u5371\u6025\u4e4b \u65f6\uff0c\u8fd8\u53ef\u4ee5\u5f53\u4f5c\u6697\u5668\u7684\u3002 \u3000\u3000\u674e\u3001\u5b5f\u4e8c\u4eba\u53cc\u5251\u5408\u58c1\uff0c\u5251\u5149\u56db\u5c55\uff0c\u5408\u6210\u4e86\u4e00\u9053\u5706\u5f27\u3002\u53ea\u542c\u5f97\u53ee\u53ee\u94db\u94db\u4e4b\u58f0\u4e0d\u7edd\uff0c\u4e5d\u4e2a\u94dc \u73af\u5c3d\u90fd\u6253\u843d\uff0c\u6709\u7684\u8fd8\u7ed9\u5288\u6210\u4e24\u534a\uff01\u53d1\u51fa\u5f02\u5e38\u7684\u97f3\u54cd\u3002 \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u626b\u8361\u6b8b\u654c\u8981\u7d27\uff0c\u8bf7\u4f60\u53bb\u52a9\u5c60\u51e4\uff01\u201d\u53e3\u4e2d\u8bf4\u7740\uff0c\u8eab\u5f62\u5df2\u662f\u671d\u7740\u6768\u5a49\u5954\u53bb\uff0c\u539f \u6765\u5b5f\u660e\u971e\u60f3\u548c\u6768\u5a49\u7ed3\u4ea4\uff0c\u56e0\u6b64\u8981\u8d81\u8fd9\u4e2a\u673a\u4f1a\u4e0e\u6768\u5a49\u5e76\u80a9\u5fa1\u654c\u3002 \u3000\u3000\u90a3\u4e94\u4e2a\u56f4\u653b\u5c60\u51e4\u7684\u6c49\u5b50\u89c1\u5927\u52bf\u5df2\u53bb\uff0c\u65e0\u5fc3\u604b\u6218\uff0c\u4e0d\u5f85\u674e\u601d\u5357\u6740\u6765\uff0c\u4fbf\u5373\u4e00\u54c4\u800c\u6563\uff0c\u5c60\u51e4 \u4e0d\u7406\u4ed6\u4eec\uff0c\u5374\u5411\u90a3\u7ea2\u8863\u5587\u561b\u8ffd\u53bb\uff01\u674e\u601d\u5357\u6050\u5979\u6709\u5931\uff0c\u8ddf\u4e0a\u53bb\u7ed9\u5979\u538b\u9635\u3002 \u3000\u3000\u5c60\u51e4\u95ee\u9053\uff1a\u201c\u8fd9\u5587\u561b\u53ef\u662f\u6628\u665a\u548c\u90a3\u8363\u5f69\u4e00\u9053\uff0c\u8981\u6765\u6349\u62ff\u6211\u7684\u4e8c\u5e08\u54e5\u7684\uff1f\u201d\u674e\u601d\u5357\u9053\uff1a \u201c\u4e0d\u9519\u3002\u4ed6\u5df2\u7ecf\u7740\u4e86\u5b5f\u5973\u4fa0\u4e00\u5251\u4e86\u3002\u201d\u5c60\u51e4\u94f6\u7259\u4e00\u54ac\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u80fd\u653e\u8fc7\u4e86\u4ed6\uff01\u201d \u3000\u3000\u6b64\u65f6\u90a3\u7ea2\u8863\u5587\u561b\u521a\u521a\u62a2\u4e86\u4e00\u5339\u5750\u9a91\uff0c\u8de8\u4e0a\u9a6c\u80cc\u3002\u5c60\u51e4\u628a\u624b\u4e00\u626c\uff0c\u53d1\u51fa\u4e86\u8fde\u73af\u6bd2\u9556\u3002\u7ea2\u8863 \u5587\u561b\u4e00\u81c2\u53d7\u4f24\uff0c\u6325\u6756\u4e0d\u7075\uff0c\u53ea\u6253\u843d\u4e86\u4e00\u652f\u6bd2\u9f99\u9556\uff0c\u7b2c\u4e8c\u652f\u6bd2\u9f99\u9556\u5c04\u7740\u4e86\u4ed6\u7684\u540e\u5fc3\uff0c\u6bd2\u9f99\u9556\u89c1 \u8840\u5c01\u5589\uff0c\u7ea2\u8863\u5587\u561b\u4e00\u4e2a\u5012\u683d\u8471\u8dcc\u4e0b\u9a6c\u6765\uff0c\u53d1\u51fa\u4e86\u4e00\u58f0\u88c2\u5fc3\u80ba\u7684\u547c\u53eb\uff0c\u5c31\u5012\u5728\u8840\u6cca\u4e4b\u4e2d\u4e0d\u4f1a\u52a8 \u4e86\u3002 \u3000\u3000\u6768\u5a49\u72ec\u6218\u6df3\u4e8e\u8191\u6b63\u611f\u4e0d\u652f\uff0c\u5b5f\u660e\u971e\u6765\u5230\uff0c\u4e00\u7167\u9762\u4fbf\u4e0b\u6740\u624b\uff01 \u3000\u3000\u6df3\u4e8e\u8191\u53cd\u624b\u4e00\u94a9\uff0c\u94a9\u5c16\u5bf9\u7740\u5b5f\u660e\u971e\u7684\u864e\u53e3\u3002\u8fd9\u4e00\u62db\u653b\u654c\u4e4b\u6240\u5fc5\u6551\uff0c\u672c\u6765\u662f\u53cd\u5b88\u4e3a\u653b\u7684\u4e0a \u4e58\u62db\u6570\uff0c\u53ef\u60dc\u6df3\u4e8e\u8191\u8981\u5bf9\u4ed8\u7684\u4e0d\u5355\u662f\u4e00\u4e2a\u5b5f\u660e\u971e\uff0c\u5728\u4ed6\u524d\u9762\u8fd8\u6709\u4e00\u4e2a\u6768\u5a49\u4e5f\u6ca1\u95f2\u7740\u3002\u6768\u5a49\u8d81 \u8fd9\u673a\u4f1a\uff0c\u5530\u7684\u4e00\u5251\u6307\u5230\u4e86\u4ed6\u7684\u80cc\u5fc3\u3002\u6df3\u4e8e\u8191\u80cc\u8179\u53d7\u654c\uff0c\u96be\u4e8e\u517c\u987e\uff0c\u53ea\u597d\u5148\u89e3\u6768\u5a49\u8fd9\u4e00\u62db\u8db3\u4ee5 \u53d6\u4ed6\u6027\u547d\u7684\u5251\u62db\u3002\u5b5f\u660e\u971e\u4f55\u7b49\u77eb\u6377\uff0c\u5251\u950b\u4e00\u8f6c\uff0c\u500f\u7684\u4e00\u4e2a\u201c\u659c\u5207\u85d5\u201d\u7684\u62db\u5f0f\u659c\u524a\u4e0b\u6765\uff0c\u767b\u65f6 \u5728\u6df3\u4e8e\u8191\u7684\u5de6\u81c2\u5212\u5f00\u4e86\u4e00\u9053\u957f\u957f\u7684\u4f24\u53e3\u3002\u8981\u4e0d\u662f\u4ed6\u5012\u7eb5\u5f97\u5feb\uff0c\u8fd9\u6761\u81c2\u818a\u9669\u4e9b\u5c31\u8981\u7ed9\u5b5f\u660e\u971e\u786c \u751f\u751f\u7684\u5207\u65ad\u3002 \u3000\u3000\u5c60\u51e4\u559d\u9053\uff1a\u201c\u628a\u6027\u547d\u7559\u4e0b\u6765\uff01\u201d\u5979\u6068\u6781\u4e86\u6df3\u4e8e\u8191\uff0c\u4e00\u626c\u624b\u7adf\u662f\u4e09\u652f\u6bd2\u9556\u8fde\u73af\u53d1\u51fa\uff0c\u6df3\u4e8e \u8191\u59d4\u5b9e\u4e86\u5f97\uff0c\u4e00\u81c2\u53d7\u4f24\uff0c\u53ea\u662f\u5355\u94a9\u4e00\u62e8\uff0c\u53ea\u542c\u5f97\u94db\u94db\u58f0\u54cd\uff0c\u4e24\u652f\u98de\u9556\u7ed9\u4ed6\u6253\u4e86\u56de\u6765\uff0c\u548c\u7b2c\u4e09 \u652f\u98de\u9556\u4e00\u649e\uff0c\u4e09\u652f\u9752\u9f99\u9556\u90fd\u843d\u5728\u5730\u4e0a\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u6df3\u4e8e\u8191\u65e9\u5df2\u8de8\u4e0a\u4e86\u9a6c\u80cc\uff0c\u7edd\u5c18\u800c\u53bb\u3002\u4ed6\u8fd9\u5339\u5750\u9a91\u4e43\u662f\u5343\u4e2d\u9009\u4e00\u7684\u56fd\u5916 \u826f\u9a79\uff0c\u4e0d\u5728\u660e\u6167\u516c\u4e3b\u9001\u7ed9\u674e\u601d\u5357\u7684\u90a3\u5339\u540d\u9a6c\u4e4b\u4e0b\u3002 \u3000\u3000\u5c60\u51e4\u53f9\u4e86\u4e00\u58f0\u201c\u53ef\u60dc\u201d\uff0c\u628a\u6bd2\u9f99\u9556\u62fe\u56de\u6697\u5668\u56ca\u4e2d\uff0c\u4e0a\u524d\u5f85\u4e0e\u4ed6\u4eec\u62db\u547c\uff0c\u53ea\u89c1\u5b5f\u660e\u971e\u5df2\u7ecf \u6e58\u6768\u5a49\u642d\u8bdd\u4e86\u3002 \u3000\u3000\u5b5f\u660e\u971e\u843d\u843d\u5927\u65b9\u5730\u7b11\u9053\uff1a\u201c\u8fd9\u4f4d\u60f3\u5fc5\u662f\u6768\u59d0\u59d0\u4e86\uff1f\u5c60\u59d0\u59d0\u56de\u6765\u548c\u6211\u8bf4\u8d77\uff0c\u6211\u6b63\u5728\u9057\u61be\u6050 \u6015\u89c1\u4e0d\u5230\u6768\u59d0\u59d0\uff0c\u8c01\u77e5\u4f60\u4eec\u5c31\u6765\u4e86\u3002\u8fd9\u6b21\u53ef\u771f\u662f\u591a\u8c22\u4f60\u4eec\u5566\u3002\u201d \u3000\u3000\u6768\u5a49\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u5e94\u8be5\u7684\uff0c\u5b5f\u5973\u4fa0\u4e8e\u5357\u54e5\u6709\u6551\u547d\u4e4b\u6069\uff0c\u6211\u4eec\u5f97\u77e5\u6d88\u606f\uff0c\u5c82\u80fd\u4e0d\u6765\u62a5\u7b54\uff1f \u8bf4\u771f\u4e2a\u7684\uff0c\u6211\u4e5f\u5f88\u60f3\u89c1\u5b5f\u59d0\u59d0\u5462\u3002\u201d\u6768\u5a49\u5f53\u7136\u4e0d\u597d\u610f\u601d\u8bf4\u51fa\u5979\u662f\u674e\u601d\u5357\u7684\u672a\u5a5a\u59bb\u7684\u8eab\u4efd\uff0c\u4f46 \u5728\u8a00\u8bed\u4e4b\u95f4\uff0c\u5df2\u662f\u9690\u9690\u7ea6\u7ea6\u5730\u900f\u9732\u51fa\u6765\u3002\u4e00\u53e5\u4eb2\u4eb2\u70ed\u70ed\u7684\u201c\u5357\u54e5\u201d\uff0c\u5c31\u8db3\u4ee5\u8868\u660e\u4e86\u5979\u548c\u674e\u601d \u5357\u7684\u5173\u7cfb\u662f\u4e0e\u4f17\u4e0d\u540c\u4e86\u3002 \u3000\u3000\u674e\u601d\u5357\u5012\u662f\u6709\u70b9\u611f\u5230\u5c34\u5c2c\uff0c\u4f46\u597d\u5728\u5b5f\u660e\u971e\u843d\u843d\u5927\u65b9\uff0c\u674e\u601d\u5357\u53d7\u4e86\u5979\u7684\u5f71\u54cd\uff0c\u795e\u6001\u8fd9\u624d\u6062 \u590d\u81ea\u7136\u3002\u5f53\u4e0b\u674e\u601d\u5357\u4e0a\u524d\u4e0e\u5b5f\u660e\u971e\u91cd\u65b0\u89c1\u8fc7\u793c\uff0c\u8c22\u8fc7\u4e86\u5979\u5f53\u65e5\u76f8\u6551\u4e4b\u6069\u3002 \u3000\u3000\u5b5f\u660e\u971e\u95ee\u4e86\u4ed6\u5728\u8499\u53e4\u7684\u906d\u9047\uff0c\u674e\u601d\u5357\u636e\u5b9e\u56de\u7b54\uff0c\u6700\u540e\u8bf4\u9053\uff1a\u201c\u5bb6\u7236\u4e0d\u5e78\u906d\u5978\u4eba\u9677\u5bb3\uff0c\u591a \u4e8f\u5a49\u59b9\u6551\u62a4\uff0c\u6211\u4eec\u7236\u5b50\u624d\u80fd\u89c1\u4e0a\u4e00\u9762\u3002\u201d\u5b5f\u660e\u971e\u542c\u4e86\u8fd9\u8bdd\uff0c\u5fc3\u4e2d\u66f4\u662f\u4e86\u7136\uff0c\u60c5\u77e5\u4ed6\u4eec\u7684\u5173\u7cfb \u51b3\u4e0d\u53ea\u662f\u6b62\u4e8e\u201c\u5144\u59b9\u201d\u3002 \u3000\u3000\u5b5f\u660e\u971e\u5fc3\u5185\u5fae\u9178\uff0c\u5374\u4e5f\u6697\u6697\u5730\u4e3a\u4ed6\u4eec\u6b22\u559c\uff0c\u60f3\u9053\uff1a\u201c\u8fd9\u4eec\u4f4d\u59d1\u5a18\u51fa\u81ea\u540d\u95e8\uff0c\u53c8\u662f\u624d\u8c8c\u53cc \u5168\u3002\u5979\u548c\u601d\u5357\u76f8\u914d\uff0c\u6b63\u662f\u4e00\u5bf9\u5929\u9020\u5730\u8bbe\u7684\u4f73\u5076\u3002\u201d \u3000\u3000\u5c60\u51e4\u5fc3\u76f4\u53e3\u5feb\uff0c\u7b11\u9053\uff1a\u201c\u674e\u516c\u5b50\uff0c\u6211\u4eca\u65e5\u8bf7\u4f60\u4f60\u4e0d\u6765\uff0c\u6211\u8fd8\u53ea\u5f53\u4f60\u6ca1\u6709\u826f\u5fc3\u5462\u3002\u5982\u4eca\u6211 \u660e\u767d\u4e86\uff0c\u539f\u6765\u5982\u6b64\u3002\u201d\u674e\u601d\u5357\u3001\u6768\u5a49\u4e0e\u5b5f\u660e\u971e\u90fd\u4e0d\u7981\u9762\u4e0a\u4e00\u7ea2\u3002\u5b5f\u660e\u971e\u55d4\u9053\uff1a\u201c\u5c60\u59d0\u59d0\uff0c\u4f60 \u600e\u4e48\u80e1\u4e71\u8bf4\u8bdd\uff0c\u4e5f\u4e0d\u6015\u5ba2\u4eba\u89c1\u602a\u3002\u201d \u3000\u3000\u6768\u5a49\u4e0d\u60f3\u5c40\u9762\u5c34\u5c2c\uff0c\u7b11\u9053\uff1a\u201c\u6211\u4eec\u53ef\u4e0d\u662f\u4ec0\u4e48\u5ba2\u4eba\u3002\u5b5f\u59d0\u59d0\uff0c\u6211\u548c\u4f60\u867d\u7136\u521d\u6b21\u89c1\u9762\uff0c\u4f46 \u5357\u54e5\u548c\u4f60\u5374\u662f\u65e9\u5df2\u76f8\u8bc6\u7684\u4e86\uff0c\u6240\u4ee5\uff0c\u6211\u5bf9\u59d0\u59d0\u4e5f\u6709\u4e00\u89c1\u5982\u6545\u4e4b\u611f\u5462\u3002\u201d\u8fd9\u51e0\u53e5\u8bdd\u8bf4\u5f97\u5f88\u5f97 \u4f53\uff0c\u4f46\u8bdd\u4e2d\u7684\u918b\u5473\uff0c\u5374\u4e5f\u9690\u9690\u55c5\u5f97\u51fa\u6765\u3002\u5b5f\u660e\u971e\u542c\u4e86\uff0c\u4e0d\u89c9\u6697\u6697\u76b1\u7709\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u4f4d\u6768 \u59d1\u5a18\u6837\u6837\u90fd\u597d\uff0c\u53ef\u60dc\u5374\u662f\u6709\u70b9\u5c0f\u5fc3\u773c\u513f\u3002\u201d \u3000\u3000\u5ffd\u542c\u5f97\u8349\u4e1b\u4e2d\u6709\u547b\u541f\u4e4b\u58f0\uff0c\u539f\u6765\u6709\u4e2a\u897f\u590f\u6b66\u58eb\u53d7\u4e86\u5251\u4f24\uff0c\u4eba\u8fd8\u672a\u6b7b\uff0c\u6b63\u5728\u90a3\u91cc\u6323\u624e\u3002\u5c60 \u51e4\u5fc3\u4e2d\u4e00\u52a8\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u6b63\u8981\u627e\u4e2a\u6d3b\u53e3\u5ba1\u95ee\u3002\u597d\uff0c\u4f60\u4eec\u8c08\u5427\uff0c\u6211\u53bb\u6599\u7406\u8fd9\u53ae\u3002\u201d \u3000\u3000\u5c60\u51e4\u628a\u8fd9\u6b66\u58eb\u62d6\u4e86\u51fa\u6765\uff0c\u7ed9\u4ed6\u6577\u4e0a\u4e86\u91d1\u521b\u836f\uff0c\u8bf4\u9053\uff1a\u201c\u6709\u4ef6\u4e8b\u60c5\u6211\u8981\u95ee\u4f60\uff0c\u4f60\u8bf4\u5b9e \u8bdd\uff01\u201d\u8fd9\u6b66\u58eb\u5f97\u5979\u6577\u4e0a\u4e86\u91d1\u521b\u836f\uff0c\u75bc\u75db\u51cf\u4e86\u8bb8\u591a\uff0c\u4ee5\u4e3a\u53ef\u4ee5\u6d3b\u547d\uff0c\u5927\u559c\u8fc7\u671b\uff0c\u8bf4\u9053\uff1a\u201c\u59d1\u5a18 \u8bf7\u95ee\uff0c\u4f46\u6709\u6240\u77e5\uff0c\u5b9a\u5f53\u5949\u544a\u3002\u201d \u3000\u3000\u5c60\u51e4\u8bf4\u9053\uff1a\u201c\u8fd9\u7ea2\u8863\u5587\u561b\u6628\u665a\u548c\u4e00\u4e2a\u6c49\u4eba\u540d\u53eb\u8363\u5f69\u7684\u540c\u5728\u4e00\u8d77\uff0c\u4f60\u77e5\u9053\u5417\uff1f\u201d\u90a3\u897f\u590f\u6b66 \u571f\u4e00\u9053\uff1a\u201c\u77e5\u9053\u3002\u201d\u5c60\u51e4\u9053\uff1a\u201c\u8fd9\u4e2a\u8363\u5f69\u7684\u5e08\u7236\u540d\u53eb\u9633\u5929\u96f7\uff0c\u4f60\u77e5\u9053\u5417\uff1f\u201d\u897f\u590f\u6b66\u571f\u9053\uff1a \u201c\u9633\u5929\u96f7\u662f\u91d1\u56fd\u7684\u5927\u5185\u9ad8\u624b\uff0c\u6211\u4eec\u867d\u7136\u50fb\u5904\u897f\u590f\uff0c\u4e5f\u662f\u4e45\u95fb\u5176\u540d\u7684\u4e86\u3002\u201d \u3000\u3000\u5c60\u51e4\u9053\uff1a\u201c\u6211\u8981\u95ee\u4f60\u7684\u5c31\u6b63\u662f\u8fd9\u4ef6\u4e8b\u60c5\u3002\u8499\u53e4\u4e0e\u91d1\u56fd\u6b63\u5728\u4ea4\u6218\uff0c\u9633\u5929\u96f7\u65e2\u7136\u662f\u91d1\u56fd\u7684\u5927 \u5185\u9ad8\u624b\uff0c\u4f55\u4ee5\u4ed6\u7684\u5f1f\u5b50\u5374\u548c\u8499\u53e4\u7684\u5587\u561b\u4e00\u540c\u529e\u6848\u3002\u201d \u3000\u3000\u897f\u590f\u6b66\u571f\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u5e78\u4e8f\u4f60\u95ee\u7740\u4e86\u6211\uff0c\u8fd9\u662f\u4e00\u4e2a\u5916\u4eba\u7edd\u4e0d\u77e5\u9053\u7684\u673a\u5bc6\uff01\u201d\u5c60\u51e4 \u53c8\u7ed9\u4ed6\u6577\u4e0a\u4e00\u628a\u91d1\u521b\u836f\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u53ef\u4ee5\u544a\u8bc9\u6211\u5417\uff1f\u201d \u3000\u3000\u897f\u590f\u6b66\u571f\u9053\uff1a\u201c\u59d1\u5a18\u5f85\u6211\u8fd9\u6837\u597d\uff0c\u6211\u5c82\u6562\u9690\u7792\u3002\u9633\u5929\u96f7\u662f\u4e2a\u89c1\u98ce\u4f7f\u8235\u7684\u5bb6\u4f19\uff0c\u5982\u4eca\u8499\u53e4 \u52bf\u5927\uff0c\u9633\u5929\u96f7\u65e9\u5df2\u548c\u54f2\u522b\u6697\u4e2d\u6709\u4e86\u5f80\u6765\uff0c\u51c6\u5907\u5f85\u5230\u8499\u53e4\u5175\u4e34\u57ce\u4e0b\u4e4b\u65f6\uff0c\u4ed6\u5c31\u8981\u5728\u91d1\u4eac\u4f5c\u5185\u5e94 \u7684\u4e86\u3002\u201d \u3000\u3000\u5c60\u51e4\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\u3002\u4f46\u4f60\u4e5f\u662f\u8ddf\u4ed6\u4eec\u4e00\u4f19\u7684\uff0c\u4f60\u662f\u4e0d\u662f\u4e5f\u51c6\u5907\u5728\u897f\u590f\u4f5c\u8499\u53e4\u7684\u5185\u5e94 \u5462\uff1f\u201d \u3000\u3000\u897f\u590f\u6b66\u58eb\u6ee1\u9762\u901a\u7ea2\uff0c\u8bb7\u8bb7\u8bf4\u9053\uff1a\u201c\u826f\u79bd\u62e9\u6728\u800c\u6816\uff0c\u5fe0\u81e3\u62e9\u4e3b\u800c\u4e8b\u3002\u6211\u5728\u897f\u590f\u51fa\u4e0d\u4e86\u5934\uff0c \u8fd9\u3001\u8fd9\u4e5f\u662f\u4e0d\u5f97\u5df2\u7684\u4e8b\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u5fc3\u4e2d\u4e00\u52a8\uff0c\u8bf4\u9053\uff1a\u201c\u8499\u53e4\u7684\u5927\u519b\u542c\u8bf4\u8981\u79fb\u5e05\u5411\u897f\uff0c\u5148\u6253\u8d35\u56fd\uff0c\u8fd9\u662f\u771f\u7684\u5427\uff1f\u201d\u897f \u590f\u6b66\u571f\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u674e\u516c\u5b50\u4f60\u4e5f\u77e5\u9053\u4e86\uff1f\u201d \u3000\u3000\u5176\u5b9e\u674e\u601d\u5357\u662f\u56e0\u4e3a\u8499\u53e4\u5c6f\u5175\u9f99\u6c99\u5806\uff0c\u6839\u636e\u519b\u60c5\u5224\u65ad\u7684\uff0c\u5e76\u975e\u77e5\u9053\u8499\u53e4\u7684\u519b\u4e8b\u8ba1\u5212\u3002\u4ed6\u8fd9 \u4e48\u4e00\u5957\u53e3\u98ce\uff0c\u679c\u7136\u4ece\u8fd9\u897f\u590f\u6b66\u571f\u53e3\u4e2d\u5f97\u5230\u4e86\u8bc1\u5b9e\u3002 \u3000\u3000\u5c60\u51e4\u51b7\u7b11\u9053\uff1a\u201c\u539f\u6765\u4f60\u4e5f\u662f\u5356\u56fd\u6c42\u8363\uff0c\u4e0e\u9633\u5929\u96f7\u6b63\u662f\u4e00\u4e18\u4e4b\u8c89\uff01\u201d\u897f\u590f\u6b66\u58eb\u5927\u60ca\u9053\uff1a \u201c\u59d1\u5a18\uff0c\u4f60\u8bf4\u8fc7\u9976\u6211\u4e00\u547d\u7684\u3002\u201d\u5c60\u51e4\u9053\uff1a\u201c\u6211\u53ea\u662f\u8981\u4f60\u8bf4\u5b9e\u8bdd\uff0c\u51e0\u66fe\u7b54\u5e94\u8fc7\u9976\u4f60\u6027\u547d\uff1f\u522b\u7684 \u53ef\u9976\uff0c\u5356\u56fd\u4e4b\u8f88\u7edd\u4e0d\u53ef\u9976\uff01\u201d\u5530\u7684\u4e00\u5251\uff0c\u767b\u65f6\u628a\u897f\u590f\u6b66\u58eb\u9489\u5728\u5730\u4e0a\u3002 \u3000\u3000\u6768\u5a49\u7ed9\u5979\u5413\u4e86\u4e00\u8df3\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u6837\u82f1\u8c8c\u7684\u59d1\u5a18\uff0c\u60f3\u4e0d\u5230\u7adf\u662f\u6740\u4eba\u4e0d\u7728\u773c\u3002\u4e0d\u8fc7\uff0c\u5979 \u7684\u624b\u6bb5\u867d\u7136\u72e0\u8fa3\uff0c\u5982\u4e5f\u4ee4\u4eba\u6df1\u611f\u75db\u5feb\u3002\u201d \u3000\u3000\u8981\u77e5\u6768\u5a49\u7684\u51fa\u8eab\u548c\u5c60\u51e4\u3001\u5b5f\u660e\u971e\u90fd\u4e0d\u540c\uff0c\u6c14\u8d28\u4e5f\u81ea\u7136\u4e24\u6837\u3002\u56e0\u6b64\u5c3d\u7ba1\u5979\u7ed9\u5979\u4eec\u90a3\u79cd\u5dfe\u5e3c \u987b\u7709\u7684\u6c14\u8d28\u6240\u5438\u5f15\uff0c\u5185\u5fc3\u4e0d\u7531\u5f97\u4e0d\u5bf9\u5979\u4eec\u4f69\u670d\uff0c\u4f46\u4e5f\u603b\u611f\u5230\u81ea\u5df1\u548c\u5979\u4eec\u4e0d\u662f\u540c\u4e00\u7c7b\u7684\uff0c\u5373\u4f7f \u4e0d\u81f3\u4e8e\u683c\u683c\u4e0d\u5165\uff0c\u4e5f\u7edd\u4e0d\u80fd\u6c34\u4e73\u4ea4\u878d\u3002\u76f8\u5f62\u4e4b\u4e0b\uff0c\u674e\u601d\u5357\u53ef\u5c31\u548c\u5979\u4eec\u878d\u6d3d\u591a\u4e86\u3002\u6768\u5a49\u5ffd\u5730\u6709 \u4e2a\u5f02\u6837\u7684\u611f\u89c9\uff0c\u89c9\u5f97\u81ea\u5df1\u7ad9\u5728\u674e\u601d\u5357\u548c\u5b5f\u660e\u971e\u4e4b\u95f4\uff0c\u5c31\u4f3c\u4e00\u4e2a\u201c\u5916\u4eba\u201d\u4e00\u6837\u3002\u867d\u7136\u674e\u601d\u5357\u548c \u5979\u8bf4\u7684\u8bdd\u6bd4\u548c\u5b5f\u660e\u971e\u8bf4\u7684\u8fd8\u591a\u3002 \u3000\u3000\u4e0d\u77e5\u4e0d\u89c9\u5df2\u662f\u4e1c\u65b9\u5927\u767d\uff0c\u674e\u601d\u5357\u8bf4\u9053\uff1a\u201c\u8499\u53e4\u82e5\u662f\u79fb\u5e08\u897f\u5411\uff0c\u897f\u590f\u5c31\u8981\u6210\u4e3a\u6218\u573a\uff0c\u6b64\u5730 \u4e0d\u5b9c\u4e45\u7559\uff0c\u6211\u770b\u4f60\u4eec\u4e5f\u662f\u8d76\u5feb\u56de\u53bb\u7684\u597d\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u6211\u4eec\u8981\u7b49\u5b8b\u94c1\u8f6e\u592b\u5987\u4ece\u8499\u53e4\u6765\u4e0e\u6211\u4eec\u4f1a\u5408\uff0c\u4f60\u4eec\u5148\u56de\u53bb\u5427\u3002\u201d\u6768\u5a49\u9053\uff1a \u201c\u6211\u4eec\u4e5f\u4e0d\u5dee\u5728\u51e0\u5929\uff0c\u5927\u5bb6\u540c\u884c\u6709\u4e2a\u4f34\uff0c\u5357\u54e5\uff0c\u4f60\u7684\u610f\u601d\u600e\u6837\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u672a\u66fe\u56de\u7b54\uff0c\u5b5f\u660e\u971e\u5df2\u5148\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u53ef\u80fd\u8fd8\u8981\u4f1a\u5408\u4e00\u4e9b\u9ed1\u9053\u4e0a\u4eba\u7269\uff0c\u4f60\u4eec\u4e0d\u662f\u9053 \u4e2d\u4eba\uff0c\u6050\u6015\u591a\u5c11\u6709\u70b9\u907f\u5fcc\u3002\u800c\u4e14\u8499\u53f3\u9791\u5b50\u4e5f\u6b63\u5728\u8ffd\u6355\u601d\u5357\uff0c\u4f60\u4eec\u5b9e\u5728\u72af\u4e0d\u7740\u4e3a\u6211\u4eec\u803d\u6401\u3002\u6211 \u770b\u8fd8\u662f\u4f60\u4eec\u5148\u56de\u53bb\u7684\u597d\u3002\u201d\u539f\u6765\u5b5f\u660e\u971e\u6050\u6015\u6768\u5a49\u591a\u7591\uff0c\u662f\u4ee5\u4e0d\u613f\u610f\u548c\u4ed6\u4eec\u540c\u884c\u3002 \u3000\u3000\u5b5f\u660e\u971e\u65e2\u7136\u8fd9\u6837\u8bf4\uff0c\u6768\u5a49\u81ea\u662f\u4e0d\u4fbf\u575a\u6301\uff0c\u4e8e\u662f\u8bf4\u9053\uff1a\u201c\u597d\u5427\uff0c\u90a3\u6211\u4eec\u5c31\u5148\u8d70\u4e00\u6b65\uff0c\u4f46\u613f \u56de\u5230\u4e2d\u539f\uff0c\u54b1\u4eec\u53ef\u4ee5\u91cd\u89c1\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u548c\u6768\u5a49\u51fa\u4e86\u8774\u8776\u8c37\uff0c\u514d\u4e0d\u4e86\u8981\u8c08\u8d77\u5b5f\u660e\u971e\uff0c\u674e\u601d\u5357\u7b11\u9053\uff1a\u201c\u5a49\u59b9\uff0c\u4f60\u53ef\u4ee5\u653e\u5fc3\u4e86 \u5427\uff1f\u5b5f\u660e\u971e\u5bf9\u5f85\u4f60\u6211\u90fd\u662f\u4e00\u6837\u3002\u4f60\u770b\u5979\u7684\u6001\u5ea6\uff0c\u5c31\u53ef\u4ee5\u77e5\u9053\u5979\u6839\u672c\u5c31\u6ca1\u5b58\u7740\u7537\u5973\u4e4b\u89c1\uff0c\u66f4\u4e0d \u8981\u8bf4\u4ec0\u4e48\u79c1\u60c5\u3002\u201d \u3000\u3000\u6768\u5a49\u55d4\u9053\uff1a\u201c\u6211\u51e0\u65f6\u6709\u8fc7\u4e0d\u653e\u5fc3\u7684\uff1f\u4f60\u7684\u5fc3\u5018\u82e5\u4e0d\u662f\u5411\u7740\u6211\uff0c\u627e\u628a\u4f60\u7f1a\u5728\u88d9\u8fb9\u4e5f\u6ca1\u6709\u3002 \u4e0d\u9519\uff0c\u5b5f\u660e\u971e\u7684\u786e\u662f\u843d\u848b\u5927\u65b9\uff0c\u662f\u5dfe\u5e3c\u987b\u7709\uff0c\u662f\u5973\u4e2d\u8c6a\u6770\uff0c\u602a\u4e0d\u5f97\u4f60\u8981\u79f0\u8d5e\u5979\u3002\u53ef\u4f60\u4e5f\u4e0d\u8be5 \u628a\u6211\u770b\u6210\u4e86\u591a\u7591\u5584\u5992\u3001\u6c14\u91cf\u72ed\u7a84\u7684\u5973\u4eba\u5440\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u82e6\u7b11\u9053\uff1a\u201c\u6211\u4e0d\u8fc7\u968f\u4fbf\u8bf4\u4e00\u53e5\u8bdd\uff0c\u4f60\u5374\u60f3\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u597d\uff0c\u4f46\u5f97\u4f60\u653e\u5fc3\u5c31\u597d\uff0c\u54b1 \u4eec\u4e0d\u5fc5\u5189\u8c08\u5979\u5566\u3002\u201d \u3000\u3000\u6768\u5a49\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4f60\u6015\u548c\u6211\u8c08\u5979\uff0c\u53ef\u89c1\u4f60\u5b9e\u5728\u8fd8\u662f\u5fd8\u5979\u4e0d\u4e86\u3002\u201d\u6768\u5a49\u4e0d\u613f\u610f\u8fd9\u79cd\u4e0d\u6109\u5feb \u7684\u6c14\u6c1b\u4fdd\u6301\u4e0b\u53bb\uff0c\u56e0\u6b64\u542c\u4e86\u674e\u601d\u5357\u7684\u8bdd\uff0c\u679c\u7136\u4e5f\u5c31\u4e0d\u518d\u63d0\u5b5f\u660e\u971e\u4e86\u3002\u4f46\u867d\u7136\u4e0d\u63d0\uff0c\u5b5f\u660e\u971e\u6295 \u5728\u5979\u5fc3\u4e0a\u7684\u9634\u5f71\u5374\u4ecd\u8fd8\u4e0d\u80fd\u6d88\u9664\u3002 \u3000\u3000\u4e24\u4eba\u5404\u6000\u5fc3\u4e8b\uff0c\u7b56\u9a6c\u524d\u884c\uff0c\u4ed6\u4eec\u7684\u9a6c\u5feb\uff0c\u53ea\u8d70\u4e86\u4e24\u5927\uff0c\u5df2\u63a5\u8fd1\u91d1\u56fd\u548c\u897f\u590f\u7684\u8fb9\u5883\u3002 \u3000\u3000\u8fd9\u4e00\u65e5\u6b63\u884c\u8d70\u95f4\uff0c\u5ffd\u89c1\u5c18\u5934\u5927\u8d77\uff0c\u4e00\u5927\u7fa4\u96be\u6c11\uff0c\u6276\u8001\u643a\u5e7c\uff0c\u50cf\u6f6e\u6c34\u4e00\u6837\u4ece\u524d\u9762\u9000\u6765\u3002 \u3000\u3000\u674e\u601d\u5357\u5927\u5403\u4e00\u60ca\uff0c\u5fd9\u622a\u7740\u4e00\u4e2a\u96be\u6c11\u95ee\u9053\uff1a\u201c\u524d\u9762\u51fa\u4e86\u4ec0\u4e48\u4e8b\uff1f\u201d\u96be\u6c11\u9053\uff1a\u201c\u5feb\u8d70\uff0c\u5feb \u8d70\uff01\u8499\u53e4\u9791\u5b50\u5df2\u7ecf\u6253\u8fdb\u6765\u5566\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u662f\u65e9\u5df2\u77e5\u9053\u8499\u53e4\u519b\u79fb\u5e08\u897f\u5411\u7684\uff0c\u4f46\u8fd9\u652f\u5947\u5175\u4ece\u897f\u590f\u7684\u540e\u95e8\u6253\u8fdb\u6765\uff0c\u6765\u5f97\u5982\u6b64\u8fc5 \u901f\uff0c\u8fd8\u662f\u5927\u5927\u51fa\u4e4e\u4ed6\u610f\u6599\u4e4b\u5916\u3002 \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u600e\u4e48\u529e\uff1f\u54b1\u4eec\u7684\u9a6c\u5feb\uff0c\u8981\u662f\u627e\u5f97\u4e00\u6761\u8def\uff0c\u53ef\u4ee5\u907f\u8fc7\u8499\u53e4\u7684\u5927\u519b\u2026\u2026\u201d\u674e\u601d\u5357 \u9053\uff1a\u201c\u60c5\u51b5\u8fd9\u6837\u6df7\u4e71\uff0c\u5f88\u96be\u63a2\u660e\u865a\u5b9e\uff0c\u524d\u884c\u5371\u9669\u592a\u5927\uff0c\u53ea\u597d\u9000\u56de\u53bb\uff01\u5148\u627e\u4e2a\u5730\u65b9\u8eb2\u8eb2\u5427\u3002\u201d \u3000\u3000\u8f6c\u773c\u95f4\u8def\u4e0a\u5df2\u6324\u6ee1\u96be\u6c11\uff0c\u4ed6\u4eec\u9a91\u7740\u9a6c\u6742\u5728\u4eba\u5806\u4e4b\u4e2d\uff0c\u6bd4\u4eba\u884c\u4e5f\u5feb\u4e0d\u4e86\u591a\u5c11\u3002\u674e\u601d\u5357\u4e00\u770b \u4e0d\u662f\u529e\u6cd5\uff0c\u53ea\u597d\u53eb\u6768\u5a49\u79bb\u5f00\u5927\u8def\uff0c\u5411\u8352\u5c71\u5954\u9003\uff0c\u8fd8\u672a\u66fe\u6324\u51fa\u4eba\u5806\uff0c\u897f\u590f\u7684\u6e83\u519b\u53c8\u5df2\u4f3c\u6f6e\u6c34\u4e00 \u822c\u6d8c\u6765\u3002 \u3000\u3000\u8fd9\u4e9b\u6e83\u519b\u53ea\u987e\u9003\u547d\uff0c\u53ef\u4e0d\u7406\u4f1a\u767e\u59d3\u7684\u6b7b\u6d3b\uff0c\u9a91\u7740\u9a6c\u7684\u5c31\u5411\u4eba\u5806\u51b2\u8fc7\u53bb\uff0c\u65e0\u6570\u5987\u5b7a\u5728\u94c1\u8e44 \u4e4b\u4e0b\u7ed9\u8df5\u8e0f\u5f97\u904d\u4f53\u9cde\u4f24\uff0c\u54ed\u58f0\u9707\u5929\uff1b\u5931\u4e86\u5750\u9a91\u7684\u4e5f\u4ed7\u7740\u5200\u67aa\u51b2\u6563\u4eba\u7fa4\uff0c\u593a\u8def\u800c\u9003\uff0c\u9003\u907f\u4e0d\u53ca \u7684\u8001\u767e\u59d3\uff0c\u6b7b\u5728\u4e71\u5175\u5200\u67aa\u4e4b\u4e0b\u7684\u4e5f\u5f88\u4e0d\u5c11\u3002 \u3000\u3000\u674e\u601d\u5357\u53c8\u662f\u6124\u6012\uff0c\u53c8\u662f\u611f\u6168\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u6837\u7684\u58eb\u5175\uff0c\u754f\u654c\u5982\u864e\uff0c\u671b\u98ce\u800c\u9003\uff1b\u5bf9\u5f85\u81ea\u5df1\u7684 \u767e\u59d3\u5374\u53c8\u662f\u6b8b\u66b4\u5982\u72fc\uff0c\u8086\u610f\u8e42\u8e8f\uff0c\u8fd9\u8fd8\u80fd\u6253\u4ec0\u4e48\u4ed7\uff1f\u602a\u4e0d\u5f97\u8499\u53e4\u5175\u80fd\u591f\u4ee5\u5c11\u80dc\u591a\uff0c\u957f\u9a71\u76f4\u5165 \u4e86\u3002\u201d \u3000\u3000\u5fc3\u5ff5\u672a\u5df2\uff0c\u4e00\u5c0f\u961f\u6e83\u5175\u5df2\u7ecf\u5411\u4ed6\u4eec\u51b2\u6765\uff0c\u7eb7\u7eb7\u547c\u558a\uff1a\u201c\u6211\u8981\u5750\u9a91\u201d\uff0c\u201c\u7537\u7684\u53ef\u4ee5\u4f5c\u592b \u5b50\u201d\uff0c\u201c\u4f60\u8981\u5750\u9a91\uff0c\u90a3\u5973\u7684\u5c31\u7559\u7ed9\u6211\u5427\uff01\u201d\u672a\u66fe\u5230\u624b\uff0c\u5df2\u5728\u8bae\u5b9a\u5206\u8d43\u4e86\u3002 \u3000\u3000\u674e\u601d\u5357\u3001\u6768\u5a49\u5927\u6012\uff0c\u53cc\u5251\u6325\u821e\uff0c\u6740\u4e86\u51e0\u4e2a\u4eba\uff0c\u6e83\u519b\u65b9\u77e5\u5389\u5bb3\uff0c\u4e00\u54c4\u800c\u6563\u3002\u4f46\u524d\u961f\u7684\u6e83\u519b \u6563\u4e86\uff0c\u540e\u9762\u5927\u961f\u7684\u6e83\u519b\u53c8\u8d76\u6765\u4e86\u3002 \u3000\u3000\u674e\u601d\u5357\u6012\u9053\uff1a\u201c\u4f60\u4eec\u4e0d\u6562\u6253\u8499\u53e4\u9791\u5b50\uff0c\u5374\u6765\u6b3a\u4fae\u767e\u59d3\u3002\u597d\uff0c\u4f60\u4eec\u6765\u5427\uff0c\u6765\u4e00\u4e2a\u6211\u6740\u4e00 \u4e2a\uff0c\u6765\u4e00\u53cc\u6211\u6740\u4e00\u53cc\uff01\u201d \u3000\u3000\u8bdd\u72b9\u672a\u4e86\uff0c\u5ffd\u89c1\u6e83\u519b\u56db\u6563\u5954\u9003\uff0c\u6709\u7684\u4ece\u4ed6\u4eec\u65c1\u8fb9\u9003\u8fc7\uff0c\u5374\u4e5f\u6ca1\u6709\u62db\u60f9\u4ed6\u4eec\u3002\u674e\u601d\u5357\u8d77\u521d \u83ab\u540d\u5176\u5999\uff0c\u62ac\u773c\u4e00\u671b\uff0c\u8fd9\u624d\u770b\u5230\uff0c\u539f\u6765\u662f\u4e00\u961f\u8499\u53e4\u9a91\u5175\u5df2\u7ecf\u6740\u5230\u3002 \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u5a49\u59b9\uff0c\u4f60\u8ddf\u7740\u6211\uff01\u201d\u593a\u8fc7\u4e24\u652f\u957f\u77db\uff0c\u5de6\u6311\u53f3\u523a\uff0c\u5728\u4e71\u519b\u4e2d\u51b2\u5f00\u4e86\u4e00\u6761\u8def\uff0c \u5230\u4e86\u8352\u91ce\uff0c\u6e83\u5175\u5df2\u7ecf\u5c11\u4e86\uff0c\u674e\u601d\u5357\u53eb\u4e86\u4e00\u58f0\u201c\u5a49\u59b9\u201d\uff0c\u4e0d\u89c1\u7b54\u5e94\uff0c\u56de\u5934\u4e00\u770b\uff0c\u5374\u5df2\u4e0d\u89c1\u4e86\u6768 \u5a49\uff01\u674e\u601d\u5357\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u4ed6\u53ea\u9053\u6768\u5a49\u662f\u4e00\u76f4\u8ddf\u5728\u4ed6\u7684\u80cc\u540e\uff0c\u7adf\u4e0d\u77e5\u5728\u4ec0\u4e48\u65f6\u5019\u7ed9\u4e71\u519b\u51b2 \u6563\u4e86\u3002 \u3000\u3000\u674e\u601d\u5357\u53eb\u58f0\uff0c\u201d\u82e6\u4e5f\uff01\u201d\u6b32\u5f85\u56de\u53bb\u5bfb\u627e\uff0c\u53ea\u89c1\u5c18\u6c99\u6eda\u6eda\uff0c\u4e07\u9a6c\u5954\u817e\uff0c\u8499\u53e4\u7684\u5927\u519b\u5df2\u7ecf\u6740 \u5230\uff0c\u4e3b\u529b\u6cbf\u7740\u5b98\u9053\u633a\u8fdb\uff0c\u4e24\u7ffc\u5219\u5728\u65f7\u91ce\u5c55\u5f00\uff0c\u5206\u51fa\u65e0\u6570\u5c0f\u961f\u9a91\u5175\uff0c\u641c\u7d22\u6b8b\u654c\u3002 \u3000\u3000\u5176\u4e2d\u4e00\u4e2a\u5c0f\u961f\u8ffd\u9010\u897f\u590f\u6e83\u519b\uff0c\u8ffd\u5230\u4e86\u674e\u601d\u5357\u7684\u6240\u5728\uff0c\u674e\u601d\u5357\u633d\u8d77\u6210\u5409\u601d\u6c57\u6240\u8d50\u7684\u90a3\u5f20\u94c1 \u80ce\u5f13\uff0c\u55d6\u3001\u55d6\u3001\u55d6\uff0c\u8fde\u73e0\u7bad\u53d1\uff0c\u4e09\u679d\u7bad\u5c04\u7ffb\u4e86\u4e09\u4e2a\u8499\u53e4\u9a91\u5175\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u53e6\u5916\u4e24\u9a91\u5df2 \u7ecf\u51b2\u5230\u4ed6\u7684\u9762\u524d\u3002\u674e\u601d\u5357\u5927\u559d\u4e00\u58f0\uff0c\u8f7b\u8212\u733f\u81c2\uff0c\u6293\u7740\u4e00\u652f\u5411\u4ed6\u523a\u6765\u7684\u957f\u77db\uff0c\u5c06\u90a3\u8499\u53e4\u5175\u8fde\u4eba \u5e26\u77db\u731b\u7684\u62fd\u4e86\u8fc7\u6765\uff0c\u4e00\u4e2a\u65cb\u98ce\u6025\u821e\uff0c\u4fbf\u629b\u51fa\u53bb\uff0c\u6070\u597d\u628a\u53e6\u4e00\u4e2a\u8499\u53e4\u5175\u4e5f\u649e\u7ffb\u4e86\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u6709\u4eba\u559d\u9053\uff1a\u201c\u597d\u7bad\u6cd5\uff01\u201d\u674e\u601d\u5357\u62ac\u5934\u671b\u53bb\uff0c\u53ea\u89c1\u4e00\u9762\u5927\u65d7\u8fce\u98ce\u62db\u5c55\uff0c\u5927\u65d7\u4e0b\u4e00\u4e2a \u5a01\u98ce\u51db\u51db\u7684\u5c06\u519b\u8de8\u7740\u6218\u9a6c\uff0c\u505c\u5728\u8def\u8fb9\uff0c\u6b63\u5411\u7740\u4ed6\u4eec\u8fd9\u8fb9\u671b\u6765\u3002\u8fd9\u4e2a\u5c06\u519b\u4e0d\u662f\u522b\u4eba\uff0c\u6b63\u662f\u795e\u7bad \u624b\u54f2\u522b\u3002\u539f\u6765\u8fd9\u4e00\u8def\u5927\u519b\u5c31\u662f\u4ed6\u7edf\u7387\u7684\u3002 \u3000\u3000\u674e\u601d\u5357\u6ee1\u8eab\u6ce5\u6c61\uff0c\u8ddd\u79bb\u53c8\u5728\u767e\u6b65\u5f00\u5916\uff0c\u54f2\u522b\u672c\u6765\u4e0d\u77e5\u9053\u4ed6\u662f\u8c01\uff0c\u4f46\u56e0\u89c1\u4ed6\u7bad\u6cd5\u9ad8\u5f3a\uff0c\u4e0d \u89c9\u6280\u75d2\uff0c\u7b11\u9053\uff1a\u201c\u96be\u5f97\u6709\u4e2a\u5bf9\u624b\uff0c\u54b1\u4eec\u8f83\u91cf\u8f83\u91cf\uff01\u201d\u5f13\u5982\u9739\u9700\uff0c\u7bad\u4f3c\u6d41\u661f\uff0c\u4e5f\u662f\u8fde\u73e0\u7bad\u53d1\uff0c \u4e00\u53d1\u4e09\u7bad\u3002 \u3000\u3000\u54f2\u522b\u7684\u7bad\u6cd5\u6bd4\u674e\u601d\u5357\u9ad8\u660e\uff0c\u674e\u601d\u5357\u63d0\u8d77\u94c1\u80ce\u5f13\u4e00\u62e8\uff0c\u62d4\u843d\u4e86\u7b2c\u4e00\u679d\uff0c\u4e00\u4e2a\u201c\u8e6c\u91cc\u85cf\u8eab\u201d \u8eb2\u8fc7\u4e86\u7b2c\u4e8c\u679d\uff0c\u7b2c\u4e09\u679d\u7bad\u5c04\u7740\u4e86\u4ed6\u7684\u5750\u9a91\uff0c\u674e\u601d\u5357\u56de\u5934\u4e00\u7bad\uff0c\u4e5f\u5411\u7740\u54f2\u522b\u5c04\u53bb\u3002 \u3000\u3000\u54f2\u522b\u4e00\u542c\u5229\u7bad\u5288\u7a7a\u4e4b\u58f0\uff0c\u4e0d\u7531\u5f97\u5fc3\u5934\u4e00\u51db\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u662f\u4e00\u5f20\u5934\u53f7\u7684\u94c1\u80ce\u5f13\u3002\u201d\u628a\u624b\u4e00 \u62db\uff0c\u628a\u90a3\u679d\u7bad\u63a5\u5230\u624b\u4e2d\u3002\u6210\u5409\u601d\u6c57\u7684\u5f13\u7bad\u662f\u7279\u5236\u7684\uff0c\u54f2\u522b\u4e00\u770b\uff0c\u7acb\u5373\u5c31\u77e5\u9053\u662f\u674e\u601d\u5357\u4e86\u3002 \u3000\u3000\u54f2\u522b\u559d\u9053\uff1a\u201c\u539f\u6765\u662f\u4f60\u8fd9\u5c0f\u5b50\uff01\u5509\uff0c\u513f\u90ce\u4eec\uff0c\u4e0d\u8981\u8ba9\u4ed6\u8dd1\u4e86\uff01\u201d\u90a3\u5c0f\u961f\u9a91\u5175\u7ed9\u674e\u601d\u5357\u6740 \u4e86\u4e94\u4eba\uff0c\u8fd8\u6709\u56db\u4e2a\uff0c\u542c\u5f97\u4e3b\u5c06\u4e0b\u4ee4\uff0c\u987e\u4e0d\u5f97\u8ffd\u6740\u897f\u590f\u6e83\u519b\uff0c\u9f50\u90fd\u5411\u674e\u601d\u5357\u8ffd\u53bb\u3002 \u3000\u3000\u674e\u601d\u5357\u7684\u5750\u9a91\u8d1f\u75db\u72c2\u5954\uff0c\u8dd1\u51fa\u4e86\u6570\u91cc\u4e4b\u5730\uff0c\u529b\u4e0d\u80fd\u652f\uff0c\u56db\u8e44\u5c48\u5730\uff0c\u5012\u4e86\u4e0b\u53bb\u3002\u674e\u601d\u5357\u65bd \u5c55\u201c\u71d5\u9752\u5341\u516b\u7ffb\u201d\u7684\u529f\u592b\uff0c\u5728\u5730\u4e0a\u4e00\u4e2a\u76d8\u65cb\uff0c\u957f\u5251\u6325\u821e\uff0c\u4eba\u672a\u8d77\u7acb\uff0c\u5df2\u65a9\u65ad\u4e86\u516b\u6761\u9a6c\u817f\uff0c\u628a \u56db\u4e2a\u8ffd\u5175\u8dcc\u4e0b\u9a6c\u6765\u3002 \u3000\u3000\u54f2\u522b\u5feb\u9a6c\u8ffd\u6765\uff0c\u4eba\u672a\u5230\uff0c\u7bad\u5148\u53d1\uff0c\u674e\u601d\u5357\u6293\u8d77\u4e00\u4e2a\u58eb\u5175\u4f5c\u76fe\u724c\u8fde\u6321\u4e09\u7bad\uff0c\u5ffd\u89c9\u80c1\u4e0b\u4e00 \u9ebb\u3002\u539f\u6765\u662f\u54f2\u522b\u7684\u4e00\u679d\u795e\u7bad\u7a7f\u8fc7\u4e86\u90a3\u5175\u571f\u7684\u8eab\u4f53\uff0c\u5c04\u4f24\u4e86\u674e\u601d\u5357\u3002\u4e0d\u8fc7\u4e5f\u5e78\u4e8f\u6709\u8fd9\u4e2a\u201c\u76fe \u724c\u201d\uff0c\u5c04\u5230\u4e86\u674e\u601d\u5357\u7684\u8eab\u4e0a\u4e4b\u65f6\uff0c\u8fd9\u679d\u7bad\u7684\u52b2\u9053\u5df2\u7ecf\u5927\u51cf\uff0c\u4f24\u5f97\u4e0d\u7b97\u5f88\u91cd\u3002\u53ef\u662f\u4f24\u5f97\u867d\u7136\u4e0d \u91cd\uff0c\u4f46\u674e\u601d\u5357\u5931\u4e86\u5750\u9a91\uff0c\u5f62\u52bf\u4ea6\u662f\u5341\u5206\u5371\u9669\u4e86\uff01 \u3000\u3000\u674e\u601d\u5357\u6b63\u5728\u594b\u6218\u593a\u8def\u4e4b\u9645\uff0c\u5ffd\u542c\u5f97\u547c\u547c\u98ce\u54cd\uff0c\u9ec4\u6c99\u56db\u8d77\u3002\u8fd9\u662f\u897f\u5317\u9ec4\u571f\u8352\u539f\u4e0a\u7f55\u89c1\u7684 \u201c\u9f99\u5377\u98ce\u201d\uff0c\u65e0\u5de7\u4e0d\u5de7\uff0c\u6070\u597d\u5728\u8fd9\u4e2a\u7d27\u5f20\u7684\u65f6\u523b\u7ed9\u674e\u601d\u5357\u9047\u4e0a\u4e86\uff0c\u8fd9\u4e00\u9635\u72c2\u98ce\uff0c\u4e0d\u557b\u505a\u4e86\u4ed6 \u7684\u6551\u661f\u3002 \u3000\u3000\u72c2\u98ce\u522e\u5730\u800c\u6765\uff0c\u5439\u52a8\u4e86\u677e\u6563\u7684\u9ec4\u571f\u5c42\uff0c\u5df7\u8d77\u4e86\u9ec4\u8272\u7684\u6c99\u96fe\uff0c\u50cf\u6570\u5341\u767e\u91cd\u539a\u539a\u7684\u9ec4\u5e55\uff0c\u853d \u5730\u906e\u5929\uff0c\u6c99\u96fe\u4e2d\u53ea\u89c1\u4eba\u5f71\u5e62\u5e62\uff0c\u96be\u5206\u654c\u53cb\uff0c\u65f7\u91ce\u4e2d\u98ce\u529b\u7279\u522b\u5f3a\u52b2\uff0c\u53cc\u65b9\u90fd\u6025\u4e8e\u627e\u5bfb\u907f\u98ce\u4e4b \u5904\uff0c\u987e\u4e0d\u5f97\u518d\u6253\u4e0b\u53bb\u3002 \u3000\u3000\u98ce\u58f0\u547c\u547c\u4e4b\u4e2d\u5ffd\u542c\u5f97\u4e00\u7247\u91d1\u94c1\u4ea4\u9e23\u4e4b\u58f0\uff0c\u8fd9\u662f\u4ece\u5927\u8def\u4e0a\u4f20\u6765\u7684\u3002\u8499\u53e4\u7684\u9a91\u5175\u6b63\u5728\u8def\u4e0a\u884c \u8fdb\uff0c\u78b0\u4e0a\u8fd9\u9635\u72c2\u98ce\uff0c\u4e0d\u80fd\u4e0d\u6b47\u4e0b\u9a6c\u6765\uff0c\u961f\u5f62\u6563\u4e71\u3002\u6709\u4e00\u961f\u4fd8\u864f\u8d81\u6b64\u65f6\u673a\u9003\u8dd1\uff0c\u6709\u7684\u593a\u4e86\u5175\u58eb \u7684\u6b66\u5668\uff0c\u8fd8\u593a\u4e86\u5175\u58eb\u7684\u9a6c\u5339\uff0c\u5192\u7740\u72c2\u98ce\uff0c\u51b2\u6740\u51fa\u53bb\u3002 \u3000\u3000\u54f2\u522b\u6b64\u65f6\u5df2\u79bb\u5f00\u5927\u961f\uff0c\u5374\u4e0d\u77e5\u519b\u4e2d\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\u60c5\uff0c\u53ea\u9053\u662f\u6709\u654c\u519b\u4e58\u77fe\u5077\u88ad\u3002\u4ed6\u662f\u4e00\u519b \u4e3b\u5e05\uff0c\u5fc5\u987b\u56de\u53bb\u67e5\u660e\u771f\u76f8\uff0c\u5e94\u4ed8\u610f\u5916\uff0c\u9547\u5b9a\u519b\u5fc3\u3002\u5f53\u4e0b\u60f3\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u53d7\u4e86\u7bad\u4f24\uff0c\u8c05\u4ed6\u8dd1\u4e0d \u51fa\u897f\u590f\u56fd\u5883\u3002\u98ce\u8fc7\u4e4b\u540e\uff0c\u6211\u53eb\u4eba\u591a\u7ed8\u4ed6\u7684\u56fe\u5f62\uff0c\u5206\u53d1\u8fb9\u5883\u7684\u5404\u8425\u9a7b\u519b\uff0c\u53eb\u4ed6\u4eec\u591a\u52a0\u7559\u610f\u5c31 \u662f\u3002\u201d\u4e8e\u662f\u62e8\u8f6c\u9a6c\u5934\uff0c\u8d76\u56de\u519b\u4e2d\u3002 \u3000\u3000\u674e\u601d\u5357\u54ac\u5b9e\u7259\u6839\uff0c\u5728\u72c2\u98ce\u4e2d\u5954\u8d70\uff0c\u9ec4\u6c99\u6251\u9762\uff0c\u6c14\u90fd\u51e0\u4e4e\u900f\u4e0d\u8fc7\u6765\uff0c\u597d\u51e0\u6b21\u9669\u4e9b\u5012\u4e86\u4e0b \u53bb\uff0c\u4f46\u674e\u601d\u5357\u4ed7\u7740\u987d\u5f3a\u7684\u610f\u5fd7\u800c\u6ca1\u6709\u505c\u4e0b\u811a\u6b65\u3002 \u3000\u3000\u8fd9\u4e00\u9635\u98ce\u5439\u4e86\u4e00\u987f\u996d\u7684\u529f\u592b\uff0c\u65b9\u624d\u505c\u6b62\u3002\u72c2\u98ce\u8fc7\u540e\uff0c\u674e\u601d\u5357\u5b9a\u775b\u4e00\u77a7\uff0c\u53ea\u89c1\u81ea\u5df1\u5df2\u7ecf\u5230 \u4e86\u5c71\u811a\uff0c\u540e\u9762\u5e76\u65e0\u8ffd\u5175\u3002 \u3000\u3000\u674e\u601d\u5357\u53ea\u89c9\u53cc\u817f\u9178\u9ebb\uff0c\u767e\u9ab8\u6b32\u6563\uff0c\u4f24\u53e3\u75bc\u75db\u6b32\u88c2\uff0c\u6478\u4e86\u4e00\u6478\uff0c\u6e7f\u6f09\u6f09\u7684\uff0c\u4e5f\u4e0d\u77e5\u662f\u8840\u662f \u6c57\uff0c\u4ed6\u8eab\u4e0a\u6ee1\u662f\u6ce5\u6c99\uff0c\u6d41\u4e0b\u7684\u6c57\u4e5f\u53d8\u6210\u9ec4\u8272\u7684\u4e86\u3002 \u3000\u3000\u674e\u601d\u5357\u968f\u8eab\u5e26\u6709\u5e72\u7cae\u548c\u91d1\u521b\u836f\uff0c\u5e78\u597d\u672a\u66fe\u5931\u843d\u3002\u5f53\u4e0b\u5c06\u5e95\u886b\u6495\u7834\uff0c\u62b9\u53bb\u8840\u6c61\uff0c\u6577\u4e0a\u4e86\u5168 \u521b\u836f\uff0c\u5c06\u4f24\u53e3\u5305\u624e\u8d77\u6765\uff0c\u5fc3\u91cc\u60f3\u9053\uff1b\u201c\u73b0\u5728\u8fd8\u4e0d\u80fd\u8bf4\u662f\u8131\u79bb\u9669\u5883\uff0c\u5165\u5c71\u8d8a\u6df1\u8d8a\u597d\u3002\u201d\u4e8e\u662f\u5fcd \u7740\u6e34\uff0c\u5403\u4e86\u4e24\u4e2a\u786c\u998d\u998d\uff0c\u5f3a\u632f\u7cbe\u795e\uff0c\u722c\u4e0a\u5c71\u53bb\u3002 \u3000\u3000\u76f4\u5230\u9ec4\u660f\u4e4b\u540e\uff0c\u674e\u601d\u5357\u65b9\u59cb\u722c\u5230\u5c71\u4e0a\uff0c\u5e78\u597d\u627e\u7740\u4e00\u6761\u5c71\u6eaa\uff0c\u674e\u601d\u5357\u559d\u8fc7\u4e86\u6c34\uff0c\u6d17\u4e86\u4e2a \u6fa1\uff0c\u7cbe\u795e\u6e10\u6e10\u6062\u590d\u3002 \u3000\u3000\u674e\u601d\u5357\u5b81\u795e\u6253\u5750\uff0c\u9ed8\u8fd0\u7384\u52a8\uff0c\u884c\u6c14\u6d3b\u8840\uff0c\u4e5f\u4e0d\u77e5\u5750\u4e86\u591a\u5c11\u65f6\u5019\uff0c\u81ea\u89c9\u6c14\u8fbe\u56db\u68a2\uff0c\u5c0f\u8179\u53d1 \u70ed\uff0c\u77e5\u9053\u81ea\u5df1\u4f24\u4e0d\u788d\u4e8b\u4e86\uff0c\u8fd9\u624d\u7ad9\u8d77\u6765\uff0c\u7741\u773c\u4e00\u770b\uff0c\u53ea\u89c1\u6708\u5728\u5929\u5fc3\uff0c\u98ce\u5439\u8349\u52a8\uff0c\u6797\u4e2d\u4e00\u7247\u9759 \u5bc2\uff0c\u8fdc\u5904\u80e1\u524d\u4e4b\u58f0\uff0c\u5c1a\u81ea\u9690\u9690\u53ef\u95fb\uff0c\u4e24\u519b\u8ffd\u9010\uff0c\u5374\u4e0d\u77e5\u5230\u4e86\u4f55\u5904\u4e86\u3002\u674e\u601d\u5357\u770b\u7740\u6708\u5149\u4e0b\u81ea\u5df1 \u7684\u5f71\u5b50\uff0c\u60f3\u8d77\u5931\u53bb\u7684\u6768\u5a49\uff0c\u4e0d\u89c9\u5fc3\u75db\u795e\u4f24\u3002 \u3000\u3000\u674e\u601d\u5357\u60f3\u8d77\u6768\u5a49\u5bf9\u4ed6\u7684\u79cd\u79cd\u597d\u5904\uff0c\u5982\u4eca\u5269\u4e0b\u81ea\u5df1\u5f71\u53ea\u5f62\u5355\uff0c\u6768\u5a49\u6b7b\u751f\u672a\u535c\uff0c\u4e0d\u7981\u60b2\u4ece\u4e2d \u6765\uff0c\u96be\u4ee5\u65ad\u7edd\u3002\u201c\u6768\u5a49\u672c\u9886\u4e0d\u5f31\u4e8e\u6211\uff0c\u4f46\u613f\u5979\u4e5f\u80fd\u591f\u50cf\u6211\u4e00\u6837\u9003\u51fa\u751f\u5929\u3002\u5929\u6daf\u6d77\u89d2\uff0c\u7ec8\u6211\u4e00 \u751f\uff0c\u6211\u65e0\u8bba\u5982\u4f55\u4e5f\u8981\u628a\u5979\u627e\u5230\u3002\u201d\u674e\u601d\u5357\u5bf9\u7740\u660e\u6708\u53d1\u4e86\u8a93\uff0c\u5fc3\u91cc\u624d\u597d\u8fc7\u4e00\u4e9b\u3002 \u3000\u3000\u4e00\u9635\u98ce\u5439\u8fc7\uff0c\u4e71\u8349\u4e1b\u4e2d\u597d\u50cf\u6709\u6089\u6089\u7684\u58f0\u54cd\u3002\u674e\u601d\u5357\u5728\u4e00\u573a\u5267\u6218\u4e4b\u540e\uff0c\u53ea\u5403\u4e86\u70b9\u5e72\u7cae\uff0c\u6b64 \u65f6\u7cbe\u795e\u6062\u590d\uff0c\u809a\u5b50\u91cc\u997f\u5f97\u96be\u53d7\uff0c\u542c\u5f97\u8349\u4e1b\u4e2d\u7684\u58f0\u54cd\uff0c\u4ee5\u4e3a\u85cf\u7740\u4ec0\u4e48\u91ce\u5154\u4e4b\u7c7b\u7684\u5c0f\u52a8\u7269\uff0c\u5fc3 \u60f3\uff1a\u201c\u6b63\u597d\u7ed9\u6211\u5145\u9965\u3002\u201d\u4e8e\u662f\u968f\u624b\u62fe\u8d77\u4e24\u9897\u77f3\u5b50\uff0c\u5411\u8349\u4e1b\u4e2d\u6253\u53bb\uff0c\u4ed6\u51c6\u5907\u65bd\u7528\u201c\u8fde\u73e0\u9556\u201d\u7684 \u6253\u6cd5\uff0c\u7b2c\u4e00\u9897\u77f3\u5b50\u628a\u91ce\u517d\u5413\u8dd1\u51fa\u6765\uff0c\u7b2c\u4e8c\u9897\u77f3\u5b50\u5c31\u8981\u628a\u5b83\u6253\u7740\u3002 \u3000\u3000\u4e0d\u6599\u8349\u4e1b\u4e2d\u84e6\u5730\u7a9c\u51fa\u4e00\u4e2a\u4eba\u6765\uff0c\u7b2c\u4e8c\u9897\u77f3\u5b50\u672a\u66fe\u6253\u51fa\uff0c\u4ed6\u5df2\u7ecf\u6251\u5230\u4e86\u674e\u601d\u5357\u7684\u773c\u524d\u4e86\u3002 \u3000\u3000\u6708\u5149\u4e0b\u770b\u5f97\u5206\u660e\uff0c\u53ea\u89c1\u9762\u524d\u7684\u8fd9\u4e2a\u6c49\u5b50\u6d51\u8eab\u5c18\u571f\uff0c\u8863\u88f3\u8840\u6e0d\u6591\u6591\uff0c\u4e5f\u4e0d\u77e5\u4ed6\u662f\u4ec0\u4e48\u8eab \u4efd\uff0c\u54ea\u4e00\u56fd\u4eba\uff1f\u4f46\u7a7f\u7684\u5374\u662f\u8499\u53e4\u4eba\u7684\u670d\u9970\u3002 \u3000\u3000\u674e\u601d\u5357\u53ea\u9053\u662f\u54f2\u522b\u6d3e\u6765\u641c\u7d22\u4ed6\u7684\u4eba\uff0c\u65e0\u6687\u601d\u7d22\uff0c\u8fde\u5fd9\u62d4\u51fa\u5251\u6765\uff0c\u5530\u7684\u4e00\u5251\uff0c\u4fbf\u523a\u4ed6\u7684\u7a74 \u9053\u3002\u674e\u601d\u5357\u662f\u51c6\u5907\u5c06\u4ed6\u70b9\u4e86\u7a74\u9053\u4e4b\u540e\uff0c\u518d\u4ed4\u7ec6\u76d8\u95ee\u4ed6\u7684\u3002 \u3000\u3000\u8fd9\u4e00\u62db\u523a\u7a74\u5251\u6cd5\u662f\u674e\u601d\u5357\u7684\u770b\u5bb6\u672c\u9886\uff0c\u6ee1\u4ee5\u4e3a\u4e00\u4e2a\u666e\u901a\u7684\u8499\u53e4\u9791\u5b50\uff0c\u51b3\u4e0d\u80fd\u9003\u8fc7\u4ed6\u8fd9\u4e00 \u5251\u3002\u4e0d\u6599\u8fd9\u6c49\u5b50\u7684\u8eab\u624b\u7adf\u662f\u5341\u5206\u77eb\u6377\uff0c\u5927\u5927\u51fa\u4e4e\u674e\u601d\u5357\u7684\u610f\u6599\u4e4b\u5916\u3002 \u3000\u3000\u53ea\u89c1\u9752\u5149\u4e00\u95ea\uff0c\u201c\u94db\u201d\u7684\u4e00\u58f0\uff0c\u8fd9\u6c49\u5b50\u62d4\u5251\u51fa\u9798\u3001\u89e3\u62db\u3001\u8fd8\u5251\u4e09\u4e2a\u52a8\u4f5c\uff0c\u4e00\u6c14\u5475\u6210\u3002\u4e0d \u4f46\u5316\u89e3\u4e86\u674e\u601d\u5357\u8fd9\u4e00\u62db\u7cbe\u5999\u7684\u523a\u7a74\u5251\u6cd5\uff0c\u800c\u4e14\u53cd\u624b\u4e00\u6307\uff0c\u78a7\u83b9\u83b9\u7684\u5251\u5149\u95ea\u7535\u822c\u5730\u4fbf\u6307\u5230\u4e86\u674e \u601d\u5357\u7684\u9762\u95e8\u3002 \u3000\u3000\u674e\u601d\u5357\u970d\u5730\u4e00\u4e2a\u201c\u51e4\u70b9\u5934\u201d\uff0c\u9752\u94a2\u5251\u4e00\u62ac\uff0c\u4e00\u62db\u201c\u4e3e\u706b\u9563\u5929\u201d\uff0c\u5c06\u90a3\u4eba\u7684\u957f\u5251\u62d4\u5f00\u3002\u90a3 \u4eba\u4e00\u4e2a\u76d8\u9f99\u7ed5\u6b65\uff0c\u7ed5\u5230\u674e\u601d\u5357\u4fa7\u9762\uff0c\u5530\u5730\u53c8\u662f\u4e00\u5251\u523a\u5411\u674e\u601d\u5357\u80c1\u4e0b\u7684\u201c\u6108\u6c14\u7a74\u201d\u3002 \u3000\u3000\u674e\u601d\u5357\u4e00\u4e2a\u201c\u5927\u5f2f\u8170\uff0c\u659c\u63d2\u67f3\u201d\uff0c\u5728\u95f4\u4e0d\u5bb9\u53d1\u4e4b\u9645\uff0c\u907f\u5f00\u4e86\u8fd9\u4e00\u62db\uff0c\u4e0d\u7531\u5f97\u5fc3\u4e2d\u4e00\u51db\uff0c \u60f3\u9053\uff1a\u201c\u539f\u6765\u8fd9\u53ae\u4e5f\u4f1a\u523a\u7a74\uff0c\u5012\u662f\u4e0d\u53ef\u5c0f\u89d1\u4e86\uff01\u201d \u3000\u3000\u5fc3\u5ff5\u672a\u5df2\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u8fd9\u4eba\u53c8\u5df2\u6251\u5230\uff0c\u8fd0\u5251\u5982\u98ce\uff0c\u4e00\u53e3\u6c14\u8fde\u653b\u4e86\u5341\u4e03\u516b\u62db\u3002 \u3000\u3000\u674e\u601d\u5357\u7684\u672c\u9886\uff0c\u5176\u5b9e\u5e76\u4e0d\u5728\u8fd9\u6c49\u5b50\u4e4b\u4e0b\uff0c\u4f46\u56e0\u6c14\u529b\u4e0d\u6d4e\uff0c\u8179\u4e2d\u9965\u997f\uff0c\u6321\u4e86\u5341\u6570\u62db\u4e4b\u540e\uff0c \u5df2\u662f\u529b\u4e0d\u4ece\u5fc3\uff0c\u53ea\u80fd\u62db\u67b6\u3002\u597d\u5728\u8fd9\u6c49\u5b50\u4e5f\u597d\u50cf\u6709\u70b9\u540e\u52b2\u4e0d\u7ee7\u7684\u6a21\u6837\uff0c\u4e00\u53e3\u6c14\u653b\u4e86\u5341\u516d\u516b\u62db\uff0c \u5251\u6cd5\u4ea6\u5df2\u6e10\u6e10\u7f13\u6162\u4e0b\u6765\u3002 \u3000\u3000\u674e\u601d\u5357\u5fc3\u4e2d\u4e00\u52a8\uff1a\u201c\u8fd9\u4eba\u7684\u5251\u6cd5\u597d\u4f3c\u5728\u54ea\u91cc\u89c1\u8fc7\u4f3c\u7684\uff1f\u201d\u521a\u8981\u559d\u6b62\uff0c\u90a3\u4eba\u5df2\u662f\u731b\u5730\u8df3\u51fa \u5708\u5b50\uff0c\u559d\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u5927\u4e08\u592b\u884c\u4e0d\u66f4\u540d\uff0c\u5750\u4e0d\u6539\u59d3\uff0c\u6211\u662f\u5927\u6c49\u7537\u513f\u674e\u601d\u5357\u3002\u4f60\u662f\u8c01\uff1f\u201d \u3000\u3000\u90a3\u4eba\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u662f\u5927\u6c49\u7537\u513f\uff0c\u6211\u4e5f\u662f\u5927\u6c49\u7537\u513f\uff0c\u8fd9\u573a\u67b6\u4e0d\u5fc5\u518d\u6253\u4e86\u5427\u3002\u674e\u5144\uff0c\u82e5\u662f \u6211\u731c\u5f97\u4e0d\u9519\u7684\u8bdd\uff0c\u4f60\u5e94\u662f\u5c11\u6797\u6d3e\u7684\u3002\u4e0d\u77e5\u5c0a\u5e08\u662f\u54ea\u4e00\u4f4d\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u5bb6\u5e08\u662f\u5c11\u6797\u6d3e\u7684\u4fd7\u5bb6\u5f1f\u5b50\uff0c\u4ed6\u8001\u4eba\u5bb6\u59d3\u8c37\u540d\u5e73\u9633\u3002\u201d \u3000\u3000\u90a3\u4eba\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u674e\u5144\u662f\u8c37\u5e73\u9633\u8c37\u5927\u4fa0\u7684\u9ad8\u8db3\uff0c\u602a\u4e0d\u5f97\u4f7f\u5f97\u8fd9\u4e48\u597d\u7684\u8fbe\u6469\u5251\u6cd5\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u6211\u8fd8\u6ca1\u6709\u8bf7\u6559\u5144\u53f0\u9ad8\u59d3\u5927\u540d\u5462\uff0c\u53ef\u4ee5\u89c1\u544a\u5417\uff1f\u201d \u3000\u3000\u90a3\u4eba\u9053\uff1a\u201c\u5c0f\u5f1f\u59d3\u77f3\uff0c\u5355\u540d\u4e00\u4e2a\u749e\u5b57\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u662f\u77f3\u5144\uff0c\u602a\u4e0d\u5f97\u6211\u770b\u4f60\u7684\u5251\u6cd5\u4f3c\u66fe\u76f8\u8bc6\u3002\u201d \u3000\u3000\u77f3\u5657\u8be7\u9053\uff1a\u201c\u674e\u5144\u4f55\u4ee5\u8bc6\u5f97\u6211\u7684\u672c\u95e8\u5251\u6cd5\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u5c0a\u5e08\u662f\u5c60\u5927\u4fa0\u5c60\u767e\u57ce\u5427\uff1f\u4ee4\u5e08\u59b9\u5c60\u51e4\u6628\u5929\u8fd8\u662f\u548c\u6211\u5728\u4e00\u8d77\u7684\u3002\u201d \u3000\u3000\u77f3\u749e\u60ca\u559c\u4ea4\u96c6\uff0c\u8fde\u5fd9\u95ee\u9053\uff0c\u201c\u5c60\u5e08\u59b9\u5728\u4ec0\u4e48\u5730\u65b9\uff1f\u6211\u8fd8\u6709\u4e2a\u4e8c\u5e08\u54e5\u540d\u5524\u9f99\u521a\uff0c\u4e0d\u77e5\u674e\u5144 \u53ef\u4e5f\u89c1\u8fc7\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u5c60\u59d1\u5a18\u548c\u4e00\u4f4d\u5b5f\u59d1\u5a18\u5728\u8774\u8776\u8c37\uff0c\u7b49\u5f85\u5b8b\u94c1\u8f6e\u592b\u5987\u4e0e\u4ed6\u4eec\u4f1a\u5408\u3002\u9f99\u5e08\u5144\u5219\u3001 \u5219\u5df2\u4e0d\u5e78\u6b7b\u4e86\u3002\u201d \u3000\u3000\u77f3\u749e\u5927\u60ca\u9053\uff1a\u201c\u600e\u4e48\u6b7b\u7684\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u628a\u5148\u540e\u78b0\u89c1\u9f99\u521a\u3001\u5c60\u51e4\u4e4b\u4e8b\uff0c\u539f\u539f\u672c\u672c\u5730\u544a\u8bc9\u4e86\u77f3\u749e\uff0c\u77f3\u749e\u542c\u5f97\u53cc\u76ee\u5782\u6cea\uff0c\u53c8\u662f \u4f24\u5fc3\uff0c\u53c8\u662f\u6124\u6068\uff0c\u5f53\u4e0b\u62d4\u5251\u780d\u77f3\uff0c\u53d1\u4e0b\u8a93\u9053\uff1a\u201c\u8fd9\u90fd\u662f\u6df3\u4e8e\u8191\u8fd9\u8d3c\u5b50\u5bb3\u4e86\u9f99\u5e08\u5144\u7684\u3002\u6211\u4e0d\u6740 \u6b64\u8d3c\uff0c\u8a93\u4e0d\u4e3a\u4eba\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u5f85\u4ed6\u6012\u6c14\u7a0d\u5e73\u4e4b\u540e\uff0c\u8bf4\u9053\uff1a\u201c\u77f3\u5144\uff0c\u4f60\u600e\u4e48\u6765\u5230\u8fd9\u91cc\u7684\uff1f\u4f55\u4ee5\u7a7f\u7684\u8fd9\u8eab\u670d\u9970\uff1f\u201d \u3000\u3000\u77f3\u749e\u9053\uff1a\u201c\u8bf4\u6765\u60ed\u6127\uff0c\u6628\u65e5\u6211\u78b0\u4e0a\u8499\u53e4\u5927\u519b\uff0c\u4f17\u5be1\u4e0d\u654c\uff0c\u4e0d\u5e78\u88ab\u4fd8\u3002\u4eca\u65e5\u65e5\u95f4\u7ecf\u8fc7\u5be5\u513f \u6c9f\u65f6\uff0c\u8499\u53e4\u9a91\u5175\u6b63\u5728\u8ffd\u51fb\u897f\u590f\u6e83\u519b\uff0c\u65e0\u5de7\u4e0d\u5de7\uff0c\u522e\u8d77\u4e86\u4e00\u573a\u7f55\u89c1\u7684\u9f99\u5377\u98ce\uff0c\u98ce\u6c99\u853d\u76ee\uff0c\u4fd8\u864f \u4e58\u673a\u9003\u4ea1\uff0c\u5c0f\u5f1f\u4fa5\u5e78\u9003\u4e86\u51fa\u6765\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u7b11\u9053\uff1a\u201c\u8fd9\u53ef\u771f\u5de7\u6781\u4e86\uff0c\u6211\u4e5f\u662f\u5728\u90a3\u573a\u98ce\u66b4\u4e2d\u9003\u51fa\u6765\u7684\u3002\u4f60\u4e00\u5b9a\u5f88\u997f\u4e86\u5427\uff0c\u6211\u8fd9 \u91cc\u8fd8\u6709\u70b9\u5e72\u7cae\u3002\u201d \u3000\u3000\u77f3\u749e\u9053\uff1a\u201c\u6211\u521a\u624d\u6293\u4e86\u4e00\u53ea\u91ce\u5154\uff0c\u4f60\u8eab\u4e0a\u5e26\u6709\u706b\u77f3\u5417\uff1f\u201d \u3000\u3000\u77f3\u749e\u4ece\u4e71\u8349\u4e1b\u4e2d\u628a\u91ce\u5154\u53d6\u51fa\uff0c\u674e\u601d\u5357\u70e7\u8d77\u4e86\u4e00\u5806\u706b\uff0c\u4e24\u4eba\u5206\u98df\u70e4\u5154\u5e72\u7cae\uff0c\u7cbe\u795e\u5927\u632f\u3002 \u3000\u3000\u674e\u601d\u5357\u60f3\u8d77\u6768\u5a49\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u5728\u98ce\u66b4\u4e4b\u4e2d\uff0c\u53ef\u66fe\u89c1\u8fc7\u4e00\u4e2a\u5982\u6b64\u8fd9\u822c\u7684\u5c11\u5973\uff1f\u201d\u5fc3\u77e5\u5e0c\u671b \u751a\u662f\u6e3a\u832b\uff0c\u4e0d\u8fc7\u59d1\u4e14\u4e00\u95ee\u800c\u5df2\u3002 \u3000\u3000\u4e0d\u6599\u77f3\u749e\u542c\u4e86\u674e\u601d\u5357\u8be6\u8ff0\u6768\u5a49\u7684\u670d\u9970\u76f8\u8c8c\u4e4b\u540e\uff0c\u8bf4\u9053\uff1a\u201c\u89c1\u8fc7\u3002\u5509\uff0c\u8fd9\u4f4d\u59d1\u5a18\u7684\u906d\u9047\u5176 \u60e8\uff01\u2026\u2026\u201d\u8bf4\u81f3\u6b64\u5904\u731b\u7136\u5982\u6709\u6240\u89c9\uff0c\u8fde\u5fd9\u95ee\u9053\uff1a\u201c\u4e0d\u77e5\u8fd9\u4f4d\u59d1\u5a18\u662f\u674e\u5144\u7684\u4ec0\u4e48\u4eba\uff1f\u201d\u6b63\u662f\uff1a \u3000\u3000\u4e71\u4e16\u9e33\u9e2f\u906d\u6298\u7ffc\uff0c\u751f\u79bb\u6b7b\u522b\u603b\u5fc3\u4f24\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b\u2014\u2014\u7b2c\u5341\u56db\u56de\u3000\u8d3c\u5b50\u51e0\u756a\u65bd\u8be1\u8ba1\u3000\u94b1\u9556\u4e00\u63b7\u7834\u5978\u8c0b \u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b \u7b2c\u5341\u56db\u56de\u3000\u8d3c\u5b50\u51e0\u756a\u65bd\u8be1\u8ba1\u3000\u94b1\u9556\u4e00\u63b7\u7834\u5978\u8c0b \u3000\u3000\u674e\u601d\u5357\u5fc3\u5934\u201c\u535c\u901a\u201d\u4e00\u8df3\uff0c\u5f3a\u81ea\u9547\u6444\u5fc3\u795e\uff0c\u8bf4\u9053\uff1a\u201c\u662f\u548c\u6211\u540c\u8def\u8d70\u96be\u7684\u670b\u53cb\uff0c\u5979\u4e5f\u662f\u6c49 \u4eba\uff0c\u6d41\u843d\u5f02\u56fd\uff0c\u5b64\u82e6\u65e0\u4f9d\uff0c\u4e0d\u5e78\u53c8\u9047\u4e0a\u6218\u7978\uff0c\u60f3\u56de\u6545\u4e61\uff0c\u662f\u4ee5\u6211\u4e0e\u5979\u7ed3\u4f34\u540c\u884c\uff0c\u7565\u5c3d\u4fdd\u62a4\u4e4b \u8d23\uff0c\u4e0d\u6599\u6628\u65e5\u5374\u5728\u4e71\u519b\u4e4b\u4e2d\u5931\u6563\u3002\u5979\u7684\u906d\u9047\u600e\u6837\uff1f\u4f60\u53ef\u4ee5\u544a\u8bc9\u6211\u5417\uff1f\u201d\u674e\u601d\u5357\u6015\u77f3\u749e\u6709\u6240\u987e \u8651\uff0c\u4e0d\u613f\u5410\u9732\u771f\u60c5\uff0c\u56e0\u6b64\u7792\u8fc7\u4e86\u4ed6\u4e0e\u6768\u5a49\u7684\u672a\u5a5a\u592b\u59bb\u7684\u5173\u7cfb\u3002\u4e0d\u8fc7\u4ed6\u628a\u6768\u5a49\u8bf4\u6210\u662f\u4e0e\u4ed6\u76f8\u4f9d \u4e3a\u547d\u7684\u201c\u96be\u53cb\u201d\uff0c\u90a3\u4e5f\u662f\u4e8b\u5b9e\u3002 \u3000\u3000\u77f3\u749e\u9053\uff1a\u201c\u5c31\u662f\u5728\u90a3\u573a\u98ce\u66b4\u6765\u5230\u4e4b\u524d\u53d1\u751f\u7684\u4e8b\u3002\u4fd8\u864f\u662f\u5206\u5f00\u7537\u5973\u7684\uff0c\u5728\u5973\u4fd8\u864f\u7684\u961f\u4f0d \u4e2d\uff0c\u6709\u4e00\u4e2a\u7ea2\u8863\u5973\u5b50\u4f3c\u4e4e\u5c31\u662f\u4f60\u6240\u8bf4\u7684\u90a3\u4f4d\u59d1\u5a18\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u4e0d\u9519\u3002\u6628\u5929\u5979\u7a7f\u7684\u6b63\u662f\u4e00\u4ef6\u6de1\u7ea2\u8272\u7684\u8863\u88f3\uff0c\u539f\u6765\u5979\u505a\u4e86\u4fd8\u864f\u3002\u5979\u53d1\u751f\u4e86\u4ec0 \u4e48\u4e8b\u60c5\uff1f\u201d \u3000\u3000\u77f3\u749e\u9053\uff1a\u201c\u4f60\u522b\u96be\u8fc7\uff0c\u6050\u6015\u5979\u3001\u5979\u4e0d\u80fd\u56de\u5bb6\u4e86\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u4e00\u628a\u6293\u7740\u77f3\u749e\uff0c\u53eb\u9053\uff1a\u201c\u7a76\u7adf\u600e\u4e48\u6837\u4e86\uff1f\u5feb\u544a\u8bc9\u6211\u3002\u201d\u674e\u601d\u5357\u5fc3\u4e2d\u5df2\u77e5\u4e0d\u5999\uff0c\u4f46 \u4e00\u4e2a\u201c\u6b7b\u201d\u5b57\u8fd8\u662f\u4e0d\u6562\u8bf4\u51fa\u53e3\u6765\u3002 \u3000\u3000\u77f3\u749e\u53f9\u5b50\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u6709\u4e00\u4e2a\u9791\u5b50\u519b\u5b98\u89c1\u5979\u957f\u5f97\u8c8c\u7f8e\uff0c\u8d70\u53bb\u8c03\u620f\u5979\uff0c\u8fd9\u4f4d\u59d1\u5a18\u597d\u70c8 \u6027\uff0c\u638f\u51fa\u4e86\u4e00\u67c4\u5315\u9996\uff0c\u53ea\u662f\u4e00\u4e0b\u5b50\u5c31\u628a\u90a3\u9791\u5b50\u523a\u6b7b\uff0c\u540e\u6765\u3001\u540e\u6765\u9791\u5b50\u7eb7\u7eb7\u8dd1\u6765\uff0c\u90a3\u4f4d\u59d1\u5a18\u62bd \u51fa\u5315\u9996\uff0c\u4e00\u5200\u5c31\u523a\u8fdb\u4e86\u81ea\u5df1\u7684\u5fc3\u7a9d\u3002\u5979\u3001\u5979\u81ea\u5c3d\u4e86\uff01\u2026\u2026\u201d \u3000\u3000\u4fe8\u4f3c\u6674\u5929\u8d77\u4e86\u9739\u96f3\uff0c\u201c\u8f70\u201d\u7684\u4e00\u58f0\uff0c\u674e\u601d\u5357\u7684\u8111\u888b\u90fd\u597d\u4f3c\u7ed9\u70b8\u5f00\u4e86\u3002\u4ed6\u5446\u662f\u6728\u9e21\uff0c\u4e2d\u4e86 \u75af\u4f3c\u7684\uff0c\u53cc\u773c\u5706\u7741\uff0c\u773c\u6cea\u5374\u6ca1\u6d41\u51fa\u3002 \u3000\u3000\u77f3\u749e\u5927\u60ca\u9053\uff1a\u201c\u674e\u5144\uff0c\u9192\u9192\uff01\u201d \u3000\u3000\u8fc7\u4e86\u534a\u664c\uff0c\u674e\u601d\u5357\u624d\u54c7\u7684\u4e00\u58f0\u54ed\u4e86\u51fa\u6765\uff0c\u53eb\u9053\uff1a\u201c\u5a49\u59b9\uff0c\u4f60\u6b7b\u5f97\u597d\u60e8\uff01\u4f60\u6b7b\u4e86\u6211\u600e\u4e48\u8fd8 \u80fd\u591f\u72ec\u751f\uff1f\u201d \u3000\u3000\u77f3\u749e\u89c1\u674e\u601d\u5357\u5982\u6b64\u60c5\u72b6\uff0c\u5df2\u77e5\u674e\u601d\u5357\u548c\u8fd9\u4f4d\u59d1\u5a18\u7edd\u975e\u666e\u901a\u201c\u96be\u53cb\u201d\u5173\u7cfb\uff0c\u5f53\u4e0b\u5b89\u6170\u4ed6 \u9053\uff1a\u201c\u7537\u5973\u4fd8\u864f\u662f\u5206\u5f00\u7684\uff0c\u8ddd\u79bb\u76f8\u5f53\u8fdc\uff0c\u4e5f\u8bb8\u6211\u770b\u5f97\u4e0d\u592a\u6e05\u695a\uff0c\u672a\u5fc5\u5c31\u662f\u4f60\u8bf4\u7684\u90a3\u4f4d\u59d1 \u5a18\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u62ed\u6cea\u9053\uff1a\u201c\u4f60\u4e0d\u5fc5\u5b89\u6170\u6211\u4e86\u3002\u8fd9\u5973\u5b50\u7a7f\u7684\u662f\u7ea2\u8863\u88f3\uff0c\u53c8\u90a3\u4e48\u70c8\u6027\uff0c\u4e0d\u662f\u5979\u8fd8\u662f \u8c01\uff1f\u201d \u3000\u3000\u77f3\u749e\u9053\uff1a\u201c\u59d1\u5a18\u5bb6\u7a7f\u7ea2\u8863\u88f3\u7684\u6700\u5e73\u5e38\uff0c\u4e0d\u89c1\u5f97\u5c31\u53ea\u6709\u5979\u624d\u7a7f\u7ea2\u8863\u5427\u3002\u8fd8\u6709\uff0c\u5728\u90a3\u5973\u5b50\u4e00 \u5200\u523a\u8fdb\u81ea\u5df1\u7684\u5fc3\u7a9d\u4e4b\u540e\uff0c\u98ce\u66b4\u968f\u5373\u5c31\u6765\uff0c\u6211\u4eec\u7537\u4fd8\u864f\u76ee\u7779\u8fd9\u4ef6\u63ba\u4e8b\uff0c\u4eba\u4eba\u90fd\u662f\u6c14\u6124\u975e\u5e38\uff0c\u8d81 \u7740\u98ce\u66b4\uff0c\u6211\u4eec\u7684\u66b4\u52a8\u4e5f\u5c31\u7206\u53d1\u4e86\u3002\u90a3\u5973\u5b50\u6b7b\u4e86\u6ca1\u6709\uff0c\u6211\u4eec\u5c31\u4e0d\u77e5\u9053\u4e86\u3002\u5982\u679c\u662f\u523a\u5f97\u4e0d\u6df1\u7684 \u8bdd\uff0c\u8bf4\u4e0d\u5b9a\u8fd8\u53ef\u4ee5\u6d3b\u547d\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u5fcd\u4e0d\u4f4f\u53c8\u6d41\u6cea\uff0c\u54ac\u4e86\u54ac\u7259\uff0c\u8bf4\u9053\uff1a\u201c\u6d3b\u8f6c\u6765\u4e5f\u8fd8\u662f\u843d\u5728\u9791\u5b50\u7684\u624b\u4e2d\uff0c\u4ee5\u5979\u90a3\u6837\u521a \u70c8\u7684\u6027\u60c5\uff0c\u603b\u662f\u514d\u4e0d\u4e86\u4e00\u6b7b\u7684\u3002\u201d \u3000\u3000\u77f3\u749e\u7d27\u7d27\u63e1\u7740\u674e\u601d\u5357\u7684\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u674e\u5144\uff0c\u6709\u4e24\u53e5\u8bdd\u4e0d\u77e5\u6211\u8be5\u4e0d\u8be5\u8bf4\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u4f60\u6211\u540c\u9053\u4e2d\u4eba\uff0c\u4e00\u89c1\u5982\u6545\uff0c\u6709\u8bdd\u76f4\u8bf4\u65e0\u59a8\uff01\u201d\u77f3\u749e\u9053\uff1a\u201c\u8bf7\u674e\u5144\u628a\u773c\u5149\u770b \u8fdc\u4e00\u4e9b\uff0c\u4e0d\u8981\u53ea\u662f\u770b\u5230\u81ea\u5df1\u4eb2\u4eba\u6240\u53d7\u7684\u4e0d\u5e78\uff01\u201d \u3000\u3000\u8fd9\u4e24\u53e5\u8bdd\u4fe8\u5982\u5f53\u5934\u68d2\u559d\uff0c\u674e\u601d\u5357\u609a\u7136\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8bb2\u5f97\u5bf9\uff0c\u662f\u6211\u9519\u4e86\u3002\u201d\u77f3\u749e\u63a5\u4e0b \u53bb\u8bf4\u900d\uff1a\u201c\u5728\u8fd9\u573a\u5927\u6218\u4e71\u4e2d\uff0c\u65e0\u8f9c\u6b7b\u96be\u7684\u4eba\u4e0d\u77e5\u591a\u5c11\u3002\u5982\u679c\u90a3\u4f4d\u58ee\u70c8\u727a\u7272\u7684\u7ea2\u8863\u5973\u5b50\u5f53\u771f\u662f \u674e\u5144\u90a3\u4f4d\u59d1\u5a18\uff0c\u674e\u5144\u5c31\u66f4\u5e94\u8be5\u6709\u52c7\u6c14\u6d3b\u4e0b\u53bb\uff0c\u4e3a\u4e86\u7ed9\u5979\u62a5\u4ec7\uff0c\u4e5f\u4e3a\u4e86\u7ed9\u65e0\u6570\u65e0\u8f9c\u800c\u6b7b\u7684\u4eba\u4eec \u62a5\u4ec7\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u6ee1\u9762\u901a\u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u77f3\u5144\u7684\u91d1\u7389\u826f\u8a00\uff01\u201d\u62b9\u5e72\u773c\u6cea\uff0c\u62ac\u8d77\u5934\u6765\uff0c\u53ea\u89c1\u5929\u5df2\u4eae \u4e86\u3002 \u3000\u3000\u77f3\u749e\u9053\uff1a\u201c\u6211\u8be5\u8d70\u5566\u3002\u591a\u8c22\u674e\u5144\u544a\u8bc9\u6211\u5e08\u59b9\u7684\u6d88\u606f\uff0c\u6211\u60f3\u5230\u8774\u8776\u8c37\u627e\u5bfb\u5979\u4eec\u3002\u674e\u5144\uff0c\u4f60 \u6709\u4ec0\u4e48\u8ba1\u5212\uff0c\u5018\u82e5\u4f60\u4e0d\u6025\u4e8e\u56de\u5bb6\uff0c\u54b1\u4eec\u540c\u8d70\u5982\u4f55\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u8499\u53e4\u9a91\u5175\u6765\u53bb\u5982\u98ce\uff0c\u6b64\u65f6\u60f3\u5fc5\u662f\u6b63\u5728\u5411\u897f\u590f\u7684\u4eac\u57ce\u8fdb\u519b\uff0c\u6211\u60f3\u8d81\u8fd9\u4e2a\u5f53\u53e3 \u6e9c\u8fc7\u8fb9\u5883\u3002\u5426\u5219\u5f85\u4ed6\u4eec\u56de\u5e08\u653b\u91d1\uff0c\u90a3\u5c31\u96be\u4ee5\u8fc7\u5173\u4e86\u3002\u201d \u3000\u3000\u77f3\u749e\u77e5\u9053\u674e\u601d\u5357\u662f\u8499\u53e4\u7684\u201c\u9003\u72af\u201d\uff0c\u542c\u4ed6\u8bf4\u5f97\u6709\u7406\uff0c\u4fbf\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u6211\u4e0d\u52c9\u5f3a\u4f60 \u4e86\u3002\u54b1\u4eec\u540e\u4f1a\u6709\u671f\u4e86\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u4f60\u8fd9\u8eab\u67d3\u6709\u8840\u8ff9\u7684\u8863\u88f3\u6050\u6015\u60f9\u4eba\u6ce8\u76ee\uff0c\u82e5\u4e0d\u5acc\u5f03\uff0c\u8bf7\u628a\u6211\u8fd9\u4ef6\u5939\u8863\u7f69 \u4e0a\u3002\u201d\u674e\u601d\u5357\u8131\u4e0b\u7a7f\u5728\u4e2d\u95f4\u7684\u5939\u8863\uff0c\u56e0\u4e3a\u662f\u7a7f\u5728\u4e2d\u95f4\uff0c\u8fd8\u672a\u6cbe\u4e0a\u5c18\u571f\uff0c\u76f8\u5f53\u65b0\u51c0\u3002\u4ed6\u4eec\u7684\u8eab \u6750\u76f8\u5dee\u4e0d\u4e86\u591a\u5c11\uff0c\u77f3\u749e\u8131\u4e0b\u4fd8\u864f\u201c\u5939\u8863\u201d\uff0c\u7a7f\u4e0a\u8fd9\u4ef6\u5939\u8863\uff0c\u521a\u597d\u5408\u9002\u3002 \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u8499\u53e4\u7684\u5927\u519b\u4e0d\u77e5\u8fc7\u5b8c\u4e86\u6ca1\u6709\uff0c\u77f3\u5144\u4f60\u662f\u5411\u5185\u5730\u8d70\u7684\uff0c\u53ef\u5f97\u5206\u5916\u5c0f\u5fc3\u3002\u201d \u3000\u3000\u77f3\u749e\u9053\uff1a\u201c\u591a\u8c22\u674e\u5144\u63d0\u9192\uff0c\u6211\u4f1a\u5f53\u5fc3\u7684\u3002\u6211\u8d70\u5566\uff0c\u4f60\u53ef\u6709\u4ec0\u4e48\u8bdd\u8981\u6211\u5e26\u7ed9\u5e08\u59b9\u4e48\uff1f\u201d\u77f3 \u749e\u662f\u56e0\u4e3a\u674e\u601d\u5357\u6628\u5929\u8fd8\u548c\u4ed6\u7684\u5e08\u59b9\u540c\u5728\u4e00\u8d77\uff0c\u662f\u4ee5\u6709\u6b64\u4e00\u95ee\u3002 \u3000\u3000\u674e\u601d\u5357\u82e5\u6709\u6240\u601d\uff0c\u534a\u664c\u8bf4\u9053\uff1a\u201c\u6ca1\u6709\u4ec0\u4e48\uff0c\u53ea\u8bf7\u4ee3\u6211\u5411\u4ee4\u5e05\u59b9\u548c\u5b5f\u5973\u4fa0\u95ee\u5019\uff0c\u544a\u8bc9\u4ed6\u4eec \u6211\u5df2\u7ecf\u56de\u53bb\u4e86\u3002\u201d \u3000\u3000\u77f3\u749e\u8d70\u540e\uff0c\u674e\u601d\u5357\u6444\u571f\u4e3a\u9999\uff0c\u9ed8\u9ed8\u7977\u544a\uff1a\u201c\u5a49\u59b9\uff0c\u6211\u5411\u4f60\u53d1\u8a93\uff0c\u4e0d\u7ba1\u4f60\u662f\u6b7b\u662f\u6d3b\uff0c\u6211\u4eca \u751f\u51b3\u4e0d\u518d\u5a36\uff01\u4f60\u82e5\u4e0d\u5e78\u6b7b\u4e86\uff0c\u6211\u4e00\u5b9a\u8981\u591a\u6740\u51e0\u4e2a\u9791\u5b50\uff0c\u7ed9\u4f60\u62a5\u4ec7\uff01\u201d\u7977\u544a\u4e4b\u540e\uff0c\u4e0d\u89c9\u53c8\u6697\u6697 \u53f9\u4e86\u53e3\u6c14\uff0c\u5fc3\u9053\uff1a\u201c\u6211\u600e\u80fd\u8fd8\u5e7b\u60f3\u5979\u6d3b\u5728\u4eba\u95f4\uff0c\u5979\u5f53\u7136\u662f\u6b7b\u4e86\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u8fd9\u4e2a\u8a93\u8a00\u4e0d\u662f\u65e0\u56e0\u800c\u53d1\u7684\u3002 \u3000\u3000\u6768\u5a49\u751f\u524d\u6700\u4e0d\u653e\u5fc3\u4ed6\u4e0e\u5b5f\u660e\u971e\uff0c\u8fd9\u4e00\u70b9\u674e\u601d\u5357\u5f53\u7136\u4e0d\u4f1a\u4e0d\u77e5\u3002 \u3000\u3000\u4ed6\u4e4b\u6240\u4ee5\u4e0d\u613f\u610f\u8ddf\u77f3\u749e\u56de\u5230\u8774\u8776\u8c37\u53bb\u89c1\u5b5f\u660e\u971e\uff0c\u4e5f\u5c31\u662f\u4e3a\u4e86\u8fd9\u4e2a\u7f18\u6545\uff0c\u6768\u5a49\u82e5\u662f\u5728\u751f\u7684 \u8bdd\uff0c\u4ed6\u5012\u8fd8\u53ef\u4ee5\u548c\u5b5f\u660e\u971e\u4fdd\u6301\u53cb\u8c0a\uff1b\u6768\u5a49\u4e00\u6b7b\uff0c\u4ed6\u81ea\u611f\u5185\u759a\u4e8e\u5fc3\uff0c\u5374\u662f\u4e0d\u613f\u518d\u548c\u5b5f\u660e\u971e\u89c1\u9762 \u4e86\u3002\u662f\u4e3a\u4e86\u6015\u81ea\u5df1\u63a7\u5236\u4e0d\u4e86\u81ea\u5df1\u7684\u60c5\u611f\u5462\uff1f\u8fd8\u662f\u6084\u89e6\u52a8\u4e86\u81ea\u5df1\u7684\u521b\u4f24\uff1f\u4ed6\u4e0d\u77e5\u9053\uff0c\u4ed6\u4e5f\u6ca1\u6709 \u60f3\u8fc7\u3002\u8fd9\u662f\u9690\u85cf\u5728\u4ed6\u5185\u5fc3\u6df1\u5904\u7684\u79d8\u5bc6\uff0c\u8fde\u4ed6\u81ea\u5df1\u4e5f\u4e0d\u6562\u63ed\u5f00\u3002\u5982\u4eca\u4ed6\u4f5c\u4e86\u8fd9\u4e2a\u51b3\u5b9a\uff0c\u53ea\u662f\u56e0 \u4e3a\u4ed6\u8ba4\u4e3a\u201c\u5e94\u5f53\u201d\u8fd9\u6837\u505a\uff0c\u624d\u7b97\u201c\u5bf9\u5f97\u4f4f\u201d\u6768\u5a49\u3002 \u3000\u3000\u4f46\u6768\u5a49\u662f\u6b7b\u662f\u751f\uff0c\u8fd9\u5374\u8fd8\u662f\u4e00\u4e2a\u8c1c\u3002 \u3000\u3000\u77f3\u749e\u53ea\u662f\u8bf4\u5230\u6768\u5a49\u7528\u5315\u9996\u523a\u8fdb\u81ea\u5df1\u7684\u5fc3\u7a9d\u4e3a\u6b62\uff0c\u540e\u6765\u7684\u4e8b\u60c5\u4ed6\u4e5f\u5c31\u4e0d\u77e5\u9053\u4e86\u3002\u4ed6\u5e76\u6ca1\u6709 \u4eb2\u773c\u770b\u89c1\u6768\u5a49\u7684\u6b7b\u4ea1\u3002 \u3000\u3000\u5728\u611f\u60c5\u4e0a\uff0c\u674e\u601d\u5357\u662f\u5e0c\u671b\u6768\u5a49\u81ea\u6740\u672a\u6b7b\uff0c\u8fd8\u6d3b\u7740\u7684\u3002\u4f46\u6839\u636e\u5f53\u65f6\u7684\u60c5\u5f62\u5224\u65ad\uff0c\u7406\u667a\u544a\u8bc9 \u4ed6\uff0c\u6768\u5a49\u751f\u8fd8\u7684\u5e0c\u671b\u5df2\u662f\u6781\u4e3a\u6e3a\u832b\uff0c\u7b80\u76f4\u53ef\u4ee5\u8bf4\u662f\u51b3\u4e0d\u53ef\u80fd\u7684\u4e86\uff01\u674e\u601d\u5357\u53ea\u597d\u6000\u7740\u4e00\u9897\u521b\u4f24 \u7684\u5fc3\uff0c\u9065\u671b\u70fd\u706b\u6f2b\u5927\u7684\u6545\u56fd\uff0c\u4e00\u4e2a\u4eba\u518d\u8e0f\u4e0a\u4e86\u8346\u68d8\u6ee1\u9014\u7684\u65c5\u7a0b\u3002 \u3000\u3000\u674e\u601d\u5357\u4ee5\u4e3a\u6768\u5a49\u662f\u5fc5\u6b7b\u65e0\u7591\u4e86\uff0c\u54ea\u77e5\u4e8b\u60c5\u5f80\u5f80\u6709\u51fa\u4eba\u610f\u6599\u4e4b\u5916\uff0c\u6768\u5a49\u5e76\u6ca1\u6709\u6b7b\uff0c\u5979\u8fd8\u6d3b \u7740\uff01 \u3000\u3000\u4e0d\u9519\uff0c\u77f3\u749e\u6240\u89c1\u7684\u90a3\u4e2a\u7ea2\u8863\u5c11\u5973\u786e\u662f\u6768\u5a49\uff0c\u6768\u5a49\u5728\u90a3\u7fa4\u5982\u72fc\u4f3c\u864e\u7684\u8499\u53e4\u5175\u5411\u5979\u6251\u6765\u7684\u65f6 \u5019\uff0c\u5979\u62b1\u7740\u201c\u5b81\u6b7b\u4e0d\u8fb1\u201d\u7684\u5fc3\u60c5\uff0c\u4e5f\u7684\u786e\u66fe\u7528\u5315\u9996\u81ea\u6740\u3002 \u3000\u3000\u5c31\u5728\u5979\u5200\u5c16\u5212\u7834\u8863\u88f3\uff0c\u808c\u80a4\u5df2\u7ecf\u53ef\u4ee5\u611f\u5f97\u51b0\u51b7\u7684\u5200\u5c16\u7684\u65f6\u5019\uff0c\u5ffd\u5730\u624b\u8155\u597d\u50cf\u7ed9\u8682\u8681\u53ee\u4e86 \u4e00\u4e0b\uff0c\u5200\u5c16\u6ed1\u8fc7\u4e00\u8fb9\uff0c\u6768\u5a49\u5403\u4e86\u4e00\u60ca\uff0c\u5315\u9996\u5760\u4e0b\u3002 \u3000\u3000\u4e5f\u5c31\u662f\u5728\u8fd9\u4e2a\u5343\u94a7\u4e00\u53d1\u4e4b\u65f6\uff0c\u72c2\u98ce\u9aa4\u8d77\uff0c\u9ec4\u6c99\u853d\u5929\u3002\u53ea\u542c\u5f97\u51e0\u58f0\u88c2\u4eba\u5fc3\u80ba\u7684\u547c\u558a\uff0c\u90a3\u51e0 \u4e2a\u5373\u5c06\u6251\u5230\u5979\u8eab\u524d\u7684\u9791\u5b50\u7a81\u7136\u5012\u4e0b\uff01 \u3000\u3000\u8f6c\u77ac\u95f4\u6740\u58f0\u9707\u5730\uff0c\u9ec4\u6c99\u6f20\u6f20\u4e4b\u4e2d\u6218\u9a6c\u5954\u817e\uff0c\u5200\u67aa\u8000\u773c\uff0c\u968f\u7740\u98ce\u66b4\u7684\u5230\u6765\uff0c\u4e5f\u7206\u53d1\u4e86\u4fd8\u864f \u7684\u5927\u66b4\u52a8\uff0c\u5f15\u8d77\u4e86\u5927\u6df7\u4e71\u3002 \u3000\u3000\u6768\u5a49\u8d81\u8fd9\u4e2a\u6df7\u4e71\u7684\u673a\u4f1a\u8fde\u5fd9\u9003\u8dd1\uff0c\u5979\u7684\u672c\u9886\u672c\u6765\u4e0d\u5f31\uff0c\u8f7b\u529f\u66f4\u662f\u9ad8\u660e\uff0c\u6df7\u4e71\u4e2d\u6740\u4e86\u51e0\u4e2a \u8ffd\u5175\uff0c\u5c45\u7136\u9003\u51fa\u53bb\u4e86\u3002 \u3000\u3000\u9ed1\u6697\u4e2d\u96be\u5206\u654c\u53cb\uff0c\u5979\u4e0d\u6562\u5411\u4eba\u591a\u7684\u5730\u65b9\u8dd1\uff0c\u8dd1\u5230\u4e86\u8352\u91ce\u4e0a\u3002\u6b64\u65f6\u72c2\u98ce\u4ea6\u5df2\u6e10\u6e10\u9759\u6b62\u4e86\u3002 \u3000\u3000\u6768\u5a49\u56db\u987e\u832b\u832b\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5357\u54e5\u4e0d\u77e5\u600e\u4e48\u6837\u4e86\uff1f\u6211\u8ba4\u4e0d\u5f97\u8def\uff0c\u600e\u4e48\u529e\u5462\uff1f\u201d\u80e1\u4e71\u5730 \u8d70\uff0c\u6050\u6015\u4f1a\u78b0\u4e0a\u8499\u53e4\u5175\uff0c\u4f46\u8fd9\u8352\u91ce\u53c8\u975e\u5b89\u8eab\u4e4b\u5730\u3002 \u3000\u3000\u6768\u5a49\u6b63\u81ea\u8e0c\u8e87\uff0c\u5ffd\u89c1\u4e00\u4e2a\u4eba\u5411\u5979\u8dd1\u6765\uff0c\u8dd1\u5f97\u65cb\u98ce\u4e5f\u4f3c\uff0c\u8fd9\u8eab\u8f7b\u529f\u51b3\u4e0d\u5728\u5979\u4e4b\u4e0b\u3002 \u3000\u3000\u6768\u5a49\u5927\u5403\u4e00\u60ca\uff0c\u53ea\u9053\u662f\u8ffd\u5175\u6765\u5230\u3002\u5979\u7684\u5251\u5728\u88ab\u4fd8\u864f\u7684\u65f6\u5019\u5df2\u88ab\u7f34\u53bb\uff0c\u8d34\u8eab\u6536\u85cf\u7684\u4e00\u67c4\u5315 \u9996\u4e5f\u5931\u6389\u4e86\u3002\u6765\u8005\u82e5\u662f\u5f3a\u654c\uff0c\u5979\u624b\u65e0\u5bf8\u94c1\uff0c\u53ef\u662f\u96be\u4ee5\u62b5\u6321\u3002 \u3000\u3000\u6768\u5a49\u6b63\u81ea\u7740\u6025\uff0c\u90a3\u4eba\u5df2\u6765\u5230\u5979\u7684\u9762\u524d\uff0c\u51fa\u4e4e\u6768\u5a49\u7684\u610f\u5916\uff0c\u6765\u7684\u7adf\u662f\u4e2a\u8eab\u6750\u9880\u9880\u7684\u7f8e\u5c11 \u5e74\uff0c\u770b\u6837\u5b50\u4e0d\u50cf\u574f\u4eba\uff0c\u66f4\u5947\u602a\u7684\u662f\uff0c\u6768\u5a49\u660e\u77e5\u81ea\u5df1\u4ece\u6765\u6ca1\u6709\u89c1\u8fc7\u6b64\u4eba\uff0c\u4f46\u5374\u6709\u201c\u4f3c\u66fe\u76f8\u8bc6\u201d \u7684\u611f\u89c9\u3002 \u3000\u3000\u8fd9\u7f8e\u5c11\u5e74\u4e5f\u5f88\u6e29\u6587\u6709\u793c\uff0c\u6765\u5230\u6768\u5a49\u9762\u524d\uff0c\u606d\u606d\u656c\u656c\u5730\u4f5c\u4e86\u4e00\u63d6\uff0c\u8bf4\u9053\uff1a\u201c\u8bf7\u6055\u5192\u772f\uff0c\u6211 \u4e00\u76f4\u653e\u5fc3\u4e0d\u4e0b\uff0c\u53ea\u6015\u59d1\u5a18\u8131\u4e0d\u4e86\u9669\u3002\u59d1\u5a18\uff0c\u4f60\u65e0\u6059\u4e48\uff1f\u201d \u3000\u3000\u6768\u5a49\u597d\u751f\u8be7\u5f02\uff0c\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u201d \u3000\u3000\u8fd9\u5c11\u5e74\u9053\uff1a\u201c\u5c0f\u751f\u59d3\u675c\uff0c\u6728\u571f\u675c\uff0c\u5355\u540d\u4e00\u4e2a\u96c4\u5b57\uff0c\u82f1\u96c4\u7684\u96c4\u3002\u4f46\u6211\u53ea\u4f1a\u51e0\u624b\u4e09\u811a\u732b\u7684\u529f \u592b\uff0c\u4e0e\u82f1\u96c4\u4e8c\u5b57\u5374\u662f\u76f8\u5dee\u592a\u8fdc\u3002\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\u6211\u66fe\u7ecf\u9047\u9669\uff1f\u201d \u3000\u3000\u675c\u96c4\u5e76\u4e0d\u76f4\u63a5\u56de\u7b54\u8fd9\u53e5\u95ee\u8bdd\uff0c\u5374\u638f\u51fa\u4e00\u628a\u67d3\u8840\u7684\u5315\u9996\uff0c\u9012\u7ed9\u6768\u5a49\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u59d1\u5a18\u7684 \u4e1c\u897f\u5427\uff1f\u59d1\u5a18\u529b\u6377\u5f3a\u5bc7\uff0c\u5c0f\u53ef\u5341\u5206\u4f69\u670d\u3002\u201d \u3000\u3000\u6768\u5a49\u604d\u7136\u5927\u609f\uff0c\u8bf4\u9053\uff1a\u201c\u54e6\uff0c\u6562\u60c5\u4f60\u5c31\u662f\u521a\u624d\u6551\u6211\u7684\u90a3\u4e2a\u4eba\uff1f\u201d\u8fde\u5fd9\u5411\u675c\u96c4\u9053\u8c22\u3002 \u3000\u3000\u675c\u96c4\u9053\uff1a\u201c\u6211\u521a\u624d\u4e5f\u662f\u6df7\u5728\u4fd8\u864f\u961f\u4e2d\uff0c\u5e78\u4e8f\u90a3\u573a\u98ce\u66b4\u6765\u5f97\u5de7\uff0c\u6211\u624d\u80fd\u591f\u4fa5\u5e78\u6210\u529f\u3002\u54b1\u4eec \u90fd\u662f\u53d7\u96be\u7684\u4eba\uff0c\u60a3\u96be\u76f8\u52a9\uff0c\u7406\u6240\u5f53\u4e3a\uff0c\u59d1\u5a18\uff0c\u4f60\u4e0d\u5fc5\u5ba2\u6c14\u3002\u55ef\uff0c\u6211\u8fd8\u6ca1\u6709\u8bf7\u6559\u59d1\u5a18\u7684\u82b3\u540d \u5462\uff0c\u53ef\u80af\u89c1\u544a\u4e48\uff1f\u201d \u3000\u3000\u6768\u5a49\u8bf4\u4e86\u81ea\u5df1\u7684\u540d\u5b57\uff0c\u675c\u96c4\u95ee\u9053\uff1a\u201c\u6768\u59d1\u5a18\uff0c\u4f60\u4e0a\u54ea\u513f\uff1f\u201d \u3000\u3000\u6768\u5a49\u542c\u4ed6\u8bdd\u4e2d\u4f3c\u6709\u9080\u5979\u540c\u884c\u4e4b\u610f\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u8fd9\u4eba\u662f\u4e2a\u4fa0\u4e49\u4e4b\u58eb\uff0c\u4ed6\u6551\u4e86\u6211\u7684\u6027\u547d\uff0c \u6211\u544a\u8bc9\u4ed6\u5b9e\u8bdd\uff0c\u6599\u4ea6\u65e0\u59a8\u3002\u201d \u3000\u3000\u5f53\u4e0b\u6768\u5a49\u8863\u8884\u4e00\u63d6\uff0c\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u6069\u516c\u76f8\u6551\uff0c\u6211\u4eec\u672c\u662f\u592b\u59bb\u4e24\u4eba\uff0c\u51c6\u5907\u56de\u4e61\u7684\u3002\u201d\u6768\u5a49 \u628a\u81ea\u5df1\u8bf4\u6210\u6709\u592b\u4e4b\u5987\uff0c\u4e43\u662f\u51fa\u4e8e\u4e00\u79cd\u9632\u5fae\u675c\u6e10\u7684\u5fc3\u7406\uff0c\u867d\u7136\u5979\u4fe1\u5f97\u8fc7\u675c\u96c4\u4e0d\u662f\u574f\u4eba\u3002 \u3000\u3000\u675c\u96c4\u6014\u4e86\u4e00\u6014\uff0c\u4f3c\u4e4e\u5fbd\u611f\u610f\u5916\uff0c\u4f46\u968f\u5373\u5c31\u795e\u8272\u5982\u5e38\uff0c\u7528\u540c\u60c5\u7684\u53e3\u543b\u95ee\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u4f60 \u4eec\u592b\u59bb\u662f\u7ed9\u4e71\u5175\u51b2\u6563\u7684\uff0c\u8fd9\u53ef\u771f\u662f\u592a\u4e0d\u5e78\u4e86\u3002\u4f46\u4f60\u7684\u672c\u9886\u8fd9\u6837\u597d\uff0c\u5c0a\u592b\u60f3\u5fc5\u662f\u6b66\u6797\u4e2d\u6709\u540d\u7684 \u4eba\u7269\uff0c\u5927\u7ea6\u4e5f\u53ef\u4ee5\u8131\u8138\u5427\uff0c\u5374\u4e0d\u77e5\u5c0a\u592b\u662f\u54ea\u4e00\u4f4d\uff1f\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u6211\u7684\u4e08\u592b\u540d\u5524\u674e\u601d\u5357\uff0c\u4eca\u5e74\u4e8c\u5341\u4e09\u5c81\uff0c\u5931\u6563\u4e4b\u65f6\u4ed6\u7a7f\u7684\u662f\u4e00\u4ef6\u7070\u8272\u7684\u8001\u7f8a\u76ae \u8884\uff0c\u4e0d\u77e5\u4f60\u53ef\u66fe\u89c1\u8fc7\u4ed6\uff1f\u201d\u8ddf\u7740\u8be6\u7ec6\u7684\u8bf4\u4e86\u674e\u601d\u5357\u7684\u76f8\u8c8c\u7279\u5f81\u3002 \u3000\u3000\u675c\u96c4\u542c\u4e86\u4e4b\u540e\uff0c\u4f4e\u4e0b\u4e86\u5934\uff0c\u53f9\u9053\uff1a\u201c\u674e\u5927\u5ac2\uff0c\u539f\u6765\u90a3\u4eba\u5c31\u662f\u4f60\u7684\u4e08\u592b\uff0c\u5509\u2014\u2014\u4f60\u542c\u4e86\u53ef \u522b\u96be\u8fc7\uff01\u201d \u3000\u3000\u6768\u5a49\u60ca\u559c\u4ea4\u96c6\uff0c\u8fde\u5fd9\u95ee\u9053\uff1a\u201c\u4f60\u89c1\u7740\u4ed6\u4e86\uff1f\u4ed6\u3001\u4ed6\u51fa\u4e86\u4ec0\u4e48\u4e8b\u60c5\uff1f\u201d \u3000\u3000\u675c\u96c4\u9053\uff1a\u201c\u4ed6\u6b63\u662f\u548c\u6211\u4e00\u9053\u4ece\u4e71\u519b\u4e2d\u6740\u51fa\u6765\u7684\u3002\u8499\u53e4\u7684\u4e00\u4e2a\u5c06\u519b\u5341\u5206\u5389\u5bb3\uff0c\u4e0d\u77e5\u600e\u7684\uff0c \u4ed6\u597d\u4f3c\u8ba4\u5f97\u4f60\u7684\u4e08\u592b\uff0c\u5bf9\u4ed6\u7279\u522b\u8ffd\u5f97\u7d27\uff0c\u4ed6\u3001\u4ed6\u7ed9\u90a3\u4e2a\u5c06\u519b\u4e00\u7bad\u5c04\u6b7b\u4e86\uff01\u201d \u3000\u3000\u6768\u5a49\u5728\u4e71\u519b\u4e4b\u4e2d\u4e5f\u66fe\u89c1\u5230\u54f2\u522b\u7684\u5e05\u65d7\uff0c\u5982\u4eca\u675c\u96c4\u8bf4\u7684\u8fd9\u4e2a\u8499\u53e4\u5c06\u519b\uff0c\u867d\u7136\u6ca1\u6709\u8bf4\u51fa\u540d \u5b57\uff0c\u6768\u5a49\u5df1\u77e5\u5fc5\u662f\u54f2\u522b\u65e0\u7591\u3002\u54f2\u522b\u7684\u795e\u7bad\u6768\u5a49\u662f\u89c1\u8fc7\u7684\uff0c\u675c\u96c4\u8bf4\u674e\u601d\u5357\u6b7b\u5728\u54f2\u522b\u7bad\u4e0b\uff0c\u6768\u5a49 \u54ea\u80fd\u4e0d\u4fe1\u3002 \u3000\u3000\u8fd9\u5239\u90a3\u95f4\uff0c\u6768\u5a49\u53ea\u89c9\u5730\u8f6c\u5929\u65cb\uff0c\u4eba\u4f3c\u98ce\u4e2d\u4e4b\u70db\uff0c\u6447\u6447\u6b32\u5760\u3002 \u3000\u3000\u5728\u5c06\u8fd1\u660f\u8ff7\u7684\u72b6\u6001\u4e2d\uff0c\u6768\u5a49\u611f\u5230\u4e00\u53cc\u6709\u529b\u7684\u624b\u81c2\u5c06\u5979\u62b1\u4f4f\uff0c\u6768\u5a49\u970d\u7136\u4e00\u60ca\uff0c\u4e0d\u5047\u601d\u7d22\u5730 \u5c31\u628a\u90a3\u4eba\u63a8\u5f00\uff0c\u7741\u773c\u4e00\u770b\uff0c\u53ea\u89c1\u675c\u96c4\u6ee1\u9762\u901a\u7ea2\u7684\u7ad9\u5728\u5979\u7684\u8eab\u65c1\uff0c\u5583\u5583\u8bf4\u9053\uff1a\u201c\u6211\u6015\u4f60\u6655\u8fc7 \u53bb\uff0c\u674e\u5927\u5ac2\uff0c\u4eba\u6b7b\u4e0d\u80fd\u590d\u751f\uff0c\u4f60\u4e5f\u4e0d\u8981\u592a\u60b2\u4f24\u4e86\u3002\u201d \u3000\u3000\u6768\u5a49\u51fa\u81ea\u540d\u95e8\uff0c\u7d20\u6765\u6ce8\u91cd\u793c\u4eea\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u4eba\u867d\u7136\u597d\u5fc3\uff0c\u4f46\u6211\u53ef\u4e0d\u80fd\u8981\u4e00\u4e2a\u964c\u751f\u7684 \u91cc\u4eba\u670d\u4f8d\u3002\u6211\u5fc5\u987b\u633a\u4f4f\uff0c\u4e0d\u80fd\u660f\u8ff7\uff01\u201d\u5e78\u4e8f\u6768\u5a49\u6709\u6b64\u987e\u8651\uff0c\u8fd9\u624d\u6ca1\u6709\u6655\u5012\u3002 \u3000\u3000\u6768\u5a49\u5fcd\u7740\u773c\u6cea\uff0c\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u4f60\u544a\u8bc9\u6211\u8fd9\u4e2a\u6d88\u606f\u3002\u4f60\u7684\u6069\u60e0\u6211\u662f\u4e0d\u80fd\u62a5\u7b54\u4e86\uff0c\u4f60\u53bb \u5427\uff01\u201d \u3000\u3000\u675c\u96c4\u89c1\u5979\u604d\u4f3c\u68a8\u82b1\u5e26\u96e8\uff0c\u66f4\u663e\u5f97\u59ff\u5bb9\u6e05\u4e3d\uff0c\u4ee4\u4eba\u7231\u601c\uff0c\u4e0d\u7981\u6026\u7136\u5fc3\u52a8\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u8fd9 \u6837\u7684\u7f8e\u4eba\u513f\u54ea\u91cc\u53bb\u627e\uff1f\u4f55\u51b5\u5979\u53c8\u662f\u540d\u95e8\u95fa\u79c0\uff0c\u624d\u8c8c\u53cc\u5168\uff01\u867d\u7136\u662f\u518d\u5a5a\u4e4b\u5987\uff0c\u6211\u82e5\u5f97\u5979\u4e3a\u59bb\uff0c \u4e5f\u4e0d\u6789\u865a\u5ea6\u6b64\u751f\u4e86\u3002\u4f46\u8fdf\u65e9\u662f\u5230\u53e3\u7684\u9992\u5934\uff0c\u6211\u53ef\u4e0d\u80fd\u64cd\u4e4b\u8fc7\u6025\u3002\u6211\u8981\u5979\u7518\u5fc3\u60c5\u613f\u5730\u5ac1\u7ed9\u6211\uff0c \u624d\u6709\u60c5\u8da3\u53ef\u8a00\u3002\u201d \u3000\u3000\u675c\u96c4\u7684\u672c\u9886\u672c\u6765\u5728\u6768\u5a49\u4e4b\u4e0a\uff0c\u5047\u5982\u4ed6\u8981\u7528\u5f3a\u7684\u8bdd\uff0c\u6768\u5a49\u521a\u624d\u90a3\u4e00\u638c\u51b3\u4e0d\u80fd\u5c06\u4ed6\u63a8\u5f00\u3002\u53ea \u56e0\u4ed6\u4e00\u6765\u77e5\u9053\u6768\u5a49\u7684\u5bb6\u4e16\uff0c\u4e8c\u6765\u89c1\u4e86\u6768\u5a49\u50b2\u7136\u4e0d\u53ef\u4fb5\u72af\u7684\u795e\u6c14\uff0c\u4e0d\u7531\u5f97\u5fc3\u4e2d\u865a\u602f\uff0c\u5bf9\u6768\u5a49\u53c8 \u662f\u7231\u6155\uff0c\u53c8\u662f\u8d77\u656c\uff0c\u8fd9\u624d\u6539\u53d8\u4e86\u4e3b\u610f\uff0c\u51b3\u610f\u8981\u7528\u6c34\u78e8\u7684\u529f\u592b\uff0c\u4ee4\u6768\u5a49\u5c31\u8303\u3002 \u3000\u3000\u675c\u96c4\u4e3b\u610f\u6253\u5b9a\uff0c\u88c5\u51fa\u4e00\u526f\u5341\u5206\u5173\u5fc3\u7684\u795e\u6c14\u8bf4\u9053\uff1a\u201c\u674e\u5927\u5ac2\u4f60\u53bb\u54ea\u513f\uff1f\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u4f60\u4e0d\u5fc5\u7ba1\u6211\uff0c\u6211\u81ea\u6709\u6211\u7684\u53bb\u5904\u3002\u201d\u8bf4\u7740\u8bdd\uff0c\u773c\u6cea\u4e0d\u7981\u53c8\u6ef4\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u675c\u96c4\u8bf4\u9053\uff1a\u201c\u674e\u5927\u5ac2\uff0c\u4f60\u53ef\u4e0d\u80fd\u7cca\u6d82\u3002\u4e00\u6b7b\u5bb9\u6613\uff0c\u4f46\u5c0a\u592b\u672a\u4e86\u4e4b\u4e8b\uff0c\u5374\u7531\u4f55\u4eba\u4ee3\u529e\uff1f\u6bd4 \u5982\u8bf4\uff0c\u4f60\u5c1a\u6709\u7fc1\u59d1\u7684\u8bdd\uff0c\u8fd9\u5949\u517b\u4e4b\u8d23\uff0c\u522b\u4eba\u5c31\u66ff\u4ee3\u4e0d\u4e86\u3002\u751f\u6bd4\u6b7b\u96be\uff0c\u4f60\u662f\u5973\u4e2d\u8c6a\u6770\uff0c\u60f3\u4e0d\u81f3 \u4e8e\u907f\u96be\u5c31\u6613\uff01\u201d \u3000\u3000\u8fd9\u756a\u8bdd\u8bf4\u5f97\u5341\u5206\u8bda\u6073\uff0c\u4e5f\u6b63\u597d\u8bf4\u4e2d\u4e86\u6768\u5a49\u7684\u5fc3\u601d\u3002\u539f\u6765\u6768\u5a49\u679c\u7136\u662f\u60f3\u6b89\u592b\u7684\uff0c\u542c\u4e86\u675c\u96c4 \u8fd9\u4e00\u756a\u8bdd\uff0c\u4e0d\u7981\u6e05\u9192\u8fc7\u6765\uff0c\u60f3\u9053\uff0c\u201c\u4e0d\u9519\uff0c\u6211\u7684\u5a46\u5a46\u8fd8\u5728\uff0c\u6211\u751f\u662f\u674e\u5bb6\u7684\u4eba\u6b7b\u662f\u674e\u5bb6\u7684\u9b3c\uff0c \u6211\u5c31\u662f\u8981\u6b7b\uff0c\u4e5f\u8be5\u89c1\u4e86\u6211\u7684\u5a46\u5a46\uff0c\u5f85\u5979\u8001\u4eba\u5bb6\u5f97\u7ec8\u5929\u5e74\u4e4b\u540e\u627e\u624d\u80fd\u6b7b\u3002\u8fd8\u6709\u674e\u5927\u54e5\u7684\u5927\u4ec7\u672a \u62a5\uff0c\u6211\u53c8\u5c82\u80fd\u8f7b\u751f\uff1f\u201d \u3000\u3000\u60f3\u81f3\u6b64\u5904\uff0c\u6768\u5a49\u6bc5\u7136\u8bf4\u9053\uff1a\u201c\u4f60\u653e\u5fc3\uff0c\u6211\u4e0d\u4f1a\u6b7b\u7684\uff01\u201d \u3000\u3000\u675c\u96c4\u9053\uff1a\u201c\u73b0\u4e0b\u5175\u614c\u9a6c\u4e71\uff0c\u6b64\u53bb\u4e2d\u539f\uff0c\u8fe2\u8fe2\u4e07\u91cc\uff0c\u5927\u5ac2\u867d\u662f\u5dfe\u5e3c\u987b\u7709\uff0c\u4e00\u4e2a\u4eba\u8d70\u8fd9\u6837\u7684 \u957f\u9014\u603b\u662f\u4e0d\u4fbf\u3002\u6211\u4e5f\u662f\u8981\u56de\u4e2d\u539f\u7684\uff0c\u4e0d\u5982\u4f60\u6211\u7ed3\u4f34\u540c\u884c\uff0c\u5f7c\u6b64\u6709\u4e2a\u7167\u987e\uff0c\u597d\u5417\uff1f\u201d \u3000\u3000\u6768\u5a49\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u4eba\u770b\u6765\u4f3c\u662f\u4e2a\u6b63\u4eba\u541b\u5b50\uff0c\u4f46\u5b64\u7537\u5be1\u5973\uff0c\u4e07\u91cc\u540c\u884c\uff0c\u4e5f\u603b\u662f\u4e0d\u4fbf\u3002\u201d \u8f6c\u5ff5\u53c8\u60f3\uff1a\u201c\u4f46\u6211\u603b\u5f97\u6709\u4eba\u5e26\u8def\uff0c\u4e0d\u4f9d\u9760\u4ed6\u5374\u53c8\u9760\u8c01\uff1f\u201d \u3000\u3000\u6768\u5a49\u8e0c\u8e87\u672a\u51b3\uff0c\u675c\u96c4\u597d\u4f3c\u731c\u5230\u5979\u7684\u5fc3\u610f\uff0c\u8bf4\u9053\uff1a\u201c\u65f6\u9022\u4e71\u4e16\uff0c\u5b88\u793c\u4e0d\u82e5\u4ece\u6743\u3002\u6c5f\u6e56\u513f \u5973\uff0c\u4f3c\u4e5f\u4e0d\u5fc5\u592a\u62d8\u6ce5\u4e8e\u907f\u7537\u5973\u4e4b\u5acc\u3002\u53ea\u8981\u54b1\u4eec\u5149\u660e\u78ca\u843d\uff0c\u540c\u884c\u53c8\u6709\u4f55\u59a8\uff1f\u5927\u5ac2\uff0c\u4f60\u5c31\u5f53\u6211\u662f \u4eb2\u4eba\u597d\u5566\u3002\u8def\u4e0a\u82e5\u662f\u6709\u4eba\u95ee\u8d77\uff0c\u54b1\u4eec\u53ef\u4ee5\u8ba4\u4f5c\u2014\u2014\u201d \u3000\u3000\u6768\u5a49\u5fc3\u5ff5\u4e00\u52a8\uff0c\u8bf4\u9053\uff1a\u201c\u5bf9\uff0c\u54b1\u4eec\u53ef\u4ee5\u8ba4\u4f5c\u5144\u59b9\u3002\u4f60\u6551\u4e86\u6211\u7684\u6027\u547d\uff0c\u6211\u65e0\u4ee5\u4e3a\u62a5\uff0c\u53ea\u6709 \u628a\u4f60\u5f53\u4f5c\u5927\u54e5\u4e00\u6837\u5c0a\u656c\uff0c\u7565\u5c3d\u5bf8\u5fc3\u3002\u201d \u3000\u3000\u675c\u96c4\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u6b63\u6709\u6b64\u610f\uff0c\u96be\u5f97\u4f60\u4e5f\u4e00\u6837\u5fc3\u601d\u3002\u90a3\u5c31\u8bf7\u6055\u6211\u5984\u81ea\u5c0a\u5927\uff0c\u6211\u53ef \u8981\u53eb\u4f60\u4e00\u58f0\u59b9\u5b50\u5566\u3002\u201d\u5fc3\u91cc\u5374\u60f3\uff1a\u201c\u73b0\u5728\u6211\u53eb\u4f60\u59b9\u5b50\uff0c\u5c06\u6765\u603b\u6709\u4e00\u5929\u53eb\u4f60\u5a18\u5b50\u3002\u201d \u3000\u3000\u81ea\u6b64\u4e24\u4eba\u4e00\u8def\u540c\u884c\u3002\u6700\u521d\u51e0\u5929\uff0c\u6768\u5a49\u8fd8\u662f\u5c0f\u5fc3\u7ffc\u7ffc\u5730\u63d0\u9632\uff0c\u540e\u6765\u89c1\u675c\u96c4\u5904\u5904\u4ee5\u793c\u81ea\u6301\uff0c \u8fd9\u624d\u6e10\u6e10\u653e\u4e86\u5fc3\u3002 \u3000\u3000\u675c\u96c4\u4f3c\u4e4e\u5341\u5206\u719f\u6089\u5730\u7406\uff0c\u5e26\u9886\u6768\u5a49\u591c\u884c\u65e5\u5bbf\uff0c\u4e13\u8d70\u5c71\u8def\uff0c\u679c\u7136\u6ca1\u6709\u78b0\u4e0a\u4e71\u519b\u3002\u5728\u8352\u6797\u91cc \u7761\u89c9\u7684\u65f6\u5019\uff0c\u675c\u96c4\u603b\u662f\u8d70\u5f97\u8fdc\u8fdc\u7684\u7ed9\u5979\u770b\u5b88\u3002\u6768\u5a49\u521d\u65f6\u4e0d\u6562\u719f\u7761\uff0c\u540e\u6765\u597d\u51e0\u6b21\u89c1\u4ed6\u5982\u6b64\uff0c\u5fc3 \u91cc\u53cd\u800c\u89c9\u5f97\u8fc7\u610f\u4e0d\u53bb\uff0c\u81ea\u52a8\u63d0\u51fa\u8981\u548c\u4ed6\u8f6e\u73ed\u770b\u5b88\uff0c\u5bf9\u4ed6\u7684\u6212\u5907\u4e5f\u677e\u61c8\u8bb8\u591a\u4e86\u3002 \u3000\u3000\u6768\u5a49\u6697\u6697\u5e86\u5e78\uff0c\u96be\u5f97\u9047\u4e0a\u8fd9\u6837\u4e00\u4e2a\u5b88\u793c\u7684\u541b\u5b50\uff0c\u6b8a\u4e0d\u77e5\u8fd9\u6b63\u662f\u675c\u96c4\u7684\u624b\u6bb5\uff0c\u8981\u730e\u53d6\u5979\u7684 \u82b3\u5fc3\u3002 \u3000\u3000\u8fc7\u4e86\u51e0\u5929\uff0c\u4e24\u4eba\u6e10\u6e10\u719f\u7edc\uff0c\u675c\u96c4\u95ee\u8d77\u5979\u5728\u8499\u53e4\u7684\u7ecf\u5386\uff0c\u6768\u5a49\u89c9\u5f97\u53ef\u4ee5\u5bf9\u4ed6\u8bf4\u7684\u90fd\u5bf9\u4ed6\u8bf4 \u4e86\u3002\u4e0d\u8fc7\u6768\u5a49\u867d\u7136\u4e0d\u662f\u201c\u53ea\u8bf4\u4e09\u5206\u8bdd\u201d\uff0c\u5374\u4e5f\u5e76\u672a\u201c\u5168\u629b\u4e00\u7247\u5fc3\u201d\uff0c\u5979\u8ba4\u4e3a\u4e0d\u8be5\u8bf4\u7684\u5c31\u53ea\u5b57 \u4e0d\u63d0\u3002\u4f8b\u5982\u5c60\u767e\u57ce\u4e4b\u6b7b\uff0c\u5c60\u51e4\u9080\u4e86\u5b5f\u660e\u971e\u6765\u523a\u6740\u7236\u4ec7\u4eba\u7b49\u7b49\uff0c\u56e0\u4e3a\u8fd9\u662f\u522b\u4eba\u7684\u4e8b\u60c5\uff0c\u5979\u5c31\u6ca1 \u6709\u548c\u675c\u96c4\u8bf4\u8fc7\u3002 \u3000\u3000\u675c\u96c4\u7684\u8eab\u4e16\u4e5f\u53ea\u662f\u9690\u9690\u7ea6\u7ea6\u5730\u5411\u5979\u5410\u9732\uff0c\u6768\u5a49\u53ea\u77e5\u9053\u4ed6\u662f\u6b66\u6797\u4e16\u5bb6\uff0c\u7236\u4eb2\u5df2\u6b7b\uff0c\u6bcd\u4eb2\u517b \u75c5\u5728\u5bb6\uff0c\u53ea\u6709\u4e00\u4e2a\u59b9\u5b50\u3002\u5176\u4ed6\u7684\u4e8b\u60c5\uff0c\u675c\u96c4\u6ca1\u6709\u591a\u8bf4\uff0c\u6768\u5a49\u4e0d\u662f\u597d\u7ba1\u95f2\u4e8b\u7684\u4eba\uff0c\u4e5f\u5c31\u6ca1\u6709\u95ee \u4ed6\u3002 \u3000\u3000\u53ea\u6709\u4e00\u5929\uff0c\u5f53\u6768\u5a49\u8c08\u8d77\u5979\u54e5\u54e5\u60e8\u6b7b\u7684\u65f6\u5019\uff0c\u675c\u96c4\u53f9\u6c14\u8bf4\u9053\uff1a\u201c\u6211\u867d\u6709\u4e00\u4e2a\u59b9\u5b50\uff0c\u4f46\u6709\u7b49 \u4e8e\u65e0\uff0c\u8bf4\u8d77\u6765\u54b1\u4eec\u90fd\u662f\u4e00\u6837\u3002\u201d\u6768\u5a49\u8fd9\u624d\u77e5\u9053\u4ed6\u4eec\u5144\u59b9\u65e9\u5df2\u5931\u548c\u3002\u4f46\u675c\u96c4\u4e5f\u53ea\u662f\u8bf4\u4e86\u90a3\u4e48\u4e00 \u53e5\uff0c\u5931\u548c\u7684\u539f\u56e0\u5374\u6ca1\u6709\u518d\u8c08\u4e0b\u53bb\u3002\u6768\u5a49\u5fc3\u60f3\u8fd9\u4e00\u5b9a\u662f\u4ed6\u59b9\u59b9\u4e0d\u5bf9\uff0c\u6015\u5f15\u8d77\u793e\u96c4\u7684\u4f24\u5fc3\uff0c\u6b64\u540e \u4e00\u76f4\u907f\u514d\u63d0\u4ed6\u59b9\u59b9\u3002 \u3000\u3000\u4e00\u8def\u65e0\u4e8b\uff0c\u8fd9\u4e00\u5929\u4ed6\u4eec\u51fa\u4e86\u897f\u590f\u56fd\u5883\uff0c\u6765\u5230\u4e86\u6218\u706b\u5c1a\u672a\u6ce2\u53ca\u7684\u4e00\u4e2a\u5c0f\u9547\u3002 \u3000\u3000\u6768\u5a49\u6ee1\u6000\u6b22\u559c\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u4e86\uff0c\u603b\u7b97\u56de\u5230\u6c49\u4eba\u7684\u5730\u65b9\u3002\u8fd9\u4e2a\u9547\u5b50\u4e0d\u77e5\u53ef\u6709\u6210\u8863\u5e97\uff0c\u627e\u60f3 \u4e70\u51e0\u4ef6\u66ff\u6362\u8863\u88f3\u3002\u201d \u3000\u3000\u675c\u96c4\u7b11\u9053\uff1a\u201c\u662f\u554a\uff0c\u8fd9\u5341\u51e0\u5929\u6765\u5f53\u771f\u662f\u59d4\u5c48\u4f60\u4e86\u3002\u7761\u5728\u91ce\u5730\u8352\u5c71\uff0c\u6ee1\u8eab\u5c18\u58eb\uff0c\u628a\u4f60\u7684\u5929 \u751f\u4e3d\u8d28\u90fd\u906e\u63a9\u4e86\u3002\u6211\u8bf4\u4f60\u8fd8\u5e94\u8be5\u4e70\u4e00\u9762\u955c\u5b50\uff0c\u4e00\u628a\u68b3\u5b50\u3002\u54b1\u4eec\u518d\u627e\u4e00\u95f4\u5ba2\u5e97\u4f4f\u4e0b\u3002\u665a\u4e0a\u4f60\u53ef \u4ee5\u597d\u597d\u68b3\u6d17\u4e00\u756a\uff0c\u4e5f\u53ef\u4ee5\u7761\u4e2a\u597d\u89c9\u4e86\u3002\u201d \u3000\u3000\u6768\u5a49\u751f\u6027\u597d\u6d01\uff0c\u7ed9\u675c\u96c4\u8bf4\u4e2d\u5979\u7684\u5fc3\u4e8b\uff0c\u5bf9\u4ed6\u8fd9\u4e00\u756a\u4e0d\u592a\u5e84\u91cd\u7684\u8bf4\u8bdd\uff0c\u867d\u7136\u89c9\u5f97\u6709\u70b9\u523a \u8033\uff0c\u4e5f\u4e0d\u600e\u6837\u653e\u5728\u5fc3\u4e0a\u3002\u5979\u4e0e\u675c\u96c4\u65e9\u5df2\u4ece\u964c\u751f\u53d8\u4e3a\u719f\u7edc\uff0c\u53ea\u9053\u675c\u96c4\u662f\u56e0\u4e3a\u51fa\u4e86\u9669\u5883\uff0c\u5fc3\u60c5\u8f7b \u677e\uff0c\u6240\u4ee5\u548c\u5979\u5f00\u5f00\u73a9\u7b11\u7684\u3002 \u3000\u3000\u6768\u5a49\u7b11\u9053\uff1a\u201c\u68b3\u5b50\u955c\u5b50\u5e76\u4e0d\u6253\u7d27\uff0c\u6211\u5012\u662f\u8fd8\u60f3\u4e70\u4e00\u628a\u5251\u6216\u957f\u5200\u3002\u201d\u5979\u7684\u4f69\u5251\u65e9\u5df2\u5728\u88ab\u4fd8 \u4e4b\u65f6\u7f34\u53bb\uff0c\u5982\u4eca\u53ea\u5269\u4e0b\u4e00\u67c4\u5315\u9996\uff0c\u82e5\u662f\u5f53\u771f\u4e0e\u5f3a\u654c\u4ea4\u624b\uff0c\u53ef\u6d4e\u4e0d\u4e86\u4ec0\u4e48\u4e8b\u3002 \u3000\u3000\u675c\u96c4\u9053\uff1a\u201c\u6211\u4e5f\u60f3\u4e70\u4e24\u5339\u5750\u9a91\uff0c\u7ed9\u4f60\u4ee3\u6b65\u3002\u54b1\u4eec\u53bb\u627e\u627e\u770b\u3002\u201d\u8fd9\u4e2a\u5c0f\u9547\u867d\u7136\u89c4\u6a21\u4e0d\u5927\uff0c \u4f46\u56e0\u907f\u96be\u800c\u6765\u7684\u4eba\u5f88\u591a\uff0c\u672c\u5730\u4eba\u7684\u5546\u5e97\u52a0\u4e0a\u96be\u6c11\u6446\u8bbe\u7684\u644a\u5b50\u5012\u4e5f\u76f8\u5f53\u70ed\u95f9\u3002 \u3000\u3000\u6768\u5a49\u627e\u5230\u4e86\u4e00\u95f4\u4e24\u6bcd\u5973\u5f00\u8bbe\u7684\u65e7\u8863\u94fa\uff0c\u5927\u4e3a\u6b22\u559c\uff0c\u5fc3\u60f3\uff1a\u201c\u5e97\u5b50\u90fd\u662f\u5973\u4eba\uff0c\u6211\u53ef\u4ee5\u5728\u91cc \u9762\u8bd5\u7a7f\u4e5f\u662f\u65e0\u59a8\u3002\u201d\u4e8e\u662f\u8bf4\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u4e0d\u662f\u8981\u4e70\u5750\u9a91\u5417\uff1f\u4f60\u770b\u5bf9\u9762\u6709\u5341\u51e0\u4e2a\u4eba\u7275\u7740\u9aa1\u9a6c \u6392\u5728\u90a3\u513f\uff0c\u4f3c\u4e4e\u662f\u8981\u51fa\u5356\u7684\u3002\u4f60\u53bb\u770b\u770b\uff0c\u987a\u4fbf\u5728\u5e02\u573a\u6253\u4e00\u8f6c\uff0c\u770b\u770b\u6709\u6ca1\u6709\u4eba\u51fa\u5356\u5200\u5251\u3002\u201d \u3000\u3000\u675c\u96c4\u5fc3\u9053\uff1a\u201c\u8fd9\u5c0f\u599e\u513f\u5982\u4eca\u5bf9\u6211\u5df2\u662f\u670d\u670d\u5e16\u5e16\uff0c\u8c05\u5979\u4e0d\u4f1a\u80cc\u6211\u79c1\u9003\u3002\u5c31\u662f\u8981\u9003\u4e5f\u9003\u4e0d\u51fa \u6211\u7684\u638c\u5fc3\u3002\u6211\u5012\u4e50\u5f97\u8d81\u6b64\u673a\u4f1a\uff0c\u6253\u542c\u6253\u542c\u4e00\u4e9b\u4e0d\u4fbf\u8ba9\u5979\u77e5\u9053\u7684\u6d88\u606f\u3002\u201d\u8bf4\u9053\uff1a\u201c\u597d\u7684\uff0c\u6211\u7ed9 \u4f60\u627e\u4e00\u627e\u3002\u8fd9\u51e0\u4e24\u94f6\u5b50\u4f60\u62ff\u7740\u3002\u82e5\u4e0d\u591f\u7528\uff0c\u4f60\u518d\u627e\u6211\u3002\u201d\u65e7\u8863\u94fa\u7684\u5973\u8001\u677f\u7b11\u9053\uff1a\u201c\u53c8\u4e0d\u662f\u7c07 \u65b0\u7684\u7ef8\u7f0e\u8863\u88f3\uff0c\u6709\u8fd9\u8bb8\u591a\u94f6\u5b50\uff0c\u8db3\u4ee5\u591f\u4e86\u3002\u201d \u3000\u3000\u6768\u5a49\u8eab\u4e0a\u672c\u6765\u8fd8\u6709\u51e0\u9897\u5bc6\u85cf\u7684\u91d1\u8c46\uff0c\u8981\u5f85\u4e0d\u8981\u675c\u96c4\u7684\u94f6\u5b50\uff0c\u4f46\u8f6c\u5ff5\u4e00\u60f3\uff0c\u91d1\u5b50\u53ef\u662f\u4e0d\u4fbf \u9732\u773c\uff0c\u5979\u4e0e\u675c\u96c4\u53c8\u662f\u5144\u59b9\u76f8\u79f0\uff0c\u4e5f\u4e0d\u80fd\u9732\u51fa\u7834\u7efd\u3002\u4e8e\u662f\u53ea\u597d\u63a5\u4e86\u4e0b\u6765\uff0c\u8bf4\u4e86\u4e00\u58f0\uff1a\u201c\u8c22 \u8c22\u3002\u201d\u90a3\u5973\u8001\u677f\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u5144\u59b9\u53ef\u771f\u662f\u5ba2\u6c14\u5f97\u7d27\u554a\uff0c\u8fdb\u6765\u5427\u3002\u201d \u3000\u3000\u5e97\u91cc\u867d\u7136\u5356\u7684\u53ea\u662f\u65e7\u8863\uff0c\u8d27\u8272\u5012\u5f88\u9f50\u5168\uff0c\u5176\u4e2d\u4e14\u4e0d\u4e4f\u51fa\u8eab\u5bcc\u8d35\u4eba\u5bb6\u7684\u96be\u6c11\u5bc4\u552e\u7684\u4e0a\u597d\u8863 \u88f3\u3002\u6768\u5a49\u9009\u4e86\u51e0\u4ef6\u8272\u7d20\u6de1\u96c5\u8d28\u5730\u9887\u4f73\u7684\u8863\u670d\uff0c\u8bd5\u8fc7\u5408\u8eab\uff0c\u4ed8\u4e86\u94b1\u9ad8\u9ad8\u5174\u5174\u5730\u51fa\u6765\u627e\u675c\u96c4\u3002 \u3000\u3000\u65e7\u8863\u94fa\u524d\u6709\u4e2a\u6c49\u5b50\uff0c\u6234\u7740\u4e00\u9876\u6597\u7b20\uff0c\u538b\u5f97\u5f88\u4f4e\uff0c\u906e\u8fc7\u4e86\u534a\u8fb9\u8138\u5b54\u3002\u6768\u5a49\u51fa\u6765\u7684\u65f6\u5019\uff0c\u5ffd \u542c\u5f97\u4ed6\u8f7b\u8f7b\u201c\u54a6\u201d\u4e86\u4e00\u58f0\u3002 \u3000\u3000\u6768\u5a49\u672c\u6765\u4e5f\u8fd8\u672a\u66fe\u6ce8\u610f\uff0c\u8d70\u4e86\u4e00\u4f1a\uff0c\u5ffd\u5730\u53d1\u89c9\u8fd9\u4eba\u8ddf\u5728\u540e\u9762\u3002\u6768\u5a49\u8f6c\u5165\u4e00\u6761\u5c0f\u5df7\uff0c\u518d\u8d70 \u51fa\u6765\uff0c\u4ed6\u8fd8\u662f\u8ddf\u5728\u540e\u9762\u3002 \u3000\u3000\u6768\u5a49\u5fc3\u4e2d\u6709\u6c14\uff0c\u84e6\u5730\u7ad9\u4f4f\uff0c\u90a3\u4eba\u9669\u4e9b\u649e\u7740\u4e86\u5979\uff0c\u8fde\u5fd9\u6b62\u6b65\u3002\u6768\u5a49\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u5e72\u4ec0 \u4e48\u7684\uff0c\u4e3a\u4ec0\u4e48\u8001\u662f\u8ddf\u7740\u6211\uff1f\u201d \u3000\u3000\u90a3\u4eba\u4e0e\u6768\u5a49\u6b63\u9762\u76f8\u5bf9\uff0c\u628a\u6768\u5a49\u7684\u9762\u8c8c\u770b\u5f97\u66f4\u6e05\u695a\u4e86\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5929\u4e0b\u51b3\u6ca1\u6709\u8fd9\u6837\u76f8\u8c8c \u76f8\u540c\u7684\u4eba\uff0c\u8fd9\u4e00\u5b9a\u662f\u5979\uff0c\u54a6\uff0c\u90a3\u65e5\u6211\u4eb2\u773c\u89c1\u5979\u81ea\u6740\u7684\uff0c\u5979\u7adf\u7136\u8fd8\u6ca1\u6709\u6b7b\uff01\u2026\u2026\u201d \u3000\u3000\u6768\u5a49\u89c1\u4ed6\u5b9a\u4e86\u773c\u775b\u770b\u5979\uff0c\u5fc3\u91cc\u66f4\u4e0d\u8212\u670d\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u7a76\u7adf\u662f\u5e72\u4ec0\u4e48\u7684\uff1f\u4f60\u54d1\u4e86\u4e48\uff1f\u201d\u5fc3 \u60f3\uff1a\u201c\u82e5\u662f\u6b64\u4eba\u7b54\u4e0d\u4e0a\u6765\uff0c\u90a3\u5c31\u662f\u6709\u5fc3\u8c03\u620f\u6211\u7684\u4e86\u3002\u6211\u4e0d\u5e9f\u4e86\u4ed6\u7684\u62db\u5b50\uff0c\u4e5f\u5f97\u65bd\u4ee5\u8584\u60e9\u3002\u201d \u3000\u3000\u90a3\u4eba\u79fb\u5f00\u76ee\u5149\uff0c\u770b\u4e86\u770b\u5468\u56f4\u7684\u73af\u5883\uff0c\u4ed6\u4eec\u6240\u5728\u4e4b\u5904\uff0c\u5bf9\u9762\u6b63\u662f\u5e02\u573a\uff0c\u65c1\u8fb9\u4e5f\u6709\u4eba\u6765\u4eba \u5f80\u3002\u90a3\u4eba\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u987b\u5f97\u8bbe\u4e2a\u6cd5\u5b50\u5f15\u5979\u5230\u50fb\u9759\u4e4b\u5904\u8bf4\u8bdd\u624d\u597d\u3002\u201d \u3000\u3000\u6768\u5a49\u6b63\u8981\u53d1\u4f5c\uff0c\u53ea\u542c\u5f97\u90a3\u4eba\u606d\u606d\u656c\u656c\u5730\u8bf4\u9053\uff1a\u201c\u6211\u662f\u9003\u96be\u7684\uff0c\u7f3a\u4e4f\u76d8\u7f20\uff0c\u6709\u4e00\u53e3\u597d\u5200\u60f3 \u5356\u4e0e\u8bc6\u4e3b\u3002\u59d1\u5a18\uff0c\u4f60\u53ef\u60f3\u8981\u4e48\uff1f\u201d \u3000\u3000\u6768\u5a49\u6b63\u60f3\u4e70\u5200\u5251\u4f7f\u7528\uff0c\u542c\u8bf4\u4ed6\u8981\u5356\u5200\uff0c\u4fbf\u9053\uff1a\u201c\u5200\u5728\u4f55\u5904\uff0c\u62ff\u6765\u7ed9\u6211\u770b\u770b\u3002\u201d \u3000\u3000\u90a3\u4eba\u5728\u5939\u8863\u91cc\u89e3\u4e0b\u4e00\u67c4\u4f69\u5200\uff0c\u9012\u7ed9\u6768\u5a49\uff0c\u6768\u5a49\u62d4\u51fa\u9798\u6765\u4e00\u770b\uff0c\u53ea\u89c1\u5bd2\u5149\u8000\u76ee\uff0c\u604d\u4f3c\u4e00\u6cd3 \u79cb\u6c34\uff0c\u4e0d\u7531\u5f97\u8d5e\u9053\uff1a\u201c\u7aef\u7684\u662f\u4e00\u67c4\u5b9d\u5200\uff01\u201d\u90a3\u4eba\u8d54\u7b11\u9053\uff1a\u201c\u5b9d\u5200\u8bf4\u4e0d\u4e0a\uff0c\u6bd4\u666e\u901a\u7684\u5200\u5f3a\u5f97\u591a \u5374\u662f\u771f\u7684\u3002\u201d \u3000\u3000\u6768\u5a49\u5fc3\u91cc\u8d77\u7591\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\u6211\u8981\u4e70\u5200\uff1f\u201d\u8981\u77e5\u5973\u5b50\u4e70\u5200\u4e4b\u4e8b\u867d\u7136\u4e0d\u662f\u6ca1\u6709\uff0c\u6bd5 \u7adf\u4e5f\u662f\u5c11\u89c1\u7684\u3002\u6768\u5a49\u5fc3\u60f3\uff1a\u201c\u6211\u521a\u624d\u548c\u675c\u96c4\u8bf4\u8bdd\u7684\u65f6\u5019\uff0c\u597d\u50cf\u5e76\u6ca1\u6709\u8fd9\u6837\u7684\u4eba\u5728\u65c1\u8fb9\u3002\u96be\u9053 \u4ed6\u662f\u8eb2\u5728\u54ea\u4e00\u4e2a\u89d2\u843d\u91cc\u542c\u89c1\u4e86\uff1f\u4f46\u4e3a\u4ec0\u4e48\u4ed6\u5374\u53c8\u4e0d\u5f53\u573a\u51fa\u6765\uff1f\u201d \u3000\u3000\u90a3\u4eba\u8bf4\u9053\uff1a\u201c\u59d1\u5a18\u662f\u4ece\u897f\u590f\u9003\u6765\u7684\u5427\uff1f\u6709\u6ca1\u6709\u540c\u4f34\uff1f\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u8fd9\u5173\u4f60\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u90a3\u4eba\u9053\uff1a\u201c\u4e00\u4e2a\u5973\u5b50\u82e5\u80fd\u591f\u4ece\u5175\u614c\u9a6c\u4e71\u4e4b\u4e2d\u9003\u51fa\u6765\uff0c\u60f3\u5fc5\u662f\u7ec3\u8fc7\u4e00\u70b9\u6b66\u529f\u7684\u3002\u56e0\u6b64\u6211\u731c \u60f3\u59d1\u5a18\u6216\u8005\u8981\u4e70\u4e00\u53e3\u5200\u9632\u8eab\u3002\u201d \u3000\u3000\u8fd9\u4e2a\u7406\u7531\u867d\u7136\u662f\u8fd9\u4eba\u4e34\u65f6\u60f3\u51fa\u6765\u7684\uff0c\u4e5f\u8fd8\u53ef\u4ee5\u81ea\u5706\u5176\u8bf4\u3002\u6768\u5a49\u4e0d\u518d\u9a73\u4ed6\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8fd8 \u6709\u4e00\u67c4\u4f69\u5251\uff0c\u53ef\u5426\u4e5f\u7ed9\u6211\u770b\u4e00\u770b\uff1f\u201d \u3000\u3000\u90a3\u4eba\u9053\uff1a\u201c\u8fd9\u67c4\u4f69\u5251\u6211\u60f3\u7559\u4f5c\u81ea\u7528\uff0c\u53ea\u80fd\u5356\u8fd9\u53e3\u5200\u3002\u201d \u3000\u3000\u6768\u5a49\u867d\u7136\u7591\u5fc3\u672a\u91ca\uff0c\u4f46\u5979\u4e5f\u59d4\u5b9e\u662f\u60f3\u8981\u8fd9\u628a\u5b9d\u5200\uff0c\u5fc3\u60f3\uff1a\u201c\u7ba1\u4ed6\u662f\u5e72\u4ec0\u4e48\u7684\uff0c\u53ea\u8981\u4ed6\u5356 \u7ed9\u6211\u5c31\u884c\u3002\u201d\u4e8e\u662f\u95ee\u9053\uff1a\u201c\u4f60\u8981\u591a\u5c11\u94f6\u5b50\uff1f\u201d \u3000\u3000\u90a3\u4eba\u7b54\u9053\uff1a\u201c\u6211\u53ea\u6c42\u5356\u4e0e\u8bc6\u4e3b\uff0c\u82e5\u975e\u8bc6\u4e3b\uff0c\u5343\u91d1\u4e0d\u5356\uff0c\u82e5\u662f\u8bc6\u4e3b\uff0c\u968f\u4fbf\u8d4f\u51e0\u4e24\u94f6\u5b50\uff0c\u591f \u6211\u5230\u4e2d\u5dde\u7684\u8def\u8d39\u5c31\u884c\u3002\u4e0d\u8fc7\uff0c\u6211\u6709\u51e0\u53e5\u8bdd\u60f3\u95ee\u59d1\u5a18\uff0c\u4f60\u53ef\u4e0d\u53ef\u4ee5\u548c\u6211\u5230\u90a3\u8336\u5e97\u53bb\u8bf4\uff1f\u201d \u3000\u3000\u6768\u5a49\u8be7\u9053\uff1a\u201c\u4f60\u6709\u4ec0\u4e48\u8bdd\uff0c\u4e0d\u53ef\u4ee5\u5728\u8fd9\u91cc\u8bf4\u4e48\uff1f\u201d\u90a3\u4eba\u9053\uff1a\u201c\u8fd9\u91cc\u4e0d\u662f\u8bf4\u8bdd\u4e4b\u6240\u3002\u201d \u3000\u3000\u6768\u5a49\u60ca\u7591\u4e0d\u5b9a\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u4eba\u5012\u662f\u6709\u70b9\u53e4\u602a\uff0c\u6211\u4e0e\u4ed6\u7d20\u5473\u5e73\u751f\uff0c\u4ed6\u5374\u6709\u4ec0\u4e48\u8bdd\u8981\u548c \u6211\u8bf4\u5462\uff1f\u83ab\u975e\u662f\u85c9\u5356\u5200\u4e3a\u540d\uff0c\u60f3\u8981\u8bf1\u62d0\u6211\u4e48\uff1f\u201d\u60f3\u81f3\u6b64\u5904\uff0c\u4e0d\u89c9\u5fc3\u4e2d\u6697\u7b11\uff1a\u201c\u82e5\u7136\u4ed6\u662f\u4e0d\u6000 \u597d\u610f\uff0c\u6211\u5012\u8981\u60e9\u6212\u60e9\u6212\u4ed6\u3002\u54fc\uff0c\u96be\u9053\u6211\u8fd8\u6015\u4ed6\u8bf1\u62d0\u4e0d\u6210\uff01\u201d \u3000\u3000\u6768\u5a49\u597d\u5947\u5fc3\u8d77\uff0c\u6b63\u8981\u548c\u8fd9\u4eba\u540c\u53bb\uff0c\u773c\u5149\u4e00\u77a5\uff0c\u5ffd\u7136\u770b\u89c1\u675c\u96c4\u7684\u80cc\u5f71\uff0c\u6b63\u6324\u5728\u4e00\u5806\u4eba\u4e2d\u3002 \u6768\u5a49\u53eb\u9053\uff1a\u201c\u5927\u54e5\u5feb\u6765\uff0c\u5e2e\u4e00\u5e2e\u6211\uff0c\u770b\u770b\u8fd9\u628a\u5b9d\u5200\u3002\u201d \u3000\u3000\u675c\u96c4\u6324\u51fa\u4eba\u5806\uff0c\u5306\u5306\u5fd9\u5fd9\u5730\u8dd1\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u54ea\u91cc\u6765\u7684\u5b9d\u5200\uff1f\u201d\u6768\u5a49\u9053\uff1a\u201c\u662f\u8fd9\u4f4d\u5927\u54e5\u5356 \u7ed9\u6211\u7684\uff0c\u4ed6\u4e0d\u4e0e\u6211\u8bba\u4ef7\uff0c\u7ed9\u5c11\u4e86\u53ef\u4e0d\u597d\uff0c\u4f60\u770b\u4e00\u770b\u8be5\u7ed9\u591a\u5c11\u5462\uff1f\u201d \u3000\u3000\u675c\u96c4\u7741\u5927\u4e86\u773c\u775b\uff0c\u8bf4\u9053\uff1a\u201c\u4eba\u5462\uff1f\u201d\u6768\u5a49\u5403\u4e86\u4e00\u60ca\uff0c\u56de\u5934\u770b\u65f6\uff0c\u54ea\u6709\u4eba\u5728\u5979\u80cc\u540e\uff1f\u5356\u5200 \u7684\u6c49\u5b50\u7adf\u4e0d\u77e5\u662f\u5728\u4ec0\u4e48\u65f6\u5019\u8dd1\u4e86\u3002 \u3000\u3000\u6768\u5a49\u5927\u611f\u60ca\u5f02\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4eba\u771f\u6700\u5947\u602a\uff0c\u600e\u7684\u5200\u4e5f\u4e0d\u8981\uff0c\u94b1\u4e5f\u4e0d\u8981\uff0c\u5c31\u8dd1\u4e86\uff01\u201d \u3000\u3000\u675c\u96c4\u770b\u4e86\u770b\u8fd9\u67c4\u5b9d\u5200\uff0c\u9762\u4e0a\u4e5f\u9732\u51fa\u60ca\u5f02\u7684\u795e\u8272\uff0c\u201c\u54a6\u201d\u4e86\u4e00\u58f0\u3002\u6768\u5a49\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u770b \u51fa\u4e86\u4ec0\u4e48\uff1f\u201d\u675c\u96c4\u6014\u4e86\u4e00\u6014\uff0c\u534a\u54cd\u624d\u5b9a\u4e86\u5fc3\u795e\u8bf4\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\u3002\u8fd9\u5200\u662f\u6d1b\u9633\u4e00\u5bb6\u6709\u540d\u7684\u5200\u94fa \u94f8\u7684\uff0c\u7aef\u662f\u628a\u5b9d\u5200\u3002\u90a3\u4eba\u60f3\u5fc5\u662f\u4e2d\u539f\u4eba\u6c0f\u4e86\uff1f\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u770b\u4ed6\u6a21\u6837\u4f3c\u662f\u6c49\u4eba\u3002\u201d\u675c\u96c4\u9053\uff1a\u201c\u4ed6\u548c\u4f60\u8bf4\u4e86\u4e9b\u4ec0\u4e48\uff1f\u201d\u6768\u5a49\u9053\uff1a \u201c\u4ed6\u597d\u4f3c\u6709\u4ec0\u4e48\u4e8b\u60c5\u8981\u544a\u8bc9\u6211\uff0c\u9080\u6211\u5230\u90a3\u8fb9\u8336\u94fa\u8bf4\u8bdd\uff0c\u6211\u521a\u60f3\u53bb\uff0c\u4f60\u5c31\u6765\u4e86\u3002\u201d \u3000\u3000\u675c\u96c4\u9053\uff1a\u201c\u90a3\u4eba\u662f\u4ec0\u4e48\u6478\u6837\uff1f\u201d\u6768\u5a49\u8bf4\u4e86\u4e4b\u540e\uff0c\u675c\u96c4\u9053\uff1a\u201c\u6211\u5df2\u770b\u4e2d\u4e24\u5339\u5750\u9a91\uff0c\u6b63\u8981\u53eb \u4f60\u8fc7\u53bb\uff0c\u55ef\uff0c\u4f60\u770b\u89c1\u4e48\uff1f\u5c31\u5728\u9aa1\u9a6c\u5e02\u573a\u7684\u4e1c\u89d2\uff0c\u90a3\u4e2a\u82b1\u767d\u987b\u5b50\u7684\u8001\u5927\u7237\uff0c\u4ed6\u6709\u4e00\u5339\u9752\u9aa1\uff0c\u4e00 \u5339\u67a3\u7ea2\u9a6c\uff0c\u4f60\u53bb\u770b\u770b\uff0c\u5408\u4e0d\u5408\u610f\uff1f\u82e5\u662f\u5408\u610f\uff0c\u4f60\u5c31\u628a\u5b83\u4e70\u4e0b\u6765\uff0c\u4f60\u5728\u90a3\u91cc\u7b49\u6211\uff0c\u6211\u53bb\u627e\u627e\u90a3 \u4eba\u3002\u201d\u8bf4\u7f62\uff0c\u628a\u4e24\u952d\u5927\u5143\u5b9d\u4ea4\u7ed9\u6768\u5a49\uff0c\u5306\u5306\u5c31\u8d70\u3002 \u3000\u3000\u6768\u5a49\u4e0d\u89c9\u6709\u70b9\u8d77\u7591\uff1a\u201c\u4e3a\u4ec0\u4e48\u675c\u96c4\u4e0d\u8981\u6211\u4e00\u540c\u53bb\u627e\uff1f\u201d\u4f46\u56e0\u5979\u5bf9\u675c\u96c4\u5df2\u662f\u76f8\u5f53\u4fe1\u8d56\uff0c\u8f6c \u5ff5\u4e00\u60f3\uff1a\u201c\u662f\u4e86\u3002\u60f3\u5fc5\u4ed6\u662f\u987e\u8651\u6211\u662f\u4e2a\u5be1\u5987\u7684\u8eab\u4efd\uff0c\u4e0d\u4fbf\u5230\u5904\u5bfb\u627e\u4e00\u4e2a\u964c\u751f\u7684\u7537\u5b50\u3002\u90a3\u4e24\u5339 \u4ed6\u770b\u4e2d\u4e86\u7684\u5750\u9a91\uff0c\u4ed6\u4e5f\u53ef\u80fd\u6015\u7ed9\u4eba\u5bb6\u5148\u4e70\u4e86\u53bb\u3002\u201d \u3000\u3000\u6768\u5a49\u5230\u4e86\u9aa1\u9a6c\u5e02\u573a\uff0c\u770b\u770b\u90a3\u4e00\u9aa1\u4e00\u9a6c\uff0c\u679c\u7136\u5f88\u662f\u4e0d\u9519\u3002\u95ee\u4e86\u4e00\u95ee\uff0c\u624d\u77e5\u675c\u96c4\u5df2\u7ecf\u8bb2\u59a5\u4e86 \u4ef7\u94b1\u7684\uff0c\u6070\u597d\u662f\u4e24\u4e2a\u5927\u5143\u5b9d\u3002\u6768\u5a49\u4fbf\u4e70\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u7b49\u4e86\u5927\u7ea6\u534a\u4e2a\u65f6\u8fb0\uff0c\u675c\u96c4\u8fd9\u624d\u56de\u6765\u3002\u6768\u5a49\u9053\uff1a\u201c\u600e\u4e48\u6837\uff1f\u89c1\u7740\u4e86\u6ca1\u6709\uff1f\u201d\u675c\u96c4\u6447\u4e86\u6447 \u5934\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u4eba\u771f\u662f\u53e4\u602a\uff0c\u4e0d\u77e5\u8dd1\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u6768\u5a49\u60ca\u7591\u4e0d\u5b9a\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4eba\u4e0d\u77e5\u662f\u4ec0\u4e48\u95e8\u9053\uff1f\u6211\u62ff\u4e86\u4ed6\u7684\u5b9d\u5200\u8fd8\u672a\u4ed8\u94b1\u5462\u3002\u201d \u3000\u3000\u675c\u96c4\u7b11\u9053\uff1a\u201c\u4e0d\u7ba1\u4ed6\u4e86\uff0c\u4ed6\u4e0d\u6765\u8ba8\uff0c\u4f60\u5c31\u4e50\u5f97\u5360\u4ed6\u8fd9\u4e2a\u4fbf\u5b9c\u3002\u54b1\u4eec\u53bb\u627e\u5ba2\u5e97\u5427\u3002\u201d \u3000\u3000\u8fd9\u4e2a\u8fb9\u7586\u7684\u5c0f\u9547\uff0c\u5e73\u65e5\u672c\u662f\u751a\u4e3a\u8352\u51c9\u7684\u3002\u73b0\u5728\u6b63\u503c\u6218\u65f6\uff0c\u5e73\u6dfb\u4e86\u8bb8\u591a\u96be\u6c11\uff0c\u5374\u662f\u7a81\u7136\u7e41 \u8363\u8d77\u6765\u3002\u65b0\u5f00\u7684\u9152\u697c\u8336\u8086\u548c\u5ba2\u5e97\u4e3a\u6570\u4e0d\u5c11\uff0c\u4f46\u4ecd\u7136\u4f9b\u4e0d\u5e94\u6c42\uff0c\u4ed6\u4eec\u627e\u4e86\u51e0\u95f4\u5ba2\u5e97\uff0c\u95f4\u95f4\u90fd\u662f \u5ba2\u6ee1\u3002 \u3000\u3000\u6700\u540e\u627e\u5230\u4e00\u5bb6\u6700\u5927\u7684\u5ba2\u5e97\uff0c\u675c\u96c4\u58f0\u660e\u613f\u610f\u4ed8\u53cc\u500d\u7684\u623f\u94b1\uff0c\u5e97\u4e3b\u4eba\u772f\u4e86\u773c\u775b\uff0c\u6253\u91cf\u4e86\u4ed6\u4eec \u4e00\u4e0b\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u662f\u592b\u59bb\u5417\uff1f\u201d\u6768\u5a49\u7ea2\u4e86\u7c89\u9762\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u662f\uff0c\u6211\u4eec\u662f\u5144\u59b9\u3002\u201d \u3000\u3000\u675c\u96c4\u7b11\u9053\uff1a\u201c\u662f\u4e0d\u662f\u592b\u59bb\uff0c\u53c8\u6709\u4ec0\u4e48\u5173\u7cfb\uff1f\u201d \u3000\u3000\u5e97\u4e3b\u4eba\u70b9\u4e86\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u662f\u592b\u59bb\u5c31\u6ca1\u95ee\u9898\u3002\u4e0d\u8fc7\uff0c\u73b0\u5728\u4f60\u4eec\u662f\u5144\u59b9\uff0c\u4e5f\u8fd8\u53ef\u4ee5\u5546\u91cf\u3002 \u6211\u4eec\u53ea\u6709\u4e00\u95f4\u623f\u95f4\uff0c\u4f60\u4eec\u5144\u59b9\u540c\u4f4f\u4e5f\u4e0d\u6253\u7d27\u5427\uff1f\u201d \u3000\u3000\u6768\u5a49\u76b1\u4e86\u7709\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u600e\u4e48\u53ea\u6709\u4e00\u95f4\u623f\u95f4\uff1f\u201d \u3000\u3000\u675c\u96c4\u614c\u5fd9\u626f\u4e86\u626f\u5979\u7684\u8863\u8896\uff0c\u8bf4\u9053\uff1a\u201c\u65e2\u7136\u6ca1\u6709\u591a\u4f59\u7684\u623f\u95f4\uff0c\u90a3\u5c31\u53ea\u597d\u5c06\u5c31\u4f4f\u4e0b\u4e86\u3002\u597d \u5427\uff0c\u6211\u4eec\u8981\u4e86\u3002\u8be5\u591a\u5c11\u623f\u94b1\uff1f\u201d \u3000\u3000\u5e97\u4e3b\u4eba\u9053\uff1a\u201c\u8fd9\u662f\u672c\u5e97\u6700\u597d\u7684\u4e00\u95f4\u5957\u623f\uff0c\u623f\u94b1\u96be\u514d\u8981\u591a\u4e00\u70b9\uff0c\u4e09\u4e24\u94f6\u5b50\u4e00\u5929\u3002\u4e0d\u8fc7\u4e5f\u5e78 \u4e8f\u662f\u9ad8\u4ef7\u7684\u623f\u5b50\uff0c\u6240\u4ee5\u624d\u7a7a\u4e0b\u6765\u3002\u201d \u3000\u3000\u675c\u96c4\u4e8c\u8bdd\u4e0d\u8bf4\uff0c\u4ed8\u4e86\u516d\u4e24\u94f6\u5b50\uff0c\u5c31\u53eb\u5e97\u4e3b\u4eba\u9886\u4ed6\u4eec\u8fdb\u623f\u3002 \u3000\u3000\u6768\u5a49\u4e00\u770b\uff0c\u8fd9\u5957\u623f\u539f\u6765\u662f\u9644\u6709\u4e00\u95f4\u5c0f\u5c0f\u7684\u5ba2\u5385\uff0c\u8fd9\u624d\u7a0d\u7a0d\u5b89\u5fc3\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u675c\u96c4\u662f\u6b63 \u4eba\u541b\u5b50\uff0c\u5c31\u5f53\u662f\u8352\u6797\u9732\u5bbf\uff0c\u4ed6\u5728\u65c1\u8fb9\u7ed9\u6211\u5b88\u591c\uff0c\u53c8\u6709\u4f55\u59a8\uff1f\u53ea\u4e0d\u8fc7\u6211\u60f3\u68b3\u6d17\u6362\u8863\uff0c\u5374\u662f\u6709\u70b9 \u4e0d\u4fbf\u3002\u201d\u675c\u96c4\u4f3c\u662f\u77e5\u9053\u5979\u7684\u5fc3\u610f\uff0c\u5e97\u4e3b\u4eba\u51fa\u53bb\u4e4b\u540e\uff0c\u675c\u96c4\u8bf4\u9053\uff1a\u201c\u623f\u95f4\u96be\u627e\uff0c\u4f60\u4e0d\u602a\u6211\u5192\u6627 \u5427\u3002\u73b0\u5728\u4f60\u5148\u7528\u8fd9\u4e2a\u623f\u95f4\uff0c\u6211\u51fa\u53bb\u8d70\u4e00\u4f1a\uff0c\u770b\u770b\u6709\u4ec0\u4e48\u4e1c\u897f\u8fd8\u8981\u4e70\uff0c\u987a\u4fbf\u518d\u627e\u4e00\u627e\u90a3\u4e2a \u4eba\u3002\u201d \u3000\u3000\u6768\u5a49\u6697\u6697\u611f\u6fc0\u675c\u96c4\u7684\u4f53\u8d34\uff0c\u675c\u96c4\u8d70\u540e\uff0c\u6768\u5a49\u53eb\u5e97\u5c0f\u4e8c\u6253\u6c34\u8fdb\u6765\uff0c\u5173\u95ed\u7a97\u6237\uff0c\u75db\u75db\u5feb\u5feb\u5730 \u6d17\u4e86\u4e2a\u6fa1\u3002 \u3000\u3000\u623f\u95f4\u7684\u5e03\u7f6e\u5f0f\u5f0f\u9f50\u5168\uff0c\u6709\u4e00\u9762\u5927\u94dc\u955c\u78e8\u5f97\u5149\u53ef\u9274\u4eba\uff0c\u6768\u5a49\u81ea\u53f9\u4e86\u53e3\u6c14\uff0c\u201c\u53ef\u60dc\u5357\u54e5\u4e0d\u80fd \u4f34\u5728\u6211\u8eab\u8fb9\uff0c\u770b\u6211\u68b3\u5986\u3002\u201d \u3000\u3000\u955c\u4e2d\u4e00\u70b9\u6bb7\u7ea2\uff0c\u6768\u5a49\u6014\u4e86\u4e00\u6014\uff0c\u4e0d\u89c9\u53c8\u662f\u957f\u957f\u5730\u53f9\u4e86\u53e3\u6c14\u3002\u539f\u6765\u90a3\u662f\u5979\u7389\u81c2\u7684\u4e00\u70b9\u201c\u5b88 \u5bab\u7802\u201d\uff0c\u662f\u5979\u5f53\u5e74\u79bb\u5bb6\u4e4b\u65f6\uff0c\u5979\u7684\u6bcd\u4eb2\u7ed9\u5979\u70b9\u4e0a\u7684\u3002\u8fd9\u5b88\u5bab\u7802\u662f\u6d17\u4e0d\u53bb\u62b9\u4e0d\u6389\u7684\uff0c\u53ea\u6709\u5728\u7ed3 \u5a5a\u4e4b\u540e\uff0c\u624d\u81ea\u884c\u6d88\u5931\u3002\u5979\u672c\u6765\u5df2\u7ecf\u5fd8\u4e86\uff0c\u5982\u4eca\u5728\u955c\u4e2d\u53cd\u6620\u51fa\u6765\uff0c\u5fc3\u5934\u81ea\u662f\u6709\u8bb8\u591a\u611f\u89e6\uff0c\u5979\u60f3 \u8d77\u4e86\u6bcd\u4eb2\uff0c\u60f3\u8d77\u4e86\u54e5\u54e5\uff0c\u5f53\u7136\u66f4\u60f3\u8d77\u4e86\u674e\u601d\u5357\u3002\u201c\u8981\u662f\u5357\u54e5\u8fd8\u6d3b\u7740\u7684\u8bdd\uff0c\u4ed6\u770b\u89c1\u8fd9\u9897\u5b88\u5b98 \u7802\uff0c\u603b\u8be5\u76f8\u4fe1\u5f97\u8fc7\u6211\u662f\u6e05\u767d\u7684\u4e86\u3002\u201d \u3000\u3000\u6768\u5a49\u6b63\u81ea\u611f\u6000\u5174\u53f9\uff0c\u81ea\u60dc\u81ea\u601c\uff0c\u5ffd\u542c\u5f97\u6709\u4eba\u5728\u95e8\u5916\u8f7b\u8f7b\u54b3\u55fd\u3002\u6768\u5a49\u9762\u4e0a\u4e00\u9635\u53d1\u70e7\uff0c\u8fde\u5fd9 \u9547\u6151\u5fc3\u795e\uff0c\u8bf4\u9053\uff1a\u201c\u662f\u5927\u54e5\u56de\u6765\u4e86\u4e48\uff1f\u201d \u3000\u3000\u675c\u96c4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u662f\u6211\u3002\u6211\u53ef\u4ee5\u8fdb\u6765\u4e48\uff1f\u201d \u3000\u3000\u6768\u5a49\u5f00\u4e86\u623f\u95e8\uff0c\u7531\u4e8e\u5fc3\u60c5\u7d0a\u4e71\u4e4b\u6545\uff0c\u8863\u8896\u8fd8\u672a\u653e\u4e0b\uff0c\u81c2\u4e0a\u7684\u4e00\u9897\u201c\u5b88\u5bab\u7802\u201d\u6620\u5165\u675c\u96c4\u7684 \u773c\u5e18\u3002\u675c\u96c4\u4e0d\u89c9\u53d1\u51fa\u4f1a\u5fc3\u7684\u5fae\u7b11\uff0c\u539f\u6765\u4ed6\u65e9\u5df2\u63a2\u542c\u5f97\u6768\u5a49\u548c\u674e\u601d\u5357\u7684\u5173\u7cfb\uff0c\u5fc3\u91cc\u4e00\u76f4\u5728\u6000\u7591 \u4ed6\u4eec\u4e8c\u4eba\u672a\u66fe\u201c\u5706\u623f\u201d\u7684\uff0c\u4eca\u89c1\u4e86\u5979\u8fd9\u9897\u201c\u5b88\u5bab\u7802\u201d\uff0c\u8fd9\u5224\u65ad\u5df2\u5f97\u5230\u4e86\u8bc1\u5b9e\uff0c\u4ed6\u4e3a\u4e86\u63a9\u9970\u81ea \u5df1\u90aa\u6076\u7684\u5fc3\u601d\uff0c\u4fbf\u88c5\u4f5c\u6f2b\u4e0d\u7ecf\u610f\u5730\u8bf4\u9053\uff1a\u201c\u5a49\u59b9\u4f60\u68b3\u5986\u597d\u4e86\uff1f\u54e5\u54e5\u6599\u5f97\u4e0d\u9519\u5427\uff0c\u627e\u65e9\u5c31\u77e5\u9053 \u4f60\u4f4f\u4e86\u5ba2\u5e97\uff0c\u7b2c\u4e00\u4ef6\u4e8b\u5c31\u662f\u8981\u597d\u597d\u68b3\u6d17\u4e00\u756a\uff0c\u73b0\u5728\u679c\u7136\u662f\u8d8a\u53d1\u51fa\u843d\u5f97\u6807\u81f4\u4e86\u3002\u201d \u3000\u3000\u6768\u5a49\u653e\u4e0b\u8896\u5b50\uff0c\u6b63\u5bb9\u8bf4\u9053\uff1a\u201c\u6211\u662f\u672a\u4ea1\u4eba\u7684\u8eab\u4efd\uff0c\u5927\u54e5\uff0c\u4f60\u53ef\u522b\u5f00\u73a9\u7b11\u3002\u600e\u4e48\u6837\uff0c\u4f60\u627e \u7740\u4e86\u90a3\u4eba\u6ca1\u6709\uff1f\u201d \u3000\u3000\u675c\u96c4\u6062\u590d\u4e86\u5e73\u65f6\u6b63\u895f\u5371\u5750\u7684\u6001\u5ea6\uff0c\u8bf4\u9053\uff1a\u201c\u8fd8\u662f\u6ca1\u6709\u627e\u7740\u3002\u4e0d\u8fc7\u6211\u5982\u6253\u542c\u5230\u4e86\u4e24\u4e2a\u91cd\u8981 \u7684\u6d88\u606f\u3002\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u54e6\uff0c\u662f\u4ec0\u4e48\u6d88\u606f\uff1f\u201d \u3000\u3000\u675c\u96c4\u9053\uff1a\u201c\u7b2c\u4e00\u4ef6\u662f\u897f\u590f\u7684\u4eac\u57ce\u5df2\u7ed9\u8499\u53e4\u5927\u519b\u653b\u7834\uff0c\u897f\u590f\u56fd\u4e3b\u674e\u5b89\u5168\u732e\u5973\u6295\u964d\u3002\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u897f\u590f\u541b\u5eb8\u5175\u5f31\uff0c\u58eb\u65e0\u6597\u5fd7\uff0c\u7ed9\u8499\u53e4\u6240\u706d\uff0c\u8fd9\u4e5f\u662f\u610f\u6599\u4e2d\u4e8b\u3002\u201d \u3000\u3000\u675c\u96c4\u9053\uff1a\u201c\u7b2c\u4e8c\u4ef6\u4e8b\u53ef\u80fd\u4f1a\u51fa\u4e4e\u4f60\u7684\u610f\u6599\u4e86\u3002\u8499\u53e4\u706d\u4e86\u897f\u590f\uff0c\u8fc5\u5373\u53c8\u79fb\u5e05\u5357\u5411\uff0c\u518d\u5ea6\u4fb5 \u5165\u91d1\u56fd\u7684\u7586\u57df\u3002\u4e0d\u8fc7\u5374\u4e0d\u662f\u4ece\u8fd9\u6761\u8def\u6765\uff0c\u4f46\u4e5f\u8bf4\u4e0d\u5b9a\u4f1a\u5206\u5175\u5230\u6b64\u7684\u3002\u201d \u3000\u3000\u6768\u5a49\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u603b\u4e4b\uff0c\u662f\u8981\u9003\u96be\u7f62\u4e86\u3002\u201d\u5fc3\u60f3\uff1a\u201c\u4f46\u8fd9\u4e5f\u4e0d\u662f\u4ec0\u4e48\u610f\u5916\u4e4b \u4e8b\uff1f\u201d \u3000\u3000\u675c\u96c4\u9053\uff1a\u201c\u4f60\u731c\u8499\u53e4\u7684\u519b\u7684\u4e3b\u5e05\u662f\u8c01\uff1f\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u6211\u600e\u4e48\u77e5\u9053\u3002\u201d \u3000\u3000\u675c\u96c4\u9053\uff1a\u201c\u4e00\u6b63\u4e00\u526f\uff0c\u6b63\u5143\u5e05\u662f\u6210\u5409\u601d\u6c57\u7684\u4e09\u9a78\u9a6c\u9547\u56fd\u738b\u5b50\u3002\u526f\u5143\u5e05\u5c31\u662f\u5192\u540d\u674e\u5e0c\u6d69\uff0c \u771f\u540d\u4f59\u4e00\u4e2d\u7684\u90a3\u4e2a\u5bb6\u4f19\uff0c\u8fd9\u4f60\u53ef\u610f\u60f3\u4e0d\u5230\u5427\uff1f\u201d\u674e\u601d\u5357\u4e4b\u7236\u7ed9\u4f59\u4e00\u4e2d\u5192\u540d\u6240\u5bb3\u4e4b\u4e8b\uff0c\u6768\u5a49\u662f \u66fe\u7ecf\u544a\u8bc9\u675c\u96c4\u7684\u3002 \u3000\u3000\u6768\u5a49\u54ac\u4e86\u54ac\u7259\uff0c\u8bf4\u9053\uff1a\u201c\u4f59\u4e00\u4e2d\u5356\u53cb\u6c42\u8363\uff0c\u5c0f\u4eba\u5f97\u5fd7\uff0c\u5b9e\u662f\u53ef\u6068\u3002\u6211\u4e0d\u662f\u6ca1\u6709\u6599\u5230\uff0c\u53ea \u662f\u60f3\u4e0d\u5230\u4ed6\u6765\u5f97\u8fd9\u4e48\u5feb\uff01\u201d \u3000\u3000\u675c\u96c4\u8bf4\u9053\uff1a\u201c\u4f59\u4e00\u4e2d\u6765\u5230\u4e2d\u539f\uff0c\u54b1\u4eec\u82e5\u662f\u8981\u523a\u4ed6\u7684\u8bdd\uff0c\u53ef\u8981\u6bd4\u5728\u8499\u53e4\u4fbf\u5229\u5f97\u591a\u3002\u867d\u7136\u4ed6 \u8eab\u5c45\u9ad8\u4f4d\uff0c\u4f46\u6bd5\u7adf\u662f\u5728\u6c49\u4eba\u7684\u5730\u65b9\uff0c\u6211\u53ef\u4ee5\u9080\u4e00\u73ed\u6297\u8499\u7684\u5fd7\u571f\uff0c\u627e\u5bfb\u673a\u4f1a\uff0c\u6251\u6740\u6b64\u7360\uff01\u201d \u3000\u3000\u6768\u5a49\u60f3\u8d77\u674e\u601d\u5357\u751f\u524d\u7684\u8ba1\u5212\uff0c\u6b63\u662f\u548c\u675c\u96c4\u6240\u8bf4\u7684\u4e00\u6837\uff0c\u4e0d\u89c9\u53c8\u60ca\u53c8\u559c\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u53ef\u662f \u5371\u9669\u975e\u5e38\u7684\u4e8b\u54ea\uff0c\u4f60\u5f53\u771f\u613f\u610f\u62fc\u4e86\u6027\u547d\u53bb\u5e72\u8fd9\u6869\u4e8b\u60c5\u4e48\uff1f\u201d \u3000\u3000\u675c\u96c4\u4f5c\u51fa\u4e00\u526f\u5927\u4e49\u51db\u7136\u7684\u795e\u6c14\u8bf4\u9053\uff1a\u201c\u4f59\u4e00\u4e2d\u8fd9\u53ae\u4e3a\u864e\u4f5c\u6005\uff0c\u5356\u53cb\u6c42\u8363\uff0c\u5b9e\u662f\u4eba\u60c5\u96be \u6055\uff0c\u5929\u7406\u96be\u5bb9\u3002\u4e8e\u516c\u4e8e\u79c1\uff0c\u6211\u90fd\u5e94\u8be5\u628a\u4ed6\u9664\u6389\u3002\u5a49\u59b9\uff0c\u6211\u77e5\u9053\u4ed6\u662f\u4f60\u7684\u4e0d\u5171\u6234\u5929\u7684\u6740\u592b\u4ec7 \u4eba\uff0c\u5c31\u53ea\u662f\u4e3a\u4e86\u4f60\u7684\u7f18\u6545\uff0c\u6211\u4e5f\u7518\u613f\u820d\u4e86\u8fd9\u6761\u6027\u547d\u3002\u4f55\u51b5\u4ed6\u8fd8\u662f\u54b1\u4eec\u6c49\u4eba\u7684\u516c\u654c\u5462\uff01\u201d \u3000\u3000\u6768\u5a49\u542c\u4e86\u4ed6\u7684\u8bdd\uff0c\u5fc3\u4e2d\u611f\u52a8\u4e4b\u6781\uff0c\u4e0d\u89c9\u73e0\u6cea\u76c8\u7736\uff0c\u5c31\u62dc\u4e86\u4e0b\u53bb\uff0c\u8bf4\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u5bf9\u6211 \u8fd9\u6837\u597d\uff0c\u6211\u771f\u4e0d\u77e5\u5e94\u8be5\u5982\u4f55\u611f\u6fc0\u4f60\u3002\u201d \u3000\u3000\u675c\u96c4\u5fbd\u5fae\u53e3\u7b11\uff0c\u628a\u5979\u6276\u4e86\u8d77\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u5a49\u59b9\u4f60\u8fd9\u6837\u8bf4\uff0c\u5c31\u662f\u628a\u54e5\u54e5\u5f53\u505a\u4e86\u5916\u4eba\u4e86\u3002\u4e3a \u4e86\u4f60\uff0c\u6211\u662f\u8d74\u6c64\u8e48\u706b\uff0c\u5728\u6240\u4e0d\u8f9e\u3002\u8fd9\u5fc3\u4e8b\u6211\u5e0c\u671b\u4f60\u80fd\u660e\u767d\u3002\u201d\u5fc3\u91cc\u6697\u81ea\u601d\u91cf\uff0c\u8fd9\u96cc\u513f\u7ed9\u6211\u54c4 \u5f97\u94ed\u611f\u4e8e\u5fc3\uff0c\u4e00\u5fc3\u4e00\u610f\u4f9d\u9760\u4e8e\u6211\uff0c\u770b\u6765\u662f\u65f6\u673a\u5df2\u7ecf\u6210\u70ed\u4e86\u3002 \u3000\u3000\u6768\u5a49\u6014\u4e86\u4e00\u6014\uff0c\u60f3\u9053\uff1a\u201c\u5927\u54e5\u4eca\u665a\u7684\u8a00\u8bed\u4f3c\u4e4e\u4e0e\u5f80\u65e5\u5927\u4e0d\u76f8\u540c\uff0c\u4ed6\u5411\u6211\u5410\u9732\u5fc3\u4e8b\uff0c\u8fd9\u662f \u4ec0\u4e48\u610f\u601d\uff1f\u662f\u4ec5\u4ec5\u4e3a\u4e86\u5144\u59b9\u4e4b\u60c5\u5462\uff0c\u8fd8\u662f\u522b\u6709\u7528\u5fc3\uff1f\u201d\u53ef\u601c\u6768\u5a49\u9605\u4e16\u672a\u6df1\uff0c\u76f4\u5230\u6b64\u65f6\uff0c\u8fd8\u662f \u628a\u675c\u96c4\u5f53\u4f5c\u597d\u4eba\uff0c\u4e0d\u6562\u628a\u4ed6\u60f3\u5f97\u592a\u574f\u3002 \u3000\u3000\u5fc3\u5ff5\u672a\u5df2\uff0c\u53ea\u542c\u5f97\u675c\u96c4\u53c8\u5fae\u7b11\u9053\uff1a\u201c\u5a49\u59b9\uff0c\u4f60\u8fd8\u662f\u8fd9\u6837\u5ff5\u5ff5\u4e0d\u5fd8\u6b7b\u53bb\u7684\u4e08\u592b\u5417\uff1f\u201d \u3000\u3000\u6768\u5a49\u5fc3\u5934\u4e00\u51db\uff0c\u6b63\u5bb9\u8bf4\u9053\uff1a\u201c\u6211\u4e0e\u601d\u5357\u77e2\u8a93\u540c\u751f\u5171\u6b7b\uff0c\u53ea\u56e0\u4ed6\u7684\u5927\u4ec7\u672a\u62a5\uff0c\u6211\u624d\u82df\u6d3b\u81f3 \u4eca\u3002\u201d \u3000\u3000\u675c\u96c4\u6447\u4e86\u6447\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u5a49\u59b9\uff0c\u8bf7\u4f60\u542c\u6211\u4e00\u8a00\u3002\u6211\u4ee5\u4e3a\u4f60\u5df2\u7ecf\u662f\u975e\u5e38\u5bf9\u5f97\u4f4f\u674e\u601d\u5357\u4e86\u3002 \u6b7b\u8005\u5df2\u77e3\uff0c\u751f\u8005\u5c82\u80fd\u4e3a\u6b7b\u8005\u8bef\u4e86\u4e00\u751f\uff0c\u4f60\u6b63\u9752\u6625\u5e74\u5c11\uff0c\u2018\u5b88\u8282\u2019\u4e8c\u5b57\u53ea\u662f\u8150\u5112\u6240\u8bb2\u7684\u793c\u6cd5\u3002 \u4f60\u662f\u5973\u4e2d\u82f1\u6770\uff0c\u53c8\u5c82\u5b9c\u4e3a\u8fd9\u8150\u5112\u7684\u793c\u6cd5\u6240\u62d8\uff1f\u201d \u3000\u3000\u6768\u5a49\u53d8\u4e86\u9762\u8272\uff0c\u8bf4\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u662f\u529d\u6211\u6539\u5ac1\uff1f\u201d \u3000\u3000\u675c\u96c4\u8bf4\u9053\uff1a\u201c\u674e\u601d\u5357\u5728\u4e5d\u6cc9\u4e4b\u4e0b\uff0c\u60f3\u5fc5\u4e5f\u5e0c\u671b\u4f60\u80fd\u591f\u53e6\u6709\u6240\u6258\uff0c\u514d\u5f97\u4ed6\u6cc9\u4e0b\u4e0d\u5b89\u3002\u201d \u3000\u3000\u6768\u5a49\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u53eb\u6211\u5ac1\u8c01\uff1f\u201d \u3000\u3000\u675c\u96c4\u542c\u5979\u8a00\u8bed\uff0c\u77a7\u5979\u795e\u8272\uff0c\u5fc3\u4e2d\u5df2\u77e5\u4e0d\u5999\u3002\u4f46\u8fd8\u662f\u60f3\u8bd5\u4e00\u8bd5\uff0c\u671f\u671f\u827e\u827e\u5730\u8bf4\u9053\uff1a\u201c\u5a49 \u59b9\uff0c\u90a3\u65e5\u4f60\u4ece\u9791\u5b50\u519b\u4e2d\u6740\u4e86\u51fa\u6765\uff0c\u6211\u5bf9\u4f60\u7684\u521a\u70c8\u5c31\u5df2\u7ecf\u662f\u5341\u5206\u4f69\u670d\u4e86\u3002\u4e00\u8def\u540c\u884c\uff0c\u4f60\u7684\u4eba\u54c1 \u80f8\u895f\uff0c\u6587\u624d\u6b66\u827a\uff0c\u66f4\u662f\u4ee4\u6211\u822c\u822c\u503e\u5012\u3002\u96be\u5f97\u4f60\u8bb8\u6211\u7ed3\u4e3a\u5144\u59b9\uff0c\u54b1\u4eec\u4fe9\u4e5f\u8fd8\u7b97\u60c5\u6027\u76f8\u6295\uff0c\u5982\u4eca \u54b1\u4eec\u53c8\u662f\u547d\u8fd0\u76f8\u540c\u751f\u6b7b\u4e0e\u5171\uff0c\u56e0\u6b64\u6211\u662f\u5728\u60f3\u3001\u5728\u60f3\uff0c\u54b1\u4eec\u662f\u4e0d\u662f\u53ef\u4ee5\u6bd4\u5144\u59b9\u66f4\u8fdb\u4e00\u6b65\u2014\u2014\u82e5 \u662f\u6211\u80fd\u591f\u66ff\u4f60\u62a5\u4e86\u4ec7\uff0c\u800c\u53c8\u4fa5\u5e78\u672a\u6b7b\u7684\u8bdd\uff0c\u4f60\u80fd\u4e0d\u80fd\u591f\u7b54\u5e94\u6211\uff0c\u6211\u2026\u2026\u201d \u3000\u3000\u6768\u5a49\u602b\u7136\u53d8\u8272\uff0c\u8bf4\u9053\uff1a\u201c\u539f\u6765\u4f60\u679c\u7136\u662f\u6000\u6709\u5f02\u5fc3\u3002\u6211\u544a\u8bc9\u4f60\uff0c\u6211\u751f\u662f\u674e\u5bb6\u7684\u4eba\uff0c\u6b7b\u662f\u674e \u5bb6\u7684\u9b3c\uff0c\u6b64\u5fd7\u51b3\u4e0d\u8f6c\u79fb\uff01\u4f60\u65e2\u7136\u522b\u6709\u6240\u56fe\uff0c\u6211\u4e5f\u4e0d\u6562\u8981\u4f60\u62a5\u4ec7\u4e86\u3002\u5c31\u6b64\u544a\u522b\uff01\u201d \u3000\u3000\u675c\u96c4\u53eb\u9053\uff1a\u201c\u5a49\u59b9\u6162\u8d70\uff01\u201d\u5ffd\u5730\u5de6\u53f3\u5f00\u5f13\uff0c\u567c\u567c\u556a\u556a\u5730\u81ea\u5df1\u6253\u4e86\u81ea\u5df1\u4e24\u4e2a\u5634\u5df4\u3002 \u3000\u3000\u675c\u96c4\u81ea\u6253\u5634\u5df4\uff0c\u6b64\u4e8b\u5927\u5927\u51fa\u4e4e\u6768\u5a49\u610f\u6599\u4e4b\u5916\uff0c\u6768\u5a49\u4e0d\u89c9\u6115\u7136\u4f4f\u6b65\u3002\u53ea\u542c\u5f97\u675c\u96c4\u8bf4\u9053\uff1a \u201c\u5a49\u59b9\uff0c\u6211\u662f\u7ed9\u9b3c\u8ff7\u4e86\u5fc3\u7a8d\uff0c\u4e00\u65f6\u7cca\u6d82\uff0c\u8bf4\u51fa\u4e86\u4f60\u4e0d\u4e2d\u542c\u7684\u8bdd\u6765\u3002\u4e0d\u8fc7\uff0c\u6211\u59d4\u5b9e\u662f\u5bf9\u4f60\u5341\u5206 \u503e\u6155\uff0c\u4f46\u6c42\u4f60\u80fd\u539f\u8c05\u6211\u7684\u4e00\u65f6\u7cca\u6d82\u3002\u4ece\u4eca\u4e4b\u540e\uff0c\u6211\u77e2\u8a93\u4ee5\u793c\u76f8\u5b88\uff0c\u51b3\u4e0d\u6562\u518d\u8bf4\u534a\u53e5\u4eb5\u8fb1\u7684\u8bf4 \u8bdd\u3002\u5a49\u59b9\uff0c\u4f60\u80fd\u539f\u8c05\u6211\u5417\uff1f\u5426\u5219\u6211\u53ef\u771f\u662f\u65e0\u5730\u81ea\u5bb9\u4e86\uff01\u201d \u3000\u3000\u4e00\u4e2a\u7f8e\u4e3d\u7684\u5c11\u5973\uff0c\u6709\u4eba\u5bf9\u5979\u8868\u793a\u503e\u6155\uff0c\u5373\u4f7f\u5979\u8ba8\u538c\u8fd9\u4e2a\u4eba\uff0c\u5fc3\u91cc\u4e5f\u8fd8\u662f\u6709\u51e0\u5206\u6b22\u559c\u7684\u3002 \u4f55\u51b5\u6768\u5a49\u5bf9\u675c\u96c4\u4e00\u5411\u5c31\u6709\u597d\u611f\uff0c\u800c\u4e14\u66fe\u7ecf\u53d7\u8fc7\u4ed6\u7684\u201c\u5927\u6069\u201d\u5462\uff1f \u3000\u3000\u6768\u5a49\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5982\u4eca\u4ed6\u5df2\u77e5\u9053\u6211\u8981\u4e3a\u601d\u5357\u5b88\u8282\u7684\u51b3\u5fc3\uff0c\u4ed6\u81ea\u5df1\u4e5f\u77e5\u9053\u81ea\u5df1\u7684\u7cca\u6d82\u4e86\uff0c \u6211\u4f3c\u4e4e\u4e5f\u4e0d\u5e94\u8be5\u4ee4\u4ed6\u592a\u8fc7\u96be\u582a\u3002\u201d \u3000\u3000\u60f3\u81f3\u6b64\u5904\uff0c\u6768\u5a49\u53c8\u5750\u4e86\u4e0b\u6765\uff0c\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u8fc7\u53bb\u7684\u8fc7\u53bb\u4e86\uff0c\u6211\u53ea\u5f53\u6ca1\u6709\u542c\u5230\u4f60\u521a\u624d\u7684\u8bf4 \u8bdd\u3002\u4f60\u4e5f\u4e0d\u5fc5\u518d\u63d0\u3002\u4eca\u540e\u54b1\u4eec\u8fd8\u662f\u5144\u59b9\u3002\u201d \u3000\u3000\u675c\u96c4\u6697\u6697\u6b22\u559c\uff0c\u5374\u88c5\u4f5c\u4e0d\u80dc\u7f9e\u69d0\u7684\u6837\u5b50\uff0c\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u6837\u6211\u624d\u5b89\u5fc3\u3002\u59b9\u592b\u7684 \u4ec7\uff0c\u6211\u8fd8\u662f\u8981\u66ff\u4ed6\u62a5\u7684\u3002\u55ef\uff0c\u73b0\u5728\u592d\u8272\u5df2\u665a\uff0c\u5a49\u59b9\uff0c\u4f60\u997f\u4e86\u5427\uff1f\u201d \u3000\u3000\u6768\u5a49\u4e3a\u4e86\u8f6c\u79fb\u8bdd\u9898\uff0c\u5927\u5927\u65b9\u65b9\u5730\u8bf4\u9053\uff1a\u201c\u662f\u6709\u70b9\u89c9\u5f97\u997f\u4e86\u3002\u628a\u5e97\u5c0f\u4e8c\u53eb\u6765\uff0c\u80e1\u4e71\u5f04\u70b9\u4e1c \u897f\u5403\u5427\uff01\u201d \u3000\u3000\u675c\u96c4\u7b11\u9053\uff1a\u201c\u6211\u65e9\u5df2\u5429\u5490\u4ed6\u4eec\u51c6\u5907\u4e86\u3002\u201d\u5f53\u4e0b\uff0c\u51fa\u5916\u6253\u4e86\u4e00\u8f6c\uff0c\u56de\u6765\u7684\u65f6\u5019\uff0c\u5e97\u5c0f\u4e8c\u679c \u7136\u8ddf\u7740\u4ed6\u642c\u6765\u4e86\u4e00\u684c\u9152\u5e2d\u3002 \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u8fd9\u4e48\u591a\u9152\u83dc\u600e\u4e48\u5403\u5f97\u4e86\uff1f\u201d\u675c\u96c4\u9053\uff1a\u201c\u54b1\u4eec\u6328\u4e86\u8bb8\u591a\u65e5\u5b50\u7684\u82e6\uff0c\u4e5f\u8be5\u4eab\u53d7\u4e86 \u4e00\u4e0b\uff0c\u5403\u4e0d\u4e86\u5c31\u62e3\u559c\u6b22\u7684\u6bcf\u6837\u5403\u4e00\u70b9\u3002\u201d\u8bf4\u7f62\uff0c\u5bf9\u5e97\u5c0f\u4e8c\u6325\u4e00\u6325\u624b\uff0c\u793a\u610f\u4ed6\u4e0d\u7528\u5728\u4e00\u65c1\u4f3a \u5019\uff0c\u5e97\u5c0f\u4e8c\u6446\u597d\u9152\u5e2d\uff0c\u4fbf\u5373\u9000\u4e0b\u3002 \u3000\u3000\u6768\u5a49\u5728\u675c\u96c4\u6bb7\u52e4\u76f8\u529d\u4e4b\u4e0b\u6328\u4e86\u51e0\u8457\u83dc\uff0c\u675c\u96c4\u5012\u6ee1\u4e24\u676f\u9152\uff0c\u8bf4\u9053\uff1a\u201c\u5a49\u59b9\uff0c\u73b0\u5728\u54b1\u4eec\u603b\u7b97 \u662f\u6682\u65f6\u8131\u51fa\u9669\u5883\u4e86\uff0c\u6211\u548c\u4f60\u5e72\u4e00\u676f\uff0c\u5e86\u8d3a\u5e86\u8d3a\u3002\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u6211\u4e0d\u4f1a\u559d\u9152\u3002\u201d \u3000\u3000\u675c\u96c4\u7b11\u9053\uff1a\u201c\u8fd9\u4e5f\u4e0d\u662f\u70c8\u9152\uff0c\u53ea\u559d\u4e00\u676f\uff0c\u4e0d\u4f1a\u9189\u7684\u3002\u5403\u8fc7\u4e86\u996d\uff0c\u6211\u5230\u5916\u9762\u53e6\u627e\u4e2a\u5730\u65b9\u8fc7 \u591c\uff0c\u6211\u662f\u7537\u4eba\uff0c\u6ca1\u6709\u5ba2\u5e97\uff0c\u9732\u5bbf\u4e5f\u884c\u3002\u201d\u8a00\u4e0b\u4e4b\u610f\uff0c\u663e\u7136\u662f\u6015\u6768\u5a49\u4e0d\u653e\u5fc3\uff0c\u662f\u4ee5\u4ed6\u907f\u5acc\u7591 \u4e86\u3002 \u3000\u3000\u6768\u5a49\u5012\u6709\u70b9\u8fc7\u610f\u4e0d\u53bb\uff0c\u5fc3\u60f3\uff1a\u201c\u4ed6\u548c\u6211\u4e00\u8def\u540c\u884c\uff0c\u672a\u66fe\u5bf9\u6211\u6709\u8fc7\u4e1d\u6beb\u4e0d\u8f68\u7684\u52a8\u4f5c\u3002\u770b\u6765 \u4ed6\u603b\u8fd8\u80fd\u591f\u7b97\u5f97\u4e0a\u662f\u4e2a\u53d1\u4e4e\u60c5\u6b62\u4e4e\u793c\u7684\u541b\u5b50\u3002\u201d \u3000\u3000\u6768\u5a49\u6b63\u81ea\u8e0c\u8e87\uff0c\u53ea\u89c1\u675c\u96c4\u5df2\u662f\u4e00\u996e\u800c\u5c3d\uff0c\u628a\u676f\u5e95\u7ffb\u4e86\u8f6c\u6765\u3002\u8bf4\u9053\uff1a\u201c\u6211\u5148\u5e72\u4e3a\u656c\u4e86\u3002\u5a49 \u59b9\uff0c\u4f60\u82e5\u4e0d\u559d\uff0c\u90a3\u5c31\u662f\u8fd8\u602a\u6211\u4e86\u3002\u201d \u3000\u3000\u6768\u5a49\u542c\u4ed6\u8fd9\u6837\u8bf4\uff0c\u4e0d\u5f97\u5df2\u4e3e\u8d77\u9152\u676f\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u5427\uff0c\u6211\u867d\u7136\u4e0d\u4f1a\u559d\u9152\uff0c\u8fd9\u4e00\u676f\u4e5f\u8fd8\u8981\u966a \u5927\u54e5\u559d\u7684\u3002\u201d\u675c\u96c4\u89c1\u5979\u80af\u559d\uff0c\u5fc3\u4e2d\u6697\u6697\u6b22\u559c\u3002 \u3000\u3000\u6768\u5a49\u4e3e\u8d77\u9152\u676f\uff0c\u6b63\u8981\u559d\u4e0b\uff0c\u5ffd\u542c\u5f97\u201c\u94db\u201d\u7684\u4e00\u58f0\uff0c\u7a97\u53e3\u7a81\u7136\u98de\u8fdb\u6765\u4e00\u679a\u94dc\u94b1\uff0c\u6253\u788e\u4e86\u6768 \u5a49\u624b\u4e2d\u7684\u9152\u676f\u3002 \u3000\u3000\u6768\u5a49\u5927\u5403\u4e00\u60ca\uff0c\u53ea\u542c\u5f97\u7a97\u5916\u90a3\u4eba\u6025\u58f0\u53eb\u9053\uff1a\u201c\u9152\u4e2d\u6709\u8ff7\u836f\uff0c\u7edd\u4e0d\u53ef\u559d\uff01\u201d\u542c\u8fd9\u53e3\u97f3\uff0c\u6b63 \u662f\u65e5\u95f4\u5356\u5200\u7ed9\u5979\u7684\u90a3\u4e2a\u6c49\u5b50\u3002 \u3000\u3000\u675c\u96c4\u5927\u6012\uff0c\u4e00\u638c\u63a8\u5f00\u7a97\u6237\uff0c\u4fbf\u8df3\u51fa\u53bb\uff0c\u559d\u9053\uff1a\u201c\u597d\u5440\uff0c\u539f\u6765\u662f\u4f60\uff0c\u6211\u5df2\u7ecf\u9976\u4e86\u4f60\u7684\u6027 \u547d\uff0c\u4f60\u8fd8\u6562\u5230\u8fd9\u91cc\u6765\u548c\u6211\u6363\u4e71\uff01\u201d \u3000\u3000\u90a3\u4eba\u4e00\u4e2a\u8f6c\u8eab\uff0c\u5c31\u4ece\u5c4b\u9876\u8df3\u4e86\u4e0b\u53bb\uff0c\u626c\u58f0\u53eb\u9053\uff1a\u201c\u5927\u5e08\u54e5\uff0c\u4f60\u5bb3\u4e86\u9f99\u521a\u5df2\u662f\u5929\u7406\u4e0d\u5bb9\uff0c \u5982\u4eca\u53c8\u4f7f\u5c3d\u5fc3\u673a\uff0c\u5bb3\u4e00\u4e2a\u65e0\u4f9d\u65e0\u9760\u7684\u5973\u5b50\uff0c\u4f60\u8fd8\u662f\u4eba\u5417\uff1f\u6768\u59d1\u5a18\uff0c\u4f60\u522b\u542c\u4ed6\u7684\u8bdd\uff0c\u674e\u601d\u5357\u3001 \u674e\u601d\u5357\u2014\u2014\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u675c\u96c4\u5df2\u662f\u8ffd\u5230\uff0c\u95ea\u7535\u822c\u5730\u4e00\u5251\u5c31\u523a\u8fc7\u53bb\uff0c\u90a3\u4eba\u6321\u4e86\u4e00\u5251\uff0c\u864e\u53e3\u9178 \u9ebb\uff0c\u957f\u5251\u51e0\u4e4e\u628a\u63e1\u4e0d\u7262\uff0c\u53ea\u597d\u98de\u5feb\u9003\u8d70\u3002 \u3000\u3000\u675c\u96c4\u6012\u4e0d\u53ef\u6b47\uff0c\u6c89\u58f0\u559d\u9053\uff1a\u201c\u77f3\u749e\uff0c\u8fd9\u662f\u4f60\u81ea\u8ba8\u82e6\u5403\uff0c\u4eca\u665a\u6211\u53ef\u662f\u4e0d\u80fd\u518d\u9976\u4f60\u4e86\u3002\u201d\u6954 \u800c\u4e0d\u820d\u5730\u9489\u5728\u90a3\u4eba\u540e\u9762\uff0c\u7d27\u7d27\u8ffd\u8e2a\u3002 \u3000\u3000\u539f\u6765\u8fd9\u4e2a\u5356\u5200\u7684\u6c49\u5b50\u4e0d\u662f\u522b\u4eba\uff0c\u6b63\u662f\u674e\u601d\u5357\u90a3\u65e5\u5728\u5c71\u4e0a\u9047\u89c1\u7684\u90a3\u4e2a\u6c49\u5b50\u2014\u2014\u5c60\u51e4\u7684\u60c5\u4eba \u77f3\u749e\u3002\u800c\u8fd9\u4e2a\u675c\u96c4\u5219\u662f\u5c60\u51e4\u7684\u54e5\u54e5\u5c60\u9f99\u7684\u5316\u540d\u3002 \u3000\u3000\u77f3\u749e\u90a3\u65e5\u548c\u674e\u601d\u5357\u5206\u624b\u4e4b\u540e\uff0c\u672c\u6765\u662f\u8981\u5728\u8774\u8776\u8c37\u627e\u4ed6\u7684\u5e08\u59b9\u7684\uff0c\u8def\u4e0a\u4e71\u519b\u963b\u585e\uff0c\u85cf\u85cf\u8eb2 \u8eb2\u5730\u5c31\u803d\u6401\u51e0\u5929\uff0c\u5230\u4e86\u8774\u8776\u8c37\u4e4b\u65f6\uff0c\u5c60\u51e4\u8fd9\u4e00\u73ed\u4eba\u65e9\u5df2\u8d70\u4e86\u3002 \u3000\u3000\u77f3\u749e\u53ea\u597d\u6253\u7b97\u5148\u56de\u5c71\u5be8\u518d\u8bf4\uff0c\u60f3\u4e0d\u5230\u5728\u8fd9\u5c0f\u9547\u78b0\u89c1\u6768\u5a49\uff0c\u6700\u521d\u4ed6\u8fd8\u4e0d\u6562\u76f8\u4fe1\u6768\u5a49\u5c31\u662f\u90a3 \u4e2a\u4ed6\u66fe\u7ecf\u89c1\u8fc7\u7684\u5df2\u7ecf\u201c\u81ea\u6740\u201d\u4e86\u7684\u7ea2\u8863\u5973\u5b50\uff0c\u540e\u6765\u8d8a\u770b\u8d8a\u50cf\uff0c\u8fd9\u624d\u85c9\u5356\u5200\u540d\uff0c\u5f15\u6768\u5a49\u548c\u4ed6\u8bf4 \u8bdd\u7684\u3002\u53ef\u60dc\u4ed6\u8fd8\u672a\u66fe\u8bf4\u51fa\u674e\u601d\u5357\u7684\u6d88\u606f\uff0c\u5c31\u7ed9\u5c60\u9f99\u6253\u65ad\u3002\u56e0\u6b64\u4ed6\u53ea\u597d\u5192\u9669\u5230\u5ba2\u5e97\u6765\uff0c\u51c6\u5907\u5411 \u6768\u5a49\u63d0\u51fa\u8b66\u544a\uff0c\u65e0\u5de7\u4e0d\u5de7\uff0c\u6070\u5de7\u649e\u7834\u4e86\u5c60\u9f99\u7528\u836f\u9152\u6765\u9a97\u6768\u5a49\u8fd9\u5e55\u628a\u620f\u3002 \u3000\u3000\u5c60\u9f99\u751f\u6015\u4ed6\u8bf4\u51fa\u66f4\u4e0d\u4e2d\u542c\u7684\u8bdd\u6765\uff0c\u4e00\u8ffd\u4e0a\u4fbf\u65bd\u6740\u624b\uff0c\u77f3\u749e\u7684\u5185\u529b\u4e0d\u53ca\u5e05\u5144\uff0c\u8f7b\u529f\u7a0d\u80dc\u4e00 \u7b79\uff0c\u5f53\u4e0b\u8fb9\u6253\u8fb9\u8d70\uff0c\u677e\u4e86\u53e3\u6c14\uff0c\u4f9d\u7136\u56b7\u4e86\u51fa\u9053\uff1a\u201c\u674e\u601d\u5357\u8fd8\u5728\u4eba\u95f4\uff01\u201d\u5c60\u9f99\u5927\u6012\u9053\uff1a\u201c\u674e\u601d \u5357\u8fd8\u5728\u4eba\u95f4\uff0c\u4f60\u53ef\u662f\u4e0d\u80fd\u518d\u6d3b\u5728\u4eba\u95f4\u4e86\uff01\u201d\u77f3\u749e\u5185\u529b\u4e0d\u53ca\u5c60\u9f99\u4e4b\u80fd\u6301\u4e45\uff0c\u5927\u7ea6\u8dd1\u51fa\u4e86\u5341\u91cc\u4e4b \u5916\uff0c\u7ec8\u4e8e\u7ed9\u5c60\u9f99\u8ffd\u4e0a\u3002 \u3000\u3000\u77f3\u749e\u6700\u540e\u8bf4\u7684\u90a3\u53e5\u201c\u674e\u601d\u5357\u8fd8\u5728\u4eba\u95f4\u201d\uff0c\u6768\u5a49\u5e76\u6ca1\u6709\u542c\u5f97\u5b8c\u5168\u3002\u4f46\u4ed6\u6307\u8d23\u5c60\u9f99\u7684\u8a00\u8bed\uff0c \u6768\u5a49\u5374\u5df2\u662f\u542c\u5f97\u6e05\u6e05\u695a\u695a\u3002\u4ece\u4ed6\u53e3\u4e2d\u8bf4\u51fa\u6765\u7684\u201c\u674e\u601d\u5357\u201d\u8fd9\u4e09\u4e2a\u5b57\uff0c\u6768\u5a49\u5374\u4e5f\u9690\u7ea6\u542c\u89c1\u4e86\u3002 \u3000\u3000\u6768\u5a49\u5446\u82e5\u6728\u9e21\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u795e\u667a\u624d\u6e10\u6e10\u6062\u590d\u6e05\u660e\uff0c\u60f3\u9053\uff1a\u201c\u8fd9\u4eba\u8bf4\u8d77\u5357\u54e5\u7684\u540d\u5b57\uff0c\u90a3\u4e48 \u4ed6\u662f\u8ba4\u8bc6\u5357\u54e5\u7684\u4e86\uff0c\u4ed6\u8bf4\u8fd9\u9152\u4e2d\u6709\u8ff7\u836f\uff0c\u5374\u4e0d\u77e5\u662f\u771f\u662f\u5047\uff1f\u675c\u96c4\u7684\u4e3a\u4eba\u60f3\u6765\u4e0d\u81f3\u4e8e\u5982\u6b64\u5351\u52a3 \u5427\uff1f\u201d\u5fc3\u4e2d\u6b63\u5728\u534a\u4fe1\u534a\u7591\u4e4b\u9645\uff0c\u5ffd\u542c\u5f97\u201c\u55b5\u201d\u7684\u4e00\u58f0\uff0c\u53c8\u628a\u6768\u5a49\u5413\u4e86\u4e00\u8df3\uff0c\u539f\u6765\u662f\u4e00\u53ea\u732b\u4ece \u6253\u5f00\u7684\u7a97\u53e3\u8df3\u4e86\u8fdb\u6765\u3002\u6b63\u662f\uff1a \u3000\u3000\u753b\u864e\u753b\u76ae\u96be\u753b\u9aa8\uff0c\u77e5\u4eba\u77e5\u9762\u4e0d\u77e5\u5fc3\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b\u2014\u2014\u7b2c\u5341\u4e94\u56de\u3000\u6728\u5170\u6562\u5171\u80e1\u9a91\u53bb\u3000\u5d14\u62a4\u7a7a\u5bfb\u6545\u4fa3\u6765 \u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b \u7b2c\u5341\u4e94\u56de\u3000\u6728\u5170\u6562\u5171\u80e1\u9a91\u53bb\u3000\u5d14\u62a4\u7a7a\u5bfb\u6545\u4fa3\u6765 \u3000\u3000\u8fd9\u53ea\u732b\u60f3\u662f\u95fb\u5230\u9c7c\u8089\u7684\u9999\u5473\uff0c\u6765\u5230\u4e86\u6768\u5a49\u7684\u623f\u95f4\uff0c\u4f46\u89c1\u6709\u4eba\uff0c\u5374\u53c8\u4e0d\u6562\u8df3\u4e0a\u684c\u5b50\uff0c\u4e8e\u662f \u5bf9\u7740\u6768\u5a49\uff0c\u772f\u772f\u5730\u53eb\u3002 \u3000\u3000\u6768\u5a49\u5ffd\u5730\u7075\u673a\u4e00\u52a8\uff0c\u5fc3\u9053\uff1a\u201c\u6211\u4f55\u4e0d\u5c06\u8fd9\u53ea\u5c0f\u732b\u8bd5\u8bd5\uff0c\u5018\u82e5\u771f\u662f\u8ff7\u836f\uff0c\u90a3\u4e5f\u4e0d\u4f1a\u6bd2\u6b7b\u4e86 \u5b83\u3002\u201d\u4e8e\u662f\u5939\u4e86\u4e00\u5757\u9c7c\u8089\uff0c\u8638\u4e86\u5730\u4e0a\u672a\u5e72\u7684\u6b8b\u9152\uff0c\u8bf4\u9053\uff1a\u201c\u522b\u5435\uff0c\u522b\u5435\uff0c\u6211\u5582\u4f60\u5403\u4e2a\u9971\uff0c\u5403 \u9971\u4e86\u597d\u597d\u7761\u4e00\u89c9\u3002\u201d \u3000\u3000\u54ea\u77e5\u8fd9\u53ea\u732b\u53ea\u5403\u4e86\u4e00\u5c0f\u5757\u9c7c\u8089\uff0c\u8ddd\u79bb\u5403\u9971\u8fd8\u5dee\u5f97\u8fdc\uff0c\u5ffd\u5730\u5c31\u53e3\u5410\u767d\u6cab\uff0c\u95ed\u4e0a\u773c\u775b\uff0c\u679c\u7136 \u5c31\u6655\u4e86\u8fc7\u53bb\u4e86\u3002 \u3000\u3000\u6768\u5a49\u5413\u5f97\u51b7\u6c57\u76f4\u6d41\uff0c\u5446\u4e86\u534a\u664c\uff0c\u84e6\u5730\u8df3\u4e86\u8d77\u6765\uff0c\u53eb\u9053\uff1a\u201c\u679c\u7136\u675c\u96c4\u5728\u8fd9\u9152\u4e2d\u4e0b\u4e86\u8ff7 \u836f\u3002\u201d \u3000\u3000\u8fd9\u5239\u90a3\u95f4\uff0c\u6768\u5a49\u53c8\u662f\u5403\u60ca\uff0c\u53c8\u662f\u6124\u6012\uff0c\u800c\u66f4\u4ee4\u5979\u611f\u5230\u96be\u53d7\u7684\u662f\u4eba\u5fc3\u96be\u6d4b\u7684\u60b2\u54c0\u3002\u4e00\u4e2a\u5979 \u8ba4\u4e3a\u662f\u6b63\u4eba\u541b\u5b50\u7684\u4eba\uff0c\u539f\u6765\u7adf\u662f\u5982\u6b64\u4e00\u4e2a\u4eba\u9762\u517d\u5fc3\u7684\u5bb6\u4f19\uff0c\u6768\u5a49\u53ea\u89c9\u4e00\u9635\u9635\u51b7\u610f\u76f4\u900f\u5fc3\u5934\u3002 \u3000\u3000\u6768\u5a49\u84e6\u5730\u60f3\u9053\uff1a\u201c\u90a3\u4eba\u6307\u65a5\u675c\u96c4\u8bf4\u8c0e\uff0c\u53c8\u63d0\u8d77\u5357\u54e5\u7684\u540d\u5b57\uff0c\u60f3\u5fc5\u4e00\u5b9a\u662f\u6709\u5173\u4e8e\u5357\u54e5\u7684\u6d88 \u606f\u8981\u544a\u8bc9\u6211\uff01\u5bf9\uff0c\u6211\u975e\u627e\u7740\u4ed6\u4e0d\u53ef\uff01\u8fd8\u6709\u90a3\u4e2a\u4eba\u9762\u517d\u5fc3\u7684\u5bb6\u4f19\uff01\u6211\u4e5f\u975e\u627e\u4ed6\u7b97\u5e10\u4e0d\u53ef\uff01\u201d \u3000\u3000\u5ba2\u5e97\u4e3b\u4eba\u95fb\u58f0\u8d76\u81f3\uff0c\u521a\u597d\u89c1\u5230\u6768\u5a49\u50cf\u5927\u9e1f\u4e00\u822c\u4ece\u7a97\u53e3\u201c\u98de\u201d\u51fa\u53bb\uff0c\u5413\u5f97\u4ed6\u76ee\u77aa\u53e3\u5446\uff0c\u6697 \u81ea\u60f3\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u7adf\u662f\u4e2a\u5973\u98de\u8d3c\uff0c\u5e78\u597d\u6211\u5df2\u6536\u4e86\u4ed6\u4eec\u7684\u53cc\u500d\u623f\u94b1\u3002\u201d \u3000\u3000\u6768\u5a49\u4e0d\u77e5\u77f3\u749e\u9003\u5411\u4f55\u65b9\uff0c\u51fa\u4e86\u5c0f\u9547\uff0c\u5148\u5411\u4e1c\u9762\u5bfb\u627e\uff0c\u6070\u6070\u8d70\u4e86\u4e2a\u76f8\u53cd\u7684\u65b9\u5411\u3002 \u3000\u3000\u4e14\u8bf4\u5c60\u9f99\u8ffd\u4e0a\u4e86\u77f3\u749e\uff0c\u4e00\u6296\u624b\u5c31\u662f\u4e00\u652f\u6bd2\u9f99\u9556\u5f84\u5c04\u8fc7\u53bb\uff0c\u77f3\u749e\u53cd\u5251\u4e00\u62cd\uff0c\u201c\u94db\u201d\u7684\u4e00 \u58f0\uff0c\u6bd2\u9f99\u9556\u51e0\u4e4e\u662f\u64e6\u7740\u4ed6\u7684\u989d\u89d2\u98de\u8fc7\u3002\u5c60\u9f99\u7684\u8155\u529b\u751a\u5f3a\uff0c\u77f3\u749e\u53ea\u80fd\u7a0d\u7a0d\u62e8\u6b6a\u4ed6\u7684\u98de\u9556\uff0c\u5374\u4e0d \u80fd\u5c06\u5b83\u6253\u843d\u3002 \u3000\u3000\u77f3\u749e\u6df1\u77e5\u6bd2\u9f99\u9556\u7684\u5389\u5bb3\uff0c\u4e0d\u6562\u8ba9\u4ed6\u518d\u53d1\uff0c\u65e2\u7136\u662f\u8dd1\u4e0d\u8131\uff0c\u53ea\u597d\u4e3b\u52a8\u91c7\u53d6\u653b\u52bf\u6765\u5236\u6b62\u4ed6\u3002 \u5f53\u4e0b\u77f3\u749e\u4e00\u54ac\u7259\u6839\uff0c\u731b\u6251\u4e0a\u53bb\uff0c\u559d\u9053\uff1a\u201c\u4f60\u5bb3\u6b7b\u4e86\u4e8c\u5e08\u54e5\u8fd8\u8981\u5bb3\u6211\uff0c\u4f60\u6211\u540c\u95e8\u4e4b\u4e49\u5df2\u7edd\uff0c\u4eca \u65e5\u4e0d\u662f\u4f60\u6b7b\u4fbf\u662f\u6211\u4ea1\uff0c\u770b\u5251\uff01\u201d \u3000\u3000\u5c60\u9f99\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u53d7\u6211\u5bb6\u4f20\u827a\u7684\u6069\u5fb7\uff0c\u4e0d\u601d\u62a5\u7b54\uff0c\u53cd\u800c\u52fe\u5f15\u6211\u7684\u59b9\u59b9\uff0c\u8bef\u4e86\u5979\u5927\u597d\u5a5a \u59fb\uff0c\u6211\u624d\u4e0d\u80af\u9976\u4f60\u5462\uff01\u597d\uff01\u4f60\u8981\u62fc\u547d\uff0c\u90a3\u5c31\u6765\u5427\uff0c\u4f46\u53ea\u6015\u4f60\u7eb5\u7136\u662f\u8981\u4e0e\u6211\u62fc\u547d\uff0c\u4e5f\u8fd8\u5dee\u592a \u8fdc\uff01\u201d \u3000\u3000\u5c60\u9f99\u53e3\u4e2d\u8bf4\u8bdd\uff0c\u624b\u5e95\u4e1d\u6beb\u4e0d\u7f13\uff0c\u4e00\u53e3\u9752\u94a2\u5251\u6307\u4e1c\u6253\u897f\uff0c\u6307\u5357\u6253\u5317\uff0c\u5c31\u5728\u8bf4\u8fd9\u51e0\u53e5\u8bdd\u7684\u65f6 \u95f4\uff0c\u5df2\u662f\u5411\u77f3\u749e\u653b\u51fa\u4e86\u8fde\u73af\u5341\u516b\u5251\uff0c\u5251\u5251\u90fd\u662f\u6307\u5411\u77f3\u749e\u7684\u8981\u5bb3\u7a74\u9053\u3002 \u3000\u3000\u5e78\u4e8f\u77f3\u749e\u6df1\u8bf8\u672c\u95e8\u5251\u6cd5\uff0c\u8fd9\u624d\u80fd\u591f\u52c9\u5f3a\u62b5\u6321\uff0c\u4f46\u5c60\u9f99\u7684\u529f\u529b\u8fdc\u80dc\u4e8e\u4ed6\uff0c\u4e34\u654c\u7684\u7ecf\u9a8c\u66f4\u662f \u6bd4\u4ed6\u4e30\u5bcc\uff0c\u77f3\u749e\u4f7f\u51fa\u6d51\u8eab\u672c\u9886\uff0c\u5140\u662f\u65e0\u6cd5\u53cd\u6251\u3002\u4e94\u5341\u62db\u8fc7\u540e\uff0c\u77f3\u749e\u9669\u8c61\u73af\u751f\uff0c\u6574\u4e2a\u8eab\u5f62\u5df2\u5728 \u5c60\u9f99\u5251\u5149\u7b3c\u7f69\u4e4b\u4e0b\u3002 \u3000\u3000\u77f3\u749e\u773c\u770b\u652f\u6301\u4e0d\u4f4f\uff0c\u6b63\u8981\u65bd\u5c55\u4e00\u62db\u4e24\u8d25\u4ff1\u4f24\u7684\u5251\u6cd5\uff0c\u5ffd\u542c\u5f97\u6709\u4eba\u53eb\u9053\uff1a\u201c\u54a6\uff0c\u90a3\u4e0d\u662f\u77f3 \u749e\u5427\uff1f\u77f3\u749e\uff0c\u4f60\u600e\u4e48\u548c\u5927\u5e08\u5144\u6253\u8d77\u6765\u4e86\uff1f\u201d\u77f3\u749e\u4e00\u770b\uff0c\u559c\u51fa\u671b\u5916\u3002\u539f\u6765\u98de\u5954\u800c\u6765\u7684\u8fd9\u4e2a\u4eba\u662f \u5b8b\u94c1\u8f6e\u3002\u5728\u5c71\u5be8\u7684\u4e03\u516b\u4e2a\u5927\u5934\u76ee\u4e4b\u4e2d\uff0c\u77f3\u749e\u548c\u4ed6\u76f8\u4ea4\u6700\u539a\uff0c\u800c\u4e14\u5728\u4e0d\u5230\u4e00\u4e2a\u6708\u4e4b\u524d\uff0c\u4ed6\u4eec\u8fd8 \u66fe\u7ecf\u5728\u8774\u8776\u8c37\u7684\u9644\u8fd1\u89c1\u8fc7\u9762\uff0c\u5c60\u9f99\u6740\u5bb3\u9f99\u521a\u4e4b\u4e8b\uff0c\u4ed6\u4e5f\u662f\u5df2\u7ecf\u77e5\u9053\u7684\u4e86\u3002 \u3000\u3000\u77f3\u749e\u8fde\u5fd9\u53eb\u9053\uff1a\u201c\u5b8b\u5927\u54e5\uff0c\u4f60\u6765\u8bc4\u8bc4\u8fd9\u4e2a\u9053\u7406\u3002\u4ed6\u521a\u624d\u8ff7\u5978\u4e00\u4e2a\u6709\u592b\u4e4b\u5987\uff0c\u7ed9\u6211\u649e\u7834\uff0c \u4ed6\u5c31\u8981\u6740\u6211\uff01\u201d \u3000\u3000\u5c60\u9f99\u559d\u9053\uff1a\u201c\u80e1\u8bf4\u516b\u9053\u3002\u6211\u60e9\u6cbb\u4ed6\u662f\u56e0\u4e3a\u4ed6\u72af\u4e86\u95e8\u89c4\uff01\u201d \u3000\u3000\u77f3\u749e\u51b7\u7b11\u9053\uff1a\u201c\u6211\u72af\u4e86\u4ec0\u4e48\u89c4\uff1f\u4f60\u624d\u771f\u6b63\u662f\u672c\u95e8\u7684\u53db\u5f92\u5462\uff01\u4f60\u8c0b\u5bb3\u4e86\u4e8c\u5e08\u54e5\uff0c\u53c8\u52fe\u7ed3\u5916 \u4eba\uff0c\u903c\u5ac1\u5e08\u59b9\u2026\u2026\u201d \u3000\u3000\u5c60\u9f99\u5927\u6012\u9053\uff1a\u201c\u4f60\u6709\u4ec0\u4e48\u51ed\u636e\uff0c\u6562\u8bf4\u6211\u662f\u8c0b\u6740\u9f99\u521a\uff1f\u7239\u7239\u5df2\u6b7b\uff0c\u5982\u4eca\u6211\u5c31\u662f\u5c71\u5be8\u4e4b\u4e3b\uff0c \u4f60\u5bf9\u6211\u4e0d\u656c\uff0c\u6211\u5c31\u53ef\u4ee5\u628a\u4f60\u5904\u6b7b\uff01\u201d\u4e00\u53e3\u5251\u76d8\u65cb\u98de\u821e\uff0c\u7740\u7740\u8fdb\u903c\uff0c\u653b\u5f97\u8d8a\u53d1\u51f6\u72e0\u3002 \u3000\u3000\u5b8b\u94c1\u8f6e\u4e00\u770b\u4e0d\u5999\uff0c\u8fde\u5fd9\u628a\u65e5\u6708\u53cc\u8f6e\u67b6\u4f4f\u5c60\u9f99\u7684\u957f\u5251\uff0c\u53eb\u9053\uff1a\u201c\u5c11\u5be8\u4e3b\uff0c\u6709\u8bdd\u597d\u8bf4\u3002\u522b\u4f24 \u4e86\u5e08\u5144\u5f1f\u7684\u548c\u6c14\u3002\u201d \u3000\u3000\u5c60\u9f99\u6012\u9053\uff1a\u201c\u5b8b\u94c1\u8f6e\uff0c\u4f60\u662f\u8981\u5e2e\u8fd9\u5c0f\u5b50\u548c\u6211\u4f5c\u5bf9\u4e48\uff1f\u201d \u3000\u3000\u5b8b\u94c1\u8f6e\u672c\u6765\u662f\u4e2a\u5ac9\u6076\u5982\u4ec7\u7684\u6c49\u5b50\uff0c\u4f46\u56e0\u4e00\u6765\u5c60\u9f99\u6bd5\u7adf\u8fd8\u662f\u5c11\u5be8\u4e3b\u7684\u8eab\u4efd\uff0c\u5728\u672a\u66fe\u5c06\u4ed6\u7684 \u7f6a\u6076\u63ed\u9732\uff0c\u672a\u66fe\u7ecf\u8fc7\u4f17\u8bae\u5c06\u4ed6\u58f0\u8ba8\u4e4b\u524d\uff0c\u5b8b\u94c1\u8f6e\u4e0d\u4fbf\u4ee5\u4e0b\u72af\u4e0a\uff1b\u4e8c\u6765\u5b8b\u94c1\u8f6e\u6df1\u77e5\u5c60\u9f99\u7684\u672c \u9886\uff0c\u4ed6\u548c\u77f3\u749e\u8054\u624b\uff0c\u53ea\u6015\u4e5f\u8fd8\u662f\u6253\u4e0d\u8fc7\u5c60\u9f99\u3002\u5c60\u9f99\u82e5\u662f\u53d1\u8d77\u72e0\u6765\uff0c\u77f3\u749e\u56fa\u7136\u662f\u6027\u547d\u96be\u4fdd\uff0c\u8fde \u5b8b\u94c1\u8f6e\u53ea\u6015\u4e5f\u8981\u7ed9\u4ed6\u6740\u4e86\u706d\u53e3\u3002 \u3000\u3000\u5b8b\u94c1\u8f6e\u5fcd\u4f4f\u4e86\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u5c11\u5be8\u4e3b\uff0c\u6211\u662f\u5e2e\u7406\u4e0d\u5e2e\u4eb2\u3002\u4f60\u4eec\u5148\u4f4f\u4e86\u624b\uff0c\u5f85\u6211\u53bb\u67e5\u660e\u6b64 \u4e8b\uff0c\u5018\u82e5\u77f3\u749e\u5bf9\u4f60\u7684\u6307\u63a7\u4e43\u662f\u8c0e\u8a00\uff0c\u90a3\u65f6\u5c31\u662f\u4f60\u80af\u9976\u4ed6\uff0c\u6211\u4e5f\u4e0d\u80af\u653e\u8fc7\u4ed6\u7684\uff01\u201d\u5b8b\u94c1\u8f6e\u5f53\u7136 \u77e5\u9053\u66f2\u5728\u5c60\u9f99\uff0c\u4ed6\u8bf4\u8fd9\u8bdd\uff0c\u4e0d\u8fc7\u662f\u7ed9\u5c60\u9f99\u627e\u4e2a\u53f0\u9636\u8ba9\u4ed6\u81ea\u5df1\u4e0b\u6765\u7f62\u4e86\u3002\u8981\u77e5\u67e5\u660e\u771f\u76f8\uff0c\u603b\u5f97 \u8d39\u4e00\u4e9b\u65f6\u5019\uff0c\u5c60\u9f99\u5fc3\u865a\u80c6\u602f\uff0c\u81ea\u4f1a\u4e00\u8d70\u4e86\u4e4b\u3002 \u3000\u3000\u5c82\u77e5\u5c60\u9f99\u867d\u7136\u662f\u5fc3\u865a\u80c6\u602f\uff0c\u5374\u4e5f\u4e0d\u80af\u4e00\u8d70\u4e86\u4e4b\u3002\u77f3\u749e\u4e0d\u63ed\u7834\u4ed6\u7684\u7f6a\u6076\u72b9\u53ef\uff0c\u5982\u4eca\u4ed6\u5df2\u77e5 \u9053\u77f3\u749e\u6d1e\u6089\u4ed6\u7684\u7f6a\u884c\uff0c\u7109\u80fd\u8fd8\u5bb9\u5f97\u77f3\u749e\u6d3b\u5728\u4e16\u4e0a\uff1f \u3000\u3000\u5c60\u9f99\u8e0c\u8e87\u7247\u523b\uff0c\u7ec8\u4e8e\u662f\u201c\u6740\u4eba\u706d\u53e3\u201d\u7684\u5ff5\u5934\u5360\u4e86\u4e0a\u98ce\uff0c\u5f53\u4e0b\u731b\u7684\u4e00\u5251\uff0c\u63a8\u5f00\u4e86\u5b8b\u94c1\u8f6e\u7684 \u65e5\u6708\u53cc\u8f6e\uff0c\u559d\u9053\uff1a\u201c\u5b8b\u5934\u9886\uff0c\u6b64\u4e8b\u4e0e\u4f60\u65e0\u5173\u3002\u6211\u662f\u4ee5\u638c\u95e8\u5e08\u5144\u7684\u8eab\u4efd\u6e05\u7406\u95e8\u6237\uff0c\u4f60\u5927\u53ef\u4e0d\u5fc5 \u591a\u7ba1\u95f2\u4e8b\uff01\u201d \u3000\u3000\u5c60\u9f99\u4e00\u6765\u662f\u6015\u591c\u957f\u68a6\u591a\uff0c\u4e8c\u6765\u56e0\u4e3a\u5b8b\u94c1\u8f6e\u662f\u4ed6\u7236\u4eb2\u751f\u524d\u6700\u5f97\u529b\u7684\u624b\u4e0b\uff0c\u5c60\u9f99\u5bf9\u4ed6\u8fd8\u591a\u5c11 \u6709\u70b9\u987e\u5fcc\uff0c\u8fd9\u624d\u628a\u5b8b\u94c1\u8f6e\u6487\u5f00\uff0c\u800c\u53ea\u662f\u58f0\u8a00\u8981\u5bf9\u4ed8\u77f3\u749e\u7684\u3002 \u3000\u3000\u5b8b\u94c1\u8f6e\u53ea\u9000\u4e86\u4e00\u6b65\uff0c\u5c60\u9f99\u4e00\u5251\u523a\u51fa\uff0c\u4ed6\u7acb\u5373\u53c8\u8df3\u4e0a\u6765\uff0c\u6321\u7740\u77f3\u749e\uff0c\u53eb\u9053\uff1a\u201c\u4e14\u6162\uff01\u201d \u3000\u3000\u5c60\u9f99\u6309\u5251\u65a5\u9053\uff1a\u201c\u5b8b\u5934\u9886\uff0c\u4f60\u662f\u6709\u610f\u548c\u6211\u8fc7\u4e0d\u53bb\u4e48\uff1f\u6211\u5df2\u7ecf\u544a\u8bc9\u4e86\u4f60\uff0c\u8fd9\u4e8b\u4e0d\u5fc5\u4f60\u6765\u591a \u7ba1\uff01\u201d \u3000\u3000\u5b8b\u94c1\u8f6e\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u662f\u4f60\u672c\u95e8\u4e4b\u4e8b\uff0c\u6211\u7ba1\u4e0d\u7740\u3002\u4f46\u5374\u6709\u4e00\u4e2a\u4eba\u5374\u7ba1\u5f97\u7740\uff01\u201d \u3000\u3000\u5c60\u9f99\u559d\u9053\uff1a\u201c\u8c01\uff1f\u201d \u3000\u3000\u5b8b\u94c1\u8f6e\u9053\uff1a\u201c\u4f60\u7684\u59b9\u59b9\u51e4\u59d1\u5a18\u3002\u5979\u6b63\u8981\u627e\u5bfb\u77f3\u749e\uff0c\u4e5f\u66fe\u5429\u5490\u8fc7\u627e\uff0c\u53eb\u627e\u5e2e\u5979\u7559\u5fc3\u3002\u5982\u4eca \u4f60\u4eec\u95f9\u6210\u8fd9\u4e2a\u6837\u5b50\uff0c\u6211\u53d7\u4e86\u4ee4\u59b9\u4e4b\u6258\uff0c\u4f3c\u4e4e\u4e5f\u4e2a\u80fd\u4e0d\u7ba1\u4e00\u7ba1\u4e86\uff01\u201d \u3000\u3000\u5c60\u9f99\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e2b\u5934\u73b0\u5728\u54ea\u513f\uff1f\u201d \u3000\u3000\u5b8b\u94c1\u8f6e\u5148\u4e0d\u7b54\u8bdd\uff0c\u5ffd\u5730\u628a\u624b\u4e00\u626c\uff0c\u4e00\u679d\u86c7\u7130\u7bad\u5c04\u4e0a\u5929\u7a7a\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u7b49\u4f1a\u513f\u3002\u5979\u5c31\u6765 \u5230\uff01\u201d \u3000\u3000\u5c60\u9f99\u6012\u9053\uff1a\u201c\u6211\u6b63\u8981\u7ba1\u6559\u5979\u5462\uff0c\u5979\u5012\u60f3\u6765\u7ba1\u6211\u4e86\uff1f\u201d \u3000\u3000\u5b8b\u94c1\u8f6e\u9053\uff1a\u201c\u8fd9\u662f\u4f60\u4eec\u7684\u5bb6\u4e8b\uff0c\u6211\u4e0d\u4fbf\u591a\u5634\u3002\u4e0d\u8fc7\uff0c\u4f60\u5c31\u662f\u8981\u60e9\u6cbb\u77f3\u749e\uff0c\u4e5f\u4e0d\u5dee\u8fd9\u4e00\u4f1a \u513f\u3002\u5f85\u4ee4\u59b9\u6765\u4e86\uff0c\u4f60\u4eec\u8bf4\u4e2a\u6e05\u695a\uff0c\u518d\u60e9\u6cbb\u4e5f\u8fd8\u4e0d\u8fdf\uff0c\u514d\u5f97\u4f24\u4e86\u5144\u59b9\u7684\u548c\u6c14\u3002\u201d \u3000\u3000\u5c60\u9f99\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u8fd9\u4e2b\u5934\u773c\u91cc\u53ea\u6709\u4e00\u4e2a\u77f3\u749e\uff0c\u54ea\u91cc\u8fd8\u6709\u6211\u8fd9\u4e2a\u54e5\u54e5\u3002\u54fc\uff0c\u6211\u504f\u4e0d\u80fd\u53eb\u5979 \u5982\u613f\uff0c\u5b8b\u94c1\u8f6e\uff0c\u4f60\u65e2\u7136\u77e5\u9053\u8fd9\u662f\u6211\u4eec\u7684\u5bb6\u4e8b\uff0c\u90a3\u4f60\u5c31\u522b\u6321\u5728\u4e2d\u95f4\u3002\u201d\u8a00\u4e0b\u4e4b\u610f\uff0c\u5b9e\u662f\u60f3\u8d81\u5c60 \u51e4\u672a\u5230\u4e4b\u524d\uff0c\u5148\u6740\u4e86\u77f3\u749e\u3002 \u3000\u3000\u5b8b\u8f76\u8f6e\u9053\uff1a\u201c\u6211\u53d7\u4e86\u98ce\u59d1\u5a18\u4e4b\u6258\uff0c\u5fc5\u987b\u8ba9\u77f3\u749e\u89c1\u5979\u4e00\u9762\u3002\u4f60\u8981\u6740\u4ed6\uff0c\u7b49\u4f60\u59b9\u59b9\u6765\u4e86\u518d \u8bf4\u3002\u201d\u8a00\u4e0b\u4e4b\u610f\uff0c\u5c60\u9f99\u82e5\u60f3\u73b0\u5728\u52a8\u624b\uff0c\u4ed6\u53ea\u6709\u548c\u77f3\u749e\u8054\u624b\u5e94\u4ed8\u3002 \u3000\u3000\u5c60\u9f99\u5e76\u975e\u6015\u4ed6\u59b9\u59b9\uff0c\u53ea\u56e0\u4ed6\u5982\u4eca\u53ea\u662f\u5355\u8eab\u4e00\u4eba\uff0c\u5018\u82e5\u5c60\u51e4\u6765\u5230\uff0c\u5c60\u51e4\u7684\u672c\u9886\u4e0d\u8fc7\u6bd4\u4ed6\u7a0d \u900a\u4e00\u7b79\uff0c\u52a0\u4e0a\u4e00\u4e2a\u77f3\u749e\uff0c\u4ed6\u662f\u5fc5\u8d25\u65e0\u7591\u3002\u5373\u4f7f\u5c60\u51e4\u672a\u5230\uff0c\u77f3\u749e\u548c\u5b8b\u94c1\u8f6e\u8054\u624b\uff0c\u4ed6\u4e5f\u4e0d\u6613\u53d6 \u80dc\uff1b\u4ed6\u521a\u624d\u58f0\u52bf\u6c79\u6c79\uff0c\u5176\u5b9e\u5c31\u662f\u60f3\u63a2\u63a2\u5b8b\u94c1\u8f6e\u7684\u53e3\u98ce\u3002\u5b8b\u94c1\u8f6e\u575a\u51b3\u7684\u8868\u793a\u4e86\u6001\u5ea6\uff0c\u4ed6\u5c31\u4e0d\u80fd \u4e0d\u591a\u52a0\u8003\u8651\u4e86\u3002 \u3000\u3000\u5c60\u9f99\u5fc3\u60f3\uff1a\u201c\u5b8b\u94c1\u8f6e\u5e2e\u5b9a\u4e86\u8fd9\u5c0f\u5b50\uff0c\u6211\u8981\u6740\u4ed6\uff0c\u81f3\u5c11\u4e5f\u5f97\u5728\u767e\u62db\u5f00\u5916\u3002\u51e4\u4e2b\u5934\u4e00\u5230\uff0c\u53ea \u4f3c\u53cd\u800c\u662f\u6211\u8dd1\u4e0d\u6389\u3002\u201d \u3000\u3000\u5c60\u9f99\u60f3\u81f3\u6b64\u5904\uff0c\u4e0d\u7531\u5f97\u80c6\u602f\u8d77\u6765\uff0c\u5f53\u4e0b\u865a\u5f20\u58f0\u52bf\uff0c\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u8010\u70e6\u7b49 \u5979\u3002\u5979\u6765\u4e86\uff0c\u4f60\u53eb\u5979\u548c\u77f3\u749e\u5230\u9547\u4e0a\u6700\u5927\u7684\u90a3\u5bb6\u5ba2\u5e97\u627e\u6211\u3002\u201d \u3000\u3000\u5b8b\u94c1\u8f6e\u5fcd\u7740\u4e86\u7b11\uff0c\u6545\u610f\u5927\u58f0\u53eb\u9053\uff1a\u201c\u51e4\u59d1\u5a18\u5c31\u8981\u6765\u4e86\uff0c\u55ef\u3002\u6211\u597d\u50cf\u5df2\u7ecf\u542c\u5f97\u9a6c\u8e44\u7684\u54cd\u58f0 \u5566\uff01\u4f60\u4f55\u4e0d\u591a\u7b49\u4f1a\u513f\uff1f\u201d\u5c60\u9f99\u4e00\u80a1\u52b2\u513f\u5730\u98de\u8dd1\uff0c\u88c5\u4f5c\u6ca1\u6709\u542c\u89c1\u5b8b\u94c1\u8f6e\u7684\u8bdd\u3002 \u3000\u3000\u5f85\u5230\u770b\u4e0d\u89c1\u5c60\u9f99\u7684\u80cc\u5f71\uff0c\u5b8b\u94c1\u8f6e\u8fd9\u624d\u54c8\u54c8\u5730\u7b11\u51fa\u58f0\u6765\u3002 \u3000\u3000\u77f3\u749e\u6b63\u81ea\u671b\u773c\u6b32\u7a7f\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u5e08\u59b9\u600e\u4e48\u8fd8\u4e0d\u524d\u6765\uff1f\u201d\u5ffd\u5730\u542c\u5f97\u5b8b\u94c1\u8f6e\u7684\u7b11\u58f0\uff0c\u4e0d\u89c9 \u4e00\u695e\uff0c\u968f\u5373\u604d\u7136\u5927\u609f\uff0c\u8bf4\u9053\uff1a\u201c\u5b8b\u5927\u54e5\uff0c\u6562\u60c5\u4f60\u662f\u9a97\u4ed6\u7684\u4e48\uff1f\u201d \u3000\u3000\u5b8b\u94c1\u8f6e\u7b11\u9053\uff1a\u201c\u4e0d\u9519\u3002\u82e5\u4e0d\u662f\u6211\u8c0e\u8bf4\u4f60\u7684\u5e08\u59b9\u5c31\u8981\u5230\u6765\uff0c\u7109\u80fd\u5413\u5f97\u4ed6\u8d70\uff1f\u201d \u3000\u3000\u77f3\u749e\u597d\u751f\u5931\u671b\uff0c\u5b8b\u94c1\u8f6e\u9053\uff1a\u201c\u4f46\u4f60\u7684\u5e08\u59b9\u6258\u6211\u627e\u4f60\uff0c\u8fd9\u5374\u662f\u771f\u7684\u3002\u5979\u7684\u786e\u662f\u975e\u5e38\u60e6\u8bb0\u4f60 \u5462\uff01\u201d \u3000\u3000\u77f3\u749e\u9053\uff1a\u201c\u4f60\u89c1\u5230\u4e86\u5c60\u51e4\uff1f\u201d \u3000\u3000\u5b8b\u94c1\u8f6e\u9053\uff1a\u201c\u6211\u548c\u4f60\u5206\u624b\u4e4b\u540e\u7684\u7b2c\u4e94\u5929\uff0c\u5c31\u5728\u767d\u72fc\u6cb3\u7554\u8ffd\u4e0a\u4e86\u5979\u4eec\uff0c\u5979\u662f\u548c\u5b5f\u5927\u4fa0\u7684\u5973 \u513f\u5b5f\u660e\u971e\u540c\u5728\u4e00\u8d77\u7684\u3002\u6211\u628a\u4f60\u7684\u6d88\u606f\u544a\u8bc9\u5979\uff0c\u5979\u5f88\u6b22\u559c\u3002\u4e0d\u8fc7\uff0c\u5979\u56e0\u4e3a\u8981\u548c\u5b5f\u660e\u971e\u8d76\u7740\u56de \u53bb\uff0c\u542c\u8bf4\u662f\u51c6\u5907\u8bf7\u5b5f\u5927\u4fa0\u6765\u4e3a\u4f60\u7684\u5e08\u7236\u62a5\u4ec7\u3002\u6240\u4ee5\u5979\u53ea\u6709\u6258\u6211\u5355\u72ec\u56de\u6765\u627e\u4f60\u3002\u201d \u3000\u3000\u77f3\u749e\u53c8\u662f\u6b22\u559c\uff0c\u53c8\u662f\u5931\u671b\u3002\u6b22\u559c\u7684\u662f\u5f97\u5230\u4e86\u5c60\u51e4\u786e\u5207\u7684\u6d88\u606f\uff0c\u5931\u671b\u7684\u662f\u5979\u6ca1\u6709\u6765\u3002\u77f3\u749e \u53f9\u4e86\u53e3\u6c14\u8bf4\u9053\uff1a\u201c\u5e08\u59b9\u672a\u6765\u4e0d\u6253\u7d27\uff0c\u53ea\u662f\u6211\u6015\u5c60\u9f99\u56de\u53bb\u4e0d\u80af\u653e\u8fc7\u90a3\u4f3c\u59d1\u5a18\uff0c\u54b1\u4eec\u4e24\u4eba\u6ca1\u6709\u5e08 \u59b9\u5e2e\u5fd9\u3002\u4e5f\u662f\u65e0\u6cd5\u6551\u5979\u3002\u201d \u3000\u3000\u5b8b\u94c1\u8f6e\u9053\uff1a\u201c\u4f60\u8bf4\u8fd9\u4f4d\u59d1\u5a18\u662f\u4f60\u4e00\u4f4d\u597d\u670b\u53cb\u7684\u672a\u5a5a\u59bb\u5b50\uff0c\u8fd9\u6258\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u77f3\u749e\u9053\uff1a\u201c\u5c31\u662f\u6211\u548c\u4f60\u8bf4\u8fc7\u7684\u90a3\u4f4d\u674e\u601d\u5357\u3002\u201d \u3000\u3000\u5b8b\u94c1\u8f6e\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u539f\u6765\u662f\u674e\u601d\u5357\uff0c\u8fd9\u5c31\u4e0d\u5fc5\u62c5\u5fe7\u4e86\u3002\u201d \u3000\u3000\u77f3\u749e\u8be7\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5b8b\u94c1\u8f6e\u9053\uff1a\u201c\u4ed6\u7684\u672a\u5a5a\u59bb\u5b50\u540d\u53eb\u6768\u5a49\uff0c\u66fe\u7ecf\u548c\u6211\u4ea4\u8fc7\u624b\u7684\uff0c\u5251\u6cd5\u5f88\u662f\u7cbe\u5999\u3002\u5c60\u9f99\u7684\u672c\u9886 \u6216\u4f1a\u66f4\u6bd4\u5979\u9ad8\u660e\u4e00\u4e9b\uff0c\u4f46\u4e5f\u4e0d\u5bb9\u6613\u80dc\u5f97\u5979\u7684\u3002\u4f55\u51b5\uff0c\u4f60\u4e00\u62c6\u7a7f\u4e86\u5c60\u9f99\u7684\u9a97\u5c40\uff0c\u5979\u8fd8\u4e0d\u4f1a\u9003 \u8d70\uff1f\u201d \u3000\u3000\u77f3\u749e\u653e\u4e86\u4e00\u70b9\u5fc3\uff0c\u8bf4\u9053\uff1a\u201c\u867d\u7136\u5982\u6b64\uff0c\u54b1\u4eec\u8fd8\u662f\u627e\u7740\u5979\u7684\u597d\u3002\u674e\u601d\u5357\u6709\u6069\u4e8e\u6211\uff0c\u6211\u8fd8\u672a \u5f97\u673a\u4f1a\u62a5\u7b54\u4ed6\u5462\u3002\u201d \u3000\u3000\u5b8b\u94c1\u8f6e\u9053\uff1a\u201c\u597d\u5427\uff0c\u54b1\u4eec\u8fd9\u5c31\u56de\u53bb\uff0c\u89c1\u673a\u800c\u884c\u3002\u201d \u3000\u3000\u8def\u4e0a\u77f3\u749e\u95ee\u9053\uff1a\u201c\u4f60\u600e\u4e48\u4f1a\u540c\u674e\u601d\u5357\u592b\u59bb\u6253\u8d77\u6765\u7684\u3002\u201d \u3000\u3000\u5b8b\u94c1\u8f6e\u9053\uff1a\u201c\u8bf4\u6765\u60ed\u6127\uff0c\u6211\u8bef\u4f1a\u4ed6\u7684\u7236\u4eb2\u662f\u6295\u9760\u9791\u5b50\u7684\u5978\u8d3c\uff0c\u8c01\u77e5\u90a3\u4e2a\u5978\u8d3c\u5374\u662f\u4e00\u4e2a\u5192 \u540d\u7684\u5c0f\u4eba\uff0c\u8fd9\u4e8b\u7684\u771f\u76f8\uff0c\u6211\u8fd8\u662f\u89c1\u5230\u4f60\u7684\u5e08\u59b9\u4e4b\u540e\uff0c\u65b9\u624d\u5b8c\u5168\u660e\u767d\u7684\u3002\u201d \u3000\u3000\u77f3\u749e\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u674e\u601d\u5357\u66fe\u7ecf\u548c\u6211\u8bf4\u8fc7\uff0c\u4ed6\u5728\u8774\u8776\u8c37\u89c1\u8fc7\u6211\u7684\u5e08\u59b9\u3002\u90a3\u4e48\u90a3\u4f4d\u6768\u59d1\u5a18\u60f3 \u5fc5\u4e5f\u662f\u548c\u5979\u76f8\u8bc6\u7684\u4e86\uff1f\u201d \u3000\u3000\u5b8b\u94c1\u8f6e\u7b11\u9053\uff1a\u201c\u5f53\u7136\u76f8\u8bc6\u3002\u5979\u548c\u5b5f\u5973\u4fa0\u542c\u5230\u4e86\u6768\u5a49\u5df2\u6b7b\u7684\u6d88\u606f\uff0c\u8fd8\u66fe\u54ed\u4e86\u4e00\u573a\u5462\u3002\u600e\u4e48 \u4f60\u4f20\u7684\u8fd9\u4e2a\u6d88\u606f\u4e43\u662f\u5047\u7684\u3002\u201d \u3000\u3000\u77f3\u749e\u7b11\u9053\uff1a\u201c\u90a3\u4f4d\u6768\u59d1\u5a18\u6b63\u662f\u65e0\u5904\u53ef\u4ee5\u5b89\u8eab\uff0c\u54b1\u4eec\u627e\u5230\u5979\uff0c\u5c31\u8bf7\u5979\u5230\u5c71\u5be8\u53bb\uff0c\u597d\u53eb\u5e08\u59b9 \u6b22\u559c\u3002\u201d \u3000\u3000\u77f3\u749e\u54ea\u91cc\u77e5\u9053\uff0c\u6768\u5a49\u6b64\u65f6\u4e5f\u6b63\u5728\u627e\u4ed6\u3002 \u3000\u3000\u4e14\u8bf4\u6768\u5a49\u6000\u7740\u6ee1\u8154\u6012\u706b\uff0c\u4e00\u5fc3\u8981\u627e\u5c60\u9f99\u7b97\u5e10\uff0c\u51fa\u4e86\u5ba2\u5e97\uff01\u4fbf\u5373\u65bd\u5c55\u8f7b\u529f\u3002\u4e00\u53e3\u6c14\u8dd1\u4e86\u5341 \u51e0\u91cc\u8def\uff0c\u65e2\u672a\u53d1\u73b0\u5c60\u9f99\uff0c\u4e5f\u672a\u53d1\u73b0\u90a3\u4e2a\u5356\u5200\u7684\u6c49\u5b50\uff01 \u3000\u3000\u6768\u5a49\u5fc3\u60f3\uff1a\u201c\u83ab\u975e\u4ed6\u4eec\u8d70\u7684\u4e0d\u662f\u8fd9\u4e00\u6761\u8def\uff1f\u201d\u6768\u5a49\u8dd1\u4e86\u5341\u51e0\u91cc\u8def\uff0c\u9999\u6c57\u6dcb\u6f13\uff0c\u8138\u58eb\u6577\u7684 \u7c89\u7ed9\u6c57\u6c34\u6eb6\u5316\uff0c\u6e7f\u817b\u817b\u7684\u89c9\u5f97\u4e0d\u5927\u8212\u670d\uff0c\u4fbf\u638f\u51fa\u4e00\u6761\u624b\u5e16\uff0c\u63e9\u62b9\u6c57\u6c34\uff0c\u51c6\u5907\u6b47\u606f\u4f1a\u513f\uff0c\u518d\u5f80 \u897f\u9762\u5bfb\u627e\u3002 \u3000\u3000\u8fd9\u665a\u6708\u8272\u5f88\u597d\uff0c\u624b\u5e15\u4e0a\u7ee3\u7740\u7684\u4e00\u5bf9\u8272\u5f69\u660e\u8273\u7684\u9e33\u9e2f\u6620\u5165\u4e86\u6768\u5a49\u7684\u773c\u5e18\u3002\u6768\u5a49\u6014\u4e86\u4e00\u6014\uff0c \u60f3\u4e86\u8d77\u6765\uff0c\u539f\u6765\u8fd9\u6761\u624b\u5e15\u6b63\u662f\u5361\u6d1b\u4e1d\u4e0e\u5979\u5206\u624b\u4e4b\u65f6\uff0c\u9001\u7ed9\u5979\u505a\u7eaa\u5ff5\u7684\u90a3\u6761\u624b\u5e16\u3002\u5979\u4e00\u76f4\u8d34\u8eab \u6536\u85cf\uff0c\u5728\u8def\u4e0a\u820d\u4e0d\u5f97\u4f7f\u7528\uff0c\u6b64\u65f6\u5374\u4e8e\u65e0\u610f\u4e2d\u638f\u4e86\u51fa\u6765\u3002 \u3000\u3000\u6768\u5a49\u5bf9\u7740\u8fd9\u6761\u624b\u5e15\uff0c\u4e0d\u7981\u751f\u51fa\u8bb8\u591a\u611f\u89e6\u3002\u5979\u60f3\u8d77\u4e86\u5979\u5f53\u65f6\u88ab\u5361\u6d1b\u4e1d\u7684\u6b4c\u58f0\u6240\u5438\u5f15\uff0c\u8ffd\u7eb5 \u524d\u5f80\uff0c\u53d1\u73b0\u5361\u6d1b\u4e1d\u88ab\u897f\u590f\u6b66\u58eb\u6240\u4fd8\uff0c\u5979\u548c\u674e\u601d\u5357\u4e00\u8d77\u5c06\u5361\u6d1b\u4e1d\u6551\u51fa\u864e\u53e3\u7684\u5f80\u4e8b\u3002\u5982\u4eca\u5374\u53ea\u5269 \u4e0b\u5979\u5f62\u5355\u5f71\u53ea\uff0c\u5bf9\u6708\u6000\u4eba\u3002\u5979\u53c8\u60f3\u8d77\u5361\u6d1b\u4e1d\u5343\u91cc\u8fe2\u8fe2\u968f\u5979\u7684\u60c5\u90ce\u4e4b\u4e8b\uff0c\u90a3\u6837\u75f4\u5fc3\u7684\u5973\u5b50\uff0c\u5b9e \u662f\u4e16\u95f4\u7f55\u89c1\u3002\u540c\u75c5\u76f8\u601c\uff0c\u6768\u5a49\u4e0d\u7981\u809d\u80a0\u5bf8\u65ad\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u5361\u6d1b\u4e1d\u56de\u5bb6\u7b49\u5f85\uff0c\u6216\u8005\u8fd8\u6709\u7b49\u5f97 \u7740\u5979\u7684\u60c5\u90ce\u56de\u6765\u7684\u4e00\u65e5\uff0c\u6211\u5374\u662f\u53ea\u6015\u4eca\u751f\u4eca\u4e16\uff0c\u518d\u4e5f\u89c1\u4e0d\u7740\u5357\u54e5\u4e86\u3002\u201d \u3000\u3000\u6cea\u5149\u4e2d\u5ffd\u5730\u95ea\u51fa\u4e00\u4e1d\u5e0c\u671b\u7684\u5149\u8292\uff0c\u6768\u5a49\u60f3\u8d77\u4e86\u90a3\u4e2a\u5b9d\u5200\u6c49\u5b50\u8bf4\u4e86\u4e00\u534a\u4fbf\u7ed9\u5c60\u9f99\u6253\u65ad\u7684\u8bf4 \u8bdd\uff1a\u201c\u6768\u59d1\u5a18\uff0c\u4f60\u522b\u542c\u4ed6\u7684\u8c0e\u8bdd\uff0c\u674e\u601d\u5357\u4ed6\u3001\u4ed6\u2014\u2014\u201d\u201c\u4ed6\u600e\u4e48\u6837\u5462\uff1f\u201d \u3000\u3000\u6768\u5a49\u4ed4\u7ec6\u63a8\u6572\uff0c\u90a3\u4eba\u5df2\u7ecf\u63ed\u7834\u4e86\u4ed6\u7ed9\u6211\u559d\u7684\u4e43\u662f\u6bd2\u9152\uff0c\u90a3\u4e48\u4ed6\u6240\u6307\u7684\u201c\u8c0e\u8bdd\u201d\u5fc5\u5b9a\u662f\u53e6 \u5916\u4e00\u6869\u4e8b\u60c5\u3002\u4ed6\u5728\u8bf4\u4e86\u8fd9\u53e5\u8bdd\u4e4b\u540e\uff0c\u8ddf\u7740\u4fbf\u63d0\u5230\u5357\u54e5\u7684\u540d\u5b57\uff0c\u60f3\u6765\u662f\u5e94\u8be5\u548c\u5357\u54e5\u6709\u5173\u7684\u4e86\u3002 \u55ef\uff0c\u83ab\u975e\u5357\u54e5\u8fd8\u5728\u4eba\u95f4\u3002\u675c\u96c4\u8fd9\u8d3c\u5b50\u8bf4\u4ed6\u6b7b\u4e86\uff0c\u5176\u5b9e\u662f\u9a97\u6211\u7684\uff01\u201d \u3000\u3000\u6768\u5a49\u6293\u7740\u4e86\u8fd9\u4e00\u7ebf\u5e0c\u671b\uff0c\u6068\u4e0d\u5f97\u9a6c\u4e0a\u627e\u7740\u90a3\u4e2a\u5356\u5200\u7684\u6c49\u5b50\uff0c\u5411\u4ed6\u95ee\u4e2a\u6c34\u843d\u77f3\u51fa\uff0c\u5f53\u4e0b\u5979 \u968f\u624b\u5c06\u90a3\u6761\u624b\u5e15\u7cfb\u5728\u8170\u4e0a\uff0c\u4fbf\u5f80\u897f\u8d70\u3002 \u3000\u3000\u521a\u8d70\u5f97\u4e0d\u8fdc\uff0c\u5ffd\u542c\u5f97\u6811\u6797\u91cc\u4f3c\u6709\u9a6c\u8e44\u9a70\u9aa4\u7684\u58f0\u54cd\uff0c\u8fd8\u672a\u600e\u4e48\u542c\u5f97\u6e05\u695a\u3002\u6768\u5a49\u5fc3\u60f3\uff1a\u201c\u8fd9 \u4e48\u665a\u4e86\uff0c\u4ec0\u4e48\u4eba\u5728\u8d76\u8def\u5462\uff1f\u8d76\u8def\u4f55\u4ee5\u53c8\u820d\u5927\u8def\u4e0d\u8d70\u4e86\uff1f\u201d \u3000\u3000\u6768\u5a49\u8d77\u4e86\u7591\u56e2\uff0c\u63a8\u60f3\u591a\u534a\u662f\u6c5f\u6e56\u4eba\u7269\uff0c\u53ef\u80fd\u6709\u90a3\u4e2a\u5356\u5200\u7684\u6c49\u5b50\u5728\u5185\u3002\u6768\u5a49\u4e5f\u77e5\u9053\u8fd9\u4e2a\u5e0c \u671b\u751a\u662f\u6e3a\u832b\uff0c\u4f46\u5979\u6015\u4e07\u4e00\u9519\u8fc7\uff0c\u7ec8\u751f\u9057\u61be\uff0c\u4e8e\u662f\u4fbf\u6084\u6084\u5730\u8d70\u5165\u6797\u4e2d\uff0c\u5077\u770b\u6765\u7684\u662f\u8c01\u3002 \u3000\u3000\u90a3\u961f\u4eba\u9a6c\u6765\u5f97\u5feb\u6781\uff0c\u6768\u5a49\u4e00\u5165\u6797\u4e2d\uff0c\u5df2\u542c\u51fa\u6709\u5341\u6570\u9a91\u4e4b\u591a\uff0c\u6768\u5a49\u5403\u4e86\u4e00\u60ca\uff0c\u614c\u5fd9\u8eb2\u5230\u4e00 \u5757\u5927\u77f3\u540e\u9762\uff0c\u53ef\u662f\u6708\u5149\u5df2\u628a\u5979\u7684\u5f71\u5b50\u6295\u5c04\u5728\u5730\u4e0a\uff0c\u6070\u597d\u90a3\u662f\u4e00\u5757\u5bf8\u8349\u4e0d\u751f\u7684\u8352\u5730\uff0c\u98de\u9a91\u800c\u6765 \u7684\u90a3\u4e9b\u4eba\u8fdc\u8fdc\u7684\u5c31\u770b\u89c1\u4e86\u5979\u7684\u5f71\u5b50\u3002 \u3000\u3000\u4e3a\u9996\u7684\u4e00\u4e2a\u519b\u5b98\u559d\u9053\uff1a\u201c\u662f\u8c01\u8eb2\u5728\u90a3\u513f\uff1f\u8fd8\u4e0d\u5feb\u5feb\u7ed9\u6211\u6eda\u51fa\u6765\uff01\u201d\u8bf4\u7684\u662f\u8499\u53e4\u8bdd\u3002\u539f\u6765 \u8fd9\u662f\u4e00\u961f\u8499\u53e4\u9a91\u5175\uff0c\u62c5\u5f53\u5927\u519b\u7684\u201c\u65a5\u51c6\u201d\u4fa6\u5bdf\u5175\uff0c\u591c\u665a\u884c\u519b\uff0c\u7a81\u7834\u8fb9\u5883\uff0c\u51c6\u5907\u7ed9\u4fb5\u5165\u91d1\u5883\u7684 \u8499\u53e4\u5927\u519b\u5f00\u8def\u7684\u4eba\u6570\u4e0d\u591a\uff0c\u603b\u5171\u53ea\u6709\u5341\u9a91\u3002\u90a3\u519b\u5b98\u4e00\u58f0\u547c\u559d\uff01\u9a91\u5feb\u9a6c\uff0c\u9f50\u90fd\u5411\u7740\u6768\u5a49\u85cf\u533f\u4e4b \u5904\u51b2\u51fb\u3002 \u3000\u3000\u201c\u597d\uff0c\u593a\u4ed6\u4eec\u4e00\u5339\u5750\u9a91\u3002\u201d\u7d22\u6027\u7ad9\u4e86\u51fa\u6765\uff0c\u5148\u53d1\u5236\u4eba\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u524d\u9762\u4e00\u9a91\u5df2\u7ecf\u51b2\u5230\u3002\u6768\u5a49\u628a\u624b\u4e00\u626c\uff0c\u4e00\u9897\u77f3\u5b50\u98de\u4e86\u51fa\u53bb\uff0c\u8fd9\u662f\u5979\u521a\u624d \u968f\u624b\u62fe\u8d77\u5f53\u4f5c\u6697\u5668\u7684\uff0c\u4f7f\u7684\u662f\u6697\u5668\u6253\u7a74\u7684\u624b\u6cd5\uff0c\u6b63\u4e2d\u5bf9\u65b9\u80f8\u819b\u201c\u87d2\u77fe\u7a74\u201d\u7684\u65b9\u4f4d\uff0c\u53ea\u542c\u5f97\u53e9 \u5f53\uff0c\u7684\u4e00\u58f0\uff0c\u77f3\u5b50\u53cd\u5f39\u843d\u5730\u3002\u539f\u6765\u90a3\u4e2a\u58eb\u5175\u662f\u62ab\u7740\u76d4\u7532\u7684\uff0c\u9694\u4e86\u4e00\u91cd\u76d4\u7532\uff0c\u81ea\u662f\u6536\u4e0d\u5230\u6253\u7a74 \u7684\u529f\u6548\uff0c\u6768\u5a49\u5306\u5fd9\u4e2d\u6ca1\u6709\u60f3\u5230\u8fd9\u5c42\uff0c\u7a7a\u81ea\u8d39\u4e86\u6c14\u529b\u3002 \u3000\u3000\u53ef\u662f\u867d\u6ca1\u6536\u5230\u6253\u7a74\u529f\u6548\uff0c\u90a3\u4eba\u7ed9\u77f3\u5b50\u51fb\u4e2d\u80f8\u819b\uff0c\u4e5f\u662f\u75db\u5f97\u96be\u53d7\uff0c\u201c\u54ce\u54df\u201d\u4e00\u58f0\uff0c\u9669\u4e9b\u8dcc \u4e0b\u9a6c\u6765\u3002\u63a7\u5236\u4e0d\u4f4f\u7ef3\u7f00\uff0c\u90a3\u5339\u9a6c\u659c\u523a\u51b2\u51fa\u53bb\u4e86\u3002 \u3000\u3000\u540e\u9762\u4e00\u9a91\u98de\u5feb\u5954\u4e0a\uff0c\u53eb\u9053\uff1a\u201c\u8fd9\u5c0f\u59d1\u5a18\u5012\u4f3c\u6709\u70b9\u672c\u9886\uff01\u201d\u6768\u5a49\u62d4\u5200\u51fa\u9798\uff0c\u659c\u8eab\u4e00\u7a9c\uff0c\u5200 \u950b\u6a2a\u524a\u9a6c\u8db3\u3002\u90a3\u4eba\u4f7f\u7684\u662f\u67c4\u957f\u77db\uff0c\u4ece\u9a6c\u80cc\u4e0a\u76f4\u63b7\u4e0b\u6765\u3002\u53ea\u89c1\u5200\u5149\u8fc7\u5904\uff0c\u706b\u82b1\u98de\u6e85\uff0c\u90a3\u4eba\u7684\u77db \u5934\u7ed9\u6768\u5a49\u7684\u5b9d\u5200\u6240\u65ad\uff0c\u4f46\u6768\u5a49\u4e5f\u6ca1\u6709\u65ab\u7740\u9a6c\u8db3\uff0c\u90a3\u5339\u9a6c\u98de\u5feb\u7684\u4e5f\u8fc7\u53bb\u4e86\u3002 \u3000\u3000\u6768\u5a49\u864e\u53e3\u5fae\u611f\u9178\u9ebb\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u51e0\u4e2a\u9791\u5b50\u5012\u4e5f\u4e0d\u662f\u6cdb\u6cdb\u4e4b\u8f88\u3002\u201d\u5fc3\u5ff5\u672a\u5df2\uff0c\u53c8\u6709\u4e24 \u9a91\u5feb\u9a6c\u5411\u5979\u5de6\u53f3\u5939\u51fb\uff0c\u8fd9\u4e24\u4eba\u4f7f\u7684\u4e43\u662f\u957f\u67aa\u5927\u77db\uff0c\u6768\u5a49\u7684\u5200\u53ea\u6709\u4e8c\u5c3a\u516b\u5bf8\uff0c\u867d\u7136\u950b\u5229\u4e5f\u662f\u5403 \u4e8f\u3002\u800c\u4e14\u4e00\u5728\u9a6c\u4e0a\uff0c\u4e00\u5728\u5730\u4e0b\uff0c\u5bf9\u6768\u5a49\u66f4\u662f\u4e0d\u5229\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u4e24\u5339\u5feb\u9a6c\u773c\u770b\u5c31\u8981\u649e\u5230\u6768\u5a49\u8eab\u4e0a\uff0c\u6768\u5a49\u4e00\u770b\u52bf\u96be\u517c\u987e\uff0c\u767e\u5fd9\u4e2d\u53ea\u597d\u65bd \u5c55\u8f7b\u529f\uff0c\u811a\u5c16\u4e00\u70b9\uff0c\u767b\u65f6\u5982\u5927\u9e1f\u822c\u5730\u659c\u63a0\u51fa\u53bb\uff0c\u6070\u6070\u5728\u95f4\u4e0d\u5bb9\u53d1\u4e4b\u9645\uff0c\u907f\u5f00\u4e86\u957f\u67aa\u5927\u621f\u7684\u7a7f \u68ad\u653b\u51fb\u3002 \u3000\u3000\u90a3\u519b\u5b98\u53eb\u9053\uff1a\u201c\u5c0f\u59d1\u5a18\u597d\u4fca\u7684\u529f\u592b\uff01\u4f46\u4f60\u4e5f\u4e0d\u5fc5\u62fc\u547d\uff0c\u6211\u4eec\u5e76\u4e0d\u60f3\u5c06\u4f60\u96be\u4e3a\uff0c\u53ea\u662f\u8981\u5411 \u4f60\u95ee\u4e2a\u660e\u767d\uff1f\u201d \u3000\u3000\u6768\u5a49\u600e\u80fd\u76f8\u4fe1\u4ed6\u7684\u8bf4\u8bdd\u3002\u773c\u770b\u53c8\u6709\u4e00\u9a91\u51b2\u5230\uff0c\u6768\u5a49\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u6b21\u53ef\u4e0d\u80fd\u653e\u8fc7\u4ed6 \u4e86\u3002\u201d\u8eab\u5f62\u75be\u8d77\uff0c\u518d\u4e00\u6b21\u65bd\u5c55\u8d85\u5353\u7684\u8f7b\u529f\uff0c\u5f84\u5411\u9a91\u5728\u9a6c\u4e0a\u7684\u90a3\u4e2a\u8499\u53e4\u5175\u6251\u53bb\uff0c\u811a\u5c16\u672a\u70b9\u7740\u9a6c \u9791\uff0c\u5200\u950b\u5df2\u662f\u671d\u7740\u5bf9\u65b9\u7684\u54bd\u5589\u62b9\u53bb\u3002 \u3000\u3000\u54ea\u77e5\u87b3\u8782\u6355\u8749\uff0c\u9ec4\u96c0\u5728\u540e\uff0c\u6768\u5a49\u7684\u5200\u5c1a\u672a\u89e6\u53ca\u90a3\u4eba\u7684\u8eab\u4f53\uff0c\u53ea\u6240\u5f97\u201c\u7830\u201d\u7684\u4e00\u58f0\uff0c\u6768\u7897 \u7684\u8eab\u4f53\u5148\u7ed9\u4e00\u6761\u7ef3\u5708\u5957\u4f4f\u3002\u539f\u6765\u90a3\u4e2a\u519b\u5b98\u4e43\u662f\u732a\u6237\u51fa\u8eab\uff0c\u6253\u730e\u7684\u672c\u9886\u5728\u8499\u53e4\u6570\u4e00\u6570\u4e8c\u3002\u7528\u7ef3 \u7d22\u6d3b\u64d2\u731b\u517d\uff0c\u6b63\u662f\u4ed6\u62ff\u624b\u7684\u7edd\u6280\uff0c\u6768\u5a49\u8eab\u5b50\u60ac\u7a7a\uff0c\u7ed9\u7ef3\u5708\u5957\u4f4f\uff0c\u54ea\u8fd8\u80fd\u6323\u624e\u3002 \u3000\u3000\u6768\u5a49\u7ed9\u4ed6\u66f3\u4e86\u843d\u5730\uff0c\u8fd0\u529b\u4e00\u6323\uff0c\u5c82\u77e5\u8fd9\u6761\u7ef3\u7d22\u5e76\u975e\u666e\u901a\u7684\u9ebb\u7ef3\uff0c\u4e43\u662f\u7528\u7280\u725b\u7684\u7b4b\u5236\u6210 \u7684\uff0c\u6768\u5a49\u6323\u624e\u4e0d\u8131\uff0c\u7728\u773c\u95f4\uff0c\u8fde\u624b\u81c2\u4e5f\u7ed9\u7f1a\u4f4f\u4e86\uff0c\u6768\u5a49\u7684\u5b9d\u5200\u54a3\u5577\u5760\u5730\u3002 \u3000\u3000\u6768\u5a49\u559d\u9053\uff1a\u201c\u4f60\u6562\u78b0\u4e00\u78b0\u6211\uff0c\u6211\u4e0e\u4f60\u540c\u5f52\u4e8e\u5c3d\uff01\u201d\u6768\u5a49\u6b64\u8a00\uff0c\u5012\u4e5f\u4e0d\u662f\u865a\u58f0\u606b\u5413\uff0c\u5979\u867d \u7136\u53cc\u624b\u88ab\u7f1a\uff0c\u6307\u5934\u8fd8\u6709\u70b9\u7a74\u7684\u529f\u592b\u3002 \u3000\u3000\u90a3\u519b\u5b98\u76b1\u4e86\u76b1\u7709\u5934\uff0c\u8bf4\u9041\uff1a\u201c\u6211\u4eec\u8499\u53e4\u6b66\u58eb\u4e5f\u5e76\u975e\u5982\u4f60\u6240\u60f3\u8c61\u90a3\u6837\u90fd\u662f\u574f\u4eba\u3002\u4f60\u653e\u5fc3\uff0c \u6211\u51b3\u4e0d\u4f1a\u6b3a\u4fae\u4f60\u7684\u3002\u4f46\u4f60\u5fc5\u987b\u544a\u8bc9\u6211\u4f60\u662f\u4ec0\u4e48\u4eba\uff0c\u4f60\u8fd9\u8eab\u672c\u9886\u662f\u4ece\u54ea\u91cc\u5b66\u6765\u7684\uff0c\u53c8\u4e3a\u4ec0\u4e48\u4e09 \u66f4\u534a\u591c\u8eb2\u5728\u8fd9\u6811\u6797\u91cc\u9762\uff1f\u201d \u3000\u3000\u6768\u5a49\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u8981\u561b\u4f60\u5c31\u6740\u6211\uff0c\u8981\u561b\u4f60\u5c31\u653e\u6211\u3002\u6211\u53ef\u4e0d\u80fd\u53d7\u4f60\u76d8\u95ee\uff01\u201d \u3000\u3000\u90a3\u519b\u5b98\u7b11\u9053\uff1a\u201c\u597d\u4e2a\u5014\u5f3a\u7684\u59d1\u5a18\u3002\u4f46\u8fd9\u5374\u6559\u6211\u4e3a\u96be\u4e86\u3002\u6211\u5f53\u7136\u4e0d\u4f1a\u6740\u4f60\uff0c\u4f46\u683c\u4e8e\u519b\u89c4\uff0c \u672a\u7ecf\u5ba1\u95f4\uff0c\u5374\u4e5f\u4e0d\u80fd\u968f\u4fbf\u5c31\u653e\u4e86\u4f60\uff01\u201d \u3000\u3000\u90a3\u519b\u5b98\u4ed4\u7ec6\u6253\u91cf\u4e86\u6768\u5a49\u4e00\u756a\uff0c\u89c9\u5f97\u9762\u8c8c\u597d\u719f\uff0c\u597d\u4f3c\u5728\u54ea\u91cc\u89c1\u8fc7\u4f3c\u7684\uff0c\u4f46\u5374\u60f3\u4e0d\u8d77\u6765\uff0c\u5fc3 \u4e2d\u5f88\u662f\u5947\u602a\uff0c\u9042\u7f13\u7f13\u5730\u5411\u5979\u8d70\u8fd1\u3002\u6768\u5a49\u53cd\u6b63\u662f\u62fc\u4e86\u4e00\u6b7b\uff0c\u4e5f\u4e0d\u7406\u4ed6\u3002 \u3000\u3000\u90a3\u519b\u5b98\u773c\u5149\u4e00\u77a5\uff0c\u77a7\u89c1\u6768\u5a49\u7cfb\u5728\u8170\u95f4\u7684\u90a3\u6761\u624b\u5e15\uff0c\u5ffd\u5730\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8fd9\u6761 \u624b\u5e15\u662f\u4ece\u54ea\u91cc\u5f97\u6765\u7684\uff1f\u201d \u3000\u3000\u6768\u5a49\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4f60\u95ee\u8fd9\u4e2a\u5e72\u561b\uff1f\u201d\u90a3\u519b\u5b98\u60ca\u7591\u4e0d\u5b9a\uff0c\u5148\u4e0d\u8a00\u8bed\uff0c\u8fde\u5fd9\u5c31\u7528\u4ed6\u90a3\u67c4 \u957f\u957f\u7684\u9a6c\u5200\u8fde\u7740\u5200\u9798\u5f80\u524d\u4e00\u4f38\uff0c\u628a\u8fd9\u6761\u624b\u5e15\u6311\u4e86\u8fc7\u5939\uff0c\u4ed6\u679c\u7136\u9075\u5b88\u6768\u5a49\u7684\u7981\u7ea6\uff0c\u867d\u7136\u662f\u6025\u4e8e \u5f97\u5230\u8fd9\u6761\u624b\u5e15\uff0c\u4e5f\u6ca1\u6709\u6328\u7740\u6768\u5a49\u7684\u8eab\u4f53\u3002 \u3000\u3000\u90a3\u519b\u5b98\u5c55\u5f00\u624b\u5e15\u4e00\u770b\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4e00\u70b9\u4e0d\u9519\uff0c\u8fd9\u662f\u5361\u6d1b\u4e1d\u4eb2\u624b\u6240\u7ee3\u7684\u624b\u5e15\u3002\u201d\u614c\u5fd9\u518d \u95ee\uff1a\u201c\u4f60\u4e00\u5b9a\u8981\u544a\u8bc9\u6211\uff0c\u8fd9\u6761\u624b\u5e15\u662f\u4ec0\u4e48\u4eba\u7ed9\u4f60\u7684\uff1f\u201d \u3000\u3000\u6768\u5a49\u5fc3\u4e2d\u4e00\u52a8\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u5148\u544a\u8bc9\u6211\uff0c\u4f60\u662f\u4e0d\u662f\u4e5f\u6709\u4e00\u6761\u540c\u6837\u7684\u624b\u5e15\uff1f\u201d \u3000\u3000\u90a3\u519b\u5b98\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d\u8bf4\u7f62\uff0c\u89e3\u5f00\u76d4\u7532\uff01\u4ece\u8d34\u8eab\u7684\u6c57\u886b\u888b\u4e2d\u638f\u51fa\u4e00 \u6761\u624b\u5e16\uff0c\u5c55\u5f00\u6765\u7ed9\u6768\u5a49\u770b\uff0c\u4e0a\u9762\u679c\u7136\u662f\u7ee3\u7740\u4e00\u5f0f\u4e00\u6837\u7684\u4e00\u53ea\u5140\u9e70\u3002 \u3000\u3000\u6768\u5a49\u8fd9\u624d\u8bf4\u9053\uff1a\u201c\u662f\u4e00\u4e2a\u540d\u53eb\u5361\u6d1b\u4e1d\u7684\u8499\u53e4\u59d1\u5a18\u9001\u7ed9\u6211\u7684\u3002\u4ed6\u8bf4\u5979\u4e00\u5171\u7ee3\u4e86\u4e09\u6761\uff0c\u4e00\u6761 \u7ed9\u4e86\u5979\u7684\u672a\u5a5a\u592b\u5a7f\uff0c\u4e00\u6761\u5979\u81ea\u5df1\u4fdd\u5b58\uff0c\u8fd9\u4e00\u6761\u5979\u5374\u9001\u7ed9\u4e86\u6211\u3002\u201d \u3000\u3000\u90a3\u519b\u5b98\u53c8\u559c\u53c8\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5c31\u662f\u5979\u7684\u672a\u5a5a\u592b\u963f\u76d6\uff0c\u4f60\u662f\u5728\u4ec0\u4e48\u65f6\u5019\u4ec0\u4e48\u5730\u65b9\u89c1\u7740\u5979 \u7684\uff1f\u4e3a\u4f55\u5979\u9001\u4f60\u8fd9\u6761\u624b\u5e15\uff1f\u201d\u539f\u6765\u5361\u6d1b\u4e1d\u66fe\u7ecf\u548c\u4ed6\u8bf4\u8fc7\uff0c\u8fd9\u7b2c\u4e09\u6761\u624b\u5e15\u5979\u662f\u51c6\u5907\u9001\u7ed9\u5979\u6700\u597d \u7684\u4e00\u4f4d\u5973\u53cb\uff0c\u5982\u4eca\u9001\u7ed9\u4e86\u6768\u5a49\uff0c\u663e\u7136\u5728\u5979\u7684\u5fc3\u76ee\u4e4b\u4e2d\uff0c\u6768\u5a49\u5df2\u662f\u66ff\u4ee3\u4e86\u5979\u539f\u6765\u7684\u90a3\u4f4d\u670b\u53cb\u7684 \u4f4d\u7f6e\u3002 \u3000\u3000\u6768\u5a49\u65e9\u5df2\u4ece\u5361\u6cbb\u4e1d\u7684\u53e3\u4e2d\u77e5\u9053\u963f\u76d6\u7684\u4e3a\u4eba\uff0c\u6b64\u65f6\u4e5f\u662f\u4e0d\u7981\u53c8\u60ca\u53c8\u559c\uff0c\u4f46\u5374\u8fd8\u60f3\u8bd5\u4ed6\u4e00 \u8bd5\uff0c\u6545\u610f\u677f\u8d77\u9762\u5b54\u4e0d\u7406\u4f1a\u4ed6\u3002 \u3000\u3000\u963f\u76d6\u77bf\u7136\u4e00\u7701\uff0c\u8fde\u5fd9\u89e3\u4e86\u6768\u5a49\u7684\u6346\u7f1a\uff0c\u8d54\u793c\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u4f60\u662f\u5361\u6d1b\u4e1d\u7684\u597d\u670b\u53cb\uff0c\u5192\u72af \u4e86\u59d1\u5a18\uff0c\u8fd8\u8bf7\u6055\u7f6a\u3002\u201d \u3000\u3000\u6768\u5a49\u8fd9\u624d\u8bf4\u51fa\u90a3\u65e5\u4e0e\u5361\u6d1b\u4e1d\u76f8\u9047\u7684\u4e8b\u60c5\u3002\u963f\u76d6\u542c\u4e86\uff0c\u5927\u5403\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u539f\u6765\u4f60\u8fd8\u662f\u5361 \u6d1b\u4e1d\u7684\u6551\u547d\u6069\u4eba\uff0c\u6211\u771f\u662f\u8be5\u6b7b\uff0c\u8be5\u6b7b\uff01\u201d\u8bf4\u7f62\uff0c\u7adf\u7136\u4f0f\u5728\u5730\u4e0a\uff0c\u4ee5\u9996\u987f\u5730\uff0c\u7ed9\u6768\u5a49\u884c\u4e86\u4e00\u4e2a \u5927\u793c\u3002\u8499\u53e4\u4eba\u6700\u5c0a\u91cd\u7684\u793c\u8282\u662f\u4f0f\u5728\u5730\u4e0a\u55c5\u5bf9\u65b9\u7684\u811a\uff0c\u5e78\u4e8f\u963f\u76d6\u77e5\u9053\u6c49\u4eba\u7537\u5973\u6709\u522b\uff0c\u793c\u4eea\u4e0d \u540c\uff0c\u53ea\u662f\u4f0f\u800c\u4e0d\u55c5\u3002 \u3000\u3000\u6768\u5a49\u4e0d\u4fbf\u6276\u4ed6\u8d77\u6765\uff0c\u4fbf\u8fd8\u4e86\u4e00\u793c\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4f9d\u519b\u4ee4\u884c\u4e8b\uff0c\u6211\u4e5f\u4e0d\u80fd\u602a\u4f60\u3002\u4f46\u4f60\u73b0\u5728\u53ef \u4ee5\u653e\u6211\u4e86\u5427\u3002\u201d \u3000\u3000\u963f\u76d6\u8e0c\u8e87\u7247\u523b\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u65e0\u610f\u7559\u96be\u59d1\u5a18\u3002\u4e0d\u8fc7\uff0c\u6211\u4e3a\u59d1\u5a18\u7740\u60f3\uff0c\u4f60\u4e00\u4e2a\u5355\u8eab\u5973\u5b50\uff0c\u8fd9 \u6761\u8def\u53ea\u6015\u5f88\u4e0d\u597d\u8d70\u54ea\uff01\u5bf9\u5566\uff0c\u6211\u8fd8\u672a\u8bf7\u6559\u59d1\u5a18\u7684\u9ad8\u59d3\u5927\u540d\uff0c\u4f55\u4ee5\u4f1a\u4e00\u4e2a\u4eba\u6765\u5230\u6b64\u5730\uff1f\u59d1\u5a18\u521a \u624d\u4e0d\u662f\u8bf4\uff0c\u90a3\u65e5\u6551\u5361\u6d1b\u4e1d\u7684\u65f6\u5019\uff0c\u4f60\u662f\u6709\u4e00\u4f4d\u670b\u53cb\u540c\u5728\u4e00\u8d77\u7684\u5417\uff1f\u201d \u3000\u3000\u6768\u5a49\u5fc3\u5ff5\u4e00\u52a8\uff0c\u60f3\u9053\uff1a\u201c\u8fd9\u4eba\u5012\u4f3c\u4e2a\u6709\u8840\u6027\u7684\u6c49\u5b50\uff0c\u6211\u53c8\u4e8e\u4ed6\u6709\u6069\uff0c\u60f3\u6765\u4ed6\u4e0d\u81f3\u4e8e\u5bb3 \u6211\u3002\u6211\u4f55\u4e0d\u5411\u4ed6\u6253\u542c\u6253\u542c\u5357\u54e5\u7684\u6d88\u606f\uff0c\u662f\u6b7b\u662f\u751f\uff0c\u8bf4\u4e0d\u5b9a\u4ed6\u53ef\u80fd\u77e5\u9053\u3002\u201d \u3000\u3000\u6768\u5a49\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u61c2\u5f97\u6c49\u8bed\u5417\uff1f\u201d \u3000\u3000\u963f\u76d6\u70b9\u4e86\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u8bf4\u5f97\u4e0d\u597d\u3002\u201d\u2026\u2026\u8a00\u4e0b\u4e4b\u6025\uff0c\u81f3\u5c11\u4ed6\u662f\u542c\u5f97\u61c2\u7684\u4e86\u3002 \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u8bf7\u4f60\u6452\u9000\u5de6\u53f3\u3002\u201d \u3000\u3000\u963f\u76d6\u4f1a\u610f\uff0c\u6325\u4e86\u6325\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u5230\u6811\u6797\u91cc\u642d\u4e2a\u5e10\u7bf7\u3002\u201d\u9a6c\u7ae5\u95ee\u9053\uff1a\u201c\u4eca\u665a\u4e0d\u8d70\u4e86 \u5417\uff1f\u201d\u963f\u76d6\u9053\uff1a\u201c\u5f85\u4f1a\u513f\u518d\u8d70\u3002\u642d\u597d\u4e86\u4f60\u4eec\u6765\u544a\u8bc9\u6211\u3002\u597d\uff0c\u90fd\u53bb\u5427\u3002\u201d \u3000\u3000\u963f\u76d6\u7684\u624b\u4e0b\u9000\u4e0b\u4e4b\u540e\uff0c\u963f\u76d6\u8bf4\u9053\uff1a\u201c\u8fd9\u4e9b\u4eba\u90fd\u662f\u548c\u6211\u540c\u65f6\u6295\u519b\u7684\u540c\u4e00\u65cf\u4eba\uff0c\u90fd\u662f\u4e0e\u6211\u4ece \u5c0f\u4e00\u540c\u957f\u5927\u7684\u3002\u5176\u5b9e\u59d1\u5a18\u5927\u53ef\u653e\u5fc3\uff0c\u5373\u4f7f\u4ed6\u4eec\u5728\u65c1\uff0c\u4f60\u8bf4\u4e9b\u4ec0\u4e48\u79d8\u5bc6\uff0c\u4ed6\u4eec\u4e5f\u51b3\u4e0d\u4f1a\u6cc4\u6f0f\u51fa \u53bb\u3002\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u4e0d\u662f\u6211\u4e0d\u653e\u5fc3\uff0c\u53ea\u56e0\u6211\u8fd9\u4f4d\u670b\u53cb\u7684\u540d\u5b57\u662f\u4e0d\u4fbf\u5bf9\u4eba\u8bf4\u7684\u3002\u201d \u3000\u3000\u963f\u76d6\u9053\uff1a\u201c\u53ef\u4ee5\u544a\u8bc9\u6211\u4e48\uff1f\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u4f60\u662f\u5361\u6d1b\u4e1d\u7684\u672a\u5a5a\u592b\uff0c\u544a\u8bc9\u4f60\u81ea\u662f\u65e0\u59a8\u3002\u4ed6\u53eb\u505a\u674e\u601d\u5357\uff0c\u4f60\u53ef\u542c\u8fc7\u8fd9\u4e2a\u540d \u5b57\uff1f\u201d \u3000\u3000\u963f\u76d6\u7528\u8499\u53e4\u8bdd\u548c\u6c49\u8bed\u628a\u201c\u674e\u601d\u5357\u201d\u8fd9\u4e2a\u540d\u5b57\u5ff5\u4e86\u4e24\u904d\uff0c\u84e6\u5730\u5927\u5403\u4e00\u60ca\uff0c\u5939\u58f0\u53eb\u9053\uff1a\u201c\u674e \u601d\u5357\uff1f\u554a\u5440\uff0c\u6562\u60c5\u4f60\u8fd9\u4f4d\u670b\u53cb\u5c31\u662f\u6211\u4eec\u7684\u5143\u5e05\u6240\u8981\u641c\u6355\u7684\u4eba\u4e86\u3002 \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5c82\u53ea\u4f60\u4eec\u7684\u5143\u5e05\u8981\u641c\u6355\u4ed6\uff0c\u4f60\u4eec\u7684\u5927\u6c57\u4e5f\u8981\u6349\u62ff\u4ed6\u5462\uff01\u4f60\u6015\u4e0d\u6015\uff1f\u201d \u3000\u3000\u963f\u76d6\u4e00\u62cd\u80f8\u819b\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u6551\u4e86\u6211\u7684\u5361\u6d1b\u4e1d\uff0c\u6211\u5c31\u662f\u8d54\u4e86\u6027\u547d\u4e5f\u4e0d\u8db3\u62a5\u7b54\u4f60\u7684\u5927\u6069\u3002\u4f60 \u6709\u4ec0\u4e48\u8981\u6211\u5e2e\u5fd9\u7684\u5c3d\u7ba1\u8bf4\uff0c\u6211\u51b3\u4e0d\u6015\u53d7\u7d2f\u3002\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u6211\u4e0d\u8981\u4f60\u5e2e\u4ec0\u4e48\u5fd9\uff0c\u53ea\u662f\u60f3\u6253\u542c\u4ed6\u7684\u6d88\u606f\u3002\u201d \u3000\u3000\u963f\u76d6\u9053\uff1a\u201c\u6211\u53ea\u77e5\u9053\u6211\u4eec\u7684\u5143\u5e05\u53eb\u753b\u5e08\u753b\u4e86\u4ed6\u7684\u56fe\u50cf\uff0c\u4f20\u4ee4\u5404\u8425\uff0c\u8bf4\u662f\u8981\u6349\u62ff\u6b64\u4eba\u3002\u6b64 \u5916\uff0c\u6211\u5c31\u4e0d\u77e5\u4ec0\u4e48\u6d88\u606f\u4e86\u3002\u201d \u3000\u3000\u6768\u5a49\u518d\u95ee\uff1a\u201c\u542c\u8bf4\u4ed6\u5df2\u7ed9\u4f60\u4eec\u56fd\u4e2d\u6700\u51fa\u540d\u7684\u795e\u7bad\u624b\u54f2\u522b\u5c06\u519b\u4e00\u7bad\u5c04\u6b7b\uff0c\u8fd9\u6837\u91cd\u5927\u7684\u6d88 \u606f\uff0c\u4f60\u90fd\u4e0d\u77e5\u9053\u5417\uff1f\u201d \u3000\u3000\u963f\u76d6\u9053\uff1a\u201c\u54e6\uff0c\u6709\u8fd9\u6837\u7684\u4e8b\uff1f\u6211\u53ef\u6ca1\u6709\u542c\u4eba\u8bf4\u8fc7\uff01\u4e0d\u8fc7\uff0c\u6050\u6015\u8fd9\u4e2a\u6d88\u606f\u662f\u5047\u7684\u5427\uff1f\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u4f55\u4ee5\u89c1\u5f97\uff1f\u201d \u3000\u3000\u963f\u76d6\u9053\uff1a\u201c\u82e5\u662f\u771f\u7684\uff0c\u5143\u5e05\u5c31\u8be5\u901a\u4ee4\u5404\u8425\u603b\u5175\uff0c\u53d6\u6d88\u8fd9\u4e00\u547d\u4ee4\uff0c\u514d\u5f97\u5c06\u58eb\u767d\u8d39\u7cbe\u795e\u3002\u4f46 \u76f4\u5230\u73b0\u5728\u6211\u4eec\u90fd\u6ca1\u6709\u63a5\u5230\u8fd9\u6837\u7684\u901a\u544a\u3002\u201d \u3000\u3000\u6768\u5a49\u559c\u51fa\u671b\u5916\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u6837\u8bf4\u6765\uff0c\u5357\u54e5\u6216\u8bb8\u8fd8\u771f\u7684\u6d3b\u5728\u4eba\u95f4\u3002\u201d \u3000\u3000\u6768\u5a49\u6b63\u8981\u544a\u8f9e\uff0c\u963f\u76d6\u5ffd\u9053\uff1a\u201c\u6768\u59d1\u5a18\uff0c\u4f60\u4e0d\u80fd\u8d70\uff01\u201d \u3000\u3000\u6768\u5a49\u6014\u4e86\u4e00\u6014\uff0c\u95ee\u9053\uff1a\u201c\u6211\u8fd8\u6ca1\u6709\u544a\u8bc9\u4f60\u6211\u7684\u59d3\u540d\uff0c\u4f60\u600e\u4e48\u5c31\u77e5\u9053\u4e86\uff1f\u201d \u3000\u3000\u963f\u76d6\u9053\uff1a\u201c\u6211\u6b63\u8981\u544a\u8bc9\u4f60\uff0c\u5728\u6211\u4eec\u7684\u5143\u5e05\u5206\u53d1\u4e86\u674e\u601d\u5357\u7684\u56fe\u50cf\u4e4b\u540e\uff0c\u795e\u7ffc\u8425\u7684\u6728\u534e\u9ece\u5c06 \u519b\u53c8\u9001\u6765\u4e00\u4e2a\u5c11\u5973\u7684\u753b\u50cf\uff0c\u5143\u5e05\u53eb\u753b\u5e08\u590d\u753b\u4e86\u8bb8\u591a\u5f20\uff0c\u6211\u4eec\u5de6\u8425\u7684\u603b\u5175\u4e5f\u5f97\u5230\u4e00\u5f20\uff0c\u6211\u66fe\u7ecf \u89c1\u8fc7\u3002\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u54e6\uff0c\u90a3\u5c11\u5973\u60f3\u6765\u5c31\u662f\u6211\u4e86\uff1f\u201d \u3000\u3000\u963f\u76d6\u9053\uff1a\u201c\u4e0d\u9519\u3002\u603b\u5175\u8bf4\uff0c\u5143\u5e05\u544a\u8bc9\u4ed6\uff0c\u8fd9\u4f4d\u59d1\u5a18\u59d3\u6768\u540d\u5a49\uff0c\u662f\u548c\u674e\u601d\u5357\u4e00\u8d77\u7684\uff0c\u53eb\u6211 \u4eec\u4e00\u5e76\u7559\u795e\u3002\u201d\u539f\u6765\u963f\u76d6\u4e00\u89c1\u4e86\u6768\u5a49\uff0c\u5c31\u89c9\u5f97\u9762\u8c8c\u597d\u719f\uff0c\u4f46\u76f4\u5230\u6768\u5a49\u8bf4\u51fa\u4e86\u674e\u601d\u5357\u7684\u540d\u5b57\u2014 \u2014\u4ed6\u624d\u60f3\u5f97\u8d77\u6765\u3002 \u3000\u3000\u963f\u76d6\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u5b9e\u4e0d\u76f8\u7792\uff0c\u6211\u4eec\u662f\u7ed9\u5927\u519b\u6253\u524d\u7ad9\u7684\uff0c\u5927\u519b\u660e\u5929\u5c31\u4f1a\u4ece\u8fd9\u6761\u8def\u6765\uff0c\u4f60\u82e5 \u662f\u7ee7\u7eed\u5411\u524d\u884c\uff0c\u5b9a\u5c06\u9047\u4e0a\u6218\u4e8b\uff01\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u90a3\u4e5f\u53ea\u6709\u542c\u5929\u7531\u547d\u4e86\u3002\u201d \u3000\u3000\u963f\u76d6\u9053\uff1a\u201c\u59d1\u5a18\u82e5\u662f\u9047\u4e0a\u91d1\u56fd\u7684\u6e83\u519b\uff0c\u867d\u7136\u5371\u9669\uff0c\u4e5f\u8fd8\u597d\u4e9b\uff0c\u5018\u82e5\u9047\u4e0a\u89c1\u8fc7\u4f60\u7684\u56fe\u50cf\u7684 \u6211\u4eec\u7684\u519b\u5b98\uff0c\u90a3\u662f\u51b3\u4e0d\u4f1a\u628a\u4f60\u653e\u8fc7\u7684\u3002\u6211\u4eec\u7684\u9a91\u5175\u884c\u52a8\u8fc5\u901f\uff0c\u59d1\u5a18\uff0c\u4f60\u5411\u524d\u8d70\uff0c\u4e0d\u51fa\u4e24\u5929\uff0c \u4e00\u5b9a\u4f1a\u7ed9\u53d1\u73b0\u3002\u6211\u4e0d\u9047\u89c1\u4f60\u4fbf\u7f62\uff0c\u65e2\u7136\u76f8\u9047\uff0c\u6211\u600e\u80fd\u8ba9\u4f60\u542c\u5929\u7531\u547d\uff1f\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u90a3\u53c8\u5982\u4f55\uff1f\u201d \u3000\u3000\u963f\u76d6\u9053\uff1a\u201c\u6211\u5012\u6709\u4e2a\u4e3b\u610f\uff0c\u4e0d\u77e5\u59d1\u5a18\u80af\u4e0d\u80af\u4f9d\u4ece\uff1f\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u4f60\u5148\u8bf4\u6765\u542c\u542c\uff01\u201d \u3000\u3000\u963f\u76d6\u9053\uff1a\u201c\u4f60\u5bb6\u4f4f\u4f55\u5904\uff1f\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u84df\u5dde\u7684\u4e00\u4e2a\u4e61\u4e0b\u3002\u201d \u3000\u3000\u963f\u76d6\u9053\uff1a\u201c\u84df\u5dde\uff0c\u90a3\u662f\u79bb\u5927\u90fd\u4e0d\u592a\u8fdc\u7684\u5730\u65b9\u5427\uff1f\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u4e5f\u6709\u56db\u4e94\u767e\u91cc\u3002\u201d \u3000\u3000\u963f\u76d6\u9053\uff1a\u201c\u90a3\u5c31\u4fbf\u5f53\u4e86\uff0c\u6211\u4eec\u8981\u4ece\u6cb3\u5357\u8fdb\u5175\uff0c\u653b\u53d6\u5927\u90fd\uff0c\u6b63\u8981\u7ecf\u8fc7\u8d35\u4e61\u3002\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u90a3\u53c8\u600e\u6837\uff1f\u201d \u3000\u3000\u963f\u76d6\u9053\uff1a\u201c\u6211\u60f3\u8bf7\u59d1\u5a18\u6682\u53d7\u59d4\u5c48\uff0c\u5145\u5f53\u6211\u7684\u9a6c\u7ae5\u3002\u5973\u626e\u7537\u88c5\uff0c\u6df7\u5728\u6211\u7684\u8425\u4e2d\u3002\u5230\u4e86\u4f60\u7684 \u4e61\u4e0b\uff0c\u90a3\u65f6\u4f60\u518d\u5077\u5077\u56de\u53bb\u3002\u8981\u6349\u62ff\u4f60\u7684\u4eba\uff0c\u51b3\u4e0d\u4f1a\u60f3\u5230\u4f60\u8eb2\u5728\u519b\u4e2d\u7684\u3002\u8fd9\u6837\uff0c\u770b\u6765\u5371\u9669\uff0c\u5176 \u5b9e\u5374\u662f\u5e73\u5b89\u5f97\u591a \u3000\u3000\u6768\u5a49\u5fc3\u60f3\uff1a\u201c\u6211\u4e00\u4e2a\u5973\u5b50\uff0c\u600e\u80fd\u548c\u58eb\u5175\u6df7\u5728\u4e00\u8d77\uff1f\u201d\u8e0c\u8e87\u7247\u523b\uff0c\u8bf4\u9053\uff1a\u201c\u6050\u6015\u4e0d\u65b9\u4fbf \u5427\u3002\u800c\u4e14\u53ea\u6015\u4e5f\u7792\u4e0d\u8fc7\u4f17\u4eba\u8033\u76ee\uff0c\u8981\u662f\u6cc4\u9732\u51fa\u53bb\uff0c\u6211\u4e0d\u6253\u7d27\uff0c\u56e0\u4e3a\u6211\u65e9\u5df2\u628a\u751f\u6b7b\u7f6e\u4e4b\u5ea6\u5916\u7684 \u4e86\uff0c\u4f46\u5374\u96be\u514d\u8fde\u7d2f\u4e86\u5c06\u519b\u3002\u201d \u3000\u3000\u963f\u76d6\u7b11\u9053\uff1a\u201c\u7b2c\u4e00\uff0c\u6211\u4e0d\u662f\u4ec0\u4e48\u5c06\u519b\u3002\u6211\u53ea\u662f\u5c0f\u5c0f\u7684\u4e00\u4e2a\u767e\u592b\u957f\uff0c\u5c06\u519b\u4eec\u662f\u4e0d\u4f1a\u7ea4\u5c0a\u964d \u8d35\u6765\u5230\u6211\u7684\u8425\u4e2d\u5de1\u67e5\u7684\uff1b\u7b2c\u4e8c\uff0c\u6211\u624b\u4e0b\u7684\u4eba\u90fd\u662f\u548c\u6211\u540c\u4e00\u65cf\u7684\u670b\u53cb\uff0c\u5c24\u5176\u662f\u8fd9\u5341\u4e2a\u4eba\u66f4\u662f\u5982 \u540c\u5144\u5f1f\u4e00\u822c\uff0c\u6211\u5bf9\u4ed6\u4eec\u4ea4\u4ee3\u8fc7\u540e\uff0c\u4ed6\u4eec\u51b3\u4e0d\u4f1a\u6cc4\u9732\u51fa\u53bb\uff1b\u7b2c\u4e09\uff0c\u6211\u4eec\u4f4f\u7684\u662f\u5e10\u5e55\uff0c\u6211\u8ba9\u4f60\u5355 \u72ec\u4e00\u4e2a\u5e10\u5e55\uff0c\u5c3d\u91cf\u7ed9\u4f60\u65b9\u4fbf\u3002\u6768\u59d1\u5a18\uff0c\u6709\u6211\u7ed9\u4f60\u62c5\u4fdd\uff0c\u4f60\u4e5f\u5e94\u8be5\u76f8\u4fe1\u5f97\u8fc7\u6211\u7684\u624b\u4e0b\u4e0d\u4f1a\u9a9a\u6270 \u4f60\u5427\uff1f\u201d \u3000\u3000\u6768\u5a49\u542c\u4ed6\u4e3a\u81ea\u5df1\u8bbe\u60f3\u5f97\u8fd9\u6837\u5468\u5230\uff0c\u5f88\u662f\u611f\u6fc0\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5f53\u7136\u4fe1\u5f97\u8fc7\u4f60\u3002\u4e0d\u8fc7\uff0c\u88c5\u675f\u53ef \u6539\uff0c\u6211\u7684\u76f8\u8c8c\u2014\u2014\u201d \u3000\u3000\u963f\u76d6\u9053\uff1a\u201c\u8981\u6539\u53d8\u76f8\u8c8c\uff0c\u53c8\u6709\u4f55\u96be\uff1f\u53ea\u8981\u4f60\u613f\u610f\u5fcd\u53d7\u59d4\u5c48\uff0c\u6211\u7acb\u5373\u6559\u4f60\u6539\u5bb9\u6613\u8c8c\u7684\u6cd5 \u5b50\u3002\u201d \u3000\u3000\u6768\u5a49\u542c\u4ed6\u8bf4\u5f97\u8fd9\u6837\u80af\u5b9a\uff0c\u4e0d\u7981\u52a8\u4e86\u597d\u5947\u4e4b\u5fc3\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u5148\u8ba9\u6211\u8bd5\u8bd5\uff0c\u770b\u770b\u662f\u5426\u771f\u7684\u80fd \u591f\u6539\u5bb9\u6613\u8c8c\uff1f\u201d \u3000\u3000\u963f\u76d6\u9053\uff1a\u201c\u4f60\u811a\u8fb9\u7684\u8fd9\u4e9b\u9752\u8349\uff0c\u5c31\u662f\u6539\u5bb9\u6613\u8c8c\u7684\u5999\u836f\uff0c\u4f60\u62d4\u4e00\u628a\uff0c\u5c06\u5b83\u56bc\u70c2\uff0c\u628a\u8349\u6c41\u6d82 \u5728\u9762\u4e0a\uff0c\u518d\u548c\u4e00\u70b9\u70c2\u6ce5\u6577\u4e00\u904d\uff0c\u7136\u540e\u5c0f\u5fc3\u7684\u62b9\u6389\u5b83\u3002\u4f60\u8bd5\u8bd5\u770b\u3002\u201d \u3000\u3000\u6768\u5a49\u4f9d\u4ed6\u6240\u6559\uff0c\u6d82\u4e0a\u8349\u6c41\u70c2\u6ce5\u4e4b\u540e\uff0c\u5230\u6cb3\u8fb9\u4e00\u77a7\uff0c\u53ea\u89c1\u6c34\u4e2d\u6620\u51fa\u4e00\u4e2a\u9752\u7d2b\u8272\u7684\u9762\u5e9e\uff0c\u679c \u7136\u662f\u5148\u540e\u5224\u82e5\u4e24\u4eba\uff0c\u5fc3\u60f3\uff1a\u201c\u6211\u518d\u6362\u4e0a\u9a6c\u7ae5\u7684\u8863\u88f3\uff0c\u53ea\u6015\u5c31\u662f\u5357\u54e5\uff0c\u4e5f\u672a\u5fc5\u8ba4\u5f97\u6211\u4e86\u3002 \u3000\u3000\u963f\u76d6\u8bf4\u9053\uff1a\u201c\u6211\u662f\u4e00\u4e2a\u730e\u4eba\uff0c\u957f\u5e74\u7d2f\u6708\u5728\u6df1\u5c71\u4e4b\u4e2d\uff0c\u5bf9\u8fd9\u4e9b\u8349\u836f\u5f88\u662f\u719f\u6089\uff0c\u5c0f\u65f6\u5019\u6211\u5c31 \u5e38\u5e38\u7528\u8fd9\u79cd\u9752\u8349\u6539\u4e86\u9762\u8c8c\uff0c\u626e\u9b3c\u5413\u4eba\u7684\u3002\u6070\u5de7\u8fd9\u91cc\u5c31\u6709\u8fd9\u79cd\u9752\u8349\uff0c\u8fd9\u6b63\u662f\u5929\u52a9\u4f60\u4e86\u3002\u4f60\u53ef\u4ee5 \u591a\u91c7\u4e00\u4e9b\u5907\u7528\u3002\u201d \u3000\u3000\u6768\u5a49\u9ed8\u4e0d\u4f5c\u58f0\uff0c\u963f\u76d6\u53c8\u9053\uff1a\u201c\u600e\u4e48\u6837\u4e86\uff1f\u4f60\u770b\u8fd8\u6709\u4ec0\u4e48\u7834\u7efd\u6ca1\u6709\uff1f\u4e3b\u610f\u53ef\u4ee5\u62ff\u5b9a\u4e86 \u5427\uff1f\u201d \u3000\u3000\u6768\u5a49\u5fc3\u4e2d\u4e00\u52a8\uff0c\u60f3\u8d77\u4e00\u4e8b\uff0c\u95ee\u9053\uff1a\u201c\u542c\u8bf4\u4f60\u4eec\u8fd9\u4e00\u8def\u7684\u526f\u5143\u5e05\u662f\u4e2a\u6c49\u4eba\uff0c\u662f\u5417\uff1f\u201d \u3000\u3000\u963f\u76d6\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u4eba\u540d\u53eb\u674e\u5e0c\u6d69\uff0c\u5f88\u5f97\u5927\u6c57\u7684\u4fe1\u4efb\uff0c\u6545\u800c\u7834\u4f8b\u63d0\u62d4\u4ed6\u505a\u4e86\u8fd9\u4e00\u8def\u7684\u526f \u5143\u5e05\u3002\u6768\u59d1\u5a18\uff0c\u4f60\u53ef\u662f\u8bf4\u5f97\u6b64\u4eba\uff1f\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u4e0d\u8ba4\u8bc6\u3002\u53ea\u56e0\u4ed6\u662f\u6c49\u4eba\uff0c\u6c49\u4eba\u5f53\u4f60\u4eec\u7684\u5927\u5b98\u7684\u6781\u4e3a\u5c11\u6709\uff0c\u6545\u6b64\u95ee\u95ee\uff0c\u4ed6\u6765\u4e86 \u6ca1\u6709\uff1f\u201d \u3000\u3000\u963f\u76d6\u9053\uff1a\u201c\u4ed6\u548c\u5143\u5e05\u540c\u5728\u4e2d\u519b\uff0c\u6765\u662f\u4e00\u5b9a\u8981\u6765\u7684\uff0c\u4f46\u8981\u7a0d\u8fdf\u51e0\u5929\u3002\u201d\u4ed6\u4fe1\u4e86\u6768\u5a49\u7684\u8bdd\uff0c \u53ea\u9053\u6768\u5a49\u56e0\u4e3a\u540c\u662f\u6c49\u4eba\u7684\u5173\u7cfb\uff0c\u6545\u6b64\u597d\u5947\u591a\u95ee\u3002\u5374\u4e0d\u77e5\u9053\u8fd9\u5192\u540d\u201c\u674e\u5e0c\u6d69\u201d\u771f\u540d\u4f59\u4e00\u4e2d\u7684\u5bb6 \u4f19\uff0c\u6b63\u662f\u674e\u601d\u5357\u548c\u6768\u5a49\u7684\u5927\u4ec7\u4eba\uff0c\u674e\u601d\u5357\u7684\u7236\u4eb2\u56fa\u7136\u662f\u7ed9\u4ed6\u5bb3\u6b7b\uff0c\u6768\u5a49\u7684\u54e5\u54e5\u4e5f\u53ef\u4ee5\u8bf4\u662f\u95f4 \u63a5\u6b7b\u5728\u4ed6\u7684\u624b\u4e0a\u3002 \u3000\u3000\u6768\u5a49\u6697\u81ea\u601d\u91cf\uff1a\u201c\u5357\u54e5\u6b7b\u751f\u672a\u535c\uff0c\u4f46\u4e0d\u7ba1\u4ed6\u662f\u6b7b\u662f\u751f\uff0c\u4ed6\u7684\u5927\u4ec7\u6211\u603b\u662f\u8981\u66ff\u4ed6\u62a5\u7684\u3002\u6211 \u82e5\u6df7\u5728\u8499\u53e4\u519b\u4e2d\uff0c\u5012\u662f\u4e00\u4e2a\u53ef\u4ee5\u523a\u6740\u4f59\u4e00\u4e2d\u7684\u673a\u4f1a\u3002\u201d \u3000\u3000\u6768\u5a49\u6253\u5b9a\u4e86\u4e3b\u610f\uff0c\u4fbf\u4e0e\u963f\u76d6\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u4f60\u7ed9\u6211\u60f3\u5f97\u8fd9\u6837\u5468\u5230\u3002\u6211\u4e5f\u53ea\u6709\u501a\u9760\u4f60\u7684\u7167\u987e \u4e86\u3002\u4e0d\u8fc7\uff0c\u6211\u8fd8\u6709\u4e24\u4e2a\u8981\u6c42\uff0c\u8981\u8bf7\u4f60\u8c05\u89e3\u7684\u3002\u201d \u3000\u3000\u963f\u76d6\u9053\uff1a\u201c\u4f60\u662f\u5361\u6d1b\u4e1d\u7684\u6069\u4eba\uff0c\u4e5f\u5c31\u662f\u6211\u7684\u6069\u4eba\u3002\u4f60\u6709\u4ec0\u4e48\u8bf4\u8bdd\uff0c\u5c3d\u7ba1\u5429\u5490\u597d\u4e86\u3002\u7528\u4e0d \u7740\u5ba2\u6c14\u3002\u201d \u3000\u3000\u6768\u5a49\u8bf4\u9053\uff1a\u201c\u7b2c\u4e00\u4e2a\u8981\u6c42\uff0c\u4f60\u8981\u8ba9\u6211\u6765\u53bb\u81ea\u4e3b\u3002\u6211\u53ef\u80fd\u968f\u65f6\u79bb\u8425\u800c\u53bb\uff0c\u4e0d\u4e00\u5b9a\u7b49\u5230\u56de\u81f3 \u5bb6\u4e61\u3002\u201d \u3000\u3000\u963f\u76d6\u9053\uff1a\u201c\u4f46\u6c42\u4f60\u80fd\u591f\u5e73\u5b89\u8131\u9669\uff0c\u4f60\u89c9\u5f97\u4ec0\u4e48\u65f6\u5019\u79bb\u5f00\u6700\u597d\uff0c\u6211\u90fd\u53ef\u4ee5\u7ed9\u4f60\u5e2e\u5fd9\u3002\u201d \u3000\u3000\u6768\u5a49\u518d\u9053\uff1a\u201c\u7b2c\u4e8c\u4e2a\u8981\u6c42\uff0c\u4f60\u4eec\u5018\u82e5\u662f\u548c\u91d1\u519b\u4ea4\u6218\uff0c\u6211\u53ef\u4ee5\u968f\u4f60\u4e0a\u9635\uff1b\u4f60\u4eec\u82e5\u662f\u6740\u622e\u767e \u59d3\uff0c\u6211\u53ef\u4e0d\u80fd\u4f9d\u4ece\u3002\u201d \u3000\u3000\u963f\u76d6\u9053\uff1a\u201c\u5361\u6d1b\u4e1d\u4e5f\u66fe\u548c\u6211\u8bf4\u8fc7\u8fd9\u6837\u7684\u8bdd\u3002\u4e0d\u7792\u4f60\u8bf4\uff0c\u6211\u5176\u5b9e\u662f\u8ba8\u538c\u8fd9\u573a\u6218\u4e89\uff0c\u5018\u82e5\u4e0d \u662f\u987e\u53ca\u8499\u53e4\u6b66\u58eb\u7684\u8363\u8a89\uff0c\u6211\u65e9\u5c31\u60f3\u9003\u56de\u5bb6\u53bb\u4e0e\u5361\u6d1b\u4e1d\u76f8\u805a\u4e86\u3002\u4f60\u653e\u5fc3\uff0c\u6211\u51b3\u4e0d\u4f1a\u6740\u5bb3\u65e0\u8f9c\u7684 \u8001\u767e\u59d3\uff0c\u5373\u4f7f\u519b\u4ee4\u96be\u8fdd\uff0c\u8981\u6211\u53bb\u641c\u7d22\u6c11\u5c45\u7684\u8bdd\uff0c\u6211\u4e5f\u53ea\u6709\u6577\u884d\u8fc7\u53bb\uff0c\u5b81\u52ab\u8d22\u7269\uff0c\u4e5f\u4e0d\u4f24 \u4eba\u3002\u201d \u3000\u3000\u6768\u5a49\u7b11\u9053\uff1a\u201c\u4f60\u5012\u8bf4\u5f97\u5f88\u662f\u5766\u7387\u3002\u90a3\u4e48\uff0c\u6211\u4e5f\u6709\u8a00\u5728\u5148\uff0c\u4f60\u4eec\u6253\u7684\u867d\u662f\u91d1\u56fd\uff0c\u4f46\u91d1\u56fd\u7684 \u5927\u90e8\u5206\u5730\u65b9\uff0c\u672c\u6765\u662f\u6211\u4eec\u7684\uff0c\u5c45\u4f4f\u7684\u8001\u767e\u59d3\u4e5f\u5927\u534a\u662f\u6c49\u4eba\u3002\u6211\u82e5\u9003\u4e86\u56de\u53bb\uff0c\u8bf4\u4e0d\u5b9a\u6709\u4e00\u65e5\u4f1a \u548c\u4f60\u5728\u6c99\u573a\u76f8\u89c1\uff0c\u4f60\u6211\u53d8\u4f5c\u654c\u4eba\u3002\u201d \u3000\u3000\u963f\u76d6\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u4fb5\u5360\u522b\u56fd\u5730\u65b9\uff0c\u6b3a\u4fae\u522b\u56fd\u767e\u59d3\uff0c\u8fd9\u672c\u6765\u662f\u6211\u4eec\u7406\u4e8f\uff0c\u4f46\u5927\u6c57\u6709 \u547d\uff0c\u6211\u4eec\u4e5f\u662f\u8eab\u4e0d\u7531\u4e3b\uff0c\u53ea\u6709\u542c\u4ed6\u5dee\u9063\u4e86\u3002\u6211\u552f\u6c42\u62a5\u7b54\u4f60\u7684\u6069\u60c5\uff0c\u5c06\u6765\u5c31\u662f\u6b7b\u5728\u4f60\u7684\u624b\u4e0a\uff0c \u6211\u4e5f\u51b3\u4e0d\u6028\u4f60\u3002\u201d \u3000\u3000\u6768\u5a49\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4ed6\u867d\u7136\u8fd8\u6709\u4e00\u4e9b\u7cca\u6d82\uff0c\u4f46\u5728\u8499\u53e4\u4eba\u4e2d\uff0c\u603b\u7b97\u5f97\u662f\u4e2a\u6bd4\u8f83\u660e\u767d\u4e8b\u7406\u7684 \u4e86\u3002\u8981\u4e00\u4e2a\u4eba\u5927\u5f7b\u5927\u609f\uff0c\u5e76\u4e0d\u662f\u4e00\u4ef6\u5bb9\u6613\u7684\u4e8b\u60c5\uff0c\u6025\u4e5f\u6025\u4e0d\u6765\u7684\u3002\u6211\u53ea\u80fd\u5c06\u6765\u6709\u673a\u4f1a\u518d\u529d\u529d \u4ed6\u4e86\u3002\u201d \u3000\u3000\u7ecf\u8fc7\u4e86\u8fd9\u573a\u8c08\u8bdd\uff0c\u5f7c\u6b64\u90fd\u80fd\u8c05\u89e3\uff0c\u6768\u5a49\u542c\u53d6\u4e86\u963f\u76d6\u66ff\u5979\u7684\u5b89\u6392\u3002\u963f\u76d6\u5439\u54cd\u53f7\u89d2\uff0c\u628a\u4ed6\u7684 \u624b\u4e0b\u62db\u6765\uff0c\u6b64\u65f6\u5df2\u662f\u5c06\u8fd1\u5929\u660e\u7684\u65f6\u5206\uff0c\u963f\u76d6\u8981\u4ed6\u4eec\u642d\u7684\u5e10\u7bf7\u4e5f\u65e9\u5df2\u642d\u597d\u3002 \u3000\u3000\u4ed6\u4eec\u770b\u89c1\u6768\u5a49\u76f8\u8c8c\u6539\u53d8\uff0c\u90fd\u5f88\u8be7\u5f02\u3002\u963f\u76d6\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u6768\u59d1\u5a18\u662f\u5361\u6d1b\u4e1d\u7684\u6069\u4eba\uff0c\u5979\u72ec\u81ea \u56de\u4e61\uff0c\u6050\u6015\u8def\u4e0d\u597d\u8d70\u3002\u56e0\u6b64\uff0c\u6211\u60f3\u8ba9\u5979\u5973\u626e\u7537\u88c5\uff0c\u7559\u5728\u519b\u4e2d\uff0c\u4f60\u4eec\u53ef\u4e0d\u8bb8\u6cc4\u6f0f\u51fa\u53bb\u3002\u201d \u3000\u3000\u8fd9\u4e9b\u4eba\u548c\u5361\u6d1b\u4e1d\u4e5f\u90fd\u662f\u76f8\u719f\u7684\uff0c\u5f02\u53e3\u540c\u58f0\u8bf4\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u54b1\u4eec\u7406\u5e94\u62a5\u7b54\u3002\u201d\u6709\u7684\u8fd8 \u9053\uff1a\u201c\u6768\u59d1\u5a18\u6b66\u827a\u9ad8\u5f3a\uff0c\u5018\u80fd\u7559\u5728\u519b\u4e2d\uff0c\u6559\u6211\u4eec\u51e0\u62db\u672c\u9886\uff0c\u6b63\u662f\u6c42\u4e4b\u4e0d\u5f97\uff01\u201d\u8499\u53e4\u6c11\u98ce\u5c1a \u6b66\uff0c\u6700\u4f69\u670d\u6709\u672c\u9886\u7684\u4eba\u3002\u6768\u5a49\u4ee5\u4e00\u4e2a\u5e74\u7eaa\u8f7b\u8f7b\u7684\u5973\u5b50\uff0c\u521a\u624d\u63a5\u8fde\u6253\u8d25\u4e86\u4ed6\u4eec\u51e0\u4e2a\u4eba\uff0c\u4ed6\u4eec\u65e9 \u5df2\u4f69\u670d\u5f97\u4e94\u4f53\u6295\u5730\u3002 \u3000\u3000\u963f\u76d6\u7684\u9a6c\u7ae5\u5e74\u65b9\u5341\u4e94\uff0c\u4f46\u9aa8\u683c\u7c97\u58ee\uff0c\u957f\u5f97\u6bd4\u6768\u5a49\u8fd8\u7a0d\u5fae\u9ad8\u4e9b\u3002\u963f\u76d6\u53eb\u4ed6\u62ff\u51fa\u4e00\u5957\u8863\u88f3\u9001 \u7ed9\u6768\u5a49\uff0c\u7b11\u9053\uff1a\u201c\u4ece\u4eca\u4e4b\u540e\uff0c\u4f60\u4eec\u5e94\u8be5\u53eb\u5979\u505a\u6768\u5927\u54e5\uff0c\u53ef\u4e0d\u80fd\u518d\u53eb\u6768\u59d1\u5a18\u3002\u6768\u5927\u54e5\uff0c\u8bf7\u4f60\u5230 \u5e10\u7bf7\u91cc\u66f4\u8863\uff0c\u770b\u770b\u5408\u4e0d\u5408\u8eab\u3002\u5176\u4ed6\u4e00\u4e9b\u7ec6\u8282\uff0c\u6211\u4f1a\u548c\u4ed6\u4eec\u8bf4\u7684\u3002\u201d \u3000\u3000\u6768\u5a49\u770b\u5f97\u51fa\u8fd9\u4e9b\u4eba\u90fd\u662f\u6206\u76f4\u6c49\u5b50\uff0c\u800c\u4e14\u662f\u771f\u5fc3\u5730\u4f69\u670d\u5979\uff0c\u5fc3\u91cc\u5f88\u662f\u9ad8\u5174\uff0c\u60f3\u9053\uff1a\u201c\u963f\u76d6 \u8bf4\u5f97\u4e0d\u9519\uff0c\u8499\u53e4\u4eba\u91cc\u9762\u4e5f\u6709\u597d\u7684\u3002\u5176\u5b9e\u8fd9\u4e9b\u58eb\u5175\u4e5f\u5927\u90fd\u662f\u7a77\u82e6\u7684\u8001\u767e\u59d3\uff0c\u4ed6\u4eec\u5e76\u4e0d\u60f3\u8981\u6253 \u4ed7\uff0c\u7a77\u5175\u9ee9\u6b66\u7684\u53ea\u662f\u4ed6\u4eec\u7684\u9996\u9886\u3002\u201d\u53c8\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u6211\u5728\u8fd9\u91cc\u627e\u5f97\u4e2a\u5b89\u8eab\u4e4b\u5730\uff0c\u4f46\u4e0d\u77e5\u5357\u5927 \u54e5\u5982\u4eca\u5374\u662f\u6d41\u843d\u4f55\u65b9\uff1f\u201d\u4ece\u963f\u76d6\u7684\u53e3\u4e2d\u5df2\u7ecf\u8bc1\u660e\u4e86\u675c\u96c4\u90a3\u53ae\u8bf4\u7684\u4e43\u662f\u5047\u8bdd\uff0c\u90a3\u4e48\uff0c\u5357\u54e5\u60f3\u5fc5 \u662f\u5e94\u8be5\u8fd8\u6d3b\u5728\u4eba\u95f4\uff01\u201d \u3000\u3000\u6768\u5a49\u54ea\u91cc\u77e5\u9053\uff0c\u5c31\u5728\u6b64\u523b\u601d\u5ff5\u674e\u601d\u5357\u4e4b\u9645\uff0c\u674e\u601d\u5357\u6b63\u5728\u90a3\u4e2a\u5c0f\u9547\u4e0a\u627e\u5979\u3002 \u3000\u3000\u82b1\u5f00\u4e24\u6735\uff0c\u5404\u8868\u4e00\u679d\u3002\u6768\u5a49\u5728\u963f\u76d6\u7684\u8425\u4e2d\u65e2\u7136\u6682\u65f6\u627e\u5230\u4e86\u5b89\u8eab\u4e4b\u5730\uff0c\u6309\u4e0b\u4e0d\u8868\u3002\u4e14\u8bf4\u674e \u601d\u5357\u4ec6\u4ec6\u98ce\u5c18\uff0c\u5386\u5c3d\u8270\u96be\u9669\u963b\uff0c\u597d\u4e0d\u5bb9\u6613\u8eb2\u8fc7\u4e86\u654c\u4eba\u7684\u641c\u7d22\u8ffd\u8e2a\uff0c\u6e9c\u8fc7\u4e86\u897f\u590f\u7684\u8fb9\u754c\uff0c\u7ec8\u4e8e \u4e5f\u6765\u5230\u4e86\u8fd9\u4e2a\u8fb9\u5883\u7684\u5c0f\u9547\u3002 \u3000\u3000\u6765\u5230\u4e86\u8fd9\u4e2a\u6218\u706b\u5c1a\u672a\u6ce2\u53ca\u7684\u5730\u65b9\uff0c\u674e\u601d\u5357\u8981\u505a\u7684\u7b2c\u4e00\u4ef6\u4e8b\uff0c\u5c31\u662f\u6253\u542c\u6768\u5a49\u7684\u6d88\u606f\u3002\u8fd9\u4e2a \u5c0f\u9547\u662f\u4ece\u897f\u590f\u9003\u6765\u7684\u96be\u6c11\u5fc5\u7ecf\u4e4b\u5730\uff0c\u674e\u601d\u5357\u5fc3\u60f3\uff0c\u5018\u82e5\u6768\u5a49\u90a3\u65e5\u80fd\u591f\u4fa5\u5e78\u9003\u751f\uff0c\u8bf4\u4e0d\u5b9a\u5c31\u53ef \u4ee5\u5728\u8fd9\u4e2a\u5c0f\u9547\u6253\u542c\u5230\u5979\u7684\u6d88\u606f\u3002 \u3000\u3000\u4e8e\u662f\u674e\u601d\u5357\u5728\u7406\u4e86\u4e2a\u53d1\uff0c\u6362\u4e86\u4e00\u4ef6\u65b0\u8863\u4e4b\u540e\uff0c\u4fbf\u5f00\u59cb\u8fdb\u884c\u67e5\u8bbf\uff0c\u4ed6\u77e5\u9053\u4eba\u60c5\u52bf\u5229\uff0c\u662f\u4ee5 \u5fc5\u987b\u6253\u626e\u5f97\u50cf\u4e2a\u9614\u7ef0\u7684\u5bcc\u5bb6\u5b50\u5f1f\uff0c\u624d\u4e0d\u81f3\u906d\u4eba\u767d\u773c\uff0c\u4fbf\u4e8e\u8bbf\u67e5\u3002 \u3000\u3000\u674e\u601d\u5357\u731c\u5f97\u4e0d\u9519\uff0c\u8fd9\u95f4\u5ba2\u5e97\u6b63\u662f\u6768\u5a49\u548c\u5c60\u9f99\u6628\u665a\u6295\u5bbf\u7684\u90a3\u95f4\u5ba2\u5e97\uff0c\u674e\u601d\u5357\u6765\u5230\u7684\u65f6\u5019\uff0c \u5df2\u662f\u7b2c\u4e8c\u5929\u7684\u4e0a\u5348\uff0c\u5c60\u9f99\u8fd8\u672a\u56de\u6765\u3002\u5e97\u4e3b\u4eba\u6b63\u5728\u4e3a\u6628\u665a\u95f9\u98de\u8d3c\u4e4b\u4e8b\u5fe1\u5fe1\u4e0d\u5b89\uff0c\u4f46\u89c1\u674e\u601d\u5357\u4e00 \u6d3e\u8d35\u5bb6\u516c\u5b50\u7684\u6c14\u6d3e\uff0c\u53ea\u597d\u6253\u70b9\u7cbe\u795e\uff0c\u4e0a\u524d\u63a5\u5f85\u3002 \u3000\u3000\u674e\u601d\u5357\u5927\u6a21\u5927\u6837\u5730\u8bf4\u9053\uff1a\u201c\u6211\u8981\u4e00\u95f4\u4e0a\u623f\uff0c\u94f6\u5b50\u6211\u4e0d\u5728\u4e4e\u3002\u591a\u8d35\u4e5f\u8981\u3002\u201d\u65c1\u8fb9\u4e00\u4e2a\u4f19\u8ba1 \u8bc9\u51a4\u9053\uff1a\u201c\u6211\u5df2\u7ecf\u544a\u8bc9\u8fd9\u4f4d\u76f8\u516c\uff0c\u8bf4\u662f\u6ca1\u6709\u623f\u95f4\uff0c\u4ed6\u4e0d\u76f8\u4fe1\uff0c\u8001\u677f\u4f60\u548c\u4ed6\u8bf4\u5427\u3002\u201d\u674e\u601d\u5357\u6de1 \u6de1\u8bf4\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u5012\u662f\u6211\u4e0d\u8be5\u60ca\u52a8\u4f60\u4eec\u7684\u8001\u677f\u4e86\u3002\u201d \u3000\u3000\u5e97\u4e3b\u4eba\u662f\u4e2a\u89c1\u94b1\u773c\u5f00\u7684\u5bb6\u4f19\uff0c\u8fde\u5fd9\u8bf4\u9053\uff1a\u201c\u54ea\u91cc\u7684\u8bdd\uff0c\u8d35\u5ba2\u4e0a\u95e8\uff0c\u6211\u662f\u7406\u8be5\u62db\u5f85\u7684\u3002\u5c0f \u4e8c\uff0c\u5012\u8336\u3002\u76f8\u516c\uff0c\u8bf7\u4f60\u5148\u5750\u4e0b\u6765\uff0c\u54b1\u4eec\u6162\u6162\u5546\u91cf\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u6709\u5c31\u6709\uff0c\u6ca1\u6709\u5c31\u6ca1\u6709\uff0c\u8fd8\u8981\u5546\u91cf\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5ba2\u5e97\u7684\u4e00\u822c\u89c4\u77e9\uff0c\u8fc7\u4e86\u4e2d\u5348\u5c31\u7b97\u4e00\u5929\u3002\u5c60\u9f99\u6628\u665a\u8981\u7684\u90a3\u95f4\u623f\u95f4\uff0c\u867d\u7136\u8bf4\u660e\u4e86\u662f\u53ea\u4f4f\u4e00\u665a \u7684\uff0c\u4f46\u4ed6\u6ca1\u6709\u56de\u6765\u9000\u623f\uff0c\u4f9d\u7167\u89c4\u77e9\uff0c\u5fc5\u987b\u8fc7\u4e86\u6b63\u5348\uff0c\u65b9\u80fd\u79df\u7ed9\u522b\u4eba\u3002 \u3000\u3000\u5e97\u4e3b\u4eba\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u4e24\u5144\u59b9\u534a\u591c\u4e09\u66f4\u4ece\u7a97\u5b50\u8df3\u51fa\u53bb\uff0c\u73b0\u5728\u8fd8\u672a\u56de\u6765\uff0c\u53ea\u6015\u5f53\u771f\u662f\u4e00\u5bf9 \u98de\u8d3c\u3002\u201d\u60f3\u8d77\u98de\u8d3c\u7684\u53ef\u6015\uff0c\u4e0d\u7531\u5f97\u5fc3\u91cc\u4e0d\u614c\u3002\u53ef\u662f\u4ed6\u53c8\u820d\u4e0d\u5f97\u63a8\u6389\u4e0a\u95e8\u7684\u8d22\u795e\u3002 \u3000\u3000\u5e97\u4e3b\u4eba\u6697\u81ea\u601d\u91cf\uff1a\u201c\u53ea\u6709\u4e00\u4e2a\u65f6\u8fb0\u5c31\u8fc7\u4e2d\u5348\u4e86\uff0c\u4ed6\u4eec\u51fa\u53bb\u4e86\u8fd9\u6837\u4e45\u4e0d\u89c1\u8e2a\u5f71\uff0c\u672a\u5fc5\u5c31\u6709 \u90a3\u4e48\u5de7\uff1f\u4e2d\u5348\u4e4b\u524d\u8d76\u56de\u6765\u5427\u3002\u597d\uff0c\u8fd9\u4e2a\u9669\u6211\u5012\u4e0d\u59a8\u5192\u4e00\u5192\u5b83\u3002\u201d\u4e3b\u610f\u6253\u5b9a\uff0c\u4e8e\u662f\u8bf4\u9053\uff1a\u201c\u5c0f \u5e97\u59d4\u5b9e\u662f\u6ca1\u6709\u623f\u95f4\uff0c\u4e0d\u8fc7\u6709\u4e24\u4f4d\u5ba2\u4eba\u662f\u51c6\u5907\u4eca\u5929\u8d70\u7684\uff0c\u6240\u4ee5\u3001\u6240\u4ee5\uff0c\u6211\u8bf4\u8fd8\u6709\u5546\u91cf\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u8be7\u9053\uff1a\u201c\u90a3\u8fd8\u6709\u4ec0\u4e48\u95ee\u9898\uff0c\u5c31\u8981\u4ed6\u4eec\u7559\u4e0b\u7684\u8fd9\u95f4\u7a7a\u623f\u597d\u3002\u201d \u3000\u3000\u5e97\u4e3b\u4eba\u541e\u541e\u5410\u5410\u5730\u8bf4\u9053\uff1a\u201c\u4f46\u73b0\u5728\u8fd8\u4e0d\u662f\u2018\u7a7a\u623f\u2019\u3002\u662f\u3001\u662f\u8fd9\u6837\u7684\uff0c\u4ed6\u4eec\u5df2\u7ecf\u51fa\u53bb\u4e86\uff0c \u73b0\u5728\u5c1a\u672a\u56de\u6765\u3002\u4f9d\u7167\u89c4\u77e9\uff0c\u987b\u5f97\u8fc7\u4e86\u4e2d\u5348\u65b9\u53ef\u51fa\u79df\u3002\u800c\u4e14\u5047\u5982\u4ed6\u4eec\u6539\u53d8\u8fdb\u610f\uff0c\u8981\u7ee7\u7eed\u4f4f\u4e0b\u53bb \u7684\u8bdd\uff0c\u6211\u4eec\u4e5f\u5f97\u5148\u8ba9\u4ed6\u4f4f\u3002\u53ef\u662f\u6211\u53c8\u4e0d\u77e5\u9053\u4ed6\u4eec\u4ec0\u516c\u65f6\u5019\u56de\u6765\u3002\u201d\u5e97\u4e3b\u4eba\u6709\u6240\u987e\u5fcc\uff0c\u53ea\u8bf4\u4ed6 \u4eec\u201c\u5df2\u7ecf\u201d\u51fa\u53bb\uff0c\u4e0d\u6562\u8bf4\u51fa\u4ed6\u4eec\u662f\u534a\u591c\u51fa\u53bb\u7684\u3002 \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u8fd9\u4e2a\u597d\u529e\u3002\u4ed6\u4ed8\u4e86\u591a\u5c11\u623f\u94b1\uff0c\u6211\u53cc\u500d\u8d54\u7ed9\u4f60\u3002\u53e6\u5916\uff0c\u6211\u4f4f\u4e00\u665a\uff0c\u4e5f\u662f\u53cc\u500d \u4ed8\u4f60\u623f\u94b1\u3002\u5373\u4f7f\u4ed6\u56de\u6765\u8fd8\u8981\u623f\u95f4\uff0c\u4f60\u53cc\u500d\u8d54\u94b1\uff0c\u4ed6\u4e5f\u8be5\u6ca1\u8bdd\u8bf4\u4e86\u3002\u4ed6\u623f\u4e2d\u82e5\u6709\u884c\u674e\uff0c\u4f60\u53ef\u4ee5 \u7ed9\u4ed6\u6682\u65f6\u4fdd\u7ba1\u3002\u201d \u3000\u3000\u5e97\u4e3b\u4eba\u9053\uff1a\u201c\u90a3\u7537\u5ba2\u4eba\u5f88\u51f6\uff0c\u4ed6\u82e5\u662f\u56de\u6765\u8d23\u602a\uff0c\u6211\u53ef\u662f\u6709\u70b9\u5bb3\u6015\u62c5\u5f53\u4e0d\u8d77\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u7b11\u9053\uff1a\u201c\u60f3\u6765\u4ed6\u4eec\u4e5f\u4e0d\u81f3\u4e8e\u7559\u4e0b\u4ec0\u4e48\u8d35\u91cd\u7684\u4e1c\u897f\u5728\u65e0\u4eba\u770b\u5b88\u7684\u623f\u95f4\u3002\u4e0d\u8fc7\uff0c\u4f60\u65e2 \u7136\u6015\u62c5\u5f53\u4e0d\u8d77\uff0c\u6211\u4e5f\u4e0d\u59a8\u591a\u7b49\u4e00\u4e2a\u65f6\u8fb0\u3002\u8fc7\u4e86\u4e2d\u5348\uff0c\u4ed6\u4eec\u82e5\u662f\u8fd8\u4e0d\u56de\u6765\uff0c\u4f60\u5c31\u53ef\u4ee5\u540d\u6b63\u8a00\u987a \u7684\u628a\u4ed6\u4eec\u7684\u4e1c\u897f\u5c01\u5b58\u8d77\u6765\u4e86\u3002\u201d\u674e\u601d\u5357\u7684\u4e3b\u8981\u76ee\u7684\u662f\u6253\u542c\u6d88\u606f\uff0c\u6709\u6ca1\u6709\u623f\u95f4\u4f4f\u5012\u5728\u5176\u6b21\uff0c\u4e50 \u5f97\u8d81\u8fd9\u7a7a\u6687\u4e0e\u5e97\u4e3b\u95f2\u804a\u3002 \u3000\u3000\u674e\u601d\u5357\u7b11\u9053\uff1a\u201c\u5927\u5bb6\u90fd\u4e0d\u613f\u610f\u6709\u6218\u4e8b\u53d1\u751f\uff0c\u552f\u6709\u4f60\u4eec\u5f00\u5ba2\u5e97\u7684\u5012\u662f\u53ef\u4ee5\u4e58\u673a\u53d1\u4e2a\u5c0f\u8d22\u3002 \u8fd9\u6b21\u9003\u96be\u7684\u4eba\u5f88\u591a\uff0c\u4f60\u4eec\u8fd9\u95f4\u5ba2\u5e97\u5f53\u771f\u662f\u5ba2\u4f3c\u4e91\u6765\u4e86\uff01\u201d \u3000\u3000\u5e97\u4e3b\u4eba\u9053\uff1a\u201c\u6258\u8d56\uff0c\u6258\u8d56\uff0c\u751f\u610f\u8fd8\u8fc7\u5f97\u53bb\u3002\u4f46\u9003\u51fa\u6765\u7684\u96be\u6c11\uff0c\u4e5f\u90fd\u662f\u7a77\u7684\u591a\u3002\u50cf\u4f60\u8fd9\u6837 \u6709\u94b1\u7684\u5927\u7237\uff0c\u5341\u4e2d\u6ca1\u6709\u4e00\u4e8c\u3002\u800c\u4e14\u6211\u4eec\u4e5f\u4e0d\u77e5\u9053\u4ec0\u4e48\u65f6\u5019\u6218\u7978\u4f1a\u8513\u5ef6\u5230\u8fd9\u513f\u6765\uff0c\u4e0d\u8fc7\u662f\u505a\u4e00 \u5929\u751f\u610f\u7b97\u4e00\u5929\u7f62\u4e86\u3002\u8fd8\u662f\u592a\u5e73\u7684\u597d\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u4f60\u8bf4\u5f97\u662f\u3002\u6211\u597d\u5728\u662f\u5355\u8eab\u7537\u5b50\uff0c\u8eab\u4f53\u4e5f\u8fd8\u5f3a\u58ee\uff0c\u9003\u96be\u6bd4\u8f83\u5bb9\u6613\u3002\u4e00\u8def\u4e0a\u6211 \u770b\u89c1\u8bb8\u591a\u8001\u5f31\u5987\u5b7a\uff0c\u6d41\u5b70\u9053\u5de6\uff0c\u90a3\u624d\u771f\u662f\u53eb\u51c4\u60e8\u5462\u3002\u5bf9\u5566\uff0c\u8bf4\u5230\u8fd9\u91cc\uff0c\u6211\u5012\u60f3\u95ee\u95ee\u4f60\u4e86\uff0c\u4f60 \u8fd9\u95f4\u5ba2\u5e97\u53ef\u5e38\u6709\u9003\u96be\u6765\u7684\u5973\u5b50\u6295\u5bbf\u4e48\uff1f\u201d \u3000\u3000\u5e97\u4e3b\u4eba\u8bef\u4f1a\u4e86\u674e\u601d\u5357\u7684\u610f\u601d\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u5c0f\u5e97\u5973\u5ba2\u6765\u6295\u5bbf\u7684\u4e0d\u591a\uff0c\u6765\u6295\u5bbf\u4e5f\u90fd\u662f\u6709\u4e3b \u513f\u7684\u3002\u4e0d\u8fc7\uff0c\u4f60\u82e5\u662f\u60f3\u4e70\u4e2a\u4eba\u670d\u4f8d\uff0c\u6211\u5012\u53ef\u4ee5\u66ff\u4f60\u8bbe\u6cd5\u3002\u6709\u597d\u4e9b\u7a77\u4eba\u5bb6\u7684\u5973\u513f\uff0c\u9003\u96be\u5230\u9547 \u4e0a\uff0c\u6ca1\u5730\u65b9\u4f4f\uff0c\u6ca1\u94b1\u4e70\u4e1c\u897f\u5403\uff0c\u53ea\u8981\u4f60\u5582\u9971\u5979\uff0c\u5979\u5c31\u4e56\u4e56\u5730\u8ddf\u4f60\u4e86\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u4e3a\u4e4b\u6115\u7136\uff0c\u8fde\u5fd9\u6447\u624b\u8bf4\u9053\uff1a\u201c\u4e0d\uff0c\u4e0d\u3002\u6211\u4e0d\u662f\u8fd9\u4e2a\u610f\u601d\uff0c\u53ea\u662f\u60f3\u5411\u4f60\u6253\u542c\u4e00\u4e2a \u4eba\u3002\u201d \u3000\u3000\u5e97\u4e3b\u4eba\u9053\u3002\u201c\u4ec0\u4e48\u4eba\uff1f\u66fe\u7ecf\u4f4f\u8fc7\u5c0f\u5e97\u7684\u5973\u5ba2\u4e0d\u591a\uff0c\u4f60\u8bf4\u8bf4\u770b\uff0c\u8981\u662f\u6211\u89c1\u8fc7\u7684\uff0c\u6216\u8bb8\u8fd8\u4f1a \u8bb0\u5f97\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u662f\u4e00\u4e2a\u4e8c\u5341\u5c81\u5de6\u53f3\u7684\u5355\u8eab\u5973\u5b50\u3002\u201d\u5f53\u4e0b\u8be6\u7ec6\u7684\u63cf\u7ed8\u4e86\u6768\u5a49\u7684\u5bb9\u8c8c\u7279\u5f81\u3002\u5e97 \u4e3b\u4eba\u542c\u4e86\uff0c\u5927\u5403\u4e00\u60ca\uff0c\u6c89\u541f\u4e0d\u8bed\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u539f\u6765\u4ed6\u662f\u548c\u90a3\u5973\u98de\u8d3c\u719f\u8bc6\u7684\uff0c\u5982\u4eca\u53c8\u6070\u5de7\u662f \u79df\u4e86\u90a3\u5973\u98de\u8d3c\u6628\u665a\u6240\u4f4f\u7684\u623f\u95f4\u3002\u544a\u4e0d\u544a\u8bc9\u4ed6\u5462\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u62ff\u51fa\u4e00\u952d\u5341\u4e24\u91cd\u7684\u7eb9\u94f6\uff0c\u9012\u4e86\u8fc7\u53bb\uff0c\u8bf4\u9053\uff1a\u201c\u4e00\u70b9\u70b9\u5c0f\u610f\u601d\uff0c\u7ed9\u4f60\u5f53\u8336\u8d44\u3002\u201d \u3000\u3000\u5e97\u4e3b\u4eba\u60f3\u8981\u53c8\u4e0d\u6562\u8981\uff0c\u652f\u543e\u8bf4\u9053\uff1a\u201c\u6211\u597d\u50cf\u6ca1\u6709\u89c1\u8fc7\u8fd9\u6837\u7684\u5973\u5b50\uff0c\u539a\u8d50\u6211\u53ef\u4e0d\u6562\u53d7\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u4f60\u60f3\u60f3\u770b\uff0c\u5c31\u662f\u4f60\u4e0d\u77e5\u9053\uff0c\u9001\u70b9\u8336\u8d44\u6211\u4e5f\u8fd8\u662f\u8981\u7ed9\u4f60\u7684\u3002\u201d \u3000\u3000\u5e97\u4e3b\u4eba\u7231\u8d22\u5982\u547d\uff0c\u5fc3\u91cc\u867d\u7136\u6709\u70b9\u5bb3\u6015\uff0c\u53cc\u624b\u5374\u628a\u94f6\u5b50\u63a5\u4e86\u8fc7\u53bb\uff0c\u53e3\u4e2d\u5219\u6545\u4f5c\u63a8\u8f9e\uff1a\u201c\u8336 \u8d44\u4e5f\u7528\u4e0d\u4e86\u8fd9\u8bb8\u591a\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u7b11\u9053\uff1a\u201c\u4f60\u82e5\u662f\u613f\u610f\u548c\u6211\u4ea4\u4e2a\u670b\u53cb\u7684\u8bdd\uff0c\u8fd9\u4e00\u676f\u8336\u5c31\u4e0d\u6b62\u503c\u5341\u4e24\u94f6\u5b50\u4e86\uff01\u201d\u8fb9\u8bf4 \u8fb9\u628a\u8336\u676f\u65cb\u8f6c\uff0c\u8bf4\u5b8c\u4e4b\u540e\uff0c\u5c06\u8336\u676f\u7aef\u8d77\u6765\u559d\uff0c\u53ea\u89c1\u8336\u51e0\u4e0a\u7559\u4e0b\u4e00\u4e2a\u51f9\u9677\u7684\u676f\u75d5\uff01 \u3000\u3000\u5e97\u4e3b\u4eba\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u4ed6\u4e5f\u662f\u4e2a\u8001\u6c5f\u6e56\u4e86\uff0c\u5f53\u7136\u542c\u5f97\u61c2\u674e\u601d\u5357\u7684\u8bdd\uff0c\u674e\u601d\u5357\u5b9e\u662f\u5728\u5411 \u4ed6\u6697\u793a\uff1a\u201c\u4f60\u82e5\u662f\u591f\u670b\u53cb\u7684\u8bdd\uff0c\u6211\u53ef\u4ee5\u7ed9\u4f60\u66f4\u591a\u7684\u94b1\uff1b\u4f60\u82e5\u662f\u4e0d\u591f\u670b\u53cb\uff0c\u5c06\u6d88\u606f\u7792\u4f4f\uff0c\u7ed9\u6211 \u77e5\u9053\uff01\u53ef\u4e0d\u8981\u602a\u6211\u4e0d\u5ba2\u6c14\u4e86\u3002\u201d \u3000\u3000\u5e97\u4e3b\u4eba\u5728\u674e\u601d\u5357\u7684\u5a01\u80c1\u5229\u8bf1\u4e4b\u4e0b\uff0c\u6697\u81ea\u601d\u9053\uff1a\u201c\u8fd9\u4eba\u6216\u8bb8\u662f\u98de\u8d3c\u4e00\u4f19\uff0c\u6216\u8bb8\u662f\u6765\u6349\u62ff\u98de \u8d3c\u7684\uff0c\u4f46\u770b\u6765\u5bf9\u6211\u5374\u662f\u6ca1\u6709\u6076\u610f\u3002\u201d\u53c8\u60f3\uff1a\u201c\u4ed6\u7684\u529f\u592b\u8fd9\u6837\u597d\uff0c\u4e0d\u7ba1\u4ed6\u548c\u98de\u8d3c\u662f\u53cb\u662f\u654c\uff0c\u5373 \u4f7f\u98de\u8d3c\u56de\u6765\uff0c\u6211\u4e5f\u53ef\u4ee5\u63a8\u7ed9\u4ed6\u53bb\u5bf9\u4ed8\uff0c\u5bf9\u5566\uff0c\u6211\u8fd8\u662f\u8bc6\u76f8\u4e00\u70b9\u7684\u597d\uff0c\u514d\u5f97\u5f53\u771f\u53d8\u6210\u4e86\u656c\u9152\u4e0d \u5403\u5403\u7f5a\u9152\u3002\u201d\u5e97\u4e3b\u4eba\u76d8\u7b97\u597d\u4e86\uff0c\u88c5\u4f5c\u7a81\u7136\u60f3\u8d77\u7684\u6837\u5b50\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u662f\u6709\u8fd9\u4e48\u4e00\u4e2a\u5973\u5b50\u66fe \u7ecf\u5230\u6b64\u6295\u5bbf\u3002\u4e0d\u8fc7\u5979\u5374\u5e76\u975e\u5355\u8eab\uff0c\u4f60\u521a\u624d\u8bf4\u5979\u662f\u5355\u8eab\u5973\u5b50\uff0c\u6211\u4e00\u65f6\u5012\u60f3\u4e0d\u8d77\u4e86\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u6014\u4e86\u4e00\u6014\uff0c\u8bf4\u9053\uff1a\u201c\u54e6\uff0c\u4e0d\u662f\u5355\u8eab\uff1f\u90a3\u4e48\uff0c\u5979\u662f\u548c\u8c01\u4e00\u9053\u6765\u7684\uff1f\u201d \u3000\u3000\u5e97\u4e3b\u4eba\u9053\uff1a\u201c\u548c\u4e00\u4e2a\u5c11\u5e74\u7537\u5b50\uff0c\u5979\u8bf4\u662f\u5979\u7684\u54e5\u54e5\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u66f4\u4e3a\u8be7\u5f02\u9053\uff1a\u201c\u90a3\u4eba\u662f\u4ec0\u4e48\u76f8\u8c8c\uff0c\u5979\u771f\u7684\u662f\u8ba4\u90a3\u4eba\u4f5c\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5e97\u4e3b\u4eba\u8bd5\u63a2\u9053\uff1a\u201c\u4f60\u5927\u7ea6\u77e5\u9053\u5979\u7684\u8eab\u4e16\u5427\uff1f\u6211\u5374\u4e0d\u77e5\u9053\u5979\u6709\u6ca1\u6709\u54e5\u54e5\uff0c\u53ea\u662f\u5979\u8fd9\u6837\u8bf4\uff0c \u6211\u4eec\u5f53\u7136\u4e5f\u53ea\u80fd\u76f8\u4fe1\u5979\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u5979\u7684\u54e5\u54e5\u65e9\u5df2\u6b7b\u4e86\u3002\u201d \u3000\u3000\u5e97\u4e3b\u4eba\u70b9\u4e86\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u5144\u59b9\u7684\u76f8\u8c8c\u5168\u4e0d\u76f8\u4f3c\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u5fc3\u6025\u5982\u711a\uff0c\u8fde\u5fd9\u95ee\u9053\uff1a\u201c\u4ed6\u4eec\u8fd8\u5728\u8fd9\u91cc\u4e0d\u5728\uff1f\u201d \u3000\u3000\u5e97\u4e3b\u4eba\u8fd9\u624d\u8bf4\u9053\uff1a\u201c\u4f60\u521a\u624d\u79df\u7684\u8fd9\u95f4\u623f\uff0c\u6b63\u5c31\u662f\u4ed6\u4eec\u6628\u665a\u4f4f\u7684\u90a3\u4e00\u95f4\u623f\u3002\u5b9e\u4e0d\u76f8\u7792\uff0c\u4ed6 \u4eec\u662f\u6628\u665a\u51fa\u53bb\u7684\uff0c\u73b0\u5728\u8fd8\u672a\u56de\u6765\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u770b\u7a97\u5929\u8272\uff0c\u65e5\u5934\u6b63\u5728\u5f53\u4e2d\uff0c\u8bf4\u9053\uff1a\u201c\u73b0\u5728\u5df1\u662f\u5348\u65f6\uff0c\u597d\uff0c\u4f60\u53ef\u4ee5\u548c\u627e\u8fdb\u90a3\u95f4\u623f\u95f4 \u4e86\u3002\u201d \u3000\u3000\u5c60\u9f99\u3001\u6768\u5a49\u6628\u665a\u51fa\u53bb\u4e4b\u540e\uff0c\u5e97\u4e3b\u4eba\u4e0d\u6562\u79c1\u81ea\u8fdb\u53bb\uff0c\u623f\u4e2d\u8fd8\u662f\u4fdd\u6301\u539f\u72b6\u3002\u674e\u601d\u5357\u63a8\u5f00\u4e86\u623f \u95e8\uff0c\u4e00\u773c\u5c31\u770b\u89c1\u6ee1\u684c\u7684\u9152\u83dc\uff0c\u4e0d\u89c9\u51b7\u7b11\u9053\uff1a\u201c\u4ed6\u4eec\u5012\u662f\u61c2\u5f97\u4eab\u4e50\u5440\u3002\u201d \u3000\u3000\u5e97\u4e3b\u4eba\u9053\uff1a\u201c\u90a3\u4e2a\u7537\u7684\u4e00\u8fdb\u6765\u5c31\u53eb\u627e\u7ed9\u4ed6\u51c6\u5907\u9152\u5e2d\u3002\u4e0d\u8fc7\uff0c\u9152\u5374\u662f\u4ed6\u81ea\u5df1\u4e70\u6765\u7684\u3002\u201d \u3000\u3000\u5e97\u4e3b\u4eba\u5de1\u89c6\u4e00\u904d\uff0c\u8bf4\u9053\uff1a\u201c\u9152\u676f\u6253\u70c2\u4e86\u4e00\u53ea\u3002\u54a6\uff0c\u6211\u7684\u5927\u82b1\u63cf\u600e\u4e48\u7761\u5728\u8fd9\u513f\uff1f\u201d\u539f\u6765\u90a3 \u53ea\u732b\u5403\u4e86\u6cbe\u6709\u836f\u9152\u7684\u4e00\u5757\u9c7c\u8089\uff0c\u5140\u81ea\u660f\u8ff7\u672a\u9192\u3002 \u3000\u3000\u674e\u601d\u5357\u7684\u76ee\u5149\u843d\u5728\u5e8a\u4e0a\u7684\u4e00\u4e2a\u5305\u88b1\u4e0a\uff0c\u4ed6\u8ba4\u5f97\u8fd9\u662f\u6768\u5a49\u6240\u5e26\u7684\u5305\u88b1\uff0c\u5728\u68b3\u5986\u53f0\u4e0a\uff0c\u94dc\u955c \u65c1\u8fb9\uff0c\u674e\u601d\u5357\u53c8\u53d1\u73b0\u6768\u5a49\u7528\u5269\u7684\u80ed\u8102\u6c34\u7c89\uff0c\u674e\u601d\u5357\u4e0d\u7981\u6ee1\u8179\u7591\u4e91\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u5979\u4e0e\u6211\u60e8\u906d \u751f\u79bb\u6b7b\u522b\u4e4b\u75db\uff0c\u5979\u600e\u7684\u8fd8\u6709\u5fc3\u601d\u6253\u626e\uff1f\u5509\uff0c\u5973\u4e3a\u60a6\u5df1\u8005\u5bb9\uff0c\u83ab\u975e\u5979\u771f\u662f\u5df2\u7ecf\u53d8\u5fc3\u4e86\uff1f\u201d \u3000\u3000\u5e97\u4e3b\u4eba\u9053\uff1a\u201c\u6211\u53eb\u4eba\u9a6c\u4e0a\u7ed9\u4f60\u6536\u62fe\u8fd9\u95f4\u623f\u95f4\uff0c\u597d\u5417\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u6b63\u81ea\u4e0d\u597d\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u4f60\u8d76\u5feb\u53eb\u4eba\u642c\u5f00\u8fd9\u684c\u6d12\u5e2d\u5427\uff0c\u6211\u53ef\u4e0d\u60f3\u5bf9\u7740\u51b7\u9152\u6b8b \u80b4\uff01\u201d\u6b63\u662f\uff1a \u3000\u3000\u53ea\u56e0\u79bb\u5408\u5bfb\u5e38\u4e8b\uff0c\u51b7\u9152\u6b8b\u80b4\u4fbf\u8d77\u7591\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b\u2014\u2014\u7b2c\u5341\u516d\u56de\u3000\u7834\u955c\u65e7\u8863\u64a9\u5992\u6068\u3000\u6b8b\u80b4\u5269\u9152\u60f9\u7591\u731c \u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b \u7b2c\u5341\u516d\u56de\u3000\u7834\u955c\u65e7\u8863\u64a9\u5992\u6068\u3000\u6b8b\u80b4\u5269\u9152\u60f9\u7591\u731c \u3000\u3000\u53ef\u60dc\u674e\u601d\u5357\u6ca1\u6709\u8bd5\u4e00\u8bd5\u5c1d\u5c1d\u90a3\u4e9b\u51b7\u9152\u6b8b\u80b4\uff0c\u5426\u5219\u53ea\u8981\u9152\u4e00\u6cbe\u5507\uff0c\u4ee5\u4ed6\u5bf9\u4e8e\u836f\u7269\u7684\u77e5\u8bc6\uff0c \u7acb\u5373\u5c31\u53ef\u4ee5\u77e5\u9053\u9152\u4e2d\u662f\u6709\u9ebb\u836f\uff0c\u8bd5\u51fa\u9152\u4e2d\u6709\u836f\uff0c\u5c60\u9f99\u90aa\u6076\u7684\u4f01\u56fe\u4e5f\u5c31\u7792\u4e0d\u8fc7\u4ed6\u4e86\u3002\u800c\u73b0\u5728\u4ed6 \u5374\u5f53\u6768\u5a49\u548c\u90a3\u7537\u5b50\u662f\u996e\u9152\u4f5c\u4e50\u3001\u4e24\u60c5\u76f8\u60a6\u3002 \u3000\u3000\u5e97\u5c0f\u4e8c\u8fdb\u6765\uff0c\u6536\u62fe\u4e86\u9152\u5e2d\uff0c\u8ddf\u7740\u628a\u90a3\u53ea\u9163\u7761\u7684\u732b\u4e5f\u62b1\u51fa\u53bb\u4e86\u3002\u674e\u601d\u5357\u6b63\u5728\u6c14\u5934\u4e0a\uff0c\u5bf9\u8fd9 \u53ea\u6709\u5f02\u5e38\u6001\u7684\u732b\uff0c\u7adf\u7136\u4e5f\u6ca1\u6709\u7559\u610f\u3002 \u3000\u3000\u674e\u601d\u5357\u5bf9\u5e97\u4e3b\u4eba\u8bf4\u9053\uff1a\u201c\u623f\u4e2d\u53ea\u6709\u8fd9\u4e2a\u5305\u88b1\uff0c\u5e76\u65e0\u522b\u7684\u884c\u674e\u3002\u5305\u88b1\u6211\u4e5f\u6478\u8fc7\u4e86\uff0c\u91cc\u9762\u5e76 \u65e0\u94f6\u94b1\u3002\u6211\u60f3\uff0c\u6211\u60f3\u2014\u2014\u8fd9\u4e2a\u5305\u88b1\uff0c\u4f60\u5c31\u8ba9\u6211\u66ff\u4f60\u4fdd\u7ba1\u5427\u3002\u90a3\u5973\u7684\u56de\u6765\uff0c\u6211\u81ea\u4f1a\u4ea4\u7ed9\u5979 \u7684\u3002\u201d \u3000\u3000\u5e97\u4e3b\u4eba\u6068\u4e0d\u5f97\u5b8c\u5168\u63a8\u8131\u5173\u7cfb\uff0c\u8fde\u5fd9\u8bf4\u9053\uff1a\u201c\u8fd9\u6837\u6700\u597d\u4e0d\u8fc7\u3002\u4f60\u8fd8\u6709\u4ec0\u4e48\u8981\u6211\u4f3a\u5019\u5417\uff1f\u201d \u674e\u601d\u5357\u9053\uff1a\u201c\u4e0d\u7528\u4e86\u3002\u4ed6\u4eec\u56de\u6765\u4f60\u518d\u544a\u8bc9\u6211\uff0c\u73b0\u5728\u6211\u8981\u6b47\u4e00\u4f1a\u513f\u3002\u201d \u3000\u3000\u5e97\u4e3b\u4eba\u8d70\u540e\uff0c\u674e\u601d\u5357\u5173\u4e0a\u623f\u95e8\uff0c\u89e3\u5f00\u5305\u88b1\uff0c\u5305\u88b1\u91cc\u6709\u6768\u5a49\u4e24\u5957\u7834\u65e7\u7684\u8863\u88f3\uff0c\u8863\u88f3\u4e0a\u6cbe\u6ee1 \u5c18\u571f\uff0c\u8fd8\u6ca1\u6709\u6d17\u6da4\u7684\u3002\u539f\u6765\u6768\u5a49\u56e0\u4e3a\u8fd9\u4e24\u5957\u7834\u8863\uff0c\u662f\u5979\u548c\u674e\u601d\u5357\u540c\u5728\u4e00\u8d77\u7684\u65f6\u5019\u7a7f\u7740\u7684\uff0c\u6362 \u4e86\u65b0\u8863\uff0c\u4e0d\u5fd8\u65e7\u4fa3\uff0c\u662f\u4ee5\u597d\u597d\u7684\u6536\u85cf\u8d77\u6765\uff0c\u4f5c\u4e3a\u7eaa\u5ff5\u3002 \u3000\u3000\u674e\u601d\u5357\u7779\u7269\u601d\u4eba\uff0c\u4e0d\u7531\u5f97\u5fc3\u9178\u6cea\u54bd\uff0c\u809d\u80a0\u5bf8\u65ad\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u53e4\u4eba\u4e91\uff1a\u8863\u4e0d\u5982\u65b0\uff0c\u4eba\u4e0d \u5982\u6545\u3002\u5509\uff0c\u4f46\u5a49\u59b9\u820d\u4e0d\u5f97\u6545\u8863\uff0c\u5012\u662f\u628a\u6211\u5fd8\u4e86\uff0c\u96be\u9053\u5bf9\u5979\u6765\u8bf4\uff0c\u53cd\u800c\u662f\u4eba\u4e0d\u5982\u65b0\u3001\u8863\u4e0d\u5982\u6545 \u4e48\uff1f\u201d \u3000\u3000\u64a9\u8d77\u5f80\u65e5\u6069\u60c5\uff0c\u8d8a\u60f3\u8d8a\u662f\u96be\u53d7\uff0c\u4e5f\u8d8a\u60f3\u8d8a\u662f\u751f\u6c14\uff0c\u201c\u96be\u9053\u5f80\u65e5\u7684\u6069\u60c5\u90fd\u662f\u5047\u7684\u4e86\uff1f\u751f\u5219 \u540c\u80cc\uff0c\u6b7b\u5219\u540c\u7a74\uff0c\u8fd9\u662f\u5979\u66fe\u5411\u627e\u53d1\u8fc7\u7684\u8a93\u8a00\u3002\u8a00\u72b9\u5728\u8033\uff0c\u96be\u9053\u4e5f\u90fd\u5fd8\u4e86\uff1f\u6211\u4e3a\u5979\u4e0d\u60dc\u8eb2\u5f00\u4e86 \u5b5f\u660e\u971e\uff0c\u8c01\u5982\u5979\u79bb\u5f00\u6211\uff0c\u5374\u7acb\u5373\u5c31\u6709\u4e86\u65b0\u4eba\uff01\u201d \u3000\u3000\u6012\u6c14\u96be\u6d88\u4e86\uff0c\u674e\u601d\u5357\u5fcd\u4e0d\u4f4f\u62d4\u51fa\u5251\u6765\uff0c\u5728\u90a3\u9762\u94dc\u955c\u4e0a\u5de6\u4e00\u9053\u53f3\u4e00\u9053\u5730\u5212\u5f00\u4e86\u4e03\u516b\u9053\u88c2 \u75d5\uff0c\u6068\u6068\u5730\u8bf4\u9053\uff1a\u201c\u7834\u955c\u96be\u5706\uff0c\u597d\uff0c\u7531\u5979\u53bb\u7f62\uff01\u201d \u3000\u3000\u6012\u6c14\u53d1\u6cc4\u4e86\u4e00\u4e9b\uff0c\u674e\u601d\u5357\u5ffd\u5730\u611f\u5230\u6709\u70b9\u60ed\u6127\uff0c\u5fc3\u4e2d\u81ea\u8d23\uff1a\u201c\u5979\u5e76\u6ca1\u6709\u6b20\u4f60\u4ec0\u4e48\uff0c\u5012\u662f\u4f60 \u6b20\u5979\u7684\u592a\u591a\u4e86\u3002\u5979\u66fe\u7ecf\u8863\u4e0d\u89e3\u5e26\u5730\u670d\u4f8d\u4f60\u7684\u7236\u4eb2\uff0c\u5979\u7684\u54e5\u54e5\u56e0\u4f60\u800c\u6b7b\u3002\u4f60\u4fdd\u62a4\u4e0d\u4e86\u5979\uff0c\u5979\u5728 \u8fd9\u4e16\u754c\u4e0a\u5b64\u96f6\u96f6\u7684\u53ea\u6709\u4e00\u4e2a\u4eb2\u4eba\uff0c\u4f60\u53c8\u600e\u80fd\u602a\u5979\u53e6\u5bfb\u9e33\u4fa3\uff1f\u4f60\u548c\u5979\u4e5f\u672a\u6210\u4e3a\u592b\u5987\uff0c\u5979\u53c8\u4e0d\u77e5 \u9053\u4f60\u662f\u6b7b\u662f\u751f\uff0c\u4f60\u53c8\u600e\u80fd\u8d23\u5907\u5730\u4e0d\u4e3a\u4f60\u5b88\u8282\u5462\uff1f\u201d\u53c8\u60f3\uff1a\u201c\u5f53\u521d\u4f60\u5176\u5b9e\u4e5f\u5e76\u4e0d\u662f\u60f3\u548c\u5979\u505a\u592b \u59bb\u7684\uff0c\u4eca\u53c8\u4f55\u5fc5\u4e3a\u5979\u7684\u7435\u7436\u53e6\u62b1\u800c\u6c14\u6124\u96be\u6d88\uff1f\u5c31\u53ea\u5f53\u6ca1\u6709\u8ba4\u8bc6\u5979\u8fd9\u4e2a\u4eba\u5427\u3002\u201d \u3000\u3000\u8bdd\u867d\u5982\u6b64\uff0c\u4f46\u674e\u601d\u5357\u53c8\u600e\u80fd\u5fd8\u6389\u4ed6\u4eec\u540e\u6765\u90a3\u4e00\u6bb5\u76f8\u4f9d\u4e3a\u547d\u7684\u60c5\u8c0a\uff1f \u3000\u3000\u6b63\u81ea\u601d\u6f6e\u8d77\u4f0f\uff0c\u54c0\u4f24\u96be\u4ee5\u81ea\u89e3\u4e4b\u65f6\uff0c\u5ffd\u542c\u5fae\u98ce\u98d2\u7136\uff0c\u674e\u601d\u5357\u62ac\u773c\u4e00\u671b\uff0c\u53ea\u89c1\u4e00\u4e2a\u4eba\u5df2\u7ecf \u4ece\u7a97\u5b50\u8df3\u8fdb\u6765\u4e86\u3002\u8fd9\u4e2a\u4ece\u7a97\u53e3\u8df3\u8fdb\u6765\u7684\u4eba\u6b63\u662f\u5c60\u9f99\u3002 \u3000\u3000\u5c60\u9f99\u662f\u8dd1\u56de\u6765\u627e\u6768\u5a49\u7684\uff0c\u4ed6\u4e0d\u77e5\u6768\u5a49\u6709\u6ca1\u6709\u53d1\u73b0\u836f\u9152\u7684\u79d8\u5bc6\uff0c\u5fc3\u91cc\u8fd8\u5b58\u7740\u4e07\u4e00\u7684\u5e0c\u671b\uff0c \u5e0c\u671b\u6768\u5a49\u6ca1\u6709\u8d70\uff0c\u5728\u7b49\u4ed6\u56de\u6765\u3002 \u3000\u3000\u201c\u4e00\u8def\u4e0a\u6211\u5df2\u628a\u5979\u54c4\u5f97\u670d\u670d\u5e16\u5e16\uff0c\u5373\u4f7f\u5979\u542c\u4e86\u77f3\u749e\u7684\u8bdd\u5bf9\u6211\u8d77\u7591\uff0c\u53ea\u8981\u6211\u5584\u4e3a\u8bf4\u8f9e\uff0c\u4e5f \u8bb8\u5979\u8fd8\u4f1a\u76f8\u4fe1\u6211\u7684\u3002\u201d\u5c60\u9f99\u5fc3\u60f3\u3002\u4f46\u56e0\u6349\u6a21\u4e0d\u51c6\uff0c\u53c8\u5bb3\u6015\u5c60\u51e4\u548c\u77f3\u749e\u8ffd\u6765\uff0c\u6240\u4ee5\u4e0d\u613f\u4ece\u6b63\u95e8 \u8fdb\u6765\uff0c\u60ca\u52a8\u5e97\u4e3b\uff0c\u4ee5\u514d\u803d\u6401\u65f6\u95f4\u3002\u4ed6\u6253\u7b97\u4e00\u56de\u6765\u5c31\u8d70\uff0c\u6768\u5a49\u82e5\u7136\u8fd8\u5728\u623f\u4e2d\u7b49\u4ed6\uff0c\u90a3\u56fa\u7136\u6700 \u597d\uff0c\u4ed6\u53ef\u4ee5\u5e26\u4e86\u6768\u5a49\u9003\u8d70\uff1b\u82e5\u7136\u4e0d\u89c1\u6768\u5a49\uff0c\u4ed6\u4e5f\u5fc5\u987b\u9a6c\u4e0a\u79bb\u5f00\u8fd9\u4e2a\u5c0f\u9547\uff0c\u514d\u5f97\u649e\u4e0a\u5c60\u51e4\u3002 \u3000\u3000\u4e0d\u6599\u63a8\u7a97\u4e00\u671b\uff0c\u4e0d\u89c1\u6768\u5a49\uff0c\u5374\u89c1\u5230\u4e86\u5728\u623f\u4e2d\u53d1\u5446\u7684\u674e\u601d\u5357\u3002 \u3000\u3000\u5c60\u9f99\u4ee5\u524d\u867d\u6ca1\u89c1\u8fc7\u674e\u601d\u5357\uff0c\u4f46\u5374\u89c1\u8fc7\u4ed6\u7684\u56fe\u50cf\u3002\u6b64\u65f6\u5fc3\u4e2d\u53c8\u60ca\u53c8\u559c\uff0c\u4e00\u8df3\u8fdb\u6765\uff0c\u7acb\u5373\u88c5 \u6a21\u4f5c\u6837\u5730\u559d\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff0c\u4e3a\u4ec0\u4e48\u79c1\u81ea\u95ef\u5165\u6211\u7684\u623f\u95f4\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u5b88\u5728\u623f\u4e2d\uff0c\u672c\u6765\u5c31\u662f\u8981\u7b49\u5f85\u4ed6\u548c\u6768\u5a49\u56de\u6765\u7684\uff0c\u6240\u4ee5\u5e76\u4e0d\u611f\u89c9\u610f\u5916\u3002\u4f46\u8fd9\u4e2a\u548c\u6768\u5a49 \u201c\u540c\u623f\u201d\u7684\u7537\u5b50\u7a81\u7136\u95f4\u51fa\u73b0\u5728\u4ed6\u7684\u9762\u524d\uff0c\u4ed6\u8fd8\u662f\u7981\u4e0d\u4f4f\u5fc3\u4e71\u5982\u9ebb\uff0c\u5fc3\u5934\u535c\u901a\u901a\u5730\u8df3\uff0c\u4e00\u65f6\u95f4 \u4e0d\u77e5\u8bf4\u4e9b\u4ec0\u4e48\u624d\u597d\u3002 \u3000\u3000\u5c60\u9f99\u6697\u81ea\u601d\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u662f\u9547\u56fd\u738b\u5b50\u6240\u8981\u7f09\u62ff\u7684\u72af\u4eba\uff0c\u6211\u82e5\u628a\u4ed6\u9664\u4e86\uff0c\u5012\u662f\u4e00\u4e3e\u4e24\u5f97\u3002 \u4e0d\u8fc7\u542c\u8bf4\u5730\u662f\u5c11\u6797\u540d\u5bb6\u8c37\u5e73\u9633\u7684\u5f97\u610f\u5f1f\u5b50\uff0c\u53ea\u6015\u4e0d\u5927\u597d\u60f9\u3002\u201d\u89c1\u674e\u601d\u5357\u597d\u4f3c\u53d1\u4e86\u5446\u4f3c\u7684\uff0c\u53cc \u773c\u7741\u5f97\u53c8\u5706\u53c8\u5927\uff0c\u53ea\u662f\u51b7\u51b7\u5730\u77aa\u770b\u4ed6\uff0c\u5c60\u9f99\u5012\u662f\u4e0d\u7981\u5fc3\u91cc\u6709\u70b9\u53d1\u6bdb\uff0c\u5f53\u4e0b\u6293\u7d27\u5251\u67c4\uff0c\u8fc8\u524d\u4e00 \u6b65\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u804b\u7684\u5417\uff1f\u542c\u4e0d\u89c1\u6211\u7684\u8bf4\u8bdd\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u5b9a\u4e86\u5b9a\u795e\uff0c\u8bf4\u9053\uff1a\u201c\u6768\u5a49\u5462\uff0c\u8bf7\u4f60\u628a\u5979\u53eb\u6765\uff0c\u5979\u81ea\u7136\u4f1a\u77e5\u9053\u6211\u662f\u8c01\uff1f\u201d \u3000\u3000\u5c60\u9f99\u201c\u54fc\u201d\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u597d\u6ca1\u793c\u8c8c\uff0c\u4f60\u662f\u5979\u7684\u4ec0\u4e48\u4eba\uff0c\u51ed\u4ec0\u4e48\u6211\u8981\u8ba9\u4f60\u89c1\u5979\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u5fcd\u4e0d\u4f4f\u9053\uff1a\u201c\u6211\u662f\u4ed6\u7684\u4e08\u592b\uff01\u201d \u3000\u3000\u5c60\u9f99\u5fc3\u60f3\uff1a\u201c\u539f\u6765\u4ed6\u8fd8\u6ca1\u6709\u89c1\u5230\u6768\u5a49\uff0c\u597d\uff0c\u4e14\u5f85\u6211\u6c14\u4ed6\u3002\u201d\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u80e1\u8bf4\u516b\u9053\uff0c\u627e \u548c\u6768\u5a49\u76f8\u8bc6\u7684\u65f6\u5019\uff0c\u5979\u8fd8\u662f\u4e2a\u51b0\u6e05\u7389\u6d01\u7684\u5973\u5b50\uff0c\u54ea\u91cc\u6765\u7684\u4e08\u592b\uff1f\u4f60\u77e5\u9053\u6211\u662f\u8c01\uff0c\u6211\u624d\u662f\u5979\u7684 \u4e08\u592b\uff01\u8fd9\u95f4\u623f\u95f4\uff0c\u5c31\u662f\u6211\u4eec\u6628\u665a\u7684\u6d1e\u623f\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u6c14\u5f97\u51e0\u4e4e\u53d1\u660f\uff0c\u4f46\u5374\u4e0d\u80fd\u4e0d\u4fe1\u5c60\u9f99\u7684\u8bdd\uff0c\u5fc3\u60f3\uff1a\u201c\u83ab\u975e\u4ed6\u4eec\u505a\u4e86\u592b\u59bb\uff0c\u8fd9\u53ae\u53c8\u7109 \u80fd\u77e5\u9053\u5a49\u59b9\u8fd8\u662f\u2018\u51b0\u6e05\u7389\u6d01\u2019\uff1f\u201d \u3000\u3000\u5c60\u9f99\u8d81\u8fd9\u673a\u4f1a\uff0c\u4e00\u6296\u624b\u628a\u6697\u85cf\u7684\u201c\u6bd2\u9f99\u9556\u201d\u5c04\u51fa\u3002\u674e\u601d\u5357\u6c14\u5f97\u53d1\u660f\uff0c\u672c\u6765\u975e\u4e2d\u4e0d\u53ef\uff0c\u5e78 \u800c\u5ba2\u5e97\u91cc\u7684\u4e00\u4e2a\u5c0f\u4f19\u8ba1\uff0c\u56e0\u4e3a\u542c\u5f97\u623f\u4e2d\u6709\u5435\u95f9\u4e4b\u58f0\uff0c\u8d70\u6765\u5f20\u671b\uff0c\u770b\u89c1\u6628\u633d\u4f4f\u7684\u8fd9\u4e2a\u201c\u98de\u8d3c\u201d \u6b63\u5728\u623f\u4e2d\uff0c\u51f6\u795e\u6076\u715e\u822c\u5730\u5bf9\u7740\u521a\u624d\u7684\u5ba2\u4eba\uff0c\u4e0d\u7531\u60ca\u5f97\u5927\u53eb\u4e00\u58f0\uff0c\u8fd9\u4e00\u58f0\u53eb\u5f97\u6070\u662f\u65f6\u5019\uff0c\u521a\u597d \u78b0\u4e0a\u5c60\u9f99\u628a\u4ed6\u7684\u6bd2\u9556\u5c04\u51fa\u3002 \u3000\u3000\u8fd9\u4e00\u58f0\u60ca\u9192\u4e86\u674e\u601d\u5357\uff0c\u62ac\u5934\u770b\u65f6\uff0c\u5c60\u9f99\u7684\u98de\u9556\u5df2\u662f\u5c04\u5230\uff0c\u674e\u601d\u5357\u970d\u7684\u4e00\u4e2a\u201c\u51e4\u70b9\u5934\u201d\uff0c \u98de\u8d77\u4e00\u811a\uff0c\u628a\u4e00\u5f20\u684c\u5b50\u8e22\u5f97\u98de\u4e86\u8d77\u6765\uff0c\u6070\u597d\u6321\u4f4f\u4e86\u90a3\u652f\u98de\u9556\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u5c60\u9f99\u5df2\u662f\u6251\u5230\uff0c\u674e\u601d\u5357\u6012\u9053\uff1a\u201c\u597d\u5440\uff0c\u4f60\u9738\u5360\u4e86\u6211\u7684\u59bb\u5b50\uff0c\u8fd8\u60f3\u5bb3\u6211 \u6027\u547d\uff01\u201d\u62d4\u5251\u51fa\u9798\uff0c\u201c\u94db\u2019\u7684\u4e00\u58f0\uff0c\u67b6\u5f00\u4e86\u5c60\u9f99\u5288\u6765\u7684\u4e00\u5251\u3002 \u3000\u3000\u5c60\u9f99\u9a82\u9053\uff1a\u201c\u8fd8\u6562\u8bf4\u662f\u4f60\u7684\u59bb\u5b50\uff01\u201d\u8d81\u7740\u5360\u4e86\u5148\u624b\u4e4b\u5229\uff0c\u66b4\u98ce\u9aa4\u96e8\u822c\u5730\u5411\u674e\u601d\u5357\u731b\u653b\u3002 \u5c60\u9f99\u4f7f\u7684\u662f\u5bb6\u4f20\u7684\u201c\u8ffd\u9b42\u593a\u547d\u5251\u6cd5\u201d\uff0c\u72e0\u8fa3\u65e0\u6bd4\uff0c\u6740\u5f97\u674e\u601d\u5357\u53ea\u6709\u62db\u67b6\u4e4b\u529f\u3002 \u3000\u3000\u4f46\u674e\u601d\u5357\u4e5f\u975e\u6cdb\u6cdb\u4e4b\u8f88\uff0c\u4ed6\u4e4b\u6240\u4ee5\u5403\u4e8f\uff0c\u4e0d\u8fc7\u662f\u56e0\u4e3a\u5fc3\u7eea\u4e0d\u5b81\u4e4b\u6545\uff0c\u4e00\u52a8\u4e86\u624b\uff0c\u53cd\u800c\u51b7 \u9759\u4e0b\u6765\u3002\u5c60\u9f99\u653b\u5f97\u72e0\uff0c\u4ed6\u4e5f\u5b88\u5f97\u7a33\uff0c\u5c11\u6797\u6d3e\u7684\u8fbe\u6469\u5251\u6cd5\uff0c\u672c\u6765\u5584\u4e8e\u4ee5\u67d4\u514b\u521a\uff0c\u674e\u601d\u5357\u89c1\u62db\u89e3 \u62db\u89c1\u5f0f\u62c6\u5f0f\u7684\u6c89\u7740\u5316\u89e3\uff0c\u89e3\u5f00\u4e86\u5c60\u9f99\u8fde\u73af\u4e09\u5341\u516d\u5f0f\u7684\u201c\u9053\u9b42\u593a\u547d\u201d\u5251\u62db\u4e4b\u540e\uff0c\u53cc\u65b9\u6253\u6210\u4e86\u5e73 \u624b\u3002\u674e\u601d\u5357\u7f13\u8fc7\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u7eb5\u4f7f\u4f60\u548c\u6768\u5a49\u7ed3\u4e86\u592b\u59bb\uff0c\u6211\u4e5f\u4e00\u5b9a\u8981\u89c1\u5979\u4e00\u89c1\uff01\u201d \u3000\u3000\u5c60\u9f99\u9a82\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u4f60\u8fd8\u662f\u6b7b\u5fc3\u4e0d\u606f\uff0c\u4f60\u60f3\u8981\u89c1\u5979\u662f\u4e0d\u884c\u7684\u4e86\uff0c\u6211\u9001\u4f60\u53bb\u89c1\u960e\u738b \u5427\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u8ba9\u6211\u89c1\u5979\uff0c\u6211\u53ea\u6709\u548c\u4f60\u62fc\u547d\u4e86\uff01\u201d\u4ed6\u5e76\u6ca1\u6709\u66b4\u8df3\u5982\u96f7\uff0c\u4f46\u8fd9\u51b7 \u9177\u7684\u795e\u60c5\uff0c\u5374\u6bd4\u66b4\u6012\u8fd8\u66f4\u53ef\u6015\u3002 \u3000\u3000\u5c60\u9f99\u5f3a\u653b\u4e0d\u4e0b\uff0c\u5fc3\u91cc\u6709\u70b9\u53d1\u614c\uff0c\u8981\u77e5\u4ed6\u662f\u53d7\u4e86\u77f3\u749e\u7684\u6050\u5413\u9003\u56de\u6765\u7684\uff0c\u77f3\u749e\u5413\u4ed6\u8bf4\u4ed6\u7684\u59b9 \u59b9\u5c60\u51e4\u5c31\u8981\u6765\u5230\uff0c\u4ed6\u4fe1\u4ee5\u4e3a\u771f\uff0c\u751f\u6015\u77f3\u749e\u548c\u5c60\u51e4\u5c31\u8981\u6765\u627e\u4ed6\u7b97\u5e10\u3002\u6b64\u65f6\u6697\u7b97\u4e0d\u6210\uff0c\u5f3a\u653b\u4e0d \u4e0b\uff0c\u5df2\u662f\u4e0d\u6562\u5e94\u6218\u3002 \u3000\u3000\u674e\u601d\u5357\u559d\u9053\uff1a\u201c\u54ea\u91cc\u8d70\uff01\u201d\u5982\u5f71\u968f\u5f62\u5730\u7d27\u7d27\u9489\u4f4f\u5c60\u9f99\uff0c\u963b\u6b62\u4ed6\u4ece\u7a97\u53e3\u8df3\u62d9\u3002\u53cd\u5b88\u4e3a\u653b\uff0c \u5251\u5251\u523a\u5411\u5c60\u9f99\u7684\u8981\u5bb3\u3002 \u3000\u3000\u5c60\u9f99\u7709\u5934\u4e00\u76b1\uff0c\u8ba1\u4e0a\u5fc3\u6765\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u771f\u7684\u559c\u6b22\u6768\u5a49\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u65a5\u9053\uff1a\u201c\u8fd9\u53e5\u8bdd\u4e0d\u8be5\u7531\u4f60\u95ee\u6211\uff0c\u4f60\u53eb\u6768\u5a49\u89c1\u6211\uff0c\u6211\u81ea\u4f1a\u8ddf\u5979\u8bf4\uff01\u201d \u3000\u3000\u5c60\u9f99\u51b7\u7b11\u9053\uff1a\u201c\u6211\u662f\u5979\u7684\u4e08\u592b\uff0c\u6211\u4e3a\u4ec0\u4e48\u4e0d\u80fd\u8bf4\uff01\u6211\u8bf4\uff0c\u4f60\u82e5\u7136\u771f\u7684\u662f\u7231\u6768\u5a49\uff0c\u5c31\u5e94\u8be5 \u4e3a\u5979\u7740\u60f3\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u6014\u4e86\u4e00\u6014\uff0c\u832b\u7136\u9053\uff1a\u201c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5c60\u9f99\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u8bf4\u4f60\u5e94\u8be5\u4e3a\u5979\u7740\u60f3\uff01\u4f60\u60f3\u60f3\uff0c\u5979\u5df2\u7ecf\u5fc3\u7518\u60c5\u613f\u5730\u5ac1\u4e86\u6211\uff0c\u4f60\u4f55\u82e6\u8981 \u5c06\u5979\u7ea0\u7f20\uff1f\u89c1\u4e86\u5979\u53c8\u6709\u4ec0\u4e48\u7528\uff1f\u53ea\u662f\u5bb3\u5f97\u5979\u75db\u82e6\u800c\u5df2\uff01\u201d \u3000\u3000\u8fd9\u51e0\u53e5\u8bdd\u597d\u50cf\u6bd2\u7bad\u4f3c\u5730\u5c04\u8fdb\u674e\u601d\u5357\u7684\u5fc3\u7a9d\uff0c\u674e\u601d\u5357\u5fc3\u5934\u4e00\u9707\uff0c\u6697\u81ea\u601d\u91cf\uff1a\u201c\u4e0d\u9519\uff0c\u89c1\u4e86 \u5979\u53c8\u6709\u4ec0\u4e48\u7528\uff1f\u5979\u5df2\u7ecf\u6709\u4e86\u522b\u4eba\u4e86\uff01\u201d\u5fc3\u5ff5\u672a\u5df2\uff0c\u5c60\u9f99\u5530\u7684\u4e00\u5251\u523a\u6765\uff0c\u674e\u601d\u5357\u5fc3\u7070\u610f\u51b7\uff0c\u53f9 \u53e3\u6c14\u9053\uff1a\u201c\u4f60\u53bb\u7f62\uff01\u201d\u4fa7\u8eab\u4e00\u8ba9\uff0c\u5c60\u9f99\u7acb\u5373\u4ece\u7a97\u53e3\u8df3\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u8fd9\u5239\u90a3\u95f4\uff0c\u674e\u601d\u5357\u5fc3\u4e2d\u8f6c\u4e86\u51e0\u4e2a\u5ff5\u5934\uff0c\u6700\u521d\u60f3\u7684\u662f\uff1a\u201c\u6211\u5c31\u8fd9\u6837\u8ba9\u4ed6\u8d70\u4e86\u5417\uff1f\u201d\u7ee7\u800c\u53c8 \u60f3\uff1a\u201c\u4e0d\u8ba9\u4ed6\u8d70\u53c8\u80fd\u600e\u6837\uff1f\u96be\u9053\u6211\u8fd8\u80fd\u5c06\u4ed6\u6740\u6389\u5417\uff1f\u5a49\u59b9\u548c\u4ed6\u5df2\u7136\u505a\u4e86\u592b\u59bb\uff0c\u6740\u4e86\u4ed6\u5c31\u5bb3\u4e86 \u5a49\u59b9\u4e00\u751f\u3002\u201d\u60f3\u81f3\u6b64\u5904\uff0c\u53c8\u4e0d\u7981\u81ea\u6127\u81ea\u8d23\uff1a\u201c\u674e\u601d\u5357\u5440\u674e\u601d\u5357\uff0c\u4f60\u5c82\u80fd\u8fd9\u6837\u80f8\u895f\u72ed\u7a84\uff1f\u4f60\u82e5 \u662f\u771f\u7684\u7231\u5979\uff0c\u5c31\u8be5\u4e3a\u4e86\u5979\u7684\u5e78\u798f\u7740\u60f3\u3002\u83ab\u8bf4\u4f60\u4e0d\u8be5\u4f24\u5bb3\u5979\u7684\u4e08\u592b\uff0c\u5c31\u662f\u89c1\u5979\u7684\u5ff5\u5934\u4e5f\u4e0d\u5e94\u518d \u6709\u4e86\u3002\u201d \u3000\u3000\u60f3\u81f3\u6b64\u5904\uff0c\u5ffd\u542c\u5f97\u5916\u9762\u4e00\u58f0\u60e8\u53eb\u3002\u539f\u6765\u662f\u5c60\u9f99\u4ece\u7a97\u53e3\u7a9c\u51fa\u53bb\u7684\u65f6\u5019\uff0c\u90a3\u4e2a\u5413\u5f97\u5446\u4e86\u7684\u5c0f \u4f19\u8ba1\u8fd8\u672a\u66fe\u6765\u5f97\u53ca\u8eb2\u5165\u5e10\u623f\uff0c\u5c60\u9f99\u6068\u4ed6\u521a\u624d\u7684\u90a3\u4e00\u58f0\u53eb\u558a\uff0c\u60ca\u9192\u4e86\u674e\u601d\u5357\uff0c\u4ee5\u81f4\u4ed6\u7684\u6697\u7b97\u4e0d \u80fd\u6210\u529f\u3002\u5c60\u9f99\u8fc1\u6012\u5230\u8fd9\u4e2a\u5c0f\u4f19\u8ba1\u8eab\u4e0a\uff0c\u987a\u624b\u4e00\u5251\u5c31\u7ed3\u679c\u4e86\u4ed6\u7684\u6027\u547d\u3002 \u3000\u3000\u674e\u601d\u5357\u95fb\u58f0\u8ffd\u51fa\uff0c\u76ee\u7779\u60e8\u72b6\uff0c\u4e0d\u7531\u5f97\u5fc3\u4e2d\u5927\u6012\uff0c\u559d\u9053\uff1a\u201c\u597d\u5440\uff0c\u6211\u653e\u4f60\u8d70\uff0c\u4f60\u5f97\u4e86\u6027 \u547d\uff0c\u5374\u8fd8\u8981\u4f24\u5bb3\u65e0\u8f9c\uff01\u201d \u3000\u3000\u5c60\u9f99\u6628\u65e5\u6240\u4e70\u7684\u4e00\u9a6c\u4e00\u9aa1\u8fd8\u7cfb\u5728\u9a6c\u53a9\uff0c\u6b64\u65f6\u5c60\u9f99\u5df2\u7ecf\u6253\u5f00\u9a6c\u53a9\uff0c\u8de8\u4e0a\u4e86\u5750\u9a91\uff0c\u674e\u601d\u5357\u65b9 \u624d\u8ffd\u51fa\u5ba2\u5e97\uff0c\u5c60\u9f99\u5411\u4ed6\u98de\u51fa\u4e00\u652f\u6bd2\u9f99\u9556\uff0c\u8ddf\u7740\u53c8\u628a\u53e6\u4e00\u652f\u6bd2\u9f99\u9556\u63d2\u8fdb\u90a3\u5339\u9ed1\u9aa1\u7684\u80cc\u810a\uff0c\u514d\u5f97 \u4e3a\u674e\u601d\u5357\u6240\u7528\u3002 \u3000\u3000\u674e\u601d\u5357\u6253\u843d\u4e86\u6bd2\u9f99\u9556\uff0c\u53ea\u542c\u5f97\u5c60\u9f99\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u674e\u601d\u5357\uff0c\u4f60\u540e\u6094\u5df2\u7ecf\u6765\u4e0d\u53ca\u4e86\uff01\u4f60\u8ffd\u4e0d \u4e0a\u5566\uff01\u54c8\u54c8\uff0c\u4eca\u65e5\u4f60\u4e0d\u6740\u6211\uff0c\u6211\u53ef\u4e0d\u9886\u4f60\u7684\u60c5\uff0c\u4ed6\u65e5\u6211\u5fc5\u6740\u4f60\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u6012\u706b\u51b2\u5929\uff0c\u62d4\u6b65\u5c31\u8ffd\u3002\u53ef\u662f\u4ed6\u7684\u8f7b\u529f\u867d\u597d\uff0c\u5374\u603b\u662f\u8dd1\u4e0d\u8fc7\u9a8f\u9a6c\u3002\u8ffd\u51fa\u4e86\u8fd9\u4e2a\u5c0f \u9547\uff0c\u5728\u66ae\u8272\u82cd\u832b\u4e4b\u4e2d\uff0c\u5c60\u9f99\u7684\u4e00\u4eba\u4e00\u9a91\u5df1\u662f\u53ea\u80fd\u770b\u89c1\u4e00\u4e2a\u6a21\u7cca\u7684\u9ed1\u70b9\u4e86\u3002 \u3000\u3000\u665a\u98ce\u5439\u6765\uff0c\u674e\u601d\u5357\u6e05\u9192\u4e0b\u6765\uff0c\u53f9\u4e86\u53e3\u6c14\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u4eba\u539f\u6765\u662f\u4e2a\u574f\u86cb\uff0c\u6211\u4e3a\u4e86\u5a49\u59b9 \u7684\u5e78\u798f\u9976\u4ed6\uff0c\u4f46\u5a49\u59b9\u5ac1\u4e86\u8fd9\u6837\u7684\u4eba\uff0c\u53c8\u7109\u80fd\u5f97\u5230\u5e78\u798f\uff1f\u65e9\u77e5\u5982\u6b64\uff0c\u6211\u5b9e\u5728\u662f\u4e0d\u5e94\u9976\u4ed6\u7684\u3002\u201d \u53c8\u60f3\uff1a\u201c\u5a49\u59b9\u4e3a\u4ec0\u4e48\u53c8\u4f1a\u5ac1\u7ed9\u8fd9\u6837\u7684\u4eba\u5462\uff1f\u5979\u4e00\u5411\u806a\u660e\uff0c\u96be\u9053\u7adf\u7136\u4e00\u70b9\u4e5f\u770b\u4e0d\u51fa\u8fd9\u53ae\u662f\u4e2a\u574f \u4eba\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u6b63\u5728\u4e3a\u6768\u5a49\u53f9\u606f\uff0c\u5ffd\u542c\u5f97\u9a6c\u7fa4\u9a70\u9a8b\u7684\u8e44\u58f0\uff0c\u9690\u9690\u4f20\u6765\uff0c\u56de\u5934\u4e00\u671b\uff0c\u53ea\u89c1\u4e00\u961f\u8499\u53e4 \u9a91\u5175\u5df2\u7ecf\u8fdb\u5165\u8fd9\u4e2a\u5c0f\u9547\u3002\u7ee3\u7740\u5140\u9e70\u7684\u8499\u53e4\u519b\u65d7\uff0c\u8fdc\u8fdc\u5730\u4e5f\u770b\u5f97\u89c1\u4e86\u3002 \u3000\u3000\u674e\u601d\u5357\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u60f3\uff1a\u201c\u8499\u53e4\u9791\u5b50\u600e\u7684\u6765\u5f97\u8fd9\u6837\u5feb\uff1f\u6211\u4e00\u8def\u8eb2\u907f\u4ed6\u4eec\uff0c\u59cb\u7ec8\u8fd8\u662f\u7ed9\u4ed6 \u4eec\u8ddf\u5728\u540e\u9762\u3002\u201d\u4f17\u5be1\u4e0d\u654c\uff0c\u674e\u601d\u5357\u4e5f\u4e0d\u613f\u4f5c\u65e0\u8c13\u7684\u640f\u6597\uff0c\u53ea\u597d\u518d\u9003\u3002 \u3000\u3000\u674e\u601d\u5357\u54ea\u91cc\u77e5\u9053\uff0c\u8fdb\u5165\u8fd9\u4e2a\u5c0f\u9547\u7684\uff0c\u539f\u6765\u5c31\u662f\u963f\u76d6\u7684\u90a3\u4e00\u8425\u58eb\u5175\uff0c\u4ed6\u4eec\u662f\u62c5\u4efb\u4fa6\u5bdf\u4efb\u52a1 \u7684\u201c\u523a\u63a2\u201d\u90e8\u961f\uff0c\u5e76\u975e\u4f5c\u6218\u7684\u5927\u519b\uff0c\u800c\u4e14\u6768\u5a49\u5c31\u6b63\u5728\u8fd9\u4e00\u8425\u4e2d\u3002\u4ed6\u8fd9\u4e48\u4e00\u8dd1\uff0c\u53ef\u5c31\u9519\u8fc7\u4e86\u548c \u6768\u5a49\u76f8\u89c1\u7684\u673a\u4f1a\u3002 \u3000\u3000\u805a\u96c6\u5728\u9547\u58eb\u7684\u96be\u6c11\u770b\u89c1\u8499\u53e4\u9a6c\u961f\u6765\u5230\uff0c\u7eb7\u7eb7\u9003\u8d70\u3002\u963f\u76d6\u4e0b\u4ee4\uff0c\u4e0d\u8bb8\u4f24\u5bb3\u767e\u59d3\uff0c\u53eb\u624b\u4e0b\u529d \u8c15\u767e\u59d3\u4e0d\u5fc5\u9003\u8dd1\uff0c\u4f46\u5df2\u7ecf\u8dd1\u4e86\u7684\uff0c\u5c31\u7531\u5f97\u4ed6\u4eec\u81ea\u53bb\uff0c\u4e5f\u4e0d\u5fc5\u518d\u6349\u56de\u6765\u3002 \u3000\u3000\u6768\u5a49\u770b\u89c1\u4e00\u4e2a\u8dd1\u5728\u6700\u524d\u9762\u7684\u6c49\u5b50\uff0c\u8dd1\u5f97\u975e\u5e38\u4e4b\u5feb\uff0c\u6b64\u65f6\u5df2\u662f\u9ec4\u660f\u65f6\u5206\uff0c\u66ae\u8272\u82cd\u832b\u4e4b\u4e2d\uff0c \u53ea\u80fd\u9690\u7ea6\u770b\u89c1\u4ed6\u7684\u80cc\u5f71\u3002\u6768\u5a49\u5fc3\u5934\u4e00\u52a8\uff0c\u4f9d\u7a00\u89c9\u5f97\u8fd9\u4e2a\u80cc\u5f71\u4f3c\u66fe\u76f8\u8bc6\uff0c\u4e00\u5ff5\u672a\u5df2\uff0c\u8fd9\u4eba\u7684\u80cc \u5f71\u5df2\u7136\u6d88\u5931\u3002 \u3000\u3000\u6768\u5a49\u54d1\u7136\u5931\u7b11\uff0c\u5fc3\u671b\u60f3\u9053\uff1a\u201c\u4e0d\u4f1a\u8fd9\u6837\u5de7\u5427\uff0c\u6211\u771f\u662f\u60f3\u5f97\u592a\u75f4\u4e86\uff01\u201d\u5979\u4ee5\u4e3a\u8fd9\u662f\u56e0\u4e3a\u5979 \u592a\u8fc7\u60f3\u5ff5\u674e\u601d\u5357\u7684\u7f18\u6545\uff0c\u4ee5\u81f4\u628a\u4e00\u4e2a\u8eab\u6750\u7a0d\u5fae\u76f8\u4f3c\u7684\u4eba\u90fd\u5f53\u4f5c\u4e86\u674e\u601d\u5357\u3002\u54ea\u91cc\u77e5\u9053\u8fd9\u4e2a\u4eba\u771f \u7684\u662f\u674e\u601d\u5357\uff01 \u3000\u3000\u6768\u5a49\u4e5f\u66fe\u5230\u8fc7\u6628\u665a\u6240\u4f4f\u7684\u90a3\u95f4\u5ba2\u5e97\u67e5\u8fc7\u4e00\u904d\uff0c\u5979\u5f53\u7136\u4e0d\u4f1a\u77e5\u9053\u674e\u601d\u5357\u5230\u8fc7\u8fd9\u95f4\u5ba2\u5e97\uff0c\u7528 \u610f\u53ea\u662f\u5728\u63a2\u5bfb\u5c60\u9f99\u7684\u4e0b\u843d\u3002\u5ba2\u5e97\u7684\u8001\u677f\u65e9\u5df2\u8eb2\u8d77\u6765\u4e86\u3002\u4f19\u8ba1\u6015\u60f9\u4e8b\u4e0a\u8eab\uff0c\u8c01\u4e5f\u4e0d\u6562\u5bf9\u4e00\u4e2a\u8499 \u53e4\u58eb\u5175\u591a\u8bf4\u3002\u6768\u5a49\u95ee\u4e0d\u51fa\u4e2a\u6240\u4ee5\u7136\u6765\uff0c\u4e5f\u53ea\u597d\u7b97\u4e86\u3002 \u3000\u3000\u5728\u9547\u4e0a\u6b47\u4e86\u4e00\u665a\uff0c\u7b2c\u4e8c\u65e5\uff0c\u5927\u519b\u9646\u7eed\u6765\u5230\uff0c\u963f\u76d6\u8fd9\u4e00\u8425\u5f52\u56de\u539f\u6765\u7684\u7f16\u5236\uff0c\u4e0d\u518d\u62c5\u4efb\u201c\u523a \u63a2\u201d\u4efb\u52a1\u3002\u6768\u5a49\u4f4f\u5728\u8425\u4e2d\uff0c\u963f\u76d6\u679c\u7136\u5904\u5904\u4e8e\u5979\u65b9\u4fbf\u3002\u5979\u626e\u6f14\u7684\u662f\u9a6c\u592b\u7684\u89d2\u8272\uff0c\u72ec\u81ea\u4e00\u4e2a\u5e10 \u7bf7\uff0c\u53ea\u987b\u66ff\u963f\u76d6\u770b\u7ba1\u9a6c\u5339\uff0c\u5176\u4ed6\u5dee\u4e8b\uff0c\u5168\u90fd\u8c41\u514d\uff0c\u77e5\u9053\u5979\u662f\u5973\u7684\u53ea\u6709\u5341\u4e2a\u4eba\uff0c\u8fd9\u5341\u4e2a\u4eba\u4e5f\u90fd \u80fd\u5b88\u53e3\u5982\u74f6\uff0c\u6ca1\u6709\u6cc4\u6f0f\u5979\u7684\u79d8\u5bc6\u3002\u6768\u5a49\u4e00\u65f6\u6709\u70b9\u4e0d\u5b89\uff0c\u6e10\u6e10\u4e5f\u5c31\u60ef\u4e86\u3002 \u3000\u3000\u8499\u53e4\u5927\u519b\u4fb5\u5165\u91d1\u56fd\u7684\u5883\u754c\u4e4b\u540e\uff0c\u4e00\u8def\u52bf\u5982\u7834\u7af9\uff0c\u963f\u76d6\u8fd9\u4e00\u8425\u4eba\u6839\u672c\u5c31\u6ca1\u6709\u6253\u8fc7\u4ed7\uff0c\u76f4\u5230 \u4e86\u516d\u76d8\u5c71\u4e0b\uff0c\u8499\u53e4\u7684\u5927\u519b\u624d\u5f00\u59cb\u9047\u5230\u6bd4\u8f83\u575a\u5f3a\u7684\u62b5\u6297\u3002\u91d1\u56fd\u7684\u5b88\u5c06\u80e1\u6c99\u864e\u9887\u6709\u5c06\u624d\uff0c\u501a\u5c71\u4e3a \u57ce\uff0c\u51ed\u9669\u56fa\u5b88\uff0c\u8499\u53e4\u7684\u524d\u950b\u8fdb\u653b\u6570\u6b21\uff0c\u5747\u4e0d\u5f97\u901e\uff0c\u53ea\u597d\u5728\u5c71\u811a\u9a7b\u624e\u4e0b\u6765\uff0c\u7b49\u5f85\u63f4\u519b\u3002 \u3000\u3000\u8fc7\u4e86\u51e0\u5929\uff0c\u6768\u5a49\u542c\u8bf4\u4e3b\u5e05\u9547\u56fd\u738b\u5b50\u548c\u526f\u5e05\u201c\u674e\u5e0c\u6d69\u201d\u90fd\u5df2\u5230\u4e86\u3002\u6768\u5a49\u9042\u5f00\u59cb\u51c6\u5907\u884c\u523a\u8fd9 \u4e2a\u5192\u724c\u526f\u5e05\u4f59\u4e00\u4e2d\u7684\u8ba1\u5212\u3002 \u3000\u3000\u4f59\u4e00\u4e2d\u9887\u901a\u5175\u6cd5\uff0c\u53c8\u4f1a\u5df4\u7ed3\uff0c\u9547\u56fd\u738b\u5b50\u5bf9\u4ed6\u8a00\u542c\u8ba1\u4ece\uff0c\u56e0\u6b64\u4ed6\u867d\u7136\u53ea\u662f\u526f\u5143\u5e05\uff0c\u5b9e\u9645\u5982 \u7b49\u4e8e\u662f\u7531\u4ed6\u4e3b\u6301\u519b\u4e8b\u3002\u4f59\u4e00\u4e2d\u5e26\u5175\u4e3b\u5f20\u7a33\u91cd\uff0c\u4ed6\u4f30\u8ba1\u5c71\u4e0a\u7684\u5b58\u7cae\u7edd\u4e0d\u80fd\u957f\u4e45\u652f\u6301\uff0c\u4e8e\u662f\u91c7\u53d6 \u56f4\u56f0\u7684\u7b56\u7565\uff0c\u5e76\u5207\u65ad\u5c71\u4e0a\u7684\u6c34\u6e90\u3002 \u3000\u3000\u4f59\u4e00\u4e2d\u91c7\u53d6\u4e86\u56f4\u56f0\u5b88\u519b\u7684\u6218\u7565\uff0c\u5012\u662f\u6709\u5229\u4e8e\u6768\u5a49\u7684\u884c\u523a\u8ba1\u5212\u3002\u4fd7\u8bed\u8bf4\uff1a\u201c\u5929\u4e0b\u65e0\u96be\u4e8b\uff0c \u53ea\u6015\u6709\u5fc3\u4eba\u3002\u201d\u5979\u7559\u5fc3\u6253\u542c\uff0c\u7ec8\u4e8e\u77e5\u9053\u4e86\u4f59\u4e00\u4e2d\u7684\u8425\u5e10\u6240\u5728\uff0c\u8425\u4e2d\u5b88\u536b\u7684\u60c5\u51b5\u4e5f\u5927\u81f4\u6478\u4e86\u4e2a \u5e95\u3002 \u3000\u3000\u8fd9\u4e00\u5929\u665a\u4e0a\uff0c\u9634\u96e8\u970f\u970f\uff0c\u65e0\u6708\u65e0\u661f\uff0c\u6b63\u662f\u4e00\u4e2a\u4e0b\u624b\u7684\u597d\u673a\u4f1a\u3002\u6768\u5a49\u5f85\u81f3\u4e09\u66f4\u65f6\u5206\uff0c\u8425\u4e2d \u58eb\u5175\u90fd\u719f\u7761\u4e86\uff0c\u5979\u6084\u6084\u6e9c\u51fa\u5e10\u7bf7\uff0c\u4fbf\u53bb\u884c\u523a\u4f59\u4e00\u4e2d\u3002\u4f59\u4e00\u4e2d\u5e10\u5e55\u524d\u9762\u7684\u56db\u4e2a\u5b88\u536b\u5728\u96e8\u591c\u4e2d\u5f53 \u503c\uff0c\u4e0d\u514d\u7cbe\u795e\u677e\u61c8\uff0c\u6839\u672c\u5c31\u6ca1\u6709\u53d1\u73b0\u6768\u5a49\u7684\u8e2a\u5f71\u3002 \u3000\u3000\u4f59\u4e00\u4e2d\u6240\u4f4f\u7684\u9526\u5e10\u5f53\u7136\u662f\u6bd4\u5175\u58eb\u7684\u5e10\u5e55\u5bbd\u655e\u8212\u9002\uff0c\u6240\u5360\u7684\u5730\u65b9\u6709\u666e\u901a\u519c\u5bb6\u5341\u51e0\u95f4\u623f\u5b50\u4e4b \u5927\uff0c\u7ea6\u6709\u4e09\u4e08\u591a\u9ad8\uff0c\u7bf7\u9876\u56db\u8fb9\u7fd8\u8d77\uff0c\u4e2d\u95f4\u5fae\u51f9\uff0c\u4f46\u5927\u81f4\u662f\u5e73\u5766\u7684\uff0c\u548c\u4e00\u822c\u4eba\u5bb6\u7684\u5c4b\u9876\u4e5f\u5dee\u4e0d \u591a\u3002 \u3000\u3000\u6768\u5a49\u5c55\u5f00\u8d85\u5353\u7684\u8f7b\u529f\uff0c\u9ed1\u591c\u91cc\u8d81\u7740\u5b88\u536b\u7684\u58eb\u5175\u6b63\u5728\u62b1\u6028\u7684\u65f6\u5019\uff0c\u6377\u5982\u98de\u9e1f\u822c\u7684\u63a0\u4e0a\u7bf7 \u9876\uff0c\u7acb\u5373\u4fef\u4f0f\u4e0b\u6765\u3002\u5b88\u536b\u7684\u53ea\u662f\u7559\u5fc3\u524d\u9762\u7684\u52a8\u9759\uff0c\u6839\u672c\u5c31\u4e0d\u4f1a\u56de\u5934\u67e5\u770b\u7bf7\u9876\u7684\u60c5\u51b5\u3002\u800c\u4e14\u7bf7 \u9876\u662f\u56db\u9762\u7fc5\u8d77\u7684\uff0c\u5373\u4f7f\u4ed6\u4eec\u56de\u5934\uff0c\u4e5f\u770b\u4e0d\u89c1\u4fef\u4f0f\u7684\u6768\u5a49\u3002 \u3000\u3000\u6768\u5a49\u7528\u4e00\u67c4\u950b\u5229\u7684\u5315\u9996\uff0c\u8f7b\u8f7b\u5730\u5272\u5f00\u4e86\u4e00\u6761\u7f1d\uff0c\u8d34\u7740\u773c\u775b\u770b\u4e0b\u53bb\u3002\u53ea\u89c1\u5e10\u4e2d\u706f\u706b\u8f89\u714c\uff0c \u5143\u5e05\u9547\u56fd\u738b\u5b50\u4e5f\u5728\u91cc\u9762\uff0c\u4f59\u4e00\u4e2d\u6b63\u966a\u7740\u4ed6\u770b\u4e00\u5f20\u5730\u56fe\uff0c\u53e3\u8bb2\u6307\u5212\uff0c\u4f3c\u4e4e\u662f\u5728\u548c\u9547\u56fd\u738b\u5b50\u8bb2\u89e3 \u7528\u5175\u7684\u7b56\u7565\u3002\u65c1\u8fb9\u8fd8\u6709\u4e24\u4e2a\u6b66\u58eb\u3002 \u3000\u3000\u6768\u5a49\u5fc3\u4e2d\u9ed8\u7977\uff1a\u201c\u516c\u516c\u3001\u54e5\u54e5\uff0c\u6c42\u4f60\u4eec\u5728\u5929\u4e4b\u7075\u4fdd\u4f30\uff0c\u8ba9\u6211\u4e00\u51fb\u6210\u529f\uff01\u201d\u94f6\u7259\u4e00\u54ac\uff0c\u628a \u5315\u9996\u5bf9\u51c6\u4e86\u4f59\u4e00\u4e2d\u7528\u529b\u63b7\u53bb\u3002 \u3000\u3000\u4e5f\u662f\u4f59\u4e00\u4e2d\u547d\u4e0d\u8be5\u7edd\uff0c\u521a\u597d\u4ed6\u4f4e\u4e0b\u5934\u6765\u770b\u90a3\u5f20\u519b\u7528\u5730\u56fe\uff0c\u53ea\u542c\u5f97\u201c\u548b\u5693\u201d\u4e00\u58f0\uff0c\u98de\u5200\u5256 \u5f00\u4e86\u4ed6\u5934\u4e0a\u6240\u6234\u7684\u76ae\u5e3d\uff0c\u51e0\u4e4e\u662f\u8d34\u7740\u4ed6\u7684\u5934\u76ae\u524a\u8fc7\uff0c\u4f46\u5374\u53ea\u662f\u524a\u53bb\u4e86\u4ed6\u7684\u4e00\u64ae\u5934\u53d1\u3002 \u3000\u3000\u4f59\u4e00\u4e2d\u5413\u5f97\u201c\u5495\u549a\u201d\u4e00\u58f0\u8dcc\u5012\uff0c\u9547\u56fd\u738b\u5b50\u5927\u53eb\u9053\uff1a\u201c\u6709\u523a\u5ba2\uff01\u201d\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u5e10\u4e2d \u7684\u4e24\u540d\u6b66\u58eb\u5df2\u7ecf\u8ffd\u51fa\uff0c\u5176\u4e2d\u4e00\u4e2a\u8df3\u4e0a\u4e86\u7bf7\u9876\uff0c\u55d6\u55d6\u55d6\u8fde\u73af\u4e09\u9556\u5c31\u5411\u6768\u5a49\u5c04\u53bb\u3002 \u3000\u3000\u6768\u5a49\u6697\u81ea\u53f9\u4e86\u4e00\u58f0\u201c\u53ef\u60dc\uff01\u201d\u4e8b\u5df2\u8d25\u9732\uff0c\u6768\u5a49\u53ea\u597d\u9003\u8d70\u4e86\u3002 \u3000\u3000\u8fd9\u4e24\u4e2a\u6b66\u58eb\u7684\u672c\u9886\u5f88\u662f\u4e0d\u5f31\uff0c\u4e09\u9556\u843d\u7a7a\uff0c\u8ddf\u8e2a\u6025\u8ffd\uff0c\u5e10\u5916\u7684\u536b\u58eb\u4e5f\u5728\u5927\u53eb\u201c\u6709\u523a\u5ba2\uff0c\u6709 \u523a\u5ba2\uff01\u201d\u4e00\u9762\u558a\uff0c\u4e00\u9762\u8ffd\u3002 \u3000\u3000\u4f59\u4e00\u4e2d\u8fd9\u5ea7\u5e10\u5e55\u548c\u9547\u56fd\u738b\u5b50\u7684\u201c\u5e05\u5e10\u201d\u76f8\u8ddd\u4e0d\u8fdc\uff0c\u5e05\u5e10\u7684\u536b\u58eb\u95fb\u58f0\u8d76\u51fa\uff0c\u53c2\u52a0\u8ffd\u6355\u3002\u6709 \u4e9b\u536b\u58eb\u4e14\u5df2\u515c\u622a\u5728\u6768\u5a49\u7684\u524d\u9762\u3002 \u3000\u3000\u5e78\u800c\u662f\u4e2a\u9634\u96e8\u970f\u970f\u7684\u591c\u665a\uff0c\u65e0\u6708\u65e0\u661f\uff0c\u4e94\u6b65\u4e4b\u5916\uff0c\u5c31\u770b\u4e0d\u6e05\uff0c\u6768\u5a49\u7075\u673a\u4e00\u52a8\uff0c\u4e5f\u5927\u53eb\uff1a \u201c\u6349\u523a\u5ba2\uff0c\u6349\u523a\u5ba2\u5440\uff01\u201d\u8eab\u5f62\u4e00\u4e2a\u76d8\u65cb\uff0c\u4ece\u4e00\u68f5\u6811\u540e\u8dc3\u51fa\uff0c\u6df7\u5728\u4e00\u7fa4\u536b\u571f\u4e4b\u4e2d\u3002 \u3000\u3000\u9547\u56fd\u738b\u5b50\u9a91\u9a6c\u8ffd\u51fa\uff0c\u4ed6\u5f97\u4e86\u4f59\u4e00\u4e2d\u4e4b\u6559\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u522b\u5fd9\u7740\u8ffd\uff0c\u5148\u7740\u770b\u4f60\u8eab\u8fb9\u7684\u4eba\uff0c \u5018\u82e5\u662f\u4f60\u4e0d\u8ba4\u8bc6\u7684\u964c\u751f\u4eba\uff0c\u5c31\u5148\u628a\u4ed6\u6293\u4f4f\uff01\u201d\u6700\u5148\u8ffd\u51fa\u7684\u90a3\u4e24\u4e2a\u6b66\u58eb\u662f\u89c1\u8fc7\u6768\u5a49\u7684\u80cc\u5f71\u7684\uff0c \u4e5f\u5728\u53eb\u9053\uff1a\u201c\u523a\u5ba2\u662f\u4e2a\u7626\u5c0f\u7684\u5bb6\u4f19\uff0c\u4f60\u4eec\u591a\u7559\u795e\u5427\u3002\u201d \u3000\u3000\u591c\u8272\u867d\u6d53\uff0c\u8eab\u8fb9\u7684\u4eba\u8fd8\u662f\u53ef\u4ee5\u8fa8\u8ba4\u7684\uff0c\u548c\u6768\u5a49\u4e00\u8d77\u8dd1\u7684\u90a3\u4e2a\u536b\u58eb\u970d\u7136\u4e00\u7701\uff0c\u7acb\u5373\u5411\u6768\u5a49 \u7684\u7435\u7436\u9aa8\u6293\u4e0b\uff0c\u559d\u9053\uff1a\u201c\u4f60\u662f\u54ea\u4e00\u8425\u7684\uff01\u201d \u3000\u3000\u8fd9\u4eba\u662f\u4e2a\u6454\u89d2\u7684\u597d\u624b\uff0c\u6768\u5a49\u6c89\u80a9\u7f29\u8098\uff0c\u53cd\u624b\u4e00\u5251\uff0c\u80a9\u8180\u7ed9\u4ed6\u4e00\u6273\uff0c\u7adf\u7136\u6ca1\u523a\u7740\u4ed6\u3002\u8fd9\u4eba \u7528\u4e86\u4e2a\u201c\u80a9\u8f66\u5f0f\u201d\u8170\u4e00\u5f13\uff0c\u817f\u4e00\u62ac\uff0c\u624b\u4e00\u6273\uff0c\u628a\u6768\u5a49\u7684\u8eab\u5b50\u62ac\u4e86\u8d77\u6765\uff0c\u4ece\u4ed6\u7684\u80a9\u4e0a\u7ffb\u8fc7\u53bb\uff0c \u5927\u53eb\u9053\uff1a\u201c\u523a\u5ba2\u5728\u8fd9\u91cc\u4e86\u3002\u201d \u3000\u3000\u8fd9\u536b\u58eb\u4e00\u4e2a\u201c\u80a9\u8f66\u5f0f\u201d\uff0c\u628a\u6768\u5a49\u6454\u7ffb\uff0c\u5ffd\u89c9\u8170\u80c1\u4e00\u9ebb\uff0c\u6768\u5a49\u6ca1\u6709\u8dcc\u5012\uff0c\u53cd\u800c\u662f\u4ed6\u8dcc\u5012\u3002 \u539f\u6765\u6768\u5a49\u867d\u7136\u4e0d\u4f1a\u6454\u89d2\uff0c\u5374\u4f1a\u70b9\u7a74\uff0c\u5c31\u5728\u8fd9\u4eba\u628a\u5979\u4ece\u80a9\u5934\u7ffb\u8fc7\u53bb\u7684\u65f6\u5019\uff0c\u6768\u5a49\u4e00\u6307\u70b9\u4e86\u4ed6\u80c1 \u4e0b\u7684\u6108\u6c14\u7a74\u3002 \u3000\u3000\u6768\u5a49\u5728\u534a\u7a7a\u4e2d\u4e00\u4e2a\u7b4b\u6597\u7ffb\u4e86\u4e0b\u6765\uff0c\u811a\u5c16\u521a\u521a\u7740\u5730\uff0c\u9547\u56fd\u738b\u5b50\u968f\u8eab\u62a4\u4e3b\u7684\u4e00\u540d\u6b66\u58eb\u5df2\u7ecf\u6251 \u5230\u3002\u8fd9\u4eba\u7ec3\u6709\u201c\u5927\u529b\u9e70\u722a\u529f\u201d\uff0c\u6768\u5a49\u7acb\u8db3\u672a\u7a33\uff0c\u7ed9\u4ed6\u4e00\u6293\u6293\u4e86\u8d77\u6765\uff0c\u53ea\u542c\u5f97\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c \u6768\u5a49\u7684\u201c\u53f7\u886b\u201d\u7ed9\u4ed6\u6495\u88c2\u4e86\u4e00\u5e45\uff0c\u53ef\u662f\u4e5f\u8fd8\u6ca1\u6709\u7ed9\u4ed6\u6293\u4f4f\u3002 \u3000\u3000\u6768\u5a49\u4e00\u62db\u201c\u4e91\u624b\u201d\u8361\u5f00\u4e86\u4ed6\u63a5\u8fde\u800c\u6765\u7684\u4e00\u6293\uff0c\u9752\u5149\u4e00\u95ea\uff0c\u5200\u5df2\u51fa\u9798\uff0c\u5530\u5530\u5530\u8fde\u73af\u4e09\u5200\u903c \u5f97\u8fd9\u6b66\u58eb\u624b\u5fd9\u811a\u4e71\u3002\u6768\u4ee4\u516c\u5f53\u5e74\u4ed7\u7740\u4e00\u67c4\u91d1\u5200\u5efa\u529f\u7acb\u4e1a\uff0c\u53f7\u79f0\u201c\u91d1\u5200\u65e0\u654c\u201d\u3002\u6768\u5a49\u7684\u5200\u6cd5\u51fa \u81ea\u5bb6\u4f20\uff0c\u5b9e\u4e0d\u5728\u5979\u54e5\u54e5\u4ee3\u6388\u7684\u5ce8\u7709\u5251\u6cd5\u4e4b\u4e0b\u3002 \u3000\u3000\u8fd9\u6b66\u58eb\u5fc3\u5934\u4e00\u51db\uff1a\u201c\u60f3\u4e0d\u5230\u8fd9\u5c0f\u5b50\u5012\u662f\u4e2a\u624e\u624b\u7684\u5f3a\u654c\uff01\u201d\u8fde\u5fd9\u5927\u53eb\uff1a\u201c\u4f60\u4eec\u8fd8\u4e0d\u5feb \u4e0a\uff01\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u6768\u5a49\u559d\u58f0\uff1a\u201c\u7740\uff01\u201d\u5200\u950b\u659c\u63a0\uff0c\u5df2\u662f\u628a\u4ed6\u7684\u4e00\u6761\u5de6\u81c2\u5288\u4e86\u4e0b\u6765\u3002\u8fd9\u8fd8\u662f\u6768 \u5a49\u624b\u4e0b\u7559\u60c5\uff0c\u624d\u6ca1\u6709\u65a9\u7834\u4ed6\u7684\u5929\u7075\u76d6\u3002 \u3000\u3000\u6768\u5a49\u5c55\u5f00\u4e86\u8d85\u5353\u7684\u8f7b\u529f\uff0c\u62d4\u8db3\u98de\u5954\uff0c\u9ed1\u591c\u4e4b\u4e2d\uff0c\u53c8\u517c\u521a\u521a\u4e0b\u4e86\u4e00\u573a\u96e8\uff0c\u5730\u9762\u6ce5\u6cde\uff0c\u90a3\u4e9b \u8499\u53e4\u6b66\u58eb\u7a7f\u7740\u9a6c\u9774\uff0c\u54ea\u91cc\u8ffd\u5f97\u4e0a\u5979\uff1f\u4f46\u6b64\u65f6\u53f7\u89d2\u957f\u9e23\uff0c\u5404\u8425\u7684\u5175\u58eb\u5df2\u7ecf\u7eb7\u7eb7\u51fa\u6765\u641c\u6355\u523a\u5ba2\u3002 \u6768\u5a49\u4e0d\u6562\u6df7\u5165\u4eba\u5806\uff0c\u53ea\u597d\u5f80\u65e0\u4eba\u7684\u5c71\u8def\u4e0a\u8dd1\u3002\u8033\u8fb9\u9690\u9690\u542c\u5f97\u9547\u56fd\u738b\u5b50\u53eb\u9053\uff1a\u201c\u5c01\u9501\u5404\u5904\u51fa \u53e3\uff0c\u5404\u8425\u76d8\u67e5\uff0c\u8c05\u8fd9\u523a\u5ba2\u4e5f\u8dd1\u4e0d\u6389\u3002\u201d \u3000\u3000\u6570\u4e07\u5927\u519b\u5c6f\u5728\u5c71\u4e0b\uff0c\u8fde\u8425\u5341\u4f59\u91cc\uff0c\u5c71\u4e0a\u53c8\u662f\u654c\u519b\uff0c\u6768\u5a49\u4e0d\u7981\u6697\u6697\u53eb\u58f0\uff1a\u201c\u82e6\u4e5f\uff01\u201d\u5fc3\u91cc \u60f3\u9053\uff1a\u201c\u770b\u6765\u662f\u8dd1\u4e0d\u51fa\u53bb\u4e86\uff0c\u6211\u7ed9\u6349\u7740\u4e0d\u6253\u7d27\uff0c\u4f46\u5374\u8fde\u7d2f\u4e86\u963f\u76d6\u3002\u53ea\u597d\u627e\u4e2a\u5730\u65b9\u6682\u8eb2\u4e00\u8eb2 \u5427\u3002\u201d \u3000\u3000\u6768\u5a49\u8dd1\u4e0a\u5c71\u5761\uff0c\u94bb\u8fdb\u4e00\u4e2a\u6797\u5b50\uff0c\u7a81\u7136\u53d1\u73b0\u6811\u6797\u4e2d\u4e5f\u6709\u8bb8\u591a\u5e10\u5e55\uff0c\u6768\u5a49\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a \u201c\u60f3\u4e0d\u5230\u8fd9\u91cc\u4e5f\u6709\u519b\u8425\uff0c\u4f46\u4f55\u4ee5\u6ca1\u4eba\u51fa\u6765\u5462\uff1f\u201d \u3000\u3000\u54ea\u77e5\u5fc3\u5ff5\u672a\u5df2\uff0c\u6811\u6797\u91cc\u5df2\u662f\u6709\u4e03\u516b\u4e2a\u4eba\u5411\u5979\u6251\u6765\uff0c\u9f50\u58f0\u559d\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\uff0c\u7ed9\u6211\u7ad9\u7740\uff01\u201d \u4e2a\u4e2a\u58f0\u97f3\u6e05\u8106\uff0c\u539f\u6765\u90fd\u662f\u5973\u5b50\u3002\u6768\u5a49\u672c\u6765\u5c31\u8981\u52a8\u624b\u7684\uff0c\u7a81\u7136\u53d1\u89c9\u6765\u7684\u90fd\u662f\u5973\u5175\uff0c\u4e0d\u89c9\u6115\u7136\u3002 \u3000\u3000\u524d\u9762\u7684\u4e00\u4e2a\u4eae\u4e86\u706b\u6298\uff0c\u671d\u7740\u6768\u5a49\u4e00\u7167\uff0c\u5927\u6012\u9053\uff1a\u201c\u5c82\u6709\u6b64\u7406\uff0c\u539f\u6765\u8fd8\u662f\u4e00\u4e2a\u81ed\u5c0f\u5b50\uff01\u4f60 \u6765\u8fd9\u91cc\u505a\u4ec0\u4e48\uff1f\u8bf4\uff01\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u4e0b\u9762\u53d1\u73b0\u6709\u523a\u5ba2\uff0c\u6211\u662f\u8ffd\u523a\u5ba2\u6765\u7684\u3002\u201d \u3000\u3000\u90a3\u5973\u5175\u9053\uff1a\u201c\u8ffd\u523a\u5ba2\u4e5f\u4e0d\u80fd\u95ef\u5230\u6211\u4eec\u7684\u5973\u8425\u6765\uff0c\u4f60\u662f\u54ea\u4e00\u8425\u7684\uff0c\u96be\u9053\u4e0d\u77e5\u9053\u8fd9\u91cc\u662f\u7981\u5730 \u4e48\uff1f\u201d\u53e6\u4e00\u4e2a\u5973\u5175\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u5b9a\u7136\u4e0d\u6000\u597d\u610f\uff0c\u628a\u4ed6\u7f1a\u8d77\u6765\u518d\u8bf4\u3002\u201d \u3000\u3000\u6b64\u65f6\u4e0b\u9762\u5df2\u662f\u6709\u4eba\u8dd1\u4e0a\u5c71\u6765\uff0c\u8fdc\u8fdc\u5730\u4fbf\u5728\u5927\u58f0\u53eb\u558a\uff1a\u201c\u523a\u5ba2\u9003\u4e0a\u5c71\u4e86\uff01\u5973\u8425\u7684\u59d0\u59b9\u7559 \u795e\uff0c\u522b\u8ba9\u523a\u5ba2\u60ca\u5413\u4e86\u516c\u4e3b\uff01\u201d \u3000\u3000\u4e3a\u9996\u7684\u5973\u5175\u9053\uff1a\u201c\u8fd9\u4e2a\u5c0f\u5b50\u83ab\u975e\u5c31\u662f\u523a\u5ba2\uff1f\u201d\u53e6\u4e00\u4e2a\u5973\u5175\u9053\uff1a\u201c\u53eb\u4e00\u4e2a\u5e05\u5e10\u7684\u6b66\u58eb\u6765\u8ba4 \u8ba4\u5427\u3002\u201d\u4e3a\u9996\u7684\u5973\u5175\u9053\uff1a\u201c\u5bf9\u3002\u54b1\u4eec\u4e5f\u61d2\u5f97\u7406\u8fd9\u95f2\u4e8b\uff0c\u4ea4\u7ed9\u4ed6\u4eec\u5ba1\u95ee\u5c31\u662f\u3002\u201d\u5f53\u4e0b\u62ff\u51fa\u7ef3 \u7d22\uff0c\u5c31\u8981\u6346\u7f1a\u6768\u5a49\u3002 \u3000\u3000\u6768\u5a49\u6b63\u81ea\u6697\u6697\u53eb\u82e6\uff0c\u5ffd\u542c\u5f97\u6709\u4e2a\u719f\u6089\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u5728\u95f9\u4e9b\u4ec0\u4e48\uff1f\u201d\u6768\u5a49\u62ac\u5934\u4e00 \u770b\uff0c\u6765\u7684\u6b63\u662f\u660e\u6167\u516c\u4e3b\u3002 \u3000\u3000\u4e3a\u9996\u7684\u5973\u5175\u9053\uff1a\u201c\u8fd9\u81ed\u5c0f\u5b50\u95ef\u5230\u54b1\u4eec\u8fd9\u513f\u3002\u201d\u5c71\u4e01\u53c8\u6b63\u5728\u95f9\u523a\u5ba2\uff0c\u6211\u4eec\u60f3\u628a\u4ed6\u4ea4\u7ed9\u5143\u5e05 \u5ba1\u95ee\u3002\u201d\u6768\u5a49\u53eb\u9053\uff1a\u201c\u516c\u4e3b\u5f00\u6069\uff0c\u6211\u53ea\u662f\u8bef\u72af\u7981\u5730\uff0c\u5e76\u975e\u523a\u5ba2\u3002\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u4eb2\u81ea\u63d0\u4e86\u4e00\u76cf\u706f\u7b3c\uff0c\u4ed4\u7ec6\u5730\u6253\u91cf\u4e86\u4e00\u4e0b\u6768\u5a49\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u4eba\u7684\u53e3\u97f3\u597d\u719f\uff0c \u597d\u50cf\u662f\u4e2a\u5973\u5b50\uff0c\u6211\u5728\u54ea\u91cc\u542c\u8fc7\u7684\u5462\uff1f\u201d \u3000\u3000\u6768\u5a49\u7684\u53f7\u886b\u521a\u624d\u7ed9\u90a3\u6b66\u571f\u6495\u7834\u4e86\u4e00\u5e45\uff0c\u91cc\u9762\u7a7f\u7684\u4e00\u4ef6\u7c89\u7ea2\u8272\u7684\u886c\u8863\u9732\u4e86\u4e00\u89d2\u3002\u660e\u6167\u516c\u4e3b \u8d8a\u53d1\u8d77\u7591\uff0c\u5fc3\u60f3\uff1a\u201c\u82e5\u662f\u6211\u4eec\u7684\u5175\u58eb\uff0c\u51b3\u4e0d\u4f1a\u7a7f\u8fd9\u79cd\u6c49\u4eba\u7684\u5973\u88c5\u886c\u8863\u3002\u201d\u84e6\u5730\u60f3\u4e86\u8d77\u6765\uff1a \u201c\u83ab\u975e\u5979\u5c31\u662f\u548c\u674e\u601d\u5357\u540c\u5728\u4e00\u8d77\u7684\u90a3\u4e2a\u5973\u5b50\uff1f\u53ef\u662f\u5979\u600e\u7684\u53d8\u6210\u4e86\u8fd9\u4e2a\u6a21\u6837\uff1f\u201d\u8981\u77e5\u660e\u6167\u516c\u4e3b \u90a3\u65e5\u591c\u8352\u8c37\u53d1\u73b0\u674e\u601d\u5357\u548c\u6768\u5a49\u4e4b\u65f6\uff0c\u5979\u662f\u628a\u6768\u5a49\u5f53\u4f5c\u60c5\u654c\u770b\u5f85\u7684\uff0c\u56e0\u6b64\u5bf9\u5979\u7684\u5370\u8c61\u7279\u522b\u6df1 \u523b\u3002\u6768\u5a49\u7684\u9762\u8c8c\u867d\u7136\u6539\u4e86\uff0c\u5979\u8fd8\u8bb0\u5f97\u6768\u5a49\u8bf4\u8bdd\u7684\u58f0\u97f3\u3002 \u3000\u3000\u660e\u6167\u516c\u4e3b\u53c8\u60ca\u53c8\u559c\uff0c\u8fde\u5fd9\u8bf4\u9053\uff1a\u201c\u6211\u8981\u4eb2\u81ea\u5ba1\u95ee\u8fd9\u5c0f\u5b50\uff0c\u4e0d\u8bb8\u4f60\u4eec\u8bf4\u51fa\u53bb\u3002\u201d\u660e\u6167\u516c\u4e3b \u624b\u4e0b\u7684\u5973\u5175\u6697\u6697\u731c\u7591\uff1a\u201c\u83ab\u975e\u54b1\u4eec\u7684\u516c\u4e3b\u770b\u4e0a\u4e86\u8fd9\u5c0f\u5b50\uff1f\u201d\u5979\u4eec\u90fd\u77e5\u9053\u660e\u6167\u516c\u4e3b\u662f\u8ba8\u538c\u9547\u56fd \u738b\u5b50\u7684\uff0c\u5f53\u4e0b\u9f50\u58f0\u8bf4\u9053\uff1a\u201c\u662f\u3002\u516c\u4e3b\u653e\u5fc3\uff0c\u8c01\u95ee\u6211\u4eec\uff0c\u6211\u4eec\u90fd\u8bf4\u4e0d\u77e5\u9053\uff01\u201d \u3000\u3000\u4f59\u4e00\u4e2d\u5e10\u4e2d\u90a3\u4e24\u4e2a\u6b66\u58eb\u5df2\u7ecf\u6765\u5230\uff0c\u4ed6\u4eec\u77e5\u9053\u8fd9\u662f\u7981\u5730\uff0c\u4e0d\u6562\u5165\u5185\uff0c\u53ea\u80fd\u5728\u6797\u5b50\u5916\u9762\u9ad8\u58f0 \u7980\u62a5\uff1a\u201c\u6709\u4e00\u4e2a\u523a\u5ba2\u597d\u50cf\u662f\u5411\u4f60\u4eec\u8fd9\u513f\u8dd1\u6765\uff0c\u4f60\u4eec\u89c1\u7740\u4e86\u6ca1\u6709\uff1f\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u65a5\u9053\uff1a\u201c\u4e09\u66f4\u534a\u591c\u4f60\u4eec\u778e\u80e1\u95f9\u4ec0\u4e48\uff1f\u6211\u6ca1\u6709\u7ed9\u523a\u5ba2\u60ca\u52a8\uff0c\u5012\u662f\u7ed9\u4f60\u4eec\u5435\u9192\u4e86\u3002 \u4f60\u4eec\u90fd\u7ed9\u6211\u6eda\uff01\u201d \u3000\u3000\u90a3\u4e24\u4e2a\u6b66\u58eb\u60f3\u4e0d\u5230\u660e\u6167\u516c\u4e3b\u4eb2\u81ea\u51fa\u6765\u65a5\u8d23\u4ed6\u4eec\uff0c\u5927\u60ca\u4e4b\u4e0b\uff0c\u614c\u4e0d\u8fed\u5730\u8bf4\u9053\uff1a\u201c\u662f\uff0c\u662f\u3002 \u60ca\u52a8\u4e86\u516c\u4e3b\uff0c\u8fd8\u671b\u516c\u4e3b\u6055\u7f6a\u3002\u201d\u5982\u540c\u4e27\u5bb6\u4e4b\u72ac\uff0c\u8d76\u5fd9\u5939\u8d77\u5c3e\u5df4\u5c31\u8dd1\u3002 \u3000\u3000\u660e\u78a7\u516c\u4e3b\u5c06\u6768\u5a49\u5e26\u8ff8\u5979\u7684\u5e10\u5e55\uff0c\u6452\u9000\u5de6\u53f3\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u771f\u4eba\u9762\u524d\u83ab\u8bf4\u5047\u8bdd\uff0c\u4f60\u7a76\u7adf\u662f \u8c01\uff1f\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u8bf7\u8ba9\u6211\u5148\u62b9\u4e00\u628a\u8138\u3002\u201d\u519b\u8425\u4e2d\u7528\u6c34\u751a\u4e3a\u73cd\u8d35\uff0c\u4f46\u5728\u516c\u4e3b\u7684\u5e10\u5e55\u91cc\uff0c\u90a3\u76db\u6c34\u7684 \u767d\u7389\u76d8\u5374\u662f\u4efb\u4f55\u65f6\u5019\u90fd\u76db\u6ee1\u7518\u6cc9\u7684\u3002\u6768\u5a49\u6d17\u8fc7\u4e86\u8138\uff0c\u9732\u51fa\u4e86\u5e90\u5c71\u771f\u76f8\u3002\u6b63\u662f\uff1a \u3000\u3000\u5c71\u7a77\u6c34\u5c3d\u7591\u65e0\u8def\uff0c\u67f3\u6697\u82b1\u660e\u53c8\u4e00\u6751\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b\u2014\u2014\u7b2c\u5341\u4e03\u56de\u3000\u864e\u6005\u6709\u5fc3\u7559\u4fa0\u5973\u3000\u7f81\u56fe\u672a\u9042\u6b92\u5929\u9a84 \u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b \u7b2c\u5341\u4e03\u56de\u3000\u864e\u6005\u6709\u5fc3\u7559\u4fa0\u5973\u3000\u7f81\u56fe\u672a\u9042\u6b92\u5929\u9a84 \u3000\u3000\u660e\u6167\u516c\u4e3b\u53c8\u60ca\u53c8\u559c\uff0c\u7d27\u63e1\u7740\u6768\u5a49\u7684\u624b\u8bf4\u9053\uff1a\u201c\u679c\u7136\u662f\u4f60\uff01\u4f60\u600e\u4e48\u4f1a\u5230\u8fd9\u513f\u6765\u7684\uff1f\u201d \u3000\u3000\u6768\u5a49\u8bf4\u9053\uff1a\u201c\u5b9e\u4e0d\u76f8\u7792\uff0c\u523a\u5ba2\u5c31\u662f\u6211\uff0c\u5982\u4eca\u6211\u843d\u5728\u516c\u4e3b\u624b\u4e2d\uff0c\u4f46\u51ed\u516c\u4e3b\u5904\u7f6e\u3002\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u7b11\u9053\uff1a\u201c\u4f60\u8fd8\u8bb0\u5f97\u5417\uff1f\u6211\u66fe\u7ecf\u9080\u8bf7\u4f60\u6765\u548c\u6797\u4e0e\u6211\u4f5c\u4f34\u7684\uff0c\u90a3\u65f6\u4f60\u4e0d\u80af\u5e94\u5141\u3002\u60f3 \u4e0d\u5230\u4eca\u665a\u4f60\u4e0d\u8bf7\u81ea\u6765\uff0c\u6211\u662f\u6b22\u559c\u8fd8\u6765\u4e0d\u53ca\u5462\uff0c\u600e\u4f1a\u4f24\u5bb3\u4f60\uff1f\u4f60\u653e\u5fc3\u597d\u4e86\uff01\u201d \u3000\u3000\u6768\u5a49\u8fd8\u4e86\u4e00\u793c\uff0c\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u516c\u4e3b\u4ec1\u6148\uff0c\u6211\u613f\u610f\u4e3a\u5974\u4e3a\u5a62\uff0c\u670d\u4f8d\u516c\u4e3b\u3002\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u9053\uff1a\u201c\u597d\u59d0\u59d0\uff0c\u522b\u8bf4\u8fd9\u6837\u7684\u8bdd\u3002\u54b1\u4eec\u603b\u7b97\u662f\u2018\u5b89\u7b54\u2019\u4e86\uff0c\u6211\u600e\u80fd\u4ee4\u4f60\u53d7\u5230\u59d4 \u5c48\uff1f\u4f60\u7559\u5728\u6211\u8fd9\u513f\uff0c\u628a\u6211\u5f53\u4f5c\u59d0\u59b9\u5982\u4f55\uff1f\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u516c\u4e3b\u91d1\u679d\u7389\u53f6\uff0c\u6211\u9ad8\u6500\u4e0d\u8d77\u3002\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u82e6\u7b11\u9053\uff1a\u201c\u6211\u662f\u8bda\u610f\u548c\u4f60\u7ed3\u4ea4\u7684\uff0c\u4f60\u4e0d\u80af\u5e94\u627f\uff0c\u90a3\u5c31\u662f\u770b\u6211\u4e0d\u8d77\u4e86\u3002\u201d \u3000\u3000\u6768\u5a49\u8fd9\u6b21\u8feb\u4e8e\u65e0\u5948\uff0c\u5728\u660e\u6167\u516c\u4e3b\u9762\u524d\u9732\u51fa\u539f\u6765\u8eab\u4efd\uff0c\u5fc3\u4e2d\u5176\u5b9e\u8fd8\u662f\u6709\u70b9\u60f4\u60f4\u4e0d\u5b89\u7684\u3002\u5982 \u4eca\u770b\u89c1\u660e\u6167\u516c\u4e3b\u7684\u786e\u4f3c\u662f\u5177\u6709\u8bda\u610f\uff0c\u5fc3\u4e0a\u7684\u4e00\u5757\u77f3\u5934\u624d\u843d\u5730\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u963f\u76d6\u867d\u7136\u5904\u5904\u7167 \u987e\u6211\uff0c\u4f46\u6211\u4e00\u4e2a\u5355\u8eab\u5973\u5b50\uff0c\u6df7\u5728\u7537\u4eba\u5806\u91cc\uff0c\u603b\u662f\u4e0d\u4fbf\u3002\u201d\u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u516c\u4e3b\u539a\u610f\uff0c\u5728\u6211 \u65e0\u8def\u53ef\u6295\u4e4b\u9645\uff0c\u8ba9\u6211\u6258\u5e87\u5e10\u4e0b\u3002\u4f46\u59d0\u59b9\u76f8\u79f0\uff0c\u6211\u5374\u662f\u4e0d\u6562\u5f53\u7684\u3002\u800c\u4e14\u522b\u4eba\u542c\u89c1\u4e86\uff0c\u4e5f\u4f1a\u5f15\u8d77 \u95f2\u8bdd\u3002\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u60f3\u4e86\u4e00\u60f3\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u5c31\u540d\u4e49\u4e0a\u59d4\u5c48\u4f60\u4f5c\u6211\u7684\u4f8d\u5973\uff0c\u79c1\u5e95\u4e0b\u54b1\u4eec\u8fd8\u662f\u59d0\u59b9\u76f8 \u79f0\uff0c\u4f60\u4e0d\u5fc5\u518d\u5ba2\u6c14\u4e86\u3002\u201d\u8bf4\u7f62\uff0c\u548c\u6768\u5a49\u5e76\u80a9\u4e00\u7ad9\uff0c\u7b11\u9053\uff1a\u201c\u54b1\u4eec\u7684\u8eab\u6750\u5012\u5dee\u4e0d\u591a\uff0c\u4f60\u6362\u4e0a\u6211 \u7684\u8863\u88f3\u8bd5\u8bd5\u3002\u6211\u8fd8\u6709\u8bdd\u8981\u548c\u4f60\u8bf4\u5462\u3002\u201d \u3000\u3000\u6768\u5a49\u6362\u4e86\u8863\u88f3\uff0c\u63ed\u5f00\u73e0\u5e18\uff0c\u53ea\u89c1\u660e\u6167\u516c\u4e3b\u5728\u5e10\u4e2d\u5f98\u5f8a\u987e\u5f71\uff0c\u82e5\u6709\u6240\u601d\uff0c\u6768\u5a49\u5fc3\u91cc\u6697\u7b11\uff1a \u201c\u60f3\u6765\u5979\u8fd8\u662f\u5fd8\u4e0d\u4e86\u5357\u54e5\u3002\u201d \u3000\u3000\u679c\u7136\u4fbf\u542c\u5f97\u660e\u6167\u516c\u4e3b\u95ee\u9053\uff1a\u201c\u6768\u59d0\u59d0\uff0c\u4f60\u4e0d\u662f\u548c\u674e\u601d\u5357\u5728\u4e00\u8d77\u7684\u5417\uff1f\u4ed6\u5374\u53c8\u5230\u54ea\u91cc\u53bb \u4e86\uff1f\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u6211\u4e5f\u6b63\u5728\u627e\u4ed6\u3002\u201d\u5f53\u4e0b\u5c06\u4e0e\u674e\u601d\u5357\u5931\u6563\u7684\u7ecf\u8fc7\uff0c\u4ee5\u53ca\u81ea\u5df1\u7684\u906d\u9047\uff0c\u539f\u539f\u672c\u672c \u5730\u544a\u8bc9\u660e\u6167\u516c\u4e3b\u3002 \u3000\u3000\u660e\u6167\u516c\u4e3b\u6005\u7136\u8bf4\u9053\uff1a\u201c\u8fd9\u771f\u662f\u610f\u60f3\u4e0d\u5230\u7684\u4e8b\u3002\u4f46\u6709\u4e00\u70b9\u4f60\u53ef\u4ee5\u653e\u5fc3\uff0c\u4f60\u542c\u5230\u7684\u90a3\u4e2a\u6d88\u606f \u7edd\u5bf9\u662f\u4e2a\u8c23\u8a00\uff0c\u54f2\u522b\u6211\u89c1\u8fc7\u4e86\uff0c\u4ed6\u6839\u672c\u6ca1\u6709\u627e\u7740\u674e\u601d\u5357\uff0c\u5c04\u6b7b\u674e\u601d\u5357\u7684\u4e8b\uff0c\u5f53\u7136\u66f4\u662f\u5b50\u865a\u4e4c \u6709\u4e86\u3002\u201d \u3000\u3000\u672c\u6765\u963f\u76d6\u4e5f\u66fe\u5bf9\u6768\u5a49\u8bf4\u8fc7\u674e\u601d\u5357\u6ca1\u6709\u88ab\u6355\uff0c\u4e0d\u8fc7\u963f\u76d6\u53ea\u662f\u636e\u7406\u63a8\u6d4b\uff0c\u5982\u4eca\u4ece\u660e\u6167\u516c\u4e3b\u53e3 \u4e2d\u8bf4\u51fa\uff0c\u5374\u662f\u6700\u6709\u529b\u7684\u8bc1\u636e\u3002\u6768\u5a49\u5fc3\u4e2d\u65e0\u9650\u6b22\u559c\uff0c\u60f3\u9053\uff1a\u201c\u53ea\u8981\u5357\u54e5\u8fd8\u5728\u4eba\u95f4\uff0c\u6211\u518d\u53d7\u591a\u6b64 \u82e6\uff0c\u90a3\u4e5f\u7b97\u4e0d\u4e86\u4ec0\u4e48\u3002\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u9053\uff1a\u201c\u4f60\u4e00\u4e2a\u5b64\u8eab\u5973\u5b50\uff0c\u6df7\u5165\u767e\u4e07\u519b\u4e2d\uff0c\u884c\u523a\u4ec7\u4eba\uff0c\u52c7\u6c14\u5b9e\u662f\u4ee4\u4eba\u656c\u4f69\u3002\u4f46\u8fd9 \u4e5f\u672a\u514d\u592a\u5192\u9669\u4e86\uff01\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u4f59\u4e00\u4e2d\u8fd9\u53ae\u4e27\u5c3d\u5929\u826f\uff0c\u4ed6\u4e0d\u4f46\u662f\u9677\u5bb3\u674e\u601d\u5357\u7239\u7239\u7684\u4ec7\u4eba\uff0c\u4e5f\u662f\u6740\u5bb3\u6211\u54e5\u54e5\u7684 \u51f6\u624b\u3002\u6211\u4eec\u4e0e\u4ed6\u6709\u4e0d\u5171\u6234\u5929\u4e4b\u4ec7\uff0c\u4eca\u665a\u6211\u51b3\u610f\u884c\u523a\u4ed6\u7684\u65f6\u5019\uff0c\u5df2\u662f\u628a\u751f\u6b7b\u7f6e\u4e4b\u5ea6\u5916\uff0c\u4e5f\u5c31\u7b97 \u4e0d\u5f97\u4ec0\u4e48\u5192\u9669\u4e0d\u5192\u9669\u4e86\uff01\u201d\u660e\u6167\u516c\u4e3b\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u8fd8\u60f3\u4e0d\u60f3\u62a5\u4ec7\uff1f\u201d \u3000\u3000\u6768\u5a49\u54ac\u7259\u9053\uff1a\u201c\u4f46\u6559\u4e09\u5bf8\u6c14\u5728\uff0c\u8fdf\u65e9\u4e5f\u8981\u62a5\u4ec7\u3002\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u7b11\u9053\uff1a\u201c\u4f46\u6211\u53ef\u4e0d\u60f3\u4f60\u62ff\u6027\u547d\u53bb\u6362\u53d6\u4ec7\u4eba\u7684\u9996\u7ea7\uff0c\u8fd9\u4e8b\u4f60\u4ea4\u7ed9\u6211\u5427\uff0c\u65e9\u5219\u5341 \u5929\uff0c\u8fdf\u5219\u534a\u6708\uff0c\u6211\u628a\u4f59\u4e00\u4e2d\u7684\u9996\u7ea7\u5949\u9001\u7ed9\u4f60\uff01\u201d \u3000\u3000\u6768\u5a49\u534a\u4fe1\u534a\u7591\uff0c\u8bf4\u9053\uff1a\u201c\u53ef\u662f\u4ed6\u662f\u526f\u5143\u5e05\u5462\uff01\u201d\u5fc3\u60f3\u660e\u6167\u516c\u4e3b\u867d\u7136\u662f\u4e2a\u4e3b\u5b50\uff0c\u4f46\u8981\u6740\u519b \u4e2d\u7684\u5927\u5c06\uff0c\u6050\u6015\u4e5f\u6ca1\u6709\u8fd9\u7965\u7684\u6743\u529b\u5427\uff1f \u3000\u3000\u660e\u6167\u516c\u4e3b\u7b11\u9053\uff1a\u201c\u4ed6\u8fd9\u4e2a\u526f\u5143\u5e05\u662f\u6211\u7239\u7239\u7ed9\u4ed6\u7684\uff0c\u6211\u7239\u7239\u53ef\u4ee5\u8ba9\u4ed6\u505a\u5b98\uff0c\u4e5f\u5c31\u53ef\u4ee5\u6740 \u4ed6\u3002\u6211\u7239\u7239\u5c31\u8981\u6765\u4e86\uff0c\u5230\u65f6\u6211\u5c31\u62c6\u7a7f\u4f59\u4e00\u4e2d\u5192\u540d\u9876\u66ff\u7684\u4e8b\u60c5\uff0c\u770b\u6211\u7239\u7239\u80fd\u4e0d\u80fd\u5bb9\u4ed6\uff1f\u201d \u3000\u3000\u6768\u5a49\u60f3\u9053\uff1a\u201c\u5979\u662f\u6210\u5409\u601d\u6c57\u6700\u5ba0\u7231\u7684\u5c0f\u5973\u513f\uff0c\u5979\u82e5\u662f\u5728\u7236\u4eb2\u9762\u524d\u6267\u610f\u8981\u6740\u4f59\u4e00\u4e2d\u7684\u8bdd\uff0c \u603b\u4f1a\u6709\u4e03\u516b\u5206\u628a\u63e1\u3002\u8fd9\u6837\uff0c\u6211\u867d\u7136\u4e0d\u80fd\u624b\u5203\u4ec7\u4eba\uff0c\u4e5f\u603b\u7b97\u662f\u62a5\u4ec7\u4e86\u3002\u201d\u4e8e\u662f\u53cc\u819d\u5c48\u4e0b\uff0c\u8bf4 \u9053\uff1a\u201c\u516c\u4e3b\u8fd9\u6837\u4e3a\u6211\u5c3d\u5fc3\u5c3d\u529b\uff0c\u6211\u771f\u4e0d\u77e5\u5982\u4f55\u624d\u80fd\u62a5\u7b54\u4f60\u7684\u5927\u6069\uff1f\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u628a\u5979\u6276\u8d77\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u53c8\u6765\u4e86\uff0c\u4f60\u6211\u65e2\u7136\u662f\u4ee5\u59d0\u59b9\u76f8\u79f0\uff0c\u4f60\u7684\u4e8b\u60c5\u4e0d\u4e5f\u5c31\u662f\u6211 \u7684\u4e8b\u60c5\u5417\uff1f\u4f55\u51b5\u73b0\u5728\u5c1a\u672a\u6210\u4e8b\uff0c\u5f85\u5272\u53d6\u4e86\u4f59\u4e00\u4e2d\u7684\u9996\u7ea7\uff0c\u4f60\u518d\u5411\u6211\u9053\u8c22\u4e5f\u8fd8\u4e0d\u8fdf\u3002\u201d \u3000\u3000\u7b2c\u4e8c\u5929\uff0c\u679c\u7136\u5c31\u6709\u4e86\u5feb\u9a6c\u4f20\u6765\u7684\u6d88\u606f\uff0c\u8bf4\u662f\u6210\u5409\u601d\u6c57\u201c\u5fa1\u9a7e\u4eb2\u5f81\u201d\uff0c\u5e26\u9886\u4e86\u201c\u795e\u7ffc\u8425\u201d \u4ece\u897f\u590f\u51fa\u53d1\uff0c\u4e03\u65e5\u4e4b\u5185\uff0c\u5c31\u5c06\u5230\u8fbe\u3002 \u3000\u3000\u9547\u56fd\u738b\u5b50\u548c\u4f59\u4e00\u4e2d\u641c\u5bfb\u4e0d\u5230\u523a\u5ba2\uff0c\u5f53\u7136\u662f\u53c8\u60ca\u53c8\u6012\uff0c\u4f46\u4e5f\u65e0\u53ef\u5948\u4f55\u3002\u641c\u67e5\u523a\u5ba2\u867d\u7136\u7d27 \u8981\uff0c\u53ef\u6210\u5409\u601d\u6c57\u8981\u6765\u201c\u5fa1\u9a7e\u4eb2\u5f81\u201d\u7684\u4e8b\u60c5\u66f4\u4e3a\u7d27\u8981\uff0c\u6210\u5409\u601d\u6c57\u6d3e\u6765\u7684\u4f7f\u8005\u5df2\u7ecf\u4f20\u8fbe\u4e86\u4ed6\u7684\u610f \u65e8\uff0c\u4ed6\u5e0c\u671b\u5728\u4ed6\u6765\u5230\u4e4b\u65f6\uff0c\u80fd\u591f\u7acb\u9a6c\u5728\u516d\u76d8\u5c71\u4e0a\u3002\u6362\u53e5\u8bdd\u8bf4\uff0c\u5c31\u662f\u8981\u4ed6\u4eec\u5728\u4e03\u5929\u4e4b\u5185\uff0c\u4e00\u5b9a \u8981\u653b\u4e0b\u516d\u76d8\u5c71\u4e86\u3002 \u3000\u3000\u9547\u56fd\u738b\u5b50\u548c\u4f59\u4e00\u4e2d\u53ea\u597d\u628a\u641c\u67e5\u523a\u5ba2\u7684\u4e8b\u6682\u5df2\u6401\u8fc7\u4e00\u8fb9\uff0c\u52a0\u7d27\u8fdb\u884c\u653b\u5360\u516d\u76d8\u5c71\u7684\u519b\u4e8b\u8ba1 \u5212\u3002\u5f53\u7136\u4ed6\u4eec\u4e3a\u4e86\u63d0\u9632\u523a\u5ba2\u518d\u6765\uff0c\u9632\u536b\u4e5f\u662f\u52a0\u5f3a\u4e86\u3002 \u3000\u3000\u516d\u76d8\u5c71\u7684\u91d1\u56fd\u5b88\u519b\u5df2\u88ab\u8e1e\u56f0\u591a\uff0c\u7cae\u98df\u672a\u7aed\uff0c\u4f46\u6c34\u6e90\u5207\u65ad\uff0c\u6bd4\u7f3a\u7cae\u8fd8\u66f4\u96be\u6328\u3002\u5728\u8499\u53e4\u5175\u731b \u653b\u4e4b\u4e0b\uff0c\u91d1\u519b\u7684\u526f\u5c06\u6740\u4e86\u4e3b\u5e05\u80e1\u6c99\u864e\uff0c\u7ec8\u4e8e\u5728\u7b2c\u4e94\u5929\u5c31\u5ba3\u544a\u4e86\u6295\u964d\u3002 \u3000\u3000\u653b\u5360\u4e86\u516d\u76d8\u5c71\u7684\u7b2c\u4e8c\u5929\uff0c\u660e\u6167\u516c\u4e3b\u6b63\u5728\u5e10\u4e2d\u548c\u6768\u5a49\u95f2\u804a\uff0c\u5ffd\u6709\u5973\u5175\u8fdb\u6765\u62a5\u9053\uff1a\u6210\u5409\u601d\u6c57 \u5df2\u7ecf\u6765\u5230\uff0c\u6bd4\u5979\u4eec\u9884\u6599\u7684\u6765\u65e9\u4e86\u4e00\u5929\u3002 \u3000\u3000\u660e\u6167\u516c\u4e3b\u5927\u559c\u9053\uff1a\u201c\u5927\u6c57\u73b0\u5728\u4f55\u5904\uff1f\u5973\u5175\u9053\uff1a\u201c\u5143\u5e05\u5230\u516d\u76d8\u5c71\u53bb\u4e86\u3002\u201d\u660e\u6167\u516c\u4e3b\u53f9\u9053\uff1a \u201c\u7239\u7239\u603b\u662f\u8bb0\u6302\u7740\u6253\u4ed7\u7684\u4e8b\u60c5\uff0c\u96be\u5f97\u4e00\u53d9\u5929\u8bba\u4e4b\u4e50\u3002\u201d\u5f53\u4e0b\u4fbf\u53eb\u5973\u5175\u5907\u9a6c\uff0c\u56de\u8fc7\u5934\u6765\u5bf9\u6768\u5a49 \u8bf4\u9053\uff1a\u201c\u4f60\u5728\u5e10\u4e2d\u9759\u5019\u597d\u97f3\uff0c\u6211\u8fd9\u6b21\u89c1\u4e86\u7239\u7239\uff0c\u4e00\u5b9a\u8981\u628a\u4f59\u4e00\u4e2d\u7684\u4e8b\u60c5\u548c\u76d8\u6258\u51fa\u3002\u201d \u3000\u3000\u4e14\u8bf4\u6210\u5409\u601d\u6c57\u4ece\u897f\u590f\u5306\u5306\u8d76\u6765\uff0c\u542c\u8bf4\u516d\u76d8\u5c71\u5df2\u7ecf\u653b\u4e0b\uff0c\u5341\u5206\u9ad8\u5174\uff0c\u4eba\u672a\u79bb\u9791\uff0c\u987e\u4e0d\u5f97\u75b2 \u52b3\uff0c\u4fbf\u5e26\u4e86\u4e00\u9762\u519b\u65d7\uff0c\u5728\u9547\u56fd\u738b\u5b50\u966a\u540c\u4e4b\u4e0b\u7b56\u9a6c\u767b\u5c71\uff0c\u4ed6\u8981\u4eb2\u624b\u628a\u7b2c\u4e00\u9762\u519b\u65d7\u63d2\u5728\u516d\u76d8\u5c71 \u9876\u3002 \u3000\u3000\u8bf8\u5c06\u8fce\u5408\u6210\u5409\u601d\u6c57\u597d\u5927\u559c\u529f\u7684\u5fc3\u7406\uff0c\u8c01\u90fd\u4e0d\u6562\u8d8a\u8fc7\u4ed6\u7684\u524d\u9762\uff0c\u5c71\u4e0a\u88ab\u4fd8\u7684\u91d1\u56fd\u5b98\u5175\uff0c\u65e9 \u5df2\u62bc\u81f3\u5c71\u4e0b\uff0c\u7a7a\u51fa\u4e86\u5730\u65b9\uff0c\u4ee5\u4f9b\u6210\u5409\u601d\u6c57\u9a70\u9a8b\u3002\u9547\u56fd\u738b\u5b50\u4e0d\u5373\u4e0d\u79bb\u7684\u8ddf\u5728\u540e\u9762\uff0c\u9065\u4e3a\u4fdd\u62a4\uff0c \u5c71\u4e0a\u5e76\u65e0\u654c\u4eba\uff0c\u81ea\u662f\u4e0d\u5fe7\u6709\u4eba\u884c\u523a\u3002 \u3000\u3000\u6210\u5409\u601d\u6c57\u4e0a\u5230\u534a\u5c71\uff0c\u8c6a\u5174\u5927\u53d1\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u80e1\u6c99\u864e\u662f\u91d1\u56fd\u7684\u7b2c\u4e00\u5458\u6218\u5c06\uff0c\u516d\u76d8\u5c71\u7684\u96c4 \u5173\u662f\u91d1\u56fd\u6700\u9669\u8981\u7684\u95e8\u6237\uff0c\u5982\u4eca\u80e1\u6c99\u864e\u5df2\u6b7b\uff0c\u91d1\u56fd\u7684\u95e8\u6237\u4e5f\u5df2\u88ab\u6211\u4eec\u6253\u5f00\uff0c\u4e2d\u539f\u7684\u9526\u7ee3\u6cb3\u5c71\uff0c \u770b\u6765\u662f\u553e\u624b\u53ef\u5f97\u4e86\uff01\u201d \u3000\u3000\u8bf8\u5c06\u4e3a\u4e86\u51d1\u5174\uff0c\u9f50\u58f0\u5531\u8d77\u9882\u6b4c\u548c\u6218\u6b4c\uff1a \u3000\u3000\u201c\u6211\u7684\u4e07\u4f17\u5723\u4e3b\u2014\u2014 \u3000\u3000\u6210\u5409\u601d\u6c57\uff1a \u3000\u3000\u4e0a\u5929\u8d50\u7ed9\u4f60\u8d85\u4eba\u7684\u529b\u6c14\uff0c \u3000\u3000\u767e\u6b65\u7a7f\u6768\u7684\u7bad\uff0c \u3000\u3000\u4f7f\u9003\u9038\u7684\u767e\u59d3\uff0c\u5c48\u670d\u6295\u964d\uff1b \u3000\u3000\u767e\u53d1\u767e\u4e2d\u7684\u7bad\uff0c \u3000\u3000\u4f7f\u6e83\u9003\u7684\u53db\u4f17\uff0c\u7f34\u67aa\u6295\u964d\u3002 \u3000\u3000\u8bf4\u5230\u7684\u5730\u65b9\u5c31\u5230\uff0c \u3000\u3000\u53bb\u628a\u575a\u77f3\u7c89\u788e\uff0c \u3000\u3000\u8bf4\u653b\u7684\u5730\u65b9\u5c31\u653b\u3002 \u3000\u3000\u5360\u628a\u786c\u5ca9\u6363\u6bc1\uff1b \u3000\u3000\u51f8\u82db\u5c71\u52aa\u5f00\uff0c \u3000\u3000\u628a\u6df1\u6c34\u65ad\u6db8\uff0c \u3000\u3000\u8fd9\u6837\u52c7\u6562\u7684\u6740\u654c\u3002\u201d \u3000\u3000\u9547\u56fd\u738b\u5b50\u4e58\u673a\u62cd\u9a6c\uff0c\u8bf4\u9053\uff1a\u201c\u5c82\u53ea\u4e2d\u539f\u7684\u9526\u7ee3\u6cb3\u5c71\uff0c\u6709\u5927\u6c57\u7387\u9886\u6211\u4eec\uff0c\u5168\u4e16\u754c\u90fd\u8981\u53d8\u6210 \u54b1\u4eec\u8499\u53e4\u4eba\u7684\u7267\u573a\u4e86\u3002\u201d \u3000\u3000\u6210\u53f0\u601d\u6c57\u5f97\u610f\u975e\u51e1\uff0c\u7b56\u9a6c\u626c\u97ad\uff0c\u54c8\u54c8\u5927\u7b11\u3002 \u3000\u3000\u4e0d\u6599\u6218\u6b4c\u65a5\u7ec8\uff0c\u7b11\u58f0\u672a\u6b47\uff0c\u6210\u53f0\u601d\u6c57\u7684\u6218\u9a6c\uff0c\u7a81\u7136\u5de7\u5c16\u524d\u8ddf\uff0c\u628a\u6210\u5409\u601d\u6c57\u6389\u4e86\u4e0b\u6765\uff0c\u8fde \u65e5\u9716\u96e8\uff0c\u5c71\u8def\u6e7f\u6ed1\uff0c\u6210\u53f0\u601d\u8ba1\u6bd5\u7adf\u662f\u4e2a\u5c06\u8fd1\u4e03\u5341\u5c81\u7684\u8001\u4eba\u4e86\uff0c\u6454\u5012\u4e4b\u540e\uff0c\u7adf\u7136\u722c\u4e0d\u8d77\u6765\uff0c\u9aa8 \u788c\u788c\u5730\u76f4\u6eda\u4e0b\u53bb\u3002 \u3000\u3000\u8bf4\u4ec0\u4e48\u201c\u8d85\u4eba\u7684\u529b\u6c14\u201d\uff0c\u8bf4\u4ec0\u4e48\u201c\u5168\u80fd\u7684\u5723\u4e3b\u201d\uff0c\u201c\u9882\u6b4c\u201d\u548c\u201c\u6218\u6b4c\u201d\u5728\u6b64\u65f6\u54cd\u5f7b\u4e91 \u9704\uff0c\u6070\u597d\u53d8\u6210\u4e86\u5bf9\u6210\u5409\u601d\u6c57\u7684\u8bbd\u523a\uff01 \u3000\u3000\u9547\u56fd\u738b\u5b50\u614c\u5fd9\u628a\u4ed6\u6276\u8d77\uff0c\u53ef\u601c\u6210\u53f3\u601d\u6c57\u5df2\u8840\u67d3\u5f81\u888d\uff0c\u8bf4\u4e0d\u51fa\u8bdd\uff01 \u3000\u3000\u660e\u6167\u516c\u4e3b\u6b64\u65f6\u6b63\u5728\u534a\u5c71\u5761\uff0c\u770b\u89c1\u6210\u5409\u601d\u6c57\u6454\u4e0b\u9a6c\uff0c\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff01\u8fde\u5fd9\u8d76\u6765\uff0c\u53eb\u58eb\u5175 \u7acb\u5373\u5728\u5761\u4e0a\u642d\u8d77\u7bf7\u5e10\uff0c\u628a\u7236\u4eb2\u6276\u5165\u5e10\u4e2d\u4f11\u606f\u3002\u4e0d\u4e45\uff0c\u5fa1\u533b\u4ea6\u5df2\u8d76\u5230\uff0c\u66ff\u6210\u5409\u601d\u6c57\u9a73\u597d\u65ad\u9aa8\uff0c \u6577\u4e86\u4e0a\u597d\u7684\u91d1\u521b\u836f\u3002 \u3000\u3000\u6210\u5409\u601d\u6c57\u7741\u5f00\u773c\u775b\uff0c\u7b2c\u4e00\u773c\u5c31\u770b\u89c1\u7231\u5973\u5728\u5730\u9762\u524d\uff0c\u6210\u5409\u601d\u6c57\u5fae\u7b11\u9053\uff1a\u201c\u5413\u574f\u4f60\u4e86\u5427\uff1f\u201d \u660e\u6167\u516c\u4e3b\u9053\uff1a\u201c\u7239\u7239\uff0c\u4f60\u600e\u4e48\u4e86\uff1f\u201d\u6210\u5409\u601d\u6c57\u9053\uff1a\u201c\u6211\u53d7\u547d\u4e8e\u5929\uff0c\u8981\u505a\u5929\u4e0b\u4e07\u56fd\u7684\u5927\u6c57\uff0c\u5929 \u4e0b\u672a\u66fe\u5f81\u670d\uff01\u6211\u600e\u80fd\u6b7b\u6389\u3002\u201d\u8bf4\u7f62\u54c8\u54c8\u5927\u8299\uff0c\u4e0d\u6599\u8fd9\u4e00\u7b11\u7275\u52a8\u4e86\u521b\u4f24\uff0c\u4f24\u53e3\u590d\u88c2\uff0c\u628a\u6210\u5409\u601d \u6c57\u75db\u5f97\u6b7b\u53bb\u6d3b\u6765\u3002\u6210\u5927\u601d\u6c57\u5f3a\u5fcd\u7740\u75db\uff0c\u4e5f\u7ec8\u4e8e\u4e5f\u518d\u80fd\u4e0d\u559f\u7136\u53f9\u9053\uff1a\u201c\u6211\u7a76\u7adf\u662f\u8001\u4e86\u3002\u201d\u2014\u2014 \u660e\u6167\u516c\u4e3b\u9053\uff1a\u201c\u7239\uff0c\u4f60\u8981\u597d\u597d\u6b47\u4e00\u4f1a\u513f\u5427\uff1f\u201d\u6b64\u65f6\u5e10\u4e2d\u6324\u6ee1\u4e86\u4eba\uff0c\u540e\u9762\u7684\u4eba\u770b\u4e0d\u89c1\u6210\u8d70\u601d \u6c57\uff0c\u7eb7\u7eb7\u63a2\u95ee\u4f24\u52bf\uff0c\u6210\u5409\u601d\u6c57\u76b1\u8d77\u7709\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u8fd8\u6ca1\u6709\u6b7b\u5462\uff0c\u4f60\u4eec\u5728\u8fd9\u91cc\u5435\u4ec0\u4e48\uff0c\u90fd\u7ed9 \u6211\u6eda\u51fa\u53bb\uff01\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u597d\u8a00\u9053\uff1a\u201c\u5927\u6c57\u8981\u6e05\u6e05\u9759\u9759\u5730\u6b47\u4e00\u4f1a\uff0c\u8fd9\u91cc\u6709\u6211\u670d\u4f8d\u5c31\u591f\u4e86\u3002\u4f60\u4eec\u660e\u5929\u518d\u6765 \u5427\u3002\u201d \u3000\u3000\u8bf8\u5c06\u9646\u7eed\u9000\u51fa\uff0c\u6700\u540e\u53ea\u5269\u4e0b\u9547\u56fd\u738b\u5b50\u548c\u660e\u6167\u516c\u4e3b\u3002\u660e\u6167\u516c\u4e3b\u4e00\u77aa\u773c\u775b\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4e5f\u51fa \u53bb\uff01\u201d \u3000\u3000\u9547\u56fd\u738b\u5b50\u5446\u4e86\u4e00\u5446\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u3001\u6211\u90fd\u4e0d\u80fd\u7559\u4e0b\u6765\u5417\uff1f\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u9053\uff1a\u201c\u7239\u7239\u5429\u5490\u7684\uff0c\u53eb\u4f60\u4eec\u90fd\u6eda\u51fa\u53bb\uff0c\u4f60\u4e5f\u4e0d\u80fd\u4f8b\u5916\uff01\u201d \u3000\u3000\u6210\u5409\u601d\u6c57\u6447\u4e86\u6447\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u5509\uff0c\u4f60\u4eec\u600e\u4e48\u8001\u662f\u4e00\u89c1\u9762\u5c31\u5435\u67b6\uff0c\u628a\u6211\u70e6\u6b7b\u4e86\uff01\u597d\u5427\uff0c\u9547 \u56fd\uff0c\u4f60\u5c31\u51fa\u53bb\u5427\u3002\u201d\u6210\u5409\u601d\u6c57\u672c\u6765\u662f\u60f3\u7559\u4e0b\u9547\u56fd\u738b\u5b50\u7684\uff0c\u4f46\u89c1\u5973\u513f\u4e0d\u559c\u6b22\uff0c\u5fc3\u91cc\u6015\u70e6\uff0c\u53ea\u597d \u8ba9\u660e\u6167\u516c\u4e3b\u5355\u72ec\u7559\u4e0b\u3002 \u3000\u3000\u9547\u56fd\u738b\u5b50\u9ed1\u8138\u6cdb\u7ea2\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4f60\u559c\u6b22\u6211\u4e5f\u7f62\uff0c\u4e0d\u559c\u6b22\u6211\u4e5f\u7f62\uff0c\u4f60\u603b\u662f\u6211\u7684\u4eba\u4e86\u3002\u73b0 \u5728\u6682\u4e14\u8ba9\u4f60\uff0c\u5f85\u4f60\u505a\u4e86\u6211\u7684\u59bb\u5b50\uff0c\u770b\u4f60\u8fd8\u6562\u518d\u51f6\uff1f\u201d \u3000\u3000\u6210\u5409\u601d\u6c57\u95ed\u76ee\u517b\u795e\uff0c\u4e0d\u77e5\u4e0d\u89c9\u5730\u5c31\u7761\u7740\u4e86\u3002\u660e\u6167\u516c\u4e3b\u4e0d\u6562\u60ca\u52a8\u4ed6\uff0c\u5fc3\u4e2d\u6697\u81ea\u76d8\u7b97\u4e24\u4ef6\u4e8b \u60c5\uff0c\u7b2c\u4e00\u4ef6\u662f\u8981\u7236\u4eb2\u6740\u4f59\u4e00\u4e2d\uff0c\u7b2c\u4e8c\u4ef6\u662f\u5979\u4e0d\u613f\u610f\u5ac1\u7ed9\u9547\u56fd\u738b\u5b50\uff0c\u5e0c\u671b\u7236\u4eb2\u7b54\u5141\u3002\u5728\u660e\u6167\u516c \u4e3b\u7684\u5fc3\u76ee\u4e2d\uff0c\u8ba4\u4e3a\u7b2c\u4e00\u4ef6\u4e8b\u5927\u7ea6\u4e0d\u96be\uff0c\u7b2c\u4e8c\u4ef6\u4e8b\u5c31\u6050\u6015\u8981\u5927\u8d39\u5507\u820c\u4e86\u3002 \u3000\u3000\u6210\u5409\u601d\u6c57\u5c0f\u7761\u4e86\u4e24\u4e2a\u65f6\u8fb0\uff0c\u65b9\u59cb\u9192\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u963f\u9791\u6d77\u522b\u59ec\uff0c\u4f60\u8fd8\u6ca1\u8d70\uff1f\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u9053\uff1a\u201c\u6211\u8d70\u4e86\u8c01\u670d\u4f8d\u4f60\uff1f\u4f60\u7684\u624b\u4e0b\u867d\u591a\uff0c\u4f46\u6211\u77e5\u9053\u4f60\u4e0d\u4f1a\u559c\u6b22\u4ed6\u4eec\u670d\u4f8d\u4f60 \u7684\u3002\u201d \u3000\u3000\u6210\u5409\u601d\u6c57\u7b11\u9053\uff1a\u201c\u6bd5\u7adf\u662f\u4f60\u6700\u771f\u5fc3\u75bc\u6211\u3002\u6211\u662f\u6700\u559c\u6b22\u4f60\uff0c\u4f46\u6211\u4e0d\u613f\u4f60\u7d2f\u574f\u4e86\uff0c\u5929\u5c31\u8981\u9ed1 \u4e86\uff0c\u4f60\u4e5f\u56de\u53bb\u65e9\u70b9\u6b47\u606f\u5427\u3002\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u6447\u4e86\u6447\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u7239\uff0c\u4f60\u8d76\u6211\u4e5f\u4e0d\u8d70\uff01\u201d \u3000\u3000\u6210\u5409\u601d\u6c57\u671b\u4e86\u5973\u513f\u4e00\u773c\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u4f60\u597d\u50cf\u662f\u6709\u4ec0\u4e48\u8bdd\u8981\u548c\u6211\u8bf4\uff0c\u662f\u4e48\uff1f\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u9053\uff1a\u201c\u7239\u7239\u771f\u806a\u660e\uff0c\u4e00\u731c\u5c31\u7740\u3002\u201d \u3000\u3000\u6210\u5409\u601d\u6c57\u5f97\u610f\u7b11\u9053\uff1a\u201c\u4f60\u6709\u5fc3\u4e8b\uff0c\u603b\u662f\u7792\u4e0d\u8fc7\u6211\u7684\uff0c\u8bf4\u5427\uff01\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u7b11\u9053\uff1a\u201c\u8fd9\u4f60\u53ef\u731c\u9519\u4e86\u3002\u8fd9\u4e00\u6b21\u8981\u8bf4\u7684\u662f\u4f60\u7684\u4e8b\u60c5\u3002\u201d \u3000\u3000\u6210\u5409\u601d\u6c57\u6014\u4e86\u4e00\u6014\u8bf4\u9053\uff1a\u201c\u662f\u6211\u7684\u4e8b\u60c5\uff1f\u6211\u6709\u4ec0\u4e48\u4e8b\u60c5\u8981\u4f60\u64cd\u5fc3\uff1f\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\u4f60\u7684\u8fd9\u4f4d\u526f\u5143\u5e05\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6210\u5409\u601d\u6c57\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u662f\u674e\u5e0c\u6d69\u5417\uff1f\u4ed6\u672c\u6765\u662f\u4fd8\u864f\uff0c\u4f46\u5374\u662f\u9887\u6709\u672c\u9886\u7684\u7684\u4fd8\u864f\uff0c\u56e0\u6b64\u624d \u6349\u62d4\u4ed6\u7684\u3002\u4e3a\u4f55\u4f60\u8981\u95ee\u4ed6\uff1f\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u6447\u4e86\u6447\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u7239\uff0c\u4f60\u7ed9\u4ed6\u9a97\u4e86\u3002\u4ed6\u4e0d\u662f\u674e\u5e0c\u6d69\u3002\u4ed6\u539f\u6765\u7684\u540d\u5b57\u4f59\u4e00\u4e2d\uff0c \u4ed6\u5bb3\u6b7b\u4e86\u674e\u5e0c\u6d69\uff0c\u7136\u540e\uff0c\u5192\u540d\u9876\u66ff\uff0c\u6765\u9a97\u4f60\u505a\u5b98\u7684\u3002\u201d\u5f53\u4e0b\u628a\u4f59\u4e00\u4e2d\u600e\u6837\u8c0b\u5bb3\u674e\u5e0c\u6d69\u7684\u4e8b \u60c5\uff0c\u539f\u539f\u672c\u672c\u5730\u544a\u8bc9\u4e86\u6210\u5409\u601d\u6c49\uff01 \u3000\u3000\u6210\u5409\u601d\u6c57\u8010\u5fc3\u542c\u5979\u8bb2\u5b8c\u4e4b\u540e\uff0c\u8bf4\u9053\uff1a\u201c\u54e6\uff0c\u7adf\u6709\u8fd9\u6837\u7684\u4e8b\u60c5\uff01\u4f46\u5982\u6b64\u8bf4\u6765\uff0c\u8fd9\u4e2a\u4f59\u4e00\u4e2d \u5012\u4e5f\u7b97\u5f97\u662f\u4e2a\u806a\u660e\u624d\u667a\u4e4b\u58eb\u5462\uff01\u201d\u660e\u6167\u516c\u4e3b\u6014\u4e86\u4e00\u6014\uff0c\u8bf4\u9053\uff1a\u201c\u7239\uff0c\u542c\u4f60\u7684\u53e3\u6c14\uff0c\u4f60\u5012\u597d\u4f3c \u8fd8\u5728\u79f0\u8d5e\u4ed6\u4e86\u3002\u201d \u3000\u3000\u6210\u5409\u601d\u6c57\u9053\uff1a\u201c\u90a3\u4e48\u4f9d\u4f60\u4e4b\u89c1\uff0c\u6211\u5e94\u8be5\u5c06\u4ed6\u600e\u6837\uff1f\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u9053\uff1a\u201c\u8fd9\u6837\u5356\u53cb\u6c42\u8363\u7684\u5c0f\u4eba\uff0c\u7239\u7239\uff0c\u4f60\u600e\u80fd\u8ba9\u4ed6\u8eab\u5c45\u9ad8\u4f4d\uff1f\u4f60\u4e0d\u6740\u4ed6\uff0c\u4e5f\u5e94\u8be5 \u628a\u4ed6\u8d76\u51fa\u53bb\u3002\u201d\u660e\u6167\u516c\u4e3b\u5fc3\u60f3\uff0c\u53ea\u8981\u628a\u4f59\u4e00\u4e2d\u9010\u51fa\u8425\uff0c\u90a3\u65f6\u4ed6\u5931\u53bb\u4e86\u8d41\u501f\uff0c\u6768\u5a49\u8981\u6740\u4ed6\uff0c\u81ea \u662f\u6613\u4e8e\u53cd\u638c\u3002\u8ba9\u6768\u5a49\u4eb2\u624b\u62a5\u4ec7\uff0c\u6bd4\u501f\u5200\u6740\u4eba\u66f4\u597d\u3002 \u3000\u3000\u6210\u5409\u601d\u6c57\u76b1\u4e86\u76b1\u7709\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u5c0f\u5b69\u5b50\u7684\u89c1\u8bc6\u3002\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u53c8\u60ca\u53c8\u607c\uff0c\u8bf4\u9053\uff1a\u201c\u7239\uff0c\u6211\u8fd9\u662f\u4e3a\u4f60\u7740\u60f3\uff0c\u4f60\u653e\u5fc3\u5f97\u4e0b\u8fd9\u6837\u5351\u9119\u72e0\u6bd2\u5c0f\u4eba\u7559\u5728 \u4f60\u7684\u8eab\u8fb9\u5417\uff1f\u4f60\u6700\u8ba8\u538c\u624b\u4e0b\u4eba\u5bf9\u4f60\u4e0d\u5fe0\u5b9e\uff0c\u4f60\u53c8\u600e\u80fd\u5bb9\u5fcd\u5f97\u4e0b\u8fd9\u4e2a\u5192\u724c\u7684\u4f59\u4e00\u4e2d\u6b3a\u9a97\u4f60 \u5462\uff1f\u201d \u3000\u3000\u6210\u5409\u601d\u6c57\u7b11\u9053\uff1a\u201c\u6240\u4ee5\u6211\u8bf4\u4f60\u662f\u5c0f\u5b69\u5b50\u7684\u89c1\u8bc6\uff0c\u8fd9\u4ef6\u4e8b\u60c5\u4f60\u53ea\u770b\u5230\u4e00\u9762\uff0c\u5c31\u4ee5\u4e3a\u6211\u662f\u53d7 \u4eba\u9a97\u4e86\uff0c\u8fd9\u4e0d\u662f\u5e7c\u7a1a\u5f97\u7d27\u4e48\uff1f\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u9053\uff1a\u201c\u4ed6\u5192\u540d\u9876\u66ff\uff0c\u600e\u4e48\u8fd8\u4e0d\u662f\u9a97\u4f60\uff1f\u201d \u3000\u3000\u6210\u5409\u601d\u6c57\u9053\uff1a\u201c\u4f60\u4e0d\u8981\u6fc0\u52a8\uff0c\u5750\u4e0b\u6765\uff0c\u5f85\u6211\u6162\u6162\u5730\u548c\u4f60\u8bf4\u3002\u9996\u5148\uff0c\u6211\u8981\u7684\u53ea\u662f\u4e00\u4e2a\u6709\u624d \u5e72\uff0c\u800c\u53c8\u80fd\u4e3a\u6211\u6240\u7528\u7684\u6c49\u4eba\uff0c\u6709\u4e00\u4e2a\u8fd9\u6837\u7684\u4eba\u5bf9\u6211\u5f81\u670d\u4e2d\u56fd\u6709\u5f88\u5927\u7684\u5e2e\u52a9\uff0c\u4f60\u61c2\u4e0d\u61c2\uff1f\u81f3\u4e8e \u8fd9\u4e2a\u4eba\u662f\u59d3\u4f59\u4e5f\u7f62\uff0c\u662f\u59d3\u674e\u4e5f\u7f62\uff0c\u8fd9\u5374\u65e0\u5173\u91cd\u8981\uff01\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u51b7\u4e86\u534a\u622a\uff0c\u8bf4\u9053\uff1a\u201c\u7239\uff0c\u4f46\u8fd9\u4eba\u5374\u662f\u4e2a\u5351\u9119\u72e0\u6bd2\u7684\u5c0f\u4eba\u554a\uff01\u201d \u3000\u3000\u6210\u5409\u601d\u6c57\u7b11\u9053\uff1a\u201c\u6211\u53c8\u4e0d\u662f\u6311\u9009\u5973\u5a7f\uff0c\u7ba1\u4ed6\u54c1\u884c\u4f5c\u751a\uff1f\u4f55\u51b5\u4ed6\u53c8\u662f\u6c49\u4eba\uff0c\u5e76\u975e\u6211\u7684\u4eb2\u4fe1 \u5b50\u4f84\uff0c\u4ed6\u54c1\u884c\u8d8a\u662f\u4e0d\u7aef\uff0c\u6211\u5c31\u8d8a\u53d1\u653e\u5fc3\u7528\u4ed6\uff01\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u542c\u4e86\u7236\u4eb2\u7684\u8bdd\uff0c\u5c31\u50cf\u6389\u5230\u51b0\u7a9f\u91cc\u4f3c\u7684\uff0c\u5fc3\u5934\u611f\u5230\u9635\u9635\u5bd2\u610f\uff0c\u60f3\u9053\uff1a\u201c\u539f\u6765\u7239\u7239 \u662f\u8fd9\u6837\u7528\u4eba\u7684\u3002\u5509\uff0c\u5176\u5b9e\u4ed6\u7ed9\u6211\u9009\u7684\u9a78\u9a6c\uff0c\u4f55\u5c1d\u4e0d\u4e5f\u662f\u53ea\u56fe\u5229\u7528\uff0c\u54ea\u66fe\u771f\u6b63\u4e3a\u4e86\u6211\u7684\u5e78\u798f\u7740 \u60f3\uff1f\u9547\u56fd\u8fd9\u53ae\u4e0d\u4f46\u76f8\u8c8c\u4e11\u964b\uff0c\u800c\u4e14\u542c\u8bf4\u4ed6\u8fd8\u8d2a\u8d22\u597d\u8272\uff0c\u54c1\u884c\u4e5f\u5e76\u4e0d\u7aef\u6b63\u3002\u201d \u3000\u3000\u539f\u6765\u9547\u56fd\u4e43\u662f\u6c6a\u53e4\u65cf\u7684\u738b\u5b50\uff0c\u6c6a\u53e4\u65cf\u662f\u8499\u53e4\u6700\u5927\u7684\u4e00\u4e2a\u90e8\u843d\uff0c\u6210\u5409\u601d\u6c57\u628a\u6700\u5ba0\u7231\u7684\u5c0f\u5973 \u513f\u8bb8\u7ed9\u4ed6\uff0c\u672c\u6765\u5c31\u662f\u60f3\u5229\u7528\u6c6a\u53e4\u65cf\u7684\u5175\u529b\u3002 \u3000\u3000\u6210\u5409\u601d\u6c57\u89c1\u5973\u513f\u4f4e\u9996\u6c89\u601d\uff0c\u4ee5\u4e3a\u5979\u5c1a\u672a\u61c2\u5f97\u81ea\u5df1\u7684\u610f\u601d\uff0c\u4e8e\u662f\u63a5\u4e0b\u53bb\u8bf4\u9053\uff1a\u201c\u4f60\u8981\u77e5 \u9053\uff0c\u54b1\u4eec\u662f\u8981\u53bb\u62a2\u6c49\u4eba\u7684\u5730\u65b9\u7684\uff0c\u8bf4\u8001\u5b9e\u8bdd\uff0c\u80af\u6765\u6548\u5fe0\u4e8e\u6211\u7684\u6c49\u4eba\uff0c\u5f53\u7136\u662f\u8d2a\u56fe\u5bcc\u8d35\u7684\u5351\u9119 \u5c0f\u4eba\u4e86\u3002\u82e5\u662f\u6b63\u4eba\u541b\u5b50\uff0c\u54ea\u6709\u53cd\u8fc7\u6765\u5e2e\u5fd9\u54b1\u4eec\u6253\u4ed6\u4eec\u81ea\u5df1\u4eba\u7684\u9053\u7406\uff1f \u3000\u3000\u201c\u81f3\u4e8e\u8bf4\u5230\u4ed6\u6b3a\u9a97\u6211\u8fd9\u4e00\u5c42\uff0c\u90a3\u8981\u770b\u4ed6\u7684\u4f01\u56fe\u662f\u4ec0\u4e48\uff0c\u4ed6\u82e5\u662f\u4f01\u56fe\u80cc\u53db\u6211\uff0c\u6211\u5f53\u7136\u5bb9\u4ed6 \u4e0d\u5f97\uff1b\u4ed6\u662f\u60f3\u9a97\u4e00\u4e2a\u5b98\u513f\u505a\u505a\uff0c\u6211\u53c8\u4f55\u541d\u60dc\u4e8e\u8d4f\u4ed6\u4e00\u4e2a\u526f\u5143\u5e05\u7684\u865a\u8854\u3002\u201d \u3000\u3000\u201c\u4f60\u8bfb\u8fc7\u6c49\u4eba\u7684\u4e66\uff0c\u6709\u4e00\u53e5\u6c49\u4eba\u7684\u6210\u8bed\u4f60\u77e5\u4e0d\u77e5\u9053\uff0c\u8fd9\u53e5\u6210\u8bed\u53eb\u65e0\u6bd2\u4e0d\u4e08\u592b\uff01\u4f59\u4e00\u4e2d\u8c0b \u5bb3\u4e86\u674e\u5e0c\u6d69\uff0c\u624b\u6bb5\u7684\u786e\u72e0\u6bd2\uff0c\u4f46\u8fd9\u4e5f\u4e0d\u8bc1\u660e\u4e86\u4ed6\u7684\u624d\u5e72\u5417\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\uff0c\u8fd9\u6837\u7684\u4eba\u5728\u6211\u8eab\u8fb9\uff0c\u6211\u603b\u8fd8\u8981\u63d0\u9632\u4ed6\u7684\u3002\u8bf4\u8001\u5b9e\u8bdd\uff0c\u6211\u4e5f\u53ea\u662f\u628a\u4ed6\u5f53\u4f5c\u9e70\u72ac\u6765 \u4f7f\u7528\u800c\u5df2\u3002\u6c49\u4eba\u8fd8\u6709\u53e5\u6210\u8bed\u8bf4\uff1a\u72e1\u514d\u6b7b\uff0c\u8d70\u72d7\u70f9\u3002\u6c49\u4eba\u7684\u9526\u7ee3\u6cb3\u5c71\u662f\u6211\u6240\u8981\u730e\u53d6\u7684\u2018\u72e1 \u5154\u2019\uff0c\u5982\u4eca\u54b1\u4eec\u4e0d\u8fc7\u5f00\u59cb\u6253\u91d1\u56fd\uff0c\u6211\u5c82\u80fd\u70f9\u4e86\u6211\u7684\u8d70\u72d7\u4f59\u4e00\u4e2d\uff1f\u4f60\u529d\u6211\u6740\u4ed6\uff0c\u5c06\u6765\u6211\u662f\u4f1a\u6740 \u4ed6\u7684\uff01\u4f46\u8fd9\u5fc5\u987b\u7b49\u5230\u706d\u4e86\u91d1\u56fd\uff0c\u53c8\u6e21\u6c5f\u706d\u4e86\u5357\u5b8b\u4e4b\u540e\uff01\u201d \u3000\u3000\u201c\u597d\u4e86\uff0c\u4f60\u73b0\u5728\u5e94\u8be5\u660e\u767d\u4e86\u5427\uff1f\u4ee5\u540e\u4e0d\u8bb8\u518d\u63d0\u6740\u4f59\u4e00\u4e2d\u7684\u4e8b\uff0c\u8fde\u4f59\u4e00\u4e2d\u662f\u5192\u540d\u9876\u66ff\u7684 \u4e8b\uff0c\u4e5f\u4e0d\u80fd\u6cc4\u6f0f\u534a\u53e5\uff01\u6b64\u4e8b\u5173\u7cfb\u91cd\u5927\uff0c\u4f60\u8981\u7d27\u8bb0\uff0c\u4e0d\u53ef\u8fdd\u80cc\uff01\u4f60\u56de\u53bb\u5427\uff01\u201d \u3000\u3000\u6210\u5409\u601d\u6c57\u4f24\u53e3\u8fd8\u5728\u75bc\u75db\uff0c\u8bf4\u4e86\u8fd9\u4e00\u5927\u5806\u8bdd\uff0c\u6c14\u529b\u5df2\u662f\u6709\u70b9\u4e0d\u7ee7\uff0c\u6c14\u5598\u5401\u5401\u8bf4\u5b8c\u4e4b\u540e\uff0c\u53c8 \u518d\u95ed\u76ee\u517b\u795e\u3002 \u3000\u3000\u660e\u6167\u516c\u4e3b\u77e5\u9053\u7239\u7239\u7684\u813e\u6c14\uff0c\u8bf4\u5230\u505a\u5230\uff0c\u4ed6\u8bf4\u5f97\u5982\u6b64\u65a9\u9489\u622a\u94c1\uff0c\u81ea\u5df1\u600e\u6837\u6492\u5a07\u4e5f\u662f\u65e0\u6d4e\u4e8e \u4e8b\u3002 \u3000\u3000\u5f53\u4e0b\u660e\u6167\u516c\u4e3b\u53ea\u597d\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u7239\uff0c\u4f60\u65e2\u7136\u8d70\u8981\u7528\u8fd9\u6761\u8d70\u72d7\uff0c\u6211\u4e0d\u6cc4\u6f0f\u5c31\u662f\u3002\u4f46 \u6211\u7684\u5fc3\u91cc\uff0c\u90a3\u662f\u975e\u5e38\u8ba8\u538c\u8fd9\u6761\u8d70\u72d7\u7684\u3002\u7239\uff0c\u4f60\u597d\u597d\u517b\u4f24\u5427\uff0c\u660e\u5929\u4e00\u65e9\u6211\u518d\u6765\u770b\u4f60\u3002\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u6b63\u8981\u79bb\u5f00\uff0c\u6210\u5409\u601d\u6c57\u60f3\u8d77\u4e00\u4e8b\uff0c\u5ffd\u5730\u5f20\u5f00\u773c\u775b\uff0c\u53eb\u9053\uff1a\u201c\u660e\u6167\uff0c\u56de\u6765\uff01\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u610f\u51b7\u5fc3\u7070\uff0c\u56de\u8fc7\u5934\u6765\u8bf4\u9053\uff1a\u201c\u7239\uff0c\u6211\u6c42\u4f60\u7684\u4f60\u90fd\u4e0d\u80af\u7b54\u5141\uff0c\u8fd8\u6709\u4ec0\u4e48\u597d\u8bf4\u7684 \u5462\uff1f\u201d \u3000\u3000\u6210\u5409\u601d\u6c57\u559d\u4e86\u4e86\u4e00\u53e3\u53c2\u6c64\uff0c\u7f13\u7f13\u8bf4\u9053\uff0c\u201c\u8fd9\u4e9b\u4e8b\u60c5\u4f60\u662f\u600e\u4e48\u77e5\u9053\u7684\uff1f\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\u60c5\uff1f\u201d \u3000\u3000\u6210\u5409\u601d\u6c57\u9053\uff1a\u201c\u6709\u5173\u4f59\u4e00\u4e2d\u7684\u8fd9\u4e9b\u4e8b\u60c5\uff0c\u662f\u8c01\u544a\u8bc9\u4f60\u7684\uff1f\u201d\u8bed\u6c14\u6e10\u89c1\u51cc\u5389\u3002 \u3000\u3000\u5728\u7236\u4eb2\u559d\u95ee\u4e4b\u4e0b\uff0c\u660e\u6167\u516c\u4e3b\u8bb7\u8bb7\u8bf4\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u8fd9\u4e2a\u2026\u2026\u201d \u3000\u3000\u6210\u5409\u601d\u6c57\u864e\u76ee\u4e00\u77aa\uff0c\u5389\u58f0\u8bf4\u9053\uff1a\u201c\u4ec0\u4e48\u8fd9\u4e2a\u90a3\u4e2a\u7684\uff1f\u8bf4\uff01\u662f\u4e0d\u662f\u674e\u601d\u5357\u8fd9\u5c0f\u5b50\u544a\u8bc9\u4f60 \u7684\uff1f\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u77e5\u9053\u96be\u4ee5\u9690\u7792\uff0c\u53ea\u597d\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u662f\u674e\u601d\u5357\u544a\u8bc9\u6211\u7684\u3002\u201d \u3000\u3000\u6210\u5409\u601d\u6c57\u51b7\u7b11\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u8981\u7ed9\u4ed6\u62a5\u4ec7\u4e86\uff0c\u662f\u4e48\uff1f\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u53c8\u6c14\u53c8\u607c\uff0c\u8bf4\u9053\uff1a\u201c\u4e8b\u60c5\u603b\u6709\u4e2a\u662f\u975e\u66f2\u76f4\uff0c\u674e\u601d\u5357\u7ed9\u4f59\u4e00\u4e2d\u8c0b\u5bb3\u4e86\u4ed6\u7684\u7236\u4eb2\uff0c \u96be\u9053\u4ed6\u4e0d\u8be5\u62a5\u4ec7\u5417\uff1f\u4e0d\u9519\uff0c\u8fd9\u4ef6\u4e8b\u60c5\u6211\u4e5f\u7684\u786e\u662f\u770b\u4e0d\u8fc7\u773c\uff0c\u4f46\u7239\u7239\u4f60\u65e2\u7136\u8981\u5305\u5e87\u574f\u4eba\uff0c\u6211\u4e5f \u6ca1\u6709\u6cd5\u5b50\u3002\u201d \u3000\u3000\u6210\u5409\u601d\u6c57\u9053\uff1a\u201c\u6211\u4e0d\u548c\u4f60\u8bf4\u8fd9\u4e9b\uff0c\u6211\u53ea\u95ee\u4f60\uff0c\u674e\u601d\u5357\u4ec0\u4e48\u65f6\u5019\u544a\u8bc9\u4f60\u7684\uff1f\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u9053\uff1a\u201c\u7239\uff0c\u4f60\u7ba1\u8fd9\u95f2\u4e8b\u5e72\u561b\uff1f\u4f60\u517b\u4f24\u8981\u7d27\uff0c\u4f55\u5fc5\u591a\u7406\u95f2\u4e8b\uff1f\u201d \u3000\u3000\u6210\u5409\u601d\u6c57\u9053\uff1a\u201c\u8fd9\u53ef\u5e76\u975e\u95f2\u4e8b\uff0c\u674e\u601d\u5357\u8fd9\u5c0f\u5b50\u8eab\u4e0a\u6709\u4e00\u672c\u97e9\u4e16\u5fe0\u7684\u5175\u6cd5\uff0c\u6211\u975e\u628a\u4ed6\u6293\u56de \u6765\u4e0d\u53ef\uff01\u4f60\u662f\u4e0d\u662f\u6700\u8fd1\u548c\u4ed6\u79c1\u4f1a\uff1f\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u9053\uff1a\u201c\u5728\u548c\u6797\u72e9\u730e\u90a3\u5929\uff0c\u6211\u662f\u548c\u674e\u601d\u5357\u4f1a\u4e86\u9762\uff0c\u8fd8\u662f\u4f60\u53eb\u4ed6\u966a\u6211\u6253\u730e\u7684\u3002\u201d \u3000\u3000\u6210\u5409\u601d\u6c57\u9053\uff1a\u201c\u6211\u4e0d\u662f\u6307\u90a3\u4e00\u6b21\u3002\u674e\u601d\u5357\u8fd9\u5c0f\u5b50\u813e\u6c14\u5014\u5f3a\u5f97\u5f88\uff0c\u4f9d\u6211\u731c\u60f3\uff0c\u5728\u548c\u6797\u72e9\u730e \u4e4b\u65f6\uff0c\u4ed6\u4e00\u5b9a\u8fd8\u672a\u77e5\u9053\u4f59\u4e00\u4e2d\u662f\u4ed6\u7684\u6740\u7236\u4ec7\u4eba\uff0c\u5426\u5219\u4ed6\u5c82\u80fd\u8fd8\u8ddf\u7740\u4f59\u4e00\u4e2d\u6765\u89c1\u6211\uff1f\u8fd9\u4e9b\u4e8b \u60c5\uff0c\u4e00\u5b9a\u662f\u4ed6\u6700\u8fd1\u544a\u8bc9\u4f60\u7684\uff01\u201d\u660e\u6167\u516c\u4e3b\u7779\u6c14\u8bf4\u9053\uff1a\u201c\u7239\uff0c\u4f60\u65e2\u7136\u5b9a\u8981\u76d8\u6839\u95ee\u5e95\uff0c\u6211\u5c31\u544a\u8bc9 \u4f60\u5427\u3002\u662f\u4ed6\u79bb\u5f00\u548c\u6797\u4e4b\u540e\uff0c\u6211\u7792\u7740\u4f60\u53bb\u8ffd\u8d76\u4ed6\uff0c\u5728\u4e00\u4e2a\u5730\u65b9\u89c1\u4e86\u9762\uff0c\u4ed6\u544a\u8bc9\u6211\u7684\u3002\u8bf4\u8fd1\u4e0d \u8fd1\uff0c\u4e5f\u6709\u51e0\u4e2a\u6708\u4e86\u3002\u201d \u3000\u3000\u6210\u5409\u601d\u6c57\u9053\uff1a\u201c\u4ed6\u73b0\u5728\u5462\uff1f\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u9053\uff1a\u201c\u4ed6\u65e9\u5df2\u8d70\u4e86\uff0c\u6211\u600e\u4e48\u77e5\u9053\u4ed6\u5728\u90a3\u513f\uff1f\u201d \u3000\u3000\u6210\u5409\u601d\u6c57\u9053\uff1a\u201c\u662f\u4f60\u5e2e\u5fd9\u4ed6\u8d70\u7684\uff1f\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u9053\uff1a\u201c\u4e0d\u9519\u3002\u6211\u7ed9\u4e86\u4ed6\u4e00\u9762\u4ee4\u724c\uff01\u7239\uff0c\u4f60\u8981\u600e\u6837\u5904\u7f5a\u6211\uff0c\u6211\u4fef\u9996\u542c\u547d\u5c31\u662f\uff01\u201d \u3000\u3000\u6210\u5409\u601d\u6c57\u53f9\u4e86\u53e3\u6c14\uff0c\u5ffd\u5730\u67d4\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u653e\u8d70\u6211\u7684\u72af\u4eba\uff0c\u5b9e\u5728\u662f\u5927\u5927\u7684\u4e0d\u8be5\uff0c\u4f46\u6211\u5374\u600e \u820d\u5f97\u8d23\u7f5a\u4f60\u3002\u4f60\u662f\u4e0d\u662f\u559c\u6b22\u59d3\u674e\u7684\u8fd9\u5c0f\u5b50\uff1f\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u5fc3\u75db\u5982\u5272\uff0c\u60f3\u9053\uff1a\u201c\u6211\u559c\u6b22\u4ed6\u53c8\u80fd\u600e\u6837\uff0c\u4ed6\u65e9\u5df2\u6709\u4e86\u610f\u4e2d\u4eba\u4e86\u3002\u201d \u3000\u3000\u6210\u5409\u601d\u6c57\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u5fc5\u5bb3\u6015\uff0c\u4f60\u82e5\u662f\u771f\u7684\u559c\u6b22\u4ed6\uff0c\u4f60\u53ef\u4ee5\u628a\u4ed6\u53eb\u56de\u6765\u3002\u53ea\u8981\u4ed6\u80af\u4f9d \u987a\uff0c\u6211\u4e0d\u4f1a\u5c06\u4ed6\u96be\u4e3a\u7684\u3002\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u9053\uff1a\u201c\u6211\u5df2\u7ecf\u8bf4\u8fc7\uff0c\u6211\u548c\u4ed6\u6700\u540e\u4e00\u6b21\u7684\u89c1\u9762\u4e43\u662f\u51e0\u4e2a\u6708\u4ee5\u524d\u7684\u4e8b\u60c5\uff0c\u6211\u600e\u80fd\u627e \u5f97\u7740\u4ed6\uff1f\u7239\uff0c\u4f60\u4e0d\u5fc5\u95ee\u6211\u559c\u4e0d\u559c\u6b22\u674e\u601d\u5357\uff0c\u6211\u53ea\u80fd\u544a\u8bc9\u4f60\uff0c\u6211\u4e0d\u559c\u6b22\u4f60\u7ed9\u6211\u6311\u9009\u7684\u8fd9\u4e2a\u9a78 \u9a6c\uff01\u201d \u3000\u3000\u6210\u5409\u601d\u6c57\u9053\uff1a\u201c\u674e\u601d\u5357\u5f53\u771f\u4e0d\u5728\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u607c\u9053\uff1a\u201c\u7239\uff0c\u96be\u9053\u6211\u8fd8\u4f1a\u628a\u4e00\u4e2a\u5927\u6d3b\u4eba\u85cf\u8d77\u6765\u5417\uff1f\u201d \u3000\u3000\u6210\u5409\u601d\u6c57\u677f\u8d77\u9762\u5b54\u9053\uff1a\u201c\u8c05\u4f60\u4e5f\u4e0d\u6562\u3002\u4f46\u4f60\u8981\u660e\u767d\uff0c\u4f60\u662f\u8bb8\u4e86\u4eba\u5bb6\u7684\u4e86\u3002\u6211\u7ed9\u4f60\u627e\u7684\u9a78 \u9a6c\uff0c\u4f60\u559c\u6b22\u4e5f\u7f62\uff0c\u4e0d\u559c\u6b22\u4e5f\u7f62\uff0c\u4f60\u90fd\u5f97\u5ac1\u7ed9\u4ed6\uff01\u6211\u8fd8\u544a\u8bc9\u4f60\uff0c\u674e\u601d\u5357\u662f\u6c49\u4eba\uff0c\u4f60\u5c31\u662f\u559c\u6b22 \u4ed6\uff0c\u4e5f\u4e0d\u80fd\u5ac1\u7ed9\u4ed6\u7684\u3002\u4f60\u660e\u767d\u4e86\u4e48\uff1f\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u6c14\u5f97\u73e0\u6cea\u76c8\u7736\uff0c\u6124\u7136\u8bf4\u9053\uff1a\u201c\u8c01\u8bf4\u6211\u8981\u5ac1\u7ed9\u4ed6\uff0c\u6211\u4ec0\u4e48\u4eba\u4e5f\u4e0d\u5ac1\uff01\u201d \u3000\u3000\u6210\u5409\u601d\u6c57\u9053\uff1a\u201c\u5b69\u5b50\u6c14\uff0c\u5f85\u6211\u7684\u4f24\u597d\u4e86\uff0c\u6211\u5c31\u7ed9\u4f60\u6210\u5a5a\uff0c\u514d\u5f97\u4f60\u4e09\u5fc3\u4e8c\u610f\u3002\u597d\u5566\uff0c\u4f60\u53ef \u4ee5\u56de\u53bb\u4e86\u3002\u56de\u53bb\u597d\u597d\u7684\u60f3\u60f3\uff0c\u4e0d\u8981\u8f9c\u8d1f\u4e86\u6211\u5bf9\u4f60\u7684\u5ba0\u7231\uff01\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u5fc3\u9053\uff1a\u201c\u4f60\u54ea\u91cc\u662f\u5ba0\u6211\u7231\u6211\uff1f\u903c\u6211\u5ac1\u7ed9\u90a3\u4e11\u516b\u602a\uff0c\u5206\u660e\u662f\u628a\u6211\u63a8\u5165\u706b\u5751\u3002\u201d\u4f46 \u6210\u5409\u601d\u6c57\u6b63\u5728\u517b\u4f24\uff0c\u660e\u6167\u516c\u4e3b\u53c8\u600e\u80fd\u60f9\u4ed6\u751f\u6c14\uff0c\u53ea\u597d\u4e0d\u518d\u8bf4\u8bdd\uff0c\u542b\u7740\u773c\u6cea\u79bb\u5f00\u3002 \u3000\u3000\u660e\u6167\u516c\u4e3b\u95f7\u95f7\u4e0d\u4e50\u8d70\u8fdb\u8425\u623f\uff0c\u6768\u5a49\u89c1\u5979\u7684\u4f38\u8272\uff0c\u5df2\u77e5\u4e0d\u5999\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5979\u672c\u4e0d\u60f3\u4f9d\u8d56 \u4ed6\u4eba\u5e2e\u6211\u62a5\u4ec7\u3002\u201d\u56e0\u6b64\uff0c\u867d\u6709\u70b9\u5931\u671b\uff0c\u4ecd\u7136\u5b89\u6170\u660e\u6167\u516c\u4e3b\u9053\uff1a\u201c\u516c\u4e3b\uff0c\u4f60\u4e3a\u4e86\u6211\u7684\u4e8b\u60c5\u5982\u6b64 \u5c3d\u529b\uff0c\u6210\u4e0e\u4e0d\u6210\uff0c\u6211\u90fd\u662f\u4e00\u6837\u611f\u6fc0\u4f60\u7684\uff01\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u6068\u6068\u8bf4\u9053\uff1a\u201c\u7239\u7239\u4e0d\u8ba9\u6211\u6740\u4f59\u4e00\u4e2d\uff0c\u4f46\u4ed6\u603b\u6709\u5931\u52bf\u7684\u4e00\u5929\uff0c\u8fdf\u65e9\u4f1a\u72af\u5728\u6211\u7684\u624b \u91cc\uff0c\u6211\u662f\u51b3\u610f\u548c\u4ed6\u4f5c\u5bf9\u5230\u5e95\u7684\u4e86\uff01\u201d \u3000\u3000\u8bf4\u662f\u8fd9\u6837\u8bf4\uff0c\u660e\u6167\u516c\u4e3b\u5374\u662f\u6ca1\u6709\u6740\u5f97\u4e86\u4f59\u4e00\u4e2d\u7684\u628a\u63e1\u3002\u800c\u4e14\u76ee\u524d\u6765\u8bf4\uff0c\u5979\u975e\u4f46\u5e2e\u5fd9\u4e0d\u4e86 \u6768\u5a49\uff0c\u5979\u81ea\u5df1\u4e5f\u6b63\u6709\u7740\u7126\u5fc3\u7684\u4e8b\u60c5\uff0c\u60f3\u8981\u6c42\u4eba\u5e2e\u5fd9\u4e5f\u5e2e\u5fd9\u4e0d\u4e86\u3002 \u3000\u3000\u8fd9\u665a\u660e\u6167\u516c\u4e3b\u8f97\u8f6c\u53cd\u4fa7\uff0c\u4e0d\u80fd\u6210\u5bd0\uff0c\u6210\u5409\u601d\u6c57\u8bf4\u8fc7\uff0c\u4e00\u5f85\u4f24\u597d\uff0c\u5c31\u8981\u5979\u4e0e\u9547\u56fd\u738b\u5b50\u6210 \u4eb2\u3002\u5979\u77e5\u9053\u6210\u5409\u601d\u6c57\u7684\u8bdd\u4e00\u8bf4\u51fa\u6765\uff0c\u90a3\u5c31\u662f\u5df2\u6210\u5b9a\u5c40\uff0c\u51b3\u4e0d\u80fd\u66f4\u6539\u7684\u4e86\uff0c\u8fd9\u53ef\u600e\u4e48\u529e\u5462\uff1f \u3000\u3000\u5c06\u8fd1\u4e94\u66f4\u65f6\u5206\uff0c\u660e\u6167\u516c\u4e3b\u65b9\u59cb\u6726\u6726\u80e7\u80e7\u5730\u7761\u4e86\u4e00\u89c9\u3002\u7b2c\u4e8c\u5929\u9192\u6765\uff0c\u5df2\u662f\u65e5\u4e0a\u4e09\u7aff\u65f6\u5206\u3002 \u660e\u6167\u516c\u4e3b\u60f3\u53bb\u63a2\u75c5\uff0c\u53c8\u6015\u7236\u4eb2\u4e0e\u5979\u518d\u63d0\u5a5a\u4e8b\u3002\u6b63\u81ea\u8e0c\u8e87\uff0c\u5973\u5175\u8fdb\u6765\u7980\u62a5\uff0c\u660e\u6167\u516c\u4e3b\u8fd9\u624d\u77e5 \u9053\uff0c\u56e0\u4e3a\u5c71\u4e0a\u533b\u7597\u4e0d\u4fbf\uff0c\u9547\u56fd\u738b\u5b50\u7b49\u4eba\u5df2\u7ecf\u628a\u5979\u7684\u7236\u4eb2\u9001\u5230\u516d\u76d8\u5c71\u5357\u4e00\u4e2a\u540d\u53eb\u56fa\u539f\u7684\u5c0f\u57ce\u517b \u75c5\u53bb\u4e86\u3002 \u3000\u3000\u8499\u53e4\u7684\u519b\u4e8b\u8ba1\u5212\uff0c\u672c\u6765\u662f\u51c6\u5907\u95ef\u8fc7\u4e86\u516d\u76d8\u5c71\u7684\u5929\u9669\u4e4b\u540e\uff0c\u5c31\u4e00\u9f13\u4f5c\u6c14\uff0c\u76f4\u6363\u5927\u90fd\u7684\u3002\u4e3a \u4e86\u6210\u5409\u601d\u6c57\u610f\u5916\u53d7\u4f24\uff0c\u8fd9\u519b\u4e8b\u8ba1\u5212\u53ea\u597d\u6682\u65f6\u5ef6\u6401\uff0c\u9547\u56fd\u738b\u5b50\u4e3a\u4e86\u8ba8\u597d\u5cb3\u7236\uff0c\u7559\u5728\u56fa\u539f\u4f8d\u5019\u75c5 \u4eba\uff0c\u5927\u519b\u4ea4\u7ed9\u4e24\u4e2a\u526f\u5143\u5e05\u638c\u7ba1\uff0c\u4f59\u4e00\u4e2d\u662f\u66f4\u5f97\u91cd\u7528\u4e86 \u3000\u3000\u660e\u6167\u516c\u4e3b\u60f3\u5230\u56fa\u539f\u63a2\u75c5\uff0c\u5374\u53c8\u6015\u89c1\u9547\u56fd\u738b\u5b50\u3002\u6210\u5409\u601d\u6c57\u4e5f\u6ca1\u6709\u6d3e\u4eba\u53ec\u5979\u524d\u5f80\uff0c\u660e\u6167\u516c\u4e3b \u53ea\u597d\u95f7\u5728\u5e10\u4e2d\uff0c\u7b49\u5019\u5ba3\u53ec\u3002 \u3000\u3000\u4e0d\u77e5\u4e0d\u89c9\u8fc7\u4e86\u5341\u6765\u5929\uff0c\u5140\u662f\u4e0d\u89c1\u6210\u5409\u601d\u6c57\u6d3e\u6709\u4eba\u6765\u3002\u4f46\u56fa\u539f\u65b9\u9762\u7684\u6d88\u606f\u5219\u662f\u7ecf\u5e38\u4e0d\u65ad\uff0c \u6bcf\u5929\u4f20\u6765\u7684\u6d88\u606f\u90fd\u8bf4\u662f\u6210\u5409\u601d\u6c57\u7684\u75c5\u51b5\u6709\u6240\u597d\u8f6c\u3002\u660e\u6167\u516c\u4e3b\u7a0d\u7a0d\u5b89\u5fc3\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u7239\u7239\u60f3 \u5fc5\u662f\u8fd8\u5728\u751f\u6211\u7684\u6c14\uff0c\u6240\u4ee5\u4e0d\u8981\u6211\u53bb\u89c1\u4ed6\u3002\u4f46\u53ea\u8981\u4ed6\u7684\u75c5\u597d\uff0c\u6211\u591a\u53d7\u4e00\u70b9\u59d4\u5c48\uff0c\u90a3\u4e5f\u7b97\u4e0d\u4e86\u4ec0 \u4e48\u3002\u201d\u4f46\u662f\u6b22\u559c\u4e4b\u4e2d\u4e5f\u6709\u62c5\u5fc3\uff0c\u6210\u5409\u601d\u6c57\u7684\u75c5\u597d\u4e86\uff0c\u53ea\u6015\u5c31\u8981\u903c\u5979\u6210\u4eb2\u4e86\u3002 \u3000\u3000\u6768\u5a49\u56f0\u5728\u8425\u4e2d\uff0c\u540c\u6837\u7684\u4e5f\u662f\u95f7\u95f7\u4e0d\u4e50\u3002\u5979\u6df7\u5728\u8499\u53e4\u519b\u4e2d\uff0c\u672c\u6765\u6000\u6709\u4e24\u4e2a\u76ee\u7684\uff0c\u4e00\u4e2a\u662f\u5c31 \u8fd1\u523a\u6740\u4ec7\u4eba\uff0c\u4e00\u4e2a\u662f\u968f\u519b\u56de\u4e61\u3002\u5982\u4eca\u4ec7\u4eba\u8fd1\u5728\u54ab\u5c3a\uff0c\u5979\u5374\u65e0\u6cd5\u62a5\u4ec7\uff1b\u5927\u519b\u505c\u9a7b\u516d\u76d8\u5c71\u4e0b\uff0c\u53c8 \u4e0d\u77e5\u4f55\u65e5\u65b9\u5f97\u56de\u4e61\uff1f\u522b\u7684\u5973\u5175\u53ef\u4ee5\u5230\u5904\u6e9c\u8fbe\uff0c\u5979\u6015\u7ed9\u4eba\u770b\u51fa\u5e90\u5c71\u771f\u8c8c\uff0c\u5374\u662f\u53ea\u80fd\u6d3b\u52a8\u5728\u5973\u8425 \u7684\u201c\u7981\u201d\u5730\u4e4b\u4e2d\u3002 \u3000\u3000\u8fd9\u65e5\u6768\u5a49\u5728\u6797\u4e2d\u91c7\u6458\u9c9c\u82b1\uff0c\u5ffd\u542c\u5f97\u9a6c\u94c3\u58f0\u54cd\uff0c\u53ea\u89c1\u4e00\u4e2a\u5c11\u5e74\u519b\u5b98\u9a91\u7740\u9a6c\u51b2\u6765\uff0c\u7adf\u7136\u95ef\u8fdb \u4e86\u5973\u8425\u7684\u201c\u7981\u5730\u201d\u6765\u4e86\u3002 \u3000\u3000\u6768\u5a49\u559d\u9053\uff0c\u201c\u4ec0\u4e48\u4eba\uff1f\u8fd9\u91cc\u662f\u5973\u8425\uff0c\u4f60\u77e5\u4e0d\u77e5\u9053\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5e74\u519b\u5b98\u9053\uff1a\u201c\u6211\u6b63\u662f\u6765\u627e\u660e\u6167\u516c\u4e3b\u7684\u3002\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u4f60\u6765\u627e\u516c\u4e3b\uff0c\u4e5f\u4e0d\u80fd\u64c5\u81ea\u95ef\u8fdb\uff0c\u4f60\u53eb\u4ec0\u4e48\u540d\u5b57\uff0c\u62a5\u4e0a\u540d\u6765\uff0c\u6211\u7ed9\u4f60\u901a\u62a5\u3002\u4f60 \u51fa\u6797\u5b50\u5916\u9762\u7b49\u53bb\u3002\u201d \u3000\u3000\u5c11\u5e74\u519b\u5b98\u7ffb\u8eab\u4e0b\u9a6c\uff0c\u5bf9\u7740\u6768\u5a49\u4ed4\u7ec6\u5730\u6253\u91cf\u4e86\u4e00\u4f1a\uff0c\u7b11\u9053\uff1a\u201c\u6211\u4ee5\u524d\u597d\u4f3c\u6ca1\u6709\u89c1\u8fc7\u4f60\uff0c\u4f60 \u662f\u51e0\u65f6\u6765\u7684\uff0c\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u201d \u3000\u3000\u6768\u5a49\u607c\u9053\uff1a\u201c\u4f60\u61c2\u4e0d\u61c2\u89c4\u77e9\uff0c\u6211\u53eb\u4f60\u51fa\u53bb\uff0c\u542c\u89c1\u4e86\u6ca1\u6709\uff1f\u201d \u3000\u3000\u5c11\u5e74\u519b\u5b98\u7b11\u9053\uff1a\u201c\u597d\u4fca\u7684\u59d1\u5a18\uff0c\u60f3\u4e0d\u5230\u5374\u8fd9\u6837\u51f6\uff0c\u4ec0\u4e48\u89c4\u77e9\uff0c\u4f60\u8bf4\u7ed9\u6211\u542c\u542c\u3002\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u4efb\u4f55\u4eba\u4e0d\u8bb8\u8e0f\u8fdb\u5973\u8425\u7684\u8425\u5730\uff0c\u96be\u9053\u4f60\u7adf\u4e0d\u77e5\u3002\u201d \u3000\u3000\u5c11\u5e74\u519b\u5b98\u9053\uff1a\u201c\u4efb\u4f55\u4eba\u4e0d\u8bb8\u8e0f\u8ff8\uff0c\u53ea\u6709\u6211\u662f\u4f8b\u5916\u3002\u5582\uff0c\u4f60\u8fd8\u6ca1\u6709\u56de\u7b54\u6211\u5462\uff0c\u4f60\u53eb\u4ec0\u4e48\u540d \u5b57\uff1f\u201d \u3000\u3000\u6768\u5a49\u7591\u4e91\u9661\u8d77\uff0c\u5fc3\u60f3\uff0c\u201c\u8fd9\u4eba\u5982\u6b64\u80c6\u5927\uff0c\u7f20\u95ee\u4e0d\u4f11\uff0c\u83ab\u975e\u4ed6\u662f\u4f59\u4e00\u4e2d\u6d3e\u6765\u4fa6\u5bdf\u6211\u7684\uff1f\u82e5 \u4e0d\u662f\uff0c\u90a3\u5c31\u662f\u6709\u5fc3\u8c03\u620f\u6211\u4e86\uff1f\u201d \u3000\u3000\u6768\u5a49\u6b63\u81ea\u4e00\u809a\u76ae\u95f7\u6c14\u65e0\u5904\u53d1\u6cc4\uff0c\u65a5\u9053\uff1a\u201c\u7ba1\u4f60\u662f\u4ec0\u4e48\u4eba\uff0c\u4f60\u64c5\u95ef\u8425\u5730\uff0c\u4e0d\u9000\u51fa\u53bb\uff0c\u6211\u5c31 \u8981\u62ff\u4f60\uff01\u201d \u3000\u3000\u5c11\u5e74\u519b\u5b98\u7b11\u9053\uff1a\u201c\u5f53\u771f\uff1f\u53ef\u662f\u6211\u5374\u4e0d\u76f8\u4fe1\u4f60\u8fd9\u6837\u67d4\u5f31\u7684\u59d1\u5a18\u62ff\u5f97\u4e86\u6211\u5462\uff01\u201d \u3000\u3000\u6768\u5a49\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u662f\u4e48\uff1f\u201d\u7a81\u7136\u4e00\u8dc3\u800c\u8d77\uff0c\u9a88\u6307\u5982\u621f\uff0c\u95ea\u7535\u822c\u5730\u4fbf\u70b9\u5230\u4e86\u90a3\u519b\u5b98\u7684\u9762 \u95e8\u3002 \u3000\u3000\u8fd9\u4e00\u62db\u201c\u4e8c\u9f99\u62a2\u73e0\u201d\u662f\u5ce8\u5d4b\u6d3e\u5ae1\u4f20\u7684\u201c\u7a7a\u624b\u5165\u767d\u5203\u201d\u529f\u592b\u3002\u6768\u5a49\u4ece\u5979\u54e5\u54e5\u6768\u6ed4\u90a3\u513f\u5b66\u6765 \u7684\uff0c\u53cc\u6307\u4e00\u51fa\uff0c\u4fbf\u6316\u5bf9\u65b9\u7684\u773c\u73e0\uff0c\u624b\u6cd5\u72e0\u8fa3\u65e0\u6bd4\u3002\u5c11\u5e74\u519b\u5b98\u5403\u4e86\u4e00\u60ca\uff1b\u559d\u9053\uff1a\u201c\u597d\u72e0\uff01\u201d\u5de6 \u638c\u4e00\u7acb\uff0c\u62a4\u7740\u9762\u95e8\uff0c\u811a\u8ddf\u534a\u65cb\uff0c\u53f3\u624b\u7684\u9a6c\u97ad\u626c\u8d77\u3002\u4ed6\u662f\u8499\u53e4\u6570\u4e00\u6570\u4e8c\u7684\u6454\u89d2\u597d\u624b\uff0c\u53ea\u5f85\u6768\u5a49 \u53cc\u6307\u4e00\u5230\uff0c\u7acb\u5373\u4fbf\u53ef\u62d7\u6298\u6768\u5a49\u7684\u624b\u6307\u3002\u8ddf\u7740\u9a6c\u97ad\u6325\u51fa\uff0c\u4fbf\u53ef\u64d2\u4e86\u6768\u5a49\u3002 \u3000\u3000\u5c82\u77e5\u6768\u5a49\u4e43\u662f\u201c\u58f0\u4e1c\u51fb\u897f\u201d\u4e4b\u8ba1\uff0c\u5979\u548c\u8fd9\u5c11\u5e74\u519b\u5b98\u5e76\u65e0\u6df1\u4ec7\u5927\u6068\uff0c\u54ea\u80fd\u6316\u6389\u5bf9\u65b9\u7684\u773c \u73e0\uff1f\u4e0d\u8fc7\u5979\u8fd9\u4e48\u4f5c\u52bf\u4e00\u6316\uff0c\u5374\u662f\u653b\u654c\u4e4b\u6240\u5fc5\u6551\uff0c\u8feb\u4f7f\u5bf9\u65b9\u4e0d\u80fd\u4e0d\u5168\u795e\u9632\u62a4\u9762\u95e8\u3002\u5c31\u5728\u5c11\u5e74\u519b \u5b98\u5355\u638c\u4e00\u7acb\u4e4b\u9645\uff0c\u6768\u5a49\u500f\u5730\u53d8\u62db\uff0c\u8863\u8896\u4e00\u6325\uff0c\u62c2\u7740\u5c11\u5e74\u519b\u5b98\u7684\u864e\u53e3\uff0c\u767b\u65f6\u5c31\u628a\u4ed6\u53f3\u624b\u6240\u62ff\u7684 \u9a6c\u97ad\u5377\u53bb\u3002\u5c11\u5e74\u519b\u5b98\u7684\u6c14\u529b\u672c\u6765\u6bd4\u6768\u5a49\u5927\u5f97\u591a\uff0c\u82e5\u7136\u62f3\u6765\u811a\u5f80\u7684\u660e\u6253\uff0c\u6768\u5a49\u51b3\u5360\u4e0d\u5230\u4ed6\u7684\u4fbf \u5b9c\uff0c\u4f46\u5982\u4eca\u653b\u5176\u65e0\u5907\uff0c\u8fd9\u201c\u58f0\u4e1c\u51fb\u897f\u201d\u7684\u6253\u6cd5\u5374\u662f\u4e00\u4e3e\u6210\u529f\u3002 \u3000\u3000\u5c11\u5e74\u519b\u5b98\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u597d\u4fca\u7684\u8eab\u624b\uff0c\u4f46\u4f60\u62a2\u4e86\u6211\u7684\u9a6c\u97ad\u505a\u4ec0\u4e48\uff1f\u4f60\u662f\u660e\u6167\u7684\u4f8d\u5973\uff0c\u96be\u9053 \u4f60\u60f3\u505a\u6211\u7684\u9a6c\u592b\uff1f\u201d \u3000\u3000\u6768\u5a49\u593a\u4e86\u9a6c\u97ad\uff0c\u63e1\u5728\u624b\u4e2d\uff0c\u53ea\u89c9\u5206\u91cf\u4e0d\u8f7b\uff0c\u8fd9\u624d\u53d1\u73b0\uff0c\u539f\u6765\u8fd9\u6761\u9a6c\u97ad\u4e43\u662f\u7f20\u4e86\u539a\u539a\u4e00\u5708 \u201c\u4e4c\u91d1\u94f6\u4e1d\u201d\u7684\u9a6c\u97ad\u3002\u963f\u5c14\u6cf0\u5c71\u51fa\u4ea7\u7684\u201c\u4e4c\u91d1\u201d\u6bd4\u9ec4\u91d1\u8fd8\u66f4\u8d35\u91cd\uff0c\u8fd9\u6761\u9a6c\u97ad\u663e\u662f\u4ef7\u503c\u8fde\u57ce\u7684 \u5b9d\u8d1d\u4e86\u3002\u53ef\u4ee5\u63a8\u60f3\u5f97\u5230\uff0c\u6709\u8fd9\u6761\u9a6c\u97ad\u7684\u4e3b\u4eba\uff0c\u5f53\u7136\u4e5f\u51b3\u4e0d\u662f\u666e\u901a\u7684\u519b\u5b98\u3002 \u3000\u3000\u6768\u5a49\u660e\u77e5\u5bf9\u65b9\u4e0d\u662f\u5e38\u4eba\uff0c\u4f46\u6068\u4ed6\u51fa\u8a00\u4e0d\u900a\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u7ba1\u4ed6\u662f\u4ec0\u4e48\u4eba\uff1f\u8bef\u95ef\u7981\u5730\uff0c\u6211 \u4e88\u4ee5\u8584\u60e9\uff0c\u8c05\u660e\u6167\u516c\u4e3b\u4e5f\u4e0d\u80fd\u602a\u6211\u3002\u201d \u3000\u3000\u5c11\u5e74\u519b\u5b98\u7b11\u58f0\u672a\u5df2\uff0c\u6768\u5a49\u559d\u9053\uff1a\u201c\u7ed9\u6211\u8eba\u4e0b\u3002\u201d\u9a6c\u97ad\u6253\u51fa\uff0c\u4f7f\u7684\u662f\u201c\u67af\u85e4\u7f20\u6811\u201d\u7684\u62db \u6570\uff0c\u5377\u5411\u5c11\u5e74\u519b\u5b98\u7684\u53cc\u8db3\u3002 \u3000\u3000\u5c11\u5e74\u519b\u5b98\u8fd9\u6b21\u5df2\u6709\u4e86\u9632\u5907\uff0c\u7b11\u9053\uff1a\u201c\u4e0d\u89c1\u5f97\uff01\u201d\u8eab\u5f62\u95ea\u5904\uff0c\u4e00\u8bb0\u201c\u624b\u6325\u7435\u7436\u201d\uff0c\u7ffb\u8eab\u62a2 \u8fdb\uff0c\u53cd\u624b\u64d2\u62ff\u3002\u6768\u5a49\u97ad\u68a2\u4e00\u8f6c\uff0c\u5f85\u5377\u4ed6\u7684\u5c0f\u81c2\uff0c\u5c11\u5e74\u519b\u5b98\u4e00\u4e2a\u65cb\u8eab\uff0c\u6539\u624b\u4e3a\u62f3\uff0c\u62f3\u6c14\u8e1e\u8e1e\uff0c \u4ecd\u662f\u62a2\u73ab\u7684\u62db\u6570\u3002\u6768\u5a49\u6325\u821e\u9a6c\u97ad\uff0c\u6d3b\u4f3c\u7075\u86c7\u3002 \u3000\u3000\u5c11\u5e74\u519b\u5b98\u7b11\u9053\uff1a\u201c\u4f60\u7684\u97ad\u6cd5\u4e5f\u5f88\u4e0d\u9519\uff0c\u6211\u4ee5\u4e3a\u4f60\u662f\u4e2a\u67d4\u5f31\u7684\u59d1\u5a18\uff0c\u5012\u662f\u6211\u8d70\u4e86\u773c\u4e86\u3002\u201d \u7b11\u58f0\u672a\u6536\uff0c\u970d\u5730\u559d\u9053\uff1a\u2018\u6492\u624b\uff01\u201d\u4e94\u6307\u5408\u62e2\uff0c\u4e00\u6293\u6293\u7740\u4e86\u9a6c\u97ad\u3002 \u3000\u3000\u6768\u5a49\u654c\u4e0d\u8fc7\u4ed6\u7684\u6c14\u529b\uff0c\u6025\u4e2d\u751f\u667a\uff0c\u624b\u8155\u4e00\u98a4\uff0c\u97ad\u68a2\u597d\u4f3c\u86c7\u5934\u6602\u8d77\uff0c\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u6253\u7740 \u4e86\u5c11\u5e74\u519b\u5b98\u819d\u76d6\u7684\u201c\u73af\u8df3\u7a74\u201d\uff0c\u5c11\u5e74\u519b\u5b98\u7528\u529b\u4e00\u626f\uff0c\u628a\u9a6c\u97ad\u593a\u4e86\u8fc7\u6765\uff0c\u53ef\u662f\u4e0e\u6b64\u540c\u65f6\uff0c\u4ed6\u819d \u76d6\u4e00\u9ebb\uff0c\u4e5f\u4e0d\u7531\u5f97\u201c\u535c\u901a\u201d\u5730\u8dcc\u5012\u4e86\u3002 \u3000\u3000\u6768\u5a49\u9a6c\u97ad\u88ab\u593a\uff0c\u8eab\u5411\u524d\u503e\uff0c\u91cd\u5fc3\u4e0d\u7a33\uff0c\u4e5f\u662f\u9669\u4e9b\u8dcc\u5012\uff0c\u4e0d\u8fc7\u5979\u7684\u8f7b\u529f\u5f88\u597d\uff0c\u8eab\u5f62\u4e00\u6643\uff0c \u968f\u5373\u4e00\u4e2a\u201c\u9e5e\u5b50\u7ffb\u8eab\u201d\u8dc3\u8d77\u540e\u81ea\u843d\uff0c\u5e73\u5e73\u7a33\u7a33\u5730\u7ad9\u4f4f\u3002 \u3000\u3000\u5c11\u5e74\u519b\u5b98\u5168\u8eab\u62ab\u7532\uff0c\u6025\u5207\u95f4\u5374\u662f\u722c\u4e0d\u8d77\u6765\u3002\u6768\u5a49\u6b63\u8981\u8fc7\u53bb\u64d2\u4ed6\uff0c\u5ffd\u542c\u5f97\u4e00\u7247\u60ca\u60f6\u7684\u58f0\u97f3 \u53eb\u9053\uff1a\u201c\u4e4c\u6d17\u5a1c\uff0c\u4f60\u5e72\u4ec0\u4e48\uff1f\u5feb\u4f4f\u624b\uff01\u201d\u539f\u6765\u662f\u8425\u4e2d\u7684\u5973\u5175\u95fb\u58f0\u8d76\u51fa\uff0c\u201c\u4e4c\u6c49\u5a1c\u201d\u662f\u660e\u6167\u516c \u4e3b\u7ed9\u6768\u5a49\u53d6\u7684\u8499\u53e4\u540d\u5b57\u3002 \u3000\u3000\u5c11\u5e74\u519b\u5b98\u56e0\u662f\u62ab\u7740\u76d4\u7532\uff0c\u6545\u6b64\u201c\u73af\u8df3\u7a74\u201d\u867d\u7ed9\u97ad\u68a2\u51fb\u4e2d\uff0c\u7a74\u9053\u5e76\u672a\u5c01\u95ed\u3002\u4e0d\u5f85\u90a3\u4e9b\u5973\u5175 \u6276\u4ed6\uff0c\u6b64\u65f6\u5df2\u662f\u7ad9\u4e86\u8d77\u6765\u3002\u5c11\u5e74\u519b\u5b98\u7b11\u9053\uff1a\u201c\u5979\u5bf9\u660e\u6167\u5f88\u662f\u5fe0\u5fc3\uff0c\u6211\u4e5f\u6ca1\u6709\u53d7\u4f24\uff0c\u4f60\u4e0d\u5fc5\u602a \u5979\uff01\u201d \u3000\u3000\u90a3\u4e9b\u5973\u5175\u653e\u4e0b\u4e86\u5fc3\uff0c\u8bf4\u9053\uff1a\u201c\u4e4c\u6c49\u5a1c\uff0c\u597d\u5728\u4f60\u6ca1\u6709\u95ef\u51fa\u5927\u7978\uff0c\u4f60\u77e5\u9053\u4ed6\u662f\u4ec0\u4e48\u4eba\uff1f\u201d\u6768 \u5a49\u9053\uff1a\u201c\u6211\u600e\u4e48\u77e5\u9053\uff1f\u201d\u548c\u6768\u5a49\u4ea4\u60c5\u6700\u597d\u7684\u4e00\u4e2a\u5973\u5175\u8bf4\u9053\uff1a\u201c\u4ed6\u662f\u54b1\u4eec\u7684\u56db\u738b\u5b50\uff01\u5728\u56db\u4e2a\u54e5 \u54e5\u4e4b\u4e2d\uff0c\u516c\u4e3b\u548c\u4ed6\u6700\u597d\u3002\u7b49\u4e0b\u4f60\u5411\u516c\u4e3b\u6c42\u60c5\u5427\u3002\u201d \u3000\u3000\u6768\u5a49\u5403\u4e86\u4e00\u60ca\uff0c\u8fd9\u624d\u77e5\u9053\u5c11\u5e74\u519b\u5b98\u7684\u8eab\u4efd\u4e43\u662f\u6210\u5409\u601d\u6c57\u7684\u7b2c\u56db\u5b50\u62d6\u96f7\u3002\u6768\u5a49\u66fe\u542c\u5f97\u674e\u601d \u5357\u63d0\u8d77\u62d6\u96f7\u4e4b\u4e8b\uff0c\u77e5\u9053\u62d6\u96f7\u548c\u660e\u6167\u516c\u4e3b\u6b63\u662f\u6210\u5409\u6069\u6c57\u6700\u5ba0\u7231\u7684\u4e00\u5b50\u4e00\u5973\uff0c\u62d6\u96f7\u8c6a\u8fc8\u8c41\u8fbe\uff0c\u5bf9 \u4eba\u771f\u8bda\uff0c\u548c\u4ed6\u7684\u4e09\u4e2a\u54e5\u54e5\u5927\u4e0d\u76f8\u540c\u3002 \u3000\u3000\u674e\u601d\u5357\u90a3\u6b21\u53c2\u52a0\u6210\u5409\u601d\u6c57\u7684\u72e9\u730e\uff0c\u4e0e\u62d6\u96f7\u76f8\u8bc6\uff0c\u5f7c\u6b64\u60fa\u60fa\u76f8\u60dc\uff01\u62d6\u96f7\u66fe\u9001\u4ed6\u201c\u54c8\u8fbe\u201d \uff08\u201c\u54c8\u8fbe\u201d\u5373\u624b\u5e15\uff0c\u8499\u53e4\u548c\u897f\u85cf\u7684\u4e60\u4fd7\uff0c\u9001\u54c8\u8fbe\u5373\u662f\u8868\u793a\u53cb\u8c0a\uff09\uff0c\u628a\u674e\u601d\u5357\u5f53\u4f5c\u201c\u5b89\u7b54\u201d \uff08\u597d\u670b\u53cb\uff09\u7684\u3002 \u3000\u3000\u62d6\u96f7\u542c\u89c1\u4e86\u90a3\u5973\u5175\u7684\u8bdd\uff0c\u7b11\u9053\uff1a\u201c\u6211\u90fd\u4e0d\u8bb8\u4f60\u4eec\u602a\u5979\uff0c\u6211\u5f53\u7136\u66f4\u662f\u4e0d\u4f1a\u602a\u5979\u7684\u4e86\u3002\u4f55\u9700 \u4ec0\u4e48\u6c42\u60c5\uff1f\u597d\u5566\uff0c\u73b0\u5728\u4f60\u53ef\u4ee5\u5e26\u6211\u53bb\u89c1\u660e\u6167\u4e86\u5427\uff1f\u201d \u3000\u3000\u6768\u5a49\u9053\u4e86\u4e2a\u6b49\uff0c\u5e26\u9886\u62d6\u96f7\u8fdb\u5e10\u3002\u660e\u6167\u516c\u4e3b\u95f7\u5750\u5e10\u4e2d\uff0c\u5ffd\u89c1\u62d6\u96f7\u6765\u5230\uff0c\u4e0d\u89c9\u53c8\u60ca\u53c8\u559c\uff0c\u8bf4 \u9053\uff1a\u201c\u56db\u54e5\uff0c\u4f60\u4e0d\u662f\u7559\u5728\u548c\u6797\u76d1\u56fd\u7684\u5417\uff0c\u600e\u4e48\u6765\u5230\u8fd9\u513f\u6765\u4e86\uff1f\u201d \u3000\u3000\u62d6\u96f7\u9053\uff1a\u201c\u6211\u542c\u5f97\u7239\u7239\u53d7\u4f24\uff0c\u524d\u51e0\u5929\u5df2\u7ecf\u6765\u5230\u56fa\u539f\u4e86\u3002\u4f60\u8fd9\u4e2a\u4f8d\u5973\u6211\u4ee5\u524d\u597d\u4f3c\u6ca1\u6709\u89c1 \u8fc7\uff0c\u4f60\u51e0\u65f6\u6536\u4e0b\u7684\uff1f\u5979\u4e5f\u597d\u4f3c\u4e0d\u662f\u54b1\u4eec\u8349\u539f\u4e0a\u7684\u59d1\u5a18\u5427\uff1f\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u9053\uff1a\u201c\u539f\u6765\u4f60\u662f\u4ece\u56fa\u539f\u6765\u7684\u3002\u7239\u7239\u600e\u4e48\u6837\u4e86\uff1f\u4e4c\u6c49\u5a1c\u7684\u4e8b\u60c5\u6162\u6162\u6211\u4e0e\u4f60\u518d \u8c08\u3002\u201d\u660e\u6167\u516c\u4e3b\u8fd8\u62ff\u4e0d\u5b9a\u4e3b\u610f\u662f\u5426\u628a\u6768\u5a49\u7684\u6765\u5386\u544a\u8bc9\u4ed6\uff0c\u800c\u4e14\u5979\u4e5f\u662f\u7684\u786e\u6025\u4e8e\u60f3\u8981\u77e5\u9053\u6210\u5409 \u601d\u6c57\u7684\u6d88\u606f\uff0c\u6545\u6b64\u8f7b\u8f7b\u5730\u79fb\u8f6c\u8bdd\u9898\u3002 \u3000\u3000\u62d6\u96f7\u9eef\u7136\u8bf4\u9053\uff1a\u201c\u7239\u7239\u7bad\u4f24\u590d\u53d1\uff0c\u75c5\u52bf\u5782\u5371\u3002\u6211\u6b63\u662f\u5949\u4e86\u7239\u7239\u4e4b\u547d\u53eb\u4f60\u53bb\u7684\uff0c\u7239\u7239\u5bf9\u4f60 \u597d\u50cf\u6709\u4ec0\u4e48\u4e8b\u60c5\u4e0d\u5927\u6ee1\u610f\uff0c\u6211\u5230\u56fa\u539f\u51e0\u5929\uff0c\u4ed6\u90fd\u6ca1\u63d0\u8d77\u4f60\u3002\u4f46\u6628\u665a\u75c5\u52bf\u9006\u8f6c\u4e4b\u540e\uff0c\u7239\u7239\u5374\u662f \u5f88\u60f3\u5ff5\u4f60\uff0c\u4e00\u665a\u53eb\u7740\u4f60\u7684\u540d\u5b57\u3002\u4f60\u8d76\u5feb\u51c6\u5907\uff0c\u4eca\u5929\u5c31\u548c\u6211\u4e00\u540c\u53bb\u5427\u3002\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u5927\u5403\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u600e\u7684\u7a81\u7136\u4f1a\u6c89\u91cd\u8d77\u6765\uff1f\u6211\u8fd9\u91cc\u6bcf\u5929\u63a5\u5230\u7684\u90fd\u662f\u597d\u6d88\u606f\uff0c\u6211 \u8fd8\u4ee5\u4e3a\u7239\u7239\u5df2\u7ecf\u75c5\u597d\u4e86\u5462\u3002\u201d \u3000\u3000\u62d6\u96f7\u82e6\u7b11\u9053\uff1a\u201c\u8fd9\u662f\u7239\u7239\u6050\u6015\u5f71\u54cd\u519b\u5fc3\uff0c\u6240\u4ee5\u4e0d\u80af\u8ba9\u5175\u58eb\u77e5\u9053\u4ed6\u7684\u75c5\u52bf\u6c89\u91cd\u3002\u4f60\u4e0d\u5728\u56fa \u539f\uff0c\u5f53\u7136\u4e0d\u4f1a\u77e5\u9053\u771f\u60c5\u4e86\u3002\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u5fc3\u4e71\u5982\u9ebb\uff0c\u8bf4\u9053\uff1a\u201c\u6c89\u91cd\u5230\u4ec0\u4e48\u7530\u5730\uff0c\u53ef\u6709\u6027\u547d\u4e4b\u5fe7\uff1f\u9547\u56fd\u8fd9\u53ae\u5982\u4eca\u662f\u5426\u8fd8\u5728 \u7239\u7239\u8eab\u8fb9\u4f3a\u5019\u3002\u201d \u3000\u3000\u62d6\u96f7\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u96be\u8bf4\u5f97\u5f88\u3002\u4e0d\u8fc7\u7239\u7239\u5e74\u8fd1\u4e03\u5341\uff0c\u4e00\u751f\u6c57\u9a6c\uff0c\u7acb\u4e0b\u4e86\u8499\u53e4\u9707\u53e4\u94c4 \u4eca\u7684\u529f\u4e1a\uff0c\u5373\u4f7f\u4e07\u4e00\u4e0d\u5e78\uff0c\u4e5f\u662f\u6ca1\u6709\u4ec0\u4e48\u9057\u61be\u7684\u4e86\u3002\u4f60\u4e5f\u4e0d\u5fc5\u592a\u8fc7\u60b2\u4f24\u3002\u201d\u6b47\u4e86\u4e00\u6b47\uff0c\u53c8 \u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u4e0d\u559c\u6b22\u9547\u56fd\u738b\u5b50\uff0c\u7239\u7239\u75c5\u5371\uff0c\u4f60\u7684\u5a5a\u4e8b\u662f\u4e00\u5b9a\u8981\u5ef6\u6401\u7684\u4e86\u3002\u6240\u4ee5\u4f60\u4e5f\u65e0\u987b\u6025 \u4e8e\u60f3\u6cd5\u5bf9\u4ed8\u3002\u8fd9\u6b21\u4f60\u53bb\u56fa\u539f\uff0c\u6709\u6211\u966a\u7740\u4f60\uff0c\u9547\u56fd\u738b\u5b50\u51b3\u4e0d\u6562\u5bf9\u4f60\u7f57\u55e6\u3002\u201d \u3000\u3000\u539f\u6765\u660e\u6167\u516c\u4e3b\u5728\u5144\u5f1f\u4e4b\u4e2d\u548c\u62d6\u96f7\u4ea4\u60c5\u6700\u597d\uff0c\u660e\u6167\u516c\u4e3b\u7684\u5fc3\u4e8b\u662f\u4ece\u6765\u4e0d\u7792\u62d6\u96f7\u7684\u3002\u62d6\u96f7\u4e3a \u4eba\u6b63\u76f4\uff0c\u4e0e\u9547\u56fd\u738b\u5b50\u4e5f\u662f\u4e00\u5411\u4e0d\u548c\u3002 \u3000\u3000\u660e\u6167\u516c\u4e3b\u9053\uff1a\u201c\u597d\uff0c\u4f60\u51fa\u53bb\u4e00\u4f1a\uff0c\u5f85\u6211\u6362\u8fc7\u8863\u88f3\u3002\u987a\u4fbf\u8bf7\u4f60\u53eb\u5979\u4eec\u7ed9\u6211\u5907\u9a6c\uff0c\u8981\u6311\u9009\u4e09 \u5339\u6700\u597d\u7684\u9a8f\u9a6c\u3002\u201d \u3000\u3000\u62d6\u96f7\u5ffd\u9053\uff1a\u201c\u4f60\u51c6\u5907\u5e26\u54ea\u4e00\u4e2a\u4f8d\u5973\u4f34\u4f60\u53bb\uff1f\u201d\u660e\u6167\u516c\u4e3b\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4f60\u95ee\u8fd9\u4e2a\u5e72 \u5417\uff1f\u201d \u3000\u3000\u62d6\u96f7\u76ee\u5149\u8f6c\u5411\u6768\u5a49\uff0c\u8bf4\u9053\uff1a\u201c\u4e4c\u6c49\u5a1c\u7684\u672c\u9886\u5f88\u662f\u4e0d\u9519\uff0c\u521a\u624d\u6211\u90fd\u9669\u4e9b\u7ed9\u5979\u64d2\u4e86\u3002\u4f9d\u6211\u770b \u6765\uff0c\u5979\u7684\u672c\u9886\u4e0d\u4f46\u662f\u5973\u5b50\u4e4b\u4e2d\u7f55\u6709\uff0c\u54b1\u4eec\u7684\u6b66\u571f\u6050\u6015\u4e5f\u6ca1\u6709\u51e0\u4e2a\u6bd4\u5f97\u4e0a\u5979\u3002\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u604d\u7136\u5927\u609f\uff0c\u4f5c\u4e86\u4e2a\u4f1a\u5fc3\u7684\u5fae\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u539f\u6765\u4f60\u662f\u60f3\u6211\u5e26\u5979\u540c\u53bb\u3002\u201d \u3000\u3000\u62d6\u96f7\u9053\uff1a\u201c\u4f60\u6709\u4e00\u4e2a\u672c\u9886\u9ad8\u5f3a\u7684\u4f8d\u5973\u4fdd\u62a4\uff0c\u6211\u4e5f\u53ef\u4ee5\u66f4\u52a0\u653e\u5fc3\u3002\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u9053\uff1a\u201c\u597d\uff0c\u4f60\u5148\u51fa\u53bb\uff0c\u5f85\u6211\u95ee\u95ee\u5979\u7684\u610f\u601d\u3002\u201d \u3000\u3000\u62d6\u96f7\u8d70\u51fa\u5e10\u5e55\uff0c\u6768\u5a49\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u53bb\u3002\u201d\u5fc3\u60f3\uff1a\u201c\u62d6\u96f7\u5df2\u7ecf\u8d77\u7591\uff0c\u770b\u6765\u4ed6\u5df2\u7ecf\u8bc6\u7834\u6211\u662f \u6c49\u4eba\u3002\u867d\u7136\u4ed6\u662f\u674e\u601d\u5357\u7684\u670b\u53cb\uff0c\u4f46\u4ed6\u6bd5\u7adf\u4e5f\u662f\u4e00\u4e2a\u738b\u5b50\uff0c\u4e0d\u89c1\u5f97\u4ed6\u5c31\u4f1a\u4e3a\u4e86\u670b\u53cb\u7684\u7f18\u6545\u653e\u7eb5 \u94a6\u72af\u3002\u6211\u7684\u8eab\u4efd\u8fd8\u662f\u4ee5\u4e0d\u8ba9\u4ed6\u77e5\u9053\u7684\u4e3a\u5b9c\u3002\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u6c89\u541f\u7247\u523b\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u66ff\u4f60\u60f3\u8fc7\u4e86\uff0c\u6211\u770b\u4f60\u8fd8\u662f\u548c\u6211\u540c\u53bb\u7684\u597d\uff01\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u660e\u6167\u516c\u4e3b\u9053\uff1a\u201c\u4f60\u5355\u72ec\u7559\u5728\u8fd9\u513f\uff0c\u6211\u4e0d\u653e\u5fc3\u3002\u201d\u987f\u4e86\u4e00\u987f\uff0c\u63a5\u4e0b \u53bb\u8bf4\u9053\uff1a\u201c\u90a3\u665a\u4ed6\u4eec\u5df2\u7ecf\u7591\u5fc3\u523a\u5ba2\u662f\u9003\u5230\u8fd9\u513f\uff0c\u4e0d\u8fc7\u788d\u7740\u6211\u624d\u4e0d\u6562\u6765\u641c\u67e5\u7f62\u4e86\u3002\u6211\u8d70\u4e86\u4e4b \u540e\uff0c\u53ea\u6015\u4f59\u4e00\u4e2d\u4e0d\u6562\u660e\u6765\u4e5f\u4f1a\u6697\u6765\u3002\u201d \u3000\u3000\u6768\u5a49\u4e00\u60f3\uff0c\u4f59\u4e00\u4e2d\u9634\u9669\u96be\u6d4b\uff0c\u62d6\u96f7\u81f3\u5c11\u6bd4\u4ed6\u597d\u4e9b\uff0c\u4e0e\u5176\u7ed9\u4f59\u4e00\u4e2d\u6697\u7b97\uff0c\u5b81\u53ef\u8ba9\u62d6\u96f7\u8d77 \u7591\uff0c\u56e0\u6b64\u4e5f\u5c31\u7b54\u5e94\u4e86\u3002 \u3000\u3000\u56fa\u539f\u5728\u516d\u76d8\u5c71\u4e4b\u5357\u4e00\u767e\u4f59\u91cc\uff0c\u9a8f\u9a6c\u75be\u9a70\uff0c\u5f53\u5929\u665a\u4e0a\u4fbf\u5230\u3002\u62d6\u96f7\u4e0e\u660e\u6167\u516c\u4e3b\u8fdb\u5165\u5927\u6c57\u7684\u91d1 \u5e10\u63a2\u75c5\uff0c\u6768\u5a49\u5219\u5728\u53e6\u5916\u4e00\u5ea7\u4f9b\u7ed9\u516c\u4e3b\u4f8d\u5973\u4e13\u7528\u7684\u5e10\u5e55\u6b47\u606f\u3002\u6b63\u662f\uff1a \u3000\u3000\u660e\u77e5\u4e0d\u662f\u4f34\uff0c\u60c5\u6025\u4e14\u76f8\u968f\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b\u2014\u2014\u7b2c\u5341\u516b\u56de\u3000\u68f1\u68f1\u98ce\u9aa8\u60ca\u96c4\u4e3b\u3000\u60d8\u60d8\u60c5\u6000\u6005\u6167\u59ec \u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b \u7b2c\u5341\u516b\u56de\u3000\u68f1\u68f1\u98ce\u9aa8\u60ca\u96c4\u4e3b\u3000\u60d8\u60d8\u60c5\u6000\u6005\u6167\u59ec \u3000\u3000\u62d6\u96f7\u4e0e\u660e\u6167\u516c\u4e3b\u8d70\u8fd1\u91d1\u5e10\uff0c\u521a\u597d\u542c\u89c1\u6210\u5409\u601d\u6c57\u5728\u5e10\u4e2d\u9a82\u4eba\uff1a\u201c\u54fc\uff0c\u8bf4\u4ec0\u4e48\u591a\u884c\u4ec1\u4e49\uff0c\u5c11 \u65bd\u6740\u622e\uff0c\u8fd9\u4e0d\u8fc7\u662f\u8150\u5112\u4e4b\u89c1\uff0c\u8fc2\u62d9\u4e4b\u8a00\uff01\u6211\u82e5\u4e0d\u628a\u654c\u4eba\u6740\u5f97\u80c6\u5bd2\uff0c\u7109\u80fd\u4f7f\u56db\u65b9\u6151\u670d\uff1f\u54fc\uff0c\u6211 \u53d7\u547d\u4e8e\u5929\uff0c\u5929\u4e0b\u672a\u66fe\u4e00\u7edf\uff0c\u6211\u8981\u6b7b\u4e5f\u6b7b\u4e0d\u4e86\u7684\uff0c\u4e0d\u8981\u4f60\u533b\uff0c\u4f60\u8d70\uff0c\u4f60\u8d70\uff01\u6211\u5012\u4e0d\u4fe1\uff0c\u4e0d\u8981\u4f60 \u96be\u9053\u6211\u5c31\u4f1a\u6b7b\uff01\u201d\u9a82\u58f0\u4e2d\u4e00\u4e2a\u80cc\u7740\u836f\u56ca\u7684\u8001\u8005\u8d70\u51fa\u5e10\u6765\u3002 \u3000\u3000\u62d6\u96f7\u83ab\u540d\u5176\u5999\uff0c\u95ee\u90a3\u8f6e\u503c\u7684\u91d1\u5e10\u6b66\u571f\u9053\uff1a\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u6b66\u58eb\u9053\uff1a\u201c\u8fd9\u4eba\u662f\u6c49\u4eba\u7684\u540d\u533b\u67f3\u5143\u5b97\uff0c\u597d\u4e0d\u5bb9\u6613\u624d\u8bf7\u5f97\u4ed6\u6765\u7ed9\u5927\u6c57\u770b\u75c5\u7684\u3002\u201d \u3000\u3000\u62d6\u96f7\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u5927\u6c57\u53c8\u628a\u4ed6\u8d76\u8dd1\u5462\uff1f\u201d \u3000\u3000\u6b66\u58eb\u9053\uff1a\u201c\u542c\u8bf4\u4ed6\u529d\u5927\u6c57\u591a\u884c\u4ec1\u4e49\uff0c\u5c11\u65bd\u6740\u622e\uff0c\u8fd9\u6837\u624d\u80fd\u5fc3\u6c14\u548c\u5e73\uff0c\u76ca\u5bff\u5ef6\u5e74\u3002\u5927\u6c57\u542c \u4e0d\u8fdb\u53bb\uff0c\u6240\u4ee5\u9a82\u4ed6\u3002\u201d \u3000\u3000\u62d6\u96f7\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u8fde\u8fd9\u4f4d\u540d\u533b\u90fd\u4e0d\u6562\u4e0b\u836f\u4e86\u4e48\uff1f\u201d\u6b66\u58eb\u9ed8\u9ed8\u5730\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u62d6\u96f7\u548c\u660e\u6167\u516c\u4e3b\u8fde\u5fd9\u8d70\u8fdb\u91d1\u5e10\uff0c\u53ea\u89c1\u6210\u5409\u601d\u6c57\u6c14\u5598\u5401\u5401\u5730\u95ed\u7740\u773c\u775b\uff0c\u60f3\u662f\u9a82\u4eba\u4e4b\u540e\uff0c\u5341 \u5206\u75b2\u5026\u3002\u6210\u5409\u601d\u6c57\u7684\u540e\u5983\u548c\u4e09\u4e2a\u513f\u5b50\u672f\u8d64\u3001\u5bdf\u5408\u53f0\u3001\u7a9d\u9614\u53f0\u56f4\u5728\u4ed6\u7684\u8eab\u8fb9\u3002 \u3000\u3000\u6210\u5409\u601d\u6c57\u7684\u5983\u5b50\u5728\u4ed6\u8033\u8fb9\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u5927\u6c57\uff0c\u4f60\u6700\u5ba0\u7231\u7684\u963f\u9791\u6d77\u522b\u59ec\u548c\u62d6\u96f7\u6765\u4e86\uff01\u201d\u62d6 \u96f7\u8d70\u4e0a\u524d\u9053\uff1a\u201c\u7239\uff0c\u6211\u548c\u59b9\u59b9\u6765\u4e86\uff0c\u4f60\u597d\u70b9\u5417\uff1f\u201d \u3000\u3000\u6210\u5409\u601d\u6c57\u7f13\u7f13\u5f20\u5f00\u773c\u775b\uff0c\u5ffd\u5730\u53c8\u5927\u53eb\u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u4f60\u6015\u6211\u6d3b\u4e0d\u4e0b\u53bb\u5417\uff1f\u6211\u8981\u628a\u4e16\u754c\u53d8\u4f5c \u8499\u53e4\u4eba\u7684\u7267\u573a\uff0c\u8c01\u6562\u8fdd\u6297\u6211\u7684\u610f\u65e8\uff1f\u6211\u662f\u4e00\u5b9a\u8fd8\u8981\u6d3b\u4e0b\u53bb\u7684\uff01\u201d \u3000\u3000\u53ef\u601c\u6210\u5409\u601d\u6c57\u6b63\u662f\u4e3a\u4e86\u611f\u5230\u751f\u547d\u7684\u5feb\u8981\u6d88\u901d\u800c\u53d8\u5f97\u75af\u72c2\uff0c\u8fd9\u4e48\u5927\u53eb\u4e00\u901a\u4e4b\u540e\uff0c\u7acb\u5373\u53c8\u660f \u8ff7\u8fc7\u53bb\u3002 \u3000\u3000\u5e74\u957f\u7684\u738b\u516c\u6084\u58f0\u8bf4\u9053\uff1a\u201c\u770b\u8fd9\u6837\u5b50\uff0c\u6050\u6015\u5927\u6c57\u662f\u4e0d\u6210\u4e86\u3002\u54b1\u4eec\u8fd8\u662f\u8bf7\u4ed6\u5429\u5490\u540e\u4e8b\u5427\uff01\u201d \u3000\u3000\u7a9d\u9614\u53f0\u9053\uff1a\u201c\u4e0d\u77e5\u5927\u6c57\u8fd8\u4f1a\u4e0d\u4f1a\u9192\u6765\uff1f\u201d \u3000\u3000\u672f\u8d64\u9053\uff1a\u201c\u6211\u662f\u957f\u5b50\uff0c\u5f53\u7136\u5e94\u7531\u6211\u7ee7\u627f\u6c57\u4f4d\uff01\u201d\u5bdf\u5408\u53f0\u9053\uff1a\u201c\u5478\uff01\u4f60\u914d\uff01\u201d \u3000\u3000\u6210\u5409\u601d\u6c57\u6726\u80e7\u4e2d\u4f3c\u4e4e\u542c\u5f97\u6709\u4eba\u4e89\u5435\uff0c\u53c8\u7741\u5f00\u4e86\u773c\u775b\u3002 \u3000\u3000\u5e74\u7eaa\u6700\u957f\u7684\u4e24\u4e2a\u738b\u516c\u8dea\u4e0b\u53bb\u9053\uff1a\u201c\u4f60\u50cf\u9ad8\u5c71\u4f3c\u7684\u91d1\u8eab\uff0c\u5982\u679c\u5012\u584c\u4e86\uff0c\u4f60\u7684\u5927\u6c57\u56fd\u7531\u8c01\u6765 \u7edf\u6cbb\uff1f\u4f60\u50cf\u67f1\u6881\u4f3c\u7684\u91d1\u8eab\uff0c\u5982\u679c\u503e\u5012\u4e86\uff0c\u4f60\u7684\u795e\u5a01\u5927\u6c30\uff0c\u7531\u8c01\u6765\u9ad8\u4e3e\uff1f\u4f60\u7684\u56db\u4e2a\u513f\u5b50\u4e4b\u4e2d\uff0c \u7531\u8c01\u6765\u6267\u653f\uff1f\u4f60\u7684\u513f\u5b50\u4eec\uff0c\u5144\u5f1f\u4eec\uff0c\u5c5e\u6c11\u767e\u59d3\u4eec\u4ee5\u53ca\u540e\u5983\u7b49\u4eba\uff0c\u8bf7\u5927\u6c57\u4f60\u7ed9\u6211\u4eec\u7559\u4e0b\u5723 \u65e8\u3002\u201d \u3000\u3000\u6210\u5409\u601d\u6c57\u9893\u7136\u5730\u53f9\u4e86\u53e3\u6c14\uff0c\u81ea\u8a00\u81ea\u8bed\u9053\uff1a\u201c\u6211\u5f53\u771f\u662f\u8981\u6b7b\u4e86\u4e48\uff1f\u201d\u6b64\u65f6\u4ed6\u7a0d\u7a0d\u6e05\u9192\u4e86 \u4e9b\uff0c\u5df2\u7ecf\u77e5\u9053\u81ea\u5df1\u662f\u62d7\u4e0d\u8fc7\u6b7b\u795e\u4e86\u3002 \u3000\u3000\u4f17\u4eba\u90fd\u4e0d\u6562\u542d\u58f0\uff0c\u6210\u5409\u601d\u6c57\u7684\u76ee\u5149\u7f13\u7f13\u7684\u4ece\u56db\u4e2a\u513f\u5b50\u8eab\u4e0a\u63a0\u8fc7\uff0c\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u4f60 \u4eec\u8fd8\u8bb0\u5f97\u6211\u6559\u4f60\u4eec\u6298\u7bad\u7684\u6545\u4e8b\u4e48\uff1f\u4f60\u4eec\u8981\u50cf\u4e00\u675f\u7bad\u4f3c\u7684\u805a\u5728\u4e00\u8d77\uff0c\u654c\u4eba\u624d\u4e0d\u80fd\u5c06\u4f60\u4eec\u6298\u65ad\u3002 \u5982\u4eca\u6211\u8fd8\u672a\u6b7b\uff0c\u4f60\u4eec\u5c31\u4e92\u76f8\u4e89\u5435\uff0c\u6211\u6b7b\u4e86\u4e5f\u4e0d\u80fd\u5fc3\u5b89\u3002\u201d\u56db\u4e2a\u513f\u5b50\u9f50\u58f0\u5e94\u4e86\u4e00\u4e2a\u201c\u662f\u201d\u5b57\uff0c \u53ef\u662f\u5bdf\u5408\u53f0\u4e0e\u672f\u8d64\u4f9d\u7136\u6012\u76ee\u800c\u89c6\uff0c\u663e\u89c1\u654c\u610f\u672a\u6d88\u3002 \u3000\u3000\u539f\u6765\u672f\u8d64\u7684\u6bcd\u4eb2\u66fe\u88ab\u6210\u5409\u601d\u6c57\u7684\u654c\u4eba\u8511\u91cc\u5409\u90e8\u6240\u4fd8\uff0c\u672f\u8d64\u662f\u5728\u91ca\u4fd8\u4e4b\u540e\u4ed6\u6bcd\u4eb2\u5728\u5f52\u9014\u4e2d \u751f\u7684\uff0c\u56e0\u6b64\u4ed6\u7684\u5144\u5f1f\u8bf4\u4ed6\u201c\u6765\u5386\u4e0d\u660e\u201d\uff0c\u90fd\u4e0d\u628a\u4ed6\u5f53\u4f5c\u957f\u5144\u770b\u5f85\uff0c\u5c24\u5176\u662f\u5bdf\u5408\u53f0\u66f4\u4e0d\u670d\u4ed6\uff0c \u66fe\u6709\u597d\u51e0\u6b21\u5f53\u9762\u9a82\u4ed6\u201c\u91ce\u79cd\u201d\uff0c\u521a\u624d\u6210\u5409\u601d\u6c57\u660f\u8ff7\u4e4b\u65f6\uff0c\u672f\u8d64\u60f3\u4ee5\u957f\u5b50\u7684\u8eab\u4efd\u7ee7\u627f\u5927\u4f4d\uff0c\u5bdf \u5408\u53f0\u53c8\u7acb\u5373\u65a5\u4ed6\u201c\u4e0d\u914d\u201d\u3002\u5176\u65f6\u6210\u5409\u601d\u6c57\u6070\u597d\u9192\u6765\uff0c\u90fd\u542c\u89c1\u4e86\u3002 \u3000\u3000\u6210\u5409\u601d\u6c57\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u82e5\u7acb\u672f\u8d64\u4e3a\u6c57\uff0c\u4ed6\u7684\u5144\u5f1f\u4e00\u5b9a\u4e0d\u80af\u670d\u4ed6\u3002\u5bdf\u5408\u53f0\u5f88\u4f1a\u6253\u4ed7\uff0c\u4f46\u5341 \u5206\u8dcb\u6248\uff0c\u7acb\u4ed6\u4e3a\u6c57\uff0c\u53ea\u6015\u4e5f\u4f1a\u60f9\u8d77\u5185\u4e71\u3002\u7a9d\u9614\u53f0\u6027\u60c5\u5fe0\u539a\uff0c\u6700\u5f97\u90e8\u4e0b\u62e5\u6234\uff0c\u53ef\u662f\u7cbe\u660e\u4e0d\u8db3\uff0c \u6b64\u65f6\u82e5\u7acb\u4ed6\u4e3a\u6c57\uff0c\u5bf9\u4ed6\u6050\u6015\u662f\u7978\u975e\u798f\u3002\u201d \u3000\u3000\u8981\u77e5\u7a9d\u9614\u53f0\u867d\u5f97\u90e8\u4e0b\u62e5\u6234\uff0c\u4f46\u5728\u516d\u76d8\u5c71\u8fd9\u4e00\u8def\u7684\u519b\u961f\u5374\u662f\u9547\u56fd\u738b\u5b50\u7edf\u7387\u7684\uff0c\u9547\u56fd\u738b\u5b50\u662f \u62e5\u62a4\u5bdf\u5408\u53f0\u7684\uff0c\u5bdf\u5408\u53f0\u5b9a\u7136\u4e0d\u80af\u8ba9\u7a9d\u9614\u53f0\u5b89\u5750\u5927\u6c57\u7684\u5b9d\u5ea7\uff0c\u6545\u6b64\u6210\u5409\u601d\u6c57\u867d\u7136\u60f3\u8981\u7a9d\u9614\u53f0\u505a \u4ed6\u7684\u7ee7\u627f\u4eba\uff0c\u5374\u4e5f\u8003\u8651\u5230\u4e86\u65f6\u5730\u4e0d\u5b9c\u3002 \u3000\u3000\u6700\u540e\u6210\u5409\u601d\u6c57\u60f3\u8d77\u4e86\u62d6\u96f7\uff0c\u62d6\u96f7\u662f\u4ed6\u5ba0\u7231\u7684\u5c0f\u513f\u5b50\u3002\u201c\u62d6\u96f7\u4eba\u5f88\u80fd\u5e72\uff0c\u53ef\u662f\u4ed6\u5e74\u7eaa\u6700 \u5c0f\uff0c\u5a01\u4fe1\u672a\u7acb\uff0c\u8981\u4ed6\u6276\u52a9\u7a9d\u9614\u53f0\u6700\u597d\uff0c\u7acb\u4ed6\u4e3a\u6c57\uff0c\u5374\u4e0d\u9002\u5b9c\u3002\u201d \u3000\u3000\u6210\u5409\u601d\u6c57\u8e0c\u8e87\u672a\u51b3\uff0c\u559d\u4e86\u4e00\u53e3\u53c2\u6c64\u4e4b\u540e\uff0c\u8bf4\u9053\uff1a\u201c\u5929\u4e0b\u5927\u5f97\u5f88\u3002\u4f60\u4eec\u6253\u5e73\u4e86\u5929\u4e0b\uff0c\u5404\u9886 \u4e00\u4e2a\u6c57\u56fd\uff0c\u4e5f\u5c31\u4e0d\u5fc5\u4e89\u5435\u4e86\u3002\u201d \u3000\u3000\u5bdf\u5408\u53f0\u9053\uff1a\u201c\u7239\u7239\u8bf4\u7684\u662f\u3002\u4f46\u6c49\u4eba\u6709\u4e24\u53e5\u8bdd\u8bf4\uff1a\u5929\u65e0\u4e8c\u65e5\uff0c\u6c11\u65e0\u4e8c\u738b\u3002\u8fd9\u8bdd\u4e5f\u4f3c\u4e4e\u5f88\u6709 \u9053\u7406\u3002\u201d \u3000\u3000\u6210\u5409\u601d\u6c57\u7709\u5934\u4e00\u76b1\uff0c\u4e3b\u610f\u5df2\u5b9a\uff0c\u8bf4\u9053\uff1a\u201c\u505a\u9996\u9886\u7684\u4eba\u5e94\u8be5\u5f97\u5230\u6700\u5927\u591a\u6570\u7684\u4eba\u7684\u62e5\u62a4\uff0c\u6211 \u5c31\u662f\u5728\u65a1\u96be\u6cb3\u7684\u5927\u4f1a\u4e2d\uff0c\u53d7\u5404\u90e8\u914b\u957f\u4e00\u81f4\u63a8\u4e3e\u624d\u505a\u5927\u6c57\u7684\u3002\u8fd9\u4e2a\u89c4\u4f8b\u5f88\u597d\uff0c\u5e94\u8be5\u7acb\u4e3a\u6cd5\u5236\u3002 \u4ee5\u540e\u4e16\u4e16\u4ee3\u4ee3\uff0c\u6c38\u8fdc\u9075\u4f9d\u3002\u201d \u3000\u3000\u4e24\u4e2a\u6700\u5e74\u957f\u7684\u738b\u516c\u8bf4\u9053\uff1a\u201c\u8bf7\u5927\u6c57\u8be6\u52a0\u6307\u793a\u3002\u201d\u56e0\u4e3a\u8fd9\u53ea\u662f\u4e00\u4e2a\u539f\u5219\uff0c\u8fd8\u6ca1\u6709\u63a5\u89e6\u5230\u5177 \u4f53\u7684\u95ee\u9898\u3002 \u3000\u3000\u6210\u5409\u601d\u6c57\u9053\uff1a\u201c\u597d\uff0c\u4f60\u4eec\u542c\u7740\uff1a\u6211\u6b7b\u4e4b\u540e\uff0c\u4f60\u4eec\u8981\u5c06\u6211\u7684\u5c38\u4f53\u8fd0\u56de\u548c\u6797\uff0c\u9650\u4e09\u4e2a\u6708\u4e4b \u5185\uff0c\u53ec\u96c6\u5404\u90e8\u914b\u957f\u3001\u738b\u516c\u3001\u5404\u519b\u5c06\u9886\u5f00\u4e2a\u5927\u4f1a\uff0c\u8fd9\u4e2a\u4f1a\u53ef\u4ee5\u53eb\u505a\u2018\u5e93\u91cc\u5c14\u6cf0\u4f1a\u2019\uff08\u8499\u8bed\u2018\u5e93 \u91cc\u5c14\u6cf0\u2019\u662f\u2018\u5404\u4e2a\u6709\u6743\u529b\u7684\u4eba\u2019\u7684\u610f\u601d\u3002\uff09\uff0c\u5e93\u91cc\u5c14\u6cf0\u4f1a\u79c9\u627f\u6211\u7684\u9057\u547d\uff0c\u63a8\u4e3e\u7ee7\u4f4d\u7684\u5927\u6c57\u3002 \u65b0\u7684\u5927\u6c57\u672a\u63a8\u51fa\u4e4b\u524d\uff0c\u7531\u62d6\u96f7\u76d1\u56fd\uff01\u201d \u3000\u3000\u5bdf\u5408\u53f0\u542c\u4e86\u6210\u5409\u601d\u6c57\u7684\u9057\u5631\uff0c\u5927\u5931\u6240\u671b\u3002\u5c24\u5176\u4e0d\u5fff\u7684\u662f\uff0c\u975e\u4f46\u5927\u6c57\u4e4b\u4f4d\u6ca1\u6709\u786e\u5b9a\uff0c\u8fde \u201c\u76d1\u56fd\u201d\u4e5f\u6ca1\u4ed6\u7684\u4efd\u513f\u3002\u4e0d\u8fc7\u6b63\u56e0\u4e3a\u5927\u6c57\u4e4b\u4f4d\u672a\u5b9a\uff0c\u4ed6\u8fd8\u5b58\u6709\u4e00\u7ebf\u5e0c\u671b\u3002 \u3000\u3000\u6000\u6709\u91ce\u5fc3\u7684\u4eba\u603b\u662f\u628a\u81ea\u5df1\u4f30\u8ba1\u8fc7\u9ad8\u7684\uff0c\u5bdf\u5408\u53f0\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6211\u6253\u4ed7\u529f\u52b3\u6700\u5927\uff0c\u738b\u516c\u3001\u914b \u957f\uff0c\u54ea\u4e2a\u4e0d\u6015\u6211\u51e0\u5206\uff1f\u5e93\u91cc\u5c14\u6cf0\u4f1a\u4e2d\uff0c\u53ea\u8981\u6709\u51e0\u4e2a\u5f97\u529b\u7684\u4eba\u52a9\u6211\uff0c\u90a3\u4e9b\u914b\u957f\u738b\u516c\u81ea\u5fc5\u968f\u58f0\u9644 \u548c\u3002\u8fd9\u5927\u6c57\u7684\u5b9d\u5ea7\uff0c\u7ec8\u5f52\u662f\u6211\u56ca\u4e2d\u4e4b\u7269\u3002\u201d\u6210\u5409\u601d\u6c57\u7684\u9057\u5631\u4e0d\u80fd\u66f4\u6539\uff0c\u5bdf\u5408\u53f0\u53c8\u60f3\u5f97\u5982\u610f\uff0c \u56e0\u6b64\u4e5f\u5c31\u4e0d\u613f\u5192\u9669\u53bb\u53d1\u52a8\u5175\u53d8\u94f2\u9664\u7a9d\u9614\u53f0\u548c\u62d6\u96f7\u4e86\u3002 \u3000\u3000\u4eba\u4eba\u90fd\u5728\u51dd\u795e\u8046\u542c\u6210\u5409\u601d\u6c57\u7684\u9057\u5631\uff0c\u9762\u4e0a\u7684\u8868\u60c5\u56e0\u5404\u4eba\u5229\u5bb3\u5173\u7cfb\u7684\u4e0d\u540c\u800c\u6216\u559c\u6216\u5fe7\u3002\u53ea \u6709\u660e\u6167\u516c\u4e3b\u5bf9\u9057\u5631\u65e0\u6240\u5173\u6000\uff0c\u5979\u4e00\u5fc3\u53ea\u662f\u5173\u6000\u4e34\u5371\u7684\u8001\u7236\u3002 \u3000\u3000\u6210\u5409\u601d\u6c57\u5728\u8fd9\u201c\u56de\u5149\u8fd4\u7167\u201d\u4e4b\u9645\uff0c\u611f\u89e6\u7279\u522b\u7075\u654f\uff0c\u4ed6\u770b\u5230\u4e86\u660e\u6167\u516c\u4e3b\u773c\u89d2\u6676\u83b9\u7684\u6cea\u73e0\uff0c \u4e0d\u89c9\u5fc3\u91cc\u4e00\u9178\uff0c\u60f3\u9053\uff1a\u201c\u5230\u5e95\u662f\u963f\u9791\u6d77\u522b\u59ec\u75bc\u6211\uff0c\u4e0d\u50cf\u5bdf\u5408\u53f0\u4ed6\u4eec\uff0c\u6211\u8fd8\u672a\u6b7b\uff0c\u4ed6\u4eec\u5c31\u5728\u52fe \u5fc3\u6597\u89d2\u4e86\u3002\u201d \u3000\u3000\u6210\u5409\u601d\u6c57\u5fae\u611f\u6b49\u759a\uff0c\u4f4e\u58f0\u53eb\u9053\uff1a\u201c\u963f\u9791\u6d77\u522b\u59ec\uff01\u201d\u660e\u6167\u516c\u4e3b\u9053\uff1a\u201c\u7239\uff0c\u6211\u5728\u8fd9\u513f\u3002\u201d\u6210 \u5409\u601d\u6c57\u629a\u7740\u5979\u7684\u624b\u8bf4\u9053\uff1a\u201c\u6211\u5f88\u62b1\u6b49\u672a\u80fd\u7b54\u5e94\u4f60\u6700\u540e\u4e00\u4e2a\u8bf7\u6c42\uff0c\u4f60\u5fc3\u91cc\u8fd8\u5728\u602a\u6211\u5417\uff1f\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u77e5\u9053\u4ed6\u662f\u6307\u81ea\u5df1\u548c\u9547\u56fd\u738b\u5b50\u7684\u5a5a\u4e8b\uff0c\u4ed6\u8fd9\u4e48\u8bf4\uff0c\u5df2\u7ecf\u662f\u6709\u70b9\u6094\u610f\u7684\u4e86\u3002\u660e\u6167\u516c \u4e3b\u4e0d\u77e5\u5982\u4f55\u56de\u7b54\u624d\u597d\uff0c\u773c\u6cea\u4e0d\u81ea\u7981\u5730\u7c0c\u7c0c\u800c\u4e0b\uff0c\u786c\u54bd\u8bf4\u9053\uff1a\u201c\u7239\uff0c\u6211\u4efb\u51ed\u4f60\u7684\u4e3b\u610f\u3002\u201d\u6210\u5409 \u601d\u6c57\u9053\uff1a\u201c\u4f60\u4e0d\u5fc5\u96be\u8fc7\uff0c\u6211\u6b7b\u4e4b\u540e\uff0c\u4f60\u7684\u56db\u54e5\u62d6\u96f7\u4e00\u5b9a\u4f1a\u7167\u987e\u4f60\u7684\u3002\u4f60\u8ba9\u62d6\u96f7\u7ed9\u4f60\u4f5c\u4e3b \u5427\u3002\u201d\u8bdd\u4e2d\u6709\u8bdd\uff0c\u5373\u662f\u628a\u660e\u6167\u516c\u4e3b\u7684\u5a5a\u59fb\uff0c\u4ea4\u7ed9\u62d6\u96f7\u5904\u7f6e\u4e86\u3002 \u3000\u3000\u8981\u77e5\u6210\u5409\u601d\u6c57\u6b64\u9645\u8fd8\u5f97\u5229\u7528\u9547\u56fd\u738b\u5b50\u7684\u5175\u529b\uff0c\u6240\u4ee5\u4ed6\u4e0d\u80fd\u660e\u8bf4\u3002\u4f46\u82e5\u5c06\u6765\u65f6\u79fb\u52bf\u6613\uff0c\u9547 \u56fd\u738b\u5b50\u7684\u5229\u7528\u4ef7\u503c\u82e5\u7136\u6d88\u5931\uff0c\u8fd9\u4e00\u5b97\u5973\u513f\u6240\u4e0d\u613f\u610f\u7684\u5a5a\u59fb\uff0c\u6210\u5409\u601d\u6c57\u5f53\u7136\u4e5f\u5c31\u65e0\u987b\u575a\u6301\u3002\u4e0d \u8fc7\u5c06\u6765\u65f6\u52bf\u53d8\u5f97\u5982\u4f55\uff0c\u6210\u5409\u601d\u6c57\u4e5f\u662f\u96be\u6599\uff0c\u6545\u6b64\u53ea\u80fd\u4ea4\u7ed9\u62d6\u96f7\u5904\u7f6e\u3002\u4ed6\u8fd9\u51e0\u53e5\u8bdd\u4e5f\u5373\u662f\u5411\u62d6 \u96f7\u6697\u793a\uff1a\u201c\u5018\u82e5\u4f60\u5c06\u6765\u8fd8\u8981\u5229\u7528\u9547\u56fd\u738b\u5b50\uff0c\u90a3\u5c31\u4e0d\u80fd\u8ba9\u59b9\u5b50\u6094\u5a5a\u3002\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u542c\u61c2\u7236\u4eb2\u7684\u8bdd\uff0c\u54ed\u7740\u53eb\u9053\uff1a\u201c\u7239\uff0c\u4f60\u4e0d\u80fd\u6b7b\uff01\u201d\u6210\u5409\u601d\u6c57\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a \u201c\u6211\u73b0\u5728\u660e\u767d\u4e86\uff0c\u4eba\u603b\u662f\u8981\u6b7b\u7684\uff01\u6211\u662f\u76f8\u4fe1\u5168\u4e16\u754c\u4f1a\u53d8\u4f5c\u8499\u53e4\u4eba\u7684\u7267\u573a\uff0c\u53ea\u5bf9\u60dc\u8fd9\u4e00\u5929\u6211\u662f \u770b\u4e0d\u89c1\u4e86\uff01\u201d\u8bf4\u8bdd\u7684\u58f0\u97f3\u8d8a\u6765\u8d8a\u662f\u5fae\u5f31\uff0c\u4e00\u4ee3\u5929\u9a84\uff0c\u7ec8\u4e8e\u4e00\u7791\u4e0d\u89c6\u3002 \u3000\u3000\u660e\u6167\u516c\u4e3b\u53f7\u9676\u5927\u54ed\uff0c\u5bdf\u5408\u4f1a\u9053\uff1a\u201c\u4f60\u522b\u54ed\u4e71\u4e86\u4eba\u5fc3\uff0c\u54b1\u4eec\u8fd8\u8981\u5546\u91cf\u5927\u4e8b\u5462\u3002\u201d\u5f53\u4e0b\u738b \u516c\u3001\u540e\u5983\u3001\u5c06\u9886\u7b49\u4eba\uff0c\u5c31\u5728\u5e10\u4e2d\u5f00\u4e2a\u4e34\u65f6\u4f1a\u8bae\uff0c\u5546\u8bae\u5982\u4f55\u7ed9\u6210\u5409\u601d\u6c57\u4e3e\u4e27\uff0c\u4ee5\u53ca\u653b\u91d1\u7684\u519b\u4e8b \u884c\u52a8\u662f\u505c\u6b62\u8fd8\u662f\u7ee7\u7eed\u7b49\u7b49\u95ee\u9898\u3002\u5728\u4f1a\u4e2d\u56e0\u5229\u5bb3\u5173\u7cfb\u7684\u4e0d\u540c\uff0c\u5c11\u4e0d\u514d\u53c8\u662f\u4e00\u573a\u4e89\u5435\u3002 \u3000\u3000\u9547\u56fd\u738b\u5b50\u867d\u7136\u542c\u4e0d\u61c2\u6210\u5409\u601d\u6c57\u4e34\u7ec8\u4e4b\u9645\u5bf9\u660e\u6167\u516c\u4e3b\u6240\u8bf4\u7684\u90a3\u51e0\u53e5\u8bdd\u7684\u542b\u610f\uff0c\u4f46\u4ea6\u9690\u9690\u611f \u5230\u201c\u5927\u4e8b\u201d\u4e0d\u5999\u3002\u4e00\u6765\u62d6\u96f7\u4e0e\u4ed6\u4e00\u5411\u4e0d\u548c\uff0c\u5982\u4eca\u7531\u62d6\u96f7\u76d1\u56fd\uff0c\u81ea\u662f\u5bf9\u4ed6\u4e0d\u5229\uff1b\u4e8c\u6765\u6210\u5409\u601d\u6c57 \u4e00\u6b7b\uff0c\u6309\u7167\u8499\u53e4\u7684\u4e60\u4fd7\uff0c\u867d\u7136\u4e0d\u5fc5\u5982\u6c49\u4eba\u4e4b\u5b88\u4e09\u5e74\u4e4b\u5b5d\uff0c\u4f46\u4ed6\u4e0e\u660e\u6167\u516c\u4e3b\u7684\u5a5a\u4e8b\u81f3\u5c11\u4e5f\u8981\u6401 \u5230\u65b0\u7684\u5927\u6c57\u7ee7\u4f4d\u4e4b\u540e\u4e86\u3002\u4ed6\u5f53\u7136\u77e5\u9053\u660e\u6167\u516c\u4e3b\u4e0d\u559c\u6b22\u4ed6\uff0c\u5a5a\u4e8b\u6401\u7f6e\u4e0b\u6765\uff0c\u8d8a\u8fdf\u8d8a\u662f\u5bf9\u4ed6\u4e0d \u5229\u3002 \u3000\u3000\u5bdf\u5408\u53f0\u6084\u6084\u5730\u5c06\u9547\u56fd\u738b\u5b50\u62c9\u8fc7\u4e00\u8fb9\uff0c\u8bf4\u9053\uff1a\u201c\u91d1\u56fd\u8fdf\u65e9\u662f\u54b1\u4eec\u56ca\u4e2d\u4e4b\u7269\uff0c\u4f9d\u6211\u4e4b\u89c1\uff0c\u4f60 \u8fd8\u662f\u73ed\u5e08\u56de\u56fd\u7684\u597d\uff0c\u6211\u82e5\u5f97\u7ee7\u5927\u7edf\uff0c\u90a3\u65f6\u5b9a\u80fd\u4ee4\u4f60\u6837\u6837\u5982\u610f\u3002\u201d\u8fd9\u51e0\u53e5\u8bdd\u8bf4\u5f97\u592a\u660e\u663e\u4e86\uff0c\u9547 \u56fd\u738b\u5b50\u518d\u7b28\uff0c\u4e5f\u542c\u5f97\u61c2\u4ed6\u7684\u610f\u601d\u3002\u5bdf\u5408\u53f0\u662f\u63d0\u51fa\u4ea4\u6362\u6761\u4ef6\uff0c\u53ea\u8981\u4ed6\u5e2e\u5fd9\u5bdf\u5408\u53f0\u53d6\u5f97\u6c57\u4f4d\uff0c\u5bdf \u5408\u53f0\u5c31\u53ef\u4ee5\u7b54\u5e94\u4ed6\u7684\u4efb\u4f55\u8981\u6c42\uff0c\u548c\u660e\u6167\u516c\u4e3b\u7684\u5a5a\u4e8b\uff0c\u90a3\u5f53\u7136\u4e5f\u662f\u4e0d\u6210\u95ee\u9898\u7684\u4e86\u3002 \u3000\u3000\u9547\u56fd\u738b\u5b50\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u8fd9\u8def\u519b\u4e8b\u662f\u7531\u6211\u6307\u6325\u7684\uff0c\u4e0d\u7ba1\u4f60\u4eec\u6709\u65e0\u5f02\u8bae\uff0c\u6211\u662f\u51b3\u610f\u73ed\u5e08\u7684 \u4e86\u3002\u201d \u3000\u3000\u5bdf\u4f1a\u53f0\u9053\uff1a\u201c\u662f\u5440\uff0c\u5927\u6c57\u4e00\u6b7b\uff0c\u5c06\u571f\u81ea\u662f\u65e0\u5fc3\u6253\u4ed7\uff0c\u54b1\u4eec\u4e5f\u5e94\u8be5\u8ba9\u4ed6\u4eec\u56de\u53bb\u7ed9\u5927\u6c57\u9001 \u4e27\uff0c\u8ba9\u4ed6\u4eec\u8868\u793a\u5bf9\u5927\u6c57\u7684\u54c0\u60bc\u624d\u5bf9\u4e86\u3002\u201d \u3000\u3000\u8fd9\u4e2a\u5927\u5e3d\u5b50\u4e00\u538b\u4e0b\u6765\uff0c\u738b\u516c\u3001\u5927\u81e3\u751a\u81f3\u8fde\u62d6\u96f7\u5728\u5185\uff0c\u7eb5\u7136\u6709\u4eba\u4e0d\u5927\u540c\u610f\uff0c\u4e5f\u662f\u4e0d\u6562\u53cd\u5bf9 \u4e86\u3002\u4e8e\u662f\u9547\u56fd\u738b\u5b50\u5e26\u4e86\u4ed6\u7684\u968f\u4ece\u6b66\u58eb\uff0c\u7acb\u5373\u8d70\u51fa\u91d1\u5e10\uff0c\u51c6\u5907\u8d76\u56de\u516d\u76d8\u5c71\u524d\u7ebf\uff0c\u4e0b\u4ee4\u73ed\u5e08\u3002 \u3000\u3000\u6b64\u65f6\u5df2\u662f\u7b2c\u4e8c\u5929\u7684\u65e9\u6668\u4e86\u3002\u4e14\u8bf4\u6768\u5a49\u4e00\u89c9\u9192\u6765\uff0c\u672a\u89c1\u660e\u6167\u516c\u4e3b\u56de\u6765\uff0c\u5fc3\u4e2d\u6709\u70b9\u4e0d\u5b89\uff0c\u9042 \u8d70\u51fa\u5e10\u5e55\uff0c\u5728\u9644\u8fd1\u7684\u5c71\u8fb9\u6563\u6b65\uff0c\u6697\u4e2d\u5bdf\u770b\u91d1\u5e10\u7684\u52a8\u9759\u3002 \u3000\u3000\u4e0d\u6599\u5979\u672a\u76fc\u5230\u660e\u6167\u516c\u4e3b\uff0c\u5374\u5148\u78b0\u89c1\u4e86\u4ece\u91d1\u5e10\u5306\u5306\u8d76\u51fa\u6765\u7684\u9547\u56fd\u738b\u5b50\u3002 \u3000\u3000\u9547\u56fd\u738b\u5b50\u5927\u4e8b\u5728\u8eab\uff0c\u672c\u6765\u662f\u6ca1\u6709\u6ce8\u610f\u5979\u7684\uff0c\u4f46\u4ed6\u7684\u4e24\u4e2a\u968f\u4ece\u6b66\u571f\uff0c\u5374\u6ce8\u610f\u5230\u4e86\u6768\u5a49\uff0c\u8fd9 \u4e24\u4e2a\u6b66\u571f\u6b63\u662f\u5728\u6768\u5a49\u884c\u523a\u4f59\u4e00\u4e2d\u90a3\u665a\uff0c\u66fe\u7ecf\u548c\u5979\u4ea4\u8fc7\u624b\u7684\u3002 \u3000\u3000\u6768\u5a49\u867d\u7136\u53d8\u4e86\u88c5\u675f\uff0c\u8eab\u6750\u53ef\u662f\u4e0d\u80fd\u6539\u53d8\u7684\u3002 \u3000\u3000\u4ece\u5979\u7684\u9762\u8c8c\u8f6e\u5ed3\u4e5f\u4f9d\u7a00\u53ef\u4ee5\u770b\u5f97\u51fa\u5f53\u665a\u90a3\u4e2a\u523a\u5ba2\u7684\u5f71\u5b50\u3002\u90a3\u4e24\u4e2a\u6b66\u571f\u7591\u5fc3\u5927\u8d77\uff0c\u767b\u65f6\u5c31 \u4e0a\u524d\u559d\u95ee\uff1a\u201c\u4f60\u662f\u4ec0\u4e48\u4eba\u4e86\uff1f\u201d \u3000\u3000\u6768\u5a49\u5f88\u9547\u5b9a\u5730\u7b54\u9053\uff1a\u201c\u6211\u662f\u660e\u6167\u516c\u4e3b\u7684\u4f8d\u5973\uff01\u201d \u3000\u3000\u9547\u56fd\u738b\u5b50\u672c\u6765\u4e0d\u6ce8\u610f\u5979\u7684\uff0c\u4e00\u542c\u8bf4\u662f\u660e\u6167\u516c\u4e3b\u7684\u4f8d\u5973\uff0c\u4e0d\u89c9\u4e5f\u6ce8\u610f\u8d77\u6765\u4e86\u3002 \u3000\u3000\u9547\u56fd\u738b\u5b50\u4e00\u770b\u662f\u4e2a\u4f8d\u5973\uff0c\u4e0d\u89c9\u4e5f\u8d77\u4e86\u7591\u5fc3\uff0c\u8bf4\u9053\uff1a\u201c\u660e\u6167\u516c\u4e3b\u7684\u4f8d\u5973\uff0c\u5c82\u80fd\u7528\u4e2a\u6c49\u4eba\uff1f \u6211\u770b\u4f60\u662f\u5192\u5145\u7684\u5427\uff1f\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u660e\u6167\u516c\u4e3b\u5c31\u5728\u5e10\u4e2d\uff0c\u4e0d\u4fe1\u4f60\u53ef\u4ee5\u95ee\u5979\uff01\u201d \u3000\u3000\u9547\u56fd\u738b\u5b50\u772f\u7740\u773c\u775b\u7b11\u9053\uff1a\u201c\u8fd9\u96cc\u513f\u5012\u662f\u957f\u5f97\u4e0d\u9519\u3002\u201d\u90a3\u4e24\u4e2a\u6b66\u58eb\u9053\uff1a\u201c\u7980\u5143\u5e05\uff0c\u8fd9\u96cc\u513f \u597d\u50cf\u662f\u90a3\u665a\u7684\u523a\u5ba2\u5462\uff01\u201d \u3000\u3000\u6768\u5a49\u5fc3\u91cc\u53d1\u614c\uff0c\u4f46\u795e\u8272\u4ecd\u7136\u4e0d\u9732\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u59d4\u5b9e\u662f\u660e\u6167\u516c\u4e3b\u7684\u4f8d\u5973\uff0c\u8bf7\u5143\u5e05\u4e00\u95ee\u516c\u4e3b \u4fbf\u77e5\uff01\u201d \u3000\u3000\u9547\u56fd\u738b\u5b50\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u62ff\u660e\u6167\u6765\u5413\u6211\u4e48\uff1f\u55ef\uff0c\u5373\u4f7f\u4f60\u5f53\u771f\u662f\u5979\u7684\u4f8d\u5973\uff0c\u53c8\u600e\u4e48\u6837\uff1f\u6211\u5c31 \u4e0d\u80fd\u6cbb\u4f60\u7684\u7f6a\u4e48\uff1f\u660e\u6167\u5979\u6536\u5bb9\u6c49\u5973\uff0c\u5148\u81ea\u4e0d\u8be5\uff01\u201d\u8bf4\u81f3\u6b64\u5904\uff0c\u84e6\u5730\u8138\u5b54\u4e00\u677f\uff0c\u559d\u9053\uff1a\u201c\u4e0d\u5fc5 \u987e\u5fcc\uff0c\u5c06\u5979\u62ff\u4e0b\uff01\u201d \u3000\u3000\u539f\u6765\u9547\u56fd\u738b\u5b50\u56e0\u4e3a\u53d7\u4e86\u660e\u6167\u516c\u4e3b\u7684\u51b7\u6de1\uff0c\u6b63\u81ea\u5fc3\u4e2d\u6709\u6c14\uff1b\u4e8c\u6765\u4ed6\u53c8\u5782\u6d8e\u6768\u5a49\u7684\u59ff\u8272\u3002\u6545 \u6b64\u6b63\u662f\u5df4\u4e0d\u5f97\u6709\u4e2a\u85c9\u53e3\uff0c\u597d\u628a\u5979\u63b3\u8d70\u3002 \u3000\u3000\u90a3\u4e24\u4e2a\u6b66\u58eb\u4e00\u58f0\u201c\u5f97\u4ee4\uff01\u201d\u53cc\u53cc\u8dc3\u4e0a\uff0c\u9ed1\u8863\u6b66\u571f\u5148\u5230\uff0c\u4e00\u6293\u5c31\u5411\u6768\u5a49\u7684\u7435\u7436\u9aa8\u6293\u4e0b\u6765\u3002 \u6768\u5a49\u77e5\u9053\u4ed6\u7684\u6454\u89d2\u529f\u592b\u4e86\u5f97\uff0c\u7109\u80fd\u5bb9\u4ed6\u6293\u7740\uff1f\u5f53\u4e0b\u4e00\u4e2a\u76d8\u9f99\u7ed5\u6b65\uff0c\u6325\u8896\u5411\u90a3\u6b66\u571f\u7684\u9762\u95e8\u62c2 \u53bb\u3002\u53ea\u542c\u5f97\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u6768\u5a49\u7684\u8863\u8896\u7ed9\u6495\u53bb\u4e86\u4e00\u622a\uff0c\u90a3\u6b66\u58eb\u7684\u773c\u89d2\u4e5f\u7ed9\u8863\u8896\u62c2\u4e2d\uff0c\u773c\u775b\u706b \u8fa3\u8fa3\u5730\u4f5c\u75db\uff0c\u4e0d\u89c9\u6d41\u51fa\u6cea\u6765\u3002\u8fd9\u4e00\u62db\u72e0\u8fa3\u7684\u64d2\u62ff\u624b\u4e5f\u5c31\u7ed9\u6768\u5a49\u89e3\u4e86\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u9ec4\u886b\u6b66\u58eb\u8ddf\u7740\u4ea6\u5df2\u6251\u5230\uff0c\u6768\u5a49\u62d4\u5251\u51fa\u9798\uff0c\u65a5\u9053\uff1a\u201c\u4f60\u597d\u5927\u80c6\uff0c\u6562\u6765\u6b3a \u6211\uff01\u770b\u5251\uff01\u201d\u90a3\u6b66\u571f\u9053\uff1a\u201c\u5143\u5e05\u6709\u4ee4\uff0c\u7ba1\u4f60\u662f\u4e0d\u662f\u516c\u4e3b\u7684\u4f8d\u5973\uff01\u201d\u5de6\u624b\u4e3e\u8d77\u76fe\u724c\uff0c\u201c\u94db\u201d\u4e00 \u58f0\uff0c\u6321\u5f00\u4e86\u6768\u5a49\u7684\u5251\uff0c\u53f3\u624b\u7684\u5b9d\u5200\u7acb\u5373\u8fdb\u62db\uff0c\u65ab\u6768\u5a49\u7684\u8db3\u8e1d\u3002\u9547\u56fd\u738b\u5b50\u559d\u9053\uff1a\u201c\u4e0d\u8981\u4f24\u5979\uff0c \u6211\u8981\u6d3b\u7684\uff01\u201d\u6b66\u58eb\u5e94\u9053\uff1a\u201c\u662f\uff01\u201d\u5200\u950b\u4e0a\u64a9\uff0c\u60f3\u8981\u903c\u4f7f\u6768\u5a49\u5f03\u5251\u3002 \u3000\u3000\u54ea\u77e5\u6768\u5a49\u5251\u6cd5\u5947\u8be1\u83ab\u6d4b\uff0c\u8fd9\u6b66\u58eb\u5373\u4f7f\u5168\u529b\u5bf9\u4ed8\uff0c\u53ea\u6015\u4e5f\u662f\u4ec5\u80fd\u5468\u65cb\uff0c\u4f55\u51b5\u662f\u6709\u6240\u987e\u5fcc\uff0c \u4e34\u65f6\u53d8\u62db\u3002\u53ea\u89c1\u5251\u5149\u95ea\u5904\uff0c\u90a3\u6b66\u58eb\u201c\u54ce\u54df\u201d\u4e00\u58f0\uff0c\u5012\u8dc3\u4e09\u6b65\u3002\u539f\u6765\u662f\u5de6\u81c2\u5df2\u7740\u4e86\u4e00\u5251\u3002\u5e78\u4e8f \u4ed6\u6709\u76d4\u7532\u62a4\u8eab\uff0c\u4e0d\u81f4\u53d7\u4f24\u3002\u4f46\u5916\u8863\u5212\u7834\uff0c\u62a4\u8eab\u7684\u94dc\u955c\u53c8\u788e\u4e86\u4e00\u5757\uff0c\u4ea6\u662f\u5403\u60ca\u4e0d\u5c0f\uff01 \u3000\u3000\u9ed1\u8863\u6b66\u571f\u4e0e\u6768\u5a49\u4ea4\u4e86\u4e00\u62db\uff0c\u5df2\u7ecf\u8ba4\u51fa\u5979\u7684\u5bb6\u6570\uff0c\u53eb\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u96cc\u513f\u6b63\u662f\u90a3\u665a\u7684\u523a \u5ba2\uff01\u201d\u5f53\u4e0b\u63c9\u4e86\u63c9\u773c\u775b\uff0c\u968f\u5373\u62d4\u51fa\u6708\u7259\u5f2f\u5200\uff0c\u4e0a\u524d\u6765\u52a9\u540c\u4f34\u3002 \u3000\u3000\u9547\u56fd\u738b\u5b50\u6b64\u65f6\u5df2\u77e5\u6768\u5a49\u4e86\u5f97\uff0c\u4e8e\u662f\u53c8\u518d\u53d8\u66f4\u547d\u4ee4\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u51c6\u4f60\u4eec\u4f24\u5979\uff0c\u53ea\u8981\u4e0d\u628a\u5979 \u5f04\u6210\u6b8b\u5e9f\uff01\u201d \u3000\u3000\u8fd9\u4e24\u4e2a\u6b66\u58eb\u4e43\u662f\u8499\u53e4\u519b\u4e2d\u7684\u4e00\u6d41\u597d\u624b\uff0c\u672c\u9886\u751a\u662f\u4e0d\u5f31\u3002\u6768\u5a49\u82e5\u662f\u5355\u6253\u72ec\u6597\uff0c\u53ef\u4ee5\u80dc\u5f97\u4ed6 \u4eec\uff0c\u5982\u4eca\u4ee5\u4e00\u654c\u4e8c\uff0c\u5374\u662f\u4e0d\u514d\u7a0d\u5904\u4e0b\u98ce\u4e86\u3002\u5e78\u4e8f\u8fd9\u4e24\u4e2a\u6b66\u58eb\u56e0\u4e3a\u5949\u547d\u4e0d\u53ef\u628a\u5979\u5f04\u6210\u6b8b\u5e9f\uff0c\u56e0 \u6b64\u867d\u7136\u53ef\u4ee5\u4f24\u5979\uff0c\u4e5f\u8fd8\u591a\u5c11\u6709\u70b9\u9887\u5fcc\u3002 \u3000\u3000\u6768\u5a49\u60c5\u77e5\u4e45\u6218\u4e0b\u53bb\uff0c\u5fc5\u5b9a\u5403\u4e8f\uff0c\u60c5\u6025\u4e4b\u4e0b\uff0c\u4e5f\u5c31\u987e\u4e0d\u5f97\u60ca\u52a8\u91d1\u5e10\u7684\u516c\u4e3b\u5927\u81e3\u4e86\uff0c\u5927\u58f0\u53eb \u9053\uff1a\u201c\u516c\u4e3b\u3001\u516c\u4e3b\u8bf7\u4f60\u51fa\u6765\uff01\u6709\u4eba\u6b3a\u8d1f\u6211\u5462\uff01\u201d \u3000\u3000\u6768\u5a49\u548c\u90a3\u6b66\u58eb\u5728\u5c71\u8fb9\u6076\u6597\uff0c\u8ddd\u79bb\u6210\u5409\u601d\u6c57\u7684\u91d1\u5e10\u7ea6\u6709\u4e09\u91cc\u4e4b\u9065\uff0c\u4f46\u56e0\u5979\u662f\u7528\u201c\u4f20\u97f3\u5165 \u5bc6\u201d\u7684\u5185\u529f\u5c06\u58f0\u97f3\u8fdc\u8fdc\u7684\u9001\u51fa\u53bb\uff0c\u660e\u6167\u516c\u4e3b\u5750\u5728\u91d1\u5e10\u4e4b\u4e2d\uff0c\u4ecd\u662f\u9690\u9690\u53ef\u95fb\u3002 \u3000\u3000\u6b64\u65f6\u91d1\u5e10\u8bf8\u4eba\u8981\u5546\u91cf\u7684\u4e8b\u60c5\u5927\u81f4\u4e5f\u5df2\u5f97\u5230\u534f\u8bae\u4e86\uff0c\u660e\u6167\u516c\u4e3b\u9690\u9690\u542c\u5f97\u6768\u5a49\u7684\u53eb\u58f0\uff0c\u5403\u4e86 \u4e00\u60ca\uff0c\u5bf9\u62d6\u96f7\u9053\uff1a\u201c\u56db\u54e5\uff0c\u597d\u50cf\u662f\u6211\u90a3\u4f8d\u5973\u53eb\u6211\uff0c\u6211\u548c\u4f60\u51fa\u53bb\u770b\u770b\u3002\u201d\u62d6\u96f7\u9053\uff1a\u201c\u597d\uff0c\u4f60\u5148 \u51fa\u53bb\uff0c\u6211\u968f\u540e\u5c31\u6765\u3002\u201d\u62d6\u96f7\u56e0\u4e3a\u7a9d\u9614\u53f0\u521a\u8981\u548c\u4ed6\u8bf4\u8bdd\uff0c\u662f\u4ee5\u9700\u8981\u7a0d\u8fdf\u7247\u523b\uff0c\u7b49\u7a9d\u9614\u53f0\u8bf4\u5b8c\uff0c \u4ed6\u624d\u597d\u8d70\u3002 \u3000\u3000\u660e\u6167\u516c\u4e3b\u5306\u5306\u8d76\u5230\uff0c\u5927\u6012\u65a5\u9053\uff1a\u201c\u4f60\u4eec\u51ed\u4ec0\u4e48\u6b3a\u4fae\u6211\u7684\u4f8d\u5973\uff01\u201d \u3000\u3000\u9547\u56fd\u738b\u5b50\u51b7\u7b11\u9053\uff1a\u201c\u5979\u6709\u523a\u5ba2\u5acc\u7591\uff0c\u6211\u662f\u4e00\u519b\u4e3b\u5e05\uff0c\u5c82\u80fd\u5f87\u79c1\u8f7b\u653e\uff1f\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u6768\u5a49\u5df2\u7ecf\u6539\u4e86\u88c5\uff0c\u600e\u7684\u8fd8\u662f\u7ed9\u4ed6\u4eec\u770b\u4e86\u51fa\u6765\uff1f\u201d\u4f46\u660e\u6167\u516c \u4e3b\u4e00\u6765\u4f8d\u7740\u6ca1\u6709\u771f\u51ed\u5b9e\u636e\u7ed9\u4ed6\u4eec\u62ff\u5230\uff0c\u4e8c\u6765\u5979\u53c8\u6709\u62d6\u96f7\u4f5c\u5979\u540e\u76fe\uff0c\u56e0\u6b64\u5fc3\u91cc\u867d\u7136\u5403\u60ca\uff0c\u53e3\u6c14 \u4f9d\u7136\u5f3a\u786c\uff0c\u559d\u9053\uff1a\u201c\u80e1\u8bf4\u516b\u9053\uff0c\u90a3\u665a\u5979\u4e00\u76f4\u5728\u6211\u8eab\u8fb9\uff0c\u7109\u80fd\u53bb\u4f5c\u523a\u5ba2\uff01\u201d \u3000\u3000\u9547\u56fd\u738b\u5b50\u51b7\u7b11\u9053\uff1a\u201c\u662f\u548c\u4e0d\u662f\uff0c\u987b\u5f97\u6211\u4eb2\u81ea\u5ba1\u8baf\u65b9\u77e5\u3002\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u53d8\u4e86\u9762\u8272\uff0c\u65a5\u9053\uff1a\u201c\u5c82\u6709\u6b64\u7406\uff0c\u6211\u7239\u7239\u521a\u6b7b\uff0c\u4f60\u4eec\u5c31\u8981\u6b3a\u8d1f\u6211\u4e86\uff1f\u6211\u7684\u547d\u4ee4\u4f60 \u4eec\u80c6\u6562\u4e0d\u4f9d\uff0c\u4f60\u4eec\u773c\u4e2d\u8fd8\u6709\u6211\u6ca1\u6709\uff1f\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u7528\u7684\u662f\u201c\u4f60\u4eec\u201d\u4e8c\u5b57\uff0c\u90a3\u4e24\u4e2a\u6b66\u571f\u7109\u5f97\u4e0d\u60ca\uff0c\u5fc3\u91cc\u4ff1\u662f\u60f3\u9053\uff1a\u201c\u5143\u5e05\u4e0e\u516c\u4e3b\u4f5c \u5bf9\uff0c\u6211\u4eec\u5939\u5728\u5f53\u4e2d\uff0c\u8fd9\u53ef\u72af\u4e0d\u7740\uff01\u201d\u4e8e\u662f\u4e0d\u7ea6\u800c\u540c\u7684\uff0c\u9000\u8fc7\u4e00\u8fb9\uff0c\u628a\u773c\u671b\u7740\u9547\u56fd\u738b\u5b50\u3002 \u3000\u3000\u9547\u56fd\u738b\u5b50\u66b4\u8df3\u5982\u96f7\uff0c\u5927\u6012\u559d\u9053\uff1a\u201c\u597d\uff0c\u4f60\u4eec\u4e0d\u6562\u6293\u5979\uff0c\u5f85\u6211\u6765\u6293\uff01\u201d\u4ed6\u4e00\u6012\u4e4b\u4e0b\uff0c\u706b\u6c14 \u653b\u5fc3\uff0c\u4e5f\u4e0d\u60f3\u60f3\u6768\u5a49\u7684\u672c\u9886\u6bd4\u4ed6\u9ad8\u660e\u5f97\u591a\uff0c\u7adf\u7136\u4e0d\u52a0\u601d\u7d22\u5730\u5c31\u8dd1\u4e0a\u524d\u53bb\u6293\u6768\u5a49\u3002 \u3000\u3000\u6768\u5a49\u63d2\u5251\u5f52\u9798\uff0c\u4e00\u95ea\u95ea\u5f00\u3002\u9547\u56fd\u738b\u5b50\u4e0d\u77e5\u5bf9\u65b9\u4e43\u662f\u8ba9\u4ed6\uff0c\u53c8\u518d\u6251\u4e0a\u524d\u53bb\uff0c\u53cc\u624b\u5408\u62b1\uff0c\u559d \u9053\uff1a\u201c\u770b\u4f60\u5f80\u54ea\u91cc\u9003\uff01\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u51b7\u7b11\u9053\uff1a\u201c\u4ed6\u8981\u6b3a\u8d1f\u4f60\uff0c\u4f60\u548c\u4ed6\u6253\u597d\u4e86\u3002\u6709\u6211\u5728\u6b64\uff0c\u4e0d\u5fc5\u6015\u4ed6\uff01\u201d \u3000\u3000\u6768\u5a49\u6b63\u662f\u8981\u7b49\u660e\u6167\u516c\u4e3b\u8fd9\u53e5\u8bf4\u8bdd\uff0c\u5f53\u4e0b\u4e5f\u5c31\u4e0d\u518d\u5ba2\u6c14\uff0c\u4e00\u4e2a\u8f6c\u8eab\uff0c\u4e00\u638c\u6325\u51fa\uff0c\u6e05\u6e05\u8106\u8106 \u5730\u6253\u4e86\u9547\u56fd\u738b\u5b50\u4e00\u8bb0\u8033\u5149\u3002\u867d\u7136\u8fd8\u4e0d\u7b97\u662f\u65bd\u5c55\u6740\u624b\uff0c\u8fd9\u4e00\u8bb0\u8033\u5149\u4ea6\u5df1\u7740\u5b9e\u6253\u5f97\u4e0d\u8f7b\uff01 \u3000\u3000\u9547\u56fd\u738b\u5b50\u534a\u8fb9\u9762\u5b54\u706b\u8fa3\u8fa3\u4f5c\u75db\uff0c\u4ed6\u81ea\u6709\u751f\u4ee5\u6765\uff0c\u53ea\u6709\u4eba\u5bb6\u5949\u627f\u4ed6\u7684\uff0c\u51e0\u66fe\u53d7\u8fc7\u5982\u6b64\u4fae \u8fb1\uff1f\u66b4\u6012\u4e4b\u4e0b\uff0c\u54ea\u91cc\u8fd8\u987e\u5f97\u60dc\u7389\u601c\u9999\uff0c\u62d4\u51fa\u4f69\u5200\uff0c\u5411\u6768\u5a49\u5c31\u65ab\u3002 \u3000\u3000\u6768\u5a49\u672c\u6765\u53ef\u4ee5\u62d4\u5251\u628a\u4ed6\u523a\u4f24\u7684\uff0c\u5374\u6545\u610f\u88c5\u4f5c\u7ed9\u4ed6\u6b3a\u4fae\u7684\u6837\u5b50\uff0c\u62d4\u8db3\u4fbf\u9003\u3002\u539f\u6765\u6b64\u65f6\u62d6\u96f7 \u5df2\u7ecf\u9a91\u9a6c\u8d76\u6765\uff0c\u9547\u56fd\u738b\u5b50\u80cc\u5411\u91d1\u5e10\uff0c\u5c1a\u672a\u77e5\u9053\u3002 \u3000\u3000\u9547\u56fd\u738b\u5b50\u6b63\u5728\u6076\u72e0\u72e0\u5730\u821e\u5200\u8ffd\u6740\u6768\u5a49\uff0c\u62d6\u96f7\u4e00\u89c1\u5927\u6012\uff0c\u62cd\u9a6c\u8d76\u4e0a\uff0c\u9547\u56fd\u738b\u5b50\u559d\u9053\uff1a\u201c\u662f \u8c01\uff1f\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u62d6\u96f7\u5530\u7684\u4e00\u97ad\u6253\u4e0b\uff0c\u5df1\u662f\u628a\u9547\u56fd\u738b\u5b50\u7684\u5b9d\u5200\u6253\u843d\u3002 \u3000\u3000\u9547\u56fd\u738b\u5b50\u56de\u5934\u4e00\u770b\uff0c\u8fd9\u624d\u77e5\u9053\u662f\u62d6\u96f7\u6253\u843d\u4ed6\u7684\u5b9d\u5200\u3002\u9547\u56fd\u738b\u5b50\u53c8\u60ca\u53c8\u6012\uff0c\u53ef\u53c8\u4e0d\u6562\u53d1 \u4f5c\u3002\u62d6\u96f7\u54fc\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u52ff\u91cc\uff0c\u4f60\u8eab\u4e3a\u5143\u5e05\uff0c\u6b3a\u4fae\u4e00\u4e2a\u5f31\u8d28\u5973\u6d41\uff0c\u7f9e\u4e5f\u4e0d\u7f9e\uff1f\u201d \u3000\u3000\u9547\u56fd\u738b\u5b50\u7ed9\u6768\u5a49\u6253\u4e86\u4e00\u5df4\u638c\uff0c\u8138\u4e0a\u72b9\u81ea\u706b\u8fa3\u8fa3\u4f5c\u75db\uff0c\u4f46\u62d6\u96f7\u5e76\u4e0d\u77e5\u9053\u3002\u9547\u56fd\u738b\u5b50\u788d\u7740\u9762 \u5b50\uff0c\u6b63\u662f\u6709\u82e6\u8bf4\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u660e\u6167\u516c\u4e3b\u201c\u6076\u4eba\u5148\u544a\u72b6\u201d\uff0c\u53eb\u9053\uff1a\u201c\u56db\u54e5\uff0c\u4f60\u6765\u5f97\u6b63\u597d\uff0c\u4f60\u7ed9\u6211\u8bc4\u8bc4\u8fd9\u4e2a\u9053\u7406\u3002\u4ed6\u8bec\u8d56 \u6211\u7684\u4f8d\u5973\u662f\u523a\u5ba2\uff0c\u8fd9\u5c82\u4e0d\u662f\u8352\u5929\u4e0b\u4e4b\u5927\u5510\uff01\u6211\u7684\u4f8d\u5973\u600e\u4f1a\u53bb\u884c\u523a\u4ed6\uff1f\u4f55\u51b5\u4e8b\u60c5\u53d1\u751f\u90a3\u665a\uff0c\u6211 \u8fd9\u4f8d\u5973\u6839\u672c\u5c31\u6ca1\u6709\u79bb\u5f00\u8fc7\u6211\u8eab\u8fb9\u3002\u201d \u3000\u3000\u9547\u56fd\u738b\u5b50\u5fcd\u4f4f\u6c14\u8fa9\u9053\uff1a\u201c\u8fd9\u662f\u4ed6\u4eec\u4eb2\u773c\u770b\u89c1\u7684\uff0c\u8fd8\u80fd\u6709\u5047\uff1f\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u5524\u90a3\u4e24\u4e2a\u6b66\u58eb\u8fc7\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u665a\u4f60\u4eec\u770b\u89c1\u7684\u523a\u5ba2\uff0c\u662f\u7537\u662f\u5973\uff1f\u201d \u3000\u3000\u90a3\u4e24\u4e2a\u6b66\u58eb\u9053\uff1a\u201c\u662f\u4e2a\u5c0f\u5b50\u3002\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u9053\uff1a\u201c\u90a3\u5c0f\u5b50\u662f\u4e11\u662f\u4fca\uff1f\u201d \u3000\u3000\u90a3\u665a\u6768\u5a49\u8fd8\u662f\u672a\u66fe\u62b9\u6389\u5316\u88c5\u7684\uff0c\u5979\u7684\u8138\u4e0a\u6d82\u6709\u4e00\u79cd\u53ef\u4ee5\u6539\u6362\u80a4\u8272\u7684\u8349\u6c41\uff0c\u867d\u7136\u4e0d\u662f\u4e11\u964b \u4e0d\u582a\uff0c\u4e5f\u662f\u751a\u4e3a\u96be\u770b\u7684\u4e86\u3002\u90a3\u4e24\u4e2a\u6b66\u58eb\u53ea\u597d\u636e\u5b9e\u7b54\u9053\uff1a\u201c\u662f\u4e2a\u4e11\u5c0f\u5b50\u3002\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u51b7\u7b11\u9053\uff1a\u201c\u7740\u5440\uff01\u6211\u8fd9\u4f8d\u5973\u53ef\u662f\u4e2a\u7f8e\u4eba\u513f\u5462\uff01\u5979\u53c8\u4e0d\u662f\u5996\u602a\uff0c\u5c82\u80fd\u53d8\u6210\u4e2a\u4e11\u5c0f \u5b50\uff1f\u201d \u3000\u3000\u90a3\u4e24\u4e2a\u6b66\u58eb\u8baa\u8baa\u8bf4\u9053\uff1a\u201c\u8eab\u6750\u6709\u70b9\u76f8\u4f3c\uff0c\u672c\u9886\u597d\u50cf\u4e5f\u5dee\u4e0d\u591a\u3002\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u51b7\u7b11\u9053\uff1a\u201c\u8eab\u6750\u76f8\u4f3c\u7684\u4eba\u591a\u5f97\u5f88\uff01\u672c\u9886\u9ad8\u5f3a\u7684\u5973\u5b50\u66f4\u4e0d\u5e0c\u7f55\uff0c\u6211\u5e10\u4e0b\u7684\u5973\u5175\u54ea \u4e00\u4e2a\u4e0d\u4f1a\u6b66\u827a\uff1f\u201d \u3000\u3000\u62d6\u96f7\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4e2a\u518d\u4ed4\u7ec6\u77a7\u77a7\uff0c\u770b\u8fd8\u6709\u4ec0\u4e48\u53ef\u7591\u4e4b\u5904\u6ca1\u6709\uff1f\u201d\u8a00\u4e0b\u4e4b\u610f\uff0c\u5373\u662f\u8bf4\u8eab\u6750 \u76f8\u4f3c\u4e0d\u8db3\u4e3a\u51ed\u7684\u4e86\u3002 \u3000\u3000\u8fd9\u4e24\u4e2a\u6b66\u58eb\u5df2\u7ecf\u77e5\u9053\u62d6\u96f7\u7ad9\u5728\u516c\u4e3b\u4e00\u8fb9\uff0c\u8fde\u5fd9\u89c1\u98ce\u9a76\u8235\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u5929\u665a\u4e0a\u4e0b\u96e8\uff0c\u65e0\u6708 \u65e0\u661f\uff0c\u6211\u4eec\u672c\u6765\u770b\u5f97\u4e0d\u5927\u6e05\u695a\u3002\u53ea\u6015\u8ba4\u9519\u4e86\u4eba\uff0c\u4e5f\u662f\u6709\u7684\uff0c\u8bf7\u516c\u4e3b\u539f\u8c05\u3002\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u51b7\u7b11\u9053\uff1a\u201c\u52ff\u91cc\uff0c\u4f60\u8fd8\u6709\u4ec0\u4e48\u53ef\u8bf4\u7684\uff1f\u201d \u3000\u3000\u9547\u56fd\u738b\u5b50\u9053\uff1a\u201c\u662f\u5426\u523a\u5ba2\uff0c\u6682\u4e14\u4e0d\u8bba\uff0c\u4f46\u4f60\u6536\u5bb9\u6c49\u5973\uff0c\u603b\u662f\u4e0d\u8be5\u3002\u201d\u90a3\u4e24\u4e2a\u6b66\u58eb\u4e0d\u6562\u575a \u6301\uff0c\u9547\u56fd\u7389\u5b50\u7684\u53e3\u98ce\u4e5f\u5c31\u4e0d\u7531\u5f97\u8f6f\u4e86\u51e0\u5206\u3002 \u3000\u3000\u62d6\u96f7\u9053\uff1a\u201c\u8fd9\u4e2a\u4f60\u5012\u662f\u9519\u602a\u660e\u6167\u4e86\uff0c\u4f60\u77e5\u9053\u7684\u53ea\u662f\u4ee5\u524d\u7684\u89c4\u77e9\uff0c\u6c49\u4eba\u4e0d\u53ef\u4ee5\u4f5c\u738b\u5b50\u548c\u516c \u4e3b\u7684\u968f\u4ece\u3002\u4f46\u81ea\u5927\u6c57\u51b3\u5b9a\u541e\u5e76\u4e2d\u534e\u4e4b\u540e\uff0c\u8fd9\u6761\u89c4\u77e9\u65e9\u5df2\u6539\u4e86\u3002\u6211\u4eec\u8981\u4f7f\u6c49\u4eba\u4e50\u610f\u4e3a\u6211\u4eec\u6240 \u7528\uff0c\u5c31\u4e0d\u80fd\u5bf9\u4ed6\u4eec\u6b67\u89c6\u3002\u773c\u524d\u5c31\u6709\u4e2a\u4f8b\u5b50\uff0c\u674e\u5e0c\u6d69\u4e0d\u662f\u5f53\u4f60\u7684\u526f\u5143\u5e05\u5417\uff1f\u526f\u5143\u5e05\u90fd\u53ef\u4ee5\u7528\u6c49 \u4eba\uff0c\u4f55\u51b5\u4f8d\u5973\uff1f\u201d \u3000\u3000\u9547\u56fd\u738b\u5b50\u65e0\u8a00\u53ef\u5bf9\uff0c\u60bb\u60bb\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u76d1\u56fd\uff0c\u4f60\u8fd9\u4e48\u8bf4\uff0c\u6211\u8fd8\u6709\u4ec0\u4e48\u597d\u8bf4\u7684\u5462\uff1f\u201d \u3000\u3000\u62d6\u96f7\u4e5f\u4e0d\u60f3\u4ee4\u4ed6\u592a\u8fc7\u96be\u582a\uff0c\u5f53\u4e0b\u6e29\u8a00\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u4e00\u519b\u4e3b\u5e05\uff0c\u5982\u4eca\u6b63\u6709\u5927\u4e8b\u5f85\u4f60\u53bb\u529e\uff0c \u8fd9\u70b9\u5c0f\u4e8b\uff0c\u4f60\u5c31\u4e0d\u5fc5\u64cd\u5fc3\u4e86\uff0c\u5982\u679c\u8fd9\u4e2a\u6c49\u5973\u5f53\u771f\u662f\u6709\u5acc\u7591\u7684\u8bdd\uff0c\u6211\u4e5f\u4f1a\u66ff\u4f60\u67e5\u4e2a\u6c34\u843d\u77f3\u51fa \u7684\u3002\u201d \u3000\u3000\u5927\u6c57\u6ca1\u6709\u9009\u51fa\u4e4b\u524d\uff0c\u201c\u76d1\u56fd\u201d\u5c31\u662f\u8499\u53e4\u7684\u6700\u9ad8\u9996\u9886\uff0c\u9547\u56fd\u738b\u5b50\u867d\u7136\u8dcb\u6248\uff0c\u4e5f\u662f\u4e0d\u6562\u548c\u4ed6 \u5bf9\u6297\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4e0d\u9519\u3002\u62d6\u96f7\u7684\u8bdd\uff0c\u5012\u662f\u63d0\u9192\u6211\u4e86\u3002\u5f85\u6211\u73ed\u5e08\u56de\u56fd\uff0c\u5e2e\u52a9\u5bdf\u5408\u53f0\u5750\u4e0a\u5927\u6c57 \u7684\u5b9d\u5ea7\uff0c\u90a3\u65f6\u4f55\u6c42\u4e0d\u5f97\uff1f\u201d\u8fd9\u4e48\u4e00\u60f3\uff0c\u4e5f\u5c31\u4e0d\u518d\u95f9\u4e86\u3002 \u3000\u3000\u9547\u56fd\u738b\u5b50\u548c\u4ed6\u7684\u4e24\u4e2a\u6b66\u571f\u8d70\u5f00\u4e4b\u540e\uff0c\u660e\u6167\u516c\u4e3b\u8bf4\u9053\uff1a\u201c\u5a49\u59b9\uff0c\u4f60\u53d7\u59d4\u5c48\u4e86\u3002\u4f60\u56de\u53bb\u6362\u8863 \u88f3\u5427\uff0c\u5f85\u4f1a\u513f\u6211\u518d\u6765\u770b\u4f60\u3002\u201d \u3000\u3000\u6768\u5a49\u7684\u8863\u88f3\u88ab\u90a3\u6b66\u58eb\u6495\u88c2\u4e86\u4e00\u5e45\uff0c\u6253\u6597\u4e2d\u53c8\u6cbe\u4e86\u4e0d\u5c11\u5c18\u58eb\uff0c\u7684\u786e\u9700\u8981\u6362\u8fc7\u4e00\u4ef6\u65b0\u8863\uff0c\u5f53 \u4e0b\u6768\u5a49\u591a\u8c22\u4e86\u660e\u610f\u516c\u4e3b\uff0c\u56de\u8f6c\u90a3\u5ea7\u7ed9\u5979\u4e13\u7528\u7684\u5e10\u5e55\u3002 \u3000\u3000\u62d6\u96f7\u76ee\u9001\u6768\u5a49\u7684\u80cc\u5f71\uff0c\u5f85\u5979\u8d70\u5f97\u8fdc\u4e86\uff0c\u65b9\u59cb\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u4e2a\u4f8d\u5973\uff0c\u786e\u662f\u5f88\u6709\u80c6\u91cf\u3002\u6628\u5929 \u5979\u8981\u62ff\u6211\uff0c\u4eca\u5929\u53c8\u6562\u5f97\u7f6a\u9547\u56fd\u738b\u5b50\u3002\u4f60\u662f\u54ea\u91cc\u627e\u6765\u7684\u8fd9\u4e2a\u6c49\u5973\uff1f\u73b0\u5728\u53ef\u4ee5\u544a\u8bc9\u6211\u4e86\u5427\uff1f\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u9053\uff1a\u201c\u4e0d\u662f\u6211\u627e\u6765\u7684\uff0c\u662f\u5979\u81ea\u5df1\u8dd1\u6765\u7684\u3002\u201d \u3000\u3000\u62d6\u96f7\u9053\uff1a\u201c\u8fd9\u6211\u5c31\u4e0d\u660e\u767d\u4e86\uff0c\u5979\u4e00\u4e2a\u5355\u8eab\u5973\u5b50\uff0c\u4f55\u4ee5\u4f1a\u8dd1\u5230\u54b1\u4eec\u519b\u4e2d\uff1f\u90a3\u662f\u4ec0\u4e48\u65f6\u5019\u7684 \u4e8b\u60c5\uff1f\u4f55\u4ee5\u4f60\u53c8\u4f1a\u6536\u7559\u5979\u5462\uff1f\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u5c31\u662f\u95f9\u523a\u5ba2\u7684\u90a3\u5929\u665a\u4e0a\u3002\u5979\u65e0\u8def\u53ef\u8d70\u3002\u6211\u53ea\u597d\u6536\u7559\u5979\u4e86\uff0c\u8fd9\u4f60\u8be5 \u660e\u767d\u4e86\u5427\uff1f\u201d \u3000\u3000\u62d6\u96f7\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u96be\u9053\u5979\u771f\u7684\u662f\u523a\u5ba2\u5417\uff1f\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u9053\uff1a\u201c\u4e0d\u9519\u3002\u4e0d\u8fc7\uff0c\u90a3\u5929\u665a\u4e0a\uff0c\u5979\u5012\u4e0d\u662f\u60f3\u53bb\u884c\u523a\u8fd9\u4e2a\u4e11\u516b\u602a\u7684\u3002\u5979\u8981\u6740\u7684\u4eba \u662f\u4f59\u4e00\u4e2d\u3002\u201d \u3000\u3000\u62d6\u96f7\u9053\uff1a\u201c\u8c01\u662f\u4f59\u4e00\u4e2d\uff1f\u201d \u3000\u3000\u660e\u7bd9\u516c\u4e3b\u9053\uff1a\u201c\u5c31\u662f\u5192\u540d\u674e\u5e0c\u6d69\u7684\u90a3\u4e2a\u5bb6\u4f19\uff0c\u6b64\u4e8b\u8bf4\u6765\u8bdd\u957f\u3002\u201d\u62d6\u96f7\u6025\u4e8e\u77e5\u9053\u6768\u5a49\u7684\u4e8b \u60c5\uff0c\u6253\u65ad\u660e\u6167\u516c\u4e3b\u7684\u8bdd\u95ee\u9053\uff1a\u201c\u4f59\u4e00\u4e2d\u7684\u4e8b\u6162\u6162\u518d\u8bf4\uff0c\u8fd9\u4e2a\u6c49\u5973\u7a76\u7adf\u662f\u4ec0\u4e48\u4eba\uff1f\u4f60\u80af\u6536\u7559 \u5979\uff0c\u4e00\u5b9a\u662f\u65e9\u5c31\u76f8\u8bc6\u7684\u4e86\u3002\u8fd9\u6bb5\u4ea4\u60c5\u53c8\u662f\u600e\u6837\u6500\u4e0a\u7684\uff1f\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u56db\u54e5\uff0c\u4f60\u770b\u4e0a\u5979\u4e86\uff0c\u662f\u4e48\uff1f\u4f46\u6211\u529d\u4f60\u4e0d\u5fc5\u767d\u8d39\u5fc3\u673a\uff0c\u56e0\u4e3a \u5979\u65e9\u5df2\u662f\u540d\u82b1\u6709\u4e3b\u4e86\uff01\u201d \u3000\u3000\u62d6\u96f7\u751a\u662f\u5c34\u5c2c\uff0c\u8bf4\u9053\uff1a\u201c\u522b\u5f00\u73a9\u7b11\uff0c\u6211\u53ea\u4e0d\u8fc7\u60f3\u77e5\u9053\u5979\u7684\u6765\u5386\u800c\u5df2\uff0c\u519b\u4e2d\u6df7\u8fdb\u4e00\u4e2a\u523a \u5ba2\uff0c\u8fd9\u53ef\u4e0d\u662f\u5f53\u800d\u7684\u554a\u3002\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u9053\uff1a\u201c\u597d\u5427\uff0c\u4f60\u65e2\u7136\u4e0d\u662f\u60f3\u6253\u5979\u7684\u4e3b\u610f\uff0c\u90a3\u6211\u5c31\u544a\u8bc9\u4f60\u5427\uff0c\u5979\u662f\u674e\u601d\u5357\u7684\u672a\u5a5a \u59bb\u5b50\uff0c\u540d\u53eb\u6768\u5a49\u3002\u201d \u3000\u3000\u62d6\u96f7\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u5979\u662f\u674e\u601d\u5357\u7684\u672a\u5a5a\u59bb\u5b50\uff1f\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u5fae\u7b11\u9053\uff1a\u201c\u4e0d\u9519\u3002\u8fd9\u4f60\u8be5\u660e\u767d\u4e86\u5427\uff1f\u674e\u601d\u5357\u4e0d\u4e5f\u662f\u4f60\u7684\u2018\u5b89\u7b54\u2019\u5417\uff1f\u201d \u3000\u3000\u62d6\u96f7\u5927\u4e3a\u60f6\u60d1\uff0c\u8bf4\u9053\uff1a\u201c\u674e\u601d\u5357\u7684\u672a\u5a5a\u59bb\u5b50\u4e3a\u4ec0\u4e48\u8981\u6765\u884c\u523a\u54b1\u4eec\u7684\u526f\u5143\u5e05\uff1f\u4f60\u3001\u4f60\u65e2\u7136 \u77e5\u9053\u4e86\u5979\u7684\u8eab\u4efd\uff0c\u53c8\u4e3a\u4ec0\u4e48\u8fd8\u662f\u5bf9\u5979\u8fd9\u6837\u597d\u5462\uff1f\u201d \u3000\u3000\u539f\u6765\u660e\u6167\u516c\u4e3b\u867d\u7136\u6ca1\u6709\u5c06\u5fc3\u4e8b\u660e\u767d\u7684\u544a\u8bc9\u62d6\u96f7\uff0c\u4f46\u90a3\u65e5\u5728\u80af\u7279\u5c71\u4e0a\u72e9\u730e\uff0c\u660e\u6167\u516c\u4e3b\u548c\u674e \u601d\u5357\u4eb2\u70ed\u7684\u60c5\u5f62\uff0c\u5374\u662f\u7792\u4e0d\u8fc7\u62d6\u96f7\u7684\u773c\u775b\u3002\u660e\u6167\u516c\u4e3b\u4e3a\u4e86\u8892\u62a4\u674e\u601d\u5357\uff0c\u4e0d\u60dc\u548c\u9547\u56fd\u738b\u5b50\u95f9 \u7ffb\uff0c\u8fd9\u4e5f\u662f\u4ed6\u4eb2\u773c\u89c1\u5230\u7684\u3002\u662f\u4ee5\u5f53\u5730\u73b0\u5728\u770b\u5230\u59b9\u59b9\u4e0e\u201c\u60c5\u654c\u201d\u4eb2\u5982\u59d0\u59b9\uff0c\u5c31\u4e0d\u7981\u9887\u611f\u8be7\u5f02 \u4e86\u3002 \u3000\u3000\u660e\u6167\u516c\u4e3b\u7b11\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u4f9d\u4f60\u7684\u60f3\u6cd5\uff0c\u6211\u5e94\u8be5\u5bf9\u5979\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u62d6\u96f7\u8bb7\u8bb7\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\u3002\u4e0d\u8fc7\uff0c\u4f60\u73b0\u5728\u5bf9\u5979\u8fd9\u6837\u597d\uff0c\u6211\u5374\u662f\u5f88\u4f69\u670d\u4f60\u7684\uff01\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u61c2\u5f97\u4f60\u7684\u610f\u601d\uff0c\u4e0d\u7792\u4f60\u8bf4\uff0c\u6211\u4e5f\u66fe\u5bf9\u8fd9\u4f4d\u6768\u59d1\u5a18\u6709\u8fc7\u5992 \u5fcc\u7684\u65f6\u5019\uff0c\u6211\u8fd8\u8d77\u8fc7\u6076\u6bd2\u7684\u5ff5\u5934\uff0c\u60f3\u8981\u62c6\u6563\u4ed6\u4eec\u8fd9\u5bf9\u9e33\u9e2f\u5462\u3002\u4f46\u540e\u6765\u6211\u60f3\u4e86\u53c8\u60f3\uff0c\u4ed6\u662f\u6c49 \u4eba\uff0c\u6211\u4eca\u751f\u662f\u51b3\u4e0d\u80fd\u548c\u4ed6\u6210\u4e3a\u592b\u5987\u7684\u4e86\uff0c\u4f55\u5fc5\u505a\u8fd9\u6837\u635f\u4eba\u800c\u4e0d\u5229\u5df1\u7684\u4e8b\u60c5\uff1f\u4f55\u51b5\u5728\u4ed6\u7684\u5fc3\u76ee \u4e4b\u4e2d\uff0c\u4e5f\u53ea\u6709\u4e00\u4e2a\u6768\u5a49\u3002\u4fd7\u8bed\u8bf4\u5f3a\u626d\u7684\u74dc\u4e0d\u751c\uff0c\u5373\u4f7f\u6211\u80fd\u591f\u51ed\u4ed7\u6211\u7684\u52bf\u529b\uff0c\u5c06\u4ed6\u4eec\u5206\u5f00\uff0c\u4ed6 \u7684\u5fc3\u4e5f\u51b3\u4e0d\u662f\u5c5e\u4e8e\u6211\u7684\uff0c\u6700\u540e\uff0c\u6211\u60f3\u901a\u4e86\uff0c\u6211\u5e94\u8be5\u4f7f\u6211\u559c\u6b22\u7684\u4eba\u5f97\u5230\u5e78\u798f\uff01\u8fd9\u5c31\u662f\u4e3a\u4ec0\u4e48\u6211 \u6536\u7559\u8fd9\u4f4d\u6768\u59d1\u5a18\u7684\u539f\u56e0\uff0c\u8bf4\u5b9e\u5728\u8bdd\uff0c\u6211\u4e0d\u662f\u4e3a\u4e86\u5979\uff0c\u662f\u4e3a\u4e86\u674e\u601d\u5357\u554a\uff01\u201d \u3000\u3000\u62d6\u96f7\u5927\u529b\u611f\u52a8\uff0c\u8bf4\u9053\uff1a\u201c\u5bf9\uff0c\u4e09\u59b9\uff0c\u4f60\u771f\u662f\u5973\u4e2d\u4e08\u592b\u3002\u674e\u601d\u5357\u77e5\u9053\u4e86\u4e5f\u4e00\u5b9a\u5f88\u611f\u6fc0\u4f60 \u7684\u3002\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u9053\uff1a\u201c\u56db\u54e5\uff0c\u4f60\u53c8\u8bf4\u9519\u4e86\u3002\u6211\u5e76\u4e0d\u662f\u8981\u4ed6\u611f\u6fc0\u624d\u8fd9\u6837\u505a\u7684\u3002\u201d \u3000\u3000\u62d6\u96f7\u9053\uff1a\u201c\u6211\u77e5\u9053\u3002\u4f46\u4f60\u4e0d\u8981\u4ed6\u611f\u6fc0\uff0c\u4ed6\u4e5f\u4f1a\u611f\u6fc0\u4f60\u7684\u3002\u4f60\u8fd9\u6837\u505a\uff0c\u8bf4\u4e0d\u5b9a\u8fd8\u4f1a\u5e2e\u4e86\u6211 \u7684\u5fd9\u5462\uff01\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u8be7\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u62d6\u96f7\u7b11\u9053\uff1a\u201c\u54b1\u4eec\u73b0\u5728\u867d\u662f\u6682\u65f6\u7f62\u5175\uff0c\u5c06\u6765\u603b\u8fd8\u662f\u8981\u5e76\u541e\u4e2d\u539f\u3002\u674e\u601d\u5357\u662f\u6c49\u4eba\u4e2d\u7684\u82f1 \u6770\uff0c\u4ed6\u82e5\u80fd\u4e3a\u6211\u6240\u7528\u2014\u2014\u201d \u3000\u3000\u539f\u6765\u62d6\u96f7\u867d\u7136\u548c\u674e\u601d\u5357\u4ea4\u4e86\u670b\u53cb\uff0c\u4f46\u4ed6\u8fd9\u4efd\u53cb\u8c0a\u5374\u5e76\u975e\u5168\u65e0\u79c1\u5fc3\u7684\uff0c\u4ed6\u591a\u5c11\u6709\u70b9\u60f3\u5229\u7528 \u674e\u601d\u5357\u7684\u5fc3\u610f\u5728\u5185\u3002\u4ed6\u5df2\u51b3\u5b9a\u4e86\u5c06\u6765\u8981\u5265\u593a\u9547\u56fd\u738b\u5b50\u7684\u5175\u6743\uff0c\u7531\u4ed6\u81ea\u5df1\u4eb2\u81ea\u9886\u5175\u541e\u91d1\u706d\u5b8b\u3002 \u8fd9\u5c31\u9700\u8981\u8bb8\u591a\u6709\u672c\u9886\u7684\u6c49\u4eba\u76f8\u52a9\u4e86\u3002 \u3000\u3000\u660e\u6167\u516c\u4e3b\u6447\u4e86\u6447\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u77e5\u9053\u674e\u601d\u5357\u5014\u5f3a\u7684\u813e\u6c14\uff0c\u53ea\u6015\u4ed6\u672a\u5fc5\u4f1a\u4e3a\u4f60\u6240\u7528\uff01\u201d \u3000\u3000\u62d6\u96f7\u9053\uff1a\u201c\u8fd9\u662f\u4ee5\u540e\u7684\u4e8b\u60c5\uff0c\u4ee5\u540e\u518d\u8bf4\u3002\u4f46\u4f60\u73b0\u5728\u6253\u7b97\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u9053\uff1a\u201c\u4f60\u662f\u8bf4\u600e\u6837\u5904\u7f6e\u6768\u59d1\u5a18\u8fd9\u4ef6\u4e8b\u60c5\u4e48\uff1f\u201d \u3000\u3000\u62d6\u96f7\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5979\u662f\u884c\u523a\u54b1\u4eec\u526f\u5143\u5e05\u7684\u51f6\u624b\uff0c\u6b64\u4e8b\u4eca\u65e5\u867d\u7136\u7ed9\u54b1\u4eec\u538b\u4e0b\u53bb\u4e86\uff0c\u603b\u4e0d\u80fd \u4e00\u76f4\u538b\u4e0b\u53bb\u7684\uff01\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u9053\uff0c\u201c\u90a3\u4f59\u4e00\u4e2d\u5176\u5b9e\u4e5f\u662f\u8be5\u6740\uff01\u201d\u5f53\u4e0b\u628a\u4f59\u4e00\u4e2d\u5192\u540d\u9876\u66ff\uff0c\u5982\u4f55\u8c0b\u5bb3\u674e\u601d\u5357\u7236 \u5b50\u7684\u60c5\u4e8b\u539f\u539f\u672c\u672c\u5730\u544a\u8bc9\u62d6\u96f7\u3002 \u3000\u3000\u62d6\u96f7\u8bf4\u9053\uff1a\u201c\u8fd9\u5bb6\u4f19\u5c06\u6765\u6211\u662f\u4f1a\u6740\u5730\u7684\uff0c\u4e0d\u8fc7\u73b0\u5728\u5374\u4e0d\u80fd\u6740\u3002\u6240\u4ee5\u54b1\u4eec\u8fd8\u662f\u5c0f\u5fc3\u4e00\u70b9\u7684 \u597d\u3002\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u6c89\u9ed8\u4e0d\u8bed\uff0c\u62d6\u96f7\u53ea\u9053\u5979\u8fd8\u4e0d\u662f\u600e\u4e48\u660e\u767d\uff0c\u4e8e\u662f\u7ee7\u7eed\u7ed9\u5979\u89e3\u91ca\uff1a\u201c\u73b0\u5728\u5927\u6c57\u4e4b\u4f4d \u672a\u5b9a\uff0c\u770b\u60c5\u52bf\u5c06\u662f\u7a9d\u9614\u53f0\u548c\u5bdf\u5408\u53f0\u4e4b\u4e89\u3002\u6211\u5012\u65e0\u610f\u4e8e\u767b\u4e0a\u5b9d\u5ea7\uff0c\u53ea\u60f3\u53d6\u5f97\u5175\u6743\u3002\u4e09\u54e5\uff08\u7a9d\u9614 \u53f0\uff09\u505a\u5927\u6c57\u5bf9\u6211\u4eec\u6bd4\u8f83\u6709\u5229\uff0c\u6211\u662f\u51b3\u610f\u6276\u52a9\u4ed6\u7684\u4e86\u3002\u4f46\u9547\u56fd\u738b\u5b50\u5374\u662f\u5bdf\u5408\u53f0\u7684\u4eba\uff0c\u4f59\u4e00\u4e2d\u662f \u5bdf\u5408\u53f0\u7684\u526f\u624b\uff0c\u5018\u82e5\u4f60\u628a\u4e00\u4e2a\u66fe\u7ecf\u884c\u523a\u8fc7\u4f59\u4e00\u4e2d\u7684\u523a\u5ba2\u5e26\u56de\u548c\u6797\uff0c\u6050\u6015\u3001\u6050\u6015\u591a\u5c11\u6709\u70b9\u4e0d \u4fbf\uff0c\u8bf4\u4e0d\u5b9a\u8fd8\u4f1a\u7ed9\u5bf9\u65b9\u62ff\u6765\u4f5c\u653b\u51fb\u54b1\u4eec\u7684\u85c9\u53e3\uff0c\u90a3\u65f6\u8fde\u4e09\u54e5\u7684\u5927\u6c57\u4e4b\u4f4d\u4e5f\u8981\u53d7\u5230\u5f71\u54cd\u4e86\u3002\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u4f60\u4eec\u4e4b\u95f4\u7684\u52fe\u5fc3\u6597\u7528\u5982\u6b64\u5389\u5bb3\uff01\u8fd9\u4e48\u8bf4\u6211\u53ea\u80fd\u548c\u6768\u5a49\u5206\u624b \u4e86\u3002\u201d \u3000\u3000\u62d6\u96f7\u9053\uff1a\u201c\u662f\u5440\u3002\u5979\u662f\u674e\u601d\u5357\u7684\u672a\u5a5a\u59bb\u5b50\uff0c\u5979\u4e5f\u5e94\u8be5\u56de\u53bb\u627e\u5979\u7684\u4e08\u592b\u3002\u4f60\u603b\u4e0d\u80fd\u4e00\u76f4\u5c06 \u5979\u7559\u5728\u8eab\u65c1\u3002\u5979\u8d70\u4e86\u4e4b\u540e\uff0c\u522b\u4eba\u8981\u67e5\u8fd9\u4ef6\u6848\u5b50\u4e5f\u5c31\u65e0\u4ece\u67e5\u8d77\u4e86\u3002\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u9053\uff1a\u201c\u4ed6\u4eec\u4e0d\u4f1a\u66f4\u52a0\u6000\u7591\u4e48\uff1f\u201d \u3000\u3000\u62d6\u96f7\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u4f1a\u8c0e\u79f0\u5979\u5728\u884c\u519b\u4e4b\u4e2d\u610f\u5916\u6b7b\u4ea1\u4e48\uff1f\u522b\u4eba\u7eb5\u6709\u6000\u7591\uff0c\u62ff\u4e0d\u5230\u4eba\u8bc1\uff0c\u4e5f\u662f \u96be\u5948\u4f60\u4f55\u3002\u4f55\u51b5\u4e09\u4e2a\u6708\u4e4b\u540e\uff0c\u5c31\u662f\u4e09\u54e5\u505a\u5927\u6c57\u4e86\u3002\u90a3\u65f6\u5927\u5c40\u5df2\u5b9a\uff0c\u6211\u6740\u4f59\u4e00\u4e2d\u90fd\u53ef\u4ee5\uff0c\u8fd9\u4e2a \u5c0f\u5c0f\u7684\u6848\u5b50\u8fd8\u6709\u8c01\u6562\u91cd\u7ffb\uff1f\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u9053\uff1a\u201c\u6211\u672c\u6765\u820d\u4e0d\u5f97\u548c\u5979\u5206\u624b\u7684\uff0c\u4f46\u542c\u4f60\u8fd9\u4e48\u8bf4\uff0c\u4e8e\u516c\u4e8e\u79c1\uff0c\u8fd8\u662f\u8ba9\u5979\u8d70\u4e86\u7684 \u597d\u3002\u4f46\u5374\u600e\u6837\u5c06\u5979\u9001\u8d70\u5462\uff1f\u201d \u3000\u3000\u62d6\u96f7\u9053\uff1a\u201c\u6211\u73b0\u5728\u8eab\u4e3a\u76d1\u56fd\uff0c\u8981\u653e\u8d70\u4e00\u4e2a\u4eba\u8fd8\u4e0d\u5bb9\u6613\uff01\u4f60\u53eb\u5979\u6362\u4e86\u7537\u88c5\uff0c\u51fa\u6765\u89c1\u6211\u3002\u201d \u3000\u3000\u4e14\u8bf4\u6768\u5a49\u5728\u5e10\u4e2d\u6362\u4e86\u8863\u88f3\uff0c\u6b63\u81ea\u5fd0\u5fd1\u4e0d\u5b89\uff0c\u660e\u6167\u516c\u4e3b\u8fdb\u6765\u8bf4\u9053\uff1a\u201c\u6768\u59d0\u59d0\uff0c\u6211\u8981\u544a\u8bc9\u4f60 \u4e00\u4ef6\u4e8b\u60c5\uff0c\u6211\u7239\u7239\u5df2\u7ecf\u6b7b\u4e86\uff01\u201d \u3000\u3000\u6768\u5a49\u5df2\u7ecf\u77e5\u9053\u6210\u5409\u601d\u6c57\u75c5\u52bf\u6c89\u91cd\uff0c\u6240\u4ee5\u5e76\u4e0d\u611f\u89c9\u610f\u5916\u3002\u4f46\u5979\u662f\u4e2a\u806a\u660e\u4eba\uff0c\u89c1\u660e\u6167\u516c\u4e3b\u5982 \u6b64\u90d1\u91cd\u5730\u544a\u8bc9\u5979\uff0c\u5f53\u7136\u60f3\u5f97\u5230\u8fd9\u4f1a\u5173\u7cfb\u81ea\u5df1\u7684\u51fa\u5904\uff0c\u5f53\u4e0b\u5b89\u6170\u4e86\u660e\u6167\u516c\u4e3b\u51e0\u53e5\uff0c\u8bf4\u9053\uff1a\u201c\u90a3 \u4e48\u516c\u4e3b\u662f\u4e0d\u662f\u8981\u56de\u8f6c\u548c\u6797\uff1f\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u9053\uff1a\u201c\u6211\u5c31\u662f\u4e3a\u4e86\u8fd9\u4ef6\u4e8b\u60c5\u4e3a\u96be\uff0c\u6211\u672c\u6765\u7b54\u5e94\u8981\u9001\u4f60\u56de\u53bb\u7684\uff0c\u73b0\u5728\u53c8\u4e0d\u77e5\u8981\u7b49 \u5230\u4ec0\u4e48\u65f6\u5019\u4e86\u3002\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u591a\u8499\u516c\u4e3b\u836b\u5e87\uff0c\u6b64\u6069\u611f\u6fc0\u4e0d\u5c3d\u3002\u5982\u4eca\u516c\u4e3b\u8981\u56de\u548c\u6797\uff0c\u6211\u81ea\u662f\u4e0d\u4fbf\u8ddf\u968f\uff0c\u8bf7\u516c \u4e3b\u8bb8\u6211\u56de\u56fd\u3002\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u9053\uff1a\u201c\u4f60\u6211\u76f8\u4ea4\u4e00\u573a\uff0c\u60c5\u5982\u59d0\u59b9\uff0c\u8bf4\u5fc3\u91cc\u8bdd\uff0c\u6211\u5b9e\u5728\u820d\u4e0d\u5f97\u4f60\u8d70\u3002\u4f46\u5929\u4e0b\u65e0\u4e0d \u6563\u4e4b\u7b75\u5e2d\uff0c\u6211\u4e5f\u4e0d\u4fbf\u803d\u8bef\u4f60\u7684\u9752\u6625\u3002\u4f46\u613f\u4f60\u4e00\u56de\u53bb\uff0c\u5f88\u5feb\u5c31\u627e\u7740\u4f60\u7684\u674e\u90ce\u3002\u201d \u3000\u3000\u6768\u5a49\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5176\u5b9e\u516c\u4e3b\u4e5f\u662f\u4e2a\u82e6\u547d\u7684\u4eba\uff0c\u770b\u6765\u5979\u5bf9\u5357\u54e5\u8fd8\u662f\u5ff5\u5ff5\u4e0d\u5fd8\uff0c\u53ea\u53ef\u60dc\u6211\u5374 \u662f\u7231\u83ab\u80fd\u52a9\u3002\u201d\u5f53\u4e0b\u8c22\u8fc7\u4e86\u660e\u6167\u516c\u4e3b\uff0c\u95ee\u9053\uff1a\u201c\u516c\u4e3b\u51e0\u65f6\u56de\u8f6c\u548c\u6797\uff1f\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u9053\uff1a\u201c\u5c31\u5728\u4eca\u5929\u3002\u201d\u6768\u5a49\u9053\uff1a\u201c\u90a3\u4e48\u6211\u2014\u2014\u201d\u660e\u6167\u516c\u4e3b\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u7528\u62c5\u5fc3\uff0c \u6211\u65e9\u5df2\u66ff\u4f60\u5b89\u6392\u597d\u4e86\u3002\u201d \u3000\u3000\u8fd9\u5ea7\u5e10\u5e55\u672c\u6765\u662f\u7559\u7ed9\u660e\u6167\u516c\u4e3b\u548c\u5979\u7684\u4f8d\u5973\u4e13\u7528\u7684\uff0c\u660e\u6167\u516c\u4e3b\u5728\u534a\u4e2a\u6708\u524d\u5c31\u51c6\u5907\u6765\u4f34\u5979\u7684 \u7236\u4eb2\u517b\u75c5\uff0c\u56f0\u6b64\u5e10\u4e2d\u7528\u5177\u5e94\u6709\u5c3d\u6709\uff0c\u516c\u4e3b\u7684\u4e00\u90e8\u5206\u8863\u7269\uff0c\u4e5f\u662f\u65e9\u5c31\u642c\u6765\u4e86\u7684\u3002 \u3000\u3000\u660e\u6167\u516c\u4e3b\u6253\u5f00\u4e00\u4e2a\u8863\u67dc\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u5355\u72ec\u56de\u53bb\uff0c\u53ef\u5f97\u518d\u6362\u8fc7\u8863\u88f3\u3002\u201d\u6768\u5a49\u672c\u6765\u6709\u4e00\u5957\u963f \u76d6\u9001\u5979\u7684\u8863\u670d\uff0c\u4f46\u8fd9\u5957\u8863\u670d\u5df2\u7ecf\u7834\u70c2\uff0c\u800c\u4e14\u4e5f\u6ca1\u6709\u5e26\u6765\u3002\u6768\u5a49\u6b63\u81ea\u62c5\u5fc3\u7a7f\u7740\u516c\u4e3b\u4f8d\u5973\u7684\u670d \u9970\uff0c\u4e0d\u4fbf\u8d70\u8def\uff0c\u6b64\u65f6\u89c1\u660e\u6167\u62ff\u51fa\u4e00\u5957\u7537\u5b50\u7684\u8863\u88f3\uff0c\u4e0d\u89c9\u559c\u51fa\u671b\u5916\uff0c\u8bf4\u9053\uff1a\u201c\u516c\u4e3b\uff0c\u4f60\u600e\u4e48\u65e9 \u5c31\u51c6\u5907\u597d\u4e86\uff1f\u4f60\u77e5\u9053\u6211\u4eca\u5929\u8981\u8d70\u7684\u4e48\uff1f\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u7b11\u9053\uff1a\u201c\u6211\u4e5f\u5e38\u5e38\u559c\u6b22\u626e\u4f5c\u7537\u5b50\u7684\uff0c\u4e0d\u8fc7\u4f60\u6ca1\u6709\u89c1\u8fc7\u7f62\u4e86\u3002\u8fd9\u662f\u6211\u7684\u730e\u88c5\uff0c\u4f60 \u8bd5\u8bd5\u5408\u4e0d\u5408\u8eab\uff1f\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u548c\u5979\u7684\u8eab\u6750\u5dee\u4e0d\u591a\uff0c\u6768\u5a49\u4e00\u8bd5\u4e4b\u4e0b\uff0c\u6b63\u597d\u5408\u9002\u3002\u660e\u6167\u516c\u4e3b\u53c8\u89e3\u4e0b\u5979\u7684\u4f69\u5251\uff0c\u8bf4 \u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u662f\u4f7f\u5251\u7684\uff0c\u8fd9\u628a\u5251\u4f60\u4e5f\u62ff\u53bb\u5427\u3002\u201d \u3000\u3000\u8fd9\u662f\u4e00\u628a\u767e\u70bc\u7cbe\u94a2\u7684\u5b9d\u5251\uff0c\u5251\u67c4\u9542\u91d1\u523b\u7389\uff0c\u540d\u8d35\u975e\u5e38\u3002\u6768\u5a49\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u600e\u80fd \u63a5\u53d7\u516c\u4e3b\u8fd9\u6837\u8d35\u91cd\u7684\u793c\u7269\uff1f\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u9053\uff1a\u201c\u4f60\u6211\u7684\u4ea4\u60c5\uff0c\u96be\u9053\u4e0d\u6bd4\u8fd9\u793c\u7269\u66f4\u8d35\u91cd\u5417\uff1f\u4f60\u82e5\u662f\u63a8\u8f9e\uff0c\u90a3\u5c31\u662f\u770b\u4e0d\u8d77 \u6211\u3002\u201d\u6768\u5a49\u611f\u5979\u60c5\u610f\u771f\u631a\uff0c\u53ea\u597d\u6536\u4e0b\u3002 \u3000\u3000\u660e\u6167\u516c\u4e3b\u9053\uff1a\u201c\u597d\u4e86\uff0c\u73b0\u5728\u54b1\u4eec\u53ef\u4ee5\u53bb\u89c1\u62d6\u96f7\u4e86\u3002\u201d \u3000\u3000\u6768\u5a49\u6709\u70b9\u60f4\u60f4\u4e0d\u5b89\uff0c\u8bf4\u9053\uff1a\u201c\u8fd8\u8981\u89c1\u62d6\u96f7\u4e48\uff1f\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u9053\uff1a\u201c\u8fd9\u6b21\u9547\u56fd\u738b\u5b50\u73ed\u5e08\u56de\u56fd\uff0c\u4f60\u5728\u8def\u4e0a\u53ef\u80fd\u9047\u4e0a\u6211\u4eec\u7684\u5b98\u5175\u662f\u6bd4\u8f83\u5c11\u4e86\u3002\u4e0d \u8fc7\u4e5f\u8fd8\u662f\u6709\u51c6\u5907\u7684\u597d\u3002\u62d6\u96f7\u73b0\u5728\u662f\u76d1\u56fd\uff0c\u4ed6\u53ef\u4ee5\u7ed9\u4f60\u65b9\u4fbf\u3002\u4f60\u7684\u4e8b\u6211\u5df2\u7ecf\u544a\u8bc9\u4e86\u4ed6\uff0c\u4ed6\u613f\u610f \u5e2e\u4f60\u7684\u5fd9\u3002\u4ed6\u548c\u674e\u6069\u5357\u4e5f\u662f\u5f88\u8981\u597d\u7684\u670b\u53cb\uff0c\u4f60\u5c3d\u53ef\u4ee5\u653e\u5fc3\u3002\u201d \u3000\u3000\u4e8c\u4eba\u8d70\u51fa\u5e10\u5e55\uff0c\u53ea\u89c1\u62d6\u96f7\u5df2\u5728\u5916\u9762\u7b49\u5019\u3002 \u3000\u3000\u62d6\u96f7\u62ff\u51fa\u4e00\u6280\u4ee4\u7bad\uff0c\u8bf4\uff1a\u201c\u8fd9\u662f\u523b\u6709\u6211\u540d\u5b57\u7684\u4ee4\u7bad\uff0c\u6709\u4eba\u95ee\u4f60\uff0c\u4f60\u8bf4\u662f\u6211\u6d3e\u4f60\u5230\u5357\u671d\u53bb \u4f5c\u7ec6\u4f5c\u597d\u4e86\uff0c\u76f8\u4fe1\u6ca1\u6709\u4eba\u6562\u4e3a\u96be\u4f60\u7684\u3002\u201d \u3000\u3000\u6768\u5a49\u6b63\u5f85\u63a5\u8fc7\u4ee4\u7bad\uff0c\u53ea\u542c\u5f97\u62d6\u96f7\u53c8\u5728\u8bf4\u9053\uff1a\u201c\u4f60\u56de\u53bb\u89c1\u4e86\u601d\u5357\u8bf7\u4ee3\u6211\u5411\u4ed6\u81f4\u610f\u3002\u76ee\u524d\u6211 \u4eec\u867d\u7136\u6682\u65f6\u505c\u6b62\u5e72\u6208\uff0c\u5c06\u6765\u603b\u8fd8\u662f\u8981\u95ee\u9f0e\u4e2d\u539f\u7684\u3002\u6211\u5df2\u51b3\u5b9a\u4e86\u7531\u6211\u81ea\u5df1\u7edf\u7387\u5e08\u65c5\uff0c\u996e\u9a6c\u9ec4 \u6cb3\uff0c\u8bf4\u4e0d\u5b9a\u6211\u4eec\u5c06\u6765\u8fd8\u6709\u76f8\u89c1\u4e4b\u65e5\u3002\u201d \u3000\u3000\u6768\u5a49\u731b\u7136\u4e00\u7701\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u62d6\u96f7\u548c\u660e\u6167\u516c\u4e3b\u4e0d\u540c\uff0c\u4ed6\u662f\u8499\u53e4\u7684\u76d1\u56fd\uff0c\u5c06\u6765\u4ed6\u82e5\u7edf\u5175\u8fdb \u72af\u4e2d\u539f\uff0c\u4ed6\u5c31\u662f\u6211\u7684\u654c\u4eba\u4e86\u3002\u6211\u5c82\u53ef\u5982\u6b64\u7cca\u6d82\uff0c\u8f7b\u6613\u63a5\u53d7\u4ed6\u7684\u6069\u60e0\uff1f\u201d \u3000\u3000\u60f3\u81f3\u6b64\u5904\uff0c\u6768\u5a49\u8fde\u5fd9\u628a\u672c\u6765\u60f3\u63a5\u4ee4\u7bad\u7684\u624b\u4e00\u63a8\uff0c\u8bf4\u9053\uff1a\u201c\u8bf7\u6055\u6c11\u5973\u4e0d\u8bc6\u62ac\u4e3e\uff0c\u8fd9\u4ee4\u7bad\u8fd8 \u662f\u8bf7\u738b\u5b50\u6536\u56de\u53bb\u5427\u3002\u201d \u3000\u3000\u62d6\u96f7\u5927\u611f\u610f\u5916\uff0c\u7709\u5934\u4e00\u76b1\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u5374\u4e3a\u4f55\uff1f\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u6211\u82e5\u53d7\u4e86\u738b\u5b50\u5982\u6b64\u5927\u6069\uff0c\u53ea\u6015\u6211\u548c\u674e\u601d\u5357\u4eca\u751f\u90fd\u662f\u96be\u4ee5\u62a5\u7b54\uff01\u201d \u3000\u3000\u62d6\u96f7\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6211\u548c\u674e\u601d\u5357\u662f\u4ea4\u6362\u8fc7\u2018\u54c8\u8fbe\u2019\u7684\u2018\u5b89\u7b54\u2019\uff0c\u4f60\u5c31\u7b49\u4e8e\u662f\u6211\u7684\u5ac2\u5b50\u4e00 \u822c\u3002\u6211\u5e2e\u5fd9\u4f60\u662f\u5e94\u8be5\u7684\uff0c\u5c82\u6709\u671b\u4f60\u62a5\u7b54\u4e4b\u7406\u3002\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u8bdd\u867d\u5982\u6b64\uff0c\u4f46\u6211\u4eec\u6c49\u4eba\u7684\u89c4\u77e9\uff0c\u53d7\u4e86\u4eba\u5bb6\u7684\u6069\u60e0\uff0c\u5c31\u7b49\u4e8e\u6b20\u4e86\u503a\u4e00\u822c\uff0c\u503a\u603b \u662f\u8981\u8fd8\u4e86\u624d\u80fd\u5fc3\u5b89\u3002\u56e0\u6b64\u738b\u5b50\u867d\u7136\u4e0d\u671b\u62a5\u7b54\uff0c\u6211\u5374\u662f\u4e0d\u80fd\u539a\u989c\u9886\u53d7\u3002\u201d \u3000\u3000\u62d6\u96f7\u7709\u5934\u76b1\u5f97\u66f4\u6df1\uff0c\u534a\u664c\u5ffd\u5730\u8bf4\u9053\uff1a\u201c\u6211\u61c2\u5f97\u4f60\u7684\u610f\u601d\u4e86\u3002\u597d\uff0c\u90a3\u6211\u5c31\u548c\u4f60\u6253\u5f00\u5929\u7a97\u8bf4 \u4eae\u8bdd\u5427\uff0c\u54b1\u4eec\u516c\u79c1\u5206\u5f00\uff0c\u6211\u7ed9\u4f60\u4ee4\u7bad\uff0c\u8fd9\u662f\u79c1\u8c0a\u3002\u5c06\u6765\u82e5\u662f\u4e3a\u4e86\u56fd\u5bb6\u5927\u4e8b\uff0c\u4f60\u4eec\u592b\u5987\u8981\u5728\u6c99 \u573a\u4e0e\u6211\u76f8\u89c1\uff0c\u6211\u4e5f\u4e0d\u602a\u4f60\u4eec\uff01\u8fd9\u4f60\u603b\u53ef\u63a5\u53d7\u4e86\u5427\uff1f\u201d\u62d6\u96f7\u7684\u8bdd\u662f\u8fd9\u6837\u8bf4\uff0c\u5fc3\u91cc\u5374\u8fd8\u662f\u60f3\u7528\u6069 \u6765\u5ba0\u7edc\u674e\u601d\u5357\u548c\u6768\u5a49\u7684\u3002 \u3000\u3000\u6768\u5a49\u6b63\u8272\u8bf4\u9053\uff1a\u201c\u516c\u8c0a\u79c1\u60c5\u6709\u65f6\u4e5f\u5f88\u96be\u5206\u5f97\u6e05\u695a\uff0c\u738b\u5b50\u7684\u597d\u610f\u6211\u5fc3\u9886\u4e86\uff0c\u56e0\u4e3a\u6211\u4e0d\u80fd\u4ee4 \u674e\u601d\u5357\u4e3a\u96be\uff01\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u89c1\u6768\u5a49\u6267\u610f\u4e0d\u80af\u63a5\u53d7\uff0c\u5bf9\u5979\u5012\u662f\u4e0d\u7981\u591a\u4e86\u4e00\u91cd\u656c\u4f69\uff0c\u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u7684\u5927\u519b \u4ece\u516d\u76d8\u5c71\u64a4\u9000\u56de\u56fd\uff0c\u56fa\u539f\u5317\u9762\uff0c\u6211\u4eec\u5e76\u65e0\u9a7b\u519b\u3002\u4f60\u4ece\u8fd9\u6761\u8def\u8d70\uff0c\u98ce\u9669\u8f83\u5c11\uff0c\u5018\u82e5\u6709\u751a\u610f\u5916\uff0c \u4f60\u53eb\u4ed6\u4eec\u628a\u4f60\u5e26\u6765\u89c1\u6211\u3002\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u591a\u8c22\u516c\u4e3b\u6307\u70b9\uff01\u201d\u5f53\u4e0b\u8de8\u4e0a\u660e\u6167\u4e48\u4e3b\u9001\u7ed9\u5979\u7684\u5750\u9a91\uff0c\u4e92\u9053\u73cd\u91cd\uff0c\u4fbf\u5373\u6325\u624b\u544a \u522b\u3002 \u3000\u3000\u62d6\u96f7\u76ee\u9001\u6768\u5a49\u4e00\u4eba\u9a91\u9a6c\u7edd\u5c18\u800c\u53bb\uff0c\u6447\u4e86\u6447\u5934\uff0c\u8138\u8272\u751a\u662f\u96be\u770b\u3002 \u3000\u3000\u660e\u6167\u516c\u4e3b\u9053\uff1a\u201c\u56db\u54e5\uff0c\u4f60\u4e0d\u662f\u602a\u5979\u4e0d\u8bc6\u62ac\u4e3e\u5427\uff1f\u6211\u5012\u662f\u89c9\u5f97\u5979\u5f88\u96be\u5f97\u5462\u3002\u201d \u3000\u3000\u62d6\u96f7\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u7684\u786e\u5f88\u662f\u96be\u5f97\uff0c\u4f46\u4e5f\u6b63\u662f\u56e0\u6b64\uff0c\u5374\u4f7f\u6211\u5bdd\u98df\u96be\u5b89\u4e86\uff01\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u201c\u5657\u55e4\u201d\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u6015\u5979\u8def\u4e0a\u51fa\u4e8b\uff1f\u90a3\u4e5f\u4e0d\u7528\u8fd9\u6837\u4e25\u91cd\uff0c\u6405\u5230\u5bdd\u98df\u96be \u5b89\u5440\uff1f\u8fd9\u6050\u6015\u4e0d\u662f\u5b8c\u5168\u4e3a\u4e86\u674e\u601d\u5357\u7684\u7f18\u6545\u4e86\u5427\uff1f\u201d \u3000\u3000\u62d6\u96f7\u602b\u7136\u4e0d\u60a6\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u60f3\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u660e\u6167\u4e48\u4e3b\u89c1\u54e5\u54e5\u8bf4\u5f97\u5982\u6b64\u90d1\u91cd\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u6539\u5bb9\u95ee\u9053\uff1a\u201c\u7136\u5219\u53c8\u662f\u4e3a\u4f55\uff1f\u201d \u3000\u3000\u62d6\u96f7\u9053\uff1a\u201c\u4f60\u60f3\u4e00\u4e2a\u65e0\u4f9d\u65e0\u9760\u7684\u5f31\u5973\uff0c\u4e5f\u80fd\u6709\u8fd9\u6837\u7684\u9aa8\u6c14\uff0c\u8fd9\u8fd8\u4e0d\u53ef\u6015\u5417\uff01\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u604d\u7136\u5927\u609f\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u4f60\u662f\u6015\u5c06\u6765\u5f81\u670d\u4e0d\u4e86\u6c49\u4eba\uff1f\u201d \u3000\u3000\u62d6\u96f7\u9053\uff1a\u201c\u4e0d\u9519\u3002\u6211\u4e00\u5411\u542c\u5f97\u6c49\u4eba\u8bb2\u7a76\u2018\u6c14\u8282\u2019\u4e8c\u5b57\uff0c\u8bf4\u662f\uff1a\u5bcc\u8d35\u4e0d\u80fd\u6deb\uff0c\u8d2b\u8d31\u4e0d\u80fd \u79fb\uff0c\u5a01\u6b66\u4e0d\u80fd\u5c48\u3002\u4e09\u59b9\uff0c\u4f60\u8bfb\u8fc7\u6c49\u4eba\u7684\u4e66\uff0c\u8fd9\u51e0\u53e5\u8bdd\u4f60\u61c2\u4e48\uff1f\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u70b9\u4e86\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u4ee5\u524d\u6559\u6211\u6c49\u4e66\u7684\u5148\u751f\u66fe\u7ecf\u7ed9\u6211\u8bb2\u89e3\u8fc7\u3002\u201d \u3000\u3000\u62d6\u96f7\u63a5\u4e0b\u53bb\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u90a3\u6211\u5c31\u4e0d\u5fc5\u7ed9\u4f60\u89e3\u91ca\u4e86\u3002\u4f46\u6211\u8fc7\u53bb\u867d\u7136\u77e5\u9053\u6c49\u4eba\u8bb2\u7a76\u6c14\u8282\uff0c\u5fc3 \u91cc\u603b\u662f\u4e0d\u5927\u76f8\u4fe1\uff0c\u6211\u60f3\u54ea\u91cc\u80fd\u6709\u8fd9\u6837\u7684\u5b8c\u4eba\u5462\uff1f\u6211\u4e00\u624b\u62ff\u7740\u5200\u5251\uff0c\u4e00\u624b\u62ff\u7740\u5b98\u804c\u91d1\u94f6\uff0c\u8c01\u4eba \u5728\u6211\u9762\u524d\u7684\u8fd8\u4e0d\u4f4e\u5934\uff1f\u73b0\u5728\u6211\u89c1\u4e86\u8fd9\u4f4d\u6768\u59d1\u5a18\uff0c\u6211\u624d\u77e5\u9053\u7684\u786e\u6709\u8fd9\u6837\u7684\u4eba\uff0c\u66f4\u53ef\u6015\u7684\u662f\uff0c\u5979 \u8fd8\u662f\u4e2a\u5f31\u8d28\u5973\u6d41\u5462\u3002\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u9053\uff1a\u201c\u6c49\u4eba\u672a\u5fc5\u4eba\u4eba\u50cf\u5979\u8fd9\u6837\uff0c\u4e0d\u662f\u4e5f\u6709\u4f59\u4e00\u4e2d\u8fd9\u6837\u7684\u5bf9\u54b1\u4eec\u5351\u8eaf\u5c48\u819d\u7684\u5927\u7537 \u4eba\u4e48\uff1f\u201d \u3000\u3000\u62d6\u96f7\u9053\uff1a\u201c\u6015\u7684\u662f\u50cf\u4f59\u4e00\u4e2d\u8fd9\u6837\u7684\u4eba\u53ea\u662f\u6781\u5c11\u6570\uff0c\u5927\u591a\u6570\u7684\u6c49\u4eba\u5018\u82e5\u50cf\u8fd9\u4f4d\u6768\u59d1\u5a18\u4e00 \u6837\uff0c\u54b1\u4eec\u5c06\u6765\u5c31\u662f\u5360\u4e86\u6c49\u4eba\u7684\u5730\u65b9\uff0c\u53ea\u6015\u4e5f\u662f\u4e0d\u80fd\u957f\u4e45\u3002\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u7b11\u9053\uff1a\u201c\u90a3\u5c31\u4e0d\u5fc5\u53bb\u6253\u6c49\u4eba\u7684\u5730\u65b9\u597d\u4e86\u3002\u201d \u3000\u3000\u62d6\u96f7\u9053\uff1a\u201c\u7239\u7239\u7684\u9057\u5631\u4f60\u6211\u5c82\u80fd\u8fdd\u80cc\uff1f\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u9053\uff1a\u201c\u7239\u7239\u5df2\u7ecf\u6b7b\u4e86\uff0c\u4f60\u9075\u547d\u4e5f\u597d\uff0c\u8fdd\u80cc\u4e5f\u597d\uff0c\u53cd\u6b63\u4ed6\u90fd\u662f\u4e0d\u4f1a\u77e5\u9053\u7684\u4e86\u3002\u201d \u3000\u3000\u62d6\u96f7\u76b1\u8d77\u7709\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8fd9\u662f\u4e0d\u61c2\u4e8b\u7684\u5b69\u5b50\u8bdd\uff01\u6211\u82e5\u4e0d\u53bb\u6253\u6c49\u4eba\u7684\u5730\u65b9\uff0c\u7109\u80fd\u638c\u63e1\u5175 \u6743\u3002\u6211\u638c\u63e1\u4e0d\u4e86\u5175\u6743\uff0c\u4e0d\u4f46\u662f\u6211\u8981\u7ed9\u4e8c\u54e5\u6740\u6389\uff0c\u8fde\u4f60\u4e5f\u6ca1\u6709\u4fdd\u969c\uff01\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u9ed8\u7136\u4e0d\u8bed\u3002\u9065\u671b\u8fdc\u65b9\uff0c\u6768\u5a49\u65e9\u5df2\u8d70\u5f97\u4e0d\u89c1\u4e86\uff0c\u4f46\u5728\u5979\u8d70\u8fc7\u7684\u90a3\u6761\u8def\u4e0a\uff0c\u9a6c\u8e44\u6e85 \u8d77\u7684\u5c18\u571f\u8fd8\u5728\u98de\u626c\u3002 \u3000\u3000\u660e\u6167\u516c\u4e3b\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u522b\u4eba\u90fd\u7fa1\u6155\u6211\u8eab\u4e3a\u516c\u4e3b\uff0c\u6211\u5012\u662f\u7fa1\u6155\u6768\u5a49\u3002\u5979\u867d\u7136\u4e5f\u662f\u7236\u6bcd\u53cc \u4ea1\uff0c\u4f46\u8fd8\u6709\u4e00\u4e2a\u674e\u601d\u5357\uff0c\u6211\u5374\u662f\u8fde\u4e00\u4e2a\u77e5\u5fc3\u7684\u670b\u53cb\u90fd\u6ca1\u6709\u3002\u5979\u53ef\u4ee5\u6d77\u9614\u5929\u7a7a\u4efb\u610f\u98de\u7fd4\uff0c\u6211\u5374 \u50cf\u662f\u56f0\u5728\u91d1\u4e1d\u7b3c\u91cc\u7684\u9e1f\u513f\uff0c\u4e0d\u80fd\u81ea\u4e3b\u3002\u5509\uff0c\u6768\u59d0\u59d0\u5b9e\u5728\u6bd4\u6211\u5e78\u798f\u5f97\u591a\uff0c\u4f46\u613f\u5979\u5728\u8def\u4e0a\u4e0d\u8981\u51fa \u4e8b\u624d\u597d\u3002\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u7684\u795d\u798f\u6ca1\u6709\u843d\u7a7a\uff0c\u6768\u5a49\u679c\u7136\u662f\u4e00\u8def\u5e73\u5b89\uff0c\u6ca1\u6709\u51fa\u4e8b\u3002\u5979\u4f9d\u7167\u660e\u6167\u516c\u4e3b\u7684\u6307\u70b9\uff0c \u4ece\u56fa\u539f\u4e4b\u5317\u7ed5\u8fc7\u516d\u76d8\u5c71\uff0c\u4e00\u4e2a\u8499\u4e3b\u5175\u4e5f\u6ca1\u9047\u4e0a\u3002 \u3000\u3000\u660e\u6167\u516c\u4e3b\u5728\u6302\u5ff5\u5979\uff0c\u5979\u5219\u5728\u6302\u5ff5\u674e\u601d\u5357\u3002\u201c\u5357\u54e5\u4e0d\u77e5\u5df2\u7ecf\u9003\u4e86\u51fa\u6765\u6ca1\u6709\uff1f\u4eba\u6d77\u832b\u832b\uff0c\u5374 \u5230\u54ea\u91cc\u5bfb\u627e\u4ed6\u5462\uff1f\u201d\u6768\u5a49\u5fc3\u60f3\u3002\u6b63\u662f\uff1a \u3000\u3000\u4eba\u6d77\u832b\u832b\u4f55\u5904\u89c5\uff1f\u4e3a\u541b\u4e00\u65e5\u4e5d\u56de\u80a0\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b\u2014\u2014\u7b2c\u5341\u4e5d\u56de\u3000\u82e6\u9152\u53c8\u6dfb\u8c6a\u6770\u6cea\u3000\u6625\u6ce2\u672a\u901d\u6545\u4eba\u60c5 \u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b \u7b2c\u5341\u4e5d\u56de\u3000\u82e6\u9152\u53c8\u6dfb\u8c6a\u6770\u6cea\u3000\u6625\u6ce2\u672a\u901d\u6545\u4eba\u60c5 \u3000\u3000\u6768\u5a49\u5ffd\u5730\u5fc3\u5ff5\u4e00\u52a8\uff0c\u8bb0\u8d77\u4e86\u674e\u601d\u5357\u7684\u6545\u4e61\u4e43\u662f\u5728\u5c71\u4e1c\u6b66\u57ce\u3002\u6768\u5a49\u6697\u81ea\u601d\u9053\uff1a\u201c\u5357\u54e5\u82e5\u662f \u5df2\u7ecf\u8131\u9669\u7684\u8bdd\uff0c\u4ed6\u4e00\u5b9a\u8981\u56de\u4e61\u63a2\u671b\u6bcd\u4eb2\u7684\u3002\u5bf9\uff0c\u6211\u5230\u6b66\u57ce\u627e\u4ed6\uff01\u201d \u3000\u3000\u6768\u5a49\u731c\u5f97\u4e0d\u9519\uff0c\u674e\u601d\u5357\u7684\u786e\u662f\u5728\u56de\u4e61\u7684\u9014\u4e2d\u3002\u4f46\u6768\u5a49\u5374\u4e0d\u77e5\u9053\uff0c\u5979\u81ea\u5df1\u662f\u62b1\u7740\u6ee1\u6000\u5e0c\u671b \u53bb\u627e\u674e\u601d\u5357\uff1b\u674e\u601d\u5357\u5bf9\u5979\u5219\u662f\u5df2\u7ecf\u7edd\u671b\uff0c\u4ed6\u662f\u6000\u7740\u4e00\u9897\u521b\u4f24\u7684\u5fc3\u7075\uff0c\u72ec\u81ea\u56de\u4e61\u7684\u3002 \u3000\u3000\u82b1\u5f00\u4e24\u6735\uff0c\u5404\u8868\u4e00\u679d\u3002\u4e14\u8bf4\u674e\u601d\u5357\u90a3\u65e5\u9003\u51fa\u4e86\u8fb9\u5883\u7684\u90a3\u4e2a\u5e02\u9547\u4e4b\u540e\uff0c\u5fc3\u4e2d\u65e0\u9650\u60b2\u9178\u3002\u4ed6 \u4ee5\u4e3a\u6768\u5a49\u5df2\u7ecf\u6539\u5ac1\uff0c\u4e0d\u4f46\u4e0d\u6562\u5b58\u7740\u201c\u7834\u955c\u91cd\u5706\u201d\u7684\u5e0c\u671b\uff0c\u8fde\u89c1\u90fd\u4e0d\u60f3\u518d\u89c1\u6768\u5a49\u4e86\u3002\u8fd9\u4e5f\u602a\u4e0d \u5f97\u4ed6\uff0c\u56e0\u4e3a\u4ed6\u66fe\u7ecf\u5230\u8fc7\u6768\u5a49\u548c\u5c60\u9f99\u6295\u5bbf\u7684\u90a3\u95f4\u5ba2\u5e97\uff0c\u77e5\u9053\u4ed6\u4eec\u4e24\u4eba\u662f\u540c\u4f4f\u4e00\u95f4\u623f\u95f4\u3002\u5728\u90a3\u95f4 \u623f\u95f4\u91cc\u4ed6\u8fd8\u627e\u5230\u6768\u5a49\u5f03\u6389\u7684\u65e7\u8863\u88f3\uff0c\u800c\u4e14\u8fd8\u66fe\u548c\u81ea\u79f0\u662f\u6768\u5a49\u4e08\u592b\u7684\u5c60\u9f99\u4ea4\u8fc7\u624b\u6765\uff0c\u4ed6\u54ea\u91cc\u77e5 \u9053\u5176\u4e2d\u53e6\u6709\u8bb8\u591a\u66f2\u6298\uff1f \u3000\u3000\u626c\u5a49\u90a3\u51e0\u4ef6\u65e7\u8863\u88f3\u4ed6\u5df2\u7ecf\u6536\u8fdb\u81ea\u5df1\u7684\u884c\u56ca\u5e26\u8d70\uff0c\u6bcf\u6b21\u5c55\u793a\u65e7\u8863\uff0c\u5c31\u597d\u50cf\u770b\u89c1\u6768\u5a49\u7684\u5f71 \u5b50\uff0c\u5f15\u8d77\u4ed6\u65e0\u9650\u4f24\u5fc3\u3002 \u3000\u3000\u201c\u53e4\u8bed\u6709\u4e91\uff1a\u8863\u4e0d\u5982\u65b0\uff0c\u4eba\u4e0d\u5982\u6545\u3002\u4f46\u53ea\u6015\u5a49\u59b9\u5374\u662f\u5bf9\u7740\u65b0\u4eba\u5fd8\u4e86\u6545\u4eba\u4e86\u3002\u201d\u53c8\u60f3\uff1a \u201c\u4f46\u8fd9\u4e5f\u602a\u4e0d\u5f97\u5979\uff0c\u5979\u65e0\u4f9d\u65e0\u9760\uff0c\u53c8\u4e0d\u77e5\u9053\u6211\u662f\u6b7b\u662f\u751f\u3002\u201d\u201c\u4e0d\u8fc7\u5979\u5ac1\u7684\u90a3\u4e2a\u4eba\u786e\u5b9e\u5728\u662f\u4e2a \u5351\u52a3\u5c0f\u4eba\uff0c\u8fd9\u5374\u662f\u6211\u4e0d\u80fd\u4e0d\u4e3a\u5979\u53f9\u606f\u7684\u3002\u5947\u602a\uff0c\u4ee5\u5979\u8fd9\u6837\u61c2\u4e8b\u660e\u7406\u7684\u806a\u660e\u5973\u5b50\uff0c\u600e\u4f1a\u5ac1\u7ed9\u90a3 \u4e2a\u4eba\u5462\uff1f\u201d\u201c\u4f46\u8fd9\u4e5f\u662f\u5404\u4e2a\u4eba\u7684\u7f18\u5206\uff0c\u6211\u66ff\u5979\u53f9\u606f\u4e5f\u662f\u633d\u6551\u4e0d\u6765\u3002\u5509\uff0c\u6211\u8eab\u8d1f\u5bb6\u56fd\u6df1\u4ec7\uff0c\u8fd9 \u4e9b\u70e6\u607c\u7684\u79c1\u60c5\uff0c\u4e0d\u60f3\u4e5f\u7f62\u3002\u201d\u8bdd\u867d\u5982\u6b64\uff0c\u4f46\u6768\u5a49\u4e0e\u4ed6\u66fe\u7ecf\u5171\u540c\u8fc7\u4e86\u5927\u534a\u5e74\u540c\u547d\u76f8\u4f9d\u7684\u65e5\u5b50\uff0c \u6768\u5a49\u7684\u5f71\u5b50\uff0c\u4ed6\u662f\u600e\u6837\u4e5f\u4e0d\u80fd\u5fd8\u6389\u7684\u3002 \u3000\u3000\u674e\u601d\u5357\u6302\u5ff5\u7740\u8870\u8001\u7684\u6bcd\u4eb2\uff0c\u65e5\u591c\u517c\u7a0b\u8d76\u8def\uff0c\u8def\u4e0a\u5e78\u597d\u4e5f\u6ca1\u610f\u5916\uff0c\u8fd9\u4e00\u65e5\u4ed6\u7ec8\u4e8e\u56de\u5230\u4e86\u5bb6 \u4e61\u3002 \u3000\u3000\u674e\u601d\u5357\u62ac\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u4ed6\u5bb6\u7684\u5927\u95e8\u7d27\u95ed\uff0c\u6a90\u5934\u7684\u86db\u7f51\u7eb5\u6a2a\u4ea4\u9519\uff0c\u4e5f\u6ca1\u4eba\u626b\u9664\u3002\u674e\u601d\u5357\u4e0d \u7981\u6709\u70b9\u5947\u602a\uff1a\u201c\u5988\u662f\u9876\u7231\u6d01\u51c0\u7684\u4eba\uff0c\u96be\u9053\u5979\u662f\u75c5\u4e86\uff0c\u6240\u4ee5\u624d\u6ca1\u6709\u626b\u9664\uff1f\u4f46\u5927\u767d\u5929\u4e3a\u4ec0\u4e48\u53c8\u8981 \u5173\u4e0a\u95e8\u5462\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u4e00\u638c\u63a8\u5f00\u5927\u95e8\uff0c\u53eb\u9053\uff1a\u201c\u5988\uff0c\u6211\u56de\u6765\u5566\uff01\u201d\u5c4b\u5b50\u91cc\u9759\u6084\u6084\u7684\uff0c\u552f\u6709\u4ed6\u7684\u56de\u58f0\u3002\u674e \u601d\u5357\u4e00\u9897\u5fc3\u7830\u7830\u5730\u8df3\uff0c\u614c\u5fd9\u4e09\u6b65\u5e76\u4f5c\u4e24\u6b65\u8dd1\u8fdb\u53bb\u3002\u8e0f\u5165\u524d\u5385\uff0c\u53ea\u89c1\u4e00\u5177\u68fa\u6750\u6446\u5728\u5f53\u4e2d\u3002\u674e\u601d \u5357\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u53cc\u817f\u4e00\u9178\uff0c\u767b\u65f6\u8dcc\u5012\uff0c\u6251\u5728\u68fa\u6750\u4e0a\uff01 \u3000\u3000\u8033\u8fb9\u5ffd\u542c\u5f97\u4e00\u4e2a\u6148\u7965\u7684\u58f0\u97f3\u53eb\u9053\uff1a\u201c\u674e\u76f8\u516c\uff0c\u9192\u9192\uff0c\u9192\u9192\uff01\u201d\u674e\u601d\u5357\u722c\u8d77\u8eab\u6765\uff0c\u62ac\u773c\u4e00 \u770b\uff0c\u8ba4\u5f97\u662f\u90bb\u5c45\u7684\u5f20\u5927\u53d4\u3002\u674e\u601d\u5357\u832b\u7136\u95ee\u9053\uff1a\u201c\u5f20\u5927\u53d4\uff0c\u6211\u5988\uff0c\u6211\u5988\u2014\u2014\u201d\u5176\u5b9e\u8fd9\u4e00\u95ee\u5df2\u662f \u591a\u4f59\uff0c\u4ed6\u5bb6\u91cc\u53ea\u6709\u4e00\u4e2a\u8001\u6bcd\u4eb2\uff0c\u4e0d\u662f\u6bcd\u4eb2\u7684\u68fa\u6750\u8fd8\u80fd\u662f\u8c01\u7684\u68fa\u6750\uff1f \u3000\u3000\u5f20\u5927\u53d4\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u82e6\u547d\u7684\u5b69\u5b50\uff0c\u4f60\u5988\u5df2\u7ecf\u6b7b\u4e86\uff01\u201d \u3000\u3000\u5f20\u5927\u53d4\u62b9\u4e86\u62b9\u773c\u6cea\uff0c\u63a5\u7740\u5f80\u4e0b\u8bf4\u9053\uff1a\u201c\u4f60\u5988\u8eab\u5b50\u672c\u6765\u5c31\u4e0d\u5927\u597d\uff0c\u4e0a\u4e2a\u6708\u521d\uff0c\u5979\u542c\u8bf4\u8499\u53e4 \u9791\u5b50\u5175\u5c31\u8981\u6253\u6765\uff0c\u62c5\u5fe7\u5f97\u5f88\u3002\u5979\u8bf4\u5979\u540e\u6094\u53eb\u4f60\u53bb\u627e\u7239\u7239\uff0c\u62c5\u5fe7\u6218\u4e8b\u4e00\u8d77\uff0c\u4f60\u4e5f\u56de\u4e0d\u6765\u4e86\u3002\u6211 \u529d\u89e3\u8bf4\u5357\u54e5\u513f\u7cbe\u660e\u80fd\u5e72\uff0c\u591a\u534a\u4f1a\u627e\u7740\u4ed6\u7239\uff0c\u5c31\u662f\u627e\u4e0d\u7740\u4e5f\u4f1a\u56de\u6765\u7684\u3002\u53ef\u662f\u6211\u867d\u7136\u767e\u822c\u5f00\u89e3\uff0c \u603b\u5374\u662f\u6d88\u9664\u4e0d\u4e86\u5979\u5fc3\u4e2d\u7684\u5fe7\u8651\u3002\u5c31\u8fd9\u6837\u5979\u5f97\u4e86\u75c5\uff0c\u4e61\u4e0b\u53c8\u6ca1\u6709\u4ec0\u4e48\u597d\u533b\u751f\uff0c\u62d6\u5230\u4e86\u672c\u6708\u521d \u4e5d\uff0c\u5979\u7ec8\u4e8e\u4e00\u75c5\u4e0d\u8d77\uff0c\u7b49\u4e0d\u5230\u4f60\u56de\u6765\u4e86\u3002\u4f60\u5bb6\u5e76\u65e0\u4eb2\u4eba\uff0c\u662f\u6211\u64c5\u81ea\u4f5c\u4e3b\uff0c\u66ff\u4f60\u5988\u7f6e\u4e86\u8fd9\u4e00\u68fa \u6750\uff0c\u8349\u8349\u7ed9\u5979\u6536\u6b93\uff0c\u505c\u68fa\u5728\u5802\uff0c\u7b49\u4f60\u56de\u6765\u4e0b\u846c\u3002\u5440\uff0c\u5357\u54e5\u513f\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u53cc\u81ea\u53d1\u5446\uff0c\u7d27\u7d27\u54ac\u7740\u5634\u5507\uff0c\u8840\u6c34\u4ece\u7259\u7f1d\u91cc\u5f80\u5916\u76f4\u6dcc\uff0c\u731b\u5730\u5934\u649e\u68fa\u6750\uff0c\u53eb\u9053\uff1a \u201c\u5988\uff0c\u90fd\u662f\u5b69\u513f\u4e0d\u5b5d\uff0c\u7d2f\u4f60\u6b7b\u4e0d\u7791\u76ee\uff01\u201d \u3000\u3000\u5f20\u5927\u53d4\u8fde\u5fd9\u5c06\u4ed6\u62d6\u4f4f\uff0c\u8bf4\u9053\uff1a\u201c\u5357\u54e5\u513f\uff0c\u674e\u5bb6\u53ea\u6709\u4f60\u4e00\u6761\u6839\u5b50\uff0c\u4f60\u8981\u542c\u5927\u53d4\u7684\u8bdd\uff0c\u597d\u597d \u4fdd\u91cd\u81ea\u5df1\uff0c\u8fd9\u624d\u5bf9\u5f97\u4f4f\u4f60\u6b7b\u53bb\u7684\u6bcd\u4eb2\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u795e\u667a\u987f\u590d\u4e86\u51e0\u5206\u6e05\u9192\uff0c\u8fd9\u624d\u568e\u9676\u75db\u54ed\u8d77\u6765\u3002\u5f20\u5927\u53d4\u5f85\u4ed6\u75db\u54ed\u4e86\u4e00\u573a\uff0c\u8bf4\u9053\uff1a\u201c\u4eba \u6b7b\u4e0d\u80fd\u590d\u751f\uff0c\u4f60\u65e2\u7136\u56de\u6765\u4e86\uff0c\u8fd8\u662f\u8ba9\u4f60\u5988\u65e9\u4e9b\u5165\u571f\u4e3a\u5b89\u5427\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u8dea\u4e0b\u53bb\u7ed9\u5f20\u5927\u53d4\u78d5\u4e86\u4e09\u4e2a\u54cd\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u591a\u8499\u5927\u53d4\u7167\u6599\u6211\u5a18\uff0c\u5927\u5fb7\u5927\u6069\uff0c\u65e0\u4ee5\u4e3a \u62a5\u3002\u6211\u5988\u7684\u4e27\u4e8b\uff0c\u8fd8\u5f97\u8bf7\u5927\u53d4\u5e2e\u5fd9\u3002\u201d \u3000\u3000\u5f20\u5927\u53d4\u9053\uff1a\u201c\u4fd7\u8bed\u8bf4\u8fdc\u4eb2\u4e0d\u5982\u8fd1\u90bb\uff0c\u60a3\u96be\u76f8\u52a9\uff0c\u8fd9\u662f\u5e94\u8be5\u7684\u3002\u4f60\u5c31\u62e9\u4e2a\u65e5\u5b50\uff0c\u5b89\u846c\u4f60\u7684 \u6bcd\u4eb2\u5427\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u98ce\u6c34\u8fd9\u4e00\u5957\u6211\u662f\u4e0d\u76f8\u4fe1\u7684\u3002\u5927\u53d4\u4f60\u8bf4\u5f97\u5bf9\uff0c\u8fd8\u662f\u8ba9\u5988\u65e9\u70b9\u5165\u571f\u4e3a\u5b89\u7684\u597d\uff0c \u660e\u5929\u4e0d\u77e5\u5927\u53d4\u6709\u6ca1\u6709\u7a7a\uff1f\u201d \u3000\u3000\u5f20\u5927\u53d4\u9053\uff1a\u201c\u73b0\u5728\u662f\u519c\u95f2\u65f6\u8282\uff0c\u4f60\u660e\u5929\u529e\u7406\u4e27\u4e8b\uff0c\u6211\u53eb\u5de6\u90bb\u53f3\u91cc\uff0c\u90fd\u6765\u7ed9\u4f60\u5e2e\u5fd9\u3002\u201d \u3000\u3000\u7b2c\u4e8c\u5929\u674e\u601d\u5357\u846c\u4e86\u6bcd\u4eb2\uff0c\u56de\u6765\u4e4b\u540e\uff0c\u5c06\u5bb6\u4e2d\u5269\u4f59\u7684\u8863\u7269\uff0c\u5c3d\u90fd\u5206\u7ed9\u5de6\u90bb\u53f3\u91cc\uff0c\u53e6\u5916\u7279\u522b \u916c\u8c22\u7ed9\u4ed6\u5e2e\u5fd9\u6700\u5927\u7684\u5f20\u5927\u53d4\uff0c\u5c06\u4ece\u8499\u53e4\u5e26\u56de\u6765\u7684\u94f6\u5b50\u90fd\u9001\u7ed9\u4e86\u4ed6\u3002 \u3000\u3000\u5f20\u5927\u53d4\u9053\uff1a\u201c\u4f60\u628a\u5bb6\u91cc\u7684\u4e1c\u897f\u90e8\u9001\u7ed9\u4eba\uff0c\u96be\u9053\u8fd9\u4e2a\u5bb6\u4f60\u4e0d\u8981\u4e86\u4e48\uff1f\u4f60\u53c8\u4e0d\u662f\u53d1\u8d22\u56de\u6765\uff0c \u4f60\u7684\u94f6\u5b50\u6211\u4e0d\u80fd\u8981\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u6211\u6b63\u60f3\u544a\u8bc9\u5927\u53d4\uff0c\u660e\u5929\u6211\u5c31\u8981\u8d70\u4e86\u3002\u201d \u3000\u3000\u5f20\u5927\u53d4\u9053\uff1a\u201c\u4f60\u4e00\u56de\u6765\u5c31\u8d70\uff1f\u4e5f\u4e0d\u7b49\u2018\u6ee1\u4e03\u2019\u4e48\u3002\u201d\uff08\u6c11\u95f4\u4e60\u4fd7\uff0c\u5b5d\u5b50\u5b88\u7075\u4e03\u4e03\u56db\u5341\u4e5d \u5929\uff0c\u662f\u8c13\u201c\u6ee1\u4e03\u201d\uff09\u3002 \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u6211\u7236\u6bcd\u53cc\u4ea1\uff0c\u8fd9\u5c4b\u5b50\u6211\u662f\u4e0d\u60f3\u518d\u4f4f\u4e0b\u53bb\u4e86\u3002\u6211\u7239\u751f\u524d\u66fe\u7ecf\u6559\u8bf2\u8fc7\u6211\uff1a\u56fd\u5bb6 \u5174\u4ea1\uff0c\u5339\u592b\u6709\u8d23\u3002\u8981\u4e3a\u56fd\u5bb6\u3001\u4e3a\u767e\u59d3\u5c3d\u81ea\u5df1\u7684\u529b\uff0c\u624d\u7b97\u5f97\u662f\u5927\u5b5d\u5927\u5fe0\u3002\u76ee\u4e0b\u8499\u53e4\u7684\u9791\u5b50\u5175\u5df2 \u7ecf\u5f00\u59cb\u5165\u4fb5\uff0c\u8fd9\u6b63\u662f\u8981\u6211\u51fa\u529b\u7684\u65f6\u5019\uff0c\u6240\u4ee5\u6211\u60f3\uff0c\u6211\u4e0d\u7ed9\u6211\u5988\u5b88\u7075\uff0c\u5988\u5927\u7ea6\u4e5f\u4e0d\u4f1a\u8d23\u602a\u6211 \u7684\u3002\u201d \u3000\u3000\u5f20\u5927\u53d4\u70b9\u4e86\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u7537\u513f\u5fd7\u5728\u56db\u65b9\uff0c\u50cf\u4f60\u8fd9\u6837\u7684\u4eba\u6750\uff0c\u672c\u6765\u4e5f\u4e0d\u8be5\u56fa\u5b88\u5bb6 \u56ed\u7684\u3002\u597d\u5427\uff0c\u90a3\u4e48\uff0c\u4f60\u5c31\u8d70\u5427\u3002\u4f60\u7684\u5bb6\u6211\u5e2e\u4f60\u7167\u6599\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u82e6\u7b11\u9053\uff1a\u201c\u6211\u8fd9\u4e00\u8d70\uff0c\u4e0d\u77e5\u4ec0\u4e48\u65f6\u5019\u624d\u80fd\u56de\u6765\uff0c\u8fd9\u4e2a\u7834\u7834\u70c2\u70c2\u7684\u5bb6\uff0c\u5176\u5b9e\u4e5f\u7528\u4e0d \u7740\u4ec0\u4e48\u7167\u6599\u4e86\u3002\u4f60\u82e5\u662f\u4e0d\u5acc\u5f03\uff0c\u5c31\u9001\u7ed9\u4f60\u505a\u725b\u68da\u4e5f\u597d\uff0c\u505a\u5806\u67f4\u8349\u7684\u5730\u65b9\u4e5f\u597d\u3002\u5916\u9762\u6211\u8fd8\u6709\u51e0 \u4e2a\u597d\u670b\u53cb\uff0c\u4e0d\u6101\u6ca1\u5904\u8ba8\u751f\u6d3b\u3002\u8fd9\u70b9\u94f6\u5b50\u4e5f\u52a1\u5fc5\u8bf7\u4f60\u6536\u4e0b\u3002\u201d \u3000\u3000\u5f20\u5927\u53d4\u63a8\u8f9e\u4e0d\u6389\uff0c\u53ea\u597d\u6536\u4e0b\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u4e48\u660e\u5929\u6211\u6765\u7ed9\u4f60\u9001\u884c\u3002\u201d\u674e\u601d\u5357\u9053\uff1a\u201c\u4e0d\u6562\u60ca \u52a8\u5927\u53d4\u4e86\uff0c\u4f60\u5df2\u7ecf\u5fd9\u4e86\u4e00\u6574\u5929\u4e86\uff0c\u660e\u5929\u6211\u53ef\u80fd\u5929\u6ca1\u4eae\u5c31\u52a8\u8eab\u7684\u3002\u201d \u3000\u3000\u9001\u8d70\u4e86\u5f20\u5927\u53d4\u540e\uff0c\u674e\u601d\u5357\u5bf9\u7740\u6bcd\u4eb2\u7684\u724c\u4f4d\uff0c\u4e0d\u7981\u60b2\u4ece\u4e2d\u6765\uff0c\u96be\u4ee5\u65ad\u7edd\uff0c\u53c8\u54ed\u4e86\u4e00\u573a\u3002 \u3000\u3000\u8fd9\u65f6\u5df2\u662f\u4e8c\u66f4\u65f6\u5206\uff0c\u674e\u601d\u5357\u77e5\u9053\u4eca\u665a\u662f\u65e0\u8bba\u5982\u4f55\u4e5f\u7761\u4e0d\u7740\u7684\u4e86\u3002\u4ed6\u627e\u5230\u4e86\u4e00\u74f6\u9648\u9152\uff0c\u5c31 \u5728\u7075\u5802\uff0c\u501f\u9152\u6d47\u6101\u3002 \u3000\u3000\u9648\u9152\u672c\u6765\u662f\u6251\u9f3b\u55b7\u9999\uff0c\u4f46\u559d\u5165\u4e86\u674e\u601d\u5357\u7684\u53e3\u4e2d\uff0c\u5374\u53d8\u6210\u4e86\u597d\u50cf\u6d78\u8fc7\u9ec4\u83b2\u7684\u82e6\u9152\u3002\u8fd9\u4e00\u5e74 \u6765\uff0c\u4ed6\u7ecf\u5386\u8fc7\u7684\u79cd\u79cd\u82e6\u96be\u7684\u906d\u9047\uff0c\u4ed6\u60f3\u8981\u5fd8\u6389\u800c\u53c8\u504f\u504f\u5fd8\u4e0d\u4e86\u7684\u8bb0\u5fc6\uff0c\u90fd\u968f\u7740\u9152\u610f\uff0c\u6d8c\u4e0a\u5fc3 \u5934\u3002 \u3000\u3000\u4ed6\u60f3\u8d77\u4e86\u53bb\u5e74\u79bb\u5bb6\u4e4b\u65f6\uff0c\u4ed6\u6bcd\u4eb2\u5bf9\u4ed6\u7684\u53ee\u549b\u5631\u5490\u3002\u4ed6\u6ca1\u6709\u8f9c\u8d1f\u6bcd\u4eb2\u7684\u671f\u671b\uff0c\u5728\u7ecf\u5386\u4e86\u5927 \u6f20\u6d41\u6c99\u7684\u8270\u9669\u65c5\u7a0b\u4e4b\u540e\uff0c\u7ec8\u4e8e\u5728\u90a3\u5ea7\u8352\u5c71\u627e\u5230\u4e86\u4ed6\u7684\u7236\u4eb2\uff0c\u53ef\u662f\u4ed6\u4eec\u7236\u5b50\u76f8\u5904\u8fd8\u4e0d\u5230\u4e00\u5929\uff0c \u5f53\u5929\u665a\u4e0a\uff0c\u4ed6\u90a3\u5ae1\u4eb2\u7684\u7236\u4eb2\u5c31\u5728\u4ed6\u7684\u6000\u4e2d\u901d\u4e16\u3002 \u3000\u3000\u4ed6\u60f3\u8d77\u4e86\u7236\u4eb2\u4e34\u7ec8\u4e4b\u524d\u7ed3\u4e0b\u7684\u201c\u7ea2\u7ef3\u201d\uff0c\u628a\u4ed6\u548c\u6768\u5a49\u7edc\u5728\u4e00\u8d77\u3002\u4ed6\u7684\u7236\u4eb2\u975e\u5e38\u559c\u6b22\u6768 \u5a49\uff0c\u4ed6\u8fd8\u8bb0\u5f97\u5728\u4ed6\u4eec\u4e8c\u4eba\u613f\u610f\u9075\u547d\u8ba2\u5a5a\u4e4b\u540e\uff0c\u4ed6\u7684\u7236\u4eb2\u662f\u591a\u4e48\u7684\u559c\u60a6\uff0c\u4ed6\u7684\u6b7b\u662f\u5728\u6ee1\u6000\u559c\u60a6 \u7684\u7b11\u58f0\u4e4b\u4e2d\u65ad\u6c14\u7684\u3002\u201c\u7239\u7239\u6b7b\u7684\u65f6\u5019\u5012\u662f\u6ca1\u6709\u534a\u70b9\u75db\u82e6\uff0c\u4ed6\u4ee5\u4e3a\u6211\u4eec\u5b9a\u80fd\u767d\u5934\u5055\u8001\uff0c\u5e78\u798f\u7ec8 \u751f\u3002\u5509\uff0c\u4ed6\u53c8\u600e\u77e5\u9053\u6211\u4eec\u4f1a\u6709\u4eca\u65e5\u2014\u2014\u672a\u66fe\u6b7b\u522b\uff0c\u5148\u5df2\u751f\u79bb\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u53c8\u4e00\u6b21\u6253\u5f00\u4e86\u90a3\u4e2a\u7834\u65e7\u7684\u5305\u88b1\uff0c\u7ffb\u770b\u4e86\u6768\u5a49\u7559\u4e0b\u7684\u7834\u8863\u88f3\uff0c\u4e0d\u7531\u5f97\u5fc3\u4e2d\u65e0\u9650\u611f \u89e6\uff0c\u9152\u4eba\u6101\u80a0\uff0c\u8d8a\u53d1\u82e6\u4e86\u3002 \u3000\u3000\u674e\u601d\u5357\u6447\u4e86\u6447\u5934\uff0c\u8111\u6d77\u91cc\u5ffd\u5730\u6cdb\u8d77\u53e6\u4e00\u4e2a\u5c11\u5973\u7684\u5f71\u5b50\uff0c\u8fd9\u662f\u4ed6\u597d\u4e45\u4ee5\u6765\u90fd\u6ca1\u6709\u60f3\u8fc7\u7684\u5b5f \u660e\u971e\uff0c\u4e0d\u77e5\u600e\u7684\uff0c\u4eca\u665a\u5728\u4ed6\u6ee1\u6000\u82e6\u695a\u7684\u65f6\u5019\uff0c\u53c8\u6084\u6084\u5730\u6765\u4e86\u3002\u4ed6\u6447\u4e86\u6447\u5934\uff0c\u4f3c\u4e4e\u60f3\u8981\u6447\u843d\u5b5f \u660e\u971e\u7684\u5f71\u5b50\uff0c\u53ef\u662f\u8fd9\u5f71\u5b50\u7adf\u4f3c\u4e2a\u4e0d\u901f\u4e4b\u5ba2\uff0c\u5f3a\u786c\u975e\u5e38\uff0c\u6765\u4e86\u5c31\u8d76\u4e0d\u8d70\u4e86\u3002 \u3000\u3000\u674e\u601d\u5357\u60f3\u9053\uff1a\u201c\u5c60\u51e4\u548c\u5b5f\u660e\u971e\u4e0d\u77e5\u5df2\u7ecf\u56de\u5230\u5979\u4eec\u7684\u5c71\u5be8\u6ca1\u6709\uff1f\u8499\u53e4\u5927\u519b\u5357\u4fb5\uff0c\u91d1\u56fd\u7684\u5b98 \u5175\u662f\u4e00\u5b9a\u62b5\u6321\u4e0d\u4f4f\u7684\u3002\u80fd\u591f\u501a\u9760\u7684\u53ea\u6709\u4e49\u519b\u3002\u5c60\u767e\u57ce\u751f\u524d\u662f\u7eff\u6797\u4e4b\u96c4\uff0c\u5c31\u4e0d\u77e5\u5728\u4ed6\u6b7b\u540e\uff0c\u90a3 \u4e9b\u4e09\u5c71\u4e94\u5cb3\u7684\u597d\u6c49\uff0c\u80af\u4e0d\u80af\u542c\u5c60\u51e4\u7684\u53f7\u4ee4\uff1f\u6211\u8be5\u4e0d\u8be5\u53bb\u770b\u770b\u5979\u4eec\uff0c\u5e2e\u5e2e\u5979\u4eec\u7684\u5fd9\u5462\uff1f\u201d \u3000\u3000\u60f3\u81f3\u6b64\u5904\uff0c\u674e\u601d\u5357\u5ffd\u5730\u6697\u6697\u5403\u4e86\u4e00\u60ca\uff0c\u597d\u50cf\u7a81\u7136\u53d1\u73b0\u4e86\u81ea\u5df1\u5fc3\u5e95\u7684\u79d8\u5bc6\uff0c\u201c\u4e3a\u4ec0\u4e48\u6211\u603b \u662f\u5fd8\u4e0d\u4e86\u5b5f\u660e\u971e\uff1f\u201d\u53d1\u73b0\u4e86\u8fd9\u4e2a\u79d8\u5bc6\uff0c\u4e0d\u7531\u5f97\u6ee1\u9762\u901a\u7ea2\uff0c\u81ea\u5df1\u8d23\u5907\u81ea\u5df1\uff1a\u201c\u674e\u601d\u5357\u5440\u674e\u601d \u5357\uff0c\u4f60\u600e\u80fd\u5982\u6b64\u8584\u60c5\uff1f\u4f60\u548c\u6768\u5a49\u66fe\u7ecf\u505a\u8fc7\u76f8\u4f9d\u4e3a\u547d\u7684\u9e33\u9e2f\uff0c\u5373\u4f7f\u5979\u5ac1\u4e86\u522b\u4eba\uff0c\u5979\u5728\u4f60\u5fc3\u4e2d\u7684 \u4f4d\u7f6e\u4e5f\u7edd\u4e0d\u662f\u522b\u4e2a\u5c11\u5973\u6240\u80fd\u4ee3\u66ff\u7684\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u4e00\u53e3\u628a\u74f6\u4e2d\u7684\u4f59\u9152\u559d\u5b8c\uff0c\u773c\u524d\u53c8\u4eff\u4f5b\u6447\u6643\u7740\u6768\u5a49\u695a\u695a\u53ef\u601c\u7684\u5f71\u5b50\u3002\u674e\u601d\u5357\u968f\u5373\u53c8 \u60f3\uff1a\u201c\u5b81\u6559\u5a49\u59b9\u8d1f\u6211\uff0c\u6211\u51b3\u4e0d\u53ef\u8d1f\u4e86\u5a49\u59b9\u3002\u4f46\u6211\u82e5\u56e0\u6b64\u4e0d\u6562\u53bb\u89c1\u5b5f\u660e\u971e\uff0c\u53ea\u6015\u4e5f\u8fd8\u662f\u4e2a\u5fc3 \u75c5\uff01\u5fc3\u4e2d\u5018\u65e0\u6742\u5ff5\uff0c\u53c8\u4f55\u8981\u6015\u89c1\u5979\u5462\uff1f\u548c\u5979\u4eec\u8054\u624b\u5fa1\u654c\uff0c\u8fd9\u662f\u4e00\u4ef6\u5927\u4e8b\uff0c\u5e94\u8be5\u505a\u7684\u3002\u53ea\u8981\u4f60 \u4e0d\u628a\u5b83\u5f53\u4f5c\u4e00\u4e2a\u85c9\u53e3\uff0c\u5c31\u662f\u4e0e\u5b5f\u660e\u971e\u671d\u5915\u76f8\u5904\uff0c\u90a3\u53c8\u4f55\u59a8\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u6b63\u81ea\u5728\u5fc3\u4e2d\u53cd\u590d\u8bba\u8fa9\uff0c\u601d\u5982\u4e71\u9ebb\uff0c\u5ffd\u5730\u9690\u9690\u542c\u5f97\u4f3c\u4e4e\u6709\u5200\u5251\u78b0\u51fb\u7684\u58f0\u97f3\u8fdc\u8fdc\u4f20 \u6765\u3002\u674e\u601d\u5357\u5403\u4e86\u4e00\u60ca\uff0c\u9152\u610f\u9192\u4e86\u51e0\u5206\u3002\u5c31\u5728\u6b64\u65f6\uff0c\u4e00\u58f0\u6e05\u8106\u7684\u5578\u58f0\u5438\u4ece\u8fdc\u5904\u4f20\u6765\u4e86\uff0c\u542c\u8fd9\u5578 \u58f0\u597d\u50cf\u662f\u51fa\u4e8e\u5c11\u5973\u4e4b\u53e3\u3002 \u3000\u3000\u597d\u5728\u8fd9\u5578\u58f0\u6765\u5f97\u53ca\u65f6\uff0c\u5c31\u5728\u674e\u601d\u5357\u5403\u60ca\u8df3\u8d77\u7684\u65f6\u5019\uff0c\u4e00\u652f\u98de\u9556\u7a81\u7136\u4ece\u7a97\u53e3\u5c04\u5165\u3002\u674e\u601d\u5357 \u628a\u9152\u74f6\u4e00\u6454\uff0c\u53ee\u94db\u4e00\u58f0\uff0c\u9152\u74f6\u5f53\u7136\u662f\u788e\u6210\u7247\u7247\uff0c\u4f46\u90a3\u652f\u98de\u9556\u5374\u4e5f\u7ed9\u4ed6\u6253\u843d\u4e86\u3002 \u3000\u3000\u6b8a\u4e0d\u77e5\u674e\u601d\u5357\u56fa\u7136\u5403\u60ca\uff0c\u5411\u4ed6\u5077\u88ad\u7684\u90a3\u4e2a\u4eba\u6bd4\u4ed6\u5403\u60ca\u66f4\u751a\u3002\u9152\u74f6\u4e43\u662f\u6613\u788e\u7684\u74f7\u5668\uff0c\u674e\u601d \u5357\u7528\u4e00\u4e2a\u9152\u74f6\u800c\u80fd\u6253\u843d\u4ed6\u7684\u94c1\u5236\u7684\u98de\u9556\uff0c\u529f\u529b\u663e\u7136\u662f\u5728\u4ed6\u4e4b\u4e0a\u3002\u90a3\u4eba\u5fc3\u91cc\u6697\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u5927 \u6c57\u751f\u524d\u90a3\u6837\u8d4f\u8bc6\u4ed6\uff0c\u8fd9\u5c0f\u5b50\u7684\u672c\u9886\u53ea\u6015\u8fd8\u5728\u6211\u4eec\u7684\u8bb8\u591a\u91d1\u5e10\u6b66\u58eb\u4e4b\u4e0a\u3002\u201d \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u674e\u601d\u5357\u5df2\u662f\u4e00\u4e2a\u201c\u71d5\u5b50\u7a7f\u5e18\u201d\u5f0f\u4ece\u7a97\u53e3\u5c31\u8df3\u51fa\u53bb\uff0c\u5927\u6012\u559d\u9053\uff1a\u201c\u4f60\u662f \u8c01\uff0c\u4e3a\u4ec0\u4e48\u534a\u591c\u4e09\u66f4\u524d\u6765\u6697\u7b97\u3002\u201d \u3000\u3000\u90a3\u4eba\u53cd\u624b\u4e00\u5200\uff0c\u67b6\u5f00\u674e\u601d\u5357\u7684\u957f\u5251\uff0c\u559d\u9053\uff1a\u201c\u5927\u6c57\u5f85\u4f60\u4e0d\u8584\uff0c\u4f60\u4e3a\u4ec0\u4e48\u79c1\u9003\uff1f\u201d \u3000\u3000\u6708\u5149\u4e0b\u674e\u601d\u5357\u4ed4\u7ec6\u770b\u4e86\u90a3\u4e2a\u4eba\u7684\u76f8\u8c8c\uff0c\u4f9d\u7136\u8ba4\u5f97\u662f\u90a3\u6b21\u5728\u80af\u7279\u5c71\u72e9\u730e\u4e4b\u65f6\uff0c\u8ddf\u968f\u9547\u56fd\u738b \u5b50\u7684\u4e00\u4e2a\u968f\u4ece\u6b66\u58eb\u3002 \u3000\u3000\u674e\u601d\u5357\u6012\u9053\uff1a\u201c\u597d\u5440\uff0c\u6211\u9003\u56de\u81ea\u5df1\u7684\u5bb6\u91cc\uff0c\u4f60\u4eec\u8fd8\u4e0d\u80af\u653e\u8fc7\u6211\u4e48\uff1f\u54fc\uff0c\u8fd9\u662f\u6c49\u4eba\u7684\u5730 \u65b9\uff0c\u53ef\u7531\u4e0d\u5f97\u4f60\u884c\u51f6\u4e86\uff01\u201d \u3000\u3000\u90a3\u4eba\u89e3\u4e86\u674e\u601d\u5357\u7684\u51e0\u62db\uff0c\u5df2\u662f\u4f7f\u51fa\u6d51\u8eab\u672c\u9886\uff0c\u5403\u529b\u975e\u5e38\uff0c\u660e\u77e5\u4e0d\u654c\uff0c\u5ffd\u5730\u628a\u624b\u4e00\u626c\uff0c\u53c8 \u4e00\u679a\u6697\u5668\u6253\u51fa\u3002\u674e\u601d\u5357\u4fa7\u8eab\u4e00\u95ea\uff0c\u53ea\u542c\u5f97\u201c\u8f70\u201d\u7684\u4e00\u58f0\uff0c\u6697\u5668\u70b8\u5f00\uff0c\u767b\u65f6\u70e7\u8d77\u4e86\u4e00\u4e2a\u706b\u5934\u3002 \u539f\u6765\u5728\u6210\u5409\u601d\u6c57\u7684\u65f6\u4ee3\uff0c\u8499\u53e4\u4eba\u5df2\u5f00\u59cb\u61c2\u5f97\u4f7f\u7528\u706b\u836f\u3002\u8fd9\u662f\u8499\u53e4\u519b\u4e2d\u7279\u6709\u7684\u4e00\u79cd\u706b\u836f\u6697\u5668\uff0c \u597d\u5728\u53ea\u662f\u521d\u5177\u96cf\u5f62\u7684\u706b\u5668\uff0c\u5a01\u529b\u8fd8\u4e0d\u662f\u5341\u5206\u60ca\u4eba\u3002 \u3000\u3000\u90a3\u4eba\u81ea\u77e5\u4e0d\u654c\uff0c\u6697\u5668\u6253\u4e0d\u4e2d\u674e\u601d\u5357\uff0c\u8f6c\u8eab\u4fbf\u9003\u3002\u674e\u601d\u5357\u987e\u4e0d\u5f97\u6551\u706b\uff0c\u5148\u8ffd\u654c\u4eba\u3002\u51e0\u4e2a\u8d77 \u4f0f\uff0c\u8ffd\u5230\u90a3\u4eba\u8eab\u540e\uff0c\u559d\u9053\uff1a\u201c\u6765\u800c\u4e0d\u5f80\u975e\u793c\u4e5f\uff0c\u4f60\u4e5f\u63a5\u63a5\u6211\u7684\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u53cc\u6307\u4e00\u5f39\uff0c\u94ee\u94ee\u4e24\u58f0\uff0c\u628a\u4e24\u679a\u94dc\u94b1\u5f53\u4f5c\u6697\u5668\u98de\u51fa\u3002\u8499\u53e4\u6b66\u58eb\u53cd\u5200\u4e00\u62e8\uff0c\u6253\u843d\u4e86\u4e00 \u679a\u201c\u94b1\u9556\u201d\uff0c\u7b2c\u4e8c\u679a\u201c\u94b1\u9556\u201d\u5374\u8eb2\u4e0d\u5f00\uff0c\u6b63\u4e2d\u4ed6\u7684\u201c\u75c5\u9631\u7a74\u201d\u3002\u8499\u53e4\u6b66\u58eb\u201c\u554a\u5440\u201d\u4e00\u58f0\uff0c\u535c \u901a\u8dcc\u5012\u3002 \u3000\u3000\u674e\u601d\u5357\u6b63\u8981\u4e0a\u53bb\u64d2\u4ed6\uff0c\u5ffd\u542c\u5f97\u90a3\u6e05\u8106\u7684\u5578\u58f0\u53c8\u4f20\u4e86\u5230\u6765\uff0c\u5578\u58f0\u6bd4\u5148\u524d\u8f83\u5f31\uff0c\u663e\u7136\u5df2\u662f\u4e2d \u6c14\u4e0d\u8db3\u3002\u674e\u601d\u5357\u5403\u4e86\u4e00\u60ca\uff1a\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u58f0\u97f3\u4f3c\u662f\u4e00\u4e2a\u5973\u5b50\uff0c\u5979\u662f\u8c01\u5462\uff1f\u6551\u4eba\u8981\u7d27\uff0c\u8fd9\u53ae \u5df2\u7ecf\u7ed9\u6211\u70b9\u4e86\u7a74\u9053\uff0c\u56de\u6765\u518d\u6162\u6162\u5ba1\u95ee\u4ed6\u4e5f\u8fd8\u4e0d\u8fdf\u3002\u201d \u3000\u3000\u5f53\u4e0b\u674e\u601d\u5357\u5c55\u5f00\u4e86\u201c\u516b\u6b65\u8d76\u8749\u201d\u7684\u8f7b\u529f\uff0c\u5411\u58f0\u97f3\u6765\u5904\u75be\u5954\u800c\u53bb\u3002\u5230\u4e86\u6751\u8fb9\uff0c\u53ea\u89c1\u4e00\u4e2a\u7ea2 \u8863\u5973\u5b50\u4e0e\u4e00\u4e2a\u4f7f\u5355\u5200\u7684\u6c49\u5b50\u6b63\u5728\u6253\u5f97\u5341\u5206\u6fc0\u70c8\u3002 \u3000\u3000\u6708\u5149\u4e0b\u770b\u5f97\u5206\u660e\uff0c\u8fd9\u7ea2\u8863\u5973\u5b50\u4e0d\u662f\u522b\u4eba\uff0c\u6b63\u662f\u5b5f\u660e\u971e\u3002\u8fd9\u5239\u90a3\u95f4\uff0c\u674e\u601d\u5357\u5f53\u771f\u662f\u53c8\u60ca\u53c8 \u559c\uff0c\u51e0\u4e4e\u5446\u4e86\u3002\u4ed6\u521a\u521a\u8fd8\u5728\u5de6\u601d\u53f3\u60f3\uff0c\u8981\u4e0d\u8981\u53bb\u627e\u5b5f\u660e\u971e\u3002\u5c82\u77e5\u4e0d\u5fc5\u4ed6\u53bb\u5bfb\u627e\uff0c\u5982\u4eca\u5b5f\u660e\u971e \u5c31\u51fa\u73b0\u5728\u4ed6\u7684\u773c\u524d\u4e86\u3002 \u3000\u3000\u548c\u5b5f\u660e\u971e\u4ea4\u624b\u7684\u90a3\u4e2a\u4e2d\u5e74\u6c49\u5b50\uff0c\u5200\u6cd5\u6781\u51f6\uff0c\u8fdc\u8fdc\u770b\u53bb\uff0c\u5c31\u50cf\u4e00\u56e2\u767d\u5149\u88f9\u7740\u4e00\u7247\u7ea2\u971e\u3002\u5b5f \u660e\u971e\u8863\u8882\u98d8\u98d8\uff0c\u5de6\u51b2\u53f3\u7a81\uff0c\u603b\u662f\u7a81\u4e0d\u7834\u90a3\u56e2\u767d\u5149\u7684\u7b3c\u7f69\u3002 \u3000\u3000\u5b5f\u660e\u971e\u6b64\u65f6\u4ea6\u5df2\u770b\u89c1\u4e86\u674e\u601d\u5357\uff0c\u5927\u559c\u53eb\u9053\uff1a\u201c\u601d\u5357\uff0c\u4f60\u56de\u6765\u4e86\uff1f\u8fd9\u53ae\u662f\u9633\u5929\u96f7\u7684\u4f84\u513f\uff0c \u4ed6\u4eec\u53d4\u4f84\u4e8c\u4eba\uff0c\u90fd\u662f\u79c1\u901a\u8499\u53e4\u7684\u5978\u8d3c\uff01\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u90a3\u6c49\u5b50\u731b\u7684\u4e00\u5200\u5288\u53bb\uff0c\u5b5f\u660e\u971e\u8fd8\u4e86\u4e00\u62db \u201c\u62bd\u64a4\u8fde\u73af\u201d\uff0c\u6302\u4e24\u80a9\uff0c\u523a\u5c0f\u8179\uff0c\u5251\u52bf\u4e5f\u662f\u6781\u4e3a\u51cc\u5389\u3002 \u3000\u3000\u5c82\u77e5\u90a3\u6c49\u5b50\u4e43\u662f\u62db\u91cc\u85cf\u62db\uff0c\u5f0f\u4e2d\u5957\u5f0f\uff0c\u9661\u7136\u95f4\u53cd\u624b\u4e00\u638c\uff0c\u5200\u4e2d\u5957\u638c\uff0c\u6307\u4e1c\u6253\u897f\uff0c\u6307\u5357\u6253 \u5317\uff0c\u5927\u559d\u4e00\u58f0\u201c\u5012\uff01\u201d\u90a3\u4e00\u638c\u5954\u96f7\u9a87\u7535\u822c\u7684\u5c31\u6253\u5230\u4e86\u5b5f\u660e\u971e\u7684\u80f8\u524d\u3002\u5b5f\u660e\u971e\u7684\u9752\u94dc\u5251\u7ed9\u4ed6\u7684 \u6734\u5200\u903c\u4f4f\uff0c\u773c\u770b\u8fd9\u4e00\u638c\u96be\u4ee5\u95ea\u5f00\u3002 \u3000\u3000\u597d\u5728\u4e09\u65b9\u9762\u52a8\u4f5c\u90fd\u5feb\uff0c\u5c31\u5728\u90a3\u6c49\u5b50\u7684\u4e00\u638c\u582a\u582a\u8981\u6253\u5230\u5b5f\u660e\u971e\u80f8\u524d\u7684\u65f6\u5019\uff0c\u674e\u601d\u5357\u8eab\u5f62\u4e00 \u63a0\uff0c\u8fde\u4eba\u5e26\u5251\u5316\u4f5c\u4e86\u4e00\u9053\u94f6\u5149\uff0c\u4e00\u62db\u201c\u767d\u8679\u8d2f\u65e5\u201d\uff0c\u5251\u950b\u4ea6\u5df2\u523a\u5230\u4e86\u90a3\u6c49\u5b50\u7684\u540e\u5fc3\u3002 \u3000\u3000\u8fd9\u6c49\u5b50\u662f\u5927\u9b54\u5934\u9633\u5929\u96f7\u7684\u4f84\u513f\uff0c\u540d\u5524\u9633\u575a\u767d\u3002\u9633\u5929\u96f7\u662f\u90aa\u6d3e\u4e2d\u6570\u4e00\u6570\u4e8c\u7684\u9ad8\u624b\uff0c\u4ed6\u6ca1\u6709 \u513f\u5b50\uff0c\u628a\u8fd9\u4e2a\u4f84\u513f\u89c6\u540c\u5df2\u51fa\u3002\u9633\u575a\u767d\u81ea\u5e7c\u8ddf\u4ed6\uff0c\u5df2\u5f97\u4e86\u5176\u53d4\u7684\u8863\u94b5\u771f\u4f20\uff0c\u6b66\u529f\u59d4\u5b9e\u4e0d\u5f31\uff0c\u6b64 \u65f6\u542c\u5f97\u80cc\u540e\u91d1\u5203\u5288\u98ce\u4e4b\u58f0\uff0c\u8fde\u5fd9\u4e00\u4e2a\u65cb\u8eab\uff0c\u5200\u950b\u5de6\u63a0\uff0c\u67b6\u5f00\u4e86\u674e\u601d\u5357\u7684\u957f\u5251\uff0c\u8fd9\u4e00\u62db\u6551\u5f97\u9669 \u6781\uff0c\u4f46\u4e5f\u6070\u5230\u597d\u5904\u3002\u674e\u601d\u5357\u4e5f\u4e0d\u7981\u6697\u6697\u559d\u4e86\u4e2a\u5f69\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u5e08\u7236\u628a\u9633\u5929\u96f7\u89c6\u4e3a\u5e73 \u751f\u7684\u52b2\u654c\uff0c\u8fde\u4ed6\u7684\u4f84\u513f\u7adf\u4e5f\u8fd9\u822c\u4e86\u5f97\uff01\u201d \u3000\u3000\u5b5f\u660e\u971e\u8f7b\u529f\u8d85\u5353\uff0c\u9633\u575a\u767d\u56e0\u4e3a\u8981\u817e\u51fa\u4e00\u53ea\u624b\u6765\u62db\u67b6\u674e\u601d\u5357\uff0c\u5de6\u638c\u867d\u7136\u4ecd\u5411\u5b5f\u660e\u971e\u6253\u53bb\uff0c \u4f46\u53bb\u52bf\u5df2\u662f\u7565\u7f13\u3002\u5b5f\u660e\u971e\u8eab\u5f62\u4e00\u98d8\u4e00\u95ea\uff0c\u5df2\u662f\u7a9c\u8fc7\u4e00\u8fb9\uff0c\u7d27\u8ddf\u7740\u201c\u5530\u201d\u7684\u4e00\u5251\uff0c\u5c31\u6307\u5230\u4e86\u9633 \u575a\u767d\u80c1\u4e0b\u7684\u201c\u9b42\u95e8\u7a74\u201d\u3002 \u3000\u3000\u9633\u575a\u767d\u638c\u6cd5\u6025\u53d8\uff0c\u4f7f\u51fa\u201c\u7a7a\u624b\u5165\u767d\u5203\u201d\u7684\u529f\u592b\uff0c\u4e94\u6307\u5982\u94a9\uff0c\u53cd\u6263\u5b5f\u660e\u971e\u7684\u624b\u8155\u3002\u5b5f\u660e\u971e \u5251\u62db\u5947\u5feb\uff0c\u5251\u67c4\u4e00\u6c89\uff0c\u5251\u950b\u7565\u504f\uff0c\u201c\u55e4\u201d\u7684\u4e00\u58f0\u8f7b\u54cd\uff0c\u9633\u575a\u767d\u7684\u8863\u895f\u5df2\u7ed9\u5251\u950b\u6233\u7834\u4e00\u6d1e\u3002\u8fd9 \u8fd8\u5e78\u4e8f\u662f\u5b5f\u660e\u971e\u8981\u907f\u4ed6\u7684\u53cd\u624b\u64d2\u62ff\uff0c\u6545\u800c\u4e0d\u80fd\u4e0d\u5251\u950b\u7565\u504f\uff0c\u5426\u5219\u82e5\u662f\u523a\u4e2a\u6b63\u7740\u7684\u8bdd\uff0c\u4ed6\u7684\u5c0f \u8179\u4e5f\u8981\u5f00\u4e86\u4e2a\u6d1e\u3002 \u3000\u3000\u9633\u575a\u767d\u5413\u51fa\u4e00\u8eab\u51b7\u6c57\uff0c\u5927\u6012\u559d\u9053\uff1a\u201c\u597d\uff0c\u4f60\u4eec\u4e24\u4e2a\u5c31\u5e76\u80a9\u5b50\u4e0a\u5427\u3002\u674e\u601d\u5357\uff0c\u6211\u82e5\u6015\u4f60\uff0c \u6211\u4e5f\u4e0d\u6765\u4e86\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u542c\u5f97\u201c\u5e76\u80a9\u5b50\u201d\u4e09\u5b57\uff0c\u751a\u611f\u523a\u8033\uff0c\u4e0d\u7531\u5f97\u9762\u4e0a\u4e00\u7ea2\u3002\u4f46\u4ed6\u4e5f\u6ca1\u6709\u8e0c\u8e87\uff0c\u7acb\u5373\u53c8\u662f \u4e00\u5251\u523a\u51fa\uff0c\u559d\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5bf9\u4ed8\u4f60\u8fd9\u7b49\u52fe\u7ed3\u9791\u5b50\u7684\u5978\u8d3c\uff0c\u4f55\u987b\u8bb2\u7a76\u4ec0\u4e48\u6c5f\u6e56\u89c4\u77e9\uff1f\u4f60\u6015\u4e5f \u597d\uff0c\u4e0d\u6015\u4e5f\u597d\uff0c\u6211\u7684\u5b9d\u5251\u5c31\u53ea\u77e5\u9053\u8981\u65a9\u6076\u9664\u5978\uff01\u201d \u3000\u3000\u9633\u575a\u767d\u51b7\u7b11\u9053\uff1a\u201c\u51ed\u4f60\u8fd9\u70b9\u5fae\u672a\u4e4b\u6280\uff0c\u4e5f\u60f3\u6740\u6211\uff1f\u201d\u8bdd\u867d\u5982\u6b64\uff0c\u4ed6\u5bf9\u674e\u601d\u5357\u8fd9\u52b2\u9053\u5341\u8db3 \u7684\u5251\u62db\uff0c\u53ef\u662f\u4e0d\u80fd\u4e0d\u5c0f\u5fc3\u5e94\u4ed8\u3002 \u3000\u3000\u674e\u601d\u5357\u7684\u8fbe\u6469\u5251\u6cd5\u662f\u5c11\u6797\u6d3e\u7684\u6b63\u5b97\uff0c\u62db\u6570\u6216\u8005\u4e0d\u5982\u5b5f\u660e\u971e\u7684\u5947\u8be1\uff0c\u4f46\u653b\u5b88\u54b8\u76f4\uff0c\u6cd5\u5ea6\u8c2c \u4e25\uff0c\u5374\u662f\u6beb\u65e0\u7834\u7efd\u53ef\u5bfb\uff0c\u800c\u4e14\u4ed6\u7684\u529f\u529b\u4e5f\u5728\u5b5f\u660e\u971e\u4e4b\u4e0a\uff0c\u6545\u6b64\u9633\u575a\u767d\u5bf9\u4ed8\u5b5f\u660e\u971e\u53ef\u4ee5\u7a33\u5360\u4e0a \u98ce\uff0c\u5bf9\u4ed8\u674e\u601d\u5357\u53ef\u662f\u96be\u64cd\u80dc\u7b97\u3002\u674e\u3001\u5b5f\u4e24\u4eba\u8054\u624b\uff0c\u4ed6\u5f53\u7136\u662f\u66f4\u96be\u62db\u67b6\u4e86\u3002 \u3000\u3000\u5341\u51e0\u62db\u8fc7\u540e\uff0c\u9633\u575a\u767d\u6697\u6697\u53eb\u82e6\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6211\u82e5\u4e0d\u5192\u9669\uff0c\u640f\u4e00\u640f\uff0c\u53ea\u6015\u96be\u9003\u6027\u547d\uff01\u201d \u6fc0\u6218\u4e2d\u6545\u4f0e\u91cd\u65bd\uff0c\u84e6\u5730\u53c8\u662f\u4e00\u62db\u201c\u5200\u4e2d\u5939\u638c\u201d\uff0c\u5411\u674e\u601d\u5357\u5288\u4e0b\u3002 \u3000\u3000\u5b5f\u660e\u971e\u53eb\u9053\uff1a\u201c\u5c0f\u5fc3\uff0c\u4ed6\u7ec3\u7684\u662f\u94c1\u7802\u638c\uff01\u201d\u674e\u601d\u5357\u9053\uff1a\u201c\u65e0\u59a8\uff01\u201d\u4ee5\u638c\u5bf9\u638c\uff0c\u7830\u7684\u4e00 \u58f0\uff0c\u53cc\u638c\u76f8\u4ea4\uff0c\u9633\u575a\u81ea\u8eab\u5f62\u4e00\u6643\uff0c\u5012\u7eb5\u51fa\u4e09\u4e08\u5f00\u5916\uff0c\u5b5f\u660e\u971e\u4e00\u5251\u523a\u53bb\uff0c\u5728\u4ed6\u80a9\u5934\u5212\u5f00\u4e86\u4e00\u9053 \u4f24\u53e3\u3002\u9633\u575a\u767d\u4f3c\u8d1f\u4f24\u7684\u91ce\u517d\u4e00\u6837\uff0c\u72c2\u5578\u800c\u9003\u3002 \u3000\u3000\u5b5f\u660e\u971e\u6b63\u8981\u53bb\u8ffd\uff0c\u5ffd\u89c1\u674e\u601d\u5357\u505c\u5728\u539f\u5730\uff0c\u53cc\u7709\u7d27\u76b1\uff0c\u5e76\u65e0\u8ffd\u654c\u4e4b\u610f\uff0c\u4e0d\u89c9\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4 \u9053\uff1a\u201c\u674e\u5927\u54e5\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u8fd0\u6c14\u4e09\u8f6c\uff0c\u6d88\u4e86\u80f8\u4e2d\u7684\u4e00\u80a1\u70e6\u95f7\u4e4b\u611f\uff0c\u8bf4\u9053\uff1a\u201c\u6ca1\u4e8b\u4e86\u3002\u4f46\u8fd9\u53ae\u94c1\u7802\u638c\u7684\u5389\u5bb3\u5374 \u662f\u51fa\u4e4e\u6211\u610f\u6599\u4e4b\u5916\u3002\u201d\u4f38\u624b\u7ed9\u5b5f\u660e\u971e\u4e00\u770b\uff0c\u53ea\u89c1\u638c\u5fc3\u7ea2\u80bf\u4e86\u4e00\u5757\uff0c\u5c31\u4f3c\u7ed9\u70e7\u70ed\u7684\u94c1\u68d2\u70d9\u8fc7\u4e00 \u80a1\uff0c\u5b5f\u660e\u971e\u770b\u4e86\uff0c\u4e5f\u662f\u4e0d\u7981\u4e3a\u4e4b\u9a87\u7136\u3002 \u3000\u3000\u539f\u6765\u9633\u575a\u767d\u7684\u529f\u529b\u5e76\u4e0d\u5728\u674e\u601d\u5357\u4e4b\u4e0b\uff0c\u4f46\u56e0\u4ed6\u8981\u63d0\u9632\u5b5f\u660e\u971e\u7684\u4fa7\u88ad\uff0c\u4e0d\u80fd\u5168\u529b\u65bd\u4e3a\uff0c\u8fd9 \u624d\u7ed9\u674e\u601d\u5357\u4e00\u638c\u9707\u8dd1\u7684\u3002 \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u5b5f\u59d1\u5a18\uff0c\u4f60\u600e\u4e48\u4f1a\u5230\u6211\u7684\u6751\u5b50\u91cc\u6765\u7684\uff1f\u8fd9\u53ae\u662f\u6070\u5de7\u7ed9\u4f60\u78b0\u4e0a\u7684\uff0c\u8fd8\u662f\u4f60\u9884 \u5148\u77e5\u9053\u4e86\u4ed6\u4eec\u7684\u56fe\u8c0b\uff1f\u201d \u3000\u3000\u5b5f\u660e\u971e\u7b11\u9053\uff1a\u201c\u6211\u6b63\u662f\u6765\u627e\u5bfb\u4f60\u7684\u554a\uff01\u4eca\u665a\u4e4b\u4e8b\uff0c\u8bf4\u662f\u78b0\u5de7\u78b0\u4e0a\u4e5f\u53ef\uff0c\u8bf4\u662f\u9884\u5148\u77e5\u9053\u4e5f \u53ef\uff0c\u8bf4\u6765\u8bdd\u957f\uff0c\u5f85\u4f1a\u513f\u54b1\u4eec\u6162\u6162\u518d\u8bf4\u3002\u8fd9\u59d3\u9633\u7684\u8fd8\u6709\u4e00\u4e2a\u540c\u515a\uff0c\u7ed9\u4f60\u6253\u53d1\u4e86\u6ca1\u6709\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u90a3\u4e2a\u8499\u53e4\u6b66\u58eb\u7ed9\u6211\u70b9\u4e86\u7a74\u9053\uff0c\u73b0\u5728\u54b1\u4eec\u53ef\u4ee5\u56de\u53bb\u76d8\u95ee\u4ed6\u7684\u53e3\u4f9b\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u662f\u628a\u90a3\u4e2a\u8499\u5360\u6b66\u58eb\u85cf\u5728\u8def\u8fb9\u7684\u4e71\u9769\u4e1b\u4e2d\u7684\uff0c\u53ea\u9053\u4ed6\u8fd8\u8eba\u5728\u90a3\u51e0\uff0c\u4e0d\u6599\u8d70\u56de\u53bb\u4e00 \u770b\uff0c\u90a3\u4e2a\u8499\u53e4\u6b66\u571f\u5df2\u7ecf\u4e0d\u89c1\uff0c\u4e5f\u4e0d\u77e5\u4ed6\u662f\u81ea\u5df1\u89e3\u5f00\u4e86\u7a74\u9053\u9003\u8d70\u7684\u8fd8\u662f\u6709\u4eba\u5c06\u4ed6\u6551\u8d70\u7684\u3002 \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u54a6\uff0c\u6751\u5b50\u91cc\u6709\u706b\u5149\uff0c\u662f\u4e0d\u662f\u4f60\u7684\u2014\u2014\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u5df2\u6709\u6751\u5b50\u91cc\u7684\u4eba\u5411\u4ed6\u4eec \u8dd1\u6765\uff0c\u53eb\u9053\uff1a\u201c\u674e\u76f8\u516c\uff0c\u4f60\u8fd8\u4e0d\u8d76\u5feb\u56de\u53bb\uff0c\u4f60\u7684\u5c4b\u5b50\u7740\u706b\u4e86\uff01\u201d\u539f\u6765\u90a3\u4e2a\u8499\u53e4\u6b66\u58eb\u6240\u53d1\u7684\u4e00 \u679a\u786b\u78fa\u5f39\u5df2\u7ecf\u9020\u6210\u4e86\u706b\u707e\u3002 \u3000\u3000\u5e78\u597d\u674e\u601d\u5357\u5df2\u5c06\u5bb6\u4e2d\u7684\u5bb6\u79c1\u6742\u7269\u5206\u7ed9\u6751\u4eba\uff0c\u5bb6\u4e2d\u56db\u58c1\u8427\u6761\uff0c\u53ef\u71c3\u4e4b\u7269\u4e0d\u591a\uff0c\u867d\u7136\u8d77\u706b\uff0c \u706b\u52bf\u5e76\u4e0d\u65fa\u76db\u3002\u5f85\u5230\u674e\u601d\u5357\u56de\u5230\u5bb6\u95e8\u7684\u65f6\u5019\uff0c\u5de6\u90bb\u53f3\u91cc\u65e9\u5df2\u5e2e\u5fd9\u4ed6\u628a\u706b\u5934\u6251\u706d\u4e86\u3002 \u3000\u3000\u5f20\u5927\u53d4\u8bf4\u9053\uff1a\u201c\u963f\u5f25\u9640\u4f5b\uff0c\u6211\u8fd8\u6015\u4f60\u5728\u68a6\u4e2d\u7740\u4e86\u706b\u4e5f\u4e0d\u77e5\u9053\u5462\uff0c\u5e78\u4e8f\u4f60\u65e9\u5df2\u9003\u4e86\u51fa\u6765\u3002 \u8fd9\u706b\u662f\u600e\u4e48\u8d77\u7684\uff1f\u8fd9\u4f4d\u59d1\u5a18\u53c8\u662f\u8c01\u4eba\uff1f\u201d\u6751\u5b50\u91cc\u7684\u4eba\u89c1\u674e\u601d\u5357\u4e0e\u4e00\u4e2a\u964c\u751f\u7684\u5c11\u5973\u4e0a\u540c\u56de\u6765\uff0c \u90fd\u5f88\u8be7\u5f02\u3002 \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u6709\u4e2a\u8499\u53e4\u9791\u5b50\u6765\u627e\u6211\u7684\u9ebb\u70e6\uff0c\u8fd9\u628a\u706b\u5c31\u662f\u90a3\u9791\u5b50\u653e\u7684\u3002\u8fd9\u4f4d\u5b5f\u59d1\u5a18\u662f\u6211\u7684 \u670b\u53cb\uff0c\u5e78\u4e8f\u662f\u5979\u53d1\u73b0\u6709\u4eba\u8981\u6765\u5bb3\u6211\uff0c\u628a\u6211\u53eb\u4e86\u51fa\u6765\u3002\u53e6\u5916\u8fd8\u6709\u4e2a\u5f3a\u76d7\u4e5f\u662f\u5979\u5e2e\u5fd9\u6211\u6253\u8dd1\u4e86 \u7684\uff01\u201d \u3000\u3000\u6b64\u65f6\u5929\u5df2\u5927\u4eae\uff0c\u6765\u770b\u70ed\u95f9\u7684\u6751\u4eba\u8d8a\u6765\u8d8a\u591a\uff0c\u628a\u5b5f\u660e\u971e\u770b\u5f97\u5f88\u662f\u4e0d\u597d\u610f\u601d\u3002 \u3000\u3000\u8fd9\u4e9b\u4eba\u4e0d\u4f46\u662f\u50cf\u770b\u65b0\u5a18\u5b50\u4e00\u6837\u7684\u770b\u5b5f\u660e\u971e\uff0c\u800c\u4e14\u8fd8\u5728\u7a88\u7a88\u79c1\u8bae\uff1a\u201c\u60f3\u4e0d\u5230\u8fd9\u6837\u7f8e\u8c8c\u7684\u59d1 \u5a18\u6709\u8fd9\u6837\u5927\u7684\u672c\u9886\uff01\u201d\u201c\u4f60\u770b\u5979\u548c\u674e\u76f8\u516c\u7ad9\u5728\u4e00\u8d77\uff0c\u53ef\u4e0d\u6b63\u662f\u5929\u751f\u4e00\u5bf9\uff0c\u5730\u9053\u4e00\u53cc\u3002\u201d\u9976\u662f \u5b5f\u660e\u971e\u895f\u6000\u723d\u6717\uff0c\u542c\u4e86\u8fd9\u4e9b\u8bdd\uff0c\u4e5f\u4e0d\u7981\u7c89\u8138\u6cdb\u7ea2\u3002 \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u6211\u672c\u6765\u662f\u4eca\u65e9\u5c31\u8981\u8d70\u7684\uff0c\u591a\u8c22\u5404\u4f4d\u4e61\u90bb\u5e2e\u5fd9\u6551\u706b\uff0c\u90fd\u5728\u8fd9\u513f\uff0c\u6211\u5c31\u5728\u8fd9\u91cc \u5411\u5404\u4f4d\u4e00\u603b\u9053\u522b\u4e86\u3002\u201d\u5f53\u4e0b\u4f5c\u4e86\u4e00\u4e2a\u7f57\u5708\u63d6\u3002\u4fbf\u5373\u5e26\u4e86\u5b5f\u660e\u971e\u79bb\u5f00\u3002 \u3000\u3000\u5c31\u5728\u674e\u3001\u5b5f\u4e8c\u4eba\u4e0e\u4f17\u4eba\u9053\u522b\u7684\u65f6\u5019\uff0c\u5c71\u5761\u4e0a\u4e00\u68f5\u5927\u6811\u540e\u9762\u6709\u4e00\u4e2a\u4eba\u4e5f\u6b63\u5728\u6084\u6084\u79bb\u5f00\u3002\u674e \u601d\u5357\u6263\u5b5f\u660e\u971e\u90fd\u6ca1\u6709\u53d1\u89c9\u3002 \u3000\u3000\u4e24\u4eba\u51fa\u4e86\u6751\u5b50\uff0c\u624d\u6709\u4f59\u6687\u53d9\u8bdd\u3002\u674e\u601d\u5357\u95ee\u9053\uff1a\u201c\u4f60\u600e\u4e48\u4f1a\u6765\u627e\u6211\u7684\uff1f\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u5728\u897f\u590f\u8774\u8776\u8c37\u7684\u65f6\u5019\uff0c\u5c60\u51e4\u7684\u5e08\u54e5\u77f3\u749e\u66fe\u7ecf\u6765\u89c1\u8fc7\u6211\u4eec\u3002\u4ed6\u8bf4\u8d77\u4f60\u6765\uff0c\u6211 \u4eec\u624d\u77e5\u9053\u4f60\u7684\u906d\u9047\u3002\u55ef\uff0c\u674e\u5927\u54e5\uff0c\u4f60\u4e5f\u4e0d\u5fc5\u592a\u8fc7\u96be\u8fc7\uff0c\u6768\u59d1\u5a18\u803f\u70c8\u6210\u4ec1\uff0c\u867d\u6b7b\u72b9\u751f\u3002\u201d \u3000\u3000\u539f\u6765\u77f3\u749e\u5728\u8774\u8776\u8c37\u89c1\u5b5f\u660e\u971e\u7684\u65f6\u5019\uff0c\u5c1a\u672a\u77e5\u9053\u6768\u5a49\u5176\u5b9e\u672a\u6b7b\uff0c\u4ed6\u662f\u540e\u6765\u5728\u90a3\u4e2a\u8fb9\u5883\u7684\u5c0f \u9547\u518d\u6b21\u89c1\u5230\u6768\u5a49\uff0c\u624d\u77e5\u9053\u6768\u5a49\u6d3b\u5728\u4eba\u95f4\u7684\u3002\u5b5f\u660e\u971e\u548c\u77f3\u749e\u5728\u8774\u8776\u8c37\u5206\u624b\u4e4b\u540e\uff0c\u5c31\u6ca1\u6709\u518d\u89c1\u8fc7 \u4ed6\u4e86\u3002 \u3000\u3000\u5b5f\u660e\u971e\u4ee5\u4e3a\u6768\u5a49\u5df2\u6b7b\uff0c\u767e\u822c\u7684\u529d\u5c09\u674e\u601d\u5357\uff0c\u628a\u674e\u601d\u5357\u5f04\u5f97\u557c\u7b11\u7686\u975e\u3002\u5b5f\u660e\u971e\u89c1\u674e\u601d\u5357\u5e76 \u6ca1\u6389\u4e0b\u773c\u6cea\uff0c\u5012\u662f\u89c9\u5f97\u6709\u70b9\u5947\u602a\uff0c\u5fc3\u60f3\uff1a\u201c\u4ed6\u5012\u597d\u50cf\u5e76\u4e0d\u600e\u6837\u60b2\u4f24\uff0c\u96be\u9053\u4ed6\u548c\u6768\u5a49\u7684\u611f\u60c5\u5e76 \u4e0d\u5982\u6211\u60f3\u8c61\u7684\u90a3\u6837\u597d\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u5374\u5728\u6697\u81ea\u60f3\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6768\u5a49\u867d\u7136\u8fd8\u6d3b\u5728\u4eba\u95f4\uff0c\u4f46\u5979\u5df2\u4f5c\u4e86\u522b\u4eba\u7684\u59bb\u5b50\uff0c\u5728\u6211\u7684 \u5fc3\u4e2d\uff0c\u4e5f\u53ea\u80fd\u5f53\u5979\u6b7b\u4e86\u3002\u201d\u4e8e\u662f\u6de1\u6de1\u8bf4\u9053\uff01\u201d\u5b5f\u59d1\u5a18\uff0c\u591a\u8c22\u4f60\u7684\u5173\u5fc3\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u662f\u6015\u4f60\u56e0\u6b64\u96be\u8fc7\uff0c\u4f60\u770b\u5f97\u5f00\u5c31\u597d\u3002\u201d\u5fc3\u60f3\uff1a\u201c\u4eba\u5bb6\u8bf4\u7537\u513f \u591a\u8584\u5e78\uff0c\u8fd9\u8bdd\u5f53\u771f\u8bf4\u5f97\u4e0d\u9519\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u4e0d\u60f3\u591a\u8c08\u6768\u5a49\uff0c\u8f6c\u4e2a\u8bdd\u9898\u95ee\u9053\uff1a\u201c\u5b5f\u59d1\u5a18\uff0c\u4f60\u600e\u4e48\u4e0d\u5728\u5c60\u51e4\u7684\u5c71\u5be8\uff0c\u72ec\u81ea\u5230\u4e86\u8fd9 \u513f\uff1f\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u5c60\u51e4\u5df2\u7ecf\u77e5\u9053\u5979\u7684\u6740\u7236\u4ec7\u4eba\u4e43\u662f\u5927\u9b54\u5934\u9633\u5929\u96f7\uff0c\u8fd9\u4ec7\u6050\u6015\u5f88\u4e0d\u6613\u62a5\u7684\u3002\u4e3a \u4e86\u5e2e\u5979\u7684\u5fd9\uff0c\u6211\u4ece\u8499\u53e4\u56de\u6765\u4e4b\u540e\uff0c\u53c8\u9a6c\u4e0a\u8d76\u56de\u6c5f\u5357\u8001\u5bb6\uff0c\u8bf7\u6211\u7239\u7239\u51fa\u6765\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u4ee4\u5c0a\u6765\u4e86\u6ca1\u6709\uff1f\u201d \u3000\u3000\u5b5f\u660e\u971e\u503c\uff1a\u201c\u6c5f\u5357\u7684\u6b66\u6797\u670b\u53cb\u6b63\u5728\u4f1a\u5546\u5e94\u4ed8\u8499\u53e4\u5357\u4fb5\u4e4b\u4e8b\uff0c\u5bb6\u7236\u6050\u6015\u8981\u8fdf\u4e00\u4e9b\u65f6\u5019\u624d\u80fd \u6e21\u6c5f\u3002\u6709\u5173\u4f60\u7684\u4e8b\u60c5\uff0c\u6211\u5df2\u662f\u548c\u5bb6\u7236\u8bf4\u4e86\uff0c\u4ed6\u66fe\u7ecf\u8bef\u4f1a\u8fc7\u4f60\uff0c\u5982\u4eca\u77e5\u9053\u4e86\u771f\u76f8\uff0c\u5f88\u662f\u62b1 \u6b49\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u8fd9\u6ca1\u4ec0\u4e48\uff0c\u4f46\u5f97\u5b5f\u5927\u4fa0\u77e5\u9053\u4e86\u771f\u76f8\u5c31\u597d\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u8bf4\u9053\uff1a\u201c\u5c60\u51e4\u7684\u5c71\u5be8\u5728\u9634\u5e73\u53bf\u4e1c\u5357\u9762\u7684\u7405\u739b\u5c71\uff0c\u8ddd\u79bb\u6b66\u57ce\u4e0d\u8fc7\u662f\u56db\u4e94\u5929\u8def\u7a0b\u3002\u6211 \u4ece\u6c5f\u5357\u56de\u6765\uff0c\u8def\u7ecf\u6b66\u57ce\uff0c\u60f3\u8d77\u4e86\u4f60\u3002\u4e0d\u77e5\u4f60\u56de\u6765\u6ca1\u6709\uff0c\u662f\u4ee5\u7279\u6765\u63a2\u671b\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u591a\u8c22\u4f60\u7684\u60e6\u8bb0\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u7b11\u9053\uff1a\u201c\u8bf4\u5b9e\u8bdd\uff0c\u6211\u6765\u627e\u4f60\uff0c\u5e76\u975e\u53ea\u662f\u4e3a\u4e86\u60e6\u8bb0\u4f60\u800c\u6765\u63a2\u671b\u4f60\u7684\uff0c\u6211\u662f\u60f3\u627e\u4f60\u53bb \u5e2e\u5fd9\u5c60\u51e4\u3002\u6211\u7684\u7239\u7239\u4e00\u65f6\u4e0d\u80fd\u591f\u6765\uff0c\u5c71\u5be8\u91cc\u6b63\u9700\u8981\u6709\u672c\u9886\u7684\u4eba\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u8fd9\u662f\u6211\u5e94\u8be5\u505a\u7684\u4e8b\u3002\u4f60\u4e0d\u6765\u627e\u6211\uff0c\u6211\u4e5f\u4f1a\u53bb\u627e\u4f60\u4eec\u7684\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u6211\u5728\u6b66\u57ce\u6070\u5de7\u78b0\u89c1\u9633\u575a\u767d\u5411\u4eba\u6253\u542c\u4f60\u5bb6\u7684\u4f4f\u5740\uff0c\u6211\u5c31\u7559\u4e0a\u4e86\u795e\u3002\u4ed6 \u6253\u542c\u4e86\u5730\u5740\u4e4b\u540e\uff0c\u548c\u90a3\u4e2a\u9791\u5b50\u7528\u8499\u65b9\u8bdd\u5546\u8bae\u3002\u6211\u7684\u8499\u53e4\u8bdd\u8bb2\u5f97\u4e0d\u597d\uff0c\u542c\u5374\u662f\u5b8c\u5168\u542c\u5f97\u61c2\u7684\u3002 \u8fd9\u624d\u77e5\u9053\uff0c\u539f\u6765\u4ed6\u7684\u53d4\u7236\u65e9\u5df2\u79c1\u901a\u8499\u53e4\uff0c\u8fd9\u6b21\u8ba9\u4ed6\u966a\u8fd9\u9791\u5b50\u524d\u6765\uff0c\u4e00\u6765\u662f\u770b\u4f60\u5df2\u7ecf\u56de\u5bb6\u6ca1 \u6709\uff0c\u5018\u82e5\u4f60\u5df2\u56de\u5bb6\uff0c\u4ed6\u4eec\u5c31\u8981\u53d6\u4f60\u7684\u9996\u7ea7\u5e26\u56de\u53bb\u7ed9\u4f59\u4e00\u4e2d\u3002\u82e5\u662f\u4f60\u5c1a\u672a\u56de\u5bb6\uff0c\u4ed6\u4eec\u4e5f\u8981\u5230\u4f60 \u5bb6\u4e2d\u641c\u4e0a\u4e00\u641c\u3002\u542c\u4ed6\u4eec\u7684\u8c08\u8bdd\uff0c\u597d\u50cf\u4f60\u5bb6\u91cc\u6709\u4e00\u672c\u4ec0\u4e48\u5175\u4e66\uff0c\u8fd9\u672c\u5175\u4e66\u662f\u6210\u5409\u601d\u6c57\u90fd\u60f3\u8981 \u7684\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u604d\u7136\u5927\u609f\uff0c\u60f3\u9053\uff0c\u201c\u662f\u4e86\uff0c\u6211\u66fe\u7ecf\u9a97\u8fc7\u4f59\u4e00\u4e2d\uff0c\u8bf4\u8fd9\u672c\u5175\u4e66\u6211\u6ca1\u5e26\u51fa\u6765\uff0c\u602a\u4e0d\u5f97 \u4ed6\u8981\u6d3e\u4eba\u5230\u6211\u5bb6\u4e2d\u6765\u641c\u4e86\u3002\u53ea\u662f\u4e3a\u4e86\u6211\u7684\u7f18\u6545\uff0c\u4e5f\u8bb8\u4ed6\u4eec\u8fd8\u4e0d\u4f1a\u5982\u6b64\u90d1\u91cd\u5176\u4e8b\u5462\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u6211\u6697\u5730\u91cc\u8ddf\u8e2a\u4ed6\u4eec\uff0c\u5c82\u77e5\u4ed6\u4eec\u65e9\u5c31\u6709\u4e86\u6212\u5907\uff0c\u4e00\u8fdb\u6751\u5b50\uff0c\u5c31\u7ed9\u4ed6\u4eec \u53d1\u73b0\u3002\u9633\u575a\u767d\u8fd9\u53ae\u7684\u94c1\u7802\u638c\u59d4\u5b9e\u5389\u5bb3\uff0c\u8981\u4e0d\u662f\u4f60\u51fa\u6765\uff0c\u6211\u51e0\u4e4e\u906d\u4e86\u4ed6\u7684\u6bd2\u624b\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u542c\u8bf4\u5c60\u767e\u57ce\u8fd8\u6709\u4e2a\u513f\u5b50\u540d\u5524\u5c60\u9f99\uff0c\u5c60\u767e\u57ce\u6b7b\u540e\uff0c\u662f\u4e0d\u662f\u7531\u4ed6\u7ee7\u4efb\u7eff\u6797\u76df \u4e3b\uff1f\u201d\u674e\u601d\u5357\u54ea\u91cc\u77e5\u9053\uff0c\u90a3\u4e00\u665a\u5728\u90a3\u8fb9\u57ce\u7684\u5c0f\u5ba2\u5e97\u4e2d\uff0c\u4ed6\u4ee5\u4e3a\u662f\u6768\u5a49\u4e08\u592b\u7684\u90a3\u4e2a\u7537\u5b50\uff0c\u5c31\u662f \u5c60\u9f99\uff01 \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u522b\u63d0\u8fd9\u4e2a\u4eba\u5566\uff01\u8fd9\u4e2a\u4eba\u662f\u4e2a\u5927\u574f\u86cb\uff0c\u5c60\u51e4\u7684\u4e8c\u5e08\u54e5\u9f99\u521a\u5c31\u662f\u7ed9\u4ed6\u5bb3\u6b7b\u7684\u3002 \u8fd9\u53ae\u548c\u7eff\u6797\u8d25\u7c7b\u6df3\u4e8e\u5468\u7236\u5b50\u76f8\u4e92\u52fe\u7ed3\uff0c\u5df2\u7ecf\u6709\u4e86\u8bb8\u591a\u8bc1\u636e\uff0c\u8bc1\u660e\u4ed6\u4eec\u4e5f\u662f\u79c1\u901a\u8499\u53e4\u7684\u4e86\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u53f9\u606f\u9053\uff1a\u201c\u5c60\u767e\u57ce\u4e00\u4e16\u82f1\u96c4\uff0c\u6709\u8fd9\u6837\u4e00\u4e2a\u4e0d\u8096\u513f\u5b50\uff0c\u771f\u662f\u4e0d\u5e78\u3002\u90a3\u4e48\uff0c\u5c60\u51e4\u548c\u5979 \u7684\u54e5\u54e5\u2014\u2014\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u5c60\u51e4\u548c\u5979\u7684\u54e5\u54e5\u65e9\u5df2\u65ad\u7edd\u4e86\u5144\u59b9\u4e4b\u60c5\u3002\u5c60\u9f99\u4e0d\u6562\u56de\u6765\uff0c\u5982\u4eca\u5c60\u767e\u57ce\u7684\u65e7\u5c5e \u5df2\u63a8\u5c60\u51e4\u4e3a\u4e3b\u3002\u4e0d\u8fc7\uff0c\u56e0\u4e3a\u5979\u662f\u4e2a\u5e74\u8f7b\u7684\u59d1\u5a18\uff0c\u5979\u7236\u4eb2\u7684\u624b\u4e0b\u670d\u5979\uff0c\u522b\u7684\u5be8\u4e3b\uff0c\u5374\u5c31\u672a\u5fc5\u4f1a \u670d\u5979\u4e86\u3002\u6240\u4ee5\u5979\u73b0\u5728\u6b63\u662f\u975e\u5e38\u9700\u8981\u6709\u4eba\u5e2e\u5979\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u5c60\u51e4\u5012\u662f\u4e2a\u9976\u6709\u7236\u98ce\u7684\u5dfe\u5e3c\u987b\u7709\uff0c\u5047\u4ee5\u65f6\u65e5\uff0c\u5979\u4e00\u5b9a\u80fd\u591f\u5728\u7eff\u6797\u4e2d\u6811\u7acb\u5a01 \u4fe1\u7684\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u5c60\u51e4\u7684\u786e\u662f\u4e2a\u6709\u672c\u9886\u7684\u59d1\u5a18\uff0c\u4f46\u5bf9\u4e8e\u884c\u519b\u7528\u5175\u4e4b\u9053\uff0c\u6050\u6015\u5374\u662f\u5916\u884c\u4e86\u3002\u4f60 \u662f\u5c06\u95e8\u4e4b\u5b50\uff0c\u719f\u8bfb\u5175\u4e66\uff0c\u6b64\u53bb\u6b63\u597d\u52a9\u5979\u4e00\u81c2\u4e4b\u529b\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u7b11\u9053\uff1a\u201c\u7239\u7239\u7559\u4e0b\u7684\u5175\u4e66\u6211\u662f\u8bfb\u5f97\u5f88\u719f\uff0c\u4f46\u4e66\u672c\u4e0a\u7684\u4e1c\u897f\u662f\u6b7b\u7684\uff0c\u7528\u8d77\u6765\u5374\u8fd8\u4e0d \u77e5\u884c\u4e0d\u884c\u5462\uff1f\u54a6\u2014\u2014\u201d\u8bf4\u5230\u4e00\u534a\uff0c\u5ffd\u5730\u505c\u4e0b\uff0c\u4f3c\u4e4e\u662f\u5728\u51dd\u795e\u9759\u542c\u4ec0\u4e48\u58f0\u97f3\u3002 \u3000\u3000\u5b5f\u660e\u971e\u8be7\u9053\uff1a\u201c\u4f60\u53d1\u89c9\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u6811\u6797\u91cc\u4f3c\u4e4e\u6709\u4e2a\u5973\u4eba\u5728\u53f9\u6c14\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u771f\u7684\u5417\uff1f\u6211\u53ef\u6ca1\u6709\u542c\u89c1\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u6211\u53bb\u770b\u770b\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u7b11\u9053\uff1a\u201c\u4e5f\u8bb8\u662f\u4e2a\u53d7\u4e86\u5a46\u5a46\u7684\u6c14\u7684\u5c0f\u5ab3\u5987\uff0c\u4f60\u7ba1\u8fd9\u79cd\u95f2\u4e8b\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u8fd8\u662f\u5230\u6797\u5b50\u91cc\u770b\u4e86\u4e00\u770b\uff0c\u4f46\u5374\u4e0d\u89c1\u6709\u4eba\u3002\u5b5f\u660e\u971e\u9053\uff1a\u201c\u4f60\u4ee5\u4e3a\u662f\u4ec0\u4e48\u4eba\u8eb2\u5728\u91cc \u9762\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u60ca\u7591\u4e0d\u5b9a\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u83ab\u975e\u6211\u771f\u7684\u662f\u7591\u5fc3\u751f\u6697\u5154\u4e86\uff1f\u6768\u5a49\u5df2\u7ecf\u5ac1\u4e86\u4eba\uff0c\u5979\u53c8\u600e \u4f1a\u5230\u8fd9\u91cc\u6765\u5462\uff1f\u201d\u539f\u6765\u4ed6\u542c\u5230\u7684\u90a3\u58f0\u53f9\u606f\uff0c\u4f3c\u662f\u6768\u5a49\u7684\u58f0\u97f3\u3002 \u3000\u3000\u674e\u601d\u5357\u4ee5\u4e3a\u8fd9\u662f\u56e0\u4e3a\u81ea\u5df1\u601d\u5ff5\u6768\u5a49\u7684\u7f18\u6545\uff0c\u4ee5\u81f3\u628a\u522e\u8fc7\u6797\u68a2\u7684\u98ce\u58f0\u5f53\u6210\u4e86\u6768\u5a49\u7684\u53f9\u606f\uff0c \u4e0d\u89c9\u54d1\u7136\u5931\u7b11\u3002 \u3000\u3000\u674e\u601d\u5357\u4e0d\u613f\u4e0e\u5b5f\u660e\u971e\u518d\u63d0\u6768\u5a49\uff0c\u53ea\u597d\u5982\u6b64\u8bf4\u9053\uff1a\u201c\u6211\u6050\u6015\u662f\u54ea\u4e00\u5bb6\u53d7\u4e86\u59d4\u5c48\u7684\u5c0f\u5ab3\u5987\u5728 \u8fd9\u91cc\u81ea\u5bfb\u77ed\u89c1\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u597d\u4e86\uff0c\u90a3\u4e48\u4f60\u73b0\u5728\u53ef\u4ee5\u653e\u5fc3\u4e86\u3002\u201d\u5fc3\u56e0\u6697\u6697\u597d\u7b11\u674e\u601d\u5357\u6709\u70b9\u5a46\u5a46\u5988\u5988\u3002 \u3000\u3000\u674e\u601d\u5357\u8d70\u51fa\u6797\u5b50\uff0c\u4e0d\u77e5\u600e\u7684\uff0c\u5fc3\u91cc\u603b\u662f\u6709\u70b9\u60f4\u60f4\u4e0d\u5b89\u3002\u8d70\u8fc7\u4e00\u9053\u5c0f\u6865\uff0c\u674e\u601d\u5357\u5ffd\u5730\u60f3\u8d77 \u9646\u6e38\u7684\u4e24\u53e5\u8bd7\uff1a\u201c\u4f24\u5fc3\u6865\u4e0b\u6625\u6ce2\u7eff\uff0c\u66fe\u89c1\u60ca\u9e3f\u7167\u5f71\u6765\u3002\u201d\u6865\u4e0b\u6625\u6ce2\uff0c\u4f3c\u4e4e\u6447\u6643\u51fa\u6768\u5a49\u7684\u5f71 \u5b50\u3002 \u3000\u3000\u674e\u601d\u5357\u60f3\u8d77\u4e86\u90a3\u65e5\u5728\u90a3\u8352\u8c37\u4e4b\u4e2d\uff0c\u4e0e\u6768\u5a49\u4e34\u6d41\u7167\u5f71\u7684\u5f80\u4e8b\u3002\u6768\u5a49\u5728\u5e7d\u8c37\u6e05\u6eaa\u4e4b\u65c1\uff0c\u63c9\u788e \u4e86\u4e00\u6735\u6735\u843d\u82b1\uff0c\u8ba9\u5b83\u968f\u7740\u6d6a\u82b1\u98d8\u8d70\u3002\u90a3\u5929\u6b63\u662f\u4ed6\u4eec\u8ba2\u5a5a\u4e4b\u540e\u7684\u7b2c\u4e8c\u5929\uff0c\u6768\u5a49\u6000\u7591\u4ed6\u53ea\u662f\u56e0\u4e3a \u7236\u547d\u96be\u8fdd\uff0c\u624d\u4e0e\u81ea\u5df1\u8ba2\u5a5a\uff0c\u6545\u800c\u5bf9\u666f\u4f24\u60c5\u3002\u611f\u6000\u8eab\u4e16\uff0c\u501f\u7740\u6d41\u6c34\u843d\u82b1\uff0c\u53d1\u6cc4\u80f8\u4e2d\u7684\u90c1\u95f7\u3002 \u3000\u3000\u674e\u601d\u5357\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u843d\u82b1\u6d41\u6c34\u4e24\u65e0\u60c5\uff0c\u60f3\u4e0d\u5230\u5a49\u59b9\u90a3\u65e0\u6240\u62c5\u5fc3\u7684\u4e8b\u60c5\uff0c\u5982\u4eca\u7adf\u662f\u679c\u771f\u5982 \u6b64\u3002\u4f46\u8fd9\u53ef\u5e76\u4e0d\u662f\u6211\u7684\u65e0\u60c5\uff0c\u800c\u662f\u5a49\u59b9\u5148\u53d8\u4e86\u5fc3\u3002\u4f46\u8fd9\u53c8\u602a\u5f97\u4e86\u8c01\u5462\uff1f\u602a\u7684\u53ea\u662f\u9791\u5b50\u7684\u4e71\u519b \u5206\u6563\u4e86\u6211\u4eec\uff0c\u5a49\u59b9\u4e5f\u672a\u5fc5\u662f\u60f3\u8981\u53d8\u5fc3\u7684\u554a\u3002\u5509\uff0c\u82b1\u81ea\u98d8\u96f6\u6c34\u81ea\u6d41\uff0c\u5a49\u59b9\u5982\u4eca\u53c8\u4e0d\u77e5\u6d41\u843d\u4f55\u65b9 \u4e86\uff1f\u90a3\u53ae\u51b3\u975e\u826f\u4f34\uff0c\u53ea\u6015\u5a49\u59b9\u4e5f\u662f\u96be\u4ee5\u4e0e\u4ed6\u53ae\u5b88\u767d\u5934\u3002\u201d\u53c8\u60f3\uff1a\u201c\u9646\u6e38\u5f53\u5e74\u5728\u6c88\u56ed\u6240\u5bf9\u7684\u6625 \u6ce2\uff0c\u66fe\u89c1\u4ed6\u7684\u65e7\u4fa3\u2018\u60ca\u9e3f\u7167\u5f71\u2019\uff0c\u8fd9\u9053\u6865\u4e0b\u7684\u6625\u6ce2\uff0c\u5374\u6050\u6015\u662f\u89c1\u4e0d\u5230\u5a49\u59b9\u7684\u5f71\u5b50\u4e86\u3002\u53ef\u7b11\u6211 \u521a\u624d\u8fd8\u4ee5\u4e3a\u662f\u5979\u6765\u4e86\u5462\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u60f3\u8d77\u5f80\u4e8b\uff0c\u4e0d\u7981\u60b2\u4ece\u4e2d\u6765\uff0c\u96be\u4ee5\u65ad\u7edd\u3002\u5b5f\u660e\u971e\u770b\u89c1\u674e\u601d\u5357\u773c\u89d2\u6709\u9897\u6676\u83b9\u7684\u6cca\u73e0\uff0c \u4e0d\u89c9\u6014\u4e86\u4e00\u6014\uff0c\u8bf4\u9053\uff1a\u201c\u601d\u5357\uff0c\u4f60\u5728\u60f3\u7740\u4ec0\u4e48\u5fc3\u4e8b\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u6211\u7236\u4eb2\u5ba2\u6b7b\u5f02\u56fd\uff0c\u6211\u4e07\u91cc\u5f52\u6765\uff0c\u4e0d\u5e78\u6bcd\u4eb2\u53c8\u5df2\u901d\u4e16\u3002\u5982\u4eca\u522b\u4e86\u6545\u4e61\uff0c\u5f53\u771f \u662f\u65e0\u5bb6\u53ef\u5f52\u4e86\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u4eba\u751f\u4e0d\u5982\u610f\u4e8b\u5e38\u516b\u4e5d\uff0c\u4e71\u4e16\u4e2d\u906d\u9022\u4e0d\u5e78\u7684\u53c8\u5c82\u53ea\u4f60\u4e00\u4eba\uff1f\u5916\u9762\u6709\u8fbd\u9614\u7684\u5927 \u5730\uff0c\u5904\u5904\u65e0\u5bb6\u5904\u5904\u5bb6\uff0c\u4f60\u4e5f\u4e0d\u5fc5\u592a\u4f24\u5fc3\u4e86\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u731b\u7136\u4e00\u7701\uff0c\u8bf4\u9053\uff0c\u201c\u4f60\u8bf4\u5f97\u597d\uff0c\u5916\u9762\u6709\u8fbd\u9614\u7684\u5927\u5730\uff0c\u6211\u662f\u5e94\u8be5\u8d70\u51fa\u6211\u81ea\u5df1\u5fc3\u4e2d\u7b51 \u8d77\u6765\u7684\u5c0f\u5708\u5b50\u4e86\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u6000\u7740\u60d8\u60d8\u7684\u5fc3\u60c5\uff0c\u4e5f\u6000\u7740\u5bf9\u5916\u9762\u8fbd\u9614\u5927\u5730\u7684\u61a7\u61ac\uff0c\u522b\u4e86\u5bb6\u56ed\uff0c\u548c\u5b5f\u660e\u971e\u968f\u7740\u6c34\u6d41 \u7684\u65b9\u5411\u4e1c\u4eac\u3002 \u3000\u3000\u5728\u674e\u601d\u5357\u8d70\u8fc7\u53bb\u540e\u4e0d\u4e45\uff0c\u6709\u4e00\u4e2a\u5c11\u5973\u4ece\u6811\u6797\u91cc\u51fa\u6765\uff0c\u7ecf\u8fc7\u90a3\u9053\u5c0f\u6865\uff0c\u628a\u4e00\u7247\u7247\u7684\u9ec4\u53f6\u629b \u5165\u6cb3\u4e2d\u3002\u8fd9\u65f6\u867d\u662f\u521d\u6625\uff0c\u4f46\u4e25\u5bd2\u72b9\u5389\uff0c\u6797\u4e2d\u6625\u82b1\u672a\u5f00\u3002 \u3000\u3000\u8fd9\u5c11\u5973\u4e0d\u662f\u522b\u4eba\uff0c\u6b63\u662f\u6768\u5a49\u3002\u674e\u601d\u5357\u521a\u624d\u5e76\u975e\u9519\u89c9\uff0c\u5728\u6797\u4e2d\u8f7b\u8f7b\u53f9\u606f\u7684\u5973\u5b50\u7684\u786e\u662f\u5979\uff01 \u53ef\u60dc\uff0c\u674e\u601d\u5357\u6ca1\u6709\u4ed4\u7ec6\u641c\u5bfb\uff0c\u5982\u4eca\u6768\u5a49\u51fa\u6765\uff0c\u4ed6\u548c\u5b5f\u660e\u971e\u5df1\u662f\u8d70\u5f97\u8fdc\u4e86\uff01 \u3000\u3000\u6768\u5a49\u7ad9\u5728\u6865\u4e0a\u628a\u4e00\u7247\u7247\u7684\u9ec4\u53f6\u629b\u5165\u6cb3\u4e2d\uff0c\u5fc3\u4e2d\u7684\u4f24\u75db\u5b9e\u662f\u6bd4\u674e\u601d\u5357\u66f4\u751a\uff01 \u3000\u3000\u5979\u5386\u5c3d\u8270\u8f9b\uff0c\u9003\u56de\u6545\u56fd\uff0c\u6ee1\u6000\u5e0c\u671b\uff0c\u6765\u5bfb\u627e\u674e\u601d\u5357\u3002\u4e0d\u6599\u89c1\u7740\u4e86\u674e\u601d\u5357\uff0c\u674e\u601d\u5357\u5374\u662f\u4e0e \u5b5f\u660e\u971e\u540c\u5728\u4e00\u8d77\u3002\u5979\u60f3\u8981\u62dc\u89c1\u7684\u5a46\u5a46\u4e5f\u65e9\u5df2\u6b7b\u4e86\u3002 \u3000\u3000\u5b5f\u660e\u971e\u8bf4\u7684\u8bdd\u5979\u542c\u89c1\u4e86\uff0c\u90a3\u4e9b\u6751\u4eba\u7684\u8bae\u8bba\u5979\u4e5f\u90fd\u542c\u89c1\u4e86\u3002\u60c5\u4eba\u7684\u773c\u775b\u91cc\u662f\u85cf\u4e0d\u7740\u4e00\u7c92\u6c99 \u5b50\u7684\uff0c\u6768\u5a49\u4e0d\u7981\u60f3\u9053\uff1a\u201c\u539f\u6765\u8fd9\u4f4d\u5b5f\u59d1\u5a18\u4ee5\u4e3a\u6211\u5df2\u6b7b\u4e86\uff0c\u602a\u4e0d\u5f97\u5979\u8981\u6765\u627e\u5357\u54e5\u3002\u201d\u53c8\u60f3\uff1a \u201c\u90a3\u4e9b\u6751\u4eba\u8bf4\u5f97\u4e0d\u9519\uff0c\u5357\u54e5\u548c\u5979\u60c5\u6027\u76f8\u6295\uff0c\u4ed6\u4eec\u4e24\u4eba\u7ed3\u4e3a\u592b\u5987\uff0c\u7684\u786e\u662f\u50cf\u6751\u4eba\u6240\u8bf4\u7684\u5929\u751f\u4e00 \u5bf9\uff0c\u5730\u9020\u4e00\u53cc\u3002\u201d\u518d\u60f3\uff1a\u201c\u65e2\u7136\u4ed6\u4eec\u4ee5\u4e3a\u6211\u5df2\u6b7b\u4e86\uff0c\u90a3\u6211\u53c8\u4f55\u5fc5\u8fd8\u51fa\u73b0\u5728\u4ed6\u4eec\u7684\u9762\u524d\uff0c\u505a\u4ed6 \u4eec\u7684\u7eca\u811a\u77f3\u5462\uff1f\u5509\uff0c\u6211\u4e5f\u628a\u81ea\u5df1\u5f53\u505a\u81ea\u5df1\u662f\u5df2\u7ecf\u6b7b\u4e86\u5427\uff01\u201d \u3000\u3000\u6768\u5a49\u60f3\u5230\u4f24\u5fc3\u4e4b\u5904\uff0c\u771f\u60f3\u8df3\u5165\u6cb3\u4e2d\u81ea\u5c3d\uff0c\u4f46\u8f6c\u5ff5\u4e00\u60f3\uff1a\u201c\u4f59\u4e00\u4e2d\u4e0d\u4f46\u662f\u54e5\u54e5\u7684\u4ec7\u4eba\uff0c\u4e5f \u662f\u6211\u7684\u4ec7\u4eba\uff0c\u5927\u4ec7\u672a\u62a5\uff0c\u6211\u5c82\u53ef\u8f7b\u751f\uff1f\u201d\u5979\u521a\u624d\u5077\u542c\u5230\u7684\uff0c\u5b5f\u660e\u971e\u529d\u6170\u674e\u601d\u5357\u7684\u90a3\u4e24\u53e5\u8bdd\uff0c \u5ffd\u5730\u4f3c\u662f\u5728\u5979\u8033\u8fb9\u91cd\u54cd\u8d77\u6765\uff1a\u201c\u5916\u9762\u8fd8\u6709\u8fbd\u9614\u7684\u5929\u5730\uff0c\u4e3a\u4ec0\u4e48\u4f60\u4e0d\u8fc8\u6b65\u8d70\u51fa\u53bb\u5462\uff1f\u201d \u3000\u3000\u6768\u5a49\u60f3\u9053\uff1a\u201c\u8fd9\u4f4d\u5b5f\u59d1\u5a18\u867d\u7136\u662f\u62a2\u4e86\u6211\u7684\u5357\u54e5\uff0c\u4f46\u5979\u8fd9\u4e24\u53e5\u8bdd\u53ef\u662f\u8bf4\u5f97\u771f\u4e0d\u9519\u3002\u6211\u4e5f\u5e94 \u8be5\u8d70\u51fa\u6211\u7684\u5c0f\u5708\u5b50\u4e86\u3002\u201d\u53ef\u662f\u5929\u5730\u867d\u5927\uff0c\u4f55\u5904\u5bb9\u8eab\uff1f\u674e\u601d\u5357\u53ef\u4ee5\u548c\u5b5f\u660e\u971e\u540c\u53bb\u6295\u5954\u5c60\u51e4\uff0c\u5979 \u5374\u53c8\u53bb\u6295\u5954\u4f55\u4eba\uff1f\u5728\u8fd9\u4e2a\u4e16\u754c\u4e0a\u5979\u5df2\u7ecf\u6ca1\u6709\u4e00\u4e2a\u4eb2\u4eba\uff0c\u8fde\u6700\u540e\u4e00\u4e2a\u66fe\u7ecf\u4e0e\u5979\u76f8\u4f9d\u4e3a\u547d\u7684\u674e\u601d \u5357\u4e5f\u90fd\u968f\u522b\u4eba\u8d70\u4e86\uff0c\u5979\u8fd8\u6709\u8c01\u53ef\u4ee5\u4f9d\u9760\u5462\uff1f \u3000\u3000\u4e0d\u8fc7\u8def\u603b\u662f\u8981\u8d70\u7684\uff0c\u5373\u4f7f\u773c\u524d\u6ca1\u6709\u8def\uff0c\u4e5f\u6ca1\u6709\u540c\u4f34\uff0c\u5355\u72ec\u4e00\u4e2a\u4eba\u4e5f\u8fd8\u662f\u8981\u8d70\u7684\u3002\u8fd9\u4e16\u754c \u4e0a\u672c\u6765\u6ca1\u6709\u8def\uff0c\u8def\u5c31\u662f\u4eba\u8d70\u51fa\u6765\u7684\u554a\uff01 \u3000\u3000\u4e8e\u662f\u6768\u5a49\u5f3a\u6291\u4e0b\u60b2\u75db\u7684\u5fc3\u60c5\uff0c\u79bb\u5f00\u4e86\u674e\u601d\u5357\u7684\u6751\u5b50\u3002 \u3000\u3000\u5979\u4e0d\u613f\u610f\u78b0\u4e0a\u674e\u601d\u5357\uff0c\u9009\u4e86\u53e6\u4e00\u6761\u5c71\u8def\u8d70\uff0c\u8d70\u5230\u4e2d\u5348\u65f6\u5206\uff0c\u4ece\u4e00\u5904\u5c71\u5188\u7ecf\u8fc7\uff0c\u5ffd\u542c\u5f97\u6811 \u6797\u91cc\u6709\u91d1\u94c1\u4ea4\u9e23\u4e4b\u58f0\u3002 \u3000\u3000\u6768\u5a49\u672c\u662f\u6ca1\u6709\u5fc3\u60c5\u591a\u7ba1\u95f2\u4e8b\uff0c\u4f46\u6811\u6797\u91cc\u90a3\u4e24\u4e2a\u4eba\u9ad8\u547c\u9163\u6597\u4e4b\u58f0\uff0c\u5374\u662f\u58f0\u58f0\u4f20\u5165\u5979\u7684\u8033 \u6735\uff0c\u5176\u4e2d\u4e00\u4e2a\u4eba\u7684\u58f0\u97f3\u597d\u50cf\u5979\u662f\u5728\u54ea\u513f\u542c\u89c1\u8fc7\u4f3c\u7684\u3002 \u3000\u3000\u6768\u5a49\u6014\u4e86\u4e00\u6014\uff0c\u4e0d\u89c9\u505c\u4e0b\u811a\u6b65\u3002\u5c31\u5728\u6b64\u65f6\uff0c\u53ea\u542c\u5f97\u90a3\u4e2a\u58f0\u97f3\u4f3c\u66fe\u542c\u8fc7\u7684\u4eba\u53c8\u5728\u559d\u9053\uff1a \u201c\u4f60\u4eec\u60f3\u53bb\u8c0b\u5bb3\u674e\u601d\u5357\uff0c\u6211\u77f3\u749e\u53ea\u8981\u8fd8\u6709\u4e00\u53e3\u6c14\u5728\uff0c\u5c31\u7edd\u4e0d\u80fd\u8ba9\u4f60\u4eec\u5f97\u901e\uff01\u201d \u3000\u3000\u53e6\u4e00\u4e2a\u4eba\u78d4\u78d4\u602a\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u672c\u6765\u5c31\u8981\u6740\u4f60\uff0c\u4f60\u5982\u4eca\u65e2\u662f\u8981\u4e3a\u674e\u601d\u5357\u5356\u547d\uff0c\u90a3\u6211\u5c31\u6b63 \u597d\u6210\u5168\u4f60\u4e86\uff01\u201d \u3000\u3000\u6768\u5a49\u970d\u7136\u4e00\u60ca\uff0c\u8fde\u5fd9\u8dd1\u8fdb\u6797\u5b50\u91cc\u770b\uff0c\u53ea\u89c1\u4e00\u4e2a\u4f7f\u5251\u7684\u5c11\u5e74\u548c\u4e00\u4e2a\u4f7f\u53cc\u94a9\u7684\u6c49\u5b50\u6b63\u5728\u6253\u5f97 \u5341\u5206\u6fc0\u70c8\u3002\u6b63\u662f\uff1a \u3000\u3000\u8def\u9014\u5904\u5904\u591a\u8346\u68d8\uff0c\u53ea\u56e0\u90aa\u6b63\u4e0d\u76f8\u5bb9\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u56de\u3000\u865a\u4f20\u8c22\u5973\u5fc3\u5982\u94c1\u3000\u76fc\u5230\u8427\u90ce\u773c\u6b32\u7a7f \u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b \u7b2c\u4e8c\u5341\u56de\u3000\u865a\u4f20\u8c22\u5973\u5fc3\u5982\u94c1\u3000\u76fc\u5230\u8427\u90ce\u773c\u6b32\u7a7f \u3000\u3000\u6768\u5a49\u8d70\u775b\u4e00\u770b\uff0c\u8ba4\u5f97\u4f7f\u5251\u7684\u8fd9\u4e2a\u5c11\u5e74\u6b63\u662f\u4ece\u524d\u5728\u90a3\u8fb9\u5883\u7684\u5c0f\u9547\u5356\u5200\u7ed9\u5979\u7684\u90a3\u4e2a\u4eba\u3002\u4e5f\u5c31 \u662f\u5728\u90a3\u5929\u665a\u4e0a\uff0c\u5f53\u5c60\u9f99\u60f3\u7528\u836f\u9152\u4e0e\u5979\u5730\u5e72\u676f\u7684\u65f6\u5019\uff0c\u8dd1\u6765\u6253\u788e\u5979\u7684\u676f\u5b50\u7684\u90a3\u4e2a\u4eba\u3002 \u3000\u3000\u6b64\u65f6\u77f3\u749e\u4ea6\u5df2\u770b\u89c1\u4e86\u6768\u5a49\uff0c\u53c8\u60ca\u53c8\u559c\uff0c\u8fde\u5fd9\u53eb\u9053\uff1a\u201c\u4f60\u662f\u6768\u59d1\u5a18\u5417\uff1f\u201d\u8bf4\u8bdd\u5206\u795e\uff0c\u9669\u4e9b \u7ed9\u90a3\u4f7f\u94a9\u7684\u53f9\u5b50\u4e00\u94a9\u52fe\u7740\u3002 \u3000\u3000\u6768\u5a49\u987e\u4e0d\u5f97\u7b54\u8bdd\uff0c\u5530\u7684\u62d4\u5251\u51fa\u9798\uff0c\u4e00\u5251\u5c31\u5411\u90a3\u4f7f\u94a9\u7684\u6c49\u5b50\u523a\u53bb\u3002 \u3000\u3000\u90a3\u6c49\u5b50\u672c\u9886\u4e5f\u59d4\u5b9e\u4e86\u5f97\uff0c\u5728\u8179\u80cc\u53d7\u654c\u4e4b\u4e0b\uff0c\u5c45\u7136\u4e0d\u614c\u4e0d\u5fd9\u7684\u53cd\u624b\u4e00\u94a9\uff0c\u5c31\u628a\u6768\u5a49\u7684\u4e00\u62db \u201c\u7389\u5973\u6295\u68ad\u201d\u6bd4\u89e3\u5f00\u4e86\u3002 \u3000\u3000\u90a3\u6c49\u5b50\u602a\u58f0\u7b11\u9053\uff1a\u201c\u539f\u6765\u4f60\u5c31\u662f\u6768\u5a49\uff0c\u4f46\u4f60\u53ef\u77e5\u9053\u6211\u662f\u8c01\uff1f\u201d \u3000\u3000\u6768\u5a49\u6012\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u662f\u79c1\u901a\u9791\u5b50\u7684\u5978\u8d3c\uff01\u201d \u3000\u3000\u90a3\u6c49\u5b50\u7b11\u9053\uff1a\u201c\u9519\u4e86\uff0c\u9519\u4e86\uff0c\u8bf4\u8d77\u6765\u6211\u662f\u4f60\u7684\u81f3\u4eb2\u5462\uff0c\u4f60\u5982\u4f55\u53cd\u52a9\u5916\u4eba\uff1f\u201d \u3000\u3000\u6768\u5a49\u65a5\u9053\uff1a\u201c\u80e1\u8bf4\u516b\u9053\uff0c\u770b\u5251\uff01\u201d \u3000\u3000\u77f3\u749e\u8bf4\u9053\uff1a\u201c\u4f60\u8bf4\u5f97\u4e0d\u9519\uff0c\u8fd9\u53ae\u540d\u53eb\u6df3\u4e8e\u8191\uff0c\u6b63\u662f\u9633\u575a\u767d\u9080\u6765\uff0c\u9634\u8c0b\u56fe\u5bb3\u674e\u601d\u5357\u7684\u5978 \u8d3c\u3002\u201d \u3000\u3000\u6df3\u4e8e\u8191\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5927\u4e08\u592b\u884c\u4e0d\u66f4\u540d\uff0c\u5750\u4e0d\u6539\u59d3\u3002\u6df3\u4e8e\u8191\u5c31\u662f\u6211\uff0c\u6211\u5c31\u662f\u6df3\u4e8e \u8191\u3002\u5c60\u9f99\u53ef\u66fe\u548c\u4f60\u8bf4\u8fc7\u6211\u5427\uff1f\u201d \u3000\u3000\u6768\u5a49\u83ab\u540d\u5176\u5999\uff0c\u53ea\u5f53\u4ed6\u662f\u75af\u8a00\u75af\u8bed\uff0c\u624b\u5e95\u5251\u62db\u4e1d\u6beb\u4e0d\u7f13\u3002 \u3000\u3000\u6df3\u4e8e\u8191\u53cc\u94a9\u98de\u821e\uff0c\u63a5\u8fde\u89e3\u4e86\u6768\u5a49\u548c\u77f3\u749e\u7684\u4e09\u62db\uff0c\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u5c60\u9f99\u5df2\u7ecf\u7b54\u5e94\u628a\u59b9\u5b50\u8bb8\u914d \u4e0e\u6211\uff0c\u5c60\u5927\u5ac2\uff0c\u4f60\u60f3\u60f3\uff0c\u5c60\u9f99\u662f\u4f60\u7684\u4e08\u592b\uff0c\u6211\u662f\u5c60\u9f99\u7684\u59b9\u4e08\uff0c\u54b1\u4eec\u8fd8\u4e0d\u662f\u81f3\u4eb2\u5417\uff1f\u54c8\uff0c\u770b\u4f60 \u795e\u60c5\uff0c\u83ab\u975e\u5c60\u9f99\u8fd8\u6ca1\u6709\u5c06\u4ed6\u7684\u771f\u540d\u544a\u8bc9\u4f60\u5427\uff1f\u597d\uff0c\u90a3\u4e48\u6211\u73b0\u5728\u544a\u8bc9\u4f60\uff0c\u5c60\u9f99\u5c31\u662f\u90a3\u4e2a\u548c\u4f60\u53cc \u5bbf\u53cc\u98de\u7684\u675c\u96c4\uff0c\u4f60\u4eec\u7684\u4e8b\u6211\u5168\u90e8\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u6df3\u4e8e\u8191\u8fd9\u756a\u8bdd\u628a\u6768\u5a49\u6c14\u5f97\u53cc\u773c\u53d1\u9ed1\uff0c\u6df3\u4e8e\u8191\u4e58\u673a\u731b\u4e0b\u6740\u624b\uff0c\u53cc\u94a9\u500f\u5730\u9501\u4f4f\u4e86\u6768\u5a49\u7684\u5251 \u5c16\u3002 \u3000\u3000\u77f3\u749e\u65e9\u5df2\u8df3\u5f00\u4e09\u6b65\uff0c\u6b63\u5728\u6df3\u4e8e\u8191\u8981\u4e0b\u6740\u624b\u7684\u65f6\u5019\uff0c\u77f3\u749e\u628a\u624b\u4e00\u626c\uff0c\u4e00\u652f\u6bd2\u9f99\u9556\u5411\u6df3\u4e8e\u8191 \u6253\u6765\u3002\u6df3\u4e8e\u8191\u8bc6\u5f97\u5389\u5bb3\uff0c\u53ea\u597d\u817e\u51fa\u5de6\u624b\u7684\u4e00\u67c4\u94a9\u6765\uff0c\u8dcb\u5f00\u8fd9\u4e00\u652f\u6bd2\u9f99\u9556\u3002 \u3000\u3000\u6768\u5a49\u5403\u4e8f\u662f\u5728\u6c14\u529b\u8f83\u5f31\uff01\u82e5\u8bba\u62db\u6570\u7684\u7cbe\u5999\uff0c\u5979\u5374\u662f\u8fd8\u5728\u6df3\u4e8e\u8191\u4e4b\u4e0a\u7684\u3002\u6b64\u65f6\u538b\u529b\u51cf\u8f7b\uff0c \u6df3\u4e8e\u8191\u7684\u5355\u94a9\u9501\u62ff\u4e0d\u7a33\u5979\u7684\u5b9d\u5251\uff0c\u6768\u5a49\u4e00\u62db\u201c\u4e09\u8f6c\u6cd5\u8f6e\u201d\uff0c\u628a\u94a9\u4e0a\u7684\u6708\u7259\u524a\u65ad\u4e86\u4e24\u9f7f\u3002 \u3000\u3000\u77f3\u749e\u53eb\u9053\uff1a\u201c\u6768\u59d1\u5a18\uff0c\u8fd9\u53ae\u72d7\u5634\u91cc\u4e0d\u957f\u8c61\u7259\uff0c\u522b\u4e2d\u4ed6\u6fc0\u5c06\u4e4b\u8ba1\u3002\u201d \u3000\u3000\u6768\u5a49\u6c89\u4f4f\u4e86\u6c14\uff0c\u7d27\u54ac\u94f6\u7259\uff0c\u72e0\u72e0\u5730\u6740\u4e0a\u53bb\uff0c\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u5978\u8d3c\uff0c\u6211\u975e\u6740\u4e86\u4f60\u4e0d\u53ef\uff01\u201d \u3000\u3000\u6df3\u4e8e\u8191\u672c\u6765\u60f3\u8981\u6c14\u574f\u6768\u5a49\uff0c\u624d\u6709\u628a\u63e1\u53ef\u4ee5\u53d6\u80dc\u7684\u3002\u54ea\u77e5\u5f04\u5de7\u53cd\u62d9\uff0c\u6768\u5a49\u4f7f\u51fa\u4e24\u8d25\u4ff1\u4f24\u7684 \u62fc\u547d\u62db\u6570\u3001\u7adf\u662f\u9510\u4e0d\u53ef\u5f53\uff01 \u3000\u3000\u6df3\u4e8e\u8191\u82e5\u662f\u5355\u72ec\u5bf9\u4ed8\u6768\u5a49\uff0c\u90a3\u8fd8\u81ea\u53ef\uff0c\u4f46\u5982\u4eca\u9664\u4e86\u6768\u5a49\u4e4b\u5916\uff0c\u8fd8\u6709\u4e00\u4e2a\u77f3\u749e\u3002\u77f3\u749e\u6b66\u529f \u867d\u7136\u7a0d\u900a\u4e8e\u4ed6\uff0c\u4e5f\u662f\u4e00\u4e2a\u52b2\u654c\u3002 \u3000\u3000\u77f3\u749e\u5c55\u5f00\u7ed5\u8eab\u6e38\u6597\u7684\u6218\u672f\uff0c\u5077\u7a7a\u5c31\u53d1\u51fa\u6bd2\u9f99\u9556\u3002\u6df3\u4e8e\u8191\u6700\u60e7\u6015\u7684\u4e5f\u6b63\u662f\u6bd2\u9f99\u9556\u3002\u8fd9\u6bd2\u9f99 \u9556\u89c1\u8840\u5c01\u5589\uff0c\u6df3\u4e8e\u8191\u5fc5\u987b\u63d0\u5fc3\u540a\u80c6\u5730\u5e94\u4ed8\u3002 \u3000\u3000\u6768\u5a49\u4e00\u652f\u5b9d\u5251\u6307\u4e1c\u6253\u897f\uff0c\u6307\u5357\u6253\u5317\uff0c\u62db\u62db\u90fd\u662f\u6307\u5411\u6df3\u4e8e\u8191\u7684\u8981\u5bb3\u3002\u5979\u8fd9\u652f\u5251\u4e43\u662f\u660e\u6167\u516c \u4e3b\u6240\u8d60\u7684\u5b9d\u5251\uff0c\u5f53\u771f\u6709\u524a\u94c1\u5982\u6ce5\u4e4b\u80fd\uff0c\u5439\u6bdb\u7acb\u65ad\u4e4b\u5229\uff0c\u6df3\u4e8e\u8191\u4e00\u4e2a\u758f\u795e\uff0c\u53ea\u542c\u5f97\u201c\u54e8\u201d\u7684\u4e00 \u58f0\uff0c\u6df3\u4e8e\u8191\u53f3\u624b\u7684\u90a3\u67c4\u94a9\u7ed9\u5979\u4e00\u5251\u524a\u65ad\uff01 \u3000\u3000\u6df3\u4e8e\u8191\u89c1\u52bf\u4e0d\u5999\uff0c\u865a\u6643\u4e00\u62db\uff0c\u4e00\u9762\u8dd1\u4e00\u9762\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u5c60\u5927\u5ac2\uff0c\u4f60\u60f3\u6740\u6211\u706d\u53e3\u5427\uff1f\u563f\uff1f \u563f\uff0c\u82e5\u8981\u4eba\u4e0d\u77e5\uff0c\u9664\u975e\u5df1\u83ab\u4e3a\u3002\u4f60\u548c\u5c60\u9f99\u7684\u5978\u60c5\uff0c\u6211\u4e0d\u8bf4\uff0c\u674e\u601d\u5357\u4e5f\u7ec8\u987b\u77e5\u9053\u3002\u54fc\uff0c\u54fc\uff0c\u4ed6 \u8fd8\u80fd\u518d\u8981\u4f60\u5417\uff1f\u4f9d\u6211\u4e4b\u89c1\uff0c\u4f60\u4e0d\u5982\u5ac1\u7ed9\u5c60\u9f99\u7b97\u4e86\uff0c\u4f55\u82e6\u8fd8\u8981\u4e5e\u6c42\u674e\u601d\u5357\u8986\u6c34\u91cd\u6536\uff1f\u201d \u3000\u3000\u6768\u5a49\u672c\u6765\u662f\u60f3\u6c89\u4f4f\u6c14\u7684\uff0c\u4f46\u542c\u4e86\u8fd9\u6837\u6c61\u8511\u5979\u7684\u8bf4\u8bdd\uff0c\u5374\u4e0d\u7531\u5f97\u6c14\u5f97\u53cc\u817f\u53d1\u8f6f\uff0c\u8ffd\u4ed6\u4e0d \u4e0a\u3002 \u3000\u3000\u77f3\u749e\u559d\u58f0\uff1a\u201c\u4f4f\u53e3\uff01\u201d\u4e09\u652f\u6bd2\u9f99\u9556\u8fde\u73af\u98de\u53bb\u3002\u6df3\u4e8e\u8191\u53ea\u5269\u4e00\u67c4\u5355\u94a9\u6253\u843d\u4e86\u4e24\u652f\uff0c\u7b2c\u4e09\u652f \u5c04\u4ed6\u53f3\u80c1\uff0c\u906e\u62e6\u4e0d\u53ca\uff0c\u5fd9\u626d\u8eab\u65f6\uff0c\u9556\u5c16\u5df2\u64e6\u7740\u4ed6\u7684\u808b\u65c1\u98de\u8fc7\uff0c\u523a\u7a7f\u4e86\u4e00\u7247\u8089\u76ae\u3002 \u3000\u3000\u6df3\u4e8e\u8191\u4e5f\u771f\u591f\u72e0\uff0c\u4e00\u94a9\u52fe\u4e0b\uff0c\u628a\u53d7\u4f24\u7684\u4e00\u5757\u76ae\u8089\u6495\u4e86\u4e0b\u6765\uff0c\u8840\u6d41\u5982\u6ce8\uff0c\u4e5f\u987e\u4e0d\u5f97\u6577\u4e0a\u91d1 \u521b\u836f\u4fbf\u81ea\u8dd1\u4e86\u3002\u8fd9\u6837\u4ed6\u867d\u7136\u727a\u7272\u4e86\u4e00\u5757\u76ae\u8089\uff0c\u6027\u547d\u5374\u53ef\u4ee5\u4fdd\u5168\u3002 \u3000\u3000\u77f3\u749e\u542c\u5f97\u201c\u5495\u549a\u201d\u4e00\u58f0\uff0c\u56de\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u6768\u5a49\u5750\u5728\u5730\u4e0a\uff0c\u6cea\u6c34\u76c8\u7736\uff0c\u77f3\u749e\u8bf4\u9053\uff1a\u201c\u6768\u59d1 \u5a18\uff0c\u8fd9\u53ae\u5c31\u50cf\u75af\u72d7\u4e00\u822c\uff0c\u503c\u4e0d\u5f97\u4e3a\u4ed6\u6c14\u574f\u4e86\u81ea\u5df1\u7684\u8eab\u5b50\u3002\u201d \u3000\u3000\u6768\u5a49\u62ed\u4e86\u773c\u6cea\uff0c\u5fc3\u91cc\u81ea\u601d\uff1a\u201c\u6211\u672c\u6765\u53ef\u4ee5\u628a\u81ea\u5df1\u5f53\u4f5c\u6b7b\u4e86\uff0c\u4ece\u6b64\u4e0d\u89c1\u5357\u54e5\uff0c\u4f46\u8fd9\u53ae\u542b\u8840 \u55b7\u4eba\uff0c\u4fdd\u4e0d\u5b9a\u4e0d\u4f1a\u4f20\u5230\u5357\u54e5\u7684\u8033\u6735\u91cc\u53bb\uff0c\u6211\u6b7b\u4e86\u4e5f\u4e0d\u80fd\u6e05\u767d\u3002\u5509\uff0c\u6211\u4e0d\u89c1\u5357\u54e5\u4e5f\u4e0d\u6253\u7d27\uff0c\u4f46 \u6211\u4e00\u5b9a\u8981\u8ba9\u5357\u54e5\u77e5\u9053\u6211\u8fd9\u4e2a\u8eab\u5b50\u662f\u6e05\u767d\u7684\u3002\u201d \u3000\u3000\u6768\u5a49\u7ad9\u4e86\u8d77\u6765\uff0c\u5411\u77f3\u749e\u65bd\u4e86\u4e00\u793c\uff0c\u8bf4\u9053\uff1a\u201c\u77f3\u5927\u54e5\uff0c\u591a\u8c22\u4f60\u90a3\u665a\u63ed\u7834\u90a3\u53ae\u7684\u5978\u8c0b\uff0c\u6211\u3001 \u6211\u6ca1\u6709\u4e0a\u90a3\u53ae\u7684\u5f53\u3002\u201d \u3000\u3000\u77f3\u749e\u653e\u4e0b\u5fc3\u4e0a\u7684\u4e00\u5757\u77f3\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u771f\u60ed\u6127\uff0c\u6211\u6709\u8fd9\u6837\u7684\u4e00\u4e2a\u5e08\u54e5\u3002\u201d \u3000\u3000\u6768\u5a49\u6014\u4e86\u4e00\u6014\uff0c\u8bf4\u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u675c\u96c4\u662f\u4f60\u7684\u5e08\u54e5\uff1f\u201d \u3000\u3000\u77f3\u749e\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4ed6\u7684\u771f\u540d\u662f\u53eb\u5c60\u9f99\uff0c\u662f\u6211\u6069\u5e08\u5c60\u767e\u57ce\u7684\u513f\u5b50\u3002\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u90a3\u4e48\u4ed6\u5c31\u662f\u5c60\u51e4\u7684\u54e5\u54e5\uff1f\u201d \u3000\u3000\u77f3\u749e\u9053\uff1a\u201c\u867d\u662f\u5144\u59b9\uff0c\u53ef\u4e0d\u4e00\u6837\u3002\u5c60\u51e4\u662f\u4e2a\u597d\u59d1\u5a18\u3002\u201d\u6768\u5a49\u9053\uff1a\u201c\u6211\u77e5\u9053\uff0c\u6211\u89c1\u8fc7\u5979 \u7684\u3002\u201d\u5fc3\u91cc\u5728\u60f3\uff1a\u201c\u5357\u54e5\u6b63\u662f\u8981\u5230\u5c60\u51e4\u7684\u5c71\u5be8\uff0c\u8fd9\u6837\uff0c\u8c23\u8a00\u5c31\u66f4\u6613\u4f20\u5230\u4ed6\u7684\u8033\u6735\u4e86\u3002\u201d \u3000\u3000\u77f3\u749e\u95ee\u9053\uff1a\u201c\u6768\u59d1\u5a18\uff0c\u542c\u8bf4\u601d\u5357\u5927\u54e5\u5df2\u7ecf\u56de\u6765\uff0c\u4f60\u5230\u8fc7\u4ed6\u7684\u5bb6\u91cc\u6ca1\u6709\uff1f\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u6211\u6b63\u662f\u4ece\u4ed6\u7684\u90a3\u6761\u6751\u5b50\u51fa\u6765\u3002\u4e0d\u9519\uff0c\u4ed6\u662f\u5df2\u7ecf\u56de\u6765\u4e86\u3002\u201d \u3000\u3000\u77f3\u749e\u8be7\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u4f60\u8fd8\u6ca1\u6709\u89c1\u7740\u601d\u5357\u5927\u54e5\u5417\uff1f\u201d \u3000\u3000\u6768\u5a49\u4e0d\u7981\u53c8\u662f\u5fc3\u91cc\u4e00\u9178\uff0c\u8bf4\u9053\uff1a\u201c\u89c1\u3001\u89c1\u7740\u4e86\u3002\u201d \u3000\u3000\u77f3\u749e\u66f4\u662f\u8be7\u5f02\uff0c\u8bf4\u9053\uff1a\u201c\u4f55\u4ee5\u4f60\u4e00\u4e2a\u4eba\u5728\u8fd9\u513f\uff0c\u601d\u5357\u5927\u54e5\u5462\uff1f\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u6211\u89c1\u7740\u4ed6\uff0c\u4ed6\u6ca1\u89c1\u7740\u6211\u3002\u4ed6\u548c\u5b5f\u5973\u4fa0\u4e00\u540c\u8d70\u4e86\u3002\u201d \u3000\u3000\u77f3\u749e\u9053\uff1a\u201c\u54e6\uff0c\u5b5f\u660e\u971e\u4e5f\u6765\u4e86\u4e48\uff1f\u60f3\u5979\u5fc5\u662f\u66ff\u5c60\u51e4\u6765\u9080\u601d\u5357\u5927\u54e5\u4e0a\u5c71\u7684\u5427\u3002\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u6b63\u662f\uff0c\u9633\u575a\u767d\u8fd9\u53ae\u5c31\u662f\u5979\u548c\u601d\u5357\u8054\u624b\u6253\u8d25\u7684\u3002\u201d \u3000\u3000\u77f3\u749e\u6599\u60f3\u5176\u4e2d\u8d70\u6709\u9690\u60c5\uff0c\u60f3\u4e86\u4e00\u60f3\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u4e48\u5012\u662f\u53ef\u4ee5\u7701\u5f97\u6211\u591a\u8dd1\u4e00\u8d9f\u4e86\uff0c\u4e0d\u8fc7\uff0c \u6709\u53e5\u8bdd\u6211\u4e0d\u77e5\u8be5\u4e0d\u8be5\u95ee\u4f60\uff1f\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u77f3\u5927\u54e5\u4f46\u8bf4\u65e0\u59a8\u3002\u201d \u3000\u3000\u77f3\u749e\u9053\uff1a\u201c\u4f60\u65e2\u7136\u89c1\u7740\u4e86\u601d\u5357\u5927\u54e5\uff0c\u4f55\u4ee5\u4e0d\u548c\u4ed6\u4eec\u4e00\u540c\u8d70\u5462\uff1f\u201d \u3000\u3000\u6768\u5a49\u82e6\u7b11\u9053\uff1a\u201c\u56e0\u4e3a\u4ed6\u4eec\u4ee5\u4e3a\u6211\u5df2\u7ecf\u6b7b\u4e86\u3002\u201d \u3000\u3000\u77f3\u749e\u542c\u4e86\uff0c\u4e0d\u89c9\u6115\u7136\u3002\u4f46\u4ed6\u4e5f\u662f\u4e2a\u7ecf\u8fc7\u60c5\u573a\u98ce\u6d6a\u7684\u4eba\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u4ece\u6768\u5a49\u7684\u795e\u8272\u4e2d\u94bb\u51fa \u4e00\u4e2a\u604d\u7136\u5927\u609f\uff0c\u60f3\u9053\uff1a\u201c\u539f\u6765\u5979\u662f\u5728\u5403\u5b5f\u660e\u971e\u7684\u918b\u3002\u201d\u5f53\u4e0b\u59d4\u5a49\u8bf4\u9053\uff1a\u201c\u5b5f\u5973\u9655\u662f\u4e2a\u5f88\u723d\u6717 \u7684\u59d1\u5a18\uff0c\u5bf9\u7537\u5973\u4e4b\u5acc\uff0c\u5411\u662f\u4e0d\u5927\u907f\u5fcc\u7684\u3002\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u601d\u5357\u4e0e\u5979\u76f8\u8bc6\uff0c\u672c\u6765\u5c31\u662f\u5728\u6211\u4e4b\u524d\uff0c\u4f60\u522b\u8bef\u4f1a\uff0c\u4ee5\u4e3a\u6211\u6709\u4ec0\u4e48\u5c0f\u5fc3\u773c\u513f\u3002\u201d \u3000\u3000\u77f3\u749e\u5fae\u7b11\u9053\uff1a\u201c\u636e\u6211\u6240\u77e5\uff0c\u5b5f\u5973\u4fa0\u548c\u5c60\u51e4\u5bf9\u4f60\u90fd\u5f88\u94a6\u4f69\uff0c\u4f60\u82e5\u662f\u4e0a\u5c71\uff0c\u5979\u4eec\u4e00\u5b9a\u5341\u5206\u9ad8 \u5174\uff01\u201d \u3000\u3000\u6768\u5a49\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e0a\u53bb\u505a\u4ec0\u4e48\uff1f\u5509\uff0c\u77f3\u5927\u54e5\u3001\u4f60\u3001\u4f60\u4e0d\u61c2\u7684\uff01\u201d \u3000\u3000\u77f3\u749e\u9053\uff1a\u201c\u626c\u59d1\u5a18\uff0c\u6562\u60c5\u4f60\u662f\u548c\u601d\u5357\u5927\u54e5\u6709\u4e86\u4e00\u70b9\u513f\u8bef\u4f1a\u4e86\uff1f\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u6211\u53ef\u6ca1\u6709\u8bef\u4f1a\u4ed6\u3002\u201d \u3000\u3000\u77f3\u749e\u9053\uff1a\u201c\u90a3\u4e48\u6216\u8bb8\u4f60\u662f\u6015\u4ed6\u8bef\u4f1a\u4f60\uff1f\u4f60\u653e\u5fc3\uff0c\u6211\u5728\u897f\u590f\u66fe\u7ecf\u89c1\u8fc7\u601d\u5357\u5927\u54e5\uff0c\u4ed6\u5bf9\u4f60\u5341 \u5206\u6302\u5ff5\u3002\u4ed6\u77e5\u9053\u4e86\u4f60\u7684\u906d\u9047\uff0c\u53ea\u4f1a\u5bf9\u4f60\u540c\u60c5\uff0c\u51b3\u4e0d\u4f1a\u5bf9\u4f60\u53d1\u751f\u8bef\u4f1a\u3002\u4f60\u82e5\u8fd8\u4e0d\u653e\u5fc3\uff0c\u6211\u3001\u6211 \u4e5f\u4f1a\u7ed9\u4f60\u6d17\u5237\u7684\u3002\u201d \u3000\u3000\u6768\u5a49\u9762\u4e0a\u4e00\u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u53ef\u4ee5\u628a\u4f60\u76ee\u51fb\u7684\u4e8b\u60c5\u544a\u8bc9\u4ed6\uff0c\u4f46\u6211\u7684\u4e0b\u843d\u4e0d\u8981\u544a\u8bc9\u4ed6\u3002\u6211\u5e0c \u671b\u4ed6\u4ecd\u7136\u662f\u628a\u6211\u5f53\u4f5c\u662f\u6b7b\u4e86\u7684\u597d\u3002\u201d \u3000\u3000\u77f3\u749e\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u4e0d\u4e3a\u4ec0\u4e48\u3002\u53ea\u56e0\u6211\u662f\u52ab\u540e\u4f59\u751f\uff0c\u6b64\u5fc3\u65e9\u5df2\u6210\u7070\u4e86\u3002\u201d \u3000\u3000\u77f3\u749e\u77e5\u5979\u8a00\u4e0d\u7531\u8877\uff0c\u4f46\u4e00\u65f6\u95f4\u5374\u4e0d\u77e5\u5982\u4f55\u7ed9\u5979\u5f00\u89e3\u624d\u597d\u3002 \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u77f3\u5927\u54e5\uff0c\u591a\u8c22\u4f60\u90a3\u65e5\u628a\u8fd9\u67c4\u5b9d\u5200\u7ed9\u6211\uff0c\u5982\u4eca\u539f\u58c1\u5f52\u8d75\u3002\u201d\u4ea4\u8fd8\u5b9d\u5200\uff0c\u4fbf\u5373\u544a \u8f9e\u3002 \u3000\u3000\u77f3\u749e\u53eb\u9053\uff1a\u201c\u4e14\u6162\uff01\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u6211\u5df2\u7ecf\u53e6\u5916\u627e\u4e86\u4e00\u652f\u597d\u5251\uff0c\u8fd9\u67c4\u5b9d\u5200\uff0c\u7406\u5e94\u8fd8\u4f60\u3002\u201d \u3000\u3000\u77f3\u749e\u9053\uff1a\u201c\u6211\u8bf4\u7684\u4e0d\u662f\u5b9d\u5200\uff0c\u6211\u8bf4\u7684\u662f\u4f60\uff01\u8bf7\u4f60\u6162\u8d70\uff01\u201d \u3000\u3000\u6768\u5a49\u82e6\u7b11\u9053\uff1a\u201c\u8fd8\u6709\u4ec0\u4e48\u597d\u8bf4\u7684\u5462\uff1f\u201d \u3000\u3000\u77f3\u749e\u9053\uff1a\u201c\u6768\u59d1\u5a18\uff0c\u4f60\u4e00\u4e2a\u4eba\u8981\u4e0a\u54ea\u513f\uff1f\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u4e0d\u77e5\u9053\u3002\u8d70\u5230\u54ea\u513f\uff0c\u5c31\u7b97\u54ea\u513f\uff01\u201d \u3000\u3000\u77f3\u749e\u9053\uff1a\u201c\u6768\u59d1\u5a18\uff0c\u4f60\u65e2\u7136\u662f\u6ca1\u6709\u5730\u65b9\u597d\u53bb\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u53ef\u4ee5\u5230\u6211\u4eec\u7684\u5c71\u585e\uff1f\u6211\u4eec\u662f\u8981\u62b5 \u5fa1\u8499\u53e4\u9791\u5b50\u7684\uff0c\u9791\u5b50\u4e5f\u662f\u4f60\u7684\u4ec7\u4eba\u4e0d\u662f\uff1f\u54b1\u4eec\u6709\u5171\u540c\u7684\u654c\u4eba\uff0c\u53c8\u4e3a\u4ec0\u4e48\u4e0d\u80fd\u540c\u5fc3\u5408\u529b\u62b5\u5fa1\u654c \u4eba\u5462\uff1f\u6768\u59d1\u5a18\uff0c\u5927\u654c\u5f53\u524d\uff0c\u4f60\u662f\u4e0d\u662f\u53ef\u4ee5\u6682\u65f6\u629b\u5f00\u79c1\u4eba\u7684\u4e8b\u60c5\uff0c\u51b7\u9759\u5730\u518d\u60f3\u4e00\u60f3\uff01\u201d \u3000\u3000\u77f3\u749e\u8bf4\u5f97\u5341\u5206\u8bda\u6073\uff0c\u6768\u5a49\u4e0d\u7981\u4e3a\u4e4b\u611f\u52a8\uff0c\u5fc3\u60f3\uff1a\u201c\u662f\u5440\uff0c\u51ed\u6211\u4e00\u4eba\u4e4b\u529b\uff0c\u4e5f\u662f\u51b3\u8ba1\u6740\u4e0d \u4e86\u4f59\u4e00\u4e2d\u3002\u201d \u3000\u3000\u6768\u5a49\u60f3\u4e86\u4e00\u4f1a\uff0c\u8bf4\u9053\uff1a\u201c\u77f3\u5927\u54e5\uff0c\u6211\u53ef\u4ee5\u5230\u4f60\u4eec\u7684\u5c71\u5be8\uff0c\u4f46\u4f60\u5f97\u4f9d\u6211\u4e24\u4ef6\u4e8b\u60c5\u3002\u201d \u3000\u3000\u77f3\u749e\u9053\uff1a\u201c\u597d\uff0c\u4f60\u8bf4\u5427\uff01\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u7b2c\u4e00\u4ef6\uff0c\u6211\u6539\u626e\u7537\u5b50\u6295\u5954\u4f60\u4eec\u7684\u5c71\u5be8\u4f5c\u4e2a\u5c0f\u55bd\u5175\uff0c\u4f60\u53ea\u8d1f\u5f15\u8350\u4e4b\u8d23\uff0c\u53ef\u4e0d\u8bb8 \u66b4\u9732\u6211\u7684\u8eab\u4efd\u3002\u6211\u4e5f\u4e0d\u8981\u4f5c\u4ec0\u4e48\u5934\u76ee\u3002\u201d \u3000\u3000\u77f3\u749e\u7b11\u9053\uff1a\u201c\u8001\u767e\u59d3\u6295\u5954\u6211\u4eec\u7684\u5c71\u5be8\u7684\u6bcf\u5929\u90fd\u6709\uff0c\u4f60\u8981\u5f53\u4e2a\u5c0f\u55bd\u5175\u5bb9\u6613\u5f97\u5f88\u3002\u4f46\u6211\u4e0d\u660e \u767d\u4f60\u4e3a\u4ec0\u4e48\u8981\u8fd9\u6837\u505a\u3002\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u4e0d\u613f\u610f\u6709\u4efb\u4f55\u4eba\u77e5\u9053\u6211\u7684\u8eab\u4efd\u6765\u5386\uff0c\u9664\u4f60\u4e4b\u5916\u3002\u201d \u3000\u3000\u77f3\u749e\u9053\uff1a\u201c\u597d\u5427\uff0c\u6211\u7b54\u5e94\u4f60\u3002\u7b2c\u4e8c\u4ef6\u5462\uff1f\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u4f60\u5343\u4e07\u4e0d\u80fd\u544a\u8bc9\u674e\u601d\u5357\uff0c\u8bf4\u6211\u8fd8\u6d3b\u5728\u4eba\u95f4\uff01\u5f53\u7136\u6211\u5728\u5c71\u5be8\u7684\u4e8b\uff0c\u66f4\u4e0d\u80fd\u544a\u8bc9 \u4ed6\uff01\u201d \u3000\u3000\u77f3\u749e\u9053\uff1a\u201c\u597d\u5427\uff0c\u6211\u5c31\u53ea\u544a\u8bc9\u4ed6\uff0c\u6211\u66fe\u7ecf\u5728\u8fb9\u5883\u90a3\u4e2a\u5c0f\u9547\u78b0\u89c1\u8fc7\u4f60\u8fd9\u4ef6\u4e8b\uff0c\u522b\u7684\u6211\u90fd\u4e0d \u8bf4\u3002\u4f46\u6211\u4e5f\u4e0d\u80fd\u9a97\u4ed6\uff0c\u8bf4\u4f60\u5df2\u7ecf\u6b7b\u4e86\u3002\u201d\u6768\u5a49\u7684\u5fc3\u4e8b\u88ab\u77f3\u749e\u6b64\u65f6\u662f\u731c\u5230\u51e0\u5206\u3002 \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u4e5f\u597d\uff0c\u6211\u662f\u6b7b\u662f\u751f\uff0c\u8ba9\u4ed6\u81ea\u5df1\u731c\u6d4b\u3002\u541b\u5b50\u4e00\u8a00\uff0c\u5feb\u9a6c\u4e00\u97ad\uff0c\u54b1\u4eec\u5c31\u8fd9\u6837\u8bf4\u8bdd \u4e86\uff01\u201d \u3000\u3000\u539f\u6765\u6768\u5a49\u662f\u6253\u7b97\u5f53\u4e2a\u55bd\u5175\uff0c\u597d\u5c31\u8fd1\u89c2\u5bdf\u674e\u601d\u5357\u7684\u52a8\u9759\uff0c\u5018\u82e5\u53d1\u89c9\u674e\u601d\u5357\u771f\u7684\u662f\u7231\u5b5f\u660e \u971e\uff0c\u5979\u5c31\u51c6\u5907\u5728\u6218\u4e8b\u8fc7\u540e\uff0c\u98d8\u7136\u8fdc\u53bb\uff0c\u6c38\u8fdc\u4e5f\u4e0d\u5728\u674e\u601d\u5357\u8ddf\u524d\u9732\u9762\u3002 \u3000\u3000\u4e24\u4eba\u8bf4\u5b9a\u4e4b\u540e\uff0c\u6768\u5a49\u4fbf\u4e0e\u77f3\u749e\u540c\u8d74\u5c60\u51e4\u7684\u5c71\u5be8\u3002\u5728\u8def\u4e0a\u4e00\u4e2a\u5c0f\u9547\u4f4f\u4e86\u4e00\u5bb5\uff0c\u6539\u4e86\u7537\u88c5\u3002 \u5979\u5df2\u7ecf\u6709\u8fc7\u4e00\u6b21\u5973\u626e\u7537\u88c5\u7684\u7ecf\u9a8c\uff0c\u8fd9\u6b21\u626e\u4f5c\u4e00\u4e2a\u6c49\u4eba\uff0c\u5f53\u7136\u66f4\u662f\u9a7e\u8f7b\u5c31\u719f\u3002 \u3000\u3000\u77f3\u749e\u9075\u5b88\u8bfa\u8a00\uff0c\u628a\u5979\u5e26\u5230\u4e86\u5c71\u5be8\u4e4b\u540e\uff0c\u679c\u7136\u5b88\u53e3\u5982\u74f6\u3002\u4ed6\u66ff\u6768\u5a49\u8bbe\u60f3\u5f97\u5f88\u5468\u5230\uff0c\u8ba9\u5979\u5145 \u5f53\u4e00\u4e2a\u77ad\u671b\u54e8\u7684\u54e8\u5175\u3002\u4e86\u671b\u54e8\u662f\u4e00\u4eba\u8d1f\u8d23\u7684\uff0c\u53ea\u987b\u548c\u53e6\u5916\u51e0\u4e2a\u8001\u5175\u8f6e\u7559\u503c\uff0c\u5374\u4e0d\u5fc5\u548c\u5927\u4f19\u513f \u6df7\u5728\u4e00\u8d77\u3002 \u3000\u3000\u77f3\u749e\u66ff\u6768\u5a49\u5b89\u6392\u59a5\u5f53\u4e4b\u540e\uff0c\u8fd9\u624d\u53bb\u89c1\u5c60\u51e4\u3002 \u3000\u3000\u8e0f\u5165\u805a\u4e49\u5385\uff0c\u53ea\u89c1\u5c60\u51e4\u6b63\u5728\u966a\u5ba2\u4eba\u8bf4\u8bdd\uff0c\u674e\u601d\u5357\u548c\u5b5f\u660e\u971e\u4e5f\u90fd\u5728\u5ea7\u3002\u90a3\u56db\u4e2a\u5ba2\u4eba\u662f\u996e\u9a6c \u5ddd\u7684\u8463\u5f00\u5c71\uff0c\u8df3\u864e\u6da7\u7684\u9093\u98de\uff0c\u91ce\u732a\u6797\u7684\u80e1\u9b41\u548c\u74e6\u5c97\u5be8\u7684\u674e\u65ed\u3002\u8fd9\u56db\u4e2a\u4eba\u90fd\u662f\u5be8\u4e3b\u6216\u526f\u5be8\u4e3b\u7684 \u8eab\u4efd\u3002 \u3000\u3000\u5c60\u51e4\u7b11\u9053\uff1a\u201c\u77f3\u5e08\u54e5\uff0c\u4f60\u6251\u4e86\u4e2a\u7a7a\u5427\uff1f\u5b5f\u59d0\u59d0\u5df2\u7ecf\u66ff\u54b1\u4eec\u63a5\u4e86\u674e\u5927\u54e5\u4e0a\u5c71\u4e86\u3002\u201d\u77f3\u749e\u4e00 \u6765\u56e0\u4e3a\u6709\u5ba2\u4eba\u5728\u5ea7\uff0c\u4e8c\u6765\u4ed6\u7b54\u5e94\u4e86\u6768\u5a49\u4fdd\u5b88\u79d8\u5bc6\uff0c\u4e0d\u4fbf\u7ec6\u8bf4\u6b64\u884c\u7684\u7ecf\u8fc7\uff0c\u5f53\u4e0b\u542b\u7b11\u7684\u8bf4\u4e86\u51e0 \u53e5\u5ba2\u5957\u7684\u8bdd\uff0c\u4e0e\u674e\u601d\u5357\u3001\u5b5f\u660e\u971e\u4ee5\u53ca\u56db\u4f4d\u5ba2\u4eba\u5206\u522b\u89c1\u8fc7\u4e86\u793c\u3002 \u3000\u3000\u5c60\u51e4\u8bf4\u9053\uff1a\u201c\u77f3\u5e08\u54e5\uff0c\u4f60\u56de\u6765\u5f97\u6b63\u597d\u3002\u6211\u7ea6\u4e86\u5341\u516b\u5bb6\u585e\u4e3b\uff0c\u660e\u65e5\u5c31\u5728\u54b1\u4eec\u8fd9\u513f\u805a\u4f1a\uff0c\u5171 \u5546\u6297\u654c\u5927\u8ba1\u3002\u201d\u77f3\u749e\u8fd9\u624d\u77e5\u9053\u8fd9\u56db\u4f4d\u5be8\u4e3b\u662f\u5148\u671f\u5230\u8fbe\u7684\u3002 \u3000\u3000\u8463\u5f00\u5c71\u662f\u5c60\u767e\u57ce\u751f\u524d\u7684\u597d\u53cb\uff0c\u4f3c\u662f\u6709\u70b9\u5fe7\u5f62\u4e8e\u8272\uff0c\u8bf4\u9053\uff1a\u201c\u5c60\u59d1\u5a18\uff0c\u4f60\u53ef\u6709\u9080\u8bf7\u6df3\u4e8e\u8191 \u4e48\uff1f\u201d \u3000\u3000\u5c60\u51e4\u9053\uff1a\u201c\u4ed6\u4eec\u7236\u5b50\u65e9\u5df2\u7ecf\u4e0d\u662f\u54b1\u4eec\u4e00\u6761\u8def\u4e0a\u7684\u4eba\u4e86\uff0c\u8463\u4f2f\u4f2f\u8fd8\u6ca1\u77e5\u9053\u4e48\uff1f\u201d \u3000\u3000\u8463\u5f00\u5c71\u9053\uff1a\u201c\u6211\u77e5\u9053\u3002\u4e0d\u8fc7\u2014\u2014\u201d \u3000\u3000\u5c60\u51e4\u9053\uff1a\u201c\u4e0d\u8fc7\u600e\u4e48\uff1f\u201d \u3000\u3000\u8463\u5f00\u5c71\u9053\uff1a\u201c\u7eff\u6797\u4e2d\u7684\u670b\u53cb\u672a\u5fc5\u4e2a\u4e2a\u90fd\u80fd\u591f\u660e\u767d\u5927\u662f\u5927\u975e\uff0c\u4ee4\u5c0a\u53bb\u4e16\u4e4b\u540e\uff0c\u7eff\u6797\u4e2d\u8d44\u671b \u6700\u9ad8\u7684\u5c31\u662f\u6df3\u4e8e\u5468\u4e86\u3002\u636e\u6211\u4eec\u6240\u77e5\uff0c\u4ed6\u5f88\u6709\u610f\u4e8e\u7ee7\u4efb\u7eff\u6797\u76df\u4e3b\uff0c\u5982\u4eca\u6b63\u5728\u5927\u8086\u6d3b\u52a8\u3002\u660e\u65e5\u4e4b \u4f1a\uff0c\u4f60\u6ca1\u6709\u8bf7\u4ed6\uff0c\u53ea\u6015\u4ed6\u4f1a\u4ece\u4e2d\u7834\u574f\u3002\u201d \u3000\u3000\u5c60\u51e4\u9053\uff1a\u201c\u6211\u8bf7\u4ed6\u4ed6\u4e5f\u8981\u4ece\u4e2d\u7834\u574f\u7684\uff0c\u4e0d\u5982\u5e72\u8106\u4e0d\u8bf7\u8fd8\u597d\u3002\u4f46\u4f60\u4ee5\u4e3a\u4ed6\u4f1a\u5982\u4f55\u7834\u574f \u5462\u3002\u201d \u3000\u3000\u8463\u5f00\u5c71\u9053\uff1a\u201c\u6211\u4eec\u90fd\u662f\u613f\u610f\u62e5\u6234\u59d1\u5a18\u7ee7\u627f\u4ee4\u5c0a\u4e4b\u4f4d\u7684\uff1b\u4f46\u96be\u4fdd\u6ca1\u6709\u4e00\u4e9b\u4eba\u53d7\u4e86\u6df3\u4e8e\u5468\u7684 \u6050\u5413\uff0c\u4e0d\u6562\u6765\u5f00\u4f1a\u3002\u6765\u4e86\u7684\u53ea\u6015\u4e5f\u672a\u5fc5\u5927\u5bb6\u9f50\u5fc3\u3002\u201d \u3000\u3000\u5c60\u51e4\u8bf4\u9053\uff1a\u201c\u6211\u5e74\u8f7b\u8bc6\u6d45\uff0c\u7eff\u6797\u76df\u4e3b\u4e4b\u4f4d\uff0c\u90a3\u662f\u51b3\u8ba1\u4e0d\u6562\u5f53\u7684\u3002\u4f46\u5f53\u52a1\u4e4b\u6025\u5e76\u975e\u63a8\u9009\u7eff \u6797\u76df\u4e3b\uff0c\u800c\u662f\u5171\u5546\u6297\u654c\u5927\u8ba1\uff0c\u6df3\u4e8e\u5468\u662f\u4e00\u5b9a\u8981\u60f3\u6cd5\u7834\u574f\u54b1\u4eec\u8fd9\u4e2a\u96c6\u4f1a\u7684\uff0c\u8fd9\u4e2a\u6211\u65e9\u4f30\u8ba1\u5728 \u5185\u3002\u660e\u65e5\u4e4b\u4f1a\uff0c\u5341\u516b\u5bb6\u5be8\u4e3b\u6765\u5f97\u591a\u5c11\u5c31\u7b97\u591a\u5c11\u3002\u610f\u89c1\u4e0d\u540c\uff0c\u5c31\u8bf4\u5230\u76f8\u540c\u4e3a\u6b62\uff0c\u679d\u8282\u4e4b\u4e89\uff0c\u53ef \u4ee5\u62bc\u540e\uff0c\u53ea\u6c42\u5927\u5bb6\u540c\u610f\u8981\u62b5\u6297\u8499\u53e4\u9791\u5b50\u7684\u5165\u4fb5\u5c31\u884c\u3002\u6211\u76f8\u4fe1\u5927\u9053\u7406\u603b\u662f\u8bf4\u5f97\u901a\u7684\u3002\u201d \u3000\u3000\u8463\u5f00\u5c71\u89c1\u5c60\u51e4\u751a\u6709\u51b3\u65ad\uff0c\u5927\u4e3a\u6b23\u6170\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u771f\u662f\u6709\u5176\u7236\u5fc5\u6709\u5176\u5973\uff0c\u8d24\u4f84\u5973\uff0c\u4e0d\u662f \u6211\u5938\u8d5e\uff0c\u4f60\u529f\u592b\u7684\u672c\u9886\u6216\u8bb8\u4e0d\u53ca\u4ee4\u5c0a\uff0c\u4f46\u8fd9\u4efd\u89c1\u8bc6\uff0c\u8fd9\u4efd\u6bc5\u529b\uff0c\u5374\u662f\u4e00\u70b9\u4e0d\u8f93\u4e8e\u4ee4\u5c0a\u4e86\u3002\u65e0 \u8bba\u5982\u4f55\uff0c\u6211\u4e00\u5b9a\u8981\u62e5\u6234\u4f60\u505a\u7eff\u6797\u5be8\u4e3b\u3002\u201d \u3000\u3000\u90a3\u53e5\u4fd7\u8bed\u672c\u6765\u662f\u201c\u6709\u5176\u7236\u5fc5\u6709\u5176\u5b50\u201d\uff0c\u8463\u5f00\u5c71\u628a\u4e00\u4e2a\u201c\u5b50\u201d\u5b57\u6539\u4e3a\u201c\u5973\u201d\u5b57\uff0c\u5c60\u51e4\u542c \u4e86\uff0c\u4e0d\u89c9\u9eef\u7136\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u54e5\u54e5\u4e0d\u8096\uff0c\u4f17\u5bb6\u53d4\u4f2f\u53ea\u6015\u4e5f\u90fd\u662f\u77e5\u9053\u7684\u4e86\u3002\u201d \u3000\u3000\u5c60\u51e4\u60f3\u8d77\u54e5\u54e5\uff0c\u5fc3\u91cc\u751a\u4e3a\u96be\u8fc7\uff0c\u542c\u4e86\u8463\u5f00\u5c71\u79f0\u8d5e\u5979\u7684\u8bf4\u8bdd\uff0c\u8138\u4e0a\u6b8a\u65e0\u559c\u60a6\u4e4b\u8272\uff0c\u8bf4\u9053\uff1a \u201c\u8463\u4f2f\u4f2f\u592a\u8fc7\u5938\u5956\u6211\u4e86\u3002\u4f46\u8bf7\u8463\u4f2f\u4f2f\u5343\u4e07\u4e0d\u53ef\u63a8\u8350\u6211\u4f5c\u7eff\u6797\u76df\u4e3b\uff0c\u660e\u65e5\u4e4b\u4f1a\uff0c\u6211\u81ea\u6709\u4e3b \u5f20\u3002\u201d\u8463\u5f00\u5c71\u7b49\u4eba\u53ea\u9053\u5979\u662f\u5ba2\u6c14\u7684\u8bf4\u8bdd\uff0c\u5927\u5bb6\u90fd\u662f\u5fae\u5fae\u4e00\u7b11\u3002 \u3000\u3000\u5c60\u51e4\u9053\uff1a\u201c\u56db\u4f4d\u4f2f\u4f2f\u8fdc\u6765\uff0c\u8def\u9014\u52b3\u7d2f\uff0c\u8bf7\u65e9\u6b47\u6073\u3002\u77f3\u5e08\u54e5\u3002\u8bf7\u4f60\u966a\u56db\u4f4d\u4f2f\u4f2f\u524d\u5f80\u5ba2 \u6808\u3002\u201d \u3000\u3000\u77f3\u749e\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6768\u5a49\u88ab\u6211\u90a3\u4e0d\u8096\u7684\u5927\u5e08\u5144\u8bec\u8511\uff0c\u5979\u53d7\u7684\u8fd9\u4e2a\u51a4\u6789\uff0c\u6211\u662f\u5e94\u8be5\u66ff\u5979\u6d17\u5237 \u7684\u3002\u4f46\u4eca\u5929\u6050\u6015\u662f\u4e0d\u884c\u4e86\uff0c\u597d\u5728\u6768\u5a49\u5df2\u5230\u5c71\u5be8\uff0c\u8fdf\u65e9\u5b9a\u4f1a\u6c34\u843d\u77f3\u51fa\uff0c\u4e5f\u4e0d\u5fc5\u6025\u5728\u4e00\u65f6\u3002\u5f85\u660e \u65e5\u4e4b\u4f1a\u8fc7\u540e\uff0c\u627e\u4e2a\u673a\u4f1a\uff0c\u6211\u518d\u548c\u674e\u601d\u5357\u8bf4\u5427\u3002\u201d \u3000\u3000\u7b2c\u4e8c\u5929\u5404\u4e2a\u5c71\u5be8\u7684\u5be8\u4e3b\u7ee7\u7eed\u6765\u5230\uff0c\u5230\u4e86\u4e2d\u5348\u65f6\u5206\uff0c\u5df2\u7ecf\u6765\u4e86\u5341\u4e09\u5bb6\u5be8\u4e3b\u3002\u5927\u4f1a\u662f\u5b9a\u5728\u4e2d \u5348\u5f00\u59cb\u7684\uff0c\u5c60\u51e4\u672c\u6765\u4e0d\u6307\u671b\u5341\u516b\u5bb6\u5be8\u4e3b\u5c3d\u90fd\u5982\u7ea6\u800c\u6765\uff0c\u6765\u4e86\u5341\u4e09\u5bb6\u5be8\u4e3b\uff0c\u5979\u5df2\u662f\u559c\u51fa\u671b\u5916\u3002 \u5f53\u4e0b\u6309\u7167\u539f\u5b9a\u7684\u8ba1\u5212\uff0c\u51c6\u65f6\u5f00\u4f1a\u3002 \u3000\u3000\u6b63\u5728\u5404\u4eba\u5c31\u5ea7\u4e4b\u9645\uff0c\u5ffd\u89c1\u5de1\u5c71\u7684\u4e00\u4e2a\u5c0f\u5934\u76ee\uff0c\u6ee1\u9762\u60ca\u60f6\u4e4b\u8272\uff0c\u5306\u5306\u8dd1\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u5be8 \u4e3b\uff0c\u4e0d\u597d\u4e86\uff01\u201d\u5c60\u51e4\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\u60c5\u5982\u6b64\u60ca\u614c\uff01\u201d \u3000\u3000\u5c0f\u5934\u76ee\u9053\uff1a\u201c\u6df3\u4e8e\u5468\u90a3\u53ae\u95ef\u8fdb\u5c71\u5be8\uff0c\u6211\u4eec\u62e6\u4ed6\u4e0d\u4f4f\u3002\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u53ea\u542c\u5f97\u6df3\u4e8e\u5468\u7684\u58f0\u97f3 \u5df2\u5728\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8001\u592b\u4e0d\u8bf7\u81ea\u6765\uff0c\u8d24\u4f84\u5973\u60f3\u6765\u4e0d\u81f3\u62d2\u7edd\u6211\u5427\uff01\u201d \u3000\u3000\u6df3\u4e8e\u5468\u4e00\u9a6c\u5f53\u5148\uff0c\u5927\u8e0f\u6b65\u5730\u8d70\u5165\u4e86\u805a\u4e49\u5385\u3002\u540e\u9762\u8ddf\u6709\u5341\u6765\u4e2a\u4eba\uff0c\u5176\u4e2d\u4e94\u4e2a\u662f\u5728\u5c60\u51e4\u8bf7\u5ba2 \u540d\u5355\u4e4b\u5185\u7684\uff0c\u53e6\u5916\u516d\u4e03\u4e2a\u4eba\uff0c\u5219\u662f\u6df3\u4e8e\u5468\u7684\u515a\u7fbd\uff0c\u8eab\u4efd\u4e5f\u90fd\u662f\u4e00\u5be8\u4e4b\u4e3b\u3002 \u3000\u3000\u5c60\u51e4\u867d\u7136\u65e9\u5c31\u60f3\u5230\u6df3\u4e8e\u5468\u5b9a\u8981\u4ece\u4e2d\u7834\u574f\uff0c\u4f46\u5374\u8fd8\u6ca1\u6709\u60f3\u5230\uff0c\u4ed6\u7ade\u662f\u5e26\u4e86\u5927\u961f\u4eba\u9a6c\uff0c\u516c\u7136 \u800c\u6765\u3002\u53cc\u65b9\u65d7\u9f13\u76f8\u5f53\uff0c\u6210\u4e86\u9488\u950b\u76f8\u5bf9\u7684\u5f62\u52bf\uff0c\u8fd9\u4e2a\u5f62\u52bf\uff0c\u7a0d\u4e00\u5e94\u4ed8\u4e0d\u5f53\uff0c\u53ea\u6015\u5c31\u4f1a\u917f\u6210\u4e00\u573a \u7eff\u6797\u4e2d\u7684\u5927\u706b\u5e76\uff01 \u3000\u3000\u5c60\u51e4\u5f3a\u81ea\u9547\u5b9a\u5fc3\u795e\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4e0d\u77e5\u6df3\u4e8e\u5be8\u4e3b\u6b64\u6765\uff0c\u6709\u4f55\u6307\u6559\uff1f\u201d \u3000\u3000\u6df3\u4e8e\u5468\u9053\uff1a\u201c\u4e00\u662f\u4e3a\u79c1\uff0c\u4e8c\u662f\u4e3a\u516c\u3002\u201d \u3000\u3000\u5c60\u51e4\u9053\uff1a\u201c\u613f\u95fb\u5176\u8be6\u3002\u201d \u3000\u3000\u6df3\u4e8e\u5468\u9053\uff1a\u201c\u5c0f\u513f\u7740\u4e86\u8d35\u5be8\u7684\u77f3\u5934\u9886\u4e00\u652f\u6bd2\u9f99\u9556\uff0c\u4fa5\u5e78\u672a\u6b7b\uff0c\u60f3\u5411\u77f3\u5934\u9886\u518d\u8ba8\u6559\u8ba8\u6559\u3002 \u8bf7\u95ee\u77f3\u5934\u9886\u5728\u8fd9\u91cc\u4e48\uff1f\u201d \u3000\u3000\u77f3\u749e\u7ad9\u4e86\u51fa\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4ee4\u90ce\u7684\u786e\u662f\u53d7\u4e86\u6211\u7684\u9556\u4f24\uff0c\u4f46\u4f60\u53ef\u77e5\u9053\u662f\u56e0\u4f55\u6545\u6211\u624d\u4f1a \u7528\u6bd2\u9556\u6253\u4ed6\u7684\u4e48\uff1f\u201d \u3000\u3000\u6df3\u4e8e\u5468\u9053\uff1a\u201c\u6211\u4e0d\u7ba1\u662f\u56e0\u4f55\u6545\uff0c\u6c5f\u6e56\u4e0a\u4e00\u8a00\u4e0d\u5408\uff0c\u5200\u67aa\u76f8\u89c1\u4e5f\u662f\u5e38\u4e8b\u3002\u4f46\u6211\u4e0e\u4f60\u7684\u5e08\u7236 \u751f\u524d\u591a\u5c11\u6709\u70b9\u4ea4\u60c5\uff0c\u4f60\u82e5\u662f\u4e0e\u5c0f\u513f\u660e\u5200\u660e\u67aa\u5730\u6bd4\u6b66\u4e5f\u8fd8\u7f62\u4e86\u3002\u8fd9\u79cd\u89c1\u8840\u5c01\u5589\u7684\u6bd2\u9f99\u9556\u6253\u4ed6\uff0c \u603b\u662f\u4e0d\u8be5\uff01\u201d \u3000\u3000\u77f3\u749e\u6012\u6c14\u4e0a\u51b2\uff0c\u4ea2\u58f0\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u4f60\u65e2\u7136\u86ee\u4e0d\u8bb2\u7406\uff0c\u90a3\u5c31\u51b2\u7740\u6211\u6765\uff0c\u4e3a\u4f60\u7684\u5b9d\u8d1d\u513f\u5b50\u62a5 \u4ec7\u5427\u3002\u201d \u3000\u3000\u6df3\u4e8e\u5468\u53d8\u4e86\u9762\u8272\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u5e08\u7236\u5728\u751f\u524d\u4e5f\u4e0d\u6562\u5bf9\u6211\u65e0\u793c\uff0c\u4f60\u662f\u4ec0\u4e48\u4e1c\u897f\uff0c\u80c6\u6562\u5411\u6211 \u6311\u6218\uff1f\u54fc\uff0c\u4f60\u7684\u6bd2\u9f99\u9556\u867d\u7136\u5389\u5bb3\uff0c\u4e5f\u8fd8\u672a\u80fd\u53d6\u4e86\u6211\u513f\u5b50\u6027\u547d\u3002\u6211\u8ba9\u4f60\u548c\u4ed6\u518d\u6253\u4e00\u573a\uff0c\u53ea\u8981\u4f60 \u4e0d\u662f\u501a\u591a\u4e3a\u80dc\uff0c\u6211\u51b3\u4e0d\u63d2\u624b\uff01\u201d \u3000\u3000\u8463\u5f00\u5c71\u5fc3\u60f3\uff1a\u201c\u6b64\u4e8b\u82e5\u5728\u5e73\u5e38\u7684\u65e5\u5b50\u53d1\u751f\uff0c\u5f53\u7740\u4f17\u5bb6\u5be8\u4e3b\u7684\u9762\uff0c\u81ea\u8be5\u5206\u4e2a\u662f\u975e\u66f2\u76f4\uff0c\u4f46 \u4eca\u65e5\u4e4b\u4f1a\uff0c\u4f55\u7b49\u91cd\u8981\uff0c\u5c82\u80fd\u4e3a\u4e86\u4ed6\u4eec\u7684\u79c1\u4e8b\u803d\u8bef\uff1f\u201d\u4e8e\u662f\u4fbf\u529d\u89e3\u9053\uff1a\u201c\u6df3\u4e8e\u5be8\u4e3b\u65e2\u662f\u8fd9\u4e48 \u8bf4\uff0c\u90a3\u5c31\u5f85\u4ee4\u90ce\u7684\u4f24\u597d\u4e86\uff0c\u518d\u53eb\u77f3\u749e\u4e0a\u8d35\u5be8\u8d54\u7f6a\u4e5f\u8fd8\u4e0d\u8fdf\u3002\u201d \u3000\u3000\u77f3\u749e\u9053\uff1a\u201c\u7406\u4e8f\u7684\u4e0d\u662f\u6211\uff0c\u8981\u6211\u8d54\u7f6a\uff0c\u4e07\u4e07\u4e0d\u80fd\uff01\u201d \u3000\u3000\u5404\u5bb6\u5be8\u4e3b\u90fd\u662f\u548c\u8463\u5f00\u5c71\u540c\u6837\u5fc3\u601d\uff0c\u9f50\u58f0\u529d\u89e3\u9053\uff1a\u201c\u662f\u8c01\u7406\u76f4\u7406\u4e8f\u90fd\u597d\uff0c\u8fc7\u4e86\u4eca\u5929\u518d \u8bf4\u3002\u201d\u8a00\u4e0b\u4e4b\u610f\uff0c\u5373\u662f\u5f85\u8fd9\u6b21\u5927\u4f1a\u8fc7\u540e\uff0c\u5927\u5bb6\u81ea\u4f1a\u7ed9\u4ed6\u4eec\u8bc4\u7406\u3002 \u3000\u3000\u77f3\u749e\u731b\u7136\u4e00\u7701\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4e0d\u80fd\u56e0\u6211\u4e00\u65f6\u4e4b\u6c14\uff0c\u803d\u6401\u4e86\u5927\u5bb6\u7684\u6b63\u4e8b\uff0c\u518d\u8bf4\uff0c\u542c \u8fd9\u8001\u8d3c\u7684\u8a00\u8bed\uff0c\u6df3\u4e8e\u8191\u4f3c\u662f\u5df2\u628a\u6211\u548c\u6768\u5a49\u8054\u624b\u5bf9\u4ed8\u4ed6\u7684\u4e8b\u60c5\u8bf4\u4e86\uff0c\u5374\u4e0d\u77e5\u4ed6\u4f55\u4ee5\u4e0d\u63d0\u6768\u5a49\uff1f \u4ed6\u65e2\u7136\u4e0d\u63d0\u6768\u5a49\uff0c\u6211\u53c8\u4f55\u5fc5\u5f53\u4f17\u6392\u9732\u51fa\u6765\uff1f\u201d\u4ed6\u662f\u7b54\u5e94\u8fc7\u6768\u5a49\u4fdd\u5b88\u79d8\u5bc6\u7684\uff0c\u60f3\u5230\u4e86\u8fd9\u5c42\uff0c\u9ed8 \u7136\u4e0d\u8bed\u3002 \u3000\u3000\u5c60\u51e4\u5fcd\u4f4f\u4e86\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u5427\uff0c\u8fd9\u4ef6\u79c1\u4e8b\u6682\u4e14\u653e\u4e0b\uff0c\u4e0d\u77e5\u6df3\u4e8e\u5be8\u4e3b\u6240\u8bf4\u7684\u516c\u4e8b\u53c8\u662f\u90a3 \u6869\uff1f\u201d \u3000\u3000\u6df3\u4e8e\u5468\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u5c60\u59d1\u5a18\u4f60\u9080\u96c6\u4e86\u5341\u516b\u5bb6\u5be8\u4e3b\u5230\u6b64\u805a\u4f1a\uff01\u8001\u592b\u597d\u6b79\u4e5f\u662f\u4e00\u5bb6\u5be8\u4e3b\uff0c\u4e0d \u77e5\u4f55\u4ee5\u89c1\u62d2\uff1f\u8fde\u98ce\u58f0\u4e5f\u7792\u7740\u8001\u592b\uff1f\u201d \u3000\u3000\u5c60\u51e4\u9053\uff1a\u201c\u6b63\u56e0\u4f60\u662f\u7eff\u6797\u524d\u8f88\uff0c\u6211\u4eec\u5c0f\u8f88\u7684\u805a\u4f1a\uff0c\u4e0d\u6562\u60ca\u52a8\u4f60\u8001\u4eba\u5bb6\u3002\u8fd9\u6b21\u6211\u4eec\u51c6\u5907\u8c08 \u7684\u4e5f\u5e76\u975e\u7eff\u6797\u4e4b\u4e8b\uff0c\u4e0d\u8fc7\u662f\u5fd7\u540c\u9053\u5408\u7684\u670b\u53cb\uff0c\u805a\u5728\u4e00\u8d77\uff0c\u968f\u4fbf\u804a\u804a\u7f62\u4e86\u3002\u201d\u8fd9\u756a\u8bdd\u5916\u5706\u5185 \u65b9\uff0c\u9690\u9690\u5305\u542b\u7740\u201c\u9053\u4e0d\u540c\u4e0d\u76f8\u4e3a\u8c0b\u201d\u8fd9\u5c42\u610f\u601d\u3002 \u3000\u3000\u6df3\u4e8e\u5468\u6253\u4e86\u4e2a\u54c8\u54c8\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u60f3\u60ca\u52a8\u6211\uff0c\u4f46\u6211\u5982\u4eca\u5df2\u7ecf\u6765\u4e86\uff0c\u662f\u8981\u8d76\u6211\u51fa\u53bb\u5462\uff0c\u8fd8 \u662f\u8ba9\u6211\u2018\u656c\u966a\u5931\u793c\u2019\uff1f\u6211\u53ef\u662f\u672a\u5fc5\u548c\u4f60\u5c60\u59d1\u5a18\u2018\u5fd7\u540c\u9053\u5408\u2019\u7684\u554a\uff01\u201d \u3000\u3000\u6df3\u4e8e\u5468\u7684\u4e00\u4e2a\u515a\u7fbd\u8ddf\u7740\u8bf4\u9053\uff1a\u201c\u5176\u5b9e\u82e5\u662f\u5927\u5bb6\u5fd7\u540c\u9053\u5408\uff0c\u5c60\u59d1\u5a18\uff0c\u4f60\u8fd9\u4e2a\u4f1a\u4e5f\u5927\u53ef\u4e0d\u5fc5 \u53ec\u5f00\u4e86\uff01\u201d\u8bcd\u950b\u5484\u5484\uff0c\u4f46\u4e5f\u4e0d\u80fd\u8bf4\u4ed6\u5168\u65e0\u63e1\u7406\u3002 \u3000\u3000\u5c60\u51e4\u4e0d\u613f\u8fd9\u6b21\u7684\u7eff\u6797\u5927\u4f1a\u906d\u4ed6\u7834\u574f\uff0c\u5fc3\u60f3\uff1a\u201c\u4e5f\u597d\uff0c\u4e14\u770b\u4ed6\u6709\u4ec0\u4e48\u6b6a\u7406\uff1f\u9053\u7406\u662f\u4e0d\u6015\u8fa9 \u8bba\u7684\uff0c\u5728\u4f1a\u4e2d\u63ed\u9732\u4ed6\u7684\u9762\u76ee\uff0c\u6b63\u662f\u4e8e\u6211\u6709\u5229\u3002\u201d\u4e8e\u662f\u8bf4\u9053\uff1a\u201c\u6211\u53ea\u6015\u8bf7\u4e0d\u52a8\u524d\u8f88\uff0c\u96be\u5f97\u4f60\u5c48 \u9a7e\u524d\u6765\uff0c\u6211\u4eec\u5f53\u7136\u662f\u6df1\u8868\u6b22\u8fce\u3002\u201d \u3000\u3000\u4e0d\u6599\u4e00\u6ce2\u672a\u5e73\uff0c\u4e00\u6ce2\u53c8\u8d77\uff0c\u5c60\u51e4\u521a\u8981\u5ba3\u5e03\u5f00\u4f1a\uff0c\u6df3\u4e8e\u5468\u5ffd\u9053\uff1a\u201c\u4e14\u6162\uff01\u201d\u5c60\u51e4\u9053\uff1a\u201c\u6df3 \u4e8e\u524d\u8f88\u6709\u4f55\u6307\u6559\uff1f\u201d\u6df3\u4e8e\u5468\u9053\uff1a\u201c\u4e3b\u4eba\u8fd8\u672a\u6765\u5230\uff0c\u6b64\u4f1a\u600e\u80fd\u5c31\u5f00\uff1f\u201d \u3000\u3000\u5c60\u51e4\u6014\u4e86\u4e00\u6014\uff0c\u8bf4\u9053\uff1a\u201c\u4ec0\u4e48\u4e3b\u4eba\uff1f\u201d \u3000\u3000\u6df3\u4e8e\u5468\u9053\uff1a\u201c\u6b64\u6b21\u4e4b\u4f1a\uff0c\u662f\u5426\u8d35\u5be8\u4f5c\u7684\u4e1c\u9053\u4e3b\uff1f\u201d\u5c60\u51e4\u9053\uff1a\u201c\u4e0d\u9519\uff01\u201d\u6df3\u4e8e\u5468\u51b7\u51b7\u8bf4 \u9053\uff1a\u201c\u90a3\u4e48\u5c31\u8be5\u8bf7\u4f60\u54e5\u54e5\u51fa\u6765\u5440\uff01\u201d \u3000\u3000\u8463\u5f00\u5c71\u9053\uff1a\u201c\u6df3\u4e8e\u524d\u8f88\u6709\u6240\u4e0d\u77e5\uff0c\u5c60\u5be8\u4e3b\u53bb\u4e16\u4e4b\u540e\uff0c\u6b64\u5be8\u4e4b\u4e3b\u5df2\u7531\u5c60\u59d1\u5a18\u7ee7\u4efb\uff01\u201d\u5176\u5b9e \u6df3\u4e8e\u5468\u4e43\u662f\u660e\u77e5\u6545\u95ee\uff0c\u8463\u5f00\u5c71\u4e5f\u77e5\u9053\u4ed6\u662f\u6709\u5fc3\u6311\u8845\u3002\u4e0d\u8fc7\u8463\u5f00\u5c71\u548c\u5c60\u51e4\u90fd\u4e0d\u613f\u610f\u4f1a\u672a\u5f00\u6210\u5148 \u95f9\u8d77\u6765\uff0c\u662f\u4ee5\u867d\u7136\u77e5\u9053\u4ed6\u662f\u660e\u77e5\u6545\u95ee\uff0c\u4e5f\u53ea\u597d\u8010\u5fc3\u89e3\u91ca\u3002 \u3000\u3000\u6df3\u4e8e\u5468\u53cc\u773c\u4e00\u7ffb\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u7eff\u6797\u89c4\u77e9\uff0c\u4e00\u5be8\u4e4b\u4e3b\uff0c\u4e43\u662f\u7236\u6b7b\u5b50\u627f\uff0c\u5c60\u767e\u57ce\u5e76\u975e\u6ca1\u6709 \u513f\u5b50\uff0c\u4f3c\u4e4e\u8fd8\u672a\u8f6e\u5230\u4f60\u5c60\u59d1\u5a18\u4f5c\u4e3b\uff01\u201d \u3000\u3000\u5c60\u51e4\u9053\uff1a\u201c\u5bb6\u5144\u4e45\u5df2\u4e0d\u5728\u5c71\u5be8\uff0c\u4f17\u5934\u76ee\u4ee5\u5be8\u4e3b\u4e4b\u4f4d\u4e0d\u80fd\u4e45\u60ac\uff0c\u662f\u4ee5\u6211\u53ea\u597d\u52c9\u4e3a\u5176\u96be\uff0c\u5949 \u4e86\u5bb6\u6bcd\u4e4b\u547d\uff0c\u6682\u884c\u6444\u4f4d\u3002\u201d\u5c60\u51e4\u56e0\u4e3a\u4e0d\u613f\u610f\u628a\u5bb6\u4e11\u5916\u626c\uff0c\u53ea\u80fd\u5982\u6b64\u89e3\u91ca\u3002 \u3000\u3000\u6df3\u4e8e\u5468\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u4f60\u54e5\u54e5\u5982\u679c\u56de\u6765\uff0c\u8fd9\u5be8\u4e3b\u4e4b\u4f4d\uff0c\u8fd8\u662f\u5e94\u8be5\u7531\u4ed6\u7ee7\u4efb\u7684\u4e86\uff1f\u201d \u3000\u3000\u5c60\u767e\u57ce\u751f\u524d\u6700\u5f97\u529b\u7684\u624b\u4e0b\u5b8b\u94c1\u8f6e\u6b64\u65f6\u5df2\u5347\u4efb\u4e3a\u526f\u5be8\u4e3b\uff0c\u4ed6\u662f\u4e2a\u6027\u60c5\u66b4\u8e81\u7684\u4eba\uff0c\u542c\u4e86\u8fd9 \u8bdd\uff0c\u4e0d\u7531\u5f97\u52a8\u4e86\u6012\u6c14\uff0c\u970d\u5730\u7ad9\u4e86\u8d77\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u5c60\u59d1\u5a18\u7684\u5bb6\u4e8b\uff0c\u5916\u4eba\u4f3c\u4e4e\u4e0d\u5b9c\u591a\u95ee \u5427\uff1f\u201d \u3000\u3000\u6df3\u4e8e\u5468\u4e00\u5411\u5984\u81ea\u5c0a\u5927\uff0c\u5c24\u5176\u5728\u5c60\u767e\u57ce\u53bb\u4e16\u4e4b\u540e\uff0c\u4ed6\u65e9\u5c31\u4ee5\u76df\u4e3b\u81ea\u5c45\u3002\u5b8b\u94c1\u8f6e\u6012\u6c14\u51b2\u51b2\u5730 \u9876\u649e\u4e86\u4ed6\uff0c\u4f17\u4eba\u90fd\u4e0d\u7981\u6709\u70b9\u63d0\u5fc3\u540a\u80c6\uff0c\u53ea\u6015\u6df3\u4e8e\u5468\u5c31\u8981\u501f\u6545\u5927\u95f9\u8d77\u6765\u3002\u4e0d\u6599\u5927\u51fa\u4f17\u4eba\u610f\u5916\uff0c \u6df3\u4e8e\u5468\u5012\u6ca1\u6709\u53d1\u813e\u6c14\uff0c\u53cd\u800c\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u5012\u662f\u602a\u6211\u591a\u4e8b\u4e86\u3002\u4e0d\u8fc7\uff0c\u5c60\u59d1\u5a18\u7684 \u5bb6\u4e8b\uff0c\u6211\u7ba1\u4e0d\u5f97\u4e5f\u8fd8\u6709\u4eba\u7ba1\u5f97\uff01\u201d \u3000\u3000\u8bdd\u72b9\u672a\u4e86\uff0c\u5ffd\u542c\u5f97\u201c\u4e53\u201d\u7684\u4e00\u58f0\uff0c\u805a\u4e49\u5385\u672c\u6765\u5df2\u7ecf\u5173\u95ed\u4e86\u7684\u5927\u95e8\uff0c\u5ffd\u7136\u7ed9\u4eba\u8e22\u5f00\u3002\u4f17\u4eba \u5927\u5403\u4e00\u60ca\uff0c\u4e0d\u77e5\u662f\u8c01\u5982\u6b64\u5927\u80c6\uff0c\u62ac\u5934\u770b\u65f6\uff0c\u53ea\u89c1\u4e24\u4e2a\u4eba\u5e76\u80a9\u8d70\u5165\uff0c\u5176\u4e2d\u4e00\u4eba\u6b63\u662f\u5c60\u9f99\uff01\u53e6\u5916 \u4e00\u4e2a\uff0c\u5374\u662f\u8c01\u90fd\u8ba4\u4e0d\u5f97\u7684\u964c\u751f\u4eba\u3002 \u3000\u3000\u8463\u5f00\u5c71\u7b49\u4eba\u65b9\u59cb\u604d\u7136\u5927\u609f\uff0c\u539f\u6765\u6df3\u4e8e\u5468\u662f\u65e9\u5df2\u548c\u5c60\u9f99\u7ea6\u597d\u4e86\u7684\uff0c\u5c60\u9f99\u5982\u679c\u6ca1\u6709\u6df3\u4e8e\u5468\u6491 \u8170\uff0c\u5f53\u7136\u4e5f\u662f\u4e0d\u6562\u56de\u6765\u3002 \u3000\u3000\u674e\u601d\u5357\u9aa4\u7136\u770b\u89c1\u5c60\u9f99\uff0c\u4e0d\u7531\u5f97\u5446\u4e86\u4e00\u5446\uff0c\u5fc3\u4e2d\u53c8\u662f\u4f24\u75db\uff0c\u53c8\u662f\u60ca\u5947\uff0c\u6674\u81ea\u60f3\u9053\uff1a\u201c\u4ed6\u600e \u4e48\u4e5f\u5230\u8fd9\u91cc\u6765\u4e86\uff0c\u6768\u5a49\u6ca1\u6709\u548c\u4ed6\u540c\u6765\uff0c\u8fd9\u53c8\u662f\u4f55\u6545\u5462\uff1f\u96be\u9053\u5979\u5df2\u7ecf\u77e5\u9053\u4e86\u6211\u5728\u8fd9\u91cc\u4e86\u4e48\uff1f\u201d \u3000\u3000\u5c60\u9f99\u4e00\u8d70\u8fdb\u6765\uff0c\u767b\u65f6\u5f15\u8d77\u4e86\u5168\u573a\u54c4\u52a8\uff0c\u9664\u4e86\u6df3\u4e8e\u5468\u662f\u65e9\u5df2\u77e5\u9053\u7684\u4e4b\u5916\uff0c\u5176\u4ed6\u7684\u4eba\u90fd\u662f\u5927 \u611f\u60ca\u5947\uff0c\u7eb7\u7eb7\u8bae\u8bba\u3002 \u3000\u3000\u674e\u601d\u5357\u5b9a\u4e86\u5b9a\u795e\uff0c\u6084\u58f0\u95ee\u77f3\u749e\u9053\uff1a\u201c\u8fd9\u4eba\u662f\u8c01\uff1f\u201d\u77f3\u749e\u5c1a\u672a\u56de\u7b54\uff0c\u53ea\u89c1\u5c60\u51e4\u5df2\u6307\u7740\u5c60\u9f99 \u558a\u9053\uff1a\u201c\u4f60\u3001\u4f60\u8fd8\u6709\u8138\u56de\u6765\uff1f\u201d \u3000\u3000\u5c60\u9f99\u201c\u55ef\u201d\u4e86\u4e00\u58f0\uff0c\u50b2\u7136\u8bf4\u9053\uff1a\u201c\u6211\u81ea\u5df1\u7684\u5bb6\uff0c\u6211\u4e3a\u4ec0\u4e48\u4e0d\u80fd\u56de\u6765\uff1f\u201d \u3000\u3000\u5c60\u51e4\u60f3\u8d77\u4e86\u9f99\u521a\u7684\u60e8\u6b7b\uff0c\u53c8\u662f\u4f24\u5fc3\uff0c\u53c8\u662f\u6124\u6068\uff0c\u8bf4\u51fa\u8bdd\u6765\uff0c\u58f0\u97f3\u90fd\u98a4\u6296\u4e86\uff0c\u201c\u4f60\u3001\u4f60\u3002 \u4f60\u7528\u6bd2\u638c\u5bb3\u4e86\u4e8c\u5e08\u54e5\uff0c\u8fd9\u4ef6\u4e8b\uff0c\u4f60\u3001\u4f60\u8d56\u4e0d\u4e86\uff01\u201d \u3000\u3000\u5c60\u9f99\u51b7\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u9f99\u521a\u662f\u7ed9\u6211\u6253\u4e86\u4e00\u638c\u3002\u4ed6\u5df2\u7ecf\u6b7b\u4e86\u4e48\uff1f\u6b7b\u5f97\u6d3b\u8be5\uff0c\u6211\u4e3a\u4ec0\u4e48\u8981 \u8d56\uff1f\u201d \u3000\u3000\u5c60\u51e4\u54ac\u4e86\u54ac\u7259\uff0c\u51db\u7136\u8bf4\u9053\uff1a\u201c\u56fd\u6709\u56fd\u6cd5\uff0c\u5be8\u6709\u5be8\u89c4\uff0c\u4f60\u6beb\u65e0\u9053\u7406\u6740\u4e86\u9f99\u521a\uff0c\u5c71\u5be8\u5bb9\u4f60\u4e0d \u5f97\uff01\u201d \u3000\u3000\u5c60\u9f99\u4fa7\u76ee\u659c\u8155\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u8981\u8feb\u6211\u8bf4\u51fa\u7406\u7531\uff1f\u6211\u8fd9\u662f\u987e\u5168\u4f60\u7684\u989c\u9762\uff0c\u8bf4\u51fa \u6765\uff0c\u4e8e\u4f60\u5e76\u65e0\u597d\u5904\uff01\u201d \u3000\u3000\u5c60\u51e4\u6c14\u5f97\u67f3\u7709\u5012\u7ad6\uff0c\u970d\u5730\u7ad9\u4e86\u8d77\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u6709\u4ec0\u4e48\u6015\u4f60\u8bf4\u7684\uff1f\u8bf4\u5427\uff0c\u6211\u5012\u8981\u770b\u4f60\u5982 \u4f55\u542b\u8840\u55b7\u4eba\uff01\u201d \u3000\u3000\u5c60\u9f99\u9053\uff1a\u201c\u4f60\u548c\u4e8c\u5e08\u5f1f\u4e09\u5e08\u5f1f\u7684\u4e8b\u60c5\u4f60\u81ea\u5df1\u77e5\u9053\uff0c\u6211\u597d\u6b79\u662f\u4f60\u7684\u5144\u957f\uff0c\u6211\u53ef\u4e0d\u80fd\u8ba9\u4f60\u8d25 \u574f\u95e8\u98ce\uff01\u201d \u3000\u3000\u5c60\u51e4\u6c14\u5f97\u82b1\u5bb9\u53d8\u8272\uff0c\u6cea\u73e0\u513f\u5728\u773c\u7736\u91cc\u6253\u8f6c\uff0c\u559d\u9053\uff1a\u201c\u80e1\u8bf4\u516b\u9053\uff0c\u6211\u600e\u6837\u8d25\u574f\u95e8\u98ce\u4e86\uff1f\u4f60 \u624d\u662f\u6709\u8fb1\u5bb6\u95e8\u5462\uff01\u597d\uff0c\u4f60\u56de\u6765\u5f97\u6b63\u597d\uff0c\u5f53\u7740\u4e00\u4f17\u540c\u95e8\uff0c\u5be8\u4e2d\u5934\u76ee\uff0c\u4f60\u6211\u5206\u4e2a\u662f\u975e\u66f2\u76f4\uff0c\u4eca\u65e5 \u6211\u8981\u66ff\u7239\u7239\u6e05\u7406\u95e8\u6237\uff01\u201d \u3000\u3000\u5c60\u9f99\u7eb5\u58f0\u5927\u7b11\u9053\uff1a\u201c\u6211\u8fd8\u672a\u66fe\u6574\u8083\u95e8\u98ce\uff0c\u4f60\u5c31\u8981\u6e05\u7406\u95e8\u6237\uff1f\u81ed\u4e2b\u5934\uff0c\u4f60\u8d81\u6211\u4e0d\u5728\u5bb6\u7684\u65f6 \u5019\uff0c\u7b3c\u7edc\u4e86\u4e00\u4f17\u5e08\u5f1f\uff0c\u5c31\u4ee5\u4e3a\u53ef\u4ee5\u722c\u5230\u6211\u7684\u5934\u4e0a\u4e48\uff1f\u54fc\uff0c\u7239\u7239\u6b7b\u4e86\uff0c\u5c60\u5bb6\u7684\u4e8b\u60c5\u4e5f\u8fd8\u8f6e\u4e0d\u5230 \u4f60\u4eec\u4f5c\u4e3b\uff01\u201d \u3000\u3000\u4e00\u4e2a\u8bf4\u8981\u6574\u8083\u95e8\u98ce\uff0c\u4e00\u4e2a\u8bf4\u8981\u6e05\u7406\u95e8\u6237\uff0c\u95f9\u5f97\u4e0d\u53ef\u5f00\u4ea4\u3002\u6df3\u4e8e\u5468\u90a3\u5e2e\u4eba\u5e78\u707e\u4e50\u7978\uff0c\u8896\u624b \u65c1\u89c2\uff0c\u717d\u98ce\u62e8\u706b\u3002\u8463\u5f00\u5c71\u3001\u9093\u98de\u7b49\u4eba\u751f\u6015\u5c60\u9f99\u8bf4\u51fa\u66f4\u96be\u542c\u7684\u8bdd\uff0c\u8fde\u5fd9\u529d\u89e3\uff1a\u201c\u5c60\u4e16\u5144\uff0c\u81ea\u5bb6 \u5144\u59b9\uff0c\u522b\u8981\u95f9\u51fa\u7b11\u8bdd\uff01\u201d\u201d\u201c\u5c60\u59d1\u5a18\uff0c\u4eca\u65e5\u5927\u4f19\u513f\u662f\u4e3a\u4e86\u5546\u91cf\u62b5\u5fa1\u9791\u5b50\u7684\u5927\u4e8b\u6765\u7684\uff0c\u4f60\u4eec\u7684 \u5bb6\u4e8b\uff0c\u4e0d\u662f\u53ef\u4ee5\u6401\u5230\u540e\u5934\uff1f\u201d \u3000\u3000\u6709\u4e2a\u4e0d\u77e5\u5c31\u91cc\u800c\u53c8\u6027\u60c5\u66b4\u8e81\u7684\u5be8\u4e3b\u56b7\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u4eec\u4e0d\u662f\u542c\u4f60\u4eec\u5144\u59b9\u5435\u67b6\u6765\u7684\uff01\u8fd9\u4f1a \u5f00\u8fc7\u4e4b\u540e\uff0c\u4f60\u4eec\u8981\u6e05\u7406\u95e8\u6237\u4e5f\u597d\uff0c\u6574\u987f\u95e8\u98ce\u4e5f\u597d\uff0c\u542c\u4f60\u4eec\u7684\u4fbf\uff01\u201d \u3000\u3000\u5c60\u51e4\u5fc3\u4e2d\u4e00\u51db\uff0c\u60f3\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4ed6\u4eec\u60f3\u626f\u5230\u79c1\u4e8b\u4e0a\u5934\uff0c\u4e2d\u4f24\u6211\u7684\u540d\u8a89\uff0c\u4ee5\u9042\u4ed6\u4eec\u7834\u574f\u8fd9 \u6b21\u7eff\u6797\u5927\u4f1a\u7684\u9634\u8c0b\uff0c\u6211\u53ef\u4e0d\u5f97\u4e0a\u4e86\u4ed6\u4eec\u7684\u5f53\uff01\u201d \u3000\u3000\u6df3\u4e8e\u5468\u8fd8\u6709\u66f4\u5927\u7684\u56fe\u8c0b\uff0c\u4e00\u770b\u7fa4\u96c4\u4e0d\u503c\u5c60\u9f99\u6240\u4e3a\uff0c\u4e8e\u662f\u4e5f\u5c31\u8f6c\u98ce\u4f7f\u8235\uff0c\u6309\u7740\u5c60\u9f99\uff0c\u4e0d\u8bb8 \u4ed6\u518d\u95f9\u3002 \u3000\u3000\u674e\u601d\u5357\u8fd9\u624d\u77e5\u9053\u5c60\u9f99\u662f\u5c60\u51e4\u7684\u54e5\u54e5\uff0c\u4e0d\u7531\u5f97\u5fc3\u4e71\u5982\u9ebb\uff0c\u832b\u7136\u5982\u68a6\u3002\u5b5f\u660e\u971e\u6084\u6084\u5728\u4ed6\u8033\u8fb9 \u8bf4\u9053\uff1a\u201c\u674e\u5927\u54e5\uff0c\u4eca\u65e5\u4e4b\u4e8b\uff01\u6050\u6015\u8fd8\u5f97\u8981\u4f60\u51fa\u5934\u5462\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u6014\u4e86\u4e00\u6014\uff0c\u5fc3\u60f3\uff1a\u201c\u4e3a\u4f55\u8981\u6211\u51fa\u5934\uff0c\u96be\u9053\u5979\u5df2\u7ecf\u77e5\u9053\u6768\u5a49\u8ddf\u4e86\u8fd9\u53ae\u4e4b\u4e8b\uff1f\u4f46\u8fd9\u4e5f \u4e0d\u8fc7\u662f\u6211\u4eec\u4e09\u4eba\u4e4b\u95f4\u7684\u79c1\u4e8b\uff0c\u8fd9\u79cd\u79c1\u4e8b\uff0c\u53c8\u600e\u80fd\u5f53\u7740\u4e00\u4f17\u82f1\u96c4\uff0c\u5ba3\u4e4b\u4e8e\u53e3\u3002\u201d \u3000\u3000\u6b64\u65f6\u5341\u516b\u5bb6\u5be8\u4e3b\u5747\u5df2\u5c31\u5ea7\uff0c\u5c60\u9f99\u4e0e\u90a3\u964c\u751f\u4eba\u4e5f\u9760\u62e2\u4e86\u6765\uff0c\u6b63\u5728\u627e\u5bfb\u5ea7\u4f4d\u3002\u5608\u5608\u6742\u6742\u7684\u58f0 \u97f3\uff0c\u6e10\u6e10\u9759\u6b62\u4e0b\u6765\uff0c\u674e\u601d\u5357\u81ea\u662f\u4e0d\u4fbf\u5411\u77f3\u749e\u518d\u95ee\u3002 \u3000\u3000\u6df3\u4e8e\u5468\u5ffd\u9053\uff1a\u201c\u5c60\u59d1\u5a18\uff0c\u4f60\u4e0b\u6765\u6b47\u6b47\u5427\uff01\u201d\u5c60\u51e4\u60f3\u4e0d\u5230\u4ed6\u53c8\u6765\u6311\u8845\uff0c\u6012\u9053\uff1a\u201c\u4f60\u8fd9\u662f\u4ec0 \u4e48\u610f\u601d\uff1f\u201d\u6df3\u4e8e\u5468\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\u610f\u601d\u3002\u53ea\u662f\u4f60\u54e5\u54e5\u56de\u6765\u4e86\uff0c\u8fd9\u4e2a\u4e3b\u4f4d\u4f3c\u4e4e\u5e94\u8be5\u8ba9\u4ed6\u5750 \u4e86\u5427\uff01\u201d \u3000\u3000\u5c60\u51e4\u9762\u5b54\u4e00\u677f\uff0c\u8bf4\u9053\uff01\u201d\u6211\u6b63\u8981\u5ba3\u5e03\uff0c\u8fd9\u91cc\u6ca1\u6709\u5c60\u9f99\u7684\u5ea7\u4f4d\uff01\u201d \u3000\u3000\u5c60\u9f99\u970d\u5730\u8d77\u7acb\uff0c\u5927\u6012\u8bf4\u9053\uff1a\u201c\u5c82\u6709\u6b64\u7406\uff0c\u6709\u6211\u5728\u6b64\uff0c\u8fd8\u8f6e\u4e0d\u5230\u4f60\u6765\u5f53\u5bb6\uff0c\u4f60\u8fd9\u81ed\u4e2b\u5934\u7adf \u8981\u8d76\u6211\uff1f\u201d \u3000\u3000\u6df3\u4e8e\u5468\u6446\u51fa\u4e00\u526f\u201c\u4e3b\u6301\u516c\u9053\u201d\u7684\u957f\u8005\u795e\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u5c60\u59d1\u5a18\uff0c\u4f60\u8fd9\u672a\u514d\u592a\u8fc7\u5206\u4e86\u5427\uff1f\u4ed6 \u662f\u4f60\u7684\u54e5\u54e5\uff0c\u8eab\u4efd\u662f\u7405\u739b\u5c71\u7684\u5c11\u5be8\u4e3b\uff0c\u73b0\u5728\u5f00\u7684\u662f\u7eff\u6797\u5927\u4f1a\uff0c\u4f60\u6709\u4ec0\u4e48\u7406\u7531\u8981\u628a\u4ed6\u8d76\u51fa \u53bb\uff1f\u201d \u3000\u3000\u5c60\u51e4\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u4e00\u5b9a\u8981\u95ee\u7406\u7531\u3001\u597d\u5427\uff0c\u90a3\u6211\u5c31\u53ea\u597d\u5f53\u4f17\u8bf4\u4e86\u3002\u5c60\u9f99\u6709\u548c\u8499\u53e4\u9791\u5b50\u52fe \u7ed3\u7684\u4e25\u91cd\u5acc\u7591\uff0c\u54b1\u4eec\u8fd9\u4e2a\u4f1a\u662f\u5546\u91cf\u5982\u4f55\u5bf9\u4ed8\u8499\u53e4\u9791\u5b50\u7684\uff0c\u5018\u82e5\u8ba9\u4ed6\u53c2\u52a0\uff0c\u5c82\u4e0d\u662f\u7b49\u4e8e\u5728\u4f1a\u4e2d \u5b89\u63d2\u4e86\u4e00\u4e2a\u654c\u4eba\u7684\u5978\u7ec6\uff01\u201d \u3000\u3000\u5c60\u9f99\u9762\u8272\u5927\u53d8\uff0c\u559d\u9053\uff1a\u201c\u4f60\u6709\u4ec0\u4e48\u51ed\u8bc1\u3002\u201d \u3000\u3000\u5c60\u51e4\u9053\uff1a\u201c\u4f60\u548c\u6df3\u4e8e\u8191\u662f\u597d\u670b\u53cb\uff0c\u534a\u5e74\u4e4b\u524d\uff0c\u4f60\u4eec\u4e8c\u4eba\u66fe\u7ecf\u540c\u4f4f\u8499\u53e4\uff0c\u6709\u8fd9\u4e8b\u4e48\uff1f\u201d \u3000\u3000\u5c60\u9f99\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u662f\u548c\u4ed6\u53bb\u4fa6\u5bdf\u5bb6\u7236\u7684\u4ec7\u4eba\u3002\u4f60\u4e0d\u4e5f\u4e00\u9053\u53bb\u4e86\u4e48\uff1f\u201d \u3000\u3000\u5c60\u51e4\u9053\uff1a\u201c\u4f60\u662f\u5426\u548c\u8499\u53e4\u9791\u5b50\u52fe\u7ed3\uff0c\u6211\u8fd8\u6ca1\u6709\u62ff\u5230\u786e\u5207\u7684\u8bc1\u636e\u3002\u4f46\u6df3\u4e8e\u8191\u505a\u4e86\u8499\u53e4\u7684\u9e70 \u5927\uff0c\u8fd9\u5374\u662f\u201c\u5343\u771f\u4e07\u786e\uff0c\u65e0\u53ef\u7f6e\u7591\u7684\u4e86\u3002\u6709\u4e00\u5929\u6211\u4eec\u8774\u8776\u8c37\u4e2d\u906d\u53d7\u9791\u5b50\u7684\u56f4\u653b\uff0c\u5176\u4e2d\u5c31\u6709\u6df3 \u4e8e\u8191\u5728\uff01\u201d \u3000\u3000\u6df3\u4e8e\u5468\u51e0\u6b21\u8d77\u7acb\uff0c\u60f3\u6253\u65ad\u5c60\u51e4\u7684\u8bf4\u8bdd\uff0c\u90fd\u7ed9\u4f17\u4eba\u7684\u543c\u58f0\u538b\u4e86\u4e0b\u53bb\u3002\u8463\u5f00\u5c71\u51b7\u7b11\u9053\uff1a\u201c\u6df3 \u4e8e\u524d\u88f4\uff0c\u8ba9\u5979\u8bf4\u5b8c\u4e86\u4f60\u518d\u8bf4\uff0c\u597d\u4e48\uff1f\u201d\u80e1\u9b41\u4e5f\u9053\uff1a\u201c\u4ed6\u4eec\u7684\u5bb6\u4e8b\u6211\u4eec\u53ef\u4ee5\u4e0d\u7ba1\uff0c\u4f46\u8fd9\u79cd\u5927\u662f \u5927\u975e\u7684\u95ee\u9898\uff0c\u5374\u5fc5\u987b\u5206\u8fa8\u6e05\u695a\uff01\u201d \u3000\u3000\u5c60\u51e4\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u90a3\u6b21\u6df3\u4e8e\u8191\u5f15\u9791\u5b50\u6765\u2018\u56f4\u6355\u2019\u6211\u4eec\uff0c\u5176\u4e2d\u867d\u7136\u6ca1\u6709\u5c60\u9f99\uff0c\u4f46\u4ed6\u4eec\u4e8c\u4eba \u662f\u4e00\u9053\u5f80\u8499\u53e4\u7684\uff0c\u5c60\u9f99\u56de\u6765\u4e4b\u540e\uff0c\u4e5f\u662f\u4f4f\u5728\u6df3\u4e8e\u8191\u7684\u5bb6\u91cc\u3002\u6240\u4ee5\u6211\u8bf4\u4ed6\u6709\u91cd\u5927\u7684\u5acc\u7591\uff0c\u4e3a\u4e86 \u8c28\u614e\u8d77\u89c1\uff0c\u6211\u4e3b\u5f20\u4e0d\u80fd\u8ba9\u4ed6\u53c2\u4e0e\u6b64\u4f1a\uff01\u201d \u3000\u3000\u6df3\u4e8e\u5468\u51b7\u7b11\u9053\uff1a\u201c\u597d\u5440\uff0c\u8bf4\u5230\u6211\u4eec\u7236\u5b50\u7684\u5934\u4e0a\u6765\u4e86\u3002\u90a3\u4e48\u6211\u4e5f\u8981\u5f53\u4f17\u63ed\u9732\u4e00\u4e8b\uff0c\u5c60\u59d1\u5a18 \u66fe\u7ecf\u8bb8\u5a5a\u6211\u513f\uff0c\u5982\u4eca\u5979\u79c1\u604b\u5e08\u5144\uff0c\u5343\u65b9\u767e\u8ba1\u60f3\u8981\u6094\u5a5a\uff0c\u563f\uff0c\u563f\uff0c\u5979\u7684\u8bdd\u4f3c\u4e4e\u4e0d\u53ef\u5168\u4fe1\u5427\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u5fcd\u65e0\u53ef\u5fcd\uff0c\u970d\u5730\u7ad9\u4e86\u51fa\u53bb\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u662f\u8bc1\u4eba\u3002\u201d \u3000\u3000\u6df3\u4e8e\u5468\u4fa7\u76ee\u659c\u7768\uff0c\u95ee\u9053\uff1a\u201c\u8fd9\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u5c60\u9f99\u51b7\u7b11\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u540d\u53eb\u674e\u601d\u5357\uff0c\u4ed6\u7684\u7236\u4eb2\u674e\u5e0c\u6d69\u5b98\u5c45\u8499\u5360\u4f10\u91d1\u7684\u4e2d\u8def\u526f\u5143\u5e05\u4e4b \u804c\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u559d\u9053\uff1a\u201c\u80e1\u8bf4\u516b\u9053\uff0c\u6211\u7239\u7239\u65e9\u5df2\u6b7b\u4e86\u3002\u90a3\u4e2a\u505a\u9791\u5b50\u526f\u5143\u5e05\u7684\u4f59\u4e00\u4e2d\u662f\u5192\u6211\u7239\u7239\u4e4b \u540d\u7684\u5978\u8d3c\uff01\u201d \u3000\u3000\u6df3\u4e8e\u5468\u9053\uff1a\u201c\u600e\u77e5\u9053\u4f60\u7684\u8bdd\u53c8\u662f\u771f\u662f\u5047\uff1f\u201d \u3000\u3000\u5b5f\u660e\u971e\u548c\u5b8b\u94c1\u8f6e\u592b\u5987\u4e00\u9f50\u7ad9\u4e86\u8d77\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u90fd\u53ef\u4ee5\u7ed9\u4ed6\u8bc1\u660e\u3002\u201d\u5b5f\u660e\u971e\u8fd8\u628a\u5f53\u65e5 \u5979\u7684\u7236\u4eb2\u5982\u4f55\u8bef\u4f1a\u674e\u601d\u5357\uff0c\u540e\u6765\u53c8\u662f\u5982\u4f55\u7684\u660e\u767d\u4e86\u771f\u76f8\u7684\u7ecf\u8fc7\uff0c\u90fd\u8be6\u7ec6\u8bf4\u4e86\u3002 \u3000\u3000\u4f17\u4eba\u90fd\u77e5\u9053\u5b5f\u660e\u971e\u662f\u5b5f\u5927\u4fa0\u5b5f\u5c11\u521a\u4e4b\u5973\uff0c\u4ee5\u4ed6\u4eec\u7236\u5973\u7684\u8eab\u4efd\uff0c\u90a3\u662f\u51b3\u4e0d\u4f1a\u8bf4\u8c0e\u7684\u3002\u5b8b\u94c1 \u8f6e\u592b\u5987\u662f\u7eff\u6797\u4e2d\u4e00\u5bf9\u4ee5\u6b63\u76f4\u51fa\u540d\u7684\u592b\u5987\uff0c\u4f17\u4eba\u4e5f\u90fd\u76f8\u4fe1\u5f97\u8fc7\u3002\u4e8e\u662f\u7eb7\u7eb7\u8bf4\u9053\uff1a\u201c\u90a3\u4e48\u5c31\u8bf7\u674e \u516c\u5b50\u628a\u5f53\u65e5\u4e4b\u4e8b\u8bf4\u51fa\u6765\u8ba9\u5927\u5bb6\u542c\u542c\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u90a3\u65e5\u6df3\u4e8e\u8191\u5e26\u9886\u9791\u5b50\u56f4\u653b\u8774\u8776\u8c37\uff0c\u6211\u4e5f\u5728\u573a\uff0c\u5e76\u4e14\u66fe\u7ecf\u548c\u4ed6\u4ea4\u8fc7\u624b\u6765\u3002\u201d \u3000\u3000\u6b64\u8a00\u4e00\u51fa\uff0c\u4f17\u5bb6\u5be8\u4e3b\u4e0d\u7ea6\u800c\u540c\u5730\u628a\u76ee\u5149\u9f50\u96c6\u5728\u5c60\u9f99\u4e0e\u6df3\u4e8e\u5468\u7684\u8eab\u4e0a\u3002\u8463\u5f00\u5c71\u9053\uff1a\u201c\u6df3\u4e8e \u524d\u8f88\uff0c\u4ee4\u90ce\u6b64\u6b21\u8499\u53e4\u4e4b\u884c\uff0c\u6050\u6015\u6709\u8bb8\u591a\u4e8b\u60c5\u662f\u7792\u7740\u4f60\u7684\u5427\uff1f\u201d\u8463\u5f00\u5c71\u56e0\u6015\u6df3\u4e8e\u5468\u8001\u7f9e\u6210\u6012\uff0c \u5f53\u573a\u53d1\u4f5c\uff0c\u662f\u4ee5\u8fd9\u51e0\u53e5\u8bdd\u8fd8\u662f\u7ed9\u4ed6\u5f00\u8131\u7684\u3002\u8981\u77e5\u6df3\u4e8e\u5468\u5982\u679c\u77e5\u60c5\u7684\u8bdd\uff0c\u90a3\u5c31\u662f\u7236\u5b50\u540c\u8c0b\u7684 \u4e86\u3002 \u3000\u3000\u8df3\u864e\u6da7\u7684\u5be8\u4e3b\u9093\u98de\u662f\u4e2a\u706b\u7206\u7684\u6027\u5b50\uff0c\u5374\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u5fc5\u987b\u67e5\u4e2a\u6c34\u843d\u77f3\u51fa\uff0c\u6df3\u4e8e\u5be8\u4e3b\u662f\u7eff \u6797\u7684\u8001\u524d\u8f88\uff0c\u60f3\u5fc5\u4e0d\u81f3\u4e8e\u4e3a\u4e86\u79c1\u60c5\u5e87\u62a4\u513f\u5b50\u7684\uff01\u201d\u8fd9\u8bdd\u5341\u5206\u660e\u663e\uff0c\u5df2\u662f\u8981\u903c\u6df3\u4e8e\u5468\u201c\u5927\u4e49\u706d \u4eb2\u201d\uff01 \u3000\u3000\u5c60\u9f99\u5ffd\u5730\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u5373\u4f7f\u771f\u7684\u662f\u548c\u8499\u53e4\u4eba\u5f80\u6765\uff0c\u90a3\u4e5f\u4e0d\u662f\u4ec0\u4e48\u5927\u4e0d\u4e86\u4e4b\u4e8b\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u6211\u8bf4\u7684\u4e0d\u662f\u666e\u901a\u7684\u8499\u4eba\uff0c\u662f\u8499\u53e4\u6b66\u58eb\u3002\u6df3\u4e8e\u8191\u548c\u6210\u5409\u601d\u6c57\u624b\u4e0b\u7684\u6b66\u58eb\u52fe \u7ed3\uff0c\u6b8b\u5bb3\u6211\u4eec\u6c49\u4eba\u7684\u8c6a\u6770\uff0c\u8fd9\u8fd8\u662f\u5c0f\u4e8b\u5417\uff1f\u201d \u3000\u3000\u5c60\u9f99\u9053\uff1a\u201c\u6df3\u4e8e\u8191\u5f53\u65e5\u672a\u5fc5\u77e5\u9053\u8774\u8776\u8c37\u4e2d\u7684\u4eba\u662f\u8c01\uff0c\u8fd9\u4ef6\u4e8b\u5f88\u53ef\u80fd\u662f\u4e2a\u8bef\u4f1a\u3002\u4ed6\u548c\u8499\u53e4 \u6b66\u58eb\u5230\u4e86\u8774\u8776\u8c37\uff0c\u5f62\u96be\u52bf\u7981\uff0c\u96be\u4ee5\u8c03\u505c\uff0c\u53c8\u4e0d\u77e5\u9053\u674e\u601d\u5357\u662f\u8c01\uff0c\u52a8\u624b\u8d77\u6765\uff0c\u8fd9\u4e5f\u672a\u5c1d\u4e0d\u53ef\u539f \u8c05\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u6293\u7740\u8bdd\u67c4\uff0c\u7acb\u5373\u8bf4\u9053\uff1a\u201c\u4f60\u65e2\u7136\u627f\u8ba4\u4e86\u4ed6\u548c\u8499\u53e4\u6b66\u58eb\u52fe\u7ed3\uff0c\u90a3\u8fd8\u53ef\u4ee5\u539f\u8c05\u5417\uff1f\u201d \u3000\u3000\u5c60\u9f99\u9053\uff1a\u201c\u6211\u5148\u95ee\u95ee\u5927\u5bb6\uff0c\u4f60\u4eec\u662f\u5426\u8ba4\u4e3a\u8499\u53e4\u6b66\u58eb\u5c31\u662f\u6211\u4eec\u7684\u654c\u4eba\uff1f\u201d \u3000\u3000\u5c60\u51e4\u6012\u9053\uff1a\u201c\u4f60\u8fd9\u662f\u4ec0\u4e48\u8bdd\uff1f\u8499\u53e4\u9791\u5b50\u5165\u4fb5\u4e2d\u539f\uff0c\u4ed6\u4eec\u7684\u6b66\u58eb\uff0c\u8fd8\u80fd\u4e0d\u662f\u54b1\u4eec\u7684\u654c\u4eba \u5417\uff1f\u9664\u975e\u8fd9\u4e2a\u6b66\u58eb\u662f\u80cc\u53db\u4ed6\u4eec\u7684\u5927\u6c57\uff0c\u90a3\u53c8\u53e6\u5f53\u522b\u8bba\uff01\u201d\u6b63\u662f\uff1a \u3000\u3000\u53ef\u53f9\u7518\u5fc3\u4e3a\u864e\u6005\uff0c\u800c\u4eca\u53c8\u4f5c\u8427\u5899\u4e89\u3002 \u3000\u3000\u6b32\u5982\u540e\u4e8b\u5982\u4f55\uff0c\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u4e00\u56de\u3000\u5978\u76f8\u6c42\u548c\u4f20\u5723\u65e8\u3000\u7fa4\u8c6a\u805a\u4f1a\u5b9a\u96c4\u76df \u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b \u7b2c\u4e8c\u5341\u4e00\u56de\u3000\u5978\u76f8\u6c42\u548c\u4f20\u5723\u65e8\u3000\u7fa4\u8c6a\u805a\u4f1a\u5b9a\u96c4\u76df \u3000\u3000\u5c60\u51e4\u4e49\u6b63\u8f9e\u4e25\uff0c\u7fa4\u96c4\u8038\u7136\u52a8\u5bb9\uff0c\u90fd\u8bf4\uff1a\u201c\u4e0d\u9519\uff0c\u662f\u975e\u4e0d\u53ef\u4e0d\u8fa8\uff0c\u5927\u8282\u5b9a\u987b\u65e0\u4e8f\uff0c\u8499\u53e4\u9791 \u5b50\u5165\u4fb5\u4e2d\u539f\uff0c\u54b1\u4eec\u5c82\u53ef\u4e0d\u540c\u5fc3\u6297\u654c\uff1f\u201d \u3000\u3000\u5c60\u9f99\u62e9\u81c2\u53eb\u9053\uff1a\u201c\u5404\u4f4d\u8bf7\u7a0d\u5fae\u51b7\u9759\uff0c\u518d\u8bf7\u4e09\u601d\uff01\u83ab\u56e0\u4e00\u65f6\u6fc0\u6124\uff0c\u8bef\u4e86\u56fd\u5bb6\u767e\u5e74\u5927\u8ba1\uff01\u201d \u3000\u3000\u8463\u5f00\u5c71\u9053\uff1a\u201c\u597d\u5427\uff0c\u90a3\u6211\u4eec\u5c31\u542c\u4e00\u542c\u5c60\u5c11\u5be8\u4e3b\u7684\u9ad8\u89c1\uff01\u201d \u3000\u3000\u5c60\u9f99\u9053\uff1a\u201c\u8fd9\u53ef\u4e0d\u662f\u6211\u4e00\u4e2a\u4eba\u7684\u610f\u601d\uff0c\u53f2\u5927\u4eba\uff0c\u8bf7\u8d77\uff01\u201d \u3000\u3000\u4e0e\u5c60\u9f99\u540c\u6765\u7684\u90a3\u4e2a\u964c\u751f\u4eba\u7ad9\u4e86\u8d77\u6765\uff0c\u7fa4\u96c4\u542c\u8bf4\u4ed6\u662f\u4ec0\u4e48\u201c\u53f2\u5927\u4eba\u201d\uff0c\u90fd\u662f\u4e0d\u7981\u6115\u7136\u3002\u8463 \u5f00\u5c71\u9053\uff1a\u201c\u54b1\u4eec\u5f00\u7684\u662f\u7eff\u6797\u5927\u4f1a\uff0c\u8fd9\u4f4d\u53f2\u5927\u4eba\u2014\u2014\u201d \u3000\u3000\u5c60\u9f99\u9053\uff1a\u201c\u8fd9\u4f4d\u53f2\u5927\u4eba\u662f\u4ece\u4e34\u5b89\u6765\u7684\uff0c\u4ed6\u7684\u53d4\u7236\u6b63\u662f\u5927\u5b8b\u7684\u5f53\u671d\u5bb0\u76f8\u53f2\u5f25\u8fdc\u3002\u53f2\u76f8\u56fd\u529b \u56fe\u6062\u590d\u4e2d\u539f\uff0c\u9a71\u9664\u91d1\u864f\uff0c\u6545\u6b64\u6d3e\u4e86\u4ed6\u7684\u4f84\u513f\u4f5c\u8054\u7edc\u4f7f\uff0c\u6765\u4e0e\u6cb3\u5317\u7684\u5404\u8def\u4e49\u519b\u8054\u7edc\u3002\u54b1\u4eec\u8eab\u5728 \u7eff\u6797\uff0c\u5fc3\u6000\u6545\u56fd\uff0c\u671d\u5ef7\u6d3e\u6765\u7684\u8054\u7edc\u4f7f\uff0c\u54b1\u4eec\u4f3c\u4e4e\u4e0d\u8be5\u62d2\u7edd\u5427\uff1f\u201d \u3000\u3000\u4f17\u4eba\u8fd9\u624d\u77e5\u9053\u6b64\u4eba\u662f\u5357\u5b8b\u6d3e\u6765\u7684\u4f7f\u8005\uff0c\u5e76\u975e\u91d1\u56fd\u7684\u5b98\u513f\u3002\u4e2d\u539f\u6ca6\u9677\uff0c\u767e\u6709\u4f59\u5e74\uff0c\u5927\u5bb6\u90fd \u662f\u76fc\u671b\u6545\u56fd\u7b50\u590d\uff0c\u542c\u8bf4\u4ed6\u662f\u671d\u5ef7\u4f7f\u8005\uff0c\u4f17\u4eba\u5174\u594b\u83ab\u540d\uff0c\u597d\u4f3c\u76fc\u5230\u4e86\u201c\u4eb2\u4eba\u201d\u4e00\u6837\uff0c\u53ea\u6709\u674e\u601d \u5357\u548c\u5b5f\u660e\u971e\u4e8c\u4eba\u6bd4\u8f83\u51b7\u9759\uff0c\u8d77\u4e86\u7591\u5fc3\u3002 \u3000\u3000\u5b5f\u660e\u971e\u662f\u4ece\u6c5f\u5357\u6765\u7684\uff0c\u5979\u7684\u7236\u4eb2\u5b5f\u5c11\u521a\u4e43\u662f\u6c5f\u5357\u6b66\u6797\u7684\u9886\u8896\u4eba\u7269\uff0c\u6d88\u606f\u6700\u4e3a\u7075\u901a\u3002\u5b5f\u660e \u971e\u66fe\u542c\u5f97\u7236\u4eb2\u8bf4\u8fc7\uff0c\u5357\u5b8b\u5bb0\u76f8\u53f2\u5f25\u8fdc\u9ad8\u5b98\u5c0a\u7235\uff0c\u8d2a\u8d4c\u4e13\u6743\uff0c\u597d\u5927\u559c\u529f\uff0c\u89c1\u8bc6\u6d45\u964b\u3002\u867d\u7136\u5c1a\u4e0d \u81f3\u5982\u79e6\u6867\u4e4b\u901a\u756a\u5356\u56fd\uff0c\u4e5f\u662f\u4e00\u4e2a\u6c11\u6028\u6cb8\u817e\u7684\u5978\u76f8\u3002 \u3000\u3000\u5b5f\u5c11\u521a\u5c31\u662f\u56e0\u4e3a\u77e5\u9053\u53f2\u5f25\u8fdc\u4e3b\u5f20\u8054\u8499\u53e4\u4ee5\u706d\u91d1\uff0c\u8fd9\u624d\u5230\u8499\u53e4\u6253\u4e86\u4e00\u8f6c\uff0c\u63a2\u542c\u865a\u4e70\u7684\u3002\u674e \u601d\u5357\u90a3\u6b21\u5728\u6208\u58c1\u4e0e\u5b5f\u5c11\u521a\u7236\u5973\u76f8\u9047\uff0c\u4e5f\u66fe\u542c\u5f97\u5b5f\u5c11\u521a\u8bf4\u8fc7\u8fd9\u4ef6\u4e8b\u60c5\u3002\u6545\u6b64\u674e\u3001\u5b5f\u4e8c\u4eba\u90fd\u662f\u4e0d \u7981\u5927\u8d77\u731c\u7591\uff0c\u201c\u53f2\u5f25\u8fdc\u6d3e\u6765\u7684\u4f7f\u8005\uff0c\u53ea\u6015\u6ca1\u6709\u4ec0\u4e48\u597d\u4e8b\uff01\u201d \u3000\u3000\u5404\u5bb6\u5be8\u4e3b\u53ea\u77e5\u9053\u53f2\u5f25\u8fdc\u662f\u5357\u5b8b\u7684\u5bb0\u76f8\uff0c\u5bf9\u4ed6\u7684\u5e95\u7ec6\u5374\u5e76\u4e0d\u6e05\u695a\uff0c\u56e0\u6b64\u5927\u5bb6\u90fd\u9053\uff1a\u201c\u96be\u5f97 \u671d\u5ef7\u7684\u4f7f\u8005\u5230\u6765\uff0c\u552f\u4eec\u7684\u7eff\u6797\u5927\u4f1a\u6b63\u597d\u8bf7\u53f2\u5927\u4eba\u5ba3\u793a\u671d\u5ef7\u7684\u610f\u65e8\u3002\u201d\u4e0d\u8fc7\u4e5f\u6709\u51e0\u4e2a\u4eba\u5728\u54c4\u95f9 \u4e4b\u4e2d\uff0c\u7a83\u7a83\u79c1\u8bae\u4e86\uff1a\u201c\u4e0d\u77e5\u4ed6\u7684\u8eab\u4efd\u662f\u771f\u662f\u5047\uff1f\u201d \u3000\u3000\u90a3\u4f4d\u201c\u53f2\u5927\u4eba\u201d\u53d6\u51fa\u4e00\u5377\u6587\u4e66\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u5c0f\u5b98\u5949\u59d4\u7684\u6587\u4e66\uff0c\u8bf7\u5404\u4f4d\u8fc7\u76ee\u3002\u201d\u4e0a\u9762\u76d6 \u6709\u201c\u67a2\u5bc6\u5e9c\u201d\u7684\u5173\u9632\uff08\u67a2\u5bc6\u5e9c\u662f\u5357\u5b8b\u6700\u9ad8\u7684\u519b\u4e8b\u673a\u6784\uff0c\u5f53\u65f6\u7531\u5bb0\u76f8\u517c\u4efb\u6b63\u67a2\u5bc6\u4f7f\uff09\uff0c\u6709\u51e0\u4f4d \u5be8\u4e3b\u5230\u8fc7\u6c5f\u5357\uff0c\u89c1\u8fc7\u67a2\u5bc6\u5e9c\u7684\u5e03\u544a\uff0c\u8ba4\u5f97\u5173\u9632\u4e0d\u5047\u3002\u4e8e\u662f\u5927\u5bb6\u5747\u65e0\u5f02\u8bae\uff0c\u4fbf\u8bf7\u8fd9\u4f4d\u201c\u53f2\u5927 \u4eba\u201d\u5217\u5e2d\u8fd9\u6b21\u7684\u7eff\u6797\u5927\u4f1a\u3002\u4ece\u6587\u4e66\u4e2d\uff0c\u5927\u5bb6\u4e5f\u624d\u77e5\u9053\u4e86\u8fd9\u4f4d\u201c\u53f2\u5927\u4eba\u201d\u540d\u53eb\u53f2\u516c\u671b\u3002 \u3000\u3000\u8fd9\u4e48\u4e00\u6765\uff0c\u767b\u65f6\u6210\u4e86\u55a7\u5bbe\u593a\u4e3b\u7684\u5f62\u52bf\uff0c\u5c60\u51e4\u53cd\u800c\u88ab\u51b7\u843d\u4e00\u65c1\u4e86\u3002 \u3000\u3000\u5c60\u9f99\u5f97\u610f\u6d0b\u6d0b\u5730\u9ad8\u58f0\u8bf4\u9053\uff1a\u201c\u8bf7\u53f2\u5927\u4eba\u5ba3\u793a\u671d\u5ef7\u610f\u65e8\u3002\u201d \u3000\u3000\u53f2\u516c\u671b\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u671d\u5ef7\u7684\u610f\u65e8\u662f\u8054\u8499\u53e4\u4ee5\u706d\u91d1\uff0c\u53ea\u5f85\u8499\u53e4\u7684\u5927\u519b\u4e00\u5165\u4e2d\u539f\uff0c\u671d\u5ef7\u4fbf\u5373 \u51fa\u5175\u6e21\u6c5f\uff0c\u4e0e\u8499\u53e4\u5357\u5317\u5939\u653b\u91d1\u864f\uff01\u201d \u3000\u3000\u5404\u5bb6\u5be8\u4e3b\u4e4b\u4e2d\uff0c\u4e5f\u5e76\u975e\u6ca1\u6709\u89c1\u8bc6\u7684\u4eba\uff0c\u8463\u5f00\u5c71\u9996\u5148\u7ad9\u4e86\u8d77\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u8499\u53e4\u4e43\u662f\u864e\u72fc\u4e4b \u56fd\uff0c\u4e0e\u8499\u53e4\u8054\u76df\uff0c\u662f\u5426\u4fbf\u53ef\u4ee5\u8fd8\u6211\u6cb3\u5c71\uff0c\u6b64\u4e8b\u53ea\u6015\u3001\u53ea\u6015\u4f1a\u6709\u540e\u60a3\uff01\u201d \u3000\u3000\u53f2\u516c\u671b\u9053\uff1a\u201c\u8bf8\u4f4d\u4e0d\u5fc5\u8fc7\u8651\uff0c\u671d\u5ef7\u5f53\u7136\u662f\u6df1\u601d\u719f\u8651\uff0c\u65b9\u624d\u51b3\u5b9a\u4e0e\u8499\u53e4\u8054\u76df\u7684\u3002\u6211\u53ef\u4ee5\u544a \u8bc9\u5404\u4f4d\u4e00\u4e2a\u79d8\u5bc6\uff0c\u6210\u5409\u601d\u6c57\u7684\u4f7f\u8005\u66fe\u5230\u8fc7\u4e34\u5b89\uff0c\u4e0e\u5bb6\u53d4\u8ba2\u5546\u4e86\u5bc6\u7ea6\u3002\u7ea6\u5b9a\u706d\u91d1\u4e4b\u540e\uff0c\u5b8b\u56fd\u7834 \u91d1\u6240\u4fb5\u7684\u7586\u571f\u5c3d\u7686\u5f52\u8fd8\uff0c\u8499\u53e4\u53ea\u8981\u91d1\u56fd\u539f\u6709\u7684\u5c5e\u571f\u4ee5\u53ca\u897f\u590f\u3002\u53e6\u5916\uff0c\u8499\u53e4\u7684\u519b\u664c\u5219\u7531\u5b8b\u56fd\u8d1f \u62c5\u3002 \u3000\u3000\u201c\u5982\u6b64\u8ba2\u7ea6\uff0c\u770b\u6765\u867d\u7136\u662f\u8499\u53e4\u7a0d\u5360\u4fbf\u5b9c\uff0c\u4f46\u6211\u4eec\u5927\u5b8b\u4e5f\u5e76\u4e0d\u5403\u4e8f\uff0c\u751a\u81f3\u8fd8\u53ef\u4ee5\u8bf4\u662f\u6211\u4eec \u5927\u5927\u5360\u4e86\u4fbf\u5b9c\u3002 \u3000\u3000\u201c\u5404\u4f4d\u987b\u5f53\u77e5\u9053\uff0c\u671d\u5ef7\u2018\u5357\u6e21\u2019\u4e4b\u540e\uff0c\u4f55\u4ee5\u4e00\u767e\u4f59\u5e74\uff0c\u7a7a\u6709\u590d\u56fd\u4e4b\u5fc3\uff0c\u7ec8\u4e8e\u96be\u6210\u4e8b\u5b9e\uff1f \u90a3\u90fd\u662f\u56e0\u4e3a\u654c\u5f3a\u6211\u5f31\u7684\u7f18\u6545\uff01\u53ea\u51ed\u5b8b\u56fd\u4e4b\u529b\uff0c\u81ea\u4fdd\u5c1a\u5acc\u4e0d\u8db3\uff0c\u9a71\u654c\u66f4\u8c08\u4e0d\u5230\u3002\u5982\u4eca\u5929\u8d50\u826f \u673a\uff0c\u8499\u53e4\u5d1b\u8d77\uff0c\u5175\u529b\u4e4b\u5f3a\uff0c\u5929\u4e0b\u65e0\u5339\u3002\u8499\u53e4\u613f\u610f\u4e0e\u5b8b\u8054\u76df\uff0c\u6761\u4ef6\u53c8\u5e76\u4e0d\u82db\u523b\u3002\u5404\u4f4d\u60f3\u60f3\uff0c\u8fd9 \u8fd8\u4e0d\u662f\u4e8e\u8f7d\u96be\u9022\u7684\u673a\u4f1a\u5417\uff1f\u201d \u3000\u3000\u201c\u56e0\u6b64\u671d\u5ef7\u7684\u610f\u65e8\uff1a\u5e0c\u671b\u6cb3\u5317\u7684\u4e49\u519b\u5207\u52ff\u4e0e\u8499\u53e4\u4e3a\u654c\uff0c\u7834\u574f\u8499\u5b8b\u7684\u8054\u76df\uff0c\u8499\u53e4\u5927\u519b\u6240\u5230 \u4e4b\u5904\uff0c\u5404\u4f4d\u8fd8\u5f53\u5c3d\u529b\u534f\u52a9\uff0c\u5373\u4f7f\u53d7\u4e86\u4ec0\u4e48\u59d4\u5c48\uff0c\u4e5f\u5e94\u5728\u6240\u4e0d\u8ba1\uff0c\u4f46\u6c42\u6253\u8d25\u91d1\u864f\uff0c\u590d\u6211\u65e7\u7586\uff0c \u4fbf\u662f\u671d\u5ef7\u6240\u671b\u4e86\uff01\u201d \u3000\u3000\u4f1a\u4e2d\u9664\u4e86\u5c60\u51e4\u9080\u8bf7\u7684\u5341\u516b\u5bb6\u5be8\u4e3b\u4e4b\u5916\uff0c\u8fd8\u6709\u6df3\u4e8e\u5468\u5e26\u6765\u7684\u4e00\u5e2e\u4eba\u3002\u5341\u516b\u5bb6\u5be8\u4e3b\u4e5f\u5e76\u975e\u4e0e \u5c60\u51e4\u9f50\u5fc3\u7684\uff0c\u2014\u2014\u5176\u4e2d\u4e94\u4e2a\u4fbf\u662f\u8f83\u4e3a\u503e\u5411\u4e8e\u6df3\u4e8e\u5468\u7684\u4eba\uff0c\u800c\u5269\u4e0b\u7684\u5341\u4e09\u5bb6\u5be8\u4e3b\u4e4b\u4e2d\uff0c\u6709\u771f\u77e5 \u707c\u89c1\u7684\u4e5f\u4e0d\u8fc7\u6709\u9650\u51e0\u4eba\uff0c\u56e0\u6b64\u5728\u542c\u4e86\u201c\u671d\u5ef7\u4f7f\u8005\u201d\u53f2\u516c\u671b\u7684\u8fd9\u756a\u8bf4\u8bdd\u4e4b\u540e\uff0c\u7edd\u5927\u591a\u6570\u90fd\u611f\u5230 \u60f6\u60d1\uff0c\u867d\u7136\u5bf9\u4e8e\u53f2\u516c\u671b\u6240\u8bf4\u7684\u5fc5\u987b\u4e0e\u8499\u53e4\u5316\u654c\u4e3a\u53cb\u4e00\u4e8b\uff0c\u5fc3\u4e2d\u89c9\u5f97\u5f88\u4e0d\u8212\u670d\uff0c\u4f46\u4e5f\u89c9\u5f97\u4ed6\u8bf4 \u7684\u672a\u5c1d\u6ca1\u6709\u9053\u7406\u3002\u5c24\u5176\u56e0\u4e3a\u8fd9\u662f\u201c\u671d\u5ef7\u7684\u65e8\u610f\u201d\u3002\u8fd9\u9876\u5927\u5e3d\u5b50\u4e00\u538b\u4e0b\u6765\uff0c\u672c\u6765\u662f\u6709\u5f02\u8bae\u7684\u4eba \u4e5f\u5c31\u4e0d\u6562\u8bf4\u8bdd\u4e86\u3002 \u3000\u3000\u674e\u601d\u5357\u89c1\u5927\u5bb6\u5664\u82e5\u5bd2\u8749\uff0c\u5c40\u9762\u5341\u5206\u4e0d\u5999\uff0c\u4e0d\u7981\u80f8\u4e2d\u70ed\u8840\u6cb8\u817e\uff0c\u4e0d\u987e\u4e00\u5207\u5730\u4fbf\u7ad9\u4e86\u8d77\u6765\uff0c \u6717\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u662f\u521a\u4ece\u8499\u53e4\u56de\u6765\u7684\uff0c\u636e\u6211\u6240\u77e5\uff0c\u6210\u5409\u601d\u6c57\u7684\u91ce\u5fc3\u51b3\u975e\u53ea\u8981\u706d\u91d1\uff0c\u706d\u91d1\u4e4b\u540e\uff0c \u8fd8\u8981\u541e\u5b8b\uff0c\u56e0\u6b64\u6211\u8ba4\u4e3a\u671d\u5ef7\u7684\u8fd9\u9879\u653f\u7b56\u51b3\u4e0d\u53ef\u884c\uff01\u201d \u3000\u3000\u6df3\u4e8e\u5468\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff0c\u201c\u671d\u5ef7\u7684\u610f\u65e8\uff0c\u4f60\u4e5f\u6562\u8fdd\u80cc\u4e48\uff1f\u54fc\uff0c\u4f60\u8fd9\u5c0f\u5b50\u6709\u591a\u5927\u89c1 \u8bc6\uff0c\u96be\u9053\u5c31\u80fd\u80dc\u4e8e\u5e99\u5802\u7684\u8bf8\u516c\uff1f\u201d \u3000\u3000\u5c60\u9f99\u5219\u5728\u563f\u563f\u51b7\u7b11\uff1a\u201c\u6210\u5409\u601d\u6c57\u7684\u5fc3\u601d\uff0c\u4f60\u8fd9\u5c0f\u5b50\u53c8\u600e\u6837\u77e5\u9053\u7684\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u6211\u542c\u5f97\u6210\u5409\u601d\u6c57\u4eb2\u53e3\u8bf4\u7684\u3002\u201d \u3000\u3000\u5c60\u9f99\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6210\u544a\u601d\u6c57\u4f55\u4ee5\u4f1a\u5bf9\u4f60\u8bf4\u8fd9\u4e9b\u8bf4\u8bdd\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u8fd9\u662f\u4ed6\u5bf9\u90e8\u4e0b\u8bf8\u5c06\u7684\u8bad\u8bdd\uff0c\u6211\u9002\u9022\u5176\u4f1a\uff0c\u6070\u6070\u542c\u5230\u3002\u201d\u5f53\u4e0b\u5c06\u90a3\u65e5\u5728\u80af\u7279 \u5c71\u4e0a\u7684\u906d\u9047\uff0c\u4e00\u4e00\u8bf4\u4e86\u51fa\u6765\u3002 \u3000\u3000\u5c60\u9f99\u9053\uff1a\u201c\u7167\u4f60\u8fd9\u4e48\u8bf4\u6cd5\uff0c\u4f60\u90a3\u65e5\u5728\u72e9\u730e\u4e2d\u66fe\u7ecf\u52c7\u6740\u7280\u725b\uff0c\u6551\u4e86\u6210\u5409\u601d\u6c57\u5973\u513f\u7684\u4e00\u547d\u3002 \u90a3\u4e48\u6210\u5409\u601d\u6c57\u662f\u5e94\u8be5\u5f88\u611f\u6fc0\u4f60\u4e86\uff0c\u4ed6\u5982\u4f55\u8fd8\u80af\u653e\u4f60\u56de\u6765\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u5766\u7136\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4ed6\u66fe\u7ecf\u8981\u5c01\u6211\u505a\u2018\u91d1\u5e10\u6b66\u58eb\u2019\uff0c\u6211\u4e0d\u7518\u4e3a\u4ed6\u6240\u7528\uff0c\u7b2c\u4e09\u5929\u5c31 \u5192\u9669\u9003\u51fa\u4e86\u548c\u6797\u3002\u201d \u3000\u3000\u5c60\u9f99\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u7f16\u6545\u4e8b\u7684\u672c\u4e8b\u5012\u5f88\u4e0d\u9519\uff0c\u53ef\u60dc\u672a\u514d\u6709\u70b9\u8352\u5510\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u5404\u4f4d\u8bf7\u7a0d\u5f85\u7247\u523b\uff0c\u6211\u62ff\u4e00\u6837\u4e1c\u897f\u7ed9\u5404\u4f4d\u770b\u770b\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u8fdb\u5165\u540e\u5802\uff0c\u5b5f\u660e\u971e\u4fbf\u5373\u7ad9\u4e86\u8d77\u6765\uff0c\u63a5\u4e0b\u53bb\u8bf4\u9053\uff1a\u201c\u6211\u548c\u7239\u7239\u66fe\u5468\u6e38\u8499\u5360\u5404\u5730\uff0c\u534a \u5e74\u6709\u591a\u3002\u6240\u89c1\u6240\u95fb\uff0c\u8499\u53e4\u786e\u5b9e\u662f\u5728\u4f5c\u541e\u91d1\u706d\u5b8b\u7684\u51c6\u5907\u3002\u201d\u5f53\u4e0b\u5c06\u8499\u53e4\u5982\u4f55\u5728\u5e93\u4f26\u6c60\u3001\u845b\u817e\u6e56 \u7b49\u5730\u8bad\u7ec3\u6c34\u5e08\uff0c\u4ee5\u53ca\u5982\u4f55\u62db\u52df\u6c49\u4eba\u4e3a\u4ed6\u6240\u7528\u7b49\u7b49\u60c5\u51b5\uff0c\u8be6\u7ec6\u9053\u51fa\uff0c\u4f17\u5bb6\u5be8\u4e3b\u90fd\u4fe1\u5f97\u8fc7\u5b5f\u5927\u4fa0 \u7236\u5973\u7684\u8eab\u4efd\uff0c\u51b3\u4e0d\u4f1a\u7f16\u9020\u8c0e\u8a00\uff0c\u542c\u4e86\u8fd9\u4e9b\u201c\u654c\u60c5\u62a5\u9053\u201d\uff0c\u4e0d\u7981\u9762\u9762\u76f8\u89d1\u3002 \u3000\u3000\u674e\u601d\u5357\u62ff\u4e86\u4e00\u526f\u5f13\u7bad\u51fa\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u6210\u5409\u601d\u6c57\u81ea\u7528\u7684\u94c1\u80ce\u5f13\uff0c\u4ed6\u56e0\u4e3a\u6211\u6551\u4e86\u4ed6\u5973\u513f \u7684\u6027\u547d\uff0c\u9001\u7ed9\u6211\u7684\u3002\u5404\u4f4d\u8bf7\u770b\uff0c\u8fd9\u526f\u5f13\u7bad\u4f3c\u4e4e\u4e0d\u662f\u4e2d\u539f\u7684\u5de7\u624b\u5320\u4eba\u6240\u80fd\u94f8\u9020\u7684\u5427\uff1f\u201d \u3000\u3000\u6210\u5409\u601d\u6c57\u7684\u94c1\u80ce\u5f13\u662f\u7528\u963f\u5c14\u6cf0\u5c71\u6240\u4ea7\u7684\u7cbe\u94c1\u94f8\u9053\uff0c\u6bd4\u540c\u6837\u4e00\u628a\u7684\u666e\u901a\u94c1\u80ce\u5f13\u6c89\u91cd\u5f97\u591a\u3002 \u5f53\u65f6\u8499\u53e4\u5df2\u4ece\u6ce2\u65af\u8f93\u5165\u4e00\u79cd\u5148\u8fdb\u7684\u70bc\u94c1\u6280\u672f\uff0c\u80fd\u5c06\u767e\u70bc\u94a2\u5316\u4e3a\u7ed5\u6307\u67d4\uff0c\u8fd9\u628a\u6c89\u91cd\u7684\u94c1\u80ce\u5f13\u53ef \u4ee5\u62c9\u5f97\u5f13\u5982\u6ee1\u6708\uff0c\u8fd9\u79cd\u70bc\u94c1\u7684\u6280\u672f\uff0c\u5f53\u65f6\u5373\u4f7f\u662f\u4e2d\u539f\u7684\u5de7\u624b\u5320\u4eba\u4e5f\u8fd8\u4e0d\u80fd\u505a\u5230\u3002 \u3000\u3000\u4f17\u5bb6\u5be8\u4e3b\u542c\u4e86\u5b5f\u660e\u971e\u7684\u201c\u654c\u60c5\u62a5\u9053\u201d\uff0c\u53c8\u770b\u4e86\u8fd9\u526f\u5f13\u7bad\uff0c\u4e0d\u7531\u5f97\u5bf9\u674e\u601d\u5357\u7684\u8bf4\u8bdd\u4fe1\u4e86\u4e2a \u4e03\u516b\u5206\u3002 \u3000\u3000\u5c60\u9f99\u5f3a\u8fa9\u9053\uff1a\u201c\u5373\u4f7f\u8fd9\u526f\u94c1\u80ce\u5f13\u662f\u8499\u53e4\u5320\u4eba\u6240\u9020\uff0c\u4e5f\u672a\u5fc5\u662f\u6210\u5409\u601d\u6c57\u81ea\u7528\u4e4b\u7269\u3002\u53c8\u5373\u4f7f \u662f\u6210\u5409\u601d\u6c57\u81ea\u7528\u7684\uff0c\u4e5f\u672a\u80fd\u5c31\u8bc1\u660e\u4e86\u6210\u5409\u601d\u6c57\u786e\u5b9e\u8bf4\u8fc7\u4e86\u8981\u2018\u541e\u91d1\u706d\u5b8b\u2019\u7684\u8a00\u8bed\uff01\u201d \u3000\u3000\u8463\u5f00\u5c71\u9053\uff1a\u201c\u8499\u53e4\u5d1b\u8d77\u4e4b\u540e\uff0c\u706d\u56fd\u65e0\u6570\uff0c\u4ee5\u5b83\u7684\u5175\u529b\uff0c\u5355\u72ec\u706d\u91d1\uff0c\u7ef0\u7ef0\u6709\u4f59\uff0c\u4f55\u5fc5\u548c\u5927 \u5b8b\u8054\u76df\uff0c\u90a3\u8fd8\u4e0d\u662f\u4e3a\u4e86\u6d88\u8017\u5927\u5b8b\u7684\u56fd\u529b\uff1f\u4f9d\u6211\u4e4b\u89c1\uff0c\u5bf9\u2018\u541e\u91d1\u706d\u5b8b\u2019\u8fd9\u4e2a\u8bf4\u6cd5\uff0c\u54b1\u4eec\u662f\u5b81\u53ef \u4fe1\u5176\u6709\uff0c\u4e0d\u53ef\u4fe1\u5176\u65e0\uff01\u201d \u3000\u3000\u6df3\u4e8e\u5468\u9053\uff1a\u201c\u671d\u5ef7\u5df2\u6709\u51b3\u7b56\uff0c\u4f60\u4eec\u5984\u81ea\u731c\u7591\uff0c\u96be\u9053\u8981\u8fdd\u80cc\u671d\u5ef7\u7684\u610f\u65e8\u4e48\u3002\u201d \u3000\u3000\u8463\u5f00\u5c71\u5fcd\u4e0d\u4f4f\u6c14\uff0c\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u671d\u5ef7\u6709\u671d\u5ef7\u7684\u610f\u65e8\uff0c\u7eff\u6797\u6709\u7eff\u6797\u7684\u89c4\u77e9\uff0c\u54b1\u4eec\u53c8\u4e0d\u662f\u5403 \u671d\u5ef7\u7684\u4ff8\u7984\uff0c\u4f55\u987b\u4e00\u5b9a\u9075\u5b88\u671d\u5ef7\u7684\u610f\u65e8\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u8463\u5be8\u4e3b\u8bf4\u5f97\u4e0d\u9519\uff0c\u7eff\u6797\u7684\u5144\u5f1f\u65e2\u7136\u8981\u7ec4\u6210\u4e49\u519b\uff0c\u90a3\u5c31\u5f97\u4e3a\u8001\u767e\u59d3\u7740 \u60f3\u3002\u671d\u5ef7\u7684\u610f\u65e8\uff0c\u5bf9\u8001\u767e\u59d3\u6709\u5229\u7684\u54b1\u4eec\u9075\u4ece\uff0c\u6709\u5bb3\u7684\u54b1\u4eec\u4e3a\u4f55\u4e0d\u80fd\u8fdd\u80cc\uff1f\u8499\u53e4\u9791\u5b50\u4fb5\u5165\u4e2d \u539f\uff0c\u5360\u7684\u662f\u6c49\u4eba\u7684\u5730\u65b9\uff0c\u6740\u7684\u662f\u4e2d\u534e\u7684\u513f\u5973\uff0c\u96be\u9053\u54b1\u4eec\u53cd\u800c\u8fd8\u8981\u5e2e\u52a9\u5b83\u5417\uff1f\u201d \u3000\u3000\u53f2\u516c\u671b\u9762\u8272\u94c1\u9752\uff0c\u53eb\u9053\uff1a\u201c\u53cd\u4e86\uff0c\u53cd\u4e86\u3002\u201d \u3000\u3000\u8df3\u864e\u6da7\u7684\u5be8\u4e3b\u9093\u98de\u54c8\u54c8\u5927\u7b11\uff1a\u201c\u5f53\u7136\u662f\u53cd\u4e86\uff0c\u4e0d\u53cd\uff0c\u6211\u53c8\u4f55\u5fc5\u4e0a\u5c71\u505a\u5f3a\u76d7\uff1f\u6211\u4e0d\u61c2\u4ec0\u4e48 \u5927\u9053\u7406\uff0c\u8c01\u6b3a\u538b\u6211\u4eec\u8fd9\u4e9b\u5c0f\u767e\u59d3\u6211\u5c31\u53cd\u8c01\uff0c\u91d1\u864f\u8981\u53cd\uff0c\u8499\u53e4\u9791\u5b50\u8981\u53cd\uff0c\u4f60\u5b8b\u56fd\u7684\u5b98\u513f\u82e5\u7136\u6043 \u7740\u5b98\u5bb6\u7684\u52bf\u529b\u538b\u6211\uff0c\u6211\u4e5f\u8981\u53cd\uff01\u201d \u3000\u3000\u6df3\u4e8e\u5468\u6da8\u7ea2\u4e86\u8138\uff0c\u8bf4\u9053\uff1a\u201c\u53ea\u51ed\u54b1\u4eec\u8fd9\u5e2e\u4e4c\u5408\u4e4b\u4f17\uff0c\u5c31\u80fd\u591f\u9a71\u9664\u91d1\u864f\uff0c\u517c\u4e14\u62b5\u5fa1\u8499\u53e4\u9791 \u5b50\u7684\u5165\u4fb5\u5417\uff1f\u65e0\u8bba\u5982\u4f55\uff0c\u5927\u5b8b\u603b\u662f\u54b1\u4eec\u7684\u90a6\u56fd\uff0c\u5927\u5b8b\u7684\u56fd\u541b\u603b\u662f\u54b1\u4eec\u7684\u7687\u4e0a\uff0c\u6211\u4e3b\u5f20\u9075\u5949\u671d \u5ef7\u610f\u65e8\u3002\u201d \u3000\u3000\u5c60\u9f99\u8bf4\u9053\uff0c\u201c\u4e0d\u9519\uff0c\u4e24\u5bb3\u76f8\u6743\u53d6\u5176\u8f7b\uff0c\u8054\u5408\u8499\u53e4\uff0c\u9a71\u9664\u4e86\u91d1\u864f\u518d\u8bf4\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff0c\u201c\u8c01\u8bf4\u54b1\u4eec\u662f\u4e4c\u5408\u4e4b\u4f17\uff1f\u8c01\u8bf4\u54b1\u4eec\u7684\u529b\u91cf\u5355\u8584\uff1f\u54b1\u4eec\u662f\u548c\u8001\u767e\u59d3\u7ad9\u5728\u4e00\u8d77 \u7684\uff0c\u53ea\u8981\u54b1\u4eec\u771f\u7684\u662f\u4e3a\u56fd\u4e3a\u6c11\uff0c\u8001\u767e\u59d3\u4e5f\u5c31\u4f1a\u5e2e\u54b1\u4eec\uff01\u8499\u53e4\u9791\u5b50\u548c\u5973\u771f\u9791\u5b50\u52a0\u8d77\u6765\uff0c\u4e5f\u6ca1\u6709 \u54b1\u4eec\u6c49\u4eba\u7684\u8001\u767e\u59d3\u591a\uff01\u201d \u3000\u3000\u53cc\u65b9\u9488\u950b\u76f8\u5bf9\uff0c\u5435\u95f9\u8d77\u6765\uff0c\u4e24\u8fb9\u90fd\u6709\u9644\u79f0\u7684\u4eba\uff0c\u805a\u4e49\u5385\u4e2d\u4e89\u8bba\u7eb7\u7eb7\uff0c\u4e71\u6210\u4e00\u7247\u3002 \u3000\u3000\u5317\u8292\u5c71\u7684\u8001\u5be8\u4e3b\u5434\u6052\u8bf4\uff1a\u201c\u8fd9\u6837\u7684\u5435\u95f9\u4e5f\u5435\u4e0d\u51fa\u4e2a\u7ed3\u679c\uff0c\u4fd7\u8bed\u8bf4\u86c7\u65e0\u5934\u800c\u4e0d\u884c\uff0c\u4f9d\u6211\u4e4b \u89c1\uff0c\u54b1\u4eec\u5e94\u5f53\u5148\u63a8\u51fa\u4e00\u4f4d\u76df\u4e3b\uff0c\u90fd\u542c\u76df\u4e3b\u7684\u53f7\u4ee4\uff01\u201d \u3000\u3000\u5434\u6052\u662f\u7eff\u6797\u7684\u8001\u524d\u8f88\uff0c\u4ed6\u4e00\u8bf4\u8bdd\uff0c\u4e0d\u4f46\u6df3\u4e8e\u5468\u8fd9\u5e2e\u4eba\u9644\u548c\uff1b\u5341\u516b\u5bb6\u5be8\u4e3b\uff0c\u5341\u6709\u516b\u4e5d\u4e5f\u90fd \u8bf4\u4ed6\u6709\u7406\u3002 \u3000\u3000\u8463\u5f00\u5c71\u9053\uff1a\u201c\u5c60\u76df\u4e3b\u53bb\u4e16\u4e4b\u540e\uff0c\u7eff\u6797\u4e2d\u6ca1\u6709\u4e00\u4e2a\u5927\u5bb6\u5fc3\u60a6\u8bda\u670d\u7684\u5934\u513f\uff0c\u7684\u786e\u662f\u96be\u4ee5\u9f50\u5fc3 \u5408\u529b\u3002\u4f9d\u6211\u4e4b\u89c1\uff0c\u65b0\u76df\u4e3b\u662f\u4e00\u5b9a\u8981\u63a8\u9009\u51fa\u6765\u7684\u3002\u4e0d\u8fc7\uff0c\u505a\u4e86\u76df\u4e3b\u4e5f\u5e76\u975e\u5c31\u53ef\u4ee5\u72ec\u65ad\u72ec\u884c\uff0c\u5927 \u4e8b\u8fd8\u662f\u5e94\u8be5\u4f17\u4eba\u5546\u8bae\uff0c\u4e89\u6301\u4e0d\u4e0b\u4e4b\u65f6\uff0c\u90a3\u624d\u7531\u76df\u4e3b\u4ef2\u88c1\u3002\u201d \u3000\u3000\u4f17\u53e3\u4e00\u8f9e\uff0c\u5927\u5bb6\u5bf9\u5e94\u8be5\u63a8\u9009\u65b0\u76df\u4e3b\u4e00\u4e8b\u90fd\u65e0\u5f02\u8bae\u3002\u8df3\u864e\u6da7\u7684\u9093\u98de\u4fbf\u9053\uff1a\u201c\u6545\u76df\u4e3b\u5c60\u767e\u57ce \u662f\u4ee5\u7405\u739b\u5c71\u7684\u5be8\u4e3b\u8eab\u4efd\u5f53\u9009\u7684\uff0c\u7405\u739b\u5c71\u662f\u7eff\u6797\u4e2d\u6700\u5927\u7684\u4e00\u4e2a\u5c71\u5934\uff0c\u7405\u739b\u5c71\u5982\u4eca\u662f\u5949\u5c60\u51e4\u59d1\u5a18 \u4f5c\u5be8\u4e3b\uff0c\u6211\u4ee5\u4e3a\u5c31\u8fd9\u6837\u987a\u7406\u6210\u7ae0\uff0c\u7531\u5c60\u59d1\u5a18\u7ee7\u627f\u76df\u4e3b\u4e4b\u4f4d\uff0c\u6700\u4e3a\u9002\u5f53\uff01\u201d \u3000\u3000\u6df3\u4e8e\u5468\u51b7\u7b11\u9053\uff1a\u201c\u5c60\u767e\u57ce\u8fd8\u6709\u4e00\u4f4d\u516c\u5b50\u5728\u8fd9\u91cc\u5462\uff01\u82e5\u8bf4\u8981\u2018\u7ee7\u627f\u2019\u7684\u8bdd\uff0c\u90a3\u5c31\u5e94\u8be5\u662f\u5c60 \u9f99\u7ee7\u627f\u624d\u5bf9\uff01\u201d \u3000\u3000\u5c60\u9f99\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u6df3\u4e8e\u5be8\u4e3b\u5fb7\u9ad8\u671b\u91cd\uff0c\u5148\u7236\u5728\u751f\u4e4b\u65f6\uff0c\u5bf9\u6df3\u4e8e\u5be8\u4e3b\u4e5f\u662f\u6781\u4e3a\u4f69\u670d\u7684\u3002\u76df \u4e3b\u4e4b\u4f4d\uff0c\u6df3\u4e8e\u5be8\u4e3b\u4e0d\u5750\u8c01\u4eba\u6562\u5750\uff1f\u201d \u3000\u3000\u6df3\u4e8e\u5468\u5047\u60fa\u60fa\u5730\u6380\u987b\u7b11\u9053\uff1a\u201c\u6211\u63a8\u9009\u5c60\u4e16\u5144\uff0c\u5c60\u4e16\u5144\u600e\u7684\u628a\u8fd9\u4e2a\u62c5\u5b50\u6401\u5230\u6211\u7684\u80a9\u5934\u6765 \u5566\uff1f\u8fd9\u4e2a\u53eb\u8001\u673d\u5982\u4f55\u62c5\u5f53\u5f97\u8d77\uff1f\u201d \u3000\u3000\u5c60\u9f99\u9053\uff1a\u201c\u6211\u662f\u5fc3\u60a6\u8bda\u670d\u5730\u62e5\u6234\u6df3\u4e8e\u524d\u8f88\uff0c\u6df3\u4e8e\u524d\u8f88\u505a\u4e86\u76df\u4e3b\uff0c\u6211\u53ea\u6c42\u6267\u97ad\u968f\u9563\uff0c\u4e8e\u613f \u5df2\u8db3\u3002\u201d \u3000\u3000\u6df3\u4e8e\u5468\u90a3\u5e2e\u4eba\u6b22\u547c\u9053\uff1a\u201c\u59dc\u662f\u8001\u7684\u8fa3\uff0c\u76df\u4e3b\u4e4b\u4f4d\uff0c\u662f\u5e94\u8be5\u8bf7\u4e00\u4f4d\u8001\u6210\u6301\u91cd\u7684\u4eba\u51fa\u6765\u62c5\u5f53 \u624d\u5bf9\u3002\u96be\u5f97\u5c60\u5c11\u5be8\u4e3b\u5982\u6b64\u8c26\u9000\uff0c\u6df3\u4e8e\u524d\u8f88\uff0c\u4f60\u5c31\u52c9\u4e3a\u5176\u96be\u5427\uff01\u201d \u3000\u3000\u5b8b\u94c1\u8f6e\u51b7\u7b11\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u597d\u4e0d\u8981\u8138\uff0c\u8001\u5be8\u4e3b\u6b7b\u65e0\u5bf9\u8bc1\uff0c\u4ed6\u5c31\u80e1\u8bf4\u516b\u9053\u3002\u54fc\uff0c\u4ec0\u4e48\u5fb7\u9ad8\u671b \u91cd\uff0c\u8001\u5978\u5de8\u6ed1\u5012\u662f\u771f\u7684\u3002\u201d \u3000\u3000\u6df3\u4e8e\u5468\u5728\u624b\u4e0b\u7684\u6b22\u547c\u58f0\u4e2d\u88c5\u4f5c\u542c\u4e0d\u89c1\u5b8b\u94c1\u8f6e\u7684\u8bf4\u8bdd\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5e74\u8001\u6015\u70e6\uff0c\u5b9e\u662f\u4e0d\u60f3\u505a \u8fd9\u76df\u4e3b\u3002\u4f46\u5404\u4f4d\u5982\u6b64\u70ed\u5fc3\uff0c\u6211\u4e5f\u4e0d\u597d\u592a\u8fc7\u8f9c\u8d1f\u5404\u4f4d\u7684\u7f8e\u610f\uff0c\u53ea\u597d\u52c9\u4e3a\u5176\u96be\u4e86\uff01\u201d \u3000\u3000\u5b8b\u94c1\u8f6e\u5927\u58f0\u9053\uff1a\u201c\u76df\u4e3b\u8fd8\u6ca1\u6709\u63a8\u5b9a\u5462\uff0c\u8fd9\u2018\u52c9\u4e3a\u5176\u96be\u2019\u56db\u5b57\uff0c\u6df3\u4e8e\u5be8\u4e3b\uff0c\u4f60\u672a\u514d\u8bf4\u5f97\u592a \u65e9\u4e86\uff01\u201d \u3000\u3000\u5c60\u9f99\u6012\u9053\uff1a\u201c\u6211\u505a\u54e5\u54e5\u7684\u90fd\u7518\u613f\u9000\u8ba9\u4e86\uff0c\u5c60\u51e4\u8fd9\u5c0f\u4e2b\u5934\u4e5f\u914d\u548c\u6df3\u4e8e\u524d\u8f88\u4e89\u593a\u76df\u4e3b\u4e48\uff1f\u201d \u3000\u3000\u5b8b\u94c1\u8f6e\u9053\uff1a\u201c\u6709\u5fd7\u4e0d\u5728\u5e74\u9ad8\uff0c\u65e0\u8c0b\u7a7a\u751f\u767e\u5c81\u3002\u4f60\u9000\u8ba9\u662f\u4f60\u7684\u4e8b\uff0c\u6211\u4eec\u5374\u662f\u62e5\u62a4\u5c60\u51e4\u59d1 \u5a18\u3002\u201d \u3000\u3000\u5c60\u51e4\u7ad9\u4e86\u8d77\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u5404\u4f4d\u8bf7\u522b\u4e89\u5435\uff0c\u542c\u6211\u4e00\u8a00\uff01\u201d \u3000\u3000\u5c60\u9f99\u563f\u563f\u51b7\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u6211\u4e14\u770b\u770b\u4f60\u7684\u9762\u76ae\u80fd\u6709\u591a\u539a\uff1f\u201d \u3000\u3000\u5c60\u51e4\u9053\uff1a\u201c\u76ee\u524d\u6700\u7d27\u8981\u7684\u4e8b\u60c5\u662f\u5982\u4f55\u62b5\u5fa1\u8499\u53e4\u9791\u5b50\u2026\u2026\u201d\u5c60\u9f99\u53c8\u63d2\u53e3\u9053\uff1a\u201c\u8fd9\u662f\u8c01\u4eba\u5b9a \u4e0b\u7684\u5927\u8ba1\uff0c\u4f60\u8fd8\u672a\u66fe\u505a\u76df\u4e3b\u5462\uff01\u201d\u5c60\u51e4\u4e0d\u7406\u4ed6\uff0c\u7ee7\u7eed\u8bf4\u4e0b\u53bb\uff1a\u201c\u4f9d\u6211\u4e4b\u89c1\uff0c\u54b1\u4eec\u63a8\u9009\u51fa\u6765\u7684 \u4eba\uff0c\u5e94\u8be5\u79f0\u4e3a\u4e49\u519b\u7684\u76df\u4e3b\u66f4\u4e3a\u9002\u5f53\uff0c\u8fd9\u6837\u53ef\u4ee5\u5bb9\u7eb3\u7eff\u6797\u4ee5\u5916\u7684\u4eba\u7269\u3002\u5404\u5bb6\u5be8\u4e3b\u5c31\u662f\u5404\u8def\u4e49\u519b \u7684\u9996\u9886\u3002\u4f46\u505a\u76df\u4e3b\u7684\u4eba\uff0c\u5374\u4e0d\u4e00\u5b9a\u8981\u662f\u7eff\u6797\u7684\u5be8\u4e3b\u3002\u201d \u3000\u3000\u8463\u5f00\u5c71\u9996\u5148\u8868\u793a\u8d5e\u540c\uff0c\u5404\u5bb6\u5be8\u4e3b\u542c\u5c60\u51e4\u8bf4\u5f97\u6709\u7406\uff0c\u4e5f\u90fd\u7eb7\u7eb7\u8868\u793a\u540c\u610f\u3002 \u3000\u3000\u5c60\u9f99\u51b7\u7b11\u9053\uff1a\u201c\u4ec0\u4e48\u540d\u4e49\u6211\u4e0d\u7ba1\uff0c\u603b\u4e4b\u4f60\u662f\u4e0d\u914d\u5f53\u8fd9\u76df\u4e3b\u3002\u201d \u3000\u3000\u5c60\u51e4\u9053\uff1a\u201c\u4f60\u8fd9\u8bdd\u5012\u662f\u8bf4\u5f97\u4e00\u70b9\u4e0d\u9519\uff0c\u6211\u7684\u786e\u4e0d\u914d\u5f53\u8fd9\u76df\u4e3b\u3002\u5f53\u52a1\u4e4b\u6025\u65e2\u662f\u8981\u62b5\u5fa1\u8499\u53e4 \u9791\u5b50\uff0c\u8fd9\u4f4d\u76df\u4e3b\u5c31\u4e0d\u4ec5\u662f\u8981\u6b66\u529f\u597d\uff0c\u800c\u4e14\u662f\u8981\u901a\u6653\u5175\u6cd5\u624d\u884c\uff0c\u6211\u5fc3\u76ee\u4e2d\u6709\u4e00\u4f4d\u6700\u9002\u5f53\u7684\u4eba \u7269\uff0c\u5404\u4f4d\u60f3\u4f1a\u540c\u610f\u7684\uff01\u201d \u3000\u3000\u5c60\u9f99\u9053\uff1a\u201c\u662f\u8c01\uff1f\u201d \u3000\u3000\u5c60\u51e4\u5e94\u58f0\u8bf4\u9053\uff1a\u201c\u5c31\u662f\u6700\u8fd1\u624d\u4ece\u8499\u53e4\u56de\u6765\u7684\u674e\u5c11\u4fa0\u674e\u601d\u5357\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u5927\u5403\u4e00\u60ca\uff0c\u8fde\u5fd9\u8bf4\u9053\uff1a\u201c\u8fd9\u600e\u4e48\u53ef\u4ee5\uff1f\u6211\u662f\u65b0\u6765\u4e4d\u5230\uff0c\u4e0e\u5404\u5904\u5c71\u5be8\u4ece\u65e0\u6e0a\u6e90\u3002\u201d \u3000\u3000\u5c60\u51e4\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u4e00\u6765\u674e\u516c\u5b50\u662f\u5c06\u95e8\u4e4b\u540e\uff0c\u7cbe\u901a\u5175\u6cd5\uff1b\u4e8c\u6765\u4ed6\u662f\u5c11\u676f\u6d3e\u8c37\u5927\u4fa0\u8c37\u5e73\u9633\u7684 \u9ad8\u5f92\uff0c\u51fa\u8eab\u540d\u95e8\u6b63\u6d3e\uff0c\u505a\u4e86\u76df\u4e3b\uff0c\u6709\u5229\u4e8e\u53f7\u53ec\u7eff\u6797\u4e4b\u5916\u7684\u6c5f\u6e56\u8c6a\u6770\uff1b\u4e09\u6765\u4ed6\u521a\u4ece\u8499\u53e4\u56de\u6765\uff0c \u719f\u6089\u654c\u60c5\uff0c\u54b1\u4eec\u8981\u5bf9\u6297\u5f3a\u654c\uff0c\u5fc5\u987b\u77e5\u5df1\u77e5\u5f7c\u3002\u4ed6\u4e00\u8eab\u517c\u8fd9\u4e09\u4e2a\u6761\u4ef6\uff0c\u4f9d\u6211\u770b\u6765\uff0c\u76df\u4e3b\u7684\u4eba \u9009\uff0c\u662f\u6ca1\u6709\u8c01\u6bd4\u4ed6\u66f4\u9002\u5f53\u7684\u4e86\u3002\u201d \u3000\u3000\u5c60\u51e4\u539f\u6765\u9080\u8bf7\u7684\u5341\u516b\u5bb6\u5be8\u4e3b\u4e4b\u4e2d\uff0c\u7ea6\u6709\u4e00\u534a\u662f\u65e9\u7ecf\u5c60\u51e4\u758f\u901a\u4e86\u7684\uff0c\u53e6\u4e00\u5c0f\u534a\uff0c\u542c\u5979\u8bf4\u5f97 \u6709\u7406\uff0c\u4e5f\u7eb7\u7eb7\u8868\u793a\u8d5e\u540c\u3002 \u3000\u3000\u539f\u5148\u63a8\u9009\u5c60\u51e4\u4f5c\u76df\u4e3b\u7684\u8df3\u864e\u6da7\u5be8\u4e3b\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u54b1\u4eec\u63a8\u9009\u7684\u662f\u4e49\u519b\u76df\u4e3b\uff0c\u4e0d\u662f\u7eff \u6797\u76df\u4e3b\uff0c\u4e0e\u7eff\u6797\u4ece\u65e0\u6e0a\u6e90\uff0c\u8fd9\u53c8\u6709\u4ec0\u4e48\u5173\u7cfb\uff1f\u5c60\u59d1\u5a18\u8350\u8d24\u81ea\u4ee3\uff0c\u9093\u67d0\u6df1\u8868\u656c\u4f69\u3002\u9093\u67d0\u613f\u610f\u653e \u5f03\u524d\u8bae\uff0c\u6539\u63a8\u674e\u5c11\u4fa0\u4f5c\u4e49\u519b\u76df\u4e3b\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u8fd8\u8981\u63a8\u8f9e\uff0c\u5b5f\u660e\u971e\u8bf4\u9053\uff1a\u201c\u4f60\u5fd8\u4e86\u5bb6\u56fd\u4e4b\u6068\u4e48\uff1f\u4f60\u8981\u60f3\u62a5\u4ec7\u96ea\u6068\uff0c\u9760\u4e00\u4e2a\u4eba\u7684\u529b \u91cf\u662f\u4e0d\u884c\u7684\uff0c\u4f55\u51b5\u8fd9\u5e76\u4e0d\u5355\u5355\u662f\u4f60\u4e00\u4e2a\u4eba\u62a5\u4ec7\u7684\u95ee\u9898\uff0c\u5927\u5bb6\u5c5e\u671b\u4e8e\u4f60\uff0c\u4f60\u5c82\u80fd\u754f\u96be\u63a8\u502d\uff01\u201d \u674e\u601d\u5357\u89c1\u5b5f\u660e\u971e\u4ee5\u5927\u4e49\u76f8\u8d23\uff0c\u53ea\u597d\u4e0d\u518d\u4f5c\u58f0\u3002 \u3000\u3000\u5c60\u9f99\u53eb\u9053\uff1a\u201c\u867d\u7136\u63a8\u9009\u7684\u662f\u4e49\u519b\u76df\u4e3b\uff0c\u4e0d\u4e00\u5b9a\u8981\u7eff\u6797\u4e2d\u4eba\uff0c\u4f46\u4e5f\u5e94\u8be5\u662f\u5fb7\u9ad8\u671b\u91cd\u7684\u624d \u6210\uff01\u201d \u3000\u3000\u5b8b\u94c1\u8f6e\u51b7\u7b11\u9053\uff1a\u201c\u79c1\u901a\u8499\u53e4\u9791\u5b50\u7684\u8fd8\u8c08\u5f97\u4e0a\u4ec0\u4e48\u5fb7\u9ad8\u671b\u91cd\uff01\u201d\u4e00\u53e5\u8bdd\u8bbd\u523a\u4e86\u4e24\u4e2a\u4eba\uff0c\u6df3 \u4e8e\u5468\u4e0e\u5c60\u9f99\u90fd\u4e0d\u7981\u53d8\u4e86\u9762\u8272\u3002 \u3000\u3000\u5c60\u9f99\u6012\u9053\uff1a\u201c\u4e0e\u8499\u53e4\u8054\u76df\uff0c\u8fd9\u662f\u671d\u5ef7\u7684\u610f\u65e8\uff0c\u76ee\u524d\u5927\u4f1a\u4e5f\u8fd8\u6ca1\u6709\u516c\u8bae\uff0c\u4f60\u51ed\u4ec0\u4e48\u5c31\u8bf4\u79c1 \u901a\u8499\u53e4\u662f\u4e2a\u7f6a\u540d\uff1f\u201d\u5c60\u9f99\u56e0\u4e3a\u65e0\u8bcd\u53ef\u5bf9\uff0c\u53ea\u597d\u515c\u4e2a\u5708\u5b50\uff0c\u53c8\u56de\u5230\u6700\u521d\u7684\u4e89\u8bba\u3002 \u3000\u3000\u8463\u5f00\u5c71\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5f53\u52a1\u4e4b\u6025\u662f\u5e94\u8be5\u5148\u628a\u76df\u4e3b\u63a8\u9009\u51fa\u6765\uff0c\u597d\u51b3\u5b9a\u54b1\u4eec\u7eff\u6797\u7684\u5927\u8ba1\uff01\u201d \u3000\u3000\u6b64\u65f6\u7531\u4e8e\u5c60\u9f99\u3001\u5c60\u51e4\u5747\u5df2\u653e\u5f03\uff0c\u76df\u4e3b\u7684\u4eba\u9009\u53ea\u5269\u4e0b\u4e86\u6df3\u4e8e\u5468\u4e0e\u674e\u601d\u5357\u4e8c\u4eba\u3002\u5c60\u51e4\u8fd9\u8fb9\u7684 \u4eba\u62e5\u62a4\u674e\u601d\u5357\uff0c\u4e0e\u6df3\u4e8e\u5468\u90a3\u5e2e\u4eba\u4e89\u6267\u4e0d\u4e0b\u3002 \u3000\u3000\u7eff\u6797\u524d\u8f88\u5434\u6052\u8bf4\u9053\uff1a\u201c\u6309\u7167\u7eff\u6797\u89c4\u77e9\uff0c\u76df\u4e3b\u4e4b\u4f4d\u6709\u6240\u4e89\u6267\uff0c\u5e94\u51ed\u6b66\u529f\u51b3\u5b9a\uff01\u201d \u3000\u3000\u77f3\u749e\u9053\uff1a\u201c\u5c60\u59d1\u5a18\u521a\u624d\u8bf4\u8fc7\uff0c\u62b5\u5fa1\u9791\u5b50\uff0c\u6b66\u529f\u5e76\u4e0d\u662f\u6700\u91cd\u8981\u7684\u3002\u201d \u3000\u3000\u6df3\u4e8e\u5468\u7684\u526f\u5be8\u4e3b\u80e1\u52b2\u51b7\u7b11\uff1a\u201c\u4f60\u5c31\u53ea\u77e5\u9053\u4f60\u7684\u5c60\u59d1\u5a18\uff0c\u5c60\u51e4\u8bf4\u7684\u5c31\u662f\u91d1\u79d1\u7389\u5f8b\u5417\uff1f\u201d \u3000\u3000\u5434\u6052\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u6211\u8bb0\u5f97\u4ee4\u5c0a\u5f53\u5e74\u4e5f\u662f\u56e0\u4ed6\u5728\u7eff\u6797\u5927\u4f1a\u4e4b\u4e2d\uff0c\u65e0\u4eba\u53ef\u4e0e\u4ed6\u6297\u624b\uff0c\u8fd9\u624d\u88ab \u63a8\u4e3a\u76df\u4e3b\u7684\u3002\u201d \u3000\u3000\u539f\u6765\u5434\u6052\u65e9\u5df2\u5f97\u4e86\u6df3\u4e8e\u5468\u8bb8\u4e0b\u4ed6\u7684\u597d\u5904\uff0c\u662f\u4ee5\u53e3\u53e3\u58f0\u58f0\u4ee5\u7eff\u6797\u4f20\u7edf\u4e3a\u91cd\uff0c\u8868\u9762\u770b\u6765\u4f3c\u662f \u516c\u5e73\uff0c\u5176\u5b9e\u5374\u662f\u6697\u4e2d\u504f\u8892\u4e86\u6df3\u4e8e\u5468\u3002 \u3000\u3000\u5434\u6052\u6b64\u8a00\u4e00\u51fa\uff0c\u6df3\u4e8e\u5468\u8fd9\u5e2e\u4eba\u90fd\u62cd\u638c\u53eb\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4e0d\u9519\uff0c\u5230\u5e95\u662f\u5434\u8001\u524d\u8f88\u5904\u4e8b\u516c\u5141\u3002 \u505a\u76df\u4e3b\u561b\uff0c\u5f53\u7136\u662f\u5e94\u8be5\u672c\u9886\u9ad8\u5f3a\u624d\u884c\u3002\u80dc\u8005\u4e3a\u738b\uff0c\u8fd9\u8fd8\u6709\u4ec0\u4e48\u597d\u8bf4\u7684\uff01\u201d\u8981\u77e5\u6df3\u4e8e\u5468\u4e43\u662f\u548c \u5c60\u767e\u57ce\u9f50\u540d\u7684\u7eff\u6797\u9ad8\u624b\uff0c\u5c60\u767e\u57ce\u53bb\u4e16\u4e4b\u540e\uff0c\u6b66\u6797\u4e2d\u6b66\u529f\u6700\u5f3a\u7684\u5c31\u8981\u6570\u5230\u4ed6\u4e86\u3002\u82e5\u51ed\u6b66\u529f\u5b9a \u593a\uff0c\u4ed6\u81ea\u662f\u7a33\u64cd\u80dc\u7b97\u3002 \u3000\u3000\u5c60\u51e4\u8fd9\u8fb9\u7684\u4eba\u660e\u77e5\u5434\u6052\u662f\u5728\u6674\u4e2d\u504f\u8892\u6df3\u4e8e\u5468\uff0c\u4f46\u65e2\u7136\u4e89\u6267\u4e0d\u4e0b\uff0c\u4e5f\u53ea\u597d\u7167\u4ed6\u7684\u529e\u6cd5\u3002 \u3000\u3000\u8463\u5f00\u5c71\u9053\uff1a\u201c\u636e\u6211\u6240\u77e5\uff0c\u4ee5\u6b66\u529f\u5b9a\u593a\u76df\u4e3b\uff0c\u4e43\u662f\u7531\u53cc\u65b9\u5404\u81ea\u9009\u51fa\u597d\u624b\u6bd4\u6597\uff0c\u6597\u5230\u4e86\u6700 \u540e\uff0c\u6709\u4e00\u65b9\u65e0\u529b\u518d\u6218\uff0c\u7518\u613f\u8ba4\u8f93\uff0c\u5c31\u7531\u80dc\u65b9\u63a8\u9009\u7684\u76df\u4e3b\u7ee7\u4efb\u3002\u4e0d\u77e5\u662f\u5426\u8fd9\u6837\uff1f\u201d \u3000\u3000\u5434\u6052\u9053\uff1a\u201c\u4e0d\u9519\u3002\u4f46\u76df\u4e3b\u5019\u9009\u4eba\u81f3\u5c11\u8981\u6253\u4e00\u573a\u3002\u8fde\u80dc\u53ef\u4ee5\u8fde\u6253\uff0c\u4f46\u5728\u8fde\u80dc\u4e24\u573a\u4e4b\u540e\uff0c\u53ef \u4ee5\u6709\u6743\u4f11\u606f\uff0c\u5f85\u5230\u660e\u5929\u518d\u6253\u3002\u201d \u3000\u3000\u5c60\u51e4\u8fd9\u8fb9\u4eba\u6570\u8f83\u591a\uff0c\u5e73\u5747\u6765\u8bf4\uff0c\u6b66\u529f\u4e5f\u6bd4\u5bf9\u65b9\u8f83\u5f3a\u3002\u4f46\u6df3\u4e8e\u5468\u7684\u6b66\u529f\uff0c\u5374\u53c8\u9ad8\u51fa\u4f17\u4eba\u4e4b \u4e0a\uff0c\u56e0\u6b64\u5434\u6052\u8865\u5145\u7684\u4e24\u70b9\uff0c\u90fd\u662f\u6709\u5229\u4e8e\u6df3\u4e8e\u5468\u7684\u3002\u4ed6\u5f97\u80dc\u4e4b\u540e\u53ef\u4ee5\u8fde\u7eed\u6253\u4e0b\u53bb\uff0c\u53c8\u53ef\u4ee5\u6709\u6743 \u4f11\u606f\uff0c\u4e0d\u6015\u53d7\u8f66\u8f6e\u6218\u62d6\u57ae\u3002 \u3000\u3000\u8463\u5f00\u5c71\u76b1\u8d77\u7709\u5934\uff0c\u5fc3\u60f3\uff1a\u201c\u5982\u6b64\u6253\u6cd5\uff0c\u5c82\u4e0d\u662f\u8981\u8ba9\u6df3\u4e8e\u5468\u5927\u901e\u5a01\u98ce\uff1f\u201d\u4f46\u4e00\u65f6\u5374\u53c8\u627e\u4e0d \u5230\u85c9\u53e3\u4e0e\u5434\u6052\u4e89\u8fa9\u3002 \u3000\u3000\u5b5f\u660e\u971e\u7b11\u9053\uff1a\u201c\u7eff\u6797\u7684\u65e7\u89c4\u77e9\u65e2\u662f\u5982\u6b64\uff0c\u5c31\u8ba9\u4ed6\u4eec\u5360\u70b9\u4fbf\u5b9c\u5427\uff0c\u4e0d\u5fc5\u65a4\u65a4\u8ba1\u8f83\u4e86\u3002\u201d\u8463 \u5f00\u5c71\u542c\u5979\u8fd9\u6837\u8bf4\u6cd5\uff0c\u4f3c\u4e4e\u5bf9\u53d6\u80dc\u9887\u6709\u628a\u63e1\uff0c\u5fc3\u4e2d\u7591\u60d1\u4e0d\u5b9a\u3002\u5f53\u4e0b\u4e5f\u5c31\u4e0d\u518d\u8a00\u8bed\u3002 \u3000\u3000\u6df3\u4e8e\u5468\u81ea\u9ad8\u8eab\u4efd\uff0c\u5f53\u7136\u4e0d\u613f\u7b2c\u4e00\u4e2a\u4e0b\u573a\uff0c\u5728\u4ed6\u8fd9\u5e2e\u4eba\u4e2d\uff0c\u6709\u4e24\u4e2a\u6b66\u529f\u5f88\u5f3a\u7684\u9ad8\u624b\uff0c\u4e00\u4e2a \u662f\u9ed1\u77f3\u5c97\u7684\u5927\u5be8\u4e3b\u67f3\u6d1e\u5929\uff0c\u4e03\u5341\u4e8c\u624b\u8fde\u73af\u593a\u547d\u5251\u6cd5\u7cbe\u5999\u7edd\u4f26\uff1b\u4e00\u4e2a\u662f\u8fbd\u4e1c\u7684\u72ec\u811a\u5927\u76d7\u5d14\u9547 \u5c71\uff0c\u4e00\u8eab\u5916\u5bb6\u529f\u592b\u5df1\u7ec3\u5230\u94dc\u76ae\u94c1\u9aa8\u5200\u67aa\u4e0d\u5165\u7684\u5883\u754c\u3002\u6df3\u4e8e\u5468\u76ee\u6ce8\u67f3\u3001\u5d14\u4e8c\u4eba\uff0c\u793a\u610f\u8981\u4ed6\u4eec\u4e0b \u573a\uff0c\u4e0d\u6599\uff0c\u67f3\u6d1e\u5929\u626d\u8f6c\u4e86\u5934\uff0c\u907f\u5f00\u4ed6\u7684\u76ee\u5149\uff0c\u5d14\u9547\u5c71\u66f4\u5999\uff0c\u4ed6\u773c\u89c2\u9f3b\u9f3b\u89c2\u5fc3\uff0c\u7aef\u5750\u4e0d\u52a8\uff0c\u7adf \u7136\u88c5\u4f5c\u4e0d\u61c2\u6df3\u4e8e\u5468\u7684\u610f\u601d\u3002 \u3000\u3000\u6df3\u4e8e\u5468\u5fc3\u4e2d\u6709\u6c14\uff0c\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u52b2\u8282\uff0c\u4f60\u6253\u7b2c\u4e00\u573a\u5427\u3002\u201d\u80e1\u52b2\u8282\u662f\u4ed6\u7684\u526f\u5be8 \u4e3b\uff0c\u5f53\u7136\u53ea\u6709\u9075\u547d\u3002 \u3000\u3000\u80e1\u52b2\u8282\u63d0\u7740\u539a\u80cc\u65ab\u5c71\u5200\uff0c\u5927\u8e0f\u6b65\u8d70\u5165\u6f14\u6b66\u573a\uff0c\u8bf4\u9053\uff1a\u201c\u674e\u516c\u5b50\uff0c\u6211\u4e0d\u81ea\u91cf\u529b\uff0c\u8bf7\u6559\u4f60\u7684 \u5c11\u6797\u5251\u6cd5\u3002\u201d \u3000\u3000\u80e1\u52b2\u8282\u662f\u548c\u6df3\u4e8e\u5468\u540c\u4e00\u8f88\u7684\u6210\u540d\u4eba\u7269\uff0c\u5c60\u51e4\u8fd9\u8fb9\u7684\u5341\u4e09\u5bb6\u5be8\u4e3b\u81ea\u95ee\u90fd\u4e0d\u662f\u4ed6\u7684\u5bf9\u624b\uff0c\u4ed6 \u4e00\u4e0a\u6765\u5c31\u5411\u674e\u601d\u5357\u6311\u6218\uff0c\u4f17\u4eba\u65e0\u4e0d\u60ca\u5fc3\u3002 \u3000\u3000\u8463\u5f00\u5c71\u9053\uff1a\u201c\u674e\u516c\u5b50\u7684\u5c11\u6797\u5251\u6cd5\u4e0d\u5fd9\u65bd\u5c55\uff0c\u80e1\u5927\u54e5\uff0c\u54b1\u4eec\u662f\u8001\u670b\u53cb\u4e86\uff0c\u8001\u670b\u53cb\u5148\u5207\u78cb\u5207 \u78cb\u5427\uff01\u201d \u3000\u3000\u8463\u5f00\u5c71\u6b63\u8981\u66ff\u674e\u601d\u5357\u4e0b\u573a\uff0c\u5ffd\u89c1\u4eba\u5f71\u4e00\u6643\uff0c\u5b5f\u660e\u971e\u540e\u53d1\u5148\u81f3\uff0c\u5230\u4e86\u573a\u5fc3\u3002 \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u8463\u4f2f\u4f2f\uff0c\u8bf7\u8ba9\u4f84\u5973\u5148\u63a5\u4e00\u573a\uff0c\u4f84\u5973\u4e45\u95fb\u80e1\u8001\u524d\u8f88\u7684\u91d1\u5200\u5a01\u540d\uff0c\u96be\u5f97\u6709\u8fd9\u673a \u4f1a\u9886\u6559\u3002\u201d \u3000\u3000\u8463\u5f00\u5c71\u6b63\u6101\u4e0d\u662f\u80e1\u52b2\u8282\u7684\u5bf9\u624b\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u53f0\u4e0a\u5531\u620f\u4e0d\u5982\u53f0\u4e0b\u770b\u620f\uff0c\u80e1\u8001\u54e5\u7684\u5288\u5c71\u5200 \u5bf9\u4f60\u5bb6\u4f20\u7684\u8e51\u4e91\u5251\u6b63\u662f\u68cb\u9022\u654c\u624b\uff0c\u6211\u8fd9\u8001\u5934\u5b50\u4e5f\u60f3\u5f00\u5f00\u773c\u754c\uff0c\u8fd9\u4e00\u573a\u5c31\u8ba9\u7ed9\u4f60\u5427\u3002\u201d \u3000\u3000\u80e1\u52b2\u8282\u6697\u6697\u5403\u60ca\uff0c\u4ed6\u5012\u4e0d\u662f\u4e3a\u4e86\u5bb3\u6015\u5b5f\u660e\u971e\uff0c\u800c\u662f\u6015\u4f24\u4e86\u6c5f\u5357\u5927\u4fa0\u5b5f\u5c11\u521a\u7684\u5973\u513f\u3002\u5f53\u4e0b \u6a2a\u5200\u4e00\u7acb\uff0c\u8bf4\u9053\uff1a\u201c\u5b5f\u5973\u4fa0\u4e0d\u5fc5\u5ba2\u6c14\uff0c\u6211\u4e0e\u4ee4\u5c0a\u4e5f\u662f\u8001\u76f8\u8bc6\u4e86\u3002\u54b1\u4eec\u70b9\u5230\u4e3a\u6b62\uff0c\u8c01\u80dc\u8c01\u8d25\uff0c \u4e0d\u5fc5\u4ecb\u6000\u3002\u201d \u3000\u3000\u5c60\u9f99\u5728\u6df3\u4e8e\u5468\u8eab\u8fb9\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4fd7\u8bed\u8bf4\u5927\u6811\u5e95\u4e0b\u597d\u906e\u836b\uff0c\u8fd9\u8bdd\u8bf4\u5f97\u679c\u7136\u4e0d\u9519\u3002\u53ef\u60dc\u6211\u7684 \u7236\u4eb2\u6b7b\u5f97\u65e9\u4e86\u3002\u201d\u6b64\u8bdd\u8bdd\u4e2d\u6709\u8bdd\uff0c\u5de6\u53f3\u5f00\u5f13\uff0c\u8ba5\u523a\u5b5f\u660e\u971e\u6cbe\u7236\u4eb2\u7684\u5149\uff0c\u4e5f\u8ba5\u523a\u4e86\u80e1\u52b2\u8282\u5bf9\u5b5f \u5c11\u521a\u7684\u602f\u610f\u3002 \u3000\u3000\u6df3\u4e8e\u5468\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u5b5f\u5c11\u521a\u53f7\u79f0\u6c5f\u5357\u5927\u4fa0\uff0c\u5374\u4e5f\u672a\u5fc5\u591f\u5f97\u4e0a\u79f0\u4e3a\u906e\u836b\u7684\u5927\u6811\u3002\u201d \u3000\u3000\u80e1\u52b2\u8282\u9762\u4e0a\u4e00\u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u5b5f\u5973\u4fa0\uff0c\u8bf7\u51fa\u62db\uff01\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u665a\u8f88\u4e0d\u6562\u903e\u8d8a\uff0c\u8bf7\u80e1\u8001\u524d\u8f88\u5148\u8d50\u4e09\u62db\uff01\u201d \u3000\u3000\u5c60\u9f99\u9053\uff1a\u201c\u54c8\uff0c\u5c45\u7136\u8981\u8ba9\u4e09\u62db\uff0c\u597d\u5927\u7684\u53e3\u6c14\u3002\u80e1\u5be8\u4e3b\u7684\u91d1\u5200\uff0c\u5728\u4f60\u7684\u773c\u4e2d\u53ea\u662f\u7834\u94dc\u70c2\u94c1 \u4e86\uff01\u201d \u3000\u3000\u5b5f\u660e\u971e\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u5fc5\u6311\u62e8\u662f\u975e\uff0c\u4f60\u82e5\u662f\u77a7\u4e0d\u987a\u773c\uff0c\u5f85\u4f1a\u513f\u5c3d\u53ef\u4e0b\u573a\uff01\u201d \u3000\u3000\u5c60\u9f99\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff1a\u201c\u4f60\u4eec\u542c\u542c\uff0c\u8fd8\u672a\u4ea4\u624b\uff0c\u5979\u5c31\u597d\u50cf\u662f\u5341\u62ff\u4e5d\u7a33\uff0c\u51c6\u80fd\u8d62\u5f97\u4e86\u8fd9\u4e00\u573a \u4e86\uff01\u201d \u3000\u3000\u5b5f\u660e\u971e\u8fd9\u8bdd\u672c\u6765\u662f\u9488\u5bf9\u5c60\u9f99\u800c\u53d1\u7684\uff0c\u4f46\u4e5f\u6709\u6b20\u8003\u8651\u4e4b\u5904\uff0c\u8bf4\u4e86\u51fa\u6765\uff0c\u7ed9\u5c60\u9f99\u4e00\u52a0\u6e32\u67d3\uff0c \u53d8\u6210\u4e86\u5bf9\u80e1\u52b2\u8282\u7684\u4e00\u79cd\u8f7b\u8511\u3002\u80e1\u52b2\u8282\u662f\u4e2a\u65e9\u5df2\u6210\u540d\u7684\u4eba\u7269\uff0c\u867d\u7136\u660e\u77e5\u5c60\u9f99\u4e43\u662f\u6311\u62e8\uff0c\u4e5f\u4e0d\u80fd \u4e0d\u611f\u5230\u81ea\u5c0a\u5fc3\u53d7\u4e86\u635f\u4f24\u3002 \u3000\u3000\u5f53\u4e0b\u80e1\u52b2\u8282\u63d0\u8d77\u539a\u80cc\u65ab\u5c71\u5200\uff0c\u8bf4\u9053\uff1a\u201c\u5b5f\u5bb6\u5251\u6cd5\u5999\u7edd\u5929\u4e0b\uff0c\u5b5f\u5973\u4fa0\u8981\u8ba9\u4e09\u62db\uff0c\u8fd9\u4e5f\u4e0d\u7b97 \u5f97\u662f\u770b\u5c0f\u8001\u592b\uff01\u201d\u8bdd\u867d\u5982\u6b64\uff0c\u5176\u5b9e\u5df2\u662f\u52a8\u4e86\u6012 \u3000\u3000\u53ea\u89c1\u5200\u5149\u75be\u95ea\uff0c\u80e1\u52b2\u8282\u5de6\u5288\u4e00\u5200\uff0c\u53f3\u5288\u4e00\u5200\uff0c\u6240\u7684\u4e24\u5200\uff0c\u90fd\u662f\u6070\u5de7\u4ece\u80e1\u660e\u971e\u7684\u9b13\u8fb9\u524a \u8fc7\uff0c\u5374\u8fde\u5979\u7684\u5934\u53d1\u4e5f\u6ca1\u78b0\u7740\u3002\u5b5f\u660e\u971e\u52a8\u4e5f\u4e0d\u52a8\uff0c\u6c14\u5b9a\u795e\u95f2\uff0c\u5bf9\u4ed6\u8fd9\u4e24\u5200\u7adf\u4f3c\u662f\u89c6\u800c\u4e0d\u89c1\uff0c\u8fd9 \u5239\u90a3\u95f4\u7fa4\u96c4\u90fd\u4e0d\u7981\u5931\u58f0\u60ca\u547c\u3002 \u3000\u3000\u5f53\u7136\uff0c\u80e1\u52b2\u8282\u8fd9\u4e24\u5200\u5e76\u4e0d\u662f\u60f3\u65ab\u4f24\u5b5f\u660e\u971e\u7684\uff0c\u4f46\u4ed6\u8fd9\u67c4\u539a\u80cc\u65ab\u5c71\u5200\u91cd\u8fbe\u56db\u5341\u516b\u65a4\uff0c\u6325\u821e \u8fd9\u6837\u6c89\u91cd\u7684\u5927\u5200\u8981\u6070\u5230\u597d\u5904\u7684\u4ece\u5b5f\u660e\u971e\u9b13\u8fb9\u524a\u8fc7\uff0c\u800c\u4e0d\u9519\u624b\u4f24\u4eba\uff0c\u8fd9\u771f\u662f\u8c08\u4f55\u5bb9\u6613\uff1f \u3000\u3000\u5b5f\u660e\u971e\u5374\u4e5f\u4e0d\u52a8\uff0c\u5f53\u7136\u4e5f\u662f\u770b\u51fa\u4ed6\u662f\u865a\u62db\u3002\u8fd9\u4efd\u9547\u5b9a\u7684\u529f\u592b\uff0c\u66f4\u662f\u60ca\u4eba\u3002 \u3000\u3000\u8fd9\u4e24\u62db\u867d\u7136\u8fd8\u4e0d\u662f\u771f\u6b63\u4ea4\u624b\uff0c\u7fa4\u96c4\u5df2\u662f\u6df1\u6df1\u4f69\u670d\uff0c\u4f69\u670d\u80e1\u52b2\u8282\u7684\u5200\u6cd5\uff0c\u66f4\u4f69\u670d\u5b5f\u660e\u971e\u7684 \u80c6\u91cf\u3002 \u3000\u3000\u4f17\u4eba\u559d\u5f69\u58f0\u4e2d\uff0c\u5c60\u9f99\u53c8\u5728\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4e0b\u573a\u65e0\u7236\u5b50\uff0c\u4e3e\u624b\u4e0d\u9976\u4eba\u3002\u8fd9\u91cc\u53ef\u4e0d\u662f\u5957\u4ea4\u60c5\u7684 \u5730\u65b9\uff01\u201d \u3000\u3000\u80e1\u52b2\u8282\u542c\u4e86\u8fd9\u51e0\u53e5\u8bdd\uff0c\u5fc3\u4e2d\u53c8\u6c14\u53c8\u607c\u3002\u8981\u77e5\u4ed6\u662f\u4ee5\u524d\u8f88\u7684\u8eab\u4efd\u548c\u5b5f\u660e\u971e\u52a8\u624b\u7684\uff0c\u5b5f\u660e\u971e \u8ba9\u4ed6\u4e09\u62db\uff0c\u4ed6\u4e0d\u80fd\u4e0d\u865a\u65ab\u4e24\u5200\uff0c\u4fdd\u6301\u81ea\u5df1\u7684\u8eab\u4efd\uff0c\u4e0d\u6599\u5374\u906d\u5230\u4e86\u5c60\u9f99\u7684\u51b7\u5632\uff0c\u80e1\u52b2\u8282\u5fc3\u91cc\u60f3 \u9053\uff1a\u201c\u5c60\u9f99\u7684\u51b7\u5632\u70ed\u8bbd\u4e0d\u6253\u7d27\uff0c\u53ea\u6015\u6df3\u4e8e\u5468\u8d77\u4e86\u731c\u7591\uff0c\u4ee5\u4e3a\u6211\u4e0d\u80af\u4e3a\u4ed6\u51fa\u529b\u3002\u201d \u3000\u3000\u601d\u5ff5\u53ca\u6b64\uff0c\u80e1\u52b2\u8282\u53ea\u597d\u629b\u5f00\u987e\u5fcc\uff0c\u4e00\u54ac\u7259\u6839\uff0c\u63d0\u8d77\u4e86\u539a\u80cc\u65ab\u5c71\u5200\uff0c\u4e00\u5200\u5c31\u5288\u4e0b\u53bb\u3002\u8fd9\u4e00 \u5200\u53ef\u5c31\u4e0d\u662f\u865a\u7740\uff0c\u800c\u662f\u4ed6\u7684\u6740\u624b\u7edd\u62db\u4e86\u3002 \u3000\u3000\u4e00\u5200\u5288\u51fa\uff0c\u4fe8\u4f3c\u6674\u5929\u95ea\u8d77\u4e86\u4e00\u9053\u7535\u5149\uff0c\u5a01\u52bf\u6781\u662f\u60ca\u4eba\u3002\u5b5f\u660e\u971e\u8d5e\u9053\uff1a\u201c\u80e1\u8001\u524d\u8f88\u7684\u5feb\u5200 \u679c\u7136\u662f\u540d\u4e0d\u865a\u4f20\uff01\u201d\u8bdd\u58f0\u672a\u4e86\uff0c\u5200\u5149\u4eba\u5f71\u4e4b\u4e2d\uff0c\u53ea\u89c1\u5b5f\u660e\u971e\u8863\u8882\u98d8\u98d8\uff0c\u5df2\u662f\u63a0\u5230\u4e86\u4e09\u4e08\u5f00 \u5916\uff0c\u80e1\u52b2\u8282\u7684\u5200\u950b\u8fde\u5979\u7684\u8863\u89d2\u90fd\u6ca1\u6cbe\u7740\uff0c\u7fa4\u8c6a\u4e0d\u7981\u53c8\u662f\u5927\u58f0\u53eb\u597d\uff0c\u5f69\u58f0\u5982\u96f7\uff01 \u3000\u3000\u80e1\u52b2\u8282\u66f4\u662f\u5403\u60ca\u4e0d\u5c0f\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u5979\u521a\u624d\u6562\u4e8e\u795e\u8272\u4e0d\u53d8\u7684\u63a5\u6211\u4e24\u62db\u865a\u62db\uff0c\u539f\u6765 \u5979\u7684\u8f7b\u529f\u7adf\u662f\u5982\u6b64\u4f73\u5999\uff01\u5373\u4f7f\u6211\u90a3\u4e24\u5200\u6240\u5b9e\u7684\u8bdd\uff0c\u6599\u60f3\u4e5f\u662f\u780d\u5979\u4e0d\u7740\uff01\u201d \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u5b5f\u660e\u971e\u8eab\u4f3c\u65cb\u98ce\uff0c\u500f\u5730\u4e00\u63a0\uff0c\u4eae\u5251\u51fa\u9798\uff0c\u5251\u5c16\u4e0a\u5410\u51fa\u78a7\u83b9\u83b9\u7684\u5bd2\u5149\uff0c \u523a\u5230\u4e86\u80e1\u52b2\u8282\u7684\u5de6\u80c1\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u6765\u800c\u4e0d\u5f80\u975e\u793c\u4e5f\uff0c\u665a\u8f88\u8fd8\u62db\u4e86\uff01\u201d \u3000\u3000\u80e1\u52b2\u8282\u6a2a\u5200\u4e00\u8361\uff0c\u770b\u4f3c\u5341\u5206\u7b80\u5355\uff0c\u84e6\u7684\u4e00\u4e2a\u62db\u5f0f\uff0c\u5185\u4e2d\u5374\u85cf\u7740\u597d\u51e0\u4e2a\u590d\u6742\u7684\u53d8\u5316\uff0c\u5c06\u6d51 \u8eab\u906e\u62e6\u5f97\u98ce\u96e8\u4e0d\u900f\u3002\u53ea\u542c\u5f97\u201c\u53ee\u201d\u7684\u4e00\u58f0\uff0c\u5b5f\u660e\u971e\u7684\u5251\u5c16\u5728\u5200\u80cc\u4e0a\u8f7b\u8f7b\u4e00\u70b9\uff0c\u817e\u8eab\u98de\u8d77\uff0c\u53c8 \u9000\u5230\u4e86\u4e00\u4e08\u5f00\u5916\u3002 \u3000\u3000\u80e1\u52b2\u8282\u98de\u8eab\u6251\u4e0a\uff0c\u4e00\u5200\u5149\u5316\u4f5c\u4e86\u4e00\u9053\u957f\u8679\uff0c\u62a4\u7740\u81ea\u8eab\uff0c\u4e0d\u6c42\u80dc\uff0c\u5148\u9632\u8d25\uff0c\u5b88\u4e2d\u5e26\u653b\u3002\u867d \u7136\u662f\u4e03\u5206\u5b88\uff0c\u4e09\u5206\u653b\uff0c\u4f46\u56e0\u4ed6\u7684\u5200\u6cd5\u865a\u5b9e\u83ab\u6d4b\uff0c\u6bcf\u4e00\u62db\u90fd\u53ef\u4ee5\u653b\u5b88\u4e92\u6613\uff0c\u5b5f\u660e\u971e\u82e5\u662f\u7a0d\u6709\u4e0d \u614e\uff0c\u5c31\u4f1a\u7740\u4e86\u4ed6\u7684\u9053\u513f\u3002 \u3000\u3000\u5b5f\u660e\u971e\u81ea\u5fd6\uff1a\u201c\u4ed6\u7684\u5200\u6cd5\u9632\u5b88\u5f97\u5982\u6b64\u4e25\u5bc6\uff0c\u6c14\u529b\u53c8\u6bd4\u6211\u5927\uff0c\u53ef\u600e\u751f\u7834\u5f97\u4ed6\u5462\uff1f\u201d\u8981\u77e5\u82e5 \u662f\u786c\u78b0\u786c\u6253\u7684\u8bdd\uff0c\u529b\u5f3a\u8005\u80dc\uff0c\u529b\u5f31\u8005\u8d25\uff0c\u5b5f\u660e\u971e\u7684\u9752\u94a2\u5251\u5b9a\u7136\u4f1a\u7ed9\u4ed6\u7684\u539a\u80cc\u65ab\u5c71\u5200\u78d5\u98de\uff01 \u3000\u3000\u5b5f\u660e\u971e\u4e00\u65f6\u60f3\u4e0d\u51fa\u7834\u654c\u4e4b\u6cd5\uff0c\u53ea\u597d\u65bd\u5c55\u817e\u3001\u632a\u3001\u95ea\u3001\u5c55\u7684\u5c0f\u5de7\u529f\u592b\uff0c\u6307\u4e1c\u6253\u897f\uff0c\u6307\u5357\u6253 \u5317\uff0c\u7a7f\u82b1\u8774\u8776\u822c\u7684\u4e0e\u80e1\u52b2\u8282\u6e38\u6597\uff0c\u6597\u4e86\u6570\u5341\u62db\uff0c\u5200\u5251\u7adf\u7136\u6ca1\u6709\u78b0\u8fc7\u4e00\u4e0b\u3002\u4f46\u5b5f\u660e\u971e\u6b65\u6b65\u540e \u9000\uff0c\u770b\u6765\u5374\u662f\u5904\u5728\u4e0b\u98ce\u3002 \u3000\u3000\u6b64\u8fdb\u5f7c\u9000\uff0c\u8f6c\u77ac\u95f4\u5df2\u662f\u7ed5\u573a\u4e00\u5468\u3002\u80e1\u52b2\u8282\u5360\u4e0d\u5230\u534a\u70b9\u4fbf\u5b9c\uff0c\u5fc3\u4e2d\u4e5f\u662f\u6697\u775b\u53eb\u82e6\uff1a\u201c\u5979\u7684 \u8eab\u6cd5\u6bd4\u6211\u8f7b\u7075\uff0c\u6211\u65ab\u5979\u4e0d\u7740\uff0c\u5979\u5df2\u662f\u7acb\u4e8e\u4e0d\u8d25\u4e4b\u5730\u3002\u518d\u7ed5\u4e24\u5468\uff0c\u6211\u7684\u6c14\u529b\u90fd\u7ed9\u5979\u8017\u5c3d\u4e86\uff0c\u90a3 \u65f6\u5c82\u4e0d\u7cdf\u7cd5\u3002\u201d \u3000\u3000\u5c60\u9f99\u53c8\u5728\u51b7\u7b11\u9053\uff1a\u201c\u8981\u6bd4\u6b66\u5c31\u5f97\u89c1\u4e2a\u771f\u7ae0\uff0c\u8fd9\u7b97\u662f\u4ec0\u4e48\u6253\u6cd5\uff1f\u201d\u4ed6\u6068\u4e0d\u5f97\u5b5f\u660e\u971e\u4f24\u5728\u80e1 \u52b2\u8282\u5200\u4e0b\uff0c\u8fd9\u4e00\u6b21\u7684\u51b7\u5632\u70ed\u8bbd\uff0c\u5374\u662f\u5411\u5b5f\u660e\u971e\u800c\u53d1\u4e86\u3002 \u3000\u3000\u8bdd\u72b9\u672a\u4e86\uff0c\u53ea\u89c1\u5b5f\u660e\u971e\u7a81\u7136\u51dd\u8eab\u6b62\u6b65\uff0c\u9752\u94a2\u5251\u75be\u5982\u5189\u95ea\u822c\u5730\u5212\u4e86\u51e0\u4e2a\u5706\u5708\uff0c\u4e00\u5708\u63a5\u7740\u4e00 \u5708\uff0c\u5957\u7740\u80e1\u52b2\u8282\u7684\u5200\u5149\uff0c\u53ee\u53ee\u94db\u94db\u4e4b\u58f0\u4e0d\u7edd\u4e8e\u8033\u3002 \u3000\u3000\u5c60\u9f99\u6697\u81ea\u6b22\u559c\uff1a\u201c\u8fd9\u4e2b\u5934\u679c\u7136\u6c89\u4e0d\u4f4f\u6c14\uff0c\u4e2d\u4e86\u6211\u7684\u6fc0\u5c06\u4e4b\u8ba1\u3002\u201d\u54ea\u77e5\u5fc3\u5ff5\u672a\u5df2\uff0c\u5ffd\u89c1\u6df3 \u4e8e\u5468\u76b1\u8d77\u4e86\u7709\u5934\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4e0d\u597d\uff01\u201d \u3000\u3000\u6df3\u4e8e\u5468\u662f\u60f3\u63d0\u9192\u80e1\u52b2\u8282\u7684\uff0c\u53ef\u662f\u5df2\u7ecf\u8fdf\u4e86\u3002\u5c31\u5728\u8fd9\u5239\u90a3\u95f4\uff0c\u53ea\u89c1\u5b5f\u660e\u971e\u7684\u9752\u94a2\u5251\u77eb\u82e5\u6e38 \u9f99\uff0c\u659c\u5212\u4e00\u4e2a\u5708\u5708\uff0c\u6b63\u5212\u4e00\u4e2a\u5708\u5708\uff0c\u5de6\u5212\u4e00\u4e2a\u5708\u5708\uff0c\u53f3\u5212\u4e00\u4e2a\u5708\u5708\uff0c\u5708\u91cc\u5957\u5708\uff0c\u56db\u4e2a\u5708\u5708\u4e00 \u5212\u4e0b\u6765\uff0c\u84e6\u542c\u5f97\u201c\u94db\u201d\u7684\u4e00\u58f0\uff0c\u80e1\u52b2\u8282\u7684\u539a\u80cc\u65ab\u5c71\u5200\u5df2\u662f\u8131\u624b\u98de\u51fa\u3002 \u3000\u3000\u539f\u6765\u5728\u7ed5\u573a\u4e00\u5468\u4e4b\u540e\uff0c\u5b5f\u660e\u971e\u5df2\u662f\u60f3\u51fa\u4e86\u7834\u654c\u4e4b\u6cd5\uff0c\u5979\u8fd9\u5957\u6301\u5251\u5212\u5708\u7684\u5251\u6cd5\uff0c\u634f\u7684\u662f \u201c\u4e71\u73af\u5251\u8bc0\u201d\uff0c\u8bc0\u4e91\uff1a\u201c\u8fde\u7eed\u5982\u73af\uff0c\u5b9b\u8f6c\u5982\u610f\uff0c\u4e71\u6563\u5fc3\u795e\uff0c\u4ee5\u5de7\u964d\u529b\u201d\uff0c\u80e1\u52b2\u8282\u7684\u5927\u5200\u7ed9\u5979 \u5251\u5708\u5957\u7740\uff0c\u5de6\u7275\u53f3\u5f15\uff0c\u6c14\u529b\u53d1\u6325\u4e0d\u4e86\u4f5c\u7528\uff0c\u4f7f\u5f97\u8d8a\u6025\u5c31\u8d8a\u53d1\u5bb9\u6613\u53d7\u654c\u514b\u5236\uff0c\u8fd9\u5c31\u548c\u7528\u7ef3\u5b50\u7f1a \u7740\u77f3\u5934\uff0c\u821e\u5230\u6025\u65f6\uff0c\u5c31\u4f1a\u8131\u624b\u98de\u53bb\u4e00\u6837\u3002\u6df3\u4e8e\u5468\u770b\u51fa\u4e86\u8fd9\u4e2a\u9053\u7406\uff0c\u5c60\u9f99\u4e0e\u80e1\u52b2\u8282\u7684\u6b66\u5b66\u9020\u8be3 \u5374\u662f\u5dee\u4e86\u4e00\u7b79\u3002\u4e00\u4e2a\u770b\u8d70\u4e86\u773c\uff0c\u7740\u4e86\u9053\u513f\u3002 \u3000\u3000\u5b5f\u660e\u971e\u63d2\u5251\u5165\u9798\uff0c\u8bf4\u9053\uff1a\u201c\u8bf7\u6055\u665a\u8f88\u5f97\u7f6a\u4e86\u3002\u201d\u80e1\u52b2\u8282\u6ee1\u9762\u7f9e\u60ed\uff1b\u62fe\u8d77\u4e86\u539a\u80cc\u65ab\u5c71\u5200\uff0c \u8d70\u5230\u4e86\u6df3\u4e8e\u5468\u9762\u524d\uff0c\u8bf4\u9053\uff1a\u201c\u5c5e\u4e0b\u65e0\u80fd\uff0c\u6298\u4e86\u5be8\u4e3b\u7684\u9510\u6c14\uff0c\u65e0\u989c\u518d\u5728\u5c71\u5be8\u7acb\u8db3\uff0c\u4ece\u4eca\u4e4b\u540e\uff0c \u51b3\u610f\u91d1\u76c6\u6d17\u624b\uff0c\u95ed\u95e8\u5c01\u5200\uff0c\u8bf7\u6055\u5c5e\u4e0b\u544a\u9000\u4e86\u3002\u201d \u3000\u3000\u6df3\u4e8e\u5468\u7709\u5934\u6253\u7ed3\uff0c\u8bf4\u9053\uff1a\u201c\u80dc\u8d25\u5175\u5bb6\u5e38\u4e8b\uff0c\u4f60\u5df2\u5c3d\u529b\u800c\u4e3a\uff0c\u6211\u662f\u4e0d\u4f1a\u8d23\u602a\u4f60\u7684\uff0c\u4f60\u662f\u6211 \u7684\u526f\u5be8\u4e3b\uff0c\u4f55\u5fc5\u5982\u6b64\uff1f\u4f55\u5fc5\u5982\u6b64\uff01\u201d \u3000\u3000\u80e1\u52b2\u8282\u9eef\u7136\u8bf4\u9053\uff1a\u201c\u7eb5\u7136\u5be8\u4e3b\u4e0d\u52a0\u602a\u7f6a\uff0c\u6211\u4ea6\u81ea\u89c9\u65e0\u989c\u3002\u5be8\u4e3b\u624b\u4e0b\uff0c\u591a\u7684\u662f\u5c11\u5e74\u82f1\u6770\uff0c \u80e1\u67d0\u8001\u673d\u65e0\u80fd\uff0c\u7406\u5f53\u9000\u907f\u8ba9\u8d24\u3002\u201d\u8bf4\u7f62\uff0c\u4e5f\u4e0d\u7406\u6df3\u4e8e\u5468\u662f\u5426\u51c6\u8bb8\uff0c\u4fbf\u5373\u80cc\u8d77\u5927\u5200\uff0c\u5f84\u81ea\u4e0b \u5c71\u3002 \u3000\u3000\u539f\u6765\u80e1\u52b2\u8282\u8d25\u5728\u4e00\u4e2a\u5c11\u5973\u4e4b\u540e\uff0c\u56fa\u7136\u662f\u81ea\u89c9\u65e0\u989c\uff0c\u4f46\u4e5f\u8fd8\u4e0d\u81f3\u4e8e\u4fbf\u5373\u6c42\u53bb\u7684\u3002\u4ed6\u4e4b\u6240\u4ee5 \u548c\u6df3\u4e8e\u5468\u51b3\u88c2\uff0c\u66f4\u5927\u7684\u539f\u56e0\uff0c\u662f\u4e0d\u6ee1\u5c60\u9f99\u5bf9\u4ed6\u7684\u51b7\u5632\u70ed\u8bbd\u3002 \u3000\u3000\u6df3\u4e8e\u5468\u751a\u4e3a\u7740\u607c\uff0c\u4f46\u5728\u8fd9\u6837\u7684\u573a\u5408\uff0c\u5374\u4e5f\u4e0d\u4fbf\u628a\u80e1\u52b2\u8282\u62c9\u56de\u6765\u3002\u62c9\u62c9\u626f\u626f\uff0c\u90a3\u5c06\u662f\u66f4\u5931 \u9762\u5b50\u7684\u4e8b\uff0c\u56e0\u6b64\u53ea\u597d\u8ba9\u4ed6\u53bb\u4e86\u3002 \u3000\u3000\u5c60\u9f99\u4e5f\u6709\u81ea\u5df1\u7684\u6253\u7b97\uff0c\u4ed6\u60c5\u77e5\u4ed6\u7236\u4eb2\u7684\u624b\u4e0b\uff0c\u5bf9\u4ed6\u65e9\u5df2\u79bb\u5fc3\uff0c\u7405\u739b\u5c71\u5be8\u4e3b\u4e4b\u4f4d\uff0c\u59b9\u59b9\u5df2 \u7ecf\u5728\u4f17\u5934\u76ee\u62e5\u6234\u4e4b\u4e0b\u63a5\u4efb\uff0c\u4ed6\u662f\u65e0\u671b\u4e86\u3002\u5047\u5982\u80fd\u505a\u6df3\u4e8e\u5468\u7684\u526f\u5be8\u4e3b\uff0c\u867d\u7136\u59d4\u5c48\u4e00\u70b9\uff0c\u4e5f\u8fd8\u53ef \u7b97\u662f\u5931\u4e4b\u4e1c\u9685\uff0c\u6536\u4e4b\u6851\u6986\u3002\u56e0\u6b64\u4ed6\u5176\u5b9e\u662f\u6709\u610f\u628a\u80e1\u52b2\u8282\u6392\u6324\u6389\u7684\u3002 \u3000\u3000\u4e0d\u8fc7\u80e1\u52b2\u8282\u4e34\u53bb\u7684\u51e0\u53e5\u8bdd\uff0c\u5374\u662f\u4ee4\u4ed6\u7684\u9762\u5b50\u5f88\u8fc7\u4e0d\u53bb\uff0c\u90a3\u51e0\u53e5\u8bdd\uff0c\u8c01\u90fd\u542c\u5f97\u61c2\u662f\u5411\u4ed6\u8bf4 \u7684\uff1a\u201c\u6211\u4e0d\u884c\uff0c\u770b\u4f60\u7684\u5427\uff01\u201d\u5c60\u9f99\u82e5\u8981\u633d\u56de\u9762\u5b50\uff0c\u5c31\u975e\u4e0b\u573a\u4e0e\u5b5f\u660e\u971e\u6bd4\u5251\u4e0d\u53ef\u3002 \u3000\u3000\u6df3\u4e8e\u5468\u8fd9\u5e2e\u4eba\u4e2d\uff0c\u672c\u9886\u6700\u9ad8\u7684\u67f3\u6d1e\u5929\u4e0e\u5d14\u9547\u5c71\u4e8c\u4eba\u5e76\u65e0\u4e0b\u573a\u7684\u610f\u601d\u3002\u5c60\u9f99\u81ea\u5fd6\u5e76\u65e0\u53d6\u80dc \u7684\u628a\u63e1\uff0c\u4e5f\u81ea\u6709\u70b9\u8e0c\u8e87\u3002\u4f46\u56e0\u4ed6\u6709\u8a00\u5728\u524d\uff0c\u82e5\u662f\u4e0d\u6562\u4e0b\u573a\u4e0e\u5b5f\u660e\u971e\u8f83\u91cf\uff0c\u8fd9\u53ef\u662f\u5927\u5931\u9762\u5b50\u7684 \u4e8b\uff0c\u5c60\u9f99\u53ea\u597d\u786c\u7740\u5934\u76ae\u7ad9\u4e86\u8d77\u6765\u3002 \u3000\u3000\u5ffd\u89c1\u4e00\u4e2a\u94c1\u5854\u822c\u7684\u5927\u53cc\uff0c\u4e1a\u5df2\u5feb\u6b65\u8dd1\u51fa\u573a\u5fc3\uff0c\u7c97\u58f0\u8bf4\u9053\uff1a\u201c\u5b5f\u59d1\u5a18\uff0c\u4f60\u8bf7\u6b47\u6b47\u3002\u5b8b\u5be8 \u4e3b\uff0c\u6211\u65e9\u5c31\u60f3\u548c\u4f60\u6253\u4e0a\u4e00\u67b6\uff0c\u4eca\u65e5\u6b63\u597d\u8d81\u6b64\u673a\u4f1a\uff0c\u9886\u6559\u9886\u6559\u4f60\u7684\u65e5\u6708\u53cc\u8f6e\u6709\u4f55\u4e86\u5f97\uff1f\u201d \u3000\u3000\u539f\u6765\u8fd9\u4e2a\u83bd\u6c49\u540d\u53eb\u5468\u9547\u6d77\uff0c\u4e43\u662f\u5d14\u9547\u5c71\u7684\u5e08\u5f1f\u3002\u4ed6\u7684\u672c\u9886\u8fdc\u8fdc\u4e0d\u5982\u5e08\u5144\uff0c\u6027\u60c5\u5374\u662f\u6781\u4e3a \u66b4\u8e81\uff0c\u6709\u4e00\u6b21\u5b8b\u94c1\u8f6e\u56e0\u4e3a\u67d0\u4e8b\u4e0e\u4ed6\u4e89\u6267\uff0c\u4e24\u4eba\u90fd\u662f\u706b\u66dd\u7684\u813e\u6c14\uff0c\u4e00\u8a00\u4e0d\u5408\uff0c\u4fbf\u5373\u6325\u62f3\u76f8\u5411\uff0c \u53ea\u56e0\u5f53\u573a\u6709\u4eba\u529d\u89e3\uff0c\u8fd9\u624d\u6ca1\u6709\u6253\u6210\u3002 \u3000\u3000\u5c60\u9f99\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5b5f\u59d1\u5a18\u5df2\u7ecf\u706f\u4e86\u4e00\u573a\uff0c\u54b1\u4eec\u4e0d\u80fd\u5360\u5979\u7684\u4fbf\u5b9c\u3002\u201d\u8fd9\u8bdd\u5f53\u7136\u662f\u4e3a\u4ed6\u7684 \u9000\u7f29\u4f5c\u63a9\u9970\u7684\uff0c\u5b5f\u660e\u971e\u731c\u5230\u4ed6\u7684\u5fc3\u610f\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u5f85\u4f60\u60f3\u597d\u4e86\u5982\u4f55\u5316\u89e3\u6211\u7684\u5251\u6cd5\uff0c\u518d \u51fa\u573a\u5427\u3002\u201d \u3000\u3000\u5468\u9547\u6d77\u4e00\u51fa\u573a\u5c31\u628a\u95f7\u5728\u809a\u76ae\u91cc\u7684\u8bdd\u90fd\u8bf4\u51fa\u6765\uff0c\u7fa4\u96c4\u542c\u4e86\uff0c\u54c8\u54c8\u5927\u7b11\u3002\u674e\u601d\u5357\u4f4e\u58f0\u548c\u5b8b\u94c1 \u8f6e\u8bf4\u9053\uff1a\u201c\u8fd9\u4e2a\u5012\u662f\u4e2a\u76f4\u6027\u6c49\u5b50\uff0c\u5b8b\u5be8\u4e3b\u4f60\u2014\u2014\u201d\u674e\u601d\u5357\u7684\u610f\u601d\u662f\u60f3\u8bf7\u5b8b\u94c1\u8f6e\u5728\u6bd4\u6b66\u4e4b\u65f6\uff0c \u627e\u4e2a\u597d\u673a\u4f1a\u4e0e\u5bf9\u65b9\u5316\u654c\u4e3a\u53cb\uff0c\u800c\u4e0d\u53ef\u8ba4\u771f\u62fc\u547d\u7684\uff0c\u4e0d\u6599\u5b8b\u94c1\u8f6e\u4e5f\u662f\u4e2a\u6025\u6027\u5b50\uff0c\u674e\u601d\u5357\u7684\u8bdd\u6ca1 \u6709\u8bf4\u5b8c\uff0c\u4ed6\u5df2\u7ecf\u8dd1\u4e86\u51fa\u53bb\uff0c\u653e\u5927\u55d3\u5b50\u56b7\u9053\uff1a\u201c\u4f60\u662f\u4e2a\u76f4\u6c49\u5b50\uff0c\u6211\u7684\u809a\u80a0\u4e5f\u4e0d\u4f1a\u62d0\u5f2f\u3002\u4f60\u77a7\u6211 \u4e0d\u987a\u773c\uff0c\u6211\u4e5f\u77a7\u4f60\u4e0d\u987a\u773c\u3002\u597d\uff0c\u54b1\u4eec\u8fd9\u5c31\u597d\u597d\u5730\u6253\u4e0a\u4e00\u67b6\u5427\u3002\u201d \u3000\u3000\u5468\u9547\u6d77\u4f7f\u7684\u662f\u85e4\u86c7\u68d2\uff0c\u8fd9\u662f\u4e00\u79cd\u8f6f\u4e2d\u5e26\u786c\u7684\u5175\u5668\uff0c\u4f7f\u7528\u8fd9\u79cd\u5175\u5668\u5168\u51ed\u8155\u529b\uff0c\u8155\u529b\u7a0d\u5dee\uff0c \u5c31\u4f7f\u5b83\u4e0d\u52a8\u3002\u5b8b\u94c1\u8f6e\u7684\u65e5\u6708\u53cc\u8f6e\u66f4\u662f\u4e00\u79cd\u7eaf\u521a\u7684\u5175\u5668\uff0c\u4e24\u4eba\u8fd9\u4e00\u8f83\u91cf\uff0c\u7b49\u4e8e\u662f\u94c1\u626b\u5e1a\u9047\u4e0a\u4e86 \u77f3\u5730\u5802\uff0c\u5fc5\u5b9a\u662f\u8981\u51ed\u7740\u786c\u529f\u592b\u6765\u51b3\u80dc\u8d1f\uff0c\u5176\u4e2d\u5b9e\u662f\u6beb\u65e0\u53ef\u4ee5\u53d6\u5de7\u4e4b\u5904\u3002 \u3000\u3000\u4e24\u4eba\u90fd\u6ca1\u6709\u5ba2\u5957\u8bdd\u8bf4\uff0c\u4e00\u7167\u9762\u7acb\u5373\u5c31\u662f\u540c\u65f6\u51fa\u62db\u3002\u5468\u9547\u6d77\u4f7f\u7684\u662f\u62e6\u8170\u76d8\u6253\u7684\u62db\u6570\uff0c\u5b8b\u94c1 \u8f6e\u65e5\u8f6e\u6a2a\u6321\uff0c\u6708\u8f6e\u5374\u662f\u81ea\u4e0a\u800c\u4e0b\u7684\u76f4\u5288\u4ed6\u7684\u5929\u7075\u76d6\u3002 \u3000\u3000\u6df3\u4e8e\u5468\u54fc\u4e86\u4e00\u58f0\uff0c\u5bf9\u7ad9\u5728\u4ed6\u65c1\u8fb9\u7684\u5d14\u9547\u5c71\u8bf4\u9053\uff1a\u201c\u53c8\u4e0d\u662f\u6709\u4ec0\u4e48\u6df1\u4ec7\u5927\u6068\uff0c\u5b8b\u94c1\u8f6e\u5c45\u7136 \u4e0b\u5f97\u8fd9\u6837\u6bd2\u624b\u3002\u201d\u8981\u77e5\u5d14\u9547\u5c71\u7684\u4e00\u8eab\u5916\u5bb6\u529f\u592b\u65e9\u5df2\u7ec3\u5230\u767b\u5cf0\u9053\u6781\u7684\u5730\u6b65\uff0c\u80dc\u8fc7\u5e08\u5f1f\u4e0d\u77e5\u591a \u5c11\u3002\u6df3\u4e8e\u5468\u6050\u6015\u4ed6\u4e0d\u80af\u4e3a\u81ea\u5df1\u6548\u529b\uff0c\u662f\u4ee5\u51fa\u8a00\u6311\u62e8\u3002 \u3000\u3000\u5d14\u9547\u5c71\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u5b8b\u94c1\u8f6e\u62db\u6570\u867d\u72e0\uff0c\u6211\u7684\u5e08\u5f1f\u4e5f\u4e0d\u89c1\u5f97\u5c31\u662f\u7a3b\u8349\u4eba\u3002\u80dc\u8d25\u7eb5\u7136\u96be\u6d4b\uff0c \u603b\u6ca1\u6709\u8fd9\u6837\u5bb9\u6613\u7ed9\u4ed6\u6253\u5012\u7684\u3002\u201d\u770b\u6765\u4ed6\u5bf9\u5e08\u5f1f\u5012\u662f\u9887\u6709\u4fe1\u5fc3\uff0c\u8bf4\u4e86\u8fd9\u8bdd\uff0c\u5c31\u6324\u4e0a\u524d\u5934\u89c2\u6218 \u53bb\uff0c\u6df3\u4e8e\u5468\u78b0\u4e86\u4e2a\u8f6f\u9489\u5b50\uff0c\u597d\u751f\u6ca1\u8da3\u3002 \u3000\u3000\u5b8b\u94c1\u8f6e\u7684\u6708\u8f6e\u76f4\u5288\u4e0b\u6765\uff0c\u53ea\u89c1\u5468\u9547\u6d77\u7684\u85e4\u86c7\u68d2\u5ffd\u5730\u4e5f\u86c7\u5934\u9ad8\u6602\uff0c\u201c\u94db\u201d\u7684\u4e00\u58f0\uff0c\u5c31\u628a\u8f6e \u5b50\u63a8\u5f00\u4e86\u3002 \u3000\u3000\u4e24\u4eba\u90fd\u6700\u5fc3\u4e2d\u4e00\u51db\uff0c\u77e5\u9053\u9047\u4e0a\u52b2\u654c\u3002\u5468\u9547\u6d77\u8361\u5f00\u4e86\u6708\u8f6e\uff0c\u68d2\u7aef\u6709\u5982\u96c1\u7fc5\u659c\u63a0\uff0c\u5288\u626b\u4e0b \u6765\uff0c\u5f84\u53d6\u5bf9\u65b9\u7684\u7435\u7436\u9aa8\u3002\u8fd9\u7435\u7436\u9aa8\u662f\u4eba\u8eab\u8981\u5bb3\u4e4b\u5904\uff0c\u5b8b\u94c1\u8f6e\u7109\u80fd\u8ba9\u4ed6\u5288\u4e2d\uff0c\u4e00\u58f0\u5927\u559d\uff0c\u65e5\u8f6e \u7ffb\u4e0a\uff0c\u53cd\u7838\u5468\u9547\u6d77\u7684\u9762\u95e8\u3002\u8fd9\u662f\u4ee5\u653b\u4e3a\u5b88\u7684\u62db\u6570\uff0c\u5468\u9547\u6d77\u4e0d\u6562\u610f\u6162\uff0c\u6025\u5c55\u85e4\u86c7\u68d2\uff0c\u4f7f\u51fa\u4e86\u4e00 \u62db\u201c\u659c\u6302\u5355\u97ad\u201d\uff0c\u5f80\u5916\u4e00\u6302\u3002\u5b8b\u94c1\u8f6e\u7acb\u523b\u62bd\u62db\u6362\u5f0f\uff0c\u8f6e\u968f\u8eab\u8f6c\uff0c\u201c\u94db\u201d\u7684\u4e00\u58f0\uff0c\u628a\u4ed6\u7684\u85e4\u86c7 \u68d2\u62e8\u5f00\u3002 \u3000\u3000\u4e24\u4eba\u90fd\u662f\u786c\u78b0\u786c\u63a5\u7684\u6253\u6cd5\uff0c\u53cc\u8f6e\u4e00\u68d2\u4fe8\u4f3c\u4e24\u56e2\u767d\u4e91\u88f9\u4f4f\u4e00\u6761\u5f20\u7259\u821e\u722a\u7684\u82cd\u9f99\uff0c\u76d8\u65cb\u98de \u821e\u3002\u8f6c\u773c\u95f4\u6597\u4e86\u4e94\u5341\u591a\u62db\uff0c\u5140\u662f\u672a\u5206\u80dc\u8d1f\u3002\u8d8a\u6597\u8d8a\u731b\uff0c\u7fa4\u96c4\u770b\u5f97\u60ca\u5fc3\u52a8\u9b44\uff0c\u5404\u81ea\u66ff\u5df1\u65b9\u7684\u4eba \u634f\u4e00\u628a\u6c57\u3002 \u3000\u3000\u6df3\u4e8e\u5468\u5374\u662f\u770b\u5f97\u6697\u6697\u76b1\u7709\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u6837\u7684\u786c\u6253\u786c\u62fc\uff0c\u53ea\u6015\u8981\u843d\u4e2a\u4e24\u8d25\u4ff1\u4f24\u3002\u201d\u8981 \u77e5\u4ed6\u8fd9\u4e00\u65b9\u5df2\u7ecf\u8f93\u4e86\u4e00\u62db\uff0c\u6df3\u4e8e\u5468\u5f53\u7136\u662f\u5e0c\u671b\u6273\u56de\u4e00\u573a\u7684\u3002\u4f46\u8f6c\u5ff5\u4e00\u60f3\uff1a\u201c\u4e24\u8d25\u4ff1\u4f24\u4e5f\u597d\uff0c \u5468\u9547\u6d77\u82e5\u53d7\u91cd\u4f24\u66f4\u5999\uff0c\u90a3\u6837\u5c31\u53ef\u4ee5\u6fc0\u5f97\u4ed6\u7684\u5e08\u5144\u4e0b\u573a\u4e86\u3002\u201d \u3000\u3000\u5c60\u9f99\u5750\u5728\u6df3\u4e8e\u5468\u4fa7\u8fb9\uff0c\u5ffd\u5730\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u8fd9\u6837\u7684\u86ee\u6253\u6ca1\u4ec0\u4e48\u770b\u5934\uff0c\u6df3\u4e8e\u524d\u8f88\uff0c\u6211\u60f3\u8bf7\u4f60 \u6307\u70b9\u8ff7\u6d25\u3002\u201d\u6df3\u4e8e\u5468\u9053\uff1a\u201c\u54e6\uff0c\u6307\u70b9\u4ec0\u4e48\uff1f\u201d\u5c60\u9f99\u9053\uff1a\u201c\u5b5f\u660e\u971e\u7684\u4e71\u73af\u5251\u8bc0\u5f88\u662f\u5389\u5bb3\uff0c\u5e94\u5f53 \u5982\u4f55\u5bf9\u4ed8\uff1f\u201d \u3000\u3000\u6df3\u4e8e\u5468\u4e5f\u6b63\u5728\u7422\u78e8\u8fd9\u4e2a\u95ee\u9898\uff0c\u4f5c\u4e0b\u4e00\u573a\u7684\u51c6\u5907\u3002\u56e0\u4e3a\u5bf9\u65b9\u7684\u5b5f\u660e\u971e\u6700\u5c11\u8fd8\u8981\u6253\u4e00\u573a\u7684\uff0c \u4ed6\u8fd9\u4e00\u65b9\uff0c\u9664\u975e\u662f\u51fa\u201c\u8fde\u73af\u593a\u547d\u5251\u201d\u67f3\u6d1e\u5929\uff0c\u6216\u8005\u53ef\u6709\u53d6\u80dc\u7684\u628a\u63e1\uff0c\u5176\u4ed6\u7684\u4eba\uff0c\u5728\u5251\u6cd5\u4e0a\u53ea \u6015\u90fd\u662f\u80dc\u4e0d\u8fc7\u5b5f\u660e\u971e\u3002\u4ed6\u81ea\u5df1\u788d\u4e8e\u8eab\u4efd\uff0c\u5374\u53c8\u4e0d\u4fbf\u4e0b\u573a\u548c\u4f84\u5973\u8f88\u7684\u5b5f\u660e\u971e\u6bd4\u5251\u3002 \u3000\u3000\u5c60\u9f99\u662f\u4ed6\u8fd9\u65b9\u4ec5\u6b21\u4e8e\u67f3\u6d1e\u5929\u7684\u5251\u672f\u9ad8\u624b\uff0c\u7528\u5c60\u9f99\u6765\u5bf9\u6297\u5b5f\u660e\u971e\uff0c\u867d\u7136\u672a\u5fc5\u80fd\u591f\u53d6\u80dc\uff0c\u5374 \u591a\u5c11\u6709\u70b9\u6307\u671b\uff0c\u662f\u4ee5\u6df3\u4e8e\u5468\u6b63\u662f\u5df4\u4e0d\u5f97\u5c60\u9f99\u6765\u5411\u4ed6\u8ba8\u6559\u3002\u4ed6\u60f3\u4e86\u4e00\u60f3\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u5b5f\u5bb6\u7684 \u4e71\u73af\u5251\u8bc0\u7740\u91cd\u7684\u662f\u6270\u654c\u5fc3\u795e\uff0c\u4ee5\u5de7\u964d\u529b\uff0c\u4f60\u9996\u5148\u5e94\u8be5\u529b\u6c42\u9547\u5b9a\uff0c\u4e0d\u53ef\u4e3a\u5bf9\u65b9\u4e71\u5212\u7684\u5251\u5708\u6270\u4e71 \u5fc3\u795e\u3002\u201d\u5c60\u9f99\u9053\uff1a\u201c\u90a3\u53c8\u600e\u6837\u624d\u53ef\u4ee5\u7834\u5979\u5462\uff1f\u201d \u3000\u3000\u6df3\u4e8e\u5468\u9053\uff1a\u201c\u4e71\u73af\u5251\u8bc0\u63a5\u7eed\u5982\u73af\uff0c\u5b9b\u8f6c\u5982\u610f\u3002\u5b5f\u660e\u971e\u7d27\u5b88\u6b64\u8bc0\u4f7f\u51fa\u7684\u5251\u6cd5\u786e\u662f\u6beb\u65e0\u7834\u7efd \u53ef\u5bfb\uff0c\u4e0d\u8fc7\uff0c\u597d\u5728\u5979\u7684\u529f\u5200\u672a\u5230\uff0c\u4f60\u53ef\u4ee5\u5192\u9669\u63d2\u5165\u5979\u7684\u5251\u5708\u4e4b\u4e2d\uff0c\u4ee5\u91cd\u624b\u6cd5\u9707\u65ad\u5979\u7684\u957f \u5251\u3002\u201d\u8bf4\u7f62\uff0c\u7528\u624b\u4e00\u6307\u6697\u6697\u5730\u6bd4\u5212\u4e86\u51e0\u4e0b\uff0c\u5c60\u9f99\u672c\u6765\u662f\u4e2a\u5f88\u806a\u660e\u7684\u4eba\uff0c\u4e0d\u987b\u6df3\u4e8e\u5468\u591a\u52a0\u8bb2 \u89e3\uff0c\u5df2\u662f\u5fc3\u9886\u795e\u4f1a\u3002 \u3000\u3000\u6b63\u5f53\u5c60\u9f99\u5411\u6df3\u4e8e\u5468\u6697\u4ed6\u91cc\u8ba8\u6559\u7684\u65f6\u5019\uff0c\u573a\u4e2d\u5df2\u662f\u5230\u4e86\u53cc\u65b9\u5168\u529b\u76f8\u62fc\uff0c\u80dc\u8d1f\u5f85\u51b3\u4e4b\u65f6\uff0c\u6fc0 \u6218\u4e2d\uff0c\u5468\u9547\u6d77\u6740\u5f97\u6027\u8d77\uff0c\u4e0d\u987e\u4e00\u5207\u5730\u62a2\u8fdb\u5b8b\u94c1\u8f6e\u53cc\u8f6e\u6240\u5408\u6210\u7684\u5708\u5b50\u4e4b\u4e2d\uff0c\u85e4\u86c7\u50a3\u4f7f\u51fa\u4e86\u201c\u72ec \u5288\u534e\u5c71\u201d\u7684\u731b\u62db\uff0c\u4e00\u68d2\u5f53\u5934\u5288\u4e0b\u3002\u5b8b\u94c1\u8f6e\u53cc\u8f6e\u4e00\u5408\uff0c\u53ea\u542c\u5f97\u201c\u94db\u201d\u7684\u4e00\u58f0\uff0c\u5982\u96f7\u9707\u8033\u3002\u53ee\u94db \u58f0\u4e2d\uff0c\u53c8\u5939\u7740\u201c\u548b\u5693\u201d\u4e00\u58f0\u5de8\u54cd\u3002 \u3000\u3000\u8fd9\u5239\u90a3\u95f4\u3002\u53cc\u65b9\u89c2\u6218\u7684\u4eba\u90fd\u662f\u4e0d\u7531\u81ea\u5df1\u5730\u7ad9\u4e86\u8d77\u6765\uff0c\u7741\u5927\u4e86\u773c\u775b\u3002\u53ea\u89c1\u5b8b\u94c1\u8f6e\u3001\u5468\u9547\u6d77 \u5404\u81ea\u5012\u9000\u4e09\u6b65\uff0c\u5468\u9547\u6d77\u624b\u4e2d\u7684\u85e4\u86c7\u68d2\u53ea\u5269\u4e0b\u4e86\u77ed\u77ed\u7684\u4e00\u622a\uff0c\u5b8b\u94c1\u8f6e\u53f3\u624b\u7684\u65e5\u8f6e\u98de\u4e0a\u4e86\u534a\u7a7a\uff0c \u5de6\u624b\u7684\u6708\u8f6e\u4e5f\u65ad\u4e86\u51e0\u9f7f\u6708\u7259\u3002\u85e4\u86c7\u68d2\u662f\u7ed9\u53cc\u8f6e\u5939\u65ad\u7684\uff0c\u4f46\u5468\u9547\u6d77\u90a3\u4e00\u5288\u7684\u5343\u94a9\u4e4b\u529b\uff0c\u5b8b\u94c1\u8f6e \u4ea6\u662f\u7ecf\u53d7\u4e0d\u8d77\u3002 \u3000\u3000\u674e\u601d\u5357\u559c\u9053\uff1a\u201c\u53cc\u65b9\u90fd\u6ca1\u53d7\u4f24\uff0c\u6b63\u597d\u53ef\u4ee5\u6536\u573a\u3002\u201d\u4e0d\u6599\u8bdd\u72b9\u672a\u4e86\uff0c\u5468\u9547\u6d77\u5ffd\u5730\u53c8\u6251\u4e0a \u6765\uff0c\u8bf4\u9053\uff1a\u201c\u5175\u5668\u4e0a\u4e0d\u5206\u80dc\u8d1f\uff0c\u5b8b\u5927\u54e5\uff0c\u6211\u9886\u6559\u4f60\u7684\u638c\u6cd5\u3002\u201d\u539f\u6765\u90a3\u6839\u85e4\u86c7\u68d2\u662f\u4e00\u4ef6\u96be\u5f97\u7684 \u5175\u5668\uff0c\u5468\u9547\u6d77\u75db\u5fc3\u4e8e\u85e4\u86c7\u68d2\u88ab\u5bf9\u65b9\u6298\u65ad\uff0c\u5b9a\u8981\u4e0e\u5b8b\u94c1\u8f6e\u5206\u51fa\u8f93\u8d62\u3002\u4e0d\u8fc7\uff0c\u4ece\u4ed6\u6539\u53e3\u4ee5\u201c\u5b8b\u5927 \u54e5\u201d\u76f8\u79f0\uff0c\u53ef\u77e5\u4ed6\u5bf9\u5b8b\u94c1\u8f6e\u4ea6\u5df2\u6709\u4e86\u51e0\u5206\u656c\u610f\u3002 \u3000\u3000\u5b8b\u94c1\u8f6e\u5c82\u7518\u793a\u5f31\uff0c\u7acb\u5373\u8bf4\u9053\uff1a\u201c\u4e45\u4ef0\u5468\u5927\u54e5\u7684\u4e94\u884c\u62f3\u529f\u529b\u6df1\u539a\uff0c\u5468\u5927\u54e5\u6709\u547d\uff0c\u6562\u4e0d\u5949 \u966a\u3002\u201d\u629b\u5f00\u4e86\u6708\u8f6e\uff0c\u6446\u5f00\u4e86\u67b6\u5f0f\uff0c\u53cc\u65b9\u567c\u567c\u556a\u556a\u5730\u5c31\u6253\u8d77\u6765\u3002 \u3000\u3000\u4f17\u4eba\u65b9\u81ea\u677e\u4e86\u53e3\u6c14\uff0c\u5fc3\u60c5\u53c8\u518d\u7d27\u5f20\u3002\u674e\u601d\u5357\u6447\u5934\u53f9\u9053\uff1a\u201c\u8fd9\u771f\u662f\u4f55\u82e6\uff01\u201d \u3000\u3000\u5468\u9547\u6d77\u7684\u4e94\u884c\u62f3\u7eaf\u4ee5\u786c\u529f\u53d6\u80dc\uff0c\u51fa\u624b\u5168\u53d6\u653b\u52bf\u3002\u7b2c\u4e00\u62db\u5b8b\u94c1\u8f6e\u786c\u63a5\u786c\u67b6\uff0c\u4e24\u80a1\u529b\u91cf\u76f8\u78b0 \u76f8\u649e\uff0c\u65d7\u9f13\u76f8\u5f53\uff0c\u6b63\u597d\u662f\u534a\u65a4\u516b\u4e24\uff0c\u53cc\u65b9\u76f8\u6301\u4e0d\u4e0b\uff0c\u5b8b\u94c1\u8f6e\u9661\u7684\u4e00\u58f0\u5927\u559d\uff0c\u62bd\u62f3\u6362\u817f\uff0c\u98de\u8d77 \u4e86\u9e33\u9e2f\u8fde\u73af\u817f\u731b\u8e22\u5bf9\u65b9\u4e0b\u76d8\u3002 \u3000\u3000\u5468\u9547\u6d77\u559d\u58f0\u201c\u6765\u5f97\u597d\uff01\u201d\u4fa7\u8eab\u5fae\u95ea\uff0c\u7acb\u5373\u4ee5\u5feb\u6377\u65e0\u4f26\u7684\u638c\u6cd5\u731b\u524a\u5b8b\u94c1\u8f6e\u7684\u819d\u76d6\u3002\u5b8b\u94c1\u8f6e \u8e22\u51fa\u4e86\u4e03\u817f\uff0c\u5468\u9547\u6d77\u524a\u4e86\u516b\u638c\uff0c\u53cc\u65b9\u500f\u8fdb\u500f\u9000\uff0c\u5b8b\u94c1\u8f6e\u6ca1\u6709\u8e22\u7740\u5bf9\u65b9\uff0c\u5373\u4e5f\u6ca1\u6709\u7ed9\u4ed6\u524a\u4e2d\uff0c \u4ecd\u7136\u662f\u4e0d\u5206\u9ad8\u4e0b\u3002 \u3000\u3000\u8fde\u73af\u817f\u4e0d\u80fd\u4e45\u6218\uff0c\u5b8b\u94c1\u8f6e\u53ea\u597d\u53c8\u518d\u6539\u7528\u5288\u6302\u638c\u5e94\u654c\uff0c\u5468\u9547\u6d77\u7684\u4e94\u884c\u62f3\u5168\u53d6\u653b\u52bf\uff0c\u524d\u62db\u672a \u6536\uff0c\u540e\u62db\u5373\u53d1\uff0c\u8fde\u7528\u201c\u5288\u3001\u94bb\u3001\u70ae\u3001\u6a2a\u3001\u5d29\u201d\u4e94\u5b57\u8bc0\uff0c\u4e94\u884c\u751f\u514b\uff0c\u62f3\u62f3\u6253\u5411\u5b8b\u94c1\u8f6e\u7684\u8981\u5bb3\uff0c \u75be\u5982\u72c2\u98ce\uff01 \u3000\u3000\u5b8b\u94c1\u8f6e\u5fc3\u5934\u706b\u8d77\uff0c\u60f3\u9053\uff1a\u201c\u4e0d\u7ed9\u4f60\u70b9\u989c\u8272\u77a7\u77a7\uff0c\u4f60\u4e5f\u4e0d\u77e5\u6211\u7684\u5389\u5bb3\uff01\u201d\u5f53\u4e0b\u7528\u201c\u5343\u65a4 \u5760\u201d\u7684\u91cd\u8eab\u6cd5\u7a33\u4f4f\u4e0b\u76d8\uff0c\u5728\u638c\u6cd5\u4e2d\u517c\u65bd\u64d2\u62ff\u5316\u89e3\u4e4b\u6280\uff0c\u5468\u9547\u6d77\u72c2\u98ce\u66b4\u96e8\u822c\u5730\u731b\u653b\u4e86\u4e00\u8f6e\uff0c\u5b8b \u94c1\u8f6e\u7ade\u662f\u5140\u7acb\u5982\u5c71\uff0c\u534a\u6b65\u4e5f\u6ca1\u6709\u9000\u8ba9\u3002\u8fd9\u4e00\u6765\u53cc\u65b9\u90fd\u662f\u4e0d\u7531\u5f97\u6697\u6697\u4f69\u670d\u3002 \u3000\u3000\u6597\u5230\u7d27\u5904\uff0c\u5468\u9547\u6d77\u7a81\u53d1\u4e00\u62f3\uff0c\u7528\u201c\u5288\u201d\u5b57\u8bc0\u5411\u7740\u5b8b\u94c1\u8f6e\u7684\u5929\u7075\u76d6\u5288\u4e0b\u3002\u62f3\u529b\u6781\u731b\u3002\u5b8b\u94c1 \u8f6e\u6a2a\u638c\u4e00\u6321\uff0c\u53cd\u624b\u64d2\u62ff\uff0c\u4f46\u56e0\u62f3\u529b\u8f83\u5f3a\uff0c\u62f3\u638c\u76f8\u62b5\uff0c\u5b8b\u94c1\u8f6e\u62f3\u5fc3\u75bc\u75db\uff0c\u64d2\u62ff\u624b\u65bd\u5c55\u4e0d\u51fa\uff0c\u53ea \u597d\u968f\u638c\u4e00\u62e8\uff0c\u628a\u5468\u9547\u6d77\u7684\u62f3\u529b\u5378\u5f00\uff0c\u5468\u9547\u6d77\u987d\u5f3a\u4e4b\u6781\uff0c\u8fd9\u4e00\u62db\u672c\u6765\u662f\u5e94\u8be5\u6536\u62db\u6362\u5f0f\u7684\uff0c\u4ed6\u7adf \u7136\u4e0d\u9000\u53cd\u8fdb\uff0c\u4e00\u4e2a\u201c\u72ee\u5b50\u6447\u5934\u201d\uff0c\u7a81\u7136\u6539\u7528\u201c\u94bb\u62f3\u201d\uff0c\u4e0a\u51fb\u654c\u4eba\u9762\u95e8\u3002\u8fd9\u4e00\u62db\u6709\u4e2a\u540d\u5802\uff0c\u53eb \u505a\u201c\u51b2\u5929\u70ae\u201d\uff0c\u5389\u5bb3\u65e0\u6bd4\uff0c\u4ed6\u7528\u5230\u4e86\u8fd9\u4e00\u62db\uff0c\u4e43\u662f\u6709\u5fc3\u548c\u5b8b\u94c1\u8f6e\u4e00\u5b9a\u8981\u5206\u51fa\u4e2a\u80dc\u8d1f\u7684\u4e86\uff01\u6b63 \u662f\uff1a \u3000\u3000\u76db\u6c14\u96be\u76f8\u4e0b\uff0c\u638c\u5e95\u5224\u96cc\u96c4\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u4e8c\u56de\u3000\u94c1\u638c\u4e89\u96c4\u55df\u8001\u5c06\u3000\u98de\u5200\u5de7\u63b7\u6298\u5f3a\u4eba \u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b \u7b2c\u4e8c\u5341\u4e8c\u56de\u3000\u94c1\u638c\u4e89\u96c4\u55df\u8001\u5c06\u3000\u98de\u5200\u5de7\u63b7\u6298\u5f3a\u4eba \u3000\u3000\u4f17\u4eba\u60ca\u547c\u4e2d\uff0c\u53ea\u89c1\u5b8b\u94c1\u8f6e\u500f\u5730\u7ffb\u8eab\uff0c\u53cd\u624b\u64d2\u62ff\uff0c\u5927\u559d\u4e00\u58f0\uff1a\u201c\u7740\uff01\u201d\u53f3\u638c\u4ece\u5468\u9547\u6d77\u65f6\u4e0b \u7a7f\u8fc7\uff0c\u5de6\u638c\u659c\u524a\u4e0b\u6765\uff0c\u53cc\u638c\u4e00\u5408\uff0c\u201c\u5494\u5693\u201d\u4e00\u58f0\uff0c\u767b\u65f6\u628a\u5468\u9547\u6d77\u7684\u4e00\u6761\u53f3\u81c2\u786c\u751f\u751f\u7684\u6298\u65ad\uff01 \u5468\u9547\u6d77\u5927\u543c\u4e00\u58f0\uff0c\u5de6\u62f3\u6325\u51fa\uff0c\u6b63\u4e2d\u5b8b\u94c1\u8f6e\u7684\u5fc3\u53e3\uff01\u5b8b\u94c1\u8f6e\u6454\u51fa\u4e86\u4e00\u4e08\u5f00\u5916\uff0c\u5468\u9547\u6d77\u624b\u81c2\u6298 \u65ad\uff0c\u75db\u5f7b\u5fc3\u80ba\uff0c\u6655\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u67f3\u4e09\u5a18\u5927\u60ca\u5931\u8272\uff0c\u8d76\u5fd9\u80de\u51fa\u6765\u6551\u63f4\u4e08\u592b\uff0c\u5d14\u9547\u5c71\u4e5f\u8d76\u51fa\u6765\u6276\u4ed6\u5e08\u5f1f\u3002 \u3000\u3000\u67f3\u4e09\u5a18\u672a\u66fe\u8dd1\u5230\u4e08\u592b\u8eab\u8fb9\uff0c\u53ea\u89c1\u5b8b\u94c1\u8f6e\u4e00\u4e2a\u201c\u9ca4\u9c7c\u6253\u633a\u201d\u7ffb\u8d77\u8eab\u6765\u3002\u67f3\u4e09\u5a18\u677e\u4e86\u53e3\u6c14\uff0c \u77e5\u9053\u4e08\u592b\u53d7\u4f24\u4e0d\u91cd\uff0c\u4f46\u53d7\u4f24\u4e4b\u540e\uff0c\u4fbf\u5373\u7528\u529b\uff0c\u603b\u662f\u4e0d\u5b9c\u3002 \u3000\u3000\u67f3\u4e09\u5a18\u7709\u5934\u4e00\u76b1\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u600e\u4e48\u4e86\u3002\u201d\u5fc3\u60f3\uff1a\u201c\u4ed6\u8fd9\u5014\u5f3a\u7684\u813e\u6c14\u771f\u662f\u81f3\u6b7b\u4e0d\u6539\uff0c\u5728\u8fd9 \u4e2a\u5f53\u53e3\uff0c\u8fd8\u8981\u901e\u5f3a\uff01\u201d \u3000\u3000\u5b8b\u94c1\u8f6e\u4e0d\u7b54\u59bb\u5b50\u7684\u8bdd\uff0c\u4e00\u8dc3\u8d77\u6765\uff0c\u4fbf\u5373\u62b1\u62f3\u8bf4\u9053\uff1a\u201c\u6bd5\u7adf\u662f\u5468\u5927\u54e5\u672c\u9886\u9ad8\u5f3a\uff0c\u6211\u53d7\u4f24 \u4e86\uff0c\u8fd9\u4e00\u573a\u6211\u662f\u7518\u5fc3\u8ba4\u8f93\uff01\u201d\u8bf4\u7f62\uff0c\u8fd9\u624d\u4e00\u53e3\u9c9c\u8840\u55b7\u4e86\u51fa\u6765\u3002 \u3000\u3000\u5b8b\u94c1\u8f6e\u7518\u613f\u4f5c\u8d1f\uff0c\u6b64\u8a00\u4e00\u51fa\uff0c\u7fa4\u96c4\u90fd\u662f\u5927\u611f\u610f\u5916\uff01\u8981\u77e5\u5b8b\u94c1\u8f6e\u867d\u7136\u662f\u53d7\u4e86\u4f24\uff0c\u5410\u4e86\u8840\uff0c \u4f46\u5bf9\u65b9\u6298\u81c2\u660f\u5012\uff0c\u53d7\u7684\u4f24\u53ef\u662f\u6bd4\u4ed6\u66f4\u91cd\u3002\u8ba4\u771f\u8bf4\u6765\uff0c\u5e94\u8be5\u7b97\u662f\u5468\u9547\u6d77\u8f93\u4e86\u3002 \u3000\u3000\u539f\u6765\u5b8b\u94c1\u8f6e\u4e0e\u5468\u9547\u6d77\u6076\u6597\u4e00\u573a\uff0c\u53cd\u800c\u8d77\u4e86\u60fa\u60fa\u76f8\u60dc\u4e4b\u611f\u3002\u6b64\u65f6\u8feb\u4e8e\u65e0\u5948\uff0c\u6298\u65ad\u4e86\u5bf9\u65b9\u7684 \u4e00\u6761\u624b\u81c2\uff0c\u5fc3\u91cc\u597d\u751f\u8fc7\u610f\u4e0d\u53bb\u3002\u662f\u4ee5\u81ea\u7518\u4f5c\u8d1f\uff0c\u8868\u793a\u656c\u91cd\u5bf9\u65b9\u3002 \u3000\u3000\u67f3\u4e09\u5a18\u719f\u6089\u4e08\u592b\u7684\u813e\u6c14\uff0c\u6697\u81ea\u53f9\u9053\uff1a\u201c\u6211\u53ea\u9053\u4ed6\u662f\u901e\u5f3a\uff0c\u5374\u539f\u6765\u4ed6\u662f\u8d76\u7740\u8d77\u6765\u8ba4\u8f93\uff0c\u751f \u6015\u4eba\u5bb6\u8bf4\u5728\u524d\u5934\uff0c\u5224\u4ed6\u5f97\u80dc\u3002\u5509\uff0c\u4ed6\u7adf\u7136\u4e0d\u987e\u81ea\u5df1\u4f24\u540e\u4e0d\u80fd\u7528\u529b\uff0c\u5f53\u771f\u662f\u4e0d\u77e5\u7231\u60dc\u81ea\u5df1\u7684\u8eab \u5b50\u4e86\uff01\u4f46\u8fd9\u4e5f\u624d\u771f\u6b63\u8bf4\u5f97\u662f\u6027\u5b50\u5014\u5f3a\u7684\u786c\u6c49\u5b50\u5927\u4e08\u592b\uff01\u201d \u3000\u3000\u5d14\u9547\u5c71\u8f7b\u8f7b\u7ed9\u5e08\u5f1f\u63a8\u62ff\uff0c\u5468\u9547\u6d77\u9192\u4e86\u8fc7\u6765\uff0c\u53eb\u9053\uff1a\u201c\u4e0d\u6210\uff01\u4e0d\u6210\uff01\u201d\u4f17\u4eba\u83ab\u540d\u5176\u5999\uff0c\u4e00 \u65f6\u95f4\u542c\u4e0d\u61c2\u4ed6\u8bf4\u7684\u662f\u4ec0\u4e48\u610f\u601d\u3002\u5d14\u9547\u5c71\u5728\u4ed6\u8033\u8fb9\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u5e08\u5f1f\uff0c\u4f60\u522b\u96be\u8fc7\u3002\u5f85\u5b8b\u94c1\u8f6e\u7684 \u4f24\u517b\u597d\u4e86\uff0c\u6211\u518d\u53bb\u627e\u4ed6\u4e3a\u4f60\u62a5\u4ec7\u3002\u201d \u3000\u3000\u4e0d\u6599\u5468\u9547\u6d77\u63a5\u7740\u5c31\u9053\uff1a\u201c\u4e0d\u6210\uff0c\u4e0d\u6210\uff01\u8fd9\u600e\u80fd\u7b97\u662f\u4f60\u8f93\uff1f\u6211\u7684\u4f24\u6bd4\u4f60\u91cd\uff0c\u5f53\u7136\u662f\u6211\u8f93 \u4e86\uff0c\u6211\u53ef\u4e0d\u80fd\u5360\u4f60\u7684\u4fbf\u5b9c\uff01\u201d\u4ed6\u8bf4\u4e86\u8fd9\u51e0\u53e5\u8bdd\uff0c\u75db\u5f97\u9762\u4e0a\u53d8\u8272\u3002 \u3000\u3000\u5d14\u9547\u5c71\u5c06\u4ed6\u62b1\u8d77\uff0c\u8bf4\u9053\uff1a\u201c\u5e08\u5f1f\uff0c\u4f60\u517b\u4f24\u8981\u7d27\uff01\u201d\u5468\u9547\u6d77\u9053\uff1a\u201c\u5e08\u54e5\uff0c\u613f\u6253\u670d\u8f93\uff0c\u62a5\u4ec7 \u4e8c\u5b57\uff0c\u4f60\u518d\u4e5f\u4f11\u63d0\u3002\u514d\u5f97\u6211\u5fc3\u91cc\u4e0d\u5b89\uff01\u201d \u3000\u3000\u4e24\u4eba\u62a2\u7740\u8ba4\u8f93\uff0c\u7fa4\u96c4\u542c\u4e86\uff0c\u4e0d\u7981\u53c8\u662f\u597d\u7b11\uff0c\u53c8\u662f\u4f69\u670d\u3002\u8463\u5f00\u5c71\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u4e24\u4f4d\u5149\u660e\u78ca \u843d\uff0c\u90fd\u662f\u4e00\u822c\u7684\u597d\u6c49\u5b50\u5927\u5927\u592b\u3002\u8fd9\u4e00\u573a\u5c31\u7b97\u662f\u6253\u5e73\u4e86\u5427\uff01\u4ec0\u4e48\u4f24\u8f7b\u4f24\u91cd\uff0c\u4f55\u5fc5\u65a4\u65a4\u8ba1\u8f83\uff01\u201d \u6b64\u8a00\u6b63\u5408\u4f17\u5fc3\uff0c\u53cc\u65b9\u5747\u65e0\u5f02\u8bae\u3002 \u3000\u3000\u6df3\u4e8e\u5468\u53d6\u51fa\u4e00\u4e2a\u76d2\u5b50\uff0c\u6311\u51fa\u4e86\u4e00\u56e2\u836f\u818f\uff0c\u8bf4\u9053\uff1a\u201c\u8fd8\u662f\u7528\u6211\u8fd9\u7eed\u65ad\u818f\u5427\uff0c\u6211\u66ff\u4ee4\u5f1f\u63a5 \u9aa8\u3002\u201d \u3000\u3000\u6df3\u4e8e\u5468\u8fd9\u7eed\u65ad\u818f\u4e43\u662f\u7528\u5343\u5e74\u201c\u7eed\u65ad\u201d\u5236\u70bc\u7684\u836f\u818f\uff0c\u73cd\u8d35\u65e0\u6bd4\uff0c\u7eed\u7b4b\u9a73\u9aa8\uff0c\u529f\u6548\u5982\u795e\uff0c\u5d14 \u9547\u5c71\u5982\u679c\u662f\u81ea\u5df1\u53d7\u4f24\uff0c\u51b3\u4e0d\u613f\u53d7\u4ed6\u6069\u60e0\uff0c\u4f46\u56e0\u662f\u5e08\u5f1f\u53d7\u4e86\u91cd\u4f24\uff0c\u53ea\u597d\u63a5\u53d7\u4e0b\u6765\uff0c\u5411\u4ed6\u9053\u8c22\u3002 \u3000\u3000\u6df3\u4e8e\u5468\u53eb\u4eba\u7528\u4e24\u5757\u6728\u677f\uff0c\u5939\u7740\u5468\u9547\u6d77\u7684\u65ad\u81c2\uff0c\u6577\u4e86\u836f\u818f\uff0c\u5bf9\u6b63\u4f24\u53e3\uff0c\u63a5\u597d\u65ad\u9aa8\uff0c\u624b\u6cd5\u7eaf \u719f\u4e4b\u6781\uff0c\u5d14\u9547\u5c71\u867d\u7136\u662f\u8ba8\u538c\u4ed6\uff0c\u4e5f\u4e0d\u7531\u5f97\u6709\u70b9\u611f\u6fc0\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5982\u679c\u7531\u6211\u65bd\u6551\uff0c\u5e08\u5f1f\u53ea\u6015 \u8981\u591a\u5403\u8bb8\u591a\u82e6\u5934\u3002\u201d \u3000\u3000\u6df3\u4e8e\u5468\u66ff\u5468\u9547\u6d77\u63a5\u597d\u65ad\u9aa8\uff0c\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u4ee4\u5e08\u5f1f\u613f\u6253\u670d\u8f93\uff0c\u4e0d\u6127\u662f\u4e2a\u5149\u660e\u78ca\u843d\u7684\u6c49\u5b50\u3002 \u4f46\u4ed6\u662f\u7528\u2018\u4e94\u884c\u62f3\u2019\u6298\u5728\u5b8b\u94c1\u8f6e\u7684\u624b\u4e0b\u7684\uff0c\u5b8b\u94c1\u8f6e\u4e0d\u8fc7\u662f\u7eff\u6797\u4e2d\u7684\u4e8c\u6d41\u811a\u8272\uff0c\u65c1\u4eba\u4e0d\u77e5\u9053\uff0c \u6050\u6015\u4f1a\u770b\u8f7b\u4e86\u2018\u4e94\u884c\u62f3\u2019\u4e86\uff01\u201d \u3000\u3000\u6df3\u4e8e\u5468\u8fd9\u8bdd\u7528\u610f\u5341\u5206\u660e\u663e\uff0c\u662f\u8981\u6fc0\u5d14\u9547\u5c71\u4e0b\u573a\u7684\u3002\u5d14\u9547\u5c71\u4e5f\u660e\u77e5\u4ed6\u662f\u6fc0\u5c06\u4e4b\u8ba1\uff0c\u4f46\u4e00\u6765 \u53d7\u4e86\u4ed6\u7684\u6069\u60e0\uff0c\u4e8c\u6765\u53c8\u662f\u6d89\u53ca\u5e08\u95e8\u8363\u8fb1\uff0c\u660e\u77e5\u6df3\u4e8e\u5468\u8981\u5229\u7528\u4ed6\uff0c\u4e5f\u53ea\u597d\u8bf4\u9053\uff1a\u201c\u6df3\u4e8e\u524d\u8f88\uff0c \u4f60\u522b\u5fd9\u50ac\u6211\uff0c\u5d14\u67d0\u7d20\u6765\u662f\u6069\u6028\u5206\u660e\u3002\u4eca\u65e5\u591a\u8499\u4f60\u6551\u4e86\u6211\u7684\u5e05\u5144\uff0c\u5d14\u67d0\u5b9a\u5f53\u8bbe\u6cd5\u62a5\u7b54\u4e8e\u4f60\uff01\u201d \u3000\u3000\u67f3\u4e09\u5a18\u6b63\u8981\u56de\u53bb\u7167\u6599\u4e08\u592b\uff0c\u6df3\u4e8e\u5468\u8fd9\u8fb9\u6709\u4e00\u4e2a\u4eba\u5df2\u662f\u5230\u4e86\u573a\u5fc3\uff0c\u53eb\u9053\uff1a\u201c\u67f3\u4e09\u5a18\uff0c\u4e14\u6162 \u56de\u53bb\uff01\u201d\u67f3\u4e09\u5a18\u56de\u8fc7\u5934\u6765\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u662f\u535c\u5be8\u4e3b\u3002\u6562\u60c5\u535c\u5be8\u4e3b\u6709\u610f\u8d50\u6559\u4e48\uff1f\u201d\u90a3 \u4eba\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u535c\u67d0\u4e45\u4ef0\u67f3\u4e09\u5a18\u7684\u795e\u97ad\u7edd\u6280\uff0c\u6697\u5668\u529f\u592b\uff0c\u5c24\u5176\u4e86\u5f97\u3002\u535c\u67d0\u4e0d\u81ea\u91cf\u529b\uff0c\u8981\u5411\u4e09 \u5a18\u8ba8\u6559\u51e0\u624b\u9ad8\u62db\uff01\u201d \u3000\u3000\u539f\u6765\u8fd9\u4eba\u540d\u53eb\u56fe\u5357\uff0c\u5916\u53f7\u201c\u591a\u81c2\u733f\u201d\uff0c\u4ee5\u4e00\u624b\u6697\u5668\u529f\u592b\u9a70\u8a89\u5927\u6c5f\u5357\u5317\u3002\u4f46\u81ea\u67f3\u4e09\u5a18\u5d1b\u8d77 \u540e\uff0c\u4ed6\u8fd9\u64c5\u4f7f\u6697\u5668\u7684\u540d\u5934\uff0c\u5374\u662f\u4e0d\u514d\u7ed9\u67f3\u4e09\u5a18\u593a\u4e86\u4ed6\u7684\u51e0\u5206\u5149\u5f69\u3002\u4fd7\u8bed\u8bf4\uff1a\u201c\u4e00\u5c71\u4e0d\u80fd\u85cf\u4e8c \u864e\u201d\uff0c\u662f\u4ee5\u4ed6\u5b9a\u8981\u4e0e\u67f3\u4e09\u5a18\u5206\u4e2a\u9ad8\u4f4e\u3002 \u3000\u3000\u67f3\u4e09\u5a18\u9053\uff1a\u201c\u597d\u8bf4\uff0c\u597d\u8bf4\uff01\u5be8\u4e3b\u7684\u5224\u5b98\u7b14\u70b9\u7a74\u529f\u592b\u548c\u7779\u5668\u672c\u9886\u6211\u4e5f\u662f\u4e45\u4ef0\u7684\u4e86\u3002\u535c\u5be8\u4e3b \u65e2\u662f\u4e0d\u541d\u6307\u6559\uff0c\u5c31\u8bf7\u8d50\u62db\u5427\uff01\u201d \u3000\u3000\u535c\u56fe\u5357\u9053\uff1a\u201c\u5ba2\u4e0d\u50ed\u4e3b\uff0c\u8fd8\u662f\u8bf7\u4e09\u5a18\u5148\u8d50\u9ad8\u62db\uff01\u201d\u8bdd\u4f3c\u5ba2\u6c14\uff0c\u795e\u60c5\u5374\u662f\u751a\u4e3a\u72c2\u5984\u3002\u535c\u56fe \u5357\u5728\u7eff\u6797\u4e2d\u7684\u884c\u4e8b\u4e00\u5411\u662f\u5fc3\u72e0\u624b\u8fa3\uff0c\u67f3\u4e09\u5a18\u672c\u6765\u5c31\u5bf9\u4ed6\u6ca1\u6709\u597d\u611f\uff0c\u6b64\u65f6\u89c1\u4ed6\u795e\u60c5\u50b2\u6162\uff0c\u5fc3\u91cc \u4e5f\u662f\u4e0d\u7981\u6709\u6c14\uff0c\u4fbf\u9053\uff1a\u201c\u597d\uff0c\u90a3\u6211\u5c31\u4e0d\u5ba2\u6c14\u4e86\uff01\u201d\u957f\u97ad\u4e00\u6325\uff0c\u767b\u65f6\u5377\u8d77\u4e86\u4e00\u56e2\u97ad\u5f71\uff0c\u5411\u535c\u56fe \u5357\u626b\u53bb\u3002 \u3000\u3000\u535c\u56fe\u5357\u7528\u7684\u662f\u4e00\u652f\u5224\u5b98\u7b14\u3002\u5224\u5b98\u7b14\u4e00\u822c\u662f\u53cc\u7b14\u5408\u4f7f\u7684\uff0c\u4ed6\u5374\u53ea\u7528\u5355\u7b14\uff0c\u800c\u4e14\u6bd4\u666e\u901a\u7684\u5224 \u5b98\u7b14\u957f\u5f97\u591a\uff0c\u53ef\u4ee5\u5f53\u4f5c\u5c0f\u82b1\u67aa\u4f7f\u3002\u67f3\u4e09\u5a18\u957f\u97ad\u6253\u6765\uff0c\u4ed6\u7528\u5224\u5b98\u7b14\u8f7b\u8f7b\u4e00\u6311\u5c31\u6311\u5f00\u4e86\u3002 \u3000\u3000\u54ea\u77e5\u67f3\u4e09\u5a18\u7684\u97ad\u6cd5\u5de7\u5999\u975e\u5e38\uff0c\u5979\u8fd9\u4e00\u62db\u540d\u53eb\u201c\u56de\u98ce\u62c2\u67f3\u201d\uff0c\u4e00\u8361\u8d77\u6765\uff0c\u4ece\u534a\u7a7a\u4e2d\u500f\u5730\u4e00 \u6296\uff0c\u63a5\u8fde\u51e0\u4e2a\u8f6c\u6298\uff0c\u97ad\u68a2\u547c\u54cd\uff0c\u97ad\u5f71\u91cd\u91cd\uff0c\u7adf\u7136\u4e0d\u77e5\u5979\u662f\u6253\u5411\u4f55\u65b9\u3002\u535c\u56fe\u5357\u628a\u5224\u5b98\u7b14\u76d8\u5934\u6325 \u821e\uff0c\u53eb\u9053\uff1a\u201c\u795e\u97ad\u7edd\u6280\uff0c\u679c\u7136\u540d\u4e0d\u865a\u4f20\uff01\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u67f3\u4e09\u5a18\u7684\u957f\u97ad\u5df2\u662f\u7f20\u4e0a\u4ed6\u7684\u7b14\u8eab\uff01 \u3000\u3000\u535c\u56fe\u5357\u4e00\u58f0\u5927\u559d\uff0c\u9661\u5730\u632f\u7b14\u4e00\u6325\uff0c\u7f20\u5728\u7b14\u4e0a\u7684\u8f6f\u97ad\u767b\u65f6\u89e3\u4e86\u3002\u97ad\u98ce\u7b14\u5f71\u4e4b\u4e2d\uff0c\u535c\u56fe\u5357\u6b3a \u8eab\u731b\u6251\uff0c\u626c\u7b14\u70b9\u67f3\u4e09\u5a18\u7684\u201c\u6108\u6c14\u7a74\u201d\uff0c\u67f3\u4e09\u5a18\u659c\u8eab\u4e00\u63a0\uff0c\u8eab\u968f\u97ad\u8f6c\uff0c\u97ad\u68a2\u63a0\u8fc7\u4e4b\u5904\uff0c\u535c\u56fe\u5357 \u5934\u6234\u7684\u6be1\u5e3d\u5df2\u7ed9\u8f6f\u97ad\u626b\u843d\uff0c\u67f3\u4e09\u5a18\u843d\u5728\u4e09\u4e08\u4e4b\u5916\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u535c\u5be8\u4e3b\uff0c\u4f60\u8981\u4e0d\u8981\u6b47\u4e00\u4f1a \u513f\u3002\u201d \u3000\u3000\u539f\u6765\u67f3\u4e09\u5a18\u4ee5\u201c\u56de\u98ce\u626b\u53f6\u201d\u7684\u795e\u97ad\u7edd\u6280\uff0c\u7f20\u4e0a\u4e86\u7b14\u8eab\uff0c\u672c\u6765\u662f\u60f3\u593a\u4e86\u4ed6\u8fd9\u652f\u5224\u5b98\u7b14\u7684\uff0c \u4f46\u4e0d\u6210\u529f\uff0c\u97ad\u68a2\u4e00\u6296\uff0c\u4e58\u673a\u5c31\u70b9\u4e86\u4ed6\u7684\u201c\u80a9\u9631\u7a74\u201d\uff0c\u518d\u4e00\u63a0\uff0c\u53c8\u626b\u843d\u4e86\u4ed6\u7684\u6be1\u5e3d\u3002 \u3000\u3000\u8bba\u5185\u529b\u662f\u535c\u56fe\u5357\u8f83\u5f3a\uff0c\u4ed6\u80fd\u591f\u628a\u7f20\u5728\u7b14\u4e0a\u7684\u8f6f\u97ad\u6296\u5f00\uff0c\u8fd9\u4efd\u529f\u529b\u6bd4\u67f3\u4e09\u5a18\u80dc\u8fc7\u4e0d\u53ea\u4e00 \u7b79\u3002\u4f46\u8bba\u62db\u6570\uff0c\u4ed6\u5219\u662f\u6309\u8fde\u8f93\u4e86\u4e24\u62db\u3002\u5f7c\u6b64\u90fd\u662f\u7eff\u6797\u7684\u6210\u540d\u4eba\u7269\uff0c\u6309\u8bf4\u4ed6\u662f\u5e94\u8be5\u8ba4\u8f93\u4e86\u7684\uff0c \u4f46\u4ed6\u660e\u77e5\u67f3\u4e09\u5a18\u7684\u5b9e\u529b\u6bd4\u4e0d\u4e0a\u5730\uff0c\u800c\u4ed6\u7684\u770b\u5bb6\u672c\u9886\u53c8\u672a\u4f7f\u51fa\uff0c\u81ea\u662f\u4e0d\u80af\u7518\u4f11\uff01 \u3000\u3000\u67f3\u4e09\u5a18\u70b9\u7740\u4e86\u4ed6\u7684\u201c\u80a9\u9631\u7a74\u201d\uff0c\u89c1\u4ed6\u5c45\u7136\u8fd8\u662f\u7eb5\u8dc3\u5982\u98de\uff0c\u77f3\u81c2\u4e5f\u5e76\u65e0\u6325\u52a8\u4e0d\u7075\u7684\u8ff9\u8c61\uff0c \u5fc3\u91cc\u4e5f\u662f\u597d\u751f\u9a87\u7136\uff0c\u60f3\u9053\uff1a\u201c\u6b64\u4eba\u4e0d\u4f46\u5185\u529b\u6df1\u539a\uff0c\u70b9\u7a74\u89e3\u7a74\u7684\u529f\u592b\u4e5f\u662f\u5728\u6211\u4e4b\u4e0a\u3002\u770b\u6765\u6211\u662f \u4e0d\u5b9c\u548c\u4ed6\u786c\u6253\u53bb\u4e86\uff0c\u8981\u5236\u80dc\u975e\u5f97\u4f7f\u7528\u6697\u5668\u4e0d\u53ef\uff01\u201d \u3000\u3000\u5c82\u77e5\u535c\u56fe\u5357\u662f\u540c\u6837\u60f3\u6cd5\uff1a\u201c\u8fd9\u5a46\u5a18\u7684\u8f7b\u529f\u6bd4\u6211\u9ad8\u660e\uff01\u62db\u6570\u4e5f\u662f\u6bd4\u6211\u5947\u8be1\u3002\u5979\u7684\u97ad\u957f\uff0c\u6211 \u7684\u7b14\u77ed\uff0c\u8981\u9632\u5fa1\u5979\u8fd9\u6761\u795e\u51fa\u9b3c\u6ca1\u7684\u957f\u97ad\u771f\u4e0d\u5bb9\u6613\uff01\u770b\u6765\u8fd8\u662f\u7acb\u5373\u548c\u5979\u8f83\u91cf\u6697\u5668\u4e3a\u4f73\uff01\u201d \u3000\u3000\u53cc\u65b9\u90fd\u662f\u77e5\u5df1\u77e5\u5f7c\u7684\u9ad8\u624b\uff0c\u67f3\u4e09\u5a18\u5403\u4e8f\u5728\u6c14\u529b\u8f83\u5dee\uff0c\u7ed9\u535c\u56fe\u5357\u4e00\u4e2a\u731b\u6251\uff0c\u4e0d\u80fd\u4e0d\u5012\u7eb5\u5f00 \u53bb\uff0c\u5979\u8eab\u5f62\u672a\u7a33\uff0c\u535c\u56fe\u5357\u5df2\u662f\u51fa\u624b\uff01 \u3000\u3000\u53ea\u542c\u5f97\u201c\u94ee\u94ee\u201d\u6570\u58f0\uff0c\u535c\u56fe\u5357\u5f39\u6307\u53d1\u51fa\u4e86\u4e09\u679a\u94b1\u9556\uff0c\u5411\u4e0a\u4e2d\u4e0b\u4e09\u8def\u6253\u6765\u3002\u4e0a\u53d6\u7709\u5c16\u7684 \u201c\u9633\u767d\u7a74\u201d\uff0c\u4e2d\u53d6\u80f8\u53e3\u7684\u201c\u4e73\u7a81\u7a74\u201d\uff0c\u4e0b\u53d6\u819d\u76d6\u7684\u201c\u73af\u8df3\u7a74\u201d\u3002\u8fd9\u4e09\u679a\u94b1\u9556\u662f\u540c\u65f6\u53d1\u51fa\u7684\uff0c \u5230\u4e86\u654c\u4eba\u8ddf\u524d\uff0c\u7adf\u7136\u80fd\u5206\u6210\u4e0a\u4e2d\u4e0b\u4e09\u8def\uff0c\u8ba4\u7a74\u4e0d\u5dee\u6beb\u5398\uff0c\u5f53\u771f\u662f\u4e00\u7b49\u4e00\u7684\u9ad8\u660e\u624b\u6cd5\uff01 \u3000\u3000\u67f3\u4e09\u5a18\u970d\u5730\u4e00\u4e2a\u51e4\u70b9\u5934\uff0c\u907f\u8fc7\u4e86\u6253\u5411\u4e0a\u76d8\u7684\u94b1\u9556\uff0c\u957f\u97ad\u500f\u5730\u6536\u56de\uff0c\u6253\u843d\u4e86\u5c04\u5411\u80f8\u53e3\u7684\u94b1 \u9556\uff0c\u4e00\u4f38\u817f\uff0c\u201c\u53ee\u201d\u7684\u4e00\u58f0\uff0c\u53c8\u628a\u6253\u5979\u819d\u76d6\u7684\u90a3\u679a\u94b1\u9556\u8e22\u98de\u4e86\uff0c\u5979\u811a\u4e0a\u7a7f\u7684\u662f\u4e00\u53cc\u9576\u6709\u94c1\u7247 \u7684\u978b\u5b50\u3002 \u3000\u3000\u8fd9\u51e0\u4e0b\u5154\u8d77\u9e5e\u843d\uff0c\u535c\u56fe\u5357\u7684\u6697\u5668\u56fa\u7136\u662f\u53d1\u5f97\u5999\uff0c\u67f3\u4e09\u5a18\u95ea\u6321\u6697\u5668\u7684\u529f\u592b\u66f4\u662f\u5999\u5230\u6beb\u98a0\u3002 \u7fa4\u96c4\u90fd\u4e0d\u7981\u5927\u58f0\u559d\u5f69\u3002 \u3000\u3000\u535c\u56fe\u5357\u62a2\u4e86\u5148\u53d1\u5236\u4eba\u4e4b\u5229\uff0c\u4e0d\u5bb9\u67f3\u4e09\u5a18\u6709\u5598\u606f\u7684\u673a\u4f1a\uff0c\u4e00\u626c\u624b\u53c8\u662f\u4e09\u67c4\u98de\u9525\u6253\u6765\u3002 \u3000\u3000\u98de\u9525\u662f\u5206\u91cf\u6c89\u91cd\u7684\u6697\u5668\uff0c\u6253\u843d\u94b1\u9556\u5bb9\u6613\uff0c\u8981\u6253\u843d\u98de\u9525\u53ef\u5c31\u96be\u5f97\u591a\u4e86\u3002\u535c\u56fe\u5357\u624b\u6bb5\u72e0\u8fa3\u4e4b \u6781\uff0c\u4e09\u67c4\u98de\u9525\u521a\u53d1\uff0c\u8ddf\u7740\u53c8\u662f\u53cc\u624b\u9f50\u626c\uff0c\u7eed\u53d1\u516d\u67c4\u98de\u9525\uff0c\u5de6\u9762\u4e09\u67c4\uff0c\u53f3\u9762\u4e09\u67c4\uff0c\u5835\u6b7b\u4e86\u67f3\u4e09 \u5a18\u5411\u4e24\u8fb9\u95ea\u8eb2\u7684\u8def\u3002\u6700\u5148\u53d1\u7684\u4e09\u67c4\u98de\u9525\u5219\u662f\u5411\u5979\u6b63\u4e2d\u98de\u6765\u3002 \u3000\u3000\u5728\u573a\u7684\u7eff\u6797\u8c6a\u5ba2\uff0c\u4eba\u4eba\u90fd\u4f1a\u4f7f\u7528\u6697\u5668\uff0c\u4f46\u50cf\u535c\u56fe\u5357\u8fd9\u6837\u9ad8\u660e\u800c\u53c8\u72e0\u8fa3\u7684\u6697\u5668\u529f\u592b\uff0c\u5374\u662f \u4ece\u672a\u89c1\u8fc7\uff0c\u662f\u4ee5\u672c\u6765\u662f\u5e94\u8be5\u559d\u5f69\u7684\uff0c\u6b64\u65f6\u5374\u5ffd\u7136\u9e26\u96c0\u65e0\u58f0\uff0c\u5927\u5bb6\u90fd\u5728\u63d0\u5fc3\u540a\u80c6\uff0c\u4e3a\u67f3\u4e09\u5a18\u7684 \u6027\u547d\u62c5\u5fe7\u4e86\u3002 \u3000\u3000\u53ea\u542c\u5f97\u67f3\u4e09\u5a18\u4e00\u58f0\u957f\u5578\uff0c\u53eb\u9053\uff1a\u201c\u535c\u5be8\u4e3b\u597d\u6697\u5668\u529f\u592b\uff01\u201d\u957f\u97ad\u6325\u51fa\uff0c\u95ea\u7535\u822c\u5730\u6253\u4e86\u4e00\u4e2a \u76d8\u65cb\uff0c\u5de6\u64a9\u53f3\u62d4\u3002\u5411\u5979\u6b63\u9762\u6253\u6765\u7684\u4e09\u67c4\u98de\u9525\uff0c\u7ed9\u97ad\u68a2\u62e8\u6b6a\u4e86\u51c6\u5934\uff0c\u4e00\u67c4\u5411\u5de6\u8fb9\u98de\u53bb\uff0c\u4e00\u67c4\u5411 \u53f3\u8fb9\u98de\u53bb\uff0c\u4f46\u5f53\u4e2d\u7684\u4e00\u67c4\u4ecd\u7136\u5411\u5979\u98de\u6765\uff0c\u67f3\u4e09\u5a18\u6234\u4e0a\u9e7f\u76ae\u624b\u5957\uff0c\u4f38\u624b\u4e00\u63a5\uff0c\u63a5\u4e0b\u4e86\u8fd9\u67c4\u98de \u9525\u3002 \u3000\u3000\u4e24\u67c4\u98de\u9525\u5de6\u53f3\u98de\u51fa\uff0c\u521a\u597d\u78b0\u4e0a\u4e86\u535c\u56fe\u5357\u4ece\u4e24\u9762\u5411\u5979\u6253\u6765\u7684\u4e24\u7ec4\u98de\u9525\uff0c\u6bcf\u7ec4\u98de\u9525\u867d\u6709\u4e09 \u67c4\uff0c\u4f46\u4e00\u7ecf\u78b0\u649e\uff0c\u8fde\u73af\u4e92\u51fb\uff0c\u90fd\u662f\u5931\u4e86\u51c6\u5934\u3002\u67f3\u4e09\u5a18\u53ea\u987b\u63a5\u4e86\u6b63\u4e2d\u7684\u4e00\u67c4\u98de\u9525\uff0c\u5c31\u4ece\u5bb9\u4e0d\u8feb \u7684\u4ece\u6b63\u9762\u95ef\u51fa\u4e86\u98de\u9525\u4e4b\u9635\u3002 \u3000\u3000\u67f3\u4e09\u5a18\u672c\u6765\u8981\u628a\u63a5\u4e0b\u7684\u8fd9\u67c4\u98de\u9525\u53cd\u6253\u56de\u53bb\u7684\uff0c\u4f46\u63a5\u5230\u4e86\u624b\u4e4b\u65f6\uff0c\u864e\u53e3\u7adf\u7136\u611f\u5230\u4e00\u8fb9\u9178 \u9ebb\uff0c\u60c5\u77e5\u6253\u51fa\u53bb\u4e5f\u4e0d\u4f1a\u6253\u4e2d\u5bf9\u65b9\uff0c\u732e\u4e11\u4e0d\u5982\u85cf\u62d9\uff0c\u7d22\u6027\u6545\u793a\u5927\u65b9\uff0c\u629b\u4e0b\u4e86\u98de\u9525\uff0c\u7b11\u9053\uff1a\u201c\u535c \u5be8\u4e3b\uff0c\u4f60\u7684\u6697\u5668\u6253\u5b8c\u4e86\u6ca1\u6709\uff1f\u8bf7\u4e00\u5e76\u6253\u6765\u5427\uff01\u201d \u3000\u3000\u539f\u6765\u67f3\u4e09\u5a18\u7684\u5185\u529b\u4e0d\u53ca\u5bf9\u65b9\uff0c\u82e5\u662f\u51ed\u7740\u786c\u529f\u592b\u626b\u6253\u7684\u8bdd\uff0c\u4e00\u67c4\u98de\u9525\u5979\u4e5f\u4e0d\u80fd\u6253\u843d\uff0c\u4f46\u5999 \u5c31\u5999\u5728\u5979\u501f\u529b\u4f7f\u52b2\u7684\u529f\u592b\u4f7f\u5f97\u6070\u5230\u597d\u5904\uff0c\u62e8\u6b6a\u4e24\u67c4\u98de\u9525\uff0c\u501f\u5bf9\u65b9\u7684\u52b2\u529b\uff0c\u6253\u4e71\u5176\u4ed6\u7684\u98de\u9525\uff0c \u8fd9\u624d\u80fd\u591f\u5316\u9669\u4e3a\u5937\u3002 \u3000\u3000\u535c\u56fe\u5357\u60f3\u4e0d\u5230\u4ed6\u53d1\u51fa\u7684\u4e09\u9762\u5939\u653b\u654c\u4eba\u7684\u9525\u9635\uff0c\u7adf\u7136\u7ed9\u67f3\u4e09\u5a18\u7528\u5982\u6b64\u5de7\u5999\u7684\u624b\u6cd5\u8f7b\u63cf\u6de1\u5199 \u5730\u7834\u4e86\uff0c\u5fc3\u4e2d\u4e0d\u7981\u4e00\u9635\u832b\u7136\u3002 \u3000\u3000\u6b64\u65f6\u7fa4\u96c4\u65b9\u59cb\u653e\u677e\u4e86\u6263\u7d27\u7684\u5fc3\u5f26\uff0c\u767b\u65f6\u7206\u53d1\u4e86\u5982\u96f7\u9707\u8033\u7684\u559d\u5f69\u58f0\uff01 \u3000\u3000\u67f3\u4e09\u5a18\u53eb\u535c\u56fe\u5357\u628a\u5269\u4e0b\u7684\u6697\u5668\u4e00\u5e76\u5411\u5979\u6253\u6765\uff0c\u8fd9\u8bdd\u8bf4\u5f97\u5341\u5206\u6316\u82e6\uff0c\u535c\u56fe\u5357\u6bd5\u7adf\u662f\u7eff\u6797\u4e2d \u7684\u6210\u540d\u4eba\u7269\uff0c\u5982\u4f55\u80fd\u591f\u7ed9\u522b\u4eba\u770b\u5c0f\uff1f\u5f53\u4e0b\u53ea\u597d\u52c9\u5f3a\u7b11\u9053\uff1a\u201c\u535c\u67d0\u4eca\u5929\u662f\u629b\u7816\u5f15\u7389\uff0c\u5982\u4eca\u662f\u8be5 \u89c1\u8bc6\u4e09\u5a18\u7684\u9ad8\u62db\uff01\u201d\u5fc3\u4e2d\u5219\u5728\u76d8\u7b97\uff0c\u5982\u4f55\u5728\u6321\u8fc7\u67f3\u4e09\u5a18\u7684\u4e00\u9635\u6697\u5668\u4e4b\u540e\uff0c\u518d\u65bd\u53cd\u51fb\uff0c\u633d\u56de\u9762 \u5b50\uff0c\u6b64\u65f6\u4ed6\u5df2\u4e0d\u6c42\u6253\u80dc\u5bf9\u65b9\uff0c\u53ea\u6c42\u4e24\u65e0\u635f\u4f24\uff0c\u6253\u6210\u5e73\u624b\uff0c\u4e8e\u613f\u5df2\u8db3\u3002 \u3000\u3000\u67f3\u4e09\u5a18\u7528\u8bf4\u8bdd\u201c\u514b\u201d\u4f4f\u5bf9\u65b9\uff0c\u6b64\u65f6\u5012\u4e0d\u6025\u4e8e\u51fa\u624b\u4e86\u3002\u5f53\u4e0b\u4ece\u5bb9\u7b11\u9053\uff1a\u201c\u535c\u5be8\u4e3b\u5ba2\u6c14\u4e86\uff0c \u4f46\u6765\u800c\u4e0d\u5f80\u975e\u793c\u4e5f\uff0c\u535c\u5be8\u4e3b\u65e2\u7136\u5982\u6b64\u5ba2\u6c14\uff0c\u90a3\u6211\u5c31\u8fd8\u656c\u5427\uff0c\u8bf7\u535c\u5be8\u4e3b\u6307\u6559\uff01\u201d \u3000\u3000\u67f3\u4e09\u5a18\u8bf4\u7f62\uff0c\u626c\u624b\u53d1\u51fa\u4e09\u70b3\u67f3\u53f6\u5200\u3002\u4e09\u67c4\u98de\u5200\u6392\u6210\u201c\u54c1\u201d\u5b57\u5411\u535c\u56fe\u5357\u5c04\u6765\uff0c\u535c\u56fe\u5357\u89c1\u5979 \u624b\u6cd5\u5e76\u65e0\u7279\u5f02\u4e4b\u5904\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u539f\u6765\u5979\u63a5\u6697\u5668\u7684\u529f\u592b\u867d\u7136\u4e86\u5f97\uff0c\u53d1\u6697\u5668\u7684\u529f\u592b\u5374\u662f\u5bfb \u5e38\u3002\u201d\u6709\u5fc3\u70ab\u8000\u672c\u4e8b\uff0c\u968f\u624b\u53d1\u51fa\u4e09\u679a\u94dc\u94b1\uff0c\u628a\u4e09\u67c4\u98de\u5200\u9f50\u90fd\u6253\u843d\u3002\u94dc\u94b1\u7684\u5206\u91cf\u6bd4\u98de\u5200\u8f7b\u5f97 \u591a\uff0c\u4ed6\u7528\u94dc\u94b1\u6253\u843d\u98de\u5200\uff0c\u786e\u662f\u96be\u5f97\u7684\u771f\u5b9e\u672c\u9886\uff0c\u6df3\u4e8e\u5468\u8fd9\u8fb9\u7684\u4eba\u7eb7\u7eb7\u7ed9\u4ed6\u559d\u5f69\u52a9\u5a01\u3002 \u3000\u3000\u67f3\u4e09\u5a18\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u535c\u5be8\u4e3b\u529f\u529b\u6df1\u539a\uff0c\u4f69\u670d\uff0c\u4f69\u670d\uff01\u4f46\u793c\u5c1a\u5f80\u6765\uff0c\u6211\u4e5f\u8fd8\u6709\u4e00\u70b9 \u5fae\u672b\u4e4b\u6280\uff0c\u8981\u8bf7\u5c39\u5be8\u4e3b\u518d\u52a0\u6307\u6559\uff01\u201d\u535c\u56fe\u5357\u5148\u540e\u53d1\u4e86\u4e09\u679a\u94b1\u9556\uff0c\u4e5d\u67c4\u98de\u9525\uff0c\u6b64\u65f6\u67f3\u4e09\u5a18\u624d\u4e0d \u8fc7\u53d1\u4e86\u4e09\u67c4\u98de\u5200\uff0c\u82e5\u662f\u8981\u8bb2\u201c\u793c\u5c1a\u5f80\u6765\u201d\u7684\u8bdd\uff0c\u67f3\u4e09\u5a18\u5f53\u7136\u8fd8\u6709\u7eed\u53d1\u6697\u5668\u7684\u6743\u5229\uff0c\u9664\u975e\u535c\u56fe \u5357\u4e0d\u8981\u9762\u5b50\uff0c\u4e0d\u987e\u8eab\u4efd\uff0c\u5426\u5219\u5c31\u4e0d\u80fd\u62a2\u53d1\u6697\u5668\u3002 \u3000\u3000\u535c\u56fe\u5357\u6253\u843d\u4e86\u5979\u7684\u4e09\u67c4\u98de\u5200\uff0c\u80c6\u6c14\u9661\u58ee\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u54b1\u4eec\u662f\u516c\u5e73\u8f83\u91cf\uff0c\u6211\u5c82\u80fd\u5360\u4f60\u7684 \u4fbf\u5b9c\uff1f\u4f60\u8fd8\u6709\u591a\u5c11\u6697\u5668\uff0c\u5c3d\u7ba1\u53d1\u6765\u5427\uff01\u201d \u3000\u3000\u67f3\u4e09\u5a18\u9053\uff1a\u201c\u597d\uff0c\u6211\u4e5f\u4e0d\u8981\u5360\u4f60\u7684\u4fbf\u5b9c\uff0c\u5982\u6570\u5949\u8fd8\uff0c\u8bf7\u4f60\u6536\u4e0b\uff01\u201d\u53cc\u624b\u9f50\u626c\uff0c\u8f6c\u773c\u4e4b \u95f4\uff0c\u53ea\u89c1\u6ee1\u5929\u90fd\u662f\u94f6\u5149\uff0c\u4e0d\u591a\u4e0d\u5c11\u6b63\u662f\u516d\u67c4\u98de\u5200\u3002\u867d\u7136\u53ea\u662f\u516d\u67c4\u98de\u5200\uff0c\u4f46\u4ea4\u53c9\u98de\u821e\uff0c\u5374\u4f3c\u7ec7 \u6210\u4e86\u4e00\u9762\u5149\u7f51\uff0c\u4ee4\u4eba\u770b\u5f97\u773c\u82b1\u64a9\u4e71\uff01 \u3000\u3000\u65c1\u4eba\u770b\u5f97\u773c\u82b1\u64a9\u4e71\uff0c\u535c\u56fe\u5357\u662f\u4e2a\u6697\u5668\u7684\u5927\u884c\u5bb6\uff0c\u4e00\u773c\u770b\u53bb\uff0c\u5374\u662f\u770b\u51fa\u4e86\u5176\u4e2d\u5927\u6709\u5965\u5999\u3002 \u8fd9\u516d\u67c4\u98de\u5200\u6ca1\u6709\u4e00\u70b3\u662f\u5411\u4ed6\u7b14\u76f4\u98de\u6765\u7684\uff0c\u6765\u52bf\u5e76\u4e0d\u8fc5\u731b\uff0c\u516d\u67c4\u98de\u5200\u5728\u7a7a\u4e2d\u76d8\u65cb\u98de\u821e\uff0c\u5012\u597d\u50cf \u7528\u610f\u4e0d\u5728\u4e4e\u4f24\u4eba\uff0c\u800c\u662f\u5728\u201c\u53d8\u620f\u6cd5\u201d\u4f3c\u7684\uff01 \u3000\u3000\u535c\u56fe\u5357\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\u6253\u6cd5\uff1f\u201d\u9976\u4ed6\u89c1\u591a\u8bc6\uff0c\u8fd9\u6837\u53e4\u602a\u7684\u6697\u5668\u624b\u6cd5\u4ed6 \u4e5f\u89c1\u4ece\u672a\u89c1\u8fc7\u3002 \u3000\u3000\u535c\u56fe\u5357\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u5fc3\u9053\uff1a\u201c\u7ba1\u5979\u662f\u4ec0\u4e48\u6253\u6cd5\uff0c\u6211\u628a\u5979\u7684\u98de\u5200\u90fd\u6253\u843d\u4e0b\u6765\uff0c\u5979\u4e0d\u8ba4\u8f93 \u4e5f\u662f\u4e0d\u884c\uff01\u201d\u5f53\u4e0b\u638f\u51fa\u4e86\u4e00\u628a\u94dc\u94b1\uff0c\u5411\u7a7a\u4e2d\u4e00\u6d12\u3002\u4ed6\u751f\u6015\u4e00\u679a\u94dc\u94b1\u6253\u4e0d\u843d\u4e00\u67c4\u98de\u5200\uff0c\u8fd9\u4e00\u5927 \u628a\u94dc\u94b1\u8db3\u6709\u5341\u51e0\u679a\u4e4b\u591a\uff01 \u3000\u3000\u4e0d\u6599\u94dc\u94b1\u5c1a\u672a\u78b0\u7740\u98de\u5200\uff0c\u90a3\u6ee1\u7a7a\u7684\u98de\u5200\u5148\u81ea\u78b0\u649e\u8d77\u6765\uff0c\u6709\u7684\u659c\u98de\uff0c\u6709\u7684\u76f4\u5c04\uff0c\u6709\u7684\u5212\u7740 \u5706\u5708\u524d\u8fdb\uff0c\u6709\u7684\u4f5c\u201c\u5f27\u5f62\u98de\u884c\u201d\uff0c\u4e00\u77ac\u4e4b\u95f4\uff0c\u98de\u884c\u7684\u65b9\u4f4d\u5168\u90fd\u6539\u53d8\uff01\u535c\u56fe\u5357\u7684\u4e00\u5927\u628a\u94dc\u94b1\u6d12 \u53bb\uff0c\u53ea\u6709\u51e0\u679a\u78b0\u7740\u98de\u5200\uff0c\u5176\u4f59\u7684\u5168\u90fd\u843d\u7a7a\u3002\u7ed9\u94dc\u94b1\u78b0\u7740\u7684\u98de\u5200\u4e5f\u6ca1\u6709\u6253\u843d\uff0c\u800c\u662f\u8f6c\u4e86\u4e2a\u5f2f\uff0c \u53c8\u5411\u7740\u535c\u56fe\u5357\u98de\u6765\uff01 \u3000\u3000\u539f\u6765\u67f3\u4e09\u5a18\u6700\u5148\u53d1\u7684\u90a3\u4e09\u67c4\u98de\u5200\u4e0d\u8fc7\u662f\u8bd5\u63a2\u6027\u8d28\uff0c\u8fd9\u6b21\u53d1\u7684\u516d\u67c4\u98de\u5200\u624d\u662f\u5979\u7684\u5e73\u751f\u7edd \u6280\uff01\u5979\u6df1\u77e5\u535c\u56fe\u5357\u529f\u529b\u6bd4\u5979\u6df1\u539a\uff0c\u6545\u6b64\u53d1\u51fa\u7684\u98de\u5200\u4e0d\u662f\u5411\u4ed6\u7b14\u76f4\u98de\u53bb\uff0c\u8fd9\u6837\u5c31\u53ef\u4ee5\u907f\u514d\u4e0e\u4ed6 \u94b1\u9556\u6253\u6765\u7684\u529b\u9053\u76f4\u63a5\u78b0\u51fb\u3002\u81f3\u4e8e\u5979\u81ea\u5df1\u6240\u53d1\u7684\u516d\u67c4\u98de\u5200\u4e92\u76f8\u78b0\u51fb\uff0c\u90a3\u5374\u662f\u4e00\u79cd\u6781\u5de7\u5999\u7684\u624b \u6cd5\u3002\u78b0\u51fb\u4e4b\u540e\uff0c\u98de\u884c\u7684\u8f68\u9053\u6539\u53d8\uff0c\u4ecd\u7136\u53ef\u4ee5\u98de\u53bb\u4f24\u5bb3\u654c\u4eba\u3002\u800c\u4e14\u5728\u98de\u5200\u5148\u81ea\u78b0\u51fb\u4e4b\u540e\uff0c\u5c31\u4f1a \u4ea7\u751f\u4e00\u80a1\u65b0\u7684\u529b\u9053\uff0c\u518d\u7ed9\u5bf9\u65b9\u7684\u94dc\u94b1\u78b0\u7740\uff0c\u4e5f\u5c31\u4e0d\u4f1a\u6253\u843d\u4e86\u3002 \u3000\u3000\u535c\u56fe\u5357\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u98de\u5200\u4e1a\u5df2\u6765\u5230\uff0c\u8ddd\u79bb\u592a\u8fd1\uff0c\u60f3\u518d\u7528\u6697\u5668\u62b5\u5fa1\u98de\u5200\uff0c\u4e5f\u662f\u4e0d\u53ef\u80fd \u7684\u4e86\uff01\u5371\u6025\u4e4b\u4e2d\uff0c\u535c\u56fe\u5357\u53ea\u597d\u7528\u5224\u5b98\u7b14\u62a4\u7740\u9876\u5fc3\uff0c\u7acb\u5373\u5c06\u8eab\u5b50\u5012\u4e0b\uff0c\u5728\u5730\u4e0a\u6253\u4e86\u51e0\u4e2a\u5927\u7ffb\u3002 \u3000\u3000\u53ea\u542c\u5f97\u201c\u53ee\u94db\u201d\u58f0\u54cd\uff0c\u6709\u4e24\u67c4\u98de\u5200\u7ed9\u4ed6\u7684\u5224\u5b98\u7b14\u6311\u5f00\uff1b\u6709\u4e24\u67c4\u98de\u5200\u51e0\u4e4e\u662f\u8d34\u7740\u4ed6\u7684\u53cc\u80a9 \u524a\u8fc7\uff1b\u8fd8\u6709\u4e24\u67c4\u98de\u5200\u843d\u5728\u4ed6\u7684\u540e\u9762\uff0c\u53ea\u5dee\u5c11\u8bb8\u5c31\u8981\u9489\u7740\u4ed6\u7684\u53cc\u8db3\u3002 \u3000\u3000\u516d\u67c4\u98de\u5200\u867d\u7136\u90fd\u6ca1\u6709\u4f24\u7740\u535c\u56fe\u5357\uff0c\u4f46\u4ed6\u4e5f\u662f\u591f\u72fc\u72c8\u7684\u4e86\uff0c\u4ed6\u5728\u5730\u4e0a\u6253\u4e86\u51e0\u4e2a\u5927\u7ffb\uff0c\u8df3\u8d77 \u6765\u65f6\uff0c\u8863\u88f3\u6ee1\u662f\u6c61\u6ce5\uff0c\u624b\u811a\u5934\u9762\u4e5f\u90fd\u6cbe\u4e86\u4e0d\u5c11\u6c61\u6ce5\u6d4a\u6c34\uff01 \u3000\u3000\u535c\u56fe\u5357\u60f3\u8d77\u521a\u624d\u7684\u60ca\u9669\uff0c\u4e0d\u7531\u5f97\u5413\u51fa\u4e86\u4e00\u8eab\u51b7\u6c57\u3002\u4f46\u5728\u60ca\u9b42\u7a0d\u5b9a\u4e4b\u540e\uff0c\u5fc3\u91cc\u53c8\u5728\u6697\u6697\u5f97 \u610f\uff1a\u201c\u6bd5\u7adf\u5979\u4e5f\u672a\u80fd\u4f24\u6211\uff0c\u8fd9\u4e00\u573a\u6700\u591a\u662f\u7b97\u6253\u4e2a\u5e73\u624b\uff0c\u603b\u4e0d\u80fd\u8bf4\u6211\u8f93\u4e86\uff01\u201d \u3000\u3000\u4ed6\u6b63\u60f3\u53d1\u8bdd\uff0c\u5ffd\u89c9\u5934\u9876\u597d\u50cf\u7ed9\u8682\u8681\u53ee\u4e86\u4e00\u53e3\u4f3c\u7684\uff0c\u6709\u70b9\u9ebb\u75db\u3002\u5c31\u5728\u6b64\u65f6\uff0c\u53ea\u89c1\u5b8b\u94c1\u8f6e\u7ad9 \u4e86\u8d77\u6765\uff0c\u8131\u4e0b\u5e3d\u5b50\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u70ed\uff0c\u597d\u70ed\uff0c\u4f60\u4eec\u6253\u5f97\u8fd9\u6837\u6fc0\u70c8\uff0c\u6211\u5728\u65c1\u8fb9\u89c2\u6218\u7684\u4e5f\u90fd\u6d41\u6c57 \u4e86\uff0c\u4f60\u4eec\u4e0d\u89c9\u5f97\u70ed\u5417\uff1f\u201d\u5b8b\u94c1\u8f6e\u521a\u624d\u6240\u53d7\u7684\u4f24\u3002\u4f24\u662f\u4e0d\u8f7b\uff0c\u4f46\u5728\u670d\u4e86\u201c\u5c0f\u8fd8\u4e39\u201d\u4e4b\u540e\uff0c\u4f11\u606f \u4e86\u4e00\u6bb5\u65f6\u95f4\uff0c\u8bf4\u8bdd\u7684\u58f0\u97f3\u5df2\u662f\u5b8f\u4eae\u5982\u5e38\u3002 \u3000\u3000\u67f3\u4e09\u5a18\u6c14\u5b9a\u795e\u95f2\u5730\u7ad9\u5728\u535c\u56fe\u5357\u5bf9\u9762\uff0c\u7b11\u9053\uff1a\u201c\u535c\u5be8\u4e3b\uff0c\u4f60\u6b47\u4e00\u4f1a\u513f\u518d\u6253\u597d\u4e0d\u597d\uff1f\u8bf4\u8001\u5b9e \u8bdd\uff0c\u6211\u4e5f\u662f\u70ed\u5f97\u96be\u53d7\uff0c\u5f88\u60f3\u5230\u4e00\u8fb9\u51c9\u5feb\u51c9\u5feb\u53bb\u4e86\uff01\u201d \u3000\u3000\u4ed6\u4e24\u592b\u59bb\u4e00\u5531\u4e00\u548c\uff0c\u65c1\u4eba\u542c\u6765\uff0c\u53ea\u9053\u5b8b\u94c1\u8f6e\u662f\u601c\u60dc\u4ed6\u7684\u59bb\u5b50\uff0c\u6539\u800c\u63d0\u8bae\u4f11\u606f\u3002\u6709\u7684\u4eba\u8fd8 \u5728\u66ff\u67f3\u4e09\u5a18\u53ef\u60dc\uff0c\u60f3\u9053\uff1a\u201c\u4f55\u5fc5\u5fd9\u5728\u8fd9\u4e00\u65f6\uff0c\u4e00\u9f13\u4f5c\u6c14\uff0c\u5c06\u4ed6\u6253\u8d25\u4e86\u4e0d\u662f\u66f4\u597d\uff1f\u201d\u8fd9\u4e9b\u4eba\u90fd \u5df2\u770b\u5f97\u51fa\u6765\uff0c\u535c\u56fe\u5357\u867d\u7136\u672c\u8eab\u7684\u529f\u529b\u8f83\u9ad8\uff0c\u4f46\u6b64\u65f6\u5df2\u662f\u7d2f\u5f97\u4e0d\u582a\uff0c\u4e14\u53c8\u6c14\u9981\u7684\u4e86\uff0c\u800c\u67f3\u4e09\u5a18 \u5374\u5c1a\u6709\u4f59\u529b\u3002 \u3000\u3000\u535c\u56fe\u5357\u770b\u89c1\u5b8b\u94c1\u8f6e\u8131\u4e0b\u5e3d\u5b50\u6247\u51c9\uff0c\u76ee\u5149\u76ef\u7740\u81ea\u5df1\u7684\u5934\u9876\u9690\u9690\u4f3c\u542b\u7b11\u610f\uff0c\u5fc3\u4e5f\u9661\u5730\u4e00\u60ca\uff1a \u201c\u83ab\u975e\u6211\u5df2\u7740\u4e86\u6697\u7b97\uff01\u201d\u5927\u60ca\u4e4b\u4e0b\uff0c\u8fde\u5fd9\u8131\u4e0b\u81ea\u5df1\u7684\u5e3d\u5b50\uff0c\u53ea\u89c1\u5e3d\u5b50\u6b63\u4e2d\u9f50\u9f50\u6574\u6574\u7684\u63d2\u7740\u4e09 \u679d\u6885\u82b1\u9488\uff0c\u53ea\u9732\u51fa\u4e00\u70b9\u9488\u5934\uff0c\u9664\u4e86\u4ed6\u672c\u4eba\u4e4b\u5916\uff0c\u65c1\u4eba\u90fd\u770b\u4e0d\u89c1\u3002 \u3000\u3000\u535c\u56fe\u5357\u7ffb\u8fc7\u5e3d\u5b50\u4e00\u770b\uff0c\u89c1\u90a3\u9488\u5c16\u4eae\u6676\u6676\u7684\uff0c\u77e5\u9053\u4e0d\u662f\u6bd2\u9488\uff0c\u8fd9\u624d\u677e\u4e86\u53e3\u6c14\u3002\u4ed6\u662f\u4e2a\u4f7f\u6697 \u5668\u7684\u5927\u884c\u5bb6\uff0c\u5f53\u4e0b\u4e5f\u5c31\u604d\u7136\u5927\u609f\uff0c\u539f\u6765\u67f3\u4e09\u5a18\u628a\u4e09\u53e3\u6885\u82b1\u9488\u6df7\u5728\u98de\u5200\u4e4b\u4e2d\u6253\u51fa\uff0c\u6885\u82b1\u9488\u65e0\u58f0 \u65e0\u606f\uff0c\u535c\u56fe\u5357\u987e\u5f97\u62b5\u5fa1\u98de\u5200\uff0c\u8fd9\u5c31\u7740\u4e86\u5bf9\u65b9\u7684\u9053\u513f\uff01 \u3000\u3000\u67f3\u4e09\u5a18\u7684\u624b\u6bb5\u867d\u7136\u662f\u6709\u70b9\u53d6\u5de7\uff0c\u4f46\u4ee5\u5206\u91cf\u6781\u8f7b\u7684\u6885\u82b1\u9488\u5c45\u7136\u80fd\u591f\u548c\u98de\u5200\u540c\u65f6\u6253\u5230\uff0c\u8fd9\u5374 \u662f\u6700\u4e0a\u4e58\u7684\u6697\u5668\u624b\u6cd5\uff01\u535c\u56fe\u5357\u8981\u5bf9\u65b9\u63d0\u51fa\u65b9\u59cb\u53d1\u89c9\uff0c\u867d\u672a\u53d7\u4f24\uff0c\u4ea6\u5df2\u662f\u8f93\u4e86\u62db\uff0c\u4f55\u51b5\u67f3\u4e09\u5a18 \u5e76\u975e\u4e0d\u80fd\u4f24\u4ed6\uff0c\u53ea\u662f\u987e\u5168\u4ed6\u7684\u989c\u9762\uff0c\u624b\u4e0b\u7559\u60c5\u800c\u5df2\uff01\u535c\u56fe\u5357\u81ea\u5fd6\uff1a\u201c\u8fd9\u4e09\u53e3\u6885\u82b1\u9488\u6b63\u662f\u5f53\u7740 \u6211\u8111\u95e8\u7684\u767e\u4f1a\u7a74\u65b9\u4f4d\uff0c\u82e5\u5979\u7a0d\u7a0d\u7528\u591a\u4e00\u70b9\u52b2\u9053\uff0c\u53c8\u6216\u8005\u662f\u6539\u7531\u6bd2\u9488\u7684\u8bdd\uff0c\u6b64\u9645\u6211\u7109\u80fd\u8fd8\u6709\u547d \u5728\uff1f\u201d \u3000\u3000\u535c\u56fe\u5357\u53c8\u662f\u611f\u6fc0\u53c8\u662f\u60ed\u6127\uff0c\u5f53\u4e0b\u53cc\u62f3\u4e00\u62f1\uff0c\u8bf4\u9053\uff1a\u201c\u67f3\u9999\u4e3b\u7684\u6697\u5668\u529f\u592b\u8fdc\u8fdc\u5728\u6211\u4e4b\u4e0a\uff0c \u535c\u67d0\u7518\u62dc\u4e0b\u98ce\u3002\u201d\u6b64\u8a00\u4e00\u51fa\uff0c\u6df3\u4e8e\u5468\u8fd9\u8fb9\u7684\u4eba\u90fd\u662f\u5927\u611f\u8be7\u5f02\uff0c\u7eb7\u7eb7\u56b7\u9053\uff1a\u201c\u4f60\u8fd8\u6ca1\u6709\u8f93 \u554a\uff01\u201d\u201c\u8fd9\u4e00\u573a\u81f3\u5c11\u4e5f\u8be5\u7b97\u662f\u6253\u4e2a\u5e73\u624b\uff01\u201d \u3000\u3000\u6df3\u4e8e\u5468\u9ed1\u4e86\u9762\u5b54\uff01\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u53eb\u56b7\u4ec0\u4e48\uff1f\u80dc\u8d1f\u5175\u5bb6\u5e38\u4e8b\uff0c\u53c8\u5c1a\u672a\u5b8c\u573a\uff0c\u4f55\u5fc5\u65a4\u65a4\u8ba1 \u8f83\uff1f\u5d14\u5144\uff0c\u4ee4\u5e08\u5f1f\u7684\u65ad\u9aa8\u9a73\u597d\u4e86\u5427\uff1f\u201d \u3000\u3000\u539f\u6765\u535c\u56fe\u5357\u7740\u4e86\u67f3\u4e09\u5a18\u7684\u4e09\u53e3\u6885\u82b1\u9488\uff0c\u5728\u573a\u8bf8\u4eba\u53ea\u6709\u6df3\u4e8e\u5468\u548c\u674e\u601d\u5357\u770b\u5f97\u51fa\u6765\uff0c\u5176\u4ed6\u7684 \u4eba\u90fd\u8fd8\u662f\u83ab\u540d\u5947\u5999\u3002\u6df3\u4e8e\u5468\u751f\u6015\u67f3\u4e09\u5a18\u6296\u9732\u51fa\u6765\uff0c\u66f4\u635f\u81ea\u5df1\u8fd9\u8fb9\u7684\u9762\u5b50\uff0c\u662f\u4ee5\u5b81\u53ef\u8ba4\u8f93\uff0c\u8fd8 \u53ef\u7a0d\u7a0d\u906e\u7792\u3002 \u3000\u3000\u6df3\u4e8e\u5468\u6700\u540e\u7684\u4e00\u53e5\u8bdd\u662f\u5411\u5d14\u9547\u5c71\u8bf4\u7684\uff0c\u5d14\u9547\u5c71\u5f53\u7136\u61c2\u5f97\u4ed6\u7684\u610f\u601d\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u597d\uff0c\u6211 \u7ed9\u4f60\u6253\u56de\u4e24\u573a\u5c31\u662f\u3002\u201d\u4e8e\u662f\u7f13\u7f13\u8d70\u4e0b\u573a\u5fc3\uff0c\u8bf4\u9053\uff1a\u201c\u4eca\u65e5\u96be\u5f97\u6709\u8fd9\u673a\u4f1a\uff0c\u5d14\u67d0\u751a\u613f\u4ee5\u6b66\u4f1a \u53cb\uff0c\u8bf7\u5404\u4f4d\u82f1\u96c4\u6307\u6559\u3002\u201d\u8a00\u4e0b\u4e4b\u610f\uff0c\u5df2\u662f\u8868\u660e\u4e86\u4ed6\u4e0d\u613f\u6d89\u8db3\u4e24\u65b9\u7684\u76df\u4e3b\u4e4b\u4e89\uff0c\u53ea\u662f\u610f\u6b32\u5207\u78cb \u6b66\u529f\uff0c\u70b9\u5230\u5373\u6b62\u800c\u5df2\u3002\u800c\u4e14\u4ed6\u6240\u8981\u201c\u8bf7\u6559\u201d\u7684\u662f\u201c\u5404\u4f4d\u82f1\u96c4\u201d\uff0c\u4e5f\u5e76\u6ca1\u6709\u6307\u660e\u53ea\u662f\u8981\u548c\u5c60\u51e4 \u8fd9\u8fb9\u4f5c\u5bf9\u3002 \u3000\u3000\u4e0d\u8fc7\u4ed6\u65e2\u7136\u662f\u6df3\u4e8e\u5468\u9080\u6765\u7684\u9ad8\u624b\uff0c\u53c8\u662f\u5728\u6df3\u4e8e\u5468\u50ac\u4fc3\u4e4b\u4e0b\u51fa\u573a\u7684\uff0c\u56e0\u6b64\u4ed6\u7684\u8bdd\u867d\u7136\u8bf4\u5f97 \u4e0d\u662f\u5341\u5206\u660e\u767d\uff0c\u4e5f\u7b97\u662f\u66ff\u6df3\u4e8e\u5468\u51fa\u573a\u7684\u4e86\u3002\u5c60\u51e4\u8fd9\u8fb9\u7684\u4eba\u77e5\u9053\u4ed6\u7684\u4e3a\u96be\u4e4b\u5904\uff0c\u4e5f\u542c\u51fa\u4e86\u4ed6\u5e76 \u65e0\u654c\u610f\uff0c\u4f46\u5374\u4e5f\u4e0d\u80fd\u4e0d\u627e\u4eba\u5e94\u4ed8\u4ed6\u3002 \u3000\u3000\u5d14\u9547\u5c71\u7684\u5927\u529b\u91d1\u521a\u638c\u4e45\u5df2\u871a\u58f0\u6b66\u6797\uff0c\u7fa4\u96c4\u90fd\u77e5\u9053\u4ed6\u7684\u672c\u9886\u8fdc\u8fdc\u5728\u4ed6\u5e08\u5f1f\u4e4b\u4e0a\uff0c\u8981\u627e\u4e00\u4e2a \u4eba\u80fd\u548c\u4ed6\u5339\u654c\u7684\u8fd8\u5f53\u771f\u4e0d\u6613\uff0c\u5c60\u51e4\u5fc3\u60f3\uff1a\u201c\u674e\u601d\u5357\u82e5\u7136\u4e0b\u573a\uff0c\u53ef\u4ee5\u80dc\u4ed6\u3002\u4f46\u674e\u601d\u5357\u8981\u7559\u7740\u6c14 \u529b\u5bf9\u4ed8\u6df3\u4e8e\u5468\uff0c\u800c\u4e14\u5d14\u9547\u5c71\u662f\u4ee5\u638c\u529b\u79f0\u96c4\u7684\uff0c\u674e\u601d\u5357\u82e5\u662f\u7528\u5251\u80dc\u4ed6\u53cc\u638c\uff0c\u4ea6\u662f\u80dc\u4e4b\u4e0d\u6b66\u3002\u201d \u3000\u3000\u5c60\u51e4\u6b63\u81ea\u8003\u8651\u4eba\u9009\uff0c\u53ea\u89c1\u8463\u5f00\u5c71\u5df2\u7ecf\u8d70\u4e86\u51fa\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u8001\u673d\u672c\u6765\u4e0d\u6562\u4e0e\u5d14\u5144\u4e89\u80dc\uff0c\u4f46 \u597d\u5728\u5f7c\u6b64\u90fd\u662f\u4ee5\u6b66\u4f1a\u53cb\uff0c\u8c01\u80dc\u8c01\u8d25\uff0c\u4ed8\u4e4b\u4e00\u7b11\uff0c\u4ea6\u662f\u65e0\u59a8\u3002\u201d \u3000\u3000\u8463\u5f00\u5c71\u4ee5\u201c\u5927\u6454\u7891\u624b\u201d\u9a70\u8a89\u6c5f\u6e56\uff0c\u5c11\u5e74\u65f6\u5019\uff0c\u66fe\u6709\u4e00\u53cc\u94c1\u638c\u6253\u8d25\u5188\u6714\u4e03\u96c4\u7684\u6218\u7ee9\u3002\u4e0d \u8fc7\uff0c\u6b63\u5982\u4ed6\u81ea\u5df1\u6240\u8bf4\uff0c\u4ed6\u5982\u4eca\u5df1\u662f\u5e74\u8fc8\u4f53\u8870\u4e86\u3002\u662f\u4ee5\u521a\u624d\u7fa4\u96c4\u8003\u8651\u4eba\u9009\uff0c\u8c01\u90fd\u6ca1\u6709\u60f3\u5230\u8981\u4ed6 \u51fa\u573a\u3002 \u3000\u3000\u5d14\u9547\u5c71\u9053\uff1a\u201c\u5f97\u8463\u8001\u82f1\u96c4\u6307\u6559\uff0c\u4f55\u5e78\u5982\u4e4b\uff01\u4e45\u4ef0\u8001\u82f1\u96c4\u5927\u6454\u7891\u624b\u7684\u82f1\u540d\uff0c\u54b1\u4eec\u5c31\u5728\u638c\u5e95 \u5370\u8bc1\u5370\u8bc1\u5427\uff01\u201d\u7fa4\u96c4\u542c\u5f97\u5d14\u9547\u5c71\u8bf4\u51fa\u201c\u5370\u8bc1\u201d\u4e8c\u5b57\uff0c\u7b49\u4e8e\u518d\u6b21\u8868\u660e\u53ea\u662f\u201c\u5207\u78cb\u201d\u4e4b\u610f\uff0c\u8fd9\u624d \u7a0d\u7a0d\u653e\u5fc3\u3002 \u3000\u3000\u5d14\u9547\u5c71\u5206\u5c5e\u665a\u8f88\uff0c\u5148\u884c\u51fa\u62db\u8868\u793a\u656c\u610f\uff0c\u53ea\u89c1\u4ed6\u5355\u638c\u5212\u4e86\u4e00\u9053\u5706\u5f27\u7f13\u7f13\u63a8\u51fa\uff0c\u8463\u5f00\u5c71\u5f13\u8170 \u84c4\u52bf\uff0c\u5f85\u4ed6\u638c\u5230\uff0c\u9661\u5730\u624b\u8155\u4e00\u7ffb\uff0c\u53cd\u624b\u4e00\u638c\u62cd\u51fa\u3002\u53ea\u542c\u5f97\u201c\u84ec\u201d\u7684\u4e00\u58f0\uff0c\u5d14\u9547\u5c71\u5012\u9000\u4e09\u6b65\u3002 \u3000\u3000\u638c\u98ce\u6240\u53ca\uff0c\u6c99\u98de\u77f3\u8d70\uff0c\u7ad9\u5f97\u8f83\u8fd1\u7684\u65c1\u89c2\u8bf8\u4eba\u7eb7\u7eb7\u540e\u9000\u3002 \u3000\u3000\u5c60\u51e4\u8fd9\u8fb9\u7684\u4eba\u5927\u4e3a\u6b22\u559c\uff0c\u5fc3\u60f3\uff1a\u201c\u8463\u5f00\u5c71\u5b9d\u5200\u672a\u8001\uff0c\u53ea\u6015\u6253\u5f97\u8d62\u5d14\u9547\u5c71\u4e5f\u8bf4\u4e0d\u5b9a\u3002\u201d\u56e0 \u4e3a\u5d14\u9547\u5c71\u7ed9\u4ed6\u4e00\u638c\u9707\u9000\u4e09\u6b65\uff0c\u5d14\u9547\u5c71\u662f\u5426\u624b\u4e0b\u7559\u60c5\uff0c\u5927\u5bb6\u90fd\u4e0d\u77e5\u9053\u3002\u4f46\u53cc\u65b9\u638c\u98ce\u7684\u5f3a\u52b2\u5374\u662f \u6709\u76ee\u5171\u7779\u7684\u3002 \u3000\u3000\u8463\u5f00\u5c71\u81ea\u5df1\u5fc3\u91cc\u660e\u767d\uff0c\u5d14\u9547\u5c71\u4e0d\u4f46\u662f\u624b\u4e0b\u7559\u60c5\uff0c\u800c\u4e14\u6839\u672c\u662f\u84c4\u529b\u672a\u53d1\u3002\u539f\u6765\u5d14\u9547\u5c71\u90a3\u4e00 \u638c\u6709\u4e2a\u540d\u5802\uff0c\u53eb\u505a\u201c\u9f99\u95e8\u4e09\u6fc0\u6d6a\u201d\uff0c\u82e5\u662f\u638c\u529b\u5c3d\u53d1\u7684\u8bdd\uff0c\u5e94\u8be5\u6709\u4e09\u91cd\u529b\u9053\uff0c\u4e00\u91cd\u731b\u8fc7\u4e00\u91cd\uff0c \u53ef\u662f\u5d14\u9547\u5c71\u53ea\u53d1\u51fa\u7b2c\u4e00\u91cd\u529b\u9053\uff0c\u4fbf\u5373\u6536\u638c\u9000\u6b65\u4e86\u3002 \u3000\u3000\u8fd9\u4e00\u638c\u5d14\u9547\u5c71\u7ed9\u4ed6\u9707\u9000\u4e09\u6b65\uff0c\u4e5f\u662f\u59cb\u6599\u4e4b\u4e0d\u53ca\u3002\u4ed6\u4ee5\u4e3a\u8463\u5f00\u5c71\u5e74\u8fc8\uff0c\u751f\u6015\u7528\u529b\u592a\u731b\u4f24\u4e86 \u8463\u5f00\u5c71\uff0c\u8fd9\u53ef\u4e0d\u597d\u610f\u601d\uff0c\u8bd5\u4e86\u4e00\u62db\u4e4b\u540e\uff0c\u59cb\u77e5\u8463\u5f00\u5c71\u7684\u638c\u529b\u867d\u7136\u6bd4\u4e0d\u4e0a\u4ed6\uff0c\u5374\u662f\u5728\u4ed6\u539f\u6765\u7684 \u4f30\u8ba1\u4e4b\u4e0a\u3002 \u3000\u3000\u6b66\u6797\u4e2d\u4eba\uff0c\u5bf9\u4e00\u4e2a\u201c\u540d\u201d\u5b57\u90fd\u662f\u9887\u4e3a\u7740\u91cd\u7684\u3002\u5d14\u9547\u5c71\u4e0d\u613f\u4f24\u4e86\u524d\u8f88\uff0c\u53ef\u662f\u4e5f\u4e0d\u613f\u8f93\u7ed9\u8463 \u5f00\u5c71\uff0c\u4e8e\u662f\u5728\u8bd5\u4e86\u4e00\u62db\u4e4b\u540e\uff0c\u5c31\u9010\u6e10\u52a0\u5f3a\u638c\u529b\uff0c\u53cc\u65b9\u638c\u98ce\u547c\u547c\uff0c\u6218\u51b5\u4e5f\u5c31\u6e10\u6e10\u7d27\u5f20\u4e86\u3002\u8fc7\u4e86 \u7247\u523b\uff0c\u53ea\u89c1\u8463\u5f00\u5c71\u5934\u4e0a\u5192\u51fa\u70ed\u817e\u817e\u7684\u767d\u6c14\uff0c\u90a3\u662f\u4ed6\u7684\u6c57\u6c34\u6240\u84b8\u53d1\u7684\u3002\u5d14\u9547\u5c71\u7684\u989d\u4e0a\u5374\u8fd8\u672a\u89c1 \u4e00\u9897\u6c57\u73e0\u3002 \u3000\u3000\u5c60\u51e4\u4e0d\u7981\u5927\u5927\u5403\u60ca\uff0c\u5fc3\u60f3\uff1a\u201c\u5d14\u9547\u5c71\u867d\u65e0\u654c\u610f\uff0c\u4f46\u5728\u6fc0\u6218\u4e4b\u4e2d\uff0c\u4e5f\u96be\u4fdd\u4e0d\u4f1a\u5931\u624b\u4f24\u4eba\uff01 \u5509\uff0c\u8463\u8001\u524d\u8f88\u4e5f\u662f\u597d\u80dc\uff0c\u6597\u5230\u8fd9\u4e2a\u65f6\u5019\uff0c\u4e5f\u8be5\u8ba4\u8f93\u624d\u662f\uff0c\u4f55\u5fc5\u518d\u62fc\u4e0b\u53bb\uff1f\u201d \u3000\u3000\u5c60\u51e4\u4e0d\u77e5\uff0c\u5979\u6240\u60f3\u5f97\u5230\u7684\uff0c\u5d14\u3001\u8463\u4e8c\u4eba\u4e5f\u65e9\u5df2\u60f3\u5230\u4e86\u3002\u8463\u5f00\u5c71\u60f3\uff1a\u201c\u5f85\u4ed6\u8fd9\u4e00\u5957\u91d1\u521a\u638c \u62db\u5f0f\u4f7f\u5168\uff0c\u6211\u81ea\u5f53\u7518\u62dc\u4e0b\u98ce\u3002\u201d\u539f\u6765\u8463\u5f00\u5c71\u751f\u6027\u9177\u55dc\u6b66\u5b66\uff0c\u96be\u5f97\u6709\u8fd9\u673a\u4f1a\uff0c\u4e00\u7aa5\u91d1\u521a\u638c\u7684\u7edd \u6280\uff0c\u662f\u4ee5\u5b81\u613f\u82e6\u82e6\u652f\u6491\uff0c\u4e5f\u60f3\u4e00\u7aa5\u5168\u8c79\u3002 \u3000\u3000\u5d14\u9547\u5c71\u5219\u5728\u6697\u81ea\u60f3\u9053\uff1a\u201c\u518d\u6253\u4e0b\u53bb\uff0c\u6211\u867d\u4e0d\u60f3\u4f24\u4ed6\uff0c\u53ea\u6015\u8fd9\u8001\u513f\u8fc7\u540e\u4e5f\u8981\u5927\u75c5\u4e00\u573a\u3002\u201d \u4e8e\u662f\u5356\u4e2a\u7834\u7efd\uff0c\u5f85\u8463\u5f00\u5c71\u53cc\u638c\u4ece\u4e2d\u5bab\u51fb\u8fdb\uff0c\u4ed6\u8f7b\u8f7b\u4f7f\u4e86\u4e00\u62db\u201c\u9e64\u818a\u624b\u201d\uff0c\u4e00\u62e7\u3001\u4e00\u6258\u3001\u4e00 \u62cd\uff0c\u628a\u8463\u5f00\u5c71\u8f7b\u8f7b\u63a8\u51fa\u4e86\u516d\u4e03\u4e08\u3002 \u3000\u3000\u4ed6\u8fd9\u4e00\u62e7\u3001\u4e00\u6258\u3001\u4e00\u62cd\uff0c\u4e09\u4e2a\u52a8\u4f5c\u4e00\u6c14\u5475\u6210\uff0c\u8fc5\u901f\u4e4b\u6781\uff0c\u4f17\u4eba\u8fd8\u672a\u770b\u6e05\u4ed6\u7684\u624b\u6cd5\uff0c\u53ea\u89c1 \u4ed6\u4e5f\u201c\u8e6c\u3001\u8e6c\u3001\u8e6c\u201d\u7684\u63a5\u8fde\u9000\u51fa\u4e86\u516d\u4e03\u6b65\u3002\u8463\u5f00\u5c71\u6253\u4e86\u51e0\u4e2a\u76d8\u65cb\u65b9\u59cb\u7a33\u4f4f\u8eab\u5f62\uff0c\u4ed6\u4e5f\u8ddf\u7740\u6253 \u4e86\u51e0\u4e2a\u76d8\u65cb\uff0c\u548c\u8463\u5f00\u5c71\u5b8c\u5168\u4e00\u6837\uff0c\u4e00\u4e2a\u4e0d\u591a\uff0c\u4e00\u4e2a\u4e0d\u5c11\u3002 \u3000\u3000\u8463\u5f00\u5c71\u7ad9\u7a33\u4e86\u811a\u6b65\uff0c\u5598\u8fc7\u53e3\u6c14\uff0c\u6b63\u60f3\u8bf4\u8bdd\uff0c\u5d14\u9547\u5c71\u5df2\u62a2\u5148\u8bf4\u9053\uff1a\u201c\u8463\u8001\u82f1\u96c4\u638c\u6cd5\u9ad8\u660e\uff0c \u665a\u8f88\u591a\u627f\u76f8\u6bd4\uff0c\u4fa5\u5e78\u6253\u4e86\u4e2a\u5e73\u624b\u3002\u201d\u5d14\u9547\u5c71\u8fd9\u8bdd\u5012\u4e5f\u5e76\u975e\u8a00\u4e0d\u7531\u8877\uff0c\u4ee5\u638c\u6cd5\u800c\u8bba\uff0c\u8463\u5f00\u5c71\u7684 \u201c\u5927\u6454\u7891\u624b\u201d\u786e\u662f\u4e0e\u4ed6\u7684\u201c\u91d1\u521a\u638c\u201d\u5404\u6709\u5e72\u79cb\uff0c\u96be\u5206\u9ad8\u4e0b\u3002\u8463\u5f00\u5c71\u54c8\u54c8\u5927\u7b11\uff0c\u7b11\u8fc7\u4e4b\u540e\uff0c\u8fd9 \u624d\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u8001\u4e86\uff0c\u4e0d\u4e2d\u7528\u4e86\u3002\u4f46\u8fd9\u5206\u660e\u662f\u8001\u5f1f\u8ba9\u6211\uff0c\u6211\u4e5f\u4e0d\u80fd\u539a\u7740\u8138\u5ea6\u5f53\u4f5c\u6253\u548c\u3002\u201d \u3000\u3000\u6df3\u4e8e\u5468\u4e00\u76f4\u9ed1\u7740\u8138\u5b54\uff0c\u6b64\u65f6\u65b9\u59cb\u6709\u4e00\u4e1d\u559c\u8272\uff0c\u7fd8\u8d77\u62c7\u6307\u8d5e\u9053\uff1a\u201c\u4e24\u4f4d\u90fd\u662f\u5149\u660e\u78ca\u843d\u7684\u597d \u6c49\u5b50\uff0c\u4f69\u670d\uff0c\u4f69\u670d\uff01\u201d \u3000\u3000\u8df3\u864e\u6da7\u7684\u9093\u98de\u6027\u60c5\u723d\u76f4\uff0c\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u82e5\u4e0d\u662f\u8463\u8001\u82f1\u96c4\u8ba4\u4e86\u8f93\uff0c\u8c05\u4f60\u4e5f\u4e0d\u4f1a \u8d5e\u4ed6\u3002\u201d\u6df3\u4e8e\u5468\u5e78\u5f97\u5d14\u9547\u5c71\u7ed9\u4ed6\u6273\u56de\u4e00\u573a\uff0c\u6b22\u559c\u4e0d\u6687\uff0c\u5bf9\u8fd9\u4e9b\u51b7\u5632\u70ed\u8bbd\uff0c\u53ea\u88c5\u4f5c\u662f\u542c\u4e0d\u89c1\u3002 \u3000\u3000\u8463\u5f00\u5c71\u9053\uff1a\u201c\u53ef\u60dc\uff0c\u53ef\u60dc\uff01\u201d\u5d14\u9547\u5c71\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u53ef\u60dc\u4ec0\u4e48\uff1f\u201d\u8463\u5f00\u5c71\u9053\uff1a\u201c\u542c\u8bf4 \u4f60\u7684\u5927\u529b\u91d1\u521a\u638c\u5171\u6709\u516b\u516b\u516d\u56db\u5f0f\uff0c\u53ef\u60dc\u6211\u53ea\u89c1\u4e86\u56db\u5341\u516b\u5f0f\uff0c\u672a\u66fe\u5f97\u7aa5\u5168\u8c79\u3002\u201d \u3000\u3000\u5d14\u9547\u5c71\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u591a\u627f\u8463\u8001\u82f1\u96c4\u76f8\u8ba9\uff0c\u5c0f\u5f1f\u5e78\u672a\u843d\u8d25\uff0c\u54ea\u4f4d\u82f1\u96c4\u518d\u6765\u6307\u6559\uff1f\u201d \u6309\u7167\u6bd4\u6b66\u7684\u89c4\u77e9\uff0c\u4ed6\u80dc\u4e86\u4e00\u573a\uff0c\u8fd8\u53ef\u4ee5\u518d\u6253\u4e00\u573a\u3002\u5d14\u9547\u5c71\u8fd9\u51e0\u53e5\u8bdd\u7684\u610f\u601d\uff0c\u4e00\u6765\u662f\u8868\u660e\u4e86\u4ed6 \u613f\u610f\u518d\u6253\u4e0b\u53bb\uff0c\u4e8c\u6765\u4e5f\u7b49\u4e8e\u662f\u95f4\u63a5\u7b54\u5141\u8463\u5f00\u5c71\u7684\u8bf7\u6c42\uff0c\u53ea\u8981\u6709\u4eba\u8ddf\u4ed6\u518d\u6253\uff0c\u4ed6\u5c31\u53ef\u4ee5\u628a\u516b\u516b \u516d\u5341\u56db\u5f0f\u91d1\u521a\u638c\u6f14\u4e2a\u9f50\u5168\u3002 \u3000\u3000\u4f46\u8fd9\u4e00\u6765\u53c8\u7b49\u4e8e\u662f\u7ed9\u5c60\u51e4\u51fa\u4e2a\u96be\u9898\u4e86\u3002\u8463\u5f00\u5c71\u7684\u5927\u6454\u7891\u624b\u90fd\u8d25\u5728\u4ed6\u7684\u91d1\u521a\u638c\u4e0b\uff0c\u8fd8\u6709\u4f55 \u4eba\u53ef\u4ee5\u5e94\u4ed8\u4ed6\u7684\u638c\u529b\uff1f \u3000\u3000\u7fa4\u96c4\u6b63\u5728\u5546\u8bae\uff0c\u5ffd\u89c1\u4e00\u4e2a\u9ec4\u8863\u6c49\u5b50\u4ece\u4eba\u4e1b\u4e2d\u8d70\u51fa\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u53ef\u4e0d\u624d\uff0c\u8bf7\u5d14\u9547\u5c71\u6307\u6559\u4e2a \u4e09\u62db\u4e24\u5f0f\u3002\u201d\u8fd9\u4eba\u5e74\u7eaa\u4e0d\u8fc7\u4e8c\u5341\u591a\u5c81\uff0c\u957f\u5f97\u7709\u6e05\u76ee\u79c0\uff0c\u5012\u50cf\u662f\u4e2a\u6587\u5f31\u7684\u4e66\u751f\uff0c\u54ea\u91cc\u662f\u4e2a\u7eff\u6797 \u597d\u6c49\u7684\u6478\u6837\uff1f \u3000\u3000\u4e24\u8fb9\u7684\u4eba\u90fd\u4e0d\u8ba4\u8bc6\u8fd9\u4e2a\u5c11\u5e74\uff0c\u4e0d\u7981\u5927\u4e3a\u8be7\u5f02\uff0c\u4ff1\u662f\u60f3\u9053\uff1a\u201c\u8fd9\u4eba\u662f\u8c01\uff0c\u6709\u6b64\u80c6\u91cf\uff1f\u201d \u3000\u3000\u5d14\u9547\u5c71\u9053\uff1a\u201c\u4e0d\u6562\u3002\u8bf7\u95ee\u9601\u4e0b\u9ad8\u59d3\u5927\u540d\uff1f\u662f\u54ea\u6761\u7ebf\u4e0a\u7684\u670b\u53cb\uff1f\u201d\u90a3\u9ec4\u8863\u5c11\u5e74\u9053\uff1a\u201c\u5c0f\u53ef \u4e0d\u8fc7\u662f\u7405\u739b\u5c71\u7684\u4e00\u4e2a\u65e0\u540d\u5c0f\u5352\u3002\u201d \u3000\u3000\u5c60\u9f99\u51b7\u7b11\u9053\uff1a\u201c\u65e0\u540d\u5c0f\u5352\u4e5f\u597d\uff0c\u5927\u82f1\u96c4\u5927\u8c6a\u6770\u4e5f\u597d\uff0c\u603b\u5f97\u6709\u4e2a\u540d\u5b57\u5427\uff1f\u201d\u9ec4\u8863\u5c11\u5e74\u8fd9\u624d \u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u5c0f\u59d3\u891a\uff0c\u8d31\u5b57\u4e91\u5cf0\u3002\u4f46\u6c42\u5f97\u4f1a\u9ad8\u4eba\uff0c\u975e\u56fe\u626c\u540d\u7acb\u4e07\u3002\u5192\u6627\u51fa\u573a\uff0c\u6559\u5404\u4f4d\u89c1\u7b11 \u4e86\u3002\u201d \u3000\u3000\u7fa4\u96c4\u90fd\u662f\u4e00\u6014\uff0c\u5fc3\u9053\uff1a\u201c\u891a\u4e91\u5cf0\uff0c\u8fd9\u540d\u5b57\u53ef\u4ece\u6765\u6ca1\u6709\u542c\u8fc7\u3002\u201d\u5c60\u51e4\u542c\u4ed6\u8bf4\u662f\u81ea\u5df1\u5c71\u5be8\u7684 \u4eba\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6216\u8bb8\u662f\u6700\u8fd1\u6295\u6765\u7684\u5427\uff1f\u5f85\u4f1a\u513f\u6211\u95ee\u5b8b\u94c1\u8f6e\uff0c\u60f3\u5fc5\u662f\u7ecf\u4ed6\u7684\u624b\u62db\u6536\u7684\u3002\u201d \u3000\u3000\u539f\u6765\u5c60\u51e4\u4e5f\u4e0d\u77e5\u9053\u6b64\u4eba\uff0c\u53ea\u9053\u4ed6\u662f\u81ea\u5df1\u4e0d\u5728\u5c71\u5be8\u7684\u65f6\u5019\u6765\u6295\u5954\u7684\u3002\u4f46\u56e0\u526f\u5be8\u4e3b\u5b8b\u94c1\u8f6e\u53d7 \u4e86\u4f24\u5df2\u8fdb\u5185\u5802\u4f11\u606f\uff0c\u662f\u4ee5\u65e0\u4ece\u8be2\u95ee\u3002 \u3000\u3000\u5d14\u9547\u5c71\u9053\uff1a\u201c\u5f7c\u6b64\u5370\u8bc1\u6b66\u529f\uff0c\u891a\u5144\u4f55\u7528\u5ba2\u6c14\uff1f\u8bf7\uff01\u201d \u3000\u3000\u891a\u4e91\u5cf0\u53cc\u638c\u4e00\u5408\uff0c\u7f13\u7f13\u5212\u4e86\u4e2a\u5708\uff0c\u4f7f\u51fa\u201c\u7ae5\u5b50\u62dc\u89c2\u97f3\u201d\u7684\u201c\u8bf7\u624b\u5f0f\u201d\uff0c\u5e73\u63a8\u51fa\u53bb\u3002\u638c\u52bf \u7f13\u6162\u65e0\u529b\uff0c\u7fa4\u96c4\u770b\u5f97\u90fd\u4e0d\u7981\u6697\u6697\u76b1\u7709\u3002\u8981\u77e5\u4ed6\u4eec\u867d\u7136\u4e0d\u6562\u671f\u671b\u8fd9\u4e2a\u201c\u65e0\u540d\u5c0f\u5352\u201d\u80fd\u591f\u53d6\u80dc\uff0c \u4f46\u4ed6\u65e2\u7136\u662f\u4ee3\u8868\u7405\u739b\u5c71\u51fa\u573a\uff0c\u81f3\u5c11\u4e5f\u5e94\u8be5\u6253\u5f97\u6709\u4e2a\u201c\u8c31\u513f\u201d\uff0c\u82e5\u7136\u4e09\u62db\u4e24\u5f0f\u5c31\u6298\u5728\u5bf9\u65b9\u624b \u4e0b\uff0c\u5c82\u975e\u5927\u5927\u635f\u4e86\u5df1\u65b9\u7684\u989c\u9762\uff1f \u3000\u3000\u4f17\u4eba\u6b63\u5728\u6697\u7b11\u8fd9\u4e2a\u59d3\u891a\u7684\u5c11\u5e74\u672a\u514d\u592a\u8fc7\u4e0d\u81ea\u91cf\u529b\uff0c\u53ea\u89c1\u5d14\u9547\u5c71\u5df2\u7ecf\u548c\u4ed6\u5bf9\u4e86\u4e00\u638c\uff0c\u53cc\u638c \u76f8\u4ea4\uff0c\u65e0\u58f0\u65e0\u606f\uff0c\u4e0e\u521a\u624d\u90a3\u4e00\u573a\u5d14\u3001\u8463\u4e8c\u4eba\u7684\u6597\u638c\u5927\u4e0d\u76f8\u540c\u3002\u4e00\u62db\u8fc7\u540e\uff0c\u5d14\u9547\u5c71\u7684\u9762\u4e0a\u7adf\u7136 \u9732\u51fa\u5341\u5206\u60ca\u8be7\u7684\u795e\u60c5\uff01 \u3000\u3000\u539f\u6765\u891a\u4e91\u5cf0\u7684\u638c\u52bf\u867d\u4f3c\u7f13\u6162\uff0c\u4f46\u5d14\u9547\u5c71\u90a3\u4e48\u9738\u9053\u7684\u91d1\u521a\u638c\u6253\u8fc7\u53bb\uff0c\u7adf\u7136\u4e0d\u80fd\u6447\u64bc\u4ed6\u7684\u5206 \u6beb\u3002\u53cc\u638c\u4e00\u4ea4\uff0c\u53ea\u89c9\u6709\u4e00\u80a1\u6781\u67d4\u548c\u7684\u529b\u9053\u8fc5\u5373\u5c31\u628a\u4ed6\u7684\u91d1\u521a\u638c\u529b\u5316\u89e3\u4e86\u3002\u8fd9\u60c5\u5f62\u5c31\u4f3c\u628a\u4e00\u5757 \u5927\u77f3\u5934\u6295\u8fdb\u6c34\u4e2d\u4e00\u6837\u3002 \u3000\u3000\u5d14\u9547\u5c71\u5927\u4e3a\u60ca\u8be7\uff0c\u5fc3\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u8fd9\u4e2a\u7709\u6e05\u76ee\u79c0\u7684\u5c11\u5e74\u7adf\u662f\u4e2a\u8eab\u6000\u7edd\u6280\u7684\u9ad8\u624b\uff01\u53ef\u5f97\u6253 \u9192\u7cbe\u795e\uff0c\u83ab\u8981\u6298\u5728\u4ed6\u7684\u624b\u4e0b\u624d\u597d\uff01\u201d\u5f53\u4e0b\u4e00\u4e2a\u201c\u8de8\u864e\u767b\u5c71\u201d\uff0c\u8fdb\u6b65\u6b3a\u8eab\uff0c\u628a\u638c\u529b\u52a0\u5230\u4e86\u516b\u4e5d \u5206\uff0c\u63a5\u8fde\u4f7f\u51fa\u4e86\u4e09\u62db\u6781\u4e3a\u731b\u70c8\u7684\u62db\u5f0f\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u4ecd\u7136\u662f\u8f7b\u98d8\u98d8\u7684\u53d1\u638c\uff0c\u5168\u7528\u67d4\u529b\u5316\u89e3\u5bf9\u65b9\u7684\u731b\u52b2\uff0c\u4f46\u89c1\u4ed6\u8eab\u5f62\u6643\u52a8\uff0c\u8863\u8882\u98d8\u98d8\uff0c \u5b9b\u5982\u6d41\u6c34\u884c\u4e91\uff0c\u968f\u7740\u5bf9\u65b9\u7684\u638c\u52bf\u500f\u8fdb\u500f\u9000\uff0c\u5d14\u9547\u5c71\u90a3\u4e48\u521a\u731b\u7684\u638c\u529b\u7adf\u7136\u65e0\u5948\u4ed6\u4f55\uff01\u7fa4\u96c4\u8fd9\u624d \u5927\u5927\u60ca\u5f02\u3002 \u3000\u3000\u5b5f\u660e\u971e\u81ea\u5e7c\u8ddf\u968f\u7236\u4eb2\uff0c\u89c1\u591a\u8bc6\u4e86\uff0c\u4f4e\u58f0\u5bf9\u5c60\u51e4\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u6700\u4e0a\u4e58\u7684\u5185\u5bb6\u638c\u529b\u3002\u606d\u559c\uff0c \u606d\u559c\uff0c\u5c60\u59d0\u59d0\uff0c\u6211\u7adf\u4e0d\u77e5\u4f60\u7684\u5c71\u5be8\u91cc\u6709\u5982\u6b64\u4e00\u4e2a\u80fd\u4eba\uff01\u201d \u3000\u3000\u5d14\u9547\u5c71\u6210\u540d\u4ee5\u6765\uff0c\u4ece\u672a\u78b0\u8fc7\u5982\u6b64\u9ad8\u624b\uff0c\u4e0d\u7981\u7cbe\u795e\u9661\u632f\uff0c\u8d8a\u6597\u4e16\u8d8a\u89c1\u731b\u70c8\uff0c\u5728\u4ed6\u5168\u529b\u65bd\u4e3a \u4e4b\u4e0b\uff0c\u516b\u516b\u516d\u5341\u56db\u5f0f\u91d1\u521a\u638c\u4f7f\u5f97\u5174\u81f4\u6dcb\u6f13\uff0c\u624b\u811a\u8d77\u5904\uff0c\u5168\u5e26\u52b2\u98ce\u3002\u573a\u8fb9\u7ad9\u5f97\u7a0d\u8fd1\u7684\u4eba\u90fd\u89c9\u5f97 \u6709\u5982\u5229\u5200\u522e\u9762\uff0c\u614c\u4e0d\u8fed\u5730\u540e\u9000\u3002 \u3000\u3000\u8fd9\u4e00\u573a\u6076\u6597\u770b\u5f97\u4eba\u4eba\u60ca\u5fc3\u52a8\u9b44\uff0c\u5c60\u51e4\u867d\u7136\u770b\u51fa\u4e86\u891a\u4e91\u5cf0\u662f\u4e2a\u5185\u5bb6\u9ad8\u624b\uff0c\u5374\u8fd8\u662f\u4e0d\u7981\u6709\u70b9 \u6697\u6697\u62c5\u5fc3\uff0c\u60f3\u9053\uff1a\u201c\u5d14\u9547\u5c71\u7684\u91d1\u521a\u638c\u731b\u6251\uff0c\u5185\u529b\u7adf\u4f3c\u65e0\u7a77\u65e0\u5c3d\u3002\u867d\u8bf4\u67d4\u80fd\u514b\u521a\uff0c\u5374\u4e0d\u77e5\u8fd9\u59d3 \u891a\u7684\u5c11\u5e74\u80fd\u5426\u652f\u6301\u5230\u6700\u540e\u4e00\u523b\uff1f\u201d \u3000\u3000\u6b8a\u4e0d\u77e5\u5728\u8868\u9762\u770b\u6765\u4f3c\u662f\u5d14\u9547\u5c71\u5360\u4e86\u516b\u6210\u653b\u52bf\uff0c\u891a\u4e91\u5cf0\u53ea\u6709\u5316\u89e3\u4ed6\u653b\u52bf\u7684\u4efd\u513f\uff0c\u5076\u7136\u624d\u80fd \u8fd8\u4e00\u4e24\u62db\u3002\u4f46\u5728\u5d14\u9547\u5c71\u672c\u4eba\uff0c\u5374\u662f\u6bcf\u4e00\u62db\u90fd\u611f\u5230\u5bf9\u65b9\u7684\u538b\u529b\uff01 \u3000\u3000\u891a\u4e91\u5cf0\u53d1\u638c\u67d4\u5982\u67f3\u7d6e\uff0c\u4f46\u5185\u4e2d\u6697\u85cf\u6f5c\u529b\uff0c\u5d14\u9547\u5c71\u5c01\u95ed\u5f97\u7a0d\u5fae\u4e0d\u591f\u5468\u5bc6\uff0c\u5bf9\u65b9\u7684\u638c\u529b\u5c31\u53cd \u6251\u8fc7\u6765\uff0c\u72b9\u5982\u60ca\u6d9b\u9aa4\u81f3\uff0c\u9022\u9699\u5373\u5165\uff0c\u5d14\u9547\u5c71\u5168\u795e\u5bf9\u4ed8\uff0c\u4e0d\u8fc7\u7247\u523b\uff0c\u6c57\u5982\u96e8\u4e0b\uff0c\u5fc3\u4e2d\u6697\u6697\u53eb \u82e6\u3002 \u3000\u3000\u4e0d\u77e5\u4e0d\u89c9\u4e4b\u95f4\uff0c\u5d14\u9547\u5c71\u4e00\u5957\u516b\u516b\u516d\u5341\u56db\u5f0f\u7684\u91d1\u521a\u638c\u6cd5\uff0c\u5df2\u7ecf\u53cd\u590d\u4f7f\u4e86\u4e24\u904d\u3002\u6fc0\u6218\u4e2d\uff0c\u891a \u4e91\u5cf0\u5ffd\u5730\u4e00\u98d8\u4e00\u95ea\uff0c\u6251\u8fdb\u4e86\u5d14\u9547\u5c71\u53cc\u638c\u5408\u51fb\u7684\u5708\u5b50\uff0c\u5de6\u638c\u4e00\u7275\uff0c\u53f3\u638c\u4e00\u5e26\uff0c\u5d14\u9547\u5c71\u7acb\u8db3\u4e0d \u7a33\uff0c\u659c\u7a9c\u51fa\u6570\u4e08\u5f00\u5916\uff0c\u63a5\u8fde\u8f6c\u4e86\u51e0\u4e2a\u5708\u5708\u3002\u891a\u53bb\u5cf0\u4e00\u62db\u5f97\u624b\uff0c\u7acb\u5373\u4e5f\u7a9c\u8fc7\u4e00\u8fb9\uff0c\u540c\u6837\u7684\u8f6c\u4e86 \u51e0\u4e2a\u5708\u5708\u3002 \u3000\u3000\u674e\u601d\u5357\u53eb\u9053\uff1a\u201c\u5f53\u771f\u662f\u68cb\u9022\u5bf9\u624b\uff0c\u8fd9\u4e00\u573a\u53c8\u662f\u6253\u4e2a\u5e73\u624b\uff01\u201d\u5176\u5b9e\u674e\u601d\u5357\u4f55\u5c1d\u4e0d\u77e5\uff0c\u891a\u4e91 \u5cf0\u4e43\u662f\u4f9d\u7965\u753b\u846b\u82a6\uff0c\u6709\u610f\u8ba9\u5d14\u9547\u5c71\u7684\u3002\u5d14\u9547\u5c71\u521a\u624d\u8ba9\u8463\u5f00\u5c71\u4e00\u62db\uff0c\u6b64\u523b\u891a\u53bb\u5cf0\u4e5f\u540c\u6837\u7684\u8ba9\u56de \u4e86\u4ed6\u4e00\u62db\u3002 \u3000\u3000\u674e\u601d\u5357\u770b\u51fa\u891a\u4e91\u5cf0\u7684\u7528\u610f\uff0c\u4f46\u60f3\u8fd9\u51e0\u53e5\u8bdd\u82e5\u662f\u7531\u4ed6\u6765\u8bf4\uff0c\u672a\u514d\u592a\u7740\u75d5\u8ff9\u3002\u662f\u4ee5\u674e\u601d\u5357\u66ff \u4ed6\u8bf4\u4e86\u3002 \u3000\u3000\u5d14\u9547\u5c71\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u957f\u6c5f\u540e\u6d6a\u63a8\u524d\u6d6a\uff0c\u4e16\u4e0a\u65b0\u4eba\u6362\u65e7\u4eba\u3002\u8fd9\u8bdd\u5f53\u771f\u8bf4\u5f97\u4e0d\u9519\u3002\u891a \u5144\uff0c\u6211\u5e73\u751f\u81ea\u8d1f\u91d1\u521a\u638c\u529b\u672a\u9022\u654c\u624b\uff0c\u4eca\u5929\u5374\u662f\u4e0d\u80fd\u4e0d\u670d\u4f60\u4e86\uff01\u201d \u3000\u3000\u4f17\u4eba\u542c\u5f97\u5d14\u9547\u5c71\u81ea\u5df1\u8ba4\u8f93\uff0c\u90fd\u662f\u4e0d\u7981\u5927\u4e3a\u9a87\u5f02\uff0c\u7eb7\u7eb7\u6253\u542c\u891a\u4e91\u5cf0\u7684\u6765\u5386\u3002\u4f46\u5374\u6ca1\u6709\u4e00\u4e2a \u4eba\u77e5\u9053\u4ed6\u7684\u6765\u5386\u3002\u5d14\u9547\u5c71\u521a\u521a\u9000\u4e0b\uff0c\u6df3\u4e8e\u5468\u8fd9\u8fb9\u4e00\u4e2a\u767d\u8863\u7684\u4e2d\u5e74\u6c49\u5b50\u7acb\u5373\u8ddf\u7740\u51fa\u6765\uff0c\u5fae\u7b11\u8bf4 \u9053\uff1a\u201c\u891a\u5144\u795e\u6280\uff0c\u4ee4\u4eba\u5927\u5f00\u773c\u754c\u3002\u67f3\u67d0\u89c1\u730e\u5fc3\u559c\uff0c\u7279\u6765\u8bf7\u6559\u9ad8\u62db\u3002\u201d \u3000\u3000\u8fd9\u4e2a\u4eba\u662f\u5d14\u9547\u5c71\u7684\u597d\u670b\u53cb\uff0c\u4e5f\u662f\u6df3\u4e8e\u5468\u767e\u822c\u7684\u5927\u5957\u4ea4\u60c5\uff0c\u624d\u8bf7\u5f97\u4ed6\u6765\u7684\u4e24\u5927\u9ad8\u624b\u4e4b\u4e00\u7684 \u67f3\u6d1e\u5929\u3002 \u3000\u3000\u67f3\u6d1e\u5929\u7684\u540d\u5934\u6bd4\u5d14\u9547\u5c71\u66f4\u5927\uff0c\u4ed6\u662f\u5251\u672f\u7684\u5927\u540d\u5bb6\uff0c\u4ee5\u4e03\u5341\u4e8c\u624b\u8fde\u73af\u593a\u547d\u5251\u6cd5\u79f0\u96c4\u7eff\u6797\u3002 \u867d\u7136\u6216\u8005\u8fd8\u672a\u80fd\u4e0e\u5b5f\u5c11\u521a\u3001\u8c37\u5e73\u9633\u7b49\u524d\u8f88\u5251\u5ba2\u6bd4\u80a9\uff0c\u4f46\u6b66\u6797\u516c\u8bba\u5df2\u662f\u8ba4\u4e3a\u4ed6\u53ef\u4ee5\u5217\u540d\u5728\u5f53\u4e16 \u7684\u5341\u5927\u5251\u5ba2\u4e4b\u5185\u3002 \u3000\u3000\u67f3\u6d1e\u5929\u6700\u521d\u672c\u6765\u662f\u548c\u5d14\u9547\u5c71\u4e00\u6837\uff0c\u5e76\u4e0d\u51c6\u5907\u51fa\u624b\u5e2e\u5fd9\u6df3\u4e8e\u5468\u7684\u3002\u4f46\u540e\u6765\u770b\u89c1\u5d14\u9547\u5c71\u8d25\u5728 \u8fd9\u65e0\u540d\u5c11\u5e74\u7684\u624b\u4e0a\uff0c\u5374\u662f\u4e0d\u7531\u5f97\u4e0d\u5f15\u8d77\u4e86\u597d\u5947\u4e4b\u5fc3\u3002\u4ed6\u8bf4\u4ed6\u662f\u201c\u89c1\u730e\u5fc3\u559c\u201d\uff0c\u8fd9\u4e5f\u7684\u786e\u662f\u4ed6 \u7684\u771f\u5fc3\u8bf4\u8bdd\u3002\u4e0d\u8fc7\uff0c\u4ed6\u867d\u7136\u4e0d\u542b\u654c\u610f\uff0c\u5374\u4e5f\u591a\u5c11\u6709\u70b9\u60f3\u8981\u66ff\u597d\u53cb\u8d62\u56de\u4e00\u573a\u7684\u610f\u601d\u3002 \u3000\u3000\u8463\u5f00\u5c71\u9053\uff1a\u201c\u67f3\u5144\uff0c\u4f60\u662f\u4f7f\u5251\u7684\u5927\u540d\u5bb6\uff0c\u4e0d\u5982\u8bf7\u674e\u516c\u5b50\u966a\u4f60\u8fc7\u62db\u5427\u3002\u201d\u5c60\u51e4\u8fd9\u8fb9\u7684\u8ba1\u5212 \u662f\u51c6\u5907\u7559\u4e0b\u674e\u601d\u5357\u6765\u5bf9\u4ed8\u6df3\u4e8e\u5468\u6216\u8005\u5c60\u9f99\u7684\uff0c\u8fd9\u8ba1\u5212\u8463\u5f00\u5c71\u5f53\u7136\u77e5\u9053\u3002\u4e0d\u8fc7\u56e0\u4e3a\u67f3\u6d1e\u5929\u51fa\u573a \u51fa\u4e4e\u4ed6\u4eec\u8fd9\u8fb9\u7684\u610f\u6599\u4e4b\u5916\uff0c\u67f3\u6d1e\u5929\u592a\u8fc7\u5389\u5bb3\uff0c\u8463\u5f00\u5c71\u518d\u4e09\u601d\u91cf\uff0c\u9664\u4e86\u674e\u601d\u5357\u53ea\u6015\u65e0\u4eba\u80fd\u591f\u5e94 \u4ed8\u5f97\u4e86\u4ed6\u7684\u5251\u62db\uff0c\u662f\u4ee5\u53ea\u597d\u4e34\u65f6\u6539\u53d8\u8ba1\u5212\u3002 \u3000\u3000\u4e0d\u6599\u67f3\u6d1e\u5929\u5374\u9053\uff1a\u201c\u5251\u672f\u540d\u5bb6\u7684\u79f0\u53f7\u6211\u4e0d\u6562\u5f53\uff0c\u4f46\u674e\u516c\u5b50\u82e5\u8981\u6307\u6559\u7684\u8bdd\uff0c\u7559\u5f85\u4e0b\u4e00\u573a\u6211 \u518d\u5949\u966a\u5982\u4f55\uff1f\u8fd9\u4e00\u573a\u8bf7\u8ba9\u6211\u5148\u4e0e\u891a\u5144\u5207\u78cb\u5370\u8bc1\u638c\u4e0a\u7684\u529f\u592b\u3002\u201d \u3000\u3000\u67f3\u6d1e\u5927\u7adf\u7136\u820d\u957f\u7528\u77ed\uff0c\u4e0d\u4f7f\u5251\u800c\u8981\u4e0e\u891a\u4e91\u5cf0\u6bd4\u638c\uff0c\u6b64\u8a00\u4e00\u51fa\uff0c\u7fa4\u96c4\u90fd\u662f\u59cb\u6599\u4e0d\u53ca\u3002\u891a\u4e91 \u5cf0\u638c\u6cd5\u7684\u7cbe\u5999\u662f\u6709\u76ee\u5171\u7779\u7684\uff0c\u7fa4\u96c4\u5fc3\u60f3\uff1a\u201c\u67f3\u6d1e\u5929\u82e5\u4e0d\u7528\u5251\uff0c\u90a3\u5012\u662f\u4e0d\u5fc5\u6015\u4ed6\u4e86\u3002\u201d \u3000\u3000\u53ea\u6709\u6df3\u4e8e\u5468\u77e5\u9053\uff0c\u67f3\u6d1e\u5929\u4e0d\u4f46\u5251\u672f\u9ad8\u660e\uff0c\u4ed6\u7684\u7ef5\u638c\u51fb\u77f3\u5982\u7c89\u7684\u529f\u592b\u4e5f\u662f\u6781\u4e3a\u4e86\u5f97\uff0c\u53ea\u6709 \u5728\u5d14\u9547\u5c71\u4e4b\u4e0a\uff0c\u51b3\u4e0d\u5728\u5d14\u9547\u5c71\u4e4b\u4e0b\u3002\u53ea\u56e0\u4e3a\u4ed6\u5251\u672f\u7684\u540d\u6c14\u5927\u54cd\uff0c\u5bf9\u4ed8\u5f3a\u654c\u53c8\u4ece\u6765\u53ea\u662f\u7528\u5251\uff0c \u662f\u4ee5\u4ed6\u7684\u62ff\u4e0a\u529f\u592b\uff0c\u4e3a\u5251\u672f\u7684\u540d\u6c14\u63a9\u76d6\uff0c\u7eff\u6797\u4e2d\u5c11\u4eba\u77e5\u6653\u3002 \u3000\u3000\u6df3\u4e8e\u5468\u5fc3\u60f3\uff1a\u201c\u67f3\u6d1e\u5929\u5251\u672f\u7b2c\u4e00\uff0c\u638c\u6cd5\u7b2c\u4e8c\u3002\u4f46\u5373\u4f7f\u53ea\u662f\u6bd4\u638c\uff0c\u6599\u60f3\u4e5f\u53ef\u4ee5\u8d62\u5f97\u8fd9\u4e2a\u5c0f \u5b50\u4e86\u3002\u201d\u6b63\u662f\uff1a \u3000\u3000\u4e00\u9e23\u60ca\u4eba\u597d\u8eab\u624b\uff0c\u4e0d\u8bc6\u5c11\u5e74\u4f55\u5904\u6765\uff1f \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u4e09\u56de\u3000\u53cc\u96c4\u6bd4\u5251\u60ca\u5fc3\u9b44\u3000\u5c11\u4fa0\u4f24\u60c5\u52a8\u6740\u673a \u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b \u7b2c\u4e8c\u5341\u4e09\u56de\u3000\u53cc\u96c4\u6bd4\u5251\u60ca\u5fc3\u9b44\u3000\u5c11\u4fa0\u4f24\u60c5\u52a8\u6740\u673a \u3000\u3000\u4e0d\u6599\u6df3\u4e8e\u5468\u5fc3\u5ff5\u672a\u5df2\uff0c\u891a\u4e91\u5cf0\u5df2\u5728\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u4e45\u4ef0\u67f3\u8235\u4e3b\u5251\u672f\u5929\u4e0b\u65e0\u53cc\uff0c\u5982\u82e5\u4e0d\u5411\u67f3 \u8235\u4e3b\u8ba8\u6559\u5251\u672f\uff0c\u5c82\u4e0d\u662f\u5982\u5165\u5b9d\u5c71\u7a7a\u624b\u56de\uff1f\u891a\u67d0\u4ee5\u5c0f\u53ef\u4e0d\u624d\uff0c\u5012\u662f\u5b81\u613f\u8bf7\u6559\u67f3\u8235\u4e3b\u7684\u5251\u672f\uff01\u201d \u3000\u3000\u67f3\u6d1e\u5929\u8981\u4e0e\u5bf9\u65b9\u6bd4\u638c\uff0c\u6b64\u4e8b\u5df2\u662f\u9887\u51fa\u4f17\u4eba\u610f\u6599\u4e4b\u5916\uff0c\u54ea\u77e5\u891a\u4e91\u5cf0\u7adf\u7136\u4e0d\u80af\u5360\u6b64\u4fbf\u5b9c\uff0c\u660e \u77e5\u5bf9\u65b9\u662f\u4e2a\u5251\u672f\u7684\u5927\u540d\u5bb6\uff0c\u4ecd\u7136\u8981\u4e0e\u5bf9\u65b9\u6bd4\u5251\uff0c\u7fa4\u96c4\u4e0d\u7981\u53c8\u662f\u60ca\u8be7\uff0c\u53c8\u662f\u4f69\u670d\uff0c\u5fc3\u4e2d\u4ff1\u662f\u60f3 \u9053\uff1a\u201c\u96be\u9053\u8fd9\u4e2a\u65e0\u540d\u5c11\u5e74\u5728\u5251\u672f\u4e0a\u4e5f\u6709\u8fc7\u4eba\u7684\u9020\u8be3\uff1f\u4f46\u4e0d\u7ba1\u5982\u4f55\uff0c\u4ed6\u8fd9\u4efd\u4e0d\u754f\u5f3a\u654c\u7684\u8c6a\u6c14\uff0c \u5df2\u662f\u8db3\u4ee5\u4ee4\u4eba\u5fc3\u6298\uff01\u201d \u3000\u3000\u6df3\u4e8e\u5468\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5370\u8bc1\u6b66\u529f\uff0c\u81ea\u662f\u4e0d\u62d8\u4e00\u683c\u3002\u6bd4\u638c\u4e5f\u597d\uff0c\u6bd4\u5251\u4e5f\u597d\uff0c\u5251\u638c\u90fd\u6bd4 \u66f4\u597d\uff01\u8bf7\u4e24\u4f4d\u5c3d\u5c55\u6240\u957f\uff0c\u8ba9\u6211\u4eec\u4e5f\u597d\u5927\u9971\u773c\u798f\uff01\u201d \u3000\u3000\u77f3\u749e\u9053\uff1a\u201c\u891a\u5144\uff0c\u8bf7\u4f60\u8bd5\u8bd5\u8fd9\u67c4\u9752\u94a2\u5251\u5408\u4e0d\u5408\u7528\uff1f\u201d\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u65e0\u987b\u8d39\u5fc3\uff0c\u5c0f\u5f1f\u643a\u6709 \u5175\u5203\u3002\u201d \u3000\u3000\u500f\u5730\u4e00\u4e2a\u8f6c\u8eab\uff0c\u624b\u4e2d\u5df2\u591a\u4e86\u4e00\u67c4\u957f\u5251\u3002\u539f\u6765\u4ed6\u7684\u4f69\u5251\u4e43\u662f\u4e00\u628a\u53ef\u5316\u4f5c\u7ed5\u6307\u67d4\u7684\u8f6f\u5251\uff0c\u7cfb \u5728\u8170\u95f4\uff0c\u5f53\u4f5c\u8170\u5e26\uff0c\u65c1\u4eba\u770b\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u67f3\u6d1e\u592d\u4e00\u770b\u5c31\u77e5\u9053\u662f\u4e00\u628a\u5b9d\u5251\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4ed6\u82e5\u662f\u4ed7\u7740\u5b9d\u5251\u7684\u950b\u5229\uff0c\u6211\u5012\u4e0d\u6015\u3002\u4f46\u4ed6 \u80fd\u591f\u4f7f\u7528\u8fd9\u79cd\u8f6f\u5251\uff0c\u60f3\u5fc5\u5728\u5251\u672f\u4e0a\u4e5f\u6709\u76f8\u5f53\u9020\u8be3\u3002\u201d\u8981\u77e5\u5728\u5404\u79cd\u5175\u5203\u4e4b\u4e2d\uff0c\u8f6f\u5251\u6700\u4e3a\u96be\u4f7f\u3002 \u4f46\u5251\u672f\u5018\u82e5\u7ec3\u5230\u5341\u5206\u9ad8\u660e\u7684\u65f6\u5019\uff0c\u6709\u6ca1\u6709\u5b9d\u5251\uff0c\u90fd\u662f\u4e00\u6837\u3002 \u3000\u3000\u53cc\u65b9\u5404\u81ea\u4e3e\u5251\u8bf4\u4e86\u4e00\u4e2a\u201c\u8bf7\u201d\u5b57\uff0c\u9661\u7136\u95f4\uff0c\u53ea\u542c\u5f97\u4e00\u9635\u6025\u4fc3\u7684\u53ee\u5f53\u4e4b\u58f0\uff0c\u7aef\u7684\u6709\u5982\u7e41\u5f26 \u6025\u54cd\u3002\u539f\u6765\u4ed6\u4eec\u5728\u8fd9\u4e00\u62db\u4e4b\u95f4\uff0c\u53cc\u5251\u5df2\u662f\u78b0\u51fb\u4e86\u5341\u4e03\u516b\u4e0b\uff01 \u3000\u3000\u559d\u5f69\u58f0\u4e2d\uff0c\u53ea\u89c1\u706b\u82b1\u56db\u6e85\uff0c\u67f3\u6d1e\u5929\u817e\u8eab\u8dc3\u8d77\uff0c\u501f\u891a\u4e91\u5cf0\u8f6f\u5251\u7684\u53cd\u5f39\u4e4b\u529b\uff0c\u51cc\u7a7a\u51fb\u4e0b\uff0c\u5f84 \u523a\u4ed6\u80cc\u5fc3\u7684\u201c\u98ce\u5e9c\u7a74\u201d\uff0c\u891a\u4e91\u5cf0\u53cd\u5251\u4e00\u5708\uff0c\u53c8\u662f\u201c\u54e8\u201d\u7684\u4e00\u58f0\uff0c\u67f3\u6d1e\u5929\u8eab\u5f62\u843d\u5730\uff0c\u891a\u4e91\u5cf0\u6536 \u52bf\u4e0d\u4f4f\uff0c\u4e5f\u4e0d\u7531\u81ea\u5df1\u5730\u6253\u4e86\u4e00\u4e2a\u76d8\u65cb\uff01 \u3000\u3000\u4ea4\u6362\u4e86\u4e24\u62db\uff0c\u53cc\u65b9\u90fd\u662f\u4e0d\u7981\u4fef\u9996\u4e00\u77a7\uff0c\u67f3\u6d1e\u5929\u89c1\u5251\u8eab\u4e0a\u5e76\u65e0\u4f24\u75d5\uff0c\u677e\u4e86\u4e00\u6c14\u3002\u4f46\u5fc3\u91cc\u5374 \u4e5f\u597d\u751f\u8be7\u5f02\uff0c\u6697\u81ea\u5bfb\u601d\uff1a\u201c\u8fd9\u4eba\u662f\u4f55\u95e8\u6d3e\uff0c\u600e\u7684\u6211\u5374\u77a7\u4e0d\u51fa\u6765\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u7684\u5251\u8eab\u4e5f\u6ca1\u4f24\u75d5\uff0c\u4f46\u867d\u7136\u662f\u53cc\u65b9\u7684\u5251\u90fd\u6ca1\u53d7\u4f24\uff0c\u891a\u4e91\u5cf0\u5374\u662f\u8981\u6bd4\u67f3\u6d1e\u5929\u66f4\u4e3a\u5403\u60ca \u4e86\u3002\u8981\u77e5\u4ed6\u7684\u5251\u8d28\u5728\u5bf9\u65b9\u4e4b\u4e0a\uff0c\u53cc\u65b9\u7684\u5251\u90fd\u6ca1\u53d7\u4f24\uff0c\u90a3\u5373\u662f\u5bf9\u65b9\u7684\u5251\u672f\u8981\u6bd4\u4ed6\u7565\u80dc\u4e00\u7b79\u4e86\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u867d\u7136\u5403\u60ca\uff0c\u5374\u4e5f\u5e76\u4e0d\u6c14\u9981\u3002\u56e0\u4e3a\u4ed6\u4e5f\u6709\u4e00\u4e2a\u6709\u5229\u7684\u6761\u4ef6\uff0c\u67f3\u6d1e\u5929\u662f\u6210\u540d\u5251\u5ba2\uff0c\u6240 \u4f7f\u7684\u5251\u672f\u891a\u4e91\u5cf0\u65e9\u5df2\u77e5\u9053\uff0c\u4f46\u891a\u4e91\u5cf0\u7684\u5251\u672f\uff0c\u67f3\u6d1e\u5929\u5374\u662f\u8fd8\u672a\u6478\u5230\u5e95\u7ec6\u3002\u4ea6\u5373\u662f\uff0c\u891a\u4e91\u5cf0\u662f \u77e5\u5df1\u77e5\u5f7c\uff0c\u67f3\u6d1e\u5929\u5219\u662f\u4ec5\u77e5\u5df1\u800c\u4e0d\u77e5\u5f7c\u3002 \u3000\u3000\u53cc\u65b9\u867d\u7136\u90fd\u662f\u62b1\u7740\u4ee5\u6b66\u4f1a\u53cb\u7684\u5fc3\u601d\uff0c\u4f46\u9ad8\u624b\u6bd4\u6597\uff0c\u5c82\u53c8\u5bb9\u5f97\u6709\u534a\u62db\u76f8\u8ba9\uff1f\u67f3\u6d1e\u5927\u7684\u4e03\u5341 \u4e8c\u624b\u8fde\u73af\u593a\u547d\u5251\u6cd5\uff0c\u987e\u540d\u601d\u4e49\uff0c\u4e43\u662f\u4ee5\u72e0\u8fa3\u89c1\u957f\u3002\u6597\u5230\u7d27\u5904\uff0c\u53ea\u89c1\u4ed6\u8fd0\u5251\u5982\u98ce\uff0c\u5251\u5251\u6307\u5411\u891a \u4e91\u5cf0\u7684\u8981\u5bb3\u7a74\u9053\uff01 \u3000\u3000\u67f3\u6d1e\u5929\u7684\u5251\u672f\u65e9\u5df2\u7ec3\u5230\u6536\u53d1\u968f\u5fc3\u7684\u5883\u754c\uff0c\u4ed6\u4f7f\u51fa\u5982\u6b64\u72e0\u8fa3\u7684\u5251\u6cd5\uff0c\u76ee\u7684\u662f\u53ea\u6c42\u80dc\u62db\uff0c\u6709 \u628a\u63e1\u53ef\u4ee5\u4e0d\u4f24\u5bf9\u65b9\u6027\u547d\u3002\u4f46\u7fa4\u96c4\u4e0d\u77e5\uff0c\u5728\u4ed6\u4eec\u7684\u773c\u4e2d\uff0c\u8fd9\u5df2\u662f\u4e00\u573a\u6027\u547d\u76f8\u6251\u7684\u6076\u6218\uff01\u770b\u5230\u7d27 \u5f20\u4e4b\u5904\uff0c\u5f53\u771f\u662f\u6bcf\u4e00\u4e2a\u4eba\u624b\u5fc3\u91cc\u90fd\u634f\u7740\u4e00\u628a\u51b7\u6c57\u3002 \u3000\u3000\u7247\u523b\u4e4b\u95f4\u5df2\u662f\u8fc7\u4e86\u56db\u4e94\u5341\u62db\uff0c\u770b\u5f97\u4eba\u4eba\u8be7\u5f02\u3002\u67f3\u6d1e\u5929\u7684\u5251\u672f\u9ad8\u660e\uff0c\u8fd9\u662f\u5927\u5bb6\u90fd\u77e5\u9053\u4e86 \u7684\uff0c\u4f46\u891a\u4e91\u5cf0\u7684\u5251\u672f\uff0c\u5947\u8be1\u7edd\u4f26\uff0c\u5982\u662f\u5927\u51fa\u4f17\u4eba\u610f\u6599\u4e4b\u5916\uff01 \u3000\u3000\u5728\u67f3\u6d1e\u5929\u7684\u731b\u653b\u4e4b\u4e0b\uff0c\u6709\u8bb8\u591a\u51cc\u5389\u7684\u5251\u62db\uff0c\u770b\u6765\u891a\u4e91\u5cf0\u5df2\u662f\u51b3\u8ba1\u8eb2\u907f\u4e0d\u5f00\uff0c\u4f46\u4e0d\u77e5\u600e \u7684\uff0c\u891a\u4e91\u5cf0\u7adf\u662f\u968f\u624b\u5316\u89e3\uff0c\u4e3e\u91cd\u82e5\u8f7b\uff0c\u6bcf\u4e00\u6b21\u90fd\u662f\u5728\u95f4\u4e0d\u5bb9\u53d1\u4e4b\u9645\uff0c\u903c\u5f97\u654c\u4eba\u8f6c\u653b\u4e3a\u5b88\u3002\u65c1 \u89c2\u7684\u4eba\u4e0d\u4e4f\u5251\u672f\u597d\u624b\uff0c\u7adf\u662f\u8fde\u4ed6\u4f7f\u7528\u4ec0\u4e48\u624b\u6cd5\u90fd\u770b\u4e0d\u6e05\u695a\u3002 \u3000\u3000\u65c1\u89c2\u7684\u4eba\u90fd\u611f\u5230\u8be7\u5f02\uff0c\u67f3\u6d1e\u5929\u5219\u662f\u66f4\u8981\u6697\u6697\u5403\u60ca\u4e86\u3002\u8981\u77e5\u4ed6\u662f\u5251\u672f\u7684\u5927\u540d\u5bb6\uff0c\u5404\u5bb6\u5404\u6d3e \u7684\u5251\u672f\u65e0\u4e0d\u77e5\u6653\uff0c\u4ed6\u672c\u6765\u4ee5\u4e3a\u53ea\u987b\u6597\u4e2a\u5341\u6570\u62db\uff0c\u5c31\u53ef\u4ee5\u770b\u51fa\u5bf9\u65b9\u7684\u5b88\u6d3e\u6765\u5386\u7684\uff0c\u54ea\u77e5\u8fc7\u4e86\u56db \u4e94\u5341\u62db\uff0c\u4ecd\u7136\u77a7\u4e0d\u51fa\u534a\u70b9\u7aef\u502a\u3002\u891a\u4e91\u5cf0\u7684\u51fa\u62db\uff0c\u5f80\u5f80\u662f\u4ece\u4ed6\u610f\u60f3\u4e0d\u5230\u7684\u65b9\u4f4d\u523a\u6765\uff0c\u4f9d\u7167\u6b63\u5b97 \u7684\u5251\u8bc0\u8bf4\u6765\uff0c\u90a3\u662f\u5927\u5927\u8fdd\u80cc\u6b66\u5b66\u539f\u7406\u7684\uff0c\u4f46\u5374\u53c8\u662f\u504f\u504f\u6070\u5230\u597d\u5904\u7684\u5316\u89e3\u4e86\u4ed6\u51cc\u5389\u7684\u653b\u52bf\u3002 \u3000\u3000\u4e0d\u8fc7\u67f3\u6d1e\u5929\u867d\u7136\u5403\u60ca\uff0c\u5374\u4e5f\u5e76\u4e0d\u754f\u60e7\u3002\u56e0\u4e3a\u4ed6\u53ea\u662f\u6478\u4e0d\u6e05\u5bf9\u65b9\u7684\u5e95\u7ec6\u800c\u5df2\uff0c\u5e76\u975e\u5251\u672f\u4e0a \u4e0d\u5982\u5bf9\u65b9\u3002\u6597\u4e86\u56db\u4e94\u5341\u62db\uff0c\u4ed6\u4e00\u76f4\u662f\u7d27\u63e1\u5148\u624b\uff0c\u7a33\u7a33\u5360\u4e86\u4e03\u6210\u653b\u52bf\u3002 \u3000\u3000\u4e0d\u77e5\u4e0d\u89c9\u6597\u5230\u4e86\u767e\u62db\u5f00\u5916\uff0c\u67f3\u6d1e\u5929\u6c42\u80dc\u5fc3\u5207\uff0c\u628a\u4ed6\u5251\u672f\u4e2d\u72e0\u8fa3\u7684\u957f\u5904\u5168\u90e8\u53d1\u6325\uff0c\u6b64\u65f6\u5018 \u82e5\u891a\u4e91\u5cf0\u4e2d\u4ed6\u4e00\u5251\u7684\u8bdd\uff0c\u5373\u4f7f\u67f3\u6d1e\u65e0\u53ef\u4ee5\u6536\u53d1\u968f\u5fc3\uff0c\u891a\u4e91\u5cf0\u4e5f\u662f\u96be\u514d\u8981\u53d7\u4f24\u7684\u4e86\u3002\u6218\u51b5\u8d8a\u6765 \u8d8a\u89c1\u60ca\u9669\uff0c\u6b64\u65f6\u8fde\u674e\u601d\u5357\u548c\u5b5f\u660e\u971e\u4e8c\u4eba\uff0c\u4e5f\u662f\u4e0d\u7531\u5f97\u4e0d\u6697\u6697\u4e3a\u891a\u4e91\u5cf0\u62c5\u5fc3\u4e86\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u989d\u89d2\u5fae\u5fae\u89c1\u6c57\uff0c\u4f46\u795e\u8272\u4ecd\u5f88\u4ece\u5bb9\u3002\u53ea\u89c1\u4e00\u4e2a\u653b\u5f97\u8fc5\u75be\uff0c\u6709\u5982\u5929\u98ce\u6d77\u96e8\uff0c\u8feb\u4eba\u800c \u6765\uff1b\u4e00\u4e2a\u5b88\u5f97\u6c89\u7a33\uff0c\u6709\u5982\u957f\u5824\u5367\u6ce2\uff0c\u4e0d\u4e3a\u6447\u52a8\uff01\u800c\u4e14\u5f80\u5f80\u5728\u67f3\u6d1e\u5929\u653b\u5f97\u6781\u4e3a\u7d27\u8feb\u4e4b\u65f6\uff0c\u891a\u4e91 \u5cf0\u7a81\u7136\u53cd\u6251\u4e00\u4e24\u62db\uff0c\u53cd\u6251\u7684\u62db\u6570\u5947\u8be1\u7edd\u4f26\uff0c\u767b\u65f6\u53c8\u626d\u8f6c\u4e86\u52a3\u52bf\uff0c\u6273\u6210\u5e73\u624b\uff01 \u3000\u3000\u6fc0\u6218\u4e2d\u67f3\u6d1e\u5929\u5ffd\u5730\u5192\u9669\u8fdb\u62db\uff0c\u5251\u5149\u6709\u5982\u65e5\u9053\u957f\u8679\uff0c\u6a2a\u5377\u8fc7\u53bb\u3002\u4e00\u62db\u4e4b\u4e2d\u85cf\u7740\u8fde\u73af\u4e03\u5f0f\u3002 \u8fd9\u5239\u90a3\u95f4\uff0c\u7fa4\u96c4\u90fd\u662f\u5927\u60ca\u5931\u8272\u3002\u5c3d\u7ba1\u4ed6\u4eec\u6bcf\u4e00\u4e2a\u4eba\u90fd\u66fe\u7ecf\u5386\u8fc7\u4e0d\u5c11\u6076\u6597\u7684\u573a\u9762\uff0c\u4f46\u8fd9\u6837\u4e00\u62db \u751f\u6b7b\u7acb\u5224\u72e0\u8fa3\u5251\u62db\uff0c\u4ed6\u4eec\u5374\u662f\u4ece\u672a\u89c1\u8fc7\u3002\u8fd9\u5239\u90a3\u95f4\uff0c\u4eba\u4eba\u5c4f\u606f\u800c\u89c2\uff0c\u5f53\u771f\u662f\u8fde\u4e00\u6839\u9488\u8dcc\u5728\u5730 \u4e0b\u90fd\u542c\u5f97\u89c1\u54cd\uff01 \u3000\u3000\u9661\u7136\u95f4\u53ea\u89c1\u4e24\u9053\u5251\u5149\u77eb\u82e5\u6e38\u9f99\u7684\u98de\u4e0a\u5929\u7a7a\uff0c\u4e24\u6761\u4eba\u5f71\u500f\u7684\u5206\u5f00\uff0c\u540c\u65f6\u53eb\u9053\uff1a\u201c\u597d\u5251 \u6cd5\uff01\u201d\u8fc7\u4e86\u7247\u523b\uff0c\u8fd9\u624d\u542c\u89c1\u201c\u54e8\u54e8\u201d\u7684\u4e24\u58f0\u91d1\u77f3\u4ea4\u51fb\u7684\u6e05\u8106\u58f0\u54cd\uff0c\u4e24\u67c4\u957f\u5251\u540c\u65f6\u6389\u4e0b\uff0c\u63d2\u8fdb \u94fa\u6ee1\u788e\u77f3\u7684\u786c\u5730\uff0c\u51e0\u4e4e\u6ca1\u81f3\u5251\u67c4\uff01 \u3000\u3000\u8fd9\u6837\u7684\u7ed3\u5c40\u5f53\u771f\u662f\u7686\u5927\u6b22\u559c\uff0c\u7fa4\u96c4\u7ef7\u7d27\u7684\u5fc3\u5f26\u8fd9\u624d\u677e\u4e86\u4e0b\u6765\uff0c\u8f70\u7136\u559d\u5f69\u3002\u8463\u5f00\u5c71\u7b49\u4eba\u54c8 \u54c8\u7b11\u9053\uff1a\u201c\u65d7\u9f13\u76f8\u5f53\uff0c\u53ef\u79f0\u53cc\u7edd\u3002\u7aef\u7684\u662f\u4ee4\u6211\u4eec\u5927\u5f00\u773c\u754c\u4e86\uff01\u201d \u3000\u3000\u4e24\u4eba\u7684\u5251\u540c\u65f6\u51fa\u624b\uff0c\u8fd9\u4e00\u573a\u6bd4\u5251\u5f53\u7136\u5e94\u8be5\u7b97\u662f\u4e0d\u5206\u80dc\u8d1f\u3002\u8463\u5f00\u5c71\u7684\u8bf4\u8bdd\u5c31\u662f\u8868\u660e\u8fd9\u4e2a\u610f \u601d\uff0c\u7fa4\u96c4\u4ea6\u89c9\u5f97\u6beb\u65e0\u7591\u4e49\u3002\u4e0d\u6599\u8463\u5f00\u5c71\u7684\u8bdd\u521a\u521a\u8bf4\u5b8c\uff0c\u6df3\u4e8e\u5468\u63a5\u7740\u5c31\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4e0d\u89c1 \u5f97\uff01\u201d \u3000\u3000\u6b64\u65f6\u67f3\u3001\u891a\u4e8c\u4eba\u90fd\u5404\u81ea\u628a\u5251\u62d4\u4e86\u8d77\u6765\uff0c\u891a\u4e91\u5cf0\u4fef\u9996\u4e00\u77a7\u3001\u4e0d\u89c9\u53d8\u4e86\u9762\u8272\uff0c\u968f\u5373\u62f1\u624b\u8bf4 \u9053\uff1a\u201c\u6df3\u4e8e\u5be8\u4e3b\u8bf4\u5f97\u4e0d\u9519\uff0c\u8fd9\u4e00\u573a\u662f\u6211\u8f93\u4e86\uff01\u201d \u3000\u3000\u539f\u6765\u53cc\u65b9\u7684\u5251\u4e0a\u90fd\u6709\u4e00\u4e2a\u5c0f\u5c0f\u7684\u7f3a\u53e3\uff0c\u4f46\u891a\u4e91\u5cf0\u7528\u7684\u662f\u4e00\u628a\u524a\u94c1\u5982\u6ce5\u7684\u5b9d\u5251\uff0c\u67f3\u6d1e\u5929\u7684 \u5251\u5374\u4e0d\u8fc7\u662f\u4e00\u628a\u666e\u901a\u7684\u9752\u521a\u5251\uff0c\u56e0\u6b64\u867d\u7136\u662f\u540c\u6837\u635f\u4e86\u5175\u5203\uff0c\u5374\u8bc1\u660e\u4e86\u67f3\u6d1e\u5929\u7684\u529f\u529b\u8f83\u80dc\u4e00 \u7b79\u3002 \u3000\u3000\u67f3\u6d1e\u5929\u6697\u6697\u53eb\u4e86\u4e00\u58f0\uff0c\u201c\u60ed\u6127\u201d\uff0c\u8bf4\u9053\uff1a\u201c\u891a\u5144\u4f55\u5fc5\u8fc7\u8c26\uff0c\u5728\u5251\u6cd5\u4e0a\u6211\u5e76\u6ca1\u8d62\u4f60\u3002\u201d\u8fd9 \u8bdd\u5012\u4e5f\u8bf4\u5f97\u5341\u5206\u5766\u767d\uff0c\u5728\u67f3\u6d1e\u5927\u7684\u5185\u5fc3\u7684\u786e\u5e76\u4e0d\u611f\u89c9\u5230\u80dc\u5229\u7684\u559c\u60a6\uff0c\u53cd\u800c\u662f\u591a\u5c11\u6709\u70b9\u60ed\u6127 \u7684\u3002 \u3000\u3000\u539f\u6765\u67f3\u6d1e\u5929\u5728\u548c\u4ed6\u6597\u5230\u767e\u62db\u5f00\u5916\u4e4b\u540e\uff0c\u8fd9\u624d\u60f3\u5230\u4e86\u4e00\u62db\u514b\u654c\u5236\u80dc\u7684\u5999\u6cd5\uff0c\u4ed6\u7528\u5f3a\u653b\u731b\u6251 \u7684\u201c\u591c\u6218\u516b\u65b9\u201d\u7684\u62db\u5f0f\uff0c\u5251\u5149\u7b3c\u7f69\u4e86\u5bf9\u65b9\u7684\u8eab\u4f53\uff0c\u5728\u8fd9\u6837\u60c5\u5f62\u4e4b\u4e0b\uff0c\u5bf9\u65b9\u7eb5\u6709\u602a\u62db\uff0c\u523a\u5411\u4ed6 \u610f\u60f3\u4e0d\u5230\u7684\u65b9\u4f4d\uff0c\u81f3\u591a\u4e5f\u53ea\u662f\u4e24\u8d25\u4ff1\u4f24\u800c\u5df2\u3002\u8981\u89e3\u62c6\u4ed6\u8fd9\u6837\u731b\u70c8\u7684\u5251\u62db\u5374\u662f\u4e07\u4e07\u4e0d\u80fd\u7684\u3002\u4ed6 \u81ea\u5fd6\u529f\u529b\u6bd4\u891a\u4e91\u5cf0\u9ad8\uff0c\u82e5\u662f\u4e24\u8d25\u4ff1\u4f24\uff0c\u891a\u4e91\u5cf0\u5b9a\u7136\u4f24\u5f97\u6bd4\u4ed6\u66f4\u91cd\u3002\u662f\u4ee5\u4ed6\u4f30\u8ba1\u891a\u4e91\u5cf0\u4e00\u5b9a\u4e0d \u6562\u786c\u62fc\uff0c\u552f\u4e00\u53ef\u4ee5\u9003\u547d\u7684\u529e\u6cd5\u5c31\u662f\u6254\u5251\u62b5\u6321\u4e00\u4e0b\uff0c\u8ddf\u7740\u4f0f\u5728\u5730\u4e0a\uff0c\u6253\u6eda\u9003\u51fa\u4ed6\u7684\u5251\u5708\u3002\u82e5\u7136 \u5f53\u771f\u5982\u4ed6\u6240\u7b97\u7684\u8bdd\uff0c\u891a\u4e91\u5cf0\u4e4b\u8d25\u5c31\u662f\u8d25\u5f97\u5341\u5206\u72fc\u72c8\u7684\u4e86\u3002 \u3000\u3000\u4e0d\u6599\u891a\u4e91\u5cf0\u5374\u5e76\u4e0d\u5982\u4ed6\u6240\u7b97\uff0c\u5728\u8fd9\u6700\u5173\u952e\u7684\u4e00\u62db\u7adf\u7136\u786c\u63a5\u4e0b\u6765\uff0c\u800c\u4e14\u63a5\u5f97\u5341\u5206\u5de7\u5999\uff0c\u891a \u4e91\u5cf0\u662f\u7528\u201c\u7ede\u5251\u5f0f\u201d\u5316\u89e3\u4e86\u4ed6\u4e00\u90e8\u5206\u7684\u52b2\u9053\uff0c\u4e24\u4eba\u7684\u5251\u8fd9\u624d\u540c\u65f6\u8131\u624b\u7684\u3002\u662f\u4ee5\u6597\u5251\u7684\u7ed3\u679c\u53ea \u80fd\u8bc1\u660e\u67f3\u6d1e\u5929\u7684\u529f\u529b\u8f83\u9ad8\uff0c\u5728\u5251\u6cd5\u4e0a\u5219\u4ecd\u662f\u4e0d\u5206\u80dc\u8d1f\u3002\u800c\u76ee\u5374\u4ee5\u529f\u529b\u800c\u8bba\uff0c\u4e5f\u7684\u786e\u53ea\u80fd\u7b97\u662f \u201c\u8f83\u80dc\u4e00\u7b79\u201d\u800c\u5df2\uff0c\u56e0\u4e3a\u67f3\u6d1e\u5929\u7684\u5251\u4e5f\u662f\u540c\u65f6\u8131\u624b\u7684\u3002 \u3000\u3000\u867d\u7136\u5982\u6b64\uff0c\u4f46\u8fd9\u6837\u7684\u7ed3\u679c\u4ea6\u5df2\u662f\u5927\u51fa\u4f17\u4eba\u610f\u6599\u4e4b\u5916\uff0c\u8c01\u90fd\u60f3\u4e0d\u5230\u4e00\u4e2a\u85c9\u85c9\u65e0\u540d\u7684\u891a\u4e91 \u5cf0\uff0c\u5c45\u7136\u80fd\u591f\u548c\u4e00\u4e2a\u5251\u672f\u5927\u540d\u5bb6\u6253\u6210\u5e73\u624b\uff01\u4f55\u51b5\u891a\u4e91\u5cf0\u8fd8\u662f\u66fe\u7ecf\u548c\u5d14\u9547\u5c71\u5148\u62fc\u4e86\u4e00\u573a\u7684\u3002 \u3000\u3000\u5b5f\u660e\u971e\u7ad9\u5728\u674e\u601d\u5357\u7684\u65c1\u8fb9\uff0c\u6084\u58f0\u8bf4\u9053\uff1a\u201c\u5982\u679c\u891a\u4e91\u5cf0\u4e0d\u662f\u5148\u8017\u4e86\u4e00\u573a\u5185\u529b\uff0c\u8bf4\u4e0d\u5b9a\u8fd9\u4e00 \u573a\u8fd8\u53ef\u4ee5\u80dc\u5462\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u529f\u529b\u662f\u67f3\u6d1e\u5929\u8f83\u9ad8\uff0c\u5251\u6cd5\u4e5f\u7b97\u5f97\u5f88\u662f\u4e0d\u9519\u3002\u891a\u4e91\u5cf0\u82e5\u4e0d\u662f\u5148\u6253\u4e00\u573a\uff0c\u4f9d\u6211 \u770b\u6765\uff0c\u6216\u8005\u53ef\u4ee5\u5728\u4e24\u65b9\u9762\u90fd\u6273\u6210\u5e73\u624b\uff0c\u8981\u80dc\u4ed6\u6050\u6015\u8fd8\u662f\u4e0d\u6613\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff0c\u201c\u5982\u679c\u662f\u7531\u4f60\u5bf9\u4ed8\u4ed6\u5462\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u5fae\u7b11\u9053\uff1a\u201c\u8fd9\u5c31\u96be\u8bf4\u5f97\u5f88\u4e86\u3002\u201d\u8a00\u4e0b\u4e4b\u610f\uff0c\u5176\u5b9e\u5373\u662f\u8bf4\u81ea\u5df1\u81f3\u5c11\u4e5f\u4e0d\u4f1a\u8f93\u7ed9\u67f3\u6d1e \u5929\u3002 \u3000\u3000\u4ed6\u4eec\u4e8c\u4eba\u8bf4\u8bdd\u7684\u58f0\u97f3\u5f88\u7ec6\uff0c\u56e0\u4e3a\u5f53\u573a\u8bc4\u8bba\u9ad8\u624b\u7684\u5251\u8fed\uff0c\u7ed9\u4eba\u542c\u89c1\u4e86\u96be\u514d\u60f9\u8d77\u662f\u975e\uff0c\u662f\u4ee5 \u81ea\u662f\u4e0d\u597d\u9ad8\u8c08\u9614\u8bba\u3002 \u3000\u3000\u4e0d\u6599\u4ed6\u4eec\u867d\u7136\u662f\u7a83\u7a83\u79c1\u8bae\uff0c\u5374\u4e5f\u7ed9\u67f3\u6d1e\u5929\u542c\u89c1\u4e86\u3002\u67f3\u6d1e\u5929\u56e0\u4e3a\u66fe\u7ecf\u542c\u5f97\u5c60\u51e4\u4ecb\u7ecd\u674e\u601d\u5357 \u7684\u6765\u5386\u4e4b\u65f6\uff0c\u8bf4\u4ed6\u662f\u5c11\u6797\u6d3e\u8c37\u5e73\u9633\u8c37\u5927\u4fa0\u7684\u9ad8\u8db3\u5f1f\u5b50\u3001\u8fbe\u6469\u5251\u8fed\u7684\u8863\u94b5\u4f20\u4eba\uff0c\u6545\u6b64\u4e00\u76f4\u90fd\u662f \u5bf9\u4ed6\u5341\u5206\u7559\u610f\u7684\u3002 \u3000\u3000\u67f3\u6d1e\u5929\u7684\u6c14\u91cf\u867d\u7136\u5e76\u4e0d\u72ed\u7a84\uff0c\u5374\u4e5f\u4e0d\u662f\u5fc3\u80f8\u5341\u5206\u5bbd\u5e7f\u7684\u4eba\uff0c\u542c\u4e86\u4ed6\u548c\u5b5f\u660e\u971e\u7684\u8bae\u8bba\uff0c\u4e0d \u89c9\u8d77\u4e86\u597d\u80dc\u4e4b\u5fc3\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4ed6\u662f\u5341\u51e0\u5bb6\u5be8\u4e3b\u63a8\u4e3e\u51fa\u6765\u4e0e\u6df3\u4e8e\u5468\u4e89\u593a\u76df\u4e3b\u7684\u4eba\uff0c\u6211\u5012\u8981\u770b \u770b\u4ed6\u6709\u4f55\u672c\u9886 \u3000\u3000\u891a\u4e91\u5cf0\u8ba4\u8f93\u4e4b\u540e\uff0c\u4fbf\u5373\u9000\u5f00\uff0c\u67f3\u6d1e\u5929\u5219\u662f\u5251\u4e0d\u5f52\u9798\uff0c\u7ad9\u5728\u573a\u4e2d\uff0c\u62ac\u773c\u5411\u674e\u601d\u5357\u671b\u53bb\uff0c\u9ad8 \u58f0\u8bf4\u9053\uff1a\u201c\u521a\u624d\u5c0f\u5f1f\u6709\u8a00\u5728\u5148\uff0c\u8fd9\u4e00\u573a\u4fbf\u8bf7\u674e\u516c\u5b50\u6307\u6559\u3002\u201d \u3000\u3000\u4e00\u6765\u662f\u6709\u8a00\u5728\u5148\uff0c\u4e8c\u6765\u5c60\u51e4\u8fd9\u8fb9\u4e5f\u6ca1\u6709\u8db3\u4ee5\u548c\u67f3\u6d1e\u5929\u5339\u654c\u7684\u5251\u672f\u9ad8\u624b\u3002\u521a\u624d\u597d\u4e0d\u5bb9\u6613\u5947 \u8ff9\u822c\u5730\u94bb\u51fa\u4e00\u4e2a\u891a\u4e91\u5cf0\uff0c\u5982\u4eca\u891a\u4e91\u5cf0\u5df2\u7ecf\u8d25\u4e0b\u9635\u6765\uff0c\u5374\u54ea\u91cc\u53bb\u518d\u627e\u4e00\u4e2a\u891a\u4e91\u5cf0\uff1f\u662f\u4ee5\u674e\u601d\u5357 \u201c\u4e49\u4e0d\u5bb9\u8f9e\u201d\uff0c\u53ea\u597d\u4eb2\u81ea\u4e0b\u573a\u4e86\u3002 \u3000\u3000\u4e89\u593a\u76df\u4e3b\u7684\u4e00\u65b9\u4eb2\u81ea\u4e0b\u573a\uff0c\u8fd9\u4e00\u573a\u5f53\u7136\u662f\u66f4\u4e3a\u5f15\u4eba\u6ce8\u76ee\u3002\u67f3\u6d1e\u5929\u7684\u5251\u6cd5\u662f\u5927\u5bb6\u89c1\u8fc7\u4e86 \u7684\uff0c\u674e\u601d\u5357\u7684\u5251\u6cd5\u5982\u4f55\uff0c\u5374\u53ea\u6709\u5b5f\u660e\u971e\u4e0e\u5c60\u51e4\u4e24\u4eba\u77e5\u9053\u3002 \u3000\u3000\u5927\u5bb6\u90fd\u60f3\u770b\u770b\u674e\u601d\u5357\u7684\u5251\u6cd5\u53c8\u6709\u4ec0\u4e48\u7cbe\u5999\u4e4b\u5904\uff0c\u8463\u5f00\u5c71\u3001\u9093\u98de\u8fd9\u4e00\u6591\u8001\u6210\u6301\u91cd\u7684\u524d\u8f88\uff0c \u8fd8\u4e0d\u514d\u6697\u6697\u4e3a\u674e\u601d\u5357\u62c5\u5fc3\uff0c\u201c\u5018\u82e5\u4ed6\u7684\u5251\u6cd5\u8fd8\u6bd4\u4e0d\u4e0a\u891a\u4e91\u5cf0\u7684\u8bdd\uff0c\u8fd9\u53ef\u662f\u5927\u5931\u5149\u5f69\u4e86\u3002\u201d \u3000\u3000\u5fc3\u5ff5\u672a\u5df2\uff0c\u53ea\u89c1\u674e\u601d\u5357\u5df2\u662f\u5e73\u5251\u5f53\u80f8\uff0c\u5411\u67f3\u6d1e\u5929\u8bf4\u4e86\u4e00\u4e2a\u201c\u8bf7\u201d\u5b57\u3002\u4ed6\u968f\u968f\u4fbf\u4fbf\u7acb\u4e86\u4e00 \u4e2a\u95e8\u6237\uff0c\u610f\u6001\u751a\u662f\u4ece\u5bb9\u3002 \u3000\u3000\u67f3\u6d1e\u5929\u5fc3\u60f3\uff1a\u201c\u6211\u4e14\u7ed9\u4ed6\u4e00\u4e2a\u4e0b\u9a6c\u5a01\uff01\u201d\u5f53\u4e0b\u8bf4\u58f0\u201c\u6709\u8bf7\uff01\u201d\u6beb\u4e0d\u5ba2\u6c14\u5730\u4e3e\u8d77\u957f\u5251\uff0c\u95ea \u7535\u822c\u7684\u4fbf\u5411\u674e\u601d\u5357\u523a\u53bb\uff0c\u8fd9\u4e00\u5251\u6765\u5f97\u51cc\u5389\u4e4b\u6781\uff0c\u800c\u4e14\u662f\u811a\u8e0f\u4e2d\u5bab\uff0c\u5e73\u80f8\u523a\u5230\u3002\u6b66\u5b66\u6709\u4e91\uff1a \u201c\u5200\u8d70\u767d\uff0c\u5251\u8d70\u9ed1\u201d\uff0c\u5373\u662f\u8bf4\u5251\u52bf\u6765\u53d6\u7684\u591a\u662f\u504f\u950b\u3002\u800c\u4eca\u67f3\u6d1e\u5929\u89c1\u9762\u7b2c\u4e00\u62db\u5c31\u4ece\u6b63\u9762\u653b\u6765\uff0c \u4e0d\u4f9d\u5251\u672f\u7684\u5e38\u7406\uff0c\u8fd9\u663e\u7136\u662f\u5b58\u5fc3\u8511\u89c6\u5bf9\u65b9\u7684\uff01 \u3000\u3000\u5b5f\u660e\u971e\u770b\u5f97\u6709\u6c14\uff0c\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4f60\u7684\u5251\u6cd5\u7eb5\u7136\u9ad8\u660e\uff0c\u4e5f\u4e0d\u8be5\u5982\u6b64\u65e0\u793c\uff01 \u54fc\uff0c\u4f55\u51b5\u4f60\u7684\u5251\u6cd5\u4e5f\u4e0d\u89c1\u5f97\u5c31\u4e00\u5b9a\u80dc\u5f97\u4e86\u5357\u54e5\uff01\u201d \u3000\u3000\u5176\u5b9e\u67f3\u6d1e\u5929\u5012\u4e0d\u662f\u6545\u610f\u5bf9\u674e\u601d\u5357\u65e0\u793c\u7684\uff0c\u4ed6\u53ea\u662f\u5b58\u5fc3\u8981\u6fc0\u6012\u674e\u601d\u5357\u3002\u4ed6\u662f\u6b66\u5b66\u7684\u5927\u540d \u5bb6\uff0c\u6df1\u77e5\u80dc\u8d1f\u7684\u8bc0\u7a8d\u3002\u9ad8\u624b\u6bd4\u62fc\uff0c\u5018\u82e5\u6709\u4e00\u65b9\u6c14\u8e81\u5fc3\u6d6e\uff0c\u5373\u4f7f\u4ed6\u7684\u672c\u9886\u8fd8\u8981\u80dc\u8fc7\u5bf9\u65b9\uff0c\u7ed3\u679c \u4e5f\u662f\u5fc5\u8d25\u65e0\u7591\u3002 \u3000\u3000\u65c1\u89c2\u8bf8\u4eba\u90fd\u4e3a\u674e\u601d\u5357\u6124\u6124\u4e0d\u5e73\uff0c\u674e\u601d\u5357\u5374\u662f\u795e\u8272\u5982\u5e38\uff0c\u6beb\u4e0d\u52a8\u6012\u3002\u4f46\u89c1\u4ed6\u5140\u7acb\u5982\u5c71\uff0c\u7eb9 \u4e1d\u4e0d\u52a8\uff0c\u5f85\u5230\u67f3\u6d1e\u5929\u7684\u5251\u5c16\u582a\u582a\u5c31\u8981\u523a\u5230\u4ed6\u7684\u80f8\u53e3\u7684\u65f6\u5019\uff0c\u8fd9\u624d\u9661\u7136\u95f4\u628a\u5251\u4e00\u7ffb\uff0c\u4e00\u62db\u201c\u91d1 \u9e4f\u5c55\u7fc5\u201d\uff0c\u659c\u524a\u51fa\u53bb\uff01 \u3000\u3000\u8fd9\u4e00\u62db\u62ff\u634f\u65f6\u5019\uff0c\u5999\u5230\u6beb\u98a0\uff0c\u67f3\u6d1e\u5929\u7684\u5251\u62db\u5df2\u662f\u7a0d\u5acc\u7528\u8001\uff0c\u800c\u674e\u601d\u5357\u5219\u662f\u6625\u4e91\u521d\u5c55\uff0c\u9510 \u6c14\u65b9\u5f20\u3002\u8fd9\u6b63\u5408\u4e4e\u5175\u6cd5\u4e0a\u201c\u907f\u5176\u671d\u8e2a\uff0c\u51fb\u5176\u66ae\u5f52\u201d\u7684\u9053\u7406\uff0c\u65c1\u89c2\u7684\u5251\u672f\u884c\u5bb6\u90fd\u4e0d\u7981\u559d\u8d77\u5f69 \u6765\uff01 \u3000\u3000\u67f3\u6d1e\u5929\u5fc3\u5934\u4e00\u51db\uff0c\u8fde\u5fd9\u53d8\u5f0f\u6536\u62db\u3002\u5e78\u800c\u4ed6\u89c1\u673a\u5f97\u5feb\uff0c\u5426\u5219\u8fd9\u4e00\u6761\u624b\u81c2\u5c31\u7b49\u4e8e\u9001\u4e0a\u53bb\u7ed9\u5bf9 \u65b9\u780d\u4e86\u3002\u4ed6\u8fd9\u4e00\u62db\u53d8\u5f0f\uff0c\u5b88\u4e2d\u5e26\u653b\uff0c\u521a\u67d4\u4e95\u6d4e\uff0c\u4ee4\u674e\u601d\u5357\u4e0d\u80fd\u4e58\u80dc\u8ffd\u51fb\u3002\u867d\u7136\u662f\u7a0d\u5c45\u540e\u624b\uff0c \u5374\u4e5f\u6beb\u65e0\u7834\u7efd\u53ef\u5bfb\uff0c\u59d4\u5b9e\u7b97\u5f97\u662f\u4e00\u62db\u6781\u9ad8\u660e\u7684\u5251\u6cd5\u3002\u4f46\u7fa4\u96c4\u607c\u4ed6\u9a84\u72c2\uff0c\u5982\u662f\u65e0\u4eba\u7ed9\u4ed6\u559d\u5f69\u3002 \u3000\u3000\u674e\u601d\u5357\u8d5e\u4e86\u4e00\u4e2a\u201c\u597d\u201d\u5b57\uff0c\u8ddf\u7740\u4e5f\u7acb\u5373\u6536\u62db\u53d8\u5f0f\uff0c\u5e76\u4e0d\u8d2a\u653b\u3002\u67f3\u6d1e\u5929\u8bf1\u654c\u4e4b\u8ba1\u4e0d\u6210\uff0c\u5fc3 \u91cc\u5bf9\u674e\u601d\u5357\u4e5f\u662f\u597d\u751f\u4f69\u670d\uff1a\u201c\u8fd9\u4eba\u5f53\u771f\u662f\u4e0d\u53ef\u5c0f\u89d1\uff0c\u5251\u672f\u9ad8\u660e\u8fd8\u5728\u5176\u6b21\uff0c\u8fd9\u4efd\u6db5\u517b\u7684\u529f\u592b\u66f4 \u662f\u96be\u80fd\uff01\u201d \u3000\u3000\u53cc\u65b9\u8bd5\u4e86\u4e00\u62db\u4e4b\u540e\uff0c\u5f7c\u6b64\u90fd\u662f\u4e0d\u6562\u7a0d\u6709\u5927\u610f\u3002\u5f53\u4e0b\u5c31\u8ba4\u771f\u5730\u8f83\u91cf\u8d77\u6765\u3002 \u3000\u3000\u7fa4\u96c4\u89c1\u674e\u601d\u5357\u7684\u7b2c\u4e00\u62db\u5c31\u4f7f\u5f97\u90a3\u6837\u8001\u8fa3\uff0c\u5927\u5bb6\u90fd\u4ee5\u4e3a\u4ed6\u5fc5\u6709\u7cbe\u5f69\u7684\u540e\u7740\u6e90\u6e90\u800c\u6765\uff0c\u4e0d\u6599 \u770b\u4e86\u5341\u51e0\u62db\uff0c\u53ea\u89c1\u674e\u601d\u5357\u4f7f\u7684\u90fd\u662f\u4e00\u4e9b\u666e\u901a\u7684\u62db\u6570\uff0c\u5e76\u65e0\u7279\u5f02\u4e4b\u5904\u3002 \u3000\u3000\u8463\u5f00\u5c71\u6084\u58f0\u8bf4\u9053\uff1a\u201c\u8fbe\u6469\u5251\u6cd5\u600e\u7684\u5982\u6b64\u5e73\u89e3\uff0c\u8fd9\u771f\u7684\u662f\u8fbe\u6469\u5251\u6cd5\u5417\uff1f\u201d\u8463\u5f00\u5c71\u4ee5\u5927\u6454\u4ffe \u624b\u9a70\u8a89\u6c5f\u6e56\uff0c\u5bf9\u5251\u6cd5\u5374\u5e76\u4e0d\u662f\u5341\u5206\u5728\u884c\uff0c\u6545\u6b64\u5077\u5077\u5730\u5411\u5b5f\u660e\u971e\u8bf7\u6559\u3002 \u3000\u3000\u5b5f\u660e\u971e\u7b11\u9053\uff1a\u201c\u8fd9\u5f53\u7136\u662f\u5982\u5047\u5305\u6362\u7684\u8fbe\u6469\u5251\u6cd5\uff0c\u54c8\uff0c\u6211\u8fd8\u5f53\u771f\u60f3\u4e0d\u5230\u674e\u601d\u5357\u4f7f\u5f97\u5982\u6b64\u9ad8 \u660e\u5462\u4e5f\u96be\u4e3a\u4e86\u4ed6\uff0c\u4e0d\u77e5\u662f\u600e\u4e48\u7ec3\u7684\uff01\u201d \u3000\u3000\u8463\u5f00\u5c71\u4f3c\u4fe1\u4e0d\u4fe1\u7684\u795e\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u4f55\u4ee5\u6211\u4e0d\u89c9\u5f97\u6709\u4f55\u7279\u5f02\u4e4b\u5904\uff1f\u4f60\u770b\uff0c\u67f3\u6d1e\u5929\u7684\u5251\u6cd5\u4f7f \u5f97\u4f55\u7b49\u8f7b\u7075\u7fd4\u52a8\uff0c\u4ed6\u7684\u5251\u6cd5\u5374\u4f3c\u4e4e\u6709\u70b9\u6da9\u6ede\u4e0d\u8212\uff1f\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u6211\u7239\u7239\u8bf4\uff0c\u6700\u9ad8\u660e\u7684\u5251\u6cd5\u662f\u4ee5\u2018\u62d9\u2019\u80dc\u2018\u5de7\u2019\u3002\u7075\u5de7\u5bb9\u6613\uff0c\u8981\u8fbe\u5230\u2018\u8fd4\u5657 \u5f52\u771f\u2019\u7684\u2018\u62d9\u2019\u7684\u5883\u754c\u8fd9\u5c31\u96be\u5f97\u591a\u4e86\u3002\u4ee5\u674e\u601d\u5357\u73b0\u5728\u7684\u9053\u8be3\u800c\u8bba\uff0c\u8fd9\u8fbe\u6469\u5251\u6cd5\u8db3\u6709\u4e8c\u5341\u5e74\u4ee5 \u4e0a\u7684\u529f\u529b\uff01\u4ed6\u4eca\u5e74\u4e5f\u4e0d\u8fc7\u624d\u4e8c\u5341\u591a\u5c81\uff0c\u6240\u4ee5\u6211\u8bf4\u8fd9\u771f\u662f\u96be\u4e3a\u4e86\u4ed6\uff0c\u4e0d\u77e5\u662f\u600e\u4e48\u7ec3\u7684\uff1f\u201d \u3000\u3000\u8463\u5f00\u5c71\u8d77\u521d\u53ea\u662f\u534a\u4fe1\u534a\u7591\uff0c\u4f46\u770b\u4e0b\u53bb\u5374\u4e0d\u7531\u5f97\u4ed6\u4e0d\u76f8\u4fe1\u4e86\u3002\u53ea\u89c1\u674e\u601d\u5357\u4f7f\u51fa\u5927\u5f00\u5927\u9608\u7684 \u5251\u6cd5\uff0c\u6beb\u65e0\u82b1\u5de7\u53ef\u8a00\uff0c\u4f46\u4e0d\u8bba\u67f3\u6d1e\u592d\u4f7f\u51fa\u5982\u4f55\u5947\u8be1\u83ab\u6d4b\u7684\u5251\u62db\uff0c\u5374\u90fd\u7ed9\u674e\u601d\u5357\u4e00\u4e00\u6321\u4e86\u56de \u53bb\u3002 \u3000\u3000\u67f3\u6d1e\u5929\u6697\u775b\u5403\u60ca\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6211\u7834\u4e0d\u4e86\u4ed6\u7684\u9632\u5fa1\uff0c\u4e45\u6218\u4e0b\u53bb\uff0c\u5b9a\u5fc5\u5403\u4e8f\u3002\u201d\u5f53\u4e0b\u786c\u7740 \u5934\u5c16\uff0c\u91c7\u53d6\u901f\u6218\u901f\u51b3\u7684\u6218\u672f\u3002 \u3000\u3000\u4e03\u5341\u4e8c\u624b\u8fde\u73af\u593a\u547d\u5251\u6cd5\u7aef\u7684\u662f\u72e0\u8fa3\u975e\u51e1\uff0c\u653b\u52bf\u5c55\u5f00\uff0c\u5b9b\u5982\u957f\u6c5f\u5927\u6cb3\u6eda\u6eda\u800c\u4e0a\uff0c\u770b\u5f97\u4f17\u4eba \u76ee\u7729\u795e\u8ff7\u3002 \u3000\u3000\u8463\u5f00\u5c71\u4e0d\u89c9\u53c8\u62c5\u5fc3\u8d77\u6765\uff0c\u8bf4\u9053\uff0c\u201c\u674e\u516c\u5b50\u6b65\u6b65\u540e\u9000\uff0c\u6050\u6015\u8981\u7cdf\uff01\u201d\u5b5f\u660e\u971e\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4 \u9053\uff1a\u201c\u65e0\u59a8\uff01\u201d\u6b64\u65f6\u5979\u4ea6\u662f\u76ee\u4e0d\u6687\u63a5\uff0c\u751f\u6015\u9519\u8fc7\u4e00\u62db\u7cbe\u5999\u7684\u5251\u62db\uff0c\u65e0\u6687\u7ed9\u8463\u5f00\u5c71\u591a\u4f5c\u89e3\u91ca \u4e86\u3002 \u3000\u3000\u8868\u9762\u770b\u6765\u662f\u67f3\u6d1e\u5929\u5927\u5360\u653b\u52bf\uff0c\u4f46\u5728\u6b66\u5b66\u540d\u5bb6\u7684\u773c\u91cc\uff0c\u5374\u5df2\u770b\u5f97\u51fa\u662f\u674e\u601d\u5357\u80dc\u5238\u7a33\u64cd\u3002\u539f \u6765\u4e0a\u4e58\u5251\u672f\u7684\u539f\u7406\u662f\u5728\u4e0d\u53ef\u80dc\u4e4b\u4e2d\u6c42\u80dc\uff0c\u67f3\u6d1e\u5929\u91c7\u53d6\u95ea\u7535\u653b\u51fb\u7684\u6218\u672f\uff0c\u5c31\u662f\u60f3\u8981\u8feb\u4f7f\u674e\u601d\u5357 \u9732\u51fa\u7834\u7efd\u3002 \u3000\u3000\u54ea\u77e5\u674e\u601d\u5357\u867d\u7136\u662f\u6b65\u6b65\u540e\u9000\uff0c\u4f46\u6bcf\u9000\u4e00\u6b65\uff0c\u5c31\u6d88\u53bb\u4e86\u67f3\u6d1e\u5929\u7684\u4e00\u5206\u653b\u52bf\u3002\u67f3\u6d1e\u5929\u5c31\u662f\u627e \u4e0d\u51fa\u4ed6\u7684\u7834\u7efd\uff0c\u53cd\u800c\u6210\u4e86\u5f3a\u5f29\u4e4b\u672a\uff0c\u6e10\u6e10\u6709\u96be\u4ee5\u4e3a\u7ee7\u4e4b\u611f\u3002 \u3000\u3000\u518d\u8fc7\u7247\u523b\uff0c\u53ea\u89c1\u67f3\u6d1e\u5929\u7684\u62db\u6570\u8d8a\u51fa\u8d8a\u5feb\uff0c\u524d\u62db\u672a\u51fa\uff0c\u540e\u62db\u5373\u53d1\u3002\u674e\u601d\u5357\u5374\u662f\u8d8a\u6765\u8d8a\u6162\uff0c \u5251\u5c16\u4e0a\u5c31\u597d\u4f3c\u60ac\u7740\u4e00\u58f0\u77f3\u5934\u4f3c\u7684\uff0c\u4e1c\u4e00\u6307\uff0c\u897f\u4e00\u5212\uff0c\u8fdf\u7f13\u975e\u5e38\u3002\u4f46\u8bf4\u4e5f\u5947\u602a\uff0c\u53cd\u800c\u662f\u67f3\u6d1e\u5929 \u5927\u6c57\u6dcb\u6f13\uff0c\u9762\u8272\u8d8a\u6765\u8d8a\u89c1\u6c89\u91cd\u3002\u674e\u601d\u5357\u5012\u662f\u4ece\u5bb9\u4e0d\u8feb\uff0c\u4e3e\u91cd\u82e5\u8f7b\u3002 \u3000\u3000\u770b\u81f3\u6b64\u65f6\uff0c\u5b5f\u660e\u971e\u65b9\u59cb\u677e\u4e86\u53e3\u6c14\uff0c\u6084\u6084\u5bf9\u8463\u5f00\u5c71\u8bf4\u9053\uff1a\u201c\u67f3\u6d1e\u5929\u4e45\u6218\u4e0d\u4e0b\uff0c\u81ea\u77e5\u5fc5\u8d25\uff0c \u770b\u6765\u4ed6\u662f\u8981\u4f5c\u9669\u4e2d\u6c42\u80dc\u7684\u6253\u7b97\u4e86\u3002\u4f46\u5982\u6b64\u4e00\u6765\uff0c\u4ed6\u53ef\u5c31\u8981\u8d25\u5f97\u66f4\u5feb\u4e86\uff01\u201d \u3000\u3000\u8bdd\u72b9\u672a\u4e86\uff0c\u53ea\u89c1\u67f3\u6d1e\u5929\u679c\u7136\u4e00\u8dc3\u800c\u8d77\uff0c\u4f7f\u51fa\u9669\u62db\uff0c\u5de6\u4e00\u62db\u201c\u4e07\u91cc\u98de\u971c\u201d\uff0c\u53f3\u4e00\u62db\u201c\u5e72\u5c71 \u843d\u6728\u201d\uff0c\u4e24\u9053\u5251\u5149\u4ea4\u53c9\u7a7f\u63d2\uff0c\u674e\u601d\u5357\u597d\u50cf\u5df2\u5728\u4ed6\u7684\u5251\u5149\u7b3c\u7f69\u4e4b\u4e0b\uff0c\u773c\u770b\u5c31\u8981\u7ed9\u4ed6\u5728\u8eab\u4e0a\u6420\u4e2a \u900f\u660e\u7684\u7a9f\u7abf\uff01 \u3000\u3000\u4f17\u4eba\u60ca\u547c\u58f0\u4e2d\uff0c\u53ea\u89c1\u674e\u601d\u5357\u970d\u5730\u4e00\u4e2a\u201c\u51e4\u70b9\u5934\u201d\uff0c\u5012\u7eb5\u4e08\u5f00\u5916\uff0c\u5934\u4e0a\u6234\u7684\u4e00\u9876\u76ae\u5e3d\u8dcc\u4e0b \u5730\u6765\u3002\u6df3\u4e8e\u5468\u8fd9\u8fb9\u7684\u4eba\u5927\u559c\u82e5\u72c2\uff0c\u8f70\u7136\u53eb\u9053\uff1a\u201c\u67f3\u8235\u4e3b\u80dc\u4e86\uff01\u201d \u3000\u3000\u4f46\u5728\u8fd9\u4e9b\u4eba\u72c2\u547c\u602a\u53eb\u58f0\u4e2d\uff0c\u5374\u542c\u5f97\u4e00\u4e2a\u6e05\u8106\u7684\u58f0\u97f3\u53eb\u9053\uff1a\u201c\u674e\u516c\u5b50\u80dc\u4e86\uff01\u201d\u8fd9\u662f\u5b5f\u660e\u971e \u7684\u53eb\u58f0\u3002 \u3000\u3000\u67f3\u6d1e\u5929\u4e5f\u89c9\u5f97\u4f3c\u4e4e\u80dc\u5f97\u4fa5\u5e78\uff0c\u542c\u5f97\u5b5f\u660e\u971e\u8fd9\u4e48\u8bf4\uff0c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u8fde\u5fd9\u4fef\u9996\u4e00\u77a7\uff0c\u8fd9\u4e00\u77a7\u767b \u65f6\u4ee4\u4ed6\u9762\u7ea2\u8fc7\u8033\u3002 \u3000\u3000\u539f\u6765\u5728\u4ed6\u7684\u80f8\u8863\u4e0a\u6709\u4e09\u4e2a\u94dc\u94b1\u822c\u5927\u5c0f\u7684\u5706\u6d1e\uff0c\u4e0d\u95ee\u53ef\u77e5\u5f53\u7136\u662f\u674e\u601d\u752b\u7684\u6770\u4f5c\u4e86\u3002 \u3000\u3000\u8fd9\u5f53\u771f\u662f\u201c\u65c1\u89c2\u8005\u6e05\uff0c\u5f53\u5c40\u8005\u8ff7\u201d\uff0c\u67f3\u6d1e\u592d\u8fd9\u6837\u7684\u5251\u672f\u5927\u540d\u5bb6\u4e5f\u8981\u5f85\u65c1\u4eba\u559d\u7834\u4e4b\u540e\uff0c\u624d \u77e5\u9053\u5bf9\u65b9\u5df2\u7ecf\u5728\u4ed6\u7684\u8eab\u4e0a\u7559\u4e0b\u201c\u6807\u8bb0\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u5f85\u4ed6\u53d1\u89c9\u540e\uff0c\u8fd9\u624d\u5fae\u5fae\u4e00\u7b11\uff0c\u5c06\u624b\u638c\u644a\u5f00\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u5f1f\u4e0d\u614e\uff0c\u635f\u4e86\u67f3\u5144\u8863\u88f3\uff0c \u8fd8\u8bf7\u89c1\u8c05\u3002\u201d\u638c\u4e0a\u6709\u4e09\u7247\u5706\u5f62\u7834\u5e03\u3002 \u3000\u3000\u67f3\u6d1e\u5927\u867d\u7136\u4e5f\u6311\u843d\u4e86\u674e\u601d\u5357\u5934\u6234\u7684\u76ae\u5e3d\uff0c\u4f46\u6311\u843d\u5e3d\u5b50\u5bb9\u6613\uff0c\u5728\u5bf9\u65b9\u80f8\u53e3\u90e8\u4f4d\uff0c\u5272\u5f00\u4ed6\u7684 \u8863\u88f3\uff0c\u800c\u662f\u6070\u6070\u5f62\u6210\u540c\u6837\u5927\u5c0f\u7684\u5706\u5b54\uff0c\u8fd9\u6bd4\u6311\u843d\u5e3d\u5b50\u4f55\u6b62\u8270\u96be\u5341\u500d\uff01 \u3000\u3000\u8fd8\u6709\u4e00\u5c42\uff0c\u674e\u601d\u5357\u662f\u7528\u201c\u51e4\u70b9\u5934\u201d\u7684\u62db\u6570\u907f\u5f00\u67f3\u6d1e\u5929\u7684\u5251\u62db\u7684\uff0c\u867d\u7136\u95ea\u907f\u5f97\u8fd8\u4e0d\u7b97\u5341\u5206 \u6210\u529f\uff0c\u5374\u603b\u662f\u907f\u5f00\u4e86\u3002\u9ad8\u624b\u6bd4\u6597\uff0c\u76f8\u5dee\u4e0d\u8fc7\u6beb\u5398\uff0c\u4ea6\u5373\u662f\u8bf4\u67f3\u6d1e\u5929\u53ea\u80fd\u6311\u843d\u4ed6\u7684\u5e3d\u5b50\uff0c\u5374\u65e0 \u6cd5\u4f24\u53ca\u4ed6\u7684\u8eab\u4f53\u3002\u56e0\u6b64\u5373\u4f7f\u5355\u8bba\u8fd9\u62db\uff0c\u674e\u601d\u5357\u4e5f\u4e0d\u8fc7\u8f93\u4e86\u534a\u62db\u800c\u5df2\uff0c\u6027\u547d\u7684\u5371\u9669\u5219\u662f\u6ca1\u6709 \u7684\u3002 \u3000\u3000\u4f46\u674e\u601d\u5357\u523a\u4ed6\u7684\u90a3\u4e00\u5251\u53ef\u5c31\u5927\u5927\u4e0d\u540c\u4e86\uff0c\u4ed6\u6839\u672c\u5c31\u6ca1\u53d1\u89c9\uff0c\u901e\u8bba\u907f\u5f00\uff1f\u5982\u679c\u674e\u601d\u5357\u7acb\u5fc3 \u8981\u53d6\u4ed6\u6027\u547d\u7684\u8bdd\uff0c\u5251\u5c16\u53ea\u8981\u523a\u6df1\u534a\u5bf8\uff0c\u4ed6\u7684\u80f8\u53e3\u5df2\u662f\u5f00\u4e86\u4e09\u4e2a\u7a9f\u7abf\u3002\u56e0\u6b64\u67f3\u6d1e\u5929\u4e0d\u4ec5\u662f\u8f93\u4e86 \u4e00\u62db\uff0c\u6027\u547d\u5c24\u64cd\u4e4b\u522b\u4eba\u4e4b\u624b\u3002 \u3000\u3000\u67f3\u6d1e\u5929\u53c8\u662f\u7f9e\u6127\uff0c\u53c8\u662f\u611f\u6fc0\u3002\u4ed6\u662f\u4e2a\u5251\u672f\u7684\u5927\u540d\u5bb6\uff0c\u5728\u4f17\u76ee\u77aa\u77aa\u4e4b\u4e0b\uff0c\u81ea\u662f\u4e0d\u80fd\u62b5\u8d56\u3002 \u4ed6\u4e16\u4e0d\u60f3\u62b5\u8d56\uff0c\u5f53\u4e0b\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u5929\u5916\u6709\u5929\uff0c\u4eba\u5916\u6709\u4eba\uff0c\u8fd9\u8bdd\u5f53\u771f\u8bf4\u5f97\u4e0d\u9519\u3002\u674e\u5144\uff0c \u4f60\u7684\u5251\u672f\u6bd4\u6211\u9ad8\u660e\u5341\u500d\u3002\u5c0f\u5f1f\u8fd9\u4e00\u573a\u662f\u8f93\u5f97\u53e3\u670d\u5fc3\u670d\uff01\u201d \u3000\u3000\u67f3\u6d1e\u5927\u63d2\u5251\u5f52\u9798\uff0c\u8d70\u56de\u53bb\u5411\u5d14\u9547\u5c71\u82e6\u7b11\u8bf4\u9053\uff1a\u201c\u5d14\u5144\uff0c\u4f60\u4e5f\u5df2\u7ecf\u62a5\u7b54\u4e86\u6df3\u4e8e\u5be8\u4e3b\u4e86\uff0c\u54b1 \u4eec\u8fd8\u662f\u8d70\u5427\u3002\u201d \u3000\u3000\u6b64\u8a00\u6b63\u5408\u5d14\u9547\u5c71\u5fc3\u610f\uff0c\u5f53\u4e0b\u4fbf\u5411\u6df3\u4e8e\u5468\u9053\uff01\u201d\u6df3\u4e8e\u5be8\u4e3b\uff0c\u6211\u4eec\u4e8c\u4eba\u7ed9\u4f60\u51fa\u8d5b\u4e24\u573a\uff0c\u5dee\u5e78 \u65e0\u529f\u65e0\u8fc7\uff0c\u544a\u8f9e\u4e86\u3002\u201d\u5d14\u9547\u5c71\u80dc\u4e86\u8463\u5f00\u5c71\uff0c\u8d25\u7ed9\u891a\u4e91\u5cf0\uff1b\u67f3\u6d1e\u5929\u5219\u662f\u80dc\u4e86\u891a\u4e91\u5cf0\uff0c\u8d25\u7ed9\u674e\u601d \u5357\u3002\u5404\u81ea\u80dc\u4e00\u8d1f\u4e00\uff0c\u6545\u6b64\u8bf4\u662f\u201c\u65e0\u529f\u65e0\u8fc7\u201d\u3002\u6df3\u4e8e\u5468\u77e5\u9053\u4ed6\u4eec\u4e8c\u4eba\u4e0d\u4f1a\u4e3a\u4ed6\u6240\u662f\uff0c\u5f97\u4ed6\u4eec\u6273 \u56de\u4e24\u573a\uff0c\u5df2\u662f\u5fc3\u6ee1\u610f\u8db3\uff0c\u5f53\u4e0b\u5ba2\u6c14\u51e0\u53e5\uff0c\u4e5f\u5c31\u4e0d\u518d\u633d\u7559\u3002\u5d14\u9547\u5c71\u7684\u5e08\u5f1f\u5df2\u7ecf\u9a73\u597d\u65ad\u9aa8\uff0c\u4f24\u52bf \u65e0\u788d\uff0c\u8ddf\u4e86\u4ed6\u4eec\u4e8c\u4eba\u4e00\u540c\u4e0b\u5c71\u3002 \u3000\u3000\u674e\u601d\u5357\u80dc\u4e86\u67f3\u6d1e\u5929\uff0c\u7fa4\u96c4\u7eb7\u7eb7\u5411\u4ed6\u9053\u8d3a\u3002\u674e\u601d\u5357\u9053\uff1a\u201c\u4fa5\u5e78\u83b7\u80dc\uff0c\u4f55\u6562\u4e91\u529f\uff1f\u201d\u201c\u4fa5 \u5e78\u201d\u662f\u5047\u7684\uff0c\u4f46\u4ed6\u80dc\u4e86\u8fd9\u573a\uff0c\u5374\u4e5f\u7684\u786e\u662f\u4e0d\u6562\u81ea\u6ee1\u3002\u60f3\u8d77\u521a\u624d\u7684\u60ca\u9669\u4e4b\u5904\uff0c\u5982\u679c\u95ea\u907f\u5f97\u7a0d\u6709 \u5dee\u9519\u7684\u8bdd\uff0c\u8111\u888b\u5c31\u8981\u7ed9\u67f3\u6d1e\u5927\u523a\u7a7f\uff0c\u4ea4\u624b\u4e4b\u65f6\u4e0d\u77e5\u5bb3\u6015\uff0c\u8fc7\u540e\u59cb\u65b9\u77e5\u60ca\uff0c\u674e\u601d\u5357\u4e5f\u4e0d\u7981\u51fa\u4e86 \u4e00\u8eab\u51b7\u6c57\u3002 \u3000\u3000\u6df3\u4e8e\u5468\u54fc\u4e86\u4e00\u58f0\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u5c1a\u672a\u7ec8\u573a\uff0c\u8981\u5f00\u5e86\u529f\u5bb4\u4f3c\u4e4e\u8fd8\u65e9\u4e00\u70b9\u5427\uff1f\u201d\u7fa4\u96c4\u4e89\u7740\u5411 \u674e\u601d\u5357\u9053\u8d3a\uff0c\u4ed6\u662f\u8d8a\u770b\u8d8a\u4e0d\u987a\u773c\uff0c\u5f53\u4e0b\u4fbf\u60f3\u4eb2\u81ea\u4e0b\u573a\uff0c\u5411\u674e\u601d\u5357\u6311\u6218\u3002\u4f46\u5c60\u9f99\u5374\u5df2\u5148\u4ed6\u800c \u51fa\u3002 \u3000\u3000\u4e0d\u8fc7\u5c60\u9f99\u5374\u4e0d\u662f\u5411\u674e\u601d\u5357\u6311\u6218\uff0c\u800c\u662f\u5411\u5b5f\u660e\u971e\u6311\u6218\u3002 \u3000\u3000\u5c60\u9f99\u81ea\u5fd6\uff0c\u4eca\u65e5\u65e0\u8bba\u5982\u4f55\u662f\u8981\u6597\u4e00\u573a\u7684\uff0c\u4e0d\u662f\u6597\u5b5f\u660e\u971e\uff0c\u5c31\u662f\u6597\u674e\u601d\u5357\uff0c\u4ed6\u5f97\u4e86\u6df3\u4e8e\u5468 \u7684\u6307\u6559\uff0c\u6597\u5b5f\u660e\u971e\u867d\u65e0\u5fc5\u80dc\u628a\u63e1\uff0c\u4f46\u4e5f\u4e0d\u662f\u6ca1\u6709\u53ef\u80dc\u4e4b\u673a\u3002\u56e0\u6b64\u4ed6\u60f3\u4e0e\u5176\u6597\u674e\u601d\u5357\uff0c\u4e0d\u5982\u6597 \u5b5f\u660e\u971e\u3002 \u3000\u3000\u674e\u601d\u5357\u4e00\u89c1\u5c60\u9f99\u4e0b\u573a\uff0c\u4e0d\u7531\u5f97\u5fc3\u5934\u706b\u8d77\uff0c\u5927\u8e0f\u6b65\u5c31\u8d70\u51fa\u53bb\u3002\u5c60\u9f99\u7b11\u9053\uff1a\u201c\u674e\u516c\u5b50\uff0c\u4f60\u521a \u521a\u6597\u4e86\u4e00\u573a\uff0c\u8fd8\u662f\u6b47\u4e00\u6b47\u5427\u3002\u5b5f\u59d1\u5a18\uff0c\u4f60\u5df2\u7ecf\u6b47\u8fc7\u4e86\uff0c\u6211\u60f3\u5411\u4f60\u8ba8\u6559\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u5f88\u597d\uff01\u201d\u8d70\u51fa\u573a\u6765\uff0c\u60f3\u53eb\u674e\u601d\u5357\u56de\u53bb\uff0c\u4e0d\u6599\u674e\u601d\u5357\u5374\u62e6\u7740\u5979\uff0c\u8bf4\u9053\uff1a\u201c\u7167 \u6bd4\u6b66\u7684\u89c4\u77e9\uff0c\u6211\u4f3c\u4e4e\u8fd8\u53ef\u4ee5\u518d\u6253\u4e00\u573a\u3002\u5c60\u9f99\uff0c\u4f60\u80dc\u4e86\u6211\uff0c\u518d\u5411\u5b5f\u59d1\u5a18\u8ba8\u6559\u4e5f\u8fd8\u4e0d\u8fdf\uff01\u5f53\u7136\uff0c \u5982\u679c\u4f60\u662f\u6015\u4e86\u6211\uff0c\u4e0d\u6562\u4e0e\u6211\u52a8\u624b\uff0c\u90a3\u53c8\u53e6\u5f53\u522b\u8bba\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u4e00\u76f4\u662f\u6587\u8d28\u5f6c\u5f6c\uff0c\u5bf9\u4eba\u751a\u6709\u793c\u8c8c\u7684\uff0c\u67f3\u6d1e\u5929\u521a\u624d\u51e0\u6b21\u4e09\u756a\u8981\u6fc0\u6012\u4ed6\uff0c\u4ed6\u4e5f\u662f\u6beb\u4e0d \u52a8\u6c14\u3002\u6b64\u9645\u4ed6\u5ffd\u7136\u4e00\u6539\u5e38\u6001\uff0c\u58f0\u8272\u4ff1\u5389\u7684\u5b9a\u8981\u5bf9\u4ed8\u5c60\u9f99\uff0c\u7fa4\u96c4\u4e0d\u77e5\u4ed6\u4eec\u4eba\u4e4b\u95f4\u7684\u201c\u8fc7\u8282\u201d\uff0c \u90fd\u662f\u9887\u611f\u8be7\u5f02\u3002 \u3000\u3000\u5b5f\u660e\u971e\u53ea\u9053\u6768\u5a49\u65e9\u5df2\u6b7b\u4e86\uff0c\u5e76\u4e0d\u77e5\u9053\u5c60\u9f99\u66fe\u7ecf\u6709\u8fc7\u6b3a\u9a97\u6768\u5a49\u7684\u8fd9\u6869\u4e8b\u60c5\u3002\u6b64\u65f6\u5979\u89c1\u674e\u601d \u5357\u773c\u775b\u597d\u4f3c\u5c31\u8981\u55b7\u51fa\u706b\u6765\uff0c\u5fc3\u4e2d\u4e5f\u662f\u4e0d\u7981\u731c\u7591\u4e0d\u5b9a\uff1a\u201c\u4e3a\u4ec0\u4e48\u4ed6\u8fd9\u6837\u6068\u5c60\u9f99\u5462\uff1f\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u597d\u5427\uff0c\u8fd9\u4e00\u573a\u6211\u8ba9\u7ed9\u4f60\u3002\u4e0d\u8fc7\u2026\u2026\u201d\u674e\u601d\u5357\u9053\uff1a\u201c\u4e0d\u8fc7\u4ec0\u4e48\uff1f\u201d\u5b5f\u660e\u971e\u5fae \u5fbd\u4e00\u7b11\uff0c\u5728\u674e\u601d\u5357\u8033\u8fb9\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u8bf7\u4f60\u770b\u5728\u5c60\u51e4\u9762\u4e0a\uff0c\u4e0d\u8981\u53d6\u4ed6\u6027\u547d\u3002\u201d\u674e\u601d\u5357\u610f \u4e71\u5fc3\u70e6\uff0c\u201c\u55ef\u201d\u4e86\u4e00\u58f0\uff0c\u4e0d\u8bf4\u7b54\u5e94\uff0c\u4e5f\u4e0d\u8bf4\u4e0d\u7b54\u5e94\u3002\u5979\u4e0e\u674e\u601d\u5357\u7684\u8033\u8bed\uff0c\u65c1\u4eba\u542c\u4e0d\u89c1\uff0c\u5c60\u9f99 \u662f\u542c\u5f97\u89c1\u7684\u3002 \u3000\u3000\u674e\u601d\u5357\u7684\u8bf4\u8bdd\u5df2\u7136\u8bf4\u5230\u8fd9\u4e2a\u5730\u6b65\uff0c\u5c60\u9f99\u867d\u7136\u5bb3\u6015\uff0c\u5374\u4e5f\u4e0d\u80fd\u9000\u7f29\u3002\u5b5f\u660e\u971e\u7684\u8a00\u8bed\uff0c\u66f4\u6fc0 \u8d77\u4e86\u4ed6\u7684\u6012\u6c14\uff0c\u4e00\u6012\u4e4b\u4e0b\uff0c\u786c\u7740\u5934\u76ae\u559d\u9053\uff1a\u201c\u4e0b\u573a\u65e0\u7236\u5b50\uff0c\u52a8\u624b\u4e0d\u7559\u60c5\u3002\u597d\u5427\uff0c\u674e\u601d\u5357\uff0c\u4f60 \u6709\u672c\u9886\u4f60\u5c3d\u7ba1\u6765\u53d6\u6211\u6027\u547d\uff01\u201d \u3000\u3000\u6df3\u4e8e\u5468\u597d\u4f3c\u663e\u51fa\u5f88\u4e0d\u8010\u70e6\u7684\u795e\u6c14\uff0c\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u5435\u4ec0\u4e48\uff0c\u4f60\u4eec\u8981\u62fc\u4e2a\u4f60\u6b7b\u6211\u6d3b\uff0c\u90a3\u5c31\u62fc \u597d\u4e86\uff01\u201d\u8868\u9762\u4f3c\u4e4e\u662f\u8d23\u5907\u5c60\u9f99\uff0c\u5176\u5b9e\u4e43\u662f\u63d0\u9192\u5c60\u9f99\u3002 \u3000\u3000\u5c60\u9f99\u970d\u7136\u4e00\u7701\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5927\u654c\u5f53\u524d\uff0c\u5207\u5fcc\u52a8\u6c14\u3002\u8fd9\u53ae\u521a\u521a\u7ecf\u8fc7\u4e00\u573a\u6076\u6597\uff0c\u6c14 \u529b\u81f3\u5c11\u8017\u4e86\u51e0\u5206\uff0c\u4ed6\u8fd8\u8981\u5411\u6211\u5927\u53d1\u813e\u6c14\uff0c\u8fd9\u6b63\u662f\u6c42\u4e4b\u4e0d\u5f97\uff01\u597d\uff0c\u4e14\u7b49\u6211\u6c89\u7740\u5730\u5e94\u4ed8\u4ed6\uff0c\u6709\u673a \u4f1a\u518d\u6fc0\u4ed6\u53d1\u6012\u3002\u201d \u3000\u3000\u4e8e\u662f\u5c60\u9f99\u6545\u610f\u4fa7\u76ee\u659c\u77a7\uff0c\u4f5c\u51fa\u4e00\u526f\u8f7b\u654c\u7684\u795e\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u770b\u5728\u4f60\u521a\u521a\u6597\u4e00\u573a\u7684\u4efd\u4e0a\uff0c\u6211 \u8ba9\u4f60\u5148\u51fa\u62db\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u6012\u9053\uff1a\u201c\u6211\u4f55\u5fc5\u4f60\u8ba9\uff1f\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u5c60\u9f99\u7a81\u7136\u5c31\u662f\u4e00\u62db\u201c\u7384\u9e1f\u5212\u6c99\u201d\uff0c\u5411\u674e\u601d\u5357 \u5f53\u80f8\u5212\u53bb\u3002\u51fa\u5251\u4e4b\u540e\uff0c\u8fd9\u624d\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u8981\u6211\u8ba9\uff0c\u90a3\u6211\u5c31\u4e0d\u5ba2\u6c14\u4e86\uff01\u201d \u3000\u3000\u65c1\u89c2\u8bf8\u4eba\u4ee5\u4e3a\u5c60\u9f99\u81f3\u5c11\u8fd8\u6709\u51e0\u53e5\u95e8\u9762\u8bdd\u9700\u8981\u4ea4\u4ee3\uff0c\u4e0d\u6599\u4ed6\u5728\u674e\u601d\u5357\u8bf4\u8bdd\u4e4b\u9645\uff0c\u7a81\u7136\u5c31\u52a8 \u8d77\u624b\u6765\u3002\u800c\u674e\u601d\u5357\u7684\u5251\u8fd8\u672a\u51fa\u9798\u3002\u7fa4\u96c4\u53c8\u60ca\u53c8\u6012\uff0c\u7eb7\u7eb7\u559d\u9a82\uff01 \u3000\u3000\u53ea\u8d1d\u767d\u5149\u4e00\u95ea\uff0c\u674e\u601d\u5357\u8eab\u79fb\u6b65\u6362\uff0c\u201c\u94db\u201d\u7684\u4e00\u58f0\uff0c\u5df2\u662f\u628a\u5c60\u9f99\u7684\u957f\u5251\u683c\u5f00\u3002\u4ed6\u95ea\u8eab\u3001\u62d4 \u5251\u3001\u8fd8\u51fb\uff0c\u4e09\u4e2a\u52a8\u4f5c\u4e00\u6c14\u5475\u6210\uff0c\u5feb\u5982\u95ea\u7535\uff0c\u59ff\u52bf\u7f8e\u5999\u4e4b\u6781\u3002\u7fa4\u96c4\u672c\u6765\u662f\u4e3a\u4ed6\u634f\u4e00\u628a\u6c57\uff0c\u4e89\u7740 \u5728\u9a82\u5c60\u9f99\u7684\u624b\u6bb5\u5351\u9119\u7684\uff0c\u6b64\u65f6\u4e00\u53d8\u800c\u4e3a\u5411\u674e\u601d\u5357\u5927\u58f0\u559d\u5f69\uff0c\u5012\u662f\u987e\u4e0d\u5f97\u518d\u9a82\u5c60\u9f99\u4e86\u3002 \u3000\u3000\u5c60\u9f99\u51b7\u7b11\u9053\uff1a\u201c\u4eca\u65e5\u662f\u5251\u5e95\u5224\u6b7b\u751f\uff0c\u96be\u9053\u8fd8\u8981\u8bb2\u4ec0\u4e48\u5c27\u821c\u7684\u63d6\u8ba9\u4e4b\u9053\u4e48\uff1f\u201d\u7b11\u58f0\u672a\u5df2\uff0c \u5530\u5530\u5530\u7684\u53c8\u662f\u8fde\u73af\u4e09\u5251\u3002\u4ed6\u7684\u672c\u9886\u4e5f\u5f53\u771f\u4e0d\u5f31\uff0c\u5728\u674e\u601d\u5357\u4ee5\u5982\u6b64\u7cbe\u5999\u7684\u5251\u672f\u89e3\u4e86\u4ed6\u7684\u5947\u88ad\u4e4b \u540e\uff0c\u5c45\u7136\u8fd8\u80fd\u591f\u8fde\u62a2\u5148\u624b\u3002 \u3000\u3000\u674e\u601d\u5357\u5927\u6012\uff0c\u5fc3\u9053\uff1a\u201c\u597d\u5440\uff0c\u4f60\u65e2\u7136\u8981\u62fc\u4e2a\u4f60\u6b7b\u6211\u6d3b\uff0c\u6211\u53ef\u4e5f\u987e\u4e0d\u4e86\u8fd9\u8bb8\u591a\u4e86\uff01\u201d\u53cc\u65b9 \u52a8\u4e86\u6740\u673a\uff0c\u5251\u62db\u8d8a\u6765\u8d8a\u89c1\u51f6\u9669\u3002\u674e\u601d\u5357\u4f7f\u4e86\u4e00\u62db\u201c\u674e\u4e86\u5c04\u77f3\u201d\uff0c\u523a\u4ed6\u624b\u8155\uff1b\u5c60\u9f99\u8fc5\u5373\u5c31\u8fd8\u4e86 \u4e00\u62db\u201c\u5b5f\u5fb7\u732e\u529b\u201d\uff0c\u53cd\u624b\u523a\u4ed6\u80c1\u4e0b\u7684\u201c\u6108\u6c14\u7a74\u201c\u3002\u9488\u950b\u76f8\u5bf9\uff0c\u65d7\u9f13\u76f8\u5f53\uff0c\u4e24\u4eba\u907f\u8fc7\u8fd9\u62db\u4e4b \u540e\uff0c\u90fd\u662f\u5404\u81ea\u53eb\u4e86\u4e00\u58f0\uff1a\u201c\u597d\u9669\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u66fe\u7ecf\u548c\u5c60\u9f99\u4ea4\u8fc7\u4e00\u6b21\u624b\uff0c\u81ea\u5fd6\u5927\u6709\u628a\u63e1\u53ef\u4ee5\u80dc\u4ed6\uff0c\u6545\u6b64\u4e0d\u514d\u7a0d\u7a0d\u6709\u70b9\u8f7b\u654c\uff0c\u6068\u4e0d \u5f97\u4e09\u62db\u4e24\u5f0f\u5c31\u201c\u6253\u53d1\u201d\u4e86\u4ed6\uff0c\u5373\u4f7f\u4e0d\u53d6\u4ed6\u7684\u6027\u547d\uff0c\u4e5f\u8981\u53eb\u4ed6\u51fa\u4e56\u9732\u4e11\u3001\u624d\u6d88\u80f8\u4e2d\u7684\u6124\u6c14\u3002\u4e0d \u6599\u5341\u6570\u62db\u5df2\u8fc7\uff0c\u53cc\u65b9\u62a2\u653b\u4e4b\u4e0b\uff0c\u7adf\u662f\u674e\u601d\u5357\u78b0\u4e0a\u7684\u9669\u62db\u66f4\u591a\uff01 \u3000\u3000\u8981\u77e5\u5c60\u9f99\u7684\u7236\u4eb2\u5c60\u767e\u57ce\u4e43\u662f\u4e0d\u51fa\u4e16\u7684\u6b66\u5b66\u5947\u624d\uff0c\u5341\u516b\u822c\u6b66\u827a\u4ef6\u4ef6\u7cbe\u901a\uff0c\u5c60\u9f99\u867d\u7136\u8bf4\u4e0d\u4e0a \u5df2\u5f97\u7236\u4eb2\u7684\u8863\u94b5\u771f\u4f20\uff0c\u4f46\u4e5f\u5f97\u4e86\u4e09\u56db\u5206\u672c\u9886\uff0c\u5c24\u5176\u5bf9\u4e8e\u5c60\u767e\u57ce\u81ea\u521b\u7684\u6b66\u529f\u4e2d\u7684\u5947\u8be1\u4e0e\u8f9b\u8fa3\u4e4b \u5904\uff0c\u7279\u522b\u7528\u5fc3\u94bb\u7814\uff0c\u662f\u4ee5\u5373\u4ee5\u5251\u6cd5\u800c\u8bba\uff0c\u4ed6\u4e5f\u5b9e\u662f\u4e0d\u5728\u67f3\u6d1e\u5929\u4e4b\u4e0b\u3002 \u3000\u3000\u867d\u7136\u5982\u6b64\uff01\u674e\u601d\u5357\u8fd8\u662f\u53ef\u4ee5\u80dc\u4ed6\u7684\u3002\u4f46\u56e0\u4e00\u6765\u4ed6\u5df2\u7ed9\u5c60\u9f99\u6fc0\u6012\uff0c\u9ad8\u624b\u6bd4\u6597\u7684\u5927\u5fcc\u6b63\u662f\u6c14 \u8e81\u5fc3\u6d6e\uff1b\u4e8c\u6765\u4ed6\u53c8\u4e0d\u8be5\u6709\u70b9\u8f7b\u654c\uff0c\u4e00\u4e0a\u6765\u5c31\u7ed9\u5c60\u9f99\u62a2\u4e86\u5148\u624b\u3002 \u3000\u3000\u4f46\u674e\u601d\u5357\u6bd5\u7adf\u662f\u4e2a\u5728\u6b66\u5b66\u4e0a\u6709\u6df1\u6e5b\u9053\u8be3\u7684\u4eba\uff0c\u8fde\u9047\u9669\u62db\u4e4b\u540e\uff0c\u731b\u7136\u4e00\u7701\uff1a\u201c\u4e0d\u5bf9\uff0c\u4e0d \u5bf9\uff0c\u6211\u600e\u80fd\u628a\u6b32\u901f\u5219\u4e0d\u8fbe\u7684\u9053\u7406\u5fd8\u4e86\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u5fc3\u795e\u4e00\u5b9a\uff0c\u6218\u672f\u5373\u53d8\u3002\u53ea\u89c1\u4ed6\u811a\u8e0f\u4e94\u884c\u516b\u5366\u65b9\u4f4d\uff0c\u5728\u5c60\u9f99\u72c2\u98ce\u66b4\u96e8\u822c\u5730\u6025\u653b\u4e4b \u4e0b\uff0c\u63a5\u8fde\u9000\u4e86\u4e03\u6b65\u3002\u8fde\u4e03\u6b65\u4e00\u9000\uff0c\u7acb\u65f6\u5c31\u6d88\u89e3\u4e86\u5c60\u9f99\u7684\u5148\u624b\uff0c\u8f6c\u5b88\u4e3a\u653b\u3002 \u3000\u3000\u8fd9\u4e00\u573a\u6bd4\u5251\u672a\u5fc5\u6bd4\u521a\u624d\u90a3\u573a\u7cbe\u5f69\uff0c\u4f46\u56e0\u53cc\u65b9\u5df2\u9732\u6740\u673a\uff0c\u5374\u662f\u6bd4\u674e\u601d\u5357\u6597\u67f3\u6d1e\u5929\u4e4b\u65f6\u51f6\u9669 \u591a\u4e86\u3002\u53cc\u65b9\u5728\u573a\u89c2\u6218\u7684\u4eba\uff0c\u90fd\u662f\u4e0d\u7531\u5f97\u4e0d\u63d0\u5fc3\u540a\u80c6\u3002 \u3000\u3000\u5f53\u5c40\u7684\u5c60\u9f99\u66f4\u662f\u6697\u6697\u5403\u60ca\uff0c\u6b64\u65f6\u4ed6\u5148\u624b\u5df2\u5931\uff0c\u53ea\u6709\u62db\u67b6\u7684\u4efd\u513f\u3002 \u3000\u3000\u674e\u601d\u5357\u8f6c\u5b88\u4e3a\u653b\u4e4b\u540e\uff0c\u62db\u6cd5\u8d8a\u53d1\u6c89\u7a33\uff0c\u6bcf\u4e00\u62db\u90fd\u662f\u653b\u5b88\u517c\u5907\u7684\u4e0a\u4e58\u5251\u6cd5\uff0c\u6839\u672c\u4e0d\u7ed9\u5c60\u9f99 \u4ee5\u53cd\u653b\u7684\u673a\u4f1a\u3002 \u3000\u3000\u4f46\u89c1\u5251\u82b1\u9519\u843d\uff0c\u5251\u6c14\u7eb5\u6a2a\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u5c60\u9f99\u7684\u6574\u4e2a\u8eab\u5f62\uff0c\u90fd\u5df2\u5728\u4ed6\u5251\u52bf\u7b3c\u7f69\u4e4b\u4e0b\u3002 \u3000\u3000\u5c60\u9f99\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5982\u6b64\u4e0b\u53bb\uff0c\u53ea\u6709\u675f\u624b\u5f85\u6bd9\u7684\u4efd\u513f\u3002\u201d\u7709\u5934\u4e00\u76b1\uff0c\u8ba1\u4e0a\u5fc3\u6765\uff0c\u5ffd\u5730\u8f7b\u58f0 \u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u4f60\u6740\u4e86\u6211\uff0c\u6768\u59d1\u5a18\u4e5f\u672a\u5fc5\u5c31\u4f1a\u5ac1\u4f60\uff01\u201d\u674e\u601d\u5357\u6012\u9053\uff1a\u201c\u4f60\u80e1\u8bf4\u4ec0\u4e48\uff1f\u201d\u5c60\u9f99 \u9053\uff1a\u201c\u6211\u529d\u4f60\u8fd8\u662f\u4e0d\u5fc5\u5728\u4f5c\u5c0f\u4eba\u5427\uff0c\u6768\u59d1\u5a18\u662f\u4e0d\u662f\u4f60\u628a\u5979\u8f6f\u7981\u8d77\u6765\u4e86\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u6014\u4e86\u4e00\u5f81\uff0c\u4e0d\u89c9\u53c8\u518d\u95ee\u9053\uff1a\u201c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u201d\u5c60\u9f99\u9053\uff1a\u201c\u5979\u5148\u6211\u800c\u6765\uff0c\u4eca\u4e3a\u4f55\u4e0d \u89c1\uff1f\u54fc\uff0c\u4e00\u5b9a\u662f\u4f60\u548c\u6211\u7684\u59b9\u59b9\u540c\u8c0b\uff0c\u5c06\u5979\u8f6f\u7981\uff01\u674e\u601d\u5357\u5440\u674e\u601d\u5357\uff0c\u4f60\u5e94\u8be5\u77e5\u9053\uff0c\u6768\u59d1\u5a18\u5df2\u7ecf \u662f\u6211\u7684\u7231\u59bb\uff0c\u4f60\u8feb\u5979\u518d\u5ac1\uff0c\u6211\u505a\u9b3c\u4e5f\u4e0d\u9976\u4f60\uff01\u201d \u3000\u3000\u5c60\u9f99\u8bf4\u8bdd\u4e4b\u65f6\uff0c\u624b\u5e95\u4e1d\u6beb\u4e0d\u7f13\uff0c\u53cc\u5251\u78b0\u51fb\u7684\u53ee\u94db\u58f0\u54cd\uff0c\u63a9\u76d6\u4e86\u4ed6\u7684\u58f0\u97f3\uff0c\u65c1\u4eba\u53ea\u89c1\u4ed6\u5634 \u5507\u5f00\u9616\uff0c\u5374\u4e0d\u77e5\u4ed6\u662f\u8bf4\u4e9b\u4ec0\u4e48\uff0c\u4f46\u89c1\u674e\u601d\u5357\u7684\u795e\u60c5\uff0c\u8d8a\u6765\u8d8a\u662f\u6124\u6012\u3002\u4f46\u4ed6\u8138\u4e0a\u7684\u795e\u8272\u4f17\u4eba\u53ef \u89c1\uff0c\u4ed6\u5fc3\u5e95\u7684\u60b2\u4f24\uff0c\u5374\u662f\u770b\u4e0d\u51fa\u6765\uff0c\u8fde\u5b5f\u660e\u971e\u90fd\u4e0d\u77e5\u9053\u3002\u5979\u5fc3\u4e2d\u53ea\u662f\u4e0d\u4f4f\u601d\u91cf\uff1a\u201c\u4e0d\u77e5\u5c60\u9f99 \u8bf4\u4e86\u4e9b\u4ec0\u4e48\uff0c\u60f9\u5f97\u4ed6\u5982\u6b64\u6124\u6012\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u4e0d\u613f\u63d0\u8d77\u6768\u5a49\u7684\u540d\u5b57\uff0c\u6068\u6068\u8bf4\u9053\uff1a\u201c\u4e0d\u7ba1\u4f60\u8bf4\u4e9b\u4ec0\u4e48\uff0c\u4eca\u65e5\u6211\u51b3\u4e0d\u80fd\u8f7b\u6613\u9976 \u4f60\uff01\u201d\u8bdd\u867d\u5982\u6b64\uff0c\u4ed6\u5374\u7ed9\u5c60\u9f99\u7684\u8fd9\u51e0\u53e5\u8bf4\u8bdd\u60f9\u5f97\u5fc3\u4e71\u5982\u9ebb\uff0c\u81ea\u601d\u81ea\u60f3\uff0c\u201c\u4ed6\u8bf4\u7684\u4e0d\u77e5\u662f\u771f\u662f \u5047\uff1f\u5a49\u59b9\u771f\u7684\u5df2\u7ecf\u6765\u5230\u8fd9\u91cc\u4e86\u5417\uff1f\u5018\u82e5\u5979\u89c1\u5230\u6211\u4e0e\u5979\u7684\u4e08\u592b\u6027\u547d\u76f8\u640f\uff0c\u4e0d\u77e5\u5979\u7684\u5fc3\u4e2d\u4f5c\u4f55\u611f \u60f3\uff1f\u201d \u3000\u3000\u5c60\u9f99\u7684\u786e\u662f\u601d\u7591\u6768\u5a49\u5df2\u7ecf\u6765\u4e86\u7405\u739b\u5c71\uff0c\u4ed6\u56de\u5c71\u5be8\u7684\u76ee\u7684\u4e4b\u4e00\uff0c\u4e5f\u5c31\u662f\u4e3a\u4e86\u627e\u5bfb\u6768\u5a49\u3002\u6b64 \u65f6\u89c1\u4e86\u674e\u601d\u5357\u7684\u795e\u8272\uff0c\u5fc3\u77e5\u4ed6\u4e0e\u6768\u5a49\u5c1a\u672a\u89c1\u9762\uff0c\u4e0d\u89c9\u677e\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u674e\u601d\u5357\u867d\u8bf4\u662f\u4e0d\u80af\u653e\u8fc7\u5c60\u9f99\uff0c\u4f46\u4ed6\u6b64\u65f6\u5fc3\u4e71\u5982\u9ebb\uff0c\u89c9\u5f97\u773c\u524d\u8fd9\u4e2a\u53ef\u618e\u53ef\u6068\u7684\u4eba\uff0c\u4e3a\u4e86\u6768 \u5a49\u7684\u7f18\u6545\uff0c\u5374\u662f\u6740\u4ed6\u4e0d\u662f\uff0c\u4e0d\u6740\u4e5f\u4e0d\u662f\uff01\u9ad8\u624b\u6bd4\u6597\uff0c\u54ea\u5bb9\u5f97\u5fc3\u795e\u5206\u6563\uff1f\u5c60\u9f99\u767b\u65f6\u53c8\u62a2\u5230\u4e86\u653b \u52bf\uff0c\u201c\u55e4\u201d\u7684\u4e00\u58f0\u8f7b\u664c\uff0c\u5251\u5c16\u523a\u7a7f\u4e86\u674e\u601d\u5357\u7684\u8863\u895f\uff01 \u3000\u3000\u8fd9\u4e00\u5251\u867d\u672a\u4f24\u7740\u674e\u601d\u5357\uff0c\u603b\u7b97\u662f\u80dc\u4e86\u4e00\u62db\u3002\u6df3\u4e8e\u5468\u8fd9\u8fb9\u7684\u4eba\u7eb7\u7eb7\u4e3a\u5c60\u9f99\u6253\u6c14\uff0c\u5927\u58f0\u559d \u5f69\u3002 \u3000\u3000\u8fd9\u4e00\u7247\u5f69\u58f0\u53cd\u800c\u4ee4\u674e\u601d\u5357\u51b7\u9759\u4e0b\u6765\uff0c\u5fc3\u9053\uff1a\u201c\u6211\u4e0d\u6740\u4ed6\uff0c\u4e5f\u51b3\u4e0d\u80fd\u8ba9\u4ed6\u6740\u6211\uff01\u201d\u5fc3\u795e\u4e00 \u5b9a\uff0c\u5251\u5149\u66b4\u957f\uff0c\u5de6\u4e00\u62db\u201c\u4e07\u91cc\u98de\u971c\u201d\uff0c\u53f3\u4e00\u62db\u201c\u5e72\u5c71\u843d\u6728\u201d\uff0c\u767b\u65f6\u628a\u5c40\u9762\u626d\u8f6c\u3002\u7ed9\u5c60\u9f99\u559d\u5f69 \u7684\u4eba\u4e5f\u767b\u65f6\u5624\u82e5\u5bd2\u8749\u3002 \u3000\u3000\u674e\u601d\u5357\u5fc3\u9053\uff1a\u201c\u662f\u65f6\u5019\u4e86\uff01\u201d\u5251\u62db\u50ac\u7d27\uff0c\u6709\u5982\u957f\u6c5f\u5927\u6cb3\uff0c\u6eda\u6eda\u800c\u4e0a\uff01\u6b64\u65f6\u5c60\u9f99\u83ab\u8bf4\u65e0\u6cd5 \u53cd\u6251\uff0c\u8fde\u62db\u67b6\u4e5f\u90fd\u4e3a\u96be\u4e86\u3002 \u3000\u3000\u5c60\u51e4\u5168\u795e\u89c2\u6218\uff0c\u5fc3\u4e2d\u4e03\u4e0a\u516b\u843d\u3002\u5979\u5f53\u7136\u5e0c\u671b\u674e\u601d\u5357\u5f97\u80dc\uff0c\u53ef\u662f\u5979\u53ea\u6709\u8fd9\u4e2a\u54e5\u54e5\uff0c\u5c3d\u7ba1\u54e5 \u54e5\u884c\u4e3a\u8352\u8c2c\uff0c\u4f46\u5144\u59b9\u4e4b\u60c5\u603b\u8fd8\u662f\u591a\u5c11\u6709\u7684\u3002\u6b64\u65f6\u5c60\u9f99\u5df2\u5728\u674e\u601d\u5357\u5251\u52bf\u5ba0\u7f69\u4e4b\u4e0b\uff0c\u674e\u601d\u5357\u4e00\u5251 \u72e0\u8fc7\u4e00\u5251\uff0c\u770b\u6765\u5df2\u662f\u968f\u65f6\u53ef\u4ee5\u53d6\u4ed6\u6027\u547d\u3002 \u3000\u3000\u5c60\u51e4\u4e0d\u89c9\u6709\u70b9\u5bb3\u6015\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4f46\u613f\u674e\u601d\u5357\u5251\u4e0b\u7a0d\u7a0d\u7559\u60c5\uff0c\u628a\u4ed6\u523a\u4f24\u4e5f\u65e0\u6240\u8c13\u3002\u5018\u82e5 \u628a\u4ed6\u6740\u4e86\uff0c\u6211\u53d7\u5f97\u8d77\uff0c\u53ea\u6015\u5988\u53d7\u4e0d\u8d77\uff01\u201d\u3002 \u3000\u3000\u5fc3\u5ff5\u672a\u5df2\uff0c\u5ffd\u89c1\u4e00\u4e2a\u5c0f\u4e2b\u5934\u8d70\u5230\u5979\u7684\u9762\u524d\uff0c\u5c60\u51e4\u4e00\u770b\uff0c\u6b63\u662f\u670d\u4f8d\u5979\u7684\u6bcd\u4eb2\u7684\u4e2b\u5934\uff0c\u5403\u4e86 \u4e00\u60ca\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u6765\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u90a3\u5c0f\u4e2b\u5934\u6ee1\u8138\u60ca\u60f6\u4e4b\u8272\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u8001\u592a\u592a\u542c\u8bf4\u5c11\u7237\u56de\u6765\u4e86\uff0c\u5979\u62c5\u5fc3\u4f60\u4eec\u9aa8\u8089\u76f8\u6b8b\uff0c \u53eb\u6211\u51fa\u6765\u544a\u8bc9\u4f60\uff0c\u4e0d\u53ef\u548c\u5c11\u7237\u52a8\u624b\u3002\u5979\u8fd8\u5429\u5490\u53eb\u5c11\u7237\u7acb\u5373\u53bb\u89c1\u5979\u3002\u8fd9\uff0c\u8fd9\u600e\u4e48\u529e\uff1f\u201d\u539f\u6765\u5c60 \u51e4\u7684\u6bcd\u4eb2\u81ea\u4e08\u592b\u6b7b\u540e\uff0c\u5c31\u4e00\u76f4\u662f\u5367\u75c5\u5728\u5e8a\uff0c\u662f\u4ee5\u4e0d\u80fd\u4eb2\u81ea\u51fa\u6765\u3002 \u3000\u3000\u5c60\u51e4\u7684\u6bcd\u4eb2\u53ea\u662f\u62c5\u5fc3\u4ed6\u4eec\u5144\u59b9\u76f8\u6b8b\uff0c\u5374\u60f3\u4e0d\u5230\u6b64\u9645\u4e0e\u5c60\u9f99\u52a8\u624b\u7684\u4e43\u662f\u5916\u4eba\u3002 \u3000\u3000\u5c60\u51e4\u6b63\u662f\u62c5\u5fc3\u7ed9\u6bcd\u4eb2\u77e5\u9053\uff0c\u542c\u4e86\u8fd9\u4e2b\u5934\u7684\u4f20\u8bdd\uff0c\u5fc3\u91cc\u8d8a\u53d1\u4e0d\u5b89\uff0c\u60f3\u9053\uff1a\u201c\u5018\u82e5\u662f\u6211\u4e0e\u54e5 \u54e5\u4ea4\u624b\uff0c\u8fd9\u8fd8\u597d\u529e\u3002\u4f46\u6211\u5374\u600e\u597d\u53eb\u674e\u601d\u5357\u9976\u4ed6\uff1f\u4f55\u51b5\u4eca\u65e5\u4e4b\u4e8b\uff0c\u4e0d\u4ec5\u662f\u76df\u4e3b\u4e4b\u4e89\uff0c\u8fd8\u662f\u6b63\u90aa \u4e4b\u6597\uff0c\u6211\u82e5\u5f87\u79c1\uff0c\u5c82\u4e0d\u6559\u5929\u4e0b\u82f1\u96c4\u7b11\u8bdd\uff1f\u201d \u3000\u3000\u5b5f\u660e\u971e\u770b\u51fa\u5c60\u51e4\u4e3a\u96be\u4e4b\u5904\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5df2\u7ecf\u548c\u4ed6\u8bf4\u8fc7\u4e86\uff0c\u51e4\u59d0\uff0c\u4f60\u653e\u5fc3\u5427\uff01\u201d\u5979\u8fd9\u8bdd\u662f \u6709\u610f\u8bf4\u7ed9\u674e\u601d\u5357\u542c\u7684\uff0c\u8bf4\u5f97\u7279\u522b\u5927\u58f0\u3002\u4f46\u8bdd\u867d\u5982\u6b64\uff0c\u5979\u5fc3\u91cc\u4e5f\u662f\u6beb\u65e0\u628a\u63e1\uff0c\u4e0d\u77e5\u674e\u601d\u5357\u4f1a\u4e0d \u4f1a\u8f7b\u6613\u653e\u8fc7\u5c60\u9f99\uff1f \u3000\u3000\u674e\u601d\u5357\u773c\u89c2\u56db\u9762\u8033\u542c\u516b\u65b9\uff0c\u89c1\u90a3\u5c0f\u4e2b\u5934\u51fa\u6765\uff0c\u4e0e\u5c60\u51e4\u6084\u6084\u8bf4\u8bdd\uff0c\u5fc3\u4e2d\u5df2\u662f\u731c\u5230\u4e86\u51e0\u5206\u3002 \u3000\u3000\u6597\u5230\u7d27\u5904\uff0c\u674e\u601d\u5357\u4e00\u62db\u201c\u8986\u76d6\u516d\u5408\u201d\uff0c\u5251\u52bf\u5982\u8679\uff0c\u628a\u5c60\u9f99\u5708\u4f4f\uff0c\u8fd9\u5239\u90a3\u95f4\u4ed6\u5fc3\u4e2d\u8f6c\u4e86\u597d \u51e0\u4e2a\u5ff5\u5934\uff1a\u201c\u6b64\u4eba\u884c\u4e3a\u90aa\u6076\uff0c\u4e0e\u6211\u53c8\u6709\u593a\u59bb\u4e4b\u4ec7\uff0c\u6211\u6740\u4ed6\u5462\u8fd8\u662f\u4e0d\u6740\uff1f\u201d \u3000\u3000\u542c\u4e86\u5b5f\u660e\u971e\u7684\u8bdd\uff0c\u7ec8\u4e8e\u5fc3\u91cc\u4e00\u9178\uff0c\u60f3\u9053\uff1a\u201c\u6211\u6740\u4e86\u4ed6\uff0c\u6768\u5a49\u8fd9\u4e00\u751f\u4e5f\u5c31\u6bc1\u4e86\u3002\u5b5f\u59d1\u5a18\u548c \u5c60\u59d1\u5a18\u6050\u6015\u4e5f\u4e0d\u80fd\u539f\u8c05\u6211\uff01\u201d \u3000\u3000\u601d\u5ff5\u53ca\u6b64\uff0c\u674e\u601d\u5357\u4e0d\u7981\u53f9\u4e86\u53e3\u6c14\uff0c\u559d\u9053\uff1a\u201c\u53bb\u5427\u3002\u201d\u6539\u523a\u4e3a\u62cd\uff0c\u5e73\u5251\u62cd\u4e0b\uff0c\u201c\u94db\u201d\u7684\u4e00 \u58f0\uff0c\u628a\u5c60\u9f99\u7684\u957f\u5251\u6253\u843d\u540e\u8fc5\u5373\u98de\u8d77\u4e00\u811a\uff0c\u5c06\u4ed6\u8e22\u4e86\u4e00\u4e2a\u7b4b\u6597\uff01\u4e0d\u6599\u674e\u601d\u5357\u80af\u9976\u5c60\u9f99\uff0c\u5c60\u9f99\u5374 \u662f\u4e0d\u80af\u9976\u4ed6\u3002\u4ed6\u4e00\u4e2a\u7b4b\u6597\u7ffb\u51fa\u53bb\uff0c\u6697\u4e2d\u5df2\u662f\u53d6\u51fa\u4e86\u6bd2\u9f99\u9556\uff0c\u4eba\u672a\u7ad9\u8d77\uff0c\u4e09\u652f\u6bd2\u9f99\u9556\u5c31\u4ece\u80ef\u4e0b \u6253\u51fa\uff01\u6bd4\u6b66\u5df2\u7ecf\u5206\u51fa\u80dc\u8d1f\uff0c\u800c\u4e14\u8fd8\u662f\u674e\u601d\u5357\u9976\u4e86\u5c60\u9f99\u7684\u6027\u547d\uff0c\u8c01\u90fd\u60f3\u4e0d\u5230\u5c60\u9f99\u7adf\u4f1a\u6069\u5c06\u4ec7 \u62a5\uff0c\u7a81\u7136\u4e0b\u6b64\u6bd2\u624b\uff01 \u3000\u3000\u5c60\u51e4\u4e0e\u5b5f\u660e\u971e\u4e0d\u7ea6\u800c\u540c\u5730\u4e00\u58f0\u5c16\u53eb\uff0c\u98de\u8dd1\u51fa\u53bb\u3002\u5c60\u51e4\u60f3\u8981\u5236\u6b62\u54e5\u54e5\uff0c\u5b5f\u660e\u971e\u60f3\u8981\u4fdd\u62a4\u674e \u601d\u5357\uff0c\u4f46\u90fd\u5df2\u6765\u4e0d\u53ca\u4e86\uff0c\u5b5f\u660e\u971e\u53d1\u51fa\u4e09\u679a\u94b1\u9556\uff0c\u60f3\u628a\u6bd2\u9f99\u9563\u6253\u843d\uff0c\u4f46\u662f\u6bd2\u9f99\u9556\u51fa\u624b\u5728\u524d\uff0c\u52bf \u9053\u53c8\u6025\u53c8\u52b2\uff0c\u5b5f\u660e\u971e\u6240\u53d1\u7684\u94b1\u9556\u6ca1\u6709\u4e00\u679a\u78b0\u7740\u3002 \u3000\u3000\u4e09\u652f\u6bd2\u9f99\u9556\u95ea\u7535\u800c\u81f3\uff0c\u773c\u770b\u5c31\u8981\u5728\u674e\u601d\u5357\u8eab\u4e0a\u63d2\u5165\uff0c\u9661\u7136\u95f4\u53ea\u89c1\u674e\u601d\u5357\u5728\u534a\u7a7a\u4e2d\u4e00\u4e2a\u7b4b \u6597\u5012\u7ffb\u4e0b\u6765\u3002\u65c1\u4eba\u8fd8\u4ee5\u4e3a\u4ed6\u4e2d\u4e86\u6697\u5668\uff0c\u9f50\u58f0\u60ca\u547c\u3002\u4e0d\u6599\u4ed6\u5374\u662f\u5728\u8fd9\u6027\u547d\u4fc4\u9877\u4e4b\u95f4\uff0c\u663e\u51fa\u4e86\u975e \u51e1\u7684\u672c\u9886\uff01 \u3000\u3000\u53ea\u542c\u5f97\u201c\u94ee\u201d\u7684\u4e00\u58f0\uff0c\u4e00\u652f\u6bd2\u9f99\u9556\u8dcc\u5728\u5730\u4e0a\uff0c\u968f\u5373\u201c\u94db\u201d\u7684\u4e00\u54cd\uff0c\u4e00\u652f\u6bd2\u9f99\u9556\u7adf\u7136\u5411\u7740 \u5c60\u9f99\u53cd\u6253\u56de\u6765\u3002\u8ddf\u7740\u9752\u5149\u4e00\u95ea\uff0c\u7b2c\u4e09\u652f\u6bd2\u9f99\u9556\u64e6\u7740\u674e\u601d\u5357\u7684\u80a9\u5934\u98de\u8fc7\uff0c\u9489\u5728\u4e00\u68f5\u6811\u4e0a\u3002 \u3000\u3000\u539f\u6765\u674e\u601d\u5357\u5728\u8fd9\u77ac\u606f\u4e4b\u95f4\u5df2\u662f\u4f7f\u51fa\u4e09\u79cd\u4e0d\u540c\u7684\u4e0a\u4e58\u6b66\u529f\u3002\u90a3\u4e09\u652f\u6bd2\u9f99\u9556\u662f\u5411\u7740\u4e0a\u4e2d\u4e0b\u4e09 \u8def\u6253\u6765\u7684\uff0c\u674e\u601d\u5357\u5728\u534a\u7a7a\u4e2d\u4e00\u4e2a\u7b4b\u6597\u7ffb\u4e0b\u6765\uff0c\u53f3\u8db3\u4e00\u8e6c\uff0c\u628a\u6253\u5411\u4e0b\u76d8\u7684\u6bd2\u9f99\u9556\u8e22\u843d\uff0c\u6a2a\u5251\u5728 \u80f8\u524d\u4e00\u6321\uff0c\u5251\u67c4\u4e00\u649e\uff0c\u6253\u5411\u4e2d\u76d8\u7684\u6bd2\u9f99\u9556\u5012\u98de\u56de\u53bb\uff0c\u81f3\u4e8e\u6253\u5411\u4e0a\u76d8\u7684\u90a3\u652f\u6bd2\u9f99\u9556\uff0c\u5219\u662f\u56e0\u4e3a \u4ed6\u8fd9\u4e2a\u7b4b\u6597\u7ffb\u5f97\u6070\u5230\u597d\u5904\uff0c\u672c\u662f\u8981\u5c04\u53e5\u4ed6\u7684\u54bd\u5589\u7684\uff0c\u5374\u4ece\u4ed6\u7684\u80a9\u5934\u4fa7\u8fb9\u98de\u8fc7\u53bb\u4e86\u3002 \u3000\u3000\u674e\u601d\u5357\u4ee5\u8d85\u5353\u7684\u8f7b\u529f\uff0c\u9ad8\u660e\u7684\u5251\u672f\uff0c\u5f3a\u52b2\u7684\u817f\u529b\uff0c\u6216\u6253\u6216\u95ea\uff0c\u7834\u4e86\u5bf9\u65b9\u8fd9\u4e09\u652f\u72e0\u8fa3\u65e0\u6bd4 \u7684\u6bd2\u9f99\u9556\uff0c\u7fa4\u96c4\u4e00\u60ca\uff0c\u767b\u65f6\u53d1\u51fa\u4e86\u5982\u96f7\u7684\u5f69\u58f0\u3002 \u3000\u3000\u4f46\u5728\u559d\u5f69\u58f0\u4e2d\u5374\u6742\u7740\u5c60\u9f99\u7684\u4e00\u58f0\u60e8\u53eb\uff0c\u539f\u6765\u5c60\u9f99\u5df2\u4f24\u5728\u81ea\u5df1\u6240\u53d1\u51fa\u7684\u4e00\u652f\u6bd2\u9f99\u9556\u4e4b\u4e0b\u3002 \u4ed6\u662f\u7ed9\u674e\u601d\u5357\u8e22\u7ffb\u7684\uff0c\u4ece\u80ef\u4e0b\u53cd\u624b\u6253\u51fa\u4e09\u652f\u6bd2\u9f99\u9556\u4e4b\u540e\uff0c\u521a\u8981\u7ad9\u8d77\uff0c\u90a3\u4e00\u652f\u53cd\u5c04\u56de\u6765\u7684\u6bd2\u9f99 \u9556\u6070\u597d\u63d2\u8fdb\u4ed6\u7684\u80a9\u5934\u3002 \u3000\u3000\u8fd9\u4e00\u58f0\u60e8\u53eb\u7aef\u7684\u662f\u4ee4\u4eba\u60ca\u5fc3\u52a8\u9b44\u3002\u4f17\u4eba\u90fd\u77e5\u9053\u5c60\u5bb6\u7684\u6bd2\u9f99\u9556\u4e43\u662f\u89c1\u8840\u5c01\u5589\u7684\u6697\u5668\uff0c\u5c60\u9f99 \u81ea\u4f5c\u81ea\u53d7\uff0c\u4eba\u4eba\u90fd\u611f\u75db\u5feb\uff0c\u4f46\u4e5f\u4e0d\u7981\u76f8\u5bf9\u9a87\u7136\u3002\u5239\u90a3\u95f4\u5168\u573a\u90fd\u662f\u9e26\u96c0\u65e0\u58f0\uff0c\u91cd\u5f52\u5bc2\u9759\u3002\u4eba\u4eba \u6ce8\u89c6\u4e8b\u60c5\u7684\u6f14\u53d8\u3002 \u3000\u3000\u5c60\u9f99\u8eab\u4e0a\u867d\u6709\u89e3\u836f\uff0c\u4f46\u4e2d\u4e86\u8fd9\u652f\u6bd2\u9f99\u9556\u4e4b\u540e\uff0c\u9877\u8346\u95f4\u5df2\u662f\u6d51\u8eab\u9ebb\u75d2\uff0c\u534a\u70b9\u529b\u6c14\u4e5f\u65e0\uff0c\u54ea \u91cc\u8fd8\u80fd\u81ea\u638f\u89e3\u836f\uff1f \u3000\u3000\u5c60\u51e4\u53eb\u9053\uff1a\u201c\u4f60\u3001\u4f60\u3001\u4f60\u7b80\u76f4\u4e0d\u662f\u4eba\uff0c\u597d\u5566\uff0c\u73b0\u5728\u662f\u5bb3\u4eba\u53cd\u5bb3\u81ea\u5df1\uff0c\u4f60\u3001\u4f60\u2026\u2026\u201d\u5979\u75db \u6068\u5c60\u9f99\u624b\u6bb5\u7684\u5351\u9119\uff0c\u672c\u6765\u662f\u60f3\u9a82\u4ed6\u4e00\u987f\u7684\uff0c\u4f46\u542c\u4e86\u4ed6\u8fd9\u4e00\u58f0\u60e8\u53eb\u4e4b\u540e\uff0c\u5374\u4e5f\u4e0d\u7531\u5f97\u5fc3\u91cc\u60ca \u614c\uff0c\u9a82\u4e0d\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u5c60\u51e4\u5583\u5583\u8bf4\u9053\uff1a\u201c\u81ea\u4f5c\u5b7d\uff0c\u4e0d\u53ef\u6d3b\u3002\u81ea\u4f5c\u5b7d\uff0c\u4e0d\u53ef\u6d3b\uff01\u201d\u8bdd\u867d\u5982\u6b64\uff0c\u5979\u603b\u662f\u4e0d\u5fcd\u89c1\u5979\u54e5 \u54e5\u60e8\u6b7b\uff0c\u662f\u4ee5\u53e3\u91cc\u8fd9\u6837\u8bf4\uff0c\u811a\u5374\u5df2\u662f\u8d70\u5730\u5230\u4e86\u54e5\u54e5\u8ddf\u524d\uff0c\u4f38\u51fa\u624b\u6307\uff0c\u70b9\u4e86\u4ed6\u4e09\u5904\u7a74\u9053\uff0c\u514d\u5f97 \u4ed6\u4f53\u4e2d\u7684\u6bd2\u8d28\u8fc5\u901f\u4fb5\u5165\u5fc3\u623f\u3002\u8ddf\u7740\u5728\u4ed6\u8eab\u4e0a\u6211\u5230\u4e86\u89e3\u836f\uff0c\u7ed9\u4ed6\u670d\u4e0b\uff0c\u7136\u540e\u62d4\u8d77\u6bd2\u9f99\u9556\uff0c\u5728\u4f24 \u53e3\u6577\u4e0a\u80fd\u89e3\u6bd2\u7684\u91d1\u521b\u836f\u3002\u5e78\u4e8f\u5c60\u51e4\u6df1\u77e5\u6bd2\u9f99\u9556\u7684\u89e3\u6cd5\uff0c\u8fd9\u624d\u4fdd\u5168\u4e86\u5c60\u9f99\u7684\u4e00\u6761\u6027\u547d\u3002 \u3000\u3000\u674e\u601d\u5357\u6b49\u7136\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u662f\u6709\u610f\u4f24\u4ed6\u7684\uff0c\u6211\u8fd9\u662f\u8feb\u4e0d\u5f97\u5df2\u3002\u201d\u5c60\u51e4\u9053\uff1a\u201c\u6211\u660e\u767d\uff0c\u8fd9\u5f53 \u7136\u602a\u4e0d\u5f97\u4f60\u3002\u201d \u3000\u3000\u5c60\u9f99\u5750\u8d77\u8eab\u6765\uff0c\u7741\u5f00\u773c\u775b\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u522b\u5728\u6211\u8ddf\u524d\u4e00\u5531\u4e00\u548c\uff0c\u8bf4\u8fd9\u4e9b\u98ce\u51c9\u8bdd\u5566\uff01\u201d \u5c60\u51e4\u53c8\u662f\u4f24\u5fc3\uff0c\u53c8\u662f\u6c14\u607c\uff0c\u8bf4\u9053\uff1a\u201c\u54e5\u54e5\uff0c\u4f60\u5230\u4e86\u5982\u4eca\uff0c\u7adf\u8fd8\u4e0d\u77e5\u4fae\u6539\u5417\uff1f\u201d \u3000\u3000\u5c60\u9f99\u7ffb\u4e86\u4e00\u7ffb\u767d\u5f2f\u5f2f\u7684\u773c\u73e0\uff0c\u663e\u7136\u662f\u5bf9\u674e\u601d\u5357\u6028\u6bd2\u672a\u6d88\uff0c\u4f46\u5df2\u65e0\u6c14\u529b\u4e0e\u59b9\u59b9\u53cd\u5507\u76f8\u8ba5\u3002 \u4ed6\u62db\u4e86\u62db\u624b\uff0c\u65ad\u65ad\u7eed\u7eed\u5730\u53eb\u9053\uff0c\u201c\u6625\u5170\uff0c\u4f60\u8fc7\u6765\u3002\u201d \u3000\u3000\u90a3\u5c0f\u4e2b\u5934\u5413\u5f97\u9762\u9752\u5507\u767d\u5730\u6765\u5230\u5c60\u9f99\u9762\u524d\uff0c\u5c60\u9f99\u98a4\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u6276\u6211\u8fdb\u53bb\uff01\u201d\u5c60\u51e4\u6014\u4e86\u4e00 \u6014\uff0c\u9053\uff0c\u201c\u4f60\u505a\u4ec0\u4e48\uff1f\u201d\u5c60\u9f99\u54ac\u7834\u6687\u5507\uff0c\u4f7f\u52b2\u8bf4\u9053\uff1a\u201c\u6211\u81ea\u5df1\u7684\u5bb6\uff0c\u6211\u4e0d\u80fd\u8fdb\u53bb\u4e48\uff1f\u201d \u3000\u3000\u5c60\u9f99\u670d\u4e86\u89e3\u836f\u867d\u65e0\u6027\u547d\u4e4b\u5fe7\uff0c\u4f46\u4e5f\u8fd8\u662f\u8981\u4eba\u670d\u4f8d\u7684\uff0c\u5fc5\u987b\u6709\u4e2a\u5730\u65b9\u517b\u75c5\u624d\u884c\u3002\u5c60\u51e4\u5fc3\u5934 \u4e00\u8f6f\uff0c\u8bf4\u9053\uff1a\u201c\u6625\u5170\uff0c\u4f60\u6276\u4ed6\u8fdb\u540e\u9662\uff0c\u627e\u95f4\u9759\u5ba4\u7ed9\u4ed6\u3002\u6682\u65f6\u4e0d\u8981\u7ed9\u5988\u77e5\u9053\u3002\u5f85\u6211\u56de\u6765\uff0c\u518d\u4f5c \u5904\u7f6e\u3002\u201d \u3000\u3000\u4e00\u9635\u7eb7\u4e71\u8fc7\u540e\uff0c\u5c60\u51e4\u8d70\u56de\u539f\u4f4d\u3002\u674e\u601d\u5357\u548c\u5b5f\u660e\u971e\u6b63\u8981\u56de\u53bb\uff0c\u53ea\u89c1\u5bf9\u65b9\u7684\u4e3b\u5c06\u6df3\u4e8e\u5468\u5df2\u7ecf \u4e0b\u573a\u3002 \u3000\u3000\u6df3\u4e8e\u5468\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u6bd4\u6b66\u96be\u514d\u4f24\u4eba\uff0c\u4f60\u4eec\u5a46\u5a46\u5988\u5988\u7684\u4e5f\u95f9\u591f\u4e86\u5427\u3002\u522b\u803d\u6401\u6b63\u4e8b\uff0c\u65f6\u5019\u4e0d \u65e9\uff0c\u8c01\u6765\u4e0e\u6211\u4e00\u51b3\u96cc\u96c4\uff1f\u201d \u3000\u3000\u539f\u6765\u6df3\u4e8e\u5468\u89c1\u5c60\u9f99\u53d7\u4e86\u4f24\uff0c\u67f3\u6d1e\u5929\u3001\u5d14\u9547\u5c71\u53c8\u5df2\u8d70\u4e86\uff0c\u4ed6\u8fd9\u4e00\u8fb9\u5df2\u65e0\u5c1a\u582a\u4e00\u6218\u7684\u9ad8\u624b\uff0c \u662f\u4ee5\u552f\u6709\u4eb2\u81ea\u51fa\u9a6c\uff0c\u633d\u56de\u8d25\u5c40\u3002\u4ed7\u7740\u81ea\u5df1\u6df1\u6e5b\u7684\u6b66\u529f\uff0c\u6280\u538b\u5f53\u573a\uff0c\u6151\u670d\u7fa4\u96c4\u3002 \u3000\u3000\u8bba\u5355\u6253\u72ec\u6597\u7684\u672c\u9886\uff0c\u5c60\u51e4\u8fd9\u8fb9\u7684\u4eba\u6ca1\u6709\u4e00\u4e2a\u53ef\u4ee5\u6bd4\u5f97\u4e0a\u4ed6\u3002\u5c60\u51e4\u5fc3\u60f3\uff1a\u201c\u82e5\u662f\u7239\u7239\u672a \u6b7b\uff0c\u767e\u62db\u4e4b\u5185\u53ef\u4ee5\u80dc\u4ed6\u3002\u4f46\u5982\u4eca\u5374\u54ea\u91cc\u53bb\u627e\u4e00\u4e2a\u53ef\u4ee5\u548c\u4ed6\u4e00\u6218\u7684\u5bf9\u624b\u4e86 \u3000\u3000\u7fa4\u96c4\u90fd\u6ca1\u5e94\u58f0\uff0c\u674e\u601d\u5357\u5ffd\u5730\u56de\u8f6c\u80a0\u5fc3\uff0c\u8bf4\u9053\uff1a\u201c\u6df3\u4e8e\u5be8\u4e3b\uff0c\u6211\u63a5\u4f60\u7684\u9ad8\u62ac\uff01\u201d \u3000\u3000\u6df3\u4e8e\u5468\u51b7\u51b7\u8bf4\u9053\uff01\u201d\u4f60\u5df2\u7ecf\u6253\u4e86\u4e24\u573a\u4e86\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6309\u89c4\u77e9\u6211\u6253\u4e86\u4e24\u573a\u4e4b\u540e\uff0c\u53ef\u4ee5\u4f11\u606f\u3002\u4f46\u6211\u81ea\u613f\u653e\u5f03\u8fd9\u4e2a\u6743\u5229\uff0c\u8fd9\u5374 \u5e76\u4e0d\u72af\u89c4\uff01\u201d \u3000\u3000\u6df3\u4e8e\u5468\u7709\u6bdb\u4e00\u626c\uff0c\u4e0d\u7f6e\u53ef\u5426\u3002\u7fa4\u96c4\u7eb7\u7eb7\u56b7\u9053\uff1a\u201c\u8fd9\u4e0d\u516c\u5e73\u3002\u201d \u3000\u3000\u6df3\u4e8e\u5468\u672c\u662f\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b50\u81ea\u5df1\u627e\u6b7b\u5417\uff1f\u7531\u5f97\u4f60\u3002\u201d\u4f46\u542c\u5f97\u7fa4\u96c4\u8fd9\u4e48\u4e00\u56b7\uff0c\u4e3a\u4e86 \u7ef4\u62a4\u81ea\u5df1\u201c\u7eff\u6797\u81f3\u5c0a\u201d\u7684\u9762\u5b50\uff0c\u53ea\u6709\u6545\u4f5c\u4e0d\u5c51\u7684\u795e\u6c14\uff0c\u76b1\u4e86\u76b1\u7709\u5934\u8bf4\u9053\uff1a\u201c\u4f60\u867d\u7136\u613f\u610f\uff0c\u6211 \u53ef\u4e0d\u60f3\u6361\u8fd9\u4e2a\u4fbf\u5b9c\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u597d\u5427\uff0c\u90a3\u5c31\u7531\u6211\u9894\u6559\u6df3\u4e8e\u5be8\u4e3b\u7684\u9ad8\u62db\uff01\u201d \u3000\u3000\u4e00\u6765\u662f\u8f88\u5206\u4e0d\u5f53\uff0c\u4e8c\u6765\u5b5f\u660e\u971e\u4e5f\u662f\u6253\u4e86\u4e00\u573a\u7684\uff0c\u6df3\u4e8e\u5468\u65e2\u7136\u62d2\u7edd\u4e86\u674e\u601d\u5357\uff0c\u5f53\u7136\u4e5f\u4e0d\u80fd \u591f\u63a5\u53d7\u5b5f\u660e\u971e\u4f5c\u5bf9\u624b\u3002 \u3000\u3000\u674e\u601d\u5357\u548c\u5b5f\u660e\u971e\u90fd\u662f\u4e0d\u80af\u9000\u4e0b\uff0c\u6df3\u4e8e\u5468\u5fc3\u4e2d\u4e00\u52a8\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u5427\uff0c\u4f60\u4eec\u5c31\u5e76\u80a9\u5b50\u4e0a\u5427\u3002 \u8fd9\u6837\uff0c\u603b\u4e0d\u80fd\u8bf4\u662f\u4e0d\u516c\u5e73\u4e86\u3002\u201d\u4ed6\u6697\u81ea\u76d8\u7b97\uff0c\u674e\u601d\u5357\u5df2\u7ecf\u6076\u6597\u4e24\u573a\uff0c\u6c14\u529b\u4e0d\u52a0\uff0c\u5251\u672f\u867d\u7136\u7cbe \u5999\uff0c\u4ed6\u4e5f\u6709\u7834\u89e3\u4e4b\u6cd5\uff0c\u81f3\u4e8e\u5b5f\u670b\u971e\u5219\u66f4\u4e0d\u5728\u4ed6\u7684\u773c\u5185\u3002 \u3000\u3000\u674e\u601d\u5357\u672c\u6765\u8fd8\u662f\u6709\u70b9\u4e0d\u5927\u613f\u610f\uff0c\u5b5f\u660e\u971e\u5374\u9053\uff1a\u201c\u597d\uff0c\u4f60\u662f\u957f\u8f88\uff0c\u6211\u4eec\u4ee5\u4e8c\u654c\u4e00\uff0c\u4e5f\u4e0d\u7b97 \u5360\u4e86\u4f60\u7684\u4fbf\u5b9c\u3002\u5c31\u8fd9\u6837\u5427\u3002\u201d\u5b5f\u660e\u971e\u5df2\u7ecf\u540c\u610f\uff0c\u674e\u601d\u5357\u81ea\u662f\u4e0d\u4fbf\u575a\u6301\u5230\u5e95\u3002 \u3000\u3000\u674e\u3001\u5b5f\u4e8c\u4eba\u5e76\u80a9\u4e00\u7acb\uff0c\u53cc\u5251\u51fa\u9798\u3002\u674e\u601d\u5357\u9053\uff1a\u201c\u6df3\u4e8e\u5be8\u4e3b\uff0c\u8bf7\u4eae\u5175\u5203\u3002\u201d\u6df3\u4e8e\u5468\u54c8\u54c8\u7b11 \u9053\uff1a\u201c\u5c60\u767e\u57ce\u4ee5\u6b7b\uff0c\u6211\u7684\u53cc\u94a7\u662f\u65e9\u5df2\u4e0d\u7528\u7684\u4e86\u3002\u4eca\u65e5\u6211\u53ef\u5e76\u6ca1\u6709\u643a\u5e26\u624b\u5e26\u5203\u4e0a\u5c71\u3002\u201d\u8a00\u4e0b\u4e4b \u610f\uff0c\u5bf9\u4ed8\u5c0f\u8f88\uff0c\u5b9e\u662f\u4e0d\u503c\u5f97\u4ed6\u52a8\u7528\u5175\u5203\u3002 \u3000\u3000\u5b5f\u660e\u971e\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u4f60\u8981\u7a7a\u624b\u8f83\u91cf\u6211\u4eec\u4e5f\u6210\u3002\u201d\u987b\u77e5\u4eca\u65e5\u4e43\u662f\u76df\u4e3b\u4e4b\u4e89\uff0c\u5b5f\u660e\u971e\u4f46 \u6c42\u80fd\u52a9\u674e\u601d\u5357\u53d6\u5f97\u80dc\u5229\uff0c\u5979\u662f\u665a\u8f88\uff0c\u4ee5\u5251\u654c\u638c\uff0c\u4e5f\u4e0d\u80fd\u7b97\u662f\u6709\u5931\u4f53\u9762\u3002 \u3000\u3000\u4e0d\u6599\u6df3\u4e8e\u5468\u5374\u7b11\u9053\uff1a\u201c\u7a7a\u624b\u672c\u6765\u4e5f\u884c\uff0c\u4e0d\u8fc7\u5bf9\u4f60\u7239\u7239\u5374\u4f3c\u4e4e\u6709\u70b9\u4e0d\u656c\uff0c\u8fd9\u6837\u5427\uff0c\u6211\u968f\u624b \u4fbf\u62ff\u4e00\u6837\u4e1c\u897f\u5f53\u4f5c\u5175\u5668\uff0c\u4e0d\u4f7f\u6211\u7684\u72ec\u95e8\u53cc\u94a9\uff0c\u4e5f\u5c31\u662f\u4e86\u3002\u201d \u3000\u3000\u5c60\u51e4\u9053\uff1a\u201c\u77f3\u5e08\u54e5\uff0c\u4f60\u53eb\u4ed6\u4eec\u628a\u5341\u516b\u822c\u5175\u5668\u62ff\u6765\uff0c\u8ba9\u6df3\u4e8e\u5be8\u4e3b\u9009\u7528\u3002\u201d \u3000\u3000\u6df3\u4e8e\u5468\u9053\uff1a\u201c\u4e0d\u7528\uff0c\u5b66\u6b66\u7684\u4eba\uff0c\u4fe1\u624b\u62c8\u6765\uff0c\u90fd\u662f\u6b66\u5668\uff0c\u4f55\u987b\u8fd9\u6837\u9ebb\u70e6\u3002\u201d \u3000\u3000\u8463\u5f00\u5c71\u5fcd\u8010\u4e0d\u4f4f\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u90a3\u5c31\u4fe1\u624b\u62c8\u6765\u5427\uff0c\u4e0d\u5fc5\u8001\u662f\u5439\u725b\u4e86\u3002\u201d \u3000\u3000\u6df3\u4e8e\u5468\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u5c60\u59d1\u5a18\uff0c\u6211\u501f\u8d35\u5be8\u7684\u5927\u949f\u4e00\u7528\u3002\u201d \u3000\u3000\u5728\u8fd9\u6f14\u6b66\u573a\u7684\u5f53\u4e2d\uff0c\u6709\u4e00\u53e3\u5927\u94dc\u949f\uff0c\u6709\u4e8b\u4e4b\u65f6\uff0c\u9e23\u949f\u805a\u4f17\u7528\u7684\uff0c\u6df3\u4e8e\u5468\u8bf4\u4e86\u8fd9\u8bdd\u4e4b\u540e\uff0c \u4e0d\u5f85\u5c60\u51e4\u7b54\u590d\uff0c\u7acb\u5373\u628a\u548c\u4e00\u626c\uff0c\u90a3\u53e3\u5927\u949f\u7a81\u7136\u4ece\u7a7a\u4e2d\u6389\u4e0b\uff0c\u5413\u5f97\u65c1\u8fb9\u7684\u4eba\u7eb7\u7eb7\u8eb2\u907f\uff0c\u539f\u6765\u4ed6 \u662f\u7528\u4e00\u679a\u94dc\u94b1\u6253\u51fa\u53bb\uff0c\u5272\u65ad\u4e86\u540a\u949f\u7684\u7ef3\u7d22\u3002 \u3000\u3000\u6df3\u4e8e\u5468\u4e00\u4e2a\u201c\u864e\u8df3\u201d\u8dc3\u4e0a\u524d\u53bb\uff0c\u53cc\u81c2\u4e00\u4f38\uff0c\u628a\u8fd9\u53e3\u94dc\u949f\u63a5\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u4ed6\u521a\u624d\u6253\u51fa\u7684\u90a3\u679a\u94dc\u94b1\uff0c\u5e76\u4e0d\u662f\u78e8\u5229\u4e86\u8fb9\u7684\u201c\u94b1\u9556\u201d\uff0c\u5c45\u7136\u80fd\u591f\u5272\u65ad\u7c97\u7ef3\uff0c\u8fd9\u4efd\u529f\u529b\u5df2 \u8db3\u60ca\u4eba\u3002\u8fd9\u53e3\u94dc\u949f\u6709\u767e\u591a\u65a4\u91cd\uff0c\u5728\u7a7a\u4e2d\u6389\u4e0b\u6765\uff0c\u8981\u628a\u5b83\u63a5\u4e0b\uff0c\u53cc\u81c2\u5c11\u8bf4\u4e5f\u5f97\u6709\u5343\u65a4\u6c14\u529b\uff0c\u6df3 \u4e8e\u5468\u5728\u77ac\u606f\u4e4b\u95f4\uff0c\u663e\u9732\u4e86\u4e24\u624b\u9707\u4e16\u9a87\u4fd7\u7684\u529f\u592b\uff0c\u7fa4\u96c4\u867d\u7136\u4e0d\u6ee1\u4ed6\u7684\u4e3a\u4eba\uff0c\u5374\u4e5f\u4e0d\u7981\u4e3a\u4e4b\u559d \u5f69\u3002 \u3000\u3000\u6df3\u4e8e\u5468\u6258\u94dc\u949f\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5c31\u7528\u8fd9\u4ef6\u201c\u7b28\u5bb6\u4f19\u201d\u548c\u4f60\u4eec\u800d\u800d\uff0c\u6765\u5427\uff0c\u51fa\u5251\u5440\uff0c\u674e\u516c\u5b50\uff0c \u53ea\u8981\u4f60\u4eec\u80dc\u5f97\u4e86\u6211\uff0c\u8fd9\u76df\u4e3b\u5c31\u662f\u4f60\u7684\u4e86\u3002\u201d\u674e\u601d\u5357\u600e\u4e48\u4e5f\u6ca1\u60f3\u4ed6\u5982\u6b64\u5632\u5f04\uff0c\u4f46\u5927\u654c\u5728\u524d\uff0c\u5374 \u4e5f\u4e0d\u6562\u8f7b\u89c6\uff0c\u5f53\u4e0b\u624b\u634f\u5251\u8bc0\uff0c\u907f\u5f00\u6b63\u9762\uff0c\u5530\u5730\u4fbf\u523a\u8fc7\u53bb\u3002\u6b63\u662f\uff1a \u3000\u3000\u4e0d\u754f\u8c6a\u5f3a\u540c\u6297\u654c\uff0c\u82f1\u96c4\u809d\u80c6\u5973\u513f\u60c5\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u56db\u56de\u3000\u5e2d\u4e0a\u7fa4\u82f1\u540c\u795d\u8d3a\u3000\u573a\u8fb9\u4e00\u5973\u72ec\u6006\u7136 \u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b \u7b2c\u4e8c\u5341\u56db\u56de\u3000\u5e2d\u4e0a\u7fa4\u82f1\u540c\u795d\u8d3a\u3000\u573a\u8fb9\u4e00\u5973\u72ec\u6006\u7136 \u3000\u3000\u4e0a\u4e58\u6b66\u5b66\u8bb2\u7a76\u7684\u662f\u4ee5\u5de7\u964d\u529b\uff0c\u5251\u672f\u5c24\u5176\u6ce8\u91cd\u8f7b\u7075\u7fd4\u52a8\uff0c\u662f\u4ee5\u674e\u601d\u5357\u867d\u7136\u6709\u70b9\u5403\u60ca\u4e8e\u6df3\u4e8e \u5468\u7684\u529f\u529b\u60ca\u4eba\uff0c\u5374\u4e5f\u5e76\u4e0d\u754f\u60e7\uff0c\u5fc3\u60f3\u4ed6\u7684\u5927\u949f\u6321\u5f97\u6b63\u9762\uff0c\u6211\u5c31\u7ed9\u4ed6\u6765\u4e2a\u4fa7\u88ad\u3002\u4e00\u5251\u523a\u51fa\uff0c\u5251 \u5c16\u4e0b\u5782\uff0c\u6307\u5411\u6df3\u4e8e\u5468\u817f\u5f2f\u7684\u201c\u98ce\u5e02\u7a74\u201d\uff0c\u5b5f\u660e\u971e\u4e5f\u662f\u540c\u4e00\u5fc3\u601d\uff0c\u7acb\u5373\u4e0e\u674e\u601d\u5357\u914d\u5408\uff0c\u95ea\u7535\u51fa \u62db\uff0c\u4e00\u5251\u523a\u5411\u4ed6\u80c1\u4e0b\u7684\u201c\u6108\u6c14\u7a74\u201d\u3002 \u3000\u3000\u54ea\u77e5\u6df3\u4e8e\u5468\u7adf\u662f\u4e3e\u91cd\u82e5\u8f7b\uff0c\u8eab\u624b\u77eb\u6377\u4e4b\u6781\u3002\u53ea\u89c1\u4ed6\u624b\u6258\u94dc\u949f\uff0c\u811a\u8ddf\u4e00\u4e2a\u76d8\u65cb\uff0c\u201c\u545c\u547c\u201d \u4e24\u58f0\uff0c\u4e24\u7ffc\u88ad\u6765\u7684\u4e24\u67c4\u957f\u5251\uff0c\u90fd\u7ed9\u94dc\u949f\u8361\u5f00\uff01\u674e\u601d\u5357\u8fd8\u4e0d\u89c9\u5f97\u600e\u4e48\uff0c\u5b5f\u660e\u971e\u5374\u662f\u864e\u53e3\u4e00\u9ebb\uff0c \u957f\u5251\u9669\u4e9b\u638c\u63e1\u4e0d\u7262\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u6df3\u4e8e\u5468\u7684\u94dc\u949f\u5df2\u662f\u5411\u674e\u601d\u5357\u63a8\u6765\uff0c\u674e\u601d\u5357\u4e00\u98d8\u4e00\u95ea\uff0c\u65bd\u5c55\u8f7b\u7075\u8fc5\u6377\u7684 \u5251\u672f\uff0c\u5251\u5c16\u4e0a\u53ea\u7528\u4e86\u4e24\u4e09\u5206\u529b\u9053\uff0c\u907f\u514d\u4ee5\u529b\u78b0\u529b\uff0c\u521a\u5219\u6613\u6298\u3002 \u3000\u3000\u6df3\u4e8e\u5468\u8eab\u5b50\u6ef4\u6e9c\u6e9c\u4e00\u8f6c\uff0c\u53ea\u542c\u53ee\u53ee\u94db\u94db\u4e4b\u58f0\u4e0d\u7edd\u4e8e\u8033\uff0c\u5b9b\u5982\u594f\u4e50\u3002\u674e\u3001\u5b5f\u4e8c\u4eba\u610f\u6b32\u4e58\u6687 \u62b5\u9699\uff0c\u653b\u4ed6\u7a7a\u95e8\uff0c\u4e0d\u6599\u4ecd\u7136\u90fd\u662f\u523a\u5728\u94dc\u949f\u4e4b\u4e0a\u3002\u5e78\u5f13\u4ed6\u4eec\u529b\u9053\u6ca1\u6709\u7528\u8db3\u3002\u90a3\u94dc\u949f\u7684\u53cd\u9707\u4e4b\u529b \u8fd8\u53ef\u4ee5\u7ecf\u53d7\u5f97\u8d77\u3002\u7fa4\u96c4\u6697\u6697\u5403\u60ca\uff0c\u4ff1\u662f\u60f3\u9053\uff1a\u201c\u5982\u6b64\u6253\u6cd5\uff0c\u6df3\u4e8e\u5468\u5c82\u975e\u5df2\u662f\u7acb\u4e8e\u4e0d\u8d25\u4e4b \u5730\uff1f\u201d \u3000\u3000\u674e\u3001\u5b5f\u4e8c\u4eba\u89c1\u523a\u4ed6\u4e0d\u7740\uff0c\u5251\u62db\u7acb\u53d8\uff0c\u6bcf\u4e00\u62db\u90fd\u662f\u865a\u865a\u5b9e\u5b9e\uff0c\u610f\u5728\u5251\u5148\uff0c\u4e0d\u78b0\u4ed6\u7684\u94dc\u949f\uff0c \u53ea\u662f\u7559\u5fc3\u5bfb\u89c5\u4ed6\u7684\u7834\u7efd\u3002\u6df3\u4e8e\u5468\u867d\u80fd\u4e3e\u91cd\u82e5\u8f7b\uff0c\u4f46\u94dc\u949f\u6bd5\u7adf\u662f\u7b28\u91cd\u4e4b\u7269\uff0c\u65e0\u8bba\u5982\u4f55\uff0c\u4e5f\u4e0d\u80fd \u4f7f\u5f97\u4e00\u4f3c\u5200\u5251\u7684\u7075\u6d3b\u3002 \u3000\u3000\u662f\u4ee5\u6df3\u4e8e\u5468\u7528\u767e\u591a\u65a4\u91cd\u7684\u94dc\u949f\u4f5c\u5175\u5668\uff0c\u9632\u5fa1\u6709\u4f59\uff0c\u653b\u51fb\u5374\u662f\u4e0d\u8db3\u3002\u674e\u3001\u5b5f\u4e8c\u4eba\u4e0d\u78b0\u4ed6\u7684 \u94dc\u949f\uff0c\u6c14\u529b\u635f\u8017\u4e0d\u5927\u3002\u4f46\u5018\u82e5\u4ed6\u68a2\u6709\u7834\u7efd\uff0c\u674e\u3001\u5b5f\u4e8c\u4eba\u7684\u865a\u62db\u7acb\u5373\u5c31\u53ef\u53d8\u4f5c\u5b9e\u62db\uff0c\u4e58\u6687\u62b5 \u9699\uff0c\u653b\u4ed6\u4e00\u4e2a\u63aa\u624b\u4e0d\u53ca\u3002 \u3000\u3000\u8fd9\u6837\u7684\u6253\u6cd5\u7684\u786e\u662f\u522b\u5f00\u751f\u9762\uff0c\u6fc0\u6597\u4e2d\u6df3\u4e8e\u5468\u6b3a\u8d1f\u5b5f\u660e\u971e\u529b\u5f31\uff0c\u94dc\u949f\u5411\u5979\u63a8\u53bb\u3002\u54ea\u77e5\u5b5f\u660e \u971e\u6c14\u529b\u867d\u5f31\uff0c\u8f7b\u529f\u5374\u662f\u751a\u4e3a\u7cbe\u5999\u3002 \u3000\u3000\u53ea\u542c\u5f97\u201c\u547c\u201d\u7684\u4e00\u58f0\uff0c\u5b5f\u660e\u971e\u7684\u7ea4\u8db3\u4e00\u70b9\u94dc\u949f\u7684\u4e0a\u65b9\uff0c\u8eab\u5f62\u5df2\u662f\u7fe9\u5982\u98de\u9e1f\u822c\u5730\u4ece\u6df3\u4e8e\u5468 \u5934\u9876\u8d8a\u8fc7\u3002\u94dc\u949f\u7684\u529b\u9053\u5728\u4e8e\u6b63\u9762\uff0c\u6025\u5207\u4e4b\u95f4\uff0c\u51b3\u4e0d\u80fd\u628a\u529b\u9053\u7acb\u5373\u8f6c\u79fb\u65b9\u5411\u63a8\u5411\u4e0a\u65b9\uff0c\u662f\u4ee5\u5b5f \u660e\u971e\u8fd9\u62db\u770b\u6765\u60ca\u9669\uff0c\u5176\u5b9e\u5374\u662f\u5c65\u9669\u5982\u5937\u3002 \u3000\u3000\u5b5f\u660e\u971e\u98de\u8fc7\u6df3\u4e8e\u5468\u5934\u9876\uff0c\u811a\u672a\u6cbe\u5730\uff0c\u5251\u5c16\u5df1\u662f\u523a\u5230\u4e86\u80cc\u5fc3\u7684\u201c\u5927\u690e\u7a74\u201d\uff0c\u6df3\u4e8e\u5468\u7684\u94dc\u949f \u6321\u5f00\u4e86\u674e\u601d\u5357\u524d\u9762\u523a\u6765\u7684\u5251\u62db\uff0c\u53cd\u624b\u4e00\u62ff\uff0c\u4f7f\u51fa\u7a7a\u624b\u5165\u767d\u5203\u7684\u529f\u592b\u5f3a\u593a\u5b5f\u660e\u971e\u7684\u957f\u5251\u3002\u80cc\u540e \u7adf\u4f3c\u957f\u7740\u773c\u775b\u4e00\u6837\uff0c\u64d2\u62ff\u624b\u6cd5\u4f7f\u5f97\u51cc\u5389\u975e\u5e38\uff0c\u5b5f\u660e\u971e\u4e00\u5251\u523a\u7a7a\uff0c\u7acb\u5373\u95ea\u5f00\uff0c\u53c8\u65bd\u4fa7\u51fb\u3002 \u3000\u3000\u6df3\u4e8e\u5468\u7684\u6458\u62ff\u624b\u6cd5\u867d\u7136\u4e0d\u540c\u51e1\u54cd\uff0c\u4f46\u53cc\u624b\u5206\u5f00\uff0c\u5355\u81c2\u6258\u7740\u94dc\u949f\uff0c\u529b\u9053\u81ea\u662f\u5927\u51cf\u3002\u674e\u3001\u5b5f \u4e8c\u4eba\u524d\u540e\u5939\u653b\uff0c\u767b\u65f6\u626d\u8f6c\u4e86\u52a3\u52bf\uff0c\u5341\u6570\u62db\u4e00\u8fc7\uff0c\u6df3\u4e8e\u5468\u5e94\u4ed8\u4e0d\u6687\uff0c\u597d\u51e0\u6b21\u9669\u4e9b\u7ed9\u674e\u601d\u5357\u523a \u4e2d\u3002 \u3000\u3000\u6df3\u4e8e\u5468\u6697\u6697\u53eb\u4e86\u4e00\u58f0\uff1a\u201c\u4e0d\u597d\uff01\u201d\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4ed6\u4eec\u7528\u7ed5\u8eab\u6e38\u6597\u7684\u6cd5\u5b50\uff0c\u6211\u7684\u94dc\u949f\u7b28 \u91cd\uff0c\u5374\u662f\u53d1\u6325\u4e0d\u4e86\u5a01\u529b\uff0c\u4e45\u6218\u4e0b\u53bb\uff0c\u53ea\u6015\u96be\u514d\u6709\u4e00\u4e24\u4e2a\u7834\u7efd\uff0c\u7ed9\u4ed6\u4eec\u4e58\u865a\u800c\u8fdb\u3002\u201d \u3000\u3000\u5fc3\u5ff5\u4e00\u8f6c\uff0c\u6218\u672f\u7acb\u6539\u3002\u674e\u601d\u5357\u6b63\u81ea\u4f7f\u7528\u865a\u865a\u5b9e\u5b9e\u7684\u5251\u6cd5\uff0c\u4ece\u6b63\u9762\u5411\u4ed6\u4f5c\u8bd5\u63a2\u6027\u7684\u8fdb\u653b\uff0c \u6df3\u4e8e\u5468\u5ffd\u5730\u5927\u559d\u4e00\u58f0\uff0c\u7a81\u7136\u628a\u94dc\u949f\u63b7\u51fa\uff0c\u949f\u53e3\u671d\u5730\uff0c\u5411\u674e\u601d\u5357\u5f53\u5934\u7f69\u4e0b\u3002 \u3000\u3000\u674e\u601d\u5357\u5927\u5403\u4e00\u60ca\uff0c\u5e78\u4e8f\u4ed6\u8df3\u8dc3\u5f97\u5feb\uff0c\u5728\u95f4\u4e0d\u5bb9\u53d1\u4e4b\u9645\u4e00\u95ea\u95ea\u5f00\u3002\u5b5f\u660e\u971e\u4e00\u5251\u523a\u5230\u6df3\u4e8e\u5468 \u80cc\u540e\uff0c\u6df3\u4e8e\u5468\u8df3\u4e0a\u4e24\u6b65\uff0c\u628a\u90a3\u94dc\u949f\u4e00\u63a8\uff0c\u94dc\u949f\u672a\u66fe\u843d\u5730\uff0c\u7ed9\u4ed6\u63a8\u52a8\uff0c\u8f6c\u79fb\u4e86\u65b9\u5411\uff0c\u53c8\u5411\u5b5f\u660e \u971e\u98de\u6765\u3002 \u3000\u3000\u5b5f\u660e\u971e\u4f7f\u51fa\u8d85\u5999\u8f7b\u529f\uff0c\u8dc3\u8d77\u4e00\u4e08\u591a\u9ad8\uff0c\u6070\u6070\u4ece\u949f\u9876\u63a0\u8fc7\uff0c\u4f46\u5251\u5c16\u5374\u5df2\u7ed9\u94dc\u949f\u64e6\u4e86\u4e00\u4e0b\uff0c \u9707\u5f97\u5979\u864e\u53e3\u6d41\u8840\uff0c\u843d\u5730\u4e4b\u65f6\uff0c\u811a\u5c16\u51e0\u4e4e\u7ad9\u7acb\u4e0d\u7a33\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u6df3\u4e8e\u5468\u4e00\u62f3\u6253\u51fa\uff0c\u51fb\u5728\u94dc\u949f\u4e4b\u4e0a\uff0c\u94dc\u949f\u53c8\u5411\u674e\u601d\u5357\u98de\u6765\u3002\u674e\u601d\u5357\u5012\u7eb5 \u907f\u5f00\uff0c\u6df3\u4e8e\u5468\u98de\u5feb\u5730\u8d76\u4e0a\u53bb\uff0c\u5728\u94dc\u949f\u4e0a\u8f7b\u8f7b\u5730\u4e00\u63a8\u4e00\u62e8\uff0c\u529b\u9053\u79fb\u8f6c\uff0c\u94dc\u949f\u7b2c\u4e8c\u6b21\u5411\u5b5f\u660e\u971e\u538b \u4e0b\u3002 \u3000\u3000\u5b5f\u660e\u971e\u521a\u521a\u7ad9\u7a33\uff0c\u5598\u606f\u672a\u5b9a\uff0c\u94dc\u949f\u98de\u5230\uff0c\u53ea\u597d\u9003\u907f\u3002\u4f46\u5979\u6c14\u529b\u4e0d\u52a0\uff0c\u8fd9\u4e00\u8dc3\u672a\u80fd\u53ca\u8fdc\uff0c \u8033\u542c\u5f97\u547c\u547c\u98ce\u54cd\uff0c\u90a3\u80a1\u5927\u529b\u4f3c\u4e4e\u5c31\u8981\u649e\u5230\u5979\u7684\u80cc\uff0c\u5b5f\u660e\u971e\u5fc3\u91cc\u4e00\u51c9\uff0c\u53ea\u9053\u6027\u547d\u96be\u4fdd\u3002\u5ffd\u89c9\u8eab \u5b50\u4e00\u8f7b\uff0c\u539f\u6765\u662f\u674e\u601d\u5357\u98de\u8eab\u63a0\u6765\uff0c\u8f7b\u8f7b\u5c06\u5979\u4e00\u5e26\uff0c\u4e24\u4eba\u4f7f\u51fa\u201c\u6bd4\u7ffc\u53cc\u98de\u201d\u7684\u8f7b\u529f\uff0c\u9003\u51fa\u4e86\u94dc \u949f\u8ffd\u51fb\u7684\u8303\u56f4\u4e4b\u5916\u3002 \u3000\u3000\u674e\u3001\u5b5f\u4e8c\u4eba\uff0c\u4ece\u751f\u5230\u6b7b\uff0c\u4ece\u6b7b\u5230\u751f\u8d70\u4e86\u4e00\u4e2a\u5faa\u73af\u3002\u5728\u8fd9\u77ac\u95f4\uff0c\u60ca\u547c\u4e4b\u58f0\u4e0e\u559d\u5f69\u4e4b\u58f0\u4ea4 \u4f5c\uff0c\u4eba\u4eba\u90fd\u662f\u770b\u5f97\u60ca\u5fc3\u52a8\u9b44\u3002\u521a\u521a\u5728\u4e3a\u674e\u3001\u5b5f\u4e8c\u4eba\u62c5\u5fe7\u800c\u60ca\u547c\uff0c\u8ddf\u7740\u53c8\u4e3a\u4ed6\u4eec\u8fd9\u62db\u7edd\u5999\u7684\u8f7b \u529f\u800c\u559d\u5f69\u3002 \u3000\u3000\u5728\u60ca\u547c\u4e0e\u559d\u5f69\u58f0\u4e2d\uff0c\u674e\u601d\u5357\u9690\u9690\u542c\u5f97\u4e00\u58f0\u5c16\u9510\u7684\u60ca\u53eb\uff0c\u662f\u4e00\u4e2a\u5973\u5b50\u7684\u58f0\u97f3\uff0c\u542c\u6765\u7adf\u4f3c\u9887 \u4e3a\u719f\u6089\u3002\u674e\u601d\u5357\u5fc3\u623f\u8df3\u4e86\u4e00\u8df3\uff0c\u4f46\u5728\u8fd9\u6027\u547d\u4fc4\u9877\u4e4b\u95f4\uff0c\u5374\u4e5f\u65e0\u6687\u53bb\u601d\u7d22\u8fd9\u662f\u8c01\u7684\u58f0\u97f3\u4e86\u3002 \u3000\u3000\u5c60\u51e4\u6d51\u8eab\u51b7\u6c57\uff0c\u5fc3\u91cc\u60f3\u53eb\u674e\u601d\u5357\u548c\u5b5f\u660e\u971e\u4e0d\u8981\u518d\u6253\uff0c\u8d76\u5feb\u56de\u6765\u3002\u4f46\u5982\u679c\u4ed6\u4eec\u8ba4\u8f93\u7684\u8bdd\uff0c \u6027\u547d\u867d\u53ef\u4fdd\u5168\uff0c\u8fd9\u76df\u4e3b\u4e4b\u4f4d\u5374\u662f\u6df3\u4e8e\u5468\u5750\u5b9a\u7684\u4e86\u3002\u6bd4\u6b66\u7684\u89c4\u77e9\uff0c\u867d\u7136\u53ef\u4ee5\u6253\u5230\u6700\u540e\u4e00\u4eba\uff0c\u4f46 \u674e\u3001\u5b5f\u8054\u624b\uff0c\u90fd\u5df2\u8f93\u4e86\uff0c\u8fd8\u6709\u8c01\u4eba\u6562\u53bb\u9001\u547d\uff1f \u3000\u3000\u5c60\u51e4\u6b63\u60f3\u4e0d\u987e\u4e00\u5207\uff0c\u53eb\u4ed6\u4eec\u56de\u6765\uff0c\u573a\u4e2d\u53c8\u5df2\u7ecf\u8fc7\u4e86\u51e0\u6b21\u60ca\u9669\u7684\u573a\u9762\u3002\u674e\u3001\u5b5f\u4e8c\u4eba\u867d\u65e0\u8fd8 \u51fb\u4e4b\u529b\uff0c\u4f46\u5374\u4e5f\u80fd\u591f\u907f\u5f00\u4e86\u94dc\u949f\u7684\u8ffd\u51fb\u3002\u539f\u6765\u4ed6\u4eec\u56e0\u4e3a\u4ece\u672a\u78b0\u8fc7\u8fd9\u79cd\u6253\u6cd5\uff0c\u6700\u521d\u672a\u514d\u5403\u60ca\uff0c \u5982\u4eca\u6e10\u6e10\u9547\u5b9a\u4e0b\u6765\uff0c\u4e24\u4eba\u5f7c\u6b64\u76f8\u52a9\uff0c\u7ed5\u573a\u75be\u8d70\uff0c\u4f7f\u51fa\u201c\u79fb\u6b65\u6362\u5f62\u201d\u7684\u8f7b\u529f\u8eab\u6cd5\uff0c\u8dd1\u4e00\u6b65\u5c31\u8f6c \u4e00\u4e2a\u65b9\u4f4d\uff0c\u6df3\u4e8e\u5468\u7684\u94dc\u949f\u5fc5\u987b\u7528\u638c\u529b\u63a8\u52a8\uff0c\u624d\u80fd\u8f6c\u5f2f\uff0c\u6025\u5207\u4e4b\u95f4\uff0c\u5f53\u7136\u662f\u4e0d\u80fd\u78b0\u7740\u4ed6\u4eec\u3002 \u3000\u3000\u5f62\u52bf\u867d\u7136\u597d\u4e9b\uff0c\u4f46\u5371\u9669\u4ecd\u7136\u672a\u8fc7\uff0c\u8981\u77e5\u5b5f\u660e\u971e\u5df2\u662f\u6c14\u529b\u4e0d\u52a0\uff0c\u674e\u601d\u5357\u4ea6\u5c06\u5230\u4e86\u5f3a\u5f29\u4e4b\u672b \u7684\u7530\u5730\uff0c\u4ed6\u4eec\u7ed5\u573a\u75be\u8d70\uff0c\u5018\u6709\u4e00\u6b65\u8d70\u5f97\u8f83\u6162\uff0c\u6216\u8005\u95ea\u907f\u5f97\u4e0d\u9002\u5b9c\uff0c\u9a6c\u4e0a\u5c31\u6709\u7ed9\u94a2\u949f\u538b\u6210\u8089\u997c \u7684\u5371\u9669\u3002 \u3000\u3000\u4f46\u6df3\u4e8e\u5468\u8fd9\u6837\u6253\u6cd5\uff0c\u4e00\u638c\u51fb\u94dc\u949f\uff0c\u8ffd\u9010\u654c\u4eba\u2014\u2014\u4e5f\u662f\u9887\u8017\u6c14\u529b\u3002\u56e0\u6b64\u80dc\u8d1f\u7684\u5173\u952e\u5728\u4e8e\u8c01 \u7684\u6c14\u529b\u80fd\u591f\u6301\u4e45\u3002\u5047\u5982\u6df3\u4e8e\u5468\u9996\u5148\u652f\u6301\u4e0d\u4f4f\uff0c\u65e0\u6cd5\u4ee5\u638c\u529b\u63a8\u52a8\u94dc\u949f\u7684\u8bdd\uff0c\u4ed6\u624b\u62ff\u94dc\u949f\u9632\u5fa1\uff0c \u674e\u3001\u5b5f\u4e8c\u4eba\u8054\u624b\u53cd\u653b\uff0c\u5c31\u5927\u6709\u53ef\u4ee5\u53d6\u80dc\u4e4b\u673a\u3002 \u3000\u3000\u5f53\u7136\u8fd9\u4e2a\u673a\u4f1a\u662f\u5f88\u5c0f\u7684\uff0c\u674e\u601d\u5357\u5df2\u7ecf\u6253\u4e86\u4e24\u573a\uff0c\u5b5f\u660e\u971e\u662f\u4e2a\u5973\u5b50\uff0c\u6c14\u529b\u66f4\u5f31\uff0c\u800c\u4e14\u5979\u4e5f \u66fe\u7ecf\u6253\u4e86\u4e00\u573a\uff0c\u5927\u5bb6\u8fd9\u6837\u7684\u8017\u4e0b\u53bb\uff0c\u4e00\u5b9a\u662f\u674e\u3001\u5b5f\u4e8c\u4eba\u96be\u4ee5\u652f\u6301\u3002\u4e0d\u8fc7\u6df3\u4e8e\u5468\uff0c\u4e00\u6765\u662f\u8001\u524d \u8f88\uff0c\u4e8c\u6765\u662f\u751f\u529b\u519b\uff0c\u5373\u4f7f\u53ef\u4ee5\u51fb\u8d25\u674e\u3001\u5b5f\u4e8c\u4eba\uff0c\u53ea\u8981\u8fc7\u4e86\u767e\u62db\u4e4b\u5916\uff0c\u4ed6\u4e5f\u662f\u80dc\u4e4b\u4e0d\u6b66\u4e86\u3002 \u3000\u3000\u6df3\u4e8e\u5468\u662f\u4e2a\u6700\u8981\u4f53\u9762\u7684\u4eba\uff0c\u60f3\u5230\u4e86\u8fd9\u5c42\uff0c\u5fc3\u91cc\u4e0d\u89c9\u6709\u70b9\u6025\u8e81\u3002\u6709\u4e00\u638c\u4ed6\u7684\u638c\u529b\u53d1\u5f97\u592a \u731b\uff0c\u94dc\u949f\u98de\u51fa\uff0c\u53bb\u52bf\u6781\u901f\uff0c\u4ed6\u81ea\u5df1\u7684\u811a\u6b65\u4e00\u65f6\u4e5f\u8ffd\u8d76\u4e0d\u4e0a\u3002\u674e\u601d\u5357\u770b\u51fa\u7834\u7efd\uff0c\u7a81\u7136\u4ece\u94dc\u949f\u65c1 \u8fb9\u7ed5\u8fc7\uff0c\u95ea\u7535\u822c\u7684\u4e00\u4e2a\u8d77\u4f0f\uff0c\u5c31\u5230\u4e86\u6df3\u4e8e\u5468\u8ddf\u524d\uff0c\u4e3e\u5251\u4fbf\u523a\u3002 \u3000\u3000\u4ee5\u638c\u529b\u63a8\u52a8\u94dc\u949f\u51fb\u654c\uff0c\u81ea\u5df1\u5fc5\u987b\u4e0e\u94dc\u949f\u4fdd\u6301\u9002\u5f53\u7684\u8ddd\u79bb\uff0c\u65b9\u80fd\u63a7\u5236\u81ea\u5982\u3002\u6b64\u9645\uff0c\u6df3\u4e8e\u5468 \u56e0\u4e3a\u672a\u80fd\u7d27\u8ddf\u94dc\u949f\uff0c\u7ed9\u674e\u601d\u5357\u6740\u5230\u4e86\u9762\u524d\uff0c\u90a3\u5c31\u53ea\u6709\u7a7a\u624b\u62b5\u654c\u4e86\u3002 \u3000\u3000\u94dc\u949f\u5931\u4e86\u63a7\u5236\uff0c\u5411\u573a\u5916\u98de\u51fa\uff0c\u65c1\u89c2\u7684\u4eba\u5413\u5f97\u7eb7\u7eb7\u8eb2\u907f\uff0c\u867d\u7136\u8eb2\u907f\uff0c\u4f46\u6709\u51e0\u4e2a\u4eba\u662f\u8dd1\u5f97\u4e0d \u591f\u5feb\u7684\uff0c\u773c\u770b\u8fd9\u51e0\u4e2a\u4eba\u5c31\u8981\u7ed9\u94dc\u949f\u538b\u6bd9\u3002\u5c31\u5728\u60ca\u53eb\u58f0\u4e2d\uff0c\u5ffd\u89c1\u4e00\u6761\u4eba\u5f71\u51cc\u7a7a\u4e0b\u964d\uff0c\u5f53\u771f\u662f\u5154 \u8d77\u9e58\u843d\uff0c\u5feb\u5230\u4e86\u6781\u70b9\uff0c\u5f85\u5230\u4f17\u4eba\u770b\u5f97\u6e05\u695a\u4e4b\u65f6\uff0c\u53ea\u542c\u5f97\u201c\u94db\u201d\u7684\u4e00\u58f0\u5de8\u54cd\uff0c\u94dc\u949f\u5df2\u7ecf\u843d\u4e0b\u5730 \u6765\u3002\u6709\u4e00\u4e2a\u4eba\u5927\u9a6c\u91d1\u5200\u7684\u5750\u5728\u94dc\u949f\u4e4b\u4e0a\u3002\u7fa4\u96c4\u53c8\u60ca\u53c8\u559c\uff0c\u4e0d\u7ea6\u800c\u540c\u5730\u53eb\u9053\uff1a\u201c\u5b5f\u5927\u4fa0\uff01\u201d \u3000\u3000\u539f\u6765\u8fd9\u4e2a\u4eba\u6b63\u662f\u5b5f\u660e\u971e\u7684\u7236\u4eb2\u5b5f\u5c11\u521a\uff0c\u4ed6\u5230\u573a\u65f6\uff0c\u6070\u5de7\u770b\u89c1\u94dc\u949f\u98de\u51fa\u573a\u5916\uff0c\u4ed6\u4e00\u8dc3\u4e09\u4e08 \u591a\u9ad8\uff0c\u8fd0\u7528\u5343\u65a4\u5760\u7684\u91cd\u8eab\u6cd5\uff0c\u786c\u751f\u751f\u5c31\u628a\u94dc\u949f\u538b\u4e0b\u6765\u4e86\u3002 \u3000\u3000\u5b5f\u660e\u971e\u6b63\u81ea\u6301\u5251\u5411\u6df3\u4e8e\u5468\u5954\u53bb\uff0c\u770b\u89c1\u7236\u4eb2\u6765\u4e86\uff0c\u559c\u51fa\u671b\u5916\uff0c\u811a\u6b65\u4e0d\u81ea\u89c9\u505c\u4e86\u4e00\u4e0b\uff0c\u62ac\u773c \u671b\u5979\u7236\u4eb2\u3002\u674e\u6069\u5357\u5df2\u662f\u5f3a\u5f29\u4e4b\u672b\uff0c\u4e00\u4e2a\u4eba\u62b5\u654c\u4e0d\u4f4f\u6df3\u4e8e\u5468\u7684\u638c\u529b\uff0c\u88ab\u4ed6\u4e2d\u6307\u4e00\u5f39\uff0c\u957f\u5251\u8131\u624b \u98de\u51fa\u3002 \u3000\u3000\u5b5f\u660e\u971e\u731b\u5730\u4e00\u7701\uff0c\u8fde\u5fd9\u63a5\u4e0b\u674e\u601d\u5357\u7684\u5251\uff0c\u5954\u4e0a\u524d\u53bb\uff0c\u6321\u4e86\u4e00\u62db\uff0c\u53cd\u624b\u628a\u957f\u5251\u9012\u7ed9\u674e\u601d \u5357\u3002\u674e\u6069\u5357\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u4e00\u573a\u80dc\u8d1f\u672a\u5206\uff0c\u54b1\u4eec\u548c\u4ed6\u6253\u5230\u5e95\uff01\u201d \u3000\u3000\u5b5f\u5c11\u521a\u63d0\u8d77\u94dc\u949f\uff0c\u8d70\u8fdb\u573a\u6765\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6df3\u4e8e\u5468\uff0c\u4f60\u7684\u5175\u5668\u90fd\u5df2\u5931\u4e86\uff0c\u8fd8\u597d\u610f\u601d\u518d\u6253 \u4e0b\u53bb\u4e48\uff1f\u201d \u3000\u3000\u6df3\u4e8e\u5468\u6012\u9053\uff1a\u201c\u96be\u9053\u8fd9\u5c31\u7b97\u4f5c\u662f\u6211\u8f93\u4e86\u4e0d\u6210\uff1f\u201d\u8981\u77e5\u4ed6\u6b64\u523b\u867d\u7136\u662f\u4ee5\u4e00\u53cc\u8089\u638c\u5bf9\u4ed8\u674e\u3001 \u5b5f\u4e8c\u4eba\u7684\u957f\u5251\uff0c\u5982\u8fd8\u662f\u4ed6\u7a0d\u7a0d\u5360\u4e86\u4e00\u70b9\u4e0a\u98ce\uff0c\u6bd4\u6b66\u7684\u89c4\u77e9\u5e76\u65e0\u89c4\u5b9a\u4e00\u4e2a\u4eba\u59cb\u7ec8\u8981\u7528\u4e00\u4ef6\u5175 \u5668\u3002 \u3000\u3000\u5b5f\u5c11\u521a\u9053\uff0c\u201c\u597d\uff0c\u90a3\u5c31\u7b97\u4ed6\u4eec\u8f93\u4e86\u4e5f\u6210\uff0c\u4e0b\u4e00\u573a\u6211\u6765\u966a\u4f60\u73a9\u73a9\u3002\u201d\u5b5f\u660e\u971e\u9053\uff1a\u201c\u7239\uff0c\u600e \u80fd\u5c31\u7b97\u662f\u6211\u4eec\u8f93\u4e86\uff1f\u201d \u3000\u3000\u6df3\u4e8e\u5468\u5927\u5927\u5403\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4e5f\u8981\u4e0b\u573a\u3002\u201d\u5b5f\u5c11\u521a\u9053\uff1a\u201c\u4f60\u65e2\u7136\u4e00\u5b9a\u8981\u6b3a\u8d1f\u6211\u7684\u5973\u513f\uff0c \u6211\u8fd8\u5c82\u80fd\u53ea\u662f\u548c\u4f60\u5f00\u5f00\u73a9\u7b11\uff01\u201d \u3000\u3000\u6df3\u4e8e\u5468\u8fde\u5fd9\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u8fd9\u4e00\u573a\u5c31\u7b97\u662f\u5e73\u624b\u597d\u4e86\u3002\u5b5f\u5927\u4fa0\uff0c\u4f60\u7684\u6c14\u53ef\u6d88\u4e86\u5427\uff1f\u201d \u3000\u3000\u5b5f\u5c11\u521a\u9053\uff1a\u201c\u6211\u65e2\u975e\u548c\u4f60\u6597\u6c14\uff0c\u4ea6\u975e\u4fe1\u53e3\u548c\u4f60\u5f00\u5f00\u73a9\u7b11\u3002\u4f60\u8981\u8d62\u4ed6\u4eec\u4e5f\u597d\uff0c\u5f53\u4f5c\u5e73\u624b\u4e5f \u597d\uff0c\u751a\u81f3\u8ba4\u8f93\u4e5f\u597d\uff0c\u603b\u4e4b\uff0c\u4e0b\u4e00\u573a\u6211\u975e\u548c\u4f60\u8f83\u91cf\u4e0d\u53ef\uff0c\u8fd9\u662f\u76df\u4e3b\u4e4b\u4e89\uff0c\u6211\u5e2e\u674e\u601d\u5357\u8fd9\u8fb9\uff0c\u6309 \u89c4\u77e9\u6211\u53ef\u4f3c\u548c\u4f60\u6253\u7b2c\u4e8c\u573a\u5427\uff1f\u201d\u540e\u9762\u8fd9\u51e0\u53e5\u8bdd\uff0c\u4ed6\u662f\u5411\u516c\u8bc1\u4eba\u4e4b\u4e00\u7684\u5434\u6052\u8bf4\u7684\u3002 \u3000\u3000\u5434\u6052\u53ea\u597d\u79c9\u516c\u8bf4\u9053\uff1a\u201c\u4ed6\u82e5\u8d62\u4e86\u8fd9\u573a\uff0c\u81f3\u5c11\u8fd8\u8981\u518d\u6253\u4e00\u573a\u624d\u80fd\u4f11\u606f\uff0c\u5b5f\u5927\u4fa0\u4f60\u5f53\u7136\u6709\u6743 \u5411\u4ed6\u6311\u6218\u3002\u4e0d\u8fc7\u4ed6\u8fd9\u4e00\u573a\u82e5\u662f\u8f93\u4e86\uff0c\u4f60\u5f53\u7136\u4e5f\u5c31\u7528\u4e0d\u7740\u548c\u4ed6\u518d\u6253\u5566\u3002\u201d \u3000\u3000\u6df3\u4e8e\u5468\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6211\u867d\u7136\u6709\u628a\u63e1\u53ef\u4ee5\u80dc\u5f97\u4ed6\u4eec\uff0c\u4f46\u81f3\u5c11\u6050\u6015\u4e5f\u8981\u5728\u767e\u62db\u4e4b\u5916\u3002\u201d\u5176\u5b9e \u674e\u3001\u5b5f\u4e8c\u4eba\u5251\u672f\u7cbe\u5999\uff0c\u76ee\u524d\u6df3\u4e8e\u5468\u867d\u7136\u80fd\u4ee5\u6c14\u529b\u5360\u4f18\uff0c\u4f46\u7a7a\u624b\u5bf9\u4ed8\u53cc\u5251\uff0c\u603b\u662f\u6bd4\u8f83\u5403\u529b\uff0c\u4e45 \u6218\u4e0b\u53bb\uff0c\u8c01\u80dc\u8c01\u8d25\uff0c\u5b9e\u96be\u9884\u6599\u3002\u6df3\u4e8e\u5468\u81ea\u4ee5\u4e3a\u6709\u628a\u63e1\u53ef\u80dc\uff0c\u90a3\u53ea\u662f\u81ea\u5df1\u5b89\u6170\u81ea\u5df1\u7f62\u4e86\u3002 \u3000\u3000\u4ed6\u672c\u4ee5\u4e3a\u4ee5\u5b5f\u5c11\u521a\u8fd9\u6837\u7684\u8eab\u4efd\uff0c\u4e0d\u4f1a\u5728\u4ed6\u6253\u4e86\u4e00\u573a\u4e4b\u540e\uff0c\u5411\u4ed6\u6311\u6218\u7684\u3002\u6545\u6b64\u4ed6\u521a\u624d\u6253\u7b97 \u53ea\u4f24\u674e\u601d\u5357\u800c\u4e0d\u4f24\u5b5f\u660e\u971e\uff0c\u8fd9\u6837\u8d62\u4e86\uff0c\u5bf9\u5b5f\u5c11\u521a\u4e5f\u7b97\u662f\u5356\u4e00\u4e2a\u4eba\u60c5\u3002\u4f46\u5982\u4eca\u5b5f\u5c11\u521a\u65e2\u662f\u65e0\u8bba \u5982\u4f55\u4e5f\u8981\u548c\u4ed6\u8f83\u91cf\uff0c\u4ed6\u5c31\u4e0d\u80fd\u4e0d\u53e6\u4f5c\u6253\u7b97\u4e86\u3002 \u3000\u3000\u6253\u4e0b\u53bb\u7684\u8bdd\uff0c\u4ed6\u672a\u5fc5\u80fd\u591f\u7a33\u64cd\u80dc\u7b97\uff0c\u80dc\u4e86\uff0c\u4e5f\u5f97\u5728\u767e\u62db\u4e4b\u5916\uff0c\u6c14\u529b\u5f53\u7136\u5927\u4e3a\u8017\u635f\uff0c\u53c8\u5982 \u4f55\u80fd\u591f\u5bf9\u4ed8\u8fd9\u4e00\u4f4d\u201c\u795e\u5251\u201d\u9a70\u540d\u6c5f\u6e56\u7684\u5b5f\u5927\u4fa0\u5b5f\u5c11\u521a\uff1f \u3000\u3000\u53ef\u662f\u8981\u4ed6\u5411\u674e\u601d\u5357\u548c\u5b5f\u660e\u971e\u8ba4\u8f93\uff0c\u4ed6\u5f53\u7136\u53c8\u662f\u4e0d\u7518\u5fc3\u7684\u3002\u8fd9\u4e0d\u4ec5\u4ec5\u662f\u9762\u5b50\u95ee\u9898\uff0c\u800c\u4e14\u662f \u76df\u4e3b\u8c01\u5c5e\u7684\u95ee\u9898\uff0c\u4ed6\u4e00\u8ba4\u8f93\uff0c\u4ed6\u8fd9\u4e00\u8fb9\u6839\u672c\u5c31\u6ca1\u6709\u53ef\u4ee5\u51fa\u573a\u7684\u4eba\u4e86\u3002 \u3000\u3000\u4e24\u7686\u4e0d\u5999\uff0c\u65e0\u53ef\u5948\u4f55\uff0c\u6df3\u4e8e\u5468\u552f\u6709\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u8fd9\u4e00\u573a\u5f53\u4f5c\u5e73\u624b\u3002\u5b5f\u5927\u4fa0\uff0c\u4f60\u4e00\u5b9a\u8981\u4f38 \u91cf\u6211\uff0c\u4e0b\u4e00\u573a\u6211\u53ea\u597d\u820d\u547d\u5949\u966a\u4e86\u3002\u201d\u4ed6\u660e\u77e5\u81ea\u5df1\u6c14\u529b\u5145\u6c9b\u4e5f\u672a\u5fc5\u654c\u5f97\u8fc7\u5b5f\u5c11\u521a\uff0c\u4f46\u603b\u8fd8\u5b58\u6709 \u4e00\u70b9\u4fa5\u5e78\u7684\u5ff5\u5934\uff0c\u662f\u4ee5\u5b81\u613f\u6b64\u523b\u5c31\u7b97\u5e73\u624b\uff0c\u597d\u4fdd\u7559\u4e00\u70b9\u6c14\u529b\u7b49\u4e0b\u4e00\u573a\u5bf9\u4ed8\u5b5f\u5c11\u521a\u3002\u5b5f\u660e\u971e\u5728 \u5f62\u52bf\u4e0a\u5360\u4e86\u4e0a\u98ce\uff0c\u8fd8\u4e0d\u613f\u7f62\u624b\uff0c\u674e\u601d\u5357\u7b11\u9053\uff1a\u201c\u4ed6\u5230\u5e95\u662f\u8001\u524d\u8f88\uff0c\u5c31\u8ba9\u4ed6\u4e00\u70b9\uff0c\u5f53\u4f5c\u5e73\u624b\u3002 \u5426\u5219\u5c06\u4ed6\u6253\u8d25\uff0c\u4f60\u7239\u7239\u7684\u2018\u795e\u5251\u2019\uff0c\u6211\u5c31\u6ca1\u6709\u773c\u798f\u770b\u4e86\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u8fd9\u624d\u7b11\u9053\uff1a\u201c\u597d\u5427\uff0c\u770b\u5728\u7239\u7239\u7684\u4efd\u4e0a\uff0c\u90a3\u54b1\u4eec\u5c31\u4e0d\u5fc5\u96be\u4e3a\u4ed6\u4e86\u3002\u201d\u4e24\u4eba\u9000\u4e0b\uff0c\u548c \u5c60\u51e4\u7b49\u4eba\uff0c\u7ad9\u5728\u573a\u8fb9\u89c2\u6218\u3002 \u3000\u3000\u6df3\u4e8e\u5468\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u5b5f\u5927\u4fa0\u795e\u5251\u65e0\u654c\uff0c\u6df3\u4e8e\u5468\u4eca\u65e5\u672a\u5e26\u53cc\u94a9\uff0c\u8d25\u5728\u5b5f\u5927\u4fa0\u7684\u5251\u4e0b\uff0c\u90a3\u4e5f \u503c\u5f97\u3002\u201d \u3000\u3000\u5b5f\u5c11\u521a\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u5fc5\u62c5\u60ca\u5bb3\u6015\uff0c\u66f4\u4e0d\u5fc5\u7528\u8a00\u8bed\u6fc0\u6211\u3002\u5b5f\u67d0\u662f\u4f55\u7b49\u6837\u4eba\uff0c\u5c82\u80fd\u5360\u4f60\u7684\u4fbf \u5b9c\uff1f\u597d\uff0c\u6211\u5148\u95ee\u4f60\uff0c\u4f60\u7528\u4ec0\u4e48\u5175\u5668\uff1f\u201d \u3000\u3000\u6df3\u4e8e\u5468\u63d0\u8d77\u90a3\u53e3\u94dc\u949f\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u8fd8\u662f\u7528\u8fd9\u4e2a\u7b28\u5bb6\u4f19\u3002\u201d\u8981\u77e5\u6df3\u4e8e\u5468\u5373\u4f7f\u6709\u53cc\u94a9\u5728\u624b\uff0c \u4e5f\u662f\u51b3\u8ba1\u62b5\u6321\u4e0d\u4e86\u5b5f\u5c11\u521a\u90a3\u795e\u5947\u83ab\u6d4b\u7684\u5251\u6cd5\u7684\uff0c\u5012\u4e0d\u5982\u7528\u8fd9\u4e2a\u5927\u94dc\u949f\uff0c\u6216\u8bb8\u8fd8\u53ef\u4ee5\u7a0d\u7a0d\u5360\u4e00 \u70b9\u5175\u5668\u4e0a\u7684\u4fbf\u5b9c\u3002\u56e0\u4e3a\u4ed6\u5df2\u7ecf\u8bd5\u8fc7\u4e00\u573a\uff0c\u7528\u94dc\u949f\u5bf9\u4ed8\u5b5f\u660e\u971e\u548c\u674e\u601d\u5357\u7684\u53cc\u5251\uff0c\u5360\u4e86\u5f88\u5927\u7684\u4e0a \u98ce\u3002 \u3000\u3000\u5f53\u7136\u5b5f\u5c11\u521a\u4e0d\u662f\u5b5f\u660e\u971e\u548c\u674e\u601d\u5357\u6240\u80fd\u6bd4\u62df\uff0c\u4f46\u81f3\u591a\u4e5f\u4e0d\u8fc7\u7b49\u4e8e\u4e24\u4e2a\u674e\u601d\u5357\uff0c\u800c\u4e14\u4e00\u4eba\u5355 \u5251\uff0c\u4e0d\u80fd\u524d\u540e\u5939\u653b\uff0c\u4e5f\u6bd4\u8f83\u5bb9\u6613\u5bf9\u4ed8\u3002\u662f\u4ee5\u6df3\u4e8e\u5468\u81ea\u5fd6\uff0c\u7528\u8fd9\u53e3\u94dc\u949f\u62b5\u6321\u4ed6\u7684\u201c\u795e\u5251\u201d\uff0c\u7eb5 \u4e0d\u80fd\u80dc\uff0c\u4e5f\u8bb8\u5c1a\u53ef\u4ee5\u907f\u514d\u5403\u4e8f\u3002 \u3000\u3000\u5728\u573a\u7684\u4eba\u8fde\u6df3\u4e8e\u5468\u5728\u5185\uff0c\u90fd\u4ee5\u4e3a\u5b5f\u5c11\u521a\u5fc5\u662f\u7528\u5251\u65e0\u7591\uff0c\u6240\u4ee5\u6df3\u4e8e\u5468\u4e5f\u6ca1\u95ee\u4ed6\u7528\u7684\u4ec0\u4e48\u5175 \u5668\uff0c\u54ea\u77e5\u5b5f\u5c11\u521a\u5374\u9053\uff1a\u201c\u597d\uff0c\u4f60\u7528\u94dc\u949f\uff0c\u6211\u53ea\u51ed\u4e00\u53cc\u8089\u638c\uff0c\u5728\u5175\u5668\u4e0a\u6211\u8ba9\u4f60\u5148\u5360\u70b9\u4fbf\u5b9c\u3002\u4f60 \u8be5\u6ca1\u8bdd\u8bf4\u4e86\u5427\u3002\u201d \u3000\u3000\u6b64\u8a00\u4e00\u51fa\uff0c\u4f17\u4eba\u90fd\u662f\u5927\u4e3a\u60ca\u8be7\uff0c\u5fc3\u60f3\u94dc\u949f\u63a8\u538b\u4e4b\u529b\uff0c\u91cd\u903e\u5343\u65a4\uff0c\u5c82\u662f\u8840\u8089\u4e4b\u8eaf\u6240\u80fd\u62b5 \u654c\uff1f\u674e\u601d\u5357\u9053\uff1a\u201c\u53ef\u60dc\uff0c\u53ef\u60dc\uff0c\u4f60\u7239\u7239\u4e0d\u7528\u5251\uff0c\u54b1\u4eec\u53ef\u662f\u5c11\u4e86\u773c\u798f\u4e86\u3002\u201d\u5b5f\u660e\u971e\u9053\uff1a\u201c\u8c05\u8fd9 \u8001\u5339\u592b\u4e5f\u503c\u4e0d\u5f97\u6211\u7239\u7239\u7528\u5251\u3002\u201d \u3000\u3000\u6df3\u4e8e\u5468\u5fcd\u4f4f\u4e86\u6c14\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6211\u53ea\u6c42\u4e0d\u8d25\uff01\u4f55\u5fc5\u548c\u4f60\u4e89\u4e00\u65f6\u7684\u4f53\u9762\u3002\u201d\u4e8e\u662f\u8bf4\u9053\uff1a \u201c\u597d\uff0c\u90a3\u5c31\u8bf7\u5b5f\u5927\u4fa0\u53d1\u62db\u3002\u201d \u3000\u3000\u5b5f\u5c11\u521a\u5374\u53c8\u7b11\u9053\uff1a\u201c\u4e14\u6162\uff0c\u4f60\u662f\u6253\u4e86\u4e00\u573a\u7684\uff01\u5728\u6c14\u529b\u4e0a\u6211\u4e5f\u4e0d\u80fd\u5360\u4f60\u7684\u4fbf\u5b9c\uff0c\u8fd9\u6837\u5427\uff0c \u5c60\u59d1\u5a18\uff0c\u8bf7\u4f60\u628a\u4e00\u652f\u9999\u63d2\u5728\u5730\u4e0a\uff0c\u53ea\u80fd\u9732\u51fa\u4e09\u5206\u4e4b\u4e00\u3002\u201d \u3000\u3000\u5c60\u51e4\u83ab\u540d\u5176\u5999\uff0c\u4f9d\u8a00\u628a\u9999\u63d2\u5165\u6ce5\u4e2d\uff0c\u53ea\u9732\u51fa\u4e09\u5206\u4e4b\u4e00\u3002\u5b5f\u5c11\u521a\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u6211\u7a7a\u624b\u4e0e\u4f60 \u62fc\u6597\uff0c\u5730\u4e0a\u7684\u9999\u706b\u7184\u4e86\uff0c\u5c31\u7b97\u4f60\u8d62\uff01\u201d\u201c\u70e7\u4e00\u652f\u9999\u7684\u4e09\u5206\u4e4b\u4e00\uff0c\u6240\u7528\u7684\u65f6\u95f4\uff0c\u82e5\u662f\u7528\u5200\u5251\u8fc7 \u62db\u7684\u8bdd\uff0c\u6700\u591a\u4e0d\u8fc7\u4e09\u5341\u62db\u3002\u60f3\u6765\u5b5f\u5c11\u521a\u662f\u56e0\u4e3a\u5bf9\u65b9\u7528\u94dc\u949f\u5e94\u6218\uff0c\u65e0\u6cd5\u4e00\u62db\u4e00\u62db\u7684\u8ba1\u7b97\u62db\u6570\uff0c \u6545\u6b64\u63d0\u51fa\u8fd9\u4e2a\u9650\u5236\u7684\u529e\u6cd5\uff0c\u8ba9\u6df3\u4e8e\u5468\u5c3d\u5360\u4fbf\u5b9c\u3002 \u3000\u3000\u6df3\u4e8e\u5468\u6240\u6015\u7684\u6b63\u662f\u6c14\u529b\u4e0d\u80fd\u6301\u4e45\uff0c\u542c\u5f97\u5b5f\u5c11\u521a\u8fd9\u4e48\u4e00\u8bf4\uff0c\u4e0d\u7981\u53c8\u559c\u53c8\u6012\uff0c\u5fc3\u9053\uff1a\u201c\u4f60\u4e5f \u5927\u5c0f\u89d1\u6211\u4e86\uff0c\u6211\u80dc\u4f60\u4e0d\u5f97\uff0c\u96be\u9053\u4e00\u652f\u9999\u7684\u4e09\u5206\u4e4b\u4e00\u65f6\u523b\uff0c\u6211\u4e5f\u5b88\u4e0d\u4e86\u5417\uff1f\u201d\u4e8e\u662f\u8bf4\u9053\uff1a \u201c\u597d\uff0c\u8fd9\u662f\u4f60\u81ea\u5df1\u8bf4\u7684\uff0c\u4f60\u8f93\u4e86\u53ef\u6028\u4e0d\u5f97\u6211\uff01\u201d\u5b5f\u5c11\u521a\u53eb\u5c60\u51e4\u70b9\u4e86\u9999\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u9999\u706b \u4e00\u7184\uff0c\u6211\u5c31\u8ba4\u8f93\uff01\u5e9f\u8bdd\u5c11\u8bf4\uff0c\u8d76\u5feb\u52a8\u624b\uff01\u201d \u3000\u3000\u6df3\u4e8e\u5468\u5360\u4e86\u4fbf\u5b9c\uff0c\u81ea\u662f\u4e0d\u597d\u610f\u601d\u91c7\u7528\u62d6\u5ef6\u6218\u672f\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6211\u4e14\u653b\u4ed6\u6570\u62db\uff0c\u653b\u4ed6\u4e0d \u4e0b\uff0c\u518d\u5b88\u4e0d\u8fdf\u3002\u201d\u4e3b\u610f\u6253\u5b9a\uff0c\u63d0\u8d77\u94dc\u949f\uff0c\u7acb\u5373\u4ee5\u6cf0\u5c71\u538b\u9876\u4e4b\u52bf\uff0c\u5411\u5b5f\u5c11\u521a\u7838\u4e0b\u3002 \u3000\u3000\u5b5f\u5c11\u521a\u7adf\u7136\u4e0d\u8eb2\u4e0d\u95ea\uff0c\u53ea\u89c1\u4ed6\u8eab\u8eaf\u4e00\u77ee\uff0c\u5de6\u638c\u4e00\u62cd\u94dc\u949f\uff0c\u53f3\u62f3\u8ddf\u7740\u6363\u51fa\u3002\u53ea\u542c\u5f97\u201c\u94db\u201d \u7684\u4e00\u58f0\u5de8\u54cd\uff0c\u9707\u8033\u6b32\u804b\u3002\u5b5f\u5c11\u521a\u7ad9\u5728\u539f\u5730\uff0c\u5bf8\u6b65\u4e0d\u79fb\uff0c\u53cd\u800c\u662f\u6df3\u4e8e\u5468\u63a5\u8fde\u9000\u51fa\u4e86\u4e03\u516b\u6b65\u3002 \u3000\u3000\u539f\u6765\u5b5f\u5c11\u521a\u8fd9\u4e00\u62f3\u4e00\u638c\uff0c\u7528\u7684\u4e43\u662f\u521a\u67d4\u5e76\u6d4e\u7684\u529b\u9053\u3002\u5de6\u638c\u62cd\u7740\u94dc\u949f\uff0c\u8f7b\u8f7b\u4e00\u5e26\uff0c\u5df2\u662f\u628a \u5bf9\u65b9\u63a8\u6765\u7684\u90a3\u80a1\u521a\u731b\u529b\u9053\u5f15\u8fc7\u4e00\u8fb9\uff0c\u7136\u540e\u4e00\u62f3\u6363\u51fa\uff0c\u62f3\u529b\u80dc\u8fc7\u4e86\u5bf9\u65b9\u7684\u529b\u9053\uff0c\u4e24\u80a1\u529b\u9053\u52a0\u5728 \u4e00\u8d77\uff0c\u5168\u90fd\u53cd\u51fb\u56de\u53bb\uff0c\u8fd9\u6bd4\u501f\u529b\u6253\u529b\u8fd8\u8981\u5389\u5bb3\uff0c\u6df3\u4e8e\u5468\u5982\u4f55\u7ecf\u53d7\u5f97\u8d77\uff1f\u4ed6\u53ea\u9000\u6570\u6b65\uff0c\u5c1a\u672a\u8dcc \u5012\uff0c\u5df2\u7ecf\u5f88\u4e0d\u9519\u4e86\u3002 \u3000\u3000\u6df3\u4e8e\u5468\u5403\u4e86\u4e2a\u4e8f\uff0c\u7acb\u5373\u91c7\u53d6\u5b88\u52bf\uff0c\u94dc\u949f\u6321\u5728\u80f8\u524d\uff0c\u4e0d\u518d\u63a8\u51fa\u3002\u767b\u65f6\u559d\u5f69\u4e4b\u58f0\u4e0e\u7b11\u9a82\u4e4b\u58f0 \u4ea4\u4f5c\uff0c\u6709\u7684\u8bf4\u9053\uff1a\u201c\u597d\u4e0d\u8981\u8138\uff0c\u60f3\u62d6\u8fc7\u53bb\uff01\u201d\u6709\u7684\u8bf4\u9053\uff1a\u201c\u65e2\u6765\u4e89\u593a\u76df\u4e3b\uff0c\u5374\u505a\u7f29\u5934\u4e4c\u9f9f\uff0c \u7f9e\u4e5f\u4e0d\u7f9e\uff01\u201d\u6df3\u4e8e\u5468\u5bf9\u65c1\u4eba\u7684\u803b\u7b11\uff0c\u53ea\u5f53\u662f\u89c6\u800c\u4e0d\u89c1\uff0c\u542c\u800c\u4e0d\u95fb\u3002 \u3000\u3000\u5b5f\u5c11\u521a\u7b11\u9053\uff1a\u201c\u6df3\u4e8e\u8001\u513f\uff0c\u8fd9\u94dc\u949f\u4fdd\u62a4\u4e0d\u4e86\u4f60\uff0c\u4f60\u60f3\u505a\u7f29\u5934\u4e4c\u9f9f\u4e5f\u662f\u4e0d\u6210\uff01\u201d\u6b3a\u8eab\u76f4 \u8fdb\uff0c\u201c\u94db\u201d\u7684\u4e00\u58f0\uff0c\u62f3\u5934\u53c8\u51fb\u5728\u94dc\u949f\u4e4b\u4e0a\u3002 \u3000\u3000\u8fd9\u4e00\u62f3\u53ea\u662f\u5b5f\u5c11\u521a\u672c\u8eab\u7684\u529b\u9053\uff0c\u9694\u7740\u4e00\u53e3\u94dc\u949f\uff0c\u6df3\u4e8e\u5468\u52c9\u5f3a\u8fd8\u6328\u5f97\u8d77\uff0c\u4f46\u864e\u53e3\u4ea6\u5df2\u611f\u5230 \u9178\u9ebb\u4e86\u3002 \u3000\u3000\u5b5f\u5c11\u521a\u4e00\u91c7\u653b\u52bf\uff0c\u4fbf\u4f3c\u957f\u6c5f\u5927\u6cb3\u6eda\u6eda\u800c\u4e0a\uff0c\u6216\u7528\u62f3\u51fb\uff0c\u6216\u7528\u62ff\u62cd\uff0c\u8f6c\u77ac\u4e4b\u95f4\uff0c\u5df2\u662f\u5728\u94dc \u949f\u4e4b\u4e0a\u51fb\u4e86\u4e03\u62f3\uff0c\u62cd\u4e86\u516b\u638c\uff01\u53ee\u94db\u4e4b\u58f0\uff0c\u8fde\u7ef5\u4e0d\u65ad\uff0c\u9707\u8033\u5982\u96f7\uff0c\u65c1\u89c2\u8bf8\u4eba\uff0c\u4eba\u4eba\u90fd\u5835\u4f4f\u8033 \u6735\u3002\u4f46\u6df3\u4e8e\u5468\u77e5\u662f\u4e0d\u80fd\u3002\u6df3\u4e8e\u5468\u662f\u63d0\u7740\u94dc\u949f\u7684\uff0c\u5728\u949f\u58f0\u5927\u4f5c\u4e4b\u4e0b\uff0c\u5f53\u7136\u662f\u5341\u5206\u96be\u53d7\uff0c\u597d\u50cf\u8033 \u819c\u90e8\u9707\u7834\u4e86\u3002 \u3000\u3000\u4e0d\u8fc7\u7247\u523b\uff0c\u6df3\u4e8e\u5468\u53ea\u89c9\u4f53\u5185\u6c14\u8840\u7ffb\u6d8c\uff0c\u5b5f\u5c11\u521a\u6bcf\u4e00\u62f3\u51fb\u7740\u94dc\u949f\uff0c\u4ed6\u7684\u80f8\u53e3\u5c31\u5982\u540c\u53d7\u4e86\u5927 \u94c1\u9524\u7684\u4e00\u51fb\u3002\u6df3\u4e8e\u5468\u6697\u6697\u53eb\u58f0\u201c\u4e0d\u597d\u201d\uff0c\u8d81\u7740\u6c14\u529b\u5c1a\u672a\u8870\u7aed\uff0c\u767b\u65f6\u6539\u53d8\u6218\u672f\uff0c\u7528\u529b\u628a\u94dc\u949f\u63b7 \u51fa\u3002\u4ed6\u521a\u624d\u4ee5\u638c\u529b\u63a8\u52a8\u94dc\u949f\uff0c\u8ffd\u51fb\u674e\u3001\u5b5f\u4e8c\u4eba\uff0c\u9887\u5360\u4e0a\u98ce\uff0c\u6b64\u65f6\u65e2\u4e0d\u80fd\u5b88\uff0c\u53ea\u597d\u91cd\u65bd\u6545\u6280\u3002 \u5982\u6b64\u6253\u6cd5\uff0c\u867d\u7136\u66f4\u8d39\u6c14\u529b\uff0c\u4f46\u90a3\u4e00\u652f\u9999\u9732\u51fa\u5730\u9762\u7684\u5df2\u53ea\u5269\u4e0b\u77ed\u77ed\u4e00\u8282\uff0c\u6df3\u4e8e\u5468\u4f30\u91cf\u65f6\u95f4\uff0c\u53ea \u987b\u5b5f\u5c11\u521a\u8eb2\u907f\u4ed6\u4e09\u6b21\u7684\u629b\u63b7\uff0c\u9999\u706b\u5c31\u4f1a\u7184\u706d\u3002\u5b5f\u5c11\u521a\u6709\u8a00\u5728\u5148\uff0c\u9999\u706b\u4e00\u706d\uff0c\u4ed6\u5c31\u8981\u4f5c\u8f93\u7684\u3002 \u3000\u3000\u5982\u4e0d\u77e5\u5b5f\u5c11\u521a\u6b63\u662f\u8981\u4ed6\u5982\u6b64\uff0c\u94dc\u949f\u629b\u6765\uff0c\u5b5f\u5c11\u521a\u4e00\u58f0\u957f\u5578\uff0c\u559d\u9053\uff1a\u201c\u6765\u5f97\u597d\uff01\u201d\u98de\u8eab\u8dc3 \u8d77\u8fce\u7740\u7167\u9762\u98de\u6765\u7684\u94dc\u949f\uff0c\u4f7f\u51fa\u4e0a\u4e58\u6b66\u529f\u4e2d\u8f6c\u79fb\u529b\u9053\u7684\u529f\u592b\uff0c\u53ea\u662f\u8f7b\u8f7b\u4e00\u638c\uff0c\u90a3\u53e3\u94dc\u949f\u767b\u65f6\u60bc \u8f6c\u65b9\u5411\uff0c\u53cd\u800c\u5411\u6df3\u4e8e\u5468\u98de\u53bb\u3002\u6df3\u4e8e\u5468\u811a\u8e0f\u201c\u4e4b\u201d\u5b57\u5f62\uff0c\u7ed5\u573a\u75be\u8d70\uff0c\u867d\u7136\u72fc\u72c8\u975e\u5e38\uff0c\u5374\u4e5f\u907f\u5f00 \u4e86\u94dc\u949f\u7684\u53cd\u51fb\u3002 \u3000\u3000\u8463\u5f00\u5c71\u559d\u5f69\u9053\uff1a\u201c\u597d\u5440\uff0c\u8fd9\u6b63\u662f\u4ee5\u5176\u4eba\u4e4b\u9053\u8fd8\u6cbb\u5176\u4eba\u4e4b\u8eab\uff01\u201d\u5b5f\u660e\u971e\u7b11\u9053\uff1a\u201c\u6df3\u4e8e\u5468\u8fd9 \u8001\u513f\u4ee5\u524d\u8f88\u81ea\u5c45\uff0c\u5374\u8fd8\u8981\u5411\u54b1\u4eec\u5b66\u5e08\uff0c\u4e0d\u77e5\u4ed6\u7f9e\u4e5f\u4e0d\u7f9e\uff1f\u201d \u3000\u3000\u539f\u6765\u6df3\u4e8e\u5468\u907f\u5f00\u94dc\u949f\u7684\u8eab\u6cd5\u6b65\u6cd5\uff0c\u6b63\u662f\u674e\u3001\u5b5f\u4e8c\u4eba\u521a\u624d\u6240\u7528\u7684\u201c\u79fb\u6b65\u6362\u5f62\u201d\u8fd9\u5957\u529f\u592b\u3002 \u5176\u5b9e\u8fd9\u5957\u529f\u592b\u5e76\u975e\u72ec\u5bb6\u6240\u6709\uff0c\u8eab\u5177\u4e0a\u4e58\u6b66\u529f\u7684\u4eba\u5341\u4e5d\u4f1a\u7528\uff0c\u6df3\u4e8e\u5468\u4f9d\u6837\u753b\u846b\u82a6\u539f\u4e5f\u65e0\u59a8\u3002\u4e0d \u8fc7\u4ed6\u4ee5\u8001\u524d\u8f88\u81ea\u5c45\uff0c\u5371\u6025\u4e4b\u65f6\uff0c\u4e0d\u80fd\u522b\u51fa\u5fc3\u88c1\uff0c\u5374\u8981\u6548\u6cd5\u5c0f\u8f88\uff0c\u8fd9\u5c31\u96be\u514d\u53d7\u4eba\u8ba5\u8bee\u4e86\u3002 \u3000\u3000\u674e\u601d\u5357\u7b11\u9053\uff1a\u201c\u53ef\u60dc\u4ed6\u7684\u5bf9\u624b\u662f\u4f60\u7684\u7239\u7239\uff0c\u4ed6\u8981\u6548\u6cd5\u54b1\u4eec\uff0c\u4e5f\u662f\u51b3\u8ba1\u96be\u9003\u4e00\u8d25\u3002\u201d\u8bdd\u867d \u5982\u6b64\uff0c\u674e\u601d\u5357\u5374\u4e0d\u7981\u6709\u70b9\u62c5\u5fc3\u3002\u8981\u77e5\u95ee\u9898\u7684\u5173\u952e\u5e76\u4e0d\u5728\u4e8e\u8c01\u80dc\u8c01\u8d1f\uff0c\u800c\u662f\u5728\u4e8e\u65f6\u95f4\uff0c\u4e00\u652f\u9999 \u7684\u4e09\u5206\u4e4b\u4e00\uff0c\u5f88\u5feb\u5c31\u4f1a\u70e7\u5b8c\uff0c\u6b64\u65f6\u5df2\u53ea\u5269\u4e0b\u6700\u540e\u4e00\u70b9\u4e86\u3002\u5018\u82e5\u9999\u706b\u4e00\u706d\uff0c\u5b5f\u5c11\u521a\u7eb5\u7136\u6253\u4f24\u4e86 \u6df3\u4e8e\u5468\uff0c\u4e5f\u53ea\u80fd\u7b97\u662f\u8f93\u4e86\u3002 \u3000\u3000\u5fc3\u5ff5\u672a\u5df2\uff0c\u53ea\u542c\u5f97\u201c\u94db\u201d\u7684\u4e00\u58f0\uff0c\u5b5f\u5c11\u521a\u53c8\u5df2\u4e00\u638c\u51fb\u51fa\uff0c\u6df3\u4e8e\u5468\u5371\u6025\u4e4b\u9645\uff0c\u5206\u5916\u673a\u7075\uff0c \u5168\u526f\u7cbe\u795e\uff0c\u90fd\u7528\u6765\u6ce8\u89c6\u5b5f\u5c11\u521a\u7684\u52a8\u4f5c\uff0c\u89c1\u5b5f\u5c11\u521a\u7684\u638c\u950b\u662f\u5411\u5de6\u65b9\u62cd\u51fa\uff0c\u4e0d\u5f85\u4ed6\u78b0\u7740\u94dc\u949f\uff0c\u62a2 \u5148\u4fbf\u5411\u53f3\u65b9\u95ea\u907f\u3002 \u3000\u3000\u672c\u6765\u9ad8\u624b\u6bd4\u6597\uff0c\u8d35\u5728\u77e5\u673a\u3002\u6df3\u4e8e\u5468\u7aa5\u4f3a\u65b9\u5411\uff0c\u62a2\u5148\u95ea\u907f\uff0c\u6b63\u662f\u6df1\u660e\u6b64\u7406\uff0c\u5426\u5219\u82e5\u5f85\u4ed6\u94dc \u949f\u98de\u6765\uff0c\u5c31\u672a\u5fc5\u6765\u5f97\u53ca\u95ea\u8eb2\u4e86\u3002 \u3000\u3000\u4e0d\u6599\u5b5f\u5c11\u521a\u7684\u638c\u529b\u5947\u5999\u83ab\u6d4b\uff0c\u4ed6\u8ba9\u6df3\u4e8e\u5468\u770b\u89c1\u4ed6\u7684\u52a8\u4f5c\uff0c\u6b63\u662f\u8981\u8bf1\u6df3\u4e8e\u5468\u4e0a\u5f53\u7684\u3002\u8fd9\u4e00 \u638c\u770b\u4f3c\u5411\u5de6\u65b9\u62cd\u51fa\uff0c\u62cd\u4e0b\u4e4b\u65f6\uff0c\u5374\u7528\u4e86\u56de\u65cb\u7684\u529b\u9053\u3002\u53ea\u542c\u5f97\u201c\u94db\u201d\u7684\u4e00\u58f0\u54cd\uff0c\u6df3\u4e8e\u5468\u521a\u5411\u53f3 \u65b9\u4e00\u95ea\uff0c\u94dc\u949f\u5df2\u662f\u500f\u7136\u8f6c\u4e86\u65b9\u5411\uff0c\u98de\u5230\u4ed6\u7684\u8eab\u524d\u3002\u6df3\u4e8e\u5468\u5413\u5f97\u9b42\u98de\u9b44\u6563\uff0c\u6b64\u65f6\u4ed6\u5df2\u6765\u4e0d\u53ca\u8df3 \u8dc3\u95ea\u5f00\uff0c\u53ea\u597d\u4f0f\u5728\u5730\u4e0a\u6253\u6eda\uff0c\u5b5f\u5c11\u521a\u8eab\u5f62\u4e00\u4e2a\u8d77\u4f0f\uff0c\u8ffd\u4e0a\u94dc\u949f\uff0c\u53cc\u624b\u4e00\u6309\uff0c\u94dc\u949f\u7684\u949f\u53e3\u6b63\u5bf9 \u7740\u6df3\u4e8e\u5468\u7684\u5929\u7075\u76d6\uff0c\u559d\u9053\uff1a\u201c\u4f60\u670d\u4e86\u4e48\uff1f\u201d \u3000\u3000\u6b64\u65f6\u5b5f\u5c11\u521a\u662f\u6293\u7740\u949f\u9876\u7684\u94c1\u94a9\u8ddf\u7740\u4ed6\u8dd1\u7684\uff0c\u6df3\u4e8e\u5468\u5728\u5730\u4e0a\u6253\u6eda\uff0c\u65e0\u8bba\u5982\u4f55\u4e5f\u8dd1\u4e0d\u8fc7\u5b5f\u5c11 \u521a\uff0c\u5b5f\u5c11\u521a\u53ea\u8981\u628a\u94dc\u949f\u7f69\u4e0b\uff0c\u767b\u65f6\u5c31\u53ef\u4ee5\u8981\u4e86\u6df3\u4e8e\u5468\u7684\u6027\u547d\u3002 \u3000\u3000\u6df3\u4e8e\u5468\u65e0\u53ef\u5948\u4f55\uff0c\u53ea\u597d\u53eb\u9053\uff1a\u201c\u5b5f\u3001\u5b5f\u5927\u4fa0\u624b\u4e0b\u7559\u60c5\uff0c\u6211\u3001\u6211\u8ba4\u8f93\u4e86\u3002\u201d\u5b5f\u5c11\u521a\u54c8\u54c8\u4e00 \u7b11\uff0c\u63d0\u8d77\u94dc\u949f\uff0c\u95ee\u9053\uff1a\u201c\u5c60\u59d1\u5a18\uff0c\u9999\u706b\u7184\u4e86\u6ca1\u6709\u3002\u201d\u5c60\u51e4\u7b11\u9053\uff1a\u201c\u6070\u5230\u597d\u5904\uff0c\u9999\u706b\u72b9\u7ea2\uff01\u201d \u3000\u3000\u6df3\u4e8e\u5468\u722c\u8d77\u6765\u4e00\u770b\uff0c\u9732\u51fa\u5730\u9762\u7684\u90a3\u652f\u9999\u7684\u4e09\u5206\u4e4b\u4e00\uff0c\u521a\u521a\u70e7\u5b8c\uff0c\u8fd8\u6709\u4e00\u70b9\u9999\u5934\u672a\u706d\u3002\u6df3 \u4e8e\u5468\u5927\u53f9\u201c\u6666\u6c14\u201d\uff0c\u53ef\u60dc\u5c31\u53ea\u5dee\u4e86\u8fd9\u4e48\u4e00\u70b9\u65f6\u95f4\u3002\u4f46\u4ed6\u5f97\u4ee5\u6b7b\u91cc\u9003\u751f\uff0c\u81ea\u5df1\u4e5f\u89c9\u5f97\u662f\u201c\u4e0d\u5e78 \u4e2d\u4e4b\u5927\u5e78\u201d\uff0c\u6b64\u65f6\u4ed6\u53ea\u6015\u5b5f\u5c11\u521a\u518d\u6709\u7559\u96be\uff0c\u76df\u4e3b\u4e4b\u4f4d\u5374\u662f\u60f3\u4e5f\u4e0d\u6562\u60f3\u4e86\u3002 \u3000\u3000\u5b5f\u5c11\u521a\u653e\u4e0b\u94dc\u949f\uff0c\u8bf4\u9053\uff1a\u201c\u5ff5\u4f60\u4fee\u4e3a\u4e0d\u6613\uff0c\u4f60\u65e2\u8ba4\u8f93\uff0c\u5c31\u8ba9\u4f60\u53bb\u5427\u3002\u4f46\u613f\u4f60\u4ece\u6b64\u6d17\u5fc3\u9769 \u9762\uff0c\u4e0d\u518d\u80e1\u4f5c\u975e\u4e3a\uff01\u201d \u3000\u3000\u4e0e\u6df3\u4e8e\u5468\u4e00\u540c\u6765\u7684\u5171\u6709\u5341\u4e94\u4eba\uff0c\u9664\u4e86\u67f3\u6d1e\u5929\u3001\u5d14\u9547\u5c71\u3001\u5468\u9547\u6d77\u4e09\u4eba\u5148\u5df2\u79bb\u5f00\uff0c\u8fd8\u6709\u4e00\u5341 \u4e8c\u5bb6\u5be8\u4e3b\uff0c\u6df3\u4e8e\u5468\u4ee5\u4e3a\u4ed6\u4eec\u4f1a\u8ddf\u81ea\u5df1\u8d70\u7684\uff0c\u54ea\u77e5\u8fd9\u5341\u4e8c\u5bb6\u5be8\u4e3b\u8fde\u5411\u4ed6\u8868\u793a\u540c\u60c5\u7684\u4e5f\u6ca1\u4e00\u4e2a\u3002 \u5927\u5c40\u4e00\u5b9a\uff0c\u4ed6\u4eec\u5c31\u4e89\u5148\u6050\u540e\u7684\u62e5\u4e0a\u524d\u53bb\uff0c\u6709\u7684\u5411\u674e\u601d\u5357\u9053\u8d3a\uff0c\u8d3a\u4ed6\u505a\u4e86\u7eff\u6797\u7684\u65b0\u76df\u4e3b\uff1b\u6709\u7684 \u5411\u5b5f\u5c11\u521a\u606d\u7ef4\uff0c\u5bf9\u4ed6\u7684\u7edd\u4e16\u795e\u529f\u4f69\u670d\u5f97\u4e94\u4f53\u6295\u5730\uff1b\u6709\u7684\u5219\u5411\u5c60\u51e4\u8868\u767d\uff0c\u8868\u767d\u4ed6\u4eec\u4e5f\u6709\u6297\u654c\u4e4b \u5fc3\uff0c\u53ea\u56e0\u4e0d\u77e5\u6df3\u4e8e\u5468\u7236\u5b50\u6697\u4e2d\u4e0e\u9791\u5b50\u5f80\u6765\uff0c\u56e0\u6b64\u624d\u4f1a\u4e0a\u4ed6\u7684\u5f53\u3002\u8fd9\u4e9b\u4eba\u5012\u4e5f\u4e0d\u662f\u8ddf\u7ea2\u9876\u767d\uff0c \u4ed6\u4eec\u4e4b\u4e2d\u7684\u7edd\u5927\u591a\u6570\uff0c\u7684\u7684\u786e\u786e\u662f\u5728\u8fd9\u6b21\u4e8b\u4ef6\u4e4b\u4e2d\uff0c\u624d\u64e6\u4eae\u4e86\u773c\u775b\u7684\u3002 \u3000\u3000\u6df3\u4e8e\u5468\u9762\u76ee\u65e0\u5149\uff0c\u53ea\u597d\u4e00\u4e2a\u4eba\u7070\u6e9c\u6e9c\u7684\u72ec\u81ea\u4e0b\u5c71\u3002 \u3000\u3000\u5357\u5b8b\u4f7f\u8005\u3001\u5bb0\u76f8\u53f2\u5f25\u8fdc\u7684\u4f84\u513f\u53f2\u516c\u671b\u4e5f\u4e0a\u524d\u5411\u65b0\u76df\u4e3b\u9053\u8d3a\uff0c\u8bf4\u9053\uff1a\u201c\u73b0\u4eca\u98ce\u4e91\u52a8\u8361\uff0c\u674e \u516c\u5b50\u662f\u5c06\u95e8\u4e4b\u540e\uff0c\u8363\u81ba\u76df\u4e3b\uff0c\u8fd9\u6b63\u662f\u674e\u516c\u5b50\u62a5\u56fd\u7684\u65f6\u673a\u3002\u8fd8\u671b\u674e\u516c\u5b50\u80fd\u5584\u4f53\u671d\u5ef7\u7684\u610f\u65e8\uff0c\u8054 \u8499\u53e4\u4ee5\u4f10\u91d1\uff0c\u4e2d\u539f\u6545\u571f\uff0c\u6062\u590d\u53ef\u671f\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u6211\u662f\u505a\u4e49\u519b\u7684\u76df\u4e3b\uff0c\u4e0d\u662f\u505a\u671d\u5ef7\u7684\u5b98\u3002\u671d\u5ef7\u7684\u51b3\u7b56\u6211\u4eec\u81ea\u4f1a\u90d1\u91cd\u5546 \u8ba8\uff0c\u4f46\u5374\u4e0d\u80fd\u7ea6\u675f\u6211\u4eec\u3002\u201d \u3000\u3000\u8463\u5f00\u5c71\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\u3002\u671d\u5ef7\u6709\u671d\u5ef7\u7684\u610f\u65e8\uff0c\u6211\u4eec\u4e5f\u6709\u6211\u4eec\u7684\u610f\u65e8\u3002\u6211\u4eec\u662f\u4ee5\u8001\u767e\u59d3 \u7684\u610f\u65e8\u4e3a\u610f\u65e8\u3002\u8001\u767e\u59d3\u8981\u4fdd\u5bb6\u536b\u56fd\uff0c\u90a3\u6211\u4eec\u5c31\u975e\u5f97\u540c\u9791\u5b50\u6253\u5230\u5e95\u4e0d\u53ef\u3002\u5973\u771f\u9791\u5b50\u8981\u6253\uff0c\u8499\u53e4 \u9791\u5b50\u4e5f\u8981\u6253\u3002\u53f2\u5927\u4eba\uff0c\u6211\u662f\u4e2a\u7c97\u4eba\uff0c\u8bf4\u8bdd\u4e0d\u61c2\u8f6c\u5f2f\u62b9\u89d2\uff0c\u5192\u72af\u4e86\u4f60\uff0c\u4f60\u83ab\u89c1\u602a\u3002\u201d\u53f2\u516c\u671b\u597d \u751f\u6ca1\u8da3\uff0c\u4e5f\u53ea\u597d\u4e0b\u5c71\u4e86\u3002 \u3000\u3000\u6b64\u65f6\u5927\u5c40\u5df2\u5b9a\uff0c\u4f17\u4eba\u7686\u5927\u6b22\u559c\u3002\u674e\u601d\u5357\u4e0a\u524d\u4e0e\u5b5f\u5c11\u521a\u91cd\u65b0\u89c1\u8fc7\u4e86\u793c\uff0c\u5b5f\u5c11\u521a\u54c8\u54c8\u5927\u7b11\uff0c \u8bf4\u9053\uff1a\u201c\u4f60\u7684\u4e8b\u60c5\u6211\u90fd\u77e5\u9053\u4e86\uff0c\u5230\u5e95\u662f\u6211\u5973\u513f\u7684\u773c\u529b\u6bd4\u6211\u9ad8\u660e\u5f97\u591a\uff0c\u6211\u8bef\u4f1a\u4e86\u4f60\uff0c\u8fd8\u5f97\u8bf7\u4f60 \u539f\u8c05\u5462\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u8001\u524d\u8f88\u8a00\u91cd\u4e86\uff0c\u8001\u524d\u8f88\u5bf9\u6211\u7684\u97ad\u7b56\uff0c\u90a3\u4e5f\u662f\u4e3a\u4e86\u6211\u7684\u597d\u3002\u665a\u8f88\u5c82\u6562\u6709\u6240\u4e0d \u6ee1\uff1f\u201d\u4ed6\u628a\u5b5f\u5c11\u521a\u90a3\u65e5\u5bf9\u4ed6\u7684\u7559\u4e66\u201c\u8b66\u544a\u201d\u8bf4\u6210\u662f\u201c\u97ad\u7b56\u201d\uff0c\u8bf4\u5f97\u5341\u5206\u5f97\u4f53\u3002\u7fa4\u96c4\u4e0d\u77e5\u5185\u91cc \u60c5\u7531\uff0c\u5fc3\u4e2d\u4ff1\u662f\u60f3\u9053\uff1a\u201c\u5b5f\u5927\u4fa0\u5927\u7ea6\u662f\u60f3\u8981\u674e\u76df\u4e3b\u4f5c\u5973\u5a7f\uff0c\u770b\u6765\u674e\u76df\u4e3b\u4e5f\u662f\u5341\u5206\u613f\u610f\u3002\u4ed6\u548c \u5b5f\u59d1\u5a18\u5018\u82e5\u7ed3\u6210\u592b\u5987\uff0c\u90a3\u5012\u771f\u662f\u518d\u597d\u4e5f\u4e0d\u8fc7\u7684\u7f8e\u6ee1\u59fb\u7f18\u4e86\uff01\u201d \u3000\u3000\u5728\u4f17\u4eba\u7686\u5927\u6b22\u559c\u3001\u5174\u9ad8\u91c7\u70c8\u4e4b\u4e2d\uff0c\u5374\u6709\u4e00\u4eba\u6ee1\u6000\u6101\u7eea\uff0c\u6084\u6084\u79bb\u5f00\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u6b63\u662f\u6768\u5a49\uff0c\u521a\u624d\u674e\u3001\u5b5f\u4e8c\u4eba\u8054\u624b\uff0c\u6076\u6597\u6df3\u4e8e\u5468\u4e4b\u65f6\uff0c\u5979\u5df1\u7ecf\u6765\u4e86\uff0c\u4f46\u56e0\u5979\u5df2\u7ecf\u6539 \u5bb9\u6613\u8c8c\uff0c\u7a7f\u7684\u53c8\u662f\u666e\u901a\u55bd\u5175\u7684\u773c\u9970\uff0c\u8c01\u4e5f\u6ca1\u6709\u6ce8\u610f\u5979\u3002\u674e\u601d\u5357\u9047\u9669\u4e4b\u65f6\uff0c\u66fe\u7ecf\u542c\u5230\u5979\u7684\u5c16\u53eb \u4e4b\u58f0\uff0c\u4f46\u5f53\u65f6\u53d1\u58f0\u60ca\u53eb\u7684\u4e0d\u6b62\u4e00\u4e2a\uff0c\u674e\u601d\u5357\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u662f\u5979\u3002 \u3000\u3000\u6768\u5a49\u89c1\u4ed6\u4e0e\u5b5f\u660e\u971e\u8054\u624b\uff0c\u7ec8\u4e8e\u8131\u9669\uff0c\u5fc3\u4e2d\u53c8\u662f\u6b22\u559c\uff0c\u53c8\u662f\u8f9b\u9178\u3002\u6b64\u65f6\u89c1\u5230\u5b5f\u5c11\u521a\u628a\u674e\u601d \u5357\u548c\u4ed6\u7684\u5973\u513f\u62c9\u5728\u4e00\u8d77\uff0c\u4eb2\u4eb2\u70ed\u70ed\u5730\u53d9\u8bdd\uff0c\u7fa4\u96c4\u90fd\u56f4\u7ed5\u7740\u4ed6\u4eec\uff0c\u628a\u4ed6\u4eec\u4e09\u4eba\u5f53\u4f5c\u4e2d\u5fc3\uff0c\u81ea\u5df1 \u5374\u53ea\u6709\u5728\u65c1\u8fb9\u8fdc\u8fdc\u89c2\u770b\u7684\u4efd\u513f\uff0c\u5fc3\u91cc\u7684\u4f24\u611f\uff0c\u90a3\u662f\u66f4\u4e0d\u7528\u8bf4\u4e86\u3002 \u3000\u3000\u6768\u5a49\u5fcd\u7740\u773c\u6cea\uff0c\u5fc3\u91cc\u81ea\u5df1\u5bf9\u81ea\u5df1\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u80fd\u54ed\uff0c\u6211\u4e0d\u80fd\u8ba9\u4ed6\u77e5\u9053\uff0c\u5b5f\u59d1\u5a18\u5bf9\u4ed6\u6709\u60c5 \u6709\u4e49\uff0c\u4ed6\u548c\u5b5f\u59d1\u5a18\u662f\u6bd4\u548c\u6211\u66f4\u9002\u5408\u7684\u3002\u5c31\u8ba9\u4ed6\u5f53\u6211\u662f\u6b7b\u4e86\u5427\uff0c\u4f55\u82e6\u8fd8\u8981\u963b\u788d\u4ed6\u7684\u7f8e\u6ee1\u59fb \u7f18\uff1f\u201d \u3000\u3000\u6768\u5a49\u542b\u7740\u773c\u6cea\u6084\u6084\u79bb\u5f00\uff0c\u53ef\u601c\u674e\u601d\u5357\u4e00\u70b9\u4e5f\u4e0d\u77e5\u9053\u3002\u4fd7\u8bed\u8bf4\u4eba\u9022\u559c\u4e8b\u7cbe\u795e\u723d\uff0c\u6b64\u9645\u6b63\u662f \u4ed6\u6700\u9ad8\u5174\u7684\u65f6\u5019\uff0c\u867d\u7136\u4e0d\u81f3\u4e8e\u5c31\u5fd8\u4e86\u6768\u5a49\uff0c\u4f46\u5fc3\u4e2d\u7684\u4f24\u611f\u5374\u5df2\u662f\u629b\u4e4b\u8111\u540e\u4e86\u3002\u6b64\u9645\u4ed6\u5fc3\u4e2d\u60f3 \u7684\uff0c\u53ea\u662f\u5982\u4f55\u5f53\u4e00\u4e2a\u79f0\u804c\u7684\u76df\u4e3b\uff0c\u597d\u5b9e\u73b0\u80f8\u4e2d\u62b1\u8d1f\uff0c\u6d17\u96ea\u5bb6\u56fd\u4e4b\u4ec7\u3002 \u3000\u3000\u5927\u5bb6\u5174\u9ad8\u91c7\u70c8\u4e4b\u4e2d\uff0c\u4e5f\u8fd8\u6709\u4e00\u4e2a\u4eba\u662f\u5fc3\u4e2d\u9887\u611f\u4e0d\u5b89\u7684\uff0c\u8fd9\u4e2a\u4eba\u662f\u77f3\u749e\u3002 \u3000\u3000\u77f3\u749e\u770b\u51fa\u4e86\u5b5f\u5c11\u521a\u6709\u628a\u5973\u513f\u8bb8\u914d\u4e0e\u674e\u601d\u5357\u4e4b\u610f\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5b5f\u59d1\u5a18\u662f\u5c60\u51e4\u6700\u8981\u597d\u7684\u670b \u53cb\uff0c\u5c60\u51e4\u4e00\u5b9a\u662f\u5e0c\u671b\u5979\u5ac1\u7ed9\u674e\u601d\u5357\u7684\u3002\u674e\u601d\u5357\u4e0d\u77e5\u6768\u59d1\u5a18\u7684\u906d\u9047\uff0c\u4ed6\u5bf9\u5b5f\u660e\u971e\u6050\u6015\u4ea6\u662f\u96be\u514d \u6709\u60c5\u3002\u6211\u5018\u82e5\u8bf4\u51fa\u6768\u5a49\u5c31\u5728\u8fd9\u513f\uff0c\u674e\u601d\u5357\u4e0d\u77e5\u4f1a\u600e\u4e48\u6837\uff1f\u4f46\u5bf9\u5b5f\u5927\u4fa0\u7236\u5973\u800c\u8a00\uff0c\u53ef\u662f\u4e00\u4e2a\u5927 \u5927\u7684\u6253\u51fb\u4e86\uff0c\u5c60\u51e4\u53ea\u6015\u4e5f\u4e0d\u9ad8\u5174\u6211\u8fd9\u4e48\u505a\u7684\u3002\u4f46\u6211\u82e5\u4e0d\u8bf4\u660e\u771f\u76f8\uff0c\u53c8\u5982\u4f55\u5bf9\u5f97\u4f4f\u6768\u5a49\uff1f\u5509\uff0c \u6211\u5e94\u8be5\u5e2e\u5fd9\u8c01\u624d\u597d\u5462\uff1f\u201d \u3000\u3000\u77f3\u749e\u8f6c\u4e86\u51e0\u4e2a\u5ff5\u5934\uff0c\u7ec8\u4e8e\u7acb\u8db3\u4e3b\u610f\uff0c\u60f3\u9053\uff1a\u201c\u5927\u4e08\u592b\u5e94\u5f53\u4ed7\u4e49\u52c7\u4e3a\uff0c\u5c82\u53ef\u53ea\u987e\u79c1\u4eba\u5229 \u5bb3\u3002\u6768\u59d1\u5a18\u8eab\u4e16\u51c4\u51c9\uff0c\u5979\u6bd4\u5b5f\u660e\u971e\u66f4\u9700\u8981\u4e00\u4e2a\u4eb2\u4eba\uff0c\u5c60\u51e4\u82e5\u77e5\u771f\u76f8\uff0c\u4e5f\u672a\u5fc5\u4f1a\u602a\u6211\u591a\u5634\u3002\u5c31 \u662f\u602a\u6211\uff0c\u6211\u4e5f\u5e94\u5f53\u628a\u6768\u5a49\u7684\u4e8b\u60c5\u544a\u8bc9\u674e\u601d\u5357\u3002\u4e0d\u8fc7\u4ed6\u559c\u6b22\u8c01\uff0c\u8fd9\u662f\u4ed6\u7684\u4e8b\u60c5\u3002\u6211\u73b0\u5728\u4e5f\u4e0d\u77e5 \u9053\u4ed6\u7684\u5fc3\u610f\u3002\u6768\u5a49\u4e0e\u6211\u6709\u7ea6\uff0c\u53ea\u8bb8\u6211\u4e3a\u5979\u8fa9\u51a4\uff0c\u4e0d\u8bb8\u6211\u8bf4\u51fa\u5979\u7684\u4e0b\u843d\u3002\u6211\u5c31\u7167\u8fd9\u6837\u529e\u597d\u4e86\u3002 \u5f85\u4ed6\u77e5\u9053\u4e86\u771f\u76f8\uff0c\u77e5\u9053\u6768\u5a49\u8fd8\u6d3b\u5728\u4eba\u95f4\uff0c\u90a3\u65f6\u4e14\u770b\u4ed6\u662f\u6545\u5251\u60c5\u6df1\uff0c\u8fd8\u662f\u8d2a\u65b0\u5fd8\u65e7\uff1f\u201d \u3000\u3000\u4e0d\u8fc7\u77f3\u749e\u867d\u7136\u7acb\u8db3\u4e86\u4e3b\u610f\uff0c\u6b64\u9645\u5374\u8fd8\u4e0d\u662f\u8bf4\u8bdd\u7684\u673a\u4f1a\uff0c\u5927\u5bb6\u6b63\u5728\u9ad8\u9ad8\u5174\u5174\uff0c\u8bf4\u8fd9\u79cd\u513f\u5973 \u79c1\u60c5\uff0c\u4e0d\u4f46\u662f\u5927\u6740\u98ce\u666f\uff0c\u800c\u4e14\u4e5f\u4e0d\u9002\u5b9c\u3002\u56e0\u6b64\u77f3\u749e\u53ea\u597d\u7b49\u5230\u4e86\u5e86\u529f\u5bb4\u8fc7\u540e\uff0c\u6709\u673a\u4f1a\u518d\u8bf4\u3002 \u3000\u3000\u674e\u601d\u5357\u5ffd\u5730\u60f3\u8d77\u4e00\u4e2a\u4eba\uff0c\u5728\u5c60\u51e4\u53eb\u4eba\u6446\u5e86\u529f\u5bb4\u4e4b\u65f6\uff0c\u4ed6\u6e38\u76ee\u56db\u987e\uff0c\u4e0d\u89c1\u8fd9\u4e2a\u4eba\uff0c\u8fde\u5fd9\u95ee \u9053\uff1a\u201c\u4eca\u65e5\u6253\u8d25\u5d14\u9547\u5c71\u7684\u90a3\u4f4d\u891a\u82f1\u96c4\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u8981\u77e5\u82e5\u662f\u8bba\u529f\u7684\u8bdd\uff0c\u5b5f\u5c11\u521a\u5f53\u7136\u662f\u529f\u52b3\u6700\u5927\uff0c\u674e\u601d\u5357\u5176\u6b21\uff0c\u4f46\u891a\u4e91\u5cf0\u4e5f\u662f\u529f\u52b3\u4e0d\u5c0f\u3002\u4ed6 \u4e0d\u4f46\u6253\u8d25\u5d14\u9547\u5c71\uff0c\u800c\u4e14\u4ee5\u60ca\u4eba\u7684\u5251\u672f\u548c\u67f3\u6d1e\u5929\u6253\u6210\u5e73\u624b\u3002\u5728\u5e86\u529f\u5bb4\u4e0a\uff0c\u5f53\u7136\u662f\u4e0d\u80fd\u5c11\u4e86\u8fd9\u4e2a \u4eba\u7684\u3002\u6b63\u662f\uff1a \u3000\u3000\u795e\u9f99\u89c1\u9996\u4e0d\u89c1\u5c3e\uff0c\u5374\u4ece\u4f55\u5904\u89c5\u65af\u4eba\uff1f \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002"}),showOnHomepage:!0},{name:"\u7b2c\u4e8c\u5341\u4e94\u56de\u3000\u63ed\u5f00\u8ff7\u96fe\u660e\u771f\u76f8\u3000\u59cb\u8bc6\u51b0\u5fc3\u5c5e\u654c\u4eba",content:(0,t.jsx)(t.Fragment,{children:"\u5c60\u51e4\u970d\u7136\u4e00\u7701\uff0c\u8bf4\u9053\uff1a\u201c\u600e\u4e48\uff0c\u891a\u4e91\u5cf0\u6ca1\u6709\u548c\u4f60\u4eec\u4e00\u8d77\u8fdb\u6765\u4e48\uff1f\u6211\u5012\u662f\u4e00\u65f6\u5fd8\u4e86\u62db\u547c\u4ed6 \u4e86\u3002\u5feb\u5feb\u8bf7\u4ed6\u8fdb\u6765\u3002\u201d \u3000\u3000\u539f\u6765\u5c60\u51e4\u672c\u662f\u65e9\u5c31\u60f3\u95ee\u891a\u4e91\u5cf0\u7684\u6765\u5386\u7684\uff0c\u53ea\u56e0\u5728\u891a\u4e91\u5cf0\u548c\u67f3\u6d1e\u5929\u4ea4\u624b\u4e4b\u540e\uff0c\u891a\u4e91\u5cf0\u9000\u4e86 \u4e0b\u6765\uff0c\u6309\u7740\u5c31\u662f\u674e\u601d\u5357\u548c\u5b5f\u660e\u971e\u76f8\u7ee7\u51fa\u573a\uff0c\u674e\u3001\u5b5f\u4e8c\u4eba\u548c\u5bf9\u65b9\u8fde\u573a\u6076\u6597\uff0c\u5c60\u51e4\u7684\u6ce8\u610f\u529b\u90fd\u653e \u5728\u4ed6\u4e8c\u4eba\u8eab\u4e0a\uff0c\u4e00\u76f4\u672a\u6709\u529f\u592b\u4e0e\u891a\u4e91\u5cf0\u8bf4\u8bdd\u3002 \u3000\u3000\u5f85\u5230\u6df3\u4e8e\u5468\u8d25\u8d70\uff0c\u5927\u5c40\u5df2\u5b9a\u4e4b\u540e\uff0c\u4f17\u4eba\u90fd\u56f4\u62e2\u6765\u5411\u65b0\u76df\u4e3b\u9053\u8d3a\uff0c\u5c60\u51e4\u4e5f\u5fd9\u7740\u62db\u547c\u5b5f\u660e\u971e \u7684\u7236\u4eb2\uff0c\u672a\u66fe\u7559\u610f\u891a\u4e91\u5cf0\u3002\u5979\u4ee5\u4e3a\u891a\u4e91\u5cf0\u8d70\u662f\u5728\u8fd9\u4e00\u7fa4\u4eba\u5f53\u4e2d\uff0c\u76f4\u5230\u674e\u601d\u5357\u95ee\u8d77\uff0c\u8fd9\u624d\u53d1\u89c9 \u891a\u4e91\u5cf0\u5df2\u7ecf\u4e0d\u89c1\u3002 \u3000\u3000\u51e0\u4e2a\u5c0f\u5934\u76ee\u5949\u4e86\u5c60\u51e4\u4e4b\u547d\u51fa\u53bb\u627e\u5bfb\u891a\u4e91\u5cf0\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u90fd\u56de\u6765\u62a5\u9053\uff0c\u6ca1\u6709\u627e\u7740\uff01 \u3000\u3000\u6b64\u65f6\u5e86\u529f\u5bb4\u5df2\u7ecf\u6446\u597d\uff0c\u5e2d\u4f4d\u4e5f\u90fd\u6709\u4e86\u5b89\u6392\u4e86\u3002\u891a\u4e91\u5cf0\u7684\u5ea7\u4f4d\u662f\u5b89\u6392\u5728\u674e\u601d\u5357\u65c1\u8fb9\uff0c\u540c\u5b5f \u5c11\u521a\u7236\u5973\u4ee5\u53ca\u5c60\u51e4\u540c\u4e00\u5e2d\u7684\uff0c\u4e0d\u89c1\u4ed6\u6765\uff0c\u53ea\u597d\u865a\u4f4d\u4ee5\u5f85\u3002 \u3000\u3000\u674e\u601d\u5357\u53f9\u9053\uff1a\u201c\u8fd9\u4f4d\u891a\u5144\u771f\u662f\u5982\u795e\u9f99\u4e4b\u89c1\u9996\u4e0d\u89c1\u5c3e\uff0c\u4f46\u5e86\u529f\u5bb4\u4e0a\u6ca1\u6709\u4ed6\uff0c\u5374\u662f\u7f8e\u4e2d\u4e0d\u8db3 \u4e86\u3002\u201d\u5c60\u51e4\u9053\uff1a\u201c\u6599\u60f3\u4ed6\u8fd8\u4e0d\u4f1a\u4e0b\u5c71\u5427\uff1f\u54b1\u4eec\u660e\u5929\u6162\u6162\u5730\u627e\u3002 \u3000\u3000\u5b5f\u5c11\u521a\u9053\uff1a\u201c\u4f60\u4eec\u8bf4\u7684\u8fd9\u4f4d\u891a\u4e91\u5cf0\u662f\u8c01\uff1f\u201d\u5b5f\u660e\u971e\u9053\uff1a\u201c\u7239\uff0c\u53ef\u60dc\u4f60\u6765\u8fdf\u4e86\u4e00\u6b65\uff0c\u6ca1\u6709 \u770b\u89c1\u3002\u8fd9\u59d3\u891a\u7684\u4e00\u8fde\u6597\u4e86\u4e24\u573a\uff0c\u7b2c\u4e00\u573a\u6253\u8d25\u4e86\u5d14\u9547\u5c71\uff0c\u7b2c\u4e8c\u573a\u548c\u67f3\u6d1e\u5929\u6253\u6210\u5e73\u624b\uff0c\u53ea\u56e0\u4ed6\u7528 \u7684\u662f\u628a\u5b9d\u5251\uff0c\u53cc\u65b9\u5175\u5203\u90fd\u6709\u7f3a\u53e3\uff0c\u4ed6\u81ea\u8ba4\u529f\u529b\u7a0d\u900a\uff0c\u8fd9\u624d\u81ea\u613f\u4f5c\u8d1f\u7684\u3002\u5176\u5b9e\u4ed6\u82e5\u662f\u6ca1\u6709\u548c\u5d14 \u9547\u5c71\u5148\u62fc\u4e86\u4e00\u573a\u638c\u529b\u7684\u8bdd\uff0c\u67f3\u6d1e\u5929\u6050\u6015\u4e5f\u672a\u5fc5\u662f\u4ed6\u5bf9\u624b\u3002\u201d \u3000\u3000\u5b5f\u5c11\u521a\u7a0d\u611f\u8be7\u5f02\uff0c\u8bf4\u9053\uff1a\u201c\u771f\u7684\u5417\uff1f\u5d14\u9547\u5c71\u7684\u91d1\u521a\u638c\u548c\u67f3\u6d1e\u5929\u7684\u516b\u4ed9\u5251\u5728\u6c5f\u6e56\u4e0a\u4e5f\u90fd\u7b97 \u5f97\u662f\u51fa\u7c7b\u62d4\u8403\u7684\u4e86\uff0c\u8fd9\u4f4d\u59d3\u891a\u7684\u5c11\u5e74\u80fd\u591f\u6253\u8d25\u5d14\u9547\u5c71\u3001\u6253\u548c\u67f3\u6d1e\u5929\uff0c\u540e\u8f88\u4e2d\u7adf\u6709\u5982\u6b64\u80fd\u4eba\uff0c \u8fd9\u53ef\u771f\u662f\u51fa\u4e4e\u6211\u7684\u610f\u5916\uff01\u201d\u5fc3\u4e2d\u53ef\u5728\u6697\u6697\u5947\u602a\uff1a\u201c\u540e\u8f88\u4e2d\u6709\u5982\u6b64\u80fd\u4eba\uff0c\u600e\u7684\u6211\u5374\u4e0d\u77e5\u9053 \u5462\uff1f\u201d\u8981\u77e5\u5b5f\u5c11\u521a\u4ea4\u6e38\u5e7f\u9614\uff0c\u5bf9\u540e\u8f88\u4e2d\u7684\u6770\u51fa\u4eba\u7269\uff0c\u5c24\u5176\u662f\u4e00\u5411\u6ce8\u610f\u7684\u3002 \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u662f\u5440\uff0c\u6211\u4eec\u90fd\u5728\u5947\u602a\uff0c\u5c60\u59d0\u59d0\u7684\u5c71\u5be8\u4e2d\u6709\u8fd9\u6837\u4e00\u4e2a\u4eba\u7269\uff0c\u6211\u4eec\u7adf\u7136\u90fd\u4e0d\u77e5 \u9053\u4ed6\u7684\u6765\u5386\u3002\u4ee5\u4ed6\u7684\u672c\u9886\u800c\u8bba\uff0c\u5c71\u5be8\u4e2d\u4eba\u80dc\u5f97\u8fc7\u4ed6\u7684\uff0c\u53ea\u6015\u53ea\u6709\u54b1\u4eec\u7684\u65b0\u5be8\u4e3b\u4e86\uff0c\u5973\u513f\u4e5f\u81ea \u6127\u4e0d\u5982\u3002\u201d\u674e\u601d\u5357\u8c26\u865a\u9053\uff1a\u201c\u5b5f\u59d1\u5a18\uff0c\u4f60\u5ba2\u6c14\u4e86\u3002\u6211\u4e5f\u672a\u5fc5\u6bd4\u5f97\u4e0a\u4ed6\u3002\u201d \u3000\u3000\u5b5f\u5c11\u521a\u54c8\u54c8\u7b11\u9000\uff1a\u201c\u971e\u513f\uff0c\u4f60\u4e00\u5411\u4e0d\u80af\u670d\u4eba\u7684\uff0c\u542c\u4f60\u8fd9\u4e48\u8bf4\uff0c\u8fd9\u4f4d\u891a\u5c11\u4fa0\u7684\u672c\u9886\u90a3\u662f\u5f53 \u771f\u4e0d\u9519\u4e86\u3002\u4f46\u4f60\u4eec\u4e0d\u77e5\u4ed6\u7684\u6765\u5386\uff0c\u96be\u9053\u4ece\u4ed6\u7684\u5251\u6cd5\u4e2d\u4e5f\u770b\u4e0d\u51fa\u4ed6\u7684\u95e8\u6d3e\u4e48\uff1f\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u4ed6\u7684\u5251\u6cd5\u4e0e\u4e2d\u539f\u5404\u5927\u95e8\u6d3e\u90fd\u4e0d\u76f8\u540c\uff0c\u770b\u4e0d\u51fa\u4ed6\u662f\u4ec0\u4e48\u5bb6\u6570\u3002\u201d\u5b5f\u5c11\u521a\u9053\uff1a \u201c\u54e6\uff0c\u4e0e\u4e2d\u539f\u5404\u5927\u95e8\u6d3e\u90fd\u4e0d\u76f8\u540c\uff1f\u201d\u795e\u60c5\u5982\u6709\u6240\u601d\u3002 \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u5b5f\u5927\u4fa0\u89c1\u591a\u8bc6\u4e86\uff0c\u53ef\u60f3\u5f97\u5230\u8fd9\u4eba\u7684\u6765\u5386\uff1f\u201d\u5b5f\u5c11\u521a\u9053\uff1a\u201c\u8fd9\u4eba\u7ea6\u83ab\u6709\u591a\u5927 \u5e74\u7eaa\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u5927\u7ea6\u4e5f\u4e0d\u8fc7\u4e8c\u5341\u6765\u5c81\u3002\u201d\u5b5f\u5c11\u521a\u6447\u4e86\u6447\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u6211\u5c31\u731c\u4e0d\u7740\u4e86\u3002\u201d \u5b5f\u660e\u971e\u95ee\u9053\uff1a\u201c\u4ed6\u7684\u6765\u5386\u548c\u5e74\u7eaa\u6709\u4f55\u5173\u7cfb\uff1f\u201d \u3000\u3000\u5b5f\u5c11\u521a\u9053\uff1a\u201c\u4ed6\u7684\u5251\u6cd5\u4e0e\u5404\u6d3e\u90fd\u4e0d\u76f8\u540c\uff0c\u8981\u8fbe\u5230\u8fd9\u6837\u7684\u9020\u8be3\uff0c\u5fc5\u987b\u878d\u4f1a\u5404\u5bb6\u5404\u6d3e\u6b66\u5b66\u7684 \u7cbe\u534e\u624d\u884c\u3002\u636e\u6211\u6240\u77e5\uff0c\u5148\u4e00\u8f88\u7684\u6b66\u6797\u9690\u9038\u4e4b\u4e2d\u5012\u6709\u4e00\u4e24\u4e2a\u8fd9\u6837\u7684\u4eba\u7269\uff0c\u4f46\u4ed6\u4eec\u7684\u5f1f\u5b50\u4e5f\u90fd\u662f \u4e2d\u5e74\u4ee5\u4e0a\u7684\u4eba\u4e86\u3002\u800c\u4e14\u8fd9\u4f4d\u891a\u82f1\u96c4\u7684\u5e74\u7eaa\u4e0d\u8fc7\u4e8c\u5341\u6765\u5c81\uff0c\u5373\u4f7f\u6709\u540d\u5e08\u6307\u70b9\uff0c\u4e5f\u4f3c\u4e4e\u4e0d\u5e94\u5c31\u6709 \u5982\u6b64\u9020\u8be3\u3002\u201d \u3000\u3000\u5c60\u51e4\u7b11\u9053\uff1a\u201c\u4ed6\u4eca\u5929\u65e2\u7136\u9732\u4e86\u8fd9\u4e48\u4e00\u624b\uff0c\u60f3\u6765\u4e0d\u81f3\u4e8e\u5c31\u8eb2\u8d77\u6765\u4e0d\u89c1\u6211\u4eec\u7684\u3002\u65e2\u7136\u731c\u4e0d\u900f \u4ed6\u7684\u6765\u5386\uff0c\u90a3\u5c31\u7b97\u4e86\u3002\u53cd\u6b63\u5c06\u6765\u4f1a\u77e5\u9053\u7684\u3002\u54b1\u4eec\u8fd8\u662f\u5148\u559d\u9152\u5427\u3002\u201d \u3000\u3000\u5e86\u529f\u5bb4\u4e0a\u559c\u6c14\u6d0b\u6d0b\uff0c\u5404\u5bb6\u5be8\u4e3b\uff0c\u76f8\u7ee7\u5411\u65b0\u76df\u4e3b\u9053\u8d3a\u3001\u656c\u9152\uff0c\u5b5f\u660e\u971e\u7b11\u9765\u5982\u82b1\uff0c\u4e5f\u662f\u9891\u9891 \u5411\u674e\u601d\u5357\u529d\u9152\u3002 \u3000\u3000\u674e\u601d\u5357\u6709\u4e86\u51e0\u5206\u9152\u610f\uff0c\u53ea\u89c9\u773c\u7684\u4eba\u5f71\u6447\u6643\uff0c\u5411\u4ed6\u529d\u9152\u7684\u5b5f\u660e\u971e\u597d\u50cf\u7a81\u7136\u53d8\u6210\u4e86\u6768\u5a49\uff0c\u674e \u601d\u5357\u5403\u4e86\u4e00\u60ca\uff0c\u5b9a\u775b\u770b\u65f6\uff0c\u5374\u89c1\u5b5f\u660e\u971e\u4e5f\u4f3c\u4e4e\u6709\u70b9\u8be7\u5f02\uff0c\u628a\u624b\u4e2d\u7684\u9152\u676f\u653e\u4e86\u4e0b\u6765\uff0c\u8bf4\u9053\uff1a \u201c\u5357\u54e5\uff0c\u4f60\u5728\u60f3\u4ec0\u4e48\uff1f\u201d\u674e\u601d\u5357\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\uff0c\u6211\u5927\u7ea6\u662f\u559d\u9152\u559d\u5f97\u591a\u4e86\u3002\u201d\u5fc3\u91cc\u5374\u5728\u60f3\u9053\uff1a \u201c\u6211\u4e0d\u80fd\u518d\u60f3\u6768\u5a49\u4e86\u3002\u5979\u5df2\u7ecf\u53e6\u6709\u6240\u5c5e\uff0c\u6211\u518d\u60f3\u5979\uff0c\u53ea\u662f\u81ea\u5bfb\u70e6\u607c\u3002\u201d \u3000\u3000\u5b5f\u5c11\u521a\u9053\uff1a\u201c\u4f60\u4eca\u5929\u4e5f\u591f\u7d2f\u4e86\uff0c\u90a3\u5c31\u5c11\u559d\u70b9\u9152\uff0c\u65e9\u4e9b\u5b89\u6b47\u5427\u3002\u201d \u3000\u3000\u5e2d\u6563\u4e4b\u540e\uff0c\u77f3\u749e\u9053\uff1a\u201c\u601d\u5357\u5144\uff0c\u6211\u9001\u4f60\u56de\u623f\u3002\u201d\u674e\u601d\u5357\u7b11\u9053\uff1a\u201c\u4f60\u6015\u6211\u5f53\u771f\u559d\u9189\u4e86 \u4e48\uff1f\u201d\u4f46\u89c1\u77f3\u749e\u4e00\u756a\u597d\u610f\uff0c\u4ed6\u5f53\u7136\u4e5f\u662f\u4e0d\u4fbf\u62d2\u7edd\u3002 \u3000\u3000\u6b64\u65f6\u5df2\u662f\u4e8c\u66f4\u65f6\u5206\uff0c\u8d70\u51fa\u4e86\u5927\u95e8\uff0c\u77f3\u749e\u9053\uff1a\u201c\u601d\u5357\u5144\uff0c\u4f60\u7cbe\u795e\u5982\u4f55\uff0c\u60f3\u4e0d\u60f3\u73b0\u5728\u5c31 \u7761\uff1f\u201d\u674e\u601d\u5357\u9053\uff1a\u201c\u7d2f\u662f\u6709\u70b9\u7d2f\u7684\uff0c\u4f46\u4e5f\u4e0d\u81f3\u4e8e\u75b2\u5026\u4e0d\u582a\u3002\u201d \u3000\u3000\u77f3\u749e\u9053\uff1a\u201c\u4eca\u665a\u6708\u8272\u5f88\u597d\uff0c\u6211\u5012\u662f\u8fd8\u4e0d\u60f3\u7761\u3002\u201d\u674e\u601d\u5357\u9053\uff1a\u201c\u597d\uff0c\u90a3\u54b1\u4eec\u5c31\u8054\u5e8a\u591c\u8bdd\uff0c \u5c71\u5be8\u4e2d\u7684\u4e8b\u60c5\uff0c\u6211\u65b0\u6765\u4e4d\u5230\uff0c\u4e5f\u60f3\u5411\u4f60\u8bf7\u6559\u3002\u201d \u3000\u3000\u77f3\u749e\u5fae\u7b11\u9053\uff1a\u201c\u65e5\u95f4\u6211\u89c1\u540e\u5c71\u7684\u6885\u82b1\u5df2\u7ecf\u76db\u5f00\uff0c\u54b1\u4eec\u4e0d\u5982\u6708\u591c\u8d4f\u6885\u5982\u4f55\uff1f\u201d\u674e\u601d\u5357\u7b11 \u9053\uff1a\u201c\u96be\u5f97\u77f3\u5144\u6709\u6b64\u96c5\u5174\uff0c\u5c0f\u5f1f\u81ea\u5f53\u5949\u966a\u3002\u201d\u5ffd\u5730\u5fc3\u4e2d\u4e00\u52a8\uff0c\u60f3\u9053\uff1a\u201c\u77f3\u749e\u4f55\u4ee5\u65e0\u7aef\u9080\u6211\u8d4f \u6885\uff1f\u83ab\u975e\u4ed6\u662f\u6709\u4ec0\u4e48\u4e8b\u60c5\u8981\u548c\u6211\u8bf4\uff0c\u5acc\u5728\u5be8\u4e2d\u8bf4\u8bdd\u4e0d\u4fbf\uff1f\u201d \u3000\u3000\u77f3\u749e\u5728\u524d\u5e26\u8def\uff0c\u5230\u4e86\u6885\u6797\uff0c\u6b63\u60f3\u505c\u4e0b\u6765\u4e0e\u674e\u601d\u5357\u8bf4\u8bdd\uff0c\u5ffd\u89c1\u6709\u4e24\u9a91\u9a6c\u8dd1\u4e0a\u5c71\u6765\u3002 \u3000\u3000\u77f3\u749e\u89c9\u5f97\u5947\u602a\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u4e48\u665a\u4e86\uff0c\u600e\u7684\u8fd8\u6709\u4eba\u9a91\u9a6c\u4e0a\u5c71\uff1f\u8dd1\u5f97\u8fd9\u6837\u5feb\uff0c\u96be\u9053\u662f\u5c71 \u4e0b\u53d1\u751f\u4e86\u4ec0\u4e48\u7d27\u6025\u4e4b\u4e8b\uff1f\u201d\u5fc3\u6709\u6240\u7591\uff0c\u53ea\u597d\u628a\u8981\u4e0e\u674e\u601d\u5357\u6240\u8bf4\u7684\u8bdd\uff0c\u6682\u4e14\u6401\u4e0b\uff0c\u51fa\u53bb\u4e00\u770b\u3002 \u3000\u3000\u53ea\u89c1\u662f\u4e00\u4e2a\u5728\u5c71\u4e0b\u4e3b\u6301\u9152\u5e97\u7684\u5c0f\u5934\u76ee\u3002\u8fd9\u9152\u5e97\u662f\u5c71\u5be8\u5f00\u8bbe\u7684\uff0c\u4e00\u6765\u7528\u4f5c\u5c71\u5be8\u7684\u8033\u76ee\uff0c\u4e8c \u6765\u4e5f\u62c5\u8d1f\u7ed9\u5c71\u5be8\u63a5\u5f15\u5ba2\u4eba\u4e0a\u5c71\u7684\u4efb\u52a1\u3002\u53e6\u4e00\u4e2a\u7c97\u8c6a\u6c49\u5b50\uff0c\u77f3\u749e\u5374\u4e0d\u8ba4\u5f97\u3002 \u3000\u3000\u90a3\u5c0f\u5934\u76ee\u89c1\u5230\u526f\u5be8\u4e3b\uff0c\u8fde\u5fd9\u5728\u9a6c\u80cc\u4e0a\u65bd\u793c\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u5927\u54e5\u662f\u4ece\u98de\u9f99\u5c71\u8d76\u6765\u7684\uff0c\u8bf4\u662f \u6709\u8981\u4e8b\u8981\u89c1\u54b1\u4eec\u7684\u5be8\u4e3b\u3002\u5be8\u4e2d\u7684\u5e86\u529f\u5bb4\u4e0d\u77e5\u53ef\u6563\u4e86\u4e48\uff1f\u201d \u3000\u3000\u77f3\u749e\u8d77\u521d\u4ee5\u4e3a\u662f\u5c71\u4e0b\u53d1\u73b0\u654c\u60c5\uff0c\u6b64\u65f6\u542c\u8bf4\u662f\u53cb\u5be8\u7684\u4eba\u524d\u6765\u6c42\u89c1\u5c60\u51e4\uff0c\u65b9\u59cb\u653e\u4e0b\u4e86\u5fc3\u3002\u8fd9 \u4eba\u65e2\u7136\u8bb2\u660e\u4e86\u98de\u9f99\u5c71\u7684\u201c\u8981\u4e8b\u201d\u662f\u8981\u5411\u5c60\u51e4\u5f53\u9762\u9648\u8ff0\u7684\uff0c\u77f3\u749e\u81ea\u662f\u4e0d\u4fbf\u591a\u95ee\u3002\u5f53\u4e0b\u8bf4\u9053\uff1a \u201c\u5e86\u529f\u5bb4\u521a\u6563\uff0c\u5be8\u4e3b\u4e5f\u8bb8\u5c31\u8981\u7761\u4e86\uff0c\u4f60\u4eec\u5feb\u5feb\u53bb\u5427\u3002\u201d \u3000\u3000\u8fd9\u4e24\u4eba\u8d70\u8fc7\u4e4b\u540e\uff0c\u6885\u6797\u4e2d\u91cd\u5f52\u5bc2\u9759\u3002\u674e\u601d\u5357\u7b11\u9053\uff1a\u201c\u77f3\u5927\u54e5\uff0c\u4f60\u600e\u7684\u5ffd\u7136\u6709\u6b64\u96c5\u5174\uff1f\u201d \u3000\u3000\u77f3\u749e\u7b11\u9053\uff1a\u201c\u5b9e\u4e0d\u76f8\u7792\uff0c\u6211\u5e76\u4e0d\u662f\u4e3a\u4e86\u8d4f\u6885\u6765\u7684\u3002\u6211\u53ea\u662f\u4e00\u4e2a\u7c97\u6c49\uff0c\u54ea\u91cc\u61c2\u5f97\u4ec0\u4e48\u8d4f\u82b1 \u73a9\u6708\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u5fc3\u4e2d\u4e00\u52a8\uff0c\u5df2\u77e5\u81ea\u5df1\u731c\u5f97\u4e0d\u9519\uff0c\u4e8e\u662f\u8bf4\u9053\uff1a\u201c\u90a3\u53c8\u662f\u4e3a\u4e86\u4ec0\u4e48\u5462\uff1f\u201d \u3000\u3000\u77f3\u749e\u7565\u4e00\u6c89\u541f\uff0c\u8bf4\u9053\uff1a\u201c\u674e\u5144\uff0c\u8bb0\u5f97\u54b1\u4eec\u90a3\u6b21\u5728\u8499\u53e4\u76f8\u9047\u4e4b\u65f6\uff0c\u4f60\u66fe\u5411\u6211\u6253\u542c\u8fc7\u6768\u59d1\u5a18 \u7684\u6d88\u606f\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9762\u8272\u4e00\u6c89\uff0c\u8bf4\u9053\uff1a\u201c\u73b0\u5728\u6211\u5df2\u4e0d\u60f3\u77e5\u9053\u5979\u7684\u6d88\u606f\u3002\u201d \u3000\u3000\u77f3\u749e\u9053\uff1a\u201c\u5f53\u65f6\u6211\u4e0d\u77e5\u9053\u5979\u7684\u6d88\u606f\uff0c\u4f46\u73b0\u5728\u6211\u5374\u77e5\u9053\u3002\u4f60\u5f53\u771f\u4e0d\u8981\u542c\u4e48\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u5fc3\u4e2d\u9635\u9635\u9178\u75db\uff0c\u60f3\u8bf4\u4e0d\u8981\u518d\u542c\uff0c\u4f46\u5374\u59cb\u7ec8\u8fd8\u662f\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u7528\u8bf4\u6211\u4e5f \u77e5\u9053\u3002\u5509\uff0c\u4f24\u5fc3\u4e4b\u4e8b\uff0c\u8fd8\u63d0\u5b83\u5e72\u561b\uff1f\u201d \u3000\u3000\u77f3\u749e\u9053\uff1a\u201c\u4f60\u77e5\u9053\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u6211\u77e5\u9053\u5979\u8fd8\u6d3b\u5728\u4eba\u95f4\uff0c\u4f46\u6211\u5df2\u4e0d\u8981\u518d\u89c1\u5979\u4e86\u3002\u201d \u3000\u3000\u77f3\u749e\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u77f3\u5927\u54e5\uff0c\u4f60\u4e0d\u8981\u903c\u6211\u8bf4\u51fa\u6765\u597d\u4e0d\u597d\uff1f\u201d\u5fc3\u60f3\uff1a\u201c\u4f73\u4eba\u5df2\u5c5e\u6c99\u54a4\u5229\uff0c\u4e49\u58eb\u4eca \u65e0\u53e4\u62bc\u8859\u3002\u5c60\u9f99\u662f\u62a2\u4e86\u5a49\u59b9\u7684\u2018\u6c99\u54a4\u5229\u2019\uff0c\u4f60\u77f3\u749e\u5374\u4e0d\u80fd\u505a\u6211\u7684\u2018\u53e4\u62bc\u8859\u2019\u3002\u6211\u8bf4\u7ed9\u4f60\u542c\uff0c \u53c8\u6709\u4f55\u7528\uff1f\u201d \u3000\u3000\u5fc3\u5ff5\u672a\u5df2\uff0c\u53ea\u542c\u5f97\u77f3\u749e\u5df2\u5728\u7b11\u9053\uff1a\u201c\u674e\u5144\uff0c\u5176\u5b9e\u4f60\u4e0d\u8bf4\uff0c\u6211\u4e5f\u77e5\u9053\u4f60\u662f\u5728\u60f3\u4e9b\u4ec0\u4e48\u3002\u674e \u5144\uff0c\u4f60\u60f3\u5f97\u5b8c\u5168\u9519\u4e86\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u4e0d\u89c9\u4e00\u6014\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u600e\u77e5\u9053\u6211\u5728\u60f3\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u77f3\u749e\u9053\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u4ee5\u4e3a\u4f73\u4eba\u5df2\u5c5e\u6c99\u54a4\u5229\uff1f\u9519\u4e86\uff01\u9519\u4e86\uff0c\u5927\u5927\u7684\u9519\u4e86\uff01\u201d \u3000\u3000\u77f3\u749e\u6b64\u8a00\u4e00\u51fa\uff0c\u674e\u601d\u5357\u4e0d\u89c9\u8df3\u4e86\u8d77\u6765\uff0c\u5931\u58f0\u56b7\u9053\uff1a\u201c\u77f3\u5927\u54e5\uff0c\u4f60\u3001\u4f60\u77e5\u9053\u4e86\u4e00\u4e9b\u4ec0 \u4e48\uff1f\u201d \u3000\u3000\u77f3\u749e\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u53d7\u4e86\u5c60\u9f99\u7684\u9a97\uff0c\u4e0a\u4e86\u4ed6\u7684\u5f53\u4e86\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u5446\u4e86\u4e00\u9635\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u7684\u610f\u601d\u662f\u6768\u59d1\u5a18\u5ac1\u7ed9\u5c60\u9f99\u4e4b\u4e8b\uff0c\u4e43\u662f\u5047\u7684\uff1f\u201d \u3000\u3000\u77f3\u749e\u7b11\u9053\uff1a\u201c\u5c60\u9f99\u867d\u7136\u662f\u6211\u7684\u5927\u5e08\u54e5\uff0c\u4f46\u6211\u8fd8\u662f\u8981\u8bf4\uff0c\u4ed6\u60f3\u8981\u6768\u59d1\u5a18\u5440\uff0c\u90a3\u6bd4\u765e\u86e4\u87c6\u60f3 \u5403\u5929\u9e45\u8089\u8fd8\u96be\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u60ca\u7591\u4e0d\u5b9a\uff0c\u8bf4\u9053\uff1a\u201c\u6b64\u8bdd\u5f53\u771f\uff1f\u201d \u3000\u3000\u77f3\u749e\u53f9\u4e86\u53e3\u6c14\uff0c\u4e0d\u7b54\u674e\u601d\u5357\u7684\u8bdd\uff0c\u674e\u601d\u5357\u9053\uff1a\u201c\u77f3\u5927\u54e5\uff0c\u4f60\u600e\u4e48\u4e86\uff1f\u201d \u3000\u3000\u77f3\u749e\u9053\uff1a\u201c\u6211\u662f\u4e3a\u6768\u5a49\u53f9\u606f\u3002\u674e\u5144\uff0c\u4f60\u4e0e\u5979\u76f8\u5904\u5df2\u975e\u4e00\u65e5\uff0c\u600e\u7684\u8fd9\u6837\u4e0d\u76f8\u4fe1\u5979\uff01\u6211\u5df2\u7ecf \u8bf4\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u5c60\u9f99\u53ea\u662f\u765e\u86e4\u87c6\u60f3\u5403\u5929\u9e45\u8089\u800c\u5df2\uff0c\u4f60\u8fd8\u4e0d\u80af\u76f8\u4fe1\uff01\u82e5\u7ed9\u6768\u59d1\u5a18\u77e5\u9053\uff0c\u5979\u4e00\u5b9a \u8981\u53f9\u8bc6\u9519\u4f60\u8fd9\u4e2a\u4eba\u4e86\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u53c8\u60ca\u53c8\u559c\uff0c\u8bf4\u9053\uff1a\u201c\u4f46\u4ed6\u4eec\u4e4b\u4e8b\uff0c\u6211\u5374\u662f\u4eb2\u81ea\u89c1\u5230\u7684\uff0c\u5e76\u975e\u53ea\u51ed\u5c60\u9f99\u4e00\u9762\u4e4b \u8bcd\u3002\u201d \u3000\u3000\u77f3\u749e\u9053\uff1a\u201c\u4f60\u89c1\u5230\u4e86\u4ed6\u4eec\u540c\u5728\u4e00\u8d77\uff1f\u201d\u674e\u601d\u5357\u60f3\u8d77\u90a3\u65e5\u6240\u89c1\uff0c\u4e0d\u89c9\u53c8\u662f\u5fc3\u75db\u5982\u7ede\uff0c\u4f4e\u4f4e \u7684\u201c\u55ef\u201d\u4e86\u4e00\u58f0\u3002 \u3000\u3000\u77f3\u749e\u9053\uff1a\u201c\u4f60\u5f53\u771f\u5728\u90a3\u5c0f\u5ba2\u5e97\u7684\u623f\u4e2d\u89c1\u5230\u4e86\u573a\u59d1\u5a18\uff1f\u6211\u770b\uff0c\u6050\u6015\u4f60\u53ea\u662f\u89c1\u5230\u4e86\u5c60\u9f99 \u5427\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u4e0d\u89c9\u53c8\u8df3\u4e86\u8d77\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u65e5\u4e4b\u4e8b\uff0c\u600e\u7684\u4f60\u4e5f\u77e5\u9053\u4e86\u4e48\uff1f\u201d \u3000\u3000\u77f3\u749e\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\u3002\u6211\u624d\u662f\u4e0d\u4f46\u89c1\u5230\u4e86\u5c60\u9f99\uff0c\u800c\u4e14\u4e5f\u89c1\u5230\u4e86\u6768\u5a49\u3002\u6211\u662f\u6bd4\u4f60\u65e9\u4e00\u5929\u5230\u90a3 \u5c0f\u9547\u7684\uff0c\u53ef\u60dc\u54b1\u4eec\u9519\u8fc7\u4e86\u89c1\u9762\u7684\u673a\u4f1a\u3002\u5426\u5219\u4f60\u5bf9\u6768\u59d1\u5a18\u4e5f\u4e0d\u4f1a\u8d77\u8fd9\u6837\u6df1\u7684\u8bef\u4f1a\u4e86\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u5598\u8fc7\u4e86\u4e00\u53e3\u6c14\uff0c\u8fde\u5fd9\u6293\u7740\u77f3\u749e\u7684\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u7a76\u7adf\u662f\u600e\u4e48\u4e00\u56de\u4e8b\u60c5\uff0c\u77f3\u5927\u54e5\uff0c\u4f60 \u5feb\u8bf4\u7ed9\u6211\u542c\uff01\u201d \u3000\u3000\u77f3\u749e\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u597d\uff0c\u6211\u521a\u624d\u8981\u8bf4\u4f60\u5374\u4e0d\u8981\u542c\uff0c\u73b0\u5728\u4f60\u53ef\u662f\u8981\u542c\u4e86\u5427\uff1f\u201d\u674e\u601d\u5357\u9053\uff1a \u201c\u77f3\u5927\u54e5\uff0c\u6c42\u6c42\u4f60\u522b\u5356\u5173\u5b50\u4e86\u3002\u6211\u8fd9\u53a2\u5411\u4f60\u8d54\u793c\u5566\uff01\u201d \u3000\u3000\u77f3\u749e\u4e0d\u5fcd\u89c1\u674e\u601d\u5357\u592a\u8fc7\u7740\u6025\uff0c\u8fd9\u624d\u6162\u6761\u65af\u7406\u5730\u8bf4\u4e86\u51fa\u6765\u3002\u4ece\u90a3\u665a\u600e\u6837\u63ed\u7834\u5c60\u9f99\u7684\u5978\u8c0b\uff0c \u600e\u4e48\u6253\u788e\u4ed6\u7528\u6765\u9a97\u6768\u5a49\u6240\u996e\u7684\u836f\u9152\u9152\u676f\u7b49\u7b49\uff0c\u4e00\u4e00\u7684\u8bf4\u4e86\u51fa\u6765\uff0c\u8ddf\u7740\u53c8\u628a\u6768\u5a49\u544a\u8bc9\u4ed6\u7684\uff0c\u5c60 \u9f99\u5982\u4f55\u5192\u5145\u597d\u4eba\uff0c\u54c4\u9a97\u6768\u5a49\u4e0e\u4ed6\u540c\u884c\uff0c\u4f46\u6768\u5a49\u59cb\u7ec8\u6ca1\u6709\u4e0a\u4ed6\u7684\u5f53\uff0c\u7b49\u7b49\u4e8b\u60c5\uff0c\u4e5f\u90fd\u5bf9\u674e\u601d\u5357 \u8bf4\u4e86\u3002 \u3000\u3000\u674e\u601d\u5357\u542c\u4e86\u4e4b\u540e\uff0c\u4e0d\u89c9\u53c8\u662f\u75db\u6068\uff0c\u53c8\u662f\u7f9e\u60ed\u3002\u75db\u6068\u5c60\u9f99\u624b\u6bb5\u7684\u5351\u9119\uff0c\u60ed\u6127\u81ea\u5df1\u7684\u7cca\u6d82\uff0c \u77f3\u749e\u7b11\u9053\uff1a\u201c\u73b0\u5728\u4f60\u660e\u767d\u4e86\u5427\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u6ee1\u9762\u901a\u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u77f3\u5927\u54e5\uff0c\u6211\u8fd9\u6837\u7cca\u6d82\uff0c\u6211\u81ea\u5df1\u4e5f\u4e0d\u80fd\u9976\u6055\u81ea\u5df1\u3002\u73b0\u5728\u6211\u53ea\u6c42 \u4f60\u544a\u8bc9\u6211\uff0c\u6768\u59d1\u5a18\u5728\u54ea\u513f\uff1f\u6211\u8981\u53bb\u5411\u5979\u8d1f\u8346\u8bf7\u7f6a\uff0c\u624d\u5f97\u5fc3\u5b89\uff01\u201d \u3000\u3000\u77f3\u749e\u6c89\u541f\u534a\u664c\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u662f\u89c1\u5230\u4e86\u6768\u59d1\u5a18\uff0c\u4f46\u5979\u6b64\u9645\u4eba\u5728\u4f55\u5904\uff0c\u6211\u5374\u4e0d\u77e5\u3002\u4f55 \u51b5\u4f60\u73b0\u5728\u65b0\u4efb\u76df\u4e3b\uff0c\u53c8\u600e\u80fd\u629b\u5f00\u5927\u4e8b\u4e0d\u7406\uff0c\u524d\u53bb\u627e\u5979\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u6211\u5e76\u975e\u8981\u79bb\u5f00\u5c71\u5be8\uff0c\u629b\u4e0b\u8fd9\u91cc\u7684\u4e8b\u60c5\u4e0d\u7ba1\uff0c\u53ea\u662f\u8981\u77e5\u9053\u5979\u7684\u4e0b\u843d\uff0c\u624d\u597d\u8bbe \u6cd5\u627e\u5979\u3002\u5979\u4e0e\u6211\u4e00\u6837\u6709\u7740\u56fd\u4ec7\u5bb6\u6068\uff0c\u627e\u4e86\u5979\u6765\uff0c\u4e0d\u6b63\u662f\u53ef\u4ee5\u4e3a\u4e49\u519b\u589e\u52a0\u4e00\u4efd\u529b\u91cf\u5417\uff1f\u77f3\u5927 \u54e5\uff0c\u4f60\u4e00\u5b9a\u77e5\u9053\u5979\u7684\u4e0b\u843d\u7684\uff0c\u4e3a\u4ec0\u4e48\u4f60\u4e0d\u544a\u8bc9\u6211\u5462\uff1f\u201d \u3000\u3000\u77f3\u749e\u7b11\u9053\uff1a\u201c\u4f60\u8981\u5411\u6768\u59d1\u5a18\u8d1f\u8346\u8bf7\u7f6a\uff0c\u4e5f\u4e0d\u5fc5\u6025\u5728\u4e00\u65f6\u3002\u53ea\u8981\u4f60\u5f53\u771f\u662f\u5fc3\u91cc\u6709\u5979\uff0c\u603b\u6709 \u4e00\u5929\u5979\u4f1a\u660e\u767d\u4f60\u7684\u5fc3\u4e8b\uff0c\u81ea\u5df1\u6765\u627e\u4f60\u7684\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u5446\u4e86\u4e00\u5446\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u4e0d\u662f\u6768\u5a49\u7684\u610f\u601d\uff1f\u201d\u77f3\u749e\u9053\uff1a\u201c\u4f60\u4e0d\u5fc5\u7ba1\u662f\u4e0d\u662f\u5979\u7684\u610f \u601d\uff0c\u4f60\u7167\u6211\u7684\u8bdd\u505a\uff0c\u4e00\u5b9a\u4e0d\u4f1a\u9519\u3002\u201d\u8bdd\u4e2d\u4e4b\u610f\u5df2\u662f\u9ed8\u8ba4\u3002 \u3000\u3000\u674e\u601d\u5357\u8fd8\u60f3\u518d\u95ee\uff0c\u5ffd\u89c1\u4e00\u4e2a\u5c0f\u4e2b\u9b1f\u5411\u4ed6\u4eec\u8d70\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u77f3\u7edf\u9886\uff0c\u539f\u6765\u4f60\u548c\u76df\u4e3b\u5728\u8fd9 \u513f\uff0c\u53ef\u627e\u5f97\u6211\u597d\u82e6\u3002\u201d\u8fd9\u5c0f\u4e2b\u9b1f\u6b63\u662f\u5c60\u51e4\u7684\u8d34\u8eab\u4f8d\u5973\u6625\u5170\u3002 \u3000\u3000\u77f3\u749e\u9053\uff1a\u201c\u6709\u4ec0\u4e48\u4e8b\u5417\uff1f\u201d\u6625\u5170\u9053\uff1a\u201c\u5c0f\u59d0\u8bf7\u4f60\u53bb\u89c1\u4e00\u89c1\u5979\u3002\u201d\u77f3\u749e\u731c\u60f3\u53ef\u80fd\u662f\u4e0e\u98de\u9f99 \u5c71\u7684\u6765\u4eba\u6709\u5173\uff0c\u4e8e\u662f\u8bf4\u9053\uff1a\u201c\u674e\u5144\uff0c\u4f60\u4e5f\u591f\u7d2f\u4e86\uff0c\u4eca\u665a\u5148\u629b\u5f00\u5fc3\u4e8b\uff0c\u597d\u597d\u7761\u4e00\u89c9\u5427\u3002\u660e\u5929\u4e00 \u65e9\uff0c\u6211\u518d\u6765\u627e\u4f60\u3002\u201d\u56e0\u4e3a\u5c60\u51e4\u5e76\u6ca1\u8bf7\u674e\u601d\u5357\uff0c\u77f3\u749e\u81ea\u662f\u4e0d\u4fbf\u9080\u4ed6\u540c\u5f80\u3002 \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u4f60\u53bb\u5427\uff0c\u6211\u5728\u8fd9\u91cc\u518d\u5f85\u4e00\u4f1a\u3002\u4f60\u770b\u8fd9\u6885\u82b1\u5f00\u5f97\u591a\u597d\uff01\u201d\u4ed6\u4e0d\u60f3\u7ed9\u8fd9\u5c0f\u4e2b\u9b1f \u7aa5\u7834\u4ed6\u7684\u5fc3\u4e8b\uff0c\u662f\u4ee5\u7528\u8d4f\u6885\u6765\u4f5c\u63a9\u9970\u3002\u6625\u5170\u201c\u5657\u55e4\u201d\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u539f\u6765\u674e\u76df\u4e3b\u4e5f\u662f\u8fd9\u6837\u6b22 \u559c\u6885\u82b1\u3002\u8fd9\u51e0\u6811\u6885\u82b1\uff0c\u8fd8\u662f\u90a3\u5e74\u5b5f\u59d1\u5a18\u6765\u7684\u65f6\u5019\uff0c\u4eb2\u624b\u683d\u7684\u5462\u3002\u201d \u3000\u3000\u77f3\u749e\u4e0e\u90a3\u5c0f\u4e2b\u9b1f\u8d70\u540e\uff0c\u674e\u601d\u5357\u72ec\u81ea\u5f98\u5f8a\uff0c\u601d\u5982\u6f6e\u6d8c\uff0c\u84e6\u5730\u5fc3\u4e2d\u4e00\u52a8\uff0c\u60f3\u9053\uff1a\u201c\u5a49\u59b9\u4e0d\u77e5 \u662f\u5426\u8bef\u4f1a\u6211\u5df2\u79fb\u60c5\u522b\u604b\uff0c\u6240\u4ee5\u5979\u624d\u4e0d\u80af\u51fa\u6765\u4e0e\u6211\u76f8\u89c1\u5462\uff1f\u201d \u3000\u3000\u8ddf\u7740\u4ed6\u53c8\u60f3\u8d77\u4e86\u5c60\u9f99\u65e5\u95f4\u548c\u4ed6\u6240\u8bf4\u7684\u8bdd\uff0c\u542c\u5c60\u9f99\u7684\u53e3\u6c14\uff0c\u4f3c\u4e4e\u662f\u7591\u5fc3\u6768\u5a49\u5df2\u5728\u5c71\u5be8\u4e4b \u4e2d\u3002\u662f\u4ee5\u51fa\u8a00\u8bd5\u63a2\uff0c\u8bd5\u63a2\u4ed6\u662f\u4e0d\u662f\u5df2\u7ecf\u548c\u6768\u5a49\u89c1\u4e86\u9762\u3002 \u3000\u3000\u674e\u601d\u5357\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5c60\u9f99\u8fd9\u53ae\u8bf4\u7684\u8bdd\u5f53\u7136\u4e0d\u80fd\u76f8\u4fe1\uff0c\u4f46\u4ed6\u4f55\u4ee5\u65e0\u7aef\u7aef\u7684\u4f1a\u6000\u7591\u5a49\u59b9\u5728\u8fd9 \u5c71\u5be8\u4e4b\u4e2d\uff0c\u60f3\u6765\u6216\u8bb8\u4e0d\u81f3\u4e8e\u662f\u7a7a\u7a74\u6765\u98ce\u5427\uff1f\u201d\u518d\u60f3\uff1a\u201c\u5c60\u9f99\u4e2d\u4e86\u6bd2\u9f99\u9556\uff0c\u5df2\u6577\u4e0a\u89e3\u836f\uff0c\u4ed6\u5982 \u8d56\u5728\u8fd9\u91cc\u4e0d\u80af\u8d70\u3002\u5c71\u5be8\u4e2d\u7684\u5934\u8033\u90fd\u662f\u654c\u89c6\u4ed6\u7684\uff0c\u4ed6\u4f55\u4ee5\u7518\u613f\u5fcd\u53d7\u8fd9\u4efd\u96be\u582a\uff1f\u83ab\u975e\u8fd9\u662f\u4e00\u4e2a\u7c4d \u53e3\uff0c\u4ed6\u8981\u7559\u5728\u8fd9\u91cc\u4fa6\u5bdf\u5a49\u59b9\uff1f\u201d \u3000\u3000\u6b63\u5728\u601d\u7591\u4e0d\u5b9a\uff0c\u5ffd\u7136\u82b1\u679d\u98a4\u52a8\uff0c\u6709\u4e2a\u5c11\u5973\u7a81\u7136\u4ece\u82b1\u6811\u4e1b\u4e2d\u94bb\u51fa\uff0c\u6084\u6ca1\u58f0\u5730\u5c31\u51fa\u73b0\u5728\u4ed6\u7684 \u9762\u524d\u3002 \u3000\u3000\u674e\u601d\u5357\u5fc3\u5934\u201c\u535c\u901a\u201d\u4e00\u8df3\uff0c\u62ac\u5934\u770b\u65f6\uff0c\u53ea\u89c1\u4e00\u4e2a\u5c11\u5973\uff0c\u7b11\u76c8\u76c8\u7684\u7ad9\u5728\u4ed6\u7684\u9762\u524d\u3002\u674e\u601d\u5357 \u5927\u5931\u6240\u671b\uff0c\u4e0d\u89c9\u201c\u554a\u5440\u201d\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u660e\u971e\uff0c\u539f\u6765\u662f\u4f60\uff01\u201d \u3000\u3000\u5b5f\u660e\u971e\u6709\u70b9\u8be7\u5f02\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u4ee5\u4e3a\u662f\u8c01\u3002\u201d\u674e\u601d\u5357\u9762\u4e0a\u4e00\u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4ee5\u4e3a\u662f\u77f3\u749e\u56de \u6765\u4e86\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u4ed6\u548c\u5c60\u51e4\u89c1\u4e86\u9762\uff0c\u6050\u6015\u8fd8\u6709\u8bb8\u591a\u4f53\u5df1\u7684\u8bdd\u513f\u8981\u8bf4\u5462\u3002\u201d\u63a5\u7740\u9053\uff1a\u201c\u6211\u542c\u5f97 \u77f3\u749e\u8bf4\u4f60\u5728\u8fd9\u513f\uff0c\u4e0d\u77e5\u4f60\u8d70\u4e86\u6ca1\u6709\uff0c\u59d1\u4e14\u8fc7\u6765\u770b\u770b\uff0c\u8c01\u77e5\u4f60\u8fd8\u5728\u8fd9\u513f\u3002\u600e\u7684\u4f60\u6076\u6597\u4e86\u4e00\u6574 \u5929\uff0c\u5c45\u7136\u8fd8\u6709\u5982\u6b64\u96c5\u5174\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u77f3\u749e\u4e0d\u60f3\u65e9\u7761\uff0c\u6211\u966a\u4ed6\u51fa\u6765\u804a\u804a\uff0c\u600e\u7684\u4f60\u4e5f\u8fd8\u6ca1\u6709\u7761\uff1f\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u6211\u4e5f\u662f\u966a\u7239\u7239\u8bf4\u8bdd\u3002\u7239\u7239\u4eca\u5929\u89c1\u4e86\u4f60\u5341\u5206\u9ad8\u5174\uff0c\u8fde\u8fde\u5bf9\u6211\u5938\u8d5e\u4f60\u5462\u3002\u8bf4\u4f60 \u4e0d\u4f46\u6b66\u529f\u597d\uff0c\u4eba\u54c1\u4e5f\u597d\u3002\u66f4\u96be\u5f97\u7684\u662f\u5c11\u5e74\u8001\u6210\uff0c\u582a\u5f53\u91cd\u4efb\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u7b11\u9053\uff1a\u201c\u522b\u7ed9\u6211\u8138\u4e0a\u8d34\u91d1\u4e86\uff0c\u8981\u4e0d\u662f\u4f60\u7239\u7239\u6070\u597d\u4eca\u65e5\u8d76\u5230\uff0c\u6df3\u4e8e\u5468\u65e9\u5df2\u505a\u4e86\u76df\u4e3b \u4e86\uff0c\u6211\u60f3\u8981\u62c5\u5f53\u91cd\u4efb\uff0c\u4ea6\u662f\u4e0d\u80fd\u3002\u5bf9\u5566\uff0c\u5c60\u51e4\u627e\u77f3\u5927\u54e5\uff0c\u4e0d\u77e5\u53ef\u662f\u4e3a\u4e86\u98de\u9f99\u5c71\u4e4b\u4e8b\uff1f\u201d\u4ed6\u4e0d \u613f\u610f\u5b5f\u660e\u971e\u8001\u662f\u8c08\u4ed6\uff0c\u662f\u4ee5\u8bf4\u4e86\u51e0\u53e5\uff0c\u4fbf\u5373\u626d\u8f6c\u8bdd\u9898\u3002 \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u98de\u9f99\u5c71\u7684\u7aa6\u5be8\u4e3b\u7aa6\u5b89\u5e73\u662f\u6709\u7d27\u8981\u7684\u4e8b\u6765\u5411\u5c60\u51e4\u6c42\u63f4\u3002\u4e0d\u8fc7\uff0c\u5c60\u51e4\u627e \u77f3\u749e\u5546\u91cf\uff0c\u5374\u4e0d\u4ec5\u4ec5\u662f\u4e3a\u4e86\u98de\u9f99\u5c71\u4e4b\u4e8b\uff0c\u800c\u662f\u4e3a\u4e86\u5979\u7684\u54e5\u54e5\u3002\u5bf9\u5566\uff0c\u6211\u6b63\u60f3\u95ee\u4f60\uff0c\u5c60\u9f99\u662f\u5426 \u548c\u4f60\u6709\u4ec7\uff1f\u4f60\u4eca\u5929\u548c\u4ed6\u4ea4\u624b\u4e4b\u65f6\uff0c\u597d\u50cf\u662f\u975e\u5e38\u6068\u4ed6\u3002\u5f53\u65f6\u6211\u548c\u5c60\u51e4\u90fd\u6709\u70b9\u63d0\u5fc3\u540a\u80c6\u5462\u3002\u201d\u8fd9 \u4e2a\u7591\u95ee\uff0c\u5b5f\u660e\u971e\u662f\u65e9\u5df2\u60f3\u95ee\u674e\u601d\u5357\u7684\u4e86\u3002\u53ea\u56e0\u4e0d\u4fbf\u5728\u4eba\u524d\u63d0\u8d77\uff0c\u76f4\u5230\u6b64\u523b\uff0c\u4e24\u4eba\u76f8\u5bf9\uff0c\u624d\u6709 \u673a\u4f1a\u53d1\u95ee\u3002 \u3000\u3000\u674e\u601d\u5357\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u5bf9\u4ed6\u7684\u786e\u662f\u5341\u5206\u75db\u6068\u7684\uff0c\u4f46\u73b0\u5728\u5df2\u6ca1\u6709\u90a3\u4e48\u6068\u4e86\u3002\u201d \u3000\u3000\u76c2\u660e\u971e\u8be7\u9053\uff1a\u201c\u8fd9\u53c8\u662f\u4e3a\u4e86\u4ec0\u4e48\uff1f\u201d\u672c\u6765\u5979\u5e94\u63a5\u7740\u8bf4\u98de\u9f99\u5c71\u4e4b\u4e8b\u7684\uff0c\u4f46\u56e0\u597d\u5947\u5fc3\u8d77\uff0c\u5374 \u662f\u975e\u5f97\u6253\u7834\u8fd9\u4e2a\u95f7\u846b\u82a6\u4e0d\u53ef\u3002 \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u5176\u5b9e\u6211\u4e0e\u5c60\u9f99\u4e5f\u6ca1\u4ec0\u4e48\u6df1\u4ec7\u5927\u6068\uff0c\u4e0d\u8fc7\u4ed6\u4e3a\u4eba\u4e0d\u7aef\uff0c\u6211\u548c\u4ed6\u4ea4\u624b\u4e4b\u65f6\uff0c\u81ea \u662f\u96be\u514d\u5bf9\u4ed6\u50e7\u6076\u3002\u4e5f\u8bb8\u56e0\u6b64\u5f62\u4e4b\u8f9e\u8272\uff0c\u7ed9\u4f60\u770b\u51fa\u6765\u4e86\u3002\u4f46\u4ed6\u73b0\u5728\u613f\u610f\u7559\u5728\u5bb6\u91cc\u517b\u4f24\uff0c\u6216\u8bb8\u4ed6 \u5df2\u6709\u4e86\u4e00\u70b9\u6094\u8fc7\u4e4b\u5fc3\u3002\u6240\u4ee5\u6211\u4e5f\u5c31\u4e0d\u90a3\u4e48\u75db\u6068\u4ed6\u4e86\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u5e76\u975e\u662f\u60f3\u7792\u7740\u6768\u5a49\u7684\u4e8b\u60c5\uff0c\u4e0d\u8fc7\u5b5f\u660e\u971e\u95ee\u4ed6\u4f55\u4ee5\u75db\u6068\u5c60\u9f99\uff0c\u4ed6\u5374\u662f\u4e0d\u4fbf\u8bf4\u51fa\u4ed6\u66fe \u6000\u7591\u5c60\u9f99\u52fe\u642d\u4e0a\u4ed6\u7684\u672a\u5a5a\u59bb\uff0c\u53ea\u597d\u542b\u542b\u7cca\u7cca\u5730\u7b54\u590d\u5979\u3002 \u3000\u3000\u5b5f\u660e\u971e\u5374\u4ee5\u4e3a\u674e\u601d\u5357\u8bf4\u7684\u201c\u884c\u4e3a\u4e0d\u7aef\u201d\uff0c\u4e43\u662f\u6307\u5c60\u9f99\u52fe\u7ed3\u6df3\u4e8e\u5468\u7236\u5b50\uff0c\u610f\u6b32\u6295\u9760\u8499\u53e4\u8fd9 \u4e9b\u4e8b\u60c5\u3002\u4e8e\u662f\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5c60\u9f99\u4ea4\u4e0a\u532a\u4eba\uff0c\u8d70\u4e0a\u90aa\u8def\uff0c\u7684\u786e\u662f\u4ee4\u4eba\u618e\u6068\u3002\u4f46\u4f60\u4ee5\u4e3a\u4ed6\u4f1a\u6094 \u8fc7\uff0c\u6050\u6015\u8fd8\u662f\u60f3\u5f97\u592a\u597d\u4e86\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u6014\u4e86\u4e00\u6014\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u7684\u610f\u601d\u662f\u8bf4\u5c60\u9f99\u85c9\u53e3\u5728\u5bb6\u517b\u75c5\uff0c\u53ef\u80fd\u6709\u4ec0\u4e48\u56fe\u8c0b\uff1f\u4f46\u5c71\u5be8 \u91cc\u7684\u5927\u5c0f\u5934\u76ee\uff0c\u90fd\u662f\u5c60\u51e4\u7684\u4eba\u4e86\uff0c\u4ed6\u5373\u4f7f\u4e0d\u6000\u597d\u610f\uff0c\u6050\u6015\u4e5f\u4e0d\u80fd\u4e3a\u6240\u6b32\u4e3a\u5427\uff1f\u660e\u971e\uff0c\u4f46\u4f60\u4ee5 \u4e3a\u4ed6\u5728\u56fe\u8c0b\u4ec0\u4e48\u5462\uff1f\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u4f60\u8bf4\u5f97\u4e0d\u9519\uff0c\u8981\u5728\u8fd9\u5c71\u5be8\u91cc\u5174\u98ce\u4f5c\u6d6a\uff0c\u4ed6\u5927\u7ea6\u662f\u505a\u4e0d\u5230\u7684\u3002\u4e0d\u8fc7\uff0c\u4ed6\u8be1\u8ba1 \u591a\u6e4d\uff0c\u82e5\u662f\u8981\u5728\u6697\u4e2d\u7834\u574f\u4ec0\u4e48\uff0c\u4e5f\u672a\u5fc5\u4e0d\u80fd\u505a\u5230\uff0c\u6545\u6b64\uff0c\u5c60\u51e4\u548c\u6211\uff0c\u867d\u7136\u4e0d\u77e5\u4ed6\u662f\u56fe\u8c0b\u4ec0 \u4e48\uff0c\u4f46\u603b\u89c9\u5f97\u662f\u8981\u63d0\u9632\u7684\u597d\u3002\u800c\u4e14\u5c60\u9f99\u597d\u6b79\u662f\u5c60\u51e4\u7684\u54e5\u54e5\uff0c\u4ed6\u4e00\u5b9a\u8981\u8d56\u5728\u5bb6\u91cc\u4e0d\u8d70\uff0c\u4e5f\u4ee4\u5c60 \u51e4\u5341\u5206\u96be\u5904\u3002\u5c60\u51e4\u5c31\u662f\u56e0\u6b64\uff0c\u624d\u8981\u627e\u77f3\u749e\u5546\u91cf\u7684\u3002\u201d \u3000\u3000\u5c60\u9f99\u627e\u4e2a\u85c9\u53e3\uff0c\u6709\u6240\u56fe\u8c0b\uff0c\u8fd9\u4e00\u5c42\u674e\u601d\u5357\u65e9\u5df2\u60f3\u5230\u3002\u4e0d\u8fc7\u4ed6\u60f3\u5230\u7684\u8fd8\u4e0d\u662f\u5c60\u9f99\u7834\u574f\u4e49\u519b \u6297\u8499\u4e4b\u4e8b\uff0c\u800c\u662f\u62c5\u5fc3\u4ed6\u8981\u7559\u5728\u5c71\u4e0a\u4fa6\u5bdf\u6768\u5a49\u3002\u542c\u4e86\u5b5f\u660e\u971e\u7684\u8bdd\u4e4b\u540e\uff0c\u674e\u601d\u5357\u591a\u4e86\u4e00\u5c42\u8b66\u60d5\u3002 \u674e\u601d\u5357\u9053\uff1a\u201c\u98de\u9f99\u5c71\u4e0d\u77e5\u51fa\u4e86\u4ec0\u4e48\u4e8b\u60c5\uff0c\u53ef\u4e0d\u53ef\u4ee5\u8ba9\u6211\u77e5\u9053\uff1f\u201d \u3000\u3000\u5b5f\u660e\u971e\u7b11\u9053\uff1a\u201c\u4f60\u662f\u76df\u4e3b\uff0c\u8fd9\u4ef6\u4e8b\u60c5\u672c\u6765\u662f\u5e94\u8be5\u7980\u62a5\u4f60\u7684\u3002\u4f46\u5c60\u51e4\u4e0d\u77e5\u4f60\u8fd8\u672a\u7761\uff0c\u6050\u6015 \u59a8\u788d\u4e86\u4f60\u7684\u7761\u7720\uff0c\u6240\u4ee5\u4e0d\u6562\u627e\u4f60\uff0c\u4f60\u522b\u591a\u5fc3\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u7b11\u9053\uff1a\u201c\u6211\u7684\u6c14\u91cf\u4e0d\u4f1a\u8fd9\u6837\u72ed\u7a84\u7684\u3002\u6211\u4eca\u5929\u624d\u505a\u76df\u4e3b\uff0c\u98de\u9f99\u5c71\u7684\u4eba\uff0c\u5f53\u7136\u4e5f\u8fd8\u672a \u66fe\u77e5\u9053\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u98de\u9f99\u5c71\u5728\u4ec0\u4e48\u5730\u65b9\uff0c\u4f60\u77e5\u9053\u4e86\u4e48\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u662f\u4e0d\u662f\u5728\u6cb3\u5357\u5317\u90e8\uff0c\u4e0e\u9655\u897f\u4ea4\u754c\u7684\u5730\u65b9\uff1f\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u4e0d\u9519\u3002\u8fd9\u4e2a\u5730\u65b9\uff0c\u6b63\u662f\u8499\u53e4\u9791\u5b50\u8fdb\u4fb5\u4e2d\u539f\u6240\u5fc5\u7ecf\u7684\u8981\u9053\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u5403\u4e86\u4e00\u60ca\uff0c\u8fde\u5fd9\u95ee\u9053\uff1a\u201c\u53ef\u662f\u5df2\u7ecf\u53d1\u73b0\u654c\u8e2a\uff1f\u201d \u3000\u3000\u5b5f\u660e\u971e\u70b9\u4e86\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\u3002\u8fd9\u6b21\u5c60\u51e4\u6492\u4e0b\u7eff\u6797\u5e16\uff0c\u9080\u8bf7\u5404\u5bb6\u5be8\u4e3b\u5230\u4f1a\uff0c\u98de\u9f99\u5c71\u6ca1 \u6d3e\u4eba\u6765\uff0c\u8d77\u521d\u6211\u4eec\u4ee5\u4e3a\u662f\u8def\u8fdc\u7684\u5173\u7cfb\uff0c\u73b0\u5728\u624d\u77e5\u9053\uff0c\u4ed6\u4eec\u6b63\u662f\u5728\u5168\u529b\u51c6\u5907\u62b5\u5fa1\u9791\u5b50\u7684\u5165 \u4fb5\uff01\u201d \u3000\u3000\u674e\u6069\u5357\u7b97\u4e86\u4e00\u7b97\u8def\u7a0b\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u79bb\u5f00\u897f\u590f\u4e4b\u65f6\uff0c\u8499\u53e4\u7684\u5927\u519b\u8fd8\u9a7b\u5728\u9f99\u6c99\u5806\uff0c\u8fd9\u6837\u5feb\u5c31 \u5230\u4e86\u98de\u9f99\u5c71\u4e48\uff1f\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u8fd9\u4eba\u79bb\u5f00\u98de\u9f99\u5c71\u7684\u65f6\u5019\uff0c\u9791\u5b50\u5c1a\u672a\u6765\u5230\uff0c\u4e0d\u8fc7\uff0c\u6050\u6015\u4e5f\u5feb\u4e86\u3002\u4ed6\u4eec\u6253\u542c\u5230 \u4e86\u786e\u5b9e\u7684\u519b\u60c5\uff0c\u9791\u5b50\u7684\u524d\u950b\u5df2\u7ecf\u8fc7\u4e86\u5b9d\u9e21\uff0c\u6b63\u5728\u6cbf\u7740\u6c49\u6c34\u4e1c\u8fdb\u3002\u8499\u53e4\u7684\u9a91\u5175\u884c\u52a8\u8fc5\u6377\uff0c\u5929\u4e0b \u77e5\u540d\uff0c\u8bf4\u4e0d\u5b9a\u73b0\u5728\u5df2\u7ecf\u8e0f\u8fdb\u4e86\u98de\u9f99\u5c71\u4e86\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u98de\u9f99\u5c71\u662f\u6765\u8bf7\u6551\u5175\u7684\u4e86\uff1f\u4ed6\u4eec\u7684\u5be8\u4e3b\u662f\u8c01\uff0c\u4ed6\u6562\u4ee5\u4e00\u4e2a\u5c71\u5be8\u4e4b\u529b\u6765 \u6297\u5fa1\u9791\u5b50\uff0c\u4e5f\u7b97\u5f97\u662f\u4e2a\u8c6a\u6770\u4e86\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u98de\u9f99\u5c71\u7684\u5bd2\u4e3b\u540d\u5524\u7aa6\u5b89\u5e73\uff0c\u4ee5\u7ef5\u638c\u529f\u592b\u9a70\u8a89\u7eff\u6797\uff0c\u542c\u8bf4\u8fd8\u662f\u5c60\u767e\u57ce\u751f\u524d\uff0c \u6709\u4e00\u6b21\u548c\u4ed6\u6bd4\u8bd5\u638c\u529b\uff0c\u5c60\u767e\u57ce\u4e5f\u7528\u7ef5\u638c\u529f\u592b\uff0c\u51fb\u77f3\u5982\u7c89\uff0c\u8fd9\u624d\u5c06\u4ed6\u6536\u4f0f\u7684\u3002\u5f53\u65f6\u5c60\u767e\u57ce\u5df2\u662f \u505a\u4e86\u591a\u5e74\u7684\u7eff\u6797\u76df\u4e3b\u4e86\uff0c\u4ed6\u4ecd\u7136\u975e\u5f97\u548c\u76df\u4e3b\u6bd4\u8fc7\u638c\u529b\uff0c\u624d\u80af\u670d\u4ece\uff0c\u53ef\u89c1\u4ed6\u662f\u5982\u4f55\u5014\u5f3a\uff0c\u636e\u8bf4 \u4ed6\u5e73\u7d20\u7684\u884c\u5f84\uff0c\u4e43\u662f\u9ed1\u767d\u4e24\u9053\u90fd\u4e0d\u5356\u5e10\u7684\u3002\u56e0\u6b64\u867d\u7136\u4e0d\u662f\u80e1\u4f5c\u975e\u4e3a\uff0c\u5374\u4e5f\u4e0d\u662f\u4ec0\u4e48\u4fa0\u4e49\u4e4b \u58eb\u3002\u4e0d\u8fc7\uff0c\u4ed6\u8fd9\u6b21\u7518\u5192\u4ee5\u5375\u51fb\u77f3\u4e4b\u9669\uff0c\u8a93\u6297\u9791\u5b50\uff0c\u5012\u662f\u9887\u51fa\u4eba\u610f\u5916\uff0c\u4ee4\u4eba\u4e0d\u80fd\u4e0d\u4f69\u670d\u4ed6 \u4e86\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u662f\u5440\uff0c\u8bc4\u4ef7\u4eba\u7269\uff0c\u5e94\u8be5\u4ece\u5927\u5904\u7740\u773c\uff0c\u5c0f\u8282\u6709\u4e9b\u5730\u65b9\u9519\u4e86\uff0c\u90a3\u4e5f\u4e0d\u5fc5\u6df1\u7a76\u3002 \u4e0d\u8fc7\uff0c\u98de\u9f99\u5c71\u8ddd\u6b64\u5904\u751a\u8fdc\uff0c\u53ea\u6015\u54b1\u4eec\u6240\u53d1\u7684\u6551\u5175\u8d76\u4e0d\u53ca\u5427\uff1f\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u7aa6\u5be8\u4e3b\u62c5\u5fc3\u7684\u4e5f\u6b63\u662f\u6050\u6015\u5927\u961f\u4eba\u9a6c\u884c\u52a8\u96be\u514d\u6bd4\u8f83\u8fdf\u7f13\uff0c\u5728\u8def\u4e0a\u53c8\u5bb9\u6613\u51fa \u4e8b\uff0c\u56e0\u6b64\u4ed6\u4e00\u9762\u8bf7\u6c42\u54b1\u4eec\u53d1\u5175\uff0c\u4e00\u9762\u8bf7\u6c42\u54b1\u4eec\u5148\u6d3e\u51e0\u4e2a\u5f97\u529b\u7684\u9ad8\u624b\u7acb\u5373\u8d76\u53bb\u5e2e\u5fd9\u4ed6\u4eec\uff0c\u4eba\u9009 \u6700\u597d\u662f\u61c2\u5f97\u5175\u6cd5\u548c\u5728\u6b66\u6797\u4e2d\u6709\u5a01\u671b\u7684\u3002 \u3000\u3000\u201c\u56e0\u4e3a\u5018\u82e5\u6709\u8fd9\u6837\u7684\u51e0\u4e2a\u4eba\u5230\u4ed6\u4eec\u7684\u5c71\u5be8\uff0c\u4e0d\u4f46\u53ef\u4ee5\u5e2e\u5fd9\u4ed6\u4eec\u7b56\u5212\u519b\u4e8b\uff0c\u800c\u4e14\u53ef\u4ee5\u53f7\u53ec \u9644\u8fd1\u4e00\u4e9b\u96f6\u661f\u6563\u80a1\u7684\u4e49\u519b\uff0c\u8fd9\u4e9b\u4e49\u519b\u5927\u90fd\u662f\u7eff\u6797\u5144\u5f1f\uff0c\u5360\u636e\u4e86\u4e00\u4e9b\u8f83\u5c0f\u7684\u5c71\u5934\uff0c\u518d\u52a0\u5165\u4e00\u90e8 \u5206\u4e0d\u7518\u5f03\u5bb6\u9003\u96be\u7684\u8001\u767e\u59d3\u7ec4\u6210\u3002\u56e0\u6b64\u98de\u9f99\u5c71\u7684\u7aa6\u5be8\u4e3b\u5e0c\u671b\u6700\u597d\u662f\u5c60\u51e4\u4e5f\u80fd\u591f\u4eb2\u81ea\u51fa\u9a6c\uff0c\u5979\u662f \u524d\u4efb\u7eff\u6797\u76df\u4e3b\u7684\u5973\u513f\uff0c\u4ee5\u5979\u7684\u8eab\u4efd\u5f53\u4e0d\u96be\u53f7\u53ec\u96f6\u661f\u7684\u4e49\u519b\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u5c60\u51e4\u6050\u6015\u4e0d\u4fbf\u79bb\u5f00\u5c71\u5be8\u5427\uff1f\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u6b63\u662f\u5982\u6b64\u3002\u5c60\u51e4\u5012\u4e0d\u662f\u6015\u5be8\u4e2d\u65e0\u4eba\u4e3b\u6301\uff0c\u800c\u662f\u6015\u5979\u8d70\u4e86\u4e4b\u540e\uff0c\u5c60\u9f99\u8bf4\u4e0d\u5b9a \u5c31\u4f1a\u6363\u4e71\u3002\u800c\u4e14\u8fd8\u6709\u4e00\u5c42\uff0c\u5979\u8fd9\u4e00\u53bb\uff0c\u5c71\u5be8\u7684\u5f1f\u5144\u5f53\u7136\u4e5f\u8981\u62bd\u8c03\u81f3\u5c11\u4e00\u534a\u4ee5\u4e0a\u968f\u540e\u53bb\u7684\u3002\u5be8 \u4e2d\u529b\u91cf\u51cf\u5f31\u4e86\u4e00\u534a\uff0c\u6df3\u4e8e\u5468\u5018\u82e5\u4e58\u673a\u524d\u6765\u5e76\u541e\u8fd9\u4e2a\u5c71\u5be8\uff0c\u53c8\u6709\u5c60\u9f99\u4e0e\u4ed6\u91cc\u5e94\u5916\u5408\uff0c\u8fd9\u53ef\u4e5f\u662f \u76f8\u5f53\u5371\u9669\u7684\u554a\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u7b11\u9053\uff1a\u201c\u6211\u65e2\u7136\u4fa5\u5e78\u5f53\u4e0a\u4e86\u76df\u4e3b\uff0c\u8fd9\u4ef6\u4e8b\u6211\u81ea\u662f\u4e49\u4e0d\u5bb9\u8f9e\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u7b11\u9053\uff1a\u201c\u4e0d\u7792\u4f60\u8bf4\uff0c\u6211\u548c\u5c60\u51e4\u4e5f\u662f\u65e9\u5df2\u60f3\u5230\uff0c\u5018\u82e5\u4f60\u53bb\uff0c\u90a3\u662f\u6700\u597d\u4e0d\u8fc7\u3002\u4e0d\u8fc7\uff0c \u5c60\u51e4\u5374\u6015\u59d4\u5c48\u4e86\u4f60\u7684\u76df\u4e3b\u4e4b\u5c0a\uff0c\u4e0d\u4fbf\u5411\u4f60\u542f\u53e3\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u54ce\uff0c\u8fd9\u53ef\u662f\u4f60\u4eec\u7684\u4e0d\u662f\u4e86\u3002\u54b1\u4eec\u8bb8\u8eab\u4e3a\u56fd\uff0c\u54ea\u6709\u4ec0\u4e48\u59d4\u5c48\u4e0d\u59d4\u5c48\u7684\uff1f\u505a\u76df \u4e3b\u53c8\u4e0d\u662f\u5f53\u7687\u5e1d\uff0c\u6709\u4ec0\u4e48\u4e0d\u80fd\u52a8\u7684\uff1f\u7687\u5e1d\u4e5f\u6709\u5fa1\u9a7e\u4eb2\u5f81\u7684\u65f6\u5019\u554a\uff01\u597d\uff0c\u6211\u8fd9\u5c31\u5411\u5c60\u51e4\u8ba8\u4ee4 \u53bb\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u7b11\u9053\uff1a\u201c\u4f60\u662f\u76df\u4e3b\uff0c\u4f60\u559c\u6b22\u6d3e\u8c01\u53bb\u5c31\u662f\u8c01\u53bb\uff0c\u8bf4\u4ec0\u4e48\u8ba8\u4ee4\u5462\uff1f\u98de\u9f99\u5c71\u4e0d\u8fc7\u8fd8\u672a\u77e5 \u9053\u4f60\u662f\u76df\u4e3b\uff0c\u6240\u4ee5\u624d\u5411\u5c60\u51e4\u8bf7\u6c42\u7684\u3002\u8bf4\u8d77\u6765\uff0c\u6211\u5012\u662f\u8981\u5411\u4f60\u8ba8\u4ee4\u5462\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u8ba8\u4ec0\u4e48\u4ee4\uff1f\u201d\u5b5f\u660e\u971e\u9053\uff1a\u201c\u5230\u98de\u9f99\u5c71\u53bb\u5440\u3002\u201d\u674e\u601d\u5357\u6014\u4e86\u4e00\u6014\u9053\uff1a\u201c\u4f60\u4e5f \u8981\u53bb\uff1f\u201d\u5b5f\u660e\u971e\u7b11\u9053\uff1a\u201c\u600e\u4e48\uff0c\u4f60\u5acc\u6211\u505a\u4e0d\u4e86\u4f60\u7684\u52a9\u624b\u4e48\uff1f\u201d\u674e\u601d\u5357\u9053\uff1a\u201c\u4e0d\u662f\u8fd9\u4e2a\u610f\u601d\u3002 \u4f60\u522b\u8bef\u4f1a\uff0c\u4e0d\u8fc7\u2014\u2014\u201d \u3000\u3000\u5b5f\u660e\u971e\u6709\u70b9\u7740\u607c\uff0c\u9053\uff1a\u201c\u4e0d\u8fc7\u4ec0\u4e48\uff1f\u201d\u674e\u601d\u5357\u6c89\u541f\u534a\u664c\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u6211\u770b\u4f60\u8fd8\u662f\u7559 \u5728\u5c71\u4e0a\u534f\u52a9\u5c60\u51e4\u66f4\u597d\u3002\u6211\u60f3\u6211\u548c\u5b8b\u94c1\u8f6e\u592b\u5987\u5148\u884c\u8d76\u53bb\uff0c\u77f3\u749e\u7387\u9886\u63f4\u519b\u968f\u540e\u800c\u6765\uff0c\u6709\u8fd9\u4e48\u591a\u4eba \u53bb\uff0c\u4e5f\u5c31\u591f\u4e86\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u67f3\u7709\u5fae\u8e59\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4ed6\u597d\u50cf\u662f\u8981\u907f\u5f00\u6211\uff0c\u83ab\u975e\u4ed6\u662f\u6015\u4eba\u95f2\u8bdd\uff0c\u8981\u907f\u7537\u5973\u4e4b \u5acc\uff1f\u5176\u5b9e\u6c5f\u6e56\u513f\u5973\u6839\u672c\u5c31\u4e0d\u4f1a\u8bb2\u7a76\u8fd9\u5957\u3002\u4ed6\u6765\u7684\u65f6\u5019\u4ed6\u4e0d\u662f\u548c\u6211\u4e00\u8def\u540c\u884c\u7684\u4e48\uff0c\u600e\u7684\u73b0\u5728\u5ffd \u7136\u53c8\u8981\u907f\u8d77\u5acc\u7591\u6765\u4e86\uff1f\u201d\u60f3\u81f3\u6b64\u5904\uff0c\u5b5f\u660e\u971e\u5fc3\u91cc\u81ea\u662f\u4e0d\u514d\u6709\u70b9\u4e0d\u5927\u75db\u5feb\u3002\u4e0d\u8fc7\uff0c\u5979\u867d\u7136\u662f\u4e2a \u6027\u683c\u723d\u6717\u7684\u4e2d\u5e3c\u82f1\u96c4\uff0c\u5374\u4e5f\u4e0d\u597d\u610f\u601d\u5411\u674e\u601d\u5357\u76d8\u6839\u95ee\u5e95\uff0c\u5f53\u4e0b\u53ea\u597d\u8bf4\u9053\uff1a\u201c\u6211\u662f\u6015\u4f60\u6ca1\u6709\u5f97 \u529b\u7684\u5e2e\u624b\uff0c\u5b8b\u94c1\u8f6e\u592b\u5987\u867d\u7136\u4e0d\u5f31\uff0c\u4f46\u82e5\u78b0\u5230\u50cf\u6df3\u4e8e\u5468\u90a3\u6837\u7684\u5f3a\u654c\uff0c\u4ed6\u4eec\u6050\u6015\u5c31\u5e2e\u4e0d\u4e0a\u4f60\u7684\u5fd9 \u4e86\u3002\u8fd9\u6837\u5427\uff0c\u6211\u8bf7\u7239\u7239\u548c\u4f60\u4e00\u9053\u53bb\uff0c\u4f60\u8bf4\u597d\u4e48\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u5b5f\u8001\u524d\u8f88\u82e5\u53bb\uff0c\u81ea\u662f\u6700\u597d\u4e0d\u8fc7\u3002\u4e0d\u8fc7\uff0c\u7559\u5b88\u5c71\u5be8\u4e5f\u5f88\u91cd\u8981\uff0c\u6709\u4f60\u7239\u7239\u5728 \u6b64\uff0c\u6df3\u4e8e\u5468\u5c31\u4e0d\u6562\u5984\u52a8\u4e86\u3002\u201d\u53c8\u9053\uff1a\u201c\u53ef\u60dc\u891a\u4e91\u5cf0\u672a\u66fe\u627e\u7740\uff0c\u8981\u4e0d\u7136\u4ed6\u505a\u6211\u7684\u526f\u624b\uff0c\u5012\u662f\u6700 \u9002\u5408\u7684\u4eba\u9009\u3002\u54b1\u4eec\u8fd8\u662f\u627e\u5c60\u51e4\u5546\u91cf\u5546\u91cf\u5427\u3002\u201d\u5b5f\u660e\u971e\u9053\uff1a\u201c\u597d\u5427\uff0c\u90a3\u6211\u4eec\u73b0\u5728\u5c31\u56de\u53bb\u627e\u5c60 \u51e4\uff0c\u5979\u6b63\u5728\u548c\u77f3\u749e\u8bf4\u8bdd\uff0c\u6b64\u523b\u6216\u8bb8\u8fd8\u672a\u6563\u5462\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u731c\u5f97\u4e0d\u9519\uff0c\u674e\u601d\u5357\u4e0d\u613f\u548c\u5979\u540c\u53bb\uff0c\u7684\u786e\u662f\u4e3a\u4e86\u907f\u5acc\u3002\u4e0d\u8fc7\uff0c\u674e\u601d\u5357\u5e76\u4e0d\u662f\u6015\u522b\u4eba \u7684\u95f9\u8bdd\uff0c\u800c\u662f\u6015\u7ed9\u6768\u5a49\u8bef\u4f1a\u3002 \u3000\u3000\u4f46\u674e\u601d\u5357\u5374\u4e0d\u77e5\u9053\uff0c\u6768\u5a49\u6b64\u9645\uff0c\u6b63\u662f\u5728\u8fd9\u6885\u6797\u4e4b\u4e2d\u3002 \u3000\u3000\u539f\u6765\u6768\u5a49\u7684\u54e8\u5c97\uff0c\u5c31\u5728\u9644\u8fd1\u3002\u6b64\u65f6\u521a\u597d\u4e0d\u662f\u5979\u5f53\u503c\u7684\u65f6\u5019\u3002\u5979\u770b\u89c1\u674e\u601d\u5357\u4e0e\u77f3\u749e\u8fdb\u5165\u6885 \u6797\u4e4b\u65f6\uff0c\u60f3\u5077\u542c\u77f3\u749e\u548c\u4ed6\u8bf4\u4ec0\u4e48\uff0c\u4fbf\u4e5f\u6084\u6084\u5730\u8fdb\u5165\u6885\u6797\u3002\u4f46\u5979\u53ea\u542c\u5230\u4ed6\u4eec\u4e0b\u534a\u6bb5\u7684\u8bf4\u8bdd\uff0c\u5c60 \u51e4\u90a3\u4e2a\u4e2b\u9b1f\u4fbf\u6765\u628a\u77f3\u749e\u53eb\u53bb\u3002\u674e\u601d\u5357\u5411\u77f3\u749e\u5410\u9732\u5bf9\u5979\u7684\u76f8\u601d\u4e4b\u60c5\uff0c\u5979\u53ef\u6ca1\u6709\u542c\u89c1\u3002\u53ea\u77e5\u9053\u674e \u601d\u5357\u5df2\u7ecf\u6000\u7591\u5979\u662f\u5728\u8fd9\u5c71\u4e0a\uff0c\u800c\u77f3\u749e\u5219\u5b88\u7740\u4fe1\u7ea6\uff0c\u5e76\u6ca1\u6709\u6cc4\u9732\u5979\u7684\u884c\u85cf\u3002 \u3000\u3000\u77f3\u749e\u8d70\u540e\uff0c\u5979\u8e0c\u8e87\u518d\u4e09\uff0c\u5c1a\u672a\u62ff\u5b9a\u4e3b\u610f\u662f\u5426\u8981\u51fa\u6765\u4e0e\u674e\u601d\u5357\u76f8\u89c1\uff0c\u8ddf\u7740\u5b5f\u660e\u971e\u4e5f\u5c31\u6765 \u4e86\u3002 \u3000\u3000\u6768\u5a49\u4ece\u82b1\u6811\u4e1b\u4e2d\u8d70\u51fa\uff0c\u770b\u7740\u674e\u601d\u5357\u4e0e\u5b5f\u660e\u971e\u7684\u80cc\u5f71\u53cc\u53cc\u800c\u53bb\uff0c\u5fc3\u4e2d\u53c8\u662f\u4e0d\u7981\u4e00\u9635\u96be\u8fc7\u3002 \u3000\u3000\u6768\u5a49\u60f3\u9053\uff1a\u201c\u5b5f\u660e\u971e\u4e0e\u5357\u54e5\u4eca\u65e5\u6715\u624b\u6297\u5fa1\u5f3a\u654c\uff0c\u5982\u4eca\u53c8\u8981\u548c\u4ed6\u540c\u53bb\u98de\u9f99\u5c71\uff0c\u5979\u5bf9\u5357\u54e5\u503e \u5fc3\uff0c\u770b\u6765\u5df2\u662f\u6beb\u65e0\u7591\u95ee\u4e86\u3002\u542c\u5979\u7684\u53e3\u6c14\uff0c\u5b5f\u5927\u4fa0\u4e5f\u4f3c\u4e4e\u9887\u6709\u770b\u4e2d\u5357\u54e5\u505a\u5973\u5a7f\u4e4b\u610f\u3002\u53ef\u662f\u5357\u54e5 \u4e3a\u4ec0\u4e48\u4e0d\u5e94\u5141\u4e0e\u5979\u540c\u53bb\u5462\uff1f\u662f\u4e0d\u662f\u4e3a\u4e86\u6211\u7684\u7f18\u6545\u5462\uff1f\u201d \u3000\u3000\u60f3\u81f3\u6b64\u5904\uff0c\u6768\u5a49\u5fc3\u4e2d\u65b9\u59cb\u611f\u5230\u4e00\u4e1d\u751c\u610f\uff0c\u8138\u4e0a\u7efd\u51fa\u4e00\u4e1d\u7b11\u5bb9\u3002\u4f46\u8fd9\u4e1d\u7b11\u5bb9\u5374\u50cf\u5929\u8fb9\u7684\u660e \u6708\u4e00\u6837\uff0c\u5ffd\u5730\u88ab\u4e00\u7247\u4e4c\u4e91\u906e\u76d6\uff0c\u6768\u5a49\u63a5\u7740\u60f3\u9053\uff1a\u201c\u5373\u4f7f\u5357\u54e5\u5c1a\u672a\u5c06\u6211\u5fd8\u6000\uff0c\u4f46\u4ed6\u5fc3\u4e0a\u6bd5\u7adf\u4e5f \u662f\u6709\u4e86\u4e00\u4e2a\u5b5f\u59d1\u5a18\u4e86\uff0c\u5b5f\u59d1\u5a18\u548c\u4ed6\u6027\u60c5\u76f8\u6295\uff0c\u6bd4\u6211\u548c\u4ed6\u9002\u5408\u591a\u4e86\u3002\u6211\u662f\u4e2a\u601c\u4e01\u5b64\u82e6\u7684\u59d1\u5a18\uff0c \u53ea\u80fd\u62d6\u7d2f\u5357\u54e5\u3002\u5b5f\u59d1\u5a18\u7684\u7236\u4eb2\u5374\u662f\u5f53\u4e16\u5927\u4fa0\uff0c\u5357\u54e5\u5018\u82e5\u548c\u5979\u7ed3\u5408\uff0c\u90a3\u5c31\u5f53\u771f\u662f\u6709\u4e86\u6cf0\u5c71\u4e4b\u9760 \u4e86\u3002\u201d\u6768\u5a49\u81ea\u5df1\u89c9\u5f97\u672c\u8eab\u7684\u6761\u4ef6\u6837\u6837\u4e0d\u53ca\u4eba\u5bb6\uff0c\u4e0d\u7981\u4e3a\u4e4b\u9eef\u7136\u6c14\u9981\u3002 \u3000\u3000\u6768\u5a49\u53c8\u60f3\u8d77\u81ea\u5df1\u5c0f\u65f6\u5019\u7684\u4e00\u4ef6\u4e8b\u60c5\uff0c\u5979\u6709\u4e00\u4e2a\u5fc3\u7231\u7684\u7389\u676f\uff0c\u6709\u4e00\u5929\u5931\u624b\u6389\u5728\u5730\u4e0a\uff0c\u7f3a\u4e86 \u4e00\u89d2\uff0c\u4ece\u6b64\u5979\u5c31\u4e0d\u518d\u8981\u8fd9\u4e2a\u7389\u676f\u3002\u5979\u54e5\u54e5\u8bf4\u53ef\u4ee5\u8bf7\u5de7\u624b\u5320\u4eba\u9576\u8865\uff0c\u4e0d\u662f\u4ed4\u7ec6\u77a7\u7684\u8bdd\uff0c\u51b3\u8ba1\u770b \u4e0d\u51fa\u88c2\u75d5\u7684\u3002\u5979\u8bf4\u7eb5\u7136\u770b\u4e0d\u51fa\u4e5f\u603b\u662f\u6709\u4e86\u4e00\u9053\u88c2\u75d5\uff0c\u6709\u4e86\u88c2\u75d5\u7684\u4e1c\u897f\uff0c\u5373\u4f7f\u662f\u5947\u73cd\u5f02\u5b9d\uff0c\u5979 \u4e5f\u5b81\u53ef\u9001\u7ed9\u522b\u4eba\uff0c\u81ea\u5df1\u51b3\u4e0d\u4f1a\u518d\u8981\u3002 \u3000\u3000\u5979\u7684\u6027\u683c\u81ea\u5e7c\u5c31\u662f\u4e60\u60ef\u4e8e\u8ffd\u6c42\u201c\u5b8c\u7f8e\u201d\u7684\u4e1c\u897f\uff0c\u6b64\u9645\uff0c\u5979\u7684\u5fc3\u5883\u4e5f\u6b63\u662f\u8fd9\u6837\u3002 \u3000\u3000\u5979\u77e5\u9053\u53ea\u8981\u81ea\u5df1\u9732\u9762\uff0c\u674e\u601d\u5357\u4e00\u5b9a\u4e0d\u4f1a\u8d1f\u5979\uff0c\u5b5f\u660e\u971e\u518d\u597d\uff0c\u4e5f\u662f\u62a2\u4e0d\u8fc7\u5979\u7684\u3002\u53ef\u662f\u5979\u4e0d \u613f\u610f\u8fd9\u6837\u505a\uff0c\u56e0\u4e3a\u5979\u89c9\u5f97\u674e\u601d\u5357\u548c\u5979\u7684\u611f\u60c5\u5df2\u662f\u50cf\u90a3\u7389\u676f\u4e00\u6837\uff0c\u6709\u4e86\u88c2\u75d5\u3002 \u3000\u3000\u6768\u5a49\u7f13\u7f13\u8d70\u51fa\u6885\u6797\uff0c\u53ea\u89c1\u6708\u4eae\u5df2\u8fc7\u4e2d\u5929\uff0c\u4e0d\u7981\u53c8\u518d\u60f3\u9053\uff1a\u201c\u6b64\u9645\u5357\u54e5\u548c\u5c60\u51e4\u60f3\u5fc5\u5df2\u662f\u5546 \u91cf\u597d\u4e86\uff0c\u660e\u5929\u4ed6\u5c31\u8981\u5230\u98de\u9f99\u5c71\u53bb\u4e86\uff0c\u4e0d\u7ba1\u4ed6\u548c\u5b5f\u660e\u971e\u53bb\u4e5f\u597d\uff0c\u548c\u5b5f\u5927\u4fa0\u53bb\u4e5f\u597d\uff0c\u4ed6\u4eec\u7684\u5173\u7cfb \u603b\u662f\u53c8\u4eb2\u8fd1\u4e86\u4e00\u5c42\uff0c\u6211\u53c8\u4f55\u5fc5\u63d2\u8db3\u5728\u4ed6\u4eec\u4e4b\u95f4\u5462\uff1f\u201d \u3000\u3000\u4e0d\u8fc7\uff0c\u5979\u867d\u7136\u662f\u597d\u50cf\u4e0b\u4e86\u51b3\u5fc3\uff0c\u5fc3\u4e2d\u4ecd\u662f\u538b\u4e0d\u4e0b\u5bf9\u674e\u601d\u5357\u7684\u7275\u6302\uff0c\u65e0\u8bba\u5982\u4f55\uff0c\u4e5f\u4e0d\u80fd\u505c \u6b62\u81ea\u5df1\u53bb\u601d\u60f3\u6709\u5173\u674e\u601d\u5357\u7684\u4e8b\u60c5\u3002\u5979\u60f3\u8d77\u4e86\u674e\u601d\u5357\u660e\u65e5\u5c31\u8981\u53bb\u98de\u9f99\u5c71\uff0c\u4e00\u9635\u51b7\u98ce\u5439\u6765\uff0c\u5979\u84e6 \u5730\u731b\u7136\u4e00\u7701\uff0c\u4f3c\u4e4e\u89c9\u5f97\u6709\u4ec0\u4e48\u4e8b\u60c5\u4e0d\u5bf9\uff0c\u98de\u9f99\u5c71\u662f\u56e0\u4e3a\u53d1\u73b0\u654c\u8e2a\u5df2\u8fc7\u5b9d\u9e21\uff0c\u6765\u5411\u5c60\u51e4\u6c42\u63f4 \u7684\u3002\u5b5f\u660e\u971e\u628a\u8fd9\u4e2a\u6d88\u606f\u544a\u8bc9\u674e\u601d\u5357\u4e4b\u65f6\uff0c\u674e\u601d\u5357\u66fe\u7ecf\u6000\u7591\u8fc7\u654c\u9a91\u4f55\u4ee5\u6765\u5f97\u5982\u6b64\u4e4b\u5feb\uff0c\u4f46\u56e0\u8499 \u53e4\u9a91\u5175\u7684\u884c\u52a8\u8fc5\u901f\u5929\u4e0b\u7686\u77e5\uff0c\u674e\u601d\u5357\u867d\u7136\u7a0d\u68a2\u6000\u7591\uff0c\u968f\u5373\u4fe1\u4e3a\u4e8b\u5b9e\u3002 \u3000\u3000\u6768\u5a49\u9690\u9690\u89c9\u5f97\u4e0d\u5bf9\u5c31\u662f\u8fd9\u4ef6\u4e8b\u60c5\u3002\u5979\u970d\u7136\u4e00\u60ca\u4e4b\u540e\uff0c\u8fde\u5fd9\u7ea6\u675f\u81ea\u5df1\u7eb7\u4e71\u7684\u60c5\u7eea\uff0c\u8ddf\u7740\u8fd9 \u6761\u601d\u8def\u60f3\u4e0b\u53bb\uff0c\u8d8a\u60f3\u8d8a\u53d1\u73b0\u4e86\u5176\u4e2d\u5927\u6709\u7834\u7efd\u3002 \u3000\u3000\u6210\u5409\u601d\u6c57\u5728\u516d\u76d8\u5c71\u4e0b\u75c5\u901d\uff0c\u62d6\u96f7\u53d7\u547d\u2018\u76d1\u56fd\u2019\uff0c\u8499\u53e4\u5927\u519b\u5168\u90e8\u64a4\u56de\u548c\u6797\u3002\u6210\u5409\u601d\u6c57\u7684\u56db \u4e2a\u738b\u5b50\uff0c\u548c\u8499\u53e4\u7684\u91cd\u8981\u5c06\u9886\uff0c\u4ee5\u53ca\u5404\u76df\u7684\u738b\u516c\uff0c\u5c06\u8981\u5728\u548c\u6797\u4e3e\u884c\u201c\u5e93\u91cc\u5c14\u6cf0\u5927\u4f1a\u201d\uff0c\u63a8\u8d70\u7ee7 \u4efb\u7684\u5927\u6c57\u3002 \u3000\u3000\u8fd9\u4e9b\u4e8b\u60c5\uff0c\u674e\u601d\u5357\u8fd8\u672a\u77e5\u9053\uff0c\u6768\u5a49\u5f53\u65f6\u548c\u660e\u6167\u516c\u4e3b\u5728\u4e00\u8d77\uff0c\u5374\u662f\u77e5\u9053\u5f97\u5341\u5206\u6e05\u695a\u7684\u3002 \u3000\u3000\u6768\u5a49\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u7a9d\u9614\u53f0\u4e0e\u5bdf\u5408\u53f0\u90fd\u60f3\u7ee7\u627f\u5927\u6c57\u4e4b\u4f4d\uff0c\u62d6\u96f7\u4e0e\u9547\u56fd\u738b\u5b50\u5219\u5404\u52a9\u4e00\u65b9\uff0c\u4ed6 \u4eec\u5f7c\u6b64\u52fe\u5fc3\u6597\u89d2\uff0c\u5728\u5927\u6c57\u4e4b\u4f4d\u672a\u66fe\u786e\u5b9a\u4e4b\u524d\uff0c\u8499\u53e4\u662f\u51b3\u8ba1\u4e0d\u4f1a\u51fa\u5175\u7684\u3002\u5927\u6c57\u987b\u7531\u5e93\u91cc\u5c14\u6cf0\u5927 \u4f1a\u63a8\u4e3e\uff0c\u5e93\u91cc\u5c14\u6cf0\u5927\u4f1a\u53c8\u5fc5\u987b\u5404\u4f4d\u90e8\u843d\u7684\u738b\u516c\u90fd\u6765\u53c2\u52a0\uff0c\u660e\u6167\u516c\u4e3b\u4f30\u8ba1\uff0c\u8fd9\u4e2a\u5927\u4f1a\u5f00\u6210\u81f3\u5c11 \u4e5f\u5f97\u5728\u534a\u5e74\u4e4b\u540e\uff0c\u518d\u52a0\u4e0a\u5f80\u6765\u548c\u6797\u7684\u65f6\u95f4\uff0c\u8499\u53e4\u518d\u6b21\u53d1\u5175\u603b\u5f97\u5728\u516b\u4e5d\u4e2a\u6708\u4e4b\u540e\u4e86\u3002\u6211\u4e0e\u660e\u6167 \u516c\u4e3b\u5206\u624b\u81f3\u4eca\u4e0d\u8fc7\u4e09\u4e2a\u591a\u6708\uff0c\u4ed6\u4eec\u7684\u5e93\u91cc\u5c14\u6cf0\u5927\u4f1a\u6050\u6015\u624d\u53d1\u51fa\u901a\u77e5\uff0c\u8499\u5360\u7684\u9a91\u5175\u5982\u4f55\u80fd\u591f\u5728 \u6c49\u6c34\u4e4b\u4e1c\u51fa\u73b0\uff1f\u201d \u3000\u3000\u6768\u5a49\u6839\u636e\u6240\u77e5\u7684\u4e8b\u5b9e\u548c\u884c\u519b\u7684\u65f6\u95f4\u5224\u65ad\uff0c\u5b8c\u5168\u53ef\u4ee5\u65ad\u5b9a\u98de\u9f99\u5c71\u7684\u7aa6\u5be8\u4e3b\u5fc5\u662f\u8c0e\u62a5\u519b\u6e05\uff01 \u4ed6\u4e3a\u4ec0\u4e48\u8981\u8c0e\u62a5\u519b\u60c5\u5462\uff1f\u6211\u867d\u7136\u4e0d\u77e5\u5176\u6545\uff0c\u4f46\u60f3\u5fc5\u5176\u4e2d\u4e00\u5b9a\u662f\u6709\u4e2a\u5927\u9634\u8c0b\u4e86\uff01\u5357\u54e5\u5230\u98de\u9f99\u5c71 \u53bb\uff0c\u8fd9\u5c82\u4e0d\u662f\u81ea\u6295\u7f57\u7f51\uff1f\u201d \u3000\u3000\u6768\u5a49\u53d1\u73b0\u4e86\u8fd9\u4e2a\u5371\u9669\uff0c\u6697\u81ea\u601d\u5fd6\uff1a\u201c\u4fd7\u8bed\u8bf4\u660e\u67aa\u6613\u8eb2\uff0c\u6697\u7bad\u96be\u9632\uff0c\u6211\u65e2\u7136\u8bc6\u7834\u4e86\u98de\u9f99\u5c71 \u7684\u9634\u8c0b\uff0c\u5e94\u8be5\u963b\u6b62\u5357\u54e5\u524d\u5f80\uff0c\u4f46\u6211\u53c8\u4e0d\u60f3\u4e0e\u5357\u54e5\u89c1\u9762\uff0c\u600e\u4e48\u597d\u5462\uff1f\u55ef\uff0c\u6709\u4e86\uff0c\u6211\u7ed9\u4ed6\u6765\u4e2a\u7559 \u4e66\u793a\u8b66\uff0c\u4ed6\u672a\u66fe\u89c1\u8fc7\u6211\u7684\u5b57\u8ff9\uff0c\u6211\u628a\u5b57\u5199\u5f97\u6f66\u8349\u4e9b\uff0c\u8ba9\u4ed6\u770b\u4e0d\u51fa\u662f\u5973\u5b50\u7684\u4e66\u6cd5\uff0c\u4ed6\u5c31\u672a\u5fc5\u731c \u5f97\u7740\u662f\u6211\u6240\u4e3a\u4e86\u3002\u201d \u3000\u3000\u6768\u5a49\u5728\u54e8\u5c97\u4e4b\u6240\uff0c\u6709\u4e00\u95f4\u5355\u72ec\u7684\u623f\u95f4\uff0c\u4e8e\u662f\u6768\u5a49\u56de\u5230\u81ea\u5df1\u7684\u623f\u95f4\uff0c\u5306\u5306\u5730\u5199\u4e86\u4e00\u5c01\u4fe1\uff0c \u5c31\u6084\u6084\u7684\u6e9c\u5165\u5185\u5be8\u3002\u674e\u601d\u5357\u6240\u4f4f\u7684\u5730\u65b9\uff0c\u5979\u662f\u65e9\u5c31\u77e5\u9053\u4e86\u7684\u3002 \u3000\u3000\u6b64\u65f6\u5df2\u662f\u66f4\u8fc7\u540e\uff0c\u9664\u4e86\u804c\u53f8\u5b88\u591c\u7684\u903b\u5175\u4e4b\u5916\uff0c\u5927\u5bb6\u5728\u559d\u8fc7\u5e86\u529f\u9152\u4e4b\u540e\u90fd\u7761\u53bb\u4e86\u3002\u6768\u5a49\u795e \u4e0d\u77e5\u9b3c\u4e0d\u89c9\u5730\u6478\u5230\u4e86\u674e\u601d\u5357\u7684\u4f4f\u6240\uff0c\u6b63\u60f3\u8fdb\u53bb\uff0c\u5ffd\u89c1\u4e00\u6761\u4eba\u5f71\uff0c\u7a81\u7136\u5728\u5c4b\u9876\u51fa\u73b0\u3002 \u3000\u3000\u6768\u5a49\u5403\u4e86\u4e00\u60ca\uff0c\u8fde\u5fd9\u8eb2\u5728\u4e00\u5757\u5927\u77f3\u540e\u9762\u3002\u6708\u8272\u6726\u80e7\u4e4b\u4e0b\uff0c\u4f9d\u7a00\u8ba4\u5f97\u8fd9\u4e2a\u4eba\u5c31\u662f\u65e5\u95f4\u5927\u51fa \u98ce\u5934\u7684\u891a\u4e91\u5cf0\uff01 \u3000\u3000\u6768\u5a49\u597d\u751f\u5947\u602a\uff1a\u201c\u4ed6\u4e0d\u53c2\u52a0\u5e86\u529f\u5bb4\uff0c\u5374\u8dd1\u6765\u5357\u54e5\u7684\u623f\u95f4\u5e72\u4ec0\u4e48\uff1f\u201d\u5fc3\u5ff5\u672a\u5df2\uff0c\u84e6\u5730\u53c8\u89c1 \u4e00\u6761\u9ed1\u5f71\u4ece\u4e00\u68f5\u5927\u6811\u4e4b\u540e\u7a9c\u51fa\uff0c\u524d\u9762\u90a3\u6761\u9ed1\u5f71\u8ffd\u6765\uff0c\u8fd9\u4e2a\u4eba\u5374\u662f\u5c60\u9f99\u3002 \u3000\u3000\u6768\u5a49\u89c1\u4e86\u5c60\u9f99\uff0c\u4e0d\u7531\u5f97\u5fc3\u4e2d\u5927\u6012\uff0c\u60f3\u9053\uff1a\u201c\u539f\u6765\u4ed6\u679c\u7136\u662f\u5047\u88c5\u517b\u4f24\uff0c\u56fe\u8c0b\u4e0d\u8f68\uff01\u54fc\uff0c\u4e0d \u95ee\u53ef\u77e5\uff0c\u4ed6\u8dd1\u6765\u5357\u54e5\u7684\u623f\u95f4\uff0c\u4e00\u5b9a\u662f\u60f3\u8981\u6697\u7b97\u5357\u54e5\u7684\u4e86\uff01\u201d \u3000\u3000\u4e24\u4e2a\u5b88\u591c\u7684\u903b\u5175\u597d\u50cf\u542c\u5230\u4ec0\u4e48\u58f0\u606f\uff0c\u8fde\u5fd9\u8fc7\u6765\u5bdf\u770b\uff0c\u4e00\u4e2a\u8bf4\u9053\uff1a\u201c\u662f\u4e0d\u662f\u6709\u4eba\u7ecf\u8fc7\uff1f\u201d \u53e6\u5916\u4e00\u4e2a\u8bf4\u9053\uff1a\u201c\u6050\u6015\u662f\u98de\u9e1f\u7ecf\u8fc7\u5427\uff1f\u76df\u4e3b\u8fd8\u672a\u56de\u6765\uff0c\u54ea\u91cc\u4f1a\u6709\u4eba\u5462\uff1f\u201d\u5148\u5934\u90a3\u4e2a\u9053\uff1a\u201c\u54b1 \u4eec\u8fd8\u662f\u8fdb\u53bb\u770b\u770b\u5427\uff0c\u4e5f\u597d\u653e\u5fc3\u4e00\u4e9b\u3002\u201d \u3000\u3000\u6768\u5a49\u89c1\u8fd9\u4e24\u4e2a\u903b\u5175\u5df2\u7ecf\u8fdb\u5165\u674e\u601d\u5357\u7684\u623f\u95f4\uff0c\u5979\u5f53\u7136\u662f\u4e0d\u4fbf\u518d\u53bb\u6295\u4e66\u4e86\u3002\u5f53\u4e0b\uff0c\u5979\u4ece\u5927\u77f3 \u540e\u9762\u51fa\u6765\uff0c\u53ea\u89c1\u5c60\u9f99\u7684\u5f71\u5b50\u6b63\u5728\u94bb\u8fdb\u6885\u6797\u3002 \u3000\u3000\u6768\u5a49\u6697\u81ea\u60f3\u9053\uff1a\u201c\u4e14\u770b\u770b\u4ed6\u4eec\u6709\u4ec0\u4e48\u52fe\u642d\u3002\u201d\u5fc3\u5ff5\u4e00\u52a8\uff0c\u4fbf\u5373\u65bd\u5c55\u8f7b\u529f\uff0c\u8ddf\u4ed6\u524d\u5f80\uff0c\u4ece \u53e6\u4e00\u9762\u8fdb\u5165\u6885\u6797\u3002\u6768\u5a49\u5728\u6697\uff0c\u4ed6\u4eec\u5728\u660e\uff0c\u6768\u5a49\u6709\u5fc3\u8eb2\u5f00\u4ed6\u4eec\uff0c\u679c\u7136\u6ca1\u6709\u7ed9\u4ed6\u4eec\u53d1\u73b0\u3002 \u3000\u3000\u53ea\u542c\u5f97\u8f7b\u8f7b\u7684\u4e09\u4e0b\u638c\u58f0\uff0c\u8ddf\u7740\u53c8\u662f\u4e09\u4e0b\u638c\u51fb\uff0c\u5c60\u9f99\u8bf4\u9053\uff1a\u201c\u5e76\u541e\u516d\u5408\u201d\uff0c\u891a\u4e91\u5cf0\u8bf4\u9053\uff1a \u201c\u7eb5\u6a2a\u4e5d\u5dde\u201d\uff0c\u4e24\u4e2a\u4eba\u540c\u58f0\u54c8\u54c8\u53e3\u7b11\uff0c\u4e24\u6761\u9ed1\u5f71\u540c\u65f6\u5404\u81ea\u4ece\u4e00\u68f5\u6885\u6811\u540e\u9762\u51fa\u6765\u3002 \u3000\u3000\u5c60\u9f99\u9053\uff1a\u201c\u539f\u6765\u891a\u5144\u679c\u7136\u662f\u81ea\u5df1\u4eba\uff0c\u5e78\u4e8f\u5c0f\u5f1f\u6ca1\u6709\u9c81\u83bd\u3002\u201d\u891a\u4e91\u5cf0\u7b11\u9053\uff1a\u201c\u5c0f\u5f1f\u66f4\u60f3\u4e0d \u5230\u5c60\u5144\u4e5f\u662f\u8fd9\u6761\u7ebf\u4e0a\u7684\u670b\u53cb\uff01\u201d \u3000\u3000\u6768\u5a49\u8eb2\u5728\u6697\u5904\uff0c\u542c\u4e86\u6b64\u8a00\uff0c\u5fc3\u4e2d\u9707\u9a87\u4e4b\u6781\uff01\u5979\u505a\u68a6\u4e5f\u6599\u60f3\u4e0d\u5230\uff0c\u65e5\u95f4\u7acb\u4e86\u5927\u529f\u7684\u891a\u4e91 \u5cf0\uff0c\u7adf\u7136\u662f\u5c60\u9f99\u7684\u201c\u81ea\u5df1\u4eba\u201d\u3002 \u3000\u3000\u201c\u4ed6\u65e2\u7136\u662f\u548c\u5c60\u9f99\u4e00\u8def\u7684\uff0c\u65e5\u95f4\u4f55\u4ee5\u53c8\u5e2e\u52a9\u5357\u54e5\uff1f\u82e5\u8bf4\u4ed6\u662f\u4e3a\u4e86\u53d6\u5f97\u5357\u54e5\u7684\u4fe1\u4efb\uff0c\u4f55\u4ee5 \u540e\u6765\u53c8\u533f\u4e0d\u9732\u9762\uff0c\u8fde\u5e86\u529f\u5bb4\u4e5f\u4e0d\u53c2\u52a0\uff1f\u201d\u6768\u5a49\u6697\u81ea\u601d\u91cf\uff0c\u767e\u601d\u4e0d\u5f97\u5176\u89e3\u3002 \u3000\u3000\u5fc3\u5ff5\u672a\u5df2\uff0c\u53ea\u542c\u5f97\u5c60\u9f99\u5df2\u628a\u8fd9\u4e24\u4e2a\u95ee\u9898\u63d0\u4e86\u51fa\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u891a\u5144\u6df1\u85cf\u4e0d\u9732\uff0c\u5c0f\u5f1f\u4f69\u670d\u4e4b \u81f3\u3002\u4f46\u5c0f\u5f1f\u5374\u6709\u4e00\u4e8b\u4e0d\u660e\uff0c\u891a\u5144\u662f\u5426\u9884\u5148\u77e5\u9053\u5b5f\u5c11\u521a\u8fd9\u8001\u5339\u592b\u4eca\u65e5\u5b9a\u7136\u6765\u5230\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u7b11\u9053\uff1a\u201c\u5c0f\u5f1f\u5e76\u65e0\u795e\u7b97\u672c\u9886\uff0c\u7109\u80fd\u5148\u77e5\uff1f\u201d \u3000\u3000\u5c60\u9f99\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u90a3\u4e48\u6309\u7167\u5f53\u65f6\u7684\u5f62\u52bf\uff0c\u6df3\u4e8e\u8001\u524d\u8f88\u53d6\u5f97\u76df\u4e3b\u7684\u5e0c\u671b\u6781\u5927\uff0c\u891a\u5144\u4f55 \u4ee5\u53cd\u800c\u51fa\u5934\u4e0e\u6211\u4eec\u4f5c\u5bf9\uff1f\u51fa\u5934\u4e4b\u540e\uff0c\u53c8\u4f55\u4ee5\u907f\u5f00\uff0c\u4e0d\u4e0e\u674e\u601d\u5357\u8fd9\u5c0f\u5b50\u89c1\u9762\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u60f3\u5fc5\u4f60\u8fd8\u672a\u77e5\u9053\u6211\u7684\u6765\u5386\u5427\uff1f\u201d \u3000\u3000\u5c60\u9f99\u9053\uff1a\u201c\u6b63\u8981\u8bf7\u6559\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u7ea2\u82b1\u7eff\u53f6\uff0c\u867d\u7136\u662f\u540c\u51fa\u4e00\u6e90\uff0c\u4f46\u9053\u4e0a\u540c\u6e90\uff0c\u4e5f\u8fd8\u987b\u5404\u4e3a\u5176\u4e3b\u3002\u5c60\u5144\u53ef\u660e\u6b64 \u610f\u3002\u201d \u3000\u3000\u5c60\u9f99\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u9517\u5144\u7684\u80cc\u540e\u5f53\u5bb6\u548c\u6df3\u4e8e\u5be8\u4e3b\u4e00\u6837\uff0c\u90fd\u662f\u5e94\u4e86\u8499\u53e4\u5927\u6c57\u4e4b\u8058\u7684\u4e86\uff0c \u5374\u4e0d\u77e5\u8fd9\u4f4d\u5f53\u5bb6\u7684\u662f\u8c01\u3002\u201d \u3000\u3000\u6768\u5a49\u4ed4\u7ec6\u4e00\u60f3\uff0c\u65b9\u59cb\u660e\u767d\uff0c\u5fc3\u9053\uff1a\u201c\u539f\u6765\u891a\u4e91\u5cf0\u4e43\u662f\u6000\u6709\u79c1\u5fc3\uff0c\u4ed6\u4e0d\u613f\u610f\u6df3\u4e8e\u5468\u593a\u5f97\u76df \u4e3b\uff0c\u662f\u4e3a\u4e86\u4ed6\u7684\u5f53\u5bb6\u6253\u7b97\u3002\u4ed6\u4e0d\u80fd\u8ba9\u6df3\u4e8e\u5468\u5728\u8499\u53e4\u5927\u6c57\u8ddf\u524d\u7684\u5730\u4f4d\u9ad8\u8fc7\u4ed6\u7684\u5f53\u5bb6\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u4f60\u5f53\u771f\u60f3\u8981\u77e5\u9053\uff1f\u201d \u3000\u3000\u5c60\u9f99\u9053\uff1a\u201c\u65e2\u662f\u9053\u4e0a\u540c\u6e90\uff0c\u60f3\u6765\u8001\u5144\u8bf4\u4e5f\u65e0\u59a8\u3002\u201d\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u597d\uff0c\u4f60\u65e2\u7136\u60f3\u8981\u77e5\u9053\uff0c \u6211\u4e5f\u53ef\u4ee5\u8bf4\u4e0e\u4f60\u542c\u3002\u4f46\u53ea\u6015\u4f60\u77e5\u9053\u4e4b\u540e\uff0c\u53cd\u800c\u4e8e\u4f60\u4e0d\u4fbf\u3002\u201d \u3000\u3000\u5c60\u9f99\u5927\u4e3a\u60ca\u6115\uff0c\u8bb7\u8bb7\u8bf4\u9053\uff1a\u201c\u5018\u82e5\u4e0d\u4fbf\uff0c\u90a3\u3001\u90a3\u5c31\u4e0d\u8bf4\u4e5f\u7f62\u3002\u201d \u3000\u3000\u8bdd\u72b9\u672a\u4e86\uff0c\u53ea\u89c1\u891a\u4e91\u5cf0\u5355\u638c\u5212\u4e86\u4e00\u9053\u5706\u5f27\uff0c\u8f7b\u8f7b\u5730\u62cd\u5728\u4e00\u68f5\u6885\u6811\u4e4b\u4e0a\u3002\u8f6c\u773c\u4e4b\u95f4\uff0c\u6885\u82b1 \u7eb7\u843d\u5982\u96e8\uff0c\u53ea\u5269\u4e0b\u5149\u79c3\u79c3\u7684\u67af\u679d\u3002 \u3000\u3000\u6768\u5a49\u66fe\u89c1\u8fc7\u4ed6\u65e5\u95f4\u4ee5\u638c\u529b\u6298\u670d\u5d14\u9547\u5c71\uff0c\u6b64\u65f6\u867d\u7136\u89c9\u5f97\u4ed6\u8fd9\u4e00\u638c\u4e4b\u529b\uff0c\u9690\u9690\u631f\u7740\u98ce\u96f7\u4e4b \u58f0\uff0c\u6bd4\u65e5\u95f4\u6240\u89c1\uff0c\u66f4\u5176\u5f3a\u52b2\uff0c\u4f46\u8fd9\u53ea\u662f\u7a0d\u5f3a\u7a0d\u5f31\u4e4b\u5206\u800c\u5df2\uff0c\u56e0\u6b64\u4e5f\u5e76\u4e0d\u600e\u4e48\u8be7\u5f02\u3002 \u3000\u3000\u4f46\u5c60\u9f99\u5c31\u4e0d\u540c\u4e86\u3002\u5c3d\u7ba1\u65e5\u95f4\u891a\u4e91\u5cf0\u4ee5\u638c\u529b\u6298\u670d\u5d14\u9547\u5c71\u4e4b\u65f6\uff0c\u4ed6\u4e5f\u5728\u573a\uff0c\u6b64\u65f6\u5374\u4ecd\u662f\u4e0d\u7981 \u5927\u60ca\u5931\u8272\uff0c\u5446\u4e86\u534a\u664c\uff0c\u8fd9\u624d\u8bf4\u5f97\u51fa\u8bdd\u6765\uff1a\u201c\u4f60\u3001\u4f60\u53ef\u662f\u9633\u5929\u96f7\u7684\u5f1f\u5b50\uff1f\u201d\u58f0\u97f3\u4e5f\u90fd\u7981\u4e0d\u4f4f\u98a4 \u6296\u4e86\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u662f\u53c8\u600e\u6837\uff0c\u4e0d\u662f\u53c8\u600e\u6837\uff1f\u563f\uff0c\u563f\uff0c\u4f60\u7684\u773c\u529b\u5012\u662f\u5f88\u4e0d\u9519\u5440\uff0c\u770b\u5f97\u51fa \u6211\u8fd9\u4e00\u638c\u7528\u7684\u4e43\u662f\u5929\u96f7\u795e\u529f\u3002\u201d\u867d\u7136\u5c1a\u672a\u627f\u8ba4\u81ea\u5df1\u5c31\u662f\u9633\u5929\u96f7\u7684\u5f1f\u5b50\uff0c\u53e3\u98ce\u4e4b\u4e2d\u5df2\u662f\u900f\u51fa\u81f3 \u5c11\u4e5f\u662f\u4e0e\u9633\u5929\u96f7\u5927\u6709\u6e0a\u6e90\u3002 \u3000\u3000\u6b64\u65f6\u6768\u5a49\u65b9\u59cb\u5927\u5927\u5403\u60ca\u3002\u539f\u6765\u9633\u5929\u96f7\u4e0d\u662f\u522b\u4eba\uff0c\u6b63\u662f\u6740\u5bb3\u5c60\u767e\u57ce\u7684\u51f6\u624b\u3002\u5f53\u65e5\u5c60\u51e4\u548c\u5979 \u7684\u5927\u5e08\u5144\u9f99\u521a\u7b49\u4eba\u5f80\u8499\u53e4\u8bbf\u67e5\uff0c\u5df2\u5f97\u786e\u8bc1\u3002\u8fd9\u4e8b\u6768\u5a49\u662f\u77e5\u9053\u7684\u3002 \u3000\u3000\u6768\u5a49\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4e14\u770b\u5c60\u9f99\u600e\u6837\u5bf9\u4ed8\u4ed6\u7684\u6740\u7236\u4ec7\u4eba\u7684\u5f1f\u5b50\uff1f\u201d\u5fc3\u5ff5\u672a\u5df2\uff0c\u53ea\u542c\u5f97\u5c60\u9f99\u53f9 \u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\uff0c\u602a\u4e0d\u5f97\u4f60\u8bf4\u4e0d\u65b9\u4fbf\u8ba9\u6211\u77e5\u9053\u3002\u201d\u891a\u4e91\u5cf0\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u73b0\u5728\u77e5 \u9053\u4e5f\u8fd8\u4e0d\u8fdf\u3002\u4f60\u8981\u4e0e\u6211\u7ffb\u8138\u4e5f\u597d\uff0c\u4e0e\u6211\u643a\u624b\u4e5f\u597d\uff0c\u90fd\u7531\u5f97\u4f60\uff01\u201d \u3000\u3000\u5c60\u9f99\u53c8\u5446\u4e86\u534a\u664c\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u7ba1\u4f60\u662f\u5426\u9633\u5929\u96f7\u7684\u5f1f\u5b50\uff0c\u4f46\u6211\u7684\u6740\u7236\u4ec7\u4eba\u53ea\u662f\u9633\u5929\u96f7\u800c \u4e0d\u662f\u4f60\u3002\u4eca\u65e5\u54b1\u4eec\u5229\u5bb3\u76f8\u540c\uff0c\u81ea\u5f53\u5f7c\u6b64\u643a\u624b\u3002\u4f46\u6211\u7684\u6740\u7236\u4e4b\u4ec7\u8fd8\u662f\u8981\u62a5\u7684\uff0c\u4f60\u82e5\u662f\u7ef4\u62a4\u5e08 \u95e8\uff0c\u54b1\u4eec\u5c31\u53ea\u6709\u62fc\u4e2a\u6b7b\u6d3b\u4e86\u3002\u4f46\u6211\u51b3\u4e0d\u5148\u5411\u4f60\u52a8\u624b\u3002\u201d\u540e\u9762\u8fd9\u4e00\u6bb5\u8bdd\uff0c\u5206\u660e\u53ea\u662f\u60f3\u987e\u4f4f\u4f53\u9762 \u7684\u8bf4\u8bdd\u4e86\u3002 \u3000\u3000\u6768\u5a49\u542c\u4e86\u8fd9\u8bdd\uff0c\u5fc3\u91cc\u6697\u81ea\u6012\u9a82\uff1a\u201c\u5c60\u9f99\u8fd9\u53ae\u7b80\u76f4\u662f\u6ca1\u6709\u534a\u70b9\u8840\u6027\u3002\u771f\u60f3\u4e0d\u5230\u5c60\u767e\u57ce\u82f1\u96c4 \u76d6\u4e16\uff0c\u7adf\u7136\u4f1a\u6709\u8fd9\u6837\u4e00\u4e2a\u513f\u5b50\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u54c8\u54c8\u7b11\u903c\uff1a\u201c\u8bc6\u65f6\u52a1\u8005\u4e3a\u4fca\u6770\uff0c\u5c60\u5144\u6709\u6b64\u51b3\u65ad\uff0c\u5c0f\u5f1f\u5341\u5206\u4f69\u670d\u3002\u597d\uff0c\u4f60\u627e\u4e0d\u627e\u9633 \u5929\u96f7\u62a5\u4ec7\uff0c\u90a3\u662f\u4f60\u7684\u4e8b\u3002\u5728\u8fd9\u5c71\u5be8\u4e4b\u4e2d\uff0c\u6211\u4e00\u5b9a\u5e2e\u4f60\u7684\u5fd9\u5c31\u662f\u3002\u201d \u3000\u3000\u6768\u5a49\u5fc3\u601d\u6781\u7ec6\uff0c\u542c\u89c1\u891a\u4e91\u5cf0\u76f4\u547c\u9633\u5929\u96f7\u4e4b\u540d\uff0c\u4e0d\u89c9\u6709\u70b9\u8be7\u5f02\uff0c\u5fc3\u60f3\uff1a\u201c\u891a\u4e91\u5cf0\u82e5\u662f\u9633\u5929 \u96f7\u7684\u5f1f\u5b50\uff0c\u4f3c\u4e4e\u4e0d\u8be5\u76f4\u547c\u5e08\u7236\u4e4b\u540d\u3002\u201d \u3000\u3000\u5c60\u9f99\u95ee\u9053\uff1a\u201c\u891a\u5144\uff0c\u4f60\u5728\u4eca\u5929\u51fa\u98ce\u5934\u4e4b\u540e\uff0c\u5ffd\u7136\u533f\u4e0d\u9732\u9762\uff0c\u662f\u4e0d\u662f\u6015\u6211\u59b9\u59b9\u8bc6\u7834\u4f60\u7684\u6765 \u5386\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u8fd9\u5012\u4e0d\u662f\u3002\u4f60\u59b9\u59b9\u867d\u7136\u7cbe\u660e\uff0c\u89c1\u8bc6\u5c1a\u4e0d\u5982\u4f60\uff0c\u6211\u53c8\u672a\u66fe\u9732\u51fa\u5929\u96f7\u795e\u529f\uff0c\u5979 \u7109\u80fd\u77e5\u9053\u6211\u7684\u6765\u5386\u3002\u201d \u3000\u3000\u5c60\u9f99\u9053\uff1a\u201c\u7136\u5219\u53c8\u662f\u4e3a\u4f55\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u77e5\u9053\u98de\u9f99\u5c71\u6709\u4e2a\u4eba\u4eca\u65e5\u4f1a\u6765\uff0c\u4ed6\u662f\u77e5\u9053\u6211\u7684\u6765\u5386\u3002\u201d \u3000\u3000\u5c60\u9f99\u7b11\u9053\uff1a\u201c\u891a\u5144\uff0c\u8fd9\u4f60\u5c31\u662f\u53ea\u77e5\u5176\u4e00\uff0c\u4e0d\u77e5\u5176\u4e8c\u4e86\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u54e6\uff0c\u90a3\u6211\u5012\u8981\u5411\u5c60\u5144\u8bf7\u6559\u4e86\uff0c\u6211\u6240\u4e0d\u77e5\u9053\u7684\u8fd9\u2018\u5176\u4e8c\u2019\u53c8\u662f\u4ec0 \u4e48\uff1f\u201d \u3000\u3000\u5c60\u9f99\u7b11\u9053\uff1a\u201c\u98de\u9f99\u5c71\u4e5f\u662f\u54b1\u4eec\u7684\u4eba\uff0c\u7aa6\u5be8\u4e3b\u9063\u4eba\u8c0e\u62a5\u519b\u60c5\uff0c\u8fd9\u662f\u65e9\u5c31\u548c\u6211\u5546\u8bae\u597d\u7684\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u4f55\u4ee5\u8981\u8c0e\u62a5\u519b\u60c5\uff1f\u201d \u3000\u3000\u5c60\u9f99\u9053\uff1a\u201c\u8fd9\u662f\u8c03\u864e\u79bb\u5c71\u4e4b\u8ba1\u5440\uff0c\u891a\u5144\uff0c\u4f60\u8fd9\u6837\u806a\u660e\u7684\u4eba\uff0c\u96be\u9053\u8fd8\u4f1a\u4e0d\u77e5\u9053\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u662f\u4f60\u548c\u7aa6\u5b89\u5e73\u5408\u8c0b\u8bbe\u4e0b\u7684\u9677\u9644\uff0c\u8bf1\u4f60\u59b9\u59b9\u4e0a\u5f53\u7684\u3002\u201d \u3000\u3000\u5c60\u9f99\u9053\uff1a\u201c\u662f\u5440\uff0c\u5c60\u51e4\u8fd9\u4e2b\u5934\u5e26\u9886\u5979\u7684\u4eba\u9a6c\u4e00\u79bb\u5f00\u5c71\u5be8\uff0c\u6211\u5728\u8fd9\u91cc\u5c31\u53ef\u4ee5\u4e3a\u6240\u6b32\u4e3a \u4e86\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u5012\u4e0b\u5f97\u8fa3\u624b\uff0c\u4e0d\u60dc\u4e0e\u5916\u4eba\u4e32\u901a\u8c0b\u5bb3\u4eb2\u59b9\u5b50\u3002\u4f69\u670d\uff0c\u4f69\u670d\uff01\u201d \u3000\u3000\u5c60\u9f99\u7b11\u9053\uff1a\u201c\u4fd7\u8bed\u8bf4\u5f97\u597d\uff0c\u65e0\u6bd2\u4e0d\u4e08\u592b\uff0c\u8c01\u53eb\u5979\u62a2\u4e86\u6211\u7684\u5be8\u4e3b\u4e4b\u4f4d\uff0c\u8fd8\u4e0d\u628a\u6211\u5f53\u4f5c\u54e5\u54e5 \u770b\u5f85\u3002\u4e0d\u8fc7\uff0c\u6211\u4e5f\u4e0d\u662f\u8c0b\u5bb3\u5979\uff0c\u6211\u5012\u662f\u597d\u5fc3\u597d\u610f\u8981\u66ff\u5979\u627e\u4e2a\u597d\u4e08\u592b\u7684\u3002\u4e0d\u7792\u4f60\u8bf4\uff0c\u6df3\u4e8e\u5468\u7684 \u513f\u5b50\u6df3\u4e8e\u8191\u5c31\u5728\u98de\u9f99\u5c71\u90a3\u8fb9\u7b49\u7740\u5979\u3002\u201d \u3000\u3000\u6768\u5a49\u542c\u5230\u8fd9\u91cc\uff0c\u53c8\u60ca\u53c8\u559c\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u679c\u7136\u4e0d\u51fa\u6211\u4e4b\u6240\u6599\uff0c\u98de\u9f99\u5c71\u662f\u4ed6\u4eec\u7684\u4eba\uff0c\u5c60\u9f99 \u8fd9\u53ae\u8bbe\u8ba1\u628a\u59b9\u59b9\u5f80\u706b\u5751\u91cc\u63a8\uff0c\u771f\u662f\u8be5\u6b7b\uff01\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u4f46\u636e\u6211\u6240\u77e5\uff0c\u4f60\u7684\u59b9\u59b9\u5927\u7ea6\u662f\u4e0d\u4f1a\u5230\u98de\u9f99\u5c71\u53bb\u4e86\uff0c\u516b\u6210\u662f\u674e\u601d\u5357\u8fd9\u5c0f\u5b50\u81ea \u5df1\u53bb\u3002\u201d \u3000\u3000\u5c60\u9f99\u9053\uff1a\u201c\u90a3\u5c31\u66f4\u597d\u4e86\u3002\u642c\u8d70\u4e86\u8fd9\u5757\u5927\u77f3\u5934\uff0c\u54b1\u4eec\u5728\u8fd9\u91cc\u5c31\u66f4\u65b9\u4fbf\u884c\u4e8b\u4e86\u3002\u891a\u5144\uff0c\u6211\u51c6 \u5907\u674e\u601d\u5357\u4e00\u8d70\u5c31\u53d1\u96be\uff0c\u4f60\u4e00\u5b9a\u5f97\u5e2e\u6211\u8fd9\u4e2a\u5927\u5fd9\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u4f60\u4e0d\u6015\u5b5f\u5c11\u521a\uff1f\u201d \u3000\u3000\u5c60\u9f99\u9053\uff1a\u201c\u5b5f\u5c11\u521a\u6709\u610f\u628a\u5973\u513f\u8bb8\u914d\u7ed9\u674e\u601d\u5357\uff0c\u96be\u9053\u4f60\u8fd8\u770b\u4e0d\u51fa\u6765\uff1f\u674e\u601d\u5357\u82e5\u662f\u524d\u5f80\u98de\u9f99 \u5c71\uff0c\u5b5f\u5c11\u521a\u7236\u5973\u4e00\u5b9a\u4f1a\u8ddf\u7740\u4ed6\u53bb\u3002\u5230\u65f6\u53ea\u8981\u4f60\u5e2e\u5fd9\u6211\u5236\u670d\u5c60\u51e4\u548c\u77f3\u749e\u4e8c\u4eba\uff0c\u6211\u5c31\u53ef\u4ee5\u593a\u56de\u8fd9 \u5ea7\u5c71\u5be8\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u8fd9\u4e2a\u5fd9\u6211\u5f53\u7136\u662f\u4e00\u5b9a\u8981\u5e2e\u4f60\u7684\u3002\u201d \u3000\u3000\u6768\u5a49\u60f3\u8981\u77e5\u9053\u7684\u79d8\u5bc6\u73b0\u5df2\u77e5\u9053\uff0c\u5fc3\u60f3\uff1a\u201c\u4e0d\u77e5\u4ed6\u4eec\u8fd8\u6709\u4ec0\u4e48\u79d8\u5bc6\u8981\u8bf4\uff0c\u4f46\u6211\u77e5\u9053\u4e86\u8fd9\u4e48 \u591a\u4e5f\u5df2\u591f\u4e86\u3002\u4e0d\u5982\u56de\u53bb\u8bbe\u6cd5\u544a\u8bc9\u77f3\u749e\uff0c\u4e5f\u514d\u5f97\u7ed9\u4ed6\u4eec\u53d1\u73b0\u3002\u201d \u3000\u3000\u6768\u5a49\u5374\u4e0d\u77e5\u9053\uff0c\u5c31\u5728\u5979\u52a8\u8fd9\u5ff5\u5934\u4e4b\u9645\uff0c\u5c60\u9f99\u5df2\u7ecf\u53d1\u73b0\u4e86\u5979\u3002\u539f\u6765\u6768\u5a49\u542c\u5230\u60ca\u5fc3\u52a8\u9b44\u4e4b \u5904\uff0c\u4e0d\u77e5\u4e0d\u89c9\u5fd8\u8bb0\u4e86\u8981\u653e\u8f7b\u547c\u5438\uff0c\u7ed9\u5c60\u9f99\u542c\u51fa\u4e86\u5979\u7684\u58f0\u606f\u3002 \u3000\u3000\u5c60\u9f99\u542c\u51fa\u4e86\u9644\u8fd1\u6709\u4eba\uff0c\u795e\u8272\u4ecd\u662f\u4e1d\u6beb\u4e0d\u9732\uff0c\u7a81\u7136\u95f4\u5c31\u671d\u7740\u6768\u5a49\u8eb2\u4e4b\u5904\u6251\u53bb\uff0c\u6768\u5a49\u5374\u8fd8\u5728 \u8e0c\u8e87\u672a\u51b3\u662f\u5426\u8981\u518d\u542c\u4e0b\u53bb\u3002\u6b63\u662f\uff1a \u3000\u3000\u79d8\u5bc6\u5c82\u80fd\u4f20\u516d\u8033\uff0c\u5978\u8c0b\u4e00\u7834\u6740\u673a\u840c\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u516d\u56de\u3000\u9c7c\u8dc3\u9e22\u98de\u5bfb\u6545\u4fa3\u3000\u9f99\u6f5c\u8c79\u9690\u6709\u7384\u673a \u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b \u7b2c\u4e8c\u5341\u516d\u56de\u3000\u9c7c\u8dc3\u9e22\u98de\u5bfb\u6545\u4fa3\u3000\u9f99\u6f5c\u8c79\u9690\u6709\u7384\u673a \u3000\u3000\u5c31\u5728\u5c60\u9f99\u5411\u6768\u5a49\u5077\u88ad\u7684\u65f6\u5019\uff0c\u891a\u4e91\u5cf0\u4e5f\u5ffd\u5730\u559d\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\u8eb2\u5728\u8fd9\u91cc\uff0c\u8fd8\u4e0d\u7ed9\u6211\u6eda\u51fa \u6765\uff01\u201d \u3000\u3000\u5e78\u4e8f\u6709\u891a\u4e91\u5cf0\u8fd9\u4e48\u4e00\u559d\uff0c\u6768\u5a49\u8fd9\u624d\u53d1\u89c9\u5c60\u9f99\u6251\u6765\uff0c\u8fde\u5fd9\u4f7f\u4e2a\u201c\u98ce\u522e\u843d\u82b1\u201d\u7684\u8eab\u6cd5\uff0c\u6070\u597d \u53ca\u65f6\u7684\u907f\u5f00\u4e86\u5c60\u9f99\u7684\u4e00\u62db\u6740\u624b\u3002 \u3000\u3000\u5c60\u9f99\u5fc3\u91cc\u597d\u751f\u7740\u607c\uff0c\u60f3\u9053\uff1a\u201c\u891a\u4e91\u5cf0\u6b66\u529f\u5f88\u662f\u4e0d\u9519\uff0c\u600e\u7684\u4eba\u5374\u8fd9\u6837\u7cca\u6d82\uff0c\u8fd9\u4e0d\u7b49\u4e8e\u662f\u63d0 \u9192\u4e86\u4ed6\u5417\uff1f\u201d\u4ed6\u8981\u501a\u4ed7\u891a\u4e91\u5cf0\uff0c\u4e0d\u4fbf\u53e3\u51fa\u6028\u8a00\uff0c\u53ea\u597d\u8bf4\u9053\uff1a\u201c\u891a\u5144\uff0c\u5feb\u6765\u7ed3\u679c\u8fd9\u53ae\uff0c\u4e0d\u8981\u7ba1 \u4ed6\u662f\u8c01\u4e86\uff01\u201d \u3000\u3000\u539f\u6765\u6768\u5a49\u5728\u8ddf\u77f3\u749e\u4e0a\u5c71\u4e4b\u65f6\uff0c\u65e9\u5df2\u6362\u4e86\u7537\u88c5\uff0c\u626e\u4f5c\u4e00\u4e2a\u666e\u901a\u7684\u55bd\u5175\uff0c\u53c8\u7528\u963f\u76d6\u7ed9\u5979\u914d\u5236 \u7684\u836f\u7c89\uff0c\u6d82\u5728\u8138\u4e0a\uff0c\u53d8\u5316\u4e86\u9762\u8c8c\uff0c\u56e0\u6b64\u5728\u9eef\u6de1\u7684\u6708\u5149\u4e4b\u4e0b\uff0c\u5c60\u9f99\u4e00\u70b9\u4e5f\u770b\u4e0d\u51fa\u8fd9\u4e2a\u76f8\u8c8c\u5e73\u51e1 \u7684\u55bd\u5175\u5c31\u662f\u4ed6\u8981\u627e\u5bfb\u7684\u6768\u5a49\u3002 \u3000\u3000\u6768\u5a49\u6068\u4e0d\u5f97\u4e00\u5251\u6740\u4e86\u5c65\u9f99\uff0c\u5018\u82e5\u53ea\u6709\u5c60\u9f99\u4e00\u4e2a\u4eba\uff0c\u5979\u975e\u548c\u5c60\u9f99\u62fc\u547d\u4e0d\u53ef\u3002\u4f46\u73b0\u5728\u5979\u5374\u4e0d \u80fd\u4e0d\u987e\u5fcc\u4e0e\u5c60\u9f99\u5728\u4e00\u8d77\u7684\u891a\u4e91\u5cf0\u3002\u891a\u4e91\u5cf0\u7684\u672c\u9886\u8fdc\u8fdc\u5728\u5979\u4e4b\u4e0a\uff0c\u53ea\u8981\u4ed6\u4e00\u51fa\u624b\uff0c\u5979\u6839\u672c\u5c31\u6ca1 \u6709\u548c\u5c60\u9f99\u62fc\u547d\u7684\u673a\u4f1a\u3002\u6768\u5a49\u5fc3\u60f3\uff1a\u201c\u5c0f\u4e0d\u5fcd\u5219\u4e71\u5927\u8c0b\uff0c\u8fd8\u662f\u8d76\u5feb\u9003\u51fa\u53bb\uff0c\u63ed\u7834\u4ed6\u7684\u5978\u8c0b\u8981 \u7d27\uff01\u201d \u3000\u3000\u5c60\u9f99\u5c82\u80fd\u5bb9\u5979\u9003\u8d70\uff0c\u4e00\u638c\u6253\u7a7a\uff0c\u540e\u62db\u7acb\u53d1\u3002\u672c\u6765\u6768\u5a49\u7684\u8f7b\u529f\u662f\u8f83\u4ed6\u9ad8\u660e\u7684\uff0c\u4f46\u56e0\u7ed9\u4ed6\u5236 \u4e86\u5148\u673a\uff0c\u6025\u5207\u4e4b\u95f4\uff0c\u7adf\u662f\u5316\u89e3\u4e0d\u4e86\u4ed6\u7684\u653b\u52bf\uff0c\u8fde\u62d4\u5251\u62b5\u6321\u4e5f\u6ca1\u7a7a\u6687\u3002 \u3000\u3000\u6768\u5a49\u7684\u5185\u529b\u5f53\u7136\u4e0d\u53ca\u5c60\u9f99\uff0c\u6b64\u65f6\u65e0\u6687\u62d4\u5251\uff0c\u53ea\u80fd\u65bd\u5c55\u817e\u3001\u632a\u3001\u95ea\u3001\u5c55\u7684\u5c0f\u5de7\u529f\u592b\u4e1c\u8eb2\u897f \u95ea\uff0c\u95f4\u4e2d\u7528\u7cbe\u5999\u7684\u70b9\u7a74\u624b\u6cd5\u8fd8\u51fb\uff0c\u4f46\u5728\u5c60\u9f99\u638c\u529b\u7b3c\u7f69\u4e4b\u4e0b\uff0c\u7ec8\u662f\u5927\u5927\u5403\u4e8f\u3002\u8fd8\u5e78\u5c60\u9f99\u65e5\u95f4\u4e2d \u4e86\u4e00\u652f\u6bd2\u9f99\u9556\uff0c\u867d\u7136\u7acb\u5373\u670d\u4e86\u89e3\u836f\uff0c\u6b64\u9645\u5df2\u662f\u5e76\u65e0\u59a8\u788d\uff0c\u4f46\u529f\u529b\u4e5f\u4e0d\u514d\u6253\u4e86\u4e24\u5206\u6298\u6263\uff0c\u6768\u5a49 \u8fd9\u624d\u80fd\u591f\u62b5\u6321\u4e86\u5341\u6765\u62db\u3002 \u3000\u3000\u5c60\u9f99\u662f\u4ee5\u95ea\u7535\u822c\u7684\u5c0f\u64d2\u62ff\u624b\u6cd5\u5411\u6768\u5a49\u6251\u51fb\u7684\uff0c\u5341\u6765\u62db\u4e0d\u8fc7\u7247\u5230\u65f6\u95f4\uff0c\u4f46\u5728\u8fd9\u7247\u5230\u4e4b\u95f4\uff0c \u5c60\u9f99\u5df2\u662f\u7591\u5fc3\u5927\u8d77\uff1a\u201c\u600e\u7684\u6b64\u4eba\u7684\u8eab\u624b\u4f3c\u4e4e\u6211\u66fe\u76f8\u8bc6\uff1f\u4ed6\u4e00\u5b9a\u4e0d\u662f\u4e2a\u666e\u901a\u7684\u55bd\u5175\uff0c\u666e\u901a\u7684\u55bd \u5175\u51b3\u6ca1\u6709\u5982\u6b64\u672c\u9886\uff01\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u5f85\u6211\u6765\u53d6\u8fd9\u53ae\u6027\u547d\uff01\u201d\u4eba\u672a\u5230\uff0c\u638c\u5148\u53d1\u3002\u6768\u5a49\u5fc3\u91cc\u6697\u6697\u53eb\u82e6\uff0c\u53ea\u9053\u81ea\u5df1\u96be \u9003\u4e00\u547d\u3002\u4e0d\u6599\u891a\u4e91\u5cf0\u7684\u5288\u7a7a\u638c\u529b\u6253\u6765\uff0c\u6768\u5a49\u53ea\u89c9\u597d\u4f3c\u7ed9\u4eba\u8f7b\u8f7b\u63a8\u4e86\u4e00\u4e0b\u4f3c\u7684\uff0c\u53cd\u800c\u662f\u5c60\u9f99 \u201c\u54ce\u54df\u201d\u7684\u53eb\u4e86\u8d77\u6765\uff0c\u811a\u6b65\u4e00\u4e2a\u8e09\u8dc4\uff0c\u7adf\u7136\u5411\u4e00\u65c1\u659c\u7a9c\u4e09\u6b65\uff0c\u65b9\u80fd\u7a33\u4f4f\u8eab\u5f62\u3002\u6768\u5a49\u85c9\u7740\u90a3\u80a1 \u5288\u7a7a\u638c\u529b\u7684\u4e00\u63a8\uff0c\u4e00\u4e2a\u201c\u7ec6\u9600\u5de7\u7ffb\u4e91\u201d\u5012\u7eb5\u51fa\u4e08\u8bb8\u5f00\u5916\uff0c\u7acb\u5373\u65bd\u5c55\u8f7b\u529f\u98de\u8dd1\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u5bf9\u4e0d\u4f4f\uff0c\u5c60\u5144\uff0c\u53ef\u662f\u6211\u7684\u638c\u529b\u8bef\u4f24\u4e86\u4f60\uff1f\u201d\u4e0a\u6765\u6276\u4f4f\u5c60\u9f99\u3002\u5c60\u9f99\u4e00\u89c1\u6768\u5a49 \u7684\u8f7b\u529f\u8eab\u6cd5\uff0c\u8fd9\u624d\u84e6\u7136\u60ca\u89c9\uff0c\u77e5\u9053\u8fd9\u4e2a\u55bd\u5175\u662f\u8c01\u4e86\u3002\u5c60\u9f99\u672c\u6765\u5c31\u8981\u53bb\u8ffd\u7684\uff0c\u6b64\u65f6\u891a\u4e91\u5cf0\u5374\u53c8 \u6070\u597d\u4e0a\u6765\u6276\u4ed6\uff0c\u6293\u7740\u4e86\u4ed6\u7684\u624b\u81c2\u3002 \u3000\u3000\u5c60\u9f99\u7ed9\u4ed6\u6c14\u5f97\u557c\u7b11\u7686\u975e\uff0c\u8fde\u5fd9\u53eb\u9053\uff1a\u201c\u4f60\u6293\u7740\u6211\u5e72\u4ec0\u4e48\uff1f\u8d76\u5feb\u53bb\u6293\u90a3\u81ed\u4e2b\u5934\u624d\u662f\uff01\u5979\u5c31 \u662f\u6768\u5a49\u5440\uff01\u6211\u6ca1\u53d7\u4f24\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u8d54\u7b11\u9053\uff1a\u201c\u5bf9\u4e0d\u4f4f\uff0c\u6211\u771f\u662f\u7cca\u6d82\u4e86\u3002\u5176\u5b9e\u6211\u5e94\u8be5\u60f3\u5f97\u5230\u7684\uff0c\u4ee5\u4f60\u5c60\u5144\u7684\u529f\u529b\uff0c\u867d \u7136\u53d7\u6211\u638c\u529b\u6ce2\u53ca\uff0c\u4e5f\u662f\u4e0d\u4f1a\u53d7\u4f24\u7684\u3002\u597d\uff0c\u597d\uff0c\u54b1\u4eec\u8d76\u5feb\u53bb\u8ffd\uff01\u201d \u3000\u3000\u5c60\u9f99\u7ed9\u4ed6\u6b6a\u7f20\u4e86\u7247\u523b\uff0c\u6b64\u65f6\u6768\u5a49\u65e9\u5df2\u8dd1\u51fa\u6885\u6797\u4e86\u3002 \u3000\u3000\u6768\u5a49\u8dd1\u51fa\u6885\u6797\uff0c\u677e\u4e86\u53e3\u6c14\uff0c\u5ffd\u5730\u5fc3\u5ff5\u4e00\u52a8\uff0c\u4e0d\u89c9\u53c8\u8d77\u4e86\u4e00\u4e2a\u7591\u56e2\uff0c\u60f3\u9053\uff1a\u201c\u5947\u602a\uff0c\u4ee5\u891a \u4e91\u5cf0\u7684\u672c\u9886\uff0c\u4ed6\u82e5\u8981\u7528\u5288\u7a7a\u638c\u529b\u4f24\u6211\uff0c\u5c82\u80fd\u8bef\u4f24\u4e86\u5c60\u9f99\uff1f\u4ed6\u90a3\u80a1\u638c\u529b\u5012\u597d\u50cf\u5e2e\u5fd9\u6211\u9003\u8dd1\u4f3c \u7684\u3002\u8fd9\u662f\u4ec0\u4e48\u7f18\u6545\uff1f\u201d \u3000\u3000\u4f46\u6b64\u9645\u5979\u5df2\u662f\u65e0\u6687\u63a8\u6572\u8fd9\u4e2a\u7f18\u6545\u4e86\uff0c\u5979\u5fc5\u987b\u7acb\u5373\u626c\u58f0\u62a5\u8b66\uff0c\u53eb\u5927\u5bb6\u6765\u6349\u62ff\u5978\u7ec6\u3002\u4e8e\u662f\u5979 \u8dd1\u4e0a\u9ad8\u5904\uff0c\u634f\u7740\u55d3\u5b50\uff0c\u5c16\u58f0\u53eb\u9053\uff1a\u201c\u891a\u4e91\u5cf0\u662f\u5978\u7ec6\uff0c\u5c60\u9f99\u4e5f\u662f\u5978\u7ec6\uff01\u5feb\u6765\u6349\u62ff\u4ed6\u4eec\uff0c\u522b\u8ba9\u4ed6 \u4eec\u8dd1\u4e86\uff01\u201d \u3000\u3000\u6768\u5a49\u867d\u7136\u6709\u70b9\u6000\u7591\u891a\u4e91\u5cf0\u521a\u624d\u7684\u4e3e\u52a8\u53ef\u80fd\u662f\u6697\u4e2d\u52a9\u5979\u3002\u4f46\u56e0\u4ed6\u4eb2\u53e3\u8bf4\u51fa\u4ed6\u80cc\u540e\u7684\u201c\u5f53\u5bb6 \u4eba\u201d\u662f\u9633\u5929\u96f7\uff0c\u800c\u4e14\u53c8\u548c\u5c60\u9f99\u7ea6\u597d\u4e86\u8981\u540c\u6076\u76f8\u6d4e\uff0c\u56e0\u6b64\u5979\u4e5f\u53ea\u80fd\u628a\u4ed6\u5f53\u4f5c\u5978\u7ec6\u770b\u5f85\u4e86\u3002 \u3000\u3000\u6768\u5a49\u5728\u7528\u201c\u4f20\u97f3\u5165\u5bc6\u201d\u7684\u5185\u529f\u62a5\u8b66\u4e4b\u540e\uff0c\u4fbf\u5373\u6e9c\u56de\u5979\u5728\u54e8\u6240\u7684\u623f\u95f4\uff0c\u4e0d\u518d\u51fa\u5934\u3002 \u3000\u3000\u53ea\u542c\u5f97\u5927\u5be8\u91cc\u53f7\u89d2\u545c\u545c\u5439\u8d77\uff0c\u7247\u523b\u4e4b\u540e\uff0c\u56db\u5904\u90fd\u662f\u4eba\u58f0\u811a\u6b65\u58f0\uff0c\u6709\u51e0\u4e2a\u4eba\u8fd8\u5230\u5979\u8fd9\u5ea7\u54e8 \u6240\u8be2\u95ee\uff0c\u6709\u6ca1\u6709\u770b\u89c1\u5c60\u3001\u891a\u4e8c\u4eba\u7ecf\u8fc7\u3002\u6768\u5a49\u4ece\u4ed6\u4eec\u7684\u53e3\u4e2d\uff0c\u77e5\u9053\u5c60\u51e4\u5df2\u7ecf\u4eb2\u81ea\u4e0b\u4ee4\uff0c\u52a1\u5fc5\u8981 \u62ff\u83b7\u8fd9\u4e24\u4e2a\u4eba\u3002 \u3000\u3000\u55a7\u95f9\u4e86\u4e00\u4e2a\u66f4\u6b21\uff0c\u624d\u6e10\u6e10\u9759\u6b62\u4e0b\u6765\uff0c\u6768\u5a49\u6ca1\u6709\u79bb\u5f00\u54e8\u6240\uff0c\u5374\u4e0d\u77e5\u4ed6\u4eec\u662f\u5426\u62ff\u83b7\u891a\u4e91\u5cf0\u548c \u5c60\u9f99\u4e86\u3002 \u3000\u3000\u7b2c\u4e8c\u5929\u4e00\u65e9\uff0c\u6768\u5a49\u521a\u63a5\u4e86\u73ed\uff0c\u4e00\u4e2a\u4eba\u72ec\u81ea\u5728\u54e8\u5c97\u7684\u65f6\u5019\uff0c\u77f3\u749e\u6084\u6084\u5730\u8d70\u6765\u627e\u5979\u3002 \u3000\u3000\u6768\u5a49\u559c\u9053\uff1a\u201c\u6211\u6b63\u60f3\u627e\u4f60\uff0c\u4f60\u5374\u5148\u6765\u4e86\u3002\u201d \u3000\u3000\u77f3\u749e\u9053\uff1a\u201c\u6628\u665a\u53eb\u558a\u6349\u62ff\u5978\u7ec6\u7684\u662f\u4e0d\u662f\u4f60\uff1f\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u4e0d\u9519\u3002\u6211\u5728\u6885\u6797\u542c\u5230\u4e86\u4ed6\u4eec\u5728\u5077\u5077\u5546\u8bae\u3002\u201d \u3000\u3000\u77f3\u749e\u9053\uff1a\u201c\u5c60\u9f99\u662f\u5978\u7ec6\u6211\u4eec\u5e76\u4e0d\u6000\u7591\uff0c\u4f46\u891a\u4e91\u5cf0\u4e5f\u662f\u5978\u7ec6\uff0c\u8fd9\u5012\u662f\u5927\u51fa\u6211\u4eec\u610f\u6599\u4e4b \u5916\u3002\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u662f\u5440\uff0c\u5f53\u65f6\u6211\u4e5f\u51e0\u4e4e\u4e0d\u6562\u76f8\u4fe1\u6211\u81ea\u5df1\u7684\u8033\u6735\u3002\u8fd8\u6709\u4e00\u4e2a\u4e5f\u662f\u6765\u4f5c\u5978\u7ec6\u7684 \u5462\u3002\u201d \u3000\u3000\u8bdd\u672a\u8bf4\u5b8c\uff0c\u77f3\u749e\u5ffd\u5730\u63d2\u53e3\u9053\uff1a\u201c\u662f\u4e0d\u662f\u98de\u9f99\u5c71\u6d3e\u6765\u7684\u90a3\u4e2a\u4f7f\u8005\uff1f\u4ed6\u662f\u5949\u4e86\u7aa6\u5b89\u5e73\u7684\u547d \u4ee4\uff0c\u524d\u6765\u8c0e\u62a5\u519b\u60c5\u7684\uff0c\u662f\u4e48\uff1f\u201d \u3000\u3000\u6768\u5a49\u6014\u4e86\u4e00\u6014\uff0c\u8bf4\u9053\uff1a\u201c\u54a6\uff0c\u4f60\u600e\u4e48\u4e5f\u77e5\u9053\u4e86\uff1f\u201d \u3000\u3000\u77f3\u749e\u5fae\u5fae\u7b11\u9053\uff0c\u62ff\u51fa\u4e86\u4e00\u5c01\u4fe1\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u5c01\u4fe1\u4e5f\u662f\u4f60\u5199\u7684\u5427\uff1f\u201d \u3000\u3000\u6768\u5a49\u8be7\u9053\uff1a\u201c\u4ec0\u4e48\u4fe1\uff1f\u201d\u77f3\u749e\u9053\uff1a\u201c\u5728\u674e\u601d\u5357\u623f\u4e2d\u53d1\u73b0\u7684\u8fd9\u5c01\u4fe1\uff0c\u4fe1\u4e2d\u63ed\u53d1\u4e86\u98de\u9f99\u5c71\u7684 \u5978\u8c0b\uff0c\u6211\u4ee5\u4e3a\u662f\u4f60\u5199\u7684\uff0c\u600e\u7684\u4f60\u4e0d\u77e5\u9053\uff1f\u90a3\u4e48\u5012\u662f\u6211\u731c\u9519\u4e86\u3002\u201d \u3000\u3000\u6768\u5a49\u66f4\u662f\u5947\u602a\uff0c\u8bf4\u9053\uff1a\u201c\u8bf7\u4f60\u7ed9\u6211\u770b\u770b\u3002\u201d\u62c6\u5f00\u4fe1\u4e00\u770b\uff0c\u4fe1\u4e2d\u7684\u5185\u5bb9\u679c\u7136\u548c\u5979\u60f3\u5199\u7684\u5dee \u4e0d\u591a\uff0c\u7b14\u8ff9\u751a\u4e3a\u59a9\u5a9a\uff0c\u4f46\u67d4\u4e2d\u5bd3\u521a\uff0c\u770b\u5f97\u51fa\u662f\u7537\u5b50\u5047\u5192\u5973\u5b50\u7684\u7b14\u3002 \u3000\u3000\u6768\u5a49\u7b11\u9053\uff1a\u201c\u8fd9\u53ef\u771f\u662f\u5947\u602a\u4e86\uff0c\u4e0d\u7792\u4f60\u8bf4\uff0c\u6211\u5012\u662f\u7684\u786e\u5199\u4e86\u4e00\u5c01\u4fe1\uff0c\u548c\u8fd9\u5c01\u4fe1\u7684\u5185\u5bb9\u5dee \u4e0d\u591a\u7684\uff0c\u4f46\u6628\u665a\u6211\u5374\u672a\u5f97\u673a\u4f1a\u6295\u5165\u674e\u601d\u5357\u7684\u623f\u4e2d\uff0c\u54a6\uff0c\u4f60\u770b\uff0c\u8fd9\u5c01\u4fe1\u8fd8\u5728\u8fd9\u91cc\u5462\u3002\u201d \u3000\u3000\u77f3\u749e\u770b\u4e86\u6768\u5a49\u6240\u5199\u7684\u4fe1\uff0c\u518d\u542c\u4e86\u6768\u5a49\u8bb2\u8ff0\u6628\u665a\u7684\u6240\u89c1\uff0c\u4e0d\u89c9\u4e5f\u662f\u5927\u4e3a\u8be7\u5f02\uff0c\u8bf4\u9053\uff1a\u201c\u4f60 \u770b\u89c1\u891a\u4e91\u5cf0\u4ece\u674e\u601d\u5357\u7684\u623f\u95f4\u91cc\u94bb\u51fa\u6765\uff0c\u96be\u9053\uff0c\u96be\u9053\u90a3\u5c01\u4fe1\u7adf\u662f\u891a\u4e91\u5cf0\u5199\u7684\u4e0d\u6210\uff1f\u4f46\u4f60\u53c8\u8bf4\u891a \u4e91\u5cf0\u4e43\u662f\u5978\u7ec6\u3002\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u662f\u5440\uff0c\u6240\u4ee5\u6211\u89c9\u5f97\u5947\u602a\u6781\u4e86\uff01\u891a\u4e91\u5cf0\u4e0e\u5c60\u9f99\u7528\u4ed6\u4eec\u81ea\u5df1\u4eba\u7684\u6697\u53f7\u8054\u7edc\uff0c\u8ddf\u7740 \u53c8\u5bc6\u8c0b\u7be1\u5be8\u4e3b\u7684\u4f4d\u5b50\uff0c\u8fd9\u90fd\u662f\u6211\u4eb2\u773c\u770b\u89c1\uff0c\u4eb2\u8033\u542c\u5230\u7684\u3002\u201d\u53c8\u9053\uff1a\u201c\u5f53\u65f6\u6211\u770b\u89c1\u891a\u4e91\u5cf0\u4ece\u674e \u601d\u5357\u7684\u623f\u95f4\u91cc\u94bb\u51fa\u6765\uff0c\u6211\u8fd8\u4ee5\u4e3a\u4ed6\u662f\u60f3\u8c0b\u523a\u5357\u54e5\uff0c\u4f46\u73b0\u5728\u4f60\u7ed9\u6211\u770b\u4e86\u8fd9\u5c01\u4fe1\uff0c\u6211\u5374\u4e0d\u80fd\u4e0d\u6709 \u70b9\u6000\u7591\u81ea\u5df1\u7684\u60f3\u6cd5\u4e86\u3002\u201d \u3000\u3000\u77f3\u749e\u9053\uff1a\u201c\u6216\u8bb8\u662f\u7b2c\u4e09\u4e2a\u4eba\uff1f\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u891a\u4e91\u5cf0\u62ff\u7740\u4e86\u6ca1\u6709\uff1f\u201d \u3000\u3000\u77f3\u749e\u9053\uff1a\u201c\u6ca1\u6709\uff0c\u4ed6\u548c\u5c60\u9f99\u90fd\u8dd1\u4e86\u3002\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u5982\u679c\u4ed6\u662f\u6697\u4e2d\u5e2e\u5fd9\u54b1\u4eec\u7684\uff0c\u4ed6\u4f55\u4e0d\u8bf4\u660e\u771f\u76f8\uff0c\u5374\u8981\u8dd1\u5462\uff1f\u201d \u3000\u3000\u77f3\u749e\u9053\uff1a\u201c\u662f\u5440\uff01\u6211\u4e5f\u662f\u8fd9\u4e48\u60f3\u3002\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u6b64\u4e8b\u7ec8\u6709\u6c34\u843d\u77f3\u51fa\u4e4b\u65f6\uff0c\u6682\u4e14\u4e0d\u5fc5\u7ba1\u5b83\u3002\u4f46\u8fd9\u5c01\u4fe1\u5357\u54e5\u548c\u5c60\u51e4\u4ed6\u4eec\u90fd\u770b\u8fc7\u4e86 \u5427\uff1f\u201d\u77f3\u749e\u9053\uff1a\u201c\u90fd\u770b\u8fc7\u4e86\u3002\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u4eec\u51c6\u5907\u5982\u4f55\u5bf9\u4ed8\uff1f\u201d \u3000\u3000\u77f3\u749e\u9053\uff1a\u201c\u8fd8\u662f\u6309\u7167\u539f\u6765\u7684\u8ba1\u5212\uff0c\u7531\u674e\u601d\u5357\u4e0e\u5b5f\u5927\u4fa0\u524d\u5f80\u98de\u9f99\u5c71\u3002\u201d\u6768\u5a49\u8be7\u9053\uff1a\u201c\u4e3a\u4f55 \u660e\u77e5\u662f\u4e2a\u9677\u9631\uff0c\u8981\u5f80\u9677\u9631\u91cc\u8dd1\uff1f\u201d \u3000\u3000\u77f3\u749e\u7b11\u9053\uff1a\u201c\u8ba1\u5212\u867d\u662f\u539f\u6765\u7684\u8ba1\u5212\uff0c\u4f46\u4e5f\u5fae\u6709\u4e0d\u540c\u3002\u867d\u7136\u660e\u77e5\u662f\u4e2a\u9677\u9631\uff0c\u4f46\u674e\u76df\u4e3b\u548c\u5b5f \u5927\u5feb\u51b3\u5b9a\u5c06\u8ba1\u5c31\u8ba1\uff0c\u4ecd\u548c\u98de\u9f99\u5c71\u4f2a\u4f7f\u8005\u4e00\u540c\u56de\u53bb\u3002\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u98de\u9f99\u5c71\u90a3\u4e2a\u4f7f\u8005\u5462\uff1f\u201d \u3000\u3000\u77f3\u749e\u9053\uff1a\u201c\u90a3\u4e2a\u4f7f\u8005\u8fd8\u8499\u5728\u9f13\u91cc\uff0c\u4ee5\u4e3a\u6211\u4eec\u5df2\u4e2d\u4e86\u8c03\u864e\u79bb\u5c71\u8ba1\u5462\u3002\u201d \u3000\u3000\u6768\u5a49\u604d\u7136\u5927\u609f\uff0c\u8bf4\u9053\uff0c\u201c\u6211\u660e\u767d\u4e86\uff0c\u5357\u54e5\u6b64\u53bb\uff0c\u4e3a\u7684\u662f\u8981\u67e5\u771f\u76f8\u3002\u201d \u3000\u3000\u77f3\u749e\u9053\uff1a\u201c\u4e0d\u9519\u3002\u4f60\u5f88\u806a\u660e\uff0c\u4e00\u731c\u5c31\u7740\u3002\u601d\u5357\u5144\u65b0\u4efb\u76df\u4e3b\uff0c\u98de\u9f99\u5c71\u7684\u4eba\u4e0d\u4f1a\u8ba4\u8bc6\u4ed6\u7684\uff0c \u4ed6\u4ee5\u76df\u4e3b\u7684\u8eab\u4efd\u6697\u4e2d\u524d\u5f80\uff0c\u62ff\u5230\u4e86\u7aa6\u5b89\u5e73\u4e0e\u8499\u53e4\u79c1\u901a\u7684\u51ed\u636e\uff0c\u4fbf\u53ef\u5f53\u673a\u7acb\u65ad\u3002\u6709\u5b5f\u8001\u524d\u8f88\u52a9 \u4ed6\uff0c\u8981\u5236\u670d\u7aa6\u5b89\u5e73\u6599\u4e5f\u4e0d\u96be\u3002\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u597d\uff0c\u8fd9\u6837\u6211\u5c31\u53ef\u4ee5\u653e\u5fc3\u4e86\u3002\u201d \u3000\u3000\u77f3\u749e\u7b11\u9053\uff1a\u201c\u6211\u544a\u8bc9\u4f60\u4e00\u4ef6\u4e8b\u60c5\uff0c\u4f60\u66f4\u53ef\u4ee5\u653e\u5fc3\u3002\u6211\u548c\u601d\u5357\u5144\u5df2\u7ecf\u8c08\u8fc7\u4e86\uff0c\u4ed6\u5bf9\u4f60\u5b9e\u5728 \u662f\u60c5\u6df1\u4e00\u7247\uff0c\u5f53\u4ed6\u77e5\u9053\u4e86\u4f60\u53d7\u4e86\u59d4\u5c48\u4e4b\u65f6\uff0c\u6094\u6068\u4e4b\u60c5\u6ea2\u4e8e\u8f9e\u8272\uff0c\u4ed6\u8fd8\u8bf4\u8981\u5bf9\u4f60\u8d1f\u8346\u8bf7\u7f6a \u5462\u3002\u201d \u3000\u3000\u6768\u5a49\u9762\u4e0a\u4e00\u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u548c\u4ed6\u8bf4\u7684\u8bdd\u6211\u90fd\u542c\u89c1\u4e86\uff0c\u4f46\u6211\u73b0\u5728\u8fd8\u4e0d\u60f3\u89c1\u4ed6\u3002\u201d\u5176\u5b9e\u5979\u6628 \u665a\u5728\u6885\u6797\u5077\u542c\uff0c\u53ea\u662f\u542c\u89c1\u4e86\u4e00\u534a\u3002 \u3000\u3000\u77f3\u749e\u7b11\u9053\uff1a\u201c\u90a3\u4e5f\u597d\uff0c\u8ba9\u4ed6\u4ece\u98de\u9f99\u5c71\u56de\u6765\u518d\u8bf4\u4e0d\u8fdf\u3002\u5426\u5219\u4ed6\u77e5\u9053\u4f60\u5728\u8fd9\u513f\uff0c\u6050\u6015\u662f\u4e0d\u613f \u79bb\u5f00\u4f60\u4e86\u3002\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u4f60\u51fa\u6765\u4e45\u4e86\uff0c\u4f60\u5148\u56de\u53bb\u5427\uff0c\u6709\u4e8b\u60c5\u6211\u518d\u627e\u4f60\u3002\u201d \u3000\u3000\u8bdd\u72b9\u672a\u4e86\uff0c\u53ea\u542c\u5f97\u5b5f\u660e\u971e\u7684\u58f0\u97f3\u53eb\u9053\uff1a\u201c\u77f3\u5927\u54e5\uff0c\u77f3\u5927\u54e5\uff01\u201d \u3000\u3000\u6768\u5a49\u5403\u4e86\u4e00\u60ca\uff0c\u84e6\u5730\u8bf4\u9053\uff1a\u201c\u7cdf\u7cd5\uff0c\u60f3\u4e0d\u5230\u5979\u6765\u627e\u4f60\u3002\u4f60\u53ef\u5f97\u7ed9\u6211\u906e\u7792\u70b9\u513f\u3002\u201d \u3000\u3000\u77f3\u749e\u9053\uff1a\u201c\u6211\u5728\u8fd9\u513f\uff0c\u6709\u4ec0\u4e48\u4e8b\u4e48\uff1f\u201d \u3000\u3000\u5b5f\u660e\u971e\u8d70\u4e86\u8fc7\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u601d\u5357\u5c31\u8981\u52a8\u8eab\u4e86\uff0c\u90a3\u5c01\u4fe1\u662f\u8c01\u5199\u7684\uff0c\u4f60\u53ef\u67e5\u5230\u4e86\u7ebf\u7d22\u4e48\uff1f\u201d \u5fc3\u91cc\u9887\u89c9\u5947\u602a\uff0c\u60f3\u9053\uff1a\u201c\u4ed6\u5411\u5c60\u51e4\u8ba8\u4e86\u8fd9\u5c01\u4fe1\uff0c\u8bf4\u662f\u8981\u62ff\u53bb\u4ed4\u7ec6\u7422\u78e8\uff0c\u5982\u600e\u7684\u8dd1\u5230\u8fd9\u513f\u6765\u548c \u4e00\u4e2a\u54e8\u5175\u95f2\u804a\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u8d77\u4e86\u7591\u5fc3\uff0c\u4e0d\u7981\u5bf9\u6768\u5a49\u770b\u591a\u4e86\u4e24\u773c\uff0c\u77f3\u749e\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u65b0\u6765\u7684\u5f1f\u5144\uff0c\u6211\u6765\u67e5\u95ee \u4ed6\u6628\u665a\u6709\u6ca1\u53d1\u73b0\u5978\u7ec6\u4ece\u8fd9\u513f\u9003\u8fc7\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u770b\u4e86\u4e24\u773c\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5947\u602a\uff0c\u8fd9\u4eba\u5012\u597d\u4f3c\u5728\u54ea\u513f\u89c1\u8fc7\u7684\uff1f\u201d\u8981\u77e5\u6768\u5a49\u867d\u7136\u6539\u4e86 \u7537\u88c5\uff0c\u9762\u8c8c\u4e5f\u5df2\u6539\u53d8\uff0c\u4f46\u8eab\u6750\u4f53\u6001\u662f\u6539\u4e0d\u4e86\u7684\uff0c\u5c24\u5176\u90a3\u5bf9\u660e\u5982\u79cb\u6c34\u7684\u773c\u775b\uff0c\u5b5f\u660e\u971e\u5728\u90a3\u6b21\u548c \u5979\u4f1a\u9762\u4e4b\u540e\uff0c\u66fe\u7559\u4e0b\u6df1\u523b\u7684\u5370\u8c61\uff0c\u6545\u6b64\u4e00\u89c1\u4fbf\u6709\u4f3c\u66fe\u76f8\u8bc6\u4e4b\u611f\u3002\u4f46\u5b5f\u660e\u971e\u5374\u4e5f\u60f3\u4e0d\u5230\u4f1a\u662f\u6768 \u5a49\u3002 \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u4f60\u53eb\u4ec0\u4e48\u540d\u5b57\uff0c\u662f\u77f3\u5934\u9886\u5f15\u8350\u4f60\u4e0a\u5c71\u7684\u5417\uff1f\u201d \u3000\u3000\u6768\u5a49\u80e1\u4e71\u634f\u9020\u4e86\u4e00\u4e2a\u540d\u5b57\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u662f\u524d\u51e0\u5929\u6295\u5954\u6765\u7684\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u4f60\u4eec\u5e76\u975e\u65e9\u5c31\u76f8\u8bc6\uff1f\u201d \u3000\u3000\u77f3\u749e\u9053\uff1a\u201c\u4ed6\u6765\u7684\u65f6\u5019\uff0c\u662f\u6211\u63a5\u89c1\u4ed6\uff0c\u8003\u95ee\u4ed6\u7684\u6b66\u529f\uff0c\u4f3c\u4e4e\u8fd8\u8fc7\u5f97\u53bb\uff0c\u6545\u6b64\u6211\u53eb\u4ed6\u5728\u8fd9 \u513f\u770b\u5b88\u8fd9\u5ea7\u54e8\u5c97\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u77f3\u5927\u54e5\u8d4f\u8bc6\u7684\u4eba\uff0c\u672c\u9886\u60f3\u5fc5\u662f\u4e0d\u9519\u7684\u4e86\u3002\u4f60\u7ec3\u8fc7\u4e9b\u4ec0\u4e48\u529f\u592b\uff1f\u201d\u8a00\u8bed\u4e4b \u4e2d\uff0c\u7adf\u6709\u8981\u8bd5\u6768\u5a49\u6b66\u529f\u4e4b\u610f\u3002 \u3000\u3000\u6768\u5a49\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u7ec3\u7684\u4e0d\u8fc7\u662f\u4e61\u4e0b\u4eba\u7684\u628a\u5f0f\uff0c\u600e\u6562\u5728\u5b5f\u5973\u4fa0\u8ddf\u524d\u732e\u4e11\uff1f\u201d \u3000\u3000\u77f3\u749e\u5fc3\u60f3\uff1a\u201c\u53ef\u4e0d\u80fd\u8ba9\u5979\u8d77\u7591\u3002\u201d\u5f53\u4e0b\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u8bf4\u601d\u5357\u5144\u5c31\u8981\u52a8\u8eab\u4e86\u5417\uff1f\u201d \u3000\u3000\u5b5f\u660e\u971e\u77e5\u9053\u674e\u601d\u5357\u5927\u7ea6\u8fd8\u6709\u4e00\u4e2a\u65f6\u8fb0\u624d\u8d70\uff0c\u5979\u672c\u6765\u60f3\u8bd5\u4e86\u6768\u5a49\u7684\u6b66\u529f\u624d\u56de\u53bb\u9001\u884c\u7684\uff0c\u4f46 \u56e0\u5979\u662f\u8dd1\u6765\u50ac\u77f3\u749e\u56de\u53bb\u7684\uff0c\u5982\u4eca\u77f3\u749e\u53cd\u8fc7\u6765\u63d0\u9192\u5979\uff0c\u5979\u81ea\u662f\u4e0d\u4fbf\u518d\u5728\u8fd9\u91cc\u9017\u7559\u4e86\u3002 \u3000\u3000\u5b5f\u660e\u971e\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4e8b\u6709\u7f13\u6025\u8f7b\u91cd\u4e4b\u5206\uff0c\u6b64\u4eba\u770b\u5b88\u8fd9\u5ea7\u54e8\u5c97\uff0c\u6211\u4ec0\u4e48\u65f6\u5019\u90fd\u53ef\u4ee5 \u627e\u4ed6\u3002\u601d\u5357\u52a8\u8eab\u5728\u5373\uff0c\u6211\u53ef\u4e0d\u80fd\u963b\u8bef\u4e86\u77f3\u749e\u4e0e\u4ed6\u8bdd\u522b\u3002\u201d\u5176\u5b9e\u5979\u81ea\u5df1\u4e5f\u662f\u6015\u6ca1\u6709\u65f6\u95f4\u4e0e\u674e\u601d \u5357\u8bdd\u522b\uff0c\u4e8e\u662f\u5728\u542c\u4e86\u77f3\u749e\u7684\u8bdd\u4e4b\u540e\uff0c\u4fbf\u5373\u5bf9\u6768\u5a49\u8bf4\u9053\uff1a\u201c\u4f60\u6563\u503c\u4e4b\u540e\uff0c\u8bf7\u5230\u5185\u5be8\u627e\u6211\uff0c\u6211\u548c \u5c60\u5be8\u4e3b\u60f3\u770b\u770b\u4f60\u7684\u529f\u592b\u3002\u4f60\u4e0d\u5fc5\u5ba2\u6c14\uff0c\u5c71\u5be8\u91cc\u6b63\u9700\u8981\u6709\u672c\u9886\u7684\u4eba\uff0c\u4f60\u82e5\u662f\u6709\u771f\u5b9e\u7684\u529f\u592b\uff0c\u5c60 \u5be8\u4e3b\u662f\u5e94\u8be5\u63d0\u62d4\u4f60\u505a\u5934\u76ee\u7684\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u8d70\u540e\uff0c\u6768\u5a49\u795e\u601d\u4e0d\u5b9a\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u770b\u5b5f\u660e\u971e\u7684\u795e\u6c14\uff0c\u5206\u660e\u5979\u5df1\u662f\u5bf9\u6211\u8d77\u7591\u3002\u6211 \u5728\u8fd9\u5c71\u5be8\u4e2d\u6050\u6015\u662f\u4e0d\u80fd\u6df7\u4e0b\u53bb\u4e86\u3002\u201d\u5de6\u601d\u53f3\u60f3\uff0c\u7ec8\u4e8e\u60f3\u5230\u4e86\u4e00\u4e2a\u201c\u8d70\u201d\u5b57\uff0c\u201c\u6211\u4f55\u4e0d\u4e5f\u5230\u98de \u9f99\u5c71\u53bb\uff1f\u6211\u73b0\u5728\u8fd9\u4e2a\u6a21\u6837\uff0c\u5357\u54e5\u89c1\u4e86\u6211\u4e5f\u672a\u5fc5\u8ba4\u5f97\u3002\u6211\u6697\u4e2d\u8ddf\u968f\u4ed6\uff0c\u5c0f\u5fc3\u4e5f\u5c31\u662f\u4e86\u3002\u6211\u7684\u672c \u9886\u867d\u4e0d\u53ca\u4ed6\uff0c\u5fc5\u8981\u4e4b\u65f6\uff0c\u4e5f\u53ef\u4ee5\u505a\u4ed6\u4e00\u4e2a\u5e2e\u624b\u3002\u201d \u3000\u3000\u6768\u5a49\u6253\u5b9a\u4e86\u4e3b\u610f\uff0c\u4fbf\u5373\u5199\u4e86\u4e00\u5c01\u544a\u522b\u7684\u4e66\u4fe1\uff0c\u5728\u6563\u503c\u4e4b\u65f6\uff0c\u4ea4\u7ed9\u63a5\u73ed\u7684\u55bd\u5175\uff0c\u8bf4\u9053\uff1a \u201c\u77f3\u5934\u9886\u82e5\u6765\u627e\u6211\uff0c\u4f60\u628a\u8fd9\u5c01\u4fe1\u7ed9\u4ed6\u3002\u6211\u6709\u70b9\u5c0f\u4e8b\uff0c\u8981\u4e0b\u5c71\u884c\u4e00\u884c\uff0c\u544a\u4e24\u5929\u5047\u3002\u201d\u90a3\u55bd\u5175\u7b11 \u9053\uff1a\u201c\u8fd9\u70b9\u5c0f\u4e8b\uff0c\u4f55\u987b\u7980\u544a\u77f3\u526f\u5be8\u4e3b\uff0c\u6211\u627e\u4e00\u4f4d\u5f1f\u5144\u66ff\u4f60\u5f53\u503c\uff0c\u4e5f\u5c31\u884c\u4e86\u3002\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u6211\u662f\u77f3\u5934\u9886\u5dee\u9063\u6765\u8fd9\u91cc\u770b\u5b88\u7684\uff0c\u6050\u6015\u4ed6\u4f1a\u95ee\u8d77\u3002\u201d\u90a3\u55bd\u5175\u9053\uff1a\u201c\u597d\u5427\uff0c\u5018\u82e5 \u4ed6\u5230\u6b64\u5de1\u903b\u95ee\u8d77\u4e86\u4f60\uff0c\u6211\u5c31\u7ed9\u4ed6\u3002\u201d\u6768\u5a49\u5b89\u6392\u59a5\u5f53\uff0c\u4fbf\u5373\u6084\u6084\u4e0b\u5c71\u3002 \u3000\u3000\u5b5f\u660e\u971e\u4e0e\u77f3\u749e\u56de\u5230\u5927\u5385\uff0c\u53ea\u89c1\u4f17\u4eba\u6b63\u5728\u66ff\u674e\u601d\u5357\u996f\u884c\uff0c\u91cd\u8981\u7684\u5934\u76ee\u90fd\u5df2\u9f50\u96c6\uff0c\u5c31\u53ea\u7b49\u4ed6 \u4eec\u4e8c\u4eba\u4e86\u3002 \u3000\u3000\u5c60\u51e4\u9053\uff1a\u201c\u77f3\u5e08\u54e5\uff0c\u4f60\u5230\u54ea\u513f\u53bb\u4e86\uff0c\u5927\u4f19\u513f\u6b63\u5728\u7b49\u4f60\u5462\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u7b11\u9053\uff1a\u201c\u4ed6\u53bb\u627e\u4e00\u4e2a\u54e8\u5175\u804a\u5929\uff0c\u4e0d\u662f\u6211\u53bb\u50ac\u4ed6\uff0c\u6050\u6015\u4ed6\u73b0\u5728\u8fd8\u672a\u56de\u6765\u5462\u3002\u201d \u3000\u3000\u5c60\u51e4\u607c\u9053\uff1a\u201c\u6211\u4ee5\u4e3a\u4f60\u6709\u4ec0\u4e48\u7d27\u8981\u7684\u4e8b\u60c5\u51fa\u53bb\u5de1\u67e5\uff0c\u600e\u7684\u5374\u8dd1\u53bb\u548c\u4e00\u4e2a\u54e8\u5175\u95f2\u804a\uff1f\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u4f60\u4e0d\u8981\u8bf4\u4ed6\uff0c\u8fd9\u4e2a\u54e8\u5175\u6709\u70b9\u7279\u522b\u3002\u201d \u3000\u3000\u5c60\u51e4\u9053\uff1a\u201c\u600e\u4e48\u7279\u522b\u4e86\uff1f\u201d\u5b5f\u660e\u971e\u9053\uff1a\u201c\u542c\u77f3\u749e\u8bf4\uff0c\u4ed6\u7684\u6b66\u529f\u5f88\u662f\u4e0d\u9519\u3002\u6211\u4e5f\u66fe\u7559\u610f\u4e86 \u4ed6\u7684\u773c\u795e\uff0c\u7684\u786e\u4f3c\u662f\u7ec3\u8fc7\u4e0a\u4e58\u5185\u529f\u7684\u4f1a\u5bb6\u3002\u8fd8\u6709\u4e00\u5c42\u5947\u602a\uff0c\u8fd9\u4eba\u4f3c\u4e4e\u662f\u6211\u66fe\u7ecf\u89c1\u8fc7\u7684\u3002\u6211\u5374 \u60f3\u4e0d\u8d77\u6765\u3002\u201d \u3000\u3000\u5c60\u51e4\u9053\uff1a\u201c\u54e6\uff0c\u5c71\u5be8\u91cc\u6709\u8fd9\u6837\u7684\u4e00\u4e2a\u4eba\uff1f\u4e00\u4e2a\u6765\u5386\u4e0d\u660e\u7684\u891a\u4e91\u5cf0\u5df2\u662f\u95f9\u5f97\u6ee1\u5929\u98ce\u96e8\uff0c\u53ef \u4e0d\u80fd\u518d\u6709\u7b2c\u4e8c\u4e2a\u891a\u4e91\u5cf0\u4e86\u3002\u8fd9\u4eba\u5e95\u7ec6\u5982\u4f55\uff0c\u975e\u5f97\u67e5\u4e2a\u660e\u767d\u4e0d\u53ef\uff01\u201d \u3000\u3000\u5b5f\u660e\u971e\u671b\u4e86\u77f3\u749e\u4e00\u773c\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4eba\u7684\u6765\u5386\u5e94\u662f\u77f3\u5927\u54e5\u77e5\u9053\u7684\uff0c\u5c60\u59d0\u59d0\u4e0d\u5fc5\u62c5\u5fe7\u3002\u201d \u3000\u3000\u5c60\u51e4\u9053\uff1a\u201c\u4ed6\u662f\u4ec0\u4e48\u4eba\uff0c\u672c\u9886\u600e\u6837\uff1f\u6765\u4e86\u591a\u4e45\u4e86\uff1f\u201d\u5fc3\u91cc\u6709\u70b9\u5947\u602a\uff1a\u201c\u77f3\u5e08\u54e5\u4f55\u4ee5\u4e00\u76f4 \u6ca1\u6709\u544a\u8bc9\u6211\u5462\uff1f\u201d \u3000\u3000\u77f3\u749e\u8bf4\u9053\uff1a\u201c\u6211\u4e5f\u6ca1\u6709\u600e\u6837\u76d8\u95ee\u8fc7\u4ed6\uff0c\u53ea\u77e5\u4ed6\u662f\u4e00\u4e2a\u7236\u6bcd\u53cc\u4ea1\u7684\u82e6\u4eba\u513f\uff0c\u5e76\u65e0\u4eb2\u53cb\u53ef\u4ee5 \u501a\u9760\uff0c\u653e\u6b64\u6295\u5954\u4e49\u519b\u3002\u672c\u9886\u8fd8\u8fc7\u5f97\u53bb\uff0c\u4f46\u4f3c\u4e4e\u4e5f\u4e0d\u89c1\u5f97\u662f\u600e\u6837\u9ad8\u660e\u3002\u4e0d\u8fc7\uff0c\u6216\u8bb8\u662f\u6211\u770b\u5dee\u4e86 \u4e5f\u8bf4\u4e0d\u5b9a\u3002\u597d\u5728\u5b5f\u5973\u4fa0\u5df2\u7ea6\u4e86\u4ed6\u4e0b\u5348\u6765\u89c1\uff0c\u4f60\u4eec\u82e5\u6709\u7591\u5fc3\uff0c\u5230\u65f6\u53ef\u4ee5\u4ed4\u7ec6\u95ee\u4ed6\u3002\u201d\u53c8\u9053\uff0c \u201c\u4ed6\u662f\u524d\u5929\u6765\u7684\uff0c\u6211\u89c1\u4f60\u8fd9\u4e24\u5929\u4e8b\u60c5\u592a\u5fd9\uff0c\u6240\u4ee5\u6211\u5c31\u66ff\u4f60\u4f5c\u4e3b\uff0c\u624d\u5f55\u7528\u4e86\u4ed6\uff0c\u6ca1\u6709\u544a\u8bc9\u4f60 \u4e86\u3002\u201d \u3000\u3000\u77f3\u749e\u6545\u610f\u628a\u6768\u5a49\u7684\u672c\u9886\u8bf4\u5f97\u666e\u666e\u901a\u901a\uff0c\u90a3\u662f\u4e3a\u4e86\u4e0d\u60f9\u8d77\u5b5f\u660e\u971e\u4e0e\u5c60\u51e4\u7684\u7591\u5fc3\u4e4b\u6545\u3002\u201c\u6768 \u7897\u5018\u82e5\u662f\u60f3\u7792\u7740\u81ea\u5df1\u7684\u8eab\u4efd\uff0c\u5b5f\u59d1\u5a18\u8bd5\u5979\u6b66\u529f\uff0c\u5979\u81ea\u662f\u4e0d\u4f1a\u6296\u51fa\u6765\u3002\u5018\u82e5\u5979\u613f\u610f\u8ba9\u5b5f\u59d1\u5a18\u77e5 \u9053\uff0c\u90a3\u5c31\u662f\u5979\u7684\u4e8b\u4e86\u3002\u5e08\u59b9\u77e5\u9053\u5176\u4e2d\u539f\u59d4\uff0c\u60f3\u6765\u4e5f\u4e0d\u4f1a\u602a\u6211\u3002\u201d\u77f3\u749e\u5fc3\u60f3\u3002 \u3000\u3000\u5c60\u51e4\u5fc3\u60f3\uff0c\u65e2\u7136\u5b5f\u660e\u971e\u7ea6\u4e86\u6b64\u4eba\uff0c\u8981\u67e5\u95ee\u6b64\u4eba\u6765\u5386\uff0c\u4e5f\u5c31\u4e0d\u5fc5\u5fd9\u5728\u4e00\u65f6\u3002\u662f\u4ee5\u542c\u4e86\u77f3\u749e \u7684\u89e3\u91ca\u4e4b\u540e\uff0c\u867d\u7136\u89c9\u5f97\u4ed6\u8bf4\u5f97\u4e0d\u591f\u6e05\u695a\uff0c\u5374\u4e5f\u4e0d\u518d\u95ee\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u674e\u601d\u5357\u95ee\u9053\uff1a\u201c\u6628\u665a\u90a3\u4e2a\u9996\u5148\u53d1\u73b0\u5978\u7ec6\u7684\u4eba\uff0c\u53ef\u77e5\u9053\u4e86\u662f\u8c01\u4e48\uff1f\u201d \u3000\u3000\u77f3\u749e\u9053\uff1a\u201c\u8fd8\u672a\u77e5\u9053\u3002\u201d\u674e\u601d\u5357\u9053\uff1a\u201c\u8fd9\u5012\u662f\u5947\u602a\u4e86\u3002\u6b64\u4eba\u65e2\u7136\u4e3e\u53d1\u5978\u7ec6\uff0c\u4f55\u4ee5\u8fc7\u540e\u53c8 \u4e0d\u6765\u5411\u5be8\u4e3b\u7980\u62a5\uff1f\u201d \u3000\u3000\u5c60\u51e4\u9053\uff1a\u201c\u76df\u4e3b\u4f60\u653e\u5fc3\u524d\u53bb\u98de\u9f99\u5c71\uff0c\u8fd9\u91cc\u7684\u4e8b\uff0c\u4e0d\u5fc5\u7275\u6302\uff0c\u6211\u4f1a\u67e5\u4e2a\u6c34\u843d\u77f3\u51fa\u7684\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u6211\u5e76\u975e\u653e\u5fc3\u4e0d\u4e0b\uff0c\u53ea\u662f\u89c9\u5f97\u6628\u665a\u4e4b\u4e8b\uff0c\u63a5\u4e8c\u8fde\u4e09\u90fd\u662f\u4ee4\u4eba\u767e\u601d\u83ab\u89e3\u3002\u90a3\u5c01 \u4fe1\u662f\u8c01\u5199\u7684\uff0c\u73b0\u5728\u4e5f\u8fd8\u672a\u77e5\u9053\u3002\u201d \u3000\u3000\u5b5f\u5c11\u521a\u5ffd\u9053\uff1a\u201c\u77f3\u749e\uff0c\u4f60\u628a\u90a3\u5c01\u4fe1\u4ea4\u7ed9\u6211\u3002\u201d\u63a5\u8fc7\u4e86\u4fe1\uff0c\u4ed4\u7ec6\u518d\u770b\u4e00\u904d\uff0c\u597d\u50cf\u7528\u5fc3\u601d\u7d22 \u7684\u6837\u5b50\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u624d\u6536\u8d77\u6765\u3002 \u3000\u3000\u5c60\u51e4\u9053\uff1a\u201c\u5b5f\u4f2f\u4f2f\u53ef\u770b\u51fa\u4e86\u4ec0\u4e48\u7834\u7efd\uff1f\u201d \u3000\u3000\u5b5f\u5c11\u521a\u9053\uff1a\u201c\u8fd9\u4e66\u6cd5\u5012\u50cf\u662f\u6211\u4e00\u4e2a\u8001\u670b\u53cb\u7684\u7b14\u8ff9\uff0c\u4e0d\u8fc7\u6b64\u4eba\u5df2\u7ecf\u9690\u5c45\u591a\u5e74\uff0c\u51b3\u4e0d\u4f1a\u5230\u8fd9 \u91cc\u6765\u7684\u3002\u8fd9\u5c01\u4fe1\u6682\u4e14\u7531\u6211\u4fdd\u7ba1\uff0c\u6709\u673a\u4f1a\u89c1\u4e86\u6b64\u4eba\uff0c\u6211\u518d\u95ee\u4ed6\u3002\u201d \u3000\u3000\u5c60\u51e4\u95ee\u9053\uff1a\u201c\u90a3\u4eba\u662f\u8c01\uff1f\u201d\u5b5f\u5c11\u521a\u9053\uff1a\u201c\u662f\u4e00\u4e2a\u884c\u4e8b\u6709\u70b9\u602a\u50fb\uff0c\u4e0d\u613f\u610f\u4eba\u5bb6\u77e5\u9053\u4ed6\u7684\u59d3 \u540d\u7684\u8001\u524d\u8f88\u3002\u201d\u5c60\u51e4\u542c\u5f97\u5b5f\u5c11\u521a\u8fd9\u4e48\u8bf4\uff0c\u4e0d\u4fbf\u518d\u95ee\u4e0b\u53bb\u3002 \u3000\u3000\u5b5f\u5c11\u521a\u6c89\u541f\u534a\u664c\uff0c\u5ffd\u5730\u8bf4\u9053\uff1a\u201c\u6211\u770b\u891a\u4e91\u5cf0\u4e5f\u672a\u5fc5\u5c31\u662f\u5978\u7ec6\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u7239\u7239\u4f55\u4ee5\u89c1\u5f97\uff1f\u201d\u5b5f\u5c11\u521a\u9053\uff1a\u201c\u6211\u662f\u4f9d\u5e38\u7406\u63a8\u6d4b\u3002\u5018\u82e5\u4ed6\u662f\u5978\u7ec6\uff0c\u4ed6\u8d39\u4e86 \u8fd9\u6837\u5927\u7684\u6c14\u529b\u4e0d\u60dc\u4e0e\u6df3\u4e8e\u5468\u4f5c\u5bf9\uff0c\u597d\u4e0d\u5bb9\u6613\u624d\u53d6\u5f97\u4e86\u54b1\u4eec\u7684\u4fe1\u4efb\uff0c\u4ed6\u600e\u80af\u8f7b\u6613\u79bb\u5f00\u5c71\u5be8\uff1f\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff0c\u201c\u53ef\u60dc\u90a3\u4e2a\u6700\u5148\u558a\u53eb\u6349\u5978\u7ec6\u7684\u4eba\u627e\u4e0d\u7740\u3002\u6211\u60f3\u4ed6\u4e00\u5b9a\u662f\u6709\u6240\u89c1\u800c\u4e91\u7136\uff0c\u8bf4\u4e0d \u5b9a\u5c31\u662f\u89c1\u5230\u4e86\u4ed6\u548c\u5c60\u9f99\u540c\u5728\u4e00\u8d77\u3002\u8981\u4e0d\u7136\u4ed6\u4e0d\u4f1a\u628a\u8fd9\u4e24\u4e2a\u4eba\u7684\u540d\u5b57\u8fde\u5728\u4e00\u8d77\u53eb\u51fa\u6765\u3002\u201d \u3000\u3000\u5b5f\u5c11\u521a\u9053\uff1a\u201c\u5373\u4f7f\u5982\u6b64\uff0c\u4e5f\u672a\u80fd\u89e3\u91ca\u6211\u6240\u8bf4\u7684\u8fd9\u4e2a\u7591\u70b9\u3002\u4ed6\u660e\u77e5\u9053\u54b1\u4eec\u4f1a\u91cd\u7528\u4ed6\u7684\uff0c\u4ed6 \u4f55\u5fc5\u5192\u9669\u5728\u7b2c\u4e00\u592d\u7684\u665a\u4e0a\u5c31\u4e0e\u5c60\u9f99\u76f8\u4f1a\uff1f\u8ba9\u601d\u5357\u548c\u6211\u79bb\u5f00\u5c71\u5be8\u4e4b\u540e\uff0c\u4ed6\u4e5f\u505a\u4e86\u5927\u5934\u76ee\uff0c\u90a3\u65f6 \u540d\u6b63\u8a00\u987a\u7684\u4e0e\u5c60\u9f99\u89c1\u9762\u4e0d\u53ef\u4ee5\u5417\uff1f\u5c60\u9f99\u5728\u81ea\u5df1\u5bb6\u91cc\u517b\u4f24\uff0c\u4ed6\u4eec\u5728\u5c71\u5be8\u91cc\u78b0\u4e0a\u4e86\u8c08\u51e0\u53e5\u8bdd\uff0c\u90a3 \u4e5f\u662f\u5f88\u666e\u901a\u7684\u4e8b\u60c5\uff0c\u8c01\u4e5f\u4e0d\u4f1a\u6ce8\u610f\u3002\u201d \u3000\u3000\u5c60\u51e4\u9053\uff0c\u201c\u5b5f\u4f2f\u4f2f\u8bf4\u5f97\u6709\u7406\uff0c\u4f46\u4f55\u4ee5\u4ed6\u5728\u6628\u65e5\u6bd4\u6b66\u4e4b\u540e\uff0c\u5c31\u533f\u4e0d\u9732\u9762\uff1f\u53c8\u4f55\u4ee5\u4ed6\u53d7\u4e86\u51a4 \u6789\uff0c\u4ecd\u4e0d\u51fa\u6765\u8fa9\u767d\uff1f\u5982\u679c\u4ed6\u4e0d\u662f\u5978\u7ec6\u7684\u8bdd\u3002\u201d \u3000\u3000\u5b5f\u5c11\u521a\u9053\uff1a\u201c\u8fd9\u4e24\u6869\u4e8b\u60c5\u6211\u4e5f\u731c\u60f3\u4e0d\u900f\u3002\u6211\u4e0d\u6562\u8bf4\u4ed6\u4e00\u5b9a\u4e0d\u662f\u5978\u7ec6\uff0c\u6211\u7684\u610f\u601d\u662f\u5e94\u8be5\u614e \u91cd\u4e00\u4e9b\uff0c\u4e0d\u80fd\u53ea\u51ed\u4e00\u4e2a\u4eba\u8bf4\u4ed6\u662f\u5978\u7ec6\uff0c\u5c31\u5224\u5b9a\u4ed6\u662f\u5978\u7ec6\u3002\u201d \u3000\u3000\u5c60\u51e4\u9053\uff1a\u201c\u8fd9\u4e2a\u5f53\u7136\u3002\u5982\u679c\u4eca\u540e\u5728\u5c71\u5be8\u91cc\u53d1\u73b0\u4e86\u4ed6\uff0c\u6211\u4eec\u4e5f\u4e0d\u4f1a\u90a3\u6837\u9c81\u83bd\uff0c\u4e0d\u95ee\u6e05\u695a\u5c31 \u5411\u4ed6\u52a8\u624b\u7684\u3002\u201d \u3000\u3000\u996f\u884c\u9152\u559d\u8fc7\uff0c\u5df2\u662f\u8fd1\u5348\u65f6\u5206\uff0c\u674e\u601d\u5357\u4fbf\u4e0e\u5b5f\u5c11\u521a\u4e0b\u5c71\u3002\u77f3\u749e\u4e0e\u5b5f\u660e\u971e\u90fd\u6ca1\u6709\u5355\u72ec\u548c\u674e\u601d \u5357\u8bf4\u8bdd\u7684\u673a\u4f1a\u3002 \u3000\u3000\u5b5f\u660e\u971e\u7b49\u5230\u508d\u665a\u65f6\u5206\uff0c\u4ecd\u7136\u4e0d\u89c1\u6768\u5a49\u524d\u6765\u627e\u5979\uff0c\u4e0d\u89c9\u8d77\u4e86\u7591\u5fc3\uff0c\u4fbf\u5230\u90a3\u5ea7\u54e8\u5c97\u627e\u5bfb\u6768 \u5a49\u3002 \u3000\u3000\u90a3\u4e2a\u6b63\u5728\u5f53\u503c\u7684\u8001\u55bd\u5175\u8bf4\u9053\uff1a\u201c\u5b5f\u5973\u4fa0\u95ee\u7684\u662f\u90a3\u4f4d\u65b0\u6765\u7684\u59d3\u6613\u7684\u5c0f\u4f19\u5b50\u5417\uff01\u201d\u5b5f\u660e\u971e \u9053\uff1a\u201c\u6b63\u662f\uff0c\u4ed6\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u90a3\u8001\u55bd\u5175\u9053\uff1a\u201c\u4ed6\u544a\u5047\u4e24\u5929\uff0c\u4e0b\u5c71\u53bb\u4e86\u3002\u201d\u5b5f\u660e\u971e\u9053\uff1a\u201c\u54e6\uff0c\u4ed6\u4e3a\u4e86\u4ec0\u4e48\u4e8b\u60c5\u544a\u5047\uff1f\u201d \u3000\u3000\u90a3\u8001\u55bd\u5175\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\uff0c\u4e0d\u8fc7\u4ed6\u6709\u4e00\u5c01\u4fe1\u6258\u6211\u4ea4\u7ed9\u77f3\u526f\u5be8\u4e3b\u3002\u6211\u60f3\u4fe1\u4e2d\u4e00\u5b9a\u4f1a\u8bf4\u660e\u7f18 \u6545\u7684\u3002\u5b5f\u5973\u4fa0\uff0c\u8bf7\u4f60\u770b\u8fd9\u5c01\u4fe1\u3002\u201d \u3000\u3000\u6768\u5a49\u867d\u7136\u4ea4\u4ee3\u8fc7\u8fd9\u4e2a\u55bd\u5175\uff0c\u8fd9\u5c01\u4fe1\u53ea\u80fd\u4ea4\u7ed9\u77f3\u749e\uff0c\u4f46\u8fd9\u8001\u55bd\u5175\u77e5\u9053\u5b5f\u660e\u971e\u662f\u5c60\u51e4\u6700\u8981\u597d \u7684\u670b\u53cb\uff0c\u77f3\u749e\u53c8\u662f\u5c60\u51e4\u7684\u5e08\u5144\uff0c\u5fc3\u60f3\u8fd9\u5c01\u4fe1\u4ea4\u7ed9\u5b5f\u660e\u971e\u4e5f\u662f\u4e00\u6837\uff0c\u4e8e\u662f\u5c31\u4e0d\u7406\u4f1a\u6768\u5a49\u7684\u5429\u5490 \u4e86\u3002 \u3000\u3000\u5b5f\u660e\u971e\u63a5\u8fc7\u4e86\u4fe1\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u5c01\u4fe1\u662f\u7ed9\u77f3\u5934\u9886\u7684\uff0c\u6211\u4e0d\u4fbf\u79c1\u81ea\u62c6\u5f00\uff0c\u4e0d\u8fc7\uff0c\u6211\u53ef\u4ee5\u66ff\u4f60 \u8f6c\u4ea4\u3002\u201d\u5f53\u4e0b\u5c31\u62ff\u4e86\u8fd9\u5c01\u4fe1\u53bb\u627e\u77f3\u749e\u3002 \u3000\u3000\u5b5f\u660e\u971e\u867d\u7136\u4e0d\u4fbf\u628a\u4fe1\u62c6\u5f00\u6765\u770b\uff0c\u4f46\u4fe1\u5c01\u4e0a\u7684\u51e0\u4e2a\u5b57\u5374\u5df2\u6620\u5165\u5979\u7684\u773c\u5e18\uff0c\u6768\u5a49\u7684\u5b57\u5199\u5f97\u5341 \u5206\u5a1f\u79c0\uff0c\u4e00\u770b\u5c31\u77e5\u662f\u51fa\u81ea\u5973\u5b50\u4e4b\u624b\u3002 \u3000\u3000\u5b5f\u660e\u971e\u672c\u8eab\u662f\u4e2a\u5973\u5b50\uff0c\u5bf9\u5973\u5b50\u7684\u4f53\u6001\u7279\u70b9\u81ea\u662f\u6bd4\u8f83\u5bb9\u6613\u8bc6\u522b\uff0c\u5979\u4eca\u6668\u521d\u89c1\u6768\u5a49\u4e4b\u65f6\uff0c\u56e0 \u4e3a\u6839\u672c\u6ca1\u6709\u60f3\u5230\u5979\u662f\u5973\u5b50\uff0c\u6ca1\u6709\u600e\u6837\u7559\u5fc3\u3002\u6b64\u65f6\u4ece\u6768\u5a49\u7684\u5b57\u8ff9\u5f15\u8d77\u6000\u7591\uff0c\u4ed4\u7ec6\u56de\u60f3\uff0c\u8fd9\u4e2a \u201c\u5c0f\u55bd\u5175\u201d\u76f8\u8c8c\u867d\u7136\u4e11\u964b\uff0c\u4f46\u773c\u5982\u79cb\u6c34\uff0c\u7709\u4f3c\u6625\u5c71\uff0c\u5341\u6307\u5c16\u5c16\uff0c\u67f3\u8170\u8885\u5a1c\uff0c\u80a4\u8272\u5982\u7389\u3002\u8d8a\u60f3 \u8d8a\u89c9\u5f97\u8fd9\u201c\u5c0f\u55bd\u5175\u201d\u4e0d\u4f3c\u7537\u513f\uff0c\u9664\u4e86\u9762\u8c8c\u4e4b\u5916\uff0c\u8eab\u6750\u4f53\u6001\uff0c\u5206\u660e\u662f\u4e2a\u7f8e\u4eba\u80da\u5b50\u3002 \u3000\u3000\u5b5f\u660e\u971e\u5fc3\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u77f3\u749e\u5f53\u65f6\u7684\u795e\u8272\u4f3c\u4e4e\u6709\u70b9\u614c\u5f20\uff0c\u539f\u6765\u4ed6\u7792\u7740\u5c60\u51e4\uff0c\u79c1\u81ea\u6536\u85cf\u4e86\u4e00 \u4e2a\u5c11\u5973\uff01\u54fc\uff0c\u5c60\u51e4\u5bf9\u4ed6\u8fd9\u6837\u597d\uff0c\u4ed6\u7adf\u6562\u5982\u6b64\uff0c\u5f53\u771f\u662f\u5c82\u6709\u6b64\u7406\uff01\u201d\u518d\u53c8\u60f3\u9053\uff1a\u201c\u4e0d\u5bf9\uff0c\u4e0d \u5bf9\u3002\u77f3\u749e\u4e3a\u4eba\u8001\u5b9e\uff0c\u4e0e\u5c60\u51e4\u53c8\u662f\u9752\u6885\u7af9\u9a6c\u4e4b\u4ea4\uff0c\u5c82\u80fd\u5982\u6b64\u8f7b\u6613\u5c31\u79fb\u60c5\u522b\u604b\uff1f\u5185\u4e2d\u4e00\u5b9a\u53e6\u6709\u8e4a \u8df7\uff01\u201d \u3000\u3000\u5b5f\u660e\u971e\u6ee1\u8179\u7591\u56e2\uff0c\u62ff\u7740\u8fd9\u5c01\u4fe1\u53bb\u627e\u77f3\u749e\uff0c\u6070\u597d\u77f3\u749e\u4e5f\u60f3\u6765\u627e\u6768\u5a49\uff0c\u4e24\u4eba\u5728\u534a\u8def\u9047\u4e0a\u3002 \u3000\u3000\u5b5f\u660e\u971e\u4e00\u89c1\u77f3\u749e\u5c31\u51b7\u7b11\u9053\uff1a\u201c\u77f3\u5e08\u54e5\uff0c\u4f60\u505a\u5f97\u597d\u4e8b\uff01\u201d \u3000\u3000\u77f3\u749e\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u971e\u59d0\uff0c\u4f60\u8bf4\u4ec0\u4e48\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u4f60\u4e0d\u5fc5\u7792\u6211\u4e86\uff0c\u6211\u5df2\u7ecf\u77e5\u9053\u5566\u3002\u201d \u3000\u3000\u77f3\u749e\u83ab\u540d\u5947\u5999\uff0c\u9053\uff1a\u201c\u77e5\u9053\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u90a3\u5973\u5b50\u662f\u8c01\uff1f\u4f60\u4e3a\u4ec0\u4e48\u66ff\u5979\u906e\u7792\uff1f\u201d \u3000\u3000\u77f3\u749e\u8fd9\u624d\u77e5\u9053\u5979\u8bf4\u7684\u662f\u6768\u5a49\uff0c\u5fc3\u91cc\u597d\u751f\u5947\u602a\uff1a\u201c\u5979\u662f\u600e\u5730\u770b\u51fa\u6765\u7684\uff1f\u201d \u3000\u3000\u5b5f\u660e\u971e\u628a\u4fe1\u4e00\u626c\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u5979\u7559\u7ed9\u4f60\u7684\u4fe1\uff0c\u4f60\u62ff\u53bb\u770b\u5427\u3002\u54fc\uff0c\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u6211\u529d\u4f60\u8fd8 \u662f\u4ece\u5b9e\u62db\u6765\u7684\u597d\uff0c\u5426\u5219\u6211\u544a\u8bc9\u5c60\u51e4\uff0c\u53ea\u6015\u2026\u2026\u201d \u3000\u3000\u77f3\u749e\u63a5\u8fc7\u4e86\u4fe1\uff0c\u6ee1\u9762\u901a\u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u5b5f\u5973\u4fa0\uff0c\u4f60\u522b\u8bef\u4f1a\uff0c\u5509\uff0c\u4f60\u4e00\u5b9a\u8981\u77e5\u9053\uff0c\u6211\u53ea\u597d\u544a \u8bc9\u4f60\u4e86\uff0c\u5979\u2026\u2026\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff0c\u201c\u5979\u662f\u8c01\uff1f\u201d \u3000\u3000\u4e8b\u5df2\u5982\u65af\uff0c\u77f3\u749e\u53ea\u597d\u5b9e\u8bdd\u5b9e\u8bf4\uff0c\u9053\uff1a\u201c\u5979\u662f\u6768\u5a49\uff01\u201d \u3000\u3000\u5b5f\u660e\u971e\u5403\u4e86\u4e00\u60ca\uff0c\u5931\u58f0\u53eb\u9053\uff1a\u201c\u539f\u6765\u5979\u8fd8\u6d3b\u7740\uff01\u201d\u968f\u5373\u604d\u7136\u5927\u609f\uff1a\u201c\u602a\u4e0d\u5f97\u6211\u597d\u50cf\u5728\u54ea \u91cc\u89c1\u8fc7\uff0c\u539f\u6765\u5979\u662f\u6768\u5a49\uff01\u201d \u3000\u3000\u77f3\u749e\u4e00\u9762\u770b\u4fe1\uff0c\u4e00\u9762\u82e6\u7b11\u9053\uff1a\u201c\u5979\u6709\u96be\u8a00\u4e4b\u9690\uff0c\u4e0d\u613f\u610f\u8ba9\u674e\u601d\u5357\u548c\u4f60\u77e5\u9053\uff0c\u4f46\u6211\u5374\u4e5f\u60f3 \u4e0d\u5230\u5979\u4f1a\u7a81\u7136\u79bb\u5f00\u5c71\u5be8\u7684\u3002\u5b5f\u5973\u4fa0\uff0c\u8bf7\u4f60\u4e0d\u8981\u602a\u6211\uff0c\u8fd9\u3001\u8fd9\u2026\u2026\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u4f60\u4e0d\u5fc5\u8bf4\u5566\uff0c\u6211\u90fd\u660e\u767d\u4e86\uff01\u201d\u4e00\u4e2a\u8f6c\u8eab\uff0c\u52a0\u5feb\u811a\u6b65\uff0c\u5f84\u81ea\u53bb\u4e86\u3002 \u3000\u3000\u77f3\u749e\u4e0d\u89c9\u6709\u70b9\u66ff\u5b5f\u660e\u971e\u96be\u8fc7\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4e8b\u96be\u4e24\u5168\uff0c\u603b\u662f\u96be\u514d\u6709\u4e00\u4e2a\u4eba\u8981\u4f24\u5fc3\u7684\uff0c\u65e9 \u4e9b\u77e5\u9053\uff0c\u5bf9\u5b5f\u660e\u971e\u6216\u8005\u8fd8\u4f1a\u597d\u4e9b\u3002\u201d \u3000\u3000\u76c2\u660e\u971e\u72ec\u81ea\u56de\u53bb\uff0c\u5fc3\u4e2d\u4e00\u7247\u832b\u7136\u3002\u8fd9\u4ef6\u4e8b\u592a\u51fa\u4e4e\u5979\u610f\u6599\u4e4b\u5916\u4e86\uff0c\u5979\u987b\u5f97\u597d\u597d\u5730\u60f3\u4e00\u60f3\u3002 \u8fb9\u8d70\u8fb9\u60f3\uff0c\u8d8a\u60f3\u8d8a\u662f\u5fc3\u4e71\uff0c\u4e0d\u7531\u5f97\u5341\u5206\u96be\u8fc7\u3002 \u3000\u3000\u4f46\u5b5f\u660e\u971e\u7684\u96be\u8fc7\u5374\u4e0d\u662f\u4e3a\u4e86\u81ea\u5df1\uff0c\u800c\u662f\u4e3a\u4e86\u6768\u5a49\u3002\u201c\u6768\u59d1\u5a18\u4e3a\u4f55\u51fa\u8d70\uff0c\u4e0d\u95ee\u53ef\u77e5\uff0c\u663e\u7136 \u662f\u5bf9\u6211\u6709\u5f88\u6df1\u7684\u8bef\u4f1a\u4e86\u3002\u4e8b\u60c5\u65e2\u662f\u7531\u6211\u800c\u8d77\uff0c\u6211\u5e94\u8be5\u627e\u7740\u5979\uff0c\u5411\u5979\u8868\u660e\u5fc3\u8ff9\u624d\u5bf9\u3002\u201d\u5b5f\u660e\u971e \u5fc3\u60f3\u3002 \u3000\u3000\u5c60\u51e4\u89c1\u5b5f\u660e\u971e\u8dd1\u6765\u627e\u5979\uff0c\u795e\u8272\u6709\u5f02\uff0c\u4e0d\u89c9\u6014\u4e86\u4e00\u6014\uff0c\u8bf4\u9053\uff1a\u201c\u971e\u59d0\uff0c\u4f60\u53ef\u662f\u6709\u4ec0\u4e48\u5fc3 \u4e8b\uff1f\u90a3\u4e2a\u5c0f\u55bd\u5175\u662f\u4ec0\u4e48\u4eba\uff0c\u4f60\u77e5\u9053\u4e86\u4e48\uff1f\u201d\u5c60\u51e4\u65e9\u5df2\u77e5\u9053\u5979\u662f\u53bb\u627e\u90a3\u5c0f\u55bd\u5175\u7684\uff0c\u89c1\u5979\u6101\u7709\u4e0d \u5c55\u56de\u6765\uff0c\u53ea\u9053\u6b64\u4e8b\u4e0e\u90a3\u5c0f\u55bd\u5175\u6709\u5173\u3002 \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u90a3\u4e2a\u5c0f\u55bd\u5175\u5c31\u662f\u6768\u5a49\u3002\u201d \u3000\u3000\u5c60\u51e4\u4e5f\u662f\u5927\u4e3a\u60ca\u6115\uff0c\u8bf4\u9053\uff1a\u201c\u771f\u7684\u5417\uff1f\u53ef\u771f\u662f\u610f\u60f3\u4e0d\u5230\uff01\u201d \u3000\u3000\u5b5f\u660e\u971e\u8bf4\u4e86\u4e8b\u60c5\u7684\u7ecf\u8fc7\uff0c\u8bf4\u9053\uff1a\u201c\u6240\u4ee5\u73b0\u5728\u6211\u8981\u5411\u4f60\u8f9e\u884c\u4e86\u3002\u5c71\u5be8\u91cc\u6709\u77f3\u5e08\u54e5\u548c\u5b8b\u94c1\u8f6e \u592b\u5987\u7b49\u8f85\u52a9\u4f60\uff0c\u5c11\u6211\u4e00\u4e2a\u4eba\u6599\u60f3\u4e5f\u662f\u65e0\u5173\u7d27\u8981\u3002\u201d \u3000\u3000\u5c60\u51e4\u4e0d\u89c9\u53c8\u662f\u4e00\u6115\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8981\u53bb\u627e\u6768\u5a49\uff1f\u201d\u5b5f\u660e\u971e\u70b9\u4e86\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u53ea\u6709\u5982\u6b64\uff0c \u624d\u80fd\u4f7f\u5f97\u4ed6\u4eec\u592b\u59bb\u7834\u955c\u91cd\u5706\u3002\u201d \u3000\u3000\u5c60\u51e4\u9053\uff1a\u201c\u971e\u59d0\uff0c\u4f60\u2026\u2026\u5509\uff0c\u4e8b\u60c5\u867d\u7136\u662f\u5e94\u8be5\u8fd9\u6837\u505a\uff0c\u4f46\u8fd9\u53ef\u96be\u4e3a\u4f60\u4e86\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u67f3\u7709\u5fae\u8e59\uff0c\u5ffd\u5730\u82e6\u7b11\u9053\uff1a\u201c\u4f60\u4e5f\u50cf\u77f3\u749e\u4e00\u6837\uff0c\u4ee5\u4e3a\u6211\u662f\u7231\u4e0a\u4e86\u674e\u601d\u5357\uff1f\u201d \u3000\u3000\u5c60\u51e4\u5bf9\u5979\u8bf4\u9053\uff1a\u201c\u5927\u5bb6\u90fd\u4ee5\u4e3a\u6768\u5a49\u5df2\u7ecf\u6b7b\u4e86\uff0c\u4f60\u5c31\u662f\u7231\u4e0a\u4e86\u674e\u601d\u5357\uff0c\u90a3\u4e5f\u4e0d\u80fd\u602a\u4f60 \u7684\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u6211\u76f8\u77e5\u591a\u5e74\uff0c\u8fde\u4f60\u4e5f\u5bf9\u6211\u6709\u6240\u8bef\u4f1a\uff0c\u90a3\u5c31\u5f53\u771f\u662f\u602a\u4e0d\u5f97\u6768 \u5a49\u4e86\u3002\u201d \u3000\u3000\u5c60\u51e4\u77e5\u9053\u5b5f\u660e\u971e\u4ece\u6765\u4e0d\u8bf4\u8c0e\u8bdd\uff0c\u4e0d\u89c9\u6709\u70b9\u5947\u602a\uff0c\u8bf4\u9053\uff1a\u201c\u5bf9\u4e0d\u4f4f\uff0c\u6211\u4ee5\u4e3a\u4f60\u548c\u674e\u601d\u5357\u6027 \u60c5\u76f8\u6295\uff0c\u4f60\u4f1a\u559c\u6b22\u4ed6\u7684\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u4e5f\u66fe\u81ea\u5df1\u95ee\u8fc7\u81ea\u5df1\uff1a\u6211\u662f\u4e0d\u662f\u559c\u6b22\u674e\u601d\u5357\uff1f\u6211\u662f\u559c\u6b22\u4ed6\u7684\u3002\u4f46\u8fd9 \u4e2a\u2018\u559c\u6b22\u2019\u5374\u548c\u4f60\u559c\u6b22\u77f3\u749e\u7684\u90a3\u79cd\u2018\u559c\u6b22\u2019\u4e0d\u540c\uff0c\u6211\u662f\u628a\u4ed6\u5f53\u4f5c\u4e00\u4e2a\u597d\u670b\u53cb\u770b\u5f85\u7684\uff0c\u4f60\u660e\u767d \u5417\uff1f\u201d \u3000\u3000\u5b5f\u660e\u971e\u662f\u4e2a\u6027\u683c\u723d\u6717\u7684\u5973\u5b50\uff0c\u5979\u6240\u8bf4\u7684\u4e5f\u7684\u786e\u662f\u7531\u8877\u4e4b\u8a00\u3002\u5979\u5bf9\u674e\u601d\u5357\u5f88\u6709\u597d\u611f\uff0c\u5047\u5982 \u6ca1\u6709\u6768\u5a49\uff0c\u8bf4\u4e0d\u5b9a\u4ed6\u4eec\u7684\u611f\u60c5\u4f1a\u53d1\u5c55\u6210\u4e3a\u7231\u4fa3\uff0c\u4f46\u5728\u76ee\u524d\uff0c\u5374\u8fd8\u53ea\u662f\u505c\u7559\u5728\u77e5\u5df1\u7684\u9636\u6bb5\u3002 \u3000\u3000\u5c60\u51e4\u7b11\u9053\uff1a\u201c\u597d\uff0c\u90a3\u6211\u5c31\u653e\u5fc3\u4e86\u3002\u201d\u53c8\u9053\uff1a\u201c\u8fd9\u4f4d\u573a\u59d1\u5a18\u4e5f\u662f\u6709\u70b9\u5c0f\u5fc3\u773c\u513f\uff0c\u4f55\u5fc5\u8eb2\u7740 \u54b1\u4eec\u5462\uff1f\u5176\u5b9e\u53ea\u8981\u5979\u80af\u9732\u9762\uff0c\u5373\u4f7f\u6709\u4ec0\u4e48\u8bef\u4f1a\uff0c\u90a3\u4e5f\u662f\u5f88\u5feb\u5c31\u53ef\u4ee5\u5f04\u5f97\u6e05\u695a\u7684\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u7b11\u9053\uff1a\u201c\u8fd9\u53ef\u602a\u4e0d\u5f97\u6768\u5a49\uff0c\u5979\u7684\u51fa\u8eab\u548c\u54b1\u4eec\u4e0d\u540c\uff0c\u60f3\u6cd5\u600e\u4f1a\u4e00\u6837\uff1f\u5979\u662f\u540d\u95e8\u95fa \u79c0\uff0c\u81ea\u662f\u96be\u514d\u6709\u70b9\u77dc\u6301\u3002\u50cf\u54b1\u4eec\u8fd9\u6837\u60f3\u8bf4\u5c31\u8bf4\u60f3\u505a\u5c31\u505a\u7684\u8349\u83bd\u7ea2\u5986\u3002\u53ea\u6015\u5728\u6768\u5a49\u773c\u4e2d\u770b\u6765\uff0c \u624d\u5f53\u771f\u662f\u4e0d\u50cf\u4e2a\u5973\u5b69\u513f\u5bb6\u5462\uff01\u201d \u3000\u3000\u5c60\u51e4\u7b11\u9053\uff1a\u201c\u8bdd\u8bf4\u5f97\u4e0d\u9519\uff0c\u53ef\u6211\u5c31\u662f\u8ba8\u538c\u4e00\u822c\u5973\u5b69\u513f\u5bb6\u8fd9\u80a1\u626d\u626d\u634f\u634f\u7684\u5473\u513f\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u53c8\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u6768\u5a49\u4e5f\u662f\u5b9e\u5728\u53ef\u601c\uff0c\u5979\u52ab\u540e\u4f59\u751f\uff0c\u65e9\u5df2\u662f\u65e0\u4f9d\u65e0\u9760\uff0c\u552f\u4e00\u7684\u4eb2 \u4eba\uff0c\u5c31\u53ea\u6709\u4e00\u4e2a\u674e\u601d\u5357\u4e86\uff0c\u8fd9\u4e5f\u96be\u602a\u5979\u60a3\u5f97\u60a3\u5931\uff0c\u591a\u7591\u5584\u5992\u4e86\u3002\u201d \u3000\u3000\u5c60\u51e4\u9053\uff1a\u201c\u971e\u59d0\uff0c\u6211\u6700\u4f69\u670d\u4f60\u7684\u5c31\u662f\u8fd9\u4e00\u70b9\uff0c\u4f60\u5904\u5904\u90fd\u80fd\u591f\u4e3a\u522b\u4eba\u7740\u60f3\u3002\u597d\u5427\uff0c\u5c71\u5be8\u4e4b \u4e8b\u4f60\u4e0d\u5fc5\u6302\u5fc3\uff0c\u4f46\u613f\u4f60\u627e\u5230\u90a3\u4f4d\u6768\u5927\u5c0f\u59d0\u3002\u201d \u3000\u3000\u7b2c\u4e8c\u65e5\u4e00\u65e9\uff0c\u5b5f\u660e\u971e\u4fbf\u5373\u4e0b\u5c71\uff0c\u5979\u5bf9\u6768\u5a49\u7684\u5fc3\u601d\u6478\u5f97\u5f88\u900f\uff0c\u6599\u60f3\u5979\u65e0\u5904\u53ef\u53bb\uff0c\u4e00\u5b9a\u662f\u6697 \u4e2d\u8ddf\u8e2a\u674e\u601d\u5357\u3002\u4e8e\u662f\u5b5f\u660e\u971e\u5f84\u81ea\u524d\u5f80\u98de\u9f99\u5c71\uff0c\u4e00\u8def\u4e0a\u6253\u542c\u6709\u6ca1\u6709\u90a3\u4e48\u4e00\u4e2a\u76f8\u8c8c\u4e11\u964b\u7684\u201c\u5c0f\u55bd \u5175\u201d\u7ecf\u8fc7\u3002 \u3000\u3000\u8fd9\u4e00\u65e5\uff0c\u5230\u4e86\u4e00\u4e2a\u53bf\u57ce\uff0c\u5929\u8272\u5df2\u665a\uff0c\u5b5f\u660e\u971e\u5c31\u8fdb\u57ce\u4e2d\u6295\u5bbf\u3002 \u3000\u3000\u4e0d\u6599\u627e\u4e86\u51e0\u5bb6\u5ba2\u5e97\uff0c\u90fd\u8bf4\u6ca1\u6709\u623f\u95f4\u3002\u5b5f\u660e\u971e\u597d\u751f\u5947\u602a\uff0c\u5728\u8fd9\u5175\u8352\u9a6c\u4e71\u7684\u5e74\u5934\uff0c\u6765\u5f80\u7684\u5ba2 \u5546\u6781\u5c11\uff0c\u8fd9\u4e2a\u53bf\u57ce\u53c8\u4e0d\u662f\u4e2a\u4ea4\u901a\u8981\u9053\uff0c\u54ea\u6709\u95f4\u95f4\u5ba2\u5e97\u90fd\u662f\u5ba2\u6ee1\u7684\u9053\u7406\uff1f\u5b5f\u660e\u971e\u5fc3\u60f3\uff1a\u201c\u96be\u9053 \u56e0\u4e3a\u6211\u662f\u4e2a\u5e74\u8f7b\u5973\u5b50\uff0c\u4ed6\u4eec\u4e0d\u6562\u6536\u7559\u6211\u4e48\uff1f\u201d\u53ef\u662f\u5317\u65b9\u91d1\u56fd\u6cbb\u4e0b\u7684\u98ce\u4fd7\u548c\u5357\u65b9\u4e0d\u540c\uff0c\u5317\u65b9\u7684 \u5973\u5b50\u548c\u7537\u5b50\u5e76\u65e0\u592a\u5927\u7684\u5206\u522b\uff0c\u72ec\u81ea\u51fa\u95e8\u7684\u5973\u5b50\u867d\u4e0d\u5982\u7537\u5b50\u4e4b\u591a\uff0c\u5374\u4e5f\u662f\u53f8\u7a7a\u89c1\u60ef\u7684\u3002 \u3000\u3000\u5b5f\u660e\u971e\u627e\u5230\u6700\u540e\u4e00\u95f4\u5ba2\u5e97\uff0c\u660e\u660e\u770b\u89c1\u5e97\u4e2d\u51b7\u51b7\u6e05\u6e05\uff0c\u4f19\u8ba1\u90fd\u5728\u95f2\u7740\u6ca1\u4e8b\u60c5\u505a\uff0c\u5728\u5e10\u623f\u91cc \u8d4c\u94b1\u73a9\u513f\uff0c\u4f46\u90a3\u638c\u67dc\u7684\u4ecd\u7136\u662f\u677f\u7740\u8138\u5b54\u9053\uff1a\u201c\u6ca1\u623f\u95f4\uff01\u201d \u3000\u3000\u5b5f\u660e\u971e\u4e0d\u7531\u5f97\u751f\u6c14\u9053\uff1a\u201c\u4f60\u662f\u6015\u6211\u6ca1\u623f\u94b1\u7ed9\u4f60\u5462\uff0c\u8fd8\u662f\u6015\u6211\u6765\u5386\u4e0d\u660e\uff1f\u4f60\u7684\u5e97\u5b50\u5206\u660e\u6ca1 \u6709\u5ba2\u4eba\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u80af\u79df\u7ed9\u6211\u4f4f\uff1f\u6211\u53ef\u4ee5\u5148\u4ea4\u623f\u94b1\u7ed9\u4f60\uff0c\u6709\u4ec0\u4e48\u9ebb\u70e6\uff0c\u4e5f\u7531\u6211\u72ec\u81ea\u627f\u62c5\u5c31 \u662f\u3002\u201d \u3000\u3000\u90a3\u638c\u67dc\u7684\u8bf4\u9053\uff1a\u201c\u591a\u5c11\u623f\u94b1\u4e5f\u4e0d\u79df\uff0c\u6765\u5386\u6e05\u767d\u4e5f\u4e0d\u79df\uff01\u201d\u5b5f\u660e\u971e\u5fcd\u7740\u4e86\u6c14\u9053\uff1a\u201c\u4e3a\u4ec0 \u4e48\uff1f\u201d\u90a3\u638c\u67dc\u7684\u9053\uff1a\u201c\u4e0d\u4e3a\u4ec0\u4e48\uff0c\u662f\u5973\u7684\u5c31\u4e0d\u79df\uff01\u201d \u3000\u3000\u5b5f\u660e\u971e\u51e0\u4e4e\u5fcd\u4e0d\u4f4f\u5c31\u8981\u53d1\u4f5c\uff0c\u4f46\u8f6c\u5ff5\u4e00\u60f3\uff1a\u201c\u4f55\u5fc5\u548c\u5c0f\u4eba\u4e00\u822c\u89c1\u8bc6\uff1f\u201d\u518d\u60f3\uff1a\u201c\u6211\u4e00\u8def \u6765\uff0c\u4e5f\u66fe\u6295\u5bbf\u8fc7\u51e0\u5bb6\u5ba2\u5e97\uff0c\u4f55\u4ee5\u552f\u72ec\u8fd9\u4e2a\u53bf\u57ce\u7684\u5ba2\u5e97\u4e0d\u80af\u6536\u5bb9\u5973\u5ba2\uff0c\u60f3\u5fc5\u5176\u4e2d\u8d70\u6709\u7f18\u6545\u3002\u770b \u6765\u4ed6\u4eec\u4e43\u662f\u987e\u5fcc\u4ec0\u4e48\uff0c\u4e0d\u80af\u5bf9\u6211\u8bf4\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u597d\u5947\u5fc3\u8d77\uff0c\u79bb\u5f00\u4e86\u90a3\u5bb6\u5ba2\u5e97\u4e4b\u540e\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u5ba2\u5e97\u4e0d\u80af\u6536\u5bb9\uff0c\u6211\u4e14\u5148\u5403\u9971\u4e86\u809a \u5b50\u518d\u8bf4\u3002\u9152\u5e97\u95f2\u4eba\u4f17\u591a\uff0c\u8bf4\u4e0d\u5b9a\u53ef\u4ee5\u6253\u542c\u5230\u5176\u4e2d\u7f18\u6545\u3002\u201d \u3000\u3000\u4e0d\u6599\u4e0a\u4e86\u4e00\u5bb6\u9152\u697c\uff0c\u90a3\u638c\u67dc\u7684\u4e5f\u9053\uff1a\u201c\u5bf9\u4e0d\u4f4f\uff0c\u6211\u4eec\u8fd9\u513f\u4e0d\u62db\u5f85\u5973\u5ba2\uff01\u201d \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u906d\u4eba\u767d\u773c\u7f18\u4f55\u6545\uff0c\u5c82\u56e0\u751f\u662f\u5973\u513f\u8eab\uff1f \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u4e03\u56de\u3000\u4eba\u95f4\u70df\u70df\u77e5\u4f55\u9650\u3000\u5fc3\u5e95\u6ce2\u6d9b\u6216\u66f4\u6df1 \u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b \u7b2c\u4e8c\u5341\u4e03\u56de\u3000\u4eba\u95f4\u70df\u70df\u77e5\u4f55\u9650\u3000\u5fc3\u5e95\u6ce2\u6d9b\u6216\u66f4\u6df1 \u3000\u3000\u5b5f\u660e\u971e\u4e0d\u7531\u5f97\u6c14\u4e0a\u5fc3\u5934\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u54ea\u95e8\u5b50\u7684\u89c4\u77e9\uff1f\u6211\u8d70\u8fc7\u7684\u5730\u65b9\u4e5f\u4e0d\u7b97\u5c11\uff0c\u4ece \u6765\u6ca1\u542c\u8bf4\u8fc7\u5f00\u996d\u5e97\u7684\u4e0d\u8bb8\u5973\u5ba2\u8fdb\uff01\u201d \u3000\u3000\u638c\u67dc\u7684\u9053\uff1a\u201c\u522b\u5904\u6ca1\u6709\u8fd9\u6837\u7684\u89c4\u77e9\uff0c\u6211\u4eec\u8fd9\u513f\u5c31\u6709\uff01\u4f60\u4e0d\u76f8\u4fe1\uff0c\u5c3d\u53ef\u4ee5\u5230\u522b\u5bb6\u53bb\uff01\u201d \u3000\u3000\u9152\u697c\u4e0a\u7684\u4e24\u4e2a\u5ba2\u4eba\u505a\u597d\u505a\u6b79\u5730\u529d\u9053\uff1a\u201c\u5927\u59d0\uff0c\u505a\u4e70\u5356\u662f\u4e24\u76f8\u60c5\u613f\u7684\u4e8b\uff0c\u4ed6\u4e0d\u613f\u610f\u5356\u4e1c\u897f \u7ed9\u4f60\uff0c\u90a3\u4e5f\u662f\u4e0d\u80fd\u52c9\u5f3a\u4ed6\u7684\u3002\u201d \u3000\u3000\u53e6\u4e00\u4e2a\u8bf4\u5f97\u66f4\u5e72\u8106\uff1a\u201c\u4f60\u770b\u8fd9\u9152\u697c\u4e0a\u54ea\u6709\u5973\u5ba2\u4eba\uff0c\u4f60\u522b\u5728\u8fd9\u513f\u60f9\u4e8b\u4e86\uff01\u201d\u770b\u8fd9\u60c5\u5f62\uff0c\u5979 \u5982\u679c\u4e0d\u8d70\u7684\u8bdd\uff0c\u4e0d\u5f85\u638c\u67dc\u7684\u5f00\u58f0\uff0c\u8fd9\u4f19\u5ba2\u4eba\u5c31\u8981\u66ff\u638c\u67dc\u7684\u4e0b\u9010\u5ba2\u4ee4\u4e86\u3002 \u3000\u3000\u5b5f\u660e\u971e\u4e0d\u613f\u81ea\u8ba8\u6ca1\u8da3\uff0c\u53ea\u597d\u8d70\u51fa\u8fd9\u5bb6\u9152\u5bb6\uff0c\u8d8a\u60f3\u8d8a\u662f\u5947\u602a\uff1a\u201c\u60f9\u4e8b\uff1f\u4ed6\u4eec\u6015\u6211\u60f9\u4ec0\u4e48 \u4e8b\uff1f\u96be\u9053\u4ed6\u4eec\u77e5\u9053\u6211\u662f\u5728\u7eff\u6797\u4e2d\u53ae\u6df7\u7684\u5973\u5f3a\u76d7\u4e0d\u6210\uff1f\u7edd\u6ca1\u6709\u8fd9\u4e2a\u9053\u7406\uff01\u54fc\uff0c\u6211\u4e00\u5b9a\u8981\u6253\u542c\u4e2a \u6e05\u695a\u3002\u201d\u53ef\u662f\u5411\u8c01\u6253\u542c\u5462\uff0c\u9152\u697c\u5ba2\u5e97\uff0c\u5bf9\u5973\u5ba2\u4eba\u90fd\u662f\u907f\u82e5\u86c7\u874e\uff0c\u8fd9\u771f\u662f\u5b5f\u660e\u971e\u4ece\u6240\u672a\u6709\u7684 \u201c\u5947\u9047\u201d\uff0c\u5b5f\u660e\u971e\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5012\u9709\uff0c\u83ab\u4e0d\u6210\u4eca\u665a\u7adf\u8981\u997f\u7740\u809a\u76ae\u8fc7\u4e00\u665a\u4e48\uff1f\u201d \u3000\u3000\u5b5f\u660e\u971e\u6b63\u81ea\u4f4e\u9996\u601d\u91cf\u4eca\u665a\u5982\u4f55\u662f\u597d\uff0c\u5ffd\u542c\u5f97\u6709\u4eba\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u522b\u6c14\u607c\u3002\u4f60\u5728\u57ce \u4e2d\u53ef\u6709\u670b\u53cb\u4e48\uff1f\u201d \u3000\u3000\u5b5f\u660e\u971e\u56de\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u4e00\u4e2a\u8001\u6c49\u8d70\u5230\u5979\u7684\u8eab\u8fb9\uff0c\u795e\u8272\u4f3c\u4e4e\u6709\u70b9\u614c\u5f20\uff0c\u4f46\u8bf4\u8bdd\u7684\u53e3\u543b\uff0c\u5374 \u662f\u5341\u5206\u540c\u60c5\u5979\u7684\u3002\u5b5f\u660e\u971e\u8ba4\u5f97\u8fd9\u8001\u4eba\u662f\u521a\u624d\u5728\u90a3\u9152\u697c\u4e0a\u7684\u4e00\u4e2a\u5ba2\u4eba\u3002 \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u6211\u82e5\u662f\u6709\u4eb2\u53cb\u5728\u6b64\uff0c\u4e5f\u7528\u4e0d\u7740\u4e0a\u9152\u697c\u5403\u996d\u4e86\u3002\u201d \u3000\u3000\u90a3\u8001\u6c49\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u4f55\u4ee5\u5355\u8eab\u4e00\u4eba\u5230\u6b64\uff1f\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u6211\u662f\u524d\u5f80\u7075\u6b66\u6295\u4eb2\uff0c\u8def\u7ecf\u6b64\u5730\u7684\u3002\u201d\u7075\u6b66\u5728\u98de\u9f99\u5c71\u9644\u8fd1\uff0c\u5b5f\u660e\u971e\u4e0d\u60ef\u8bf4 \u614c\uff0c\u6025\u5207\u95f4\u60f3\u4e0d\u5230\u522b\u7684\u5408\u9002\u5730\u540d\uff0c\u4fe1\u53e3\u5c31\u8bf4\u4e86\u51fa\u6765\u3002 \u3000\u3000\u90a3\u8001\u6c49\u9053\uff1a\u201c\u5509\uff0c\u6211\u529d\u4f60\u8fd8\u662f\u522b\u5f80\u524d\u8d70\u4e86\u3002\u201d\u5b5f\u660e\u971e\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u90a3\u8001\u6c49\u9053\uff1a\u201c\u8fd9 \u91cc\u4e0d\u65b9\u4fbf\u8bf4\u8bdd\uff0c\u4f60\u4eca\u665a\u5927\u7ea6\u8fd8\u6ca1\u6709\u4f4f\u5904\u5427\uff1f\u201d\u5b5f\u660e\u971e\u9053\uff1a\u201c\u6b63\u662f\uff0c\u5ba2\u5e97\u90fd\u4e0d\u80af\u6536\u7559\u6211\u3002\u201d\u90a3 \u8001\u6c49\u9053\uff1a\u201c\u53ef\u601c\uff0c\u53ef\u601c\uff01\u8fd9\u4e48\u665a\u4e86\uff0c\u4f60\u4e5f\u4e0d\u4fbf\u51fa\u57ce\u53bb\u627e\u5730\u65b9\u6c42\u5bbf\u4e86\u3002\u4e0d\u5982\u5c31\u5230\u8001\u6c49\u7684\u5bb6\u91cc\u53bb \u5427\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u5ba2\u5e97\u4e0d\u80af\u6536\u7559\u6211\uff0c\u5b9a\u6709\u539f\u56e0\u3002\u4f60\u4e0d\u6015\u6211\u8fde\u7d2f\u4f60\u5417\uff1f\u201d\u90a3\u8001\u6c49\u9053\uff1a\u201c\u6211\u53ea\u6709 \u4e00\u4e2a\u8001\u4f34\u513f\uff0c\u90fd\u662f\u4e00\u5927\u628a\u5e74\u7eaa\u4e86\u3002\u201d\u8a00\u4e0b\u4e4b\u610f\uff0c\u5373\u4f7f\u53d7\u5230\u8fde\u7d2f\uff0c\u4ed6\u4e5f\u4e0d\u5728\u4e4e\u3002 \u3000\u3000\u5b5f\u660e\u971e\u662f\u723d\u76f4\u7684\u813e\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u4f60\u65e2\u7136\u4e0d\u6015\uff0c\u90a3\u6211\u4e5f\u5c31\u4e0d\u5ba2\u6c14\u6253\u6270\u4f60\u4e86\u3002\u201d \u3000\u3000\u5f53\u4e0b\u90a3\u8001\u6c49\u5728\u524d\u9886\u8def\uff0c\u7a7f\u8fc7\u51e0\u6761\u5f2f\u5f2f\u66f2\u66f2\u7684\u5c0f\u5df7\uff0c\u628a\u5b5f\u660e\u971e\u5e26\u56de\u4ed6\u7684\u5bb6\u4e2d\u3002\u5b5f\u660e\u971e\u5fc3\u91cc \u60f3\u9053\uff1a\u201c\u8fd9\u8001\u5934\u513f\u770b\u4f3c\u8001\u6001\u9f99\u949f\uff0c\u6b65\u5c65\u5012\u662f\u751a\u4e3a\u77eb\u6377\u3002\u201d \u3000\u3000\u90a3\u8001\u6c49\u5173\u4e0a\u5927\u95e8\uff0c\u53eb\u9053\uff1a\u201c\u8001\u4f34\u513f\uff0c\u6709\u5ba2\u4eba\u6765\u5566\u3002\u201d\u4e00\u4e2a\u8001\u5a46\u5a46\u8d70\u4e86\u51fa\u6765\uff0c\u89c1\u4e86\u5b5f\u660e \u971e\uff0c\u4e0d\u89c9\u597d\u751f\u60ca\u5f02\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u59d1\u5a18\u662f\u2014\u2014\u201d \u3000\u3000\u90a3\u8001\u6c49\u9053\uff1a\u201c\u5bf9\u5566\uff0c\u6211\u8fd8\u6ca1\u6709\u8bf7\u6559\u59d1\u5a18\u8d35\u59d3\u82b3\u540d\u3002\u201d\u5b5f\u660e\u971e\u62a5\u4e86\u59d3\u540d\uff0c\u90a3\u8001\u6c49\u9053\uff1a\u201c\u8fd9 \u4f4d\u5b5f\u59d1\u5a18\u662f\u524d\u5f80\u7075\u6b66\u6295\u4eb2\u7684\uff0c\u8def\u7ecf\u6b64\u5730\uff0c\u65e0\u5904\u53ef\u4ee5\u6295\u5bbf\u3002\u201d\u90a3\u8001\u5a46\u5a46\u9053\uff1a\u201c\u8fd9\u4e24\u65e5\u98ce\u58f0\u6b63 \u7d27\uff0c\u4f60\u628a\u8fd9\u6837\u6807\u81f4\u7684\u59d1\u5a18\u5e26\u56de\u5bb6\u91cc\uff0c\u82e5\u662f\u51fa\u4e86\u5c94\u5b50\uff0c\u54b1\u4eec\u53ef\u62c5\u5f53\u4e0d\u8d77\u3002\u201d\u5df4 \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u8001\u4e08\u4e00\u7247\u597d\u5fc3\uff0c\u6211\u662f\u6781\u4e3a\u611f\u6fc0\uff0c\u4f46\u82e5\u8fde\u7d2f\u4e86\u4f60\u4eec\uff0c\u6211\u5374\u8fc7\u610f\u4e0d\u53bb\u3002\u6211\u5f80\u522b \u5904\u5c31\u662f\u3002\u201d \u3000\u3000\u90a3\u8001\u5a46\u5a46\u5fd9\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u8bef\u4f1a\u4e86\uff0c\u6211\u4eec\u4e0d\u662f\u4e0d\u80af\u6536\u7559\u4f60\uff0c\u53ea\u662f\u6015\u4f60\u51fa\u4e86\u4e8b\u60c5\uff0c\u6211\u4eec\u53ef \u5bf9\u4f60\u4e0d\u8d77\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u82e5\u662f\u8fd9\u6837\uff0c\u90a3\u4f60\u5012\u4e0d\u7528\u66ff\u6211\u62c5\u5fc3\u3002\u4f46\u4e0d\u77e5\u5a46\u5a46\u62c5\u5fc3\u7684\u662f\u4ec0\u4e48\u4e8b\u60c5\uff1f\u201d \u3000\u3000\u90a3\u8001\u5a46\u5a46\u9053\uff1a\u201c\u6b64\u4e8b\u8bf4\u6765\u8bdd\u957f\u2026\u2026\u201d\u90a3\u8001\u6c49\u9053\uff1a\u201c\u8fd9\u4f4d\u5b5f\u59d1\u5a18\u8fd8\u6ca1\u6709\u5403\u665a\u996d\u5462\uff0c\u4f60\u5148\u7ed9 \u5979\u5f04\u70b9\u4e1c\u897f\u5403\u5427\u3002\u8fd9\u6869\u4e8b\u60c5\uff0c\u6211\u548c\u5979\u8bf4\u597d\u4e86\u3002\u201d \u3000\u3000\u90a3\u8001\u5a46\u5a46\u9053\uff1a\u201c\u4e0d\u6015\u5b5f\u59d1\u5a18\u89c1\u7b11\uff0c\u5bb6\u91cc\u53ea\u6709\u51e0\u65a4\u7c97\u9762\uff0c\u6211\u7ed9\u4f60\u6253\u4e24\u53ea\u9e21\u86cb\u4e0b\u7897\u9762\u513f\uff0c\u4f60 \u53ef\u522b\u5acc\u7b80\u6162\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u5411\u8fd9\u5bf9\u8001\u592b\u59bb\u91cd\u65b0\u89c1\u8fc7\u793c\uff0c\u8bf4\u9053\uff1a\u201c\u5a46\u5a46\u4e0d\u8981\u5ba2\u6c14\uff0c\u6211\u5728\u8fd9\u57ce\u671b\u6709\u94b1\u4e5f\u4e70\u4e0d\u5230\u4e1c \u897f\u5403\uff0c\u4f46\u6c42\u4e00\u9971\uff0c\u5df2\u662f\u611f\u6069\u4e0d\u6d45\u3002\u201d \u3000\u3000\u90a3\u8001\u6c49\u9053\uff1a\u201c\u6211\u59d3\u8d3a\uff0c\u6392\u884c\u7b2c\u4e5d\uff0c\u6ca1\u513f\u6ca1\u5973\uff0c\u59d1\u5a18\uff0c\u8bf7\u4f60\u6055\u6211\u501a\u8001\u5356\u8001\uff0c\u4f60\u5230\u4e86\u6211\u7684\u5bb6 \u91cc\uff0c\u6211\u5c31\u628a\u4f60\u5f53\u4f5c\u5b59\u5973\u513f\u4e00\u6837\u770b\u5f85\uff0c\u6211\u4e0d\u5ba2\u6c14\uff0c\u4f60\u4e5f\u4e0d\u5fc5\u5ba2\u6c14\u4e86\u3002\u201d \u3000\u3000\u90a3\u8001\u5a46\u5a46\u8fdb\u53bb\u4e4b\u540e\uff0c\u5b5f\u660e\u971e\u9053\uff1a\u201c\u4e5d\u516c\uff0c\u5ba2\u5e97\u9152\u697c\uff0c\u6cb3\u4ee5\u4e0d\u80af\u62db\u5f85\u5973\u5ba2\uff1f\u8bf7\u4f60\u8001\u4eba\u5bb6\u660e \u767d\u89c1\u544a\u3002\u201d \u3000\u3000\u8d3a\u4e5d\u516c\u9053\uff1a\u201c\u56e0\u4e3a\u53bf\u91cc\u51fa\u4e86\u4e2a\u91c7\u82b1\u8d3c\u3002\u201d \u3000\u3000\u6b64\u8a00\u4e00\u51fa\u5012\u662f\u5927\u51fa\u5b5f\u660e\u971e\u610f\u6599\u4e4b\u5916\uff0c\u95ee\u9053\uff1a\u201c\u4e00\u4e2a\u91c7\u82b1\u8d3c\uff0c\u600e\u7684\u5c31\u95f9\u5f97\u5982\u6b64\u7316\u72c2\uff0c\u4eba\u4eba \u90fd\u6015\u4e86\u4ed6\uff01\u8fde\u5ba2\u5e97\u9152\u697c\uff0c\u90fd\u4e0d\u6562\u505a\u5973\u5ba2\u7684\u751f\u610f\u4e86\uff1f\u201d \u3000\u3000\u8d3a\u4e5d\u516c\u9053\uff1a\u201c\u5509\uff0c\u5b5f\u59d1\u5a18\u4f60\u4e0d\u77e5\u9053\uff0c\u8fd9\u91c7\u82b1\u8d3c\u53ef\u662f\u5fc3\u5f88\u624b\u8fa3\uff0c\u51f6\u5f97\u5f88\u5440\uff01\u636e\u8bf4\u4ed6\u5728\u522b\u5904 \u5df2\u7ecf\u505a\u4e86\u8bb8\u591a\u6848\u5b50\uff0c\u6ca1\u4eba\u80fd\u591f\u5236\u670d\u4ed6\uff0c\u6700\u8fd1\u624d\u95f9\u5230\u6211\u4eec\u8fd9\u4e2a\u53bf\u91cc\u6765\u7684\uff0c\u59d1\u5a18\uff0c\u6211\u53ea\u8bf4\u6211\u4eec\u53bf \u91cc\u7684\u4e24\u4e2a\u6848\u5b50\u7ed9\u4f60\u542c\u5427\u3002\u201d \u3000\u3000\u8d3a\u4e5d\u516c\u559d\u4e86\u53e3\u8336\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u53bf\u91cc\u6709\u4e2a\u59d3\u5b59\u7684\u5927\u6237\uff0c\u5bb6\u8d22\u4e07\u8d2f\uff0c\u5bb6\u4e2d\u7684\u62a4\u9662\u5c31\u6709\u516b\u4eba \u4e4b\u591a\uff0c\u542c\u8bf4\u90fd\u662f\u4ece\u5404\u5730\u91cd\u91d1\u8058\u8bf7\u6765\u7684\u540d\u6b66\u5e08\u3002 \u3000\u3000\u201c\u5b59\u5927\u6237\u6709\u4e2a\u72ec\u751f\u7684\u5973\u513f\uff0c\u5e74\u65b9\u5341\u516b\uff0c\u5df2\u7ecf\u8bb8\u914d\u4eba\u5bb6\uff0c\u5c31\u8981\u51fa\u5ac1\u7684\u4e86\u3002\u90bb\u53bf\u51fa\u73b0\u91c7\u82b1\u8d3c \u7684\u98ce\u58f0\uff0c\u5f00\u59cb\u4f20\u6765\uff0c\u4e00\u822c\u8001\u767e\u59d3\u8fd8\u672a\u77e5\u9053\uff0c\u5b59\u5927\u6237\u6d88\u606f\u7075\u901a\uff0c\u542c\u5f97\u6b64\u4e8b\uff0c\u5f53\u7136\u662f\u53eb\u90a3\u4e9b\u62a4\u9662 \u4e25\u5bc6\u6212\u5907\uff0c\u4e0d\u5728\u8bdd\u4e0b\u3002 \u3000\u3000\u201c\u4e0d\u6599\u5c31\u5728\u8fd9\u4f4d\u5b59\u5c0f\u59d0\u51fa\u5ac1\u7684\u524d\u4e09\u5929\u665a\u4e0a\uff0c\u90a3\u4e2a\u91c7\u82b1\u8d3c\u6765\u4e86\uff0c\u516c\u7136\u5c31\u5728\u7ee3\u623f\u4e2d\u903c\u5978\uff0c\u58f0 \u55a7\u6237\u5916\u3002\u516b\u4e2a\u62a4\u9662\u95fb\u58f0\u800c\u81f3\uff0c\u90a3\u91c7\u82b1\u8d3c\u7b11\u9053\uff1a\u2018\u6211\u672c\u6765\u4e0d\u60f3\u62a2\u4eba\u7684\uff0c\u73b0\u5728\u7ed9\u4f60\u4eec\u8d25\u4e86\u6211\u5174\uff0c \u6211\u5374\u504f\u8981\u62a2\u4eba\u4e86\uff01\u2019 \u3000\u3000\u201c\u636e\u8bf4\u8fd8\u4e0d\u5230\u4e00\u76cf\u8336\u7684\u65f6\u523b\uff0c\u548c\u8fd9\u4e2a\u91c7\u82b1\u8d3c\u52a8\u624b\u7684\u62a4\u9662\uff0c\u516b\u4e2a\u4eba\u4e2d\u5c31\u6b7b\u4e86\u516d\u4e2a\uff0c\u53e6\u5916\u4e24 \u4e2a\u4e5f\u53d7\u4e86\u91cd\u4f24\uff0c\u4e00\u4e2a\u65ad\u4e86\u4e00\u6761\u624b\u81c2\uff0c\u4e00\u4e2a\u7ed9\u65ab\u6389\u4e00\u6761\u5927\u817f\u3002 \u3000\u3000\u201c\u8fd9\u91c7\u82b1\u8d3c\u5f53\u65f6\u662f\u62b1\u7740\u5b59\u5927\u6237\u7684\u95fa\u5973\uff0c\u53ea\u7528\u4e00\u53ea\u624b\u5c31\u6740\u4f24\u4e86\u90a3\u516b\u4e2a\u62a4\u9662\u7684\uff0c\u4ed6\u4f24\u4eba\u4e4b \u540e\uff0c\u62b1\u7740\u5b59\u5c0f\u59d0\u4ece\u5c4b\u9876\u9003\u51fa\u53bb\uff0c\u542c\u8bf4\u4ed6\u5728\u74e6\u9762\u884c\u8d70\u5982\u98de\uff0c\u8fde\u4e00\u7247\u74e6\u4e5f\u6ca1\u8e29\u788e\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5927\u6237\u4eba\u5bb6\u7684\u62a4\u9662\uff0c\u5927\u591a\u662f\u76f8\u4e92\u6807\u699c\uff0c\u5f7c\u6b64\u5439\u5618\uff0c\u9a97\u53d6\u94b1\u8d22\u7684\u4e8c\u4e09\u6d41 \u89d2\u8272\uff0c\u672a\u5fc5\u6709\u4ec0\u4e48\u771f\u5b9e\u7684\u672c\u9886\u3002\u4e0d\u8fc7\uff0c\u8fd9\u91c7\u82b1\u8d3c\u62b1\u7740\u4e00\u4e2a\u4eba\uff0c\u5728\u74e6\u9762\u98de\u8dd1\uff0c\u6ca1\u6709\u8e0f\u788e\u4e00\u7247 \u74e6\uff0c\u5982\u679c\u662f\u771f\u7684\u8bdd\uff0c\u8fd9\u4efd\u8f7b\u529f\uff0c\u5012\u4e5f\u662f\u4e0d\u5bb9\u5c0f\u89d1\u3002\u201d \u3000\u3000\u8d3a\u4e5d\u516c\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u7b2c\u4e8c\u4ef6\u6848\u5b50\u53ef\u5c31\u662f\u524d\u5929\u665a\u4e0a\u624d\u53d1\u751f\u7684\u4e8b\u60c5\u4e86\u3002\u8fd9\u6b21\u95f9\u5f97\u66f4\u51f6\uff0c\u95f9\u5230 \u4e86\u57ce\u91cc\u5b88\u5907\u8001\u7237\u7684\u5b98\u8859\u91cc\u3002 \u3000\u3000\u201c\u5b88\u5907\u8001\u7237\u7684\u5ab3\u5987\u4e0d\u80af\u4f9d\u4ece\uff0c\u5927\u58f0\u53eb\u558a\uff0c\u7ed9\u90a3\u91c7\u82b1\u8d3c\u6d3b\u751f\u751f\u7684\u627c\u6b7b\uff01\u5b98\u8859\u91cc\u7684\u536b\u5175\u8bf4\u591a \u4e0d\u591a\uff0c\u8bf4\u5c11\u4e0d\u5c11\uff0c\u4ed6\u603b\u6709\u4e00\u767e\u51e0\u5341\u5427\uff0c\u90fd\u6765\u56f4\u62e2\u4e86\u90a3\u95f4\u623f\u5b50\u3002\u91c7\u82b1\u8d3c\u624b\u821e\u53cc\u5200\u51b2\u51fa\u53bb\uff0c\u4e71\u7bad \u96e8\u4e0b\uff0c\u5374\u6ca1\u4e00\u652f\u7bad\u5c04\u5f97\u7740\u4ed6\uff0c\u53cd\u800c\u662f\u6709\u5341\u591a\u4e2a\u536b\u5175\uff0c\u4f24\u5728\u4ed6\u62d4\u56de\u6765\u7684\u5229\u7bad\u4e4b\u4e0b\u3002\u90a3\u4e48\u591a\u4eba\uff0c \u53ea\u597d\u773c\u7741\u7741\u5730\u770b\u7740\u4ed6\u9003\u8dd1\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u8fd9\u6765\u82b1\u8d3c\u662f\u600e\u4e48\u6837\u7684\u4e00\u4e2a\u4eba\uff1f\u65e2\u7136\u6709\u8bb8\u591a\u4eba\u548c\u4ed6\u5bf9\u8fc7\u76f8\uff0c\u60f3\u5fc5\u4f60\u66fe\u542c\u4eba\u8bf4 \u8fc7\u5427\uff1f\u201d \u3000\u3000\u8d3a\u4e5d\u516c\u9053\uff1a\u201c\u542c\u8bf4\u5927\u7ea6\u662f\u4e00\u4e2a\u4e8c\u5341\u4e94\u516d\u5c81\u5e74\u7eaa\u7684\u5c11\u5e74\uff0c\u8eab\u6750\u9ad8\u9ad8\u7684\uff0c\u7709\u5fc3\u6709\u9897\u9ed1\u75e3\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u81ea\u5e7c\u8ddf\u968f\u7236\u4eb2\u5728\u6c5f\u6e56\u4e0a\u884c\u8d70\uff0c\u6709\u70b9\u540d\u6c14\u7684\u9ed1\u767d\u4e24\u9053\u4eba\u7269\uff0c\u5979\u6ca1\u89c1\u8fc7\u4e5f\u542c\u8bf4\u8fc7\uff0c\u5979 \u6253\u542c\u8fd9\u4e2a\u91c7\u82b1\u8d3c\u7684\u5e74\u7eaa\u3001\u76f8\u8c8c\uff0c\u5c31\u662f\u5e0c\u671b\u53ef\u4ee5\u627e\u5230\u4e00\u70b9\u7ebf\u7d22\u3002\u4e0d\u6599\u542c\u4e86\u8d3a\u4e5d\u516c\u7684\u63cf\u7ed8\uff0c\u5374\u4e0d \u7531\u5f97\u5979\u4e0d\u5927\u611f\u610f\u5916\u3002 \u3000\u3000\u539f\u6765\u8d3a\u4e5d\u516c\u8bf4\u7684\u8fd9\u4e2a\u91c7\u82b1\u8d3c\uff0c\u5f88\u50cf\u8bf4\u7684\u5c31\u662f\u891a\u4e91\u5cf0\uff01\u5b5f\u660e\u971e\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8eab\u6750\u5e74\u7eaa\u6216\u8005 \u8fd8\u6709\u76f8\u4f3c\uff0c\u4f46\u7709\u5fc3\u4e0a\u7684\u4e00\u9897\u9ed1\u75e3\uff0c\u891a\u4e91\u5cf0\u4e5f\u6709\u3002\u5982\u679c\u4e0d\u662f\u4ed6\uff0c\u8fd9\u5c31\u672a\u514d\u592a\u5de7\u5408\u4e86\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u60f3\u4e86\u4e00\u60f3\uff0c\u95ee\u9053\uff1a\u201c\u5b59\u5927\u6237\u5bb6\u90a3\u4ef6\u6848\u5b50\u662f\u54ea\u4e00\u5929\u7684\u4e8b\u60c5\uff1f\u201d\u8d3a\u4e5d\u516c\u9053\uff1a\u201c\u662f\u521d\u4e94 \u90a3\u5929\u665a\u4e0a\u53d1\u751f\u7684\u3002\u201d\u5b5f\u660e\u971e\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u8ddd\u4eca\u4e5f\u4e0d\u8fc7\u53ea\u6709\u4e94\u5929\u3002\u201d\u8d3a\u4e5d\u516c\u9053\uff0c\u201c\u662f\u5440\uff0c\u4e94\u5929 \u4e4b\u5185\uff0c\u8fd9\u91c7\u82b1\u8d3c\u5c31\u505a\u4e86\u4e24\u4ef6\u6848\u5b50\uff0c\u800c\u4e14\u6240\u62a2\u7684\u4eba\u5bb6\u53c8\u662f\u975e\u5bcc\u5373\u8d35\uff0c\u4f60\u8bf4\u7109\u80fd\u4e0d\u4ee4\u5f97\u4eba\u5fc3\u60f6 \u60f6\uff01\u201d \u3000\u3000\u5b5f\u660e\u971e\u6697\u81ea\u5bfb\u601d\uff1a\u201c\u5047\u5b9a\u891a\u4e91\u5cf0\u90a3\u665a\u5df2\u7ecf\u9003\u4e0b\u5c71\u53bb\uff0c\u4ed6\u4e5f\u4e0d\u8fc7\u53ea\u662f\u6bd4\u6211\u5148\u8d70\u4e24\u5929\u3002\u5b88\u5907 \u8859\u95e8\u90a3\u4ef6\u6848\u5b50\uff0c\u6216\u6709\u53ef\u80fd\u662f\u4ed6\u505a\u7684\uff0c\u4f46\u5b59\u5927\u6237\u5bb6\u90a3\u4ef6\u6848\u5b50\uff0c\u5c31\u51b3\u4e0d\u80fd\u662f\u4ed6\u6240\u4e3a\u7684\u4e86\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u7591\u56e2\u6ee1\u8179\uff0c\u53c8\u518d\u95ee\u9053\uff1a\u201c\u4e24\u5b97\u6848\u5b50\u7684\u91c7\u82b1\u8d3c\u662f\u5426\u540c\u4e00\u4e2a\u4eba\uff1f\u201d\u8d3a\u4e5d\u516c\u4f3c\u4e4e\u6709\u70b9\u8be7 \u5f02\uff0c\u8bf4\u9053\uff1a\u201c\u5b5f\u59d1\u8e09\u4f55\u4ee5\u6000\u7591\u4e0d\u662f\u540c\u4e00\u4e2a\u4eba\uff1f\u201d\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u4f4d\u59d1\u5a18\u4e5f\u771f\u80c6\u5927\uff0c\u542c\u4e86\u91c7\u82b1 \u8d3c\u7684\u4e8b\u60c5\uff0c\u5c45\u7136\u6beb\u4e0d\u60ca\u614c\uff0c\u8fd8\u8981\u67e5\u6839\u95ee\u5e95\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u6211\u4e0d\u8fc7\u95ee\u95ee\u800c\u5df2\u3002\u8bf4\u4e0d\u5b9a\u90a3\u91c7\u82b1\u8d3c\u8fd8\u6709\u515a\u7fbd\u5462\uff1f\u201d \u3000\u3000\u8d3a\u4e5d\u516c\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u4e00\u4e2a\u91c7\u82b1\u8d3c\u5df2\u7ecf\u95f9\u5f97\u5929\u7ffb\u5730\u8986\uff0c\u518d\u591a\u4e00\u4e2a\uff0c\u90a3\u8fd8\u5f97\u4e86\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u662f\u540c\u4e00\u4e2a\u4eba\u4e86\uff1f\u201d \u3000\u3000\u8d3a\u4e5d\u516c\u9053\uff1a\u201c\u524d\u5929\u665a\u4e0a\u7684\u6848\u5b50\uff0c\u5b88\u5907\u8859\u95e8\u7684\u536b\u5175\u6709\u6570\u5341\u4eba\u4e4b\u591a\u770b\u89c1\u8fd9\u4e2a\u91c7\u82b1\u8d3c\uff0c\u5bf9\u4ed6\u7684 \u76f8\u8c8c\u81ea\u7136\u662f\u8bf4\u5f97\u6bd4\u8f83\u6e05\u695a\u3002\u5b59\u5927\u6237\u5bb6\u5374\u53ea\u6709\u516b\u4e2a\u62a4\u9662\u548c\u4ed6\u671d\u8fc7\u76f8\uff0c\u5176\u4ed6\u7684\u5bb6\u4eba\uff0c\u5f53\u65f6\u4e0d\u662f\u8eb2 \u8d77\u6765\uff0c\u5c31\u662f\u8d76\u5230\u73b0\u573a\u4e4b\u65f6\uff0c\u90a3\u91c7\u82b1\u8d3c\u5df2\u7ecf\u8d70\u4e86\u3002\u5b59\u5bb6\u7684\u516b\u4e2a\u62a4\u9662\u516d\u6b7b\u4e24\u4f24\uff0c\u91cd\u4f24\u7684\u90a3\u4e24\u4e2a\u60f3 \u6765\u4e00\u5b9a\u662f\u8bf4\u5f97\u4e0d\u6e05\u695a\u7684\u3002\u6240\u4ee5\u4f60\u95ee\u6211\u662f\u4e0d\u662f\u540c\u4e00\u4e2a\u4eba\uff0c\u6211\u4e5f\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u201c\u56e0\u4e3a\u6211\u53ea\u662f\u5728\u9152\u697c\u4e0a\u542c\u5f97\u6709\u4eba\u8c08\u8bba\u5b88\u5907\u8859\u95e8\u53d1\u73b0\u7684\u90a3\u4e2a\u91c7\u82b1\u8d3c\uff0c\u90a3\u4e9b\u4eba\u548c\u8859\u95e8\u7684\u536b\u5175 \u975e\u4eb2\u5373\u6545\uff0c\u8bf4\u8d77\u6765\u7ed8\u5f71\u7ed8\u58f0\uff0c\u662f\u4ee5\u542c\u7684\u4eba\u4e5f\u53ef\u4ee5\u8bb2\u5f97\u51fa\u90a3\u91c7\u82b1\u8d3c\u7684\u6837\u8c8c\u3002\u5b59\u5bb6\u90a3\u4e24\u4e2a\u53d7\u4e86\u91cd \u4f24\u7684\u62a4\u9662\uff0c\u5728\u6211\u8ba4\u8bc6\u7684\u4eba\u4e2d\u8c01\u4e5f\u6ca1\u6709\u548c\u4ed6\u4eec\u8bf4\u8fc7\u8bdd\uff0c\u4e0d\u8fc7\u5927\u5bb6\u90fd\u6ca1\u6709\u60f3\u8fc7\u662f\u5426\u540c\u4e00\u4e2a\u4eba\u7684\u95ee \u9898\uff0c\u6211\u4e5f\u53ea\u80fd\u5f53\u4f5c\u662f\u540c\u4e00\u4e2a\u4eba\u4e86\u3002\u201d \u3000\u3000\u8d3a\u4e5d\u516c\u7f57\u54e9\u7f57\u55e6\u5730\u8bf4\u4e86\u4e00\u5927\u7bc7\uff0c\u8fd8\u662f\u4e0d\u80fd\u89e3\u51b3\u5b5f\u660e\u971e\u5fc3\u4e2d\u7684\u7591\u95ee\u3002\u5b5f\u660e\u971e\u5bfb\u601d\uff1a\u201c\u5018\u82e5 \u662f\u540c\u4e00\u4e2a\u4eba\u7684\u8bdd\uff0c\u90a3\u5c31\u4e00\u5b9a\u662f\u6709\u4eba\u5047\u5192\u891a\u4e91\u5cf0\u6765\u9677\u5bb3\u4ed6\u4e86\u3002\u4f46\u5982\u679c\u662f\u4e24\u4e2a\u4eba\u7684\u8bdd\uff0c\u5b88\u5907\u8859\u95e8 \u90a3\u4ef6\u6848\u5b50\uff0c\u5374\u6709\u53ef\u80fd\u662f\u4ed6\u505a\u7684\u3002\u201d \u3000\u3000\u8d3a\u4e5d\u516c\u9053\uff0c\u201c\u5b5f\u59d1\u5a18\uff0c\u4f60\u597d\u50cf\u5e76\u4e0d\u600e\u4e48\u5bb3\u6015\u91c7\u82b1\u8d3c\uff1f\u201d \u3000\u3000\u5b5f\u660e\u971e\u7b11\u9053\uff1a\u201c\u8d35\u53bf\u90a3\u4e9b\u9152\u697c\u5ba2\u5e97\u7684\u638c\u67dc\u5374\u4e0d\u4f46\u5bb3\u6015\u91c7\u82b1\u8d3c\uff0c\u8fde\u6211\u4e5f\u90fd\u5bb3\u6015\u3002\u201d \u3000\u3000\u8d3a\u4e5d\u516c\u9053\uff0c\u201c\u91c7\u82b1\u8d3c\u95f9\u5f97\u8fd9\u6837\u51f6\uff0c\u8c01\u6562\u4e0d\u6015\uff1f\u5c24\u5176\u662f\u5f00\u5ba2\u5e97\u7684\uff0c\u5047\u5982\u6709\u4e2a\u5973\u5ba2\u4eba\u88ab\u91c7\u82b1 \u8d3c\u903c\u5978\u4e0d\u9042\u6740\u4e86\uff0c\u5373\u4f7f\u8fd9\u91c7\u82b1\u8d3c\u6ca1\u6709\u4e71\u6740\u522b\u4eba\uff0c\u5ba2\u5e97\u91cc\u7684\u4eba\u4e5f\u90fd\u8981\u53d7\u5230\u8fde\u7d2f\u4e86\u3002\u6700\u5c11\u5b98\u5e9c\u5c31 \u8981\u628a\u5f53\u665a\u4f4f\u5728\u5ba2\u5e97\u7684\u4eba\u4e00\u4e2a\u4e2a\u62ff\u6765\u76d8\u95ee\uff0c\u4f60\u60f3\u8c01\u613f\u610f\u60f9\u8fd9\u9ebb\u70e6\uff1f\u201d \u3000\u3000\u5b5f\u660e\u971e\u7b11\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\uff0c\u602a\u4e0d\u5f97\u4ed6\u4eec\u89c1\u6211\u4e0a\u95e8\uff0c\u5c31\u597d\u4f3c\u78b0\u4e0a\u707e\u661f\u4e00\u6837\u3002\u201d \u3000\u3000\u8d3a\u4e5d\u516c\u9053\uff1a\u201c\u5b5f\u59d1\u5a18\uff0c\u4f60\u82e5\u662f\u76f8\u8c8c\u4e11\u964b\u7684\u8bdd\uff0c\u6211\u5012\u4e0d\u7528\u66ff\u4f60\u62c5\u5fc3\u3002\u4f60\u53c8\u5e74\u8f7b\uff0c\u53c8\u8c8c\u7f8e\uff0c \u53ef\u662f\u5343\u4e07\u4e0d\u80fd\u8ba9\u90a3\u91c7\u82b1\u8d3c\u78b0\u4e0a\u554a\uff01\u600e\u7684\u4f60\u5012\u597d\u50cf\u5e76\u4e0d\u5982\u4f55\u5fe7\u8651\uff1f\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u6211\u6b63\u5df4\u4e0d\u5f97\u90a3\u91c7\u82b1\u8d3c\u524d\u6765\u627e\u6211\uff01\u201d \u3000\u3000\u8d3a\u4e5d\u516c\u6014\u4e86\u4e00\u6014\uff0c\u968f\u5373\u597d\u50cf\u6709\u70b9\u660e\u767d\u8fc7\u6765\u7684\u795e\u6c14\uff0c\u91cd\u65b0\u6253\u91cf\u4e86\u5b5f\u660e\u971e\u4e00\u773c\uff0c\u8bf4\u9053\uff1a\u201c\u6562 \u60c5\u59d1\u5a18\u662f\u4e00\u4f4d\u61c2\u5f97\u6b66\u529f\u7684\u5973\u4e2d\u82f1\u6770\uff0c\u5c0f\u8001\u51e0\u5012\u662f\u5931\u656c\u4e86\uff01\u201d \u3000\u3000\u76c2\u660e\u971e\u5ffd\u9053\uff1a\u201c\u4e5d\u516c\u3001\u6211\u770b\u4f60\u4e5f\u662f\u4e00\u4f4d\u5927\u6709\u672c\u9886\u7684\u4eba\uff0c\u6211\u521a\u624d\u5012\u662f\u770b\u8d70\u773c\u4e86\u3002\u201d\u53e3\u4e2d\u8bf4 \u7740\u8bdd\uff0c\u7a81\u7136\u5c31\u662f\u4e00\u638c\u5411\u8d3a\u4e5d\u516c\u7684\u7435\u7436\u9aa8\u6293\u4e0b\uff01 \u3000\u3000\u8d3a\u4e5d\u516c\u5927\u5403\u4e00\u60ca\uff0c\u672c\u80fd\u5730\u628a\u624b\u81c2\u4e00\u62ac\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u5df2\u662f\u7ed9\u5b5f\u660e\u971e\u6293\u7740\u4e86\u4ed6\u7684\u624b \u8155\u3002\u5b5f\u660e\u971e\u662f\u4e2a\u6b66\u5b66\u7684\u884c\u5bb6\uff0c\u4e00\u6293\u7740\u4e86\u5bf9\u65b9\u7684\u624b\u8155\uff0c\u5c31\u8bd5\u51fa\u4e86\u8d3a\u4e5d\u516c\u6b66\u529f\u7684\u6df1\u6d45\uff0c\u5fc3\u91cc\u60f3 \u9053\uff1a\u201c\u8fd9\u4f4d\u8001\u524d\u8f88\u679c\u7136\u662f\u4f1a\u6b66\u529f\uff0c\u4f46\u5374\u4e0d\u5982\u6211\u6240\u6599\u7684\u9ad8\u660e\u3002\u201d \u3000\u3000\u539f\u6765\u5b5f\u660e\u971e\u56e0\u4e3a\u521a\u624d\u5728\u8857\u4e0a\u7684\u65f6\u5019\uff0c\u8d3a\u4e5d\u516c\u8d70\u5230\u5979\u7684\u8eab\u8fb9\u5979\u624d\u53d1\u73b0\uff0c\u53c8\u4ece\u8d3a\u4e5d\u516c\u7684\u773c\u795e \u548c\u77eb\u5065\u7684\u811a\u6b65\u770b\u51fa\u4ed6\u61c2\u6b66\u529f\uff0c\u8fd8\u53ea\u9053\u4ed6\u662f\u4e00\u4f4d\u524d\u8f88\u9ad8\u624b\u3002\u662f\u4ee5\u5fc3\u91cc\u4e0d\u7981\u9887\u6709\u6000\u7591\uff1a\u201c\u4f55\u4ee5\u4ed6 \u8981\u88c5\u90a3\u6837\u6015\u4e8b\u7684\u6837\u5b50\u5462\uff1f\u201d \u3000\u3000\u8d3a\u4e5d\u516c\u7ed9\u5979\u4e00\u628a\u6293\u7740\u624b\u8155\uff0c\u5413\u5f97\u8fde\u5fd9\u53eb\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u522b\u5f00\u73a9\u7b11\uff0c\u6211\u8fd9\u51e0\u6839\u8001\u9aa8\u5934\u53ef\u7ecf\u4e0d \u8d77\u59d1\u5a18\u7684\u4e00\u6293\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u628a\u624b\u653e\u5f00\uff0c\u8d54\u4e86\u4e2a\u7f6a\uff0c\u8bf4\u9053\uff1a\u201c\u8bf7\u4e5d\u516c\u83ab\u602a\uff0c\u6211\u82e5\u4e0d\u662f\u8fd9\u4e48\u4e00\u8bd5\uff0c\u53ea\u6015\u4e5d\u516c\u8fd8\u662f \u771f\u4eba\u4e0d\u80af\u9732\u76f8\u5462\uff01\u201d \u3000\u3000\u8d3a\u4e5d\u516c\u82e6\u7b11\u9053\uff1a\u201c\u8fd8\u8bf4\u4ec0\u4e48\u2018\u771f\u4eba\u2019\u4e0d\u2018\u771f\u4eba\u2019\u5440\uff0c\u8001\u4e86\uff0c\u4e0d\u4e2d\u7528\u4e86\u3002\u6211\u5012\u662f\u60f3\u4e0d\u5230\u59d1 \u5a18\u6709\u8fd9\u6837\u597d\u7684\u8eab\u624b\uff0c\u51cf\u5c11\u4e86\u6211\u51e0\u5206\u7684\u5fe7\u8651\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u8001\u82f1\u96c4\u8fc7\u8c26\u4e86\u3002\u4f46\u4e0d\u77e5\u4e5d\u516c\u53ef\u66fe\u4fa6\u5bdf\u8fc7\u90a3\u91c7\u82b1\u8d3c\u7684\u884c\u8e2a\u4e48\uff0c\u82e5\u662f\u6709\u751a\u7ebf \u7d22\uff0c\u6211\u5012\u60f3\u53bb\u4f1a\u4e00\u4f1a\u4ed6\u3002\u201d \u3000\u3000\u8d3a\u4e5d\u516c\u9053\uff1a\u201c\u5b9e\u4e0d\u76f8\u7792\uff0c\u5c0f\u8001\u513f\u5c11\u5e74\u4e4b\u65f6\uff0c\u662f\u66fe\u5b66\u8fc7\u51e0\u5929\u529f\u592b\u3002\u5982\u4eca\u4e00\u6765\u662f\u5e74\u7eaa\u8001\u4e86\uff0c \u4e8c\u6765\u90a3\u91c7\u82b1\u8d3c\u5b9e\u5728\u5389\u5bb3\uff0c\u5c0f\u8001\u513f\u81ea\u95ee\uff0c\u5373\u4f7f\u662f\u5e74\u8f7b\u4e09\u5341\u5e74\uff0c\u4e5f\u51b3\u4e0d\u662f\u4ed6\u7684\u5bf9\u624b\uff0c\u5982\u4f55\u6562\u53bb\u60f9 \u4ed6\uff1f\u201d \u3000\u3000\u5b5f\u660e\u971e\u8bd5\u8fc7\u4ed6\u7684\u529f\u592b\uff0c\u5e76\u4e0d\u5982\u81ea\u5df1\u6240\u60f3\u8c61\u7684\u9ad8\u660e\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5982\u679c\u90a3\u91c7\u82b1\u8d3c\u5f53\u771f\u6709\u4ed6 \u6240\u8bf4\u7684\u90a3\u6837\u5389\u5bb3\uff0c\u8fd9\u4f4d\u8001\u4eba\u5bb6\u5012\u4e5f\u662f\u6709\u81ea\u77e5\u4e4b\u660e\uff0c\u602a\u4e0d\u5f97\u4ed6\u7684\u80c6\u5c0f\u3002\u201d \u3000\u3000\u5fc3\u5ff5\u672a\u5df2\uff0c\u53ea\u542c\u5f97\u8d3a\u4e5d\u516c\u53c8\u5df2\u8bf4\u9053\uff1a\u201c\u5b5f\u59d1\u5a18\uff0c\u6211\u6709\u51e0\u53e5\u4e0d\u4e2d\u542c\u7684\u8bf4\u8bdd\uff0c\u8bf7\u59d1\u5a18\u4e0d\u8981\u89c1 \u602a\u3002\u201d\u5b5f\u660e\u971e\u9053\uff1a\u201c\u4e5d\u516c\u4f46\u8bf4\u65e0\u59a8\u3002\u201d \u3000\u3000\u8d3a\u4e5d\u516c\u9053\uff1a\u201c\u4e0d\u662f\u6211\u957f\u4ed6\u4eba\u5fd7\u6c14\uff0c\u706d\u81ea\u5df1\u5a01\u98ce\u3002\u59d1\u5a18\u672c\u9886\u867d\u7136\u4e86\u5f97\uff0c\u53ef\u60dc\u5b64\u638c\u96be\u9e23\uff0c\u5355 \u72ec\u53bb\u6597\u90a3\u91c7\u82b1\u8d3c\uff0c\u53ea\u6015\u672a\u5fc5\u80fd\u591f\u7a33\u64cd\u80dc\u7b97\u3002\u8001\u673d\u81ea\u6068\u672c\u9886\u4e0d\u6d4e\uff0c\u6050\u6015\u5e2e\u4e0d\u4e86\u59d1\u5a18\u7684\u5fd9\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u8bf4\u5b9e\u5728\u8bdd\uff0c\u6211\u867d\u7136\u60f3\u6597\u4e00\u6597\u8fd9\u4e2a\u6076\u8d3c\uff0c\u4f46\u6211\u6709\u4e8b\u5728\u8eab\uff0c\u6050\u6015\u4e5f\u4e0d\u80fd\u5728\u8d35\u53bf \u9017\u7559\u591a\u4e45\uff0c\u80fd\u4e0d\u80fd\u4f1a\u4e0a\u8fd9\u91c7\u82b1\u8d3c\u8fd8\u662f\u672a\u53ef\u77e5\u4e4b\u6570\u5462\u3002\u4e0d\u8fc7\uff0c\u82e5\u662f\u4e07\u4e00\u9047\u4e0a\uff0c\u6597\u4e0d\u8fc7\u4e5f\u662f\u8981\u6597 \u7684\u3002\u201d \u3000\u3000\u8d3a\u4e5d\u516c\u9053\uff1a\u201c\u6211\u5012\u66fe\u60f3\u8fc7\u4e00\u6761\u597d\u4e3b\u610f\uff0c\u4e0d\u8fc7\u3001\u4e0d\u8fc7\u5176\u4e2d\u8fd8\u6709\u4e3a\u96be\u4e4b\u5904\uff0c\u53ea\u6015\u884c\u4e0d\u901a\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u5148\u522b\u7406\u4f1a\u884c\u4e0d\u884c\u5f97\u901a\uff0c\u4e5d\u516c\u65e2\u6709\u4e3b\u610f\uff0c\u8bf7\u8bf4\u51fa\u5927\u5bb6\u53c2\u8be6\u5982\u4f55\uff1f\u201d \u3000\u3000\u8d3a\u4e5d\u516c\u9053\uff1a\u201c\u7405\u739b\u5c71\u7684\u5c60\u5be8\u4e3b\u5728\u65e5\uff0c\u6700\u80af\u6d4e\u5f31\u9504\u5f3a\uff0c\u5a01\u540d\u8fdc\u9707\uff0c\u9644\u8fd1\u53bf\u4efd\uff0c\u5b98\u519b\u548c\u9ed1\u9053 \u4e0a\u7684\u5f3a\u4eba\u90fd\u4e0d\u6562\u4f5c\u6076\u3002\u4f46\u542c\u8bf4\u4ed6\u5df2\u7ecf\u6b7b\u4e86\uff0c\u4e0d\u77e5\u63a5\u4efb\u7684\u5be8\u4e3b\u662f\u5426\u8fd8\u662f\u50cf\u4ed6\u4ece\u524d\u4e00\u6837\uff1f\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u54e6\uff0c\u4e5d\u516c\u662f\u60f3\u6c42\u52a9\u4e8e\u7405\u739b\u5c71\uff1f\u201d \u3000\u3000\u8d3a\u4e5d\u516c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u7405\u739b\u5c71\u79bb\u6b64\u4e0d\u8fc7\u6570\u65e5\u8def\u7a0b\uff0c\u5018\u82e5\u4ed6\u4eec\u80af\u5e2e\u5fd9\uff0c\u4f55\u60e7\u4e00\u4e2a\u5c0f\u5c0f\u7684\u91c7\u82b1 \u8d3c\uff1f\u4f46\u5373\u4f7f\u65b0\u5be8\u4e3b\u7ee7\u627f\u5c60\u5be8\u4e3b\u7684\u9057\u98ce\uff0c\u6211\u4e5f\u8ba4\u4e0d\u5f97\u7405\u739b\u5c71\u7684\u4eba\uff0c\u5374\u627e\u8c01\u7a7f\u9488\u5f15\u7ebf\uff1f\u201d \u3000\u3000\u5b5f\u660e\u971e\u5fc3\u60f3\u8fd9\u5012\u662f\u4e2a\u597d\u4e3b\u610f\uff0c\u4e8e\u662f\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u5fd9\u6211\u5012\u53ef\u4ee5\u5e2e\u5f97\u4e0a\u3002\u201d \u3000\u3000\u8d3a\u4e5d\u516c\u559c\u9053\uff1a\u201c\u5b5f\u59d1\u5a18\u548c\u7405\u739b\u5c71\u7684\u5934\u9886\u6709\u4ea4\u60c5\uff1f\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u5b9e\u4e0d\u76f8\u7792\uff0c\u7405\u739b\u5c71\u7684\u65b0\u5be8\u4e3b\u5c60\u51e4\u5c31\u662f\u8001\u5be8\u4e3b\u7684\u5973\u513f\uff0c\u5979\u4e0e\u6211\u60c5\u5982\u59d0\u59b9\u3002\u8fd9 \u6837\u5427\uff0c\u6211\u6253\u7b97\u5728\u8fd9\u91cc\u803d\u6401\u4e24\u5929\uff0c\u4fa6\u5bdf\u90a3\u91c7\u82b1\u8d3c\u7684\u52a8\u9759\uff0c\u82e5\u7136\u5f97\u4e0d\u5230\u7ed3\u679c\uff0c\u6211\u5199\u4e00\u5c01\u4fe1\u7ed9\u4f60\u5e26 \u7ed9\u5c60\u51e4\uff0c\u5979\u4e00\u5b9a\u4f1a\u4f38\u624b\u7ba1\u8fd9\u6869\u4e8b\u7684\u3002\u201d \u3000\u3000\u8d3a\u4e5d\u516c\u5927\u559c\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u8001\u673d\u53ef\u8981\u4e3a\u672c\u53bf\u7684\u767e\u59d3\u62dc\u8c22\u59d1\u5a18\u7684\u5927\u5fb7\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u8fde\u5fd9\u5c06\u8d3a\u4e5d\u516c\u6276\u8d77\uff0c\u8bf4\u9053\uff1a\u201c\u4e3a\u6c11\u9664\u5bb3\uff0c\u8fd9\u662f\u6211\u8f88\u5f53\u4e3a\u4e4b\u4e8b\uff0c\u4f55\u987b\u8a00\u8c22\uff01\u201d \u3000\u3000\u90a3\u8001\u5a46\u5a46\u6367\u4e86\u4e00\u7897\u70ed\u817e\u817e\u7684\u9762\u51fa\u6765\uff0c\u8be7\u9053\uff1a\u201c\u54a6\uff0c\u4f60\u4eec\u5728\u95f9\u4ec0\u4eba\uff1f\u201d \u3000\u3000\u8d3a\u4e5d\u516c\u9053\uff1a\u201c\u8001\u4f34\u513f\uff0c\u4f60\u60f3\u4e0d\u5230\u5427\uff0c\u8fd9\u4f4d\u5b5f\u59d1\u5a18\u539f\u6765\u662f\u4e00\u4f4d\u5927\u6709\u6765\u5934\u7684\u4eba\u7269\uff0c\u5979\u548c\u7405\u739b \u5c71\u5c60\u8001\u5be8\u4e3b\u7684\u5973\u513f\u4e43\u662f\u91d1\u5170\u59d0\u59b9\uff0c\u5979\u5df2\u7ecf\u7b54\u5e94\u5e2e\u5fd9\u54b1\u4eec\u9664\u6389\u90a3\u91c7\u82b1\u8d3c\u4e86\u3002\u201d \u3000\u3000\u90a3\u8001\u5a46\u5a46\u4f3c\u4e4e\u6709\u70b9\u534a\u4fe1\u534a\u7591\u7684\u795e\u6c14\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u6562\u60c5\u597d\uff0c\u4f46\u5728\u7405\u739b\u5c71\u7684\u4eba\u672a\u6765\u4e4b\u524d\uff0c\u59d1 \u5a18\u4eca\u665a\u8fd8\u662f\u591a\u52a0\u5c0f\u5fc3\u7684\u597d\u3002\u201d\u53c8\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u8fd9\u4e48\u6837\u8bf4\uff0c\u4f60\u5012\u5c31\u662f\u4e00\u4f4d\u8bf7\u4e5f\u8bf7\u4e0d\u6765\u7684\u8d35\u5ba2 \u4e86\u3002\u53ef\u60dc\u6211\u6ca1\u6709\u597d\u4e1c\u897f\u62db\u5f85\u4f60\uff0c\u53ea\u6709\u4e00\u7897\u7c97\u9762\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u8fde\u5fd9\u63a5\u8fc7\u90a3\u4e00\u7897\u9762\uff0c\u8bf4\u9053\uff1a\u201c\u5a46\u5a46\uff0c\u4f60\u592a\u5ba2\u6c14\u4e86\u3002\u82e5\u4e0d\u662f\u591a\u5f97\u4f60\u4eec\uff0c\u4eca\u665a\u6211\u6050\u6015 \u53ea\u6709\u997f\u809a\u5b50\u4e86\u5462\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u628a\u8fd9\u7897\u9762\u5403\u5f97\u5e72\u5e72\u51c0\u51c0\uff0c\u53ea\u89c9\u6709\u751f\u4ee5\u6765\uff0c\u4ece\u6ca1\u5403\u8fc7\u8fd9\u6837\u597d\u5403\u7684\u4e1c\u897f\uff0c\u4e0d\u7981\u6697\u6697\u597d \u7b11\uff1a\u201c\u4fd7\u8bed\u8bf4\u9965\u4e0d\u62e9\u98df\uff0c\u5f53\u771f\u4e0d\u9519\u3002\u5c60\u51e4\u82e5\u7136\u89c1\u5230\u6211\u8fd9\u526f\u998b\u76f8\uff0c\u4e00\u5b9a\u4f1a\u7b11\u7834\u4e86\u809a\u76ae\u4e86\u3002\u201d \u3000\u3000\u5403\u8fc7\u4e86\u9762\uff0c\u90a3\u8001\u5a46\u5a46\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u4e00\u8def\u8f9b\u82e6\u3002\u65e9\u70b9\u6b47\u5427\u3002\u201d\u9886\u5b5f\u660e\u971e\u8fdb\u4e86\u5ba2\u623f\uff0c\u53c8\u518d \u4f4e\u58f0\u8bf4\uff1a\u201c\u59d1\u5a18\uff0c\u6211\u77e5\u9053\u4f60\u5f88\u7d2f\u4e86\uff0c\u53ef\u4e5f\u4e0d\u8981\u7761\u5f97\u592a\u719f\u3002\u5018\u82e5\u542c\u5f97\u4ec0\u4e48\u58f0\u606f\uff0c\u4f60\u9a6c\u4e0a\u53eb\u558a\uff0c \u6211\u4eec\u7684\u623f\u95f4\u5c31\u5728\u659c\u5bf9\u9762\u3002\u6211\u90a3\u8001\u4f19\u4f34\u867d\u7136\u4e0d\u6d4e\u4e8b\uff0c\u4e5f\u8fd8\u4f1a\u51e0\u624b\u62f3\u811a\u3002\u201d\u770b\u6765\u5979\u5e76\u4e0d\u76f8\u4fe1\u5b5f\u660e \u971e\u771f\u662f\u4e2a\u6709\u672c\u9886\u7684\u5973\u4fa0\uff0c\u4e5f\u4e0d\u77e5\u9053\u5b5f\u660e\u971e\u5df2\u7ecf\u8bd5\u8fc7\u4e86\u5979\u7684\u201c\u8001\u4f34\u513f\u201d\u7684\u529f\u592b\u3002\u5b5f\u660e\u971e\u9053\uff1a \u201c\u591a\u8c22\u5a46\u5a46\u5173\u5fc3\uff0c\u4f60\u4e5f\u65e9\u70b9\u6b47\u5427\u3002\u201d \u3000\u3000\u5176\u5b9e\u4e0d\u7528\u8fd9\u8001\u5a46\u5a46\u53ee\u5631\uff0c\u5b5f\u660e\u971e\u4e5f\u662f\u7761\u4e0d\u7740\u89c9\u7684\u3002\u201c\u90a3\u91c7\u82b1\u8d3c\u662f\u4e0d\u662f\u891a\u4e91\u5cf0\u5462\uff1f\u7239\u7239\u6ca1 \u6709\u89c1\u8fc7\u4ed6\uff0c\u4f55\u4f3c\u6562\u76f8\u4fe1\u4ed6\u662f\u597d\u4eba\uff1f\u53ef\u60dc\u90a3\u65e5\u6ca1\u6709\u65f6\u95f4\u4ed4\u7ec6\u95ee\u4ed6\u3002\u201d\u5b5f\u660e\u971e\u60f3\u8d77\u4e86\u891a\u4e91\u5cf0\u79cd\u79cd \u53e4\u602a\u7684\u884c\u5f84\uff0c\u60f3\u8d77\u4e86\u90a3\u65e5\u5979\u7236\u4eb2\u8bf4\u8bdd\u7684\u795e\u6c14\uff1b\u867d\u5f79\u660e\u8a00\uff0c\u5374\u5206\u660e\u662f\u5f88\u60c5\u4efb\u4ed6\uff0c\u4e0d\u7981\u7591\u56e2\u6ee1 \u8179\u3002\u5f53\u4e0b\u548c\u8863\u800c\u7761\uff0c\u95ed\u76ee\u517b\u795e\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u5b5f\u660e\u971e\u6e10\u6e10\u6709\u4e86\u4e00\u70b9\u5026\u610f\uff0c\u5ffd\u542c\u5f97\u201c\u535c\u201d\u7684\u4e00\u58f0\u54cd\uff0c\u4f3c\u4e4e\u6709\u4eba\u5728\u7a97\u5b50 \u8f7b\u8f7b\u5f39\u4e86\u4e00\u4e0b\u3002\u8fd9\u8f7b\u8f7b\u7684\u4e00\u5f39\uff0c\u767b\u65f6\u4ee4\u5b5f\u660e\u971e\u7761\u610f\u5168\u6d88\uff0c\u7cbe\u795e\u4e00\u632f\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u83ab\u975e\u5f53\u771f \u662f\u90a3\u8d3c\u513f\u6765\u4e86\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u62ff\u8d77\u5b9d\u5251\uff0c\u6b63\u8981\u5047\u88c5\u719f\u7761\uff0c\u5f85\u90a3\u91c7\u82b1\u8d3c\u8fdb\u6765\uff0c\u51b7\u4e0d\u9632\u5730\u7ed9\u4ed6\u4e00\u5251\u3002 \u3000\u3000\u4e0d\u6599\u4e8b\u60c5\u5927\u51fa\u5b5f\u660e\u971e\u610f\u6599\u4e4b\u5916\u3002\u5728\u90a3\u8f7b\u8f7b\u7684\u4e00\u5f39\u8fc7\u540e\uff0c\u8d3c\u4eba\u5e76\u6ca1\u6709\u63a8\u5f00\u7a97\u5b50\uff0c\u5b5f\u660e\u971e\u5374 \u597d\u4f3c\u542c\u5f97\u6709\u4eba\u5728\u5979\u8033\u8fb9\u8bf4\u9053\uff1a\u201c\u5b5f\u59d1\u5a18\u8bf7\u522b\u58f0\u5f20\uff0c\u6211\u662f\u891a\u4e91\u5cf0\uff0c\u6211\u6709\u8bdd\u548c\u4f60\u8bf4\u3002\u8bf7\u4f60\u51fa \u6765\uff01\u201d \u3000\u3000\u58f0\u97f3\u7ec6\u82e5\u6e38\u4e1d\uff0c\u4f46\u5374\u542c\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u7684\u786e\u662f\u891a\u4e91\u5cf0\uff01\u4ed6\u7528\u7684\u662f\u4e0a\u4e58\u7684\u201c\u4f20\u97f3\u5165\u5bc6\u201d\u7684\u5185 \u529f\uff0c\u628a\u58f0\u97f3\u51dd\u6210\u4e00\u4e1d\uff0c\u9001\u5165\u5b5f\u660e\u971e\u7684\u8033\u6735\uff0c\u5373\u4f7f\u5b5f\u660e\u971e\u65c1\u8fb9\u6709\u4eba\uff0c\u90a3\u4eba\u7684\u5185\u529f\u82e5\u662f\u4e0d\u5982\u5b5f\u660e \u971e\uff0c\u4e5f\u51b3\u4e0d\u4f1a\u542c\u89c1\u3002 \u3000\u3000\u5b5f\u660e\u971e\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u770b\u8fd9\u60c5\u5f62\uff0c\u4ed6\u591a\u534a\u4e0d\u662f\u91c7\u82b1\u8d3c\u4e86\u3002\u201d\u4f46\u9632\u4eba\u4e4b\u5fc3\u4e0d\u53ef\u65e0\uff0c\u5b5f\u660e\u971e\u4ece \u7a97\u53e3\u8df3\u51fa\u53bb\u7684\u65f6\u5019\uff0c\u4ecd\u7136\u662f\u4eae\u51fa\u4e86\u5b9d\u5251\uff0c\u628a\u5b9d\u5251\u6321\u5728\u8eab\u524d\u7684\u3002 \u3000\u3000\u6de1\u6de1\u7684\u6708\u5149\u4e4b\u4e0b\uff0c\u53ea\u89c1\u5c4b\u9876\u6709\u6761\u9ed1\u5f71\u3002\u539f\u6765\u891a\u4e91\u5cf0\u65e9\u5df2\u9632\u5979\u4f1a\u6709\u7591\u5fc3\uff0c\u5148\u8eb2\u5f00\u7684\u3002\u5b5f\u660e \u971e\u9762\u4e0a\u4e00\u7ea2\uff0c\u8ddf\u7740\u5c31\u8df3\u4e0a\u53bb\u3002 \u3000\u3000\u5c0f\u6237\u4eba\u5bb6\u7684\u623f\u5c4b\uff0c\u5c4b\u6a90\u79bb\u5730\u4e0d\u8fc7\u662f\u4e00\u4e08\u591a\u9ad8\uff0c\u4ee5\u5b5f\u660e\u971e\u7684\u8f7b\u529f\uff0c\u6309\u8bf4\u8df3\u4e0a\u53bb\u4e43\u662f\u4e0d\u8d39\u5439 \u7070\u4e4b\u529b\uff0c\u4e0d\u6599\u8df3\u867d\u7136\u662f\u8df3\u4e0a\u53bb\u4e86\uff0c\u4f46\u5f53\u5979\u63d0\u6c14\u4e4b\u65f6\uff0c\u80f8\u53e3\u5ffd\u4f3c\u5fae\u6709\u9ebb\u6728\u4e4b\u611f\uff0c\u843d\u811a\u7a0d\u91cd\uff0c\u51e0 \u4e4e\u8e0f\u788e\u4e86\u4e00\u7247\u74e6\u3002\u5e78\u540e\u891a\u4e91\u5cf0\u7acb\u5373\u4f38\u624b\u6276\u5979\uff0c\u5e2e\u5979\u7a33\u7740\u8eab\u5f62\uff0c\u8fd9\u624d\u6ca1\u6709\u5f04\u51fa\u58f0\u54cd\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u5403\u4e86\u4e00\u60ca\uff0c\u6084\u58f0\u95ee\u9053\uff1a\u201c\u4f60\u53ef\u6709\u4ec0\u4e48\u89c9\u5f97\u4e0d\u5bf9\u4e48\uff1f\u201d\u5b5f\u660e\u971e\u77e5\u9053\u4ed6\u8fd9\u4e00\u95ee\u4e43\u662f\u56e0 \u4e3a\u81ea\u5df1\u51e0\u4e4e\u5931\u8db3\u800c\u53d1\uff0c\u5979\u5bf9\u81ea\u5df1\u7684\u8f7b\u529f\u7a81\u7136\u5931\u7075\u4e5f\u662f\u6709\u70b9\u8be7\u5f02\uff0c\u4f46\u5374\u4ee5\u4e3a\u8fd9\u662f\u56e0\u4e3a\u5fc3\u795e\u4e0d\u5b9a \u6240\u81f3\u3002\u8981\u77e5\u5fc3\u795e\u4e0d\u5b9a\uff0c\u5185\u606f\u5c31\u96be\u4ee5\u8c03\u548c\uff0c\u8f7b\u529f\u4e5f\u5c31\u4e0d\u514d\u53d7\u4e86\u5f71\u54cd\u3002 \u3000\u3000\u5b5f\u660e\u971e\u8bd5\u4e00\u8fd0\u6c14\uff0c\u89c9\u5f97\u5e76\u65e0\u5f02\u72b6\uff0c\u4e8e\u662f\u8bf4\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\u5440\u3002\u201d\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u597d\uff0c\u90a3\u6211\u5c31 \u653e\u5fc3\u4e86\u3002\u6b64\u5730\u4e0d\u5b9c\u4e45\u7559\uff0c\u54b1\u4eec\u5feb\u70b9\u8d70\u5427\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u4f60\u7684\u610f\u601d\u662f\u8d3a\u4e5d\u516c\u4e0d\u662f\u597d\u4eba\uff1f\u201d\u8bf8\u4e91\u5cf0\u9053\uff1a\u201c\u6211\u8fd8\u672a\u62ff\u5f97\u51c6\uff0c\u6211\u53ea\u77e5\u9053\u4ed6 \u662f\u9ed1\u9053\u51fa\u8eab\uff0c\u4ee5\u524d\u7684\u58f0\u540d\uff0c\u53ef\u662f\u5e76\u4e0d\u600e\u6837\u597d\u7684\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u4f46\u4ed6\u4eec\u592b\u5987\u5bf9\u6211\u53ef\u662f\u5f88\u597d\uff0c\u6211\u600e\u53ef\u4e0d\u8f9e\u800c\u884c\uff1f\u201d\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u5bb3\u4eba\u4e4b\u5fc3\u4e0d \u53ef\u6709\uff0c\u9632\u4eba\u4e4b\u5fc3\u4e0d\u53ef\u65e0\u3002\u6211\u770b\u8fd8\u662f\u8d70\u7684\u597d\uff01\u201d \u3000\u3000\u4f46\u5b5f\u660e\u971e\u8fd8\u662f\u4e0d\u613f\u610f\u8d70\uff0c\u5979\u6697\u81ea\u5bfb\u601d\uff1a\u201c\u8d3a\u4e5d\u516c\u672c\u9886\u5e73\u5eb8\uff0c\u5373\u4f7f\u4ed6\u60f3\u5bb3\u6211\uff0c\u4e5f\u505a\u4e0d \u5230\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u5e76\u4e0d\u628a\u8d3a\u4e5d\u516c\u653e\u5728\u5fc3\u4e0a\uff0c\u5012\u662f\u89c9\u5f97\u891a\u4e91\u5cf0\u7684\u7a81\u5982\u5176\u6765\uff0c\u751a\u4e3a\u5947\u602a\uff0c\u4e8e\u662f\u95ee\u9053\uff1a \u201c\u4f60\u600e\u4e48\u77e5\u9053\u6211\u5728\u8fd9\u513f\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u77e5\u9053\u5979\u5bf9\u81ea\u5df1\u5c1a\u6709\u7591\u5fc3\uff0c\u4e0d\u8bf4\u660e\u767d\uff0c\u6050\u6015\u5979\u662f\u4e0d\u80af\u8ddf\u81ea\u5df1\u8d70\u7684\uff0c\u201c\u4f60\u4ece\u90a3\u95f4\u9152\u697c \u51fa\u6765\uff0c\u6211\u5df2\u7ecf\u770b\u89c1\u4f60\u4e86\u3002\u201d\u891a\u4e91\u5cf0\u8bf4\u3002 \u3000\u3000\u201c\u90a3\u4e48\u6709\u4eba\u5192\u5145\u4f60\u505a\u6765\u82b1\u8d3c\uff0c\u60f3\u5fc5\u4f60\u4e5f\u662f\u77e5\u9053\u7684\u4e86\uff1f\u201d \u3000\u3000\u201c\u6211\u6b63\u662f\u4e3a\u4e86\u6b64\u4e8b\u800c\u6765\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u4e0d\u89c9\u53c8\u662f\u4e00\u6014\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u5230\u8fd9\u91cc\u6765\u627e\u91c7\u82b1\u8d3c\u3001\u96be\u9053\u4f60\u4ee5\u4e3a\u8d3a\u4e5d\u516c\u662f\u90a3\u91c7\u82b1\u8d3c\u7684 \u515a\u7fbd\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u662f\u6709\u6b64\u6000\u7591\uff0c\u6240\u4ee5\u6211\u624d\u53eb\u4f60\u5feb\u8d70\u3002\u201d\u5b5f\u660e\u971e\u8be7\u9053\uff1a\u201c\u8fd9\u6211\u5c31\u4e0d\u61c2 \u4e86\uff0c\u4f60\u4e0d\u60f3\u62ff\u90a3\u4e2a\u5192\u5145\u4f60\u7684\u91c7\u82b1\u8d3c\uff1f\u5982\u679c\u5f53\u771f\u5982\u4f60\u6240\u8bf4\uff0c\u90a3\u91c7\u82b1\u8d3c\u4f1a\u6765\u6b64\u5904\uff0c\u54b1\u4eec\u4e24\u4eba\u6715 \u624b\uff0c\u4e0d\u6b63\u662f\u53ef\u4ee5\u62ff\u83b7\u4ed6\u5417\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u6211\u4e5f\u6599\u5f97\u4e0d\u662f\u5f88\u51c6\uff0c\u4e0d\u8fc7\u5982\u679c\u662f\u771f\u7684\u8bdd\uff0c\u54b1\u4eec\u4e8c\u4eba\u8054\u624b\uff0c\u6050\u6015\u4e5f\u8fd8\u662f\u8981\u5403 \u4e8f\u7684\u3002\u4f60\u522b\u5fd8\u8bb0\uff0c\u4ed6\u4eec\u662f\u6709\u4e8c\u4e2a\u4eba\u5440\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u4e0d\u89c9\u5931\u7b11\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8d3a\u4e5d\u516c\u672c\u9886\u5e73\u5eb8\uff0c\u4ed6\u7684\u59bb\u5b50\u66f4\u662f\u4e1d\u6beb\u4e0d\u61c2\u6b66\u529f\uff0c\u891a\u4e91\u5cf0 \u7adf\u4f1a\u6015\u4e86\u4ed6\u4eec\uff0c\u771f\u662f\u7b11\u8bdd\uff01\u201d\u4f46\u4e5f\u89c9\u5f97\u6709\u70b9\u5947\u602a\uff0c\u891a\u4e91\u5cf0\u90a3\u65e5\u6597\u5d14\u9547\u5c71\u4e0e\u67f3\u6d1e\u5929\u4e4b\u65f6\uff0c\u80fd\u628a \u751f\u6b7b\u7f6e\u4e4b\u5ea6\u5916\uff0c\u4f55\u4ee5\u6b64\u65f6\u5ffd\u5730\u53c8\u53d8\u5f97\u80c6\u5b50\u8fd9\u4e48\u5c0f\u4e86\uff1f \u3000\u3000\u76c2\u660e\u971e\u6b63\u60f3\u5bf9\u4ed6\u8bf4\u660e\u8d3a\u4e5d\u516c\u592b\u59bb\u4e0d\u8db3\u4e3a\u60e7\uff0c\u891a\u4e91\u5cf0\u5ffd\u9053\uff1a\u201c\u8fdf\u4e86\uff0c\u8fdf\u4e86\uff0c\u90a3\u53ae\u5df2\u7ecf\u6765 \u5566\uff01\u201d \u3000\u3000\u5b5f\u660e\u971e\u51dd\u795e\u770b\u53bb\uff0c\u53ea\u89c1\u4e00\u6761\u9ed1\u5f71\u5954\u6765\uff0c\u6377\u5982\u98de\u9e1f\u3002\u891a\u4e91\u5cf0\u5c06\u5979\u4e00\u62c9\uff0c\u4f0f\u5728\u5c4b\u810a\u7684\u74e6\u6c9f\u4e4b \u95f4\uff0c\u6084\u58f0\u8bf4\u9053\uff1a\u201c\u4e0d\u53ef\u9c81\u83bd\uff0c\u4e14\u770b\u4ed6\u4eec\u6709\u4ec0\u4e48\u628a\u620f\u3002\u201d \u3000\u3000\u53ea\u542c\u5f97\u201c\u556a\u201d\u7684\u4e00\u58f0\uff0c\u91c7\u82b1\u8d3c\u63a8\u5f00\u7a97\u5b50\uff0c\u8fdb\u4e86\u623f\u95f4\u3002\u5b5f\u660e\u971e\u60f3\u9053\uff1a\u201c\u8fd9\u91c7\u82b1\u8d3c\u5fd2\u4e5f\u80c6 \u5927\uff0c\u7adf\u6562\u4e0d\u7528\u8ff7\u9999\uff0c\u82e5\u7136\u6211\u5728\u623f\u4e2d\uff0c\u51b7\u4e0d\u9632\u7684\u7ed9\u4ed6\u4e00\u5251\uff0c\u53ea\u6015\u4ed6\u4e0d\u6b7b\u4e5f\u5f97\u91cd\u4f24\uff01\u201d \u3000\u3000\u5fc3\u5ff5\u672a\u5df2\uff0c\u90a3\u91c7\u82b1\u8d3c\u5df2\u662f\u201c\u54a6\u201d\u7684\u4e00\u58f0\u53eb\u4e86\u51fa\u6765\uff1a\u201c\u8fd9\u5973\u5a03\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u968f\u5373\u542c\u5f97\u8d3a\u4e5d\u516c\u7684\u58f0\u97f3\u559d\u9053\uff1a\u201c\u597d\u80c6\u5927\u7684\u6deb\u8d3c\uff0c\u7adf\u6562\u6b3a\u8d1f\u5230\u6211\u8001\u4eba\u5bb6\u5934\u4e0a\u6765\u4e86\uff01\u201d\u90a3\u91c7 \u82b1\u8d3c\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u51e0\u6839\u8001\u9aa8\u5934\u4e0d\u8db3\u6321\u6211\u4e00\u51fb\uff0c\u5feb\u5feb\u628a\u90a3\u5973\u5a03\u5b50\u4ea4\u51fa\u6765\uff0c\u9976\u4f60\u4e00\u547d\uff01\u201d \u3000\u3000\u5b5f\u660e\u971e\u542c\u89c1\u4e0b\u9762\u5df2\u662f\u4e52\u4e52\u4e53\u4e53\u5730\u6253\u4e86\u8d77\u6765\uff0c\u6309\u637a\u4e0d\u4f4f\uff0c\u5c31\u60f3\u4e0b\u53bb\u3002\u891a\u4e91\u5cf0\u6293\u7740\u5979\u7684\u624b\uff0c \u6084\u58f0\u8bf4\u9053\uff1a\u201c\u518d\u770b\u4e00\u4f1a\u3002\u201d \u3000\u3000\u4e0d\u8fc7\u7247\u523b\uff0c\u5200\u5251\u78b0\u51fb\u7684\u58f0\u97f3\u5df2\u7ecf\u9759\u5bc2\uff0c\u5c4b\u5b50\u91cc\u5374\u900f\u51fa\u706f\u5149\uff0c\u539f\u6765\u90a3\u91c7\u82b1\u8d3c\u5df2\u662f\u628a\u8d3a\u4e5d\u516c \u6253\u5012\uff0c\u70b9\u8d77\u706f\u6765\u3002\u6b64\u523b\u4ed6\u6b63\u5728\u4eae\u706f\u5bfb\u627e\u82b1\u59d1\u5a18\u4e86\u3002 \u3000\u3000\u90a3\u8001\u5a46\u5a46\u98a4\u98a4\u5dcd\u5dcd\u5730\u8d70\u51fa\u5385\u5802\uff0c\u54c0\u54c0\u6c42\u544a\uff1a\u201c\u5927\u738b\uff0c\u4f60\u9976\u4e86\u6211\u7684\u8001\u4f34\u513f\u5427\u3002\u6211\u5bb6\u91cc\u59d4\u5b9e \u662f\u6ca1\u6709\u95fa\u5973\u3002\u201d \u3000\u3000\u90a3\u91c7\u82b1\u8d3c\u9a82\u9053\uff1a\u201c\u4f60\u8fd9\u8001\u8654\u5a46\u8fd8\u60f3\u9a97\u6211\uff0c\u4f60\u6ca1\u6709\u95fa\u5973\uff0c\u53ef\u6709\u522b\u4eba\u7684\u95fa\u5973\u5728\u4f60\u5bb6\u6295\u5bbf\uff0c\u4f60 \u5f53\u6211\u4e0d\u77e5\u9053\u5417\uff1f\u5979\u8eb2\u5230\u54ea\u91cc\u53bb\u4e86\uff0c\u5feb\u8bf4\uff01\u201d \u3000\u3000\u8d3a\u4e5d\u516c\u6012\u9053\uff1a\u201c\u8001\u4f34\u513f\uff0c\u54b1\u4eec\u62fc\u7740\u592b\u59bb\u4e00\u540c\u6bd9\u547d\u5427\uff01\u54fc\uff0c\u83ab\u8bf4\u6211\u4e0d\u77e5\u9053\uff0c\u5c31\u662f\u77e5\u9053\uff0c\u6211 \u4e5f\u51b3\u4e0d\u80fd\u544a\u8bc9\u4f60\u3002\u4f60\u628a\u6211\u4e00\u5200\u6740\u4e86\u5427\u3002\u201d \u3000\u3000\u90a3\u91c7\u82b1\u8d3c\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u8bf4\uff0c\u96be\u9053\u6211\u5c31\u4e0d\u4f1a\u81ea\u5df1\u627e\u4e48\uff1f\u4e0d\u8fc7\uff0c\u4f60\u8fd9\u4e24\u4e2a\u8001\u5bb6\u4f19\u5982\u6b64\u5634 \u786c\uff0c\u6211\u5374\u975e\u7ed9\u4f60\u4e00\u70b9\u82e6\u5934\u5403\u5403\u4e0d\u53ef\uff01\u201d\u4e00\u9762\u8bf4\u8bdd\uff0c\u4e00\u9762\u628a\u8d3a\u4e5d\u516c\u592b\u59bb\u7f1a\u4e86\u8d77\u6765\u3002\u7f1a\u597d\u4e4b\u540e\uff0c \u62ff\u4e86\u4e00\u6761\u97ad\u5b50\uff0c\u567c\u567c\u556a\u556a\u5730\u5c31\u671d\u7740\u8fd9\u5bf9\u8001\u592b\u59bb\u7684\u8eab\u4e0a\u4e71\u6253\uff01 \u3000\u3000\u8d3a\u4e5d\u516c\u4e5f\u771f\u786c\u6c14\uff0c\u54ac\u7740\u4e86\u7259\u62b5\u53d7\uff0c\u4e0d\u542d\u4e00\u58f0\u3002\u53ef\u662f\u90a3\u8001\u5a46\u5a46\u5374\u7ecf\u53d7\u4e0d\u8d77\uff0c\u91c7\u82b1\u8d3c\u4e00\u97ad\u4e00 \u9053\u8840\u75d5\uff0c\u6253\u5f97\u5979\u5636\u58f0\u568e\u53eb\uff01 \u3000\u3000\u8001\u5a46\u5a46\u7684\u58f0\u58f0\u53eb\u558a\uff0c\u542c\u5728\u5b5f\u660e\u971e\u7684\u5fc3\u91cc\uff0c\u5c31\u4f3c\u97ad\u5b50\u6253\u5728\u5979\u8eab\u4e0a\u4e00\u6837\u96be\u53d7\uff0c\u7981\u4e0d\u4f4f\u731b\u5730\u5c31 \u7529\u5f00\u4e86\u891a\u4e91\u5cf0\u6309\u7740\u5979\u7684\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u542c\uff0c\u96be\u9053\u4f60\u8fd8\u4ee5\u4e3a\u8fd9\u4e24\u4f4d\u8001\u4eba\u5bb6\u662f\u91c7\u82b1\u8d3c\u7684\u515a\u7fbd \u5417\uff1f\u201d\u5979\u7529\u5f00\u4e86\u891a\u4e91\u5cf0\u7684\u624b\uff0c\u53e3\u4e2d\u8bf4\u8bdd\uff0c\u8eab\u5b50\u5df2\u662f\u8df3\u4e86\u4e0b\u53bb\uff01 \u3000\u3000\u91c7\u82b1\u8d3c\u89c1\u5b5f\u660e\u971e\u95ef\u4e86\u8fdb\u6765\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u8fd8\u662f\u4e00\u4f4d\u4f1a\u628a\u620f\u7684\u59d1\u5a18\u5462\uff0c\u8fd9\u66f4\u597d\u4e86\uff0c \u4f60\u5c31\u6b63\u5f0f\u5ac1\u4e86\u6211\u5427\uff0c\u54b1\u4eec\u592b\u59bb\u4fe9\u2026\u2026\u201d \u3000\u3000\u5b5f\u660e\u971e\u559d\u9053\uff1a\u201c\u4f4f\u5634\uff01\u201d\u5530\u7684\u4e00\u5251\u523a\u53bb\uff0c\u706f\u5149\u4e0b\u53ea\u89c1\u8fd9\u91c7\u82b1\u8d3c\u8eab\u6750\u9880\u7855\uff0c\u9762\u8c8c\u548c\u891a\u4e91\u5cf0 \u679c\u7136\u6709\u51e0\u5206\u76f8\u4f3c\uff0c\u7709\u5fc3\u4e5f\u6709\u4e00\u9897\u9ed1\u75e3\u3002\u4f46\u5b5f\u660e\u971e\u4e00\u770b\u5c31\u77e5\u9053\u8fd9\u9897\u9ed1\u75e3\u662f\u4eba\u5de5\u70b9\u4e0a\u7684\uff0c\u4ed6\u7684\u76f8 \u8c8c\u4e5f\u662f\u7ecf\u8fc7\u5316\u5986\uff0c\u770b\u5f97\u51fa\u662f\u6709\u610f\u626e\u5f97\u4f3c\u891a\u4e91\u5cf0\uff0c\u597d\u63a9\u9970\u672c\u6765\u9762\u76ee\u7684\u3002 \u3000\u3000\u91c7\u82b1\u8d3c\u5047\u626e\u891a\u4e91\u5cf0\u672c\u662f\u5728\u5b5f\u660e\u971e\u610f\u6599\u4e4b\u4e2d\uff0c\u6ca1\u6709\u4ec0\u4e48\u503c\u5f97\u5947\u602a\u3002\u4f46\u5947\u602a\u7684\u662f\uff0c\u8fd9\u91c7\u82b1\u8d3c \u867d\u7136\u63a9\u9970\u4e86\u672c\u6765\u9762\u76ee\uff0c\u7ed9\u5b5f\u660e\u971e\u7684\u5370\u8c61\u4ecd\u662f\u4f3c\u66fe\u76f8\u8bc6\u3002\u4f46\u7a76\u7adf\u662f\u5728\u4ec0\u4e48\u5730\u65b9\u89c1\u8fc7\u7684\u5462\uff1f\u6025\u5207 \u4e4b\u95f4\uff0c\u5b5f\u660e\u971e\u5374\u662f\u60f3\u4e0d\u8d77\u6765\u4e86\u3002 \u3000\u3000\u6b64\u65f6\u4ea6\u5df2\u4e0d\u5bb9\u5b5f\u660e\u971e\u518d\u60f3\uff0c\u8fd9\u91c7\u82b1\u8d3c\u7aef\u7684\u662f\u5341\u5206\u5389\u5bb3\uff0c\u53cc\u624b\u7a7a\u7a7a\uff0c\u5c45\u7136\u5c31\u6765\u786c\u62a2\u5b5f\u660e\u971e \u7684\u957f\u5251\u3002 \u3000\u3000\u5b5f\u660e\u971e\u89c1\u4ed6\u7a7a\u624b\u5165\u767d\u5203\u7684\u529f\u592b\u751a\u4e3a\u4e86\u5f97\uff0c\u4e0d\u6562\u8f7b\u654c\uff0c\u8fde\u5fd9\u5251\u8d70\u504f\u950b\uff0c\u9752\u5149\u4e00\u95ea\u3001\u5251\u5c16\u76f4 \u523a\u90a3\u91c7\u82b1\u8d3c\u9888\u540e\u810a\u9aa8\u7684\u201c\u5929\u9699\u7a74\u201d\uff0c\u90a3\u91c7\u82b1\u8d3c\u4e00\u4e2a\u201c\u79fb\u5f62\u6362\u4f4d\u201d\uff0c\u5df2\u662f\u7ed5\u5230\u5b5f\u660e\u971e\u4fa7\u9762\uff0c\u559d \u58f0\u201c\u64a4\u5251\uff01\u201d\u53cd\u638c\u5c31\u5207\u5979\u8109\u95e8\u3002 \u3000\u3000\u5b5f\u660e\u971e\u7684\u5bb6\u4f20\u5251\u6cd5\u4ee5\u53d8\u5316\u5947\u8be1\u89c1\u957f\uff0c\u5bf9\u65b9\u7684\u64d2\u62ff\u624b\u6cd5\u867d\u7136\u51cc\u5389\uff0c\u5979\u4e5f\u50b2\u7136\u4e0d\u60e7\u3002\u5f53\u4e0b\u4e00 \u58f0\u51b7\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u89c1\u5f97\uff01\u201d\u5251\u5c16\u4e00\u98a4\uff0c\u5f84\u53d8\u6210\u201c\u6d77\u71d5\u63a0\u6ce2\u201d\u7684\u62db\u654c\uff0c\u659c\u70b9\u5bf9\u65b9\u810a\u9aa8\u7684\u201c\u7cbe \u4fc3\u7a74\u201d\u3002 \u3000\u3000\u8fd9\u4e00\u62db\u201c\u6d77\u71d5\u63a0\u6ce2\u201d\uff0c\u672c\u662f\u5b5f\u660e\u971e\u7684\u5f97\u610f\u6740\u624b\u62db\u6570\uff0c\u6b64\u9645\u53cc\u65b9\u8ddd\u79bb\u751a\u8fd1\uff0c\u5b5f\u660e\u971e\u8fc5\u901f\u523a \u51fa\uff0c\u6ee1\u4ee5\u4e3a\u975e\u4e2d\u4e0d\u53ef\uff0c\u4e0d\u6599\u5c31\u5728\u5979\u523a\u51fa\u4e4b\u65f6\uff0c\u5ffd\u5730\u611f\u5230\u80f8\u53e3\u4e00\u9ebb\uff0c\u5251\u5c16\u867d\u7136\u6cbe\u7740\u5bf9\u65b9\u7684\u8863 \u88f3\uff0c\u5374\u5df2\u662f\u8f6f\u800c\u65e0\u529b\uff01\u8fd9\u91c7\u82b1\u8d3c\u4f1a\u201c\u6cbe\u8863\u5341\u516b\u8dcc\u201d\u7684\u529f\u592b\uff0c\u5251\u5c16\u5c1a\u672a\u523a\u7a7f\u4ed6\u7684\u8863\u88f3\uff0c\u5c31\u6ed1\u5f00 \u4e86\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u53ea\u542c\u5f97\u91c7\u82b1\u8d3c\u7eb5\u58f0\u7b11\u9053\uff1a\u201c\u7f8e\u4eba\u513f\uff0c\u8fc7\u6765\u5427\uff01\u201d\u53cc\u81c2\u7b95\u5f20\uff0c\u4fe8\u4f3c\u5140\u9e70 \u6251\u5154\uff0c\u500f\u5730\u5c31\u5411\u76c2\u660e\u971e\u6293\u4e86\u4e0b\u6765\uff01 \u3000\u3000\u5b5f\u660e\u971e\u5251\u62db\u4f7f\u8001\uff0c\u6025\u5207\u95f4\u54ea\u80fd\u56de\u5251\u9632\u8eab\uff0c\u773c\u770b\u5c31\u8981\u7ed9\u91c7\u82b1\u8d3c\u6293\u7740\uff0c\u5ffd\u542c\u5f97\u201c\u7830\u201d\u7684\u4e00 \u58f0\uff0c\u4e00\u6761\u4eba\u5f71\uff0c\u75be\u5982\u98de\u7bad\u822c\u7684\u5c04\u6765\u3002\u539f\u6765\u662f\u891a\u4e91\u5cf0\u8e22\u5f00\u5927\u95e8\uff0c\u53ca\u65f6\u8d76\u5230\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u7684\u5288\u7a7a\u638c\u529b\u4f7f\u5f97\u5999\u5230\u6beb\u5dc5\uff0c\u4eba\u8fd8\u672a\u5230\uff0c\u638c\u529b\u5df2\u5230\u4e86\u5b5f\u660e\u971e\u8eab\u4e0a\uff0c\u8f7b\u8f7b\u628a\u5979\u63a8\u8fc7\u4e00 \u8fb9\uff0c\u8fc5\u5373\u5c31\u628a\u865a\u62db\u53d8\u5b9e\uff0c\u5411\u90a3\u91c7\u82b1\u8d3c\u75be\u6251\u8fc7\u53bb\u3002 \u3000\u3000\u53cc\u638c\u76f8\u4ea4\uff0c\u53d1\u51fa\u95f7\u96f7\u4f3c\u7684\u4e00\u58f0\u5de8\u54cd\u3002\u91c7\u82b1\u8d3c\u8eab\u5f62\u4e00\u6643\uff0c\u659c\u9000\u4e09\u6b65\u3002\u891a\u4e91\u5cf0\u559d\u9053\uff1a\u201c\u539f\u6765 \u662f\u4f60\uff01\u201d\u91c7\u82b1\u8d3c\u51b7\u7b11\u9053\uff1a\u201c\u662f\u6211\u53c8\u600e\u4e48\u6837\uff1f\u54fc\uff0c\u4f60\u89c1\u4e86\u6211\uff0c\u5c45\u7136\u8fd8\u6562\u65e0\u793c\uff01\u201d \u3000\u3000\u891a\u4e91\u5cf0\u7eb5\u58f0\u7b11\u9053\uff1a\u201c\u4f60\u8d70\u773c\u4e86\uff0c\u4f60\u4ee5\u4e3a\u6211\u662f\u4ec0\u4e48\u4eba\uff1f\u54fc\uff0c\u4f60\u80c6\u6562\u5192\u6211\u4e4b\u540d\u4e3a\u6076\uff0c\u6211\u4e0d\u4f46 \u65e0\u793c\uff0c\u6211\u8fd8\u8981\u6740\u4e86\u4f60\u5462\uff01\u201d \u3000\u3000\u90a3\u91c7\u82b1\u8d3c\u4e5f\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u9519\u4e86\uff0c\u6211\u5192\u4f60\u7684\u540d\u5b57\uff0c\u6b63\u662f\u56e0\u4e3a\u65e9\u5df2\u77e5\u9053\u4f60\u4e0d\u662f\u4e2a\u597d\u4e1c\u897f\u3002\u53ef \u7b11\u4f60\u8fd8\u6d0b\u6d0b\u5f97\u610f\uff0c\u4ee5\u4e3a\u662f\u6211\u8d70\u773c\u4e86\uff1f\u201d\u4e24\u4eba\u53e3\u4e2d\u8bf4\u8bdd\uff0c\u624b\u5e95\u4e1d\u6beb\u4e0d\u7f13\uff0c\u5728\u8bf4\u8fd9\u51e0\u53e5\u8bdd\u7684\u65f6 \u95f4\uff0c\u5df2\u662f\u8fc5\u5373\u8fc7\u4e86\u5341\u4f59\u62db\u3002 \u3000\u3000\u5b5f\u660e\u971e\u95ea\u8fc7\u4e00\u8fb9\uff0c\u53ea\u89c9\u6c14\u5598\u5fc3\u8df3\uff0c\u4e0d\u7981\u5927\u5403\u4e00\u60ca\uff1a\u201c\u600e\u7684\u6211\u4eca\u665a\u6c14\u529b\u5982\u6b64\u4e0d\u6d4e\uff1f\u201d\u672a\u53ca \u4ed4\u7ec6\u601d\u91cf\uff0c\u90a3\u8001\u5a46\u5a46\u53c8\u5728\u201c\u54ce\u54df\uff0c\u54ce\u54df\u201d\u7684\u53eb\u8d77\u75db\u6765\u3002\u8fd9\u51e0\u58f0\u5636\u53eb\uff0c\u53eb\u5f97\u5b5f\u660e\u971e\u5fc3\u70e6\u610f\u4e71\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u4e0e\u90a3\u91c7\u82b1\u8d3c\u8d8a\u6597\u8d8a\u7d27\uff0c\u53cc\u65b9\u5747\u5df2\u6539\u7528\u5175\u5203\u3002\u4f46\u89c1\u5200\u4f3c\u6e38\u9f99\uff0c\u5251\u5982\u98de\u51e4\uff0c\u8d70\u7684\u90fd\u662f \u72e0\u8fa3\u4e4b\u6781\u7684\u62db\u6570\uff0c\u8c01\u4eba\u5076\u4e00\u4e0d\u614e\uff0c\u4fbf\u6709\u8840\u6e85\u5c18\u57c3\u7684\u5371\u9669\uff0c\u6b64\u65f6\u8c01\u4e5f\u4e0d\u6562\u5206\u795e\u8bf4\u8bdd\uff0c\u4f46\u4ecd\u7136\u662f \u891a\u4e91\u5cf0\u7a0d\u5360\u4e00\u70b9\u4e0a\u98ce\u3002 \u3000\u3000\u5b5f\u660e\u971e\u5598\u8fc7\u53e3\u6c14\uff0c\u672c\u6765\u5c31\u60f3\u4e0a\u53bb\u4e0e\u891a\u4e91\u5cf0\u8054\u624b\u7684\uff0c\u542c\u5f97\u90a3\u8001\u5a46\u5a46\u201c\u54ce\u54df\uff0c\u54ce\u54df\u201d\u7684\u8fde\u8fde \u53eb\u75db\uff0c\u970d\u7136\u4e00\u7701\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6211\u600e\u5730\u5982\u6b64\u7cca\u6d82\uff0c\u5e94\u8be5\u5148\u6551\u4e86\u8fd9\u4e24\u4f4d\u8001\u4eba\u5bb6\u624d\u662f\uff01\u201d\u5f53\u4e0b\u8fde \u5fd9\u8fc7\u53bb\u7ed9\u90a3\u8001\u5a46\u5a46\u89e3\u5f00\u675f\u7f1a\u3002 \u3000\u3000\u90a3\u8001\u5a46\u5a46\u662f\u7ed9\u4e94\u82b1\u5927\u7ed1\u7f1a\u5728\u4e00\u5f20\u957f\u6905\u7684\u9760\u80cc\u4e0a\u7684\uff0c\u5b5f\u660e\u971e\u5c0f\u5fc3\u7ffc\u7ffc\u5730\u7ed9\u5979\u5272\u65ad\u7ef3\u5b50\uff0c\u90a3 \u8001\u5a46\u5a46\u542b\u6cea\u8bf4\u9053\uff1a\u201c\u5b5f\u59d1\u5a18\uff0c\u591a\u8c22\u4f60\u5566\u3002\u201d\u5b5f\u660e\u971e\u5341\u5206\u96be\u8fc7\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\uff0c\u90fd\u662f\u6211\u8fde\u7d2f\u4e86\u4f60 \u8001\u4eba\u5bb6\u3002\u201d \u3000\u3000\u4e0d\u6599\u8bdd\u72b9\u672a\u4e86\uff0c\u5ffd\u542c\u5f97\u201c\u5530\u201d\u7684\u4e00\u58f0\uff0c\u53ea\u89c9\u52b2\u98ce\u98d2\u7136\uff0c\u6709\u4e00\u6761\u8f6f\u97ad\u4e4b\u7c7b\u7684\u5175\u5668\uff0c\u5df2\u7ecf\u88ad \u5230\u4e86\u5979\u7684\u80cc\u540e\u3002 \u3000\u3000\u5b5f\u660e\u971e\u5927\u5403\u4e00\u60ca\uff0c\u5e78\u800c\u5979\u662f\u4e2a\u6b66\u5b66\u9887\u6709\u9020\u8be3\u7684\u4eba\uff0c\u867d\u60ca\u4e0d\u4e71\uff0c\u4e00\u89c9\u4e0d\u5999\uff0c\u7acb\u5373\u659c\u8eab\u4e00 \u8dc3\uff0c\u8fd9\u624d\u6ca1\u6709\u7ed9\u8f6f\u97ad\u5377\u7740\u3002\u4f46\u9976\u662f\u5979\u8eb2\u95ea\u5f97\u5982\u6b64\u4e4b\u5feb\uff0c\u80cc\u810a\u8fd8\u662f\u7ed9\u8f6f\u97ad\u62bd\u4e86\u4e00\u4e0b\uff0c\u706b\u8fa3\u8fa3\u7684 \u4f5c\u75db\u3002 \u3000\u3000\u5b5f\u660e\u971e\u53cd\u624b\u4e00\u5251\uff0c\u524a\u65ad\u4e86\u90a3\u4eba\u7684\u4e00\u622a\u8f6f\u97ad\uff0c\u56de\u8fc7\u5934\u6765\uff0c\u770b\u6e05\u695a\u4e86\u90a3\u4e2a\u5411\u5979\u5077\u88ad\u7684\u4eba\u3002\u8fd9 \u4e00\u4e0b\u66f4\u662f\u5413\u5f97\u5b5f\u660e\u971e\u53e3\u5446\u76ee\u77aa\uff0c\u5982\u540c\u505a\u4e86\u4e00\u4e2a\u6076\u68a6\uff01 \u3000\u3000\u4f60\u9053\u8fd9\u4e2a\u5411\u5979\u5077\u88ad\u7684\u4eba\u662f\u8c01\uff1f\u539f\u6765\u5c31\u662f\u90a3\u4e2a\u540c\u60c5\u5979\u3001\u6536\u7559\u5979\uff0c\u800c\u4e14\u4e5f\u66fe\u88ab\u6765\u82b1\u8d3c\u201c\u6bd2 \u6253\u201d\u4e00\u987f\u4e4b\u540e\u7f1a\u8d77\u6765\u4e86\u7684\u8d3a\u4e5d\u516c\uff01 \u3000\u3000\u6b64\u65f6\u8d3a\u4e5d\u516c\u5df2\u662f\u81ea\u5df1\u8131\u7f1a\u800c\u51fa\uff0c\u4ed6\u7528\u6765\u5411\u5b5f\u660e\u971e\u5077\u88ad\u7684\u5175\u5668\uff0c\u539f\u6765\u4e0d\u662f\u8f6f\u97ad\uff0c\u800c\u662f\u672c\u6765 \u7f1a\u5728\u4ed6\u8eab\u4e0a\u7684\u4e00\u6761\u957f\u7ef3\u3002 \u3000\u3000\u6076\u68a6\u5c1a\u672a\u7ed3\u675f\uff0c\u5c31\u5728\u5b5f\u660e\u971e\u60ca\u8be7\u5df2\u6781\uff0c\u4e00\u4e2a\u201c\u4f60\u201d\u5b57\u521a\u521a\u53eb\u51fa\u6765\u7684\u65f6\u5019\uff0c\u9661\u7136\u95f4\u53ea\u89c9\u80a9 \u5934\u5267\u75db\uff0c\u5b5f\u660e\u971e\u4e00\u4e2a\u8e09\u8dc4\uff0c\u51e0\u4e4e\u8dcc\u5012\uff0c\u8033\u8fb9\u53ea\u542c\u5f97\u90a3\u8001\u5a46\u5a46\u72de\u7b11\u9053\uff1a\u201c\u5b5f\u59d1\u5a18\uff0c\u4f60\u4e2d\u8ba1 \u4e86\uff01\u201d \u3000\u3000\u5b5f\u660e\u971e\u8fd9\u624d\u77e5\u9053\u8d3a\u4e5d\u516c\u8fd9\u4e00\u5bf9\u592b\u59bb\uff0c\u679c\u7136\u662f\u90a3\u91c7\u82b1\u8d3c\u7684\u540c\u515a\u3002\u8fd9\u8001\u5a46\u5a46\u7684\u8870\u8fc8\u795e\u6001\u5b8c\u5168 \u662f\u88c5\u51fa\u6765\u7684\uff0c\u5979\u5c82\u53ea\u61c2\u5f97\u6b66\u529f\uff0c\u800c\u4e14\u662f\u4e2a\u9887\u4e3a\u5389\u5bb3\u7684\u9ad8\u624b\u3002\u81f3\u4e8e\u8d3a\u4e5d\u516c\u7684\u672c\u9886\uff0c\u8fdc\u8fdc\u5728\u5979\u4f30 \u8ba1\u4e4b\u4e0a\uff0c\u90a3\u662f\u66f4\u4e0d\u7528\u8bf4\u4e86\u3002 \u3000\u3000\u5b5f\u660e\u971e\u53c8\u60ca\u53c8\u6012\uff0c\u60f3\u4e0d\u5230\u4eba\u5fc3\u9669\u6076\uff0c\u7adf\u81f3\u5982\u65af\uff01\u6b64\u65f6\u5979\u7684\u53f3\u80a9\u7740\u4e86\u90a3\u8001\u5a46\u5a46\u7684\u4e00\u6293\uff0c\u8fd8 \u5e78\u6ca1\u6709\u6293\u788e\u7435\u7436\u9aa8\uff0c\u4f46\u4e00\u6761\u81c2\u818a\u5df2\u662f\u9ebb\u6728\u4e0d\u7075\u3002 \u3000\u3000\u5b5f\u660e\u971e\u5927\u6012\u4e4b\u4e0b\uff0c\u5251\u4ea4\u5de6\u624b\uff0c\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u4e2a\u8001\u8d3c\u5f53\u771f\u662f\u8499\u7740\u4eba\u76ae\u7684\u6076\u72fc\uff0c\u597d\uff0c\u53ea\u8981\u4f60 \u4eec\u6740\u4e0d\u4e86\u6211\uff0c\u6211\u5c31\u975e\u6740\u4e86\u4f60\u4eec\u4e0d\u53ef\uff01\u201d \u3000\u3000\u8d3a\u4e5d\u516c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5b5f\u59d1\u5a18\uff0c\u4f60\u8fd8\u60f3\u548c\u6211\u4eec\u62fc\u547d\u5417\uff1f\u563f\u563f\uff0c\u53ea\u6015\u4f60\u662f\u6709\u5fc3\u65e0\u529b\u7684\u4e86\uff01\u4e0d \u4fe1\u4f60\u5c31\u8bd5\u8bd5\uff01\u201d \u3000\u3000\u5b5f\u660e\u971e\u4e00\u5251\u523a\u51fa\uff0c\u679c\u7136\u662f\u529b\u4e0d\u4ece\u5fc3\uff0c\u8fd9\u4e00\u62db\u540d\u4e3a\u201c\u9f99\u95e8\u4e09\u53e0\u6d6a\u201d\uff0c\u4e00\u62db\u4e09\u5f0f\uff0c\u672c\u6765\u5e94\u8be5 \u8fde\u53d1\u4e09\u91cd\u52b2\u9053\u7684\uff0c\u7ed3\u679c\u53ea\u53d1\u5230\u7b2c\u4e8c\u91cd\uff0c\u7b2c\u4e09\u91cd\u4fbf\u4f7f\u4e0d\u51fa\u6765\u3002\u8d3a\u4e5d\u516c\u6325\u821e\u957f\u7ef3\uff0c\u5f53\u4f5c\u8f6f\u97ad\u6765 \u4f7f\uff0c\u53ea\u542c\u5f97\u201c\u567c\u556a\u201d\u58f0\u54cd\uff0c\u5b5f\u660e\u971e\u5df2\u662f\u7740\u4e86\u4e24\u97ad\uff0c\u8d3a\u4e5d\u516c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5982\u4f55\uff1f\u201d \u3000\u3000\u539f\u6765\u5728\u5b5f\u660e\u971e\u98df\u7684\u90a3\u7897\u9762\u4e2d\uff0c\u8d3a\u4e5d\u516c\u5df2\u7ecf\u4e0b\u4e86\u4e00\u79cd\u836f\u7269\uff0c\u8fd9\u79cd\u836f\u7269\u65e0\u8272\u65e0\u5473\uff0c\u5374\u6709\u9165\u7b4b \u8f6f\u9aa8\u7684\u529f\u6548\uff0c\u5f53\u65f6\u4e0d\u4f1a\u5bdf\u89c9\uff0c\u8981\u8fc7\u4e00\u4e2a\u65f6\u8fb0\u65b9\u59cb\u53d1\u4f5c\u51fa\u6765\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u53eb\u9053\uff1a\u201c\u5b5f\u59d1\u5a18\uff0c\u6c89\u4f4f\u4e86\u6c14\uff0c\u4e0d\u53ef\u52a8\u6012\uff01\u201d\u53e3\u91cc\u8bf4\u8bdd\uff0c\u624b\u4e2d\u5df2\u662f\u5530\u7684\u4e00\u5251\u523a\u51fa\uff0c \u5f84\u523a\u90a3\u91c7\u82b1\u8d3c\u7684\u9762\u4e0a\u53cc\u775b\u3002\u8fd9\u4e00\u62db\u7528\u5f97\u51f6\u9669\u4e4b\u6781\uff0c\u91c7\u82b1\u8d3c\u8eab\u5f62\u4e00\u4fa7\uff0c\u8fd8\u4e86\u4e00\u62db\u201c\u4e3e\u706b\u64a9 \u5929\u201d\uff0c\u5251\u5c16\u4e0a\u6307\uff0c\u523a\u5411\u891a\u4e91\u5cf0\u7684\u5c0f\u8179\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u891a\u4e91\u5cf0\u8d81\u7740\u5bf9\u65b9\u907f\u62db\u4e4b\u9645\uff0c\u7acb\u5373\u98de\u8eab\u8dc3\u8d77\uff0c\u8eab\u5f62\u540e\u7eb5\uff0c\u53ea\u542c\u5f97\u201c\u55e4\u201d \u7684\u4e00\u58f0\u8f7b\u54cd\uff0c\u891a\u4e91\u5cf0\u7684\u8863\u88f3\u4e0b\u6446\uff0c\u7ed9\u90a3\u91c7\u82b1\u8d3c\u524a\u53bb\u4e86\u4e00\u5e45\uff0c\u4f46\u891a\u4e91\u5cf0\u5374\u5df2\u8dc3\u5230\u5b5f\u660e\u971e\u7684\u8eab \u65c1\u3002\u891a\u4e91\u5cf0\u811a\u6b65\u672a\u7a33\uff0c\u5251\u62db\u5df2\u5148\u653b\u51fa\uff0c\u4ed6\u7684\u80cc\u540e\u5c31\u50cf\u957f\u7740\u773c\u775b\u4e00\u6837\uff0c\u53cd\u624b\u4e00\u5251\uff0c\u767b\u65f6\u6296\u8d77\u4e86 \u4e09\u6735\u5251\u82b1\uff0c\u6307\u5411\u90a3\u8001\u5a46\u5a46\u7684\u4e09\u5904\u8981\u8109\u7a74\u9053\uff0c\u8001\u5a46\u5a46\u5927\u5403\u4e00\u60ca\uff0c\u53ea\u597d\u53d8\u4f5c\u4e86\u201c\u6eda\u5730\u846b\u82a6\u201d\uff0c\u4f0f \u5728\u5730\u4e0a\uff0c\u63a5\u8fde\u6253\u4e86\u51e0\u4e2a\u6eda\uff0c\u65b9\u59cb\u907f\u5f00\u4e86\u891a\u4e91\u5cf0\u5251\u52bf\u7684\u7b3c\u7f69\u3002 \u3000\u3000\u8d3a\u4e5d\u516c\u559d\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u8fd8\u6562\u901e\u5f3a\uff1f\u201d\u891a\u4e91\u5cf0\u51b7\u7b11\u9053\uff1a\u201c\u4e3a\u4f55\u4e0d\u6562\uff1f\u8c05\u4f60\u8fd9\u8001\u8d3c\u4e5f\u7559\u4e0d \u4f4f\u6211\uff01\u201d\u52a8\u4f5c\u5feb\u5982\u95ea\u7535\uff0c\u4e00\u62db\u903c\u9000\u4e86\u90a3\u8001\u5a46\u5a46\uff0c\u5df2\u662f\u8fde\u4eba\u5e26\u5251\uff0c\u5316\u4f5c\u4e86\u4e00\u9053\u94f6\u5149\uff0c\u653b\u8fdb\u4e86\u8d3a \u4e5d\u516c\u7684\u957f\u7ef3\u98de\u821e\u6240\u56f4\u6210\u7684\u5708\u5b50\uff01 \u3000\u3000\u5251\u5149\u7d22\u5f71\u4e4b\u4e2d\uff0c\u53ea\u89c1\u4e00\u6bb5\u6bb5\u9ed1\u5ffd\u5ffd\u7684\u4e1c\u897f\u56db\u65b9\u98de\u51fa\uff0c\u539f\u6765\u5728\u8fd9\u77ac\u606f\u4e4b\u95f4\uff0c\u8d3a\u4e5d\u516c\u90a3\u6761\u4e00 \u4e08\u591a\u957f\u7684\u7ef3\u7d22\uff0c\u5df2\u662f\u7ed9\u891a\u4e91\u5cf0\u524a\u6210\u4e86\u5341\u4e03\u516b\u6bb5\uff0c\u53ea\u5269\u4e0b\u4e09\u5c3a\u4e0d\u5230\u4e86\uff01\u8d3a\u4e5d\u516c\u672c\u662f\u628a\u8fd9\u6761\u957f\u7ef3 \u5f53\u4f5c\u8f6f\u97ad\u4f7f\u7684\uff0c\u6b64\u65f6\u957f\u7ef3\u53d8\u4f5c\u4e86\u8fde\u7f1a\u4e1c\u897f\u4e5f\u4e0d\u591f\u7528\u7684\u77ed\u77ed\u4e00\u622a\uff0c\u8f6f\u97ad\u7684\u529f\u7528\u5982\u4f55\u8fd8\u80fd\u53d1\u6325\uff1f \u518d\u4e0d\u7f29\u624b\uff0c\u53ea\u6015\u5c31\u8981\u7ed9\u891a\u4e91\u5cf0\u524a\u5230\u624b\u6307\u4e86\u3002 \u3000\u3000\u8d3a\u4e5d\u516c\u5413\u5f97\u9b42\u98de\u9b44\u6563\uff0c\u8fde\u5fd9\u8df3\u5f00\u3002\u91c7\u82b1\u8d3c\u6b64\u65f6\u65b9\u59cb\u653b\u5230\uff0c\u891a\u4e91\u5cf0\u6a2a\u5251\u62a4\u80f8\uff0c\u5de6\u624b\u62c9\u7740\u5b5f \u660e\u971e\u4fbf\u8dd1\u3002\u53cc\u5251\u76f8\u4ea4\uff0c\u201c\u94ee\u201d\u7684\u4e00\u58f0\u54cd\uff0c\u891a\u4e91\u5cf0\u7684\u957f\u5251\u7adf\u7ed9\u8361\u5f00\uff0c\u80a9\u5934\u7740\u4e86\u4e00\u5251\u3002 \u3000\u3000\u539f\u6765\u891a\u4e91\u5cf0\u7684\u672c\u9886\u4e0d\u8fc7\u6bd4\u90a3\u91c7\u82b1\u8d3c\u7a0d\u80dc\u4e00\u7b79\uff0c\u6b64\u65f6\u56e0\u8981\u5206\u51fa\u4e00\u81c2\u4e4b\u529b\u76f8\u52a9\u5b5f\u660e\u971e\u9003\u8dd1\uff0c \u6545\u800c\u5728\u8fd9\u4e00\u62db\u5c31\u4e0d\u80fd\u4e0d\u5403\u4e86\u70b9\u4e8f\u3002\u4f46\u867d\u7136\u5982\u6b64\uff0c\u90a3\u91c7\u82b1\u8d3c\u7684\u8109\u95e8\u4e5f\u9669\u4e9b\u7ed9\u891a\u4e91\u5cf0\u5212\u4f24\uff0c\u91c7\u82b1 \u8d3c\u5403\u4e86\u4e00\u60ca\uff0c\u672a\u53ca\u6362\u62db\u52c7\u653b\uff0c\u891a\u4e91\u5cf0\u5df2\u662f\u5e26\u7740\u5b5f\u660e\u971e\u95ef\u51fa\u4e86\u5927\u95e8\u3002\u8d3a\u4e5d\u516c\u592b\u59bb\u7ed9\u4ed6\u6740\u5f97\u6015 \u4e86\uff0c\u90fd\u662f\u4e0d\u6562\u62e6\u963b\u3002\u891a\u4e91\u5cf0\u95ef\u51fa\u4e86\u5927\u95e8\uff0c\u5ffd\u5730\u8eab\u5f62\u4e00\u77ee\uff0c\u53cd\u624b\u63fd\u7740\u5b5f\u660e\u971e\u7684\u67f3\u8170\u3002 \u3000\u3000\u5b5f\u660e\u971e\u7ed9\u4ed6\u7a81\u7136\u4e00\u62b1\uff1b\u4e0d\u89c9\u6115\u7136\u3002\u891a\u4e91\u5cf0\u8eab\u8eaf\u4e00\u77ee\uff0c\u628a\u5b5f\u660e\u971e\u80cc\u4e86\u8d77\u6765\uff0c\u6025\u58f0\u8bf4\u9053\uff1a \u201c\u6293\u7d27\u6211\u7684\u80a9\u5934\uff01\u201d\u5b5f\u660e\u971e\u8fd9\u624d\u77e5\u9053\uff0c\u891a\u4e91\u5cf0\u662f\u8981\u80cc\u7740\u5979\u8dd1\u3002 \u3000\u3000\u5b5f\u660e\u971e\u672c\u6765\u662f\u4e2a\u6027\u60c5\u8c6a\u8fc8\u7684\u5973\u4e2d\u8c6a\u6770\uff0c\u6b64\u9645\u5979\u81ea\u77e5\u5df2\u662f\u4e0d\u80fd\u65bd\u5c55\u8f7b\u529f\uff0c\u4e5f\u53ea\u597d\u4e0d\u907f\u7537\u5973 \u4e4b\u5acc\u4e86\u3002\u4f46\u5979\u6709\u751f\u4ee5\u6765\uff0c\u4ece\u672a\u66fe\u6709\u8fc7\u4e0e\u4e00\u4e2a\u7537\u5b50\u5982\u6b64\u4eb2\u8fd1\uff0c\u4f0f\u5728\u891a\u4e91\u5cf0\u7684\u80cc\u4e0a\uff0c\u4ecd\u662f\u4e0d\u7981\u9762 \u7ea2\u8033\u70ed\uff0c\u4e00\u9897\u82b3\u5fc3\u535c\u535c\u4e71\u8df3\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u7684\u5de6\u80a9\u53d7\u4e86\u5251\u4f24\uff0c\u5e78\u4e8f\u53ea\u662f\u4f24\u7740\u76ae\u8089\uff0c\u5e76\u65e0\u5927\u788d\uff0c\u4e0d\u8fc7\u4ea6\u662f\u9c9c\u8840\u6dcb\u6f13\u7684\u4e86\u3002\u5b5f\u660e \u971e\u4e0d\u6562\u78b0\u7740\u4ed6\u7684\u4f24\u53e3\uff0c\u53ea\u80fd\u6293\u7d27\u4ed6\u672a\u53d7\u4f24\u7684\u53f3\u80a9\uff0c\u5fc3\u4e2d\u53c8\u662f\u611f\u6fc0\uff0c\u53c8\u662f\u7f9e\u6127\u3002\u611f\u6fc0\u7684\u662f\u891a\u4e91 \u5cf0\u4e0d\u987e\u53d7\u4f24\uff0c\u6551\u5979\u6027\u547d\u3002\u7f9e\u6127\u7684\u662f\u81ea\u5df1\u521a\u624d\u4e0d\u76f8\u4fe1\u4ed6\u7684\u8bf4\u8bdd\uff0c\u6ca1\u6709\u542c\u4ece\u4ed6\u7684\u89c4\u529d\uff0c\u4ece\u901f\u79bb \u5f00\uff0c\u4ee5\u81f4\u81ea\u5df1\u53d7\u4e86\u8d3a\u4e5d\u516c\u7684\u6697\u7b97\uff0c\u8fd8\u8fde\u7d2f\u4ed6\u4e5f\u53d7\u4e86\u4f24\u3002 \u3000\u3000\u90a3\u91c7\u82b1\u8d3c\u8ffd\u4e86\u51fa\u6765\uff0c\u891a\u4e91\u5cf0\u80cc\u7740\u5b5f\u660e\u971e\u521a\u521a\u8df3\u4e0a\u74e6\u80cc\uff0c\u91c7\u82b1\u8d3c\u558a\u9053\uff1a\u201c\u8fd8\u60f3\u8dd1\u4e48\uff0c\u4e0b\u6765 \u5427\uff01\u201d\u547c\u7684\u4e00\u638c\u5288\u51fa\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u7684\u5929\u96f7\u529f\u8fd8\u5dee\u51e0\u5206\u706b\u5019\uff0c\u56de\u53bb\u518d\u7ec3\u4e24\u5e74\u5427\uff01\u201d\u4e00\u4e2a\u5728\u5c4b\u9876\uff0c\u4e00\u4e2a\u5728 \u5730\u4e0a\uff0c\u53c8\u7528\u5288\u7a7a\u638c\u8f83\u91cf\u4e0a\u4e86\u3002\u638c\u529b\u6fc0\u8361\u4e4b\u4e0b\uff0c\u5c4b\u9876\u5f00\u4e86\u4e00\u4e2a\u5929\u7a97\uff0c\u788e\u88c2\u4e86\u5341\u51e0\u5757\u7816\u74e6\u3002\u4f46\u891a \u4e91\u5cf0\u5374\u6ca1\u6709\u8dcc\u4e0b\uff0c\u800c\u662f\u8df3\u8fc7\u4e86\u53e6\u4e00\u5ea7\u6c11\u623f\u3002\u5012\u662f\u90a3\u91c7\u82b1\u8d3c\u7ed9\u4ed6\u7684\u638c\u529b\u4e00\u9707\uff0c\u4e0d\u7531\u81ea\u5df1\u5730\u8e09\u8dc4 \u540e\u9000\uff0c\u80f8\u53e3\u597d\u50cf\u7ed9\u5de8\u9524\u4e00\u51fb\u4f3c\u7684\uff0c\u51e0\u4e4e\u5012\u4e0b\u3002 \u3000\u3000\u91c7\u82b1\u8d3c\u5927\u9646\u4e00\u60ca\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u53ae\u7684\u5929\u96f7\u529f\u679c\u7136\u662f\u6bd4\u6211\u9ad8\u660e\uff0c\u867d\u7136\u4ed6\u5df2\u53d7\u4f24\uff0c\u6211\u5b64\u8eab \u8dd1\u53bb\u8ffd\u4ed6\uff0c\u53ea\u6015\u4e5f\u672a\u5fc5\u80fd\u591f\u7a33\u64cd\u80dc\u7b97\u3002\u201d\u8981\u77e5\u8d3a\u4e5d\u516c\u592b\u59bb\u7684\u672c\u9886\u867d\u7136\u4e0d\u5f31\uff0c\u4f46\u8f7b\u529f\u5374\u4e0e\u891a\u4e91 \u5cf0\u76f8\u5dee\u751a\u8fdc\u3002\u82e5\u5728\u5c4b\u5185\u640f\u6597\uff0c\u4ed6\u4eec\u592b\u59bb\u867d\u7136\u6597\u4e0d\u8fc7\u891a\u4e91\u5cf0\uff0c\u8fd8\u53ef\u4ee5\u505a\u8fd9\u91c7\u82b1\u8d3c\u7684\u5e2e\u624b\u3002\u4e00\u5230 \u891a\u4e91\u5cf0\u95ef\u4e86\u51fa\u53bb\uff0c\u4ed6\u4eec\u53ef\u5c31\u5e2e\u4e0d\u4e0a\u8fd9\u91c7\u82b1\u8d3c\u7684\u5fd9\u4e86\u3002\u8fd9\u91c7\u82b1\u8d3c\u602f\u610f\u4e00\u751f\uff0c\u53ea\u597d\u773c\u7741\u7741\u5730\u770b\u7740 \u891a\u4e91\u5cf0\u80cc\u7740\u5b5f\u660e\u971e\u9003\u8d70\u3002 \u3000\u3000\u5b5f\u660e\u971e\u53ea\u6562\u6293\u7740\u891a\u4e91\u5cf0\u672a\u53d7\u4f24\u7684\u53f3\u80a9\uff0c\u5728\u891a\u4e91\u5cf0\u8df3\u8fc7\u53e6\u4e00\u95f4\u6c11\u623f\u7684\u65f6\u5019\uff0c\u51e0\u4e4e\u7ed9\u629b\u4e86\u4e0b \u53bb\u3002\u891a\u4e91\u5cf0\u89c9\u5bdf\uff0c\u8fde\u5fd9\u8bf4\u9053\uff1a\u201c\u5b5f\u59d1\u5a18\uff0c\u4f60\u4e0d\u5fc5\u987e\u5fcc\uff0c\u6211\u7684\u4f24\u5e76\u4e0d\u91cd\u3002\u201d\u5b5f\u660e\u971e\u5927\u4e3a\u611f\u52a8\uff0c \u8bf4\u9053\uff1a\u201c\u891a\u5927\u54e5\uff0c\u6211\u771f\u4e0d\u8be5\uff0c\u6211\u3001\u6211\u4e00\u76f4\u5728\u7591\u5fc3\u4f60\uff0c\u5374\u539f\u6765\u4f60\u662f\u8fd9\u6837\u7684\u597d\u4eba\uff01\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u73b0\u5728\u4e0d\u662f\u8bb2\u5ba2\u6c14\u8bdd\u7684\u65f6\u5019\uff0c\u5feb\uff0c\u5feb\u6293\u7d27\u6211\u7684\u80a9\u5934\u3002\u201d\u90a3\u91c7\u82b1\u8d3c\u867d\u7136\u4e0d\u6562\u8ffd \u6765\uff0c\u4f46\u891a\u4e91\u5cf0\u5374\u4e0d\u80fd\u4e0d\u63d0\u9632\u4ed6\u6765\u8ffd\u8d76\u3002 \u3000\u3000\u5b5f\u660e\u971e\u638f\u51fa\u91d1\u521b\u836f\uff0c\u4f0f\u5728\u4ed6\u7684\u80cc\u4e0a\uff0c\u7ed9\u4ed6\u6577\u4e0a\u4f24\u53e3\uff0c\u8fd9\u624d\u6562\u5c0f\u5fc3\u7ffc\u7ffc\u5730\u6293\u7740\u4ed6\u7684\u53cc\u80a9\uff0c \u907f\u514d\u78b0\u7740\u4ed6\u7684\u4f24\u53e3\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u8dd1\u51fa\u53bf\u57ce\uff0c\u89c1\u90a3\u91c7\u82b1\u8d3c\u6ca1\u6709\u8ffd\u6765\uff0c\u8fd9\u624d\u653e\u4e0b\u4e86\u5fc3\u3002\u5f53\u4e0b\u628a\u5b5f\u660e\u971e\u80cc\u5230\u6797\u4e2d\uff0c\u627e\u4e86 \u4e00\u4e2a\u9690\u853d\u7684\u5730\u65b9\u5750\u4e0b\uff0c\u6b64\u65f6\u5df2\u662f\u4e1c\u65b9\u5927\u767d\u7684\u6e05\u6668\u4e86\u3002\u6b63\u662f\uff1a \u3000\u3000\u4e16\u6545\u672a\u6df1\u906d\u6697\u7b97\uff0c\u6700\u96be\u731c\u6d4b\u662f\u4eba\u5fc3\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u516b\u56de\u3000\u5404\u81ea\u6709\u60c5\u6210\u7737\u5c5e\u3000\u672a\u77e5\u4f55\u5904\u89c5\u88d9\u94ec \u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b \u7b2c\u4e8c\u5341\u516b\u56de\u3000\u5404\u81ea\u6709\u60c5\u6210\u7737\u5c5e\u3000\u672a\u77e5\u4f55\u5904\u89c5\u88d9\u94ec \u3000\u3000\u5b5f\u660e\u971e\u89c1\u891a\u4e91\u5cf0\u6ee1\u5934\u5927\u6c57\uff0c\u597d\u751f\u8fc7\u610f\u4e0d\u53bb\uff0c\u8bf4\u9053\uff1a\u201c\u891a\u5927\u54e5\uff0c\u6211\u4e0d\u80fd\u8dd1\u8def\uff0c\u7d2f\u4e86\u4f60\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u90a3\u53ae\u5047\u5192\u6211\u7684\u540d\u5b57\uff0c\u505a\u51fa\u4e0b\u4e09\u6ee5\u7684\u4e8b\u60c5\uff0c\u5373\u4f7f\u6ca1\u6709\u4eca\u665a\u4e4b\u4e8b\uff0c\u6211\u4e5f\u7edd\u4e0d\u4f1a \u653e\u8fc7\u4ed6\u7684\u3002\u4f60\u6211\u90fd\u662f\u4ece\u7405\u739b\u5c71\u4e0b\u6765\u7684\u4eba\uff0c\u4f60\u518d\u548c\u6211\u5ba2\u6c14\uff0c\u90a3\u5c31\u662f\u4e0d\u628a\u6211\u5f53\u4f5c\u81ea\u5df1\u4eba\u4e86\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u597d\uff0c\u5927\u6069\u4e0d\u8a00\u8c22\uff0c\u4f46\u6211\u6b66\u529f\u6062\u590d\u4e0d\u4e86\uff0c\u5c82\u4e0d\u662f\u4e00\u76f4\u8981\u62d6\u7d2f\u4f60\uff0c\u8fd9\u53ef\u5982\u4f55\u662f \u597d\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u4f60\u8bd5\u4e00\u8bd5\uff0c\u53ef\u8fd8\u80fd\u591f\u51dd\u805a\u771f\u6c14\u4e48\uff1f\u201d\u5b5f\u660e\u971e\u9053\uff1a\u201c\u8bd5\u8fc7\u4e86\uff0c\u771f\u6c14\u5012\u662f\u8fd8\u80fd \u8fd0\u884c\uff0c\u4f46\u5374\u611f\u5230\u9aa8\u8f6f\u7b4b\u9165\uff0c\u4f7f\u4e0d\u51fa\u529b\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u771f\u6c14\u80fd\u591f\u8fd0\u884c\uff0c\u90a3\u5c31\u66f4\u597d\u4e86\u3002\u6211\u6709\u4e00\u9897\u5c11\u6797\u5bfa\u8d60\u7ed9\u5bb6\u5e08\u7684\u5c0f\u8fd8\u4e39\uff0c\u867d\u7136\u4e0d \u662f\u89e3\u9165\u9aa8\u6563\u7684\u5bf9\u75c7\u89e3\u836f\uff0c\u4f46\u529f\u80fd\u9a71\u90aa\u53bb\u6bd2\uff0c\u56fa\u672c\u57f9\u539f\u3002\u4f60\u670d\u4e86\u6b64\u836f\uff0c\u6211\u4ee5\u672c\u8eab\u771f\u6c14\u52a9\u4f60\u63a8\u8840 \u8fc7\u5bab\uff0c\u8ba9\u836f\u529b\u53ef\u4ee5\u5145\u5206\u53d1\u6325\uff0c\u6211\u60f3\u5927\u7ea6\u7528\u4e0d\u4e86\u4e00\u4e2a\u65f6\u8fb0\uff0c\u4f60\u5c31\u53ef\u4ee5\u597d\u4e86\u3002\u201d \u3000\u3000\u201c\u63a8\u8840\u8fc7\u5bab\u201d\uff0c\u5bf9\u65b9\u7684\u624b\u638c\u662f\u8981\u548c\u81ea\u5df1\u7684\u8eab\u4f53\u63a5\u89e6\u7684\uff0c\u4f46\u5b5f\u660e\u971e\u521a\u624d\u5df2\u7ecf\u4f0f\u5728\u4ed6\u7684\u80cc \u4e0a\uff0c\u8ba9\u4ed6\u80cc\u7740\u8dd1\u4e86\u8fd9\u8bb8\u591a\u8def\uff0c\u6b64\u65f6\u5f53\u7136\u4e5f\u4e0d\u518d\u987e\u5fcc\u8fd9\u70b9\u7537\u5973\u4e4b\u5acc\u3002\u4f46\u867d\u7136\u5982\u6b64\uff0c\u5f53\u53cc\u65b9\u7684\u624b \u638c\u7d27\u7d27\u76f8\u63e1\u4e4b\u65f6\uff0c\u4e5f\u8fd8\u662f\u4e0d\u514d\u6709\u4e9b\u513f\u5f02\u6837\u7684\u611f\u89c9\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u7684\u6b66\u529f\uff0c\u5dee\u4e0d\u591a\u5df2\u662f\u4e00\u6d41\u9ad8\u624b\u7684\u5883\u754c\uff0c\u5b5f\u660e\u971e\u670d\u4e86\u5c0f\u8fd8\u4e39\uff0c\u5f97\u4ed6\u63a8\u8840\u8fc7\u5bab\uff0c\u679c\u7136 \u89c1\u6548\u6781\u5feb\uff0c\u7ed3\u679c\u5c1a\u672a\u5230\u4e00\u4e2a\u65f6\u8fb0\uff0c\u5b5f\u660e\u971e\u5df2\u662f\u7cbe\u795e\u590d\u632f\uff0c\u8bd5\u8bd5\u62f3\u811a\uff0c\u529f\u529b\u5df2\u7ecf\u6062\u590d\u4e86\u4e03\u516b \u5206\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u8d5e\u9053\uff1a\u201c\u5b5f\u59d1\u5a18\uff0c\u4f60\u7684\u5185\u529f\u5982\u6b64\u7eaf\u539a\uff0c\u771f\u4e0d\u6127\u662f\u5b5f\u5927\u4fa0\u7684\u5973\u513f\u3002\u82e5\u662f\u6362\u4e86\u522b\u4eba\uff0c \u7740\u4e86\u9165\u9aa8\u6563\u7684\u6bd2\uff0c\u54ea\u91cc\u8fd8\u80fd\u591f\u8fd0\u884c\u771f\u6c14\uff1f\u201d \u3000\u3000\u5b5f\u660e\u971e\u7b11\u9053\uff1a\u201c\u6211\u8fd8\u6ca1\u6709\u591a\u8c22\u4f60\uff0c\u4f60\u5012\u5938\u5956\u8d77\u6211\u6765\u4e86\u3002\u597d\uff0c\u73b0\u5728\u54b1\u4eec\u53ef\u4ee5\u8c08\u8c08\u6b63\u4e8b\u4e86\uff0c \u6211\u6709\u51e0\u4e2a\u7591\u56e2\uff0c\u60f3\u8981\u5411\u4f60\u8bf7\u6559\u3002\u9996\u5148\u6211\u60f3\u77e5\u9053\u7684\u662f\uff1a\u90a3\u91c7\u82b1\u8d3c\u662f\u8c01\uff1f\u542c\u4f60\u7684\u53e3\u6c14\uff0c\u4ed6\u548c\u4f60\u4f3c \u4e4e\u5f88\u662f\u637b\u719f\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u7b11\u9053\uff1a\u201c\u5c82\u53ea\u637b\u719f\uff0c\u4ed6\u8fd8\u662f\u6211\u7684\u540c\u95e8\u5e08\u5144\u5f1f\u5462\uff01\u4ed6\u7684\u7236\u4eb2\u662f\u6211\u7684\u5927\u5e08\u4f2f\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u4f60\u7684\u5927\u5e08\u4f2f\u662f\u8c01\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u5c31\u662f\u76ee\u524d\u8eab\u4e3a\u91d1\u56fd\u56fd\u5e08\u7684\u9633\u5929\u96f7\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u539f\u6765\u5c31\u662f\u9633\u5929\u96f7\u5440\uff01\u6697\u6740\u5c60\u767e\u57ce\u7684\u51f6\u624b\uff0c\u5c60\u51e4\u5df2\u7ecf\u67e5\u660e\uff0c\u4e5f \u5c31\u662f\u4ed6\uff01\u6b64\u4e8b\u4f60\u53ef\u77e5\u9053\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u6211\u8fd8\u77e5\u9053\u4ed6\u4e00\u9762\u505a\u91d1\u56fd\u7684\u56fd\u5e08\uff0c\u4e00\u9762\u53c8\u79c1\u901a\u8499\u53e4\u5462\u3002\u53bb\u5e74\u4ed6\u66fe\u628a\u81ea\u5df1\u7684\u4f84 \u5b50\u6d3e\u5f80\u8499\u53e4\uff0c\u4ed6\u8fd9\u4e2a\u4f84\u5b50\u540d\u53eb\u9633\u575a\u767d\uff0c\u5c31\u662f\u4f60\u6628\u665a\u6240\u89c1\u7684\u90a3\u4e2a\u91c7\u82b1\u8d3c\u4e86\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u604d\u7136\u5927\u609f\uff0c\u8bf4\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u662f\u4ed6\uff0c\u602a\u4e0d\u5f97\u6211\u89c9\u5f97\u4f3c\u66fe\u76f8\u8bc6\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u8be7\u9053\uff1a\u201c\u4f60\u66fe\u7ecf\u89c1\u8fc7\u4ed6\u7684\uff1f\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u9633\u575a\u767d\u8fd9\u53ae\u56de\u6765\u7684\u65f6\u5019\uff0c\u662f\u4e0d\u662f\u8fd8\u6709\u4e2a\u8499\u53e4\u6b66\u58eb\u966a\u7740\u4ed6\u7684\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u8499\u53e4\u6b66\u58eb\u540d\u53eb\u52ff\u5962\uff0c\u4e43\u662f\u6210\u5409\u601d\u6c57\u624b\u4e0b\u4e09\u5341\u516d\u540d\u91d1\u5e10\u6b66\u58eb\u4e4b\u4e00\uff0c \u6211\u77e5\u9053\u8fd9\u4e2a\u6d88\u606f\uff0c\u66fe\u7ecf\u60f3\u53bb\u4fa6\u5bdf\u4ed6\u4eec\u7684\u884c\u8e2a\uff0c\u8ddf\u5f97\u4e0d\u7d27\uff0c\u4e2d\u9014\u5931\u4e86\u4ed6\u4eec\u7684\u53bb\u5411\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u8fd9\u5c31\u5bf9\u4e86\u3002\u201d\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u4f60\u5728\u54ea\u91cc\u78b0\u4e0a\u4ed6\u4eec\uff1f\u201d\u5b5f\u660e\u971e\u9053\uff1a\u201c\u5728\u674e\u76df\u4e3b \u7684\u5bb6\u4e61\u3002\u201d\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u54e6\uff0c\u6211\u660e\u767d\u4e86\u3002\u674e\u76df\u4e3b\u9003\u51fa\u548c\u6797\uff0c\u4ed6\u4eec\u60f3\u5fc5\u662f\u5949\u4e86\u6210\u5409\u601d\u6c57\u4e4b\u547d\uff0c \u524d\u6765\u6697\u7b97\u674e\u601d\u5357\u7684\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u636e\u674e\u76df\u4e3b\u8bf4\uff0c\u90a3\u8499\u53e4\u6b66\u58eb\u662f\u60f3\u5230\u4ed6\u5bb6\u91cc\u5077\u4e00\u672c\u5175\u4e66\u7684\uff0c\u9633\u575a\u767d\u8fd9\u53ae\u7684\u52a8\u673a \u5982\u4f55\u5219\u4e0d\u5f97\u800c\u77e5\u3002\u90a3\u665a\uff0c\u8499\u53e4\u6b66\u58eb\u5077\u5165\u674e\u5bb6\uff0c\u9633\u575a\u767d\u5728\u5916\u9762\u628a\u98ce\uff0c\u6211\u6070\u597d\u4e5f\u5728\u90a3\u5929\u665a\u4e0a\u53bb\u627e \u674e\u76df\u4e3b\uff0c\u9ed1\u591c\u91cc\u5728\u6811\u6797\u4e2d\u548c\u4ed6\u52a8\u8d77\u624b\u6765\uff0c\u51e0\u4e4e\u4e3a\u4ed6\u6240\u8d25\uff0c\u5e78\u4e8f\u674e\u76df\u4e3b\u5728\u51fb\u5012\u4e86\u90a3\u4e2a\u8499\u53e4\u6b66\u58eb \u4e4b\u540e\uff0c\u53ca\u65f6\u8d76\u5230\uff0c\u8fd9\u624d\u628a\u4ed6\u6740\u9000\u3002\u4f46\u90a3\u4e2a\u7ed9\u674e\u76df\u4e3b\u70b9\u4e86\u7a74\u9053\u7684\u8499\u53e4\u6b66\u58eb\uff0c\u5374\u4e5f\u4e58\u673a\u8dd1\u4e86\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u542c\u5230\u8fd9\u91cc\uff0c\u5ffd\u5730\u63d2\u53e3\u95ee\u9053\uff1a\u201c\u674e\u76df\u4e3b\u662f\u5c11\u6797\u6d3e\u8c37\u5927\u4fa0\u7684\u5ae1\u4f20\u5f1f\u5b50\uff0c\u5c11\u6797\u5bfa\u8fbe\u6469\u7956 \u5e08\u6240\u4f20\u7684\u70b9\u7a74\u624b\u6cd5\u7cbe\u5999\u65e0\u6bd4\uff0c\u4e2d\u571f\u7684\u522b\u6d3e\u9ad8\u624b\uff0c\u56e0\u4e0e\u5c11\u6797\u6d3e\u5e38\u6709\u63a5\u89e6\u7684\u673a\u4f1a\uff0c\u6216\u8005\u8fd8\u6709\u6240 \u77e5\uff0c\u4f46\u8981\u89e3\u4e5f\u4e0d\u5bb9\u6613\u3002\u5728\u8499\u53e4\u6b66\u58eb\u4e4b\u4e2d\uff0c\u5219\u6050\u6015\u662f\u65e0\u4eba\u77e5\u6653\u7684\u4e86\u3002\u52ff\u5962\u65e2\u7136\u7ed9\u674e\u76df\u4e3b\u70b9\u4e86\u7a74 \u9053\uff0c\u4f55\u4ee5\u4ed6\u8fd8\u80fd\u591f\u9003\u8dd1\u5462\uff1f\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u8fd9\u6211\u5c31\u4e0d\u77e5\u9053\u4e86\uff0c\u6309\u5f53\u65f6\u7684\u60c5\u5f62\u800c\u8bba\uff0c\u9633\u575a\u767d\u662f\u7edd\u4e0d\u80fd\u5206\u51fa\u8eab\u6765\u7ed9\u4ed6\u89e3\u7a74 \u7684\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9887\u4e3a\u8be7\u5f02\uff0c\u8bf4\u9053\uff1a\u201c\u52ff\u5962\u7684\u672c\u9886\uff0c\u6599\u4ed6\u96be\u4ee5\u81ea\u89e3\u7a74\u9053\u3002\u96be\u9053\u8fd8\u6709\u7b2c\u4e09\u4e2a\u4eba\u7ed9\u4ed6\u89e3 \u7a74\u4e0d\u6210\uff1f\u201d \u3000\u3000\u5b5f\u660e\u971e\u56de\u60f3\u8d77\u90a3\u4e00\u665a\u7684\u4e8b\u60c5\uff0c\u4e0d\u89c9\u6709\u70b9\u60c6\u6005\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6768\u5a49\u5bf9\u6211\u8bef\u4f1a\uff0c\u591a\u534a\u662f\u56e0\u90a3 \u665a\u6211\u53bb\u63a5\u674e\u601d\u5357\u4e0a\u5c71\u4e4b\u4e8b\u800c\u8d77\u3002\u201d\u4e0d\u613f\u518d\u8c08\u4e0b\u53bb\uff0c\u8bf4\u9053\uff1a\u201c\u73b0\u5728\u8be5\u8f6e\u5230\u6211\u542c\u4f60\u7684\u6545\u4e8b\u4e86\u3002\u9633 \u5929\u96f7\u662f\u4f60\u7684\u5927\u5e08\u4f2f\uff0c\u4e3a\u4f55\u4f60\u8981\u6765\u5e2e\u52a9\u6211\u4eec\uff1f\u4ed6\u7684\u4f84\u5b50\u4e3a\u4f55\u53c8\u8981\u5047\u5192\u4f60\u7684\u540d\u5b57\uff0c\u5e72\u51fa\u8fd9\u7b49\u6b66\u6797 \u4e2d\u4eba\u6240\u75db\u6068\u7684\u91c7\u82b1\u6848\u5b50\u6765\u9677\u5bb3\u4f60\u5462\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u6b64\u4e8b\u8bf4\u6765\u8bdd\u957f\uff0c\u5f85\u6211\u4ece\u5934\u8bf4\u8d77\u5427\u3002 \u3000\u3000\u201c\u6211\u5e08\u7956\u6709\u56db\u4e2a\u5f92\u5f1f\uff0c\u9633\u5929\u96f7\u5c45\u957f\uff0c\u662f\u6211\u7684\u5927\u5e08\u4f2f\u3002\u4e8c\u5e08\u4f2f\u65e9\u5df2\u53bb\u4e16\uff0c\u6ca1\u6709\u7559\u4e0b\u4f20\u4eba\u3002 \u56db\u5e08\u53d4\u5341\u4f59\u5e74\u524d\u5931\u8e2a\uff0c\u81f3\u4eca\u4e0d\u77e5\u4e0b\u843d\u3002\u6211\u7684\u5e08\u7236\u6392\u884c\u7b2c\u4e09\uff0c\u53ef\u8bf4\u662f\u9633\u5929\u96f7\u552f\u4e00\u7684\u5e08\u5f1f\u4e86\u3002 \u3000\u3000\u201c\u9633\u5929\u96f7\u7684\u7236\u4eb2\u662f\u6c49\u4eba\uff0c\u6bcd\u4eb2\u662f\u91d1\u4eba\uff0c\u6309\u8bf4\u4ed6\u59d3\u7236\u4eb2\u4e4b\u59d3\uff0c\u5e94\u8be5\u7b97\u662f\u6c49\u4eba\uff0c\u4f46\u4ed6\u8d2a\u56fe\u5bcc \u8d35\uff0c\u5374\u4ee5\u91d1\u4eba\u81ea\u5c45\uff0c\u4e8c\u5341\u5e74\u80fd\u4ed6\u6295\u5165\u524d\u4efb\u7684\u91d1\u56fd\u56fd\u5e08\u95e8\u4e0b\uff0c\u4e3a\u91d1\u864f\u6548\u52b3\uff0c\u4ed7\u7740\u4ed6\u7684\u4e00\u8eab\u6b66 \u529f\uff0c\u7984\u4f4d\u5e74\u5e74\u9ad8\u5347\uff0c\u7ec8\u4e8e\u5728\u4e09\u5e74\u4e4b\u524d\uff0c\u7531\u4ed6\u7ee7\u4efb\u505a\u5230\u4e86\u91d1\u56fd\u7684\u56fd\u5e08\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u60f3\u5fc5\u662f\u4ed6\u4eec\u5e08\u5f92\u4e0d\u503c\u9633\u5929\u96f7\u7684\u6240\u4e3a\uff0c\u6545\u6b64\u540c\u95e8\u53d8\u6210\u4e86\u4ec7\u654c\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u9053\u4e0d\u540c\u4e0d\u76f8\u4e3a\u8c0b\uff0c\u81ea\u5927\u5e08\u4f2f\u51fa\u4ed5\u91d1\u671d\u4e4b\u540e\uff0c\u5bb6\u5e08\u5373\u5165\u5c71\u9690\u5c45\uff0c\u548c\u4ed6 \u5f88\u5c11\u5f80\u6765\u4e86\u3002\u4f46\u97f3\u8baf\u5219\u5c1a\u672a\u65ad\u7edd\u3002\u5927\u5e08\u4f2f\u5ba6\u9014\u5f97\u610f\uff0c\u5076\u5c14\u4e5f\u6d3e\u9063\u95e8\u4eba\u5f1f\u5b50\uff0c\u524d\u6765\u95ee\u8baf\uff0c\u60f3\u8bf7 \u5bb6\u5e08\u51fa\u5c71\uff0c\u4f5c\u4ed6\u81c2\u52a9\uff0c\u5bb6\u5e08\u603b\u662f\u59d4\u5a49\u62d2\u7edd\u3002\u5bb6\u5e08\u79c1\u5e95\u4e0b\u66fe\u5bf9\u6211\u8bf4\uff0c\u4ed6\u5728\u521d\u5165\u5e08\u95e8\u7684\u90a3\u4e24\u5e74\uff0c \u5927\u5e08\u4f2f\u66fe\u4ee3\u5e08\u7956\u4f20\u827a\uff0c\u6559\u8fc7\u4ed6\u7ec3\u529f\u592b\uff0c\u6b66\u6797\u4e2d\u4eba\uff0c\u6700\u91cd\u5e08\u4eec\u6069\u4e49\uff0c\u56e0\u6b64\u4ed6\u4e0d\u80fd\u4e0e\u5927\u5e08\u4f2f\u5272\u5e2d \u7edd\u4ea4\u3002\u4e0d\u8fc7\uff0c\u4ed6\u53c8\u8bf4\u8fd9\u53ea\u662f\u534a\u4e2a\u539f\u56e0\uff0c\u53e6\u5916\u534a\u4e2a\u539f\u56e0\uff0c\u5219\u662f\u6015\u5927\u5e08\u4f2f\u5fc3\u72e0\u624b\u8fa3\uff0c\u5018\u82e5\u65ad\u7136\u7684 \u4e0e\u4ed6\u7ffb\u9762\uff0c\u53ea\u6015\u4f1a\u5f15\u8d77\u5927\u5e08\u4f2f\u7684\u62a5\u590d\uff0c\u90a3\u65f6\u5c31\u96be\u4fdd\u6ca1\u6709\u706d\u95e8\u4e4b\u7978\u4e86\uff0c\u662f\u4ee5\u4ed6\u9690\u5fcd\u81f3\u4eca\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u8fd9\u624d\u77e5\u9053\u81ea\u5df1\u53ea\u731c\u5230\u4e86\u4e00\u534a\u3002\u539f\u6765\u891a\u4e91\u5cf0\u7684\u5e08\u7236\u867d\u7136\u4e0d\u503c\u9633\u5929\u96f7\u7684\u6240\u4e3a\uff0c\u5374\u4e5f\u8fd8 \u662f\u85d5\u65ad\u4e1d\u8fde\u7684\u3002\u6709\u70b9\u4e0d\u5927\u9ad8\u5174\uff0c\u4e8e\u662f\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u8fd9\u6b21\u516c\u7136\u548c\u9633\u5929\u96f7\u7684\u4f84\u5b50\u52a8\u624b\uff0c\u5c31 \u4e0d\u6015\u7978\u53ca\u5e08\u95e8\u4e86\u4e48\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u7b11\u9053\uff1a\u201c\u4f60\u8fd8\u6ca1\u6709\u542c\u5b8c\u6211\u7684\u8bdd\u5462\uff0c\u5230\u4e86\u6700\u8fd1\u51e0\u5e74\uff0c\u60c5\u5f62\u53c8\u4e0d\u540c\u4e86\u3002 \u3000\u3000\u201c\u5927\u5e08\u4f2f\u4e09\u5e74\u524d\u505a\u4e86\u91d1\u56fd\u7684\u56fd\u5e08\uff0c\u5bf9\u6211\u5e08\u7236\u7684\u5a01\u8feb\u5229\u8bf1\u8d8a\u6765\u8d8a\u7d27\uff0c\u6700\u540e\u4e00\u6b21\u4ed6\u4eb2\u81ea\u51fa \u9a6c\uff0c\u2018\u5149\u4e34\u2019\u5e08\u7236\u7684\u8349\u5e90\uff0c\u6254\u4e0b\u8bdd\u6765\uff0c\u8bf4\u9053\uff1a\u2018\u4f60\u4e0d\u80af\u51fa\u5c71\u52a9\u6211\uff0c\u7a76\u7adf\u662f\u4f55\u7528\u5fc3\uff1f\u2019\u5e08\u7236\u7ed9 \u4ed6\u903c\u5f97\u6ca1\u6709\u529e\u6cd5\uff0c\u53ea\u597d\u4f7f\u7528\u7f13\u5175\u4e4b\u8ba1\uff0c\u5047\u610f\u7b54\u5141\u8003\u8651\uff0c\u4f46\u6709\u4e00\u4e9b\u5bb6\u4e8b\u987b\u5f97\u5b89\u6392\u59a5\u5f53\uff0c\u65b9\u80fd\u6210 \u884c\u3002\u5927\u5e08\u4f2f\u7ed9\u4e86\u4ed6\u4e00\u4e2a\u9650\u671f\uff0c\u8fd9\u624d\u80af\u8d70\u3002 \u3000\u3000\u201c\u5927\u5e08\u4f2f\u8d70\u540e\uff0c\u5bb6\u5e08\u5bf9\u6211\u5410\u51fa\u5fc3\u8179\u7684\u8bf4\u8bdd\uff0c\u4ed6\u8bf4\uff1a\u2018\u4f60\u548c\u6211\u90fd\u662f\u6c49\u4eba\uff0c\u4f60\u7684\u5927\u5e08\u4f2f\u5982\u4eca \u505a\u4e86\u91d1\u56fd\u7684\u56fd\u5e08\uff0c\u6b8b\u6740\u6297\u91d1\u4e49\u571f\uff0c\u6076\u884c\u8d8a\u6765\u8d8a\u5927\uff0c\u6211\u5c82\u80fd\u53ea\u987e\u79c1\u6069\uff0c\u5fd8\u4e86\u5927\u4e49\uff1f\u8001\u5b9e\u8bf4\uff0c\u6211 \u662f\u65e9\u5c31\u60f3\u548c\u4ed6\u4e00\u62fc\u7684\u4e86\uff0c\u4f46\u8fc7\u53bb\u6211\u56e0\u4e3a\u4e00\u6765\u529b\u4e0d\u80fd\u654c\uff0c\u4e8c\u6765\u4f60\u7684\u529f\u592b\u4e5f\u672a\u5b66\u6210\uff0c\u6211\u82e5\u4e0e\u4ed6\u62fc \u4e86\uff0c\u672c\u95e8\u7684\u6b66\u827a\u81ea\u6211\u800c\u65ad\uff0c\u53ea\u7559\u7ed9\u4e3a\u864e\u4f5c\u6005\u7684\u9633\u5929\u96f7\u6043\u4ee5\u884c\u51f6\uff0c\u8fd9\u6211\u5c31\u5bf9\u4e0d\u8d77\u7956\u5e08\u4e86\u3002\u6240\u4ee5 \u6211\u4e00\u76f4\u9690\u5fcd\u81f3\u4eca\u3002\u8fd9\u51e0\u5e74\u6765\u52a0\u7d27\u4f20\u6388\u4f60\u7684\u6b66\u529f\uff0c\u6682\u65f6\u907f\u514d\u548c\u9633\u5929\u96f7\u51b3\u88c2\uff0c\u62d6\u5f97\u4e00\u5929\u5c31\u662f\u4e00 \u5929\u3002\u6211\u7684\u82e6\u5fc3\uff0c\u5e0c\u671b\u4f60\u80fd\u660e\u767d\u3002\u4f46\u73b0\u5728\u5df2\u662f\u62d6\u4e0d\u4e0b\u53bb\u4e86\uff0c\u4f60\u9003\u5230\u5357\u65b9\u53bb\u5427\u3002\u2019 \u3000\u3000\u201c\u6211\u662f\u4e2a\u5b64\u513f\uff0c\u591a\u8499\u5e08\u7236\u629a\u517b\u6210\u4eba\u7684\uff0c\u5e08\u95e8\u4e4b\u6069\uff0c\u91cd\u4e8e\u7236\u6bcd\u3002\u6211\u5f53\u7136\u4e0d\u80af\u8ba9\u5e08\u7236\u8d74\u96be\uff0c \u6211\u8bf4\u4e0d\u5982\u8ba9\u6211\u53bb\u884c\u523a\u9633\u5929\u96f7\u5427\u3002\u6211\u5e08\u7236\u4e0d\u80af\u7b54\u5e94\u3002 \u3000\u3000\u201c\u8bae\u8bba\u672a\u5b9a\uff0c\u9650\u671f\u5c31\u8981\u6ee1\u4e86\u3002\u5ffd\u7136\u6709\u5e08\u7236\u7684\u4e00\u4f4d\u597d\u670b\u53cb\u6765\uff0c\u4e00\u5e2d\u8bdd\u89e3\u51b3\u4e86\u6211\u4eec\u5e08\u5f92\u7684\u96be \u9898\uff0c\u4f60\u731c\u8fd9\u4e2a\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u5b5f\u660e\u971e\u6014\u4e86\u4e00\u6014\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u600e\u4e48\u77e5\u9053\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u7b11\u9053\uff1a\u201c\u5c31\u662f\u4f60\u7684\u7236\u4eb2\u2014\u2014\u5b5f\u5c11\u521a\u5b5f\u5927\u4fa0\uff01\u201d \u3000\u3000\u5b5f\u660e\u971e\u604d\u7136\u5927\u609f\uff0c\u8bf4\u9053\uff1a\u201c\u539f\u6765\u7239\u7239\u548c\u4f60\u7684\u5e08\u7236\u662f\u597d\u670b\u53cb\uff0c\u8fd9\u5c31\u602a\u4e0d\u5f97\u4e86\u3002\u201d\u891a\u4e91\u5cf0\u8be7 \u9053\uff1a\u201c\u4ec0\u4e48\u602a\u4e0d\u5f97\uff1f\u201d\u5b5f\u660e\u971e\u9053\uff1a\u201c\u5f85\u4f1a\u513f\u6211\u518d\u548c\u4f60\u8bf4\uff0c\u5148\u8bf4\u4f60\u7684\u5427\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u90a3\u4e24\u5929\u6211\u521a\u597d\u4e0d\u5728\u5bb6\uff0c\u4ee4\u5c0a\u529d\u544a\u5bb6\u5e08\u7684\u8bf4\u8bdd\u662f\u5e08\u7236\u540e\u6765\u544a\u8bc9\u6211\u7684\u3002 \u3000\u3000\u201c\u4ee4\u5c0a\u5148\u95ee\u5bb6\u5e08\uff1a\u2018\u4f60\u5230\u5927\u90fd\u89c1\u4e86\u9633\u5929\u96f7\uff0c\u662f\u6253\u7b97\u5149\u660e\u6b63\u5927\u7684\u5411\u4ed6\u6311\u6218\u5462\uff0c\u8fd8\u662f\u6697\u4e2d\u884c \u523a\uff1f\u2019\u5bb6\u5e08\u8bf4\uff1a\u2018\u6211\u548c\u9633\u5929\u96f7\u662f\u540c\u4e00\u8f88\u5206\u7684\u5e08\u5144\u5f1f\uff0c\u4ed6\u4e0e\u6211\u53c8\u6709\u4ee3\u5e08\u4f20\u827a\u4e4b\u6069\uff0c\u5982\u4eca\u6211\u867d\u7136 \u662f\u5927\u4e49\u706d\u4eb2\uff0c\u4e5f\u4e0d\u80fd\u91c7\u7528\u6697\u6740\u7684\u624b\u6bb5\u3002\u2019\u4ee4\u5c0a\u8bf4\uff1a\u2018\u90a3\u4e48\u4f60\u662f\u6253\u7b97\u76f4\u65a5\u5176\u975e\uff0c\u5149\u660e\u6b63\u5927\u7684\u6253 \u51fa\u6e05\u7406\u5e08\u95e8\u7684\u65d7\u53f7\uff0c\u7136\u540e\u624d\u4e0e\u4ed6\u52a8\u624b\u7684\u4e86\uff1f\u2019\u5bb6\u5e08\u8bf4\uff1a\u2018\u4e0d\u9519\uff0c\u6211\u7684\u6253\u7b97\u6b63\u662f\u8fd9\u6837\uff0c\u2019\u4e8e\u662f \u4ee4\u5c0a\u5c31\u95ee\u5bb6\u5e08\uff1a\u2018\u4f60\u6709\u6ca1\u6709\u53d6\u80dc\u7684\u628a\u63e1\uff1f\u2019\u5bb6\u5e08\u5766\u7136\u8bf4\u9053\uff1a\u2018\u6ca1\u6709\u3002\u4f46\u6211\u73b0\u5728\u5df2\u7ed9\u5e08\u5144\u903c\u5f97 \u65e0\u8def\u53ef\u8d70\uff0c\u9664\u4e86\u4e0e\u4ed6\u62fc\u6389\u4e00\u547d\uff0c\u8fd8\u6709\u4ec0\u4e48\u529e\u6cd5\u3002\u2019 \u3000\u3000\u201c\u4ee4\u5c0a\u6447\u4e86\u6447\u5934\uff0c\u8bf4\u9053\uff1a\u2018\u8fd9\u4e0d\u662f\u4e2a\u597d\u529e\u6cd5\u3002\u8001\u5b9e\u8bf4\uff0c\u5373\u4f7f\u4f60\u80fd\u62fc\u6389\u9633\u5929\u96f7\uff0c\u6211\u4e5f\u4e0d\u6562 \u8d5e\u540c\u3002\u2019\u5bb6\u5e08\u9053\uff1a\u2018\u90a3\u4e48\u4f9d\u4f60\u4e4b\u89c1\u5982\u4f55\uff1f\u2019\u4ee4\u5c0a\u9053\uff1a\u4f9d\u6211\u4e4b\u89c1\uff0c\u4e0d\u5982\u7531\u4ee4\u5f92\u66ff\u4ee3\u4f60\uff0c\u5047\u610f\u505a \u9633\u5929\u96f7\u7684\u624b\u4e0b\u3002\u2019\u5bb6\u5e08\u9053\uff1a\u2018\u4f60\u7684\u610f\u601d\u53ef\u662f\u53eb\u4e91\u5cf0\u53bb\u6697\u6740\u4ed6\uff1f\u4e0d\u9519\uff0c\u6c49\u8d3c\u4e0d\u4e24\u7acb\uff0c\u6309\u7167\u6c5f\u6e56 \u89c4\u77e9\uff0c\u665a\u4e00\u8f88\u5012\u662f\u53ef\u4ee5\u7528\u6697\u5f1f\u7684\u624b\u6bb5\uff0c\u4e0d\u81f4\u62db\u4eba\u975e\u8bae\uff0c\u4e0d\u8fc7\uff0c\u4e91\u5cf0\u7684\u672c\u9886\u548c\u9633\u5929\u96f7\u5dee\u5f97\u66f4 \u8fdc\uff0c\u6697\u6740\u51b3\u4e0d\u4f1a\u6210\u529f\u3002\u2019 \u3000\u3000\u201c\u4ee4\u5c0a\u53c8\u6447\u4e86\u6447\u5934\uff0c\u8bf4\u9053\uff1a\u2018\u6740\u4e86\u4e00\u4e2a\u9633\u5929\u96f7\uff0c\u8fd8\u4f1a\u6709\u7b2c\u4e8c\u4e2a\u9633\u5929\u96f7\u3002\u6697\u6740\u5c31\u7b97\u6210\u529f\uff0c \u4e5f\u6ca1\u6709\u4ec0\u4e48\u5927\u7528\u3002\u6211\u4e0d\u662f\u8fd9\u4e2a\u610f\u601d\u3002\u2019 \u3000\u3000\u201c\u4ee4\u5c0a\u8fd9\u624d\u8be6\u7ec6\u4ed6\u8bf4\u51fa\u4e86\u4ed6\u7684\u8ba1\u5212\uff0c\u4ed6\u8bf4\uff1a\u2018\u9633\u5929\u96f7\u6700\u53ef\u6076\u4e4b\u5904\uff0c\u4e0d\u4ec5\u662f\u5728\u4e8e\u4ed6\u80cc\u53db\u5e08 \u95e8\uff0c\u800c\u662f\u5728\u4e8e\u4ed6\u52a9\u7ea3\u4e3a\u8650\uff0c\u4e3a\u91d1\u864f\u641c\u6355\u6297\u91d1\u7684\u4e49\u58eb\u548c\u5bf9\u4ed8\u4e49\u519b\uff0c\u662f\u4e48\uff1f\u2019\u5bb6\u5e08\u9053\uff1a\u2018\u4e0d \u9519\u3002\u2019\u4ee4\u5c0a\u9053\uff1a\u2018\u56e0\u6b64\uff0c\u6211\u7684\u610f\u601d\u662f\u8981\u4ee4\u5f92\u57cb\u4f0f\u5728\u654c\u4eba\u7684\u5fc3\u810f\u4f5c\u4e3a\u6211\u65b9\u7684\u8033\u76ee\u3002\u8fd9\u4e0d\u662f\u6bd4\u53ea \u6740\u6389\u4e00\u4e2a\u9633\u5929\u96f7\u6709\u7528\u5f97\u591a\u4e48\uff1f\u2019 \u3000\u3000\u201c\u5bb6\u5e08\u9053\uff1a\u2018\u4e3b\u610f\u4e0d\u9519\uff0c\u4f46\u53ea\u6015\u9633\u5929\u96f7\u4e0d\u80af\u8ba9\u5cf0\u513f\u66ff\u6211\u3002\u2019\u4ee4\u5c0a\u9053\uff1a\u2018\u4f60\u5e76\u65e0\u5b50\u5973\uff0c\u8fd9 \u4e2a\u5f99\u5f1f\u4f60\u5f85\u4ed6\u6bd4\u513f\u5b50\u8fd8\u4eb2\uff0c\u9633\u5929\u96f7\u662f\u77e5\u9053\u7684\u3002\u4ed6\u7684\u6b66\u529f\u4e5f\u5df2\u5f97\u4e86\u4f60\u7684\u771f\u4f20\uff0c\u9633\u5929\u96f7\u600e\u4f1a\u4e0d\u8981 \u4ed6\u5462\uff1f\u8001\u5b9e\u8bf4\uff0c\u5982\u679c\u6211\u662f\u9633\u5929\u96f7\u7684\u8bdd\uff0c\u6211\u5c31\u5b81\u613f\u8981\u4f60\u7684\u5f92\u5f1f\uff0c\u4e0d\u613f\u8981\u4f60\u3002\u56e0\u4e3a\u4f60\u662f\u4ed6\u7684\u5e08 \u5f1f\uff0c\u813e\u6c14\u5014\u5f3a\uff0c\u4e14\u53c8\u4e0a\u4e86\u5e74\u7eaa\uff0c\u4ed6\u81ea\u5df1\u4e5f\u5e94\u77e5\u9053\u8981\u6307\u6325\u4f60\u662f\u4e0d\u6613\u5f97\u5fc3\u5e94\u624b\u7684\u3002\u4f46\u5bf9\u4e00\u4e2a\u665a\u4e00 \u8f88\u7684\u5e08\u4f84\uff0c\u4ed6\u5c31\u53ef\u4ee5\u4efb\u610f\u5dee\u9063\u4e86\u3002\u6bd4\u5982\u8bf4\uff0c\u4ed6\u82e5\u6709\u4e8b\u8981\u4e00\u4e2a\u4eba\u4e3a\u4ed6\u6548\u5954\u8d70\u4e4b\u52b3\uff0c\u5f53\u7136\u662f\u9009\u5e74 \u8f7b\u529b\u58ee\u7684\u4ee4\u5f92\uff0c\u96be\u9053\u597d\u610f\u601d\u5dee\u9063\u4e00\u628a\u5e74\u7eaa\u7684\u4f60\u4e48\uff1f\u4ee4\u5f92\u5728\u4ed6\u638c\u63e1\u4e4b\u4e2d\uff0c\u4ed6\u4e5f\u4e0d\u4f1a\u6015\u4f60\u9020\u53cd\u3002 \u4f9d\u6211\u770b\u6765\uff0c\u8fd9\u6761\u8ba1\u5212\u591a\u534a\u662f\u884c\u5f97\u901a\u7684\u3002\u518d\u8bf4\uff0c\u5373\u4f7f\u884c\u4e0d\u901a\uff0c\u4e5f\u53ef\u4ee5\u4f5c\u4e3a\u7f13\u5175\u4e4b\u8ba1\uff0c\u8ba9\u54b1\u4eec\u53ef \u4ee5\u4ece\u5bb9\u7684\u518d\u60f3\u522b\u4e2a\u529e\u6cd5\u3002\u2019\u201d \u3000\u3000\u5b5f\u660e\u971e\u7b11\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u9633\u575a\u767d\u90a3\u53ae\u9a82\u4f60\u4e0d\u662f\u4e2a\u597d\u4e1c\u897f\uff0c\u60f3\u5fc5\u4f60\u5df2\u662f\u4f9d\u8ba1\u800c\u884c\u4e86\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u6b63\u662f\u3002\u6211\u4f9d\u4ee4\u5c0a\u4e4b\u8ba1\uff0c\u5230\u4e86\u5927\u90fd\u89c1\u9633\u5929\u96f7\uff0c\u4ed6\u679c\u7136\u628a\u6211\u7559\u4e86\u4e0b\u6765\uff0c\u51c6\u6211\u66ff \u4ee3\u5e08\u7236\uff0c\u8fd8\u9001\u4e86\u6211\u4e00\u4e2a\u4e8c\u7b49\u5f85\u536b\u7684\u804c\u8854\u5462\u3002\u201d \u3000\u3000\u201c\u6211\u5728\u4ed6\u7684\u603b\u7ba1\u5e9c\u6df7\u4e86\u4e24\u5e74\u591a\uff0c\u5012\u4e5f\u63a2\u542c\u5f97\u4e0d\u5c11\u6d88\u606f\uff0c\u597d\u51e0\u6b21\u4ed6\u8981\u7f09\u6355\u6297\u91d1\u4e49\u58eb\uff0c\u90fd\u7ed9 \u6211\u4e8b\u5148\u901a\u98ce\u62a5\u4fe1\u653e\u6389\u4e86\u3002\u5927\u90fd\u7684\u4e10\u5e2e\u5206\u8235\u548c\u6211\u662f\u6709\u8054\u7edc\u7684\uff0c\u6bcf\u4e00\u6b21\u6211\u90fd\u628a\u6d88\u606f\u5f88\u5de7\u5999\u7684\u9001\u51fa \u53bb\uff0c\u81ea\u5df1\u4ece\u4e0d\u9732\u9762\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u4f46\u8fd9\u6837\u7684\u4e8b\u60c5\u4e00\u591a\uff0c\u9633\u5929\u96f7\u5c82\u80fd\u6ca1\u6709\u6000\u7591\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u4f60\u6599\u5f97\u4e0d\u9519\uff0c\u8fd9\u4e5f\u5c31\u662f\u6211\u4e3a\u4ec0\u4e48\u8981\u51fa\u8d70\u7684\u539f\u56e0\u4e86\u3002\u4f46\u8d77\u521d\u4ed6\u8fd8\u672a\u66fe\u6000\u7591\u662f \u6211\u7684\uff0c\u51a4\u6789\u6740\u4e86\u4ed6\u7684\u4e24\u4e2a\u624b\u4e0b\uff0c\u6e10\u6e10\u5c31\u6000\u7591\u5230\u6211\u7684\u8eab\u4e0a\u4e86\u3002\u6211\u53d1\u89c9\u4e0d\u5999\uff0c\u5728\u4ed6\u4e0b\u624b\u4e4b\u524d\u7684\u67d0 \u4e00\u5929\u665a\u4e0a\uff0c\u8d81\u4ed6\u5165\u91d1\u5bab\u503c\u591c\u7684\u673a\u4f1a\uff0c\u4f2a\u9020\u51fa\u5dee\u7684\u516c\u6587\uff0c\u8fde\u591c\u9003\u51fa\u5927\u90fd\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u4f60\u9003\u51fa\u5927\u90fd\uff0c\u5c31\u5f84\u81ea\u6295\u5954\u5c60\u51e4\u7684\u5c71\u5be8\u4e48\uff1f\u4f60\u662f\u4e0d\u662f\u77e5\u9053\u6211\u7684\u7239\u7239\u4f1a\u6765 \u7684\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u8fd9\u5012\u4e0d\u662f\u3002\u4e0d\u8fc7\uff0c\u6df3\u4e8e\u5468\u548c\u5c60\u9f99\u79c1\u901a\u8499\u53e4\uff0c\u6211\u5374\u662f\u77e5\u9053\u7684\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u4f60\u662f\u8981\u6765\u544a\u5bc6\u7684\u4e86\uff0c\u662f\u4e48\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u70b9\u4e86\u70b9\u5934\uff0c\u63a5\u4e0b\u53bb\u8bf4\u9053\uff1a\u201c\u4f46\u6211\u56e0\u4e3a\u4e0d\u77e5\u5c60\u51e4\u7684\u771f\u610f\uff0c\u53ea\u6050\u5979\u504f\u8892\u54e5\u54e5\uff0c\u6545\u6b64\u6211 \u4e0d\u6562\u5192\u6627\u5411\u5979\u544a\u5bc6\uff0c\u6253\u7b97\u770b\u6e05\u695a\u4e86\u518d\u8bf4\u3002 \u3000\u3000\u201c\u6211\u5230\u4e86\u7405\u739b\u5c71\u4e0d\u8fc7\u4e09\u5929\uff0c\u5c31\u78b0\u4e0a\u4e86\u6b66\u6797\u5927\u4f1a\u3002\u6b64\u4f1a\u4e00\u5f00\uff0c\u6211\u624d\u77e5\u9053\u5c60\u51e4\u662f\u4e2a\u6df1\u660e\u5927\u4e49 \u7684\u5973\u4e2d\u8c6a\u6770\uff0c\u987e\u8651\u65b9\u59cb\u6253\u6d88\u3002\u4f46\u6b64\u65f6\u5df2\u662f\u7528\u4e0d\u7740\u6211\u6765\u544a\u5bc6\uff0c\u4f60\u548c\u674e\u76df\u4e3b\u5728\u4f1a\u4e0a\u5df2\u7ecf\u63ed\u53d1\u4e86\u4ed6 \u4eec\u7684\u79c1\u901a\u8499\u53e4\u9791\u5b50\u7684\u52fe\u5f53\u5566\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u4f55\u4ee5\u4f60\u90a3\u4e00\u665a\u4e0d\u53c2\u52a0\u5e86\u529f\u5bb4\u5462\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u6211\u5728\u9633\u5929\u96f7\u7684\u603b\u7ba1\u5e9c\u4e0a\uff0c\u6709\u4e00\u6b21\u66fe\u89c1\u8fc7\u98de\u9f99\u5c71\u7684\u4e8c\u5be8\u4e3b\u524d\u6765\u62dc\u4f1a\u9633\u5929\u96f7\u3002 \u9633\u5929\u96f7\u4e00\u9762\u505a\u91d1\u56fd\u7684\u56fd\u5e08\uff0c\u4e00\u9762\u548c\u8499\u53e4\u9791\u5b50\u52fe\u642d\uff0c\u56e0\u6b64\u6211\u6000\u7591\u98de\u9f99\u5c71\u7684\u7aa6\u5be8\u4e3b\u6050\u6015\u4e5f\u662f\u4e0e\u4ed6 \u4eec\u4e00\u8def\u7684\u4eba\uff0c\u4f46\u8fd8\u6ca1\u6709\u5f97\u5230\u8bc1\u5b9e\u3002 \u3000\u3000\u201c\u90a3\u4e00\u665a\u98de\u9f99\u5c71\u6d3e\u9063\u4f7f\u8005\u6765\u5411\u5c60\u51e4\u6c42\u63f4\uff0c\u6211\u60f3\u5982\u679c\u6211\u7684\u6000\u7591\u5c5e\u5b9e\uff0c\u5c60\u9f99\u4e00\u5b9a\u662f\u77e5\u9053\u5185\u60c5 \u7684\u3002\u6211\u8981\u4ece\u5c60\u9f99\u53e3\u4e2d\u5957\u51fa\u5b9e\u8bdd\uff0c\u53c8\u60f3\u77e5\u9053\u4ed6\u56de\u7405\u739b\u5c71\u8fd8\u6709\u4ec0\u4e48\u522b\u7684\u76ee\u7684\uff0c\u6240\u4ee5\u6211\u5fc5\u987b\u627e\u5bfb\u673a \u4f1a\uff0c\u4e0e\u5c60\u9f99\u79c1\u4e0b\u89c1\u9762\u3002\u82e5\u662f\u53c2\u52a0\u4f60\u4eec\u7684\u5e86\u529f\u5bb4\uff0c\u6211\u8fd9\u8ba1\u5212\u5c31\u884c\u4e0d\u901a\u4e86\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u7ed9\u674e\u601d\u5357\u90a3\u5c01\u63ed\u53d1\u98de\u9f99\u5c71\u8be1\u8ba1\u4e4b\u4fe1\uff0c\u60f3\u5fc5\u4e5f\u662f\u4f60\u5199\u7684\u4e86\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f46\u53ef\u60dc\u6211\u4e0e\u5c60\u9f99\u79c1\u4f1a\u4e4b\u65f6\uff0c\u7ed9\u6768\u59d1\u5a18\u649e\u7834\uff0c\u5f53\u65f6\u7684\u60c5\u5f62\uff0c\u4e2a\u4e2d\u66f2 \u6298\uff0c\u51b3\u4e0d\u80fd\u5bb9\u6211\u81ea\u8fa9\uff0c\u6545\u6b64\u6211\u53ea\u597d\u6682\u4e14\u8eb2\u907f\u4e86\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u7b11\u9053\uff1a\u201c\u90a3\u4e00\u665a\u8fde\u6211\u4e5f\u4ee5\u4e3a\u4f60\u662f\u5978\u7ec6\u5462\uff0c\u771f\u662f\u59d4\u5c48\u4f60\u4e86\u3002\u5012\u662f\u6211\u7684\u7239\u7239\u597d\u50cf\u77e5\u9053 \u662f\u4f60\u4f3c\u7684\uff0c\u5f88\u4e3a\u4f60\u8bf4\u4e86\u4e00\u4e9b\u597d\u8bdd\uff0c\u53eb\u6211\u4eec\u4e0d\u8981\u5984\u8d77\u7591\u5fc3\uff0c\u5728\u771f\u76f8\u672a\u66fe\u5927\u767d\u4e4b\u524d\uff0c\u5207\u4e0d\u53ef\u51a4\u6789 \u4e86\u597d\u4eba\uff01\u201d \u3000\u3000\u891a\u4e91\u5cf0\u8be7\u9053\uff1a\u201c\u771f\u7684\u4e48\uff0c\u8fd9\u53ef\u662f\u6709\u70b9\u5947\u602a\u4e86\uff01\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u4f60\u5947\u602a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u90a3\u4e00\u5929\u4f60\u7239\u7239\u5e76\u6ca1\u6709\u89c1\u5230\u6211\uff0c\u800c\u4e14\u5c31\u7b97\u4ed6\u89c1\u5230\u6211\uff0c\u53ea\u6015\u4ed6\u4e5f\u4e0d\u4f1a\u8ba4\u8bc6\u6211 \u7684\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u4ee4\u5c0a\u4e0e\u5bb6\u5e08\u867d\u662f\u597d\u53cb\uff0c\u4f46\u5374\u4e0d\u662f\u65f6\u5e38\u89c1\u9762\u7684\u3002\u81ea\u6211\u6295\u5165\u5e08\u95e8\u4e4b\u540e\uff0c\u636e\u6211\u6240 \u77e5\uff0c\u4ee4\u5c0a\u524d\u540e\u4e0d\u8fc7\u6765\u8fc7\u4e09\u6b21\uff0c\u7b2c\u4e00\u6b21\u4ed6\u6765\u7684\u65f6\u5019\uff0c\u6211\u8fd8\u662f\u4e2a\u5c0f\u5b69\u5b50\uff0c\u53ea\u6709\u516b\u4e5d\u5c81\u5927\uff0c\u540d\u5b57\u4e5f \u4e0d\u662f\u73b0\u5728\u7684\u540d\u5b57\u3002\u7b2c\u4e8c\u7b2c\u4e09\u6b21\u6211\u6070\u5de7\u90fd\u4e0d\u5728\u5bb6\uff0c\u9519\u8fc7\u4e86\u4e0e\u4ee4\u5c0a\u89c1\u9762\u7684\u673a\u4f1a\u3002\u56e0\u6b64\uff0c\u5373\u4f7f\u4ed6\u73b0 \u5728\u89c1\u5230\u6211\uff0c\u4ed6\u53c8\u600e\u4e48\u4f1a\u77e5\u9053\u6211\u5c31\u662f\u90a3\u4e2a\u5c0f\u5b69\u5b50\u5462\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u60f3\u4e86\u4e00\u60f3\uff0c\u8bf4\u9053\uff1a\u201c\u4ee4\u5e08\u5c0a\u59d3\u5927\u540d\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u8bf4\u4e86\u5e08\u7236\u7684\u540d\u5b57\uff0c\u5b5f\u660e\u971e\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u662f\u534e\u65e0\u8679\u534e\u8001\u524d\u8f88\u3002\u5bb6\u7236\u66fe\u548c\u6211\u8bf4\u8fc7\u4ee4 \u5e08\uff0c\u636e\u8bf4\u4ee4\u5e08\u5085\u5b66\u591a\u624d\uff0c\u7434\u68cb\u8bd7\u4e66\uff0c\u533b\u672f\u661f\u76f8\uff0c\u65e0\u4e00\u4e0d\u7cbe\u3002\u60f3\u5fc5\u4f60\u4e5f\u5b66\u5230\u4eca\u5e08\u7684\u8bb8\u591a\u672c\u9886 \u4e86\uff01\u201d \u3000\u3000\u8bf8\u4e91\u5cf0\u7b11\u9053\uff1a\u201c\u522b\u4eba\u4e3a\u6b64\u7fa1\u6155\u5bb6\u5e08\uff0c\u5bb6\u5e08\u5374\u662f\u5e38\u5e38\u540e\u6094\uff0c\u4ed6\u8bf4\u82e5\u4e0d\u662f\u5f53\u5e74\u6240\u597d\u7684\u6742\u5b66\u592a \u591a\uff0c\u4ed6\u7684\u6b66\u529f\u4e5f\u4e0d\u81f3\u4e8e\u8d76\u4e0d\u4e0a\u9633\u5929\u96f7\u4e86\u3002\u56e0\u6b64\uff0c\u4ed6\u53ea\u662f\u7763\u4fc3\u6211\u52e4\u7ec3\u6b66\u529f\uff0c\u4e0d\u8bb8\u6211\u591a\u52a1\u6742 \u5b66\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u8bfb\u4e66\u5199\u5b57\uff0c\u4ed6\u603b\u662f\u8981\u6559\u4f60\u7684\u5427\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u5f53\u7136\u662f\u8981\u6559\uff0c\u8981\u4e0d\u7136\u6211\u5c82\u4e0d\u662f\u53d8\u6210\u4e86\u5f00\u773c\u778e\u5b50\u4e86\u3002\u4f46\u6211\u8bfb\u8fc7\u7684\u4e66\u4e5f \u4e0d\u8fc7\u662f\u4e00\u90e8\u8bba\u8bed\uff0c\u51e0\u672c\u62f3\u7ecf\uff0c\u4ec0\u4e48\u8bd7\u8bcd\u6b4c\u8d4b\uff0c\u6211\u662f\u4e00\u6982\u4e0d\u61c2\u7684\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u4f60\u5e08\u7236\u6559\u4f60\u5199\u5b57\uff0c\u4f60\u7684\u4e66\u6cd5\u603b\u662f\u548c\u4ed6\u76f8\u4f3c\u7684\u4e86\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u604d\u7136\u5927\u609f\uff0c\u8bf4\u9053\uff1a\u201c\u60f3\u5fc5\u662f\u4ee4\u5c0a\u770b\u4e86\u6211\u7559\u7ed9\u674e\u76df\u4e3b\u7684\u90a3\u5c01\u4fe1\u4e86\uff1f\u4ed6\u4ece\u8fd9\u5c01\u4fe1\u7684\u4e66 \u6cd5\uff0c\u53ef\u80fd\u731c\u5230\u4e86\u6211\u7684\u6765\u5386\u3002\u201d\u63a5\u7740\u53c8\u7b11\u9053\uff1a\u201c\u4f46\u6211\u7684\u4e66\u6cd5\u6bd4\u8d77\u5e08\u7236\u4ed6\u8001\u4eba\u5bb6\u53ef\u4e5f\u8fd8\u5dee\u5f97\u8fdc \u5462\uff01\u201d \u3000\u3000\u5b5f\u660e\u971e\u7b11\u9053\uff1a\u201c\u6211\u60f3\u4e5f\u662f\u3002\u8fd9\u6837\u6240\u4ee5\u7239\u7239\u4e0d\u6562\u9a6c\u4e0a\u65ad\u5b9a\u5c31\u662f\u4f60\u3002\u55ef\uff0c\u771f\u60f3\u4e0d\u5230\u5728\u8fd9\u91cc\u4f1a \u89c1\u5230\u4f60\uff0c\u4ed6\u65e5\u6211\u548c\u4f60\u4e00\u540c\u53bb\u89c1\u7239\u7239\uff0c\u4ed6\u77e5\u9053\u662f\u4f60\uff0c\u4e00\u5b9a\u5341\u5206\u6b22\u559c\u7684\u3002\u4f60\u5230\u4e86\u8fd9\u91cc\u591a\u4e45\u4e86\uff1f\u662f \u56e0\u4e3a\u77e5\u9053\u6709\u91c7\u82b1\u8d3c\u5192\u4f60\u7684\u540d\uff0c\u624d\u5728\u8fd9\u91cc\u9057\u7559\u7684\u4e48\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u4e0d\u9519\u3002\u5176\u5b9e\u5728\u4eca\u665a\u4e4b\u524d\uff0c\u6211\u5df2\u7ecf\u731c\u5230\u51e0\u5206\u662f\u8c01\u5192\u5145\u6211\u4e86\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u7ee7\u7eed\u9053\uff1a\u201c\u6211\u731c\u9633\u575a\u767d\u8fd9\u53ae\u5df2\u7ecf\u77e5\u9053\u4e86\u6211\u4ece\u5927\u90fd\u79c1\u9003\u7684\u6d88\u606f\uff0c\u8bf4\u4e0d\u5b9a\u5c31\u662f\u4ed6\u53d4\u7236 \u6388\u610f\u4ed6\u5982\u6b64\u505a\u7684\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u542c\u4ed6\u7684\u53e3\u6c14\uff0c\u4f60\u80cc\u53db\u4ed6\u7684\u53d4\u7236\uff0c\u4ed6\u662f\u5df2\u7ecf\u77e5\u9053\u7684\u4e86\u3002\u4ed6\u6068\u4f60\u8fd9\u662f\u5fc5 \u7136\u7684\uff0c\u4f46\u6211\u5374\u4e0d\u660e\u767d\uff0c\u4ed6\u4e3a\u4ec0\u4e48\u8981\u7528\u8fd9\u6837\u4e0b\u6d41\u7684\u65b9\u6cd5\u6765\u9677\u5bb3\u4f60\uff1f\u9633\u5929\u96f7\u8eab\u4e3a\u91d1\u56fd\u56fd\u5e08\uff0c\u624b\u4e0b \u80fd\u4eba\u8c05\u5fc5\u4e0d\u5c11\uff0c\u4ed6\u8981\u6740\u4f60\uff0c\u5927\u53ef\u4ee5\u4fa6\u9a91\u56db\u51fa\uff0c\u641c\u6355\u4f60\u5440\uff01\u7528\u5230\u8fd9\u6837\u4e0b\u6d41\u7684\u624b\u6bb5\uff0c\u771f\u662f\u592a\u4ee4\u4eba \u60f3\u8c61\u4e0d\u5230\u4e86\uff01\u201d \u3000\u3000\u891a\u4e91\u5cf0\u7b11\u9053\uff1a\u201c\u5b5f\u59d1\u5a18\uff0c\u4f60\u662f\u4e2a\u5149\u660e\u6b63\u76f4\u7684\u5973\u4fa0\uff0c\u5f53\u7136\u60f3\u4e0d\u5230\u4e16\u95f4\u4f1a\u6709\u8bb8\u591a\u4e0b\u6d41\u7684\u4f0e \u4fe9\u3002\u8fd9\u4e9b\u4eba\u4ec0\u4e48\u4f24\u5929\u5bb3\u7406\u7684\u4e8b\u60c5\u4e0d\u6562\u505a\uff0c\u8981\u9677\u5bb3\u522b\u4eba\uff0c\u8fd8\u987e\u5f97\u4e0b\u6d41\u4e0d\u4e0b\u6d41\u4e48\uff1f \u3000\u3000\u201c\u636e\u6211\u731c\u60f3\uff0c\u7b2c\u4e00\uff0c\u9633\u5929\u96f7\u73b0\u5728\u662f\u811a\u8e0f\u4e24\u6761\u8239\u3002\u4ed6\u52fe\u7ed3\u8499\u53e4\u9791\u5b50\u4e4b\u4e8b\uff0c\u4e0d\u80fd\u8ba9\u4e0d\u662f\u5fc3\u8179 \u7684\u91d1\u56fd\u6b66\u58eb\u77e5\u9053\uff0c\u8fd9\u6837\uff0c\u4ed6\u80fd\u591f\u6d3e\u9063\u51fa\u6765\u641c\u6355\u6211\u7684\u4eba\u5c31\u4e0d\u4f1a\u592a\u591a\u4e86\uff0c\u4ed6\u81ea\u5df1\u8981\u5750\u9547\u5927\u90fd\uff0c\u4e0d \u80fd\u64c5\u79bb\uff0c\u6700\u53ef\u9760\u7684\u5c31\u53ea\u6709\u4ed6\u7684\u4f84\u5b50\u3002 \u3000\u3000\u201c\u7b2c\u4e8c\uff0c\u9633\u575a\u767d\u5192\u6211\u4e4b\u540d\uff0c\u4f5c\u51fa\u91c7\u82b1\u6848\u5b50\uff0c\u6b66\u6797\u4e2d\u7684\u4fa0\u4e49\u4e4b\u4e0a\uff0c\u81ea\u5fc5\u662f\u5bf9\u6211\u75db\u6068\u975e\u5e38\uff0c \u8fd9\u6837\u5c31\u53ef\u65ad\u7edd\u6211\u6295\u5954\u4e49\u519b\u7684\u540e\u8def\uff01\u7b2c\u4e09\uff0c\u82e5\u662f\u6211\u4e0d\u7518\u5fc3\u53d7\u4ed6\u9677\u5bb3\uff0c\u6211\u81ea\u5fc5\u633a\u8eab\u800c\u51fa\uff0c\u627e\u8fd9\u91c7 \u82b1\u8d3c\u7b97\u5e10\uff0c\u8fd9\u6837\uff0c\u4ed6\u4eec\u5c82\u4e0d\u662f\u5c31\u53ef\u4ee5\u4e0d\u8d39\u5439\u7070\u4e4b\u529b\uff0c\u627e\u5230\u6211\u4e48\uff1f\u9633\u575a\u767d\u81ea\u89c6\u751a\u9ad8\uff0c\u5728\u4eca\u665a\u52a8 \u624b\u4e4b\u65f6\uff0c\u4e0d\u95ee\u53ef\u77e5\uff0c\u4ed6\u5f53\u7136\u662f\u4ee5\u4e3a\u4e00\u5b9a\u53ef\u4ee5\u80dc\u5f97\u8fc7\u6211\u7684\uff0c\u4f55\u51b5\u4ed6\u8fd8\u6709\u8d3a\u4e5d\u516c\u8fd9\u4e2a\u8001\u8d3c\u5e2e \u5fd9\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u542c\u4ed6\u5206\u6790\u5f97\u5165\u60c5\u5165\u7406\uff0c\u5927\u4e3a\u4f69\u670d\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u4f60\u73b0\u5728\u5df2\u7ecf\u77e5\u9053\u4e86\u8fd9\u4ef6\u4e8b\u60c5\u7684 \u771f\u76f8\u4e86\uff0c\u4f60\u6253\u7b97\u600e\u4e48\u529e\u5462\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u7ecf\u8fc7\u4eca\u665a\u4e4b\u4e8b\uff0c\u8d3a\u8001\u8d3c\u5f53\u7136\u662f\u8981\u642c\u5bb6\u7684\u4e86\uff0c\u800c\u4e14\uff0c\u5c31\u7b97\u54b1\u4eec\u80fd\u591f\u627e\u5230\u4ed6\uff0c \u54b1\u4eec\u53ea\u6709\u4e24\u4eba\uff0c\u4e5f\u672a\u5fc5\u662f\u4ed6\u4eec\u5bf9\u624b\u3002\u6682\u65f6\uff0c\u6211\u662f\u60f3\u4e0d\u51fa\u6709\u4ec0\u4e48\u597d\u4e3b\u610f\u6765\u4e86\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u7684\u5251\u4f24\u672a\u6108\uff0c\u6211\u7684\u6b66\u529f\u6050\u6015\u4e5f\u8fd8\u8981\u51e0\u5929\u624d\u80fd\u5b8c\u5168\u6062\u590d\uff0c\u76ee\u524d\u627e\u4ed6 \u4eec\u52a8\u624b\u5b9e\u662f\u4e0d\u5b9c\u3002\u541b\u5b50\u62a5\u4ec7\uff0c\u5341\u5e74\u672b\u665a\uff0c\u6211\u770b\u8fd8\u662f\u7559\u5f85\u5c06\u6765\u89c1\u4e86\u6211\u7684\u7239\u7239\u518d\u8bf4\u5427\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u5b5f\u59d1\u5a18\uff0c\u4f60\u53c8\u4f55\u5fc5\u72ec\u81ea\u4e0b\u5c71\uff1f\u4f60\u672c\u6765\u662f\u51c6\u5907\u4e0a\u54ea\u513f\u7684\uff1f\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u6211\u51c6\u5907\u524d\u5f80\u98de\u9f99\u5c71\u4f1a\u7239\u7239\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u7b11\u9053\uff1a\u201c\u674e\u76df\u4e3b\u4e0e\u4f60\u7239\u7239\u540c\u884c\uff0c\u4f60\u8fd8\u653e\u5fc3\u4e0d\u4e0b\u4e48\uff1f\u201d\u5b5f\u660e\u971e\u674f\u8138\u6cdb\u7ea2\uff0c\u82b3\u5fc3\u9887\u662f \u6709\u70b9\u7740\u607c\uff0c\u4f46\u8f6c\u5ff5\u4e00\u60f3\uff1a\u201c\u8fde\u5c60\u51e4\u4e5f\u4ee5\u4e3a\u6211\u548c\u674e\u601d\u5357\u6709\u751a\u79c1\u60c5\uff0c\u53c8\u600e\u80fd\u602a\u5f97\u4ed6\u80e1\u731c\u4e71\u60f3\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u8bdd\u8bf4\u51fa\u53e3\uff0c\u770b\u89c1\u5b5f\u660e\u971e\u9762\u8272\u4e0d\u5bf9\uff0c\u8fd9\u624d\u84e6\u5730\u7701\u89c9\u8bf4\u9519\u4e86\u8bdd\uff0c\u540e\u6094\u5df2\u6765\u4e0d\u53ca\uff0c\u540c\u65f6 \u81ea\u5df1\u4e5f\u89c9\u5f97\u6709\u70b9\u5947\u602a\uff1a\u201c\u6211\u4e3a\u4ec0\u4e48\u4f1a\u8bf4\u8fd9\u6837\u7684\u8bdd\u5462\uff1f\u6211\u4e0e\u5979\u4e0d\u8fc7\u521a\u521a\u76f8\u8bc6\uff0c\u96be\u9053\u6211\u7684\u5185\u5fc3\u7adf \u7136\u662f\u5728\u5992\u5fcc\u674e\u601d\u5357\u800c\u4e0d\u81ea\u89c9\u4e48\uff1f\u201d \u3000\u3000\u5b5f\u660e\u971e\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u674e\u601d\u5357\u6b66\u529f\u5353\u7edd\uff0c\u4eba\u53c8\u7cbe\u660e\uff0c\u7528\u4e0d\u7740\u6211\u66ff\u4ed6\u62c5\u5fc3\uff0c\u6211\u653e\u5fc3\u4e0d\u4e0b\u7684\u662f \u53e6\u4e00\u4e2a\u4eba\u3002\u201d\u891a\u4e91\u5cf0\u542c\u4e86\uff0c\u66f4\u89c9\u5f97\u4e0d\u662f\u5473\u513f\uff0c\u5583\u5583\u8bf4\u9053\uff1a\u201c\u5b5f\u59d1\u5a18\u4f11\u8981\u8bef\u4f1a\uff0c\u674e\u76df\u4e3b\u662f\u4eba\u4e2d \u9f99\u51e4\uff0c\u6211\u51b3\u6ca1\u6709\u8f7b\u89c6\u4ed6\u7684\u610f\u601d\uff0c\u53ea\u56e0\u4ed6\u8eab\u8d1f\u4e49\u519b\u91cd\u8d23\uff0c\u6211\u3001\u6211\u662f\u96be\u514d\u6709\u70b9\u8fc7\u8651\uff0c\u4f46\u4e0d\u77e5\u59d1\u5a18 \u653e\u5fc3\u4e0d\u4e0b\u7684\u5374\u53c8\u662f\u8c01\uff1f\u201d\u891a\u4e91\u5cf0\u7684\u81ea\u8fa9\u6545\u610f\u626d\u8f6c\u8bdd\u9898\uff0c\u60f3\u8981\u63a9\u9970\u4ed6\u539f\u6765\u7684\u610f\u601d\uff0c\u5374\u4e0d\u77e5\u8fd9\u6b63 \u662f\u6b32\u76d6\u5f25\u5f70\u3002 \u3000\u3000\u5b5f\u660e\u971e\u5fc3\u91cc\u6697\u6697\u597d\u7b11\uff0c\u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u6211\u653e\u5fc3\u4e0d\u4e0b\u7684\u662f\u4ed6\u7684\u672a\u5a5a\u59bb\u5b50\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u6014\u4e86\u4e00\u6014\uff0c\u8be7\u9053\uff1a\u201c\u674e\u76df\u4e3b\u65e9\u5df2\u8ba2\u4e86\u4eb2\u7684\u4e48\uff1f\u4ed6\u7684\u672a\u5a5a\u59bb\u5b50\u73b0\u5728\u54ea\u513f\uff0c\u4f55\u4ee5\u4f1a\u4ee4 \u4f60\u653e\u5fc3\u4e0d\u4e0b\uff1f\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u90a3\u665a\u9ad8\u53eb\u6349\u5978\u7ec6\u7684\u90a3\u4e2a\u5c0f\u55bd\u5175\uff0c\u5c31\u662f\u674e\u6069\u5357\u7684\u672a\u5a5a\u59bb\u5b50\u4e86\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u5927\u4e3a\u5947\u602a\uff0c\u8bf4\u9053\uff1a\u201c\u5979\u4e3a\u4f55\u8981\u626e\u4f5c\u5c0f\u55bd\u5175\uff1f\u201d \u3000\u3000\u5b5f\u660e\u971e\u7b11\u9053\uff1a\u201c\u4eba\u5bb6\u592b\u59bb\u95f4\u7684\u4e8b\u60c5\uff0c\u4f60\u5c31\u4e0d\u5fc5\u7ba1\u8fd9\u8bb8\u591a\u4e86\u3002\u603b\u4e4b\uff0c\u4ed6\u8fd9\u672a\u5a5a\u59bb\u5b50\u5982\u4eca\u4e5f \u6b63\u662f\u671d\u7740\u8fd9\u6761\u8def\u8d70\uff0c\u548c\u54b1\u4eec\u4e00\u6837\u662f\u8981\u5230\u98de\u9f99\u5c71\u7684\u3002\u5979\u7684\u6c5f\u6e56\u9605\u5386\u53ef\u4e0d\u80fd\u548c\u76df\u4e3b\u76f8\u6bd4\uff0c\u662f\u4ee5\u6211 \u4e0d\u653e\u5fc3\u3002\u201d\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u54e6\uff0c\u6240\u4ee5\u4f60\u8981\u5728\u6697\u4e2d\u4fdd\u62a4\u5979\u7684\u3002\u201d\u5b5f\u660e\u971e\u4e0d\u613f\u628a\u4e2a\u4e2d\u66f2\u6298\u8bf4\u51fa\u6765\uff0c \u53ea\u597d\u542b\u7cca\u8bf4\u9053\uff1a\u201c\u4e5f\u53ef\u4ee5\u8fd9\u6837\u8bf4\u5427\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u6ee1\u6000\u6b22\u559c\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u6211\u53ea\u77e5\u9053\u5b5f\u59d1\u5a18\u662f\u503e\u5fc3\u4e8e\u674e\u601d\u5357\uff0c\u5374\u539f\u6765\u5b8c\u5168\u4e0d\u662f\u8fd9\u4e00 \u56de\u4e8b\u3002\u55ef\uff0c\u53ea\u8981\u4e0d\u662f\u8fd9\u6837\u4fbf\u597d\u4e86\uff0c\u6211\u53c8\u4f55\u5fc5\u53bb\u63a2\u542c\u4eba\u5bb6\u7684\u9690\u79c1\u3002\u201d\u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c \u90a3\u4e48\u54b1\u4eec\u5c31\u8d76\u5feb\u53bb\u98de\u9f99\u5c71\u5427\u3002\u201d\u4f46\u6c42\u5f97\u4e0e\u5b5f\u660e\u971e\u540c\u884c\uff0c\u5c31\u4e0d\u518d\u95ee\u674e\u601d\u5357\u548c\u6768\u5a49\u7684\u4e8b\u60c5\u4e86\u3002 \u3000\u3000\u5b5f\u660e\u971e\u628a\u6768\u5a49\u7684\u8eab\u4efd\u544a\u8bc9\u891a\u4e91\u5cf0\u4e4b\u540e\uff0c\u5ffd\u5730\u5fc3\u5ff5\u4e00\u52a8\uff0c\u5c31\u50cf\u521a\u624d\u891a\u4e91\u5cf0\u201c\u8bf4\u9519\u201d\u4e86\u8bdd\u4e4b \u540e\u7684\u60f3\u6cd5\u4e00\u6837\uff0c\u81ea\u5df1\u4e5f\u89c9\u5f97\u6709\u70b9\u5947\u602a\uff1a\u201c\u6211\u4e3a\u4ec0\u4e48\u8981\u628a\u8fd9\u4ef6\u4e8b\u544a\u8bc9\u4ed6\uff1f\u96be\u9053\u6211\u662f\u6015\u4ed6\u8bef\u4f1a \u4e48\uff1f\u201d\u4ed6\u4eec\u4e8c\u4eba\uff0c\u4e0d\u7ea6\u800c\u540c\u5730\u90fd\u53d1\u73b0\u4e86\u81ea\u5df1\u5fc3\u5e95\u7684\u79d8\u5bc6\u4e86\u3002 \u3000\u3000\u6309\u4e0b\u891a\u3001\u5b5f\u4e8c\u4eba\u4e0d\u8868\u3002\u4e14\u8bf4\u6768\u5a49\u4e0b\u5c71\u4e4b\u540e\uff0c\u6005\u6005\u60c6\u60c6\u5730\u72ec\u81ea\u524d\u884c\uff0c\u5fc3\u60c5\u548c\u891a\u3001\u5b5f\u4e8c\u4eba\u5219 \u662f\u5927\u4e0d\u76f8\u540c\u4e86\u3002 \u3000\u3000\u6768\u5a49\u626e\u4f5c\u4e00\u4e2a\u76f8\u8c8c\u4e11\u964b\u7684\u5c0f\u53ae\uff0c\u522b\u4eba\u4e0d\u77e5\u9053\u5979\u672c\u6765\u662f\u4e2a\u7f8e\u8c8c\u7684\u5973\u5b50\uff0c\u5f53\u7136\u4e5f\u5c31\u4e0d\u4f1a\u53bb\u6ce8 \u610f\u5979\u3002\u662f\u4ee5\u5b5f\u660e\u971e\u6240\u66fe\u906d\u9047\u7684\u5c34\u5c2c\u4e8b\u60c5\uff0c\u5979\u662f\u4e00\u4ef6\u6ca1\u78b0\u8fc7\u3002 \u3000\u3000\u91c7\u82b1\u8d3c\u7684\u6848\u5b50\u5728\u8fd9\u51e0\u53bf\u95f9\u5f97\u6cb8\u6cb8\u626c\u626c\uff0c\u6768\u5a49\u4e00\u8def\u884c\u6765\uff0c\u4e5f\u66fe\u542c\u5f97\u6709\u4eba\u8c08\u8bba\u90a3\u4e2a\u6deb\u90aa\u6b8b\u66b4 \u7684\u91c7\u82b1\u8d3c\uff0c\u4f46\u56e0\u81ea\u5df1\u6709\u4e8b\u5728\u8eab\uff0c\u4e0d\u80fd\u803d\u6401\uff0c\u867d\u7136\u75db\u6068\uff0c\u5bf9\u8fd9\u91c7\u82b1\u8d3c\u4e5f\u662f\u65e0\u53ef\u5948\u4f55\uff0c\u53ea\u597d\u5f53\u505a \u65b0\u95fb\u6765\u542c\uff0c\u542c\u8fc7\u4e5f\u5c31\u7b97\u4e86\u3002 \u3000\u3000\u6768\u5a49\u56e0\u4e3a\u9053\u8def\u4e0d\u719f\uff0c\u867d\u7136\u662f\u65e9\u534a\u5929\u4e0b\u5c71\uff0c\u8d70\u5f97\u53cd\u800c\u6bd4\u5b5f\u660e\u971e\u8f83\u6162\u3002 \u3000\u3000\u8fd9\u65e5\uff0c\u6768\u5a49\u7ecf\u8fc7\u90a3\u5ea7\u53bf\u57ce\uff0c\u56e0\u4e3a\u65f6\u95f4\u5c1a\u65e9\uff0c\u6ca1\u6709\u8fdb\u57ce\u6295\u5bbf\u3002 \u3000\u3000\u8499\u53e4\u5927\u519b\u5357\u4fb5\u7684\u6d88\u606f\u5df2\u4f20\u6765\u66f4\u517c\u53c8\u6709\u91c7\u82b1\u8d3c\u7684\u51fa\u73b0\uff0c\u662f\u4ee5\u8d8a\u5f80\u5317\u8d70\uff0c\u8def\u4e0a\u884c\u4eba\u8d8a\u5c11\u3002\u6768 \u5a49\u6025\u4e8e\u8d76\u5230\u98de\u9f99\u5c71\u53bb\uff0c\u4e0d\u77e5\u4e0d\u89c9\u5df2\u662f\u5165\u9ed1\u65f6\u5206\uff0c\u8d70\u5230\u4e86\u8352\u5c71\u91ce\u5730\uff0c\u9519\u4e86\u5bbf\u5934\uff0c\u627e\u4e0d\u7740\u4eba\u5bb6 \u4e86\u3002 \u3000\u3000\u5317\u65b9\u7684\u6c14\u5019\u6bd4\u5357\u65b9\u5bd2\u51b7\u5f97\u591a\uff0c\u6b64\u65f6\u5df2\u662f\u65e9\u6625\u4e8c\u6708\uff0c\u4e00\u5230\u5165\u9ed1\uff0c\u9633\u5149\u6d88\u901d\u4e4b\u540e\uff0c\u4fbf\u4f3c\u56de\u5230 \u4e86\u5bd2\u51b7\u7684\u4e25\u51ac\u3001\u5929\u4e0a\u98d8\u4e0b\u9e45\u6bdb\u822c\u7684\u96ea\u82b1\u3002 \u3000\u3000\u6768\u5a49\u60c5\u6000\u60d8\u60d8\uff0c\u672c\u6765\u662f\u4e0d\u613f\u610f\u56de\u5fc6\u7684\u65e7\u4e8b\uff0c\u4e0d\u7531\u81ea\u5df1\u5730\u6d8c\u4e0a\u4e86\u5fc3\u5934\u3002 \u3000\u3000\u5979\u60f3\u8d77\u4e86\u4e0e\u674e\u601d\u5357\u5728\u56de\u56fd\u9014\u4e2d\u7684\u4e00\u4e2a\u96ea\u591c\uff0c\u4e24\u4eba\u5fcd\u53d7\u7740\u9965\u5bd2\uff0c\u5374\u5728\u61a7\u61ac\u7740\u5bb6\u5ba4\u4e4b\u4e50\u3002\u6768 \u5a49\u8bf4\u5979\u5bb6\u91cc\u85cf\u4e66\u751a\u5bcc\uff0c\u674e\u601d\u5357\u8bf4\u4eba\u751f\u7684\u4e50\u4e8b\u4e4b\u65f6\uff0c\u5c31\u662f\u96ea\u591c\u5173\u95e8\u8bfb\u4e66\uff0c\u5982\u679c\u518d\u6709\u4e00\u58f6\u9152\uff0c\u4e00 \u7089\u9999\uff0c\u90a3\u5c31\u66f4\u5176\u7f8e\u5999\u4e86\u3002\u6768\u5a49\u7b11\u8bf4\u6211\u53ef\u4ee5\u7ed9\u4f60\u6696\u9152\uff0c\u7ed9\u4f60\u6dfb\u9999\u3002\u8bf4\u5230\u6b64\u5904\uff0c\u4e24\u4eba\u4e0d\u7ea6\u800c\u540c\u5730 \u90fd\u5ff5\u51fa\u4e86\u53e4\u4eba\u201c\u7ea2\u8896\u6dfb\u9999\u591c\u8bfb\u4e66\u201d\u7684\u8bd7\u53e5\u3002 \u3000\u3000\u56de\u5fc6\u7a81\u7136\u53d8\u4e86\u4e00\u676f\u82e6\u9152\u3002\u6768\u5a49\u53f9\u4e86\u53e3\u6c14\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5982\u4eca\u7ed9\u4f60\u7ea2\u8896\u6dfb\u9999\u7684\u5df2\u662f\u53e6\u6709\u5176 \u4eba\uff0c\u4e0d\u5fc5\u6211\u6765\u670d\u4f8d\u4f60\u4e86\u3002\u201d\u6768\u5a49\u72ec\u81ea\u8e0f\u96ea\u524d\u884c\uff0c\u611f\u89c9\u4e00\u7247\u51b0\u51b7\u3002\u5e76\u975e\u5979\u7684\u8eab\u4f53\u7ecf\u53d7\u4e0d\u8d77\uff0c\u800c \u662f\u96ea\u82b1\u98d8\u5728\u5979\u7684\u8eab\u8eaf\uff0c\u51b7\u5728\u5979\u7684\u5fc3\u5934\u3002 \u3000\u3000\u773c\u524d\u662f\u4e00\u7247\u53ae\u6740\u7684\u5bd2\u51b7\u666f\u8c61\uff0c\u56de\u5fc6\u5374\u53c8\u56de\u5230\u4e86\u98ce\u548c\u65e5\u4e3d\u7684\u6625\u5929\u3002\u90a3\u4e00\u5929\u5979\u8eb2\u5728\u674e\u601d\u5357\u90a3 \u6761\u6751\u5b50\u7684\u5c71\u5761\u4e0a\uff0c\u773c\u770b\u7740\u674e\u601d\u5357\u548c\u5b5f\u660e\u971e\u53cc\u53cc\u8d70\u8fc7\u5c0f\u6865\uff0c\u674e\u601d\u5357\u6458\u4e0b\u51e0\u6735\u91ce\u82b1\uff0c\u629b\u5165\u6cb3\u4e2d\uff0c \u7247\u7247\u843d\u82b1\uff0c\u968f\u6ce2\u800c\u901d\u3002\u5979\u542c\u5f97\u674e\u601d\u5357\u7684\u4e00\u58f0\u957f\u53f9\uff0c\u5979\u542c\u5f97\u5c0f\u6cb3\u6d41\u7684\u545c\u54bd\u3002\u201c\u4f24\u5fc3\u6865\u4e0b\u6625\u6ce2 \u7eff\uff0c\u66fe\u89c1\u60ca\u9e3f\u63a0\u5f71\u6765\u3002\u2019\u90a3\u65f6\u5357\u54e5\u5728\u60f3\u7740\u4ec0\u4e48\u5fc3\u4e8b\u5462\uff1f\u4ed6\u662f\u51b3\u8ba1\u4e0d\u4f1a\u77e5\u9053\u6211\u5c31\u8eb2\u5728\u4ed6\u7684\u8eab\u8fb9 \u7684\uff0c\u4ed6\u7684\u4f24\u5fc3\u5f53\u7136\u4e0d\u662f\u505a\u7ed9\u6211\u770b\u3002\u5509\uff0c\u83ab\u975e\u4ed6\u7684\u786e\u662f\u8fd8\u6ca1\u6709\u5fd8\u8bb0\u6211\uff0c\u4ed6\u5728\u60f3\u8d77\u6211\u4eec\u66fe\u7ecf\u505a\u8fc7 \u7684\u5f80\u4e8b\uff1f\u201d\u5fc3\u4e2d\u5fae\u611f\u6696\u610f\uff0c\u4f46\u968f\u5373\u53c8\u5728\u81ea\u7b11\u81ea\u601c\uff1a\u201c\u8fd9\u4e0d\u8fc7\u662f\u6211\u7684\u75f4\u5fc3\u5984\u60f3\u7f62\u4e86\uff0c\u5357\u54e5\u662f\u6709 \u4e86\u65b0\u4eba\uff0c\u8fd8\u4f1a\u8bb0\u5f97\u65e7\u4eba\uff1f\u201d \u3000\u3000\u56de\u5fc6\u91cc\u7684\u201c\u6625\u5929\u201d\u6bd4\u4e25\u51ac\u66f4\u51b7\uff0c\u6768\u5a49\u4e00\u58f0\u957f\u53f9\uff1a\u201c\u6211\u8fd8\u5728\u60f3\u8fd9\u4e9b\u5e72\u561b\uff01\u201d\u731b\u4e00\u62ac\u5934\u53ea\u89c1 \u767d\u96ea\u7691\u7691\u7684\u5c71\u5934\u6709\u4e00\u5ea7\u53e4\u5e99\uff0c\u81ea\u5df1\u4e0d\u77e5\u4e0d\u89c9\u4e4b\u95f4\uff0c\u5df2\u662f\u8d70\u5230\u53e4\u5e99\u4e4b\u524d\u4e86\u3002 \u3000\u3000\u6768\u5a49\u7981\u4e0d\u4f4f\u53c8\u60f3\u8d77\u4e86\u90a3\u4e2a\u96ea\u591c\u548c\u674e\u601d\u5357\u8eb2\u5728\u78e8\u623f\u70e4\u706b\u7684\u4e8b\uff0c\u5c31\u662f\u90a3\u5929\u665a\u4e0a\uff0c\u5979\u8ba4\u8bc6\u5c60\u51e4 \u7684\u3002\u60f3\u4e0d\u5230\u53ea\u51e0\u4e2a\u6708\u529f\u592b\uff0c\u4e00\u4ef6\u4ef6\u7684\u4e8b\u60c5\u53d8\u5316\u5f97\u5927\u5927\u51fa\u4e4e\u5979\u610f\u6599\u4e4b\u5916\uff0c\u5982\u4eca\u5979\u662f\u6084\u6084\u5730\u79bb\u5f00 \u4e86\u5c60\u51e4\u5c71\u5be8\uff0c\u53ea\u5f71\u5b64\u8eab\u5730\u8d70\u5230\u8fd9\u91cc\u6765\u4e86\u3002\u201c\u53cd\u6b63\u6211\u4e0d\u5230\u5bbf\u5934\uff0c\u5c31\u6682\u65f6\u5728\u8fd9\u7834\u5e99\u8fc7\u4e00\u665a\u5427\u3002\u201d \u6768\u5a49\u5fc3\u60f3\u3002 \u3000\u3000\u6768\u5a49\u8d70\u8fdb\u5e99\u95e8\uff0c\u53ea\u89c1\u5730\u4e0a\u6709\u4e00\u5806\u706b\uff0c\u67f4\u90fd\u70e7\u5f97\u5dee\u4e0d\u591a\u53d8\u6210\u70ad\u4e86\uff0c\u4f46\u8fd8\u5728\u5410\u7740\u5fae\u5f31\u7684\u706b \u82d7\uff0c\u5c1a\u672a\u5b8c\u5168\u7184\u706d\u3002 \u3000\u3000\u6768\u5a49\u770b\u89c1\u5e99\u91cc\u5e76\u6ca1\u6709\u4eba\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u60f3\u5fc5\u662f\u5317\u65b9\u9003\u96be\u7684\u96be\u6c11\u66fe\u5728\u8fd9\u91cc\u6b47\u8fc7\uff0c\u5df2\u7ecf\u8d70 \u4e86\u3002\u8bf4\u4e0d\u5b9a\u4e5f\u53ef\u80fd\u662f\u53eb\u5316\u5b50\u3002\u4f46\u5728\u8fd9\u6837\u7684\u5bd2\u591c\uff0c\u4ed6\u4eec\u65e2\u7136\u751f\u4e86\u706b\uff0c\u5374\u53c8\u4e3a\u4f55\u4e0d\u5728\u5e99\u91cc\u4f4f\u4e00\u665a \u5462\uff1f\u201d\u6768\u5a49\u731c\u60f3\u4e0d\u900f\uff0c\u968f\u624b\u52a0\u4e86\u51e0\u5757\u5e72\u67f4\uff0c\u628a\u706b\u91cd\u65b0\u70e7\u65fa\u8d77\u6765\uff0c\u60f3\u9053\uff1a\u201c\u6211\u73b0\u5728\u7a7f\u7684\u662f\u5c0f\u55bd \u5175\u7684\u7834\u65e7\u8863\u88f3\uff0c\u6ee1\u8eab\u5c18\u571f\uff0c\u770b\u6765\u5927\u7ea6\u4e5f\u50cf\u4e2a\u96be\u6c11\u4e86\u3002\u5c31\u662f\u6709\u4eba\u56de\u6765\uff0c\u6211\u4e5f\u4e0d\u5fc5\u907f\u5fcc\u3002\u201d \u3000\u3000\u8fd9\u665a\u7684\u60c5\u666f\u592a\u50cf\u5979\u4e0e\u674e\u601d\u5357\u5728\u78e8\u623f\u70e4\u706b\u7684\u90a3\u4e00\u591c\u4e86\uff0c\u9664\u4e86\u4e00\u5904\u662f\u78e8\u623f\uff0c\u4e00\u5904\u6709\u53e4\u5e99\u4e4b \u5916\uff0c\u6240\u4e0d\u540c\u7684\uff0c\u53ea\u662f\u6b64\u9645\u5979\u7684\u8eab\u8fb9\u5e76\u6ca1\u6709\u4e00\u4e2a\u674e\u601d\u5357\u3002 \u3000\u3000\u6768\u5a49\u6b63\u81ea\u6d6e\u60f3\u8054\u7fe9\uff0c\u5ffd\u542c\u5f97\u6709\u4e09\u4e0b\u638c\u58f0\uff0c\u4e24\u957f\u4e00\u77ed\uff0c\u8fd9\u638c\u58f0\u662f\u5bf9\u6b63\u5e99\u95e8\u7684\u65b9\u5411\u4f20\u6765\u7684\u3002 \u6388\u7740\u4e1c\u897f\u4e24\u9762\uff0c\u4e5f\u4f20\u6765\u8fd9\u6837\u7684\u4e09\u4e0b\u638c\u58f0\u3002 \u3000\u3000\u6768\u5a49\u591a\u5c11\u6709\u70b9\u6c5f\u6e56\u7ecf\u9a8c\uff0c\u4e00\u542c\u4fbf\u77e5\u8fd9\u4e9b\u4eba\u662f\u4ee5\u638c\u58f0\u4f5c\u4e3a\u6697\u53f7\uff0c\u4e0d\u7981\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a \u201c\u539f\u6765\u662f\u9ed1\u9053\u4e2d\u4eba\u5728\u8fd9\u91cc\u805a\u4f1a\u3002\u201d \u3000\u3000\u82e5\u662f\u666e\u901a\u7684\u96be\u6c11\uff0c\u6768\u5a49\u4e0d\u7528\u8eb2\u907f\uff0c\u4f46\u6765\u7684\u662f\u9ed1\u9053\u4e2d\u4eba\uff0c\u6768\u5a49\u5c31\u4e0d\u80fd\u4e0d\u6682\u4e14\u4e00\u8eb2\u4e86\u3002\u987b\u77e5 \u9ed1\u9053\u4e2d\u4eba\uff0c\u662f\u6700\u5fcc\u7ed9\u5916\u4eba\u77e5\u9053\u4ed6\u4eec\u7684\u79d8\u5bc6\u7684\u3002 \u3000\u3000\u8fd9\u5ea7\u795e\u5e99\u7834\u65e7\u4e0d\u582a\uff0c\u4f46\u795e\u9f9b\u5374\u8fd8\u76f8\u5f53\u5b8c\u6574\u3002\u90a3\u5c0a\u5750\u7740\u7684\u795e\u50cf\uff0c\u6bd4\u6768\u5a49\u7ad9\u8d77\u6765\u8fd8\u9ad8\u4e00\u4e2a \u5934\uff0c\u800c\u4e14\u8fd8\u6709\u5e10\u5e4c\u906e\u63a9\u3002\u6768\u5a49\u627e\u4e0d\u5230\u53ef\u4ee5\u8eb2\u85cf\u7684\u5730\u65b9\uff0c\u53ea\u597d\u63ed\u5f00\u5e10\u5e4c\uff0c\u8eb2\u5230\u795e\u50cf\u540e\u9762\u3002 \u3000\u3000\u521a\u521a\u8eb2\u597d\uff0c\u811a\u6b65\u58f0\u5df2\u7ecf\u8fdb\u4e86\u5e99\u95e8\u3002\u6768\u5a49\u5077\u7aa5\u51fa\u53bb\uff0c\u53ea\u89c1\u6765\u7684\u662f\u4e09\u4e2a\u7c97\u8c6a\u6c49\u5b50\uff0c\u4e2d\u95f4\u4e00\u4e2a \u9762\u6709\u5200\u75a4\u7684\u6c49\u5b50\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u521a\u624d\u5df2\u7ecf\u6765\u8fc7\u4e86\u4e48\uff1f\u201d\u4e3a\u9996\u7684\u90a3\u4eba\u8bf4\u9053\uff1a\u201c\u6ca1 \u6709\u5440\u3002\u718a\u5927\u54e5\uff0c\u4f60\u4e3a\u4f55\u6709\u6b64\u4e00\u95ee\uff1f\u201d \u3000\u3000\u90a3\u718a\u5927\u54e5\u9053\uff1a\u201c\u6211\u597d\u50cf\u8bb0\u5f97\u51fa\u6765\u4e4b\u65f6\uff0c\u5e76\u672a\u6dfb\u4e0a\u67f4\u706b\uff0c\u4e0d\u8be5\u70e7\u5f97\u8fd9\u6837\u65fa\u7684\u3002\u201d\u5de6\u9996\u7684\u90a3 \u4eba\u8bf4\u9053\uff1a\u201c\u6216\u8005\u662f\u8d3a\u4e5d\u516c\u6765\u8fc7\u4e86\u3002\u4ed6\u548c\u4f60\u90fd\u662f\u8fd9\u513f\u7684\u5730\u4e3b\u3002\u201d\u90a3\u718a\u5927\u54e5\u70b9\u4e86\u70b9\u5934\u8bf4\u9053\uff1a\u201c\u4e0d \u9519\uff0c\u4e00\u5b9a\u662f\u4e5d\u516c\u4ed6\u8001\u4eba\u5bb6\u3002\u201d \u3000\u3000\u53f3\u9996\u7684\u90a3\u4eba\u9053\uff1a\u201c\u4e5d\u516c\u627e\u6211\u4eec\u6765\uff0c\u4e0d\u77e5\u4e3a\u4e86\u4f55\u4e8b\uff1f\u201d\u90a3\u718a\u5927\u54e5\u5c1a\u672a\u56de\u7b54\uff0c\u5de6\u9996\u7684\u90a3\u4eba\u5df2 \u5728\u53eb\u9053\uff1a\u201c\u4e5d\u516c\u6765\u4e86\uff01\u201d \u3000\u3000\u8d3a\u4e5d\u516c\u4e5f\u662f\u548c\u4e24\u4e2a\u4eba\u540c\u6765\uff0c\u5148\u6765\u7684\u90a3\u4e09\u4e2a\u4eba\u5411\u4ed6\u884c\u8fc7\u4e86\u793c\uff01\u5176\u4e2d\u4e24\u4eba\u95ee\u9053\uff1a\u201c\u8d3a\u5927\u5a18 \u5462\uff1f\u201d\u8d3a\u4e5d\u516c\u9053\uff1a\u201c\u6628\u665a\u6211\u4eec\u642c\u4e86\u5bb6\uff0c\u5979\u7559\u5728\u5bb6\u91cc\u3002\u201d\u90a3\u4e2a\u59d3\u718a\u7684\u7b11\u9053\uff1a\u201c\u4e5d\u516c\u5bb6\u91cc\u6765\u4e86\u8d35 \u5ba2\uff0c\u4f60\u4eec\u4e24\u4f4d\u5c1a\u672a\u77e5\u9053\u4e48\uff1f\u201d \u3000\u3000\u90a3\u4e24\u4eba\u9053\uff1a\u201c\u4e0d\u77e5\u662f\u54ea\u4f4d\u8d35\u5ba2\uff1f\u201d\u8d3a\u4e5d\u516c\u9053\uff1a\u201c\u5f85\u4f1a\u513f\u518d\u8bf4\u3002\u718a\u8001\u5927\uff0c\u4f60\u628a\u4eca\u665a\u4e4b\u4e8b\u544a \u8bc9\u4e86\u4ed6\u4eec\u6ca1\u6709\uff1f\u201d\u6b63\u662f\uff1a \u3000\u3000\u4f55\u5904\u6f5c\u6765\u795e\u79d8\u5ba2\uff0c\u72d0\u7fa4\u72d7\u515a\u6562\u5174\u6ce2\uff1f \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u4e5d\u56de\u3000\u6bd4\u7ffc\u51cc\u7a7a\u60b2\u94e9\u7fbd\u3000\u8fde\u679d\u5927\u5730\u611f\u540c\u601c \u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b \u7b2c\u4e8c\u5341\u4e5d\u56de\u3000\u6bd4\u7ffc\u51cc\u7a7a\u60b2\u94e9\u7fbd\u3000\u8fde\u679d\u5927\u5730\u611f\u540c\u601c \u3000\u3000\u9762\u6709\u5200\u75a4\u7684\u90a3\u4e2a\u6c49\u5b50\u8bf4\u9053\uff1a\u201c\u996e\u9a6c\u5ddd\u7684\u5f20\u5be8\u4e3b\u548c\u91ce\u732a\u6797\u7684\u77f3\u5e2e\u4e3b\u5df2\u7ecf\u77e5\u9053\u4e86\uff0c\u5927\u718a\u5e84\u7684 \u718a\u5e84\u4e3b\u521a\u521a\u6765\u5230\uff0c\u5c1a\u672a\u66fe\u544a\u8bc9\u4ed6\u3002\u201d\u8ddf\u4e5d\u516c\u4e00\u540c\u8fdb\u6765\u7684\u90a3\u4e24\u4e2a\u6c49\u5b50\u9053\uff1a\u201c\u6211\u4eec\u4e5f\u662f\u521a\u521a\u8d76\u5230 \u4e86\uff0c\u4e0d\u77e5\u4e5d\u516c\u98de\u51fd\u76f8\u53ec\uff0c\u4e3a\u4e86\u4f55\u4e8b\uff0c\u6b63\u8981\u8bf7\u6559\u3002\u201d \u3000\u3000\u8d3a\u4e5d\u516c\u9053\uff1a\u201c\u8001\u673d\u53d7\u4eba\u4e4b\u6258\uff0c\u60f3\u8bf7\u5404\u4f4d\u9f0e\u529b\u5e2e\u5fd9\u3002\u201d\u90a3\u4e09\u4e2a\u672a\u77e5\u539f\u59d4\u7684\u4eba\u9f50\u58f0\u8bf4\u9053\uff1a \u201c\u4e5d\u516c\u4e0d\u5fc5\u5ba2\u6c14\uff0c\u6211\u4eec\u90fd\u662f\u9760\u4f60\u8001\u6491\u8170\u7684\uff0c\u6709\u8bdd\u53ea\u7ba1\u5429\u5490\u597d\u4e86\u3002\u201d \u3000\u3000\u8d3a\u4e5d\u516c\u9053\uff1a\u201c\u4e5f\u4e0d\u662f\u4ec0\u4e48\u5927\u4e8b\uff0c\u53ea\u662f\u60f3\u8bf7\u5404\u4f4d\u5e2e\u5fd9\u67e5\u63a2\u4e24\u4e2a\u4eba\u7684\u53bb\u5411\u3002\u201d \u3000\u3000\u996e\u9a6c\u5ddd\u7684\u5f20\u5be8\u4e3b\u95ee\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u4eba\u53ef\u662f\u8d35\u53cb\u7684\u4ec7\u5bb6\uff1f\u201d\u8d3a\u4e5d\u516c\u70b9\u4e86\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u4e5f\u662f \u6211\u7684\u4ec7\u4eba\uff01\u201d\u5927\u718a\u5e84\u7684\u5e84\u4e3b\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u6211\u4eec\u4e00\u53d1\u73b0\u8fd9\u4e24\u4e2a\u4eba\u7684\u884c\u8e2a\uff0c\u5e72\u8106\u5c31\u628a \u4ed6\u4eec\u64d2\u4e0b\uff0c\u4ea4\u7ed9\u4e5d\u516c\u53d1\u843d\u5c31\u662f\u3002\u8bf7\u4e5d\u516c\u544a\u8bc9\u6211\u4eec\u4e00\u70b9\u7ebf\u7d22\uff0c\u6211\u4eec\u9a6c\u4e0a\u5c31\u53bb\u62ff\u4eba\u3002\u201d \u3000\u3000\u90a3\u77f3\u5e2e\u4e3b\u5374\u662f\u6bd4\u8f83\u8001\u6210\u6301\u91cd\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8d3a\u4e5d\u516c\u867d\u8bf4\u4e0d\u662f\u4ec0\u4e48\u5927\u4e8b\uff0c\u4f46\u4ee5\u4ed6\u7684\u672c\u9886\uff0c \u5c1a\u8981\u5174\u5e08\u52a8\u4f17\uff0c\u5bf9\u4ed8\u8fd9\u4e24\u4e2a\u4eba\u3002\u8fd9\u4e24\u4e2a\u60f3\u5fc5\u4e0d\u662f\u7b49\u95f2\u4e4b\u8f88\u4e86\u3002\u201d\u4e8e\u662f\u5c0f\u5fc3\u7ffc\u7ffc\u5730\u95ee\u9053\uff1a\u201c\u4e0d \u77e5\u8fd9\u4e24\u4e2a\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u8d3a\u4e5d\u516c\u9053\uff1a\u201c\u8fd9\u4e24\u4eba\u4e00\u7537\u4e00\u5973\uff0c\u7537\u7684\u7ea6\u83ab\u6709\u4e8c\u5341\u6765\u5c81\uff0c\u5973\u7684\u5219\u4f3c\u4e4e\u672a\u6ee1\u4e8c\u5341\u3002\u201d \u3000\u3000\u718a\u5e84\u4e3b\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u539f\u6765\u662f\u4e24\u4e2a\u4e73\u81ed\u672a\u5e72\u7684\u5c0f\u5b50\u3001\u4e2b\u5934\uff0c\u90a3\u8fd8\u4e0d\u624b\u5230\u64d2\u6765\uff1f\u8fd9\u4e24\u4e2a\u4eba\u53eb \u4ec0\u4e48\u540d\u5b57\uff1f\u201d \u3000\u3000\u8d3a\u4e5d\u516c\u9053\uff1a\u201c\u7537\u7684\u53eb\u891a\u4e91\u5cf0\uff0c\u5973\u7684\u53eb\u5b5f\u660e\u971e\u3002\u201d \u3000\u3000\u77f3\u5e2e\u4e3b\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u5b5f\u660e\u971e\uff1f\u5979\u662f\u4e0d\u662f\u6c5f\u5357\u5927\u4fa0\u5b5f\u5c11\u521a\u7684\u5973\u513f\uff1f\u201d\u8d3a\u4e5d\u516c\u9053\uff1a \u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u718a\u5e84\u4e3b\u4e5f\u662f\u5927\u5403\u4e00\u60ca\uff0c\u767b\u65f6\u7b11\u4e0d\u51fa\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u542c\u8bf4\u891a\u4e91\u5cf0\u662f\u91d1\u56fd\u56fd\u5e08\u9633\u5929\u96f7\u7684\u5f97\u529b\u624b \u4e0b\uff0c\u4e0d\u77e5\u53ef\u662f\u6b64\u4eba\uff1f\u201d\u8d3a\u4e5d\u516c\u53c8\u70b9\u4e86\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u6b63\u662f\u3002\u4ed6\u4e0d\u4ec5\u662f\u9633\u5927\u96f7\u7684\u624b\u4e0b\uff0c\u8fd8\u662f\u9633 \u5929\u96f7\u7684\u5e08\u4f84\u5462\uff01\u201d \u3000\u3000\u6b64\u8a00\u4e00\u51fa\uff0c\u4f17\u4eba\u90fd\u662f\u9762\u9762\u76f8\u89d1\uff0c\u9887\u6709\u96be\u8272\u3002\u718a\u5e84\u4e3b\u8bf4\u9053\uff1a\u201c\u542c\u8bf4\u891a\u4e91\u5cf0\u5df2\u5c3d\u5f97\u4ed6\u5e08\u7236\u534e \u5929\u8679\u7684\u771f\u4f20\uff0c\u5b5f\u660e\u971e\u65e2\u662f\u6c5f\u5357\u5927\u4fa0\u5b5f\u5c11\u521a\u7684\u5973\u513f\uff0c\u672c\u9886\u60f3\u5fc5\u4e5f\u5dee\u4e0d\u5230\u54ea\u91cc\u53bb\u3002\u8fd9\u4e24\u4e2a\u4eba\u6211\u4eec \u6050\u6015\u62db\u60f9\u4e0d\u8d77\uff01\u201d \u3000\u3000\u77f3\u5e2e\u4e3b\u5219\u8bf4\u9053\uff1a\u201c\u6c5f\u5357\u5927\u4fa0\u5b5f\u5c11\u521a\u7684\u5973\u513f\uff0c\u600e\u7684\u4f1a\u4e0e\u91d1\u56fd\u56fd\u5e08\u9633\u5929\u96f7\u7684\u5e08\u4f84\u8d70\u5728\u4e00\u8def\uff0c \u8bf7\u4e5d\u516c\u6055\u6211\u5192\u6627\u731c\u7591\uff0c\u6211\u662f\u89c9\u5f97\u8fd9\u4e8b\u6709\u70b9\u5947\u602a\u3002\u201d \u3000\u3000\u8eb2\u5728\u4f5b\u50cf\u540e\u9762\u7684\u6768\u5a49\u66f4\u662f\u89c9\u5f97\u5947\u602a\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u59d3\u891a\u8fd9\u53ae\u539f\u6765\u679c\u7136\u662f\u5978\u7ec6\u3002\u4f46\u5b5f\u660e\u971e \u82e5\u662f\u53bb\u8ffd\u6355\u4ed6\u7684\uff0c\u89c1\u4e86\u9762\u5c31\u8be5\u52a8\u624b\u624d\u5bf9\uff0c\u4f55\u4ee5\u8fd8\u4f1a\u4e0e\u4ed6\u4e00\u8def\u540c\u884c\u5462\uff1f\u8fd9\u59d3\u8d3a\u7684\u8001\u5934\u513f\u4e3a\u4f55\u53c8 \u8981\u628a\u4ed6\u4eec\u4e8c\u4eba\u4e00\u540c\u62d8\u6355\uff1f\u4ed6\u5230\u5e95\u662f\u5e2e\u54ea\u4e00\u8fb9\u7684\uff1f\u201d \u3000\u3000\u5fc3\u5ff5\u672a\u5df2\uff0c\u53ea\u542c\u5f97\u8d3a\u4e5d\u516c\u5df2\u5728\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6b64\u4e8b\u4e00\u70b9\u4e5f\u4e0d\u5947\u602a\uff01\u201d \u3000\u3000\u996e\u9a6c\u5ddd\u7684\u5f20\u5be8\u4e3b\u9053\uff1a\u201c\u8bf7\u8d3a\u4e5d\u516c\u660e\u767d\u89c1\u544a\u3002\u201d\u8d3a\u4e5d\u516c\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u53ea\u77e5\u5176\u4e00\uff0c\u4e0d\u77e5 \u5176\u4e8c\u3002\u891a\u4e91\u5cf0\u8eab\u4e3a\u91d1\u56fd\u6b66\u58eb\uff0c\u90a3\u662f\u8fc7\u53bb\u7684\u4e8b\uff0c\u73b0\u5728\u4ed6\u65e9\u5df2\u548c\u9633\u5929\u96f7\u95f9\u7ffb\uff01\u201d \u3000\u3000\u718a\u5e84\u4e3b\u6014\u4e86\u4e00\u6014\uff0c\u8bf4\u9053\uff1a\u201c\u4ed6\u7adf\u7136\u548c\u4ed6\u7684\u5e08\u4f2f\u95f9\u7ffb\u4e86\uff1f\u90a3\u4e48\u54b1\u4eec\u5c31\u4e0d\u5fc5\u5bb3\u6015\u4ed6\u80cc\u540e\u7684\u9760 \u5c71\u5566\u3002\u201d \u3000\u3000\u8d3a\u4e5d\u516c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u4eec\u82e5\u662f\u80fd\u591f\u6d3b\u6349\u891a\u4e91\u5cf0\uff0c\u4e0d\u4f46\u4e0d\u7528\u62c5\u5fc3\u9633\u5929\u96f7\u7684\u8d23\u602a\uff0c\u4ed6\u8fd8\u4f1a\u91cd \u91cd\u6709\u8d4f\u5462\uff01\u201d \u3000\u3000\u9762\u6709\u5200\u75a4\u7684\u90a3\u4e2a\u6c49\u5b50\u8bf4\u9053\uff1a\u201c\u8bf8\u4f4d\u82e5\u6709\u6000\u7591\uff0c\u6211\u8fd8\u53ef\u4ee5\u544a\u8bc9\u4f60\u4eec\u4e00\u4ef6\u4e8b\u60c5\u3002\u4e5d\u516c\u521a\u624d\u8bf4 \u6b64\u4e8b\u4ed6\u662f\u53d7\u4eba\u4e4b\u6258\uff0c\u4f60\u4eec\u731c\u90a3\u4e2a\u4eba\u662f\u8c01\uff1f\u201d\u6b64\u4eba\u540d\u53eb\u718a\u58ee\uff0c\u4e43\u662f\u8d3a\u4e5d\u516c\u7684\u526f\u624b\u3002 \u3000\u3000\u718a\u5e84\u4e3b\u9053\uff1a\u201c\u718a\u5144\uff0c\u8fd8\u662f\u8bf7\u4f60\u8d76\u5feb\u628a\u8fd9\u8c1c\u5e95\u63ed\u6653\u5427\uff0c\u6211\u4eec\u600e\u4e48\u4f1a\u77e5\u9053\u5462\uff1f\u201d \u3000\u3000\u718a\u58ee\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u5c31\u662f\u9633\u5929\u96f7\u7684\u4f84\u5b50\uff0c\u9633\u516c\u5b50\u9633\u575a\u767d\u3002\u4ed6\u4e5f\u5c31\u662f\u8fd1\u6765\u90a3\u4e2a\u95f9\u5f97\u6ee1\u57ce\u98ce\u96e8 \u7684\u91c7\u82b1\u8d3c\uff01\u201d \u3000\u3000\u4f17\u4eba\u542c\u4e86\u90fd\u662f\u5927\u4e3a\u8be7\u5f02\uff0c\u95ee\u9053\uff1a\u201c\u9633\u516c\u5b50\u4f55\u4ee5\u4e0d\u5728\u4eac\u4e2d\uff0c\u5374\u8dd1\u51fa\u6765\u505a\u91c7\u82b1\u8d3c\uff1f\u201d \u3000\u3000\u8d3a\u4e5d\u516c\u9053\uff1a\u201c\u8fd9\u5c31\u6b63\u662f\u548c\u891a\u4e91\u5cf0\u6709\u5173\u4e86\u3002\u9633\u516c\u5b50\u662f\u5192\u5145\u891a\u4e91\u5cf0\u5e72\u4e0b\u91c7\u82b1\u6848\u5b50\u7684\u3002\u201d\u996e\u9a6c \u5ddd\u7684\u5f20\u5be8\u4e3b\u9053\uff1a\u201c\u6562\u60c5\u662f\u8981\u5f15\u891a\u4e91\u5cf0\u51fa\u6765\uff1f\u201d\u8d3a\u4e5d\u516c\u7b11\u9053\uff1a\u201c\u5230\u5e95\u662f\u5f20\u5be8\u4e3b\u806a\u660e\uff0c\u4e00\u731c\u5c31 \u7740\u3002\u53ef\u60dc\u5f15\u4e86\u891a\u4e91\u5cf0\u51fa\u6765\uff0c\u5374\u53c8\u7ed9\u4ed6\u9003\u4e86\u3002\u201d \u3000\u3000\u5f53\u4e0b\u8d3a\u4e5d\u516c\u628a\u6628\u665a\u4ed6\u4e0e\u9633\u575a\u767d\u8bbe\u8ba1\u8bf1\u6355\u891a\u3001\u5b5f\u4e8c\u4eba\u7684\u4e8b\u60c5\u8bf4\u4e86\u51fa\u6765\uff0c\u4f17\u4eba\u65b9\u77e5\u4e2a\u4e2d\u66f2 \u6298\u3002 \u3000\u3000\u6768\u5a49\u8eb2\u5728\u4f5b\u50cf\u540e\u9762\u5077\u542c\uff0c\u542c\u5230\u8fd9\u91cc\uff0c\u4e0d\u7981\u53c8\u662f\u60ca\u5947\uff0c\u53c8\u662f\u60ed\u6127\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u90a3\u665a\u6211\u672a \u66fe\u5206\u6e05\u7682\u767d\uff0c\u5c31\u628a\u891a\u4e91\u5cf0\u5f53\u4f5c\u5978\u7ec6\uff0c\u771f\u662f\u592a\u9c81\u83bd\u4e86\u3002\u602a\u4e0d\u5f97\u5f53\u65f6\u4ed6\u597d\u50cf\u6709\u610f\u653e\u6211\u9003\u8d70\u3002\u4f46\u5374 \u4e0d\u77e5\u4ed6\u4e0e\u5c60\u9f99\u6697\u4e2d\u52fe\u642d\uff0c\u53c8\u662f\u600e\u4e48\u4e00\u56de\u4e8b\u60c5\uff1f\u201d\u6768\u5a49\u867d\u7136\u5c1a\u672a\u660e\u767d\u5168\u90e8\u771f\u76f8\uff0c\u4f46\u6765\u9f99\u53bb\u8109\uff0c \u5df2\u662f\u731c\u5230\u51e0\u5206\u3002\u6599\u60f3\u5176\u4e2d\u5fc5\u662f\u53e6\u6709\u539f\u56e0\uff0c\u891a\u4e91\u5cf0\u51b3\u975e\u4e0e\u5c60\u9f99\u4e00\u8def\uff0c\u5426\u5219\u5b5f\u660e\u971e\u600e\u4f1a\u4e0e\u4ed6\u8054 \u624b\u3002 \u3000\u3000\u53ea\u542c\u5f97\u996e\u9a6c\u5ddd\u7684\u90a3\u4e2a\u5f20\u5be8\u4e3b\u53c8\u8bf4\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\uff0c\u90a3\u6211\u4eec\u5c31\u4e0d\u7528\u987e\u5fcc\u4e86\u3002\u53ea\u4e0d\u8fc7\u8fd9\u4e8c\u4eba \u7684\u672c\u9886\u90fd\u662f\u5341\u5206\u4e86\u5f97\u2014\u2014\u201d \u3000\u3000\u8d3a\u4e5d\u516c\u7b11\u9053\uff1a\u201c\u6240\u4ee5\u6211\u5e76\u4e0d\u82db\u6c42\u5404\u4f4d\u5c06\u4ed6\u4eec\u62ff\u4e0b\uff0c\u53ea\u987b\u901a\u98ce\u62a5\u8baf\u5c31\u884c\u3002\u5b5f\u5c11\u521a\u90a3\u5973\u513f\u670d \u4e86\u6211\u7684\u9165\u9aa8\u6563\uff0c\u6599\u60f3\u8dd1\u5f97\u672a\u8fdc\uff0c\u5404\u4f4d\u82e5\u662f\u53d1\u73b0\u4ed6\u4eec\u8e2a\u8ff9\uff0c\u8bf7\u9a6c\u4e0a\u5230\u718a\u8001\u5927\u90a3\u513f\u62a5\u8baf\uff0c\u6211\u548c\u9633 \u516c\u5b50\u81ea\u4f1a\u5bf9\u4ed8\u4ed6\u4eec\u3002\u201d \u3000\u3000\u77f3\u5e2e\u4e3b\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4e48\u4e8b\u4e0d\u5b9c\u8fdf\uff0c\u54b1\u4eec\u9a6c\u4e0a\u56de\u53bb\uff0c\u5404\u81ea\u591a\u6d3e\u4eba\u624b\uff0c\u56db\u51fa\u641c\u67e5\u5c31\u662f\u3002\u201d\u539f \u6765\u8d3a\u4e5d\u516c\u4eca\u665a\u6240\u53ec\u96c6\u7684\u8fd9\u51e0\u4e2a\u4eba\uff0c\u4e43\u662f\u5468\u56f4\u4e09\u767e\u91cc\u5185\u5404\u8def\u9ed1\u9053\u7684\u9996\u9886\u3002\u8fd9\u4e2a\u547d\u4ee4\u4e00\u4e0b\uff0c\u5c31\u7b49 \u4e8e\u662f\u5728\u65b9\u5706\u4e09\u767e\u91cc\u5185\uff0c\u5e03\u4e0b\u5929\u7f57\u5730\u7f51\u4e86\u3002 \u3000\u3000\u6768\u5a49\u867d\u662f\u5bf9\u5b5f\u660e\u971e\u5e76\u65e0\u597d\u611f\uff0c\u4f46\u542c\u5f97\u5979\u5df2\u53d7\u4f24\uff0c\u5fc3\u91cc\u5982\u662f\u4e0d\u7981\u4e3a\u5979\u62c5\u5fe7\uff0c\u6697\u81ea\u601d\u91cf\uff1a \u201c\u6709\u4ec0\u4e48\u529e\u6cd5\u53ef\u4ee5\u5e2e\u5fd9\u5979\u5462\u3002\u55ef\uff0c\u6700\u7b80\u5355\u7684\u529e\u6cd5\u5f53\u7136\u662f\u83ab\u8fc7\u4e8e\u628a\u8981\u641c\u6355\u5979\u7684\u4eba\u5168\u90fd\u6740\u4e86\u3002\u4f46 \u4ed6\u4eec\u5171\u6709\u516d\u4eba\u4e4b\u591a\uff0c\u6b66\u529f\u6df1\u6d45\u5982\u4f55\uff0c\u6211\u662f\u4e00\u70b9\u90fd\u4e0d\u77e5\u9053\uff0c\u5018\u82e5\u6740\u4e0d\u4e86\u4ed6\u4eec\uff0c\u5c82\u975e\u5f04\u5de7\u6210\u62d9\uff1f \u6211\u8d54\u4e86\u6027\u547d\u4e0d\u6253\u7d27\uff0c\u5374\u8fde\u7ed9\u5979\u62a5\u8baf\u7684\u4eba\u90fd\u6ca1\u6709\u4e86\u3002\u201d \u3000\u3000\u6b63\u81ea\u8e0c\u8e87\u672a\u51b3\uff0c\u53ea\u542c\u5f97\u90a3\u8d3a\u4e5d\u516c\u5df2\u5728\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u5c31\u8fd9\u6837\u5427\u3002\u54b1\u4eec\u53ef\u4ee5\u6563\u4e86\u3002\u201d \u3000\u3000\u6768\u5a49\u84e6\u5730\u5f97\u4e86\u4e00\u4e2a\u4e3b\u610f\uff1a\u201c\u770b\u6765\u8fd9\u4e2a\u8d3a\u4e5d\u516c\u4e43\u662f\u4ed6\u4eec\u7684\u9886\u8896\uff0c\u4f1a\u6563\u4e4b\u540e\uff0c\u6211\u5355\u72ec\u8ddf\u8e2a \u4ed6\uff0c\u5148\u628a\u4ed6\u6740\u4e86\u3002\u76c2\u3001\u891a\u4e8c\u4eba\u7684\u884c\u8e2a\uff0c\u4f9d\u7406\u63a8\u6d4b\uff0c\u4e0d\u51fa\u4e24\u6761\u8def\u3002\u4e00\u6761\u662f\u56de\u5c60\u51e4\u7684\u5c71\u5be8\uff1b\u4e00\u6761 \u662f\u524d\u5f80\u98de\u9f99\u5c71\u4e0e\u5b5f\u5927\u4fa0\u548c\u5357\u54e5\u76f8\u4f1a\u3002\u82e5\u662f\u524d\u8005\uff0c\u6709\u5c71\u5be8\u7684\u4eba\u63a5\u5e94\uff0c\u5927\u6982\u53ef\u4fdd\u65e0\u8651\u3002\u82e5\u662f\u540e \u8005\uff0c\u53cd\u6b63\u6211\u4e5f\u662f\u8981\u5f80\u98de\u9f99\u5c71\u7684\uff0c\u8def\u4e0a\u591a\u52a0\u7559\u5fc3\uff0c\u8bf4\u4e0d\u5b9a\u53ef\u4ee5\u627e\u89c1\u4ed6\u4eec\u3002\u201d \u3000\u3000\u6768\u5a49\u6253\u5b9a\u4e3b\u610f\uff0c\u5012\u662f\u5df4\u4e0d\u5f97\u8fd9\u4e9b\u4eba\u8d76\u5feb\u79bb\u5f00\u3002\u4f46\u60f3\u4e0d\u5230\u5c31\u5728\u8fd9\u4e9b\u4eba\u7eb7\u7eb7\u8d77\u7acb\u4e4b\u65f6\uff0c\u8d3a\u4e5d \u516c\u7684\u526f\u624b\u718a\u58ee\u5ffd\u5730\u8bf4\u9053\uff1a\u201c\u8bf7\u5404\u4f4d\u7a0d\u5f85\u4e00\u4f1a\uff0c\u6709\u4e00\u4ef6\u4e8b\u6211\u60f3\u5f04\u4e2a\u660e\u767d\u3002\u201d\u8d3a\u4e5d\u516c\u9053\uff1a\u201c\u4f55 \u4e8b\uff1f\u201d\u718a\u58ee\u9053\uff1a\u201c\u521a\u624d\u6709\u8c01\u6765\u8fc7\u6ca1\u6709\u4e86\uff1f\u201d\u90a3\u4e9b\u4eba\u9f50\u58f0\u7b54\u9053\uff1a\u201c\u6ca1\u6709\u5440\uff0c\u201d\u8d3a\u4e5d\u516c\u9053\uff1a\u201c\u718a \u5144\u4f55\u4ee5\u6709\u6b64\u4e00\u95ee\uff1f\u201d\u718a\u58ee\u9053\uff1a\u201c\u6211\u51fa\u53bb\u8fce\u63a5\u5404\u4f4d\u4e4b\u65f6\uff0c\u4e00\u65f6\u5fd8\u8bb0\u6dfb\u4e0a\u67f4\u706b\uff0c\u6309\u8bf4\u662f\u5e94\u8be5\u65e9\u5c31 \u7184\u706d\u4e86\u7684\u3002\u4f46\u5404\u4f4d\u4eb2\u773c\u89c1\u5230\uff0c\u5728\u54b1\u4eec\u8fdb\u6765\u4e4b\u9645\uff0c\u8fd9\u706b\u4e0d\u662f\u70e7\u5f97\u6b63\u65fa\u4e48\uff1f\u4e5d\u516c\uff0c\u6211\u8fd8\u4ee5\u4e3a\u662f\u4f60 \u8001\u4eba\u5bb6\u6765\u8fc7\u5462\u3002\u201d\u8d3a\u4e5d\u516c\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u4e00\u5b9a\u662f\u6709\u5916\u4eba\u6765\u8fc7\u4e86\u3002\u201d\u77f3\u5e2e\u4e3b\u4e5f \u9053\uff1a\u201c\u8fd9\u662f\u4e00\u5b9a\u65e0\u7591\u7684\u4e86\uff0c\u53ea\u4e0d\u77e5\u8fd9\u4eba\u8d70\u4e86\u6ca1\u6709\uff1f\u201d \u3000\u3000\u718a\u58ee\u6700\u4e3a\u9c81\u83bd\uff0c\u7acb\u5373\u8bf4\u9053\uff1a\u201c\u8fd9\u4eba\u53ea\u6015\u8fd8\u8eb2\u5728\u5e99\u4e2d\uff0c\u54b1\u4eec\u4e14\u641c\u4ed6\u4e00\u641c\uff01\u201d \u3000\u3000\u6768\u5a49\u624b\u6309\u5251\u67c4\uff0c\u5fc3\u60c5\u4f3c\u7ef7\u7d27\u4e86\u7684\u5f13\u5f26\uff0c\u53ea\u5f85\u4ed6\u4eec\u4e00\u62c9\u5f00\u5e10\u5e54\uff0c\u5c31\u7acb\u5373\u5148\u53d1\u5236\u4eba\uff0c\u8df3\u51fa\u53bb \u548c\u4ed6\u4eec\u51b3\u4e00\u6b7b\u6218\u3002\u6740\u5f97\u4e00\u4e2a\u5c31\u662f\u4e00\u4e2a\uff0c\u6740\u5f97\u4e00\u53cc\u5c31\u662f\u4e00\u53cc\u3002 \u3000\u3000\u773c\u770b\u718a\u58ee\u5c31\u8981\u52a8\u624b\u641c\u7d22\uff0c\u8d3a\u4e5d\u516c\u5ffd\u9053\uff1a\u201c\u4e0d\uff0c\u4f9d\u6211\u770b\u8fd9\u4eba\u4e00\u5b9a\u662f\u5df2\u7ecf\u8d70\u4e86\u3002\u4f60\u60f3\u8fd9\u5e99\u91cc \u80fd\u6709\u591a\u5927\u5730\u65b9\uff0c\u4ed6\u54ea\u4f1a\u6709\u8fd9\u6837\u7b28\u8eb2\u5728\u5e99\u4e2d\u675f\u624b\u5f85\u64d2\uff1f\u54b1\u4eec\u8fd8\u662f\u8d76\u5feb\u5206\u5934\u53bb\u8ffd\uff0c\u8fd8\u53ef\u4ee5\u53ca\u65e9\u5c06 \u4ed6\u6293\u56de\u6765\uff01\u201d \u3000\u3000\u6768\u5a49\u7d27\u5f20\u7684\u5fc3\u60c5\u677e\u4e86\u4e0b\u6765\uff0c\u5fc3\u91cc\u6697\u9053\uff1a\u201c\u7b28\u8d3c\uff0c\u7b28\u8d3c\uff0c\u5feb\u5feb\u8d70\u5427\uff01\u201d\u5979\u8eb2\u5728\u795e\u50cf\u540e\u9762\uff0c \u53ea\u80fd\u4ece\u5e10\u5e54\u7684\u7f1d\u9699\u5077\u7aa5\uff0c\u770b\u4e0d\u6e05\u695a\u5916\u9762\u7684\u60c5\u5f62\uff0c\u5374\u4e0d\u77e5\u8d3a\u4e5d\u516c\u6b64\u65f6\u6b63\u5728\u5411\u5404\u4eba\u6697\u4f7f\u773c\u8272\uff0c\u8fd9 \u4e9b\u4eba\u9664\u4e86\u718a\u58ee\u6027\u60c5\u8f83\u4e3a\u9c81\u83bd\u4e4b\u5916\uff0c\u4e2a\u4e2a\u90fd\u662f\u6c5f\u6e56\u4e0a\u7684\u5927\u884c\u5bb6\uff0c\u4ed6\u4eec\u624d\u4e0d\u7b28\u5462\u3002 \u3000\u3000\u8d3a\u4e5d\u516c\u4f5c\u52bf\u6b32\u8d70\uff0c\u5ffd\u5730\u4e00\u4e2a\u8f6c\u8eab\uff0c\u201c\u547c\u201d\u7684\u4e00\u638c\u5c31\u5411\u795e\u9f9b\u5288\u53bb\uff0c\u559d\u9053\uff1a\u201c\u5c0f\u8d3c\uff0c\u8fd8\u4e0d\u51fa \u6765\uff01\u201d\u8fd9\u4e00\u8bb0\u5288\u7a7a\u638c\u7684\u638c\u529b\uff0c\u767b\u65f6\u628a\u795e\u6a61\u63a8\u5012\uff0c\u538b\u5728\u6768\u5a49\u8eab\u4e0a\uff01 \u3000\u3000\u8fd9\u5ea7\u7834\u5e99\u4e00\u89c8\u65e0\u4f59\uff0c\u8d3a\u4e5d\u516c\u8001\u5978\u5de8\u733e\uff0c\u65e9\u5df2\u6599\u5230\u5e99\u4e2d\u82e5\u662f\u6709\u4eba\u7684\u8bdd\uff0c\u8fd9\u4eba\u4e00\u5b9a\u662f\u85cf\u5728\u795e \u9f9b\u540e\u9762\uff0c\u6545\u6b64\u4f6f\u4f5c\u79bb\u5f00\uff0c\u5374\u51fa\u5176\u4e0d\u610f\u5730\u7a81\u65bd\u5077\u88ad\u3002 \u3000\u3000\u5e78\u800c\u6768\u5a49\u8f7b\u529f\u8d85\u5353\uff0c\u5c31\u5728\u90a3\u795e\u50cf\u5411\u5979\u538b\u4e0b\u4e4b\u65f6\uff0c\u5de6\u638c\u4e00\u63a8\u4e00\u6309\uff0c\u53ea\u542c\u5f97\u201c\u8f70\u9686\u201d\u4e00\u58f0\u3001 \u795e\u50cf\u5012\u4e0b\uff0c\u6768\u5a49\u5374\u5df2\u662f\u817e\u8eab\u800c\u8d77\uff0c\u53f3\u624b\u4ea6\u5df2\u62d4\u5251\u51fa\u9798\u3002 \u3000\u3000\u718a\u5e84\u4e3b\u54c8\u54c8\u5927\u7b11\uff0c\u53eb\u9053\uff1a\u201c\u4e5d\u516c\u795e\u673a\u5999\u7b97\uff0c\u679c\u7136\u628a\u8fd9\u5c0f\u8d3c\u63ea\u51fa\u6765\u4e86\u3002\u597d\u5c0f\u5b50\uff0c\u8dd1\u4e0d\u4e86 \u5566\u3002\u5feb\u5feb\u4ece\u5b9e\u62db\u6765\uff0c\u662f\u8c01\u4e3b\u4f7f\u2014\u2014\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u53ea\u89c1\u5bd2\u5149\u8000\u773c\uff0c\u51b7\u6c14\u68ee\u68ee\uff0c\u6768\u5a49\u4eba\u672a\u7740\u5730\uff0c \u5df2\u662f\u5530\u7684\u4e00\u5251\uff0c\u51cc\u7a7a\u523a\u4e0b\u3002 \u3000\u3000\u718a\u5e84\u4e3b\u5728\u8fd9\u73ed\u4eba\u4e2d\u6027\u60c5\u6700\u4e3a\u66b4\u8e81\uff0c\u6b66\u529f\u5374\u662f\u8f83\u5dee\uff0c\u4ed6\u7ec3\u7684\u662f\u5927\u6454\u7891\u624b\u7684\u529f\u592b\uff0c\u4ee5\u6c14\u529b\u89c1 \u957f\uff0c\u78b0\u4e0a\u4e86\u6768\u5a49\u8f7b\u7075\u8fc5\u6377\u7684\u5251\u6cd5\uff0c\u6839\u672c\u5c31\u6ca1\u6709\u62db\u67b6\u7684\u673a\u4f1a\u3002 \u3000\u3000\u53ea\u542c\u5f97\u201c\u5494\u5693\u201d\u4e00\u58f0\uff0c\u718a\u5e84\u4e3b\u90a3\u84b2\u6247\u822c\u7684\u5927\u624b\u521a\u521a\u62ac\u8d77\uff0c\u4e24\u6839\u624b\u6307\uff0c\u5df2\u7ecf\u7ed9\u5251\u950b\u524a\u65ad\u3002 \u718a\u5e84\u4e3b\u75db\u5f97\u201c\u54c7\u54c7\u201d\u5927\u53eb\uff0c\u5ffd\u89c9\u8eab\u5b50\u4e00\u8f7b\uff0c\u597d\u50cf\u76ae\u7403\u822c\u7ed9\u4eba\u629b\u4e86\u8d77\u6765\uff0c\u8dcc\u51fa\u4e00\u4e08\u5f00\u5916\u3002\u539f\u6765 \u662f\u77f3\u5e2e\u4e3b\u89c1\u52bf\u4e0d\u5999\uff0c\u5728\u8fd9\u95f4\u4e0d\u5bb9\u53d1\u4e4b\u9645\uff0c\u4ee5\u6781\u5de7\u5999\u7684\u624b\u6cd5\u5c06\u4ed6\u629b\u51fa\u53bb\u7684\u3002 \u3000\u3000\u718a\u5e84\u4e3b\u8dcc\u4e86\u4e2a\u56db\u811a\u671d\u5929\uff0c\u722c\u8d77\u6765\u5927\u6012\u8bf4\u9053\uff1a\u201c\u77f3\u8001\u4e09\uff0c\u600e\u4e48\u4f60\u4e5f\u6765\u6b3a\u8d1f\u6211\u4e86\uff1f\u201d\u8d3a\u4e5d\u516c \u51b7\u51b7\u8bf4\u9053\u4e86\uff1a\u201c\u4e0d\u662f\u77f3\u8001\u4e09\uff0c\u4f60\u7684\u5403\u996d\u5bb6\u4f19\u65e9\u5df2\u6ca1\u5566\u3002\u201d\u718a\u5e84\u4e3b\u6014\u4e86\u4e00\u6014\uff0c\u65b9\u59cb\u604d\u7136\u5927\u609f\u662f \u77f3\u5e2e\u4e3b\u6551\u4e86\u4ed6\u7684\u6027\u547d\u3002 \u3000\u3000\u8fd9\u4e2a\u59d3\u77f3\u7684\u5e2e\u4e3b\u4f7f\u7684\u4e00\u5bf9\u5224\u5b98\u7b14\uff0c\u672c\u9886\u6bd4\u718a\u5e84\u4e3b\u9ad8\u5f3a\u5f97\u591a\uff0c\u6b64\u65f6\u5df2\u7ecf\u548c\u6768\u5a49\u5c55\u5f00\u6076\u6597\u3002 \u3000\u3000\u6768\u5a49\u5229\u4e8e\u901f\u6218\u901f\u51b3\uff0c\u5f53\u4e0b\u5251\u8d70\u8f7b\u7075\uff0c\u4ee5\u95ea\u7535\u822c\u7684\u5251\u6cd5\uff0c\u4e00\u53e3\u6c14\u75be\u653b\u4e86\u6570\u5341\u62db\uff0c\u6740\u5f97\u77f3\u5e2e \u4e3b\u8fde\u8fde\u540e\u9000\u3002\u53ef\u662f\u4ed6\u7684\u53cc\u7b14\u5c01\u95ed\u5f97\u751a\u4e3a\u4e25\u5bc6\uff0c\u867d\u7136\u5931\u4e86\u5148\u624b\uff0c\u6768\u5a49\u60f3\u8981\u5728\u6025\u5207\u4e4b\u95f4\u5c06\u4ed6\u523a\u4f24 \u5374\u4e5f\u4e0d\u80fd\u3002 \u3000\u3000\u718a\u5e84\u4e3b\u9053\uff0c\u201c\u54a6\uff0c\u8fd9\u5c0f\u8d3c\u5012\u662f\u6709\u70b9\u96be\u6597\u3002\u77f3\u8001\u4e09\uff0c\u4e0d\u5fc5\u7740\u8c0e\uff0c\u6211\u6765\u5e2e\u4f60\uff0c\u597d\u5c0f\u5b50\u3002\u4ed6\u524a \u4e86\u6211\u7684\u4e24\u6839\u6307\u5934\uff0c\u6211\u975e\u65a9\u4ed6\u4e24\u6761\u624b\u81c2\u4e0d\u53ef\uff01\u201d\u62d4\u51fa\u4e86\u539a\u80cc\u780d\u5c71\u5200\uff0c\u5374\u662f\u8fdf\u8fdf\u4e0d\u6562\u4e0a\u524d\uff0c\u539f\u6765 \u4ed6\u7ed9\u6768\u5a49\u524a\u4e86\u4e24\u6839\u6307\u5934\uff0c\u5fc3\u4e2d\u5df2\u662f\u9887\u6709\u602f\u610f\uff0c\u53ea\u56e0\u4e0d\u613f\u5f53\u4f17\u5931\u4e86\u9762\u5b50\uff0c\u6545\u6b64\u5927\u547c\u5c0f\u53eb\uff0c\u865a\u5f20 \u58f0\u52bf\u3002 \u3000\u3000\u8d3a\u4e5d\u516c\u9053\uff1a\u201c\u8001\u718a\uff0c\u4f60\u7ed9\u6211\u5b89\u9759\u70b9\u5427\u3002\u4f60\u5230\u5916\u9762\u770b\u770b\uff0c\u770b\u8fd9\u5c0f\u8d3c\u8fd8\u6709\u6ca1\u6709\u515a\u7fbd\u3002\u5e99\u91cc\u7684 \u4e8b\uff0c\u4f60\u5c31\u4e0d\u5fc5\u7ba1\u4e86\u3002\u201d\u718a\u5e84\u4e3b\u6b63\u662f\u5df4\u4e0d\u5f97\u4ed6\u5982\u6b64\u8bf4\uff0c\u8fde\u5fd9\u5e94\u9053\uff1a\u201c\u5bf9\uff01\u6211\u7ed9\u4f60\u8001\u4eba\u5bb6\u628a\u98ce\uff0c \u6709\u4f60\u8001\u4eba\u5bb6\u5728\u6b64\uff0c\u8fd8\u6101\u8fd9\u5c0f\u5b50\u8dd1\u5f97\u4e86\u5417\uff1f\u201d \u3000\u3000\u8d3a\u4e5d\u516c\u6296\u51fa\u8f6f\u97ad\uff0c\u53eb\u9053\uff1a\u201c\u56db\u9762\u5305\u56f4\uff0c\u8981\u64d2\u6d3b\u53e3\uff01\u201d\u957f\u97ad\u821e\u5f97\u547c\u547c\u51e4\u54cd\uff0c\u5728\u4f17\u4eba\u672a\u66fe\u5408 \u56f4\u4e4b\u524d\uff0c\u5148\u62a2\u4e0a\u53bb\uff0c\u4e3a\u77f3\u5e2e\u4e3b\u89e3\u56f0\u3002 \u3000\u3000\u6b64\u65f6\u77f3\u5e2e\u4e3b\u5728\u6768\u5a49\u6025\u653b\u4e4b\u4e0b\uff0c\u5df2\u662f\u65e0\u6cd5\u95ea\u907f\u3002\u53ea\u542c\u5f97\u201c\u94db\u201d\u7684\u4e00\u58f0\uff0c\u5251\u7b14\u76f8\u4ea4\uff0c\u706b\u82b1\u56db \u6e85\uff0c\u77f3\u5e2e\u4e3b\u7684\u5224\u5b98\u7b14\u635f\u4e86\u4e00\u4e2a\u7f3a\u53e3\u3002 \u3000\u3000\u6768\u5a49\u8fd9\u4e00\u62db\u201c\u957f\u6cb3\u843d\u65e5\u201d\u5c1a\u672a\u4f7f\u8db3\uff0c\u5251\u52bf\u4e00\u5708\uff0c\u8fce\u4e0a\u4e86\u8d3a\u4e5d\u516c\u7684\u8f6f\u97ad\uff0c\u201c\u5494\u5693\u201d\u4e00\u58f0\uff0c \u628a\u8f6f\u97ad\u4e5f\u524a\u53bb\u4e86\u4e00\u622a\u3002\u539f\u6765\u6768\u5a49\u7528\u7684\u8fd9\u4e00\u628a\u5251\uff0c\u4e43\u662f\u660e\u6167\u516c\u4e3b\u6240\u8d60\u7684\u5b9d\u5251\uff0c\u6709\u65ad\u91d1\u622a\u94c1\u4e4b \u80fd\uff0c\u5439\u6bdb\u7acb\u65ad\u4e4b\u5229\u3002 \u3000\u3000\u8d3a\u4e5d\u516c\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u54ea\u6761\u7ebf\u4e0a\u7684\u670b\u53cb\uff0c\u5feb\u8bf4\u51fa\u6765\uff0c\u514d\u5f97\u81ea\u8bef\uff01\u201d\u4ed6\u89c1\u4e00\u4e2a\u8c8c \u4e0d\u60ca\u4eba\u7684\u201c\u5c0f\u53ae\u201d\u6709\u8fd9\u6837\u4e00\u628a\u4ef7\u503c\u8fde\u57ce\u7684\u5b9d\u5251\uff0c\u5251\u6cd5\u53c8\u662f\u5982\u6b64\u7cbe\u5947\uff0c\u4e0d\u7531\u5f97\u60ca\u7591\u4e0d\u5b9a\uff0c\u6349\u6a21 \u4e0d\u900f\u6768\u5a49\u7684\u6765\u5386\u3002 \u3000\u3000\u6768\u5a49\u5530\u5530\u5530\u8fde\u73af\u4e09\u5251\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u6211\u662f\u708e\u9ec4\u5b50\u5b59\uff0c\u4e2d\u534e\u513f\u5973\uff0c\u4f60\u7b49\u79c1\u901a\u9791\u5b50\u4e4b\u8f88\uff0c\u4f11 \u8981\u5984\u60f3\u4e0e\u6211\u7ed3\u4ea4\u3002\u201d \u3000\u3000\u8d3a\u4e5d\u516c\u5927\u6012\u9053\uff1a\u201c\u6211\u4e0d\u8fc7\u770b\u4f60\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u672c\u9886\u8fd8\u8fc7\u5f97\u53bb\uff0c\u60f3\u9976\u4f60\u4e00\u547d\uff0c\u4f60\u7adf\u7136\u4e0d\u53d7\u62ac \u4e3e\uff01\u54fc\uff0c\u4f60\u4ee5\u4e3a\u6211\u6015\u4f60\u4e0d\u6210\uff01\u201d\u8d3a\u4e5d\u516c\u97ad\u6cd5\u4e5f\u771f\u4e86\u5f97\uff0c\u5403\u4e86\u4e00\u6b21\u4e8f\u4e4b\u540e\uff0c\u90a3\u6761\u8f6f\u97ad\u4f7f\u5f97\u7adf\u662f \u77eb\u82e5\u6e38\u9f99\uff01\u6768\u5a49\u518d\u60f3\u524a\u65ad\u4ed6\u7684\u8f6f\u97ad\u5df2\u662f\u4e0d\u80fd\u591f\u4e86\u3002 \u3000\u3000\u8d3a\u4e5d\u516c\u7684\u526f\u624b\u718a\u58ee\u548c\u996e\u9a6c\u5ddd\u7684\u5f20\u5be8\u4e3b\u7b49\u4eba\u56db\u65b9\u9f50\u4e0a\uff0c\u5408\u56f4\u4e4b\u52bf\u5df2\u6210\u3002\u6768\u5a49\u4ee5\u4e00\u654c\u4e94\uff0c\u767b \u65f6\u9669\u8c61\u73af\u751f\uff01 \u3000\u3000\u5e78\u4e8f\u8d3a\u4e5d\u516c\u5bf9\u5979\u7684\u5b9d\u5251\u4e5f\u8fd8\u6709\u70b9\u513f\u987e\u5fcc\uff0c\u5426\u5219\u4ed6\u53ea\u987b\u548c\u77f3\u5e2e\u4e3b\u8054\u624b\uff0c\u5df2\u662f\u53ef\u4ee5\u7a33\u64cd\u80dc \u7b97\u3002\u6768\u5a49\u4e00\u89c1\u5f62\u52bf\u4e0d\u5999\uff0c\u7acb\u5373\u6539\u53d8\u6253\u6cd5\uff0c\u5c55\u5f00\u4e86\u4e00\u5957\u865a\u5b9e\u83ab\u6d4b\u7684\u5251\u672f\uff0c\u6307\u4e1c\u6253\u897f\uff0c\u6307\u5357\u6253 \u5317\uff0c\u4e0d\u6c42\u6025\u653b\uff0c\u53ea\u662f\u89c5\u9699\u5bfb\u6687\uff0c\u8bbe\u6cd5\u4f7f\u654c\u4eba\u5404\u81ea\u4e3a\u6218\uff0c\u524a\u5f31\u5bf9\u65b9\u56f4\u653b\u7684\u5a01\u80c1\u3002 \u3000\u3000\u4e94\u4eba\u4e4b\u4e2d\uff0c\u4ee5\u8d3a\u4e5d\u516c\u7684\u672c\u9886\u6700\u5f3a\uff0c\u4ed6\u5bf9\u5b9d\u5251\u4e00\u6709\u987e\u5fcc\uff0c\u4fbf\u4e0d\u80fd\u5c3d\u6570\u53d1\u6325\u4ed6\u7684\u97ad\u6cd5\u4e4b\u957f\u3002 \u5176\u4ed6\u56db\u4eba\u5728\u6768\u5a49\u653b\u5b88\u83ab\u6d4b\u7684\u5947\u8be1\u5251\u672f\u9a9a\u6270\u4e4b\u4e0b\uff0c\u6025\u5207\u4e4b\u95f4\uff0c\u4e5f\u662f\u4e0d\u80fd\u6536\u4e92\u76f8\u547c\u5e94\u4e4b\u6548\uff0c\u6768\u5a49 \u8fd9\u624d\u80fd\u591f\u52c9\u5f3a\u652f\u6301\u3002 \u3000\u3000\u4f46\u7ec8\u7a76\u5be1\u4e0d\u654c\u4f17\uff0c\u65f6\u95f4\u4e00\u957f\uff0c\u6768\u5a49\u6c14\u529b\u4e0d\u8db3\uff0c\u7ec8\u4e8e\u9677\u5165\u4e86\u96be\u4ee5\u4e3a\u7ee7\u7684\u5883\u5730\u3002\u6fc0\u6218\u4e2d\u53ea\u542c \u5f97\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u6768\u5a49\u4e00\u5251\u523a\u7834\u4e86\u5f20\u5be8\u4e3b\u7684\u8863\u895f\uff0c\u5f20\u5be8\u4e3b\u53cd\u800c\u54c8\u54c8\u5927\u7b11\uff1b\u8bf4\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c \u4f60\u4f7f\u4e0d\u52a8\u5251\u5566\uff0c\u8fd9\u628a\u5b9d\u5251\u7ed9\u4e86\u6211\u5427\uff01\u201d\u539f\u6765\u6768\u5a49\u8fd9\u4e00\u5251\u523a\u7834\u4ed6\u7684\u8863\u895f\uff0c\u5374\u5df2\u65e0\u529b\u7a7f\u8fc7\uff0c\u4e1d\u6beb \u6ca1\u6709\u4f24\u53ca\u4ed6\u7684\u76ae\u8089\u3002\u5f20\u5be8\u4e3b\u5f53\u7136\u77e5\u9053\u5979\u5df2\u7ecf\u662f\u5230\u4e86\u5f3a\u5f29\u4e4b\u672b\u4e86\u3002 \u3000\u3000\u5f20\u5be8\u4e3b\u5728\u5927\u7b11\u58f0\u4e2d\u6b3a\u8eab\u76f4\u8fdb\uff0c\u4f7f\u51fa\u201c\u7a7a\u624b\u5165\u767d\u5203\u201d\u7684\u529f\u592b\uff0c\u786c\u62a2\u6768\u5a49\u7684\u5ba4\u5251\uff0c\u8d3a\u4e5d\u516c\u7684 \u8f6f\u97ad\u4ea6\u7740\u5730\u5377\u6765\uff0c\u4e00\u4e2a\u201c\u67af\u85e4\u7ed5\u4e00\u6811\u201d\uff0c\u7f20\u7ed5\u6768\u5a49\u7684\u53cc\u8db3\uff0c\u773c\u770b\u6768\u5a49\u7684\u5b9d\u5251\u5c31\u8981\u7ed9\u4ed6\u4eec\u62a2 \u53bb\uff0c\u4eba\u4e5f\u96be\u514d\u8981\u906d\u6d3b\u64d2\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u5371\u673a\u77ac\u606f\u4e4b\u95f4\uff0c\u5ffd\u542c\u5f97\u5728\u5916\u628a\u98ce\u7684\u718a\u5e84\u4e3b\u5927\u559d\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\uff1f\u4e0d\u8bb8\u8fdb\u53bb\u3002\u201d\u968f\u5373 \u542c\u5f97\u6251\u901a\u5012\u5730\u4e4b\u58f0\uff0c\u718a\u5e84\u4e3b\u53eb\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u4f60\u6562\u6253\u6211\uff01\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u4e00\u4e2a\u4eba\u5df2\u7ecf\u8fdb\u4e86\u5e99 \u95e8\uff01 \u3000\u3000\u718a\u5e84\u4e3b\u5728\u540c\u4f19\u4e2d\u867d\u7136\u672c\u9886\u8f83\u5dee\uff0c\u5728\u6c5f\u6e56\u4e0a\u4e5f\u603b\u7b97\u662f\u53eb\u5f97\u54cd\u5b57\u53f7\u7684\u4eba\u7269\uff0c\u5982\u4eca\u53ea\u4e0d\u8fc7\u4e00\u4e2a \u7167\u9762\u5c31\u7ed9\u6765\u4eba\u51fb\u5012\uff0c\u8d3a\u4e5d\u516c\u7b49\u4eba\u90fd\u662f\u4e0d\u7981\u5927\u5403\u4e00\u60ca\uff01\u53ea\u5f97\u5f97\u90a3\u4eba\u5927\u53eb\u9053\uff1a\u201c\u4e14\u6162\u52a8\u624b\uff01\u201d\u58f0 \u5230\u4eba\u5230\uff0c\u5df2\u662f\u8fdb\u4e86\u5e99\u95e8\uff01 \u3000\u3000\u8d3a\u4e5d\u516c\u8fd9\u4e00\u4f19\u4eba\u4ed7\u7740\u4eba\u591a\u52bf\u4f17\uff0c\u5c82\u80af\u7518\u4f11\uff1f\u8d3a\u4e5d\u516c\u6bd4\u8f83\u7a33\u91cd\uff0c\u672a\u51fa\u624b\uff0c\u5148\u52a8\u53e3\uff0c\u95ee\u9053\uff1a \u201c\u9601\u4e0b\u662f\u54ea\u6761\u7ebf\u4e0a\u7684\u670b\u53cb\uff0c\u8bf7\u62a5\u4e2a\u4e07\u513f\uff01\u201d\u996e\u9a6c\u5ddd\u7684\u5f20\u5be8\u4e3b\u5374\u5df2\u4e00\u626c\u624b\u5c31\u53d1\u51fa\u4e86\u6697\u5668\uff0c\u51b7\u7b11 \u8bf4\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u6211\u5012\u8981\u770b\u4f60\u6709\u4ec0\u4e48\u672c\u9886\uff0c\u80c6\u6562\u6765\u7ba1\u95f2\u4e8b\uff01\u201d \u3000\u3000\u5f20\u5be8\u4e3b\u53d1\u7684\u6697\u5668\u662f\u4e00\u679a\u94c1\u76d4\u8638\uff0c\u5728\u6697\u5668\u4e2d\u662f\u5c5e\u4e8e\u6bd4\u8f83\u6c89\u91cd\u7684\u4e00\u7c7b\uff0c\u4e0d\u6599\u4ed6\u8fd9\u679a\u94c1\u76d4\u8638\u5c04 \u5230\u4e2d\u9014\uff0c\u53ea\u89c1\u91d1\u5149\u4e00\u95ea\u201c\u94db\u201d\u7684\u4e00\u58f0\uff0c\u5df2\u662f\u7ed9\u90a3\u4eba\u53d1\u51fa\u7684\u4e00\u652f\u91d1\u9556\u6253\u843d\u3002\u90a3\u4eba\u6de1\u6de1\u8bf4\u9053\uff1a \u201c\u5728\u4e0b\u6ca1\u6709\u4ec0\u4e48\u672c\u9886\uff0c\u53ea\u51ed\u8fd9\u652f\u91d1\u9556\uff0c\u60f3\u8bf7\u5404\u4f4d\u8d4f\u4e2a\u9762\u5b50\u3002\u201d \u3000\u3000\u7528\u9ec4\u91d1\u94f8\u9053\u7684\u6697\u5668\u4e43\u662f\u6781\u4e3a\u7f55\u89c1\u4e4b\u7269\uff0c\u8d3a\u4e5d\u516c\u89c1\u95fb\u5e7f\u535a\uff0c\u767b\u65f6\u60f3\u8d77\u4e86\u4e00\u4e2a\u4eba\u6765\uff0c\u5fc3\u5934\u4e00 \u9707\uff0c\u8fde\u5fd9\u53eb\u9053\uff1a\u201c\u4f4f\u624b\uff0c\u4f4f\u624b\uff01\u4e0d\u53ef\u9c81\u83bd\uff01\u201d \u3000\u3000\u90a3\u652f\u91d1\u9556\u6253\u843d\u4e86\u94c1\u76d4\u8638\uff0c\u4f59\u52bf\u672a\u8870\uff0c\u201c\u5494\u5693\u201d\u4e00\u58f0\uff0c\u63d2\u5165\u6881\u67f1\uff0c\u5140\u81ea\u98a4\u52a8\u4e0d\u4f11\u3002\u8fd9\u4e00\u77ac \u95f4\uff0c\u4f17\u4eba\u90fd\u55c5\u5230\u4e00\u822c\u6de1\u6de1\u7684\u8165\u5473\uff0c\u8fd9\u624d\u77e5\u9053\u8fd9\u4eba\u7684\u91d1\u9556\uff0c\u4e0d\u4ec5\u662f\u7f55\u89c1\u7684\u91d1\u9556\uff0c\u800c\u4e14\u8fd8\u662f\u6dec\u4e86 \u5267\u6bd2\u7684\u6bd2\u9556\uff01 \u3000\u3000\u8d3a\u4e5d\u516c\u7b49\u4eba\u56fa\u7136\u662f\u5927\u5927\u5403\u60ca\uff0c\u4f46\u6700\u5403\u60ca\u7684\u8fd8\u662f\u6768\u5a49\u3002\u5f53\u8fd9\u4eba\u672a\u66fe\u51fa\u73b0\u4e4b\u65f6\uff0c\u5979\u8fd8\u4ee5\u4e3a\u6765 \u7684\u4e43\u662f\u6551\u661f\uff0c\u5982\u4eca\u65b9\u59cb\u77e5\u9053\uff0c\u6765\u7684\u975e\u4f46\u4e0d\u662f\u6551\u661f\uff0c\u53cd\u800c\u662f\u7978\u661f\uff01\u8fd9\u4eba\u662f\u4e00\u4e2a\u6bd4\u8d3a\u4e5d\u516c\u4ed6\u4eec\u66f4 \u9634\u9669\u7684\u654c\u4eba\uff0c\u4e5f\u6b63\u662f\u6768\u5a49\u6700\u75db\u6068\u7684\u4e00\u4e2a\u4ec7\u4eba\uff01\u8fd9\u5239\u90a3\u95f4\uff0c\u6768\u5a49\u4e0d\u7531\u5f97\u53c8\u60ca\u53c8\u6012\uff0c\u9669\u4e9b\u6655\u4e86\u8fc7 \u53bb\u3002 \u3000\u3000\u5e78\u800c\u8d3a\u4e5d\u516c\u7b49\u4eba\u5df2\u7ecf\u4f4f\u624b\uff0c\u6768\u5a49\u6643\u4e86\u4e24\u6643\uff0c\u84e6\u5730\u4e00\u54ac\u7259\u6839\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u53cd\u6b63\u4eca\u65e5\u662f\u5fc5 \u6b7b\u65e0\u7591\u7684\u4e86\uff0c\u62fc\u4e86\u6027\u547d\uff0c\u4e5f\u5f97\u53eb\u8fd9\u8d3c\u5b50\u4e24\u8d25\u4ff1\u4f24\uff01\u201d \u3000\u3000\u90a3\u4eba\u4e00\u51fa\u624b\u628a\u8d3a\u4e5d\u516c\u7b49\u4eba\u9707\u4f4f\uff0c\u5f97\u610f\u6d0b\u6d0b\u5730\u8bf4\u9053\uff1a\u201c\u5404\u4f4d\u90fd\u662f\u89c1\u591a\u8bc6\u5e7f\u4e4b\u4eba\uff0c\u6bd2\u9f99\u9556\u7684 \u540d\u5b57\uff0c\u5404\u4f4d\u5927\u6982\u4f1a\u77e5\u9053\u5427\uff1f\u201d \u3000\u3000\u8d3a\u4e5d\u516c\u9053\uff1a\u201c\u524d\u4efb\u7eff\u6797\u76df\u4e3b\u5c60\u767e\u57ce\u5c60\u5927\u4fa0\u662f\u9601\u4e0b\u7684\u4ec0\u4e48\u4eba\uff1f\u201d\u90a3\u4eba\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8001\u524d\u8f88 \u679c\u7136\u597d\u773c\u529b\uff0c\u770b\u51fa\u4e86\u5728\u4e0b\u7684\u6765\u5386\u4e86\u3002\u5b9e\u4e0d\u76f8\u7792\uff0c\u5c60\u76df\u4e3b\u6b63\u662f\u5bb6\u7236\u3002\u201d \u3000\u3000\u539f\u6765\u8fd9\u4e2a\u4eba\u4e0d\u662f\u522b\u4e2a\uff0c\u6b63\u662f\u5c60\u9f99\u3002 \u3000\u3000\u8d3a\u4e5d\u516c\u677e\u4e86\u53e3\u6c14\uff0c\u7acb\u5373\u4e5f\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u539f\u6765\u662f\u5c60\u516c\u5b50\uff0c\u8fd9\u771f\u662f\u5927\u6c34\u51b2\u5012\u4e86\u9f99\u738b\u5e99\uff0c\u81ea \u5bb6\u4eba\u4e0d\u8ba4\u5f97\u81ea\u5bb6\u4eba\u4e86\uff01\u201d \u3000\u3000\u539f\u6765\u8d3a\u4e5d\u516c\u4ee5\u524d\u867d\u7136\u6ca1\u6709\u89c1\u8fc7\u5c60\u9f99\uff0c\u4f46\u4ece\u9633\u575a\u767d\u7684\u53e3\u4e2d\uff0c\u5374\u5df2\u77e5\u9053\u5c60\u9f99\u4e5f\u662f\u6295\u9760\u4e86\u8499\u53e4 \u9791\u5b50\u7684\u4eba\u7269\uff1b\u4e0d\u8fc7\u4ed6\u662f\u548c\u6df3\u4e8e\u5468\u4e00\u4f19\uff0c\u5e76\u975e\u5c5e\u4e8e\u9633\u5929\u96f7\u3001\u9633\u575a\u767d\u53d4\u4f84\u8fd9\u4e00\u8def\u800c\u5df2\uff0c\u867d\u7136\u4e0d\u662f \u5c5e\u4e8e\u4e00\u8def\uff0c\u5374\u603b\u662f\u540c\u6076\u76f8\u6d4e\u7684\u81ea\u5df1\u4eba\u4e86\u3002 \u3000\u3000\u718a\u5e84\u4e3b\u6b64\u65f6\u6b63\u5728\u54fc\u54fc\u53fd\u53fd\u5730\u8d70\u8fdb\u6765\uff0c\u542c\u4e86\u8d3a\u4e5d\u516c\u7684\u8a00\u8bed\uff0c\u4e0d\u89c9\u6014\u4e86\u4e00\u6014\uff0c\u8bf4\u9053\uff1a\u201c\u4e5d \u516c\uff0c\u8fd9\u5c0f\u5b50\u4e0d\u662f\u5206\u660e\u6765\u6405\u5c40\u7684\u5417\uff1f\u600e\u7684\u5ffd\u7136\u53c8\u53d8\u6210\u4e86\u54b1\u4eec\u7684\u81ea\u5df1\u4eba\u4e86\uff1f\u201d \u3000\u3000\u8d3a\u4e5d\u516c\u559d\u9053\uff1a\u201c\u718a\u5144\u5f1f\u4f11\u5f97\u65e0\u793c\uff01\u8fd9\u4f4d\u5c60\u516c\u5b50\u548c\u9633\u516c\u5b50\u4e43\u662f\u4e00\u6761\u7ebf\u4e0a\u7684\u670b\u53cb\uff0c\u7ea2\u82b1\u7eff \u53f6\uff0c\u540c\u51fa\u4e00\u5bb6\uff0c\u600e\u4e48\u4e0d\u662f\u81ea\u5df1\u4eba\u5462\uff1f\u201d \u3000\u3000\u5c60\u9f99\u65e9\u5df2\u731c\u5230\u4e86\u4ed6\u4eec\u7684\u6765\u5386\uff0c\u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u5404\u4f4d\u5927\u6982\u662f\u9633\u56fd\u5e08\u7684\u624b\u4e0b\u4e86\uff1f\u201d \u3000\u3000\u8d3a\u4e5d\u516c\u9053\uff1a\u201c\u8fd8\u6ca1\u6709\u8fd9\u4e2a\u798f\u5206\uff0c\u4e0d\u8fc7\u9633\u56fd\u5e08\u7684\u4f84\u5b50\u5df2\u7ecf\u5e94\u627f\u4e86\u6211\u4eec\uff0c\u53ea\u5f85\u6211\u4eec\u7acb\u4e86\u529f \u52b3\uff0c\u4ed6\u5c31\u53ef\u4ee5\u4ee3\u4ed6\u7239\u7239\u4f5c\u4e3b\uff0c\u6536\u5f55\u6211\u4eec\u3002\u5b9e\u4e0d\u76f8\u7792\uff0c\u4eca\u665a\u6211\u4eec\u6b63\u662f\u5949\u4e86\u9633\u516c\u5b50\u4e4b\u547d\uff0c\u5728\u6b64\u805a \u4f1a\uff0c\u7ed9\u4ed6\u529e\u4e8b\u7684\u3002\u8fd9\u5c0f\u5b50\u5077\u5077\u8eb2\u5728\u8fd9\u91cc\uff0c\u7a83\u542c\u6211\u4eec\u7684\u79d8\u5bc6\uff0c\u4ed6\u4e5f\u5df2\u7ecf\u5766\u7136\u76f4\u8ba4\u662f\u548c\u6211\u4eec\u4f5c\u5bf9 \u7684\u4e86\uff0c\u4e0d\u77e5\u5c60\u516c\u5b50\u4f55\u4ee5\u8981\u5e87\u62a4\u4ed6\u3002\u201d \u3000\u3000\u5c60\u9f99\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5404\u4f4d\u770b\u8d70\u773c\u4e86\uff0c\u8fd9\u4eba\u5e76\u975e\u5c0f\u5b50\uff0c\u5b9e\u4e0d\u76f8\u7792\uff0c\u5979\u662f\u6211\u7684\u5a18\u5b50\uff01\u201d \u3000\u3000\u6b64\u8a00\u4e00\u51fa\uff0c\u7fa4\u76d7\u90fd\u662f\u5927\u4e3a\u8be7\u5f02\u3002\u8d3a\u4e5d\u516c\u6bd5\u7adf\u662f\u4e2a\u5bcc\u6709\u7ecf\u9a8c\u7684\u884c\u5bb6\uff0c\u4ed4\u7ec6\u4e00\u770b\uff0c\u679c\u7136\u53d1\u73b0 \u4e86\u6768\u5a49\u7684\u201c\u5f02\u76f8\u2019\uff1a\u6700\u663e\u8457\u7684\u662f\u6ca1\u6709\u5589\u7ed3\u3001\u53ef\u4ee5\u8bc1\u660e\u5979\u786e\u662f\u5973\u5b50\u65e0\u7591\u3002 \u3000\u3000\u4f46\u8d3a\u4e5d\u516c\u8fd8\u662f\u89c9\u5f97\u5947\u602a\uff0c\u5fc3\u91cc\u60f3\u9000\uff1a\u201c\u4ee5\u5c60\u516c\u5b50\u7684\u76f8\u8c8c\u6b66\u529f\uff0c\u7adf\u4f1a\u5a36\u4e2a\u4e11\u5973\u4e3a\u59bb\uff0c\u5012\u662f \u602a\u4e8b\u3002\u8fd9\u4e14\u4e0d\u8bf4\uff0c\u8fd9\u5c0f\u4e2b\u5934\u53e3\u53e3\u58f0\u58f0\u75db\u9a82\u6211\u4eec\u52fe\u7ed3\u9791\u5b50\uff0c\u5206\u660e\u4e0d\u662f\u4e00\u6761\u7ebf\u4e0a\u7684\u4eba\uff0c\u5374\u53c8\u4f55\u4ee5 \u4f1a\u5ac1\u7ed9\u4ed6\u5462\uff1f\u201d \u3000\u3000\u5c60\u9f99\u597d\u4f3c\u77e5\u9053\u4ed6\u4eec\u7684\u5fc3\u601d\uff0c\u7b11\u9053\uff1a\u201c\u6211\u8fd9\u5a18\u5b50\u7ed9\u5978\u4eba\u5f15\u8bf1\uff0c\u8bef\u5165\u6b67\u9014\uff0c\u6539\u5bb9\u6613\u8c8c\uff0c\u80cc\u6211 \u79c1\u9003\uff0c\u4e5f\u602a\u4e0d\u5f97\u5404\u4f4d\u4e0e\u5979\u4e3a\u96be\uff0c\u4f46\u6211\u5ff5\u5728\u592b\u59bb\u7684\u60c5\u4e49\uff0c\u8fd8\u662f\u60f3\u8bf7\u5404\u4f4d\u7a0d\u7a0d\u7ed9\u6211\u4e00\u70b9\u9762\u5b50\uff0c\u8ba9 \u6211\u81ea\u5df1\u4e86\u65ad\u6b64\u4e8b\uff01\u201d \u3000\u3000\u8d3a\u4e5d\u516c\u6599\u60f3\u5176\u4e2d\u5b9a\u6709\u66f2\u6298\uff0c\u4e0d\u4fbf\u591a\u95ee\uff0c\u5f53\u4e0b\u6253\u4e86\u4e2a\u54c8\u54c8\uff0c\u8bf4\u9053\uff1a\u201c\u592b\u59bb\u95f4\u95f9\u70b9\u522b\u626d\uff0c\u4ea6 \u5c5e\u5bfb\u5e38\u3002\u5c60\u516c\u5b50\u7684\u5bb6\u4e8b\uff0c\u6211\u4eec\u81ea\u662f\u4e0d\u6562\u8fc7\u95ee\u3002\u201d\u63a5\u7740\u5bf9\u6768\u5a49\u4f5c\u4e86\u4e2a\u63d6\uff0c\u8bf4\u9053\uff1a\u201c\u5c60\u5ac2\u5b50\uff0c\u8bf7 \u6055\u6211\u4eec\u6709\u773c\u65e0\u73e0\uff0c\u521a\u624d\u591a\u6709\u5f97\u7f6a\u4e86\u3002\u201d\u7387\u4f17\u9000\u4e86\uff0c\u7ad9\u5728\u56db\u8fb9\uff0c\u770b\u8fd9\u573a\u70ed\u95f9\u3002 \u3000\u3000\u6768\u5a49\u4e00\u76f4\u6c89\u4f4f\u4e86\u6c14\uff0c\u9ed8\u8fd0\u7384\u529f\uff0c\u79ef\u805a\u6c14\u529b\uff0c\u6062\u590d\u75b2\u52b3\uff0c\u5bf9\u4ed6\u4eec\u8f7b\u4f7b\u7684\u4e3e\u6b62\uff0c\u4fae\u8fb1\u7684\u8a00 \u8bed\uff0c\u5b9b\u5982\u89c6\u800c\u4e0d\u89c1\uff0c\u542c\u800c\u4e0d\u95fb\u3002\u5c60\u9f99\u4ee5\u4e3a\u6768\u5a49\u81ea\u77e5\u5df2\u662f\u9003\u4e0d\u51fa\u4ed6\u7684\u638c\u5fc3\uff0c\u51c6\u5907\u5411\u4ed6\u5c48\u670d\u7684 \u4e86\uff0c\u7981\u4e0d\u4f4f\u5fc3\u82b1\u6012\u653e\uff0c\u4e0a\u524d\u8bf4\u9053\uff1a\u201c\u6768\u59d1\u5a18\uff0c\u4f46\u6c42\u4f60\u56de\u5fc3\u8f6c\u610f\uff0c\u6211\u662f\u65e2\u5f80\u4e0d\u7a76\uff0c\u50cf\u4ee5\u524d\u4e00\u6837 \u7684\u6b22\u559c\u4f60\u3002\u201d \u3000\u3000\u8bdd\u72b9\u672a\u4e86\uff0c\u6768\u5a49\u5530\u7684\u4e00\u5251\u5c31\u523a\u8fc7\u6765\uff0c\u559d\u9053\uff1a\u201c\u653e\u4f60\u7684\u5c41\uff01\u4eca\u5929\u4e0d\u662f\u4f60\u6b7b\u4fbf\u662f\u6211\u4ea1\uff01\u201d \u3000\u3000\u5c60\u9f99\u51b7\u4e0d\u53ca\u9632\uff0c\u767e\u5fd9\u4e2d\u4e00\u4e2a\u8098\u9524\u649e\u53bb\u3002\u6768\u5a49\u5251\u6cd5\u7cbe\u5947\uff0c\u5403\u4e8f\u5728\u6c14\u529b\u4e0d\u8db3\uff0c\u867d\u7136\u5598\u606f\u5df2 \u5b9a\uff0c\u75b2\u52b3\u4e5f\u5c1a\u672a\u6062\u590d\u3002\u7ed9\u4ed6\u4e00\u649e\uff0c\u957f\u5251\u6b6a\u8fc7\u4e00\u8fb9\u3002\u201c\u55e4\u201d\u7684\u4e00\u58f0\u54cd\uff0c\u5251\u5c16\u53ea\u662f\u523a\u7834\u4e86\u4ed6\u7684\u8863 \u8896\u3002 \u3000\u3000\u5c60\u9f99\u770b\u51fa\u6768\u5a49\u7528\u7684\u4e43\u662f\u5b9d\u5251\uff0c\u6697\u81ea\u53eb\u58f0\u201c\u597d\u9669\uff01\u201d\u4ed6\u8fd9\u4e00\u649e\uff0c\u8098\u7aef\u82e5\u975e\u6b63\u597d\u649e\u7740\u65e0\u950b\u7684 \u5251\u810a\u7684\u8bdd\uff0c\u53ea\u6015\u4ed6\u8fd9\u4e00\u6761\u624b\u81c2\u5df2\u7ed9\u5b9d\u5251\u5207\u4e0b\u6765\u4e86\u3002 \u3000\u3000\u8d3a\u4e5d\u516c\u5403\u4e86\u4e00\u60ca\uff0c\u53eb\u9053\uff1a\u201c\u5c60\u516c\u5b50\uff0c\u4f60\u8fd9\u4f4d\u5a18\u5b50\u6b63\u5728\u6c14\u5934\u4e0a\uff0c\u6050\u6015\u4e0d\u4f1a\u542c\u4f60\u4e4b\u529d\uff0c\u4f60\u4e0d \u4f24\u5979\uff1b\u4e5f\u5f97\u63d0\u9632\u5979\u4f24\u4e86\u4f60\uff01\u201d \u3000\u3000\u5c60\u9f99\u4e3a\u4e86\u4fdd\u6301\u4f53\u9762\uff0c\u88c5\u4f5c\u82e5\u65e0\u5176\u4e8b\u7684\u6837\u5b50\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6211\u7684\u597d\u5a18\u5b50\uff0c\u592b\u59bb\u53cd\u76ee\uff0c\u4e5f\u7528\u4e0d \u7740\u8fd9\u6837\u62a1\u5200\u5f04\u5251\u5440\uff0c\u4f60\u770b\u670b\u53cb\u4eec\u90fd\u7b11\u8bdd\u4e86\u3002\u4e0d\u8fc7\uff0c\u5404\u4f4d\u4e5f\u4e0d\u5fc5\u62c5\u5fc3\uff0c\u6211\u8fd9\u5a18\u5b50\u7684\u813e\u6c14\u6211\u662f\u77e5 \u9053\u7684\uff0c\u5979\u5728\u6c14\u5934\u4e0a\u81ea\u662f\u96be\u514d\u88c5\u5f97\u6765\u52bf\u6c79\u6c79\uff0c\u5176\u5b9e\u5979\u7684\u5fc3\u91cc\u662f\u4e0d\u60f3\u4f24\u6211\u7684\u3002\u201d \u3000\u3000\u8bdd\u72b9\u672a\u4e86\uff0c\u6768\u5a49\u53c8\u5df2\u662f\u5530\u5530\u5530\u7684\u8fde\u73af\u4e09\u5251\uff0c\u5251\u5251\u6307\u5411\u5c60\u9f99\u7684\u8981\u5bb3\uff01\u718a\u5e84\u4e3b\u662f\u4e2a\u6d51\u4eba\uff0c\u5931 \u58f0\u53eb\u9053\uff1a\u201c\u5c60\u516c\u5b50\uff0c\u6211\u770b\u4e0d\u5927\u5bf9\u5427\uff01\u4f60\u8fd9\u5a18\u5b50\u4e0d\u4ec5\u662f\u8981\u4f24\u4f60\uff0c\u6050\u6015\u8fd8\u8981\u53d6\u4f60\u7684\u6027\u547d\u5462\uff01\u201d \u3000\u3000\u5c60\u9f99\u5df2\u6709\u51c6\u5907\uff0c\u4f7f\u4e00\u53e3\u6734\u5200\u628a\u6768\u5a49\u7684\u653b\u52bf\u89e3\u5f00\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u4f4d\u670b\u53cb\u8bf7\u653e\u5fc3\uff0c\u6211\u4eec\u592b\u59bb\u6253 \u67b6\u5df2\u975e\u4e00\u6b21\u4e86\uff0c\u5979\u5c31\u662f\u771f\u7684\u60f3\u8981\u4f24\u6211\uff0c\u4e5f\u662f\u51b3\u8ba1\u505a\u4e0d\u5230\u7684\u3002\u4f55\u51b5\u5e76\u4e0d\u662f\u771f\u7684\u5462\u3002\u4e0d\u8fc7\uff0c\u6211\u7684 \u597d\u5a18\u5b50\uff0c\u4f60\u8fd9\u6b21\u4e5f\u5b9e\u5728\u662f\u6709\u70b9\u8fc7\u5206\u4e86\uff0c\u4e3a\u4e86\u4e0d\u7ed9\u670b\u53cb\u7b11\u8bdd\uff0c\u4f60\u82e5\u4e0d\u80af\u653e\u4e0b\u5175\u5203\uff0c\u6211\u4e5f\u53ea\u597d\u5f97 \u7f6a\u4f60\u4e86\uff01\u201d \u3000\u3000\u5c60\u9f99\u672c\u6765\u662f\u60f3\u628a\u6768\u5a49\u751f\u64d2\u7684\uff0c\u4f46\u5728\u6768\u5a49\u62fc\u7740\u4e24\u8d25\u4ff1\u4f24\u7684\u72e0\u8fa3\u5251\u6cd5\u4e4b\u4e0b\uff0c\u53ea\u5f97\u629b\u5f00\u4e86\u601c\u9999 \u60dc\u7389\u4e4b\u5fc3\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u770b\u6765\u5979\u662f\u4e0d\u80af\u987a\u4ece\u6211\u7684\u4e86\u3002\u4f46\u6211\u5f97\u4e0d\u5230\u5979\uff0c\u4e5f\u7edd\u4e0d\u80fd\u8ba9\u5979\u6295\u5165\u674e\u601d \u5357\u7684\u6000\u62b1\uff01\u201d\u60f3\u5230\u96be\u4ee5\u4e24\u5168\uff0c\u4e0d\u7531\u5f97\u5992\u706b\u653b\u5fc3\uff0c\u6740\u673a\u9661\u8d77\u3002\u957f\u5200\u821e\u5f97\u970d\u970d\u751f\u98ce\uff0c\u7adf\u7136\u4e5f\u662f\u4e00 \u6d3e\u8fdb\u624b\u7684\u62db\u6570\u3002 \u3000\u3000\u5c60\u9f99\u7684\u672c\u9886\u672c\u6765\u5c31\u80dc\u8fc7\u6768\u5a49\u4e00\u7b79\uff0c\u6b64\u65f6\u53c8\u5360\u4e86\u6c14\u529b\u5145\u6c9b\u7684\u4fbf\u5b9c\u3002\u5341\u6570\u62db\u4e00\u8fc7\uff0c\u767b\u65f6\u5c31\u5360 \u4e86\u538b\u5012\u7684\u4f18\u52bf\u3002\u6768\u5a49\u7528\u7684\u867d\u662f\u5b9d\u5251\uff0c\u4f46\u6c14\u529b\u4e0d\u8db3\uff0c\u5374\u662f\u524a\u4e0d\u65ad\u4ed6\u7684\u539a\u80cc\u6734\u5200\u3002\u4e0d\u8fc7\u4e5f\u5e78\u4e8f\u5979 \u7528\u7684\u662f\u4e00\u67c4\u5b9d\u5251\uff0c\u5c60\u9f99\u591a\u5c11\u8981\u6709\u70b9\u987e\u5fcc\u3002 \u3000\u3000\u6768\u5a49\u62fc\u7740\u8c41\u4e86\u6027\u547d\uff0c\u5c3d\u7ba1\u5904\u4e8e\u52a3\u52bf\uff0c\u5251\u6cd5\u4ecd\u662f\u72e0\u8fa3\u5f02\u5e38\uff0c\u5c60\u9f99\u4e0d\u6562\u518d\u8bf4\u98ce\u51c9\u8bdd\uff0c\u6740\u5f97\u6027 \u8d77\uff0c\u7ea2\u4e86\u53cc\u773c\uff0c\u89e3\u62db\u8fd8\u62db\uff0c\u6bcf\u4e00\u5200\u4e5f\u662f\u780d\u5411\u6768\u5a49\u7684\u8981\u5bb3\u3002 \u3000\u3000\u8d3a\u4e5d\u516c\u8fd9\u73ed\u4eba\u770b\u5f97\u60ca\u5fc3\u52a8\u9b44\uff0c\u718a\u5e84\u4e3b\u5600\u5495\u9053\uff1a\u201c\u8fd9\u54ea\u91cc\u50cf\u662f\u592b\u59bb\u6253\u67b6\uff0c\u5206\u660e\u662f\u4f60\u6b7b\u6211\u6d3b \u7684\u53ae\u62fc\u5440\uff01\u201d\u8d3a\u4e5d\u516c\u6697\u5730\u4f7f\u4e86\u4e2a\u670d\u8272\uff0c\u53eb\u4ed6\u4e0d\u8981\u591a\u8bdd\u3002 \u3000\u3000\u4e00\u6765\u56e0\u4e3a\u5c60\u9f99\u6709\u8a00\u5728\u5148\uff0c\u58f0\u660e\u4e0d\u8981\u5916\u4eba\u76f8\u52a9\uff1b\u4e8c\u6765\u8d3a\u4e5d\u516c\u7b49\u4eba\u4ea6\u90fd\u5df2\u770b\u5f97\u6e05\u695a\uff0c\u6768\u5a49\u5df2 \u662f\u5f3a\u5f29\u4e4b\u672b\uff0c\u5fc5\u8d25\u65e0\u7591\u3002\u6545\u6b64\u4ed6\u4eec\u4e5f\u5c31\u4e50\u5f97\u8896\u624b\u65c1\u89c2\u4e86\u3002 \u3000\u3000\u5c60\u9f99\u8d8a\u903c\u8d8a\u7d27\uff0c\u6fc0\u6597\u4e2d\u5ffd\u5730\u4e00\u62db\u5200\u4e2d\u5939\u638c\uff0c\u559d\u9053\uff1a\u201c\u64a4\u5251\uff01\u201d\u5200\u5982\u96c1\u7fc5\uff0c\u659c\u5288\u4e0b\u6765\uff0c\u6768 \u5a49\u5251\u62db\u4e1a\u5df2\u4f7f\u8001\uff0c\u82e5\u4e0d\u64a4\u5251\u6025\u9000\uff0c\u8fd9\u6761\u624b\u81c2\u53ea\u6015\u5c31\u8981\u7ed9\u4ed6\u7684\u5feb\u5200\u5207\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u5e72\u94a7\u4e00\u53d1\u4e4b\u65f6\uff0c\u5ffd\u542c\u5f97\u4e00\u4e2a\u6e05\u8106\u7684\u58f0\u97f3\u4e5f\u5728\u559d\u9053\uff1a\u201c\u4f4f\u624b\u3002\u201d \u3000\u3000\u58f0\u5230\u4eba\u5230\uff0c\u53ea\u89c1\u4e00\u4e2a\u9ad8\u9f3b\u6df1\u76ee\u7684\u6b66\u58eb\u5927\u8e0f\u6b65\u62a2\u5165\u5e99\u95e8\u3002\u521a\u624d\u90a3\u4e2a\u4eba\u8bf4\u8bdd\u7684\u58f0\u97f3\u5206\u660e\u662f\u4e2a \u5973\u5b50\uff0c\u8fdb\u6765\u7684\u5374\u662f\u4e00\u4e2a\u8499\u53e4\u6b66\u58eb\uff0c\u8d3a\u4e5d\u516c\u7b49\u4eba\u90fd\u662f\u4e0d\u7981\u6115\u7136\uff01 \u3000\u3000\u718a\u5e84\u4e3b\u628a\u5b88\u95e8\u53e3\uff0c\u9996\u5f53\u5176\u51b2\uff0c\u770b\u89c1\u662f\u4e2a\u8499\u53e4\u6b66\u58eb\uff0c\u6b63\u81ea\u8fdf\u7591\u5e94\u4e0d\u5e94\u62e6\u963b\uff0c\u90a3\u6b66\u58eb\u5df2\u662f\u731b \u5730\u559d\u9053\uff1a\u201c\u8ba9\u5f00\uff01\u201d\u957f\u81c2\u4e00\u4f38\uff0c\u624b\u6cd5\u5feb\u5230\u6781\u70b9\uff0c\u718a\u5e84\u4e3b\u83ab\u540d\u5176\u5999\u7684\u5c31\u7ed9\u4ed6\u6454\u4e86\u4e00\u4e2a\u7b4b\u6597\uff0c\u8fde \u4ed6\u662f\u600e\u6837\u51fa\u624b\u4e5f\u672a\u77e5\u9053\u3002 \u3000\u3000\u5c60\u9f99\u90a3\u4e00\u5200\u6b63\u8981\u5288\u4e0b\uff0c\u9661\u7136\u542c\u5f97\u90a3\u4e00\u58f0\u6e05\u8106\u7684\u559d\u58f0\uff0c\u4e0d\u7531\u5f97\u5fc3\u5934\u4e00\u9707\uff0c\u731b\u5730\u60f3\u8d77\u4e00\u4e2a\u4eba \u6765\uff0c\u5374\u4e0d\u6562\u76f8\u4fe1\u8fd9\u4e2a\u4eba\u4f1a\u7a81\u7136\u5728\u6b64\u5730\u51fa\u73b0\uff01 \u3000\u3000\u5c31\u5728\u4ed6\u8fdf\u8fdf\u7591\u7591\uff0c\u8fd9\u4e00\u5200\u5c06\u5288\u672a\u5288\u4e4b\u9645\uff0c\u90a3\u4e2a\u8499\u53e4\u6b66\u58eb\u5df2\u7ecf\u628a\u718a\u5e84\u4e3b\u6454\u5012\uff0c\u5927\u6012\u559d\u9053\uff1a \u201c\u4f60\u6562\u4e0d\u542c\u516c\u4e3b\u7684\u8bdd\uff01\u201d\u547c\u7684\u4e00\u58f0\uff0c\u4e00\u6761\u957f\u7ef3\u629b\u51fa\uff0c\u5230\u4e86\u5c60\u9f99\u9762\u524d\uff0c\u500f\u5730\u6253\u6210\u4e00\u4e2a\u7ef3\u5708\uff0c\u4ee5 \u5c60\u9f99\u7684\u8eab\u624b\u7adf\u7136\u95ea\u8eb2\u4e0d\u5f00\uff0c\u53ea\u542c\u5f97\u201c\u94db\u201d\u7684\u4e00\u58f0\uff0c\u7ef3\u5708\u521a\u597d\u5957\u7740\u4ed6\u7684\u5200\u70b3\uff0c\u628a\u4ed6\u7684\u957f\u5200\u593a\u51fa \u4e86\u624b\uff0c\u629b\u5728\u5730\u4e0a\u3002 \u3000\u3000\u5728\u5c60\u9f99\u90a3\u4e00\u5200\u5288\u4e0b\u4e4b\u65f6\uff0c\u6768\u5a49\u4e0d\u7518\u64a4\u5251\uff0c\u62fc\u7740\u7ed9\u4ed6\u524a\u65ad\u624b\u81c2\uff0c\u5de6\u638c\u4e00\u638c\u6253\u53bb\u3002\u4e0d\u6599\u53d8\u51fa \u610f\u5916\uff0c\u5c60\u9f99\u7684\u957f\u5200\u7ed9\u8499\u53e4\u6b66\u58eb\u593a\u53bb\uff0c\u7ed3\u679c\u53d8\u6210\u4e86\u5c60\u9f99\u5927\u5927\u5403\u4e8f\uff0c\u7ed9\u6768\u5a49\u91cd\u91cd\u5730\u6253\u4e86\u4e00\u8bb0\u8033 \u5149\uff0c\u867d\u8bf4\u6768\u5a49\u5df2\u662f\u6c14\u529b\u4e0d\u52a0\uff0c\u8fd9\u4e00\u638c\u4ecd\u7136\u6253\u5f97\u4ed6\u9762\u4e0a\u5f00\u82b1\uff01 \u3000\u3000\u8d3a\u4e5d\u516c\u3001\u77f3\u5e2e\u4e3b\u4e8c\u4eba\u5927\u60ca\u4e4b\u4e0b\uff0c\u8fde\u5fd9\u62a2\u4e0a\uff0c\u4fdd\u62a4\u5c60\u9f99\u3002\u6768\u5a49\u77e5\u9053\u8fd9\u4e8c\u4eba\u672c\u9886\u4e86\u5f97\uff0c\u5f53\u4e0b \u4e5f\u5c31\u4e0d\u518d\u8fdb\u62db\uff0c\u63d2\u5251\u5f52\u9798\uff0c\u559c\u51fa\u671b\u5916\u7684\u4e0a\u524d\u4e0e\u90a3\u8499\u53e4\u6b66\u58eb\u76f8\u89c1\u8bf4\u9053\uff1a\u201c\u963f\u76d6\uff0c\u4f60\u6765\u4e86\uff01\u201d \u3000\u3000\u539f\u6765\u8fd9\u4e2a\u8499\u53e4\u6b66\u58eb\u4e0d\u662f\u522b\u4eba\uff0c\u6b63\u662f\u674e\u601d\u5357\u548c\u6768\u5a49\u7684\u597d\u670b\u53cb\u963f\u76d6\u3002\u6768\u5a49\u66fe\u7ecf\u6551\u8fc7\u4ed6\u7684\u672a\u5a5a \u59bb\u5b50\u7684\u6027\u547d\uff0c\u540e\u6765\u6768\u5a49\u5f97\u4ee5\u6df7\u5165\u8499\u53e4\u519b\u8425\u884c\u523a\u4ec7\u4eba\uff0c\u4e5f\u90fd\u662f\u5168\u51ed\u963f\u76d6\u7684\u5e2e\u5fd9\u3002 \u3000\u3000\u963f\u76d6\u7b11\u9053\uff1a\u201c\u4e0d\u4ec5\u662f\u6211\u6765\u4e86\uff0c\u516c\u4e3b\u548c\u5361\u6d1b\u4e1d\u4e5f\u6765\u4e86\u5462\uff01\u201d \u3000\u3000\u8bdd\u72b9\u672a\u4e86\uff0c\u53ea\u89c1\u4e24\u4e2a\u5c11\u5973\uff0c\u5df2\u662f\u8054\u8882\u8d70\u4e86\u8fdb\u6765\u3002\u524d\u9762\u90a3\u4e2a\u5c11\u5973\u62ab\u7740\u72d0\u88d8\uff0c\u6c14\u5ea6\u9ad8\u534e\uff0c\u679c \u7136\u6b63\u662f\u660e\u6167\u516c\u4e3b\uff0c\u8ddf\u5728\u5979\u540e\u9762\u7684\u90a3\u4e2a\u5c11\u5973\u8863\u88f3\u6de1\u96c5\uff0c\u6e05\u4e3d\u7edd\u4fd7\uff0c\u6b63\u662f\u963f\u76d6\u7684\u672a\u5a5a\u59bb\u5361\u6d1b\u4e1d\u3002 \u3000\u3000\u718a\u5e84\u4e3b\u722c\u8d77\u8eab\u6765\uff0c\u6478\u4e00\u6478\u989d\u89d2\u6454\u80bf\u7684\u5927\u7624\uff0c\u6562\u6012\u800c\u4e0d\u6562\u8a00\uff0c\u8eb2\u5728\u4e00\u89d2\uff0c\u72ec\u81ea\u5600\u5495\uff1a\u201c\u521a \u624d\u6765\u4e86\u4e00\u4e2a\u81ea\u8ba4\u662f\u4eba\u5bb6\u4e08\u592b\u7684\u4ec0\u4e48\u5c60\u516c\u5b50\uff0c\u628a\u6211\u6454\u4e86\u4e00\u8dcc\uff1b\u5982\u4eca\u4e0d\u77e5\u662f\u54ea\u91cc\u94bb\u51fa\u6765\u7684\u516c\u4e3b\uff0c \u53c8\u53eb\u6211\u53d8\u4f5c\u4e86\u6eda\u5730\u846b\u82a6\u3002\u7f62\u4e86\uff0c\u7f62\u4e86\uff0c\u603b\u4e4b\u662f\u6211\u5012\u9709\u7f62\u4e86\uff01\u201d \u3000\u3000\u8d3a\u4e5d\u516c\u7b49\u4eba\u7ed9\u963f\u5b5f\u77eb\u5065\u7684\u8eab\u624b\u5413\u4f4f\uff0c\u6b64\u65f6\u53c8\u542c\u8bf4\u6765\u7684\u662f\u4ec0\u4e48\u516c\u4e3b\uff0c\u4e00\u65f6\u95f4\u4e0d\u77e5\u662f\u771f\u662f \u5047\uff0c\u4e0d\u7981\u90fd\u662f\u9762\u9762\u76f8\u89d1\uff0c\u8c01\u4e5f\u4e0d\u6562\u4f5c\u58f0\u3002 \u3000\u3000\u5c60\u9f99\u66fe\u7ecf\u5728\u548c\u6797\u505a\u8fc7\u795e\u7ffc\u8425\u7edf\u9886\u6728\u534e\u9ece\u7684\u5ba2\u4eba\uff0c\u4e5f\u66fe\u53c2\u52a0\u8fc7\u51e0\u6b21\u8499\u53e4\u8d35\u65cf\u4e3e\u884c\u7684\u72e9\u730e\uff0c \u662f\u4ee5\u8ba4\u5f97\u660e\u6167\u516c\u4e3b\u3002\u4ed6\u77e5\u9053\u660e\u6167\u516c\u4e3b\u662f\u6210\u5409\u601d\u6c57\u6700\u5ba0\u7231\u7684\u5c0f\u5973\u513f\uff0c\u65b0\u4efb\u76d1\u56fd\u7684\u62d6\u96f7\uff0c\u548c\u5979\u53c8 \u662f\u6700\u8981\u597d\u7684\u5144\u59b9\u3002 \u3000\u3000\u4ed6\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u660e\u6167\u516c\u4e3b\u4f1a\u7a81\u7136\u6765\u5230\u6b64\u95f4\uff0c\u4e00\u89c1\u4e4b\u4e0b\uff0c\u5927\u60ca\u5931\u8272\uff0c\u8fde\u5fd9\u5411\u660e\u6167\u516c\u4e3b\u8dea \u4e0b\uff0c\u884c\u4e86\u53c2\u62dc\u7684\u5927\u793c\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u77e5\u516c\u4e3b\u8fdc\u6765\uff0c\u6709\u5931\u8fce\u8fce\uff0c\u8bf7\u516c\u4e3b\u6055\u7f6a\u3002\u201d\u8d3a\u4e5d\u516c\u7b49\u4eba\u8ddf\u5728 \u4ed6\u7684\u540e\u9762\uff0c\u4e5f\u90fd\u77ee\u4e86\u534a\u622a\u3002 \u3000\u3000\u660e\u6167\u516c\u4e3b\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u6b3a\u4fae\u8fd9\u4f4d\u6768\u59d1\u5a18\uff1f\u201d \u3000\u3000\u5c60\u9f99\u597d\u751f\u7eb3\u7f55\uff0c\u201c\u600e\u7684\u660e\u6167\u516c\u4e3b\u4e00\u89c1\u5c31\u8ba4\u5f97\u6768\u5a49\u5462\uff1f\u201d\u4ed6\u5f53\u7136\u4e0d\u4f1a\u77e5\u9053\uff0c\u6768\u5a49\u66fe\u7ecf\u505a\u8fc7 \u660e\u6167\u516c\u4e3b\u540d\u4e49\u4e0a\u7684\u4f8d\u5973\uff0c\u6697\u5730\u91cc\u4e14\u8fd8\u662f\u59d0\u59b9\u76f8\u79f0\u7684\u3002\u5728\u6768\u5a49\u521d\u89c1\u660e\u6167\u516c\u4e3b\u4e4b\u65f6\uff0c\u4e5f\u6b63\u662f\u76ee\u524d \u8fd9\u526f\u6253\u626e\u3002 \u3000\u3000\u5c60\u9f99\u5728\u660e\u6167\u516c\u4e3b\u9762\u524d\uff0c\u81ea\u662f\u4e0d\u6562\u4e71\u8bf4\uff0c\u5f53\u4e0b\u8baa\u8baa\u8bf4\u9053\uff1a\u201c\u516c\u4e3b\u65e2\u7136\u77e5\u9053\u8fd9\u4f4d\u6768\u59d1\u5a18\uff0c\u60f3 \u5fc5\u4e5f\u77e5\u9053\u5979\u662f\u674e\u601d\u5357\u7684\u672a\u5a5a\u59bb\u4e86\u3002\u674e\u601d\u5357\u53ef\u6b63\u662f\u8d35\u56fd\u6240\u8981\u7f09\u6355\u7684\u94a6\u72af\u5440\uff01\u201d \u3000\u3000\u718a\u5e84\u4e3b\u662f\u4e2a\u6d51\u4eba\uff0c\u5fcd\u4e0d\u7740\u53c8\u63d2\u53e3\u9053\uff1a\u201c\u54a6\uff0c\u4f60\u4e0d\u662f\u8bf4\u4f60\u662f\u5979\u7684\u4e08\u592b\u5417\uff1f\u600e\u7684\u5979\u53c8\u6709\u4e00\u4e2a \u59d3\u674e\u7684\u672a\u5a5a\u592b\u5462\uff1f\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u51b7\u7b11\u9053\uff1a\u201c\u539f\u6765\u4f60\u662f\u765e\u86e4\u87c6\u60f3\u5403\u5929\u9e45\u8089\uff0c\u8d2a\u56fe\u7f8e\u8272\uff0c\u5374\u85c9\u53e3\u662f\u7ed9\u6211\u4eec\u6548\u52b3\u3002\u201d \u3000\u3000\u5c60\u9f99\u5413\u5f97\u8fde\u8fde\u78d5\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u516c\u4e3b\u660e\u9274\uff0c\u5c0f\u4eba\u6548\u5fe0\u5927\u6c57\uff0c\u5b9e\u65e0\u4e8c\u5fc3\u3002\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u5927\u6c57\u8981\u628a\u674e\u601d\u5357\u9063\u56de\u53bb\uff0c\u4e3a\u7684\u6b63\u662f\u8981\u91cd\u7528\u4ed6\uff0c\u4f60\u77e5\u4e0d \u77e5\u9053\uff1f\u201d \u3000\u3000\u5c60\u9f99\u53c8\u78d5\u4e86\u4e2a\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u53ea\u6015\u674e\u601d\u5357\u4e0d\u4f1a\u4e3a\u8d35\u56fd\u6240\u7528\u4e86\uff0c\u4ed6\u5982\u4eca\u5df2\u7ecf\u505a\u4e86\u6c49\u4eba\u4e49\u519b\u7684 \u76df\u4e3b\u5566\u3002\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u65a5\u9053\uff1a\u201c\u6211\u4eec\u5982\u4f55\u5904\u7f6e\u94a6\u72af\uff0c\u7528\u4e0d\u7740\u4f60\u6765\u591a\u5634\uff0c\u4f60\u4eec\u90fd\u7ed9\u6211\u6eda\u5f00\uff01\u201d \u3000\u3000\u5c60\u9f99\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u542c\u8bf4\u660e\u6167\u516c\u4e3b\u5bf9\u674e\u601d\u5357\u9887\u6709\u610f\u601d\uff0c\u4e00\u5b9a\u662f\u5979\u8981\u4eb2\u81ea\u6298\u78e8\u6768\u5a49\u4e86\u3002\u4e5f \u597d\uff0c\u6768\u5a49\u867d\u7136\u4e0d\u80fd\u5f52\u6211\u6240\u6709\uff0c\u4f46\u674e\u601d\u5357\u603b\u662f\u5f97\u4e0d\u5230\u5979\u4e86\u3002\u201d\u5f53\u4e0b\u5e94\u9053\uff1a\u201c\u662f\uff0c\u662f\u3002\u201d\u548c\u8d3a\u4e5d \u516c\u7b49\u4eba\u8fde\u5fd9\u8d70\u5f00\u3002 \u3000\u3000\u6768\u5a49\u62b9\u4e86\u4e00\u989d\u51b7\u6c57\uff0c\u4e0a\u524d\u8c22\u8fc7\u660e\u6167\u516c\u4e3b\u6551\u547d\u4e4b\u6069\uff0c\u8bf4\u9053\uff1a\u201c\u4eca\u665a\u771f\u662f\u597d\u9669\uff0c\u516c\u4e3b\uff0c\u4f60\u82e5 \u662f\u6765\u8fdf\u4e00\u6b65\uff0c\u53ea\u6015\u5df2\u662f\u89c1\u4e0d\u7740\u6211\u4e86\u3002\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u7b11\u9053\uff1a\u201c\u6211\u4e5f\u662f\u4e8b\u6025\u884c\u9669\uff0c\u5e78\u4e8f\u8fd9\u8d3c\u5b50\u4e0d\u77e5\u6211\u7684\u5e95\u7ec6\uff0c\u7ed9\u6211\u4e00\u5413\u5c31\u5413\u8dd1\u4e86\u3002\u201d \u3000\u3000\u6768\u5a49\u83ab\u540d\u5176\u5999\uff0c\u6014\u4e86\u4e00\u6014\uff0c\u95ee\u9053\uff1a\u201c\u516c\u4e3b\uff0c\u4f60\u4f55\u4ee5\u4e0d\u5728\u548c\u6797\uff0c\u5374\u5230\u4e86\u6b64\u5730\uff1f\u90a3\u8d3c\u5b50\u4e0d\u662f \u6b63\u56e0\u4e3a\u77e5\u9053\u4f60\u662f\u516c\u4e3b\uff0c\u624d\u4e0d\u6562\u4e0d\u542c\u4f60\u7684\u8bdd\u5417\uff1f\u4f55\u4ee5\u4f60\u8bf4\u4ed6\u4e0d\u77e5\u4f60\u7684\u5e95\u7ec6\uff1f\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u73b0\u5728\u5df2\u7ecf\u4e0d\u662f\u516c\u4e3b\u4e86\u3002\u201d \u3000\u3000\u6768\u5a49\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u516c\u4e3b\u4e3a\u4f55\u8fd9\u6837\u8bf4\u3002\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u9053\uff1a\u201c\u6211\u662f\u9003\u51fa\u548c\u6797\u7684\u3002\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u62d6\u96f7\u4e0d\u662f\u7b54\u5e94\u8fc7\u4fdd\u62a4\u4f60\u5417\uff1f\u96be\u9053\u9547\u56fd\u738b\u5b50\u8fd8\u6562\u5c06\u4f60\u96be\u4e3a\uff1f\u201d\u6768\u5a49\u6599\u60f3\u5b9a\u7136\u662f \u4e3a\u4e86\u9547\u56fd\u738b\u5b50\u5411\u5979\u903c\u5a5a\uff0c\u5426\u5219\u660e\u6167\u516c\u4e3b\u4f55\u987b\u9003\u8d70\uff1f \u3000\u3000\u660e\u6167\u516c\u4e3b\u8bf4\u9053\uff1a\u201c\u6768\u59d0\u59d0\uff0c\u4f60\u4e0d\u61c2\uff0c\u8eab\u4e3a\u516c\u4e3b\uff0c\u8fd9\u5b9e\u5728\u662f\u6700\u4e0d\u5e78\u7684\u4e8b\u60c5\u3002\u8868\u9762\u770b\u6765\uff0c\u4eab \u5c3d\u8363\u534e\u5bcc\u8d35\uff0c\u5176\u5b9e\u5374\u662f\u6837\u6837\u4e8b\u60c5\u90fd\u5f97\u542c\u4eba\u6446\u5e03\u3002\u5144\u5f1f\u4e4b\u4e2d\uff0c\u62d6\u96f7\u867d\u7136\u548c\u6211\u611f\u60c5\u6700\u597d\uff0c\u4f46\u4e34\u5230 \u5229\u5bb3\u5173\u5934\uff0c\u4ed6\u4e5f\u662f\u548c\u6211\u7239\u7239\u4e00\u6837\uff0c\u5b81\u613f\u727a\u7272\u6211\u7684\u5e78\u798f\uff0c\u5fc5\u987b\u4fdd\u6301\u4f4f\u6743\u52bf\u3002 \u3000\u3000\u201c\u6211\u4eec\u8499\u53e4\u7684\u89c4\u4f8b\uff0c\u5927\u6c57\u7ee7\u627f\uff0c\u662f\u7531\u5e93\u91cc\u5c14\u6cf0\u5927\u4f1a\u63a8\u5b9a\u7684\u3002\u5728\u8fd9\u4e2a\u5927\u4f1a\u4e2d\uff0c\u7edf\u5175\u7684\u5c06 \u9886\uff0c\u5f80\u5f80\u80fd\u591f\u5de6\u53f3\u5c40\u52bf\u3002 \u3000\u3000\u201c\u4e89\u593a\u5927\u6c57\u4e4b\u4f4d\u7684\uff0c\u4e3b\u8981\u662f\u4e8c\u54e5\u5bdf\u5408\u53f0\u548c\u4e09\u54e5\u7a9d\u9614\u53f0\uff0c\u62d6\u96f7\u62e5\u62a4\u4e09\u54e5\uff0c\u4e09\u54e5\u7b54\u5e94\u4e8b\u6210\u4e4b \u540e\uff0c\u8ba9\u4ed6\u638c\u63e1\u5175\u6743\u3002\u4f46\u7edf\u5175\u7684\u5c06\u9886\uff0c\u5927\u591a\u6570\u5374\u662f\u62e5\u62a4\u4e8c\u54e5\u3002 \u3000\u3000\u201c\u9547\u56fd\u738b\u5b50\u662f\u56db\u8def\u5143\u5e05\u4e4b\u4e00\uff0c\u4e09\u54e5\u4e3a\u4e86\u62c9\u62e2\u4ed6\uff0c\u613f\u610f\u7b54\u5e94\u4ed6\u4efb\u4f55\u6761\u4ef6\u3002\u8fd9\u53ae\u63d0\u51fa\u7684\u7b2c\u4e00 \u4e2a\u6761\u4ef6\u5c31\u662f\u8981\u6211\u4e0e\u4ed6\u6210\u4eb2\uff0c\u7b2c\u4e8c\u4e2a\u6761\u4ef6\u624d\u662f\u8981\u505a\u526f\u5175\u9a6c\u5927\u5143\u5e05\u3002\u62d6\u96f7\u5fd7\u5728\u638c\u63e1\u5175\u6743\uff0c\u9547\u56fd\u738b \u5b50\u7518\u613f\u505a\u4ed6\u7684\u526f\u624b\uff0c\u8fd9\u6869\u4ea4\u6613\u4ed6\u81ea\u662f\u6b23\u7136\u540c\u610f\u4e86\u3002\u53ef\u601c\u6211\u7adf\u7ed9\u8499\u5728\u9f13\u91cc\uff0c\u62d6\u96f7\u53e3\u53e3\u58f0\u58f0\u8bf4\u4fdd \u62a4\u6211\uff0c\u6697\u5730\u91cc\u5df2\u5728\u5e03\u7f6e\u8ba9\u9547\u56fd\u738b\u5b50\u62a2\u4eb2\u3002 \u3000\u3000\u201c\u5e78\u4e8f\u963f\u76d6\u542c\u5f97\u98ce\u58f0\uff0c\u5077\u5077\u6765\u544a\u8bc9\u6211\uff0c\u4e5f\u662f\u5168\u9760\u4ed6\u7684\u7b56\u5212\uff0c\u6211\u624d\u80fd\u9003\u51fa\u548c\u6797\u3002\u201d \u3000\u3000\u963f\u76d6\u8bf4\u9053\uff1a\u201c\u6211\u8fd9\u4e5f\u662f\u4e3a\u4e86\u6211\u548c\u5361\u6d1b\u4e1d\u3002\u6768\u59d1\u5a18\uff0c\u4f60\u8bb0\u5f97\u5417\uff1f\u4f60\u529d\u8fc7\u6211\u4e0d\u8981\u6253\u8fd9\u4e0d\u4e49\u4e4b \u6218\u7684\u3002\u5f53\u65f6\u6211\u4e3a\u4e86\u6b66\u58eb\u7684\u8363\u8a89\uff0c\u6211\u6ca1\u6709\u7b54\u5e94\u4f60\u3002 \u3000\u3000\u201c\u5230\u4e86\u6218\u573a\uff0c\u6211\u624d\u660e\u767d\u8fc7\u6765\uff0c\u6253\u8fd9\u573a\u4ed7\u7684\u786e\u662f\u5927\u5927\u4e0d\u5bf9\u3002\u6211\u4eb2\u773c\u770b\u89c1\uff0c\u6211\u4eec\u7684\u6b66\u58eb\u5978\u6deb \u63b3\u63a0\uff0c\u65e0\u6240\u4e0d\u4e3a\uff0c\u5c06\u519b\u4eec\u4e3a\u975e\u4f5c\u6b79\uff0c\u90a3\u5c31\u66f4\u4e0d\u5fc5\u8bf4\u4e86\u3002\u6211\u6700\u7231\u7684\u662f\u5361\u6cbc\u4e1d\uff0c\u6211\u5c31\u66fe\u7ecf\u8fd9\u6837\u60f3 \u8fc7\uff0c\u5047\u5982\u522b\u4eba\u8981\u62a2\u8d70\u6211\u7684\u5361\u6cbb\u4e1d\uff0c\u6211\u4f1a\u600e\u4e48\u6837\u5462\uff1f\u201d \u3000\u3000\u5361\u6cbc\u4e1d\u82e6\u7b11\u9053\uff1a\u201c\u90a3\u6b21\u82e5\u4e0d\u662f\u591a\u4e8f\u4e86\u674e\u516c\u5b50\u548c\u6768\u59d1\u5a18\uff0c\u6211\u65e9\u5df2\u7ecf\u88ab\u4eba\u62a2\u53bb\u4e86\u3002\u8fd8\u6709\u4e00\u6b21 \u66f4\u5371\u9669\uff0c\u6211\u5230\u519b\u4e2d\u63a2\u671b\u4f60\uff0c\u6ca1\u89c1\u7740\u4f60\uff0c\u5374\u649e\u4e0a\u4e86\u9547\u56fd\u8fd9\u53ae\uff0c\u628a\u6211\u7f1a\u67b6\u3002\u5e78\u4e8f\u5927\u6c57\u548c\u516c\u4e3b\u6070\u5de7 \u5728\u90a3\u5929\u6765\u5230\uff0c\u5426\u5219\u771f\u662f\u4e0d\u582a\u8bbe\u60f3\uff01\u6211\u8001\u65e9\u5c31\u529d\u4f60\u4e0d\u8981\u6253\u8fd9\u52b3\u4ec0\u5b50\u7684\u6218\u4e86\uff0c\u53ef\u60dc\u4f60\u5374\u4e0d\u80af\u542c\u6211 \u7684\u8bdd\u3002\u201d \u3000\u3000\u963f\u76d6\u9053\uff1a\u201c\u90fd\u662f\u6211\u4e0d\u597d\uff0c\u7d2f\u4f60\u53d7\u4e86\u8fd9\u8bb8\u591a\u59d4\u5c48\u3002\u4f46\u73b0\u5728\u660e\u767d\uff0c\u4e5f\u8fd8\u4e0d\u7b97\u592a\u8fdf\u3002\u4f60\u8bf4\u662f \u5417\u3002\u201d \u3000\u3000\u5361\u6d1b\u4e1d\u9053\uff1a\u201c\u662f\u554a\uff0c\u4f46\u613f\u54b1\u4eec\u80fd\u591f\u5e73\u5b89\u65e0\u4e8b\u5730\u505a\u4e00\u5bf9\u592b\u59bb\uff0c\u767d\u5934\u5230\u8001\uff0c\u4e0d\u8bba\u65e5\u5b50\u8fc7\u5f97\u600e \u4e48\u82e6\uff0c\u6211\u90fd\u5fc3\u6ee1\u610f\u8db3\u4e86\u3002\u201d\u5979\u662f\u4e2a\u5728\u8349\u539f\u4e0a\u957f\u5927\u7684\u7267\u7f8a\u59d1\u5a18\uff0c\u4ece\u6765\u6ca1\u6709\u53d7\u8fc7\u4ec0\u4e48\u793c\u6cd5\u7684\u718f \u9676\uff0c\u4e0d\u50cf\u6c49\u4eba\u7684\u5927\u5bb6\u95fa\u79c0\uff0c\u5fc3\u91cc\u660e\u660e\u559c\u6b22\u4e86\u4ec0\u4e48\u4eba\uff0c\u4e5f\u8981\u8bf7\u591a\u4f5c\u6001\uff0c\u5979\u662f\u60f3\u5230\u4ec0\u4e48\u5c31\u8bf4\u4ec0 \u4e48\uff0c\u8bf4\u81f3\u6b64\u5904\uff0c\u7b11\u9765\u5982\u82b1\u3002 \u3000\u3000\u963f\u76d6\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u9003\u5230\u654c\u56fd\u6765\uff0c\u672c\u6765\u662f\u4ef6\u5371\u9669\u7684\u4e8b\u60c5\u3002\u597d\u5728\u6211\u4eec\u8fd9\u8eab\u6253\u626e\uff0c\u522b\u4eba\u731c \u4e0d\u900f\u6211\u4eec\u7684\u6765\u5386\u3002\u6211\u7684\u76f8\u8c8c\u4e00\u770b\u5c31\u77e5\u4e0d\u662f\u6c49\u4eba\uff0c\u4f46\u548c\u5317\u65b9\u957f\u5927\u7684\u91d1\u56fd\u4eba\u5374\u65e0\u591a\u5927\u5206\u522b\u3002\u6211\u4f1a \u8bb2\u5973\u771f\u65b9\u8a00\uff0c\u78b0\u4e0a\u91d1\u56fd\u7684\u5b98\u5175\uff0c\u4ed6\u4eec\u8fd8\u4ee5\u4e3a\u6211\u4eec\u662f\u4ec0\u4e48\u8d35\u4eba\u5462\u3002\u9ed1\u9053\u4e0a\u7684\u8d3c\u4eba\u5012\u662f\u78b0\u8fc7\u51e0 \u6b21\uff0c\u4e0d\u8fc7\u90fd\u662f\u4e00\u4e9b\u672c\u4e8b\u5e73\u5eb8\u7684\u5c0f\u8d3c\uff0c\u4e09\u62f3\u4e24\u811a\u5c31\u6253\u53d1\u4e86\u3002 \u3000\u3000\u201c\u6211\u4eec\u867d\u7136\u53ef\u4ee5\u7792\u5f97\u8fc7\u5b98\u5175\uff0c\u4f46\u4ee5\u516c\u4e3b\u7684\u8eab\u4efd\uff0c\u7f6e\u8eab\u654c\u56fd\u4e4b\u4e2d\uff0c\u603b\u662f\u5c0f\u5fc3\u4e3a\u4e0a\u3002\u4e07\u4e00\u7ed9 \u4ed6\u4eec\u53d1\u89c9\u6211\u4eec\u7684\u6765\u5386\uff0c\u90a3\u5c31\u5927\u5927\u4e0d\u597d\u4e86\uff0c\u662f\u4ee5\u6211\u4eec\u4e00\u8def\u4e0a\u5df2\u662f\u4e60\u60ef\u4e8e\u591c\u884c\u65e5\u5bbf\u3002 \u3000\u3000\u201c\u524d\u51e0\u5929\u6211\u4eec\u5728\u8def\u4e0a\u542c\u8bf4\u8fd9\u91cc\u6709\u91c7\u82b1\u8d3c\u51fa\u73b0\uff0c\u6211\u5c31\u7279\u522b\u7559\u795e\u3002\u521a\u624d\u7ecf\u8fc7\u5c71\u4e0b\uff0c\u770b\u89c1\u5e99\u91cc \u6709\u706b\u5149\uff0c\u53c8\u542c\u5f97\u6709\u5973\u5b50\u53eb\u558a\u7684\u58f0\u97f3\uff0c\u8d77\u521d\u6211\u8fd8\u4ee5\u4e3a\u662f\u91c7\u82b1\u8d3c\u53c8\u5728\u8fd9\u91cc\u6b3a\u51cc\u5987\u5973\u5462\u3002\u518d\u4ed4\u7ec6\u4e00 \u542c\uff0c\u8fd9\u624d\u542c\u51fa\u662f\u4f60\u7684\u58f0\u97f3\u3002\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u4f60\u7684\u672c\u9886\u53ef\u662f\u5927\u8fdb\u4e86\u554a\uff0c\u521a\u624d\u591a\u4e8f\u4f60\u593a\u4e86\u90a3\u8d3c\u5b50\u7684\u5175\u5203\u3002\u201d \u3000\u3000\u963f\u76d6\u7b11\u9053\uff1a\u201c\u4ec0\u4e48\u672c\u9886\uff0c\u8fd9\u4e0d\u8fc7\u662f\u6211\u5e73\u65e5\u7ec3\u51fa\u6765\u7684\u8c0b\u751f\u6280\u80fd\u7f62\u4e86\uff0c\u6211\u662f\u9760\u6253\u730e\u4e3a\u751f\u7684\uff0c \u7528\u8747\u7d22\u6d3b\u6349\u731b\u517d\uff0c\u8fd9\u5c31\u662f\u6211\u7684\u770b\u5bb6\u672c\u9886\u4e86\u3002\u8bf4\u8001\u5b9e\u8bdd\uff0c\u82e5\u7136\u771f\u4e2a\u8f83\u91cf\uff0c\u6211\u7edd\u4e0d\u662f\u90a3\u8d3c\u5b50\u7684\u5bf9 \u624b\u3002\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u7b11\u9053\uff1a\u201c\u4f60\u4e5f\u4e0d\u5fc5\u8fc7\u8c26\uff0c\u4f60\u7684\u6454\u89d2\u529f\u592b\uff0c\u5728\u8499\u53e4\u6b66\u58eb\u4e2d\u4e5f\u662f\u5c11\u6709\u7684\u4e86\u3002\u201d \u3000\u3000\u5361\u6d1b\u4e1d\u5ffd\u5730\u201c\u5657\u55e4\u201d\u4e00\u7b11\uff0c\u5979\u9053\uff1a\u201c\u6768\u59d1\u5a18\uff0c\u4eca\u665a\u82e5\u4e0d\u662f\u516c\u4e3b\u548c\u963f\u76d6\u4e0e\u6211\u4e00\u8d77\uff0c\u6211\u6050\u6015 \u8fd8\u4e0d\u6562\u8ba4\u4f60\u5462\u3002\u4f60\u600e\u7684\u53d8\u6210\u8fd9\u4e2a\u6837\u5b50\uff1f\u201d\u539f\u6765\u6768\u5a49\u5728\u7ecf\u8fc7\u4e00\u573a\u6fc0\u6218\u4e4b\u540e\uff0c\u5927\u6c57\u6dcb\u6f13\uff0c\u8138\u4e0a\u643d \u7684\u8349\u836f\u88ab\u6c57\u6c34\u51b2\u6d17\uff0c\u4e00\u5757\u9752\u4e00\u5757\u7eff\uff0c\u5f62\u72b6\u751a\u4e3a\u6ed1\u7a3d\u3002 \u3000\u3000\u6768\u5a49\u7b11\u9053\uff1a\u201c\u8fd9\u6539\u5bb9\u6613\u8c8c\u7684\u8349\u836f\uff0c\u8fd8\u662f\u4f60\u7684\u963f\u76d6\u7ed9\u6211\u627e\u6765\u7684\u5462\u3002\u6211\u73b0\u5728\u7684\u6837\u5b50\u4e00\u5b9a\u662f\u5f88 \u96be\u770b\u4e86\uff0c\u662f\u5417\uff1f\u201d\u5361\u6d74\u4e1d\u9053\uff1a\u201c\u4f60\u6d17\u4e00\u628a\u8138\u5427\uff0c\u6211\u8fd9\u91cc\u6709\u6c34\u3002\u201d\u539f\u6765\u8499\u53e4\u4eba\u6765\u5f80\u6c99\u6f20\uff0c\u60ef\u5e38 \u662f\u5e26\u6709\u6c34\u56ca\u7684\uff0c\u963f\u76d6\u7b49\u4eba\u6b64\u6b21\u867d\u662f\u6765\u5230\u5e76\u4e0d\u7f3a\u6c34\u7684\u5730\u65b9\uff0c\u4ecd\u7136\u4e0d\u6539\u6545\u4e61\u7684\u4e60\u60ef\u3002 \u3000\u3000\u6768\u5a49\u6fc0\u6218\u4e4b\u540e\uff0c\u6b63\u81ea\u611f\u5230\u75b2\u5026\uff0c\u8bf4\u9053\uff1a\u201c\u4e5f\u597d\u3002\u201d\u6d17\u8fc7\u4e86\u8138\uff0c\u7cbe\u795e\u4e3a\u4e4b\u4e00\u723d\u3002 \u3000\u3000\u5361\u6d1b\u4e1d\u9053\uff1a\u201c\u6211\u4eec\u771f\u662f\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u4f1a\u5728\u8fd9\u91cc\u78b0\u89c1\u4f60\u3002\u4f60\u731c\u516c\u4e3b\u4e3a\u4f55\u4e0d\u5f80\u522b\u7684\u5730\u65b9\u907f \u96be\uff0c\u5374\u8dd1\u5230\u8fd9\u91cc\u6765\uff1f\u201d \u3000\u3000\u6768\u5a49\u5fc3\u4e2d\u65e9\u5df2\u660e\u767d\uff0c\u4f46\u5374\u4e0d\u4fbf\u8bf4\u7a7f\u3002\u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u662f\u5440\uff0c\u6211\u6b63\u60f3\u8bf7\u95ee\u516c\u4e3b\u51c6\u5907\u5f80\u54ea\u513f \u53bb\uff1f\u53ef\u6709\u6295\u5954\u4e4b\u6240\u4e48\uff1f\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u6211\u73b0\u5728\u662f\u6709\u5bb6\u96be\u5f52\uff0c\u6709\u56fd\u96be\u6295\u3002\u6f02\u6d41\u5f02\u5730\uff0c\u65e0\u4eb2\u65e0\u6545\uff0c\u54ea\u6709\u4ec0 \u4e48\u597d\u7684\u53bb\u5904\u53ef\u4ee5\u6295\u5954\uff1f\u55ef\uff0c\u674e\u3001\u674e\u516c\u5b50\u5462\uff1f\u201d\u660e\u6167\u516c\u4e3b\u672c\u6765\u60f3\u7b49\u6768\u5a49\u81ea\u5df1\u8bf4\u7684\uff0c\u6768\u5a49\u5374\u4e00\u76f4 \u6ca1\u6709\u63d0\u8d77\u674e\u601d\u5357\uff0c\u660e\u6167\u516c\u4e3b\u5fcd\u4e0d\u4f4f\u53ea\u597d\u95ee\u5979\u4e86\u3002 \u3000\u3000\u5361\u6d1b\u4e1d\u5fc3\u76f4\u8ba1\u5feb\uff0c\u8ddf\u7740\u4fbf\u5373\u8bf4\u9053\uff0c\u201c\u6768\u59d1\u5a18\uff0c\u4f60\u4e0d\u77e5\u9053\uff0c\u516c\u4e3b\u5728\u4e2d\u56fd\u65e0\u4eb2\u65e0\u6545\uff0c\u76f8\u8bc6\u7684 \u552f\u6709\u4f60\u4eec\uff0c\u5979\u53ef\u662f\u628a\u4f60\u4eec\u5f53\u4f5c\u4eb2\u4eba\u4e00\u822c\u5462\u3002\u8fd9\u6b21\u5979\u6b63\u662f\u8981\u6765\u627e\u5bfb\u4f60\u4eec\u7684\u3002\u542c\u8bf4\u674e\u516c\u5b50\u7684\u5bb6\u4e61 \u5728\u5c71\u4e1c\u6b66\u57ce\uff0c\u6211\u4eec\u5c31\u662f\u51c6\u5907\u5230\u6b66\u57ce\u53bb\u7684\u3002\u60f3\u4e0d\u5230\u5728\u8fd9\u91cc\u9047\u4e0a\u4f60\uff0c\u7701\u5f97\u6211\u4eec\u8d70\u8bb8\u591a\u8def\u4e86\u3002\u5bf9 \u5566\uff0c\u6768\u59d1\u5a18\uff0c\u6211\u4e5f\u6b63\u60f3\u95ee\u4f60\uff0c\u4e3a\u4f55\u4f60\u72ec\u81ea\u4e00\u4eba\u884c\u8d70\uff0c\u674e\u516c\u5b50\u5374\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u6768\u5a49\u542c\u5979\u4eec\u8bf4\u8d77\u4e86\u674e\u601d\u5357\uff0c\u4e0d\u89c9\u9eef\u7136\u795e\u4f24\uff0c\u660e\u6167\u516c\u4e3b\u8bef\u4f1a\u4e86\u5979\u7684\u610f\u601d\uff0c\u53ea\u9053\u5979\u5fc3\u4e2d\u4ecd\u6709 \u82a5\u8482\uff0c\u5992\u610f\u672a\u6d88\uff0c\u4e0d\u613f\u81ea\u5df1\u8dd1\u53bb\u6295\u5954\u674e\u601d\u5357\u3002\u4e8e\u662f\u8bf4\u9053\uff1a\u201c\u6211\u662f\u5bfb\u627e\u4f60\u4eec\uff0c\u4e0d\u8fc7\u6211\u4e5f\u77e5\u9053\u4f60 \u4eec\u662f\u4e0d\u4fbf\u6536\u7559\u6211\u7684\uff0c\u6240\u4ee5\u6211\u5e76\u4e0d\u60f3\u6253\u6270\u4f60\u4eec\u3002\u4f60\u653e\u5fc3\uff0c\u6211\u4eec\u8499\u53e4\u4eba\u672c\u6765\u5c31\u662f\u60ef\u4e8e\u6d41\u6d6a\u3001\u5c45\u65e0 \u5b9a\u5904\u7684\u6c11\u65cf\uff0c\u4e2d\u56fd\u5730\u65b9\u8fd9\u4e48\u5927\uff0c\u6211\u6b63\u597d\u8d81\u8fd9\u4e2a\u673a\u4f1a\u5230\u5404\u5904\u901b\u901b\u3002\u6709\u4e00\u5929\uff0c\u8d70\u5f97\u538c\u5026\u4e86\uff0c\u6211\u5c31 \u627e\u4e2a\u5e7d\u9759\u7684\u5730\u65b9\u4f4f\u4e0b\u6765\u3002\u6211\u8fd8\u6709\u70b9\u73e0\u5b9d\u53ef\u4ee5\u53d8\u5356\uff0c\u5927\u7ea6\u8db3\u591f\u7ef4\u6301\u751f\u8ba1\u3002\u201d \u3000\u3000\u6768\u5a49\u5341\u5206\u611f\u52a8\uff0c\u4e0d\u89c9\u6c81\u51fa\u4e86\u6cea\u73e0\uff0c\u7d27\u7d27\u63e1\u4f4f\u660e\u6167\u516c\u4e3b\u7684\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u516c\u4e3b\uff0c\u4f60\u8bef\u4f1a\u4e86\u3002 \u4f60\u5f85\u6211\u8fd9\u6837\u597d\uff0c\u6211\u5c31\u662f\u7c89\u8eab\u788e\u9aa8\uff0c\u4ea6\u65e0\u4ee5\u4e3a\u62a5\u3002\u4e0d\u8fc7\uff0c\u674e\u3001\u674e\u601d\u5357\u2014\u2014\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u89c1\u5979\u54ed\u4e86\u51fa\u6765\uff0c\u4e0d\u89c9\u5403\u4e86\u4e00\u60ca\uff0c\u8fde\u5fd9\u95ee\u9053\uff1a\u201c\u674e\u601d\u5357\u600e\u4e48\u6837\u4e86\uff1f\u201d\u5fc3\u60f3\u5728\u8fd9\u5175 \u8352\u9a6c\u4e71\u7684\u5e74\u5934\uff0c\u674e\u601d\u5357\u95f4\u5173\u4e07\u91cc\uff0c\u4ece\u548c\u6797\u9003\u56de\u672c\u56fd\uff0c\u96be\u4fdd\u5728\u8def\u4e0a\u4e0d\u906d\u610f\u5916\u3002 \u3000\u3000\u6768\u5a49\u62b9\u5e72\u773c\u6cea\uff0c\u5fcd\u7740\u4f24\u5fc3\u8bf4\u9053\uff1a\u201c\u4ed6\u6ca1\u6709\u6b7b\uff0c\u4e0d\u8fc7\u4ed6\u5df2\u7ecf\u4e0e\u6211\u5206\u624b\u4e86\u3002\u201d\u660e\u6167\u516c\u4e3b\u6115\u7136 \u95ee\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u6b63\u662f\uff1a \u3000\u3000\u4e07\u91cc\u8fdc\u6765\u5bfb\u6545\u4fa3\uff0c\u60ca\u5fc3\u60c5\u6d77\u53c8\u7ffb\u6ce2\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b\u2014\u2014\u7b2c\u4e09\u5341\u56de\u3000\u516c\u4e3b\u9003\u5a5a\u60c5\u6005\u6005\u3000\u8427\u90ce\u907f\u9762\u610f\u832b\u832b \u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b \u7b2c\u4e09\u5341\u56de\u3000\u516c\u4e3b\u9003\u5a5a\u60c5\u6005\u6005\u3000\u8427\u90ce\u907f\u9762\u610f\u832b\u832b \u3000\u3000\u6768\u5a49\u4e0d\u613f\u5411\u660e\u6167\u516c\u4e3b\u7ec6\u8bc9\u4f24\u5fc3\u4e4b\u4e8b\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u65e5\u6211\u4eec\u78b0\u5230\u4e86\u8d35\u56fd\u7684\u795e\u7bad\u624b\u54f2\u522b\uff0c\u4e71\u519b \u53ae\u6740\u4e4b\u4e2d\uff0c\u6211\u4e0e\u4ed6\u5931\u6563\u4e86\u3002\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u677e\u4e86\u53e3\u6c14\uff0c\u7b11\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\uff0c\u6211\u8fd8\u53ea\u9053\u662f\u4f60\u4eec\u5c0f\u4e24\u53e3\u5b50\u5435\u7ffb\u4e86\u5462\u3002\u6768\u59d0\u59d0\uff0c \u6211\u5f88\u60ed\u6127\uff0c\u662f\u6211\u4eec\u7684\u5c06\u519b\u4f7f\u5f97\u4f60\u4eec\u592b\u59bb\u79bb\u6563\uff0c\u4e0b\u4ee4\u6349\u62ff\u4f60\u4eec\u7684\u53c8\u6b63\u662f\u6211\u7684\u7236\u4eb2\u3002\u4e0d\u8fc7\u4f60\u53ef\u4ee5 \u653e\u5fc3\uff0c\u674e\u516c\u5b50\u82e5\u7136\u88ab\u64d2\uff0c\u751a\u6216\u5df2\u906d\u4e0d\u5e78\u7684\u8bdd\uff0c\u54f2\u522b\u4e00\u5b9a\u4f1a\u62a5\u544a\u6211\u7684\u56db\u54e5\u7684\u3002\u4f46\u6211\u5728\u548c\u6797\u53ef\u5e76 \u6ca1\u6709\u542c\u5230\u8fd9\u6837\u7684\u6d88\u606f\u3002\u201d \u3000\u3000\u5361\u6d1b\u4e1d\u9053\uff1a\u201c\u4f60\u73b0\u5728\u6253\u542c\u5230\u4ed6\u7684\u6d88\u606f\u6ca1\u6709\uff1f\u201d\u6768\u5a49\u8fdf\u7591\u534a\u664c\uff0c\u8bf4\u9053\uff1a\u201c\u8fd8\u6ca1\u6709\u3002\u201d\u5361\u6cbc \u4e1d\u9053\uff1a\u201c\u674e\u516c\u5b50\u672c\u9886\u9ad8\uff0c\u6211\u60f3\u4ed6\u4e00\u5b9a\u4f1a\u5e73\u5b89\u65e0\u4e8b\u7684\u3002\u201d\u8bf4\u7740\u53c8\u7b11\u9053\uff1a\u201c\u4e5f\u96be\u602a\u4f60\u5fc3\u91cc\u5fe7\u6101\uff0c \u6211\u548c\u963f\u76d6\u5206\u79bb\u7684\u90a3\u6bb5\u65e5\u5b50\uff0c\u4e5f\u662f\u65e5\u91cc\u591c\u91cc\u60f3\u7740\u4ed6\u7684\u3002\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u6211\u624d\u4e0d\u60f3\u4ed6\u5462\uff01\u201d\u5361\u6d1b\u4e1d\u89c1\u5979\u7684\u6001\u5ea6\u597d\u4f3c\u751a\u4e3a\u8ba4\u771f\uff0c\u6014\u4e86\u4e00\u6014\uff0c\u7b11\u9053\uff1a\u201c\u662f \u5440\uff0c\u4ed6\u5fc3\u91cc\u53ea\u6709\u4e00\u4e2a\u4f60\uff0c\u8fdf\u65e9\u603b\u4f1a\u89c1\u9762\uff0c\u4e0d\u6015\u4ed6\u98de\u5230\u54ea\u91cc\u53bb\u3002\u539f\u4e5f\u7528\u4e0d\u7740\u62c5\u5fc3\u3002\u201d\u5979\u81ea\u4ee5\u4e3a \u61c2\u5f97\u6768\u5a49\u7684\u5fc3\u610f\uff0c\u4e0d\u6599\u6b63\u662f\u89e6\u7740\u6768\u5a49\u7684\u4f24\u5fc3\u4e4b\u5904\u3002 \u3000\u3000\u6768\u5a49\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u4e71\u4e16\u79bb\u5408\u672c\u5c5e\u5bfb\u5e38\uff0c\u53cc\u65b9\u97f3\u8baf\u65ad\u7edd\uff0c\u751f\u6b7b\u672a\u77e5\uff0c\u4ed6\u82e5\u89c1\u7740\u4e86\u6bd4\u6211\u66f4\u597d \u7684\u5973\u5b50\uff0c\u8c01\u53c8\u4fdd\u5f97\u4ed6\u4e0d\u4f1a\u65e5\u4e45\u60c5\u751f\uff1f\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u9053\uff1a\u201c\u6211\u6562\u62c5\u4fdd\u3002\u522b\u4eba\u4e0d\u77e5\u9053\u4ed6\u5bf9\u4f60\u7684\u6df1\u60c5\uff0c\u6211\u662f\u77e5\u9053\u7684\u3002\u83ab\u8bf4\u4f60\u662f\u624d\u8c8c\u53cc \u5168\uff0c\u5973\u5b50\u4e4b\u4e2d\u7f55\u89c1\uff0c\u5c31\u7b97\u8fd8\u6709\u6bd4\u4f60\u66f4\u7f8e\u7684\u5929\u4ed9\u4e0b\u51e1\uff0c\u674e\u516c\u5b50\u4e5f\u51b3\u4e0d\u4f1a\u53d8\u5fc3\uff01\u6211\u90fd\u76f8\u4fe1\u4ed6\uff0c\u96be \u9053\u4f60\u53cd\u800c\u4e0d\u6562\u76f8\u4fe1\u4ed6\u4e86\uff1f\u201d \u3000\u3000\u963f\u76d6\u4e0e\u5361\u6d1b\u4e1d\u5e76\u4e0d\u77e5\u9053\u660e\u6167\u516c\u4e3b\u66fe\u7ecf\u559c\u6b22\u8fc7\u674e\u601d\u5357\uff0c\u53ea\u6709\u6768\u5a49\u5fc3\u4e2d\u660e\u767d\u3002\u542c\u4e86\u8fd9\u4e00\u756a \u8bdd\uff0c\u5fc3\u4e2d\u751a\u4e3a\u611f\u52a8\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u662f\u5440\uff0c\u4ed6\u4e3a\u4e86\u6211\u4e0d\u6015\u5f97\u7f6a\u6210\u5409\u601d\u6c57\uff0c\u4e3a\u4e86\u6211\u4e0d\u60dc\u553e\u5f03\u553e\u624b \u53ef\u5f97\u7684\u5bcc\u8d35\u8363\u534e\u3002\u660e\u6167\u516c\u4e3b\u5e76\u975e\u4e00\u4e2a\u666e\u901a\u7684\u5973\u5b50\uff0c\u5979\u7684\u7f8e\u8c8c\u4e5f\u662f\u4e16\u95f4\u7f55\u6709\u7684\u3002\u5357\u54e5\u82e5\u7136\u662f\u4e2a \u5bb9\u6613\u53d8\u5fc3\u7684\u4eba\uff0c\u65e9\u5c31\u505a\u4e86\u8499\u53e4\u7684\u9a78\u9a6c\u4e86\u3002\u201d\u60f3\u81f3\u6b64\u5904\uff0c\u5bf9\u5b5f\u660e\u971e\u7684\u731c\u5fcc\u4e4b\u5fc3\u867d\u672a\u5168\u6d88\uff0c\u5bf9\u674e \u601d\u5357\u5374\u5df2\u591a\u4e86\u51e0\u5206\u76f8\u4fe1\u3002 \u3000\u3000\u660e\u6167\u516c\u4e3b\u8bf4\u4e86\u8fd9\u756a\u8bf4\u8bdd\uff0c\u4e0d\u7531\u5f97\u4e5f\u662f\u8ffd\u601d\u5f80\u4e8b\uff0c\u611f\u6000\u8eab\u4e16\uff0c\u9eef\u7136\u795e\u4f24\u3002\u5361\u6d1b\u4e1d\u83ab\u540d\u5176 \u5999\uff0c\u8bf4\u9053\uff1a\u201c\u54a6\uff0c\u4f60\u4eec\u4e24\u4e2a\u662f\u600e\u4e48\u6837\u7684\uff1f\u6768\u59d1\u5a18\uff0c\u4f60\u662f\u4fe1\u53e3\u5f00\u5f00\u73a9\u7b11\u7684\u5427\uff0c\u516c\u4e3b\u4f60\u5374\u8ba4\u771f\u7684 \u529d\u8d77\u5979\u4e86\uff1f\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u5b9a\u4e86\u5b9a\u795e\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u4f60\u77e5\u9053\u6211\u6ca1\u6709\u4ec0\u4e48\u670b\u53cb\uff0c\u4f60\u4eec\u4e8c\u4eba\u548c\u6768\u59d1\u5a18\u5c31\u662f\u6211\u6700 \u8981\u597d\u7684\u670b\u53cb\u4e86\u3002\u6768\u59d1\u5a18\u8fc7\u53bb\u548c\u6211\u4e00\u8d77\u7684\u65f6\u5019\uff0c\u6837\u6837\u5173\u5fc3\u6211\uff0c\u6240\u4ee5\u6211\u4e5f\u5173\u5fc3\u5979\u3002\u6211\u4e0d\u613f\u610f\u89c1\u5230 \u5979\u6709\u534a\u70b9\u5fe7\u6101\u3002\u867d\u7136\u6211\u77e5\u9053\u5979\u662f\u8bf4\u7b11\uff0c\u6211\u4e5f\u4e0d\u77e5\u4e0d\u89c9\u5730\u5c31\u5f53\u771f\u529d\u8d77\u5979\u4e86\u3002\u201d \u3000\u3000\u6768\u5a49\u4e3a\u4e86\u66ff\u660e\u6167\u516c\u4e3b\u63a9\u9970\uff0c\u4e5f\u4e3a\u81ea\u5df1\u63a9\u9970\uff0c\u5f53\u4e0b\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u662f\u8bf4\u7740\u73a9\u7684\uff0c\u591a\u8c22\u516c \u4e3b\u5173\u5fc3\u3002\u73b0\u5728\u54b1\u4eec\u8bf4\u6b63\u7ecf\u7684\u5427\u3002\u4f60\u4eec\u5728\u6c5f\u6e56\u6d41\u6d6a\uff0c\u8fd9\u53ef\u4e0d\u662f\u529e\u6cd5\uff01\u201d \u3000\u3000\u6768\u5a49\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u6211\u5012\u6709\u4e2a\u5730\u65b9\uff0c\u5c31\u4e0d\u77e5\u516c\u4e3b\u80af\u4e0d\u80af\u5c48\u9a7e\u524d\u5f80\uff1f\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u82e6\u7b11\u9053\uff1a\u201c\u6211\u662f\u4f46\u6c42\u6709\u4e2a\u5bb9\u8eab\u4e4b\u5730\uff0c\u8fd8\u8c08\u5f97\u4e0a\u4ec0\u4e48\u5c48\u4e0d\u5c48\u9a7e\uff1f\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u6211\u6709\u4e00\u4f4d\u670b\u53cb\uff0c\u662f\u4e00\u5bb6\u5c71\u5be8\u7684\u5be8\u4e3b\uff0c\u516c\u4e3b\u5230\u90a3\u91cc\u85cf\u8eab\u6700\u662f\u5b89\u5168\u4e0d\u8fc7\u3002\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u597d\u5947\u5fc3\u8d77\uff0c\u6697\u81ea\u5bfb\u601d\uff1a\u201c\u5be8\u4e3b\u4e0d\u5c31\u662f\u5f3a\u76d7\u5934\u5b50\u5417\uff0c\u6211\u600e\u597d\u5230\u5f3a\u76d7\u5de2\u91cc\u85cf\u8eab\uff1f\u201d \u95ee\u9053\uff1a\u201c\u8fd9\u4f4d\u5be8\u4e3b\u662f\u7537\u7684\u8fd8\u662f\u5973\u7684\uff1f\u201d \u3000\u3000\u6768\u5a49\u731c\u60f3\u5f97\u5230\u516c\u4e3b\u7684\u5fc3\u610f\uff0c\u7b11\u9053\uff1a\u201c\u662f\u4e2a\u5973\u7684\u3002\u5979\u867d\u7136\u662f\u4f4d\u7eff\u6797\u9996\u9886\uff0c\u5374\u4e0e\u522b\u7684\u5f3a\u76d7\u4e0d \u540c\u3002\u5979\u624b\u4e0b\u7684\u55bd\u5175\u6050\u6015\u6bd4\u4f60\u4eec\u5175\u58eb\u7684\u7eaa\u5f8b\u8fd8\u597d\u5427\u3002\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u9887\u6709\u7236\u51e4\uff0c\u6709\u4e00\u526f\u8c6a\u8fc8\u7684\u6027\u683c\uff0c\u7b11\u9053\uff1a\u201c\u65e2\u7136\u6709\u8fd9\u6837\u4e00\u4f4d\u5dfe\u5e3c\u82f1\u96c4\uff0c\u6211\u548c\u5979\u7ed3 \u8bc6\u4e5f\u662f\u65e0\u59a8\u3002\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u597d\u6559\u516c\u4e3b\u5f97\u77e5\uff0c\u6211\u8fd9\u4f4d\u670b\u53cb\u540d\u53eb\u5c60\u51e4\uff0c\u5979\u662f\u5c60\u5927\u4fa0\u5c60\u767e\u57ce\u7684\u5973\u513f\u3002\u516c\u4e3b\u6216\u8bb8 \u66fe\u7ecf\u542c\u8fc7\u8fd9\u4f4d\u5c60\u5927\u4fa0\u7684\u540d\u5b57\u5427\uff1f\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u636e\u6211\u6240\u77e5\uff0c\u5c60\u767e\u57ce\u66fe\u7ecf\u5728\u91d1\u56fd\u95f9\u5f97\u5929\u7ffb\u5730\u8986\uff0c\u540e\u6765\u9003\u5230\u8499 \u53e4\uff0c\u91d1\u56fd\u56fd\u5e08\u9633\u5929\u96f7\u4eb2\u81ea\u6765\u8ffd\u6355\u4ed6\u3002\u5f53\u65f6\u6211\u4eec\u548c\u91d1\u56fd\u5c1a\u672a\u4ea4\u5175\uff0c\u9633\u5929\u96f7\u5f97\u5230\u6211\u4eec\u91d1\u5e10\u6b66\u58eb\u7684 \u5e2e\u5fd9\uff0c\u5728\u6492\u54c8\u62c9\u5927\u6c99\u6f20\u4e2d\u5c06\u4ed6\u51fb\u6bd9\u3002\u8fd9\u4f4d\u5c60\u59d1\u5a18\u77e5\u9053\u6211\u662f\u8499\u53e4\u7684\u516c\u4e3b\uff0c\u53ea\u6015\u4f1a\u628a\u6211\u5f53\u4f5c\u4ec7 \u4eba\u3002\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u8fd9\u4f4d\u5c60\u59d1\u5a18\u6df1\u660e\u662f\u975e\uff0c\u81ea\u4f1a\u5206\u6e05\u654c\u53cb\u3002\u6740\u5979\u7236\u4eb2\u7684\u662f\u9633\u5929\u96f7\u548c\u4f60\u4eec\u7684\u6b66\u58eb\uff0c \u8fd9\u4e0e\u516c\u4e3b\u6beb\u65e0\u5173\u7cfb\uff0c\u6211\u6562\u62c5\u4fdd\u5979\u51b3\u4e0d\u4f1a\u8fc1\u6012\u5230\u516c\u4e3b\u8eab\u4e0a\u3002\u516c\u4e3b\u65e0\u987b\u987e\u8651\u3002\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u9053\uff1a\u201c\u53ea\u6050\u5979\u7684\u624b\u4e0b\u672a\u5fc5\u80fd\u591f\u50cf\u5979\u4e00\u6837\u660e\u8fa8\u662f\u975e\u3002\u201d \u3000\u3000\u6768\u5a49\u60f3\u4e86\u4e00\u60f3\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u5c31\u8fd9\u6837\u5427\uff0c\u516c\u4e3b\u4f60\u6682\u4e14\u4e0d\u5fc5\u8868\u9732\u8eab\u4efd\uff0c\u6211\u5199\u4e00\u5c01\u4fe1\u7ed9\u4f60\u5e26 \u53bb\uff0c\u53ea\u8bf4\u4f60\u4eec\u662f\u6211\u7684\u670b\u53cb\u4fbf\u884c\u4e86\u3002\u4e0d\u8fc7\uff0c\u6709\u4e00\u4ef6\u4e8b\uff0c\u6211\u4e5f\u60f3\u5411\u4f60\u4eec\u5148\u8bf4\u660e\u767d\uff0c\u4ed6\u4eec\u4e43\u662f\u4e49 \u519b\uff0c\u8d35\u56fd\u7684\u519b\u961f\u82e5\u7136\u6253\u6765\uff0c\u4ed6\u4eec\u662f\u4e00\u5b9a\u4f1a\u8d77\u800c\u62b5\u6297\u7684\u3002\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u751a\u662f\u82e6\u607c\uff0c\u534a\u664c\u8bf4\u9053\uff1a\u201c\u6211\u660e\u767d\u3002\u6211\u4eec\u7684\u58eb\u5175\u8dd1\u5230\u4f60\u4eec\u7684\u5730\u65b9\u6253\u4ed7\uff0c\u672c\u6765\u5c31\u662f \u6211\u4eec\u7684\u4e0d\u5bf9\u3002\u771f\u5230\u5176\u65f6\uff0c\u6211\u907f\u5f00\u4fbf\u662f\u3002\u6768\u59d0\u59d0\uff0c\u8bf7\u4f60\u4e5f\u8c05\u89e3\u6211\u7684\u82e6\u8877\u3002\u6211\u662f\u53cd\u5bf9\u7239\u7239\u5174\u5175\u6765 \u5360\u4f60\u4eec\u7684\u5730\u65b9\u7684\uff0c\u4f46\u6211\u4e5f\u4e0d\u80fd\u4e0e\u672c\u56fd\u4e3a\u654c\u3002\u201d \u3000\u3000\u6768\u5a49\u70b9\u4e86\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e2a\u6211\u4e5f\u660e\u767d\u3002\u201d\u5e99\u4e2d\u6709\u7559\u7ed9\u5ba2\u4eba\u7b7e\u9999\u6cb9\u7684\u7eb8\u7b14\uff0c\u6768\u5a49\u5728\u201c\u9999 \u6cb9\u7c3f\u201d\u4e0b\uff0c\u6495\u4e0b\u4e00\u5f20\u767d\u7eb8\uff0c\u5199\u597d\u4e86\u4e00\u5c01\u4fe1\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u5230\u7405\u739b\u5c71\u53bb\uff0c\u5148\u627e\u4e00\u4f4d\u540d\u53eb\u77f3\u749e\u7684 \u526f\u5be8\u4e3b\uff0c\u4ed6\u5c31\u4f1a\u5e26\u4f60\u4eec\u53bb\u89c1\u5c60\u59d1\u5a18\u4e86\u3002\u201d\u8981\u77e5\u5c60\u51e4\u7684\u5c71\u5be8\u4e2d\u53ea\u6709\u77f3\u749e\u6df1\u77e5\u6768\u5a49\uff0c\u6768\u5a49\u4e0e\u5c60\u51e4 \u5374\u8fd8\u8c08\u4e0d\u4e0a\u662f\u77e5\u4ea4\uff0c\u662f\u4ee5\u6768\u5a49\u59d4\u6258\u77f3\u749e\u4ee3\u4e3a\u5148\u7980\u3002 \u3000\u3000\u660e\u6167\u516c\u4e3b\u6536\u4e86\u4fe1\uff0c\u7b11\u9053\uff1a\u201c\u4ece\u524d\u4f60\u9003\u96be\u9003\u5230\u6211\u4eec\u8499\u53e4\u7684\u519b\u8425\u91cc\u6765\uff0c\u5982\u4eca\u6211\u8dd1\u5230\u4f60\u4eec\u6c49\u4eba \u7684\u5c71\u5be8\u53bb\u6c42\u5e87\u62a4\uff0c\u8fd9\u771f\u662f\u65e0\u72ec\u6709\u5076\uff0c\u5148\u540e\u8f89\u6620\u4e86\u3002\u201d \u3000\u3000\u963f\u76d6\u62ac\u5934\u4e00\u770b\uff0c\u8bf4\u9053\uff1a\u201c\u5929\u5df1\u4eae\u4e86\uff0c\u671d\u971e\u707f\u70c2\uff0c\u4eca\u5929\u4e00\u5b9a\u662f\u4e2a\u597d\u5929\u6c14\u3002\u201d\u660e\u6167\u516c\u4e3b\u9053\uff1a \u201c\u6768\u59d0\u59d0\uff0c\u591a\u8c22\u4f60\u7684\u5e2e\u52a9\uff0c\u5929\u5df2\u4eae\u4e86\uff0c\u54b1\u4eec\u5e94\u8be5\u5206\u624b\u5566\u3002\u201d \u3000\u3000\u5361\u6d1b\u4e1d\u9053\uff1a\u201c\u6768\u59d0\u59d0\uff0c\u4f46\u613f\u4f60\u4e0e\u674e\u516c\u5b50\u65e9\u65e5\u76f8\u9022\uff0c\u6b64\u540e\u6c38\u4e0d\u5206\u79bb\uff0c\u5c31\u50cf\u6211\u548c\u963f\u76d6\u4e00\u6837\uff0c \u4f60\u662f\u6211\u4eec\u592b\u59bb\u7684\u5927\u6069\u4eba\uff0c\u53ef\u60dc\u6211\u5374\u6ca1\u6709\u672c\u9886\u5e2e\u52a9\u4f60\u3002\u201d \u3000\u3000\u6768\u5a49\u770b\u89c1\u5361\u6d1b\u4e1d\u7684\u90a3\u628a\u4e1c\u4e0d\u62c9\uff08\u4e50\u5668\uff09\u5e26\u5728\u8eab\u8fb9\uff0c\u60f3\u8d77\u5f80\u4e8b\uff0c\u51c4\u7136\u7b11\u9053\uff1a\u201c\u4f46\u613f\u5982\u6b64\uff0c \u4f46\u5c31\u53ea\u6015\u6211\u6ca1\u6709\u4f60\u8fd9\u798f\u6c14\u3002\u5361\u6d1b\u4e1d\uff01\u8bb0\u5f97\u6211\u662f\u88ab\u4f60\u7684\u6b4c\u58f0\u5438\u5f15\u56e0\u800c\u8ba4\u8bc6\u4f60\u7684\uff0c\u4f60\u80fd\u591f\u4e3a\u6211\u5f39 \u4e00\u6b21\u4e1c\u4e0d\u62c9\u5417\uff1f\u201d \u3000\u3000\u5361\u6d1b\u4e1d\u9053\uff1a\u201c\u597d\uff0c\u6211\u7ed9\u4f60\u5531\u4e00\u9996\u6211\u4eec\u8349\u539f\u4e0a\u7684\u6c11\u6b4c\uff0c\u795d\u798f\u4f60\u65e9\u65e5\u4e0e\u60c5\u90ce\u76f8\u4f1a\u3002\u201d\u5361\u6d1b\u4e1d \u5929\u771f\u65e0\u90aa\uff0c\u5979\u8eab\u5fc3\u6c89\u6d78\u5728\u5e78\u798f\u4e4b\u4e2d\uff0c\u5c31\u6beb\u4e0d\u63a9\u9970\u5730\u8bf4\u4e86\u51fa\u6765\uff0c\u4e5f\u613f\u522b\u4eba\u83b7\u5f97\u4e0e\u5979\u540c\u6837\u7684\u5e78 \u798f\u3002 \u3000\u3000\u5361\u6d1b\u4e1d\u62c9\u4e00\u62c9\u963f\u76d6\uff0c\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u5408\u5531\u3002\u201d\u5f53\u4e0b\u5f39\u8d77\u4e86\u4e1c\u4e0d\u62c9\uff0c\u5531\u9053\uff1a \u3000\u3000\u201c\u5927\u98ce\u5377\u8d77\u4e86\u9ec4\u6c99\uff0c \u3000\u3000\u5929\u8fb9\u7684\u5140\u9e70\u76d8\u65cb\u6b32\u4e0b\u3002 \u3000\u3000\u54e5\u5440\uff0c\u4f60\u5c31\u662f\u8fd9\u53ea\u82f1\u6b66\u7684\u5140\u9e70\uff0c \u3000\u3000\u4f46\u4f60\u867d\u7136\u4e0d\u6015\u98ce\u6c99\uff0c \u3000\u3000\u4f60\u4e5f\u4e0d\u8981\u4e0b\u6765\u5566\u3002\u201d \u3000\u3000\u963f\u76d6\u63a5\u4e0b\u53bb\u5531\u9053\uff1a \u3000\u3000\u201c\u5927\u98ce\u5377\u8d77\u4e86\u9ec4\u6c99\uff0c \u3000\u3000\u8349\u539f\u4e0a\u7684\u7f8a\u7fa4\u60ca\u9003\u9a87\u6015\u3002 \u3000\u3000\u7267\u7f8a\u7684\u59d1\u5a18\u8ff7\u832b\u5931\u63aa\uff0c \u3000\u3000\u6050\u6015\u56de\u4e0d\u4e86\u5bb6\u3002 \u3000\u3000\u59b9\u5440\uff0c\u6211\u4e0d\u662f\u4e0d\u6015\u98ce\u6c99\uff0c \u3000\u3000\u6211\u662f\u6015\u4f60\u8ff7\u5931\u8def\u9014\uff0c\u56de\u4e0d\u4e86\u5bb6\u3002 \u3000\u3000\u4efb\u98ce\u6c99\u591a\u5927\uff0c \u3000\u3000\u6211\u4e5f\u8981\u6765\u5e26\u4f60\u56de\u5bb6\uff01\u201d \u3000\u3000\u8349\u539f\u4e0a\u8fd9\u9996\u6c11\u6b4c\u611f\u60c5\u771f\u631a\uff0c\u542c\u5f97\u6768\u5a49\u75f4\u4e86\u3002\u4e0d\u89c9\u6697\u81ea\u60f3\u9053\uff1a\u201c\u81ea\u4ece\u6211\u4e0e\u5357\u54e5\u5206\u624b\u4e4b\u540e\uff0c \u6211\u5f53\u771f\u5c31\u50cf\u662f\u4e00\u53ea\u8ff7\u9014\u7684\u7f94\u7f8a\uff0c\u6ca1\u4eba\u7ed9\u6211\u5e26\u8def\u3002\u6211\u51e0\u4e4e\u7ed9\u5c60\u9f99\u6240\u9a97\uff0c\u8fd9\u4e00\u6b21\u6211\u4e5f\u4e0d\u77e5\u662f\u5426\u53c8 \u505a\u9519\u4e86\u4e8b\uff0c\u9519\u602a\u4e86\u5b5f\u660e\u971e\uff1f\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u8d5e\u9053\uff1a\u201c\u5531\u5f97\u771f\u597d\u3002\u963f\u76d6\uff0c\u6211\u8fd8\u672a\u77e5\u9053\uff0c\u539f\u6765\u4f60\u4e5f\u662f\u54b1\u4eec\u8349\u539f\u7684\u6b4c\u624b\u5462\u3002\u201d \u3000\u3000\u6768\u5a49\u5982\u68a6\u521d\u9192\uff0c\u8bf4\u9053\uff1a\u201c\u5361\u6d1b\u4e1d\uff0c\u591a\u8c22\u4f60\u4e86\uff0c\u4f60\u5f39\u5f97\u771f\u597d\u3002\u201d \u3000\u3000\u8bdd\u72b9\u672a\u4e86\uff0c\u53ea\u542c\u5f97\u6709\u4eba\u8bf4\u9053\uff1a\u201c\u5f39\u5531\u5f97\u771f\u597d\uff0c\u8349\u539f\u4e4b\u6b4c\u5f53\u771f\u662f\u540d\u4e0d\u865a\u4f20\u3002\u201d\u53c8\u6709\u4eba\u9053\uff1a \u201c\u679c\u7136\u5979\u4eec\u8fd8\u5728\u8fd9\u91cc\u3002\u201d \u3000\u3000\u53ea\u89c1\u6709\u56db\u4e2a\u4eba\u8d70\u8fdb\u5e99\u6765\uff0c\u4e3a\u9996\u7684\u6b63\u662f\u8d3a\u4e5d\u516c\u3002\u7b2c\u4e8c\u4eba\u662f\u4e2a\u8001\u5987\u3002\u7b2c\u4e09\u4eba\u662f\u4e2a\u8499\u53e4\u6b66\u58eb\uff0c \u4e5f\u5c31\u662f\u8bf4\u8bdd\u8d5e\u7f8e\u5361\u6d1b\u4e1d\u4e4b\u4eba\u3002\u8d70\u5728\u6700\u540e\u7684\u662f\u4e00\u4e2a\u4e8c\u5341\u591a\u5c81\u7684\u5c11\u5e74\uff0c\u6768\u5a49\u89c1\u4e86\u6b64\u4eba\uff0c\u4e0d\u89c9\u5927\u5403 \u4e00\u60ca\u3002 \u3000\u3000\u539f\u6765\u8fd9\u4e2a\u5c11\u5e74\u4e0d\u662f\u522b\u4eba\uff0c\u6b63\u662f\u91d1\u56fd\u56fd\u5e08\u9633\u5929\u96f7\u7684\u4f84\u5b50\u9633\u575a\u767d\u3002 \u3000\u3000\u90a3\u5929\u665a\u4e0a\uff0c\u9633\u575a\u767d\u548c\u4e00\u4e2a\u8499\u53e4\u6b66\u58eb\u5230\u674e\u601d\u5357\u5bb6\u91cc\u5077\u7a83\u5175\u4e66\uff0c\u6070\u597d\u5b5f\u660e\u971e\u4e5f\u6765\u627e\u5bfb\u674e\u601d \u5357\uff0c\u5728\u674e\u5bb6\u5c4b\u540e\u7684\u677e\u5c71\u78b0\u4e0a\u9633\u575a\u767d\uff0c\u4e24\u4eba\u6597\u5251\uff0c\u5b5f\u660e\u971e\u51e0\u4e4e\u8d25\u5728\u4ed6\u7684\u624b\u4e0b\u3002\u5e78\u4e8f\u674e\u601d\u5357\u5728\u51fb \u5012\u4e86\u90a3\u4e2a\u8499\u53e4\u6b66\u58eb\u4e4b\u540e\uff0c\u53ca\u65f6\u8d76\u5230\uff0c\u4e24\u4eba\u8054\u624b\uff0c\u8fd9\u624d\u6740\u5f97\u9633\u575a\u767d\u5927\u8d25\u800c\u9003\u3002 \u3000\u3000\u90a3\u5929\u665a\u4e0a\uff0c\u6768\u5a49\u4e5f\u6b63\u597d\u6765\u5230\uff0c\u8eb2\u5728\u677e\u6797\u4e4b\u4e2d\uff0c\u628a\u7ecf\u8fc7\u7684\u60c5\u5f62\u90fd\u770b\u5728\u773c\u5185\u3002 \u3000\u3000\u6768\u5a49\u8ba4\u51fa\u4e86\u9633\u575a\u767d\uff0c\u8ddf\u7740\u4e5f\u8ba4\u51fa\u4e86\u6b64\u9645\u4e0e\u8d3a\u4e5d\u516c\u3001\u9633\u575a\u767d\u4e00\u540c\u6765\u7684\u8fd9\u4e2a\u6b66\u58eb\uff0c\u5c31\u662f\u90a3\u5929 \u665a\u4e0a\u7684\u90a3\u4e2a\u8499\u53e4\u6b66\u58eb\u3002 \u3000\u3000\u6765\u7684\u8fd9\u56db\u4e2a\u4eba\uff0c\u6768\u5a49\u8ba4\u5f97\u4e09\u4e2a\u3002\u53ea\u6709\u8d3a\u4e5d\u516c\u7684\u59bb\u5b50\u5979\u672a\u89c1\u8fc7\u3002 \u3000\u3000\u6768\u5a49\u5927\u5403\u4e00\u60ca\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8d3a\u4e5d\u516c\u53bb\u800c\u590d\u6765\uff0c\u5b9a\u7136\u4e0d\u6000\u597d\u610f\u3002\u9633\u575a\u767d\u8fd9\u53ae\u7684\u672c\u9886\u548c\u5357 \u54e5\u5dee\u4e0d\u4e86\u591a\u5c11\uff0c\u6211\u51b3\u4e0d\u662f\u4ed6\u7684\u654c\u624b\u3002\u8d3a\u4e5d\u516c\u548c\u90a3\u8499\u53e4\u6b66\u58eb\u4ea6\u975e\u5eb8\u624b\uff0c\u518d\u52a0\u4e0a\u4e00\u4e2a\u6b66\u529f\u672a\u77e5\u6df1 \u6d45\u7684\u8001\u5a46\u5a46\uff01\u53ea\u6015\u963f\u76d6\u4e5f\u662f\u96be\u4ee5\u5bf9\u4ed8\u7684\u4e86\u3002\u201d \u3000\u3000\u5fc3\u5ff5\u672a\u5df2\uff0c\u53ea\u89c1\u90a3\u4e2a\u8499\u53e4\u6b66\u571f\u5df2\u7ecf\u7ad9\u5728\u660e\u6167\u516c\u4e3b\u7684\u9762\u524d\uff0c\u606d\u606d\u656c\u656c\u5730\u884c\u4e86\u4e00\u4e2a\u5927\u793c\uff0c\u8bf4 \u9053\uff1a\u201c\u7a9d\u9614\u53f0\u5927\u6c57\u548c\u62d6\u96f7\u76d1\u56fd\u8bf7\u516c\u4e3b\u56de\u53bb\u3002\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u9053\uff1a\u201c\u6211\u5230\u4e2d\u539f\u6765\u6563\u4e00\u6563\u5fc3\uff0c\u6e38\u5174\u6b63\u6d53\uff0c\u4e0d\u60f3\u56de\u53bb\u3002\u201d \u3000\u3000\u90a3\u6b66\u58eb\u9053\uff1a\u201c\u8bf7\u516c\u4e3b\u73cd\u91cd\u4e07\u91d1\u4e4b\u4f53\uff0c\u4e0d\u5b9c\u5728\u654c\u56fd\u4e45\u7559\u3002\u5f85\u54b1\u4eec\u706d\u4e86\u91d1\u3001\u5b8b\u4e24\u56fd\uff0c\u7edf\u4e00\u4e2d \u539f\u4e4b\u540e\uff0c\u516c\u4e3b\u518d\u6765\u6e38\u73a9\u4e0d\u8fdf\u3002\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u6012\u9053\uff1a\u201c\u52ff\u5962\uff0c\u4f60\u6562\u7ba1\u6211\uff01\u201d \u3000\u3000\u90a3\u6b66\u571f\u9053\uff1a\u201c\u5c0f\u4eba\u4e0d\u6562\uff0c\u4e0d\u8fc7\u8fd9\u662f\u5927\u6c57\u548c\u76d1\u56fd\u7684\u547d\u4ee4\u3002\u201d \u3000\u3000\u539f\u6765\u6b64\u4eba\u540d\u53eb\u52ff\u5962\uff0c\u4e43\u662f\u91d1\u5e10\u6b66\u58eb\u4e2d\u6392\u540d\u7b2c\u5341\u516b\u7684\u4eba\u7269\uff0c\u6b66\u529f\u867d\u7136\u5e76\u4e0d\u5f88\u5f3a\uff0c\u4f46\u5374\u662f\u62d6 \u96f7\u7684\u4eb2\u4fe1\uff0c\u6545\u6b64\u62d6\u96f7\u624d\u4f1a\u53eb\u4ed6\u966a\u9633\u575a\u767d\u56de\u56fd\uff0c\u8d1f\u8d23\u76d1\u89c6\u9633\u575a\u767d\u4e0e\u4ed6\u53d4\u7236\uff0c\u5e76\u4e3a\u62d6\u96f7\u5e72\u51e0\u6869\u673a \u5bc6\u4e4b\u4e8b\u7684\u3002 \u3000\u3000\u516c\u4e3b\u7684\u51fa\u8d70\u5728\u9633\u575a\u767d\u56de\u56fd\u4e4b\u540e\uff0c\u62d6\u96f7\u7acb\u5373\u6d3e\u4eba\u6765\u901a\u77e5\u52ff\u5962\uff0c\u53eb\u4ed6\u501f\u52a9\u91d1\u56fd\u56fd\u5e08\u9633\u5929\u96f7\u4e4b \u529b\uff0c\u641c\u67e5\u516c\u4e3b\u7684\u884c\u8e2a\u3002\u6070\u6070\u662f\u4e09\u5929\u4e4b\u524d\uff0c\u52ff\u5962\u63a5\u5230\u4e86\u8fd9\u4e2a\u547d\u4ee4\u3002 \u3000\u3000\u52ff\u5962\u627e\u5230\u4e86\u9633\u575a\u767d\uff0c\u9633\u575a\u767d\u662f\u4f4f\u5728\u8d3a\u4e5d\u516c\u7684\u5bb6\u91cc\u7684\uff0c\u6070\u5de7\u8d3a\u4e5d\u516c\u4e5f\u56de\u6765\u62a5\u8baf\uff0c\u8bf4\u662f\u5728\u4e00 \u5ea7\u7834\u5e99\u91cc\u78b0\u89c1\u660e\u6167\u516c\u4e3b\u548c\u4e00\u4e2a\u59d3\u6768\u7684\u5973\u5b50\u3002\u8fd9\u6b63\u662f\u201c\u8e0f\u7834\u94c1\u978b\u65e0\u89c5\u5904\uff0c\u5f97\u6765\u5168\u4e0d\u8d39\u529f\u592b\u201d\u3002 \u9633\u575a\u767d\u4e0e\u52ff\u5962\u5e26\u4e86\u8d3a\u4e5d\u516c\u592b\u5987\uff0c\u4fbf\u9a6c\u4e0a\u8d76\u6765\u4e86\u3002 \u3000\u3000\u5c60\u9f99\u4e0e\u9633\u575a\u767d\u5e76\u975e\u4e00\u8def\uff0c\u4ed6\u4e0e\u9633\u575a\u767d\u7684\u53d4\u7236\u53c8\u6709\u6740\u7236\u4e4b\u4ec7\uff0c\u867d\u8bf4\u5c60\u9f99\u5df2\u662f\u51b3\u610f\u5356\u8eab\u6295 \u9760\uff0c\u65e9\u5df2\u628a\u7236\u4ec7\u629b\u4e4b\u8111\u540e\uff0c\u4f46\u5728\u9762\u5b50\u4e0a\u4ecd\u662f\u4e0d\u4fbf\u4e0e\u9633\u5bb6\u7236\u5b50\u4e00\u6c14\u3002\u6545\u800c\u5c60\u9f99\u6628\u665a\u4e43\u662f\u72ec\u81ea\u8d70 \u7684\u3002 \u3000\u3000\u9633\u575a\u767d\u662f\u4e2a\u597d\u8272\u4e4b\u5f92\uff0c\u542c\u8bf4\u674e\u601d\u5357\u7684\u672a\u5a5a\u59bb\u6768\u5a49\u4e5f\u5728\u90a3\u4e2a\u5e99\u5b50\u91cc\u53d1\u73b0\uff0c\u5fc3\u91cc\u6697\u81ea\u6b22\u559c\uff0c \u60f3\u9053\uff1a\u201c\u4e45\u95fb\u8fd9\u4f4d\u6768\u59d1\u5a18\u4e43\u662f\u540d\u95e8\u95fa\u79c0\uff0c\u7edd\u8272\u7f8e\u4eba\uff0c\u5c60\u9f99\u5343\u65b9\u767e\u8ba1\u4e5f\u5f97\u4e0d\u5230\u5979\uff0c\u60f3\u4e0d\u5230\u5982\u4eca \u649e\u5728\u6211\u7684\u624b\u4e0a\uff0c\u53ef\u4e0d\u80fd\u5c06\u5979\u653e\u8fc7\u4e86\u3002\u201d\u4e8e\u662f\u6025\u6025\u5fd9\u5fd9\u5e26\u4e86\u8d3a\u4e5d\u516c\u592b\u5987\u4f5c\u4e3a\u52a9\u624b\uff0c\u4fbf\u4e0e\u52ff\u5962\u8fde \u591c\u8d76\u6765\u3002 \u3000\u3000\u81f3\u4e8e\u718a\u5e84\u4e3b\u7b49\u4eba\uff0c\u4e00\u6765\u56e0\u4e3a\u52ff\u5962\u4e0d\u613f\u4ed6\u4eec\u4e0e\u95fb\u6b64\u4e8b\uff1b\u4e8c\u6765\u4ed6\u4eec\u672c\u9886\u5e73\u5eb8\uff0c\u4e5f\u5e2e\u4e0d\u4e0a\u4ec0\u4e48 \u5927\u5fd9\uff0c\u6545\u6b64\u9633\u575a\u767d\u8fd8\u662f\u6309\u7167\u539f\u6765\u7684\u8ba1\u5212\uff0c\u53eb\u4ed6\u4eec\u5404\u56de\u539f\u5730\uff0c\u7763\u4fc3\u624b\u4e0b\uff0c\u4fa6\u5bdf\u891a\u3001\u5b5f\u4e8c\u4eba\u7684\u884c \u8e2a\u3002 \u3000\u3000\u9633\u575a\u767d\u4e0e\u52ff\u5962\u4e00\u8def\u62c5\u5fc3\uff0c\u6050\u6015\u4f1a\u6251\u4e2a\u7a7a\u3002\u8d76\u5230\u8fd9\u5ea7\u7834\u5e99\uff0c\u53d1\u89c9\u516c\u4e3b\u548c\u6768\u5a49\u90fd\u672a\u79bb\u5f00\uff0c\u81ea \u662f\u4e0d\u80dc\u4e4b\u559c\u3002 \u3000\u3000\u4e14\u8bf4\u660e\u6167\u516c\u4e3b\u62d2\u7edd\u56de\u53bb\uff0c\u52ff\u5962\u8bf4\u51fa\u8fd9\u662f\u7a9d\u9614\u53f0\u5927\u6c57\u548c\u62d6\u96f7\u76d1\u56fd\u7684\u547d\u4ee4\uff0c\u516c\u4e3b\u6012\u9053\uff1a\u201c\u4f60 \u62ff\u5927\u6c57\u548c\u76d1\u56fd\u7684\u547d\u4ee4\u6050\u5413\u6211\u5417\uff1f\u4f60\u56de\u53bb\u66ff\u6211\u544a\u8bc9\u4ed6\u4eec\uff0c\u8fd9\u4e2a\u516c\u4e3b\u6211\u4e0d\u505a\u4e86\uff0c\u4ece\u4eca\u4e4b\u540e\uff0c\u6211\u4e0d \u6cbe\u4ed6\u4eec\u7684\u5149\uff0c\u4ed6\u4eec\u4e5f\u522b\u6765\u7ba1\u6211\uff01\u201d \u3000\u3000\u52ff\u5962\u7728\u4e86\u7728\u773c\u775b\uff0c\u4f5c\u4e86\u4e2a\u4e3a\u96be\u7684\u795e\u6001\u8bf4\u9053\uff1a\u201c\u516c\u4e3b\u53ef\u4ee5\u4e0d\u63a5\u5927\u6c57\u7684\u547d\u4ee4\uff0c\u5974\u624d\u5374\u662f\u4e0d\u6562 \u4e0d\u9075\uff01\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u8981\u600e\u6837\uff1f\u201d\u52ff\u5962\u8de8\u4e0a\u4e00\u6b65\uff0c\u8bf4\u9053\uff1a\u201c\u8bf7\u516c\u4e3b\u56de\u53bb\uff01\u201d\u4f38\u51fa\u624b\u6765\uff0c \u60f3\u62c9\u660e\u6167\u516c\u4e3b\u3002\u660e\u6167\u516c\u4e3b\u559d\u9053\uff1a\u201c\u4f60\u6562\u65e0\u793c\uff01\u201d\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u53ea\u542c\u5f97\u201c\u84ec\u201d\u7684\u4e00\u58f0\uff0c\u963f \u76d6\u5df2\u662f\u51fa\u624b\u6293\u7740\u4e86\u4ed6\uff0c\u5c06\u4ed6\u91cd\u91cd\u5730\u6454\u4e86\u4e00\u4e2a\u7b4b\u6597\uff01 \u3000\u3000\u52ff\u5962\u7d20\u77e5\u963f\u76d6\u4e43\u662f\u672c\u56fd\u6570\u4e00\u6570\u4e8c\u7684\u6454\u89d2\u597d\u624b\uff0c\u672c\u6765\u5df2\u6709\u9632\u5907\u3002\u54ea\u77e5\u4ecd\u8eb2\u4e0d\u4e86\u3002\u52ff\u5962\u722c\u4e86 \u8d77\u6765\uff0c\u5927\u6012\u559d\u9053\uff1a\u201c\u90fd\u662f\u4f60\u4eec\u4e8c\u4eba\u5506\u6446\u516c\u4e3b\uff0c\u975e\u628a\u4f60\u4eec\u62bc\u56de\u53bb\u4e0d\u53ef\uff01\u201d\u4e00\u4e2a\u8f6c\u8eab\uff0c\u7a81\u7136\u4fbf\u5411 \u5361\u6d1b\u4e1d\u6251\u53bb\u3002 \u3000\u3000\u963f\u76d6\u6309\u7167\u8499\u53e4\u6b66\u571f\u7684\u6454\u89d2\u89c4\u77e9\uff0c\u53cc\u81c2\u7b95\u5f20\uff0c\u51c6\u5907\u4ed6\u722c\u8d77\u6765\u5411\u81ea\u5df1\u53cd\u6251\uff0c\u4e0d\u6599\u52ff\u5962\u5374\u5411\u5361 \u6d1b\u4e1d\u6251\u53bb\uff0c\u5012\u662f\u9887\u51fa\u963f\u76d6\u610f\u6599\u4e4b\u5916\u3002 \u3000\u3000\u6768\u5a49\u5c55\u8896\u4e00\u62c2\uff0c\u559d\u9053\uff1a\u201c\u65e0\u803b\u4e4b\u5f92\uff0c\u7ed9\u6211\u5012\u4e0b\uff01\u201d\u8896\u4e2d\u5ba0\u6307\uff0c\u70b9\u4e2d\u4e86\u52ff\u5962\u80c1\u4e0b\u7684\u9ebb\u7a74\u3002 \u54ea\u77e5\u52ff\u5962\u7684\u672c\u9886\u867d\u4e0d\u5982\u5979\uff0c\u4f46\u540d\u5217\u91d1\u5e10\u6b66\u58eb\u4e4b\u4e2d\uff0c\u6b66\u529f\u4ea6\u975e\u6cdb\u6cdb\u3002\u4ed6\u7ec3\u6709\u4e00\u8eab\u94c1\u5e03\u886b\u7684\u529f \u592b\uff0c\u76ae\u7c97\u8089\u539a\uff0c\u7a74\u9053\u867d\u7ed9\u70b9\u4e2d\uff0c\u5374\u53ea\u662f\u4e00\u9635\u9178\u9ebb\uff0c\u8eab\u5b50\u5e76\u6ca1\u5012\u4e0b\u3002 \u3000\u3000\u963f\u76d6\u65cb\u98ce\u822c\u75be\u6251\u8fc7\u53bb\uff0c\u5927\u6012\u65a5\u9053\uff1a\u201c\u6789\u4f60\u8eab\u4e3a\u91d1\u5e10\u6b66\u58eb\uff0c\u6709\u80c6\u7684\u548c\u6211\u8f83\u91cf\uff0c\u4e3a\u4f55\u6b3a\u8d1f\u4e00 \u4e2a\u4e0d\u4f1a\u6b66\u529f\u7684\u5973\u5b50\uff01\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u5df1\u4e0e\u52ff\u5962\u62c6\u4e86\u51e0\u62db\u3002\u52ff\u5962\u9178\u9ebb\u672a\u8fc7\uff0c\u53cc\u62f3\u65e0\u529b\uff0c\u6253\u4e86\u4ed6\u4e24 \u62f3\uff0c\u963f\u76d6\u53ea\u5f53\u662f\u6293\u75d2\uff0c\u731b\u5730\u8db3\u5c16\u4e00\u52fe\uff0c\u53c8\u628a\u52ff\u5962\u6454\u4e86\u4e00\u8de4\u3002 \u3000\u3000\u6768\u5a49\u53eb\u9053\uff1a\u201c\u5c0f\u5fc3\uff01\u201d\u963f\u76d6\u5ffd\u89c9\u80cc\u540e\u52b2\u98ce\u98d2\u7136\uff0c\u77e5\u9053\u6709\u4eba\u5077\u88ad\u3002\u5f53\u4e0b\u80a9\u5934\u4e00\u77ee\uff0c\u53cd\u624b\u4fbf \u63ea\u3002\u8fd9\u662f\u6454\u89d2\u4e2d\u7684\u201c\u80a9\u80cc\u5f0f\u201d\uff0c\u963f\u76d6\u6700\u5f97\u610f\u7684\u624b\u6cd5\u4e4b\u4e00\u3002\u4e0d\u6599\u4ed6\u8fd9\u4e00\u63ea\u6ca1\u6709\u63ea\u7740\u90a3\u4eba\uff0c\u5374\u53cd \u800c\u7ed9\u90a3\u4eba\u6263\u7740\u4e86\u624b\u8155\u3002 \u3000\u3000\u963f\u76d6\u6c89\u80a9\u62f1\u80cc\uff0c\u501f\u529b\u4f7f\u529b\uff0c\u8eab\u8eaf\u4e00\u77ee\uff0c\u547c\u7684\u4e00\u58f0\uff0c\u628a\u9633\u575a\u767d\u6380\u4e86\u8d77\u6765\uff0c\u4ece\u4ed6\u7684\u5934\u9876\u7ffb\u4e86 \u8fc7\u53bb\u3002\u9633\u575a\u767d\u6293\u4f4f\u4ed6\u7684\u624b\u8155\u4e0d\u653e\uff0c\u7528\u529b\u4e00\u62d6\uff0c\u963f\u76d6\u534a\u8eab\u9178\u9ebb\uff0c\u4f7f\u4e0d\u4e0a\u52b2\uff0c\u5de6\u62f3\u75be\u51fa\u5982\u98ce\uff0c\u4e00 \u4e2a\u201c\u51b2\u5929\u70ae\u201d\u6253\u4ed6\u4e0b\u5df4\u3002 \u3000\u3000\u9633\u575a\u767d\u7ffb\u5230\u4e86\u963f\u76d6\u524d\u9762\uff0c\u4f7f\u51fa\u201c\u94c1\u677f\u6865\u201d\u529f\u592b\uff0c\u8fd9\u4e00\u4e3e\u6070\u6070\u4ece\u4ed6\u9762\u95e8\u6253\u4e86\u8fc7\u53bb\u3002\u8bf4\u65f6 \u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u6768\u5a49\u5df2\u662f\u4fa7\u7684\u4e00\u5251\u523a\u6765\uff0c\u9633\u575a\u767d\u903c\u5f97\u653e\u5f00\u963f\u76d6\uff0c\u659c\u8dc3\u4e09\u6b65\uff0c\u907f\u5f00\u6768\u5a49\u7684\u5251\u62db\u3002 \u3000\u3000\u9633\u575a\u767d\u7b11\u9053\uff1a\u201c\u6768\u59d1\u5a18\uff0c\u597d\u5251\u6cd5\uff01\u4f46\u53ef\u60dc\u4f60\u6c14\u529b\u4e0d\u8db3\uff0c\u6050\u6015\u8fd8\u662f\u6253\u4e0d\u8fc7\u6211\u7684\u3002\u4f60\u8fd9\u6837\u5982 \u82b1\u4f3c\u7389\u7684\u7f8e\u4eba\u513f\uff0c\u8981\u662f\u6211\u4e00\u4e2a\u4e0d\u5c0f\u5fc3\u8bef\u4f24\u4e86\u4f60\uff0c\u5012\u662f\u4e8e\u5fc3\u4e0d\u5fcd\u4e86\uff0c\u4e0d\u5982\u2026\u2026\u201d\u6768\u5a49\u65a5\u9053\uff1a \u201c\u6cb9\u5634\u6ed1\u820c\uff0c\u53c8\u6709\u4f55\u7528\uff01\u770b\u5251\uff01\u201d\u51fa\u5251\u5982\u7535\uff0c\u5c31\u5728\u9633\u575a\u767d\u8bf4\u8fd9\u51e0\u53e5\u8bdd\u7684\u5f53\u513f\uff0c\u5979\u5df2\u662f\u4e00\u53e3\u6c14 \u653b\u4e86\u5341\u4e03\u516b\u62db\uff0c\u5251\u5251\u6307\u5411\u5bf9\u65b9\u7684\u8981\u5bb3\u7a74\u9053\u3002 \u3000\u3000\u9633\u575a\u767d\u6321\u5230\u6700\u540e\u4e00\u62db\uff0c\u53ea\u542c\u5f97\u201c\u94db\u201d\u7684\u4e00\u58f0\uff0c\u53cc\u5251\u76f8\u4ea4\uff0c\u9633\u575a\u767d\u7684\u957f\u5251\u635f\u4e86\u4e00\u4e2a\u7f3a\u53e3\u3002 \u987b\u77e5\u6768\u5a49\u7528\u7684\u4e43\u662f\u4e00\u628a\u5b9d\u5251\uff0c\u6c14\u529b\u867d\u7136\u4e0d\u8db3\uff0c\u4f46\u7ed9\u5979\u7684\u5251\u78b0\u4e2a\u6b63\u7740\uff0c\u5bfb\u5e38\u7684\u5175\u5203\u51fa\u662f\u96be\u4ee5\u62b5 \u6321\u3002 \u3000\u3000\u9633\u575a\u767d\u5403\u4e86\u4e00\u60ca\uff0c\u767b\u65f6\u50cf\u7ed9\u4eba\u5c01\u4f4f\u4e86\u53e3\uff0c\u7b11\u4e0d\u51fa\u6765\uff0c\u4e5f\u987e\u4e0d\u5f97\u518d\u8bf4\u98ce\u51c9\u8bdd\u4e86\u3002\u6768\u5a49\u4e58\u80dc \u8ffd\u51fb\uff0c\u4e00\u5251\u523a\u4ed6\u54bd\u5589\u3002\u9633\u575a\u767d\u4e00\u62db\u201c\u6a2a\u4e91\u65ad\u5cf0\u201d\uff0c\u7528\u4e0a\u4e86\u4e5d\u5206\u529b\u9053\uff0c\u53ea\u542c\u5f97\u53c8\u662f\u201c\u94db\u201d\u7684\u4e00 \u58f0\uff0c\u9633\u575a\u767d\u7684\u957f\u5251\u53c8\u63bc\u4e86\u4e00\u4e2a\u7f3a\u53e3\u3002\u53ef\u662f\u8fd9\u4e00\u6b21\u5374\u4e0e\u4e0a\u6b21\u4e0d\u540c\u4e86\uff0c\u6768\u5a49\u7ed9\u4ed6\u8fd9\u80a1\u731b\u529b\u4e00\u9707\uff0c \u767b\u65f6\u864e\u53e3\u9178\u9ebb\uff0c\u624b\u4e2d\u7684\u5b9d\u5251\u51e0\u4e4e\u638c\u63e1\u4e0d\u7262\uff0c\u5927\u60ca\u4e4b\u4e0b\uff0c\u4e5f\u903c\u5f97\u75be\u9000\u6570\u6b65\uff01 \u3000\u3000\u9633\u575a\u767d\u80dc\u7b97\u5728\u63e1\uff0c\u53c8\u5f97\u610f\u8d77\u6765\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6768\u59d1\u5a18\uff0c\u4f60\u7eb5\u6709\u5b9d\u5251\uff0c\u4e5f\u662f\u96be\u5948\u6211\u4f55\uff0c\u4e0d \u4fe1\u4f60\u518d\u8bd5\u8bd5\u3002\u201d \u3000\u3000\u6768\u5a49\u77e5\u9053\u5bf9\u65b9\u7684\u529f\u529b\u8fdc\u80dc\u4e8e\u5df1\uff0c\u53ea\u51ed\u5b9d\u5251\u4e4b\u5229\uff0c\u4e0d\u80fd\u4e00\u4e0b\u5b50\u524a\u65ad\u4ed6\u7684\u5175\u5203\uff0c\u786c\u62fc\u786c\u6253\uff0c \u7ec8\u4e8e\u8fd8\u662f\u81ea\u5df1\u5403\u4e8f\u3002\u5f53\u4e0b\u7d27\u54ac\u94f6\u7259\uff0c\u6307\u4e1c\u6253\u897f\uff0c\u6307\u5357\u6253\u5317\uff0c\u628a\u4e00\u53e3\u5b9d\u5251\u4f7f\u5f97\u7075\u6d3b\u65e0\u6bd4\u3002\u9633\u575a \u767d\u6025\u5207\u4e4b\u95f4\u80dc\u5979\u4e0d\u5f97\uff0c\u53ea\u597d\u6682\u53d6\u5b88\u52bf\u3002 \u3000\u3000\u963f\u76d6\u4f4e\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u624b\u8155\u597d\u50cf\u7ed9\u94c1\u94b3\u94b3\u8fc7\u4e00\u6837\uff0c\u73b0\u51fa\u4e24\u6307\u8840\u75d5\u3002\u963f\u76d6\u5927\u6012\uff0c\u89e3\u4e0b\u56f4\u8170\u7684 \u957f\u7ef3\uff0c\u6325\u4e86\u4e00\u9053\u5706\u5f27\uff0c\u547c\u547c\u98ce\u54cd\uff0c\u5411\u9633\u575a\u767d\u5f53\u5934\u5957\u4e0b\u3002 \u3000\u3000\u8d3a\u4e5d\u516c\u4ee5\u4e3a\u963f\u76d6\u4e1a\u5df2\u53d7\u4f24\uff0c\u60f3\u6361\u73b0\u6210\u7684\u4fbf\u5b9c\uff0c\u6b64\u65f6\u4ea6\u5df2\u53d6\u51fa\u8f6f\u97ad\uff0c\u970d\u5730\u5411\u963f\u76d6\u4e0b\u4e09\u8def\u5377 \u6765\u3002 \u3000\u3000\u52ff\u5962\u4e5f\u6ca1\u95f2\u7740\uff0c\u4ed6\u7528\u7684\u662f\u4e00\u5bf9\u65e5\u6708\u8f6e\uff0c\u53cc\u8f6e\u9ad8\u4e3e\uff0c\u66ff\u9633\u575a\u767d\u6321\u7740\u963f\u76d6\u7684\u957f\u7ef3\u3002\u4ed6\u8fd9\u53e3\u5bf9 \u8f6e\u5b50\u4e43\u662f\u7f55\u89c1\u7684\u5916\u95e8\u5175\u5668\uff0c\u8f6e\u8f74\u6709\u5341\u4e8c\u652f\u5c16\u9510\u7684\u952f\u9f7f\uff0c\u963f\u76d6\u7684\u957f\u7ef3\u82e5\u662f\u7ed9\u4ed6\u9501\u4f4f\uff0c\u4e0d\u65ad\u4e5f\u8981 \u5927\u5927\u5403\u4e8f\uff0c\u597d\u4e2a\u963f\u76d6\uff0c\u7ef3\u5b50\u5c31\u50cf\u957f\u7740\u773c\u775b\u4f3c\u7684\uff0c\u534a\u7a7a\u4e2d\u547c\u7684\u8f6c\u4e86\u4e00\u5708\uff0c\u6070\u6070\u62e6\u7740\u4e86\u8d3a\u4e5d\u516c\u7684 \u8f6f\u97ad\u3002 \u3000\u3000\u7ef3\u97ad\u76f8\u4e92\u7ea0\u7f20\uff0c\u7ef3\u957f\u97ad\u77ed\uff0c\u7ef3\u5b50\u7684\u4e00\u7aef\u6709\u4e00\u6bb5\u5782\u4e0b\uff0c\u963f\u76d6\u7528\u529b\u4e00\u6325\uff0c\u90a3\u6bb5\u7ef3\u5b50\u7a81\u7136\u8c61\u86c7 \u5934\u6602\u8d77\uff0c\u53ea\u542c\u5f97\u201c\u556a\u201d\u7684\u4e00\u58f0\u54cd\uff0c\u8d3a\u4e5d\u516c\u7684\u811a\u9aa8\u7ed9\u6253\u4e2a\u6b63\u7740\u3002 \u3000\u3000\u8d3a\u4e5d\u516c\u7528\u6b62\u4e86\u5343\u65a4\u5760\u7684\u91cd\u8eab\u6cd5\uff0c\u672c\u6765\u60f3\u4ed7\u7740\u6df1\u539a\u7684\u5185\u529f\u628a\u963f\u76d6\u62d6\u5012\u7684\uff0c\u4e0d\u6599\u53cd\u800c\u7740\u4e86\u963f \u76d6\u7684\u9053\u513f\u3002\u8fd9\u4e00\u4e0b\u6253\u5f97\u4ed6\u75db\u5f7b\u5fc3\u80ba\uff0c\u5343\u65a4\u5760\u7684\u91cd\u8eab\u6cd5\u767b\u65f6\u7834\u4e86\u3002\u8eab\u4e0d\u7531\u5df1\u7684\u4fbf\u5411\u524d\u503e\uff0c\u53cd\u800c \u8981\u7ed9\u963f\u76d6\u62d6\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u8d3a\u5a46\u5a46\u89c1\u4e08\u592b\u4e0d\u5229\uff0c\u5927\u5403\u4e00\u60ca\uff0c\u9f99\u5934\u62d0\u6756\u6025\u5fd9\u4f38\u51fa\uff0c\u5411\u7740\u957f\u7ef3\u7684\u4e2d\u90e8\u4e00\u6311\uff0c\u963f\u76d6\u7684\u957f\u7ef3 \u6b64\u65f6\u5df2\u662f\u7ef7\u7d27\u5f97\u597d\u4f3c\u5f13\u5f26\uff0c\u8d3a\u5a46\u5a46\u7684\u9f99\u5934\u62d0\u6756\u6311\u4e86\u4e0a\u53bb\uff0c\u7adf\u7136\u7ed9\u5b83\u5f39\u5f00\uff0c\u9707\u5f97\u864e\u53e3\u53d1\u70ed\uff01\u4f46 \u4e5f\u5e78\u4e8f\u6709\u5979\u76f8\u52a9\uff0c\u8f6c\u79fb\u4e86\u963f\u5b5f\u7684\u529b\u9053\uff0c\u8d3a\u4e5d\u516c\u7684\u8f6f\u97ad\u65b9\u624d\u80fd\u591f\u89e3\u5f00\uff0c\u8eb2\u8fc7\u4e00\u8fb9\uff0c\u7ad9\u7a33\u4e86\u811a \u6b65\u3002 \u3000\u3000\u539f\u6765\u963f\u76d6\u867d\u7136\u6ca1\u6709\u7ec3\u8fc7\u5185\u529f\uff0c\u4f46\u5374\u662f\u5929\u751f\u7684\u795e\u529b\u3002\u4ed6\u5728\u6df1\u5c71\u4e2d\u4ee5\u6253\u730e\u4e3a\u751f\uff0c\u7528\u7ef3\u5b50\u6355\u6349 \u731b\u517d\u6b63\u662f\u4ed6\u7684\u62ff\u624b\u7edd\u6280\uff0c\u8fd9\u6761\u7ef3\u5b50\u662f\u63c9\u5408\u4e86\u725b\u7b4b\u6413\u6210\u7684\uff0c\u7b49\u4e8e\u8f6f\u97ad\u4e00\u6837\u3002\u4ed6\u81ea\u5df1\u609f\u51fa\u6765\u7684\u4e00 \u5957\u4f7f\u7528\u957f\u7ef3\u7684\u529f\u592b\uff0c\u5176\u5b9e\u4e5f\u5c31\u662f\u4e00\u5957\u5947\u5999\u7684\u97ad\u6cd5\uff0c\u53ea\u6709\u5728\u8d3a\u4e5d\u516c\u7684\u97ad\u6cd5\u4e4b\u4e0a\uff0c\u51b3\u4e0d\u5728\u4ed6\u4e4b \u4e0b\u3002 \u3000\u3000\u52ff\u5962\u5c06\u65e5\u6708\u53cc\u8f6e\u76d8\u65cb\u98de\u821e\uff0c\u4e0a\u524d\u9501\u62ff\u963f\u76d6\u7684\u957f\u7ef3\uff0c\u52ff\u5962\u6df1\u6089\u963f\u76d6\u7684\u529f\u592b\uff0c\u4ed6\u7684\u65e5\u6708\u53cc\u8f6e \u6b63\u662f\u53ef\u4ee5\u514b\u5236\u957f\u7ef3\u7684\u5175\u5668\uff0c\u963f\u76d6\u51b7\u7b11\u9053\uff1a\u201c\u6789\u4f60\u8eab\u4e3a\u91d1\u5e10\u6b66\u58eb\uff0c\u4ee5\u591a\u4e3a\u80dc\uff0c\u7b97\u5f97\u4ec0\u4e48\u597d \u6c49\uff01\u201d\u52ff\u5962\u9053\uff1a\u201c\u6211\u662f\u6349\u62ff\u53db\u9006\uff0c\u8c01\u548c\u4f60\u8bb2\u7a76\u4ec0\u4e48\u6b66\u58eb\u7684\u89c4\u77e9\uff1f\u201d \u3000\u3000\u963f\u76d6\u6012\u9053\uff1a\u201c\u597d\uff0c\u4f60\u4eec\u5c31\u662f\u501a\u591a\u4e3a\u80dc\uff0c\u6211\u4ea6\u4f55\u60e7\uff01\u201d\u957f\u7ef3\u6325\u52a8\uff0c\u77eb\u82e5\u6e38\u9f99\uff0c\u6216\u5706\u6216\u76f4\uff0c \u6253\u6210\u5708\u4e4b\u65f6\uff0c\u4fe8\u7136\u662f\u91d1\u94a2\u5708\u7684\u7528\u6cd5\uff0c\u6296\u76f4\u4e4b\u65f6\uff0c\u53c8\u53ef\u5f53\u4f5c\u8f6f\u97ad\u7f20\u626b\uff0c\u53d8\u5e7b\u65e0\u65b9\uff0c\u4ee5\u4e00\u654c\u4e09\uff0c \u5140\u662f\u653b\u591a\u5b88\u5c11\uff01 \u3000\u3000\u957f\u7ef3\u53ef\u4ee5\u6253\u5230\u4e09\u4e08\u5f00\u5916\uff0c\u5728\u963f\u76d6\u795e\u529b\u6325\u821e\u4e4b\u4e0b\uff0c\u5708\u5b50\u6e10\u6e10\u6269\u5927\uff0c\u52ff\u5962\u7b49\u4eba\u51e0\u4e4e\u7ed9\u4ed6\u903c\u5f97 \u5728\u5e99\u4e2d\u7acb\u8db3\u4e0d\u4f4f\u3002\u52ff\u5962\u5012\u5438\u4e86\u4e00\u53e3\u51c9\u6c14\uff0c\u5fc3\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u9547\u56fd\u738b\u5b50\u5bf9\u4ed6\u4e5f\u6709\u51e0\u5206\u987e\u5fcc\uff0c\u66fe\u7ecf \u60f3\u8981\u7b3c\u7edc\u4ed6\uff0c\u4e0d\u6562\u62a2\u4ed6\u7684\u5361\u6d1b\u4e1d\u3002\u8fd9\u53ae\u7684\u6b66\u5b66\u65e0\u5e08\u81ea\u901a\uff0c\u786e\u5b9e\u7b97\u5f97\u662f\u4e2a\u5947\u6750\u5f02\u80fd\u4e4b\u58eb\u3002\u6211\u53ea \u9053\u53ef\u4ee5\u5360\u5f97\u5175\u5668\u7684\u4fbf\u5b9c\uff0c\u4e0d\u6599\u65e5\u6708\u53cc\u8f6e\u4e5f\u514b\u5236\u4ed6\u7684\u957f\u7ef3\u4e0d\u4f4f\u3002\u201d \u3000\u3000\u9633\u575a\u767d\u7684\u672c\u9886\u8fdc\u5728\u52ff\u5962\u7b49\u4eba\u4e4b\u4e0a\uff0c\u4ed6\u4e0e\u6768\u5a49\u6597\u5251\uff0c\u6b64\u65f6\u4e1a\u5df2\u9887\u5360\u4e0a\u98ce\u3002\u4f46\u7ed9\u963f\u76d6\u7684\u957f\u7ef3 \u5a01\u80c1\uff0c\u9519\u8fc7\u4e86\u8bb8\u591a\u53ef\u4ee5\u53d6\u80dc\u7684\u673a\u4f1a\u3002 \u3000\u3000\u9633\u575a\u767d\u54fc\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u5c71\u6c9f\u91cc\u7684\u628a\u5f0f\u4e5f\u6562\u901e\u80fd\u3002\u8d3a\u8001\u4e0d\u5fc5\u60ca\u614c\uff0c\u6211\u7ed9\u4f60\u6253\u53d1\u8fd9\u53ae\uff0c \u7a81\u7136\u4e00\u8dc3\u800c\u4e0a\uff0c\u95ea\u7535\u822c\u7684\u4e00\u5251\u524a\u53bb\u3002\u963f\u76d6\u957f\u7ef3\u4e00\u5708\uff0c\u5374\u662f\u4e0d\u5982\u4ed6\u5feb\uff0c\u5251\u5149\u8fc7\u5904\uff0c\u7ef3\u5b50\u77ed\u4e86\u4e00 \u622a\u3002\u6768\u5a49\u4e00\u62db\u201c\u7389\u5973\u6295\u68ad\u201d\uff0c\u653b\u654c\u4e4b\u6240\u5fc5\u6551\uff0c\u9633\u575a\u767d\u8f6c\u8fc7\u8eab\u6765\uff0c\u521a\u597d\u53c8\u63a5\u4e0a\u4e86\u6768\u5a49\u7684\u5251\u62db\u3002 \u3000\u3000\u963f\u76d6\u5929\u751f\u795e\u529b\uff0c\u53ef\u60dc\u6ca1\u6709\u5b66\u8fc7\u4e0a\u4e58\u7684\u5185\u529f\uff0c\u5bf9\u4e8e\u8fd0\u6c14\u4f7f\u529b\u7684\u6cd5\u95e8\uff0c\u81ea\u662f\u4e0d\u53ca\u9633\u575a\u767d\u90a3\u4e48 \u9ad8\u660e\uff0c\u9633\u575a\u767d\u5728\u6fc0\u6597\u4e2d\u4e58\u6687\u62b5\u9699\uff0c\u4e00\u6709\u673a\u4f1a\uff0c\u5c31\u8dc3\u51fa\u5708\u5b50\u524a\u963f\u76d6\u7684\u957f\u7ef3\u3002\u4ed6\u7528\u7684\u53ea\u662f\u4e00\u628a\u666e \u901a\u7684\u9752\u94a2\u5251\uff0c\u4f46\u7531\u4e8e\u5185\u52b2\u8d2f\u6ce8\u5251\u5c16\uff0c\u5c45\u7136\u4e5f\u80fd\u65ad\u91d1\u622a\u94c1\u3002\u963f\u76d6\u7684\u957f\u7ef3\u7ed9\u4ed6\u4e00\u6bb5\u6bb5\u7684\u524a\u6389\uff0c\u4e0d \u5230\u4e00\u6ce8\u9999\u7684\u65f6\u523b\uff0c\u4e8c\u4e08\u591a\u957f\u7684\u7ef3\u5b50\u5269\u4e0b\u6765\u7684\u53ea\u6709\u516d\u4e03\u5c3a\u957f\u4e86\u3002 \u3000\u3000\u6768\u5a49\u60c5\u77e5\u963f\u76d6\u82e5\u6709\u4e0d\u6d4b\uff0c\u81ea\u5df1\u66f4\u662f\u72ec\u529b\u96be\u652f\uff0c\u5f53\u4e0b\u8c41\u4e86\u6027\u547d\uff0c\u6025\u6025\u62a2\u653b\uff0c\u963b\u6b62\u9633\u575a\u767d\u518d \u524a\u963f\u76d6\u7684\u7ef3\u5b50\u3002\u6768\u5a49\u7684\u5251\u6cd5\u672c\u4ee5\u8f7b\u7075\u8fc5\u6377\u89c1\u957f\uff0c\u53ef\u662f\u7531\u4e8e\u8fde\u756a\u6076\u6597\uff0c\u6c14\u529b\u4e0d\u52a0\uff0c\u6b64\u9645\u5df2\u662f\u5c06 \u8fd1\u6cb9\u5c3d\u706f\u67af\u7684\u5883\u5730\uff0c\u51fa\u5251\u53cd\u800c\u4e0d\u53ca\u9633\u575a\u767d\u4e4b\u5feb\u4e86\u3002 \u3000\u3000\u9633\u575a\u767d\u53c8\u524a\u65ad\u4e86\u963f\u76d6\u7684\u4e24\u6bb5\u7ef3\u5b50\uff0c\u80dc\u7b97\u7a33\u64cd\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6768\u59d1\u5a18\uff0c\u53ea\u8981\u4f60\u987a\u4ece\u6211\uff0c\u6211 \u4e0d\u4f1a\u4e3a\u96be\u4f60\u7684\u3002\u8fd9\u4e2a\u86ee\u5b50\u4e0e\u4f60\u975e\u4eb2\u975e\u6545\uff0c\u4f60\u53c8\u4f55\u5fc5\u7406\u4f1a\u4ed6\u5462\u3002\u563f\uff0c\u563f\uff0c\u4e5d\u516c\uff0c\u4f60\u548c\u52ff\u5962\u5927\u7ea6 \u4e0d\u7528\u6211\u518d\u5e2e\u5fd9\u4e86\u5427\uff1f\u201d \u3000\u3000\u52ff\u5962\u539a\u7740\u8138\u76ae\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u91ce\u4eba\u5982\u4eca\u5df2\u662f\u91dc\u5e95\u4e4b\u9c7c\uff0c\u74ee\u4e2d\u4e4b\u9ccc\uff0c\u6211\u4eec\u81ea\u4f1a\u624b\u5230\u64d2\u6765\uff0c\u4e0d \u7528\u9633\u516c\u5b50\u8d39\u795e\u4e86\u3002\u9633\u516c\u5b50\uff0c\u4f60\u8fd8\u662f\u7528\u70b9\u5fc3\u673a\uff0c\u628a\u8fd9\u5982\u82b1\u4f3c\u7389\u7684\u7f8e\u4eba\u513f\u64d2\u5230\u624b\u5427\u3002\u201d \u3000\u3000\u9633\u575a\u767d\u8981\u80dc\u6768\u5a49\u5df2\u662f\u4e0d\u96be\uff0c\u4e3a\u96be\u7684\u662f\u600e\u6837\u624d\u80fd\u4e0d\u81f4\u4ee4\u5979\u53d7\u4f24\u5c06\u5979\u6d3b\u6349\u3002\u52ff\u5962\u6b63\u8bf4\u4e2d\u4e86\u4ed6 \u7684\u5fc3\u601d\u3002\u9633\u575a\u767d\u5927\u7b11\u9053\uff1a\u201c\u4f60\u4e5f\u591a\u7528\u70b9\u5fc3\u673a\u5427\uff0c\u6253\u53d1\u4e86\u8fd9\u4e2a\u86ee\u5b50\uff0c\u4f60\u4eec\u8349\u539f\u4e0a\u7684\u7b2c\u4e00\u7f8e\u4eba\uff0c \u4e5f\u662f\u5c5e\u4e8e\u4f60\u4e86\u3002\u54b1\u4eec\u4e00\u4eba\u4e00\u4e2a\uff0c\u5c82\u4e0d\u7f8e\u54c9\uff01\u201d \u3000\u3000\u963f\u76d6\u5e73\u751f\u6700\u656c\u7231\u7684\u4eba\u5c31\u662f\u4ed6\u7684\u59bb\u5b50\u5361\u6d1b\u4e1d\uff0c\u542c\u4e86\u4ed6\u4eec\u8f7b\u8584\u7684\u8a00\u8bed\uff0c\u6c14\u5f97\u80ba\u90fd\u51e0\u4e4e\u70b8\u4e86\u3002 \u53ef\u662f\u4ed6\u7684\u957f\u7ef3\u6b64\u65f6\u5df2\u662f\u53ea\u5269\u4e0b\u4e94\u516d\u5c3a\u957f\uff0c\u53ea\u80fd\u5f53\u4f5c\u77ed\u97ad\u4f7f\u7528\uff0c\u539f\u6709\u7684\u5a01\u529b\u5927\u5927\u51cf\u5f31\uff0c\u8bb8\u591a\u72ec \u521b\u7684\u7cbe\u5999\u62db\u6570\uff0c\u51ed\u7740\u8fd9\u4e48\u4e00\u6761\u77ed\u77ed\u7684\u7ef3\u5b50\uff0c\u4ea6\u5df2\u4e0d\u80fd\u518d\u4f7f\u51fa\u6765\u3002\u4f46\u963f\u76d6\u8fd8\u662f\u54ac\u7d27\u7259\u6839\uff0c\u62fc\u6b7b \u6076\u6597\u3002 \u3000\u3000\u8d3a\u4e5d\u516c\u7684\u8f6f\u97ad\u6070\u4f3c\u6bd2\u86c7\u5410\u4fe1\uff0c\u5ffd\u4f38\u5ffd\u7f29\uff0c\u4e13\u95e8\u88ad\u51fb\u963f\u76d6\u7684\u4e0b\u76d8\uff1b\u8d3a\u5a46\u5a46\u7684\u9f99\u5934\u62d0\u6756\u5219\u5982 \u51fa\u6d77\u86df\u9f99\uff0c\u6363\u5411\u963f\u76d6\u4e2d\u8def\uff1b\u52ff\u5962\u4e5f\u4e0d\u793a\u5f31\uff0c\u4e00\u5bf9\u65e5\u6708\u8f6e\u5316\u4f5c\u4e86\u4e24\u5708\u94f6\u5149\uff0c\u7f69\u7740\u963f\u76d6\u7684\u4e0a\u8eab\u3002 \u8fd9\u4e09\u4eba\u521a\u624d\u7ed9\u963f\u76d6\u6253\u5f97\u72fc\u72c8\u4e0d\u582a\uff0c\u6b64\u9645\u5f97\u4e86\u9633\u575a\u767d\u4e4b\u52a9\uff0c\u80dc\u5229\u5728\u671b\uff0c\u5927\u5bb6\u90fd\u662f\u4e89\u7740\u62a2\u653b\u3002 \u3000\u3000\u963f\u76d6\u4e0d\u4f1a\u4f7f\u529b\uff0c\u4e00\u4e0a\u6765\u5c31\u662f\u731b\u653b\u731b\u6253\uff0c\u7ef3\u5b50\u524a\u65ad\u4e4b\u540e\uff0c\u54ac\u7259\u82e6\u6218\uff0c\u7528\u529b\u66f4\u591a\u3002\u4ed6\u867d\u662f\u5929 \u751f\u795e\u529b\uff0c\u4e16\u7981\u4e0d\u8d77\u592a\u591a\u6d88\u8017\uff0c\u5728\u6b64\u6d88\u5f7c\u957f\u7684\u60c5\u5f62\u4e4b\u4e0b\uff0c\u963f\u76d6\u6e10\u6e10\u5730\u4fbf\u89c9\u5f97\u529b\u4e0d\u4ece\u5fc3\uff0c\u7ed9\u5bf9\u65b9 \u56f4\u653b\u5f97\u53ea\u6709\u62db\u67b6\u7684\u4efd\u513f\u3002 \u3000\u3000\u6768\u5a49\u60c5\u5f62\u66f4\u7cdf\uff0c\u8fde\u62db\u67b6\u90fd\u611f\u4e3a\u96be\u3002\u5e78\u800c\u9633\u575a\u767d\u60f3\u8981\u628a\u5979\u6d3b\u6349\uff0c\u5bf9\u5979\u8c41\u4e86\u6027\u547d\u7684\u6253\u6cd5\uff0c\u8fd8 \u5f53\u771f\u6709\u70b9\u987e\u5fcc\u3002 \u3000\u3000\u6768\u5a49\u5fc3\u60f3\uff1a\u201c\u6211\u7edd\u4e0d\u80fd\u843d\u5728\u6b64\u4eba\u624b\u4e0a\uff01\u201d\u6b63\u60f3\u56de\u5251\u81ea\u5c3d\uff0c\u5ffd\u542c\u5f97\u9633\u575a\u767d\u559d\u9053\uff1a\u201c\u4ec0\u4e48 \u4eba\uff1f\u201d\u4e0e\u6b64\u540c\u65f6\uff0c\u52ff\u5962\u5374\u201c\u54a6\u201d\u7684\u4e00\u58f0\u53eb\u4e86\u8d77\u6765\uff01 \u3000\u3000\u9633\u575a\u767d\u4f3c\u4e4e\u597d\u751f\u8be7\u5f02\uff0c\u6a2a\u5251\u4e00\u5c01\uff0c\u628a\u6768\u5a49\u903c\u9000\u51e0\u6b65\uff0c\u56de\u8fc7\u5934\u6765\uff0c\u6ce8\u89c6\u90a3\u4eba\u7684\u52a8\u9759\u3002 \u3000\u3000\u6768\u5a49\u672c\u6765\u60f3\u8981\u81ea\u520e\u7684\uff0c\u6b64\u65f6\u770b\u89c1\u6709\u4eba\u8fdb\u6765\uff0c\u4e0d\u5982\u662f\u53cb\u662f\u654c\uff0c\u5f53\u7136\u662f\u8981\u770b\u6e05\u695a\u518d\u8bf4\u4e86\u3002 \u3000\u3000\u53ea\u89c1\u90a3\u4eba\u9762\u90e8\u8499\u7740\u9ed1\u5dfe\uff0c\u53ea\u9732\u51fa\u4e00\u5bf9\u9ed1\u6f06\u5149\u4eae\u7684\u773c\u73e0\uff0c\u6e38\u76ee\u56db\u987e\uff0c\u7f13\u7f13\u5730\u5411\u52ff\u5962\u8d70\u53bb\uff0c \u5374\u4e00\u53e5\u8bdd\u4e5f\u6ca1\u6709\u8bf4\u3002 \u3000\u3000\u6768\u5a49\u8fd9\u4e00\u60ca\u6bd4\u9633\u575a\u767d\u66f4\u751a\uff0c\u539f\u6765\u8fd9\u4e2a\u8499\u9762\u4eba\u5979\u4e5f\u662f\u66fe\u7ecf\u89c1\u8fc7\u7684\u3002 \u3000\u3000\u90a3\u665a\u6768\u5a49\u8eb2\u5728\u677e\u6797\u4e4b\u4e2d\uff0c\u770b\u89c1\u674e\u601d\u5357\u7528\u91cd\u624b\u6cd5\u70b9\u4e86\u52ff\u5962\u7684\u7a74\u9053\uff0c\u628a\u52ff\u5962\u629b\u5728\u8349\u5806\u91cc\uff0c\u4fbf \u5306\u5306\u8fdb\u53bb\u76f8\u52a9\u5b5f\u660e\u971e\u4e86\u3002 \u3000\u3000\u6768\u5a49\u672c\u6765\u60f3\u5077\u6109\u8ddf\u53bb\u770b\u7684\uff0c\u5c31\u5728\u6b64\u65f6\uff0c\u51fa\u73b0\u4e86\u4e00\u4e2a\u8499\u9762\u4eba\uff0c\u5413\u5f97\u6768\u5a49\u4e0d\u6562\u52a8\u5f39\u3002\u56e0\u4e3a\u5979 \u4e0d\u613f\u610f\u81ea\u5df1\u7684\u884c\u85cf\u7ed9\u4eba\u63ed\u7834\u3002 \u3000\u3000\u8fd9\u8499\u9762\u4eba\u628a\u52ff\u5962\u4ece\u8349\u5806\u91cc\u62c9\u51fa\u6765\uff0c\u8f6c\u77ac\u4e4b\u95f4\u4fbf\u7ed9\u4ed6\u89e3\u4e86\u7a74\u9053\u3002\u674e\u601d\u5357\u7684\u72ec\u95e8\u70b9\u7a74\u624b\u6cd5\u7adf \u7136\u7ed9\u4ed6\u4e0d\u5047\u601d\u7d22\u5730\u4fbf\u89e3\u5f00\uff0c\u4f7f\u5f97\u6768\u5a49\u60ca\u5947\u4e0d\u5df2\u3002 \u3000\u3000\u66f4\u5947\u602a\u7684\u662f\uff0c\u8499\u9762\u4eba\u89e3\u5f00\u4e86\u52ff\u5962\u7684\u7a74\u9053\u4e4b\u540e\uff0c\u7adf\u7136\u4e0d\u7b49\u52ff\u5962\u5411\u4ed6\u9053\u8c22\uff0c\u7a81\u7136\u50cf\u9b3c\u5f71\u4e00\u822c \u7684\u53c8\u6d88\u5931\u4e86\u3002 \u3000\u3000\u5176\u540e\u674e\u601d\u5357\u4e0e\u5b5f\u660e\u971e\u8054\u624b\u6253\u8d25\u4e86\u9633\u575a\u767d\uff0c\u4e00\u540c\u56de\u6765\uff0c\u53d1\u73b0\u52ff\u5962\u5df2\u7ecf\u9003\u53bb\uff0c\u4e24\u4eba\u90fd\u662f\u5927\u4e3a \u8be7\u5f02\uff0c\u4e0d\u77e5\u52ff\u5962\u662f\u81ea\u884c\u89e3\u7a74\uff0c\u8fd8\u662f\u53e6\u5916\u5c1a\u6709\u6b66\u529f\u6781\u9ad8\u7684\u515a\u7fbd\u3002 \u3000\u3000\u8fd9\u8c1c\u5e95\u53ea\u6709\u6768\u5a49\u77e5\u9053\u3002\u4f46\u4e5f\u4e0d\u662f\u5b8c\u5168\u660e\u767d\uff0c\u56e0\u4e3a\u5979\u4e5f\u6349\u6478\u4e0d\u5b9a\u8fd9\u4e2a\u8499\u9762\u4eba\u548c\u52ff\u5962\u7a76\u7adf\u662f \u4ec0\u4e48\u5173\u7cfb\u3002\u5982\u679c\u662f\u540c\u515a\u7684\u8bdd\uff0c\u4ed6\u4e0d\u8be5\u4e0d\u53d1\u4e00\u8a00\u4fbf\u5373\u79bb\u5f00\u7684\u3002 \u3000\u3000\u4f46\u65e0\u8bba\u5982\u4f55\uff0c\u4ed6\u603b\u662f\u52ff\u5962\u7684\u6551\u547d\u6069\u4eba\uff0c\u5373\u4f7f\u4e0d\u662f\u540c\u515a\uff0c\u4e5f\u5fc5\u662f\u540c\u4e00\u6761\u8def\u4e0a\u7684\u4eba\u4e86\u3002 \u3000\u3000\u800c\u73b0\u5728\u8fdb\u6765\u7684\u8fd9\u4e2a\u8499\u9762\u4eba\uff0c\u4e5f\u6b63\u662f\u6768\u5a49\u90a3\u665a\u6240\u89c1\u7684\u90a3\u4e2a\u8499\u9762\u4eba\u3002\u867d\u7136\u5728\u6c5f\u6e56\u4e0a\u51fa\u73b0\u7684\u591c \u884c\u4eba\u4e3a\u4e86\u4e0d\u613f\u8ba9\u4eba\u8ba4\u51fa\u5e90\u5c71\u771f\u76f8\uff0c\u4e5f\u5e38\u6709\u8499\u7740\u9762\u5dfe\u7684\uff0c\u4f46\u540c\u6837\u7684\u88c5\u675f\uff0c\u540c\u6837\u7684\u8eab\u6750\uff0c\u66f4\u52a0\u4e0a \u8fd9\u4e00\u5bf9\u5177\u6709\u7279\u5f81\u7684\u70af\u70af\u6709\u795e\u7684\u5927\u773c\u775b\uff0c\u6768\u5a49\u4e00\u89c1\u4fbf\u77e5\u9053\u662f\u540c\u4e00\u4e2a\u4eba\u65e0\u7591\u3002 \u3000\u3000\u6768\u5a49\u672c\u6765\u5b58\u7740\u4e07\u4e00\u7684\u5e0c\u671b\uff0c\u5e0c\u671b\u6765\u7684\u662f\u4fa0\u4e49\u9053\uff0c\u4e0d\u6599\u6765\u7684\u53c8\u662f\u4e00\u4e2a\u654c\u4eba\u3002\u6768\u5a49\u4e0d\u7531\u5f97\u5927 \u5931\u6240\u671b\uff0c\u51c9\u900f\u5fc3\uff0c\u6697\u53eb\u7cdf\u7cd5\u3002 \u3000\u3000\u5fc3\u5ff5\u672a\u5df2\uff0c\u53ea\u542c\u5f97\u52ff\u5962\u5df2\u5728\u5927\u559c\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u90a3\u665a\u7ed9\u6211\u89e3\u7a74\u4e4b\u4eba\u5417\uff1f\u6069\u516c\uff0c\u6211\u6b63\u60f3\u627e \u4f60\uff01\u201d \u3000\u3000\u90a3\u8499\u9762\u4eba\u6da9\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u7ed9\u6211\u6eda\u5f00\uff01\u201d \u3000\u3000\u6b64\u8a00\u4e00\u51fa\uff0c\u4e24\u8fb9\u7684\u4eba\u90fd\u662f\u5927\u5403\u4e00\u60ca\u3002\u52ff\u5962\u8be7\u9053\uff1a\u201c\u4f60\u3001\u4f60\u6551\u4e86\u6211\u7684\u547d\uff0c\u4f55\u4ee5\uff0c\u4f55\u4ee5\u5982\u4eca \u2014\u2014\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u90a3\u4eba\u84e6\u5730\u628a\u52ff\u5962\u4e00\u628a\u6293\u4e86\u8d77\u6765\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u81ea\u5df1\u6eda\u5f00\uff0c\u8981\u6211\u52a8\u624b \u5417\uff1f\u201d\u5927\u559d\u4e00\u58f0\uff0c\u5c31\u50cf\u629b\u63b7\u76ae\u7403\u4e00\u6837\uff0c\u5c06\u52ff\u5962\u629b\u51fa\u95e8\u5916\uff01\u8fd9\u4e00\u62db\u6b63\u662f\u6781\u5389\u5bb3\u7684\u5927\u6367\u7891\u624b\u529f \u592b\u3002 \u3000\u3000\u8fd9\u4e00\u7a81\u5982\u5176\u6765\u7684\u53d8\u5316\u5927\u51fa\u4f17\u4eba\u610f\u6599\u4e4b\u5916\uff0c\u8d3a\u4e5d\u516c\u592b\u59bb\u7ad9\u5728\u52ff\u5962\u4e24\u65c1\uff0c\u867d\u7136\u8fd1\u5728\u54ab\u5c3a\uff0c\u4ea6 \u662f\u4e0d\u53ca\u51fa\u624b\u76f8\u52a9\u3002\u8d3a\u4e5d\u516c\u5927\u5403\u4e00\u60ca\uff0c\u559d\u9053\uff1a\u201c\u54ea\u91cc\u6765\u7684\u5c0f\u5b50\uff0c\u80c6\u6562\u4f24\u5bb3\u8d35\u4eba\uff01\u201d\u547c\u7684\u4e00\u97ad\u6a2a \u626b\u8fc7\u53bb\u3002 \u3000\u3000\u8fd9\u4e00\u62db\u201c\u56de\u98ce\u626b\u67f3\u201d\u8fde\u73af\u4e09\u97ad\u672c\u662f\u8d3a\u4e5d\u516c\u751f\u5e73\u5f97\u610f\u7684\u7edd\u6280\uff0c\u54ea\u77e5\u8499\u9762\u4eba\u7adf\u7136\u4e0d\u95ea\u4e0d\u8eb2\uff0c \u97ad\u5f71\u5377\u5730\u800c\u6765\uff0c\u53ea\u89c1\u4ed6\u4e00\u4e2a\u201c\u6e38\u7a7a\u63c9\u722a\u201d\u53cc\u6307\u4e00\u94b3\uff0c\u8d5b\u5982\u5229\u526a\uff0c\u201c\u5494\u5693\u201d\u4e00\u58f0\uff0c\u8d3a\u4e5d\u516c\u8fd9\u6761 \u8f6f\u97ad\u5df2\u662f\u65ad\u4e3a\u4e24\u622a\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u8d3a\u5a46\u5a46\u7684\u9f99\u5934\u62d0\u6756\u65e9\u5df2\u5288\u9762\u6253\u6765\uff0c\u8499\u9762\u4eba\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u8fd9\u5bf9\u65e0\u803b\u7684 \u8d3c\u592b\u59bb\uff0c\u4e5f\u90fd\u7ed9\u6211\u6eda\u5427\uff01\u201d\u51fa\u624b\u5982\u7535\uff0c\u8d3a\u5a46\u5a46\u8fde\u4ed6\u7528\u7684\u662f\u4ec0\u4e48\u624b\u6cd5\u90fd\u672a\u66fe\u770b\u5f97\u6e05\u695a\uff0c\u53ea\u89c9\u864e \u53e3\u4e00\u9ebb\uff0c\u9f99\u5934\u62d0\u6756\u5df2\u662f\u7ed9\u4ed6\u5288\u624b\u593a\u53bb\uff01\u8499\u9762\u4eba\u4e00\u58f0\u5927\u559d\uff0c\u53ea\u89c1\u90a3\u6839\u62d0\u6756\u63d2\u8fdb\u571f\u91cc\uff0c\u6ca1\u81f3\u6756 \u68a2\uff0c\u8d3a\u4e5d\u516c\u592b\u59bb\u5413\u5f97\u9b42\u98de\u9b44\u6563\uff0c\u6ca1\u547d\u98de\u5954\u3002 \u3000\u3000\u963f\u76d6\u6536\u8d77\u4e86\u534a\u622a\u7ef3\u7d22\uff0c\u8d5e\u9053\uff1a\u201c\u597d\u6c49\u5b50\uff01\u201d\u90a3\u8499\u9762\u4eba\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u80fd\u591f\u4ee5\u4e00\u654c \u4e09\uff0c\u4e5f\u662f\u4e00\u6761\u597d\u6c49\u5b50\u554a\uff01\u201d \u3000\u3000\u8fd9\u8499\u9762\u4eba\u5728\u7247\u523b\u4e4b\u95f4\uff0c\u6454\u4eba\u3001\u65ad\u97ad\u3001\u593a\u6756\uff0c\u8fde\u8d25\u4e09\u4e2a\u597d\u624b\uff0c\u6b66\u529f\u4e4b\u5f3a\uff0c\u4ee4\u5f97\u9633\u575a\u767d\u4e5f\u4e0d \u7981\u6709\u70b9\u5403\u60ca\u3002 \u3000\u3000\u4f46\u6b64\u65f6\u6768\u5a49\u4e0e\u963f\u76d6\u90fd\u5df2\u662f\u5f3a\u5f29\u4e4b\u672a\uff0c\u9633\u575a\u767d\u53ea\u8981\u80fd\u591f\u6253\u8d25\u8fd9\u4e2a\u8499\u9762\u4eba\uff0c\u4ecd\u662f\u53ef\u4ee5\u64cd\u7eb5\u5168 \u5c40\uff0c\u662f\u4ee5\u4ed6\u867d\u7136\u6709\u4e00\u70b9\u5403\u60ca\uff0c\u4ecd\u662f\u6b32\u56fe\u4e00\u535a\u3002 \u3000\u3000\u8499\u9762\u4eba\u56de\u8fc7\u5934\u6765\uff0c\u4e00\u53cc\u70af\u70af\u6709\u795e\u7684\u773c\u775b\u76ef\u7740\u9633\u575a\u767d\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u505a\u4e0b\u8fd9\u7b49\u4e0b\u4e09\u6ee5\u7684\u52fe \u5f53\uff0c\u4e0d\u6015\u73b7\u8fb1\u5e08\u95e8\u5417\uff1f\u201d\u8bdd\u4e2d\u4e4b\u610f\uff0c\u663e\u7136\u5df1\u662f\u77e5\u9053\u9633\u575a\u767d\u505a\u4e86\u91c7\u82b1\u8d3c\u7684\u4e8b\u60c5\u3002 \u3000\u3000\u9633\u575a\u767d\u5fc3\u5934\u4e00\u9707\uff0c\u559d\u9053\uff1a\u201c\u4f60\u5230\u5e95\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u8499\u9762\u4eba\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u7978\u798f\u65e0\u95e8\uff0c\u552f\u4eba\u81ea\u53ec\u3002\u4f60\u82e5\u6019\u6076\u4e0d\u609b\uff0c\u6211\u5c31\u662f\u4f60\u7684\u52fe\u9b42\u4f7f\u8005\uff01\u201d \u3000\u3000\u9633\u575a\u767d\u6c14\u5f80\u4e0a\u51b2\uff0c\u559d\u9053\uff1a\u201c\u597d\uff0c\u9601\u4e0b\u53e3\u51fa\u72c2\u8a00\uff0c\u6211\u5012\u8981\u9886\u6559\u9886\u6559\u4f60\u7684\u672c\u4e8b\u4e86\u3002\u770b\u4e00\u770b\u5230 \u5e95\u662f\u4f60\u52fe\u6211\u9b42\uff0c\u8fd8\u662f\u6211\u593a\u4f60\u7684\u547d\uff01\u201d \u3000\u3000\u58f0\u72b9\u672a\u4e86\uff0c\u53ea\u89c1\u9633\u575a\u767d\u817e\u8eab\u8dc3\u8d77\uff0c\u4e00\u62db\u201c\u9e70\u51fb\u957f\u7a7a\u201d\uff0c\u9752\u94a2\u5251\u5df2\u662f\u51cc\u7a7a\u523a\u4e0b\u3002\u8499\u9762\u4eba\u970d \u7684\u4e00\u4e2a\u201c\u51e4\u70b9\u5934\u201d\uff0c\u62d4\u5251\u51fa\u9798\uff0c\u8fd8\u4e86\u4e00\u62db\u201c\u9c7c\u7fd4\u6d45\u5e95\u201d\uff0c\u53cc\u5251\u76f8\u4ea4\uff0c\u201c\u94db\u201d\u7684\u4e00\u58f0\u54cd\uff0c\u9633\u575a \u767d\u63a0\u8fc7\u4e00\u8fb9\uff0c\u8499\u9762\u4eba\u9000\u540e\u4e24\u6b65\u3002 \u3000\u3000\u8fd9\u89c1\u9762\u7684\u7b2c\u4e00\u62db\uff0c\u53cc\u65b9\u6070\u6070\u662f\u6253\u6210\u5e73\u624b\uff0c\u8c01\u90fd\u6ca1\u6709\u5360\u5230\u4fbf\u5b9c\u3002\u4f46\u8499\u9762\u4eba\u662f\u5728\u5bf9\u65b9\u51fa\u62db\u4e4b \u540e\u624d\u62d4\u5251\u7684\uff0c\u4ed6\u62d4\u5251\u3001\u8fd8\u62db\u3001\u79fb\u8eab\u3001\u6613\u4f4d\uff0c\u51e0\u4e2a\u52a8\u4f5c\uff0c\u4e00\u6c14\u5475\u6210\uff0c\u82e5\u8bba\u8eab\u624b\u7684\u77eb\u6377\uff0c\u5374\u662f\u6bd4 \u9633\u575a\u767d\u8f83\u80dc\u4e00\u7b79\u4e86\u3002 \u3000\u3000\u9633\u575a\u767d\u8bd5\u4e86\u4e00\u62db\uff0c\u60ca\u7591\u4e0d\u5b9a\uff0c\u6b63\u662f\uff1a \u3000\u3000\u53ea\u4e3a\u5fc3\u865a\u795e\u667a\u4e71\uff0c\u7591\u4ed6\u964c\u8def\u53c8\u76f8\u9022\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b\u2014\u2014\u7b2c\u4e09\u5341\u4e00\u56de\u3000\u592a\u606f\u5bb9\u989c\u975e\u5f80\u65e5\u3000\u53ea\u987b\u5fc3\u5730\u80dc\u4ece\u524d \u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b \u7b2c\u4e09\u5341\u4e00\u56de\u3000\u592a\u606f\u5bb9\u989c\u975e\u5f80\u65e5\u3000\u53ea\u987b\u5fc3\u5730\u80dc\u4ece\u524d \u3000\u3000\u6768\u5a49\u5728\u65c1\u89c2\u6218\uff0c\u4e5f\u662f\u60ca\u7591\u4e0d\u5b9a\u3002 \u3000\u3000\u6768\u5a49\u51dd\u795e\u9ed8\u60f3\uff0c\u8fd9\u4eba\u7684\u5251\u6cd5\uff0c\u4f3c\u66fe\u89c1\u8fc7\uff1f\u9aa4\u7136\u60f3\u4e86\u8d77\u6765\uff0c\u53ef\u4e0d\u6b63\u662f\u90a3\u891a\u4e91\u5cf0\u6240\u7528\u7684\u5251 \u6cd5\uff1f \u3000\u3000\u90a3\u65e5\uff0c\u674e\u601d\u5357\u4e0e\u6df3\u4e8e\u5468\u4e89\u593a\u76df\u4e3b\u4e4b\u4f4d\uff0c\u891a\u4e91\u5cf0\u7a81\u5982\u5176\u6765\uff0c\u66ff\u674e\u601d\u5357\u51fa\u573a\uff0c\u4e0e\u5bf9\u65b9\u7684\u5251\u672f \u540d\u5bb6\u67f3\u6d1e\u5929\u6bd4\u5251\uff0c\u7ed3\u679c\u6253\u6210\u5e73\u624b\uff0c\u4ee4\u7fa4\u96c4\u60ca\u5f02\u4e0d\u5df2\uff01\u90a3\u4e00\u5929\u6768\u5a49\u8eb2\u5728\u4eba\u4e1b\u4e4b\u4e2d\u89c2\u6218\uff0c\u5bf9\u4ed6\u6240 \u7528\u7684\u5947\u8be1\u7edd\u4f26\u7684\u5251\u6cd5\uff0c\u5370\u8c61\u751a\u6df1\u3002 \u3000\u3000\u201c\u96be\u9053\u8fd9\u4e2a\u8499\u9762\u4eba\u5c31\u662f\u891a\u4e91\u5cf0\uff1f\u4f46\u58f0\u97f3\u4e0e\u8eab\u6750\u5374\u53c8\u4e0d\u5927\u76f8\u8c61\uff0c\u5012\u53eb\u4eba\u6349\u6478\u4e0d\u900f\u4e86\u3002\u201d\u6768 \u5a49\u5fc3\u60f3\u3002 \u3000\u3000\u5fc3\u5ff5\u672a\u5df2\uff0c\u53ea\u542c\u5f97\u9633\u575a\u767d\u5927\u58f0\u559d\u9053\uff1a\u201c\u891a\u4e91\u5cf0\uff0c\u4f60\u522b\u88c5\u795e\u5f04\u9b3c\u5566\uff01\u201d\u8981\u77e5\u58f0\u97f3\u4e0e\u8eab\u6750\u867d \u7136\u4e0d\u5bf9\uff0c\u4f46\u58f0\u97f3\u53ef\u4ee5\u5047\u88c5\uff0c\u8eab\u6750\u7ecf\u8fc7\u9ad8\u660e\u7684\u5316\u88c5\u672f\u4e5f\u53ef\u4ee5\u6539\u53d8\u3002\u9633\u575a\u767d\u5c31\u662f\u4e2a\u4e2d\u9ad8\u624b\uff0c\u6545\u6b64 \u4ed6\u8ba4\u5b9a\u4e86\u8fd9\u4e2a\u8499\u9762\u4eba\u5fc5\u662f\u891a\u4e91\u5cf0\u65e0\u7591\u3002 \u3000\u3000\u6768\u5a49\u6b63\u81ea\u60f3\u9053\uff1a\u201c\u679c\u7136\u662f\u891a\u4e91\u5cf0\uff01\u201d\u4e0d\u6599\u8fd9\u8499\u9762\u4eba\u5374\u4f3c\u6014\u4e86\u4e00\u6014\uff0c\u8bf4\u9053\uff1a\u201c\u8c01\u662f\u891a\u4e91 \u5cf0\uff1f\u201d \u3000\u3000\u9633\u575a\u767d\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u8fd8\u4e0d\u8ba4\uff01\u201d\u51b7\u7b11\u58f0\u4e2d\uff0c\u731b\u6251\u8fc7\u53bb\uff0c\u5251\u4e2d\u5939\u638c\uff0c\u9690\u9690\u631f\u7740\u98ce\u96f7\u4e4b\u58f0\uff01 \u3000\u3000\u90a3\u4eba\u4ee5\u5251\u5c01\u5251\uff0c\u4ee5\u638c\u654c\u638c\uff0c\u201c\u84ec\u201d\u7684\u4e00\u58f0\uff0c\u5c06\u9633\u575a\u767d\u9707\u9000\u4e09\u6b65\uff01 \u3000\u3000\u9633\u575a\u767d\u559d\u9053\uff1a\u201c\u597d\uff0c\u4f60\u8fd8\u6562\u8bf4\u4f60\u4e0d\u662f\u891a\u4e91\u5cf0\uff1f\u54fc\uff0c\u4f60\u8fd9\u5c0f\u5b50\u662f\u548c\u6211\u4f5c\u5bf9\u5b9a\u5566\uff01\u201d \u3000\u3000\u53cc\u65b9\u6240\u62fc\u7684\u90a3\u4e00\u638c\uff0c\u7528\u7684\u90fd\u662f\u201c\u5929\u96f7\u529f\u201d\uff0c\u201c\u5929\u96f7\u529f\u201d\u6b63\u662f\u9633\u575a\u767d\u7684\u5e08\u95e8\u7edd\u5b66\uff0c\u53ea\u6709\u4ed6 \u7684\u672c\u95e8\u9ad8\u624b\uff0c\u624d\u61c2\u5f97\u8fd9\u95e8\u529f\u592b\uff0c\u636e\u9633\u575a\u767d\u6240\u77e5\uff0c\u9664\u4e86\u4ed6\u7684\u53d4\u7236\u4e0e\u5e08\u53d4\u534e\u5929\u8679\u4e4b\u5916\uff0c\u665a\u4e00\u8f88\u7684 \u5c31\u53ea\u6709\u81ea\u5df1\u548c\u891a\u4e91\u5cf0\u7ec3\u6210\u4e86\u8fd9\u95e8\u529f\u592b\u3002\u8fd9\u8499\u9762\u4eba\u5f53\u7136\u4e0d\u4f1a\u662f\u4ed6\u5e08\u53d4\uff0c\u90a3\u4e48\u4e0d\u662f\u891a\u4e91\u5cf0\u662f\u8c01\uff1f \u3000\u3000\u8499\u9762\u4eba\u6643\u4e86\u4e24\u6643\uff0c\u968f\u5373\u7a33\u5f80\u8eab\u5f62\uff0c\u8bf4\u9053\uff1a\u201c\u54e6\uff0c\u6211\u660e\u767d\u4e86\u3002\u891a\u4e91\u5cf0\u5927\u7ea6\u662f\u534e\u5929\u8679\u7684\u5f1f\u5b50 \u5427\uff1f\u60f3\u5fc5\u4ed6\u4e5f\u662f\u4e0d\u803b\u4f60\u4eec\u6240\u4e3a\u7684\u4e86\uff0c\u662f\u4e48\uff1f\u201d \u3000\u3000\u9633\u575a\u767d\u8bd5\u4e86\u8fd9\u638c\uff0c\u8bd5\u51fa\u5bf9\u65b9\u7684\u201c\u5929\u96f7\u529f\u201d\u4e0e\u81ea\u5df1\u4e0d\u76f8\u4f2f\u4ef2\uff0c\u6bd4\u891a\u4e91\u5cf0\u4f3c\u4e4e\u7a0d\u900a\u4e00\u7b79\uff0c\u5728 \u81ea\u5df1\u90a3\u62db\u6740\u624b\u4e4b\u4e0b\uff0c\u7167\u7406\u5bf9\u65b9\u662f\u4e0d\u6562\u4e0d\u7528\u5168\u529b\u7684\u3002\u9633\u575a\u767d\u4e0d\u7981\u53c8\u662f\u60ca\u7591\u4e0d\u5b9a\u4e86\u3002\u8fd9\u8499\u9762\u4eba\u7a76 \u7adf\u662f\u4e0d\u662f\u891a\u4e91\u5cf0\u5462\uff1f \u3000\u3000\u9633\u575a\u767d\u559d\u9053\uff1a\u201c\u5927\u4e08\u592b\u4f55\u5fc5\u85cf\u5934\u9732\u5c3e\uff1f\u4f60\u4e0d\u662f\u891a\u4e91\u5cf0\u53c8\u662f\u8c01\uff1f\u8bf4\uff01\u201d \u3000\u3000\u8499\u9762\u4eba\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u8981\u4e0d\u8981\u6211\u63ed\u7a7f\u4f60\u7684\u884c\u5f84\uff0c\u4f60\u5e72\u4e0b\u7684\u90a3\u4e9b\u80ae\u810f\u6848\u5b50\uff0c\u90a3\u624d\u771f\u662f\u89c1\u4e0d\u5f97 \u4eba\u7684\u4e0b\u6d41\u52fe\u5f53\u5462\uff01\u76ee\u524d\u6211\u4e0d\u613f\u610f\u4f60\u77e5\u9053\u6211\u662f\u8c01\uff0c\u90a3\u662f\u56e0\u4e3a\u6211\u8fd8\u4e0d\u60f3\u6740\u4f60\uff0c\u4ee5\u540e\u4f60\u81ea\u4f1a\u660e\u767d \u7684\u3002\u73b0\u5728\u6211\u53ea\u662f\u95ee\u4f60\u4e00\u53e5\uff0c\u4f60\u6eda\u4e0d\u6eda\uff1f\u4f60\u4e0d\u81ea\u5df1\u6eda\u5f00\uff0c\u53ef\u5c31\u83ab\u602a\u6211\u624b\u4e0b\u65e0\u60c5\u4e86\uff01\u201d \u3000\u3000\u9633\u575a\u767d\u8001\u7f9e\u6210\u6012\uff0c\u559d\u9053\uff1a\u201c\u4f60\u82e5\u662f\u672c\u95e8\u5f1f\u5b50\uff0c\u4f60\u5c31\u8be5\u77e5\u9053\u6211\u7684\u53d4\u53d4\u4e43\u662f\u638c\u95e8\u3002\u4f60\u6562\u5bf9\u6211 \u5982\u6b64\u65e0\u793c\uff01\u201d \u3000\u3000\u8499\u9762\u4eba\u51b7\u7b11\u9053\uff1a\u201c\u4e8f\u4f60\u5f00\u53e3\u672c\u95e8\uff0c\u95ed\u53e3\u672c\u95e8\uff0c\u672c\u95e8\u7956\u5e08\u7684\u6212\u5f8b\uff0c\u5f00\u5b97\u660e\u4e49\u7b2c\u4e00\u6761\u8bf4\u7684\u662f \u4ec0\u4e48\uff1f\u4f60\u8bf4\u6765\u7ed9\u6211\u542c\u542c\uff01\u201d \u3000\u3000\u9633\u5929\u96f7\u8fd9\u4e00\u95e8\u6d3e\u7684\u5f00\u5c71\u7956\u5e08\uff0c\u672c\u662f\u4e2a\u4fa0\u4e49\u4e4b\u4e0a\uff0c\u751f\u524d\u95e8\u4e0b\u5341\u6761\u6212\u5f8b\uff0c\u7b2c\u4e00\u6761\u5c31\u662f\u53db\u56fd\u6c42 \u8363\u8005\uff0c\u672c\u95e8\u5f1f\u5b50\u4eba\u4eba\u5f97\u800c\u8bdb\u4e4b\u3002\u7b2c\u4e8c\u6761\u624d\u662f\u6b3a\u5e08\u706d\u7956\u8005\u6b7b\uff0c\u8499\u9762\u4eba\u5f00\u53e3\u5c31\u95ee\u8fd9\u4e00\u6761\uff0c\u663e\u7136\u4ed6 \u786e\u662f\u9633\u575a\u767d\u7684\u540c\u95e8\u65e0\u7591\u4e86\u3002 \u3000\u3000\u9633\u575a\u767d\u8001\u7f9e\u6210\u6012\uff0c\u559d\u9053\uff1a\u201c\u4ec0\u4e48\u6212\u5f8b\uff0c\u6211\u53d4\u53d4\u662f\u73b0\u4efb\u638c\u95e8\uff0c\u4ed6\u8bf4\u7684\u8bdd\u5c31\u662f\u6212\u5f8b\u3002\u4f60\u6562\u62ff \u4ec0\u4e48\u7956\u5e08\u7684\u6212\u5f8b\u6765\u538b\u6211\u4e48\uff1f\u201d \u3000\u3000\u8499\u9762\u4eba\u864e\u76ee\u653e\u5149\uff0c\u84e6\u5730\u65a5\u9053\uff1a\u201c\u4f4f\u53e3\uff0c\u4f60\u4eec\u53d4\u4f84\u5356\u56fd\u6c42\u8363\uff0c\u6b3a\u5e08\u706d\u7956\uff0c\u8fd8\u914d\u548c\u6211\u62c9\u626f\u4ec0 \u4e48\u540c\u95e8\u5173\u7cfb\uff1f\u201d \u3000\u3000\u4fd7\u8bed\u8bf4\u90aa\u4e0d\u80dc\u6b63\uff0c\u9633\u575a\u767d\u7ed9\u4ed6\u4e00\u559d\uff0c\u4e0d\u89c9\u6709\u70b9\u5403\u60ca\uff0c\u4f46\u4ed6\u5e73\u7d20\u6a2a\u884c\u60ef\u4e86\uff0c\u600e\u7518\u793a\u5f31\uff1f\u5f53 \u4e0b\u4e00\u58f0\u6012\u543c\uff0c\u518d\u5ea6\u6251\u4e0a\u3002 \u3000\u3000\u638c\u98ce\u5251\u5f71\u4e4b\u4e2d\uff0c\u9633\u575a\u767d\u84e6\u5730\u4e00\u62db\u201c\u4e91\u9501\u82cd\u5c71\u201d\uff0c\u5411\u8499\u9762\u4eba\u7435\u7436\u9aa8\u6293\u53bb\uff0c\u9633\u575a\u767d\u6c89\u80a9\u7f29 \u65f6\uff0c\u6a2a\u638c\u51fb\u51fa\uff0c\u201c\u84ec\u201d\u7684\u4e00\u58f0\uff0c\u53cc\u65b9\u53c8\u786c\u5bf9\u4e86\u4e00\u638c\u3002\u8fd9\u4e00\u6b21\u5374\u662f\u9633\u575a\u767d\u9000\u591a\u4e86\u4e00\u6b65\u4e86\u3002 \u3000\u3000\u8499\u9762\u4eba\u4e00\u5360\u4e0a\u98ce\uff0c\u5251\u4e2d\u5939\u638c\uff0c\u7d27\u7eb5\u6025\u4e0a\uff0c\u903c\u5f97\u9633\u575a\u767d\u5e94\u4ed8\u4e0d\u6687\uff0c\u65e0\u53ef\u5948\u4f55\uff0c\u53ea\u597d\u4e0e\u4ed6\u62fc \u638c\u3002 \u3000\u3000\u8499\u9762\u4eba\u638c\u82e5\u5954\u96f7\uff0c\u5251\u5982\u9a87\u7535\uff0c\u8f6c\u773c\u4e4b\u95f4\uff0c\u5df2\u548c\u9633\u575a\u767d\u5bf9\u4e86\u516b\u638c\uff0c\u53cc\u5251\u76f8\u4ea4\uff0c\u53ee\u53ee\u94db\u94db\u4e4b \u58f0\uff0c\u66f4\u662f\u6709\u5982\u7e41\u5f26\u6025\u594f\uff0c\u4e5f\u4e0d\u77e5\u78b0\u51fb\u4e86\u591a\u5c11\u4e0b\u4e86\u3002 \u3000\u3000\u8fd9\u516b\u638c\u786c\u62fc\u4e0b\u672a\uff0c\u9633\u575a\u767d\u53ea\u89c9\u80f8\u53e3\u53d1\u95f7\uff0c\u51b7\u6c57\u76f4\u6d41\uff0c\u6c14\u5598\u5fc3\u8df3\u3002\u539f\u6765\u4ed6\u4eec\u7684\u529f\u529b\u672c\u662f\u5728 \u4f2f\u4ef2\u4e4b\u95f4\u7684\uff0c\u4f46\u56e0\u9633\u575a\u767d\u524d\u665a\u4e0e\u891a\u4e91\u5cf0\u786c\u62fc\u201c\u5929\u96f7\u529f\u201d\uff0c\u4f24\u4e86\u5143\u6c14\uff0c\u867d\u65e0\u5927\u788d\uff0c\u5374\u4e5f\u672a\u80fd\u5b8c \u5168\u6062\u590d\u3002\u662f\u4ee5\u548c\u8fd9\u8499\u9762\u4eba\u518d\u62fc\u201c\u5929\u96f7\u529f\u201d\uff0c\u81ea\u662f\u4e0d\u514d\u8981\u5403\u4e8f\u4e86\u3002 \u3000\u3000\u9633\u575a\u767d\u672c\u6765\u662f\u60f3\u901f\u6218\u901f\u51b3\uff0c\u6b64\u65f6\u6218\u8fd9\u8499\u9762\u4eba\u4e0d\u4e0b\uff0c\u800c\u6768\u5a49\u4e0e\u963f\u76d6\u53c8\u5728\u4e00\u65c1\u864e\u89c6\u7708\u7708\uff0c\u4e0d \u7531\u5f97\u5fc3\u91cc\u53d1\u614c\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u82e5\u5f85\u8fd9\u86ee\u5b50\u548c\u6768\u5a49\u4e5f\u6062\u590d\u4e86\u6c14\u529b\uff0c\u90a3\u65f6\uff0c\u53ea\u6015\u6211\u8981\u9003\u8d70\u4e5f\u96be\u3002 \u7f62\u4e86\uff0c\u7f62\u4e86\uff0c\u597d\u6c49\u4e0d\u5403\u773c\u524d\u4e8f\uff0c\u4e09\u5341\u516d\u7740\uff0c\u8fd8\u662f\u8d70\u4e3a\u4e0a\u7740\uff01\u201d\u5f53\u4e0b\u865a\u6643\u4e00\u62db\uff0c\u593a\u95e8\u800c\u9003\u3002\u8499 \u9762\u4eba\u51b7\u7b11\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u7978\u798f\u65e0\u95e8\uff0c\u552f\u4eba\u81ea\u62db\u3002\u4eca\u65e5\u6682\u4e14\u9976\u4f60\u4e00\u906d\u3002\u4f60\u56de\u53bb\u81ea\u5df1\u597d\u597d\u60f3 \u5427\u3002\u201d \u3000\u3000\u9633\u575a\u767d\u8d70\u540e\uff0c\u6768\u5a49\u4e0e\u963f\u76d6\u4e0a\u524d\u9053\u8c22\uff0c\u8499\u9762\u4eba\u9053\uff1a\u201c\u6b66\u6797\u540c\u9053\uff0c\u7406\u8be5\u60a3\u96be\u76f8\u52a9\uff0c\u4e9b\u987b\u5c0f \u4e8b\uff0c\u4f55\u8db3\u6302\u9f7f\u3002\u201d \u3000\u3000\u963f\u76d6\u7ad6\u8d77\u62c7\u6307\u8d5e\u9053\uff1a\u201c\u597d\u6c49\u5b50\uff0c\u597d\u6c49\u5b50\uff01\u6211\u4eec\u7684\u5b98\u957f\u5e38\u8bf4\u6c49\u4eba\u72e1\u733e\uff0c\u539f\u6765\u8fd9\u5168\u662f\u9a97\u6211\u4eec \u7684\uff0c\u73b0\u5728\u6211\u624d\u77e5\u9053\uff0c\u6c49\u4eba\u624d\u771f\u662f\u591f\u670b\u53cb\u5462\uff01\u4f60\u6211\u7d20\u4e0d\u76f8\u8bc6\uff0c\u8fd9\u6b21\u591a\u4e8f\u4f60\u6551\u4e86\u6211\u7684\u6027\u547d\u4e86\u3002\u201d \u3000\u3000\u6768\u5a49\u6050\u4ed6\u8d77\u7591\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u670b\u53cb\u867d\u7136\u662f\u8499\u53e4\u4eba\uff0c\u4f46\u4ed6\u5374\u662f\u4e0d\u7518\u66ff\u8499\u53e4\u5927\u6c57\u5356\u547d\uff0c\u9003\u51fa \u6765\u7684\u3002\u201d \u3000\u3000\u8499\u9762\u4eba\u9053\uff1a\u201c\u6211\u77e5\u9053\u3002\u4f60\u4eec\u521a\u624d\u548c\u90a3\u4e24\u4e2a\u8d3c\u5b50\u8bf4\u7684\u8bdd\uff0c\u6211\u90fd\u542c\u89c1\u4e86\uff0c\u8499\u53e4\u4eba\u548c\u54b1\u4eec\u6c49\u4eba \u4e00\u6837\uff0c\u90fd\u662f\u6709\u597d\u4eba\u4e5f\u6709\u574f\u4eba\u7684\u3002\u201d \u3000\u3000\u963f\u76d6\u559c\u4e0d\u81ea\u80dc\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u6c49\u5b50\uff0c\u4f60\u613f\u610f\u548c\u6211\u4ea4\u670b\u53cb\uff1f\u201d\u8499\u9762\u4eba\u9053\uff1a\u201c\u5f53\u7136\u613f\u610f\u3002\u201d\u963f \u76d6\u9053\uff1a\u201c\u6211\u53eb\u963f\u76d6\uff0c\u4f60\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u201d\u8499\u9762\u4eba\u9053\uff1a\u201c\u540d\u5b57\u4e0d\u8fc7\u662f\u4e2a\u7b26\u53f7\uff0c\u4f60\u53eb\u6211\u505a\u8499\u9762\u4eba\u5c31 \u884c\u3002\u53cd\u6b63\u4e0d\u8bba\u6211\u53eb\u505a\u963f\u732b\u963f\u72d7\uff0c\u4f60\u90fd\u662f\u613f\u610f\u548c\u6211\u4ea4\u670b\u53cb\u7684\uff0c\u662f\u4e48\uff1f\u201d \u3000\u3000\u6768\u5a49\u5728\u6c5f\u6e56\u5386\u7ec3\u4e86\u51e0\u5e74\uff0c\u9887\u77e5\u4e00\u4e9b\u6c5f\u6e56\u5fcc\u8bb3\uff0c\u6599\u60f3\u6b64\u4eba\u5b9a\u662f\u6709\u96be\u8a00\u4e4b\u9690\uff0c\u662f\u4ee5\u4e0d\u613f\u628a\u59d3 \u540d\u793a\u4eba\u3002 \u3000\u3000\u963f\u76d6\u70b9\u4e86\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u8bf4\u5f97\u6709\u7406\u3002\u6211\u4eec\u8499\u53e4\u8d35\u4eba\u7684\u540d\u5b57\u90fd\u662f\u8d77\u5f97\u5341\u5206\u597d\u542c\u7684\uff0c\u4f46\u5341\u4e2a \u81f3\u5c11\u6709\u4e5d\u4e2a\u662f\u574f\u4eba\u3002\u201d\u5361\u6d1b\u4e1d\u76b1\u4e86\u76b1\u7709\uff0c\u5728\u963f\u76d6\u8033\u8fb9\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u6768\u59d1\u5a18\u6b63\u5728\u548c\u4ed6\u8bf4\u8bdd\uff0c\u4f60 \u522b\u6253\u5c94\u4e86\u3002\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u539f\u6765\u4f60\u5df2\u7ecf\u542c\u89c1\u6211\u4eec\u7684\u8bf4\u8bdd\uff0c\u90a3\u5c31\u4e0d\u7528\u6211\u591a\u8d39\u5507\u820c\u4e86\uff0c\u6211\u53eb\u6768\u5a49\u3002\u201d \u3000\u3000\u8499\u9762\u4eba\u9053\uff1a\u201c\u6768\u59d1\u5a18\uff0c\u6211\u5411\u4f60\u6253\u542c\u4e00\u4e2a\u4eba\u3002\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u8499\u9762\u4eba\u9053\uff1a\u201c\u662f\u548c\u4f60\u5e74\u7eaa\u5dee\u4e0d\u591a\u7684\u5973\u5b50\uff0c\u540d\u53eb\u5b5f\u660e\u971e\uff0c\u662f\u6c5f\u5357\u5927\u4fa0\u5b5f\u5c11\u521a\u7684\u5973\u513f\u3002\u201d\u63a5 \u7740\u7b11\u9053\uff1a\u201c\u521d\u65f6\u6211\u8fd8\u4ee5\u4e3a\u4f60\u662f\u5b5f\u59d1\u5a18\u5462\uff0c\u8c01\u77e5\u8ddf\u8e2a\u9519\u4e86\u3002\u4e0d\u8fc7\u9519\u5f97\u4e5f\u662f\u6b63\u597d\u3002\u201d \u3000\u3000\u6768\u5a49\u559c\u9053\uff1a\u201c\u539f\u6765\u4f60\u662f\u5b5f\u660e\u971e\u7684\u670b\u53cb\u3002\u201d\u8499\u9762\u4eba\u9053\uff1a\u201c\u8c08\u4e0d\u4e0a\u662f\u670b\u53cb\uff0c\u4e0d\u8fc7\u6211\u548c\u5979\u7236\u4eb2 \u76f8\u8bc6\uff0c\u4e0e\u5979\u5374\u53ea\u662f\u89c1\u8fc7\u4e00\u4e24\u6b21\u9762\uff0c\u4f60\u8ba4\u8bc6\u5979\uff1f\u201d \u3000\u3000\u6768\u5a49\u7b11\u9053\uff1a\u201c\u4f55\u53ea\u8ba4\u8bc6\uff1f\u51e0\u5929\u4e4b\u524d\uff0c\u6211\u8fd8\u548c\u5979\u540c\u5728\u4e00\u8d77\u5462\u3002\u6b64\u9645\uff0c\u5979\u548c\u5979\u7684\u7236\u4eb2\u5df2\u7ecf\u5f80 \u98de\u9f99\u5c71\u53bb\u4e86\u3002\u4f60\u662f\u4e0d\u662f\u8981\u627e\u4ed6\u4eec\u7236\u5973\uff1f\u201d \u3000\u3000\u8499\u9762\u4eba\u9053\uff1a\u201c\u6211\u5e76\u4e0d\u60f3\u7279\u5730\u53bb\u627e\u5979\u3002\u89c1\u4e86\u9762\u8bf7\u4f60\u66ff\u6211\u5411\u5b5f\u5927\u4fa0\u95ee\u5019\u4e00\u58f0\uff0c\u8bf4\u662f\u66fe\u7ecf\u78b0\u89c1 \u8fc7\u8fd9\u6837\u4e00\u4e2a\u4eba\uff0c\u5b5f\u5927\u4fa0\u5c31\u4f1a\u77e5\u9053\u7684\u3002\u201d\u6768\u5a49\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6211\u53ef\u4e0d\u613f\u5728\u5b5f\u660e\u971e\u773c\u524d\u9732\u9762\u3002\u4e0d \u8fc7\uff0c\u6211\u603b\u662f\u4e0d\u80fd\u6c38\u8fdc\u7792\u7740\u5357\u54e5\u7684\uff0c\u7792\u4e0d\u8fc7\u5357\u54e5\uff0c\u5f53\u7136\u4e5f\u5c31\u7792\u4e0d\u8fc7\u5979\u4e86\u3002\u201d\u4e3a\u4e86\u4e0d\u613f\u8ba9\u8fd9\u8499\u9762 \u4eba\u5931\u671b\uff0c\u4e8e\u662f\u4e5f\u5c31\u7b54\u5e94\u4e0b\u6765\u3002 \u3000\u3000\u6768\u5a49\u60f3\u4e86\u4e00\u60f3\uff0c\u53c8\u518d\u95ee\u9053\uff1a\u201c\u4f60\u521a\u624d\u8bf4\u8ddf\u8e2a\u9519\u4e86\uff0c\u8fd9\u662f\u600e\u4e48\u4e00\u56de\u4e8b\uff1f\u201d \u3000\u3000\u8499\u9762\u4eba\u9053\uff1a\u201c\u5b5f\u59d1\u5a18\u6628\u665a\u66fe\u5728\u8fd9\u4e2a\u53bf\u57ce\u9732\u9762\uff0c\u5f53\u665a\u53bf\u57ce\u91cc\u5c31\u53d1\u751f\u4e00\u5b97\u91c7\u82b1\u6848\u5b50\uff0c\u53ef\u60dc\u6211 \u6ca1\u78b0\u4e0a\uff0c\u4e0d\u8fc7\uff0c\u5927\u81f4\u7684\u60c5\u5f62\u6211\u5374\u6253\u542c\u5230\u4e86\u3002\u8fd9\u4ef6\u6848\u5b50\u662f\u5728\u8d3a\u4e5d\u516c\u5bb6\u91cc\u53d1\u751f\u7684\uff0c\u8d3a\u4e5d\u516c\u662f\u4e2a\u9690 \u85cf\u7684\u6c5f\u6e56\u5927\u76d7\uff0c\u4e5f\u662f\u9644\u8fd1\u8fd9\u51e0\u53bf\u7684\u9ed1\u9053\u5934\u5b50\uff0c\u5e73\u751f\u4f5c\u6076\u591a\u7aef\uff0c\u4e0d\u95ee\u53ef\u77e5\uff0c\u8fd9\u662f\u4ed6\u5b89\u6392\u4e0b\u7684\u9677 \u9631\u4e86\u3002\u4e0d\u8fc7\uff0c\u542c\u8bf4\u8fd9\u6b21\u8fd9\u91c7\u82b1\u8d3c\u548c\u8d3a\u4e5d\u516c\u592b\u59bb\u5374\u662f\u5403\u4e86\u5927\u4e8f\u3002\u5728\u4ed6\u5bb6\u5bc4\u5bbf\u7684\u5973\u5ba2\u4eba\u4e0d\u77e5\u600e\u7684 \u5f97\u4e86\u4e00\u4e2a\u5c11\u5e74\u4fa0\u58eb\u4e4b\u52a9\uff0c\u4e0d\u4f46\u9003\u51fa\u864e\u53e3\uff0c\u800c\u4e14\u6740\u5f97\u90a3\u91c7\u82b1\u8d3c\u5927\u8d25\u800c\u9003\u3002\u8d3a\u4e5d\u516c\u884c\u85cf\u8d25\u9732\uff0c\u4e5f \u5413\u5f97\u8fde\u591c\u9003\u8d70\uff0c\u53e6\u627e\u5730\u65b9\u8eb2\u85cf\uff0c\u4e0d\u6562\u518d\u56de\u5bb6\u91cc\u3002\u201d \u3000\u3000\u6768\u5a49\u542c\u4e86\uff0c\u4e0d\u7981\u8bf4\u9053\uff1a\u201c\u8fd9\u53ef\u5c31\u6709\u70b9\u5947\u602a\u4e86\u3002\u201d\u8499\u9762\u4eba\u9053\uff1a\u201c\u5947\u602a\u4ec0\u4e48\uff1f\u201d\u6768\u5a49\u9053\uff1a \u201c\u90a3\u5973\u7684\u60f3\u5fc5\u662f\u5b5f\u5973\u4fa0\u4e86\u3002\u4f46\u90a3\u7537\u7684\u2014\u2014\u201d\u8499\u9762\u4eba\u9053\uff1a\u201c\u542c\u9633\u575a\u767d\u521a\u624d\u7684\u53e3\u6c14\uff0c\u90a3\u91cc\u7684\u4f3c\u4e4e \u662f\u4e00\u4e2a\u540d\u53eb\u891a\u4e91\u5cf0\u7684\u4eba\uff0c\u5426\u5219\u8fd9\u53ae\u4e0d\u4f1a\u5c06\u6211\u8bef\u8ba4\u4f5c\u4ed6\uff0c\u800c\u4e14\u5728\u8bdd\u8bed\u4e4b\u4e2d\uff0c\u900f\u9732\u51fa\u66fe\u5403\u8fc7\u90a3\u59d3 \u891a\u7684\u4e8f\u3002\u201d \u3000\u3000\u6768\u5a49\u70b9\u4e86\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e5f\u662f\u8fd9\u6837\u60f3\uff0c\u56e0\u6b64\u624d\u89c9\u5f97\u5947\u602a\u3002\u201d \u3000\u3000\u8499\u9762\u4eba\u8be7\u9053\uff1a\u201c\u8fd9\u5374\u4e3a\u4f55\uff1f\u660e\u971e\u548c\u90a3\u59d3\u891a\u7684\u540c\u5728\u4e00\u8d77\uff0c\u6709\u4f55\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u636e\u6211\u6240\u77e5\uff0c\u891a\u4e91\u5cf0\u4e0e\u5b5f\u660e\u971e\u6050\u6015\u4e0d\u662f\u4e00\u6761\u8def\u4e0a\u7684\u4eba\u3002\u201d \u3000\u3000\u8499\u9762\u4eba\u9053\uff1a\u201c\u54e6\uff0c\u90a3\u4e48\u8fd9\u59d3\u891a\u7684\u662f\u4ec0\u4e48\u4eba\uff0c\u4f60\u662f\u77e5\u9053\u7684\u4e86\uff1f\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u6211\u5bf9\u4ed6\u7684\u6765\u5386\u6beb\u65e0\u6240\u77e5\u3002\u4e0d\u8fc7\u524d\u51e0\u5929\u5728\u7405\u739b\u5c71\u4e0a\uff0c\u66fe\u53d1\u751f\u8fc7\u4e24\u6869\u4e8b\u60c5\uff0c\u4e0e\u8fd9 \u59d3\u891a\u7684\u6709\u5173\u3002\u4ed6\u7684\u884c\u5f84\u5341\u5206\u53e4\u602a\uff0c\u5927\u5bb6\u7591\u5fc3\u4ed6\u53ef\u80fd\u662f\u79c1\u901a\u8499\u53e4\u7684\u5978\u7ec6\u3002\u201d \u3000\u3000\u8499\u9762\u4eba\u9053\uff1a\u201c\u79c1\u901a\u8499\u53e4\u7684\u5978\u7ec6\uff1f\u4e0d\u4f1a\u5427\uff01\u90a3\u4e24\u6869\u4e8b\u60c5\u4f60\u53ef\u4ee5\u8bf4\u7ed9\u6211\u542c\u4e48\uff1f\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u524d\u51e0\u5929\u7405\u739b\u5c71\u4e0a\u6709\u4e2a\u7eff\u6797\u5927\u4f1a\uff0c\u63a8\u4e3e\u76df\u4e3b\u3002\u201d\u8499\u9762\u4eba\u63d2\u53e3\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u6211\u5df2 \u77e5\u9053\uff0c\u542c\u8bf4\u4e00\u65b9\u662f\u6b66\u6797\u4e2d\u540e\u8d77\u4e4b\u79c0\u7684\u5c11\u5e74\u4fa0\u58eb\u674e\u601d\u5357\uff0c\u4e00\u65b9\u662f\u7eff\u6797\u4e2d\u65e9\u5df2\u6210\u540d\u7684\u4eba\u7269\u6df3\u4e8e \u5468\u3002\u201d \u3000\u3000\u6768\u5a49\u63a5\u4e0b\u53bb\u8bf4\u9053\uff1a\u201c\u6597\u5230\u534a\u573a\uff0c\u891a\u4e91\u5cf0\u7a81\u7136\u51fa\u73b0\uff0c\u5927\u5bb6\u90fd\u4e0d\u77e5\u9053\u4ed6\u7684\u6765\u5386\uff0c\u4ed6\u5374\u5e2e\u674e\u601d \u5357\u6253\u8d25\u4e86\u5bf9\u65b9\u7684\u4e00\u540d\u9ad8\u624b\uff0c\u53c8\u548c\u5bf9\u65b9\u7684\u5251\u672f\u5927\u540d\u5bb6\u67f3\u6d1e\u5929\u6597\u4e86\u4e00\u4e2a\u548c\u5c40\u3002\u674e\u601d\u5357\u593a\u5f97\u76df\u4e3b\uff0c \u5f97\u4ed6\u52a9\u529b\u4e0d\u5c11\u3002\u201d \u3000\u3000\u8499\u9762\u4eba\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u891a\u4e91\u5cf0\u4e0d\u4e5f\u662f\u4fa0\u4e49\u9053\u4e48\uff1f\u4f55\u4ee5\u4f60\u53c8\u8bf4\u4ed6\u548c\u5b5f\u5973\u4fa0\u4e0d\u662f\u540c\u4e00\u9053\uff1f\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u4f46\u4ed6\u5f53\u665a\u53c8\u4e0e\u5c60\u9f99\u79c1\u4f1a\uff0c\u5c60\u9f99\u8fd9\u53ae\u5df2\u7ecf\u8bc1\u5b9e\u4e86\u662f\u8499\u53e4\u7684\u5978\u7ec6\uff0c\u90a3\u665a\u6211\u65e0\u610f\u4e4b \u4e2d\u5077\u542c\u5230\u4ed6\u4eec\u7684\u8bf4\u8bdd\uff0c\u4ed6\u4eec\u8fd8\u5728\u5408\u8c0b\u7be1\u593a\u7405\u739b\u5c71\u5be8\u4e3b\u4e4b\u4f4d\u5462\u3002\u55ef\uff0c\u6211\u5fd8\u8bb0\u544a\u8bc9\u4f60\u5c60\u9f99\u5c31\u662f\u7405 \u739b\u5c71\u5973\u5be8\u4e3b\u5c60\u51e4\u7684\u54e5\u54e5\uff0c\u4ed6\u4eec\u5144\u59b9\u662f\u5404\u8d70\u5404\u8def\uff0c\u5927\u4e0d\u76f8\u540c\u7684\u3002\u201d \u3000\u3000\u8499\u9762\u4eba\u9053\uff1a\u201c\u65e2\u7136\u8fd9\u6837\uff0c\u8fd9\u5c31\u771f\u662f\u6709\u70b9\u5947\u602a\u4e86\u3002\u4e0d\u8fc7\uff0c\u8bf4\u4e0d\u5b9a\u4e5f\u8bb8\u891a\u4e91\u5cf0\u662f\u60f3\u5957\u53d6\u5c60\u9f99 \u7684\u79d8\u5bc6\u3002\u5982\u679c\u4ed6\u5f53\u771f\u662f\u5978\u7ec6\u7684\u8bdd\uff0c\u6628\u665a\u4ed6\u5c31\u4e0d\u4f1a\u548c\u9633\u575a\u767d\u4f5c\u5bf9\u4e86\u3002\u53c8\u5373\u4f7f\u6628\u665a\u90a3\u4e2a\u7537\u7684\u662f\u53e6 \u4e00\u4e2a\u4eba\uff0c\u4f46\u9633\u575a\u767d\u90a3\u6837\u75db\u6068\u4ed6\uff0c\u4e5f\u53ef\u4ee5\u8bc1\u660e\u4ed6\u4e0d\u662f\u5978\u7ec6\u4e86\u3002\u201d \u3000\u3000\u6768\u5a49\u8bf4\u9053\uff1a\u201c\u8fd9\u59d3\u891a\u7684\u884c\u8e2a\u8be1\u79d8\uff0c\u6211\u4e5f\u6478\u4e0d\u6e05\u4ed6\u7684\u5e95\u7ec6\u3002\u4f60\u8981\u4e0d\u8981\u5230\u7405\u739b\u5c71\u53bb\u8bbf\u67e5\u8bbf \u67e5\uff0c\u63a2\u4e2a\u6c34\u843d\u77f3\u51fa\u3002\u201d \u3000\u3000\u8499\u9762\u4eba\u8bf4\u9053\uff1a\u201c\u6211\u662f\u60f3\u6253\u63a2\u8fd9\u4e2a\u891a\u4e91\u5cf0\u7684\u5e95\u7ec6\u7684\uff0c\u6628\u65e5\u6211\u5c31\u662f\u56e0\u4e3a\u60f3\u8981\u627e\u5bfb\u4ed6\u4eec\uff0c\u4ee5\u81f4 \u8ddf\u8e2a\u9519\u4e86\u3002\u4e0d\u8fc7\uff0c\u76ee\u524d\u6211\u8fd8\u4e0d\u80fd\u5230\u7405\u739b\u5c71\u53bb\u3002\u6211\u60f3\u891a\u4e91\u5cf0\u4e5f\u672a\u5fc5\u4e0e\u5b5f\u660e\u971e\u5c31\u56de\u8f6c\u5c71\u5be8\u53bb \u7684\u3002\u201d \u3000\u3000\u6768\u5a49\u8bf4\u9053\uff1a\u201c\u65e2\u7136\u9601\u4e0b\u53e6\u6709\u8981\u4e8b\uff0c\u6211\u81ea\u662f\u4e0d\u4fbf\u76f8\u5f3a\u3002\u4e0d\u8fc7\uff0c\u4f60\u82e5\u662f\u627e\u4e0d\u7740\u4ed6\u4eec\uff0c\u4e0d\u59a8\u5230 \u7405\u739b\u5c71\u6253\u542c\u6d88\u606f\u3002\u201d \u3000\u3000\u8499\u9762\u4eba\u70b9\u4e86\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u6768\u59d1\u5a18\uff0c\u591a\u8c22\u4f60\u544a\u8bc9\u6211\u8fd9\u8bb8\u591a\u6211\u60f3\u8981\u77e5\u9053\u7684\u4e8b\u60c5\uff0c\u65f6\u5019\u4e0d \u65e9\uff0c\u6211\u4e5f\u5e94\u8be5\u8d70\u4e86\uff0c\u54b1\u4eec\u540e\u4f1a\u6709\u671f\u3002\u201d \u3000\u3000\u963f\u76d6\u5ffd\u5730\u4e0a\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4ea4\u4e86\u4f60\u8fd9\u6837\u4e00\u4f4d\u597d\u670b\u53cb\uff0c\u5fc3\u91cc\u5f88\u662f\u6b22\u559c\uff0c\u8bf7\u4f60\u63a5\u53d7\u6211\u4e00\u70b9\u656c \u610f\u3002\u201d\u963f\u76d6\u624b\u91cc\u62ff\u7740\u4e00\u6761\u767d\u8272\u7684\u4e1d\u5dfe\u3002\u8499\u9762\u4eba\u77e5\u9053\u8fd9\u662f\u8499\u53e4\u4eba\u7684\u793c\u8282\uff0c\u9001\u624b\u5e15\u540d\u4e3a\u201c\u732e\u54c8 \u8fbe\u201d\uff0c\u662f\u5bf9\u670b\u53cb\u8868\u793a\u5c0a\u656c\u7684\u3002 \u3000\u3000\u8499\u9762\u4eba\u9053\uff1a\u201c\u4f60\u662f\u6211\u7b2c\u4e00\u4e2a\u8499\u53e4\u670b\u53cb\uff0c\u6211\u5f97\u6709\u4f60\u8fd9\u6837\u7684\u597d\u6c49\u5b50\u505a\u670b\u53cb\uff0c\u6211\u4e5f\u662f\u5f88\u9ad8\u5174 \u7684\u3002\u4f46\u62b1\u6b49\u6211\u53ef\u6ca1\u6709\u4ec0\u4e48\u793c\u7269\u7ed9\u4f60\u3002\u201d\u5f53\u4e0b\u63a5\u8fc7\u90a3\u6761\u624b\u5e15\uff0c\u6309\u7167\u8499\u53e4\u7684\u793c\u8282\uff0c\u4e0e\u963f\u76d6\u62e5\u62b1\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u8499\u9762\u4eba\u53eb\u9053\uff1a\u201c\u4f60\u3001\u4f60\u5e72\u4ec0\u4e48\uff1f\u201d\u5ffd\u5730\u4e00\u638c\u63a8\u5f00\u963f\u76d6\u3002\u539f\u6765\u963f\u76d6\u548c\u4ed6\u62e5\u62b1\u4e4b\u9645\uff0c \u7a81\u7136\u626f\u4e0b\u4ed6\u7684\u8499\u9762\u5dfe\uff0c\u5927\u5bb6\u90fd\u6ca1\u60f3\u5230\u963f\u76d6\u50bb\u91cc\u50bb\u6c14\u7684\uff0c\u4f1a\u7a81\u7136\u6765\u8fd9\u4e48\u4e00\u4e0b\u3002 \u3000\u3000\u9762\u5dfe\u63ed\u5f00\uff0c\u5361\u6d1b\u4e1d\u548c\u660e\u6167\u516c\u4e3b\u4e0d\u89c9\u201c\u554a\u5440\u201d\u4e00\u58f0\uff0c\u53eb\u4e86\u51fa\u6765\u3002\u963f\u76d6\u4e5f\u5413\u5f97\u5446\u4e86\u3002\u6768\u5a49\u867d \u7136\u6ca1\u6709\u5931\u58f0\u60ca\u547c\uff0c\u4f46\u89c1\u4e86\u8fd9\u4eba\u7684\u771f\u9762\u76ee\uff0c\u4e5f\u662f\u6709\u70b9\u4e0d\u5bd2\u800c\u6817\u3002\u4e0d\u8fc7\uff0c\u56e0\u4e3a\u5979\u7d20\u6709\u6570\u517b\uff0c\u624d\u80fd \u5fcd\u7740\u3002 \u3000\u3000\u8fd9\u4eba\u7684\u76f8\u8c8c\u5947\u4e11\uff0c\u7b80\u76f4\u51fa\u4e4e\u4efb\u4f55\u4eba\u7684\u610f\u6599\u4e4b\u5916\u3002\u53ea\u89c1\u4ed6\u8138\u4e0a\u51e0\u9053\u4f24\u75d5\uff0c\u7eb5\u6a2a\u4ea4\u9519\uff0c\u5c31\u5982 \u5341\u5b57\u8def\u53e3\u7684\u8f66\u8f68\u4e00\u822c\u3002\u7531\u4e8e\u4f24\u75a4\u51f9\u51f8\u4e0d\u5e73\uff0c\u8138\u4e0a\u7684\u808c\u8089\u4e5f\u56e0\u800c\u626d\u66f2\u53d8\u5f62\uff0c\u4ee4\u4eba\u4e00\u770b\u5c31\u89c9\u5f97\u6050 \u6016\uff0c\u4e0d\u6562\u518d\u591a\u770b\u4e00\u773c\u3002 \u3000\u3000\u6768\u5a49\u521d\u65f6\u8fd8\u6709\u51e0\u5206\u6000\u7591\u4ed6\u662f\u891a\u4e91\u5cf0\u7684\uff0c\u6b64\u9645\u89c1\u4e86\u4ed6\u7684\u201c\u5c0a\u5bb9\u201d\uff0c\u5f53\u7136\u77e5\u9053\u4e0d\u662f\u4e86\u3002\u540c\u65f6 \u6768\u5a49\u4e5f\u624d\u660e\u767d\uff0c\u4ed6\u4e3a\u4ec0\u4e48\u8981\u8499\u9762\u7684\u7f18\u6545\u4e86\u3002\u201c\u4ed6\u662f\u6050\u6015\u5413\u614c\u4e86\u522b\u4eba\uff0c\u8fd8\u4e0d\u4ec5\u4ec5\u662f\u56e0\u4e3a\u4e0d\u613f\u610f \u8ba9\u9633\u575a\u767d\u77e5\u9053\u5427\uff1f\u201d\u6768\u5a49\u5fc3\u60f3\u3002 \u3000\u3000\u963f\u76d6\u7ed9\u4ed6\u4e00\u638c\u63a8\u5f00\uff0c\u5446\u4e86\u4e00\u5446\uff0c\u8bb7\u8bb7\u8bf4\u9053\uff1a\u201c\u5bf9\u4e0d\u4f4f\uff0c\u6211\uff0c\u6211\u4e0d\u77e5\u9053\u2026\u2026\u6211\u4eec\u8499\u53e4\u4eba\u4ea4 \u670b\u53cb\uff0c\u662f\u3001\u662f\u8fd9\u6837\u7684\u2026\u2026\u201d\u4ed6\u62d9\u4e8e\u8a00\u8bed\uff0c\u4e0d\u77e5\u5982\u4f55\u624d\u80fd\u8868\u793a\u5fc3\u4e2d\u7684\u6b49\u610f\u3002 \u3000\u3000\u539f\u6765\u963f\u76d6\u5fc3\u5730\u5355\u7eaf\uff0c\u5728\u4ed6\u7684\u60f3\u6cd5\u662f\uff1a\u201c\u6211\u548c\u4ed6\u505a\u4e86\u670b\u53cb\uff0c\u5c82\u80fd\u8fde\u4ed6\u7684\u76f8\u8c8c\u662f\u600e\u4e48\u6837\u90fd\u4e0d \u77e5\u9053\u3002\u5bf9\u654c\u4eba\u6216\u9700\u906e\u7792\uff0c\u5bf9\u670b\u53cb\u5e94\u8be5\u53ef\u4ee5\u5256\u5fc3\u76f8\u89c1\uff0c\u4f55\u51b5\u9762\u76ee\u5462\uff1f\u201d\u8349\u539f\u4e0a\u7684\u7267\u6c11\u6700\u91cd\u53cb \u8c0a\uff0c\u670b\u53cb\u4e4b\u95f4\uff0c\u662f\u4ec0\u4e48\u90fd\u53ef\u4ee5\u5766\u767d\u7684\u3002\u662f\u4ee5\u963f\u76d6\u4e00\u65f6\u9ad8\u5174\u5fd8\u5f62\uff0c\u6839\u636e\u81ea\u5df1\u7684\u60f3\u6cd5\uff0c\u4e0d\u89c9\u5c31\u628a \u4ed6\u7684\u9762\u5dfe\u63ed\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u201c\u8499\u9762\u4eba\u201d\u82e6\u7b11\u9053\uff1a\u201c\u6211\u4e0d\u602a\u4f60\u3002\u5176\u5b9e\u6211\u53c8\u4f55\u5fc5\u81ea\u60ed\u5f62\u79fd\u5462\uff1f\u6211\u8fd9\u4e2a\u6a21\u6837\u5e76\u975e\u751f\u6765\u7684\uff0c \u4f46\u65e2\u7136\u53d8\u6210\u4e86\u8fd9\u4e2a\u6837\u5b50\uff0c\u7ed9\u4eba\u770b\u770b\uff0c\u53c8\u6709\u4f55\u59a8\uff1f\u963f\u76d6\uff0c\u4f60\u5bb3\u6015\u6211\u5417\uff1f\u201d \u3000\u3000\u963f\u76d6\u5766\u7136\u8bf4\u9053\uff1a\u201c\u4f60\u9762\u8c8c\u4e11\u964b\uff0c\u5fc3\u5730\u5f88\u597d\u3002\u6211\u559c\u6b22\u4f60\u8fd8\u6765\u4e0d\u53ca\u5462\uff0c\u600e\u4f1a\u6015\u4f60\uff1f\u201d \u3000\u3000\u201c\u8499\u9762\u4eba\u201d\u4ef0\u5927\u5927\u7b11\uff0c\u6717\u58f0\u541f\u9053\uff1a\u201c\u5b8b\u7389\u6f58\u5b89\u4f55\u8db3\u9053\uff0c\u4eba\u95f4\u96be\u5f97\u70ed\u5fc3\u80a0\uff0c\u4f60\u4e0d\u5acc\u6211\u4e11 \u964b\uff0c\u6211\u5f88\u9ad8\u5174\uff01\u201d \u3000\u3000\u963f\u76d6\u4e0d\u61c2\u8bd7\u610f\uff0c\u89c1\u4ed6\u9ad8\u5174\uff0c\u5fc3\u4e0a\u7684\u4e00\u5757\u77f3\u5934\u843d\u4e86\u4e0b\u6765\uff0c\u4e5f\u5c31\u8ddf\u7740\u4ed6\u50bb\u91cc\u50bb\u6c14\u5730\u8bf4\u9053\uff1a \u201c\u4f60\u4e0d\u602a\u6211\uff0c\u6211\u4e5f\u662f\u5f88\u9ad8\u5174\u554a\uff01\u201d \u3000\u3000\u6768\u5a49\u5374\u5728\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u4eba\u7684\u672c\u6765\u9762\u76ee\u4e00\u5b9a\u662f\u4e2a\u7f8e\u5c11\u5e74\uff0c\u4ed6\u8fd9\u4e24\u53e5\u8bd7\u867d\u7136\u597d\u4f3c\u6beb\u4e0d\u5728\u4e4e \u5bb9\u8c8c\uff0c\u5176\u5b9e\u5374\u662f\u803f\u803f\u4e8e\u6000\u7684\u3002\u4ed6\u81ea\u6170\u81ea\u89e3\uff0c\u4e5f\u6b63\u5c31\u662f\u81ea\u5632\u81ea\u4f24\u554a\uff01\u201d \u3000\u3000\u201c\u8499\u9762\u4eba\u201d\u63a5\u7740\u53f9\u4e86\u53e3\u6c14\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u4f46\u4e16\u4eba\u591a\u662f\u4ee5\u8c8c\u53d6\u4eba\uff0c\u50cf\u4f60\u4eec\u8fd9\u6837\u4e0d\u6015\u6211\u76f8\u8c8c \u5947\u4e11\u7684\u6050\u6015\u4e0d\u591a\u3002\u6211\u8fd8\u662f\u53ea\u597d\u505a\u8499\u9762\u4eba\u5427\u3002\u201d\u8bf4\u7f62\u53c8\u8499\u4e0a\u4e86\u9ed1\u9762\u5dfe\uff0c\u957f\u7b11\u58f0\u4e2d\uff0c\u98d8\u7136\u800c\u53bb\u3002 \u3000\u3000\u660e\u6167\u516c\u4e3b\u7b11\u9053\uff1a\u201c\u6211\u53ea\u9053\u9547\u56fd\u738b\u5b50\u662f\u5929\u4e0b\u7b2c\u4e00\u4e11\u6c49\uff0c\u8c01\u77e5\u9053\u8fd8\u6709\u76f8\u8c8c\u6bd4\u4ed6\u66f4\u4e11\u7684\u3002\u4e0d \u8fc7\uff0c\u8fd9\u4eba\u5012\u4e5f\u771f\u6709\u610f\u601d\u3002\u4f46\u4ed6\u6b66\u529f\u8fd9\u6837\u9ad8\uff0c\u5374\u4e0d\u77e5\u600e\u7684\u4f1a\u7ed9\u4eba\u4f24\u6210\u8fd9\u4e2a\u6a21\u6837\uff1f\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u8fd9\u5c31\u53eb\u505a\u4e00\u5c71\u8fd8\u6709\u4e00\u5c71\u9ad8\u4e86\u3002\u201d\u660e\u6167\u516c\u4e3b\u9053\uff1a\u201c\u4f46\u4f24\u4ed6\u7684\u4eba\u4e00\u5b9a\u662f\u4e2a\u574f\u4eba\uff0c \u574f\u4eba\u6709\u8fd9\u6837\u9ad8\u7684\u672c\u9886\uff0c\u53ef\u5c31\u4e0d\u662f\u597d\u4e8b\u4e86\u3002\u201d\u6768\u5a49\u70b9\u4e86\u70b9\u5934\uff0c\u9053\uff1a\u201c\u516c\u4e3b\u8bf4\u5f97\u4e0d\u9519\u3002\u201d\u84e6\u5730\u60f3 \u8d77\u4e86\u674e\u601d\u5357\u6765\uff0c\u5982\u4eca\u5979\u5df2\u77e5\u9053\u9633\u575a\u767d\u662f\u91d1\u56fd\u56fd\u5e08\u9633\u5929\u96f7\u7684\u4f84\u5b50\uff0c\u800c\u9633\u575a\u767d\u5c31\u662f\u90a3\u665a\u53bb\u6697\u7b97\u674e \u601d\u5357\u7684\u4eba\u3002 \u3000\u3000\u6768\u5a49\u7981\u4e0d\u4f4f\u60f3\u9053\uff1a\u201c\u8981\u5bb3\u5357\u54e5\u7684\u574f\u4eba\u53ef\u771f\u4e0d\u5c11\uff0c\u6709\u6df3\u4e8e\u5468\u548c\u5c60\u9f99\uff0c\u53c8\u6709\u9633\u5929\u96f7\u53d4\u4f84\uff0c\u8fd9 \u4e9b\u4eba\u90fd\u662f\u6b66\u529f\u6781\u4e3a\u9ad8\u5f3a\u7684\u3002\u4ed6\u6b64\u53bb\u98de\u9f99\u5c71\uff0c\u53c8\u6b63\u662f\u660e\u77e5\u5c71\u6709\u864e\uff0c\u504f\u5411\u864e\u5c71\u884c\u3002\u867d\u8bf4\u6709\u5b5f\u5927\u4fa0 \u6697\u4e2d\u4fdd\u62a4\u4ed6\uff0c\u6015\u4e5f\u662f\u51f6\u591a\u5409\u5c11\u3002\u201d \u3000\u3000\u60f3\u81f3\u6b64\u5904\uff0c\u6768\u5a49\u6068\u4e0d\u5f97\u63d2\u7ffc\u98de\u5230\u98de\u9f99\u5c71\uff0c\u7eb5\u7136\u5e2e\u4e0d\u4e86\u674e\u601d\u5357\u7684\u5927\u5fd9\uff0c\u4e5f\u53ef\u4e0e\u4ed6\u5206\u62c5\u7978 \u798f\u3002\u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u5929\u5df2\u5927\u4eae\uff0c\u6211\u4e5f\u5e94\u8be5\u8d70\u4e86\u3002\u4f60\u4eec\u5148\u53bb\u7405\u739b\u5c71\uff0c\u5728\u5c60\u51e4\u7684\u5c71\u5be8\u5b89\u987f\u4e0b\u6765\uff0c\u6211 \u518d\u56de\u6765\u770b\u4f60\u4eec\u3002\u201d\u660e\u6167\u516c\u4e3b\u9053\uff1a\u201c\u4f46\u613f\u4f60\u65e9\u65e5\u89c1\u7740\u674e\u516c\u5b50\uff0c\u66ff\u6211\u95ee\u5019\u4e00\u58f0\u3002\u201d\u4e24\u4eba\u4f9d\u4f9d\u4e0d\u820d \u800c\u522b\u3002 \u3000\u3000\u6768\u5a49\u72ec\u81ea\u8d76\u8def\uff0c\u8d8a\u5f80\u5317\u8d70\uff0c\u884c\u4eba\u8d8a\u5c11\u3002\u6768\u5a49\u5fc3\u60f3\uff1a\u201c\u542c\u90a3\u8499\u9762\u4eba\u6240\u8bf4\uff0c\u6628\u665a\u5b5f\u660e\u971e\u78b0\u4e0a \u9633\u575a\u767d\u8fd9\u53ae\uff0c\u800c\u52a9\u5979\u6253\u8d25\u9633\u575a\u767d\u5341\u4e5d\u662f\u891a\u4e91\u5cf0\u3002\u6b64\u4e8b\u82e5\u7136\u662f\u771f\uff0c\u8fd9\u53ef\u771f\u662f\u51fa\u4eba\u610f\u5916\u4e86\u3002\u4e0d\u77e5 \u4ed6\u4eec\u4f1a\u4e0d\u4f1a\u4e00\u8def\u540c\u884c\u3002\u891a\u4e91\u5cf0\u662f\u53cb\u662f\u654c\uff0c\u5c1a\u672a\u5206\u660e\u3002\u4e0d\u8fc7\uff0c\u6211\u5012\u662f\u5e0c\u671b\u5982\u90a3\u8499\u9762\u4eba\u6240\u8bf4\uff0c\u891a \u4e91\u5cf0\u662f\u4e2a\u597d\u4eba\uff0c\u201d\u539f\u6765\u5728\u6768\u5a49\u7684\u5185\u5fc3\u6df1\u5904\uff0c\u591a\u5c11\u5bf9\u5b5f\u660e\u971e\u8fd8\u662f\u6709\u70b9\u731c\u5fcc\uff0c\u56e0\u6b64\u5979\u5e0c\u671b\u891a\u4e91\u5cf0 \u662f\u4e2a\u597d\u4eba\uff0c\u5e0c\u671b\u4ed6\u4eec\u4e00\u8def\u540c\u884c\uff0c\u4f1a\u751f\u60c5\u610f\u3002 \u3000\u3000\u8fd9\u65e5\u5929\u6c14\u5947\u5bd2\uff0c\u4e0b\u4e86\u4e00\u573a\u5927\u96ea\uff0c\u6768\u5a49\u72ec\u81ea\u524d\u884c\uff0c\u4f46\u89c1\u4e00\u7247\u767d\u832b\u832b\u7684\u96ea\u666f\u3002\u96ea\u5730\u4e0a\u8fde\u91ce\u517d \u7684\u8db3\u5370\u4e5f\u6ca1\u4e00\u4e2a\u3002\u6768\u5a49\u5fc3\u60f3\uff1a\u201c\u4ed6\u4eec\u6050\u6015\u4e0d\u662f\u5230\u98de\u9f99\u5c71\u7684\u4e86\uff0c\u4e0d\u8fc7\u4ed6\u4eec\u82e5\u662f\u540c\u884c\u7684\u8bdd\uff0c\u6211\u5374 \u662f\u4e0d\u6015\u78b0\u89c1\u5b5f\u660e\u971e\u4e86\u3002\u201d \u3000\u3000\u6768\u5a49\u54ea\u91cc\u77e5\u9053\uff0c\u5b5f\u660e\u971e\u4e5f\u6b63\u662f\u5728\u627e\u5bfb\u5979\uff0c\u5b5f\u660e\u971e\u6e34\u671b\u4e0e\u5979\u76f8\u89c1\u7684\u5fc3\u60c5\uff0c\u6bd4\u5979\u66f4\u751a\u3002 \u3000\u3000\u4e14\u8bf4\u90a3\u665a\u5b5f\u660e\u971e\u4e0e\u891a\u4e91\u5cf0\u4ece\u8d3a\u4e5d\u516c\u5bb6\u91cc\u9003\u51fa\u6765\uff0c\u891a\u4e91\u5cf0\u7ed9\u5979\u6cbb\u597d\u4e86\u4f24\uff0c\u4e0d\u5f85\u5929\u660e\uff0c\u4fbf\u5373 \u8d76\u8def\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u662f\u4e2a\u7cbe\u660e\u5e72\u7ec3\u7684\u4eba\uff0c\u6599\u60f3\u9633\u575a\u767d\u548c\u8d3a\u4e5d\u516c\u4e00\u5b9a\u8fd8\u6709\u515a\u7fbd\uff0c\u8fd9\u6b21\u5403\u4e86\u4e8f\uff0c\u7edd\u4e0d\u4f1a\u8f7b \u6613\u653e\u8fc7\u4ed6\u4eec\uff0c\u5b5f\u660e\u971e\u4f24\u867d\u597d\u4e86\uff0c\u6b66\u529f\u5c1a\u672a\u5b8c\u5168\u6062\u590d\uff0c\u8fd8\u662f\u5c0f\u5fc3\u4e3a\u4e0a\u3002\u56e0\u6b64\uff0c\u4e0d\u8d70\u5927\u8def\uff0c\u9009\u4e86 \u4e00\u6761\u6bd4\u8f83\u8352\u50fb\u7684\u5c71\u8def\u884c\u8d70\uff0c\u907f\u514d\u7ed9\u654c\u65b9\u53d1\u73b0\u884c\u8e2a\u3002 \u3000\u3000\u65f6\u8282\u867d\u662f\u6625\u521d\uff0c\u5c01\u5c71\u7684\u51b0\u96ea\u5c1a\u672a\u6eb6\u89e3\uff0c\u5927\u8def\u4e0a\u90fd\u662f\u884c\u4eba\u7a00\u5c11\uff0c\u4f55\u51b5\u5c71\u95f4\u50fb\u9053\uff0c\u66f4\u662f\u6ca1\u6709 \u884c\u4eba\u4e86\u3002 \u3000\u3000\u4e24\u4eba\u6027\u60c5\u76f8\u8fd1\uff0c\u4e00\u8def\u540c\u884c\uff0c\u4e0d\u77e5\u4e0d\u89c9\u4fbf\u4f3c\u591a\u5e74\u76f8\u8bc6\u7684\u670b\u53cb\u4e00\u822c\uff0c\u6beb\u65e0\u62d8\u675f\u4e86\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u89c1\u5b5f\u660e\u971e\u4e0d\u65f6\u5730\u5bdf\u770b\u5730\u4e0a\u6709\u65e0\u884c\u4eba\u8db3\u8ff9\uff0c\u82e5\u6709\u6240\u601d\uff0c\u4e0d\u7981\u95ee\u9053\uff1a\u201c\u5b5f\u59d1\u5a18\uff0c\u4f60\u53ef \u662f\u6709\u4ec0\u4e48\u5fc3\u4e8b\uff1f\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u6211\u5728\u627e\u5bfb\u4e00\u4f4d\u670b\u53cb\uff0c\u5979\u5c31\u662f\u90a3\u665a\u9ad8\u547c\u6349\u5978\u7ec6\u7684\u90a3\u4e2a\u2018\u5c0f\u55bd\u7f57\u5175\u2019\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u7b11\u9053\uff1a\u201c\u5bf9\uff0c\u4f60\u8bf4\u8fc7\u8fd9\u4e2a\u5c0f\u55bd\u5175\u5c31\u662f\u674e\u601d\u5357\u7684\u672a\u5a5a\u59bb\u5b50\uff0c\u662f\u4e48\uff1f\u90a3\u665a\u6211\u5728\u6797\u4e2d\u5957 \u53d6\u5c60\u9f99\u7684\u79d8\u5bc6\uff0c\u7ed9\u5979\u8bef\u4f1a\u4e86\u3002\u6211\u4e5f\u5e0c\u671b\u80fd\u591f\u89c1\u7740\u5979\uff0c\u5411\u5979\u89e3\u91ca\u5462\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u5979\u591a\u534a\u662f\u5230\u98de\u9f99\u5c71\u627e\u674e\u601d\u5357\u53bb\u7684\uff0c\u6211\u62c5\u5fc3\u5979\u5355\u8eab\u4e00\u4eba\uff0c\u5018\u82e5\u78b0\u4e0a\u4e86\u9633\u575a\u767d \u8fd9\u73ed\u4eba\uff0c\u53ef\u662f\u5f88\u4e0d\u5999\u554a\uff01\u54b1\u4eec\u73b0\u5728\u8d70\u5c0f\u8def\uff0c\u6050\u6015\u96be\u4ee5\u4e0e\u5979\u76f8\u9047\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u7b11\u9053\uff1a\u201c\u9633\u575a\u767d\u8981\u7684\u662f\u50cf\u4f60\u4e00\u6837\u6807\u81f4\u7684\u82b1\u59d1\u5a18\uff0c\u90a3\u4f4d\u6768\u59d1\u5a18\u82e5\u7136\u8fd8\u662f\u5c0f\u55bd\u5175\u7684\u6253 \u626e\uff0c\u4fdd\u7ba1\u6ca1\u6709\u5371\u9669\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u55d4\u9053\uff1a\u201c\u4f60\u4e0d\u77e5\u6211\u5fc3\u91cc\u591a\u4e48\u7740\u6025\uff0c\u4f60\u5374\u548c\u6211\u8bf4\u7b11\uff0c\u5979\u5b64\u8eab\u4e00\u4eba\uff0c\u7eb5\u7136\u6ca1\u6709\u5371\u9669\uff0c \u6211\u4e5f\u662f\u5f97\u89c1\u7740\u5979\u624d\u80fd\u5b89\u5fc3\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u65e2\u7136\u5979\u662f\u5230\u98de\u9f99\u5c71\u7684\uff0c\u8fdf\u65e9\u603b\u4f1a\u89c1\u7740\uff0c\u73b0\u5728\u6025\u4e5f\u6ca1\u7528\u3002\u4f60\u548c\u90a3\u4f4d\u6768\u59d1\u5a18\u662f \u5f88\u8981\u597d\u7684\u670b\u53cb\u5427\uff1f\u201d \u3000\u3000\u8fd9\u4e00\u95ee\u52fe\u8d77\u4e86\u5b5f\u660e\u971e\u7684\u5fc3\u4e8b\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u4f60\u53ea\u77e5\u5979\u5bf9\u4f60\u6709\u6240\u8bef\u4f1a\uff0c\u5374\u4e0d\u77e5\u5979\u5bf9\u6211\u8bef\u4f1a \u66f4\u6df1\u3002\u5979\u4e3a\u6211\u800c\u79bb\u5f00\u5c71\u5be8\uff0c\u82e5\u679c\u5728\u8def\u4e0a\u6709\u751a\u610f\u5916\uff0c\u53eb\u6211\u5982\u4f55\u5bf9\u5f97\u4f4f\u674e\u601d\u5357\uff1f\u201d\u4f46\u56e0\u4e0d\u4fbf\u7ec6\u8bf4 \u5176\u4e2d\u539f\u59d4\uff0c\u53ea\u597d\u9ed8\u9ed8\u5730\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u8d5e\u9053\uff1a\u201c\u5b5f\u59d1\u5a18\uff0c\u4f60\u5bf9\u670b\u53cb\u771f\u662f\u70ed\u5fc3\uff01\u201d\u5b5f\u660e\u971e\u5ae3\u7136\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u4e5f\u662f\u4e00 \u6837\u5417\uff1f\u8fd9\u6b22\u82e5\u4e0d\u662f\u6709\u4f60\u6697\u4e2d\u76f8\u52a9\uff0c\u6211\u6b64\u523b\u54ea\u91cc\u8fd8\u80fd\u591f\u548c\u4f60\u540c\u884c\uff0c\u6050\u6015\u65e9\u5df2\u7ed9\u9633\u575a\u767d\u6349\u53bb \u4e86\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u5fc3\u91cc\u751c\u4e1d\u4e1d\u7684\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u53c8\u6765\u548c\u6211\u5ba2\u6c14\u4e86\uff0c\u55ef\uff0c\u53c8\u4e0b\u96ea\u5566\uff0c\u4f60\u51b7\u4e0d\u51b7\uff1f\u201d \u3000\u3000\u5b5f\u660e\u971e\u7b11\u9053\uff1a\u201c\u4f60\u5f53\u6211\u662f\u5a07\u751f\u60ef\u517b\u7684\u5c0f\u59d0\u4e48\uff0c\u6211\u5728\u6c5f\u5357\uff0c\u96be\u5f97\u770b\u89c1\u8fd9\u6837\u7684\u96ea\u666f\uff0c\u5c31\u662f\u518d \u51b7\uff0c\u53c8\u6709\u4f55\u59a8\uff1f\u55ef\uff0c\u4f60\u770b\u8fd9\u5c71\u4e0a\u5230\u5904\u662f\u96ea\u6811\u94f6\u82b1\uff0c\u4fe8\u5982\u7409\u7483\u4e16\u754c\uff0c\u771f\u662f\u7f8e\u6781\u4e86\uff0c\u7f8e\u6781\u4e86\uff0c \u54a6\uff0c\u4f60\u600e\u4e48\u5012\u597d\u50cf\u4e0d\u5927\u6b23\u8d4f\uff0c\u83ab\u975e\u4f60\u4e5f\u6709\u4ec0\u4e48\u5fc3\u4e8b\u4e48\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u6211\u5728\u5317\u65b9\u957f\u5927\uff0c\u8fd9\u6837\u7684\u96ea\u666f\u4ece\u5c0f\u5c31\u770b\u60ef\u4e86\u3002\u6211\u4ece\u672a\u5230\u8fc7\u6c5f\u5357\uff0c\u5bf9\u4f60\u4eec\u6c5f\u5357 \u7684\u98ce\u666f\u5012\u662f\u5fc3\u5411\u5f80\u4e4b\u5462\uff01\u201d\u63a5\u7740\u6447\u5934\u6643\u8111\u5730\u5ff5\u9053\uff1a\u201c\u66ae\u6625\u4e09\u6708\uff0c\u6c5f\u5357\u8349\u957f\uff0c\u6742\u82b1\u751f\u6811\uff0c\u7fa4\u83ba \u4e71\u98de\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u7ed9\u4ed6\u9017\u5f97\u8d8a\u53d1\u5fcd\u4fca\u4e0d\u7981\uff0c\u201c\u5657\u55e4\u201d\u7b11\u9053\uff1a\u201c\u77a7\u4e0d\u51fa\u4f60\u5012\u662f\u8bfb\u4e86\u6ee1\u809a\u5b50\u6587\u7ae0\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u6211\u8bfb\u4e66\u4e0d\u591a\uff0c\u53ef\u662f\u524d\u4eba\u8d5e\u7f8e\u6c5f\u5357\u98ce\u7269\u7684\u8bd7\u6587\u5374\u592a\u591a\u4e86\uff0c\u53ea\u8981\u968f\u4fbf\u7ffb\u7ffb\uff0c\u90fd \u53ef\u4ee5\u627e\u5230\u3002\u73b0\u5728\u662f\u4e8c\u6708\u4e2d\u65ec\uff0c\u518d\u8fc7\u534a\u4e2a\u6708\uff0c\u5c31\u662f\u66ae\u6625\u65f6\u8282\u4e86\uff0c\u5728\u8fd9\u91cc\u770b\u5f97\u89c1\u7684\u53ea\u6709\u6885\u82b1\uff0c\u9e1f \u513f\u4e48\uff0c\u66f4\u662f\u8fde\u4e11\u4e4c\u9e26\u90fd\u8eb2\u5230\u5de2\u91cc\uff0c\u83ab\u8bf4\u7fa4\u83ba\u4e71\u98de\u4e86\u3002\u6211\u8bfb\u5230\u7684\u524d\u4eba\u63cf\u5199\u6c5f\u5357\u98ce\u666f\u7684\u6587\u7ae0\uff0c\u5374 \u4e0d\u77e5\u51e0\u65f6\u624d\u80fd\u591f\u4eb2\u81ea\u5230\u6c5f\u5357\u770b\u770b\uff0c\u770b\u770b\u90a3\u6742\u82b1\u751f\u6811\uff0c\u7fa4\u83ba\u4e71\u98de\u7684\u7f8e\u666f\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u7b11\u9053\uff1a\u201c\u8fd9\u8fd8\u4e0d\u5bb9\u6613\uff0c\u4f60\u6765\u6211\u5bb6\u91cc\u505a\u5ba2\u4eba\u5427\uff0c\u6211\u53ef\u4ee5\u7ed9\u4f60\u4f5c\u5411\u5bfc\uff0c\u5e26\u4f60\u6e38\u904d\u6c5f \u5357\uff01\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u771f\u7684\u4e48\uff0c\u90a3\u4e48\u6211\u5148\u8c22\u8c22\u4f60\u4e86\uff0c\u4f46\u76fc\u8fd9\u4e00\u5929\u80fd\u591f\u65e9\u65e5\u6765\u5230\u3002\u201d\u5b5f\u660e\u971e\u542c\u51fa\u4ed6 \u8bdd\u4e2d\u6709\u8bdd\uff0c\u4e0d\u7981\u674f\u8138\u6655\u7ea2\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u7b11\u9053\uff1a\u201c\u4f60\u53c8\u5728\u60f3\u4ec0\u4e48\u5fc3\u4e8b\u4e86\u4e48\uff1f\u201d\u5b5f\u660e\u971e\u9053\uff1a\u201c\u6211\u53ea\u662f\u8bb0\u6302\u7740\u626c\u59d1\u5a18\uff0c\u6211\u7684\u5fc3 \u4e8b\u5df2\u7ecf\u5bf9\u4f60\u8bf4\u4e86\uff0c\u54ea\u8fd8\u6709\u4ec0\u4e48\u5fc3\u4e8b\u4e86\u3002\u5012\u662f\u4f60\u7684\u5fc3\u4e8b\u8fd8\u672a\u5bf9\u6211\u8bf4\u5462\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u5fc3\u5f26\u98a4\u52a8\uff0c\u51e0\u4e4e\u5fcd\u4e0d\u4f4f\u5c31\u8981\u5bf9\u5979\u503e\u5410\u5fc3\u4e8b\u3002\u4f46\u4ed6\u4eec\u76f8\u8bc6\u6bd5\u7adf\u53ea\u6709\u51e0\u5929\uff0c\u5b5f\u660e\u971e\u867d \u8bf4\u662f\u843d\u843d\u5927\u65b9\uff0c\u603b\u4e5f\u8fd8\u6709\u5c11\u5973\u7684\u77dc\u6301\uff0c\u5b5f\u660e\u971e\u65e2\u7136\u6ca1\u6709\u8868\u9732\u5fc3\u4e8b\uff0c\u891a\u4e91\u5cf0\u81ea\u4e5f\u4e0d\u6562\u5192\u6627\u3002\u5f53 \u4e0b\u5b9a\u4e86\u5b9a\u795e\uff0c\u7b11\u9053\uff1a\u201c\u6211\u662f\u6709\u4e00\u6869\u5fc3\u4e8b\uff0c\u5c31\u4e0d\u77e5\u4f60\u80fd\u4e0d\u80fd\u5e2e\u6211\u7684\u5fd9\uff1f\u201d \u3000\u3000\u5b5f\u660e\u971e\u5fc3\u5934\u9e7f\u649e\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8981\u6211\u5e2e\u4ec0\u4e48\u5fd9\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u6211\u60f3\u8bf7\u4f60\u4eec\u7236\u5973\u5e2e\u5fd9\uff0c\u6253\u542c\u4e00\u4e2a\u4eba\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u60f3\u4e0d\u5230\u4ed6\u8bf4\u7684\u4e5f\u662f\u5bfb\u4eba\u4e4b\u4e8b\uff0c\u5fc3\u5934\u65b9\u59cb\u5e73\u9759\u4e0b\u6765\uff0c\u53ef\u4e5f\u6709\u70b9\u5931\u671b\uff0c\u5f53\u4e0b\u95ee\u9053\uff1a \u201c\u4f60\u8981\u6253\u542c\u7684\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u6211\u6709\u4e00\u4f4d\u5e08\u53d4\uff0c\u5931\u8e2a\u5dee\u4e0d\u591a\u4e8c\u5341\u5e74\u4e86\uff0c\u542c\u8bf4\u662f\u9003\u5230\u6c5f\u5357\u53bb\u7684\uff0c\u4e00\u76f4\u6ca1\u6709\u6d88 \u606f\u3002\u5e08\u7236\u5728\u540c\u95e8\u4e4b\u4e2d\uff0c\u4e0e\u4ed6\u6700\u4e3a\u8981\u597d\uff0c\u6781\u4e3a\u6302\u5ff5\u3002\u4ee4\u5c0a\u662f\u540d\u6ee1\u5929\u4e0b\u7684\u6c5f\u5357\u5927\u4fa0\uff0c\u4ea4\u6e38\u5382\u9614\uff0c \u6c5f\u5357\u7684\u6b66\u6797\u4eba\u7269\uff0c\u4ee4\u5c0a\u4e00\u5b9a\u6781\u4e3a\u719f\u6089\uff0c\u662f\u4ee5\u6211\u60f3\u95ee\u4f60\u6253\u542c\u6253\u542c\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u4f60\u8fd9\u4f4d\u5e08\u53d4\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u5e08\u7236\u8fd9\u4e00\u8f88\u90fd\u662f\u4ee5\u2018\u5929\u2019\u5b57\u6392\u884c\uff0c\u56db\u5e08\u53d4\u59d3\u803f\uff0c\u540d\u5929\u98ce\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u6c89\u541f\u534a\u664c\uff0c\u8bf4\u903c\uff1a\u201c\u803f\u5929\u98ce\u8fd9\u540d\u5b57\u6211\u53ef\u6ca1\u6709\u542c\u8fc7\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u6216\u8bb8\u4ed6\u5df2\u7528\u4e86\u5316\u540d\u3002\u4e0d\u8fc7\u4fd7\u8bed\u8bf4\u5f97\u597d\uff1a\u884c\u5bb6\u4e00\u51fa\u624b\uff0c\u5c31\u77e5\u6709\u6ca1\u6709\u3002\u803f\u5e08\u53d4 \u7684\u672c\u9886\u662f\u4e00\u62ff\u4e00\u7684\u9ad8\u624b\uff0c\u4ed6\u5230\u4e86\u6c5f\u5357\uff0c\u9664\u975e\u4ece\u672a\u9732\u8fc7\u534a\u70b9\u6b66\u529f\uff0c\u5426\u5219\u603b\u4f1a\u6709\u4eba\u77e5\u9053\u3002\u4ee4\u5c0a\u662f \u6c5f\u5357\u7684\u6b66\u6797\u6cf0\u6597\uff0c\u6c5f\u5357\u7684\u6b66\u6797\u4eba\u58eb\uff0c\u82e5\u7136\u53d1\u73b0\u6709\u5916\u6765\u9ad8\u624b\uff0c\u60f3\u5fc5\u4e5f\u4f1a\u544a\u8bc9\u4ed6\u5427\u3002\u5b5f\u59d1\u5a18\uff0c\u4f60 \u4ece\u8fd9\u65b9\u9762\u56de\u60f3\u4e00\u4e0b\uff0c\u662f\u5426\u542c\u8fc7\u6709\u8fd9\u6837\u7684\u4eba\u7269\uff1f\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u5317\u65b9\u907f\u96be\u6765\u7684\u9ad8\u624b\u5012\u662f\u6709\u597d\u51e0\u4e2a\uff0c\u4f46\u4ed6\u4eec\u7684\u6765\u5386\uff0c\u7239\u7239\u90fd\u662f\u77e5\u9053\u5f97\u5f88\u6e05\u695a \u7684\uff0c\u4f3c\u4e4e\u548c\u4f60\u8bf4\u7684\u8fd9\u4f4d\u5e05\u53d4\u90fd\u4e0d\u7b26\u5408\u3002\u201d\u5f53\u4e0b\u8bf4\u51fa\u90a3\u51e0\u4e2a\u4eba\u7684\u59d3\u540d\u3001\u6765\u5386\u3001\u5e74\u5c81\u3001\u6d3e\u522b\uff0c\u679c \u7136\u90fd\u4e0d\u662f\u803f\u5929\u98ce\u3002 \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u800c\u4e14\u6211\u7239\u7239\u548c\u4f60\u5e08\u53d4\u76f8\u8bc6\uff0c\u4f60\u8fd9\u4f4d\u5e08\u53d4\u5931\u8e2a\u4e4b\u4e8b\uff0c\u60f3\u5fc5\u4ed6\u4e5f\u66fe\u5f97\u77e5\u3002\u82e5\u662f \u5f97\u5230\u4ec0\u4e48\u98ce\u58f0\uff0c\u6216\u662f\u78b0\u4e0a\u53ef\u7591\u7684\u4eba\u7269\uff0c\u4ed6\u4e00\u5b9a\u4f1a\u4eb2\u81ea\u53bb\u67e5\u4e2a\u6c34\u843d\u77f3\u51fa\uff0c\u53ef\u662f\u4ed6\u4ece\u6765\u6ca1\u6709\u548c\u6211 \u63d0\u8fc7\u8fd9\u4ef6\u4e8b\u60c5\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u803f\u5e08\u53d4\u5f53\u5e74\u4f55\u6545\u5931\u8e2a\uff0c\u5bb6\u5e08\u4e5f\u4ece\u672a\u548c\u6211\u8c08\u8fc7\u3002\u6211\u53ea\u77e5\u9053\u4ed6\u51fa\u9053\u672a\u4e45\u4fbf\u5931\u8e2a \u4e86\u3002\u6b64\u4e8b\u4ee4\u5c0a\u4e0d\u77e5\u4ea6\u6709\u53ef\u80fd\u3002\u4e0d\u8fc7\uff0c\u4f60\u4e5f\u8bf4\u5f97\u6709\u7406\uff0c\u4ee4\u5c0a\u81f3\u5c11\u662f\u89c1\u8fc7\u6211\u8fd9\u4e00\u95e8\u6d3e\u7684\u529f\u592b\u7684\uff0c \u5018\u82e5\u4ed6\u53d1\u73b0\u6c5f\u5357\u6709\u8fd9\u6837\u4e00\u4f4d\u5916\u6765\u9ad8\u624b\uff0c\u5b9a\u4f1a\u53bb\u67e5\u8bbf\u7684\uff0c\u5373\u4f7f\u67e5\u8bbf\u4e0d\u5230\uff0c\u4e5f\u4f1a\u5f15\u4ee5\u4e3a\u5947\u544a\u8bc9\u5bb6 \u5e08\u7684\u3002\u65e2\u7136\u4ee4\u5c0a\u4ece\u672a\u63d0\u8fc7\uff0c\u60f3\u5fc5\u8fd9\u4f4d\u803f\u5e08\u53d4\u4e0d\u662f\u9690\u59d3\u57cb\u540d\uff0c\u5c31\u662f\u5df2\u7ecf\u4e0d\u5728\u4eba\u95f4\u4e86\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u82e5\u6709\u6240\u601d\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u5ffd\u5730\u95ee\u9053\uff1a\u201c\u6628\u665a\u4f60\u4e0e\u9633\u575a\u767d\u8fd9\u53ae\u4ea4\u624b\uff0c\u6c60\u548c\u4f60\u5bf9\u4e86\u4e00 \u638c\uff0c\u5f88\u662f\u5403\u60ca\uff0c\u5f53\u4f60\u597d\u50cf\u5632\u7b11\u4ed6\u7684\u4e00\u79cd\u529f\u592b\u7ec3\u5f97\u5c1a\u672a\u5230\u5bb6\uff0c\u4f60\u8bf4\u7684\u662f\u4e09\u4e2a\u5b57\uff0c\u5176\u65f6\u6211\u6b63\u53d7\u8d3a \u4e5d\u516c\u7684\u6697\u7b97\uff0c\u542c\u5f97\u4e0d\u5927\u4ed4\u7ec6\uff0c\u662f\u4e0d\u662f\u53eb\u505a\u5929\u3001\u5929\u96f7\u529f\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u8bf4\u5f97\u4e00\u4e2a\u5b57\u90fd\u6ca1\u9519\u5440\uff0c\u662f\u53eb\u505a\u5929\u96f7\u529f\u3002\u8fd9\u662f\u672c\u95e8\u7edd\u6280\u4e4b\u4e00\uff0c\u4e0e\u5c11 \u6797\u5bfa\u7684\u5927\u529b\u91d1\u521a\u638c\u5f02\u66f2\u540c\u529f\uff0c\u7ec3\u5f97\u6210\u529f\u7684\u4eba\u5982\u662f\u6781\u5c11\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u540d\u53eb\u5929\u96f7\u529f\uff0c\u662f\u4e0d\u662f\u9633\u5929\u96f7\u521b\u7684\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u4e0d\u662f\uff0c\u8fd9\u662f\u672c\u95e8\u59cb\u7956\u6240\u521b\uff0c\u672c\u95e8\u53f7\u79f0\u5251\u638c\u53cc\u7edd\uff0c\u4f46\u6700\u91cd\u89c6\u7684\u8fd8\u662f\u638c\u4e0a\u7684\u5929 \u96f7\u529f\u3002\u5bb6\u5e08\u56db\u4e2a\u540c\u95e8\u4e4b\u4e2d\uff0c\u4ee5\u9633\u5929\u96f7\u7ec3\u5f97\u6700\u597d\uff0c\u5e08\u7956\u8ba4\u4e3a\u4ed6\u53ef\u4ee5\u80dc\u8fc7\u524d\u4eba\uff0c\u56e0\u6b64\u624d\u7acb\u4ed6\u4e3a\u638c \u95e8\u5f1f\u5b50\u7684\u3002\u6070\u597d\u4ed6\u90a3\u4e00\u8f88\u4ee5\u2018\u5929\u2019\u5b57\u6392\u884c\uff0c\u4ed6\u7684\u2018\u5929\u96f7\u529f\u2019\u6700\u597d\uff0c\u5c31\u7d22\u6027\u8d50\u4ed6\u4ee5\u2018\u5927\u96f7\u2019\u4e3a \u540d\uff0c\u8fd9\u5bf9\u4ed6\u672c\u662f\u6b8a\u8363\uff0c\u4f46\u60f3\u4e0d\u5230\u7956\u5e08\u5bf9\u4ed6\u671f\u671b\u5982\u662f\u4e4b\u6bb7\uff0c\u4ed6\u5374\u975e\u4f46\u4e0d\u80fd\u5149\u5927\u95e8\u6237\uff0c\u53cd\u800c\u505a\u51fa \u4e86\u6b3a\u5e08\u706d\u7956\u3001\u5356\u56fd\u6c42\u8363\u4e4b\u4e8b\uff0c\u5bb6\u5e08\u5e38\u8bf4\u597d\u5728\u5e08\u7956\u6b7b\u5f97\u65e9\uff0c\u4e0d\u77e5\u9633\u65e0\u96f7\u540e\u6765\u53d8\u5f97\u5982\u6b64\uff0c\u5426\u5219\u4ed6 \u5728\u4e5d\u6cc9\u4e4b\u4e0b\uff0c\u4e5f\u96be\u7791\u76ee\uff01\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u591a\u884c\u4e0d\u4e49\u5fc5\u81ea\u6bd9\uff0c\u9633\u5929\u96f7\u6b66\u529f\u518d\u9ad8\uff0c\u4e5f\u672a\u5fc5\u5c31\u80fd\u5982\u5fc3\u6240\u613f\u3002\u4f60\u7684\u5e08\u7236\u4e0d\u4f1a \u653e\u8fc7\u4ed6\uff0c\u6211\u7684\u7239\u7239\u4e5f\u4e0d\u4f1a\u653e\u8fc7\u4ed6\u7684\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u4f60\u8bf4\u5f97\u4e0d\u9519\uff0c\u9633\u5929\u96f7\u591a\u884c\u4e0d\u4e49\u5fc5\u81ea\u6bd9\uff0c\u603b\u6709\u4e00\u5929\uff0c\u4f1a\u81ea\u98df\u5176\u679c\u7684\u3002\u4e0d\u8fc7\u5bb6 \u5e08\u5374\u60f3\u4eb2\u81ea\u6e05\u7406\u95e8\u6237\uff0c\u662f\u4ee5\u5fc5\u987b\u627e\u5f97\u56db\u5e08\u53d4\u8054\u624b\u624d\u884c\u3002\u201d\u6b66\u6797\u89c4\u77e9\uff0c\u6e05\u7406\u95e8\u6237\uff0c\u53ea\u80fd\u662f\u672c\u6d3e \u4e2d\u4eba\u8054\u624b\uff0c\u4e0d\u80fd\u9080\u8bf7\u5916\u4eba\u5e2e\u5fd9\u7684\u3002 \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u54b1\u4eec\u6682\u4e14\u4e0d\u8c08\u9633\u5929\u96f7\u4e4b\u4e8b\uff0c\u4e91\u5cf0\uff0c\u6211\u5012\u662f\u60f3\u518d\u770b\u4e00\u770b\u4f60\u7684\u5929\u96f7\u529f\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u6211\u7684\u5929\u96f7\u529f\u4e0d\u8fc7\u624d\u6709\u4e03\u6210\u706b\u5019\uff0c\u6628\u665a\u5bf9\u4ed8\u9633\u575a\u767d\u8fd9\u53ae\uff0c\u662f\u4e0d\u5f97\u5df2\u624d\u4f7f\u51fa\u6765 \u7684\uff0c\u4f55\u5fc5\u8fd8\u8981\u6211\u732e\u62d9\u5462\uff1f\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u4f60\u4e0d\u5fc5\u5ba2\u6c14\u4e86\uff0c\u6f14\u7ed9\u6211\u770b\u770b\u5427\uff0c\u8bf7\u4f60\u6253\u8fd9\u68f5\u5927\u6811\u8bd5\u8bd5\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u7b11\u9053\uff1a\u201c\u65e2\u7136\u5c0f\u59d0\u6709\u547d\uff0c\u90a3\u6211\u53ea\u597d\u732e\u4e11\u4e86\u3002\u201d\u5f53\u4e0b\u6325\u638c\u5212\u4e86\u4e00\u9053\u5706\u5f27\uff0c\u9690\u9690\u631f\u7740 \u98ce\u96f7\u4e4b\u58f0\uff0c\u4e00\u638c\u51fb\u4e0b\u3002\u90a3\u68f5\u5927\u6811\u7eb9\u4e1d\u4e0d\u52a8\uff0c\u4f46\u8fc7\u4e86\u4e00\u4f1a\uff0c\u53ea\u542c\u5f97\u5580\u5587\u5580\u5587\u4e00\u7247\u58f0\u54cd\uff0c\u6811\u4e0a\u7684 \u6570\u5341\u679d\u6811\u679d\u5c3d\u90fd\u6298\u65ad\u3002\u6811\u53f6\u672c\u6765\u662f\u65e9\u5df2\u843d\u5c3d\u7684\uff0c\u6b64\u65f6\u53ea\u5269\u4e00\u68f5\u5149\u79c3\u79c3\u7684\u6811\u5e72\uff01 \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u82e5\u662f\u5bb6\u5e08\u7528\u8fd9\u5929\u96f7\u529f\uff0c\u4e00\u638c\u53ef\u4ee5\u5288\u5012\u5927\u6811\uff0c\u6211\u7684\u529f\u592b\u5dee\u5f97\u592a\u8fdc\u4e86\u3002\u201d\u5b5f\u660e \u971e\u5fc3\u60f3\u9633\u5929\u96f7\u7684\u529f\u592b\u8fd8\u5728\u4ed6\u7684\u5e08\u7236\u4e4b\u4e0a\uff0c\u4e0d\u7981\u9a87\u7136\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u89c9\u5f97\u6709\u70b9\u5947\u602a\uff0c\u95ee\u9053\uff1a\u201c\u660e\u971e\uff0c\u4f55\u4ee5\u4f60\u5bf9\u5929\u96f7\u529f\u8fd9\u6837\u611f\u5230\u5174\u8da3\uff1f\u201d \u3000\u3000\u5b5f\u660e\u971e\u82e5\u6709\u6240\u601d\uff0c\u534a\u664c\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4e0d\u9519\u3002\u62db\u5f0f\u548c\u529f\u592b\u90fd\u662f\u4e00\u6837\uff0c\u786e\u662f\u5929\u96f7\u529f\u65e0\u7591 \u4e86\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u8be7\u9053\uff1a\u201c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u6211\u66fe\u7ecf\u89c1\u8fc7\u4e00\u4e2a\u4eba\u4f1a\u4f7f\u5929\u96f7\u529f\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u8fde\u5fd9\u95ee\u9053\uff1a\u201c\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u8fd9\u4eba\u662f\u6211\u5728\u56db\u5e74\u7684\u78b0\u4e0a\u7684\uff0c\u5f53\u65f6\u4ed6\u4e0d\u8fc7\u662f\u4e2a\u4e8c\u5341\u5c81\u5de6\u53f3\u7684\u5c11\u5e74\uff0c\u5f53\u7136\u4e0d\u662f \u4f60\u7684\u56db\u5e08\u53d4\u3002\u4e0d\u8fc7\uff0c\u4ed6\u65e2\u7136\u4f1a\u4f7f\u5929\u96f7\u529f\uff0c\u8bf4\u4e0d\u5b9a\u662f\u4f60\u56db\u5e08\u53d4\u7684\u95e8\u4eba\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u5927\u559c\u9053\uff1a\u201c\u771f\u7684\u4e48\uff0c\u4f60\u600e\u6837\u78b0\u4e0a\u4ed6\u7684\uff0c\u4e8b\u60c5\u7ecf\u8fc7\u5982\u4f55\uff0c\u5feb\u8bf4\u7ed9\u6211\u542c\u542c\uff01\u201d \u3000\u3000\u4e00\u5e55\u5f80\u4e8b\uff0c\u7ffb\u4e0a\u5fc3\u5934\u3002\u56db\u5e74\u524d\u5b5f\u660e\u971e\u5e74\u65b9\u5341\u4e03\uff0c\u5bb6\u4f20\u6b66\u827a\uff0c\u521a\u521a\u7ec3\u6210\u3002 \u3000\u3000\u8fd9\u4e00\u5e74\u5979\u7236\u4eb2\u5b5f\u5c11\u521a\u5230\u5317\u65b9\u4f1a\u53cb\uff0c\u56e0\u4e3a\u5b5f\u660e\u971e\u672c\u9886\u5c1a\u6d45\uff0c\u6df1\u5165\u654c\u540e\uff0c\u6050\u6709\u610f\u5916\uff0c\u662f\u4ee5\u6ca1 \u6709\u5e26\u5979\u540c\u884c\u3002 \u3000\u3000\u5b5f\u5c11\u521a\u6709\u4e2a\u5802\u59ca\uff0c\u8fdc\u5ac1\u5ddd\u897f\uff0c\u4e08\u592b\u662f\u6709\u540d\u7684\u5ddd\u897f\u5927\u4fa0\u4e25\u58f0\u6d9b\uff0c\u6709\u4e2a\u5973\u513f\uff0c\u540d\u5524\u4e25\u70f7\uff0c\u62dc \u5ce8\u5d4b\u6d3e\u7684\u65e0\u76f8\u795e\u5c3c\u4e3a\u5e08\uff0c\u5e74\u7eaa\u6bd4\u5b5f\u660e\u971e\u5927\u4e24\u5c81\uff0c\u5df2\u7ecf\u5b66\u6210\u51fa\u5e08\uff0c\u5f85\u5b57\u95fa\u4e2d\u3002 \u3000\u3000\u5b5f\u5c11\u521a\u6050\u5973\u513f\u5728\u5bb6\u5bc2\u5bde\uff0c\u5c31\u53eb\u5979\u5230\u5ddd\u897f\u63a2\u4eb2\uff0c\u4e5f\u597d\u89c1\u89c1\u8fd9\u4f4d\u4ece\u672a\u89c1\u8fc7\u9762\u7684\u8868\u59ca\u3002\u6b63\u662f\uff1a \u3000\u3000\u63a2\u4eb2\u6709\u5947\u9047\uff0c\u59fb\u7f18\u4e00\u7ebf\u7275\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b\u2014\u2014\u7b2c\u4e09\u5341\u4e8c\u56de\u3000\u4e25\u4eb2\u4e0d\u8c05\u5fc3\u8339\u82e6\u3000\u7231\u4fa3\u8f7b\u79bb\u610f\u81ea\u4f24 \u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b \u7b2c\u4e09\u5341\u4e8c\u56de\u3000\u4e25\u4eb2\u4e0d\u8c05\u5fc3\u8339\u82e6\u3000\u7231\u4fa3\u8f7b\u79bb\u610f\u81ea\u4f24 \u3000\u3000\u8fd9\u4e00\u5929\u5b5f\u660e\u971e\u5230\u4e86\u59d1\u6bcd\u5bb6\u91cc\uff0c\u59d1\u4f84\u76f8\u89c1\uff0c\u81ea\u6709\u4e00\u756a\u6b22\u559c\uff0c\u4e0d\u5fc5\u7ec6\u8868\u3002 \u3000\u3000\u5b5f\u660e\u971e\u5374\u6709\u70b9\u5947\u602a\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4e3a\u4f55\u4e0d\u89c1\u59d1\u7236\u548c\u8868\u59d0\u5462\uff1f\u201d\u6b63\u8981\u52a8\u95ee\uff0c\u53ea\u53f9\u5f97\u59d1\u6bcd\u5df2 \u5728\u5429\u5490\u4e00\u4e2a\u4e2b\u9b1f\u9053\uff1a\u201c\u4f60\u53bb\u8bf7\u5c0f\u59d0\u51fa\u6765\u3002\u201d\u5b5f\u660e\u971e\u9053\uff1a\u201c\u59d1\u7236\u4e0d\u5728\u5bb6\u4e48\uff1f\u201d \u3000\u3000\u59d1\u6bcd\u9053\uff1a\u201c\u521a\u624d\u6070\u5de7\u6765\u4e86\u4e00\u4f4d\u5ba2\u4eba\uff0c\u8fd9\u4f4d\u5ba2\u4eba\u548c\u4f60\u7684\u8868\u59d0\u4e5f\u662f\u76f8\u8bc6\u7684\uff0c\u5979\u6b63\u5728\u968f\u7740\u5979\u7239 \u966a\u5ba2\u3002\u201d\u5b5f\u660e\u971e\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u5f85\u5ba2\u4eba\u8d70\u4e86\uff0c\u6211\u62dc\u89c1\u8868\u59d0\u4e5f\u8fd8\u4e0d\u8fdf\u3002\u201d\u59d1\u6bcd\u9053\uff1a\u201c\u90a3\u662f\u4e00 \u4f4d\u7537\u5ba2\u4eba\uff0c\u4f60\u8868\u59d0\u662f\u4e2a\u5973\u5b69\u513f\u5bb6\uff0c\u4e0d\u4fbf\u4e45\u966a\uff0c\u548c\u5ba2\u4eba\u89c1\u4e0a\u4e00\u89c1\uff0c\u4e5f\u7b97\u5c3d\u4e86\u793c\u6570\u3002\u6211\u672c\u6765\u5c31\u60f3 \u53eb\u5979\u51fa\u6765\u7684\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u5728\u7236\u4eb2\u8ddf\u524d\u4e00\u5411\u662f\u653e\u7eb5\u60ef\u7684\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u7537\u5973\u53c8\u6709\u4ec0\u4e48\u5206\u522b\uff0c\u7239\u7239\u7684\u670b\u53cb\u6765 \u4e86\uff0c\u4ece\u6765\u6ca1\u6709\u53eb\u6211\u56de\u907f\u7684\uff0c\u60f3\u4e0d\u5230\u59d1\u6bcd\u7adf\u662f\u5982\u6b64\u62d8\u6ce5\u793c\u6cd5\u3002\u201d \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u5979\u7684\u8868\u59d0\u4e25\u70f7\u6765\u5230\uff0c\u8868\u59d0\u59b9\u95fb\u540d\u5df2\u4e45\uff0c\u5374\u8fd8\u662f\u521d\u6b21\u89c1\u9762\u3002\u5b5f\u660e\u971e\u5176\u65f6\u5c1a\u672a\u7ed3 \u8bc6\u5c60\u51e4\uff0c\u5e76\u65e0\u5e74\u7eaa\u76f8\u82e5\u7684\u670b\u53cb\uff0c\u89c1\u8868\u59d0\u957f\u5f97\u53c8\u7f8e\u53c8\u6709\u672c\u4e8b\uff0c\u5f53\u7136\u5341\u5206\u6b22\u559c\uff0c\u62c9\u7740\u8868\u59d0\u95ee\u957f\u95ee \u77ed\u3002\u4e25\u70f7\u56fa\u7136\u4e5f\u5f88\u9ad8\u5174\uff0c\u4f46\u8c08\u8bdd\u4e4b\u65f6\uff0c\u76ee\u5149\u6d41\u6563\uff0c\u4f3c\u4e4e\u6709\u70b9\u5fc3\u795e\u4e0d\u5c5e\u7684\u6837\u5b50\u3002 \u3000\u3000\u5b5f\u660e\u971e\u90a3\u5e74\u4e0d\u8fc7\u662f\u4e2a\u5341\u516d\u4e03\u5c81\u7684\u5c0f\u59d1\u5a18\uff0c\u5c1a\u672a\u5f88\u61c2\u4eba\u4e8b\uff0c\u4f46\u4e5f\u731c\u60f3\u5f97\u5230\u8868\u59d0\u662f\u6709\u5fc3\u4e8b\u3002 \u53ea\u56e0\u521d\u6b21\u89c1\u9762\uff0c\u4e0d\u4fbf\u52a8\u95ee\u3002 \u3000\u3000\u59d1\u6bcd\u7b11\u9053\uff1a\u201c\u4eca\u5929\u4e00\u65e9\uff0c\u6211\u5c31\u542c\u5f97\u559c\u9e4a\u5431\u5431\u55b3\u55b3\u5730\u53eb\uff0c\u679c\u7136\u5c31\u6765\u4e86\u4e24\u4f4d\u7a00\u5ba2\u3002\u4f60\u8868\u59b9\u8ba4 \u8001\u8fdc\u7684\u82cf\u5dde\u8dd1\u6765\uff0c\u66f4\u662f\u96be\u5f97\uff0c\u4f60\u6709\u8868\u59b9\u4f5c\u4f34\uff0c\u53ef\u4ee5\u5b89\u5b89\u9759\u9759\u5730\u5728\u5bb6\u4e2d\u4f4f\u4e0b\u6765\u5566\u3002\u660e\u971e\uff0c\u4f60\u4e0d \u77e5\u9053\u4f60\u7684\u8868\u59d0\u6027\u5b50\u591a\u91ce\uff0c\u5979\u8001\u662f\u7f20\u6211\uff0c\u8981\u6211\u8ba9\u5979\u5728\u6c5f\u6e56\u884c\u8d70\u3002\u6211\u8bf4\u4e00\u4e2a\u5973\u5b69\u513f\u5bb6\uff0c\u5b66\u6210\u6b66 \u827a\uff0c\u4e3b\u8981\u662f\u7528\u6765\u9632\u8eab\u3002\u884c\u4fa0\u4ed7\u4e49\u5e94\u8be5\u662f\u7537\u4eba\u7684\u4e8b\u60c5\uff0c\u5973\u5b69\u513f\u5bb6\uff0c\u7ecf\u5e38\u629b\u5934\u9732\u9762\uff0c\u603b\u662f\u4e0d \u5b9c\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u4e0d\u4fbf\u9a73\u5979\u59d1\u6bcd\uff0c\u5f53\u4e0b\u7b11\u9053\uff1a\u201c\u6211\u7684\u6027\u5b50\u4e5f\u662f\u5f88\u91ce\u7684\uff0c\u7239\u7239\u81ea\u5c0f\u5c31\u5e26\u6211\u5230\u5916\u9762\u4e71 \u95ef\uff0c\u8fd9\u6b21\u4ed6\u5230\u91d1\u864f\u7684\u5317\u65b9\uff0c\u4e0d\u80af\u5e26\u6211\u53bb\uff0c\u6211\u8fd8\u548c\u4ed6\u5435\u4e86\u4e00\u67b6\u5462\u3002\u201d \u3000\u3000\u59d1\u6bcd\u7709\u5934\u7565\u76b1\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u5e74\u7eaa\u8fd8\u5c0f\uff0c\u629b\u5934\u9732\u9762\uff0c\u5c1a\u5c5e\u65e0\u59a8\u3002\u518d\u8fc7\u4e24\u5e74\uff0c\u6211\u770b\u4f60\u7684\u7239\u7239 \u4e5f\u8be5\u7ea6\u675f\u7ea6\u675f\u4f60\u4e86\u3002\u201d \u3000\u3000\u4e25\u70f7\u9053\uff1a\u201c\u5988\uff0c\u8868\u59b9\u521a\u6765\u5462\uff0c\u4f60\u5c31\u6559\u8bad\u8d77\u5979\u6765\u4e86\u3002\u201d \u3000\u3000\u59d1\u6bcd\u7b11\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u4eec\u5c0f\u4e00\u8f88\u5c31\u662f\u5acc\u6211\u4eec\u8001\u4e00\u8f88\u7684\u7f57\u55e6\uff0c\u53ef\u4e0d\u77e5\u6211\u6b63\u662f\u4e3a\u4e86\u75bc\u4f60\uff0c\u624d \u4e0d\u80fd\u4e0d\u591a\u8bf4\u51e0\u53e5\u5462\uff0c\u971e\u513f\uff0c\u6211\u548c\u4f60\u7239\u867d\u7136\u4e0d\u662f\u540c\u80de\u59d0\u5f1f\uff0c\u4f46\u6211\u4e5f\u53ea\u6709\u4f60\u7239\u4e00\u4e2a\u4eb2\u4eba\u4e86\uff0c\u6211\u5bf9 \u4f60\u5c31\u5982\u5bf9\u4eb2\u751f\u5973\u513f\u4e00\u6837\uff0c\u4f60\u8be5\u4e0d\u4f1a\u5acc\u4f60\u59d1\u5988\u7f57\u55e6\u5427\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u542c\u5f97\u5f88\u4e0d\u8212\u670d\uff0c\u4f46\u5374\u4e0d\u80fd\u4e0d\u8bf4\uff1a\u201c\u591a\u8c22\u59d1\u5988\u7684\u6559\u8bad\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u7684\u59d1\u6bcd\u672c\u6765\u8fd8\u8981\u518d\u8bf4\u4e0b\u53bb\uff0c\u5ffd\u542c\u5f97\u811a\u6b65\u58f0\u54cd\uff0c\u539f\u6765\u662f\u4e25\u58f0\u6d9b\u6b63\u5728\u9001\u5ba2\u51fa\u95e8\u3002 \u3000\u3000\u5b5f\u660e\u971e\u9694\u7a97\u671b\u53bb\uff0c\u53ea\u89c1\u8fd9\u4e2a\u5ba2\u4eba\u4e43\u662f\u4e00\u4e2a\u4e30\u795e\u4fca\u79c0\u7684\u5c11\u5e74\u3002\u6b64\u65f6\u5979\u7684\u8868\u59d0\u4e25\u70f7\u4e5f\u4e0d\u77e5\u4e0d \u89c9\u7684\u6328\u8fd1\u7a97\u53e3\uff0c\u75f4\u75f4\u7684\u5411\u5916\u5f20\u671b\u3002 \u3000\u3000\u4e25\u592b\u4eba\u54b3\u4e86\u4e00\u58f0\uff0c\u53eb\u9053\uff1a\u201c\u70f7\u513f\uff0c\u8fc7\u6765\uff01\u201d\u4e25\u70f7\u9762\u4e0a\u4e00\u7ea2\uff0c\u79bb\u5f00\u7a97\u53e3\uff0c\u8bf4\u9053\uff1a\u201c\u5988\u6709\u4ec0 \u4e48\u5429\u5490\uff1f\u201d \u3000\u3000\u4e25\u592b\u4eba\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\uff0c\u4f60\u7ed9\u6211\u5012\u4e00\u676f\u8336\u6765\u3002\u201d\u5176\u5b9e\u4e25\u5bb6\u6709\u7684\u662f\u5927\u5c0f\u4e2b\u9b1f\uff0c\u5979\u53eb\u5973\u513f\u5012 \u8336\uff0c\u4e0d\u8fc7\u662f\u8981\u628a\u5973\u513f\u652f\u5f00\u800c\u5df2\u3002 \u3000\u3000\u4e25\u58f0\u6d9b\u9001\u5ba2\u56de\u6765\uff0c\u8fdb\u5165\u5ba2\u5385\u4e0e\u5b5f\u660e\u971e\u76f8\u89c1\u3002\u4e25\u592b\u4eba\u9053\uff1a\u201c\u5ba2\u4eba\u8d70\u4e86\uff1f\u201d\u4e25\u58f0\u6d9b\u9053\uff1a\u201c\u662f \u5440\uff0c\u6211\u6ca1\u7559\u4ed6\uff0c\u4ed6\u5750\u5f97\u81ea\u89c9\u6ca1\u8da3\uff0c\u53ea\u597d\u8d70\u4e86\u3002\u201d\u8bf4\u7f62\uff0c\u53f9\u4e86\u53e3\u6c14\uff0c\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u8bf4\u8d77\u6765\u6211\u5012 \u662f\u6b20\u4e86\u4ed6\u4e00\u4efd\u4eba\u60c5\u5462\uff0c\u4f46\u8fd9\u4e5f\u662f\u65e0\u53ef\u5948\u4f55\u4e4b\u4e8b\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u542c\u5f97\u83ab\u540d\u5176\u5999\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u59d1\u7236\u65e2\u7136\u6b20\u4ed6\u4eba\u60c5\uff0c\u4f55\u4ee5\u5bf9\u4ed6\u5982\u6b64\u51b7\u8c08\uff1f\u65e0\u53ef\u5948\u4f55 \u4e4b\u4e8b\uff0c\u4e0d\u77e5\u53c8\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e25\u70f7\u7aef\u8336\u51fa\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u7239\uff0c\u8fd9\u4eba\u8fdc\u9053\u800c\u6765\uff1b\u4f60\u4e5f\u4e0d\u7559\u4ed6\u4f4f\u4e00\u4e24\u5929\u3002\u201d\u4e25\u58f0\u6d9b\u6de1\u6de1\u8bf4 \u9053\uff1a\u201c\u5bb6\u4e2d\u90fd\u662f\u5973\u7737\uff0c\u4e0d\u4fbf\u7559\u5ba2\u3002\u201d\u5b5f\u660e\u971e\u5fc3\u60f3\u539f\u6765\u59d1\u7236\u4e5f\u662f\u8fd9\u6837\u53e4\u677f\uff0c\u548c\u59d1\u6bcd\u5bf9\u6b63\u597d\u662f\u4e00 \u5bf9\u3002 \u3000\u3000\u4e25\u592b\u4eba\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\u5c31\u662f\u4f60\u4eec\u5728\u5c0f\u91d1\u5ddd\u7ed3\u8bc6\u7684\u90a3\u4e2a\u5c11\u5e74\u5417\uff1f\u770b\u4ed6\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u60f3\u4e0d\u5230\u7adf\u6709 \u90a3\u4e48\u5927\u7684\u672c\u4e8b\uff1f\u201d \u3000\u3000\u4e25\u58f0\u6d9b\u9053\uff1a\u201c\u90a3\u6b21\u82e5\u4e0d\u662f\u591a\u4e8f\u4e86\u4ed6\uff0c\u6211\u548c\u70f7\u513f\u867d\u7136\u672a\u5fc5\u6709\u6027\u547d\u4e4b\u5fe7\uff0c\u4f46\u53ea\u6015\u4e5f\u662f\u8981\u8d25\u5728 \u6ec7\u5357\u4e03\u864e\u7684\u624b\u4e0b\u4e86\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u542c\u4e86\u5927\u611f\u60ca\u5947\uff0c\u8981\u77e5\u5979\u7684\u59d1\u7236\u53f7\u79f0\u5ddd\u897f\u5927\u4fa0\uff0c\u6b66\u529f\u4e4b\u9ad8\uff0c\u4eba\u6240\u7686\u77e5\u3002\u4f46\u542c\u4ed6\u6240 \u8bf4\uff0c\u90a3\u6b21\u7684\u4e8b\u60c5\uff0c\u4f3c\u4e4e\u8fd8\u662f\u9760\u4e86\u8fd9\u4e2a\u5c11\u5e74\u62d4\u5200\u76f8\u52a9\uff0c\u65b9\u80fd\u8131\u9669\u3002 \u3000\u3000\u4e25\u58f0\u6d9b\u5ffd\u9053\uff1a\u201c\u660e\u971e\uff0c\u4f60\u6709\u4e86\u5a46\u5bb6\u6ca1\u6709\uff1f\u201d\u5b5f\u660e\u971e\u6ee1\u9762\u901a\u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u627e\u5e74\u7eaa\u8fd8\u5c0f\u5462\uff0c \u7239\u4e5f\u4ece\u6765\u6ca1\u6709\u548c\u6211\u8c08\u8fc7\u3001\u8c08\u8fc7\u8fd9\u4e2a\u3002\u201d\u4e25\u58f0\u6d9b\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u4f60\u7239\u662f\u672a\u66fe\u5c06\u4f60\u8bb8\u4eba\u4e86\u3002\u201d \u3000\u3000\u4e25\u592b\u4eba\u7b11\u9053\uff1a\u201c\u4f60\u662f\u60f3\u7ed9\u971e\u513f\u505a\u5a92\u4e48\uff1f\u201d\u4e25\u58f0\u6d9b\u6b63\u8272\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\u3002\u773c\u524d\u5c31\u6709\u4e00\u4e2a\u975e\u5e38 \u5408\u9002\u7684\u4eba\uff0c\u53ef\u60dc\u5c11\u521a\u4e0d\u77e5\u4ec0\u4e48\u65f6\u5019\u56de\u6765\uff0c\u6211\u4e5f\u6050\u6015\u6ca1\u6709\u529f\u592b\u62bd\u8eab\u5230\u6c5f\u5357\u53bb\u770b\u4ed6\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u8138\u7ea2\u76f4\u900f\u8033\u6839\uff0c\u8fde\u8fde\u6447\u5934\u8bf4\u9053\uff1a\u201c\u4e0d\u8981\uff0c\u4e0d\u8981\u3002\u6211\u8981\u8ddf\u7239\u7239\u4e00\u8f88\u5b50\u7684\u3002\u201d\u4e25\u592b\u4eba \u7b11\u9053\uff1a\u201c\u8fd9\u662f\u5c0f\u5b69\u5b50\u7684\u8bf4\u8bdd\u3002\u4e0d\u8fc7\u971e\u513f\u786e\u662f\u5e74\u7eaa\u8fd8\u5c0f\uff0c\u8fd9\u4e8b\u6162\u6162\u518d\u8bf4\u4e5f\u8fd8\u4e0d\u8fdf\u3002\u201d \u3000\u3000\u4e25\u592b\u4eba\u56de\u5230\u539f\u6765\u7684\u8bdd\u9898\uff0c\u95ee\u9053\uff1a\u201c\u90a3\u4eba\u6765\u4f5c\u4ec0\u4e48\uff0c\u662f\u5426\u631f\u6069\u800c\u6765\uff0c\u6b32\u6709\u6240\u6c42\u3002\u201d\u4e25\u58f0\u6d9b \u7b11\u9053\uff1a\u201c\u4f60\u731c\u5bf9\u4e86\u4e00\u534a\u3002\u201d\u5b5f\u660e\u971e\u5fcd\u4e0d\u4f4f\u597d\u5947\u5fc3\uff0c\u95ee\u9053\uff1a\u201c\u59d1\u7236\uff0c\u4f60\u521a\u624d\u8bf4\u7684\u90a3\u4e2a\u6545\u4e8b\u8fd8\u672a \u8bf4\u5b8c\u5462\u3002\u201d \u3000\u3000\u4e25\u58f0\u6d9b\u9053\uff1a\u201c\u5bf9\uff0c\u8fd9\u4ef6\u4e8b\u60c5\u8bf4\u7ed9\u4f60\u542c\u542c\u4e5f\u597d\uff0c\u597d\u8ba9\u4f60\u4eec\u5c0f\u4e00\u8f88\u7684\u77e5\u9053\u6c5f\u6e56\u9669\u6076\uff0c\u5929\u5916\u6709 \u5929\uff0c\u4eba\u5916\u6709\u4eba\u3002 \u3000\u3000\u201c\u4eca\u5e74\u6625\u521d\u6211\u548c\u4f60\u7684\u8868\u59d0\u5230\u5c0f\u91d1\u5ddd\u8c12\u89c1\u5979\u7684\u4e00\u4f4d\u5e08\u4f2f\uff0c\u5979\u7684\u5e08\u7236\u65e0\u76f8\u795e\u5c3c\u662f\u5ce8\u5d4b\u6d3e\u638c \u95e8\uff0c\u8fd9\u4f4d\u5e08\u4f2f\u5374\u662f\u9690\u5c45\u5728\u5c0f\u91d1\u5ddd\u7684\u9752\u7af9\u6797\u3002 \u3000\u3000\u201c\u53bb\u65f6\u5e73\u5b89\u65e0\u4e8b\uff0c\u5f52\u9014\u4e2d\u7ecf\u8fc7\u5730\u5f62\u9669\u6076\u7684\u9752\u9f99\u5c97\uff0c\u5374\u78b0\u4e0a\u4e86\u6ec7\u5357\u4e03\u864e\u3002\u8fd9\u4e03\u4e2a\u4eba\u4e43\u662f\u6ec7 \u5357\u8457\u540d\u7684\u5267\u76d7\uff0c\u7ed3\u4e3a\u5f02\u59d3\u5144\u5f1f\uff0c\u53f7\u79f0\u4e03\u864e\u3002\u4e0d\u77e5\u4f55\u6545\uff0c\u90fd\u6765\u5230\u4e86\u5c0f\u91d1\u5ddd\uff0c\u6070\u5de7\u548c\u6211\u78b0\u4e0a\u3002 \u3000\u3000\u201c\u6211\u548c\u6ec7\u5357\u4e03\u864e\u7684\u8001\u5927\u6bb5\u70b9\u82cd\u66fe\u6709\u8fc7\u8282\uff0c\u8fd9\u6b21\u964c\u8def\u76f8\u9022\uff0c\u81ea\u662f\u514d\u4e0d\u4e86\u4e00\u573a\u6076\u6597\u3002\u6211\u4f24\u4e86 \u4e03\u864e\u4e2d\u7684\u4e24\u4e2a\u4eba\uff0c\u5374\u4e2d\u4e86\u6bb5\u70b9\u82cd\u7684\u4e00\u679a\u5582\u6bd2\u6697\u5668\uff0c\u4f17\u5be1\u4e0d\u654c\uff0c\u7ed9\u4ed6\u4eec\u5305\u56f4\u4e86\u3002 \u3000\u3000\u201c\u9752\u9f99\u5c97\u5730\u52bf\u9669\u6076\uff0c\u6211\u8981\u7a81\u56f4\uff0c\u4e0d\u662f\u4e0d\u80fd\uff0c\u4f46\u53ea\u6015\u4e5f\u975e\u5f97\u4f24\u4e0a\u52a0\u4f24\u4e0d\u53ef\u3002\u800c\u4e14\u53d7\u4f24\u8fd8\u4e0d \u6253\u7d27\uff0c\u8d25\u5728\u6ec7\u5357\u4e03\u864e\u624b\u4e0b\uff0c\u6211\u8fd9\u4e00\u4e16\u82f1\u540d\u53ef\u5c31\u4fdd\u4e0d\u4f4f\u4e86\u3002 \u3000\u3000\u201c\u6211\u6b63\u5728\u65e0\u53ef\u5948\u4f55\uff0c\u62fc\u7740\u4e0e\u4ed6\u4eec\u4e24\u8d25\u4ff1\u4f24\u4e4b\u9645\uff0c\u5ffd\u542c\u5f97\u9a6c\u94c3\u58f0\u4e2d\u6765\u4e86\u4e00\u4f4d\u5c11\u5e74\u597d\u6c49\uff0c\u8fd9 \u4eba\u540d\u53eb\u8c37\u6db5\u865a\uff0c\u5c31\u662f\u521a\u624d\u6765\u7684\u90a3\u4f4d\u5ba2\u4eba\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8c37\u6db5\u865a\uff0c\u8fd9\u540d\u5b57\u6211\u53ef\u4ece\u672a\u542c\u8fc7\u3002\u600e\u7684\u6b66\u6797\u4e2d\u6709\u4e86\u5982\u6b64\u4e00\u4f4d\u540e\u8d77\u4e4b \u79c0\uff0c\u6211\u7684\u7239\u7239\u5374\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u4e25\u58f0\u6d9b\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u8fd9\u59d3\u8c37\u7684\u5c11\u5e74\u6b66\u529f\u6781\u4e3a\u4e86\u5f97\uff0c\u5251\u638c\u517c\u65bd\uff0c\u53c8\u6253\u4f24\u4e86\u4e03\u864e\u4e2d\u7684\u53e6\u5916\u4e24 \u4e2a\u3002\u4ed6\u4eec\u4e03\u4e2a\u4eba\u5df2\u6709\u8fc7\u534a\u53d7\u4f24\uff0c\u5269\u4e0b\u7684\u4e09\u4e2a\u4eba\u81ea\u77e5\u4e0d\u654c\uff0c\u53ea\u597d\u63a9\u62a4\u4f24\u8005\u9003\u8dd1\u3002\u5509\uff0c\u6211\u7eb5\u6a2a\u534a \u4e16\uff0c\u60f3\u4e0d\u5230\u4e34\u8001\u4e4b\u5e74\uff0c\u5374\u53d7\u4e86\u4e00\u4f4d\u5c0f\u8f88\u7684\u6069\u60e0\uff0c\u5b9e\u662f\u601d\u4e4b\u6709\u6127\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u95ee\u9053\uff1a\u201c\u4e0d\u77e5\u6b64\u4eba\u662f\u4f55\u6765\u5386\uff1f\u201d \u3000\u3000\u4e25\u58f0\u6d9b\u9053\uff1a\u201c\u5f53\u65f6\u6211\u4e2d\u4e86\u5582\u6bd2\u6697\u5668\uff0c\u65e0\u6687\u4e0e\u4ed6\u8be6\u8c08\u3002\u53ea\u80fd\u95ee\u4e86\u4ed6\u7684\u59d3\u540d\uff0c\u7ea6\u4ed6\u76f8\u89c1\u3002\u4ed6 \u4eca\u5929\u5c31\u662f\u5e94\u7ea6\u800c\u6765\u7684\u3002\u201d \u3000\u3000\u4e25\u592b\u4eba\u9053\uff1a\u201c\u5bf9\u5566\uff0c\u6211\u4e5f\u6b63\u60f3\u95ee\u4f60\uff0c\u4ed6\u7684\u6765\u5934\uff0c\u4f60\u73b0\u5728\u77e5\u9053\u4e86\u6ca1\u6709\uff1f\u4ed6\u4eca\u65e5\u7684\u6765\u610f\u53c8\u662f \u5982\u4f55\uff1f\u662f\u671b\u4f60\u62a5\u7b54\u5462\u8fd8\u662f\u53e6\u6709\u6240\u56fe\uff1f\u201d \u3000\u3000\u4e25\u58f0\u6d9b\u9053\uff1a\u201c\u521d\u65f6\u70f7\u513f\u5728\u65c1\uff0c\u6211\u6050\u4ed6\u6709\u6240\u8bef\u4f1a\uff0c\u4e0d\u4fbf\u67e5\u8be2\u4ed6\u7684\u5bb6\u4e16\u3002\u70f7\u513f\u8d70\u540e\uff0c\u5374\u53c8\u8bdd \u4e0d\u6295\u673a\uff0c\u4ed6\u4e0d\u4e45\u5c31\u5306\u5306\u544a\u8f9e\uff0c\u6765\u4e0d\u53ca\u95ee\u4ed6\u4e86\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u8fd9\u4eba\u65e2\u7136\u66fe\u7ecf\u5e2e\u8fc7\u59d1\u7236\u8fd9\u6837\u4e00\u4e2a\u5927\u5fd9\uff0c\u4f55\u4ee5\u53c8\u4f1a\u8bdd\u4e0d\u6295\u673a\uff1f\u201d \u3000\u3000\u4e25\u58f0\u6d9b\u82e6\u7b11\u9053\uff1a\u201c\u6211\u5e73\u751f\u6069\u6028\u5206\u660e\uff0c\u51b3\u4e0d\u662f\u5fd8\u601d\u8d1f\u4e49\u4e4b\u4eba\u3002\u8bf4\u8001\u5b9e\u8bdd\uff0c\u6211\u7ea6\u4ed6\u76f8\u89c1\uff0c\u5c31 \u662f\u5e0c\u671b\u80fd\u591f\u63a2\u542c\u6709\u4ec0\u4e48\u9700\u8981\u6211\u5e2e\u5fd9\u7684\u5730\u65b9\uff0c\u53ef\u4ee5\u62a5\u7b54\u4ed6\u7684\u6069\u60e0\u7684\u3002\u4e0d\u6599\u5f85\u6211\u77e5\u9053\u4ed6\u7684\u6765\u610f\u4e4b \u540e\uff0c\u5374\u662f\u4ee4\u6211\u5927\u5927\u4e3a\u96be\u4e86\u3002\u65e0\u53ef\u5948\u4f55\uff0c\u6211\u53ea\u597d\u6697\u793a\u7ed9\u4ed6\u77e5\u9053\uff0c\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u6211\u662f\u6ca1\u6cd5\u5982\u4ed6\u6240\u613f \u7684\u4e86\uff01\u201d \u3000\u3000\u4e25\u592b\u4eba\u9053\uff1a\u201c\u4ed6\u6c42\u4f60\u4ec0\u4e48\uff1f\u201d\u4e25\u58f0\u6d9b\u9053\uff1a\u201c\u4ed6\u6ca1\u6709\u4eb2\u53e3\u6c42\u6211\uff0c\u8fd9\u6837\u7684\u4e00\u6869\u4e8b\u60c5\uff0c\u4ed6\u5f53\u7136 \u4e5f\u662f\u4e0d\u4fbf\u4eb2\u81ea\u548c\u6211\u8bf4\u7684\u3002\u201d\u541e\u541e\u5410\u5410\uff0c\u597d\u50cf\u6709\u70b9\u4e0d\u5927\u613f\u610f\u8bf4\u51fa\u6765\u7684\u6837\u5b50\u3002 \u3000\u3000\u4e25\u592b\u4eba\u9053\uff1a\u201c\u7a76\u7adf\u662f\u4ec0\u4e48\u4e8b\u60c5\uff0c\u5728\u8fd9\u91cc\u7684\u90fd\u4e0d\u662f\u5916\u4eba\uff0c\u4f60\u8bf4\u597d\u4e86\u3002\u201d \u3000\u3000\u4e25\u58f0\u6d9b\u9053\uff1a\u201c\u4ed6\u643a\u6765\u4e86\u9752\u57ce\u6d3e\u540d\u5bbf\u7389\u5cf0\u9053\u957f\u7684\u4e00\u5c01\u4e66\u4fe1\uff0c\u4fe1\u4e2d\u5410\u9732\u6267\u67ef\u4e4b\u610f\u3002\u201d\u4e25\u70f7\u51dd \u770b\u9759\u542c\uff0c\u542c\u7236\u4eb2\u8bf4\u5230\u4e86\u201c\u6267\u67ef\u201d\u4e8c\u5b57\uff0c\u4e0d\u7981\u6ee1\u9762\u98de\u7ea2\u3002 \u3000\u3000\u5b5f\u660e\u971e\u5fcd\u4e0d\u4f4f\u8bf4\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u7389\u5cf0\u9053\u957f\u662f\u4e3a\u8868\u59d0\u505a\u5a92\uff0c\u90a3\u4e0d\u662f\u5f88\u597d\u5417\uff01\u201d\u5fc3\u91cc\u60f3\u9053\uff1a \u201c\u90a3\u59d3\u8c37\u7684\u6b66\u529f\u53c8\u9ad8\uff0c\u4eba\u53c8\u82f1\u4fca\uff0c\u800c\u4e14\u53c8\u5bf9\u59d1\u7236\u6709\u6069\uff0c\u8fd9\u5c82\u4e0d\u6b63\u662f\u5929\u4f5c\u4e4b\u5408\uff1f\u201d\u8fd9\u4e9b\u8bdd\u5979\u672c \u6765\u8981\u8bf4\u51fa\u6765\u7684\uff0c\u7a81\u7136\u53d1\u89c9\u59d1\u7236\u59d1\u6bcd\u7684\u9762\u8272\u597d\u50cf\u4e0d\u5bf9\uff0c\u5979\u6014\u4e86\u4e00\u6014\uff0c\u8bdd\u5230\u53e3\u8fb9\uff0c\u7ec8\u4e8e\u54bd\u4e0b\u3002 \u3000\u3000\u4e25\u592b\u4eba\u767d\u5979\u4e00\u773c\uff0c\u4f3c\u4e4e\u602a\u5979\u591a\u8bdd\uff0c\u534a\u664c\uff0c\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u4f60\u8868\u59d0\u65e9\u5df2\u8bb8\u4e86\u4eba\u4e86\uff01\u201d \u3000\u3000\u5b5f\u660e\u971e\u5c34\u5c2c\u4e4b\u6781\uff0c\u53ea\u597d\u8fde\u5fd9\u8bf4\u9053\uff1a\u201c\u606d\u559c\u8868\u59d0\uff0c\u539f\u6765\u8868\u59d0\u65e9\u5df2\u6709\u4e86\u5a46\u5bb6\uff0c\u6211\u8fd8\u4e0d\u77e5\u9053 \u5462\u3002\u90a3\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u4e25\u592b\u4eba\u9053\uff1a\u201c\u662f\u4f60\u59d1\u7236\u4e00\u4f4d\u597d\u53cb\u7684\u513f\u5b50\uff0c\u6b66\u5f53\u6d3e\u7684\u5f1f\u5b50\u5f20\u5143\u58eb\u3002\u201d \u3000\u3000\u4e25\u58f0\u6d9b\u8fde\u4e0b\u53bb\u8bf4\u9053\uff1a\u201c\u770b\u4e86\u8fd9\u5c01\u4fe1\uff0c\u6211\u6ca1\u8bf4\u4ec0\u4e48\u3002\u548c\u4ed6\u95f2\u804a\u4e86\u4e00\u4f1a\uff0c\u8fd9\u624d\u544a\u8bc9\u4ed6\u9053\uff0c\u660e \u5e74\u4e8c\u6708\u521d\u516b\uff0c\u662f\u5c0f\u5973\u51fa\u9601\u7684\u65e5\u5b50\uff0c\u5c4a\u65f6\u5e0c\u671b\u4ed6\u548c\u7389\u5cf0\u9053\u957f\u6765\u559d\u4e00\u676f\u6c34\u9152\u3002\u563f\uff0c\u563f\uff0c\u4ed6\u4e00\u542c\u8fd9 \u4e2a\u6d88\u606f\uff0c\u9762\u4e0a\u4e00\u9635\u9752\u4e00\u9635\u7ea2\uff0c\u968f\u5373\u5c31\u544a\u8f9e\u4e86\u3002\u6211\u597d\u751f\u8fc7\u610f\u4e0d\u53bb\uff0c\u4f46\u53ea\u662f\uff0c\u4ec0\u4e48\u529e\u6cd5\u5462\uff1f\u82e5\u662f \u522b\u7684\u4e8b\u60c5\uff0c\u8d74\u6c64\u8e48\u706b\uff0c\u6211\u4e5f\u4f1a\u7ed9\u4ed6\u529e\u5230\uff0c\u8981\u505a\u54b1\u4eec\u7684\u5973\u5a7f\uff0c\u90a3\u5c31\u53ea\u6709\u76fc\u671b\u54b1\u4eec\u518d\u751f\u4e00\u4e2a\u5973\u513f \u4e86\u3002\u201d\u5f53\u4ed6\u8bf4\u8bdd\u4e4b\u65f6\uff0c\u4e25\u592b\u4eba\u8138\u4e0a\u50cf\u7ed3\u4e86\u4e00\u5c42\u971c\uff0c\u4e25\u70f7\u5219\u4f4e\u4e0b\u4e86\u5934\uff0c\u4e0d\u542d\u4e00\u58f0\u3002\u662f\u4ee5\u4ed6\u6545\u610f \u8bf4\u4e2a\u7b11\u8bdd\uff0c\u7f13\u548c\u8fd9\u51b0\u51b7\u7684\u6c14\u6c1b\uff0c\u53ef\u662f\u4e00\u4e2a\u4eba\u90fd\u6ca1\u6709\u7b11\u3002 \u3000\u3000\u4e25\u592b\u4eba\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u70f7\u513f\uff0c\u4f60\u662f\u5feb\u8981\u51fa\u5ac1\u7684\u5973\u513f\u4e86\uff0c\u53ef\u4e0d\u80fd\u6709\u534a\u6b65\u884c\u5dee\u8e0f\u9519\uff01\u8fd9\u4eba\u867d\u7136 \u5bf9\u4f60\u6709\u6069\uff0c\u4f60\u89c1\u4ed6\u4e00\u9762\uff0c\u4ea6\u5df2\u5c3d\u4e86\u672d\u6570\uff0c\u4ee5\u540e\u53ef\u4ee5\u4e0d\u5fc5\u548c\u4ed6\u6765\u5f80\u4e86\uff01\u201d \u3000\u3000\u4e25\u70f7\u9053\uff1a\u201c\u5988\uff0c\u4f60\u4fe1\u4e0d\u8fc7\u6211\uff0c\u4f60\u5c31\u628a\u6211\u9501\u8d77\u6765\u5427\uff01\u54fc\uff0c\u6211\u548c\u4ed6\u6709\u4ec0\u4e48\u6765\u5f80\uff0c\u4eca\u5929\u4e0d\u662f\u7239 \u7239\u53eb\u6211\u51fa\u53bb\u966a\u5ba2\uff0c\u6211\u8fd8\u4e0d\u5982\u662f\u8c01\u6765\u5462\uff01\u201d \u3000\u3000\u4e25\u592b\u4eba\u9053\uff0c\u201c\u6211\u4e0d\u8fc7\u662f\u4e3a\u4e86\u9632\u8303\u672a\u7136\uff0c\u63d0\u9192\u4f60\u51e0\u53e5\uff0c\u4f60\u5c31\u548c\u6211\u8d4c\u8d77\u6c14\u6765\u4e86\u3002\u201d \u3000\u3000\u4e25\u58f0\u6d9b\u529d\u89e3\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u60c5\u5df2\u6210\u8fc7\u53bb\uff0c\u5927\u5bb6\u90fd\u4e0d\u5fc5\u6302\u5728\u5fc3\u4e0a\uff0c\u6211\u6599\u4ed6\u4eca\u65e5\u4e00\u8d70\u4e4b\u540e\uff0c\u662f \u51b3\u4e0d\u4f1a\u518d\u6765\u7684\u4e86\u3002\u201d \u3000\u3000\u4e25\u592b\u4eba\u9053\uff1a\u201c\u6211\u4e0d\u6302\u5728\u5fc3\u4e0a\uff0c\u53ea\u6015\u4f60\u4f1a\u6302\u5728\u5fc3\u4e0a\uff0c\u6b20\u4e86\u4eba\u5bb6\u7684\u6069\u60c5\uff0c\u6ca1\u6709\u62a5\u7b54\uff0c\u600e\u5f97\u5fc3 \u5b89\uff1f\u201d \u3000\u3000\u4e25\u58f0\u6d9b\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5a18\u5b50\u4e0d\u6127\u662f\u6211\u7684\u77e5\u5df1\uff0c\u6211\u8fd9\u4e2a\u4eba\u53d7\u4e0d\u5f97\u4eba\u5bb6\u7684\u6069\u60e0\uff0c\u5c24\u5176\u662f \u5c0f\u8f88\u7684\u6069\u60e0\uff0c\u53d7\u4e86\u975e\u62a5\u7b54\u4e0d\u53ef\u3002\u53ef\u60dc\u54b1\u4eec\u6ca1\u6709\u7b2c\u4e8c\u4e2a\u5973\u513f\u3002\u201d \u3000\u3000\u4e25\u592b\u4eba\u770b\u4e86\u5b5f\u660e\u971e\u4e00\u773c\uff0c\u8bf4\u9053\uff1a\u201c\u971e\u513f\uff0c\u4f60\u770b\u8fd9\u4e2a\u4eba\u600e\u4e48\u6837\uff1f\u201d\u5b5f\u660e\u971e\u9053\uff1a\u201c\u6211\u548c\u4ed6\u53c8 \u4e0d\u76f8\u8bc6\uff0c\u600e\u77e5\u4ed6\u600e\u4e48\u6837\uff1f\u4e0d\u8fc7\u4ed6\u65e2\u7136\u80fd\u591f\u5e2e\u52a9\u59d1\u7236\u8131\u9669\uff0c\u6b66\u529f\u81ea\u5fc5\u662f\u5f88\u9ad8\u7684\u3002\u201d \u3000\u3000\u4e25\u592b\u4eba\u9053\uff1a\u201c\u662f\u5440\uff0c\u76f8\u8c8c\u4e5f\u5f88\u4e0d\u9519\u5462\u3002\u552f\u4e00\u53ef\u8651\u4e4b\u5904\u5c31\u662f\u8fd8\u672a\u77e5\u9053\u4ed6\u7684\u6765\u5386\uff0c\u4e0d\u8fc7\u8fd9\u4e5f \u4e0d\u6253\u7d27\uff0c\u7389\u5cf0\u9053\u957f\u662f\u4f60\u7239\u7239\u7684\u597d\u670b\u53cb\uff0c\u65e5\u540e\u89c1\u4e86\u7389\u5cf0\u9053\u957f\uff0c\u95ee\u4e00\u95ee\u4ed6\u5c31\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u518d\u7b28\u4e5f\u542c\u5f97\u61c2\u59d1\u6bcd\u7684\u610f\u601d\uff0c\u4f55\u51b5\u5979\u5e76\u4e0d\u7b28\uff0c\u53ea\u662f\u5e74\u7eaa\u5c0f\uff0c\u4e0d\u628a\u7537\u5973\u4e4b\u4e8b\u653e\u5728\u5fc3\u4e0a \u800c\u5df2\uff0c\u6b64\u65f6\u767b\u65f6\u7701\u609f\uff0c\u5fc3\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u59d1\u7236\u4e00\u518d\u95ee\u6211\u6709\u6ca1\u6709\u5a46\u5bb6\uff0c\u539f\u6765\u662f\u60f3\u53eb\u6211\u674e\u4ee3\u6843\u50f5\uff0c \u66ff\u4ed6\u62a5\u7b54\u4eba\u5bb6\u7684\u6069\u60c5\u3002\u54fc\uff0c\u90a3\u4e2a\u4eba\u559c\u6b22\u7684\u662f\u8868\u59d0\uff0c\u600e\u80fd\u53eb\u6211\u66ff\u4ee3\u3002\u59d1\u7236\u59d1\u6bcd\u672a\u514d\u4e5f\u592a\u4e0d\u5c0a\u91cd \u6211\u4e86\u3002\u201d\u5979\u662f\u4e2a\u723d\u76f4\u65e0\u90aa\u7684\u5c11\u5973\uff0c\u5fc3\u91cc\u4e0d\u9ad8\u5174\uff0c\u8138\u4e0a\u5c31\u663e\u9732\u51fa\u6765\u3002\u8ba9\u5979\u59d1\u6bcd\u81ea\u8a00\u81ea\u8bed\uff0c\u4e00\u53e5 \u4e5f\u4e0d\u7b54\u8154\u3002 \u3000\u3000\u4e25\u70f7\u9053\uff1a\u201c\u5988\uff0c\u4f60\u6ca1\u6709\u4ec0\u4e48\u5429\u5490\u4e86\u5427\uff0c\u6211\u60f3\u56de\u623f\u95f4\u4e86\uff0c\u8868\u59b9\u6765\u5230\u54b1\u5bb6\u4f5c\u5ba2\uff0c\u6211\u4e5f\u8be5\u5c3d\u70b9 \u4e3b\u4eba\u4e4b\u9053\u624d\u662f\u3002\u8868\u59b9\uff0c\u4f60\u6765\u6211\u7684\u623f\u95f4\u770b\u770b\u597d\u4e0d\u597d\uff0c\u4f60\u5306\u5306\u6765\u5230\uff0c\u6211\u4e34\u65f6\u6ca1\u6709\u51c6\u5907\uff0c\u4f46\u4f60\u6211\u7684 \u8eab\u6750\u4e5f\u5dee\u4e0d\u591a\uff0c\u6211\u60f3\u8ba9\u4f60\u8bd5\u8bd5\u6211\u524d\u4e24\u5e74\u7684\u8863\u88f3\uff0c\u770b\u770b\u5408\u4e0d\u5408\u8eab\uff1f\u5c06\u5c31\u7a7f\u51e0\u5929\uff0c\u6211\u518d\u7ed9\u4f60\u505a\u65b0 \u7684\u3002\u8fd8\u6709\u4f60\u559c\u6b22\u4ec0\u4e48\uff0c\u4e5f\u671b\u4f60\u4e0d\u5ba2\u6c14\u544a\u8bc9\u6211\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u5df4\u4e0d\u5f97\u79bb\u5f00\u59d1\u6bcd\uff0c\u8bf4\u9053\uff1a\u201c\u8868\u59d0\u7528\u4e0d\u7740\u8fd9\u4e48\u5ba2\u6c14\uff0c\u4e00\u5207\u542c\u8868\u59d0\u5b89\u6392\u3002\u59d1\u7236\u59d1\u6bcd\uff0c \u90a3\u4e48\u6211\u548c\u8868\u59d0\u8fdb\u53bb\u4e86\u3002\u8868\u59d0\u4e00\u5b9a\u4f1a\u7ed9\u6211\u5b89\u6392\u5f97\u59a5\u5f53\u7684\uff0c\u59d1\u59d1\uff0c\u4f60\u8001\u4eba\u5bb6\u5c31\u4e0d\u5fc5\u4e3a\u6211\u8d39\u795e \u5566\u3002\u201d \u3000\u3000\u4e25\u592b\u4eba\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u660e\u971e\u8fd8\u662f\u4e2a\u5c0f\u59d1\u5a18\uff0c\u8c08\u53ca\u5979\u7684\u7ec8\u8eab\u5927\u4e8b\uff0c\u5979\u81ea\u662f\u96be\u514d\u5bb3\u7f9e\u3002\u53cd\u6b63\u5979 \u5e74\u7eaa\u5c0f\uff0c\u8fd9\u4e8b\u7b49\u5230\u5979\u7684\u7239\u7239\u56de\u6765\uff0c\u6162\u6162\u518d\u8bf4\u4e5f\u8fd8\u4e0d\u8fdf\u3002\u201d\u4e8e\u662f\u8bf4\u9053\uff1a\u201c\u597d\u5427\uff0c\u4f60\u4e00\u8def\u52b3\u7d2f\uff0c \u4e5f\u8be5\u6b47\u6b47\u4e86\u3002\u201d \u3000\u3000\u8868\u59d0\u59b9\u8fdb\u4e86\u623f\u95f4\uff0c\u4e25\u70f7\u5173\u4e0a\u623f\u95e8\uff0c\u201c\u5657\u55e4\u201d\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5988\u8fd9\u526f\u813e\u6c14\uff0c\u59d4\u5b9e\u53eb\u4eba\u96be \u53d7\u3002\u8868\u59b9\uff0c\u4f60\u7ed9\u5979\u7f57\u55e6\u4e86\u4e00\u987f\uff0c\u4e00\u5b9a\u5934\u75db\u4e86\uff0c\u662f\u5417\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u7b11\u9053\uff1a\u201c\u6211\u7239\u7239\u7684\u813e\u6c14\u5012\u662f\u5f88\u968f\u548c\u7684\u3002\u4e0d\u8fc7\u59d1\u59d1\u867d\u7136\u662f\u5bf9\u4f60\u4e25\u5389\u4e00\u4e9b\uff0c\u4e5f\u8fd8\u662f\u51fa \u4e8e\u7236\u6bcd\u7231\u5b50\u5973\u4e4b\u5fc3\u3002\u201d \u3000\u3000\u4e25\u70f7\u9053\uff1a\u201c\u6211\u771f\u7fa1\u6155\u4f60\u6709\u4e2a\u597d\u7239\u7239\uff0c\u6211\u5988\u8fd9\u4e48\u6837\u7684\u5bf9\u6211\uff0c\u662f\u75bc\u6211\u4e5f\u597d\uff0c\u662f\u7ba1\u6211\u4e5f\u597d\uff0c\u6211 \u53ef\u662f\u53d7\u4e0d\u4e86\u5462\u3002\u201d\u63a5\u7740\u53c8\u7b11\u4e86\u8d77\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5988\u4e5f\u5f88\u75bc\u4f60\u5462\uff0c\u4f60\u542c\u61c2\u4e86\u6ca1\u6709\uff0c\u5979\u521a\u624d\u60f3\u7ed9 \u4f60\u505a\u5a92\u5462\u3002\u4f60\u7684\u610f\u601d\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u5b5f\u660e\u971e\u7b11\uff0c\u9053\uff1a\u201c\u90a3\u4e2a\u4eba\u662f\u5411\u4f60\u6c42\u5a5a\u7684\uff0c\u4f60\u7684\u610f\u601d\u600e\u4e48\u6837\uff0c\u4f60\u8fd8\u6ca1\u6709\u548c\u6211\u8bf4\u5462\uff1f\u201d \u3000\u3000\u4e25\u70f7\u8138\u8272\u4e00\u53d8\uff0c\u773c\u5708\u4e5f\u7ea2\u4e86\u3002\u5b5f\u660e\u971e\u5927\u4e3a\u540e\u6094\uff0c\u8fde\u5fd9\u8bf4\u9053\uff1a\u201c\u8868\u59d0\uff0c\u6211\u662f\u4fe1\u53e3\u548c\u4f60\u5f00\u5f00 \u73a9\u7b11\u7684\u3002\u8bf4\u8bdd\u4e0d\u77e5\u8f7b\u91cd\uff0c\u4f60\u522b\u89c1\u602a\u3002\u201d \u3000\u3000\u4e25\u70f7\u9053\uff1a\u201c\u6211\u5f53\u7136\u4e0d\u4f1a\u602a\u4f60\u7684\uff0c\u4e0d\u8fc7\u8fd9\u6837\u7684\u73a9\u7b11\uff0c\u4ee5\u540e\u8fd8\u662f\u5c11\u5f00\u4e3a\u5999\u3002\u514d\u5f97\u7ed9\u6211\u5988\u542c\u89c1 \u4e86\uff0c\u53ef\u53c8\u8981\u6559\u8bad\u54b1\u4eec\u4e00\u987f\u4e86\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u662f\u5440\uff0c\u6211\u4e00\u65f6\u5fd8\u8bb0\uff0c\u4f60\u8fd8\u6709\u51e0\u4e2a\u6708\u5c31\u8981\u505a\u65b0\u5a18\u5b50\u7684\u4e86\u3002\u8868\u59d0\u592b\u662f\u540d\u95e8\u6b63\u6d3e \u7684\u5f1f\u5b50\uff0c\u6b66\u529f\u4eba\u54c1\u5fc5\u662f\u4e0a\u4e0a\u4e4b\u9009\uff0c\u4f60\u4eec\u89c1\u8fc7\u9762\u6ca1\u6709\uff1f\u201d\u6709\u4e00\u53e5\u8bdd\u5979\u9669\u4e9b\u95ee\u4e86\u51fa\u6765\uff1a\u201c\u8868\u59d0\u592b \u6bd4\u90a3\u4e2a\u4eba\u600e\u6837\uff1f\u201d\u597d\u5728\u731b\u7136\u4e00\u7701\uff0c\u8fd9\u624d\u6ca1\u6709\u518d\u6b21\u5931\u8a00\u3002 \u3000\u3000\u4e25\u70f7\u4f4e\u4e0b\u4e86\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u662f\u7239\u7239\u4ece\u5c0f\u7ed9\u6211\u8ba2\u4e0b\u7684\u5a5a\u4e8b\uff0c\u4ed6\u6765\u662f\u6765\u8fc7\u7684\uff0c\u6211\u53ef\u6ca1\u6709\u51fa\u53bb\u89c1 \u4ed6\u3002\u54b1\u4eec\u4e0d\u8c08\u8fd9\u4e2a\u4e86\uff0c\u597d\u4e0d\u597d\uff0c\u5bf9\u5566\uff0c\u4f60\u7239\u7239\u53f7\u79f0\u795e\u5251\u9707\u6c5f\u6e56\uff0c\u6211\u53ef\u60f3\u5411\u4f60\u5b66\u51e0\u62db\u5251\u6cd5 \u5462\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u5fc3\u60f3\uff1a\u201c\u770b\u6765\u5979\u597d\u50cf\u4e0d\u5927\u6b22\u559c\u8fd9\u95e8\u4eb2\u4e8b\u3002\u201d\u5f53\u4e0b\u8f6c\u8fc7\u8bdd\u9898\uff0c\u4e0e\u8868\u59d0\u8c08\u8bba\u6b66\u529f\uff0c\u4e24 \u4eba\u6027\u60c5\u76f8\u8fd1\uff0c\u751a\u4e3a\u76f8\u5f97\u3002 \u3000\u3000\u5b5f\u660e\u971e\u548c\u8868\u59d0\u540c\u7761\u4e00\u4e2a\u623f\u95f4\uff0c\u4e34\u7761\u4e4b\u524d\uff0c\u4e25\u70f7\u70b9\u8d77\u4e86\u4e00\u7089\u6a80\u9999\uff0c\u8bf4\u9053\uff1a\u201c\u6a80\u9999\u6709\u5b81\u795e\u7684 \u529f\u6548\uff0c\u6211\u4e60\u60ef\u4e86\u711a\u9999\u7761\u89c9\u7684\u3002\u201d\u5b5f\u660e\u971e\u5438\u4e86\u4e00\u53e3\u9999\u6c14\uff0c\u4e0d\u89c9\u6253\u4e86\u4e2a\u54c8\u6b20\u8bf4\u9053\uff1a\u201c\u597d\u8212\u670d\u3002\u6211 \u4e5f\u5f88\u559c\u6b22\u95fb\u8fd9\u9999\u5473\uff0c\u7684\u786e\u662f\u6709\u5b81\u795e\u50ac\u7720\u4e4b\u529f\uff0c\u6211\u53ef\u771f\u662f\u60f3\u7761\u89c9\u4e86\u3002\u201d\u4e25\u70f7\u7b11\u9053\uff1a\u201c\u4f60\u4e00\u8def\u52b3 \u7d2f\uff0c\u6211\u5c31\u662f\u60f3\u4f60\u5b89\u5b89\u9038\u9038\u7761\u4e0a\u4e00\u89c9\u5462\u3002\u201d \u3000\u3000\u4e00\u5bbf\u65e0\u8bdd\u3002\u7b2c\u4e8c\u65e5\u5b5f\u660e\u971e\u4e00\u89c9\u9192\u6765\uff0c\u53ea\u89c1\u5df2\u662f\u7ea2\u65e5\u5f53\u7a97\u7684\u65f6\u5206\uff0c\u800c\u4e14\u8fd8\u662f\u4e25\u70f7\u5c06\u5979\u53eb\u9192 \u7684\u3002\u5b5f\u660e\u971e\u7b11\u9053\uff1a\u201c\u8fd9\u4e00\u89c9\u7761\u5f97\u597d\u957f\uff0c\u6211\u771f\u53d8\u6210\u4e86\u8d2a\u7761\u7684\u61d2\u59d1\u5a18\u4e86\u3002\u201d \u3000\u3000\u4e25\u70f7\u9053\uff1a\u201c\u4f60\u8d70\u4e86\u8fd9\u4e48\u8fdc\u7684\u8def\uff0c\u5176\u5b9e\u6211\u8fd8\u5e94\u8be5\u8ba9\u4f60\u591a\u7761\u4e00\u4f1a\u7684\u3002\u4e0d\u8fc7\u6211\u6302\u7740\u60f3\u5411\u4f60\u8bf7\u6559 \u5251\u6cd5\uff0c\u53ea\u597d\u50ac\u4f60\u8d77\u6765\u4e86\u3002\u201d\u5b5f\u660e\u971e\u53ea\u9053\u778c\u7761\u662f\u75b2\u52b3\u6240\u81f3\uff0c\u4e5f\u4e0d\u600e\u6837\u5728\u610f\u3002 \u3000\u3000\u81ea\u6b64\u4e4b\u540e\uff0c\u6bcf\u5929\u90fd\u662f\u4e00\u6837\uff0c\u8868\u59d0\u59b9\u4e8c\u4eba\u95f2\u6765\u65e0\u4e8b\uff0c\u5c31\u5230\u56ed\u4e2d\u7ec3\u6b66\u3002\u4e25\u58f0\u6d9b\u592b\u5987\u89c1\u5979\u4eec\u751a \u4e3a\u76f8\u5f97\uff0c\u4e5f\u5f88\u6b22\u559c\u3002\u5b5f\u660e\u971e\u6bcf\u5929\u5411\u59d1\u7236\u59d1\u6bcd\u8bf7\u4e24\u6b21\u5b89\uff0c\u5269\u4e0b\u7684\u65f6\u95f4\u90fd\u662f\u548c\u8868\u59d0\u5728\u4e00\u8d77\u3002\u5b5f\u660e \u971e\u77e5\u9053\u8868\u59d0\u4e0d\u5927\u6ee1\u610f\u59d1\u7236\u7ed9\u5979\u8ba2\u4e0b\u7684\u5a5a\u4e8b\uff0c\u4e25\u592b\u4eba\u4e5f\u56e0\u90a3\u5929\u7684\u4e8b\u60c5\u95f9\u5f97\u4e0d\u5927\u6109\u5feb\uff0c\u56e0\u6b64\u4e00\u5bb6 \u4eba\u5728\u4e00\u8d77\u65f6\u5019\uff0c\u8c01\u90fd\u907f\u514d\u518d\u63d0\u8d77\u201c\u90a3\u4e2a\u4eba\u201d\u6765\u3002 \u3000\u3000\u5b5f\u660e\u971e\u5728\u59d1\u6bcd\u5bb6\u4e2d\u4f5c\u5ba2\uff0c\u9664\u4e86\u6709\u65f6\u8ba8\u538c\u59d1\u6bcd\u7684\u7f57\u55e6\u4e4b\u5916\uff0c\u65e5\u5b50\u5012\u662f\u8fc7\u5f97\u5f88\u5feb\u6d3b\u3002\u4e0d\u8fc7\uff0c \u8fc7\u4e86\u51e0\u5929\u4e4b\u540e\uff0c\u6709\u4e00\u4ef6\u4e8b\u60c5\uff0c\u5012\u662f\u4ee4\u5979\u89c9\u5f97\u6709\u70b9\u5947\u602a\uff0c\u8fd9\u5c31\u662f\u6bcf\u4e00\u5929\u90fd\u50cf\u521d\u6765\u7684\u7b2c\u4e8c\u5929\u4e00 \u6837\uff0c\u975e\u5f97\u8868\u59d0\u5c06\u5979\u53eb\u9192\uff0c\u5979\u5c31\u4e0d\u4f1a\u8d77\u5e8a\u3002 \u3000\u3000\u6bcf\u665a\u4e34\u7761\u4e4b\u524d\uff0c\u4e25\u70f7\u7167\u4f8b\u5fc5\u5b9a\u70b9\u4e0a\u4e00\u7089\u6a80\u9999\u3002\u8fc7\u4e86\u51e0\u5929\uff0c\u5b5f\u660e\u971e\u4e0d\u89c9\u8d77\u4e86\u7591\u5fc3\uff0c\u6697\u81ea\u60f3 \u9053\uff1a\u201c\u521d\u6765\u90a3\u665a\uff0c\u6211\u53ef\u80fd\u662f\u56e0\u4e3a\u65c5\u9014\u52b3\u987f\uff0c\u4ee5\u81f4\u8d2a\u7761\uff0c\u4f46\u4f55\u4ee5\u8fc7\u4e86\u8fd9\u4e48\u591a\u5929\uff0c\u73b0\u5728\u8fd8\u662f\u5982\u6b64 \u5462\uff1f\u867d\u8bf4\u64c5\u9999\u6709\u5b81\u795e\u5b89\u7720\u7684\u529f\u6548\uff0c\u4f3c\u4e4e\u4e5f\u4e0d\u81f3\u4e8e\u4e0d\u4f1a\u9192\u5440\uff01\u201d \u3000\u3000\u5b5f\u660e\u971e\u7684\u7236\u4eb2\u662f\u6c5f\u6e56\u7684\u5927\u884c\u5bb6\uff0c\u884c\u8d70\u6c5f\u6e56\u7684\u4eba\uff0c\u56ca\u4e2d\u4e00\u5b9a\u6709\u51e0\u79cd\u5fc5\u9700\u7684\u836f\u54c1\uff0c\u4f8b\u5982\u6cbb\u5200 \u4f24\u7684\u91d1\u521b\u836f\u548c\u89e3\u8ff7\u9999\u7684\u836f\u7269\u5c31\u662f\u3002\u5b5f\u5c11\u521a\u81ea\u5236\u7684\u743c\u82b1\u7389\u9732\u4e38\uff0c\u5c24\u5176\u662f\u64c5\u89e3\u8ff7\u9999\u7684\u7075\u836f\u3002\u8fd9\u6b21 \u5b5f\u660e\u971e\u72ec\u81ea\u51fa\u95e8\uff0c\u5979\u7684\u7236\u4eb2\u7279\u5730\u4e3a\u5979\u51c6\u5907\u4e86\u836f\u56ca\uff0c\u743c\u82b1\u7389\u9732\u4e38\u81ea\u7136\u4e5f\u5728\u5176\u5185\u3002\u5979\u7684\u7236\u4eb2\u8fd8\u4e00 \u518d\u544a\u8beb\u5979\u8bf4\uff1a\u201c\u6c5f\u6e56\u9669\u6076\uff0c\u5bb3\u4eba\u4e4b\u5fc3\u4e0d\u53ef\u6709\uff0c\u9632\u4eba\u4e4b\u5fc3\u4e0d\u53ef\u65e0\u3002\u5207\u4e0d\u53ef\u81ea\u6043\u672c\u9886\uff0c\u5c31\u7c97\u5fc3\u5927 \u610f\u3002\u8fd9\u51e0\u6837\u836f\u54c1\uff0c\u4f60\u5fc5\u987b\u65f6\u523b\u5e26\u5728\u8eab\u8fb9\uff0c\u5b81\u53ef\u5907\u800c\u4e0d\u7528\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u5f53\u7136\u4e0d\u4f1a\u6000\u7591\u4e25\u70f7\u6709\u5bb3\u5979\u4e4b\u5fc3\uff0c\u4f46\u56e0\u4e3a\u89c9\u5f97\u6709\u70b9\u5947\u602a\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8868\u59d0\u96be\u9053\u662f \u7528\u4e0a\u4e86\u8ff7\u9999\uff0c\u5426\u5219\u6211\u4f55\u4ee5\u8001\u662f\u6c89\u7761\u4e0d\u77e5\u9192\u89c9\uff1f\u743c\u82b1\u7389\u9732\u4e38\u80fd\u89e3\u8ff7\u9999\uff0c\u517c\u6709\u63d0\u795e\u4e4b\u6548\uff0c\u7ba1\u5979\u711a \u7684\u662f\u4e0d\u662f\u8ff7\u9999\uff0c\u6211\u62fc\u7740\u4eca\u665a\u4e0d\u7761\uff0c\u4e14\u670d\u4e00\u9897\u743c\u82b1\u7389\u9732\u4e38\u8bd5\u8bd5\u3002\u201d \u3000\u3000\u8fd9\u665a\u5b5f\u660e\u971e\u670d\u4e86\u836f\u4e38\uff0c\u6697\u5730\u7559\u795e\uff0c\u5230\u4e86\u4e09\u66f4\u65f6\u5206\uff0c\u53ea\u542c\u5f97\u6089\u6089\u7d22\u7d22\u7684\u58f0\u54cd\uff0c\u539f\u6765\u662f\u4e25\u70f7 \u5077\u5077\u8d77\u5e8a\uff0c\u66f4\u6362\u8863\u88f3\u3002\u4ece\u7a97\u6237\u4e2d\u900f\u8fdb\u6765\u7684\u6708\u5149\uff01\u9690\u7ea6\u53ef\u4ee5\u770b\u51fa\u4e25\u70f7\u6362\u7684\u662f\u4e00\u8eab\u9ed1\u8272\u7684\u591c\u884c \u8863\u3002 \u3000\u3000\u5b5f\u660e\u971e\u604d\u7136\u5927\u609f\uff1a\u201c\u539f\u6765\u8868\u59d0\u6bcf\u5929\u665a\u4e0a\u90fd\u662f\u7792\u7740\u6211\u5077\u5077\u51fa\u53bb\u3002\u5979\u5230\u54ea\u91cc\u53bb\u5462\uff1f\u201d\u5979\u6bd5\u7adf \u662f\u4e2a\u5e74\u7eaa\u624d\u4e0d\u8fc7\u5341\u4e03\u5c81\u7684\u5929\u771f\u65e0\u90aa\u7684\u5c0f\u59d1\u5a18\uff0c\u6839\u672c\u6ca1\u6709\u60f3\u5230\u8fd9\u4ef6\u4e8b\u60c5\u8868\u59d0\u65e2\u7136\u8981\u7792\u7740\u5979\uff0c\u5979 \u4e5f\u5c31\u5e94\u8be5\u77e5\u6240\u907f\u5fcc\u3002\u4e00\u65f6\u597d\u5947\u5fc3\u8d77\uff0c\u5c31\u6084\u6084\u5730\u8ddf\u5728\u8868\u59d0\u540e\u9762\u3002 \u3000\u3000\u5b5f\u660e\u971e\u7684\u8f7b\u529f\u6bd4\u4e25\u70f7\u9ad8\u660e\u5f97\u591a\uff0c\u800c\u4e14\u4e25\u70f7\u8bd5\u4e86\u51e0\u665a\uff0c\u89c1\u5979\u90fd\u662f\u719f\u7761\u5982\u6ce5\uff0c\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230 \u4eca\u665a\u4f1a\u6709\u4f8b\u5916\uff0c\u662f\u4ee5\u4e1d\u6beb\u6ca1\u52a0\u9632\u5907\u3002 \u3000\u3000\u8fd9\u665a\u6708\u8272\u6726\u80e7\uff0c\u5b5f\u660e\u971e\u8ddf\u8e2a\u8fdb\u5165\u4e25\u5bb6\u5c4b\u540e\u7684\u677e\u6797\u4e4b\u4e2d\uff0c\u5ffd\u542c\u5f97\u8868\u59d0\u8f7b\u8f7b\u62cd\u4e86\u4e09\u4e0b\u624b\u638c\uff0c \u8ddf\u7740\u677e\u6797\u4e2d\u4e5f\u6709\u4eba\u62cd\u4e86\u4e09\u4e0b\u624b\u638c\u76f8\u5e94\uff0c\u5b5f\u660e\u971e\u77e5\u9053\u8868\u59d0\u662f\u7ea6\u4eba\u76f8\u4f1a\uff0c\u540e\u6094\u4e0d\u8fed\uff0c\u60f3\u9053\uff1a\u201c\u65e9 \u77e5\u5982\u6b64\uff0c\u6211\u5b9e\u662f\u4e0d\u8be5\u6765\u5077\u7aa5\u5979\u7684\u79d8\u5bc6\uff01\u201d \u3000\u3000\u4f46\u4e0d\u6765\u4e5f\u5df2\u6765\u4e86\uff0c\u5b5f\u660e\u971e\u53ea\u597d\u8df3\u4e0a\u4e00\u68f5\u5927\u6811\uff0c\u85c9\u7740\u7e41\u679d\u5bc6\u53f6\uff0c\u906e\u63a9\u8eab\u5f62\uff0c\u514d\u5f97\u7ed9\u90a3\u4eba\u53d1 \u73b0\u3002 \u3000\u3000\u521a\u8eb2\u85cf\u597d\uff0c\u53ea\u89c1\u4e00\u6761\u9ed1\u5f71\u500f\u5730\u7a9c\u4e86\u51fa\u6765\uff0c\u8f7b\u8f7b\u53eb\u4e86\u4e00\u58f0\uff1a\u201c\u70f7\u59b9\u3002\u201d\u5b5f\u660e\u971e\u805a\u62e2\u76ee\u5149\u4ece \u6811\u4e0a\u671b\u4e0b\u53bb\uff0c\u8ba4\u5f97\u8fd9\u4e2a\u4eba\u6b63\u662f\u90a3\u5929\u6765\u7684\u90a3\u4e2a\u59d3\u8c37\u7684\u7f8e\u5c11\u5e74\u3002 \u3000\u3000\u5b5f\u660e\u971e\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u539f\u6765\u8fd9\u4e2a\u4eba\u8fd8\u6ca1\u6709\u8d70\uff0c\u5374\u8eb2\u5728\u8fd9\u91cc\u6bcf\u665a\u548c\u8868\u59d0\u5e7d\u4f1a\u3002 \u3000\u3000\u201c\u5440\uff0c\u8868\u59d0\u5fd2\u4e5f\u5927\u80c6\uff0c\u82e5\u662f\u7ed9\u59d1\u7236\u59d1\u6bcd\u77e5\u9053\uff0c\u8fd9\u53ef\u5982\u4f55\u662f\u597d\uff1f\u201d \u3000\u3000\u53ea\u542c\u5f97\u8c37\u6db5\u865a\u8bf4\u9053\uff1a\u201c\u70f7\u59b9\uff0c\u4eca\u665a\u6050\u6015\u662f\u54b1\u4eec\u6700\u540e\u4e00\u6b21\u76f8\u4f1a\u4e86\uff0c\u6211\u662f\u6765\u5411\u4f60\u544a\u522b\u7684 \u4e86\u3002\u201d \u3000\u3000\u4e25\u70f7\u5403\u4e86\u4e00\u60ca\uff0c\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u4f60\u3001\u4f60\u8981\u8d70\u4e86\u4e48\uff1f\u201d \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u60f3\u660e\u5929\u5c31\u56de\u53bb\u4e86\u3002\u54b1\u4eec\u8fd9\u6837\u4e0b\u53bb\uff0c\u4e0d\u662f\u529e\u6cd5\u3002\u4f60\u6bcf\u665a\u51fa\u6765\uff0c\u6211\u90fd \u4e3a\u4f60\u5fc3\u60ca\u80c6\u98a4\uff0c\u4fd7\u8bed\u8bf4\u4e0a\u5f97\u5c71\u591a\u9047\u8001\u864e\uff0c\u603b\u6709\u4e00\u5929\u4f1a\u7ed9\u4f60\u7236\u6bcd\u53d1\u89c9\u7684\u3002\u4f60\u7684\u7236\u6bcd\u53c8\u662f\u90a3\u6837\u4e25 \u5389\uff0c\u53ea\u6015\u4e0d\u80fd\u5bb9\u4f60\u8d25\u574f\u95e8\u98ce\u3002\u201d \u3000\u3000\u4e25\u70f7\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u4f60\u653e\u5fc3\uff0c\u6211\u7684\u7236\u6bcd\u51b3\u4e0d\u4f1a\u53d1\u89c9\u3002\u201d\u8c37\u6db5\u865a\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u4e25\u70f7\u9053\uff1a \u201c\u56e0\u4e3a\u6211\u5bb6\u6765\u4e86\u4e00\u4f4d\u5ba2\u4eba\u3002\u201d\u8c37\u6db5\u865a\u9053\uff1a\u201c\u5bf9\u5566\uff0c\u90a3\u5929\u4e2b\u9b1f\u628a\u4f60\u53eb\u51fa\u53bb\uff0c\u8bf4\u662f\u6765\u4e86\u8fdc\u5ba2\uff0c\u6211 \u8fd8\u6ca1\u6709\u95ee\u4f60\uff0c\u8fd9\u4f4d\u5ba2\u4eba\u662f\u8c01\u5462\uff1f\u4f46\u8fd9\u4e24\u4ef6\u4e8b\u60c5\uff0c\u53c8\u6709\u4ec0\u4e48\u5173\u8fde\uff1f\u201d \u3000\u3000\u4e25\u70f7\u9053\uff1a\u201c\u4f60\u4e00\u5b9a\u542c\u8fc7\u5979\u7684\u7236\u4eb2\u7684\u540d\u5b57\uff0c\u5979\u662f\u6c5f\u5357\u5927\u4fa0\u5b5f\u5c11\u521a\u7684\u5973\u513f\uff0c\u4e5f\u662f\u6211\u7684\u8868 \u59b9\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u6c5f\u5357\u5927\u4fa0\u5b5f\u5c11\u521a\u662f\u4f60\u7684\u8205\u7236\uff0c\u6211\u662f\u4e45\u4ef0\u4ed6\u7684\u5927\u540d\u4e86\u3002\u4e0d\u8fc7\uff0c\u4f60\u7684 \u8868\u59b9\u6765\u4e86\uff0c\u4f60\u4e0d\u662f\u66f4\u591a\u4e00\u5c42\u987e\u5fcc\u4e48\uff1f\u5374\u53c8\u4f55\u4ee5\u53cd\u800c\u53ef\u4ee5\u53eb\u6211\u653e\u5fc3\u5462\uff1f\u201d \u3000\u3000\u4e25\u70f7\u9053\uff1a\u201c\u6211\u82e5\u662f\u5355\u72ec\u4e00\u4eba\uff0c\u5988\u6216\u8005\u4f1a\u628a\u6211\u770b\u7ba1\u5f97\u66f4\u4e25\u3002\u6211\u8868\u59b9\u6765\u4e86\uff0c\u5979\u548c\u6211\u540c\u4e00\u95f4\u5367 \u623f\uff0c\u5988\u51b3\u60f3\u4e0d\u5230\u6211\u4f1a\u5728\u534a\u591c\u91cc\u5077\u5077\u51fa\u53bb\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u4f60\u628a\u54b1\u4eec\u7684\u4e8b\u60c5\u544a\u8bc9\u4e86\u8868\u59b9\uff0c\u548c\u5979\u4e32\u901a\u597d\u4e86\u7684\u4e48\uff1f\u201d \u3000\u3000\u4e25\u70f7\u9053\uff1a\u201c\u8fd9\u5012\u4e0d\u662f\u3002\u6211\u6bcf\u665a\u5728\u6a80\u9999\u7089\u4e2d\u52a0\u4e0a\u4e00\u79cd\u7279\u522b\u9999\u6599\uff0c\u540d\u4e3a\u54ed\u9163\u9999\uff0c\u6709\u8ff7\u9999\u7684\u529f \u6548\uff0c\u5374\u65e0\u8ff7\u9999\u7684\u5bb3\u5904\u3002\u5979\u5438\u4e86\u8fd9\u79cd\u9999\u6c14\uff0c\u4e00\u89c9\u5c31\u8981\u7761\u5230\u5927\u5929\u5149\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u6447\u4e86\u6447\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u53ea\u80fd\u7792\u8fc7\u4e00\u65f6\uff0c\u603b\u4e0d\u662f\u957f\u4e45\u4e4b\u8ba1\u3002\u800c\u4e14\u5982\u6b64\u5bf9\u4f60\u7684\u8868\u59b9\uff0c \u4e5f\u4e0d\u5927\u597d\u3002\u201d \u3000\u3000\u4e25\u70f7\u9053\uff1a\u201c\u54b1\u4eec\u53ea\u80fd\u89c1\u4e00\u6b65\u8d70\u4e00\u6b65\u4e86\uff0c\u6211\u672c\u6765\u8981\u548c\u4f60\u79c1\u5954\u7684\uff0c\u4f60\u53c8\u4e0d\u80af\u3002\u4f60\u8bf4\uff0c\u4f60\u6709\u4ec0 \u4e48\u957f\u4e45\u4e4b\u8ba1\uff1f\u201d \u3000\u3000\u8c37\u6db5\u865a\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u3001\u6211\u4e0d\u77e5\u9053\u3002\u4f46\u6211\u77e5\u9053\u4f60\u6765\u6625\u5c31\u8981\u505a\u65b0\u5a18\u5b50\u4e86\uff0c\u8fd9\u662f\u4f60\u7239 \u7239\u544a\u8bc9\u6211\u7684\u3002\u6211\u53ef\u4e0d\u80fd\u8d25\u574f\u4f60\u7684\u540d\u8282\u3002\u201d \u3000\u3000\u4e25\u70f7\u9053\uff1a\u201c\u54e6\uff0c\u4f60\u662f\u8ba4\u4e3a\u96be\u4ee5\u633d\u56de\uff0c\u6240\u4ee5\u5c31\u60f3\u4e00\u8d70\u4e86\u4e4b\u4e48\uff1f\u201d \u3000\u3000\u8c37\u6db5\u865a\u8bb7\u8bb7\u8bf4\u9053\uff1a\u201c\u4e0d\uff0c\u4e0d\uff0c\u6211\u4e0d\u662f\u8fd9\u6837\u7684\u4eba\uff0c\u4f60\u522b\u8bef\u4f1a\u3002\u5509\uff0c\u6211\u4e0d\u77e5\u5982\u4f55\u8bf4\u624d\u597d\uff0c\u6211 \u8fd8\u6ca1\u6709\u60f3\u51fa\u529e\u6cd5\uff0c\u4e0d\u8fc7\uff0c\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u4e25\u70f7\u5ffd\u5730\u683c\u683c\u4e00\u7b11\uff0c\u8bf4\u9002\uff1a\u201c\u6211\u5012\u662f\u6709\u4e2a\u4e3b\u610f\u3002\u201d\u8c37\u6db5\u865a\u9053\uff1a\u201c\u4ec0\u4e48\u4e3b\u610f\uff1f\u201d\u4e25\u70f7\u9053\uff1a \u201c\u6211\u8fd9\u8868\u59b9\uff0c\u4eba\u54c1\u6b66\u529f\u76f8\u8c8c\u90fd\u662f\u4e0a\u4e0a\u4e4b\u9009\uff0c\u5c31\u53ef\u60dc\u5e74\u7eaa\u5c0f\u4e00\u70b9\u3002\u201d\u5b5f\u660e\u971e\u8eb2\u5728\u6811\u4e0a\u5077\u542c\uff0c\u542c \u5230\u8fd9\u91cc\uff0c\u4e0d\u89c9\u6697\u9a82\u8868\u59d0\u5c82\u6709\u6b64\u7406\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u6b63\u5bb9\u8bf4\u9053\uff1a\u201c\u70f7\u59b9\uff0c\u4e0d\u8981\u4e71\u5f00\u73a9\u7b11\uff01\u6211\u5fc3\u91cc\u53ea\u6709\u4f60\uff0c\u96be\u9053\u4f60\u8fd8\u4e0d\u77e5\u9053\u5417\uff1f\u6211\u4fe9\u4e4b \u4e8b\uff0c\u5373\u4f7f\u6211\u65e0\u6cd5\u53ef\u4ee5\u6362\u56de\uff0c\u4eca\u751f\u6211\u4e5f\u51b3\u4e0d\u53e6\u5a36\uff01\u201d \u3000\u3000\u4e25\u70f7\u9053\uff1a\u201c\u662f\u5440\uff0c\u4f60\u662f\u8fd9\u6837\uff0c\u96be\u9053\u6211\u5c31\u4e0d\u662f\u8fd9\u6837\u5417\uff1f\u8bf4\u4ec0\u4e48\u6015\u6bc1\u4e86\u6211\u7684\u540d\u8282\uff0c\u6211\u4fe9\u4e0d\u80fd \u7ed3\u6210\u592b\u5987\uff0c\u6211\u53c8\u5c82\u80fd\u53e6\u5ac1\u4ed6\u4eba\uff1f\u201d \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u6211\u5e76\u975e\u4e22\u4e0b\u4e0d\u7ba1\uff0c\u6211\u60f3\u56de\u53bb\u548c\u7389\u5cf0\u9053\u957f\u5546\u91cf\u5546\u91cf\uff0c\u770b\u770b\u4ed6\u8fd8\u6709\u4ec0\u4e48\u529e\u6cd5\u53ef \u4ee5\u5e2e\u5fd9\uff1f\u4f60\u8fd9\u8fb9\u4e5f\u8fd8\u53ef\u4ee5\u5c3d\u529b\u8bbe\u6cd5\u3002\u201d \u3000\u3000\u4e25\u70f7\u9053\uff1a\u201c\u6211\u8fd8\u6709\u4ec0\u4e48\u6cd5\u5b50\u597d\u60f3\uff1f\u9664\u4e86\u79c1\u5954\u4e4b\u5916\uff01\u201d \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u6211\u60f3\u5973\u513f\u5728\u6bcd\u4eb2\u9762\u524d\u8bf4\u8bdd\u5bb9\u6613\u4e00\u4e9b\u3002\u4f60\u4e0d\u8981\u6015\u96be\u4e3a\u60c5\uff0c\u548c\u4f60\u6bcd\u4eb2\u8bf4\u5427\u3002\u5766 \u767d\u5730\u544a\u8bc9\u5979\uff0c\u4f60\u559c\u6b22\u7684\u662f\u6211\uff0c\u6c42\u5979\u6210\u5168\u6211\u4eec\uff0c\u8bf4\u4e0d\u5b9a\u4f2f\u6bcd\u4f1a\u7b54\u5e94\u7684\u3002\u201d \u3000\u3000\u4e25\u70f7\u6447\u4e86\u6447\u5934\uff0c\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u6211\u5766\u767d\u544a\u8bc9\u4f60\u5427\uff0c\u8fd9\u662f\u505a\u68a6\uff01\u5988\u6bd4\u7239\u66f4\u96be\u8bf4\u8bdd\uff01\u7239\u8fd8\u8bb0\u7740 \u4f60\u7684\u6069\u60c5\uff0c\u8bf4\u662f\u8981\u62a5\u7b54\u4f60\u3002\u5988\u5374\u7981\u6b62\u6211\u4ee5\u540e\u548c\u4f60\u518d\u89c1\u9762\u4e86\u5462\u3002\u5979\u5929\u5929\u90fd\u5728\u6559\u8bad\u6211\uff0c\u8981\u6211\u8c28\u9075 \u793c\u6cd5\uff0c\u6068\u4e0d\u5f97\u6211\u811a\u6b65\u4e0d\u51fa\u95fa\u95e8\uff0c\u4e56\u4e56\u5730\u7b49\u7740\u4e0a\u82b1\u8f7f\uff0c\u8fd9\u624d\u662f\u5979\u7684\u597d\u5973\u513f\uff0c\u4f60\u53eb\u6211\u5982\u4f55\u5411\u5979\u8bf4 \u60c5\uff1f\u201d \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u90a3\u4e48\u4ee4\u5c0a\u9762\u524d\uff0c\u662f\u5426\u8fd8\u6709\u633d\u56de\u4f59\u5730\uff1f\u201d \u3000\u3000\u4e25\u70f7\u53c8\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u5f20\u5bb6\u662f\u7239\u7239\u7684\u597d\u670b\u53cb\uff0c\u4ed6\u51b3\u4e0d\u4f1a\u8bb8\u6211\u9000\u5a5a\u7684\u3002\u7239\u7239\u7684\u813e\u6c14\u867d \u7136\u6ca1\u6709\u5988\u7684\u56fa\u6267\uff0c\u4e5f\u662f\u4e2a\u6781\u7231\u9762\u5b50\u7684\u4eba\u3002\u4ed6\u8ba4\u4e3a\u662f\u6709\u8fb1\u95e8\u98ce\u7684\u4e8b\u60c5\uff0c\u600e\u6837\u8bf4\u4e5f\u6ca1\u6709\u7528\u3002\u201d \u3000\u3000\u4e25\u70f7\u505c\u4e86\u7247\u523b\uff0c\u63a5\u7740\u53c8\u9053\uff1a\u201c\u4f60\u8fd9\u6b21\u5728\u5c0f\u91d1\u5ddd\u5e2e\u4e86\u6211\u7239\u7239\u8fd9\u6837\u7684\u5927\u5fd9\uff0c\u6211\u672c\u4ee5\u4e3a\u4e8b\u60c5\u53ef \u6709\u8f6c\u673a\u7684\uff0c\u8c01\u77e5\u8fd8\u662f\u4e00\u6837\u3002\u55ef\uff0c\u8bf4\u8d77\u6765\u6211\u5012\u60f3\u95ee\u4e00\u95ee\u4f60\uff0c\u90a3\u6b21\u600e\u4f1a\u6709\u8fd9\u6837\u6070\u5de7\u597d\u8ba9\u4f60\u78b0\u4e0a \u4e86\uff1f\u201d \u3000\u3000\u8c37\u6db5\u865a\u7b11\u9053\uff1a\u201c\u4e0d\u662f\u5de7\u9047\uff0c\u662f\u6211\u6709\u5fc3\u8ddf\u8e2a\u4f60\u4eec\u7684\u3002\u4f60\u4e0d\u662f\u8bf4\u8fc7\u4f60\u4eec\u5c06\u6709\u5c0f\u91d1\u5ddd\u4e4b\u884c\u5417\uff0c \u6211\u5728\u90a3\u6761\u8def\u4e0a\u7b49\u5019\u4e86\u5dee\u4e0d\u591a\u4e00\u4e2a\u6708\u5462\uff0c\u6ec7\u5357\u4e03\u864e\u8fd8\u662f\u968f\u540e\u6765\u7684\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u542c\u5230\u8fd9\u91cc\uff0c\u65b9\u59cb\u660e\u767d\uff1a\u201c\u539f\u6765\u4ed6\u4eec\u662f\u65e9\u5c31\u76f8\u8bc6\u7684\uff0c\u5e76\u975e\u8868\u59d0\u4e00\u89c1\u949f\u60c5\u3002\u201d \u3000\u3000\u4e25\u70f7\u9053\uff1a\u201c\u53ef\u60dc\u5374\u8f9c\u8d1f\u4f60\u7684\u82e6\u5fc3\u4e86\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u6ec7\u5357\u4e03\u864e\u8981\u5bb3\u4f60\u7684\u7239\u7239\uff0c\u5373\u4f7f\u4f60\u6211\u5e76\u4e0d\u76f8\u8bc6\uff0c\u8fd9\u4ef6\u4e8b\u60c5\u7ed9\u6211\u77e5\u9053\u4e86\uff0c\u6211\u4e5f \u662f\u4e0d\u80fd\u4e0d\u7ba1\u7684\u3002\u6211\u5012\u662f\u4f69\u670d\u4f60\u7684\u82e6\u5fc3\uff0c\u4f60\u90a3\u6b21\u88c5\u4f5c\u4e0e\u6211\u5e76\u4e0d\u76f8\u8bc6\uff0c\u88c5\u5f97\u771f\u50cf\u3002\u5e78\u4e8f\u5982\u6b64\uff0c\u5426 \u5219\u8ba9\u4f60\u7239\u7239\u77e5\u9053\uff0c\u90a3\u5c31\u6050\u6015\u66f4\u7cdf\u4e86\u3002\u201d \u3000\u3000\u4e25\u70f7\u9053\uff1a\u201c\u82e6\u5fc3\u4e5f\u6ca1\u6709\u7528\uff0c\u73b0\u5728\u5df2\u7ecf\u662f\u7cdf\u900f\u4e86\u3002\u4f9d\u6211\u770b\u6765\uff0c\u4f60\u56de\u53bb\u6c42\u7389\u5cf0\u9053\u957f\u8bbe\u6cd5\u4e5f\u6ca1 \u6709\u7528\u3002\u7389\u5cf0\u9053\u957f\u867d\u7136\u662f\u6211\u7239\u7239\u5c0a\u656c\u7684\u4eba\uff0c\u4f46\u603b\u6bd4\u4e0d\u4e0a\u4ed6\u7684\u9762\u5b50\u8981\u7d27\u3002\u4ed6\u4e0d\u4f1a\u5bb9\u8bb8\u6709\u4e2a\u6094\u5a5a\u7684 \u5973\u513f\uff01\u201d \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u8fd9\u6837\u8bf4\u6765\uff0c\u54b1\u4eec\u7adf\u662f\u6ca1\u6709\u6cd5\u5b50\u53ef\u60f3\u4e86\u4e48\uff1f\u201d \u3000\u3000\u4e25\u70f7\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u7684\u6cd5\u5b50\uff0c\u4f60\u53c8\u4e0d\u80af\u4f9d\u4ece\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u79c1\u5954\uff1f\u201d\u4e25\u70f7\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u9664\u4e86\u79c1\u5954\uff0c\u54ea\u8fd8\u6709\u522b\u7684\u8def\u53ef\u8d70\uff01\u201d\u8c37\u6db5\u865a\u9053\uff1a \u201c\u90a3\u5c82\u4e0d\u662f\u66f4\u8981\u6c14\u574f\u4f60\u7684\u7239\u5a18\uff1f\u6211\u3001\u6211\u4e5f\u6015\u4f60\u53d7\u4e0d\u4f4f\u4eba\u5bb6\u95f2\u8bdd\uff0c\u5c06\u6765\u5728\u4eba\u524d\u62ac\u4e0d\u8d77\u5934\u6765\uff0c\u4f1a \u540e\u6094\u7684\u3002\u201d \u3000\u3000\u4e25\u70f7\u9053\uff1a\u201c\u6211\u60f3\u8fc7\u4e86\uff0c\u54b1\u4eec\u8fd9\u4e48\u4e00\u8d70\uff0c\u7239\u5a18\u5f53\u7136\u662f\u8981\u5927\u5927\u751f\u6c14\uff0c\u4e00\u5b9a\u4e0d\u4f1a\u8ba4\u6211\u8fd9\u4e2a\u5973\u513f \u7684\u3002\u4f46\u8fc7\u4e86\u4e09\u5e74\u4e94\u8f7d\uff0c\u7239\u5a18\u8001\u4e86\uff0c\u4ed6\u4eec\u4f1a\u60f3\u5ff5\u6211\u7684\uff0c\u90a3\u65f6\u54b1\u4eec\u518d\u53bb\u6c42\u60c5\uff0c\u6211\u60f3\u4ed6\u4eec\u591a\u534a\u4f1a\u6536 \u56de\u6210\u547d\u3002 \u3000\u3000\u201c\u81f3\u4e8e\u8bf4\u5230\u522b\u4eba\u7684\u95f2\u8bdd\uff0c\u6211\u6839\u672c\u5c31\u4e0d\u653e\u5728\u5fc3\u4e0a\u3002\u8fd9\u662f\u54b1\u4eec\u4e24\u4eba\u7684\u4e8b\u60c5\uff0c\u53ea\u8981\u4f60\u6211\u89c9\u5f97\u5e78 \u798f\uff0c\u7ba1\u5b83\u522b\u4eba\u8bf4\u4e9b\u4ec0\u4e48\uff01\u201d \u3000\u3000\u5b5f\u660e\u971e\u8eb2\u5728\u6811\u4e0a\u5077\u542c\uff0c\u542c\u5230\u8fd9\u91cc\uff0c\u5fc3\u4e2d\u6697\u6697\u8d5e\u53f9\uff1a\u201c\u8868\u59d0\u771f\u4e0d\u6127\u662f\u6562\u4f5c\u6562\u4e3a\u7684\u5dfe\u5e3c\u987b \u7709\u3002\u8c37\u6db5\u865a\u8eab\u4e3a\u7537\u5b50\uff0c\u5012\u4f3c\u4e0d\u5982\u5979\u5462\u3002\u201d\u672c\u6765\u5b5f\u660e\u971e\u8d77\u521d\u4e5f\u662f\u4e0d\u5927\u8d5e\u540c\u8868\u59d0\u79c1\u5954\u7684\uff0c\u6b64\u65f6\u4e0d \u77e5\u4e0d\u89c9\u53d7\u4e86\u8868\u59d0\u7684\u5f71\u54cd\uff0c\u53cd\u800c\u6050\u6015\u8c37\u6db5\u865a\u4e0d\u6562\u7b54\u5e94\u4e86\u3002 \u3000\u3000\u4e00\u9635\u98ce\u5439\u8fc7\uff0c\u8305\u8349\u55e6\u55e6\u4f5c\u54cd\uff0c\u5b5f\u660e\u971e\u542c\u5f97\u51fa\u795e\uff0c\u6839\u672c\u6ca1\u6709\u7559\u610f\u8fd9\u58f0\u97f3\u6709\u70b9\u5f02\u6837\u3002 \u3000\u3000\u8c37\u6db5\u865a\u7259\u6839\u4e00\u54ac\uff0c\u6bc5\u7136\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u65e2\u7136\u4f60\u90fd\u4e0d\u6015\uff0c\u6211\u8fd8\u6015\u4e9b\u4ec0\u4e48\uff01\u4f60\u8981\u4e0d\u8981\u56de\u53bb\u6536\u62fe \u4e1c\u897f\uff1f\u201d \u3000\u3000\u4e25\u70f7\u7b11\u9765\u5982\u82b1\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u53ea\u8981\u4f60\uff0c\u522b\u7684\u4ec0\u4e48\u90fd\u4e0d\u8981\u5566\uff01\u201d \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4e48\u54b1\u4eec\u73b0\u5728\u5c31\u8d70\uff01\u201d \u3000\u3000\u8bdd\u72b9\u672a\u4e86\uff0c\u5ffd\u542c\u5f97\u4e00\u4e2a\u51b7\u5ced\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u8d70\uff1f\u8d70\u5f97\u8fd9\u4e48\u5bb9\u6613\uff01\u201d \u3000\u3000\u8305\u8349\u4e1b\u4e2d\uff0c\u7a81\u7136\u7a9c\u51fa\u56db\u4e94\u4eba\u6765\uff0c\u8bf4\u8bdd\u7684\u4eba\u662f\u4e2a\u4e8c\u5341\u6765\u5c81\u7684\u5c11\u5e74\uff0c\u624b\u4e2d\u62ff\u7740\u4e00\u67c4\u660e\u6643\u6643\u7684 \u5229\u5251\uff0c\u6307\u7740\u8c37\u6db5\u865a\uff0c\u8138\u4e0a\u597d\u50cf\u522e\u5f97\u4e0b\u4e00\u5c42\u971c\u3002 \u3000\u3000\u8c37\u6db5\u865a\u5927\u5403\u4e00\u60ca\uff0c\u5931\u58f0\u53eb\u9053\uff1a\u201c\u4f60\u3001\u4f60\u3001\u4f60\u662f\u2026\u2026\u201d\u4e00\u4e2a\u201c\u8c01\u201d\u5b57\u5728\u820c\u5c16\u6253\u8f6c\uff0c\u5fc3\u4e2d\u5df2 \u7136\u660e\u767d\u4e86\u51e0\u5206\uff0c\u4e0d\u6562\u95ee\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u90a3\u4eba\u51b7\u7b11\u9053\uff1a\u201c\u59d3\u8c37\u7684\uff0c\u4f60\u4e0d\u8ba4\u5f97\u6211\uff0c\u8fd9\u5c0f\u8d31\u4eba\u5e94\u8be5\u8ba4\u5f97\u6211\uff01\u201d \u3000\u3000\u8ddf\u5728\u4ed6\u8eab\u8fb9\u7684\u4e00\u4e2a\u7626\u957f\u6c49\u5b50\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6211\u8bf4\u4f60\u7684\u5a46\u5a18\u5077\u4eba\uff0c\u4f60\u4e0d\u76f8\u4fe1\uff0c\u73b0\u5728\u4f60\u4eb2\u773c\u89c1 \u5230\u4e86\u5427\uff1f\u6349\u8d3c\u6349\u8d43\uff0c\u6349\u5978\u6349\u53cc\uff0c\u5978\u60c5\u786e\u51ff\uff0c\u4f60\u8fd8\u548c\u4ed6\u4eec\u591a\u8bf4\u5e9f\u8bdd\u5e72\u5417\uff1f\u201d \u3000\u3000\u8fd9\u4e2a\u5c11\u5e74\u6b63\u662f\u4e25\u70f7\u7684\u672a\u5a5a\u592b\u5f20\u5143\u5409\u3002\u4ed6\u66fe\u7ecf\u6765\u8fc7\u4e25\u5bb6\u51e0\u6b21\uff0c\u4e25\u70f7\u867d\u6ca1\u6709\u51fa\u6765\u89c1\u4ed6\uff0c\u4e5f\u66fe \u9694\u5e18\u5077\u7aa5\uff0c\u8ba4\u5f97\u4ed6\u3002 \u3000\u3000\u53e6\u5916\u4e09\u4e2a\u4eba\u4e25\u70f7\u4e5f\u8ba4\u5f97\u662f\u5f20\u5143\u5409\u7684\u5e08\u5144\u4f2f\uff0c\u53ea\u6709\u90a3\u4e2a\u7626\u957f\u6c49\u5b50\uff0c\u4e25\u70f7\u5374\u4e0d\u77e5\u9053\u662f\u4ec0\u4e48 \u4eba\u3002 \u3000\u3000\u4e25\u70f7\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u65e2\u7136\u4eb2\u773c\u89c1\u5230\uff0c\u6211\u4e5f\u4e0d\u5fc5\u7792\u4f60\uff0c\u6211\u559c\u6b22\u7684\u662f\u4ed6\uff0c\u6211\u4e0d\u613f\u5ac1\u4f60\u3002\u8fd9\u95e8 \u4eb2\u4e8b\u662f\u6211\u7239\u7239\u548c\u4f60\u5b9a\u4e0b\u7684\uff0c\u4f60\u627e\u6211\u7239\u7239\u9000\u5a5a\u53bb\u5427\uff01\u201d \u3000\u3000\u6b64\u8a00\u4e00\u51fa\uff0c\u5f53\u771f\u662f\u6709\u5982\u77f3\u7834\u5929\u60ca\uff01\u8981\u77e5\u5b8b\u4ee3\u6700\u8bb2\u7a76\u793c\u6cd5\uff0c\u7537\u5a5a\u5973\u5ac1\uff0c\u5fc5\u5b9a\u662f\u7531\u7236\u6bcd\u4e4b \u547d\u3001\u5a92\u707c\u4e4b\u8a00\uff0c\u8fd9\u5df2\u7ecf\u662f\u88ab\u5f53\u4f5c\u5929\u7ecf\u5730\u4e49\u7684\u4e86\u3002\u4e25\u70f7\u7684\u672a\u5a5a\u592b\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u5979\u4f1a\u8bf4\u51fa\u8fd9\u756a\u8bdd \u6765\uff0c\u767b\u65f6\u6c14\u5f97\u624b\u8db3\u51b0\u51b7\uff01 \u3000\u3000\u5f20\u5143\u5409\u7684\u5927\u5e08\u5144\u4e54\u5143\u58ee\u5927\u6012\u65a5\u9053\uff1a\u201c\u4e0d\u8981\u8138\u7684\u5c0f\u8d31\u4eba\uff0c\u7adf\u6562\u8bf4\u51fa\u8fd9\u7b49\u8bdd\u6765\uff01\u5f20\u5e08\u5f1f\uff0c\u4f60 \u4e0b\u4e0d\u4e86\u624b\uff0c\u6211\u53ef\u8981\u66ff\u4f60\u4e0b\u624b\u4e86\u3002\u201d \u3000\u3000\u4e54\u5143\u58ee\u4e00\u51fa\u624b\u5c31\u662f\u5927\u64d2\u62ff\u624b\u6cd5\uff0c\u4e94\u6307\u5982\u94a9\uff0c\u201c\u535c\u201d\u7684\u5411\u4e25\u70f7\u80a9\u5934\u63d2\u4e0b\uff0c\u8fd9\u4e00\u4e0b\u82e5\u7ed9\u4ed6\u63d2 \u4e2a\u6b63\u7740\uff0c\u7435\u7436\u80af\u5b9a\u5c06\u788e\u88c2\u65e0\u7591\uff0c\u8c37\u6db5\u865a\u7109\u80fd\u8ba9\u4ed6\u4f24\u5bb3\u4e25\u70f7\uff0c\u89c1\u4ed6\u6765\u52bf\u51f6\u731b\uff0c\u5f53\u4e0b\u4e00\u62db\u201c\u5982\u5c01 \u4f3c\u95ed\u201d\u7684\u201c\u53cc\u5df7\u624b\u201d\uff0c\u628a\u4e54\u5143\u58ee\u8361\u5f00\u3002 \u3000\u3000\u4e54\u5143\u58ee\u7684\u90a3\u80a1\u731b\u52b2\u7ed9\u5bf9\u65b9\u4e00\u5c01\uff0c\u53cd\u9707\u56de\u6765\uff0c\u4e0d\u7531\u81ea\u5df1\u7684\u5012\u9000\u4e09\u6b65\uff0c\u8d8a\u53d1\u5927\u6012\uff0c\u559d\u9053\uff1a \u201c\u5b63\u5e08\u5f1f\u3001\u6881\u5e08\u5f1f\uff0c\u4f60\u4eec\u628a\u8fd9\u8d31\u4eba\u62ff\u4e0b\uff01\u5f20\u5e08\u5f1f\uff0c\u4f60\u5bf9\u8fd9\u5c0f\u8d31\u4eba\u5bb9\u6216\u6709\u60c5\uff0c\u4e0d\u5fcd\u4e0b\u624b\u3002\u8fd9\u5c0f \u5b50\u5077\u4e86\u4f60\u7684\u8001\u5a46\uff0c\u96be\u9053\u4f60\u4e5f\u54bd\u5f97\u4e0b\u8fd9\u4e00\u53e3\u6c14\uff0c\u773c\u7741\u7741\u505a\u4e2a\u6d3b\u4e4c\u9f9f\u5417\uff1f\u201d\u539f\u6765\u4e54\u5143\u58ee\u867d\u7136\u662f\u5927 \u5e08\u5144\uff0c\u4f46\u5728\u6b66\u5f53\u6d3e\u7b2c\u4e8c\u4ee3\u5f1f\u5b50\u4e4b\u4e2d\uff0c\u6b66\u529f\u6700\u5f3a\u7684\u5374\u662f\u4ed6\u7684\u4e09\u5e08\u5f1f\u5f20\u5143\u5409\uff0c\u4ed6\u81ea\u6043\u4e0d\u662f\u8c37\u6db5\u865a \u7684\u5bf9\u624b\uff0c\u4e0d\u80fd\u4e0d\u53eb\u4ed6\u7684\u5e08\u5f1f\u4e0a\u6765\u5e2e\u5fd9\u3002 \u3000\u3000\u5f20\u5143\u5409\u5446\u4e86\u4e00\u5446\uff0c\u597d\u50cf\u662f\u4ece\u6076\u68a6\u4e2d\u9192\u6765\u4e00\u6837\uff0c\u4f46\u201c\u5978\u592b\u201d\u201c\u6deb\u5987\u201d\u5374\u5728\u9762\u524d\uff0c\u8fd9\u5206\u660e\u662f \u4e8b\u5b9e\u800c\u4e0d\u662f\u5e7b\u68a6\uff01\u5f20\u5143\u5409\u542c\u4e86\u5927\u5e08\u5144\u7684\u8bdd\uff0c\u767b\u65f6\u6740\u673a\u9661\u8d77\uff0c\u6012\u53d1\u5982\u72c2\uff0c\u62d4\u5251\u51fa\u9798\uff0c\u4fbf\u5411\u8c37\u6db5 \u865a\u6251\u53bb\uff0c\u559d\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u6211\u4e0e\u4f60\u62fc\u4e86\uff01\u201d \u3000\u3000\u53e6\u4e00\u8fb9\uff0c\u5f20\u5143\u5409\u7684\u4e8c\u5e08\u5144\u5b63\u5143\u4f26\u548c\u56db\u5e08\u5f1f\u6881\u5143\u732e\u4ea6\u5df2\u62d4\u5251\u51fa\u9798\uff0c\u5de6\u53f3\u9f50\u4e0a\uff0c\u5bf9\u4e25\u70f7\u5939 \u653b\u3002\u4e25\u70f7\u6012\u9053\uff1a\u201c\u542b\u8840\u55b7\u4eba\uff0c\u81ea\u6c61\u5176\u53e3\uff01\u672c\u6765\u6211\u770b\u5728\u7239\u7239\u4efd\u4e0a\uff0c\u4e0d\u613f\u548c\u4f60\u4eec\u8ba1\u8f83\uff0c\u4f60\u4eec\u65e2\u7136 \u5b9a\u8981\u52a8\u624b\uff0c\u6211\u4e25\u70f7\u53ef\u662f\u4e0d\u80fd\u53d7\u4eba\u6b3a\u4fae\u7684\uff01\u201d\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u9752\u94a2\u5251\u626c\u7a7a\u4e00\u95ea\uff0c\u5de6\u4e00\u62db\u201c\u4e07\u91cc\u98de \u971c\u201d\uff0c\u53f3\u4e00\u62db\u201c\u5343\u5c71\u843d\u6728\u201d\uff0c\u628a\u5b63\u3001\u6881\u4e8c\u4eba\u7684\u653b\u52bf\uff0c\u5c3d\u90fd\u5316\u89e3\uff0c\u63a5\u7740\u53eb\u9053\uff1a\u201c\u8c37\u5927\u54e5\uff0c\u522b\u4eba \u8981\u4f60\u7684\u6027\u547d\uff0c\u4f60\u8fd8\u548c\u4ed6\u4eec\u5ba2\u6c14\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5f20\u5143\u5409\u4e0d\u6127\u662f\u6b66\u5f53\u7b2c\u4e8c\u4ee3\u5f1f\u5b50\u4e2d\u7684\u7b2c\u4e00\u9ad8\u624b\uff0c\u867d\u7136\u662f\u5728\u76db\u6012\u4e4b\u4e2d\uff0c\u5251\u6cd5\u5374\u662f\u4e1d\u6beb\u4e0d\u4e71\uff0c \u51fa\u624b\u51cc\u5389\u4e4b\u6781\uff01 \u3000\u3000\u53ea\u89c1\u9752\u5149\u4e00\u95ea\uff0c\u5f20\u5143\u5409\u7684\u5251\u5c16\u5df2\u6307\u5230\u4e86\u8c37\u6db5\u865a\u7684\u54bd\u5589\u3002\u4e0e\u6b64\u540c\u65f6\uff0c\u4e54\u5143\u58ee\u4ea6\u5df2\u662f\u53cc\u638c\u9f50 \u51fa\uff0c\u5de6\u638c\u63d2\u5411\u4ed6\u7684\u80f8\u812f\uff0c\u53f3\u638c\u5411\u4ed6\u7684\u5929\u7075\u76d6\u62cd\u4e0b\uff01 \u3000\u3000\u5e08\u5144\u5f1f\u540c\u65f6\u653b\u5230\uff0c\u53cc\u638c\u4e00\u5251\uff0c\u90fd\u662f\u5236\u4eba\u6b7b\u547d\u7684\u7edd\u62db\uff01\u8fd9\u4e00\u4e0b\uff0c\u8c37\u6db5\u865a\u7684\u6db5\u517b\u518d\u597d\uff0c\u4e5f\u4e0d \u7531\u5f97\u5fc3\u5934\u706b\u8d77\u4e86\u3002 \u3000\u3000\u8c37\u6db5\u865a\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5373\u4f7f\u4f60\u6068\u6211\u593a\u4e86\u4f60\u7684\u672a\u5a5a\u59bb\uff0c\u4e5f\u4e0d\u8be5\u5c31\u8981\u628a\u6211\u7f6e\u4e8e\u6b7b\u5730\u5440\uff01\u201d\u6012\u6c14 \u4e00\u751f\uff0c\u5f53\u4e0b\u4e5f\u5c31\u987e\u4e0d\u5f97\u8fd9\u8bb8\u591a\u4e86\u3002 \u3000\u3000\u638c\u98ce\u5251\u5f71\u4e4b\u4e2d\uff0c\u53ea\u89c1\u8c37\u6db5\u865a\u9661\u5730\u98de\u8eab\u8dc3\u8d77\uff0c\u4e09\u6761\u4eba\u5f71\uff0c\u500f\u5730\u5206\u5f00\uff0c\u91d1\u94c1\u4ea4\u9e23\u4e4b\u58f0\uff0c\u9707\u5f97 \u4f17\u4eba\u8033\u6735\u55e1\u55e1\u4f5c\u54cd\u3002 \u3000\u3000\u539f\u6765\u5c31\u5728\u8fd9\u5239\u90a3\u4e4b\u95f4\uff0c\u8c37\u6db5\u865a\u4ea6\u5df2\u662f\u62d4\u5251\u51fa\u9798\uff0c\u4ee5\u5251\u5bf9\u5251\uff0c\u4ee5\u638c\u5bf9\u638c\uff0c\u628a\u4e54\u3001\u5f20\u4e8c\u4eba\u5148 \u540e\u51fb\u9000\uff0c\u5f20\u5143\u5409\u672c\u9886\u8f83\u9ad8\uff0c\u5728\u8fd9\u5239\u90a3\u4e4b\u95f4\uff0c\u8fde\u8fdb\u516b\u62db\uff0c\u662f\u4ee5\u53cc\u5251\u4ea4\u51fb\uff0c\u53ee\u9e23\u4e4b\u58f0\uff0c\u4e0d\u7edd\u4e8e \u8033\uff01\u5230\u4e86\u7b2c\u4e5d\u62db\uff0c\u8c37\u6db5\u865a\u5530\u7684\u4e00\u5251\u523a\u7a7f\u4ed6\u7684\u8863\u886b\uff0c\u5251\u5cf0\u51e0\u4e4e\u662f\u8d34\u7740\u4ed6\u7684\u808b\u9aa8\u524a\u8fc7\uff0c\u8fd9\u624d\u5413\u5f97 \u4ed6\u8fde\u5fd9\u8eb2\u907f\u3002\u4e54\u5143\u58ee\u672c\u9886\u8f83\u5dee\uff0c\u8c37\u6db5\u865a\u5355\u638c\u4e0e\u4ed6\u5bf9\u654c\uff01\u4e54\u5143\u58ee\u53ea\u63a5\u4e86\u4e09\u638c\uff0c\u4fbf\u89c9\u80f8\u4e2d\u6c14\u8840\u7ffb \u6d8c\uff0c\u4e0d\u80fd\u4e0d\u9000\u4e0b\u53bb\u5598\u4e00\u53e3\u6c14\u4e86\u3002 \u3000\u3000\u8c37\u6db5\u865a\u62d4\u5251\u3001\u907f\u62db\u3001\u8fd8\u62db\u3001\u9000\u654c\u3001\u51e0\u4e2a\u52a8\u4f5c\uff0c\u4e00\u6c14\u5475\u6210\uff0c\u8eab\u624b\u77eb\u6377\uff0c\u65e0\u4ee5\u590d\u52a0\uff0c\u767b\u65f6\u628a \u8fd9\u4e24\u4e2a\u6b66\u5f53\u6d3e\u7684\u5c11\u5e74\u9ad8\u624b\uff0c\u5413\u5f97\u5fc3\u5934\u4e00\u9707\uff0c\u4e00\u65f6\u95f4\u7adf\u662f\u4e0d\u6562\u518d\u653b\u3002 \u3000\u3000\u8c37\u6db5\u865a\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u82e5\u8981\u8bb2\u7406\uff0c\u6211\u613f\u610f\u8868\u793a\u6b49\u610f\uff0c\u4f46\u6211\u4e0e\u4e25\u59d1\u5a18\u7684\u4e8b\u60c5\uff0c\u5374\u4e0d\u5bb9\u4f60\u4eec \u5e72\u6d89\uff01\u82e5\u4f60\u4eec\u6043\u591a\u4e3a\u80dc\uff0c\u59d3\u8c37\u7684\u53ea\u6709\u4e00\u6761\u6027\u547d\uff0c\u4f60\u4eec\u5982\u6709\u672c\u9886\uff0c\u62ff\u53bb\u5c31\u662f\uff01\u201d \u3000\u3000\u4e54\u5143\u58ee\u6709\u70b9\u5fc3\u602f\uff0c\u4e0d\u6562\u5411\u524d\u3002\u5c31\u5728\u6b64\u65f6\uff0c\u5ffd\u542c\u5f97\u55e4\u55e4\u58f0\u54cd\u3002\u539f\u6765\u662f\u90a3\u7626\u957f\u7684\u6c49\u5b50\u5728\u65c1\u5411 \u8c37\u6db5\u865a\u5077\u88ad\uff0c\u4e09\u679a\u900f\u9aa8\u9488\u5206\u6210\u4e0a\u4e2d\u4e0b\u4e09\u70b9\uff0c\u4e0a\u6253\u54bd\u5589\uff0c\u4e2d\u6253\u4e39\u7530\uff0c\u4e0b\u6253\u201c\u4f1a\u9634\u7a74\u201d\uff0c\u8fd9\u4e09\u5904 \u90fd\u662f\u4eba\u8eab\u8981\u5bb3\uff0c\u53ea\u987b\u6709\u4e00\u679a\u900f\u9aa8\u9489\u6253\u4e2d\uff0c\u8c37\u6db5\u865a\u4e0d\u6b7b\u4e5f\u5f97\u91cd\u4f24\u3002 \u3000\u3000\u8c37\u6db5\u865a\u95fb\u5f97\u8165\u98ce\u6251\u9f3b\uff0c\u77e5\u9053\u662f\u5582\u4e86\u6bd2\u7684\u6697\u5668\uff0c\u4e0d\u6562\u8f7b\u654c\uff0c\u5f53\u4e0b\u4e00\u62db\u201c\u7389\u5e26\u56f4\u8170\u201d\uff0c\u5251\u5149 \u6a2a\u63a0\uff0c\u4fe8\u5982\u4e00\u9053\u94f6\u8679\uff0c\u53ee\u53ee\u4e24\u58f0\uff0c\u628a\u6253\u5411\u4e39\u7530\u548c\u4f1a\u9634\u7a74\u7684\u4e24\u679a\u900f\u9aa8\u9489\u53cd\u63b8\u56de\u53bb\uff0c\u970d\u7684\u4e00\u4e2a \u201c\u51e4\u70b9\u5934\u201d\uff0c\u628a\u6253\u5411\u54bd\u5589\u7684\u4e00\u679a\u900f\u9aa8\u9489\u4e5f\u907f\u8fc7\u4e86\u3002 \u3000\u3000\u8c37\u6db5\u865a\u6b63\u60f3\u53d1\u8bdd\uff0c\u90a3\u4eba\u5df2\u5728\u51b7\u7b11\u9053\uff1a\u201c\u6349\u62ff\u5978\u592b\u6deb\u5987\uff0c\u4f55\u987b\u8bb2\u4ec0\u4e48\u6c5f\u6e56\u89c4\u77e9\uff1f\u201d\u3002\u58f0\u5230 \u4eba\u5230\uff0c\u4e00\u62db\u5200\u4e2d\u5939\u638c\uff0c\u5df2\u662f\u84e6\u5730\u5288\u6765\uff01 \u3000\u3000\u5f20\u5143\u5409\u672c\u6765\u5c31\u662f\u6012\u6c14\u586b\u80f8\uff0c\u6b64\u65f6\u89c1\u90a3\u7626\u957f\u6c49\u5b50\u5df2\u7ecf\u52a8\u624b\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u65c1\u4eba\u5c1a\u4e14\u4e3a\u6211\u6253 \u62b1\u4e0d\u5e73\uff0c\u6211\u5c82\u80fd\u7518\u5fc3\u53d7\u8fb1\uff01\u6253\u4e0d\u8fc7\u8fd9\u5c0f\u5b50\uff0c\u6700\u591a\u4e0e\u4ed6\u62fc\u4e86\u8fd9\u6761\u6027\u547d\u5c31\u662f\uff01\u53cd\u6b63\u6211\u7684\u59bb\u5b50\u88ab \u593a\uff0c\u6b64\u4ec7\u4e0d\u62a5\uff0c\u54ea\u6709\u9762\u76ee\u7acb\u4e8e\u5929\u5730\u4e4b\u95f4\uff1f\u201d\u4e00\u6012\u4e4b\u4e0b\uff0c\u7acb\u5373\u633a\u5251\u653b\u4e0a\uff0c\u559d\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u4f60 \u5c45\u7136\u8fd8\u6709\u9053\u7406\u53ef\u8bb2\u5440\uff01\u6211\u5374\u6015\u6c61\u4e86\u8033\u6735\uff0c\u6211\u53ea\u8981\u4f60\u7684\u6027\u547d\uff01\u201d \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u5f88\u597d\uff0c\u6211\u65e9\u5c31\u8bf4\u8fc7\uff0c\u6211\u8fd9\u6761\u6027\u547d\uff0c\u4f60\u6709\u672c\u9886\u62ff\u53bb\u5c31\u662f\uff01\u201d \u3000\u3000\u4e54\u5143\u58ee\u6df1\u77e5\u8fd9\u7626\u957f\u6c49\u5b50\u4e4b\u80fd\uff0c\u5f97\u4ed6\u52a9\u9635\uff0c\u80c6\u6c14\u4e3a\u4e4b\u4e00\u58ee\uff0c\u559d\u9053\uff1a\u201c\u597d\uff0c\u8fd9\u5c0f\u5b50\u8981\u62fc\u547d\uff0c \u54b1\u4eec\u6210\u5168\u4ed6\u5c31\u662f\uff01\u201d \u3000\u3000\u4e54\u5143\u58ee\u3001\u5f20\u5143\u5409\u548c\u90a3\u7626\u957f\u6c49\u5b50\u4ece\u9762\u6251\u6765\uff0c\u8c37\u6db5\u865a\u8eab\u5f62\u75be\u63a0\uff0c\u500f\u7684\u4ece\u4e24\u68f5\u4e3b\u677e\u4e4b\u95f4\u7a9c\u51fa\uff0c \u610f\u6b32\u4e0e\u4e25\u70f7\u4f1a\u5408\uff0c\u4e0d\u6599\u90a3\u7626\u957f\u6c49\u5b50\u8eab\u6cd5\u4e5f\u662f\u5feb\u5230\u6781\u70b9\uff0c\u7adf\u7136\u62a2\u5148\u4e00\u6b65\u622a\u5728\u4ed6\u7684\u524d\u5934\uff0c\u51b7\u7b11\u8bf4 \u9053\uff1a\u201c\u4f60\u4e0d\u662f\u8981\u62fc\u547d\u4e48\uff0c\u600e\u4e48\u8dd1\u4e86\uff1f\u201d \u3000\u3000\u7626\u957f\u6c49\u5b50\u5200\u4e2d\u5939\u638c\uff0c\u5de6\u624b\u5200\u81ea\u4e0a\u800c\u4e0b\u5730\u659c\u5288\u4e0b\u6765\uff0c\u4f7f\u7684\u662f\u201c\u4e91\u5c01\u5deb\u5ce1\u201d\uff0c\u53f3\u638c\u5374\u5212\u4e86\u4e00 \u9053\u5706\u5f27\uff0c\u81ea\u5de6\u800c\u53f3\u5730\u5411\u524d\u63a8\u51fa\uff0c\u4f7f\u7684\u662f\u201c\u96fe\u9501\u82cd\u5c71\u201d\u3002\u4e00\u5200\u4e00\u638c\uff0c\u653b\u5b88\u517c\u5907\uff0c\u914d\u5408\u5f97\u5999\u5230\u6beb \u98a0\uff0c\u8c37\u6db5\u865a\u4e5f\u4e0d\u7981\u5fc3\u5934\u4e00\u51db\uff1a\u201c\u8fd9\u6c49\u5b50\u7684\u672c\u9886\u59d4\u5b9e\u4e0d\u5f31\uff0c\u5012\u662f\u4e0d\u53ef\u5c0f\u89d1\u4e86\uff01\u201d \u3000\u3000\u4ee4\u8c37\u6db5\u865a\u5403\u60ca\u7684\u8fd8\u4e0d\u53ea\u6b64\uff0c\u6726\u80e7\u7684\u6708\u8272\u4e4b\u4e0b\uff0c\u53ea\u89c1\u4ed6\u4f7f\u7684\u90a3\u67c4\u6708\u7259\u5f2f\u5200\u8361\u8d77\u4e00\u5708\u84dd\u6492\u6f88 \u7684\u5149\u534e\uff0c\u8000\u773c\u751f\u8f89\uff0c\u540c\u65f6\u6709\u4e00\u80a1\u8165\u98ce\u6251\u9762\u800c\u6765\uff01\u8c37\u6db5\u865a\u662f\u4e2a\u6b66\u5b66\u7684\u5927\u884c\u5bb6\uff0c\u767b\u65f6\u7701\u89c9\uff1a\u201c\u8fd9 \u4eba\u7684\u5200\u662f\u6bd2\u5200\uff0c\u638c\u662f\u6bd2\u638c\uff01\u201d \u3000\u3000\u8c37\u6db5\u865a\u65e0\u6687\u601d\u91cf\uff0c\u201c\u5587\u201d\u7684\u4fbf\u662f\u4e00\u5251\u523a\u53bb\uff0c\u8fd9\u4e00\u5251\u4ee5\u653b\u4e3a\u5b88\uff0c\u51cc\u5389\u65e0\u6bd4\uff0c\u5f84\u523a\u90a3\u6c49\u5b50\u7684 \u54bd\u5589\uff0c\u767b\u65f6\u628a\u4ed6\u90a3\u4e00\u62db\u201c\u4e91\u5c01\u5deb\u5ce1\u201d\u89e3\u4e86\u3002 \u3000\u3000\u4f46\u6bd2\u5200\u53ef\u4ee5\u786c\u6321\uff0c\u6bd2\u638c\u5374\u662f\u4e0d\u80fd\u786c\u63a5\u3002\u90a3\u4eba\u7684\u6bd2\u638c\u5411\u524d\u63a8\u538b\uff0c\u4e1a\u5df2\u63a8\u5230\u8c37\u6db5\u865a\u8111\u524d\uff0c\u773c \u770b\u5f97\u8c37\u6db5\u865a\u65e0\u53ef\u95ea\u907f\uff0c\u975e\u786c\u63a5\u4e0d\u53ef\uff0c\u5fc3\u91cc\u6697\u6697\u5f97\u610f\uff0c\u60f3\u9053\uff1a\u201c\u6211\u62fc\u7740\u53d7\u70b9\u5185\u4f24\uff0c\u4f60\u8fd9\u5c0f\u5b50\u4e2d \u4e86\u6211\u7684\u5267\u6bd2\uff0c\u5374\u975e\u6bd9\u547d\u4e0d\u53ef\uff01\u201d \u3000\u3000\u90a3\u4eba\u6b63\u81ea\u5f97\u610f\uff0c\u5ffd\u89c1\u8c37\u6db5\u865a\u4e2d\u6307\u4e00\u4f38\uff0c\u6307\u5c16\u6b63\u5bf9\u51c6\u4e86\u4ed6\u638c\u5fc3\u7684\u201c\u52b3\u5bab\u7a74\u201d\uff0c\u51b7\u98ce\u5982\u7bad\uff0c \u5c1a\u672a\u63a5\u89e6\uff0c\u638c\u5fc3\u5df2\u662f\u6709\u4e86\u611f\u89c9\uff0c\u90a3\u4eba\u5927\u5403\u4e00\u60ca\uff0c\u614c\u5fd9\u7f29\u624b\uff01\u201d \u3000\u3000\u539f\u6765\u7ec3\u90aa\u6d3e\u6bd2\u529f\u7684\u4eba\uff0c\u6700\u5fcc\u7684\u5c31\u662f\u7ed9\u4eba\u5bb6\u7528\u91cd\u624b\u6cd5\u70b9\u7740\u4e86\u638c\u5fc3\u7684\u201c\u52b3\u5bab\u7a74\u201d\uff0c\u6b64\u7a74\u9053\u82e5 \u7ed9\u6233\u7a7f\uff0c\u6240\u7ec3\u7684\u6bd2\u529f\u5c31\u5c06\u4ed8\u4e4b\u6d41\u6c34\uff0c\u5fc5\u987b\u4ece\u5934\u7ec3\u8d77\u4e86\u3002 \u3000\u3000\u90a3\u4eba\u4e0d\u6015\u8eab\u53d7\u5185\u4f24\uff0c\u4f46\u8fd9\u4e00\u638c\u5374\u662f\u4ed6\u8d39\u4e86\u5341\u5e74\u7684\u5bd2\u6691\u4e4b\u529f\u7ec3\u6210\u7684\uff0c\u600e\u80af\u6bc1\u4e8e\u4e00\u65e6\uff1f\u8981\u77e5 \u4ed6\u662f\u4ed7\u7740\u6bd2\u638c\u79f0\u96c4\u7684\uff0c\u6bd2\u529f\u5018\u88ab\u6bc1\u53bb\uff0c\u7eb5\u7136\u53d6\u4e86\u8c37\u6db5\u865a\u7684\u6027\u547d\uff0c\u4e5f\u662f\u5f97\u4e0d\u507f\u5931\u7684\u4e86\u3002 \u3000\u3000\u8c37\u6db5\u8654\u4e00\u6307\u5413\u9000\u90a3\u4eba\uff0c\u638c\u5fc3\u4e5f\u4e0d\u7981\u6dcc\u51fa\u51b7\u6c57\uff0c\u5fc3\u60f3\uff1a\u201c\u6211\u82e5\u7ed9\u4ed6\u7684\u6bd2\u638c\u6253\u7740\uff0c\u6b7b\u662f\u4e0d\u4f1a \u6b7b\u7684\u3002\u4f46\u53d7\u4e86\u6bd2\u4f24\uff0c\u53ea\u6015\u4ecd\u662f\u9003\u4e0d\u8131\u4ed6\u4eec\u7684\u638c\u5fc3\uff01\u201d \u3000\u3000\u8c37\u6db5\u865a\u4e0e\u8fd9\u6c49\u5b50\u4e00\u5408\u5373\u5206\uff0c\u5728\u8fd9\u5239\u90a3\u4e4b\u95f4\uff0c\u53cc\u65b9\u90fd\u662f\u906d\u53d7\u5e73\u751f\u4ece\u6240\u672a\u9047\u7684\u60ca\u9669\u3002\u8bf4\u65f6 \u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u5f20\u5143\u5409\u4e0e\u4e54\u5143\u58ee\u5df2\u662f\u76f8\u7ee7\u6251\u6765\uff0c\u8c37\u6db5\u865a\u521a\u521a\u903c\u9000\u4e86\u90a3\u7626\u957f\u6c49\u5b50\uff0c\u5fc3\u795e\u672a\u8d70\uff0c\u811a \u6b65\u4e5f\u8fd8\u672a\u7a33\uff0c\u5e94\u62db\u7a0d\u7f13\uff0c\u53ea\u542c\u5f97\u201c\u55e4\u201d\u7684\u4e00\u58f0\u8f7b\u54cd\uff0c\u5f20\u5143\u5409\u5251\u5c16\u5212\u8fc7\uff0c\u5728\u8c37\u6db5\u865a\u7684\u5de6\u80a9\u5212\u5f00 \u4e86\u4e00\u9053\u4e09\u5bf8\u591a\u957f\u7684\u4f24\u53e3\uff0c\u4e54\u5143\u58ee\u5927\u559c\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u53d7\u4f24\u4e86\uff01\u201d \u3000\u3000\u4e25\u70f7\u5927\u5403\u4e00\u60ca\uff0c\u6025\u5fd9\u53eb\u9053\uff1a\u201c\u8c37\u5927\u54e5\u4f60\u5feb\u8d70\u5427\uff0c\u4e0d\u5fc5\u987e\u6211\uff01\u201d\u5979\u6df1\u77e5\u8c37\u6db5\u865a\u7684\u672c\u9886\u8fdc\u5728 \u8fd9\u4e9b\u4eba\u4e4b\u4e0a\uff0c\u5355\u6253\u72ec\u6597\uff0c\u8fd9\u4e9b\u4eba\u51b3\u8ba1\u4e0d\u662f\u4ed6\u7684\u5bf9\u624b\uff0c\u53ea\u8981\u51b2\u5f97\u51fa\u53bb\uff0c\u8fd9\u4e9b\u4eba\u4e0d\u53ef\u80fd\u540c\u65f6\u8ffd\u5f97 \u4e0a\u4ed6\uff0c\u53ea\u6709\u90a3\u4e2a\u7626\u957f\u6c49\u5b50\u8f7b\u529f\u8f83\u597d\uff0c\u4f46\u4ed6\u5355\u72ec\u4e00\u4eba\u6599\u60f3\u4e5f\u662f\u4e0d\u6562\u7a77\u8ffd\u7684\u4e86\u3002 \u3000\u3000\u4e54\u5143\u58ee\u51b7\u7b11\u9053\uff1a\u201c\u8dd1\u4e0d\u4e86\u5566\uff01\u201d\u5f20\u5143\u5409\u89c1\u4ed6\u7684\u672a\u5a5a\u59bb\u4e00\u5fc3\u5411\u7740\u201c\u91ce\u6c49\u5b50\u201d\uff0c\u66f4\u662f\u53c8\u6c14\u53c8 \u6068\uff0c\u5f53\u4e0b\u54ac\u7d27\u7259\u6839\u3001\u4e00\u58f0\u4e0d\u54cd\uff0c\u628a\u6b66\u5f53\u6d3e\u7684\u4e03\u5341\u4e8c\u624b\u8fde\u73af\u593a\u547d\u5251\u6cd5\u4f7f\u5f97\u51cc\u5389\u65e0\u6bd4\uff0c\u90a3\u7626\u957f\u6c49 \u5b50\u5bf9\u8c37\u6db5\u865a\u7684\u72e0\u8fa3\u5251\u6cd5\u4e0e\u795e\u5999\u7684\u70b9\u7a74\u529f\u592b\u867d\u7136\u6709\u4e9b\u987e\u5fcc\uff0c\u4f46\u4e00\u6765\u6043\u7740\u6709\u6bd2\u5200\u6bd2\u638c\uff0c\u4e8c\u6765\u6709\u4e54 \u5143\u58ee\u5e08\u5144\u5f1f\u4ece\u65c1\u7275\u5236\uff0c\u4ed6\u81ea\u5fd6\u80dc\u5238\u5728\u63e1\uff0c\u4e5f\u5c31\u653e\u8138\u518d\u4e0a\u3002\u4e09\u4eba\u8054\u624b\uff0c\u679c\u7136\u628a\u8c37\u6db5\u865a\u56f4\u5728\u6838 \u5fc3\u3002 \u3000\u3000\u5939\u653b\u4e25\u70f7\u7684\u90a3\u4e24\u4e2a\u6b66\u5f53\u6d3e\u5f1f\u5b50\u9a82\u9053\uff1a\u201c\u597d\u4e2a\u4e0d\u8981\u8138\u7684\u5c0f\u8d31\u4eba\uff01\u201d\u53cc\u5251\u9f50\u51fa\uff0c\u4ea4\u53c9\u7a7f\u63d2\uff0c \u5251\u950b\u6240\u6307\uff0c\u5c3d\u662f\u8981\u5bb3\u7a74\u9053\u3002\u5176\u5b9e\u4ed6\u4eec\u987e\u5fcc\u7740\u4e25\u70f7\u662f\u5ddd\u897f\u5927\u4fa0\u7684\u5973\u513f\uff0c\u53d6\u4e25\u70f7\u7684\u6027\u547d\u4ed6\u4eec\u662f\u4e0d \u6562\u7684\uff0c\u4e0d\u8fc7\u662f\u60f3\u5413\u5413\u4e25\u70f7\uff0c\u8981\u5979\u675f\u624b\u53d7\u64d2\u800c\u5df2\u3002 \u3000\u3000\u4e25\u70f7\u5374\u600e\u54bd\u5f97\u4e0b\u8fd9\u53e3\u6c14\uff0c\u5ffd\u5730\u5530\u5530\u4e24\u5251\uff0c\u53cd\u653b\u8fc7\u53bb\uff0c\u65a5\u9053\uff1a\u201c\u5634\u91cc\u653e\u5e72\u51c0\u4e9b\uff0c\u5426\u5219\u5c31\u4f11 \u602a\u6211\u5251\u4e0b\u65e0\u60c5\uff01\u201d\u4e25\u70f7\u7684\u5251\u6cd5\u5df2\u5c3d\u5f97\u7236\u4eb2\u771f\u4f20\uff0c\u5979\u6b63\u5728\u9752\u5e74\uff0c\u6c14\u529b\u867d\u7136\u5dee\u4e9b\uff0c\u8eab\u624b\u7684\u7075\u6d3b\u5c1a \u5728\u7236\u4eb2\u4e4b\u4e0a\uff0c\u90a3\u4e24\u4eba\u6599\u4e0d\u5230\u5979\u7a81\u7136\u53cd\u653b\uff0c\u53ea\u89c1\u5251\u5149\u4e00\u95ea\uff0c\u5b63\u5143\u4f26\u5934\u4e0a\u6234\u7684\u5e3d\u5b50\u5df2\u662f\u7ed9\u5979\u6311 \u843d\u3002 \u3000\u3000\u6b66\u5f53\u5c11\u6797\u4e43\u662f\u5e76\u9a7e\u9f50\u540d\u7684\u4e24\u5927\u95e8\u6d3e\uff0c\u5b63\u5143\u4f26\u4e5f\u662f\u4e2a\u5fc3\u9ad8\u6c14\u50b2\u7684\u5c11\u5e74\uff0c\u7ed9\u4e25\u70f7\u6311\u843d\u4ed6\u7684\u5e3d \u5b50\uff0c\u975e\u4f46\u4e0d\u611f\u6fc0\u4e25\u70f7\u5251\u4e0b\u7559\u60c5\uff0c\u53cd\u800c\u52c3\u7136\u5927\u6012\uff0c\u559d\u9053\uff1a\u201c\u597d\u5440\uff0c\u6211\u4e0d\u8fc7\u770b\u5728\u4f60\u7239\u7239\u9762\u4e0a\uff0c\u5bf9 \u4f60\u5ba2\u6c14\u51e0\u5206\uff0c\u4f60\u5f53\u6211\u5f53\u771f\u6015\u4e86\u4f60\u4e48\uff01\u201d \u3000\u3000\u8fd9\u4e00\u6765\u53cc\u65b9\u52a8\u4e86\u771f\u6c14\uff0c\u51fa\u624b\u5404\u4e0d\u7559\u60c5\u3002\u6b66\u5f53\u6d3e\u7684\u8fde\u73af\u593a\u547d\u5251\u6cd5\u4e5f\u662f\u4ee5\u72e0\u8fa3\u89c1\u957f\u7684\uff0c\u5e08\u5144 \u5f1f\u4e24\u4eba\u5408\u4f7f\u8fd9\u5957\u5251\u6cd5\uff0c\u4e00\u653b\u4e00\u5b88\uff0c\u66f4\u89c1\u5a01\u529b\uff0c\u53cc\u65b9\u8ba4\u771f\u53ae\u6740\u8d77\u6765\uff0c\u6bd5\u7adf\u662f\u6b66\u5f53\u6d3e\u8fd9\u4e24\u4e2a\u5f1f\u5b50 \u5360\u4e86\u4e0a\u98ce\uff0c\u4e0d\u8fc7\u4e00\u4f1a\uff0c\u4e25\u70f7\u7ed9\u4ed6\u4eec\u6740\u5f97\u53ea\u6709\u62db\u67b6\u4e4b\u529f\uff0c\u6beb\u65e0\u8fd8\u624b\u4e4b\u529b\u3002 \u3000\u3000\u8c37\u6db5\u865a\u773c\u89c2\u56db\u9762\uff0c\u8033\u542c\u516b\u65b9\uff0c\u89c1\u4e25\u70f7\u52bf\u5371\uff0c\u5ffd\u5730\u54fc\u4e86\u4e00\u58f0\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u8bf7\u6211\u8d70\u6211 \u90fd\u4e0d\u8d70\u5462\uff01\u201d\u84e6\u5730\u8eab\u5f62\u4e00\u8d77\uff0c\u9752\u94a2\u5251\u626c\u7a7a\u4e00\u95ea\uff0c\u7b14\u76f4\u7684\u5411\u5f20\u5143\u5409\u51b2\u53bb\u3002\u5f20\u65e0\u5409\u8bc6\u5f97\u4ed6\u7684\u5389 \u5bb3\uff0c\u614c\u5fd9\u5251\u4e00\u5c01\uff0c\u8c37\u6db5\u865a\u8eab\u5f62\u5411\u7740\u5f20\u5143\u5409\uff0c\u7a81\u7136\u53cd\u624b\u4e00\u638c\u6253\u80cc\u540e\u7684\u4e54\u5143\u58ee\uff0c\u53ea\u542c\u5f97\u201c\u535c\u201d\u7684 \u4e00\u58f0\uff0c\u63a5\u7740\u201c\u556a\u2019\u7684\u4e00\u54cd\uff0c\u539f\u6765\u4e54\u5143\u58ee\u4e5f\u6b63\u4e58\u673a\u5077\u88ad\uff0c\u8c37\u6db5\u865a\u7684\u80cc\u810a\u7740\u4e86\u4e54\u5143\u58ee\u7684\u4e00\u62f3\uff0c\u4e54 \u5143\u58ee\u4e5f\u7ed9\u4ed6\u6253\u4e86\u4e00\u8bb0\u8033\u5149\u3002 \u3000\u3000\u8c37\u6db5\u865a\u5185\u529f\u6df1\u539a\uff0c\u80cc\u810a\u53d7\u4e86\u4e00\u62f3\uff0c\u7b97\u4e0d\u4e86\u4ec0\u4e48\uff0c\u4e54\u5143\u58ee\u7ed9\u4ed6\u6253\u7684\u8fd9\u8bb0\u8033\u5149\u53ef\u5c31\u60e8\u4e86\u3002\u8c37 \u6db5\u865a\u6068\u4ed6\u6c61\u8a00\u79fd\u8bed\uff0c\u6709\u610f\u5c06\u4ed6\u6298\u8fb1\uff0c\u8fd9\u4e00\u8bb0\u8033\u5149\u6253\u5f97\u53c8\u72e0\u53c8\u91cd\uff0c\u4e54\u5143\u58ee\u7684\u8138\u4e0a\u767b\u65f6\u5f00\u4e86\u82b1\uff01 \u3000\u3000\u5f20\u5143\u5409\u5927\u60ca\u4e4b\u4e0b\uff0c\u751f\u6015\u8c37\u6db5\u865a\u5bf9\u5e08\u5144\u4f1a\u4e0b\u6740\u624b\uff0c\u8fde\u5fd9\u633a\u5251\u523a\u4ed6\u80cc\u5fc3\u3002\u8fd9\u4e00\u5251\u52bf\u6377\u52b2\u8db3\uff0c \u523a\u5411\u8981\u5bb3\u7a74\u9053\uff0c\u672c\u6765\u662f\u653b\u654c\u4e4b\u6240\u5fc5\u6551\u7684\u4e00\u62db\u7edd\u62db\uff0c\u54ea\u77e5\u4ed6\u5feb\u8c37\u6db5\u865a\u6bd4\u4ed6\u66f4\u5feb\uff0c\u5f20\u5143\u5409\u4e00\u5251\u523a \u7a7a\uff0c\u53ea\u89c9\u5fae\u98ce\u98d2\u7136\uff0c\u8c37\u6db5\u865a\u5df2\u662f\u4ece\u4ed6\u8eab\u8fb9\u63a0\u8fc7\u3002 \u3000\u3000\u5f20\u5143\u5409\u4e5f\u771f\u4e0d\u6127\u662f\u540d\u5bb6\u5f1f\u5b50\uff0c\u5728\u8fd9\u7edd\u9669\u4e4b\u9645\uff0c\u500f\u5730\u8eab\u8eaf\u4e00\u77ee\uff0c\u6a2a\u5251\u62a4\u5934\uff0c\u8fd8\u4e86\u4e00\u62db\u201c\u4e3e \u706b\u71ce\u5929\u201d\u3002\u53ea\u542c\u5f97\u201c\u94db\u201d\u7684\u4e00\u58f0\uff0c\u53cc\u5251\u76f8\u4ea4\uff0c\u5f20\u5143\u5409\u864e\u53e3\u9690\u9690\u4f5c\u75db\uff0c\u957f\u5251\u51e0\u4e4e\u638c\u63e1\u4e0d\u7262\u3002\u8c37 \u6db5\u865a\u4e00\u58f0\u51b7\u7b11\uff0c\u5df2\u662f\u5230\u4e86\u4ed6\u524d\u9762\u6570\u4e08\u4e4b\u5730\uff0c\u4e0e\u90a3\u7626\u957f\u6c49\u5b50\u4ea4\u4e0a\u624b\u4e86\u3002 \u3000\u3000\u5f20\u5143\u5409\u4e0d\u7531\u5f97\u6ee1\u9762\u901a\u7ea2\uff0c\u6697\u6697\u53eb\u4e86\u4e00\u58f0\u201c\u60ed\u6127\uff01\u201d\u8981\u77e5\u8c37\u6db5\u865a\u529f\u529b\u5728\u4ed6\u4e4b\u4e0a\uff0c\u521a\u624d\u8fd9\u4e00 \u62db\u8c37\u6db5\u865a\u4e1a\u5df2\u53d6\u5f97\u4e86\u653b\u4ed6\u63aa\u624b\u4e0d\u53ca\u7684\u6709\u5229\u65f6\u673a\uff0c\u5018\u82e5\u771f\u4e2a\u4f7f\u51fa\u6740\u624b\u7684\u8bdd\uff0c\u5f20\u5143\u5409\u7109\u80fd\u8fd8\u6709\u547d \u5728\uff1f\u5f20\u5143\u5409\u867d\u662f\u628a\u8c37\u6db5\u865a\u6068\u4e4b\u5165\u9aa8\uff0c\u4f46\u4e5f\u8fd8\u6709\u81ea\u77e5\u4e4b\u660e\uff0c\u5fc3\u91cc\u4e0d\u7981\u60f3\u9053\uff1a\u201c\u4ed6\u660e\u660e\u77e5\u9053\u6211\u8981 \u6740\u4ed6\uff0c\u4f55\u4ee5\u4ed6\u7adf\u5bf9\u6211\u624b\u4e0b\u7559\u60c5\uff1f\u201d \u3000\u3000\u90a3\u7626\u957f\u6c49\u5b50\u559d\u9053\uff1a\u201c\u5c0f\u5b50\u4f11\u5f97\u901e\u51f6\uff0c\u8fd8\u6709\u6211\u5462\uff01\u201d\u6321\u5728\u4ed6\u7684\u524d\u9762\uff0c\u6a2a\u5288\u4e00\u5200\uff0c\u76f4\u5288\u4e00 \u5200\uff0c\u5200\u4e2d\u53c8\u5939\u4e86\u4e24\u638c\u3002\u5200\u662f\u6bd2\u5200\uff0c\u638c\u662f\u6bd2\u638c\uff0c\u8c37\u6db5\u865a\u7684\u672c\u9886\u867d\u7136\u5728\u4ed6\u4e4b\u4e0a\uff0c\u4f46\u8981\u5728\u6025\u5207\u4e4b \u95f4\uff0c\u7834\u4ed6\u8fd9\u5200\u4e2d\u5939\u638c\u7684\u62db\u6570\u4e5f\u662f\u4e0d\u80fd\u3002\u4f55\u51b5\u8c37\u6db5\u865a\u5bf9\u4ed6\u7684\u6bd2\u5200\u6bd2\u638c\u591a\u5c11\u4e5f\u8fd8\u6709\u4e9b\u987e\u5fcc\u3002 \u3000\u3000\u4e54\u5143\u58ee\u8eab\u4e3a\u6b66\u5f53\u6d3e\u7b2c\u4e8c\u4ee3\u7684\u5927\u5f1f\u5b50\uff0c\u5e73\u65e5\u884c\u8d70\u6c5f\u6e56\uff0c\u5230\u5904\u53d7\u4eba\u5c0a\u656c\uff0c\u51e0\u66fe\u53d7\u8fc7\u5982\u6b64\u803b \u8fb1\uff1f\u7ed9\u8c37\u6db5\u865a\u72e0\u72e0\u5730\u6253\u4e86\u4e00\u8bb0\u8033\u5149\u4e4b\u540e\uff0c\u7f9e\u6127\u96be\u5f53\uff0c\u6740\u673a\u9661\u8d77\uff0c\u559d\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u4eca\u665a\u6709\u4f60 \u65e0\u6211\uff01\u201d\u8840\u6d41\u6ee1\u9762\uff0c\u5c31\u50cf\u53d1\u4e86\u75af\u7684\u91ce\u517d\u4e00\u822c\uff0c\u6076\u72e0\u72e0\u5730\u6251\u5411\u8c37\u6db5\u865a\uff01 \u3000\u3000\u5f20\u5143\u5409\u5446\u4e86\u4e00\u5446\uff0c\u8ddf\u7740\u4e5f\u5c31\u633a\u5251\u653b\u4e86\u4e0a\u53bb\u3002\u521a\u624d\u90a3\u4e00\u62db\uff0c\u5f20\u5143\u5409\u867d\u7136\u77e5\u9053\u662f\u5bf9\u65b9\u624b\u4e0b\u7559 \u60c5\uff0c\u4f46\u4e00\u6765\u593a\u59bb\u4e4b\u8fb1\u4e0d\u80fd\u4e0d\u62a5\uff1b\u4e8c\u6765\u5e08\u5144\u6b63\u5728\u4e3a\u4ed6\u62fc\u547d\uff0c\u4ed6\u53c8\u5c82\u80fd\u8896\u624b\u65c1\u89c2\uff1f\u662f\u4ee5\u8fd9\u4e00\u573a\u6076 \u6218\uff0c\u4ed6\u4ecd\u662f\u975e\u5f97\u4e0e\u8c37\u6db5\u865a\u72e0\u72e0\u53ae\u6740\u4e0d\u884c\uff0c\u4e0d\u8fc7\uff0c\u4ed6\u5fc3\u91cc\u5374\u5728\u60f3\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u7684\u6027\u547d\u6211\u53ef\u4ee5\u4e0d \u8981\uff0c\u8fd9\u53e3\u6076\u6c14\u6211\u5374\u662f\u975e\u51fa\u4e0d\u53ef\uff01\u201d\u4e0d\u4f3c\u4ed6\u7684\u5e08\u5144\u90a3\u6837\u5927\u8d77\u6740\u673a\u4e86\u3002 \u3000\u3000\u8c37\u6db5\u865a\u4ee5\u4e00\u654c\u4e09\uff0c\u50b2\u7136\u4e0d\u4ff1\uff0c\u638c\u4f3c\u5954\u96f7\uff0c\u5251\u5982\u9a87\u7535\uff0c\u5140\u662f\u653b\u591a\u5b88\u5c11\u3002\u4e0d\u8fc7\uff0c\u56e0\u4e3a\u4ed6\u7684\u5de6 \u80a9\u53d7\u4e86\u5251\u4f24\uff0c\u6597\u5230\u4e86\u4e09\u4e94\u5341\u62db\u4e4b\u540e\uff0c\u62db\u6570\u5374\u662f\u6e10\u6e10\u6709\u70b9\u8fd0\u7528\u4e0d\u7075\u3002 \u3000\u3000\u4e25\u70f7\u90a3\u8fb9\u7684\u5f62\u52bf\u6bd4\u4ed6\u66f4\u9669\uff0c\u5979\u5728\u5b63\u5143\u4f26\u3001\u6881\u5143\u732e\u8054\u5251\u5939\u653b\u4e4b\u4e0b\uff0c\u8d77\u521d\u8fd8\u53ef\u4ee5\u62db\u67b6\uff0c\u5341\u62db \u4e4b\u4e2d\uff0c\u8fd8\u653b\u4e09\u56db\u62db\uff0c\u6e10\u6e10\u5c31\u53ea\u6709\u62db\u67b6\u4e4b\u529f\uff0c\u6beb\u65e0\u8fd8\u624b\u4e4b\u529b\uff0c\u5230\u4e86\u6700\u540e\uff0c\u8fde\u62db\u67b6\u90fd\u4f3c\u4e4e\u6709\u70b9\u4e3a \u96be\u4e86\u3002 \u3000\u3000\u8fd9\u4e00\u573a\u6076\u6218\uff0c\u628a\u8eb2\u5728\u6811\u4e0a\u5077\u770b\u7684\u5b5f\u660e\u971e\u770b\u5f97\u60ca\u5fc3\u52a8\u9b44\uff0c\u51e0\u4e4e\u900f\u4e0d\u8fc7\u6c14\u6765\u3002\u5b5f\u660e\u971e\u5fc3\u91cc\u8e0c \u8e87\u4e0d\u5b9a\uff1a\u8981\u4e0d\u8981\u5377\u5165\u8fd9\u4e2a\u6f29\u6da1\u5462\uff1f\u6b63\u662f\uff1a \u3000\u3000\u60ca\u5fc3\u60c5\u6d77\u98ce\u6ce2\u6076\uff0c\u4f55\u5904\u80fd\u6c42\u9c81\u4ef2\u8fde\uff1f \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b\u2014\u2014\u7b2c\u4e09\u5341\u4e09\u56de\u3000\u5992\u706b\u653b\u5fc3\u6325\u5229\u5251\u3000\u60c5\u573a\u5931\u610f\u8d70\u4ed6\u4e61 \u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b \u7b2c\u4e09\u5341\u4e09\u56de\u3000\u5992\u706b\u653b\u5fc3\u6325\u5229\u5251\u3000\u60c5\u573a\u5931\u610f\u8d70\u4ed6\u4e61 \u3000\u3000\u5fc3\u5ff5\u672a\u5df2\uff0c\u53ea\u89c1\u4e25\u70f7\u63a5\u8fde\u53c8\u9047\u4e0a\u4e24\u6b21\u9669\u62db\uff0c\u5b5f\u660e\u971e\u6b63\u8981\u4e0d\u987e\u4e00\u5207\u8df3\u4e0b\u53bb\u5e2e\u52a9\u8868\u59d0\uff0c\u5ffd\u542c \u5f97\u8c37\u6db5\u865a\u731b\u5730\u4e00\u58f0\u5927\u559d\uff0c\u5c31\u4f3c\u4e4e\u5730\u8d77\u4e86\u4e00\u4e2a\u7126\u96f7\u3002 \u3000\u3000\u5f20\u5143\u5409\u5fc3\u5934\u4e00\u9707\uff0c\u4e0d\u77e5\u4e0d\u89c9\u9000\u4e86\u51e0\u6b65\uff0c\u4e54\u5143\u58ee\u7ed9\u4ed6\u4e00\u638c\u903c\u9000\uff0c\u8c37\u6db5\u865a\u51b2\u4e86\u51fa\u53bb\uff0c\u559d\u9053\uff1a \u201c\u4e14\u6162\u52a8\u624b\uff0c\u542c\u6211\u4e00\u8a00\uff01\u201d \u3000\u3000\u90a3\u7626\u957f\u6c49\u5b50\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u505a\u51fa\u4e86\u8fd9\u7b49\u597d\u4e8b\uff0c\u8fd8\u6709\u4ec0\u4e48\u597d\u8bf4\uff1f\u201d \u3000\u3000\u8c37\u6db5\u865a\u6307\u7740\u7626\u957f\u6c49\u5b50\u9053\uff1a\u201c\u6211\u9053\u662f\u8c01\uff0c\u539f\u6765\u9601\u4e0b\u4e43\u662f\u63d2\u7fc5\u864e\u6bb5\u70b9\u82cd\u7684\u5e08\u5f1f\uff01\u201d\u4e25\u70f7\u5931\u58f0 \u53eb\u9053\uff1a\u201c\u4ed6\u662f\u98de\u8c79\u5b50\u5417\uff1f\u201d\u8c37\u865a\u6db5\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u63d2\u7fc5\u864e\u7684\u5e05\u5f1f\u5c31\u662f\u98de\u8c79\u5b50\u891a\u9752\u5c71\uff01\u201d \u3000\u3000\u539f\u6765\u201c\u63d2\u7fc5\u864e\u201d\u6bb5\u70b9\u82cd\u4e43\u662f\u201c\u6ec7\u5357\u4e03\u864e\u201d\u7684\u9996\u9886\uff0c\u90a3\u6b21\u4e25\u70f7\u5728\u5c0f\u91d1\u5ddd\u906d\u9047\u201c\u6ec7\u5357\u4e03 \u864e\u201d\uff0c\u6240\u53d7\u7684\u4f24\u5c31\u662f\u6bb5\u70b9\u82cd\u7ed9\u4ed6\u7684\u3002\u201c\u98de\u8c79\u5b50\u201d\u891a\u9752\u5c71\u4e0d\u5728\u201c\u6ec7\u5357\u4e03\u864e\u201d\u4e4b\u5185\uff0c\u4f46\u672c\u9886\u5374\u975e \u4f46\u9ad8\u8fc7\u5176\u4ed6\u201c\u516d\u864e\u201d\uff0c\u800c\u4e14\u8fd8\u5728\u4ed6\u7684\u5e08\u5144\u201c\u63d2\u7fc5\u864e\u201d\u4e4b\u4e0a\u3002 \u3000\u3000\u4e25\u58f0\u6d9b\u548c\u201c\u6ec7\u5357\u4e03\u864e\u201d\u7ed3\u4e86\u6881\u5b50\uff0c\u6050\u9632\u891a\u9752\u5c71\u4f1a\u6765\u52a9\u4ed6\u5e08\u5144\u62a5\u4ec7\uff0c\u66fe\u4e0e\u5973\u513f\u8bf4\u8fc7\u6b64\u4eba\uff0c \u5e76\u4e14\u63d0\u9192\u5973\u513f\uff0c\u5373\u4f7f\u662f\u5728\u51fa\u5ac1\u4e4b\u540e\uff0c\u4e0e\u4e08\u592b\u540c\u8d70\u6c5f\u6e56\uff0c\u9047\u4e0a\u6b64\u4eba\uff0c\u4e5f\u5fc5\u987b\u5c0f\u5fc3\u5728\u610f\u3002\u60f3\u4e0d\u5230 \u7236\u4eb2\u8981\u5979\u63d0\u9632\u7684\u8fd9\u4e2a\u201c\u98de\u8c79\u5b50\u201d\uff0c\u4eca\u665a\u5374\u548c\u5979\u7684\u672a\u5a5a\u592b\u4e00\u8d77\u6765\u4e86\u3002 \u3000\u3000\u4e25\u70f7\u5403\u4e86\u4e00\u60ca\u4e4b\u540e\uff0c\u5fc3\u91cc\u4e5f\u5c31\u767b\u65f6\u660e\u767d\uff1a\u201c\u539f\u6765\u662f\u8fd9\u891a\u9752\u5c71\u901a\u98ce\u62a5\u8baf\uff0c\u628a\u5f20\u5143\u5409\u5e26\u6765 \u2018\u6349\u5978\u2019\u7684\u3002\u60f3\u5fc5\u662f\u6ec7\u5357\u4e03\u864e\u5728\u90a3\u6b21\u5927\u8d25\u4e4b\u540e\uff0c\u6068\u6781\u4e86\u8c37\u5927\u54e5\uff0c\u4e0d\u77e5\u600e\u6837\u7ed9\u4ed6\u4eec\u6253\u542c\u5230\u4e86\u6211 \u4eec\u4e8c\u4eba\u65e9\u6709\u4ea4\u60c5\uff0c\u544a\u8bc9\u4ed6\u7684\u5e08\u5f1f\uff0c\u53eb\u4ed6\u5e08\u5f1f\u6697\u4e2d\u4fa6\u5bdf\u6211\u4eec\u7684\u3002\u201d \u3000\u3000\u891a\u9752\u5c71\u6014\u4e86\u4e00\u6014\uff0c\u4f46\u968f\u5373\u5c31\u82e5\u65e0\u5176\u4e8b\u5730\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u9601\u4e0b\u771f\u597d\u773c\u529b\uff0c\u5c45\u7136\u770b\u51fa\u4e86\u6211\u7684\u5bb6 \u6570\uff0c\u4e0d\u9519\uff0c\u6211\u5c31\u662f\u98de\u8c79\u5b50\u891a\u9752\u5c71\uff0c\u662f\u53c8\u600e\u6837\uff1f\u201d \u3000\u3000\u8c37\u6db5\u865a\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u662f\u6b66\u5f53\u6d3e\u7684\u540d\u95e8\u5f1f\u5b50\uff0c\u548c\u8fd9\u6837\u4e00\u4e2a\u9ed1\u9053\u4e0a\u6076\u540d\u662d\u662d\u7684\u4eba\u7269\u6df7\u5728 \u4e00\u8d77\uff0c\u4e0d\u5bb3\u7f9e\u5417\uff1f\u4f60\u4eec\u77e5\u4e0d\u77e5\u9053\u6ec7\u5357\u4e03\u864e\u6b63\u662f\u5ddd\u897f\u5927\u4fa0\u4e25\u8001\u524d\u8f88\u7684\u4ec7\u4eba\uff1f\u201d \u3000\u3000\u5f20\u5143\u5409\u51b7\u7b11\u9053\uff1a\u201c\u8304\u5b50\u4e0d\u662f\u957f\u5728\u6843\u5b50\u6811\u4e0a\uff0c\u59d3\u8c37\u7684\uff0c\u4f60\u4e0d\u8981\u628a\u4e24\u4ef6\u4e0d\u76f8\u5e72\u7684\u4e8b\u60c5\u626f\u5728\u4e00 \u8d77\uff01\u4f60\u3001\u4f60\u3001\u4f60\u73b7\u6c61\u4e86\u6211\u7684\u672a\u5a5a\u59bb\uff0c\u8bf4\u4ec0\u4e48\u6211\u4e5f\u4e0d\u80fd\u653e\u4f60\u8fc7\u53bb\uff01 \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u6211\u5374\u66ff\u4f60\u53ef\u60dc\uff0c\u53ea\u6015\u4f60\u4e0a\u4e86\u5978\u4eba\u7684\u5f53\uff01\u201d\u5f20\u5143\u5409\u6012\u9053\uff1a\u201c\u4e0d\u7528\u4f60\u8fd9\u6837\u597d \u5fc3\uff01\u6211\u4e5f\u4e0d\u542c\u4f60\u82b1\u8a00\u5de7\u8bed\uff01\u201d \u3000\u3000\u4e54\u5143\u58ee\u4e5f\u51b7\u7b11\u8bf4\uff1a\u201c\u4f60\u81ea\u5df1\u5c31\u662f\u4e00\u4e2a\u90aa\u6076\u4e4b\u6781\u7684\u5978\u5f92\uff0c\u8fd8\u6709\u8138\u6307\u8d23\u522b\u4eba\uff1f\u8001\u5b9e\u544a\u8bc9\u4f60 \u5427\uff0c\u6211\u4eec\u4e0d\u4f46\u77e5\u9053\u891a\u5927\u54e5\u7684\u6765\u5386\uff0c\u800c\u4e14\u8fd8\u8981\u548c\u891a\u5927\u54e5\u4e00\u540c\u53bb\u89c1\u4e25\u8001\u524d\u8f88\u7684\u3002\u660e\u4eba\u4e0d\u505a\u6697\u4e8b\uff0c \u6211\u4eec\u5e76\u4e0d\u6015\u4e25\u8001\u524d\u8f88\u77e5\u9053\uff0c\u54c8\u54c8\uff0c\u6211\u4eec\u5012\u662f\u6015\u4f60\u6ca1\u6709\u8138\u518d\u89c1\u4e25\u8001\u524d\u8f88\u5462\u3002\u201d \u3000\u3000\u891a\u9752\u5c71\u51b7\u7b11\u9053\uff1a\u201c\u4f55\u5fc5\u4e0e\u4ed6\u591a\u8bf4\u5e9f\u8bdd\uff01\u201d\u4e54\u5143\u58ee\u9053\uff1a\u201c\u4e0d\u9519\uff01\u201d\u548c\u8eab\u6251\u4e0a\uff0c\u4e00\u62db\u201c\u8de8\u864e \u767b\u5c71\u201d\uff0c\u6a2a\u638c\u5411\u8c37\u6db5\u865a\u95e8\u9762\u51fb\u53bb\u3002\u4ed6\u4ee5\u6b66\u5f53\u6d3e\u7b2c\u4e8c\u4ee3\u5f1f\u5b50\u7684\u8eab\u4efd\uff0c\u7ed9\u8c37\u6db5\u865a\u6253\u5f97\u4ed6\u9762\u4e0a\u5f00 \u82b1\uff0c\u5982\u4eca\u773c\u770b\u80dc\u5229\u5728\u671b\uff0c\u5f53\u7136\u662f\u8981\u8a93\u62a5\u8fd9\u4e00\u8bb0\u8033\u5149\u4e4b\u8fb1\u4e86\u3002 \u3000\u3000\u5f20\u5143\u5409\u4e00\u89c1\u5e08\u5144\u51fa\u624b\uff0c\u4e0d\u5047\u601d\u7d22\uff0c\u5530\u7684\u4e5f\u662f\u4e00\u5251\u523a\u53bb\u3002\u4ed6\u4eec\u5e08\u5144\u5f1f\u60ef\u4e8e\u8054\u624b\u5bf9\u654c\uff0c\u4e00\u638c \u4e00\u5251\uff0c\u914d\u5408\u5f97\u5999\u5230\u6beb\u5dc5\uff0c\u5f20\u5143\u5409\u7684\u5251\u52bf\u5c24\u4e3a\u51cc\u5389\u3002\u891a\u9752\u5c71\u5598\u606f\u5df2\u5b9a\uff0c\u5728\u51b7\u7b11\u58f0\u4e2d\uff0c\u4ea6\u5df2\u7eb5\u8eab \u6251\u4e0a\u3002 \u3000\u3000\u8c37\u6db5\u865a\u7684\u4f24\u867d\u7136\u4e0d\u91cd\uff0c\u4f46\u5df2\u4e0d\u8010\u4e45\u6218\uff0c\u4ed6\u77e5\u9053\u5018\u82e5\u518d\u8ba9\u8fd9\u4e09\u4eba\u518d\u6b21\u5408\u56f4\uff0c\u81ea\u5df1\u60f3\u518d\u8131\u8eab \u5c31\u6ca1\u6709\u90a3\u4e48\u5bb9\u6613\u4e86\uff0c\u5f53\u4e0b\u7a81\u8d77\u602a\u62db\uff0c\u4ee5\u638c\u6321\u5251\uff0c\u4ee5\u5251\u523a\u638c\u3002\u4e54\u5143\u58ee\u7ec3\u7684\u662f\u7ef5\u638c\u529f\u592b\uff0c\u529f\u529b\u751a \u9ad8\uff0c\u5dee\u4e0d\u591a\u5df2\u7ec3\u6210\u4e86\u5165\u77f3\u5982\u7c89\u7684\u672c\u9886\u3002\u4f46\u65e0\u8bba\u5982\u4f55\u4e5f\u8fd8\u662f\u4e0d\u6562\u4ee5\u8840\u8089\u4e4b\u8eaf\uff0c\u6765\u6321\u8c37\u6db5\u865a\u631f\u7740 \u52b2\u98ce\u523a\u6765\u7684\u8fd9\u4e00\u628a\u660e\u6643\u6643\u7684\u5229\u5251\u3002 \u3000\u3000\u4e54\u5143\u58ee\u7f29\u624b\u4e0d\u8fed\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u8c37\u6db5\u865a\u7684\u5de6\u638c\u5df2\u662f\u5288\u5230\u4e86\u5f20\u5143\u5409\u7684\u80f8\u524d\uff0c\u5f20\u5143\u5409\u4e00 \u5251\u523a\u51fa\uff0c\u9661\u7136\u95f4\u53ea\u89c9\u864e\u53e3\u4e00\u9ebb\uff0c\u90a3\u67c4\u957f\u5251\u5df2\u662f\u7ed9\u8c37\u6db5\u865a\u5288\u624b\u593a\u53bb\uff01\u672c\u6765\u4ee5\u5f20\u5143\u5409\u7684\u672c\u9886\uff0c\u7edd \u4e0d\u4f1a\u8fd9\u6837\u8f7b\u6613\u4fbf\u7ed9\u4ed6\u593a\u4e86\u624b\u4e2d\u7684\u5175\u5203\uff0c\u4f46\u56e0\u8c37\u6db5\u865a\u201c\u58f0\u4e1c\u51fb\u897f\u201d\u7684\u6218\u672f\u4f7f\u5f97\u6070\u5230\u597d\u5904\uff0c\u62c6\u6563 \u4e86\u4ed6\u4eec\u5e08\u5144\u5f1f\u7684\u9632\u5fa1\uff1b\u540c\u65f6\u5f20\u5143\u5409\u4e5f\u60f3\u4e0d\u5230\u5bf9\u65b9\u7adf\u6562\u5982\u6b64\u5927\u80c6\uff0c\u53ea\u51ed\u8089\u638c\uff0c\u5c31\u6562\u6765\u593a\u4ed6\u5229 \u5251\uff0c\u662f\u4ee5\u51b7\u4e0d\u53ca\u9632\uff0c\u7ed9\u8c37\u6db5\u865a\u65bd\u5c55\u201c\u7a7a\u624b\u5165\u767d\u5203\u201d\u7684\u529f\u592b\uff0c\u53ea\u662f\u4e00\u4e2a\u7167\u9762\uff0c\u957f\u5251\u767b\u65f6\u6613\u624b\u3002 \u3000\u3000\u8c37\u6db5\u865a\u559d\u9053\uff1a\u201c\u539f\u7269\u5949\u8fd8\uff01\u201d\u628a\u593a\u6765\u7684\u957f\u5251\u98de\u51fa\uff0c\u4f46\u5374\u5411\u7740\u891a\u9752\u5c71\u98de\u53bb\u3002\u891a\u9752\u5c71\u53ef\u6ca1\u6709 \u63a5\u5251\u7684\u529f\u592b\uff0c\u614c\u5fd9\u95ea\u5f00\uff0c\u4e54\u5143\u58ee\u89c1\u8c37\u6db5\u865a\u63b7\u5251\u8fd9\u62db\uff0c\u6b63\u662f\u6b66\u5f53\u6d3e\u201c\u82cd\u9f99\u6389\u5c3e\u201d\u7684\u62db\u6570\uff0c\u5fc3\u91cc \u53c8\u662f\u5403\u60ca\uff0c\u53c8\u662f\u8be7\u5f02\uff0c\u5f53\u4e0b\u4fbf\u786c\u7740\u5934\u76ae\u98de\u8eab\u8dc3\u8d77\uff0c\u628a\u624b\u4e00\u6284\uff0c\u7528\u672c\u95e8\u7684\u201c\u63a5\u5251\u5f0f\u201d\uff0c\u628a\u5e08\u5f1f \u7684\u957f\u5251\u63a5\u4e0b\u6765\u3002\u4ed6\u7684\u5251\u672f\u4e0d\u53ca\u5e08\u5f1f\uff0c\u638c\u4e0a\u7684\u529f\u592b\u5374\u6bd4\u5e08\u5f1f\u5f3a\u5f97\u591a\uff0c\u63a5\u4e0b\u957f\u5251\uff0c\u5c45\u7136\u6ca1\u6709\u53d7 \u4f24\uff0c\u7a0d\u7a0d\u4fdd\u5b58\u4e86\u4e00\u70b9\u989c\u9762\u3002 \u3000\u3000\u6b64\u65f6\u8c37\u6db5\u865a\u5df2\u7ecf\u7a81\u56f4\u800c\u51fa\uff0c\u8dd1\u5230\u524d\u9762\u4e03\u516b\u4e08\u7684\u5bc6\u6797\u4e4b\u4e2d\u3002\u4f46\u5374\u4e0d\u77e5\u600e\u7684\uff0c\u7a81\u7136\u505c\u4e0b\u811a \u6b65\uff0c\u4e25\u70f7\u5927\u559c\u53eb\u9053\uff1a\u201c\u8c37\u5927\u54e5\uff0c\u5feb\u8dd1\uff01\u4e0d\u5fc5\u4e3a\u6211\u62c5\u5fc3\uff0c\u8c05\u4ed6\u4eec\u4e0d\u6562\u5bf9\u6211\u600e\u6837\uff1f\u201d \u3000\u3000\u5b63\u5143\u4f26\u3001\u6881\u5143\u732e\u5de6\u53f3\u5939\u653b\uff0c\u6740\u5f97\u4e25\u70f7\u53ea\u6709\u62db\u8888\u4e4b\u529f\uff0c\u6beb\u65e0\u8fd8\u624b\u4e4b\u529b\uff0c\u4e25\u70f7\u5206\u5fc3\u8bf4\u8bdd\uff0c\u7ed9 \u5bf9\u65b9\u53ef\u4e58\u4e4b\u673a\uff0c\u53ea\u542c\u5f97\u201c\u94db\u201d\u7684\u4e00\u58f0\uff0c\u5934\u4e0a\u7684\u4e00\u6839\u7389\u7c2a\u7ed9\u6881\u5143\u732e\u4e00\u5251\u524a\u65ad\u3002 \u3000\u3000\u5b63\u5143\u4f26\u51b7\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u4eec\u770b\u5728\u4f60\u7239\u7239\u7684\u4efd\u4e0a\uff0c\u662f\u4e0d\u6562\u5bf9\u4f60\u600e\u6837\u3002\u4f46\u6211\u4eec\u53ef\u8981\u628a\u4f60\u9001 \u5230\u4f60\u7239\u7239\u7684\u9762\u524d\uff0c\u5012\u8981\u770b\u770b\u4f60\u7239\u7239\u4ee5\u5ddd\u897f\u5927\u4fa0\u7684\u8eab\u4efd\u628a\u4f60\u5982\u4f55\u5904\u7f6e\uff1f\u201d \u3000\u3000\u4e54\u5143\u58ee\u63a5\u4e0b\u957f\u5251\uff0c\u4ea4\u8fd8\u5e08\u5f1f\uff0c\u9762\u8272\u94c1\u9752\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u5e08\u5f1f\u8fd8\u8bb0\u5f97\u5e08\u5c0a\u6388\u5251\u4e4b\u65f6\u7684\u5429\u5490 \u5417\uff1f\u672c\u95e8\u8363\u8fb1\uff0c\u5982\u4eca\u5c31\u5728\u4f60\u6211\u80a9\u4e0a\u4e86\uff01\u201d \u3000\u3000\u539f\u6765\u6b66\u5f53\u6d3e\u4ee5\u5251\u672f\u3001\u5185\u529f\u9a70\u540d\u5929\u4e0b\uff0c\u6bcf\u4e2a\u5f1f\u5b50\u5b66\u6210\u51fa\u5e08\u4e4b\u65e5\uff0c\u90fd\u6709\u4e00\u4e2a\u6388\u5251\u7684\u4eea\u5f0f\uff0c\u7531 \u5e08\u7236\u5f53\u7740\u4e00\u7fa4\u540c\u95e8\uff0c\u628a\u5251\u90d1\u91cd\u7684\u6388\u7ed9\u5f92\u5f1f\uff0c\u5e76\u4ea4\u4ee3\u4e24\u53e5\u8bf4\u8bdd\uff0c\u8fd9\u4e24\u53e5\u8bf4\u8bdd\u4e43\u662f\uff1a\u201c\u5251\u5728\u4eba \u5728\uff0c\u5251\u4ea1\u4eba\u4ea1\uff01\u201d\u6545\u6b64\u51e1\u662f\u6b66\u5f53\u5f1f\u5b50\uff0c\u90fd\u662f\u628a\u81ea\u5df1\u6240\u7528\u7684\u8fd9\u4e00\u628a\u5251\u770b\u5f97\u6bd4\u547d\u6839\u5b50\u8fd8\u91cd\u7684\u3002 \u3000\u3000\u5982\u4eca\u5f20\u5143\u5409\u7684\u5251\u7ed9\u8c37\u6db5\u865a\u593a\u4e86\u53bb\uff0c\u867d\u6709\u5e08\u5144\u63a5\u4e86\u56de\u6765\uff0c\u4f46\u5bf9\u5f20\u5143\u5409\u800c\u8a00\uff0c\u8fd9\u5df2\u7ecf\u662f\u5947\u803b \u5927\u8fb1\uff0c\u6bd4\u5e08\u5144\u7ed9\u6253\u4e86\u4e00\u8bb0\u8033\u5149\u7684\u803b\u8fb1\uff0c\u66f4\u5927\u5f97\u591a\uff01\u5982\u679c\u4ed6\u7684\u5251\u5c16\u4e0d\u80fd\u996e\u4ec7\u4eba\u4e4b\u8840\uff0c\u4ed6\u5c31\u6ca1\u6709 \u9762\u76ee\u518d\u89c1\u540c\u95e8\u3002\u4e54\u5143\u58ee\u5411\u4ed6\u63d0\u8d77\u201c\u5e08\u5c0a\u6388\u5251\u4e4b\u65f6\u7684\u5429\u5490\u201d\uff0c\u8fd9\u4e5f\u5c31\u662f\u903c\u4ed6\u8981\u4e3a\u5e08\u95e8\u8363\u8fb1\u62fc\u6b7b \u62a5\u4ec7\u7684\u610f\u601d\u3002 \u3000\u3000\u5f20\u5143\u5409\u63a5\u8fc7\u957f\u5251\uff0c\u9762\u4e0a\u4e00\u9635\u9752\u4e00\u9635\u7ea2\uff0c\u54ac\u7259\u8bf4\u9053\uff1a\u201c\u6b7b\u751f\u4e8b\u5c0f\uff0c\u5e08\u95e8\u8363\u8fb1\u4e8b\u5927\uff0c\u4e0d\u52b3\u5e08 \u5144\u63d0\u9192\uff0c\u5c0f\u5f1f\u4e5f\u77e5\u9053\u662f\u5e94\u8be5\u600e\u6837\u505a\u4e86\uff01\u201d\u5f53\u4e0b\u4e54\u3001\u5f20\u4e8c\u4eba\uff0c\u4e00\u4e2a\u8981\u62a5\u638c\u63b4\u4e4b\u4ec7\uff0c\u4e00\u4e2a\u8981\u96ea\u593a \u5251\u4e4b\u803b\uff0c\u53cc\u53cc\u633a\u5251\u800c\u4e0a\u3002 \u3000\u3000\u8c37\u6db5\u865a\u672c\u6765\u53ef\u4ee5\u8dd1\u5f97\u51fa\u53bb\u7684\uff0c\u4f46\u4ed6\u53ea\u4e0d\u8fc7\u8dd1\u51fa\u4e03\u516b\u4e08\u4e4b\u5730\uff0c\u5c31\u5ffd\u7136\u505c\u4e0b\u811a\u6b65\uff0c\u4e0d\u518d\u8dd1 \u4e86\uff0c\u4e25\u70f7\u5927\u4e3a\u7740\u6025\uff0c\u8fde\u58f0\u50ac\u4ed6\u5feb\u8dd1\u3002 \u3000\u3000\u773c\u770b\u4e54\u3001\u5f20\u4e8c\u4eba\u5373\u5c06\u8ffd\u5230\uff0c\u891a\u9752\u5c71\u4ea6\u5df2\u4ece\u53e6\u4e00\u9762\u653b\u6765\uff0c\u5c31\u8981\u622a\u65ad\u4ed6\u7684\u540e\u8def\uff0c\u8c37\u6db5\u865a\u84e6\u5730 \u559d\u9053\uff0c\u201c\u4e14\u53eb\u4f60\u4eec\u89c1\u8bc6\u89c1\u8bc6\u6211\u7684\u624b\u6bb5\uff01\u201d\u559d\u58f0\u4e2d\u4e00\u638c\u5411\u8eab\u8fb9\u7684\u4e00\u68f5\u5927\u6811\u5288\u4e0b\uff0c\u767b\u65f6\u597d\u50cf\u6674\u5929 \u8d77\u4e86\u4e2a\u9739\u96f3\uff0c\u4ed6\u8fd9\u4e00\u638c\u6253\u51fa\uff0c\u7adf\u662f\u9690\u9690\u631f\u7740\u98ce\u96f7\u4e4b\u58f0\uff01 \u3000\u3000\u53ea\u542c\u5f97\u4e00\u7247\u6811\u679d\u65ad\u6298\u7684\u201c\u529b\u52d2\u201d\u4e4b\u58f0\u6ee1\u7a7a\u843d\u53f6\u98de\u821e\uff0c\u90a3\u68f5\u6570\u4eba\u5408\u62b1\u7684\u5927\u6811\uff0c\u4e5f\u9707\u5f97\u4f3c\u4e4e \u5c31\u8981\u5012\u4e0b\u7684\u6837\u5b50\uff01 \u3000\u3000\u679d\u96f6\u53f6\u843d\uff0c\u6811\u9876\u4e0a\u7a81\u7136\u73b0\u51fa\u4e00\u4e2a\u4eba\u5f71\uff0c\u8fd9\u4e2a\u4eba\u7fe9\u5982\u98de\u9e1f\u4f3c\u5730\u7a81\u7136\u8df3\u4e0b\u6765\u4e86\uff01 \u3000\u3000\u539f\u6765\u5b5f\u660e\u971e\u521a\u597d\u662f\u8eb2\u5728\u8fd9\u68f5\u6811\u4e0a\u3002\u8c37\u6db5\u865a\u4ee5\u201c\u5929\u96f7\u529f\u201d\u529b\u64bc\u5927\u6811\uff0c\u5b5f\u660e\u971e\u4e5f\u7ed9\u4ed6\u9707\u5f97\u85cf \u8eab\u4e0d\u4f4f\u3002\u9669\u4e9b\u4e00\u4e2a\u201c\u5012\u683d\u8471\u201d\u8dcc\u4e86\u4e0b\u6765\uff0c\u5e78\u800c\u5979\u8f7b\u529f\u6781\u597d\uff0c\u843d\u4e0b\u4e4b\u65f6\uff0c\u811a\u5c16\u8f7b\u8f7b\u4e00\u70b9\uff0c\u501f\u7740 \u5927\u6811\u9707\u8361\u4e4b\u52bf\uff0c\u201c\u98de\u201d\u51fa\u6570\u4e08\u5f00\u5916\uff0c\u6070\u597d\u53c8\u843d\u5728\u4e25\u70f7\u7684\u9644\u8fd1\u3002 \u3000\u3000\u8c37\u6db5\u865a\u521a\u624d\u529b\u654c\u4e54\u5143\u58ee\u3001\u5f20\u5143\u5409\u3001\u891a\u9752\u5c71\u4e09\u4eba\uff0c\u4e00\u6765\u56e0\u4e3a\u672a\u77e5\u891a\u9752\u5c71\u7684\u6765\u5386\uff0c\u4e8c\u6765\u770b\u5728 \u6b66\u5f53\u6d3e\u7684\u4efd\u4e0a\uff0c\u4e09\u6765\u81ea\u5df1\u62a2\u4e86\u5f20\u5143\u5409\u7684\u672a\u5a5a\u59bb\u5b50\uff0c\u5fc3\u4e2d\u4e5f\u4e0d\u5143\u591a\u5c11\u6b49\u610f\u3002\u662f\u4ee5\u59cb\u7ec8\u672a\u66fe\u4f7f\u51fa \u6740\u624b\u3002\u6b64\u9645\u7ed9\u4ed6\u4eec\u903c\u5f97\u65e0\u53ef\u9000\u8ba9\uff0c\u4ed6\u4e0d\u80fd\u820d\u4e86\u4e25\u70f7\u800c\u9003\uff0c\u53ea\u597d\u628a\u5e08\u7236\u4f20\u7684\u201c\u5929\u96f7\u529f\u201d\u70ab\u9732\u51fa \u6765\u3002\u4f46\u8fd9\u4e00\u638c\u628a\u5b5f\u660e\u971e\u4e5f\u201c\u6253\u201d\u4e86\u4e0b\u6765\uff0c\u5374\u662f\u5927\u5927\u51fa\u4e4e\u4ed6\u610f\u6599\u4e4b\u5916\uff01 \u3000\u3000\u8fd9\u5239\u90a3\u95f4\uff0c\u6b66\u5f53\u6d3e\u7684\u56db\u4e2a\u5f1f\u5b50\u548c\u891a\u9752\u5c71\u4e5f\u90fd\u662f\u5927\u5403\u4e00\u60ca\uff0c\u4e0d\u89c9\u5446\u4e86\uff01 \u3000\u3000\u8c37\u6db5\u865a\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u4f60\u4eec\u8c01\u8981\u53d6\u6211\u6027\u547d\uff0c\u90a3\u5c31\u8bf7\u4e0a\u5427\uff01\u4f46\u6211\u5fc5\u987b\u544a\u8bc9\u4f60\u4eec\uff0c\u6211\u662f\u793c \u5c1a\u5f80\u6765\u7684\u3002\u4f60\u4eec\u65e2\u7136\u8981\u53d6\u6211\u7684\u6027\u547d\uff0c\u90a3\u4e5f\u5c31\u4f11\u602a\u6211\u4e0b\u5f97\u8fa3\u624b\u4e86\uff01\u201d \u3000\u3000\u891a\u9752\u5c71\u89c1\u4ed6\u9732\u4e86\u8fd9\u4e00\u624b\u60ca\u4e16\u9a87\u4fd7\u7684\u201c\u5929\u96f7\u529f\u201d\uff0c\u4e0d\u7531\u5f97\u6697\u6697\u5403\u60ca\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u602a\u4e0d\u5f97 \u6bb5\u5e08\u5144\u6298\u5728\u4ed6\u7684\u624b\u91cc\uff0c\u8fd9\u5c0f\u5b50\u7684\u529f\u592b\u679c\u7136\u6709\u70b9\u90aa\u95e8\uff0c\u521a\u624d\u5e76\u672a\u5c3d\u5c55\u6240\u957f\u7684\u3002\u4ed6\u82e5\u5f53\u771f\u62fc\u547d\u7684 \u8bdd\uff0c\u6211\u4eec\u7eb5\u7136\u80fd\u591f\u8054\u624b\u5c06\u4ed6\u6740\u6389\uff0c\u53ea\u6015\u4e5f\u662f\u96be\u514d\u6709\u6240\u4f24\u4ea1\u7684\uff01\u201d\u65e2\u7136\u96be\u514d\u6709\u6240\u4f24\u4ea1\uff0c\u4f24\u4ea1\u8005 \u4e5f\u96be\u4fdd\u4e0d\u662f\u81ea\u5df1\u3002\u891a\u9752\u5c71\u60f3\u81f3\u6b64\u5904\uff0c\u4e0d\u89c9\u6253\u4e86\u4e00\u4e2a\u5bd2\u5664\u3002 \u3000\u3000\u4e54\u5143\u58ee\u4e0e\u5f20\u5143\u5409\u542c\u4e86\u8c37\u6db5\u865a\u8fd9\u756a\u8bf4\u8bdd\uff0c\u9762\u9762\u76f8\u89d1\uff0c\u505a\u58f0\u4e0d\u5f97\u3002 \u3000\u3000\u8c37\u6db5\u865a\u53ea\u662f\u4e00\u638c\uff0c\u5c31\u628a\u4e00\u68f5\u679d\u7e41\u53f6\u8302\u7684\u5927\u6811\u6253\u5f97\u53ea\u5269\u5149\u79c3\u79c3\u7684\u6811\u5e72\uff0c\u4e54\u5143\u58ee\u5e73\u7d20\u4ee5\u638c\u529b \u81ea\u8d1f\uff0c\u6b64\u65f6\u89c1\u4e86\u4ed6\u8fd9\u201c\u5929\u96f7\u529f\u201d\u7684\u5a01\u529b\uff0c\u4e5f\u662f\u81ea\u6127\u4e0d\u5982\u3002\u5f20\u5143\u5409\u672c\u6765\u662f\u6ee1\u8154\u6012\u706b\uff0c\u8a93\u8981\u4e0e\u4ed6 \u62fc\u547d\u7684\uff0c\u6b64\u65f6\u4e5f\u662f\u4e0d\u7981\u6709\u70b9\u8e0c\u8e87\u83ab\u51b3\u4e86\u3002 \u3000\u3000\u4f46\u4ed6\u4eec\u4e8c\u4eba\u90fd\u662f\u8ba4\u4e3a\u81ea\u5df1\u53d7\u4e86\u5947\u803b\u5927\u8fb1\u7684\uff0c\u8981\u4ed6\u4eec\u5c31\u6b64\u7f62\u624b\uff0c\u4ed6\u4eec\u53c8\u5c82\u80fd\u7518\u5fc3\uff1f\u4e54\u5143\u58ee \u5fc3\u5ff5\u4e00\u52a8\uff0c\u5ffd\u5730\u53eb\u9053\uff1a\u201c\u5feb\u628a\u90a3\u5c0f\u8d31\u4eba\u62ff\u4e0b\uff01\u201d \u3000\u3000\u4e14\u8bf4\u5b5f\u660e\u971e\u4ece\u6811\u4e0a\u8df3\u4e0b\u6765\uff0c\u521a\u597d\u843d\u5728\u4e25\u70f7\u8eab\u8fb9\uff0c\u4e25\u70f7\u53c8\u662f\u5403\u60ca\uff0c\u53c8\u662f\u8be7\u5f02\uff0c\u5931\u58f0\u53eb\u9053\uff1a \u201c\u8868\u59b9\uff0c\u662f\u4f60\uff01\u662f\u6211\u5988\u53eb\u4f60\u6765\u7684\u4e48\uff1f\u201d \u3000\u3000\u5b63\u5143\u4f26\u3001\u6881\u5143\u732e\u4e8c\u4eba\u4e5f\u662f\u5446\u4e86\u4e00\u5446\uff0c\u4f46\u542c\u4e86\u4e54\u5143\u58ee\u7684\u58f0\u97f3\uff0c\u9a6c\u4e0a\u5c31\u9192\u89c9\u8fc7\u6765\uff0c\u4e0d\u7ea6\u800c\u540c \u5730\u53cc\u5251\u5e76\u51fa\uff0c\u5411\u4e25\u70f7\u653b\u53bb\u3002\u4e25\u70f7\u672c\u6765\u5c31\u4e0d\u662f\u4ed6\u4eec\u7684\u654c\u624b\uff0c\u6b64\u9645\u5fc3\u795e\u672a\u5b9a\uff0c\u51b7\u4e0d\u53ca\u9632\uff0c\u9661\u7136\u95f4 \u53ea\u89c1\u767d\u5203\u8000\u773c\uff0c\u7cb1\u5143\u732e\u7684\u5251\u5c16\u5df2\u6307\u5230\u4e86\u5979\u7684\u54bd\u5589\u3002\u4e25\u70f7\u767e\u5fd9\u4e2d\u7528\u4e86\u4e00\u62db\u201c\u661f\u6a2a\u6597\u8f6c\u201d\uff0c\u6a2a\u5251 \u4e00\u5c01\uff0c\u5b63\u5143\u4f26\u7684\u957f\u5251\u4ea6\u5df2\u523a\u5230\uff0c\u8fd9\u4e00\u5251\u5f84\u523a\u4e25\u70f7\u7684\u8109\u95e8\uff0c\u4e25\u70f7\u82e5\u4e0d\u6254\u5251\u7684\u8bdd\uff0c\u5c31\u975e\u53d7\u4f24\u4e0d \u53ef\u3002\u4f46\u82e5\u6254\u5251\u90a3\u5c31\u662f\u675f\u624b\u5c31\u64d2\u4e86\u3002 \u3000\u3000\u8c37\u6db5\u865a\u6599\u60f3\u4ed6\u4eec\u4e0d\u6562\u4f24\u5bb3\u4e25\u70f7\u7684\u6027\u547d\uff0c\u4f46\u8fd9\u53ea\u662f\u201c\u6599\u60f3\u201d\u800c\u5df2\uff0c\u5e76\u4e0d\u80fd\u65ad\u5b9a\u4ed6\u4eec\u7edd\u5bf9\u4e0d \u6562\uff0c\u6b64\u9645\u89c1\u4ed6\u4eec\u7a81\u65bd\u6740\u624b\uff0c\u5927\u60ca\u4e4b\u4e0b\uff0c\u5fd9\u6251\u8fc7\u53bb\uff0c\u559d\u9053\uff1a\u201c\u8c01\u6562\u4f24\u5979\uff0c\u6211\u5c31\u628a\u8c01\u6bd9\u4e86\uff01\u201d \u3000\u3000\u5f20\u5143\u5409\u3001\u4e54\u5143\u58ee\u65e9\u6599\u4ed6\u6709\u6b64\u4e00\u7740\uff0c\u62a2\u5148\u4e00\u6b65\uff0c\u6321\u5728\u4ed6\u4e0e\u4e25\u70f7\u7684\u4e2d\u95f4\uff0c\u4e54\u5143\u58ee\u51b7\u7b11\u9053\uff1a \u201c\u4f60\u8981\u6bd9\u6211\uff0c\u90a3\u4e5f\u4e0d\u96be\uff0c\u4f46\u4f60\u7684\u4e25\u59d1\u5a18\u7684\u6027\u547d\u4e5f\u4fdd\u4e0d\u4f4f\u5566\uff01\u6211\u770b\u4f60\u8fd8\u662f\u675f\u624b\u5c31\u64d2\u5427\uff0c\u597d\u6b79\u4e5f \u53ef\u4ee5\u505a\u4e00\u5bf9\u540c\u547d\u9e33\u9e2f\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u4e0e\u4e25\u70f7\u4e4b\u95f4\u5c1a\u6709\u6570\u4e08\u8ddd\u79bb\uff0c\u800c\u5f20\u3001\u4e54\u4e8c\u4eba\u53c8\u975e\u5eb8\u624b\uff0c\u8c37\u6db5\u865a\u8981\u60f3\u6253\u53d1\u4ed6\u4eec\uff0c\u51b3\u975e \u5341\u62db\u4e4b\u5185\u6240\u80fd\u529e\u5230\uff0c\u4f55\u51b5\u8fd8\u6709\u4e00\u4e2a\u672c\u9886\u66f4\u5f3a\u7684\u891a\u9752\u5c71\u8ddf\u7740\u5c31\u6765\uff0c\u662f\u4ee5\u8c37\u6db5\u865a\u660e\u77e5\u4ed6\u4eec\u662f\u7528\u4e25 \u70f7\u7684\u6027\u547d\u6765\u6050\u5413\u81ea\u5df1\uff0c\u4e5f\u53ea\u5f97\u53d7\u4ed6\u4eec\u7684\u5a01\u80c1\uff0c\u4e0d\u6562\u9c81\u83bd\u4ece\u4e8b\u3002 \u3000\u3000\u53ef\u662f\u4e54\u5143\u58ee\u7684\u7b97\u76d8\u867d\u7136\u6253\u5f97\u5982\u610f\uff0c\u5374\u4e5f\u6709\u4e00\u7740\u5931\u7b97\u4e4b\u5904\u3002\u4ed6\u53ea\u77e5\u9053\u63d0\u9632\u8c37\u6db5\u865a\uff0c\u4ee5\u4e3a\u5835 \u622a\u4e86\u8c37\u6db5\u865a\u5c31\u53ef\u4ee5\u628a\u4e25\u70f7\u624b\u5230\u64d2\u6765\uff0c\u5374\u4e0d\u77e5\u8fd1\u5728\u4e25\u70f7\u8eab\u65c1\u7684\u5b5f\u660e\u971e\u4e5f\u662f\u4e00\u4e2a\u52b2\u654c\u3002 \u3000\u3000\u5b5f\u660e\u971e\u542c\u5f97\u4e25\u70f7\u90a3\u6837\u95ee\u5979\uff0c\u5fc3\u91cc\u5341\u5206\u96be\u8fc7\uff0c\u60f3\u9053\uff1a\u201c\u6211\u8bef\u6253\u8bef\u649e\uff0c\u649e\u4e0a\u4e86\u8fd9\u6837\u5c34\u5c2c\u7684\u573a \u9762\uff0c\u5012\u6559\u8868\u59d0\u8d77\u4e86\u7591\u5fc3\uff0c\u7591\u5fc3\u6211\u662f\u505a\u4e86\u59d1\u59d1\u7684\u8033\u76ee\uff0c\u7279\u5730\u6765\u6b64\u7aa5\u4f3a\u5979\u7684\u9690\u79c1\u6765\u4e86\u3002\u201d\u5b5f\u660e\u971e \u672c\u6765\u5c31\u60f3\u52a9\u8868\u59d0\u4e00\u81c2\u4e4b\u529b\uff0c\u5e2e\u5979\u89e3\u56f0\u7684\uff0c\u6b64\u65f6\u4e3a\u4e86\u8981\u8868\u660e\u5fc3\u8ff9\uff01\u66f4\u662f\u51b3\u610f\u8981\u51fa\u624b\u4e86\u3002 \u3000\u3000\u4e14\u8bf4\u5b63\u5143\u4f26\u4e00\u5251\u523a\u5411\u4e25\u70f7\u7684\u8109\u95e8\uff0c\u773c\u770b\u4e25\u70f7\u4e0d\u662f\u6254\u5251\u5c31\u64d2\uff0c\u5c31\u975e\u5f97\u53d7\u4f24\u4e0d\u53ef\uff0c\u5ffd\u542c\u5f97\u4e00 \u58f0\u53f1\u54a4\uff0c\u4e00\u4e2a\u6e05\u8106\u7684\u58f0\u97f3\u559d\u9053\uff1a\u201c\u6492\u5251\uff01\u201d\u4e00\u9053\u94f6\u5149\u8f9c\u7136\u98de\u8d77\uff0c\u4f46\u5175\u5203\u8131\u624b\u7684\u5374\u4e0d\u662f\u4e25\u70f7\u800c \u662f\u5b63\u5143\u4f26\u3002 \u3000\u3000\u539f\u6765\u5b63\u3001\u6881\u4e8c\u4eba\u867d\u7136\u770b\u89c1\u5b5f\u660e\u971e\u843d\u5728\u4e25\u70f7\u7684\u8eab\u65c1\uff0c\u4e5f\u542c\u5f97\u4e25\u70f7\u53eb\u5979\u8868\u59b9\uff0c\u4f46\u56e0\u5b5f\u660e\u971e\u4e0d \u8fc7\u662f\u4e2a\u5341\u4e03\u5c81\u7684\u5c0f\u59d1\u5a18\uff0c\u8fd9\u4e8c\u4eba\u54ea\u91cc\u5c06\u5979\u653e\u5728\u773c\u5185\uff1f \u3000\u3000\u4e0d\u6599\u5b5f\u660e\u971e\u5e74\u7eaa\u867d\u5c0f\uff0c\u672c\u9886\u5374\u662f\u6bd4\u5979\u7684\u8868\u59d0\u8fd8\u8981\u9ad8\u5f3a\uff0c\u5b63\u5143\u4f26\u4e00\u5fc3\u60f3\u903c\u4e25\u70f7\u6254\u5251\uff0c\u773c\u770b \u5373\u5c06\u5f97\u624b\uff0c\u6b63\u81ea\u5f97\u610f\uff0c\u51b7\u4e0d\u53ca\u9632\uff0c\u7ed9\u5b5f\u660e\u971e\u6b3a\u5230\u8eab\u524d\uff0c\u4e00\u62db\u201c\u53bb\u5251\u5f0f\u201d\u7684\u7cbe\u5947\u624b\u6cd5\uff0c\u5c31\u628a\u4ed6 \u7684\u9752\u94a2\u5251\u593a\u51fa\u4e86\u624b\u3002 \u3000\u3000\u5b5f\u660e\u971e\u6697\u5730\u8ddf\u8e2a\u8868\u59d0\uff0c\u5e76\u6ca1\u643a\u5e26\u5175\u5668\uff0c\u5979\u4e5f\u4e0d\u77e5\u6b66\u5f53\u6d3e\u6709\u201c\u5251\u5728\u4eba\u5728\uff0c\u5251\u4ea1\u4eba\u4ea1\u201d\u7684\u5e08 \u8bad\uff0c\u4e00\u62db\u201c\u53bb\u5251\u5f0f\u201d\u8feb\u4f7f\u5b63\u5143\u4f26\u6254\u5251\u4e4b\u540e\uff0c\u5b69\u5b50\u6c14\u53d1\u4f5c\uff0c\u4fbf\u5373\u7b11\u9053\uff1a\u201c\u8fd9\u628a\u5251\u6b63\u5408\u6211\u7528\uff0c\u4f60 \u4e0d\u4f1a\u4f7f\uff0c\u7ed9\u4e86\u6211\u5427\uff01\u201d\u628a\u624b\u4e00\u6284\uff0c\u8f7b\u8f7b\u5de7\u5de7\u5730\u5c06\u90a3\u67c4\u957f\u5251\u63a5\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u5b5f\u660e\u971e\u593a\u5f97\u957f\u5251\uff0c\u5530\u7684\u8ddf\u7740\u4fbf\u5411\u6881\u5143\u732e\u523a\u53bb\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u5e08\u5144\u7684\u5251\u5df2\u7ecf\u7ed9\u6211\u593a\u4e86\uff0c\u4e0d\u593a \u4f60\u7684\uff0c\u5c31\u4e0d\u516c\u5e73\u4e86\u3002\u201d \u3000\u3000\u5b5f\u5bb6\u5251\u6cd5\u5947\u8be1\u83ab\u6d4b\uff0c\u6881\u5143\u732e\u5373\u4f7f\u4e0e\u5979\u5355\u6253\u72ec\u6597\uff0c\u4e5f\u51b3\u8ba1\u4e0d\u662f\u5979\u7684\u5bf9\u624b\uff0c\u4f55\u51b5\u6b64\u65f6\u4ed6\u8fd8\u6b63 \u5728\u5bf9\u4ed8\u4e25\u70f7\uff1f\u5f85\u4ed6\u84e6\u5730\u4e00\u60ca\u4e4b\u9645\uff0c\u53ea\u89c9\u864e\u53e3\u4e00\u9ebb\uff0c\u5df2\u662f\u7ed9\u5b5f\u660e\u971e\u7684\u5251\u5c16\u70b9\u7740\u4e86\u8109\u95e8\uff0c\u957f\u5251\u94db \u7684\u5760\u5730\uff01\u5b5f\u660e\u971e\u5f97\u610f\u975e\u5e38\uff0c\u683c\u683c\u7b11\u9053\uff1a\u201c\u6211\u8fd9\u53eb\u505a\u4ee5\u5176\u4eba\u4e4b\u9053\u8fd8\u6cbb\u5176\u4eba\u4e4b\u8eab\uff0c\u8c01\u53eb\u4f60\u4eec\u903c\u6211 \u8868\u59d0\u64a4\u5251\uff1f\u201d \u3000\u3000\u5b63\u3001\u7cb1\u4e8c\u4eba\u5e74\u8f7b\u6c14\u76db\uff0c\u4e00\u4e2a\u7167\u9762\u5c31\u7ed9\u4e00\u4e2a\u5c0f\u59d1\u5a18\u628a\u4ed6\u4eec\u7684\u5251\u593a\u4e86\u53bb\uff0c\u83ab\u8bf4\u6709\u201c\u5251\u5728\u4eba \u5728\uff0c\u5251\u4ea1\u4eba\u4ea1\u201d\u7684\u5e08\u8bad\uff0c\u5c31\u662f\u6ca1\u6709\uff0c\u4ed6\u4eec\u4e5f\u975e\u5f97\u548c\u5b5f\u660e\u971e\u62fc\u547d\u4e0d\u53ef\u3002 \u3000\u3000\u90a3\u8fb9\u8c37\u6db5\u865a\u4e5f\u548c\u4e54\u5143\u58ee\u3001\u5f20\u5143\u5409\u3001\u891a\u9752\u5c71\u4eba\u518d\u5ea6\u4ea4\u8d77\u624b\u6765\u3002\u8c37\u6db5\u865a\u4e00\u89c1\u4e25\u70f7\u8131\u9669\uff0c\u521a\u624d \u618b\u4e86\u4e00\u809a\u76ae\u7684\u6c14\u4e0d\u7531\u81ea\u5df1\u5730\u90fd\u8981\u53d1\u4f5c\u51fa\u6765\uff0c\u4e54\u5143\u58ee\u9996\u5f53\u5176\u51b2\uff0c\u53cc\u638c\u4e00\u4ea4\uff0c\u53ea\u542c\u5f97\u201c\u84ec\u201d\u7684\u4e00 \u58f0\uff0c\u4e54\u5143\u58ee\u50cf\u6c14\u7403\u4e00\u822c\u7684\u7ed9\u629b\u51fa\u4e86\u4e09\u4e08\u5f00\u5916\u3002 \u3000\u3000\u8c37\u6db5\u865a\u4f7f\u7684\u8fd9\u4e00\u62db\u4e43\u662f\u201c\u8fde\u73af\u76f8\u649e\u638c\u201d\uff0c\u53f3\u638c\u9707\u7ffb\u4e86\u4e54\u5143\u58ee\uff0c\u5de6\u638c\u7acb\u5373\u5954\u96f7\u9a87\u7535\u7684\u5411\u5f20 \u5143\u5409\u6253\u53bb\uff0c\u5f20\u5143\u5409\u53cc\u773c\u706b\u7ea2\uff0c\u559d\u9053\uff1a\u201c\u6211\u4e0e\u4f60\u62fc\u5566\uff01\u201d\u638c\u98ce\u5251\u5f71\u4e4b\u4e2d\uff0c\u5f20\u5143\u5409\u4e00\u5251\u523a\u7a7a\uff0c\u84e6 \u89c9\u8eab\u5b50\u4e00\u8f7b\uff0c\u5df2\u662f\u7ed9\u8c37\u6db5\u865a\u4f9d\u6837\u753b\u846b\u82a6\u5730\u629b\u4e86\u8d77\u6765\uff01 \u3000\u3000\u5f20\u5143\u5409\u5728\u534a\u7a7a\u4e2d\u4e00\u4e2a\u201c\u9e5e\u5b50\u7ffb\u8eab\u201d\u843d\u4e0b\u5730\u6765\uff0c\u53ea\u89c9\u80f8\u4e2d\u6c14\u8840\u7ffb\u6d8c\uff0c\u5341\u5206\u96be\u53d7\uff0c\u4f46\u6240\u53d7\u7684 \u4f24\uff0c\u5374\u5e76\u4e0d\u5982\u4ed6\u9884\u6599\u7684\u90a3\u6837\u4e25\u91cd\u3002\u80f8\u4e2d\u867d\u7136\u597d\u4f3c\u538b\u4e86\u4e00\u5757\u5927\u77f3\uff0c\u4f46\u8bd5\u4e00\u8fd0\u6c14\uff0c\u75bc\u75db\u4fbf\u51cf\uff0c\u53ef \u77e5\u5e76\u6ca1\u53d7\u5230\u5185\u4f24\uff0c\u4ed6\u6240\u53d7\u7684\u4f24\uff0c\u4e0d\u8fc7\u662f\u65ad\u4e86\u4e00\u6761\u808b\u9aa8\u7684\u5916\u4f24\u800c\u5df2\uff0c\u65ad\u9aa8\u4e4b\u4f24\u867d\u7136\u4e0d\u8f7b\uff0c\u4f46\u6bd4 \u8d77\u4ed6\u7684\u5e08\u5144\u4e54\u65e0\u58ee\u7ed9\u6253\u5f97\u5410\u8840\u7684\u5185\u4f24\uff0c\u53ef\u53c8\u7b97\u4e0d\u5f97\u4ec0\u4e48\u4e86\u3002 \u3000\u3000\u539f\u6765\u8c37\u6db5\u865a\u5728\u75db\u4e0b\u6740\u624b\u4e4b\u9645\uff0c\u5ffd\u5730\u60f3\u8d77\u81ea\u5df1\u5df2\u7ecf\u62a2\u4e86\u4ed6\u7684\u672a\u5a5a\u59bb\uff0c\u82e5\u518d\u628a\u4ed6\u6253\u5f97\u91cd\u4f24\uff0c \u4e8e\u5fc3\u4f55\u5fcd\uff1f\u56e0\u6b64\u5728\u90a3\u4e00\u638c\u6253\u5230\u5f20\u5143\u5409\u8eab\u4e0a\u4e4b\u65f6\uff0c\u4ed6\u53ca\u65f6\u6536\u56de\u4e86\u4e03\u5206\u638c\u529b\u3002 \u3000\u3000\u5f20\u5143\u5409\u7684\u5251\u6cd5\u5728\u540c\u95e8\u4e2d\u53f7\u79f0\u7b2c\u4e00\uff0c\u529f\u529b\u5219\u4e0d\u53ca\u5e08\u5144\uff0c\u5982\u4eca\u5e08\u5144\u53d7\u4e86\u91cd\u4f24\uff0c\u800c\u4ed6\u7684\u4f24\u5374\u8f7b \u5f97\u51fa\u4e4e\u610f\u6599\u4e4b\u5916\uff0c\u60c5\u77e5\u662f\u8c37\u6db5\u865a\u53c8\u4e00\u6b21\u9976\u4e86\u4ed6\u7684\u6027\u547d\u3002\u4ed6\u5446\u4e86\u4e00\u5446\uff0c\u8d70\u8fc7\u53bb\u6276\u8d77\u5e08\u5144\uff0c\u4e00\u65f6 \u95f4\u7adf\u662f\u4e0d\u77e5\u8be5\u5f53\u5982\u4f55\u624d\u597d\u3002 \u3000\u3000\u891a\u9752\u5c71\u5200\u4e2d\u5939\u638c\uff0c\u5df2\u662f\u4e0e\u8c37\u6db5\u865a\u62fc\u4e86\u4e09\u62db\uff0c\u8c37\u6db5\u865a\u4ee5\u5251\u654c\u5200\uff0c\u4ee5\u638c\u5bf9\u638c\u3002\u5230\u4e86\u7b2c\u4e09\u62db \u65f6\uff0c\u771f\u6c14\u5df2\u7ecf\u91cd\u805a\uff0c\u4f7f\u51fa\u4e86\u201c\u5929\u96f7\u529f\u201d\uff0c\u547c\u7684\u4e00\u638c\u5288\u5c06\u8fc7\u53bb\u3002 \u3000\u3000\u53cc\u638c\u4e00\u4ea4\uff0c\u53d1\u51fa\u4e86\u90c1\u96f7\u822c\u7684\u58f0\u54cd\uff0c\u891a\u9752\u5c71\u7acb\u8db3\u4e0d\u7a33\uff0c\u8dc4\u8dc4\u8e09\u8e09\u5730\u9000\u51fa\u4e86\u516d\u4e03\u6b65\uff0c\u4f46\u5374\u662f \u559c\u5f62\u4e8e\u8272\uff0c\u811a\u6b65\u4e00\u7a33\uff0c\u4fbf\u7acb\u5373\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u4e0d\u884c\u5566\uff0c\u5feb\u5e76\u80a9\u5b50\u4e0a\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u8eab\u5f62\u7eb9\u4e1d\u4e0d\u52a8\uff0c\u53ef\u662f\u80f8\u53e3\u5df2\u662f\u9690\u9690\u4f5c\u75db\uff0c\u638c\u5fc3\u4e5f\u6709\u4e86\u9ebb\u75d2\u75d2\u7684\u611f\u89c9\u3002\u539f\u6765\u4ed6\u7684 \u201c\u5929\u96f7\u529f\u201d\u5c1a\u672a\u7ec3\u5230\u6536\u53d1\u968f\u5fc3\u7684\u5883\u754c\uff0c\u521a\u624d\u4ed6\u4e3a\u4e86\u4e0d\u5fcd\u628a\u5f20\u5143\u5409\u6253\u5f97\u91cd\u4f24\uff0c\u4ed3\u5352\u4e4b\u95f4\uff0c\u6536\u56de \u638c\u529b\uff0c\u5f04\u5f97\u53cd\u9707\u81ea\u8eab\uff0c\u771f\u6c14\u6d6e\u6563\uff0c\u867d\u672a\u53d7\u4f24\uff0c\u529f\u529b\u5df2\u662f\u5927\u6253\u6298\u6263\uff0c\u891a\u9752\u5c71\u662f\u4e2a\u6b66\u5b66\u884c\u5bb6\uff0c\u770b \u51fa\u4e86\u4ed6\u8fd9\u5f31\u70b9\uff0c\u624d\u6562\u548c\u4ed6\u6253\u5bf9\u638c\u7684\u3002 \u3000\u3000\u891a\u9752\u5c71\u7ec3\u6709\u6bd2\u638c\u529f\u592b\uff0c\u8c37\u6db5\u865a\u7684\u529f\u529b\u5df2\u7ecf\u6253\u4e86\u6298\u6263\uff0c\u7ed9\u4ed6\u7684\u6bd2\u638c\u6253\u7740\uff0c\u867d\u7136\u662f\u8d62\u4e86\u4e00 \u62db\uff0c\u4f46\u5374\u4e2d\u4e86\u6bd2\u4e86\u3002\u6b64\u65f6\u4ed6\u5fc5\u987b\u8fd0\u6c14\u6297\u6bd2\uff0c\u5018\u82e5\u5f20\u3001\u4e54\u4e8c\u4eba\u8054\u624b\u518d\u4e0a\uff0c\u4e0e\u891a\u9752\u5c71\u5939\u653b\u7684\u8bdd\uff0c \u65f6\u95f4\u4e00\u4e45\uff0c\u4ed6\u5fc5\u6709\u6027\u547d\u4e4b\u5fe7\uff01 \u3000\u3000\u597d\u5728\u4e54\u5143\u58ee\u4f24\u5f97\u751a\u91cd\uff0c\u6709\u5fc3\u65e0\u529b\u3002\u800c\u5f20\u5143\u5409\u56e0\u4e3a\u5f97\u5bf9\u65b9\u9976\u4e86\u4e24\u6b21\uff0c\u4e5f\u662f\u4e0d\u7981\u6709\u70b9\u96be\u4e3a \u60c5\uff0c\u4e0d\u597d\u610f\u601d\u4e58\u4eba\u4e4b\u5371\u3002 \u3000\u3000\u891a\u9752\u5c71\u4e00\u4e2a\u4eba\u4e0d\u6562\u72ec\u81ea\u8fdb\u653b\uff0c\u8c37\u6db5\u865a\u5706\u7741\u53cc\u773c\uff0c\u559d\u9053\uff1a\u201c\u891a\u9752\u5c71\uff0c\u4f60\u4e0a\u6765\u5427\uff01\u6211\u8c41\u51fa\u4e86 \u8fd9\u6761\u6027\u547d\uff0c\u548c\u4f60\u62fc\u4e86\uff01\u201d \u3000\u3000\u891a\u9752\u5c71\u56de\u8fc7\u5934\u6765\uff0c\u76ef\u7740\u4e54\u3001\u5f20\u4e8c\u4eba\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4f4d\u600e\u4e48\u5566\uff1f\u8fd9\u5c0f\u5b50\u672c\u6765\u662f\u4f60\u4eec\u7684 \u5927\u4ec7\u4eba\uff0c\u4e0e\u6211\u65e0\u5173\u7684\u3002\u4f60\u4eec\u82e5\u662f\u5fcd\u5f97\u4e0b\u5931\u59bb\u593a\u5251\u4e4b\u8fb1\uff0c\u6211\u59d3\u891a\u7684\u62d4\u817f\u5c31\u8d70\uff0c\u7701\u5f97\u81ea\u8ba8\u6ca1 \u8da3\uff01\u201d \u3000\u3000\u4e54\u5143\u58ee\u8840\u6cfb\u7684\u8138\u4e0a\u4e00\u7247\u94c1\u9752\uff0c\u7529\u5f00\u4e86\u5f20\u5143\u5409\u6276\u4ed6\u7684\u624b\uff0c\u6084\u58f0\u8bf4\u9053\uff1a\u201c\u5e08\u5f1f\uff0c\u6b66\u5f53\u6d3e\u7684\u5f1f \u5b50\u51b3\u4e0d\u80fd\u8ba9\u4eba\u770b\u5c0f\uff0c\u4f60\u4e0d\u4e0a\u53bb\uff0c\u552f\u6709\u6211\u4e0a\u53bb\uff01\u201d\u4ed6\u53d7\u4f24\u751a\u91cd\uff0c\u811a\u6b65\u4e00\u8fc8\uff0c\u6447\u6447\u6b32\u5760\u3002 \u3000\u3000\u5f20\u5143\u5409\u54ac\u7259\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u6211\u4e0e\u8fd9\u5c0f\u5b50\u62fc\u4e86\u8fd9\u6761\u6027\u547d\u4fbf\u662f\uff01\u201d\u628a\u5e08\u5144\u66ff\u4e0b\uff0c\u6454\u5251\u518d\u4e0a\uff0c\u4f46 \u5fc3\u4e2d\u5374\u662f\u4e00\u7247\u832b\u7136\u3002 \u3000\u3000\u8c37\u6db5\u865a\u53d7\u4e86\u6bd2\u4f24\uff0c\u60c5\u77e5\u518d\u6218\u4e0b\u53bb\uff0c\u5fc5\u5b9a\u51f6\u591a\u5409\u5c11\uff0c\u628a\u5fc3\u4e00\u6a2a\uff0c\u60f3\u9053\uff1a\u201c\u4f60\u4eec\u5982\u6b64\u82e6\u82e6\u76f8 \u903c\uff0c\u6211\u4e5f\u552f\u6709\u5927\u5f00\u6740\u6212\u4e86\uff01\u201d\u5f53\u4e0b\u5438\u4e00\u53e3\u6c14\uff0c\u9ed8\u8fd0\u7384\u529f\uff0c\u62a4\u4f4f\u5fc3\u623f\uff0c\u6a2a\u638c\u5f53\u524d\uff0c\u51c6\u5907\u51b3\u4e00\u6b7b \u6218\u3002 \u3000\u3000\u773c\u770b\u4e00\u573a\u4f60\u6b7b\u6211\u6d3b\u7684\u6076\u6218\u53c8\u8981\u5c55\u5f00\uff0c\u5ffd\u542c\u5f97\u4e00\u4e2a\u82cd\u8001\u7684\u58f0\u97f3\u559d\u9053\uff1a\u201c\u8c01\u4eba\u8fd9\u6837\u5927\u80c6\uff0c\u6562 \u5728\u6211\u7684\u5bb6\u95e8\u95f9\u4e8b\uff0c\u90fd\u7ed9\u6211\u4f4f\u624b\uff01\u201d \u3000\u3000\u58f0\u97f3\u8fdc\u8fdc\u4f20\u6765\uff0c\u4eba\u5f71\u5374\u5c1a\u672a\u89c1\u3002\u663e\u7136\u662f\u8bf4\u8bdd\u8fd9\u4eba\u7528\u4e86\u201c\u4f20\u97f3\u5165\u5bc6\u201d\u7684\u5185\u529f\u3002\u9644\u8fd1\u4eba\u5bb6\uff0c \u8c01\u4eba\u80fd\u6709\u8fd9\u6837\u6df1\u539a\u7684\u5185\u529f\uff1f\u662f\u4ee5\u5927\u5bb6\u867d\u7136\u672a\u89c1\u5230\u4eba\uff0c\u4e5f\u90fd\u77e5\u9053\u662f\u4e25\u70f7\u4e4b\u7236\u3001\u5ddd\u897f\u5927\u4fa0\u4e25\u58f0\u6d9b \u5230\u4e86\u3002 \u3000\u3000\u679c\u7136\u8bdd\u672a\u72b9\u4e86\uff0c\u4e25\u58f0\u6d9b\u5df2\u7ecf\u73b0\u51fa\u8eab\u5f62\uff0c\u800c\u4e14\u4e0d\u4ec5\u662f\u4ed6\u4e00\u4eba\uff0c\u662f\u4ed6\u4eec\u592b\u5987\u4e00\u540c\u6765\u4e86\uff01\u5f20\u5143 \u5409\u505c\u4e0b\u811a\u6b65\uff0c\u53eb\u9053\uff1a\u201c\u5cb3\u7236\u5927\u4eba\uff0c\u4f60\u6765\u5f97\u6b63\u597d\uff01\u201d \u3000\u3000\u4e25\u58f0\u6d9b\u770b\u89c1\u8c37\u6db5\u865a\u548c\u81ea\u5df1\u7684\u5973\u513f\u90fd\u5728\u573a\u4e2d\uff0c\u53c8\u542c\u5f97\u5f20\u5143\u5409\u5982\u6b64\u8bf4\u8bdd\uff0c\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c \u6da9\u58f0\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d\u58f0\u97f3\u90fd\u98a4\u6296\u4e86\u3002 \u3000\u3000\u4e25\u592b\u4eba\u6c14\u5f97\u624b\u8db3\u51b0\u51b7\uff0c\u98a4\u58f0\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u6b7b\u4e2b\u5934\u6c14\u6b7b\u6211\u4e86\uff01\u660e\u971e\uff0c\u60f3\u4e0d\u5230\u4f60\u4e5f\u548c\u4f60\u8868\u59d0 \u4e32\u901a\u7792\u6211\uff01\u4f60\u4eec\u8fd8\u4e0d\u7ed9\u6211\u4f4f\u624b\uff01\u201d\u6b64\u65f6\u5b63\u3001\u6881\u4e8c\u4eba\u56e0\u4e3a\u8981\u593a\u56de\u4f69\u5251\uff0c\u5140\u662f\u672a\u80af\u7f62\u4f11\u3002 \u3000\u3000\u5b5f\u660e\u971e\u811a\u5c16\u4e00\u6311\uff0c\u628a\u5730\u4e0a\u7684\u4e00\u67c4\u957f\u5251\u6311\u8d77\u6765\uff0c\u7b14\u76f4\u7684\u5411\u6881\u5143\u732e\u98de\u53bb\uff0c\u968f\u5373\u628a\u624b\u4e2d\u7684\u90a3\u628a \u957f\u5251\u5012\u6301\u5251\u67c4\uff0c\u53cd\u624b\u4e00\u9001\uff0c\u9001\u5230\u5b63\u5143\u4f26\u9762\u524d\uff0c\u8bf4\u9053\uff1a\u201c\u6536\u56de\u4f60\u7684\u5251\u5427\uff01\u201d\u5b63\u5143\u4f26\u4e0d\u77e5\u6240\u63aa\uff0c \u672c\u80fd\u7684\u5c06\u5251\u63a5\u4e86\u4e0b\u6765\uff0c\u63a5\u4e86\u4e0b\u6765\uff0c\u65b9\u624d\u84e6\u5730\u7701\u89c9\u8fd9\u662f\u5927\u5931\u4f53\u9762\u4e4b\u4e8b\uff0c\u6068\u6068\u8bf4\u9053\uff1a\u201c\u8fd9\u7b14\u5e10\u6211 \u8bb0\u4e0b\u4e86\u3002\u4eca\u65e5\u770b\u5728\u4e25\u5927\u4fa0\u592b\u5987\u7684\u4efd\u4e0a\uff0c\u6682\u4e14\u4e0d\u548c\u4f60\u7b97\u3002\u201d\u6b64\u65f6\u6881\u5143\u732e\u4ea6\u5df2\u628a\u5251\u63a5\u5230\u624b\u4e2d\uff0c\u624b \u6309\u5251\u67c4\uff0c\u5411\u5b5f\u660e\u971e\u6012\u76ee\u800c\u89c6\u3002 \u3000\u3000\u4e25\u592b\u4eba\u662f\u77e5\u9053\u6b66\u5f53\u6d3e\u7684\u89c4\u77e9\uff0c\u5f53\u4e0b\u66f4\u662f\u6c14\u4e0a\u52a0\u60ca\uff0c\u987f\u8db3\u8bf4\u9053\uff1a\u201c\u4f60\u3001\u4f60\u8fd9\u4e24\u4e2a\u4e2b\u5934\u7ed9\u6211 \u60f9\u4e0b\u8fd9\u573a\u5927\u7978\uff0c\u662f\u4e0d\u662f\u8981\u628a\u6211\u6c14\u6b7b\u4e86\u624d\u75db\u5feb\uff1f\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u8fd9\u4e0d\u5173\u8868\u59d0\u7684\u4e8b\uff0c\u4ed6\u4eec\u7684\u5251\u662f\u6211\u593a\u7684\uff01\u8c01\u53eb\u4ed6\u4eec\u6b3a\u8d1f\u8868\u59d0\uff1f\u59d1\u59d1\uff0c\u4f60\u522b\u62c5 \u5fc3\uff0c\u6709\u751a\u5927\u7978\uff0c\u7531\u6211\u62c5\u5f53\u5c31\u662f\u3002\u4f60\u4eec\u542c\u7740\uff0c\u6211\u53eb\u5b5f\u660e\u971e\uff0c\u6211\u7239\u7239\u662f\u5b5f\u5c11\u521a\uff0c\u4f60\u4eec\u8981\u548c\u6211\u7b97 \u5e10\uff0c\u5c3d\u53ef\u5230\u82cf\u5dde\u627e\u6211\u3002\u201d \u3000\u3000\u4e25\u592b\u4eba\u7ed9\u5979\u5f04\u5f97\u557c\u7b11\u7686\u975e\uff0c\u4f46\u6b64\u9645\u6700\u4ee4\u5979\u62c5\u60ca\u3001\u751f\u6c14\u7684\u8fd8\u662f\u5979\u5973\u513f\u7684\u201c\u4e11\u4e8b\u201d\uff0c\u662f\u4ee5\u5979 \u5c31\u53ea\u597d\u6682\u4e14\u4e0d\u7406\u4f1a\u5b5f\u660e\u971e\u4e86\u3002 \u3000\u3000\u5f20\u5143\u5409\u5f85\u53cc\u65b9\u90fd\u5df2\u505c\u624b\u4e4b\u540e\uff0c\u65b9\u59cb\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u5cb3\u7236\u5927\u4eba\uff0c\u8fd9\u662f\u4ec0\u4e48\u4e00\u56de\u4e8b\u60c5\uff0c\u4f60\u5df2\u7ecf \u4eb2\u773c\u770b\u5230\u4e86\u3002\u5c0f\u5a7f\u6050\u6015\u8bf4\u51fa\u6765\u4f1a\u6c61\u4e86\u53e3\uff0c\u4f60\u6b32\u77e5\u5176\u8be6\uff0c\u8fd8\u662f\u8bf7\u95ee\u4f60\u81ea\u5df1\u7684\u5973\u513f\u5427\uff01\u201d \u3000\u3000\u4e25\u70f7\u9053\uff1a\u201c\u7239\u7239\uff0c\u4f60\u4e5f\u4eb2\u773c\u89c1\u5230\u4e86\uff0c\u90a3\u4eba\u662f\u63d2\u7fc5\u864e\u6bb5\u70b9\u82cd\u7684\u5e08\u5f1f\u98de\u8c79\u5b50\u891a\u9752\u5c71\uff0c\u4ed6\u4f19\u540c \u4e86\u6b66\u5f53\u6d3e\u7684\u5f1f\u5b50\u524d\u6765\uff0c\u5206\u660e\u662f\u6709\u5fc3\u627e\u4f60\u95f9\u4e8b\u7684\uff01\u201d \u3000\u3000\u891a\u9752\u5c71\u795e\u8272\u81ea\u5982\u5730\u5411\u4e25\u58f0\u6d9b\u65bd\u4e86\u4e00\u793c\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u7684\u5e08\u5144\u548c\u4e25\u5927\u4fa0\u662f\u7ed3\u4e00\u70b9\u6881 \u5b50\uff0c\u4f46\u4e0e\u4eca\u665a\u4e4b\u4e8b\u5374\u4e0d\u76f8\u5e72\uff0c\u4e25\u5927\u4fa0\uff0c\u4e0d\u7792\u4f60\u8bf4\uff0c\u6211\u7684\u5e08\u5144\u6298\u5728\u4f60\u7684\u624b\u4e0b\uff0c\u5bf9\u4f60\u8001\u5012\u662f\u4f69\u670d \u5f97\u5f88\uff0c\u4ed6\u4e0d\u670d\u6c14\u7684\u53ea\u662f\u8fd9\u4e2a\u5c0f\u5b50\u4ece\u4e2d\u6df7\u6c34\u6478\u9c7c\uff0c\u628a\u4f60\u8001\u4e5f\u6b3a\u9a97\u4e86\u3002\u563f\uff0c\u563f\uff0c\u8fd9\u8bdd\u6211\u672c\u4e0d\u5f53\u8bf4 \u7684\uff0c\u4f46\u5982\u4eca\u4f60\u8001\u5df2\u7ecf\u4eb2\u773c\u89c1\u5230\u4e86\uff0c\u6211\u4e5f\u4e0d\u59a8\u8bf4\u4e86\u3002\u8fd9\u5c0f\u5b50\u548c\u4ee4\u5a9b\u4e0d\u4f46\u65e9\u5c31\u76f8\u8bc6\uff0c\u800c\u4e14\u7792\u7740\u4f60 \u79c1\u81ea\u5f80\u6765\uff0c\u6211\u4eec\u5e08\u5144\u4f2f\u867d\u7136\u548c\u4f60\u8001\u8fc7\u53bb\u6709\u70b9\u5acc\u9699\uff0c\u4e5f\u66ff\u4f60\u8001\u4e0d\u503c\uff01\u5ddd\u897f\u5927\u4fa0\u4e00\u4e16\u82f1\u540d\uff0c\u5c82\u80fd \u8ba9\u8fd9\u5c0f\u5b50\u73b7\u8fb1\uff1f\u6211\u4eca\u665a\u6765\u6b64\uff0c\u5b9e\u4e0d\u76f8\u7792\uff0c\u5c31\u60f3\u66ff\u4f60\u8001\u6548\u52b3\uff0c\u79c1\u4e0b\u4e86\u7ed3\u6b64\u4e8b\uff0c\u514d\u5f97\u4f20\u626c\u51fa\u53bb\uff0c \u5bf9\u4f60\u8001\u7684\u9762\u5b50\uff0c\u90a3\u3001\u90a3\u5c31\u6050\u6015\u592a\u96be\u582a\u4e86\uff01\u201d \u3000\u3000\u4e25\u58f0\u6d9b\u4e00\u751f\u6700\u7231\u9762\u5b50\uff0c\u5982\u4eca\u4ed6\u7684\u5973\u513f\u88ab\u5973\u5a7f\u5e26\u4e86\u5916\u4eba\u524d\u6765\u201c\u6349\u5978\u201d\uff0c\u7d2f\u5f97\u4ed6\u5f53\u4f17\u51fa\u4e11\uff0c \u5f53\u771f\u662f\u6bd4\u8981\u4e86\u4ed6\u7684\u6027\u547d\u8fd8\u66f4\u96be\u53d7\uff01\u6de1\u6de1\u7684\u6708\u5149\u4e4b\u4e0b\uff0c\u53ea\u89c1\u4ed6\u95f7\u58f0\u4e0d\u54cd\uff0c\u8138\u4e0a\u597d\u50cf\u94fa\u4e86\u4e00\u5c42\u5bd2 \u971c\u4f3c\u7684\uff0c\u51b7\u5f97\u9a87\u4eba\u3002 \u3000\u3000\u4ed6\u867d\u7136\u6ca1\u6709\u7acb\u5373\u53d1\u4f5c\uff0c\u4f46\u8c01\u90fd\u770b\u5f97\u51fa\u6765\uff0c\u8fd9\u662f\u66b4\u98ce\u96e8\u4e4b\u524d\u7684\u5bc2\u9759\uff01\u6ca1\u4eba\u6562\u518d\u8bf4\u8bdd\uff0c\u6c89\u91cd \u7684\u6c14\u6c1b\u538b\u5f97\u4eba\u900f\u4e0d\u8fc7\u6c14\u3002\u8fde\u5f20\u5143\u5409\u5728\u5185\uff0c\u6bcf\u4e00\u9897\u5fc3\u90fd\u5728\u7830\u7830\u8df3\u52a8\uff0c\u4e0d\u77e5\u4e25\u58f0\u6d9b\u8981\u5982\u4f55\u6765\u5904\u7f6e \u4ed6\u7684\u5973\u513f\uff1f \u3000\u3000\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u5ffd\u542c\u5f97\u4e25\u58f0\u6d9b\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u8fc7\u6765\uff01\u201d\u53cc\u773c\u706b\u7ea2\uff0c\u6307\u7740\u4e25\u70f7\u3002\u90a3\u795e\u6c14 \u5c31\u597d\u50cf\u53ef\u4ee5\u628a\u5973\u513f\u4e00\u53e3\u5403\u6389\u4f3c\u7684\uff01 \u3000\u3000\u4e25\u70f7\u9f13\u8d77\u52c7\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u7239\uff0c\u5973\u513f\u4e0d\u5b5d\uff0c\u4f46\u8bf7\u7239\u7239\u542c\u6211\u2014\u2014\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u4e25\u58f0\u6d9b\u84e6\u5730\u559d \u9053\uff1a\u201c\u4f4f\u5634\uff01\u4f60\u8fd9\u8d31\u4eba\u505a\u5f97\u597d\u4e8b\uff0c\u4f60\u8fd8\u6709\u8138\u53eb\u6211\u7239\u7239\u5417\uff1f\u201d\u4e00\u8fc8\u6b65\uff0c\u5230\u4e86\u4e25\u70f7\u9762\u524d\uff0c\u547c\u7684\u4e00 \u638c\u5c31\u5288\u4e0b\u53bb\uff01 \u3000\u3000\u4e25\u70f7\u60c5\u77e5\u4e0d\u5999\uff0c\u4f46\u5374\u60f3\u4e0d\u5230\u7236\u4eb2\u7adf\u662f\u4e0d\u7531\u5206\u8bf4\uff0c\u5c31\u8981\u53d6\u5979\u6027\u547d\uff01\u8fd9\u5239\u90a3\u95f4\uff0c\u4e25\u70f7\u5413\u5f97\u5446 \u4e86\uff0c\u7adf\u7136\u4e0d\u77e5\u9003\u547d\uff01 \u3000\u3000\u5e78\u800c\u5b5f\u660e\u971e\u65e9\u6709\u9632\u5907\uff0c\u5c31\u5728\u8fd9\u5343\u94a7\u4e00\u53d1\u4e4b\u9645\uff0c\u95ea\u7535\u822c\u5730\u6251\u4e0a\u53bb\uff0c\u4e00\u638c\u63a8\u5f00\uff0c\u5b5f\u660e\u971e\u9053\uff1a \u201c\u8868\u59d0\u5feb\u9003\uff01\u201d \u3000\u3000\u4e25\u58f0\u6d9b\u89c1\u5b5f\u660e\u971e\u62e6\u5728\u9762\u524d\uff0c\u53ea\u597d\u628a\u638c\u6536\u56de\u3002 \u3000\u3000\u4e25\u70f7\u7ed9\u5b5f\u660e\u971e\u7528\u7684\u90a3\u80a1\u5de7\u52b2\u8f7b\u8f7b\u4e00\u63a8\uff0c\u5728\u6b7b\u4ea1\u7684\u8fb9\u7f18\u4e0a\u9003\u51fa\u751f\u5929\uff0c\u8fd9\u624d\u5982\u68a6\u521d\u9192\uff0c\u7701\u609f \u81ea\u5df1\u662f\u4e0d\u80fd\u5bb9\u4e8e\u7236\u6bcd\uff0c\u5fc5\u987b\u5728\u4e08\u592b\u548c\u7236\u6bcd\u4e4b\u95f4\u4f5c\u4e00\u6289\u62e9\u4e86\u3002 \u3000\u3000\u8fd9\u662f\u4e00\u4e2a\u5173\u7cfb\u7ec8\u751f\u7684\u6289\u62e9\uff0c\u8981\u4f5c\u8fd9\u6837\u7684\u6289\u62e9\u6781\u662f\u4e3a\u96be\uff01\u201c\u5018\u82e5\u4e8b\u60c5\u6ca1\u6709\u5f53\u4f17\u95f9\u5f00\uff0c\u6211\u6084 \u6084\u51fa\u8d70\uff0c\u6216\u8005\u5c06\u6765\u8fd8\u53ef\u4ee5\u6c42\u5f97\u7239\u5a18\u539f\u8c05\u3002\u5982\u4eca\u5728\u8fd9\u6837\u7684\u60c5\u5f62\u4e4b\u4e0b\u51b3\u88c2\uff0c\u7239\u7239\u975e\u628a\u6211\u7f6e\u4e4b\u6b7b\u5730 \u4e0d\u53ef\uff1b\u6211\u8fd8\u7109\u80fd\u5f97\u6c42\u4ed6\u7684\u5bbd\u6055\u5462\uff1f\u7239\u5a18\u53ea\u6709\u6211\u8fd9\u4e00\u4e2a\u5973\u513f\uff0c\u96be\u9053\u6211\u5c31\u5fcd\u5fc3\u4eca\u751f\u4e0d\u518d\u89c1\u4ed6\u4eec\uff0c \u8ba9\u4ed6\u4eec\u4f24\u5fc3\u7ec8\u8001\uff1f\u201d\u4f46\u968f\u5373\u53c8\u60f3\uff1a\u201c\u6211\u662f\u51b3\u4e0d\u80fd\u518d\u5ac1\u7ed9\u5f20\u5143\u5409\u7684\u4e86\uff0c\u7239\u7239\u521a\u624d\u6c14\u5f97\u8981\u60f3\u6740 \u6211\uff0c\u6211\u8fd8\u6709\u8138\u505a\u4ed6\u7684\u5973\u513f\u5417\uff1f\u7239\u5a18\u90fd\u662f\u6700\u8981\u9762\u5b50\u7684\u4eba\uff0c\u6211\u7559\u5728\u5bb6\u4e2d\uff0c\u4e5f\u53ea\u662f\u5bf9\u4ed6\u4eec\u7684\u803b\u8fb1\u800c \u5df2\u3002\u201d \u3000\u3000\u8fd9\u5239\u90a3\u95f4\uff0c\u4e25\u70f7\u53cd\u590d\u5730\u8f6c\u4e86\u51e0\u6b21\u5ff5\u5934\uff0c\u7ec8\u4e8e\u54ac\u4e00\u54ac\u7259\uff0c\u4e0b\u4e86\u51b3\u5fc3\uff0c\u9f13\u8d77\u52c7\u6c14\uff0c\u5411\u8c37\u6db5\u865a \u8dd1\u53bb\u3002 \u3000\u3000\u8c37\u6db5\u865a\u53c8\u559c\u53c8\u60ca\uff0c\u5f20\u5f00\u53cc\u81c2\uff0c\u8fce\u63a5\u4e25\u70f7\u3002\u4e25\u70f7\u7d27\u7d27\u5730\u6293\u4f4f\u4ed6\u7684\u53cc\u624b\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u8c37\u5927 \u54e5\uff0c\u5982\u4eca\u6211\u552f\u6709\u4f9d\u9760\u4f60\u4e86\uff0c\u4f60\u3001\u4f60\u5e26\u6211\u8d70\u5427\uff01\u201d \u3000\u3000\u5b5f\u660e\u971e\u770b\u89c1\u8868\u59d0\u5df2\u7ecf\u8dd1\u5230\u8c37\u6db5\u865a\u8eab\u8fb9\uff0c\u8fd9\u624d\u7a0d\u7a0d\u653e\u5fc3\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8c37\u6db5\u865a\u5bf9\u59d1\u7236\u66fe\u6709 \u8fc7\u6551\u547d\u4e4b\u6069\uff0c\u59d1\u7236\u603b\u4e0d\u80fd\u592a\u8fc7\u7edd\u60c5\uff0c\u5bf9\u4ed6\u4e5f\u4e0b\u6740\u624b\u5427\uff1f\u53ea\u8981\u59d1\u7236\u4e0d\u4e0b\u6740\u624b\uff0c\u4ee5\u8c37\u6db5\u865a\u7684\u6b66 \u529f\uff0c\u548c\u8868\u59d0\u8054\u624b\uff0c\u8fd9\u4e9b\u4eba\u8c05\u4e5f\u963b\u6b62\u4e0d\u4e86\u4ed6\u4eec\u3002\u201d\u5b5f\u660e\u971e\u8fd9\u6b21\u633a\u8eab\u800c\u51fa\uff0c\u63a9\u62a4\u8868\u59d0\uff0c\u5176\u5b9e\u4e5f\u662f \u6781\u4e3a\u5371\u9669\uff0c\u4e8b\u5148\u672a\u7ecf\u8003\u8651\u7684\u3002\u5047\u5982\u4e0d\u662f\u4e25\u58f0\u6d9b\u7684\u7ef5\u638c\u529f\u592b\u5df2\u5230\u6536\u53d1\u968f\u5fc3\u4e4b\u5883\uff0c\u90a3\u4e00\u638c\u65e9\u5df2\u628a \u5979\u51fb\u6bd9\u4e86\u3002 \u3000\u3000\u5b5f\u660e\u9707\u60ca\u9b42\u672a\u5b9a\uff0c\u8bf4\u9053\uff1a\u201c\u59d1\u4e08\u8bf7\u606f\u96f7\u9706\u4e4b\u6012\uff0c\u6709\u4e8b\u6162\u6162\u5546\u91cf\u3002\u201d \u3000\u3000\u5c31\u5728\u6b64\u65f6\uff0c\u5f20\u5143\u53ec\u7684\u5927\u5e08\u5144\u4e54\u5143\u58ee\u6d51\u8eab\u8840\u6c61\u8d70\u4e86\u51fa\u6765\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u5f20\u5e08\u5f1f\u7684\u7236\u6bcd\u5e08\u957f \u90fd\u4e0d\u5728\u8fd9\u513f\uff0c\u6211\u53ea\u597d\u8d8a\u8d84\u4ee3\u5e96\uff0c\u66ff\u4ed6\u4f5c\u4e3b\uff0c\u8fd9\u95e8\u4eb2\u4e8b\uff0c\u6211\u4eec\u662f\u4e0d\u6562\u9ad8\u6500\u7684\u4e86\u3002\u8fd9\u5c0f\u5b50\u73b0\u5728\u8981 \u5e26\u4ee4\u5a9b\u8fdc\u8d70\u9ad8\u98de\uff0c\u5141\u4e0d\u5141\u8bb8\u4ed6\u4eec\uff0c\u8fd9\u5c31\u662f\u4f60\u4e25\u5927\u4fa0\u7684\u4e8b\u4e86\uff01\u201d\u891a\u9752\u5c71\u8ddf\u7740\u51b7\u7b11\u9053\uff1a\u201c\u8fd9\u59d3\u8c37 \u7684\u5c0f\u5b50\u4e5f\u4e0d\u9519\u5440\uff0c\u4e25\u5927\u4fa0\uff0c\u606d\u559c\u4f60\uff0c\u53c8\u5f97\u4f73\u5a7f\u4e86\uff01\u201d \u3000\u3000\u4e25\u592b\u4eba\u53c8\u7f9e\u53c8\u6c14\uff0c\u8fc1\u6012\u5230\u5b5f\u660e\u971e\u8eab\u4e0a\uff0c\u4e00\u628a\u5c06\u5979\u62c9\u5f00\uff0c\u65a5\u9053\uff1a\u201c\u8fd9\u91cc\u8fd8\u8f6e\u4e0d\u5230\u4f60\u8bf4\u8bdd\uff0c \u4e0d\u8bb8\u4f60\u591a\u4e8b\uff01\u201d\u5b5f\u660e\u971e\u7ed9\u5979\u7a81\u7136\u62c9\u8fc7\u4e00\u8fb9\uff0c\u51e0\u4e4e\u8dcc\u5012\u3002 \u3000\u3000\u4e25\u58f0\u6d9b\u9762\u8272\u94c1\u9752\uff0c\u9661\u5730\u559d\u9053\uff1a\u201c\u8c37\u6db5\u865a\uff0c\u653e\u5f00\u6211\u7684\u5973\u513f\uff01\u201d \u3000\u3000\u4e25\u70f7\u773c\u4e2d\u6ee1\u662f\u6cea\u6c34\uff0c\u8bf4\u9053\uff1a\u201c\u7239\u7239\u4f60\u53ea\u5f53\u6211\u5df2\u7ecf\u6b7b\u4e86\u5427\u3002\u8bf7\u6055\u5973\u513f\u4e0d\u5b5d\uff0c\u5973\u513f\u51b3\u610f\u8ddf\u4ed6 \u8d70\u4e86\uff01\u201d\u4e25\u592b\u4eba\u5927\u6012\u9053\uff1a\u201c\u8d31\u4e2b\u5934\uff0c\u4f60\u8fd8\u8981\u8138\u4e0d\u8981\uff1f\u6211\u51b3\u4e0d\u8bb8\u4f60\u8ddf\u4ed6\u8d70\uff0c\u9664\u975e\u662f\u6211\u6b7b\u4e86\uff01\u201d \u3000\u3000\u4e25\u58f0\u6d9b\u5374\u4e0d\u7406\u4f1a\u5973\u513f\uff0c\u5f84\u81ea\u5bf9\u8c37\u6db5\u865a\u8bf4\u9053\uff1a\u201c\u8c37\u5c11\u4fa0\uff0c\u4f60\u4e8e\u6211\u66fe\u6709\u62d4\u5200\u76f8\u52a9\u4e4b\u6069\uff0c\u4e25\u67d0 \u51b3\u4e0d\u4f1a\u5fd8\u8bb0\u3002\u4f46\u662f\u4f60\u8981\u628a\u6211\u5973\u513f\u5e26\u8d70\uff0c\u4ee4\u6211\u5bb6\u95e8\u53d7\u8fb1\uff0c\u90a3\u5374\u662f\u4e07\u4e07\u4e0d\u80fd\uff01 \u3000\u3000\u201c\u4e25\u67d0\u4e00\u751f\u6069\u6028\u5206\u660e\uff0c\u4f60\u5982\u679c\u4e00\u5b9a\u8981\u8fd9\u6837\u505a\u7684\u8bdd\uff0c\u6211\u5c31\u53ea\u597d\u6709\u6069\u62a5\u6069\uff0c\u6709\u6028\u62a5\u6028\uff0c\u548c\u4f60 \u5728\u6b64\u2018\u4e86\u65ad\u2019\u4e86\uff01\u201d \u3000\u3000\u201c\u4e86\u65ad\u201d\u4e8c\u5b57\u7684\u610f\u601d\uff0c\u4e43\u662f\u6bd4\u201c\u51b3\u6597\u201d\u8fd8\u66f4\u4e25\u91cd\u7684\u6c5f\u6e56\u672f\u8bed\uff0c\u6b66\u6797\u4e2d\u4eba\u51b3\u4e0d\u4f1a\u8f7b\u6613\u8bf4\u51fa \u8fd9\u4e24\u4e2a\u5b57\u6765\u3002\u5b5f\u660e\u971e\u542c\u4e86\uff0c\u4e0d\u7981\u5927\u5403\u4e00\u60ca\u3002\u201c\u60f3\u4e0d\u5230\u59d1\u7236\u771f\u7684\u5be1\u60c5\u7edd\u4e49\uff0c\u4e00\u81f3\u4e8e\u65af\uff01\u201d\u6b66\u5f53 \u6d3e\u5f20\u5143\u5409\u7684\u51e0\u4e2a\u5e08\u5144\u5f1f\u542c\u4e86\uff0c\u5374\u662f\u6697\u6697\u6b22\u559c\u3002\u891a\u9752\u5c71\u5219\u662f\u559c\u6012\u53c2\u534a\uff0c\u56e0\u4e3a\u4e25\u58f0\u6d9b\u627f\u8ba4\u66fe\u53d7\u8fc7 \u8c37\u6db5\u865a\u7684\u201c\u62d4\u5200\u76f8\u52a9\u4e4b\u6069\u201d\uff0c\u90a3\u5373\u662f\u8bf4\uff0c\u4ed6\u4ecd\u7136\u662f\u628a\u891a\u9752\u5c71\u7684\u5e08\u5144\u5f53\u4f5c\u4ec7\u4eba\uff0c\u62d2\u7edd\u4e86\u891a\u9752\u5c71 \u66ff\u4ed6\u5e08\u5144\u8c03\u89e3\u7684\u8981\u6c42\u4e86\u3002 \u3000\u3000\u4e25\u58f0\u6d9b\u4e24\u8ddf\u4e00\u77aa\uff0c\u63a5\u4e0b\u53bb\u8bf4\u9053\uff1a\u201c\u8c37\u5c11\u4fa0\u6b66\u529f\u9ad8\u660e\uff0c\u4e25\u67d0\u6b7b\u5728\u4f60\u7684\u624b\u91cc\uff0c\u6ca1\u8bdd\u53ef\u8bf4\u3002\u4e07 \u4e00\u4fa5\u5e78\u4e0d\u6b7b\uff0c\u5931\u624b\u4f24\u4e86\u4f60\u7684\u6027\u547d\u7684\u8bdd\uff0c\u4e25\u67d0\u4e5f\u5f53\u81ea\u520e\uff0c\u4ee5\u62a5\u4f60\u7684\u5927\u6069\uff01\u201d \u3000\u3000\u4f17\u4eba\u65b9\u624d\u660e\u767d\u4e25\u58f0\u6d9b\u6240\u8bf4\u7684\u201c\u6709\u6069\u62a5\u6069\uff0c\u6709\u6028\u62a5\u6028\u201d\u4e43\u662f\u8fd9\u4e2a\u610f\u601d\uff0c\u65e0\u4e0d\u9a87\u7136\uff0c\u6740\u4eba\u62a5 \u6028\uff0c\u81ea\u6740\u62a5\u6069\uff0c\u8fd9\u8bdd\u5f53\u771f\u662f\u8bf4\u5230\u51b3\u7edd\u4e4b\u6781\u4e86\uff01 \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u665a\u8f88\u51b3\u4e0d\u6562\u4e0e\u4e25\u5927\u4fa0\u52a8\u624b\u3002\u201d \u3000\u3000\u4e25\u58f0\u6d9b\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4f60\u5c31\u72ec\u81ea\u79bb\u5f00\uff0c\u5341\u5e74\u4e4b\u5185\uff0c\u4e0d\u8bb8\u8e0f\u8fdb\u5ddd\u897f\u4e00\u6b65\uff01\u4f60\u82e5\u6709\u4e8b\u9700\u6211\u76f8 \u52a9\uff0c\u6258\u4eba\u634e\u4e2a\u4fe1\u6765\uff0c\u4e25\u67d0\u5b9a\u5f53\u62ab\u661f\u6234\u6708\uff0c\u8d76\u5230\u4f60\u6240\u6307\u5b9a\u7684\u6240\u5728\uff01\u201d \u3000\u3000\u9010\u5ba2\u4ee4\u5df2\u4e0b\uff0c\u8c37\u6db5\u865a\u82e5\u7136\u4e0d\u8d70\u7684\u8bdd\uff0c\u90a3\u5c31\u975e\u548c\u4e25\u58f0\u6d9b\u52a8\u624b\u4e0d\u53ef\u4e86\u3002\u4e00\u548c\u4e25\u58f0\u6d9b\u52a8\u624b\uff0c\u4e25 \u58f0\u6d9b\u662f\u4e0d\u8bba\u80dc\u8d25\u90fd\u8981\u6b7b\u7684\uff0c\u8c37\u6db5\u865a\u53c8\u5c82\u80fd\u5e26\u8d70\u5973\u513f\uff0c\u903c\u6b7b\u7236\u4eb2\u3002 \u3000\u3000\u8fd9\u5239\u90a3\u95f4\u8c37\u6db5\u865a\u809d\u80a0\u5bf8\u65ad\uff0c\u8f6c\u4e86\u597d\u51e0\u4e2a\u5ff5\u5934\uff0c\u7ec8\u4e8e\u795e\u8272\u60e8\u7136\u5730\u8bf4\u9053\uff1a\u201c\u70f7\u59b9\uff0c\u54b1\u4eec\u6ce8\u5b9a \u4eca\u4e16\u65e0\u7f18\uff0c\u8bf7\u4f60\u4e0d\u590d\u4ee5\u6211\u4e3a\u5ff5\uff01\u201d \u3000\u3000\u4e8b\u60c5\u5982\u6b64\u4e86\u7ed3\uff0c\u5012\u662f\u9887\u51fa\u4f17\u4eba\u610f\u6599\u4e4b\u5916\u3002\u5f20\u5143\u5409\u5992\u6068\u4ea4\u534a\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5931\u8d1e\u4e4b\u5987\u6211\u5f53 \u7136\u662f\u4e0d\u80fd\u518d\u8981\u4e86\uff0c\u4f46\u8fd9\u5c0f\u5b50\u548c\u6211\u4e00\u6837\u5f97\u4e0d\u5230\u624b\uff0c\u6211\u8fd9\u4e00\u53e3\u6c14\u4e5f\u53ef\u4ee5\u51fa\u4e86\u3002\u201d \u3000\u3000\u4f17\u4eba\u53ea\u9053\u8c37\u6db5\u865a\u4e00\u8d70\uff0c\u4e8b\u60c5\u5c31\u53ef\u5982\u6b64\u4e86\u7ed3\uff0c\u4e0d\u6599\u4e25\u70f7\u5ffd\u5730\u53eb\u9053\uff1a\u201c\u8c37\u90ce\u4e14\u6162\uff01\u201d\u8c37\u6db5\u865a \u56de\u8fc7\u5934\u6765\uff0c\u60e8\u7136\u8bf4\u9053\uff1a\u201c\u4e8b\u5df2\u5982\u65af\uff0c\u54b1\u4eec\u53ea\u6709\u5206\u624b\uff0c\u8fd8\u6709\u4ec0\u4e48\u53ef\u8bf4\uff1f\u201d \u3000\u3000\u4e25\u70f7\u9053\uff1a\u201c\u7978\u56e0\u7531\u6211\u800c\u8d77\uff0c\u53ea\u597d\u7531\u6211\u81ea\u884c\u4e86\u65ad\uff0c\u4ee5\u606f\u7eb7\u4e89\u3002\u7239\u7239\uff0c\u8bf7\u4f60\u4e0d\u8981\u602a\u8d23\u8c37\u90ce\uff0c \u6211\u53bb\u4e4b\u540e\uff0c\u4f60\u5c31\u628a\u4ed6\u5f53\u4f5c\u4f60\u7684\u513f\u5b50\u5427\uff01\u201d\u4e25\u58f0\u6d9b\u5927\u60ca\u9053\uff1a\u201c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u53ea\u89c1\u4e25 \u70f7\u5df2\u662f\u7a81\u7136\u62d4\u51fa\u5251\u6765\uff0c\u5411\u7740\u81ea\u5df1\u7684\u80f8\u53e3\u63d2\u4e0b\u3002\u4e0e\u6b64\u540c\u65f6\uff0c\u8c37\u6db5\u865a\u4e5f\u662f\u4e00\u58f0\u60e8\u53eb\uff01 \u3000\u3000\u6de1\u6de1\u7684\u6708\u5149\u4e4b\u4e0b\uff0c\u53ea\u89c1\u8c37\u6db5\u865a\u8840\u6d41\u6ee1\u9762\uff0c\u8863\u88f3\u4e0a\u70b9\u70b9\u9c9c\u7ea2\uff0c\u672c\u6765\u662f\u7f8a\u8102\u767d\u7389\u822c\u7684\u8138\u4e0a\uff0c \u6a2a\u4e00\u9053\u76f4\u4e00\u9053\u7684\u4ea4\u53c9\u7740\u201c\u5341\u201d\u5b57\u5f62\u7684\u8840\u75d5\uff01\u7d27\u63a5\u7740\u53ea\u542c\u5f97\u201c\u94db\u201d\u7684\u4e00\u58f0\uff0c\u4e25\u70f7\u7684\u5251\u8131\u624b\u5760 \u5730\u3002 \u3000\u3000\u539f\u6765\u8c37\u6db5\u865a\u662f\u7ed9\u5f20\u5143\u5409\u523a\u4f24\u7684\uff0c\u4e25\u70f7\u624b\u4e2d\u7684\u5251\uff0c\u5219\u662f\u7ed9\u5979\u6bcd\u4eb2\u6253\u843d\u3002 \u3000\u3000\u5f20\u5143\u5409\u773c\u770b\u7740\u81ea\u5df1\u7684\u672a\u5a5a\u59bb\u6295\u5165\u522b\u4eba\u7684\u6000\u62b1\uff0c\u8a93\u4e0e\u5bf9\u65b9\u5171\u6b7b\u540c\u751f\uff0c\u4e0d\u7981\u5992\u706b\u4e2d\u70e7\uff0c\u518d\u4e5f \u6309\u637a\u4e0d\u4f4f\uff0c\u62d4\u5251\u5c31\u5411\u8c37\u6db5\u865a\u523a\u53bb\u3002\u4ed6\u660e\u77e5\u8c37\u6db5\u865a\u7684\u6b66\u529f\u6bd4\u4ed6\u4e0d\u77e5\u9ad8\u660e\u591a\u5c11\uff0c\u4f46\u5728\u6012\u706b\u4e0a\u5934\uff0c \u4ea6\u5df2\u6839\u672c\u4e0d\u52a0\u8003\u8651\u4e86\u3002 \u3000\u3000\u4e0d\u8fc7\u5f20\u5143\u5409\u6bd5\u7adf\u662f\u540d\u95e8\u6b63\u6d3e\u7684\u5f1f\u5b50\uff0c\u867d\u7136\u5728\u76db\u6012\u4e4b\u4e0b\uff0c\u4e5f\u8fd8\u6ca1\u6709\u5fd8\u8bb0\u5bf9\u65b9\u66fe\u7ecf\u5bf9\u4ed6\u6709\u8fc7 \u4e24\u6b21\u624b\u4e0b\u7559\u60c5\uff0c\u5bf9\u672c\u8eab\u7684\u6027\u547d\u4ed6\u53ef\u4ee5\u4e0d\u52a0\u8003\u8651\uff0c\u4f46\u82e5\u7528\u5077\u88ad\u7684\u624b\u6bb5\u4f24\u4e86\u5bf9\u65b9\u7684\u6027\u547d\uff0c\u4ed6\u5c31\u4e0d \u80fd\u4e0d\u611f\u5230\u5fc3\u4e2d\u6709\u6127\u4e86\u3002\u662f\u4ee5\u4ed6\u8fd9\u4e00\u5251\u53ea\u662f\u523a\u5411\u8c37\u6db5\u865a\u7684\u9762\u95e8\uff0c\u5e76\u975e\u7acb\u5fc3\u4f24\u4ed6\u6027\u547d\uff0c\u539f\u6765\u8c37\u6db5 \u865a\u662f\u4e2a\u98ce\u5ea6\u7fe9\u7fe9\u7684\u7f8e\u5c11\u5e74\uff0c\u5f20\u5143\u5409\u7ad9\u5728\u4ed6\u7684\u9762\u524d\uff0c\u4e0d\u7981\u81ea\u60ed\u5f62\u79fd\uff0c\u51fa\u5251\u4e4b\u9645\uff0c\u5fc3\u4e2d\u53ea\u6709\u4e00\u4e2a \u5ff5\u5934\uff0c\u6211\u62fc\u4e86\u8fd9\u6761\u6027\u547d\uff0c\u4e5f\u8981\u628a\u8fd9\u5c0f\u767d\u8138\u6bc1\u4e86\u3002 \u3000\u3000\u8c37\u6db5\u865a\u60c5\u77e5\u548c\u4e25\u70f7\u7684\u59fb\u7f18\u65e0\u671b\uff0c\u6b64\u9645\u6b63\u662f\u4f24\u5fc3\u4e4b\u6781\uff0c\u54ea\u91cc\u8fd8\u987e\u5f97\u5230\u5f20\u5143\u5409\u5411\u4ed6\u6109\u88ad\u3002\u5f20 \u5143\u5409\u51fa\u624b\u5982\u7535\uff0c\u5251\u6cd5\u53c8\u5feb\u53c8\u51c6\uff0c\u5f85\u5230\u8c37\u6db5\u865a\u84e6\u5730\u89c9\u5f97\u4e00\u7247\u6c81\u51c9\uff0c\u7a81\u7136\u4e00\u9635\u5267\u75db\u4e4b\u65f6\uff0c\u8138\u4e0a\u5df2 \u7ecf\u7ed9\u5f20\u5143\u5409\u5212\u4e86\u4e00\u4e2a\u201c\u5341\u201d\u5b57\u3002 \u3000\u3000\u5f20\u5143\u5409\u5411\u8c37\u6db5\u865a\u5077\u88ad\u4e4b\u9645\uff0c\u4e5f\u6b63\u662f\u4e25\u70f7\u62d4\u5251\u81ea\u6740\u4e4b\u65f6\u3002 \u3000\u3000\u4e25\u70f7\u542c\u5f97\u8c37\u6db5\u865a\u4e00\u58f0\u5c16\u53eb\uff0c\u5927\u5403\u4e00\u60ca\uff0c\u624b\u4e2d\u7684\u77ed\u5251\u582a\u582a\u5c31\u8981\u89e6\u7740\u80f8\u819b\u4e4b\u9645\uff0c\u4e0d\u81ea\u89c9\u5730\u4f4f \u4e0b\u624b\u6765\u3002\u4e25\u592b\u4eba\u8d81\u6b64\u65f6\u673a\uff0c\u53cc\u6307\u4e00\u5f39\uff0c\u4e00\u679a\u6307\u73af\u5411\u5979\u5f39\u53bb\u3002\u5f85\u5230\u4e25\u70f7\u53d1\u89c9\u8c37\u6db5\u865a\u53d7\u4f24\u4e4b\u65f6\uff0c \u5979\u624b\u4e2d\u7684\u77ed\u5251\u4e5f\u6b63\u597d\u7ed9\u90a3\u679a\u6307\u73af\u6253\u843d\u4e86\u3002 \u3000\u3000\u8c37\u6db5\u865a\u63a9\u7740\u9762\u5b54\uff0c\u4e00\u811a\u628a\u5f20\u5143\u5409\u8e22\u7ffb\uff0c\u53ea\u8981\u518d\u52a0\u4e00\u811a\uff0c\u5c31\u53ef\u4ee5\u53d6\u4e86\u5f20\u5143\u5409\u7684\u6027\u547d\uff0c\u5fc3\u5ff5 \u5ffd\u5730\u4e00\u52a8\uff1a\u201c\u53cd\u6b63\u6211\u548c\u70f7\u59b9\u5df2\u662f\u4e0d\u80fd\u7ed3\u6210\u592b\u5987\u4e86\uff0c\u6211\u53c8\u4f55\u5fc5\u6740\u4ed6\u3002\u201d\u5f53\u4e0b\u63a9\u7740\u9762\u5b54\uff0c\u98de\u5954\u800c \u53bb\u3002 \u3000\u3000\u5f20\u5143\u5409\u660e\u77e5\u8c37\u6db5\u865a\u7684\u6b66\u529f\u6bd4\u4ed6\u9ad8\u660e\u5f97\u591a\uff0c\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u5077\u88ad\u7adf\u4f1a\u5982\u6b64\u987a\u5229\uff0c\u672c\u6765\u4ed6\u5c31\u662f \u51c6\u5907\u62fc\u4e86\u4e00\u6761\u6027\u547d\u7684\uff0c\u5982\u4eca\u6027\u547d\u4e5f\u5e76\u6ca1\u5931\u6389\u800c\u53c8\u4f24\u4e86\u4ec7\u4eba\uff0c\u6328\u4e86\u8fd9\u4e00\u811a\u5f53\u7136\u662f\u6781\u4e4b\u503c\u5f97\u4e86\u3002 \u4f46\u8bf4\u4e5f\u5947\u602a\uff0c\u4ed6\u722c\u8d77\u6765\u65f6\uff0c\u5fc3\u4e2d\u5374\u6beb\u65e0\u5feb\u610f\uff0c\u53cd\u800c\u662f\u611f\u5230\u4e00\u7247\u832b\u7136\u3002 \u3000\u3000\u4e25\u70f7\u5446\u4e86\u4e00\u5446\uff0c\u597d\u50cf\u662f\u4ece\u68a6\u4e2d\u9192\u8f6c\u8fc7\u6765\u3002\u5fd9\u5411\u8c37\u6db5\u865a\u8ffd\u53bb\uff0c\u53ef\u662f\u5979\u521a\u521a\u8fc8\u6b65\uff0c\u53ea\u4e0d\u8fc7\u8dd1 \u51fa\u6570\u4e08\u4e4b\u5730\uff0c\u5ffd\u89c9\u624b\u8155\u4e00\u7d27\uff0c\u5979\u7684\u6bcd\u4eb2\u5df2\u662f\u628a\u5979\u62d6\u4e86\u56de\u6765\u3002 \u3000\u3000\u4e14\u8bf4\u5b5f\u660e\u971e\u548c\u891a\u4e91\u5cf0\u5728\u96ea\u5730\u4e0a\u6162\u6162\u5730\u8d70\uff0c\u96ea\u5df2\u6b62\u4e86\uff0c\u5979\u7684\u6545\u4e8b\u8fd8\u6ca1\u8bf4\u5b8c\u3002\u8bf4\u5230\u8fd9\u91cc\uff0c\u5ffd \u7136\u6253\u4e86\u4e00\u4e2a\u5bd2\u5664\uff0c\u6df1\u6df1\u5730\u53f9\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u6050\u6015\u5979\u7684\u4f24\u5c1a\u672a\u75ca\u6108\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u51b7\u4e0d\u51b7\uff1f\u201d\u5b5f\u660e\u971e\u9053\uff1a\u201c\u8eab\u4e0a\u5012\u4e0d\u51b7\u3002\u4f46\u6211\u60f3 \u8d77\u90a3\u5929\u7684\u60c5\u666f\uff0c\u4ecd\u662f\u4e0d\u7981\u89c9\u5f97\u4f3c\u4e4e\u6709\u80a1\u5bd2\u610f\u76f4\u900f\u5fc3\u5934\uff01\u201d \u3000\u3000\u891a\u53bb\u5cf0\u9053\uff1a\u201c\u5bf9\u5566\uff0c\u4f60\u7684\u6545\u4e8b\u8fd8\u6ca1\u6709\u8bf4\u5b8c\u5462\uff0c\u540e\u6765\u600e\u6837\uff1f\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u8fd9\u662f\u4e00\u4e2a\u8fd8\u672a\u77e5\u9053\u7ed3\u5c40\u7684\u6545\u4e8b\u3002\u6211\u77e5\u9053\u7684\u53ea\u662f\u8c37\u6db5\u865a\u8eab\u53d7\u91cd\u4f24\uff0c\u800c\u5fc3\u4e0a\u7684 \u4f24\u53ef\u80fd\u6bd4\u8eab\u4e0a\u7684\u4f24\u66f4\u91cd\uff01\u8868\u59d0\u7ed9\u59d1\u6bcd\u62d6\u4e86\u56de\u5bb6\uff0c\u786c\u751f\u751f\u5730\u628a\u4ed6\u4eec\u4e8c\u4eba\u62c6\u656c\u4e86\uff01\u5509\uff0c\u4f60\u6ca1\u6709\u89c1 \u8fc7\u8c37\u6db5\u865a\uff0c\u4ed6\u672c\u6765\u662f\u4e2a\u5341\u5206\u82f1\u4fca\u7684\u7f8e\u5c11\u5e74\uff0c\u7ed9\u5f20\u5143\u5409\u7684\u5229\u5251\u5728\u8138\u4e0a\u5212\u8fc7\uff0c\u4f24\u75d5\u4ea4\u9519\uff0c\u90a3\u4e2a\u6837 \u5b50\uff0c\u90a3\u4e2a\u6837\u5b50\uff0c\u6211\u3001\u6211\u90fd\u4e0d\u5fcd\u5fc3\u518d\u8bf4\u4e0b\u53bb\u4e86\uff01\u201d\u5b5f\u660e\u971e\u95ed\u4e0a\u773c\u775b\uff0c\u5c31\u597d\u4f3c\u770b\u89c1\u8c37\u6db5\u865a\u90a3\u5f20\u53ef \u6016\u7684\u8138\u5b54\uff01\u4e0d\u7531\u81ea\u5df1\u5730\u53c8\u6253\u4e86\u4e2a\u5bd2\u5664\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u5fc3\u91cc\u4e5f\u662f\u5341\u5206\u66ff\u8c37\u6db5\u865a\u96be\u8fc7\uff0c\u8fc7\u4e86\u534a\u664c\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u5c31\u8bf4\u8bf4\u4f60\u7684\u8868\u59d0\u5427\u3002\u4ee5\u4f60\u8868 \u59d0\u7684\u6027\u683c\uff0c\u5979\u4e00\u5b9a\u662f\u4e0d\u4f1a\u5ac1\u7ed9\u5f20\u5143\u5409\u7684\u4e86\uff0c\u662f\u5427\uff1f\u5979\u540e\u6765\u600e\u6837\uff1f\u96be\u9053\u5979\u5c31\u7518\u5fc3\u7ed9\u5173\u5728\u5bb6\u91cc\u4e00 \u8f88\u5b50\u5417\uff1f\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u5f53\u7136\u5979\u662f\u4e0d\u4f1a\u5ac1\u7ed9\u5f20\u5143\u5409\u7684\u4e86\u3002\u5f20\u5143\u5409\u5df2\u7ecf\u7531\u4ed6\u7684\u5927\u5e08\u5144\u4f5c\u4e3b\uff0c\u5411\u6211\u7684\u59d1 \u7236\u8bb2\u660e\u4e86\u9000\u5a5a\uff0c\u59d1\u59d1\u62d6\u4e86\u8868\u59d0\u56de\u53bb\uff0c\u4ed6\u4eec\u6b66\u5f53\u6d3e\u7684\u8fd9\u51e0\u4e2a\u4eba\u548c\u90a3\u4e2a\u98de\u8c79\u5b50\u891a\u9752\u5c71\u90fd\u8d70\u4e86\u3002\u6216 \u8bb8\u662f\u4ed6\u4e0d\u597d\u610f\u601d\u518d\u89c1\u6211\u7684\u8868\u59d0\uff0c\u5f20\u5409\u5143\u5f53\u665a\u4e0d\u53d1\u4e00\u8a00\u5c31\u8d70\uff0c\u540e\u6765\u4e5f\u6ca1\u6709\u518d\u4e0a\u8fc7\u6211\u59d1\u7236\u7684 \u95e8\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u5462\uff1f\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u4f9d\u6211\u7684\u6027\u5b50\uff0c\u672c\u6765\u4e5f\u662f\u4e0d\u613f\u5728\u59d1\u6bcd\u5bb6\u4e2d\u4f4f\u4e0b\u53bb\u4e86\uff0c\u4f46\u4e3a\u4e86\u60e6\u8bb0\u8868\u59d0\uff0c\u6211\u8fd8 \u662f\u8ddf\u7740\u5979\u4eec\u4e00\u540c\u56de\u5bb6\u3002 \u3000\u3000\u201c\u59d1\u6bcd\u628a\u8868\u59d0\u53e6\u5916\u5173\u5728\u4e00\u95f4\u67f4\u623f\u58c1\uff0c\u8d77\u521d\u4e0d\u8bb8\u6211\u53bb\u770b\u5979\uff0c\u540e\u6765\u8868\u59d0\u7edd\u98df\uff0c\u4e00\u8fde\u51e0\u5929\uff0c\u7c92 \u7c73\u90fd\u6ca1\u6709\u6cbe\u7259\uff0c\u6211\u5728\u59d1\u6bcd\u7684\u9ed8\u8bb8\u4e4b\u4e0b\uff0c\u9001\u996d\u7ed9\u5979\u3002\u6211\u529d\u5979\u8bf4\uff0c\u8c37\u6db5\u865a\u5e76\u6ca1\u6709\u6b7b\uff0c\u5979\u82e5\u662f\u7edd\u98df \u800c\u6b7b\uff0c\u53eb\u8c37\u6db5\u865a\u77e5\u9053\uff0c\u8c37\u6db5\u865a\u975e\u4e3a\u5979\u6b89\u60c5\u4e0d\u53ef\uff0c\u90a3\u5c82\u4e0d\u662f\u8fde\u7d2f\u8c37\u6db5\u865a\u4e86\uff1f\u4fd7\u8bed\u8bf4\u5f97\u597d\uff1a\u7559\u5f97 \u9752\u5c71\u5728\uff0c\u4e0d\u6015\u6ca1\u67f4\u70e7\uff01\u4f55\u51b5\u4fdd\u7559\u4e00\u6761\u6027\u547d\uff0c\u5c06\u6765\u8bf4\u4e0d\u5b9a\u8fd8\u6709\u91cd\u9022\u4e4b\u65e5\u3002 \u3000\u3000\u201c\u8868\u59d0\u5728\u6211\u82e6\u529d\u4e4b\u4e0b\uff0c\u8fd9\u624d\u7b54\u5e94\u8fdb\u98df\u3002\u4f46\u5979\u6c42\u6211\u7b54\u5e94\u5979\u4e00\u4ef6\u4e8b\u60c5\uff0c\u5e2e\u5979\u9003\u8d70\u3002\u6211\u77e5\u9053\u59d1 \u7236\u59d1\u6bcd\u7684\u5389\u5bb3\uff0c\u4f46\u4e3a\u4e86\u8868\u59d0\uff0c\u6211\u5927\u80c6\u7684\u7b54\u5e94\u4e86\u3002 \u3000\u3000\u201c\u4e8b\u60c5\u51fa\u4e4e\u610f\u5916\u7684\u987a\u5229\uff0c\u5f53\u665a\u6211\u6253\u5f00\u67f4\u623f\uff0c\u628a\u8868\u59d0\u653e\u4e86\u51fa\u6765\uff0c\u4fbf\u8ddf\u7740\u5979\u4e00\u540c\u9003\u8d70\u3002\u59d1\u7236 \u59d1\u6bcd\u7adf\u7136\u6beb\u672a\u53d1\u89c9\u3002\u4e0d\uff0c\u4e5f\u8bb8\u662f\u4ed6\u4eec\u5df2\u7ecf\u53d1\u89c9\uff0c\u4f46\u5374\u6545\u610f\u88c5\u4f5c\u4e0d\u77e5\uff0c\u6709\u5fc3\u653e\u5973\u513f\u9003\u8d70\u7684\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u70b9\u4e86\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u5929\u4e0b\u6ca1\u6709\u4e0d\u7231\u5b50\u5973\u7684\u7236\u6bcd\uff0c\u6211\u60f3\u5728\u90a3\u665a\u7684\u4e8b\u60c5\u8fc7\u540e\uff0c\u4e25\u58f0\u6d9b \u592b\u5987\u6050\u6015\u4e5f\u662f\u4e0d\u514d\u4f1a\u6709\u540e\u6094\u7684\u4e86\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u6211\u4e0e\u8868\u59d0\u5206\u624b\u4e4b\u540e\uff0c\u4e00\u76f4\u6ca1\u6709\u5f97\u5230\u5979\u7684\u6d88\u606f\uff0c\u4e5f\u4e0d\u77e5\u5979\u627e\u5230\u4e86\u8c37\u6db5 \u865a\u6ca1\u6709\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u4f60\u53ef\u66fe\u628a\u8fd9\u4ef6\u4e8b\u544a\u8bc9\u4f60\u7239\u7239\uff1f\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u6211\u8fd8\u672a\u66fe\u56de\u5230\u5bb6\u4e2d\uff0c\u7239\u7239\u65e9\u5df2\u77e5\u9053\u4e86\u3002\u6b66\u5f53\u6d3e\u4e0e\u7239\u7239\u7684\u4ea4\u60c5\u4e0d\u6d45\uff0c\u5e78\u4e8f\u5982 \u6b64\uff0c\u4e54\u5143\u58ee\u5e08\u5144\u5f1f\u624d\u4e0d\u6562\u5411\u6211\u7239\u7239\u5174\u95ee\u7f6a\u4e4b\u5e08\u3002\u4f46\u5374\u4e5f\u7d2f\u6211\u53d7\u4e86\u7239\u7239\u4e00\u987f\u8d23\u5907\u3002\u7239\u7239\u8fd8\u4eb2\u81ea \u4e0a\u6b66\u5f53\u5c71\u5411\u4e54\u5143\u58ee\u3001\u5f20\u5143\u5409\u7684\u5e08\u957f\u8d54\u7f6a\uff0c\u8fd9\u4e8b\u65b9\u7b97\u4e86\u7ed3\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u7b11\u9053\uff1a\u201c\u4f60\u593a\u4e86\u6b66\u5f53\u5f1f\u5b50\u7684\u5251\uff0c\u95ef\u4e86\u5982\u6b64\u5927\u7978\uff0c\u53ea\u53d7\u4e00\u987f\u8d23\u5907\uff0c\u8fd9\u5df2\u662f\u4fbf\u5b9c\u4f60 \u4e86\uff0c\u4f60\u8fd8\u4e0d\u670d\u6c14\u4e48\uff1f\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u7239\u7239\u542c\u6211\u8bf4\u4e86\u6b64\u4e8b\u7684\u7ecf\u8fc7\uff0c\u5012\u662f\u9887\u4e3a\u540c\u60c5\u8c37\u6db5\u865a\u7684\u3002\u53ef\u60dc\u4ed6\u56de\u5bb6\u4e0d\u4e45\uff0c\u53c8 \u6709\u7b2c\u4e8c\u6b21\u7684\u5317\u65b9\u4e4b\u884c\uff0c\u662f\u4ee5\u53ea\u80fd\u62bd\u51fa\u4e00\u70b9\u65f6\u95f4\uff0c\u5230\u6b66\u5f53\u5c71\u8d54\u7f6a\uff0c\u5374\u6ca1\u6709\u65f6\u95f4\u8fdc\u8d74\u5ddd\u897f\uff0c\u5411\u9752 \u57ce\u6d3e\u7684\u540d\u5bbf\u7389\u8702\u9053\u957f\u67e5\u95ee\u8fd9\u8c37\u6db5\u865a\u7684\u6765\u5386\u4e86\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u6c89\u541f\u534a\u664c\uff0c\u8bf4\u9053\uff1a\u201c\u542c\u4f60\u6240\u8bf4\u7684\u60c5\u5f62\uff0c\u8c37\u6db5\u865a\u90a3\u665a\u4ee5\u638c\u529b\u9707\u64bc\u5927\u6811\u7684\u529f\u592b\u7684\u786e\u662f \u5929\u96f7\u529f\uff0c\u4f9d\u6b64\u770b\u6765\uff0c\u4ed6\u5f88\u53ef\u80fd\u5c31\u662f\u56db\u5e08\u53d4\u5728\u6c5f\u5357\u6240\u4f20\u7684\u5f1f\u5b50\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u4ed6\u4e5f\u5c31\u8db3\u4f60\u7684\u5e08\u5144\u5f1f\u4e86\u3002\u53ef\u60dc\u6211\u4e0d\u8bc6\u5f97\u4ed6\u7684\u5929\u96f7\u529f\uff0c\u7239\u7239\u542c\u4e86 \u6211\u7684\u53d9\u8ff0\uff0c\u9053\u8fd9\u662f\u4e00\u4f4d\u65b0\u51fa\u9053\u7684\u5c11\u5e74\u9ad8\u624b\uff0c\u5374\u4e0d\u77e5\u9053\u4ed6\u662f\u4f60\u8fd9\u4e00\u6d3e\u7684\u5f1f\u5b50\u3002\u5426\u5219\u4ee5\u6211\u7239\u7239\u4e0e \u4ee4\u5e08\u7684\u4ea4\u60c5\uff0c\u4ed6\u5c31\u662f\u4e0d\u80fd\u4eb2\u81ea\u8bbf\u67e5\uff0c\u4e5f\u4f1a\u6258\u4eba\u8bbf\u67e5\u7684\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u591a\u8c22\u4f60\u544a\u8bc9\u6211\u8fd9\u4ef6\u4e8b\u60c5\u3002\u5bb6\u5e08\u4e00\u76f4\u60e6\u8bb0\u7740\u56db\u5e08\u53d4\uff0c\u51e0\u5341\u5e74\u6765\uff0c\u6beb\u65e0\u4ed6\u7684\u97f3 \u8baf\u3002\u5982\u4eca\u603b\u7b97\u662f\u5f97\u5230\u4e00\u70b9\u6d88\u606f\u4e86\u3002\u5bb6\u5e08\u6700\u5927\u7684\u5fc3\u613f\u5c31\u662f\u627e\u5230\u56db\u5e08\u53d4\uff0c\u4e0e\u56db\u5e08\u53d4\u5408\u529b\u6e05\u7406\u95e8 \u6237\uff0c\u628a\u90a3\u4e2a\u80cc\u53db\u5e08\u95e8\u3001\u7518\u5fc3\u4e3a\u864e\u4f5c\u6005\u7684\u9633\u5929\u96f7\u9664\u6389\u3002\u6211\u60f3\u5bb6\u5e08\u77e5\u9053\u4e86\u8fd9\u4e2a\u6d88\u606f\uff0c\u4e00\u5b9a\u4f1a\u4eb2\u81ea \u5230\u6c5f\u5357\u4e00\u8d9f\uff0c\u53bb\u8bbf\u67e5\u4ed6\u4eec\u7684\u4e0b\u843d\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u4f46\u613f\u4ee4\u5e08\u80fd\u591f\u627e\u5230\u4ed6\u4eec\uff0c\u6211\u4e5f\u5f88\u60f3\u77e5\u9053\u8c37\u6db5\u865a\u548c\u8868\u59d0\u7684\u7ed3\u679c\u5462\u3002\u4f46\u613f\u8fd9\u4e2a \u6545\u4e8b\uff0c\u6709\u4e2a\u7f8e\u6ee1\u7684\u7ed3\u5c40\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u54ea\u91cc\u77e5\u9053\uff0c\u8c37\u6db5\u865a\u6b64\u9645\u6b63\u662f\u5728\u627e\u5bfb\u5979\uff0c\u800c\u4e14\u4ed6\u4e5f\u975e\u5e38\u5e0c\u671b\u80fd\u591f\u548c\u891a\u4e91\u5cf0\u89c1\u9762\u3002\u5b5f \u660e\u971e\u4e0d\u77e5\u9053\u4ed6\u5728\u4f55\u65b9\uff0c\u4ed6\u5374\u662f\u77e5\u9053\u5b5f\u660e\u971e\u548c\u891a\u4e91\u5cf0\u5728\u8fd9\u6761\u8def\u4e0a\u7684\u3002 \u3000\u3000\u539f\u6765\u8c37\u6db5\u865a\u5c31\u662f\u6768\u5a49\u4e0e\u660e\u6167\u516c\u4e3b\u5979\u4eec\u5728\u90a3\u65b9\u5e99\u6240\u89c1\u7684\u90a3\u4e2a\u8499\u9762\u4eba\u3002\u6b63\u662f\uff1a \u3000\u3000\u4e1c\u98ce\u96be\u4e0e\u82b1\u4e3a\u4e3b\uff0c\u4e24\u5904\u9e33\u9e2f\u5404\u81ea\u51c9\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b\u2014\u2014\u7b2c\u4e09\u5341\u56db\u56de\u3000\u9e33\u4fa3\u5206\u98de\u60b2\u4e27\u5fd7\u3000\u6069\u5e08\u8bad\u8bf2\u9192\u75f4\u8ff7 \u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b \u7b2c\u4e09\u5341\u56db\u56de\u3000\u9e33\u4fa3\u5206\u98de\u60b2\u4e27\u5fd7\u3000\u6069\u5e08\u8bad\u8bf2\u9192\u75f4\u8ff7 \u3000\u3000\u8c37\u6db5\u865a\u8d70\u5728\u767d\u832b\u832b\u7684\u96ea\u5730\u4e0a\uff0c\u5fc3\u60c5\u4e5f\u662f\u4e00\u7247\u832b\u7136\u3002 \u3000\u3000\u96ea\u6708\u4ea4\u8f89\uff0c\u5927\u5730\u4fe8\u5982\u7ea4\u5c18\u4e0d\u67d3\u7684\u660e\u955c\uff0c\u4ed6\u7684\u5fc3\u4e0a\u5374\u5728\u6ef4\u7740\u8840\uff0c\u8bb8\u8bb8\u591a\u591a\u9178\u751c\u82e6\u8fa3\u3001\u60b2\u6b22 \u79bb\u5408\u7684\u56de\u5fc6\uff0c\u4e00\u8d77\u6d8c\u4e0a\u5fc3\u5934\uff01 \u3000\u3000\u7ecf\u8fc7\u56db\u5e74\u7684\u517b\u606f\uff0c\u65f6\u95f4\u662f\u6700\u597d\u7684\u533b\u751f\uff0c\u533b\u6cbb\u4e86\u4ed6\u8eab\u5fc3\u7684\u521b\u4f24\u3002\u8138\u4e0a\u7684\u4f24\u75d5\u65e9\u5df2\u590d\u5408\uff0c\u5fc3 \u4e0a\u7684\u4f24\u75d5\u4e5f\u7ed9\u4ed6\u7528\u51b7\u6f20\u7684\u611f\u60c5\u906e\u63a9\u8d77\u6765\uff0c\u5c31\u50cf\u7528\u51b0\u96ea\u8986\u76d6\u672c\u6765\u5177\u6709\u751f\u547d\u529b\u7684\u91ce\u8349\u4f3c\u7684\uff0c\u4e0d\u662f \u6545\u610f\u53bb\u89e6\u52a8\u5b83\uff0c\u5c31\u597d\u50cf\u4e0d\u89c9\u5f97\u5b83\u7684\u5b58\u5728\u4e86\u3002 \u3000\u3000\u53ef\u662f\u4eca\u665a\u4ed6\u5374\u7ed9\u6768\u5a49\u548c\u963f\u76d6\u89e6\u53ca\u4e86\u5fc3\u4e0a\u7684\u521b\u4f24\uff0c\u4f24\u53e3\u53c8\u88c2\u5f00\u4e86\uff0c\u56e0\u4e3a\u6768\u5a49\u63d0\u8d77\u4e86\u5b5f\u660e\u971e \u7684\u540d\u5b57\uff0c\u800c\u963f\u76d6\u5219\u63ed\u5f00\u4e86\u4ed6\u7684\u8499\u9762\u5e03\uff0c\u53eb\u4ed6\u8bb0\u5f97\u81ea\u5df1\u662f\u4e2a\u4e11\u964b\u7684\u7537\u5b50\u3002 \u3000\u3000\u4ed6\u4e0d\u77e5\u9053\u4e25\u70f7\u5728\u627e\u5bfb\u4ed6\uff0c\u4f46\u4ed6\u5374\u662f\u6709\u610f\u628a\u81ea\u5df1\u9690\u85cf\u8d77\u6765\uff0c\u8eb2\u907f\u4e25\u70f7\u7684\u3002\u56db\u5e74\u6765\u4ed6\u6ca1\u6709\u6253 \u542c\u8fc7\u4e25\u70f7\u7684\u4e0b\u843d\uff0c\u4e5f\u542c\u4e0d\u5230\u5173\u4e8e\u4e25\u70f7\u7684\u4efb\u4f55\u6d88\u606f\u3002 \u3000\u3000\u60f3\u4e0d\u5230\u201c\u5b89\u5b89\u9759\u9759\u201d\u5730\u8fc7\u4e86\u56db\u5e74\uff0c\u4eca\u665a\u5374\u7ed9\u6768\u5a49\u5728\u4ed6\u201c\u5e73\u9759\u201d\u7684\u5fc3\u6e56\u6295\u4e0b\u4e86\u4e00\u5757\u77f3\u5b50\u3002 \u6768\u5a49\u544a\u8bc9\u4ed6\uff0c\u5b5f\u660e\u971e\u5c31\u5728\u8fd9\u6761\u8def\u4e0a\uff01\u8fd9\u4e2a\u6d88\u606f\u6b63\u5c31\u662f\u9707\u64bc\u4ed6\u5fc3\u7075\u7684\u201c\u77f3\u5b50\u201d\u554a\uff01 \u3000\u3000\u5b5f\u660e\u971e\u5c31\u5728\u8fd9\u6761\u8def\u4e0a\uff0c\u4e25\u70f7\u53c8\u5728\u4f55\u65b9\uff1f \u3000\u3000\u5b5f\u660e\u971e\u662f\u4e25\u70f7\u7684\u8868\u59b9\uff0c\u89c1\u7740\u4e86\u5b5f\u660e\u971e\uff0c\u603b\u8be5\u77e5\u9053\u4e86\u4e25\u70f7\u7684\u6d88\u606f\u5427\uff1f\u4ed6\u60f3\uff0c\u4ed6\u5f53\u7136\u4e0d\u4f1a\u77e5 \u9053\uff0c\u5b5f\u660e\u971e\u5e2e\u5fd9\u5979\u7684\u8868\u59d0\u9003\u8d70\u540e\uff0c\u5979\u4eec\u8868\u59d0\u59b9\u97f3\u8baf\u65ad\u7edd\u4ea6\u5df2\u7ecf\u6709\u56db\u5e74\u4e86\u3002 \u3000\u3000\u56db\u5e74\u6765\u4ed6\u867d\u7136\u662f\u6709\u610f\u5730\u5728\u8eb2\u907f\u4e25\u70f7\uff0c\u53ef\u662f\u4ed6\u53c8\u662f\u4f55\u7b49\u7684\u5728\u6e34\u671b\u77e5\u9053\u4e25\u70f7\u7684\u6d88\u606f\u554a\uff01\u53bb\u4e0d \u53bb\u627e\u5bfb\u5b5f\u660e\u971e\uff0c\u8bd5\u4e00\u8bd5\u5411\u5b5f\u660e\u971e\u6253\u542c\u5462\uff1f \u3000\u3000\u5fc3\u5728\u8df3\u52a8\uff0c\u8138\u4e0a\u7684\u4f24\u75d5\u4e5f\u597d\u50cf\u5728\u53d1\u70e7\uff0c\u70e7\u5f97\u4ed6\u706b\u8fa3\u8fa3\u4f5c\u75db\u3002\u4ed6\u4e0d\u77e5\u4e0d\u89c9\u5730\u62c9\u4e0b\u4e86\u4ed6\u7684\u8499 \u9762\u5e03\uff0c\u96ea\u5730\u4e0a\u73b0\u51fa\u4e00\u4e2a\u4e11\u964b\u7684\u8138\u5f62\u3002\u4ed6\u4e0d\u89c9\u82e6\u7b11\u9053\uff1a\u201c\u6211\u8fd9\u526f\u5c0a\u5bb9\u8fd8\u914d\u63a5\u53d7\u4efb\u4f55\u5973\u5b50\u7684\u7231 \u4e48\uff1f\u4f55\u51b5\u6211\u4e0e\u4e25\u70f7\u4e4b\u95f4\uff0c\u6709\u7740\u8bb8\u591a\u969c\u788d\uff0c\u6211\u4eec\u5fc5\u987b\u5206\u624b\uff0c\u8fd9\u5df2\u7ecf\u662f\u2018\u6ce8\u5b9a\u2019\u7684\u4e86\u3002\u7eb5\u7136\u5979\u548c \u963f\u76d6\u4e00\u6837\uff0c\u4e0d\u4ecb\u610f\u6211\u7684\u4e11\u964b\uff0c\u6211\u53c8\u4f55\u5fcd\u518d\u6311\u8d77\u5979\u7684\u4f24\u5fc3\uff1f\u65e2\u7136\u6211\u4e0d\u60f3\u518d\u89c1\u5979\uff0c\u90a3\u53c8\u4f55\u5fc5\u8981\u5979 \u77e5\u9053\u6211\u8fd8\u6d3b\u5728\u8fd9\u4e2a\u4e16\u4e0a\uff1f\u201d\u60f3\u5230\u6b64\u5904\uff0c\u4ed6\u51e0\u4e4e\u5c31\u60f3\u653e\u5f03\u53bb\u627e\u5bfb\u5b5f\u660e\u971e\uff0c\u5411\u5b5f\u660e\u971e\u6253\u542c\u7684\u5ff5 \u5934\u3002 \u3000\u3000\u53ef\u662f\u5728\u8fd9\u6761\u8def\u4e0a\uff0c\u8fd8\u6709\u4e00\u4e2a\u4eba\uff0c\u4e5f\u662f\u4ed6\u975e\u5e38\u5e0c\u671b\u80fd\u591f\u89c1\u9762\u7684\u3002\u8fd9\u4e2a\u4eba\u5c31\u662f\u4e0e\u5b5f\u660e\u971e\u7ed3\u4f34 \u540c\u884c\u7684\u891a\u4e91\u5cf0\u3002 \u3000\u3000\u4ed6\u542c\u4e86\u9633\u575a\u767d\u90a3\u665a\u7684\u8bf4\u8bdd\uff0c\u5df2\u7ecf\u53ef\u4ee5\u786e\u5b9a\u8fd9\u4e2a\u891a\u4e91\u5cf0\u4e00\u5b9a\u662f\u548c\u4ed6\u540c\u95e8\u7684\u5e08\u5144\u5f1f\uff0c\u800c\u4e14\u8fd9 \u4e2a\u891a\u4e91\u5cf0\u4e5f\u662f\u548c\u9633\u5929\u96f7\u3001\u9633\u575a\u767d\u4f5c\u5bf9\u7684\u4eba\u3002 \u3000\u3000\u8c37\u6db5\u865a\u60f3\u8d77\u4e86\u53e6\u5916\u4e00\u6869\u4ee4\u4ed6\u975e\u5e38\u611f\u52a8\u7684\u5f80\u4e8b\u3002 \u3000\u3000\u4ed6\u88ab\u8feb\u4e0e\u4e25\u70f7\u5206\u624b\u4e4b\u540e\uff0c\u8eab\u5fc3\u53d7\u521b\uff0c\u4e07\u5ff5\u4ff1\u7070\uff0c\u56de\u5c71\u9759\u517b\u4e86\u4e09\u5e74\u591a\uff0c\u8eab\u4e0a\u7684\u4f24\u75d5\u65e9\u5df2\u597d \u4e86\uff0c\u5fc3\u4e0a\u7684\u4f24\u75d5\u5374\u662f\u96be\u671b\u75ca\u6108\uff0c\u4e00\u4e2a\u751f\u9f99\u6d3b\u864e\u7684\u5c11\u5e74\u7adf\u7136\u53d8\u5f97\u7cbe\u795e\u9893\u4e27\uff0c\u66ae\u6c14\u6c89\u6c89\u3002 \u3000\u3000\u6709\u4e00\u5927\u665a\u4e0a\uff0c\u4ed6\u7684\u5e08\u7236\u803f\u5929\u98ce\u7a81\u7136\u95ee\u4ed6\u9053\uff1a\u201c\u4f60\u77e5\u9053\u5e08\u7236\u5e76\u975e\u6c5f\u5357\u4eba\u6c0f\uff0c\u4f46\u4f60\u53ef\u77e5\u9053\u5e08 \u7236\u4e3a\u4ec0\u4e48\u79bb\u4e61\u80cc\u4e95\uff0c\u72ec\u81ea\u6765\u5230\u65e0\u4eb2\u65e0\u6545\u7684\u6c5f\u5357\u4e48\uff1f\u201d \u3000\u3000\u8c37\u6db5\u865a\u4ece\u672a\u542c\u8fc7\u5e08\u7236\u8bf4\u53ca\u81ea\u5df1\u7684\u6765\u5386\uff0c\u5e08\u7236\u4e0d\u8bf4\uff0c\u4ed6\u4e0d\u4fbf\u95ee\uff0c\u5982\u4eca\u5e08\u7236\u81ea\u5df1\u63d0\u8d77\uff0c\u4ed6\u5f53 \u7136\u662f\u8981\u95ee\u5176\u4e2d\u7f18\u6545\u4e86\u3002 \u3000\u3000\u803f\u5929\u98ce\u53cc\u7738\u70af\u70af\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u4f60\u8981\u95ee\u5176\u4e2d\u7f18\u6545\u4e48\uff0c\u8fd9\u5f88\u7b80\u5355\uff0c\u53ea\u56e0\u4e3a\u6211\u6ca1\u6709\u5fd8\u8bb0\u6211\u662f\u6c49 \u4eba\uff0c\u6211\u4e0d\u80fd\u5fcd\u53d7\u5f02\u65cf\u7684\u7edf\u6cbb\u3002 \u3000\u3000\u201c\u4f60\u7684\u5e08\u7956\u662f\u4e00\u4f4d\u9690\u59d3\u57cb\u540d\u7684\u5927\u4fa0\uff0c\u6bd5\u751f\u4ee5\u9a71\u9664\u91d1\u864f\uff0c\u6062\u590d\u4e2d\u539f\u4e3a\u804c\u5fd7\u3002\u53ef\u60dc\u5728\u4ed6\u7684\u5f1f \u5b50\u4e4b\u4e2d\uff0c\u5374\u51fa\u4e86\u4e00\u4e2a\u53db\u5f92\u3002\u8fd9\u4e2a\u53db\u5f92\u800c\u4e14\u662f\u6b66\u529f\u6700\u5f3a\uff0c\u5c3d\u5f97\u4ed6\u8863\u94b5\u771f\u4f20\u7684\u5927\u5f1f\u5b50\uff01\u201d \u3000\u3000\u8c37\u6db5\u865a\u95ee\u9053\uff1a\u201c\u5e08\u7956\u662f\u5426\u6709\u6b20\u7cbe\u660e\uff0c\u4f55\u4ee5\u4f1a\u7acb\u4ed6\u505a\u638c\u95e8\u5f1f\u5b50\uff1f\u201d \u3000\u3000\u803f\u5929\u98ce\u9053\uff1a\u201c\u8fd9\u4eba\u4f5c\u4f2a\u7684\u529f\u592b\u6781\u662f\u5230\u5bb6\uff0c\u5728\u5e08\u95e8\u4e4b\u65f6\uff0c\u53cd\u9aa8\u4e1d\u6beb\u4e0d\u9732\u3002\u5e08\u7956\u5e76\u975e\u6709\u6b20\u7cbe \u660e\uff0c\u800c\u662f\u7231\u624d\u5fc3\u5207\u3002\u4ed6\u5165\u95e8\u6700\u65e9\u3001\u4e60\u827a\u6700\u52e4\uff0c\u4eba\u53c8\u806a\u660e\uff0c\u5bf9\u672c\u95e8\u7684\u2018\u5929\u96f7\u529f\u2019\u53c8\u6700\u6709\u5fc3\u5f97\uff0c \u5e08\u7956\u7ed9\u4ed6\u9a97\u8fc7\uff0c\u4e0d\u7acb\u4ed6\u8fd8\u80fd\u7acb\u8c01\uff1f \u3000\u3000\u201c\u5e08\u7956\u53bb\u4e16\u4e4b\u540e\uff0c\u4ed6\u65b9\u59cb\u516c\u7136\u6295\u654c\u3002\u8bf4\u662f\u2018\u6295\u654c\u2019\uff0c\u6050\u6015\u4e5f\u53ea\u8bf4\u5bf9\u4e86\u4e00\u534a\u3002\u56e0\u4e3a\u4ed6\u7684\u7236 \u4eb2\u662f\u6c49\u4eba\uff0c\u6bcd\u4eb2\u662f\u91d1\u4eba\u3002\u5e08\u7956\u6b7b\u540e\uff0c\u4ed6\u5c31\u4ee5\u91d1\u4eba\u81ea\u5c45\u4e86\u3002\u8bf4\u4e0d\u5b9a\u4ed6\u672c\u6765\u5c31\u662f\u5973\u771f\u9791\u5b50\u6d3e\u4ed6\u6765 \u5077\u5b66\u5e08\u7956\u7684\u6b66\u529f\u7684\uff0c\u4ea6\u5373\u662f\u8bf4\u4ed6\u672c\u6765\u5c31\u662f\u6211\u4eec\u7684\u654c\u4eba\uff0c\u4e0d\u8fc7\u5728\u4ed6\u53cd\u8ff9\u672a\u9732\u4e4b\u524d\uff0c\u6211\u4eec\u4e0d\u77e5\u7f62 \u4e86\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u8fd9\u4eba\u7684\u6b66\u529f\u65e2\u7136\u6781\u662f\u9ad8\u5f3a\uff0c\u6295\u9760\u4e86\u91d1\u864f\uff0c\u60f3\u5fc5\u4f1a\u53d7\u91cd\u7528\uff0c\u4ed6\u662f\u8c01\u5462\uff1f\u201d \u3000\u3000\u803f\u5929\u98ce\u9053\uff1a\u201c\u5c31\u662f\u73b0\u4efb\u91d1\u56fd\u56fd\u5e08\u7684\u9633\u5929\u96f7\u3002\u201d \u3000\u3000\u9633\u5929\u96f7\u662f\u91d1\u56fd\u7684\u7b2c\u4e00\u9ad8\u624b\uff0c\u81ed\u540d\u662d\u5f70\uff0c\u8c37\u6db5\u865a\u4e5f\u66fe\u542c\u8fc7\u4ed6\u7684\u540d\u5b57\uff0c\u5374\u60f3\u4e0d\u5230\u4ed6\u7adf\u7136\u662f\u81ea \u5df1\u7684\u5927\u5e08\u4f2f\u3002\u8c37\u6db5\u865a\u542c\u4e86\u5e08\u7236\u7684\u8bdd\uff0c\u4e0d\u89c9\u6124\u7136\u8bf4\u9053\uff1a\u201c\u8fd9\u771f\u662f\u672c\u95e8\u4e4b\u803b\uff01\u5e08\u7236\uff0c\u4f60\u83ab\u975e\u5c31\u662f \u7ed9\u8fd9\u53db\u5f92\u903c\u8d70\u7684\u4e48\uff1f\u201d \u3000\u3000\u803f\u5929\u98ce\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u5f53\u7136\u662f\u539f\u56e0\u4e4b\u4e00\u3002\u4f46\u662f\u903c\u5f97\u6211\u4e0d\u80fd\u5728\u5bb6\u4e61\u7acb\u8db3\u7684\uff0c\u4e3b\u8981\u8fd8\u662f\u9791\u5b50 \u671d\u5ef7\u3002\u4eca\u665a\u6211\u8981\u628a\u5168\u90e8\u7684\u4e8b\u5b9e\u544a\u8bc9\u4f60\uff0c\u6211\u8fd8\u8981\u4f60\u66ff\u6211\u505a\u4e00\u4ef6\u5927\u4e8b\uff0c\u4e86\u7ed3\u6211\u8fd9\u4e00\u751f\u6240\u672a\u80fd\u5b8c\u6210 \u7684\u5fc3\u613f\u3002\u4f60\u8981\u7262\u7262\u8bb0\u7740\u6211\u4eca\u665a\u7684\u8bf4\u8bdd\u3002\u522b\u5fd8\u4e86\u6211\u7684\u5429\u5490\uff01\u201d \u3000\u3000\u8c37\u6db5\u865a\u89c1\u5e08\u7236\u8bf4\u5f97\u5982\u6b64\u90d1\u91cd\uff0c\u8fde\u5fd9\u8bf4\u9053\uff1a\u201c\u5f1f\u5b50\u591a\u8499\u5e08\u7236\u6559\u517b\u6210\u4eba\uff0c\u6069\u903e\u7236\u6bcd\uff0c\u6709\u4e8b\u4f46 \u8bf7\u6069\u5e08\u5429\u5490\u3002\u201d \u3000\u3000\u803f\u5929\u98ce\u8bf4\u9053\uff1a\u201c\u4f60\u5e08\u7956\u6709\u56db\u4e2a\u5f1f\u5b50\uff0c\u6211\u6392\u884c\u6700\u672b\uff0c\u5934\u4e0a\u6709\u4e09\u4e2a\u5e08\u5144\u3002\u4e09\u5e08\u5144\u987e\u5929\u6a35\u65e9 \u6b7b\uff0c\u4e8c\u5e08\u5144\u534e\u5929\u8679\u4e3a\u4eba\u6b63\u76f4\uff0c\u4e0e\u6211\u6700\u4e3a\u76f8\u5f97\u3002\u5927\u5e08\u5144\u5c31\u662f\u90a3\u53db\u5f92\u9633\u5929\u96f7\u4e86\u3002 \u3000\u3000\u201c\u540c\u5728\u5e08\u4e4b\u65e5\uff0c\u9633\u5929\u96f7\u867d\u7136\u53cd\u8ff9\u672a\u9732\uff0c\u4f46\u6211\u5df2\u611f\u5230\u4e0e\u4ed6\u6c14\u5473\u4e0d\u6295\uff0c\u5176\u65f6\u6211\u5df2\u6697\u4e2d\u52a0\u76df\u4e49 \u519b\uff0c\u6b64\u4e8b\u53ea\u6709\u6211\u7684\u5e08\u7236\u77e5\u9053\u3002\u672c\u6765\u6211\u53ef\u4ee5\u544a\u8bc9\u4e8c\u5e08\u5144\u7684\uff0c\u4f46\u56e0\u4e8c\u5e08\u5144\u6709\u4e2a\u7f3a\u70b9\uff0c\u4ed6\u4e3a\u4eba\u867d\u7136 \u6b63\u76f4\uff0c\u6027\u60c5\u5374\u7a0d\u5acc\u61e6\u5f31\uff0c\u9047\u4e8b\u4e0d\u80fd\u5f53\u673a\u7acb\u65ad\u3002\u6211\u4e0d\u613f\u610f\u52c9\u5f3a\u4ed6\u52a0\u76df\u4e49\u519b\uff0c\u662f\u4ee5\u5fc5\u987b\u7b49\u5f85\u4ed6\u81ea \u5df1\u9732\u51fa\u53e3\u98ce\u4e4b\u65f6\uff0c\u6211\u65b9\u80fd\u628a\u79d8\u5bc6\u544a\u8bc9\u4ed6\u3002 \u3000\u3000\u201c\u5e08\u7236\u53bb\u4e16\u4e4b\u540e\uff0c\u7b2c\u4e8c\u5e74\u9633\u5929\u96f7\u5c31\u516c\u7136\u51fa\u9762\uff0c\u505a\u4e86\u91d1\u864f\u7684\u9e70\u72ac\u3002\u6211\u4e00\u5f97\u5230\u8fd9\u4e2a\u6d88\u606f\uff0c\u4fbf \u7acb\u5373\u53bb\u627e\u4e8c\u5e08\u5144\uff0c\u60f3\u8981\u4e0e\u4ed6\u8054\u624b\uff0c\u4ee3\u5e08\u6e05\u7406\u95e8\u6237\u3002\u54ea\u77e5\u4e8c\u5e08\u5144\u6015\u4e8b\uff0c\u4e0d\u6562\u4e0e\u5927\u5e08\u5144\u76f8\u6297\uff0c\u7adf \u4e0d\u77e5\u8eb2\u5230\u54ea\u91cc\u53bb\u4e86\u201d \u3000\u3000\u201c\u6211\u81ea\u77e5\u672c\u9886\u548c\u9633\u5929\u96f7\u8ddd\u79bb\u751a\u8fdc\uff0c\u72ec\u81ea\u53bb\u5bf9\u4ed8\u4ed6\uff0c\u7edd\u65e0\u6210\u529f\u5e0c\u671b\u3002\u56e0\u6b64\u552f\u6709\u4e00\u65b9\u9762\u81ea\u5df1 \u52e4\u7ec3\u6b66\u529f\uff0c\u4e00\u65b9\u9762\u6253\u542c\u4e8c\u5e08\u5144\u7684\u4e0b\u843d\uff0c\u5e0c\u671b\u627e\u7740\u4e86\u4ed6\uff0c\u53ef\u4ee5\u8bf4\u670d\u4ed6\u540c\u5fc3\u5408\u529b\u3002 \u3000\u3000\u201c\u6211\u5bb6\u4e2d\u53ea\u6709\u8001\u6bcd\u5c1a\u5b58\uff0c\u4f46\u6211\u5df2\u8ba2\u4e0b\u5a5a\u4e8b\uff0c\u672a\u5a5a\u59bb\u662f\u6211\u7684\u8868\u59b9\uff0c\u81ea\u5c0f\u5728\u6211\u5bb6\u4e2d\u5c45\u4f4f\uff0c\u4e5f \u5e78\u4e8f\u6709\u5979\uff0c\u66ff\u6211\u5c3d\u4e86\u4eba\u5b50\u4e4b\u8d23\u3002 \u3000\u3000\u201c\u6bcd\u4eb2\u672c\u6765\u8981\u6211\u5728\u51fa\u5e08\u4e4b\u540e\uff0c\u5c31\u56de\u5bb6\u5b8c\u5a5a\u7684\u3002\u6211\u627e\u4e0d\u7740\u4e8c\u5e08\u5144\uff0c\u4e5f\u51c6\u5907\u5b8c\u5a5a\u4e4b\u540e\u518d\u8bf4\uff0c \u4e8e\u662f\u4fbf\u8d76\u56de\u5bb6\u53bb\u3002 \u3000\u3000\u201c\u6211\u77e5\u9053\u9633\u5929\u96f7\u7edd\u4e0d\u4f1a\u653e\u8fc7\u6211\u7684\uff0c\u4e0d\u662f\u903c\u6211\u540c\u6d41\u5408\u6c61\uff0c\u5c31\u4e00\u5b9a\u8981\u628a\u6211\u6740\u6389\u3002\u4f46\u5374\u4ee5\u4e3a\u4ed6 \u4e0d\u77e5\u9053\u6211\u52a0\u76df\u4e49\u519b\u7684\u79d8\u5bc6\uff0c\u6b64\u65f6\u4ed6\u6b63\u5728\u5ba6\u9014\u5f97\u610f\uff0c\u672a\u5fc5\u5c31\u4f1a\u90a3\u6837\u7740\u6025\u7684\u8981\u5bf9\u4ed8\u6211\u3002\u6211\u4e5f\u60f3\u4e0d \u5230\u4ed6\u4f1a\u7528\u5351\u52a3\u7684\u624b\u6bb5\u5bf9\u4ed8\u6211\u7684\u8001\u6bcd\u3001\u59bb\u5b50\u3002 \u3000\u3000\u201c\u8868\u59b9\u662f\u6211\u9752\u6885\u7af9\u9a6c\u4e4b\u4ea4\uff0c\u6211\u81ea\u5c0f\u5c31\u559c\u6b22\u5979\u3002\u56e0\u6b64\u8fd9\u95e8\u4eb2\u4e8b\u867d\u662f\u7531\u4e8e\u7236\u6bcd\u4e4b\u547d\u5a92\u707c\u4e4b \u8a00\uff0c\u5374\u4e5f\u662f\u6211\u81ea\u5df1\u5fc3\u7518\u60c5\u613f\u7684\uff0c\u6211\u4e0e\u5979\u4e00\u522b\u591a\u5e74\uff0c\u5982\u4eca\u5b8c\u5a5a\u5728\u5373\uff0c\u5f52\u9014\u4e2d\u7684\u6ee1\u6000\u9ad8\u5174\uff0c\u90a3\u81ea \u662f\u4e0d\u6d88\u8bf4\u4e86\u3002 \u3000\u3000\u201c\u54ea\u77e5\u56de\u5230\u5bb6\u4e2d\u4e00\u770b\uff0c\u767b\u65f6\u5c31\u50cf\u51b7\u6c34\u6d47\u5934\uff0c\u628a\u6211\u7684\u6ee1\u6000\u9ad8\u5174\u51b2\u6389\u3002\u53ea\u89c1\u5927\u95e8\u4e0a\u8d34\u7740\u5b98\u5e9c \u7684\u5c01\u6761\uff0c\u6bcd\u4eb2\u548c\u8868\u59b9\u90fd\u5df2\u7ed9\u5b98\u5dee\u6349\u53bb\u4e86\u3002\u6536\u62bc\u5728\u4ec0\u4e48\u5730\u65b9\u4e5f\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u201c\u90bb\u5bb6\u7684\u4e00\u4f4d\u8001\u4f2f\u628a\u6211\u62c9\u8fdb\u4ed6\u7684\u5bb6\uff0c\u5c06\u90a3\u65e5\u7684\u4e8b\u60c5\u544a\u8bc9\u6211\uff0c\u6211\u8fd9\u624d\u77e5\u9053\u7adf\u662f\u9633\u5929\u96f7\u8fd9\u53ae \u4eb2\u81ea\u5e26\u9886\u5b98\u5dee\u6765\u6349\u62ff\u6211\u7684\u6bcd\u4eb2\u59bb\u5b50\u7684\uff0c\u800c\u4e14\u4ed6\u8fd8\u7559\u4e0b\u4e86\u4e00\u5c01\u4fe1\uff0c\u6258\u8fd9\u4f4d\u8001\u4f2f\u8f6c\u4ea4\u4e0e\u6211\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u66ff\u4ed6\u5e08\u7236\u53c8\u662f\u96be\u8fc7\uff0c\u53c8\u662f\u6c14\u6124\uff0c\u8bf4\u9053\uff1a\u201c\u9633\u5929\u96f7\u8fd9\u4eba\u9762\u517d\u5fc3\u7684\u4e1c\u897f\uff0c\u5c45\u7136\u8fd8\u6709\u8138 \u7559\u4e0b\u4fe1\u6765\uff0c\u4fe1\u4e0a\u8bf4\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u803f\u5929\u98ce\u51b7\u7b11\u9053\uff1a\u201c\u4fe1\u4e0a\u5012\u662f\u8bf4\u5f97\u5341\u5206\u5ba2\u6c14\u3002\u4ed6\u8bf4\u5e08\u5144\u5f1f\u6709\u798f\u540c\u4eab\uff0c\u6709\u7978\u540c\u5f53\uff0c\u77e5\u9053\u6211\u4e3a \u56fd\u4e8b\u5954\u8d70\uff0c\u6050\u6015\u96be\u4ee5\u517c\u987e\u5bb6\u5ead\uff0c\u65e2\u7136\u5206\u5c5e\u540c\u95e8\uff0c\u6211\u7684\u6bcd\u4eb2\u5c31\u7b49\u4e8e\u662f\u4ed6\u7684\u6bcd\u4eb2\uff0c\u6211\u7684\u59bb\u5b50\u4e5f\u5c31 \u7b49\u4e8e\u662f\u4ed6\u7684\u5f1f\u5ac2\u4e00\u6837\uff0c\u6240\u4ee5\u4ed6\u628a\u6211\u7684\u6bcd\u4eb2\u59bb\u5b50\u63a5\u5f80\u5927\u90fd\u5949\u517b\uff0c\u4e91\u4e91\u3002 \u3000\u3000\u201c\u4ed6\u7684\u4fe1\u91cc\u5df2\u7ecf\u900f\u9732\u51fa\u77e5\u9053\u6211\u52a0\u76df\u4e49\u519b\u4e4b\u4e8b\uff0c\u5341\u5206\u660e\u663e\uff0c\u4ed6\u662f\u8981\u628a\u6211\u7684\u6bcd\u4eb2\u59bb\u5b50\u4f5c\u4e3a\u4eba \u8d28\uff0c\u8981\u631f\u6211\u4e86\u3002 \u3000\u3000\u201c\u662f\u8dd1\u53bb\u4e0e\u4ed6\u62fc\u547d\uff0c\u8fd8\u662f\u6682\u4e14\u59d4\u5c48\u6c42\u5168\uff0c\u4ee5\u514d\u8fde\u7d2f\u8001\u6bcd\u7231\u59bb\u5462\uff1f\u4e24\u79cd\u5ff5\u5934\u5728\u6211\u5fc3\u4e2d\u53cd\u590d \u4ea4\u6218\uff0c\u5b9e\u5728\u4ee4\u6211\u96be\u4ee5\u51b3\u65ad\uff01\u4f46\u6700\u540e\u6211\u8fd8\u662f\u51b3\u5b9a\u4e86\u65e0\u8bba\u5982\u4f55\u4e5f\u5f97\u5148\u89c1\u4e86\u5979\u4eec\u518d\u8bf4\u3002 \u3000\u3000\u201c\u9633\u5929\u96f7\u7528\u5c3d\u624b\u6bb5\u7b3c\u7edc\u6211\uff0c\u6211\u4e00\u6765\u5230\uff0c\u4ed6\u5c31\u8bbe\u5bb4\u4e3a\u6211\u6d17\u5c18\uff0c\u53ef\u662f\u5374\u4e0d\u8ba9\u6211\u4e0e\u6bcd\u59bb\u89c1\u9762\u3002 \u3000\u3000\u201c\u6211\u4e0d\u80af\u559d\u4ed6\u7684\u9152\uff0c\u975e\u903c\u4ed6\u644a\u724c\u4e0d\u53ef\u3002\u4ed6\u8bf4\u4e86\u4e00\u5927\u8f66\u5b50\u7684\u8bdd\u529d\u6211\uff0c\u8fd9\u4e9b\u6c61\u8033\u4e4b\u8a00\u4e5f\u4e0d\u5fc5 \u7ec6\u8bf4\u5b83\u4e86\u3002\u6700\u540e\u4ed6\u63d0\u51fa\u4e86\u4e24\u4e2a\u6761\u4ef6\uff0c\u7b2c\u4e00\u8981\u6211\u7559\u4e0b\u6765\u505a\u4ed6\u5e2e\u624b\uff0c\u4e0e\u4ed6\u5171\u4eab\u2018\u8363\u534e\u2019\u3002\u7b2c\u4e8c\u8981 \u6211\u8bf4\u51fa\u6211\u6240\u77e5\u9053\u7684\u4e49\u519b\u79d8\u5bc6\u3002\u4e24\u4ef6\u4e8b\u60c5\uff0c\u4e00\u4e2a\u76ee\u7684\u3002\u603b\u4e4b\u662f\u8981\u6211\u5356\u56fd\u6c42\u8363\uff0c\u52a9\u4ed6\u52a0\u5b98\u8fdb\u7235\u7f62 \u4e86\u3002 \u3000\u3000\u201c\u662f\u53ef\u5fcd\u5b70\u4e0d\u53ef\u5fcd\uff1f\u4f46\u6295\u9f20\u5fcc\u5668\uff0c\u987e\u8651\u5230\u6bcd\u4eb2\u548c\u59bb\u5b50\u7684\u5b89\u5168\uff0c\u6211\u8fd8\u662f\u4e0d\u80fd\u4e0d\u6682\u65f6\u541e\u58f0\u5fcd \u6c14\u3002\u6211\u4e0d\u8bf4\u7b54\u5e94\u4e5f\u4e0d\u8bf4\u62d2\u7edd\uff0c\u6211\u8bf4\u4f60\u65e2\u7136\u628a\u6211\u7684\u6bcd\u4eb2\u59bb\u5b50\u2018\u63a5\u2019\u6765\uff0c\u6211\u5fc5\u987b\u548c\u5979\u4eec\u89c1\u9762\u3002\u4f60 \u4e0d\u8ba9\u6211\u4eec\u89c1\u9762\uff0c\u90a3\u5c31\u4ec0\u4e48\u4e5f\u8c08\u4e0d\u4e0a\u3002 \u3000\u3000\u201c\u4ed6\u4ee5\u4e3a\u6211\u5df2\u7ecf\u6709\u70b9\u52a8\u6447\uff0c\u4fbf\u8bf4\uff1a\u2018\u8fd9\u4e2a\u5bb9\u6613\uff0c\u4f2f\u6bcd\u548c\u5ac2\u5b50\u5c31\u4f4f\u5728\u8fd9\u513f\u3002\u2019\u6211\u8981\u6c42\u5355\u72ec \u548c\u5979\u4eec\u89c1\u9762\uff0c\u4ed6\u4e5f\u7b54\u5e94\u4e86\u3002 \u3000\u3000\u201c\u4f46\u8bf4\u53e5\u8001\u5b9e\u8bdd\uff0c\u89c1\u9762\u4e4b\u540e\uff0c\u53c8\u5982\u4f55\u5462\uff1f\u6211\u7684\u6bcd\u4eb2\u548c\u59bb\u5b50\u90fd\u662f\u4e0d\u4f1a\u6b66\u529f\uff0c\u6211\u7684\u6b66\u529f\u867d\u7ecf \u82e6\u7ec3\uff0c\u81ea\u95ee\u4e5f\u8fd8\u6bd4\u4e0d\u4e0a\u9633\u5929\u96f7\uff0c\u82e6\u8981\u786c\u95ef\u51fa\u53bb\u7684\u8bdd\uff0c\u53ea\u6015\u81ea\u8eab\u96be\u4fdd\uff0c\u66f4\u4e0d\u8981\u8bf4\u80fd\u591f\u628a\u5979\u4eec\u6551 \u51fa\u53bb\u4e86\u3002 \u3000\u3000\u201c\u8bf4\u6765\u60ed\u6127\uff0c\u4e3a\u4e86\u8001\u6bcd\u548c\u7231\u59bb\uff0c\u6211\u5f53\u65f6\u7684\u786e\u662f\u5fc3\u4e71\u5982\u9ebb\uff0c\u6beb\u65e0\u4e3b\u610f\u7684\u3002\u660e\u77e5\u9633\u5929\u96f7\u8bbe\u4e0b \u4e86\u9677\u9631\uff0c\u8fd9\u9677\u9631\u53ef\u4ee5\u4ee4\u6211\u8eab\u8d25\u540d\u88c2\uff1b\u4f46\u5fc3\u91cc\u4e5f\u672a\u5c1d\u6ca1\u6709\u534a\u70b9\u52a8\u6447\u7684\u3002 \u3000\u3000\u201c\u60f3\u4e0d\u5230\u89c1\u4e86\u5979\u4eec\uff0c\u53cd\u800c\u662f\u5979\u4eec\u8f7b\u8f7b\u6613\u6613\u5730\u66ff\u6211\u89e3\u51b3\u4e86\u96be\u9898\u3002\u5509\uff0c\u8fd9\u56db\u4e2a\u5b57\u8bf4\u6765\u5bb9\u6613\uff0c \u5728\u5979\u4eec\u53ef\u662f\u59d4\u5b9e\u4e0d\u5bb9\u6613\u505a\u5230\u7684\u554a\uff01\u201d \u3000\u3000\u8c37\u6db5\u865a\u542c\u5230\u8fd9\u91cc\uff0c\u5fc3\u4e2d\u5f88\u89c9\u5947\u602a\uff1a\u201c\u65e2\u7136\u5e08\u7236\u7684\u6bcd\u4eb2\u548c\u672a\u5a5a\u59bb\u5b50\u90fd\u662f\u4e0d\u4f1a\u6b66\u529f\uff0c\u5979\u4eec\u53c8 \u7528\u4ec0\u4e48\u6cd5\u5b50\u8131\u8eab\u5462\uff1f\u201d \u3000\u3000\u803f\u5929\u98ce\u659f\u4e86\u6ee1\u6ee1\u7684\u4e00\u7897\u9152\uff0c\u4e00\u9897\u9897\u7684\u6cea\u73e0\u6ef4\u5728\u9152\u4e2d\u3002\u8c37\u6db5\u865a\u4ece\u6765\u6ca1\u6709\u89c1\u8fc7\u5e08\u7236\u8fd9\u4e2a\u6837 \u5b50\uff0c\u4e0d\u89c9\u5927\u5403\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u5e08\u7236\uff0c\u4f60\u600e\u6837\u5566\uff1f\u201d\u4ed6\u672c\u4ee5\u4e3a\u6240\u8c13\u201c\u89e3\u51b3\u96be\u9898\u201d\u5c31\u662f\u201c\u8131 \u9669\u201d\uff0c\u6b64\u65f6\u5df2\u9690\u9690\u77e5\u9053\u731c\u5f97\u4e0d\u5bf9\u4e86\u3002 \u3000\u3000\u803f\u5929\u98ce\u542c\u4e86\u5f92\u5f1f\u8fd9\u58f0\u547c\u5524\uff0c\u597d\u50cf\u662f\u4ece\u6076\u68a6\u4e2d\u60ca\u9192\u8fc7\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u8bb0\u5f97\u90a3\u5929\u6211\u4e5f\u66fe\u8fd9\u6837\u7684 \u95ee\u6211\u7684\u6bcd\u4eb2\uff1a\u2018\u5a18\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u2019\u56e0\u4e3a\u6211\u89c1\u7740\u5979\u7684\u65f6\u5019\uff0c\u5979\u7684\u9762\u8272\u5df2\u7ecf\u662f\u5f88\u4e0d\u5bf9\u4e86\u3002 \u3000\u3000\u201c\u5a18\u8bf4\uff1a\u2018\u6ca1\u4ec0\u4e48\uff0c\u6211\u5c31\u662f\u7b49\u7740\u548c\u4f60\u89c1\u8fd9\u4e00\u6b21\u9762\u3002\u4e0d\u8fc7\uff0c\u5176\u5b9e\u4f60\u662f\u4e0d\u5e94\u8be5\u6765\u7684\u3002\u2019\u6211 \u8bf4\uff1a\u2018\u5a18\u548c\u8868\u59b9\u90fd\u5728\u8fd9\u513f\uff0c\u6211\u600e\u80fd\u591f\u4e0d\u6765\u5462\uff1f\u2019 \u3000\u3000\u201c\u5a18\u8bf4\uff1a\u2018\u6211\u77e5\u9053\u4f60\u5bf9\u6211\u4e00\u7247\u5b5d\u5fc3\uff0c\u4f46\u4f60\u53ef\u77e5\u9053\u5e94\u8be5\u5148\u56fd\u540e\u5bb6\u7684\u9053\u7406\uff1f\u2019\u6211\u8bf4\uff1a\u2018\u5b69\u513f \u4e0d\u6562\u5fd8\u8bb0\u6bcd\u4eb2\u7684\u6559\u8bad\u3002\u2019 \u3000\u3000\u201c\u5a18\u7684\u4e24\u53ea\u773c\u775b\u5ffd\u5730\u5f20\u5f00\uff0c\u76ee\u5149\u70af\u70af\u5730\u770b\u7740\u6211\uff0c\u8bf4\u9053\uff1a\u2018\u4f60\u8001\u5b9e\u544a\u8bc9\u6211\uff0c\u4f60\u53ef\u66fe\u7b54\u5e94\u66ff \u9633\u5929\u96f7\u505a\u4efb\u4f55\u4e8b\u60c5\uff1f\u2019\u6211\u8bf4\uff1a\u2018\u5b69\u513f\u5e76\u672a\u4e0a\u4ed6\u7684\u5f53\u3002\u201d \u3000\u3000\u201c\u5a18\u8fd9\u624d\u597d\u50cf\u7a0d\u7a0d\u653e\u5fc3\uff0c\u8bf4\u9053\uff1a\u2018\u597d\uff0c\u4f60\u6ca1\u6709\u4e0a\u4ed6\u7684\u5f53\u5c31\u597d\uff0c\u4f46\u4f60\u73b0\u5728\u4e00\u5b9a\u662f\u597d\u751f\u4e3a \u96be\uff0c\u9633\u5929\u96f7\u7528\u4f60\u7684\u6bcd\u4eb2\u548c\u4f60\u7684\u59bb\u5b50\u8981\u80c1\u4f60\uff0c\u4f60\u4e0d\u613f\u610f\u5c48\u670d\uff0c\u53ef\u662f\u53c8\u4e0d\u77e5\u9053\u600e\u4e48\u529e\uff0c\u662f\u5417\uff1f\u2019 \u6211\u7ed9\u6bcd\u4eb2\u8bf4\u4e2d\u5fc3\u4e8b\uff0c\u53ea\u597d\u9ed8\u7136\u4e0d\u8bed\u3002 \u3000\u3000\u201c\u5a18\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u2018\u6211\u5c31\u662f\u6015\u4f60\u4e00\u65f6\u628a\u6301\u4e0d\u5b9a\uff0c\u4e27\u4e86\u540d\u8282\u3002\u6240\u4ee5\u6211\u4e0b\u4e86\u51b3\u5fc3\u66ff\u4f60\u89e3 \u51b3\u8fd9\u4e2a\u96be\u9898\uff0c\u53ea\u8981\u4f60\u8bb0\u7740\u4eca\u665a\u6211\u5bf9\u4f60\u8bf4\u7684\u8bdd\u5c31\u884c\u4e86\u3002\u2019\u8bf4\u5230\u8fd9\u91cc\uff0c\u5a18\u7684\u9762\u4e0a\u7a81\u7136\u73b0\u51fa\u4e00\u7247\u9ed1 \u6c14\uff0c\u58f0\u97f3\u4e5f\u90fd\u5636\u54d1\u4e86\u3002\u5927\u60ca\u4e4b\u4e0b\uff0c\u6211\u8fde\u5fd9\u62b1\u7740\u6bcd\u4eb2\uff0c\u518d\u6b21\u95ee\u9053\uff1a\u2018\u5a18\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u2019 \u3000\u3000\u201c\u5a18\u7684\u8138\u4e0a\u73b0\u51fa\u7b11\u5bb9\uff0c\u8bf4\u9053\uff1a\u2018\u4f60\u8fdb\u6765\u7684\u65f6\u5019\uff0c\u6211\u53e3\u91cc\u5df2\u7ecf\u542b\u4e86\u4e00\u7c92\u836f\u4e38\u3002\u6211\u5e2e\u4e0d\u4e86\u4f60 \u7684\u5fd9\uff0c\u4f46\u4e5f\u4e0d\u80fd\u505a\u4f60\u7684\u7eca\u811a\u77f3\u3002\u6240\u4ee5\u6211\u5148\u8d70\u4e00\u6b65\uff0c\u89c1\u4f60\u7239\u7239\u53bb\u4e86\u3002\u4f60\u8d76\u5feb\u95ef\u51fa\u53bb\uff0c\u5373\u4f7f\u4e0d\u80fd \u6210\u529f\uff0c\u6b7b\u4e86\u4e5f\u662f\u6211\u7684\u597d\u513f\u5b50\u3002\u4f46\u4f60\u53ef\u5343\u4e07\u4e0d\u8981\u81ea\u5df1\u5bfb\u6b7b\uff01\u2019\u58f0\u97f3\u8d8a\u6765\u8d8a\u662f\u5fae\u5f31\uff0c\u4f46\u6bcf\u4e00\u4e2a\u5b57 \u5374\u50cf\u5de8\u96f7\u6253\u5728\u6211\u7684\u5fc3\u4e0a\u3002\u6211\u8fd9\u624d\u77e5\u9053\u5a18\u662f\u4e3a\u6211\u670d\u4e86\u6bd2\uff01\u6211\u64ac\u5f00\u5979\u7684\u5634\u5df4\uff0c\u4f46\u5df2\u7ecf\u8fdf\u4e86\uff0c\u90a3\u662f \u4e00\u79cd\u5f88\u5389\u5bb3\u7684\u6bd2\u836f\uff0c\u7528\u767d\u8721\u88f9\u4f4f\uff0c\u4f5c\u6210\u836f\u4e38\uff0c\u54ac\u7834\u4e86\u5916\u9762\u7684\u4e00\u5c42\u8721\uff0c\u4e0d\u6d88\u7247\u523b\uff0c\u5c31\u4f1a\u4e2d\u6bd2\u800c \u4ea1\u3002 \u3000\u3000\u201c\u5f53\u65f6\u6211\u60ca\u5f97\u5446\u4e86\uff0c\u5fd8\u8bb0\u4e86\u6211\u7684\u672a\u5a5a\u59bb\u8fd8\u5728\u8eab\u65c1\uff0c\u5ffd\u542c\u5f97\u5979\u8bf4\u9053\uff1a\u2018\u4f60\u5fd8\u8bb0\u4e86\u5a18\u7684\u5429\u5490 \u4e48\uff1f\u4f60\u8fd8\u5446\u5728\u8fd9\u91cc\u505a\u4ec0\u4e48\uff1f\u2019\u6211\u84e6\u7136\u4e00\u7701\uff0c\u8df3\u8d77\u6765\u9053\uff1a\u2018\u4f60\u5462\uff1f\u2019\u8868\u59b9\u8bf4\u9053\uff1a\u2018\u5a46\u5a46\u6c42\u4ec1\u5f97 \u4ec1\uff0c\u505a\u5ab3\u5987\u7684\u5c82\u80fd\u82df\u4e14\u5077\u751f\uff1f\u2019\u7a81\u7136\u4ece\u697c\u4e0a\u8df3\u4e0b\u53bb\uff0c\u6211\u4e00\u628a\u6ca1\u6709\u62c9\u7740\uff0c\u5979\u649e\u5728\u5047\u5c71\u77f3\u4e0a\uff0c\u53d1 \u51fa\u4e00\u58f0\u88c2\u4eba\u5fc3\u80ba\u7684\u547c\u558a\u3002\u6211\u8df3\u4e0b\u53bb\uff0c\u6070\u597d\u8d76\u5f97\u4e0a\u542c\u5979\u6700\u540e\u51e0\u53e5\u8bdd\uff1a\u2018\u5927\u54e5\uff0c\u8bf7\u539f\u8c05\u6211\u4e0d\u80fd\u4f34 \u4f60\u4e86\uff0c\u56e0\u4e3a\uff0c\u56e0\u4e3a\u6211\u4e0d\u60f3\u62d6\u7d2f\u4f60\uff01\u2019 \u3000\u3000\u201c\u6211\u672c\u6765\u662f\u8981\u8d76\u56de\u5bb6\u5b8c\u5a5a\u7684\uff0c\u4e0d\u6599\u4e00\u5915\u4e4b\u95f4\uff0c\u6bcd\u4eb2\u6b7b\u4e86\uff0c\u59bb\u5b50\u4e5f\u6b7b\u4e86\u3002\u4f46\u8868\u59b9\u8bf4\u5f97\u5bf9\uff0c \u5979\u4eec\u4e43\u662f\u6c42\u4ec1\u5f97\u4ec1\uff0c\u5979\u4eec\u662f\u867d\u6b7b\u72b9\u751f\u7684\uff01\u6211\u4e0d\u5fc5\u4e3a\u5979\u4eec\u4f24\u5fc3\uff0c\u6211\u53ea\u5e94\u8be5\u66ff\u5979\u4eec\u62a5\u4ec7\uff01\u201d \u3000\u3000\u8c37\u6db5\u865a\u542c\u5f97\u624b\u5fc3\u634f\u4e86\u4e00\u628a\u51b7\u6c57\uff0c\u7d27\u5f20\u5f97\u51e0\u4e4e\u900f\u4e0d\u8fc7\u6c14\u6765\uff0c\u5fc3\u91cc\u60f3\uff0c\u201c\u6211\u53ea\u9053\u6211\u7684\u906d\u9047\u5df2 \u7ecf\u4e0d\u5e78\uff0c\u8c01\u77e5\u5e08\u7236\u7684\u906d\u9047\u6bd4\u6211\u4e0d\u5e78\u5f97\u591a\uff01\u5f53\u65f6\u4ed6\u7684\u5904\u5883\uff0c\u53ea\u6015\u4e5f\u8981\u6bd4\u6211\u90a3\u665a\u7684\u5904\u5883\u66f4\u4e3a\u51f6 \u9669\uff01\u201d \u3000\u3000\u803f\u5929\u98ce\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u5f53\u65f6\u7684\u5904\u5883\u4e5f\u6ca1\u6709\u7a7a\u6687\u5bb9\u8bb8\u6211\u4f24\u5fc3\u4e86\uff0c\u9633\u5929\u96f7\u7684\u4eba\u5728\u697c\u4e0b\u770b\u5b88\uff0c\u6211 \u4e00\u8df3\u4e0b\u6765\uff0c\u4ed6\u4eec\u4fbf\u5373\u4e00\u62e5\u800c\u4e0a\u3002 \u3000\u3000\u201c\u5f53\u65f6\u6211\u4e5f\u4e0d\u77e5\u54ea\u91cc\u6765\u7684\u795e\u52c7\uff0c\u4e00\u573a\u6076\u6218\uff0c\u7ed9\u6211\u51fb\u6bd9\u4e86\u91d1\u864f\u7684\u4e03\u540d\u9ad8\u624b\uff0c\u8fde\u9633\u5929\u96f7\u4e5f\u7ed9 \u6211\u62fc\u4e86\u4e2a\u4e24\u8d25\u4ff1\u4f24\uff01\u201d \u3000\u3000\u8c37\u6db5\u865a\u62b9\u4e86\u4e00\u989d\u51b7\u6c57\uff0c\u65b9\u59cb\u677e\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u75db\u5feb\uff0c\u75db\u5feb\uff01\u5e08\u7236\uff0c\u4f60\u8fd9\u4e00\u6218\u4e5f\u8db3\u4ee5\u5927 \u5bd2\u654c\u80c6\u4e86\uff01\u4f60\u7684\u4f24\u4f24\u5f97\u91cd\u5417\uff1f\u201d \u3000\u3000\u803f\u5929\u98ce\u9053\uff1a\u201c\u90a3\u4e00\u665a\u771f\u53ef\u4ee5\u8bf4\u5f97\u662f\u6b7b\u91cc\u9003\u751f\uff01\u6211\u7684\u6b66\u529f\u672c\u6765\u662f\u4e0d\u53ca\u9633\u5929\u96f7\u7684\uff0c\u8fde\u63a5\u63a5\u4e86 \u4e09\u6b21\u4ed6\u7684\u5929\u96f7\u638c\uff0c\u5176\u5b9e\u6211\u5df2\u662f\u53d7\u4e86\u5f88\u91cd\u7684\u5185\u4f24\uff0c\u4f46\u4ed6\u5728\u6211\u62fc\u547d\u53cd\u51fb\u4e4b\u4e0b\uff0c\u4e5f\u4e2d\u4e86\u6211\u7684\u4e24\u5251\uff0c \u4ed6\u8fd9\u624d\u4e0d\u6562\u8ffd\u6211\u3002\u201d \u3000\u3000\u201c\u6211\u8eb2\u5230\u6df1\u5c71\u91cc\u81ea\u884c\u533b\u6cbb\uff0c\u533b\u4e86\u4e00\u5e74\uff0c\u8eab\u4f53\u65b9\u624d\u590d\u539f\u3002\u53ef\u662f\u6240\u53d7\u7684\u5185\u4f24\uff0c\u8fc4\u4eca\u4ecd\u662f\u672a\u80fd \u75ca\u6108\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u5403\u60ca\u9053\uff1a\u201c\u5df2\u7ecf\u8fc7\u4e86\u4e8c\u5341\u5e74\u4e86\uff0c\u73b0\u5728\u90fd\u8fd8\u672a\u597d\u5417\uff1f\u201d \u3000\u3000\u803f\u5929\u98ce\u9053\uff1a\u201c\u4f60\u4e0d\u5fc5\u62c5\u5fc3\uff0c\u5bf9\u8eab\u4f53\u5df2\u662f\u6ca1\u6709\u4ec0\u4e48\u5927\u788d\u7684\u4e86\u3002\u53ea\u4e0d\u8fc7\u5e08\u7956\u6240\u4f20\u7684\u4e0a\u4e58\u5185 \u529f\uff0c\u6211\u53ea\u80fd\u6559\u7ed9\u4f60\uff0c\u672c\u8eab\u5374\u662f\u4e0d\u80fd\u7ec3\u4e86\u3002\u8fd9\u4e5f\u662f\u4e3a\u4ec0\u4e48\u6211\u5230\u4e86\u6c5f\u5357\u4e4b\u540e\uff0c\u4ece\u6765\u4e0d\u6562\u5728\u4eba\u524d\u663e \u9732\u8fc7\u6b66\u6280\u7684\u539f\u56e0\u3002\u201d \u3000\u3000\u803f\u5929\u98ce\u53c8\u559d\u4e86\u4e00\u7897\u9152\uff0c\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u80fd\u518d\u7ec3\u4e0a\u4e58\u5185\u529f\uff0c\u8fd9\u8fd8\u4e0d\u6253\u7d27\uff0c\u66f4\u9057\u61be\u7684\u662f\uff0c \u5728\u6211\u517b\u597d\u4e86\u8eab\u4f53\u4e4b\u540e\uff0c\u5317\u65b9\u7684\u5c40\u9762\u66f4\u4e3a\u6076\u5316\uff0c\u6211\u548c\u4e49\u519b\u4e5f\u5931\u6389\u8054\u7edc\u4e86\u3002 \u3000\u3000\u201c\u6211\u5df2\u7ecf\u5217\u5165\u91d1\u864f\u7684\u2018\u94a6\u72af\u2019\u540d\u5355\uff0c\u9633\u5929\u96f7\u53d7\u7684\u4f24\u6bd4\u6211\u8f7b\uff0c\u65e9\u5df2\u597d\u4e86\u3002\u4ed6\u6b63\u5728\u4eb2\u81ea\u7387\u9886 \u5f81\u9a91\uff0c\u5230\u5904\u641c\u67e5\u6211\u7684\u4e0b\u843d\uff0c\u6211\u5728\u5317\u65b9\u5df2\u662f\u6ca1\u6709\u5bb9\u8eab\u4e4b\u5730\u3002 \u3000\u3000\u201c\u6ca1\u5948\u4f55\uff0c\u6211\u53ea\u597d\u9003\u5f80\u6c5f\u5357\u3002\u5f53\u65f6\u6211\u5e74\u7eaa\u8fd8\u8f7b\uff0c\u6b66\u529f\u867d\u7136\u53d7\u635f\uff0c\u80f8\u4e2d\u5c1a\u6709\u4e00\u8154\u70ed\u8840\u3002\u6211 \u4ee5\u4e3a\u671d\u5ef7\u603b\u662f\u8981\u8c0b\u6062\u590d\u4e2d\u539f\u7684\uff0c\u6211\u5bf9\u671d\u5ef7\u62b1\u6709\u5f88\u5927\u7684\u5e0c\u671b\u3002 \u3000\u3000\u201c\u8c01\u77e5\u6211\u5230\u4e34\u5b89\uff0c\u624d\u77e5\u9053\u6211\u60f3\u5f97\u592a\u5929\u771f\u4e86\uff0c\u671d\u5ef7\u4e0a\u4e0b\uff0c\u53ea\u6c42\u82df\u5b89\uff0c\u4e3b\u5f20\u6297\u654c\u7684\u5c06\u9886\uff0c\u4e0d \u662f\u906d\u53d7\u8d2c\u6291\uff0c\u5c31\u662f\u7ed9\u5978\u81e3\u5bb3\u6b7b\u3002\u6211\u5fc3\u7070\u610f\u51b7\u4e4b\u4f59\uff0c\u53ea\u597d\u9690\u59d3\u57cb\u540d\uff0c\u6d41\u6d6a\u6c5f\u6e56\u3002 \u3000\u3000\u201c\u53ef\u662f\u6211\u8eab\u8d1f\u56fd\u4ec7\u5bb6\u6068\uff0c\u6211\u8fd8\u662f\u4e0d\u80fd\u7518\u5fc3\u5c31\u6b64\u57cb\u6ca1\u4e00\u751f\u7684\u3002\u6211\u8fd9\u4e00\u751f\u6050\u6015\u662f\u4e0d\u80fd\u4eb2\u81ea\u624b \u5203\u4ec7\u4eba\u7684\u4e86\uff0c\u56e0\u6b64\u6211\u5c31\u5230\u5904\u7269\u8272\u4f73\u5f92\uff0c\u5e0c\u671b\u6211\u7684\u5f92\u5f1f\u80fd\u591f\u66ff\u6211\u4e86\u6b64\u5fc3\u613f\u3002\u6211\u627e\u4e86\u5341\u5e74\uff0c\u624d\u627e \u5230\u4f60\u505a\u6211\u7684\u5f92\u5f1f\u3002\u4ece\u6b64\uff0c\u6211\u5c31\u628a\u6211\u6bd5\u751f\u7684\u5fc3\u8840\uff0c\u90fd\u653e\u5728\u4f60\u7684\u8eab\u4e86\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u5927\u4e3a\u611f\u52a8\uff0c\u8bf4\u9053\uff1a\u201c\u5f1f\u5b50\u5b9e\u5728\u60ed\u6127\uff0c\u4f60\u8001\u4eba\u5bb6\u5bf9\u6211\u7684\u4e00\u7247\u82e6\u5fc3\uff0c\u6211\u3001\u6211\u4e00\u70b9\u4e5f\u4e0d \u77e5\u9053\u3002\u201d \u3000\u3000\u803f\u5929\u98ce\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u4f60\u7684\u5148\u7956\u672c\u6765\u4e5f\u662f\u5317\u65b9\u4eba\u6c0f\uff0c\u4f60\u7684\u7956\u7236\u8ddf\u968f\u5b8b\u5ba4\u5357\u8fc1\uff0c\u6765\u5230\u6e58\u897f\u843d \u7c4d\u7684\u3002\u4f60\u7684\u7236\u4eb2\u5728\u9003\u96be\u9014\u4e2d\uff0c\u4e0a\u8981\u4f8d\u5949\u8001\u7236\uff0c\u4e0b\u8981\u7167\u987e\u5e7c\u513f\uff0c\u98a0\u6c9b\u6d41\u79bb\uff0c\u4e00\u8def\u4e0a\u4e5f\u4e0d\u77e5\u53d7\u4e86 \u591a\u5c11\u82e6\u695a\uff0c\u56e0\u6b64\u4f0f\u4e0b\u75c5\u6839\u3002\u9003\u5230\u6c5f\u5357\u4e4b\u540e\uff0c\u4e0d\u5230\u4e00\u5e74\uff0c\u4f60\u7684\u7956\u7236\u53bb\u4e16\uff0c\u518d\u8fc7\u4e24\u5e74\uff0c\u4f60\u7684\u7236\u4eb2 \u4e5f\u56e0\u75c5\u4f53\u8651\u5f31\uff0c\u652f\u6491\u4e0d\u4f4f\uff0c\u5f03\u4f60\u800c\u53bb\u4e86\u3002\u6240\u4ee5\uff0c\u8bf4\u8d77\u4f60\u7684\u5bb6\u4e16\uff0c\u4e5f\u662f\u4e0e\u91d1\u864f\u6709\u4e0d\u5171\u6234\u65e0\u4e4b\u4ec7 \u7684\uff0c\u4f60\u77e5\u9053\u4e48\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u53cc\u76ee\u6d41\u6cea\uff0c\u8bf4\u9053\uff1a\u201c\u865a\u513f\u66fe\u542c\u7389\u5cf0\u9053\u957f\u8bf4\u8fc7\uff0c\u5f92\u513f\u4e0d\u6562\u5fd8\u8bb0\u3002\u201d\u539f\u6765\u8c37\u6db5\u865a\u5728 \u7956\u3001\u7236\u53cc\u4ea1\u4e4b\u540e\uff0c\u6210\u4e86\u5b64\u513f\uff0c\u5e78\u5f97\u9644\u8fd1\u7684\u4e00\u4e2a\u9053\u89c2\u6536\u7559\uff0c\u90a3\u4e2a\u9053\u89c2\u7684\u4e3b\u6301\u662f\u9752\u57ce\u6d3e\u540d\u5bbf\u7389\u5cf0 \u9053\u957f\u7684\u5e08\u4f84\u3002\u8c37\u6db5\u865a\u5728\u9053\u89c2\u505a\u4e86\u51e0\u4e2a\u6708\u5c0f\u53ae\uff0c\u7389\u5cf0\u9053\u957f\u6765\u5230\uff0c\u89c1\u4ed6\u8d44\u8d28\u751a\u4f73\uff0c\u53c8\u53ef\u601c\u4ed6\u7684\u8eab \u4e16\uff0c\u8fd9\u624d\u6536\u4e86\u4ed6\u505a\u5f92\u5f1f\uff0c\u5e26\u4e86\u4ed6\u5230\u9752\u57ce\u5c71\u3002 \u3000\u3000\u7389\u5cf0\u9053\u957f\u4e0e\u803f\u5929\u98ce\u76f8\u8bc6\uff0c\u6df1\u77e5\u803f\u5929\u98ce\u7684\u6b66\u5b66\u9020\u8be3\u8fdc\u5728\u81ea\u5df1\u4e4b\u4e0a\uff0c\u53c8\u77e5\u9053\u803f\u5929\u98ce\u6b63\u5728\u7269\u8272 \u4f73\u5f92\uff0c\u56e0\u6b64\u628a\u8fd9\u4e2a\u5f92\u5f1f\u8ba9\u4e86\u7ed9\u4ed6\u3002 \u3000\u3000\u803f\u5929\u98ce\u70b9\u4e86\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u6ca1\u6709\u5fd8\u8bb0\u5c31\u597d\u3002\u201d\u63a5\u7740\u8bf4\u9053\uff0c\u201c\u6c5f\u6e56\u6b66\u6797\u4eba\u7269\u4e4b\u4e2d\uff0c\u53ea\u6709\u7389 \u5cf0\u9053\u957f\u77e5\u9053\u6211\u7684\u6765\u5386\u3002\u4ed6\u8ba4\u4e3a\u4f60\u662f\u53ef\u9020\u4e4b\u6750\uff0c\u56e0\u6b64\u628a\u4f60\u63a8\u8350\u7ed9\u6211\uff0c\u8981\u6211\u505a\u4f60\u7684\u5e08\u7236\uff0c\u8fd9\u56fa\u7136 \u662f\u60f3\u628a\u4f60\u57f9\u517b\u6210\u6750\uff0c\u53e6\u4e00\u65b9\u9762\uff0c\u4e5f\u662f\u7389\u5cf0\u9053\u957f\u8981\u52a9\u6211\u5b8c\u6210\u5fc3\u613f\u3002 \u3000\u3000\u201c\u7389\u5cf0\u9053\u957f\u6cd5\u773c\u65e0\u5dee\uff0c\u4f60\u7684\u786e\u662f\u5929\u751f\u7684\u5b66\u6b66\u6750\u6599\uff0c\u8d44\u8d28\u8fdc\u8fdc\u5728\u6211\u4e4b\u4e0a\u3002\u6211\u6559\u4f60\u7684\uff0c\u4f60\u4e00 \u7ecf\u6307\u70b9\uff0c\u4fbf\u80fd\u9886\u609f\u3002\u4e0d\u662f\u6211\u5938\u8d5e\u4f60\uff0c\u4ee5\u4f60\u73b0\u5728\u7684\u672c\u9886\uff0c\u5df2\u7ecf\u662f\u80dc\u8fc7\u6211\u5c1a\u672a\u53d7\u4f24\u7684\u5f53\u5e74\u4e86\u3002 \u3000\u3000\u201c\u4f46\u662f\uff0c\u4f60\u7684\u8d44\u8d28\u867d\u4f73\uff0c\u5374\u662f\u4ee4\u6211\u597d\u751f\u5931\u671b\uff01\u5c11\u5e74\u4eba\u60c5\u573a\u5931\u610f\uff0c\u96be\u514d\u4f24\u5fc3\u3002\u4f46\u6211\u60f3\u4e0d\u5230 \u4f60\u4e3a\u4e86\u4e00\u4e2a\u5973\u5b50\uff0c\u7adf\u4f1a\u9893\u4e27\u5982\u65af\uff01\u4e8b\u60c5\u8fc7\u53bb\u90fd\u5c06\u8fd1\u4e09\u5e74\u4e86\uff0c\u4f60\u7adf\u7136\u8fd8\u662f\u59d4\u9761\u4e0d\u632f\uff0c\u5509\uff0c\u771f\u662f \u4ee4\u6211\u592a\u5931\u671b\u4e86\uff01\u201d \u3000\u3000\u8c37\u6db5\u865a\u542c\u5f97\u6c57\u6d41\u6d43\u80cc\uff0c\u4f4e\u4e0b\u5934\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u5f92\u5f1f\u5b9e\u5728\u8be5\u6b7b\uff0c\u8f9c\u8d1f\u4e86\u5e08\u5c0a\u5bf9\u6211\u7684\u671f\u671b\u3002\u201d \u3000\u3000\u803f\u5929\u98ce\u8fd9\u624d\u5c55\u989c\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u53ea\u662f\u60f3\u4f60\u632f\u4f5c\u8d77\u6765\uff0c\u5982\u4eca\u9192\u609f\uff0c\u4e3a\u65f6\u672a\u665a\uff01\u4f60\u7684\u6027 \u547d\uff0c\u7559\u53bb\u4e0e\u9791\u5b50\u62fc\u5427\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u5e08\u7236\u6709\u751a\u5dee\u9063\uff0c\u5f1f\u5b50\u4e07\u6b7b\u4e0d\u8f9e\u3002\u201d \u3000\u3000\u803f\u5929\u98ce\u9053\uff1a\u201c\u597d\uff0c\u4f60\u73b0\u5728\u5df2\u7ecf\u597d\u4e86\u3002\u90a3\u4e48\u660e\u5929\u4f60\u5c31\u52a8\u8eab\uff0c\u5230\u5317\u65b9\u53bb\u3002\u6211\u8981\u4f60\u66ff\u6211\u6e05\u7406\u95e8 \u6237\uff01\u201d \u3000\u3000\u8c37\u6db5\u865a\u53c8\u662f\u5174\u594b\uff0c\u53c8\u662f\u62c5\u5fe7\uff0c\u8bf4\u9053\uff1a\u201c\u591a\u8499\u601d\u5e08\u5bbd\u5ba5\uff0c\u8fd8\u628a\u8fd9\u6837\u7684\u5927\u4e8b\u4ed8\u6258\u7ed9\u6211\uff0c\u5f1f\u5b50 \u7eb5\u7136\u7c89\u8eab\u788e\u9aa8\uff0c\u4ea6\u5f53\u5c3d\u529b\u53bb\u505a\u3002\u4f46\u53ea\u6015\u62c5\u5f53\u4e0d\u8d77\uff0c\u8d1f\u4e86\u6069\u5e08\u7684\u671f\u8bb8\u3002\u201d \u3000\u3000\u803f\u5929\u98ce\u9053\uff1a\u201c\u4f60\u7684\u6b66\u529f\u5728\u540e\u8f88\u4e4b\u4e2d\u5df2\u7b97\u5f97\u662f\u51fa\u7c7b\u62d4\u8403\u7684\u4e86\uff0c\u4f46\u8981\u4f60\u53bb\u5bf9\u4ed8\u9633\u5929\u96f7\uff0c\u4f60\u5f53 \u7136\u8fd8\u662f\u6709\u6240\u4e0d\u53ca\u7684\u3002\u4e0d\u8fc7\uff0c\u4f60\u4e5f\u4e0d\u5fc5\u62c5\u5fc3\uff0c\u56e0\u4e3a\u672c\u95e8\u8fd8\u6709\u957f\u8f88\uff0c\u6e05\u7406\u95e8\u6237\u8fd9\u6837\u7684\u5927\u4e8b\uff0c\u4ed6\u51b3 \u4e0d\u4f1a\u8ba9\u4f60\u72ec\u81ea\u62c5\u5f53\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u5e08\u7236\u8bf4\u7684\u53ef\u662f\u4e09\u5e08\u4f2f\u534e\u5929\u8679\u4e48\uff1f\u201d \u3000\u3000\u803f\u5929\u98ce\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4ed6\u867d\u7136\u7a0d\u5acc\u61e6\u5f31\uff0c\u5374\u4e5f\u662f\u4fa0\u4e49\u4e2d\u4eba\u3002\u4ed6\u5f53\u5e74\u4e3a\u4e86\u6015\u9633\u5929\u96f7\uff0c\u4e0d\u77e5\u8dd1 \u5230\u4ec0\u4e48\u5730\u65b9\u8eb2\u4e86\u8d77\u6765\u3002\u4f46\u6211\u6599\u60f3\u9633\u5929\u96f7\u7edd\u4e0d\u4f1a\u653e\u8fc7\u4ed6\u7684\u3002\u8fd9\u4e8c\u5341\u5e74\u6765\uff0c\u4ed6\u4e00\u5b9a\u4e5f\u53d7\u5230\u9633\u5929\u96f7 \u7684\u8bb8\u591a\u8feb\u5bb3\u7684\u4e86\uff0c\u6211\u6df1\u77e5\u4ed6\u7684\u4e3a\u4eba\uff0c\u5728\u96be\u64cd\u80dc\u5238\u4e4b\u524d\uff0c\u6216\u8bb8\u4ed6\u4f1a\u4e0e\u9633\u5929\u96f7\u865a\u4e0e\u59d4\u86c7\uff0c\u5230\u4e86\u5fcd \u65e0\u53ef\u5fcd\u4e4b\u65f6\uff0c\u4ed6\u4e5f\u975e\u594b\u7136\u800c\u8d77\u4e0d\u53ef\u3002 \u3000\u3000\u201c\u4f60\u5230\u4e86\u5317\u65b9\uff0c\u5fc5\u987b\u8bbe\u6cd5\u6253\u542c\u534e\u5e08\u4f2f\u7684\u4e0b\u843d\u3002\u4e8b\u60c5\u867d\u5c5e\u6e3a\u832b\uff0c\u4f46\u4ea6\u5e76\u975e\u6beb\u65e0\u5e0c\u671b\u3002\u4f9d\u6211 \u63a8\u60f3\uff0c\u4ed6\u5bf9\u6e05\u7406\u95e8\u6237\u4e4b\u4e8b\uff0c\u4e00\u5b9a\u4e5f\u662f\u50cf\u6211\u4e00\u6837\uff0c\u65f6\u523b\u4e0d\u80fd\u5fd8\u6000\uff0c\u6211\u8981\u627e\u4ed6\uff0c\u5f53\u7136\u4ed6\u4e5f\u4e00\u5b9a\u60f3 \u8981\u627e\u6211\u3002\u4f60\u5728\u5317\u65b9\u95ef\u9053\uff0c\u4e0d\u59a8\u9732\u51fa\u672c\u95e8\u6b66\u529f\uff0c\u8ba9\u6d88\u606f\u4f20\u5230\u4ed6\u7684\u8033\u6735\uff0c\u4f60\u4e0d\u627e\u4ed6\uff0c\u4ed6\u4e5f\u4f1a\u6765\u627e \u4f60\u4e86\u3002\u201d\u5f53\u4e0b\uff0c\u4eb2\u7b14\u5199\u4e86\u4e00\u5c01\u4e66\u4fe1\uff0c\u4ea4\u7ed9\u4e86\u5f92\u5f1f\u3002 \u3000\u3000\u8c37\u6db5\u865a\u63a5\u53d7\u4e86\u5e08\u7236\u7684\u91cd\u6258\uff0c\u6e21\u8fc7\u957f\u6c5f\uff0c\u4e0d\u77e5\u4e0d\u89c9\u5df2\u662f\u4e00\u5e74\u3002\u5728\u8fd9\u4e00\u5e74\u4e4b\u4e2d\uff0c\u4ed6\u9996\u5148\u9047\u5230 \u7684\u672c\u95e8\u4e2d\u4eba\uff0c\u4e43\u662f\u9633\u65e0\u96f7\u7684\u4f84\u5b50\u9633\u575a\u767d\u3002\u8d77\u521d\u4ed6\u4e0d\u77e5\u9053\u9633\u575a\u767d\u7684\u6765\u5386\uff0c\u8fd8\u66fe\u5728\u6697\u4e2d\u5e2e\u8fc7\u4ed6\u4e00 \u70b9\u5fd9\uff0c\u540e\u6765\u77e5\u9053\u5e2e\u9519\u4e86\u4eba\uff0c\u53c8\u662f\u61ca\u607c\uff0c\u53c8\u662f\u5931\u671b\uff0c\u76f4\u5230\u4eca\u665a\u4ed6\u5728\u53e4\u5bfa\u4e2d\u51fb\u8d25\u4e86\u9633\u575a\u767d\uff0c\u65b9\u59cb \u51fa\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u5728\u51fb\u8d25\u9633\u575a\u767d\u7684\u540c\u65f6\uff0c\u4ed6\u65e0\u610f\u4e2d\u53c8\u5f97\u5230\u4e86\u7b2c\u4e8c\u4e2a\u540c\u95e8\u7684\u6d88\u606f\u3002 \u3000\u3000\u4ed6\u8499\u7740\u9762\u4e0e\u9633\u575a\u767d\u4ea4\u624b\u4e4b\u65f6\uff0c\u9633\u575a\u767d\u628a\u4ed6\u9519\u5f53\u4f5c\u891a\u4e91\u5cf0\uff0c\u53e3\u53e3\u58f0\u58f0\u8bf4\u8981\u62a5\u4e00\u638c\u4e4b\u4ec7\uff0c\u663e \u7136\u8fd9\u4e2a\u891a\u4e91\u5cf0\u4e43\u662f\u548c\u9633\u5929\u96f7\u7236\u5b50\u4f5c\u5bf9\u7684\u4e86\u3002 \u3000\u3000\u201c\u8fd9\u59d3\u891a\u7684\u4e00\u5b9a\u662f\u534e\u5e08\u4f2f\u7684\u5f1f\u5b50\uff0c\u627e\u5230\u4e86\u4ed6\uff0c\u5c31\u53ef\u4ee5\u5f97\u77e5\u4e09\u5e08\u4f2f\u7684\u4e0b\u843d\u3002\u4e3a\u4ec0\u4e48\u6211\u8fd8\u8981 \u8e0c\u8e87\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u662f\u548c\u4e25\u70f7\u7684\u8868\u59b9\u5b5f\u660e\u971e\u540c\u884c\u7684\u3002\u8c37\u6db5\u865a\u4e3a\u4e86\u4e0d\u613f\u89e6\u52a8\u5fc3\u5e95\u7684\u521b\u4f24\uff0c\u4e0d\u613f\u8ba9\u4e25\u70f7\u77e5 \u9053\u4ed6\u8fd8\u6d3b\u5728\u4e16\u4e0a\uff0c\u662f\u4ee5\u4ed6\u624d\u6709\u8e0c\u8e87\u7684\u3002 \u3000\u3000\u6b64\u9645\uff0c\u4ed6\u60f3\u8d77\u4e86\u5e05\u7236\u4ea4\u6258\u7ed9\u4ed6\u7684\u91cd\u4efb\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5e08\u7236\u6559\u8bad\u6211\u4e0d\u8981\u4e3a\u4e86\u513f\u5973\u79c1\u60c5\u8bef\u4e86 \u5927\u4e8b\uff0c\u53ef\u662f\u5982\u4eca\u6211\u82e5\u4e3a\u4e86\u9003\u907f\u4e25\u70f7\uff0c\u4e0d\u53bb\u627e\u891a\u4e91\u5cf0\u7684\u8bdd\uff0c\u8fd9\u5374\u6b63\u662f\u77eb\u6789\u8fc7\u6b63\uff0c\u53cd\u800c\u8bef\u4e86\u5927\u4e8b \u554a\u3002\u201d\u601d\u5ff5\u53ca\u6b64\uff0c\u5fc3\u610f\u7acb\u51b3\uff0c\u4e8e\u662f\u52a0\u5feb\u811a\u6b65\uff0c\u5728\u901a\u5f80\u98de\u9f99\u5c71\u7684\u90a3\u6761\u8def\u4e0a\uff0c\u9053\u8d76\u891a\u4e91\u5cf0\u3002 \u3000\u3000\u98ce\u96ea\u5df2\u7ecf\u6b62\u4e86\uff0c\u6b64\u65f6\u5df2\u662f\u4e09\u66f4\u65f6\u5206\uff0c\u5348\u591c\u8352\u5c71\uff0c\u4e07\u7c41\u4ff1\u5bc2\uff0c\u8c37\u6db5\u865a\u8d70\u5728\u767d\u832b\u832b\u7684\u96ea\u5730 \u4e0a\uff0c\u5fc3\u4e2d\u7684\u70e6\u607c\u5168\u90fd\u51c0\u5316\uff0c\u7075\u53f0\u4e00\u7247\u7a7a\u660e\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u51b0\u96ea\u788e\u88c2\u7684\u201c\u5386\u5386\u201d\u58f0\u54cd\uff0c\u58f0\u97f3\u53ea\u662f\u9690\u7ea6\u53ef\u95fb\uff0c\u82e5\u4e0d\u662f\u5728\u8fd9\u4e07\u7c41\u4ff1\u5bc2\u7684\u65f6\u5206\uff0c\u5f53 \u771f\u4e0d\u6613\u89c9\u5bdf\u3002 \u3000\u3000\u8c37\u6db5\u865a\u662f\u4e2a\u6b66\u5b66\u7684\u5927\u884c\u5bb6\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u591c\u884c\u4eba\u7684\u8f7b\u529f\u53ef\u662f\u9ad8\u660e\u5f97\u5f88 \u554a\uff01\u201d\u6b64\u65f6\u4ed6\u6b63\u8d70\u5230\u6811\u6797\u4e2d\u4e00\u5904\u9690\u853d\u7684\u6240\u5728\uff0c\u6709\u4e24\u5757\u76f8\u5411\u5cd9\u7acb\u7684\u73af\u5f62\u5ca9\u77f3\uff0c\u4e2d\u95f4\u6b63\u597d\u85cf\u8eab\uff0c \u8c37\u6db5\u865a\u56e0\u4e3a\u4e0d\u77e5\u9053\u6765\u4eba\u7684\u8eab\u4efd\uff0c\u4e0d\u60f3\u7ed9\u4ed6\u4eec\u53d1\u73b0\uff0c\u53ea\u597d\u6682\u4e14\u8eb2\u907f\u3002 \u3000\u3000\u521a\u521a\u5c06\u8eab\u85cf\u597d\uff0c\u53ea\u89c1\u4e24\u6761\u9ed1\u5f71\u5df2\u7ecf\u51fa\u73b0\u773c\u524d\uff0c\u540e\u9762\u7684\u90a3\u4e2a\u4eba\u8bf4\u9053\uff1a\u201c\u9c81\u5144\uff0c\u4f60\u8fd9\u8e0f\u96ea\u65e0 \u75d5\u7684\u529f\u592b\u5f53\u771f\u662f\u540d\u4e0d\u865a\u4f20\uff0c\u5c0f\u5f1f\u670d\u4e86\u4f60\u5566\uff0c\u4e0d\u7528\u6bd4\u4e86\u3002\u6b47\u4e00\u6b47\u5427\u3002\u201d \u3000\u3000\u524d\u9762\u90a3\u4eba\u7b11\u9053\uff1a\u201c\u5468\u5144\uff0c\u4f60\u7684\u5185\u529f\u5c0f\u5f1f\u4e5f\u662f\u4f69\u670d\u5f97\u5f88\u3002\u8bba\u8f7b\u529f\u6211\u6216\u8005\u80dc\u4f60\u4e00\u7b79\uff0c\u8bba\u5230\u5185 \u529b\u7684\u60a0\u957f\uff0c\u5c0f\u5f1f\u53ef\u5c31\u7518\u62dc\u4e0b\u98ce\u4e86\u3002\u5018\u82e5\u8d70\u5230\u4e94\u5341\u91cc\u5f00\u5916\uff0c\u5c0f\u5f1f\u4e00\u5b9a\u8ddf\u4e0d\u4e0a\u4f60\u3002\u4e0d\u8fc7\uff0c\u6211\u5012\u4e0d \u662f\u6709\u610f\u548c\u4f60\u6bd4\u8bd5\u7684\uff0c\u54b1\u4eec\u662f\u8981\u5728\u9650\u671f\u4e4b\u5185\uff0c\u8d76\u5230\u98de\u9f99\u5c71\u554a\uff01\u201d \u3000\u3000\u59d3\u5468\u90a3\u4eba\u8bf4\u9053\uff1a\u201c\u4ee5\u54b1\u4eec\u7684\u811a\u7a0b\uff0c\u540e\u5929\u4e00\u5b9a\u53ef\u4ee5\u5230\u8fbe\u98de\u9f99\u5c71\uff0c\u7edd\u4e0d\u81f3\u4e8e\u8bef\u4e86\u5927\u4e8b\u7684\u3002\u8fd9 \u6837\u65e5\u591c\u8d76\u8def\uff0c\u5c0f\u5f1f\u53ef\u662f\u6709\u70b9\u5403\u4e0d\u6d88\u5462\u3002\u201d \u3000\u3000\u59d3\u9c81\u7684\u90a3\u4eba\u9053\uff1a\u201c\u597d\u5427\uff0c\u90a3\u54b1\u4eec\u5c31\u5728\u8fd9\u91cc\u8212\u8212\u670d\u670d\u7684\u7761\u4e0a\u4e00\u89c9\uff0c\u8f6e\u6d41\u770b\u5b88\u3002\u201d \u3000\u3000\u59d3\u5468\u7684\u7b11\u9053\uff1a\u201c\u4f60\u662f\u5728\u51b0\u5929\u96ea\u5730\u8fc7\u60ef\u4e86\u7684\uff0c\u6211\u53ef\u6ca1\u6709\u4f60\u7684\u80fd\u8010\uff0c\u53ef\u4ee5\u5728\u96ea\u5730\u4e0a\u7761\u5f97\u7740 \u89c9\uff0c\u4f60\u4e0d\u8981\u8f83\u91cf\u6211\u4e86\uff0c\u54b1\u4eec\u8fd8\u662f\u804a\u804a\u5929\u5427\u3002\u8fd9\u6b21\u7684\u4e8b\u60c5\uff0c\u771f\u662f\u6709\u70b9\u51fa\u4e4e\u6211\u7684\u610f\u6599\u4e4b\u5916\uff0c\u6211\u60f3 \u4e0d\u5230\u98de\u9f99\u5c71\u7684\u7aa6\u5be8\u4e3b\uff0c\u7adf\u7136\u662f\u6211\u4eec\u7684\u4eba\u3002\u201d \u3000\u3000\u59d3\u9c81\u7684\u8bf4\u9053\uff1a\u201c\u7aa6\u5b89\u5e73\u5916\u8c8c\u7c97\u9c81\uff0c\u5176\u5b9e\u5374\u662f\u6781\u5bcc\u5fc3\u8ba1\u7684\u4e00\u4e2a\u4eba\u3002\u4f60\u522b\u4ee5\u4e3a\u4ed6\u548c\u90a3\u4e9b\u4ea1\u547d \u4fa0\u4e49\u9053\u7684\u4eba\u7269\u5f80\u6765\uff0c\u5c31\u662f\u4ed6\u4eec\u4e00\u8def\u3002\u4ed6\u8fd9\u6837\u505a\uff0c\u5176\u5b9e\u8fd8\u662f\u54b1\u4eec\u7684\u56fd\u5e08\u6697\u4e2d\u6388\u610f\u7684\u5462\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u539f\u6765\u8fd9\u4e24\u4e2a\u4eba\u4e43\u662f\u9633\u5929\u96f7\u7684\u624b\u4e0b\u3002\u5374\u4e0d\u77e5\u9053\u4ed6\u4eec\u8981\u5230\u98de \u9f99\u5c71\u4f5c\u4f55\u52fe\u5f53\uff0c\u597d\uff0c\u4e14\u542c\u542c\u4ed6\u4eec\u8bf4\u4ec0\u4e48\u3002\u201d \u3000\u3000\u59d3\u9c81\u7684\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u4e8b\u60c5\u5f80\u5f80\u6709\u51fa\u4eba\u610f\u6599\u4e4b\u5916\u7684\uff0c\u6bd4\u5982\u8fd9\u4e00\u6b21\u627e\u4e0d\u7740\u9633\u516c\u5b50\uff0c\u53ef\u4e0d\u662f\u4e00 \u4ef6\u602a\u4e8b\u5417\uff1f\u201d \u3000\u3000\u59d3\u5468\u7684\u8bf4\u9053\uff1a\u201c\u662f\u5440\uff0c\u672c\u6765\u8bf4\u597d\u4e86\u662f\u5728\u8d3a\u4e5d\u516c\u5bb6\u91cc\u7b49\u6211\u4eec\u7684\u3002\u5982\u4eca\uff0c\u8fde\u8d3a\u4e5d\u516c\u7adf\u4e5f\u4e0d\u77e5 \u53bb\u5411\uff0c\u6070\u6070\u5728\u54b1\u4eec\u5230\u6765\u7684\u524d\u4e00\u5929\u5c31\u642c\u4e86\u5bb6\uff0c\u4e5f\u4e0d\u77e5\u662f\u51fa\u4e86\u4ec0\u4e48\u4e8b\u60c5\uff1f\u201d \u3000\u3000\u90a3\u59d3\u5468\u7684\u6c49\u5b50\u9053\uff1a\u201c\u9c81\u5927\u54e5\uff0c\u4f60\u7684\u8f7b\u529f\u5728\u54b1\u4eec\u8fd9\u73ed\u4eba\u91cc\u9762\u662f\u6570\u4e00\u6570\u4e8c\u7684\u4e86\uff0c\u4f46\u4e0d\u77e5\u6bd4\u9633 \u516c\u5b50\u5982\u4f55\uff1f\u201d \u3000\u3000\u59d3\u9c81\u7684\u9053\uff1a\u201c\u4e0d\u662f\u6211\u5949\u627f\u9633\u516c\u5b50\uff0c\u6211\u81ea\u95ee\u662f\u6709\u6240\u4e0d\u5982\u3002\u6211\u53f7\u79f0\u8e0f\u96ea\u65e0\u75d5\uff0c\u5176\u5b9e\u8fd8\u5dee\u5f97 \u8fdc\u3002\u6709\u4e00\u6b21\u6211\u548c\u9633\u516c\u5b50\u5728\u96ea\u5c71\u6253\u730e\uff0c\u770b\u4ed6\u8ffd\u6355\u96ea\u9e21\u7684\u8eab\u624b\uff0c\u90a3\u624d\u662f\u771f\u6b63\u7684\u8e0f\u96ea\u65e0\u75d5\u5462\u3002\u4f60\u8001 \u54e5\u7684\u5185\u529f\uff0c\u6050\u6015\u4e5f\u8fd8\u4e0d\u5982\u9633\u516c\u5b50\u5427\uff1f\u201d \u3000\u3000\u59d3\u5468\u7684\u9053\uff1a\u201c\u4e00\u70b9\u4e0d\u9519\u3002\u6709\u4e00\u5929\u6211\u4e0e\u4ed6\u5370\u8bc1\u7ef5\u638c\u51fb\u77f3\u5982\u7c89\u7684\u529f\u592b\uff0c\u4ed6\u5728\u7ef5\u638c\u4e2d\u7528\u4e0a\u4e86\u5929 \u96f7\u529f\uff0c\u4e00\u638c\u51fb\u788e\u5341\u4e8c\u5757\u575a\u5b9e\u7684\u9752\u7816\uff0c\u6211\u53ea\u80fd\u51fb\u788e\u516d\u5757\u3002\u201d \u3000\u3000\u59d3\u9c81\u7684\u53f9\u9053\uff1a\u201c\u54b1\u4eec\u7684\u56fd\u5e08\u53f7\u79f0\u91d1\u56fd\u7b2c\u4e00\u9ad8\u624b\uff0c\u5f53\u771f\u662f\u540d\u4e0d\u865a\u4f20\u3002\u54b1\u4eec\u7684\u672c\u9886\u5728\u6c5f\u6e56\u4e0a \u4e5f\u603b\u7b97\u662f\u8fc7\u5f97\u53bb\u7684\u4e86\uff0c\u5374\u8fde\u4ed6\u7684\u4f84\u5b50\u90fd\u6bd4\u4e0d\u4e0a\u3002\u201d \u3000\u3000\u59d3\u5468\u7684\u9053\uff0c\u201c\u662f\u5440\uff01\u6240\u4ee5\u6211\u8bf4\u4f60\u5176\u5b9e\u7528\u4e0d\u7740\u62c5\u5fc3\uff0e\u5373\u4f7f\u6709\u4ec0\u4e48\u610f\u5916\uff0c\u4ee5\u9633\u516c\u5b50\u8fd9\u6837\u7684\u672c \u9886\uff0c\u6599\u60f3\u4e5f\u4e0d\u4f1a\u51fa\u4e8b\u7684\u3002\u201d \u3000\u3000\u59d3\u9c81\u7684\u9053\uff1a\u201c\u6211\u4e0d\u662f\u6015\u9633\u516c\u5b50\u51fa\u4e8b\uff0c\u6211\u662f\u6015\u8bef\u4e86\u98de\u9f99\u5c71\u7684\u5927\u4e8b\u3002\u201d \u3000\u3000\u59d3\u5468\u7684\u9053\uff1a\u201c\u6211\u6b63\u60f3\u95ee\u4f60\uff0c\u56fd\u5e08\u8981\u54b1\u4eec\u4f1a\u540c\u4ed6\u7684\u4f84\u5b50\uff0c\u8d76\u5f80\u98de\u9f99\u5c71\u53bb\uff0c\u7a76\u7adf\u662f\u4e3a\u4e86\u4ec0\u4e48 \u4e8b\u60c5\uff1f\u201d \u3000\u3000\u59d3\u9c81\u7684\u9053\uff1a\u201c\u4f60\u542c\u8fc7\u674e\u601d\u5357\u8fd9\u4e2a\u540d\u5b57\u4e48\uff1f\u201d \u3000\u3000\u59d3\u5468\u7684\u9053\uff1a\u201c\u674e\u601d\u5357\uff1f\u8fd9\u540d\u5b57\u597d\u719f\uff01\u5f85\u6211\u60f3\u60f3\u3002\u554a\uff0c\u5bf9\u4e86\uff0c\u524d\u51e0\u5929\u6211\u542c\u5f97\u9ed1\u9053\u4e0a\u7684\u670b\u53cb \u8bf4\u8d77\uff0c\u7eff\u6797\u65b0\u4efb\u7684\u76df\u4e3b\uff0c\u8fd9\u76df\u4e3b\u7684\u540d\u5b57\u597d\u4f3c\u5c31\u53eb\u505a\u674e\u601d\u5357\u3002\u201d \u3000\u3000\u59d3\u9c81\u7684\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u56fd\u5e08\u5c31\u662f\u8981\u54b1\u4eec\u53bb\u5e2e\u5fd9\u7aa6\u5be8\u4e3b\uff0c\u5bf9\u4ed8\u674e\u601d\u5357\u8fd9\u5c0f\u5b50\u7684\u3002\u201d \u3000\u3000\u59d3\u5468\u7684\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u548c\u54b1\u4eec\u7684\u56fd\u5e08\u6709\u4f55\u4ec7\u6028\uff1f\u201d \u3000\u3000\u59d3\u9c81\u7684\u9053\uff1a\u201c\u6211\u4eec\u7684\u56fd\u5e08\u4e0e\u4ed6\u662f\u5f80\u65e5\u65e0\u4ec7\uff0c\u8fd1\u65e5\u65e0\u51a4\u3002\u201d \u3000\u3000\u59d3\u5468\u7684\u9053\uff1a\u201c\u90a3\u5374\u4e3a\u4f55\u975e\u8981\u5343\u65b9\u767e\u8ba1\u5c06\u4ed6\u9664\u53bb\u4e0d\u53ef\uff1f\u201d \u3000\u3000\u59d3\u9c81\u7684\u9053\uff1a\u201c\u8fd9\u662f\u4e00\u4e2a\u7edd\u5927\u7684\u673a\u5bc6\uff0c\u8bf4\u7ed9\u4f60\u542c\u4e0d\u6253\u7d27\uff0c\u4f60\u53ef\u4e0d\u8bb8\u6cc4\u6f0f\u51fa\u53bb\uff01\u201d \u3000\u3000\u59d3\u5468\u7684\u9053\uff1a\u201c\u9c81\u5927\u54e5\uff0c\u4f60\u662f\u56fd\u5e08\u7684\u5fc3\u8179\uff0c\u6211\u8ddf\u968f\u56fd\u5e08\u4e5f\u6709\u591a\u5e74\uff0c\u96be\u9053\u4f60\u8fd8\u4fe1\u6211\u4e0d\u8fc7\u3002\u201d \u3000\u3000\u59d3\u9c81\u7684\u9053\uff1a\u201c\u56fd\u5e08\u5c31\u662f\u56e0\u4e3a\u77e5\u9053\u4f60\u5bf9\u4ed6\u7684\u5fe0\u5fc3\uff0c\u6240\u4ee5\u624d\u53eb\u6211\u628a\u4f60\u627e\u6765\uff0c\u4e00\u540c\u53bb\u529e\u8fd9\u6869\u4e8b \u7684\u3002\u201d\u539f\u6765\u98de\u9f99\u5c71\u7684\u7aa6\u5be8\u4e3b\u8bf7\u6c42\u9633\u5929\u96f7\u6d3e\u4eba\u5e2e\u5fd9\u4e4b\u65f6\uff0c\u8fd9\u59d3\u5468\u7684\u6b63\u5728\u5916\u5730\u51fa\u5dee\uff0c\u4ed6\u662f\u5949\u4e86\u9633 \u5929\u96f7\u7684\u624b\u4ee4\u4e34\u65f6\u8c03\u6d3e\u7684\u3002\u7ed9\u9633\u5929\u96f7\u4f20\u4ee4\u7684\u4eba\uff0c\u5c31\u662f\u8fd9\u59d3\u9c81\u7684\u6c49\u5b50\u3002 \u3000\u3000\u59d3\u9c81\u7684\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u7684\u56fd\u5e08\u548c\u674e\u601d\u5357\u867d\u7136\u6ca1\u6709\u51a4\u4ec7\uff0c\u4f46\u8fd9\u5c0f\u5b50\u5374\u662f\u8499\u53e4\u7a9d\u9614\u53f0\u5927\u6c57 \u6240\u8981\u7684\u4eba\u3002\u8fd9\u53ef\u660e\u767d\u4e86\u5427\uff1f\u201d \u3000\u3000\u59d3\u5468\u7684\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\u3002\u56fd\u5e08\u7684\u6df1\u8c0b\u8fdc\u8651\uff0c\u5f53\u771f\u662f\u4ee4\u4eba\u4f69\u670d\u3002\u201d \u3000\u3000\u59d3\u9c81\u7684\u9053\uff1a\u201c\u53ef\u4e0d\u662f\u5417\uff0c\u4ed6\u4e00\u9762\u662f\u91d1\u56fd\u7684\u56fd\u5e08\uff0c\u53e6\u4e00\u9762\u53c8\u548c\u8499\u53e4\u5927\u6c57\u6302\u4e0a\u4e86\u94a9\uff0c\u5c06\u6765\u4e0d \u8bba\u54ea\u4e00\u65b9\u5f97\u80dc\uff0c\u4ed6\u7684\u5730\u4f4d\u90fd\u662f\u4e0d\u4f1a\u52a8\u6447\u7684\u4e86\u3002\u201d \u3000\u3000\u59d3\u5468\u7684\u9053\uff1a\u201c\u8499\u53e4\u94c1\u9a91\uff0c\u65e0\u654c\u5929\u4e0b\u3002\u770b\u6765\u4e0d\u51fa\u6570\u5e74\uff0c\u4e2d\u539f\u5c31\u8981\u6613\u4e3b\u3002\u201d \u3000\u3000\u59d3\u9c81\u7684\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6240\u4ee5\u6211\u8bf4\uff0c\u54b1\u4eec\u56fd\u5e08\u7237\u8fd9\u7740\u68cb\u662f\u8d70\u5f97\u5bf9\u3002\u201d \u3000\u3000\u59d3\u5468\u7684\u8ddf\u7740\u7b11\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u674e\u601d\u5357\u5c31\u662f\u54b1\u4eec\u56fd\u5e08\u6240\u8981\u9493\u7684\u5927\u9c7c\u3002\u4f46\u6211\u6709\u4e00\u4e8b\u4e0d\u660e\uff0c\u674e \u601d\u5357\u65e2\u7136\u662f\u65b0\u4efb\u7684\u7eff\u6797\u76df\u4e3b\uff0c\u60f3\u6765\u4e0d\u81f4\u592a\u8fc7\u7cca\u6d82\uff0c\u4f55\u4ee5\u4ed6\u4f1a\u4e0a\u94a9\uff1f\u201d \u3000\u3000\u59d3\u9c81\u7684\u9053\uff1a\u201c\u8fd9\u6761\u5927\u9c7c\u662f\u65e0\u610f\u4e2d\u4e0a\u94a9\u7684\u3002\u98de\u9f99\u5c71\u7684\u7aa6\u5be8\u4e3b\u8d77\u521d\u672c\u662f\u8981\u9493\u7405\u739b\u5c71\u5c60\u767e\u57ce\u7684 \u5973\u513f\u5c60\u51e4\u7684\u3002\u6070\u597d\u674e\u601d\u5357\u8fd9\u5c0f\u5b50\u65b0\u4efb\u4e86\u7eff\u6797\u76df\u4e3b\uff0c\u4ed6\u5927\u7ea6\u662f\u60f3\u628a\u7aa6\u5b89\u5e73\u6536\u4e3a\u5df1\u7528\uff0c\u6240\u4ee5\u5c31\u66ff \u5c60\u51e4\u6765\u4e86\u3002\u4ed6\u867d\u7136\u5e76\u4e0d\u7cca\u6d82\uff0c\u4f46\u5374\u600e\u77e5\u7aa6\u5b89\u5e73\u65e9\u5df2\u662f\u54b1\u4eec\u56fd\u5e08\u7684\u4eba\u5462\uff1f\u201d \u3000\u3000\u59d3\u5468\u7684\u9053\uff1a\u201c\u542c\u8bf4\u5c60\u51e4\u9887\u6709\u7f8e\u540d\uff0c\u7aa6\u5b89\u5e73\u5b89\u6392\u9677\u9631\uff0c\u8bbe\u8ba1\u64d2\u5979\uff0c\u83ab\u975e\u662f\u770b\u4e0a\u5979\u4e48\uff1f\u4f46\u73b0 \u5728\u9493\u4e0a\u4e86\u674e\u601d\u5357\uff0c\u7aa6\u5b89\u5e73\u5c82\u4e0d\u662f\u53c8\u8981\u5931\u671b\u4e86\uff1f\u201d \u3000\u3000\u59d3\u9c81\u7684\u7b11\u9053\uff0c\u201c\u5468\u5144\uff0c\u4f60\u751f\u5e73\u597d\u8272\uff0c\u4e5f\u602a\u4e0d\u5f97\u4f60\u60f3\u5230\u5939\u7f1d\u91cc\u53bb\u3002\u4f46\u4f60\u8fd9\u4e48\u8bf4\uff0c\u624d\u771f\u662f\u7cca \u6d82\u4e86\uff01\u201d \u3000\u3000\u59d3\u5468\u7684\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u5176\u4e2d\u53e6\u6709\u539f\u56e0\uff1f\u201d \u3000\u3000\u59d3\u9c81\u7684\u9053\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\uff0c\u5c60\u51e4\u7684\u884c\u5f84\u548c\u5979\u7684\u54e5\u54e5\u5c60\u9f99\u5168\u4e0d\u4e00\u6837\uff01\u5c60\u9f99\u4e0e\u54b1\u4eec\u7684\u56fd\u5e08\u867d \u7136\u662f\u6709\u6740\u7236\u4e4b\u4ec7\uff0c\u4f46\u4ed6\u4eec\u8d70\u7684\u5374\u662f\u540c\u4e00\u6761\u8def\uff0c\u4ed6\u4e5f\u662f\u548c\u8499\u53e4\u4eba\u65e9\u5df2\u6302\u4e0a\u94a9\u7684\u3002\u5c60\u51e4\u5c31\u4e0d\u540c\uff0c \u5979\u4e0d\u4f46\u8981\u62a5\u6740\u7236\u4e4b\u4ec7\uff0c\u800c\u4e14\u5979\u8fd8\u662f\u4e49\u519b\u7684\u9996\u9886\u3002\u7aa6\u5b89\u5e73\u82e5\u80fd\u5c06\u5979\u64d2\u83b7\uff0c\u4e00\u65b9\u9762\u53ef\u4ee5\u8ba8\u597d\u5c60 \u9f99\uff0c\u4e00\u65b9\u9762\u53c8\u662f\u66ff\u8499\u53e4\u7684\u5927\u519b\u6e05\u9664\u969c\u788d\uff0c\u4e00\u4e3e\u4e24\u5f97\uff0c\u5c82\u4e0d\u7f8e\u54c9\uff1f \u3000\u3000\u201c\u4f46\u73b0\u5728\u9493\u4e0a\u4e86\u674e\u601d\u5357\uff0c\u8fd9\u53c8\u66f4\u80dc\u4e8e\u5c60\u51e4\u4e86\u3002\u4ed6\u6b22\u559c\u8fd8\u6765\u4e0d\u53ca\u5462\uff0c\u54ea\u91cc\u8fd8\u4f1a\u5931\u671b\uff1f\u201d \u3000\u3000\u8c37\u6db5\u865a\u8eb2\u5728\u5ca9\u77f3\u540e\u9762\uff0c\u5468\u3001\u9c81\u4e8c\u4eba\u5c31\u5728\u524d\u9762\u4e0d\u8fdc\u4e4b\u5904\u8bf4\u8bdd\uff0c\u8c37\u6db5\u865a\u542c\u5230\u8fd9\u91cc\uff0c\u4e0d\u89c9\u6012\u706b \u4e2d\u70e7\uff0c\u5fc3\u91cc\u60f3\u9053\uff0c\u201c\u597d\u4e2a\u6bd2\u8fa3\u7684\u9634\u8c0b\uff01\u8fd9\u4ef6\u4e8b\u6bd4\u627e\u891a\u5e08\u5144\u66f4\u7d27\u8981\uff0c\u6211\u4e0e\u674e\u601d\u5357\u867d\u7136\u7d20\u4e0d\u76f8 \u8bc6\uff0c\u4f46\u4ed6\u65e2\u662f\u4e49\u519b\u7684\u76df\u4e3b\uff0c\u6211\u5c31\u4e0d\u80fd\u8ba9\u4ed6\u843d\u5165\u5978\u4eba\u7684\u9677\u9631\uff01\u8fd9\u4e24\u4eba\u7684\u6b66\u529f\u770b\u6765\u4e0d\u5f31\uff0c\u6211\u4e0d\u77e5 \u80fd\u4e0d\u80fd\u80dc\u8fc7\u4ed6\u4eec\uff1f\u4f46\u5c31\u662f\u6740\u4e86\u4ed6\u4eec\uff0c\u4e5f\u4e0d\u662f\u6700\u597d\u7684\u529e\u6cd5\uff01\u201d \u3000\u3000\u59d3\u9c81\u7684\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u7aa6\u5be8\u4e3b\u5df2\u7ecf\u51c6\u5907\u4e86\u4e00\u79cd\u65e0\u8272\u65e0\u5473\u7684\u8499\u6c57\u836f\uff0c\u53ea\u5f85\u674e\u601d\u5357\u6765\u5230\uff0c\u5c31\u7528 \u5728\u63a5\u98ce\u9152\u4e0a\u6765\u5bf9\u4ed8\u4ed6\u3002\u563f\u563f\uff0c\u53ea\u8981\u4ed6\u9152\u4e00\u6cbe\u5507\uff0c\u5c31\u53ef\u4ee5\u4e0d\u8d39\u5439\u7070\u4e4b\u529b\u5c06\u4ed6\u64d2\u4e86\u3002\u201d \u3000\u3000\u59d3\u5468\u7684\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u8fd8\u4f55\u5fc5\u5174\u5e08\u52a8\u4f17\uff0c\u8981\u54b1\u4eec\u8001\u8fdc\u7684\u8d76\u53bb\u5e2e\u5fd9\uff1f\u201d \u3000\u3000\u59d3\u9c81\u7684\u9053\uff1a\u201c\u674e\u601d\u5357\u662f\u8499\u53e4\u5927\u6c57\u6240\u8981\u7684\u4eba\uff0c\u5173\u7cfb\u975e\u540c\u5c0f\u53ef\u3002\u6240\u4ee5\u7b97\u76d8\u867d\u7136\u662f\u8fd9\u6837\u6253\uff0c\u4f46 \u4e5f\u5f97\u9884\u9632\u4e07\u4e00\u7ed9\u4ed6\u8bc6\u7834\uff0c\u4e0d\u80af\u559d\u9152\uff0c\u90a3\u5c31\u8981\u52a8\u6b66\u4e86\u3002\u201d \u3000\u3000\u59d3\u5468\u7684\u9053\uff1a\u201c\u674e\u601d\u5357\u8fd9\u5c0f\u5b50\u672c\u9886\u5982\u4f55\u3002\u201d \u3000\u3000\u59d3\u9c81\u7684\u9053\uff1a\u201c\u4ed6\u80fd\u591f\u505a\u7eff\u6797\u7684\u76df\u4e3b\uff0c\u60f3\u6765\u4e00\u5b9a\u4e0d\u5f31\u3002\u6240\u4ee5\u9633\u56fd\u5e08\u8fd8\u6050\u6015\u54b1\u4eec\u5bf9\u4ed8\u4e0d\u4e86\uff0c \u8981\u4ed6\u7684\u4f84\u5b50\u4e5f\u4eb2\u81ea\u51fa\u9a6c\u5462\u3002\u201d \u3000\u3000\u59d3\u5468\u7684\u9053\uff1a\u201c\u542c\u8bf4\u4ed6\u5e74\u7eaa\u4e0d\u8fc7\u4e8c\u5341\u6765\u5c81\uff0c\u6211\u4e0d\u76f8\u4fe1\u4ed6\u7684\u672c\u9886\u80fd\u5728\u9633\u516c\u5b50\u4e4b\u4e0a\u3002\u201d \u3000\u3000\u59d3\u9c81\u7684\u9053\uff1a\u201c\u53ef\u662f\u73b0\u5728\u627e\u4e0d\u7740\u9633\u516c\u5b50\uff0c\u53ea\u80fd\u7531\u54b1\u4eec\u5bf9\u4ed8\u4e86\u3002\u201d\u59d3\u5468\u7684\u9053\uff1a\u201c\u8bba\u672c\u9886\u54b1\u4eec \u867d\u7136\u6bd4\u4e0d\u4e0a\u9633\u516c\u5b50\uff0c\u4f46\u82e5\u662f\u54b1\u4eec\u4e8c\u4eba\u8054\u624b\uff0c\u5374\u4e5f\u672a\u5fc5\u5c31\u4e0d\u5982\u4ed6\u4e86\u3002\u201d \u3000\u3000\u59d3\u9c81\u7684\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u54b1\u4eec\u4e8c\u4eba\u8054\u624b\uff0c\u5927\u7ea6\u662f\u8981\u6bd4\u9633\u516c\u5b50\u5f3a\u4e9b\u3002\u201d \u3000\u3000\u59d3\u5468\u7684\u9053\uff1a\u201c\u90a3\u4e48\u8fd8\u6015\u5bf9\u4ed8\u4e0d\u4e86\u674e\u601d\u5357\u8fd9\u5c0f\u5b50\u5417\uff1f\u4f55\u51b5\u7aa6\u5b89\u5e73\u4e5f\u662f\u7eff\u6797\u4e2d\u4e00\u7b49\u4e00\u7684\u597d\u624b \u5462\u3002\u56fd\u5e08\u4e5f\u672a\u514d\u5c0f\u89d1\u4e86\u54b1\u4eec\u4e86\uff01\u201d \u3000\u3000\u59d3\u9c81\u7684\u9053\uff1a\u201c\u6599\u654c\u4e0d\u59a8\u4ece\u5bbd\uff0c\u987b\u77e5\u8fd9\u6837\u7684\u5927\u4e8b\u662f\u7edd\u4e0d\u53ef\u6709\u5931\u7684\u3002\u4f55\u51b5\u674e\u601d\u5357\u4e5f\u672a\u5fc5\u662f\u5355 \u5200\u8d74\u4f1a\u3002\u201d \u3000\u3000\u59d3\u5468\u7684\u9053\uff1a\u201c\u81ea\u5c60\u767e\u57ce\u53bb\u4e16\u4e4b\u540e\uff0c\u7eff\u6797\u4e2d\u7684\u9ad8\u624b\uff0c\u9664\u4e86\u6df3\u4e8e\u5468\u7236\u5b50\u548c\u5c60\u9f99\u5916\uff0c\u6700\u591a\u518d\u52a0 \u4e0a\u4e00\u4e2a\u516b\u4ed9\u5251\u67f3\u6d1e\u5929\uff0c\u8fd9\u56db\u4e2a\u4eba\u662f\u54b1\u4eec\u6bd4\u4e0d\u4e0a\u7684\uff0c\u5176\u4ed6\u7684\u4eba\uff0c\u4e0d\u662f\u6211\u5938\u53e3\uff0c\u6211\u8fd8\u4e0d\u66fe\u5c06\u4ed6\u4eec \u653e\u5728\u773c\u5185\u5462\u3002\u201d \u3000\u3000\u59d3\u9c81\u7684\u7b11\u9053\uff1a\u201c\u5468\u5927\u54e5\uff0c\u4f60\u662f\u53ea\u77e5\u5176\u4e00\uff0c\u4e0d\u77e5\u5176\u4e8c\uff0c\u6211\u8bf4\u51fa\u4e00\u4e2a\u4eba\u6765\uff0c\u6050\u6015\u4f60\u5c31\u4e0d\u80fd\u4e0d \u53e6\u773c\u76f8\u89d1\u4e86\u3002\u201d \u3000\u3000\u59d3\u5468\u7684\u9053\uff1a\u201c\u54e6\uff0c\u90a3\u662f\u8c01\u4eba\uff0c\u5012\u8981\u8bf7\u6559\u3002\u201d \u3000\u3000\u59d3\u9c81\u7684\u9053\uff1a\u201c\u8fd9\u4eba\u5e76\u975e\u7eff\u6797\u4eba\u7269\uff0c\u4f46\u5374\u662f\u5c60\u767e\u57ce\u751f\u524d\u7684\u597d\u53cb\uff0c\u548c\u7405\u739b\u5c71\u6709\u6781\u5176\u6df1\u539a\u7684\u4ea4 \u60c5\u3002\u201d \u3000\u3000\u59d3\u5468\u7684\u5927\u5403\u4e00\u60ca\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u53ef\u662f\u53f7\u79f0\u6c5f\u5357\u5927\u4fa0\u7684\u5b5f\u5c11\u521a\u4e48\uff1f\u4ed6\u4e5f\u6765\u4e86\uff1f\u201d \u3000\u3000\u59d3\u9c81\u7684\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4ed6\u4e5f\u6765\u4e86\u3002\u201d \u3000\u3000\u59d3\u5468\u7684\u82e6\u7740\u8138\u9053\uff1a\u201c\u5047\u5982\u662f\u4ed6\u966a\u540c\u674e\u601d\u5357\u53bb\u98de\u9f99\u5c71\uff0c\u54b1\u4eec\u4e8c\u4eba\u52a0\u4e0a\u4e86\u9633\u516c\u5b50\uff0c\u6050\u6015\u4e5f\u672a \u5fc5\u662f\u4ed6\u4eec\u7684\u5bf9\u624b\u4e86\u3002\u201d \u3000\u3000\u59d3\u9c81\u7684\u7b11\u9053\uff1a\u201c\u4f60\u4e5f\u7528\u4e0d\u7740\u5c31\u8fd9\u6837\u5bb3\u6015\uff0c\u76c2\u5c11\u521a\u8fd9\u8001\u513f\u53e6\u5916\u8fd8\u6709\u4eba\u5bf9\u4ed8\uff0c\u65e0\u987b\u54b1\u4eec\u64cd \u5fc3\uff0c\u7aa6\u5b89\u5e73\u53ea\u662f\u8981\u54b1\u4eec\u53bb\u5bf9\u4ed8\u674e\u601d\u5357\u3002\u201d \u3000\u3000\u59d3\u5468\u7684\u9053\uff1a\u201c\u6df3\u4e8e\u5468\u4e48\uff1f\u201d \u3000\u3000\u59d3\u9c81\u7684\u7b11\u9053\uff1a\u201c\u5468\u5927\u54e5\uff0c\u4f60\u7684\u6d88\u606f\u592a\u4e0d\u591f\u7075\u901a\u4e86\uff0c\u6df3\u4e8e\u5468\u5728\u8fd9\u6b21\u7eff\u6797\u5927\u4f1a\u4e4b\u4e2d\uff0c\u5c31\u662f\u56e0 \u4e3a\u8d25\u5728\u5b5f\u5c11\u521a\u624b\u4e0b\uff0c\u505a\u4e0d\u6210\u76df\u4e3b\u7684\u3002\u5982\u4f55\u8fd8\u80fd\u7528\u6df3\u4e8e\u5468\u53bb\u5bf9\u4ed8\u4ed6\uff1f\u201d \u3000\u3000\u59d3\u5468\u7684\u9053\uff1a\u201c\u90a3\u4e48\u8fd8\u6709\u8c01\u80fd\u591f\u5bf9\u4ed8\u5f97\u4e86\u5b5f\u5c11\u521a\uff1f\u201d \u3000\u3000\u59d3\u9c81\u7684\u9053\uff1a\u201c\u4e0d\u80fd\u529b\u654c\uff0c\u96be\u9053\u4e0d\u4f1a\u667a\u53d6\u5417\uff1f\u201d \u3000\u3000\u59d3\u5468\u7684\u9053\uff1a\u201c\u4f60\u53c8\u8bf4\u8981\u9884\u9632\u4e07\u4e00\u3002\u5b5f\u5c11\u521a\u662f\u8001\u6c5f\u6e56\uff0c\u60f3\u5fc5\u6bd4\u674e\u601d\u5357\u66f4\u8981\u7cbe\u660e\uff0c\u5982\u679c\u4ed6\u8bc6 \u7834\u4e86\u7aa6\u5be8\u4e3b\u7684\u8499\u6c57\u836f\u2026\u2026\u201d \u3000\u3000\u59d3\u9c81\u7684\u7b11\u9053\uff1a\u201c\u6211\u8bf4\u7684\u667a\u53d6\uff0c\u4e0d\u4ec5\u662f\u6307\u8499\u6c57\u836f\u4e00\u9879\u554a\u3002\u4e0d\u8fc7\uff0c\u5f53\u7136\u4e5f\u8fd8\u662f\u8981\u9884\u9632\u4e07\u4e00 \u7684\uff0c\u5426\u5219\u56fd\u5e08\u4f55\u5fc5\u8981\u54b1\u4eec\u4f1a\u540c\u4e86\u4ed6\u7684\u4f84\u5b50\u4e00\u9053\u53bb\u5462\uff01\u201d \u3000\u3000\u59d3\u5468\u7684\u9053\uff1a\u201c\u9c81\u5927\u54e5\uff0c\u4f60\u5feb\u70b9\u63ed\u5f00\u8c1c\u5e95\u5427\uff01\u95f2\u8bdd\u8bf7\u4e0d\u5fc5\u591a\u8bf4\u4e86\u3002\u201d \u3000\u3000\u59d3\u9c81\u7684\u6162\u6761\u65af\u7406\u5730\u8bf4\u9053\uff1a\u201c\u77a7\u4f60\u6025\u6210\u8fd9\u4e2a\u6837\u513f\uff0c\u6211\u5012\u662f\u4e0d\u4fbf\u5356\u5173\u5b50\u4e86\u3002\u4f60\u53ef\u77e5\u9053\u6709\u4e2a\u53f7 \u79f0\u5ddd\u897f\u5927\u4fa0\u7684\u4e25\u58f0\u6d9b\u545c\uff1f\u201d \u3000\u3000\u59d3\u5468\u7684\u66f4\u662f\u5403\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u542c\u8bf4\u4e25\u58f0\u6d9b\u5728\u6c5f\u5357\u7684\u6b66\u6797\u4e2d\u4e5f\u662f\u6709\u6570\u7684\u4eba\u7269\uff0c\u4e0d\u8fc7\u4ed6\u8db3\u8ff9\u4ece \u6765\u4e0d\u5230\u957f\u6c5f\u4ee5\u5317\uff0c\u96be\u9053\u4ed6\u4e5f\u6765\u4e86\uff1f\u201d \u3000\u3000\u59d3\u9c81\u7684\u9053\uff1a\u201c\u4e25\u58f0\u6d9b\u6ca1\u6709\u6765\uff0c\u4ed6\u7684\u5973\u513f\u6765\u4e86\u3002\u201d \u3000\u3000\u59d3\u5468\u7684\u9053\uff1a\u201c\u4e25\u58f0\u6d9b\u7684\u5973\u513f\u548c\u54b1\u4eec\u8fd9\u4ef6\u4e8b\u60c5\u53c8\u4f55\u5e72\uff1f\u201d \u3000\u3000\u59d3\u9c81\u7684\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u77e5\u9053\u4e25\u58f0\u6d9b\u662f\u5b5f\u5c11\u521a\u7684\u59d0\u592b\u5417\uff1f\u563f\uff0c\u563f\uff0c\u8981\u5bf9\u4ed8\u5b5f\u5c11\u521a\uff0c\u53ef\u5c31\u7528\u5f97 \u7740\u8fd9\u5973\u5a03\u513f\u4e86\uff01\u201d \u3000\u3000\u8c37\u6db5\u865a\u542c\u5230\u8fd9\u91cc\uff0c\u4e0d\u7981\u5927\u5403\u4e00\u60ca\uff0c\u56db\u5e74\u6765\u4ed6\u4e00\u76f4\u8eb2\u907f\u7740\u4e25\u70f7\uff0c\u60f3\u4e0d\u5230\u5982\u4eca\u4e25\u70f7\u4e5f\u5230\u5317\u65b9 \u6765\u3002\u201c\u5979\u7684\u7236\u6bcd\u600e\u4f1a\u8ba9\u5979\u72ec\u81ea\u4e00\u4eba\u6df1\u5165\u654c\u533a\uff1f\u96be\u9053\u662f\u5979\u5ac1\u4e86\u4eba\u4e86\uff1f\u4e0d\uff0c\u4e0d\uff0c\u4e00\u5b9a\u4e0d\u4f1a\u7684\uff01\u7ecf \u8fc7\u4e86\u90a3\u665a\u7684\u4e8b\u60c5\uff0c\u5979\u600e\u80fd\u591f\u8fd8\u5ac1\u7ed9\u5f20\u5143\u5409\u5462\uff1f\u53c8\u96be\u9053\u662f\u5979\u542c\u5230\u4e86\u6211\u7684\u6d88\u606f\uff0c\u4ece\u5bb6\u4e2d\u79c1\u9003\u51fa\u6765 \u627e\u6211\u7684\u4e48\uff1f\u201d \u3000\u3000\u8c37\u6db5\u865a\u5fc3\u4e71\u5982\u9ebb\uff0c\u4e0d\u89c9\u547c\u5438\u7d27\u4fc3\uff0c\u53d1\u51fa\u8f7b\u5fae\u7684\u5598\u58f0\u3002\u59d3\u9c81\u7684\u5ffd\u5730\u559d\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u8c37\u6db5\u865a\u5403\u4e86\u4e00\u60ca\uff0c\u4ee5\u4e3a\u8fd9\u4e24\u4eba\u53d1\u73b0\u4e86\u81ea\u5df1\uff0c\u6b63\u8981\u633a\u8eab\u800c\u51fa\u3002\u5ffd\u542c\u5f97\u6709\u4eba\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u4eec \u4e24\u4f4d\u8ba4\u4e0d\u5f97\u4e86\u6211\u4e48\uff1f\u201d \u3000\u3000\u8c37\u6db5\u865a\u4ece\u77f3\u9699\u671b\u51fa\u53bb\uff0c\u53ea\u89c1\u96ea\u5730\u4e0a\u51fa\u73b0\u4e86\u4e00\u7537\u4e00\u5973\uff0c\u90a3\u5973\u7684\u6b63\u662f\u5b5f\u660e\u971e\u3002\u8c37\u6db5\u865a\u5fc3\u91cc\u60f3 \u9053\uff1a\u201c\u8fd9\u7537\u7684\u60f3\u5fc5\u5c31\u662f\u891a\u4e91\u5cf0\u4e86\u3002\u60f3\u4e0d\u5230\u6211\u6b63\u8981\u627e\u4ed6\uff0c\u5374\u5728\u8fd9\u91cc\u9047\u4e0a\u4e86\u3002\u201d \u3000\u3000\u5fc3\u5ff5\u672a\u5df2\uff0c\u679c\u7136\u4fbf\u542c\u5f97\u9c81\u3001\u5468\u4e8c\u4eba\u5f00\u58f0\u559d\u9053\uff1a\u201c\u539f\u6765\u662f\u4f60\u8fd9\u4e2a\u53cd\u8d3c\uff01\u54fc\uff0c\u54fc\uff01\u4f60\u7684\u80c6\u5b50 \u5012\u662f\u4e0d\u5c0f\u5440\uff01\u201d\u6b63\u662f\uff1a \u3000\u3000\u5c82\u6709\u82f1\u96c4\u7518\u4f5c\u8d3c\uff0c\u76f8\u9022\u964c\u8def\u7834\u5978\u8c0b\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b\u2014\u2014\u7b2c\u4e09\u5341\u4e94\u56de\u3000\u964c\u8def\u76f8\u9022\u6311\u6076\u6597\u3000\u540c\u95e8\u4f1a\u5408\u632f\u96c4\u98ce \u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b \u7b2c\u4e09\u5341\u4e94\u56de\u3000\u964c\u8def\u76f8\u9022\u6311\u6076\u6597\u3000\u540c\u95e8\u4f1a\u5408\u632f\u96c4\u98ce \u3000\u3000\u539f\u6765\u891a\u4e91\u5cf0\u672c\u662f\u8d70\u5728\u524d\u9762\u7684\uff0c\u4f46\u56e0\u5b5f\u660e\u971e\u8eab\u4e0a\u53d7\u4f24\uff0c\u867d\u65e0\u5927\u788d\uff0c\u4e8e\u8f7b\u529f\u5374\u662f\u4e0d\u514d\u7a0d\u6709\u5f71 \u54cd\uff0c\u6545\u6b64\u53cd\u800c\u843d\u5728\u540e\u5934\u4e86\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u5728\u96ea\u5730\u4e0a\u53d1\u73b0\u5468\u3001\u9c81\u4e8c\u4eba\u7684\u8db3\u5370\uff0c\u4e00\u8def\u8ffd\u6765\uff0c\u6070\u597d\u5728\u8fd9\u91cc\u78b0\u4e0a\u3002\u4ed6\u662f\u9633\u5929\u96f7\u6068\u4e4b \u5165\u9aa8\u3001\u4e25\u4ee4\u6240\u8981\u7f09\u62ff\u7684\u4eba\uff0c\u662f\u4ee5\u5468\u3001\u9c81\u4e8c\u4eba\u867d\u7136\u77e5\u9053\u4ed6\u7684\u6b66\u529f\u5389\u5bb3\uff0c\u6043\u7740\u6709\u4e8c\u4eba\u8054\u624b\uff0c\u4e5f\u5c31 \u5927\u7740\u80c6\u5b50\u8981\u548c\u4ed6\u4e00\u62fc\u4e86\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u51b7\u7b11\u9053\uff1a\u201c\u8bf7\u95ee\u4f60\u4eec\u662f\u6c49\u4eba\u8fd8\u662f\u91d1\u4eba\uff1f\u201d \u3000\u3000\u59d3\u5468\u7684\u9053\uff1a\u201c\u4f60\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u201c\u54fc\u201d\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u4f60\u8bf4\u6211\u662f\u53cd\u8d3c\uff0c\u6211\u53cd\u7684\u662f\u6b3a\u51cc\u6c49\u4eba\u7684\u91d1\u864f\uff1b\u4f60\u4eec\u7a7f\u6234\u6c49\u65cf\u8863 \u51a0\uff0c\u5982\u53cd\u800c\u8ba4\u8d3c\u4f5c\u7236\uff0c\u6b3a\u538b\u540c\u80de\uff0c\u4f60\u4eec\u624d\u662f\u4e0d\u6298\u4e0d\u6263\u7684\u53cd\u8d3c\uff01\u201d \u3000\u3000\u5468\u3001\u9c81\u4e8c\u4eba\u8001\u7f9e\u6210\u6012\uff0c\u9f50\u58f0\u559d\u9053\uff1a\u201c\u6211\u4e0d\u4e0e\u4f60\u901e\u53e3\u820c\u4e4b\u5229\uff0c\u54b1\u4eec\u638c\u5e95\u5224\u4e2a\u96cc\u96c4\uff01\u201d \u3000\u3000\u891a\u4e91\u5cf0\u51b7\u7b11\u9053\uff1a\u201c\u597d\uff0c\u4f60\u4eec\u65e2\u662f\u81f3\u6b7b\u4e0d\u609f\uff0c\u90a3\u5c31\u4e0d\u662f\u80dc\u8d1f\u4e4b\u4e89\u4e86\uff0c\u4e8e\u8106\u62fc\u4e2a\u5f3a\u5b58\u5f31\u4ea1 \u5427\uff01\u201d \u3000\u3000\u891a\u4e91\u5cf0\u6446\u51fa\u4e86\u51b3\u4e00\u6b7b\u6218\u7684\u59ff\u6001\uff0c\u5468\u3001\u9c81\u4e8c\u4eba\u5012\u662f\u4e0d\u7981\u5fc3\u4e2d\u4e00\u51db\u3002\u59d3\u9c81\u7684\u786c\u7740\u5934\u76ae\u9053\uff1a \u201c\u62fc\u5c31\u62fc\u5427\uff01\u96be\u9053\u6211\u4eec\u6015\u4f60\u4e0d\u6210\uff01\u201d \u3000\u3000\u5b5f\u660e\u971e\u5530\u7684\u62d4\u5251\u51fa\u6765\uff0c\u891a\u4e91\u5cf0\u8fde\u5fd9\u8bf4\u9053\uff1a\u201c\u5b5f\u59d1\u5a18\uff0c\u4f60\u7ed9\u6211\u63a0\u9635\uff0c\u9632\u4ed6\u4eec\u8fd8\u6709\u515a\u7fbd\u3002\u6211 \u82e5\u662f\u4e0d\u6210\uff0c\u4f60\u518d\u4e0a\u5427\u3002\u201d \u3000\u3000\u8981\u77e5\u9053\u8fd9\u4e24\u4eba\u4e43\u662f\u9633\u5929\u96f7\u624b\u4e0b\u6570\u4e00\u6570\u4e8c\u7684\u4eba\u7269\uff0c\u6b66\u529f\u5b9e\u662f\u975e\u540c\u5c0f\u53ef\u3002\u891a\u4e91\u5cf0\u5018\u82e5\u5355\u6253\u72ec \u6597\uff0c\u81ea\u5fd6\u53ef\u4ee5\u80dc\u5f97\u4ed6\u4eec\u4e2d\u7684\u4efb\u4f55\u4e00\u4e2a\uff0c\u82e5\u662f\u4ee5\u4e00\u654c\u4e8c\uff0c\u90a3\u5c31\u6ca1\u6709\u628a\u63e1\u4e86\u3002\u5b5f\u660e\u971e\u5143\u6c14\u53d7\u635f\uff0c \u672a\u66fe\u6062\u590d\uff0c\u6545\u6b64\u891a\u4e91\u5cf0\u4e0d\u653e\u5fc3\u8ba9\u5979\u4e0e\u5f3a\u624b\u5bf9\u654c\u3002 \u3000\u3000\u5b5f\u660e\u971e\u542c\u4ed6\u8bf4\u5f97\u6709\u7406\uff0c\u4e8e\u662f\u6309\u5251\u5728\u65c1\u76d1\u89c6\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u90a3\u4e24\u4eba\u5df2\u662f\u5de6\u53f3\u5939\u653b\uff0c\u540c \u65f6\u6765\u5230\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u5927\u559d\u4e00\u58f0\uff0c\u5355\u638c\u5212\u4e86\u4e00\u9053\u5706\u5f27\uff0c\u638c\u98ce\u51fb\u8361\uff0c\u9690\u9690\u631f\u7740\u98ce\u96f7\u4e4b\u58f0\u3001\u5411\u5de6\u9762\u90a3\u59d3\u5468\u7684 \u6253\u53bb\u3002 \u3000\u3000\u8c37\u6db5\u865a\u770b\u51fa\u4e86\u891a\u4e91\u5cf0\u4f7f\u7684\u662f\u201c\u5929\u96f7\u529f\u201d\uff0c\u5fc3\u91cc\u53c8\u60ca\u53c8\u559c\uff0c\u60f3\u9053\uff1a\u201c\u4ed6\u679c\u7136\u662f\u4e09\u5e08\u4f2f\u7684\u5f1f \u5b50\uff0c\u6211\u5148\u522b\u5fd9\uff0c\u4e14\u770b\u770b\u4ed6\u5b66\u7684\u672c\u95e8\u529f\u592b\u662f\u5426\u4e0e\u6211\u76f8\u540c\u3002\u201d \u3000\u3000\u5fc3\u5ff5\u672a\u5df2\uff0c\u53ea\u542c\u5f97\u201c\u84ec\u201d\u7684\u4e00\u58f0\uff0c\u90a3\u59d3\u5468\u7684\u8eab\u5f62\u4e00\u6643\uff0c\u8fde\u9000\u4e09\u6b65\u3002\u4f46\u891a\u4e91\u5cf0\u53f3\u624b\u7684\u957f \u5251\uff0c\u672c\u6765\u662f\u540c\u65f6\u523a\u5411\u53f3\u9762\u90a3\u4e2a\u59d3\u9c81\u7684\uff0c\u5374\u523a\u4e86\u4e2a\u7a7a\u3002\u90a3\u59d3\u9c81\u4f7f\u7684\u662f\u4e00\u5bf9\u94a2\u6293\uff0c\u7ed9\u4ed6\u4e00\u4e2a\u76d8\u9f99 \u7ed5\u6b65\uff0c\u53cd\u800c\u7ed5\u5230\u891a\u4e91\u5cf0\u7684\u80cc\u540e\uff0c\u891a\u4e91\u5cf0\u53cd\u624b\u4e00\u5251\uff0c\u201c\u55e4\u201d\u7684\u4e00\u58f0\u54cd\u8fc7\uff0c\u94a2\u6293\u65ad\u4e86\u4e00\u9f7f\uff0c\u891a\u4e91 \u5cf0\u7684\u8863\u88f3\u5374\u4e5f\u7ed9\u6495\u4e0b\u4e86\u4e00\u5e45\u3002 \u3000\u3000\u539f\u6765\u5468\u3001\u9c81\u4e8c\u4eba\u4e43\u662f\u5404\u7528\u6240\u957f\uff0c\u59d3\u5468\u7684\u957f\u4e8e\u5185\u529f\uff0c\u867d\u7136\u6bd4\u4e0d\u4e0a\u5929\u96f7\u529f\u7684\u5389\u5bb3\uff0c\u5374\u4e5f\u53ef\u4ee5 \u52c9\u5f3a\u62b5\u654c\u3002 \u3000\u3000\u59d3\u9c81\u7684\u957f\u4e8e\u8f7b\u529f\uff0c\u4fbf\u4ece\u4fa7\u9762\u91c7\u53d6\u5947\u88ad\u7684\u6253\u6cd5\uff0c\u4ee4\u891a\u4e91\u5cf0\u5904\u5904\u53d7\u654c\uff0c\u7a77\u4e8e\u5e94\u4ed8\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u7684\u201c\u5929\u96f7\u529f\u201d\u4e0d\u80fd\u4e00\u638c\u9707\u4f24\u59d3\u5468\u8fd9\u4e2a\u53cc\u5b50\uff0c\u7acb\u5373\u53c8\u8981\u5206\u51fa\u7cbe\u795e\u5e94\u4ed8\u8fd9\u59d3\u9c81\u7684\u5947 \u88ad\uff0c\u5982\u6b64\u6253\u6cd5\uff0c\u5f53\u7136\u662f\u4e0d\u514d\u5927\u5927\u5403\u4e8f\u3002 \u3000\u3000\u6253\u4e86\u4e00\u4f1a\uff0c\u891a\u4e91\u5cf0\u7684\u8863\u88f3\u5df2\u662f\u88ab\u90a3\u59d3\u9c81\u7684\u94a2\u6293\u6495\u7834\u4e86\u4e09\u5904\uff0c\u5e78\u800c\u8fd8\u6ca1\u4f24\u7740\u76ae\u8089\u3002\u5b5f\u660e\u971e \u89c1\u891a\u4e91\u5cf0\u8fed\u9047\u9669\u62db\uff0c\u6309\u637a\u4e0d\u4f4f\uff0c\u62d4\u5251\u51fa\u9798\uff0c\u52a0\u5165\u6218\u56e2\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u53eb\u9053\uff1a\u201c\u5b5f\u59d1\u5a18\uff0c\u4f60\u5feb\u8d70\u5427\uff01\u201d\u5b5f\u660e\u971e\u9053\uff1a\u201c\u751f\u6b7b\u4e0e\u5171\uff0c\u60a3\u96be\u540c\u5f53\uff01\u201d\u5979\u56e0\u4e3a\u891a \u4e91\u5cf0\u66fe\u7ecf\u6551\u8fc7\u5979\u7684\u6027\u547d\uff0c\u5c82\u80fd\u8ba9\u891a\u4e91\u5cf0\u4e00\u4eba\u5bf9\u4ed8\u5f3a\u654c\uff1f\u662f\u4ee5\u8868\u793a\u51b3\u5fc3\uff0c\u65e0\u8bba\u5982\u4f55\u8981\u548c\u891a\u4e91\u5cf0 \u5e76\u80a9\u4f5c\u6218\u3002\u4f46\u5979\u8bf4\u8bdd\u4e4b\u9645\uff0c\u5374\u662f\u672a\u66fe\u8be6\u52a0\u8003\u8651\u7684\uff0c\u4e00\u4e0b\u5b50\u51b2\u53e3\u800c\u51fa\uff0c\u8bf4\u51fa\u4e86\u201c\u751f\u6b7b\u4e0e\u5171\uff0c\u60a3 \u96be\u540c\u5f53\u201d\u8fd9\u516b\u4e2a\u5b57\u6765\uff0c\u8fd9\u624d\u84e6\u5730\u7701\u89c9\uff0c\u8fd9\u4e0d\u5e94\u8be5\u662f\u666e\u901a\u670b\u53cb\u6240\u8bf4\u7684\u8bdd\uff0c\u4e0d\u7981\u9762\u4e0a\u4e00\u7ea2\u3002 \u3000\u3000\u59d3\u9c81\u7684\u51b7\u7b11\u9053\uff1a\u201c\u8fd9\u5973\u5a03\u5b50\u5bf9\u4f60\u5012\u662f\u60c5\u610f\u7ef5\u7ef5\u554a\uff0c\u53ef\u60dc\u5440\uff0c\u53ef\u60dc\uff01\u201d\u59d3\u5468\u7684\u6545\u610f\u95ee\u9053\uff1a \u201c\u53ef\u60dc\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u59d3\u9c81\u7684\u9053\uff1a\u201c\u53ef\u60dc\u891a\u4e91\u5cf0\u8fd9\u5c0f\u5b50\u5df2\u662f\u65e0\u798f\u6d88\u53d7\u4e86\uff01\u9664\u975e\u4ed6\u6539\u53d8\u4e3b\u610f\uff0c\u77e5\u9053\u6094\u609f\uff0c\u8ddf\u54b1\u4eec \u56de\u4eac\u5411\u56fd\u5e08\u8bf7\u7f6a\uff0c\u5426\u5219\u4ed6\u6027\u547d\u90fd\u4fdd\u4e0d\u4f4f\uff01\u5982\u4f55\u8fd8\u80fd\u6d88\u53d7\u7f8e\u4eba\u7684\u6069\u5ba0\uff1f\u201d\u5b5f\u660e\u971e\u5927\u6012\u9053\uff1a\u201c\u653e \u5c41\uff01\u201d\u5530\u7684\u4e00\u5251\uff0c\u5c31\u5411\u4ed6\u523a\u53bb\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u542c\u4e86\u5b5f\u660e\u971e\u8bf4\u7684\u8fd9\u516b\u4e2a\u5b57\uff0c\u5fc3\u4e2d\u5374\u662f\u611f\u5230\u4e00\u80a1\u751c\u610f\uff0c\u7cbe\u795e\u5927\u632f\uff0c\u4e00\u8fde\u51e0\u8bb0\u8fdb\u624b\u7684 \u62db\u6570\uff0c\u53c8\u628a\u52a3\u52bf\u626d\u8f6c\u8fc7\u6765\u3002 \u3000\u3000\u53ef\u60dc\u5b5f\u660e\u971e\u6bd5\u7adf\u56e0\u4e3a\u5143\u6c14\u53d7\u4f24\uff0c\u672a\u66fe\u6062\u590d\uff0c\u8f7b\u529f\u6253\u4e86\u6298\u6263\uff0c\u5251\u672f\u867d\u7136\u7cbe\u5999\uff0c\u7ec8\u662f\u529b\u4e0d\u4ece \u5fc3\uff0c\u59d3\u9c81\u7684\u770b\u51fa\u5979\u7684\u5f31\u70b9\uff0c\u7740\u7740\u5411\u5979\u8fdb\u903c\u3002 \u3000\u3000\u8fd9\u4e48\u4e00\u6765\uff0c\u891a\u4e91\u5cf0\u53c8\u5fc5\u987b\u5206\u51fa\u5fc3\u795e\u6765\u7167\u987e\u5b5f\u660e\u971e\uff0c\u521a\u521a\u626d\u8f6c\u52a3\u52bf\uff0c\u4e0d\u4e45\u53c8\u9677\u4e0b\u98ce\u3002\u4f46\u5b5f \u660e\u971e\u591a\u5c11\u4e5f\u5e2e\u4e86\u4ed6\u4e00\u70b9\u5fd9\uff0c\u5206\u62c5\u4e86\u654c\u4eba\u7684\u653b\u52bf\uff0c\u8ba9\u4ed6\u4e0d\u81f3\u4e8e\u521a\u624d\u90a3\u6837\u5403\u529b\u3002\u6240\u4ee5\u5b5f\u660e\u971e\u4e0a\u6765 \u548c\u4ed6\u8054\u624b\uff0c\u53ef\u4ee5\u8bf4\u662f\u6709\u4e00\u5229\u5fc5\u6709\u4e00\u5f0a\u3002 \u3000\u3000\u8c37\u6db5\u865a\u8eb2\u5728\u77f3\u540e\u5077\u770b\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4e00\u6643\u6570\u5e74\uff0c\u539f\u6765\u5b5f\u660e\u971e\u8fd9\u5c0f\u59d1\u5a18\u4e5f\u6709\u4e86\u610f\u4e2d\u4eba\u3002\u4e0d \u77e5\u4e25\u70f7\u77e5\u4e0d\u77e5\u9053\uff1f\u201d\u53c8\u518d\u60f3\u9053\uff1a\u201c\u5b5f\u5bb6\u7684\u795e\u5251\u7684\u786e\u540d\u4e0d\u865a\u4f20\uff0c\u5b5f\u660e\u971e\u7684\u672c\u9886\u4e5f\u6bd4\u51e0\u5e74\u524d\u597d\u5f97 \u591a\u4e86\u3002\u4e0d\u8fc7\uff0c\u5979\u5374\u4f3c\u4e4e\u6709\u70b9\u529b\u4e0d\u4ece\u5fc3\uff0c\u83ab\u975e\u662f\u53d7\u4e86\u4f24\u4e48\uff1f\u201d \u3000\u3000\u8c37\u6db5\u865a\u6b63\u60f3\u51fa\u53bb\uff0c\u5ffd\u542c\u5f97\u891a\u4e91\u5cf0\u4e00\u58f0\u5927\u559d\uff0c\u628a\u8eab\u4f53\u906e\u7740\u5b5f\u660e\u971e\uff0c\u53cc\u638c\u8fde\u73af\u8fdb\u51fb\uff0c\u51fb\u9000\u4e86 \u90a3\u4e2a\u59d3\u9c81\u7684\u6c49\u5b50\u3002\u539f\u6765\u5b5f\u660e\u971e\u6b63\u5728\u9047\u5230\u9669\u62db\uff0c\u9752\u94a2\u5251\u5df2\u7ed9\u90a3\u59d3\u9c81\u7684\u94a2\u6293\u6293\u7740\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u66ff\u5b5f\u660e\u971e\u89e3\u4e86\u9669\u62db\uff0c\u6c14\u529b\u8017\u635f\u592a\u751a\uff0c\u4ea6\u5df2\u7d2f\u5f97\u6ee1\u5934\u5927\u6c57\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u971e\u59b9\uff0c\u4f60\u6b47\u4e00\u6b47\uff0c\u5f85\u4f1a\u513f\u518d\u6765\u66ff\u6211\u3002\u201d\u4ed6\u4e0e\u5b5f\u660e\u971e\u76f8\u5904\u6570\u65e5\uff0c\u8fd9\u8fd8\u662f\u7b2c\u4e00 \u6b21\u53eb\u5979\u505a\u201c\u971e\u59b9\u201d\uff0c\u5b5f\u660e\u971e\u542c\u4ed6\u5ffd\u7136\u6539\u4e86\u79f0\u547c\uff0c\u4e5f\u662f\u4e0d\u7981\u9762\u4e0a\u4e00\u7ea2\uff0c\u5fc3\u4e2d\u5374\u611f\u5230\u4e00\u80a1\u751c\u610f\u3002 \u3000\u3000\u5b5f\u660e\u971e\u8d77\u521d\u662f\u62b1\u7740\u60a3\u96be\u540c\u5f53\u7684\u5fc3\u7406\u4e0e\u891a\u4e91\u5cf0\u8054\u624b\u7684\uff0c\u4e0d\u6599\u7ecf\u8fc7\u4e86\u4e00\u573a\u6076\u6597\u4e4b\u540e\uff0c\u624d\u77e5\u9053 \u81ea\u5df1\u7684\u786e\u662f\u5143\u6c14\u672a\u66fe\u6062\u590d\uff0c\u4ee5\u81f4\u529b\u4e0d\u4ece\u5fc3\uff0c\u975e\u4f46\u5e2e\u4e0d\u4e86\u891a\u4e91\u5cf0\u4ec0\u4e48\u5927\u5fd9\uff0c\u53cd\u800c\u51e0\u4e4e\u7d2f\u5f97\u891a\u4e91 \u5cf0\u4e3a\u81ea\u5df1\u53d7\u4f24\u3002\u6b64\u65f6\u5979\u4ea6\u662f\u7b4b\u75b2\u529b\u7aed\uff0c\u4e0d\u582a\u518d\u6218\u4e86\u3002\u542c\u4e86\u891a\u4e91\u5cf0\u7684\u8bdd\uff0c\u6697\u81ea\u601d\u91cf\uff1a\u201c\u4e0d\u9519\uff0c \u6211\u662f\u4e0d\u80fd\u786c\u62fc\u7684\u4e86\u3002\u4e0e\u5176\u548c\u891a\u5927\u54e5\u540c\u5f52\u4e8e\u5c3d\uff0c\u4e0d\u5982\u6362\u4e2a\u65b9\u6cd5\u8bd5\u8bd5\uff0c\u8bf4\u4e0d\u5b9a\u53ef\u4ee5\u6b7b\u91cc\u9003\u751f\u3002\u201d \u3000\u3000\u539f\u6765\u5b5f\u5bb6\u7684\u6697\u5668\u529f\u592b\u4e5f\u662f\u6b66\u6797\u4e00\u7edd\uff0c\u53ea\u56e0\u5b5f\u660e\u971e\u6027\u60c5\u76f4\u723d\uff0c\u89c9\u5f97\u6697\u5668\u4f24\u4eba\uff0c\u4e0d\u591f\u5149\u660e\u78ca \u843d\uff0c\u662f\u4ee5\u4e00\u5411\u4e0d\u559c\u4f7f\u7528\u3002\u6b64\u9645\u5979\u65e0\u529b\u518d\u6218\uff0c\u624d\u52a8\u4e86\u7528\u6697\u5668\u7684\u5ff5\u5934\u3002\u5b5f\u5bb6\u6697\u5668\u4e0d\u62d8\u4e00\u683c\uff0c\u8bb2\u7a76 \u7684\u662f\u5de7\u5999\u624b\u6cd5\uff0c\u4fe1\u624b\u62c8\u6765\uff0c\u4fbf\u53ef\u5f53\u4f5c\u6697\u5668\u4f7f\u7528\u3002 \u3000\u3000\u59d3\u9c81\u90a3\u4e2a\u6c49\u5b50\uff0c\u6b63\u9762\u53d7\u4e86\u5929\u96f7\u529f\u7684\u9707\u64bc\uff0c\u80f8\u53e3\u5982\u53d7\u94c1\u9524\u6240\u51fb\uff0c\u53ea\u89c9\u4e94\u810f\u516d\u8151\u90fd\u597d\u4f3c\u6362\u4e86 \u4f4d\u7f6e\u4f3c\u7684\uff0c\u4e00\u65f6\u95f4\u4e5f\u662f\u4e0d\u6562\u8fd0\u7528\u771f\u529b\uff0c\u56e0\u6b64\u891a\u4e91\u5cf0\u867d\u7136\u662f\u5f3a\u5f29\u4e4b\u672b\uff0c\u4ee5\u4e00\u654c\u4e8c\uff0c\u5f62\u52bf\u5374\u662f\u6bd4 \u521a\u624d\u8fd8\u597d\u4e00\u4e9b\u3002 \u3000\u3000\u5b5f\u660e\u971e\u9000\u4e0b\u4e00\u65c1\uff0c\u6361\u8d77\u5730\u4e0a\u7684\u77f3\u5b50\uff0c\u7528\u201c\u5929\u5973\u6563\u82b1\u201d\u7684\u624b\u6cd5\u6253\u51fa\u3002\u77f3\u5b50\u4e0a\u5c31\u50cf\u957f\u7740\u773c\u775b \u4f3c\u7684\uff0c\u6bcf\u4e00\u9897\u90fd\u662f\u6253\u5411\u5468\u3001\u9c81\u4e8c\u4eba\u7684\u8981\u5bb3\u7a74\u9053\uff0c\u5e76\u4e0d\u6015\u8bef\u4f24\u4e86\u891a\u4e91\u5cf0\u3002 \u3000\u3000\u59d3\u5468\u90a3\u4e2a\u6c49\u5b50\u5185\u529f\u6df1\u539a\uff0c\u53cc\u638c\u4f7f\u5f00\uff0c\u52b2\u98ce\u547c\u547c\uff0c\u77f3\u5b50\u672a\u66fe\u6253\u5230\u4ed6\u7684\u8eab\u4e0a\uff0c\u4fbf\u5df2\u6389\u4e86\u4e0b \u6765\u3002\u59d3\u9c81\u90a3\u4e2a\u6c49\u5b50\u957f\u4e8e\u8f7b\u529f\uff0c\u5374\u53ea\u80fd\u8eb2\u95ea\u3002\u4ed6\u6709\u201c\u542c\u98ce\u8fa8\u5411\u201d\u7684\u672c\u9886\uff0c\u77f3\u5b50\u4ece\u4ec0\u4e48\u65b9\u4f4d\u6253 \u6765\uff0c\u4ed6\u4e00\u542c\u98ce\u58f0\uff0c\u4fbf\u5373\u77e5\u9053\u3002 \u3000\u3000\u4e0d\u6599\u5b5f\u660e\u971e\u7684\u6697\u5668\u624b\u6cd5\u5341\u5206\u53e4\u602a\uff0c\u4e0e\u4f17\u4e0d\u540c\u3002\u4ed6\u8eb2\u8fc7\u4e86\u51e0\u9897\u77f3\u5b50\uff0c\u6b63\u81ea\u5f97\u610f\uff0c\u60f3\u8981\u8bf4\u51e0 \u53e5\u8bdd\u595a\u843d\u5b5f\u660e\u971e\uff0c\u8bdd\u672a\u51fa\u53e3\uff0c\u542c\u5f97\u77f3\u5b50\u7834\u7a7a\u4e4b\u58f0\uff0c\u662f\u671d\u4ed6\u7684\u5de6\u80c1\u6108\u6c14\u7a74\u6253\u6765\u3002\u4ed6\u5f80\u53f3\u8fb9\u4e00 \u95ea\uff0c\u8c01\u77e5\u5b5f\u660e\u971e\u540c\u65f6\u53d1\u51fa\u7684\u4e24\u9897\u77f3\u5b50\uff0c\u5c06\u8fd1\u4ed6\u7684\u8eab\u524d\u4e4b\u9645\uff0c\u624d\u7a81\u7136\u5728\u7a7a\u4e2d\u4e00\u78b0\uff0c\u65b9\u5411\u767b\u65f6\u6539 \u53d8\uff0c\u59d3\u5468\u7684\u51b7\u4e0d\u53ca\u9632\uff0c\u201c\u80a9\u9631\u7a74\u201d\u7ed9\u4e00\u9897\u77f3\u5b50\u6253\u4e2a\u6b63\u7740\u3002 \u3000\u3000\u53ef\u60dc\u5b5f\u660e\u971e\u6c14\u529b\u4e0d\u52a0\uff0c\u867d\u7136\u6253\u4e2d\u4e86\u4ed6\u7684\u201c\u80a9\u9631\u7a74\u201d\uff0c\u5374\u53ea\u662f\u4ee4\u4ed6\u611f\u5230\u4e00\u9635\u9178\u9ebb\u800c\u5df2\uff0c\u52b2 \u529b\u672a\u5230\uff0c\u4e5f\u5c31\u6536\u4e0d\u5230\u6253\u7a74\u7684\u529f\u6548\u3002 \u3000\u3000\u4f46\u8fd9\u59d3\u5468\u7684\u81ea\u89c9\u5728\u540c\u4f34\u9762\u524d\u5931\u4e86\u9762\u5b50\uff0c\u5374\u662f\u4e0d\u7531\u5f97\u6012\u4ece\u5fc3\u8d77\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u4f60\u6709\u6697 \u5668\uff0c\u6211\u5c31\u6ca1\u6709\u5417\uff1f\u6765\u800c\u4e0d\u5f80\u975e\u793c\u4e5f\uff0c\u4f60\u4e5f\u63a5\u6211\u7684\u8bd5\u8bd5\u3002\u201d\u59d3\u9c81\u7684\u53eb\u9053\uff1a\u201c\u5468\u5144\uff0c\u7559\u8fd9\u5973\u5a03\u513f \u4e00\u547d\uff0c\u7528\u6ca1\u6709\u5582\u6bd2\u7684\u6697\u9752\u5b50\uff01\u201d \u3000\u3000\u59d3\u5468\u7684\u6c49\u5b50\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u9c81\u5144\uff0c\u539f\u6765\u4f60\u4e5f\u61c2\u5f97\u60dc\u7389\u601c\u9999\u3002\u201d \u3000\u3000\u59d3\u9c81\u7684\u7b11\u9053\uff1a\u201c\u60dc\u7389\u601c\u9999\u7684\u53e6\u6709\u5176\u4eba\uff0c\u53ef\u4e0d\u662f\u6211\u3002\u4f60\u5fd8\u8bb0\u4e86\u54b1\u4eec\u7684\u9633\u516c\u5b50\u5417\uff1f\u201d \u3000\u3000\u59d3\u5468\u7684\u970d\u7136\u4e00\u7701\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u628a\u8fd9\u7f8e\u4eba\u513f\u732e\u7ed9\u9633\u516c\u5b50\uff0c\u5012\u662f\u4e00\u4ef6\u5927\u529f\u3002\u591a\u8c22\u4f60\u7684\u6307 \u70b9\u4e86\uff01\u201d\u5f53\u4e0b\u4e00\u6296\u624b\u53d1\u51fa\u4e86\u4e09\u9897\u65e0\u6bd2\u7684\u94c1\u83e9\u63d0\u3002 \u3000\u3000\u6b64\u4eba\u7684\u6697\u5668\u529f\u592b\u4e5f\u662f\u9887\u4e3a\u4e86\u5f97\uff0c\u4e09\u9897\u94c1\u83e9\u63d0\u5206\u6253\u5b5f\u660e\u971e\u4e0a\u4e2d\u4e0b\u4e09\u5904\u7a74\u9053\u3002\u53ef\u662f\u5728\u5b5f\u660e\u971e \u773c\u4e2d\u770b\u6765\uff0c\u5374\u53ea\u662f\u96d5\u866b\u5c0f\u6280\u800c\u5df2\u3002 \u3000\u3000\u5b5f\u660e\u971e\u7b11\u9053\uff1a\u201c\u7c73\u7c92\u4e4b\u73e0\uff0c\u4e5f\u653e\u5149\u534e\uff01\u201d\u4e00\u7c92\u5c0f\u77f3\u5b50\u8f7b\u55d6\u5f39\u51fa\uff0c\u548c\u5f53\u4e2d\u90a3\u9897\u94c1\u83e9\u63d0\u4e00 \u78b0\uff0c\u5de6\u53f3\u9f50\u98de\uff0c\u6070\u597d\u53c8\u548c\u4ece\u4e24\u8fb9\u6253\u6765\u7684\u83e9\u63d0\u78b0\u4e2a\u6b63\u7740\uff0c\u4e00\u7c92\u5c0f\u77f3\u5b50\uff0c\u4e09\u9897\u94c1\u83e9\u63d0\u540c\u65f6\u6253\u843d\u5728 \u5730\u4e0a\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u5ffd\u5730\u53eb\u9053\uff1a\u201c\u971e\u59b9\uff0c\u7559\u795e\uff01\u6709\u4eba\u2014\u2014\u201d\u5b5f\u660e\u971e\u8d77\u521d\u53ea\u9053\u891a\u4e91\u5cf0\u662f\u53eb\u5979\u7559\u795e\u6697\u5668\uff0c \u5fc3\u91cc\u8fd8\u5728\u597d\u7b11\uff1a\u201c\u8fd9\u4eba\u7684\u6697\u5668\u529f\u592b\u6709\u4ec0\u4e48\u4e86\u4e0d\u8d77\uff0c\u4f55\u7528\u5927\u60ca\u5c0f\u602a\uff1f\u201d\u5f85\u542c\u5f97\u201c\u6709\u4eba\u201d\u4e8c\u5b57\uff0c \u65b9\u59cb\u5403\u60ca\uff0c\u62ac\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u4e00\u4e2a\u8499\u9762\u4eba\u6b63\u81ea\u4e00\u5757\u5927\u77f3\u5934\u540e\u9762\u8df3\u51fa\u3002 \u3000\u3000\u8fd9\u8499\u9762\u4eba\u6765\u5f97\u597d\u5feb\uff0c\u891a\u4e91\u5cf0\u8bdd\u58f0\u672a\u4e86\uff0c\u4ed6\u5df2\u500f\u7136\u6765\u5230\u4e86\u5b5f\u660e\u971e\u7684\u9762\u524d\u3002\u5b5f\u660e\u971e\u8fd9\u4e00\u60ca\u975e \u540c\u5c0f\u53ef\uff0c\u60f3\u9053\uff1a\u201c\u4ed6\u4eec\u679c\u7136\u8fd8\u6709\u515a\u7fbd\uff0c\u8fd9\u5012\u662f\u6211\u7684\u758f\u5ffd\u4e86\u3002\u65e0\u8bba\u5982\u4f55\u6211\u4e5f\u8981\u963b\u4ed6\u4e00\u963b\uff0c\u51b3\u4e0d \u80fd\u8ba9\u4ed6\u4e0a\u53bb\u5e2e\u624b\u3002\u6597\u4e0d\u8fc7\u4ed6\uff0c\u5c31\u628a\u8fd9\u6761\u6027\u547d\u4e0e\u4ed6\u62fc\u4e86\u3002\u201d\u8981\u77e5\u891a\u4e91\u5cf0\u72ec\u6597\u5468\u3001\u9c81\u4e8c\u4eba\uff0c\u5df2\u662f \u6781\u4e3a\u5403\u529b\uff0c\u8fd9\u8499\u9762\u4eba\u522b\u7684\u529f\u592b\u672a\u77e5\uff0c\u53ea\u770b\u8f7b\u529f\uff0c\u5df2\u662f\u5c1a\u5728\u90a3\u59d3\u5468\u7684\u4e4b\u4e0a\uff0c\u5b5f\u660e\u971e\u5982\u4f55\u6562\u653e\u4ed6 \u8fc7\u53bb\uff1f \u3000\u3000\u5b5f\u660e\u971e\u7d27\u54ac\u94f6\u7259\uff0c\u5f3a\u632f\u7cbe\u795e\uff0c\u5530\u7684\u4e00\u5251\u5c31\u5411\u90a3\u8499\u9762\u4eba\u523a\u53bb\u3002\u59d3\u5468\u90a3\u6c49\u5b50\u7b2c\u4e00\u6b21\u53d1\u51fa\u7684\u4e09 \u9897\u94c1\u83e9\u63d0\u7ed9\u5b5f\u660e\u971e\u6253\u843d\uff0c\u8ddf\u7740\u53c8\u53d1\u4e09\u9897\uff0c\u6070\u597d\u4e5f\u5728\u6b64\u65f6\u6253\u5230\u3002 \u3000\u3000\u8499\u9762\u4eba\u662f\u7ad9\u5728\u5b5f\u660e\u971e\u7684\u9762\u524d\u7684\uff0c\u6697\u5668\u4ece\u4ed6\u80cc\u540e\u6253\u6765\uff0c\u9762\u524d\u53c8\u6709\u5b5f\u660e\u971e\u4e00\u67c4\u660e\u6643\u6643\u7684\u5229 \u5251\uff0c\u53d8\u6210\u4e86\u80cc\u8179\u53d7\u654c\u7684\u5f62\u52bf\u3002 \u3000\u3000\u8499\u9762\u4eba\u4e0d\u7406\u80cc\u540e\u7684\u6697\u5668\uff0c\u4f38\u51fa\u4e2d\u6307\uff0c\u8f7b\u8f7b\u4e00\u5f39\uff0c\u628a\u5b5f\u660e\u971e\u7684\u5251\u5f39\u5f00\uff0c\u53ea\u542c\u5f97\u201c\u535c\u3001\u535c\u3001 \u535c\u201d\u4e09\u58f0\uff0c\u90a3\u4e09\u9897\u94c1\u83e9\u63d0\u90fd\u6253\u5728\u4ed6\u7684\u8eab\u4e0a\uff0c\u800c\u4e14\u90fd\u662f\u6b63\u4e2d\u7a74\u9053\uff0c\u4f46\u4ed6\u5374\u4f3c\u6beb\u65e0\u77e5\u89c9\u4f3c\u7684\uff0c\u8fde \u8eab\u5f62\u4e5f\u672a\u4e00\u6643\u3002 \u3000\u3000\u8499\u9762\u4eba\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u76c2\u59d1\u5a18\uff01\u662f\u6211\uff01\u4f60\u8fd8\u8bb0\u5f97\u6211\u5417\uff1f\u6211\u662f\u56db\u5e74\u524d\u5230\u8fc7\u4f60\u59d1\u6bcd\u5bb6\u4e2d\u7684\u90a3\u4e2a \u4eba\uff01\u201d \u3000\u3000\u5b5f\u660e\u971e\u5446\u4e86\u4e00\u5446\uff0c\u5931\u58f0\u53eb\u9053\uff1a\u201c\u4f60\u3001\u4f60\u662f\u8c37\u6db5\u865a\uff01\u201d\u5979\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\uff0c\u7adf\u4f1a\u5728\u8fd9\u91cc\u78b0\u89c1 \u4e86\u8c37\u6db5\u865a\u3002 \u3000\u3000\u8c37\u6db5\u865a\u5df2\u7ecf\u4ece\u5b5f\u660e\u971e\u9762\u524d\u8dd1\u4e86\u8fc7\u53bb\uff0c\u53eb\u9053\uff1a\u201c\u891a\u5e08\u5144\uff0c\u96f7\u7535\u4ea4\u8f70\uff01\u201d \u3000\u3000\u201c\u96f7\u7535\u4ea4\u8f70\u201d\u662f\u5929\u96f7\u638c\u4e2d\u4e00\u62db\u6781\u5389\u5bb3\u7684\u6740\u624b\uff0c\u4e00\u638c\u5982\u96f7\uff0c\u4e00\u638c\u5982\u7535\uff0c\u6c89\u96c4\u8fc5\u731b\uff0c\u517c\u800c\u6709 \u4e4b\u3002\u4f46\u56e0\u8fd9\u4e00\u62db\u7684\u5a01\u529b\u53d1\u6325\u5f97\u6dcb\u6ef4\u5c3d\u81f4\uff0c\u529f\u529b\u7a0d\u5f31\u7684\uff0c\u5c31\u9700\u8981\u4e24\u4eba\u5408\u4f7f\u4e86\u3002\u5426\u5219\u52c9\u5f3a\u65bd\u4e3a\uff0c \u4e8e\u81ea\u8eab\u53cd\u6709\u59a8\u5bb3\u3002\u5f53\u4eca\u4e4b\u4e16\uff0c\u80fd\u591f\u5355\u72ec\u4f7f\u7528\u8fd9\u4e00\u62db\u201c\u96f7\u7535\u4ea4\u8f70\u201d\u7684\u53ea\u6709\u9633\u5929\u96f7\u548c\u891a\u4e91\u5cf0\u7684\u5e08 \u7236\u534e\u5929\u8679\u3002\u891a\u4e91\u5cf0\u81ea\u5df1\u5374\u8fd8\u5dee\u4e09\u5e74\u529f\u529b\uff0c\u672a\u80fd\u4f7f\u7528\u8fd9\u62db\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u7a81\u7136\u542c\u5f97\u8fd9\u8499\u9762\u4eba\u53eb\u4ed6\u505a\u201c\u891a\u5e08\u5144\u201d\uff0c\u968f\u5373\u53c8\u542c\u5f97\u4ed6\u8bf4\u51fa\u201c\u96f7\u7535\u4ea4\u8f70\u201d\u7684\u62db\u540d\uff0c \u4e0d\u7981\u53c8\u662f\u5403\u60ca\uff0c\u53c8\u662f\u8be7\u5f02\u3002\u6b64\u65f6\u8c37\u6db5\u865a\u5df2\u7ecf\u51fa\u638c\uff0c\u53ea\u89c1\u4ed6\u5de6\u638c\u5212\u5f27\uff0c\u7f13\u7f13\u63a8\u51fa\uff1b\u53f3\u638c\u634f\u7740\u5251 \u8bc0\uff0c\u4e09\u6307\u7b14\u76f4\u523a\u51fa\uff0c\u52a8\u4f5c\u6bd4\u5de6\u638c\u5374\u5feb\u5f97\u591a\u3002\u8fd9\u6b63\u662f\u201c\u96f7\u7535\u4ea4\u8f70\u201d\u7684\u624b\u6cd5\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u6b64\u65f6\u5df1\u6599\u5230\u51e0\u5206\uff0c\u5f53\u4e0b\u4e0d\u5047\u601d\u7d22\u5730\u8ddf\u7740\u51fa\u62db\uff0c\u914d\u5408\u5f97\u867d\u7136\u7a0d\u6709\u53c2\u5dee\uff0c\u672a\u80fd\u66f2\u5c3d\u5176 \u5999\uff0c\u4f46\u8fd9\u5468\u3001\u9c81\u4e8c\u4eba\u5df2\u662f\u7981\u53d7\u4e0d\u8d77\uff0c\u53ea\u542c\u5f97\u201c\u7bf7\u201d\u201c\u84ec\u201d\u4e24\u58f0\uff0c\u4e24\u4eba\u540c\u65f6\u5012\u5730\u3002\u59d3\u5468\u90a3\u4e2a\u6c49 \u5b50\uff0c\u56e0\u4e3a\u662f\u6b63\u9762\u63a5\u62db\uff0c\u4f24\u5f97\u66f4\u91cd\uff0c\u762b\u5728\u5730\u4e0a\uff0c\u4e03\u7a8d\u6d41\u8840\uff0c\u597d\u50cf\u53d8\u6210\u4e86\u4e00\u5806\u70c2\u6ce5\uff0c\u773c\u89c1\u662f\u4e0d\u80fd \u6d3b\u4e86\u3002\u59d3\u9c81\u90a3\u4e2a\u6c49\u5b50\u8fd8\u80fd\u591f\u6323\u624e\uff0c\u5728\u5730\u4e0a\u63a5\u8fde\u6253\u6eda\uff0c\u4f46\u5374\u4e5f\u722c\u4e0d\u8d77\u6765\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u518d\u4e5f\u6ca1\u6709\u6000\u7591\uff0c\u5373\u4fbf\u6536\u638c\u95ee\u9053\uff1a\u201c\u4f60\u53ef\u662f\u6211\u803f\u5e08\u53d4\u7684\u5f1f\u5b50\u8c37\u5e08\u5144\u4e48\uff1f\u201d\u90a3\u8499\u9762\u4eba \u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5c0f\u5f1f\u6b63\u662f\u8c37\u6db5\u865a\u3002\u201d\u891a\u4e91\u5cf0\u5927\u559c\u9053\uff1a\u201c\u6211\u6b63\u8981\u627e\u4f60\u3002\u201d\u8c37\u6db5\u865a\u7b11\u9053\uff1a\u201c\u6211\u4e5f\u6b63 \u8981\u627e\u4f60\u3002\u201d \u3000\u3000\u59d3\u9c81\u90a3\u4e2a\u6c49\u5b50\u6070\u5de7\u6eda\u5230\u4e86\u5b5f\u660e\u971e\u8eab\u8fb9\uff0c\u5b5f\u660e\u971e\u6068\u4ed6\u521a\u624d\u53e3\u9f7f\u8f7b\u8584\uff0c\u62d4\u5251\u5c31\u8981\u6740\u4ed6\uff0c\u5ffd\u542c \u5f97\u8c37\u6db5\u865a\u53eb\u9053\uff1a\u201c\u5b5f\u59d1\u5a18\u4e14\u6162\u52a8\u624b\uff01\u201d \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u891a\u5e08\u5144\uff0c\u7b49\u4f1a\u54b1\u4eec\u518d\u53d9\uff0c\u6709\u4ef6\u7d27\u8981\u7684\u4e8b\u60c5\u6211\u8981\u95ee\u95ee\u8fd9\u53ae\u3002\u201d\u5f53\u4e0b\u5c06\u90a3\u59d3\u9c81 \u7684\u6c49\u5b50\u6276\u4e86\u8d77\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u4e25\u58f0\u6d9b\u7684\u5973\u513f\u73b0\u5728\u54ea\u513f\uff0c\u4f60\u4eec\u628a\u5979\u600e\u6837\u4e86\uff0c\u5feb\u8bf4\uff01\u201d \u3000\u3000\u6b64\u8a00\u4e00\u51fa\uff0c\u5b5f\u660e\u971e\u5403\u60ca\u4e0d\u5df2\uff0c\u8fde\u5fd9\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u8868\u59d0\u4e5f\u6765\u4e86\u4e48\uff1f\u5979\u843d\u5728\u654c\u4eba\u624b\u4e0a\uff1f\u201d \u8c37\u6db5\u865a\u9053\uff1a\u201c\u6211\u5077\u542c\u4ed6\u4eec\u521a\u624d\u7684\u8bdd\uff0c\u4ed6\u4eec\u8981\u7528\u4e25\u70f7\u6765\u63a7\u5236\u4f60\u7239\u7239\uff0c\u6050\u6015\u662f\u843d\u5728\u4ed6\u4eec\u7684\u624b\u4e0a \u4e86\uff01\u201d \u3000\u3000\u59d3\u9c81\u7684\u547b\u541f\u53eb\u9053\uff1a\u201c\u662f\uff0c\u6211\u53cd\u6b63\u662f\u8981\u6b7b\u7684\u4e86\uff0c\u6211\u4e3a\u4ec0\u4e48\u8981\u544a\u8bc9\u4f60\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u4f60\u8bf4\u4e86\u6211\u5c31\u9976\u4f60\u3002\u800c\u4e14\u6211\u8fd8\u6709\u836f\u533b\u4f60\u3002\u201d \u3000\u3000\u59d3\u9c81\u7684\u9053\uff1a\u201c\u6b64\u8bdd\u5f53\u771f\uff1f\u201d \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u541b\u5b50\u4e00\u8a00\uff0c\u5feb\u9a6c\u4e00\u97ad\u3002\u59d3\u8c37\u7684\u8bf4\u51fa\u8bdd\u6765\uff0c\u4ece\u6765\u6ca1\u6709\u4e0d\u7b97\u6570\u7684\u3002\u201d \u3000\u3000\u59d3\u9c81\u7684\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4e48\u6211\u5c31\u8bf4\u3001\u8bf4\uff0c\u5979\u3001\u5979\u5728\u9ed1\u77f3\u2014\u2014\u201d \u3000\u3000\u59d3\u9c81\u7684\u6c49\u5b50\u4e0a\u6c14\u4e0d\u63a5\u4e0b\u6c14\u7684\u8bf4\u4e86\u51e0\u4e2a\u5b57\u5c31\u505c\u4e00\u505c\uff0c\u8bf4\u5230\u4e86\u201c\u9ed1\u77f3\u201d\u4e8c\u5b57\uff0c\u4e24\u773c\u7ffb\u767d\uff0c\u53c8 \u505c\u4e0b\u6765\u4e86\u3002\u8c37\u6db5\u865a\u7528\u6307\u7532\u8f7b\u8f7b\u6311\u4e86\u4e00\u4e0b\u4ed6\u7684\u4eba\u4e2d\uff0c\u8bf4\u9053\uff1a\u201c\u9ed1\u77f3\u4ec0\u4e48\uff1f\u201d\u5b5f\u660e\u971e\u4e5f\u8fde\u5fd9\u95ee \u9053\uff1a\u201c\u5979\u843d\u5728\u4ec0\u4e48\u4eba\u7684\u624b\uff1f\u201d \u3000\u3000\u59d3\u9c81\u7684\u5634\u5507\u5f00\u5408\uff0c\u6b63\u8981\u8bf4\u8bdd\uff0c\u8c37\u6db5\u865a\u4ea6\u5df2\u628a\u8033\u6735\u63cd\u5230\u4ed6\u7684\u5634\u8fb9\uff0c\u51c6\u5907\u7528\u5fc3\u542c\u4ed6\u8bf4\u8bdd\u3002\u5c31 \u5728\u6b64\u65f6\uff0c\u5ffd\u542c\u5f97\u4ed6\u4e00\u58f0\u60e8\u53eb\uff0c\u5e95\u4e0b\u7684\u8bdd\u672a\u80fd\u8bf4\u51fa\uff0c\u5df2\u662f\u6c14\u7edd\uff01 \u3000\u3000\u59d3\u5468\u7684\u6c49\u5b50\u72de\u7b11\u9053\uff1a\u201c\u770b\u4f60\u8fd8\u6562\u6cc4\u6f0f\u79d8\u5bc6\uff01\u201d\u8c37\u6db5\u865a\u9661\u5730\u8df3\u4e86\u8d77\u6765\uff0c\u559d\u9053\uff1a\u201c\u5c82\u6709\u6b64 \u7406\uff0c\u4f60\u6740\u4e86\u4ed6\uff0c\u6211\u975e\u6bd9\u4f60\u4e0d\u53ef\u3002\u201d\u53ef\u662f\u59d3\u5468\u90a3\u6c49\u5b50\u4e0d\u5f85\u8c37\u6db5\u865a\u52a8\u624b\u6740\u4ed6\uff0c\u4ed6\u72de\u7b11\u8fc7\u540e\uff0c\u5634\u89d2 \u6d41\u51fa\u8840\u6765\uff0c\u5148\u81ea\u6b7b\u4e86\u3002 \u3000\u3000\u539f\u6765\u59d3\u5468\u8fd9\u4eba\u6697\u5668\u529f\u592b\u867d\u7136\u8fd8\u7b97\u4e0d\u5f97\u600e\u4e48\u9ad8\u660e\uff0c\u53ef\u662f\u4ed6\u5374\u6709\u4e00\u79cd\u6dec\u8fc7\u5267\u6bd2\u7684\u6885\u82b1\u9488\uff0c\u80fd \u591f\u4ee4\u4eba\u89c1\u8840\u5c01\u5589\u7684\u3002\u4ed6\u867d\u7136\u4f24\u5f97\u4e25\u91cd\uff0c\u4f46\u5f39\u6307\u53d1\u51fa\u6885\u82b1\u9488\u7684\u529b\u9053\u8fd8\u6709\u3002\u4ed6\u77e5\u9053\u8c37\u6db5\u865a\u4e0e\u891a\u4e91 \u5cf0\u7684\u5185\u529f\u5747\u6781\u6df1\u539a\uff0c\u4e0d\u662f\u4ed6\u5c0f\u5c0f\u7684\u4e00\u652f\u6885\u82b1\u9488\u6240\u80fd\u4f24\u5bb3\uff0c\u5b5f\u660e\u971e\u7684\u529f\u592b\u6bd4\u4ed6\u9ad8\u660e\u5f97\u591a\uff0c\u6050\u6015 \u4e5f\u672a\u5fc5\u6697\u7b97\u5f97\u4e86\u5979\u3002 \u3000\u3000\u4f46\u59d3\u9c81\u8fd9\u6c49\u5b50\u4ea6\u5df2\u662f\u53d7\u4e86\u91cd\u4f24\u7684\uff0c\u8981\u6697\u7b97\u4ed6\u90a3\u53ef\u662f\u6613\u5982\u53cd\u638c\u3002\u4ed6\u81ea\u77e5\u6027\u547d\u51b3\u8ba1\u96be\u4fdd\uff0c\u4e0d \u5fcd\u540c\u4f34\u80fd\u591f\u9003\u751f\uff0c\u662f\u4ee5\u5c3d\u4e86\u6700\u540e\u4e00\u70b9\u6c14\u529b\uff0c\u9aa4\u4e0b\u6bd2\u624b\u3002\u4f17\u4eba\u90fd\u5728\u7559\u610f\u542c\u8fd9\u59d3\u9c81\u7684\u8bf4\u8bdd\uff0c\u6ca1\u6709 \u9632\u5907\u3002 \u3000\u3000\u8c37\u6db5\u865a\u987f\u8db3\u53f9\u9053\uff0c\u201c\u8fd9\u4e24\u4e2a\u4eba\u4f5c\u6076\u591a\u7aef\uff0c\u81ea\u76f8\u6b8b\u6740\uff0c\u6b7b\u4e0d\u8db3\u60dc\u3002\u53ef\u60dc\u6211\u521a\u521a\u627e\u5230\u4e00\u6761\u7ebf \u7d22\uff0c\u5374\u7ed9\u8fd9\u4e2a\u8be5\u6b7b\u7684\u6253\u65ad\u4e86\u3002\u5982\u4eca\u53eb\u6211\u5982\u4f55\u53bb\u627e\u4e25\u70f7\u5462\uff1f\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u8c37\u5927\u54e5\uff0c\u4f60\u522b\u7740\u6025\u3002\u4ed6\u4eec\u65e2\u8981\u7528\u8868\u59d0\u6765\u631f\u5236\u6211\u7684\u7239\u7239\uff0c\u6211\u4eec\u5230\u4e86\u98de\u9f99\u5c71\uff0c \u60f3\u5fc5\u4f1a\u77e5\u9053\u5979\u7684\u4e0b\u843d\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u4f60\u8868\u59d0\u7684\u813e\u6c14\u4f60\u662f\u5e94\u8be5\u77e5\u9053\u7684\uff0c\u5979\u82e5\u843d\u5728\u574f\u4eba\u624b\u4e0a\uff0c\u5c82\u7518\u5c48\u670d\uff1f\u53ea\u6015\u672a\u5230 \u98de\u9f99\u5c71\uff0c\u5979\u5df2\u6b7b\u4e86\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u5728\u4e00\u65c1\u82e5\u6709\u6240\u601d\uff0c\u5e76\u4e0d\u642d\u8bdd\u3002 \u3000\u3000\u8c37\u6db5\u865a\u53f9\u4e86\u53e3\u6c14\uff0c\u60f3\u8d77\u4e86\u5e08\u7236\u4ee5\u56fd\u4e8b\u4e3a\u91cd\u7684\u6559\u8bad\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4e8b\u5df2\u5982\u65af\uff0c\u7740\u6025\u4e5f\u6ca1 \u6709\u7528\u3002\u5403\u4eec\u8fd8\u6709\u66f4\u5927\u7684\u4e8b\u60c5\u8981\u6599\u7406\u5462\u3002\u5b5f\u59d1\u5a18\uff0c\u4f60\u548c\u891a\u5e08\u5144\u53ef\u662f\u8981\u5230\u98de\u9f99\u5c71\u53bb\u7684\u5417\uff1f\u201d \u3000\u3000\u5b5f\u660e\u971e\u70b9\u4e86\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u6b63\u662f\u3002\u201d\u8c37\u6db5\u865a\u9053\uff1a\u201c\u4f55\u4ee5\u4f60\u4e0d\u4e0e\u4ee4\u5c0a\u4e00\u9053\uff1f\u6211\u597d\u4f3c\u542c\u5f97\u4ed6 \u4eec\u8bf4\u4ee4\u5c0a\u5df2\u7ecf\u548c\u53e6\u4e00\u4f4d\u65b0\u4efb\u7eff\u6797\u76df\u4e3b\u7684\u674e\u59d3\u82f1\u96c4\u524d\u5f80\u98de\u9f99\u5c71\u3002\u201d\u5b5f\u660e\u971e\u9053\uff1a\u201c\u6b64\u4e8b\u4e00\u8a00\u96be \u5c3d\u3002\u7239\u7239\u672c\u6765\u662f\u4e0d\u9700\u8981\u6211\u53bb\u7684\uff0c\u6211\u662f\u4e3a\u4e86\u627e\u5bfb\u53e6\u4e00\u4e2a\u4eba\u3002\u4e0d\u8fc7\uff0c\u8fd8\u662f\u8bf4\u4f60\u7684\u5427\uff0c\u4f60\u662f\u600e\u4e48\u77e5 \u9053\u6211\u5728\u8fd9\u513f\uff0c\u53c8\u600e\u7684\u4f1a\u77e5\u9053\u891a\u4e91\u5cf0\u662f\u4f60\u7684\u5e08\u5144\u5462\uff1f\u201d \u3000\u3000\u8c37\u6db5\u865a\u9053\uff0c\u201c\u6628\u665a\u6211\u4e5f\u78b0\u4e0a\u4e00\u4e2a\u4eba\uff0c\u662f\u8fd9\u4e2a\u4eba\u544a\u8bc9\u6211\u7684\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u8be7\u9053\uff1a\u201c\u8fd9\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u662f\u4e00\u4f4d\u59d3\u6768\u7684\u59d1\u5a18\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u53c8\u60ca\u53c8\u559c\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u78b0\u89c1\u6768\u5a49\u4e86\uff1f\u6211\u6b63\u8981\u627e\u5979\u3002\u5979\u77e5\u9053\u4e86\u4e48\uff1f\u201d \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u5979\u6628\u665a\u5728\u4e00\u5ea7\u53e4\u5e99\u91cc\u906d\u53d7\u9633\u575a\u767d\u3001\u8d3a\u4e5d\u516c\u7b49\u4eba\u56f4\u653b\uff0c\u6070\u597d\u6211\u7ecf\u8fc7\u90a3\u91cc\uff0c\u7ed9 \u5979\u89e3\u4e86\u56f4\uff0c\u5979\u662f\u4ece\u8d3a\u4e5d\u516c\u53e3\u4e2d\u77e5\u9053\u4f60\u524d\u665a\u9047\u9669\u4e4b\u4e8b\u3002\u540e\u6765\u6211\u4e0e\u9633\u575a\u767d\u4ea4\u624b\u4e4b\u65f6\uff0c\u9633\u575a\u767d\u628a\u6211 \u9519\u5f53\u4f5c\u891a\u5e08\u5144\uff0c\u56e0\u6b64\u6211\u4eec\u6599\u60f3\u524d\u665a\u5728\u8d3a\u4e5d\u516c\u5bb6\u91cc\u52a9\u4f60\u8131\u9669\u4e4b\u4eba\uff0c\u4e00\u5b9a\u662f\u891a\u5e08\u5144\u4e86\u3002\u5979\u662f\u4e0d\u662f \u77e5\u9053\u4f60\u5728\u627e\u5979\uff0c\u8fd9\u6211\u5c31\u4e0d\u77e5\u4e86\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u6768\u59d1\u5a18\u8fd8\u548c\u4f60\u8bf4\u4e86\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u5979\u8bf4\u5979\u8ba4\u8bc6\u4f60\uff0c\u4f46\u5979\u5374\u5947\u602a\u891a\u5e08\u5144\u4f55\u4ee5\u4f1a\u548c\u4f60\u4e00\u9053\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u7b11\u9053\uff1a\u201c\u8fd9\u4e5f\u602a\u4e0d\u5f97\u5979\u89c9\u5f97\u5947\u602a\uff0c\u6211\u4e5f\u662f\u524d\u5929\u665a\u4e0a\uff0c\u624d\u6e05\u695a\u891a\u4e91\u5cf0\u7684\u8eab\u4efd\u5462\u3002\u6768 \u59d1\u5a18\u5927\u7ea6\u8fd8\u5728\u7591\u5fc3\u4ed6\u662f\u91d1\u864f\u7684\u5978\u7ec6\u5427\u3002\u201d\u5f53\u4e0b\u628a\u90a3\u4e00\u665a\u5728\u7405\u739b\u5c71\u6240\u53d1\u751f\u7684\u4e8b\u60c5\u539f\u539f\u672c\u672c\u5730\u544a \u8bc9\u8c37\u6db5\u865a\u3002\u8c37\u6db5\u865a\u65b9\u59cb\u5f04\u660e\u767d\u6765\u9f99\u53bb\u8109\u3002 \u3000\u3000\u5b5f\u660e\u971e\u518d\u53c8\u95ee\u9053\uff1a\u201c\u5979\u662f\u4e0d\u662f\u4e5f\u8981\u5f80\u98de\u9f99\u5c71\u7684\uff1f\u201d \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u5979\u8981\u4e0a\u54ea\u513f\u6ca1\u6709\u544a\u8bc9\u6211\uff0c\u4e0d\u8fc7\uff0c\u5979\u5374\u662f\u548c\u51e0\u4e2a\u8499\u53e4\u4eba\u5728\u4e00\u8d77\u7684\u3002\u5176\u4e2d\u4e00\u4e2a \u6b66\u4e0a\u540d\u53eb\u963f\u76d6\u7684\uff0c\u548c\u6211\u4ea4\u4e86\u670b\u53cb\u5462\u3002\u53e6\u5916\u4e24\u4e2a\u5219\u662f\u5c11\u5973\uff0c\u770b\u8d77\u6765\u5012\u50cf\u662f\u4e3b\u4ec6\u7684\u6837\u5b50\u3002\u90a3\u4f4d\u8499 \u53e4\u5c0f\u59d0\uff0c\u6c14\u5ea6\u9ad8\u534e\uff0c\u4f3c\u4e4e\u4e0d\u662f\u666e\u901a\u4eba\u5bb6\u7684\u5973\u513f\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u5e76\u4e0d\u77e5\u9053\u8fd9\u4f4d\u201c\u8499\u53e4\u5c0f\u59d0\u201d\u7adf\u662f\u6210\u5409\u601d\u6c57\u7684\u5973\u513f\uff0c\u5b5f\u660e\u971e\u5374\u542c\u5f97\u674e\u601d\u5357\u8bf4\u8fc7\u660e\u6167 \u516c\u4e3b\u7684\u6545\u4e8b\uff0c\u731c\u60f3\u5341\u4e5d\u662f\u5979\u3002\u5f53\u4e0b\u60f3\u9053\uff1a\u201c\u5979\u548c\u660e\u6167\u516c\u4e3b\u5728\u4e00\u8d77\uff0c\u8fd9\u5012\u662f\u610f\u60f3\u4e0d\u5230\u4e4b\u4e8b\uff0c\u4e0d \u77e5\u660e\u6167\u516c\u4e3b\u4f55\u4ee5\u4f1a\u629b\u5934\u9732\u9762\uff0c\u6765\u5230\u4e2d\u539f\uff1f\u4f46\u6768\u5a49\u65e2\u662f\u548c\u5979\u540c\u5728\u4e00\u8d77\uff0c\u60f3\u5fc5\u662f\u4e0d\u4f1a\u5230\u98de\u9f99\u5c71\u53bb \u4e86\uff1f\u201d\u8fd9\u4e00\u6b21\u5b5f\u660e\u971e\u53ea\u731c\u4e2d\u4e86\u4e00\u534a\uff0c\u660e\u6167\u516c\u4e3b\u5f53\u7136\u662f\u4e0d\u4f1a\u5230\u98de\u9f99\u5c71\uff0c\u4f46\u6768\u5a49\u5374\u4ecd\u662f\u8981\u53bb\u7684\u3002 \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u539f\u6765\u4f60\u662f\u4e3a\u4e86\u627e\u90a3\u4f4d\u6768\u59d1\u5a18\u624d\u5230\u98de\u9f99\u5c71\u53bb\u7684\u5417\uff1f\u6211\u8fd8\u4ee5\u4e3a\u4f60\u662f\u4e3a\u4e86\u90a3\u4f4d\u674e \u76df\u4e3b\u7684\u4e8b\u60c5\u5462\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u9762\u4e0a\u4e00\u7ea2\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u96be\u9053\u6768\u5a49\u7adf\u7136\u628a\u6211\u4eec\u4e09\u4eba\u4e4b\u95f4\u7684\u4e8b\u60c5\u544a\u8bc9\u4e86\u4ed6\uff1f\u5979\u4e0e\u8c37 \u6db5\u865a\u4e0d\u8fc7\u662f\u521a\u521a\u76f8\u8bc6\uff0c\u7eb5\u7136\u5979\u5bf9\u6211\u5982\u4f55\u8bef\u4f1a\uff0c\u5982\u4f55\u4e0d\u6ee1\uff0c\u603b\u4e0d\u81f3\u4e8e\u5411\u4e00\u4e2a\u521a\u521a\u76f8\u8bc6\u7684\u670b\u53cb\u8bf4 \u5427\uff1f\u201d\u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u4f55\u4ee5\u4f60\u4f1a\u8fd9\u6837\u8ba4\u4e3a\uff1f\u201d \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u6211\u521a\u624d\u542c\u5f97\u8fd9\u4e24\u4e2a\u4eba\u8bf4\u51fa\u4e00\u4ef6\u79d8\u5bc6\uff0c\u4ed6\u4eec\u8bf4\u98de\u9f99\u5c71\u7684\u7aa6\u5be8\u4e3b\u662f\u9633\u5929\u96f7\u7684 \u4eba\uff0c\u8fd9\u6b21\u662f\u7279\u5730\u5b89\u6392\u4e86\u9677\u9631\uff0c\u60f3\u8bf1\u6355\u90a3\u4f4d\u674e\u76df\u4e3b\u7684\u3002\u6211\u4ee5\u4e3a\u4f60\u548c\u891a\u5e08\u5144\u4e5f\u542c\u5230\u4e86\u98ce\u58f0\uff0c\u662f\u4ee5 \u8981\u8d76\u5f80\u98de\u9f99\u5c71\u53bb\u63ed\u7834\u654c\u4eba\u7684\u9634\u8c0b\u7684\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u4f60\u8bf4\u7684\u662f\u8fd9\u4e2a\u79d8\u5bc6\u3002\u4e0d\u9519\uff0c\u8fd9\u4e2a\u79d8\u5bc6\u6211\u4eec\u662f\u65e9\u5df2\u77e5\u9053\u7684\u4e86\uff0c\u8fd8\u662f \u4f60\u7684\u891a\u5e08\u5144\u544a\u8bc9\u6211\u4eec\u7684\u5462\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u5bb6\u5e08\u56e0\u4e3a\u5929\u96f7\u529f\u5c1a\u672a\u7ec3\u5f97\u5927\u6210\uff0c\u4e0d\u80fd\u548c\u9633\u5929\u96f7\u786c\u62fc\u3002\u9633\u5929\u96f7\u903c\u4ed6\u51fa\u5c71\uff0c\u662f \u4ee5\u5bb6\u5e08\u53eb\u5c0f\u5f1f\u5047\u610f\u987a\u4ece\uff0c\u66ff\u4ed6\u51fa\u5c71\uff0c\u540d\u4e49\u4e0a\u662f\u5728\u9633\u5929\u96f7\u624b\u4e0b\u529e\u4e8b\uff0c\u5176\u5b9e\u5374\u662f\u4e3a\u4e49\u519b\u6253\u63a2\u6d88 \u606f\u3002\u6570\u6708\u524d\u5c0f\u5f1f\u884c\u85cf\u4e0d\u614e\uff0c\u5f15\u8d77\u4ed6\u7684\u7591\u5fc3\uff0c\u8fd9\u624d\u4ece\u5927\u90fd\u9003\u8dd1\u51fa\u6765\uff0c\u516c\u5f00\u53cd\u53db\u4ed6\u7684\u3002\u5982\u4eca\u4ed6\u6b63 \u5728\u4fa6\u9a91\u56db\u51fa\uff0c\u641c\u6355\u6211\u5462\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\uff0c\u602a\u4e0d\u5f97\u9633\u575a\u767d\u8fd9\u53ae\u4e00\u89c1\u4e86\u6211\uff0c\u5c31\u9a82\u6211\u5403\u91cc\u6252\u5916\u3002\u4ed6\u662f\u628a\u6211\u5f53\u4f5c \u4e86\u891a\u5e08\u5144\u4e86\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u82e6\u7b11\u9053\uff1a\u201c\u4f60\u7ed9\u9633\u575a\u767d\u8bef\u4f1a\uff0c\u6211\u4e5f\u66fe\u56e0\u6b64\u7ed9\u90a3\u4f4d\u6768\u59d1\u5a18\u8bef\u4f1a\u4e86\u5462\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u4ee4\u5c0a\u65e2\u7136\u77e5\u9053\u4e86\u5bf9\u65b9\u7684\u5978\u8c0b\uff0c\u4f9d\u7136\u524d\u5f80\u3002\u8fd9\u4e43\u662f\u660e\u77e5\u5c71\u6709\u864e\uff0c\u504f \u5411\u864e\u5c71\u884c\u4e86\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u4e0d\u9519\u3002\u4f46\u8fd9\u5374\u662f\u90a3\u4f4d\u674e\u76df\u4e3b\u7684\u610f\u601d\u3002\u4ed6\u8bf4\u4e0d\u5165\u864e\u7a74\uff0c\u7109\u5f97\u864e\u5b50\u3002\u6b63\u597d\u5c06\u8ba1 \u5c31\u8ba1\uff0c\u63ed\u7834\u5bf9\u65b9\u5978\u8c0b\uff0c\u628a\u98de\u9f99\u5c71\u7aa6\u5b89\u5e73\u7684\u90e8\u4e0b\u6536\u670d\u8fc7\u6765\u3002\u7aa6\u5b89\u5e73\u7f6a\u4e0d\u5bb9\u8bdb\uff0c\u4ed6\u7684\u90e8\u4e0b\u4ecd\u662f\u53ef \u7528\u7684\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u674e\u76df\u4e3b\u89c1\u8bc6\u8fc7\u4eba\uff0c\u4e0d\u6789\u4f60\u4eec\u63a8\u4e3e\u4ed6\u5145\u5f53\u76df\u4e3b\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u4ed6\u540d\u53eb\u674e\u601d\u5357\uff0c\u66fe\u5728\u8499\u53e4\u505a\u8fc7\u8bb8\u591a\u9707\u60ca\u9791\u5b50\u4e4b\u4e8b\u3002\u4ed6\u4e5f\u6b63\u662f\u90a3\u4f4d\u6768\u59d1\u5a18\u7684 \u672a\u5a5a\u592b\u3002\u8c37\u5e08\u5144\uff0c\u4f60\u5230\u98de\u9f99\u5c71\u5c31\u53ef\u4ee5\u89c1\u7740\u4ed6\u4e86\u3002\u8fd9\u4e2a\u4eba\u5012\u662f\u503c\u5f97\u4f60\u4ea4\u7ed3\u5462\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u6c89\u541f\u534a\u664c\uff0c\u8bf4\u9053\uff1a\u201c\u4ee4\u5c0a\u548c\u674e\u76df\u4e3b\u90fd\u662f\u6211\u60f3\u8981\u62dc\u89c1\u7684\u4eba\uff0c\u4e0d\u8fc7\u4ed6\u4eec\u5982\u4eca\u5df2\u6709\u5907\u800c \u5f80\uff0c\u6211\u5012\u662f\u7528\u4e0d\u7740\u6025\u7684\u5f80\u98de\u9f99\u5c71\u4e86\u3002\u5509\uff0c\u53ea\u53ef\u60dc\u5982\u4eca\u7ebf\u7d22\u4e2d\u65ad\uff0c\u4e0d\u77e5\u5230\u4f55\u5904\u627e\u5bfb\u4f60\u7684\u8868 \u59d0\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u5ffd\u9053\uff1a\u201c\u8c37\u5e08\u5144\uff0c\u4f60\u53ef\u4ee5\u5230\u8fd9\u4e2a\u5730\u65b9\u8bd5\u4e00\u8bd5\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u5fd9\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\u5730\u65b9\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u59d3\u9c81\u7684\u90a3\u53ae\u8bf4\u51fa\u4e86\u2018\u9ed1\u77f3\u2019\u4e8c\u5b57\uff0c\u636e\u5c0f\u5f1f\u6240\u77e5\uff0c\u4e0e\u2018\u9ed1\u77f3\u2019\u6709\u5173\u7684\uff0c\u6709\u4e24 \u4e2a\u5730\u65b9\u548c\u4e00\u4e2a\u4eba\uff0c\u5180\u5317\u84df\u53bf\u6709\u4e2a\u9ed1\u77f3\u5c97\uff0c\u98de\u9f99\u5c71\u5357\u9762\u4e09\u767e\u91cc\u4e4b\u5904\u6709\u4e2a\u9ed1\u77f3\u5e84\u3002\u53e6\u5916\u8fd8\u6709\u4e2a\u72ec \u811a\u5927\u76d7\u662f\u4e2a\u9053\u58eb\uff0c\u9053\u53f7\u53eb\u505a\u9ed1\u77f3\uff0c\u4f46\u9053\u4eba\u884c\u8e2a\u4e0d\u5b9a\u7684\uff0c\u4f9d\u5c0f\u5f1f\u4e4b\u89c1\uff0c\u5bf9\u65b9\u65e2\u7136\u662f\u8981\u4e25\u5c0f\u59d0\u6765 \u5a01\u80c1\u5b5f\u5927\u4fa0\uff0c\u60f3\u5fc5\u5979\u7684\u6240\u5728\u4e4b\u5904\u548c\u98de\u9f99\u5c71\u8ddd\u79bb\u4e0d\u4f1a\u592a\u8fdc\uff0c\u56e0\u6b64\u8c37\u5e08\u5144\u4e0d\u59a8\u5148\u5230\u9ed1\u77f3\u5e84\u6253\u542c\u6253 \u542c\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u591a\u8c22\u891a\u5e08\u5144\u6307\u6559\u3002\u4f46\u4e0d\u77e5\u9ed1\u77f3\u5e84\u53ef\u6709\u4ec0\u4e48\u53ef\u7591\u7684\u6b66\u6797\u4eba\u7269\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u9ed1\u77f3\u5e84\u6709\u4e2a\u5927\u8d22\u4e3b\uff0c\u5916\u53f7\u6d3b\u960e\u7f57\uff0c\u4f46\u4ed6\u4f1a\u4e0d\u4f1a\u6b66\u529f\uff0c\u6211\u5c31\u4e0d\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u597d\uff0c\u65e2\u7136\u6709\u8fd9\u4e00\u6761\u7ebf\u7d22\uff0c\u6211\u603b\u5f97\u53bb\u627e\u5979\u4e00\u627e\u624d\u80fd\u5b89\u5fc3\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u5ffd\u9053\uff1a\u201c\u8c37\u5927\u54e5\uff0c\u4f60\u662f\u4e0d\u662f\u6015\u89c1\u6211\u7684\u8868\u59d0\uff1f\u201d \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u6211\u77e5\u9053\u5979\u662f\u5728\u627e\u5bfb\u4f60\uff0c\u4f46\u5982\u4eca\u8fc7\u4e86\u56db\u5e74\uff0c\u4f60\u4eec\u8fd8\u6ca1\u89c1\u7740\u9762\uff0c\u60f3\u5fc5\u662f\u4e00\u76f4\u5728 \u8eb2\u907f\u5979\u4e86\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u7ed9\u5979\u8bf4\u4e2d\u5fc3\u75c5\uff0c\u53ea\u597d\u4f4e\u5934\u9ed8\u8ba4\u3002\u8fc7\u4e86\u534a\u664c\uff0c\u624d\u53f9\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u672c\u6765\u662f\u4e0d\u5e94\u8be5 \u518d\u89c1\u5979\u4e86\uff0c\u4f46\u5982\u4eca\u5979\u6709\u4e86\u5371\u9669\uff0c\u6211\u53c8\u600e\u80fd\u7f6e\u4e4b\u4e0d\u7406\uff1f\u201d \u3000\u3000\u5b5f\u660e\u971e\u6b63\u5bb9\u8bf4\u9053\uff1a\u201c\u4f60\u8bf4\u4e0d\u8be5\u89c1\u5979\uff0c\u4f60\u8fd9\u4e48\u8bf4\uff0c\u5012\u662f\u4f60\u5927\u5927\u7684\u4e0d\u8be5\u4e86\u3002\u4f60\u77e5\u4e0d\u77e5\u9053\u5979\u662f \u5982\u4f55\u7684\u60f3\u8981\u89c1\u4f60\uff1f\u4e3a\u4e86\u8981\u89c1\u4f60\uff0c\u5979\u4e0d\u60dc\u548c\u5bb6\u5ead\u51b3\u88c2\uff0c\u4f60\u5012\u8bf4\u4e0d\u8be5\u89c1\u5979\uff01\u201d\u5f53\u4e0b\u628a\u90a3\u5929\u665a\u4e0a\uff0c \u5979\u4e0e\u4e25\u70f7\u56de\u5bb6\u4e4b\u540e\u6240\u53d1\u751f\u7684\u4e8b\u60c5\u4e00\u4e94\u4e00\u5341\u5730\u544a\u8bc9\u8c37\u6db5\u865a\u3002\u8c37\u6db5\u865a\u542c\u8bf4\u4e25\u70f7\u4e3a\u4ed6\u7edd\u98df\uff0c\u8feb\u4f7f\u7236 \u6bcd\u4e0d\u80fd\u4e0d\u8ba9\u5b5f\u660e\u971e\u653e\u5979\u51fa\u6765\uff0c\u4e0d\u7531\u5f97\u5927\u4e3a\u611f\u52a8\uff0c\u773c\u6cea\u6e7f\u900f\u4e86\u8499\u9762\u7684\u9ed1\u5dfe\u3002 \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u660e\u971e\uff0c\u6211\u77e5\u9053\u5979\u662f\u4f1a\u4e3a\u4e86\u6211\u4e0d\u60dc\u4efb\u4f55\u727a\u7272\u7684\uff0c\u4f46\u4f60\u5374\u4e0d\u77e5\u9053\u2026\u2026\u201d \u3000\u3000\u8bdd\u72b9\u672a\u4e86\uff0c\u5b5f\u660e\u971e\u5df2\u5728\u8bf4\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u4e0d\u6562\u89c1\u5979\uff0c\u4e0d\u8fc7\u662f\u56e0\u4e3a\u4f60\u7684\u521b\u4f24\u672a\u6108\u7f62\u4e86\u3002\u90a3 \u5929\u665a\u4e0a\uff0c\u5f20\u5143\u5409\u523a\u4e86\u4f60\u4e00\u5251\uff0c\u8fd9\u4e00\u5251\u4e0d\u662f\u523a\u5728\u4f60\u7684\u9762\u4e0a\uff0c\u800c\u662f\u523a\u5728\u4f60\u7684\u5fc3\u4e0a\uff01\u201d \u3000\u3000\u8c37\u6db5\u865a\u518d\u6b21\u7ed9\u5979\u8bf4\u4e2d\u5fc3\u75c5\uff0c\u5fcd\u4e0d\u4f4f\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4e5f\u8bb8\u662f\u6211\u5fc3\u4e0a\u7684\u5251\u4f24\u672a\u6108\u3002\u4f46\u5982\u679c\u4f60 \u89c1\u7740\u4e86\u6211\u7684\u9762\u8c8c\uff0c\u53ea\u6015\u4f60\u4e5f\u4f1a\u5403\u4e00\u60ca\u7684\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u4ea2\u58f0\u8bf4\u9053\uff1a\u201c\u5979\u559c\u6b22\u7684\u662f\u4f60\u8fd9\u4e2a\u4eba\uff0c\u5c82\u4f1a\u65a4\u65a4\u8ba1\u8f83\u4f60\u7684\u5bb9\u8c8c\uff1f\u4f60\u4e3a\u5979\u53d7\u4e86\u4f24\uff0c\u5979 \u53ea\u6709\u66f4\u52a0\u7684\u559c\u6b22\u4f60\u3002\u4f46\u4f60\u5374\u4e3a\u6b64\u800c\u8eb2\u907f\u5979\uff0c\u8fd9\u53cd\u800c\u662f\u5c0f\u770b\u5979\u4e86\uff01\u201d \u3000\u3000\u5fc3\u75c5\u8fd8\u987b\u5fc3\u836f\u533b\uff0c\u5b5f\u660e\u971e\u8fd9\u51e0\u53e5\u8bdd\u597d\u50cf\u5f53\u5934\u68d2\u559d\uff0c\u8c37\u6db5\u865a\u542c\u8fdb\u5fc3\u4e2d\uff0c\u767b\u65f6\u5fc3\u60c5\u5f00\u6717\uff0c\u63ed \u4e0b\u4e86\u8499\u9762\u5dfe\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5927\u4e08\u592b\u4f55\u60e7\u4ee5\u771f\u9762\u793a\u4eba\uff01\u201d \u3000\u3000\u5b5f\u660e\u971e\u4e4d\u7779\u4ed6\u8fd9\u4e11\u964b\u7684\u5bb9\u989c\uff0c\u5fc3\u4e2d\u5176\u5b9e\u4e5f\u6709\u70b9\u5bb3\u6015\uff0c\u5979\u5374\u7eb5\u58f0\u7b11\u9053\uff1a\u201c\u8c37\u5927\u54e5\uff0c\u4f60\u5728\u6211 \u7684\u773c\u4e2d\u8fd8\u662f\u548c\u4ece\u524d\u4e00\u6837\u5440\uff01\u201d\u8c37\u6db5\u865a\u9053\uff1a\u201c\u5f53\u771f\u4e00\u6837\uff1f\u201d\u5b5f\u660e\u971e\u9053\uff1a\u201c\u4f60\u4ece\u524d\u4e0d\u8fc7\u662f\u559c\u6b22\u6253 \u62b1\u4e0d\u5e73\u7684\u4fa0\u58eb\uff0c\u5982\u4eca\u5374\u662f\u4e2a\u4e0d\u60dc\u6df1\u5165\u864e\u7a74\uff0c\u8a93\u6297\u5f3a\u864f\u7684\u82f1\u96c4\u3002\u771f\u6b63\u8bf4\u6765\uff0c\u73b0\u5728\u7684\u4f60\u6bd4\u4ece\u524d\u7684 \u4f60\uff0c\u90a3\u662f\u66f4\u4ee4\u4eba\u4f69\u670d\u4e86\uff01\u201d \u3000\u3000\u8c37\u6db5\u865a\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u591a\u8c22\u4f60\u89e3\u5f00\u4e86\u6211\u5fc3\u5934\u7684\u7ed3\uff0c\u4f46\u613f\u6211\u914d\u5f97\u4e0a\u4f60\u6240\u8bf4\u7684\u8bdd\u3002\u201d\u5f53\u4e0b\u4e0e\u891a \u4e91\u5cf0\u7ea6\u597d\u4e86\u5c06\u6765\u5728\u98de\u9f99\u5c71\u89c1\u9762\uff0c\u4fbf\u5373\u5728\u6668\u5149\u66e6\u5fae\u4e4b\u4e2d\uff0c\u5927\u8e0f\u6b65\u800c\u53bb\u3002\u6b63\u662f\uff1a \u3000\u3000\u8138\u4e0a\u52ab\u75d5\u4f9d\u65e7\u5728\uff0c\u5fc3\u5982\u767d\u7389\u6d01\u65e0\u7455\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b\u2014\u2014\u7b2c\u4e09\u5341\u516d\u56de\u3000\u7834\u5e3d\u906e\u989c\u5bfb\u65e7\u4fa3\u3000\u534e\u5802\u7ed3\u5f69\u95f9\u65b0\u5a18 \u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b \u7b2c\u4e09\u5341\u516d\u56de\u3000\u7834\u5e3d\u906e\u989c\u5bfb\u65e7\u4fa3\u3000\u534e\u5802\u7ed3\u5f69\u95f9\u65b0\u5a18 \u3000\u3000\u891a\u4e91\u5cf0\u7b11\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u8c37\u5e08\u5144\u5982\u6b64\u82f1\u96c4\uff0c\u5bf9\u65e0\u5173\u8f7b\u91cd\u7684\u5bb9\u8c8c\u7adf\u7136\u4f1a\u770b\u4e0d\u5f00\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u4ed6\u4ee5\u524d\u672c\u662f\u4e2a\u5341\u5206\u82f1\u4fca\u7684\u5c11\u5e74\u7684\u3002\u54b3\uff0c\u6bcf\u4e00\u4e2a\u4eba\u90fd\u53ef\u80fd\u6216\u591a\u6216\u5c11\u7684\u6709\u70b9\u4ec0 \u4e48\u5fc3\u75c5\uff0c\u53ea\u662f\u81ea\u5df1\u4e0d\u77e5\u800c\u5df2\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u4e43\u662f\u6709\u611f\u800c\u53d1\u3002\u891a\u4e91\u5cf0\u542c\u4e86\uff0c\u5ffd\u5730\u604d\u7136\u5982\u6709\u6240\u609f\uff0c\u8bf4\u9053\uff1a\u201c\u539f\u6765\u90a3\u4f4d\u6768\u59d1\u5a18\u662f\u674e \u601d\u5357\u7684\u672a\u5a5a\u59bb\u5b50\uff0c\u82e5\u4e0d\u662f\u4f60\u521a\u624d\u8bf4\u4e86\u51fa\u6765\uff0c\u6211\u8fd8\u4e0d\u77e5\u9053\u5462\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u7b11\u9053\uff1a\u201c\u8fd9\u4e5f\u662f\u65e0\u5173\u91cd\u8981\u7684\u522b\u4eba\u4e4b\u4e8b\uff0c\u4f60\u77e5\u4e0d\u77e5\u9053\u53c8\u6709\u4ec0\u4e48\u5173\u7cfb\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\u3002\u6211\u53ea\u662f\u6709\u4e00\u4e8b\u672a\u660e\uff0c\u4f55\u4ee5\u5979\u5728\u5c71\u5be8\u4e4b\u4e2d\uff0c\u8981\u5973\u626e\u7537\u88c5\uff0c\u4e0d\u8ba9\u522b\u4eba \u77e5\u9053\u5979\u7684\u8eab\u4efd\uff1f\u5982\u4eca\u53c8\u8981\u79c1\u81ea\u4e0b\u5c71\uff0c\u7d2f\u5f97\u4f60\u53bb\u627e\u5979\uff1f\u201d \u3000\u3000\u5b5f\u660e\u971e\u7b11\u4e86\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u4fe1\u4f60\u73b0\u5728\u8fd8\u4e0d\u660e\u767d\uff0c\u4f60\u8fd9\u662f\u660e\u77e5\u6545\u95ee\u5427\uff1f\u201d \u3000\u3000\u539f\u6765\u5b5f\u660e\u971e\u672c\u662f\u4e0d\u60f3\u7ed9\u891a\u4e91\u5cf0\u77e5\u9053\u5979\u548c\u6768\u5a49\u4e4b\u95f4\u7684\u8bef\u4f1a\uff0c\u4f46\u56e0\u76f8\u5904\u6570\u65e5\u4e4b\u540e\uff0c\u4e24\u4eba\u60c5\u610f \u76f8\u6295\uff0c\u5b5f\u660e\u971e\u89c9\u5f97\u5df2\u662f\u65e0\u987b\u7792\u4f4f\u4ed6\u4e86\u3002\u5979\u521a\u624d\u5bf9\u8c37\u6db5\u865a\u8bf4\u51fa\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u5176\u5b9e\u4e5f\u662f\u8bf4\u7ed9\u891a\u4e91\u5cf0 \u542c\u7684\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u7684\u786e\u662f\u65e9\u5df2\u731c\u5230\u51e0\u5206\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u660e\u971e\u662f\u4e2a\u723d\u6717\u7684\u59d1\u5a18\uff0c\u6211\u53c8\u4f55\u5fc5\u628a\u8bdd\u95f7\u5728\u5fc3 \u91cc\uff1f\u201d\u4e8e\u662f\u4e5f\u8ddf\u7740\u7b11\u4e86\u4e00\u7b11\u8bf4\u9053\uff1a\u201c\u662f\u4e0d\u662f\u90a3\u4f4d\u6768\u59d1\u5a18\u4e5f\u6000\u6709\u4ec0\u4e48\u5fc3\u75c5\uff1f\u201d\u5b5f\u660e\u971e\u53cc\u988a\u5fae \u7ea2\uff0c\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u7b11\u9053\uff1a\u201c\u8c37\u5e08\u5144\u7684\u5fc3\u75c5\u7ed9\u4f60\u533b\u597d\uff0c\u6768\u59d1\u5a18\u7684\u5fc3\u75c5\uff0c\u6050\u6015\u4e5f\u662f\u8981\u4f60\u7ed9\u5979\u533b\u6cbb\u624d\u80fd\u5f97 \u597d\u3002\u660e\u971e\uff0c\u60f3\u4e0d\u5230\u4f60\u5012\u662f\u4e2a\u5584\u4e8e\u533b\u6cbb\u522b\u4eba\u5fc3\u75c5\u7684\u540d\u533b\u5462\uff01\u201d \u3000\u3000\u5b5f\u660e\u971e\u4f6f\u55d4\u8bf4\u9053\uff1a\u201c\u4e91\u5cf0\uff0c\u6211\u53ef\u4e0d\u8bb8\u4f60\u7b11\u6211\uff01\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u4e0d\uff0c\u6211\u611f\u8c22\u4f60\u8fd8\u6765\u4e0d\u53ca\u5462\uff0c\u54ea\u91cc\u662f\u5728\u7b11\u4f60\uff1f\u201d \u3000\u3000\u5b5f\u660e\u971e\u8be7\u9053\uff1a\u201c\u4f60\u611f\u8c22\u6211\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u611f\u8c22\u4f60\u4e5f\u7ed9\u6211\u533b\u597d\u4e86\u5fc3\u75c5\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u4e00\u65f6\u95f4\u672a\u80fd\u9886\u609f\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9000\uff1a\u201c\u5b9e\u4e0d\u76f8\u7792\uff0c\u8d77\u521d\u6211\u4e5f\u662f\u6000\u7740\u548c\u6768\u59d1\u5a18\u540c\u6837\u7684\u5fc3\u75c5\uff0c\u4ee5\u4e3a\uff0c\u4ee5\u4e3a\u4f60\u662f\u2026\u2026\u201d \u3000\u3000\u5e95\u4e0b\u7684\u8bdd\uff0c\u65e0\u987b\u891a\u4e91\u5cf0\u81ea\u5df1\u8bf4\u51fa\u6765\uff0c\u5b5f\u660e\u971e\u5df2\u662f\u77e5\u9053\u3002\u891a\u4e91\u5cf0\u662f\u56e0\u4e3a\u8bef\u4f1a\u5979\u4e0e\u674e\u601d\u5357\u76f8 \u7231\uff0c\u6240\u4ee5\u624d\u4e0d\u6562\u628a\u5fc3\u4e8b\u5bf9\u5979\u8bf4\u51fa\u6765\u7684\u3002 \u3000\u3000\u76c2\u660e\u971e\u53cc\u988a\u6655\u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u73b0\u5728\u4f60\u90fd\u660e\u767d\u4e86\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u90fd\u660e\u767d\u4e86\u3002\u660e\u971e\uff0c\u73b0\u5728\u6211\u53ef\u771f\u662f\u653e\u5fc3\u5566\uff01\u201d\u5b5f\u660e\u971e\u201c\u5657\u55e4\u201d\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a \u201c\u4f60\u8fd9\u4e2a\u50bb\u5b50\uff01\u201d\u4e24\u4eba\u5fc3\u5e95\u7684\u9634\u9709\uff0c\u5c3d\u90fd\u5728\u8fd9\u4e00\u7b11\u4e4b\u4e2d\u626b\u9664\u5e72\u51c0\u4e86\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4f46\u613f\u5929\u4e0b\u6709\u60c5\u4eba\u90fd\u6210\u7737\u5c5e\uff0c\u8c37\u5e08\u5144\u6b64\u53bb\uff0c\u80fd\u591f\u548c\u4f60\u7684\u8868\u59d0\u4e00\u540c\u56de \u6765\u3002\u201d \u3000\u3000\u4e14\u8bf4\u8c37\u6db5\u865a\u4e0e\u891a\u3001\u5b5f\u4e8c\u4eba\u5206\u624b\u4e4b\u540e\uff0c\u4fbf\u5373\u72ec\u81ea\u4e1c\u884c\uff0c\u51c6\u5907\u5230\u98de\u9f99\u5c71\u4e1c\u5357\u4e09\u767e\u91cc\u5916\u7684\u9ed1\u77f3 \u5e84\u53bb\u627e\u4e25\u70f7\u3002\u4ed6\u5728\u4e00\u4e2a\u5c0f\u5e02\u9547\u4e0a\u4e70\u4e86\u4e00\u4e2a\u836f\u7bb1\u548c\u51e0\u6837\u5e38\u7528\u7684\u836f\u6750\uff0c\u80cc\u4e0a\u836f\u7bb1\uff0c\u626e\u4f5c\u4e00\u4e2a\u8d70\u6c5f \u6e56\u7684\u90ce\u4e2d\uff0c\u4ed6\u7684\u957f\u5251\u5c31\u85cf\u5728\u836f\u7bb1\u4e4b\u4e2d\u3002 \u3000\u3000\u4e09\u767e\u91cc\u8def\u7a0b\uff0c\u8c37\u6db5\u865a\u4e0d\u8fc7\u8d70\u4e86\u4e00\u5927\uff0c\u7b2c\u4e8c\u5929\u4e0a\u5348\uff0c\u4fbf\u5df2\u5230\u4e86\u9ed1\u77f3\u5e84\u4e86\u3002 \u3000\u3000\u4e00\u8def\u884c\u6765\uff0c\u8c37\u6db5\u865a\u597d\u51e0\u6b21\u78b0\u89c1\u5feb\u9a6c\u9a70\u8fc7\uff0c\u9a91\u9a6c\u7684\u4eba\u90e8\u5e26\u6709\u5175\u5668\uff0c\u4e00\u770b\u5c31\u77e5\u662f\u6c5f\u6e56\u4e0a\u7684\u4eba \u7269\u3002\u6700\u540e\u4e00\u6b21\uff0c\u662f\u5728\u8ddd\u79bb\u9ed1\u77f3\u5e84\u7ea6\u5341\u91cc\u4e4b\u5904\u78b0\u4e0a\u7684\uff0c\u4f46\u9a91\u9a6c\u7684\u5374\u662f\u4e24\u4e2a\u519b\u5b98\u3002 \u3000\u3000\u8c37\u6db5\u865a\u6697\u81ea\u8d77\u7591\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u90a3\u65e5\u8fd9\u4e9b\u4eba\u90fd\u662f\u53bb\u627e\u90a3\u4e2a\u9ed1\u77f3\u5e84\u7684\u6210\u5e84\u4e3b\u7684\uff1f\u8fd9\u4e2a\u6210\u5e84 \u4e3b\u65e2\u7136\u662f\u4e00\u65b9\u7684\u6076\u9738\uff0c\u60f3\u5fc5\u4e5f\u662f\u591a\u5c11\u4f1a\u70b9\u6b66\u529f\u7684\u4e86\u3002\u201d \u3000\u3000\u5e84\u53e3\u7684\u8def\u65c1\u6709\u4e2a\u8336\u9986\uff0c\u8c37\u6db5\u865a\u5e0c\u671b\u80fd\u591f\u6253\u542c\u5230\u4e00\u4e9b\u5173\u4e8e\u9ed1\u77f3\u5e84\u7684\u6d88\u606f\uff0c\u4fbf\u8fdb\u53bb\u559d\u8336\u3002 \u3000\u3000\u8336\u9986\u7684\u4e3b\u4eba\u770b\u89c1\u4e00\u4e2a\u9762\u4e0a\u6709\u5200\u75a4\u7684\u201c\u6076\u6c49\u201d\u8d70\u8fdb\u6765\uff0c\u4e0d\u7981\u5403\u4e86\u4e00\u60ca\uff0c\u614c\u5fd9\u6218\u6218\u5162\u5162\u5730\u6367 \u4e0a\u8336\u6765\u3002\u8c37\u6db5\u865a\u559d\u8fc7\u4e4b\u540e\uff0c\u4f38\u624b\u638f\u94b1\uff0c\u8336\u9986\u4e3b\u4eba\u8fde\u5fd9\u8bf4\u9053\uff1a\u201c\u8fd9\uff0c\u8fd9\u662f\u6211\u5b5d\u656c\u4f60\u8001\u7684\u3002\u4e00\u7897 \u7c97\u8336\uff0c\u4e0d\u6210\u656c\u610f\uff0c\u4f60\u8001\u8fd8\u8981\u5403\u4e9b\u4ec0\u4e48\uff0c\u5c3d\u7ba1\u5429\u5490\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u662f\u5c0f\u672c\u751f\u610f\uff0c\u54ea\u6709\u559d\u4e86\u4f60\u7684\u8336\u4e0d\u4ed8\u94b1\u7684\u9053\u7406\u3002\u201d\u5f53\u4e0b\u638f\u51fa\u4e86\u4e8c\u94b1\u94f6 \u5b50\uff0c\u7eb3\u5165\u4ed6\u7684\u6000\u4e2d\uff0c\u903c\u4ed6\u6536\u4e0b\u3002 \u3000\u3000\u4e00\u7897\u8336\u4e0d\u8fc7\u662f\u5356\u4e00\u6587\u94dc\u94b1\uff0c\u4e8c\u94b1\u94f6\u5b50\uff0c\u5927\u53ef\u4ee5\u5403\u4e00\u53ea\u80a5\u9e21\u4e86\u3002\u5e97\u4e3b\u4eba\u82e6\u7740\u8138\u9053\uff1a\u201c\u5c0f\u5e97 \u53ea\u6709\u5364\u725b\u8089\uff0c\u4e34\u65f6\u6050\u6015\u4e70\u4e0d\u5230\u9e21\u9e2d\u5949\u5ba2\u3002\u4f60\u8001\u8981\u559d\u9152\u5417\uff1f\u4e00\u65a4\u7ecd\u9152\uff0c\u4e00\u65a4\u5364\u725b\u8089\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u8c37\u6db5\u865a\u7b11\u9053\uff1a\u201c\u6211\u5e76\u4e0d\u809a\u997f\uff0c\u53ea\u662f\u53e3\u559d\uff0c\u4e0d\u7528\u4f60\u8d39\u795e\u5f20\u7f57\u4e86\u3002\u8336\u5df2\u559d\u8fc7\uff0c\u6211\u5c31\u8981\u8d70 \u7684\u3002\u201d \u3000\u3000\u5e97\u4e3b\u4eba\u6014\u4e86\u4e00\u6014\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8001\u7ed9\u7684\u662f\u4e8c\u94b1\u94f6\u5b50\u2026\u2026\u201d \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u5bf9\u4e0d\u4f4f\uff0c\u6211\u8eab\u4e0a\u6ca1\u5e26\u96f6\u94b1\u3002\u8fd9\u4e8c\u94b1\u94f6\u5b50\u662f\u7ed9\u4f60\u7684\uff0c\u4f60\u4e0d\u7528\u627e\u7ed9\u4e86\u3002\u201d \u3000\u3000\u5e97\u4e3b\u4eba\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u8001\u513f\u4e0d\u6562\u53d7\u5ba2\u5b98\u539a\u8d50\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u7b11\u9053\uff1a\u201c\u4f60\u5356\u8336\uff0c\u6211\u5356\u836f\uff0c\u54b1\u4eec\u5c31\u4ea4\u4e2a\u670b\u53cb\u5427\u3002\u4f60\u518d\u63a8\u8f9e\uff0c\u90a3\u5c31\u662f\u770b\u4e0d\u8d77\u6211 \u4e86\u3002\u201d \u3000\u3000\u5e97\u4e3b\u4eba\u89c1\u8c37\u6db5\u865a\u548c\u989c\u60a6\u8272\uff0c\u4e0d\u50cf\u5f00\u4ed6\u73a9\u7b11\uff0c\u8fd9\u624d\u653e\u5fc3\u6536\u4e0b\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6211\u8fd8\u53ea\u9053\u4ed6\u662f \u9ed1\u9053\u4e2d\u4eba\u5462\uff0c\u5374\u539f\u6765\u4ed6\u7684\u76f8\u8c8c\u867d\u7136\u51f6\u6076\uff0c\u5374\u662f\u4e00\u4e2a\u5927\u5927\u7684\u597d\u4eba\u3002\u201d \u3000\u3000\u8336\u9986\u4e3b\u4eba\u8fde\u8fde\u9053\u8c22\uff0c\u6536\u4e0b\u94f6\u5b50\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u8001\u662f\u4e0a\u54ea\u513f\uff1f\u201d \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u542c\u8bf4\u8d35\u5730\u6709\u4f4d\u6210\u5927\u8d22\u4e3b\uff0c\u4e0d\u77e5\u4ed6\u5bb6\u5728\u54ea\u513f\uff1f\u201d \u3000\u3000\u8336\u9986\u4e3b\u4eba\u9762\u8272\u4e00\u53d8\uff0c\u8bf4\u9053\uff1a\u201c\u539f\u6765\u4f60\u4e5f\u662f\u5230\u6210\u5bb6\u559d\u559c\u9152\u7684\u5417\uff1f\u5931\u656c\u3001\u5931\u656c\u3002\u201d\u53e3\u4e2d\u8bf4\u662f \u201c\u5931\u656c\u201d\uff0c\u5176\u5b9e\u5374\u662f\u60e7\u610f\u591a\u4e8e\u656c\u610f\u3002 \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u4e0d\uff0c\u6211\u662f\u4e2a\u8dd1\u6c5f\u6e56\u7684\u7a77\u90ce\u4e2d\uff0c\u4e0e\u6210\u5927\u8d22\u4e3b\u54ea\u91cc\u9ad8\u6500\u5f97\u4e0a\uff1f\u201d\u653e\u4f4e\u58f0\u97f3\uff0c\u7b11 \u9053\uff1a\u201c\u6211\u4e0d\u7792\u4f60\uff0c\u6211\u53ea\u662f\u60f3\u53bb\u6253\u6253\u79cb\u98ce\uff0c\u5356\u5356\u5047\u836f\u800c\u5df2\u3002\u201d \u3000\u3000\u8336\u9986\u4e3b\u4eba\u8fd9\u624d\u653e\u4e0b\u5fc3\u4e0a\u7684\u77f3\u5934\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u90a3\u6211\u5c31\u529d\u4f60\u8001\u5144\u4e0d\u5fc5\u6253\u8fd9\u4e3b\u610f\u4e86\u3002\u201d \u3000\u3000\u8c37\u51fd\u865a\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u8336\u9986\u4e3b\u4eba\u4e5f\u653e\u4f4e\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u4f60\u8001\u5144\u662f\u5916\u8def\u4eba\uff0c\u6211\u4e0d\u6015\u8bf4\u7ed9\u4f60\u542c\u3002\u8fd9\u6210\u5927\u8d22\u4e3b\u4e43\u662f\u4e2a\u4e3a \u5bcc\u4e0d\u4ec1\u7684\u8d22\u4e3b\uff0c\u4ed6\u6709\u4e2a\u5916\u53f7\u53eb\u6d3b\u960e\u7f57\uff0c\u4ed6\u4e0d\u6253\u54b1\u4eec\u7a77\u4eba\u7684\u4e3b\u610f\u5df2\u7ecf\u597d\u4e86\uff0c\u4f60\u8fd8\u60f3\u6253\u4ed6\u7684\u4e3b \u610f\uff1f\u7ed9\u4ed6\u770b\u7834\u4e86\u5356\u5047\u836f\uff0c\u53ea\u6015\u4f60\u8981\u7ed9\u4ed6\u767d\u505a\u4e09\u5e74\u957f\u5de5\u5462\uff01\u201d \u3000\u3000\u8c37\u6db5\u865a\u4f38\u4f38\u820c\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e48\u5389\u5bb3\uff01\u201d \u3000\u3000\u8336\u9986\u4e3b\u4eba\u9053\uff1a\u201c\u4e0d\u5389\u5bb3\u4e5f\u4e0d\u53eb\u6d3b\u960e\u7f57\u4e86\uff01\u201d \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u6210\u5bb6\u6709\u4ec0\u4e48\u559c\u5e86\u4e4b\u4e8b\uff1f\u6211\u4e00\u8def\u4e0a\u78b0\u5230\u597d\u591a\u9a91\u9a6c\u7684\u4eba\uff0c\u6562\u60c5\u90fd\u662f\u5230\u4ed6\u5bb6\u559d\u559c \u9152\u7684\u3002\u201d \u3000\u3000\u8336\u9986\u4e3b\u4eba\u9053\uff1a\u201c\u4ed6\u7684\u513f\u5b50\uff0c\u4eca\u65e5\u5a36\u4eb2\uff0c\u8fd9\u4e24\u5929\u4ece\u5c0f\u5e97\u95e8\u524d\u7ecf\u8fc7\u7684\u8d3a\u5ba2\u53ef\u771f\u4e0d\u5c11\u5462\uff0c\u6240\u4ee5 \u6211\u624d\u4f1a\u4ee5\u4e3a\u4f60\u8001\u5144\u4e5f\u662f\u53bb\u559d\u559c\u9152\u7684\u3002\u201d\u63a5\u7740\u53c8\u4f4e\u58f0\u9053\uff1a\u201c\u8fd9\u95e8\u4eb2\u4e8b\u662f\u62a2\u6765\u7684\uff01\u201d \u3000\u3000\u8c37\u6db5\u865a\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u662f\u62a2\u4eb2\uff1f\u201d \u3000\u3000\u8336\u9986\u4e3b\u4eba\u9053\uff1a\u201c\u662f\u5440\uff0c\u90a3\u4e2a\u53ef\u601c\u7684\u5973\u5b50\u8fd8\u662f\u5916\u5730\u4eba\u5462\uff01\u201d \u3000\u3000\u8c37\u6db5\u865a\u66f4\u662f\u5403\u60ca\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u83ab\u975e\u5c31\u662f\u4e25\u70f7\uff1f\u201d \u3000\u3000\u8336\u9986\u4e3b\u4eba\u770b\u770b\u5929\u8272\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e2a\u65f6\u5019\u6050\u6015\u5df2\u5728\u62dc\u5802\u3002\u5509\uff0c\u90a3\u5973\u5b50\u771f\u53ef\u601c\uff01\u201d\u60f3\u548c\u8c37\u6db5 \u865a\u8bf4\u90a3\u5973\u5b50\u7684\u4e8b\u60c5\uff0c\u8c37\u6db5\u865a\u5df2\u662f\u53cc\u624b\u4e00\u62f1\uff0c\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u8001\u4e08\u89c1\u544a\u3002\u201d\u5fd9\u7684\u4fbf\u8dd1\u51fa\u53bb\u4e86\u3002 \u3000\u3000\u8c37\u6db5\u865a\u6697\u81ea\u601d\u91cf\uff1a\u201c\u6309\u8bf4\u4e25\u70f7\u7684\u6b66\u529f\uff0c\u4e0d\u5e94\u8be5\u843d\u5728\u4e00\u4e2a\u571f\u9738\u7684\u624b\u91cc\uff0c\u4f46\u53ea\u6015\u4f17\u5be1\u4e0d\u654c\uff0c \u5931\u624b\u88ab\u64d2\uff0c\u4e5f\u662f\u6709\u7684\u3002\u4e0d\u7ba1\u662f\u4e0d\u662f\u5979\uff0c\u8fd9\u6869\u4e8b\u65e2\u7136\u7ed9\u6211\u649e\u4e0a\uff0c\u6211\u5c31\u975e\u7ba1\u4e0d\u53ef\uff01\u201d \u3000\u3000\u6b64\u65f6\u6b63\u662f\u4e2d\u5348\u65f6\u5206\u3002\u8c37\u6db5\u865a\u542c\u8bf4\u662f\u5348\u65f6\u6210\u4eb2\uff0c\u53ea\u6015\u53bb\u5f97\u8fdf\u4e86\uff0c\u8d76\u4e0d\u4e0a\u62dc\u5802\uff0c\u90a3\u65f6\u8981\u51b2\u8fdb \u5185\u5b85\u53bb\u628a\u65b0\u5a18\u5b50\u6551\u51fa\u6765\uff0c\u53ef\u5c31\u8d39\u4e8b\u591a\u4e86\u3002\u4e8e\u662f\u8fc8\u5f00\u811a\u6b65\uff0c\u8dd1\u8fdb\u9ed1\u77f3\u5e84\uff0c\u679c\u7136\u9690\u9690\u542c\u5f97\u6709\u7410\u5450 \u7684\u4e50\u58f0\uff0c\u8c37\u6db5\u865a\u4fbf\u671d\u7740\u90a3\u4e2a\u65b9\u5411\u98de\u8dd1\uff0c\u4e5f\u987e\u4e0d\u5f97\u8def\u65c1\u7684\u4eba\u60ca\u8bb6\u4e86\u3002 \u3000\u3000\u65b9\u5411\u6ca1\u6709\u8dd1\u9519\uff0c\u4e0d\u6d88\u7247\u523b\uff0c\u8c37\u6db5\u865a\u5df2\u662f\u6765\u5230\u4e86\u90a3\u6210\u5927\u8d22\u4e3b\u7684\u95e8\u524d\u3002 \u3000\u3000\u8c37\u6db5\u865a\u653e\u6162\u811a\u6b65\uff0c\u6697\u81ea\u601d\u91cf\uff1a\u201c\u60f3\u4e2a\u4ec0\u4e48\u6cd5\u513f\u6df7\u8fdb\u53bb\u5462\uff1f\u55ef\uff0c\u82e5\u662f\u65e0\u6cd5\u53ef\u65bd\uff0c\u90a3\u4e5f\u53ea\u597d \u786c\u95ef\u4e86\u3002\u201d \u3000\u3000\u5fc3\u5ff5\u672a\u5df2\uff0c\u5ffd\u542c\u5f97\u5065\u9a6c\u5636\u9e23\u4e4b\u58f0\uff0c\u6709\u56db\u9a91\u9a6c\u540c\u65f6\u6765\u5230\u3002\u4f46\u8fd9\u56db\u4e2a\u5ba2\u4eba\u5374\u4f3c\u4e4e\u5e76\u975e\u4e00\u4f19\uff0c \u8d70\u5728\u524d\u9762\u7684\u662f\u4e00\u5f0f\u6253\u626e\u7684\u4e09\u4e2a\u9ed1\u8863\u6c49\u5b50\uff0c\u4ece\u540e\u9762\u9053\u4e0a\u7684\u662f\u4e00\u4e2a\u767d\u888d\u5c11\u5e74\u3002\u8fd9\u767d\u888d\u5c11\u5e74\u9762\u6709\u5982 \u8c6b\u7389\uff0c\u9a91\u7684\u4e5f\u662f\u4e00\u5339\u767d\u9a6c\uff0c\u8d8a\u53d1\u663e\u5f97\u4e30\u795e\u4fca\u79c0\uff0c\u610f\u6001\u6f47\u6d12\u3002 \u3000\u3000\u8c37\u6db5\u865a\u7684\u76ee\u5149\u767b\u65f6\u7ed9\u8fd9\u5c11\u5e74\u5438\u5f15\u8fc7\u53bb\uff0c\u4ed6\u6ce8\u610f\u4e0d\u662f\u8fd9\u4e2a\u5c11\u5e74\u7684\u9762\u8c8c\uff0c\u800c\u662f\u4ed6\u9a91\u7684\u8fd9\u5339\u767d \u9a6c\u3002\u8c37\u6db5\u865a\u5584\u4e8e\u76f8\u9a6c\uff0c\u4e00\u770b\u5c31\u77e5\u9053\u8fd9\u662f\u4e00\u5339\u5341\u5206\u96be\u5f97\u7684\u540d\u9a79\u3002 \u3000\u3000\u90a3\u4e09\u4e2a\u9ed1\u8863\u6c49\u5b50\u53c8\u60ca\u53c8\u559c\uff0c\u8bf4\u9053\uff1a\u201c\u767d\u516c\u5b50\u4f60\u4e5f\u6765\u4e86\uff1f\u4ee4\u5c0a\u53ef\u597d\uff1f\u201d\u770b\u6765\u8fd9\u4e2a\u59d3\u767d\u7684\u5c11 \u5e74\u4e43\u662f\u5927\u6709\u6765\u5934\u7684\u4eba\u7269\u3002 \u3000\u3000\u90a3\u767d\u516c\u5b50\u4e5f\u62b1\u62f3\u8bf4\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u5728\u8fd9\u91cc\u9047\u4e0a\u4e86\u4f60\u4eec\u795d\u6c0f\u4e09\u96c4\uff0c\u5e78\u4f1a\uff0c\u5e78\u4f1a\uff01\u7236\u65f6\u5e38\u548c\u6211 \u63d0\u8d77\u4f60\u4eec\u7684\u3002\u201d \u3000\u3000\u6210\u5bb6\u7684\u77e5\u5ba2\u8fde\u5fd9\u4e0a\u524d\u8fce\u63a5\u8d35\u5bbe\uff0c\u5bf9\u8fd9\u59d3\u767d\u7684\u5c11\u5e74\u5c24\u5176\u606d\u656c\u3002\u8fd9\u5c11\u5e74\u8df3\u4e0b\u9a6c\u6765\uff0c\u8bf4\u9053\uff1a \u201c\u591a\u70e6\u4f60\u4eec\u7ed9\u6211\u7167\u6599\u8fd9\u5339\u5750\u9a91\uff0c\u89c2\u8fc7\u793c\u540e\uff0c\u6211\u4fbf\u8981\u8d70\u7684\u3002\u201d\u6210\u5bb6\u7684\u77e5\u5ba2\u8bf4\u9053\uff1a\u201c\u96be\u5f97\u767d\u5bb6\u516c \u5b50\u6765\u8fd9\u4e00\u8d9f\uff0c\u8bf7\u8ba9\u655d\u4e3b\u4eba\u7565\u5c3d\u5730\u4e3b\u4e4b\u8c0a\uff0c\u591a\u4f4f\u4e24\u5929\u5427\u3002\u201d \u3000\u3000\u90a3\u59d3\u767d\u7684\u5c11\u5e74\u9053\uff1a\u201c\u6211\u662f\u5949\u4e86\u5bb6\u7236\u4e4b\u547d\uff0c\u6709\u4e8b\u524d\u5f80\u84df\u5dde\u7684\uff0c\u8def\u7ecf\u6b64\u5730\uff0c\u542c\u8bf4\u8d35\u5e84\u7684\u5c11\u5e84 \u4e3b\u5927\u559c\uff0c\u7279\u6765\u9053\u8d3a\uff0c\u6050\u6015\u4e0d\u80fd\u4e45\u7559\u4e86\u3002\u201d\u6210\u5bb6\u7684\u77e5\u5ba2\u9053\uff1a\u201c\u767d\u516c\u5b50\u65e2\u7136\u6709\u4e8b\uff0c\u6211\u4eec\u4e5f\u4e0d\u80fd\u52c9 \u5f3a\uff0c\u8bf7\u516c\u5b50\u653e\u5fc3\uff0c\u516c\u5b50\u7684\u5b9d\u9a91\uff0c\u6211\u4eec\u81ea\u4f1a\u7ec6\u5fc3\u7167\u6599\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u8ddf\u5728\u4ed6\u4eec\u540e\u9762\uff0c\u4fbf\u60f3\u8fdb\u53bb\uff0c\u90a3\u77e5\u5ba2\u9053\uff1a\u201c\u767d\u516c\u5b50\uff0c\u795d\u5927\u54e5\uff0c\u8fd9\u4f4d\u670b\u53cb\u662f\u548c\u4f60\u4eec\u4e00 \u8d77\u7684\u5417\uff1f\u201d\u90a3\u59d3\u767d\u7684\u5c11\u5e74\u770b\u4e86\u8c37\u6db5\u865a\u4e00\u773c\uff0c\u4f3c\u4e4e\u6709\u70b9\u8be7\u5f02\uff0c\u8bf4\u9053\uff1a\u201c\u8bf7\u95ee\u8fd9\u4f4d\u670b\u53cb\u9ad8\u59d3\u5927 \u540d\u3002\u201d\u539f\u6765\u4ed6\u4ece\u8c37\u6db5\u865a\u7cbe\u534e\u5185\u8574\u7684\u76ee\u5149\uff0c\u4e1a\u5df2\u770b\u51fa\u4e86\u8c37\u6db5\u865a\u662f\u4e2a\u5185\u529f\u9ad8\u660e\u4e4b\u58eb\uff0c\u662f\u4ee5\u8bf4\u8bdd\u76f8 \u5f53\u5ba2\u6c14\u3002 \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u5c0f\u4eba\u662f\u4e2a\u8d70\u65b9\u90ce\u4e2d\uff0c\u8d31\u540d\u4e0d\u8db3\u4ee5\u6c61\u6e05\u542c\u3002\u201d \u3000\u3000\u90a3\u4e09\u4e2a\u9ed1\u8863\u6c49\u5b50\u5374\u6ca1\u6709\u8fd9\u4e2a\u773c\u529b\uff0c\u5f88\u4e0d\u5ba2\u6c14\u5730\u8bf4\u9053\uff1a\u201c\u8c01\u77e5\u9053\u4ed6\u662f\u5e72\u4ec0\u4e48\u7684\uff1f\u4e0d\u8ba4 \u8bc6\uff01\u201d \u3000\u3000\u6210\u5bb6\u7684\u4ec6\u5f79\u4e00\u542c\u8c37\u6db5\u865a\u548c\u8fd9\u4e24\u6279\u4eba\u90fd\u6ca1\u5173\u7cfb\uff0c\u4fbf\u5373\u4e0a\u524d\u62e6\u963b\u3002\u90a3\u59d3\u767d\u7684\u5c11\u5e74\u672c\u6765\u60f3\u7ed9\u8c37 \u6db5\u865a\u8bf4\u4e24\u53e5\u597d\u8bdd\u7684\uff0c\u4fbf\u8f6c\u5ff5\u4e00\u60f3\uff1a\u201c\u6211\u53c8\u4e0d\u77e5\u4ed6\u7684\u6765\u5386\uff0c\u4f55\u5fc5\u591a\u7ba1\u95f2\u4e8b\uff1f\u201d\u4e8e\u662f\u4e0d\u53d1\u4e00\u8a00\uff0c \u9ed8\u9ed8\u5730\u4fbf\u8ddf\u77e5\u5ba2\u8fdb\u53bb\u3002 \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u6211\u4e0d\u662f\u544a\u8bc9\u4f60\u4eec\u6211\u662f\u8d70\u65b9\u90ce\u4e2d\u5417\uff1f\u8d35\u5e9c\u529e\u559c\u4e8b\uff0c\u6211\u6765\u5356\u836f\uff0c\u8bf7\u4f60\u4eec\u8ba9\u6211\u8fdb \u53bb\u5427\uff01\u201d\u90a3\u4e9b\u6076\u4ec6\u7eb7\u7eb7\u9a82\u9053\uff1a\u201c\u6df7\u5e10\uff0c\u6df7\u5e10\uff0c\u4f60\u662f\u6709\u610f\u6765\u89e6\u9709\u5934\u7684\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u59d3\u767d\u90a3\u5c11\u5e74\u548c\u795d\u6c0f\u4e09\u96c4\u6b64\u65f6\u5df2\u7ecf\u8e0f\u8fdb\u5185\u9662\uff0c\u548c\u4ed6\u4eec\u76f8\u8bc6\u7684\u4eba\u5f88\u591a\uff0c\u7eb7\u7eb7\u4e0a\u6765\u548c\u4ed6\u4eec\u5bd2 \u6684\u3002 \u3000\u3000\u8c37\u6db5\u865a\u5ffd\u5730\u9690\u7ea6\u542c\u5f97\u6709\u4eba\u8bf4\u51fa\u201c\u62a2\u4eb2\u201d\u5b57\uff0c\u4e0d\u7981\u5fc3\u5934\u4e00\u52a8\uff0c\u8fde\u5fd9\u7ad6\u8d77\u8033\u6735\u6765\u542c\u3002\u8bf4\u8bdd\u7684 \u58f0\u97f3\u5f88\u8f7b\uff0c\u539f\u6765\u662f\u795d\u6c0f\u4e09\u96c4\u4e2d\u7684\u8001\u4e8c\u548c\u4e00\u4e2a\u4eba\u8eb2\u5728\u4e00\u89d2\u5077\u5077\u8bae\u8bba\u3002\u8c37\u6db5\u865a\u6709\u542c\u98ce\u8fa8\u5668\u7684\u672c \u9886\uff0c\u80fd\u591f\u5728\u8bf8\u58f0\u6742\u55a7\u4e4b\u4e2d\uff0c\u201c\u6355\u6349\u201d\u4ed6\u6240\u8981\u542c\u7684\u90a3\u4e2a\u58f0\u97f3\uff0c\u4f46\u7531\u4e8e\u5bbe\u5ba2\u5b9e\u5728\u592a\u5608\u6742\u4e86\uff0c\u542c\u8d77 \u6765\u8fd8\u662f\u5341\u5206\u5403\u529b\u3002 \u3000\u3000\u53ea\u542c\u5f97\u795d\u8001\u4e8c\u8ddf\u7740\u8f7b\u58f0\u7b11\u9053\uff1a\u201c\u8fd9\u53ef\u771f\u662f\u65e0\u72ec\u6709\u5076\uff01\u201d\u6b64\u65f6\u6210\u5bb6\u7684\u8c6a\u5974\u6b63\u5728\u62e6\u963b\u8c37\u6db5 \u865a\uff0c\u9a82\u4ed6\u6765\u89e6\u9709\u5934\uff0c\u4e2d\u95f4\u4e00\u53e5\u8bdd\u8c37\u6db5\u865a\u542c\u4e0d\u6e05\u695a\uff0c\u8ddf\u7740\u542c\u5f97\u90a3\u4e2a\u4eba\u8bf4\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u5180\u5317\u9053 \u4e0a\u4e5f\u6709\u8fd9\u6837\u4e00\u6869\u4e8b\u60c5\u3002\u201d\u795d\u8001\u4e8c\u9053\uff1a\u201c\u662f\u5440\uff0c\u6210\u5e84\u4e3b\u4e5f\u5c31\u662f\u9ed1\u77f3\u5e84\u4e3b\uff0c\u6240\u4ee5\u6211\u8bf4\u8fd9\u5c82\u4e0d\u662f\u65e0 \u72ec\u6709\u5076\u5417\uff1f\u201d \u3000\u3000\u90a3\u8c6a\u5974\u89c1\u8c37\u6db5\u865a\u4e0d\u7406\u4e0d\u776c\uff0c\u5927\u6012\u8bf4\u9053\uff1a\u201c\u4f60\u88c5\u50bb\u5417\uff1f\u6eda\u51fa\u53bb\u3002\u201d\u4e0d\u4f46\u52a8\u53e3\uff0c\u800c\u4e14\u52a8\u624b\u6765 \u63a8\u8c37\u6db5\u865a\u4e86\u3002 \u3000\u3000\u4e0d\u63a8\u81ea\u53ef\uff0c\u4e00\u63a8\u4e4b\u4e0b\uff0c\u53ea\u542c\u5f97\u201c\u535c\u901a\u201d\u4e00\u58f0\uff0c\u5012\u4e0b\u6765\u7684\u4e0d\u662f\u8c37\u6db5\u865a\uff0c\u800c\u662f\u90a3\u4e2a\u8c6a\u5974\u53d8\u6210 \u4e86\u6eda\u5730\u846b\u82a6\u3002 \u3000\u3000\u539f\u6765\u8c37\u6db5\u865a\u56e0\u4e3a\u4e8b\u60c5\u7d27\u6025\uff0c\u5df2\u7ecf\u653e\u5f03\u6df7\u8fdb\u53bb\u7684\u6253\u7b97\uff0c\u51b3\u5b9a\u786c\u95ef\u4e86\u3002\u4ed6\u6709\u201c\u6cbe\u8863\u5341\u516b\u8dcc\u201d \u7684\u6b66\u529f\uff0c\u82e5\u975e\u624b\u4e0b\u7559\u60c5\uff0c\u90a3\u4e2a\u8c6a\u5974\u5403\u4e8f\u8fd8\u8981\u66f4\u5927\u3002 \u3000\u3000\u90a3\u4e2a\u8c6a\u5974\u8eba\u5728\u5730\u4e0a\u7834\u53e3\u5927\u9a82\uff1a\u201c\u5c82\u6709\u6b64\u7406\uff0c\u4f60\u8fd9\u5c0f\u5b50\u6253\u4eba\uff01\u201d\u8c37\u6db5\u865a\u7b11\u9053\uff1a\u201c\u4f60\u522b\u7740 \u614c\uff0c\u4f60\u82e5\u53d7\u4f24\uff0c\u5f85\u4f1a\u513f\u6211\u7ed9\u836f\u533b\u4f60\u3002\u201d\u53cc\u81c2\u4e00\u632f\uff0c\u53c8\u8dcc\u7ffb\u4e86\u51e0\u4e2a\u8c6a\u5974\uff0c\u7acb\u5373\u4fbf\u5f80\u91cc\u95ef\u3002\u672a\u66fe \u5012\u7684\u77e5\u9053\u5389\u5bb3\uff0c\u53ea\u6562\u5927\u547c\u5c0f\u53eb\uff0c\u5374\u6ca1\u4e00\u4e2a\u4eba\u6562\u4e0a\u53bb\u62e6\u4ed6\u3002 \u3000\u3000\u6070\u5de7\u5c31\u5728\u8fd9\u4e2a\u65f6\u5019\uff0c\u97ad\u70ae\u567c\u567c\u556a\u556a\u5730\u54cd\u4e86\u8d77\u6765\uff0c\u793c\u5802\u4e2d\u516b\u97f3\u9f50\u594f\uff0c\u65b0\u4eba\u6b63\u5728\u201c\u4e0a\u5802\u201d \u4e86\u3002 \u3000\u3000\u55a7\u95f9\u7684\u58f0\u97f3\u7ed9\u97ad\u70ae\u58f0\u548c\u4e50\u58f0\u76d6\u8fc7\uff0c\u91cc\u9762\u7684\u4eba\u4e5f\u4e0d\u77e5\u9053\u5916\u9762\u5728\u95f9\u4e9b\u4ec0\u4e48\uff0c\u53ea\u9053\u662f\u65e0\u5173\u8f7b\u91cd \u7684\u5c0f\u4e8b\uff0c\u5927\u5bb6\u5fd9\u4e8e\u89c2\u793c\uff0c\u4e5f\u5c31\u6ca1\u6709\u4eba\u6765\u7ba1\u4e86\u3002 \u3000\u3000\u8c37\u6db5\u865a\u5728\u70df\u96fe\u7684\u906e\u63a9\u4e0b\u94bb\u8fdb\u5165\u5730\uff0c\u5f84\u5165\u793c\u5802\uff0c\u60f3\u627e\u90a3\u795d\u8001\u4e8c\uff0c\u5374\u6ca1\u6709\u627e\u7740\u3002\u53ea\u542c\u5f97\u795d\u8001 \u5927\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u6765\u5f97\u6b63\u662f\u65f6\u5019\uff0c\u6211\u8fd8\u6050\u6015\u8d76\u4e0d\u4e0a\u62dc\u5802\u5462\u3002\u201d \u3000\u3000\u65c1\u8fb9\u6709\u4e2a\u4eba\u5c0f\u58f0\u8bf4\u9053\uff1a\u201c\u672c\u6765\u662f\u5348\u65f6\u884c\u793c\u7684\uff0c\u542c\u8bf4\u65b0\u5a18\u5b50\u4e0d\u613f\u51fa\u6765\uff0c\u6240\u4ee5\u624d\u62d6\u5230\u8fd9\u4e2a\u65f6 \u5019\u3002\u60f3\u5fc5\u662f\u5df2\u7ecf\u8d39\u4e86\u8bb8\u591a\u5507\u820c\u6765\u529d\u5979\u7684\u4e86\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u80af\u51fa\u6765\u62dc\u5802\u7684\uff0c\u53ea\u6015\u591a\u534a\u4e0d\u662f\u4e25\u70f7\u4e86\u3002\u4f46\u65e2\u6765\u5230\uff0c\u603b\u5f97\u67e5\u4e2a\u6c34\u843d\u77f3 \u51fa\u3002\u201d\u5fc3\u5ff5\u672a\u5df2\uff0c\u53ea\u89c1\u65b0\u90ce\u548c\u65b0\u5a18\u5df2\u7ecf\u4e00\u540c\u51fa\u6765\uff0c\u90a3\u65b0\u5a18\u5b50\u662f\u6709\u4e24\u4e2a\u5065\u5987\u6276\u7740\u7684\uff0c\u663e\u7136\u662f\u906d \u53d7\u631f\u6301\u7684\u4e86\u3002 \u3000\u3000\u8d5e\u793c\u7684\u5531\u9053\uff1a\u201c\u8721\u70db\u5149\u5149\uff0c\u65b0\u4eba\u4e0a\u5802\uff0c\u767e\u5e74\u597d\u5408\uff0c\u4e94\u4e16\u5176\u660c\u3002\u65b0\u4eba\u62dc\u5929\u5730\uff0c\u4e00\u62dc\uff0c \u62dc\u2026\u2026\u201d\u201c\u62dc\u201d\u5b57\u521a\u521a\u5531\u51fa\uff0c\u7a81\u7136\u53d8\u4f5c\u4e86\u4e00\u58f0\u5c16\u53eb\u3002\u539f\u6765\u662f\u8c37\u6db5\u865a\u4ece\u4eba\u4e1b\u4e2d\u6251\u51fa\uff0c\u95ea\u7535\u822c\u5730 \u63d2\u8fdb\u4e86\u8fd9\u5bf9\u65b0\u4eba\u4e4b\u95f4\u3002\u8d5e\u793c\u8fd9\u4eba\u662f\u4e2a\u6559\u8499\u9986\u7684\u8001\u5b66\u7a76\uff0c\u9aa4\u7136\u770b\u89c1\u8c37\u6db5\u865a\u8fd9\u6ee1\u9762\u72f0\u72de\u6050\u6016\u7684\u8138 \u5b54\uff0c\u8c37\u6db5\u865a\u5e76\u6ca1\u6253\u4ed6\uff0c\u4ed6\u5df2\u662f\u6655\u8fc7\u53bb\u4e86\u3002 \u3000\u3000\u8c37\u6db5\u865a\u4e00\u624b\u6293\u7740\u65b0\u90ce\uff0c\u4e00\u624b\u63ed\u5f00\u65b0\u5a18\u7684\u7f57\u5e15\uff0c\u5fc3\u5934\u535c\u901a\u901a\u5730\u8df3\uff0c\u4e00\u63ed\u4e4b\u4e0b\uff0c\u4e0d\u7531\u5f97\u5927\u4e3a \u5931\u671b\uff0c\u539f\u6765\u8fd9\u65b0\u5a18\u679c\u7136\u4e0d\u662f\u4e25\u70f7\u3002 \u3000\u3000\u65b0\u5a18\u770b\u89c1\u4e86\u8c37\u6db5\u865a\u4f24\u75d5\u904d\u5e03\u7684\u8138\u5b54\uff0c\u4e5f\u662f\u5413\u5f97\u82b1\u5bb9\u5931\u8272\uff0c\u5c16\u53eb\u8d77\u6765\u3002\u4f46\u5979\u56e0\u4e3a\u8fde\u65e5\u906d\u53d7 \u51cc\u8fb1\uff0c\u65e9\u5df2\u6709\u70b9\u795e\u7ecf\u9ebb\u6728\uff0c\u751a\u81f3\u4e0d\u628a\u751f\u6b7b\u5f53\u4f5c\u4e00\u56de\u4e8b\u4e86\uff0c\u662f\u4ee5\u867d\u7136\u5403\u60ca\uff0c\u5c1a\u4e0d\u81f3\u4e8e\u50cf\u90a3\u8001\u5b66 \u7a76\u7684\u6655\u5012\u3002 \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u4f60\u522b\u7740\u614c\uff0c\u6211\u662f\u6765\u6551\u4f60\u7684\uff0c\u4f60\u5bb6\u4f4f\u4f55\u65b9\uff0c\u6709\u7236\u6bcd\u5417\uff1f\u201d \u3000\u3000\u9ed1\u77f3\u5e84\u7684\u6210\u5e84\u4e3b\u53c8\u60ca\u53c8\u6012\uff0c\u559d\u9053\uff1a\u201c\u4f60\u4eec\u8fd8\u4e0d\u5feb\uff0c\u5feb\u2026\u2026\u201d\u731b\u7136\u60f3\u8d77\u513f\u5b50\u843d\u5728\u522b\u4eba\u624b \u4e2d\uff0c\u6295\u9f20\u987b\u5f53\u5fcc\u5668\uff0c\u8fde\u5fd9\u6539\u53d8\u53e3\u6c14\uff0c\u6c42\u9053\uff1a\u201c\u597d\u6c49\u4e14\u6162\uff0c\u4e14\u6162\u52a8\u624b\uff0c\u4f60\u8981\u4ec0\u4e48\uff0c\u5c3d\u7ba1\u5f00\u53e3\uff0c \u53ef\u522b\u96be\u4e3a\u4e86\u6211\u7684\u513f\u5b50\uff01\u201d \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u6211\u672c\u6765\u8981\u53d6\u4f60\u513f\u5b50\u7684\u6027\u547d\uff0c\u4f60\u82e5\u60f3\u6211\u9976\u4ed6\uff1f\u90a3\u5c31\u5f97\u542c\u6211\u7684\u5429\u5490\uff01\u201d \u3000\u3000\u6210\u5e84\u4e3b\u53e0\u58f0\u8bf4\u9053\uff1a\u201c\u662f\uff0c\u662f\uff0c\u8bf7\u597d\u6c49\u5429\u5490\uff0c\u5c0f\u8001\u513f\u4e00\u5b9a\u4f9d\u4ece\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u51b7\u7b11\u9053\uff1a\u201c\u4e5f\u4e0d\u6015\u4f60\u4e0d\u4f9d\uff01\u201d\u6b63\u5f85\u8bf4\u51fa\u6761\u4ef6\uff0c\u5ffd\u89c9\u80cc\u540e\u5fae\u98ce\u98d2\u7136\u3002\u539f\u6765\u662f\u4e24\u4e2a\u64c5 \u957f\u4e8e\u4f7f\u6697\u5668\u7684\u4eba\uff0c\u5411\u4ed6\u6253\u51fa\u4e00\u679a\u900f\u9aa8\u9489\u548c\u4e00\u652f\u8774\u8776\u9556\uff0c\u4e24\u822c\u6697\u5668\u90fd\u662f\u6253\u4ed6\u80cc\u5fc3\u7684\u5927\u7a74\u7684\u3002 \u3000\u3000\u8c37\u6db5\u865a\u5c31\u4f3c\u80cc\u540e\u957f\u7740\u773c\u775b\uff0c\u5934\u4e5f\u4e0d\u56de\uff0c\u53cd\u624b\u75be\u5f39\uff0c\u53ea\u542c\u5f97\u201c\u94ee\u94ee\u201d\u4e24\u58f0\uff0c\u4e24\u679a\u6697\u5668\u90fd\u98de \u4e86\u56de\u53bb\uff0c\u201c\u7269\u5f52\u539f\u4e3b\u201d\uff0c\u900f\u9aa8\u9489\u63d2\u8fdb\u4e86\u4e00\u4e2a\u4eba\u7684\u8111\u888b\uff0c\u8774\u8776\u9556\u9489\u5728\u53e6\u4e00\u4e2a\u4eba\u7684\u5934\u89d2\uff0c\u8fd9\u4e24\u4e2a \u53d1\u6697\u5668\u6697\u7b97\u8c37\u6db5\u865a\u7684\u4eba\uff0c\u5bb3\u4eba\u4e0d\u6210\uff0c\u53cd\u800c\u5bb3\u4e86\u81ea\u5df1\uff0c\u7ed9\u53cd\u5f39\u56de\u6765\u7684\u6697\u5668\u4f24\u7740\u8981\u5bb3\uff0c\u767b\u65f6\u4e00\u547d \u545c\u547c\u3002 \u3000\u3000\u8c37\u6db5\u865a\u51b7\u7b11\u9053\uff1a\u201c\u6709\u54ea\u4e2a\u4e0d\u8981\u547d\u7684\u4fbf\u8bf7\u4e0a\u6765\uff01\u201d \u3000\u3000\u6210\u5bb6\u8d22\u96c4\u52bf\u5927\uff0c\u79f0\u9738\u4e00\u65b9\uff0c\u548c\u9ed1\u9053\u53ca\u5b98\u5e9c\u4e2d\u4eba\u5747\u6709\u6765\u5f80\uff0c\u89c2\u793c\u5bbe\u5ba2\uff0c\u5341\u4e5d\u61c2\u5f97\u6b66\u529f\u3002\u53ef \u662f\u89c1\u8c37\u6db5\u865a\u5982\u6b64\u5389\u5bb3\uff0c\u5168\u90fd\u7ed9\u4ed6\u5413\u4f4f\u4e86\u3002\u6b66\u529f\u597d\u7684\u8fd8\u5728\u5f3a\u6444\u5fc3\u795e\uff0c\u9759\u89c2\u5176\u53d8\uff0c\u80c6\u5b50\u5c0f\u7684\u5219\u5df2 \u662f\u5728\u4e89\u5148\u6050\u540e\u7684\u5411\u5927\u95e8\u8dd1\u53bb\uff0c\u60f3\u8981\u9003\u547d\u3002 \u3000\u3000\u7eb7\u95f9\u4e4b\u4e2d\uff0c\u8c37\u6db5\u865a\u542c\u5f97\u6709\u4e00\u4e2a\u4eba\u51b7\u7b11\u9053\uff1a\u201c\u631f\u6301\u4eba\u8d28\uff0c\u7b97\u5f97\u4ec0\u4e48\u597d\u6c49\uff01\u201d\u8bf4\u8bdd\u8fd9\u4eba\uff0c\u6b63 \u662f\u90a3\u4e2a\u59d3\u767d\u7684\u5c11\u5e74\u3002 \u3000\u3000\u8c37\u6db5\u865a\u4e00\u58f0\u51b7\u7b11\uff0c\u7a81\u7136\u628a\u65b0\u90ce\u4e00\u638c\u63a8\u5f00\uff0c\u98de\u8eab\u75be\u63a0\u8fc7\u53bb\uff0c\u62a2\u5728\u4f17\u4eba\u4e4b\u524d\uff0c\u5835\u4f4f\u5927\u95e8\u3002 \u3000\u3000\u6709\u4e24\u4e2a\u519b\u5b98\u521a\u521a\u8dd1\u51fa\u793c\u5802\uff0c\u8c37\u6db5\u865a\u628a\u624b\u4e00\u626c\uff0c\u4f7f\u51fa\u4e86\u5a01\u731b\u65e0\u6bd4\u7684\u201c\u5929\u96f7\u529f\u201d\uff0c\u53ea\u542c\u5f97 \u201c\u7830\u3001\u7830\u201d\u4e24\u58f0\uff0c\u90a3\u4e24\u4e2a\u519b\u5b98\u4ece\u77f3\u9636\u4e0a\u4e00\u4e2a\u5012\u683d\u8471\u5c31\u6eda\u4e0b\u53bb\uff0c\u762b\u5728\u5730\u4e0a\uff0c\u53d8\u6210\u4e86\u4e00\u5806\u70c2\u6ce5\u3002 \u539f\u6765\u5df2\u662f\u7ed9\u8c37\u6db5\u865a\u7684\u5288\u7a7a\u638c\u529b\u9707\u6bd9\u4e86\uff01 \u3000\u3000\u8c37\u6db5\u865a\u5835\u4f4f\u95e8\u53e3\uff0c\u56de\u8fc7\u5934\u6765\uff0c\u53cc\u624b\u75be\u6293\uff0c\u628a\u6d8c\u5230\u95e8\u53e3\u7684\u90a3\u4e9b\u4eba\uff0c\u5c31\u50cf\u6293\u5c0f\u9e21\u4f3c\u7684\uff0c\u4e00\u624b \u4e00\u4e2a\uff0c\u4e00\u628a\u6293\u7740\uff0c\u5c31\u5f80\u91cc\u629b\uff0c\u8f6c\u773c\u4e4b\u95f4\uff0c\u5df2\u6709\u516d\u4e03\u4e2a\u4eba\u7ed9\u4ed6\u63b7\u4e86\u56de\u53bb\u3002\u8c37\u6db5\u865a\u559d\u9053\uff1a\u201c\u4e00\u4e2a \u90fd\u4e0d\u8bb8\u8dd1\uff01\u8c01\u8981\u8dd1\u7684\uff0c\u8fd9\u4e24\u4e2a\u519b\u5b98\u5c31\u662f\u4f60\u4eec\u7684\u699c\u6837\u3002\u201d \u3000\u3000\u90a3\u4e9b\u60f3\u8dd1\u7684\u4eba\uff0c\u6b66\u529f\u90fd\u662f\u6bd4\u8f83\u5e73\u5eb8\u7684\uff0c\u89c1\u4e86\u8c37\u6db5\u865a\u5982\u6b64\u58f0\u52bf\uff0c\u5413\u5f97\u9b42\u4e0d\u9644\u4f53\uff0c\u53ea\u597d\u518d\u5f80 \u89d2\u843d\u91cc\u8eb2\u3002 \u3000\u3000\u90a3\u59d3\u767d\u7684\u5c11\u5e74\u9053\uff1a\u201c\u8fde\u8dd1\u90fd\u4e0d\u8bb8\u8dd1\uff0c\u771f\u662f\u5f3a\u6a2a\u5f97\u53ef\u4ee5\uff01\u201d\u795d\u8001\u5927\u6012\u9053\uff1a\u201c\u6211\u8fd8\u6ca1\u6709\u89c1\u8fc7 \u8fd9\u6837\u51f6\u6076\u7684\u4eba\uff0c\u54b1\u4eec\u5927\u4f19\u513f\u9f50\u4e0a\uff0c\u628a\u8fd9\u5c0f\u5b50\u5e72\u4e86\u5427\uff01\u201d \u3000\u3000\u5bbe\u5ba2\u4e4b\u4e2d\u4e0d\u4e4f\u9ed1\u9053\u4e0a\u7684\u6210\u540d\u4eba\u7269\uff0c\u5e73\u7d20\u4e5f\u662f\u81ea\u6043\u6b66\u529f\uff0c\u6a2a\u884c\u9738\u9053\u60ef\u4e86\u7684\uff0c\u4f46\u4ed6\u4eec\u81ea\u95ee\u8c01 \u4e5f\u6bd4\u4e0d\u4e0a\u8c37\u6db5\u865a\uff0c\u89c1\u8c37\u6db5\u865a\u8fd9\u526f\u51f6\u795e\u6076\u715e\u7684\u6a21\u6837\uff0c\u5fc3\u91cc\u5148\u5c31\u614c\u4e86\u3002 \u3000\u3000\u795d\u8001\u5927\u8981\u201c\u5927\u4f19\u513f\u9f50\u4e0a\u201d\uff0c\u8c01\u90fd\u6ca1\u6709\u7b54\u8bdd\uff0c\u8fde\u90a3\u4e2a\u201c\u4f17\u671b\u6240\u5f52\u201d\u6b66\u529f\u6700\u5f3a\u7684\u59d3\u767d\u5c11\u5e74\u4e5f \u9ed8\u4e0d\u4f5c\u58f0\u3002\u4ed6\u4e4b\u9ed8\u4e0d\u4f5c\u58f0\uff0c\u5374\u5e76\u4e0d\u662f\u7eaf\u7cb9\u7531\u4e8e\u5bb3\u6015\u8c37\u6db5\u865a\uff0c\u800c\u662f\u8981\u4fdd\u6301\u81ea\u5df1\u7684\u8eab\u4efd\uff0c\u4f46\u4ed6\u81ea \u6043\u5355\u6253\u72ec\u6597\u4e5f\u6ca1\u6709\u53d6\u80dc\u7684\u628a\u63e1\uff0c\u53ea\u597d\u5f53\u4f5c\u6ca1\u6709\u542c\u89c1\u795d\u8001\u5927\u7684\u8bdd\u4e86\u3002 \u3000\u3000\u8c37\u6db5\u865a\u5927\u8e0f\u6b65\u8d70\u4e86\u51fa\u6765\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u5e76\u975e\u6709\u610f\u96be\u4e3a\u5404\u4f4d\uff0c\u53ea\u662f\u987b\u5f97\u5f85\u8fd9\u4ef6\u4e8b\u4e86\u7ed3\u4e4b \u540e\uff0c\u624d\u80fd\u8ba9\u5404\u4f4d\u51fa\u53bb\u3002\u6709\u54ea\u4e2a\u4e0d\u670d\u6c14\u7684\uff0c\u5c3d\u7ba1\u51b2\u7740\u6211\u6765\uff0c\u5355\u6253\u72ec\u6597\u4e5f\u597d\uff0c\u7fa4\u6bb4\u4e5f\u597d\uff0c\u5728\u4e0b\u4e00 \u603b\u5949\u966a\u3002\u201d \u3000\u3000\u6b64\u65f6\u59d3\u767d\u90a3\u4e2a\u5c11\u5e74\u6b63\u5728\u66ff\u65b0\u90ce\u901a\u89e3\u7a74\u9053\u3002\u53ef\u662f\u8fd9\u65b0\u90ce\u662f\u7ed9\u8c37\u6db5\u865a\u7528\u72ec\u95e8\u91cd\u624b\u6cd5\u95ed\u4e86\u7a74\u9053 \u7684\uff0c\u59d3\u767d\u5c11\u5e74\u867d\u7136\u770b\u51fa\u4ed6\u53d7\u5c01\u95ed\u7684\u7a74\u9053\uff0c\u5374\u662f\u65e0\u6cd5\u89e3\u5f00\u3002 \u3000\u3000\u8c37\u6db5\u865a\u8d70\u4e0a\u524d\u53bb\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u4f60\u8bf4\u6211\u5f3a\u6a2a\uff0c\u96be\u9053\u4ed6\u4eec\u7236\u5b50\u5f3a\u62a2\u6c11\u5973\uff0c\u53cd\u800c\u662f\u5584\u826f\u4e4b \u8f88\uff1f\u563f\uff0c\u563f\uff0c\u5728\u4f60\u76f8\u8c8c\u957f\u5f97\u4e0d\u4fd7\uff0c\u5374\u539f\u6765\u4e5f\u662f\u4e00\u526f\u9ed1\u5fc3\u80a0\uff01\u201d \u3000\u3000\u59d3\u767d\u8fd9\u5c11\u5e74\u8001\u7f9e\u6210\u6012\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u9601\u4e0b\u8981\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u8c37\u6db5\u865a\u5de6\u638c\u5212\u4e86\u4e00\u9053\u5706\u5f27\uff0c\u53f3\u638c\u7a7f\u51fa\uff0c\u5411\u4ed6\u80f8\u524d\u4e00\u6309\uff0c\u559d\u9053\uff1a\u201c\u63a5\u62db\uff01\u201d\u59d3\u767d\u8fd9\u5c11\u5e74\u53cc \u638c\u9f50\u51fa\uff0c\u4f7f\u51fa\u6d51\u8eab\u6c14\u529b\uff0c\u8981\u628a\u8c37\u6db5\u865a\u63a8\u5f00\u3002\u4ed6\u7684\u529f\u529b\u867d\u7136\u4e0d\u5f31\uff0c\u5374\u600e\u6321\u5f97\u4f4f\u8c37\u6db5\u865a\u5929\u96f7\u529f\u7684 \u5a01\u529b\uff0c\u4e00\u63a8\u4e4b\u4e0b\uff0c\u8c37\u6db5\u865a\u7eb9\u98ce\u4e0d\u52a8\uff0c\u59d3\u767d\u8fd9\u5c11\u5e74\u7ed9\u4ed6\u4e00\u6309\u4e00\u6324\uff0c\u5374\u662f\u4e0d\u7531\u81ea\u5df1\u5730\u8e6c\u8e6c\u8e6c\u9000\u51fa \u4e86\u516d\u4e03\u6b65\uff01\u8c37\u6db5\u865a\u89c1\u4ed6\u6ca1\u6709\u8dcc\u5012\uff0c\u5012\u4e5f\u611f\u5230\u6709\u70b9\u610f\u5916\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u82e5\u662f\u518d\u591a\u4e00\u4e2a\u4e0e\u4ed6\u672c\u9886 \u76f8\u82e5\u7684\u4eba\uff0c\u53ea\u6015\u6211\u5c31\u8981\u96be\u4ee5\u5e94\u4ed8\u4e86\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u4e00\u638c\u9707\u9000\u4e86\u59d3\u767d\u7684\u5c11\u5e74\uff0c\u7acb\u5373\u53c8\u628a\u65b0\u90ce\u6293\u7740\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u4f60\u8bf4\u6211\u631f\u6301\u4eba\u8d28\uff0c\u5982 \u4eca\u6211\u628a\u4ed6\u4ea4\u4e86\u7ed9\u4f60\uff0c\u4f60\u53c8\u6709\u4ec0\u4e48\u80fd\u8010\u5c06\u4ed6\u4fdd\u62a4\uff1f\u563f\uff0c\u563f\uff0c\u54ea\u4e00\u4f4d\u81ea\u95ee\u6709\u672c\u9886\u53ef\u4ee5\u4fdd\u62a4\u4ed6\u7684\uff0c \u4e0d\u59a8\u5411\u6211\u8ba8\u53d6\uff0c\u6211\u8fd8\u53ef\u4ee5\u5c06\u4ed6\u4ea4\u7ed9\u4f60\u4eec\u3002\u201d \u3000\u3000\u59d3\u767d\u8fd9\u5c11\u5e74\u786c\u63a5\u4e86\u4ed6\u7684\u4e00\u638c\uff0c\u80f8\u53e3\u5982\u7ed9\u94c1\u9524\u91cd\u51fb\uff0c\u6b64\u65f6\u6b63\u8eb2\u5728\u89d2\u843d\u91cc\u547c\u547c\u5598\u6c14\uff0c\u54ea\u91cc\u8fd8 \u6562\u8bf4\u8bdd\u3002 \u3000\u3000\u5728\u8fd9\u4e00\u73ed\u4eba\u4e2d\uff0c\u59d3\u767d\u7684\u5c11\u5e74\u6b66\u529f\u6700\u5f3a\uff0c\u4f17\u4eba\u89c1\u4ed6\u4e00\u7167\u9762\u5c31\u5403\u4e86\u5927\u4e8f\uff0c\u8fde\u4ed6\u90fd\u4e0d\u6562\u8bf4\u8bdd\uff0c \u4f17\u4eba\u81ea\u7136\u66f4\u662f\u5664\u82e5\u5bd2\u8749\u4e86\u3002 \u3000\u3000\u6210\u5e84\u4e3b\u54ed\u4e27\u7740\u8138\uff0c\u4e0d\u8fed\u6c42\u9976\uff1a\u201c\u597d\u6c49\u3001\u597d\u6c49\uff0c\u8fd9\u4e0d\u5173\u6211\u7684\u4e8b\u3002\u5c0f\u8001\u513f\u51b3\u4e0d\u6562\u5192\u72af\u597d\u6c49\u7684 \u864e\u5a01\uff0c\u8bf7\u597d\u6c49\u522b\u8981\u52a8\u624b\uff0c\u6709\u4ec0\u4e48\u5429\u5490\uff0c\u5c0f\u8001\u513f\u4e00\u5b9a\u5fc5\u4f9d\u4ece\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u8fd9\u4f4d\u59d1\u5a18\uff0c\u4f60\u662f\u4ece\u54ea\u91cc\u62a2\u6765\u7684\uff0c\u6211\u8981\u4f60\u628a\u5979\u9001\u56de\u53bb\u3002\u201d \u3000\u3000\u6210\u5e84\u4e3b\u9053\uff1a\u201c\u662f\uff0c\u662f\u3002\u5979\u7684\u7239\u7239\u5c31\u5728\u8fd9\u91cc\uff0c\u6211\u9a6c\u4e0a\u8bf7\u4ed6\u9886\u56de\u53bb\u3002\u201d \u3000\u3000\u65b0\u5a18\u5b50\u63a0\u9b42\u7a0d\u5b9a\uff0c\u77e5\u9053\u8c37\u6db5\u865a\u662f\u6765\u6551\u5979\u7684\u4e86\uff0c\u8fde\u5fd9\u68c0\u8884\u65bd\u793c\uff0c\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u6069\u516c\u642d\u6551\uff0c \u4f46\u53ea\u6015\u6069\u516c\u4e00\u8d70\uff0c\u5c0f\u5973\u5b50\u53c8\u91cd\u8981\u843d\u864e\u53e3\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u6551\u4eba\u4fbf\u987b\u6551\u5f7b\uff0c\u9001\u4f5b\u9001\u5230\u897f\u5929\u3002\u4f60\u4e0d\u7528\u62c5\u5fe7\uff0c\u6211\u81ea\u6709\u6cd5\u5b50\u5bf9\u4ed8\u8fd9\u4e2a\u8001 \u8d3c\u3002\u201d \u3000\u3000\u4eba\u4e1b\u4e2d\u8d70\u51fa\u4e00\u4e2a\u5934\u6234\u5112\u51a0\u7684\u8001\u4eba\uff0c\u6ee1\u7736\u90fd\u662f\u773c\u6cea\uff0c\u65b0\u5a18\u5b50\u6251\u4e0a\u524d\u53bb\uff0c\u53eb\u9053\uff1a\u201c\u7239\u7239\uff01\u201d \u4e24\u7236\u5973\u62b1\u5728\u4e00\u8d77\uff0c\u5fcd\u4e0d\u4f4f\u653e\u58f0\u5927\u54ed\u3002 \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u4f60\u4eec\u522b\u54ed\u4e86\uff0c\u4f60\u662f\u54ea\u91cc\u4eba\u6c0f\uff0c\u600e\u6837\u7ed9\u4ed6\u62a2\u4e86\u5973\u513f\u7684\uff0c\u90fd\u544a\u8bc9\u6211\uff01\u201d \u3000\u3000\u90a3\u8001\u5112\u751f\u9053\uff1a\u201c\u6211\u662f\u4e2a\u843d\u9b44\u79c0\u624d\uff0c\u9752\u5dde\u4eba\u6c0f\uff0c\u5e26\u4e86\u5973\u513f\u5230\u84df\u5dde\u6295\u4eb2\u7684\uff0c\u4e0d\u6599\u6765\u5230\u6b64\u5730\uff0c \u5374\u7ed9\u4ed6\u4eec\u5f3a\u62a2\u4e86\u53bb\uff0c\u8fd8\u903c\u6211\u5199\u4e86\u5356\u8eab\u5951\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u597d\uff0c\u59d3\u6210\u7684\u8001\u8d3c\u542c\u7740\uff0c\u9a6c\u4e0a\u628a\u5356\u8eab\u5951\u8fd8\u7ed9\u4ed6\u4eec\uff0c\u53e6\u5916\u7f5a\u4f60\u5341\u4e24\u91d1\u5b50\uff0c\u7ed9\u4ed6 \u4eec\u7236\u5973\u906e\u7f9e\u3002\u201d \u3000\u3000\u6210\u5e84\u4e3b\u53e0\u58f0\u5e94\u9053\uff1a\u201c\u662f\uff0c\u662f\uff01\u201d\u8fde\u5fd9\u5429\u5490\u7ba1\u5bb6\u628a\u8fd9\u5973\u5b50\u7684\u5356\u8eab\u5951\u627e\u51fa\u6765\uff0c\u8fde\u540c\u5341\u4e24\u91d1\u5b50 \u5949\u4e0a\u3002 \u3000\u3000\u90a3\u8001\u5112\u751f\u5c48\u670d\u4e8e\u6deb\u5a01\u4e4b\u4e0b\uff0c\u7b7e\u5356\u8eab\u5951\u5356\u4e86\u5973\u513f\uff0c\u81ea\u89c9\u7f9e\u60ed\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u8981\u4ed6\u7684\u91d1\u5b50\uff0c \u53ea\u8981\u56de\u5973\u513f\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u4e0d\u4e49\u4e4b\u8d22\uff0c\u53d6\u4e4b\u4f55\u4f24\uff0c\u4f60\u5c3d\u7ba1\u62ff\u53bb\uff0c\u505a\u4e2a\u5c0f\u4e70\u5356\u4e5f\u597d\u3002\u5728\u91d1\u864f\u4e4b\u4e0b\uff0c\u6211\u53ea \u529d\u4f60\u522b\u53bb\u505a\u4ec0\u4e48\u52b3\u4ec0\u5b50\u7684\u79c0\u624d\u4e3e\u5b50\uff0c\u505a\u5347\u5b98\u53d1\u8d22\u7684\u68a6\u4e86\u3002\u201d \u3000\u3000\u90a3\u8001\u5112\u751f\u9053\uff1a\u201c\u662f\uff0c\u662f\u3002\u591a\u8c22\u601d\u516c\u91d1\u77f3\u826f\u8a00\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u56de\u8fc7\u5934\u6765\uff0c\u6a2a\u76ee\u4e00\u626b\u5168\u573a\uff0c\u7f13\u7f13\u8d70\u5230\u793c\u5802\u6b63\u4e2d\u7684\u4f9b\u684c\u524d\u9762\u3002 \u3000\u3000\u4f9b\u684c\u4e0a\u70b9\u7740\u4e00\u5bf9\u5927\u7ea2\u8721\u70db\uff0c\u8fd8\u6709\u91d1\u732a\u4e4b\u7c7b\u7684\u4f9b\u54c1\uff0c\u8c37\u6db5\u865a\u628a\u4f9b\u54c1\u626b\u843d\uff0c\u8721\u70db\u62d4\u6389\uff0c\u7f13\u7f13 \u4e3e\u8d77\u624b\u638c\uff0c\u8bf4\u9053\uff1a\u201c\u59d3\u6210\u7684\u8001\u8d3c\uff0c\u4f60\u7741\u5927\u773c\u775b\uff0c\u4ed4\u7ec6\u770b\u4e86\uff01\u201d \u3000\u3000\u8c37\u6db5\u865a\u4e00\u638c\u51fb\u4e0b\uff0c\u53d1\u51fa\u95f7\u96f7\u4f3c\u7684\u58f0\u97f3\uff0c\u90a3\u5f20\u4f9b\u684c\u662f\u6a80\u6728\u505a\u7684\uff0c\u7eb9\u4e1d\u4e0d\u52a8\u3002\u8fd9\u4e00\u4e0b\u5012\u662f\u5927 \u51fa\u4f17\u4eba\u610f\u5916\uff0c\u8c37\u6db5\u865a\u521a\u624d\u5927\u95f9\u793c\u5802\uff0c\u4e00\u4e3e\u624b\u5c31\u51fb\u6bd9\u4e24\u4e2a\u519b\u5b98\uff0c\u4e00\u626c\u638c\u5c31\u6253\u9000\u4e86\u59d3\u767d\u7684\u5c11\u5e74\uff0c \u4f17\u4eba\u672c\u4ee5\u4e3a\u4ed6\u6709\u66f4\u5389\u5bb3\u7684\u624b\u6bb5\u8981\u9732\u7ed9\u5927\u5bb6\u770b\u7684\uff0c\u54ea\u77e5\u8fd9\u5f20\u684c\u5b50\u7adf\u662f\u52a8\u4e5f\u4e0d\u52a8\u3002\u6709\u7684\u4eba\u5c31\u4e0d\u514d \u60f3\u9053\uff1a\u201c\u539f\u6765\u4ed6\u7684\u672c\u9886\u4ea6\u4e0d\u8fc7\u5982\u65af\uff0c\u767d\u516c\u5b50\u6253\u4e0d\u8fc7\u4ed6\uff0c\u53ea\u6015\u4e5f\u662f\u6d6a\u5f97\u865a\u540d\u7684\u4e86\u3002\u201d \u3000\u3000\u5fc3\u5ff5\u672a\u5df2\uff0c\u5ffd\u542c\u5f97\u54d7\u5566\u5566\u4e00\u7247\u58f0\u54cd\uff0c\u90a3\u5f20\u4f9b\u684c\u7a81\u7136\u5012\u584c\uff0c\u8f6c\u773c\u4e4b\u95f4\uff0c\u88c2\u6210\u4e86\u65e0\u6570\u5c0f\u5757\uff0c \u6ee1\u5802\u6728\u5c51\u98de\u626c\uff0c\u5730\u4e0a\u5806\u6ee1\u4e00\u5757\u5757\u7684\u6728\u5934\u3002 \u3000\u3000\u539f\u6765\u8c37\u6db5\u865a\u662f\u7528\u201c\u5929\u96f7\u529f\u201d\u7684\u5a01\u529b\uff0c\u90a3\u4e00\u638c\u51fb\u4e0b\uff0c\u529b\u9053\u4ece\u684c\u5b50\u4e2d\u5fc3\u50cf\u6ce2\u6d6a\u822c\u5411\u56db\u9762\u5f00 \u5c55\uff0c\u662f\u4ee5\u5f97\u987b\u8fc7\u4e86\u4e00\u4f1a\uff0c\u684c\u5b50\u624d\u4f1a\u5bf8\u5bf8\u201c\u80a2\u89e3\u201d\u3002 \u3000\u3000\u51fb\u584c\u4e00\u5f20\u575a\u5b9e\u7684\u6a80\u6728\u684c\u5b50\u5df2\u4e0d\u5bb9\u6613\uff0c\u66f4\u96be\u7684\u662f\u8fd8\u80fd\u591f\u628a\u5b83\u88c2\u6210\u65e0\u6570\u5c0f\u5757\uff0c\u8fd9\u624b\u529f\u592b\u4e00 \u663e\uff0c\u767b\u65f6\u628a\u4f17\u4eba\u5413\u5f97\u76ee\u77aa\u53e3\u5446\uff0c\u77eb\u820c\u96be\u4e0b\u3002 \u3000\u3000\u8c37\u6db5\u865a\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u59d3\u6210\u7684\u8001\u8d3c\uff0c\u4f60\u770b\u6e05\u695a\u4e86\u6ca1\u6709\uff1f\u8c05\u4f60\u7684\u72d7\u5934\u518d\u786c\uff0c\u4e5f\u786c\u4e0d\u8fc7\u8fd9\u5f20\u684c \u5b50\uff0c\u4ee5\u540e\u4f60\u82e5\u6562\u96be\u4e3a\u4ed6\u4eec\u7236\u5973\uff0c\u6211\u4e0d\u4f46\u8981\u6253\u788e\u4f60\u7684\u62d8\u5934\uff0c\u8fd8\u8981\u6740\u4f60\u5168\u5bb6\u8001\u5e7c\uff0c\u9e21\u72ac\u4e0d\u7559\uff01\u201d \u3000\u3000\u6210\u5e84\u4e3b\u5413\u5f97\u9762\u65e0\u4eba\u8272\uff0c\u201c\u535c\u901a\u201d\u5730\u5c31\u8dea\u4e86\u4e0b\u6765\uff0c\u53e9\u5934\u5982\u6363\u849c\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u8001\u513f\u4e0d\u6562\uff0c\u5c0f \u8001\u513f\u4e0d\u6562\uff01\u201d \u3000\u3000\u8c37\u6db5\u865a\u51b7\u7b11\u9053\uff1a\u201c\u8c05\u4f60\u4e5f\u4e0d\u6562\u3002\u6253\u5f00\u5927\u95e8\uff0c\u9001\u4ed6\u4eec\u7236\u5973\u51fa\u53bb\u5427\uff01\u201d \u3000\u3000\u90a3\u4e24\u7236\u5973\u8d70\u540e\uff0c\u8c37\u6db5\u865a\u53c8\u9053\uff1a\u201c\u4f60\u4e3a\u5bcc\u4e0d\u4ec1\uff0c\u6b3a\u538b\u767e\u59d3\uff0c\u672c\u5f53\u53d6\u4f60\u6027\u547d\uff0c\u5982\u4eca\u59d1\u4e14\u9976 \u4f60\uff0c\u7f5a\u4f60\u628a\u4f43\u6237\u7684\u4e09\u5e74\u7530\u79df\u6298\u6210\u94f6\u5b50\uff0c\u7edf\u7edf\u4ea4\u56de\u539f\u6765\u7684\u4f43\u6237\u3002\u4f60\u82e5\u9633\u5949\u9634\u8fdd\uff0c\u6211\u8bbf\u67e5\u6e05\u695a\uff0c \u4f60\u5c11\u8fd8\u4e00\u4e24\u94f6\u5b50\uff0c\u6211\u5c31\u5728\u4f60\u7684\u8eab\u4e0a\u5272\u4e00\u5757\u8089\u3002\u9650\u4f60\u4e09\u65e5\u4e4b\u5185\u529e\u59a5\uff0c\u4f60\u542c\u6e05\u695a\u4e86\u6ca1\u6709\uff1f\u201d \u3000\u3000\u6210\u5e84\u4e3b\u78d5\u5934\u9053\uff1a\u201c\u4fa0\u58eb\u5429\u5490\uff0c\u5c0f\u4eba\u9075\u547d\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u53d1\u843d\u4e86\u6210\u5bb6\u7236\u5b50\u4e4b\u540e\uff0c\u8fd9\u624d\u5bf9\u5bbe\u5ba2\u8bf4\u9053\uff1a\u201c\u73b0\u5728\u4f60\u4eec\u53ef\u4ee5\u8d70\u4e86\u3002\u4e0d\u8fc7\uff0c\u6709\u4e00\u4e2a\u4eba \u53ef\u5f97\u7559\u4e0b\u6765\uff01\u201d\u53cc\u773c\u4e00\u77aa\uff0c\u6307\u7740\u89d2\u843d\u7684\u4e00\u4e2a\u4eba\u9053\uff1a\u201c\u795d\u8001\u4e8c\uff0c\u4f60\u7559\u4e0b\u6765\uff0c\u6211\u6709\u8bdd\u8981\u95ee\u4f60\u3002\u201d \u539f\u6765\u8c37\u6db5\u865a\u521a\u624d\u4e0d\u8bb8\u4f17\u4eba\u51fa\u53bb\uff0c\u5c31\u662f\u56e0\u4e3a\u6015\u795d\u8001\u4e8c\u8d81\u4e71\u9003\u8dd1\u7684\u3002 \u3000\u3000\u201c\u795d\u6c0f\u4e09\u96c4\u201d\u4e0d\u77e5\u8c37\u6db5\u865a\u8981\u628a\u795d\u8001\u4e8c\u7559\u4e0b\u6765\u5e72\u4ec0\u4e48\uff0c\u4e09\u5144\u5f1f\u53c8\u662f\u5403\u60ca\uff0c\u53c8\u662f\u6124\u6012\u3002 \u3000\u3000\u90a3\u4e9b\u6765\u559d\u559c\u9152\u7684\u9ed1\u767d\u4e24\u9053\u4eba\u7269\uff0c\u542c\u5f97\u8c37\u6db5\u865a\u8bf4\u53ef\u4ee5\u8ba9\u4ed6\u4eec\u8d70\u4e86\uff0c\u5982\u5949\u7687\u6069\u5927\u8d66\uff0c\u4e89\u5148\u6050 \u540e\u800c\u9003\uff0c\u8c01\u4e5f\u4e0d\u518d\u7406\u4f1a\u795d\u5bb6\u7684\u4e09\u5144\u5f1f\u3002 \u3000\u3000\u59d3\u767d\u90a3\u5c11\u5e74\u9053\uff1a\u201c\u795d\u5927\u54e5\uff0c\u54b1\u4eec\u6709\u798f\u540c\u4eab\uff0c\u6709\u7978\u540c\u5f53\u3002\u201d\u539f\u6765\u4ed6\u5598\u606f\u5df2\u5b9a\uff0c\u5fc3\u91cc\u81ea\u601d\uff1a \u201c\u795d\u6c0f\u4e09\u96c4\u867d\u7136\u4e0d\u662f\u4e00\u6d41\u597d\u624b\uff0c\u4f46\u6709\u4ed6\u4eec\u4e09\u4eba\u76f8\u52a9\uff0c\u6216\u8bb8\u53ef\u4ee5\u548c\u8fd9\u9b3c\u8138\u6076\u6c49\u4e00\u4e89\u80dc\u8d1f\uff0c\u672a\u5fc5 \u5c31\u4e00\u5b9a\u8f93\u7ed9\u4e86\u4ed6\u3002\u201d\u4ed6\u7684\u7236\u4eb2\u4e43\u662f\u6b66\u6797\u4e2d\u6781\u6709\u540d\u671b\u7684\u4eba\u7269\uff0c\u7236\u5b50\u4e8c\u4eba\u90fd\u662f\u53d7\u4eba\u5949\u627f\u60ef\u4e86\u7684\uff0c \u8fd9\u6b21\u7ed9\u8c37\u6db5\u865a\u6253\u4e86\u4e00\u638c\uff0c\u81ea\u662f\u4e0d\u7518\u51cc\u8fb1\u3002 \u3000\u3000\u8c37\u6db5\u865a\u51b7\u7b11\u9053\uff1a\u201c\u6211\u53ea\u60f3\u95ee\u795d\u8001\u4e8c\u4e00\u53e5\u8bdd\uff0c\u4f60\u4eec\u65e2\u7136\u8981\u548c\u6211\u6253\u67b6\uff0c\u6211\u4e5f\u6b63\u597d\u4e50\u5f97\u8d81\u8fd9\u673a \u4f1a\uff0c\u60e9\u6212\u60e9\u6210\u4f60\u4eec\u8fd9\u4e9b\u6b66\u6797\u8d25\u7c7b\u4e86\uff01\u4e0d\u5fc5\u7f57\u55e6\uff0c\u4f60\u4eec\u56db\u4e2a\u4eba\u4e00\u9f50\u4e0a\u5427\uff01\u201d \u3000\u3000\u795d\u8001\u5927\u542c\u8bf4\u4ed6\u53ea\u662f\u60f3\u95ee\u4e00\u53e5\u8bf4\u8bdd\uff0c\u4e0d\u7531\u5f97\u5927\u4e3a\u540e\u6094\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u65e9\u77e5\u5982\u6b64\uff0c\u8ba9\u4ed6\u95ee\u8001 \u4e8c\u597d\u4e86\uff0c\u4f55\u5fc5\u4e0e\u4ed6\u62fc\u547d\uff1f\u201d\u4f46\u6b64\u9645\u8c37\u6db5\u865a\u5df2\u7ecf\u8bf4\u51fa\u4e86\u8981\u60e9\u6212\u4ed6\u4eec\u7684\u8bf4\u8bdd\uff0c\u795d\u5bb6\u4e09\u5144\u5f1f\u4e0b\u4e0d\u4e86 \u53f0\uff0c\u53ea\u597d\u786c\u7740\u5934\u76ae\u5e94\u6218\u3002 \u3000\u3000\u201c\u795d\u6c0f\u4e09\u96c4\u201d\u5e76\u80a9\u800c\u7acb\uff0c\u795d\u8001\u5927\u8bf4\u9053\uff1a\u201c\u4eba\u6709\u9762\uff0c\u6811\u6709\u76ae\uff0c\u9601\u4e0b\u903c\u4eba\u592a\u751a\uff0c\u6211\u4eec\u795d\u6c0f\u4e09 \u96c4\u96be\u9053\u8fd8\u6015\u4f60\u4e0d\u6210\u3002\u4e0d\u8fc7\u54b1\u4eec\u6bd5\u7adf\u6ca1\u6709\u6df1\u4ec7\u5927\u6068\uff0c\u4f3c\u4e4e\u4e5f\u4e0d\u5fc5\u62fc\u4e2a\u4f60\u6b7b\u6211\u6d3b\u3002\u6211\u4eec\u82e5\u662f\u8f93 \u4e86\uff0c\u7531\u4f60\u95ee\u8bdd\u5c31\u662f\uff0c\u9601\u4e0b\u82e5\u662f\u8f93\u4e86\uff0c\u6211\u4e5f\u4e0d\u60f3\u4e0e\u4f60\u4e3a\u96be\uff0c\u53ea\u662f\u6211\u4eec\u795d\u6c0f\u4e09\u96c4\u7684\u4e8b\u60c5\uff0c\u53ef\u5f97\u8bf7 \u4f60\u9601\u4e0b\u5c11\u7ba1\u4e86\uff01\u201d\u53e3\u5934\u867d\u7136\u8fd8\u5728\u5f3a\u786c\uff0c\u5fc3\u4e2d\u602f\u610f\u5df2\u662f\u66b4\u9732\u65e0\u9057\u3002 \u3000\u3000\u8c37\u6db5\u865a\u7eb5\u58f0\u5927\u7b11\u9053\uff1a\u201c\u597d\uff0c\u597d\uff0c\u597d\uff01\u5c31\u6309\u7167\u4f60\u5212\u51fa\u7684\u9053\u513f\u6765\u8d70\uff01\u4f60\u4eec\u81ea\u79f0\u4e09\u96c4\uff0c\u5982\u4eca\u6211 \u5012\u8981\u770b\u770b\u4f60\u4eec\u662f\u82f1\u96c4\u8fd8\u662f\u72d7\u718a\u4e86\uff01\u52a8\u624b\u5427\uff01\u201d \u3000\u3000\u8c37\u6db5\u865a\u8bf4\u5230\u201c\u52a8\u624b\u201d\u4e8c\u5b57\uff0c\u8bdd\u72b9\u672a\u4e86\uff0c\u795d\u8001\u5927\u9634\u607b\u607b\u5730\u5e94\u9053\uff1a\u201c\u9075\u547d\uff01\u201d\u4e09\u5144\u5f1f\u4e00\u9f50\u51fa \u624b\uff0c\u4e09\u6761\u6746\u68d2\uff0c\u4e09\u53ea\u91d1\u73af\uff0c\u540c\u65f6\u5411\u8c37\u6db5\u865a\u6253\u6765\u3002\u8fd9\u662f\u4ed6\u4eec\u4e09\u5144\u5f1f\u82e6\u7ec3\u6210\u529f\u3001\u4ed7\u4ee5\u79f0\u96c4\u7684\u72ec\u95e8 \u5175\u5668\u3002\u5de6\u73af\u53f3\u68d2\uff0c\u4e00\u5706\u4e00\u76f4\uff0c\u4e00\u521a\u4e00\u67d4\uff0c\u4e92\u76f8\u914d\u5408\uff0c\u5012\u662f\u6df1\u5f97\u4e0a\u4e58\u6b66\u5b66\u4e4b\u65e8\u3002 \u3000\u3000\u4e09\u6761\u68d2\u6746\u4ece\u6ef4\u6e9c\u6e9c\u8f6c\u52a8\u7684\u91d1\u73af\u4e2d\u4f38\u5c06\u51fa\u6765\uff0c\u4fe8\u5982\u4e09\u6761\u6bd2\u86c7\u51fa\u52a8\uff0c\u62e9\u4eba\u800c\u566c\uff0c\u8c37\u6db5\u865a\u4e5f\u4e0d \u7981\u5fc3\u5934\u5fae\u51db\uff0c\u60f3\u9053\uff1a\u201c\u4ed6\u4eec\u81ea\u8eab\u672c\u9886\u867d\u4e0d\u8fc7\u662f\u7b2c\u4e8c\u6d41\u4eba\u7269\uff0c\u8fd9\u4e24\u4ef6\u5175\u5668\uff0c\u5374\u59d4\u5b9e\u662f\u4e0d\u53ef\u5c0f\u89d1 \u4e86\uff01\u201d \u3000\u3000\u59d3\u767d\u90a3\u5c11\u5e74\u4e5f\u6ca1\u95f2\u7740\uff0c\u4ed6\u6bd4\u795d\u8001\u5927\u8fd8\u8981\u72e1\u733e\u9634\u72e0\uff0c\u5f53\u8c37\u6db5\u865a\u53d1\u8bdd\u4e4b\u65f6\uff0c\u4ed6\u8eab\u5f62\u5fae\u4fa7\uff0c\u65e9 \u4ee5\u84c4\u52bf\u4ee5\u5f85\u3002\u201c\u795d\u6c0f\u4e09\u96c4\u201d\u4e00\u51fa\u624b\uff0c\u4ed6\u7acb\u5373\u4e5f\u662f\u4e00\u58f0\u559d\u9053\uff1a\u201c\u5c0f\u5b50\u4f11\u592a\u7316\u72c2\uff01\u201d\u8eab\u79fb\u6b65\u8f6c\uff0c \u500f\u7684\u5230\u4e86\u8c37\u6db5\u865a\u80cc\u540e\uff0c\u4e00\u62db\u201c\u6e38\u9f99\u63a2\u722a\u201d\uff0c\u4e94\u6307\u5982\u94a9\uff0c\u75be\u6293\u4e0b\u6765\uff0c\u6240\u6293\u7684\u65b9\u4f4d\uff0c\u6b63\u662f\u8c37\u6db5\u865a \u80cc\u5fc3\u7684\u201c\u5927\u690e\u7a74\u201d\u3002\u8fd9\u7a74\u9053\u82e5\u662f\u88ab\u4eba\u62ff\u7740\uff0c\u591a\u597d\u6b66\u529f\uff0c\u4e5f\u662f\u4e0d\u80fd\u52a8\u5f39\uff01 \u3000\u3000\u8c37\u6db5\u865a\u662f\u4f55\u7b49\u4eba\u7269\uff0c\u5c82\u80fd\u7740\u4ed6\u6697\u7b97\uff0c\u638c\u98ce\u68d2\u5f71\u4e4b\u4e2d\uff0c\u53ea\u542c\u5f97\u201c\u7830\u201d\u7684\u4e00\u58f0\uff0c\u8c37\u6db5\u865a\u8eab\u5f62 \u4e00\u62f1\uff0c\u80cc\u7740\u7684\u836f\u7f50\u4ece\u5934\u9876\u7529\u8fc7\uff0c\u201c\u795d\u6c0f\u4e09\u96c4\u201d\u7684\u4e09\u6761\u6746\u68d2\uff0c\u4e00\u9f50\u6253\u5728\u836f\u7bb1\u4e4b\u4e0a\uff0c\u767b\u65f6\u628a\u836f\u7bb1 \u6253\u788e\uff01 \u3000\u3000\u59d3\u767d\u8fd9\u5c11\u5e74\u4e00\u6293\u6293\u53bb\uff0c\u773c\u770b\u5c31\u8981\u6293\u7740\u4e86\u8c37\u6db5\u865a\u80cc\u5fc3\u7684\u201c\u5927\u690e\u7a74\u201d\uff0c\u8c37\u6db5\u865a\u7684\u8eab\u5f62\u7a81\u7136\u8fd9 \u4e48\u4e00\u62f1\uff0c\u53ea\u5dee\u534a\u5bf8\u6ca1\u6709\u6293\u7740\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u8c37\u6db5\u865a\u5df2\u662f\u53cd\u624b\u4e00\u638c\uff0c\u53cd\u62ff\u5bf9\u65b9\u624b\u8155\uff0c\u767b\u65f6 \u628a\u8fd9\u59d3\u767d\u5c11\u5e74\u4e5f\u903c\u5f00\u4e86\u3002 \u3000\u3000\u8c37\u6db5\u865a\u90a3\u67c4\u957f\u5251\u662f\u85cf\u5728\u836f\u7bb1\u4e2d\u7684\uff0c\u836f\u7bb1\u6253\u788e\uff0c\u957f\u5251\u6389\u4e0b\uff0c\u8c37\u6db5\u865a\u811a\u5c16\u4e00\u6311\uff0c\u957f\u5251\u5230\u624b\uff0c \u5251\u672a\u51fa\u9798\uff0c\u5df2\u662f\u4e00\u62db\u201c\u5012\u5377\u73e0\u5e18\u201d\uff0c\u5c06\u4e09\u6761\u6746\u68d2\u683c\u4f4f\u3002 \u3000\u3000\u59d3\u767d\u8fd9\u5c11\u5e74\u62d4\u51fa\u4e86\u4e00\u53e3\u539a\u80cc\u6734\u5200\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u6211\u518d\u9886\u6559\u9601\u4e0b\u7684\u5251\u6cd5\uff01\u201d\u4ed6\u521a\u624d\u4e0e\u8c37\u6db5 \u865a\u62fc\u4e86\u4e00\u638c\uff0c\u51e0\u4e4e\u53d7\u4e86\u5185\u4f24\uff0c\u5bf9\u8c37\u6db5\u865a\u7684\u638c\u529b\u81ea\u7136\u6781\u4e3a\u5fcc\u60ee\uff0c\u4ed6\u7ec3\u6210\u7684\u4e00\u5957\u201c\u6e38\u8eab\u516b\u5366\u5200 \u6cd5\u201d\uff0c\u51fa\u9053\u4ee5\u6765\uff0c\u7f55\u9022\u654c\u624b\uff0c\u6240\u4ee5\u5e0c\u671b\u5728\u5175\u5668\u4e0a\u53ef\u4ee5\u56fe\u4e2a\u4fa5\u5e78\u3002 \u3000\u3000\u8c37\u6db5\u865a\u56e0\u4e3a\u8981\u7559\u4e0b\u795d\u8001\u4e8c\u76d8\u95ee\u53e3\u4f9b\uff0c\u6050\u9632\u81ea\u5df1\u7684\u201c\u5929\u96f7\u529f\u201d\u5a01\u529b\u592a\u5927\uff0c\u628a\u4ed6\u6253\u6b7b\uff0c\u6545\u6b64 \u4e5f\u5b81\u613f\u4f7f\u7528\u5175\u5668\uff0c\u5f53\u4e0b\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u968f\u4f60\u7684\u4fbf\uff01\u201d\u5530\u7684\u62d4\u5251\u51fa\u9798\uff0c\u4e00\u62db\u201c\u516b\u65b9\u98ce \u96e8\u201d\uff0c\u5251\u5149\u970d\u970d\uff0c\u56db\u9762\u5c55\u5f00\uff0c\u795d\u5bb6\u4e09\u5144\u5f1f\u548c\u59d3\u767d\u7684\u5c11\u5e74\u90fd\u89c9\u5f97\u5200\u5149\u8000\u773c\uff0c\u597d\u50cf\u8c37\u6db5\u865a\u8fd9\u67c4\u957f \u5251\u662f\u53ea\u4e3a\u5bf9\u4ed8\u81ea\u5df1\u800c\u523a\u6765\u7684\uff0c\u56db\u4eba\u4e0d\u7ea6\u800c\u540c\u5730\u9000\u4e86\u4e00\u6b65\u3002 \u3000\u3000\u59d3\u767d\u8fd9\u5c11\u5e74\u5012\u5438\u4e00\u53e3\u51c9\u6c14\uff0c\u5fc3\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u8fd9\u53ae\u7684\u5251\u6cd5\u7adf\u7136\u4e5f\u662f\u5982\u6b64\u4e86\u5f97\uff01\u201d\u8fde\u5fd9\u65bd\u5c55 \u95ea\u3001\u5c55\u3001\u817e\u3001\u632a\u7684\u5c0f\u5de7\u8eab\u6cd5\uff0c\u8eab\u4f3c\u6c34\u86c7\u6e38\u8d70\uff0c\u4e58\u6687\u62b5\u9699\uff0c\u6709\u673a\u53ef\u4e58\uff0c\u624d\u5288\u4e00\u5200\uff0c\u907f\u514d\u548c\u8c37\u6db5 \u865a\u786c\u62fc\u3002 \u3000\u3000\u8fd9\u5c11\u5e74\u7684\u201c\u6e38\u8eab\u516b\u5366\u5200\u6cd5\u201d\uff0c\u9020\u8be3\u786e\u4e5f\u4e0d\u51e1\uff0c\u8c37\u6db5\u865a\u7684\u5251\u6cd5\u672c\u6765\u662f\u4ee5\u5947\u8be1\u89c1\u957f\uff0c\u63a5\u8fde\u51e0 \u62db\uff0c\u7adf\u7136\u7ed9\u4ed6\u907f\u8fc7\u3002 \u3000\u3000\u8c37\u6db5\u865a\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6211\u4e14\u6536\u62fe\u4e86\u795d\u5bb6\u4e09\u5144\u5f1f\uff0c\u56de\u5934\u518d\u5bf9\u4ed8\u4ed6\u3002\u201d\u5f53\u4e0b\u4e00\u58f0\u957f\u5578\uff0c\u5251\u62db\u4e00 \u53d8\uff0c\u66b4\u98ce\u9aa4\u96e8\u822c\u7684\u5411\u201c\u795d\u6c0f\u4e09\u96c4\u201d\u653b\u53bb\uff0c\u6839\u672c\u5c31\u4e0d\u53bb\u7406\u4f1a\u59d3\u767d\u8fd9\u4e2a\u5c11\u5e74\u3002\u53ef\u662f\u56e0\u4e3a\u4ed6\u529b\u8d2f\u5251 \u5c16\uff0c\u4e1a\u5df2\u4f7f\u51fa\u4e86\u51e0\u5206\u201c\u5929\u96f7\u529f\u201d\u7684\u5a01\u529b\uff0c\u957f\u5251\u6325\u821e\u4e4b\u9645\uff0c\u9690\u9690\u631f\u7740\u98ce\u96f7\u4e4b\u58f0\uff0c\u59d3\u767d\u8fd9\u5c11\u5e74\u7684 \u6734\u5200\uff0c\u4e00\u5230\u4e86\u4ed6\u7684\u5251\u5149\u7b3c\u7f69\u4e4b\u4e0b\uff0c\u5c31\u7ed9\u8361\u5f00\uff0c\u4f11\u60f3\u65ab\u5f97\u5230\u4ed6\u7684\u8eab\u4e0a\u3002 \u3000\u3000\u8c37\u6db5\u865a\u52a0\u5f3a\u5185\u529b\uff0c\u5251\u62db\u7531\u5feb\u800c\u6162\uff0c\u5251\u5c16\u5c31\u50cf\u5760\u4e86\u91cd\u7269\u4f3c\u7684\uff0c\u4e1c\u4e00\u6307\uff0c\u897f\u4e00\u5212\uff0c\u8bba\u5251\u52bf\u7684 \u51cc\u5389\u4f3c\u4e4e\u5927\u4e0d\u5982\u524d\uff0c\u4f46\u53ea\u8981\u7ed9\u4ed6\u7684\u5251\u5c16\u8f7b\u8f7b\u78b0\u7740\uff0c\u5c31\u4e0d\u7531\u5f97\u864e\u53e3\u53d1\u70ed\uff0c\u5fc3\u5934\u4e00\u9707\uff01 \u3000\u3000\u201c\u795d\u6c0f\u4e09\u96c4\u201d\u7684\u4e09\u73af\u4e09\u68d2\uff0c\u9996\u5c3e\u76f8\u8054\uff0c\u4e92\u76f8\u547c\u5e94\uff0c\u9632\u5fa1\u5f97\u5341\u5206\u4e25\u5bc6\uff0c\u4f46\u5374\u7981\u4e0d\u8d77\u8c37\u6db5\u865a \u5185\u529b\u7684\u51b2\u51fb\u3002\u4e0d\u6d88\u7247\u523b\uff0c\u4e09\u5144\u5f1f\u90fd\u662f\u5927\u6c57\u6dcb\u6f13\uff0c\u6c14\u5598\u5982\u725b\u3002 \u3000\u3000\u8c37\u6db5\u865a\u89c1\u65f6\u673a\u5df2\u5230\uff0c\u731b\u5730\u559d\u9053\uff1a\u201c\u7740\uff01\u201d\u4e00\u5251\u523a\u8fdb\u795d\u8001\u4e8c\u7684\u73af\u4e2d\uff0c\u6b63\u4e2d\u4ed6\u7684\u864e\u53e3\uff0c\u94db\u5577 \u4e00\u58f0\uff0c\u91d1\u73af\u5760\u5730\uff0c\u8001\u5927\u8001\u4e09\u5927\u60ca\u4e4b\u4e0b\uff0c\u53cc\u73af\u53cc\u68d2\u5de6\u53f3\u6251\u6765\uff0c\u4f46\u4e1a\u5df2\u7f3a\u4e86\u4e00\u73af\uff0c\u5982\u4f55\u8fd8\u80fd\u963b\u6321 \u5f97\u4f4f\u8c37\u6db5\u865a\u7684\u653b\u52bf\uff1f\u53ea\u542c\u5f97\u201c\u5494\u5693\u201d\u8fde\u58f0\uff0c\u8c37\u6db5\u865a\u4e00\u5251\u6a2a\u62ab\uff0c\u628a\u4e24\u6839\u68d2\u540c\u65f6\u524a\u65ad\u3002\u8c37\u6db5\u865a\u51fa \u6307\u5982\u98ce\uff0c\u8fc5\u5373\u70b9\u4e86\u4e09\u4eba\u7684\u7a74\u9053\u3002 \u3000\u3000\u59d3\u767d\u90a3\u5c11\u5e74\u6e9c\u6ed1\u4e4b\u6781\uff0c\u4e00\u89c1\u201c\u795d\u6c0f\u4e09\u96c4\u201d\u5f62\u52bf\u4e0d\u5999\uff0c\u7acb\u5373\u51b2\u51fa\u5927\u95e8\uff0c\u51fa\u4e86\u5927\u95e8\uff0c\u8fd9\u624d\u626c \u58f0\u8bf4\u9053\uff1a\u201c\u9601\u4e0b\u5251\u6cd5\u9ad8\u660e\uff0c\u4f69\u670d\uff0c\u4f69\u670d\uff01\u8bf7\u9601\u4e0b\u8d50\u4e2a\u4e07\u513f\u3002\u201d \u3000\u3000\u8fd9\u662f\u6c5f\u6e56\u4e0a\u7684\u672f\u8bed\uff0c\u7559\u4e2a\u201c\u4e07\u513f\u201d\u5373\u662f\u62a5\u4e2a\u59d3\u540d\u7684\u610f\u601d\uff0c\u8981\u5bf9\u65b9\u62a5\u51fa\u59d3\u540d\uff0c\u4e43\u662f\u51c6\u5907\u4ee5 \u540e\u5bfb\u4ec7\u7684\u3002\u5f53\u7136\u8fd9\u4e5f\u53ea\u662f\u5931\u8d25\u4e00\u65b9\u8981\u633d\u56de\u51e0\u5206\u9762\u5b50\u7684\u95e8\u9762\u8bdd\u4e86\u3002 \u3000\u3000\u59d3\u767d\u8fd9\u5c11\u5e74\u53ea\u9053\u8c37\u6db5\u865a\u5fd9\u4e8e\u6536\u62fe\u201c\u795d\u6c0f\u4e09\u96c4\u201d\uff0c\u65e0\u6687\u5206\u8eab\u6765\u8ffd\u81ea\u5df1\uff0c\u662f\u4ee5\u4e50\u5f97\u8bf4\u51e0\u53e5\u6f02 \u4eae\u7684\u95e8\u9762\u8bdd\u3002\u54ea\u77e5\u8c37\u6db5\u865a\u70b9\u7a74\u7684\u624b\u6cd5\u5feb\u5230\u6781\u70b9\uff0c\u70b9\u5012\u4e86\u795d\u6c0f\u4e09\u96c4\uff0c\u59d3\u767d\u8fd9\u5c11\u5e74\u521a\u521a\u8dd1\u51fa\u5927 \u95e8\uff0c\u4ed6\u4e5f\u8ddf\u7740\u8ffd\u51fa\u6765\u4e86\u3002 \u3000\u3000\u8c37\u6db5\u865a\u51b7\u7b11\u9053\uff1a\u201c\u8f6f\u7684\u786c\u7684\uff0c\u6211\u5168\u4e0d\u5403\uff0c\u4f60\u8981\u6211\u62a5\u4e2a\u4e07\u513f\u4ee5\u5f85\u65e5\u540e\u5bfb\u4ec7\u662f\u4e0d\u662f\uff1f\u4e0d\u5fc5\u8fd9 \u6837\u8d39\u4e8b\u4e86\uff0c\u73b0\u5728\u5c31\u6765\u5427\uff01\u201d \u3000\u3000\u59d3\u767d\u90a3\u5c11\u5e74\u672c\u6765\u662f\u5954\u5411\u9a6c\u53a9\u53bb\u627e\u81ea\u5df1\u7684\u5750\u9a91\uff0c\u770b\u89c1\u8c37\u6db5\u865a\u8ffd\u6765\uff0c\u987e\u4e0d\u5f97\u8dd1\u8fdb\u9a6c\u53a9\u53bb\u627e\u81ea \u5df1\u7684\u5750\u9a91\uff0c\u8fde\u5fd9\u62a2\u4e86\u4e00\u5339\u9a6c\uff0c\u5373\u843d\u8352\u800c\u9003\uff0c\u6210\u5bb6\u56e0\u4e3a\u5bbe\u5ba2\u4f17\u591a\uff0c\u9a6c\u53a9\u5bb9\u7eb3\u4e0d\u4e0b\u8bb8\u591a\u5750\u9a91\uff0c\u662f \u4ee5\u5f80\u5730\u4e0a\u7acb\u4e86\u7cfb\u9a6c\u7684\u6728\u6869\uff0c\u6709\u4e9b\u9a6c\u5339\u4e43\u662f\u7cfb\u5728\u5916\u9762\u7684\u7a7a\u5730\u4e0a\u7684\uff01 \u3000\u3000\u8c37\u6db5\u865a\u89c1\u4ed6\u80e1\u4e71\u9a91\u4e0a\u4e00\u5339\u52a3\u9a6c\u800c\u9003\uff0c\u8fd9\u624d\u54c8\u54c8\u4e00\u7b11\uff0c\u6b62\u6b65\u4e0d\u8ffd\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8981\u6211\u7559\u4e0b\u4e07 \u513f\uff0c\u6211\u53ef\u8981\u7559\u4e07\u513f\u7684\u9a6c\u513f\u4e86\u3002\u201d\u539f\u6765\u4ed6\u662f\u770b\u4e2d\u4e86\u59d3\u767d\u5c11\u5e74\u90a3\u5339\u5343\u91cc\u9a6c\uff0c\u6545\u610f\u8ffd\u51fa\u6765\u5413\u5413\u4ed6 \u7684\u3002\u6b64\u65f6\u4ed6\u6025\u4e8e\u56de\u53bb\u76d8\u95ee\u795d\u8001\u4e8c\u7684\u53e3\u4f9b\uff0c\u5f53\u7136\u662f\u4e0d\u60f3\u53bb\u8ffd\u8fd9\u59d3\u767d\u7684\u5c11\u5e74\u4e86\u3002 \u3000\u3000\u8c37\u6db5\u865a\u56de\u5230\u4e86\u5927\u5385\uff0c\u201c\u795d\u6c0f\u4e09\u96c4\u201d\u8fd8\u5728\u54fc\u54fc\u537f\u537f\uff0c\u60f3\u8981\u8fd0\u6c14\u51b2\u5173\uff0c\u81ea\u89e3\u7a74\u9053\uff0c\u53ef\u8c37\u6db5\u865a \u7528\u7684\u662f\u91cd\u624b\u6cd5\u70b9\u7a74\uff0c\u4ed6\u4eec\u529f\u5200\u4e0d\u591f\uff0c\u7a74\u9053\u89e3\u4e0d\u5f00\uff0c\u53cd\u800c\u5f04\u5f97\u75db\u82e6\u96be\u5f53\u3002\u4e00\u8fd0\u529b\u6d51\u8eab\u5982\u9489\u523a\u3002 \u3000\u3000\u795d\u8001\u5927\u52c9\u5f3a\u53ef\u4ee5\u51fa\u58f0\uff0c\u547b\u541f\u8bf4\u9053\uff1a\u201c\u597d\u6c49\uff0c\u54b1\u4eec\u8bf4\u8fc7\u4e2a\u4e0d\u662f\u62fc\u4e2a\u6b7b\u6d3b\u7684\uff0c\u4f60\u624b\u4e0b\u7559\u60c5 \u5427\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u6211\u8bf4\u8bdd\u5f53\u7136\u7b97\u6570\u3002\u4f46\u4f60\u4eec\u8bf4\u8bdd\u4e5f\u5f97\u7b97\u6570\u3002\u6211\u95ee\u4e00\u53e5\uff0c\u4f60\u7b54\u4e00\u53e5\uff0c\u82e5\u6709\u4e0d\u5c3d \u4e0d\u5b9e\u4e4b\u5904\uff0c\u6211\u4e00\u53d1\u73b0\uff0c\u7acb\u5373\u5c31\u53d6\u4f60\u4eec\u6027\u547d\uff01\u201d \u3000\u3000\u795d\u8001\u5927\u9053\uff1a\u201c\u597d\u6c49\u5c3d\u7ba1\u95ee\u8bdd\uff0c\u6211\u4eec\u51b3\u4e0d\u6562\u6709\u534a\u53e5\u865a\u8a00\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u66ff\u4ed6\u4eec\u4e09\u5144\u5f1f\u89e3\u4e86\u7a74\u9053\uff0c\u95ee\u9053\uff1a\u201c\u795d\u8001\u4e8c\uff0c\u4f60\u521a\u624d\u8bf4\u7684\u65e0\u72ec\u6709\u5076\uff0c\u8fd9\u662f\u4ec0\u4e48\u610f \u601d\uff1f\u201d \u3000\u3000\u795d\u8001\u4e8c\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6211\u548c\u5feb\u9a6c\u97e9\u8eb2\u5728\u89d2\u843d\u91cc\u5c0f\u58f0\u8c08\u8bdd\uff0c\u5f53\u65f6\u4ed6\u4e5f\u8fd8\u6ca1\u6709\u8fdb \u6765\uff0c\u600e\u7684\u5374\u7ed9\u4ed6\u542c\u89c1\u4e86\uff1f\u201d\u8fd9\u4ef6\u4e8b\u60c5\u672c\u6765\u662f\u4e0d\u6562\u6cc4\u9732\u7ed9\u5916\u4eba\u77e5\u9053\uff0c\u4f46\u5728\u8c37\u6db5\u865a\u5a01\u80c1\u4e4b\u4e0b\uff0c\u6027 \u547d\u8981\u7d27\uff0c\u4e5f\u4e0d\u80fd\u4e0d\u8bf4\u4e86\u3002 \u3000\u3000\u795d\u8001\u4e8c\u5b9a\u4e86\u5b9a\u795e\uff0c\u5598\u8fc7\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u56e0\u4e3a\u5180\u5317\u9053\u4e0a\u4e5f\u53d1\u751f\u4e86\u540c\u6837\u7684\u4e00\u6869\u4e8b\u60c5\u3002\u201d\u795d\u8001 \u5927\u8865\u5145\u9053\uff1a\u201c\u4e00\u4f4d\u9ed1\u9053\u4e0a\u7684\u670b\u53cb\u5728\u5180\u5317\u9053\u4e0a\u62a2\u4e86\u4e00\u4e2a\u5973\u5b50\u3002 \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u90a3\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u795d\u8001\u4e8c\u9053\uff1a\u201c\u662f\u4e00\u4e2a\u9053\u58eb\uff0c\u4f46\u4e5f\u662f\u5728\u7ebf\u4e0a\u5f00\u6252\u505a\u72ec\u811a\u751f\u610f\u7684\u9ed1\u9053\u670b\u53cb\u3002\u201d\u201c\u7ebf\u4e0a\u5f00\u6252\u201d \u5c31\u662f\u5728\u6c5f\u6e56\u4e0a\u8d70\u52a8\uff0c\u5e76\u65e0\u56fa\u5b9a\u5c71\u5be8\u7684\u5f3a\u76d7\u3002\u201c\u72ec\u811a\u751f\u610f\u201d\u662f\u5e76\u65e0\u4f19\u4f34\uff0c\u72ec\u81ea\u884c\u52a8\u7684\u610f\u601d\uff0c\u4ea6 \u5373\u662f\u8bf4\u90a3\u662f\u4e2a\u72ec\u811a\u5927\u76d7\u3002 \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u662f\u4e0d\u662f\u9ed1\u77f3\u9053\u4eba\uff1f\u201d\u8fd9\u4e2a\u4eba\u662f\u4ed6\u542c\u5f97\u891a\u4e91\u5cf0\u8bf4\u8fc7\u7684\u3002 \u3000\u3000\u795d\u8001\u4e09\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6b63\u662f\u9ed1\u77f3\u9053\u957f\u3002\u9601\u4e0b\u662f\u548c\u4ed6\u76f8\u8bc6\u7684\u5427\uff1f\u201d\u4ed6\u4ee5\u4e3a\u8c37\u6db5\u865a\u548c\u9ed1\u4eba\u9053\u4eba \u6709\u4ea4\u60c5\uff0c\u6697\u6697\u6b22\u559c\u3002 \u3000\u3000\u8c37\u6db5\u865a\u54fc\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u662f\u77e5\u9053\u4ed6\u7684\uff0c\u6211\u6b63\u8981\u53bb\u627e\u4ed6\u5462\uff01\u201d \u3000\u3000\u795d\u8001\u5927\u89c1\u8c37\u6db5\u865a\u8138\u8272\u4e0d\u5bf9\uff0c\u5df2\u77e5\u4e0d\u5999\uff0c\u8fde\u5fd9\u8bf4\u9053\uff1a\u201c\u8fd9\u4e2a\u725b\u9f3b\u5b50\u81ed\u9053\u4e0a\u80e1\u4f5c\u975e\u4e3a\uff0c\u6211\u4eec \u90fd\u662f\u4e0d\u9f7f\u5730\u7684\u6240\u4e3a\u7684\u3002\u8fd9\u6b21\u4ed6\u5f3a\u62a2\u4e86\u4eba\u5bb6\u7684\u9ec4\u82b1\u95fa\u5973\uff0c\u6211\u4e5f\u770b\u4e0d\u8fc7\u773c\u3002\u201d \u3000\u3000\u795d\u8001\u4e09\u5e74\u7eaa\u8f83\u8f7b\uff0c\u6709\u70b9\u50bb\u6c14\uff0c\u5c1a\u672a\u7701\u89c9\uff0c\u5012\u6709\u70b9\u4e3a\u9ed1\u77f3\u9053\u4eba\u4e0d\u5e73\uff0c\u8bf4\u9053\uff1a\u201c\u9ed1\u77f3\u9053\u4eba\u867d \u662f\u5f3a\u6a2a\u9738\u9053\uff0c\u4f46\u5374\u5e76\u975e\u8d2a\u82b1\u597d\u8272\u4e4b\u5f92\u3002\u6211\u542c\u8bf4\u4ed6\u62a2\u7684\u8fd9\u4e2a\u5973\u5b50\uff0c\u5e76\u4e0d\u662f\u7559\u7ed9\u81ea\u5df1\u7528\u7684\uff0c\u4ed6\u662f \u62ff\u53bb\u9001\u4eba\u7684\uff0c\u8981\u8fd9\u5973\u5b50\u7684\u4eba\u4e5f\u5e76\u4e0d\u662f\u8981\u73b7\u6c61\u5979\u7684\u6e05\u767d\u3002\u201d \u3000\u3000\u795d\u8001\u5927\u77aa\u4e86\u5f1f\u5f1f\u4e00\u773c\uff0c\u8bf4\u9053\uff1a\u201c\u4e00\u4e2a\u51fa\u5bb6\u7684\u9053\u4eba\u8981\u5e72\u51fa\u6b64\u7b49\u4e8b\u6765\uff0c\u603b\u662f\u4e0d\u8be5\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u4f60\u4eec\u53ea\u8981\u628a\u77e5\u9053\u7684\u90fd\u8bf4\u51fa\u6765\uff0c\u4e0d\u5fc5\u7ba1\u9ed1\u77f3\u9053\u4eba\u4e3a\u4eba\u600e\u6837\u3002\u795d\u8001\u4e09\uff0c\u4f60\u8bf4\uff0c \u4ed6\u8981\u62ff\u8fd9\u5973\u5b50\u9001\u7ed9\u4f55\u4eba\uff1f\u201d \u3000\u3000\u795d\u8001\u4e09\u9053\uff1a\u201c\u542c\u8bf4\u662f\u8981\u9001\u7ed9\u98de\u9f99\u5c71\u7684\u7aa6\u5be8\u4e3b\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u6697\u6697\u5403\u60ca\uff0c\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u90a3\u4e2a\u5973\u5b50\u662f\u4e0d\u662f\u59d3\u4e25\u7684\uff1f\u201d \u3000\u3000\u795d\u8001\u4e09\u9053\uff1a\u201c\u539f\u6765\u4f60\u4ea6\u5df2\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u6211\u8981\u4f60\u4eec\u8bf4\u5f97\u4ed4\u7ec6\u4e00\u4e9b\uff0c\u548c\u6211\u77e5\u9053\u7684\u5bf9\u8bc1\u5bf9\u8bc1\uff0c\u770b\u770b\u4f60\u4eec\u6709\u5426\u9690\u7792\u3002\u201d \u3000\u3000\u795d\u8001\u4e09\u9053\uff1a\u201c\u6b63\u662f\uff0c\u90a3\u4e2a\u5973\u5b50\u636e\u8bf4\u8fd8\u662f\u6c5f\u5357\u9f0e\u9f0e\u6709\u540d\u7684\u6b66\u6797\u4eba\u7269\uff0c\u53f7\u79f0\u5ddd\u897f\u5927\u4fa0\u4e25\u58f0\u6d9b \u7684\u5973\u513f\u5462\uff01\u201d \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u8e0f\u7834\u94c1\u978b\u65e0\u89c5\u5904\uff0c\u5f97\u6765\u5168\u4e0d\u8d39\u529f\u592b\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b\u2014\u2014\u7b2c\u4e09\u5341\u4e03\u56de\u3000\u60ca\u6089\u9634\u8c0b\u5bfb\u65e7\u4fa3\u3000\u7a76\u660e\u771f\u76f8\u6094\u524d\u975e \u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b \u7b2c\u4e09\u5341\u4e03\u56de\u3000\u60ca\u6089\u9634\u8c0b\u5bfb\u65e7\u4fa3\u3000\u7a76\u660e\u771f\u76f8\u6094\u524d\u975e \u3000\u3000\u8c37\u6db5\u865a\u542c\u5230\u4e00\u534a\u7684\u65f6\u5019\uff0c\u65e9\u5df2\u6599\u5230\u8fd9\u4e2a\u5973\u5b50\u5b9a\u662f\u4e25\u70f7\uff0c\u4f46\u5982\u4eca\u4ece\u795d\u8001\u4e09\u53e3\u4e2d\u5f97\u5230\u4e86\u8bc1 \u5b9e\uff0c\u4ecd\u662f\u4e0d\u7981\u5927\u5403\u4e00\u60ca\uff0c\u5931\u58f0\u8bf4\u9053\uff1a\u201c\u5979\u600e\u4e48\u4f1a\u843d\u5230\u8fd9\u6076\u9053\u7684\u624b\u91cc\uff1f\u201d \u3000\u3000\u795d\u8001\u4e09\u9053\uff1a\u201c\u662f\u5440\uff0c\u521d\u65f6\u9ed1\u77f3\u9053\u4eba\u8fd8\u51e0\u4e4e\u5403\u4e86\u5979\u7684\u4e8f\u5462\u3002\u540e\u6765\u4ed6\u7528\u4e86\u8ff7\u9999\u6697\u5668\uff0c\u8fd9\u624d\u628a \u4e25\u59d1\u5a18\u64d2\u4e86\u7684\u3002\u201d \u3000\u3000\u795d\u8001\u5927\u5584\u4e8e\u89c2\u989c\u5bdf\u8272\uff0c\u770b\u4e86\u8c37\u6db5\u865a\u8fd9\u526f\u53c8\u60ca\u53c8\u6025\u7684\u795e\u60c5\uff0c\u5df2\u77e5\u8c37\u6db5\u865a\u4e0e\u8fd9\u5973\u5b50\u4ea4\u60c5\u975e \u6d45\uff0c\u4e3a\u4e86\u8ba8\u597d\u8c37\u6db5\u865a\uff0c\u8fde\u5fd9\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u662f\u524d\u5929\u5728\u82cf\u5dde\u57ce\u5916\u4e09\u5341\u91cc\u7684\u91ce\u732a\u6797\u78b0\u89c1\u4ed6\u7684\uff0c\u4ed6\u8bf4 \u4ed6\u8981\u628a\u8fd9\u5973\u5b50\u9001\u5230\u98de\u9f99\u5c71\u53bb\u7ed9\u7aa6\u5be8\u4e3b\uff0c\u4ece\u8fd9\u91cc\u5230\u98de\u9f99\u5c71\u548c\u4ece\u91ce\u732a\u6797\u5230\u98de\u9f99\u5c71\u7684\u8def\u7a0b\u662f\u4e00\u6837 \u7684\uff0c\u4e00\u822c\u7684\u5750\u9a91\uff0c\u603b\u5f97\u8dd1\u4e2a\u56db\u4e94\u5929\uff0c\u82e5\u662f\u6709\u5feb\u9a6c\u53bb\u8ffd\u8fd8\u53ef\u4ee5\u5728\u4ed6\u672a\u5230\u98de\u9f99\u5c71\u4e4b\u524d\u8ffd\u8d76\u5f97\u4e0a\u3002 \u8fd9\u725b\u9f3b\u5b50\u867d\u7136\u662f\u6211\u4eec\u7684\u670b\u53cb\uff0c\u4f46\u6211\u60f3\u4e0d\u5230\u4ed6\u4f1a\u5e72\u51fa\u8fd9\u79cd\u4e3a\u6b66\u6797\u670b\u53cb\u6240\u4e0d\u9f7f\u7684\u4e8b\u60c5\uff0c\u53ea\u6068\u6211\u7684 \u672c\u9886\u4e0e\u4ed6\u76f8\u5dee\u751a\u8fdc\uff0c\u5426\u5219\u6211\u4e5f\u8981\u6559\u8bad\u6559\u8bad\u4ed6\u4e86\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u54ac\u7259\u9053\uff1a\u201c\u4ed6\u5c31\u662f\u8d70\u5230\u5929\u8fb9\uff0c\u6211\u4e5f\u8981\u627e\u7740\u4ed6\uff01\u201d\u201d \u3000\u3000\u795d\u8001\u5927\u9053\uff1a\u201c\u5bf9\u5440\uff0c\u4e0d\u754f\u5f3a\u6a2a\uff0c\u6253\u62b1\u4e0d\u5e73\uff0c\u8fd9\u624d\u662f\u5927\u82f1\u96c4\u7684\u672c\u8272\u3002\u4e0d\u8fc7\uff0c\u4ed6\u82e5\u5230\u4e86\u98de\u9f99 \u5c71\uff0c\u4eba\u591a\u52bf\u4f17\uff0c\u5927\u4fa0\u4f60\u867d\u7136\u8fd8\u662f\u53ef\u4ee5\u7a33\u64cd\u80dc\u5238\uff0c\u7a76\u7adf\u8981\u8d39\u8bb8\u591a\u529f\u592b\u4e86\u3002\u4e0d\u5982\u5728\u9014\u4e2d\u622a\u4f4f\u4ed6\u53ef \u4ee5\u7701\u70b9\u6c14\u529b\u3002\u6210\u5e84\u4e3b\uff0c\u4f60\u8d76\u5feb\u6311\u9009\u4e00\u5339\u597d\u9a6c\u9001\u7ed9\u8fd9\u4f4d\u5927\u4fa0\u5427\u3002\u201d\u8bf4\u51fa\u8bdd\u6765\uff0c\u597d\u50cf\u662f\u5b8c\u5168\u4e3a\u8c37 \u6db5\u865a\u7740\u60f3\uff0c\u8fde\u81ea\u5df1\u662f\u6765\u4e3a\u6076\u9738\u62a2\u4eb2\u800c\u4f5c\u8d3a\u5ba2\u7684\u8eab\u4efd\u90fd\u5fd8\u8bb0\u4e86\u3002 \u3000\u3000\u90a3\u50bb\u91cc\u50bb\u6c14\u7684\u795d\u8001\u4e09\u5374\u5ffd\u5730\u53eb\u4e86\u8d77\u6765\uff1a\u201c\u54e5\u54e5\uff0c\u4f60\u600e\u80fd\u8bf4\u51fa\u8fd9\u79cd\u8bdd\uff01\u9ed1\u77f3\u9053\u4eba\u662f\u62a2\u4e86\u90a3 \u4f4d\u4e25\u59d1\u5a18\uff0c\u4f46\u8fd9\u5374\u662f\u98de\u9f99\u5c71\u7684\u7aa6\u5be8\u4e3b\u8bf7\u4ed6\u505a\u7684\uff0c\u7aa6\u5b89\u5e73\u624d\u662f\u4e3b\u4f7f\u7684\u4eba\u3002\u8fd9\u4f4d\u5927\u4fa0\uff0c\u6211\u6c42\u4f60\u4e00 \u4ef6\u4e8b\u60c5\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u867d\u7136\u75db\u6068\u9ed1\u77f3\u9053\u4eba\uff0c\u5374\u4e5f\u6709\u70b9\u559c\u6b22\u795d\u8001\u4e09\u8fd9\u4e2a\u5fc3\u76f4\u53e3\u5feb\u7684\u50bb\u5c0f\u5b50\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d \u7528\u5f00\u53e3\u4e86\uff0c\u6211\u77e5\u9053\u4f60\u662f\u6c42\u6211\u9976\u4e86\u8fd9\u81ed\u9053\u58eb\u662f\u4e0d\u662f\uff1f\u6211\u4e0d\u80fd\u7b54\u5e94\uff01\u201d \u3000\u3000\u795d\u8001\u4e09\u9053\uff1a\u201c\u4f60\u4e0d\u7b54\u5e94\uff0c\u6211\u4e5f\u8981\u8bf4\u3002\u6211\u544a\u8bc9\u4f60\uff0c\u6211\u4eec\u78b0\u89c1\u4ed6\u7684\u65f6\u5019\uff0c\u4ed6\u975e\u5e38\u96be\u4e3a\u60c5\uff0c\u6015 \u6211\u4eec\u4ee5\u4e3a\u4ed6\u662f\u6deb\u8d3c\uff0c\u8fd9\u624d\u544a\u8bc9\u6211\u4eec\u662f\u56e0\u4f55\u62a2\u8fd9\u5973\u5b50\u7684\u3002\u4f46\u7aa6\u5b89\u5e73\u4e3a\u4f55\u8981\u4ed6\u62a2\u8fd9\u5973\u5b50\uff0c\u8fd9\u4ed6\u5c31 \u4e0d\u77e5\u9053\u4e86\u3002\u4f60\u4eec\u9a82\u4ed6\u884c\u4e3a\u4e0d\u5f53\uff0c\u6211\u4e0d\u4e3a\u4ed6\u4e89\u8fa9\uff0c\u4f46\u4ed6\u5374\u7edd\u5bf9\u4e0d\u662f\u8d2a\u82b1\u597d\u8272\u4e4b\u5f92\uff0c\u8fd9\u4ef6\u4e8b\u60c5\uff0c \u4ed6\u4e5f\u53ea\u662f\u5e2e\u51f6\u800c\u5df2\u3002\u8001\u5b9e\u8bf4\uff0c\u8bb2\u8d77\u5728\u9ed1\u9053\u4e2d\u7684\u884c\u4e3a\uff0c\u4ed6\u8fd8\u7b97\u5f97\u662f\u4e2a\u54cd\u5f53\u5f53\u7684\u6c49\u5b50\u5462\u3002\u6700\u5c11\u8981 \u6bd4\u6211\u4eec\u4e09\u5144\u5f1f\u5f3a\u591a\u4e86\u3002\u201d\u8bf4\u5230\u6b64\u5904\uff0c\u77aa\u4e86\u4ed6\u54e5\u54e5\u4e00\u773c\uff0c\u663e\u7136\u662f\u4e0d\u670d\u6c14\u54e5\u54e5\u521a\u624d\u6240\u8bf4\u7684\u8bdd\u3002 \u3000\u3000\u795d\u8001\u5927\u559d\u9053\uff1a\u201c\u8fd9\u6837\u7684\u6076\u9053\u4f60\u8fd8\u66ff\u4ed6\u6c42\u60c5\uff01\u4fa0\u58eb\uff0c\u6211\u7684\u5f1f\u5f1f\u6709\u70b9\u7cca\u6d82\uff0c\u8bf7\u4f60\u539f\u8c05\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u7684\u5f1f\u5f1f\u6216\u8bb8\u7cca\u6d82\uff0c\u5374\u6ca1\u6709\u4f60\u8fd9\u6837\u4ee4\u4eba\u8ba8\u538c\uff01\u597d\uff0c\u795d\u8001 \u4e09\uff0c\u770b\u5728\u4f60\u7684\u4efd\u4e0a\uff0c\u6211\u4e0d\u6740\u4ed6\u4e5f\u5c31\u662f\u4e86\u3002\u4f46\u6211\u53ef\u4e0d\u80fd\u7b54\u5e94\u4f60\u5c31\u9976\u4e86\u4ed6\uff0c\u81f3\u5c11\u4e5f\u5f97\u5e9f\u6389\u4ed6\u7684\u6b66 \u529f\u3002\u201d \u3000\u3000\u6210\u5e84\u4e3b\u770b\u89c1\u8c37\u6db5\u865a\u8981\u8d70\uff0c\u6218\u6218\u5162\u5162\u5730\u8bf4\u9053\uff1a\u201c\u597d\u6c49\uff0c\u6211\u5df2\u53eb\u4eba\u7ed9\u4f60\u8001\u6311\u9009\u5750\u9a91\u3002\u9a6c\u4e0a\u5c31 \u53ef\u7275\u6765\u3002\u201d\u5634\u5df4\u5411\u7ba1\u5bb6\u4e00\u5476\uff0c\u7ba1\u5bb6\u8fde\u5fd9\u6367\u4e0a\u4e00\u76d8\u94f6\u5b50\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u655d\u4e3b\u4eba\u9001\u7ed9\u4f60\u8001\u7684\u4e00\u70b9 \u76d8\u7f20\uff0c\u4e0d\u6210\u656c\u610f\u3002\u201d\u6210\u5e84\u4e3b\u5df4\u4e0d\u5f97\u8c37\u6db5\u865a\u65e9\u8d70\uff0c\u53ea\u6015\u4ed6\u4e00\u4e0d\u5982\u610f\uff0c\u53c8\u8981\u548c\u81ea\u5df1\u4e3a\u96be\u3002 \u3000\u3000\u8c37\u6db5\u865a\u53cc\u773c\u4e00\u7ffb\uff0c\u60f3\u628a\u94f6\u5b50\u6454\u6389\uff0c\u5ffd\u5730\u8f6c\u5ff5\u4e00\u60f3\uff0c\u968f\u624b\u6293\u8d77\u4e86\u5341\u51e0\u952d\u788e\u94f6\uff0c\u8bf4\u9053\uff1a\u201c\u4e5f \u597d\uff0c\u53cd\u6b63\u4f60\u8fd9\u662f\u4e0d\u4e49\u4e4b\u8d22\uff01\u4f46\u4f60\u53ef\u4e0d\u8981\u4ee5\u4e3a\u6709\u4e86\u94b1\u5c31\u4ec0\u4e48\u90fd\u884c\u4e86\u3002\u4f60\u82e5\u662f\u4e0d\u4f9d\u4ece\u6211\u7684\u5429\u5490\uff0c \u6211\u4e00\u5b9a\u56de\u6765\u548c\u4f60\u7b97\u5e10\u3002 \u3000\u3000\u6210\u5e84\u4e3b\u89c1\u4ed6\u6536\u4e86\u94f6\u5b50\uff0c\u677e\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u662f\uff0c\u662f\u3002\u4e09\u5929\u4e4b\u5185\uff0c\u6211\u4e00\u5b9a\u628a\u4f60\u8001\u5429\u5490\u7684\u4e8b \u60c5\u529e\u59a5\u3002\u201d\u4ed6\u4ee5\u4e3a\u8c37\u6db5\u865a\u6536\u4e86\u4ed6\u7684\u94f6\u5b50\uff0c\u5df2\u662f\u591a\u5c11\u7ed9\u4e86\u4ed6\u4e00\u70b9\u60c5\u9762\u3002\u5374\u4e0d\u77e5\u8c37\u6db5\u865a\u4e43\u662f\u53e6\u6709 \u7528\u9014\uff0c\u540e\u6765\u6210\u5bb6\u7236\u5b50\u56e0\u4e3a\u5e76\u6ca1\u6709\u9075\u7167\u8c37\u6db5\u865a\u7684\u5429\u5490\uff0c\u5c06\u4e09\u5e74\u6765\u6240\u6536\u7684\u7530\u79df\u6298\u6210\u94f6\u5b50\u8001\u8001\u5b9e\u5b9e \u5730\u9000\u8fd8\u4f43\u6237\uff0c\u7ed3\u679c\u7ed9\u8c37\u6db5\u865a\u7387\u9886\u7684\u4e00\u652f\u4e49\u519b\u6284\u4e86\u5bb6\uff0c\u8fd9\u662f\u540e\u8bdd\uff0c\u6309\u4e0b\u4e0d\u8868\u3002 \u3000\u3000\u6210\u5bb6\u7684\u5bb6\u4e01\u7275\u6765\u4e86\u5750\u9a91\uff0c\u8c37\u6db5\u865a\u9053\uff1a\u201c\u6211\u81ea\u5df1\u4f1a\u6311\uff0c\u4e0d\u8981\u4f60\u7684\u3002\u201d\u84e6\u5730\u60f3\u8d77\u4e00\u4e8b\uff0c\u56de\u5934 \u95ee\u795d\u8001\u4e09\u9053\uff1a\u201c\u59d3\u767d\u7684\u90a3\u53ae\u662f\u4ec0\u4e48\u4eba\uff1f\u4f4f\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u795d\u8001\u4e09\u9053\uff1a\u201c\u4ed6\u540d\u53eb\u767d\u5e72\u80dc\uff0c\u4ed6\u7239\u7239\u767d\u4e07\u96c4\u4e43\u662f\u4e00\u4f4d\u5df2\u7ecf\u91d1\u76c6\u6d17\u624b\u7684\u7eff\u6797\u5927\u8c6a\uff0c\u548c\u7eff\u6797 \u4e2d\u9f0e\u9f0e\u5927\u540d\u7684\u6df3\u4e8e\u5be8\u4e3b\u4e43\u662f\u7ed3\u62dc\u5144\u5f1f\u3002\u5bb6\u4f4f\u6ca7\u5dde\u767d\u69d0\u5e84\u3002\u6df3\u4e8e\u5be8\u4e3b\u5355\u540d\u4e00\u4e2a\u5468\u5b57\u3002\u4ed6\u662f\u2014 \u2014\u201d\u795d\u8001\u4e09\u56e0\u4e3a\u89c9\u5f97\u8c37\u6db5\u865a\u597d\u50cf\u6bd4\u8f83\u770b\u5f97\u8d77\u4ed6\uff0c\u5fc3\u91cc\u5f88\u662f\u9ad8\u5174\uff0c\u56e0\u6b64\u4e0d\u538c\u5176\u8be6\u5730\u8981\u4e00\u4e94\u4e00\u5341 \u628a\u81ea\u5df1\u6240\u77e5\u9053\u7684\u90fd\u544a\u8bc9\u8c37\u6db5\u865a\u3002 \u3000\u3000\u8c37\u6db5\u865a\u5374\u4e0d\u8010\u70e6\u518d\u542c\u4e0b\u53bb\uff0c\u8bf4\u9053\uff1a\u201c\u591f\u4e86\uff0c\u591f\u4e86\u3002\u6211\u77e5\u9053\u4e86\u4ed6\u7684\u6240\u5728\u4f4f\u6240\uff0c\u81ea\u4f1a\u53bb\u627e\u4ed6 \u7684\u3002\u201d\u5f53\u4e0b\u8dd1\u8fdb\u9a6c\u53a9\uff0c\u53d6\u4e86\u767d\u5e72\u80dc\u7684\u90a3\u5339\u5750\u9a91\uff0c\u8de8\u4e0a\u9a6c\u80cc\uff0c\u7edd\u5c18\u800c\u53bb\u3002 \u3000\u3000\u8c37\u6db5\u865a\u8d70\u4e86\u4e4b\u540e\uff0c\u795d\u6c0f\u5144\u5f1f\u8fd9\u624d\u4e89\u5435\u8d77\u6765\u3002\u795d\u8001\u5927\u9a82\u4ed6\u5f1f\u5f1f\u9053\uff1a\u201c\u4f60\u5012\u4f1a\u5411\u8fd9\u4e11\u6c49\u8ba8 \u597d\u3002\u201d\u795d\u8001\u4e09\u53cd\u5507\u76f8\u8ba5\uff1a\u201c\u4f60\u624d\u662f\u5411\u4ed6\u8ba8\u597d\u3002\u6211\u95ee\u4f60\uff0c\u4f60\u4e0d\u662f\u4e00\u5411\u90fd\u5728\u4eba\u524d\u81ea\u79f0\u9ed1\u77f3\u9053\u4eba\u662f \u4f60\u7684\u597d\u670b\u53cb\u5417\uff0c\u4e3a\u4ec0\u4e48\u4f60\u5374\u5411\u90a3\u4e11\u6c49\u5b50\u5938\u5f20\u4e86\u4ed6\u7684\u7f6a\u8fc7\uff0c\u597d\u50cf\u6068\u4e0d\u5f97\u8fd9\u4e11\u6c49\u5b50\u53bb\u6740\u4e86\u4ed6\u3002\u201d \u3000\u3000\u795d\u8001\u5927\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u61c2\u5f97\u4ec0\u4e48\uff1f\u6211\u8fd9\u662f\u5506\u4f7f\u4e24\u864e\u76f8\u6597\u7684\u5999\u8ba1\u3002\u5bf9\u6211\u4eec\u6709\u5927\u5927\u7684\u597d\u5904\u3002\u201d \u3000\u3000\u795d\u8001\u4e09\u9053\uff1a\u201c\u6b64\u8bdd\u600e\u8bf4\uff1f\u201d\u795d\u8001\u4e8c\u8bf4\u9053\uff1a\u201c\u4e09\u5f1f\uff0c\u5927\u54e5\u7684\u610f\u601d\u4f60\u8fd8\u4e0d\u61c2\uff1f\u201d\u795d\u8001\u4e09\u53cc\u773c \u4e00\u77aa\uff0c\u50bb\u864e\u864e\u5730\u8bf4\u9053\uff1a\u201c\u4e0d\u61c2\uff01\u201d \u3000\u3000\u795d\u8001\u4e8c\u9053\uff1a\u201c\u9ed1\u77f3\u9053\u4eba\u5728\u9ed1\u9053\u4e0a\u62a2\u4e86\u6211\u4eec\u4e0d\u5c11\u7684\u4e70\u5356\uff0c\u8fd9\u4e11\u6c49\u5b50\u627e\u7740\u4e86\u4ed6\uff0c\u4e24\u864e\u76f8\u6597\uff0c \u5fc5\u6709\u4e00\u4f24\u3002\u82e5\u679c\u662f\u4f24\u4e86\u4e11\u6c49\u5b50\uff0c\u7b49\u4e8e\u662f\u9ed1\u77f3\u9053\u4eba\u66ff\u6211\u4eec\u62a5\u4e86\u4ec7\u3002\u4f24\u4e86\u9ed1\u77f3\u9053\u4eba\u5462\uff0c\u5bf9\u6211\u4eec\u4e5f \u4e0d\u662f\u6ca1\u6709\u597d\u5904\uff01\u563f\u563f\uff0c\u54c8\u54c8\uff01\u8fd9\u4f60\u53ef\u61c2\u4e86\u5427\uff1f\u201d \u3000\u3000\u795d\u8001\u4e09\u7741\u5927\u4e86\u773c\u775b\uff0c\u534a\u664c\u8bf4\u9053\uff1a\u201c\u61c2\u4e86\uff0c\u61c2\u4e86\uff01\u4f60\u4eec\u8fd9\u662f\u501f\u5200\u6740\u4eba\u4e4b\u8ba1\uff0c\u4f46\u8fd9\u6837\u7684\u7528\u5fc3 \u4e0d\u662f\u592a\u6076\u6bd2\u4e86\u5417\uff1f\u201d \u3000\u3000\u795d\u8001\u5927\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u91cf\u5c0f\u975e\u541b\u5b50\uff0c\u65e0\u6bd2\u4e0d\u4e08\u592b\uff01\u5e72\u6211\u4eec\u8fd9\u4e00\u884c\u7684\uff0c\u96be\u9053\u8fd8\u60f3\u505a\u6b63\u4eba\u541b \u5b50\uff1f\u201d \u3000\u3000\u539f\u6765\u9ed1\u77f3\u9053\u4eba\u7684\u6cfc\u98ce\u5200\u6cd5\u4e43\u662f\u6b66\u6797\u4e00\u7edd\uff0c\u795d\u8001\u5927\u731c\u51c6\u4e86\u8c37\u6db5\u865a\u4e3a\u4e86\u8981\u6551\u4e25\u70f7\uff0c\u5fc5\u5b9a\u662f\u9a6c \u4e0d\u505c\u8e44\u7684\u65e5\u591c\u8d77\u8def\uff0c\u8c37\u6db5\u865a\u7684\u4f53\u529b\u6d88\u8017\u4e4b\u540e\uff0c\u548c\u9ed1\u77f3\u9053\u4eba\u76f8\u6597\uff0c\u9e7f\u6b7b\u8c01\u624b\uff0c\u5c31\u96be\u4ee5\u9884\u6599\u4e86\u3002 \u795d\u8001\u5927\u76fc\u671b\u7684\u6700\u597d\u662f\u4e24\u8d25\u4ff1\u4f24\uff0c\u6240\u4ee5\u4ed6\u624d\u628a\u9ed1\u77f3\u9053\u4eba\u7684\u884c\u8e2a\u544a\u8bc9\u8c37\u6db5\u865a\u7684\u3002 \u3000\u3000\u8c37\u6db5\u865a\u679c\u7136\u4e0d\u51fa\u4ed6\u7684\u6240\u6599\uff0c\u9a91\u4e86\u767d\u5343\u80dc\u90a3\u5339\u9a8f\u9a6c\uff0c\u9a6c\u4e0d\u505c\u8e44\u7684\u4e00\u8def\u8ffd\u8e2a\u3002\u9664\u4e86\u5403\u996d\u548c\u5411 \u9014\u4eba\u6253\u542c\u4e4b\u5916\uff0c\u4e00\u5929\u4e24\u665a\uff0c\u8fde\u778c\u7761\u90fd\u6ca1\u6253\u8fc7\uff0c\u66f4\u83ab\u8bf4\u8eba\u4e0b\u6765\u6b47\u606f\u4e86\u3002 \u3000\u3000\u8c37\u6db5\u865a\u8eab\u4e0a\u6709\u51e0\u5341\u4e24\u4ece\u6210\u5e84\u4e3b\u90a3\u91cc\u62ff\u6765\u7684\u788e\u94f6\u5b50\uff0c\u662f\u4ee5\u4ed6\u7684\u76f8\u8c8c\u867d\u7136\u51f6\u6076\uff0c\u4f46\u56e0\u51fa\u624b\u8c6a \u9614\uff0c\u4e00\u8def\u4e0a\u5411\u4eba\u6253\u542c\uff0c\u5374\u662f\u4eba\u4eba\u90fd\u4e50\u610f\u628a\u6240\u77e5\u7684\u6d88\u606f\u544a\u8bc9\u4ed6\u3002 \u3000\u3000\u7b2c\u4e09\u5929\u65e9\u4e0a\uff0c\u4ed6\u5df2\u5230\u4e86\u8ddd\u79bb\u98de\u9f99\u5c71\u53ea\u6709\u4e94\u516d\u5341\u91cc\u7684\u4e00\u4e2a\u5730\u65b9\u3002\u8def\u65c1\u8336\u9986\u7684\u4e3b\u4eba\u544a\u8bc9\u4ed6\uff0c \u8bf4\u662f\u770b\u89c1\u4e00\u8f86\u9aa1\u8f66\u7ecf\u8fc7\uff0c\u9a7e\u8f66\u7684\u6b63\u662f\u4e00\u4e2a\u9053\u58eb\u3002\u81f3\u4e8e\u8f66\u4e0a\u6709\u6ca1\u6709\u5973\u5b50\uff0c\u4ed6\u5c31\u4e0d\u77e5\u9053\u4e86\u3002 \u3000\u3000\u8c37\u6db5\u865a\u5f97\u5230\u4e86\u786e\u5b9e\u7684\u6d88\u606f\uff0c\u7cbe\u795e\u9661\u632f\uff0c\u6309\u7167\u8336\u9986\u4e3b\u4eba\u6307\u70b9\u7684\u65b9\u5411\u53bb\u8ffd\u3002\u8fd9\u5929\u65e9\u4e0a\uff0c\u521a\u521a \u4e0b\u8fc7\u4e00\u573a\u96e8\uff0c\u8def\u4e0a\u7684\u8e44\u75d5\u8f68\u8ff9\uff0c\u5341\u5206\u6e05\u6670\uff0c\u7b49\u4e8e\u662f\u7ed9\u4ed6\u5f15\u8def\u3002 \u3000\u3000\u8c37\u6db5\u865a\u8ddf\u7740\u9aa1\u8f66\u8f68\u8ff9\uff0c\u5230\u4e86\u4e00\u5ea7\u6797\u8fb9\uff0c\u4e0d\u89c1\u8f66\u8ff9\uff0c\u9887\u4e3a\u8be7\u5f02\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u600e\u7684\u8fd9\u4e2a\u6076 \u9053\u628a\u9aa1\u8f66\u9a7e\u5230\u6797\u4e2d\u53bb\u4e86\uff0c\u96be\u9053\u4ed6\u7adf\u6562\u5fc3\u6000\u4e0d\u8f68\uff0c\u610f\u56fe\u975e\u793c\u4e48\uff1f\u201d \u3000\u3000\u795d\u8001\u4e09\u867d\u7136\u4e00\u518d\u548c\u4ed6\u8bf4\u8fc7\u9ed1\u77f3\u9053\u4eba\u5e76\u4e0d\u662f\u91c7\u82b1\u8d3c\uff0c\u4f46\u8c37\u6db5\u865a\u5374\u600e\u6562\u5b8c\u5168\u76f8\u4fe1\u4e86\u4ed6\u7684\u8bf4 \u8bdd\uff1f \u3000\u3000\u8c37\u6db5\u865a\u7b56\u9a6c\u5165\u6797\uff0c\u5fc3\u5934\u535c\u535c\u4e71\u8df3\u3002\u5c31\u53ef\u4ee5\u89c1\u7740\u4e25\u70f7\u4e86\uff0c\u8fd9\u6b21\u8be5\u4f1a\u89c1\u5979\u4e86\u5427\uff1f\u54fc\u3001\u54fc\uff01\u90a3 \u6076\u9053\u82e5\u6562\u52a8\u5979\u4e00\u6839\u6beb\u53d1\uff0c\u6211\u975e\u5c06\u4ed6\u788e\u77f3\u4e07\u6bb5\u4e0d\u53ef\u3002 \u3000\u3000\u4e0d\u9519\uff0c\u9aa1\u8f66\u4e2d\u7684\u90a3\u4e2a\u5973\u5b50\u7684\u786e\u662f\u4e25\u70f7\uff0c\u4f46\u4e8b\u60c5\u7684\u53d8\u5316\u5374\u5927\u5927\u51fa\u4e4e\u8c37\u6db5\u865a\u7684\u610f\u6599\u4e4b\u5916\u3002 \u3000\u3000\u6309\u4e0b\u8c37\u6db5\u865a\u6682\u4e14\u4e0d\u8868\uff0c\u4e14\u8bf4\u4e25\u70f7\u7684\u906d\u9047\u3002 \u3000\u3000\u4e25\u70f7\u79bb\u5bb6\u4e4b\u540e\uff0c\u5230\u5904\u6253\u542c\u8c37\u6db5\u865a\u7684\u4e0b\u843d\uff0c\u4e0d\u89c9\u8fc7\u4e86\u4e09\u5e74\uff0c\u8e0f\u904d\u6c5f\u5357\uff0c\u4ecd\u662f\u5f97\u4e0d\u5230\u8c37\u6db5\u865a \u7684\u6d88\u606f\u3002 \u3000\u3000\u4e25\u70f7\u5ffd\u5730\u60f3\u8d77\u8c37\u6db5\u865a\u66fe\u7ecf\u5bf9\u5979\u8bf4\u8fc7\u81ea\u5df1\u7684\u8eab\u4e16\uff0c\u662f\u4ece\u5317\u65b9\u9003\u6765\u7684\u96be\u6c11\u3002\u201c\u6216\u8bb8\u4ed6\u5df2\u7ecf\u56de \u8001\u5bb6\u53bb\u4e86\u3002\u201d\u4e25\u70f7\u5728\u6c5f\u5357\u627e\u4e0d\u7740\u8c37\u6db5\u865a\uff0c\u4e8e\u662f\u4fbf\u6e21\u8fc7\u957f\u6c5f\uff0c\u5230\u5317\u65b9\u6765\u7ee7\u7eed\u627e\u5bfb\u3002 \u3000\u3000\u4e0d\u6599\u8fd9\u4e00\u5929\u5728\u5180\u5317\u9053\u4e0a\u9047\u4e0a\u4e86\u9ed1\u77f3\u9053\u4eba\uff0c\u7ed9\u9ed1\u77f3\u9053\u4eba\u7528\u4f1a\u55b7\u8ff7\u9999\u7684\u6697\u5668\u64d2\u4e86\u3002\u9192\u6765\u4e4b \u65f6\uff0c\u53d1\u73b0\u81ea\u5df1\u5df2\u5728\u4e00\u8f86\u9aa1\u8f66\u4e4b\u4e2d\u3002 \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u6240\u7528\u7684\u90a3\u79cd\u8ff7\u9999\u6709\u9165\u7b4b\u8f6f\u9aa8\u4e4b\u80fd\uff0c\u4e25\u70f7\u9192\u4e86\u8fc7\u6765\uff0c\u624b\u8db3\u867d\u7136\u80fd\u591f\u52a8\u5f39\uff0c\u6c14\u529b\u5374 \u4f7f\u4e0d\u51fa\uff0c\u4e0d\u8fc7\u8eab\u4f53\u5e76\u65e0\u5f02\u72b6\uff0c\u4e25\u70f7\u8fd9\u624d\u7a0d\u5fae\u653e\u5fc3\u3002 \u3000\u3000\u4e25\u70f7\u53c8\u60ca\u53c8\u6c14\uff0c\u9192\u4e86\u8fc7\u6765\uff0c\u5bf9\u9ed1\u77f3\u9053\u4eba\u7834\u53e3\u5927\u9a82\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6211\u5b81\u613f\u7ed9\u4ed6\u4e00\u5200\u6740\u4e86\uff0c \u51b3\u4e0d\u80fd\u53d7\u4ed6\u6298\u8fb1\uff01\u201d \u3000\u3000\u4e0d\u6599\u9ed1\u77f3\u9053\u4eba\u5374\u4e0d\u52a8\u6c14\uff0c\u63ed\u5f00\u4e86\u8f66\u5e18\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u9192\u6765\u4e86\u4e48\uff1f\u201d\u4e25\u70f7\u9a82\u9053\uff1a\u201c\u81ed\u9053\u58eb\uff0c \u4f60\u8981\u600e\u6837\uff1f\u201d \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u7b11\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\uff0c\u8bf7\u4f60\u5403\u4e24\u4e2a\u9992\u5934\u3002\u4f60\u5df2\u7ecf\u7761\u4e86\u4e00\u5929\uff0c\u6ca1\u6709\u5403\u8fc7\u4e1c\u897f\uff0c\u73b0\u5728\u9192 \u6765\uff0c\u60f3\u5fc5\u4ea6\u5df2\u997f\u4e86\u3002\u201d \u3000\u3000\u679c\u7136\u9ed1\u77f3\u9053\u4eba\u629b\u8fdb\u4e24\u4e2a\u9992\u5934\uff0c\u8fde\u624b\u6307\u90fd\u6ca1\u6709\u78b0\u5979\u4e00\u4e0b\u3002 \u3000\u3000\u4e25\u70f7\u6014\u4e86\u4e00\u6014\uff0c\u9a82\u9053\uff1a\u201c\u8d3c\u9053\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u628a\u6211\u6740\u4e86\uff01\u6211\u544a\u8bc9\u4f60\uff0c\u6211\u662f\u5ddd\u897f\u5927\u4fa0\u4e25\u58f0\u6d9b \u7684\u5973\u513f\uff0c\u51b3\u4e0d\u4f1a\u5e73\u767d\u8ba9\u4eba\u6b3a\u8d1f\u7684\u3002\u4f60\u4e0d\u6740\u6211\uff0c\u7ec8\u6709\u4e00\u65e5\uff0c\u6211\u4f1a\u6740\u4f60\u62a5\u4ec7\uff01\u201d \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u9053\uff1a\u201c\u4e5f\u6ca1\u6709\u529e\u6cd5\uff0c\u8c01\u53eb\u6211\u53d7\u4e86\u4eba\u5bb6\u7684\u6069\u60e0\u5462\uff01\u201d \u3000\u3000\u4e25\u70f7\u542c\u4ed6\u8fd9\u4e48\u8bf4\uff0c\u5fcd\u4e0d\u4f4f\u597d\u5947\u5fc3\u95ee\u9053\uff1a\u201c\u600e\u4e48\uff0c\u4f60\u662f\u62ff\u6211\u53bb\u62a5\u7b54\u4eba\u5bb6\u6069\u60e0\u7684\u5417\uff1f\u201d \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u9053\uff1a\u201c\u5c0f\u59d0\u771f\u662f\u806a\u660e\uff0c\u731c\u5f97\u4e00\u70b9\u4e0d\u9519\u3002\u201d \u3000\u3000\u4e25\u70f7\u9053\uff1a\u201c\u90a3\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u9053\uff1a\u201c\u8fd9\u4e2a\u6211\u53ef\u4e0d\u80fd\u544a\u8bc9\u4f60\u3002\u4e0d\u8fc7\u6211\u53ef\u4ee5\u5411\u4f60\u62c5\u4fdd\uff0c\u90a3\u4eba\u51b3\u4e0d\u4f1a\u6c61\u8fb1\u4f60\u7684\uff0c\u4f60 \u53ef\u4ee5\u653e\u5fc3\u3002\u201d \u3000\u3000\u4e25\u70f7\u9a82\u9053\uff1a\u201c\u6211\u4e0d\u4fe1\u4f60\u4eec\u8fd9\u4e9b\u81ed\u9053\u58eb\u6cfc\u76ae\u8d3c\u4f1a\u5b89\u6709\u4ec0\u4e48\u597d\u5fc3\u80a0\uff01\u201d \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4fe1\u4e0d\u4fe1\u7531\u4f60\u3002\u4f46\u6211\u4e5f\u8981\u544a\u8bc9\u4f60\uff0c\u4f60\u82e5\u518d\u9a82\uff0c\u6211\u53ef\u5c31\u8981\u5bf9\u4f60\u4e0d\u5ba2\u6c14 \u4e86\uff0c\u4f60\u9a82\u4e00\u53e5\uff0c\u6211\u5c31\u6253\u4f60\u4e00\u8bb0\u8033\u5149\uff01\u201d \u3000\u3000\u4e25\u70f7\u60f3\u8981\u81ea\u6740\uff0c\u4f46\u5979\u7684\u4f69\u5251\u65e9\u5df2\u88ab\u9ed1\u77f3\u9053\u4eba\u7f34\u53bb\uff0c\u53c8\u4f7f\u4e0d\u51fa\u6c14\u529b\uff0c\u60f3\u8981\u81ea\u6740\u4e5f\u96be\u3002\u9ed1\u77f3 \u9053\u4eba\u5018\u82e5\u6253\u5979\u8033\u5149\uff0c\u5979\u662f\u4e1d\u6beb\u4e5f\u6ca1\u529e\u6cd5\u62b5\u6297\u7684\u3002 \u3000\u3000\u4ea7\u70f7\u65e0\u53ef\u5948\u4f55\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4ed6\u5bf9\u6211\u4e5f\u8fd8\u4e0d\u7b97\u592a\u8fc7\u65e0\u793c\uff0c\u6211\u5c31\u6682\u65f6\u5fcd\u53d7\u5427\u3002\u5f85\u6211\u6062\u590d\u4e86 \u6c14\u529b\uff0c\u518d\u4e0e\u4ed6\u7b97\u5e10\u3002\u201d\u4e8e\u662f\u4f4f\u53e3\u4e0d\u9a82\uff0c\u62ff\u8d77\u4e86\u9ed1\u77f3\u9053\u4eba\u629b\u8fdb\u8f66\u53a2\u7684\u9992\u5934\u3002 \u3000\u3000\u4e25\u70f7\u672c\u6765\u8981\u6454\u6389\u4ed6\u7684\u9992\u5934\uff0c\u4f46\u5b9e\u5728\u662f\u997f\u5f97\u96be\u53d7\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5982\u679c\u9992\u5934\u91cc\u6709\u6bd2\u836f\uff0c\u6211\u6b7b \u4e86\u4e5f\u597d\u3002\u5982\u679c\u6ca1\u6709\u6bd2\u836f\uff0c\u5403\u9971\u4e86\u518d\u8ddf\u4ed6\u62fc\u547d\u3002\u201d \u3000\u3000\u4e25\u70f7\u5403\u4e86\u4e24\u4e2a\u5927\u9992\u5934\uff0c\u6c14\u529b\u5012\u662f\u6062\u590d\u4e86\u4e00\u4e9b\uff0c\u53ef\u662f\u8bd5\u7740\u8fd0\u6c14\uff0c\u80f8\u53e3\u4fbf\u9690\u9690\u4f5c\u75db\u3002\u9ed1\u77f3\u9053 \u4eba\u6240\u7528\u7684\u9165\u9aa8\u6563\uff0c\u836f\u529b\u662f\u80fd\u591f\u7ef4\u6301\u4e03\u5929\u4e4b\u4e45\u7684\u3002\u4e25\u70f7\u81ea\u77e5\u5728\u672a\u80fd\u5f97\u5230\u89e3\u836f\u4e4b\u524d\uff0c\u51b3\u8ba1\u4e0d\u662f\u9ed1 \u77f3\u9053\u4eba\u7684\u5bf9\u624b\uff0c\u4e5f\u53ea\u597d\u6682\u4e14\u5fcd\u8010\u4e86\u3002 \u3000\u3000\u5982\u6b64\u4e00\u6765\uff0c\u53cc\u65b9\u5012\u662f\u53ef\u4ee5\u6682\u65f6\u76f8\u5b89\u65e0\u4e8b\u3002\u9ed1\u77f3\u9053\u4eba\u6bcf\u5929\u628a\u7cae\u98df\u7528\u6c34\u62ff\u5230\u8f66\u53a2\u6765\u7ed9\u4e25\u70f7\uff0c \u5bf9\u5979\u9887\u6709\u793c\u8c8c\u3002\u6bcf\u5929\u4e09\u6b21\u6b47\u606f\u548c\u665a\u4e0a\u7761\u89c9\u4e4b\u65f6\uff0c\u4ed6\u4e5f\u603b\u662f\u8fdc\u8fdc\u7684\u79bb\u5f00\u4e25\u70f7\uff0c\u8ba9\u4e25\u70f7\u53ef\u4ee5\u6709\u4e00 \u4e9b\u5973\u513f\u5bb6\u4e0d\u4fbf\u4e3a\u5916\u4eba\u6240\u89c1\u7684\u4e8b\u60c5\u3002 \u3000\u3000\u8fd9\u4e00\u5929\u5230\u4e86\u98de\u9f99\u5c71\u4e1c\u9762\u7ea6\u4e00\u767e\u91cc\u4e4b\u5904\uff0c\u5df2\u7ecf\u53ef\u4ee5\u9690\u9690\u770b\u89c1\u9ad8\u8038\u5165\u4e91\u7684\u98de\u9f99\u5c71\u4e86\u3002\u9ed1\u77f3\u9053 \u4eba\u5401\u4e86\u53e3\u6c14\uff0c\u81ea\u8a00\u81ea\u8bed\u9053\uff1a\u201c\u6211\u8fd9\u4e00\u751f\u4ece\u6765\u6ca1\u6709\u505a\u8fc7\u670d\u4f8d\u599e\u513f\u7684\u4e8b\uff0c\u597d\uff0c\u8fd8\u6709\u4e00\u5929\uff0c\u6211\u5c31\u53ef \u4ee5\u4e0d\u5e72\u8fd9\u82e6\u5dee\u4e8b\u4e86\u3002\u201d \u3000\u3000\u4e25\u70f7\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u4f60\u662f\u8981\u628a\u6211\u9001\u5230\u98de\u9f99\u5c71\u5417\uff1f\u201d \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u73b0\u5728\u4e0d\u59a8\u544a\u8bc9\u4f60\u4e86\u3002\u4e0d\u662f\u6211\u8981\u6349\u4f60\uff0c\u662f\u98de\u9f99\u5c71\u7684\u7aa6\u5be8\u4e3b\uff0c\u8981\u6211\u5c06 \u4f60\u2018\u8bf7\u2019\u5230\u4ed6\u90a3\u513f\u53bb\u7684\uff01\u201d \u3000\u3000\u4e25\u70f7\u8be7\u9053\uff1a\u201c\u98de\u9f99\u5c71\u7684\u7aa6\u5be8\u4e3b\u662f\u4ec0\u4e48\u4eba\uff0c\u6211\u53c8\u4e0d\u8ba4\u8bc6\u4ed6\uff01\u201d \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u4ed6\u8981\u2018\u8bf7\u2019\u4f60\uff0c\u6211\u4e5f\u5e76\u4e0d\u77e5\u9053\u3002\u4f46\u636e\u6211\u6240\u77e5\uff0c\u7aa6\u5be8\u4e3b\u4e5f\u662f\u7eff\u6797\u4e2d \u4e00\u4f4d\u54cd\u5f53\u5f53\u7684\u6c49\u5b50\uff0c\u6211\u60f3\uff0c\u4ed6\u4e0d\u4f1a\u5bf9\u4f60\u65e0\u793c\u7684\u3002\u201d \u3000\u3000\u7ecf\u8fc7\u8fd9\u51e0\u5929\u7684\u76f8\u5904\uff0c\u4e25\u70f7\u867d\u7136\u4ecd\u662f\u75db\u6068\u9ed1\u77f3\u9053\u4eba\uff0c\u4f46\u5bf9\u4ed6\u7684\u80fd\u4ee5\u793c\u76f8\u5f85\uff0c\u5374\u4e5f\u6709\u4e00\u4e1d\u597d \u611f\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u843d\u5728\u4ed6\u7684\u624b\u91cc\u8fd8\u597d\u4e00\u4e9b\uff0c\u843d\u5728\u90a3\u4e2a\u4ec0\u4e48\u7aa6\u5be8\u4e3b\u7684\u624b\u91cc\uff0c\u90a3\u5c31\u4e0d\u77e5\u5982\u4f55 \u4e86\u3002\u201d\u5c3d\u7ba1\u5979\u5bf9\u9ed1\u77f3\u9053\u4eba\u6709\u4e00\u4e1d\u597d\u611f\uff0c\u603b\u8fd8\u662f\u4e0d\u80fd\u76f8\u4fe1\u4ed6\u7684\u8bf4\u8bdd\u3002 \u3000\u3000\u4e25\u70f7\u672c\u6765\u662f\u4e2a\u4e2a\u6027\u5341\u5206\u5014\u5f3a\u7684\u5973\u5b50\uff0c\u5c31\u662f\u5728\u7236\u6bcd\u7684\u538b\u529b\u4e4b\u4e0b\uff0c\u5979\u4e5f\u662f\u4e0d\u80af\u4f4e\u5934\u7684\uff0c\u60f3\u4e0d \u5230\u5982\u4eca\u7adf\u7136\u8981\u53d7\u4eba\u6446\u5e03\uff0c\u7981\u4e0d\u4f4f\u5fc3\u4e2d\u51c4\u82e6\uff0c\u60f3\u8d77\u8c37\u6db5\u865a\u6765\u3002 \u3000\u3000\u201c\u90a3\u6b21\u6211\u548c\u7239\u7239\u78b0\u4e0a\u4e86\u6ec7\u5357\u4e03\u864e\uff0c\u5e78\u4e8f\u5f97\u8c37\u5927\u54e5\u8d76\u6765\u76f8\u6551\uff0c\u5509\uff0c\u5982\u4eca\u5374\u4e0d\u77e5\u4ed6\u5728\u4f55\u65b9 \u4e86\uff1f\u8c37\u5927\u54e5\uff0c\u8c37\u5927\u54e5\uff0c\u4f60\u53ef\u77e5\u9053\u6211\u73b0\u5728\u6b63\u5728\u53d7\u4eba\u6b3a\u4fae\u5417\uff1f\u201d \u3000\u3000\u5fc3\u5ff5\u672a\u5df2\uff0c\u5ffd\u542c\u5f97\u8e44\u58f0\u5f97\u5f97\uff0c\u6709\u4e00\u9a91\u9a6c\u8fce\u9762\u800c\u6765\u3002\u4e25\u70f7\u5fc3\u5934\u201c\u535c\u901a\u201d\u4e00\u8df3\uff1a\u201c\u96be\u9053\u5f53\u771f \u662f\u5929\u4ece\u4eba\u613f\uff0c\u8c37\u5927\u54e5\u6765\u4e86\uff1f\u201d \u3000\u3000\u8e51\u58f0\u5620\u7136\u800c\u6b62\uff0c\u90a3\u4eba\u4f3c\u4e4e\u662f\u53c8\u60ca\u53c8\u559c\u5730\u53eb\u9053\uff1a\u201c\u9ed1\u77f3\u9053\u957f\uff0c\u60f3\u4e0d\u5230\u4f60\u5df2\u7ecf\u6765\u4e86\uff01\u6211\u6b63\u60f3 \u53bb\u627e\u4f60\u5462\uff01\u201d \u3000\u3000\u5e76\u4e0d\u662f\u8c37\u6db5\u865a\u7684\u58f0\u97f3\u3002 \u3000\u3000\u4e25\u7095\u5fc3\u53c8\u4e00\u6c89\uff0c\u5979\u7684\u5e7b\u60f3\u7834\u706d\u4e86\u3002 \u3000\u3000\u4e25\u70f7\u6458\u5f00\u8f66\u5e18\u89d2\uff0c\u53ea\u89c1\u6765\u7684\u662f\u4e2a\u7626\u8138\u5b54\u5c0f\u773c\u775b\u7684\u6c49\u5b50\uff0c\u4ee4\u4eba\u4e00\u89c1\uff0c\u5c31\u4e0d\u7531\u5f97\u5fc3\u91cc\u751f\u538c\u3002 \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u8d70\u4e0a\u4e0a\u770b\uff0c\u8bf4\u9053\uff1a\u201c\u55ef\uff0c\u4f60\u4e0d\u662f\u7aa6\u65fa\u4e48\uff1f\u201d\u539f\u6765\u7aa6\u65fa\u4e43\u662f\u98de\u9f99\u5c71\u5be8\u4e3b\u7aa6\u5b89\u5e73\u7684 \u8fdc\u623f\u4f84\u513f\uff0c\u4e5f\u662f\u4ed6\u6240\u5ba0\u4fe1\u7684\u4e00\u4e2a\u5fc3\u8179\u5934\u76ee\u3002 \u3000\u3000\u7aa6\u65fa\u542c\u5f97\u9ed1\u77f3\u9053\u4eba\u8bf4\u5f97\u51fa\u4ed6\u7684\u540d\u5b57\uff0c\u5927\u4e3a\u6b22\u559c\uff0c\u8bf4\u9053\uff1a\u201c\u96be\u4e3a\u9053\u957f\u8fd8\u8bb0\u5f97\u6211\uff0c\u6211\u6b63\u662f\u7aa6 \u65fa\u3002\u5bb6\u53d4\u7279\u5730\u53eb\u6211\u6765\u8fce\u63a5\u4f60\u8001\u4eba\u5bb6\u7684\u3002\u201d \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u7b11\u9053\uff1a\u201c\u4f60\u7684\u53d4\u53d4\u53c8\u4e0d\u662f\u8bf8\u845b\u5b54\u660e\uff0c\u600e\u4f1a\u6709\u672a\u535c\u5148\u77e5\u7684\u672c\u9886\uff0c\u77e5\u9053\u6211\u4eca\u5929\u4e00\u5b9a \u4f1a\u6765\u5230\u6b64\u5904\uff1f\u201d \u3000\u3000\u7aa6\u65fa\u8bf4\u9053\uff1a\u201c\u4e25\u5bb6\u90a3\u5c0f\u599e\u513f\u4e00\u76f4\u4e0d\u89c1\u6709\u4eba\u5c06\u5979\u9001\u6765\uff0c\u5bb6\u53d4\u8fd9\u4e24\u5929\u6b63\u5728\u7b49\u5f97\u5341\u5206\u7740\u6025\u3002\u6211 \u8bf4\uff0c\u522b\u4eba\u6050\u6015\u4e5f\u6ca1\u6709\u90a3\u4e48\u5927\u7684\u672c\u9886\u6d3b\u64d2\u4e25\u58f0\u6d9b\u7684\u5973\u513f\uff0c\u80fd\u591f\u529e\u59a5\u8fd9\u4ef6\u4e8b\u7684\u9664\u975e\u662f\u9ed1\u77f3\u9053\u957f\u3002 \u5bb6\u53d4\u8bf4\uff0c\u4e0d\u9519\uff0c\u4e0d\u9519\uff0c\u90a3\u4f60\u8d76\u5feb\u53bb\u627e\u9ed1\u77f3\u9053\u957f\u63a2\u542c\u6d88\u606f\u5427\u3002\u201d\u6211\u8bf4\u4e0d\u7528\u8dd1\u90a3\u4e48\u8fdc\u53bb\u6253\u63a2\uff0c\u9ed1 \u77f3\u9053\u957f\u51c6\u4fdd\u5df2\u662f\u624b\u5230\u64d2\u6765\uff0c\u6b64\u523b\u53ea\u6015\u5df2\u5728\u9014\u4e2d\u4e86\u3002\u5bb6\u53d4\u8bf4\uff0c\u5f88\u597d\uff0c\u90a3\u4f60\u5c31\u53bb\u8fce\u63a5\u9ed1\u77f3\u9053\u957f\u4fbf \u662f\uff0c\u770b\u770b\u4f60\u6599\u5f97\u4e2d\u8fd8\u662f\u4e0d\u4e2d\u3002\u54c8\u54c8\uff0c\u679c\u7136\u6211\u4eca\u5929\u4e00\u65e9\u4e0b\u5c71\uff0c\u5929\u8fd8\u672a\u9ed1\u5c31\u78b0\u89c1\u9053\u957f\u4e86\u3002\u201d \u3000\u3000\u539f\u6765\u7aa6\u5b89\u5e73\u4e43\u662f\u904d\u6258\u6c5f\u6e56\u53cb\u597d\uff0c\u8bf7\u4ed6\u4eec\u6349\u62ff\u4e25\u70f7\u7684\uff0c\u4e0d\u4ec5\u662f\u8bf7\u4e86\u9ed1\u77f3\u9053\u4eba\u4e00\u4e2a\u800c\u5df2\u3002\u7aa6 \u65fa\u5949\u4e86\u4ed6\u7684\u547d\u4ee4\uff0c\u4e5f\u662f\u8981\u5230\u5404\u5904\u53bb\u6253\u542c\u6d88\u606f\u7684\u3002\u9002\u9022\u5176\u4f1a\uff0c\u4e00\u4e0b\u5c71\u5c31\u78b0\u4e0a\u4e86\u9ed1\u77f3\u9053\u4eba\uff0c\u4ed6\u8bf4 \u7684\u8fd9\u756a\u8bdd\uff0c\u5f53\u7136\u5c31\u5b8c\u5168\u662f\u4e3a\u4e86\u62cd\u9ed1\u77f3\u9053\u4eba\u7684\u9a6c\u5c41\u4e86\u3002 \u3000\u3000\u7231\u6234\u9ad8\u5e3d\u7684\u4eba\u5341\u5c45\u5176\u4e5d\uff0c\u9ed1\u77f3\u9053\u4eba\u4e5f\u4e0d\u4f8b\u5916\uff0c\u542c\u4e86\u54c8\u54c8\u5927\u7b11\uff1a\u201c\u7aa6\u65fa\uff0c\u4f60\u8fd9\u5c0f\u5b50\u5012\u662f\u770b \u5f97\u5f88\u51c6\u3002\u4e25\u58f0\u6d9b\u7684\u5973\u513f\u73b0\u5728\u6b63\u662f\u5728\u8fd9\u9aa1\u8f66\u4e4b\u4e0a\u3002\u4e0d\u8fc7\u4f60\u53ef\u5f97\u653e\u5c0a\u91cd\u4e9b\uff0c\u4e0d\u8bb8\u4f60\u60ca\u5413\u4e86\u5979\u3002\u4eba \u5bb6\u7684\u7236\u4eb2\u597d\u6b79\u4e5f\u662f\u6709\u5927\u4fa0\u4e4b\u79f0\u7684\u6210\u540d\u4eba\u7269\u5462\uff01\u201d\u7aa6\u65fa\u672c\u6765\u5df2\u7ecf\u4f38\u51fa\u624b\u6765\uff0c\u60f3\u8981\u63ed\u5f00\u8f66\u5e18\uff0c\u77a7 \u4e00\u77a7\u4e25\u70f7\u7684\u76f8\u8c8c\uff0c\u7ed9\u9ed1\u77f3\u9053\u4eba\u8fd9\u4e48\u4e00\u8bf4\uff0c\u4e0d\u7531\u5f97\u6ee1\u9762\u901a\u7ea2\uff0c\u8fde\u5fd9\u7f29\u624b\u3002 \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u53d7\u4e86\u4ed6\u7684\u9ad8\u5e3d\uff0c\u4e5f\u4e0d\u60f3\u4ee4\u4ed6\u592a\u8fc7\u96be\u582a\uff0c\u4e8e\u662f\u627e\u8bdd\u6765\u548c\u4ed6\u8bf4\uff0c\u7b11\u95ee\u4ed6\u9053\uff1a\u201c\u4ee4\u53d4 \u53eb\u6211\u628a\u8fd9\u599e\u513f\u9001\u6765\uff0c\u6211\u5df2\u9075\u547d\u529e\u5230\u4e86\u3002\u4f46\u6211\u8fd8\u4e0d\u77e5\u9053\u4ee4\u53d4\u4e3a\u4ec0\u4e48\u82b1\u8fd9\u6837\u5927\u7684\u6c14\u529b\uff0c\u628a\u8fd9\u5c0f\u599e \u513f\u8bf7\u6765\u5462\u3002\u4f60\u53ef\u4ee5\u544a\u8bc9\u6211\u4e48\uff1f\u201d \u3000\u3000\u539f\u6765\u9ed1\u77f3\u9053\u4eba\u56e0\u4e3a\u67d0\u6b21\u906d\u53d7\u4ec7\u5bb6\u56f4\u653b\uff0c\u5f97\u5230\u7aa6\u5b89\u5e73\u51fa\u9762\uff0c\u66ff\u4ed6\u89e3\u56f4\uff0c\u9ed1\u77f3\u9053\u4eba\u662f\u4e2a\u6069\u6028 \u5206\u660e\u7684\u4eba\uff0c\u53d7\u4e86\u7aa6\u5b89\u5e73\u7684\u6069\u60e0\uff0c\u7aa6\u5b89\u5e73\u53c8\u4f4e\u9996\u4e0b\u5fc3\u7684\u4e0e\u4ed6\u7ed3\u7eb3\uff0c\u4e24\u4eba\u9042\u6210\u4e86\u516b\u62dc\u4e4b\u4ea4\u3002\u9ed1\u77f3 \u9053\u4eba\u7b54\u5e94\u53ef\u4ee5\u4e3a\u7aa6\u5b89\u5e73\u505a\u4efb\u4f55\u4e8b\u60c5\uff0c\u662f\u4ee5\u8fd9\u6b21\u4ed6\u63a5\u5230\u4e86\u7aa6\u5b89\u5e73\u7684\u7eff\u6797\u7bad\uff0c\u867d\u7136\u4e0d\u77e5\u9053\u539f\u56e0\uff0c \u4e5f\u7167\u529e\u4e86\u3002 \u3000\u3000\u597d\u5947\u4e4b\u5fc3\uff0c\u4eba\u4eba\u7686\u6709\u3002\u662f\u4ee5\u9ed1\u77f3\u9053\u4eba\u867d\u7136\u77e5\u9053\u89c1\u5230\u4e86\u7aa6\u5b89\u5e73\u4e4b\u540e\uff0c\u7aa6\u5b89\u5e73\u4e00\u5b9a\u4f1a\u544a\u8bc9\u4ed6 \u7684\uff0c\u4f46\u8fd8\u662f\u7981\u4e0d\u4f4f\u8981\u5148\u5411\u7aa6\u65fa\u63a2\u95ee\u56e0\u7531\u3002 \u3000\u3000\u7aa6\u65fa\u8bf4\u9053\uff1a\u201c\u539f\u6765\u9053\u957f\u8fd8\u4e0d\u77e5\u9053\uff0c\u5b9e\u4e0d\u76f8\u7792\uff0c\u5bb6\u53d4\u8981\u8fd9\u4e2a\u5973\u5a03\u513f\uff0c\u4e43\u662f\u4e3a\u4e86\u8981\u6765\u5bf9\u4ed8\u5b5f \u5c11\u521a\u7684\u3002\u201d \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u6014\u4e86\u4e00\u6014\uff0c\u8bf4\u9053\uff1a\u201c\u662f\u4e0d\u662f\u4eba\u79f0\u6c5f\u5357\u5927\u4fa0\u7684\u5b5f\u5c11\u521a\uff1f\u201d \u3000\u3000\u7aa6\u65fa\u9053\uff1a\u201c\u4e0d\u9519\u3002\u8fd9\u5b5f\u5c11\u521a\u53c8\u6709\u795e\u5251\u4e4b\u79f0\uff0c\u5bb6\u53d4\u6050\u6015\u6253\u4e0d\u8fc7\u4ed6\uff0c\u53ea\u597d\u51fa\u6b64\u4e0b\u7b56\u3002\u201d \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u7528\u8fd9\u4e2a\u5973\u5a03\u513f\u5c31\u53ef\u4ee5\u5bf9\u4ed8\u5f97\u4e86\u5b5f\u5c11\u521a\uff1f\u201d \u3000\u3000\u7aa6\u65fa\u9053\uff1a\u201c\u4e25\u58f0\u6d9b\u662f\u5b5f\u5c11\u521a\u7684\u59d0\u592b\uff0c\u53ea\u6709\u8fd9\u4e00\u4e2a\u5b9d\u8d1d\u5973\u513f\u3002\u5979\u843d\u5728\u54b1\u4eec\u624b\u4e2d\uff0c\u5b5f\u5c11\u521a\u5c31 \u4e0d\u80fd\u4e0d\u6295\u9f20\u5fcc\u5668\u4e86\uff01\u201d \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u7709\u5934\u4e00\u76b1\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u7aa6\u5927\u54e5\u672c\u6765\u662f\u9ed1\u9053\u4e0a\u4e00\u4f4d\u54cd\u5f53\u5f53\u7684\u6c49\u5b50\u3002\u4f46\u8fd9\u6837\u7684\u4f5c \u4e3a\uff0c\u5374\u662f\u672a\u514d\u6709\u70b9\u5351\u9119\u4e86\u3002\u201d\u4f46\u56e0\u4ed6\u53d7\u8fc7\u7aa6\u5b89\u5e73\u7684\u6069\u60e0\uff0c\u4e0d\u4fbf\u5728\u7aa6\u65fa\u9762\u524d\u76f4\u65a5\u5176\u975e\uff0c\u5fc3\u91cc\u60f3 \u9053\uff1a\u201c\u4e14\u5f85\u6211\u89c1\u4e86\u5b89\u5e73\uff0c\u518d\u597d\u597d\u7684\u89c4\u529d\u4ed6\uff0c\u5b81\u613f\u6211\u66ff\u4ed6\u51fa\u9a6c\uff0c\u4e0e\u5b5f\u5c11\u521a\u5267\u6597\u4e00\u573a\uff0c\u6b7b\u5728\u4ed6\u7684 \u5251\u4e0b\uff0c\u4e5f\u80dc\u4e8e\u505a\u51fa\u8fd9\u7b49\u4e8b\u6765\uff0c\u7ed9\u5929\u4e0b\u82f1\u96c4\u803b\u7b11\u3002\u201d \u3000\u3000\u7aa6\u65fa\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u5df2\u7ecf\u5f97\u5230\u4e86\u786e\u5b9e\u7684\u6d88\u606f\uff0c\u5b5f\u5c11\u521a\u8fd9\u4e24\u5929\u5c31\u4f1a\u6765\u5230\u98de\u9f99\u5c71\uff0c\u6240\u4ee5\u5bb6\u53d4\u5f88 \u6025\u3002\u201d \u3000\u3000\u7aa6\u65fa\u6b63\u8981\u8bf4\u51fa\u4ed6\u7684\u8981\u6c42\uff0c\u9ed1\u77f3\u9053\u4eba\u5df2\u5148\u95ee\u9053\uff1a\u201c\u6211\u6709\u4e00\u4e8b\u672a\u660e\uff0c\u4e0d\u77e5\u8001\u5144\u662f\u5426\u53ef\u4ee5\u4e3a\u6211 \u4e00\u7834\u7591\u56e2\uff1f\u201d \u3000\u3000\u7aa6\u65fa\u8fde\u5fd9\u8bf4\u9053\uff1a\u201c\u9053\u957f\u8fd9\u6837\u5ba2\u6c14\uff0c\u6298\u715e\u5c0f\u4f84\u4e86\u3002\u4e0d\u77e5\u9053\u957f\u6b32\u77e5\u4f55\u4e8b\uff1f\u201d \u3000\u3000\u9ed1\u77f3\u9053\u4eba\uff1a\u201c\u4ee4\u53d4\u4f55\u65f6\u4e0e\u5b5f\u5c11\u521a\u7ed3\u7684\u4ec7\uff0c\u4e3a\u4f55\u6211\u4ece\u6765\u6ca1\u542c\u4ed6\u8bf4\u8fc7\u3002\u201d \u3000\u3000\u7aa6\u65fa\u9053\uff1a\u201c\u5bb6\u53d4\u4e0e\u8fd9\u5b5f\u8001\u5934\u5b50\u5f80\u65e5\u65e0\u51a4\uff0c\u8fd1\u65e5\u65e0\u4ec7\uff01\u201d \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u8be7\u9053\uff1a\u201c\u90a3\u53c8\u4e3a\u4f55\u8981\u8d39\u5c3d\u5fc3\u673a\u6765\u5bf9\u4ed8\u4ed6\uff1f\u201d \u3000\u3000\u7aa6\u65fa\u4e00\u6765\u56e0\u4e3a\u9ed1\u77f3\u9053\u4eba\u7ed9\u4e86\u4ed6\u7684\u9762\u5b50\uff0c\u4e8c\u6765\u4ed6\u53ea\u9053\u9ed1\u77f3\u9053\u4eba\u548c\u4ed6\u53d4\u53d4\u4e43\u662f\u516b\u62dc\u4e4b\u4ea4\uff0c\u8bf4 \u4e5f\u65e0\u59a8\uff0c\u4e3a\u4e86\u70ab\u8000\u81ea\u5df1\u662f\u7aa6\u5b89\u5e73\u7684\u5fc3\u8179\uff0c\u4fbf\u9053\uff1a\u201c\u8fd9\u4e2a\u79d8\u5bc6\u9664\u4e86\u5bb6\u53d4\u53ea\u6709\u6211\u77e5\u9053\uff0c\u5bb6\u53d4\u672c\u6765 \u662f\u4e0d\u8bb8\u5411\u5916\u4eba\u8bf4\u7684\uff0c\u9053\u957f\u662f\u81ea\u5df1\u4eba\uff0c\u5f53\u7136\u53ef\u4ee5\u8bf4\u5f97\u3002\u4e0d\u8fc7\u5e0c\u671b\u9053\u957f\u5343\u4e07\u5b88\u53e3\u5982\u74f6\u3002\u201d \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u5fc3\u91cc\u5f88\u4e0d\u9ad8\u5174\uff0c\u4f46\u4ecd\u662f\u5fcd\u7740\u4e0d\u53d1\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u82e5\u4fe1\u4e0d\u8fc7\u6211\uff0c\u90a3\u5c31\u4e0d\u8bf4\u597d\u4e86\u3002\u201d \u3000\u3000\u7aa6\u65fa\u60f6\u7136\u8bf4\u9053\uff1a\u201c\u9053\u957f\u5343\u4e07\u4e0d\u8981\u8bef\u4f1a\uff0c\u627e\u600e\u4f1a\u4e0d\u76f8\u4fe1\u9053\u957f\uff1f\u6b64\u4e8b\u5b9e\u5728\u7275\u8fde\u592a\u5927\uff0c\u6240\u4ee5\u6211 \u591a\u8bf4\u4e86\u4e24\u53e5\uff0c\u8bf7\u9053\u957f\u89c1\u8c05\u3002\u201d \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u9053\uff1a\u201c\u522b\u5356\u5173\u5b50\u4e86\uff0c\u723d\u723d\u5feb\u5feb\u8bf4\u5427\uff01\u201d \u3000\u3000\u7aa6\u65fa\u51d1\u5230\u9ed1\u77f3\u9053\u4eba\u8033\u8fb9\uff0c\u5c0f\u58f0\u8bf4\u9053\uff1a\u201c\u5b9e\u4e0d\u76f8\u7792\uff0c\u6700\u6025\u4e8e\u8981\u5bf9\u4ed8\u5b5f\u5c11\u521a\u7684\uff0c\u8fd8\u4e0d\u662f\u5bb6\u53d4 \u5462\uff01\u201d \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u9053\uff1a\u201c\u90a3\u4eba\u662f\u8c01\u3002\u201d\u7aa6\u65fa\u8bf4\u9053\uff1a\u201c\u662f\u9633\u5929\u96f7\u3002\u201d\u9ed1\u77f3\u9053\u4eba\u8bf4\u9053\uff1a\u201c\u9633\u5929\u96f7\u4e0d\u662f \u91d1\u56fd\u7684\u56fd\u5e08\u4e48\uff1f\u201d\u7aa6\u65fa\u7b11\u9053\uff1a\u201c\u4e00\u70b9\u4e0d\u9519\u3002\u9053\u957f\u60f3\u4e0d\u5230\u5427\uff1f\u201d \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u6697\u6697\u5403\u60ca\uff0c\u4e00\u65f6\u95f4\u5fc3\u70e6\u610f\u4e71\uff0c\u4e0d\u77e5\u5982\u4f55\u662f\u597d\u3002 \u3000\u3000\u7aa6\u65fa\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u9053\u957f\u8fd9\u8be5\u660e\u767d\u4e86\u5427\uff0c\u5c60\u767e\u57ce\u4e0e\u5b5f\u5c11\u521a\u4e43\u662f\u9633\u5929\u96f7\u7684\u4e24\u5927\u5bf9\u5934\uff0c\u5c60\u767e\u57ce \u53bb\u5e74\u5728\u8499\u53e4\u7ed9\u9633\u5929\u96f7\u4e0e\u6210\u5409\u601d\u6c57\u7684\u91d1\u5e10\u6b66\u571f\u8054\u624b\u6740\u6389\uff0c\u5982\u4eca\u5c31\u5269\u4e0b\u4e00\u4e2a\u5b5f\u5c11\u521a\u4e86\u3002\u4f46\u82e5\u7136\u4ed6 \u4e0d\u6740\u6389\u5b5f\u5c11\u521a\uff0c\u4e5f\u8fd8\u662f\u4e0d\u80fd\u5b89\u6795\u65e0\u5fe7\u7684\u3002\u201d \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u5f3a\u6444\u5fc3\u795e\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u5179\u4e8b\u4f53\u5927\uff0c\u6211\u987b\u5f97\u5957\u4ed6\u51fa\u5b9e\u8bdd\u624d\u884c\u3002\u201d\u5f53\u4e0b\u4f5c\u51fa\u7a0d\u5fae \u60ca\u8be7\u4e5f\u5374\u4e0d\u592a\u8fc7\u5206\u7684\u795e\u8272\u8bf4\u9053\uff1a\u201c\u786e\u5b9e\u662f\u6709\u70b9\u610f\u60f3\u4e0d\u5230\uff0c\u4ee4\u53d4\u662f\u51e0\u65f6\u548c\u9633\u5929\u96f7\u642d\u4e0a\u7684\u4ea4 \u60c5\uff1f\u201d \u3000\u3000\u7aa6\u65fa\u8bf4\u9053\uff1a\u201c\u5bb6\u53d4\u4e0e\u9633\u5929\u96f7\u672c\u6765\u6ca1\u6709\u4ea4\u60c5\uff0c\u4f46\u56e0\u5982\u4eca\u5df2\u662f\u4e00\u6761\u8def\u4e0a\u7684\u4eba\uff0c\u81ea\u7136\u4e5f\u5c31\u5fc5\u987b \u540c\u4ec7\u654c\u5ffe\u4e86\u3002\u201d \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u4ee4\u53d4\u662f\u5df2\u7ecf\u6697\u4e2d\u6295\u987a\u4e86\u91d1\u56fd\u4e48\uff1f\u201d \u3000\u3000\u7aa6\u65fa\u9053\uff1a\u201c\u8fd9\u5012\u4e0d\u662f\u3002\u91d1\u56fd\u76ee\u524d\u8870\u4ea1\u5728\u5373\uff0c\u8fde\u9633\u5929\u96f7\u90fd\u8981\u53e6\u6295\u660e\u4e3b\u5462\uff0c\u5bb6\u53d4\u5c82\u80fd\u4e0d\u8bc6\u65f6 \u52a1\uff0c\u5728\u8fd9\u4e2a\u65f6\u5019\u6295\u987a\u91d1\u5ef7\uff1f\u201d \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u604d\u7136\u5927\u609f\uff0c\u8bf4\u9053\uff1a\u201c\u6562\u60c5\u4ee4\u53d4\u662f\u548c\u8499\u53e4\u4eba\u5df2\u7ecf\u6302\u4e0a\u4e86\u94a9\uff1f\u201d \u3000\u3000\u7aa6\u65fa\u7b11\u9053\uff1a\u201c\u9053\u957f\u8fd9\u4e00\u731c\u53ef\u731c\u5bf9\u4e86\u3002\u6211\u8fd8\u53ef\u4ee5\u544a\u8bc9\u9053\u957f\u4e00\u4e2a\u79d8\u5bc6\uff0c\u9633\u5929\u96f7\u5982\u4eca\u867d\u7136\u662f\u8eab \u4e3a\u91d1\u56fd\u56fd\u5e08\uff0c\u5176\u5b9e\u4e5f\u662f\u770b\u98ce\u4f7f\u8235\uff0c\u548c\u8499\u53e4\u7684\u4f7f\u8005\u7ecf\u5e38\u6697\u901a\u6d88\u606f\u7684\u3002\u201d \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\uff0c\u602a\u4e0d\u5f97\u4ed6\u4eec\u4e8c\u4eba\u8981\u5408\u529b\u5bf9\u4ed8\u5b5f\u5c11\u521a\u4e86\u3002\u4f46\u4f55\u4ee5\u5b5f\u5c11\u521a\u53c8\u4f1a\u5230 \u98de\u9f99\u5c71\u6765\u5462\uff1f\u96be\u9053\u4ed6\u4e5f\u77e5\u9053\u4e86\u8fd9\u4e2a\u79d8\u5bc6\uff0c\u7279\u5730\u8dd1\u6765\u627e\u4ee4\u53d4\u7684\u6666\u6c14\u4e48\uff1f\u201d \u3000\u3000\u7aa6\u65fa\u7b11\u9053\uff1a\u201c\u5b5f\u8001\u5934\u513f\u518d\u795e\u901a\u4e86\u5927\uff0c\u4e5f\u4e0d\u4f1a\u77e5\u9053\u8fd9\u4e2a\u79d8\u5bc6\u7684\uff0c\u8fd9\u6b21\u4ed6\u662f\u81ea\u6295\u7f57\u7f51\u3002\u201d\u5f53 \u4e0b\u4fbf\u628a\u7aa6\u5b89\u5e73\u8bbe\u8ba1\u9a97\u65b0\u4efb\u7684\u7eff\u6797\u76df\u4e3b\u674e\u601d\u5357\u6765\u98de\u9f99\u5c71\u4e4b\u4e8b\uff0c\u4e00\u4e94\u4e00\u5341\u5730\u544a\u8bc9\u4e86\u9ed1\u77f3\u9053\u4eba\uff0c\u8ddf \u7740\u8bf4\u9053\uff1a\u201c\u5b5f\u5c11\u521a\u662f\u4f5c\u674e\u601d\u5357\u7684\u4fdd\u9556\u6765\u7684\u3002\u4e0d\u8fc7\u542c\u8bf4\u8fd9\u59d3\u674e\u7684\u5c0f\u5b50\u4e5f\u6781\u4e86\u5f97\uff0c\u6240\u4ee5\u5bb6\u53d4\u867d\u7136 \u8bf7\u6765\u9633\u5929\u96f7\u7684\u4f84\u5b50\u548c\u767d\u4e07\u96c4\u767d\u8001\u7237\u5b50\u8fd9\u4e24\u4f4d\u5f3a\u624b\uff0c\u4e5f\u8fd8\u662f\u6050\u6015\u96be\u64cd\u80dc\u5238\uff0c\u6545\u6b64\u5b81\u53ef\u5907\u800c\u4e0d \u7528\uff0c\u591a\u627e\u4e00\u4e2a\u529e\u6cd5\u6765\u5bf9\u4ed8\u5b5f\u5c11\u521a\uff0c\u8fd9\u5c31\u662f\u5bb6\u53d4\u4e3a\u4ec0\u4e48\u8981\u9ebb\u70e6\u9053\u957f\u5c06\u8fd9\u4e2a\u5973\u5a03\u513f\u8bf7\u6765\u7684\u539f \u56e0\u3002\u201d\u7aa6\u65fa\u54ea\u91cc\u77e5\u9053\uff0c\u4ed6\u4ee5\u4e3a\u5b5f\u5c11\u521a\u548c\u674e\u601d\u5357\u662f\u201c\u81ea\u6295\u7f57\u7f51\u201d\uff0c\u5176\u5b9e\u4ed6\u4eec\u4e8c\u4eba\u662f\u5c06\u8ba1\u5c31\u8ba1\uff0c \u6765\u627e\u4ed6\u4eec\u7684\u6666\u6c14\u7684\uff0c\u9ed1\u77f3\u9053\u4eba\u5012\u662f\u65e0\u610f\u4e2d\u8bf4\u4e2d\u4e86\u3002 \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u8fd9\u6837\u7684\u4e8b\u60c5\u5b9e\u5728\u662f\u592a\u8fc7\u51fa\u4ed6\u610f\u6599\u4e4b\u5916\u4e86\u3002\u4f46\u56e0\u7aa6\u65fa\u4e0d\u662f\u4ed6\u53ef \u4ee5\u8bf4\u771f\u5fc3\u8bdd\u7684\u5bf9\u624b\uff0c\u4ed6\u53ea\u597d\u9690\u5fcd\u4e0d\u53d1\uff0c\u76ae\u7b11\u8089\u4e0d\u7b11\u5730\u6253\u4e86\u4e00\u4e2a\u54c8\u54c8\uff0c\u8bf4\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u4eca\u53d4\u5012 \u662f\u4e2a\u8bc6\u65f6\u52a1\u7684\u4fca\u6770\u5462\u3002\u201d \u3000\u3000\u7aa6\u65fa\u88c2\u5f00\u5634\u5df4\u7b11\u9053\uff1a\u201c\u96be\u5f97\u9053\u957f\u4e5f\u662f\u4e00\u6837\u5fc3\u601d\uff0c\u8fd9\u6b63\u662f\u82f1\u96c4\u6240\u89c1\u7565\u540c\u4e86\u3002\u8bf4\u8001\u5b9e\u8bdd\uff0c\u54b1 \u4eec\u505a\u5c71\u5927\u738b\u867d\u4e5f\u4e0d\u6101\u5403\u559d\uff0c\u4f46\u603b\u4e0d\u80fd\u4e00\u8f88\u5b50\u5728\u5200\u53e3\u4e0a\u8ba8\u751f\u6d3b\u554a\uff0c\u8499\u53e4\u4eba\u773c\u770b\u5c31\u53ef\u4ee5\u5f97\u5929\u4e0b \u4e86\uff0c\u627e\u4e2a\u5b98\u513f\u505a\u505a\uff0c\u4e0b\u534a\u4e16\u5012\u662f\u53ef\u4ee5\u5b89\u4eab\u8363\u534e\u3002\u201d \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u5fc3\u91cc\u6697\u9053\uff1a\u201c\u9b3c\u624d\u548c\u4f60\u4e00\u6837\u5fc3\u601d\uff01\u60f3\u4e0d\u5230\u7aa6\u5b89\u5e73\u7adf\u4f1a\u5982\u6b64\uff0c\u8fd9\u98de\u9f99\u5c71\u53bb\u5462\u8fd8\u662f \u4e0d\u53bb\uff1f\u201d \u3000\u3000\u7aa6\u65fa\u53ea\u9053\u9ed1\u77f3\u9053\u4eba\u662f\u81ea\u5df1\u4eba\uff0c\u54ea\u77e5\u9ed1\u77f3\u9053\u4eba\u542c\u4e86\u4ed6\u8fd9\u4e00\u756a\u8bf4\u8bdd\u540e\uff0c\u5fc3\u4e2d\u5df2\u662f\u9887\u6709\u6094\u610f\u3002 \u7aa6\u65fa\u8bf4\u9053\uff1a\u201c\u5bb6\u53d4\u5982\u4eca\u6b63\u5728\u6025\u7740\u7b49\u8fd9\u5973\u5a03\u513f\uff0c\u4e0d\u5982\u9053\u957f\u5c06\u5979\u4ea4\u7ed9\u6211\uff0c\u8ba9\u6211\u5feb\u9a6c\u9001\u5979\u56de\u53bb\u5982 \u4f55\u3002\u201d \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u5fc3\u91cc\u60f3\u5230\uff1a\u201c\u82e5\u4e0d\u662f\u6253\u72d7\u8981\u770b\u4e3b\u4eba\u9762\uff0c\u4f60\u8fd9\u5c0f\u5b50\u6211\u65e9\u5c31\u4e00\u5200\u5c06\u4f60\u6740\u4e86\u3002\u4f46\u7aa6\u5b89 \u5e73\u6bd5\u7adf\u662f\u4e8e\u6211\u6709\u6069\uff0c\u8fd9\u98de\u9f99\u5c71\u6050\u6015\u8fd8\u662f\u8981\u53bb\u4e00\u8d9f\u7684\u3002\u53ea\u662f\u8fd9\u5973\u5a03\u513f\u5374\u5982\u4f55\u5904\u7f6e\uff0c\u96be\u9053\u6211\u5c31\u5f53 \u771f\u5c06\u5979\u4ea4\u7ed9\u4e86\u7aa6\u5b89\u5e73\uff0c\u8ba9\u7aa6\u5b89\u5e73\u62ff\u53bb\u8ba8\u597d\u8499\u53e4\u9791\u5b50\u5417\uff1f\u5b5f\u5c11\u521a\u7eb5\u7136\u4e0d\u6740\u6211\uff0c\u5929\u4e0b\u82f1\u96c4\u4e5f\u8981\u803b \u7b11\u6211\u554a\uff01\u201d \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u5185\u5fc3\u4ea4\u6218\u4e86\u597d\u4e00\u4f1a\uff0c\u7ec8\u4e8e\u7acb\u4e0b\u4e86\u51b3\u5fc3\uff0c\u8bf4\u9053\uff1a\u201c\u7aa6\u65fa\uff0c\u6211\u6ca1\u6709\u89c1\u8fc7\u4f60\u7684\u672c\u9886\uff0c \u4f60\u65ab\u6211\u4e00\u5200\u8bd5\u8bd5\u3002\u201d \u3000\u3000\u7aa6\u65fa\u83ab\u540d\u5176\u5999\uff0c\u5403\u4e86\u4e00\u60ca\u8bf4\u9053\uff1a\u201c\u9053\u957f\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u5f85\u4f1a\u513f\u518d\u544a\u8bc9\u4f60\uff0c\u4f60\u4e0d\u7528\u5bb3\u6015\uff0c\u5c3d\u7ba1\u65ab\u6765\u5c31\u662f\u3002\u201d \u3000\u3000\u7aa6\u65fa\u9053\uff1a\u201c\u5c0f\u4eba\u4e0d\u6562\u3002\u201d \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u9053\uff1a\u201c\u8fd9\u662f\u6211\u53eb\u4f60\u7684\uff0c\u8c05\u4f60\u4e5f\u4e0d\u80fd\u591f\u4f24\u5f97\u4e86\u6211\uff0c\u5c31\u7b97\u4f60\u4f24\u4e86\u6211\uff0c\u6211\u4e5f\u4e0d\u4f1a\u602a \u4f60\u3002\u201d \u3000\u3000\u7aa6\u65fa\u77e5\u9053\u9ed1\u77f3\u9053\u4eba\u7684\u813e\u6c14\u662f\u4e0d\u5bb9\u522b\u4eba\u8fdd\u62d7\u4ed6\u7684\uff0c\u5fc3\u91cc\u867d\u7136\u6709\u70b9\u5bb3\u6015\uff0c\u4e5f\u53ea\u597d\u62ff\u8d77\u4e86\u5200\uff0c \u8bf4\u9053\uff1a\u201c\u90a3\u5c31\u8bf7\u9053\u957f\u6055\u6211\u653e\u8086\u4e86\u3002\u201d\u865a\u5f20\u58f0\u52bf\uff0c\u6325\u4e86\u4e00\u5200\uff0c\u53ef\u8fd8\u4e0d\u6562\u5f53\u771f\u671d\u7740\u9ed1\u77f3\u9053\u4eba\u7684\u8eab \u5b50\u65ab\u4e0b\u3002\u9ed1\u77f3\u9053\u4eba\u7709\u5934\u4e00\u76b1\uff0c\u9053\uff1a\u201c\u6211\u53eb\u4f60\u65ab\u5c31\u65ab\uff0c\u4f60\u7adf\u6562\u5c0f\u770b\u6211\u4e48\uff1f\u201d\u7aa6\u65fa\u9053\uff1a\u201c\u662f\uff0c \u662f\u3002\u201d\u5c0f\u4eba\u65ab\u4e86\uff01\u201d\u95ed\u8d77\u773c\u775b\uff0c\u4e00\u5200\u65a9\u4e0b\u3002 \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u5f85\u5200\u950b\u582a\u582a\u5288\u5230\u4e4b\u9645\uff0c\u53cc\u6307\u4e00\u4f38\uff0c\u94b3\u7740\u4e86\u5200\u80cc\uff0c\u8f7b\u8f7b\u4e00\u63a8\uff0c\u7aa6\u65fa\u8dcc\u4e86\u4e2a\u56db\u811a\u671d \u5929\uff0c\u989d\u5934\u78b0\u5f97\u80bf\u8d77\u4e86\u4e00\u5927\u5757\u3002\u201d \u3000\u3000\u7aa6\u65fa\u722c\u4e86\u8d77\u6765\uff0c\u53c8\u7f9e\u53c8\u607c\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u4eba\u672c\u9886\u4e0d\u6d4e\uff0c\u6559\u9053\u957f\u89c1\u7b11\u4e86\uff0c\u4f46\u4e0d\u77e5\u9053\u957f\u4e3a\u4f55\u8981 \u8bd5\u6211\u7684\u529f\u592b\uff1f\u201d \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u4e5f\u81ea\u77e5\u672c\u9886\u4e0d\u6d4e\u4e86\u5417\uff1f\u8001\u5b9e\u544a\u8bc9\u4f60\u5427\uff0c\u8fd9\u5973\u5a03\u513f\u7684\u672c\u9886\u548c\u6211\u4e0d \u76f8\u4e0a\u4e0b\uff0c\u5979\u7684\u7236\u4eb2\u5728\u5317\u4e94\u7701\u4e5f\u6709\u8bb8\u591a\u670b\u53cb\uff0c\u4f60\u8981\u5c06\u5979\u62bc\u89e3\u56de\u5c71\uff0c\u4e0d\u6015\u51fa\u4e8b\u4e48\uff1f\u54fc\uff0c\u4f60\u73b0\u5728\u53ea \u4e0d\u8fc7\u8dcc\u4e86\u4e00\u8de4\uff0c\u82e5\u7136\u51fa\u4e8b\uff0c\u90a3\u5c31\u53ea\u6015\u8fde\u4f60\u7684\u5403\u996d\u5bb6\u4f19\u4e5f\u4fdd\u4e0d\u4f4f\u4e86\u3002\u201d \u3000\u3000\u7aa6\u65fa\u9762\u4e0a\u4e00\u9635\u9752\u4e00\u9635\u7ea2\uff0c\u8bb7\u8bb7\u8bf4\u9053\uff1a\u201c\u539f\u6765\u9053\u957f\u4e43\u662f\u4e00\u756a\u597d\u610f\uff0c\u5c0f\u7684\u5374\u5403\u4e0d\u6d88\u4e86\u3002\u65e2\u7136 \u5982\u6b64\uff0c\u5c0f\u7684\u5c31\u5148\u8d76\u56de\u53bb\u7ed9\u9053\u957f\u62a5\u4fe1\u5982\u4f55\uff1f\u201d \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u9053\uff1a\u201c\u5bf9\u5566\uff0c\u8fd9\u6837\u5c31\u6ca1\u4f60\u7684\u4e8b\u4e86\uff0c\u4f60\u5feb\u53bb\u5427\u3002\u201d\u7aa6\u65fa\u5fc3\u4e2d\u6697\u6697\u5492\u9a82\uff1a\u201c\u4f60\u8fd9\u725b \u9f3b\u5b50\u81ed\u9053\u957f\u7eb5\u7136\u662f\u51fa\u4e8e\u597d\u610f\uff0c\u4e5f\u4e0d\u8be5\u5982\u6b64\u4f5c\u5f04\u6211\u3002\u4e14\u7b49\u4f60\u5230\u4e86\u5c71\u5be8\uff0c\u6211\u518d\u6162\u6162\u7684\u6446\u5e03\u4f60\u3002\u53d4 \u53d4\u867d\u7136\u548c\u4f60\u662f\u516b\u62dc\u4e4b\u4ea4\uff0c\u8c05\u4ed6\u603b\u8fd8\u662f\u542c\u6211\u7684\u8bdd\u3002\u201d \u3000\u3000\u4e14\u8bf4\u4e25\u70f7\u5728\u9aa1\u8f66\u4e0a\u542c\u5230\u4e86\u7aa6\u65fa\u6240\u8bf4\u7684\u90a3\u756a\u8bf4\u8bdd\uff0c\u5fc3\u4e2d\u53c8\u559c\u53c8\u60ca\u3002\u559c\u7684\u662f\u542c\u5230\u4e86\u8205\u8205\u6c5f\u5357 \u5927\u4fa0\u5b5f\u5c11\u521a\u7684\u6d88\u606f\uff0c\u60ca\u7684\u662f\u77e5\u9053\u4e86\u4ed6\u4eec\u8981\u62ff\u81ea\u5df1\u6765\u8981\u631f\u8205\u8205\u7684\u8fd9\u4e2a\u9634\u8c0b\u3002 \u3000\u3000\u4e25\u70f7\u5fc3\u4e71\u5982\u9ebb\uff0c\u6697\u81ea\u601d\u9053\uff1a\u201c\u8205\u8205\u6765\u4e86\uff0c\u4e0d\u77e5\u660e\u971e\u8868\u59b9\u6765\u4e86\u6ca1\u6709\uff1f\u4e00\u522b\u56db\u5e74\uff0c\u4e0d\u77e5\u660e\u971e \u53ef\u66fe\u89c1\u8fc7\u4e86\u8c37\u6db5\u865a\uff1f\u201d\u53c8\u60f3\uff1a\u201c\u8205\u8205\u6b66\u529f\u7edd\u4e16\uff0c\u4ed6\u89c1\u4e86\u6211\uff0c\u4e00\u5b9a\u4f1a\u6551\u6211\u7684\u3002\u4e0d\u8fc7\uff0c\u53ea\u6015\u6211\u5728 \u4ed6\u4eec\u631f\u6301\u4e4b\u4e0b\uff0c\u8205\u8205\u4e0d\u6562\u52a8\u624b\uff0c\u90a3\u5c31\u53cd\u800c\u7d2f\u4e86\u4ed6\u3002\u8fd9\u662f\u5173\u7cfb\u4e49\u519b\u6297\u654c\u7684\u5927\u4e8b\uff0c\u5018\u82e5\u56e0\u6211\u800c\u8bef \u4e86\u5927\u4e8b\uff0c\u6211\u5c31\u66f4\u662f\u7f6a\u5b7d\u6df1\u91cd\u4e86\uff01\u201d \u3000\u3000\u60f3\u5230\u6b64\u5904\uff0c\u4e25\u70f7\u4e0d\u7981\u5927\u9a82\u8d77\u6765\uff1a\u201c\u4f60\u8fd9\u725b\u9f3b\u5b50\u81ed\u9053\u58eb\u81ea\u5938\u662f\u9ed1\u9053\u4e0a\u54cd\u5f53\u5f53\u7684\u6c49\u5b50\uff0c\u5374\u539f \u6765\u662f\u5356\u56fd\u6c42\u8363\u7684\u5978\u5f92\uff01\u54fc\uff0c\u8fd9\u6bd4\u6740\u4eba\u653e\u706b\u3001\u63b3\u63a0\u5978\u6deb\u7684\u532a\u5f92\u8fd8\u66f4\u53ef\u803b\u53ef\u6068\uff01\u201d \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u53eb\u9053\uff1a\u201c\u5c0f\u59d0\uff0c\u4f60\u5148\u522b\u80e1\u9a82\u597d\u4e0d\u597d\uff1f\u201d\u4e25\u70f7\u9053\uff1a\u201c\u6700\u591a\u4f60\u6740\u4e86\u6211\uff0c\u6211\u504f\u8981 \u9a82\uff01\u201d\u9ed1\u77f3\u9053\u4eba\u9053\uff1a\u201c\u6211\u7684\u5fc3\u91cc\u4e5f\u6b63\u5728\u70e6\u7740\u5462\uff0c\u6c42\u6c42\u4f60\u522b\u9a82\u597d\u4e0d\u597d\uff1f\u8ba9\u6211\u60f3\u4e00\u60f3\uff01\u597d\uff0c\u4f60\u82e5 \u518d\u9a82\uff0c\u6211\u53ea\u597d\u628a\u4f60\u629b\u5728\u8fd9\u8352\u91ce\u5582\u72fc\u4e86\uff01\u201d \u3000\u3000\u4e25\u70f7\u542c\u5f97\u4ed6\u8bf4\u5f97\u6073\u5207\uff0c\u5012\u662f\u4e0d\u7981\u4e00\u6014\uff0c\u60f3\u9053\uff1a\u201c\u96be\u9053\u4ed6\u548c\u90a3\u4e2a\u4ec0\u4e48\u98de\u9f99\u5c71\u7684\u7aa6\u5be8\u4e3b\u5e76\u4e0d \u662f\u4e00\u6761\u5fc3\uff1f\u4f46\u4ed6\u4e3a\u4ec0\u4e48\u53c8\u8981\u542c\u90a3\u5be8\u4e3b\u4e4b\u547d\u6765\u6349\u62ff\u6211\uff1f\u201d\u4e25\u70f7\u4e00\u6765\u8d77\u4e86\u7591\u5fc3\uff0c\u4e8c\u6765\u4e5f\u786e\u5b9e\u6709\u70b9 \u5bb3\u6015\u4ed6\u5c06\u81ea\u5df1\u629b\u5728\u8352\u91ce\uff0c\u201c\u65e0\u8bba\u5982\u4f55\uff0c\u8fd9\u81ed\u9053\u571f\u867d\u7136\u53ef\u6076\uff0c\u4e5f\u8fd8\u662f\u6bd4\u8f83\u6b63\u6d3e\uff0c\u6211\u82e5\u843d\u5728\u98de\u9f99 \u5c71\u7684\u4eba\u624b\u4e2d\uff0c\u5c31\u53ea\u6015\u8981\u66f4\u96be\u582a\u4e86\u3002\u201d\u4e8e\u662f\u4f4f\u53e3\u4e0d\u9a82\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4f60\u5c31\u597d\u597d\u60f3\u5427\u3002\u6211 \u662f\u4e2a\u5973\u5b50\uff0c\u4e5f\u77e5\u5356\u56fd\u6c42\u8363\u7684\u53ef\u803b\uff0c\u4f60\u662f\u4e2a\u7537\u5b50\u6c49\uff0c\u5012\u662f\u8be5\u597d\u597d\u7684\u60f3\u4e00\u60f3\uff0c\u8be5\u4e0d\u8be5\u505a\u51fa\u8fb1\u6ca1\u7956 \u5b97\u7684\u4e8b\u60c5\u4e86\u3002\u201d \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u7ed9\u5979\u8fd9\u4e48\u4e00\u8bf4\uff0c\u4e0d\u7981\u6697\u6697\u89c9\u5f97\u60ed\u6127\u3002\u539f\u6765\u4ed6\u867d\u7136\u548c\u4fa0\u4e49\u9053\u4e00\u5411\u6ca1\u6709\u5f80\u6765\uff0c\u4f46\u56fd \u5bb6\u6c11\u65cf\u7684\u89c2\u5ff5\u5374\u8fd8\u662f\u6709\u7684\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u5973\u5a03\u513f\u5012\u662f\u8bf4\u5f97\u4e0d\u9519\uff0c\u6211\u5018\u82e5\u5e2e\u7aa6\u5b89\u5e73\u5e72\u51fa\u8fd9\u79cd \u4e8b\u6765\uff0c\u53ea\u6015\u4e0d\u4f46\u662f\u53d7\u5929\u4e0b\u82f1\u96c4\u7684\u803b\u7b11\uff0c\u4e5f\u7684\u786e\u662f\u5bf9\u4e0d\u4f4f\u81ea\u5df1\u7684\u7956\u5b97\u4e86\u3002\u7ed9\u4eba\u9a82\u662f\u5356\u56fd\u6c42\u8363\u7684 \u5978\u5f92\uff0c\u6211\u8fd8\u6709\u4f55\u9762\u76ee\u7acb\u4e8e\u4eba\u4e16\uff1f\u201d\u4f46\u53e6\u4e00\u65b9\u9762\u4ed6\u53c8\u66fe\u53d7\u8fc7\u7aa6\u5b89\u5e73\u7684\u5927\u6069\uff0c\u81ea\u5df1\u66fe\u7ecf\u4eb2\u53e3\u7b54\u5e94 \u8fc7\u7aa6\u5b89\u5e73\uff0c\u8bf4\u662f\u53ef\u4ee5\u4e3a\u4ed6\u505a\u4efb\u4f55\u4e8b\u60c5\uff0c\u4ee5\u62a5\u4ed6\u7684\u5927\u6069\u3002 \u3000\u3000\u8f66\u8f6e\u6eda\u6eda\u5411\u524d\uff0c\u9ed1\u77f3\u9053\u4eba\u7684\u5fc3\u4e5f\u5728\u968f\u7740\u8f66\u8f6e\u8f6c\u52a8\uff0c\u4e00\u6b65\u4e00\u6b65\u7684\u63a5\u8fd1\u98de\u9f99\u5c71\u4e86\uff0c\u201c\u53bb\u5462\u8fd8 \u662f\u4e0d\u53bb\uff1f\u201d \u3000\u3000\u201c\u4e3a\u4e86\u987e\u5168\u6211\u548c\u7aa6\u5927\u54e5\u7684\u4ea4\u60c5\uff0c\u98de\u9f99\u5c71\u6050\u6015\u8fd8\u662f\u5e94\u8be5\u53bb\u4e00\u8d9f\u7684\u3002\u4f46\u82e5\u679c\u7aa6\u5927\u54e5\u4e0d\u542c\u6211\u7684 \u89c4\u529d\uff0c\u90a3\u53c8\u5982\u4f55\uff1f\u201d\u9ed1\u77f3\u9053\u4eba\u5e76\u4e0d\u662f\u4e00\u4e2a\u83bd\u592b\uff0c\u4ed6\u867d\u7136\u5ff5\u53ca\u516b\u62dc\u4e4b\u4ea4\uff0c\u60f3\u53bb\u529d\u544a\u7aa6\u5b89\u5e73\uff0c\u5e0c \u671b\u4ed6\u80fd\u591f\u6539\u5f26\u6613\u8f99\uff0c\u4f46\u4ed6\u4e5f\u4e0d\u80fd\u4e0d\u8003\u8651\u5230\uff0c\u585e\u5b89\u5e73\u662f\u7ecf\u8fc7\u5468\u5bc6\u7684\u5b89\u6392\uff0c\u957f\u671f\u7684\u51c6\u5907\uff0c\u624d\u4f1a\u5e72 \u51fa\u8fd9\u6869\u4e8b\u60c5\u7684\u3002\u4ed6\u6709\u53ef\u80fd\u53ea\u662f\u542c\u4e86\u81ea\u5df1\u7684\u4e00\u5e2d\u8bdd\u800c\u5c31\u6539\u53d8\u4e3b\u610f\u5417\uff1f \u3000\u3000\u201c\u7aa6\u5b89\u5e73\u79c1\u901a\u9791\u5b50\u7684\u79d8\u5bc6\uff0c\u51b3\u4e0d\u80fd\u8ba9\u5916\u4eba\u77e5\u9053\uff0c\u4ed6\u82e5\u662f\u4e0d\u542c\u6211\u7684\u89c4\u529d\uff0c\u53c8\u5c82\u80af\u653e\u8fc7\u6211 \u5462\uff1f\u7eb5\u7136\u6211\u548c\u4ed6\u6709\u516b\u62dc\u4e4b\u4ea4\uff0c\u53ea\u6015\u4ed6\u4e5f\u662f\u975e\u6740\u6211\u4e0d\u53ef\u7684\u4e86\uff01\u5982\u4eca\u5728\u4ed6\u7684\u5c71\u5be8\u91cc\uff0c\u5df2\u7ecf\u6709\u9633\u5929 \u96f7\u7684\u4f84\u5b50\u548c\u767d\u4e07\u96c4\u7b49\u4eba\uff0c\u5728\u51c6\u5907\u7740\u5e2e\u4ed6\u5bf9\u4ed8\u5b5f\u5c11\u521a\u3002\u5230\u4e86\u5176\u65f6\uff0c\u8fd9\u4e9b\u4eba\u4e5f\u5f53\u7136\u4f1a\u6765\u5bf9\u4ed8\u6211 \u4e86\u3002\u9633\u5929\u96f7\u7684\u4f84\u5b50\u672c\u9886\u5982\u4f55\uff0c\u6211\u4e0d\u77e5\u9053\u3002\u4f46\u53ea\u4ee5\u767d\u4e07\u96c4\u800c\u8bba\uff0c\u6211\u5c31\u51b3\u8ba1\u4e0d\u662f\u4ed6\u7684\u5bf9\u624b\uff01\u201d\u8ddf \u7740\u53c8\u60f3\uff1a\u201c\u6211\u6b7b\u4e0d\u8db3\u60dc\uff0c\u4ee5\u4e00\u6b7b\u6765\u62a5\u7b54\u7aa6\u652f\u5e73\u7684\u6069\u60e0\uff0c\u4e5f\u7b97\u5f97\u662f\u4e00\u7b14\u52fe\u9500\u3002\u4f46\u8fd9\u4f4d\u4e25\u59d1\u5a18\u5374 \u662f\u6211\u5c06\u5979\u9001\u5165\u4e86\u864e\u53e3\u4e86\uff01\u201d \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u8f6c\u4e86\u597d\u51e0\u6b21\u5ff5\u5934\uff0c\u7ec8\u4e8e\u7b03\u9a6d\u9aa1\u8f66\uff0c\u79bb\u5f00\u4e86\u901a\u5f80\u98de\u9f99\u5c71\u7684\u90a3\u4e00\u6761\u8def\u3002 \u3000\u3000\u9aa1\u8f66\u5728\u6811\u6797\u4e2d\u505c\u4e0b\uff0c\u4e25\u70f7\u7684\u5fc3\u4e5f\u8ddf\u7740\u4e00\u6c89\uff0c\u4e0d\u77e5\u9ed1\u77f3\u9053\u4eba\u5e26\u5979\u8fdb\u5165\u8352\u6797\uff0c\u6709\u4f55\u7528\u610f\uff1f \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u63ed\u5f00\u8f66\u5e18\uff0c\u8bf4\u9053\uff1a\u201c\u4e25\u59d1\u5a18\uff0c\u8fd9\u662f\u89e3\u836f\u3002\u846b\u82a6\u91cc\u8fd8\u6709\u534a\u846b\u82a6\u7684\u6c34\uff0c\u4f60\u7528\u6c34\u9001\u670d \u5427\u3002\u201d\u8bf4\u7f62\uff0c\u638c\u5fc3\u4e00\u644a\uff0c\u5c06\u4e24\u9897\u836f\u4e38\u653e\u5728\u4e25\u70f7\u7684\u8ddf\u524d\u3002 \u3000\u3000\u4e25\u70f7\u6014\u4e86\u4e00\u6014\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u7ed9\u6211\u89e3\u836f\uff1f\u201d \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u76f8\u4fe1\uff0c\u90a3\u5c31\u5f53\u4f5c\u662f\u6bd2\u836f\u597d\u4e86\u3002\u6211\u5e76\u4e0d\u5f3a\u8feb\u4f60\u541e\u3002\u201d \u3000\u3000\u4e25\u70f7\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6211\u7684\u6027\u547d\u672c\u6765\u5c31\u5728\u4f60\u7684\u624b\u4e2d\uff0c\u6211\u53c8\u4f55\u987b\u5bb3\u6015\u8fd9\u662f\u6bd2\u836f\u3002\u201d\u4e00\u5f20\u53e3\u4fbf\u541e \u4e0b\u4e86\u90a3\u4e24\u9897\u836f\u4e38\u3002 \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u9053\uff1a\u201c\u597d\uff0c\u96be\u5f97\u4f60\u76f8\u4fe1\u6211\u3002\u8fd9\u628a\u5251\u4e5f\u8fd8\u7ed9\u4f60\u5427\u3002\u201d\u4e25\u70f7\u7684\u5251\uff0c\u662f\u5728\u88ab\u64d2\u4e4b\u65f6\u7ed9 \u4ed6\u7f34\u53bb\u7684\u3002 \u3000\u3000\u4e25\u6d17\u541e\u4e86\u836f\u4e38\uff0c\u53ea\u89c9\u4e00\u822c\u70ed\u6c14\u4ece\u4e39\u7530\u5347\u8d77\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u53ea\u89c9\u5f97\u6c14\u8840\u7545\u901a\uff0c\u6c14\u529b\u4e5f\u6e10\u6e10\u6062 \u590d\u4e86\u3002 \u3000\u3000\u4e25\u70f7\u5530\u5730\u62d4\u51fa\u5251\u6765\uff0c\u4e00\u5251\u5411\u9ed1\u77f3\u9053\u4eba\u523a\u53bb\u3002\u9ed1\u77f3\u9053\u4eba\u53eb\u9053\uff1a\u201c\u597d\uff0c\u4f60\u8981\u62a5\u4ec7\uff0c\u90a3\u4e5f\u968f \u4f60\u3002\u201d\u4ed6\u6b63\u5728\u4e3a\u7740\u4e0d\u77e5\u5982\u4f55\u5904\u7406\u4ed6\u548c\u7aa6\u5b89\u5e73\u4e4b\u95f4\u7684\u6069\u6028\u800c\u82e6\u607c\uff0c\u662f\u4ee5\u4e25\u70f7\u4e00\u5251\u523a\u6765\uff0c\u4ed6\u5373\u4e0d \u8eb2\u907f\uff0c\u4e5f\u4e0d\u62d4\u5200\u62db\u67b6\u3002 \u3000\u3000\u53ea\u542c\u5f97\u201c\u5494\u5693\u201d\u4e00\u58f0\uff0c\u9ed1\u77f3\u9053\u4eba\u8eab\u8fb9\u7684\u4e00\u68f5\u677e\u6811\uff0c\u7ed9\u4e25\u70f7\u4e00\u5251\u65a9\u65ad\u4e86\u4e00\u682a\u7c97\u5982\u624b\u81c2\u7684\u6811 \u679d\u3002\u539f\u6765\u4e25\u70f7\u4e43\u662f\u8bd5\u8bd5\u5979\u662f\u5426\u5df2\u7ecf\u6062\u590d\u4e86\u6b66\u529f\u7684\u3002\u6b63\u662f\uff1a \u3000\u3000\u5a25\u7709\u89c1\u8bc6\u8d85\u51e1\u4fd7\uff0c\u6b7b\u91cc\u9003\u751f\u5c82\u5076\u7136\uff1f \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b\u2014\u2014\u7b2c\u4e09\u5341\u516b\u56de\u3000\u9053\u4eba\u8d60\u836f\u6c42\u5bbd\u6055\u3000\u4fa0\u58eb\u6325\u5200\u5fcd\u5fcf\u60c5 \u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b \u7b2c\u4e09\u5341\u516b\u56de\u3000\u9053\u4eba\u8d60\u836f\u6c42\u5bbd\u6055\u3000\u4fa0\u58eb\u6325\u5200\u5fcd\u5fcf\u60c5 \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u5e76\u4e0d\u6015\u6b7b\uff0c\u4f46\u5f53\u7740\u4e25\u70f7\u7684\u5251\u950b\u51e0\u4e4e\u662f\u8d34\u7740\u4ed6\u7684\u9888\u80cc\u524a\u8fc7\u4e4b\u65f6\uff0c\u4e5f\u4e0d\u7981\u7ed9\u5979\u5413\u51fa \u4e86\u4e00\u8eab\u51b7\u6c57\u3002 \u3000\u3000\u4e25\u70f7\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6211\u751f\u5e73\u4ece\u672a\u53d7\u8fc7\u4eba\u5982\u6b64\u6b3a\u8d1f\uff0c\u6309\u8bf4\u6211\u672c\u8be5\u6740\u4e86\u4f60\u65b9\u80fd\u89e3\u6211\u5fc3\u5934\u4e4b\u6068\uff0c \u5982\u4eca\u770b\u5728\u4f60\u4f3c\u4e4e\u5c1a\u6709\u5411\u5584\u4e4b\u5fc3\uff0c\u8fd9\u4e00\u5251\u6743\u4e14\u5bc4\u4e0b\uff0c\u4f46\u613f\u4f60\u597d\u597d\u505a\u4eba\u3002\u201d \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u82e6\u7b11\u9053\uff1a\u201c\u591a\u8c22\u4f60\u624b\u4e0b\u7559\u60c5\uff0c\u4f60\u5feb\u5feb\u8d70\u5427\u3002\u201d \u3000\u3000\u4e25\u70f7\u9053\uff1a\u201c\u4f60\u5462\uff1f\u4f60\u653e\u4e86\u6211\uff0c\u4f60\u600e\u4e48\u529e\uff1f\u201d\u5979\u5bf9\u9ed1\u77f3\u9053\u4eba\u7684\u6068\u610f\u867d\u672a\u5168\u6d88\uff0c\u5374\u4e5f\u6709\u4e86\u51e0 \u5206\u597d\u611f\uff0c\u662f\u4ee5\u5728\u51fa\u4e86\u4e00\u53e3\u6c14\u4e4b\u540e\uff0c\u5c31\u5fcd\u4e0d\u4f4f\u597d\u5947\u8981\u95ee\u4ed6\u4e86\u3002 \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u6211\u7684\u4e8b\u60c5\uff0c\u4f60\u5c31\u4e0d\u5fc5\u7ba1\u4e86\u3002\u201d \u3000\u3000\u4e25\u70f7\u78b0\u4e86\u4e2a\u9489\u5b50\uff0c\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u7eb3\u5251\u5165\u9798\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u624d\u6ca1\u529f\u592b\u7406\u4f60\u7684\u95f2\u4e8b\u5462\uff0c\u4f60\u597d \u81ea\u4e3a\u4e4b\u5427\u3002\u201d \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u770b\u89c1\u4e25\u70f7\u671d\u7740\u98de\u9f99\u5c71\u7684\u65b9\u5411\u8d70\u8def\uff0c\u4e0d\u89c9\u6014\u4e86\u4e00\u6014\uff0c\u53eb\u9053\uff1a\u201c\u4e25\u59d1\u5a18\uff0c\u4f60\u8d70\u9519\u65b9 \u5411\u4e86\uff0c\u5e94\u8be5\u4ece\u90a3\u4e00\u9762\u4e0b\u5c71\u7684\u3002\u201d \u3000\u3000\u4e25\u70f7\u5934\u4e5f\u4e0d\u56de\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u504f\u8981\u8d70\u8fd9\u4e2a\u65b9\u5411\uff01\u201d \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u9053\uff1a\u201c\u4e25\u59d1\u5a18\uff0c\u4f60\u5230\u5e95\u8981\u5f80\u54ea\u513f\uff1f\u201d \u3000\u3000\u4e25\u70f7\u9053\uff1a\u201c\u6211\u5f80\u54ea\u513f\uff0c\u4f60\u7ba1\u5f97\u7740\u4e48\uff1f\u201d \u3000\u3000\u539f\u6765\u4e25\u70f7\u662f\u60f3\u53bb\u4f1a\u5979\u7684\u8205\u8205\u5b5f\u5c11\u521a\u3002\u5979\u6697\u81ea\u601d\u91cf\uff1a\u201c\u73b0\u5728\u6211\u7684\u6b66\u529f\u5df2\u7ecf\u6062\u590d\uff0c\u8fd8\u6015\u4ec0 \u4e48\uff1f\u542c\u521a\u624d\u90a3\u8d3c\u6c49\u5b50\u6240\u8bf4\uff0c\u8205\u8205\u660e\u5929\u5c31\u53ef\u80fd\u5230\u98de\u9f99\u5c71\u4e86\uff0c\u6211\u4f55\u4e0d\u4e5f\u5230\u98de\u9f99\u5c71\u53bb\u4e0e\u4ed6\u76f8\u4f1a\uff1f\u201d \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u731c\u5230\u4e86\u5979\u7684\u5fc3\u601d\uff0c\u8bf4\u9053\uff1a\u201c\u4e25\u59d1\u5a18\uff0c\u4e0d\u662f\u6211\u8981\u7ba1\u4f60\uff0c\u98de\u9f99\u5c71\u4f60\u53bb\u4e0d\u5f97\uff01\u201d \u3000\u3000\u4e25\u70f7\u51b7\u7b11\u9053\uff1a\u201c\u6211\u559c\u6b22\u4e0a\u54ea\u513f\u4fbf\u4e0a\u54ea\u513f\uff0c\u7528\u4e0d\u7740\u4f60\u66ff\u6211\u62c5\u5fc3\uff01\u201d \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u9887\u6709\u51e0\u5206\u6c14\u607c\uff0c\u8981\u60f3\u4e0d\u7406\uff0c\u4f46\u8f6c\u5ff5\u4e00\u60f3\uff1a\u201c\u6211\u4f55\u5fc5\u548c\u9ec4\u6bdb\u4e2b\u5934\u4e00\u822c\u89c1\u8bc6\uff1f\u672c\u6765 \u662f\u6211\u5bf9\u5979\u4e0d\u4f4f\uff0c\u4e5f\u602a\u4e0d\u5f97\u5979\u607c\u6211\u3002\u201d\u5f53\u4e0b\u6025\u5fd9\u8d76\u4e0a\uff0c\u53eb\u9053\uff1a\u201c\u4e25\u59d1\u5a18\u4e14\u6162\uff0c\u8bf7\u4f60\u542c\u6211\u4e00 \u8a00\u3002\u201d\u4e25\u70f7\u563f\u563f\u51b7\u7b11\uff0c\u5f84\u81ea\u524d\u884c\u3002\u5979\u7684\u8f7b\u529f\u672c\u6765\u662f\u4e0d\u5728\u9ed1\u77f3\u9053\u4eba\u4e4b\u4e0b\uff0c\u4f46\u56e0\u529f\u529b\u521d\u590d\uff0c\u4e0d\u591a \u4e00\u4f1a\uff0c\u5374\u7ed9\u9ed1\u77f3\u9053\u4eba\u8ffd\u4e0a\u4e86\u3002 \u3000\u3000\u4e14\u8bf4\u8c37\u6db5\u865a\u8ddf\u7740\u9aa1\u8f66\u8f68\u8ff9\uff0c\u4e00\u8def\u8ffd\u8e2a\uff0c\u5230\u4e86\u6797\u8fb9\u9690\u9690\u542c\u89c1\u4e25\u70f7\u559d\u9a82\u7684\u58f0\u97f3\uff0c\u53c8\u559c\u53c8\u60ca\uff0c \u5927\u96e8\u8fc7\u540e\uff0c\u5c71\u8def\u602a\u77f3\u5ccb\u78f7\uff0c\u9752\u82d4\u6e7f\u6ed1\uff0c\u9a6c\u4e5f\u96be\u884c\uff0c\u8c37\u6db5\u865a\u8feb\u4e0d\u53ca\u5f85\uff0c\u98de\u8eab\u4e0b\u9a6c\uff0c\u7acb\u5373\u65bd\u5c55\u8f7b \u529f\uff0c\u671d\u7740\u58f0\u97f3\u7684\u6765\u5904\u8dd1\u53bb\u3002 \u3000\u3000\u4e25\u70f7\u773c\u770b\u5c31\u8981\u7ed9\u9ed1\u77f3\u9053\u4eba\u8ffd\u4e0a\uff0c\u65a5\u9053\uff1a\u201c\u4f60\u5e72\u4ec0\u4e48\uff1f\u201d\u9ed1\u77f3\u9053\u4eba\u6b63\u8981\u5411\u5979\u529d\u758f\uff0c\u5ffd\u89c1\u4e00 \u4e2a\u5947\u4e11\u65e0\u6bd4\u7684\u6c49\u5b50\u7a81\u7136\u51fa\u73b0\uff01 \u3000\u3000\u8c37\u6db5\u865a\u672c\u6765\u662f\u4e2a\u9762\u5982\u51a0\u7389\u7684\u7f8e\u5c11\u5e74\uff0c\u5f53\u4ed6\u548c\u4e25\u70f7\u5206\u624b\u4e4b\u65f6\uff0c\u8138\u4e0a\u521a\u521a\u7ed9\u5f20\u5143\u5409\u7684\u5229\u5251\u5212 \u4f24\uff0c\u5f53\u65f6\u867d\u7136\u662f\u8840\u6d41\u6ee1\u9762\uff0c\u4f46\u56e0\u75ae\u75a4\u672a\u7ed3\uff0c\u4ecd\u662f\u672c\u6765\u9762\u76ee\u3002\u662f\u4ee5\u5728\u4e25\u70f7\u7684\u8bb0\u5fc6\u4e4b\u4e2d\uff0c\u5979\u7684\u610f \u4e2d\u4eba\u59cb\u7ec8\u90fd\u662f\u90a3\u4e2a\u82f1\u4fca\u7684\u8c37\u6db5\u865a\uff0c\u51b3\u60f3\u4e0d\u5230\u8c37\u6db5\u865a\u5982\u4eca\u5df1\u662f\u53d8\u6210\u4e86\u8fd9\u4e2a\u6a21\u6837\u3002 \u3000\u3000\u4e00\u4e2a\u5947\u4e11\u65e0\u6bd4\u7684\u6c49\u5b50\u7a81\u7136\u5728\u5979\u9762\u524d\u51fa\u73b0\uff0c\u4e25\u70f7\u4e0d\u7981\u5403\u4e86\u4e00\u60ca\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u201d\u82cd\u82d4 \u8def\u6ed1\uff0c\u4e25\u70f7\u8dd1\u5f97\u6b63\u6025\uff0c\u9aa4\u7136\u5403\u60ca\uff0c\u51e0\u4e4e\u6ed1\u5012\u3002\u8c37\u6db5\u865a\u5fc3\u75db\u5982\u7ede\uff1a\u201c\u70f7\u59b9\u679c\u7136\u662f\u8ba4\u4e0d\u5f97\u6211\u4e86\u3002 \u4e5f\u7f62\uff0c\u6211\u7ed9\u5979\u628a\u8fd9\u81ed\u9053\u58eb\u6740\u4e86\u4fbf\u8d70\uff0c\u4e5f\u4e0d\u5fc5\u548c\u5979\u8bf4\u6211\u662f\u8c01\u4e86\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u6276\u8d77\u4e25\u70f7\uff0c\u4e25\u70f7\u66f4\u662f\u5403\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u5e72\u4ec0\u4e48\uff1f\u201d\u8bf4\u65f6\u8fdf\u90a3\u65f6\u5feb\uff0c\u9ed1\u77f3\u9053\u4eba\u5df2\u7136 \u8d76\u5230\uff0c\u5927\u6012\u559d\u9053\uff1a\u201c\u7aa6\u5b89\u5e73\u4e5f\u5f97\u7ed9\u6211\u51e0\u5206\u9762\u5b50\uff0c\u4f60\u662f\u4ec0\u4e48\u4e1c\u897f\uff0c\u4e0d\u5f97\u6211\u7684\u70b9\u5934\uff0c\u5c31\u6562\u52a8\u624b\u62a2 \u6211\u9001\u6765\u7684\u4e25\u59d1\u5a18\uff1f\u201d\u539f\u6765\u4ed6\u4ee5\u4e3a\u8c37\u6db5\u865a\u662f\u7aa6\u5b89\u5e73\u6d3e\u6765\u7684\u4eba\uff0c\u7aa6\u5b89\u5e73\u4fe1\u4e0d\u8fc7\u4ed6\uff0c\u53eb\u8fd9\u4e2a\u4eba\u6765\u63a5 \u6536\u4e25\u70f7\u7684\u3002 \u3000\u3000\u4e25\u70f7\u6323\u8131\u4e86\u8c37\u6db5\u865a\u7684\u638c\u63e1\uff0c\u89c9\u5f97\u8fd9\u4eba\u5bf9\u5979\u4f3c\u4e4e\u5e76\u65e0\u6076\u610f\uff0c\u800c\u4e14\u8fd8\u4f3c\u4e4e\u662f\u4e00\u4e2a\u5979\u4f3c\u66fe\u76f8\u8bc6 \u7684\u4eba\uff0c\u4e0d\u7531\u5f97\u4e00\u9635\u8ff7\u832b\uff0c\u7ad9\u5728\u4e00\u65c1\uff0c\u5446\u4e86\uff01\u8981\u77e5\u4ed6\u4eec\u7a76\u7adf\u662f\u4e00\u5bf9\u5fc3\u5fc3\u76f8\u5370\u7684\u604b\u4eba\uff0c\u8c37\u6db5\u865a\u7684 \u9762\u8c8c\u867d\u7136\u53d8\u4e86\uff0c\u4f46\u4ed6\u7684\u773c\u5e95\u67d4\u60c5\uff0c\u7709\u95f4\u7231\u610f\uff0c\u548c\u4ed6\u7684\u4e00\u4e9b\u4e60\u60ef\u6027\u7684\u52a8\u4f5c\uff0c\u8fd8\u662f\u53ef\u4ee5\u4ee4\u4e25\u70f7\u611f \u5230\u719f\u6089\u7684\u554a\uff01 \u3000\u3000\u8c37\u6db5\u865a\u8f7b\u8f7b\u63a8\u5f00\u4e86\u4e25\u70f7\uff0c\u84e6\u5730\u53cc\u773c\u4e00\u77aa\uff0c\u634f\u7740\u55d3\u5b50\u559d\u9053\uff1a\u201c\u6211\u662f\u52fe\u9b42\u4f7f\u8005\uff0c\u50ac\u547d\u65e0\u5e38\uff01 \u81ed\u9053\u571f\uff0c\u9886\u6b7b\u5427\uff01\u201d \u3000\u3000\u638c\u4f3c\u5954\u96f7\uff0c\u5200\u5982\u9a87\u7535\u3002\u53cc\u65b9\u90fd\u662f\u5728\u5927\u6012\u4e4b\u4e0b\uff0c\u540c\u65f6\u51fa\u624b\u3002\u9ed1\u77f3\u9053\u4eba\u7684\u5feb\u5200\u672c\u662f\u9ed1\u9053\u4e00 \u7edd\uff0c\u4f46\u8c37\u6db5\u865a\u7684\u5929\u96f7\u638c\u529b\u4f55\u7b49\u5389\u5bb3\uff0c\u5feb\u5200\u672a\u66fe\u65ab\u5230\u4ed6\u7684\u8eab\u4e0a\uff0c\u4ed6\u7684\u638c\u529b\u5df2\u662f\u72c2\u6d9b\u822c\u5730\u6d8c\u6765\uff0c \u638c\u98ce\u5200\u5f71\u4e4b\u4e2d\uff0c\u53ea\u542c\u5f97\u201c\u7830\u201d\u7684\u4e00\u58f0\uff0c\u9ed1\u77f3\u9053\u4eba\u7ed9\u4ed6\u7684\u638c\u529b\u9707\u5f97\u8fde\u9000\u51e0\u6b65\uff0c\u521a\u597d\u649e\u7740\u4e86\u4e00\u68f5 \u677e\u6811\u3002 \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u4e5f\u975e\u6cdb\u6cdb\u4e4b\u8f88\uff0c\u649e\u7740\u677e\u6811\uff0c\u767b\u65f6\u5c31\u4f3c\u76ae\u7403\u822c\u7684\u5f39\u8d77\u6765\uff0c\u6325\u5200\u53c8\u5411\u8c37\u6db5\u865a\u65ab\u53bb\u3002 \u3000\u3000\u539f\u6765\u8c37\u6db5\u865a\u8fde\u65e5\u5954\u9a70\uff0c\u679c\u7136\u662f\u4e0d\u51fa\u795d\u8001\u5927\u7684\u6240\u6599\uff0c\u4f53\u529b\u9887\u53d7\u5f71\u54cd\uff0c\u56e0\u800c\u5929\u96f7\u529f\u7684\u5a01\u529b\u4e5f \u5c31\u76f8\u5e94\u6253\u4e86\u6298\u6263\u3002\u4e5f\u5e78\u4e8f\u5982\u6b64\uff0c\u9ed1\u77f3\u9053\u4eba\u624d\u6ca1\u6709\u53d7\u4f24\u3002 \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u5403\u4e86\u4e00\u6b21\u4e8f\uff0c\u4e0d\u6562\u6b63\u9762\u518d\u63a5\u8c37\u6db5\u865a\u7684\u638c\u529b\uff0c\u5f53\u4e0b\u8eab\u5f62\u6e38\u8d70\uff0c\u6307\u4e1c\u6253\u897f\uff0c\u6307\u5357\u6253 \u5317\uff0c\u4ee5\u95ea\u7535\u822c\u7684\u5feb\u5200\uff0c\u731b\u88ad\u5bf9\u65b9\uff0c\u8981\u4ee4\u8c37\u6db5\u865a\u817e\u4e0d\u51fa\u624b\u6765\u3002 \u3000\u3000\u8c37\u6db5\u865a\u5fc3\u9053\uff1a\u201c\u8fd9\u81ed\u9053\u58eb\u679c\u7136\u662f\u6709\u4e24\u4e0b\u5b50\uff0c\u602a\u4e0d\u5f97\u70f7\u59b9\u53d7\u4ed6\u6240\u64d2\u3002\u201d\u5200\u5149\u638c\u5f71\u4e4b\u4e2d\uff0c\u5ffd \u542c\u5f97\u58f0\u5982\u88c2\u5e1b\uff0c\u8c37\u6db5\u865a\u7684\u8863\u8896\u7ed9\u5feb\u5200\u524a\u53bb\u4e86\u4e00\u622a\u3002\u4e25\u70f7\u5413\u5f97\u5c16\u53eb\u8d77\u6765\uff0c\u6b63\u8981\u4e0a\u524d\uff0c\u5374\u89c1\u9ed1\u77f3 \u9053\u4eba\u53c8\u5728\u8fde\u8fde\u540e\u9000\uff0c\u8c37\u6db5\u865a\u624b\u4e2d\u591a\u4e86\u4e00\u628a\u957f\u5251\u3002 \u3000\u3000\u539f\u6765\u8c37\u6db5\u865a\u4e43\u662f\u7528\u4e0a\u4e58\u67d4\u529f\u6325\u8896\u62c2\u5200\uff0c\u8fd9\u624d\u597d\u8d81\u52bf\u817e\u51fa\u624b\u6765\u62d4\u5251\u7684\u3002\u8fd9\u4e00\u62c2\u67d4\u4e2d\u5bd3\u521a\uff0c \u5b9e\u5df2\u8574\u85cf\u7740\u51e0\u5206\u5929\u96f7\u529f\u7684\u5a01\u529b\u3002\u5018\u82e5\u6362\u4e86\u4e00\u4e2a\u6b66\u529f\u7a0d\u5f31\u7684\u4eba\uff0c\u624b\u4e2d\u7684\u5175\u5668\u65e9\u5df2\u7ed9\u4ed6\u7684\u8863\u8896\u5377 \u53bb\u4e86\u3002 \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u7684\u5feb\u5200\u5c45\u7136\u80fd\u524a\u6389\u4ed6\u4e00\u622a\u8863\u8896\uff0c\u8c37\u6db5\u865a\u81ea\u662f\u4e0d\u6562\u8f7b\u654c\uff0c\u5251\u4e00\u51fa\u9798\uff0c\u7acb\u5373\u62a2\u653b\uff0c \u6bd4\u9ed1\u77f3\u9053\u4eba\u7684\u5feb\u5200\u4f7f\u5f97\u66f4\u5feb\uff0c\u9ed1\u77f3\u9053\u4eba\u906e\u62e6\u4e0d\u4f4f\uff0c\u53ea\u597d\u8fde\u8fde\u540e\u9000\u3002\u5728\u4e25\u70f7\u5931\u58f0\u60ca\u547c\u4e4b\u9645\uff0c\u53cc \u65b9\u7684\u5200\u5251\u5df2\u7ecf\u662f\u4ea4\u6362\u4e86\u5341\u6570\u62db\u4e86\u3002 \u3000\u3000\u4e25\u70f7\u8fd9\u4e00\u58f0\u60ca\u547c\u5df2\u662f\u628a\u5979\u5173\u5207\u8c37\u6db5\u865a\u7684\u5fc3\u60c5\u8868\u9732\u65e0\u9057\uff0c\u8c37\u6db5\u865a\u542c\u8fdb\u8033\u6735\uff0c\u5fc3\u5934\u7981\u4e0d\u4f4f \u201c\u535c\u901a\u201d\u4e00\u8df3\uff0c\u201c\u70f7\u59b9\u6562\u60c5\u662f\u5df2\u7ecf\u8ba4\u51fa\u6211\u4e86\uff0c\u539f\u6765\u5979\u8fd8\u662f\u5173\u5fc3\u6211\u7684\u3002\u201d\u767b\u65f6\u7cbe\u795e\u9661\u632f\u3002 \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u672c\u6765\u5c31\u4e0d\u662f\u8c37\u6db5\u865a\u7684\u5bf9\u624b\uff0c\u8c37\u6db5\u865a\u7cbe\u795e\u5927\u632f\uff0c\u5251\u62db\u4f7f\u5f97\u8d8a\u53d1\u51cc\u5389\u65e0\u6bd4\uff0c\u9ed1\u77f3\u9053 \u4eba\u62b5\u654c\u4e0d\u4f4f\uff01 \u3000\u3000\u4e25\u70f7\u5b9a\u4e86\u5b9a\u795e\uff0c\u53ea\u89c1\u8c37\u6db5\u865a\u5df2\u662f\u628a\u9ed1\u77f3\u9053\u4eba\u903c\u5230\u4e00\u5904\u60ac\u5d16\uff0c\u8c37\u6db5\u865a\u5530\u5530\u5530\u8fde\u73af\u4e09\u5251\uff0c\u773c \u770b\u9ed1\u77f3\u9053\u4eba\u82e5\u4e0d\u662f\u7ed9\u4ed6\u6324\u4e0b\u60ac\u5ca9\u8eab\u4e0a\u5c31\u975e\u4e2d\u5251\u4e0d\u53ef\u3002 \u3000\u3000\u4e25\u70f7\u4e00\u8dc3\u800c\u51fa\uff0c\u53eb\u9053\uff1a\u201c\u8c37\u5927\u54e5\u5251\u4e0b\u7559\u60c5\uff01\u201d\u8c37\u6db5\u865a\u7684\u5251\u5c16\u4e1a\u5df2\u6307\u5230\u9ed1\u77f3\u9053\u4eba\u7684\u54bd\u5589\uff0c \u542c\u89c1\u4e25\u70f7\u66ff\u9ed1\u77f3\u9053\u4eba\u6c42\u60c5\uff0c\u4e0d\u7981\u6014\u4e86\u4e00\u6014\uff0c\u4f46\u4ed6\u4e5f\u5e76\u6ca1\u6709\u95ee\u4ec0\u4e48\u7f18\u6545\uff0c\u968f\u5373\u628a\u5251\u950b\u4e00\u8f6c\uff0c\u6539 \u523a\u4e3a\u70b9\uff0c\u5251\u5c16\u8f7b\u8f7b\u70b9\u7740\u9ed1\u77f3\u9053\u4eba\u7684\u624b\u8155\uff0c\u94db\u5577\u4e00\u58f0\uff0c\u9ed1\u77f3\u9053\u4eba\u7684\u5feb\u5200\u5760\u5730\u3002 \u3000\u3000\u4e25\u70f7\u8dd1\u4e0a\u524d\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u8c37\u5927\u54e5\uff0c\u679c\u7136\u662f\u4f60\uff01\u4f60\u8ba9\u6211\u4ed4\u7ec6\u77a7\u77a7\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u82e6\u7b11\u9053\uff1a\u201c\u6211\u5df2\u7ecf\u53d8\u6210\u4e86\u4e11\u516b\u602a\u5566\uff0c\u6211\u53ea\u9053\u4f60\u8ba4\u4e0d\u5f97\u6211\u4e86\u3002\u201d \u3000\u3000\u4e25\u70f7\u67f3\u7709\u4e00\u626c\uff0c\u8bf4\u9053\uff1a\u201c\u5927\u54e5\uff0c\u6211\u4e0d\u7ba1\u4f60\u662f\u4fca\u662f\u4e11\uff0c\u6211\u603b\u662f\u4e00\u6837\u7684\u559c\u6b22\u4f60\uff01\u5927\u54e5\uff0c\u4f60\u4e0d \u95ee\u6211\u4e3a\u4ec0\u4e48\u8981\u4f60\u9976\u8fd9\u9053\u4eba\u5417\uff1f\u56e0\u4e3a\u8fd9\u4eba\u8fd8\u4e0d\u7b97\u592a\u574f\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u70f7\u59b9\uff0c\u6211\u77e5\u9053\u4f60\u4e00\u5b9a\u6709\u4f60\u7684\u7406\u7531\u7684\u3002\u6211\u51e0\u65f6\u66fe\u8bd5\u8fc7\u4e0d\u542c\u4f60\u7684\u8bdd\u5462\uff1f\u53c8\u4f55\u987b \u518d\u95ee\uff01\u201d \u3000\u3000\u4e25\u70f7\u9053\uff1a\u201c\u5927\u54e5\uff0c\u539f\u6765\u4f60\u4e5f\u8fd8\u662f\u50cf\u4ece\u524d\u4e00\u6837\u7684\u76f8\u4fe1\u6211\uff0c\u559c\u6b22\u6211\uff01\u201d\u559c\u6781\u800c\u6ce3\uff0c\u6295\u5165\u8c37\u6db5 \u865a\u7684\u6000\u4e2d\uff0c\u4e24\u4eba\u7d27\u7d27\u76f8\u62e5\u3002\u8c37\u6db5\u865a\u7684\u773c\u4e2d\u53ea\u6709\u4e00\u4e2a\u4e25\u70f7\uff0c\u4e25\u70f7\u7684\u773c\u4e2d\u4e5f\u53ea\u6709\u4e00\u4e2a\u8c37\u6db5\u865a\uff0c\u5bf9 \u5468\u56f4\u7684\u4e00\u5207\uff0c\u5df2\u662f\u89c6\u800c\u4e0d\u89c1\uff0c\u542c\u800c\u4e0d\u95fb\uff0c\u6839\u672c\u5c31\u5fd8\u8bb0\u4e86\u8fd8\u6709\u4e00\u4e2a\u9ed1\u77f3\u9053\u4eba\u5728\u4ed6\u4eec\u7684\u8eab\u8fb9\u3002 \u3000\u3000\u8fd9\u4e00\u53d8\u5316\u5927\u5927\u51fa\u4e4e\u9ed1\u77f3\u9053\u4eba\u610f\u6599\u4e4b\u5916\u3002\u9ed1\u77f3\u9053\u4eba\u62fe\u8d77\u957f\u5200\uff0c\u5c34\u5c2c\u4e4b\u6781\uff0c\u8fd9\u624d\u77e5\u9053\u539f\u6765\u8fd9 \u4e11\u6c49\u5b50\u4e43\u662f\u4e25\u70f7\u7684\u60c5\u4eba\u3002\u5f53\u4e0b\u4e00\u58f0\u82e6\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4e25\u59d1\u5a18\uff0c\u591a\u8c22\u4f60\u5fc3\u80f8\u5bbd\u5927\uff0c\u4e0d\u8bb0\u6211\u7684\u4ec7 \u6068\u3002\u4f60\u5df2\u7ecf\u78b0\u4e0a\u4eb2\u4eba\uff0c\u7528\u4e0d\u7740\u6211\u66ff\u4f60\u62c5\u5fc3\u4e86\uff0c\u6211\u8d70\u5566\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u65b9\u59cb\u5bdf\u89c9\u9ed1\u77f3\u9053\u4eba\u5c1a\u5728\u8eab\u65c1\uff0c\u5f53\u4e0b\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u6253\u4e0d\u6210\u76f8\u8bc6\uff0c\u4f55\u5fc5\u8fd9\u6837 \u5306\u5fd9\u5c31\u8d70\uff1f\u6211\u521a\u624d\u6ca1\u6709\u95ee\u4e2a\u6e05\u695a\u5c31\u548c\u4f60\u52a8\u624b\uff0c\u60f3\u5fc5\u662f\u5176\u4e2d\u5b9a\u6709\u8bef\u4f1a\u4e86\u3002\u6211\u5012\u5e94\u8be5\u5148\u5411\u4f60\u8d3b\u4e2a \u4e0d\u662f\u5462\u3002\u201d\u4ed6\u56e0\u4e3a\u542c\u5f97\u4e25\u70f7\u8bf4\u9ed1\u77f3\u9053\u4eba\u5e76\u975e\u574f\u4eba\uff0c\u6240\u4ee5\u624d\u4f1a\u8fd9\u6837\u8bf4\u7684\u3002 \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u6ee1\u9762\u901a\u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4e00\u70b9\u4e5f\u6ca1\u6709\u9519\uff0c\u9519\u7684\u662f\u6211\u3002\u6211\u4e0d\u8be5\u6b3a\u8d1f\u4f60\u7684\u4e25\u59d1\u5a18\uff0c \u6211\u3001\u6211\u5b9e\u5728\u662f\u6b7b\u6709\u4f59\u8f9c\uff01\u201d \u3000\u3000\u4e25\u70f7\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4ed6\u7528\u8ff7\u9999\u6349\u4e86\u6211\uff0c\u662f\u60f3\u628a\u6211\u732e\u7ed9\u98de\u9f99\u5c71\u7684\u7aa6\u5be8\u4e3b\u3002\u4f46\u5982\u4eca\u4e1a\u5df2\u540e\u6094 \u4e86\u3002\u4f60\u6765\u7684\u65f6\u5019\uff0c\u4ed6\u6b63\u5728\u628a\u89e3\u836f\u7ed9\u6211\uff0c\u653e\u6211\u8d70\u5462\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u6014\u4e86\u4e00\u6014\uff0c\u5fc3\u9053\uff1a\u201c\u539f\u6765\u795d\u8001\u4e09\u5012\u4e5f\u6ca1\u6709\u9a97\u6211\u3002\u201d\u6014\u4e86\u4e00\u6014\uff0c\u968f\u5373\u54c8\u54c8\u7b11\u9053\uff1a \u201c\u4eba\u8c01\u65e0\u8fc7\uff0c\u77e5\u8fc7\u80fd\u6539\uff0c\u5584\u83ab\u5927\u7109\u3002\u8fd9\u70b9\u6881\u5b50\uff0c\u5c31\u6b64\u63ed\u5f00\u3002\u9053\u957f\u82e5\u4e0d\u5acc\u5f03\u54b1\u4eec\u8fd8\u662f\u53ef\u4ee5\u4ea4\u4e2a \u670b\u53cb\u3002\u201d \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u89c1\u8c37\u6db5\u865a\u5982\u6b64\u8c6a\u723d\uff0c\u5fc3\u4e2d\u4e5f\u662f\u4e0d\u7531\u5f97\u4e0d\u6697\u6697\u4f69\u670d\u3002\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u4fa0 \u58eb\u770b\u5f97\u8d77\u6211\uff0c\u4f46\u6211\u5374\u662f\u4e0d\u80fd\u539f\u8c05\u81ea\u5df1\u5462\u3002\u6068\u53ea\u6068\u6211\u5f53\u521d\u4e0d\u8be5\u53d7\u4e86\u7aa6\u5b89\u5e73\u7684\u6069\u60e0\uff0c\u4ee5\u81f4\u4eca\u65e5\u4e0d \u80fd\u4e0d\u53d7\u4ed6\u7684\u6446\u5e03\u3002\u201d\u5f53\u4e0b\u5c06\u7aa6\u5b89\u5e73\u5982\u4f55\u7ed9\u4ed6\u6392\u96be\u89e3\u7eb7\u4e4b\u4e8b\uff0c\u4ee5\u53ca\u540e\u6765\u7aa6\u5b89\u5e73\u53c8\u5982\u4f55\u8981\u6349\u62ff\u4e25 \u70f7\u4e4b\u4e8b\uff0c\u4e00\u4e94\u4e00\u5341\uff0c\u90fd\u5bf9\u8c37\u6db5\u865a\u8bf4\u4e86\u3002 \u3000\u3000\u63a5\u7740\u9ed1\u77f3\u9053\u4eba\u8bf4\u9053\uff1a\u201c\u6211\u672c\u6765\u4e0d\u77e5\u9053\u5176\u4e2d\u539f\u59d4\u7684\uff0c\u521a\u624d\u78b0\u5230\u7aa6\u5b89\u5e73\u6d3e\u6765\u63a5\u6211\u7684\u4e00\u4e2a\u4eba\uff0c \u65b9\u624d\u77e5\u9053\u7aa6\u5b89\u5e73\u662f\u8981\u7528\u4e25\u59d1\u5a18\u6765\u5bf9\u4ed8\u5979\u7684\u8205\u7236\u6c5f\u5357\u5927\u4fa0\u5b5f\u5c11\u521a\uff0c\u800c\u8fd9\u4ef6\u4e8b\u60c5\u53c8\u7275\u6d89\u5230\u3001\u7275\u6d89 \u5230\u2014\u2014\u201d\u8bf4\u81f3\u6b64\u5904\uff0c\u5ffd\u5730\u60f3\u8d77\u7aa6\u5b89\u5e73\u6bd5\u7adf\u662f\u81ea\u5df1\u516b\u62dc\u4e4b\u4ea4\uff0c\u4f3c\u4e4e\u4e0d\u8be5\u5411\u4e00\u4e2a\u521d\u76f8\u8bc6\u7684\u670b\u53cb\u6cc4 \u6f0f\u4ed6\u7684\u79d8\u5bc6\u3002 \u3000\u3000\u8c37\u6db5\u865a\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u4ef6\u4e8b\u60c5\u7275\u6d89\u5230\u7aa6\u5b89\u5e73\u7684\u901a\u756a\u5356\u56fd\uff0c\u786e\u662f\u975e\u540c\u5c0f\u53ef\uff01\u201d \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u539f\u6765\u4f60\u90fd\u65e9\u5df2\u77e5\u9053\u4e86\uff1f\u201d \u3000\u3000\u8c37\u6db5\u865a\u70b9\u4e86\u70b9\u5934\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u73b0\u5728\u8fd8\u5728\u5ff5\u7740\u7aa6\u5b89\u5e73\u7ed9\u4f60\u7684\u6069\u60e0\u4e48\uff1f\u201d \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u5341\u5206\u82e6\u607c\uff0c\u6293\u6293\u5934\u76ae\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u73b0\u5728\u6b63\u662f\u4e0d\u77e5\u600e\u4e48\u624d\u597d\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u8bf4\u9053\uff1a\u201c\u5927\u4e08\u592b\u7acb\u8eab\u5904\u4e16\uff0c\u56fa\u7136\u5e94\u5f53\u6069\u6028\u5206\u660e\uff0c\u4f46\u66f4\u5e94\u8be5\u660e\u8fa8\u662f\u975e\uff0c\u62e9\u5584\u5f03\u6076\uff01 \u901a\u756a\u5356\u56fd\u4e4b\u8f88\uff0c\u79bd\u517d\u4e0d\u5982\uff0c\u5c82\u53ef\u4e3a\u4e86\u5c0f\u6069\u5c0f\u60e0\uff0c\u8fde\u4eba\u4e5f\u4e0d\u505a\u4e86\uff01\u201d\u8fd9\u51e0\u53e5\u8bdd\u8bf4\u5f97\u65a9\u9489\u622a\u94c1\uff0c \u9ed1\u77f3\u9053\u4eba\u542c\u4e86\uff0c\u609a\u7136\u52a8\u5bb9\uff0c\u5f53\u4e0b\u5411\u8c37\u6db5\u865a\u6df1\u6df1\u4e00\u63d6\uff0c\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u4f60\u7684\u91d1\u7389\u826f\u8a00\uff0c\u8d2b\u9053\u5b9e\u5728 \u60ed\u6127\uff01\u201d \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u4e00\u4e2a\u4eba\u53ea\u8981\u80fd\u591f\u8ff7\u9014\u77e5\u8fd4\uff0c\u5c31\u662f\u597d\u4eba\u3002\u9053\u957f\uff0c\u6211\u662f\u4e2a\u5fc3\u76f4\u53e3\u5feb\u7684\u4eba\uff0c\u6211\u8981 \u548c\u4f60\u8bf4\u7684\u8bdd\u90fd\u5df1\u8bf4\u4e86\u3002\u5982\u4eca\u6211\u5012\u60f3\u5411\u9053\u957f\u8bf7\u6559\u4e86\u3002\u201d \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u9053\uff1a\u201c\u4e0d\u77e5\u4fa0\u58eb\u6b32\u77e5\u4f55\u4e8b\uff1f\u201d\u8c37\u6db5\u865a\u9053\uff1a\u201c\u4fa0\u58eb\u4e8c\u5b57\u6127\u4e0d\u6562\u5f53\u3002\u6211\u540d\u53eb\u8c37\u6db5 \u865a\uff0c\u82e5\u8499\u9053\u957f\u4e0d\u5f03\uff0c\u54b1\u4eec\u5c31\u4ee5\u5e73\u8f88\u8bba\u4ea4\u5982\u4f55\uff1f\u201d\u9ed1\u77f3\u9053\u4eba\u9053\uff1a\u201c\u597d\uff0c\u8c37\u5144\u8bf7\u95ee\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u521a\u624d\u4f60\u8bf4\u4e3a\u4e25\u59d1\u5a18\u62c5\u5fc3\uff0c\u4e0d\u77e5\u662f\u62c5\u5fc3\u4f55\u4e8b\uff1f\u201d\u9ed1\u77f3\u9053\u4eba\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8fd9\u4ef6 \u4e8b\u4e48\uff0c\u5982\u4eca\u5df2\u662f\u7528\u4e0d\u7740\u6211\u62c5\u5fc3\u4e86\u3002\u201d \u3000\u3000\u4e25\u70f7\u604d\u7136\u5927\u609f\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u660e\u767d\u4e86\uff0c\u4f60\u521a\u624d\u662f\u4e0d\u662f\u8981\u62e6\u963b\u6211\u53bb\u98de\u9f99\u5c71\uff1f\u201d \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u9053\uff1a\u201c\u8c37\u5144\u672c\u9886\u80dc\u6211\u5341\u500d\uff0c\u6709\u4ed6\u966a\u4f60\u524d\u5f80\uff0c\u8fd9\u98de\u9f99\u5c71\u81ea\u662f\u53ef\u4ee5\u53bb\u5f97\u7684\u4e86\u3002\u4e0d \u8fc7\uff0c\u6211\u8fd8\u662f\u5e0c\u671b\u4f60\u4eec\u591a\u52a0\u4e00\u4e9b\u5c0f\u5fc3\uff0c\u6bd4\u8f83\u597d\u4e9b\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u4e0d\u77e5\u98de\u9f99\u5c71\u4e0a\u6709\u4e9b\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u9053\uff1a\u201c\u7aa6\u5b89\u5e73\u9080\u6765\u7684\u5e2e\u624b\u5f88\u662f\u4e0d\u5c11\u3002\u636e\u6211\u6240\u77e5\uff0c\u5176\u4e2d\u6709\u4e24\u4e2a\u4eba\u6050\u6015\u662f\u6bd4\u8f83\u96be\u4ee5 \u5bf9\u4ed8\u7684\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u54ea\u4e24\u4e2a\u4eba\uff1f\u201d \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u8bf4\u9053\uff1a\u201c\u4e00\u4e2a\u662f\u9633\u5929\u96f7\u7684\u4f84\u5b50\u9633\u575a\u767d\u3002\u8fd9\u4eba\u7684\u672c\u9886\u6211\u867d\u7136\u6ca1\u6709\u89c1\u8fc7\uff0c\u4f46\u9633\u5929\u96f7 \u53f7\u79f0\u91d1\u56fd\u7b2c\u4e00\u9ad8\u624b\uff0c\u4ed6\u4f84\u5b50\u7684\u672c\u9886\u60f3\u5fc5\u4e0d\u9519\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u7b11\u9053\uff1a\u201c\u539f\u6765\u9633\u575a\u767d\u4e5f\u5230\u4e86\u98de\u9f99\u5c71\u4e48\uff1f\u6211\u5012\u66fe\u548c\u4ed6\u4ea4\u8fc7\u624b\uff0c\u6df1\u77e5\u4ed6\u7684\u672c\u9886\u3002\u6599\u60f3 \u80dc\u4e0d\u4e86\u4ed6\uff0c\u4e5f\u4e0d\u81f3\u4e8e\u8d25\u5728\u4ed6\u7684\u624b\u4e0b\u3002\u201d \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u4e2a\u4eba\u540d\u53eb\u767d\u4e07\u96c4\uff0c\u4e43\u662f\u4e8c\u5341\u5e74\u524d\u4e0e\u6df3\u4e8e\u5468\u9f50\u540d\uff0c\u5728\u9ed1\u9053\u4e0a \u79f0\u96c4\u9053\u9738\u7684\u4eba\u7269\u3002\u540e\u6765\u56e0\u4e3a\u8d25\u5728\u5c60\u767e\u57ce\u7684\u624b\u4e0b\uff0c\u903c\u5f97\u91d1\u76c6\u6d17\u624b\uff0c\u95ed\u95e8\u5c01\u5200\u3002\u53bb\u5e74\u542c\u8bf4\u5c60\u767e\u57ce \u547d\u4e27\u8499\u53e4\uff0c\u4ed6\u624d\u4e1c\u5c71\u590d\u51fa\u7684\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u4ed6\u662f\u4e0d\u662f\u6709\u4e2a\u513f\u5b50\u540d\u53eb\u767d\u5343\u80dc\uff1f\u201d \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u9053\uff1a\u201c\u6b63\u662f\u3002\u539f\u6765\u8c37\u5144\u4e5f\u5df2\u77e5\u9053\u4e86\u4ed6\u4eec\u7684\u6765\u5386\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u9053\uff0c\u201c\u6211\u5728\u9ed1\u77f3\u5e84\u66fe\u7ecf\u548c\u767d\u5343\u80dc\u6253\u8fc7\u4e00\u67b6\u3002\u767d\u4e07\u96c4\u7684\u6765\u5386\u5219\u662f\u795d\u8001\u4e09\u544a\u8bc9\u6211\u7684\uff0c \u4e0d\u8fc7\u6ca1\u6709\u9053\u957f\u8bf4\u7684\u8be6\u7ec6\u3002\u201d \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u8fd9\u624d\u77e5\u9053\u8fd9\u6b21\u4e4b\u6240\u4ee5\u78b0\u4e0a\u8c37\u6db5\u865a\u5e76\u975e\u5076\u7136\u7684\u4e8b\u3002\u8c37\u6db5\u865a\u53c8\u7b11\u9053\uff1a\u201c\u795d\u6c0f\u4e09\u5144 \u5f1f\uff0c\u795d\u8001\u5927\u6700\u53ef\u6076\uff0c\u795d\u8001\u4e09\u5012\u662f\u4e00\u4e2a\u723d\u76f4\u7684\u6c49\u5b50\u3002\u4ed6\u5bf9\u4f60\u5f88\u662f\u4f69\u670d\uff0c\u79f0\u4f60\u662f\u9ed1\u9053\u4e0a\u4e00\u4f4d\u54cd\u5f53 \u5f53\u7684\u6c49\u5b50\u5462\u3002\u8bf4\u8001\u5b9e\u8bdd\uff0c\u6700\u521d\u6211\u662f\u4e0d\u76f8\u4fe1\u4ed6\u7684\u8bdd\u7684\uff0c\u5982\u4eca\u89c1\u4e86\u9053\u957f\uff0c\u65b9\u77e5\u540d\u4e0b\u65e0\u865a\u3002\u201d \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u7f9e\u5f97\u6ee1\u9762\u901a\u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u795d\u8001\u4e09\u7ed9\u6211\u9762\u4e0a\u8d34\u91d1\u4e86\u3002\u6211\u54ea\u91cc\u914d\u5f97\u4e0a\u597d\u6c49\u4e8c\u5b57\uff1f\u8fd9 \u6b21\u82e5\u4e0d\u662f\u591a\u5f97\u4e25\u59d1\u5a18\u4e0e\u8c37\u5144\u5584\u8a00\u5f00\u5bfc\uff0c\u6211\u53ea\u6015\u5df2\u662f\u8eab\u8d25\u540d\u88c2\uff0c\u4e3a\u5929\u4e0b\u82f1\u96c4\u6240\u4e0d\u9f7f\u4e86\u5462\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u5ffd\u5730\u5fc3\u5ff5\u4e00\u52a8\uff0c\u8bf4\u9053\uff1a\u201c\u9053\u957f\uff0c\u4f60\u82e5\u662f\u5df2\u7ecf\u60f3\u901a\u4e86\uff0c\u6211\u5012\u6709\u4e00\u4e8b\u76f8\u6c42\u3002\u201d \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u9053\uff1a\u201c\u8c37\u5144\u6709\u4ec0\u4e48\u9700\u8981\u8d2b\u9053\u6548\u52b3\u4e4b\u5904\uff0c\u8bf7\u5429\u5490\u5c31\u662f\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u9053\uff0c\u201c\u8bf7\u4f60\u4ecd\u7136\u7528\u8fd9\u8f86\u9aa1\u8f66\uff0c\u628a\u4e25\u59d1\u5a18\u62bc\u89e3\u5230\u98de\u9f99\u5c71\u53bb\u3002\u6211\u5145\u5f53\u4f60\u7684\u8f66\u592b\u3002\u201d \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u6014\u4e86\u4e00\u6014\uff0c\u89c1\u8c37\u6db5\u865a\u7684\u795e\u8272\u4e0d\u4f3c\u5728\u5f00\u73a9\u7b11\uff0c\u968f\u5373\u604d\u7136\u5927\u609f\uff0c\u8bf4\u9053\uff1a\u201c\u6562\u60c5\u8c37\u5144 \u662f\u8981\u7528\u8fd9\u6cd5\u5b50\u6df7\u5165\u98de\u9f99\u5c71\u4e48\uff1f\u201d \u3000\u3000\u4e25\u70f7\u62cd\u638c\u7b11\u9053\uff1a\u201c\u597d\u4e00\u6761\u5999\u8ba1\uff0c\u7aa6\u5b89\u5e73\u51b3\u8ba1\u6599\u60f3\u5e03\u5230\uff0c\u9ed1\u77f3\u9053\u957f\uff0c\u73b0\u5728\u5c31\u770b\u4f60\u613f\u4e0d\u613f\u610f \u5e2e\u6211\u4eec\u7684\u5fd9\u4e86\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u5e76\u975e\u4e0d\u6562\u786c\u95ef\uff0c\u4f46\u6211\u4eec\u5374\u4e0d\u613f\u591a\u4f24\u7aa6\u5b89\u5e73\u624b\u4e0b\u7684\u65e0\u8f9c\u55bd\u5175\u3002\u9053\u957f\uff0c \u6211\u8fd9\u4e5f\u662f\u4e3a\u4f60\u7740\u60f3\uff0c\u4f60\u82e5\u4e0d\u613f\u7ed9\u7aa6\u5b89\u5e73\u62d6\u4f60\u843d\u6c34\uff0c\u8981\u4fdd\u6301\u81ea\u5df1\u6e05\u767d\u7684\u8bdd\uff0c\u6050\u6015\u4e5f\u5fc5\u987b\u6709\u6240\u4f5c \u4e3a\uff0c\u624d\u80fd\u8ba9\u5929\u4e0b\u82f1\u96c4\u76f8\u4fe1\u3002\u201d \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u4e00\u6765\u662f\u7231\u60dc\u81ea\u5df1\u7684\u58f0\u8a89\uff0c\u8fd9\u6b21\u4ed6\u7cca\u91cc\u7cca\u6d82\u5730\u4e0a\u4e86\u7aa6\u5b89\u5e73\u7684\u5f53\uff0c\u7684\u786e\u662f\u5fc5\u987b\u6d17 \u5237\uff1b\u4e8c\u6765\u4ed6\u542c\u4e86\u8c37\u6db5\u865a\u6653\u4ee5\u5927\u4e49\u7684\u8bf4\u8bdd\uff0c\u7ecf\u8fc7\u4e86\u5185\u5fc3\u7684\u4ea4\u6218\uff0c\u7ec8\u4e8e\u4e5f\u9192\u609f\u4e86\u4e0d\u5e94\u8be5\u8ba9\u4e2a\u4eba\u7684 \u6069\u6028\u5de6\u53f3\u5927\u662f\u5927\u975e\u7684\u6289\u62e9\u3002 \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u60f3\u4e86\u4e00\u60f3\uff0c\u6bc5\u7136\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u6562\u8bf4\u662f\u5df2\u7ecf\u60f3\u5f97\u5341\u5206\u901a\u900f\uff0c\u4f46\u7aa6\u5b89\u5e73\u8fd9\u6837\u7684\u80e1\u4f5c \u5984\u4e3a\uff0c\u7684\u786e\u662f\u6b66\u6797\u540c\u9053\u6240\u4e0d\u80fd\u5bb9\u7684\u3002\u6211\u81ea\u662f\u4e0d\u80fd\u4e3a\u864e\u4f5c\u6005\uff0c\u597d\uff0c\u8c37\u5144\uff0c\u6211\u542c\u4f60\u7684\u5429\u5490\uff01\u4e0d \u8fc7\uff0c\u6211\u4e5f\u6709\u4e2a\u8bf7\u6c42\uff0c\u8bf7\u8c37\u5144\u5728\u5904\u7f6e\u7aa6\u5b89\u5e73\u4e4b\u524d\uff0c\u8ba9\u6211\u5148\u529d\u4ed6\u4e00\u52a8\uff0c\u7565\u5c3d\u6211\u7684\u5fc3\u4e8b\u3002\u529d\u4ed6\u4e0d \u542c\uff0c\u90a3\u5c31\u53ea\u597d\u4efb\u7531\u8c37\u5144\u5904\u7f6e\u4e86\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u77e5\u9053\u4ed6\u5c1a\u987e\u5ff5\u7740\u4e0e\u7aa6\u5b89\u5e73\u7684\u7ed3\u62dc\u4e4b\u60c5\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6c5f\u6e56\u6c49\u5b50\u5927\u591a\u8bb2\u7a76\u4e49\u6c14\uff0c\u5176 \u5b9e\u4e5f\u5e94\u8be5\u770b\u662f\u54ea\u79cd\u4e49\u6c14\u624d\u5bf9\uff0c\u4e0d\u8fc7\uff0c\u4ed6\u867d\u7136\u8fd8\u662f\u6709\u70b9\u7cca\u6d82\uff0c\u4e5f\u7b97\u662f\u96be\u5f97\u7684\u4e86\u3002\u201d\u4e8e\u662f\u70b9\u4e86\u70b9 \u5934\u51fa\u9053\uff1a\u201c\u597d\uff0c\u6211\u4f9d\u4f60\u5c31\u662f\u3002\u201d \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u9053\uff1a\u201c\u6211\u66fe\u78b0\u4e0a\u7aa6\u5b89\u5e73\u7684\u4f84\u513f\uff0c\u4ed6\u77e5\u9053\u6211\u662f\u6ca1\u96c7\u6709\u8f66\u592b\u7684\u3002\u8c37\u5144\u53ef\u4ee5\u5192\u5145\u662f\u548c \u6211\u76f8\u8bc6\u7684\u9ed1\u9053\u670b\u53cb\uff0c\u524d\u6765\u6295\u5954\u98de\u9f99\u5c71\u7684\uff0c\u521a\u597d\u5728\u8fd9\u513f\u9047\u4e0a\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u7b11\u9053\uff1a\u201c\u6211\u53ea\u6c42\u80fd\u591f\u6df7\u8fdb\u7aa6\u5b89\u5e73\u7684\u5c71\u5be8\u5c31\u884c\uff0c\u626e\u4f5c\u4ec0\u4e48\u4eba\u90fd\u65e0\u6240\u8c13\u3002\u201d \u3000\u3000\u4e25\u70f7\u5927\u4e3a\u559c\u6b22\uff0c\u8bf4\u9053\uff1a\u201c\u8c37\u5927\u54e5\uff0c\u60f3\u4e0d\u5230\u4eca\u5929\u80fd\u591f\u89c1\u7740\u4e86\u4f60\u3002\u81f3\u8fdf\u660e\u5929\uff0c\u53c8\u53ef\u4ee5\u89c1\u7740\u6211 \u7684\u8205\u8205\u3002\u53ef\u60dc\u8fd8\u6709\u660e\u971e\u8868\u59b9\uff0c\u672a\u77e5\u6d88\u606f\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u7b11\u9053\uff1a\u201c\u6211\u6b63\u8981\u544a\u8bc9\u4f60\u5462\uff0c\u6211\u5df2\u7ecf\u89c1\u7740\u5979\u4e86\u3002\u4f60\u5230\u4e86\u98de\u9f99\u5c71\uff0c\u5c31\u53ef\u4ee5\u548c\u5979\u76f8 \u4f1a\u3002\u201d \u3000\u3000\u4e25\u70f7\u53c8\u60ca\u53c8\u559c\uff0c\u8bf4\u9053\uff1a\u201c\u771f\u7684\u5417\uff1f\u5979\u4e5f\u5230\u98de\u9f99\u5c71\u53bb\uff1f\u5374\u4e3a\u4f55\u4e0d\u548c\u5979\u7236\u4eb2\u4e00\u9053\uff1f\u201d \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u53e6\u5916\u6709\u4e2a\u4e00\u4eba\u966a\u7740\u5979\u5462\uff01\u201d \u3000\u3000\u4e25\u70f7\u8be7\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u8c37\u6db5\u865a\u7b11\u9053\uff1a\u201c\u4f60\u522b\u5fd8\u8bb0\u660e\u971e\u53ea\u6bd4\u4f60\u5c0f\u4e24\u5c81\uff0c\u5982\u4eca\u5979\u4e5f\u662f\u4e00\u4f4d\u5927\u59d1\u5a18\u4e86\u3002\u5979\u6709\u4e86\u610f\u4e2d\u4eba \u5566\uff01\u201d \u3000\u3000\u4e25\u70f7\u559c\u9053\uff1a\u201c\u539f\u6765\u8fd9\u5c0f\u59ae\u5b50\u5df2\u6709\u4e86\u610f\u4e2d\u4eba\u4e86\uff0c\u8fd9\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u8c37\u6db5\u865a\u7b11\u9053\uff1a\u201c\u8fd9\u4eba\u540d\u891a\u4e91\u5cf0\u3002\u8bf4\u6765\u4e5f\u5de7\uff0c\u8fd9\u891a\u4e91\u5cf0\u6b63\u662f\u6211\u7684\u540c\u95e8\u5e08\u5144\u3002\u201d\u5f53\u4e0b\u5c06\u5de7\u9047 \u891a\u4e91\u5cf0\u4e0e\u5b5f\u660e\u971e\u7684\u7ecf\u8fc7\u544a\u8bc9\u4e25\u70f7\u3002 \u3000\u3000\u4e25\u70f7\u7b11\u9053\uff1a\u201c\u90a3\u6b21\u6211\u79bb\u5bb6\u51fa\u8d70\uff0c\u591a\u4e8f\u660e\u971e\u7684\u5e2e\u5fd9\uff0c\u8fd8\u672a\u66fe\u5411\u5979\u9053\u8c22\u5462\u3002\u5f53\u65f6\u6b63\u662f\u6211\u6700\u4f24 \u5fc3\u7684\u65f6\u5019\uff0c\u524d\u8def\u832b\u832b\uff0c\u4e5f\u4e0d\u77e5\u80fd\u4e0d\u80fd\u4e0e\u4f60\u91cd\u9022\u3002\u60f3\u4e0d\u5230\u5982\u4eca\u8fde\u660e\u971e\u4e5f\u662f\u6210\u53cc\u6210\u5bf9\u4e86\u3002\u4f46\u613f\u5979 \u4e0e\u4f60\u7684\u90a3\u4f4d\u891a\u5e08\u5144\u7ed3\u6210\u8fde\u7406\uff0c\u90a3\u5c31\u662f\u6700\u597d\u4e0d\u8fc7\u4e86\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u5728\u5979\u8033\u8fb9\u6084\u58f0\u7b11\u9053\uff1a\u201c\u4f60\u6709\u4e00\u53e5\u8bdd\u672a\u8bf4\u51fa\u6765\uff0c\u6211\u66ff\u4f60\u8bf4\u597d\u4e0d\u597d\uff1f\u201d\u4e25\u70f7\u9053\uff1a\u201c\u4f60 \u600e\u77e5\u9053\u6211\u5fc3\u91cc\u7684\u8bf4\u8bdd\uff1f\u201d\u8c37\u6db5\u865a\u9053\uff1a\u201c\u6211\u600e\u80fd\u4e0d\u77e5\u9053\uff1f\u4e3a\u4ec0\u4e48\u8fd9\u662f\u6700\u597d\u4e0d\u8fc7\u5462\uff1f\u56e0\u4e3a\u8fd9\u5c31\u6b63 \u662f\u53eb\u505a\u4eb2\u4e0a\u52a0\u4eb2\u5440\uff01\u563f\uff0c\u563f\uff0c\u4f60\u5fc3\u91cc\u60f3\u8bf4\u7684\u662f\u4e0d\u662f\u8fd9\u4e00\u53e5\u8bdd\uff1f\u201d\u4e25\u70f7\u7f9e\u5f97\u6ee1\u9762\u901a\u7ea2\uff0c\u4f46\u4e5f\u53ea \u597d\u4f4e\u4e0b\u5934\u9ed8\u8ba4\u4e86\u3002 \u3000\u3000\u9aa1\u8f66\u6ee1\u8f7d\u7740\u871c\u610f\u67d4\u60c5\uff0c\u5411\u98de\u9f99\u5c71\u9a70\u53bb\u3002\u53e6\u4e00\u6761\u901a\u5f80\u98de\u9f99\u5c71\u7684\u8def\u4e0a\uff0c\u891a\u4e91\u5cf0\u4e0e\u5b5f\u660e\u971e\u4e5f\u662f \u540c\u6837\u7684\u6ee1\u6000\u6b22\u60a6\uff0c\u5e76\u8f94\u800c\u884c\u3002 \u3000\u3000\u4e25\u70f7\u60e6\u8bb0\u7740\u5b5f\u660e\u971e\uff0c\u5b5f\u660e\u971e\u4e5f\u5728\u6302\u5ff5\u7740\u4e25\u70f7\u3002\u5c06\u5230\u98de\u9f99\u5c71\u4e4b\u65f6\uff0c\u5fc3\u60c5\u751a\u4e3a\u5174\u594b\uff0c\u53ef\u53c8\u6709 \u70b9\u62c5\u5fe7\uff0c\u5fcd\u4e0d\u4f4f\u548c\u891a\u4e91\u5cf0\u8bf4\u9053\uff1a\u201c\u4e0d\u77e5\u4f60\u7684\u90a3\u4f4d\u8c37\u5e08\u5144\u53ef\u66fe\u627e\u7740\u6211\u7684\u8868\u59d0\u3002\u4f46\u613f\u98de\u9f99\u5c71\u4e0a\u80fd \u591f\u89c1\u7740\u4ed6\u4eec\uff0c\u8fd9\u624d\u771f\u662f\u7686\u5927\u6b22\u559c\u5462\uff01\u201d \u3000\u3000\u891a\u4e91\u5cf0\u7b11\u9053\uff1a\u201c\u613f\u5929\u4e0b\u6709\u60c5\u4eba\u7686\u6210\u7737\u5c5e\u3002\u8fd9\u4e2a\u613f\u671b\u662f\u4e00\u5b9a\u80fd\u591f\u5b9e\u73b0\u7684\u3002\u4f60\u60f3\u4ed6\u4eec\u53d7\u4e86\u8fd9 \u8bb8\u591a\u6298\u78e8\uff0c\u8001\u5929\u7237\u8fd8\u597d\u610f\u601d\u518d\u6298\u78e8\u4ed6\u4eec\u5417\uff1f\u5f53\u7136\u662f\u8981\u6210\u5168\u4ed6\u4eec\u7684\u4e86\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u6cb9\u5634\u6ed1\u820c\u3002\u55ef\uff0c\u6211\u73b0\u5728\u5012\u60f3\u95ee\u4f60\u4e00\u53e5\u6b63\u7ecf\u4e8b\u5462\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u6211\u8bf4\u7684\u53ef\u90fd\u662f\u6b63\u7ecf\u8bdd\u5440\uff0c\u597d\uff0c\u4f60\u95ee\u5427\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u54b1\u4eec\u600e\u6837\u8fdb\u98de\u9f99\u5c71\uff1f\u662f\u786c\u95ef\u5462\uff1f\u8fd8\u662f\u665a\u4e0a\u5077\u53bb\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u4e0d\u5fc5\u786c\u95ef\uff0c\u4e5f\u65e0\u987b\u5077\u5077\u5730\u53bb\uff01\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u90a3\u53c8\u5982\u4f55\uff1f\u522b\u5356\u5173\u5b50\u4e86\uff0c\u5feb\u8bf4\u5427\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u7b11\u9053\uff1a\u201c\u4f60\u5fd8\u8bb0\u4e86\u6211\u662f\u9633\u5929\u96f7\u5e08\u4f84\u7684\u8eab\u4efd\u5417\uff1f\u6211\u867d\u7136\u53db\u4e86\u4ed6\uff0c\u4f46\u6b64\u4e8b\u5916\u4eba\u662f\u4e0d\u4f1a \u77e5\u9053\u7684\u3002\u6211\u8fd8\u7559\u7740\u4ee5\u524d\u5728\u9633\u5929\u96f7\u624b\u4e0b\u5f53\u5dee\u7684\u65f6\u5019\u4ed6\u7ed9\u6211\u7684\u4ee4\u7b26\u5462\u3002\u6211\u7528\u6765\u9a97\u9a97\u7aa6\u5b89\u5e73\uff0c\u6599\u60f3 \u53ef\u4ee5\u6df7\u5f97\u8fc7\u53bb\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u7aa6\u5b89\u5e73\u548c\u9633\u5929\u96f7\u4e0d\u662f\u6709\u5f80\u6765\u7684\u5417\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u4e0d\u9519\u3002\u4f46\u9633\u5929\u96f7\u662f\u4e0d\u4f1a\u628a\u8fd9\u79cd\u4ee4\u4ed6\u4e22\u9762\u5b50\u7684\u4e8b\u60c5\u544a\u8bc9\u7aa6\u5b89\u5e73\u7684\u3002\u4f55\u51b5\u4ed6\u548c \u9633\u5929\u96f7\u53c8\u4e0d\u662f\u76f4\u63a5\u6765\u5f80\uff0c\u4ed6\u7684\u624b\u4e0b\u4eba\u5373\u4f7f\u77e5\u9053\u8fd9\u4e2a\u79d8\u5bc6\uff01\u4e5f\u4e0d\u6562\u80e1\u4e71\u5bf9\u4eba\u8bf4\u7684\u3002\u53ea\u987b\u9a97\u5f97\u8fc7 \u4e00\u65f6\u5c31\u884c\u4e86\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u54b1\u4eec\u53cd\u6b63\u662f\u8981\u53bb\u7684\uff0c\u80fd\u591f\u4e0d\u52a8\u5e72\u6208\uff0c\u6df7\u5f97\u8fdb\u53bb\uff0c\u56fa\u7136\u6700\u597d\uff0c\u6df7\u4e0d\u8fdb\u53bb\uff0c\u518d \u4f5c\u6253\u7b97\uff0c\u4e5f\u8fd8\u4e0d\u8fdf\u3002\u5927\u4e0d\u4e86\u62fc\u7740\u786c\u95ef\u7f62\u4e86\u3002\u4e0d\u8fc7\u6211\u5374\u6709\u70b9\u4e3a\u6768\u5a49\u62c5\u5fc3\u5462\u3002\u201d\u539f\u6765\u5b5f\u660e\u971e\u8fd9\u6b21 \u4e0b\u5c71\uff0c\u672c\u6765\u662f\u4e3a\u7740\u8ffd\u8e2a\u6768\u5a49\u7684\uff0c\u4f46\u5982\u4eca\u5df2\u662f\u5c06\u5230\u98de\u9f99\u5c71\u4e86\uff0c\u5374\u8fd8\u6ca1\u6709\u89c1\u7740\u6768\u5a49\u3002 \u3000\u3000\u5b5f\u660e\u971e\u53f9\u4e86\u53e3\u6c14\uff0c\u7eed\u9053\uff1a\u201c\u5509\uff0c\u4f60\u4e0d\u77e5\u9053\uff0c\u6768\u59d1\u5a18\u8fd9\u6b21\u4e0b\u5c71\u662f\u56e0\u6211\u800c\u8d77\u7684\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u7b11\u9053\uff1a\u201c\u6211\u77e5\u9053\u3002\u4f60\u662f\u8981\u4f7f\u4ed6\u4eec\u592b\u59bb\u548c\u597d\uff0c\u4e5f\u662f\u4e3a\u4e86\u8981\u6c42\u81ea\u5df1\u7684\u5fc3\u4e4b\u6240\u5b89\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u9762\u4e0a\u4e00\u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u77e5\u9053\u5c31\u597d\u3002\u4f60\u60f3\u60f3\uff0c\u6768\u59d1\u5a18\u7236\u5144\u90fd\u5df1\u6b7b\u4e86\uff0c\u5728\u8fd9\u4e16\u4e0a\uff0c\u53ea \u6709\u674e\u601d\u5357\u662f\u552f\u4e00\u7684\u4eb2\u4eba\u3002\u8eab\u4e16\u4f55\u7b49\u53ef\u601c\uff01\u82e5\u662f\u6709\u4ec0\u4e48\u610f\u5916\uff0c\u53eb\u6211\u5982\u4f55\u5bf9\u5f97\u4f4f\u5979\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u6050\u6015\u5979\u672a\u5fc5\u662f\u6765\u98de\u9f99\u5c71\u5427\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u5979\u5bf9\u674e\u601d\u5357\u867d\u6709\u8bef\u4f1a\uff0c\u4f46\u6211\u6599\u60f3\u5979\u662f\u51b3\u4e0d\u80fd\u820d\u5f03\u674e\u601d\u5357\u7684\uff0c\u4e0d\u53bb\u98de\u9f99\u5c71\u53c8 \u53bb\u54ea\u91cc\uff1f\u6211\u73b0\u5728\u62c5\u5fc3\u7684\u5c31\u662f\u5979\u6df7\u4e0d\u8fdb\u53bb\uff0c\u53cd\u800c\u7ed9\u7aa6\u5b89\u5e73\u6349\u4f4f\uff0c\u90a3\u65f6\u5c31\u4ee4\u674e\u601d\u5357\u4e3a\u96be\u4e86\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u53ea\u597d\u5b89\u6170\u5979\u9053\uff1a\u201c\u5409\u4eba\u81ea\u6709\u5929\u76f8\uff0c\u6768\u59d1\u5a18\u4e5f\u4e0d\u662f\u5bfb\u5e38\u7684\u5973\u5b50\uff0c\u4f60\u4e0d\u5fc5\u9884\u5148\u4e3a\u5979\u62c5 \u5fe7\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u548c\u891a\u4e91\u5cf0\u90fd\u6599\u5f97\u4e0d\u9519\uff0c\u6768\u5a49\u679c\u7136\u662f\u6765\u4e86\u98de\u9f99\u5c71\uff0c\u800c\u4e14\u78b0\u4e0a\u4e86\u4e00\u4e2a\u610f\u5916\u7684\u201c\u673a \u9047\u201d\uff0c\u5982\u4eca\u5979\u65e9\u5df2\u662f\u6df7\u8fdb\u7aa6\u5b89\u5e73\u7684\u5c71\u5be8\u4e86\u3002 \u3000\u3000\u6682\u4e14\u6309\u4e0b\u8bf8\u3001\u5b5f\u4e8c\u4eba\u4e0d\u8868\uff0c\u5148\u8bf4\u6768\u5a49\u7684\u906d\u9047\u3002 \u3000\u3000\u6768\u5a49\u4e0e\u660e\u6167\u516c\u4e3b\u3001\u963f\u76d6\u592b\u59bb\u5206\u624b\u4e4b\u540e\uff0c\u7ee7\u7eed\u524d\u884c\u3002\u56e0\u4e3a\u5b5f\u660e\u971e\u5728\u9014\u4e2d\u66fe\u78b0\u4e0a\u4e86\u9633\u575a\u767d \u201c\u91c7\u82b1\u201d\u4e4b\u4e8b\uff0c\u803d\u6401\u4e86\u4e00\u4e9b\u65f6\u5019\uff0c\u662f\u4ee5\u6768\u5a49\u53c8\u5f97\u4ee5\u8d74\u5728\u5979\u7684\u524d\u5934\u4e86\u3002 \u3000\u3000\u8fd9\u4e00\u5929\u6768\u5a49\u8e0f\u8fdb\u4e86\u98de\u9f99\u5c71\u7684\u5c71\u53e3\uff0c\u53ea\u987b\u518d\u8d70\u591a\u5341\u51e0\u91cc\u5c71\u8def\u5c31\u662f\u7aa6\u5b89\u5e73\u7684\u5c71\u5be8\u6240\u5728\u4e86\uff0c\u5c71 \u53e3\u6709\u95f4\u9152\u5e97\uff0c\u4e43\u662f\u7aa6\u5b89\u5e73\u624b\u4e0b\u7684\u5934\u76ee\u5f00\u7684\uff0c\u4e0d\u8fc7\u6768\u5a49\u5374\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u6768\u5a49\u5973\u626e\u7537\u88c5\uff0c\u8138\u4e0a\u6d82\u4e86\u963f\u76d6\u7ed9\u4ed6\u7684\u8349\u836f\uff0c\u672c\u6765\u662f\u4e00\u5f20\u5439\u5f39\u5f97\u7834\u7684\u7c89\u8138\u53d8\u5f97\u94c1\u9752\uff0c\u5e26\u7740 \u51e0\u5206\u7c97\u8c6a\u6c14\u5473\uff0c\u5f88\u50cf\u9ed1\u9053\u4e0a\u7684\u4eba\u7269\u3002 \u3000\u3000\u6768\u5a49\u4e00\u8def\u5954\u9a70\uff0c\u6b64\u65f6\u6b63\u81ea\u611f\u5230\u8179\u9965\u53e3\u6e34\uff0c\u770b\u89c1\u8def\u65c1\u6709\u95f4\u9152\u5e97\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u7ba1\u5b83\u662f\u4ec0\u4e48 \u4eba\u5f00\u7684\uff0c\u6211\u4e14\u8fdb\u53bb\u5403\u70b9\u4e1c\u897f\uff0c\u987a\u4fbf\u4e5f\u53ef\u4ee5\u6253\u542c\u6253\u542c\u6d88\u606f\u3002\u201d \u3000\u3000\u9152\u5e97\u91cc\u5148\u5df2\u6709\u4e86\u4e09\u4e2a\u5ba2\u4eba\uff0c\u4e09\u4e2a\u4eba\u90fd\u662f\u5e26\u6709\u5175\u5668\uff0c\u76f8\u8c8c\u7c97\u8c6a\u7684\u6c49\u5b50\u3002\u5176\u4e2d\u4e00\u4eba\u559d\u5f97\u4f3c\u4e4e \u6709\u4e86\u4e03\u516b\u5206\u9189\u610f\uff0c\u6768\u5a49\u8fdb\u6765\u7684\u65f6\u5019\uff0c\u521a\u597d\u542c\u5f97\u4ed6\u5377\u7740\u820c\u5934\uff0c\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u4ed6\u5988\u7684\uff0c\u5230\u4e86\u8fd9 \u513f\uff0c\u54b1\u4eec\u8fd8\u4e0d\u653e\u5fc3\u559d\u9152\u5417\uff1f\u4f60\u4eec\u4e0d\u5fc5\u529d\u6211\uff0c\u6211\u4e00\u5b9a\u8981\u559d\u4e2a\u75db\u5feb\uff01\u4ed6\u7684\u624b\u518d\u957f\uff0c\u4e5f\u4f38\u4e0d\u5230\u8fd9 \u91cc\u3002\u5f85\u54b1\u4eec\u89c1\u4e86\u7aa6\u5be8\u4e3b\uff0c\u54fc\u3001\u54fc\uff0c\u6211\u8fd8\u8981\u4ed6\u9a6c\u8001\u5927\u7684\u597d\u770b\u5462\uff01\u201d \u3000\u3000\u4e09\u4eba\u4e4b\u4e2d\uff0c\u8fd9\u6c49\u5b50\u5df2\u7ecf\u559d\u9189\uff0c\u53e6\u5916\u4e00\u4eba\u4e5f\u5df2\u6709\u4e86\u4e03\u516b\u5206\u9152\u610f\uff0c\u53ea\u6709\u4e00\u4eba\u6bd4\u8f83\u6e05\u9192\u3002\u8fd9\u4e2a \u6bd4\u8f83\u6e05\u9192\u7684\u4eba\u8bf4\u9053\uff1a\u201c\u4e94\u54e5\uff0c\u4f60\u8981\u559d\u9152\u5c31\u559d\u597d\u4e86\uff0c\u53ef\u522b\u80e1\u8a00\u4e71\u8bed\u3002\u6211\u770b\u4f60\u7684\u9152\u4e5f\u559d\u591f\u4e86\uff0c\u54b1 \u4eec\u8fd8\u662f\u8d70\u5427\uff01\u201d \u3000\u3000\u90a3\u4e2a\u53eb\u505a\u201c\u4e94\u54e5\u201d\u7684\u4eba\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u5230\u4e86\u8fd9\u91cc\u6211\u8fd8\u6015\u4ec0\u4e48\uff1f\u8fd9\u91cc\u662f\u98de\u9f99\u5c71\uff0c\u4e0d\u662f\u56de\u9f99 \u5cad\uff0c\u5c31\u7b97\u9a6c\u8001\u5927\u5728\u8fd9\u513f\uff0c\u4ed6\u4e5f\u7ba1\u4e0d\u5230\u54b1\u4eec\uff0c\u6211\u544a\u8bc9\u4ed6\uff0c\u6211\u5c31\u662f\u8981\u6295\u5954\u98de\u9f99\u5c71\uff0c\u4ed6\u53c8\u6562\u600e\u4e48 \u6837\u3002\u54fc\u3001\u54fc\uff0c\u6211\u6015\u7684\u53ea\u662f\u7aa6\u5be8\u4e3b\u4e0d\u80af\u6536\u5bb9\u54b1\u4eec\u3002\u201d \u3000\u3000\u53e6\u4e00\u4e2a\u6709\u4e86\u4e03\u516b\u5206\u9152\u610f\u7684\u4eba\u8ddf\u7740\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u54b1\u4eec\u628a\u56de\u9f99\u5cad\u7684\u5bb6\u5f53\u732e\u7ed9\u7aa6\u5be8\u4e3b\uff0c\u8fd9\u4efd\u793c \u4e5f\u4e0d\u7b97\u592a\u8584\uff0c\u7aa6\u5be8\u4e3b\u8fd8\u80fd\u4e0d\u6536\u5bb9\u6211\u4eec\uff1f\u201d \u3000\u3000\u90a3\u4e2a\u6bd4\u8f83\u6e05\u9192\u7684\u4eba\u76b1\u4e86\u76b1\u7709\u5934\u8bf4\u9053\uff1a\u201c\u8bf4\u8bdd\u603b\u662f\u8c28\u614e\u4e00\u4e9b\u7684\u597d\uff0c\u53c8\u4f55\u5fc5\u56b7\u5f97\u901a\u5929\u4e0b\u7684\u4eba \u90fd\u77e5\u9053\uff01\u201d \u3000\u3000\u559d\u5f97\u5927\u9189\u7684\u6c49\u5b50\u8bf4\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u4f60\u662f\u6015\u9694\u5899\u6709\u8033\u3002\u54fc\uff0c\u6211\u6562\u8bf4\u6562\u505a\uff0c\u5c31\u4e0d\u6015\u6709\u4eba\u5077 \u542c\u3002\u6211\u9152\u8fd8\u672a\u559d\u591f\u5462\uff0c\u62ff\u9152\u6765\uff01\u201d \u3000\u3000\u6768\u5a49\u542c\u5230\u8fd9\u91cc\uff0c\u5fc3\u91cc\u5df2\u7136\u660e\u767d\u3002\u539f\u6765\u6768\u5a49\u5728\u7405\u739b\u5c71\u5145\u5f53\u55bd\u5175\u7684\u65f6\u5019\uff0c\u548c\u4e00\u4e2a\u8001\u5934\u76ee\u8f6e\u73ed \u770b\u5b88\u4e00\u4e2a\u5c97\u54e8\uff0c\u4e5f\u5e38\u542c\u5f97\u8fd9\u4e2a\u8001\u5934\u76ee\u8c08\u53ca\u7eff\u6797\u7684\u4e00\u4e9b\u4e8b\u60c5\u3002 \u3000\u3000\u56de\u9f99\u5cad\u6709\u4e00\u4e2a\u5c0f\u5c71\u5be8\uff0c\u5be8\u4e3b\u540d\u9a6c\u9526\u5e38\uff0c\u8eab\u6750\u77ee\u5c0f\uff0c\u6b66\u529f\u5374\u5f88\u4e0d\u5f31\uff0c\u4eba\u79f0\u4e09\u5bf8\u9489\uff0c\u4ed6\u624b\u4e0b \u6709\u4e03\u4e2a\u5934\u76ee\uff0c\u5176\u4e2d\u4e09\u4e2a\u5750\u7b2c\u4e94\u7b2c\u516d\u7b2c\u4e03\u4e09\u628a\u4ea4\u6905\u7684\u5934\u76ee\u672c\u6765\u662f\u53e6\u5916\u4e00\u4f19\u7684\uff0c\u672c\u9886\u4e5f\u90fd\u4e0d\u9519\uff0c \u53ea\u56e0\u4ed6\u4eec\u662f\u540e\u6765\u624d\u52a0\u5165\u7684\uff0c\u800c\u53c8\u4e0d\u80af\u4f4e\u5934\u670d\u5c0f\uff0c\u56e0\u6b64\u548c\u56de\u9f99\u5cad\u539f\u6765\u7684\u5934\u76ee\u5904\u5f97\u4e0d\u597d\uff0c\u9a6c\u9526\u5e38 \u4e5f\u4e0d\u5927\u4fe1\u4efb\u4ed6\u4eec\u3002\u8fd9\u4e09\u4e2a\u4eba\u66fe\u7ecf\u5077\u5077\u548c\u7405\u739b\u5c71\u63a5\u6d3d\uff0c\u60f3\u6539\u6295\u7405\u739b\u5c71\u3002\u5c60\u51e4\u4e3a\u4e86\u6015\u5931\u7eff\u6797\u548c \u6c14\uff0c\u5a49\u8a00\u63a8\u8f9e\u4e86\u4ed6\u4eec\u3002 \u3000\u3000\u98de\u9f99\u5c71\u7aa6\u5b89\u5e73\u7684\u5c71\u5be8\u5219\u662f\u4e00\u4e2a\u52bf\u529b\u96c4\u539a\u7684\u5927\u5c71\u5be8\uff0c\u8fd1\u5e74\u6765\u4ed6\u628a\u5468\u56f4\u6570\u767e\u91cc\u5185\u7684\u5c0f\u5c71\u5be8\uff0c \u4e00\u4e2a\u4e00\u4e2a\u7ed9\u4ed6\u541e\u5e76\u4e86\uff0c\u4f46\u56de\u9f99\u5cad\u5374\u5c1a\u672a\u5411\u4ed6\u5c48\u670d\u3002 \u3000\u3000\u6768\u5a49\u542c\u4e86\u4ed6\u4eec\u7684\u8c08\u8bdd\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u60f3\u5fc5\u662f\u4ed6\u4eec\u5df2\u7ecf\u548c\u9a6c\u9526\u5e38\u95f9\u7ffb\uff0c\u5982\u4eca\u662f\u6765\u6295\u5954\u98de\u9f99 \u5c71\u6765\u4e86\u3002\u201d \u3000\u3000\u6768\u5a49\u542c\u5f97\u5206\u5916\u7559\u795e\uff0c\u90a3\u4e2a\u6bd4\u8f83\u6e05\u9192\u7684\u4eba\u72e0\u72e0\u76ef\u4e86\u5979\u4e00\u773c\u3002 \u3000\u3000\u90a3\u4e2a\u559d\u9189\u4e86\u7684\u6c49\u5b50\u500f\u5730\u4e00\u62cd\u6848\u5b50\uff0c\u7ad9\u4e86\u8d77\u6765\uff0c\u51b2\u7740\u6768\u5a49\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b50\u662f\u4ec0\u4e48\u4eba\uff0c\u80c6 \u6562\u5077\u542c\u6211\u4eec\u7684\u8bf4\u8bdd\uff0c\u5403\u6211\u4e00\u62f3\uff01\u201d \u3000\u3000\u90a3\u4e2a\u6bd4\u8f83\u6e05\u9192\u7684\u4eba\u529d\u9053\uff1a\u201c\u4e94\u54e5\uff0c\u4e0d\u53ef\u9c81\u83bd\uff01\u4f60\u5728\u8fd9\u91cc\u5927\u58f0\u8bf4\u8bdd\uff0c\u600e\u80fd\u8bf4\u662f\u522b\u4eba\u5077 \u542c\uff1f\u201d \u3000\u3000\u90a3\u559d\u9189\u7684\u6c49\u5b50\u4e00\u638c\u5c06\u4ed6\u63a8\u5f00\uff0c\u4e00\u8fb9\u6b65\u5230\u4e86\u6768\u5a49\u9762\u524d\uff0c\u559d\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u6b6a\u7740\u773c\u775b\u770b\u6211\uff0c \u6211\u6709\u4ec0\u4e48\u597d\u770b\uff1f\u7ba1\u4f60\u662f\u4e0d\u662f\u6709\u5fc3\u5077\u542c\uff0c\u8fd9\u4e00\u62f3\u4f60\u662f\u5403\u5b9a\u7684\u4e86\uff01\u201d\u63ea\u7740\u6768\u5a49\uff0c\u515c\u5fc3\u5c31\u662f\u4e00\u62f3\u3002 \u3000\u3000\u6768\u5a49\u7109\u80fd\u8ba9\u4ed6\u6253\u7740\uff0c\u5f53\u4e0b\u7b3c\u624b\u8896\u4e2d\uff0c\u6325\u8896\u4e00\u5377\uff0c\u7b11\u9053\uff1a\u201c\u738b\u5be8\u4e3b\uff0c\u5e78\u4f1a\uff0c\u5e78\u4f1a\uff01\u76f8\u8bf7\u4e0d \u5982\u5076\u9047\uff0c\u8bf7\u4e94\u7237\u5750\u4e0b\uff0c\u5bb9\u5c0f\u53ef\u656c\u4e00\u676f\u3002\u201d\u90a3\u6c49\u5b50\u8fde\u6768\u5a49\u7684\u6307\u5934\u4e5f\u6ca1\u78b0\u7740\uff0c\u4fbf\u7ed9\u5979\u7684\u8863\u8896\u5377\u7740 \u4e86\u624b\u8155\uff0c\u5c31\u50cf\u7ed9\u4eba\u7d27\u7d27\u6293\u7740\u4e00\u822c\uff0c\u52a8\u5f39\u4e0d\u5f97\uff0c\u8eab\u4e0d\u7531\u5df1\u5730\u5750\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u738b\u4e94\u867d\u7136\u559d\u9189\uff0c\u4f46\u9152\u9189\u4e4b\u4e2d\u4e5f\u8fd8\u6709\u4e24\u5206\u6e05\u9192\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u9189\u610f\u53c8\u6d88\u4e86\u51e0\u5206\uff0c\u8bb7\u8bb7\u8bf4\u9053\uff1a \u201c\u4f60\u662f\u8c01\uff0c\u4f60\u600e\u7684\u8ba4\u5f97\u6211\uff1f\u201d\u6b64\u65f6\u4ed6\u5df2\u77e5\u9053\u6768\u5a49\u7684\u672c\u9886\u8fdc\u8fdc\u5728\u4ed6\u4e4b\u4e0a\u3002\u4f46\u5728\u60ca\u614c\u4e4b\u4e2d\u4e5f\u6709\u51e0 \u5206\u9ad8\u5174\uff0c\u56e0\u4e3a\u6768\u5a49\u7684\u8bdd\u5bf9\u4ed6\u5f88\u662f\u606d\u656c\u3002\u4ed6\u672c\u6765\u662f\u4e00\u4e2a\u5c0f\u5c71\u5be8\u7684\u7b2c\u4e94\u53f7\u5934\u76ee\uff0c\u6768\u5a49\u5374\u79f0\u4ed6\u4e3a \u201c\u5be8\u4e3b\u201d\u3002 \u3000\u3000\u6768\u5a49\u80e1\u4e71\u634f\u4e86\u4e00\u4e2a\u540d\u8bf4\u4e86\uff0c\u7b11\u9053\uff1a\u201c\u4e94\u7237\u7684\u5927\u540d\uff0c\u8c01\u4eba\u4e0d\u77e5\uff1f\u5c0f\u53ef\u66fe\u89c1\u8fc7\u4e94\u7237\uff0c\u4e0d\u8fc7\u6211 \u662f\u4e2a\u65e0\u540d\u5c0f\u5352\uff0c\u4e94\u7237\u5f53\u7136\u662f\u4e0d\u4f1a\u8bb0\u5f97\u7684\u4e86\u3002\u201d \u3000\u3000\u738b\u4e94\u8be7\u9053\uff1a\u201c\u4f60\u5728\u4ec0\u4e48\u65f6\u5019\u89c1\u8fc7\u6211\u7684\uff1f\u201d \u3000\u3000\u6768\u5a49\u8bf4\u9053\uff1a\u201c\u4e94\u7237\u53bb\u5e74\u662f\u4e0d\u662f\u66fe\u7ecf\u4e0a\u8fc7\u4e00\u6b21\u7405\u739b\u5c71\uff1f\u201d \u3000\u3000\u738b\u4e94\u9053\uff1a\u201c\u4e0d\u9519\u3002\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u5b9e\u4e0d\u76f8\u7792\uff0c\u5c0f\u53ef\u4e43\u662f\u5355\u7ebf\u5f00\u6252\u505a\u70b9\u6ca1\u672c\u94b1\u7684\u5c0f\u751f\u610f\uff0c\u6ca1\u8d44\u683c\u5728\u7405\u739b\u5c71\u5165\u4f19\u3002 \u4f46\u5374\u591a\u627f\u5c71\u4e0a\u4e00\u4f4d\u5934\u76ee\u7684\u7167\u987e\u3002\u8fd9\u4f4d\u5934\u76ee\u59d3\u51af\uff0c\u5355\u540d\u4e00\u4e2a\u4fe1\u5b57\u3002\u5728\u7405\u739b\u5c71\u7684\u804c\u4f4d\u4e0d\u9ad8\uff0c\u4f46\u5374 \u662f\u6df7\u4e86\u51e0\u5341\u5e74\u7684\u8001\u5934\u76ee\uff0c\u60f3\u5fc5\u4e94\u7237\u77e5\u9053\uff1f\u201d \u3000\u3000\u51af\u4fe1\u5c31\u662f\u628a\u56de\u9f99\u5cad\u7684\u4e8b\u60c5\u544a\u8bc9\u6768\u5a49\u7684\u90a3\u4e2a\u8001\u5934\u76ee\uff0c\u90a3\u4e00\u6b21\u738b\u4e94\u6765\u7405\u739b\u5c71\u79d8\u5bc6\u63a5\u6d3d\uff0c\u5c31\u662f \u7531\u4ed6\u62db\u5f85\u7684\u3002 \u3000\u3000\u738b\u4e94\u70b9\u4e86\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u77e5\u9053\uff0c\u77e5\u9053\uff0c\u539f\u6765\u4ed6\u662f\u4f60\u7684\u9760\u5c71\uff0c\u8fd9\u4e48\u8bf4\u6765\uff0c\u5012\u662f\u81ea\u5df1\u4eba\u4e86\u3002 \u6768\u5144\uff0c\u4f60\u6709\u8fd9\u6837\u597d\u7684\u672c\u9886\uff0c\u7405\u739b\u5c71\u5374\u4e0d\u80af\u6536\u5bb9\u4f60\uff0c\u90a3\u771f\u662f\u4ed6\u4eec\u8d70\u773c\u4e86\u3002\u4e0d\u8fc7\u8bdd\u8bf4\u56de\u6765\uff0c\u7405\u739b \u5c71\u7684\u5973\u5be8\u4e3b\u867d\u662f\u4e0d\u77e5\u597d\u6b79\uff0c\u8fd9\u4f4d\u7167\u987e\u4f60\u7684\u51af\u8001\u7237\u5b50\u5374\u662f\u5f88\u591f\u670b\u53cb\u3002\u201d\u738b\u4e94\u56e0\u4e3a\u5c60\u51e4\u4e0d\u80af\u63a5\u7eb3 \u4ed6\uff0c\u601d\u4e4b\u72b9\u6709\u4f59\u6124\u3002 \u3000\u3000\u6768\u5a49\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u90a3\u5929\u4e94\u7237\u4e0a\u5c71\u4e4b\u65f6\uff0c\u5c0f\u53ef\u521a\u597d\u89c1\u8fc7\u4e86\u51af\u8001\u7237\u5b50\uff0c\u627f\u4ed6\u5ba2\u6c14\uff0c\u9001\u6211\u4e0b \u5c71\uff0c\u5728\u9014\u4e2d\u89c1\u5230\u4e94\u7237\u3002\u4e94\u7237\u8bb0\u5f97\u4e48\uff1f\u201d \u3000\u3000\u90a3\u6b21\u51af\u4fe1\u63a5\u5f85\u4ed6\u4e0a\u5c71\uff0c\u662f\u5e26\u6709\u51e0\u4e2a\u4eba\u7684\u3002\u738b\u4e94\u8fde\u5fd9\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4e0d\u9519\u3002\u8bb0\u8d77\u6765\u4e86\uff0c\u662f \u6709\u4f60\u8001\u5f1f\u5728\u5185\uff0c\u6765\uff0c\u6765\uff0c\u6765\uff0c\u516d\u5f1f\uff0c\u4e03\u5f1f\uff0c\u4f60\u4eec\u90fd\u6765\u89c1\u8fc7\u8fd9\u4f4d\u6768\u5144\u3002\u201d \u3000\u3000\u6768\u5a49\u8bf4\u9053\uff1a\u201c\u5f20\u516d\u7237\u548c\u674e\u4e03\u7237\u5c0f\u53ef\u4e5f\u662f\u4e45\u4ef0\u7684\u4e86\uff01\u201d \u3000\u3000\u5f20\u516d\u5c31\u662f\u90a3\u4e2a\u6bd4\u8f83\u6e05\u9192\u7684\u6c49\u5b50\uff0c\u8bf4\u9053\uff1a\u201c\u6768\u5144\uff0c\u6211\u4eec\u8bf4\u7684\u8bdd\u4f60\u90fd\u5df2\u542c\u89c1\u4e86\u3002\u8bf7\u4f60\u53ef\u522b\u5bf9 \u5916\u4eba\u6cc4\u9732\u3002\u201d \u3000\u3000\u6768\u5a49\u8bf4\u9053\uff1a\u201c\u8fd9\u4e2a\u516d\u7237\u65e0\u987b\u987e\u8651\u3002\u5b9e\u4e0d\u76f8\u7792\uff0c\u5c0f\u5f1f\u4e5f\u662f\u60f3\u6765\u6295\u5954\u98de\u9f99\u5c71\u7aa6\u5be8\u4e3b\u7684\u5462\uff01\u201d \u3000\u3000\u5f20\u516d\u9053\uff1a\u201c\u54e6\uff0c\u4f60\u4e5f\u662f\u6765\u6295\u5954\u98de\u5c24\u5c71\u7684\uff1f\u201d\u6b63\u662f\uff1a \u3000\u3000\u65e0\u610f\u76f8\u9022\u751f\u6025\u667a\uff0c\u7792\u5929\u8fc7\u6d77\u95ef\u9f99\u6f6d\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b\u2014\u2014\u7b2c\u4e09\u5341\u4e5d\u56de\u3000\u5de7\u8ba1\u4e54\u88c5\u63a2\u864e\u7a74\u3000\u795e\u529f\u663e\u9732\u6151\u9b51\u9b45 \u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b \u7b2c\u4e09\u5341\u4e5d\u56de\u3000\u5de7\u8ba1\u4e54\u88c5\u63a2\u864e\u7a74\u3000\u795e\u529f\u663e\u9732\u6151\u9b51\u9b45 \u3000\u3000\u6768\u5a49\u8bf4\u9053\uff1a\u201c\u6211\u6709\u4e00\u53e5\u8bf4\u8bdd\uff0c\u6709\u5982\u9aa8\u9ca0\u5728\u5589\uff0c\u4e0d\u5410\u4e0d\u5feb\uff01\u201d \u3000\u3000\u738b\u4e94\u9053\uff1a\u201c\u6768\u5144\u4f46\u8bf4\u65e0\u59a8\u3002\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u4e0d\u662f\u6df1\u5c71\u96be\u85cf\u731b\u864e\uff0c\u4e0d\u662f\u5927\u6d77\u96be\u517b\u86df\u9f99\u3002\u201d \u3000\u3000\u5f20\u516d\u9053\uff1a\u201c\u4f60\u8fd9\u8bdd\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u6c5f\u6e56\u4e0a\u8c01\u4e0d\u77e5\u9053\u56de\u9f99\u5cad\u662f\u9760\u4f60\u4eec\u4e09\u4f4d\u6491\u7684\u95e8\u9762\uff0c\u90a3\u4e09\u5bf8\u9489\u9a6c\u9526\u5e38\u7eb5\u6709\u51e0\u5206\u672c \u9886\uff0c\u600e\u6bd4\u5f97\u4e0a\u4f60\u4eec\uff1f\u504f\u504f\u4ed6\u53c8\u4e0d\u77e5\u81ea\u91cf\uff0c\u7adf\u7136\u5992\u624d\u5fcc\u80fd\uff0c\u5018\u82e5\u6362\u4e86\u662f\u6211\uff0c\u6211\u4e5f\u4e0d\u4f1a\u5728\u4ed6\u5e95\u4e0b \u53d7\u8fd9\u7a9d\u56ca\u6c14\u7684\u3002\u98de\u9f99\u5c71\u8fd1\u5e74\u65e5\u76ca\u5174\u65fa\uff0c\u58f0\u5a01\u5df2\u662f\u9010\u6e10\u76d6\u8fc7\u4e86\u7405\u739b\u5c71\u3002\u56de\u9f99\u5cad\u56fa\u7136\u662f\u6c34\u6d45\u96be\u517b \u86df\u9f99\uff0c\u7405\u739b\u5c71\u4e5f\u7b97\u4e0d\u5f97\u662f\u5927\u6d77\u3002\u4e0e\u5176\u6295\u5954\u7405\u739b\u5c71\uff0c\u90a3\u5c31\u4e0d\u5982\u6295\u5954\u98de\u9f99\u5c71\u4e86\u3002\u201d\u6768\u5a49\u672c\u6765\u662f\u5927 \u5bb6\u95fa\u79c0\uff0c\u4e0d\u60ef\u548c\u6c5f\u6e56\u4eba\u7269\u6253\u4ea4\u9053\u7684\u3002\u4f46\u56e0\u5728\u6c5f\u6e56\u4e0a\u5386\u7ec3\u4e86\u51e0\u5e74\uff0c\u5b66\u4f1a\u4e86\u4e00\u5957\u6c5f\u6e56\u5207\u53e3\uff0c\u8fd9\u6b21 \u7b2c\u4e00\u6b21\u7528\u8fd9\u79cd\u6c5f\u6e56\u53e3\u543b\u8bf4\u8bdd\uff0c\u8bf4\u6765\u5c45\u7136\u4e2d\u89c4\u4e2d\u77e9\u3002 \u3000\u3000\u6768\u5a49\u7684\u8bf4\u8bdd\u4e0d\u4f46\u540c\u60c5\u4ed6\u4eec\uff0c\u800c\u4e14\u8fd8\u7ed9\u4ed6\u4eec\u6234\u4e0a\u4e86\u9ad8\u5e3d\uff0c\u8fd9\u4e09\u4e2a\u4eba\u542c\u4e86\uff0c\u5f53\u7136\u662f\u5341\u5206\u53d7 \u7528\u3002 \u3000\u3000\u738b\u4e94\u54c8\u54c8\uff0c\u7b11\u9053\uff1a\u201c\u6768\u5144\uff0c\u4ee5\u4f60\u7684\u8eab\u624b\uff0c\u5b9e\u5728\u4e5f\u4e0d\u8be5\u57cb\u6ca1\uff0c\u5bf9\u5566\uff01\u7405\u739b\u5c71\u4e0d\u80af\u6536\u5bb9\u4f60\uff0c \u4f60\u5c31\u548c\u6211\u4eec\u4e00\u540c\u4e0a\u98de\u9f99\u5c71\uff0c\u8fd9\u6b63\u662f\u6700\u597d\u4e0d\u8fc7\u3002\u201d \u3000\u3000\u6768\u5a49\u8bf4\u9053\uff1a\u201c\u53ea\u6015\u6211\u867d\u6709\u6b64\u5fc3\uff0c\u65e0\u4eba\u5f15\u8350\uff0c\u4e5f\u662f\u96be\u4ee5\u8fdb\u8eab\u3002\u201d \u3000\u3000\u738b\u4e94\u4e00\u62cd\u5df4\u638c\uff0c\u8bf4\u9053\uff1a\u201c\u6768\u5144\u4e0d\u7528\u62c5\u5fc3\uff0c\u8fd9\u70b9\u5c0f\u4e8b\u6211\u82e5\u4e0d\u80fd\u5e2e\u5fd9\uff0c\u8fd8\u8981\u670b\u53cb\u4f55\u7528\uff1f\u4f60\u548c \u6211\u4eec\u540c\u8d70\uff0c\u7aa6\u5be8\u4e3b\u603b\u4f1a\u7ed9\u6211\u51e0\u5206\u8584\u9762\u3002\u201d \u3000\u3000\u5f20\u516d\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u4e94\u54e5\uff0c\u98de\u9f99\u5c71\u867d\u8bf4\u662f\u62db\u8d24\u7eb3\u58eb\uff0c\u4f46\u4e0d\u77e5\u6765\u5386\u7684\u4eba\u6050\u6015\u4e5f\u4e0d\u4f1a\u8f7b\u6613\u5f55\u7528 \u7684\u3002\u54b1\u4eec\u4e5f\u62ff\u4e0d\u51c6\u4e00\u5b9a\u80fd\u8499\u6536\u5bb9\u5462\uff01\u201d \u3000\u3000\u738b\u4e94\u5567\u9053\uff1a\u201c\u54b1\u4eec\u7684\u6765\u5386\u7aa6\u5be8\u4e3b\u8fd8\u80fd\u4e0d\u77e5\u9053\u5417\uff1f\u8fd9\u4f4d\u6768\u5144\u662f\u6211\u7684\u670b\u53cb\uff0c\u4e5f\u4e0d\u80fd\u8bf4\u662f\u6ca1\u6709 \u6765\u5386\uff01\u201d \u3000\u3000\u738b\u4e94\u662f\u4e09\u4eba\u4e4b\u4e2d\u7684\u201c\u5927\u54e5\u201d\uff0c\u5f20\u516d\u5b9e\u662f\u4e0d\u60f3\u591a\u7ba1\u95f2\u4e8b\u7684\uff0c\u4f46\u738b\u4e94\u8fd9\u4e48\u4e00\u8bf4\uff0c\u4ed6\u4e5f\u53ea\u597d\u4e0d \u4f5c\u58f0\u4e86\u3002 \u3000\u3000\u9152\u5e97\u8001\u677f\u6e29\u4e86\u4e00\u58f6\u70ed\u9152\u51fa\u6765\uff0c\u738b\u4e94\u53d7\u4e86\u5f20\u516d\u7684\u9876\u649e\uff0c\u6b63\u81ea\u6709\u70b9\u751f\u6c14\uff0c\u62cd\u6848\u9a82\u9053\uff1a\u201c\u4e3a\u4ec0 \u4e48\u8fd9\u4e2a\u65f6\u5019\u624d\u6765\uff01\u201d \u3000\u3000\u5e97\u8001\u677f\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4e94\u7237\u4e0d\u7528\u751f\u6c14\uff0c\u5c0f\u7684\u7ed9\u4f60\u4eec\u51c6\u5907\u4e86\u4e00\u6837\u4e1c\u897f\u3002\u6765\u5f97\u6162\u4e86\uff0c\u4f60 \u522b\u89c1\u602a\u3002\u201d\u8bf4\u7f62\uff0c\u638f\u51fa\u4e86\u56db\u9762\u94dc\u724c\uff0c\u653e\u5728\u684c\u4e0a\u3002 \u3000\u3000\u738b\u4e94\u4e00\u770b\uff0c\u867d\u7136\u9189\u773c\u660f\u82b1\uff0c\u4e5f\u8fd8\u770b\u5f97\u6e05\u695a\uff0c\u8fd9\u56db\u9762\u94dc\u724c\u4e0a\u9762\uff0c\u90fd\u96d5\u523b\u6709\u4e00\u6761\u5f20\u7259\u821e\u722a\u7684 \u98de\u9f99\uff0c\u6b63\u662f\u98de\u9f99\u5c71\u7684\u6807\u8bb0\u3002 \u3000\u3000\u738b\u4e94\u5403\u4e86\u4e00\u60ca\uff0c\u8bb7\u8bb7\u8bf4\u9053\uff1a\u201c\u4f60\u3001\u4f60\u662f\u2026\u2026\u201d\u5e97\u8001\u677f\u7b11\u9053\uff1a\u201c\u5c0f\u7684\u662f\u7aa6\u5be8\u4e3b\u624b\u4e0b\u4e00\u4e2a\u4e0d \u8db3\u9053\u7684\u4eba\u3002\u8fd9\u95f4\u9152\u5e97\u5c31\u662f\u5949\u4e86\u7aa6\u5be8\u4e3b\u4e4b\u547d\u5f00\u7684\u3002\u201d \u3000\u3000\u738b\u4e94\u6014\u4e86\u4e00\u6014\uff0c\u968f\u5373\u54c8\u54c8\u5927\u7b11\uff0c\u62b1\u62f3\u4e00\u63d6\uff0c\u8bf4\u9053\uff1a\u201c\u539f\u6765\u662f\u5927\u6c34\u51b2\u5012\u4e86\u9f99\u738b\u5e99\uff0c\u81ea\u5bb6\u4eba \u8ba4\u4e0d\u5f97\u81ea\u5bb6\u4eba\u4e86\u3002\u738b\u67d0\u6709\u773c\u65e0\u73e0\uff0c\u8bf7\u8001\u5144\u591a\u591a\u6055\u7f6a\u3002\u201d \u3000\u3000\u5e97\u8001\u677f\u8d54\u7b11\u9053\uff1a\u201c\u54ea\u91cc\uff0c\u54ea\u91cc\u3002\u5f97\u4f60\u4eec\u51e0\u4f4d\u6765\u655d\u5be8\u5e2e\u5fd9\uff0c\u6211\u4e5f\u998f\u5149\u4e0d\u5c11\u3002\u4e0d\u8fc7\u655d\u5be8\u76ee\u524d \u6b63\u662f\u6709\u4e8b\uff0c\u6050\u6015\u55bd\u5175\u4e0d\u77e5\u4f60\u4eec\u7684\u6765\u5386\uff0c\u6216\u8bb8\u4f1a\u6709\u7f57\u55e6\u3002\u8fd9\u51e0\u9762\u94dc\u724c\uff0c\u662f\u81ea\u5df1\u4eba\u7684\u6807\u8bb0\uff0c\u4ed6\u4eec \u89c1\u4e86\u94dc\u724c\uff0c\u81ea\u7136\u5bf9\u4f60\u4eec\u4f18\u793c\u6709\u52a0\uff0c\u5e26\u4f60\u4eec\u8fdb\u53bb\u3002\u201d \u3000\u3000\u539f\u6765\u98de\u9f99\u5c71\u7684\u89c4\u77e9\uff0c\u5bf9\u6295\u5954\u5c71\u5be8\u7684\u4eba\uff0c\u5fc5\u987b\u7ecf\u8fc7\u9152\u5e97\u4e00\u5173\uff0c\u7531\u8fd9\u9152\u5e97\u7684\u8001\u677f\uff0c\u5148\u67e5\u660e\u6765 \u4eba\u7684\u8eab\u4efd\u3002\u5018\u82e5\u6765\u4eba\u4e0d\u77e5\u9053\u8fd9\u4e2a\u89c4\u77e9\u5f84\u81ea\u4e0a\u5c71\uff0c\u6216\u8bb8\u867d\u7136\u8fdb\u4e86\u9152\u5e97\uff0c\u4f46\u5374\u6ca1\u6709\u5f97\u5230\u94dc\u724c\u7684 \u8bdd\uff0c\u90a3\u5c31\u51f6\u591a\u5409\u5c11\u3002\u6768\u5a49\u8bef\u6253\u8bef\u649e\uff0c\u6070\u597d\u649e\u4e2a\u6b63\u7740\u3002\u8fd9\u9152\u5e97\u8001\u677f\u89c1\u5979\u672c\u9886\u9ad8\u5f3a\uff0c\u738b\u4e94\u53c8\u627f\u8ba4 \u662f\u89c1\u8fc7\u5979\u7684\u670b\u53cb\uff0c\u8fd9\u8001\u677f\u4e50\u5f97\u6210\u4eba\u4e4b\u7f8e\u3002 \u3000\u3000\u6768\u5a49\u5f97\u4e86\u94dc\u724c\uff0c\u8ddf\u56de\u9f99\u5cad\u8fd9\u4e09\u4e2a\u5934\u76ee\u4e00\u8d77\uff0c\u679c\u7136\u5f88\u987a\u5229\u7684\u672a\u53d7\u76d8\u95ee\u5c31\u6df7\u8fdb\u4e86\u98de\u9f99\u5c71\u3002\u63a5 \u5f85\u4ed6\u4eec\u7684\u662f\u7aa6\u5b89\u5e73\u7684\u526f\u5be8\u4e3b\u7f57\u4fca\u3002 \u3000\u3000\u7f57\u4fca\u62b1\u62f3\u8bf4\u9053\uff1a\u201c\u8fd9\u4e24\u65e5\u6211\u4eec\u5be8\u5185\u6709\u70b9\u4e8b\u60c5\uff0c\u5404\u65b9\u670b\u53cb\u6765\u7684\u4e0d\u5c11\uff0c\u7aa6\u5be8\u4e3b\u73b0\u5728\u6b63\u5728\u89c1 \u5ba2\uff0c\u6050\u6015\u8981\u5f85\u8fd9\u4ef6\u4e8b\u60c5\u8fc7\u4e86\uff0c\u624d\u80fd\u548c\u4f60\u4eec\u51e0\u4f4d\u8be6\u8c08\uff0c\u8bf7\u4f60\u4eec\u591a\u591a\u539f\u8c05\u3002\u201d \u3000\u3000\u738b\u4e94\u9053\uff1a\u201c\u4e0d\u77e5\u8d35\u5be8\u5fd9\u7684\u4f55\u4e8b\uff0c\u53ef\u7528\u5f97\u7740\u6211\u4eec\u6548\u52b3\uff1f\u201d \u3000\u3000\u7f57\u4fca\u9053\uff1a\u201c\u660e\u5929\u5c06\u6709\u65b0\u4efb\u7684\u7eff\u6797\u76df\u4e3b\u674e\u601d\u5357\u6765\u5230\uff0c\u662f\u4ee5\u6211\u4eec\u5fd9\u4e8e\u51c6\u5907\u63a5\u5f85\u3002\u201d\u7f57\u4fca\u56e0\u4e3a \u4ed6\u4eec\u521d\u5230\uff0c\u4e0d\u4fbf\u8be6\u8a00\u3002\u4f46\u6768\u5a49\u5374\u662f\u660e\u767d\u7684\u3002 \u3000\u3000\u738b\u4e94\u7a0d\u7a0d\u6709\u70b9\u8be7\u5f02\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u542c\u8bf4\u8fd9\u4f4d\u65b0\u76df\u4e3b\u662f\u7531\u7405\u739b\u5c71\u62e5\u7acb\u7684\uff0c\u5982\u4eca\u7aa6\u5b89\u5e73\u5bf9\u65b0 \u76df\u4e3b\u5982\u6b64\u656c\u91cd\uff0c\u6050\u6015\u5bf9\u7405\u739b\u5c71\u4e5f\u662f\u4e0d\u6562\u5f97\u7f6a\u7684\u4e86\u3002\u6211\u4eec\u66fe\u88ab\u7405\u739b\u5c71\u6240\u62d2\uff0c\u7aa6\u5b89\u5e73\u82e5\u77e5\u6b64\u4e8b\uff0c \u4e0d\u77e5\u4f1a\u4e0d\u4f1a\u63a5\u7eb3\u6211\u4eec\u3002\u201d\u4ed6\u54ea\u91cc\u77e5\u9053\uff0c\u7aa6\u5b89\u5e73\u805a\u96c6\u201c\u5404\u65b9\u597d\u53cb\u201d\uff0c\u63a5\u5f85\u76df\u4e3b\uff0c\u4e3a\u7684\u53ef\u5e76\u4e0d\u662f \u5bf9\u76df\u4e3b\u656c\u91cd\uff0c\u800c\u662f\u8981\u5bf9\u4ed8\u4ed6\u3002 \u3000\u3000\u5f53\u4e0b\u738b\u4e94\u8bf4\u660e\u4e86\u6765\u610f\uff0c\u7f57\u4fca\u5927\u4e3a\u6b22\u559c\uff0c\u8bf4\u9053\uff1a\u201c\u56de\u9f99\u5cad\u9a6c\u9526\u5e38\u4e0d\u5927\u80af\u5356\u6211\u4eec\u98de\u9f99\u5c71\u7684 \u5e10\uff0c\u6211\u4eec\u7684\u5be8\u4e3b\u6b63\u60f3\u6536\u62fe\u4ed6\u5462\u3002\u4e94\u54e5\u653e\u5fc3\uff0c\u4e8b\u6210\u4e4b\u540e\uff0c\u6211\u4eec\u4e5f\u4e0d\u6562\u59d4\u5c48\u4f60\u5728\u655d\u5be8\u5145\u5f53\u5934\u76ee\uff0c \u5c31\u7531\u4e94\u54e5\u63a5\u66ff\u9a6c\u9526\u5e38\u505a\u56de\u9f99\u5cad\u7684\u5be8\u4e3b\u597d\u4e86\u3002\u201d \u3000\u3000\u738b\u4e94\u66f4\u662f\u559c\u51fa\u671b\u5916\uff0c\u8fde\u5fd9\u62dc\u8c22\uff0c\u8bf4\u8fb9\uff1a\u201c\u5f97\u4e24\u4f4d\u5be8\u4e3b\u5982\u6b64\u683d\u57f9\uff0c\u738b\u67d0\u5018\u80fd\u5f53\u4e0a\u56de\u9f99\u5cad\u7684 \u5be8\u4e3b\uff0c\u5b9a\u5fc5\u5e74\u5e74\u8fdb\u8d21\uff0c\u5c81\u5c81\u6765\u671d\u3002\u201d \u3000\u3000\u7f57\u4fca\u8bf4\u9053\uff1a\u201c\u8bf7\u5404\u4f4d\u6682\u5f85\u4e00\u4f1a\uff0c\u5f85\u6211\u8fdb\u53bb\u770b\u770b\u7aa6\u5be8\u4e3b\u7684\u5ba2\u4eba\u8d70\u4e86\u6ca1\u6709\uff1f\u201d\u4ed6\u672c\u6765\u8bf4\u662f\u7aa6 \u5b89\u5e73\u5728\u8fd9\u51e0\u5929\u4e0d\u80fd\u63a5\u89c1\u4ed6\u4eec\u7684\uff0c\u4f46\u56e0\u738b\u4e94\u9001\u4e0a\u8fd9\u4efd\u539a\u793c\uff0c\u7acb\u5373\u6539\u5bb9\u76f8\u5f85\u3002 \u3000\u3000\u738b\u4e94\u77e5\u9053\u7f57\u4fca\u662f\u5730\u4f4d\u4ec5\u6b21\u4e8e\u7aa6\u5b89\u5e73\u7684\u4eba\uff0c\u50cf\u541e\u5e76\u56de\u9f99\u5cad\u8fd9\u6837\u7684\u5c0f\u4e8b\uff0c\u7f57\u4fca\u5df2\u7ecf\u53ef\u4ee5\u4f5c \u4e3b\u3002 \u3000\u3000\u56de\u9f99\u5cad\u662f\u4e2a\u5c0f\u5c71\u5be8\uff0c\u738b\u4e94\u7b49\u4eba\u5f97\u5230\u7f57\u4fca\u63a5\u5f85\u5df2\u662f\u5fc3\u6ee1\u610f\u8db3\uff0c\u8fde\u5fd9\u8bf4\u9053\uff1a\u201c\u4e0d\u5fc5\u9ebb\u70e6\u7aa6\u5be8 \u4e3b\u4e86\uff0c\u6709\u7f57\u5be8\u4e3b\u66ff\u6211\u4eec\u4f5c\u4e3b\u4e5f\u662f\u4e00\u6837\u3002\u201d\u6768\u5a49\u5374\u6545\u610f\u8bf4\u9053\uff1a\u201c\u7aa6\u5be8\u4e3b\u6709\u8d35\u5ba2\u9700\u8981\u62db\u547c\uff0c\u6211\u662f \u4e2a\u672a\u5165\u6d41\u7684\u65e0\u540d\u5c0f\u5352\uff0c\u5c82\u53ef\u5192\u6627\u6253\u6270\uff1f\u4e94\u7237\uff0c\u4f60\u4eec\u53bb\u89c1\u7aa6\u5be8\u4e3b\uff0c\u66ff\u5728\u4e0b\u8ba8\u4e2a\u5c0f\u5c0f\u7684\u5dee\u4e8b\uff0c\u5728 \u4e0b\u4fbf\u611f\u6fc0\u4e0d\u5c3d\u4e86\u3002\u201d \u3000\u3000\u7f57\u4fca\u53ea\u77e5\u9053\u6768\u5a49\u662f\u548c\u738b\u4e94\u7b49\u4eba\u4e00\u8d77\u6765\u7684\uff0c\u5374\u4e0d\u77e5\u9053\u4ed6\u7684\u8eab\u4efd\uff0c\u542c\u4e86\u8fd9\u8bdd\uff0c\u6014\u4e86\u4e00\u6014\uff0c\u8bf4 \u9053\uff1a\u201c\u8fd9\u4f4d\u6768\u5144\u4e0d\u662f\u56de\u9f99\u5cad\u6765\u7684\u5417\uff1f\u201d \u3000\u3000\u738b\u4e94\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u6768\u5144\u662f\u6211\u7684\u597d\u670b\u53cb\uff0c\u672c\u9886\u5f88\u662f\u4e0d\u9519\u3002\u8fd9\u6b21\u662f\u6211\u7279\u5730\u9080\u4ed6\u6765\u7684\u3002\u201d\u63a5\u7740 \u53c8\u9053\uff1a\u201c\u6768\u5144\uff0c\u54b1\u4eec\u4e00\u5411\u4e0d\u5206\u5f7c\u6b64\uff0c\u4f60\u8bf4\u8fd9\u8bdd\uff0c\u672a\u514d\u6709\u70b9\u89c1\u5916\u4e86\u5427\uff1f\u5176\u5b9e\u54b1\u4eec\u6709\u7f57\u5be8\u4e3b\u5b89\u6392 \u5df2\u662f\u5b9a\u5fc5\u59a5\u5e16\u7684\u4e86\u3002\u4e0d\u6562\u60ca\u52a8\u7aa6\u5be8\u4e3b\u3002\u201d \u3000\u3000\u539f\u6765\u738b\u4e94\u5728\u5730\u4f4d\u6bd4\u4ed6\u9ad8\u5f97\u591a\u7684\u7f57\u4fca\u9762\u524d\uff0c\u867d\u7136\u6709\u4e86\u201c\u77ee\u4e86\u4e00\u622a\u201d\u7684\u81ea\u5351\u5fc3\u7406\uff0c\u4f46\u4ed6\u4e5f\u662f \u4e2a\u5f88\u8981\u9762\u5b50\u7684\u4eba\uff0c\u6768\u5a49\u662f\u4ed6\u5e26\u6765\u7684\u4eba\uff0c\u4ed6\u53ea\u6015\u7f57\u4fca\u770b\u8f7b\u6768\u5a49\uff0c\u5931\u4e86\u4ed6\u7684\u4f53\u9762\uff0c\u5fc3\u91cc\u8fd8\u5728\u602a\u6768 \u5a49\u8bf4\u9519\u4e86\u8bdd\u5462\u3002\u5c82\u77e5\u6768\u5a49\u6545\u610f\u90a3\u4e48\u8bf4\uff0c\u5374\u662f\u8981\u6324\u51fa\u7f57\u4fca\u7684\u8bdd\u3002 \u3000\u3000\u7f57\u4fca\u4e00\u6765\u662f\u56e0\u4e3a\u5df2\u7ecf\u77e5\u9053\u4e86\u738b\u4e94\u4ed6\u4eec\u662f\u771f\u5fc3\u6765\u6295\u5954\u98de\u9f99\u5c71\uff1b\u4e8c\u6765\u4ed6\u4eec\u732e\u4e0a\u8fd9\u4efd\u539a\u793c\uff0c\u4e5f \u5f97\u4f7f\u70b9\u624b\u6bb5\u6765\u7b3c\u7edc\u4ed6\u4eec\uff1b\u4e09\u6765\u4e5f\u6015\u4ed6\u4eec\u6709\u201c\u539a\u6b64\u8584\u5f7c\u4e4b\u5206\u201d\u7684\u4e0d\u6ee1\u3002\u542c\u4e86\u8fd9\u8bdd\uff0c\u4fbf\u5373\u7b11\u9053\uff1a \u201c\u8fd9\u4f4d\u5ba2\u4eba\u662f\u4e0d\u4fbf\u7ed9\u4eba\u5bb6\u77e5\u9053\u4ed6\u7684\u8eab\u4efd\u7684\u4eba\u7269\uff0c\u4f46\u4f60\u4eec\u4e0d\u662f\u5916\u4eba\uff0c\u6211\u4e0d\u59a8\u8bf4\u7ed9\u4f60\u4eec\u77e5\u9053\u3002\u201d \u5f53\u4e0b\u5c0f\u58f0\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u5ba2\u4eba\u4e43\u662f\u9633\u56fd\u5e08\u7684\u4f84\u5b50\u9633\u575a\u767d\u3002\u4e94\u54e5\uff0c\u4f60\u4e00\u5b9a\u542c\u8fc7\u4ed6\u7684\u540d\u5b57\u5427\uff1f\u201d \u3000\u3000\u738b\u4e94\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u662f\u91d1\u56fd\u56fd\u5e08\u9633\u5929\u96f7\u7684\u4f84\u5b50\u5417\uff1f\u201d \u3000\u3000\u7f57\u4fca\u9053\uff1a\u201c\u6b63\u662f\uff0c\u4e0d\u7792\u4f60\u8bf4\uff0c\u4ed6\u5c31\u662f\u6211\u4eec\u98de\u9f99\u5c71\u7684\u5927\u9760\u5c71\u5462\u3002\u6211\u662f\u628a\u4f60\u4eec\u5f53\u4f5c\u5fc3\u8179\u770b\u5f85 \u624d\u544a\u8bc9\u4f60\u4eec\u7684\u3002\u201d \u3000\u3000\u738b\u4e94\u542c\u4ed6\u8fd9\u4e48\u4e00\u8bf4\uff0c\u987f\u89c9\u9762\u4e0a\u751f\u5149\uff0c\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u7f57\u5be8\u4e3b\u770b\u5f97\u8d77\u6211\u4eec\u3002\u7f57\u5be8\u4e3b\u8bf4\u5f97\u5bf9\uff0c \u8bc6\u65f6\u52a1\u8005\u4e3a\u4fca\u6770\uff0c\u6709\u4e86\u8fd9\u6837\u7684\u5927\u9760\u5c71\uff0c\u738b\u67d0\u6267\u97ad\u968f\u956b\uff0c\u4e5f\u6cbe\u5149\u4e86\u3002\u201d \u3000\u3000\u7f57\u4fca\u7b11\u9053\uff1a\u201c\u73b0\u5728\u4f60\u4eec\u660e\u767d\u4e86\u5427\uff0c\u7aa6\u5be8\u4e3b\u5e76\u975e\u5bf9\u5bbe\u5ba2\u6709\u8d35\u8d31\u4e4b\u5206\uff0c\u800c\u662f\u56e0\u4e3a\u8fd9\u4f4d\u9633\u516c\u5b50 \u4e0d\u60f3\u7ed9\u4eba\u77e5\u9053\u3002\u6240\u4ee5\u4ed6\u4eec\u4e24\u4eba\u4f1a\u8c08\u4e4b\u9645\uff0c\u7aa6\u5be8\u4e3b\u53ea\u597d\u4e0d\u89c1\u5916\u5ba2\u4e86\uff01\u63a5\u7740\u53c8\u7b11\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u4f60 \u4eec\u73b0\u5728\u5df2\u975e\u5916\u5ba2\uff0c\u89c1\u9762\u4e5f\u65e0\u59a8\u3002\u6211\u966a\u4f60\u4eec\u8fdb\u53bb\uff0c\u4e5f\u597d\u8ba9\u7aa6\u5be8\u4e3b\u559c\u4e0a\u52a0\u559c\u3002\u201d \u3000\u3000\u6768\u5a49\u6697\u6697\u5403\u60ca\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u539f\u6765\u9633\u575a\u767d\u8fd9\u53ae\u4e5f\u5230\u4e86\u8fd9\u513f\u4e86\u3002\u201d\u6768\u5a49\u662f\u4e0d\u8fc7\u624d\u51e0\u5929\u4e4b\u524d \u548c\u9633\u575a\u767d\u4ea4\u8fc7\u624b\u7684\uff0c\u6b64\u9645\u5979\u867d\u5df2\u6539\u5bb9\u6613\u8c8c\uff0c\u4e5f\u4e0d\u80fd\u4e0d\u6709\u70b9\u6050\u9632\u7ed9\u9633\u575a\u767d\u770b\u7834\u3002 \u3000\u3000\u738b\u4e94\u542c\u8bf4\u7f57\u4fca\u53ef\u4ee5\u5e26\u4ed6\u53bb\u89c1\u7aa6\u5b89\u5e73\u548c\u9633\u575a\u767d\uff0c\u5fc3\u4e2d\u5927\u559c\uff0c\u5634\u91cc\u8fd8\u5728\u8c26\u8f9e\uff0c\u5c31\u5728\u6b64\u65f6\uff0c\u5ffd \u6709\u4e00\u4e2a\u5c0f\u5934\u76ee\u68d2\u4e86\u4e00\u4e2a\u62dc\u5323\u8fdb\u6765\u3002 \u3000\u3000\u90a3\u5c0f\u5934\u76ee\u6367\u7740\u62dc\u5323\uff0c\u6c14\u5598\u5401\u5401\uff0c\u53ef\u77e5\u4ed6\u662f\u4e00\u8def\u8dd1\u6765\u7684\u3002\u4f46\u4ed6\u89c1\u6709\u5916\u4eba\u5728\u573a\uff0c\u4e00\u65f6\u95f4\u5374\u53c8 \u4e0d\u6562\u8bf4\u8bdd\u3002 \u3000\u3000\u7f57\u4fca\u8bf4\u9053\uff1a\u201c\u8fd9\u51e0\u4f4d\u670b\u53cb\u662f\u81ea\u5df1\u4eba\uff0c\u4f60\u4e0d\u7528\u907f\u5fcc\u3002\u201d\u8fd9\u5c0f\u5934\u76ee\u624d\u653e\u5fc3\u8bf4\u9053\uff1a\u201c\u6709\u4e24\u4e2a\u4eba \u524d\u6765\u62dc\u5c71\uff0c\u6c42\u89c1\u5be8\u4e3b\u3002\u4e8c\u5be8\u4e3b\u8bf7\u4f60\u5b9a\u593a\uff0c\u8ba9\u4e0d\u8ba9\u4ed6\u4eec\u8fdb\u6765\uff1f\u201d \u3000\u3000\u7f57\u4fca\u7709\u5934\u4e00\u76b1\uff0c\u8bf4\u9053\uff1a\u201c\u662f\u4ec0\u4e48\u6837\u7684\u4eba\u7269\uff0c\u975e\u89c1\u5be8\u4e3b\u4e0d\u53ef\uff1f\u201d\u8981\u77e5\u5728\u98de\u9f99\u5c71\u4e0a\uff0c\u4ed6\u662f\u7b2c \u4e8c\u53f7\u5f53\u5bb6\uff0c\u4e00\u822c\u5ba2\u4eba\uff0c\u90fd\u662f\u7531\u4ed6\u63a5\u89c1\u7684\u3002 \u3000\u3000\u90a3\u5c0f\u5934\u76ee\u9053\uff1a\u201c\u662f\u90dd\u5934\u76ee\u5e26\u4ed6\u4eec\u4e0a\u5c71\u7684\uff0c\u636e\u90dd\u5934\u76ee\u8bf4\uff0c\u8fd9\u4e24\u4e2a\u4eba\u53ea\u6015\u6709\u4e9b\u6765\u5386\u3002\u8bf7\u4e8c\u5be8 \u4e3b\u6253\u5f00\u4ed6\u7684\u62dc\u5323\u4fbf\u77e5\u3002\u201d \u3000\u3000\u7f57\u4fca\u6253\u5f00\u62dc\u5323\uff0c\u53ea\u89c1\u9664\u4e86\u62dc\u5e16\u4e4b\u5916\uff0c\u8fd8\u6709\u4e00\u9762\u8d64\u91d1\u4ee4\u7b26\uff0c\u7f57\u4fca\u5403\u4e86\u4e00\u60ca\uff0c\u5148\u62ff\u4e86\u4ee4\u7b26\uff0c \u4ed4\u7ec6\u5730\u770b\u4e86\u53c8\u770b\uff0c\u8fd9\u624d\u62ff\u8d77\u4e86\u62dc\u5e16\uff0c\u5ff5\u9053\uff1a\u201c\u891a\u4e91\u5cf0\uff01\u201d \u3000\u3000\u6768\u5a49\u4e5f\u4e0d\u7981\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u59d3\u891a\u7684\u4e5f\u6765\u4e86\uff0c\u4ed6\u5230\u5e95\u662f\u597d\u4eba\u5462\uff0c\u8fd8\u662f\u574f\u4eba\uff1f\u201d \u3000\u3000\u7f57\u4fca\u5401\u4e86\u53e3\u6c14\uff0c\u8fde\u5fd9\u8bf4\u9053\uff1a\u201c\u8fd9\u4e24\u4eba\u73b0\u5728\u54ea\u513f\uff1f\u62dc\u5e16\u4e0a\u53ea\u6709\u4e00\u4eba\u7684\u540d\u5b57\uff0c\u8fd8\u6709\u4e00\u4e2a\u662f \u8c01\uff1f\u201d \u3000\u3000\u90a3\u5c0f\u5934\u76ee\u9053\uff1a\u201c\u90dd\u5934\u76ee\u966a\u4ed6\u4eec\u5728\u805a\u8d24\u4eab\u7b49\u5019\u53ec\u89c1\u3002\u53e6\u5916\u4e00\u4eba\u662f\u4e2a\u59d3\u5b5f\u7684\u5c11\u5e74\u7537\u5b50\uff0c\u4ed6\u597d \u50cf\u662f\u59d3\u891a\u7684\u968f\u4ece\uff0c\u6240\u4ee5\u6ca1\u6709\u53e6\u5907\u62dc\u5e16\u3002\u201d \u3000\u3000\u6768\u5a49\u53c8\u60ca\u53c8\u559c\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u4e00\u5b9a\u662f\u5b5f\u660e\u971e\u6539\u88c5\u800c\u6765\u4e86\u3002\u5b5f\u660e\u971e\u65e2\u7136\u80af\u966a\u4ed6\u6765\uff0c\u8fd9\u891a\u4e91 \u5cf0\u6211\u5012\u662f\u9519\u7591\u4ed6\u4e86\u3002\u201d \u3000\u3000\u7f57\u4fca\u9053\uff1a\u201c\u4f60\u53eb\u90dd\u5934\u76ee\u597d\u597d\u62db\u5f85\u4ed6\u4eec\uff0c\u6211\u8fd9\u5c31\u53bb\u7980\u544a\u5be8\u4e3b\u3002\u201d\u5c0f\u5934\u76ee\u89c1\u7f57\u4fca\u4e0d\u6562\u4f5c\u4e3b\uff0c \u4e5f\u662f\u597d\u751f\u60ca\u5f02\uff0c\u5f53\u4e0b\u5949\u547d\u800c\u53bb\u3002 \u3000\u3000\u738b\u4e94\u8bf4\uff1a\u201c\u891a\u4e91\u5cf0\u8fd9\u540d\u5b57\u597d\u719f\uff0c\u4ed6\u597d\u50cf\u662f\u3001\u662f\u2026\u2026\u201d \u3000\u3000\u7f57\u4fca\u8bf4\u9053\uff1a\u201c\u4f60\u731c\u5f97\u4e0d\u9519\uff0c\u5728\u8fd9\u513f\u8bf4\u51fa\u6765\u4ea6\u662f\u65e0\u59a8\u3002\u8fd9\u891a\u4e91\u5cf0\u6b63\u662f\u9633\u56fd\u5e08\u7684\u4e00\u4e2a\u5f97\u529b\u624b \u4e0b\u3002\u542c\u8bf4\u4ed6\u7684\u672c\u9886\u4e0d\u5728\u9633\u516c\u5b50\u4e4b\u4e0b\u3002\u201d \u3000\u3000\u5f20\u516d\u8bf4\u9053\uff1a\u201c\u53ea\u4e0d\u77e5\u4f1a\u4e0d\u4f1a\u662f\u5047\u5192\u7684\uff1f\u201d \u3000\u3000\u7f57\u4fca\u8bf4\u9053\uff1a\u201c\u6211\u8bc6\u5f97\u8fd9\u9762\u4ee4\u7b26\uff0c\u786e\u662f\u56fd\u5e08\u5e9c\u53d1\u51fa\u6765\u7684\u51ed\u7167\u3002\u4e0d\u8fc7\u9633\u516c\u5b50\u5c31\u5728\u8fd9\u513f\uff0c\u4ed6\u53ef \u6ca1\u6709\u8bf4\u8fc7\u891a\u4e91\u5cf0\u4f1a\u6765\u3002\u6309\u8bf4\u891a\u4e91\u5cf0\u662f\u9633\u56fd\u5e08\u624b\u4e0b\u7b2c\u4e8c\u53f7\u4eba\u7269\uff0c\u56fd\u5e08\u82e5\u7136\u6d3e\u9063\u4ed6\u6765\uff0c\u9633\u516c\u5b50\u4e0d \u4f1a\u4e0d\u77e5\u3002\u201d\u6c89\u541f\u534a\u664c\uff0c\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u53cd\u6b63\u8fd9\u4e2a\u4eba\u7aa6\u5be8\u4e3b\u662f\u4e00\u5b9a\u8981\u89c1\u7684\u4e86\uff0c\u8d81\u7740\u9633\u516c\u5b50\u548c\u5be8\u4e3b \u5c31\u5728\u91cc\u9762\uff0c\u6211\u6b63\u597d\u8fdb\u53bb\u7980\u62a5\u3002\u4e0d\u8fc7\uff0c\u53ef\u53c8\u5f97\u8bf7\u4f60\u4eec\u7a0d\u5f85\u4e00\u4f1a\u4e86\u3002\u201d \u3000\u3000\u738b\u4e94\u8bf4\u9053\uff1a\u201c\u7f57\u5be8\u4e3b\u8bf7\u4fbf\uff0c\u4e0d\u5fc5\u5ba2\u6c14\u3002\u201d \u3000\u3000\u7f57\u4fca\u9053\uff1a\u201c\u597d\uff0c\u90a3\u5c31\u8bf7\u4f60\u4eec\u5728\u6211\u623f\u4e2d\u6b47\u6b47\uff0c\u6211\u53bb\u53bb\u5c31\u6765\u3002\u201d \u3000\u3000\u7f57\u4fca\u8d70\u4e86\u4e4b\u540e\uff0c\u6768\u5a49\u5fc3\u91cc\u53ef\u662f\u6014\u5fe1\u4e0d\u5b9a\u4e86\u3002 \u3000\u3000\u6768\u5a49\u6697\u81ea\u601d\u91cf\uff1a\u201c\u891a\u4e91\u5cf0\u5b9a\u7136\u662f\u4e0d\u77e5\u9053\u9633\u575a\u767d\u5728\u6b64\uff0c\u624d\u6562\u8fd9\u6837\u5927\u80c6\uff0c\u4ecd\u7136\u5192\u5145\u9633\u5929\u96f7\u7684 \u624b\u4e0b\u3002\u9633\u575a\u767d\u4e00\u89c1\u4ed6\u7684\u62dc\u5e16\uff0c\u8fd9\u9a97\u5c40\u5f53\u7136\u662f\u5c31\u8981\u88ab\u62c6\u7a7f\u4e86\u3002\u53ef\u6709\u4ec0\u4e48\u529e\u6cd5\u53bb\u6551\u4ed6\u4eec\u5462\uff1f\u201d \u3000\u3000\u6768\u5a49\u5de6\u601d\u53f3\u60f3\uff0c\u5140\u662f\u60f3\u4e0d\u51fa\u4e00\u4e2a\u597d\u4e3b\u610f\uff0c\u7f57\u4fca\u5df2\u7ecf\u56de\u6765\u4e86\u3002 \u3000\u3000\u738b\u4e94\u95ee\u9053\uff1a\u201c\u6765\u7684\u8fd9\u4e2a\u81ea\u79f0\u891a\u4e91\u5cf0\u7684\u4eba\u662f\u771f\u662f\u5047\uff1f\u201d \u3000\u3000\u7f57\u4fca\u7b11\u9053\uff1a\u201c\u8bf4\u771f\u4e5f\u771f\uff0c\u8bf4\u5047\u4e5f\u5047\u3002\u201d \u3000\u3000\u738b\u4e94\u8be7\u9053\uff1a\u201c\u6b64\u8bdd\u600e\u8bf4\uff1f\u201d \u3000\u3000\u7f57\u4fca\u9053\uff1a\u201c\u891a\u4e91\u5cf0\u5012\u662f\u4e0d\u5047\uff0c\u4f46\u4ed6\u7684\u8eab\u4efd\u5df2\u7ecf\u53d8\u4e86\u3002\u4ed6\u4ee5\u524d\u662f\u9633\u56fd\u5e08\u7684\u5e08\u4f84\uff0c\u5982\u4eca\u662f\u53db \u5f92\uff0c\u548c\u9633\u56fd\u5e08\u4f5c\u5bf9\u4e86\u3002\u9633\u516c\u5b50\u8fd9\u6b21\u51fa\u4eac\uff0c\u6b63\u662f\u8981\u6349\u62ff\u4ed6\u7684\u5462\u3002\u201d \u3000\u3000\u738b\u4e94\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u90a3\u4ed6\u53ef\u662f\u81ea\u6295\u7f57\u7f51\u4e86\u3002\u4f46\u4e0d\u77e5\u53ef\u6709\u7528\u5f97\u7740\u6211\u4eec\u6548\u52b3\u4e4b\u5904\u4e48\uff1f\u201d \u3000\u3000\u7f57\u4fca\u6c89\u541f\u534a\u664c\uff0c\u8bf4\u9053\uff1a\u201c\u9633\u516c\u5b50\u7684\u610f\u601d\u662f\u4e0d\u60f3\u548c\u4ed6\u6597\u529b\uff0c\u800c\u662f\u548c\u4ed6\u4eec\u6597\u667a\u3002\u8981\u4e0d\u8d39\u5439\u7070 \u4e4b\u529b\u5c06\u4ed6\u6349\u62ff\u4e0b\u6765\uff0c\u4e0d\u8fc7\u662f\u5426\u80fd\u5982\u6240\u613f\uff0c\u5374\u672a\u53ef\u6599\u2026\u2026\u201d \u3000\u3000\u738b\u4e94\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6709\u5907\u65b9\u53ef\u65e0\u60a3\u3002\u82e5\u662f\u6587\u7684\u4e0d\u884c\uff0c\u6211\u4eec\u613f\u5145\u6253\u624b\u3002\u201d\u738b\u4e94\u56e0\u4e3a\u65b0\u6765\u6295 \u5954\uff0c\u6025\u6b32\u7acb\u529f\uff0c\u5fc3\u60f3\u891a\u4e91\u5cf0\u7eb5\u7136\u5389\u5bb3\uff0c\u4f46\u6709\u9633\u575a\u767d\u3001\u7aa6\u5b89\u5e73\u3001\u767d\u4e07\u96c4\u7b49\u4eba\u5728\u573a\uff0c\u83ab\u8bf4\u4e00\u4e2a\u891a \u4e91\u5cf0\uff0c\u518d\u591a\u4e00\u4e2a\u891a\u4e91\u5cf0\u4e5f\u53ef\u4ee5\u6536\u62fe\u5f97\u4e86\uff0c\u81ea\u5df1\u5e2e\u5fd9\u6253\u67b6\uff0c\u6599\u60f3\u6ca1\u6709\u4ec0\u4e48\u98ce\u9669\uff0c\u4f46\u5374\u53ef\u4ee5\u8868\u767d \u81ea\u5df1\u5bf9\u7aa6\u5b89\u5e73\u7684\u5fe0\u5fc3\uff0c\u4f55\u4e50\u4e0d\u4e3a\uff1f \u3000\u3000\u7f57\u4fca\u8bf4\u9053\uff1a\u201c\u4e94\u54e5\u7684\u597d\u610f\u6211\u5f88\u611f\u6fc0\uff0c\u4f46\u5374\u6709\u4e2a\u987e\u8651\u3002\u201d \u3000\u3000\u738b\u4e94\u9053\uff1a\u201c\u4ec0\u4e48\u987e\u8651\uff1f\u201d \u3000\u3000\u7f57\u4fca\u8bf4\u9053\uff1a\u201c\u5b9e\u4e0d\u76f8\u7792\uff0c\u6211\u4eec\u4e5f\u662f\u51c6\u5907\u6587\u7684\u4e0d\u6210\u5c31\u6765\u6b66\u7684\u3002\u6211\u4eec\u60f3\u5b89\u6392\u51e0\u540d\u597d\u624b\uff0c\u5192\u5145 \u4ec6\u5f79\uff0c\u5728\u65c1\u4f3a\u5019\uff0c\u5018\u82e5\u891a\u4e91\u5cf0\u4e0d\u4e2d\u8ba1\uff0c\u5c31\u5927\u4f19\u513f\u4e00\u62e5\u800c\u4e0a\uff0c\u5c06\u4ed6\u751f\u64d2\u3002\u4f46\u8fd9\u51e0\u540d\u597d\u624b\u5fc5\u987b\u662f \u5e73\u65e5\u6781\u5c11\u5728\u6c5f\u6e56\u9732\u9762\u4e4b\u4eba\uff0c\u5426\u5219\u53ea\u6015\u4f1a\u7ed9\u891a\u4e91\u5cf0\u770b\u7834\u3002\u201d \u3000\u3000\u738b\u4e94\u3001\u5f20\u516d\u3001\u674e\u4e03\u4e09\u4eba\u662f\u4e0d\u8ba4\u8bc6\u891a\u4e91\u5cf0\u7684\uff0c\u4f46\u5374\u4e0d\u77e5\u891a\u4e91\u5cf0\u662f\u5426\u8ba4\u8bc6\u4ed6\u4eec\uff0c\u56e0\u4e3a\u4ed6\u4eec\u5728 \u6c5f\u6e56\u4e0a\u65e9\u5df2\u662f\u6210\u540d\u4eba\u7269\u3002\u800c\u891a\u4e91\u5cf0\u8fc7\u53bb\u51e0\u5e74\u5728\u9633\u5929\u96f7\u624b\u4e0b\u505a\u7684\u5de5\u4f5c\uff0c\u5c31\u6b63\u662f\u4e13\u95e8\u7559\u5fc3\u6c5f\u6e56\u4eba \u7269\u7684\u884c\u52a8\u7684\uff0c\u662f\u4ee5\u672a\u5fc5\u4e0d\u4f1a\u8ba4\u8bc6\u4ed6\u4eec\u3002 \u3000\u3000\u6768\u5a49\u4e58\u673a\u8bf4\u9053\uff1a\u201c\u6211\u662f\u4e00\u4e2a\u65e0\u540d\u5c0f\u5352\uff0c\u8fd9\u5dee\u4f7f\u6b63\u597d\u7531\u6211\u62c5\u5f53\u3002\u201d \u3000\u3000\u738b\u4e94\u8bf4\u9053\uff1a\u201c\u662f\u554a\uff0c\u6768\u5144\u7684\u672c\u9886\u5f88\u662f\u4e0d\u9519\uff0c\u6211\u4eec\u4e0d\u65b9\u4fbf\u53bb\uff0c\u7531\u6768\u5144\u53bb\u4e5f\u662f\u4e00\u6837\u3002\u201d \u3000\u3000\u738b\u4e94\u5728\u90a3\u9152\u5e97\u4e2d\u89c1\u8bc6\u8fc7\u6768\u5a49\u7684\u672c\u9886\uff0c\u5f88\u60f3\u7b3c\u7edc\u4ed6\uff0c\u6545\u6b64\u6781\u529b\u63a8\u8350\u3002\u6768\u5a49\u662f\u4ed6\u5e26\u6765\u7684\u4eba\uff0c \u5018\u82e5\u7acb\u4e86\u529f\u52b3\uff0c\u4ed6\u7684\u9762\u4e0a\u4e5f\u6709\u5149\u5f69\u3002 \u3000\u3000\u7f57\u4fca\u770b\u4e86\u6768\u5a49\u4e00\u773c\uff0c\u8bf4\u9053\uff1a\u201c\u4e94\u54e5\u63a8\u8350\u6768\u5144\uff0c\u4e00\u5b9a\u4e0d\u4f1a\u9519\u3002\u201d\u5f53\u4e0b\u4f38\u51fa\u624b\u6765\u4e0e\u6768\u5a49\u76f8 \u63e1\u3002 \u3000\u3000\u6768\u5a49\u77e5\u9053\u7f57\u4fca\u662f\u5b58\u5fc3\u8bd5\u63a2\u5979\u7684\u529f\u592b\uff0c\u5047\u5982\u4e0d\u613f\u4e0e\u4ed6\u63e1\u624b\uff0c\u5f53\u4e0b\u5408\u638c\u4e00\u63d6\uff0c\u8bf4\u9053\uff1a\u201c\u591a\u627f \u5be8\u4e3b\u6298\u8282\u4e0b\u4ea4\uff0c\u5c0f\u53ef\u5374\u662f\u4e0d\u6562\u9ad8\u6500\u3002\u201d\u5979\u5408\u638c\u4f5c\u63d6\uff0c\u5341\u6307\u6307\u5c16\u5411\u524d\u4f38\u51fa\uff0c\u7f57\u4fca\u7684\u624b\u638c\u78b0\u7740\u5979 \u7684\u6307\u5c16\uff0c\u53ea\u89c9\u6d51\u8eab\u4e00\u9ebb\uff0c\u4e0d\u7531\u81ea\u5df1\u5730\u9000\u4e86\u4e24\u6b65\u3002\u539f\u6765\u5df2\u662f\u7ed9\u6768\u5a49\u7684\u6307\u5c16\u70b9\u4e86\u4ed6\u638c\u5fc3\u7684\u201c\u52b3\u5bab \u7a74\u201d\u3002\u5e78\u800c\u6768\u5a49\u7528\u7684\u4e0d\u662f\u91cd\u624b\u6cd5\u70b9\u7a74\uff0c\u5426\u5219\u7f57\u4fca\u5df1\u662f\u8981\u5f53\u573a\u6454\u5012\u3002 \u3000\u3000\u7f57\u4fca\u662f\u7ec3\u6709\u94c1\u7802\u638c\u529f\u592b\u7684\uff0c\u76ae\u7c97\u8089\u539a\uff0c\u60f3\u4e0d\u5230\u7ed9\u6768\u5a49\u7684\u5c0f\u6307\u8f7b\u8f7b\u4e00\u78b0\uff0c\u5c31\u7740\u4e86\u9053\u513f\uff0c\u4e0d \u7981\u5403\u4e86\u4e00\u60ca\uff0c\u968f\u5373\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6768\u5144\u597d\u529f\u592b\uff0c\u4e0d\u5fc5\u5ba2\u6c14\uff0c\u4e0d\u5fc5\u5ba2\u6c14\uff0c\u6768\u5144\u662f\u53bb\u5f97\u7684\u4e86\uff01\u201d\u5f53 \u4e0b\u53eb\u6768\u5a49\u6362\u8fc7\u55bd\u5175\u7684\u8863\u670d\uff0c\u4fbf\u5373\u5e26\u5979\u524d\u5f80\u3002 \u3000\u3000\u7f57\u4fca\u5e26\u9886\u6768\u5a49\u5230\u201c\u805a\u4e49\u5385\u201d\u524d\uff0c\u53ea\u89c1\u5df2\u6709\u51e0\u4e2a\u5192\u5145\u4ec6\u5f79\u7684\u597d\u624b\u5728\u90a3\u91cc\u7b49\u5019\u3002\u5c31\u5728\u6b64\u65f6\uff0c \u53ea\u542c\u5f97\u6709\u4eba\u53eb\u9053\uff1a\u201c\u8d35\u5ba2\u5230\uff01\u201d\u521a\u624d\u7ed9\u7f57\u4fca\u62a5\u8baf\u7684\u90a3\u4e2a\u5934\u76ee\uff0c\u5df2\u662f\u966a\u7740\u891a\u3001\u5b5f\u4e8c\u4eba\u6765\u4e86\u3002 \u3000\u3000\u7f57\u4fca\u628a\u624b\u4e00\u6446\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u5728\u9636\u4e0b\u4f3a\u5019\u3002\u201d\u5429\u5490\u4e86\u8fd9\u4e00\u53e5\u8bdd\uff0c\u4fbf\u5f84\u81ea\u4e0a\u524d\u8fce\u63a5\u2018\u8d35 \u5bbe\u2019\uff0c\u4e0d\u518d\u7406\u4f1a\u6768\u5a49\u4e86\uff01 \u3000\u3000\u539f\u6765\u7f57\u4fca\u867d\u7136\u77e5\u9053\u6768\u5a49\u672c\u9886\u4e86\u5f97\uff0c\u5374\u56e0\u4e3a\u5979\u6bd5\u7adf\u662f\u65b0\u6765\u4e4d\u5230\uff0c\u7f57\u4fca\u8fd8\u4e0d\u6562\u501a\u4e3a\u5fc3\u8179\uff0c\u662f \u4ee5\u53eb\u5979\u548c\u53e6\u5916\u51e0\u4e2a\u4eba\u5728\u9636\u4e0b\u201c\u4f3a\u5019\u201d\uff0c\u51c6\u5907\u5728\u5fc5\u8981\u4e4b\u65f6\u624d\u52a8\u7528\u4ed6\u4eec\uff0c\u5728\u5385\u5185\u201c\u4f3a\u5019\u201d\u7684\u201c\u4ec6 \u5f79\u201d\uff0c\u624d\u662f\u7aa6\u5b89\u5e73\u548c\u4ed6\u7684\u4eb2\u4fe1\u3002\u6768\u5a49\u9887\u611f\u4e0d\u5b89\uff0c\u4e0d\u8fc7\u5728\u9636\u4e0b\u4e5f\u53ef\u4ee5\u770b\u89c1\u201c\u805a\u4e49\u5385\u201d\u4e2d\u7684\u52a8 \u9759\uff0c\u53ea\u597d\u8ba9\u4ed6\u8fd9\u6837\u5b89\u6392\u4e86\u3002 \u3000\u3000\u4e14\u8bf4\u891a\u4e91\u5cf0\u9012\u8fdb\u62dc\u5323\uff0c\u679c\u7136\u5f97\u5230\u7aa6\u5b89\u5e73\u7684\u63a5\u7eb3\uff0c\u4ee5\u9686\u91cd\u7684\u793c\u8282\u62db\u5f85\uff0c\u5fc3\u4e2d\u751a\u4e3a\u9ad8\u5174\uff0c\u4ee5 \u4e3a\u662f\u5df2\u7136\u7792\u5f97\u8fc7\u53bb\uff0c\u4e8e\u662f\u6b22\u6b22\u559c\u559c\u7684\u548c\u5b5f\u660e\u971e\u8fdb\u6765\u62dc\u89c1\u7aa6\u5b89\u5e73\u3002\u8d70\u5230\u4e86\u201c\u805a\u4e49\u5385\u201d\u524d\uff0c\u5ffd\u89c1 \u9636\u524d\u6392\u5217\u7684\u55bd\u5175\u4e4b\u4e2d\uff0c\u6709\u4e00\u4e2a\u4eba\u4f3c\u66fe\u76f8\u8bc6\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u5728\u7405\u739b\u5c71\u7684\u90a3\u4e00\u665a\u662f\u66fe\u7ecf\u548c\u6768\u5a49\u4ea4\u8fc7\u624b\u7684\uff0c\u5f53\u65f6\u6768\u5a49\u4e5f\u662f\u8fd9\u6837\u55bd\u5175\u6253\u626e\uff0c\u8138\u4e0a\u4e5f \u662f\u643d\u4e86\u963f\u76d6\u6240\u7ed9\u7684\u8349\u836f\uff0c\u6539\u4e86\u5bb9\u7684\u3002\u56e0\u6b64\u891a\u4e91\u5cf0\u4ed4\u7ec6\u4e00\u770b\uff0c\u4f7f\u8ba4\u51fa\u4e86\u5979\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u5403\u4e86\u4e00\u60ca\uff0c\u8f7b\u8f7b\u5730\u78b0\u4e86\u5b5f\u660e\u971e\u4e00\u4e0b\uff0c\u5b5f\u660e\u971e\u4e5f\u6ce8\u610f\u5230\u4e86\u3002\u9636\u4e0b\u7684\u55bd\u5175\u7ad9\u8fc7\u4e24\u8fb9\uff0c \u6768\u5a49\u8fd8\u7279\u5730\u5f2f\u8170\uff0c\u8bf4\u4e86\u4e00\u4e2a\u201c\u8bf7\u201d\u5b57\u3002 \u3000\u3000\u5b5f\u660e\u971e\u5728\u7405\u739b\u5c71\u7684\u65f6\u5019\uff0c\u4e5f\u66fe\u89c1\u8fc7\u4e00\u6b21\u6768\u5a49\uff0c\u5f53\u65f6\u6ca1\u6709\u8ba4\u51fa\u662f\u5979\uff0c\u6b64\u9645\u542c\u5230\u5979\u7684\u58f0\u97f3\uff0c \u53c8\u7ed9\u891a\u4e91\u5cf0\u8f7b\u8f7b\u4e00\u78b0\uff0c\u767b\u65f6\u604d\u7136\u5927\u609f\uff0c\u77e5\u9053\u5b9a\u662f\u6768\u5a49\u65e0\u7591\u3002 \u3000\u3000\u5b5f\u660e\u971e\u771f\u662f\u53c8\u60ca\u53c8\u559c\uff0c\u60f3\u4e0d\u5230\u8e0f\u7834\u94c1\u978b\u65e0\u89c5\u5904\uff0c\u5f97\u6765\u5168\u4e0d\u8d39\u529f\u592b\uff0c\u4e00\u4e0a\u98de\u9f99\u5c71\uff0c\u672a\u66fe\u89c1 \u7740\u7aa6\u5b89\u5e73\uff0c\u5c31\u5148\u89c1\u7740\u4e86\u6768\u7897\u3002 \u3000\u3000\u891a\u3001\u5b5f\u4e8c\u4eba\u90fd\u662f\u7cbe\u660e\u80fd\u5e72\u7684\uff0c\u770b\u89c1\u6768\u5a49\u76ee\u5149\u6709\u5f02\uff0c\u800c\u4e14\u90a3\u4e2a\u201c\u8bf7\u201d\u5b57\uff0c\u4e5f\u4e0d\u5e94\u8be5\u7531\u4e00\u4e2a \u201c\u55bd\u5175\u201d\u8bf4\u51fa\u6765\u3002\u5979\u90a3\u6837\u5927\u80c6\uff0c\u505a\u51fa\u4e0e\u201c\u8eab\u4efd\u201d\u4e0d\u76f8\u79f0\u7684\u4e3e\u52a8\uff0c\u5b9a\u7136\u662f\u6709\u7528\u610f\u7684\u4e86\u3002 \u3000\u3000\u7f57\u4fca\u4e5f\u662f\u7a0d\u68a2\u8d77\u4e86\u4e00\u70b9\u7591\u5fc3\uff0c\u4e0d\u8fc7\u4e00\u6765\u56e0\u4e3a\u6768\u5a49\u662f\u738b\u4e94\u6781\u529b\u4fdd\u8350\u7684\u201c\u670b\u53cb\u201d\uff0c\u4ed6\u4fe1\u5f97\u8fc7 \u738b\u4e94\u7684\u5fe0\u8bda\uff0c\u7eb5\u7136\u6000\u7591\uff0c\u4e5f\u60f3\u4e0d\u5230\u6768\u5a49\u548c\u891a\u3001\u5b5f\u4e8c\u4eba\u662f\u65e9\u5df2\u76f8\u8bc6\u7684\uff1b\u4e8c\u6765\u6768\u5a49\u4e5f\u53ea\u662f\u8bf4\u4e86\u4e00 \u4e2a\u201c\u8bf7\u201d\u5b57\uff0c\u5e76\u6ca1\u6709\u5176\u4ed6\u5f02\u6837\u7684\u52a8\u4f5c\u3002\u7f57\u4fca\u4ee5\u4e3a\u5979\u662f\u4e00\u65f6\u5fd8\u4e86\u81ea\u5df1\u7684\u201c\u8eab\u4efd\u201d\uff0c\u5728\u8fd9\u79cd\u573a \u5408\uff0c\u60f3\u201c\u8868\u73b0\u201d\u4e00\u4e0b\u81ea\u5df1\u800c\u5df2\u3002\u56e0\u6b64\u5fc3\u4e2d\u867d\u7136\u7a0d\u611f\u4e0d\u6ee1\uff0c\u4e5f\u6ca1\u6709\u7279\u522b\u653e\u5728\u5fc3\u4e0a\u3002 \u3000\u3000\u891a\u4e91\u5cf0\uff0c\u5b5f\u660e\u971e\u5728\u7f57\u4fca\u7684\u966a\u540c\u4e4b\u4e0b\uff0c\u8e0f\u8fdb\u201c\u805a\u4e49\u5385\u201d\uff0c\u53ea\u89c1\u5df2\u6709\u4e24\u4eba\u5728\u5802\u4e2d\u76f8\u5019\u3002\u4e00\u4e2a \u662f\u9b41\u68a7\u7684\u6c49\u5b50\uff0c\u4e00\u4e2a\u662f\u767d\u7709\u9e70\u9f3b\u7684\u8001\u8005\u3002 \u3000\u3000\u90a3\u9b41\u68a7\u7684\u6c49\u5b50\u7ad9\u4e86\u8d77\u6765\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u7aa6\u67d0\u4e0d\u624d\uff0c\u5f97\u891a\u5144\u3001\u5b5f\u5144\u5149\u4e34\u655d\u5be8\uff0c\u5e78\u4f55\u5982\u4e4b\u3002 \u62db\u5f85\u4e0d\u7528\uff0c\u5c1a\u7948\u6055\u7f6a\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u77e5\u9053\u6b64\u4eba\u4fbf\u662f\u98de\u9f99\u5c71\u7684\u7aa6\u5b89\u5e73\uff0c\u4fbf\u4e5f\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u90fd\u662f\u81ea\u5df1\u4eba\uff0c\u7aa6\u5be8\u4e3b\u5ba2\u6c14\u4ec0 \u4e48\uff1f\u201d \u3000\u3000\u7aa6\u5b89\u5e73\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u891a\u5144\u60f3\u5fc5\u77e5\u9053\u6211\u4e0e\u4ee4\u5e08\u4f2f\u7684\u5173\u7cfb\u3002\u54b1\u4eec\u5c31\u597d\u6bd4\u4e00\u5bb6\u4eba\u4e00\u6837\uff0c\u4ee5\u540e \u8fd8\u5f97\u591a\u591a\u4ef0\u4ed7\u891a\u5144\u7684\u63d0\u643a\u5462\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u7aa6\u5be8\u4e3b\u8fd9\u4e48\u8bf4\u53ef\u662f\u89c1\u5916\u4e86\u3002\u65e2\u7136\u662f\u4e00\u5bb6\u4eba\uff0c\u8fd8\u8bf4\u5f97\u5230\u4ec0\u4e48\u63d0\u643a\u4e0d\u63d0\u643a \u7684\uff1f\u201d \u3000\u3000\u7aa6\u5b89\u5e73\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u891a\u5144\u8bf4\u5f97\u4e0d\u9519\uff0c\u7aa6\u67d0\u5931\u8a00\u4e86\u3002\u8fd9\u4f4d\u5b5f\u5144\u5f1f\u662f\u2014\u2014\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u8fd9\u4f4d\u5b5f\u5144\u5f1f\u662f\u65b0\u6765\u7684\u4eba\uff0c\u655d\u5e08\u4f2f\u53eb\u6211\u5e26\u4ed6\u5728\u6c5f\u6e56\u5386\u7ec3\uff0c\u662f\u4ee5\u8fd9\u6b21\u4e0e\u4ed6\u540c \u6765\uff0c\u597d\u8ba9\u4ed6\u8d81\u6b64\u673a\u4f1a\uff0c\u591a\u7ed3\u8bc6\u7ed3\u8bc6\u5404\u65b9\u7684\u670b\u53cb\u3002\u201d \u3000\u3000\u7aa6\u5b89\u5e73\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u90fd\u662f\u81ea\u5df1\u4eba\u4e86\uff0c\u4e0d\u5fc5\u5ba2\u6c14\uff0c\u8bf7\u5750\uff0c\u8bf7\u5750\u3002\u201d \u3000\u3000\u7aa6\u5b89\u5e73\u4e0e\u4ed6\u4eec\u5bd2\u6684\u8fc7\u540e\uff0c\u8fd9\u624d\u4ecb\u7ecd\u90a3\u4f4d\u767d\u7709\u9e70\u66b4\u7684\u8001\u8005\u4e0e\u891a\u3001\u5b5f\u4e8c\u4eba\u76f8\u8bc6\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9 \u4f4d\u662f\u4e8c\u5341\u5e74\u524d\u540d\u9707\u6c5f\u6e56\u7684\u767d\u4e07\u96c4\u767d\u8001\u524d\u8f88\uff0c\u891a\u5144\u3001\u5b5f\u5144\u60f3\u5fc5\u77e5\u9053\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u6697\u6697\u5403\u4e86\u4e00\u60ca\uff0c\u60f3\u9053\uff1a\u201c\u542c\u8bf4\u767d\u4e07\u96c4\u5f53\u5e74\u662f\u4e0e\u6df3\u4e8e\u5468\u9f50\u540d\u7684\u9ed1\u9053\u4eba\u7269\uff0c\u53ea\u56e0\u8d25\u5728 \u5c60\u767e\u57ce\u624b\u4e0b\uff0c\u8fd9\u624d\u91d1\u76c6\u6d17\u624b\u7684\u3002\u6709\u6b64\u4eba\u5728\u6b64\uff0c\u5012\u662f\u8981\u591a\u52a0\u4e00\u70b9\u5c0f\u5fc3\u3002\u201d\u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u4e45\u4ef0\u767d \u8001\u82f1\u96c4\u7f8e\u540d\uff0c\u60f3\u4e0d\u5230\u5728\u8fd9\u91cc\u76f8\u4f1a\uff0c\u8bf7\u6055\u5931\u793c\u3002\u201d \u3000\u3000\u767d\u4e07\u96c4\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u8001\u4e86\uff0c\u4e0d\u4e2d\u7528\u4e86\u3002\u891a\u5144\uff0c\u8bf4\u53e5\u8001\u5b9e\u8bdd\uff0c\u8fd9\u91cc\u7684\u4e8b\uff0c\u8fd8\u5f97\u8bf7\u4f60\u591a\u5e2e\u5fd9 \u5462\u3002\u201d \u3000\u3000\u7aa6\u5b89\u5e73\u7b11\u9053\uff1a\u201c\u90fd\u662f\u81ea\u5df1\u4eba\uff0c\u5927\u5bb6\u4e0d\u5fc5\u8fc7\u5206\u5ba2\u6c14\u4e86\u3002\u8bf7\u5750\u4e0b\u6765\u8bf4\u8bdd\u5427\u3002\u201d \u3000\u3000\u5750\u5b9a\u4e4b\u540e\uff0c\u4e00\u4e2a\u55bd\u5175\u7aef\u7740\u6258\u76d8\uff0c\u6367\u4e86\u51e0\u676f\u8336\u51fa\u6765\uff0c\u4f9d\u6b21\u653e\u5728\u891a\u4e91\u5cf0\u3001\u5b5f\u660e\u971e\u3001\u7aa6\u5b89\u5e73\u3001 \u7f57\u4fca\u548c\u767d\u4e07\u96c4\u7684\u9762\u524d\u3002\u770b\u4ed6\u5c0f\u5fc3\u7ffc\u7ffc\u7684\u6837\u5b50\uff0c\u597d\u50cf\u751f\u6015\u6e85\u51fa\u4e86\u4e00\u70b9\u8336\u6c34\u4f3c\u7684\u3002\u539f\u6765\u7ed9\u891a\u3001\u4e8c \u5b5f\u4e8c\u4eba\u7684\u90a3\u4e24\u676f\u8336\uff0c\u4e43\u662f\u653e\u4e86\u201c\u9165\u9aa8\u6563\u201d\u7684\u6bd2\u8336\u3002\u8fd9\u4e2a\u201c\u55bd\u5175\u201d\u4e5f\u662f\u7aa6\u5b89\u5e73\u7684\u5fc3\u8179\u4f2a\u88c5\u7684\u3002 \u4ed6\u8fd9\u6837\u5c0f\u5fc3\u7ffc\u7ffc\uff0c\u5e76\u975e\u662f\u6015\u6cfc\u6cfb\uff0c\u800c\u662f\u6015\u653e\u9519\u4e86\u8336\u676f\u3002 \u3000\u3000\u7aa6\u5b89\u5e73\u7aef\u8d77\u8336\u676f\uff0c\u8bf4\u4e86\u4e00\u4e2a\u201c\u8bf7\u201d\u5b57\uff0c\u767d\u4e07\u96c4\u3001\u7f57\u4fca\u76f8\u7ee7\u7aef\u8d77\u4e86\u8336\u676f\uff0c\u5b5f\u660e\u971e\u628a\u773c\u5411\u891a \u4e91\u5cf0\u671b\u53bb\uff0c\u53ea\u89c1\u891a\u4e91\u5cf0\u82e5\u65e0\u5176\u4e8b\u5730\u4e5f\u62ff\u8d77\u4e86\u8336\u676f\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u628a\u8336\u676f\u51d1\u5230\u5634\u8fb9\uff0c\u55c5\u4e86\u4e00\u55c5\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u8336\u597d\u9999\uff01\u201d\u5b5f\u660e\u971e\u5fc3\u4e2d\u4e00\u52a8\uff0c\u4e5f\u5b66\u4ed6\u7684 \u6837\u5b50\uff0c\u628a\u8336\u676f\u62ff\u5728\u624b\u4e2d\uff0c\u5e76\u4e0d\u5c31\u559d\u3002 \u3000\u3000\u7aa6\u5b89\u5e73\u9053\uff1a\u201c\u8fd9\u662f\u98de\u9f99\u5c71\u7279\u4ea7\u7684\u4e91\u96fe\u8336\uff0c\u6700\u5b9c\u8d81\u70ed\u559d\u4e86\u7684\u597d\u3002\u4e24\u4f4d\u559d\u8fc7\u4e86\u8336\uff0c\u54b1\u4eec\u518d\u6765 \u559d\u9152\u3002\u201d \u3000\u3000\u4e14\u8bf4\u6768\u5a49\u5728\u9636\u4e0b\u5077\u7aa5\uff0c\u4e0d\u89c1\u9633\u575a\u767d\u5728\u573a\uff0c\u5df2\u77e5\u4ed6\u4eec\u5b9a\u662f\u8981\u7528\u9634\u8c0b\u6765\u5bf9\u4ed8\u891a\u4e91\u5cf0\uff0c\u6b64\u65f6\u770b \u89c1\u891a\u4e91\u5cf0\u548c\u5b5f\u660e\u971e\u5c31\u8981\u559d\u8336\uff0c\u4e0d\u7531\u5f97\u5fc3\u91cc\u4e00\u60ca\uff0c\u60f3\u9053\uff1a\u201c\u4ed6\u4eec\u8fd9\u4e24\u676f\u8336\u91cc\u5b9a\u6709\u53e4\u602a\uff0c\u6211\u53ef\u5f97 \u63d0\u9192\u4ed6\u4eec\u624d\u597d\uff01\u201d\u5fc3\u4e2d\u7740\u6025\uff0c\u6839\u672c\u6ca1\u6709\u60f3\u5230\u81ea\u8eab\u5371\u9669\uff0c\u4fbf\u8f7b\u8f7b\u5730\u54b3\u4e86\u4e00\u58f0\u3002\u8fd9\u4e00\u58f0\u54b3\u55fd\uff0c\u7528 \u7684\u4e43\u662f\u201c\u4f20\u97f3\u5165\u5bc6\u201d\u7684\u529f\u592b\uff0c\u9636\u4e0b\u4e00\u58f0\u54b3\uff0c\u5802\u4e0a\u8bf8\u4eba\u90fd\u542c\u5f97\u6e05\u6e05\u695a\u695a\u3002 \u3000\u3000\u867d\u7136\u542c\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u4f46\u56e0\u4ed6\u4eec\u7684\u6ce8\u610f\u529b\u90fd\u662f\u96c6\u4e2d\u5728\u891a\u4e91\u5cf0\u548c\u5b5f\u660e\u971e\u7684\u8eab\u4e0a\uff0c\u5374\u6ca1\u6709\u53d1\u73b0 \u54b3\u55fd\u7684\u662f\u8c01\u3002 \u3000\u3000\u5176\u5b9e\u65e0\u987b\u6768\u5a49\u7ed9\u4ed6\u4eec\u63d0\u9192\uff0c\u891a\u3001\u5b5f\u4e8c\u4eba\u5df2\u662f\u5bf9\u8fd9\u676f\u8336\u8d77\u4e86\u7591\u5fc3\u3002\u6b64\u65f6\u542c\u5f97\u6768\u5a49\u54b3\u58f0\uff0c\u5b5f \u660e\u971e\u88c5\u4f5c\u7a81\u7136\u5403\u4e86\u4e00\u60ca\u7684\u6837\u5b50\uff0c\u5931\u624b\u6253\u843d\u4e86\u8336\u676f\u3002 \u3000\u3000\u5c31\u5728\u8336\u676f\u5c06\u8981\u89e6\u5730\u4e4b\u9645\uff0c\u891a\u4e91\u5cf0\u6454\u8896\u4e00\u5377\uff0c\u6070\u597d\u628a\u8336\u676f\u515c\u4f4f\u3002\u8336\u676f\u6ca1\u6709\u6253\u70c2\uff0c\u53ef\u662f\u676f\u4e2d \u7684\u70ed\u8336\u5df2\u6cfc\u6d12\u4e86\u3002\u6cfc\u51fa\u6765\u7684\u70ed\u8336\uff0c\u5728\u4ed6\u8896\u98ce\u6fc0\u8361\u4e4b\u4e0b\uff0c\u5c31\u50cf\u96e8\u70b9\u4e00\u822c\uff0c\u4ece\u534a\u7a7a\u4e2d\u6d12\u4e0b\u6765\uff0c\u6070 \u597d\u4e5f\u662f\u9152\u5728\u5404\u4eba\u7684\u8336\u676f\u91cc\u3002\u4e0d\u7528\u8bf4\u8fd9\u662f\u891a\u4e91\u5cf0\u6709\u610f\u663e\u9732\u7684\u4e00\u624b\u4e0a\u4e58\u529f\u592b\u4e86\u3002 \u3000\u3000\u5b5f\u660e\u971e\u4f5c\u51fa\u5341\u5206\u60f6\u6050\u7684\u6a21\u6837\uff0c\u6413\u624b\u8bb7\u8bb7\u8bf4\u9053\uff1a\u201c\u771f\u662f\u5bf9\u4e0d\u4f4f\u4e86\uff0c\u7cdf\u8e4b\u4e86\u7aa6\u5be8\u4e3b\u7684\u9999\u8336\u3002 \u5931\u4eea\u4e4b\u7f6a\uff0c\u8bf7\u5be8\u4e3b\u83ab\u602a\u3002\u201d\u891a\u4e91\u5cf0\u4e5f\u4f6f\u4f5c\u53d1\u6012\u9053\uff1a\u201c\u4f60\u8fd9\u4eba\u771f\u662f\u4e2a\u521d\u51fa\u9053\u7684\u96cf\u513f\uff0c\u89c1\u4e0d\u5f97\u5927 \u573a\u9762\u3002\u201d \u3000\u3000\u7aa6\u5b89\u5e73\u77e5\u9053\u5df2\u7ed9\u4ed6\u4eec\u770b\u7834\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u6050\u6015\u4f60\u4eec\u662f\u4e0d\u653e\u5fc3\u559d\u6211\u7684\u8336\u5427\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u7aef\u8d77\u4e86\u8336\u676f\uff0c\u8bf4\u9053\uff1a\u201c\u5be8\u4e3b\u591a\u7591\u4e86\uff0c\u54b1\u4eec\u90fd\u662f\u81ea\u5df1\u4eba\u554a\uff0c\u6709\u4ec0\u4e48\u4e0d\u653e\u5fc3\u7684\uff1f\u4f46\u5be8 \u4e3b\u65e2\u7136\u8fd9\u4e48\u8bf4\uff0c\u54b1\u4eec\u5c31\u5927\u5bb6\u90fd\u559d\u4e86\u5427\u3002\u8bf7\uff0c\u8bf7\uff0c\u8bf7\u5440\uff01\u201d \u3000\u3000\u672c\u6765\u662f\u7aa6\u5b89\u5e73\u5411\u4ed6\u4eec\u656c\u8336\u7684\uff0c\u5982\u4eca\u891a\u4e91\u5cf0\u7a81\u7136\u53cd\u5ba2\u4e3a\u4e3b\uff0c\u8bf7\u4e3b\u65b9\u7684\u4e09\u4eba\u4e00\u540c\u559d\u8336\uff0c\u8fd9\u4e48 \u4e00\u6765\uff0c\u767b\u65f6\u4ee4\u5f97\u7aa6\u5b89\u5e73\u624b\u8db3\u65e0\u63aa\uff0c\u4e0d\u77e5\u5982\u4f55\u662f\u597d\uff0c\u8981\u77e5\u4ed6\u4eec\u7684\u8336\u676f\u91cc\u6b64\u65f6\u4ea6\u5df2\u6709\u4e86\u6bd2\u836f\uff0c\u5982 \u4f55\u6562\u559d\uff1f \u3000\u3000\u9633\u575a\u767d\u5728\u91cc\u9762\u5e03\u7f6e\u4e00\u5207\uff0c\u521a\u624d\u770b\u89c1\u891a\u3001\u5b5f\u4e8c\u4eba\u7aef\u8d77\u8336\u676f\u7684\u65f6\u5019\uff0c\u5fc3\u4e2d\u5341\u5206\u5f97\u610f\uff0c\u4e0d\u6599\u51fa \u4e86\u8fd9\u4e2a\u7ed3\u679c\uff0c\u773c\u770b\u7aa6\u5b89\u5e73\u96be\u4ee5\u4e0b\u573a\uff0c\u9633\u575a\u767d\u53ea\u597d\u633a\u8eab\u800c\u51fa\uff0c\u51c6\u5907\u6309\u7167\u539f\u6765\u7684\u8ba1\u5212\uff0c\u6587\u7684\u4e0d \u6210\uff0c\u5c31\u6765\u6b66\u7684\u3002 \u3000\u3000\u9633\u575a\u767d\u8d70\u51fa\u5927\u5802\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u891a\u4e91\u5cf0\uff0c\u6211\u5f88\u4f69\u670d\u4f60\u6709\u8fd9\u80c6\u91cf\u6765\u5230\u8fd9\u91cc\u3002\u54b1\u4eec\u5c31\u6253\u5f00\u5929 \u7a97\u8bf4\u4eae\u8bdd\u5427\u3002\u201d \u3000\u3000\u7aa6\u5b89\u5e73\u8bf4\u9053\uff1a\u201c\u5bf9\uff0c\u5bf9\u3002\u4f60\u4eec\u5e08\u5144\u5f1f\u662f\u4e00\u5bb6\u4eba\uff0c\u628a\u8bdd\u8bf4\u5f00\u4e86\uff0c\u5927\u5bb6\u90fd\u597d\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u4f60\u8981\u6211\u8bf4\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u9633\u575a\u767d\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u6765\u8fd9\u91cc\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u4f60\u53c8\u6765\u8fd9\u91cc\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u9633\u575a\u767d\u9053\uff1a\u201c\u891a\u4e91\u5cf0\uff0c\u4f60\u522b\u88c5\u849c\u4e86\u3002\u54b1\u4eec\u6253\u5f00\u6765\u8bf4\u5427\uff0c\u4f60\u5982\u4eca\u5df2\u662f\u843d\u5728\u6211\u4eec\u624b\u4e0a\uff0c\u5018\u82e5 \u4f60\u613f\u610f\u653e\u5f03\u6363\u4e71\u7684\u4f01\u56fe\uff0c\u7ad9\u8fc7\u6211\u4eec\u8fd9\u4e00\u8fb9\u6765\uff0c\u90a3\u4e48\u4f60\u6211\u8fd8\u662f\u5e08\u5144\u8363\u3002\u201d \u3000\u3000\u9633\u575a\u767d\u4e00\u6765\u662f\u987e\u5fcc\u891a\u4e91\u5cf0\u4e0e\u5b5f\u660e\u971e\u7684\u672c\u9886\uff0c\u53ea\u6015\u52a8\u8d77\u624b\u6765\uff0c\u7eb5\u7136\u80fd\u591f\u501a\u4ed7\u4eba\u591a\u64d2\u83b7\u4ed6 \u4eec\uff0c\u81ea\u5df1\u4e5f\u96be\u514d\u53d7\u4f24\uff1b\u4e8c\u6765\u8fd8\u6709\u66f4\u91cd\u8981\u7684\u654c\u4eba\uff0c\u5b5f\u5c11\u521a\u4e0e\u674e\u601d\u5357\uff0c\u968f\u65f6\u53ef\u80fd\u6765\u5230\uff0c\u5927\u654c\u5f53 \u524d\uff0c\u5c0f\u4e0d\u5fcd\u5219\u4e71\u5927\u8c0b\u3002\u662f\u4ee5\u4ed6\u8981\u5148\u8bd5\u63a2\u4e00\u4e0b\uff0c\u8bd5\u63a2\u662f\u5426\u53ef\u4ee5\u62db\u964d\u891a\u4e91\u5cf0\u3002\u82e5\u4e0d\u6210\u529f\uff0c\u90a3\u65f6\u5c31 \u53ea\u597d\u52a8\u624b\u4e86\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u6253\u4e86\u4e00\u4e2a\u54c8\u54c8\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u5927\u660e\u767d\u4f60\u7684\u610f\u601d\uff0c\u4f60\u5148\u8bf4\u8bf4\u4f60\u548c\u7aa6\u5be8\u4e3b\u7a76\u7adf\u662f\u5e72\u4e9b \u4ec0\u4e48\uff0c\u8981\u628a\u6211\u62c9\u8fc7\u4f60\u4eec\u90a3\u4e00\u8fb9\u53bb\u3002\u201d\u891a\u4e91\u5cf0\u4e5f\u4e0d\u60f3\u5373\u65f6\u52a8\u624b\uff0c\u5e0c\u671b\u80fd\u591f\u62d6\u5f97\u4e00\u65f6\u5c31\u4e00\u65f6\u3002 \u3000\u3000\u7aa6\u5b89\u5e73\u628a\u7f57\u4fca\u6084\u6084\u62c9\u8fc7\u4e00\u8fb9\uff0c\u8bf4\u9053\uff1a\u201c\u521a\u624d\u90a3\u58f0\u54b3\u55fd\u5f88\u662f\u53ef\u7591\uff0c\u4f60\u51fa\u53bb\u67e5\u4e2a\u660e\u767d\uff0c\u770b\u770b \u662f\u54ea\u4e00\u4e2a\uff1f\u201d \u3000\u3000\u9633\u575a\u767d\u4e5f\u662f\u4e2a\u673a\u8b66\u7684\u4eba\uff0c\u89c1\u891a\u4e91\u5cf0\u6545\u610f\u8bf4\u5f97\u7f20\u5939\u4e0d\u6e05\uff0c\u767b\u65f6\u7701\u609f\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u83ab\u975e\u4ed6 \u6043\u7740\u5f3a\u63f4\u5728\u540e\uff0c\u7528\u7684\u662f\u7f13\u5175\u4e4b\u8ba1\uff1f\u201d\u5f53\u4e0b\u4e00\u58f0\u51b7\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u891a\u4e91\u5cf0\uff0c\u4f60\u522b\u5728\u6211\u7684\u9762\u524d\u800d\u82b1 \u67aa\u4e86\uff0c\u4f60\u8981\u514d\u6b7b\u7684\u8bdd\uff0c\u628a\u8fd9\u676f\u8336\u559d\u4e0b\u53bb\uff0c\u8fd9\u676f\u8336\u53ea\u662f\u4ee4\u4f60\u6682\u65f6\u5931\u6389\u6b66\u529f\uff0c\u5e76\u975e\u53d6\u4f60\u6027\u547d\u3002\u5f85 \u98de\u9f99\u5c71\u7684\u4e8b\u60c5\u4e86\u7ed3\uff0c\u6211\u9001\u4f60\u53bb\u89c1\u7239\u7239\u3002\u53ea\u8981\u4f60\u628a\u548c\u6211\u7239\u7239\u4f5c\u5bf9\u7684\u4eba\u4e00\u4e00\u62db\u4f9b\u51fa\u6765\uff0c\u6211\u8d70\u7136\u4e3a \u4f60\u6c42\u60c5\uff0c\u4ece\u8f7b\u53d1\u843d\u3002\u201d \u3000\u3000\u891a\u53bb\u5cf0\u7b11\u9053\uff1a\u201c\u597d\uff0c\u6211\u672c\u6765\u5c31\u662f\u8bf7\u4f60\u4eec\u559d\u8336\u7684\uff0c\u5927\u5bb6\u559d\u5440\uff01\u201d \u3000\u3000\u773c\u770b\u53cc\u65b9\u5251\u62d4\u5f29\u5f20\uff0c\u5c31\u8981\u52a8\u624b\uff0c\u800c\u7f57\u4fca\u4ea6\u5df2\u8d70\u5230\u4e86\u6768\u5a49\u7b49\u4eba\u9762\u524d\uff0c\u5c31\u8981\u8fdb\u884c\u76d8\u95ee\u4e86\u3002\u5c31 \u5728\u6b64\u65f6\uff0c\u5ffd\u542c\u5f97\u8f66\u58f0\u8f9a\u8f9a\uff0c\u8e44\u58f0\u5f97\u5f97\uff0c\u4e00\u8f86\u9aa1\u8f66\uff0c\u76f4\u9a76\u8fdb\u6765\uff0c\u5230\u4e86\u805a\u4e49\u5385\u524d\u9762\u7684\u6f14\u6b66\u573a\u65b9\u59cb \u505c\u4e0b\u3002\u966a\u540c\u8fdb\u6765\u7684\u51e0\u4e2a\u5934\u76ee\u5927\u58f0\u62a5\u9053\uff1a\u201c\u9ed1\u77f3\u9053\u957f\u5230\uff01\u201d \u3000\u3000\u539f\u6765\u9a91\u9a6c\u56de\u5c71\u62a5\u4fe1\u7684\u90a3\u4e2a\u7aa6\u65fa\uff0c\u56e0\u4e3a\u6454\u5f97\u5f88\u91cd\uff0c\u5c3b\u9aa8\u7834\u88c2\uff0c\u9a91\u4e0a\u9a6c\u9791\uff0c\u75db\u4e0d\u53ef\u5f53\uff0c\u53ea\u597d \u4e0b\u9a6c\u6b65\u884c\u3002\u7ed3\u679c\u7ed9\u9ed1\u77f3\u9053\u4eba\u8fd9\u8f86\u9aa1\u8f66\u540e\u6765\u5c45\u4e0a\uff0c\u5148\u5230\u5c71\u5be8\u3002 \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u662f\u7aa6\u5b89\u5e73\u7684\u7ed3\u4e49\u5144\u957f\uff0c\u98de\u9f99\u5c71\u4e0a\u4e0b\u4eba\u7b49\u90fd\u8ba4\u5f97\u4ed6\uff0c\u4e5f\u77e5\u9053\u4ed6\u662f\u9001\u4e25\u58f0\u6d9b\u7684\u5973\u513f \u6765\u7684\uff0c\u56e0\u6b64\u5f53\u7136\u4e0d\u4f1a\u963b\u62e6\u4ed6\u4e86\u3002 \u3000\u3000\u7f57\u4fca\u53c8\u60ca\u53c8\u559c\uff0c\u8fde\u5fd9\u4e0a\u524d\u8fce\u63a5\uff0c\u987e\u4e0d\u5f97\u518d\u76d8\u95ee\u6768\u5a49\u4e86\u3002 \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u63ed\u5f00\u8f66\u5e18\uff0c\u88c5\u6a21\u4f5c\u88c5\u5730\u559d\u9053\uff1a\u201c\u5c0f\u59d0\uff0c\u8bf7\u4e0b\u6765\u5427\u3002\u9053\u7237\u670d\u4f8d\u4f60\u4e5f\u670d\u5f85\u5f97\u591f \u4e86\u3002\u201d\u7f57\u4fca\u7b11\u9053\uff1a\u201c\u522b\u5413\u574f\u4e86\u5979\u3002\u201d \u3000\u3000\u4e25\u70f7\u88c5\u4f5c\u77eb\u8eaf\u4e4f\u529b\u7684\u6a21\u6837\uff0c\u53c8\u60ca\u53c8\u6012\u5730\u8d70\u4e0b\u9aa1\u8f66\uff0c\u6084\u58f0\u8bf4\u9053\uff1a\u201c\u4e0d\u8bb8\u4f60\u4eec\u78b0\u6211\uff0c\u8c01\u6562\u78b0 \u6211\u4e00\u4e0b\uff0c\u6211\u5c31\u548c\u4ed6\u62fc\u4e86\u3002\u201d \u3000\u3000\u7f57\u4fca\u672c\u6765\u662f\u4e2a\u6b66\u5b66\u884c\u5bb6\uff0c\u53ef\u662f\u56e0\u4e3a\u4ed6\u77e5\u9053\u4e25\u70f7\u662f\u4e2d\u4e86\u9165\u9aa8\u6563\u7684\u6bd2\u7684\uff0c\u5374\u5e76\u4e0d\u77e5\u9053\u9ed1\u77f3\u9053 \u4eba\u540e\u6765\u628a\u89e3\u836f\u7ed9\u4e86\u5979\uff0c\u800c\u4e14\u4e25\u70f7\u53c8\u88c5\u5f97\u5f88\u50cf\uff0c\u7f57\u4fca\u4e00\u70b9\u4e5f\u6ca1\u7591\u5fc3\u3002 \u3000\u3000\u7f57\u4fca\u7b11\u9053\uff1a\u201c\u4e25\u5c0f\u59d0\uff0c\u4f60\u653e\u5fc3\uff0c\u6211\u4eec\u7edd\u4e0d\u4f1a\u5bf9\u4f60\u65e0\u793c\u7684\uff0c\u8bf7\u8fdb\u6765\u5427\u3002\u201d\u9165\u9aa8\u6563\u7684\u836f\u529b\u80fd \u4ee4\u4eba\u7b4b\u75b2\u9aa8\u8f6f\uff0c\u591a\u597d\u6b66\u529f\u4e5f\u4f7f\u4e0d\u51fa\u6765\uff0c\u4f46\u8d70\u51e0\u6b65\u8def\u8fd8\u662f\u80fd\u591f\u7684\u3002\u7f57\u4fca\u4e3a\u4e86\u8868\u793a\u5c0a\u91cd\u5979\uff0c\u679c\u7136 \u8d70\u5f97\u79bb\u5979\u8fdc\u8fdc\u7684\uff0c\u8fde\u624b\u6307\u4e5f\u6ca1\u78b0\u5979\u4e00\u4e0b\u3002 \u3000\u3000\u8c37\u6db5\u865a\u8ddf\u7740\u4e0b\u8f66\uff0c\u7f57\u4fca\u95ee\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u2014\u2014\u201d\u9ed1\u77f3\u9053\u4eba\u9053\uff1a\u201c\u8fd9\u4f4d\u8c37\u5144\u662f\u6211\u65b0\u4ea4\u7684\u670b \u53cb\uff0c\u867d\u5c5e\u65b0\u4ea4\uff0c\u5374\u662f\u4e00\u89c1\u5982\u6545\u3002\u6211\u77e5\u9053\u4f60\u4eec\u6b63\u9700\u8981\u6709\u672c\u9886\u7684\u4eba\uff0c\u6240\u4ee5\u6211\u548c\u4ed6\u6765\u4e86\u3002\u201d \u3000\u3000\u7f57\u4fca\u8bf4\u9053\uff1a\u201c\u8c37\u5144\u5149\u4e34\uff0c\u655d\u5be8\u6df1\u611f\u8363\u5e78\uff0c\u8bf7\u8c37\u5144\u7a0d\u6b47\u4e00\u4f1a\uff0c\u7aa6\u5be8\u4e3b\u518d\u4e0e\u8c37\u5144\u6df1\u8c08\u3002\u201d\u8981 \u77e5\u7aa6\u5b89\u5e73\u5728\u201c\u805a\u4e49\u5385\u201d\u4e2d\u6b63\u6709\u7740\u5927\u4e8b\u5904\u7406\uff0c\u9ed1\u77f3\u9053\u4eba\u8fdb\u53bb\u65e0\u59a8\uff0c\u8c37\u6db5\u8654\u7684\u5e95\u7ec6\u7f57\u4fca\u5c1a\u672a\u77e5 \u9053\uff0c\u800c\u4e14\u9ed1\u77f3\u9053\u4eba\u4e5f\u8bf4\u4ed6\u53ea\u662f\u65b0\u4ea4\uff0c\u7f57\u4fca\u81ea\u7136\u662f\u4e0d\u6562\u653e\u4ed6\u8fdb\u53bb\u3002 \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u7709\u5934\u4e00\u76b1\uff0c\u60f3\u8981\u8bf4\u8bdd\uff0c\u8c37\u6db5\u865a\u5374\u9053\uff1a\u201c\u4e0d\u7528\u5ba2\u6c14\uff0c\u6211\u5728\u8fd9\u91cc\u7b49\u5019\u7aa6\u5be8\u4e3b\u53ec\u89c1\u5c31 \u662f\u4e86\u3002\u201d\u5fc3\u60f3\uff1a\u201c\u5df2\u7136\u6df7\u8fdb\u6765\u4e86\uff0c\u4e0d\u5fc5\u6025\u5728\u8fd9\u65f6\u3002\u201d\u9ed1\u77f3\u9053\u4eba\u542c\u4ed6\u8fd9\u4e48\u4e00\u8bf4\uff0c\u4e5f\u6015\u5f15\u8d77\u7f57\u4fca \u7684\u7591\u5fc3\uff0c\u7834\u574f\u4e86\u539f\u5b9a\u7684\u8ba1\u5212\uff0c\u4fbf\u4e5f\u4e0d\u518d\u8bf4\u4e86\u3002\u7f57\u4fca\u9053\uff1a\u201c\u4f60\u4eec\u597d\u597d\u62db\u547c\u8fd9\u4f4d\u8c37\u7237\u3002\u201d\u5f53\u4e0b\u4fbf \u4e0e\u9ed1\u77f3\u9053\u4eba\u4e00\u524d\u4e00\u540e\u7684\u5c06\u4e25\u70f7\u5939\u5728\u4e2d\u95f4\uff0c\u8d70\u8fdb\u5927\u5385\u3002 \u3000\u3000\u90a3\u51e0\u4e2a\u5934\u76ee\u8fc7\u6765\u62db\u547c\u8c37\u6db5\u865a\u8fdb\u5ba2\u623f\u6b47\u606f\uff0c\u8c37\u6db5\u8654\u53cc\u773c\u4e00\u7ffb\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u7528\uff0c\u6211\u5c31\u5728\u8fd9\u91cc \u6b47\u6b47\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u76f8\u8c8c\u5947\u4e11\uff0c\u7ffb\u8d77\u4e00\u53cc\u602a\u773c\u51b7\u51b0\u51b0\u5730\u8bf4\u8bdd\uff0c\u628a\u90a3\u51e0\u4e2a\u5934\u76ee\u5413\u4e86\u4e00\u8df3\u3002\u6709\u4e00\u4e2a\u5934\u76ee\u5927 \u7740\u80c6\u5b50\u8bf4\u9053\uff1a\u201c\u4e8c\u5be8\u4e3b\u5429\u5490\uff0c\u8bf7\u8c37\u7237\u8fd8\u662f\u8fdb\u5ba2\u623f\u6b47\u6b47\u7684\u597d\uff0c\u5426\u5219\u6050\u6015\u6211\u4eec\u4f1a\u53d7\u602a\u8d23\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u8fd9\u5339\u9aa1\u5b50\u662f\u6211\u5fc3\u7231\u7684\u5750\u9a91\uff0c\u5b83\u7684\u817f\u53d7\u4e86\u4f24\uff0c\u6211\u8981\u7ed9\u5b83\u7597\u6cbb\u3002\u201d\u90a3\u5934\u76ee\u9053\uff1a \u201c\u8fd9\u70b9\u5c0f\u4e8b\u4ea4\u7ed9\u6211\u4eec\u529e\u597d\u4e86\u3002\u201d\u8c37\u6db5\u865a\u53c8\u662f\u4e00\u77aa\u773c\u775b\u8bf4\u9053\uff1a\u201c\u4f60\u6ca1\u542c\u8bf4\u5b83\u662f\u6211\u5fc3\u7231\u7684\u5750\u9a91 \u5417\uff0c\u6211\u7edd\u4e0d\u653e\u5fc3\u4f60\u4eec\uff0c\u6211\u8981\u4eb2\u624b\u7ed9\u5b83\u6577\u4e0a\u91d1\u521b\u836f\u3002\u4e8c\u5be8\u4e3b\u8981\u602a\u8d23\u5c31\u602a\u6211\u597d\u4e86\u3002\u201d\u6b63\u662f\uff1a \u3000\u3000\u5de7\u62a4\u4f73\u4eba\u63a2\u864e\u7a74\uff0c\u88c5\u795e\u5f04\u9b3c\u65a5\u55bd\u5175\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b\u2014\u2014\u7b2c\u56db\u5341\u56de\u3000\u4e0d\u542c\u826f\u8a00\u65bd\u8fa3\u624b\u3000\u559c\u9022\u597d\u53cb\u6597\u7fa4\u51f6 \u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b \u7b2c\u56db\u5341\u56de\u3000\u4e0d\u542c\u826f\u8a00\u65bd\u8fa3\u624b\u3000\u559c\u9022\u597d\u53cb\u6597\u7fa4\u51f6 \u3000\u3000\u8fd9\u51e0\u4e2a\u5934\u76ee\u90fd\u662f\u6697\u6697\u5600\u5495\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u4e2a\u4e11\u6c49\u5b50\u600e\u7684\u5982\u6b64\u4e0d\u901a\u60c5\u7406\uff1f\u201d\u4f46\u56e0\u8c37\u6db5\u865a \u662f\u9ed1\u77f3\u9053\u4eba\u5e26\u6765\u7684\u670b\u53cb\uff0c\u957f\u76f8\u53c8\u662f\u8fd9\u6837\u51f6\u6076\uff0c\u8c01\u4e5f\u4e0d\u6562\u60f9\u4ed6\u3002 \u3000\u3000\u8c37\u6db5\u865a\u6162\u6761\u65af\u7406\u5730\u66ff\u9aa1\u5b50\u6577\u4e0a\u4e86\u91d1\u521b\u836f\uff0c\u501a\u7740\u9aa1\u8f66\uff0c\u51b7\u773c\u5077\u770b\u201c\u805a\u4e49\u5385\u201d\u5185\u7684\u52a8\u9759\u3002\u7aa6 \u5b89\u5e73\u57cb\u4f0f\u5728\u9636\u4e0b\u7684\u597d\u624b\uff0c\u6709\u51e0\u4e2a\u5fc3\u7ec6\u7684\u4eba\uff0c\u53d1\u89c9\u4ed6\u7684\u884c\u52a8\u592a\u8fc7\u53bb\u602a\uff0c\u6697\u6697\u6ce8\u89c6\u7740\u4ed6\u3002\u53e6\u5916\u51e0 \u4e2a\u89c9\u5f97\u6768\u5a49\u521a\u624d\u90a3\u58f0\u54b3\u55fd\u5f88\u662f\u53ef\u7591\uff0c\u4e5f\u5728\u6697\u4e2d\u76d1\u89c6\u7740\u6768\u5a49\u3002 \u3000\u3000\u6768\u5a49\u66fe\u5728\u90a3\u53e4\u5e99\u4e2d\u89c1\u8fc7\u8c37\u6db5\u865a\u7684\u771f\u9762\u76ee\uff0c\u6b64\u65f6\u4e1a\u5df2\u8ba4\u51fa\u662f\u4ed6\uff0c\u53c8\u662f\u5403\u60ca\uff0c\u53c8\u662f\u6b22\u559c\u3002 \u3000\u3000\u4e14\u8bf4\u9ed1\u77f3\u9053\u4eba\u628a\u4e25\u70f7\u62bc\u8fdb\u5927\u5802\uff0c\u62b1\u62f3\u4e00\u63d6\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u7aa6\u5927\u54e5\uff0c\u5c0f\u5f1f\u5e78\u4e0d\u8fb1\u547d\uff0c\u628a\u8fd9 \u5973\u5a03\u513f\u7ed9\u4f60\u9001\u6765\u4e86\u3002\u201d\u5b5f\u660e\u971e\u770b\u4e0d\u89c1\u5916\u9762\u7684\u8c37\u6db5\u865a\uff0c\u53ea\u770b\u89c1\u8868\u59d0\u7ed9\u7f57\u4fca\u548c\u4e00\u4e2a\u9053\u58eb\u62bc\u7740\u8fdb \u6765\uff0c\u8fd9\u4e00\u60ca\u81ea\u662f\u975e\u540c\u5c0f\u53ef\u3002 \u3000\u3000\u7aa6\u5b89\u5e73\u559c\u51fa\u671b\u5916\uff0c\u8fde\u5fd9\u4e0a\u524d\u8fce\u63a5\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u9ed1\u77f3\u5927\u54e5\uff0c\u771f\u6709\u4f60\u7684\uff0c\u8f9b\u82e6\u4f60\u4e86\uff01\u201d\u4e25 \u70f7\u6545\u4f5c\u60ca\u60f6\u6025\u6012\u7684\u6a21\u6837\uff0c\u65a5\u9053\uff1a\u201c\u4f60\u662f\u4ec0\u4e48\u4eba\uff1f\u6211\u4e0e\u4f60\u4eec\u7d20\u4e0d\u76f8\u8bc6\uff0c\u4f60\u4eec\u4e3a\u4ec0\u4e48\u8981\u628a\u6211\u6349 \u6765\uff1f\u201d \u3000\u3000\u7aa6\u5b89\u5e73\u7b11\u9053\uff1a\u201c\u4e25\u59d1\u5a18\u4e0d\u5fc5\u5bb3\u6015\uff0c\u6211\u662f\u7279\u5730\u8bf7\u4f60\u6765\u7684\uff0c\u53ea\u8981\u4f60\u80af\u542c\u6211\u7684\u8bdd\uff0c\u6211\u51b3\u4e0d\u4f1a\u96be \u4e3a\u4f60\u7684\u3002\u201d \u3000\u3000\u6b64\u65f6\u767d\u4e07\u96c4\u5df2\u7ecf\u7ad9\u8d77\u8eab\u6765\uff0c\u811a\u6b65\u4e0d\u4e03\u4e0d\u516b\u5730\u7ad9\u5728\u891a\u4e91\u5cf0\u8eab\u65c1\uff0c\u6697\u85cf\u7740\u5927\u64d2\u62ff\u624b\u7684\u62db\u5f0f\uff0c \u53ea\u8981\u891a\u4e91\u5cf0\u4e00\u6709\u5f02\u52a8\uff0c\u4ed6\u5c31\u8981\u51fa\u624b\u64d2\u62ff\uff0c\u9633\u575a\u767d\u4ea6\u5df2\u62d4\u5251\u51fa\u9798\uff0c\u7ad9\u5728\u4e00\u65c1\u76d1\u89c6\u3002 \u3000\u3000\u7f57\u4fca\u770b\u89c1\u5df1\u65b9\u6212\u5907\u68ee\u4e25\uff0c\u6599\u60f3\u891a\u4e91\u5cf0\u672c\u9886\u518d\u597d\uff0c\u4e5f\u662f\u5b64\u638c\u96be\u9e23\uff0c\u653e\u4e0b\u4e86\u5fc3\uff0c\u8bf4\u9053\uff1a\u201c\u5be8 \u4e3b\uff0c\u8fd9\u91cc\u5927\u7ea6\u7528\u4e0d\u7740\u6211\u4e86\u5427\u3002\u201d\u7aa6\u5b89\u5e73\u6325\u4e86\u4e00\u6325\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u4f60\u53bb\u67e5\u95ee\u6e05\u695a\uff0c\u628a\u521a\u624d\u54b3 \u55fd\u7684\u90a3\u4e2a\u4eba\u7ed9\u6211\u627e\u51fa\u6765\u3002\u201d \u3000\u3000\u7aa6\u5b89\u5e73\u4ea4\u4ee3\u8fc7\u540e\uff0c\u4fbf\u5373\u4e0a\u524d\u5bf9\u4e25\u70f7\u7b11\u9053\uff1a\u201c\u9ed1\u77f3\u9053\u957f\u8fd8\u6ca1\u6709\u544a\u8bc9\u4f60\u662f\u4ec0\u4e48\u4eba\u5417\uff1f\u4f60\u8981\u8d70 \u662f\u63d2\u7ffc\u96be\u98de\u7684\u4e86\uff0c\u5728\u6211\u8fd9\u513f\u5b89\u5fc3\u4f4f\u4e0b\u5427\u3002\u4eba\u6765\uff0c\u628a\u5979\u5e26\u8fdb\u53bb\u6b47\u606f\u3002\u201d \u3000\u3000\u4e25\u70f7\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u8bf4\u4e2a\u6e05\u695a\uff0c\u6211\u5b81\u6b7b\u51b3\u4e0d\u53d7\u8fb1\uff01\u201d \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u8bf4\u9053\uff1a\u201c\u7aa6\u5927\u54e5\uff0c\u6211\u6709\u8bdd\u8981\u548c\u4f60\u8bf4\uff0c\u4e0d\u77e5\u4f60\u73b0\u5728\u6709\u6ca1\u6709\u7a7a\uff1f\u201d\u8bf4\u8bdd\u4e4b\u9645\uff0c\u7ad9\u5728 \u4e25\u70f7\u80cc\u540e\uff0c\u5411\u7aa6\u5b89\u5e73\u6253\u4e86\u4e2a\u773c\u8272\uff0c\u6307\u6307\u4e25\u70f7\u3002 \u3000\u3000\u7aa6\u5b89\u5e73\u77e5\u9053\u9ed1\u77f3\u7684\u813e\u6c14\uff0c\u7b11\u9053\uff1a\u201c\u9ed1\u5927\u54e5\uff0c\u8fd9\u4ef6\u4e8b\u6211\u662f\u5e94\u8be5\u548c\u4f60\u8bf4\u4e2a\u660e\u767d\u3002\u4e25\u59d1\u5a18\uff0c\u4f60 \u4e5f\u6765\u5427\uff01\u201d \u3000\u3000\u4e25\u70f7\u4f6f\u6012\u9053\uff1a\u201c\u5927\u4e0d\u4e86\u662f\u4e00\u4e2a\u6b7b\uff0c\u6211\u51b3\u4e0d\u80fd\u4efb\u7531\u4f60\u4eec\u6446\u5e03\u3002\u201d \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u9053\uff1a\u201c\u4f60\u6ca1\u6709\u542c\u5f97\u7aa6\u5be8\u4e3b\u521a\u624d\u8bf4\u7684\u8bdd\u5417\uff1f\u73b0\u5728\u5c31\u662f\u8981\u5411\u4f60\u89e3\u91ca\u660e\u767d\u7684\uff0c\u4f60\u8fdb\u53bb \u5427\uff01\u201d \u3000\u3000\u4e25\u70f7\u597d\u4f3c\u6709\u70b9\u8e0c\u8e87\u4e0d\u51b3\u7684\u795e\u6c14\uff0c\u6b32\u884c\u53c8\u6b62\u3002\u9ed1\u77f3\u9053\u4eba\u4e5f\u88c5\u6a21\u4f5c\u6837\u5730\u529d\u89e3\u5979\u9053\uff1a\u201c\u5e74\u7eaa\u8f7b \u8f7b\u7684\u59d1\u5a18\uff0c\u52a8\u4e0d\u52a8\u5bfb\u6b7b\u89c5\u6d3b\uff0c\u4f55\u82e6\u6765\u7531\uff1f\u4f60\u65e2\u7136\u4e0d\u76f8\u4fe1\u4ed6\u4eec\uff0c\u4e5f\u65e0\u59a8\u542c\u4e00\u542c\u7aa6\u5be8\u4e3b\u7684\u89e3\u91ca \u5440\uff01\u542c\u8fc7\u4e4b\u540e\uff0c\u8981\u662f\u4f60\u4ecd\u7136\u89c9\u5f97\u4e3a\u96be\u7684\u8bdd\uff0c\u90a3\u65f6\u4f60\u518d\u4f5c\u51b3\u5b9a\u4e5f\u8fd8\u4e0d\u8fdf\u3002\u201d \u3000\u3000\u4e25\u70f7\u4f3c\u4e4e\u5df2\u6709\u51e0\u5206\u7ed9\u4ed6\u8bf4\u52a8\u4e86\uff0c\u4e0d\u518d\u5435\u95f9\uff0c\u9ed1\u77f3\u9053\u4eba\u4e58\u52bf\u8f7b\u8f7b\u4e00\u63a8\uff0c\u5c06\u5979\u63a8\u5411\u524d\u884c\u3002 \u3000\u3000\u5b5f\u660e\u971e\u53c8\u662f\u7740\u6025\uff0c\u53c8\u662f\u6709\u70b9\u8be7\u5f02\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6211\u867d\u7136\u6539\u4e86\u7537\u88c5\uff0c\u4f46\u70f7\u8868\u59d0\u662f\u5e94\u8be5\u8ba4\u5f97 \u51fa\u6211\u7684\uff0c\u4e3a\u4f55\u5979\u773c\u89d2\u4e5f\u6ca1\u6709\u5411\u6211\u77a7\u77a7\uff0c\u7adf\u4f3c\u4e0d\u77e5\u9053\u6709\u6211\u8fd9\u4e2a\u4eba\u5728\u8fd9\u513f\u4f3c\u7684\uff1f\u66f4\u5947\u602a\u7684\u662f\u5979\u6027 \u5b50\u4e00\u5411\u6781\u4e3a\u5014\u5f3a\uff0c\u4f55\u4ee5\u73b0\u5728\u7adf\u4f1a\u53d7\u4eba\u54c4\u9a97\uff1f\u201d \u3000\u3000\u5b5f\u660e\u971e\u54ea\u91cc\u77e5\u9053\uff0c\u8fd9\u6b63\u662f\u9ed1\u77f3\u9053\u4eba\u548c\u4e25\u70f7\u9884\u5b9a\u7684\u8ba1\u5212\uff0c\u8fdb\u5165\u5bc6\u5ba4\uff0c\u4ed6\u4eec\u4e8c\u4eba\u7acb\u5373\u8054\u624b\u628a \u7aa6\u5b89\u5e73\u5236\u4f0f\uff0c\u7136\u540e\u7531\u9ed1\u77f3\u9053\u4eba\u529d\u4ed6\u6539\u90aa\u5f52\u6b63\uff0c\u4ee5\u5168\u9ed1\u77f3\u9053\u4eba\u7ed3\u62dc\u4e4b\u60c5\u3002\u82e5\u662f\u7aa6\u5b89\u5e73\u4e0d\u80af\u4f9d \u4ece\uff0c\u518d\u7531\u4e25\u70f7\u5904\u7f6e\u3002 \u3000\u3000\u5b5f\u660e\u971e\u53ea\u77e5\u9053\u7aa6\u5b89\u5e73\u662f\u8981\u7528\u4e25\u70f7\u6765\u631f\u5236\u5979\u7684\u7236\u4eb2\uff0c\u53ea\u6015\u4e25\u70f7\u4e0a\u4e86\u4ed6\u4eec\u7684\u5f53\uff0c\u8fdb\u53bb\u4e4b\u540e\uff0c \u4e0d\u77e5\u8981\u4f7f\u51fa\u4ec0\u4e48\u9634\u9669\u72e0\u6bd2\u7684\u624b\u6bb5\u6765\u5bf9\u4ed8\u5979\uff0c\u7740\u6025\u4e4b\u4e0b\uff0c\u65e0\u6687\u601d\u7d22\uff0c\u500f\u5730\u62d4\u5251\u51fa\u9798\u4fbf\u5411\u9ed1\u77f3\u9053 \u4eba\u523a\u53bb\u3002\u767d\u4e07\u96c4\u7b49\u4eba\u53ea\u5f53\u5979\u662f\u891a\u4e91\u5cf0\u7684\u52a9\u624b\uff0c\u5bf9\u5979\u7684\u9632\u5907\u8fdc\u4e0d\u5982\u5bf9\u891a\u4e91\u5cf0\u4e4b\u4e25\u3002 \u3000\u3000\u8fd9\u4e00\u62db\u5251\u6cd5\u4e43\u662f\u5b5f\u5bb6\u7684\u4e0d\u4f20\u4e4b\u79d8\uff0c\u5f53\u771f\u662f\u53c8\u5feb\u53c8\u51c6\uff0c\u9976\u662f\u9ed1\u77f3\u9053\u4eba\u672c\u9886\u9ad8\u5f3a\uff0c\u4e5f\u662f\u975e\u7ed9 \u5979\u523a\u4e2d\u4e0d\u53ef\u3002 \u3000\u3000\u773c\u770b\u5b5f\u660e\u971e\u7684\u5251\u5c16\u5c31\u8981\u523a\u7740\u9ed1\u77f3\u9053\u4eba\u7684\u864e\u53e3\uff0c\u4e25\u70f7\u5ffd\u4fa7\u8eab\u4e00\u95ea\uff0c\u53cd\u624b\u6263\u7740\u5b5f\u660e\u971e\u7684\u624b \u8155\u3002\u5979\u4eec\u4e8c\u4eba\u7684\u672c\u9886\u672c\u662f\u4e0d\u76f8\u4e0a\u4e0b\u7684\uff0c\u53ea\u56e0\u5b5f\u660e\u971e\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u8868\u59d0\u53cd\u800c\u4f1a\u51fa\u624b\u653b\u5979\uff0c\u800c\u4e25 \u70f7\u53c8\u719f\u6089\u5979\u7684\u5251\u6cd5\uff0c\u6545\u6b64\u5b5f\u660e\u971e\u51b7\u4e0d\u9632\u5730\u5c31\u7740\u4e86\u9053\u513f\u3002 \u3000\u3000\u5b5f\u660e\u971e\u5927\u60ca\u4e4b\u4e0b\uff0c\u5931\u58f0\u53eb\u9053\uff1a\u201c\u8868\u59d0\uff0c\u662f\u6211\u5440\uff01\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u5ffd\u89c9\u80cc\u540e\u91d1\u5200\u5288\u98ce\u4e4b\u58f0\uff0c \u9633\u575a\u767d\u5df2\u662f\u5530\u7684\u4e00\u5251\u6307\u5230\u4e86\u5b5f\u660e\u971e\u7684\u540e\u5fc3\u8981\u7a74\u3002 \u3000\u3000\u4e25\u70f7\u662f\u6050\u6015\u5b5f\u660e\u971e\u4f24\u4e86\u9ed1\u77f3\u9053\u4eba\uff0c\u8bf4\u8bdd\u5df2\u6765\u4e0d\u53ca\uff0c\u53ea\u597d\u5148\u884c\u51fa\u624b\uff0c\u5236\u6b64\u4e86\u5b5f\u660e\u971e\u3002\u4e00\u65f6 \u95f4\u4e5f\u8bbe\u60f3\u5230\u654c\u4eba\u73af\u4f3a\u5728\u65c1\uff0c\u5b9a\u7136\u4f1a\u6709\u4eba\u6765\u5411\u5b5f\u660e\u971e\u88ad\u51fb\u3002\u8fd9\u65f6\u4e25\u70f7\u521a\u521a\u6263\u4f4f\u5b5f\u660e\u971e\u7684\u624b\u8155\uff0c \u5b5f\u660e\u971e\u65e0\u6cd5\u52a8\u5f39\uff0c\u5f62\u52bf\u4e4b\u9669\uff0c\u6bd4\u9ed1\u77f3\u9053\u4eba\u521a\u624d\u53d7\u5979\u88ad\u51fb\u66f4\u751a\uff01 \u3000\u3000\u53ea\u542c\u5f97\u201c\u94db\u201d\u7684\u4e00\u58f0\uff0c\u5200\u5149\u5251\u5f71\u4e4b\u4e2d\uff0c\u9633\u575a\u767d\u9000\u4e86\u4e00\u6b65\uff0c\u7834\u53e3\u5927\u9a82\uff1a\u201c\u5c82\u6709\u6b64\u7406\uff0c\u539f\u6765 \u4f60\u8fd9\u81ed\u9053\u58eb\u7adf\u662f\u5978\u7ec6\uff01\u201d \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u4ee5\u5feb\u5200\u6321\u4e86\u9633\u575a\u767d\u7684\u4e00\u62db\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u9633\u575a\u767d\uff0c\u8fd9\u91cc\u53ef\u4e0d\u662f\u56fd\u5e08\u5e9c\uff0c\u4f60\u8981\u53d1 \u516c\u5b50\u813e\u6c14\uff0c\u56de\u53bb\u518d\u53d1\u3002\u5728\u8fd9\u91cc\u6211\u53ef\u4e0d\u80fd\u5bb9\u4f60\u5730\u4f5c\u975e\u4e3a\uff01\u201d \u3000\u3000\u9633\u575a\u767d\u5927\u6012\u9053\uff1a\u201c\u4f60\u5403\u91cc\u6252\u5916\uff0c\u8fd8\u8bf4\u6211\u662f\u80e1\u4f5c\u975e\u4e3a\uff1f\u6211\u95ee\u4f60\uff0c\u6211\u597d\u5fc3\u6551\u4f60\uff0c\u4f60\u4e3a\u4f55\u53cd\u800c \u62ff\u5200\u65ab\u6211\uff1f\u201d \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u597d\u5fc3\u6551\u6211\uff1f\u4f60\u662f\u8981\u628a\u7aa6\u5be8\u4e3b\u6263\u6211\u4eec\u8fd9\u4e9b\u4eba\u90fd\u63a8\u8fdb\u706b\u7095\uff0c\u53eb\u6211\u4eec\u8eab \u8d25\u540d\u88c2\uff0c\u8fd9\u53ef\u6bd4\u62ff\u5200\u6740\u4eba\u66f4\u4e3a\u72e0\u8fa3\uff01\u201d \u3000\u3000\u9633\u575a\u767d\u559d\u9053\uff1a\u201c\u7aa6\u5be8\u4e3b\uff0c\u4f60\u542c\u542c\u4f60\u8fd9\u7ed3\u62dc\u5144\u5f1f\u8bf4\u7684\u662f\u4ec0\u4e48\u8bdd\uff1f\u201d\u4e24\u4eba\u53e3\u4e2d\u5bf9\u9a82\uff0c\u624b\u4e0a\u7684 \u5200\u5251\u53c8\u5df2\u4ea4\u950b\u3002 \u3000\u3000\u6b64\u65f6\uff0c\u90a3\u73ed\u5192\u5145\u4ec6\u5f79\u7684\u597d\u624b\u5df2\u662f\u7eb7\u7eb7\u62e5\u4e0a\uff0c\u628a\u5b5f\u660e\u971e\u548c\u4e25\u70f7\u56f4\u5728\u5f53\u4e2d\u4e86\u3002 \u3000\u3000\u53e6\u4e00\u8fb9\uff0c\u76d1\u89c6\u7740\u891a\u4e91\u5cf0\u7684\u767d\u4e07\u96c4\u4ea6\u5df2\u51fa\u624b\u3002\u891a\u4e91\u5cf0\u65e9\u6709\u51c6\u5907\uff0c\u4e00\u62db\u201c\u6012\u96f7\u51fb\u9876\u201d\uff0c\u6a2a\u638c \u8fce\u51fb\uff0c\u4f7f\u51fa\u4e86\u5a01\u731b\u65e0\u4f26\u7684\u201c\u5929\u96f7\u529f\u201d\u3002\u638c\u52bf\u5982\u5200\uff0c\u6a2a\u524a\u5bf9\u65b9\u624b\u8155\uff0c\u4e94\u6307\u5982\u94a7\uff0c\u6697\u85cf\u540e\u7740\uff0c\u53ea \u8981\u5bf9\u65b9\u5e94\u4ed8\u4e0d\u5584\uff0c\u7acb\u5373\u4fbf\u53ef\u51fb\u88ad\u4ed6\u7684\u8111\u95e8\uff01 \u3000\u3000\u767d\u4e07\u96c4\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u7c73\u7c92\u4e4b\u73e0\uff0c\u4e5f\u653e\u5149\u534e\uff01\u201d\u53cc\u638c\u76f8\u4ea4\uff0c\u201d\u84ec\u201d\u7684\u4e00 \u58f0\uff0c\u53d1\u51fa\u90c1\u96f7\u4e5f\u4f3c\u7684\u58f0\u54cd\uff0c\u891a\u4e91\u5cf0\u90a3\u521a\u731b\u5f02\u5e38\u7684\u638c\u529b\uff0c\u7ade\u4f3c\u7ed9\u4e00\u56e2\u68c9\u7d6e\u88f9\u4f4f\uff0c\u53d1\u4e0d\u51fa\u6765\uff0c\u8bf4 \u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u767d\u4e07\u96c4\u7684\u5de6\u624b\u5df2\u662f\u5411\u891a\u4e91\u5cf0\u7684\u7435\u7436\u9aa8\u6293\u6765\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u4e00\u4e2a\u201c\u8131\u888d\u89e3\u7532\u201d\uff0c\u53ea\u542c\u5f97\u58f0\u5982\u88c2\u5e1b\u3002\u891a\u4e91\u5cf0\u7684\u4e0a\u8863\u7ed9\u4ed6\u6293\u7834\uff0c\u4f46\u891a\u4e91\u5cf0\u4ea6\u5df2\u8131 \u51fa\u5bf9\u65b9\u638c\u63e1\uff0c\u9000\u5f00\u4e24\u6b65\uff0c\u62d4\u51fa\u4e86\u5b9d\u5251\u4e86\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u4e00\u4e2a\u7167\u9762\uff0c\u4fbf\u51e0\u4e4e\u5403\u4e86\u5927\u4e8f\uff0c\u8fd9\u4e00\u60ca\uff0c\u975e\u540c\u5c0f\u53ef\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u8001\u8d3c\u679c\u7136\u5389 \u5bb3\uff0c\u602a\u4e0d\u5f97\u4e8c\u5341\u5e74\u524d\uff0c\u4ed6\u5c31\u5df2\u7ecf\u662f\u548c\u6df3\u4e8e\u5468\u9f50\u540d\u7684\u4e86\u3002\u5b5f\u5927\u4fa0\u4e0e\u674e\u601d\u5357\u4e0d\u77e5\u4ec0\u4e48\u65f6\u5019\u624d\u6765\uff0c \u4eca\u65e5\u4e4b\u4e8b\uff0c\u53ea\u6015\u662f\u51f6\u591a\u5409\u5c11\u4e86\u3002\u201d\u4f46\u891a\u4e91\u5cf0\u867d\u7136\u660e\u77e5\u4e0d\u662f\u767d\u4e07\u96c4\u7684\u5bf9\u624b\uff0c\u5374\u4ecd\u7136\u6beb\u4e0d\u754f\u60e7\uff0c \u594b\u529b\u8fce\u6218\u3002 \u3000\u3000\u767d\u4e07\u96c4\u4ea6\u662f\u5fc3\u4e2d\u5fae\u51db\uff0c\u60f3\u9053\uff1a\u201c\u6211\u95ed\u95e8\u5c01\u5200\uff0c\u82e6\u7ec3\u5341\u5e74\uff0c\u7adf\u7136\u51fb\u4e0d\u5012\u8fd9\u5c0f\u5b50\uff01\u201d\u5f53\u4e0b\u4e5f \u5c31\u4e0d\u6562\u8f7b\u654c\uff0c\u62ff\u51fa\u4e86\u5168\u526f\u672c\u9886\u6765\u5bf9\u4ed8\u891a\u4e91\u5cf0\u3002\u891a\u4e91\u5cf0\u7684\u4e00\u53e3\u957f\u5251\u5728\u4ed6\u53cc\u638c\u76d8\u65cb\u98de\u821e\u4e4b\u4e0b\uff0c\u7adf \u662f\u53ea\u6709\u62db\u67b6\u7684\u4efd\u513f\u3002 \u3000\u3000\u5802\u4e0a\u8bf8\u4eba\u6700\u5403\u60ca\u7684\u8fd8\u662f\u7aa6\u5b89\u5e73\uff0c\u4ed6\u600e\u4e5f\u6599\u60f3\u4e0d\u5230\u9ed1\u77f3\u9053\u4eba\u7adf\u7136\u4e0e\u9633\u575a\u767d\u4ea4\u8d77\u624b\u6765\uff0c\u800c\u4e14 \u8fd8\u8bf4\u51fa\u4e86\u8fd9\u6837\u7684\u4e00\u756a\u8bf4\u8bdd\u3002 \u3000\u3000\u4f46\u7aa6\u5b89\u5e73\u4e5f\u662f\u4e2a\u8001\u5978\u5de8\u6ed1\u4e4b\u8f88\uff0c\u60ca\u9b42\u7a0d\u5b9a\uff0c\u7acb\u5373\u660e\u767d\u9ed1\u77f3\u9053\u4eba\u662f\u5df2\u7ecf\u80cc\u53db\u4ed6\u4e86\u3002 \u3000\u3000\u7aa6\u5b89\u5e73\u7709\u5934\u4e00\u76b1\uff0c\u8ba1\u4e0a\u5fc3\u6765\uff0c\u4f6f\u4f5c\u4e0d\u89e3\uff0c\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u4e14\u6162\u52a8\u624b\uff01\u9ed1\u77f3\u5927\u54e5\uff0c\u8fd9\u662f\u600e\u4e48 \u56de\u4e8b\uff1f\u4f60\u8bf4\u628a\u4e25\u59d1\u5a18\u64d2\u6765\u7ed9\u6211\uff0c\u4f46\u4e25\u59d1\u5a18\u597d\u50cf\u5e76\u6ca1\u6709\u670d\u4e0b\u4f60\u7684\u9165\u9aa8\u6563\u5440\uff01\u201d \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u9053\uff1a\u201c\u7aa6\u5927\u54e5\uff0c\u521a\u624d\u7aa6\u65fa\u90fd\u5df2\u5bf9\u6211\u8bf4\u4e86\uff0c\u6211\u4ee5\u4e3a\u54b1\u4eec\u597d\u6b79\u4e5f\u7b97\u5f97\u662f\u7eff\u6797\u7684\u4e00\u53f7 \u4eba\u7269\uff0c\u5c82\u80fd\u7528\u8fd9\u7b49\u4e0b\u4e09\u6ee5\u624b\u6bb5\u628a\u4e00\u4e2a\u5c0f\u59d1\u5a18\u62ff\u6765\u4f5c\u6321\u7bad\u7891\uff01\u201d \u3000\u3000\u7aa6\u5b89\u5e73\u9053\uff1a\u201c\u54e6\uff0c\u8fd9\u4e48\u8bf4\u6765\uff0c\u4f60\u662f\u4e0d\u8d5e\u540c\u6211\u7684\u884c\u4e8b\u4e86\uff1f\u201d \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u975e\u4f46\u4e0d\u8d5e\u540c\u4f60\u5bf9\u4ed8\u4e25\u59d1\u5a18\u7684\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u6211\u8981\u8bf4\u4f60\u8fd9\u4e00\u6b21\u662f\u5168\u76d8 \u9519\u4e86\u3002\u201d \u3000\u3000\u7aa6\u5b89\u5e73\u9053\uff1a\u201c\u8bf7\u5927\u54e5\u6307\u6559\u3002\u201d \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u9053\uff1a\u201c\u54b1\u4eec\u4ec0\u4e48\u90fd\u53ef\u4ee5\u505a\uff0c\u5c31\u662f\u4e0d\u80fd\u505a\u9791\u5b50\u7684\u722a\u7259\uff01\u7aa6\u5927\u54e5\uff0c\u4f60\u672c\u6765\u662f\u7eff\u6797\u4e2d \u54cd\u5f53\u5f53\u7684\u89d2\u8272\uff0c\u53c8\u4f55\u987b\u4e3a\u864e\u4f5c\u6005\u5462\uff1f\u8bf7\u4f60\u4e09\u601d\u800c\u884c\uff01\u201d \u3000\u3000\u7aa6\u5b89\u5e73\u9053\uff1a\u201c\u5179\u4e8b\u4f53\u5927\uff0c\u54b1\u4eec\u6162\u6162\u5546\u8bae\u3002\u8bf7\u4f60\u4eec\u4e8c\u4eba\u6682\u4e14\u7f62\u624b\uff0c\u770b\u5728\u6211\u7684\u4efd\u4e0a\u5427\uff01\u201d \u3000\u3000\u9633\u575a\u767d\u672c\u6765\u662f\u6012\u706b\u5347\u8d77\u4e86\u7684\uff0c\u4f46\u4ed6\u4e5f\u662f\u806a\u660e\u7684\u4eba\uff0c\u770b\u89c1\u7aa6\u5b89\u5e73\u5077\u5077\u5bf9\u4ed6\u4f7f\u4e86\u4e00\u4e2a\u773c\u8272\uff0c \u5fc3\u5ff5\u4e00\u52a8\uff0c\u968f\u5373\u60f3\u9053\uff1a\u201c\u8fd9\u4e8b\u7aa6\u5b89\u5e73\u4e0d\u5e72\u4e5f\u5df2\u5e72\u4e86\uff0c\u4ed6\u6709\u8bb8\u591a\u628a\u67c4\u5728\u6211\u7239\u7239\u624b\u91cc\uff0c\u8c05\u4ed6\u4e0d\u6562 \u53cd\u53db\u3002\u800c\u4e14\u6709\u767d\u4e07\u96c4\u5728\u8fd9\u513f\uff0c\u4ed6\u60f3\u9020\u53cd\u4e5f\u662f\u4e0d\u6210\u3002\u4e14\u770b\u4ed6\u5982\u4f55\u5904\u7f6e\u8fd9\u81ed\u9053\u58eb\u5427\u3002\u201d \u3000\u3000\u9633\u575a\u767d\u4f6f\u4f5c\u201c\u60bb\u60bb\u7136\u89c1\u4e8e\u8f9e\u8272\u201d\u7684\u6a21\u6837\uff0c\u6258\u5730\u8df3\u51fa\u56f4\u5b50\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u597d\u5440\uff0c\u7aa6\u5be8\u4e3b\uff0c \u4f60\u65e2\u7136\u8981\u542c\u4f60\u62dc\u628a\u5144\u5f1f\u7684\u8bf4\u8bdd\uff0c\u6211\u662f\u758f\u4e0d\u95f4\u4eb2\uff0c\u544a\u8f9e\u4e86\uff01\u201d \u3000\u3000\u7aa6\u5b89\u5e73\u9053\uff1a\u201c\u54b1\u4eec\u8fdb\u53bb\u8c08\u8c08\u3002\u201d\u8d70\u5230\u9ed1\u77f3\u9053\u4eba\u8eab\u8fb9\uff0c\u4f38\u51fa\u624b\u6765\u3002\u9ed1\u77f3\u9053\u4eba\u53ea\u9053\u4ed6\u662f\u8981\u62c9 \u81ea\u5df1\u5165\u5bc6\u5ba4\u503e\u8c08\uff0c\u4e0d\u6599\u7aa6\u5b89\u5e73\u7a81\u7136\u53cd\u624b\u4e00\u62ff\uff0c\u6293\u7740\u4e86\u4ed6\u7684\u7435\u7436\u9aa8\u3002 \u3000\u3000\u7aa6\u5b89\u5e73\u7684\u201c\u9f99\u722a\u624b\u201d\u4e5f\u662f\u6b66\u6797\u4e00\u7edd\uff0c\u8bba\u771f\u5b9e\u7684\u672c\u9886\uff0c\u82e5\u7136\u5f7c\u6b64\u7528\u5200\u5251\u4ea4\u950b\uff0c\u4ed6\u4e0d\u662f\u9ed1\u77f3 \u9053\u4eba\u7684\u5bf9\u624b\u3002\u4f46\u7ed9\u4ed6\u9f99\u722a\u624b\u6293\u7740\u4e86\uff0c\u9ed1\u77f3\u9053\u4eba\u7684\u672c\u9886\u518d\u9ad8\u4e00\u500d\uff0c\u4e5f\u662f\u96be\u4ee5\u8131\u8eab\u3002 \u3000\u3000\u7aa6\u5b89\u5e73\u4e00\u6293\u6293\u7740\u4e86\u9ed1\u77f3\u9053\u4eba\uff0c\u7834\u53e3\u5927\u9a82\uff1a\u201c\u5728\u6211\u548c\u4f60\u662f\u516b\u62dc\u4e4b\u4ea4\uff0c\u53c8\u66fe\u6551\u8fc7\u4f60\u7684\u6027\u547d\uff0c \u4f60\u7adf\u7136\u80f3\u818a\u5411\u5916\uff0c\u53cd\u52a9\u5916\u4eba\uff0c\u4f60\u8bf4\uff0c\u4f60\u5bf9\u5f97\u4f4f\u6211\u4e48\uff1f\u4f60\u8d76\u5feb\u5411\u9633\u516c\u5b50\u8d54\u7f6a\uff0c\u5f53\u7740\u4f17\u4eba\uff0c\u7b54\u5e94 \u4e56\u4e56\u5730\u8ddf\u7740\u6211\u8d70\uff0c\u6216\u8bb8\u6211\u8fd8\u53ef\u4ee5\u9976\u4f60\uff0c\u5426\u5219\u53ef\u4f11\u602a\u6211\u624b\u4e0b\u65e0\u60c5\uff01\u201d \u3000\u3000\u7aa6\u5b89\u5e73\u660e\u77e5\u9ed1\u77f3\u9053\u4eba\u662f\u4e00\u526f\u5014\u5f3a\u7684\u813e\u6c14\uff01\u5341\u4e5d\u662f\u4e0d\u4f1a\u8fd9\u6837\u505a\u7684\uff0c\u4ed6\u8fd9\u756a\u8bdd\u5176\u5b9e\u4e0d\u8fc7\u662f\u8bf4 \u7ed9\u9633\u575a\u767d\u542c\u7684\u800c\u5df2\u3002\u4e07\u4e00\u9ed1\u77f3\u9053\u4eba\u80af\u8fd9\u6837\u505a\u7684\u8bdd\uff0c\u4ed6\u4e5f\u4e0d\u6015\u9ed1\u77f3\u9053\u4eba\u8bc8\u964d\uff0c\u56e0\u4e3a\u9ed1\u77f3\u9053\u4eba\u5982 \u679c\u88ab\u903c\u5c48\u8fb1\uff0c\u4ee5\u540e\u7eb5\u7136\u53cd\u6094\uff0c\u4e5f\u662f\u65e0\u9762\u76ee\u518d\u89c1\u5929\u4e0b\u82f1\u96c4\u7684\u4e86\u3002 \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u53c8\u6c14\u53c8\u6094\uff0c\u5fc3\u9053\uff1a\u201c\u6094\u4e0d\u8be5\u4e0d\u542c\u4e25\u59d1\u5a18\u7684\u8bdd\uff0c\u539f\u6765\u4ed6\u679c\u7136\u662f\u4e1d\u6beb\u4e5f\u6ca1\u7ed3\u4e49\u4e4b \u4e49\uff0c\u7adf\u7136\u7528\u4e86\u8fd9\u5351\u9119\u7684\u624b\u6bb5\u6765\u6697\u7b97\u6211\uff01\u201d \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u6c14\u5f80\u4e0a\u51b2\uff0c\u4ea2\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u5b81\u53ef\u5bf9\u4e0d\u4f4f\u4f60\uff0c\u51b3\u4e0d\u80fd\u5bf9\u4e0d\u4f4f\u81ea\u5df1\u7684\u7956\u5b97\u3002\u4f60\u4e0b\u624b \u5427\uff01\u201d \u3000\u3000\u7aa6\u5e73\u5b89\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u60f3\u6b7b\u5f97\u8fd9\u4e48\u5bb9\u6613\uff0c\u54fc\uff0c\u4e14\u5148\u8ba9\u4f60\u5403\u70b9\u82e6\u5934\uff01\u201d\u624b\u4e0a\u52a0\u4e86\u51e0\u5206\u52b2\u529b\uff0c \u634f\u5f97\u9ed1\u77f3\u9053\u4eba\u7684\u7435\u7436\u9aa8\u683c\u683c\u4f5c\u54cd\u3002\u53c8\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u81ea\u8d1f\u5feb\u5200\u65e0\u654c\uff0c\u6211\u634f\u788e\u4f60\u7684\u7435\u7436\u9aa8\uff0c\u770b\u4f60 \u8fd8\u80fd\u4f7f\u5200\uff1f\u201d \u3000\u3000\u9633\u575a\u767d\u4e00\u89c1\u7aa6\u5b89\u5e73\u6293\u7740\u4e86\u9ed1\u77f3\u9053\u4eba\uff0c\u4fbf\u5373\u54c8\u54c8\u5927\u7b11\uff0c\u7fd8\u8d77\u62c7\u6307\u8d5e\u9053\uff1a\u201c\u597d\uff0c\u5e72\u5f97\u597d\uff01\u4f60 \u64d2\u4e86\u8fd9\u81ed\u9053\u58eb\uff0c\u6211\u53ef\u8981\u53bb\u6349\u82b1\u59d1\u5a18\u4e86\u3002\u201d\u5927\u7b11\u58f0\u4e2d\uff0c\u5230\u4e86\u5b5f\u660e\u971e\u7684\u8eab\u8fb9\u3002 \u3000\u3000\u6b64\u65f6\u5b5f\u660e\u971e\u6b63\u5728\u529b\u654c\u56db\u4e2a\u597d\u624b\uff0c\u9633\u575a\u767d\u559d\u9053\uff1a\u201c\u9000\u4e0b\uff0c\u8ba9\u6211\u6765\u5bf9\u4ed8\u8fd9\u4e2a\u96cc\u513f\uff01\u201d\u72ec\u81ea\u4e0a \u524d\uff0c\u5472\u7259\u7b11\u9053\uff1a\u201c\u5b5f\u59d1\u5a18\uff0c\u4f60\u867d\u7136\u4e54\u88c5\u6253\u626e\uff0c\u5c82\u80fd\u7792\u8fc7\u6211\u7684\u773c\u775b\uff1f\u201d \u3000\u3000\u5b5f\u660e\u971e\u6c14\u5f97\u67f3\u7709\u5012\u7ad6\uff0c\u65a5\u9053\uff1a\u201c\u4e0d\u8981\u8138\u7684\u4e0b\u6d41\u80da\u5b50\u3002\u201d\u5530\u7684\u4e00\u62db\u201c\u7389\u5973\u6295\u68ad\u201d\uff0c\u5251\u62a1\u5982 \u7ec3\uff0c\u6307\u5411\u9633\u575a\u767d\u80f8\u53e3\u7684\u201c\u9b42\u95e8\u7a74\u201d\u3002 \u3000\u3000\u9633\u575a\u767d\u9053\uff1a\u201c\u597d\u72e0\u7684\u5251\u6cd5\uff01\u5509\uff0c\u4f60\u5bf9\u6211\u5c45\u7136\u4e0b\u5f97\u8fd9\u6837\u8fa3\u624b\uff0c\u771f\u662f\u4ee4\u6211\u7070\u5fc3\uff01\u201d\u4fe1\u624b\u633d\u4e86 \u4e00\u6735\u5251\u82b1\uff0c\u8f7b\u63cf\u6de1\u5199\u5730\u5c31\u628a\u5b5f\u660e\u971e\u8fd9\u4e00\u62db\u5f97\u610f\u7684\u201c\u7389\u5973\u6295\u68ad\u201d\u89e3\u4e86\uff0c\u53c8\u518d\u563b\u76ae\u7b11\u8138\u5730\u8bf4\u9053\uff1a \u201c\u539f\u6765\u4f60\u4e5f\u8fd8\u8ba4\u5f97\u6211\u8fd9\u4e2a\u91c7\u82b1\u8d3c\u3002\u563f\uff0c\u563f\uff0c\u53ef\u60dc\u4f60\u7684\u62a4\u82b1\u4eba\u53ef\u4e0d\u80fd\u817e\u51fa\u624b\u6765\u4fdd\u62a4\u4f60\u5566\uff01\u201d \u3000\u3000\u5b5f\u660e\u971e\u7684\u5251\u6cd5\u5176\u5b9e\u5e76\u4e0d\u8f93\u7ed9\u9633\u575a\u767d\uff0c\u529f\u529b\u867d\u7136\u7a0d\u6709\u4e0d\u5982\uff0c\u81f3\u5c11\u4e5f\u53ef\u4ee5\u62fc\u6597\u4e94\u4e03\u5341\u62db\u7684\u3002 \u53ea\u56e0\u7ed9\u4ed6\u6c14\u5f97\u4e03\u7a8d\u751f\u70df\uff0c\u4e00\u4ea4\u624b\u5c31\u9891\u9047\u9669\u62db\uff0c\u51e0\u4e4e\u4e3a\u4ed6\u6240\u7b97\u3002\u5b5f\u660e\u971e\u4e00\u89c9\u4e0d\u5999\uff0c\u8fde\u5fd9\u9547\u6444\u5fc3 \u795e\uff0c\u51dd\u795e\u5bf9\u654c\u3002\u4f46\u4ea6\u5df2\u662f\u5904\u4e8e\u4e0b\u98ce\u4e86\u3002 \u3000\u3000\u4e25\u70f7\u72ec\u81ea\u4e00\u4eba\uff0c\u53d7\u5341\u51e0\u4e2a\u9ad8\u624b\u7684\u5305\u56f4\uff0c\u5f62\u52bf\u66f4\u4e3a\u6076\u52a3\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u3001\u5b5f\u660e\u971e\u3001\u4e25\u70f7\u90fd\u662f\u81ea\u987e\u4e0d\u6687\uff0c\u5f53\u7136\u4e5f\u5c31\u817e\u4e0d\u51fa\u624b\u6765\u7ed9\u9ed1\u77f3\u9053\u4eba\u89e3\u56f0\u4e86\u3002 \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u7684\u7435\u7436\u9aa8\u7ed9\u7aa6\u5b89\u5e73\u634f\u5f97\u683c\u683c\u4f5c\u54cd\uff0c\u54ac\u7d27\u7259\u6839\uff0c\u4e0d\u54fc\u4e00\u58f0\u3002\u5fc3\u91cc\u597d\u751f\u6094\u6068\uff0c\u77e5\u9053 \u8fd9\u6b21\u5b9a\u7136\u8981\u6b7b\u5728\u201c\u516b\u62dc\u4e4b\u4ea4\u201d\u7684\u7aa6\u5b89\u5e73\u4e4b\u624b\u4e86\u3002 \u3000\u3000\u805a\u4e49\u5385\u4e2d\u5c55\u5f00\u6076\u6597\uff0c\u5916\u9762\u6768\u5a49\u7684\u884c\u85cf\u4ea6\u5df2\u8d25\u9739\uff0c\u6b64\u65f6\u4e5f\u548c\u7f57\u4fca\u7b49\u4eba\u52a8\u8d77\u624b\u4e86\u3002 \u3000\u3000\u4e14\u8bf4\u7f57\u4fca\u518d\u6b21\u51fa\u6765\uff0c\u5148\u628a\u4e00\u4e2a\u5fc3\u8179\u62c9\u8fc7\u4e00\u8fb9\uff0c\u6084\u6084\u95ee\u9053\uff1a\u201c\u521a\u624d\u54b3\u55fd\u7684\u90a3\u4eba\u662f\u8c01\uff1f\u201d\u95ee \u6e05\u695a\u4e86\u662f\u6768\u5a49\u4e4b\u540e\uff0c\u4e0d\u52a8\u58f0\u8272\u5730\u8d70\u5230\u6768\u5a49\u9762\u524d\uff0c\u88c5\u4f5c\u5f88\u4fe1\u4efb\u5979\u7684\u6837\u5b50\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u91cc\u9762\u5df2 \u7ecf\u52a8\u624b\u4e86\uff0c\u90a3\u51e0\u4e2a\u654c\u4eba\u9887\u4e3a\u4e86\u5f97\uff0c\u4f60\u8fdb\u53bb\u5e2e\u5e2e\u5fd9\u5427\u3002\u201d \u3000\u3000\u6768\u5a49\u65e9\u6709\u6212\u5907\uff0c\u4e0d\u8fc7\u5979\u4e5f\u662f\u6b63\u60f3\u8fdb\u53bb\u7684\uff0c\u5f53\u4e0b\u8bf4\u4e86\u4e2a\u201c\u597d\u201d\u5b57\uff0c\u4fbf\u4e0e\u7f57\u4fca\u6b65\u4e0a\u53f0\u9636\u3002\u8d70 \u5230\u4e00\u534a\uff0c\u7a81\u7136\u4e00\u4e2a\u53cd\u624b\u64d2\u62ff\uff0c\u60f3\u8981\u6263\u7740\u7f57\u4fca\u7684\u624b\u8155\u3002 \u3000\u3000\u5c82\u77e5\u53f0\u9636\u4e0a\u57cb\u4f0f\u7684\u597d\u624b\u4ea6\u662f\u65e9\u6709\u51c6\u5907\uff0c\u672c\u6765\u4ed6\u4eec\u662f\u60f3\u7b49\u5f85\u7f57\u4fca\u5c06\u5979\u8bf1\u5230\u805a\u4e49\u5385\uff0c\u8ba9\u5979\u7684 \u6ce8\u610f\u529b\u4e3a\u5385\u4e2d\u7684\u6076\u6597\u5438\u5f15\u800c\u5206\u6563\u7684\u65f6\u5019\uff0c\u624d\u51b7\u4e0d\u53ca\u9632\u5730\u88ad\u51fb\uff0c\u6b64\u65f6\u6768\u5a49\u4e00\u51fa\u624b\uff0c\u8fd9\u4e9b\u4eba\u5f53\u7136 \u4e5f\u5c31\u63d0\u524d\u52a8\u624b\u3002 \u3000\u3000\u5e78\u4e8f\u6768\u5a49\u5148\u884c\u51fa\u624b\uff0c\u91c7\u53d6\u4e86\u4e3b\u52a8\uff0c\u8fd9\u624d\u514d\u906d\u6697\u7b97\u3002 \u3000\u3000\u7f57\u4fca\u970d\u5730\u4e00\u4e2a\u201c\u51e4\u70b9\u5934\u201d\uff0c\u907f\u8fc7\u4e86\u6768\u5a49\u7684\u64d2\u62ff\uff0c\u864e\u53e3\u5374\u662f\u7ed9\u5979\u7684\u6307\u5c16\u70b9\u7740\uff0c\u706b\u8fa3\u8fa3\u7684\u4f5c \u75bc\uff0c\u8fd8\u5e78\u6ca1\u6709\u70b9\u6b63\u7a74\u9053\u3002\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u4e00\u5200\u4e00\u5251\uff0c\u5df2\u662f\u4ece\u6768\u5a49\u80cc\u540e\u523a\u6765\u3002\u7f57\u4fca\u4e5f\u7acb\u5373\u56de \u8eab\uff0c\u98de\u811a\u5411\u6768\u5a49\u8e22\u53bb\uff0c\u5927\u559d\u9053\uff1a\u201c\u539f\u6765\u4f60\u662f\u5978\u7ec6\uff01\u201d \u3000\u3000\u7f57\u4fca\u7684\u672c\u9886\u867d\u7136\u4e0d\u53ca\u6768\u5a49\uff0c\u4f46\u4ed6\u8eab\u4e3a\u98de\u9f99\u5c71\u7684\u526f\u5be8\u4e3b\uff0c\u5f53\u7136\u4e5f\u6709\u4ed6\u7684\u72ec\u5230\u7edd\u6280\uff0c\u8fd9\u201c\u9f50 \u7709\u817f\u201d\u5c31\u662f\u4ed6\u7684\u5f97\u610f\u7edd\u62db\u3002 \u3000\u3000\u6768\u5a49\u5728\u4e09\u9762\u5939\u653b\u4e4b\u4e0b\uff0c\u907f\u5f97\u5f00\u5de6\u9762\u7684\u5200\uff0c\u907f\u4e0d\u5f00\u53f3\u9762\u7684\u5251\uff0c\u5018\u82e5\u8df3\u4e0a\u4e00\u7ea7\u77f3\u9636\uff0c\u53c8\u975e\u7ed9 \u7f57\u4fca\u7684\u201c\u9f50\u7709\u817f\u201d\u8e22\u4e2d\u4e0d\u53ef\u3002\u773c\u770b\u5df2\u662f\u96be\u4ee5\u907f\u514d\u53d7\u4f24\uff0c\u5ffd\u542c\u5f97\u201c\u94db\u94db\u201d\u4e24\u58f0\uff0c\u4e00\u5200\u4e00\u5251\u540c\u65f6 \u98de\u4e0a\u4e86\u534a\u7a7a\u3002\u539f\u6765\u662f\u8c37\u6db5\u865a\u53d1\u51fa\u7684\u4e24\u679a\u77f3\u5b50\uff0c\u5c06\u90a3\u4e24\u4eba\u7684\u5200\u5251\u6253\u843d\u4e86\u3002 \u3000\u3000\u7f57\u4fca\u5c45\u9ad8\u4e34\u4e0b\uff0c\u4e00\u811a\u8e22\u5230\u6768\u5a49\u7684\u9762\u95e8\uff0c\u6768\u5a49\u970d\u5730\u4e00\u4e2a\u201c\u51e4\u70b9\u5934\u201d\uff0c\u6325\u8896\u4e00\u5377\uff0c\u515c\u7740\u978b \u5e95\uff0c\u4f7f\u51fa\u501f\u529b\u7684\u529f\u592b\uff0c\u7f57\u4fca\u8eab\u5b50\u60ac\u7a7a\uff0c\u5934\u4e0b\u811a\u4e0a\u5730\u51b2\u4e86\u4e0b\u53bb\u3002\u8c37\u6db5\u865a\u4e00\u628a\u6293\u7740\u4ed6\u7684\u540e\u5fc3\uff0c\u5f53 \u4f5c\u4e34\u65f6\u76fe\u724c\u6765\u4f7f\uff0c\u5927\u559d\u9053\uff1a\u201c\u6321\u7740\u6211\u6b7b\uff0c\u907f\u7740\u6211\u751f\uff01\u201d\u5413\u5f97\u7f57\u4fca\u9b42\u98de\u9b44\u6563\uff0c\u53eb\u9053\uff1a\u201c\u8bc0\uff0c\u5feb \u7ed9\u8fd9\u4f4d\u597d\u6c49\u8ba9\u5f00\u6761\u8def\uff01\u201d\u90a3\u4e9b\u4eba\u4e00\u6765\u662f\u6050\u9632\u4f24\u4e86\u7f57\u4fca\uff0c\u4e8c\u6765\u770b\u89c1\u8c37\u6db5\u865a\u8fd9\u526f\u51f6\u795e\u6076\u715e\u7684\u6a21 \u6837\uff0c\u4e5f\u662f\u5413\u5f97\u614c\u4e86\uff0c\u8fde\u5fd9\u4e00\u7a9d\u8702\u5730\u9000\u5165\u4e86\u201c\u805a\u4e49\u5385\u201d\u4e2d\u3002 \u3000\u3000\u8c37\u6db5\u865a\u54c8\u54c8\u5927\u7b11\uff0c\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u53ae\u4e0d\u503c\u5f97\u6211\u6765\u6740\u4f60\uff01\u201d\u4e00\u4e2a\u65cb\u98ce\u6025\u821e\uff0c\u628a\u7f57\u4fca\u629b\u4e0b\u77f3 \u9636\u3002 \u3000\u3000\u8c37\u3001\u6768\u4e8c\u4eba\u51b2\u5165\u5927\u5802\uff0c\u8c37\u6db5\u865a\u62ac\u5934\u4e00\u770b\uff0c\u89c1\u9ed1\u77f3\u9053\u4eba\u7ed9\u7aa6\u5b89\u5e73\u6293\u7740\u4e86\u7435\u7436\u9aa8\uff0c\u6700\u4e3a\u5371 \u9669\uff0c\u4e25\u70f7\u8eab\u9677\u91cd\u56f4\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u5374\u8fd8\u6ca1\u6709\u6027\u547d\u4e4b\u5fe7\u3002 \u3000\u3000\u7aa6\u5b89\u5e73\u77e5\u9053\u6765\u4e86\u5f3a\u654c\uff0c\u6b63\u8981\u52a0\u4e0a\u628a\u52b2\uff0c\u628a\u9ed1\u77f3\u9053\u4eba\u7684\u7435\u7436\u9aa8\u634f\u788e\uff0c\u8c37\u6db5\u865a\u559d\u9053\uff1a\u201c\u6492 \u624b\uff01\u201d\u4eba\u672a\u5230\u638c\u5148\u53d1\uff0c\u7aa6\u5b89\u5e73\u7ed9\u4ed6\u7684\u5288\u7a7a\u638c\u529b\u4e00\u9707\uff0c\u8eab\u5b50\u4e0d\u7531\u5f97\u5411\u524d\u503e\u659c\uff0c\u624b\u4e0a\u7684\u52b2\u529b\u767b\u65f6 \u677e\u4e86\uff0c\u9ed1\u77f3\u9053\u4eba\u4e00\u4e2a\u6c89\u80a9\u7f29\u65f6\uff0c\u8131\u51fa\u4e86\u7aa6\u5b89\u5e73\u7684\u638c\u63e1\u3002 \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u559d\u9053\uff1a\u201c\u7aa6\u5b89\u5e73\uff0c\u6211\u8fd9\u6761\u6027\u547d\u662f\u6b7b\u91cc\u9003\u751f\u7684\uff0c\u4f60\u6551\u8fc7\u6211\u4e00\u6b21\uff0c\u5982\u4eca\u4f60\u7b49\u4e8e\u662f\u6740 \u6211\u4e00\u6b21\uff0c\u4e24\u76f8\u626f\u76f4\uff0c\u6211\u6b20\u4f60\u7684\u503a\u5df2\u7ecf\u8fd8\u4e86\u3002\u4ece\u4eca\u4e4b\u540e\uff0c\u54b1\u4eec\u662f\u6069\u65ad\u4e49\u7edd\uff01\u201d \u3000\u3000\u7aa6\u5b89\u5e73\u6697\u6697\u540e\u6094\u6ca1\u6709\u53ca\u65e9\u6740\u6389\u9ed1\u77f3\u9053\u4eba\uff0c\u4f46\u6043\u7740\u4eba\u591a\uff0c\u5374\u4e5f\u4e0d\u60e7\uff0c\u62ff\u8d77\u4e86\u4e00\u5bf9\u62a4\u624b\u94a9\uff0c \u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u4f60\u8fd9\u5403\u91cc\u6252\u5916\u7684\u725b\u9f3b\u5b50\u81ed\u9053\u58eb\uff0c\u6211\u4e0e\u4f60\u8fd8\u6709\u4ec0\u4e48\u60c5\u4e49\u53ef\u8a00\uff1f\u4f60\u4ee5\u4e3a\u4f60\u6765\u4e86\u6551 \u5175\uff0c\u5c31\u53ef\u4ee5\u9003\u5f97\u51fa\u53bb\u4e48\uff1f\u54fc\uff0c\u54fc\uff0c\u4f60\u7684\u6027\u547d\u8fd8\u662f\u5728\u6211\u7684\u638c\u63e1\u4e4b\u4e2d\uff01\u201d \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u9053\uff1a\u201c\u5982\u4eca\u4f60\u8981\u53d6\u6211\u6027\u547d\uff0c\u6050\u6015\u96be\u4e86\u3002\u201d\u5200\u5149\u4e00\u95ea\uff0c\u683c\u5f00\u7aa6\u5b89\u5e73\u7684\u53cc\u94a9\u3002\u4ed6\u7684 \u5feb\u5200\u4e43\u662f\u6b66\u6797\u4e00\u7edd\uff0c\u8f6c\u773c\u4e4b\u95f4\uff0c\u95ea\u7535\u822c\u5730\u5288\u51fa\u4e86\u516d\u516d\u4e09\u5341\u516d\u5200\u3002\u53ea\u542c\u788d\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u7aa6\u5b89 \u5e73\u7684\u8863\u9886\u7ed9\u4ed6\u5212\u7834\uff0c\u5200\u950b\u76f4\u6307\u5230\u4e86\u54bd\u5589\uff01\u9ed1\u77f3\u9053\u4eba\u559d\u9053\uff1a\u201c\u6211\u518d\u9976\u4f60\u4e00\u6b21\uff0c\u4f60\u8fd8\u4e0d\u6094\u609f \u4e48\uff1f\u201d \u3000\u3000\u7aa6\u5b89\u5e73\u8d81\u6b64\u65f6\u673a\uff0c\u4e00\u4e2a\u201c\u62bd\u64a4\u8fde\u73af\u201d\uff0c\u62a4\u624b\u94a9\u5f80\u524d\u4e00\u9001\uff0c\u53cd\u800c\u94a9\u4f24\u4e86\u9ed1\u77f3\u9053\u4eba\u7684\u5c0f\u8179\u3002 \u9ed1\u77f3\u9053\u4eba\u5fcd\u7740\u75db\uff0c\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u771f\u662f\u81f3\u6b7b\u4e0d\u609f\uff0c\u6211\u53ea\u597d\u4e0e\u4f60\u62fc\u4e86\u8fd9\u6761\u6027\u547d\u4e86\uff01\u201d \u3000\u3000\u7aa6\u5b89\u5e73\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u73b0\u5728\u5df2\u662f\u91dc\u5e95\u6e38\u9b42\uff0c\u8fd8\u60f3\u4e0e\u6211\u62fc\u547d\uff01\u201d\u53cc\u94a9\u98de\u821e\uff0c\u5de6\u4e00\u62db\u201c\u7384\u4e4c\u5212 \u7802\u201d\uff0c\u53f3\u4e00\u62db\u201c\u767b\u5c71\u8de8\u864e\u201d\uff0c\u94a9\u5149\u95ea\u95ea\uff0c\u6070\u4f3c\u53cc\u9f99\u51fa\u6d77\uff0c\u88f9\u4f4f\u4e86\u9ed1\u77f3\u9053\u4eba\u7684\u4e00\u67c4\u5355\u5200\u3002 \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u5feb\u5200\u75be\u5288\uff0c\u53ee\u53ee\u94db\u94db\u4e4b\u58f0\u4e0d\u7edd\u4e8e\u8033\u3002\u53ef\u662f\u5374\u59cb\u7ec8\u6446\u8131\u4e0d\u4e86\u53cc\u94a9\u7684\u5c01\u9501\uff0c\u5200\u950b\u4e00 \u5230\u4e86\u5bf9\u65b9\u7684\u4e09\u5c3a\u4e4b\u5185\uff0c\u4fbf\u65e0\u6cd5\u653b\u5f97\u8fdb\u53bb\u3002\u6fc0\u6218\u4e2d\u9ed1\u77f3\u9053\u4eba\u53ea\u89c9\u4e00\u6761\u53f3\u81c2\u4e00\u9635\u9635\u7684\u9178\u75db\uff0c\u5feb\u5200 \u5288\u51fa\uff0c\u5df2\u662f\u529b\u4e0d\u4ece\u5fc3\uff0c\u6e10\u6e10\u6709\u70b9\u4f7f\u5524\u4e0d\u7075\u4e86\u3002 \u3000\u3000\u8fd9\u5e76\u4e0d\u662f\u9ed1\u77f3\u9053\u4eba\u6280\u4e0d\u5982\u4eba\uff0c\u4e5f\u5e76\u975e\u7aa6\u5b89\u5e73\u5df2\u60f3\u51fa\u4e86\u7834\u4ed6\u5feb\u5200\u4e4b\u6cd5\u3002\u800c\u662f\u56e0\u4e3a\u9ed1\u77f3\u9053\u4eba \u53f3\u80a9\u7684\u7435\u7436\u9aa8\u7ed9\u634f\u4f24\u4e86\u4e00\u5757\u9aa8\u5934\uff0c\u6597\u5230\u4e86\u6570\u5341\u62db\u4e4b\u540e\uff0c\u65e0\u6cd5\u652f\u6301\u4e0b\u53bb\u3002 \u3000\u3000\u7aa6\u5b89\u5e73\u6b65\u6b65\u7d27\u903c\uff0c\u9ed1\u77f3\u9053\u4eba\u4e00\u54ac\u7259\u6839\uff0c\u5200\u4ea4\u5de6\u624b\uff0c\u4f9d\u7136\u987d\u5f3a\u4f5c\u6218\u3002\u7aa6\u5b89\u5e73\u51b7\u7b11\u9053\uff1a\u201c\u56f0 \u517d\u4e4b\u6597\uff0c\u53c8\u6709\u4f55\u7528\uff1f\u201d \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u4e00\u6765\u662f\u56e0\u4e3a\u5de6\u624b\u4f7f\u5200\u4e0d\u5982\u53f3\u624b\u7684\u7eaf\u719f\uff1b\u4e8c\u6765\u662f\u5c0f\u8179\u7684\u94a9\u4f24\u8840\u8fd8\u672a\u6b62\uff0c\u6fc0\u6597\u4e4b \u4e0b\uff0c\u4f24\u53e3\u6269\u5927\uff0c\u9c9c\u8840\u66f4\u662f\u6c69\u6c69\u6d41\u51fa\uff1b\u4e8c\u6765\u53f3\u80a9\u7684\u7435\u7436\u9aa8\u75db\u5f97\u6709\u5982\u706b\u70e7\uff0c\u867d\u7136\u5de6\u624b\u4f7f\u5200\uff0c\u4ea6\u662f \u53d7\u5230\u5f71\u54cd\u3002\u662f\u4ee5\u6700\u521d\u7684\u5341\u591a\u4e8c\u5341\u62db\uff0c\u52c9\u5f3a\u8fd8\u53ef\u4ee5\u652f\u6301\uff0c\u4e8c\u5341\u62db\u8fc7\u540e\u3001\u53c8\u7ed9\u7aa6\u5b89\u5e73\u7684\u53cc\u94a9\u88f9 \u4f4f\uff0c\u53ea\u6709\u62db\u67b6\u4e4b\u529f\u4e86\u3002\u9ed1\u77f3\u9053\u4eba\u6697\u6697\u540e\u6094\uff0c\u540e\u6094\u521a\u624d\u4e0d\u8be5\u624b\u4e0b\u7559\u60c5\uff0c\u4ee5\u81f4\u7eb5\u864e\u8d3b\u60a3\u3002 \u3000\u3000\u8c37\u6db5\u865a\u7ed9\u9ed1\u77f3\u9053\u4eba\u89e3\u56f0\u4e4b\u540e\uff0c\u56e0\u4e3a\u4e25\u70f7\u7684\u5f62\u52bf\u4e5f\u662f\u5341\u5206\u5371\u6025\uff0c\u4ed6\u662f\u548c\u9ed1\u77f3\u9053\u4eba\u4ea4\u8fc7\u624b\uff0c \u77e5\u9053\u4ed6\u7684\u529f\u592b\uff0c\u53ea\u9053\u9ed1\u77f3\u9053\u4eba\u89e3\u56f0\u4e4b\u540e\uff0c\u72ec\u81ea\u5bf9\u4ed8\u7aa6\u5b89\u5e73\uff0c\u6599\u60f3\u4e0d\u4f1a\u5403\u4e8f\uff0c\u4e8e\u662f\u4fbf\u4e0d\u518d\u53bb\u7406 \u4f1a\u4ed6\u8fd9\u4e00\u8fb9\uff0c\u5148\u6551\u4e25\u70f7\u3002 \u3000\u3000\u4e25\u70f7\u770b\u89c1\u8c37\u6db5\u865a\u6765\u5230\uff0c\u7cbe\u795e\u9661\u632f\u3002\u53ea\u542c\u5f97\u8c37\u6db5\u865a\u9739\u96f3\u4f3c\u7684\u4e00\u58f0\u5927\u559d\uff0c\u95ea\u7535\u80a1\u5730\u6251\u5165\u4e86\u91cd \u56f4\uff0c\u53cc\u638c\u8fde\u73af\u5288\u51fa\uff0c\u767b\u65f6\u6709\u4e24\u4e2a\u4eba\u5012\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u8c37\u6db5\u865a\u4f7f\u5f00\u4e86\u521a\u731b\u65e0\u4f26\u7684\u201c\u5929\u96f7\u529f\u201d\uff0c\u6321\u8005\u8f9f\u6613\uff0c\u4e0d\u6d88\u7247\u523b\uff0c\u5df2\u662f\u6709\u56db\u4e94\u4eba\u4f24\u5728\u4ed6\u7684\u638c \u4e0b\uff0c\u4e25\u70f7\u4e5f\u523a\u4f24\u4e86\u4e24\u4e09\u4e2a\u4eba\uff0c\u5176\u4f59\u7684\u4eba\u90fd\u5413\u5f97\u614c\u4e86\uff0c\u8c01\u4e5f\u4e0d\u6562\u903c\u8fd1\u4ed6\u4eec\u3002 \u3000\u3000\u4e25\u70f7\u6740\u51fa\u91cd\u56f4\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u53bb\u5e2e\u660e\u971e\u8868\u59b9\uff0c\u4f60\u52a9\u4f60\u7684\u891a\u5e08\u5144\u4e00\u81c2\u4e4b\u529b\u3002\u201d\u8c37\u6db5\u865a\u8fdb\u6765\u4e4b \u65f6\uff0c\u65e9\u5df2\u6ce8\u610f\u5230\u891a\u4e91\u5cf0\u548c\u767d\u4e07\u96c4\u4ea4\u624b\u7684\u60c5\u5f62\uff0c\u77e5\u9053\u767d\u4e07\u96c4\u662f\u98de\u9f99\u5c71\u4e0a\u6700\u5f3a\u7684\u4e00\u4e2a\u654c\u624b\uff0c\u891a\u4e91 \u5cf0\u4e45\u6218\u4e0b\u53bb\u5b9a\u5fc5\u5403\u4e8f\u3002\u6b64\u65f6\u62ac\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u891a\u4e91\u5cf0\u679c\u7136\u662f\u5df2\u7ecf\u7ed9\u767d\u4e07\u96c4\u903c\u5f97\u8fde\u8fde\u540e\u9000\u3002 \u3000\u3000\u8c37\u6db5\u865a\u53eb\u9053\uff1a\u201c\u891a\u5e08\u5144\uff0c\u96f7\u7535\u4ea4\u8f70\uff01\u201d\u767d\u4e07\u96c4\u51b7\u7b11\u9053\uff1a\u201c\u4ec0\u4e48\u96f7\u7535\u4ea4\u8f70\uff01\u4f60\u8fd9\u4e11\u516b\u602a\u5413 \u5413\u522b\u4eba\u72b9\u53ef\uff0c\u60f3\u5413\u6211\u4e48\uff1f\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u9661\u7136\u95f4\u53ea\u89c9\u5f97\u4e00\u80a1\u5de8\u529b\u63a8\u6765\uff0c\u891a\u4e91\u5cf0\u4e0e\u8c37\u6db5\u865a\u540c\u65f6\u4f7f \u51fa\u4e86\u201c\u5929\u96f7\u529f\u201d\u3002\u9976\u662f\u81ea\u4e07\u96c4\u5185\u529f\u6df1\u539a\uff0c\u7ed9\u8fd9\u4e24\u80a1\u638c\u529b\u4f1a\u5408\u4e00\u971e\uff0c\u80f8\u53e3\u767b\u65f6\u4e5f\u4f3c\u53d7\u5230\u94c1\u9524\u4e00 \u51fb\uff0c\u80f8\u4e2d\u6c14\u8840\u7ffb\u6d8c\uff0c\u4e94\u8d43\u516d\u8151\u90fd\u597d\u50cf\u8981\u7ffb\u8f6c\u8fc7\u6765\u3002 \u3000\u3000\u8c37\u3001\u891a\u4e8c\u4eba\u5408\u529b\u4f7f\u51fa\u4e86\u4e00\u62db\u201c\u96f7\u7535\u4ea4\u8f70\u201d\uff0c\u7acb\u5373\u53cc\u5251\u9f50\u51fa\uff0c\u75be\u523a\u8fc7\u53bb\u3002\u767d\u4e07\u96c4\u7acb\u8db3\u672a \u7a33\uff0c\u5598\u606f\u672a\u5b9a\uff0c\u53ea\u542c\u5f97\u55e4\u55e4\u54cd\u54cd\uff0c\u5728\u7cbe\u8292\u7535\u5c04\u4e4b\u4e2d\uff0c\u4ed6\u7684\u4e24\u6761\u8863\u8896\uff0c\u5316\u6210\u4e00\u7247\u7247\u8774\u8776\u3002\u8c37\u3001 \u891a\u4e8c\u4eba\u89c1\u4ed6\u5c45\u7136\u80fd\u591f\u7528\u8863\u8896\u5316\u89e3\u5251\u62db\uff0c\u4e5f\u662f\u5f53\u771f\u4e0d\u6562\u8f7b\u654c\u3002 \u3000\u3000\u767d\u4e07\u96c4\u4e5f\u771f\u4e0d\u6127\u662f\u4e00\u6d3e\u6b66\u5b66\u5927\u5e08\uff0c\u63a5\u8fde\u9000\u4e86\u516b\u6b65\uff0c\u6bcf\u9000\u4e00\u6b65\uff0c\u5c31\u6d88\u89e3\u4e86\u5bf9\u65b9\u7684\u4e00\u5206\u529f \u52bf\uff0c\u9000\u5230\u4e86\u7b2c\u516b\u6b65\uff0c\u9635\u811a\u7a33\u5b9a\u4e0b\u6765\uff0c\u89e3\u5f00\u4e86\u675f\u8170\u7684\u76ae\u5e26\uff0c\u5f53\u4f5c\u8f6f\u97ad\uff0c\u76d8\u65cb\u98de\u821e\uff0c\u6321\u4f4f\u4e86\u8c37\u3001 \u891a\u4e8c\u4eba\u7684\u957f\u5251\u3002 \u3000\u3000\u7528\u201c\u5929\u96f7\u529f\u201d\u9887\u4e3a\u8017\u635f\u771f\u529b\uff0c\u8c37\u3001\u891a\u4e8c\u4eba\u9762\u4e34\u5f3a\u654c\uff0c\u4e0d\u6562\u591a\u7528\uff0c\u6597\u4e86\u6570\u5341\u62db\uff0c\u5f85\u767d\u4e07\u96c4 \u5c06\u8981\u53cd\u5ba2\u4e3a\u4e3b\u4e4b\u65f6\uff0c\u65b9\u59cb\u518d\u6765\u4e00\u62db\u201c\u96f7\u7535\u4ea4\u8f70\u201d\u3001\u91cd\u593a\u5148\u624b\uff0c\u538b\u4e0b\u4ed6\u7684\u51f6\u7130\u3002\u4f46\u5982\u6b64\u4e00\u6765\uff0c \u8c37\u891a\u4e8c\u4eba\u867d\u7136\u662f\u59cb\u7ec8\u5360\u5f97\u4e0a\u98ce\uff0c\u8981\u60f3\u5728\u6025\u5207\u4e4b\u95f4\u6253\u8d25\u767d\u4e07\u96c4\u5374\u4e5f\u662f\u52bf\u6240\u4e0d\u80fd\u4e86\u3002 \u3000\u3000\u4e25\u70f7\u6740\u51fa\u91cd\u56f4\uff0c\u548c\u5b5f\u660e\u971e\u4f1a\u5408\u3002\u5b5f\u660e\u971e\u7cbe\u795e\u9661\u632f\uff0c\u4e00\u62db\u201c\u5927\u6f20\u5b64\u70df\u201d\uff0c\u5251\u76f4\u5982\u77e2\uff0c\u5411\u9633 \u575a\u767d\u5f84\u523a\u8fc7\u53bb\u3002\u4e25\u70f7\u5212\u4e86\u4e00\u9053\u5706\u5f27\uff0c\u5251\u52bf\u5982\u73af\uff0c\u7f69\u4f4f\u4e86\u9633\u575a\u767d\u7684\u4e0a\u4e09\u8def\u3002\u5979\u4f7f\u7684\u8fd9\u62db\u6709\u4e2a\u540d \u5802\uff0c\u53eb\u505a\u201c\u957f\u6cb3\u843d\u65e5\u201d\uff0c\u6070\u597d\u548c\u5b5f\u660e\u971e\u90a3\u62db\u201c\u5927\u6f20\u5b64\u70df\u201d\u914d\u5408\u5f97\u5929\u8863\u65e0\u7f1d\u3002 \u3000\u3000\u9633\u575a\u767d\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u53c8\u6765\u4e86\u4e00\u4e2a\u7f8e\u4eba\u513f\uff0c\u6211\u8fd9\u8273\u798f\u53ef\u771f\u4e0d\u6d45\u554a\uff01\u201d\u7b11\u58f0\u672a\u5df2\uff0c\u201c\u54ce\u54df\u201d \u5730\u53eb\u4e86\u4e00\u58f0\uff0c\u80a9\u5934\u5df2\u662f\u7ed9\u5b5f\u660e\u971e\u7684\u5229\u5251\u5212\u7834\u4e86\u4e00\u9053\u4e09\u5bf8\u591a\u957f\u7684\u4f24\u53e3\u3002\u5e78\u800c\u6ca1\u6709\u4f24\u53ca\u9aa8\u5934\u3002 \u3000\u3000\u539f\u6765\u4e25\u70f7\u548c\u5b5f\u660e\u971e\u7684\u5251\u6cd5\u867d\u7136\u4e0d\u662f\u540c\u51fa\uff0c\u4f46\u56e0\u5979\u4eec\u66fe\u7ecf\u5728\u4e00\u8d77\u5f7c\u6b64\u5207\u78cb\uff0c\u6545\u6b64\u5404\u81ea\u7528\u5bb6 \u4f20\u7684\u5251\u6cd5\uff0c\u4e5f\u80fd\u591f\u914d\u5408\u5f97\u5341\u5206\u4e25\u5bc6\uff0c\u6709\u5982\u4e00\u4e2a\u4eba\u4f7f\u5f00\u53cc\u5251\u4e00\u822c\uff0c\u65e0\u61c8\u53ef\u51fb\u3002\u9633\u575a\u767d\u7684\u672c\u9886\u53ea \u4e0d\u8fc7\u7565\u80dc\u5b5f\u660e\u971e\u4e00\u7b79\uff0c\u52a0\u4e0a\u4e86\u4e00\u4e2a\u4e25\u70f7\uff0c\u5f53\u7136\u5c31\u4e0d\u662f\u654c\u624b\u4e86\u3002 \u3000\u3000\u98de\u9f99\u5c71\u4e0a\u7684\u5934\u76ee\u7eb7\u7eb7\u8d76\u5230\uff0c\u201c\u805a\u4e49\u5385\u201d\u867d\u7136\u751a\u5927\uff0c\u5374\u4e5f\u5bb9\u7eb3\u4e0d\u4e0b\u3002\u7aa6\u5b89\u5e73\u6b64\u65f6\u5df2\u662f\u5927\u5360 \u4e0a\u98ce\uff0c\u559d\u9053\uff1a\u201c\u7528\u4e0d\u7740\u614c\u4e71\uff0c\u4e01\u5802\u4e3b\u3001\u5218\u5be8\u4e3b\u3001\u9e7f\u8001\u524d\u8f88\u3001\u9093\u4e03\u54e5\uff0c\u4f60\u4eec\u6311\u9009\u4e03\u516b\u4e2a\u4eba\u8fdb \u6765\uff0c\u5176\u4ed6\u7684\u5404\u56de\u539f\u4f4d\u3002\u201d \u3000\u3000\u4e01\u5802\u4e3b\u548c\u5218\u5802\u4e3b\u662f\u98de\u9f99\u5c71\u672c\u9886\u6700\u9ad8\u7684\u4e24\u4f4d\u5927\u5934\u76ee\uff0c\u201c\u9e7f\u8001\u524d\u8f88\u201d\u662f\u5180\u5317\u7684\u540d\u6b66\u5e08\u9e7f\u5357 \u6f33\uff0c\u201c\u9093\u4e03\u54e5\u201d\u662f\u91d1\u9a7c\u5be8\u7684\u5be8\u4e3b\uff0c\u8fd9\u4e24\u4eba\u548c\u7aa6\u5b89\u5e73\u90fd\u662f\u6570\u5341\u5e74\u7684\u4ea4\u60c5\u3002\u8fd9\u6b21\u7aa6\u5b89\u5e73\u7279\u5730\u8bf7\u4ed6 \u4eec\u6765\u52a9\u62f3\u7684\u3002 \u3000\u3000\u4e01\u3001\u5218\u3001\u9e7f\u3001\u9093\u7b49\u4eba\u8fdb\u53bb\u4ee5\u540e\uff0c\u4e01\u3001\u5218\u4e8c\u4eba\u4fbf\u8981\u4e0a\u524d\u64d2\u62ff\u9ed1\u77f3\u9053\u4eba\uff0c\u7aa6\u5b89\u5e73\u81ea\u6043\u80dc\u5238\u5728 \u624b\uff0c\u8981\u5728\u624b\u4e0b\u9762\u524d\u663e\u663e\u5a01\u98ce\uff0c\u559d\u9053\uff1a\u201c\u4f60\u4eec\u4e0d\u957f\u773c\u775b\u5417\uff0c\u5feb\u53bb\u52a9\u9633\u516c\u5b50\u3002\u9e7f\u8001\u524d\u8f88\uff0c\u9093\u4e03 \u54e5\uff0c\u8bf7\u4f60\u4eec\u5e2e\u5e2e\u767d\u8001\u82f1\u96c4\uff0c\u628a\u90a3\u4e24\u4e2a\u5978\u7ec6\u62ff\u4e0b\u3002\u201d \u3000\u3000\u9e7f\u5357\u6f33\u548c\u9093\u4e03\u6b66\u529f\u90fd\u5f88\u4e0d\u5f31\uff0c\u4e24\u4eba\u4e00\u4e0a\uff0c\u891a\u4e91\u5cf0\u548c\u8c37\u6db5\u865a\u6210\u4e86\u4ee5\u4e8c\u654c\u4e09\uff0c\u653b\u52bf\u767b\u65f6\u5c31\u7ed9 \u5bf9\u65b9\u593a\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u53e6\u4e00\u8fb9\uff0c\u98de\u9f99\u5c71\u7684\u4e24\u4e2a\u5927\u5934\u76ee\u5218\u4e07\u548c\u4e01\u5146\u4ea6\u5df2\u4e0a\u524d\uff0c\u5411\u5b5f\u660e\u971e\u3001\u4e25\u70f7\u5c55\u5f00\u4e86\u653b\u51fb\u3002\u8fd9\u4e24 \u4e2a\u4eba\u867d\u7136\u4e0d\u662f\u4e00\u6d41\u9ad8\u624b\uff0c\u6b66\u529f\u4ea6\u975e\u6cdb\u6cdb\u3002\u5218\u4e07\u4f7f\u7684\u662f\u5927\u780d\u5200\uff0c\u5200\u91cd\u529b\u6c89\uff0c\u4e00\u5200\u5288\u4e0b\uff0c\u5b5f\u660e\u971e \u6a2a\u5251\u4e00\u5c01\uff0c\u53ea\u542c\u5f97\u201c\u94db\u201d\u7684\u4e00\u58f0\uff0c\u706b\u82b1\u98de\u6e85\uff0c\u6697\u6697\u5403\u4e86\u4e00\u60ca\uff1a\u201c\u8fd9\u4eba\u6c14\u529b\u5012\u662f\u4e0d\u5c0f\uff01\u201d \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u4e01\u5146\u7684\u4e03\u8282\u97ad\u4e00\u4e2a\u201c\u56de\u98ce\u626b\u67f3\u201d\uff0c\u4e5f\u5411\u4e25\u70f7\u626b\u4e86\u8fc7\u6765\u3002\u4e25\u70f7\u4f7f\u51fa\u79fb\u5f62 \u6362\u4f4d\u7684\u8f7b\u529f\u8eab\u6cd5\uff0c\u5251\u5982\u98de\u51e4\uff0c\u53cd\u624b\u524a\u53bb\u3002\u8fd9\u4e00\u62db\u7528\u5f97\u5341\u5206\u5de7\u5999\uff0c\u4e01\u5146\u7684\u957f\u97ad\u626b\u4e86\u4e2a\u7a7a\uff0c\u773c\u770b \u4e25\u70f7\u7684\u53cd\u624b\u5251\u524a\u6765\uff0c\u82e5\u4e0d\u6254\u97ad\uff0c\u624b\u6307\u5c31\u975e\u7ed9\u5979\u7684\u5229\u5251\u524a\u65ad\u4e0d\u53ef\uff0c\u5ffd\u542c\u5f97\u201c\u5530\u201d\u7684\u4e00\u58f0\uff0c\u9633\u575a \u767d\u4e00\u5251\u523a\u6765\uff0c\u7b11\u9053\uff1a\u201c\u4e25\u59d1\u5a18\u4f11\u5f97\u901e\u80fd\uff0c\u8fd8\u6709\u6211\u5462\uff01\u201d\u9633\u575a\u767d\u7684\u672c\u9886\u8fdc\u5728\u4e01\u5146\u4e4b\u4e0a\uff0c\u8fd9\u4e00\u5251 \u6b63\u662f\u653b\u654c\u4e4b\u6240\u5fc5\u6551\uff0c\u4e25\u70f7\u542c\u5f97\u80cc\u540e\u91d1\u5203\u5288\u98ce\u4e4b\u58f0\uff0c\u53ea\u597d\u56de\u5251\u906e\u62e6\u3002 \u3000\u3000\u5b5f\u660e\u971e\u8bd5\u51fa\u4e86\u5218\u4e07\u6c14\u529b\u4e0d\u5f31\uff0c\u7acb\u5373\u6539\u7528\u4ee5\u5de7\u964d\u529b\u7684\u6253\u6cd5\uff0c\u5251\u5c16\u8f7b\u8f7b\u4e00\u6311\uff0c\u7fe9\u82e5\u60ca\u9e3f\u7684\u4e00 \u63a0\u5373\u8fc7\uff0c\u5f85\u788d\u5bf9\u65b9\u7684\u62db\u6570\u7528\u8001\uff0c\u7acb\u5373\u4fbf\u662f\u4e00\u62db\u201c\u5999\u89e3\u8fde\u73af\u201d\uff0c\u5316\u89e3\u4e86\u5bf9\u65b9\u7684\u529b\u9053\uff0c\u628a\u5218\u4e07\u7684 \u5927\u5200\u62e8\u8fc7\u4e00\u8fb9\u3002\u53ea\u8981\u518d\u6765\u4e00\u4e2a\u6b3a\u8eab\u8fdb\u5251\uff0c\u5c31\u53ef\u4ee5\u523a\u7a7f\u5218\u4e07\u7684\u54bd\u5589\u3002\u9633\u575a\u767d\u500f\u7684\u8f6c\u8eab\uff0c\u6487\u5f00\u4e86 \u4e25\u70f7\uff0c\u53c8\u662f\u4e00\u62db\u653b\u654c\u4e4b\u6240\u5fc5\u6551\u7684\u5251\u6cd5\uff0c\u4f7f\u5f97\u5b5f\u660e\u971e\u65e0\u6687\u5bf9\u5218\u4e07\u65bd\u5c55\u6740\u624b\uff0c\u53ea\u597d\u5148\u5e94\u4ed8\u4ed6\u3002 \u3000\u3000\u9633\u575a\u767d\u5360\u4e86\u4e0a\u98ce\uff0c\u5f97\u610f\u4e4b\u6781\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4f4d\u6f02\u4eae\u7684\u5c0f\u59d0\u9001\u4e0a\u95e8\u6765\uff0c\u9633\u67d0\u5c82\u80fd\u6ca1 \u6709\u601c\u9999\u60dc\u7389\u4e4b\u4ed3\uff0c\u4e0d\u52a0\u63a5\u7eb3\uff1f\u4f60\u4eec\u4e0d\u5fc5\u5bb3\u6015\uff0c\u6211\u662f\u820d\u4e0d\u5f97\u4f24\u5bb3\u7f8e\u4eba\u513f\u7684\u3002\u4e0d\u8fc7\u4f60\u4eec\u4e5f\u5f97\u77e5\u8da3 \u4e00\u70b9\uff0c\u4e56\u4e56\u7684\u5c06\u6b66\u5668\u653e\u4e0b\u6765\u5427\uff0c\u5426\u5219\u5200\u5251\u65e0\u60c5\uff0c\u4e00\u6709\u5931\u624b\uff0c\u6bc1\u4e86\u4f60\u4eec\u7684\u5bb9\u8c8c\uff0c\u6211\u4e5f\u662f\u975e\u5e38\u9057 \u61be\u7684\u554a\uff01\u201d \u3000\u3000\u4e25\u70f7\u9053\uff1a\u201c\u72d7\u5634\u91cc\u4e0d\u957f\u8c61\u7259\uff0c\u4e0d\u503c\u5f97\u4e3a\u5b83\u751f\u6c14\uff01\u201d\u5b5f\u660e\u971e\u84e6\u7136\u4e00\u9192\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5bf9\u4ed8\u6076 \u72d7\uff0c\u53ea\u6709\u5bb0\u4e4b\uff0c\u96be\u9053\u8fd8\u8981\u548c\u5b83\u5435\u5634\u4e48\uff1f\u201d\u538b\u4e0b\u5fc3\u4e2d\u6012\u706b\uff0c\u4e0e\u9633\u575a\u767d\u7740\u7740\u62a2\u653b\u3002\u53ef\u662f\u9633\u575a\u767d\u5f97 \u4e86\u5218\u3001\u4e01\u4e8c\u4eba\u4e4b\u52a9\u3002\u5df2\u662f\u7a33\u5360\u4e0a\u98ce\u4e86\u3002 \u3000\u3000\u6768\u5a49\u95ef\u8fdb\u4e86\u201c\u805a\u4e49\u5385\u201d\u3001\u7aa6\u5b89\u5e73\u7684\u624b\u4e0b\u53ea\u9053\u5979\u662f\u65e0\u8db3\u8f7b\u91cd\u7684\u4eba\uff0c\u4e0d\u5927\u6ce8\u610f\u5979\u3002\u521a\u624d\u5728\u9636 \u4e0b\u548c\u5979\u4ea4\u8fc7\u624b\u7684\u4eba\u53eb\u9053\uff1a\u201c\u8fd9\u53ae\u662f\u5978\u7ec6\uff01\u201d\u65b9\u59cb\u6709\u4e09\u56db\u4e2a\u4eba\u4e0a\u524d\uff0c\u5c06\u5979\u680f\u622a\u3002 \u3000\u3000\u8fd9\u51e0\u4e2a\u4eba\u4e0d\u8fc7\u662f\u4e8c\u4e09\u6d41\u811a\u8272\uff0c\u7109\u80fd\u654c\u5f97\u4f4f\u6768\u5a49\u7cbe\u5999\u7684\u5251\u62db\uff0c\u4e0d\u6d88\u7247\u523b\uff0c\u6768\u5a49\u8eab\u968f\u5251\u8d70\uff0c \u53ea\u542c\u5f97\u201c\u54ce\u54df\uff0c\u54ce\u54df\u201d\u4e4b\u58f0\u6b64\u8d77\u5f7c\u843d\uff0c\u90a3\u51e0\u4e2a\u4eba\u90fd\u7ed9\u5979\u523a\u7740\u4e86\u7a74\u9053\uff0c\u5012\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u6768\u5a49\u867d\u662f\u5bf9\u5b5f\u660e\u971e\u5e76\u65e0\u597d\u611f\uff0c\u4f46\u89c1\u5979\u5f62\u52bf\u5371\u6025\uff0c\u81ea\u662f\u4e0d\u80fd\u8896\u624b\u89c2\uff0c\u5f53\u4e0b\u4fbf\u5411\u5979\u8fd9\u8fb9\u6740\u8fc7 \u6765\u3002\u5218\u4e07\u6a2a\u5200\u62db\u67b6\uff0c\u4e0d\u8fc7\u6570\u62db\uff0c\u7ed9\u5979\u4f24\u4e86\u53f3\u81c2\uff0c\u5927\u5200\u94db\u561f\u5760\u5730\u3002\u5b5f\u660e\u971e\u53eb\u9053\uff1a\u201c\u5a49\u59d0\uff0c\u4e0d\u5fc5 \u987e\u6211\uff0c\u64d2\u8d3c\u64d2\u738b\uff01\u201d \u3000\u3000\u6768\u5a49\u970d\u7136\u4e00\u7701\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u654c\u4f17\u6211\u5be1\uff0c\u4e45\u6218\u4e0b\u53bb\uff0c\u5b9a\u5fc5\u5403\u4e8f\u3002\u9664\u975e\u662f\u628a\u98de\u9f99\u5c71 \u7684\u5be8\u4e3b\u7aa6\u5b89\u5e73\u64d2\u4e86\uff0c\u65b9\u80fd\u8131\u9669\u3002\u201d \u3000\u3000\u6b64\u65f6\u805a\u4e49\u5385\u4e2d\u5206\u6210\u4e86\u4e09\u5904\u53ae\u6740\uff0c\u4e00\u5904\u662f\u8c37\u6db5\u865a\u4e0e\u891a\u4e91\u5cf0\u8054\u624b\uff0c\u548c\u767d\u4e07\u96c4\u3001\u9e7f\u5357\u6f33\u3001\u9093\u4e03 \u4e09\u4eba\u6076\u6597\uff0c\u53cc\u65b9\u90fd\u662f\u4e00\u7b49\u4e00\u7684\u9ad8\u624b\uff0c\u767d\u4e07\u96c4\u66f4\u662f\u9ed1\u9053\u4e0a\u9876\u5c16\u513f\u7684\u4eba\u7269\uff0c\u8c37\u3001\u891a\u4e8c\u4eba\u5e78\u6709\u201c\u96f7 \u7535\u4ea4\u8f70\u201d\u7684\u5929\u96f7\u529f\u7edd\u6280\uff0c\u6bcf\u5230\u5371\u6025\u4e4b\u65f6\uff0c\u4fbf\u5373\u4f7f\u7528\uff0c\u65b9\u59cb\u80fd\u591f\u652f\u6301\u3002\u4f46\u4ea6\u5df2\u662f\u751a\u5904\u4e0b\u98ce\u4e86\u3002 \u3000\u3000\u4e00\u5904\u662f\u9ed1\u77f3\u9053\u4eba\u548c\u7aa6\u5b89\u5e73\u7684\u6076\u6597\uff0c\u9ed1\u77f3\u9053\u4eba\u8d1f\u4f24\u529b\u6218\uff0c\u5f62\u52bf\u66f4\u4e3a\u6076\u52a3\u3002 \u3000\u3000\u6bd4\u8f83\u8d77\u6765\uff0c\u5012\u662f\u5b5f\u660e\u971e\u548c\u4e25\u70f7\u8fd9\u4e00\u5904\u5f62\u52bf\u7a0d\u4f73\u3002\u5979\u4eec\u5f97\u4e86\u6768\u5a49\u4e4b\u52a9\uff0c\u6740\u4f24\u4e86\u9633\u575a\u767d\u5f97\u529b \u7684\u52a9\u624b\u5218\u4e07\uff0c\u5f62\u52bf\u4e1a\u5df2\u8f6c\u5371\u4e3a\u5b89\u3002 \u3000\u3000\u98de\u9f99\u5c71\u7684\u5934\u76ee\u548c\u7aa6\u5b89\u5e73\u9080\u6765\u7684\u597d\u624b\uff0c\u9664\u4e86\u53d7\u4f24\u7684\u4e1a\u5df2\u9000\u4e0b\u5916\uff0c\u5728\u8fd9\u805a\u4e49\u5385\u4e2d\u7684\u5927\u7ea6\u8fd8\u6709 \u5341\u591a\u4e2a\u4eba\uff0c\u8fd9\u5341\u591a\u4e2a\u4eba\u6709\u4e00\u534a\u7ad9\u5728\u7aa6\u5b89\u5e73\u7684\u9644\u8fd1\uff0c\u6ce8\u89c6\u7740\u9ed1\u77f3\u9053\u4eba\uff0c\u56e0\u4e3a\u7aa6\u5b89\u5e73\u662f\u4e00\u5be8\u4e4b\u4e3b \u7684\u8eab\u4efd\uff0c\u5b9e\u9645\u4ed6\u867d\u7136\u5927\u5360\u4e0a\u98ce\uff0c\u4ed6\u7684\u624b\u4e0b\u4e5f\u4e0d\u80fd\u4e0d\u9884\u9632\u4e07\u4e00\u3002\u8fd8\u6709\u4e09\u4e2a\u5219\u66ff\u4ee3\u4e86\u53d7\u4f24\u7684\u5218 \u4e07\uff0c\u4e0a\u524d\u5e2e\u9633\u575a\u767d\u56f4\u653b\u5b5f\u3001\u4e25\u4e8c\u5973\u3002\u5269\u4e0b\u6765\u7684\u8981\u5835\u622a\u6768\u5a49\u7684\u4e0d\u8fc7\u56db\u4e94\u4e2a\u4eba\u3002 \u3000\u3000\u6768\u5a49\u4e0d\u77e5\u9ed1\u77f3\u9053\u4eba\u662f\u8c01\uff0c\u4f46\u5df2\u77e5\u9053\u7aa6\u5b89\u5e73\u662f\u98de\u9f99\u5c71\u7684\u5be8\u4e3b\uff0c\u5f53\u4e0b\u4fbf\u5373\u6325\u5251\u5982\u98ce\uff0c\u5411\u4ed6\u8fd9 \u8fb9\u6740\u53bb\u3002 \u3000\u3000\u4ee5\u6768\u5a49\u7684\u672c\u9886\u800c\u8bba\uff0c\u5bf9\u4ed8\u8fd9\u56db\u4e94\u4e2a\u4eba\u5e76\u4e0d\u56f0\u96be\u3001\u4f46\u8981\u51fb\u8d25\u4ed6\u4eec\uff0c\u5374\u4e5f\u6b8a\u975e\u6613\u4e8b\u3002\u597d\u5728\u5979 \u64c5\u957f\u8f7b\u529f\uff0c\u6709\u4e00\u5957\u540d\u53eb\u201c\u7a7f\u82b1\u7ed5\u6811\u201d\u7684\u529f\u6cd5\uff0c\u5de6\u9762\u4e00\u515c\uff0c\u53f3\u9762\u4e00\u7ed5\uff0c\u66f4\u914d\u5408\u4e86\u5979\u7cbe\u5999\u7684\u5251\u6cd5 \u51b2\u523a\uff0c\u8fb9\u6218\u8fb9\u8d70\uff0c\u7ec8\u4e8e\u7ed9\u5979\u51b2\u51fa\u4e86\u91cd\u56f4\u3002\u7aa6\u5b89\u5e73\u7684\u624b\u4e0b\u7acb\u5373\u5206\u51fa\u51e0\u4eba\u6765\u5835\u622a\u3002 \u3000\u3000\u773c\u770b\u6768\u5a49\u5c31\u53ef\u4ee5\u6740\u5230\u7aa6\u5b89\u5e73\u7684\u8eab\u8fb9\uff0c\u7aa6\u5b89\u5e73\u5ffd\u5730\u4e00\u58f0\u5927\u559d\uff0c\u53cc\u94a9\u9f50\u4e0b\uff0c\u5de6\u624b\u94a9\u52fe\u7740\u4e86\u9ed1 \u77f3\u9053\u4eba\u7684\u80a9\u5934\uff0c\u53f3\u624b\u94a9\u5c31\u8981\u5411\u4ed6\u54bd\u5589\u94a9\u4e0b\u3002 \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u5389\u58f0\u53eb\u9053\uff1a\u201c\u4eca\u65e5\u4e0d\u662f\u4f60\u6b7b\uff0c\u4fbf\u662f\u6211\u4ea1\uff01\u201d\u5200\u5149\u95ea\u5904\uff0c\u53ea\u542c\u5f97\u201c\u5494\u5693\u201d\u4e00\u58f0\uff0c \u628a\u7aa6\u5b89\u5e73\u5de6\u624b\u4e94\u6307\u9f50\u6839\u524a\u4e0b\u3002\u7aa6\u5b89\u5e73\u75db\u5f7b\u5fc3\u80ba\uff0c\u53d1\u51fa\u4e00\u58f0\u60e8\u53eb\uff0c\u817e\u5730\u98de\u8d77\u4e00\u811a\uff0c\u628a\u9ed1\u77f3\u9053\u4eba \u8e22\u4e86\u4e00\u4e2a\u7b4b\u6597\u3002 \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u7684\u80a9\u5934\u4e0a\u63d2\u4e86\u4e00\u67c4\u5229\u94a9\uff0c\u5012\u5728\u5730\u4e0a\uff0c\u8840\u6d41\u5982\u6ce8\uff0c\u5c1a\u80fd\u6323\u624e\u8d77\u6765\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6 \u5feb\uff0c\u5df2\u6709\u56db\u4e94\u4e2a\u4eba\u4e00\u9f50\u5411\u4ed6\u6251\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u6768\u5a49\u867d\u7136\u5728\u6c5f\u6e56\u4e0a\u5386\u7ec3\u4e86\u51e0\u5e74\uff0c\u4e0d\u50cf\u521d\u51fa\u9053\u65f6\u770b\u89c1\u6d41\u8840\u5c31\u5bb3\u6015\u4e86\uff0c\u4f46\u4e5f\u672a\u66fe\u89c1\u8fc7\u8fd9\u7b49\u60e8 \u70c8\u7684\u6076\u6597\uff0c\u8fd9\u5239\u90a3\u95f4\uff0c\u5979\u7ed9\u5413\u5f97\u5fc3\u4e2d\u614c\u4e71\uff0c\u4e0d\u77e5\u5e94\u4e0d\u5e94\u8be5\u518d\u53bb\u5bf9\u4ed8\u8fd9\u4e2a\u53d7\u4f24\u4e86\u7684\u7aa6\u5b89\u5e73\u3002 \u3000\u3000\u4f46\u6b63\u5728\u5979\u8e87\u8e87\u672a\u51b3\u4e4b\u9645\uff0c\u7aa6\u5b89\u5e73\u7684\u90a3\u73ed\u624b\u4e0b\u5374\u5df2\u8dd1\u8fc7\u53bb\u8981\u751f\u64d2\u53d7\u4f24\u4e86\u7684\u9ed1\u77f3\u9053\u4eba\uff0c\u800c\u4e14 \u4e5f\u6765\u653b\u51fb\u5979\u4e86\u3002\u6b63\u662f\uff1a \u3000\u3000\u8840\u96e8\u8165\u98ce\u5927\u53ae\u6740\uff0c\u5200\u5149\u5251\u5f71\u95ef\u91cd\u56f4 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b\u2014\u2014\u7b2c\u56db\u5341\u4e00\u56de\u3000\u826f\u8a00\u6709\u6548\u533b\u5fc3\u75c5\u3000\u6697\u7bad\u65e0\u529f\u7834\u8d3c\u5de2 \u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b \u7b2c\u56db\u5341\u4e00\u56de\u3000\u826f\u8a00\u6709\u6548\u533b\u5fc3\u75c5\u3000\u6697\u7bad\u65e0\u529f\u7834\u8d3c\u5de2 \u3000\u3000\u8fd9\u5239\u90a3\u95f4\u6768\u5a49\u7684\u60ca\u614c\u767b\u65f6\u5316\u4f5c\u4e86\u4e00\u56e2\u6012\u706b\uff0c\u53bb\u6551\u9ed1\u77f3\u9053\u4eba\u5df2\u6765\u4e0d\u53ca\uff0c\u6768\u5a49\u8eab\u5f62\u4e00\u8d77\uff0c\u7fe9 \u5982\u98de\u9e1f\u7684\u4ece\u524d\u9762\u90a3\u4eba\u7684\u5934\u9876\u63a0\u8fc7\uff0c\u7acb\u5373\u4fbf\u5411\u8ddd\u79bb\u8f83\u8fd1\u7684\u7aa6\u5b89\u5e73\u6251\u53bb\u3002 \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u5728\u5730\u4e0a\u6253\u4e86\u4e24\u4e2a\u5927\u7ffb\uff0c\u559d\u9053\uff1a\u201c\u53cd\u6b63\u6211\u53ea\u6709\u4e00\u6761\u6027\u547d\uff0c\u6740\u4e00\u4e2a\u591f\u672c\uff0c\u6740\u4e24\u4e2a\u6709 \u8d5a\uff01\u201d\u559d\u58f0\u4e2d\u5200\u5149\u75be\u95ea\uff0c\u53cc\u8db3\u5c1a\u672a\u80fd\u76f4\u7acb\uff0c\u624b\u4e2d\u7684\u5feb\u5200\u5df2\u662f\u5411\u56db\u65b9\u4e71\u5288\uff01\u53ea\u542c\u5f97\u201c\u5494\u5693\u201d\u8fde \u58f0\uff0c\u8f6c\u773c\u95f4\u5df2\u662f\u6709\u4e09\u6761\u624b\u81c2\u7ed9\u4ed6\u7684\u5feb\u5200\u780d\u65ad\uff01 \u3000\u3000\u53ef\u662f\u56e0\u4e3a\u9ed1\u77f3\u9053\u4eba\u53d7\u4f24\u592a\u91cd\uff0c\u4ed6\u662f\u4ee5\u8098\u652f\u5730\uff0c\u817e\u8eab\u8dc3\u8d77\u7684\u4e00\u8f6e\u5feb\u5200\u5288\u8fc7\uff0c\u6c14\u529b\u4ea6\u5df2\u7528 \u5c3d\uff0c\u201c\u535c\u901a\u201d\u7684\u53c8\u5012\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u5411\u4ed6\u653b\u51fb\u7684\u4e94\u4e2a\u4eba\u4e09\u4eba\u65ad\u81c2\uff0c\u53e6\u5916\u4e24\u4e2a\u4eba\u5413\u5f97\u9b42\u98de\u9b44\u6563\uff0c\u54ea\u91cc\u8fd8\u6562\u518d\u53bb\u60f9\u4ed6\uff1f\u53ea\u6068\u7239\u5a18 \u751f\u5c11\u4e86\u4e24\u6761\u817f\uff0c\u8fde\u5fd9\u8fdc\u8fdc\u5730\u8eb2\u5f00\uff01 \u3000\u3000\u5c31\u5728\u9ed1\u77f3\u9053\u4eba\u4ee5\u5feb\u5200\u62fc\u547d\u4e4b\u65f6\uff0c\u6768\u5a49\u4ea6\u5df2\u5728\u534a\u7a7a\u4e2d\u4e00\u4e2a\u201c\u9e5e\u5b50\u7ffb\u5b50\u201d\uff0c\u5530\u7684\u4e00\u5251\uff0c\u4fbf\u5411 \u7aa6\u5b89\u5e73\u523a\u4e0b\u3002 \u3000\u3000\u7aa6\u5b89\u5e73\u4e5f\u59d4\u5b9e\u51f6\u987d\u65e0\u6bd4\uff0c\u53f3\u624b\u4e94\u6307\u5df2\u65ad\uff0c\u53ea\u5269\u4e0b\u4e00\u67c4\u5355\u94a9\uff0c\u770b\u89c1\u6768\u5a49\u957f\u5251\u523a\u4e0b\uff0c\u5c45\u7136\u8fd8 \u6562\u8fce\u654c\uff0c\u4e00\u62db\u201c\u4e3e\u706b\u64a9\u5929\u201d\uff0c\u5de6\u624b\u7684\u62a4\u624b\u94a9\u7adf\u7136\u628a\u6768\u5a49\u7684\u957f\u5251\u9501\u4f4f\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u540e\u9762\u7684\u51e0\u4e2a\u4eba\u5df2\u8d76\u5230\uff0c\u6708\u7259\u5200\u3001\u9f50\u7709\u68cd\u3001\u9752\u94dc\u94dc\uff0c\u5c0f\u82b1\u67aa\uff0c\u56db\u822c\u5175 \u5668\uff0c\u4e00\u9f50\u5411\u6768\u5a49\u7684\u8eab\u4e0a\u6240\u523a\uff01 \u3000\u3000\u5c31\u5728\u6b64\u65f6\uff0c\u5ffd\u542c\u5f97\u6709\u4eba\u53eb\u9053\uff1a\u201c\u5b5f\u5927\u4fa0\u3001\u674e\u76df\u4e3b\u9a7e\u5230\uff01\u201d \u3000\u3000\u8fd9\u4e00\u58f0\u53eb\u558a\u767b\u65f6\u628a\u805a\u4e49\u5385\u4e2d\u7684\u7fa4\u76d7\u5413\u4f4f\u4e86\uff0c\u4ed6\u4eec\u672c\u6765\u662f\u51c6\u594b\u5b5f\u5c11\u521a\u548c\u674e\u601d\u5357\u660e\u5929\u5230\u7684\uff0c \u60f3\u4e0d\u5230\u4ed6\u4eec\u5374\u63d0\u65e9\u4e86\u4e00\u65e5\uff0c\u5728\u8fd9\u7d27\u8981\u7684\u5173\u5934\uff0c\u7a81\u5982\u5176\u6765\uff01 \u3000\u3000\u539f\u6765\u7aa6\u5b89\u5e73\u6d3e\u9063\u5f80\u7405\u739b\u5c71\u7684\u90a3\u4e2a\u4f7f\u8005\u966a\u4f34\u5b5f\u3001\u674e\u4e8c\u4eba\u56de\u5c71\uff0c\u6309\u7167\u539f\u5b9a\u7684\u8ba1\u5212\uff0c\u672c\u6765\u662f\u5e94 \u8be5\u660e\u5929\u5230\u7684\u3002\u4f46\u5b5f\u5c11\u521a\u50ac\u4ed6\u65e9\u884c\u591c\u5bbf\uff0c\u4e0d\u8bb8\u4ed6\u5728\u8def\u4e0a\u62d6\u5ef6\uff0c\u6545\u800c\u63d0\u65e9\u4e86\u4e00\u5929\u5230\u4e86\u3002 \u3000\u3000\u5230\u4e86\u98de\u9f99\u5c71\u4e4b\u65f6\uff0c\u6b63\u662f\u805a\u4e49\u5385\u4e2d\u5f00\u59cb\u6076\u6597\u7684\u65f6\u5019\uff0c\u5728\u5be8\u53e3\u8fce\u63a5\u4ed6\u4eec\u7684\u662f\u4e00\u4e2a\u5730\u4f4d\u8f83\u4f4e\uff0c \u81ea\u5df1\u4f5c\u4e0d\u5f97\u4e3b\u7684\u5c0f\u5934\u76ee\u3002 \u3000\u3000\u8fd9\u5c0f\u5934\u76ee\u4e5f\u77e5\u4e0d\u5999\uff0c\u671f\u671f\u827e\u827e\u5730\u8bf4\u9053\uff1a\u201c\u91cc\u3001\u91cc\u9762\u6709\u4e8b\uff0c\u5b5f\u5927\u4fa0\u548c\u674e\u76df\u4e3b\u662f\u5426\u7a0d\u5f85\u7247 \u65f6\u3002\u5bb9\u3001\u5bb9\u5c0f\u7684\u8fdb\u53bb\u7980\u62a5\uff1f\u201d \u3000\u3000\u5b5f\u5c11\u521a\u4fa7\u8033\u7ec6\u542c\uff0c\u9690\u9690\u542c\u5f97\u4f3c\u6709\u53ae\u6740\u4e4b\u58f0\uff0c\u6545\u610f\u95ee\u9053\uff1a\u201c\u662f\u9791\u5b50\u6740\u6765\u4e86\u4e48\uff1f\u201d\u90a3\u5c0f\u5934\u76ee \u9053\uff1a\u201c\u662f\u3001\u662f\u2026\u2026\u4e0d\uff0c\u4e0d\u662f\u3002\u201d\u5b5f\u5c11\u521a\u9053\uff1a\u201c\u90a3\u53c8\u662f\u4ec0\u4e48\u4eba\uff1f\u201d\u5c0f\u5934\u76ee\u9053\uff1a\u201c\u4e0d\uff0c\u4e0d\u77e5 \u9053\u3002\u201d\u795e\u8272\u614c\u5f20\uff0c\u8bed\u65e0\u4f26\u6b21\u3002 \u3000\u3000\u5b5f\u5c11\u521a\u672c\u6765\u8fd8\u4e0d\u6562\u65ad\u5b9a\u5be8\u4e2d\u662f\u7ec3\u6b66\u7684\u5406\u559d\u8fd8\u662f\u771f\u6b63\u7684\u53ae\u6740\u7684\u58f0\u97f3\u7684\uff0c\u6b64\u65f6\u89c1\u8fd9\u5c0f\u5934\u76ee\u5f20 \u60f6\u5931\u63aa\uff0c\u5df2\u7ecf\u53ef\u4ee5\u65ad\u8d70\u662f\u6709\u81ea\u5df1\u8fd9\u8fb9\u7684\u4eba\u6b63\u5728\u5be8\u4e2d\u88ab\u56f0\u4e86\u3002\u4e8e\u662f\u5f53\u673a\u7acb\u65ad\uff0c\u51fa\u6307\u5982\u7535\uff0c\u500f\u7684 \u4fbf\u70b9\u4e86\u90a3\u5c0f\u5934\u76ee\u7684\u7a74\u9053\u3002 \u3000\u3000\u90a3\u4e2a\u4f7f\u8005\u5927\u5403\u4e00\u60ca\uff0c\u5931\u58f0\u53eb\u9053\uff1a\u201c\u5b5f\u5927\u4fa0\uff0c\u4f60\u3001\u8fd9\u8fd9\u662f\u5e72\u561b\uff1f\u201d\u8bdd\u672a\u72b9\u4e86\uff0c\u5df2\u662f\u7ed9\u674e\u601d \u5357\u6263\u7740\u864e\u53e3\u3002 \u3000\u3000\u674e\u601d\u5357\u6c89\u58f0\u559d\u9053\uff1a\u201c\u7aa6\u5b89\u5e73\u8bbe\u4e0b\u9677\u9631\u6765\u8bf1\u6211\uff0c\u4f60\u5f53\u6211\u4e0d\u77e5\u4e48\uff1f\u8001\u5b9e\u544a\u8bc9\u4f60\u5427\uff0c\u6211\u662f\u660e\u77e5 \u5c71\u6709\u864e\uff0c\u504f\u5411\u864e\u5c71\u884c\u3002\u73b0\u5728\u5df2\u7136\u6765\u5230\uff0c\u6211\u662f\u975e\u8fdb\u53bb\u4e0d\u53ef\u7684\u4e86\uff01\u4f60\u4e3a\u864e\u4f5c\u6005\uff0c\u672c\u5e94\u53d6\u4f60\u6027\u547d\uff0c \u4f46\u6211\u7f3a\u5c11\u4e00\u4e2a\u5e26\u8def\u7684\u4eba\uff0c\u4f60\u4e56\u4e56\u5730\u5e26\u6211\u8fdb\u53bb\uff0c\u6211\u5c31\u9976\u4f60\u3002\u201d \u3000\u3000\u90a3\u4f7f\u8005\u7684\u6027\u547d\u634f\u5728\u674e\u601d\u5357\u7684\u624b\u91cc\uff0c\u81ea\u662f\u4e0d\u6562\u4e0d\u4f9d\u3002\u4ed6\u5949\u547d\u524d\u5f80\u7405\u739b\u5c71\u8bf1\u9a97\u674e\u601d\u5357\u4e4b\u4e8b\uff0c \u53ea\u6709\u7aa6\u5b89\u5e73\u548c\u51e0\u4e2a\u5927\u5934\u76ee\u77e5\u9053\uff0c\u5c71\u5be8\u91cc\u7684\u55bd\u5175\u4e5f\u53ea\u662f\u8ba4\u8bc6\u4ed6\u800c\u4e0d\u8ba4\u8bc6\u5b5f\u3001\u674e\u4e8c\u4eba\u3002\u89c1\u4ed6\u5e26\u4e86 \u4e24\u4e2a\u964c\u751f\u4eba\u8fdb\u6765\uff0c\u867d\u7136\u89c9\u5f97\u8be7\u5f02\uff0c\u4e5f\u662f\u4e0d\u6562\u591a\u95ee\u3002\u201c\u805a\u4e49\u5385\u201d\u4e2d\u6b63\u5728\u6df7\u6218\uff0c\u4e00\u822c\u55bd\u5175\u8fd8\u53ea\u9053 \u4ed6\u662f\u8bf7\u4e86\u9ad8\u624b\u53ca\u65f6\u8d76\u6765\u52a9\u6218\u7684\u3002 \u3000\u3000\u76f4\u81f3\u5230\u4e86\u201c\u805a\u4e49\u5385\u201d\u524d\uff0c\u65b9\u59cb\u6709\u8ba4\u5f97\u5b5f\u3001\u674e\u4e8c\u4eba\uff0c\u5927\u58f0\u53eb\u4e86\u51fa\u6765\u3002 \u3000\u3000\u56f4\u653b\u6768\u5a49\u90a3\u4e2a\u4eba\u542c\u8bf4\u6c5f\u5357\u5927\u4fa0\u5b5f\u5c11\u521a\u6765\u5230\uff0c\u90fd\u662f\u4e0d\u7531\u5f97\u5927\u5403\u4e00\u60ca\uff0c\u6768\u5a49\u4e00\u62db\u201c\u591c\u53c9\u63a2 \u6d77\u201d\uff0c\u9752\u94a2\u5251\u5f80\u524d\u4e00\u9001\uff0c\u6446\u8131\u4e86\u7aa6\u5b89\u5e73\u7684\u62a4\u624b\u94a9\uff0c\u7acb\u5373\u4fbf\u662f\u4e00\u62db\u201c\u626b\u8361\u516b\u65b9\u201d\uff0c\u628a\u90a3\u56db\u4e2a\u4eba \u7684\u5175\u5668\u90fd\u8361\u5f00\u4e86\u3002 \u3000\u3000\u5b5f\u5c11\u521a\u5927\u559d\u9053\uff1a\u201c\u4f4f\u624b\uff01\u201d\u8fd9\u4e00\u58f0\u5927\u559d\u9707\u5f97\u4f17\u4eba\u8033\u9f13\u55e1\u55e1\u4f5c\u54cd\uff0c\u8fde\u767d\u4e07\u96c4\u4e5f\u662f\u4e0d\u7981\u5fc3\u5934 \u4e00\u51db\uff0c\u4e0d\u6562\u4e0d\u4ece\u3002 \u3000\u3000\u53ef\u662f\u5c31\u5728\u4f17\u4eba\u6309\u4f4f\u5175\u5668\u76f8\u7ee7\u7f62\u6597\u4e4b\u65f6\uff0c\u5374\u6709\u4e00\u4e2a\u4eba\u7a81\u7136\u5411\u6768\u5a49\u6251\u53bb\uff0c\u8fd9\u662f\u9633\u575a\u767d\u3002 \u3000\u3000\u9633\u575a\u767d\u7684\u8eab\u4efd\u4e0d\u6bd4\u4ed6\u4eba\uff0c\u4ed6\u77e5\u9053\u5c71\u5be8\u4e2d\u7684\u5934\u76ee\u6216\u8bb8\u53ef\u9080\u5e78\u529f\uff0c\u674e\u601d\u5357\u548c\u891a\u4e91\u5cf0\u7b49\u4eba\u5374\u662f \u51b3\u8ba1\u4e0d\u4f1a\u653e\u8fc7\u4ed6\u7684\uff0c\u4ed6\u4e0d\u7518\u675f\u624b\u5c31\u64d2\uff0c\u662f\u4ee5\u60f3\u628a\u6768\u5a49\u64d2\u4e3a\u4eba\u8d28\u3002\u4ed6\u548c\u6768\u5a49\u4ea4\u8fc7\u624b\uff0c\u53c8\u77e5\u9053\u6768 \u5a49\u662f\u674e\u601d\u5357\u7684\u672a\u5a5a\u59bb\u5b50\uff0c\u81ea\u5fd6\u53ef\u4ee5\u6709\u51e0\u5206\u628a\u63e1\u5728\u653b\u5176\u4e0d\u5907\u7684\u60c5\u5f62\u4e4b\u4e0b\u628a\u6768\u5a49\u624b\u5230\u64d2\u6765\uff0c\u53ea\u8981 \u62ff\u7740\u4e86\u6768\u5a49\uff0c\u5c31\u4e0d\u6015\u674e\u601d\u5357\u96be\u4e3a\u4ed6\u4e86\u3002 \u3000\u3000\u6768\u5a49\u770b\u89c1\u674e\u601d\u5357\u6765\u5230\uff0c\u5fc3\u91cc\u53c8\u60ca\u53c8\u559c\uff0c\u679c\u7136\u6ca1\u6709\u63d0\u9632\uff0c\u5f85\u542c\u5f97\u80cc\u540e\u91d1\u5203\u5288\u98ce\u4e4b\u58f0\uff0c\u56de\u8fc7 \u8eab\u65f6\uff0c\u5df2\u662f\u6765\u4e0d\u53ca\u4e86\u3002\u9633\u575a\u767d\u5e73\u5251\u4e00\u62cd\uff0c\u538b\u7740\u4e86\u6768\u5a49\u624b\u4e2d\u7684\u9752\u94a2\u5251\uff0c\u8fc5\u5373\u5c31\u4f7f\u51fa\u8fd1\u8eab\u7f20\u6597\u7684 \u5c0f\u64d2\u62ff\u624b\u6cd5\uff0c\u626d\u7740\u6768\u5a49\u7684\u624b\u8155\u3002 \u3000\u3000\u4e0d\u6599\u87b3\u8782\u6355\u8749\uff0c\u5c1a\u6709\u9ec4\u96c0\u5728\u540e\u3002\u674e\u601d\u5357\u542c\u5f97\u5b5f\u660e\u971e\u7684\u53eb\u58f0\uff0c\u7acb\u5373\u98de\u8eab\u63a0\u53bb\uff0c\u8d76\u5728\u5b5f\u660e\u971e \u7684\u524d\u9762\uff0c\u5230\u4e86\u6768\u5a49\u8eab\u8fb9\u3002\u6b64\u65f6\u9633\u575a\u767d\u521a\u521a\u626d\u7740\u6768\u5a49\u7684\u624b\u81c2\uff0c\u674e\u601d\u5357\u5e76\u6307\u4fbf\u70b9\u4ed6\u7684\u80a9\u4e95\u7a74\u3002 \u3000\u3000\u9976\u662f\u9633\u575a\u767d\u672c\u9886\u9ad8\u5f3a\uff0c\u8fd9\u4e00\u6307\u4e5f\u662f\u8eb2\u95ea\u4e0d\u5f00\uff0c\u201c\u80a9\u4e95\u7a74\u201d\u7ed9\u674e\u601d\u5357\u7684\u6307\u5c16\u6233\u4e2a\u6b63\u7740\uff0c\u6c14 \u529b\u767b\u65f6\u4f7f\u4e0d\u51fa\u6765\uff0c\u6768\u5a49\u6323\u8131\u4e86\u4ed6\u7684\u638c\u63e1\uff0c\u5530\u7684\u4e00\u5251\u4fbf\u523a\u8fc7\u53bb\uff0c\u9633\u575a\u767d\u4e0d\u6562\u62db\u67b6\uff0c\u8f6c\u8eab\u5c31\u8dd1\u3002 \u3000\u3000\u674e\u601d\u5357\u559d\u9053\uff1a\u201c\u5f80\u54ea\u91cc\u8dd1\uff1f\u201d\u6b63\u8981\u53bb\u8ffd\uff0c\u6768\u5a49\u6070\u597d\u5728\u6b64\u65f6\u8f6c\u8fc7\u8eab\u6765\uff0c\u674e\u601d\u5357\u7684\u76ee\u5149\u548c\u5979 \u76f8\u89e6\uff0c\u767b\u65f6\u5446\u4e86\u3002 \u3000\u3000\u5b5f\u660e\u971e\u53eb\u9053\uff1a\u201c\u7239\uff0c\u8fd9\u5c0f\u8d3c\u662f\u9633\u5929\u96f7\u7684\u4f84\u5b50\uff01\u201d\u5b5f\u5c11\u521a\u9053\uff1a\u201c\u6211\u77e5\u9053\uff01\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u4e00 \u4e2a\u7bad\u6b65\u63a0\u8eab\u800c\u524d\uff0c\u5df2\u662f\u5835\u4f4f\u4e86\u9633\u575a\u767d\u7684\u53bb\u8def\u3002 \u3000\u3000\u9633\u575a\u767d\u5530\u7684\u4e00\u5251\u523a\u53bb\uff0c\u5b5f\u5c11\u521a\u51b7\u7b11\u9053\uff1a\u201c\u5c45\u7136\u8fd8\u6562\u548c\u6211\u52a8\u624b\uff01\u201d\u4f7f\u51fa\u201c\u5f39\u6307\u795e\u901a\u201d\u7684\u529f \u592b\uff0c\u201c\u950b\u201d\u7684\u4e00\u58f0\uff0c\u521a\u597d\u5f39\u7740\u65e0\u950b\u7684\u5251\u810a\u3002\u9633\u575a\u767d\u864e\u53e3\u4e00\u9707\uff0c\u957f\u5251\u8131\u624b\uff0c\u98de\u4e0a\u534a\u7a7a\u3002 \u3000\u3000\u5b5f\u5c11\u521a\u54fc\u4e86\u4e00\u58f0\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u8c05\u4f60\u4e5f\u9003\u4e0d\u51fa\u6211\u7684\u638c\u5fc3\uff0c\u4e56\u4e56\u7684\u675f\u624b\u5c31\u64d2\u5427\uff01\u201d\u5de6\u638c\u4e00 \u5708\uff0c\u53f3\u638c\u4e00\u63c9\uff0c\u4f7f\u51fa\u4e86\u201c\u9f99\u722a\u624b\u201d\u7684\u5927\u64d2\u62ff\u62db\u5f0f\uff0c\u6b63\u8981\u6293\u4ed6\uff0c\u5ffd\u89c9\u8111\u540e\u98ce\u751f\uff0c\u6709\u4e24\u4e2a\u4eba\u540c\u65f6 \u88ad\u5230\u3002 \u3000\u3000\u5b5f\u5c11\u521a\u773c\u89c2\u56db\u9762\uff0c\u8033\u542c\u516b\u65b9\uff0c\u7109\u80fd\u53d7\u4eba\u6697\u7b97\uff1f\u53ef\u662f\u8fd9\u4e24\u4eba\u51fa\u624b\u72e0\u8fa3\u4e4b\u6781\uff0c\u6b63\u662f\u653b\u654c\u4e4b\u6240 \u5fc5\u6551\uff0c\u5b5f\u5c11\u521a\u7684\u5185\u529f\u867d\u7136\u5dee\u4e0d\u591a\u5230\u4e86\u7089\u706b\u7eaf\u9752\u4e4b\u5883\uff0c\u4e5f\u662f\u4e0d\u6562\u8ba9\u4ed6\u4eec\u6253\u4e2d\uff0c\u53ea\u597d\u653e\u677e\u4e86\u9633\u575a \u767d\uff0c\u5148\u884c\u5bf9\u4ed8\u8fd9\u4e24\u4e2a\u4eba\u3002 \u3000\u3000\u4e24\u65b9\u9762\u52a8\u4f5c\u90fd\u662f\u5feb\u5230\u6781\u70b9\uff0c\u5b5f\u5c11\u521a\u53cd\u624b\u4e00\u62ff\uff0c\u53f3\u8fb9\u7684\u90a3\u4e2a\u4eba\u53cc\u638c\u4e00\u5408\uff0c\u201c\u62cd\u201d\u7684\u4e00\u58f0\u5c31 \u5939\u7740\u4e86\u4ed6\u7684\u624b\u8155\u3002\u5de6\u8fb9\u7684\u90a3\u4e2a\u4eba\u4e00\u62f3\u6363\u51fa\uff0c\u51b2\u51fb\u5b5f\u5c11\u521a\u7684\u9762\u95e8\u3002 \u3000\u3000\u5b5f\u5c11\u521a\u8fd1\u5341\u5e74\u6765\u8eab\u7ecf\u767e\u6218\uff0c\u4ece\u6765\u6ca1\u6709\u5403\u8fc7\u8fd9\u6837\u7684\u4e8f\uff0c\u5fc3\u5934\u4e00\u51db\uff0c\u60f3\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u8fd9\u91cc\u5c45 \u7136\u8fd8\u6709\u4e24\u4e2a\u4e00\u6d41\u597d\u624b\uff01\u201d\u5f53\u4e0b\u4f7f\u51fa\u7edd\u9876\u795e\u529f\uff0c\u970d\u5730\u8eab\u5f62\u4e00\u77ee\uff0c\u628a\u90a3\u4e2a\u62ff\u7740\u4ed6\u624b\u8155\u7684\u4eba\u50cf\u76ae\u7403 \u822c\u5730\u629b\u4e86\u8d77\u6765\uff0c\u548c\u4e00\u62f3\u6253\u6765\u7684\u90a3\u4e2a\u4eba\u649e\u4e2a\u6b63\u7740\uff0c\u90a3\u4e2a\u4eba\u4e5f\u7ed9\u649e\u8dcc\u4e86\uff0c\u5b5f\u5c11\u521a\u51fa\u624b\u5982\u7535\uff0c\u4e00\u624b \u4e00\u4e2a\u767b\u65f6\u6293\u7740\u4e86\u8fd9\u4e24\u4e2a\u4eba\u7684\u7435\u7436\u9aa8\u3002\u53ef\u662f\u9633\u575a\u767d\u5374\u5df2\u6e9c\u8d70\u4e86\u3002 \u3000\u3000\u5b5f\u5c11\u521a\u6293\u8d77\u4e86\u8fd9\u4e24\u4e2a\u4eba\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u662f\u4ec0\u4e48\u4eba\uff0c\u770b\u4f60\u4eec\u8eab\u624b\u4e0d\u51e1\uff0c\u4f55\u4ee5\u7518\u5fc3\u4f5c\u91d1\u864f\u7684 \u722a\u7259\uff1f\u201d \u3000\u3000\u4e14\u8bf4\u674e\u601d\u5357\u78b0\u7740\u4e86\u6768\u5a49\u7684\u76ee\u5149\uff0c\u767b\u65f6\u5446\u4e86\u3002\u6768\u5a49\u4e5f\u662f\u4e00\u7247\u832b\u7136\uff0c\u4e0d\u77e5\u8bf4\u4e9b\u4ec0\u4e48\u624d\u597d\u3002\u8fc7 \u4e86\u534a\u664c\uff0c\u674e\u601d\u5357\u624d\u597d\u50cf\u4ece\u68a6\u4e2d\u63a0\u9192\u8fc7\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u5a49\u59b9\uff0c\u5f53\u771f\u662f\u4f60\u4e48\uff1f\u201d \u3000\u3000\u6768\u5a49\u4f4e\u4e0b\u4e86\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u5c45\u7136\u8fd8\u8ba4\u5f97\u6211\uff1f\u201d\u5979\u662f\u5973\u626e\u7537\u88c5\uff01\u800c\u4e14\u662f\u6539\u5bb9\u6613\u8c8c\u4e86\u7684\uff0c\u89c1 \u674e\u601d\u5357\u8ba4\u5f97\u662f\u5979\uff0c\u5fc3\u5934\u4e0d\u7531\u5f97\u751c\u4e1d\u4e1d\u7684\u751a\u662f\u6b22\u559c\u3002 \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u54b1\u4eec\u662f\u63a5\u7740\u4e00\u6761\u7ef3\u5b50\u4e0a\u7684\u8682\u86b1\uff0c\u6211\u600e\u4f1a\u4e0d\u8ba4\u8bc6\u4f60\u5462\u3002\u5a49\u59b9\uff0c\u6211\u89c1\u4e86\u77f3\u749e\uff0c \u624d\u77e5\u9053\u4f60\u8fd8\u6d3b\u5728\u4eba\u95f4\uff0c\u6211\u627e\u5f97\u4f60\u597d\u82e6\u554a\uff01\u201d \u3000\u3000\u201c\u54b1\u4eec\u662f\u62f4\u5728\u4e00\u6761\u7ef3\u5b50\u4e0a\u7684\u8682\u86b1\u201d\u8fd9\u4e00\u53e5\u8bdd\uff0c\u6b63\u662f\u4ed6\u4eec\u5728\u8ba2\u5a5a\u4e4b\u540e\uff0c\u9003\u4ea1\u9014\u4e2d\uff0c\u674e\u601d\u5357 \u56e0\u4e3a\u6768\u5a49\u6000\u7591\u4ed6\u5bf9\u5979\u4e0d\u662f\u771f\u60c5\uff0c\u66fe\u7ecf\u548c\u6768\u5a49\u8bf4\u8fc7\u7684\u4e00\u53e5\u8bdd\u3002\u6b64\u9645\uff0c\u6768\u5a49\u91cd\u65b0\u4ece\u4ed6\u7684\u53e3\u4e2d\u542c\u5230 \u4e86\u8fd9\u4e00\u53e5\uff0c\u5bf9\u4ed6\u6ee1\u8154\u7684\u6028\u6068\u90fd\u597d\u50cf\u51b0\u96ea\u7ed9\u6625\u98ce\u878d\u89e3\u4e86\u3002 \u3000\u3000\u5b5f\u660e\u971e\u628a\u891a\u4e91\u5cf0\u62c9\u4e86\u8fc7\u6765\uff0c\u7b11\u563b\u563b\u4ed6\u8bf4\u9053\uff1a\u201c\u5357\u54e5\u3001\u5a49\u59d0\uff0c\u606d\u559c\u4f60\u4eec\u4eca\u65e5\u56e2\u5706\u4e86\u3002\u201d\u674e \u601d\u5357\u9762\u4e0a\u4e00\u7ea2\uff0c\u8fd9\u624d\u9192\u8d77\u4e86\u81ea\u5df1\u662f\u5728\u4f17\u76ee\u77aa\u77aa\u4e4b\u4e0b\uff0c\u4ee5\u81ea\u5df1\u4e49\u519b\u76df\u4e3b\u7684\u8eab\u4efd\uff0c\u600e\u53ef\u72ec\u81ea\u8eb2\u5728 \u4e00\u65c1\uff0c\u548c\u6768\u5a49\u5077\u8bf4\u60c5\u8bdd\uff1f \u3000\u3000\u891a\u4e91\u5cf0\u7b11\u9053\uff0c\u201c\u6211\u5374\u8981\u5411\u4e24\u4f4d\u9053\u6b49\u4e86\u3002\u674e\u76df\u4e3b\uff0c\u90a3\u5929\u6211\u6ca1\u6709\u53c2\u52a0\u5e86\u529f\u5bb4\u800c\u79c1\u56e0\u9003\u4e0b\u5c71\uff0c \u4f60\u4e00\u5b9a\u4f1a\u6709\u7591\u5fc3\u7684\u4e86\uff1f\u6768\u59d1\u5a18\uff0c\u90a3\u5929\u665a\u4e0a\uff0c\u6211\u8feb\u4e0d\u5f97\u5df2\u548c\u4f60\u52a8\u624b\uff0c\u4e5f\u8bf7\u4f60\u4e0d\u8981\u602a\u8d23\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u8ddf\u7740\u7b11\u9053\uff1a\u201c\u4e91\u5cf0\u4e3a\u4ec0\u4e48\u8981\u8fd9\u6837\u505a\uff0c\u73b0\u5728\u5927\u7ea6\u4e5f\u7528\u4e0d\u7740\u89e3\u91ca\u4e86\u5427\uff1f\u201d \u3000\u3000\u6768\u5a49\u770b\u89c1\u5b5f\u3001\u891a\u4e8c\u4eba\u5982\u6b64\u4eb2\u70ed\uff0c\u5fc3\u91cc\u53c8\u662f\u597d\u7b11\uff0c\u53c8\u662f\u6b22\u559c\uff0c\u60f3\u9053\uff1a\u201c\u539f\u6765\u4ed6\u4eec\u662f\u4e00\u5bf9\u60c5 \u4fa3\uff0c\u6211\u771f\u7cca\u6d82\uff0c\u8fd8\u4ee5\u4e3a\u5979\u662f\u548c\u5357\u54e5\u76f8\u604b\u5462\u3002\u201d\u5f53\u4e0b\u8fde\u5fd9\u8bf4\u9053\uff1a\u201c\u891a\u5927\u54e5\uff0c\u90a3\u5929\u665a\u4e0a\uff0c\u591a\u4e8f\u4f60 \u6697\u4e2d\u5e2e\u5fd9\uff0c\u6211\u624d\u5f97\u4ee5\u514d\u906d\u5c60\u9f99\u7684\u6bd2\u624b\uff0c\u6211\u8fd8\u6ca1\u6709\u591a\u8c22\u4f60\u5462\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u4f60\u7239\u7239\u643a\u4e86\u4e24\u4e2a\u4eba\uff0c\u54b1\u4eec\u8fc7\u53bb\u770b\u770b\u3002\u201d\u6b64\u65f6\u5b5f\u5c11\u521a\u6b63\u5728\u76d8\u95ee\u90a3\u4e24\u4e2a\u4eba\u7684\u6765 \u5386\uff0c\u90a3\u4e24\u4e2a\u4eba\u90fd\u662f\u88c5\u804b\u4f5c\u54d1\uff0c\u95ed\u53e3\u4e0d\u8a00\u3002 \u3000\u3000\u674e\u601d\u5357\u3001\u891a\u4e91\u5cf0\u8fd9\u4e24\u5bf9\u8d70\u8fc7\u53bb\u7684\u65f6\u5019\uff0c\u4e25\u70f7\u65e9\u5df2\u5728\u4ed6\u4eec\u4e4b\u524d\u548c\u59e8\u7236\u89c1\u4e86\u9762\uff0c\u5b5f\u5c11\u521a\u60f3\u4e0d \u5230\u5728\u8fd9\u91cc\u89c1\u7740\u4e86\u81ea\u5df1\u7684\u7525\u5973\uff0c\u5fc3\u91cc\u5341\u5206\u6b22\u559c\uff0c\u8fd8\u672a\u5f97\u6709\u7a7a\u6687\u95ee\u5979\uff0c\u5ffd\u89c1\u5973\u513f\u548c\u891a\u4e91\u5cf0\u53c8\u8d70\u4e86 \u8fc7\u6765\uff0c\u66f4\u662f\u559c\u51fa\u671b\u5916\uff0c\u7b11\u9053\uff1a\u201c\u4e91\u5cf0\uff0c\u6211\u542c\u5f97\u971e\u513f\u8bf4\u8d77\u4f60\u90a3\u65e5\u5728\u7405\u739b\u5c71\u4e0a\u4e4b\u4e8b\uff0c\u6211\u5c31\u731c\u5230\u662f \u4f60\u4e86\uff0c\u679c\u7136\u4e0d\u9519\uff0c\u4f60\u4eec\u4e24\u4eba\u600e\u4e48\u4f1a\u5728\u4e00\u8d77\u7684\uff1f\u201d\u5b5f\u660e\u971e\u7b11\u9053\uff1a\u201c\u8bf4\u6765\u8bdd\u957f\uff0c\u5f85\u4f1a\u513f\u6211\u518d\u6162\u6162 \u544a\u8bc9\u7239\u7239\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u5b9a\u773c\u5411\u90a3\u4e2a\u4eba\u4e00\u671b\uff0c\u5ffd\u5730\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u4e24\u4e2a\u9791\u5b50\u597d\u5927\u7684\u80c6\uff0c\u5c45\u7136\u6562\u5077\u5230\u4e2d\u539f\uff0c\u52fe \u7ed3\u6b66\u6797\u8d25\u7c7b\uff0c\u5174\u98ce\u4f5c\u6d6a\uff01\u201d\u5b5f\u5c11\u521a\u8be7\u9053\uff1a\u201c\u601d\u5357\uff0c\u4f60\u8ba4\u5f97\u4ed6\u4eec\uff1f\u201d\u674e\u601d\u5357\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u4eba\u662f \u8499\u53e4\u7684\u91d1\u5e10\u6b66\u58eb\uff0c\u540d\u5b57\u6211\u53ef\u8bb0\u4e0d\u5f97\u4e86\u3002\u4f46\u4ed6\u4eec\u8eab\u4e0a\u4e00\u5b9a\u85cf\u6709\u6210\u5409\u601d\u6c57\u751f\u524d\u6240\u8d50\u7684\u91d1\u724c\u7684\uff0c\u51b3 \u4e0d\u4f1a\u9519\uff01\u201d \u3000\u3000\u5b5f\u5c11\u521a\u52a8\u624b\u4e00\u641c\uff0c\u679c\u7136\u5728\u4ed6\u4eec\u7684\u8eab\u4e0a\u5404\u81ea\u641c\u51fa\u4e86\u4e00\u9762\u91d1\u724c\uff0c\u91d1\u813e\u4e0a\u523b\u6709\u4e00\u53ea\u5140\u9e70\uff0c\u632f\u7fc5 \u817e\u7a7a\uff0c\u795e\u6001\u751f\u52a8\u3002\u8fd9\u662f\u201c\u91d1\u5e10\u6b66\u58eb\u201d\u7684\u6807\u8bb0\uff0c\u91d1\u724c\u7531\u6210\u5409\u601d\u6c57\u6240\u8d50\uff0c\u5f97\u4e86\u91d1\u813e\u7684\u6b66\u58eb\u5f15\u4e3a\u6b8a \u8363\uff0c\u5f53\u7136\u662f\u968f\u8eab\u643a\u5e26\u7684\u4e86\u3002\u6210\u5409\u601d\u6c57\u66fe\u7ecf\u60f3\u5c01\u674e\u601d\u5357\u505a\u201c\u91d1\u5e10\u6b66\u58eb\u201d\uff0c\u674e\u601d\u5357\u63a8\u8f9e\u4e0d\u5c31\uff0c\u662f \u4ee5\u77e5\u9053\u6709\u8fd9\u4e2a\u89c4\u77e9\u3002 \u3000\u3000\u5b5f\u5c11\u521a\u641c\u51fa\u4e86\u91d1\u724c\uff0c\u5927\u6012\u559d\u9053\uff1a\u201c\u7aa6\u5b89\u5e73\uff0c\u4f60\u4e0d\u4f46\u52fe\u7ed3\u91d1\u864f\uff0c\u800c\u4e14\u79c1\u901a\u8499\u5360\uff0c\u8be5\u5f53\u4f55 \u7f6a\uff0c\u4f60\u81ea\u5df1\u8bf4\u5427\uff01\u201d \u3000\u3000\u6b64\u65f6\u98de\u9f99\u5c71\u7684\u5927\u5c11\u5934\u76ee\u90fd\u5df2\u8dd1\u6765\uff0c\u51e0\u4e4e\u6324\u6ee1\u4e86\u805a\u4e49\u5385\uff0c\u9707\u4e8e\u6c5f\u5357\u5927\u4f7f\u5b5f\u5c11\u521a\u7684\u58f0\u5a01\uff0c\u8c01 \u90fd\u4e0d\u6562\u9c81\u83bd\u52a8\u624b\u3002 \u3000\u3000\u8c37\u6db5\u865a\u6276\u8d77\u4e86\u9ed1\u77f3\u9053\u4eba\uff0c\u53ea\u89c1\u9ed1\u77f3\u9053\u4eba\u9762\u5982\u91d1\u7eb8\uff0c\u5df2\u662f\u54bd\u54bd\u4e00\u606f\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u7ed9\u9ed1\u77f3\u9053\u4eba\u6577\u4e0a\u4e86\u91d1\u521b\u836f\uff0c\u6084\u58f0\u8bf4\u9053\uff1a\u201c\u7aa6\u5b89\u5e73\uff0c\u6211\u4e0d\u60f3\u6740\u4e00\u4e2a\u5df2\u7ecf\u53d7\u4f24\u7684\u4eba\uff0c \u4f60\u81ea\u884c\u4e86\u65ad\u5427\uff01\u201d\u7aa6\u5b89\u5e73\u5de6\u624b\u7684\u4e94\u6839\u6307\u5934\u521a\u624d\u5728\u6fc0\u6597\u4e2d\u4e1a\u5df2\u7ed9\u9ed1\u77f3\u9053\u4eba\u7684\u5feb\u5200\u524a\u6389\uff0c\u662f\u4ee5\u8c37 \u6db5\u865a\u53e3\u51fa\u6b64\u8a00\u3002\u201c\u81ea\u884c\u4e86\u65ad\u201d\u5c31\u662f\u8981\u4ed6\u81ea\u6740\u7684\u610f\u601d\u3002 \u3000\u3000\u7aa6\u5b89\u5e73\u770b\u89c1\u5b5f\u3001\u674e\u4e8c\u4eba\u6765\u5230\uff0c\u81ea\u5df1\u5e03\u4e0b\u7684\u9677\u9631\u5b8c\u5168\u5931\u6548\uff01\u5fc3\u4e2d\u5f53\u7136\u662f\u6050\u60e7\u7684\u3002\u4f46\u4fd7\u8bed\u6709 \u4e91\uff1a\u201c\u56f0\u517d\u72b9\u6597\u201d\u3002\u4ed6\u624b\u4e0b\u8fd8\u6709\u8fd9\u8bb8\u591a\u4eba\uff0c\u5982\u4f55\u80af\u7518\u5fc3\u81ea\u5c3d\uff1f\u5f53\u4e0b\u5706\u7741\u53cc\u76ee\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a \u201c\u597d\u4e2a\u72c2\u5984\u7684\u5c0f\u5b50\uff0c\u80c6\u6562\u5728\u6211\u7684\u5be8\u4e2d\u903c\u6211\u81ea\u884c\u4e86\u65ad\uff1f\u4f17\u4f4d\u5f1f\u5144\uff0c\u4f60\u4eec\u8bf4\u8bdd\uff0c\u51ed\u7740\u6211\u4eec\u98de\u9f99\u5c71 \u5728\u7eff\u6797\u4e2d\u95ef\u51fa\u6765\u7684\u4e07\u513f\uff0c\u5c82\u80fd\u53d7\u4eba\u5982\u6b64\u4fae\u8fb1\uff0c\u7aa6\u67d0\u7eb5\u7136\u53d7\u4f24\uff0c\u4e5f\u8a93\u5fc5\u4e0e\u4f60\u8fd9\u5c0f\u5b50\u4e00\u62fc\uff01\u201d \u3000\u3000\u7aa6\u5b89\u5e73\u7684\u8fd9\u4e00\u756a\u8bf4\u8bdd\u7528\u610f\u662f\u5728\u6fc0\u53d1\u624b\u4e0b\u5934\u76ee\u4e0e\u4ed6\u540c\u4ec7\u654c\u5ffe\uff0c\u679c\u7136\u6709\u51e0\u4e2a\u7cca\u6d82\u7684\u4eba\u7ed9\u4ed6\u8bf4 \u5f97\u6c14\u6124\u586b\u81ba\uff0c\u56f4\u62e2\u5728\u4ed6\u7684\u5468\u56f4\uff0c\u5411\u8c37\u6db5\u865a\u6012\u76ee\u800c\u89c6\uff0c\u51c6\u5907\u4fdd\u536b\u4ed6\u4eec\u7684\u9996\u9886\u3002 \u3000\u3000\u4e25\u70f7\u4e5f\u4e0d\u77e5\u4e0d\u89c9\u5730\u8d70\u5230\u4e86\u8c37\u6db5\u865a\u7684\u8eab\u65c1\uff0c\u51c6\u5907\u4e0e\u4ed6\u5e76\u80a9\u4f5c\u6218\u3002 \u3000\u3000\u5b5f\u5c11\u521a\u9053\uff1a\u201c\u8fd9\u4eba\u662f\u8c01\uff1f\u201d\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u8fd9\u4f4d\u8c37\u5e08\u5144\u662f\u6211\u803f\u5e08\u53d4\u7684\u9ad8\u5f92\u3002\u201d\u5b5f\u5c11\u521a\u66fe\u7ecf \u542c\u8fc7\u5973\u513f\u8bf4\u8fc7\u8c37\u6db5\u865a\u4e0e\u4e25\u70f7\u4e4b\u4e8b\uff0c\u8bf4\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u4ed6\u5c31\u662f\u8c37\u6db5\u865a\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u70f7\u59b9\uff0c\u4f60\u7ed9\u9ed1\u77f3\u9053\u957f\u5305\u88f9\u4f24\u53e3\uff0c\u6211\u6765\u66ff\u4ed6\u62a5\u4ec7\uff01\u201d\u5530\u5730\u62d4\u51fa\u5251\u6765\uff0c \u559d\u9053\uff1a\u201c\u8c01\u8981\u66ff\u7aa6\u5b89\u5e73\u4f5c\u966a\u846c\u7684\uff0c\u6211\u5c31\u6210\u5168\u4ed6\u5427\uff01\u201d \u3000\u3000\u5b5f\u5c11\u521a\u5ffd\u5730\u559d\u9053\uff1a\u201c\u4e14\u6162\uff01\u201d\u90a3\u51e0\u4e2a\u5934\u76ee\u672c\u6765\u5df2\u662f\u51c6\u5907\u4e0a\u524d\u4e00\u62fc\u7684\uff0c\u5b5f\u5c11\u521a\u4e00\u559d\uff0c\u4e0d\u89c9 \u90fd\u6b62\u4f4f\u4e86\u811a\u6b65\u3002 \u3000\u3000\u5b5f\u5c11\u521a\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u98de\u9f99\u5c71\u95ef\u51fa\u7684\u4e07\u513f\u662f\u7ed9\u7aa6\u5b89\u5e73\u73b7\u6c61\u4e86\u7684\uff0c\u4e0e\u4f60\u4eec\u65e0\u5173\u3002\u4f60\u4eec\u770b\u770b\uff0c \u8fd9\u4e24\u4e2a\u4eba\u5c31\u662f\u8499\u53e4\u7684\u91d1\u5e10\u6b66\u58eb\uff0c\u521a\u624d\u8dd1\u6389\u7684\u90a3\u4e2a\u4eba\u53c8\u662f\u91d1\u56fd\u56fd\u5e08\u9633\u5929\u96f7\u7684\u4f84\u5b50\uff0c\u7aa6\u5b89\u5e73\u4e0d\u60dc \u52fe\u7ed3\u4f59\u864f\u3001\u79c1\u901a\u8499\u53e4\uff0c\u96be\u9053\u4f60\u4eec\u4e5f\u7518\u5fc3\u8ddf\u7740\u4ed6\u505a\u4e24\u59d3\u5bb6\u5974\u3001\u5f02\u65cf\u9e70\u722a\uff1f\u201d \u3000\u3000\u98de\u9f99\u5c71\u7684\u5934\u76ee\u548c\u7aa6\u5b89\u5e73\u9080\u6765\u7684\u597d\u624b\uff0c\u4e00\u6765\u662f\u602f\u60e7\u4e8e\u5b5f\u5c11\u521a\u7684\u201c\u795e\u5251\u201d\u5a01\u540d\uff0c\u4e8c\u6765\u5728\u7aa6\u5b89 \u5e73\u7684\u79c1\u901a\u9791\u5b50\u7684\u79d8\u5bc6\u5df2\u7ecf\u7ed9\u63ed\u7834\u4e4b\u540e\uff0c\u4e5f\u662f\u6697\u6697\u6094\u610f\uff0c\u4e0d\u6562\u516c\u7136\u52a9\u4ed6\u4e86\u3002\u90a3\u51e0\u4e2a\u7cca\u6d82\u7684\u5934\u76ee \u5e73\u65e5\u662f\u7ed9\u7aa6\u5b89\u5e73\u7528\u5c0f\u6069\u5c0f\u60e0\u7b3c\u7edc\u7684\uff0c\u4f46\u6b64\u65f6\u89c1\u4f17\u4eba\u5664\u82e5\u5bd2\u8749\uff0c\u53c8\u89c1\u7aa6\u5b89\u5e73\u5bf9\u81ea\u5df1\u7684\u628a\u5144\u5f1f\u4e5f \u80fd\u4e0b\u5f97\u8fa3\u624b\uff0c\u4ed4\u7ec6\u4e00\u60f3\uff0c\u4e5f\u89c9\u5f97\u7ed9\u7aa6\u5b89\u5e73\u966a\u846c\u5b9e\u662f\u4e0d\u503c\uff0c\u4e8e\u662f\u4e00\u4e2a\u4e2a\u7684\u5728\u4ed6\u8ddf\u524d\u6e9c\u8d70\u3002 \u3000\u3000\u7aa6\u5b89\u5e73\u9762\u5982\u58eb\u8272\uff0c\u98a4\u58f0\u8bf4\u9053\uff1a\u201c\u767d\u8001\u82f1\u96c4\uff0c\u820d\u5df1\u5982\u65af\uff0c\u6211\u4e5f\u4e0d\u6562\u8bf4\u4ec0\u4e48\u6709\u798f\u540c\u4eab\uff0c\u6709\u7978 \u540c\u5f53\u7684\u8bdd\u4e86\u3002\u6211\u6b7b\u4e0d\u8db3\u60dc\uff0c\u4f46\u82e5\u662f\u4efb\u51ed\u4ed6\u4eec\u5f97\u901e\uff0c\u53ea\u6015\u6c5f\u6e56\u4e0a\u7684\u670b\u53cb\u4f1a\u7b11\u8bdd\u767d\u8001\u82f1\u96c4\u662f\u6015\u4e86 \u5b5f\u5c11\u521a\uff01\u201d \u3000\u3000\u767d\u4e07\u96c4\u660e\u77e5\u4ed6\u662f\u51fa\u8a00\u6311\u62e8\uff0c\u60f3\u5229\u7528\u81ea\u5df1\u6765\u66ff\u4ed6\u62b5\u6321\u5f3a\u654c\uff0c\u53ef\u662f\u4e5f\u4e0d\u80fd\u4e0d\u786c\u7740\u5934\u76ae\u7ad9\u4e86\u51fa \u6765\u3002 \u3000\u3000\u539f\u6765\u767d\u4e07\u96c4\u4e0e\u7aa6\u5b89\u5e73\u4e43\u662f\u4e00\u4e18\u4e4b\u8c89\uff0c\u5f7c\u6b64\u90fd\u662f\u8d70\u4e86\u9633\u5929\u96f7\u7684\u95e8\u8def\uff0c\u51c6\u5907\u89c1\u98ce\u4f7f\u8235\uff0c\u5356\u56fd \u6c42\u8363\u7684\u3002\u7aa6\u5b89\u5e73\u8bf4\u7684\u90a3\u51e0\u53e5\u8bdd\u9690\u9690\u542b\u6709\u5a01\u80c1\u4e4b\u610f\uff0c\u5176\u5b9e\u5c31\u662f\u5411\u767d\u4e07\u96c4\u6697\u793a\uff1a\u201c\u6211\u4eec\u8bf4\u597d\u4e86\u6709 \u798f\u540c\u4eab\uff0c\u6709\u7978\u540c\u5f53\uff0c\u4f60\u82e5\u60f3\u7f6e\u8eab\u4e8b\u5916\uff0c\u6211\u5c31\u53ea\u597d\u548c\u76d8\u6258\u51fa\u4e86\u3002\u201d\u767d\u4e07\u96c4\u6709\u628a\u67c4\u634f\u5728\u7aa6\u5b89\u5e73\u7684 \u624b\u91cc\uff0c\u662f\u4ee5\u660e\u77e5\u4ed6\u7684\u7528\u610f\uff0c\u4e5f\u53ea\u597d\u786c\u7740\u5934\u76ae\uff0c\u633a\u8eab\u800c\u51fa\u3002 \u3000\u3000\u4e0d\u8fc7\u767d\u4e07\u96c4\u4e5f\u662f\u8001\u5978\u5de8\u6ed1\u4e4b\u8f88\uff0c\u7709\u5934\u4e00\u76b1\uff0c\u8ba1\u4e0a\u5fc3\u6765\uff0c\u4fbf\u5373\u8bf4\u9053\uff1a\u201c\u5b5f\u5927\u4fa0\u662f\u5f53\u4eca\u7b2c\u4e00 \u5251\u672f\u9ad8\u624b\uff0c\u767d\u67d0\u9887\u6709\u81ea\u77e5\u4e4b\u660e\uff0c\u5373\u4f7f\u767d\u67d0\u6c14\u529b\u5145\u6c9b\uff0c\u4e5f\u51b3\u8ba1\u4e0d\u662f\u5b5f\u5927\u4fa0\u7684\u5bf9\u624b\u3002\u4e0d\u8fc7\u7aa6\u5be8\u4e3b \u65e2\u7136\u8fd9\u4e48\u8bf4\u4e86\uff0c\u6211\u82e5\u4e0d\u5411\u5b5f\u5927\u4fa0\u8bf7\u6559\uff0c\u61e6\u592b\u4e4b\u8bee\uff0c\u66f4\u662f\u96be\u582a\u3002\u597d\u5728\u8d25\u5728\u5b5f\u5927\u4fa0\u624b\u91cc\u4e5f\u662f\u867d\u8d25 \u72b9\u8363\uff0c\u5b5f\u5927\u4fa0\u4f60\u5c31\u5212\u51fa\u9053\u513f\u6765\u5427\u3002\u201d \u3000\u3000\u8fd9\u756a\u8bf4\u8bdd\u8bf4\u5f97\u5341\u5206\u5f97\u4f53\uff0c\u4e00\u9762\u662f\u8868\u793a\u4e0d\u6562\u4e0e\u5b5f\u5c11\u521a\u4e3a\u654c\uff0c\u4e00\u9762\u4e5f\u6697\u793a\u4e86\u81ea\u5df1\u662f\u5df2\u7ecf\u6076\u6597 \u4e86\u4e00\u573a\uff0c\u6c14\u529b\u4e0d\u52a0\u7684\u3002\u8bd5\u60f3\u4ee5\u5b5f\u5c11\u521a\u7684\u8eab\u4efd\uff0c\u5982\u4f55\u8fd8\u80fd\u4e0e\u4ed6\u6bd4\u62fc\uff1f \u3000\u3000\u4e0d\u51fa\u6240\u6599\uff0c\u5b5f\u5c11\u521a\u679c\u7136\u8bf4\u9053\uff1a\u201c\u767d\u4e07\u96c4\uff0c\u6211\u5df2\u7ecf\u8bf4\u5f97\u5341\u5206\u6e05\u695a\uff0c\u4f60\u7adf\u7136\u8fd8\u662f\u4e0d\u5206\u9ed1\u767d\uff0c \u7ed9\u7aa6\u5b89\u5e73\u4f5c\u966a\u846c\u4e48\uff1f\u201d \u3000\u3000\u767d\u4e07\u96c4\u9053\uff1a\u201c\u6c5f\u6e56\u4e0a\u4ee5\u4e49\u6c14\u4e3a\u5148\uff0c\u6211\u4e0d\u7ba1\u4f60\u8bf4\u7684\u662f\u4ec0\u4e48\u9053\u7406\uff0c\u6211\u53ea\u77e5\u4e3a\u670b\u53cb\u4e0d\u60dc\u4e24\u808b\u63d2 \u5200\uff0c\u5373\u4f7f\u662f\u4e09\u5200\u516d\u6d1e\uff0c\u51b3\u4e0d\u76b1\u7709\u3002\u201d \u3000\u3000\u5b5f\u5c11\u521a\u9053\uff1a\u201c\u4f60\u65e2\u7136\u5b9a\u8981\u548c\u6211\u6bd4\u8bd5\uff0c\u6211\u5374\u8981\u8d62\u5f97\u4f60\u5fc3\u670d\u53e3\u670d\u624d\u884c\u3002\u4f60\u4eca\u65e5\u65e2\u7136\u6c14\u529b\u4e0d \u52a0\uff0c\u6211\u53ef\u4ee5\u8bb8\u4f60\u6539\u671f\u518d\u6bd4\u3002\u201d \u3000\u3000\u5b5f\u5c11\u521a\u8fd9\u8bdd\u5df2\u6709\u653e\u8fc7\u767d\u4e07\u96c4\u4e4b\u610f\uff0c\u53ef\u662f\u767d\u4e07\u96c4\u5728\u7aa6\u5b89\u5e73\u5a01\u80c1\u4e4b\u4e0b\uff0c\u5374\u4e0d\u80fd\u820d\u5f03\u4e86\u4ed6\uff0c\u72ec \u81ea\u8131\u8eab\uff0c\u53ea\u597d\u518d\u5192\u4e2a\u9669\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u548c\u7aa6\u5be8\u4e3b\u662f\u4f11\u621a\u4e0e\u5171\uff0c\u51b3\u4e0d\u81ea\u6c42\u5e78\u514d\u7684\uff0c\u4f60\u8981\u6539\u671f\u518d\u6bd4 \u4e5f\u884c\uff0c\u4f46\u4eca\u65e5\u4f60\u4eec\u4e5f\u4e0d\u80fd\u548c\u7aa6\u5be8\u4e3b\u52a8\u624b\u4e86\u3002\u201d \u3000\u3000\u5b5f\u5c11\u521a\u7591\u5fc3\u987f\u8d77\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u767d\u4e07\u96c4\u4f3c\u4e4e\u4e0d\u662f\u4e2a\u5f88\u91cd\u4e49\u6c14\u7684\u4eba\uff0c\u4e3a\u4f55\u4ed6\u5374\u8a93\u6b7b\u8981\u4e3a\u7aa6 \u5b89\u5e73\u5356\u547d\uff1f\u201d\u4e0d\u8fc7\u56e0\u4e3a\u6ca1\u6709\u786e\u5207\u7684\u8bc1\u636e\uff0c\u5374\u8fd8\u4e0d\u6562\u65ad\u5b9a\u4ed6\u5c31\u662f\u548c\u7aa6\u5b89\u5e73\u4e00\u6837\u7684\u5356\u56fd\u6c42\u8363\uff0c\u56e0 \u6b64\u4e00\u65f6\u4e4b\u95f4\u4e5f\u5c31\u62ff\u4e0d\u5b9a\u4e3b\u610f\u8981\u4e0d\u8981\u548c\u4ed6\u52a8\u624b\u4e86\u3002 \u3000\u3000\u5b5f\u5c11\u521a\u6b63\u81ea\u8e0c\u8e87\uff0c\u674e\u601d\u5357\u5df2\u662f\u5fcd\u4e0d\u4f4f\u8bf4\u9053\uff1a\u201c\u5272\u9e21\u7109\u7528\u725b\u5200\uff0c\u5b5f\u5927\u4fa0\u4e0d\u613f\u5360\u4f60\u4fbf\u5b9c\uff0c\u6211 \u6765\u548c\u4f60\u6bd4\u8bd5\uff01\u201d \u3000\u3000\u767d\u4e07\u96c4\u9053\uff1a\u201c\u6211\u672c\u6765\u4e0d\u613f\u548c\u5c0f\u8f88\u6bd4\u8bd5\uff0c\u4e0d\u8fc7\u4f60\u662f\u7eff\u6797\u76df\u4e3b\uff0c\u548c\u4f60\u6bd4\u8bd5\uff0c\u4e5f\u4e0d\u7b97\u8fb1\u6ca1\u6211\u7684 \u8eab\u4efd\u3002\u4f46\u4e0d\u77e5\u5b5f\u5927\u4fa0\u4e4b\u610f\u5982\u4f55\uff1f\u201d \u3000\u3000\u5b5f\u5c11\u521a\u6df1\u77e5\u674e\u601d\u5357\u7684\u672c\u9886\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u767d\u4e07\u96c4\u5df2\u7ecf\u6597\u4e86\u4e00\u573a\uff0c\u601d\u5357\u6599\u4e0d\u81f3\u4e8e\u8d25\u5728\u4ed6\u7684 \u624b\u91cc\uff0c\u4e0d\u8fc7\u53ea\u6015\u4e5f\u6ca1\u6709\u628a\u63e1\u80dc\u4ed6\u3002\u201d\u5b5f\u5c11\u521a\u6c89\u541f\u672a\u8bed\uff0c\u674e\u601d\u5357\u5df2\u5148\u8bf4\u9053\uff1a\u201c\u7b11\u8bdd\uff0c\u4f60\u95ee\u8fd9\u8bdd \u662f\u4ec0\u4e48\u610f\u601d\uff1f\u96be\u9053\u4ee5\u5b5f\u5927\u4fa0\u7684\u8eab\u4efd\uff0c\u4ed6\u8fd8\u4f1a\u6697\u7b97\u4f60\u4e0d\u6210\uff1f\u201d\u5b5f\u5c11\u521a\u542c\u674e\u601d\u5357\u7684\u53e3\u6c14\uff0c\u5012\u4f3c\u4e4e \u662f\u9887\u6709\u628a\u63e1\u3002 \u3000\u3000\u674e\u601d\u5357\u65e2\u7136\u8fd9\u6837\u8bf4\u4e86\uff0c\u5b5f\u5c11\u521a\u53ea\u597d\u8bf4\u9053\uff1a\u201c\u6709\u674e\u76df\u4e3b\u6765\u53d1\u843d\u4f60\uff0c\u6211\u5f53\u7136\u662f\u4e0d\u5c51\u518d\u7ba1\u4f60 \u4e86\u3002\u201d \u3000\u3000\u767d\u4e07\u96c4\u6b63\u662f\u5df4\u4e0d\u5f97\u5b5f\u5c11\u521a\u6709\u8fd9\u53e5\u8bf4\u8bdd\uff0c\u5fc3\u91cc\u81ea\u601d\uff1a\u201c\u53ea\u8981\u4ed6\u8896\u624b\u65c1\u89c2\uff0c\u96be\u9053\u6211\u8fd8\u6253\u4e0d\u8fc7 \u4e00\u4e2a\u540e\u751f\u5c0f\u5b50\uff1f\u201d\u5f53\u4e0b\u5927\u559c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u541b\u5b50\u4e00\u8a00\uff0c\u5feb\u9a6c\u4e00\u97ad\uff0c\u6211\u82e5\u8f93\u4e86\uff0c\u4efb\u51ed\u4f60\u4eec\u5904\u7f6e\u3002 \u4f46\u674e\u76df\u4e3b\uff0c\u5018\u82e5\u662f\u4f60\u8f93\u4e86\uff0c\u90a3\u53c8\u5982\u4f55\uff1f\u201d\u674e\u601d\u5357\u51b7\u7b11\u903c\uff1a\u201c\u968f\u4fbf\u4f60\u5212\u51fa\u9053\u513f\uff0c\u6211\u8f93\u4e86\uff0c\u51b3\u4e0d \u53cd\u6094\uff01\u201d \u3000\u3000\u767d\u4e07\u96c4\u9053\uff1a\u201c\u597d\uff0c\u5018\u82e5\u662f\u4f60\u8f93\u4e86\uff0c\u8fd9\u98de\u9f99\u5c71\u7684\u4e8b\u60c5\uff0c\u5c31\u4e0d\u8bb8\u4f60\u6765\u63d2\u624b\u4e86\u3002\u8bf7\u4f60\u4eec\u9a6c\u4e0a\u79bb \u5f00\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u597d\uff0c\u53ea\u8981\u4f60\u5728\u4e00\u767e\u62db\u4e4b\u5185\uff0c\u80dc\u5f97\u4e86\u6211\uff0c\u6211\u7acb\u5373\u4e0b\u5c71\uff0c\u7aa6\u5b89\u5e73\u6211\u4e5f\u9976\u4ed6\u5c31 \u662f\uff01\u201d \u3000\u3000\u767d\u4e07\u96c4\u559c\u51fa\u671b\u5916\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u7eff\u6797\u76df\u4e3b\uff0c\u8bf4\u7684\u8bdd\u53ef\u5f97\u7b97\u6570\u3002\u6211\u4f46\u6c42\u4f60\u4eec\u4e0d\u518d\u5e72\u9884\u98de\u9f99 \u5c71\u4e4b\u4e8b\uff0c\u81f3\u4e8e\u80dc\u8d25\u7684\u9650\u5b9a\uff0c\u5374\u4e5f\u65e0\u987b\u5c31\u662f\u767e\u62db\uff01\u201d\u4ed6\u4e00\u518d\u5f3a\u8c03\u674e\u601d\u5357\u7eff\u6797\u76df\u4e3b\u7684\u8eab\u4efd\uff0c\u5176\u5b9e \u662f\u8bf4\u7ed9\u5b5f\u5c11\u521a\u3001\u891a\u4e91\u5cf0\u7b49\u4eba\u542c\u7684\u3002\u4ed6\u81ea\u4ee5\u4e3a\u662f\u7a33\u64cd\u80dc\u5238\uff0c\u628a\u8bdd\u9884\u5148\u8bf4\u597d\uff0c\u5c31\u4e0d\u6015\u80dc\u8d1f\u5df2\u5206\u4e4b \u540e\uff0c\u5b5f\u3001\u891a\u7b49\u4eba\u518d\u6765\u96be\u4e3a\u7aa6\u5b89\u5e73\u4e86\u3002 \u3000\u3000\u5b5f\u5c11\u521a\u76b1\u4e86\u76b1\u7709\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u674e\u601d\u5357\u5fd2\u4e5f\u592a\u597d\u80dc\u4e86\u3002\u201d\u4f46\u4e3a\u4e86\u5c0a\u91cd\u674e\u601d\u5357\u7eff\u6797\u76df\u4e3b\u7684 \u8eab\u4efd\uff0c\u4e5f\u53ea\u80fd\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u80dc\u8d25\u4e4b\u6570\uff0c\u539f\u662f\u4e0d\u5fc5\u9650\u5b9a\u767e\u62db\uff01\u201d\u674e\u601d\u5357\u7b54\u5e94\u7684\u6761\u4ef6\uff0c\u4ed6\u5374\u662f \u4e0d\u4fbf\u66f4\u6539\u4e86\u3002 \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u4ed6\u662f\u6253\u8fc7\u4e86\u4e00\u573a\u7684\uff0c\u6211\u5c82\u80fd\u5360\u4ed6\u7684\u4fbf\u5b9c\uff0c\u5fc5\u987b\u8fd9\u6837\uff0c\u624d\u80fd\u4ee4\u4ed6\u6ca1\u6709\u95f2\u8bdd\u597d \u8bf4\uff0c\u5b5f\u5927\u4fa0\uff0c\u5c31\u8bf7\u4f60\u4f5c\u4e2a\u8bc1\u4eba\u5427\u3002\u201d \u3000\u3000\u5b5f\u5c11\u521a\u542c\u4ed6\u8bf4\u5f97\u597d\u50cf\u6781\u6709\u628a\u63e1\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u601d\u5357\u6027\u683c\u7a33\u91cd\uff0c\u51b3\u975e\u72c2\u5984\u4e4b\u5f92\uff1b\u4ed6\u82e5\u662f\u6ca1 \u6709\u51e0\u5206\u628a\u63e1\uff0c\u6599\u4e5f\u4e0d\u6562\u8fd9\u6837\u8bf4\u8bdd\u3002\u201d\u53ef\u662f\u4ecd\u7136\u4e0d\u7981\u6709\u70b9\u4e3a\u674e\u601d\u5357\u62c5\u5fc3\u3002 \u3000\u3000\u767d\u4e07\u96c4\u5fc3\u82b1\u6012\u653e\uff0c\u7acb\u5373\u8bf4\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u8bf7\u674e\u76df\u4e3b\u4eae\u5251\u8fdb\u62db\uff01\u201d \u3000\u3000\u6768\u5a49\u8d70\u4e0a\u524d\u6765\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u5357\u54e5\uff0c\u4f60\u7528\u6211\u7684\u5b9d\u5251\u3002\u201d\u5979\u662f\u89c1\u8fc7\u767d\u4e07\u96c4\u7684\u672c\u4e8b\uff0c\u201c\u891a\u4e91 \u5cf0\u548c\u8c37\u6db5\u865a\u4e24\u4eba\u8054\u624b\u6597\u4ed6\uff0c\u4e5f\u53ea\u4e0d\u8fc7\u6253\u4e2a\u5e73\u624b\uff0c\u674e\u601d\u5357\u7684\u672c\u9886\u53ef\u80fd\u6bd4\u891a\u4e91\u5cf0\u7a0d\u80dc\u4e00\u7b79\uff0c\u4f46\u8981 \u8bf4\u5355\u6253\u72ec\u6597\u5c31\u53ef\u4ee5\u80dc\u5f97\u4e86\u767d\u4e07\u96c4\uff0c\u8fd9\u662f\u8fde\u6768\u5a49\u4e5f\u4e0d\u6562\u76f8\u4fe1\u7684\u3002\u6768\u5a49\u73b0\u5728\u6240\u7528\u7684\u8fd9\u4e00\u628a\u5251\u4e43\u662f \u660e\u6167\u516c\u4e3b\u6240\u8d60\u7684\u5b9d\u7269\uff0c\u6709\u65ad\u91d1\u524a\u7389\u4e4b\u80fd\uff0c\u5439\u6bdb\u7acb\u65ad\u4e4b\u5229\uff0c\u6545\u800c\u6768\u5a49\u8981\u501f\u7ed9\u4ed6\u7528\uff0c\u5e0c\u671b\u4ed6\u6709\u4e86 \u5b9d\u5251\uff0c\u6216\u8005\u8fd8\u6709\u51e0\u5206\u673a\u4f1a\uff0c\u53ef\u4ee5\u5728\u767e\u62db\u4e4b\u5185\u514b\u5236\u5f3a\u654c\u3002 \u3000\u3000\u4e0d\u6599\u674e\u601d\u5357\u975e\u4f46\u4e0d\u7528\u5979\u7684\u5b9d\u5251\uff0c\u800c\u4e14\u8fde\u81ea\u5df1\u7684\u4f69\u5251\u4e5f\u89e3\u4e86\u4e0b\u6765\uff01\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u80fd\u5360\u4ed6\u534a \u70b9\u4fbf\u5b9c\uff0c\u4ed6\u65e2\u7136\u4e0d\u7528\u5175\u5668\uff0c\u6211\u5c31\u4e0e\u4ed6\u638c\u5e95\u5224\u96cc\u96c4\u5427\uff01\u201d \u3000\u3000\u767d\u4e07\u96be\u7ec3\u6709\u7ef5\u638c\u51fb\u77f3\u5982\u7c89\u7684\u529f\u592b\uff0c\u521a\u67d4\u517c\u6d4e\u7684\u638c\u529b\u8db3\u4ee5\u53f7\u79f0\u6b66\u6797\u4e00\u7edd\uff0c\u674e\u601d\u5357\u8981\u548c\u4ed6\u6bd4 \u638c\uff0c\u8fd9\u5bf9\u4ed6\u6765\u8bf4\uff0c\u6b63\u662f\u6c42\u4e4b\u4e0d\u7684\u4e8b\u3002\u5f53\u4e0b\u4e50\u5f97\u54c8\u54c8\u5927\u7b11\uff0c\u8bf4\u9053\uff0c\u201c\u674e\u5c11\u9655\u5f53\u771f\u4e0d\u69d0\u662f\u5c11\u5e74\u82f1 \u96c4\uff0c\u53ea\u51ed\u8fd9\u4efd\u8c6a\u6c14\uff0c\u5c31\u8db3\u4ee5\u4ee4\u4eba\u6298\u670d\uff01\u602a\u4e0d\u5f97\u7eff\u6797\u540c\u9053\uff0c\u63a8\u6234\u4f60\u505a\u76df\u4e3b\u4e86\u3002\u597d\uff01\u8fd9\u5c31\u8bf7\u76df\u4e3b \u8d50\u62db\u5427\uff01\u201d \u3000\u3000\u6768\u5a49\u5374\u662f\u4e0d\u7981\u624b\u5fc3\u91cc\u634f\u7740\u4e00\u628a\u51b7\u6c57\uff0c\u6697\u6697\u5403\u60ca\uff0c\u60f3\u9053\uff1a\u201c\u4ee5\u891a\u4e91\u5cf0\u548c\u8c37\u6db5\u865a\u4e24\u4eba\u5408\u4f7f\u7684 \u5929\u96f7\u638c\u529b\uff0c\u5c1a\u4e14\u80dc\u4e0d\u4e86\u8fd9\u4e2a\u8001\u8d3c\uff0c\u5357\u54e5\u4e5f\u672a\u514d\u592a\u8fc7\u6258\u5927\u4e86\u3002\u201d\u53ef\u662f\u6b64\u65f6\u53cc\u65b9\u5df2\u662f\u628a\u8bdd\u8bf4\u6ee1\uff0c \u5982\u7bad\u5728\u5f26\uff0c\u5373\u5c06\u4ea4\u624b\uff0c\u6768\u5a49\u867d\u7136\u7740\u6025\uff0c\u4ea6\u662f\u65e0\u53ef\u5982\u4f55\u3002 \u3000\u3000\u674e\u601d\u5357\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u5e7c\u4e0d\u50ed\u957f\uff0c\u4f60\u5148\u51fa\u62db\u3002\u201d\u767d\u4e07\u96c4\u66f4\u5408\u5fc3\u610f\uff0c\u7acb\u5373\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u674e\u76df \u4e3b\u65e2\u7136\u5982\u6b64\u8c26\u8ba9\uff0c\u8001\u673d\u662f\u606d\u656c\u4e0d\u5982\u4ece\u547d\u4e86\u3002\u201d\u8bdd\u58f0\u4e00\u6536\uff0c\u9a6c\u4e0a\u8de8\u6b65\u8fdb\u62db\uff0c\u547c\u7684\u4e00\u638c\uff0c\u5411\u674e\u601d \u5357\u6253\u53bb\u3002 \u3000\u3000\u674e\u601d\u5357\u559d\u9053\uff1a\u201c\u6765\u5f97\u597d\uff01\u201d\u4e00\u504f\u8eab\u5de6\u8155\u865a\u52fe\u53f3\u62f3\u75be\u5410\uff0c\u4e00\u907f\u5b9e\u5c31\u865a\uff0c\u53cd\u51fb\u767d\u4e07\u96c4\u7684\u5de6 \u201c\u80a9\u4e95\u7a74\u201d\u3002\u8fd9\u4e00\u62db\u4e24\u5f0f\uff0c\u62f3\u638c\u517c\u65bd\uff0c\u6b63\u662f\u653b\u654c\u6240\u5fc5\u6551\u7684\u6740\u624b\u7edd\u62db\uff0c\u767d\u4e07\u96c4\u8fd9\u624d\u4e0d\u7531\u5f97\u9aa4\u5403 \u4e00\u60ca\uff01\u60f3\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u679c\u7136\u4e86\u5f97\uff0c\u602a\u4e0d\u5f97\u4ed6\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u5c45\u7136\u80fd\u4ee4\u7fa4\u8c6a\u6151\u670d\uff0c\u505a\u5230\u4e86\u7eff\u6797\u76df \u4e3b\uff01\u201d\u521a\u624d\u4ed6\u867d\u7136\u53e3\u5934\u4e0a\u606d\u7ef4\u4e86\u674e\u601d\u5357\uff0c\u4f46\u5374\u662f\u8a00\u4e0d\u7531\u8877\u7684\uff0c\u5982\u4eca\u89c1\u4e86\u674e\u601d\u5357\u771f\u5b9e\u7684\u672c\u9886\uff0c \u8fd9\u624d\u662f\u771f\u6b63\u7684\u4f69\u670d\u4e86\u3002 \u3000\u3000\u4f46\u767d\u4e07\u96c4\u631f\u7740\u6570\u5341\u5e74\u529f\u529b\uff0c\u867d\u89c9\u5bf9\u65b9\u672c\u9886\u51fa\u4e4e\u4ed6\u610f\u6599\u4e4b\u5916\uff0c\u4ecd\u662f\u8ba4\u4e3a\u53ef\u64cd\u80dc\u5238\u3002\u5f53\u4e0b\u7acb \u5373\u907f\u62db\u8fd8\u62db\uff0c\u5530\u5730\u4e00\u7a9c\uff0c\u53cc\u81c2\u7b95\u5f20\uff0c\u5411\u5916\u4e00\u5c55\uff0c\u5de6\u638c\u51fb\u674e\u60f3\u5357\u7684\u989d\u95e8\uff0c\u53f3\u638c\u4f38\u51fa\uff0c\u63d2\u5411\u674e\u601d \u5357\u7684\u80f8\u819b\u3002\u8fd9\u4e00\u62db\u6709\u4e2a\u540d\u5802\u53eb\u505a\u201c\u53cc\u9f99\u51fa\u6d77\u201d\u3002 \u3000\u3000\u5bf9\u65b9\u653b\u5f97\u731b\uff0c\u674e\u601d\u5357\u4e5f\u5b88\u5f97\u5999\uff0c\u7728\u773c\u95f4\u5df2\u662f\u8eab\u79fb\u6b65\u6362\uff0c\u4f0f\u8eab\u63c9\u8fdb\uff0c\u6b65\u8d70\u8fde\u73af\uff0c\u53cc\u638c\u63e1 \u62b1\uff0c\u500f\u5730\u4e00\u5206\uff0c\u4e00\u8bb0\u8f7b\u63cf\u6de1\u5199\u7684\u201c\u63a8\u7a97\u89c1\u6708\u201d\u5c31\u628a\u5bf9\u65b9\u7684\u653b\u52bf\u5316\u89e3\u4e86\u3002 \u3000\u3000\u4e24\u4eba\u6b64\u6765\u5f7c\u5f80\uff0c\u8d8a\u6597\u8d8a\u731b\uff0c\u767d\u4e07\u96c4\u624b\u811a\u8d77\u5904\uff0c\u5168\u5e26\u52b2\u98ce\uff0c\u5468\u56f4\u6570\u4e08\u4e4b\u5185\uff0c\u65c1\u89c2\u7684\u4eba\u90fd\u611f \u5230\u4ed6\u7684\u638c\u98ce\u522e\u9762\uff0c\u9690\u9690\u4f5c\u75db\uff0c\u4e0d\u7531\u5f97\u6b65\u6b65\u540e\u9000\u3002 \u3000\u3000\u4f46\u674e\u601d\u5357\u4e5f\u4e1d\u6beb\u4e0d\u89c1\u5403\u4e8f\uff0c\u53ea\u89c1\u4ed6\u811a\u8e0f\u4e94\u884c\u516b\u5366\u65b9\u4f4d\uff0c\u638c\u968f\u8eab\u8f6c\uff0c\u6307\u4e1c\u6253\u897f\uff0c\u5ffd\u7eb5\u5ffd \u6a2a\uff0c\u5ffd\u62f3\u5ffd\u638c\uff0c\u53d8\u5316\u7e41\u590d\uff0c\u62db\u6570\u5947\u5999\uff0c\u679c\u7136\u662f\u4e0e\u4f17\u4e0d\u540c\uff01\u6597\u5230\u7d27\u5904\uff0c\u53ea\u89c1\u4e24\u6761\u4eba\u5f71\u5ffd\u5408\u5ffd \u5206\uff0c\u5df2\u5206\u4e0d\u51fa\u8c01\u662f\u767d\u4e07\u96c4\u8c01\u662f\u674e\u601d\u5357\u4e86\u3002 \u3000\u3000\u5b5f\u5c11\u521a\u8fd9\u624d\u653e\u4e0b\u5fc3\u6765\u3001\u60f3\u9053\uff0c\u201c\u5c11\u6797\u5bfa\u771f\u4f20\u7684\u8fbe\u6469\u638c\u6cd5\u679c\u7136\u662f\u975e\u540c\u51e1\u54cd\uff01\u201d\u539f\u6765\u674e\u601d\u5357 \u4f7f\u7684\u4e00\u5957\u4f0f\u864e\u62f3\u3001\u4e00\u5957\u7f57\u53cc\u638c\uff0c\u4e43\u662f\u5c11\u6797\u5bfa\u59cb\u7956\u8fbe\u6469\u7956\u5e08\u7684\u8863\u94b5\u771f\u4f20\uff0c\u5408\u8d77\u6765\u5c31\u662f\u5168\u5957\u7684\u8fbe \u6469\u638c\u6cd5\u3002\u8fbe\u6469\u638c\u6cd5\u7cbe\u6df1\u535a\u5927\uff0c\u82e5\u662f\u7ec3\u5230\u4e86\u7089\u706b\u7eaf\u9752\u4e4b\u5883\uff0c\u8db3\u4ee5\u7834\u89e3\u4efb\u4f55\u4e00\u6d3e\u7684\u62f3\u638c\u529f\u592b\u3002 \u3000\u3000\u674e\u601d\u5357\u867d\u7136\u5c1a\u672a\u7ec3\u5230\u7089\u706b\u7eaf\u9752\u4e4b\u5883\uff0c\u4f46\u5728\u4ed6\u8fdb\u6765\u4e4b\u65f6\uff0c\u770b\u89c1\u767d\u4e07\u96c4\u4ee5\u7ef5\u638c\u62b5\u6321\u891a\u3001\u8c37\u4e8c \u4eba\u7684\u201c\u5929\u96f7\u529f\u201d\uff0c\u989d\u89d2\u5df2\u662f\u6c81\u51fa\u9ec4\u8c46\u822c\u5927\u5c0f\u7684\u6c57\u73e0\uff0c\u77e5\u9053\u4ed6\u7684\u5185\u529b\u5b9a\u7136\u96be\u4ee5\u4e3a\u7ee7\uff0c\u53ea\u8981\u5728\u5185 \u529b\u4e0a\u4e0d\u8f93\u7ed9\u4ed6\uff0c\u5728\u638c\u6cd5\u4e0a\u674e\u601d\u5357\u81ea\u662f\u6709\u53d6\u80dc\u7684\u628a\u63e1\u3002\u56e0\u6b64\u674e\u601d\u5357\u624d\u6562\u5938\u4e0b\u6d77\u53e3\uff0c\u58f0\u8a00\u8981\u5728\u767e \u62db\u4e4b\u5185\u51fb\u8d25\u4ed6\u7684\u3002 \u3000\u3000\u679c\u7136\u8fc7\u4e86\u4e94\u5341\u62db\u4e4b\u540e\uff0c\u767d\u4e07\u96c4\u731b\u653b\u4e0d\u4e0b\uff0c\u6c14\u529b\u6e10\u6e10\u4e0d\u52a0\uff0c\u6b64\u6d88\u5f7c\u957f\uff0c\u767b\u65f6\u5c31\u7ed9\u674e\u601d\u5357\u62a2 \u4e86\u653b\u52bf\u3002 \u3000\u3000\u65c1\u4eba\u4e00\u65f6\u95f4\u5c1a\u672a\u770b\u5f97\u51fa\u6765\uff0c\u767d\u4e07\u96c4\u81ea\u5df1\u5df2\u662f\u5fc3\u4e2d\u660e\u767d\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u518d\u6218\u4e0b\u53bb\uff0c\u53ea\u6015\u5f53 \u771f\u8981\u5728\u4e00\u767e\u62db\u4e4b\u5185\u6298\u5728\u8fd9\u5c0f\u5b50\u7684\u624b\u91cc\u4e86\uff0c\u4f46\u6211\u5018\u82e5\u5728\u6b64\u65f6\u7f62\u624b\u6c42\u548c\uff0c\u5931\u4e86\u9762\u5b50\u5c1a\u5728\u5176\u6b21\uff0c\u7aa6 \u5b89\u5e73\u5982\u4f55\u80af\u653e\u8fc7\u6211\uff1f\u4ee5\u4ed6\u7684\u4e3a\u4eba\uff0c\u6211\u6709\u628a\u67c4\u634f\u5728\u4ed6\u7684\u624b\u91cc\uff0c\u4ed6\u5373\u4f7f\u662f\u660e\u77e5\u8dd1\u4e0d\u6389\u4e86\uff0c\u4e5f\u4e00\u5b9a \u4e0d\u80af\u8ba9\u6211\u72ec\u81ea\u9003\u751f\uff0c\u975e\u5f97\u62c9\u7740\u6211\u966a\u7740\u4ed6\u540c\u5f52\u4e8e\u5c3d\u4e0d\u53ef\uff01\u201d\u628a\u773c\u5077\u77a7\uff0c\u53ea\u89c1\u7aa6\u5b89\u5e73\u6b63\u5728\u7d27\u5f20\u4e07 \u72b6\u7684\u7ed9\u4ed6\u5450\u558a\u52a9\u5a01\u3002 \u3000\u3000\u6fc0\u6218\u4e2d\u767d\u4e07\u96c4\u5192\u9669\u8fdb\u62db\uff0c\u4e00\u8bb0\u201c\u7f9a\u7f8a\u6302\u89d2\u201d\uff0c\u5de6\u62f3\u51b2\u51fb\u4e0b\u5df4\uff0c\u53f3\u638c\u659c\u98de\uff0c\u5207\u524a\u5c0f\u81c2\uff0c\u8fd9 \u4e00\u62db\u4e24\u5f0f\uff0c\u6b63\u662f\u4ed6\u7ef5\u638c\u638c\u6cd5\u4e2d\u7684\u4e00\u62db\u4e24\u8d25\u4ff1\u4f24\u7684\u6740\u624b\u3002 \u3000\u3000\u674e\u601d\u5357\u5982\u4f55\u80fd\u8ba9\u4ed6\u8fbe\u6210\u4e24\u8d25\u4ff1\u4f24\u7684\u76ee\u7684\uff0c\u5f53\u4e0b\u4e00\u4e2a\u201c\u76d8\u9f99\u7ed5\u6b65\u201d\uff0c\u5728\u95f4\u4e0d\u5bb9\u53d1\u4e4b\u9645\u907f\u514d \u8fd8\u62db\uff0c\u53ea\u89c1\u4eba\u5f71\u7ffb\u817e\uff0c\u201c\u55e4\u201d\u7684\u4e00\u58f0\u54cd\uff0c\u674e\u601d\u5357\u7684\u4e0a\u8863\u7ed9\u4ed6\u6495\u53bb\u4e86\u4e00\u5e45\uff0c\u767d\u4e07\u96c4\u5374\u7ed9\u4ed6\u4ee5\u501f \u529b\u6253\u529b\u7684\u529f\u592b\uff0c\u4e00\u62db\u201c\u4e71\u4e91\u98de\u6e21\u201d\uff0c\u8f7b\u8f7b\u6258\u51fa\u4e86\u4e09\u6b65\u5f00\u5916\u3002 \u3000\u3000\u7aa6\u5b89\u5e73\u53eb\u9053\uff1a\u201c\u53ef\u60dc\uff01\u53ef\u60dc\uff01\u201d\u674e\u601d\u5357\u4e5f\u9053\uff1a\u201c\u53ef\u60dc\uff01\u53ef\u60dc\uff01\u201d\u891a\u4e91\u5cf0\u6b64\u65f6\u5df2\u770b\u51fa\u674e\u601d \u5357\u7a33\u64cd\u80dc\u5238\uff0c\u7b11\u9053\uff1a\u201c\u674e\u76df\u4e3b\uff0c\u4f60\u53c8\u66ff\u4ed6\u53ef\u60dc\u4ec0\u4e48\uff1f\u201d\u674e\u601d\u5357\u9053\uff1a\u201c\u53ef\u60dc\u4ed6\u51e0\u5341\u5e74\u7684\u4fee\u4e3a\uff0c \u5f97\u6765\u4e0d\u6613\uff0c\u5982\u4eca\u7adf\u56e0\u4e00\u5ff5\u4e4b\u5dee\uff0c\u66ff\u4e00\u4e2a\u901a\u756a\u5356\u56fd\u7684\u5978\u4eba\u966a\u846c\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u54ea\u91cc\u77e5\u9053\u767d\u4e07\u96c4\u4e0e\u7aa6\u5b89\u5e73\u4e43\u662f\u4e00\u4e18\u4e4b\u8c89\uff0c\u540c\u6837\u662f\u901a\u756a\u5356\u56fd\u7684\u597d\u4eba\uff0c\u4ed6\u8fd8\u60f3\u7ed9\u4ed6\u4e00 \u4e2a\u5f53\u5934\u68d2\u559d\uff0c\u2018\u70b9\u9192\u2019\u4ed6\u5462\u3002\u767d\u4e07\u96c4\u542c\u4e86\u6b64\u8a00\uff0c\u84e6\u5730\u5fc3\u5934\u4e00\u52a8\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u6709\u4e86\uff0c\u6709 \u4e86\uff01\u201d \u3000\u3000\u518d\u5ea6\u4ea4\u950b\uff0c\u767d\u4e07\u96c4\u4f5c\u51fa\u62fc\u547d\u7684\u6a21\u6837\uff0c\u5411\u524d\u731b\u6251\uff0c\u674e\u601d\u5357\u4ee5\u4e3a\u4ed6\u662f\u56f0\u517d\u72b9\u6597\uff0c\u4ea6\u662f\u4e0d\u6562\u8f7b \u654c\u3002\u7aa6\u5b89\u5e73\u5927\u4e3a\u6b22\u559c\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u8fd9\u8001\u5934\u513f\u5c45\u7136\u8fd8\u591f\u670b\u53cb\u3002\u201d\u4e0d\u6599\u5fc3\u5ff5\u672a\u5df2\uff0c\u767d\u4e07 \u96c4\u7a81\u7136\u4e00\u4e2a\u8f6c\u8eab\uff0c\u500f\u7684\u5c31\u5230\u4e86\u7aa6\u5b89\u5e73\u7684\u9762\u524d\uff0c\u547c\u7684\u4e00\u638c\u5c31\u5411\u4ed6\u5f53\u5934\u51fb\u4e0b\uff01 \u3000\u3000\u8fd9\u4e00\u4e2a\u7a81\u5982\u5176\u6765\u7684\u53d8\u5316\uff0c\u83ab\u8bf4\u7aa6\u5b89\u5e73\u662f\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\uff0c\u674e\u601d\u5357\u4ea6\u662f\u5927\u611f\u610f\u5916\u3002\u56e0\u4e3a\u4ed6\u6b63 \u5728\u6b65\u6b65\u4e3a\u8425\u7684\u9632\u5b88\uff0c\u767d\u4e07\u96c4\u7a81\u7136\u95f4\u8f6c\u79fb\u653b\u51fb\u7684\u76ee\u6807\uff0c\u4ed6\u81ea\u662f\u65e0\u6687\u8ffd\u51fb\u7684\u4e86\u3002 \u3000\u3000\u767d\u4e07\u96c4\u7684\u7ef5\u638c\u6709\u788e\u77f3\u5982\u7c89\u4e4b\u80fd\uff0c\u7aa6\u5b89\u5e73\u7ed9\u4ed6\u5f53\u5934\u4e00\u51fb\uff0c\u5982\u4f55\u8fd8\u80fd\u6d3b\u547d\uff0c\u5f53\u7136\u662f\u9a6c\u4e0a\u545c\u547c \u54c0\u54c9\u7684\u4e86\u3002 \u3000\u3000\u7247\u523b\u4e4b\u524d\uff0c\u767d\u4e07\u96c4\u8fd8\u662f\u53e3\u53e3\u58f0\u58f0\uff0c\u8bf4\u4ec0\u4e48\u201c\u4e3a\u670b\u53cb\u4e0d\u60dc\u4e24\u808b\u63d2\u5200\u201d\uff0c\u820d\u4e86\u6027\u547d\uff0c\u4e5f\u8981\u7ef4 \u62a4\u7aa6\u5b89\u5e73\u7684\u3002\u4f46\u5982\u4eca\u4ed6\u6beb\u53d1\u65e0\u4f24\uff0c\u5012\u662f\u7aa6\u5b89\u5e73\u7684\u6027\u547d\u7ed9\u4ed6\u4eb2\u624b\u7ed3\u675f\u4e86\u3002\u891a\u4e91\u5cf0\u51b7\u7b11\u9053\uff1a\u201c\u767d \u8001\u5148\u751f\uff0c\u600e\u7684\u4f60\u5ffd\u7136\u53c8\u4e0d\u8bb2\u6c5f\u6e56\u4e49\u6c14\u4e86\uff1f\u201d \u3000\u3000\u767d\u4e07\u96c4\u51fb\u6bd9\u4e86\u7aa6\u5b89\u5e73\uff0c\u56de\u8fc7\u5934\u6765\uff0c\u5bf9\u674e\u601d\u5357\u62b1\u62f3\u4e00\u62f1\uff0c\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u674e\u76df\u4e3b\u91d1\u7389\u826f\u8a00\uff0c \u767d\u67d0\u4e0d\u80dc\u60ed\u6127\uff01\u201d\u674e\u601d\u5357\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u4f60\u9192\u609f\u5f97\u8fd9\u6837\u5feb\uff0c\u5012\u662f\u6709\u70b9\u6167\u6839\u5440\uff01\u201d\u53e3\u6c14\u663e\u7136\u662f\u5bf9 \u767d\u4e07\u96c4\u6709\u6240\u6000\u7591\uff0c\u4e0d\u6562\u76f8\u4fe1\u3002 \u3000\u3000\u8c37\u6db5\u865a\u7b11\u9053\uff1a\u201c\u4ec0\u4e48\u6167\u6839\uff0c\u5206\u660e\u4ed6\u662f\u6253\u4e0d\u8fc7\u4f60\uff0c\u903c\u5f97\u51fa\u6b64\u4e0b\u7b56\uff0c\u671b\u4f60\u9976\u4ed6\u3002\u201d\u8c37\u6db5\u865a\u8fd9 \u6837\u63a8\u6d4b\uff0c\u81ea\u4ee5\u4e3a\u662f\u770b\u7a7f\u4e86\u767d\u4e07\u96c4\u7684\u5fc3\u80a0\uff0c\u5374\u600e\u77e5\u8fd8\u662f\u5931\u4e4b\u5fe0\u539a\uff0c\u767d\u4e07\u96c4\u6740\u4eba\u7684\u52a8\u673a\u6bd4\u4ed6\u6240\u63a8 \u6d4b\u7684\u5c1a\u8981\u6076\u6bd2\u5f97\u591a\uff01\u4e0d\u8fc7\u573a\u4e2d\u4e5f\u5e76\u975e\u6ca1\u6709\u660e\u773c\u4e4b\u4eba\uff0c\u8001\u4e8e\u4e16\u6545\u7684\u5b5f\u5c11\u521a\u5c31\u5df2\u9690\u9690\u8d77\u4e86\u731c\u7591\uff0c \u6697\u81ea\u9053\uff1a\u201c\u5373\u4f7f\u767d\u4e07\u96c4\u771f\u7684\u9192\u609f\uff0c\u4ed6\u4e5f\u7528\u4e0d\u7740\u4eb2\u624b\u53bb\u6740\u7aa6\u5b89\u5e73\u5440\u3002\u201d\u5f53\u4e0b\u51b7\u773c\u65c1\u89c2\uff0c\u770b\u767d\u4e07 \u96c4\u5982\u4f55\u5206\u8fa9\u3002 \u3000\u3000\u767d\u4e07\u96c4\u6ee1\u9762\u901a\u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u8fc7\u767d\u67d0\u5176\u5b9e\u4e5f\u5e76\u975e\u771f\u7684\u60f3\u4e3a\u8fd9\u53ae\u5356\u547d\uff0c\u8fd9\u53ea\u662f\u5047\u620f\u771f\u505a\u800c \u5df2\u3002\u201d\u674e\u601d\u5357\u9053\uff1a\u201c\u4f55\u8c13\u5047\u620f\u771f\u505a\uff0c\u8bf7\u9053\u5176\u8be6\u3002\u201d \u3000\u3000\u767d\u4e07\u96c4\u542b\u7b11\u8bf4\u9053\uff1a\u201c\u8bf4\u51fa\u6765\u76df\u4e3b\u8bf7\u4f60\u53ef\u522b\u89c1\u602a\uff0c\u6211\u662f\u60f3\u8bd5\u8bd5\u76df\u4e3b\u7684\u6b66\u529f\u3002\u201d\u674e\u601d\u5357\u8be7 \u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u4f60\u53ea\u662f\u60f3\u8bd5\u8bd5\u6211\u7684\u6b66\u529f\u5417\uff1f\u201d\u5fc3\u60f3\uff1a\u201c\u4ed6\u521a\u624d\u5206\u660e\u4e43\u662f\u6027\u547d\u76f8\u6251\uff0c\u54ea\u50cf\u4ed6\u73b0 \u5728\u8bf4\u7684\u8fd9\u6837\u8f7b\u677e\uff1f\u201d \u3000\u3000\u767d\u4e07\u96c4\u88c5\u51fa\u6fc0\u6602\u6177\u6168\u4e4b\u72b6\u8bf4\u9053\uff1a\u201c\u767d\u67d0\u867d\u7136\u5e74\u8001\u7cca\u6d82\uff0c\u5c1a\u4e0d\u81f3\u4e8e\u4e0d\u660e\u5927\u4e49\u3002\u7aa6\u5b89\u5e73\u79c1\u901a \u9791\u5b50\uff0c\u7518\u4f5c\u722a\u7259\uff0c\u767d\u67d0\u5c82\u80fd\u548c\u8fd9\u6837\u7684\u4eba\u8bb2\u751a\u6c5f\u6e56\u9053\u4e49\uff0c\u521a\u624d\u6211\u672c\u60f3\u4e00\u8d70\u4e86\u4e4b\u7684\uff0c\u4f46\u540e\u6765\u5ffd\u7136 \u60f3\u5230\u8d81\u8fd9\u4e2a\u673a\u4f1a\uff0c\u9886\u6559\u9886\u6559\u76df\u4e3b\u7684\u6b66\u529f\u4e5f\u597d\u3002\u6211\u77e5\u9053\u5b5f\u5927\u4fa0\u5b9a\u7136\u4e0d\u5c51\u4eb2\u81ea\u4e0b\u573a\uff0c\u591a\u534a\u662f\u76df\u4e3b \u8d50\u6559\u4e8e\u6211\u7684\uff0c\u5b9e\u4e0d\u76f8\u7792\uff0c\u6211\u89c1\u76df\u4e3b\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u5c31\u5f97\u5230\u4e86\u7eff\u6797\u540c\u9053\u7684\u62e5\u6234\u3001\u6211\u59d4\u5b9e\u662f\u6709\u70b9\u513f\u4e0d \u670d\u6c14\u5462\u3002\u662f\u4ee5\u6211\u88c5\u51fa\u4e3a\u8fd9\u53ae\u5356\u547d\u624d\u80fd\u903c\u51fa\u76df\u4e3b\u7684\u8d85\u5353\u6b66\u529f\uff0c\u4e00\u8bd5\u4e4b\u4e0b\uff0c\u624d\u77e5\u76df\u4e3b\u679c\u7136\u662f\u540d\u4e0d \u865a\u4f20\uff0c\u767d\u67d0\u5982\u4eca\u662f\u5fc3\u670d\u53e3\u670d\u4e86\uff01\u201d \u3000\u3000\u767d\u4e07\u96c4\u7684\u8bdd\u5b9e\u662f\u96be\u4ee5\u81ea\u5706\u5176\u8bf4\uff0c\u4e0d\u8fc7\u674e\u601d\u5357\u4ee5\u5fe0\u539a\u5f85\u4eba\u3002\u56e0\u6b64\u662f\u8fd9\u6837\u60f3\u9053\uff0c\u201c\u4e0d\u7ba1\u4ed6\u8bf4 \u7684\u662f\u771f\u662f\u5047\uff0c\u662f\u4e3a\u52bf\u6240\u903c\u8fd8\u662f\u53ca\u65f6\u6094\u609f\uff0c\u4ed6\u65e2\u7136\u73b0\u5728\u4e0d\u662f\u4e3a\u864e\u4f5c\u6005\uff0c\u6211\u53c8\u4f55\u59a8\u7f51\u5f00\u4e00\u9762\uff0c\u8ba9 \u4ed6\u81ea\u65b0\u3002\u201d\u4e8e\u662f\u8bf4\u9053\uff1a\u201c\u90a3\u4e48\u767d\u8001\u82f1\u96c4\u662f\u613f\u610f\u7559\u5728\u8fd9\u513f\u52a9\u6211\u4eec\u540c\u6297\u9791\u5b50\uff0c\u8fd8\u662f\u8981\u56de\u53bb\u5462\uff1f\u201d \u3000\u3000\u767d\u4e07\u96c4\u9053\uff1a\u201c\u76df\u4e3b\u624b\u4e0b\u4eba\u6750\u6d4e\u6d4e\uff0c\u767d\u67d0\u8001\u673d\u65e0\u80fd\uff0c\u65e9\u5df2\u91d1\u76c6\u6d17\u624b\uff0c\u4e0d\u60f3\u91cd\u8d70\u6c5f\u6e56\u4e86\u3002\u8bf7 \u76df\u4e3b\u8bb8\u6211\u8fd8\u5bb6\u517b\u8001\uff0c\u5f52\u9690\u6797\u6cc9\u3002\u76df\u4e3b\u82e5\u7136\u9700\u8981\u7528\u4eba\uff0c\u5c0f\u513f\u5e72\u80dc\uff0c\u5012\u6700\u53ef\u4ee5\u4e3a\u76df\u4e3b\u6267\u97ad\u968f\u956b\uff0c \u5f85\u767d\u67d0\u56de\u5bb6\u4e4b\u540e\uff0c\u81ea\u5f53\u53eb\u4ed6\u524d\u6765\u6548\u529b\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u84d3\uff1a\u201c\u597d\u5427\uff0c\u4f60\u8981\u56de\u53bb\u4fbf\u56de\u53bb\u5427\u3002\u4ee4\u90ce\u4e4b\u4e8b\uff0c\u5f85\u4ed6\u6765\u4e86\u518d\u8bf4\u3002\u6211\u8fd9\u91cc\u56fa\u7136\u9700\u8981 \u4eba\uff0c\u4f46\u4e5f\u7edd\u4e0d\u52c9\u5f3a\u522b\u4eba\u7684\u3002\u201d \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u5728\u6577\u4e0a\u91d1\u521b\u836f\u4e4b\u540e\uff0c\u6d41\u8840\u5df2\u6b62\uff0c\u7cbe\u795e\u597d\u4e86\u4e00\u4e9b\uff0c\u6b64\u65f6\u770b\u89c1\u674e\u601d\u5357\u653e\u8d70\u767d\u4e07\u96c4\uff0c \u5fcd\u4e0d\u7740\u53eb\u9053\uff1a\u201c\u7aa6\u5b89\u5e73\u56fa\u7136\u662f\u7f6a\u8be5\u4e07\u6b7b\uff0c\u4f46\u4f60\u8fd9\u8001\u5339\u592b\u5374\u4e0d\u914d\u6740\u4ed6\u3002\u674e\u76df\u4e3b\uff0c\u8fd9\u8001\u5bb6\u4f19\u53e3\u871c \u8179\u5251\uff0c\u7edd\u4e0d\u662f\u4e00\u4e2a\u597d\u4eba\uff0c\u4f60\u600e\u7684\u5c06\u4ed6\u653e\u4e86\uff1f\u201d \u3000\u3000\u767d\u4e07\u96c4\u9053\uff1a\u201c\u4f60\u6709\u4f55\u8bc1\u636e\u8bf4\u6211\u53e3\u871c\u8179\u5251\uff1f\u54fc\uff0c\u54fc\uff0c\u6b32\u52a0\u4ee5\u7f6a\uff0c\u4f55\u60a3\u65e0\u8f9e\u4e86\uff1f\u6050\u6015\u4f60\u662f\u6068 \u6211\u6740\u4f60\u7ed3\u62dc\u5144\u5f1f\uff0c\u8fd9\u624d\u542b\u8840\u55b7\u4eba\u5427\uff1f\u201d \u3000\u3000\u9ed1\u77f3\u9053\u4eba\u5927\u6012\u9053\uff1a\u201c\u4f60\u8fd9\u624d\u662f\u542b\u8840\u55b7\u4eba\uff0c\u7aa6\u5b89\u5e73\u6211\u4e5f\u8981\u6740\u4ed6\uff0c\u8fd9\u662f\u6709\u76ee\u5171\u7779\u7684\uff01\u4f60\u5012\u6253 \u4e00\u628a\uff0c\u662f\u4f55\u5c45\u5fc3\uff1f\u201d\u4f46\u4ed6\u8d23\u9a82\u767d\u4e07\u96c4\uff0c\u5374\u4e5f\u8bf4\u4e0d\u51fa\u4ed6\u548c\u7aa6\u5b89\u5e73\u662f\u4e00\u4e18\u4e4b\u8c89\u7684\u8bc1\u636e\u3002 \u3000\u3000\u674e\u601d\u5357\u529d\u89e3\u9053\uff1a\u201c\u53ea\u8981\u4e00\u4e2a\u4eba\u6709\u5411\u5584\u4e4b\u5fc3\uff0c\u6211\u4eec\u53c8\u4f55\u59a8\u4ece\u8f7b\u53d1\u843d\uff1f\u767d\u8001\u5148\u751f\uff0c\u6211\u4e0e\u4f60\u7d20 \u5473\u5e73\u751f\uff0c\u4e5f\u4e0d\u77e5\u9053\u4f60\u7684\u4e3a\u4eba\uff0c\u9ed1\u77f3\u9053\u957f\u8bf4\u7684\u8bdd\uff0c\u4f46\u613f\u4f60\u6709\u5219\u6539\u4e4b\uff0c\u65e0\u5219\u52a0\u52c9\u3002\u597d\uff0c\u4f60\u53bb \u5427\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u662f\u76df\u4e3b\u7684\u8eab\u4efd\uff0c\u4ed6\u65e2\u7136\u5982\u6b64\u8bf4\u4e86\uff0c\u9ed1\u77f3\u9053\u4eba\u867d\u7136\u4f59\u6012\u672a\u606f\uff0c\u4e5f\u53ea\u597d\u8ba9\u4ed6\u8d70\u4e86\u3002 \u3000\u3000\u5b5f\u5c11\u521a\u672c\u6765\u4e5f\u662f\u4e0d\u613f\u610f\u653e\u8d70\u767d\u4e07\u96c4\u7684\uff0c\u4f46\u4e00\u6765\u4e3a\u4e86\u5c0a\u91cd\u674e\u601d\u5357\uff0c\u4e8c\u6765\u4ed6\u4e5f\u53e6\u6709\u6253\u7b97\uff0c\u6697 \u81ea\u601d\u91cf\uff1a\u201c\u767d\u4e07\u96c4\u8fd9\u6b21\u7a81\u7136\u6740\u4e86\u7aa6\u5b89\u5e73\uff0c\u5185\u4e2d\u4e00\u5b9a\u6709\u4e0d\u53ef\u544a\u4eba\u7684\u79d8\u5bc6\uff0c\u7edd\u4e0d\u6b62\u4e8e\u53ea\u662f\u60f3\u4fdd\u547d \u8d4e\u7f6a\u8fd9\u6837\u7b80\u5355\uff0c\u653e\u4ed6\u56de\u53bb\u4e5f\u597d\uff0c\u8ba9\u4ed6\u4e0d\u52a0\u6212\u5907\uff0c\u6211\u4eec\u624d\u597d\u4fa6\u5bdf\u4ed6\u7684\u52a8\u9759\uff0c\u8bf4\u4e0d\u5b9a\u6536\u83b7\u66f4 \u5927\u3002\u201d \u3000\u3000\u767d\u4e07\u96c4\u8d70\u4e86\u4e4b\u540e\uff0c\u674e\u601d\u5357\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u7684\u5be8\u4e3b\u901a\u654c\u6709\u636e\uff0c\u5982\u4eca\u5df2\u662f\u7f6a\u6709\u5e94\u5f97\u81ea\u53d6\u706d \u4ea1\u4e86\u3002\u4f60\u4eec\u6216\u8005\u53d7\u4ed6\u8499\u9a97\uff0c\u6216\u8005\u662f\u53d7\u4ed6\u5a01\u80c1\uff0c\u4e0d\u6562\u53cd\u4ed6\uff0c\u4f46\u9996\u6076\u5df2\u6b7b\uff0c\u4e5f\u5c31\u4e0d\u5fc5\u4e00\u4e00\u8ffd\u7a76 \u4e86\u3002\u73b0\u5728\u6211\u53ea\u60f3\u95ee\u95ee\u4f60\u4eec\uff0c\u613f\u4e0d\u613f\u610f\u4e0e\u6211\u4eec\u7ed3\u76df\uff0c\u540c\u6297\u9791\u5b50\uff1f\u201d \u3000\u3000\u98de\u9f99\u5c71\u7684\u5927\u5c0f\u5934\u76ee\u6b63\u81ea\u63d0\u5fc3\u540a\u80c6\uff0c\u4e0d\u77e5\u674e\u601d\u5357\u5982\u4f55\u53d1\u843d\u4ed6\u4eec\uff0c\u542c\u5f97\u674e\u601d\u5357\u8fd9\u4e48\u4e00\u8bf4\uff0c\u81ea \u662f\u6b22\u58f0\u96f7\u52a8\u3002\u4e09\u5be8\u4e3b\u6c5f\u5fc3\u77f3\u7ad9\u4e86\u51fa\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u98de\u9f99\u5c71\u5408\u5be8\u5f1f\u5144\uff0c\u591a\u8499\u76df\u4e3b\u4e0d\u5f03\uff0c\u613f\u542c\u76df\u4e3b \u53f7\u4ee4\uff01\u201d\u539f\u6765\u4e8c\u5be8\u4e3b\u7f57\u4fca\u7ed9\u8c37\u6db5\u865a\u6454\u4e0b\u77f3\u9636\uff0c\u4e1a\u5df2\u4f24\u91cd\u6b7b\u4e86\uff01\u6545\u6b64\u4e09\u5be8\u4e3b\u6c5f\u5fc3\u77f3\u987a\u7406\u6210\u7ae0\u5730 \u505a\u4e86\u98de\u9f99\u5c71\u7684\u5be8\u4e3b\uff0c\u4ee3\u8868\u4e00\u4f17\u5f1f\u5144\u8bf4\u8bdd\uff0c \u3000\u3000\u5927\u4e8b\u5df2\u5b9a\uff0c\u6c5f\u5fc3\u77f3\u53bb\u7b79\u529e\u5e86\u529f\u5bb4\uff0c\u8c37\u6db5\u865a\u3001\u891a\u4e91\u5cf0\u3001\u4e25\u70f7\u7b49\u4eba\u8fd9\u624d\u6709\u7a7a\u6687\u6765\u548c\u5b5f\u5c11\u521a\u4e0e \u674e\u601d\u5357\u53d9\u8bdd\uff0c\u5404\u6709\u5404\u7684\u906d\u9047\u8981\u8bf4\uff0c\u4e5f\u5c31\u4e0d\u5fc5\u4e00\u4e00\u7ec6\u8868\u4e86\u3002\u6b63\u662f\uff1a \u3000\u3000\u864e\u7a74\u8361\u5e73\u64d2\u864e\u5b50\uff0c\u5e03\u65b0\u9664\u65e7\u5c3d\u6b22\u989c \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b\u2014\u2014\u7b2c\u56db\u5341\u4e8c\u56de\u3000\u6177\u6168\u91ca\u4fd8\u591a\u4e49\u91cd\u3000\u51c4\u6006\u5386\u52ab\u500d\u60c5\u575a \u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b \u7b2c\u56db\u5341\u4e8c\u56de\u3000\u6177\u6168\u91ca\u4fd8\u591a\u4e49\u91cd\u3000\u51c4\u6006\u5386\u52ab\u500d\u60c5\u575a \u3000\u3000\u6b64\u65f6\u6768\u5a49\u5df2\u7ecf\u62b9\u53bb\u4e86\u8138\u4e0a\u7684\u5316\u88c5\uff0c\u6062\u590d\u672c\u6765\u9762\u76ee\u3002\u5b5f\u5c11\u521a\u65e9\u5df2\u77e5\u9053\u5979\u662f\u674e\u601d\u5357\u7684\u672a\u5a5a \u59bb\uff0c\u53c8\u89c1\u81ea\u5df1\u7684\u5973\u513f\u548c\u891a\u4e91\u5cf0\u624b\u62c9\u7740\u624b\u7684\u7ad9\u5728\u4e00\u8d77\uff0c\u5341\u5206\u4eb2\u70ed\uff0c\u5b5f\u5c11\u521a\u4e0d\u7531\u5f97\u6697\u7b11\u81ea\u5df1\u7684\u7cca \u6d82\u3002\u8981\u77e5\u9053\u4ed6\u662f\u4ee5\u4e3a\u6768\u5a49\u5df2\u6b7b\uff0c\u624d\u5c5e\u610f\u674e\u601d\u5357\u505a\u4ed6\u5973\u5a7f\u7684\uff0c\u5982\u4eca\u6768\u5a49\u8fd8\u6d3b\u5728\u4eba\u95f4\uff0c\u4ed6\u5f53\u7136\u662f \u4e0d\u4f1a\u518d\u4f5c\u6b64\u60f3\u4e86\u3002 \u3000\u3000\u5b5f\u5c11\u521a\u6697\u81ea\u60f3\u9053\uff1a\u201c\u674e\u601d\u5357\u56fa\u7136\u662f\u4e0d\u53ef\u591a\u5f97\u7684\u5c11\u5e74\u82f1\u96c4\uff0c\u891a\u4e91\u5cf0\u4e5f\u662f\u540e\u4e00\u8f88\u4e2d\u7684\u4fca\u6770\uff0c \u4ed6\u53c8\u662f\u6211\u8001\u670b\u53cb\u7684\u5f1f\u5b50\uff0c\u971e\u513f\u5f97\u4ee5\u914d\u4ed6\uff0c\u6211\u4e5f\u8be5\u5fc3\u6ee1\u610f\u8db3\u4e86\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u7b11\u9053\uff1a\u201c\u7239\u7239\uff0c\u4e25\u8868\u59d0\u7684\u4e8b\u60c5\u53ef\u8fd8\u8981\u4f60\u7ed9\u4ed6\u5e2e\u5fd9\u5462\u3002\u201d\u5b5f\u5c11\u521a\u89c1\u4e25\u70f7\u548c\u8c37\u6db5\u865a\u4e5f \u662f\u6210\u53cc\u6210\u5bf9\uff0c\u66f4\u662f\u5fc3\u82b1\u6012\u653e\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u653e\u5fc3\uff0c\u6211\u66ff\u4f60\u4eec\u505a\u5a92\u4fbf\u662f\u3002\u201d\u4e25\u70f7\u7684\u6bcd\u4eb2\u662f\u5b5f\u5c11 \u521a\u7684\u59d0\u59d0\uff0c\u5f97\u4ed6\u7b54\u5141\u7389\u6210\uff0c\u5a5a\u4e8b\u81ea\u662f\u4e0d\u6101\u518d\u6709\u963b\u788d\u4e86\u3002\u5f53\u4e0b\u6ee1\u9762\u901a\u7ea2\u7684\u4f4e\u4e86\u5934\uff0c\u8f7b\u58f0\u8bf4\u9053\uff1a \u201c\u591a\u8c22\u8205\u8205\u3002\u201d \u3000\u3000\u5b5f\u5c11\u521a\u54c8\u54c8\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4f46\u613f\u4f60\u4eec\u6709\u60c5\u4eba\u7686\u6210\u7737\u5c5e\uff0c\u6211\u4e5f\u66ff\u4f60\u4eec\u6b22\u559c\u3002\u5982\u4eca\u6211\u4eec\u8be5\u8c08 \u4e00\u8c08\u6b63\u4e8b\u4e86\uff0c\u601d\u5357\uff0c\u90a3\u4e24\u4e2a\u8499\u53e4\u6b66\u58eb\u4f60\u8fd8\u672a\u66fe\u53d1\u843d\u5462\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u4eba\u73b0\u5728\u5df2\u7ecf\u53d8\u6210\u4e86\u6211\u4eec\u7684\u4fd8\u864f\uff0c\u6211\u4eec\u4e5f\u8be5\u597d\u597d\u5f85\u4ed6\u3002\u201d\u5f53\u4e0b\u8bf7\u6c5f\u5fc3 \u77f3\u62d4\u51fa\u4e00\u95f4\u9759\u5ba4\u5b89\u7f6e\u8fd9\u4e24\u4e2a\u6b66\u58eb\uff0c\u5e76\u4e14\u62ff\u51fa\u4e86\u4e0a\u597d\u7684\u91d1\u521b\u836f\uff0c\u7ed9\u4ed6\u4eec\u6577\u4f24\u3002 \u3000\u3000\u6c5f\u5fc3\u77f3\u628a\u4e24\u4e2a\u6b66\u58eb\u5e26\u8d70\u4e4b\u540e\uff0c\u6768\u5a49\u67f3\u7709\u5fae\u76b1\uff0c\u5fcd\u4e0d\u4f4f\u8bf4\u9053\uff1a\u201c\u5357\u54e5\uff0c\u4f60\u4e0d\u6740\u4ed6\u4eec\u72b9\u81ea\u7f62 \u4e86\uff0c\u4e3a\u4f55\u5982\u6b64\u4f18\u5f85\u4ed6\u4eec\uff1f\u6211\u4eec\u5728\u8499\u53e4\u4e4b\u65f6\uff0c\u53d7\u7684\u82e6\u53d7\u5f97\u8fd8\u672a\u591f\u4e48\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u7b11\u9053\uff1a\u201c\u6b63\u662f\u56e0\u4e3a\u6211\u5403\u591f\u4e86\u9791\u5b50\u7684\u82e6\u5934\uff0c\u6240\u4ee5\u624d\u4e0d\u613f\u610f\u5728\u4ed6\u4eec\u7684\u8eab\u4e0a\u62a5\u590d\u3002\u201d\u6768 \u5a49\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u674e\u601d\u5357\u9053\uff1a\u201c\u4f60\u662f\u8bfb\u4e66\u660e\u7406\u7684\u4eba\uff0c\u5f53\u7136\u77e5\u9053\u2018\u5df1\u6240\u4e0d\u6b32\uff0c\u52ff\u65bd\u4e8e\u4eba\u2019\u8fd9 \u53e5\u8bdd\u3002\u201d\u6768\u5a49\u9053\uff1a\u201c\u53ef\u662f\u4ed6\u4eec\u4e43\u662f\u6211\u4eec\u7684\u654c\u4eba\u554a\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u653e\u4e0b\u4e86\u6b66\u5668\uff0c\u6b3e\u662f\u4fd8\u864f\u800c\u4e0d\u662f\u654c\u4eba\u4e86\u3002\u4fd8\u864f\u548c\u6b63\u5728\u62ff\u7740\u5200\u67aa\u6740\u6211\u4eec\u7684\u654c\u4eba \u662f\u6709\u533a\u522b\u7684\uff0c\u662f\u4e48\uff1f\u4f55\u51b5\u5728\u8499\u53e4\u4e4b\u65f6\u5bb3\u5f97\u6211\u4eec\u5bb6\u7834\u4eba\u4ea1\u7684\u5143\u51f6\u7978\u9996\u4ea6\u662f\u53e6\u6709\u5176\u4eba\uff0c\u8fd9\u7b14\u5e10\u4e0d \u8be5\u7b97\u5728\u4ed6\u4eec\u8eab\u4e0a\u3002\u201d\u6768\u5a49\u6c14\u5e73\u4e86\u4e9b\uff0c\u7b11\u9053\uff1a\u201c\u8bdd\u662f\u8fd9\u6837\u8bf4\uff0c\u6211\u7684\u5fc3\u91cc\u603b\u8fd8\u662f\u6709\u6c14\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u6211\u4eec\u4ee5\u8bda\u5f85\u4eba\uff0c\u8bf4\u4e0d\u5b9a\u8fd8\u53ef\u4ee5\u5316\u654c\u4e3a\u53cb\u5462\uff0c\u5373\u4f7f\u4e0d\u6210\uff0c\u4e5f\u603b\u662f\u6709\u597d\u5904\u6ca1\u6709 \u574f\u5904\u3002\u201d\u6768\u5a49\u9053\uff1a\u201c\u6709\u4ec0\u4e48\u597d\u5904\uff1f\u201d\u674e\u601d\u5357\u9053\uff1a\u201c\u77e5\u5df1\u77e5\u5f7c\uff0c\u767e\u6218\u767e\u80dc\uff0c\u6211\u4eec\u4e5f\u5e94\u8be5\u77e5\u9053\u4e00 \u70b9\u654c\u60c5\u554a\u3002\u5018\u82e5\u5c06\u4ed6\u51cc\u8fb1\uff0c\u4ed6\u8fd8\u4f1a\u548c\u6211\u4eec\u8bf4\u771f\u8bdd\u4e48\uff1f\u201d \u3000\u3000\u6768\u5a49\u970d\u7136\u4e00\u7701\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4ece\u4ed6\u4eec\u7684\u53e3\u4e2d\u6216\u8bb8\u53ef\u4ee5\u63a2\u542c\u6211\u4eec\u4ec7\u4eba\u7684\u6d88\u606f\u3002\u201d\u674e\u601d\u5357 \u9053\uff1a\u201c\u4e0d\u8fc7\u8fd9\u53ef\u5e76\u975e\u4f7f\u7528\u6743\u672f\uff0c\u5373\u4f7f\u4ed6\u4eec\u4ec0\u4e48\u90fd\u4e0d\u8bf4\uff0c\u6211\u4eec\u4e5f\u8fd8\u662f\u8981\u8fd9\u6837\u5bf9\u5f85\u4fd8\u864f\u7684\u3002\u201d\u7fa4 \u8c6a\u521d\u65f6\u4e5f\u662f\u50cf\u6768\u5a49\u4e00\u6837\u4e0d\u5927\u5fc3\u670d\uff0c\u5f85\u5230\u542c\u674e\u601d\u5357\u8bf4\u6e05\u695a\u4e86\u9053\u7406\uff0c\u8fd9\u624d\u6697\u81ea\u5fc3\u6298\uff0c\u89c9\u5f97\u674e\u601d\u5357 \u89c1\u8bc6\u8fc7\u4eba\uff0c\u4e0d\u6127\u4f5c\u4ed6\u4eec\u7684\u76df\u4e3b\u3002 \u3000\u3000\u5e86\u529f\u5bb4\u8fc7\u540e\uff0c\u674e\u601d\u5357\u72ec\u81ea\u8fdb\u5165\u9759\u5ba4\u63a2\u671b\u90a3\u4e24\u4e2a\u8499\u53e4\u6b66\u58eb\u3002 \u3000\u3000\u90a3\u4e24\u4e2a\u6b66\u58eb\u6577\u4e0a\u91d1\u521b\u836f\u8840\u5df2\u6b62\u4e86\uff0c\u5e86\u529f\u5bb4\u7684\u9152\u83dc\u674e\u601d\u5357\u5730\u7279\u5730\u52a0\u4eba\u7ed9\u4ed6\u4eec\u9001\u4e86\u4e00\u4efd\uff0c\u8fd9 \u4e24\u4e2a\u6b66\u58eb\u4f53\u9b44\u672c\u6765\u5065\u58ee\uff0c\u5403\u9971\u4e86\u809a\u5b50\uff0c\u7cbe\u795e\u4f53\u529b\u90fd\u5df2\u6e10\u6e10\u6062\u590d\uff0c\u5fc3\u91cc\u6b63\u5728\u7eb3\u95f7\u674e\u601d\u5357\u4e3a\u4ec0\u4e48 \u8fd9\u6837\u5f85\u4ed6\u4eec\uff1f \u3000\u3000\u53ef\u662f\u4ed6\u4eec\u4ece\u5c0f\u53d7\u7684\u5c31\u662f\u4e00\u5957\u8499\u53e4\u6b66\u58eb\u7684\u4e25\u683c\u8bad\u7ec3\uff0c\u5fe0\u4e8e\u5927\u6c57\u8fd9\u4e00\u89c2\u5ff5\u5bf9\u4ed6\u4eec\u6765\u8bf4\u4e0d\u557b\u662f \u5929\u7ecf\u5730\u4e49\u3002\u662f\u4ee5\u5c3d\u7ba1\u4ed6\u4eec\u5fc3\u91cc\u4e0d\u65e0\u5bf9\u674e\u601d\u5357\u611f\u6fc0\u4e4b\u610f\uff0c\u4f46\u4e00\u89c1\u674e\u601d\u5357\u8fdb\u6765\uff0c\u4ecd\u7136\u662f\u677f\u8d77\u4e86\u9762 \u5b54\uff0c\u4f5c\u51fa\u4e00\u526f\u4e0d\u80af\u4f4e\u5934\u7684\u795e\u6c14\u3002 \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u4f60\u4eec\u597d\u4e86\u4e9b\u5417\uff0c\u53ef\u4ee5\u8d70\u52a8\u4e86\u5427\uff1f\u201d \u3000\u3000\u4e00\u4e2a\u6b66\u58eb\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u95ee\u8fd9\u4e2a\u5e72\u561b\uff1f\u201d\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6211\u8d70\u5f97\u52a8\u53c8\u600e\u4e48\u6837\uff0c\u96be\u9053\u4f60\u8fd8\u4f1a \u5c06\u6211\u653e\u4e86\uff1f\u201d \u3000\u3000\u53e6\u4e00\u4e2a\u6b66\u5927\u66f4\u662f\u51fa\u8a00\u4e0d\u900a\uff0c\u50b2\u7136\u8bf4\u9053\uff1a\u201c\u674e\u601d\u5357\u4f60\u800d\u4ec0\u4e48\u82b1\u62db\uff1f\u6211\u4eec\u8499\u5de6\u6b66\u58eb\u90fd\u662f\u94c1 \u6c49\uff0c\u843d\u5728\u4f60\u7684\u624b\u91cc\uff0c\u4f60\u8981\u6740\u4fbf\u6740\uff0c\u5265\u76ae\u62c6\u9aa8\uff0c\u6211\u4eec\u51b3\u4e0d\u76b1\u7709\uff01\u4f60\u8981\u6211\u4eec\u5411\u4f60\u5c48\u670d\uff0c\u5374\u662f\u4f11 \u60f3\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u7b11\u9053\uff1a\u201c\u6211\u4e0e\u4f60\u4eec\u65e0\u514d\u65e0\u4ec7\uff0c\u4e3a\u4f55\u8981\u6740\u4f60\u4eec\uff1f\u6211\u662f\u4eba\uff0c\u4f60\u4eec\u4e5f\u662f\u4eba\uff0c\u867d\u7136\u600e\u6837\u505a \u4eba\u4f60\u6211\u5e76\u4e0d\u4e00\u6837\uff0c\u4f46\u5927\u5bb6\u4e5f\u90fd\u662f\u4e00\u6837\u5e73\u7b49\u7684\u4eba\u554a\uff0c\u597d\u7aef\u7aef\u7684\u6211\u4e3a\u4ec0\u4e48\u53c8\u8981\u4f60\u4eec\u5c48\u8fb1\uff1f\u201d \u3000\u3000\u8fd9\u4e24\u4e2a\u6b66\u4e0a\u51e0\u66fe\u542c\u8fc7\u8fd9\u6837\u7684\u9053\u7406\uff0c\u5fc3\u91cc\u5140\u662f\u534a\u4fe1\u534a\u7591\uff0c\u8bf4\u9053\uff1a\u201c\u53ef\u662f\u6211\u4eec\u6765\u5230\u98de\u9f99\u5c71\uff0c \u6b63\u662f\u4e3a\u4e86\u5bf9\u4ed8\u4f60\u7684\u554a\uff0c\u96be\u9053\u4f60\u4e0d\u77e5\u9053\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u6211\u5f53\u7136\u77e5\u9053\u3002\u4f46\u8fd9\u4ef6\u4e8b\u5df2\u7ecf\u8fc7\u53bb\u4e86\uff0c\u73b0\u5728\u4f60\u4eec\u53ef\u5e76\u6ca1\u6709\u2018\u5bf9\u4ed8\u2019\u6211\u554a\u3002\u201d \u3000\u3000\u90a3\u4e24\u4e2a\u6b66\u58eb\u9053\uff1a\u201c\u597d\uff0c\u5c31\u7b97\u4f60\u4e0d\u8ba1\u4ec7\u6068\uff0c\u4f46\u5374\u53c8\u4f55\u5fc5\u8fd9\u6837\u6b3e\u5f85\u6211\u4eec\uff1f\u201d\u5fc3\u91cc\u8fd8\u5728\u6000\u7591\u674e \u6069\u5357\u522b\u6709\u7528\u5fc3\uff0c\u65b9\u77e5\u8981\u5982\u4f55\u6298\u78e8\u4ed6\u4eec\u3002 \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u56e0\u4e3a\u4f60\u4eec\u73b0\u5728\u5df2\u7ecf\u4e0d\u662f\u6211\u7684\u654c\u4eba\uff0c\u6211\u662f\u5c06\u4f60\u4eec\u5f53\u4f5c\u670b\u53cb\u6b3e\u5f85\u7684\u3002\u201d\u63a5\u7740\u8bf4 \u9053\uff1a\u201c\u4f55\u51b5\u6211\u4eec\u8fd8\u662f\u670b\u53cb\u5462\u3002\u6211\u4eec\u66fe\u7ecf\u5728\u80af\u7279\u5c71\u4e0a\u540c\u6253\u8fc7\u730e\u7684\uff0c\u662f\u4e48\uff1f\u201d\u90a3\u4e24\u4e2a\u6b66\u571f\u9053\uff1a \u201c\u4f60\u7684\u8bb0\u6027\u5012\u771f\u4e0d\u9519\uff0c\u4f46\u6211\u4eec\u53ef\u662f\u4e0d\u6562\u9ad8\u6500\u4e86\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u4e0d\uff0c\u6211\u7684\u8bb0\u6027\u5f88\u574f\uff0c\u4f60\u4eec\u4e24\u4f4d\u7684\u540d\u5b57\u6211\u5c31\u60f3\u4e0d\u51fa\u6765\u3002\u201d \u3000\u3000\u90a3\u4e24\u4e2a\u6b66\u58eb\u9053\uff1a\u201c\u5f53\u65f6\u4f60\u662f\u516c\u4e3b\u7684\u597d\u670b\u53cb\uff0c\u76f8\u8bc6\u7684\u4e0d\u662f\u738b\u5b50\u5c31\u662f\u5927\u81e3\uff0c\u4e5f\u6ca1\u6709\u8c01\u5c06\u6211\u4eec \u7684\u540d\u5b57\u544a\u8bc9\u8fc7\u4f60\uff0c\u4e5f\u96be\u602a\u4f60\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u548c\u4ed6\u4eec\u95f2\u8c08\u65e7\u4e8b\uff0c\u654c\u610f\u4e0d\u77e5\u4e0d\u89c9\u53c8\u6d88\u4e86\u51e0\u5206\uff0c\u8fd9\u4e24\u4e2a\u6b66\u6765\u58eb\u4e5f\u628a\u540d\u5b57\u544a\u8bc9\u4ed6\u4e86\uff0c \u4e00\u4e2a\u53eb\u505a\u7c98\u4e0d\u7f55\uff0c\u4e00\u4e2a\u53eb\u505a\u901f\u4e0d\u53f0\u3002 \u3000\u3000\u7c98\u4e0d\u7f55\u5c31\u662f\u90a3\u4e2a\u6700\u4e3a\u50b2\u6162\u7684\u4eba\uff0c\u5ffd\u5730\u970d\u7136\u4e00\u7701\uff0c\u8bf4\u9053\uff1a\u201c\u95f2\u8bdd\u5c11\u8bf4\uff0c\u674e\u601d\u5357\u4f60\u8fd9\u6b21\u6765\u5230 \u5e95\u662f\u4f55\u7528\u610f\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u7b11\u9053\uff1a\u201c\u4f60\u95ee\u6211\u7684\u6765\u610f\uff0c\u6211\u4e0d\u662f\u4e00\u6765\u5c31\u5df2\u7ecf\u548c\u4f60\u4eec\u8bf4\u4e86\u4e48\uff1f\u7b2c\u4e00\u662f\u63a2\u671b\u8001\u670b\u53cb\uff0c \u7b2c\u4e8c\u4e5f\u662f\u60f3\u6765\u770b\u770b\u4f60\u4eec\u662f\u5426\u5df2\u80fd\u884c\u52a8\u5982\u5e38\uff1f\u201d \u3000\u3000\u7c98\u4e0d\u7f55\u9053\uff1a\u201c\u6211\u4eec\u80fd\u591f\u884c\u52a8\u53c8\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u8bf7\u4e24\u4f4d\u5927\u54e5\u522b\u602a\u6211\u8bf4\u8bdd\u5766\u7387\u3002\u201d\u7c98\u4e0d\u7f55\u5fc3\u60f3\uff1a\u201c\u6765\u4e86\uff0c\u6765\u4e86\uff01\u201d\u5927\u58f0\u8bf4 \u9053\uff1a\u201c\u6211\u5c31\u662f\u60f3\u542c\u4f60\u7684\u771f\u5fc3\u8bdd\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u5728\u6211\u6765\u8bf4\uff0c\u6211\u662f\u5e0c\u671b\u4f60\u4eec\u591a\u7559\u51e0\u5929\uff0c\u4f4f\u4e0b\u6765\u4e0d\u8d70\u66f4\u597d\u3002\u4f46\u6211\u4eec\u8fd9\u91cc\u7684\u89c4\u77e9 \u662f\u8981\u7559\u5219\u7559\uff0c\u8981\u53bb\u5219\u53bb\uff0c\u51b3\u4e0d\u52c9\u5f3a\u3002\u6211\u521a\u624d\u542c\u4e24\u4f4d\u7684\u53e3\u6c14\uff0c\u5927\u7ea6\u8fd8\u662f\u60f3\u56de\u53bb\u7684\u5427\uff1f\u201d \u3000\u3000\u901f\u4e0d\u53f0\u9053\uff1a\u201c\u54e6\uff0c\u4f60\u662f\u60f3\u653e\u6211\u4eec\u56de\u53bb\uff1f\u201d\u7c98\u4e0d\u7f55\u5219\u5f20\u5927\u4e86\u53e3\uff0c\u51e0\u4e4e\u4e0d\u6562\u76f8\u4fe1\u81ea\u5df1\u7684\u8033 \u6735\u3002 \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4e24\u4f4d\u82e5\u662f\u601d\u5bb6\u5fc3\u5207\uff0c\u9a6c\u4e0a\u5c31\u8d70\u4e5f\u884c\u3002\u201d \u3000\u3000\u7c98\u4e0d\u7f55\u9053\uff1a\u201c\u4f60\u8981\u6211\u66ff\u4f60\u505a\u4ec0\u4e48\u4e8b\u60c5\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u56de\u53bb\u4e4b\u540e\uff0c\u8bf7\u4f60\u66ff\u6211\u95ee\u5019\u5f80\u65e5\u7684\u4e00\u73ed\u670b\u53cb\u3002\u201d \u3000\u3000\u7c98\u4e0d\u7f55\u9053\uff1a\u201c\u54a6\uff0c\u5982\u6b64\u8bf4\u6765\uff0c\u4f60\u662f\u6beb\u65e0\u6761\u4ef6\u7684\u5c31\u80af\u5c06\u6211\u4eec\u653e\u8d70\u4e86\u3002\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u8c01\u65e0\u7236\u6bcd\uff0c\u8c01\u65e0\u59bb\u5b50\u3002\u4efb\u4f55\u4eba\u90fd\u662f\u76fc\u671b\u548c\u5bb6\u4eba\u56e2\u805a\u7684\uff0c\u6211\u5c82\u80fd\u62c6\u6563\u4f60\u4eec\u592b \u59bb\u7236\u6bcd\uff0c\u4ee4\u4f60\u4eec\u6b7b\u522b\u751f\u79bb\uff1f\u201d \u3000\u3000\u7c98\u4e0d\u7f55\u9053\uff1a\u201c\u53ef\u662f\u6211\u3001\u6211\u4eec\u5bf9\u4f60\u2026\u2026\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u60f3\u5fc5\u4f60\u4e5f\u77e5\u9053\u4e00\u70b9\u6211\u7684\u5bb6\u4e8b\u4e86\u3002\u5b9e\u4e0d\u76f8\u7792\u6211\u7684\u7236\u4eb2\u5c31\u662f\u5728\u4e8c\u5341\u591a\u5e74\u524d\u7ed9\u4f60 \u4eec\u4fd8\u864f\u4e86\u53bb\uff0c\u4e0d\u8bb8\u8fd8\u5bb6\uff0c\u7ec8\u4e8e\u57cb\u9aa8\u5f02\u4e61\uff0c\u6b7b\u4e0d\u7791\u76ee\u7684\u3002\u6211\u4e5f\u6b63\u662f\u4e3a\u4e86\u5bfb\u627e\u7236\u4eb2\uff0c\u624d\u8dd1\u5230\u4f60\u4eec \u8499\u53e4\u53bb\u7684\u3002\u5c06\u5fc3\u6bd4\u5fc3\uff0c\u6211\u4eec\u7236\u5b50\u53d7\u8fc7\u7684\u82e6\u695a\uff0c\u6211\u53c8\u600e\u5fcd\u8981\u4f60\u4eec\u906d\u53d7\uff1f\u201d \u3000\u3000\u4e8c\u4eba\u7ed9\u674e\u601d\u5357\u8bf4\u5f97\u5927\u4e3a\u611f\u52a8\uff0c\u521a\u521a\u8fd8\u662f\u795e\u6001\u50b2\u6162\u7684\u7c98\u4e0d\u7f55\u4ea6\u5df2\u7981\u4e0d\u4f4f\u70ed\u6cea\u76c8\u7736\u3002 \u3000\u3000\u901f\u4e0d\u53f0\u9053\uff1a\u201c\u53ef\u662f\u6211\u4eec\u56de\u53bb\u4e4b\u540e\uff0c\u8bf4\u4e0d\u8d70\u5c06\u6765\u8fd8\u4f1a\u5728\u6c99\u573a\u4e0e\u4f60\u76f8\u89c1\u7684\u554a\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u5f53\u7136\u6211\u662f\u5e0c\u671b\u4f60\u4eec\u4e0d\u4f1a\u8fd9\u6837\u7684\uff0c\u4f46\u4f60\u4eec\u662f\u91d1\u5e10\u6b66\u58eb\uff0c\u9664\u975e\u4f60\u4eec\u4e0d\u5e72\uff0c\u5426\u5219 \u662f\u6050\u6015\u5f88\u96be\u907f\u514d\u8981\u7ed9\u5927\u6c57\u91cd\u65b0\u9a71\u4e0a\u6c99\u573a\u7684\u3002\u6240\u4ee5\u5018\u82e5\u662f\u6709\u90a3\u4e00\u5929\uff0c\u6211\u4e5f\u4e0d\u4f1a\u602a\u4f60\u3002\u4e0d\u8fc7\uff0c\u6211 \u4e5f\u8981\u628a\u8bdd\u8bf4\u660e\uff0c\u5230\u4e86\u6c99\u573a\u76f8\u89c1\u7684\u65f6\u5019\uff0c\u5f7c\u6b64\u53ae\u6740\uff0c\u6211\u662f\u4e0d\u4f1a\u7559\u60c5\u7684\u3002\u4f46\u5018\u82e5\u4f60\u53c8\u4e3a\u6211\u6240\u64d2\uff0c \u6211\u8fd8\u662f\u53ef\u4ee5\u653e\u4f60\u56de\u53bb\u3002\u201d \u3000\u3000\u901f\u4e0d\u53f0\u9053\uff1a\u201c\u674e\u76df\u4e3b\uff0c\u4f60\u80fd\u591f\u505a\u5230\u8fd9\u6837\uff0c\u6211\u4eec\u5df2\u7ecf\u662f\u611f\u6fc0\u4e0d\u5c3d\u4e86\u3002\u8bf4\u53e5\u8001\u5b9e\u8bdd\uff0c\u5047\u5982\u4f60 \u662f\u7ed9\u6211\u4eec\u6240\u64d2\uff0c\u6211\u4eec\u60f3\u653e\u4f60\u4e5f\u4e0d\u6562\u5462\u3002\u5509\uff0c\u6068\u53ea\u6068\u6211\u4eec\u4e24\u56fd\u4ea4\u950b\uff0c\u6211\u4eec\u662f\u4e0d\u80fd\u4e0d\u8f9c\u8d1f\u4f60\u7684\u53cb \u8c0a\u4e86\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u4e0d\uff0c\u4e0d\u80fd\u53ea\u7b3c\u7edf\u5f52\u548e\u4e8e\u4e24\u56fd\u4ea4\u950b\uff0c\u8fd9\u662f\u4f60\u4eec\u7684\u5927\u6c57\u3001\u738b\u516c\u3001\u5c06\u519b\u3001\u6743\u8d35\u9020 \u6210\u7684\u7f6a\u8fc7\uff01\u6211\u4eec\u6c49\u4eba\u53ef\u6ca1\u6709\u8dd1\u5230\u4f60\u4eec\u7684\u5730\u65b9\u53bb\u6253\u4ed7\u3002\u201d \u3000\u3000\u7c98\u4e0d\u7f55\u4e0e\u901f\u4e0d\u53f0\u65e0\u8a00\u53ef\u8fa9\uff0c\u5fc3\u91cc\u81ea\u5df1\u4e5f\u89c9\u5f97\u60ed\u6127\uff0c\u4e0d\u89c9\u90fd\u662f\u4f4e\u4e0b\u4e86\u5934\u3002 \u3000\u3000\u674e\u601d\u5357\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u4e3a\u5927\u6c57\u738b\u516c\u5c06\u519b\u6743\u8d35\u5356\u547d\uff0c\u6240\u5f97\u7684\u53c8\u662f\u4ec0\u4e48\uff1f\u4e0d\u9519\uff0c\u4f60\u4eec\u662f\u53d7 \u5c01\u4e3a\u91d1\u5e10\u6b66\u58eb\uff0c\u6bd4\u666e\u901a\u7684\u6b66\u58eb\u662f\u9ad8\u51fa\u4e00\u5934\u7684\u4e86\uff0c\u4f46\u4f60\u4eec\u7684\u6027\u547d\u5374\u662f\u671d\u5915\u4e0d\u4fdd\uff0c\u4f60\u4eec\u7684\u9c9c\u8840\u53ea \u662f\u4fdd\u4f4f\u4e86\u4ed6\u4eec\u7684\u5bcc\u8d35\u8363\u534e\uff0c\u8fd9\u53c8\u503c\u5f97\u4e48\uff1f\u4f60\u4eec\u518d\u4ed4\u7ec6\u60f3\u60f3\uff0c\u4f60\u4eec\u62fc\u547d\u6253\u4ed7\uff0c\u66ff\u4f60\u4eec\u7684\u5927\u6c57\u706d \u4e86\u65e0\u6570\u56fd\u5bb6\uff0c\u770b\u8d77\u6765\u4f60\u4eec\u8499\u53e4\u7684\u767e\u59d3\u53c8\u5f97\u5230\u4e86\u4ec0\u4e48\u597d\u5904\uff1f\u63a0\u593a\u6765\u7684\u7389\u5e1b\u51b3\u4e0d\u4f1a\u5206\u7ed9\u4ed6\u4eec\uff0c\u4ed6 \u4eec\u53ea\u6709\u51fa\u7cae\u51fa\u529b\u7684\u4efd\u513f\uff0c\u591a\u5c11\u4eba\u53c8\u56e0\u4e3a\u8fde\u5e74\u5f81\u6218\u800c\u5f04\u5230\u5bb6\u6563\u4eba\u4ea1\uff0c\u6328\u9965\u53d7\u82e6\uff1f\u53d7\u4f60\u4eec\u4fb5\u7565\u7684 \u56fd\u5bb6\uff0c\u53c8\u6709\u591a\u5c11\u4eba\u65e0\u8f9c\u88ab\u5bb3\uff0c\u8f97\u8f6c\u6d41\u79bb\uff1f\u201d \u3000\u3000\u7c98\u3001\u901f\u4e8c\u4eba\u81ea\u6709\u751f\u4ee5\u6765\uff0c\u6240\u53d7\u7684\u90fd\u662f\u201c\u600e\u6837\u624d\u662f\u4e00\u4e2a\u597d\u6b66\u58eb\u201d\u7684\u6559\u80b2\uff0c\u4ece\u6ca1\u6709\u4eba\u6562\u5411\u4ed6 \u4eec\u8bf4\u8fc7\u8fd9\u6837\u7684\u4e00\u756a\u8bdd\uff0c\u4ed4\u7ec6\u60f3\u60f3\uff0c\u4e0d\u7981\u90fd\u662f\u5185\u759a\u795e\u660e\uff0c\u89c9\u5f97\u674e\u601d\u5357\u7684\u8bf4\u8bdd\u8bf4\u5f97\u4e00\u70b9\u4e0d\u9519\u3002\u4f46 \u4e5f\u53ea\u662f\u521d\u6b65\u7684\u9192\u609f\u800c\u5df2\uff0c\u82e5\u8981\u4ed6\u4eec\u7acb\u5373\u53cd\u6297\u5927\u6c57\uff0c\u4ed6\u4eec\u8fd8\u662f\u8fde\u60f3\u4e5f\u4e0d\u6562\u60f3\u7684\u3002\u5f53\u7136\u674e\u601d\u5357\u4e5f \u6ca1\u6709\u7acb\u5373\u4fbf\u8981\u4ed6\u4eec\u8fd9\u6837\u3002 \u3000\u3000\u7c98\u4e0d\u7f55\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u674e\u516c\u5b50\uff0c\u591a\u8c22\u4f60\u7684\u8fd9\u756a\u6559\u5bfc\uff0c\u4ece\u4eca\u4e4b\u540e\uff0c\u6211\u4e5f\u4e0d\u60f3\u8d2a\u56fe\u4ec0\u4e48 \u5bcc\u8d35\u4e86\uff0c\u56de\u53bb\u4e4b\u540e\uff0c\u6211\u548c\u5bb6\u4eba\u8dd1\u5230\u6df1\u5c71\u91cc\u8eb2\u8d77\u6765\uff0c\u4ece\u6b64\u6253\u730e\u4e3a\u751f\uff0c\u4f46\u613f\u5e73\u5e73\u5b89\u5b89\u7684\u8fc7\u4e2a\u4e0b\u534a \u4e16\uff0c\u4e5f\u5c31\u5fc3\u6ee1\u610f\u8db3\u4e86\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u53ea\u6015\u4f60\u8981\u60f3\u8eb2\u907f\u4e5f\u662f\u8eb2\u907f\u4e0d\u4e86\u3002\u201d\u4e0d\u8fc7\uff0c\u5176\u4e2d\u7684\u9053\u7406\u4e00\u65f6\u4e5f\u96be\u4ee5\u8bf4 \u5f97\u4ed6\u4eec\u660e\u767d\uff0c\u5fc3\u60f3\uff1a\u201c\u4ed6\u4eec\u80fd\u591f\u8fd9\u6837\u4e5f\u5df2\u7ecf\u662f\u5f88\u4e0d\u9519\u4e86\u3002\u201d\u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u4f46\u613f\u6211\u4eec\u4ece\u4eca \u4e4b\u540e\u4e0d\u4f1a\u518d\u662f\u654c\u4eba\u3002\u4f60\u4eec\u51e0\u65f6\u56de\u53bb\uff1f\u201d \u3000\u3000\u901f\u4e0d\u53f0\u9053\uff1a\u201c\u591a\u8499\u76df\u4e3b\u653e\u6211\u4eec\u56de\u53bb\uff0c\u6211\u4eec\u60f3\u73b0\u5728\u5c31\u8d70\uff0c\u53ef\u4ee5\u5417\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u5f53\u7136\u53ef\u4ee5\uff0c\u6211\u9001\u4f60\u4eec\u4e0b\u5c71\u3002\u201d \u3000\u3000\u9001\u5230\u5c71\u811a\uff0c\u674e\u601d\u5357\u548c\u4ed6\u4eec\u6325\u624b\u9053\u522b\uff0c\u7c98\u4e0d\u7f55\u5ffd\u9053\uff1a\u201c\u674e\u516c\u5b50\uff0c\u6709\u4e00\u4ef6\u4e8b\u60c5\u6211\u8981\u548c\u4f60 \u8bf4\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\u60c5\uff1f\u201d \u3000\u3000\u7c98\u4e0d\u7f55\u9053\uff1a\u201c\u8fd9\u662f\u4e00\u4ef6\u53ea\u6709\u6211\u4eec\u4e24\u4eba\u77e5\u9053\u7684\u79d8\u5bc6\uff0c\u6211\u5728\u5927\u6c57\u9762\u524d\u53d1\u8fc7\u8a93\uff0c\u51b3\u4e0d\u80fd\u6cc4\u6f0f \u7684\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u90a3\u5c31\u4e0d\u5fc5\u8bf4\u4e86\u3002\u201d \u3000\u3000\u7c98\u4e0d\u7f55\u9053\uff1a\u201c\u4e0d\uff0c\u674e\u516c\u5b50\uff0c\u6211\u867d\u7136\u662f\u53d1\u8fc7\u8a93\uff0c\u4f46\u4f60\u5bf9\u6211\u4eec\u592a\u597d\u4e86\uff0c\u6211\u4e0d\u8bf4\u5c31\u5bf9\u4f60\u4e0d \u4f4f\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u54e6\uff0c\u662f\u548c\u6211\u6709\u5173\u7684\u4e48\uff1f\u201d \u3000\u3000\u7c98\u4e0d\u7f55\u9053\uff1a\u201c\u6b63\u662f\u3002\u5509\uff0c\u674e\u516c\u5b50\uff0c\u4f60\u5f85\u4eba\u4e5f\u592a\u539a\u9053\u4e86\uff0c\u767d\u4e07\u96c4\u8fd9\u8001\u513f\uff0c\u4f60\u5b9e\u5728\u662f\u4e0d\u5e94\u8be5 \u653e\u8d70\u4ed6\u7684\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u7c98\u4e0d\u7f55\u9053\uff1a\u201c\u4f60\u7684\u5927\u4ec7\u4eba\u6b63\u5728\u4ed6\u7684\u5bb6\u4e2d\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u53c8\u60ca\u53c8\u559c\uff0c\u8fde\u5fd9\u95ee\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u662f\u4f59\u4e00\u4e2d\u5417\uff1f\u201d \u3000\u3000\u7c98\u4e0d\u7f55\u9053\uff1a\u201c\u674e\u516c\u5b50\uff0c\u4ee4\u5c0a\u906d\u53d7\u4f59\u4e00\u4e2d\u8fd9\u53ae\u7684\u60e8\u5bb3\uff0c\u6211\u4eec\u662f\u65e9\u5df2\u77e5\u9053\u7684\u4e86\u3002\u660e\u6167\u516c\u4e3b\u66fe \u6c42\u8fc7\u56db\u738b\u5b50\u548c\u5927\u6c57\u6740\u4ed6\uff0c\u53ef\u60dc\u4e0d\u80fd\u5982\u613f\uff0c\u5927\u6c57\u975e\u4f46\u6ca1\u6709\u6740\u4ed6\uff0c\u53cd\u800c\u66f4\u91cd\u7528\u4ed6\u4e86\u3002\u8001\u5b9e\u8bf4\uff0c\u6211 \u4eec\u4e5f\u662f\u6c14\u4ed6\u4e0d\u8fc7\u3002\u201d \u3000\u3000\u539f\u6765\u6210\u5409\u601d\u6c57\u901d\u4e16\u4e4b\u540e\uff0c\u660e\u6167\u516c\u4e3b\u53c8\u66fe\u5148\u540e\u5728\u62d6\u96f7\u76d1\u56fd\u548c\u7ee7\u4efb\u7684\u5927\u6c57\u7a9d\u9614\u53f0\u4e4b\u524d\uff0c\u516c\u5f00 \u63a7\u544a\u8fc7\u4f59\u4e00\u4e2d\uff0c\u7c98\u3001\u901f\u4e8c\u4eba\u8eab\u4e3a\u91d1\u5e10\u6b66\u58eb\uff0c\u5f53\u65f6\u4e5f\u662f\u5728\u573a\u7684\u3002 \u3000\u3000\u674e\u601d\u5357\u6709\u70b9\u8be7\u5f02\uff0c\u95ee\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u4f59\u4e00\u4e2d\u8fd9\u53ae\u548c\u4f60\u4eec\u4e00\u9053\u6765\u7684\u4e86\uff1f\u4ed6\u4e0d\u662f\u5728\u505a\u7740\u9547\u56fd \u738b\u5b50\u7684\u526f\u5143\u5e05\u7684\u5417\uff0c\u600e\u7684\u5927\u6c57\u5374\u6d3e\u7ed9\u4ed6\u8fd9\u4e2a\u5dee\u4e8b\uff1f\u201d\u8981\u77e5\u4f59\u4e00\u4e2d\u867d\u7136\u4e5f\u61c2\u6b66\u827a\uff0c\u4f46\u5374\u751a\u662f\u5e73 \u5eb8\uff0c\u4f9d\u7406\u6765\u8bf4\uff0c\u662f\u4e0d\u8be5\u628a\u4ed6\u5f53\u4f5c\u4e00\u822c\u7684\u6b66\u58eb\u6765\u4f7f\u7528\u7684\u3002 \u3000\u3000\u901f\u4e0d\u53f0\u8bf4\u9053\uff1a\u201c\u662f\u8fd9\u6837\u7684\uff0c\u5927\u6c57\u5e0c\u671b\u80fd\u591f\u62c9\u62e2\u4e00\u4e9b\u6c49\u4eba\uff0c\u6700\u597d\u662f\u6c5f\u6e56\u4e0a\u7684\u6210\u540d\u4eba\u7269\u6216\u7eff \u6797\u597d\u6c49\uff0c\u5730\u65b9\u4e0a\u6709\u52bf\u529b\u7684\u7ec5\u58eb\u4e5f\u5728\u88ab\u62c9\u62e2\u4e4b\u5217\u3002\u8fd9\u6837\uff0c\u5c06\u6765\u6211\u4eec\u8fdb\u5175\u4e2d\u539f\u4e4b\u65f6\uff0c\u5f97\u5230\u8fd9\u4e9b\u4eba \u7684\u52a9\u529b\uff0c\u5c31\u53ef\u4ee5\u65b9\u4fbf\u591a\u4e86\u3002\u8fd9\u4e2a\u4efb\u52a1\uff0c\u5f53\u7136\u662f\u4f59\u4e00\u4e2d\u6700\u4e3a\u9002\u5408\uff0c\u6211\u4eec\u8fde\u6c49\u8bdd\u90fd\u8bf4\u4e0d\u6d41\u5229\uff0c\u8981 \u505a\u4e5f\u505a\u4e0d\u6765\u7684\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u51b7\u7b11\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\uff0c\u4ed6\u672c\u6765\u662f\u6c49\u4eba\u4e2d\u7684\u8d25\u7c7b\uff0c\u602a\u4e0d\u5f97\u4f60\u4eec\u7684\u5927\u6c57\u8981\u5229\u7528\u4ed6\u6765\u8fdb \u884c\u8fd9\u79cd\u8d25\u7c7b\u7684\u52fe\u5f53\u3002\u5047\u6211\u6562\u65ad\u8a00\uff0c\u50cf\u4f59\u4e00\u4e2d\u8fd9\u79cd\u8d25\u7c7b\uff0c\u4f60\u4eec\u5728\u6c49\u4eba\u4e2d\u51b3\u4e0d\u4f1a\u627e\u5230\u591a\u7684\uff0c\u7eb5 \u6709\uff0c\u4e5f\u53ea\u662f\u4e00\u5c0f\u64ae\u800c\u5df2\u3002\u201d \u3000\u3000\u901f\u4e0d\u53f0\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u4f59\u4e00\u4e2d\u8d77\u521d\u60f3\u85cf\u5728\u9633\u5929\u96f7\u5bb6\u91cc\uff0c\u4f46\u6050\u6015\u7ed9\u91d1\u56fd\u4e0e\u9633\u5929\u96f7\u654c\u5bf9\u7684\u4e00\u6d3e \u77e5\u9053\uff0c\u800c\u4e14\u4f4f\u5728\u91d1\u56fd\u7684\u4eac\u57ce\u4e5f\u4e0d\u65b9\u4fbf\uff0c\u540e\u6765\u624d\u60f3\u5230\u4e86\u53bb\u627e\u767d\u4e07\u96c4\u3002\u767d\u4e07\u96c4\u548c\u9633\u5929\u96f7\u662f\u65e9\u5c31\u6709 \u4e86\u6765\u5f80\u7684\uff0c\u4f59\u4e00\u4e2d\u548c\u4ed6\u4e5f\u662f\u65e7\u76f8\u8bc6\uff0c\u4ed6\u62ff\u4e86\u9633\u5929\u96f7\u7684\u5bc6\u4fe1\u53bb\u627e\u4ed6\uff0c\u4e24\u4eba\u89c1\u9762\u4e4b\u540e\uff0c\u679c\u7136\u81ed\u5473 \u76f8\u6295\uff0c\u4e00\u62cd\u5373\u5408\u3002\u201d \u3000\u3000\u7c98\u4e0d\u7f55\u8bf4\u9053\uff1a\u201c\u5927\u6c57\u6d3e\u6211\u4eec\u4e8c\u4eba\u505a\u4f59\u4e00\u4e2d\u7684\u5e2e\u624b\uff0c\u4ea4\u7ed9\u6211\u4eec\u4e09\u4e2a\u4efb\u52a1\uff0c\u4e00\u662f\u4fa6\u67e5\u660e\u6167\u516c \u4e3b\u7684\u4e0b\u843d\uff1b\u4e8c\u662f\u8bbe\u6cd5\u5bb3\u4f60\uff0c\u5018\u80fd\u5c06\u4f60\u643a\u56de\u8499\u53e4\u90a3\u5c31\u66f4\u597d\uff1b\u4e09\u8981\u6211\u4eec\u76d1\u89c6\u4f59\u4e00\u4e2d\u3002\u6700\u540e\u4e00\u4e2a\u4efb \u52a1\u5f53\u7136\u4e0d\u4f1a\u8ba9\u4f59\u4e00\u4e2d\u77e5\u9053\uff0c\u4f46\u5176\u4ed6\u4e24\u4e2a\u4f59\u4e00\u4e2d\u5219\u662f\u77e5\u9053\u7684\u3002\u8fd9\u4eba\u8be1\u8ba1\u591a\u7aef\uff0c\u674e\u516c\u5b50\u4f60\u8981\u591a\u70b9 \u5f53\u5fc3\u4ed6\u7684\u6697\u7b97\u624d\u597d\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u591a\u8c22\u4e86\u8fd9\u4e24\u4e2a\u4eba\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u4e48\u73b0\u5728\u4f60\u4eec\u662f\u51c6\u5907\u56de\u5bb6\u5462\uff1f\u8fd8\u662f\u56de\u5230\u767d\u4e07\u96c4\u90a3\u513f\u8ddf \u4f59\u4e00\u4e2d\uff1f\u201d \u3000\u3000\u7c98\u4e0d\u7f55\u6124\u7136\u8bf4\u9053\uff1a\u201c\u54fc\uff01\u73b0\u5728\u6211\u90fd\u4e0d\u60f3\u5e2e\u5fd9\u5927\u6c57\u6253\u4ed7\u4e86\uff0c\u8c01\u8fd8\u613f\u610f\u53bb\u8ddf\u8fd9\u4e2a\u5351\u9119\u5c0f \u4eba\uff01\u201d \u3000\u3000\u901f\u4e0d\u53f0\u5fc3\u601d\u6bd4\u8f83\u7075\u654f\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u674e\u516c\u5b50\u4f55\u4ee5\u6709\u6b64\u4e00\u95ee\uff1f\u201d\u4ed4\u7ec6\u4e00\u60f3\uff0c\u5ffd\u5730\u604d\u7136\u5927 \u609f\uff0c\u7b11\u9053\uff1a\u201c\u54b1\u4eec\u56de\u53bb\uff0c\u53cd\u6b63\u4e5f\u8981\u8def\u7ecf\u535a\u671b\uff0c\u767d\u4e07\u96c4\u7684\u5bb6\u4e61\uff0c\u4f55\u59a8\u5c31\u53bb\u627e\u90a3\u4f59\u4e00\u4e2d\u6d88\u9063\u6d88 \u9063\u3002\u201d\u7c98\u4e0d\u7f55\u8e59\u7709\u9053\uff1a\u201c\u6211\u89c1\u4e86\u8fd9\u53ae\u5c31\u60f3\u4f5c\u5455\uff0c\u6709\u4ec0\u4e48\u597d\u6d88\u9063\u7684\uff1f\u54b1\u4eec\u53c8\u4e0d\u662f\u95f2\u7740\u6ca1\u4e8b \u505a\uff01\u201d \u3000\u3000\u901f\u4e0d\u53f0\u7b11\u9053\uff1a\u201c\u5927\u54e5\u4f60\u600e\u4e48\u7cca\u6d82\u8d77\u6765\u4e86\uff1f\u6211\u8bf4\u7684\u6d88\u9063\uff0c\u53ef\u5e76\u4e0d\u662f\u53ea\u4e3a\u4e86\u627e\u4ed6\u5f00\u5f00\u5fc3 \u554a\u3002\u201d\u7c98\u4e0d\u7f55\u9053\uff1a\u201c\u4f60\u7684\u610f\u601d\u662f\u2026\u2026\u201d\u901f\u4e0d\u53f0\u9053\uff1a\u201c\u4e0d\u5c06\u4ed6\u9664\u6389\uff0c\u54b1\u4eec\u7109\u80fd\u6ca1\u4e8b\u3002\u201d \u3000\u3000\u7c98\u4e0d\u7f55\u767b\u65f6\u4e5f\u604d\u7136\u5927\u609f\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u54b1\u4eec\u5728\u98de\u9f99\u5c71\u4e8b\u8d25\u906d\u64d2\u7684\u4e8b\u60c5\uff0c\u767d\u4e07\u96c4\u4e00\u5b9a\u4f1a \u8bf4\u7ed9\u4ed6\u77e5\u9053\u7684\u3002\u5047\u82e5\u8ba9\u4ed6\u751f\u8fd8\u8499\u53e4\uff0c\u786e\u5b9e\u662f\u5bf9\u54b1\u4eec\u4e0d\u5229\u3002\u201d \u3000\u3000\u901f\u4e0d\u53f0\u9053\uff1a\u201c\u662f\u5440\uff0c\u5018\u82e5\u80fd\u591f\u5c06\u4ed6\u9664\u6389\uff0c\u5927\u6c57\u4e0d\u89c1\u6211\u4eec\u56de\u53bb\uff0c\u4e5f\u4e0d\u89c1\u4ed6\u56de\u53bb\uff0c\u5c31\u4f1a\u4ee5\u4e3a \u54b1\u4eec\u548c\u4ed6\u4e00\u6837\uff0c\u4e0d\u77e5\u9053\u662f\u7ed9\u4e2d\u539f\u54ea\u4f4d\u82f1\u96c4\u6740\u4e86\u3002\u5426\u5219\u54b1\u4eec\u7eb5\u7136\u8eb2\u8fdb\u6df1\u5c71\uff0c\u98ce\u58f0\u96be\u4fdd\u6ca1\u6709\u6cc4 \u6f0f\uff0c\u4e00\u65e6\u6cc4\u6f0f\uff0c\u4f59\u4e00\u4e2d\u5f53\u7136\u80fd\u591f\u60f3\u5f97\u5230\uff0c\u54b1\u4eec\u6700\u7ed9\u6c49\u4eba\u653e\u56de\u6765\u7684\u4e86\u3002\u90a3\u65f6\u5927\u6c57\u5b9a\u7136\u6000\u7591\u54b1\u4eec \u662f\u56de\u6765\u505a\u5978\u7ec6\u7684\uff0c\u8fd8\u80fd\u653e\u8fc7\u54b1\u4eec\u5417\uff1f\u201d \u3000\u3000\u7c98\u4e0d\u7f55\u9053\uff1a\u201c\u4f59\u4e00\u4e2d\u6b66\u827a\u5e73\u5eb8\uff0c\u54b1\u4eec\u6740\u4ed6\u4e0d\u96be\uff0c\u53ef\u662f\u4ed6\u5374\u662f\u8eb2\u5728\u767d\u4e07\u96c4\u5bb6\u4e2d\u7684\u554a\uff01\u9664\u975e \u54b1\u4eec\u6253\u7b97\u7528\u4e24\u6761\u6027\u547d\u6362\u4ed6\u4e00\u6761\uff0c\u5426\u5219\u6740\u4e86\u4ed6\u4e5f\u662f\u9003\u8dd1\u4e0d\u4e86\u3002\u201d\u987b\u77e5\u7c98\u4e0d\u7f55\u867d\u7136\u50e7\u6068\u4f59\u4e00\u4e2d\uff0c \u4f46\u7a76\u7adf\u6ca1\u6709\u6df1\u4ec7\u5927\u6068\uff0c\u82e5\u7136\u8981\u7528\u6027\u547d\u76f8\u640f\uff0c\u4ed6\u8fd8\u662f\u4e0d\u613f\u610f\u7684\u3002 \u3000\u3000\u901f\u4e0d\u53f0\u7b11\u9053\uff1a\u201c\u54b1\u4eec\u4e0d\u662f\u767d\u4e07\u96c4\u7684\u5bf9\u624b\uff0c\u53ef\u662f\u767d\u4e07\u96c4\u4e0d\u662f\u674e\u516c\u5b50\u7684\u5bf9\u624b\u554a\u3002\u51e0\u5929\u4e4b\u540e\uff0c \u54b1\u4eec\u6216\u8005\u8fd8\u4f1a\u548c\u674e\u516c\u5b50\u5728\u767d\u4e07\u96c4\u5bb6\u4e2d\u89c1\u9762\u7684\uff0c\u4e0d\u77e5\u6211\u731c\u5f97\u5bf9\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u62a5\u4ec7\u4e4b\u4e8b\uff0c\u6211\u4e0d\u6562\u52b3\u70e6\u4e24\u4f4d\u3002\u4f46\u4e24\u4f4d\u82e5\u80fd\u5728\u767d\u4e07\u96c4\u5bb6\u4e2d\u4f5c\u6211 \u5185\u5e94\uff0c\u6211\u4e14\u662f\u611f\u6fc0\u4e0d\u5c3d\u3002\u201d \u3000\u3000\u7c98\u4e0d\u7f55\u4e00\u62cd\u5927\u817f\uff0c\u8bf4\u9053\uff1a\u201c\u7740\u5440\uff0c\u6211\u771f\u662f\u53c8\u7cca\u6d82\u4e86\u3002\u674e\u516c\u5b50\u7684\u8840\u6d77\u6df1\u4ec7\uff0c\u5f53\u7136\u662f\u8981\u81ea\u5df1 \u6765\u62a5\u3002\u4ed6\u6740\u4e86\u4f59\u4e00\u4e2d\uff0c\u767d\u4e07\u96c4\u8c05\u4e5f\u9003\u8dd1\u4e0d\u4e86\uff0c\u54b1\u4eec\u8fd8\u7528\u5f97\u7740\u5bb3\u6015\u4ed6\u4e48\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u6211\u60f3\u8bf7\u4e24\u4f4d\u5148\u8d76\u56de\u53bb\uff0c\u7a33\u4f4f\u4f59\u4e00\u4e2d\uff0c\u8ba9\u4ed6\u4ecd\u7136\u7559\u5728\u767d\u4e07\u96c4\u7684\u5bb6\u91cc\uff0c\u90a3\u5c31\u662f \u5e2e\u4e86\u6211\u7684\u5fd9\u4e86\u3002\u201d \u3000\u3000\u7c98\u4e0d\u7f55\u9053\uff1a\u201c\u8fd9\u70b9\u5c0f\u4e8b\uff0c\u6211\u82e5\u4e0d\u80fd\u529e\u5230\uff0c\u90a3\u8fd8\u7b97\u5f97\u4ec0\u4e48\u670b\u53cb\uff1f\u201d\u8bf4\u7f62\u7a81\u7136\u62d4\u51fa\u5200\u6765\uff0c\u4e00 \u5200\u5411\u81ea\u5df1\u7684\u5927\u817f\u65ab\u4e0b\u3002 \u3000\u3000\u674e\u601d\u5357\u5403\u4e86\u4e00\u60ca\uff0c\u8fde\u5fd9\u62a2\u4ed6\u7684\u5200\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u5e72\u4ec0\u4e48\uff1f\u201d\u674e\u601d\u5357\u867d\u5feb\uff0c\u53ef\u662f\u7c98\u4e0d\u7f55\u5374\u5df2 \u5728\u81ea\u5df1\u7684\u5927\u817f\u5212\u5f00\u4e86\u4e00\u9053\u4e09\u5bf8\u591a\u957f\u7684\u4f24\u53e3\u4e86\u3002 \u3000\u3000\u7c98\u4e0d\u7f55\u7b11\u9053\uff1a\u201c\u4e0d\u6302\u70b9\u5f69\uff0c\u5982\u4f55\u80fd\u591f\u4f7f\u4f59\u4e00\u4e2d\u76f8\u4fe1\u6211\u4eec\u662f\u6740\u4e86\u770b\u5b88\uff0c\u9003\u56de\u6765\u7684\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u5927\u4e3a\u611f\u52a8\uff0c\u8bf4\u9053\uff1a\u201c\u4e24\u4f4d\u5982\u6b64\u91cd\u4e49\u8f7b\u751f\uff0c\u8bf7\u53d7\u5c0f\u53ef\u4e00\u62dc\u3002\u201d \u3000\u3000\u7c98\u3001\u901f\u4e8c\u4eba\u53cc\u53cc\u8dea\u5012\uff0c\u6309\u7167\u8499\u53e4\u4eba\u7684\u5927\u793c\uff0c\u5404\u81ea\u62b1\u7740\u674e\u601d\u5357\u7684\u4e00\u6761\u5927\u817f\uff0c\u543b\u4ed6\u7684\u811a\u3002\u7c98 \u4e0d\u7f55\u8bf4\u9053\uff1a\u201c\u76df\u4e3b\u5927\u4ec1\u5927\u4e49\uff0c\u6211\u90fd\u4e0d\u77e5\u9053\u600e\u6837\u624d\u80fd\u62a5\u7b54\u3002\u76df\u4e3b\u82e5\u518d\u591a\u793c\uff0c\u90a3\u5c31\u66f4\u662f\u6298\u715e\u6211 \u4e86\u3002\u201d\u901f\u4e0d\u53f0\u8bf4\u9053\uff1a\u201c\u76df\u4e3b\u8981\u6740\u4f59\u4e00\u4e2d\u62a5\u4ec7\uff0c\u6211\u4eec\u4e5f\u8981\u5c06\u4ed6\u9664\u6389\u624d\u80fd\u514d\u7978\uff0c\u5982\u4eca\u76df\u4e3b\u8981\u62a5 \u4ec7\uff0c\u6211\u4eec\u4e0d\u8fc7\u4ece\u65c1\u534f\u52a9\u800c\u5df2\uff0c\u76df\u4e3b\u4e3a\u4f55\u53cd\u800c\u591a\u8c22\u6211\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u5c06\u4ed6\u4eec\u4e8c\u4eba\u6276\u8d77\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u54b1\u4eec\u662f\u7978\u798f\u4e0e\u5171\uff0c\u809d\u80c6\u76f8\u7167\u7684\u670b\u53cb\u4e48\uff0c\u5ba2\u6c14\u8bdd\u5927 \u5bb6\u90fd\u4e0d\u5fc5\u8bf4\u4e86\u3002\u8bf7\u4e24\u4f4d\u591a\u591a\u4fdd\u91cd\uff0c\u8fdf\u5219\u5341\u5929\uff0c\u5c11\u5219\u4e94\u65e5\uff0c\u54b1\u4eec\u5728\u767d\u5bb6\u5e84\u76f8\u89c1\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9001\u522b\u4e86\u7c98\u3001\u901f\u4e8c\u4eba\uff0c\u56de\u8f6c\u5c71\u5be8\uff0c\u5b5f\u5c11\u521a\u7236\u5973\u548c\u6768\u5a49\u7b49\u4eba\u65e9\u5df2\u5728\u805a\u4e49\u5385\u7b49\u5019\uff0c\u5b5f\u5c11 \u521a\u95ee\u9053\uff1a\u201c\u53ef\u6709\u4ece\u8fd9\u4e24\u4e2a\u4eba\u7684\u53e3\u4e2d\u63a2\u542c\u5230\u4ec0\u4e48\u6d88\u606f\uff1f\u201d\u6768\u5a49\u8ddf\u7740\u7b11\u9053\uff1a\u201c\u542c\u8bf4\u8fd9\u4e24\u4e2a\u9791\u5b50\u5014 \u5f3a\u5f97\u5f88\uff01\u4e0d\u77e5\u4f60\u8fd9\u4f4d\u5148\u751f\u8bf4\u6cd5\uff0c\u80fd\u4e0d\u80fd\u4ee4\u4ed6\u4eec\u987d\u77f3\u70b9\u5934\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u7b11\u9053\uff1a\u201c\u4e0d\u4ec5\u662f\u987d\u77f3\u70b9\u5934\uff0c\u6211\u8fd8\u5f97\u5230\u4ed6\u4eec\u809d\u80c6\u76f8\u7167\u7684\u53cb\u8c0a\u5462\uff01\u6709\u4e00\u4e2a\u975e\u5e38\u91cd\u8981\u7684 \u6d88\u606f\uff0c\u8bf4\u51fa\u6765\u4e5f\u597d\u8ba9\u4f60\u9ad8\u5174\u3002\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u662f\u4ec0\u4e48\u6d88\u606f\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u54b1\u4eec\u7684\u4ec7\u4eba\u4f59\u4e00\u4e2d\u7684\u4e0b\u843d\uff0c\u6211\u5df2\u7ecf\u77e5\u9053\u4e86\uff01\u201d \u3000\u3000\u6768\u5a49\u53c8\u60ca\u53c8\u559c\uff0c\u8fde\u5fd9\u95ee\u9053\uff1a\u201c\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u5728\u767d\u4e07\u96c4\u7684\u5bb6\u4e2d\uff01\u201d \u3000\u3000\u5b5f\u5c11\u521a\u51fb\u6848\u8bf4\u9053\uff1a\u201c\u4e0d\u51fa\u6211\u4e4b\u6240\u6599\uff0c\u767d\u4e07\u96c4\u8fd9\u8001\u8d3c\u679c\u7136\u662f\u548c\u7aa6\u5b89\u5e73\u4e00\u6761\u8def\u7684\u3002\u4f59\u4e00\u4e2d\u8eb2 \u5728\u4ed6\u7684\u5bb6\u91cc\uff0c\u60f3\u5fc5\u662f\u5b9a\u6709\u9634\u8c0b\u7684\u4e86\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u8bf4\u9053\uff1a\u201c\u4e00\u70b9\u4e0d\u9519\u3002\u201d\u5f53\u4e0b\u5c06\u7c98\u3001\u901f\u4e8c\u4eba\u544a\u8bc9\u4ed6\u7684\u90a3\u4e9b\u4e8b\u60c5\u8f6c\u8ff0\u7ed9\u4f17\u4eba\u77e5\u9053\u3002 \u3000\u3000\u5b5f\u5c11\u521a\u6012\u9053\uff1a\u201c\u8fd9\u53ae\u4e3a\u864e\u4f5c\u6005\uff0c\u8fd8\u8981\u62c9\u4eba\u843d\u6c34\uff0c\u8fd9\u6837\u7684\u4eba\u51b3\u8ba1\u5bb9\u4ed6\u4e0d\u5f97\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u5f53\u7136\u5bb9\u4ed6\u4e0d\u5f97\u3002\u4e0d\u8fc7\uff0c\u4ed6\u662f\u6211\u7684\u6740\u7236\u4ec7\u4eba\uff0c\u6211\u60f3\u4eb2\u81ea\u62a5\u4ec7\uff01\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u4ed6\u4e5f\u662f\u6740\u5bb3\u6211\u5144\u957f\u7684\u4ec7\u4eba\u5440\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u4f60\u5f53\u7136\u662f\u5e94\u8be5\u548c\u6211\u4e00\u540c\u53bb\u7684\uff0c\u4f46\u5176\u4ed6\u7684\u4eba\uff0c\u6211\u5374\u4e0d\u60f3\u9ebb\u70e6\u4e86\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u767d\u4e07\u96c4\u867d\u7136\u662f\u4f60\u624b\u4e0b\u8d25\u5c06\uff0c\u4f46\u5230\u4e86\u4ed6\u7684\u5bb6\u671b\uff0c\u53ea\u6015\u4f60\u4eec\u5c31\u662f\u5be1\u4e0d\u654c\u4f17\u4e86\u3002 \u4f59\u4e00\u4e2d\u3001\u767d\u4e07\u96c4\u4e0d\u4ec5\u662f\u4f60\u4eec\u7684\u4ec7\u4eba\uff0c\u4e5f\u662f\u6b66\u6797\u516c\u654c\uff0c\u54b1\u4eec\u5927\u4f19\u513f\u53bb\u94f2\u5e73\u767d\u5bb6\u5e84\uff0c\u4e0d\u66f4\u597d \u4e48\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u4e0d\uff0c\u5927\u4f19\u513f\u90fd\u53bb\uff0c\u90a3\u53cd\u800c\u6253\u8349\u60ca\u86c7\u4e86\u3002\u7c98\u4e0d\u7f55\u548c\u901f\u4e0d\u53f0\u5df2\u7ecf\u7b54\u5e94\u4e86\u5728\u767d\u5bb6 \u5e84\u505a\u5185\u5e94\uff0c\u6211\u4eec\u51fa\u5176\u4e0d\u610f\u7684\u53bb\u591c\u88ad\u767d\u5bb6\u5e84\uff0c\u6599\u5e94\u53ef\u4ee5\u62a5\u5f97\u4e86\u8fd9\u4e2a\u5927\u4ec7\u3002\u201d \u3000\u3000\u5b5f\u5c11\u521a\u6c89\u541f\u534a\u664c\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4eba\u4e00\u591a\u98ce\u58f0\u5c31\u5bb9\u6613\u6cc4\u6f0f\uff0c\u7ed9\u4ed6\u4eec\u77e5\u9053\uff0c\u9003\u8dd1\u4e86\u53cd\u800c\u4e0d \u5999\u3002\u65e2\u7136\u4f60\u6709\u628a\u63e1\uff0c\u90a3\u5c31\u53ea\u662f\u4f60\u4eec\u4e24\u4eba\u53bb\u5427\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u542c\u5f97\u7236\u4eb2\u8fd9\u6837\u8bf4\uff0c\u60f3\u60f3\u4e5f\u6709\u9053\u7406\u3002\u867d\u7136\u4ecd\u662f\u4e3a\u674e\u601d\u5357\u62c5\u5fc3\uff0c\u4e5f\u5c31\u4e0d\u4fbf\u518d\u6301\u5f02\u8bae \u4e86\u3002 \u3000\u3000\u674e\u601d\u5357\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u8499\u53e4\u90a3\u8fb9\u7684\u60c5\u5f62\uff0c\u6211\u4e5f\u7ea6\u7565\u77e5\u9053\u4e86\u4e00\u4e9b\u3002\u6210\u5409\u601d\u6c57\u901d\u4e16\u4e4b\u540e\uff0c\u4ed6\u7684 \u513f\u5b50\u4e89\u593a\u5927\u6c57\u4e4b\u4f4d\u3002\u5982\u4eca\u5e93\u91cc\u5c14\u6cf0\u5927\u4f1a\u867d\u7136\u4e1a\u5df2\u53ec\u5f00\uff0c\u9009\u51fa\u4e86\u7a9d\u9614\u53f0\u7ee7\u627f\u6c57\u4f4d\uff0c\u4f46\u6839\u57fa\u672a \u56fa\uff0c\u7a9d\u9614\u53f0\u8fd8\u662f\u672a\u80fd\u514d\u9664\u5185\u987e\u4e4b\u5fe7\u3002\u6839\u636e\u5df2\u77e5\u9053\u7684\u6d88\u606f\u5224\u65ad\uff0c\u6700\u5c11\u5728\u4e00\u5e74\u534a\u8f7d\u4e4b\u5185\uff0c\u8499\u53e4\u5927 \u7ea6\u4e0d\u4f1a\u518d\u6b21\u51fa\u5175\u4fb5\u72af\u4e2d\u539f\u3002\u5f53\u7136\u54b1\u4eec\u7684\u4e49\u519b\u8fd8\u662f\u8981\u6212\u5907\u7684\uff0c\u4f46\u6211\u6682\u65f6\u79bb\u5f00\uff0c\u5219\u662f\u4e0d\u5927\u7d27\u8981 \u4e86\u3002\u5b5f\u5927\u4fa0\uff0c\u8bf7\u4f60\u5148\u56de\u7405\u739b\u5c71\u4ee3\u6211\u4e3b\u6301\u5927\u5c40\uff0c\u597d\u4e48\uff1f\u201d \u3000\u3000\u5b5f\u5c11\u521a\u7b11\u9053\uff1a\u201c\u6211\u8fd9\u95f2\u4e91\u91ce\u9e64\u4e4b\u8eab\uff0c\u53ea\u6015\u505a\u4e0d\u6765\u76df\u4e3b\u5462\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u5b5f\u5927\u4fa0\u82e5\u6015\u9ebb\u70e6\uff0c\u90a3\u4e48\u8bf7\u5c60\u51e4\u6682\u6444\u76df\u4e3b\u4e5f\u884c\u3002\u4e0d\u8fc7\u4ecd\u662f\u8bf7\u5b5f\u5927\u4fa0\u5e2e\u5e2e\u5979\u7684 \u5fd9\u3002\u891a\u5144\u3001\u8c37\u5144\uff0c\u6211\u77e5\u9053\u4f60\u4eec\u6709\u6e05\u7406\u5e08\u95e8\u7684\u5927\u4e8b\u8981\u529e\uff0c\u4f46\u82e5\u662f\u53ef\u4ee5\u7a0d\u7f13\u4e9b\u65f6\uff0c\u80fd\u591f\u548c\u5b5f\u5927\u4fa0 \u4e00\u53bb\u7405\u739b\u5c71\u5e2e\u5e2e\u5c60\u51e4\u7684\u5fd9\uff0c\u7b49\u6211\u56de\u6765\uff0c\u90a3\u5c31\u66f4\u597d\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u3001\u8c37\u6db5\u865a\u9f50\u58f0\u7b54\u5141\u3002\u5b5f\u5c11\u521a\u4e5f\u9053\uff1a\u201c\u597d\uff0c\u4f60\u653e\u5fc3\u53bb\u5427\uff0c\u6211\u4f1a\u66ff\u4f60\u5b89\u6392\u59a5\u5f53\u7684\u3002\u4e49 \u519b\u76ee\u524d\u6ca1\u6709\u5927\u4ed7\u8981\u6253\uff0c\u5c60\u51e4\u6682\u6444\u76df\u4e3b\u4e4b\u4f4d\uff0c\u6211\u60f3\u5979\u4e5f\u662f\u53ef\u4ee5\u6109\u5feb\u80dc\u4efb\u7684\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u8c22\u8fc7\u4e86\u5b5f\u5c11\u521a\uff0c\u5373\u65e5\u4fbf\u4e0e\u6768\u5a49\u4e0b\u5c71\u3002 \u3000\u3000\u674e\u3001\u6768\u4e8c\u4eba\u8d70\u540e\uff0c\u5b5f\u660e\u971e\u9053\uff1a\u201c\u7239\u7239\uff0c\u867d\u8bf4\u662f\u4eba\u591a\u53bb\u6050\u9632\u6253\u8349\u60ca\u86c7\uff0c\u4f46\u4e5f\u5e94\u8be5\u53bb\u591a\u4e09\u4e24 \u4e2a\u5440\uff0c\u4e3a\u4ec0\u4e48\u4f60\u53ea\u662f\u8ba9\u4ed6\u4eec\u4e24\u4e2a\u4eba\u53bb\uff1f\u201d \u3000\u3000\u5b5f\u5c11\u521a\u7b11\u9053\uff1a\u201c\u4ed6\u4eec\u662f\u4e3a\u7236\u5144\u62a5\u4ec7\uff0c\u54b1\u4eec\u660e\u91cc\u53bb\u52a9\u4ed6\u53cd\u800c\u4e0d\u597d\u3002\u4e0d\u8fc7\uff0c\u4f60\u4e5f\u4e0d\u7528\u62c5\u5fc3\uff0c \u6211\u5df2\u51b3\u5b9a\u6697\u5730\u91cc\u53bb\u5e2e\u5fd9\u4ed6\u4e86\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u559c\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\uff0c\u8fd9\u6211\u53ef\u662f\u602a\u9519\u7239\u7239\u4e86\u3002\u201d \u3000\u3000\u5b5f\u5c11\u521a\u9053\uff1a\u201c\u6211\u6697\u5730\u91cc\u5230\u767d\u5bb6\u5e84\uff0c\u5018\u82e5\u4ed6\u4eec\u5df2\u662f\u8db3\u4ee5\u5bf9\u4ed8\u5f97\u4e86\u767d\u5bb6\u5e84\u90a3\u4e00\u73ed\u4eba\uff0c\u6211\u5c31\u6839 \u672c\u4e0d\u9732\u9762\u4e86\uff0c\u4e8b\u540e\u4f60\u4e5f\u4e0d\u8981\u548c\u4ed6\u4eec\u8bf4\u3002\u201d \u3000\u3000\u5b5f\u660e\u971e\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u5f53\u7136\uff0c\u96be\u9053\u8fd8\u8981\u601d\u5357\u548c\u6768\u5a49\u9886\u54b1\u4eec\u4e00\u4e2a\u4eba\u60c5\u4e48\uff1f\u201d \u3000\u3000\u5b5f\u660e\u971e\u6027\u60c5\u723d\u6717\uff0c\u5979\u628a\u674e\u601d\u5357\u5f53\u4f5c\u6700\u597d\u7684\u670b\u53cb\u770b\u5f85\uff0c\u5bf9\u597d\u670b\u53cb\u7684\u5173\u5fc3\uff0c\u5728\u5979\u8ba4\u4e3a\u662f\u7406\u6240 \u5f53\u7136\u7684\u4e8b\uff0c\u56e0\u6b64\u5728\u891a\u4e91\u5cf0\u7684\u9762\u524d\u4e1d\u6beb\u4e0d\u52a0\u63a9\u9970\uff0c\u891a\u4e91\u5cf0\u521d\u65f6\u4e0d\u514d\u6709\u70b9\u918b\u610f\uff0c\u4f46\u5728\u770b\u51fa\u5979\u53ea\u662f \u5bf9\u4e00\u4f4d\u597d\u670b\u53cb\u7684\u771f\u631a\u5173\u6000\u4e4b\u540e\uff0c\u5374\u4e0d\u7531\u5f97\u5bf9\u5979\u66f4\u4e3a\u656c\u4f69\u4e86\u3002 \u3000\u3000\u6768\u5a49\u5bf9\u674e\u601d\u5357\u4e5f\u662f\u4e00\u6837\uff0c\u4e24\u4eba\u52ab\u540e\u76f8\u9022\uff0c\u8bef\u4f1a\u90fd\u5df2\u6d88\u9664\uff0c\u4e24\u9897\u5fc3\u662f\u6bd4\u4ee5\u524d\u66f4\u4e3a\u63a5\u8fd1\u4e86\u3002 \u8def\u4e0a\u6768\u5a49\u5c31\u548c\u674e\u601d\u5357\u8c08\u8d77\u4e86\u660e\u6167\u516c\u4e3b\u6765\u3002 \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u4f60\u53ef\u601d\u5ff5\u5979\u4e48\uff1f\u201d\u674e\u601d\u5357\u6014\u4e86\u4e00\u6014\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u7684\u5fc3\u4e0a\u53ea\u6709\u4f60\uff0c\u96be\u9053\u4f60\u73b0\u5728 \u8fd8\u6709\u6000\u7591\uff1f\u201d\u6768\u5a49\u5657\u55e4\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u81ea\u5df1\u5148\u72af\u4e86\u7591\u5fc3\u75c5\uff0c\u5374\u98a0\u5012\u8fc7\u6765\u8bf4\u6211\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u201d\u6768\u5a49\u9053\uff1a\u201c\u6000\u5ff5\u670b\u53cb\uff0c\u4eba\u4e4b\u5e38\u60c5\u3002\u4f60\u4ee5\u4e3a\u6211\u8bf4\u4f60\u4ec0\u4e48\uff1f\u201d\u674e\u601d\u5357 \u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u4f60\u8bf4\u7684\u662f\u670b\u53cb\u4e4b\u60c5\u3002\u5bf9\u4e0d\u4f4f\uff0c\u5012\u662f\u6211\u8bef\u4f1a\u4e86\u3002\u201d \u3000\u3000\u6768\u5a49\u671b\u7740\u674e\u601d\u5357\u7684\u773c\u775b\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u8bf4\u8001\u5b9e\u8bdd\uff0c\u5728\u8499\u53e4\u7684\u65f6\u5019\uff0c\u6211\u662f\u5bf9\u5979\u6709\u70b9\u5992\u5fcc \u7684\uff0c\u73b0\u5728\u6211\u53cd\u800c\u89c9\u5f97\u5979\u53ef\u601c\u4e86\u3002\u53ef\u60dc\u8fd9\u4e16\u754c\u4e0a\u53ea\u6709\u4e00\u4e2a\u4f60\uff0c\u53c8\u4e0d\u80fd\u628a\u4f60\u5206\u5f00\u4e24\u534a\uff0c\u5426\u5219\u6211\u5012 \u662f\u613f\u610f\u628a\u4f60\u8ba9\u7ed9\u5979\u5462\u3002\u4e0d\u8fc7\uff0c\u6211\u867d\u7136\u4e0d\u80fd\u628a\u4f60\u8ba9\u7ed9\u5979\uff0c\u5374\u4e5f\u5e0c\u671b\u4f60\u80fd\u591f\u7ed9\u5979\u5b89\u6170\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u4e0d\u77e5\u5176\u4e2d\u539f\u59d4\uff0c\u5012\u662f\u6709\u70b9\u8be7\u5f02\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5a49\u59b9\u7ecf\u8fc7\u4e86\u8fd9\u573a\u52ab\u96be\uff0c\u5012\u662f\u53d8\u5f97\u80f8 \u895f\u5f00\u9614\u4e86\u3002\u4f46\u5374\u4e0d\u77e5\u9053\u5979\u8bf4\u7684\u662f\u771f\u662f\u5047\uff1f\u201d\u53ea\u6015\u6768\u5a49\u662f\u7528\u8bf4\u8bdd\u8bd5\u63a2\u4ed6\uff0c\u4e00\u65f6\u95f4\u7adf\u4e0d\u6562\u642d\u8bdd\u3002 \u3000\u3000\u6768\u5a49\u597d\u4f3c\u731c\u5230\u4e86\u674e\u601d\u5357\u7684\u5fc3\u610f\uff0c\u90d1\u91cd\u8bf4\u9053\uff1a\u201c\u6211\u8bf4\u7684\u662f\u771f\u5fc3\u8bdd\u3002\u5357\u54e5\uff0c\u660e\u6167\u516c\u4e3b\u56fa\u7136\u5bf9 \u4f60\u597d\uff0c\u4f46\u548c\u6211\u4e5f\u662f\u597d\u670b\u53cb\u5462\u3002\u6211\u66fe\u7ecf\u505a\u8fc7\u5979\u7684\u4f8d\u5973\uff0c\u4f60\u60f3\u4e0d\u5230\u5427\uff1f\u201d\u5f53\u4e0b\u628a\u79bb\u6563\u4e4b\u540e\u7684\u7ecf\u8fc7 \u4ee5\u53ca\u5982\u4f55\u78b0\u4e0a\u963f\u76d6\uff0c\u5982\u4f55\u6df7\u5165\u8499\u53e4\u519b\u8425\uff0c\u5982\u4f55\u5de7\u9047\u660e\u6167\u516c\u4e3b\uff0c\u5f97\u5230\u5979\u7684\u5e87\u62a4\uff0c\u53c8\u5982\u4f55\u884c\u523a\u4f59 \u4e00\u4e2d\u4e0d\u6210\uff0c\u9003\u4e86\u51fa\u6765\u7b49\u7b49\u4e8b\u60c5\uff0c\u4e00\u4e00\u544a\u8bc9\u4e86\u674e\u601d\u5357\u3002 \u3000\u3000\u674e\u601d\u5357\u53c8\u662f\u8be7\u5f02\uff0c\u53c8\u662f\u6b22\u559c\uff0c\u8bf4\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u660e\u6167\u516c\u4e3b\u5012\u662f\u54b1\u4eec\u7684\u6069\u4eba\u4e86\u3002\u201d\u5f53\u4e0b \u4e5f\u628a\u4ece\u7c98\u4e0d\u7f55\u548c\u901f\u4e0d\u53f0\u90a3\u513f\u542c\u6765\u7684\u6d88\u606f\u544a\u8bc9\u6768\u5a49\u3002 \u3000\u3000\u6768\u5a49\u7b11\u9053\uff1a\u201c\u6211\u8fd8\u6ca1\u6709\u8bf4\u5b8c\u5462\u3002\u4f60\u8fd9\u4e2a\u6d88\u606f\u6211\u65e9\u5df2\u77e5\u9053\u4e86\u3002\u4f46\u4f60\u53ef\u77e5\u660e\u6167\u516c\u4e3b\u56e0\u4f55\u9003\u51fa \u8499\u53e4\u4e48\uff1f\u201d\u674e\u601d\u5357\u6447\u4e86\u6447\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u7c98\u4e0d\u7f55\u6ca1\u6709\u8bf4\uff0c\u6211\u4e5f\u672a\u66fe\u95ee\u4ed6\u3002\u201d\u5176\u5b9e\u4ed6\u5df2\u662f\u731c\u5230\u4e86 \u51e0\u5206\u7684\u3002 \u3000\u3000\u6768\u5a49\u7b11\u9053\uff1a\u201c\u5979\u662f\u56e0\u4e3a\u4e0d\u613f\u4e0b\u5ac1\u9547\u56fd\u738b\u5b50\uff0c\u6240\u4ee5\u624d\u7279\u5730\u9003\u5230\u4e2d\u539f\u7684\u3002\u9003\u5230\u4e2d\u539f\uff0c\u4e3a\u7684\u5374 \u6b63\u662f\u627e\u4f60\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u4f60\u53c8\u6765\u548c\u6211\u8bf4\u7b11\u4e86\u3002\u201d\u4f46\u5fc3\u91cc\u5374\u662f\u660e\u767d\uff0c\u6768\u5a49\u5e76\u975e\u8bf4\u7b11\u3002 \u3000\u3000\u6768\u5a49\u8bf4\u9053\uff1a\u201c\u5357\u54e5\uff0c\u7ecf\u8fc7\u4e86\u8fd9\u573a\u52ab\u96be\uff0c\u6211\u5df2\u77e5\u9053\u4f60\u662f\u771f\u5fc3\u5bf9\u6211\u4e86\u3002\u4f60\u4e5f\u4e0d\u5fc5\u6000\u7591\u6211\u8fd8\u662f \u50cf\u4ece\u524d\u4e00\u6837\u7684\u6c14\u91cf\u72ed\u7a84\u5566\u3002\u201d\u4e24\u4eba\u9f50\u8f94\u800c\u884c\uff0c\u8bf4\u5230\u6b64\u5904\uff0c\u4e0d\u77e5\u4e0d\u89c9\u5730\u4f38\u51fa\u624b\u76f8\u63e1\uff0c\u5927\u5bb6\u90fd\u89c9 \u5f97\u751c\u4e1d\u4e1d\u7684\uff0c\u4e24\u9897\u5fc3\u597d\u50cf\u5408\u6210\u4e86\u4e00\u9897\u4e86\u3002 \u3000\u3000\u6768\u5a49\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u524d\u51e0\u5929\u6211\u66fe\u78b0\u4e0a\u660e\u6167\u516c\u4e3b\uff0c\u6240\u4ee5\u6211\u4e0d\u4f46\u77e5\u9053\u5979\u8981\u627e\u4f60\uff0c\u800c\u4e14\u6211\u8fd8\u53ef\u4ee5 \u5411\u4f60\u62c5\u4fdd\uff0c\u53ea\u8981\u54b1\u4eec\u4ece\u767d\u5bb6\u5e84\u56de\u6765\uff0c\u4f60\u5c31\u53ef\u4ee5\u89c1\u7740\u5979\u4e86\u3002\u201d\u674e\u601d\u5357\u5947\u9053\uff1a\u201c\u771f\u7684\u5417\uff1f\u5979\u73b0\u5728 \u54ea\u513f\uff1f\u201d\u626c\u5a49\u9053\uff1a\u201c\u5c31\u5728\u5c60\u51e4\u90a3\u513f\uff0c\u662f\u6211\u4ecb\u7ecd\u5979\u53bb\u7684\u3002\u201d\u674e\u601d\u5357\u77e5\u9053\u8fd9\u4e2a\u6d88\u606f\uff0c\u5012\u662f\u4e0d\u89c9\u4e00 \u60ca\u3002\u6b63\u662f\uff1a \u3000\u3000\u4f46\u613f\u826f\u670b\u6b23\u6709\u6258\uff0c\u6545\u4eba\u60c5\u91cd\u8fd1\u4f55\u5982\uff1f \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b\u2014\u2014\u7b2c\u56db\u5341\u4e09\u56de\u3000\u6d6a\u5b50\u56de\u5934\u539f\u662f\u5047\u3000\u91d1\u679d\u4ea5\u56fd\u6094\u60c5\u75f4 \u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b \u7b2c\u56db\u5341\u4e09\u56de\u3000\u6d6a\u5b50\u56de\u5934\u539f\u662f\u5047\u3000\u91d1\u679d\u4ea5\u56fd\u6094\u60c5\u75f4 \u3000\u3000\u6768\u5a49\u5bdf\u89c9\u674e\u601d\u5357\u9762\u8272\u6709\u5f02\uff0c\u8be7\u9053\uff1a\u201c\u5357\u54e5\uff0c\u4f60\u5728\u60f3\u4e9b\u4ec0\u4e48\uff1f\u201d\u674e\u601d\u5357\u9053\uff1a\u201c\u4f60\u628a\u660e\u6167\u516c \u4e3b\u8bf7\u5230\u5c60\u51e4\u90a3\u513f\uff0c\u53ea\u6015\u6709\u4e9b\u4e0d\u5999\u3002\u201d\u6768\u5a49\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u674e\u601d\u5357\u9053\uff1a\u201c\u4f60\u5fd8\u8bb0\u4e86\u5c60\u51e4\u7684\u54e5 \u54e5\u4e48\uff1f\u90a3\u65e5\u4ed6\u548c\u6211\u6bd4\u6b66\u53d7\u4e86\u4f24\uff0c\u8d56\u5728\u5bb6\u91cc\u4e0d\u80af\u8d70\uff0c\u5018\u82e5\u7ed9\u4ed6\u77e5\u9053\u660e\u6167\u516c\u4e3b\u7684\u6765\u5386\uff0c\u53ea\u6015\u53c8\u8981 \u751f\u51fa\u4e8b\u6765\u3002\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u4f60\u6015\u5c60\u9f99\u52a0\u5bb3\u4e8e\u5979\uff1f\u201d\u674e\u601d\u5357\u9053\uff1a\u201c\u8fd9\u53ae\u5fc3\u72e0\u624b\u8fa3\uff0c\u8be1\u8ba1\u591a\u7aef\uff0c\u5b9e\u662f\u4e0d\u53ef\u4e0d \u9632\uff01\u201d\u6768\u5a49\u9053\uff1a\u201c\u8bf4\u8d77\u6765\u6211\u5012\u8981\u602a\u4f60\u5462\uff0c\u90a3\u5929\u4f60\u4e3a\u4f55\u4e0d\u5c06\u4ed6\u4e00\u5251\u6740\u4e86\uff1f\u201d\u674e\u601d\u5357\u9053\uff1a\u201c\u6211\u8fd9 \u662f\u770b\u5728\u5c60\u51e4\u7684\u4efd\u4e0a\u3002\u201d\u6768\u5a49\u9053\uff1a\u201c\u4f9d\u4f60\u770b\u5c60\u51e4\u8fd9\u4eba\u600e\u6837\uff0c\u82e5\u7136\u62ff\u5979\u548c\u660e\u971e\u76f8\u6bd4\uff0c\u53c8\u662f\u5982 \u4f55\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u4e24\u4eba\u90fd\u662f\u4e00\u822c\u723d\u76f4\u7684\u813e\u6c14\uff0c\u4f46\u5c60\u51e4\u5904\u4e8b\u5219\u4f3c\u4e4e\u66f4\u6709\u51b3\u65ad\uff0c\u4e5f\u6bd4\u5b5f\u660e\u971e\u66f4\u4e0d \u8bb2\u60c5\u9762\uff0c\u4e0d\u8fc7\uff0c\u6211\u5b81\u53ef\u8ba9\u5979\u81ea\u5df1\u5927\u4e49\u706d\u4eb2\uff0c\u5374\u4e0d\u4fbf\u5f53\u7740\u5979\u7684\u9762\u6740\u4e86\u5979\u7684\u54e5\u54e5\u3002\u201d\u6768\u5a49\u70b9\u4e86\u70b9 \u5934\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5018\u82e5\u4e0d\u662f\u56e0\u4e3a\u5c60\u592b\u4eba\u6eba\u7231\u513f\u5b50\uff0c\u5c60\u9f99\u7109\u80fd\u8fd8\u6709\u547d\u5728\uff1f\u5c60\u51e4\u5373\u4f7f\u4e0d\u4f1a\u4eb2\u624b \u6740\u4ed6\uff0c\u4e5f\u51b3\u4e0d\u4f1a\u5bb9\u4ed6\u7559\u5728\u5bb6\u4e2d\u517b\u4f24\u7684\u4e86\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5a49\u59b9\uff0c\u4f60\u4ece\u524d\u4f3c\u4e4e\u5bf9\u5c60\u51e4\u5e76\u65e0\u597d\u611f\uff0c\u73b0\u5728\u5374\u662f\u548c\u6211\u7684\u770b\u6cd5\u76f8 \u540c\uff0c\u751a\u81f3\u6bd4\u6211\u66f4\u591a\u7684\u79f0\u8d5e\u5979\u4e86\u3002\u201d \u3000\u3000\u6768\u5a49\u8bf4\u9053\uff1a\u201c\u6211\u5e76\u975e\u5bf9\u5979\u62b1\u6709\u6076\u611f\uff0c\u4e0d\u8fc7\u662f\u6211\u4eec\u7684\u6027\u60c5\u4e0d\u5927\u76f8\u540c\u7f62\u4e86\u3002\u4ee5\u524d\u6211\u5c1a\u672a\u6df1\u77e5 \u5176\u4eba\uff0c\u53ea\u89c9\u5979\u597d\u4f3c\u5bf9\u6211\u62b1\u6709\u6210\u89c1\uff0c\u5904\u5904\u5e2e\u52a9\u5b5f\u660e\u971e\u3002\u5176\u5b9e\u6211\u4f55\u5c1d\u4e0d\u4e5f\u662f\u5bf9\u5979\u62b1\u6709\u6210\u89c1\uff1f\u76f4\u5230 \u7eff\u6797\u5927\u4f1a\u90a3\u5929\uff0c\u6211\u624d\u53d1\u89c9\u5979\u662f\u4e2a\u6df1\u660e\u5927\u4e49\uff0c\u6562\u4f5c\u6562\u4e3a\u7684\u5973\u4e2d\u6770\uff0c\u5f88\u662f\u60ed\u6127\uff0c\u4e3a\u4ec0\u4e48\u4ee5\u524d\u6ca1\u6709 \u770b\u5230\u5979\u8fd9\u4e48\u591a\u957f\u5904\u3002\u201d\u63a5\u7740\u53c8\u9053\uff1a\u201c\u4ed4\u7ec6\u60f3\u6765\uff0c\u5176\u5b9e\u6211\u8fd8\u4e0d\u4ec5\u662f\u5bf9\u5c60\u51e4\u5982\u6b64\uff0c\u5bf9\u5b5f\u59d1\u5a18\u548c\u660e \u6167\u516c\u4e3b\u4e5f\u662f\u4e00\u6837\uff0c\u90fd\u662f\u6e10\u6e10\u624d\u53d1\u73b0\u5979\u4eec\u7684\u597d\u5904\u7684\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u7b11\u9053\uff1a\u201c\u4e00\u822c\u4eba\u603b\u662f\u5bb9\u6613\u770b\u5230\u522b\u4eba\u7684\u77ed\u5904\uff0c\u4e0d\u5bb9\u6613\u770b\u5230\u522b\u4eba\u7684\u957f\u5904\u7684\uff0c\u4f60\u73b0\u5728\u61c2 \u5f97\u7559\u5fc3\u6ce8\u610f\u522b\u4eba\u7684\u957f\u5904\uff0c\u5b9e\u5728\u662f\u96be\u80fd\u53ef\u8d35\uff01\u4e0d\u8fc7\uff0c\u4f60\u4f55\u4ee5\u4ece\u660e\u6167\u516c\u4e3b\u7684\u4e8b\u60c5\u8c08\u5230\u4e86\u5c60\u51e4\u7684\u4e3a \u4eba\uff0c\u5176\u4e2d\u662f\u5426\u5c1a\u6709\u56e0\u7531\uff1f\u201d \u3000\u3000\u6768\u5a49\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u62c5\u5fc3\u5c60\u9f99\u52a0\u5bb3\u660e\u6167\u516c\u4e3b\u5417\uff1f\u6211\u544a\u8bc9\u4f60\u4e24\u4ef6\u4e8b\u60c5\u5427\u3002\u7b2c\u4e00\u4ef6\u4e8b\u60c5\uff0c\u5c60 \u9f99\u5982\u4eca\u5df2\u7ecf\u4e0d\u5728\u7405\u739b\u5c71\uff0c\u636e\u6211\u63a8\u6d4b\uff0c\u6050\u6015\u5c31\u662f\u7ed9\u4ed6\u59b9\u59b9\u9a71\u9010\u7684\u3002\u7b2c\u4e8c\u4ef6\uff0c\u5c60\u9f99\u65e9\u5df2\u77e5\u9053\u660e\u6167 \u516c\u4e3b\u7684\u8eab\u4efd\uff0c\u53ea\u662f\u4e0d\u77e5\u9053\u5979\u9003\u5a5a\u8fd9\u4e2a\u79d8\u5bc6\u800c\u5df2\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u5927\u4e3a\u5947\u602a\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\u5f97\u8fd9\u6837\u6e05\u695a\uff1f\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u6211\u548c\u660e\u6167\u516c\u4e3b\u66fe\u7ecf\u5728\u4e00\u5ea7\u53e4\u5e99\u78b0\u4e0a\u5c60\u9f99\uff0c\u4ed6\u662f\u548c\u8d3a\u4e5d\u516c\u7b49\u4eba\u4e00\u4f19\u7684\uff0c\u6211\u4eec\u5148 \u78b0\u4e0a\u4ed6\uff0c\u5176\u540e\u624d\u78b0\u4e0a\u9633\u575a\u767d\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u4ed6\u89c1\u7740\u4e86\u660e\u6167\u516c\u4e3b\u4e4b\u540e\u600e\u6837\uff1f\u201d \u3000\u3000\u6768\u5a49\u7b11\u9053\uff1a\u201c\u4e00\u8138\u5b54\u8bda\u60f6\u8bda\u6050\u7684\u795e\u6c14\uff0c\u53e3\u53e3\u58f0\u58f0\u5411\u660e\u6167\u516c\u4e3b\u8bf7\u7f6a\uff0c\u53ea\u5dee\u6ca1\u6709\u8dea\u4e0b\u78d5 \u5934\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u201c\u5478\u201d\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u771f\u4e0d\u8981\u8138\uff0c\u60f3\u4e0d\u5230\u5c60\u767e\u57ce\u4e00\u4e16\u82f1\u96c4\uff0c\u751f\u4e0b\u4e86\u8fd9\u6837\u4e00\u4e2a\u513f \u5b50\u3002\u201d \u3000\u3000\u6768\u5a49\u8bf4\u9053\uff1a\u201c\u53ef\u662f\u6b63\u56e0\u4e3a\u4ed6\u8981\u5df4\u7ed3\u660e\u6167\u516c\u4e3b\uff0c\u54b1\u4eec\u53ef\u5c31\u7528\u4e0d\u7740\u62c5\u5fc3\u4e86\u3002\u4ed6\u8fd9\u6b21\u591a\u534a\u662f\u7ed9 \u5c60\u51e4\u8d76\u51fa\u6765\u7684\uff0c\u7405\u739b\u5c71\u4e0a\u7684\u5927\u5c0f\u5934\u76ee\u6ca1\u6709\u4e00\u4e2a\u4e0d\u9119\u5f03\u4ed6\uff0c\u8c05\u4ed6\u4e5f\u4e0d\u6562\u518d\u56de\u53bb\u3002\u53c8\u5373\u4f7f\u4ed6\u6709\u90a3 \u6837\u7684\u8138\u76ae\u80c6\u6562\u56de\u53bb\uff0c\u89c1\u7740\u4e86\u660e\u6167\u516c\u4e3b\uff0c\u4ed6\u4e5f\u53ea\u6709\u8ba8\u597d\u7684\u4efd\u513f\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u4f46\u613f\u5982\u4f60\u6240\u6599\u3002\u201d\u663e\u7136\u8fd8\u662f\u6709\u70b9\u4e0d\u5927\u653e\u5fc3\uff0c\u6768\u5a49\u7b11\u9053\uff1a\u201c\u4f60\u653e\u5fc3\u4e0d\u4e0b\uff0c\u54b1 \u4eec\u53ef\u4ee5\u5148\u56de\u5230\u7405\u739b\u5c71\uff0c\u7136\u540e\u624d\u53bb\u627e\u4f59\u4e00\u4e2d\u7b97\u5e10\u3002\u201d\u674e\u601d\u5357\u9053\uff1a\u201c\u4e00\u6765\u4e00\u56de\uff0c\u5c11\u8bf4\u4e5f\u5f97\u5341\u5929\u534a \u6708\uff0c\u4f59\u4e00\u4e2d\u5c82\u80fd\u5728\u767d\u5bb6\u5e84\u5750\u5f85\u54b1\u4eec\uff1f\u4e24\u4e8b\u6bd4\u8f83\uff0c\u8fd8\u662f\u62a5\u4ec7\u8981\u7d27\u3002\u201d \u3000\u3000\u6768\u5a49\u672c\u6765\u662f\u548c\u674e\u601d\u5357\u8bf4\u7b11\u7684\uff0c\u89c1\u4ed6\u8fd9\u6837\u8ba4\u771f\uff0c\u4e0d\u4fbf\u518d\u5f00\u4ed6\u7684\u73a9\u7b11\uff0c\u5f53\u4e0b\u4e5f\u4f5c\u51fa\u4e00\u526f\u90d1\u91cd \u7684\u795e\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f59\u4e00\u4e2d\u4e0d\u4ec5\u662f\u54b1\u4eec\u7684\u4ec7\u4eba\uff0c\u4e5f\u662f\u6c49\u4eba\u7684\u516c\u654c\uff0c\u5f53\u7136\u662f\u5148\u53bb\u9664\u4ed6\u8981 \u7d27\uff0c\u5feb\u70b9\u513f\u8d76\u8def\u5427\u3002\u201d \u3000\u3000\u6768\u5a49\u53ef\u6ca1\u60f3\u5230\u5c60\u9f99\u7684\u9634\u9669\u72e0\u6bd2\u5c1a\u5728\u5979\u4f30\u8ba1\u4e4b\u4e0a\u3002\u5979\u672c\u4ee5\u4e3a\u660e\u6167\u516c\u4e3b\u6295\u5954\u4e86\u5c60\u51e4\uff0c\u5c31\u53ef\u4ee5 \u5b89\u7136\u65e0\u4e8b\u7684\u3002\u5374\u4e0d\u6599\u4e8b\u60c5\u7684\u7ed3\u679c\uff0c\u7adf\u662f\u5927\u51fa\u5979\u610f\u6599\u4e4b\u5916\u3002 \u3000\u3000\u82b1\u5f00\u4e24\u6735\uff0c\u5404\u8868\u4e00\u679d\u3002\u674e\u3001\u6768\u4e8c\u4eba\u524d\u5f80\u767d\u5bb6\u5e84\u62a5\u4ec7\u4e4b\u4e8b\uff0c\u6682\u4e14\u6401\u4e0b\u4e0d\u8868\uff0c\u5148\u8868\u660e\u6167\u516c\u4e3b \u7684\u906d\u9047\u3002 \u3000\u3000\u4e14\u8bf4\u660e\u6167\u516c\u4e3b\u5e26\u4e86\u963f\u76d6\u3001\u5361\u6d1b\u4e1d\u4e8c\u4eba\u524d\u5f80\u7405\u739b\u5c71\u6295\u5954\u5c60\u51e4\uff0c\u4ed6\u4eec\u4e09\u4eba\u867d\u7136\u6539\u4e86\u6c49\u4eba\u88c5 \u675f\uff0c\u4f46\u963f\u76d6\u7684\u76f8\u8c8c\u5374\u662f\u4e0d\u50cf\u6c49\u4eba\uff0c\u4ed6\u4eec\u7684\u6c49\u8bdd\u4e5f\u8bf4\u5f97\u4e0d\u5f88\u6d41\u5229\uff0c\u5c71\u5be8\u4e0a\u7684\u5934\u76ee\u8d77\u4e86\u7591\u5fc3\uff0c\u5bf9 \u4ed6\u4ecd\u518d\u4e09\u76d8\u95ee\uff0c\u963f\u76d6\u5f53\u7136\u4e0d\u80af\u5410\u9732\u516c\u4e3b\u7684\u8eab\u4efd\uff0c\u548c\u5c71\u5be8\u7684\u5934\u76ee\u51e0\u4e4e\u51b2\u7a81\u8d77\u6765\u3002 \u3000\u3000\u5e78\u597d\u5c60\u51e4\u6070\u5de7\u51fa\u5de1\uff0c\u78b0\u4e0a\u6b64\u4e8b\uff0c\u90a3\u5934\u76ee\u7980\u544a\u5979\u9053\uff1a\u201c\u8fd9\u4e09\u4e2a\u4eba\u8bf4\u662f\u6709\u4e00\u4f4d\u59d3\u6768\u7684\u59d1\u5a18\u53eb \u4ed6\u4eec\u6765\u89c1\u4f60\u7684\u3002\u53ef\u662f\u6211\u4eec\u4ece\u6ca1\u542c\u8fc7\u90a3\u4f4d\u59d1\u5a18\u7684\u540d\u5b57\uff0c\u770b\u4ed6\u4eec\u7684\u6837\u5b50\u53c8\u4e0d\u50cf\u662f\u6c49\u4eba\uff0c\u6240\u4ee5\u6211\u4eec \u4e0d\u6562\u653e\u4ed6\u4eec\u8fdb\u53bb\u3002\u201d \u3000\u3000\u5c60\u51e4\u5403\u4e86\u4e00\u60ca\uff0c\u8fde\u5fd9\u95ee\u9053\uff1a\u201c\u90a3\u4f4d\u6768\u59d1\u5a18\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u201d \u3000\u3000\u90a3\u5934\u76ee\u9053\uff1a\u201c\u53eb\u505a\u6768\u5a49\u3002\u201d \u3000\u3000\u5c60\u51e4\u5fd9\u5411\u660e\u6167\u516c\u4e3b\u65bd\u793c\uff0c\u8bf4\u9053\uff1a\u201c\u5bf9\u4e0d\u4f4f\uff0c\u8fd9\u4f4d\u6768\u59d1\u5a18\u6b63\u662f\u6211\u60f3\u8981\u89c1\u7684\u4e00\u4f4d\u670b\u53cb\uff0c\u53ef\u662f \u4ed6\u4eec\u5e76\u4e0d\u77e5\u9053\uff0c\u8bf7\u4f60\u522b\u602a\u4ed6\u4eec\u5931\u793c\uff0c\u4f60\u662f\u54ea\u91cc\u4eba\u6c0f\uff1f\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u9053\uff1a\u201c\u6211\u4eec\u548c\u6768\u59d1\u5a18\u662f\u5728\u8499\u53e4\u76f8\u8bc6\u7684\u3002\u8fd9\u91cc\u6709\u5979\u7684\u4e00\u5c01\u4fe1\uff0c\u8bf7\u4f60\u8fc7\u76ee\u3002\u201d\u5979\u5982 \u6b64\u8bf4\u6cd5\uff0c\u867d\u7136\u6ca1\u6709\u660e\u767d\u8bf4\u51fa\u81ea\u5df1\u662f\u8499\u53e4\u4eba\uff0c\u5374\u4e5f\u7b49\u4e8e\u628a\u7c4d\u8d2f\u8bf4\u51fa\u6765\u4e86\u3002 \u3000\u3000\u6768\u5a49\u7684\u4fe1\u4e5f\u5e76\u6ca1\u6709\u8bf4\u660e\u660e\u6167\u516c\u4e3b\u7684\u8eab\u4efd\uff0c\u53ea\u8bf4\u6765\u7684\u4e09\u4eba\u662f\u81ea\u5df1\u548c\u674e\u601d\u5357\u7684\u597d\u670b\u53cb\uff0c\u6c42\u5c60 \u51e4\u6536\u7559\uff0c\u4fe1\u4e2d\u987a\u5e26\u63d0\u53ca\u5979\u4e0d\u8f9e\u800c\u522b\u4e4b\u4e8b\uff0c\u8bf7\u5c60\u51e4\u539f\u8c05\u3002\u56e0\u6b64\u5c60\u51e4\u867d\u672a\u89c1\u8fc7\u6768\u5a49\u7b14\u8ff9\uff0c\u4ea6\u77e5\u6b64 \u4fe1\u662f\u771f\u975e\u5047\u3002 \u3000\u3000\u5c60\u51e4\u4e5f\u89c9\u5f97\u5947\u602a\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6768\u5a49\u548c\u8fd9\u51e0\u4e2a\u4eba\u7684\u4ea4\u60c5\u4e00\u5b9a\u975e\u6bd4\u5bfb\u5e38\uff0c\u5426\u5219\u4e0d\u4f1a\u53eb\u4ed6\u4eec \u5230\u8fd9\u91cc\u6765\u3002\u4f46\u5728\u8fd9\u5175\u8352\u9a6c\u4e71\u7684\u65f6\u5019\uff0c\u4ed6\u4eec\u4e3a\u4ec0\u4e48\u8981\u4ece\u8499\u53e4\u8dd1\u51fa\u6765\u5462\uff1f\u201d\u5f53\u4e0b\u4fbf\u5c06\u4ed6\u4eec\u8bf7\u8fdb\u5185 \u5802\u8bf4\u8bdd\u3002 \u3000\u3000\u5c60\u51e4\u65e2\u89c9\u5f97\u5947\u602a\uff0c\u90a3\u4e9b\u5934\u76ee\u5f53\u7136\u662f\u66f4\u89c9\u5f97\u5947\u602a\u4e86\u3002\u8981\u77e5\u4ed6\u4eec\u662f\u51c6\u5907\u548c\u8499\u53e4\u201c\u9791\u5b50\u201d\u6253\u4ed7 \u7684\uff0c\u5982\u4eca\u5374\u5ffd\u7136\u6765\u4e86\u4e09\u4e2a\u8499\u53e4\u4eba\uff0c\u5176\u4e2d\u4e24\u4e2a\u8fd8\u662f\u7edd\u8272\u5973\u5b50\uff0c\u4ed6\u4eec\u600e\u7981\u5f97\u4e0d\u8d77\u731c\u7591\uff1f\u56e0\u6b64\u5c3d\u7ba1 \u5c60\u51e4\u5429\u5490\u4ed6\u4eec\u4e0d\u53ef\u4e71\u8bb2\uff0c\u5c60\u51e4\u8d70\u540e\uff0c\u4ed6\u4eec\u4ecd\u662f\u5fcd\u4e0d\u4f4f\u7a83\u7a83\u79c1\u8bae\u731c\u6d4b\u591a\u7aef\u3002 \u3000\u3000\u5185\u5802\u5750\u8d70\uff0c\u5c60\u51e4\u81ea\u662f\u514d\u4e0d\u4e86\u8981\u95ee\u660e\u6167\u516c\u4e3b\u662f\u4ec0\u4e48\u4eba\uff0c\u5982\u4f55\u4e0e\u674e\u601d\u5357\u3001\u6768\u5a49\u7ed3\u8bc6\uff1f\u660e\u6167\u516c \u4e3b\u4e0d\u613f\u5410\u9732\u8eab\u4efd\uff0c\u53ea\u597d\u7f16\u4e00\u5957\u5047\u8bdd\uff0c\u8bf4\u81ea\u5df1\u662f\u7267\u7f8a\u59d1\u5a18\uff0c\u79f0\u963f\u76d6\u592b\u5987\u662f\u540c\u4e00\u90e8\u843d\u7684\uff0c\u4e3a\u4e86\u4e0d \u60f3\u53d7\u6218\u4e89\u7684\u5e72\u6270\uff0c\u6545\u800c\u9003\u81f3\u4e2d\u539f\u3002\u6768\u5a49\u5728\u8499\u53e4\u7684\u65f6\u5019\uff0c\u4f4f\u7684\u5730\u65b9\u4e0e\u4ed6\u4eec\u76f8\u8ddd\u4e0d\u8fdc\uff0c\u6545\u800c\u76f8 \u8bc6\u3002 \u3000\u3000\u660e\u6167\u516c\u4e3b\u662f\u6210\u5409\u601d\u6c57\u5ba0\u7231\u7684\u5973\u513f\uff0c\u81ea\u5e7c\u7ed9\u4eba\u5949\u627f\u60ef\u4e86\uff0c\u81ea\u7136\u800c\u7136\u5c31\u6709\u4e00\u80a1\u9ad8\u8d35\u7684\u6c14\u6d3e\u3002 \u6b64\u9645\u867d\u7136\u6362\u4e86\u8346\u94a6\u5e03\u88d9\u7684\u6c49\u4eba\u8d2b\u5973\u670d\u88c5\uff0c\u4ecd\u662f\u63a9\u9970\u4e0d\u4f4f\u3002\u54ea\u91cc\u6709\u534a\u70b9\u50cf\u7267\u7f8a\u59d1\u5a18\uff1f \u3000\u3000\u5c60\u51e4\u6697\u6697\u76b1\u7709\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u4f4d\u59d1\u5a18\u5206\u660e\u662f\u8bf4\u8c0e\u8bdd\u3002\u201d\u4f46\u770b\u5728\u6768\u5a49\u4efd\u4e0a\uff0c\u5c60\u51e4\u4e5f\u5e76\u4e0d \u8bf4\u7834\uff0c\u4ecd\u7136\u4ee5\u793c\u76f8\u5f85\u3002 \u3000\u3000\u53ef\u662f\u5c60\u51e4\u56e0\u4e3a\u662f\u4e00\u5be8\u4e4b\u4e3b\uff0c\u65e2\u7136\u8d77\u4e86\u7591\u5fc3\uff0c\u81ea\u7136\u4e5f\u4e0d\u80fd\u4e0d\u7a0d\u52a0\u9632\u5907\u3002\u89c1\u9762\u8bf4\u8bdd\uff0c\u4e5f\u53ea\u662f \u4fdd\u6301\u7740\u8868\u9762\u7684\u5ba2\u6c14\uff0c\u5374\u7f3a\u4e4f\u4e86\u670b\u53cb\u95f4\u4e00\u79cd\u5f7c\u6b64\u4e92\u4fe1\u7684\u70ed\u8bda\uff0c\u8c08\u8bdd\u8fc7\u540e\uff0c\u5c60\u51e4\u62e8\u4e86\u4e00\u680b\u5728\u540e\u5be8 \u7684\u72ec\u7acb\u623f\u5c4b\u7ed9\u4ed6\u4eec\u4e09\u4eba\u5c45\u4f4f\uff0c\u5185\u5916\u4e4b\u95f4\u6709\u4e00\u9053\u7ecf\u5e38\u52a0\u9501\u7684\u5927\u95e8\u9694\u5f00\u3002 \u3000\u3000\u5c60\u51e4\u7684\u8fd9\u4e2a\u6001\u5ea6\uff0c\u660e\u6167\u516c\u4e3b\u8fc7\u4e0d\u4e86\u51e0\u5929\u4e5f\u89c9\u5bdf\u4e86\u3002\u5979\u662f\u7ed9\u4eba\u5949\u627f\u60ef\u4e86\u7684\uff0c\u5f53\u7136\u4e0d\u5927\u9ad8 \u5174\u3002\u5176\u5b9e\u8fd9\u5e76\u4e0d\u80fd\u602a\u8d23\u5c60\u51e4\uff0c\u5c60\u51e4\u672c\u6765\u662f\u4e2a\u70ed\u60c5\u723d\u90ce\u7684\u59d1\u5a18\uff0c\u53ea\u56e0\u5979\u8eab\u8d1f\u5c71\u5be8\u91cd\u8d23\uff0c\u800c\u53c8\u77e5 \u9053\u660e\u6167\u516c\u4e3b\u8bf4\u4e86\u8c0e\u8bdd\uff0c\u53eb\u5979\u5982\u4f55\u8fd8\u80fd\u4e0e\u660e\u6167\u516c\u4e3b\u63a8\u5fc3\u7f6e\u8179\uff1f \u3000\u3000\u5c60\u51e4\u7684\u6bcd\u4eb2\u56e0\u4e3a\u4e08\u592b\u662f\u5728\u8499\u53e4\u7ed9\u5bb3\u6b7b\u7684\uff0c\u5979\u4e0d\u6bd4\u5973\u513f\u660e\u7406\uff0c\u5e73\u65e5\u662f\u4e00\u63d0\u8d77\u8499\u53e4\u4eba\u5c31\u4e0d\u89c9 \u5fc3\u91cc\u6709\u6c14\u7684\u3002\u8fd9\u6b21\u5c60\u51e4\u6536\u7559\u660e\u6167\u516c\u4e3b\u7b49\u4eba\uff0c\u4e8b\u540e\u7980\u544a\u6bcd\u4eb2\uff0c\u6bcd\u5973\u8fd8\u56e0\u6b64\u5435\u4e86\u4e00\u573a\uff0c\u597d\u4e0d\u5bb9\u6613 \u5c60\u51e4\u624d\u5c06\u5979\u8bf4\u670d\u3002 \u3000\u3000\u660e\u6167\u516c\u4e3b\u54ea\u91cc\u77e5\u9053\u8fd9\u4e9b\u4e8b\u60c5\uff0c\u5979\u6765\u4e86\u4e09\u5929\uff0c\u624d\u89c1\u5230\u5c60\u51e4\u7684\u6bcd\u4eb2\u3002\u5c60\u51e4\u7684\u6bcd\u4eb2\u5bf9\u5979\u6781\u4e3a\u51b7 \u6de1\uff0c\u5979\u56de\u53bb\u4e4b\u540e\uff0c\u81ea\u662f\u4e0d\u514d\u53c8\u6709\u4e00\u573a\u6c14\u95f7\u3002\u5979\u4e0d\u77e5\u5c60\u51e4\u7684\u6bcd\u4eb2\u5bf9\u5979\u5df2\u662f\u51fa\u4e4e\u5973\u513f\u610f\u6599\u4e4b\u5916\u7684 \u597d\u4e86\uff0c\u5979\u89c1\u5230\u660e\u6167\u516c\u4e3b\u7f8e\u8c8c\u6e29\u67d4\uff0c\u5b8c\u5168\u4e0d\u662f\u5979\u6240\u60f3\u8c61\u7684\u201c\u6cfc\u8fa3\u756a\u5a46\u201d\uff0c\u8fd9\u624d\u5bf9\u660e\u6167\u516c\u4e3b\u6709\u4e86 \u51e0\u5206\u597d\u611f\u7684\u3002\u8981\u4e0d\u7136\u6050\u6015\u5979\u5c31\u4e0d\u4ec5\u662f\u51b7\u8a00\u51b7\u8bed\uff0c\u800c\u662f\u8981\u5f53\u573a\u53d1\u4f5c\u4e86\u3002 \u3000\u3000\u660e\u6167\u516c\u4e3b\u51e0\u66fe\u53d7\u8fc7\u4eba\u5bb6\u5982\u6b64\u51b7\u6de1\uff0c\u4e0d\u7531\u5f97\u5fc3\u4e2d\u90c1\u90c1\u4e0d\u6b22\uff0c\u9690\u9690\u4fbf\u6709\u79bb\u5c71\u4e4b\u610f\u3002\u4e00\u65e5\uff0c\u660e \u6167\u516c\u4e3b\u95f7\u5750\u623f\u4e2d\uff0c\u5361\u6d1b\u4e1d\u8fdb\u6765\u62a5\u9053\uff1a\u201c\u5c60\u5be8\u4e3b\u521a\u624d\u5dee\u9063\u4e00\u4e2a\u4e2b\u5934\u8fc7\u6765\uff0c\u8bf4\u662f\u60f3\u66ff\u54b1\u4eec\u7f1d\u5236\u65b0 \u8863\uff0c\u95ee\u4f60\u559c\u6b22\u4ec0\u4e48\u6b3e\u5f0f\u3002\u53c8\u8bf4\u5018\u82e5\u4f60\u6709\u7a7a\u7684\u8bdd\uff0c\u8bf7\u5230\u5c60\u8001\u592a\u592a\u90a3\u91cc\u4e00\u8d9f\uff0c\u5979\u6536\u85cf\u6709\u8bb8\u591a\u4e0a\u597d \u7684\u7eeb\u7f57\u7ef8\u7f0e\uff0c\u8bf7\u4f60\u81ea\u5df1\u53bb\u6311\u9009\u3002\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u54fc\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u4f55\u5fc5\u9ebb\u70e6\u4eba\u5bb6\uff0c\u6ca1\u60f9\u4eba\u8ba8\u538c\uff01\u201d \u3000\u3000\u5361\u6d1b\u4e1d\u6014\u4e86\u4e00\u6014\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u770b\u5979\u4eec\u8fd9\u6b21\u5012\u4f3c\u9887\u6709\u8bda\u610f\u7684\u3002\u5c60\u8001\u592a\u592a\u90a3\u65e5\u867d\u7136\u5bf9\u4f60\u51b7 \u6de1\uff0c\u4f46\u4eca\u65e5\u5374\u662f\u5979\u81ea\u52a8\u63d0\u51fa\uff0c\u8981\u8bf7\u4f60\u8fc7\u53bb\u6311\u9009\u8863\u6599\u7684\u5440\u3002\u201d\u63a5\u7740\u53c8\u6084\u58f0\u8bf4\u9053\uff0c\u201c\u6211\u770b\u5979\u4eec\u597d \u50cf\u662f\u5bdf\u89c9\u4e86\u4f60\u7684\u8eab\u4efd\uff0c\u5373\u4f7f\u4e0d\u77e5\u4f60\u662f\u516c\u4e3b\uff0c\u4e5f\u77e5\u4f60\u662f\u51fa\u8eab\u9ad8\u8d35\u7684\u4e86\u3002\u90a3\u4e2b\u5934\u8dd1\u6765\uff0c\u53ea\u662f\u627e\u6211 \u4f20\u8bdd\uff0c\u4e5f\u4f3c\u4e4e\u77e5\u9053\u4e86\u6211\u662f\u4f60\u7684\u4f8d\u5973\u3002\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u5979\u4eec\u53ef\u6709\u5411\u4f60\u6253\u542c\u4ec0\u4e48\uff1f\u201d\u5361\u6d1b\u4e1d\u9053\uff1a\u201c\u8fd9\u5012\u6ca1\u6709\u3002\u201d\u660e \u6167\u516c\u4e3b\u9053\uff1a\u201c\u90a3\u8001\u592a\u592a\u5018\u82e5\u662f\u56e0\u4e3a\u77e5\u9053\u6211\u7684\u8eab\u4efd\u624d\u5bf9\u6211\u524d\u5014\u540e\u606d\uff0c\u6211\u66f4\u4e0d\u9ad8\u5174\u53bb\u4e86\u3002\u201d \u3000\u3000\u5361\u6d1b\u4e1d\u9053\uff1a\u201c\u6211\u770b\u5c60\u8001\u592a\u592a\u4e5f\u4e0d\u50cf\u4e2a\u52bf\u5229\u4e4b\u4eba\uff0c\u4e5f\u8bb8\u662f\u56e0\u4e3a\u5979\u5bdf\u89c9\u4e86\u4f60\u4e0d\u662f\u5982\u4f60\u81ea\u5df1\u6240 \u8bf4\u7684\u90a3\u6837\u4e00\u4e2a\u5bfb\u5e38\u7684\u7267\u7f8a\u59d1\u5a18\uff0c\u800c\u53c8\u5bf9\u4f60\u6709\u4e86\u597d\u611f\uff0c\u6240\u4ee5\u624d\u60f3\u5230\u8981\u66ff\u4f60\u7f1d\u5236\u9002\u5408\u4f60\u8eab\u4efd\u7684\u8863 \u88f3\uff0c\u8fd9\u4e5f\u662f\u5979\u4eec\u5bf9\u5f85\u5ba2\u4eba\u7684\u793c\u8c8c\u5440\u3002\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u9053\uff1a\u201c\u6211\u4e0d\u7ba1\u5979\u4eec\u77e5\u9053\u4e86\u4e00\u4e9b\u4ec0\u4e48\uff0c\u53cd\u6b63\u6211\u662f\u4e0d\u6253\u7b97\u5728\u8fd9\u513f\u4f4f\u4e0b\u53bb\u7684\u4e86\u3002\u201d \u3000\u3000\u5361\u6d1b\u4e1d\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u9053\uff1a\u201c\u4f60\u6ca1\u6709\u611f\u89c9\u5230\u5417\uff0c\u5c60\u5be8\u4e3b\u5bf9\u54b1\u4eec\u4f3c\u4e4e\u9887\u6709\u731c\u7591\uff0c\u5904\u5904\u8981\u63d0\u9632\u7740\u54b1\u4eec\u7684 \u5462\u3002\u6bd4\u5982\u8bf4\uff0c\u5927\u95e8\u7ecf\u5e38\u52a0\u4e0a\u94c1\u9501\uff0c\u597d\u50cf\u662f\u6015\u54b1\u4eec\u51fa\u53bb\u4e71\u8d70\uff0c\u5c31\u4f1a\u63a2\u542c\u5979\u4eec\u5c71\u5be8\u7684\u79d8\u5bc6\u3002\u54b1\u4eec \u5728\u540e\u9662\u6563\u6b65\uff0c\u4e5f\u5e38\u5e38\u6709\u4e2b\u5934\u8ddf\u7740\uff0c\u5206\u660e\u662f\u76d1\u89c6\u54b1\u4eec\u3002\u201d \u3000\u3000\u5361\u6d1b\u4e1d\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u4e0d\u60f3\u7ed9\u4eba\u5bb6\u77e5\u9053\u8eab\u4efd\u7684\u5417\uff1f\u73b0\u5728\u54b1\u4eec\u5f97\u4ee5\u6df1\u85cf\u5185\u9662\uff0c\u8fd9\u6b63\u662f\u6c42\u4e4b \u4e0d\u5f97\u7684\u5440\u3002\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u662f\u4e0d\u613f\u8dd1\u5230\u5916\u9762\u548c\u90a3\u4e9b\u55bd\u5175\u53ae\u6df7\uff0c\u4f46\u4eba\u5bb6\u628a\u54b1\u4eec\u5f53\u4f5c\u56da\u5f92\u770b \u5f85\uff0c\u6211\u5374\u5f88\u4e0d\u559c\u6b22\u3002\u201d \u3000\u3000\u5361\u6d1b\u4e1d\u9053\uff1a\u201c\u4f46\u8fd9\u4e5f\u602a\u4e0d\u5f97\u5979\u4eec\uff0c\u5728\u5979\u4eec\u7684\u773c\u4e2d\uff0c\u54b1\u4eec\u603b\u8fd8\u662f\u6765\u5386\u4e0d\u660e\u7684\u8499\u53e4\u4eba\u3002\u5f85\u6768 \u59d1\u5a18\u56de\u6765\uff0c\u5c31\u597d\u529e\u4e86\u3002\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u53f9\u9053\uff1a\u201c\u5bc4\u4eba\u7bf1\u4e0b\uff0c\u5ea6\u65e5\u5982\u5e74\u3002\u8fd9\u6ecb\u5473\u513f\u53ef\u5e76\u4e0d\u597d\u53d7\u5462\u3002\u201d \u3000\u3000\u5361\u6d1b\u4e1d\u529d\u9053\uff1a\u201c\u516c\u4e3b\u4f60\u5c31\u5fcd\u8010\u4e9b\u5427\u3002\u5982\u679c\u54b1\u4eec\u73b0\u5728\u5c31\u8d70\uff0c\u5c82\u4e0d\u662f\u66f4\u60f9\u4eba\u731c\u7591\uff1f\u8bf4\u4e0d\u5b9a\u8fd8 \u4f1a\u60f9\u51fa\u610f\u5916\u7684\u9ebb\u70e6\u5462\uff01\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u9ed8\u7136\u4e0d\u8bed\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6768\u59d1\u5a18\u672a\u56de\u6765\uff0c\u65e0\u4eba\u80fd\u7ed9\u6211\u62c5\u4fdd\u3002\u6211\u82e5\u7136\u4e0d\u8f9e \u800c\u884c\uff0c\u662f\u5f88\u53ef\u80fd\u7ed9\u5979\u4eec\u5f53\u4f5c\u5978\u7ec6\u7684\u3002\u53c8\u7109\u80fd\u8d70\u5f97\u8131\u5462\uff1f\u201d \u3000\u3000\u5361\u6d1b\u4e1d\u9053\uff1a\u201c\u54b1\u4eec\u8fd8\u662f\u6682\u4e14\u4f4f\u4e0b\u6765\uff0c\u7b49\u5230\u6768\u59d1\u5a18\u56de\u6765\u4e4b\u540e\u518d\u8bf4\u5427\u3002\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u4e5f\u53ea\u597d\u5982\u6b64\u4e86\u3002\u201d \u3000\u3000\u5361\u6d1b\u4e1d\u9053\uff1a\u201c\u90a3\u4e48\u5c60\u8001\u592a\u592a\u8bf7\u4f60\u53bb\u6311\u9009\u8863\u6599\uff0c\u4f60\u53bb\u5462\u8fd8\u662f\u4e0d\u53bb\uff1f\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u9053\uff1a\u201c\u6211\u6700\u8ba8\u538c\u89c1\u8fd9\u4e2a\u8138\u4e0a\u597d\u50cf\u522e\u5f97\u4e0b\u4e00\u5c42\u971c\u7684\u8001\u592a\u5a46\u4e86\uff0c\u4e0d\u53bb\uff0c\u4e0d\u53bb\uff01\u201d \u3000\u3000\u5361\u6d1b\u4e1d\u529d\u9053\uff1a\u201c\u522b\u4eba\u7ed9\u54b1\u4eec\u9762\u5b50\uff0c\u54b1\u4eec\u4e0d\u53bb\uff0c\u6050\u6015\u4e0d\u597d\u610f\u601d\u5427\u3002\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u53d1\u8d77\u516c\u4e3b\u813e\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u8bf4\u4e0d\u53bb\u5c31\u662f\u4e0d\u53bb\uff0c\u6211\u4e0d\u8981\u5979\u7ed9\u6211\u9762\u5b50\uff0c\u6211\u4e5f\u4e0d\u60f3\u53bb\u8ba8 \u597d\u5979\uff0c\u4f60\u89c9\u5f97\u4e0d\u597d\u610f\u601d\uff0c\u4f60\u81ea\u5df1\u53bb\u597d\u4e86\u3002\u201d \u3000\u3000\u5361\u6d1b\u4e1d\u65e0\u53ef\u5948\u4f55\uff0c\u60f3\u4e86\u4e00\u60f3\uff0c\u8bf4\u9053\uff1a\u201c\u4e5f\u597d\u3002\u6211\u53bb\u628a\u8863\u6599\u62ff\u56de\u6765\uff0c\u5c31\u8bf4\u662f\u4e0d\u6562\u9ebb\u70e6\u5979 \u4eec\uff0c\u7531\u6211\u4eec\u81ea\u5df1\u7f1d\u5236\u597d\u4e86\u3002\u201d \u3000\u3000\u5361\u6d1b\u4e1d\u5f97\u5230\u4e86\u516c\u4e3b\u7684\u5141\u8bb8\uff0c\u4fbf\u5373\u72ec\u81ea\u4e00\u4eba\u53bb\u627e\u5c60\u51e4\u7684\u6bcd\u4eb2\u3002\u5979\u6697\u81ea\u7559\u5fc3\uff0c\u8fd9\u6b21\u5374\u6ca1\u6709\u53d1 \u73b0\u6709\u4e2b\u5934\u8ddf\u8e2a\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4e0d\u77e5\u662f\u516c\u4e3b\u7684\u591a\u7591\uff0c\u8fd8\u662f\u5979\u4eec\u5df2\u7ecf\u5bdf\u89c9\u6211\u4eec\u4e0d\u662f\u574f\u4eba\uff0c\u56e0\u800c\u653e \u677e\u4e86\u6212\u5907\u4e86\uff1f\u201d\u4f46\u5979\u7a7f\u8fc7\u56de\u5eca\uff0c\u8e0f\u5165\u6df1\u9662\uff0c\u8fde\u4e00\u5411\u670d\u4f8d\u5c60\u592b\u4eba\u7684\u90a3\u4e2a\u5fc3\u8179\u4e2b\u5934\u4e5f\u6ca1\u6709\u89c1\u7740\uff0c \u5374\u4e5f\u89c9\u5f97\u6709\u70b9\u5947\u602a\u3002 \u3000\u3000\u5361\u6d1b\u4e1d\u8e0f\u5165\u4e86\u9662\u5b50\uff0c\u6b63\u8981\u901a\u540d\u6c42\u89c1\uff0c\u5ffd\u542c\u5f97\u4e00\u4e2a\u7537\u5b50\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u59b9\u59b9\uff0c\u4f60\u4e0d\u8981\u7792 \u6211\u3001\u90a3\u4e09\u4e2a\u4eba\u90fd\u662f\u4ece\u8499\u53e4\u6765\u7684\uff0c\u662f\u4e48\uff1f\u201d \u3000\u3000\u8ddf\u7740\u5c60\u51e4\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u662f\u53c8\u600e\u6837\u3002\u201d \u3000\u3000\u90a3\u7537\u5b50\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u8d23\u5907\u6211\u4e0d\u8be5\u548c\u8499\u53e4\u4eba\u6765\u5f80\u7684\u4e48\uff1f\u5176\u5b9e\u6211\u5e76\u6ca1\u6709\u4f5c\u51fa\u90a3\u6837\u7684\u4e8b \u60c5\uff0c\u90fd\u662f\u674e\u601d\u5357\u8bec\u8511\u6211\u7684\u3002\u4f46\u4f60\u5b81\u53ef\u76f8\u4fe1\u5916\u4eba\uff0c\u4e0d\u76f8\u4fe1\u81ea\u5df1\u7684\u54e5\u54e5\uff0c\u90a3\u4e5f\u7b97\u4e86\u3002\u4f60\u81ea\u5df1\u4e3a\u4f55 \u4e5f\u4e0e\u8499\u53e4\u4eba\u79c1\u81ea\u6765\u5f80\u5462\uff1f\u201d \u3000\u3000\u5c60\u51e4\u8bf4\u9053\uff1a\u201c\u8fd9\u51e0\u4e2a\u8499\u53e4\u4eba\u548c\u4f60\u6240\u7ed3\u4ea4\u7684\u90a3\u4e9b\u8499\u53e4\u4eba\u53ef\u4e0d\u4e00\u6837\u3002\u201d \u3000\u3000\u90a3\u7537\u5b50\u9053\uff1a\u201c\u662f\u4ec0\u4e48\u6765\u5386\u7684\u8499\u53e4\u4eba\uff1f\u600e\u7684\u4f60\u8bf4\u662f\u4e0d\u4e00\u6837\u5462\uff1f\u201d \u3000\u3000\u5c60\u51e4\u9053\uff1a\u201c\u4e0d\u4e00\u6837\u5c31\u662f\u4e0d\u4e00\u6837\uff0c\u53cd\u6b63\u6211\u77e5\u9053\u5c31\u884c\uff0c\u4e0d\u7528\u4f60\u7ba1\u3002\u201d \u3000\u3000\u8ddf\u7740\u4e00\u4e2a\u82cd\u8001\u7684\u5987\u4eba\u8bf4\u9053\uff1a\u201c\u5509\uff0c\u4f60\u4eec\u5144\u59b9\u4e00\u89c1\u9762\u5c31\u5435\u67b6\uff0c\u8ba9\u6211\u6e05\u51c0\u4e00\u70b9\u597d\u4e0d\u597d\uff1f\u9f99 \u513f\uff0c\u4f60\u542c\u6211\u8bf4\uff0c\u90a3\u4e24\u4e2a\u8499\u53e4\u59d1\u5a18\u6211\u662f\u89c1\u8fc7\u7684\uff0c\u5979\u4eec\u53c8\u7f8e\u4e3d\uff0c\u53c8\u6e29\u67d4\uff0c\u6211\u4e5f\u89c9\u5f97\u5979\u4eec\u5f88\u4e0d\u9519 \u5462\u3002\u201d \u3000\u3000\u5361\u6d1b\u4e1d\u4e00\u542c\u5230\u8fd9\u7537\u5b50\u7684\u58f0\u97f3\uff0c\u5c31\u4e0d\u7531\u5f97\u5413\u4e86\u4e00\u8df3\uff0c\u8fd9\u58f0\u97f3\u597d\u719f\uff01\u5206\u660e\u662f\u5728\u54ea\u513f\u542c\u89c1\u8fc7 \u7684\u3002\u7591\u5fc3\u54cd\u8d77\uff0c\u56e0\u6b64\u5c31\u4e0d\u6562\u51fa\u58f0\uff0c\u6084\u6084\u5730\u7ed5\u5230\u540e\u7a97\u5077\u770b\u3002 \u3000\u3000\u4e00\u770b\u4e4b\u4e0b\uff0c\u4e0d\u7531\u5f97\u5927\u5403\u4e00\u60ca\u3002\u539f\u6765\u8fd9\u4e2a\u7537\u5b50\u5c31\u662f\u5979\u4eec\u66fe\u7ecf\u5728\u90a3\u5ea7\u53e4\u5e99\u78b0\u89c1\u8fc7\u7684\u90a3\u4e2a\u5c60 \u9f99\u3002 \u3000\u3000\u5361\u6d1b\u4e1d\u5927\u60ca\u4e4b\u4e0b\uff0c\u5fc3\u91cc\u60f3\u5230\uff1a\u201c\u539f\u6765\u8fd9\u4e2a\u4eba\u7adf\u662f\u5c60\u5be8\u4e3b\u7684\u54e5\u54e5\uff0c\u600e\u4e48\u597d\u5462\uff1f\u201d \u3000\u3000\u539f\u6765\u5c60\u9f99\u53bb\u800c\u590d\u56de\uff0c\u6b63\u662f\u4e3a\u4e86\u8981\u4fa6\u67e5\u660e\u6167\u516c\u4e3b\u7684\u79d8\u5bc6\u3002 \u3000\u3000\u90a3\u65e5\u5728\u7eff\u6797\u5927\u4f1a\u4e4b\u4e2d\uff0c\u4ed6\u7ed9\u674e\u601d\u5357\u6240\u4f24\uff0c\u4f24\u5f97\u5176\u5b9e\u4e0d\u91cd\u3002\u4ed6\u8eb2\u5728\u5bb6\u91cc\u517b\u4f24\uff0c\u6b63\u5982\u674e\u601d\u5357 \u4e4b\u6240\u6599\uff0c\u4e43\u662f\u6709\u6240\u56fe\u8c0b\u7684\u3002 \u3000\u3000\u4ed6\u662f\u60f3\u5728\u674e\u601d\u5357\u3001\u5b5f\u5c11\u521a\u7b49\u4eba\u79bb\u5c71\u4e4b\u540e\uff0c\u593a\u56de\u5be8\u4e3b\u7684\u6743\u67c4\u3002 \u3000\u3000\u53ef\u662f\u5728\u5bb6\u91cc\u4f4f\u4e86\u51e0\u5929\uff0c\u4ed6\u53d1\u89c9\u5be8\u4e2d\u7684\u5927\u5c0f\u5934\u76ee\uff0c\u5dee\u4e0d\u591a\u90fd\u662f\u62e5\u62a4\u4ed6\u7684\u59b9\u59b9\uff0c\u9119\u5f03\u4ed6\u7684\u3002 \u7eb5\u7136\u4ed6\u4e5f\u6709\u51e0\u4e2a\u5fc3\u8179\u5934\u76ee\uff0c\u4e5f\u6d4e\u4e0d\u4e86\u4e8b\u3002\u8981\u641e\u201c\u5185\u4e71\u201d\u662f\u4e0d\u6210\u7684\u4e86\uff0c\u4ed6\u53ea\u6709\u501f\u52a9\u5916\u529b\uff0c\u662f\u4ee5 \u4f4f\u4e86\u51e0\u5929\uff0c\u5c31\u88c5\u4f5c\u521b\u4f24\u5df2\u6108\uff0c\u8981\u6c42\u4e0b\u5c71\u3002\u5c60\u51e4\u5df4\u4e0d\u5f97\u4ed6\u65e9\u8d70\uff0c\u5f53\u7136\u4e0d\u52a0\u62e6\u963b\u3002\u5012\u662f\u5979\u7684\u6bcd\u4eb2 \u820d\u4e0d\u5f97\u513f\u5b50\uff0c\u4e3a\u6b64\u8fd8\u54ed\u4e86\u4e00\u573a\uff0c\u57cb\u6028\u5973\u513f\u4e0d\u8be5\u5bf9\u4eb2\u54e5\u54e5\u5982\u6b64\u65e0\u60c5\u3002 \u3000\u3000\u672c\u6765\u4ed6\u662f\u53bb\u627e\u6df3\u4e8e\u5468\u7236\u5b50\u5e2e\u5fd9\u7684\uff0c\u4f46\u6df3\u4e8e\u5468\u90a3\u65e5\u8d25\u5728\u5b5f\u5c11\u521a\u7684\u624b\u4e0a\uff0c\u51e0\u4e4e\u4e27\u4e86\u6027\u547d\uff0c\u5413 \u5f97\u5fc3\u80c6\u4ff1\u5bd2\uff0c\u4e0d\u6562\u56de\u8f6c\u81ea\u5df1\u7684\u5c71\u5be8\uff0c\u7236\u5b50\u4e8c\u4eba\u90fd\u9003\u5f80\u91d1\u4eac\u5927\u90fd\u53bb\u4e86\u3002\u5c60\u9f99\u627e\u4e0d\u7740\u6df3\u4e8e\u5468\u7236 \u5b50\uff0c\u60f3\u8d77\u4e86\u53e6\u4e00\u4e2a\u4eba\uff0c\u8fd9\u4e2a\u4eba\u662f\u767d\u4e07\u96c4\u3002 \u3000\u3000\u5c60\u9f99\u548c\u767d\u4e07\u96c4\u5e76\u4e0d\u76f8\u8bc6\uff0c\u4f46\u5374\u77e5\u9053\u4ed6\u79c1\u901a\u8499\u53e4\u7684\u79d8\u5bc6\u3002\u60f3\u6765\u767d\u4e07\u96c4\u4e5f\u5e94\u8be5\u77e5\u9053\u4ed6\u662f\u81ea\u5df1 \u4eba\u3002\u65e2\u7136\u9664\u4e86\u6df3\u4e8e\u5468\u4e4b\u5916\uff0c\u53ea\u6709\u4e00\u4e2a\u767d\u4e07\u96c4\u53ef\u4ee5\u5e2e\u4ed6\u7684\u5fd9\uff0c\u4ed6\u4fbf\u6539\u53d8\u884c\u7a0b\uff0c\u5f84\u81ea\u6295\u5954\u767d\u5bb6 \u5e84\u3002 \u3000\u3000\u9014\u4e2d\u7ecf\u8fc7\u90a3\u5ea7\u53e4\u5e99\uff0c\u51fa\u4e4e\u5c60\u9f99\u610f\u6599\u4e4b\u5916\uff0c\u9047\u4e0a\u4e86\u660e\u6167\u516c\u4e3b\u3002 \u3000\u3000\u5c60\u9f99\u548c\u8d3a\u4e5d\u516c\u8fd9\u73ed\u4eba\u7ed9\u660e\u6167\u516c\u4e3b\u65a5\u9000\u4e4b\u540e\uff0c\u8d3a\u4e5d\u516c\u9080\u5c60\u9f99\u5230\u4ed6\u5bb6\u91cc\uff0c\u7b49\u5f85\u9633\u5929\u96f7\u7684\u4f84\u5b50 \u9633\u575a\u767d\u3002 \u3000\u3000\u5c60\u9f99\u56e0\u4e3a\u9633\u5929\u96f7\u662f\u81ea\u5df1\u6740\u7236\u4ec7\u4eba\uff0c\u867d\u8bf4\u4ed6\u73b0\u5728\u662f\u548c\u9633\u5929\u96f7\u8d70\u5728\u4e00\u6761\u8def\u4e0a\uff0c\u4f46\u4ed6\u548c\u8499\u53e4\u4eba \u7684\u5173\u7cfb\u3001\u5374\u662f\u7531\u4e8e\u6df3\u4e8e\u5468\u7684\u7a7f\u9488\u5f15\u7ebf\uff0c\u5e76\u975e\u901a\u8fc7\u9633\u5929\u96f7\uff0c\u4ed6\u867d\u7136\u4e0d\u6562\u627e\u9633\u5929\u96f7\u62a5\u4ec7\uff0c\u4f46\u4e3a\u4e86 \u9762\u5b50\uff0c\u4e5f\u4e0d\u613f\u516c\u5f00\u5411\u4ec7\u4eba\u5c48\u670d\u3002\u662f\u4ee5\u4e5f\u5c31\u4e0d\u613f\u610f\u53bb\u89c1\u9633\u575a\u767d\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u4ed6\u867d\u7136\u6ca1\u6709\u8ddf\u8d3a\u4e5d\u516c\u7b49\u4eba\u56de\u53bb\uff0c\u5374\u4e5f\u6ca1\u6709\u9a6c\u4e0a\u53bb\u767d\u5bb6\u5e84\uff0c\u56e0\u4e3a\u4ed6\u5bf9\u660e\u6167\u516c\u4e3b\u8d77\u4e86 \u7591\u5fc3\u3002 \u3000\u3000\u8981\u77e5\u9053\u5c60\u9f99\u662f\u4e00\u4e2a\u6781\u4e3a\u7cbe\u7075\u7684\u4eba\uff0c\u5f53\u65f6\u867d\u7136\u7ed9\u660e\u6167\u516c\u4e3b\u5413\u9000\uff0c\u8fc7\u540e\u5374\u662f\u96be\u514d\u8d77\u4e86\u601d\u7591\uff1a \u201c\u4ee5\u660e\u6167\u516c\u4e3b\u91d1\u679d\u7389\u53f6\u7684\u8eab\u4efd\uff0c\u4e3a\u4f55\u53ea\u5e26\u4e00\u4e2a\u4f8d\u5973\uff0c\u4e00\u4e2a\u968f\u4ece\uff0c\u8dd1\u5230\u548c\u8499\u53e4\u4ea4\u6218\u7684\u91d1\u56fd\u7edf\u6cbb \u4e4b\u5730\uff1f\u201d \u3000\u3000\u5c60\u9f99\u60f3\u8981\u63a2\u67e5\u660e\u6167\u516c\u4e3b\u7684\u79d8\u5bc6\uff0c\u5728\u53e4\u5e99\u9644\u8fd1\u8eb2\u853d\u8d77\u6765\u3002\u4e0d\u4e45\u8d3a\u4e5d\u516c\u548c\u9633\u575a\u767d\u7b49\u4eba\u53bb\u800c\u590d \u6765\uff0c\u8d25\u5728\u8c37\u6db5\u865a\u4e4b\u624b\uff0c\u53c8\u4e0d\u4e45\uff0c\u8c37\u6db5\u865a\u4e5f\u72ec\u81ea\u8d70\u4e86\uff0c\u5c60\u9f99\u59cb\u7ec8\u6ca1\u6709\u9732\u9762\u3002 \u3000\u3000\u5c60\u9f99\u6ca1\u6709\u9732\u9762\uff0c\u4f46\u5df2\u5077\u542c\u4e86\u660e\u6167\u516c\u4e3b\u548c\u6768\u5a49\u6240\u8bf4\u7684\u8bdd\u3002\u6768\u5a49\u7ed9\u660e\u6167\u516c\u4e3b\u5199\u4fe1\uff0c\u53eb\u5979\u4ea4\u7ed9 \u81ea\u5df1\u7684\u59b9\u59b9\uff0c\u4ed6\u4e5f\u90fd\u5077\u5077\u7684\u770b\u5728\u773c\u4e2d\u4e86\u3002 \u3000\u3000\u5c60\u9f99\u5077\u542c\u4e86\u660e\u6167\u516c\u4e3b\u7684\u79d8\u5bc6\uff0c\u4e0d\u7981\u559c\u51fa\u671b\u5916\u3002\u4ed6\u662f\u4e2a\u653b\u4e8e\u5fc3\u8ba1\u7684\u4eba\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u6211\u5728 \u8499\u53e4\u4e4b\u65f6\uff0c\u542c\u8bf4\u5979\u5acc\u5f03\u9547\u56fd\u738b\u5b50\u800c\u7231\u6155\u8fc7\u674e\u601d\u5357\uff0c\u5f53\u65f6\u6211\u8fd8\u4e0d\u6562\u76f8\u4fe1\uff0c\u73b0\u5728\u770b\u6765\uff0c\u679c\u7136\u662f\u771f \u7684\u4e86\u3002\u9547\u56fd\u738b\u5b50\u4e11\u964b\u4e0d\u582a\uff0c\u4e5f\u602a\u4e0d\u5f97\u5979\u4f1a\u559c\u6b22\u4e0a\u674e\u601d\u5357\u8fd9\u5c0f\u767d\u8138\u3002\u4f46\u674e\u601d\u5357\u8fd9\u5c0f\u5b50\u5df2\u7ecf\u6709\u4e86 \u6768\u5a49\uff0c\u660e\u6167\u516c\u4e3b\u4ea6\u5df2\u77e5\u9053\uff0c\u65e0\u8bba\u5982\u4f55\uff0c\u5979\u662f\u5ac1\u4e0d\u6210\u674e\u601d\u5357\u7684\u4e86\u3002\u201d\u53c8\u518d\u60f3\u9053\uff1a\u201c\u4e00\u4e2a\u5973\u5b50\u5728 \u5931\u610f\u4e4b\u4e2d\uff0c\u662f\u6700\u5bb9\u6613\u7ed9\u7537\u5b50\u4fd8\u864f\u7684\u3002\u6211\u7684\u624d\u8c8c\u4e0d\u8f93\u7ed9\u674e\u601d\u5357\uff0c\u77e5\u60c5\u8bc6\u8da3\u5219\u66f4\u5728\u674e\u601d\u5357\u4e4b\u4e0a\u3002 \u5979\u4f4f\u5728\u6211\u7684\u5bb6\u4e2d\uff0c\u6211\u4e0d\u5982\u56de\u53bb\u60f3\u6cd5\u548c\u5979\u63a5\u8fd1\uff0c\u8fd1\u6c34\u697c\u53f0\uff0c\u51ed\u7740\u6211\u7684\u624b\u6bb5\uff0c\u4f55\u6101\u4e0d\u83b7\u5f97\u5979\u7684\u82b3 \u5fc3\uff1f\u54c8\u54c8\uff0c\u5018\u82e5\u6211\u505a\u4e86\u660e\u6167\u516c\u4e3b\u7684\u9644\u9a6c\uff0c\u8fd9\u53ef\u771f\u662f\u5929\u5927\u7684\u5bcc\u8d35\u4e86\u3002\u53c8\u5373\u4f7f\u4e07\u4e00\u4e0d\u80fd\u6210\u529f\uff0c\u6211 \u4e5f\u53ef\u4ee5\u5411\u62d6\u96f7\u51fa\u5356\u8fd9\u4e2a\u79d8\u5bc6\uff0c\u603b\u4e4b\u662f\u6709\u8bf4\u4e0d\u5b8c\u7684\u597d\u5904\u4e86\u3002\u201d \u3000\u3000\u5c60\u9f99\u6ee1\u809a\u5bc6\u5708\uff0c\u4e8e\u662f\u653e\u5f03\u4e86\u53bb\u627e\u767d\u4e07\u96c4\u7684\u8ba1\u5212\uff0c\u53c8\u518d\u56de\u5bb6\u3002\u4ed6\u4e3a\u4e86\u4e0d\u60f3\u660e\u6167\u516c\u4e3b\u8bc6\u7834\u4ed6 \u7684\u5fc3\uff0c\u6545\u610f\u8fdf\u51e0\u5929\u624d\u56de\u5c71\u5be8\u3002 \u3000\u3000\u56de\u5230\u5c71\u5be8\uff0c\u679c\u7136\u542c\u5f97\u5fc3\u8179\u7684\u5934\u76ee\u8bf4\u51fa\u5c71\u5be8\u662f\u6765\u4e86\u4e24\u4e2a\u8499\u53e4\u59d1\u5a18\u548c\u4e00\u4e2a\u8499\u53e4\u6b66\u58eb\uff0c\u5c60\u9f99\u9042 \u6ee1\u6000\u559c\u60a6\u7684\u53bb\u89c1\u6bcd\u4eb2\u3002 \u3000\u3000\u89c1\u4e86\u6bcd\u4eb2\uff0c\u5c60\u9f99\u7684\u5634\u5c31\u50cf\u6d82\u4e86\u871c\u7cd6\u4f3c\u7684\uff0c\u54c4\u5f97\u6bcd\u4eb2\u5341\u5206\u6b22\u559c\u3002\u4ed6\u88c5\u4f5c\u662f\u6d6a\u5b50\u56de\u5934\uff0c\u8bf4\u5728 \u5173\u5916\u662f\u5982\u4f55\u53d7\u82e6\uff0c\u60f3\u6765\u60f3\u53bb\uff0c\u8fd8\u662f\u56de\u5bb6\u7684\u597d\u3002\u5c60\u592b\u4eba\u53ea\u9053\u513f\u5b50\u5f53\u771f\u662f\u75db\u6094\u524d\u975e\uff0c\u81ea\u4ea6\u559c\u4e4b\u4e0d \u5c3d\u3002\u6bcd\u5b50\u95ee\u8bdd\u5bb6\u5e38\uff0c\u8bf4\u6765\u8bf4\u53bb\uff0c\u7ec8\u4e8e\u628a\u8bdd\u9898\u62c9\u5230\u4e86\u660e\u6167\u516c\u4e3b\u8eab\u4e0a\u3002 \u3000\u3000\u5c60\u9f99\u4f6f\u4f5c\u4e0d\u77e5\u660e\u6167\u516c\u4e3b\u7684\u6765\u5386\uff0c\u4e00\u5f00\u53e3\u5c31\u5148\u8d23\u5907\u59b9\u59b9\u4e0d\u8be5\u6536\u5bb9\u5979\u4eec\uff0c\u5c60\u592b\u4eba\u7ed9\u660e\u6167\u516c\u4e3b \u8fa9\u62a4\uff0c\u5c60\u9f99\u8fd9\u624d\u88c5\u4f5c\u76f8\u4fe1\u6bcd\u4eb2\u7684\u795e\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u771f\u7684\u5417\uff1f\u4f60\u8bf4\u8fd9\u4e24\u4e2a\u8499\u53e4\u5973\u5b50\u53c8\u7f8e\u8c8c\uff0c\u53c8\u6e29 \u67d4\uff0c\u96be\u9053\u5979\u4eec\u8fd8\u8d5b\u5f97\u8fc7\u59b9\u59b9\uff1f\u6211\u53ef\u6709\u70b9\u4e0d\u6562\u76f8\u4fe1\u5462\u3002\u201d \u3000\u3000\u5c60\u51e4\u5567\u9053\uff1a\u201c\u6211\u4e0d\u8981\u4f60\u7ed9\u6211\u6234\u9ad8\u5e3d\uff0c\u6211\u53ea\u529d\u4f60\u53ef\u522b\u6253\u5979\u4eec\u7684\u4e3b\u610f\u3002\u201d \u3000\u3000\u5c60\u592b\u4eba\u5374\u7b11\u9053\uff1a\u201c\u8bf4\u771f\u7684\uff0c\u6211\u4e5f\u60f3\u4e0d\u5230\u8499\u53e4\u7684\u599e\u513f\u7adf\u7136\u6bd4\u54b1\u4eec\u6c49\u4eba\u7684\u59d1\u5a18\u8fd8\u7f8e\u5462\u3002\u4e0d\u662f \u6211\u504f\u8892\u5916\u4eba\uff0c\u98ce\u513f\u4e5f\u7b97\u957f\u5f97\u597d\u770b\u4e86\uff0c\u6bd4\u8fd9\u5979\u4eec\uff0c\u5374\u597d\u50cf\u8fd8\u5dee\u4e00\u622a\u5462\u3002\u8bba\u6e29\u67d4\u90a3\u66f4\u662f\u6bd4\u4e0d\u4e0a\u5979 \u4eec\u4e86\u3002\u51e4\u513f\u4f60\u4e5f\u4e0d\u5fc5\u90a3\u6837\u8bf4\uff0c\u4f60\u5df2\u7ecf\u6709\u4e86\u77f3\u749e\uff0c\u4f60\u7684\u54e5\u54e5\u4e5f\u8be5\u6709\u4e2a\u5ac2\u5b50\u4e86\u3002\u5982\u679c\u4ed6\u6b22\u559c\u4e0a\u54ea \u4e00\u4e2a\u8499\u53e4\u59d1\u5a18\u7684\u8bdd\uff0c\u6211\u5012\u662f\u4e0d\u60f3\u62e6\u963b\u4ed6\u7684\u3002\u201d \u3000\u3000\u5c60\u9f99\u6545\u610f\u7b11\u9053\uff1a\u201c\u6211\u4e0d\u8fc7\u662f\u597d\u5947\u5fc3\u8d77\uff0c\u95ee\u95ee\u7f62\u4e86\u3002\u54ea\u91cc\u5c31\u8c08\u5f97\u5230\u5a5a\u5ac1\u4e4b\u4e8b\uff1f\u201d \u3000\u3000\u5c60\u592b\u4eba\u9053\uff1a\u201c\u9f99\u513f\uff0c\u6211\u5012\u662f\u60f3\u8d77\u4e86\u3002\u4f60\u5230\u8fc7\u8499\u53e4\uff0c\u61c2\u5f97\u5979\u4eec\u7684\u8bdd\uff0c\u548c\u5979\u4eec\u503e\u8c08\u503e\u8c08\uff0c\u5979 \u4eec\u4e00\u5b9a\u4f1a\u500d\u611f\u4eb2\u5207\u7684\uff0c\u5373\u4f7f\u4e0d\u662f\u8c08\u5a5a\u8bba\u5ac1\u4f60\u4e5f\u53ef\u4ee5\u6253\u542c\u6253\u542c\u5979\u4eec\u7684\u8eab\u4e16\uff0c\u5f04\u6e05\u695a\u5979\u4eec\u7684\u6765\u5386 \u5440\u3002\u6211\u5df2\u7ecf\u53eb\u4e2b\u5934\u8bf7\u5979\u4eec\u6765\u6311\u9009\u8863\u6599\u4e86\uff0c\u7b49\u4f1a\u513f\u4f60\u5c31\u53ef\u4ee5\u89c1\u7740\u5979\u4eec\u3002\u201d \u3000\u3000\u5361\u6d1b\u4e1d\u5728\u540e\u7a97\u5077\u542c\uff0c\u542c\u5230\u8fd9\u91cc\uff0c\u5927\u5403\u4e00\u60ca\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4e8b\u4e0d\u5b9c\u8fdf\uff0c\u53ef\u5f97\u8d76\u5feb\u56de\u53bb\u7980\u544a \u516c\u4e3b\u3002\u201d \u3000\u3000\u5979\u6765\u7684\u65f6\u5019\u6ca1\u6709\u78b0\u89c1\u4eba\uff0c\u51fa\u53bb\u7684\u65f6\u5019\uff0c\u8d70\u8fc7\u4e86\u56de\u5eca\uff0c\u8fd9\u624d\u78b0\u89c1\u670d\u4f8d\u5c60\u592b\u4eba\u7684\u90a3\u4e2a\u4e2b\u5934\u6625 \u5170\uff0c\u6625\u5170\u89c1\u5979\u795e\u8272\u5306\u5306\uff0c\u6709\u70b9\u89c9\u5f97\u5947\u602a\uff0c\u4fbf\u62e6\u7740\u5979\u95ee\u9053\uff1a\u201c\u4f60\u662f\u4ece\u6211\u4eec\u8001\u592b\u4eba\u90a3\u513f\u56de\u6765\u7684 \u5417\uff1f\u201d\u5361\u6d1b\u4e1d\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8001\u592b\u4eba\u53eb\u6211\u53bb\u6311\u9009\u8863\u6599\u7684\u3002\u201d\u6625\u5170\u9053\uff1a\u201c\u90a3\u4f60\u4f55\u4ee5\u53cc\u624b\u7a7a\u7a7a\uff1f\u8fd8 \u6709\u4f60\u90a3\u4f4d\u670b\u53cb\u5462\uff0c\u4e3a\u4ec0\u4e48\u5979\u4e0d\u548c\u4f60\u4e00\u8d77\u6765\uff1f\u201d \u3000\u3000\u5e78\u4e8f\u5361\u6d1b\u4e1d\u6709\u70b9\u6025\u624d\uff0c\u7709\u5934\u4e00\u76b1\uff0c\u8c0e\u8bdd\u5df2\u7ecf\u7f16\u597d\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u6765\u5f97\u4e0d\u5de7\uff0c\u539f\u6765\u4f60\u4eec\u7684\u516c \u5b50\u4e5f\u6b63\u662f\u4eca\u5929\u56de\u5bb6\u3002\u6211\u95ef\u4e86\u8fdb\u53bb\uff0c\u53ef\u6709\u70b9\u4e0d\u597d\u610f\u601d\u5462\u3002\u201d \u3000\u3000\u6625\u5170\u9053\uff1a\u201c\u90a3\u6709\u4ec0\u4e48\u5173\u7cfb\uff1f\u6211\u4eec\u6c49\u4eba\u8bfb\u4e66\u4eba\u5bb6\u7684\u5c0f\u59d0\u624d\u8bb2\u7a76\u4ec0\u4e48\u7537\u5973\u6388\u53d7\u4e0d\u4eb2\u8fd9\u5957\u793c \u6cd5\uff0c\u542c\u8bf4\u4f60\u4eec\u8499\u53e4\uff0c\u7537\u5b50\u5973\u5b50\u90fd\u662f\u4e00\u540c\u6253\u730e\u7684\uff0c\u96be\u9053\u4e5f\u8bb2\u7a76\u8fd9\u4e00\u5957\u4e48\uff1f\u201d \u3000\u3000\u5361\u6cbb\u4e1d\u9053\uff1a\u201c\u4e0d\u662f\u8fd9\u4e2a\u610f\u601d\u3002\u8001\u592b\u4eba\u6b63\u5728\u548c\u516c\u5b50\u8bf4\u8bdd\uff0c\u6211\u600e\u597d\u610f\u601d\u9ebb\u70e6\u5979\u5f00\u7bb1\u542f\u67dc\uff0c\u628a \u8863\u6599\u4e00\u5339\u5339\u5730\u642c\u51fa\u6765\u8ba9\u6211\u6311\u9009\uff1f\u4f60\u53c8\u4e0d\u5728\u90a3\u513f\u3002\u201d \u3000\u3000\u6625\u5170\u7b11\u9053\uff1a\u201c\u539f\u6765\u4f60\u662f\u56e0\u4e3a\u8fd9\u6837\u624d\u5306\u5306\u6765\uff0c\u5306\u5306\u800c\u53bb\u7684\uff0c\u5bf9\u4e0d\u4f4f\uff0c\u6211\u521a\u624d\u5949\u4e86\u8001\u592b\u4eba\u4e4b \u547d\uff0c\u7ed9\u516c\u5b50\u7096\u4e00\u76c5\u53c2\u6c64\uff0c\u73b0\u5728\u65b9\u624d\u7096\u597d\uff0c\u4f60\u548c\u6211\u56de\u53bb\u597d\u5417\uff0c\u6211\u53ef\u4ee5\u5e2e\u4f60\u7684\u5fd9\u4e86\u3002\u201d \u3000\u3000\u5361\u6d1b\u4e1d\u9053\uff1a\u201c\u4f60\u4eec\u7684\u516c\u5b50\u521a\u521a\u56de\u6765\uff0c\u6211\u770b\u8fd9\u4e2a\u65f6\u5019\u8fd8\u662f\u4e0d\u5b9c\u4e8e\u6253\u6270\u8001\u592b\u4eba\u5427\uff1f\u6167\u59d0\u8eab\u5b50 \u6709\u70b9\u4e0d\u5927\u8212\u670d\uff0c\u6211\u4e5f\u5f97\u56de\u53bb\u770b\u4e00\u770b\u5979\u3002\u201d \u3000\u3000\u6625\u5170\u9053\uff1a\u201c\u5979\u5f97\u4ec0\u4e48\u75c5\u3002\u201d \u3000\u3000\u5361\u6d1b\u4e1d\u9053\uff1a\u201c\u53ea\u662f\u4e00\u70b9\u4f24\u98ce\u5934\u75db\u7684\u5c0f\u6bdb\u75c5\uff0c\u6ca1\u4ec0\u4e48\u8981\u7d27\u7684\uff0c\u4eca\u665a\u6211\u518d\u548c\u5979\u4e00\u540c\u6765\u95ee\u5019\u8001 \u592b\u4eba\u5427\u3002\u201d \u3000\u3000\u6625\u5170\u542c\u5979\u8bf4\u5f97\u6709\u51ed\u6709\u636e\u3001\u5408\u60c5\u5408\u7406\uff0c\u5bf9\u5979\u5df2\u662f\u6ca1\u6709\u6000\u7591\uff0c\u4fbf\u9053\uff1a\u201c\u90a3\u4e5f\u597d\uff0c\u90a3\u4e48\u4eca\u665a\u54b1 \u4eec\u518d\u89c1\u3002\u201d \u3000\u3000\u5361\u6d1b\u4e1d\u6b63\u8981\u4e3e\u6b65\uff0c\u6625\u5170\u5ffd\u5730\u53c8\u56de\u8fc7\u5934\u6765\uff0c\u5c06\u5979\u5524\u4f4f\u3002\u5361\u6d1b\u4e1d\u53ea\u9053\u81ea\u5df1\u7684\u8c0e\u8a00\u7f16\u5f97\u4e0d\u597d\uff0c \u7ed9\u5979\u542c\u51fa\u4e86\u7834\u7efd\uff0c\u6697\u6697\u5403\u60ca\uff0c\u95ee\u9053\uff1a\u201c\u8fd8\u6709\u4ec0\u4e48\u4e8b\u5417\uff1f\u201d \u3000\u3000\u6625\u5170\u8bf4\u9053\uff1a\u201c\u4f24\u98ce\u867d\u662f\u5c0f\u75c5\uff0c\u4e5f\u5f97\u597d\u597d\u8c03\u7406\u624d\u884c\u3002\u5f85\u6211\u89c1\u8fc7\u4e86\u8001\u592b\u4eba\uff0c\u4fbf\u53bb\u627e\u4e00\u4f4d\u5927\u592b \u7ed9\u4f60\u90a3\u4f4d\u670b\u53cb\u770b\u770b\u3002\u201d \u3000\u3000\u5361\u6d1b\u4e1d\u9053\uff1a\u201c\u7528\u4e0d\u7740\u9ebb\u70e6\u4f60\u4eec\u5c71\u5be8\u7684\u5927\u592b\u4e86\uff0c\u6211\u4eec\u5728\u8349\u539f\u4e0a\u7267\u7f8a\uff0c\u8eab\u5b50\u90e8\u662f\u71ac\u70bc\u60ef\u7684\uff0c \u8fd9\u70b9\u70b9\u5c0f\u75c5\uff0c\u5b9e\u5728\u7b97\u4e0d\u5f97\u4ec0\u4e48\u3002\u201d \u3000\u3000\u6625\u5170\u9053\uff1a\u201c\u8fd8\u662f\u627e\u5927\u592b\u770b\u4e00\u770b\u7684\u597d\u3002\u201d \u3000\u3000\u5361\u6d1b\u4e1d\u88c5\u4f5c\u5341\u5206\u611f\u6fc0\u7684\u6837\u5b50\uff0c\u8bf4\u9053\uff1a\u201c\u59d0\u59d0\uff0c\u4f60\u5f85\u6211\u4eec\u8fd9\u6837\u597d\uff0c\u6211\u771f\u4e0d\u77e5\u5982\u4f55\u62a5\u7b54\u5462\uff0c \u4f60\u4e5f\u5feb\u53bb\u89c1\u8001\u592b\u4eba\u548c\u516c\u5b50\u5427\uff0c\u6050\u6015\u53c2\u6c64\u51b7\u4e86\u3002\u201d\u8c22\u8fc7\u4e86\u6625\u5170\uff0c\u8fde\u5fd9\u4e09\u6b65\u5e76\u4f5c\u4e24\u6b65\uff0c\u8d76\u56de\u4f4f \u6240\uff0c\u6625\u5170\u6367\u7740\u90a3\u76c5\u53c2\u6c64\uff0c\u5374\u53ea\u80fd\u4e00\u6b65\u4e00\u6b65\u5730\u8d70\u3002 \u3000\u3000\u5361\u6d1b\u4e1d\u56de\u5230\u4f4f\u6240\uff0c\u5fd9\u628a\u963f\u76d6\u53eb\u6765\uff0c\u4e00\u540c\u53bb\u89c1\u660e\u6167\u516c\u4e3b\u3002 \u3000\u3000\u660e\u6167\u516c\u4e3b\u9053\uff1a\u201c\u54a6\uff0c\u4f60\u600e\u4e48\u8fd9\u6837\u5feb\u5c31\u56de\u6765\u4e86\uff0c\u8863\u6599\u5462\uff1f\u201d \u3000\u3000\u535c\u6d1b\u4e1d\u9053\uff1a\u201c\u6211\u54ea\u6709\u529f\u592b\u53bb\u62ff\u8863\u6599\uff1f\u516c\u4e3b\uff0c\u6211\u672c\u6765\u4e0d\u8d5e\u6210\u4f60\u79bb\u5f00\u6b64\u5730\u7684\uff0c\u4f46\u73b0\u5728\u54b1\u4eec\u5374 \u662f\u975e\u5f97\u9a6c\u4e0a\u79bb\u5f00\u4e0d\u53ef\u4e86\u3002\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u5403\u60ca\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5361\u6d1b\u4e1d\u9053\uff1a\u201c\u539f\u6765\u54b1\u4eec\u5728\u7834\u5e99\u78b0\u89c1\u7684\u90a3\u4e2a\u5c60\u9f99\uff0c\u5c31\u662f\u5be8\u4e3b\u7684\u54e5\u54e5\u3002\u201d \u3000\u3000\u5c60\u9f99\u79c1\u901a\u8499\u53e4\uff0c\u8fd9\u662f\u660e\u6167\u516c\u4e3b\u65e9\u5df2\u77e5\u9053\u7684\u4e86\uff0c\u95fb\u8a00\u4e4b\u4e0b\uff0c\u4e0d\u7531\u5f97\u9762\u8272\u5927\u53d8\uff0c\u8bf4\u9053\uff1a\u201c\u6211 \u65e9\u8bf4\u6b64\u5730\u4e0d\u662f\u5b89\u8eab\u4e4b\u6240\uff0c\u73b0\u5728\u4f60\u53ef\u76f8\u4fe1\u4e86\u5427\uff1f\u5feb\u8d70\uff0c\u5feb\u8d70\uff01\u201d\u5979\u4e0d\u77e5\u5c60\u51e4\u548c\u54e5\u54e5\u5b8c\u5168\u4e24\u6837\uff0c \u751a\u81f3\u5bf9\u5c60\u51e4\u4e5f\u8d77\u4e86\u6000\u7591\u3002 \u3000\u3000\u963f\u76d6\u626d\u5f00\u5916\u9762\u90a3\u9053\u5927\u95e8\u7684\u94c1\u9501\uff0c\u4e09\u4eba\u76f4\u5954\u4e0b\u5c71\u3002\u5be8\u4e2d\u7684\u5934\u76ee\u8bc6\u5f97\u4ed6\u4eec\u662f\u5be8\u4e3b\u7684\u5ba2\u4eba\uff0c\u867d \u7136\u89c9\u5f97\u5947\u602a\uff0c\u5374\u4e5f\u4e0d\u6562\u62e6\u963b\u3002\u4e0d\u8fc7\u5728\u4ed6\u4eec\u4e0b\u5c71\u7684\u65f6\u5019\uff0c\u628a\u5b88\u5be8\u95e8\u7684\u5934\u76ee\u804c\u8d23\u6538\u5173\uff0c\u5374\u662f\u8981\u5411 \u4ed6\u4eec\u76d8\u95ee\u4e86\u3002\u963f\u76d6\u65e0\u6687\u591a\u8bf4\uff0c\u731b\u7684\u4f7f\u51fa\u8499\u53e4\u6b66\u58eb\u7684\u6454\u89d2\u7edd\u6280\uff0c\u6454\u4e86\u90a3\u5934\u76ee\u4e00\u8de4\uff0c\u53c8\u6253\u7ffb\u4e86\u51e0 \u4e2a\u55bd\u5175\uff0c\u62a2\u4e86\u4e09\u5339\u9a6c\u9a91\u7740\u5c31\u8dd1\u3002 \u3000\u3000\u4e14\u8bf4\u6625\u5170\u628a\u53c2\u6c64\u6367\u4e86\u8fdb\u53bb\uff0c\u7ed9\u4e86\u5c60\u9f99\u4e4b\u540e\uff0c\u4fbf\u4e0e\u5c60\u592b\u4eba\u8bf4\u8d77\u9047\u89c1\u5361\u6d1b\u4e1d\u4e4b\u4e8b\uff0c\u5c60\u592b\u4eba\u5927 \u4e3a\u60ca\u8be7\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u771f\u662f\u5947\u602a\u4e86\uff01\u5979\u5e76\u6ca1\u67091\u6765\u8fc7\u8fd9\u513f\u5440\uff01\u4e3a\u4f55\u5979\u8981\u9a97\u4f60\uff1f\u201d \u3000\u3000\u5c60\u51e4\u6b63\u8981\u6d3e\u4eba\u53bb\u67e5\uff0c\u5de1\u5c71\u7684\u5934\u76ee\u5df2\u662f\u8dd1\u6765\u7980\u62a5\uff1a\u201c\u90a3\u4e09\u4f4d\u5ba2\u4eba\u90fd\u9003\u8d70\u4e86\uff0c\u6253\u4f24\u4e86\u54b1\u4eec\u597d \u51e0\u4e2a\u4eba\uff01\u201d \u3000\u3000\u5c60\u51e4\u60ca\u7591\u4e0d\u5b9a\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4ed6\u4eec\u662f\u6768\u5a49\u4ecb\u7ecd\u6765\u7684\uff0c\u7167\u7406\u4e0d\u8be5\u662f\u8499\u53e4\u7684\u5bc6\u63a2\uff0c\u4f46\u5374\u4f55\u4ee5 \u8981\u9003\u8dd1\u5462\uff1f\u201d \u3000\u3000\u5c60\u9f99\u4e00\u8dc3\u800c\u8d77\uff0c\u8bf4\u9053\uff1a\u201c\u5f85\u6211\u53bb\u628a\u4ed6\u4eec\u62ff\u56de\u6765\uff01\u201d \u3000\u3000\u5c60\u51e4\u9053\uff1a\u201c\u4e0d\u5fc5\u4f60\u591a\u7ba1\u95f2\u4e8b\uff0c\u6211\u81ea\u5df1\u53bb\u8ffd\uff01\u201d \u3000\u3000\u5c60\u9f99\u9053\uff1a\u201c\u4f60\u662f\u5c71\u5be8\u4e4b\u4e3b\uff0c\u4e0d\u53ef\u8f7b\u6613\u79bb\u5c71\uff0c\u8fd9\u70b9\u6211\u6765\u529e\u597d\u628a\uff01\u201d\u5c60\u51e4\u62e6\u963b\u4e0d\u4e86\uff0c\u53ea\u597d\u8ba9 \u4ed6\u53bb\u8ffd\u3002 \u3000\u3000\u5c60\u592b\u4eba\u53f9\u9053\uff1a\u201c\u975e\u6211\u65cf\u7c7b\uff0c\u5176\u5fc3\u5fc5\u5f02\u3002\u8fd9\u53e5\u8bdd\u5f53\u771f\u8bf4\u5f97\u4e0d\u9519\u3002\u201d \u3000\u3000\u5c60\u51e4\u8d8a\u60f3\u8d8a\u89c9\u5947\u602a\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u5bf9\u3002\u201d\u5c60\u592b\u4eba\u9053\uff1a\u201c\u4ec0\u4e48\u4e0d\u5bf9\uff1f\u201d\u5c60\u51e4\u8bf4\u9053\uff1a\u201c\u5979\u4eec\u4e4b \u8d70\uff0c\u5176\u4e2d\u5b9a\u6709\u56e0\u7531\u3002\u6211\u53ef\u4e0d\u80fd\u8ba9\u54e5\u54e5\u96be\u4e3a\u5979\u4eec\u3002\u201d\u5c60\u592b\u4eba\u9053\uff1a\u201c\u5509\uff0c\u4f60\u603b\u662f\u76f8\u4fe1\u4e0d\u8fc7\u54e5\u54e5\uff0c \u6837\u6837\u90fd\u8981\u548c\u4ed6\u4f5c\u5bf9\u3002\u201d \u3000\u3000\u5c60\u51e4\u4e00\u6765\u662f\u4e0d\u613f\u6bcd\u4eb2\u751f\u6c14\uff0c\u4e8c\u6765\u5979\u867d\u7136\u76f8\u4fe1\u660e\u6167\u516c\u4e3b\u4e0d\u662f\u574f\u4eba\uff0c\u4f46\u4e5f\u4e0d\u80fd\u4e0d\u63d0\u9632\u4e07\u4e00\u3002 \u8bda\u5982\u5c60\u9f99\u6240\u8bf4\uff0c\u5979\u662f\u4e00\u5be8\u4e4b\u4e3b\uff0c\u786e\u662f\u4e0d\u4fbf\u8f7b\u79bb\u3002\u4e8e\u662f\u4fbf\u628a\u4ee4\u7bad\u4ea4\u7ed9\u77f3\u749e\uff0c\u53eb\u77f3\u749e\u9a6c\u4e0a\u5e26\u4eba\u53bb \u8ffd\u3002\u77f3\u749e\u8bf4\u9053\uff1a\u201c\u5927\u5e08\u54e5\u5df2\u7ecf\u5e26\u9886\u51e0\u4e2a\u5934\u76ee\u4e0b\u5c71\u53bb\u4e86\u3002\u201d \u3000\u3000\u5c60\u51e4\u8bf4\u9053\uff1a\u201c\u6211\u5c31\u6050\u6015\u54e5\u54e5\u80e1\u4f5c\u975e\u4e3a\uff0c\u6240\u4ee5\u8981\u4f60\u706b\u901f\u8d76\u53bb\u5236\u6b62\u4ed6\u3002\u201d\u77f3\u749e\u9053\uff1a\u201c\u5927\u5e08\u5144 \u600e\u80af\u542c\u6211\u7684\u8bdd\uff1f\u201d\u5c60\u51e4\u9053\uff1a\u201c\u4ed6\u4e0d\u670d\u4f60\u4e5f\u8be5\u670d\u8fd9\u652f\u4ee4\u7bad\uff0c\u9664\u975e\u4ed6\u4ee5\u540e\u4e0d\u518d\u56de\u6765\uff01\u201d \u3000\u3000\u539f\u6765\u5c60\u51e4\u6700\u62c5\u5fc3\u7684\u8fd8\u53ea\u662f\u6050\u6015\u5c60\u9f99\u72af\u4e86\u597d\u8272\u7684\u6bdb\u75c5\uff0c\u8bf4\u4e0d\u5b9a\u4f1a\u4fae\u8fb1\u90a3\u4e24\u4e2a\u8499\u53e4\u59d1\u5a18\uff0c\u5374 \u60f3\u4e0d\u5230\u5c60\u9f99\u53e6\u6709\u6bd4\u8fd9\u4e2a\u66f4\u5351\u9119\u5f97\u591a\u7684\u4f01\u56fe\u3002\u662f\u4ee5\u5979\u4ee5\u5be8\u4e3b\u7684\u8eab\u4efd\u53d1\u51fa\u4ee4\u7bad\uff0c\u4ee5\u4e3a\u5c60\u9f99\u81ea\u4f1a\u6743 \u8861\u8f7b\u91cd\uff0c\u4e0d\u81f3\u4e8e\u56e0\u4e3a\u4e24\u4e2a\u5973\u5b50\u7684\u7f18\u6545\uff0c\u81ea\u7edd\u5bb6\u95e8\u3002 \u3000\u3000\u963f\u76d6\u592b\u59bb\u548c\u660e\u6167\u516c\u4e3b\u867d\u7136\u7cbe\u4e8e\u9a91\u672f\uff0c\u53ef\u60dc\u4ed6\u4eec\u62a2\u6765\u7684\u9a6c\u5339\u4e0d\u8fc7\u662f\u666e\u901a\u55bd\u5175\u7684\u5750\u9a91\uff0c\u5c60\u9f99 \u5e26\u9886\u51e0\u4e2a\u5fc3\u8179\u5934\u76ee\u9a91\u4e86\u5feb\u9a6c\u53bb\u8ffd\uff0c\u4e0d\u6d88\u591a\u4e45\uff0c\u5c31\u8ffd\u4e0a\u4ed6\u4eec\u4e86\u3002 \u3000\u3000\u660e\u6167\u516c\u4e3b\u6012\u9053\uff1a\u201c\u5c60\u9f99\uff0c\u4f60\u6562\u5bf9\u6211\u65e0\u793c\uff01\u201d\u5c60\u9f99\u7b11\u563b\u563b\u5730\u9053\uff1a\u201c\u4e0d\u6562\uff0c\u6211\u6b63\u662f\u56e0\u4e3a\u672a\u66fe \u5c3d\u5730\u4e3b\u4e4b\u8c0a\uff0c\u6240\u4ee5\u624d\u8d76\u6765\u8bf7\u4f60\u56de\u53bb\u7684\u3002\u4f60\u653e\u5fc3\uff0c\u4f60\u7684\u8eab\u4efd\u6211\u662f\u51b3\u4e0d\u4f1a\u6cc4\u6f0f\u7684\u3002\u201d\u660e\u6167\u516c\u4e3b\u65a5 \u9053\uff1a\u201c\u6211\u4e0d\u53d7\u4f60\u7684\u6b3e\u5f85\uff0c\u4f60\u7ed9\u6211\u6eda\u56de\u53bb\uff01\u201d\u5c60\u9f99\u6d8e\u7740\u8138\u7b11\u9053\uff1a\u201c\u6211\u8fd9\u662f\u4e00\u756a\u597d\u610f\uff0c\u4f60\u53c8\u4f55\u5fc5 \u62d2\u4eba\u4e8e\u5343\u91cc\u4e4b\u5916\uff1f\u201d \u3000\u3000\u90a3\u51e0\u4e2a\u5934\u76ee\u4e0d\u77e5\u660e\u6167\u516c\u4e3b\u7684\u8eab\u4efd\uff0c\u60f3\u8981\u8ba8\u597d\u5c60\u9f99\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u5440\uff0c\u6211\u4eec\u7684\u5c11\u5be8\u4e3b\u7ed9\u4f60\u9762 \u5b50\u4f60\u4e0d\u8981\uff0c\u90a3\u5c31\u53ea\u597d\u8ba9\u4f60\u5403\u7f5a\u9152\u4e86\u3002\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u8fd9\u8fb9\uff0c\u963f\u76d6\u4ea6\u5df2\u52c3\u7136\u5927\u6012\uff0c\u559d\u9053\uff1a\u201c\u653e\u4f60\u7684\u5c41\uff0c\u54ea\u6709\u8fd9\u6837\u9080\u8bf7\u5ba2\u4eba\u4e4b\u7406\uff01\u597d \u5440\uff0c\u53eb\u4f60\u4eec\u6eda\u56de\u53bb\u4f60\u4eec\u4e0d\u542c\uff0c\u90a3\u5c31\u6765\u5427\u3002\u201d\u4e00\u4e2a\u5934\u76ee\u6b63\u81ea\u62cd\u9a6c\u8d77\u6765\uff0c\u548c\u660e\u6167\u516c\u4e3b\u7684\u8ddd\u79bb\u5df2\u662f \u4e0d\u5230\u4e09\u4e08\uff0c\u5ffd\u542c\u5f97\u201c\u547c\u201d\u7684\u4e00\u58f0\uff0c\u963f\u76d6\u624b\u4e2d\u6325\u51fa\u957f\u7ef3\uff0c\u767b\u65f6\u5c06\u4ed6\u5957\u4f4f\uff0c\u5c0f\u9e21\u4e00\u822c\u5730\u63d0\u4e86\u8d77 \u6765\u3002 \u3000\u3000\u660e\u6167\u516c\u4e3b\u8bf4\u9053\uff1a\u201c\u770b\u5728\u6768\u59d1\u5a18\u4efd\u4e0a\uff0c\u4e0d\u53ef\u5f97\u7f6a\u5979\u7684\u670b\u53cb\u3002\u653e\u4e86\u8fd9\u4eba\uff01\u201d\u963f\u76d6\u632f\u81c2\u4e00\u6296\u957f \u7ef3\uff0c\u5c06\u8fd9\u4eba\u629b\u51fa\u5341\u51e0\u4e08\u4e4b\u5916\u3002\u5176\u4ed6\u51e0\u4e2a\u5934\u76ee\u89c1\u4e86\u963f\u76d6\u7684\u7ef3\u5708\u7edd\u6280\u90fd\u60ca\u5f97\u5446\u4e86\u3002 \u3000\u3000\u5c60\u9f99\u4ecd\u662f\u7b11\u563b\u563b\u5730\u8bf4\u9053\uff1a\u201c\u660e\u6167\u516c\u4e3b\uff0c\u4e0d\u662f\u6211\u6562\u5bf9\u4f60\u5192\u72af\uff01\u65e0\u8010\u4f60\u8fd9\u624b\u4e0b\u592a\u8fc7\u65e0\u793c\uff0c\u6211 \u53ea\u597d\u7ed9\u4ed6\u4e00\u70b9\u6559\u8bad\u4e86\u3002\u201d \u3000\u3000\u963f\u76d6\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b50\u6700\u662f\u65e0\u803b\uff0c\u6211\u8fd8\u4e0d\u5c51\u6559\u8bad\u4f60\u5462\uff01\u201d\u53ea\u89c1\u4ed6\u957f\u7ef3\u4e00\u6296\uff0c\u547c\u547c\u98ce\u54cd\uff0c \u5f53\u4f5c\u8f6f\u97ad\u6765\u4f7f\uff0c\u5411\u5c60\u9f99\u626b\u53bb\u3002\u5c60\u9f99\u8bc6\u5f97\u5389\u5bb3\uff0c\u4e0d\u6562\u4e0e\u4ed6\u9a6c\u4e0a\u4ea4\u950b\uff0c\u5f53\u4e0b\u4e00\u4e2a\u201c\u9ec4\u97f5\u51b2\u9704\u201d\uff0c \u98de\u8eab\u4e0a\u8d77\uff0c\u843d\u4e0b\u5730\u6765\uff0c\u907f\u8fc7\u7ef3\u97ad\u7684\u626b\u6253\u3002 \u3000\u3000\u660e\u6167\u516c\u4e3b\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u867d\u7136\u53ef\u6076\uff0c\u4e5f\u4e0d\u5fc5\u7406\u4f1a\u4ed6\u4e86\uff0c\u8d70\u5427\uff01\u201d\u5979\u53ea\u9053\u5c60\u9f99\u5df2\u7ecf\u843d\u9a6c\uff0c\u81ea \u662f\u4e0d\u6562\u518d\u8ffd\u3002 \u3000\u3000\u54ea\u77e5\u963f\u76d6\u521a\u521a\u62d4\u8f6c\u9a6c\u5934\uff0c\u5c60\u9f99\u4fbf\u51b7\u7b11\u9053\uff1a\u201c\u6765\u800c\u4e0d\u5f80\u975e\u793c\u4e5f\uff0c\u8ba9\u4f60\u4e5f\u5c1d\u5c1d\u6211\u7684\u6697\u5668\u6ecb \u5473\u3002\u201d\u4e00\u6296\u624b\uff0c\u53d1\u51fa\u4e86\u4e09\u652f\u6bd2\u9f99\u9556\u3002\u963f\u76d6\u542c\u5f97\u80cc\u540e\u98ce\u58f0\uff0c\u53cd\u624b\u63a5\u9556\u3002\u6b63\u662f\uff1a \u3000\u3000\u660e\u67aa\u5bb9\u6613\u8eb2\uff0c\u6697\u7bad\u6700\u96be\u9632\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b\u2014\u2014\u7b2c\u56db\u5341\u56db\u56de\u3000\u8fa3\u624b\u91cd\u65bd\u6b3a\u5f31\u8d28\u3000\u5927\u4ec7\u672a\u62a5\u6597\u7fa4\u9b54 \u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b \u7b2c\u56db\u5341\u56db\u56de\u3000\u8fa3\u624b\u91cd\u65bd\u6b3a\u5f31\u8d28\u3000\u5927\u4ec7\u672a\u62a5\u6597\u7fa4\u9b54 \u3000\u3000\u54ea\u77e5\u6bd2\u9f99\u9556\u4e43\u662f\u6dec\u8fc7\u5267\u6bd2\u7684\u6697\u5668\uff0c\u963f\u76d6\u63a5\u7740\u98de\u9556\uff0c\u867d\u7136\u6ca1\u7ed9\u523a\u4f24\uff0c\u638c\u5fc3\u6cbe\u4e86\u6bd2\u836f\uff0c\u767b\u65f6 \u4fbf\u6709\u4e86\u4e00\u80a1\u9ebb\u75d2\u75d2\u7684\u611f\u89c9\u3002\u963f\u76d6\u53c8\u60ca\u53c8\u6012\uff0c\u9a82\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b50\uff0c\u6562\u7528\u6bd2\u9556\u6697\u7b97\u8001\u5b50\uff01\u201d \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u7b2c\u4e8c\u652f\u7b2c\u4e09\u652f\u6bd2\u9f99\u9556\u5df2\u662f\u5206\u5934\u5411\u660e\u6167\u516c\u4e3b\u548c\u5361\u6d1b\u4e1d\u6253\u53bb\uff0c\u4f46\u5374\u662f\u5c04\u9a6c \u800c\u975e\u5c04\u4eba\uff0c\u6bd2\u9f99\u9556\u89c1\u8840\u5c01\u5589\uff0c\u5979\u4eec\u7684\u5750\u9a91\u7ed9\u6bd2\u9f99\u9556\u6253\u4e2d\uff0c\u4e0d\u6d88\u7247\u523b\uff0c\u5df2\u662f\u53e3\u5410\u767d\u6c90\uff0c\u5012\u5728\u5730 \u4e0a\u3002\u5750\u9a91\u5012\u4e0b\uff0c\u660e\u6167\u516c\u4e3b\u548c\u5361\u6d1b\u4e1d\u5f53\u7136\u4e5f\u662f\u7ed9\u6454\u4e0b\u6765\u7684\u4e86\u3002 \u3000\u3000\u5361\u6d1b\u4e1d\u4e0d\u4f1a\u6b66\u827a\uff0c\u660e\u6167\u516c\u4e3b\u5374\u662f\u7cbe\u901a\u9a91\u5c04\uff0c\u5f88\u6709\u51e0\u5206\u672c\u9886\u7684\uff0c\u5f53\u4e0b\u7acb\u5373\u62d4\u51fa\u5b9d\u5251\uff0c\u4fdd\u62a4 \u5361\u6d1b\u4e1d\u3002 \u3000\u3000\u5c60\u9f99\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u4e0d\u53ef\u5bf9\u5979\u65e0\u793c\uff0c\u7ed9\u6211\u5148\u56f4\u7740\u5979\uff0c\u522b\u8ba9\u5979\u8dd1\u5c31\u884c\u4e86\u3002\u201d\u90a3\u51e0\u4e2a\u5934\u76ee\u5e94\u58f0 \u800c\u4e0a\uff0c\u51e0\u5339\u9a6c\u56f4\u7ed5\u7740\u660e\u6167\u516c\u4e3b\u6253\u8f6c\uff0c\u5374\u4e0d\u4e0e\u5979\u4ea4\u950b\u3002\u660e\u6167\u516c\u4e3b\u82e5\u5728\u9a6c\u4e0a\uff0c\u672a\u5fc5\u4f1a\u8f93\u7ed9\u90a3\u51e0\u4e2a \u5934\u76ee\uff0c\u4f46\u5728\u9a6c\u4e0b\uff0c\u5374\u662f\u65e0\u6cd5\u65bd\u5c55\u6240\u957f\uff0c\u800c\u4e14\u5979\u8fd8\u8981\u4fdd\u62a4\u5361\u6d1b\u4e1d\uff0c\u53ea\u597d\u4ed7\u5251\u4e25\u9632\uff0c\u5bc4\u671b\u4e8e\u963f\u76d6 \u53d6\u80dc\u4e86\u3002 \u3000\u3000\u660e\u6167\u516c\u4e3b\u4e0e\u5361\u6d1b\u4e1d\u5750\u9a91\u88ab\u5c04\uff0c\u4eba\u53d7\u5305\u56f4\uff0c\u963f\u76d6\u81ea\u662f\u4e0d\u80fd\u72ec\u81ea\u9a91\u9a6c\u9003\u8dd1\uff0c\u53ea\u597d\u4e5f\u8df3\u4e0b\u9a6c \u6765\uff0c\u8dd1\u8fc7\u53bb\u63f4\u6551\u5979\u4eec\u3002\u5c60\u9f99\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u662f\u6ce5\u83e9\u8428\u81ea\u8eab\u96be\u4fdd\uff0c\u8fd8\u8981\u6551\u4eba\uff01\u563f\uff0c\u563f\uff0c\u4f60\u4e0d\u7528 \u62c5\u5fc3\u5979\u4eec\uff0c\u6211\u662f\u4e0d\u4f1a\u4f24\u5979\u4eec\u4e00\u6839\u6beb\u6bdb\u7684\uff0c\u4f46\u5bf9\u4f60\u561b\uff0c\u6211\u53ef\u662f\u4e0d\u80fd\u5ba2\u6c14\u4e86\u3002\u4f60\u62c5\u5fc3\u5979\u4eec\uff0c\u4e0d\u5982 \u62c5\u5fc3\u81ea\u5df1\u5427\uff01\u201d \u3000\u3000\u5c60\u9f99\u7684\u8f7b\u529f\u662f\u7ecf\u8fc7\u82e6\u7ec3\u7684\uff0c\u963f\u76d6\u5728\u6df1\u5c71\u6253\u730e\uff0c\u65f6\u5e38\u8ffd\u9010\u91ce\u517d\uff0c\u8eab\u624b\u4e5f\u662f\u77eb\u6377\u4e0d\u51e1\uff0c\u4f46\u5374 \u6bd4\u4e0d\u8fc7\u5c60\u9f99\u82e6\u7ec3\u7684\u8f7b\u529f\u3002\u5c60\u9f99\u51e0\u4e2a\u8d77\u4f0f\uff0c\u4fbf\u5df2\u62a2\u5728\u963f\u76d6\u524d\u5934\uff0c\u62e6\u4f4f\u4e86\u4ed6\u7684\u53bb\u8def\u3002 \u3000\u3000\u963f\u76d6\u5927\u6012\u559d\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u6211\u4e0e\u4f60\u62fc\u4e86\uff01\u201d \u3000\u3000\u5c60\u9f99\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u4ee5\u4e3a\u6211\u5f53\u771f\u6253\u4e0d\u8fc7\u4f60\u5417\uff0c\u597d\uff0c\u4e14\u53eb\u4f60\u77e5\u9053\u6211\u7684\u5389\u5bb3\u4e86\u3002\u201d \u3000\u3000\u963f\u76d6\u638c\u5fc3\u4e2d\u6bd2\uff0c\u5e78\u800c\u76ae\u80a4\u6ca1\u6709\u7834\u635f\uff0c\u4ed7\u7740\u76ae\u7c97\u8089\u539a\uff0c\u6bd2\u6c14\u4e00\u65f6\u95f4\u672a\u80fd\u4fb5\u5165\u4ed6\u7684\u8eab\u4f53\uff0c\u8fd8 \u53ef\u62b5\u654c\u3002\u4e0d\u8fc7\u4ea4\u624b\u51e0\u62db\u4e4b\u540e\uff0c\u53f3\u624b\u5df2\u662f\u6e10\u6e10\u4f7f\u5524\u4e0d\u7075\u3002 \u3000\u3000\u963f\u76d6\u6362\u8fc7\u5de6\u624b\u6267\u7ef3\uff0c\u7ee7\u7eed\u548c\u5c60\u9f99\u6076\u6597\u3002\u4f46\u4ed6\u5de6\u624b\u6325\u821e\u957f\u7ef3\uff0c\u5374\u662f\u4e0d\u53ca\u53f3\u624b\u7684\u7075\u6d3b\u3002 \u3000\u3000\u5c60\u9f99\u89c1\u4ed6\u5982\u6b64\u987d\u5f3a\uff0c\u4e5f\u662f\u6709\u70b9\u5403\u60ca\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u82e5\u4e0d\u662f\u4ed6\u63a5\u4e86\u6211\u7684\u6bd2\u9f99\u9556\uff0c\u8981\u964d\u4f0f\u4ed6 \u53ea\u6015\u8fd8\u5f53\u771f\u4e0d\u6613\u5462\u3002\u201d \u3000\u3000\u6570\u5341\u62db\u8fc7\u540e\uff0c\u963f\u76d6\u90a3\u4e00\u80a1\u9ebb\u75d2\u75d2\u7684\u611f\u89c9\u5df2\u4ece\u53f3\u81c2\u5411\u4e0a\u8513\u5ef6\uff0c\u6c14\u529b\u53c8\u51cf\u4e86\u51e0\u5206\u3002\u4ed6\u7684\u957f\u7ef3 \u62db\u6570\u662f\u4ece\u6355\u6349\u91ce\u517d\u4e4b\u4e2d\u81ea\u5df1\u521b\u51fa\u6765\u7684\uff0c\u53ef\u8bf4\u662f\u81ea\u6210\u4e00\u5bb6\uff0c\u4f46\u56e0\u672a\u7ecf\u8fc7\u540d\u5e08\u6307\u70b9\uff0c\u7a76\u7adf\u4e0d\u53ca\u5c60 \u9f99\u6240\u4f7f\u7684\u5251\u6cd5\u7cbe\u5de7\u3002\u518d\u52a0\u4e0a\u6c14\u529b\u4e0d\u6d4e\uff0c\u65f6\u95f4\u4e00\u957f\uff0c\u5f53\u7136\u5c31\u4e0d\u662f\u5c60\u9f99\u7684\u5bf9\u624b\u4e86\u3002\u5c60\u9f99\u4e00\u770b\u65f6\u673a \u5df2\u5230\uff0c\u8fde\u4f7f\u51e0\u62db\u8fdb\u624b\u7684\u62db\u6570\uff0c\u5251\u5149\u95ea\u70c1\u4e4b\u4e2d\uff0c\u963f\u76d6\u7684\u957f\u7ef3\u4e00\u6bb5\u6bb5\u7ed9\u4ed6\u524a\u65ad\uff0c\u53d8\u6210\u4e86\u4e09\u5c3a\u4e0d\u5230 \u7684\u77ed\u7ef3\u4e86\u3002 \u3000\u3000\u773c\u770b\u963f\u76d6\u5c31\u8981\u62b5\u654c\u4e0d\u4f4f\uff0c\u5ffd\u542c\u5f97\u9a6c\u94c3\u58f0\u54cd\uff0c\u539f\u6765\u662f\u77f3\u749e\u8d76\u5230\u4e86\u3002 \u3000\u3000\u77f3\u749e\u53eb\u9053\uff1a\u201c\u5927\u5e08\u54e5\u4f4f\u624b\uff01\u201d\u5c60\u9f99\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u77f3\u749e\u9053\uff1a\u201c\u585e\u4e3b\u5429\u5490\uff0c\u4e0d\u53ef\u5f97\u7f6a\u5ba2 \u4eba\uff0c\u627e\u662f\u6765\u8bf7\u5979\u4eec\u56de\u53bb\u7684\uff01\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u9053\uff1a\u201c\u4f60\u4eec\u786c\u6765\u4e5f\u597d\uff0c\u8f6f\u6765\u4e5f\u597d\uff0c\u8bf4\u4ec0\u4e48\u6211\u4e5f\u662f\u4e0d\u56de\u53bb\u7684\u4e86\u3002\u201d \u3000\u3000\u77f3\u749e\u7ffb\u8eab\u4e0b\u9a6c\uff0c\u8bf4\u9053\uff1a\u201c\u4e24\u4f4d\u59d1\u5a18\u8bf7\u522b\u8bef\u4f1a\uff0c\u6211\u4eec\u51b3\u65e0\u574f\u610f\uff0c\u4e24\u4f4d\u82e5\u662f\u4e0d\u80af\u5728\u5c0f\u5be8\u7559 \u4e0b\uff0c\u6211\u4eec\u4e5f\u4e0d\u4f1a\u52c9\u5f3a\u3002\u4e0d\u8fc7\u60f3\u8bf7\u4e24\u4f4d\u53bb\u5bf9\u6211\u4eec\u7684\u5be8\u4e3b\u8bf4\u4e00\u58f0\u3002\u201d \u3000\u3000\u5c60\u9f99\u5ffd\u5730\u75be\u8fdb\u4e00\u62db\uff0c\u5c06\u963f\u76d6\u903c\u5230\u4e86\u77f3\u749e\u9762\u524d\uff0c\u77f3\u749e\u53eb\u9053\uff1a\u201c\u5927\u5e08\u54e5\uff0c\u8fd9\u662f\u5be8\u4e3b\u7684\u5429\u5490\uff0c \u8bf7\u4f4f\u624b\u5427\uff01\u201d \u3000\u3000\u5c60\u9f99\u51b7\u7b11\u9053\uff1a\u201c\u597d\uff0c\u4f60\u53eb\u6211\u4f4f\u624b\uff0c\u8fd9\u86ee\u5b50\u4ea4\u7ed9\u4f60\u4e86\uff01\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u4e00\u4e2a\u8f6c\u8eab\uff0c\u7a81\u7136\u5c31\u5411 \u660e\u6167\u516c\u4e3b\u6251\u53bb\u3002 \u3000\u3000\u660e\u6167\u516c\u4e3b\u6253\u5b9a\u4e3b\u610f\uff0c\u62fc\u7740\u4e0e\u4ed6\u4e24\u8d25\u4ff1\u4f24\uff0c\u82e5\u662f\u4f24\u4ed6\u4e0d\u5f97\uff0c\u4fbf\u5373\u56de\u5251\u81ea\u520e\u3002\u54ea\u77e5\u5c60\u9f99\u51fa\u624b \u5feb\u6781\uff0c\u660e\u6167\u516c\u4e3b\u7684\u77ed\u5251\u521a\u521a\u523a\u51fa\uff0c\u53ea\u89c9\u864e\u53e3\u4e00\u9ebb\uff0c\u5df2\u662f\u7ed9\u4ed6\u70b9\u7740\u4e86\u7a74\u9053\u3002\u5c60\u9f99\u593a\u4e86\u5979\u7684\u77ed \u5251\uff0c\u5c06\u5979\u631f\u5728\u80c1\u4e0b\u3002 \u3000\u3000\u963f\u76d6\u5927\u6012\u9053\uff1a\u201c\u597d\u5440\uff0c\u4f60\u4eec\u90fd\u4e0d\u662f\u597d\u4eba\uff01\u201d\u6b64\u65f6\u4ed6\u6b63\u597d\u4e0e\u77f3\u749e\u78b0\u4e0a\uff0c\u5927\u6012\u4e4b\u4e0b\uff0c\u4e0d\u5206\u7682 \u767d\uff0c\u671d\u7740\u77f3\u749e\uff0c\u5288\u9762\u4fbf\u662f\u4e00\u62f3\u3002 \u3000\u3000\u77f3\u749e\u6025\u4e8e\u62e6\u963b\u5e08\u5144\uff0c\u4f46\u7ed9\u963f\u76d6\u7f20\u4f4f\uff0c\u65e0\u53ef\u5948\u4f55\uff0c\u53ea\u597d\u4f7f\u51fa\u64d2\u62ff\u624b\u7684\u529f\u592b\u53cd\u6263\u4ed6\u7684\u624b\u8155\u3002 \u3000\u3000\u963f\u76d6\u672c\u9886\u5b9e\u662f\u4e0d\u5f31\uff0c\u4ed6\u82e5\u6709\u957f\u7ef3\u5728\u624b\uff0c\u77f3\u749e\u672a\u5fc5\u80dc\u5f97\u4e86\u4ed6\u3002\u4f46\u6b64\u9645\u4ed6\u4e00\u6765\u662f\u56e0\u4e3a\u5931\u4e86\u60ef \u7528\u7684\u5175\u5668\uff0c\u62f3\u811a\u529f\u529b\u6bd4\u4e0d\u8fc7\u77f3\u749e\uff1b\u4e8c\u6765\u4ed6\u548c\u5c60\u9f99\u6076\u6597\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u5df2\u662f\u6597\u5f97\u7b4b\u75b2\u529b\u7aed\u3002\u6570\u62db \u4e4b\u540e\uff0c\u7ed9\u77f3\u749e\u89d1\u4e2a\u6b63\u7740\uff0c\u4e00\u628a\u626d\u7740\u4e86\u4ed6\u7684\u624b\u7897\uff0c\u8bf4\u58f0\u201c\u5f97\u7f6a\u201d\u987a\u624b\u4e5f\u70b9\u4e86\u4ed6\u7684\u7a74\u9053\u3002 \u3000\u3000\u77f3\u749e\u8d76\u5fd9\u8dd1\u8fc7\u53bb\u53eb\u9053\uff1a\u201c\u5927\u5e08\u54e5\uff0c\u8fd9\u662f\u5be8\u4e3b\u7684\u4ee4\u7bad\uff0c\u8bf7\u4f60\u770b\u770b\uff01\u201d \u3000\u3000\u5c60\u9f99\u4ecd\u7136\u631f\u7740\u660e\u6167\u516c\u4e3b\uff0c\u5de6\u624b\u4e00\u4f38\uff0c\u628a\u90a3\u4ee4\u7bad\u62ff\u4e86\u8fc7\u6765\uff0c\u6298\u4e3a\u4e24\u6bb5\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u7405\u739b \u5c71\u672c\u6765\u662f\u6211\u7684\uff0c\u51e4\u4e2b\u5934\u64c5\u81ea\u505a\u4e86\u5be8\u4e3b\uff0c\u4f46\u770b\u5728\u5144\u59b9\u4efd\u4e0a\uff0c\u624d\u4e0d\u4e0e\u5979\u8ba1\u8f83\uff0c\u5979\u8fd8\u6562\u8981\u6211\u542c\u5979\u547d \u4ee4\uff1f\u201d \u3000\u3000\u77f3\u749e\u53c8\u60ca\u53c8\u6012\uff0c\u8bf4\u9053\uff1a\u201c\u5be8\u4e3b\u662f\u5be8\u4e2d\u4e0a\u4e0b\u63a8\u9009\u51fa\u6765\u7684\uff0c\u6211\u53ea\u77e5\u670d\u4ece\u5be8\u4e3b\u3002\u5927\u5e08\u5144\u4f60\u6709\u8bdd \u8bf4\u8bf7\u56de\u53bb\u548c\u5be8\u4e3b\u8bf4\u3002\u73b0\u5728\u6211\u53ea\u8981\u4f60\u653e\u4eba\u3002\u201d \u3000\u3000\u5c60\u9f99\u559d\u9053\uff1a\u201c\u7ed9\u6211\u6eda\u5f00\uff01\u4f60\u662f\u4ec0\u4e48\u4e1c\u897f\uff0c\u5c45\u7136\u4e5f\u6562\u5728\u6211\u9762\u524d\u6307\u624b\u5212\u811a\uff01\u201d\u5530\u7684\u4fbf\u662f\u4e00 \u5251\u3002 \u3000\u3000\u77f3\u749e\u53ea\u597d\u6301\u5251\u62b5\u6321\uff0c\u4ed6\u7684\u6b66\u529f\u672c\u6765\u4e0d\u53ca\u5c60\u9f99\uff0c\u53c8\u6015\u8bef\u4f24\u660e\u6167\u516c\u4e3b\uff0c\u4e0d\u8fc7\u6570\u62db\uff0c\u4fbf\u7ed9\u5c60\u9f99 \u6740\u5f97\u8fde\u8fde\u540e\u9000\u3002 \u3000\u3000\u5c60\u9f99\u903c\u9000\u4e86\u77f3\u749e\uff0c\u7acb\u5373\u631f\u7740\u660e\u6167\u516c\u4e3b\uff0c\u98de\u8eab\u4e00\u8dc3\uff0c\u62a2\u4e86\u4e00\u4e2a\u5934\u76ee\u7684\u5750\u9a91\uff0c\u8fd9\u4e2a\u5934\u76ee\u672c\u6765 \u662f\u4ed6\u7684\u4eba\uff0c\u89c1\u4ed6\u6251\u6765\uff0c\u5148\u81ea\u8df3\u4e0b\u9a6c\u53bb\u4e86\u3002 \u3000\u3000\u5c60\u9f99\u7b11\u9053\uff1a\u201c\u516c\u4e3b\u4f60\u522b\u5bb3\u6015\uff0c\u6211\u9001\u4f60\u56de\u53bb\u3002\u201d\u660e\u6167\u516c\u4e3b\u65a5\u9053\uff1a\u201c\u4f60\u8fd9\u65e0\u803b\u4e4b\u5f92\uff0c\u60f3\u8981\u628a \u6211\u600e\u6837\uff1f\u201d \u3000\u3000\u5c60\u9f99\u9053\uff1a\u201c\u6211\u8fd9\u662f\u4e3a\u4f60\u7684\u597d\u5440\uff0c\u5176\u5b9e\u4f60\u4e5f\u771f\u50bb\uff0c\u653e\u7740\u5bcc\u8d35\u8363\u534e\u4e0d\u4eab\uff0c\u5374\u8dd1\u6765\u5bc4\u4eba\u7bf1\u4e0b\uff0c \u4f55\u82e6\u6765\u7531\uff1f\u4e0d\u8fc7\u6210\u8bb8\u4f60\u662f\u6709\u4ec0\u4e48\u5fc3\u4e8b\u5427\uff1f\u6709\u4ec0\u4e48\u5fc3\u4e8b\u4e5f\u4e0d\u59a8\u548c\u6211\u8bf4\u7684\u3002\u4f60\u653e\u5fc3\uff0c\u6211\u51b3\u4e0d\u6562\u5bf9 \u4f60\u65e0\u793c\uff0c\u800c\u4e14\u4e00\u5b9a\u5e2e\u4f60\u7684\u5fd9\uff01\u201d \u3000\u3000\u4ed6\u4e00\u9762\u54c4\u9a97\u660e\u6167\u516c\u4e3b\uff0c\u4e00\u9762\u653e\u9a6c\u75be\u9a70\uff0c\u5f84\u5954\u767d\u5bb6\u5e84\u800c\u53bb\u3002\u539f\u6765\u4f59\u4e00\u4e2d\u6f5c\u5165\u4e2d\u539f\u8eb2\u5728\u767d\u4e07 \u96c4\u5bb6\u91cc\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u4ed6\u65e9\u5df2\u77e5\u9053\uff0c\u4ed6\u672c\u6765\u5c31\u662f\u8981\u53bb\u627e\u767d\u4e07\u96c4\u5e2e\u5fd9\u7684\uff0c\u5982\u4eca\u83b7\u5f97\u4e86\u660e\u6167\u516c\u4e3b\uff0c\u53c8 \u77e5\u9053\u6709\u4e2a\u8499\u53cb\u5f53\u8fc7\u526f\u5143\u5e05\u7684\u4f59\u4e00\u4e2d\u5728\u90a3\u513f\uff0c\u5f53\u7136\u662f\u6025\u7740\u8981\u53bb\u8c12\u89c1\u4f59\u4e00\u4e2d\uff0c\u597d\u8bf7\u4f59\u4e00\u4e2d\u4ee3\u4ed6\u7980 \u660e\u5927\u6c57\u7684\u4e86\u3002 \u3000\u3000\u77f3\u749e\u89e3\u5f00\u4e86\u963f\u76d6\u7684\u7a74\u9053\uff0c\u5411\u4ed6\u8d54\u793c\u8bf4\u9053\uff1a\u201c\u51fa\u4e86\u8fd9\u6837\u7684\u4e8b\u60c5\uff0c\u627e\u4e5f\u662f\u6599\u60f3\u4e0d\u5230\u7684\u3002\u5b9e\u4e0d \u76f8\u7792\uff0c\u521a\u624d\u548c\u4f60\u4ea4\u624b\u7684\u90a3\u4e2a\u4eba\uff0c\u867d\u7136\u662f\u6211\u4eec\u5be8\u4e3b\u7684\u54e5\u54e5\uff0c\u4f46\u5be8\u4e3b\u4e5f\u662f\u7d20\u6765\u4e0d\u503c\u4ed6\u4e3a\u4eba\u7684\u3002\u73b0 \u5728\u53ea\u597d\u8bf7\u4f60\u4eec\u5148\u56de\u5c71\u5be8\uff0c\u5be8\u4e3b\u5b9a\u4f1a\u8bbe\u6cd5\u66ff\u4f60\u4eec\u627e\u56de\u670b\u53cb\u7684\u3002\u201d \u3000\u3000\u963f\u76d6\u662f\u4e2a\u723d\u76f4\u7684\u6c49\u5b50\uff0c\u4ed6\u4eb2\u773c\u770b\u89c1\u77f3\u749e\u4e3a\u4e86\u8981\u593a\u56de\u660e\u6167\u516c\u4e3b\uff0c\u4e0d\u60dc\u4e0e\u5e08\u5144\u52a8\u624b\u4e2d\u4e14\u9669\u4e9b \u7ed9\u5c60\u9f99\u6240\u4f24\uff0c\u5f53\u7136\u4e5f\u5c31\u76f8\u4fe1\u4ed6\u4e86\uff0c\u540e\u6094\u521a\u624d\u81ea\u5df1\u592a\u9c81\u83bd\u3002 \u3000\u3000\u963f\u76d6\u592b\u59bb\u8ddf\u77f3\u749e\u56de\u8f6c\u5c71\u5be8\uff0c\u6682\u4e14\u6309\u4e0b\u4e0d\u8bb2\u3002\u5148\u8bb2\u674e\u601d\u5357\u548c\u6768\u5a49\u4e8c\u4eba\uff0c\u5230\u767d\u5bb6\u5e84\u62a5\u4ec7\u671d\u906d \u9047\u3002 \u3000\u3000\u674e\u601d\u5357\u7b97\u51c6\u65e5\u671f\uff0c\u5728\u7c98\u4e0d\u7f55\u3001\u901f\u4e0d\u53f0\u4e8c\u4eba\u56de\u5230\u767d\u5bb6\u5e84\u7684\u7b2c\u4e8c\u5929\u665a\u4e0a\uff0c\u4ed6\u548c\u6768\u5a49\u4e5f\u5230\u4e86\u767d \u5bb6\u5e84\u3002 \u3000\u3000\u4e09\u66f4\u65f6\u5206\uff0c\u674e\u3001\u6768\u4e8c\u4eba\u65bd\u5c55\u8f7b\u529f\uff0c\u4ece\u767d\u5bb6\u7684\u540e\u56ed\u8fdb\u53bb\u3002\u767d\u4e07\u96c4\u662f\u4e00\u65b9\u58eb\u9738\uff0c\u4ed6\u7684\u5bb6\u6709\u51e0 \u5341\u95f4\u623f\u5b50\uff0c\u674e\u601d\u5357\u4e0d\u77e5\u9053\u4f59\u4e00\u4e2d\u8eb2\u5728\u54ea\u4e00\u95f4\u623f\uff0c\u6b63\u81ea\u6253\u7b97\u53bb\u6293\u4e00\u4e2a\u4ec6\u4eba\u76d8\u95ee\uff0c\u53c8\u6015\u6253\u8349\u60ca \u86c7\uff0c\u6768\u5a49\u8bf4\u9053\uff1a\u201c\u4e0d\u5982\u4f60\u6211\u5206\u5934\uff0c\u6bcf\u95f4\u623f\u7aa5\u63a2\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u6050\u6015\u529f\u592b\u5927\u4e0d\u59a5\u5f53\uff0c\u641c\u5f97\u6765\u5df2\u662f\u5929\u4eae\u4e86\u3002\u800c\u4e14\u767d\u4e07\u96c4\u7684\u6b66\u529f\u975e\u540c\u5c0f\u53ef\uff0c\u54b1 \u4eec\u4e24\u4e2a\u4eba\u5728\u4e00\u8d77\u5f53\u7136\u4e0d\u6015\u4ed6\uff0c\u53ea\u662f\u4f60\u4e00\u4e2a\u4eba\u6211\u5c31\u6709\u70b9\u4e0d\u5927\u653e\u5fc3\u4e86\u3002\u201d\u6768\u5a49\u9053\uff1a\u201c\u4f46\u4f60\u53c8\u6015\u6253 \u8349\u60ca\u86c7\uff0c\u4e5f\u603b\u5f97\u60f3\u4e2a\u6cd5\u5b50\u624d\u884c\u5440\u3002\u201d \u3000\u3000\u6b63\u81ea\u8e0c\u8e87\u672a\u51b3\uff0c\u5ffd\u89c1\u6709\u4e09\u6761\u4eba\u5f71\u4ece\u4e00\u95f4\u5c4b\u5b50\u51fa\u6765\u3002\u674e\u601d\u5357\u8eb2\u5728\u5047\u5c71\u77f3\u540e\uff0c\u5b9a\u773c\u4e00\u770b\uff0c\u8d70 \u5728\u524d\u9762\u7684\u90a3\u4e2a\u4eba\u662f\u6253\u7740\u706f\u7b3c\u7684\uff0c\u674e\u601d\u5357\u770b\u6e05\u695a\u4e86\uff0c\u4e0d\u7981\u4e49\u60ca\u53c8\u559c\uff0c\u539f\u6765\u524d\u9762\u7684\u90a3\u4e2a\u4eba\u662f\u767d\u5bb6 \u7684\u4ec6\u4eba\uff0c\u540e\u9762\u90a3\u4e24\u4e2a\u6b63\u662f\u7c98\u4e0d\u7f55\u548c\u901f\u4e0d\u53f0\u3002 \u3000\u3000\u53ea\u542c\u5f97\u7c98\u4e0d\u7f55\u8bf4\u9053\uff1a\u201c\u4e0d\u77e5\u8d35\u5e84\u4e3b\u6df1\u591c\u76f8\u53ec\uff0c\u4e3a\u4e86\u4f55\u4e8b\uff1f\u201d \u3000\u3000\u90a3\u4ec6\u4eba\u9053\uff1a\u201c\u655d\u4e0a\u6b63\u5728\u548c\u4f59\u5927\u4eba\u8bf4\u8bdd\uff0c\u662f\u4f59\u5927\u4eba\u5429\u5490\u4e0b\u6765\uff0c\u8bf7\u4e24\u4f4d\u8fc7\u5927\u76f8\u4f1a\u7684\u3002\u201d \u3000\u3000\u901f\u4e0d\u53f0\u5495\u54dd\u9053\uff1a\u201c\u5947\u602a\uff0c\u8fd9\u4e48\u665a\u4e86\uff0c\u4f59\u4e00\u4e2d\u5374\u6709\u4ec0\u4e48\u4e8b\u60c5\u8981\u54b1\u4eec\u5546\u91cf\uff1f\u201d \u3000\u3000\u90a3\u4ec6\u4eba\u9053\uff1a\u201c\u8fd9\u4e2a\u5c0f\u4eba\u53ef\u4e0d\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u901f\u4e0d\u53f0\u9887\u611f\u60f6\u60d1\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u83ab\u975e\u662f\u6709\u4ec0\u4e48\u7834\u7efd\u7ed9\u4f59\u4e00\u4e2d\u8fd9\u53ae\u8bc6\u7a7f\u4e86\uff1f\u201d\u5fc3\u77e5\u4e0d\u5999\uff0c \u4f46\u4e5f\u53ea\u597d\u8ddf\u7740\u90a3\u4ec6\u4eba\u8fc7\u53bb\u3002 \u3000\u3000\u8e0f\u7834\u94c1\u978b\u65e0\u89c5\u5904\uff0c\u5f97\u6765\u5168\u4e0d\u8d39\u529f\u592b\uff0c\u7c98\u3001\u901f\u4e8c\u4eba\u60ca\u60f6\u7591\u8651\uff0c\u674e\u601d\u5357\u5374\u662f\u5927\u559c\u8fc7\u671b\uff01\u5fc3 \u60f3\uff1a\u201c\u8fd9\u53ef\u771f\u662f\u592d\u8d50\u7ed9\u6211\u7684\u62a5\u4ec7\u673a\u4f1a\u4e86\uff0c\u96be\u5f97\u767d\u4e07\u96c4\u548c\u4f59\u4e00\u4e2d\u6b63\u5728\u4e00\u8d77\uff0c\u7701\u5374\u6211\u5206\u5934\u5bfb \u627e\u3002\u201d\u5f53\u4e0b\u4fbf\u4e0e\u6768\u5a49\u501f\u7269\u969c\u5f62\uff0c\u6084\u6084\u8ddf\u5728\u540e\u9762\uff0c\u56ed\u5b50\u91cc\u6709\u7684\u662f\u5047\u5c71\u6811\u6728\uff0c\u4ed6\u4eec\u7684\u8f7b\u529f\u53c8\u6781\u8d85 \u5999\uff0c\u8ddf\u5728\u8fd9\u4e09\u4eba\u540e\u9762\uff0c\u4e1d\u6beb\u4e5f\u6ca1\u6709\u9732\u51fa\u58f0\u8ff9\u3002\u767d\u5bb6\u90a3\u8001\u4ec6\u672c\u9886\u5e73\u5eb8\uff0c\u5982\u4f55\u80fd\u591f\u5bdf\u89c9\uff1f \u3000\u3000\u8d70\u4e86\u4e00\u4f1a\uff0c\u90a3\u8001\u4ec6\u5e26\u9886\u7c98\u3001\u901f\u4e8c\u4eba\u8fdb\u5165\u4e00\u4e2a\u9662\u5b50\uff0c\u674e\u601d\u5357\u5728\u6768\u5a49\u8033\u8fb9\u5c0f\u58f0\u8bf4\u9053\uff1a\u201c\u7b49\u4f1a \u513f\u6211\u5bf9\u4ed8\u767d\u4e07\u96c4\uff0c\u4f60\u51fa\u624b\u5236\u4f0f\u4f59\u4e00\u4e2d\u3002\u201d \u3000\u3000\u9662\u5b50\u5f53\u4e2d\u7684\u4e00\u95f4\u623f\u95f4\u6709\u706f\u5149\u900f\u51fa\uff0c\u674e\u601d\u5357\u4e00\u4e2a\u201c\u9ec4\u9e4a\u51b2\u9704\u201d\u7684\u8eab\u6cd5\uff0c\u5e73\u5730\u62d4\u8d77\u6570\u4e08\uff0c\u6070 \u5982\u4e00\u53f6\u98d8\u5760\uff0c\u843d\u5904\u65e0\u58f0\u3002\u90a3\u4e09\u4e2a\u4eba\u5c1a\u672a\u8fdb\u5165\u623f\u95f4\uff0c\u4ed6\u5df2\u4f0f\u5728\u5c4b\u9876\u4e0a\u4e86\u3002\u6768\u5a49\u7d27\u8ddf\u7740\u4ed6\uff0c\u4f46\u5374 \u533f\u5728\u7a97\u4e0b\u3002 \u3000\u3000\u674e\u601d\u5357\u53cc\u8db3\u5012\u6302\u5c4b\u6a90\uff0c\u659c\u773c\u5077\u7aa5\uff0c\u53ea\u89c1\u623f\u5b50\u91cc\u6709\u4e09\u4e2a\u4eba\uff0c\u9664\u4e86\u767d\u4e07\u96c4\u548c\u4f59\u4e00\u4e2d\u4e4b\u5916\uff0c\u8fd8 \u6709\u4e00\u4e2a\u5927\u7ea6\u4e8c\u5341\u591a\u5c81\u7684\u5c11\u5e74\u3002\u674e\u601d\u5357\u60f3\u9053\uff1a\u201c\u8fd9\u4eba\u60f3\u5fc5\u662f\u767d\u4e07\u96c4\u7684\u513f\u5b50\u767d\u5343\u80dc\u4e86\u3002\u201d\u674e\u601d\u5357 \u6ca1\u6709\u4f1a\u8fc7\u767d\u5343\u80dc\uff0c\u53ea\u9053\u8001\u5b50\u4e5f\u6253\u4e0d\u8fc7\u4ed6\uff0c\u4f55\u51b5\u513f\u5b50\uff0c\u81ea\u4e0d\u628a\u767d\u5343\u80dc\u653e\u5728\u773c\u5185\u3002\u5374\u4e0d\u77e5\u767d\u5343\u80dc \u867d\u7136\u4e0d\u80fd\u5f3a\u7239\u65fa\u7956\uff0c\u6b66\u529f\u4e5f\u5f88\u4e0d\u5f31\uff0c\u5e76\u4e0d\u5728\u6768\u5a49\u4e4b\u4e0b\u3002 \u3000\u3000\u4f59\u4e00\u4e2d\u9053\uff1a\u201c\u767d\u5e84\u4e3b\uff0c\u82e5\u4e0d\u662f\u5f97\u4f60\u6cd5\u773c\u770b\u7834\uff0c\u6211\u51e0\u4e4e\u7ed9\u8fd9\u4e24\u4eba\u9a97\u8fc7\u4e86\u3002\u201d \u3000\u3000\u767d\u4e07\u96c4\u9053\uff1a\u201c\u6211\u53ea\u662f\u8d77\u7591\u800c\u5df2\uff0c\u4e0d\u6562\u8bf4\u4ed6\u4eec\u5c31\u662f\u4e00\u5b9a\u5978\u7ec6\u3002\u4f59\u5927\u4eba\u5f85\u4f1a\u513f\u522b\u9732\u58f0\u8272\uff0c\u5f85 \u6211\u5148\u8bd5\u4e00\u8bd5\u4ed6\u4eec\u3002\u201d \u3000\u3000\u4f59\u4e00\u4e2d\u70b9\u4e86\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4ed6\u4eec\u662f\u91d1\u5e10\u6b66\u58eb\uff0c\u54b1\u4eec\u662f\u4e0d\u6562\u5192\u6627\u4ece\u4e8b\u3002\u201d \u3000\u3000\u767d\u4e07\u96c4\u201c\u5618\u201d\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u4ed6\u4eec\u6765\u4e86\u3002\u201d\u674e\u601d\u5357\u597d\u5947\u5fc3\u8d77\uff0c\u60f3\u9053\uff1a\u201c\u4e14\u770b\u4ed6\u4eec\u5982\u4f55 \u8bd5\u7c98\u3001\u901f\u4e8c\u4eba\uff1f\u201d\u4ed6\u672c\u6765\u662f\u548c\u7c98\u3001\u901f\u4e8c\u4eba\u8bf4\u597d\uff0c\u8bf7\u4ed6\u4eec\u4f5c\u5185\u5e94\u7684\u3002\u5f53\u4e0b\u4fbf\u6682\u65f6\u5fcd\u4f4f\uff0c\u4e0d\u5148\u51fa \u624b\uff0c\u7b49\u5f85\u7c98\u3001\u901f\u4e8c\u4eba\u8fdb\u6765\u3002 \u3000\u3000\u7c98\u3001\u901f\u4e8c\u4eba\u8fdb\u6765\u4e0e\u4f59\u4e00\u4e2d\u89c1\u8fc7\u4e86\u793c\uff0c\u95ee\u9053\uff1a\u201c\u4f59\u5927\u4eba\u4f55\u4e8b\u53ec\u89c1\uff1f\u201d\u4f59\u4e00\u4e2d\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48 \u4e8b\u60c5\uff0c\u7c98\u4e0d\u7f55\uff0c\u4f60\u7684\u4f24\u597d\u4e86\u6ca1\u6709\uff1f\u4f60\u8fd9\u6b21\u56e0\u516c\u53d7\u4f24\uff0c\u6211\u5fc3\u91cc\u5f88\u662f\u4e0d\u5b89\u3002\u201d\u7c98\u4e0d\u7f55\u9053\uff1a\u201c\u591a\u8c22 \u5927\u4eba\u8bb0\u6302\u3002\u201d\u5fc3\u4e2d\u5374\u5728\u6697\u9a82\uff1a\u201c\u4f60\u8bb0\u6302\u6211\u7684\u4f24\u52bf\uff0c\u4e3a\u4f55\u4e0d\u8fc7\u6765\u770b\u6211\uff1f\u201d \u3000\u3000\u7c98\u4e0d\u7f55\u5fc3\u4e2d\u5492\u9a82\uff0c\u53e3\u5934\u5374\u4e0d\u80fd\u4e0d\u6577\u884d\u4f59\u4e00\u4e2d\u9053\uff1a\u201c\u591a\u8c22\u5927\u4eba\u6302\u5fc3\u3002\u6211\u7684\u4f24\u52bf\u5df2\u7ecf\u597d\u5f97\u591a \u4e86\u3002\u201d\u4f59\u4e00\u4e2d\u9053\uff1a\u201c\u771f\u7684\u5417\uff1f\u90a3\u5c31\u597d\u4e86\u3002\u6211\u6b63\u6101\u7740\u4e0d\u77e5\u4f60\u80fd\u4e0d\u80fd\u591f\u8ddf\u6211\u56de\u53bb\u5462\u3002\u201d \u3000\u3000\u7c98\u4e0d\u7f55\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u5927\u4eba\uff0c\u4f60\u8981\u56de\u56fd\uff1f\u201d \u3000\u3000\u4f59\u4e00\u4e2d\u5fae\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5982\u679c\u4f60\u7684\u4f24\u52bf\u4e0d\u788d\u4e8b\u7684\u8bdd\uff0c\u660e\u5929\u6211\u6253\u7b97\u548c\u4f60\u4eec\u56de\u53bb\u4e86\u3002\u201d \u3000\u3000\u7c98\u4e0d\u7f55\u9053\uff1a\u201c\u4f59\u5927\u4eba\u4f55\u5fc5\u8fd9\u6837\u5306\u5fd9\u56de\u53bb\uff0c\u6628\u65e5\u4f60\u4e0d\u662f\u548c\u6211\u8bf4\u8fd8\u53ef\u4ee5\u901e\u7559\u4e00\u4e9b\u65f6\u5019\u7684 \u5417\uff1f\u201d \u3000\u3000\u4f59\u4e00\u4e2d\u9053\uff1a\u201c\u6211\u672c\u6765\u8fd8\u6709\u4e24\u5904\u5730\u65b9\u8981\u53bb\u8054\u7edc\u7684\uff0c\u6070\u5de7\u4eca\u5929\u8fd9\u4e24\u5904\u5730\u65b9\u90fd\u6d3e\u6709\u4eba\u6765\uff0c\u4e0d\u7528 \u6211\u518d\u53bb\u4e86\u3002\u201d \u3000\u3000\u7c98\u4e0d\u7f55\u9053\uff1a\u201c\u8bf7\u5927\u4eba\u8fd8\u662f\u591a\u7559\u51e0\u5929\u5427\u3002\u201d\u4e00\u9762\u8bf4\u8bdd\u4e00\u9762\u601d\u7d22\u8981\u7f16\u9020\u4ec0\u4e48\u8bdd\u65b9\u80fd\u4ee4\u5f97\u4f59\u4e00 \u4e2d\u7559\u4e0b\u3002 \u3000\u3000\u4f59\u4e00\u4e2d\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5230\u5e95\u662f\u901f\u4e0d\u53f0\u5fc3\u601d\u6bd4\u8f83\u7075\u654f\uff0c\u7709\u5934\u4e00\u76b1\uff0c\u8ba1\u4e0a\u5fc3\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u51fa\u6765\u7684\u65f6\u5019\uff0c\u5927\u6c57\u66fe \u7ecf\u5429\u5490\u8fc7\u6211\u4eec\uff0c\u8981\u6211\u4eec\u89c1\u8fc7\u4e86\u9633\u5929\u96f7\uff0c\u65b9\u597d\u56de\u53bb\u3002\u201d \u3000\u3000\u4ed6\u4ee5\u4e3a\u62ac\u51fa\u4e86\u5927\u6c57\u4f5c\u6321\u7bad\u813e\uff0c\u4f59\u4e00\u4e2d\u4e00\u5b9a\u4e0d\u6562\u591a\u95ee\uff0c\u4e0d\u6599\u6b64\u8a00\u4e00\u51fa\uff0c\u5374\u6b63\u597d\u7ed9\u4f59\u4e00\u4e2d\u542c \u51fa\u4e86\u7834\u7efd\uff0c\u4ed6\u5fc3\u4e2d\u6697\u6697\u597d\u7b11\uff0c\u8138\u4e0a\u88c5\u51fa\u8be7\u5f02\u7684\u795e\u60c5\uff0c\u8bf4\u9053\uff1a\u201c\u54e6\uff0c\u5927\u6c57\u66fe\u7ecf\u6709\u8fc7\u8fd9\u4e2a\u5429\u5490 \u4e48\uff1f\u6211\u5374\u4e0d\u77e5\u9053\u5462\uff01\u201d \u3000\u3000\u901f\u4e0d\u53f0\u9053\uff1a\u201c\u6211\u4eec\u600e\u6562\u6b3a\u9a97\u5927\u4eba\u3002\u8fd9\u662f\u6211\u4eec\u4e34\u8d70\u7684\u65f6\u5019\uff0c\u5927\u6c57\u5429\u5490\u6211\u4eec\u7684\u3002\u6216\u8bb8\u5927\u6c57\u8ba4 \u4e3a\u5df2\u7ecf\u4ea4\u4ee3\u4e86\u6211\u4eec\uff0c\u5c31\u65e0\u987b\u5411\u5927\u4eba\u518d\u8bf4\u4e86\u3002\u201d\u8a00\u4e0b\u4e4b\u8a00\uff0c\u4e0d\u557b\u662f\u5411\u4f59\u4e00\u4e2d\u793a\u5a01\uff1a\u201c\u4f60\u7b97\u4ec0\u4e48 \u4e1c\u897f\uff0c\u6211\u4eec\u91d1\u5e10\u6b66\u58eb\uff0c\u6bd4\u8d77\u4f60\u6765\uff0c\u548c\u5927\u6c57\u53ef\u8981\u4eb2\u8fd1\u5f97\u591a\u5462\u3002\u201d \u3000\u3000\u82e5\u5728\u5e73\u65f6\uff0c\u4f59\u4e00\u4e2d\u542c\u4e86\u8fd9\u6837\u7684\u8bdd\uff0c\u5b9a\u7136\u5664\u82e5\u5bd2\u8749\uff0c\u5fc3\u91cc\u5341\u5206\u60f6\u6050\u7684\u3002\u4f46\u6b64\u9645\u4ed6\u5df2\u7ecf\u660e\u77e5 \u8fd9\u4e24\u4eba\u8bf4\u7684\u4e43\u662f\u5047\u8bdd\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4f60\u4f1a\u8bf4\u8c0e\uff0c\u96be\u9053\u6211\u5c31\u4e0d\u4f1a\uff0c\u4e14\u5f85\u6211\u518d\u8bd5\u4f60\u4eec\u4e00\u8bd5\u3002\u201d\u5f53 \u4e0b\u53c8\u662f\u5fae\u5fae\u4e00\u7b11\uff0c\u6de1\u6de1\u8bf4\uff1a\u201c\u53ef\u662f\u6211\u4eca\u5929\u4e5f\u63a5\u5230\u4e86\u547d\u4ee4\uff0c\u8981\u6211\u4eec\u7acb\u5373\u56de\u56fd\u5462\u3002\u201d \u3000\u3000\u7c98\u4e0d\u7f55\u4e0d\u77e5\u662f\u771f\u662f\u5047\uff0c\u4ed6\u7b54\u5e94\u8fc7\u674e\u601d\u5357\uff0c\u4e00\u5b9a\u8981\u628a\u4f59\u4e00\u4e2d\u201c\u7a33\u201d\u5728\u767d\u5bb6\u5e84\u7684\uff0c\u60c5\u6025\u4e4b \u4e0b\uff0c\u53ea\u597d\u4ecd\u7136\u85c9\u53e3\u4f24\u52bf\u672a\u6108\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u7684\u4f24\u867d\u7136\u597d\u4e86\u4e00\u4e9b\uff0c\u4f46\u53ea\u6015\u8fd8\u662f\u4e0d\u80fd\u8dcb\u6d89\u957f\u9014\u3002\u201d \u3000\u3000\u767d\u4e07\u96c4\u5ffd\u5730\u8bf4\u9053\uff1a\u201c\u6211\u6709\u4e0a\u597d\u7684\u91d1\u521b\u836f\uff0c\u8bf7\u4f60\u8ba9\u6211\u770b\u770b\u4f60\u7684\u4f24\u53e3\uff0c\u6211\u7ed9\u4f60\u6577\u3002\u201d \u3000\u3000\u7c98\u4e0d\u7f55\u9053\uff1a\u201c\u4e0d\u6562\u6709\u52b3\u5e84\u4e3b\uff0c\u6211\u5df2\u7ecf\u6577\u4e0a\u4e86\u81ea\u5236\u7684\u91d1\u521b\u836f\uff0c\u8981\u8fc7\u51e0\u4e2a\u65f6\u8fb0\uff0c\u65b9\u80fd\u6362\u836f\u3002 \u4e0d\u8fc7\u5e84\u4e3b\u7684\u597d\u610f\u6211\u4e5f\u4e0d\u6562\u63a8\u8f9e\u3002\u90a3\u4e48\u5c31\u8bf7\u5e84\u4e3b\u628a\u91d1\u521b\u836f\u7ed9\u6211\u3002\u5f85\u6211\u5230\u4e86\u53ef\u4ee5\u6362\u836f\u7684\u65f6\u5019\uff0c\u81ea \u5df1\u6577\u5427\u3002\u201d \u3000\u3000\u767d\u4e07\u96c4\u8bf4\u9053\uff1a\u201c\u8001\u673d\u7565\u61c2\u533b\u672f\uff0c\u800c\u4e14\u6211\u8fd9\u91d1\u521b\u836f\u548c\u4efb\u4f55\u4e00\u79cd\u91d1\u521b\u836f\u90fd\u662f\u6ca1\u6709\u5fcc\u514b\u7684\uff0c\u8fd8 \u662f\u8ba9\u6211\u770b\u4e00\u770b\u5427\u3002\u201d \u3000\u3000\u539f\u6765\u767d\u4e07\u96c4\u5bf9\u7c98\u3001\u901f\u4e8c\u4eba\u80fd\u591f\u79c1\u9003\u56de\u6765\uff0c\u5df2\u8d77\u4e86\u7591\u5fc3\uff0c\u6545\u6b64\u7279\u5730\u4ee5\u8d60\u91d1\u521b\u836f\u4e3a\u540d\uff0c\u5236\u9020 \u85c9\u53e3\uff0c\u67e5\u770b\u4ed6\u7684\u4f24\u52bf\u3002\u8bf4\u5230\u201c\u770b\u770b\u201d\u4e8c\u5b57\u65f6\u4ee5\u8fc5\u96f7\u4e0d\u53ca\u63a9\u8033\u7684\u624b\u6cd5\uff0c\u6495\u5f00\u4e86\u4ed6\u88f9\u4f24\u7684\u7ef7\u5e26\u3002 \u3000\u3000\u7c98\u4e0d\u7f55\u817f\u4e0a\u90a3\u9053\u4f24\u53e3\u6709\u4e09\u5bf8\u6765\u957f\uff0c\u56db\u4e94\u5206\u6df1\uff0c\u4f24\u52bf\u9aa4\u773c\u770b\u53bb\u5012\u662f\u4e0d\u8f7b\u3002\u4f46\u8fd9\u662f\u4ed6\u81ea\u5df1\u523a \u4f24\u81ea\u5df1\u7684\uff0c\u548c\u53d7\u522b\u4eba\u523a\u4f24\u81ea\u5df1\u7684\u5230\u5e95\u662f\u6709\u4e0d\u540c\uff0c\u81ea\u5df1\u523a\u4f24\u7684\u5fc5\u7136\u907f\u514d\u4f24\u53ca\u7b4b\u9aa8\uff0c\u767d\u4e07\u96c4\u662f\u4e2a \u6b66\u5b66\u7684\u5927\u884c\u5bb6\uff0c\u5c82\u80fd\u7792\u5f97\u8fc7\u4ed6\u7684\u53cc\u773c\uff1f \u3000\u3000\u7c98\u6728\u7f55\u7ed9\u4ed6\u8bc6\u7834\uff0c\u81ea\u4ea6\u4e0d\u7518\u675f\u624b\u53d7\u64d2\uff0c\u4ed6\u8eab\u4e3a\u91d1\u5e10\u6b66\u58eb\uff0c\u672c\u9886\u867d\u7136\u4e0d\u53ca\u767d\u4e07\u96c4\uff0c\u4e5f\u5f88\u4e0d \u5f31\uff0c\u7ef7\u5e26\u6495\u5f00\uff0c\u767b\u65f6\u53d1\u4f5c\uff0c\u4e00\u4e2a\u201c\u8131\u888d\u8ba9\u4f4d\u201d\uff0c\u53cc\u624b\u4e00\u6273\uff0c\u53cd\u6263\u767d\u4e07\u96c4\u7684\u864e\u53e3\u3002\u8fd9\u4e00\u62db\u662f\u4ece \u6454\u89d2\u624b\u6cd5\u4e2d\u53d8\u5316\u51fa\u6765\u7684\u62db\u6570\uff0c\u5f80\u5f80\u80fd\u591f\u8d25\u4e2d\u53d6\u80dc\uff0c\u53cd\u5236\u654c\u4eba\u3002 \u3000\u3000\u767d\u4e07\u96c4\u4e0d\u61c2\u6454\u89d2\uff0c\u4f46\u5374\u662f\u7cbe\u901a\u5927\u64d2\u62ff\u624b\u7684\u540d\u5bb6\uff0c\u5f53\u4e0b\u4e00\u4e2a\u8098\u5e95\u7a7f\u638c\uff0c\u63e9\u5c16\u6233\u5411\u7c98\u4e0d\u7f55\u7684 \u80a9\u4e95\u7a74\u3002 \u3000\u3000\u7c98\u4e0d\u7f55\u6c89\u80a9\u9000\u6b65\uff0c\u5de6\u811a\u4e00\u52fe\uff0c\u53cc\u62f3\u9f50\u51fa\uff0c\u767d\u4e07\u96c4\u4e00\u6293\u6293\u4f4f\u4e86\u4ed6\u7684\u62f3\u5934\uff0c\u4f46\u5374\u7ed9\u4ed6\u52fe\u7740\u811a \u8ddf\uff0c\u811a\u6b65\u4e00\u4e2a\u8e09\u8dc4\uff0c\u903c\u5f97\u677e\u624b\u3002 \u3000\u3000\u7c98\u4e0d\u7f55\u7684\u62f3\u5934\u7ed9\u4ed6\u6293\u7834\uff0c\u9c9c\u8840\u6dcb\u6f13\uff0c\u5927\u6012\u559d\u9053\uff1a\u201c\u4f60\u6562\u5bf9\u6211\u65e0\u793c\uff01\u201d\u767d\u4e07\u96c4\u51b7\u7b11\u9053\uff1a \u201c\u4f60\u4ee5\u524d\u662f\u6211\u7684\u5ba2\u4eba\uff0c\u6211\u81ea\u7136\u4e0d\u6562\u5bf9\u4f60\u65e0\u793c\uff0c\u4f46\u4f60\u73b0\u5728\u5df2\u7ecf\u4e0d\u662f\u6211\u7684\u5ba2\u4eba\u800c\u662f\u5978\u7ec6\u4e86\uff0c\u6211\u8fd8 \u80fd\u653e\u8fc7\u4e48\uff1f\u201d \u3000\u3000\u5c31\u5728\u767d\u4e07\u96c4\u4e0e\u7c98\u4e0d\u7f55\u5f00\u59cb\u52a8\u624b\u7684\u65f6\u5019\uff0c\u901f\u4e0d\u53f0\u4e5f\u7a81\u7136\u53d1\u96be\uff0c\u5411\u4f59\u4e00\u4e2d\u6251\u53bb\u3002\u767d\u5343\u80dc\u65e9\u6709 \u51c6\u5907\uff0c\u7acb\u5373\u5c06\u8eab\u4f53\u906e\u4f4f\u4f59\u4e00\u4e2d\uff0c\u62d4\u5251\u622a\u65a9\u901f\u4e0d\u53f0\u7684\u624b\u638c\u3002 \u3000\u3000\u901f\u4e0d\u53f0\u559d\u9053\uff1a\u201c\u64a4\u5251\uff01\u201d\u4f7f\u51fa\u7a7a\u624b\u5165\u767d\u5203\u7684\u529f\u592b\uff0c\u5f3a\u593a\u767d\u5343\u80dc\u7684\u957f\u5251\uff0c\u767d\u5343\u80dc\u4e00\u5251\u523a \u7a7a\uff0c\u624b\u8155\u5df2\u7ed9\u4ed6\u6307\u950b\u94b3\u7740\uff0c\u706b\u8fa3\u8fa3\u7684\u4f5c\u75db\u3002\u767d\u5343\u80dc\u5e94\u53d8\u4e5f\u771f\u4e86\u5f97\uff0c\u5de6\u638c\u6a2a\u638c\u5982\u5200\uff0c\u4e00\u638c\u5288 \u4e0b\uff0c\u901f\u4e0d\u53f0\u8bc6\u5f97\u5389\u5bb3\uff0c\u4e0d\u6562\u7ed9\u4ed6\u5288\u4e2d\uff0c\u5f53\u4e0b\u4fa7\u8eab\u4e00\u95ea\uff0c\u6a2a\u80b1\u5c06\u4ed6\u649e\u5f00\u3002\u767d\u5343\u80dc\u7684\u957f\u5251\u867d\u7136\u6ca1 \u6709\u7ed9\u4ed6\u593a\u53bb\uff0c\u4f46\u8fd9\u4e00\u62db\u5982\u662f\u5927\u5927\u7684\u5403\u4e86\u4ed6\u7684\u4e8f\u4e86\u3002 \u3000\u3000\u901f\u4e0d\u53f0\u649e\u5f00\u4e86\u767d\u5343\u80dc\uff0c\u7acb\u5373\u53c8\u5411\u4f59\u4e00\u4e2d\u5916\u53bb\u3002\u4ed6\u77e5\u9053\u7c98\u4e0d\u7f55\u7edd\u4e0d\u662f\u767d\u4e07\u96c4\u7684\u5bf9\u624b\uff0c\u53ea\u6709 \u628a\u4f59\u4e00\u4e2d\u64d2\u4e3a\u4eba\u8d28\uff0c\u624d\u6709\u8131\u8eab\u4e4b\u671b\u3002 \u3000\u3000\u4f59\u4e00\u4e2d\u51b7\u7b11\u9053\uff1a\u201c\u539f\u6765\u4f60\u771f\u662f\u5978\u7ec6\uff01\u54fc\uff0c\u4f60\u6b7b\u5230\u4e34\u5934\uff0c\u8fd8\u6562\u52a8\u624b\u3002\u201d \u3000\u3000\u4f0f\u5728\u5c4b\u9876\u4e0a\u7684\u674e\u601d\u5357\u53eb\u9053\uff1a\u201c\u5a49\u59b9\uff01\u52a8\u624b\uff01\u201d\u6768\u5a49\u4e00\u628a\u6885\u82b1\u9488\u4ece\u7a97\u53e3\u6253\u8fdb\u6765\uff0c\u674e\u601d\u5357\u8ddf \u7740\u7a7f\u7a97\u800c\u8fdb\uff0c\u559d\u9053\uff1a\u201c\u4f59\u4e00\u4e2d\uff0c\u4f60\u8fd8\u8ba4\u5f97\u6211\u5417\uff1f\u201d \u3000\u3000\u5c31\u5728\u6b64\u65f6\uff0c\u5ffd\u542c\u5f97\u5495\u549a\u4e00\u58f0\uff0c\u901f\u4e0d\u53f0\u7a81\u7136\u5012\u5728\u5730\u4e0a\u3002\u6768\u5a49\u90a3\u4e00\u628a\u6885\u82b1\u9488\u672c\u6765\u662f\u8981\u6253\u767d\u5343 \u80dc\uff0c\u4e5f\u4e0d\u77e5\u600e\u7684\u8fd8\u6ca1\u6709\u4e00\u6839\u9489\u6cbe\u7740\u4ed6\u7684\u8863\u88f3\uff0c\u5c31\u7eb7\u5760\u5982\u96e8\u4e86\u3002 \u3000\u3000\u8fd9\u4e24\u4e2a\u7a81\u5982\u5176\u6765\u7684\u53d8\u5316\uff0c\u90fd\u662f\u5927\u51fa\u674e\u601d\u5357\u7684\u610f\u6599\u4e4b\u5916\uff01\u8981\u77e5\u901f\u4e0d\u53f0\u662f\u8499\u53e4\u8457\u540d\u7684\u6b66\u58eb\uff0c \u4f59\u4e00\u4e2d\u867d\u6709\u6b66\u827a\uff0c\u5374\u662f\u5e73\u5eb8\uff0c\u5982\u4eca\u901f\u4e0d\u53f0\u7adf\u7136\u7ed9\u4ed6\u6253\u5012\uff0c\u5c82\u975e\u4e0d\u53ef\u601d\u8bae\u4e4b\u4e8b\uff1f \u3000\u3000\u6768\u5a49\u90a3\u4e00\u628a\u6885\u82b1\u9488\u8fde\u767d\u5343\u80dc\u7684\u8863\u88f3\u90fd\u672a\u6cbe\u4e0a\uff0c\u8fd9\u4e5f\u662f\u4ee4\u5f97\u674e\u601d\u5357\u5927\u4e3a\u60ca\u8be7\u7684\u4e8b\u60c5\uff01\u8fd9\u662f \u4e00\u79cd\u6781\u4e3a\u9ad8\u660e\u7684\u5185\u529f\uff0c\u674e\u601d\u5357\u662f\u66fe\u7ecf\u89c1\u8fc7\u767d\u5343\u80dc\u7684\u7236\u4eb2\u767d\u4e07\u96c4\u7684\u672c\u9886\u7684\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u96be\u9053 \u513f\u5b50\u7684\u6b66\u529f\u6bd4\u7236\u4eb2\u8fd8\u66f4\u5389\u5bb3\u4e0d\u6210\uff1f\u201d \u3000\u3000\u5fc3\u5ff5\u672a\u5df2\uff0c\u5ffd\u542c\u5f97\u6709\u4eba\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u5c31\u662f\u674e\u601d\u5357\u5417\uff1f\u54c8\u54c8\uff0c\u8fd9\u56de\u4f60\u53ef\u662f\u81ea\u6295\u7f57\u7f51 \u4e86\u3002\u201d\u7b11\u58f0\u94ff\u94ff\u9535\u9535\uff0c\u5b9b\u5982\u91d1\u5c5e\u4ea4\u51fb\uff0c\u523a\u8033\u5f02\u5e38\u3002 \u3000\u3000\u7b11\u58f0\u4e2d\u4e00\u80a1\u52b2\u98ce\u6251\u9762\u800c\u6765\uff0c\u674e\u601d\u5357\u5fc3\u5934\u5927\u9a87\uff1a\u201c\u60f3\u4e0d\u5230\u8fd9\u91cc\u7adf\u6709\u4e00\u4e2a\u6b66\u529f\u8fdc\u80dc\u4e8e\u767d\u4e07\u96c4 \u7684\u9ad8\u624b\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u7ed9\u90a3\u4eba\u7684\u638c\u98ce\u4e00\u9707\uff0c\u80f8\u53e3\u9690\u9690\u4f5c\u75db\uff0c\u60c5\u77e5\u5bf9\u65b9\u529f\u529b\u80dc\u8fc7\u81ea\u5df1\u4e0d\u6b62\u4e00\u7b79\uff0c\u5f53\u4e0b\u53ea\u597d \u6682\u907f\u5176\u950b\uff0c\u811a\u6b65\u672a\u66fe\u7ad9\u7a33\uff0c\u7acb\u5373\u53c8\u4ece\u7a97\u53e3\u5012\u7eb5\u51fa\u53bb\u3002\u5c31\u5728\u6b64\u65f6\uff0c\u901f\u4e0d\u53f0\u521a\u597d\u4ece\u5730\u4e0a\u722c\u4e86\u8d77 \u6765\uff0c\u548c\u90a3\u4eba\u6253\u4e86\u4e00\u4e2a\u7167\u9762\u5931\u58f0\u53eb\u9053\uff1a\u201c\u9633\u5929\u96f7\uff01\u201d\u9633\u5929\u96f7\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u8981\u548c\u6211\u4f1a\u9762\u5417\uff1f \u5982\u4eca\u9042\u4e86\u4f60\u7684\u5fc3\u613f\u4e86\u3002\u201d\u817e\u7684\u98de\u8d77\u4e00\u811a\uff0c\u53c8\u8e22\u63b7\u4e86\u901f\u4e0d\u53f0\uff0c\u8ddf\u7740\u5c31\u8ffd\u4e86\u51fa\u6765\u3002 \u3000\u3000\u539f\u6765\u9633\u5927\u96f7\u662f\u6765\u7ea6\u4f1a\u4f59\u4e00\u4e2d\u7684\uff0c\u5f53\u4ed6\u4eec\u5bf9\u7c98\u3001\u901f\u4e8c\u4eba\u8d77\u7591\u4e4b\u540e\uff0c\u65e9\u5df2\u51c6\u5907\u674e\u601d\u5357\u4f1a\u6765\u62a5 \u4ec7\u7684\u4e86\u3002 \u3000\u3000\u674e\u601d\u5357\u60f3\u4e0d\u5230\u5728\u8fd9\u91cc\u4f1a\u78b0\u4e0a\u9633\u5929\u96f7\uff0c\u660e\u77e5\u51f6\u591a\u5409\u5c11\uff0c\u5374\u4e5f\u6beb\u4e0d\u754f\u7f29\uff0c\u542c\u5f97\u80cc\u540e\u52b2\u98ce\u88ad \u6765\uff0c\u7acb\u5373\u4fa7\u8eab\u907f\u5f00\u6b63\u9762\uff0c\u53cd\u624b\u4fbf\u662f\u4e00\u5251\u3002 \u3000\u3000\u8fd9\u4e00\u5251\u662f\u5c11\u6797\u5bfa\u8fbe\u6469\u5251\u6cd5\u7684\u771f\u4f20\uff0c\u5251\u5c16\u523a\u7a74\uff0c\u5251\u950b\u524a\u808b\uff0c\u867d\u5728\u9ed1\u591c\u4e4b\u4e2d\uff0c\u7adf\u662f\u4e0d\u5dee\u6beb \u5398\u3002\u9633\u5929\u96f7\u8bc6\u5f97\u5389\u5bb3\uff0c\u4e0d\u6562\u5c0f\u89d1\uff0c\u5f53\u4e0b\u628a\u5929\u96f7\u638c\u7684\u529f\u592b\u5316\u4e3a\u6307\u52b2\uff0c\u5bf9\u51c6\u5251\u810a\u4e00\u5f39\u3002\u5929\u96f7\u638c\u662f \u4e00\u7b49\u4e00\u7684\u521a\u731b\u638c\u529b\uff0c\u628a\u8fd9\u80a1\u529b\u9053\u4ece\u6307\u5934\u4e0a\u5f39\u51fa\u6765\uff0c\u6bd4\u5c11\u6797\u5bfa\u4f5b\u95e8\u6b63\u5b97\u7684\u201c\u5f39\u6307\u795e\u901a\u201d\u66f4\u4e3a\u9738 \u9053\uff0c\u53ea\u542c\u9053\u201c\u950b\u201d\u7684\u4e00\u58f0\uff0c\u674e\u601d\u5357\u864e\u53e3\u53d1\u70ed\uff0c\u5b9d\u5251\u51e0\u4e4e\u638c\u63e1\u4e0d\u7262\uff0c\u8eab\u5f62\u8fde\u6643\uff0c\u9000\u5012\u4e86\u4e03\u516b\u6b65 \u4e4b\u5916\u3002 \u3000\u3000\u6b8a\u4e0d\u77e5\u674e\u601d\u5357\u56fa\u7136\u662f\u5927\u4e3a\u9707\u60ca\uff0c\u9633\u5929\u96f7\u5f39\u4e0d\u843d\u4ed6\u7684\u957f\u5251\uff0c\u4e5f\u662f\u4e0d\u80fd\u4e0d\u5fc3\u5934\u5fae\u51db\uff0c\u60f3\u9053\uff1a \u201c\u602a\u4e0d\u5f97\u575a\u513f\u4e0d\u662f\u4ed6\u7684\u5bf9\u624b\uff0c\u8fd9\u5c0f\u5b50\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u505a\u5230\u4e86\u7eff\u6797\u76df\u4e3b\uff0c\u679c\u7136\u662f\u6709\u51e0\u5206\u771f\u5b9e\u7684\u672c \u9886\u3002\u201d \u3000\u3000\u6768\u5a49\u89c1\u674e\u601d\u5357\u4e0d\u654c\uff0c\u8fde\u5fd9\u4e0a\u524d\u5939\u653b\u3002\u9633\u5929\u96f7\u7eb5\u58f0\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u6709\u591a\u5c11\u515a\u7fbd\uff0c\u4e00\u9f50\u6765 \u5427\u3002\u201d\u53cc\u638c\u5de6\u53f3\u5f00\u5f13\uff0c\u628a\u674e\u3001\u6768\u4e8c\u4eba\u4e00\u9f50\u903c\u9000\u3002 \u3000\u3000\u6b64\u65f6\u767d\u4e07\u96c4\u65e9\u5df2\u628a\u7c98\u4e0d\u7f55\u5236\u670d\uff0c\u8d70\u4e86\u51fa\u6765\u3002\u4ed6\u7684\u513f\u5b50\u767d\u5343\u80dc\u5219\u7559\u5728\u5c4b\u5b50\u91cc\u4fdd\u62a4\u4f59\u4e00\u4e2d\uff0c \u8ba9\u4f59\u4e00\u4e2d\u597d\u4ece\u5bb9\u7684\u5ba1\u95ee\u7c98\u3001\u901f\u4e8c\u4eba\u3002 \u3000\u3000\u626c\u5a49\u7684\u54e5\u54e5\u662f\u5ce8\u5d4b\u6d3e\u88f4\u5927\u4fa0\u7684\u5f1f\u5b50\uff0c\u6768\u5a49\u7684\u5251\u6cd5\u5f97\u5176\u4f20\u6388\uff0c\u81ea\u4e5f\u4e0d\u5f31\u3002\u53ef\u60dc\u5403\u4e8f\u5728\u6c14\u529b \u4e0d\u5982\uff0c\u548c\u9633\u5929\u96f7\u521a\u731b\u7edd\u4f26\u7684\u638c\u529b\u6bd4\u8f83\u8d77\u6765\uff0c\u5b9e\u5728\u662f\u76f8\u5dee\u592a\u8fdc\u3002\u9633\u5929\u96f7\u53cc\u638c\u8fde\u73af\u62cd\u51fa\uff0c\u53ea\u542c\u5f97 \u547c\u547c\u8f70\u8f70\uff0c\u5c31\u4f3c\u72c2\u98ce\u5377\u8d77\u5de8\u6d6a\u4e00\u822c\uff0c\u4e00\u6ce2\u672a\u5e73\uff0c\u4e00\u6ce2\u53c8\u8d77\uff0c\u4e00\u6d6a\u63a5\u7740\u4e00\u6d6a\u7684\u5411\u4ed6\u4eec\u6253\u6765\uff01 \u3000\u3000\u6768\u5a49\u4ed7\u7740\u8d85\u5353\u7684\u8f7b\u529f\uff0c\u817e\u632a\u95ea\u5c55\uff0c\u4fe8\u5982\u8f7b\u821f\u5728\u6ce2\u6d9b\u6c79\u6d8c\u3001\u6025\u6d41\u6fc0\u6e4d\u4e4b\u4e2d\u8d77\u4f0f\u56de\u65cb\uff0c\u98d8\u6447 \u6643\u52a8\uff0c\u867d\u5c1a\u4e0d\u81f3\u4e8e\u5373\u906d\u706d\u9876\u4e4b\u7978\uff0c\u4ea6\u662f\u6709\u70b9\u628a\u6301\u4e0d\u5b9a\u4e86\u3002 \u3000\u3000\u674e\u601d\u5357\u54ac\u7d27\u7259\u6839\uff0c\u62fc\u7740\u8c41\u51fa\u6027\u547d\uff0c\u628a\u8fbe\u6469\u5251\u6cd5\u4f7f\u5f97\u51cc\u5389\u65e0\u524d\uff0c\u5192\u6b7b\u731b\u653b\uff0c\u98d2\u98d2\u8fde\u58f0\uff0c\u6d51 \u8eab\u4e0a\u4e0b\u7adf\u4f3c\u95ea\u8d77\u5343\u767e\u9053\u51b7\u7535\u7cbe\u8292\u3002\u9976\u662f\u9633\u5929\u96f7\u672c\u9886\u9ad8\u5f3a\uff0c\u4e5f\u89c9\u5f97\u773c\u82b1\u64a9\u4e71\uff0c\u903c\u5f97\u7a0d\u7a0d\u653e\u677e\u6768 \u5a49\uff0c\u7528\u5927\u90e8\u5206\u7684\u7cbe\u795e\u6765\u5bf9\u4ed8\u674e\u601d\u5357\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u4ed6\u4eec\u4e8c\u4eba\u867d\u7136\u52c9\u5f3a\u53ef\u4ee5\u652f\u6301\uff0c\u9633\u5929\u96f7\u7684\u529f\u529b\u6bd5\u7adf\u662f\u8fdc\u5728\u4ed6\u4eec\u4e4b\u4e0a\uff0c\u4e8c\u4e09\u5341\u62db\u6570\u8fc7 \u540e\uff0c\u674e\u601d\u5357\u7684\u80f8\u53e3\u5df2\u662f\u9690\u9690\u611f\u5230\u75bc\u75db\u3002\u6768\u5a49\u597d\u5728\u5f97\u674e\u601d\u5357\u6321\u4f4f\u6b63\u9762\uff0c\u5979\u53ea\u662f\u4ece\u4fa7\u9762\u914d\u5408\u653b \u51fb\uff0c\u6240\u53d7\u5bf9\u65b9\u7684\u529b\u9053\u8f83\u5c0f\uff0c\u56e0\u6b64\u867d\u7136\u529f\u529b\u7a0d\u5dee\uff0c\u53cd\u800c\u4e0d\u4f3c\u674e\u601d\u5357\u8fd9\u6837\u7684\u611f\u5230\u96be\u53d7\uff0c\u4f46\u4ea6\u662f\u9999 \u6c57\u6dcb\u6f13\u4e86\u3002 \u3000\u3000\u767d\u4e07\u96c4\u4e00\u6765\u662f\u56e0\u4e3a\u5403\u8fc7\u674e\u601d\u5357\u7684\u4e8f\uff0c\u81ea\u662f\u4e0d\u6562\u5728\u9633\u5929\u96f7\u7684\u9762\u524d\u732e\u4e11\uff1b\u4e8c\u6765\u9633\u5929\u96f7\u5df2\u662f\u7a33 \u64cd\u80dc\u7b97\uff0c\u4e5f\u662f\u65e0\u9700\u4ed6\u7684\u5e2e\u5fd9\uff0c\u6545\u6b64\u767d\u4e07\u96c4\u5f53\u7136\u5c31\u4e50\u5f97\u8896\u624b\u65c1\u89c2\u4e86\u3002 \u3000\u3000\u9633\u5929\u96f7\u638c\u529b\u50ac\u7d27\uff0c\u6fc0\u6218\u4e2d\u53ea\u542c\u5f97\u201c\u94db\u201d\u7684\u4e00\u58f0\uff0c\u6768\u5a49\u7684\u5b9d\u5251\u7ed9\u4ed6\u5f39\u5f97\u8131\u624b\u98de\u53bb\u3002\u9633\u5929\u96f7 \u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4eba\u8fd8\u4e0d\u675f\u624b\u5c31\u64d2\uff0c\u5f53\u771f\u662f\u4e0d\u8981\u6027\u547d\u4e86\u4e48\uff1f\u201d \u3000\u3000\u767d\u4e07\u96c4\u4e5f\u5728\u4e00\u65c1\u6447\u65d7\u5450\u558a\uff0c\u559d\u9053\uff1a\u201c\u9633\u56fd\u5e08\u6b66\u529f\u76d6\u4e16\uff0c\u6211\u529d\u4f60\u4eec\u8fd8\u662f\u8d76\u5feb\u8ba4\u8f93\u7684\u597d\uff01\u201d \u3000\u3000\u8bdd\u72b9\u672a\u4e86\uff0c\u5ffd\u542c\u5f97\u6697\u5668\u7834\u7a7a\u4e4b\u58f0\uff0c\u523a\u8033\u5f02\u5e38\uff0c\u9633\u5929\u96f7\u559d\u9053\uff1a\u201c\u662f\u8c01\u5077\u65bd\u6697\u7b97\uff0c\u6709\u80c6\u7684\u7ad9 \u51fa\u6765\u5427\uff01\u201d \u3000\u3000\u4e0d\u6599\u90a3\u679a\u77f3\u5b50\u5374\u5e76\u975e\u5411\u4ed6\u6253\u6765\uff0c\u800c\u662f\u5411\u6768\u5a49\u90a3\u67c4\u5b9d\u5251\u649e\u51fb\u7684\u3002\u6768\u5a49\u90a3\u67c4\u5251\u521a\u521a\u8131\u624b\u98de\u4e0a \u534a\u7a7a\uff0c\u5c1a\u672a\u843d\u4e0b\uff0c\u7ed9\u90a3\u679a\u77f3\u4e8e\u78b0\u4e2a\u6b63\u7740\uff0c\u5012\u98de\u56de\u6765\u3002\u8fd9\u67c4\u5012\u98de\u56de\u6765\u7684\u5b9d\u5251\u624d\u662f\u5bf9\u51c6\u4e86\u9633\u5927\u96f7 \u7684\u80f8\u53e3\u7684\u3002 \u3000\u3000\u9633\u5929\u96f7\u5927\u5403\u4e00\u60ca\uff0c\u5fc3\u60f3\uff1a\u201c\u5f53\u4eca\u4e4b\u4e16\uff0c\u662f\u8c01\u7adf\u7136\u6709\u6b64\u529f\u529b\uff1f\u201d\u9976\u662f\u4ed6\u7684\u5929\u96f7\u529f\u65e9\u5df2\u5230\u4e86 \u7089\u706b\u7eaf\u9752\u7684\u5883\u754c\uff0c\u4f46\u662f\u8fd9\u67c4\u5251\u7684\u6765\u52bf\uff0c\u4ea6\u662f\u4e0d\u6562\u7a7a\u624b\u786c\u63a5\uff0c\u53ea\u597d\u8fc5\u901f\u79bb\u5f00\uff0c\u674e\u601d\u5357\u5f85\u90a3\u67c4\u5251 \u4ece\u9633\u5929\u96f7\u7684\u5934\u9876\u98de\u8fc7\u4e4b\u540e\uff0c\u4f7f\u51fa\u4e00\u62db\u201c\u4e3e\u706b\u64a9\u5929\u201d\uff0c\u9752\u94a2\u5251\u8f7b\u8f7b\u4e00\u62e8\uff0c\u6b64\u65f6\u90a3\u67c4\u5251\u52b2\u9053\u5df2 \u8870\uff0c\u7ed9\u4ed6\u7528\u4e2a\u201c\u5378\u201d\u5b57\u8bc0\u8f7b\u8f7b\u4e00\u62e8\uff0c\u4fbf\u5373\u843d\u4e86\u4e0b\u6765\uff0c\u6768\u5a49\u7acb\u5373\u63a5\u5230\u624b\u4e2d\u3002 \u3000\u3000\u9633\u5929\u96f7\u7a33\u4f4f\u8eab\u5f62\uff0c\u62ac\u5934\u4e00\u671b\uff0c\u53ea\u89c1\u4e00\u4e2a\u5e74\u7ea6\u4e94\u65ec\u7684\u9752\u888d\u6c49\u5b50\u5df2\u662f\u7ad9\u5728\u4ed6\u7684\u9762\u524d\uff0c\u51b7\u51b7\u8bf4 \u9053\uff1a\u201c\u6211\u7684\u77f3\u5b50\u53ef\u5e76\u4e0d\u662f\u6253\u4f60\u7684\uff0c\u8bf4\u5f97\u4e0a\u4ec0\u4e48\u6697\u7b97\uff1f\u54fc\uff0c\u6211\u73b0\u5728\u5c31\u7ad9\u51fa\u6765\u4e86\uff0c\u4f60\u5f85\u600e\u6837\uff1f\u201d \u3000\u3000\u767d\u4e07\u96c4\u4e0e\u674e\u601d\u5357\u89c1\u4e86\u6b64\u4eba\uff0c\u4e00\u4e2a\u662f\u5927\u5403\u4e00\u60ca\uff0c\u4e00\u4e2a\u662f\u5927\u559c\u8fc7\u671b\u3002\u539f\u6765\u8fd9\u9752\u8863\u6c49\u5b50\u4e0d\u662f\u522b \u4eba\uff0c\u6b63\u662f\u6c5f\u5357\u5927\u4fa0\u5b5f\u5c11\u521a\u3002 \u3000\u3000\u674e\u601d\u5357\u53eb\u9053\uff1a\u201c\u5b5f\u5927\u4fa0\uff0c\u8fd9\u53ae\u5c31\u662f\u91d1\u56fd\u7684\u56fd\u5e08\u9633\u5929\u96f7\u3002\u201d\u5b5f\u5c11\u521a\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u6211\u77e5\u9053\uff0c \u4e0d\u662f\u51b2\u7740\u4ed6\uff0c\u6211\u8fd8\u4e0d\u4f1a\u6765\u5462\uff01\u201d\u8f6c\u8fc7\u5934\u6765\uff0c\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u53c8\u76ef\u7740\u767d\u4e07\u96c4\u9053\uff1a\u201c\u767d\u4e07\u96c4\uff0c\u4f60 \u5728\u98de\u9f99\u5c71\u4e0a\u662f\u600e\u6837\u5bf9\u6211\u8bf4\u7684\uff1f\u201d\u767d\u4e07\u96c4\u66fe\u7ecf\u5bf9\u4ed6\u8bf4\u8fc7\uff0c\u8bf4\u662f\u4ece\u4eca\u4e4b\u540e\uff0c\u51b3\u610f\u201c\u606f\u5f71\u201d\u5bb6\u56ed\uff0c \u91d1\u76c6\u6d17\u624b\uff0c\u51b3\u4e0d\u6562\u518d\u51fa\u6c5f\u6e56\uff0c\u4e3a\u975e\u4f5c\u6b79\u7684\u3002 \u3000\u3000\u767d\u4e07\u96c4\u5413\u5f97\u9762\u8272\u5982\u571f\uff0c\u98a4\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u5e76\u6ca1\u6709\u8fdd\u80cc\u8bfa\u8a00\uff0c\u6211\u56de\u5bb6\u4e4b\u540e\uff0c\u672a\u66fe\u79bb\u8fc7\u5bb6\u95e8\u4e00 \u6b65\u3002\u201d\u674e\u601d\u5357\u51b7\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u786e\u662f\u672a\u66fe\u79bb\u8fc7\u5bb6\u95e8\u4e00\u6b65\u3002\u53ef\u662f\u5728\u4f60\u7684\u5bb6\u4e2d\u5374\u85cf\u6709\u4e00\u4e2a\u6c49 \u8d3c\uff0c\u8eab\u4e3a\u91d1\u56fd\u526f\u5143\u5e05\u7684\u4f59\u4e00\u4e2d\u3002\u201d \u3000\u3000\u5b5f\u5c11\u521a\u559d\u9053\uff1a\u201c\u767d\u4e07\u96c4\uff0c\u4f60\u4e0d\u5fc5\u82b1\u8a00\u5de7\u8bed\u4e86\u3002\u4f60\u628a\u4f59\u4e00\u4e2d\u4e56\u4e56\u7684\u4ea4\u51fa\u6765\uff0c\u6216\u8bb8\u6211\u8fd8\u53ef\u4ee5 \u51cf\u8f7b\u4f60\u7684\u8d23\u7f5a\uff0c\u5426\u5219\u4f60\u4f11\u60f3\u6211\u653e\u8fc7\u4e86\u4f60\uff01\u201d \u3000\u3000\u9633\u5929\u96f7\u5927\u6012\u559d\u9053\uff1a\u201c\u767d\u5e84\u4e3b\uff0c\u6709\u6211\u5728\u6b64\uff0c\u6015\u4ec0\u4e48\uff1f\u54fc\uff0c\u539f\u6765\u4f60\u5c31\u662f\u53f7\u79f0\u5929\u4e0b\u7b2c\u4e00\u795e\u5251\u7684 \u5b5f\u5c11\u521a\u5417\uff0c\u6211\u5012\u8981\u89c1\u8bc6\u89c1\u8bc6\u4f60\u7684\u672c\u9886\uff01\u201d\u8981\u77e5\u9633\u5929\u96f7\u7684\u5929\u96f7\u529f\u4e5f\u662f\u53f7\u79f0\u5929\u4e0b\u65e0\u53cc\u7684\uff0c\u6545\u6b64\uff0c \u867d\u7136\u6709\u70b9\u9707\u6151\u4e8e\u795e\u5251\u5b5f\u5c11\u521a\u7684\u5a01\u540d\uff0c\u5374\u4e5f\u8fd8\u4e0d\u80af\u4f4e\u5934\u670d\u8f93\u3002 \u3000\u3000\u5b5f\u5c11\u521a\u51b7\u7b11\u9053\uff1a\u201c\u6211\u4e5f\u6b63\u662f\u8981\u89c1\u8bc6\u89c1\u8bc6\u4f60\u8fd9\u4e00\u4f4d\u88ab\u767d\u4e07\u96c4\u6367\u4e3a\u6b66\u529f\u76d6\u4e16\u7684\u91d1\u56fd\u56fd\u5e08\uff01\u201d \u3000\u3000\u9633\u5929\u96f7\u9053\uff1a\u201c\u54b1\u4eec\u662f\u4e00\u4e2a\u5bf9\u4e00\u4e2a\uff0c\u53ef\u4e0d\u8bb8\u522b\u4eba\u63d2\u624b\uff01\u201d\u5b5f\u5c11\u521a\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u8fd8\u9700\u4f60 \u8bf4\uff01\u6765\u5427\uff01\u201d \u3000\u3000\u9633\u5929\u96f7\u9053\uff1a\u201c\u5b5f\u5c11\u521a\uff0c\u4f60\u8fd8\u4e0d\u4eae\u5251\uff0c\u66f4\u5f85\u4f55\u65f6\uff1f\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u547c\u7684\u4e00\u638c\uff0c\u5c31\u5411\u5b5f\u5c11\u521a\u6253 \u4e86\u8fc7\u6765\u3002\u8fd9\u4e00\u638c\u5df2\u662f\u4f7f\u51fa\u4e86\u7b2c\u4e5d\u91cd\u7684\u5929\u96f7\u529f\u3002 \u3000\u3000\u672c\u6765\u4ee5\u9633\u5929\u96f7\u7684\u8eab\u4efd\uff0c\u4ed6\u8fd9\u6837\u8bf4\u4e86\uff0c\u5c31\u5e94\u8be5\u7b49\u5f85\u5b5f\u5c11\u521a\u62d4\u5251\u51fa\u9798\u4e4b\u540e\u65b9\u80fd\u52a8\u624b\u7684\u3002\u5982\u4eca \u4ed6\u62a2\u5148\u52a8\u624b\uff0c\u867d\u7136\u4e5f\u7b97\u4e0d\u5f97\u662f\u5077\u88ad\uff0c\u4f46\u5df2\u7ecf\u662f\u5931\u8eab\u4efd\uff0c\u800c\u4e14\u793a\u4eba\u4ee5\u5f31\u4e86\u3002\u65c1\u89c2\u8005\u90fd\u53ef\u4ee5\u770b\u5f97 \u51fa\u6765\uff0c\u4ed6\u662f\u5206\u660e\u6709\u70b9\u5bb3\u6070\u5b5f\u5c11\u521a\u7684\u201c\u795e\u5251\u201d\uff0c\u6240\u4ee5\u624d\u8981\u5148\u53d1\u5236\u4eba\u7684\u3002 \u3000\u3000\u5b5f\u5c11\u521a\u6ca1\u6709\u62d4\u5251\uff0c\u5176\u5b9e\u4e5f\u6b63\u662f\u60f3\u8bd5\u4e00\u8bd5\u9633\u5929\u96f7\u7684\u638c\u529b\uff0c\u53cc\u638c\u76f8\u4ea4\uff0c\u53ea\u542c\u5f97\u201c\u84ec\u201d\u7684\u4e00 \u58f0\uff0c\u9633\u5929\u96f7\u7684\u8eab\u5f62\u6643\u4e86\u4e00\u6643\uff0c\u5b5f\u5c11\u521a\u5374\u662f\u63a5\u8fde\u9000\u4e86\u4e24\u6b65\u3002 \u3000\u3000\u5b5f\u5c11\u521a\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u53ae\u7684\u5929\u96f7\u529f\u679c\u7136\u540d\u4e0d\u865a\u4f20\uff0c\u82e5\u4e0d\u7528\u5251\uff0c\u53ea\u6015\u6211\u662f\u672a\u5fc5\u80dc\u5f97\u8fc7\u4ed6 \u4e86\u3002\u201d \u3000\u3000\u6b8a\u4e0d\u77e5\u5b5f\u5c11\u521a\u56fa\u7136\u662f\u6709\u70b9\u5403\u60ca\uff0c\u4f46\u9633\u5929\u96f7\u5219\u66f4\u662f\u5fc3\u5934\u5927\u9a87\u3002\u8981\u77e5\u9053\u6b66\u529f\u4e4b\u9053\uff0c\u5404\u6709\u64c5 \u957f\uff0c\u5b5f\u5c11\u521a\u4e43\u662f\u5251\u672f\u8457\u540d\uff0c\u5e76\u975e\u4ee5\u638c\u529b\u79f0\u96c4\u7684\u3002\u4f46\u4ed6\u786c\u63a5\u9633\u5929\u96f7\u7684\u638c\u529b\uff0c\u867d\u7136\u597d\u4f3c\u7565\u900a\uff0c\u5374 \u662f\u9762\u4e0d\u6539\u8272\uff0c\u5728\u4ed6\u4e0d\u64c5\u957f\u7684\u8fd9\u4e00\u65b9\u9762\u5c1a\u65e6\u5982\u6b64\u4e86\u5f97\uff0c\u5219\u4ed6\u6240\u64c5\u957f\u7684\u5251\u672f\uff0c\u66f4\u662f\u53ef\u60f3\u800c\u77e5\u4e86\uff01 \u3000\u3000\u674e\u601d\u5357\u53eb\u9053\uff1a\u201c\u5b5f\u5927\u4fa0\uff0c\u5bf9\u4ed8\u5978\u4eba\uff0c\u4f55\u5fc5\u5ba2\u6c14\uff1f\u201d\u6768\u5a49\u5219\u5728\u9a82\u9053\uff1a\u201c\u54fc\uff0c\u4ec0\u4e48\u91d1\u56fd\u56fd \u5e08\uff0c\u597d\u4e0d\u8981\u8138\u3002\u201d \u3000\u3000\u9633\u5929\u96f7\u9762\u4e0a\u4e00\u7ea2\uff0c\u5355\u638c\u5212\u4e86\u4e00\u9053\u5706\u5f27\uff0c\u6697\u85cf\u5148\u624b\u653b\u52bf\uff0c\u5374\u4e0d\u51fb\u4e0b\uff0c\u8bf4\u9053\uff1a\u201c\u5b5f\u5c11\u521a\uff0c\u6211 \u4e1a\u5df2\u53eb\u4f60\u4eae\u5251\u7684\u4e86\uff0c\u4f60\u6562\u770b\u4e0d\u8d77\u6211\uff1f\u518d\u4e0d\u4eae\u5251\uff0c\u6211\u53ef\u4e0d\u548c\u4f60\u5ba2\u6c14\u4e86\u3002\u201d \u3000\u3000\u5b5f\u5c11\u521a\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4e00\u70b9\u4e0d\u9519\uff0c\u6211\u7684\u786e\u662f\u770b\u4e0d\u8d77\u4f60\uff0c\u770b\u4e0d\u8d77\u4f60\u662f\u52a9\u7ea3\u4e3a\u8650\u7684\u5978 \u8d3c\uff01\u4e0d\u8fc7\u6211\u5b5f\u5c11\u521a\u4e09\u5341\u5e74\u672a\u9022\u654c\u624b\uff0c\u5374\u662f\u96be\u5f97\u9047\u4e0a\u6709\u4f60\u8fd9\u6837\u4e00\u8eab\u6b66\u529f\u7684\u4eba\uff0c\u597d\u8ba9\u6211\u53ef\u4ee5\u8bd5\u4e00 \u8bd5\u6211\u65b0\u8fd1\u7ec3\u6210\u7684\u5251\u672f\u4e86\u3002\u201d\u8a00\u4e0b\u4e4b\u610f\uff0c\u5bf9\u4ed6\u7684\u6b66\u529f\u5012\u662f\u770b\u5f97\u8d77\u7684\u3002 \u3000\u3000\u9633\u5929\u96f7\u662f\u4e00\u526f\u539a\u9762\u76ae\uff0c\u7ed9\u4ed6\u9a82\u5978\u8d3c\uff0c\u5e76\u4e0d\u52a8\u6012\uff0c\u53cd\u800c\u5f97\u610f\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u65e2\u7136\u8bc6\u5f97\u6211 \u7684\u5389\u5bb3\uff0c\u8fd8\u4e0d\u8d76\u5feb\u62d4\u5251\u51fa\u62db\uff01\u201d \u3000\u3000\u5b5f\u5c11\u521a\u201c\u5478\u201d\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4ee5\u4e3a\u4f60\u7684\u5929\u96f7\u529f\u5c31\u53ef\u4ee5\u6a2a\u884c\u5929\u4e0b\u5417\uff1f\u563f\uff0c\u563f\uff0c\u6211\u8fd8\u6ca1 \u6709\u79f0\u8d5e\u4f60\u5462\uff0c\u4f60\u5c31\u81ea\u5df1\u7ed9\u81ea\u5df1\u8138\u4e0a\u8d34\u91d1\u3002\u54fc\uff0c\u4f60\u8981\u89c1\u8bc6\u6211\u7684\u5251\u672f\uff0c\u90a3\u5c31\u8ba9\u4f60\u89c1\u8bc6\u5427\u3002\u201d \u3000\u3000\u5b5f\u5c11\u521a\u5012\u63e1\u5251\u67c4\uff0c\u559d\u9053\uff1a\u201c\u770b\u5251\uff01\u201d\u547c\u7684\u4e00\u80a1\u52b2\u98ce\uff0c\u4fbf\u523a\u8fc7\u6765\uff0c\u9633\u5929\u96f7\u6a2a\u638c\u51fb\u51fa\uff0c\u53ea\u542c \u5f97\u55e1\u55e1\u4e4b\u58f0\u4e0d\u7edd\u4e8e\u8033\u3002\u539f\u6765\u662f\u5b5f\u5c11\u521a\u7684\u5251\u5c16\u7ed9\u4ed6\u7684\u638c\u98ce\u9707\u8361\u53d1\u51fa\u7684\u58f0\u97f3\u3002\u4e00\u4e2a\u662f\u201c\u795e\u5251\u201d\uff0c \u4e00\u4e2a\u662f\u94c1\u638c\u65e0\u654c\uff0c\u4ea4\u624b\u6570\u62db\uff0c\u8c01\u4e5f\u5360\u4e0d\u4e86\u5bf9\u65b9\u7684\u4fbf\u5b9c\u3002 \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u5a49\u59b9\uff0c\u4f60\u53bb\u62a5\u4ec7\uff0c\u6211\u6765\u5bf9\u4ed8\u8fd9\u4e2a\u8001\u8d3c\u3002\u201d \u3000\u3000\u767d\u4e07\u96c4\u89c1\u9633\u5929\u96f7\u654c\u5f97\u4f4f\u5b5f\u5c11\u521a\uff0c\u80c6\u5b50\u767b\u65f6\u53c8\u5927\u8d77\u6765\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u674e\u601d\u5357\uff0c\u4f60\u4ee5\u4e3a\u6211\u5f53 \u771f\u662f\u6015\u4f60\u4e0d\u6210\uff1f\u201d\u674e\u601d\u5357\u201c\u5530\u201d\u7684\u4fbf\u662f\u4e00\u5251\u523a\u53bb\uff0c\u559d\u9053\uff1a\u201c\u7ba1\u4f60\u6015\u4e0d\u6015\uff0c\u4eca\u65e5\u6211\u662f\u51b3\u4e0d\u653e\u8fc7 \u4f60\u7684\u3002\u201d \u3000\u3000\u638c\u98ce\u5251\u5f71\u4e4b\u4e2d\uff0c\u53ea\u542c\u5f97\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u767d\u4e07\u96c4\u7684\u8863\u8896\u7ed9\u674e\u601d\u5357\u7684\u5b9d\u5251\u524a\u53bb\u4e86\u4e00\u5e45\uff0c\u4f46\u674e \u6069\u5357\u5374\u5e76\u4e0d\u4e58\u80dc\u8ffd\u51fb\uff0c\u53cd\u800c\u5012\u7eb5\u51fa\u4e00\u4e08\u5f00\u5916\u3002\u539f\u6765\u767d\u4e07\u96c4\u4f7f\u7684\u662f\u5341\u5206\u72e0\u8fa3\u7684\u5206\u7b4b\u9519\u9aa8\u624b\u6cd5\uff0c \u5206\u7b4b\u9519\u9aa8\u624b\u6cd5\u5229\u4e8e\u8fd1\u8eab\u7f20\u6597\uff0c\u674e\u601d\u5357\u6050\u9632\u7740\u4ed6\u6240\u7b97\uff0c\u662f\u4ee5\u4e00\u6cbe\u5373\u9000\uff0c\u5f15\u4ed6\u6765\u8ffd\u3002 \u3000\u3000\u53ef\u662f\u674e\u601d\u5357\u4e00\u9000\uff0c\u767d\u4e07\u96c4\u4e5f\u5c31\u7acb\u5373\u505c\u4e86\u811a\u6b65\uff0c\u4ecd\u7136\u7ad9\u5728\u539f\u5730\uff0c\u52a8\u4e5f\u4e0d\u52a8\u3002\u539f\u6765\u767d\u4e07\u96c4\u90a3 \u65e5\u8d25\u7ed9\u674e\u601d\u5357\u4e4b\u540e\uff0c\u56de\u5bb6\u4ed4\u7ec6\u7422\u78e8\uff0c\u89c9\u5f97\u81ea\u5df1\u8f93\u5f97\u5b9e\u5728\u4e0d\u503c\u3002\u8bba\u529f\u529b\u4ed6\u662f\u80dc\u8fc7\u674e\u601d\u5357\u7684\uff0c\u4f46 \u5403\u4e8f\u5728\u4e0d\u61c2\u5f97\u5982\u4f55\u5e94\u4ed8\u674e\u601d\u5357\u90a3\u4e00\u5957\u53d8\u5316\u83ab\u6d4b\u7684\u5251\u6cd5\u3002 \u3000\u3000\u767d\u4e07\u96c4\u6bd5\u7adf\u662f\u4e00\u4e2a\u7ecf\u9a8c\u5341\u5206\u4e30\u5bcc\u7684\u6b66\u5b66\u884c\u5bb6\uff0c\u7422\u78e8\u4e86\u51e0\u5929\uff0c\u7ec8\u4e8e\u7ed9\u4ed6\u60f3\u51fa\u4e86\u4e00\u79cd\u6218\u672f\uff0c \u8fd9\u5c31\u662f\u7528\u81ea\u5df1\u4e4b\u957f\uff0c\u653b\u654c\u4e4b\u77ed\u3002\u4ed6\u7684\u529f\u529b\u80dc\u4e8e\u674e\u601d\u5357\uff0c\u4e14\u64c5\u957f\u4e8e\u5927\u5c0f\u64d2\u62ff\u624b\u6cd5\u548c\u5206\u7b4b\u9519\u9aa8\u7684 \u529f\u592b\uff0c\u53ea\u8981\u8fd1\u8eab\u640f\u6597\uff0c\u5c31\u51b3\u4e0d\u4f1a\u5403\u4e8f\u3002 \u3000\u3000\u674e\u601d\u5357\u89c1\u5bf9\u65b9\u4e0d\u4e3a\u6240\u52a8\uff0c\u53ea\u597d\u7ee7\u7eed\u91c7\u53d6\u653b\u52bf\uff0c\u767d\u4e07\u96c4\u53cc\u638c\u76d8\u65cb\u98de\u821e\uff0c\u5b88\u5f97\u5341\u5206\u4e25\u5bc6\u3002\u6b63 \u662f\uff1a \u3000\u3000\u5343\u91cc\u8ffd\u51f6\u63a2\u864e\u7a74\uff0c\u4e14\u770b\u5251\u638c\u51b3\u96cc\u96c4\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b\u2014\u2014\u7b2c\u56db\u5341\u4e94\u56de\u3000\u5b9d\u5251\u660e\u73e0\u6b23\u6709\u6258\u3000\u91d1\u679d\u7389\u53f6\u53f9\u98d8\u96f6 \u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b \u7b2c\u56db\u5341\u4e94\u56de\u3000\u5b9d\u5251\u660e\u73e0\u6b23\u6709\u6258\u3000\u91d1\u679d\u7389\u53f6\u53f9\u98d8\u96f6 \u3000\u3000\u674e\u601d\u5357\u4ea6\u662f\u77e5\u5df1\u77e5\u5f7c\uff0c\u767d\u4e07\u96c4\u60f3\u8bf1\u4ed6\u8fd1\u8eab\u640f\u6597\uff0c\u4ed6\u5f53\u7136\u4e5f\u4e0d\u4f1a\u4e0a\u8fd9\u4e2a\u5f53\u7684\u3002\u4e24\u4eba\u4e00\u653b\u4e00 \u5b88\uff0c\u5404\u6709\u64c5\u957f\uff0c\u674e\u601d\u5357\u7eb5\u6a2a\u523a\u51fb\uff0c\u59cb\u7ec8\u4fdd\u6301\u516b\u5c3a\u5de6\u53f3\u7684\u8ddd\u79bb\uff0c\u4f01\u56fe\u5728\u8fdb\u653b\u4e2d\u627e\u5bfb\u5bf9\u65b9\u7684\u7834 \u7efd\uff0c\u4f46\u7531\u4e8e\u4e0d\u6562\u592a\u8fc7\u903c\u8fd1\uff0c\u6025\u5207\u4e4b\u95f4\uff0c\u5012\u662f\u653b\u4e0d\u7834\u5bf9\u65b9\u7684\u53cc\u638c\u4ea4\u9519\u7684\u9632\u5fa1\u3002 \u3000\u3000\u9633\u5929\u96f7\u548c\u767d\u4e07\u96c4\u81ea\u987e\u4e0d\u6687\uff0c\u5269\u4e0b\u6765\u53ef\u4ee5\u4fdd\u62a4\u4f59\u4e00\u4e2d\u7684\u4eba\u5c31\u53ea\u6709\u4e00\u4e2a\u767d\u5343\u80dc\u3002\u6768\u5a49\u773c\u770b\u5927 \u4ec7\u5f97\u62a5\uff0c\u6ee1\u6000\u6b22\u559c\u7684\u5411\u90a3\u95f4\u5c4b\u5b50\u6740\u53bb\uff0c\u559d\u9053\uff1a\u201c\u59d3\u4f59\u7684\u5978\u8d3c\uff0c\u4f60\u8dd1\u4e0d\u4e86\u5566\uff01\u201d \u3000\u3000\u4f59\u4e00\u4e2d\u5413\u5f97\u9762\u9752\u5507\u767d\uff0c\u98a4\u58f0\u8bf4\u9053\uff1a\u201c\u767d\u516c\u5b50\uff0c\u4f60\u6551\u6211\u4e00\u6551\uff0c\u6211\u4fdd\u4e3e\u4f60\u505a\u8499\u53e4\u7684\u5927\u5b98\u3002\u201d \u767d\u5343\u80dc\u9053\uff1a\u201c\u5927\u4eba\u4e0d\u5fc5\u62c5\u5fc3\uff0c\u6211\u7ed9\u4f60\u628a\u8fd9\u6cfc\u4e2b\u5934\u62ff\u6765\u4fbf\u662f\u3002\u201d \u3000\u3000\u6768\u5a49\u7684\u5916\u8c8c\u50cf\u662f\u4e2a\u5f31\u4e0d\u7981\u98ce\u7684\u5973\u5b50\uff0c\u767d\u5343\u80dc\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u4e2b\u5934\u7eb5\u6709\u51e0\u5206\u672c\u4e8b\uff0c\u8c05\u4e5f\u4e0d \u662f\u6211\u7684\u5bf9\u624b\u3002\u201d\u54ea\u77e5\u6768\u5a49\u957f\u5f97\u6e05\u79c0\u6587\u5f31\uff0c\u5251\u6cd5\u5374\u662f\u5341\u5206\u51cc\u5389\u3002\u4ea4\u624b\u4e0d\u8fc7\u6570\u62db\uff0c\u767d\u5e72\u80dc\u5df2\u662f\u611f \u5230\u96be\u4ee5\u5e94\u4ed8\u3002 \u3000\u3000\u6b64\u65f6\u9633\u5929\u96f7\u548c\u5b5f\u5c11\u521a\u8fd9\u4e00\u5bf9\u4e5f\u6e10\u6e10\u5206\u51fa\u4e86\u9ad8\u4e0b\u3002\u9633\u5929\u96f7\u5df2\u7ecf\u628a\u201c\u5929\u96f7\u529f\u201d\u53d1\u6325\u5f97\u6dcb\u6f13\u5c3d \u81f4\uff0c\u53ea\u542c\u5f97\u547c\u547c\u8f70\u8f70\uff0c\u6c99\u98de\u77f3\u8d70\uff0c\u56ed\u5b50\u91cc\u7684\u6811\u6728\u90fd\u7ed9\u9707\u5f97\u6811\u679d\u52a8\u6447\uff0c\u6811\u53f6\u7c41\u7c41\u800c\u843d\uff01\u4f46\u5b5f\u5c11 \u521a\u5982\u7a33\u5982\u76d8\u77f3\u4f3c\u7684\uff0c\u5140\u7acb\u5728\u60ca\u6d9b\u9a87\u6d6a\u4e4b\u4e2d\uff0c\u4e1d\u6beb\u4e0d\u4e3a\u6240\u52a8\u3002 \u3000\u3000\u6fc0\u6218\u4e2d\u5b5f\u5c11\u521a\u731b\u5730\u559d\u9053\uff1a\u201c\u539f\u6765\u4f60\u7684\u5929\u96f7\u529f\u4ea6\u4e0d\u8fc7\u5982\u6b64\uff0c\u9886\u6559\u4e86\uff01\u201d\u5251\u6cd5\u4e00\u53d8\uff0c\u767b\u65f6\u53cd \u5b88\u4e3a\u653b\uff0c\u98d2\u98d2\u8fde\u58f0\uff0c\u6d51\u8eab\u4e0a\u4e0b\uff0c\u7adf\u4f3c\u95ea\u8d77\u4e86\u5343\u767e\u9053\u51b7\u7535\u7cbe\u8292\uff0c\u903c\u5f97\u9633\u5929\u96f7\u773c\u82b1\u64a9\u4e71\uff0c\u4e0d\u7531\u81ea \u4e3b\u5730\u9000\u4e86\u51e0\u6b65\u3002 \u3000\u3000\u9ad8\u4e0b\u6e10\u5206\uff0c\u4f46\u5b5f\u5c11\u521a\u8981\u60f3\u53d6\u80dc\uff0c\u4e5f\u8fd8\u4e0d\u6613\u3002 \u3000\u3000\u9633\u5929\u96f7\u6bd5\u7adf\u4e5f\u7b97\u662f\u4e2a\u9876\u513f\u5c16\u513f\u7684\u9ad8\u624b\uff0c\u4ee5\u529f\u529b\u800c\u8bba\uff0c\u5e76\u4e0d\u5728\u5b5f\u5c11\u521a\u4e4b\u4e0b\uff0c\u4ed6\u9000\u4e86\u51e0\u6b65\uff0c \u63a5\u8fde\u62cd\u4e86\u51e0\u638c\uff0c\u5c45\u7136\u8fd8\u6709\u5b88\u6709\u653b\u3002 \u3000\u3000\u9633\u5929\u96f7\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4e45\u6218\u4e0b\u53bb\uff0c\u53ea\u6015\u6211\u662f\u5b9a\u8981\u5403\u4e8f\u7684\u4e86\u3002\u201d\u767e\u5fd9\u4e2d\u62bd\u773c\u5411\u767d\u4e07\u96c4\u90a3\u8fb9\u671b \u53bb\uff0c\u53ea\u89c1\u767d\u4e07\u96c4\u4e5f\u662f\u50cf\u4ed6\u4e00\u6837\uff0c\u53ea\u6709\u62db\u67b6\u4e4b\u529f\uff0c\u9633\u5929\u96f7\u4e0d\u7981\u5012\u5438\u4e86\u4e00\u53e3\u51c9\u6c14\u3002\u539f\u6765\u4ed6\u662f\u5e0c\u671b \u767d\u4e07\u96c4\u53ef\u4ee5\u80dc\u5f97\u674e\u601d\u5357\uff0c\u90a3\u65f6\u53ea\u8981\u767d\u4e07\u96c4\u4e0e\u4ed6\u8054\u624b\uff0c\u4ed6\u5c31\u6709\u628a\u63e1\u53ef\u4ee5\u6253\u8d25\u5b5f\u5c11\u521a\u4e86\u3002 \u3000\u3000\u5e0c\u671b\u65ad\u7edd\uff0c\u9633\u5929\u96f7\u6218\u610f\u6d88\u6c89\uff0c\u5b5f\u5c11\u521a\u5374\u662f\u8fd0\u5251\u5982\u98ce\uff0c\u5982\u9e70\u7fd4\u96bc\u523a\uff0c\u8d8a\u6218\u8d8a\u663e\u7cbe\u795e\uff01\u5fc3\u91cc \u60f3\u9053\uff1a\u201c\u770b\u6765\u662f\u7528\u4e0d\u5230\u767e\u62db\u5f00\u5916\u4e86\u3002\u201d \u3000\u3000\u9633\u5929\u96f7\u548c\u767d\u4e07\u96c4\u5c1a\u6709\u62db\u67b6\u4e4b\u529f\uff0c\u767d\u5343\u80dc\u7684\u5f62\u52bf\u53ef\u8fd8\u8981\u6bd4\u4ed6\u4eec\u5371\u9669\u5f97\u591a\u3002\u6768\u5a49\u6025\u4e8e\u62a5\u4ec7\uff0c \u6bcf\u4e00\u5251\u90fd\u662f\u8fdb\u624b\u7684\u62db\u6570\u3002\u5ce8\u5d4b\u6d3e\u7684\u5251\u6cd5\u672c\u6765\u662f\u4ee5\u5947\u8be1\u89c1\u957f\uff0c\u767d\u5343\u80dc\u5e76\u975e\u4ee5\u5251\u672f\u89c1\u957f\uff0c\u7109\u80fd\u62b5 \u6321\u5f97\u4f4f\uff1f\u6768\u5a49\u5de6\u4e00\u5251\u53f3\u4e00\u5251\uff0c\u5251\u5149\u4ea4\u53c9\u7a7f\u63d2\uff0c\u5251\u5251\u6307\u5411\u767d\u5343\u80dc\u7684\u8981\u5bb3\u3002\u767d\u5343\u80dc\u4e0d\u7531\u5f97\u51b7\u6c57\u76f4 \u6dcc\uff0c\u5230\u4e86\u6700\u540e\uff0c\u7adf\u662f\u6321\u4e00\u62db\u9000\u4e00\u6b65\uff0c\u4e0d\u77e5\u4e0d\u89c9\u5df2\u7ed9\u6768\u5a49\u903c\u4e0a\u53f0\u9636\u3002\u773c\u770b\u5c31\u8981\u9000\u65e0\u53ef\u9000\uff0c\u53ea\u6709 \u8eb2\u8fdb\u90a3\u95f4\u623f\u5b50\u4e86\u3002 \u3000\u3000\u623f\u5b50\u91cc\u7684\u4f59\u4e00\u4e2d\u5413\u5f97\u51b7\u6c57\u76f4\u6d41\uff0c\u60f3\u51b2\u51fa\u53bb\uff0c\u5374\u53c8\u4e0d\u6562\u3002\u672c\u6765\u4ed6\u662f\u5728\u8fd9\u95f4\u623f\u5b50\u91cc\u5ba1\u95ee\u7c98\u3001 \u901f\u4e8c\u4eba\uff0c\u6b64\u65f6\u5f53\u7136\u662f\u505c\u6b62\u5ba1\u95ee\u4e86\u3002 \u3000\u3000\u53ee\u53ee\u94db\u94db\u4e4b\u58f0\u4e0d\u7edd\u4e8e\u8033\uff0c\u8fd9\u662f\u6768\u5a49\u548c\u767d\u5343\u80dc\u6597\u5251\u7684\u58f0\u97f3\uff0c\u767d\u5343\u80dc\u6bcf\u6321\u4e00\u62db\u5c31\u9000\u4e00\u6b65\uff0c\u4f59 \u4e00\u4e2d\u6bcf\u542c\u5f97\u53ee\u94db\u4e00\u58f0\u5c31\u7981\u4e0d\u4f4f\u5fc3\u5934\u62bd\u6410\u4e00\u4e0b\u3002\u5fc3\u8df3\u7684\u58f0\u97f3\u597d\u50cf\u64c2\u9f13\u4e00\u822c\uff0c\u4f3c\u4e4e\u6bd4\u5916\u9762\u6597\u5251\u7684 \u58f0\u97f3\u66f4\u5bc6\uff01\u4ed6\u7528\u98a4\u6296\u7684\u624b\u5173\u4e0a\u4e86\u623f\u95e8\uff0c\u867d\u7136\u4ed6\u81ea\u5df1\u77e5\u9053\u8fd9\u662f\u6beb\u65e0\u610f\u4e49\u7684\u52a8\u4f5c\uff0c\u56e0\u4e3a\u6768\u5a49\u624b\u4e2d \u62ff\u7684\u4e43\u662f\u660e\u6167\u516c\u4e3b\u6240\u8d60\u7684\u5b9d\u5251\uff0c\u8fd9\u67c4\u5b9d\u5251\u6709\u65ad\u7389\u524a\u94c1\u4e4b\u80fd\uff0c\u4e24\u6247\u623f\u95e8\u5982\u4f55\u80fd\u591f\u62e6\u5f97\u4f4f\u5979\uff01\u4f46 \u53ea\u76fc\u80fd\u628a\u8fd9\u4ee4\u4eba\u5fc3\u60b8\u7684\u6597\u5251\u58f0\u5173\u5728\u5916\u8fb9\uff0c\u5173\u4e0d\u4f4f\uff0c\u58f0\u97f3\u5c0f\u4e9b\u4e5f\u662f\u597d\u7684\u3002 \u3000\u3000\u53ef\u662f\u6768\u5a49\u628a\u4ed6\u6700\u540e\u7684\u4e00\u4e1d\u5e7b\u60f3\u4e5f\u6253\u7834\u4e86\uff0c\u53cc\u5251\u4ea4\u51fb\u7684\u53ee\u53ee\u94db\u94db\u4e4b\u58f0\u4e0d\u4f46\u7ee7\u7eed\u5728\u201c\u51b2\u51fb\u201d \u7740\u4ed6\uff0c\u800c\u4e14\u8d8a\u6765\u8d8a\u662f\u6e05\u695a\u3001\u54cd\u4eae\uff01\u4e00\u7247\u8fde\u73e0\u5bc6\u822c\u54cd\u5f97\u4ed6\u7684\u8033\u9f13\u9690\u9690\u4f5c\u75bc\uff01\u6768\u5a49\u5df2\u7ecf\u628a\u767d\u5343\u80dc \u903c\u4e0a\u53f0\u9636\uff0c\u903c\u4ed6\u9000\u5230\u623f\u95e8\u53e3\u4e86\u3002 \u3000\u3000\u7c98\u4e0d\u7f55\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f59\u5927\u4eba\uff0c\u4f60\u4e5f\u77e5\u9053\u5bb3\u6015\u4e86\u4e48\u3002\u201d \u3000\u3000\u4f59\u4e00\u4e2d\u706b\u7ea2\u4e86\u773c\uff0c\u62d4\u51fa\u4e86\u4f69\u5200\u559d\u9053\uff1a\u201c\u4f60\u4eec\u53ef\u522b\u5f97\u610f\uff0c\u6211\u73b0\u5728\u8fd8\u53ef\u4ee5\u6740\u4e86\u4f60\u4eec\uff01\u201d \u3000\u3000\u901f\u4e0d\u53f0\u559d\u9053\uff1a\u201c\u4f60\u6562\uff01\u201d\u7c98\u4e0d\u7f55\u5374\u51b7\u7b11\u9053\uff1a\u201c\u597d\uff0c\u4f60\u8981\u6740\u6211\u4e48\uff1f\u597d\uff0c\u5f88\u597d\uff01\u90a3\u5c31\u65ab\u6765 \u5440\uff0c\u671d\u7740\u6211\u65ab\u6765\u5440\uff01\u54c8\uff0c\u54c8\uff0c\u4f60\u6ca1\u6709\u80c6\u91cf\uff0c\u8fde\u8fd9\u70b9\u80c6\u91cf\u90fd\u6ca1\u6709\u5417\uff1f\u201d \u3000\u3000\u7c98\u3001\u901f\u4e8c\u4eba\u662f\u7ed9\u9633\u5929\u96f7\u7528\u91cd\u624b\u6cd5\u70b9\u4e86\u819d\u76d6\u7684\u9ebb\u7a74\uff0c\u4e0b\u534a\u8eab\u762b\u75ea\uff0c\u7ad9\u4e0d\u8d77\u6765\uff0c\u4f46\u8fd8\u53ef\u4ee5\u8bf4 \u8bdd\uff0c\u624b\u4e5f\u8fd8\u4f1a\u52a8\uff0c\u4e0d\u8fc7\u6c14\u529b\u5374\u662f\u4f7f\u4e0d\u51fa\u6765\u4e86\u3002 \u3000\u3000\u4f59\u4e00\u4e2d\u6b66\u529f\u5e73\u5eb8\uff0c\u89c1\u4ed6\u53cc\u624b\u4f1a\u52a8\uff0c\u5fc3\u91cc\u4e0d\u80fd\u4e0d\u5bb3\u6015\u51e0\u5206\u3002\u8981\u77e5\u7c98\u3001\u901f\u4e8c\u4eba\u90fd\u662f\u8499\u53e4\u4e00\u7b49 \u4e00\u7684\u6454\u89d2\u9ad8\u624b\uff0c\u53ea\u6015\u4e00\u5200\u65ab\u4e0d\u7740\u4ed6\u4eec\uff0c\u5c31\u4f1a\u7ed9\u4ed6\u4eec\u53cd\u593a\u4e86\u53bb\u3002\u90a3\u65f6\u867d\u7136\u7ad9\u4e0d\u8d77\u6765\uff0c\u6709\u5200\u5728 \u624b\uff0c\u4ea6\u662f\u53ef\u4ee5\u53d6\u81ea\u5df1\u7684\u547d\u3002 \u3000\u3000\u4f59\u4e00\u4e2d\u62d4\u5200\u5728\u624b\uff0c\u4e0d\u6562\u65ab\u4e0b\uff0c\u66f4\u52a0\u614c\u4e86\uff01\u4ed6\u5bb3\u6015\u7c98\u3001\u901f\u4e8c\u4eba\u90a3\u4e24\u53cc\u6076\u72e0\u72e0\u77aa\u7740\u7684\u773c\u775b\uff0c \u4e0d\u7531\u5f97\u4e00\u6b65\u6b65\u7684\u540e\u9000\uff0c\u9000\u5230\u4e86\u5c4b\u9f9f\uff0c\u8eb2\u5728\u5c4b\u89d2\u91cc\u54c6\u54c6\u55e6\u55e6\uff01 \u3000\u3000\u6768\u5a49\u628a\u767d\u5343\u80dc\u903c\u5230\u4e86\u623f\u95e8\u53e3\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u4f60\u8981\u7ed9\u4f59\u4e00\u4e2d\u5356\u547d\u3002\u90a3\u5c31\u6210\u5168\u4f60\u5427\uff01\u201d\u623f\u95e8 \u5df2\u7ecf\u5173\u4e0a\uff0c\u767d\u5343\u80dc\u9000\u65e0\u53ef\u9000\uff0c\u800c\u4e14\u5373\u4f7f\u80fd\u591f\u9000\u5165\u623f\u4e2d\u53c8\u600e\u4e48\u6837\uff0c\u4f59\u4e00\u4e2d\u662f\u5e2e\u4e0d\u4e86\u4ed6\u7684\uff0c\u7ed3\u679c \u8fd8\u4e0d\u662f\u7ed9\u6768\u5a49\u74ee\u4e2d\u6349\u9cd6\uff0c\u624b\u5230\u64d2\u6765\uff01 \u3000\u3000\u767d\u5343\u80dc\u53eb\u9053\uff1a\u201c\u5973\u4fa0\uff0c\u9976\u2014\u2014\u201d\u4e00\u4e2a\u201c\u547d\u201d\u5b57\u5c1a\u672a\u51fa\u53e3\uff0c\u6768\u5a49\u5df2\u662f\u5530\u7684\u4e00\u5251\u523a\u7740\u4ed6\u7684\u624b \u8155\uff0c\u767d\u5343\u80dc\u957f\u5251\u5760\u5730\uff0c\u5413\u5f97\u9b42\u98de\u9b44\u6563\uff0c\u614c\u5fd9\u4f0f\u5730\u4e00\u6eda\uff0c\u4ece\u53f0\u9636\u4e0a\u76f4\u6eda\u4e0b\u53bb\u3002 \u3000\u3000\u6768\u5a49\u62a5\u4ec7\u8981\u7d27\uff0c\u65e0\u5fc3\u53d6\u4ed6\u6027\u547d\uff0c\u5f53\u4e0b\u4fbf\u4e3e\u8d77\u5b9d\u5251\uff0c\u5288\u90a3\u4e24\u6247\u623f\u95e8\uff01 \u3000\u3000\u90a3\u4e24\u6247\u623f\u95e8\u867d\u7136\u662f\u575a\u539a\u7684\u6728\u5934\u505a\u7684\uff0c\u5374\u600e\u7981\u5f97\u8d77\u5b9d\u5251\u7684\u5288\u523a\uff1f\u53ea\u542c\u5f97\u201c\u5494\u5693\u201d\u4e00\u58f0\uff0c\u88c2 \u5f00\u4e86\u4e00\u9053\u7f1d\uff0c\u201c\u5494\u5693\u201d\u4e00\u58f0\uff0c\u53c8\u88c2\u5f00\u4e86\u4e00\u9053\u7f1d\uff01\u8eb2\u5728\u5c4b\u89d2\u98a4\u6296\u7684\u4f59\u4e00\u4e2d\uff0c\u5df2\u7ecf\u770b\u5f97\u89c1\u4ece\u95e8\u7f1d \u4e2d\u63d2\u8fdb\u6765\u7684\u90a3\u4e00\u628a\u660e\u6643\u6643\u7684\u5b9d\u5251\u4e86\uff01 \u3000\u3000\u4f59\u4e00\u4e2d\u84e6\u5730\u60f3\u8d77\u5341\u591a\u5e74\u524d\u628a\u674e\u601d\u5357\u7684\u7236\u4eb2\u674e\u5e0c\u6d69\u6d3b\u57cb\u7684\u60c5\u666f\u3002\u90a3\u65f6\u674e\u5e0c\u6d69\u867d\u7136\u75c5\u5f97\u5f88 \u91cd\uff0c\u4f46\u8fd8\u6ca1\u6709\u65ad\u6c14\uff0c\u4ed6\u628a\u6ce5\u571f\u94f2\u8d77\u6765\uff0c\u4e00\u94f2\u4e00\u94f2\u7684\u6ce5\u58eb\u76d6\u5728\u674e\u5e0c\u6d69\u7684\u8eab\u4e0a\uff0c\u6df9\u6ca1\u4e86\u4ed6\u7684\u624b\uff0c \u6df9\u6ca1\u4e86\u4ed6\u7684\u811a\uff0c\u6700\u540e\u6df9\u6ca1\u4e86\u4ed6\u7684\u5934\u3002\u4ed6\u73b0\u5728\u95ed\u8d77\u773c\u775b\uff0c\u8fd8\u597d\u50cf\u770b\u5f97\u89c1\u674e\u5e0c\u6d69\u88ab\u6d3b\u57cb\u7684\u60e8\u72b6\u3002 \u3000\u3000\u5f53\u7136\u90a3\u4e2a\u65f6\u5019\u4ed6\u662f\u4e00\u70b9\u4e5f\u4e0d\u89c9\u5f97\u6709\u4ec0\u4e48\u60b2\u60e8\u7684\uff0c\u6070\u6070\u76f8\u53cd\uff0c\u90a3\u65f6\u5019\u4ed6\u7684\u5fc3\u4e2d\u662f\u5145\u6ee1\u5feb\u610f \u7684\uff0c\u201c\u6c99\u201d\u7684\u4e00\u94f2\uff0c\u201c\u6c99\u201d\u7684\u53c8\u662f\u4e00\u94f2\uff0c\u8ba9\u674e\u5e0c\u6d69\u6162\u6162\u62b5\u53d7\u6b7b\u795e\u7684\u78e8\u6298\uff0c\u4ed6\u5374\u5728\u4e00\u65c1\u6b23\u8d4f\uff0c \u8fd9\u662f\u591a\u4e48\u75db\u5feb\u554a\uff01 \u3000\u3000\u4f46\u73b0\u5728\u4ed6\u4f53\u4f1a\u5f97\u5230\u674e\u5e0c\u6d69\u5f53\u65f6\u7684\u611f\u89c9\u4e86\uff01\u201c\u5494\u5693\u201d\u4e00\u58f0\uff0c\u95e8\u4e0a\u88c2\u5f00\u4e86\u4e00\u9053\u7f1d\uff01\u201c\u5494\u5693\u201d \u4e00\u58f0\uff0c\u53c8\u88c2\u5f00\u4e86\u4e00\u9053\u7f1d\uff01\u6768\u5a49\u5b9d\u5251\u5288\u523a\u623f\u95e8\u7684\u58f0\u54cd\uff0c\u4e0d\u6b63\u662f\u548c\u4ed6\u5f53\u65f6\u94f2\u58eb\u6d3b\u57cb\u674e\u5e0c\u6d69\u7684\u97f3\u54cd \u76f8\u4f3c\u4e48\uff1f\u4ed6\u7684\u773c\u775b\u6ca1\u778e\uff0c\u4f46\u5df2\u611f\u5230\u773c\u524d\u662f\u4e00\u7247\u9ed1\u6697\u4e86\u3002 \u3000\u3000\u201c\u674e\u5e0c\u6d69\u540e\u6765\u8fd8\u5f97\u4ee5\u4fa5\u5e78\u9003\u751f\uff0c\u6211\u6050\u6015\u662f\u6ca1\u6709\u8fd9\u4e2a\u5e78\u8fd0\u4e86\u3002\u5509\uff0c\u6d3b\u62a5\u5e94\uff01\u6d3b\u62a5\u5e94\uff01\u201d \u3000\u3000\u4f59\u4e00\u4e2d\u6b63\u5728\u6b7b\u795e\u7684\u9634\u5f71\u4e4b\u4e0b\u98a4\u6296\uff0c\u5ffd\u542c\u5f97\u4e00\u4e2a\u597d\u4f3c\u719f\u6089\u7684\u58f0\u97f3\u53eb\u9053\uff1a\u201c\u767d\u5e84\u4e3b\uff0c\u767d\u5e84 \u4e3b\uff01\u4f59\u5927\u4eba\u8fd8\u5728\u8fd9\u91cc\u5417\uff1f\u201d\u968f\u5373\u542c\u5f97\u674e\u601d\u5357\u7684\u58f0\u97f3\u53eb\u9053\uff1a\u201c\u54a6\uff0c\u8fd9\u4e0d\u6b63\u662f\u660e\u6167\u516c\u4e3b\uff1f\u5c60\u9f99\uff0c \u4f60\u597d\u5927\u80c6\uff0c\u5feb\u628a\u516c\u4e3b\u653e\u4e0b\uff01\u201d \u3000\u3000\u623f\u95e8\u5df2\u7ecf\u5f00\u4e86\u4e00\u4e2a\u7a9f\u7abf\uff0c\u773c\u770b\u6768\u5a49\u5c31\u53ef\u4ee5\u7834\u95e8\u800c\u5165\uff0c\u4f46\u5b9d\u5251\u5288\u523a\u7684\u58f0\u97f3\u5374\u7a81\u7136\u505c\u6b62\u4e86\u3002 \u3000\u3000\u539f\u6765\u6b63\u662f\u5c60\u9f99\u628a\u660e\u6167\u516c\u4e3b\u62bc\u5230\u4e86\u767d\u5bb6\u5e84\u3002 \u3000\u3000\u767d\u5bb6\u7684\u4ec6\u4eba\u4e0d\u662f\u4e00\u822c\u5bfb\u5e38\u7684\u4ec6\u5f79\uff0c\u4ed6\u4eec\u90fd\u662f\u77e5\u9053\u6c5f\u5357\u5927\u4f19\u5b5f\u5c11\u521a\u7684\u5a01\u540d\u7684\uff0c\u5b5f\u5c11\u521a\u4e00\u52a8 \u624b\uff0c\u4ed6\u4eec\u5c31\u77e5\u9053\u4e3b\u4eba\u8d70\u7136\u96be\u9003\u516c\u9053\uff0c\u65e9\u5df2\u9003\u8dd1\u4e00\u7a7a\u3002\u56e0\u6b64\u5c60\u9f99\u662f\u672a\u7ecf\u901a\u62a5\u4e86\u5f84\u81ea\u8fdb\u6765\u7684\u3002 \u3000\u3000\u5c60\u9f99\u672c\u6765\u5c31\u89c9\u5f97\u5947\u602a\u7684\u4e86\uff0c\u4e00\u8def\u8fdb\u6765\uff0c\u4e00\u8def\u53eb\u558a\u201c\u767d\u5e84\u4e3b\u201d\uff0c\u8fdb\u4e86\u540e\u56ed\uff0c\u84e6\u7136\u53d1\u73b0\u5b5f\u5c11 \u521a\u548c\u674e\u601d\u5357\u6b63\u5728\u4e0e\u9633\u5929\u96f7\u3001\u767d\u4e07\u96c4\u4ea4\u624b\uff0c\u4ed6\u8fd9\u4e00\u60ca\u5f53\u771f\u662f\u975e\u540c\u5c0f\u53ef\uff01 \u3000\u3000\u674e\u601d\u5357\u8fd9\u4e00\u60ca\u4e5f\u662f\u975e\u540c\u5c0f\u53ef\uff0c\u4ed6\u662f\u65e9\u5df2\u62c5\u5fc3\u660e\u6167\u516c\u4e3b\u906d\u53d7\u5c60\u9f99\u7684\u6697\u7b97\uff0c\u73b0\u5728\u679c\u7136\u662f\u7ed9\u4ed6 \u4e0d\u5e78\u800c\u6599\u4e2d\uff01 \u3000\u3000\u674e\u601d\u5357\u90a3\u4e48\u4e00\u558a\u4e4b\u540e\uff0c\u5c60\u9f99\u5012\u662f\u51cf\u5c11\u4e86\u51e0\u5206\u5bb3\u6015\u4e86\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5bf9\u5440\uff0c\u6709\u8fd9\u4e2a\u6700\u597d\u7684 \u4eba\u8d28\uff0c\u4e3a\u4f55\u4e0d\u52a0\u5229\u7528\uff1f\u201d \u3000\u3000\u8001\u5978\u5de8\u733e\u7684\u4f59\u4e00\u4e2d\u5fc3\u601d\u8f6c\u5f97\u66f4\u5feb\uff0c\u4ed6\u5403\u4e86\u4e00\u60ca\u4e4b\u540e\uff0c\u767b\u65f6\u60f3\u5230\u4e86\u4e3b\u610f\uff0c\u4ece\u7a97\u53e3\u4f38\u51fa\u5934 \u6765\uff0c\u53eb\u9053\uff1a\u201c\u674e\u601d\u5357\uff0c\u54b1\u4eec\u6765\u8c08\u4e00\u6869\u4ea4\u6613\uff0c\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u53eb\u9053\uff1a\u201c\u674e\u516c\u5b50\uff0c\u522b\u542c\u4ed6\u7684\u82b1\u8a00\u5de7\u8bed\uff01\u201d \u3000\u3000\u4f59\u4e00\u4e2d\u7b11\u9053\uff1a\u201c\u516c\u4e3b\uff0c\u4f60\u83ab\u731c\u7591\uff0c\u6211\u8fd9\u6b21\u53ef\u662f\u4e3a\u4e86\u4f60\u597d\u5462\uff01\u6211\u77e5\u9053\u4f60\u4e0d\u613f\u610f\u5ac1\u7ed9\u9547\u56fd\u738b \u5b50\uff0c\u662f\u4e0d\u662f\uff1f\u8bf4\u53e5\u516c\u9053\u8bdd\uff0c\u4ed6\u90a3\u526f\u5c0a\u5bb9\uff0c\u4e5f\u7684\u786e\u662f\u914d\u4e0d\u8d77\u4f60\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u559d\u9053\uff1a\u201c\u6709\u8bdd\u5feb\u8bf4\uff0c\u6709\u5c41\u5feb\u653e\uff0c\u522b\u800d\u8d2b\u5634\uff01\u201d \u3000\u3000\u4f59\u4e00\u4e2d\u9053\uff1a\u201c\u674e\u516c\u5b50\uff0c\u516c\u4e3b\u5bf9\u4f60\u6709\u60c5\u6709\u4e49\uff0c\u6211\u770b\u4f60\u4e5f\u820d\u4e0d\u5f97\u5979\u56de\u548c\u6797\u7684\u5427\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u4f60\u6709\u6b63\u7ecf\u8bdd\u6ca1\u6709\uff1f\u4f60\u518d\u80e1\u8bf4\u516b\u9053\uff0c\u53ef\u4f11\u602a\u6211\u4e0d\u5ba2\u6c14\u4e86\uff01\u201d \u3000\u3000\u6768\u5a49\u6447\u4e86\u6447\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u5357\u54e5\uff0c\u54b1\u4eec\u4f55\u5fc5\u4e0e\u4ed6\u4e00\u822c\u89c1\u8bc6\u3002\u597d\uff0c\u4f59\u4e00\u4e2d\uff0c\u4f60\u5212\u51fa\u9053\u51e0\u6765 \u5427\uff0c\u6211\u4eec\u662f\u60f3\u628a\u516c\u4e3b\u7559\u4e0b\uff01\u201d\u4f59\u4e00\u4e2d\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4e48\u8fd9\u6869\u4ea4\u6613\u5c31\u53ef\u4ee5\u505a\u6210\u529f\u4e86\u3002\u516c\u4e3b\u7559\u7ed9\u4f60 \u4eec\uff0c\u4f46\u4f60\u4eec\u4e5f\u5f97\u8ba9\u6211\u56de\u548c\u6797\u53bb\uff01\u8fd8\u6709\uff0c\u9633\u56fd\u5e08\u548c\u767d\u5e84\u4e3b\uff0c\u4f60\u4eec\u4e5f\u4e0d\u8bb8\u5c06\u4ed6\u4eec\u4e3a\u96be\uff01\u201d \u3000\u3000\u5b5f\u5c11\u521a\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u5c60\u9f99\uff0c\u9633\u5929\u96f7\u662f\u4f60\u7684\u6740\u7236\u4ec7\u4eba\uff0c\u4f60\u5c31\u7518\u8ba9\u4f59\u4e00\u4e2d\u6446\u5e03\uff0c\u53cd\u800c\u5e2e\u5fd9\u4f60 \u7684\u6740\u7236\u4ec7\u4eba\u4e48\uff1f\u201d \u3000\u3000\u4f59\u4e00\u4e2d\u53eb\u9053\uff1a\u201c\u5c60\u516c\u5b50\uff0c\u4f60\u4fdd\u6211\u5e73\u5b89\u56de\u8f6c\u548c\u6797\uff0c\u6211\u4e5f\u4fdd\u4f60\u7ec8\u751f\u5bcc\u8d35\u4e0d\u5c3d\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u5c60\u9f99\uff0c\u4e00\u8bef\u4e0d\u80fd\u518d\u8bef\uff01\u53ea\u8981\u4f60\u628a\u516c\u4e3b\u7559\u4e0b\uff0c\u4ece\u4eca\u4e4b\u540e\uff0c\u6094\u6539\u524d\u975e\uff0c \u91cd\u56de\u6b63\u8def\uff0c\u6211\u4e5f\u62c5\u4fdd\u4fa0\u4e49\u9053\u4f1a\u628a\u4f60\u5f53\u4f5c\u81ea\u5df1\u4eba\u770b\u5f85\u3002\u201d \u3000\u3000\u4f59\u4e00\u4e2d\u51b7\u7b11\u9053\uff1a\u201c\u5c60\u9f99\uff0c\u4f60\u662f\u806a\u660e\u4eba\uff0c\u4f60\u53ef\u8981\u4ed4\u7ec6\u60f3\u60f3\u3002\u516c\u4e3b\u5982\u4eca\u662f\u5728\u4f60\u7684\u624b\u4e2d\uff0c\u4ed6\u4eec \u5f53\u7136\u662f\u4ec0\u4e48\u90fd\u53ef\u4ee5\u7b54\u5e94\u4f60\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u5927\u6012\u9053\uff1a\u201c\u4f60\u8fd9\u53cd\u590d\u5c0f\u4eba\uff0c\u65e0\u803b\u5978\u8d3c\uff0c\u4f60\u5f53\u522b\u4eba\u4e5f\u662f\u50cf\u4f60\u4e00\u6837\u4e48\uff1f\u95ed\u4e0a\u4f60\u7684\u4e4c\u9e26 \u5634\uff01\u201d \u3000\u3000\u4f59\u4e00\u4e2d\u9053\uff1a\u201c\u597d\uff0c\u6211\u4e0d\u8bf4\uff0c\u8ba9\u5c60\u9f99\u81ea\u5df1\u8bf4\uff01\u201d \u3000\u3000\u5c60\u9f99\u542c\u4e86\u4e24\u65b9\u7684\u8bdd\uff0c\u5fc3\u4e2d\u5584\u6076\u4ea4\u6218\uff0c\u4f46\u6bd5\u7adf\u8fd8\u662f\u4f59\u4e00\u4e2d\u7684\u8bdd\u8bf4\u5230\u4e86\u4ed6\u7684\u5fc3\u574e\u4e0a\u3002\u4ed6\u6697\u81ea \u601d\u91cf\uff1a\u201c\u674e\u601d\u5357\u6068\u6211\u4fae\u8fb1\u4ed6\u7684\u672a\u5a5a\u59bb\u5b50\uff0c\u5728\u7405\u739b\u5c71\u4e4b\u65f6\uff0c\u5c31\u66fe\u60f3\u6740\u6211\u4e86\u3002\u4ed6\u96be\u9053\u5f53\u771f\u80af\u9976\u6211 \u4e48\uff1f\u4ed6\u80af\u9976\u6211\uff0c\u6768\u5a49\u53c8\u80af\u9976\u6211\u4e48\uff1f\u6211\u505a\u51fa\u4e86\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u53c8\u6709\u4ec0\u4e48\u9762\u5b50\u518d\u56de\u7405\u739b\u5c71\uff1f\u4f55\u51b5\u56de\u5230 \u5c71\u5be8\u91cc\u4e5f\u8fd8\u4e0d\u662f\u5bc4\u4eba\u7bf1\u4e0b\uff1f\u201d \u3000\u3000\u5b5f\u5c11\u521a\u559d\u9053\uff1a\u201c\u5c60\u9f99\uff0c\u8fd9\u662f\u4f60\u6700\u540e\u7684\u673a\u4f1a\u4e86\uff0c\u4f60\u600e\u4e48\u8bf4\uff1f\u201d \u3000\u3000\u5c60\u9f99\u5fc3\u610f\u5df2\u51b3\uff0c\u8bf4\u9053\uff1a\u201c\u4e0e\u8499\u53e4\u548c\u597d\uff0c\u8fd9\u662f\u671d\u5ef7\u7684\u610f\u65e8\uff0c\u5c0f\u4f84\u8c28\u9075\u671d\u5ef7\u610f\u65e8\uff0c\u613f\u542c\u4f59\u5927 \u4eba\u7684\u5429\u5490\uff01\u201d \u3000\u3000\u5b5f\u5c11\u521a\u559d\u9053\uff1a\u201c\u6740\u7236\u4e4b\u4ec7\u4f60\u5c31\u4e0d\u62a5\u4e86\u5417\uff1f\u4f60\u8fd8\u662f\u4eba\u5417\uff1f\u201d \u3000\u3000\u5c60\u9f99\u8001\u7f9e\u6210\u6012\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u4e24\u4ef6\u4e8b\u60c5\uff0c\u4e0d\u5fc5\u626f\u5728\u4e00\u8d77\u3002\u5c0f\u4f84\u5982\u4eca\u53ea\u77e5\u8bc6\u65f6\u52a1\u8005\u4e3a\u4fca \u6770\uff0c\u81f3\u4e8e\u62a5\u4ec7\u4e0d\u62a5\u4ec7\uff0c\u90a3\u662f\u5c0f\u4f84\u4ee5\u540e\u7684\u4e8b\u60c5\uff0c\u8001\u4f2f\u5927\u53ef\u4e0d\u5fc5\u591a\u7ba1\u4e86\u3002\u201d \u3000\u3000\u5b5f\u5c11\u521a\u6c14\u5f97\u4e03\u7a8d\u751f\u70df\uff0c\u9a82\u9053\uff1a\u201c\u5c60\u767e\u57ce\u4e00\u4e16\u82f1\u540d\uff0c\u60f3\u4e0d\u5230\u7adf\u517b\u4e86\u4f60\u8fd9\u6837\u4e00\u4e2a\u4e0d\u8096\u4e4b\u5b50\uff01 \u770b\u5728\u4f60\u7239\u7239\u4f24\u4e0a\uff0c\u4eca\u65e5\u6211\u4e0d\u6740\u4f60\uff0c\u4f46\u591a\u884c\u4e0d\u4e49\u5fc5\u81ea\u6bd9\uff0c\u7ec8\u6709\u4e00\u65e5\u4f60\u4f1a\u906d\u5230\u62a5\u5e94\u7684\u3002\u4f60\u56de\u53bb\u4ed4 \u7ec6\u60f3\u6211\u8fd9\u53e5\u8bdd\u5427\u3002\u201d \u3000\u3000\u4f59\u4e00\u4e2d\u9053\uff1a\u201c\u95f2\u8bdd\u5c11\u8bf4\uff0c\u5982\u4eca\u5df2\u662f\u8a00\u660e\uff0c\u8fd9\u6869\u4ea4\u6613\uff0c\u4f60\u4eec\u662f\u505a\u4e5f\u4e0d\u505a\uff1f\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u53eb\u9053\uff1a\u201c\u674e\u516c\u5b50\uff0c\u4f60\u4e0d\u5fc5\u987e\u6211\uff0c\u53ea\u7ba1\u6740\u8fd9\u5978\u8d3c\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u5fc3\u4e71\u5982\u9ebb\uff0c\u8e0c\u8e87\u672a\u51b3\u3002\u4ed6\u7684\u7236\u4eb2\u53d7\u5c3d\u4f59\u4e00\u4e2d\u7684\u6298\u78e8\uff0c\u6b7b\u5728\u5f02\u56fd\uff0c\u8fd9\u6740\u7236\u4e4b\u4ec7\uff0c\u5c82 \u80fd\u4e0d\u62a5\uff1f\u4f46\u4ed6\u4e5f\u77e5\u9053\u660e\u6167\u516c\u4e3b\u82e5\u7ed9\u9001\u56de\u548c\u6797\uff0c\u4ee5\u5979\u7684\u6027\u683c\uff0c\u5018\u82e5\u5144\u957f\u903c\u5979\u5ac1\u7ed9\u9547\u56fd\u738b\u5b50\uff0c\u5979 \u975e\u81ea\u5c3d\u4e0d\u53ef\u3002\u800c\u9547\u56fd\u738b\u5b50\u624b\u63e1\u5175\u6743\uff0c\u7a9d\u9614\u53f0\u548c\u62d6\u96f7\u53c8\u662f\u52bf\u5fc5\u8981\u903c\u5979\u4e0b\u5ac1\uff0c\u674e\u601d\u5357\u6697\u81ea\u601d\u91cf\uff1a \u201c\u6211\u53d7\u8fc7\u660e\u6167\u516c\u4e3b\u7684\u5927\u6069\uff0c\u6b64\u6069\u53c8\u5c82\u80fd\u4e0d\u62a5\uff1f\u201d \u3000\u3000\u6768\u5a49\u4e5f\u662f\u4e00\u6837\u5fc3\u601d\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u5357\u54e5\uff0c\u6211\u4e0d\u6562\u529d\u4f60\u5e94\u627f\u4f59\u4e00\u4e2d\u7684\u6761\u4ef6\uff0c\u6211\u7684\u54e5\u54e5\u4e5f\u662f \u6b7b\u5728\u8fd9\u5978\u8d3c\u7684\u624b\u4e0a\u7684\u3002\u4e0d\u8fc7\u6211\u66fe\u53d7\u8fc7\u660e\u6167\u516c\u4e3b\u6551\u547d\u4e4b\u6069\uff0c\u5979\u56de\u53bb\u5373\u4f7f\u4e0d\u6b7b\uff0c\u8fd9\u4e00\u751f\u4e5f\u662f\u6beb\u65e0 \u4e50\u8da3\u7684\u4e86\uff0c\u53ea\u6015\u4e0d\u6b7b\u6bd4\u6b7b\u8fd8\u8981\u96be\u53d7\uff01\u6211\u53c8\u600e\u5fcd\u89c1\u5979\u5982\u6b64\uff0c\u8fd9\u600e\u4e48\u597d\u5462\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u54ac\u4e86\u54ac\u7259\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6211\u4eca\u65e5\u653e\u4e86\u4f59\u4e00\u4e2d\uff0c\u4ee5\u540e\u8fd8\u53ef\u4ee5\u62a5\u4ec7\uff0c\u660e\u6167\u516c\u4e3b\u7ed9\u9001\u56de \u53bb\uff0c\u4ee5\u540e\u53ef\u662f\u6ca1\u6709\u673a\u4f1a\u518d\u51fa\u7262\u7b3c\u4e86\u3002\u201d\u601d\u5ff5\u53ca\u6b64\uff0c\u5fc3\u610f\u7acb\u51b3\uff0c\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u4eca\u65e5\u6211\u548c\u4f60 \u505a\u8fd9\u6869\u4ea4\u6613\u3002\u65e5\u540e\u4f60\u78b0\u5728\u6211\u7684\u624b\u4e0a\uff0c\u6211\u53ef\u5c31\u4e0d\u80fd\u9976\u4f60\u4e86\uff01\u201d \u3000\u3000\u4f59\u4e00\u4e2d\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u54b1\u4eec\u53ea\u8bf4\u73b0\u5728\u7684\u4e8b\uff0c\u4ee5\u540e\u7684\u4e8b\u4ee5\u540e\u518d\u8bf4\u3002\u8001\u5b9e\u8bf4\uff0c\u4ed6\u65e5\u4f60\u82e5\u662f\u78b0\u5728 \u6211\u7684\u624b\u4e0a\uff0c\u6211\u4e5f\u662f\u540c\u6837\u7684\u4e0d\u80fd\u9976\u4f60\u7684\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u559d\u9053\uff1a\u201c\u5c60\u9f99\uff0c\u5feb\u628a\u516c\u4e3b\u653e\u4e0b\uff01\u201d \u3000\u3000\u5c60\u9f99\u9053\uff1a\u201c\u4f60\u8ba9\u4f59\u5927\u4eba\u5148\u8fc7\u6765\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u6012\u9053\uff1a\u201c\u5c82\u6709\u6b64\u7406\uff0c\u6211\u674e\u67d0\u8bf4\u4e00\u662f\u4e00\uff0c\u8bf4\u4e8c\u662f\u4e8c\uff0c\u5c82\u50cf\u4f60\u4eec\u8fd9\u6837\u7684\u5978\u8bc8\u5c0f\u4eba\uff1f\u201d \u3000\u3000\u5c60\u9f99\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u76f8\u4fe1\u6211\uff0c\u53c8\u53eb\u6211\u5982\u4f55\u76f8\u4fe1\u4f60\uff1f\u201d \u3000\u3000\u4f59\u4e00\u4e2d\u9053\uff1a\u201c\u4f60\u4eec\u522b\u5435\uff0c\u6211\u6709\u4e00\u4e2a\u529e\u6cd5\uff0c\u53cc\u65b9\u5230\u9662\u5916\u6362\u4eba\u3002\u5982\u4eca\u6211\u6682\u65f6\u7559\u5728\u4f60\u4eec\u8fd9\u8fb9\u505a \u4eba\u8d28\uff0c\u5f85\u5230\u5c60\u516c\u5b50\u653e\u516c\u4e3b\u7684\u65f6\u5019\uff0c\u4f60\u4eec\u540c\u65f6\u653e\u6211\uff0c\u8fd9\u6837\u4f60\u4eec\u8be5\u53ef\u4ee5\u653e\u5fc3\u4e86\u5427\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u597d\uff0c\u5c31\u8fd9\u4e48\u529e\uff01\u201d \u3000\u3000\u9633\u5929\u96f7\u9053\uff1a\u201c\u767d\u5e84\u4e3b\uff0c\u54b1\u4eec\u4e00\u540c\u8d70\u5427\uff0c\u8bf7\u4f60\u5907\u9a6c\u3002\u201d \u3000\u3000\u5b5f\u5c11\u521a\u54fc\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u4eca\u65e5\u4fbf\u5b9c\u4e86\u4f60\u4eec\u8fd9\u4e24\u4e2a\u8001\u8d3c\u4e86\u3002\u201d \u3000\u3000\u767d\u4e07\u96c4\u7b11\u9053\uff1a\u201c\u6211\u8fd9\u767d\u5bb6\u5e84\u5e73\u767d\u9001\u7ed9\u4f60\u4eec\uff0c\u8bf4\u8d77\u6765\u8fd9\u6869\u4ea4\u6613\u8fd8\u662f\u4f60\u4eec\u5360\u4e86\u4fbf\u5b9c\u5462\u3002\u201d\u5f53 \u4e0b\u53eb\u513f\u5b50\u5230\u9a6c\u53a9\u6311\u9009\u56db\u5339\u597d\u9a6c\uff0c\u4f59\u4e00\u4e2d\u8fd9\u624d\u653e\u4e0b\u4e86\u5fc3\uff0c\u4ece\u623f\u95f4\u8d70\u51fa\u6765\u3002 \u3000\u3000\u674e\u601d\u5357\u6263\u7740\u4ed6\u7684\u624b\u8155\uff0c\u559d\u9053\uff1a\u201c\u4e0d\u8bb8\u4e71\u52a8\uff01\u201d\u4f59\u4e00\u4e2d\u7b11\u9053\uff1a\u201c\u53cd\u6b63\u4f60\u662f\u8981\u653e\u6211\u7684\uff0c\u6211\u53c8 \u4f55\u5fc5\u5fd9\u5728\u4e00\u65f6\uff1f\u201d\u674e\u601d\u5357\u6068\u5f97\u7259\u75d2\u75d2\u7684\uff0c\u5374\u662f\u65e0\u5948\u5948\u4f55\u3002 \u3000\u3000\u5f53\u4e0b\u53cc\u65b9\u8d70\u51fa\u5e84\u5916\uff0c\u505c\u5728\u8def\u4e0a\u3002\u4e00\u8fb9\u662f\u767d\u4e07\u96c4\u7236\u5b50\u548c\u9633\u5929\u96f7\u3002\u5c60\u9f99\u56db\u4eba\u6267\u7740\u516c\u4e3b\u4f5c\u4e3a\u4eba \u8d28\uff0c\u4e00\u8fb9\u662f\u5b5f\u5c11\u521a\u3001\u674e\u601d\u5357\u3001\u6768\u5a49\u4e09\u4eba\u6267\u7740\u4f59\u4e00\u4e2d\u4f5c\u4e3a\u4eba\u8d28\uff0c\u4e24\u9635\u5bf9\u5706\uff0c\u5c60\u9f99\u53eb\u9053\uff1a\u201c\u53cc\u65b9 \u5404\u81ea\u9000\u540e\u767e\u6b65\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5c60\u9f99\u8bf4\u9053\uff1a\u201c\u6211\u6015\u4f60\u4eec\u6697\u7b97\uff0c\u4f60\u4eec\u4e5f\u672a\u5fc5\u4e0d\u6015\u6211\u4eec\u6697\u7b97\u3002\u53cc\u65b9\u5404\u9000\u767e\u6b65\u4e4b\u540e\uff0c\u6211\u6570\u5230\u4e00 \u4e2a\u2018\u4e09\u2019\u5b57\uff0c\u5927\u5bb6\u540c\u65f6\u653e\u4eba\u3002\u201d \u3000\u3000\u8981\u77e5\u8155\u529b\u518d\u5f3a\uff0c\u6697\u5668\u4e5f\u96be\u6253\u5230\u767e\u6b65\u5f00\u5916\uff0c\u5c60\u9f99\u5fc3\u60f3\u660e\u6167\u516c\u4e3b\u662f\u4e2a\u5973\u5b50\uff0c\u7eb5\u7136\u7cbe\u4e8e\u9a91\u5c04\uff0c \u8dd1\u8d77\u8def\u6765\uff0c\u672a\u5fc5\u5feb\u5f97\u8fc7\u4f59\u4e00\u4e2d\u3002\u5f00\u59cb\u653e\u4eba\u4e4b\u65f6\uff0c\u5728\u767e\u6b65\u8ddd\u79bb\u4e4b\u5185\uff0c\u5018\u6709\u4e00\u65b9\u6697\u7b97\uff0c\u5bf9\u65b9\u4e5f\u4f1a \u53d1\u6697\u5668\u7684\uff0c\u5f7c\u6b64\u6709\u6240\u987e\u5fcc\uff0c\u5f53\u7136\u662f\u4e0d\u6562\u7684\u4e86\u3002\u5f85\u5230\u4ea4\u6362\u7684\u4eba\u8d28\u8dd1\u8fc7\u4e2d\u95f4\u7684\u754c\u7ebf\uff0c\u90a3\u65f6\u5df2\u662f\u5728 \u767e\u6b65\u4e4b\u5916\u4e86\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b50\u771f\u662f\u6ee1\u8179\u673a\u5fc3\uff0c\u4e5f\u7f62\uff0c\u4f9d\u4f60\u5c31\u662f\uff01\u201d \u3000\u3000\u5f53\u4e0b\u53cc\u65b9\u9000\u540e\u767e\u6b65\u3002\u5c60\u9f99\u89e3\u5f00\u4e86\u660e\u6167\u516c\u4e3b\u7684\u675f\u7f1a\uff0c\u674e\u601d\u5357\u4e5f\u628a\u4f59\u4e00\u4e2d\u653e\u677e\uff0c\u5c60\u9f99\u7b11\u563b\u563b \u5730\u8bf4\u9053\uff1a\u201c\u53ef\u60dc\u6211\u6ca1\u798f\u6c14\u4f3a\u5019\u516c\u4e3b\uff0c\u4fbf\u5b9c\u4e86\u674e\u601d\u5357\u8fd9\u5c0f\u5b50\u4e86\u3002\u201d\u8ddf\u7740\u7f13\u7f13\u6570\u9053\uff1a\u201c\u4e00\u3001\u4e8c\u3001 \u4e09\uff01\u201d\u6570\u5230\u4e00\u4e2a\u201c\u4e09\u201d\u5b57\uff0c\u53cc\u65b9\u540c\u65f6\u628a\u4eba\u8d28\u63a8\u51fa\uff0c\u660e\u6167\u516c\u4e3b\u5411\u674e\u601d\u5357\u8fd9\u8fb9\u8dd1\u6765\uff0c\u4f59\u4e00\u4e2d\u66f4\u662f \u4f7f\u5c3d\u5403\u5976\u7684\u6c14\u529b\uff0c\u62fc\u547d\u98de\u5954\uff01 \u3000\u3000\u53cc\u65b9\u90fd\u5728\u6025\u901f\u7684\u8dd1\uff0c\u53ef\u662f\u5374\u53c8\u90fd\u5728\u8f6c\u7740\u5fc3\u601d\u3002 \u3000\u3000\u4f59\u4e00\u4e2d\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u6b21\u51fa\u6765\uff0c\u6211\u53ea\u4e0d\u8fc7\u66ff\u5927\u6c57\u6536\u4e70\u4e86\u4e00\u4e2a\u767d\u4e07\u96c4\uff0c\u5374\u53cd\u4e86\u7c98\u4e0d\u7f55\u548c\u901f \u4e0d\u53f0\uff0c\u5927\u6c57\u4e00\u5b9a\u602a\u6211\u529e\u4e8b\u4e0d\u529b\uff0c\u5018\u82e5\u7ed9\u4ed6\u77e5\u9053\u6211\u4e5f\u66fe\u843d\u5728\u654c\u4eba\u624b\u4e2d\uff0c\u662f\u7528\u660e\u6167\u516c\u4e3b\u628a\u6211\u6362\u56de \u6765\u7684\uff0c\u90a3\u65f6\u53ea\u6015\u6211\u66f4\u662f\u8981\u5403\u4e0d\u4e86\u515c\u7740\u8d70\u3002\u8981\u60f3\u514d\u7f6a\uff0c\u9664\u975e\u6211\u80fd\u591f\u628a\u516c\u4e3b\u6349\u4e86\u56de\u53bb\u3002\u201d \u3000\u3000\u60f3\u662f\u8fd9\u6837\u60f3\uff0c\u53ef\u662f\u4ed6\u4e00\u6765\u6015\u672a\u5fc5\u6253\u5f97\u8fc7\u660e\u6167\u516c\u4e3b\uff0c\u4e8c\u6765\u4e07\u4e00\u65f6\u95f4\u7a0d\u4e3a\u62d6\u957f\uff0c\u5b5f\u5c11\u521a\u3001\u674e \u601d\u5357\u4e00\u51fa\uff0c\u90a3\u65f6\u53ef\u5c31\u5f04\u5de7\u53cd\u7ecc\u4e86\u3002\u4e0d\u8fc7\uff0c\u5982\u679c\u80fd\u591f\u8fc5\u901f\u64d2\u4f4f\u516c\u4e3b\u7684\u8bdd\uff0c\u5c60\u9f99\u90a3\u8fb9\u6709\u73b0\u6210\u7684\u9a6c \u5339\uff0c\u7acb\u5373\u4e0a\u9a6c\u98de\u9003\uff0c\u5c31\u5927\u6709\u673a\u4f1a\u53ef\u4ee5\u8dd1\u5f97\u6389\u4e86\u3002 \u3000\u3000\u660e\u6167\u516c\u4e3b\u4e5f\u5728\u60f3\u9053\uff1a\u201c\u674e\u601d\u5357\u8d39\u5c3d\u5fc3\u529b\uff0c\u65b9\u80fd\u627e\u5230\u8fd9\u4e2a\u62a5\u4ec7\u7684\u673a\u4f1a\uff0c\u4e3a\u4e86\u6211\u7684\u7f18\u6545\uff0c\u653e \u4e86\u4ec7\u4eba\uff0c\u4ed6\u7eb5\u7136\u6beb\u65e0\u6028\u8a00\uff0c\u6211\u5374\u7109\u5f97\u5fc3\u5b89\uff1f\u201d \u3000\u3000\u53cc\u65b9\u5404\u62b1\u5fc3\u601d\uff0c\u8d8a\u8dd1\u8ddd\u79bb\u8d8a\u8fd1\uff0c\u5927\u5bb6\u7684\u901f\u5ea6\u90fd\u5dee\u4e0d\u591a\uff0c\u521a\u597d\u5728\u4e2d\u95f4\u754c\u7ebf\u78b0\u5934\u3002 \u3000\u3000\u4f59\u4e00\u4e2d\u5728\u8499\u53e4\u7684\u65f6\u5019\uff0c\u6bcf\u9022\u89c1\u5230\u660e\u6167\u516c\u4e3b\uff0c\u603b\u662f\u5c11\u4e0d\u514d\u884c\u53c2\u62dc\u4e4b\u793c\u7684\uff0c\u6b64\u65f6\u867d\u8bf4\u5404\u5728\u4e00 \u8fb9\uff0c\u4f46\u4e60\u60ef\u5df2\u6210\u81ea\u7136\uff0c\u78b0\u5934\u4e4b\u9645\uff0c\u4ed6\u89c1\u660e\u6167\u516c\u4e3b\u7684\u4e24\u9053\u9510\u5229\u7684\u76ee\u5149\u76ef\u7740\u4ed6\uff0c\u4e0d\u77e5\u4e0d\u89c9\u7684\u5c31\u62f1 \u624b\u8bf7\u4e86\u4e2a\u5b89\uff0c\u8bf4\u9053\uff1a\u201c\u516c\u4e3b\uff0c\u4f60\u597d\uff01\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u5fc3\u5ff5\u4e00\u52a8\uff0c\u4f4e\u58f0\u8bf4\u4e86\u51e0\u53e5\u8499\u53e4\u8bdd\u3002\u4e24\u65b9\u9762\u7684\u4eba\u90fd\u5728\u767e\u6b65\u5f00\u5916\uff0c\u660e\u6167\u516c\u4e3b\u538b\u4f4e\u4e86 \u58f0\u97f3\u8bf4\u7684\u8fd9\u51e0\u53e5\u8bdd\uff0c\u9664\u4e86\u4f59\u4e00\u4e2d\u4e4b\u5916\uff0c\u8c01\u90fd\u6ca1\u6709\u542c\u89c1\u3002\u4f46\u4f59\u4e00\u4e2d\u542c\u4e86\uff0c\u5374\u771f\u662f\u5982\u5949\u7eb6\u97f3\u60ca\u559c \u975e\u5e38\u3002 \u3000\u3000\u5979\u8bf4\u7684\u8fd9\u51e0\u53e5\u8bdd\uff1a\u201c\u4f59\u5927\u4eba\uff0c\u8bf7\u4f60\u628a\u6211\u62ff\u56de\u53bb\u3002\u5176\u5b9e\u6211\u5df2\u7ecf\u662f\u540e\u6094\u8fd9\u6b21\u51fa\u8d70\uff0c\u60f3\u56de\u548c\u6797 \u7684\u4e86\uff0c\u4e0d\u8fc7\uff0c\u6211\u4e0d\u613f\u610f\u53d7\u5c60\u9f99\u8fd9\u5c0f\u5b50\u631f\u6301\u3002\u8fd9\u529f\u52b3\u5c31\u9001\u7ed9\u4f60\u5427\uff01\u201d \u3000\u3000\u4fd7\u8bed\u8bf4\u5229\u4ee4\u667a\u660f\uff0c\u4f59\u4e00\u4e2d\u672c\u6765\u5c31\u60f3\u628a\u660e\u6167\u516c\u4e3b\u64d2\u56de\u53bb\u7684\uff0c\u53ea\u56e0\u987e\u5fcc\u5979\u7684\u6b66\u529f\uff0c\u65b9\u59cb\u8e0c\u8e87 \u672a\u51b3\u3002\u6b64\u65f6\u542c\u5f97\u5979\u81ea\u613f\u56de\u53bb\uff0c\u653e\u5fc3\u675f\u624b\u5c31\u64d2\uff0c\u628a\u8fd9\u529f\u52b3\u9001\u4e0e\u4ed6\uff0c\u4f59\u4e00\u4e2d\u81ea\u662f\u559c\u51fa\u671b\u5916\uff0c\u65e0\u6687 \u7ec6\u60f3\u4e86\u3002 \u3000\u3000\u673a\u4f1a\u7a0d\u7eb5\u5373\u901d\uff0c\u660e\u6167\u516c\u4e3b\u6b64\u65f6\u5373\u5c06\u4e0e\u4ed6\u64e6\u80a9\u800c\u8fc7\uff0c\u4f59\u4e00\u4e2d\u65e0\u6687\u7ec6\u60f3\uff0c\u7acb\u5373\u4e00\u6293\u5411\u5979\u6293 \u4e0b\u3002 \u3000\u3000\u5176\u5b9e\u50cf\u4ed6\u8fd9\u6837\u4e00\u4e2a\u8be1\u8ba1\u591a\u7aef\u7684\u4eba\uff0c\u5018\u82e5\u662f\u6709\u5145\u5206\u7684\u65f6\u95f4\u8ba9\u4ed6\u4ed4\u7ec6\u60f3\u60f3\uff0c\u4ed6\u662f\u4e0d\u5e94\u8be5\u4e0a\u660e \u6167\u516c\u4e3b\u8fd9\u4e2a\u5f53\u7684\u3002 \u3000\u3000\u539f\u6765\u660e\u6167\u516c\u4e3b\u662f\u8981\u5f15\u4ed6\u5148\u51fa\u624b\uff0c\u624d\u597d\u5c06\u4ed6\u62ff\u4e0b\uff0c\u5e2e\u5fd9\u674e\u601d\u5357\u633d\u56de\u8d25\u5c40\u7684\u3002\u8981\u77e5\u6b66\u6797\u4e2d\u4eba \u6700\u91cd\u8bfa\u8a00\uff0c\u674e\u601d\u5357\u5df2\u7ecf\u7b54\u5e94\u4e86\u8ba9\u4f59\u4e00\u4e2d\u56de\u53bb\uff0c\u6545\u6b64\u5fc5\u987b\u5f15\u4ed6\u5148\u884c\u51fa\u624b\uff0c\u7834\u574f\u4ea4\u6362\u7684\u534f\u5b9a\uff0c\u7136 \u540e\u5c06\u4ed6\u62ff\u4e0b\uff0c\u8fd9\u6837\uff0c\u7406\u4e8f\u7684\u624d\u4e0d\u662f\u674e\u601d\u5357\u8fd9\u8fb9\uff0c\u800c\u662f\u4f59\u4e00\u4e2d\u548e\u7531\u81ea\u53d6\u4e86\u3002 \u3000\u3000\u4f59\u4e00\u4e2d\u4e0d\u77e5\u662f\u8ba1\uff0c\u4e00\u6293\u6293\u4e0b\uff0c\u660e\u6167\u516c\u4e3b\u65e9\u6709\u51c6\u5907\uff0c\u7acb\u5373\u53cc\u81c2\u4e00\u5206\uff0c\u53cd\u6263\u4f59\u4e00\u4e2d\u7684\u624b\u8155\uff0c \u5495\u549a\u4e00\u58f0\uff0c\u5c06\u4f59\u4e00\u4e2d\u6454\u4e86\u4e00\u4e2a\u7b4b\u6597\uff01 \u3000\u3000\u4f59\u4e00\u4e2d\u5927\u60ca\u53eb\u9053\uff1a\u201c\u516c\u4e3b\u4f60\uff0c\u4f60\u4e0d\u662f\u8bf4\u2014\u2014\u201d\u660e\u6167\u516c\u4e3b\u5c82\u80fd\u5bb9\u4ed6\u8fa9\u8bba\uff0c\u4e00\u8db3\u4fbf\u8e0f\u4e0b\u53bb\uff0c \u8e0f\u7740\u4ed6\u7684\u80f8\u53e3\uff0c\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u8d3c\u5b50\u6562\u6b3a\u4fae\u6211\uff01\u201d\u6454\u89d2\u662f\u8499\u53e4\u4eba\u64c5\u957f\u7684\u6b66\u6280\uff0c\u660e\u6167\u516c\u4e3b\u867d\u7136\u4e0d \u662f\u4e2a\u4e2d\u9ad8\u624b\uff0c\u4e5f\u66fe\u7ec3\u8fc7\u3002\u5979\u51fa\u5176\u4e0d\u610f\u7684\u7528\u6765\u5bf9\u4ed8\u4f59\u4e00\u4e2d\uff0c\u4f59\u4e00\u4e2d\u7109\u80fd\u62b5\u5fa1\uff1f \u3000\u3000\u53ef\u60dc\u660e\u6167\u516c\u4e3b\u4e5f\u7c97\u5fc3\u4e00\u70b9\uff0c\u5979\u5236\u670d\u4f59\u4e00\u4e2d\u592a\u8fc7\u5bb9\u6613\uff0c\u5374\u5fd8\u8bb0\u4e86\u4f59\u4e00\u4e2d\u4e5f\u662f\u61c2\u5f97\u6b66\u529f\uff0c\u5e76 \u975e\u6ca1\u6709\u53cd\u51fb\u4e4b\u529b\u7684\u3002\u5728\u8fd9\u7d27\u8981\u5173\u5934\uff0c\u5979\u8fd8\u597d\u6574\u4ee5\u6687\u7684\u6307\u8d23\u4f59\u4e00\u4e2d\u7684\u7f6a\u72b6\uff0c\u5c31\u7ed9\u4e86\u4f59\u4e00\u4e2d\u4e00\u4e2a \u53cd\u51fb\u7684\u673a\u4f1a\u4e86\u3002 \u3000\u3000\u4f59\u4e00\u4e2d\u80f8\u53e3\u4e00\u633a\uff0c\u5fcd\u7740\u75bc\u75db\uff0c\u53cd\u62ff\u660e\u6167\u516c\u4e3b\u7684\u8db3\u8ddf\uff0c\u660e\u6167\u516c\u4e3b\u4e00\u811a\u8e22\u53bb\uff0c\u4f59\u4e00\u4e2d\u7740\u4e86\u4e00 \u811a\uff0c\u4f46\u5df2\u7ecf\u662f\u8df3\u8d77\u6765\u4e86\u3002 \u3000\u3000\u660e\u6167\u516c\u4e3b\u7684\u6454\u89d2\u6280\u672f\u4e0d\u8fc7\u76f8\u5f53\u4e8e\u7b2c\u4e09\u6d41\u7684\u8499\u53e4\u6b66\u58eb\uff0c\u4f59\u4e00\u4e2d\u6709\u4e86\u51c6\u5907\uff0c\u660e\u6167\u516c\u4e3b\u5c31\u4e0d\u90a3 \u4e48\u5bb9\u6613\u5236\u670d\u4ed6\u4e86\u3002\u660e\u6167\u516c\u4e3b\u4e00\u4e2a\u201c\u5708\u624b\u201d\u538b\u4ed6\u53cc\u81c2\uff0c\u4f59\u4e00\u4e2d\u4ee5\u7a7f\u638c\u63d2\u5165\uff0c\u53cd\u5288\u660e\u6167\u516c\u4e3b\u7684\u8098 \u5f2f\u5173\u8282\u3002 \u3000\u3000\u4f59\u4e00\u4e2d\u7a81\u7136\u548c\u660e\u6167\u516c\u4e3b\u52a8\u624b\uff0c\u5927\u51fa\u53cc\u65b9\u610f\u6599\u4e4b\u5916\u3002\u767d\u575a\u9633\u548c\u5c60\u9f99\u521a\u521a\u62e8\u8f6c\u9a6c\u5934\uff0c\u60f3\u6765\u62a2 \u4eba\uff0c\u8fd9\u4e00\u8fb9\u5b5f\u5c11\u521a\u548c\u674e\u601d\u5357\u5df2\u662f\u4e00\u540c\u8dc3\u51fa\u3002 \u3000\u3000\u5b5f\u5c11\u521a\u4eba\u672a\u5230\uff0c\u6697\u5668\u5148\u53d1\u3002\u4ed6\u7684\u201c\u6697\u5668\u201d\u4e0d\u8fc7\u662f\u968f\u624b\u4ece\u5730\u4e0a\u62fe\u8d77\u7684\u4e00\u9897\u77f3\u5b50\uff0c\u4f46\u7ecf\u4ed6\u7528 \u201c\u5f39\u6307\u795e\u901a\u201d\u7684\u529f\u592b\u6253\u51fa\uff0c\u5374\u4e0d\u4e9a\u4e8e\u4e00\u652f\u7ba9\u7bad\uff0c\u9633\u5929\u96f7\u8fde\u5fd9\u4f7f\u51fa\u201c\u5929\u96f7\u529f\u201d\uff0c\u547c\u7684\u4e00\u638c\u62cd \u53bb\uff0c\u90a3\u679a\u77f3\u5b50\u8f6c\u4e86\u4e00\u4e2a\u65b9\u5411\uff0c\u201c\u535c\u201d\u7684\u4e00\u58f0\uff0c\u6b63\u4e2d\u5c60\u9f99\u7684\u5750\u9a91\uff0c\u90a3\u5339\u9a8f\u9a6c\u767b\u65f6\u5012\u4e0b\u3002 \u3000\u3000\u5b5f\u5c11\u521a\u559d\u9053\uff1a\u201c\u597d\u5440\uff0c\u9633\u5929\u96f7\uff0c\u4f60\u662f\u4e0d\u662f\u8981\u548c\u6211\u91cd\u51b3\u96cc\u96c4\uff0c\u5206\u4e2a\u80dc\u8d1f\uff01\u201d \u3000\u3000\u9633\u5929\u96f7\u89c1\u5b5f\u5c11\u521a\u5df2\u7ecf\u8dc3\u51fa\uff0c\u54ea\u91cc\u8fd8\u6562\u604b\u6218\uff0c\u5fd9\u518d\u62e8\u8f6c\u9a6c\u5934\uff0c\u4e00\u6e9c\u70df\u5730\u8dd1\u4e86\u3002\u5c60\u9f99\u66f4\u662f\u5413 \u5f97\u9b42\u98de\u9b44\u6563\uff0c\u5e78\u800c\u4ed6\u4eec\u65e9\u5c31\u51c6\u5907\u4e86\u4e00\u5339\u5750\u9a91\uff0c\u8ddf\u7740\u9633\u5929\u96f7\u9003\u8d70\u3002 \u3000\u3000\u674e\u601d\u5357\u559d\u9053\uff1a\u201c\u4f59\u4e00\u4e2d\uff0c\u8fd9\u662f\u4f60\u81ea\u5bfb\u6b7b\u8def\uff0c\u6028\u4e0d\u5f97\u6211\u3002\u201d\u5530\u5730\u62d4\u5251\u51fa\u9798\uff0c\u4fbf\u8981\u6765\u53d6\u4ed6\u6027 \u547d\u3002 \u3000\u3000\u4f59\u4e00\u4e2d\u8fd8\u60f3\u6349\u4f4f\u660e\u6167\u516c\u4e3b\u4f5c\u4e3a\u4eba\u8d28\uff0c\u53ef\u662f\u4ed6\u89c1\u4e86\u674e\u601d\u5357\u660e\u6643\u6643\u7684\u5251\u5c16\uff0c\u5fc3\u80c6\u5df2\u5bd2\u3002\u660e\u6167 \u516c\u4e3b\u53cd\u624b\u4e00\u63a8\uff0c\u6273\u7740\u4ed6\u7684\u8eab\u5b50\uff0c\u9999\u80a9\u4e00\u8038\uff0c\u4f7f\u51fa\u6454\u89d2\u4e2d\u7684\u201c\u80a9\u8f66\u5f0f\u201d\uff0c\u628a\u4f59\u4e00\u4e2d\u6454\u51fa\u4e00\u4e08\u5f00 \u5916\u3002 \u3000\u3000\u4ed6\u4eec\u662f\u5728\u4e00\u6761\u501a\u5c71\u5efa\u7b51\u7684\u5c0f\u8def\u4e0a\u4ea4\u624b\u7684\uff0c\u4f59\u4e00\u4e2d\u6454\u5012\u4e4b\u5904\u6b63\u662f\u659c\u5761\uff0c\u4e00\u6454\u5012\u9aa8\u788c\u788c\u7684\u5c31 \u6eda\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u6070\u597d\u524d\u4e24\u5929\u4e0b\u8fc7\u51e0\u573a\u96e8\uff0c\u5c71\u6ce5\u677e\u6563\uff0c\u4f59\u4e00\u4e2d\u4ece\u659c\u5761\u4e0a\u6eda\u4e0b\u53bb\uff0c\u7acb\u5373\u5f15\u8d77\u5c71\u6ce5\u7684\u5012\u584c\uff01\u6ce5 \u571f\u50cf\u6d2a\u6c34\u822c\u5012\u6cfb\u4e0b\u6765\uff0c\u628a\u4f59\u4e00\u4e2d\u63a8\u4e0b\uff0c\u8f6c\u77ac\u95f4\u5c31\u6df9\u6ca1\u4e86\u4ed6\u7684\u5168\u8eab\u3002 \u3000\u3000\u53ef\u662f\u4f59\u4e00\u4e2d\u53c8\u4e0d\u80fd\u5373\u65f6\u65ad\u6c14\uff0c\u4ed6\u8fd8\u5728\u6323\u624e\uff0c\u8d8a\u6323\u624e\u8d8a\u662f\u96be\u53d7\uff0c\u597d\u4e0d\u5bb9\u6613\u4f38\u51fa\u5934\u6765\uff0c\u773c\u8033 \u53e3\u9f3b\u5168\u90e8\u585e\u6ee1\u4e86\u6ce5\u6c99\uff0c\u6323\u624e\u4e86\u51e0\u4e0b\uff0c\u53c8\u50cf\u4e00\u4e2a\u4e0d\u4f1a\u6e38\u6cf3\u7684\u4eba\u88ab\u6eba\u4f3c\u7684\uff0c\u6c89\u4e0b\u53bb\u3002 \u3000\u3000\u4f59\u4e00\u4e2d\u7684\u773c\u775b\u5df2\u7ecf\u7741\u4e0d\u5f00\u6765\uff0c\u7528\u5c3d\u4e86\u6700\u540e\u4e00\u70b9\u6c14\u529b\u6325\u52a8\u53cc\u624b\uff0c\u53eb\u9053\uff1a\u201c\u674e\u601d\u5357\uff0c\u6211\u6c42\u6c42 \u4f60\u8d76\u5feb\u6740\u4e86\u6211\u5427\uff01\u201d\u521a\u624d\u6768\u5a49\u5288\u95e8\u7684\u65f6\u5019\u4ed6\u8fd8\u53ea\u662f\u201c\u611f\u89c9\u201d\u5230\u88ab\u6d3b\u57cb\u7684\u6050\u60e7\uff0c\u5982\u4eca\u5219\u662f\u771f\u6b63 \u5c1d\u5230\u4e86\u88ab\u6d3b\u57cb\u7684\u6ecb\u5473\u4e86\u3002 \u3000\u3000\u674e\u601d\u5357\u53f9\u9053\uff1a\u201c\u81ea\u4f5c\u5b7d\uff0c\u4e0d\u53ef\u6d3b\u3002\u597d\uff0c\u6211\u5c31\u6210\u5168\u4f60\u5427\uff01\u201d\u5272\u4e0b\u4f59\u4e00\u4e2d\u7684\u9996\u7ea7\uff0c\u5411\u5929\u7980 \u544a\uff1a\u201c\u7239\u7239\uff0c\u5b69\u513f\u4eca\u65e5\u66ff\u4f60\u62a5\u4e86\u4ec7\uff01\u201d \u3000\u3000\u6768\u5a49\u628a\u660e\u6167\u516c\u4e3b\u6276\u4f4f\uff0c\u53ea\u89c1\u660e\u6167\u516c\u4e3b\u5439\u5f39\u5f97\u7834\u7684\u8138\u4e0a\u7ed9\u4f59\u4e00\u4e2d\u6293\u4f24\u4e86\u51e0\u5904\u3002\u6768\u5a49\u66ff\u5979\u6577 \u4e0a\u4e86\u91d1\u521b\u836f\uff0c\u7d27\u7d27\u62b1\u7740\u5979\u9053\uff1a\u201c\u516c\u4e3b\uff0c\u4f60\u4e3a\u6211\u53d7\u82e6\u4e86\u3002\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u5374\u662f\u9732\u51fa\u7b11\u5bb9\u8bf4\u9053\uff1a\u201c\u5a49\u59d0\uff0c\u6211\u4e0d\u662f\u7b54\u5e94\u8fc7\u4f60\u8981\u5e2e\u5fd9\u4f60\u62a5\u4ec7\u7684\u5417\uff1f\u6211\u7684\u7239\u7239\u4e0d \u80af\u6740\u4ed6\uff0c\u5982\u4eca\u603b\u7b97\u662f\u5b8c\u6210\u6211\u7684\u5fc3\u613f\u4e86\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u5f97\u62a5\u5927\u4ec7\uff0c\u81ea\u662f\u514d\u4e0d\u4e86\u4e5f\u8981\u6765\u5411\u660e\u6167\u516c\u4e3b\u9053\u8c22\uff0c\u4f46\u56e0\u6015\u6768\u5a49\u89c1\u7591\uff0c\u8868\u73b0\u51fa\u6765\u7684\u6001 \u5ea6\uff0c\u53cd\u800c\u4e0d\u5982\u6768\u5a49\u548c\u5979\u90a3\u6837\u7684\u4eb2\u70ed\u4e86\u3002 \u3000\u3000\u660e\u6167\u516c\u4e3b\u770b\u89c1\u4ed6\u4eec\u53cc\u53cc\u6765\u5230\u81ea\u5df1\u8ddf\u524d\u9053\u8c22\uff0c\u5fc3\u4e2d\u5374\u662f\u4e00\u7247\u832b\u7136\uff0c\u5341\u5206\u4f24\u611f\uff0c\u6697\u81ea\u60f3\u9053\uff1a \u201c\u6211\u5e2e\u5fd9\u4ed6\u4eec\u6740\u4e86\u4f59\u4e00\u4e2d\uff0c\u603b\u7b97\u662f\u4e86\u5374\u4e00\u4ef6\u5fc3\u613f\u3002\u4f46\u5982\u4eca\u6211\u5df2\u662f\u6709\u5bb6\u96be\u5f52\u6709\u56fd\u96be\u6295\u4e4b\u4eba\u4e86\uff0c \u4eca\u540e\u6211\u5c06\u4f55\u53bb\u4f55\u4ece\uff1f\u96be\u9053\u5c31\u8ddf\u4ed6\u4eec\u8fc7\u4e00\u8f88\u5b50\u5417\uff1f\u6768\u5a49\u7eb5\u7136\u4e0d\u8ba8\u538c\u6211\uff0c\u6211\u81ea\u5df1\u4e5f\u8981\u907f\u5acc\u554a\uff01\u4f55 \u51b5\u4ed6\u4eec\u5c0f\u592b\u59bb\u6069\u6069\u7231\u7231\uff0c\u6211\u4e00\u4e2a\u5916\u4eba\uff0c\u63d2\u5728\u4ed6\u4eec\u4e2d\u95f4\uff0c\u4e5f\u5b9e\u5728\u6ca1\u6709\u610f\u601d\u3002\u201d \u3000\u3000\u4e0d\u8fc7\u660e\u6167\u516c\u4e3b\u867d\u7136\u662f\u8fd9\u6837\u60f3\uff0c\u4f46\u4e00\u6765\u56e0\u4e3a\u65e0\u5904\u53ef\u4ee5\u5b89\u8eab\uff0c\u4e8c\u6765\u963f\u76d6\u548c\u5361\u6d1b\u4e1d\u4e5f\u8fd8\u5728\u7405\u739b \u5c71\u4e0a\uff0c\u5979\u5f53\u7136\u53ea\u597d\u548c\u674e\u601d\u5357\u3001\u6768\u5a49\u7b49\u4eba\u4e00\u540c\u56de\u53bb\u4e86\u3002 \u3000\u3000\u8fd9\u65e5\u56de\u5230\u7405\u739b\u5c71\uff0c\u4e0a\u5c71\u4e4b\u65f6\uff0c\u660e\u6167\u516c\u4e3b\u770b\u89c1\u4e1b\u6797\u4e2d\u9690\u9690\u73b0\u51fa\u4e00\u5ea7\u5e99\u5b87\uff0c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u95ee\u6768 \u5a49\u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\u5bfa\u89c2\uff1f\u4f4f\u7684\u662f\u5c3c\u59d1\u8fd8\u662f\u548c\u5c1a\uff1f\u201d \u3000\u3000\u6768\u5a49\u7b11\u9053\uff1a\u201c\u4e0d\u662f\u4f60\u8bf4\uff0c\u6211\u90fd\u6ca1\u6709\u7559\u610f\u5462\uff0c\u4f46\u4f60\u95ee\u8fd9\u4e2a\u5e72\u5417\uff1f\u201d \u3000\u3000\u5b5f\u5c11\u521a\u9053\uff1a\u201c\u4f60\u4e0d\u95ee\u6211\uff0c\u6211\u5012\u77e5\u9053\u3002\u672c\u6765\u662f\u548c\u5c1a\u5e99\u7684\uff0c\u73b0\u5728\u5df2\u53d8\u6210\u5c3c\u59d1\u5eb5\u4e86\u3002\u91cc\u9762\u4f4f\u7684 \u8fd9\u4f4d\u8001\u5c3c\u59d1\uff0c\u8fd8\u662f\u4e00\u4e2a\u5927\u6709\u6765\u5386\u7684\u4eba\u5462\u3002\u201d \u3000\u3000\u6768\u5a49\u597d\u5947\u5fc3\u8d77\uff0c\u8bf4\u9053\uff1a\u201c\u54e6\uff0c\u8fd9\u8001\u5c3c\u59d1\u662f\u4ec0\u4e48\u4eba\uff0c\u4f55\u4ee5\u5979\u5360\u4e86\u548c\u5c1a\u5e99\uff1f\u201d \u3000\u3000\u5b5f\u5c11\u521a\u9053\uff1a\u201c\u8fd9\u4e00\u5ea7\u836f\u738b\u5e99\uff0c\u672c\u6765\u662f\u4e00\u4f4d\u8001\u548c\u5c1a\u4e3b\u6301\u7684\u3002\u540e\u6765\u8fd9\u8001\u548c\u5c1a\u6b7b\u4e86\uff0c\u4ed6\u6ca1\u6709\u4f20 \u4e0b\u5f92\u5f1f\uff0c\u5c60\u767e\u57ce\u53c8\u5728\u8fd9\u91cc\u5f00\u8f9f\u4e86\u5c71\u5be8\uff0c\u5916\u95f4\u7684\u548c\u5c1a\u4e5f\u4e0d\u6562\u6765\u8fd9\u91cc\u505a\u4f5b\u4e8b\uff0c\u4e8e\u662f\u8fd9\u5ea7\u5e99\u5c31\u53d8\u6210 \u4e86\u65e0\u4eba\u4e3b\u6301\u7684\u8352\u5e99\u3002\u5927\u7ea6\u8fc7\u4e86\u5341\u5e74\uff0c\u65b9\u624d\u6765\u4e86\u4e00\u4e2a\u5c3c\u59d1\uff0c\u8fd9\u5c3c\u59d1\u6cd5\u540d\u4e86\u7f18\uff0c\u5e74\u8f7b\u65f6\u672c\u6765\u662f\u6c5f \u6e56\u4e0a\u4e00\u4e2a\u9887\u6709\u540d\u6c14\u7684\u4fa0\u5973\uff0c\u540e\u6765\u4e0d\u77e5\u4ec0\u4e48\u7f18\u6545\uff0c\u770b\u7834\u7ea2\u5c18\uff0c\u505a\u4e86\u5c3c\u59d1\u3002\u5979\u662f\u5c60\u592b\u4eba\u7684\u597d\u670b \u53cb\uff0c\u5979\u6765\u4e4b\u540e\uff0c\u5c60\u592b\u4eba\u8bf7\u5979\u7559\u4e0b\u6765\uff0c\u4e8e\u662f\u8fd9\u5ea7\u672c\u6765\u662f\u548c\u5c1a\u4e3b\u6301\u7684\u836f\u571f\u5e99\u5c31\u53d8\u6210\u4e86\u5c3c\u59d1\u5eb5 \u4e86\u3002\u201d \u3000\u3000\u522b\u4eba\u5f53\u4f5c\u662f\u542c\u6545\u4e8b\uff0c\u542c\u8fc7\u4e5f\u5c31\u7b97\u4e86\u3002\u53ea\u6709\u660e\u6167\u516c\u4e3b\u8bb0\u5728\u5fc3\u4e0a\u3002 \u3000\u3000\u56de\u5230\u5c71\u5be8\uff0c\u963f\u76d6\u592b\u59bb\u5f97\u89c1\u660e\u6167\u516c\u4e3b\uff0c\u559c\u51fa\u671b\u5916\uff0c\u5b5f\u660e\u971e\u548c\u5c60\u51e4\u770b\u89c1\u6768\u5a49\u548c\u674e\u601d\u5357\u4e00\u540c\u56de \u6765\uff0c\u4e5f\u662f\u6709\u8bf4\u4e0d\u51fa\u7684\u6b22\u559c\u3002\u8fd9\u4e00\u756a\u70ed\u95f9\u4e5f\u5c31\u4e0d\u5fc5\u7ec6\u8868\u4e86\u3002\u4f46\u660e\u6167\u516c\u4e3b\u5374\u4f3c\u85cf\u6709\u5fc3\u4e8b\uff0c\u5728\u4e00\u7247 \u6b22\u817e\u7684\u6c14\u6c1b\u4e2d\uff0c\u53ea\u6709\u5979\u672a\u9732\u7b11\u8138\u3002 \u3000\u3000\u7b2c\u4e8c\u5929\u660e\u6167\u516c\u4e3b\u5ffd\u7136\u524a\u4e86\u5934\u53d1\uff0c\u6765\u89c1\u5c60\u592b\u4eba\uff0c\u8bf7\u5c60\u592b\u4eba\u4ee3\u6c42\u4e86\u7f18\u5e08\u592a\u6536\u5979\u4e3a\u5f1f\u5b50\u3002 \u3000\u3000\u5c60\u592b\u4eba\u5927\u4e3a\u60ca\u8be7\uff0c\u6768\u5a49\u3001\u5c60\u51e4\u7b49\u4eba\u66f4\u662f\u5927\u611f\u610f\u5916\uff0c\u4f17\u4eba\u529d\u4e86\u53c8\u529d\uff0c\u4f46\u660e\u6167\u516c\u4e3b\u6267\u610f\u8981\u505a \u5c3c\u59d1\uff0c\u5c60\u592b\u4eba\u4e5f\u53ea\u597d\u6210\u5168\u5979\u7684\u5fc3\u613f\u3002\u5361\u6d1b\u4e1d\u4e0e\u5979\u60c5\u5982\u59d0\u59b9\uff0c\u867d\u7136\u6ca1\u6709\u8ddf\u5979\u524a\u53d1\uff0c\u4e5f\u642c\u5230\u836f\u738b \u5e99\u53bb\u966a\u5979\u3002\u81f3\u4e8e\u963f\u76d6\u5219\u4ecd\u7559\u5728\u5c71\u5be8\uff0c\u7b49\u5f85\u56de\u56fd\u7684\u673a\u4f1a\u3002 \u3000\u3000\u5b5f\u5c11\u521a\u56de\u6765\u4e4b\u540e\uff0c\u5c11\u4e0d\u4e86\u8981\u548c\u4f17\u4eba\u8c08\u8d77\u4ed6\u5728\u767d\u5bb6\u5e84\u78b0\u4e0a\u9633\u5929\u96f7\u7684\u4e8b\u60c5\u3002\u891a\u4e91\u5cf0\u3001\u8c37\u6db5\u865a \u4e8c\u4eba\u662f\u8d1f\u6709\u6e05\u7406\u5e08\u95e8\u7684\u8d23\u4efb\u7684\uff0c\u542c\u5f97\u7279\u522b\u7528\u5fc3\u3002 \u3000\u3000\u5b5f\u5c11\u521a\u8bb2\u4e86\u4ed6\u548c\u9633\u5929\u96f7\u4ea4\u624b\u7ecf\u8fc7\u4e4b\u540e\uff0c\u5bf9\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u4e91\u5cf0\uff0c\u4f60\u4e0d\u662f\u8981\u548c\u4f60\u7684\u8c37\u5e08\u5144\u4e00 \u540c\u53bb\u89c1\u4ee4\u5e08\u7684\u5417\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u6b63\u662f\u60f3\u8bf7\u793a\u76df\u4e3b\uff0c\u6211\u4eec\u53ef\u4e0d\u53ef\u4ee5\u6682\u65f6\u79bb\u5f00\u5c71\u5be8\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u73b0\u5728\u6211\u4eec\u5df2\u7ecf\u77e5\u9053\u4e86\u786e\u5b9e\u7684\u6d88\u606f\uff0c\u8499\u53e4\u7684\u65b0\u5927\u6c57\u5fd9\u4e8e\u6574\u987f\u5185\u90e8\uff0c\u4eca\u5e74\u4e4b\u5185 \u662f\u4e0d\u4f1a\u6765\u4fb5\u72af\u4e2d\u539f\u7684\u4e86\u3002\u4f60\u4eec\u4f46\u53bb\u65e0\u59a8\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u6211\u4eec\u51c6\u5907\u660e\u5929\u5c31\u52a8\u8eab\u4e86\u3002\u201d \u3000\u3000\u5b5f\u5c11\u521a\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u591a\u7559\u4e00\u5929\u597d\u5417\uff1f\u201d \u3000\u3000\u8c37\u6db5\u865a\u5fc3\u5ff5\u4e00\u52a8\uff0c\u8bf4\u9053\uff1a\u201c\u5b5f\u5927\u4fa0\u53ef\u662f\u770b\u51fa\u4e86\u9633\u5929\u96f7\u6b66\u529f\u7684\u7834\u7efd\uff1f\u8bf7\u4f60\u8001\u4eba\u5bb6\u4e0d\u541d\u6307 \u6559\u3002\u201d \u3000\u3000\u5b5f\u5c11\u521a\u9053\uff1a\u201c\u6307\u6559\u4e0d\u6562\uff0c\u8bf4\u8001\u5b9e\u8bdd\uff0c\u9633\u5929\u96f7\u7684\u2018\u5929\u96f7\u529f\u2019\u7684\u786e\u662f\u5389\u5bb3\u5f97\u5f88\uff0c\u6211\u4e5f\u6ca1\u6709\u529e \u6cd5\u7834\u4ed6\u3002\u4e0d\u8fc7\u542c\u8bf4\u2018\u5929\u96f7\u529f\u2019\u4e2d\u6709\u4e00\u62db\u53eb\u505a\u2018\u96f7\u7535\u4ea4\u8f70\u2019\uff0c\u4e24\u4eba\u5408\u4f7f\uff0c\u5a01\u529b\u6781\u5927\uff0c\u4f60\u4eec\u4f1a \u4e48\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u6211\u548c\u8c37\u5e08\u5144\u4f1a\u9762\u4e4b\u540e\uff0c\u66fe\u7ecf\u8bd5\u8fc7\u8fd9\u4e00\u62db\uff0c\u6253\u8d25\u4e86\u767d\u4e07\u96c4\u3002\u4f46\u5374\u4e0d\u77e5\u80fd\u5426\u62b5 \u654c\u9633\u5929\u96f7\uff1f\u201d \u3000\u3000\u5b5f\u5c11\u521a\u9053\uff1a\u201c\u4f60\u4eec\u8bd5\u7ed9\u6211\u770b\u770b\uff0c\u4e0d\u5fc5\u987e\u5fcc\uff0c\u5c3d\u7ba1\u5411\u6211\u6253\u6765\u3002\u201d \u3000\u3000\u891a\u3001\u8c37\u4e8c\u4eba\u540c\u65f6\u53d1\u638c\uff0c\u679c\u7136\u662f\u9690\u9690\u5e26\u6709\u98ce\u96f7\u4e4b\u58f0\uff0c\u65c1\u89c2\u7684\u674e\u601d\u5357\u7b49\u4eba\u90fd\u89c9\u5f97\u6709\u70b9\u7acb\u8db3\u4e0d \u7a33\u3002 \u3000\u3000\u4f46\u5b5f\u5c11\u521a\u53ea\u662f\u6643\u4e86\u4e00\u6643\uff0c\u891a\u4e91\u5cf0\u4e0e\u8c37\u6db5\u865a\u5404\u81ea\u9000\u4e86\u4e09\u6b65\u3002 \u3000\u3000\u674e\u601d\u5357\u662f\u66fe\u7ecf\u89c1\u8fc7\u5b5f\u5c11\u521a\u548c\u9633\u5929\u96f7\u4ea4\u624b\u7684\uff0c\u4e0d\u7981\u6697\u6697\u4e3a\u4ed6\u4eec\u62c5\u5fc3\uff0c\u56e0\u4e3a\u9633\u5929\u96f7\u4e0e\u5b5f\u5c11\u521a \u4ea4\u624b\u4e4b\u4f8b\uff0c\u6570\u5341\u62db\u4e4b\u5185\uff0c\u4ecd\u662f\u52bf\u5747\u529b\u654c\u7684\u3002 \u3000\u3000\u5b5f\u5c11\u521a\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u597d\uff01\u6211\u65e0\u6cd5\u7834\u5f97\u4ed6\u7684\u5929\u96f7\u529f\uff0c\u4f60\u4eec\u5374\u662f\u6709\u529e\u6cd5\u53ef\u4ee5\u7834\u7684\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u534a\u4fe1\u534a\u7591\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e00\u62db\u96f7\u7535\u4ea4\u8f70\u867d\u8bf4\u662f\u4e24\u4eba\u5408\u4f7f\uff0c\u5a01\u529b\u4f1a\u5927\u5f97\u591a\uff0c\u4f46\u9633\u5929\u96f7 \u6289\u6570\u5341\u5e74\u7684\u529f\u529b\uff0c\u6050\u6015\u8fd8\u662f\u8981\u80dc\u8fc7\u6211\u4eec\u4e24\u4eba\u5427\uff1f\u201d \u3000\u3000\u5b5f\u5c11\u521a\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8bba\u529f\u529b\u4f60\u4eec\u5f53\u7136\u662f\u6bd4\u4e0d\u4e0a\u4ed6\uff0c\u4f46\u53ea\u8981\u5584\u4e8e\u4f7f\u7528\u5185\u529b\uff0c\u8fd8\u662f\u6709\u514b\u5236\u4ed6 \u7684\u529e\u6cd5\u3002\u9633\u5929\u96f7\u7684\u638c\u529b\u867d\u731b\uff0c\u5374\u6709\u4e00\u4e2a\u7834\u7efd\uff0c\u521a\u67d4\u4e0d\u80fd\u517c\u6d4e\uff0c\u4e0d\u514d\u96be\u4ee5\u4e3a\u7ee7\uff0c\u5047\u5982\u78b0\u4e0a\u4e00\u4e2a \u61c2\u5f97\u4ed6\u8fd9\u4e00\u95e8\u5185\u529f\u5fc3\u6cd5\u7684\u4eba\uff0c\u7eb5\u7136\u529f\u529b\u7a0d\u5dee\uff0c\u53ea\u8981\u5b88\u5f97\u4f4f\uff0c\u6570\u5341\u62db\u4e4b\u540e\uff0c\u4fbf\u6709\u51fb\u8d25\u4ed6\u7684\u5e0c\u671b \u4e86\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u5c31\u53ea\u6015\u6211\u4eec\u7684\u529f\u529b\u548c\u4ed6\u76f8\u5dee\u592a\u8fdc\u3002\u201d\u6b63\u662f\uff1a \u3000\u3000\u5f3a\u5f31\u60ac\u6b8a\u4f55\u8db3\u754f\uff0c\u62fc\u5c06\u70ed\u8840\u62a5\u5e08\u95e8\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b\u2014\u2014\u7b2c\u56db\u5341\u516d\u56de\u3000\u5bc6\u5ba4\u5b9a\u8c0b\u5978\u8ba1\u9732\u3000\u534e\u5802\u95ef\u5e2d\u6740\u673a\u836b \u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b \u7b2c\u56db\u5341\u516d\u56de\u3000\u5bc6\u5ba4\u5b9a\u8c0b\u5978\u8ba1\u9732\u3000\u534e\u5802\u95ef\u5e2d\u6740\u673a\u836b \u3000\u3000\u5b5f\u5c11\u521a\u9053\uff1a\u201c\u96f7\u7535\u4ea4\u8f70\u8fd9\u4e00\u62db\u7684\u79d8\u5965\uff0c\u6211\u5982\u4eca\u867d\u4e0d\u6562\u8bf4\u662f\u5df2\u7ecf\u6e17\u900f\uff0c\u4e5f\u5df2\u61c2\u5f97\u4e00\u4e9b\u4e86\u3002 \u4e24\u4eba\u5408\u4f7f\uff0c\u7701\u529b\u5f97\u591a\u3002\u5047\u5982\u4f60\u4eec\u518d\u61c2\u5f97\u521a\u67d4\u517c\u6d4e\u7684\u8fd0\u529f\u65b9\u6cd5\uff0c\u90a3\u5c31\u66f4\u53ef\u4ee5\u6301\u4e45\u4e86\u3002 \u3000\u3000\u201c\u56e0\u4e3a\u8fd9\u4e00\u62db\u662f\u4f60\u4eec\u7684\u5e08\u7956\u7279\u5730\u521b\u51fa\u6765\u7ed9\u4e8c\u4eba\u5408\u4f7f\u7684\uff0c\u56e0\u6b64\u4f60\u4eec\u4e8c\u4eba\u8054\u624b\u7528\u8fd9\u4e00\u62db\u6765\u5e94 \u4ed8\u9633\u5929\u96f7\u4ee5\u5176\u4ed6\u62db\u6570\u53d1\u51fa\u7684\u5929\u96f7\u529f\uff0c\u5a01\u529b\u4e5f\u5c31\u4e0d\u81f3\u4e8e\u76f8\u5dee\u592a\u8fdc\u3002 \u3000\u3000\u201c\u6211\u8981\u4f60\u4eec\u591a\u7559\u4e00\u5929\uff0c\u5c31\u662f\u60f3\u8981\u4f60\u4eec\u5728\u4e00\u5929\u4e4b\u5185\uff0c\u5b66\u4f1a\u4e00\u95e8\u8fd0\u529f\u7684\u65b9\u6cd5\u3002\u201d \u3000\u3000\u891a\u3001\u8c37\u4e8c\u4eba\u5927\u559c\u8fc7\u671b\uff0c\u5f53\u4e0b\u5c31\u8ddf\u5b5f\u5c11\u521a\u5b66\u8fd9\u4e00\u95e8\u521a\u67d4\u517c\u6d4e\u7684\u8fd0\u529f\u65b9\u6cd5\u3002\u8fd9\u95e8\u65b9\u6cd5\u867d\u7136\u5965 \u5999\uff0c\u53e3\u8bc0\u5374\u751a\u7b80\u5355\uff0c\u4ed6\u4eec\u4e8c\u4eba\u6709\u4e0a\u4e58\u7684\u5185\u529f\u57fa\u7840\uff0c\u679c\u7136\u5728\u7b2c\u4e8c\u5929\u5c31\u5b66\u4f1a\u4e86\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u4eec\u662f\u8981\u56de\u53bb\u62dc\u89c1\u672c\u95e8\u5c0a\u957f\uff0c\u5546\u91cf\u5982\u4f55\u6e05\u7406\u95e8\u6237\u7684\uff0c\u56e0\u6b64\u81ea\u662f\u4e0d\u4fbf\u643a\u5e26\u5b5f\u660e\u971e\u548c\u4e25 \u70f7\u540c\u884c\u3002\u4e25\u70f7\u6570\u8f7d\u76f8\u601d\uff0c\u597d\u4e0d\u5bb9\u6613\u624d\u89c1\u7740\u4e86\u8c37\u6db5\u865a\uff0c\u76f8\u5904\u4e0d\u5230\u4e00\u6708\uff0c\u53c8\u8981\u5206\u624b\uff0c\u96be\u514d\u6709\u4f9d\u4f9d \u60dc\u522b\u4e4b\u60c5\u3002\u4f46\u597d\u5728\u8fd9\u53ea\u662f\u5c0f\u522b\uff0c\u540e\u4f1a\u6709\u671f\uff0c\u7eb5\u6709\u60dc\u522b\u4e4b\u60c5\uff0c\u4e5f\u4e0d\u81f3\u4e8e\u50cf\u4ece\u524d\u90a3\u6837\u96be\u8fc7\u3002 \u3000\u3000\u5e08\u5144\u5f1f\u8054\u8882\u540c\u884c\uff0c\u4e00\u4e2a\u6765\u81ea\u6c5f\u5357\uff0c\u4e00\u4e2a\u4e45\u5c45\u5317\u5730\uff0c\u4e24\u4eba\u4ea4\u8c08\u5357\u5317\u4e24\u5730\u7684\u6b66\u6797\u60c5\u51b5\uff0c\u4e00\u8def \u4e0a\u5012\u662f\u4e0d\u611f\u5bc2\u5bde\u3002 \u3000\u3000\u8fd9\u4e00\u65e5\u5230\u4e86\u7b26\u79bb\u96c6\uff0c\u8fd9\u662f\u4e00\u4e2a\u4e0d\u5927\u4e0d\u5c0f\u7684\u5e02\u9547\u3002\u4f46\u4ed6\u4eec\u8e0f\u8fdb\u4e86\u8fd9\u4e2a\u5e02\u96c6\u4e4b\u540e\uff0c\u5374\u53d1\u73b0\u6709 \u8bb8\u591a\u4f69\u6234\u7740\u6b66\u5668\u7684\u7c97\u8c6a\u6c49\u5b50\u5728\u4e70\u4e1c\u897f\uff0c\u8fd9\u4e9b\u4eba\u4e00\u770b\u5c31\u77e5\u662f\u6c5f\u6e56\u4eba\u7269\u3002 \u3000\u3000\u672c\u6765\u4ed6\u4eec\u5728\u8def\u4e0a\u7684\u65f6\u5019\uff0c\u5df2\u7ecf\u78b0\u89c1\u8fc7\u597d\u51e0\u4e2a\u8fd9\u6837\u7684\u4eba\u7269\u4e86\uff0c\u4f46\u5374\u6ca1\u6709\u50cf\u7b26\u79bb\u96c6\u4e4b\u591a\u5f97\u4ee4 \u4eba\u6ce8\u76ee\u3002\u4ed6\u4eec\u4e8c\u4eba\u90fd\u662f\u6c5f\u6e56\u4e0a\u7684\u5927\u884c\u5bb6\uff0c\u53ef\u4ee5\u731c\u60f3\u5f97\u5230\uff0c\u8fd9\u8bb8\u591a\u6c5f\u6e56\u4eba\u7269\u7a81\u7136\u5728\u4e00\u4e2a\u5c0f\u5e02\u9547 \u51fa\u73b0\uff0c\u4e00\u5b9a\u662f\u6709\u5f88\u4e0d\u5bfb\u5e38\u7684\u539f\u56e0\u3002 \u3000\u3000\u5176\u65f6\u5929\u8272\u5df2\u665a\uff1b\u8c37\u6db5\u865a\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u4e0e\u8fd9\u4e9b\u4eba\u4e95\u6c34\u4e0d\u72af\u6cb3\u6c34\uff0c\u7528\u4e0d\u7740\u907f\u5f00\u4ed6\u4eec\u3002\u201d\u4e8e\u662f \u4e24\u4eba\u4fbf\u53bb\u627e\u5ba2\u5e97\u6295\u5bbf\u3002\u5728\u627e\u5ba2\u5e97\u7684\u65f6\u5019\uff0c\u891a\u4e91\u5cf0\u6697\u81ea\u7559\u5fc3\uff0c\u53ea\u89c1\u90a3\u4e9b\u4eba\u4ece\u5e97\u5b50\u91cc\u51fa\u6765\uff0c\u624b\u4e0a \u90fd\u6367\u7740\u4e00\u4e2a\u62dc\u5323\u3002 \u3000\u3000\u597d\u4e0d\u5bb9\u6613\u624d\u627e\u5230\u4e00\u95f4\u5ba2\u5e97\uff0c\u5ba2\u5e97\u91cc\u4e5f\u6709\u51e0\u4e2a\u8fd9\u6837\u7684\u4f4f\u5ba2\uff0c\u4ed6\u4eec\u770b\u89c1\u891a\u3001\u8c37\u4e8c\u4eba\u6ca1\u6709\u643a\u5e26 \u62dc\u5323\uff0c\u90fd\u597d\u50cf\u6709\u70b9\u8be7\u5f02\u7684\u795e\u6c14\uff0c\u4f46\u4e5f\u6ca1\u6709\u5411\u4ed6\u4eec\u52a8\u95ee\u3002 \u3000\u3000\u665a\u996d\u4e4b\u540e\uff0c\u891a\u4e91\u5cf0\u8d70\u51fa\u5927\u5802\uff0c\u6709\u4e24\u4e2a\u4eba\u6b63\u5728\u627e\u638c\u67dc\u7684\u7ed9\u4ed6\u4eec\u5199\u62dc\u5e16\u3002 \u3000\u3000\u4e00\u4e2a\u8bf4\u9053\uff1a\u201c\u77f3\u5927\u54e5\uff0c\u4f60\u9884\u5907\u4e86\u4ec0\u4e48\u8d3a\u793c\uff1f\u201d \u3000\u3000\u90a3\u4eba\u7b11\u9053\uff1a\u201c\u5c0f\u610f\u601d\uff0c\u4e0d\u8fc7\u662f\u591c\u660e\u73e0\u4e24\u9897\uff0c\u4f60\u5462\uff1f\u201d \u3000\u3000\u524d\u9762\u90a3\u4eba\u8bf4\u9053\uff1a\u201c\u6211\u7684\u662f\u4e00\u5934\u7389\u72ee\u5b50\uff0c\u6ca1\u6709\u4f60\u7684\u793c\u7269\u5b9d\u8d35\uff0c\u4e0d\u8fc7\u5f20\u7f57\u8fd9\u4ef6\u793c\u7269\u5374\u4e5f\u82b1\u4e86 \u6211\u4e00\u70b9\u5fc3\u601d\u3002\u201d \u3000\u3000\u53e6\u4e00\u4e2a\u4eba\u8bf4\u9053\uff1a\u201c\u53f2\u8001\u82f1\u96c4\u51b3\u4e0d\u4f1a\u5728\u4e4e\u54b1\u4eec\u7684\u793c\u7269\u7684\uff0c\u53ea\u8981\u54b1\u4eec\u793c\u6570\u5230\u4e86\uff0c\u4ed6\u8001\u4eba\u5bb6\u5c31 \u4f1a\u6b22\u559c\u3002\u201d \u3000\u3000\u4ed6\u7684\u670b\u53cb\u8bf4\u9053\uff1a\u201c\u4f60\u8fd9\u8bdd\u8bf4\u5f97\u5f53\u7136\u4e0d\u9519\uff0c\u4e0d\u8fc7\u54b1\u4eec\u4e5f\u8be5\u7565\u8868\u4e00\u70b9\u656c\u610f\u3002\u201d\u6388\u7740\u53c8\u9053\uff0c \u201c\u53ef\u60dc\u6211\u8ba4\u4e0d\u5f97\u5b57\uff0c\u638c\u67dc\u7684\uff0c\u4f60\u7ed9\u6211\u5199\u5f97\u606d\u656c\u4e00\u4e9b\u3002\u201d \u3000\u3000\u638c\u67dc\u7684\u7b11\u9053\uff1a\u201c\u5c0f\u4eba\u7406\u4f1a\u5f97\u3002\u6211\u4eec\u8fd9\u4e2a\u5c0f\u5730\u65b9\u6709\u53f2\u8001\u82f1\u96c4\u8fd9\u6837\u4e00\u4f4d\u4eba\u7269\uff0c\u6211\u4eec\u4e5f\u90fd\u662f\u5f15 \u4ee5\u4e3a\u8363\u5462\uff01\u201d \u3000\u3000\u891a\u4e91\u5cf0\u542c\u5f97\u201c\u53f2\u8001\u82f1\u96c4\u201d\u56db\u4e2a\u5b57\uff0c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u5f85\u90a3\u4e24\u4e2a\u4eba\u62ff\u4e86\u5199\u597d\u7684\u62dc\u5e16\u56de\u623f\u4e4b\u540e\uff0c\u4fbf \u8d70\u4e0a\u524d\u548c\u90a3\u638c\u67dc\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u53f2\u8001\u82f1\u96c4\u7684\u5927\u540d\u53ef\u662f\u2018\u7528\u5a01\u2019\u4e8c\u5b57\uff1f\u201d \u3000\u3000\u638c\u67dc\u7684\u671b\u4e86\u4ed6\u4e00\u773c\uff0c\u597d\u50cf\u6709\u70b9\u8be7\u5f02\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5ba2\u5b98\u4f60\u662f\u4e0d\u662f\u7ed9\u4ed6\u8001\u4eba\u5bb6\u8d3a\u6625\u6765 \u7684\uff1f\u201d\u5fc3\u60f3\uff1a\u201c\u8fd9\u4eba\u4e5f\u771f\u7cca\u6d82\uff0c\u8fde\u5bff\u661f\u7684\u540d\u5b57\u90fd\u8981\u5411\u4eba\u6253\u542c\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u53f2\u8001\u82f1\u96c4\u505a\u5927\u5bff\u5440\uff0c\u6211\u4eec\u5012\u662f\u6070\u5de7\u78b0\u4e0a\u4e86\u3002\u5b9e\u4e0d\u76f8\u7792\uff0c\u53f2\u8001\u82f1\u96c4 \u7684\u5927\u540d\u6211\u4eec\u662f\u4e45\u4ef0\u7684\u4e86\uff0c\u5374\u4e00\u76f4\u65e0\u7f18\u8bc6\u8346\uff0c\u521a\u624d\u4f60\u4eec\u8bf4\u8d77\u53f2\u8001\u82f1\u96c4\uff0c\u6211\u731c\u60f3\u51c6\u662f\u4ed6\u8001\u4eba\u5bb6\uff0c \u662f\u4ee5\u4e00\u95ee\u3002\u201d \u3000\u3000\u638c\u67dc\u7684\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\u3002\u53f2\u8001\u82f1\u96c4\u4eba\u79f0\u8d5b\u5b5f\u5c1d\uff0c\u6bcf\u5e74\u6155\u540d\u53bb\u89c1\u4ed6\u8001\u4eba\u5bb6\u7684\u4e0d\u77e5\u591a\u5c11\u3002\u660e \u5929\u662f\u4ed6\u516d\u5341\u5c81\u5bff\u8fb0\uff0c\u4f60\u4eec\u78b0\u4e0a\u4e86\u6b63\u662f\u4e00\u4e2a\u597d\u673a\u4f1a\u5462\uff01\u201d\u4ed6\u89c1\u891a\u4e91\u5cf0\u8bf4\u8bdd\u4e4b\u4e2d\u9887\u6709\u60f3\u53bb\u62dc\u89c1\u53f2 \u7528\u5a01\u4e4b\u610f\uff0c\u6545\u6b64\u51fa\u8a00\u6307\u70b9\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u6709\u8fd9\u673a\u4f1a\u8ba9\u6211\u4eec\u53ef\u4ee5\u62dc\u89c1\u4ed6\u8001\u4eba\u5bb6\uff0c\u8fd9\u6b63\u662f\u6c42\u4e4b\u4e0d\u5f97\u7684\u4e8b\uff0c\u4f46\u53ef\u60dc\u6211\u4eec\u6ca1 \u6709\u51c6\u5907\u62dc\u5e16\u3002\u201d \u3000\u3000\u638c\u67dc\u7684\u9053\uff1a\u201c\u6211\u8fd9\u91cc\u6709\u73b0\u6210\u7684\u3002\u201d\u891a\u4e91\u5cf0\u559c\u9053\uff1a\u201c\u90a3\u5c31\u8bf7\u4f60\u7ed9\u6211\u4eec\u5199\u4e24\u4efd\u5427\u3002\u8fd9\u952d\u94f6\u5b50 \u804a\u4f5c\u4ee3\u7b14\u4e4b\u8d44\uff0c\u8bf7\u4f60\u6536\u4e0b\u3002\u201d \u3000\u3000\u8fd9\u4e2a\u638c\u67dc\u662f\u975e\u5e38\u5c0a\u656c\u53f2\u7528\u5a01\u7684\uff0c\u522b\u4eba\u82e5\u662f\u548c\u4ed6\u4e00\u6837\u5c0a\u656c\u4ed6\u5fc3\u76ee\u4e2d\u7684\u82f1\u96c4\uff0c\u4ed6\u5c31\u597d\u50cf\u9762\u4e0a \u8d34\u4e86\u91d1\u4f3c\u7684\uff0c\u89c9\u5f97\u5341\u5206\u5149\u5f69\u3002\u662f\u4ee5\u4ed6\u624d\u4f1a\u6002\u607f\u891a\u4e91\u5cf0\u524d\u53bb\u62dc\u5bff\uff0c\u5982\u4eca\u891a\u4e91\u5cf0\u4e0d\u4f46\u5982\u4ed6\u6240\u6599\uff0c \u8fd8\u7ed9\u4ed6\u4e00\u952d\u94f6\u5b50\u4f5c\u7b14\u916c\uff0c\u4ed6\u5f53\u7136\u662f\u5e94\u627f\u4e0d\u8fed\u4e86\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u62a5\u4e86\u4ed6\u548c\u8c37\u6db5\u865a\u7684\u540d\u5b57\u4e4b\u540e\uff0c\u8bf4\u9053\uff1a\u201c\u5f85\u4f1a\u513f\u6211\u5230\u4f60\u8fd9\u513f\u62ff\u62dc\u5e16\u3002\u201d\u4ea4\u4ee3\u6e05\u695a\uff0c \u4fbf\u5373\u56de\u623f\u3002 \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u891a\u5144\uff0c\u4f60\u6253\u542c\u5230\u4e86\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u7b11\u9053\uff1a\u201c\u8fd9\u91cc\u6709\u4f4d\u6b66\u6797\u524d\u8f88\u660e\u5929\u505a\u516d\u5341\u5927\u5bff\uff0c\u6211\u5df2\u7ecf\u7ed9\u4f60\u9884\u5907\u4e86\u4e00\u4efd\u62dc\u5e16\uff0c\u660e\u5929 \u54b1\u4eec\u4e00\u540c\u53bb\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u662f\u4ec0\u4e48\u7684\u5927\u4eba\u7269\uff1f\u54b1\u4eec\u6709\u5927\u4e8b\u5728\u8eab\uff0c\u4f55\u5fc5\u51d1\u8fd9\u70ed\u95f9\uff1f\u6211\u770b\u96c7\u4eba\u628a\u62dc\u5e16\u9001\u53bb \u4e5f\u884c\u4e86\u5427\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u7b11\u9053\uff1a\u201c\u8fd9\u4e5f\u662f\u4e00\u4ef6\u7d27\u8981\u7684\u4e8b\u5462\u3002\u672c\u6765\u6211\u53ef\u4ee5\u4e0d\u4f5c\u8fd9\u5e94\u916c\u7684\uff0c\u4f46\u65e2\u7136\u9047\u4e0a\u4e86\uff0c\u5012 \u662f\u60f3\u987a\u4fbf\u6599\u7406\u6599\u7406\u8fd9\u4ef6\u4e8b\u4e86\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u7a76\u7adf\u662f\u600e\u4e48\u4e00\u56de\u4e8b\u60c5\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u8fd9\u4f4d\u53f2\u8001\u82f1\u96c4\u7684\u8eab\u4efd\u8868\u9762\u4e0a\u662f\u4e2a\u9887\u6709\u5bb6\u4e1a\u7684\u6b66\u5e08\uff0c\u65f6\u5e38\u66ff\u4eba\u6392\u96be\u89e3\u7eb7\uff0c\u548c \u5b98\u5e9c\u4e5f\u6709\u6765\u5f80\uff0c\u4f46\u6697\u5730\u91cc\u5374\u662f\u548c\u6297\u91d1\u7684\u4e49\u58eb\u5bc6\u901a\u6d88\u606f\uff0c\u4e5f\u5e2e\u8fc7\u4e49\u519b\u7684\u5fd9\u3002 \u3000\u3000\u201c\u53f2\u7528\u5a01\u884c\u4e8b\u6781\u4e3a\u8c28\u614e\uff0c\u4f46\u65e5\u5b50\u4e45\u4e86\uff0c\u4e5f\u96be\u514d\u6cc4\u9732\u4e00\u70b9\u98ce\u58f0\u3002\u4e0d\u8fc7\u56e0\u4e3a\u4ed6\u5728\u5730\u65b9\u4e0a\u751a\u6709 \u5a01\u671b\uff0c\u91d1\u864f\u5728\u672a\u66fe\u62ff\u5230\u786e\u5207\u7684\u8bc1\u636e\u4e4b\u524d\uff0c\u5374\u662f\u4e0d\u6562\u9c81\u83bd\u3002 \u3000\u3000\u201c\u4f60\u77e5\u9053\u6211\u662f\u66fe\u5949\u4e86\u5e05\u7236\u4e4b\u547d\uff0c\u5047\u88c5\u987a\u4ece\u5e08\u4f2f\uff0c\u5728\u4ed6\u624b\u4e0b\u505a\u8fc7\u51e0\u5e74\u4e8b\u7684\u3002\u9633\u5929\u96f7\u5bf9\u6211\u5e76 \u4e0d\u5341\u5206\u4fe1\u4efb\uff0c\u4e0d\u8fc7\uff0c\u4e00\u4e9b\u4ed6\u8ba4\u4e3a\u4e0d\u662f\u592a\u91cd\u8981\u7684\u673a\u5bc6\uff0c\u6709\u65f6\u4e5f\u4f1a\u8ba9\u6211\u77e5\u9053\u3002 \u3000\u3000\u201c\u4e00\u5929\uff0c\u6709\u4e00\u4e2a\u6211\u4e0d\u77e5\u9053\u662f\u4ec0\u4e48\u6765\u5386\u7684\u4eba\u6765\u89c1\u9633\u5929\u96f7\uff0c\u4ed6\u4eec\u8bf4\u8bdd\u4e4b\u9645\uff0c\u6070\u5de7\u6211\u6709\u4e8b\u8981\u5411 \u9633\u5929\u96f7\u2018\u8bf7\u793a\u2019\uff0c\u95ef\u4e86\u8fdb\u53bb\uff0c\u542c\u5230\u4e86\u4ed6\u4eec\u540e\u534a\u6bb5\u7684\u8c08\u8bdd\u3002 \u3000\u3000\u201c\u4ed6\u4eec\u8c08\u7684\u6b63\u662f\u5173\u4e8e\u53f2\u7528\u5a01\u7684\u4e8b\u60c5\u3002 \u3000\u3000\u201c\u90a3\u4e2a\u964c\u751f\u4eba\u63d0\u8bae\u6d3e\u4e00\u4e2a\u4eba\u5230\u53f2\u5bb6\u5367\u5e95\uff0c\u9633\u5929\u96f7\u8bf4\u53f2\u7528\u5a01\u4e00\u751f\u8c28\u614e\uff0c\u5c82\u80fd\u5bb9\u5f97\u5978\u7ec6\u6df7\u5165 \u4ed6\u7684\u5bb6\u4e2d\u3002\u800c\u4e14\u6d3e\u53bb\u7684\u8fd9\u4e2a\u4eba\u5018\u82e5\u4e0d\u80fd\u548c\u4ed6\u63a5\u8fd1\uff0c\u53ea\u662f\u505a\u4e9b\u4ec6\u5f79\u4e4b\u7c7b\u7684\u4e8b\u60c5\u7684\u8bdd\uff0c\u4ea6\u662f\u6d3e\u4e0d \u4e0a\u7528\u573a\uff0c\u65e0\u6d4e\u4e8e\u4e8b\u3002 \u3000\u3000\u201c\u90a3\u4eba\u8bf4\u4ed6\u6709\u529e\u6cd5\u4f7f\u5f97\u53f2\u7528\u5a01\u6536\u8fd9\u4e2a\u4eba\u505a\u5f92\u5f1f\uff0c\u800c\u4e14\u62c5\u4fdd\u53f2\u7528\u5a01\u4f1a\u4fe1\u4efb\u8fd9\u4e2a\u4eba\u3002 \u3000\u3000\u201c\u9633\u5929\u96f7\u5927\u8d5e\u5999\u8ba1\uff0c\u6211\u8fdb\u53bb\u7684\u65f6\u5019\uff0c\u6b63\u597d\u542c\u5f97\u4ed6\u8bf4\u9053\uff1a\u2018\u771f\u6709\u8fd9\u6837\u4e00\u4e2a\u4eba\u53ef\u4ee5\u5230\u53f2\u5bb6\u5367 \u5e95\uff0c\u90a3\u4e48\u5c31\u7b97\u627e\u5230\u4e86\u53f2\u7528\u5a01\u53db\u9006\u671d\u5ef7\u7684\u8bc1\u636e\uff0c\u4e5f\u4e0d\u5fc5\u6025\u4e8e\u6293\u4ed6\u4e86\uff01\u54b1\u4eec\u8981\u653e\u957f\u7ebf\uff0c\u9493\u5927 \u9c7c\uff01\u2019 \u3000\u3000\u201c\u53ef\u60dc\u6211\u53ea\u662f\u542c\u5f97\u4ed6\u4eec\u7684\u4e00\u5c0f\u6bb5\u8bf4\u8bdd\uff0c\u5374\u4e0d\u77e5\u90a3\u4e2a\u4eba\u7684\u540d\u5b57\u3002\u6211\u5411\u9633\u5929\u96f7\u2018\u8bf7\u793a\u2019\u4e4b \u540e\uff0c\u4e0d\u6562\u4e45\u7559\uff0c\u4e5f\u4e0d\u77e5\u90a3\u4e2a\u964c\u751f\u4eba\u540e\u6765\u6709\u6ca1\u6709\u8bf4\uff0c\u4ee5\u53ca\u5982\u4f55\u5b89\u6392\u3002\u751a\u81f3\u53f2\u7528\u5a01\u5bb6\u4f4f\u4f55\u5904\uff0c\u6211 \u4ea6\u4e0d\u77e5\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u8fd9\u6761\u8ba1\u7b56\u679c\u7136\u662f\u72e0\u8fa3\u65e0\u6bd4\uff0c\u771f\u7684\u7ed9\u4ed6\u4eec\u6210\u529f\u7684\u8bdd\uff0c\u4e0d\u4f46\u53f2\u7528\u5a01\u8981\u7ed9\u5bb3\u5f97\u8eab \u8d25\u540d\u88c2\uff0c\u53ea\u6015\u8bb8\u591a\u6297\u91d1\u4e49\u571f\u4e5f\u8981\u7ed9\u91d1\u864f\u4e00\u7f51\u6253\u5c3d\u4e86\u3002\u4f46\u4f60\u540e\u6765\u51fa\u4e86\u91d1\u4eac\uff0c\u5c31\u5e94\u53bb\u901a\u77e5\u53f2\u7528 \u5a01\uff0c\u81f3\u5c11\u4e5f\u8be5\u6253\u542c\u6253\u542c\u53f2\u7528\u5a01\u6709\u6ca1\u6709\u65b0\u6536\u7684\u5f92\u5f1f\u4e86\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u6211\u9003\u51fa\u5927\u90fd\u4e0d\u8fc7\u6570\u6708\uff0c\u8fd9\u51e0\u4e2a\u6708\u6765\u6709\u8bb8\u591a\u66f4\u7d27\u8981\u7684\u4e8b\u60c5\u5f85\u529e\uff0c\u662f\u4ee5\u4e00\u76f4\u672a \u5f97\u673a\u4f1a\u53bb\u901a\u77e5\u53f2\u7528\u5a01\u3002\u81f3\u4e8e\u4ed6\u6709\u6ca1\u6709\u65b0\u6536\u5f92\u5f1f\u4e4b\u4e8b\uff0c\u6211\u5012\u662f\u6253\u542c\u8fc7\u7684\u3002\u8c01\u77e5\u4ed6\u53bb\u5e74\u4e00\u5e74\u4e4b\u5185 \u5c31\u6536\u4e86\u516d\u4e2a\u5f92\u5f1f\uff0c\u90a3\u4e2a\u5978\u7ec6\u662f\u8c01\uff0c\u8fd8\u662f\u65e0\u6cd5\u5224\u65ad\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u6211\u4e4b\u6240\u4ee5\u6ca1\u6709\u7acb\u5373\u53bb\u901a\u77e5\u4ed6\uff0c\u662f\u56e0\u4e3a\u6211\u60f3\u8fd9\u4e2a\u5978\u7ec6\u65e2\u7136\u662f\u8981\u957f\u671f\u57cb \u4f0f\u53f2\u5bb6\uff0c\u90a3\u4e48\u6211\u8fdf\u4e00\u4e9b\u65f6\u5019\u518d\u53bb\u67e5\u7a76\uff0c\u6599\u4ed6\u4e5f\u662f\u8fd8\u5728\u53f2\u5bb6\u7684\u3002\u53f2\u7528\u5a01\u4e0e\u4e49\u519b\u6697\u4e2d\u5f80\u6765\uff0c\u4f46\u6bd5 \u7adf\u4e5f\u8fd8\u4e0d\u662f\u4e49\u519b\u4e2d\u7684\u91cd\u8981\u4eba\u7269\uff0c\u662f\u4ee5\u7a0d\u7f13\u4ea6\u662f\u65e0\u59a8\u3002\u4f46\u73b0\u5728\u65e2\u7136\u6765\u5230\u6b64\u5730\uff0c\u53c8\u6070\u5de7\u78b0\u4e0a\u4e86\u53f2 \u7528\u5a01\u7684\u5bff\u8fb0\uff0c\u8fd9\u4ef6\u4e8b\u5c31\u5e94\u8be5\u987a\u4fbf\u6599\u7406\u4e86\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u5978\u7ec6\u65e9\u4e00\u65e5\u6e05\u9664\u65e9\u4e00\u65e5\u5b89\u5fc3\uff0c\u65e2\u7136\u6709\u8fd9\u6837\u4e00\u6869\u4e8b\u60c5\uff0c\u54b1\u4eec\u5c31\u662f\u5728\u6b64\u5730\u591a\u803d \u6401\u51e0\u5929\uff0c\u4ea6\u662f\u503c\u5f97\u3002\u201d \u3000\u3000\u53f2\u5bb6\u5728\u7b26\u79bb\u96c6\u4e4b\u897f\u7ea6\u4e94\u5341\u91cc\uff0c\u7b2c\u4e8c\u5929\u4e00\u65e9\u891a\u3001\u8c37\u4e8c\u4eba\u5e26\u4e86\u62dc\u5e16\uff0c\u524d\u53bb\u62dc\u5bff\uff0c\u6628\u665a\u53eb\u638c\u67dc \u5199\u62dc\u5e16\u7684\u90a3\u4e24\u4e2a\u4eba\u548c\u4ed6\u4eec\u540c\u884c\u3002\u4e92\u901a\u59d3\u540d\uff0c\u9ad8\u7684\u90a3\u4e2a\u53eb\u505a\u7ae0\u7ef4\uff0c\u77ee\u7684\u90a3\u4e2a\u53eb\u505a\u94b1\u5b9d\u3002 \u3000\u3000\u7ae0\u7ef4\u7b11\u9053\uff1a\u201c\u6628\u665a\u6211\u89c1\u4f60\u4eec\u6ca1\u5907\u6709\u62dc\u5e16\uff0c\u5f88\u89c9\u5947\u602a\uff0c\u539f\u6765\u4f60\u4eec\u4e5f\u662f\u50cf\u6211\u4e00\u6837\uff0c\u4e0d\u8bc6\u5b57 \u7684\uff0c\u539f\u6765\u891a\u4e91\u5cf0\u6c42\u638c\u67dc\u4ee3\u5199\u62dc\u5e16\u4e4b\u4e8b\uff0c\u968f\u540e\u4ed6\u5c31\u77e5\u9053\u4e86\uff0c\u662f\u4ee5\u9887\u6709\u201c\u5f15\u4e3a\u77e5\u5df1\u201d\u4e4b\u611f\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u5fc3\u4e2d\u6697\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8001\u54e5\u548b\u665a\u8bf4\u5f97\u597d\uff0c\u53f2\u8001\u82f1\u96c4\u53ea\u8981\u54b1\u4eec\u793c\u6570\u5230\u4e86\uff0c\u5c31\u4f1a\u559c \u6b22\uff0c\u62dc\u5e16\u662f\u4e0d\u662f\u81ea\u5df1\u5199\u7684\uff0c\u6709\u4f55\u5173\u7cfb\u3002\u201d \u3000\u3000\u53cc\u65b9\u8bf4\u8bdd\u6295\u673a\uff0c\u891a\u4e91\u5cf0\u4fbf\u95ee\u4ed6\u9053\uff1a\u201c\u542c\u8bf4\u53f2\u8001\u82f1\u96c4\u5f92\u5f1f\u5f88\u591a\uff0c\u5374\u4e0d\u77e5\u5171\u6709\u51e0\u4f4d\uff1f\u201d \u3000\u3000\u5f66\u7ef4\u8bf4\u9053\uff1a\u201c\u636e\u6211\u6240\u77e5\uff0c\u597d\u50cf\u4e00\u5171\u6709\u5341\u4e8c\u4e2a\u5f1f\u5b50\u3002\u5927\u5f1f\u5b50\u5f20\u901b\uff0c\u4eca\u5e74\u90fd\u5dee\u4e0d\u591a\u6709\u4e94\u5341\u5c81 \u4e86\u3002\u6700\u5c0f\u7684\u4e00\u4e2a\u5f1f\u5b50\uff0c\u542c\u8bf4\u624d\u4e8c\u5341\u5c81\u4e86\u3002\u201d \u3000\u3000\u94b1\u5b9d\u7b11\u9053\uff1a\u201c\u7ae0\u5927\u54e5\uff0c\u4f60\u7684\u6d88\u606f\u4e0d\u591f\u7075\u901a\uff0c\u4e00\u5171\u662f\u5341\u516b\u4e2a\u5f92\u5f1f\u5566\u3002\u53bb\u5e74\u4e00\u5e74\u4e4b\u5185\uff0c\u53f2\u8001 \u82f1\u96c4\u5c31\u6536\u4e86\u516d\u4e2a\u5f92\u5f1f\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u53f2\u8001\u82f1\u96c4\u4e3a\u4f55\u8fd9\u6837\u559c\u6b22\u6536\u5f92\u5f1f\uff1f\u4e0d\u6015\u826f\u83a0\u4e0d\u9f50\uff1f\u201d \u3000\u3000\u94b1\u5b9d\u8bf4\u9053\uff1a\u201c\u53f2\u8001\u82f1\u96c4\u4ea4\u6e38\u4e86\u9614\uff0c\u4ed6\u8001\u4eba\u5bb6\u6709\u4e2a\u6bdb\u75c5\uff0c\u5374\u4e0d\u8fc7\u597d\u670b\u53cb\u7684\u60c5\u9762\uff0c\u6536\u4e86\u4e00\u4e2a \u4e16\u4f84\u505a\u5f92\u5f1f\uff0c\u7b2c\u4e8c\u4e2a\u5c31\u8ddf\u7740\u6765\u3002\u4ed6\u4e0d\u60f3\u7ed9\u4eba\u5bb6\u8bf4\u4ed6\u539a\u6b64\u8584\u5f7c\uff0c\u4e5f\u5c31\u53ea\u597d\u6765\u8005\u4e0d\u62d2\u4e86\u3002\u201d \u3000\u3000\u7ae0\u7ef4\u8bf4\u9053\uff1a\u201c\u591f\u5f97\u4e0a\u505a\u53f2\u8001\u82f1\u96c4\u7684\u597d\u670b\u53cb\u7684\u81ea\u662f\u540d\u95e8\u6b63\u6d3e\u7684\u4eba\u7269\uff0c\u4ed6\u4eec\u7684\u5b50\u5f1f\u5f53\u7136\u4e5f\u8981 \u6bd4\u666e\u901a\u4eba\u5bb6\u7684\u5b50\u5f1f\u66f4\u6709\u6839\u57fa\uff0c\u5bb9\u6613\u8c03\u6559\uff0c\u8c37\u5144\u5012\u662f\u4e0d\u7528\u66ff\u53f2\u8001\u82f1\u96c4\u62c5\u5fc3\u5f92\u5f1f\u7684\u54c1\u6d41\u590d\u6742\uff0c\u826f \u83a0\u4e0d\u9f50\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u53bb\u5e74\u6536\u7684\u516d\u4e2a\u5f92\u5f1f\uff0c\u94b1\u5144\u53ef\u77e5\u9053\u662f\u4ec0\u4e48\u4eba\u4e48\uff1f\u201d \u3000\u3000\u94b1\u5b9d\u8bf4\u9053\uff1a\u201c\u6211\u53ea\u77e5\u5176\u4e2d\u4e09\u4e2a\u4eba\u7236\u5144\u7684\u6765\u5386\u3002\u201d\u8bf4\u4e86\u90a3\u4e09\u4e2a\u4eba\u7684\u540d\u5b57\uff0c\u90fd\u662f\u6c5f\u6e56\u4e0a\u4eba\u6240 \u5171\u77e5\u7684\u4fa0\u4e49\u9053\uff0c\u891a\u4e91\u5cf0\u4e5f\u66fe\u89c1\u8fc7\u7684\uff0c\u5f53\u7136\u4e0d\u662f\u4ed6\u5728\u9633\u5929\u96f7\u5bb6\u91cc\u6240\u89c1\u7684\u90a3\u4e2a\u964c\u751f\u4eba\u4e86\u3002 \u3000\u3000\u94b1\u5b9d\u53c8\u9053\uff1a\u201c\u53e6\u5916\u4e09\u4eba\u5219\u542c\u8bf4\u662f\u5e26\u827a\u6295\u5e08\u7684\u3002\u201d\u891a\u4e91\u5cf0\u6697\u81ea\u601d\u5fd6\uff1a\u201c\u8fd9\u5978\u7ec6\u60f3\u5fc5\u662f\u8fd9\u4e09 \u4e2a\u4eba\u4e2d\u7684\u4e00\u4e2a\u3002\u201d \u3000\u3000\u4ed6\u4eec\u811a\u7a0b\u8fc5\u901f\uff0c\u8c08\u8c08\u8bf4\u8bf4\uff0c\u4e0d\u77e5\u4e0d\u89c9\u5df2\u662f\u5230\u4e86\u53f2\u5bb6\u5e84\u3002\u53f2\u7528\u5a01\u679c\u7136\u4e0d\u6127\u6709\u201c\u8d5b\u5b5f\u5c1d\u201d\u4e4b \u79f0\uff0c\u891a\u3001\u8c37\u4e8c\u4eba\u9012\u4e0a\u62dc\u5e16\uff0c\u53f2\u5bb6\u77e5\u5ba2\u4e1d\u6beb\u4e0d\u52a0\u76d8\u95ee\u5c31\u8bf7\u4ed6\u4eec\u8fdb\u53bb\u4e86\u3002 \u3000\u3000\u53f2\u5bb6\u5bbe\u5ba2\u5982\u4e91\uff0c\u5927\u5385\u4e2d\u9ed1\u538b\u538b\u5730\u6324\u6ee1\u4e86\u4eba\uff0c\u891a\u4e91\u5cf0\u6839\u672c\u5c31\u6ca1\u6709\u673a\u4f1a\u63a5\u8fd1\u53f2\u7528\u5a01\u3002\u56f4\u7ed5\u7740 \u53f2\u7528\u5a01\u7684\u4e0d\u662f\u81f3\u4eb2\u5c31\u662f\u597d\u53cb\uff0c\u5426\u5219\u5c31\u662f\u6b66\u6797\u4e2d\u5927\u6709\u6765\u5934\u7684\u4eba\u7269\uff0c\u7b49\u95f2\u4e4b\u8f88\uff0c\u54ea\u80fd\u6324\u5230\u4ed6\u7684\u8eab \u8fb9\uff1f \u3000\u3000\u5e78\u800c\u53f2\u7528\u5a01\u6709\u5341\u516b\u4e2a\u5f92\u5f1f\uff0c\u5341\u516b\u540d\u5f1f\u5b50\u4ee3\u8868\u5e08\u7236\u5206\u5934\u62db\u547c\u5bbe\u5ba2\uff0c\u5012\u4e5f\u52c9\u5f3a\u53ef\u4ee5\u5e94\u4ed8\uff0c\u4e0d \u81f4\u51b7\u843d\u4e86\u5ba2\u4eba\u3002 \u3000\u3000\u94b1\u5b9d\u3001\u7ae0\u7ef4\u4e8c\u4eba\u751a\u4e3a\u6d3b\u8dc3\uff0c\u5728\u4eba\u4e1b\u4e2d\u7a7f\u6765\u63d2\u53bb\uff0c\u9022\u4eba\u70b9\u5934\uff0c\u6500\u4ea4\u60c5\uff0c\u7ed3\u670b\u53cb\u3002\u4e0d\u8fc7\u4ed6\u4eec \u4e5f\u81ea\u77e5\u591f\u4e0d\u4e0a\u8eab\u4efd\u53bb\u548c\u53f2\u7528\u5a01\u5bd2\u55a7\uff0c\u53ea\u80fd\u548c\u4ed6\u7684\u5f1f\u5b50\u6500\u4ea4\u3002\u891a\u4e91\u5cf0\u8ddf\u7740\u4ed6\uff0c\u9ed8\u4e0d\u4f5c\u58f0\uff0c\u6697\u4e2d \u7559\u610f\u53f2\u7528\u5a01\u7684\u5f1f\u5b50\u3002 \u3000\u3000\u94b1\u5b9d\u6709\u610f\u5411\u65b0\u670b\u53cb\u5938\u8000\u81ea\u5df1\u8bc6\u5f97\u4eba\u591a\uff0c\u5bf9\u891a\u4e91\u5cf0\u8bf4\u9053\uff1a\u201c\u521a\u624d\u6211\u548c\u4f60\u8bf4\u8fc7\uff0c\u53f2\u95e8\u5341\u516b\u5f1f \u5b50\uff0c\u6211\u53ea\u6709\u4e09\u4eba\u4e0d\u8bc6\uff0c\u8fd9\u4e09\u4eba\u4e43\u662f\u5e26\u827a\u6295\u5e08\u7684\uff0c\u73b0\u5728\u6211\u5df2\u77e5\u9053\u4ed6\u4eec\u662f\u8c01\uff0c\u4f60\u8981\u4e0d\u8981\u8ddf\u6211\u53bb\u548c \u4ed6\u4eec\u7ed3\u8bc6\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u6b63\u662f\u6000\u7591\u5978\u7ec6\u662f\u8fd9\u4e09\u4e2a\u4eba\u4e2d\u7684\u4e00\u4e2a\uff0c\u94b1\u5b9d\u613f\u610f\u7ed9\u4ed6\u4ecb\u7ecd\uff0c\u4ed6\u81ea\u662f\u6c42\u4e4b\u4e0d\u5f97\u3002\u5f53\u4e0b \u4fbf\u548c\u94b1\u5b9d\u4e00\u540c\u8fc7\u53bb\u3002\u4e0d\u6599\u94b1\u5b9d\u8fd8\u672a\u4ecb\u7ecd\uff0c\u5ffd\u6709\u4e00\u4eba\u4e00\u628a\u5c06\u4ed6\u62c9\u7740\uff0c\u8bf4\u9053\uff1a\u201c\u891a\u5144\uff0c\u4f60\u600e\u7684\u4e5f \u4f1a\u6765\u5230\u8fd9\u513f\u7ed9\u5bb6\u5e08\u8d3a\u5bff\uff1f\u201d\u8fd9\u4eba\u6b63\u662f\u90a3\u4e09\u4e2a\u5f92\u5f1f\u4e2d\u7684\u4e00\u4e2a\u3002 \u3000\u3000\u94b1\u5b9d\u751a\u89c9\u5c34\u5c2c\uff0c\u8bf4\u9053\uff1a\u201c\u539f\u6765\u4f60\u4eec\u65e9\u5df2\u76f8\u8bc6\u7684\u3002\u201d\u891a\u4e91\u5cf0\u5fd9\u5411\u90a3\u4eba\u6253\u4e86\u4e00\u4e2a\u773c\u8272\uff0c\u8bf4 \u9053\uff1a\u201c\u5218\u5144\uff0c\u6211\u5df2\u7ecf\u4e0d\u5728\u5927\u90fd\u5e72\u9556\u5c40\u751f\u610f\u4e86\uff0c\u6b63\u60f3\u627e\u4f60\u5e2e\u5e2e\u5fd9\u5462\u3002\u201d\u90a3\u4eba\u767b\u65f6\u4f1a\u610f\uff0c\u4fbf\u4e0e\u891a \u4e91\u5cf0\u8d70\u8fdb\u56ed\u5b50\u91cc\u53d9\u8bdd\u3002 \u3000\u3000\u539f\u6765\u8fd9\u4e2a\u4eba\u540d\u53eb\u5218\u5927\u4e3a\uff0c\u662f\u4e00\u652f\u4e49\u519b\u7684\u5934\u76ee\u3002\u891a\u4e91\u5cf0\u5728\u9633\u5929\u96f7\u624b\u4e0b\u4e4b\u65f6\uff0c\u8868\u9762\u66ff\u91d1\u5ef7\u529e \u4e8b\uff0c\u6697\u5730\u91cc\u5374\u5e38\u628a\u6d88\u606f\u9001\u7ed9\u4e49\u519b\uff0c\u4f46\u56e0\u8fd9\u662f\u4e00\u79cd\u5341\u5206\u5371\u9669\u7684\u4e8b\u60c5\uff0c\u891a\u4e91\u5cf0\u5fc5\u987b\u5341\u5206\u79d8\u5bc6\u7684\u8fdb \u884c\uff0c\u662f\u4ee5\u5373\u4f7f\u4e49\u519b\u4e2d\u4eba\uff0c\u77e5\u9053\u4ed6\u7684\u771f\u6b63\u8eab\u4efd\u7684\u4ea6\u662f\u5be5\u5be5\u65e0\u51e0\uff0c\u8fd9\u4e2a\u5218\u5927\u4e3a\u5c31\u662f\u6709\u9650\u7684\u51e0\u4e2a\u4eba \u4e2d\u4e4b\u4e00\uff0c\u66fe\u7ecf\u548c\u4ed6\u6709\u8fc7\u8054\u7edc\u7684\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u628a\u6709\u5978\u7ec6\u6df7\u4f5c\u4ed6\u7684\u540c\u95e8\u4e4b\u4e8b\u60c5\u544a\u8bc9\u4e86\u4ed6\uff0c\u5218\u5927\u4e3a\u5927\u5403\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u5f53\u771f\u6709\u8fd9\u6837 \u7684\u4e8b\uff0c\u8fd9\u5c31\u5947\u4e86\uff01\u53bb\u5e74\u548c\u6211\u4e00\u540c\u62dc\u5e08\u7684\u4e94\u4f4d\u5e08\u5144\u3002\u6211\u90fd\u662f\u77e5\u9053\u4ed6\u4eec\u7684\u6765\u5386\u7684\uff0c\u4f3c\u4e4e\u65e0\u4e00\u53ef \u7591\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u7ec6\u95ee\u5176\u8be6\uff0c\u539f\u6765\u53e6\u5916\u4e24\u4e2a\u5e26\u827a\u6295\u5e08\u7684\u4eba\u4e5f\u662f\u4e49\u519b\u4e2d\u7684\u5934\u76ee\uff0c\u800c\u4e14\u90fd\u662f\u548c\u4ed6\u4e00\u6837\uff0c \u5949\u547d\u6295\u5165\u53f2\u95e8\u7684\u3002\u81f3\u4e8e\u53e6\u5916\u90a3\u4e09\u4e2a\u4eba\u5219\u786e\u662f\u5982\u94b1\u5b9d\u6240\u8bf4\uff0c\u4ed6\u4eec\u7684\u7236\u5144\u90fd\u662f\u4fa0\u4e49\u9053\u4e2d\u7684\u4eba\u7269\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u4f46\u8fd9\u4e8b\u662f\u6211\u4eb2\u8033\u542c\u5230\u7684\uff0c\u51b3\u4e0d\u4f1a\u5047\u3002\u201d\u5218\u5927\u4e3a\u9053\uff1a\u201c\u6216\u8bb8\u90a3\u4e2a\u4eba\u540e\u6765\u77e5\u96be \u800c\u9000\uff0c\u6ca1\u6709\u62dc\u5e08\u3002\u201d\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u5b81\u53ef\u4fe1\u5176\u6709\uff0c\u4e0d\u53ef\u4fe1\u5176\u65e0\u3002\u201d\u5218\u5927\u4e3a\u9053\uff1a\u201c\u4f60\u8bf4\u5f97\u4e0d\u9519\uff0c \u6709\u5907\u65e0\u60a3\uff0c\u603b\u662f\u597d\u4e9b\u3002\u4eca\u665a\u8bf7\u4f60\u5728\u8fd9\u91cc\u7559\u5bbf\uff0c\u6211\u7ed9\u4f60\u627e\u4e2a\u673a\u4f1a\u548c\u5bb6\u5e08\u89c1\u9762\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u6b63\u5728\u601d\u7591\u4e0d\u8db3\uff0c\u5ffd\u89c1\u4eba\u4e1b\u91cc\u6709\u4e2a\u4eba\u4f3c\u66fe\u76f8\u8bc6\u3002 \u3000\u3000\u8fd9\u4eba\u662f\u5728\u54ea\u91cc\u89c1\u8fc7\u7684\u5462\uff1f\u891a\u4e91\u5cf0\u970d\u7136\u4e00\u7701\uff0c\u84e6\u5730\u60f3\u4e86\u8d77\u6765\u3002\u539f\u6765\u5c31\u662f\u90a3\u4e2a\u4ed6\u5728\u9633\u5929\u96f7\u7684 \u5bc6\u5ba4\u4e4b\u4e2d\u89c1\u8fc7\u7684\u964c\u751f\u4eba\uff01 \u3000\u3000\u53ef\u662f\u5f53\u4ed6\u8bb0\u8d77\u6765\u7684\u65f6\u5019\uff0c\u90a3\u4e2a\u4eba\u5df2\u5728\u4eba\u4e1b\u4e4b\u4e2d\u6d88\u5931\u4e86\u3002 \u3000\u3000\u5218\u5927\u4e3a\u770b\u89c1\u891a\u4e91\u5cf0\u4e00\u526f\u5b9a\u4e86\u773c\u795e\u7684\u6837\u5b50\uff0c\u4e0d\u89c9\u8be7\u9053\uff1a\u201c\u891a\u5144\uff0c\u4f60\u5728\u627e\u8c01\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u5218\u5144\uff0c\u6211\u60f3\u7acb\u523b\u62dc\u89c1\u4ee4\u5e08\uff0c\u5e0c\u671b\u6ca1\u6709\u5916\u4eba\u5728\u65c1\uff0c\u4f60\u80fd\u591f\u66ff\u6211\u529e\u5230\u5417\uff1f\u201d \u3000\u3000\u5218\u5927\u4e3a\u9053\uff1a\u201c\u4e3a\u4f55\u5982\u6b64\u7740\u6025\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u6211\u521a\u521a\u53d1\u73b0\u4e86\u90a3\u4e2a\u4eba\uff01\u201d\u5218\u5927\u4e3a\u4e00\u65f6\u5c1a\u672a\u9886\u609f\uff0c\u95ee\u9053\uff1a\u201c\u90a3\u4e2a\u4eba\u662f\u8c01\uff1f\u201d \u891a\u4e91\u5cf0\u9053\uff1a\u201c\u6211\u82e5\u77e5\u9053\u4ed6\u662f\u8c01\uff0c\u8fd9\u5c31\u597d\u529e\u4e86\uff01\u201d\u5218\u5927\u4e3a\u604d\u7136\u5927\u609f\uff0c\u8bf4\u9053\uff1a\u201c\u54e6\uff0c\u6562\u60c5\u5c31\u662f\u4f60 \u521a\u624d\u8bf4\u7684\u548c\u9633\u5929\u96f7\u5bc6\u5ba4\u5b9a\u8c0b\u7684\u90a3\u4e2a\u4eba\uff1f\u201d\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u6b63\u662f\u3002\u6211\u6015\u8fc7\u4e86\u4eca\u5929\uff0c\u4ed6\u4f1a\u8dd1\u4e86\u3002\u201d \u3000\u3000\u5218\u5927\u4e3a\u9887\u611f\u4e3a\u96be\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u662f\u4e2a\u65b0\u5165\u95e8\u7684\u5f1f\u5b50\uff0c\u5bb6\u5e08\u6b63\u5728\u548c\u4eb2\u53cb\u53d9\u8bdd\uff0c\u6211\u82e5\u8dd1\u53bb\u8bf7\u4ed6 \u8fdb\u5165\u5185\u5802\uff0c\u4ed6\u4e00\u5b9a\u8981\u5411\u6211\u95ee\u4e2a\u660e\u767d\u3002\u5728\u5927\u5ead\u5e7f\u4f17\u4e4b\u4e2d\uff0c\u8fd9\u4e2a\u79d8\u5bc6\u600e\u80fd\u8bf4\u51fa\u6765\u5462\uff1f\u4e00\u8bf4\u51fa\u6765\uff0c \u90a3\u4eba\u53ea\u6015\u5c31\u6e9c\u4e86\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u53ef\u662f\u8fd9\u662f\u552f\u4e00\u53ef\u4ee5\u67e5\u7a76\u5978\u7ec6\u7684\u7ebf\u7d22\uff0c\u4e0d\u5bb9\u8f7b\u6613\u653e\u8fc7\uff01\u201d \u3000\u3000\u5218\u5927\u4e3a\u9053\uff1a\u201c\u5f53\u7136\u4e0d\u80fd\u653e\u8fc7\u4ed6\uff01\u5f85\u6211\u60f3\u60f3\u3002\u55ef\uff0c\u6709\u4e86\uff0c\u6709\u4e86\u3002\u201d\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u4ec0\u4e48\u6709 \u4e86\uff1f\u201d\u5218\u5927\u4e3a\u9053\uff1a\u201c\u6211\u5012\u6709\u4e86\u4e00\u4e2a\u4e3b\u610f\uff0c\u4f60\u770b\u53ef\u4e0d\u53ef\u884c\uff0c\u4f60\u548c\u6211\u53bb\u627e\u90a3\u4e2a\u4eba\uff0c\u5c0f\u5fc3\u4e00\u4e9b\uff0c\u4e0d \u8981\u8ba9\u4ed6\u53d1\u73b0\uff0c\u4f60\u6307\u7ed9\u6211\u770b\uff0c\u770b\u6211\u8ba4\u4e0d\u8ba4\u8bc6\uff0c\u5018\u82e5\u6ca1\u6709\u673a\u4f1a\u7980\u544a\u5bb6\u5e08\uff0c\u54b1\u4eec\u5c31\u5148\u628a\u4ed6\u62ff\u4e0b\u518d \u8bf4\u3002\u201d\u891a\u4e91\u5cf0\u5fc3\u60f3\uff1a\u201c\u8fd9\u867d\u7136\u4e0d\u662f\u6700\u597d\u7684\u529e\u6cd5\uff0c\u4f46\u4e5f\u53ea\u597d\u5982\u6b64\u4e86\u3002\u201d\u5f53\u4e0b\u70b9\u5934\u8868\u793a\u8d5e\u540c\uff0c\u4e24 \u4eba\u4fbf\u5373\u56de\u5230\u5bff\u5802\u5bfb\u627e\u3002 \u3000\u3000\u5218\u5927\u4e3a\u548c\u891a\u4e91\u5cf0\u8d70\u8fdb\u5bff\u5802\u7684\u65f6\u5019\uff0c\u6b63\u597d\u78b0\u89c1\u4ed6\u7684\u5927\u5e08\u5144\u5f20\u901b\u5306\u5306\u5fd9\u5fd9\u5730\u8d70\u8fdb\u6765\u3002\u4f46\u867d\u7136 \u8d70\u5f97\u5306\u5fd9\uff0c\u5374\u662f\u6ee1\u8138\u559c\u6c14\u6d0b\u6d0b\u7684\u795e\u8272\uff01 \u3000\u3000\u5f20\u901b\u8d70\u5230\u5e08\u7236\u8ddf\u524d\uff0c\u5448\u4e0a\u4e00\u5f20\u62dc\u5e16\uff0c\u53f2\u7528\u5a01\u767b\u65f6\u7709\u5f00\u773c\u7b11\u5730\u7ad9\u4e86\u8d77\u6765\uff0c\u5728\u4ed6\u5468\u56f4\u7684\u670b \u53cb\uff0c\u4e5f\u50cf\u716e\u6cb8\u4e86\u4e00\u9505\u6c34\u4f3c\u7684\uff0c\u4eba\u4eba\u8038\u7136\u52a8\u5bb9\uff0c\u54d7\u5566\u54d7\u5566\u5730\u4e89\u7740\u8bf4\u8bdd\u3002\u4f17\u5bbe\u5ba2\u4e00\u65f6\u95f4\u4e5f\u5f04\u4e0d\u6e05 \u695a\u4ed6\u4eec\u5728\u95f9\u4e9b\u4ec0\u4e48\u3002 \u3000\u3000\u5f20\u901b\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u5404\u4f4d\u5e08\u5f1f\u5feb\u6765\uff0c\u968f\u5e08\u7236\u8fce\u63a5\u8d35\u5ba2\uff01\u201d\u6b64\u8a00\u4e00\u51fa\uff0c\u6ee1\u5802\u5bbe\u5ba2\u90fd\u662f\u60ca\u8be7\u65e0 \u6bd4\uff0c\u4eba\u4eba\u5fc3\u4e2d\u90fd\u662f\u60f3\u9053\uff1a\u201c\u662f\u4ec0\u4e48\u6837\u7684\u5927\u4eba\u7269\uff0c\u503c\u5f97\u53f2\u8001\u82f1\u96c4\u5982\u6b64\u5c0a\u656c\uff1f\u4eb2\u81ea\u51fa\u8fce\u4e0d\u7b97\uff0c\u8fd8 \u8981\u7387\u9886\u5f1f\u5b50\u51fa\u8fce\uff1f\u201d \u3000\u3000\u8fd9\u4e2a\u8c1c\u5e95\u5f88\u5feb\u5c31\u63ed\u5f00\u4e86\uff0c\u56e0\u4e3a\u53f2\u5bb6\u4eb2\u53cb\u4e03\u5634\u516b\u820c\u5730\u8bae\u8bba\u58f0\u97f3\uff0c\u5bbe\u5ba2\u4eec\u7528\u5fc3\u6765\u542c\uff0c\u5df2\u662f\u542c \u5f97\u6e05\u695a\u4e86\uff0c\u53ea\u542c\u5f97\u6709\u4eba\u8bf4\u9053\uff1a\u201c\u6b66\u5f53\u56db\u5927\u5f1f\u5b50\u540c\u6765\u8d3a\u5bff\uff0c\u8fd9\u771f\u662f\u4f60\u8001\u4eba\u5bb6\u5929\u5927\u7684\u9762\u5b50\uff01\u201d\u6709 \u4eba\u5374\u9053\uff1a\u201c\u4e0d\u77e5\u4ed6\u4eec\u662f\u5426\u7279\u5730\u4e3a\u4e86\u8d3a\u5bff\u800c\u6765\uff1f\u201d\u8bae\u8bba\u58f0\u4e2d\uff0c\u8fd9\u51e0\u4e2a\u6b66\u5f53\u5f1f\u5b50\u5df2\u662f\u8fdb\u4e86\u4e8c\u95e8\uff0c \u53f2\u7528\u5a01\u4ea6\u5df2\u7387\u9886\u5f1f\u5b50\uff0c\u7ad9\u5728\u5bff\u5802\u95e8\u53e3\u51c6\u5907\u8fce\u5bbe\u4e86\u3002 \u3000\u3000\u891a\u3001\u5218\u4e8c\u4eba\u56ed\u4e2d\u53d9\u8bdd\u4e4b\u65f6\uff0c\u8c37\u6db5\u865a\u662f\u4e00\u76f4\u7559\u5728\u5bff\u5802\u7684\u3002\u6b64\u65f6\u4ed6\u542c\u8bf4\u6765\u8005\u4e43\u662f\u201c\u6b66\u5f53\u56db\u5927 \u5f1f\u5b50\u201d\uff0c\u4e0d\u7981\u5927\u5403\u4e00\u60ca\uff0c\u62ac\u5934\u770b\u65f6\uff0c\u53ea\u89c1\u90a3\u56db\u4e2a\u4eba\u5df2\u662f\u9c7c\u8d2f\u800c\u5165\uff0c\u4e3a\u9996\u7684\u53ef\u4e0d\u6b63\u662f\u6b66\u5f53\u6d3e\u7684 \u638c\u95e8\u5927\u5f1f\u5b50\u4e54\u5143\u58ee\uff01\u4ee5\u4e0b\u4f9d\u6b21\u662f\u4e8c\u5f1f\u5b50\u5b63\u5143\u4f26\uff0c\u4e09\u5f1f\u5b50\u5f20\u5143\u5409\uff0c\u56db\u5f1f\u5b50\u7cb1\u5143\u732e\u3002\u5176\u4e2d\u7684\u4e09\u5f1f \u5b50\u5f20\u5143\u5409\uff0c\u6b63\u662f\u4e25\u70f7\u7684\u672a\u5a5a\u592b\uff01 \u3000\u3000\u60e8\u75db\u7684\u5f80\u4e8b\uff0c\u672c\u6765\u4ee5\u4e3a\u5df2\u6210\u8fc7\u53bb\u3002\u7ed9\u65f6\u95f4\u51b2\u6de1\u4e86\u7684\u5f80\u4e8b\uff0c\u5374\u968f\u7740\u6b66\u5f53\u56db\u5927\u5f1f\u5b50\u7684\u6765\u5230\uff0c \u84e6\u5730\u91cc\u53c8\u5728\u8c37\u6db5\u865a\u7684\u8111\u6d77\u4e2d\u91cd\u73b0\u4e86\uff01 \u3000\u3000\u90a3\u4e00\u665a\u60ca\u5fc3\u52a8\u9b44\u7684\u4e00\u5e55\u5982\u5728\u76ee\u524d\uff1a\u4ed6\u548c\u4e25\u70f7\u6b63\u5728\u6797\u4e2d\u5e7d\u4f1a\uff0c\u60c5\u8bdd\u7ef5\u7ef5\uff0c\u871c\u7231\u8f7b\u601c\uff0c\u6d51\u5fd8 \u4e00\u5207\u4e4b\u9645\uff0c\u5305\u62ec\u6709\u4e25\u70f7\u7684\u672a\u5a5a\u592b\u5728\u5185\u7684\u8fd9\u56db\u4e2a\u4eba\u7a81\u7136\u51fa\u73b0\uff0c\u4e0d\u5206\u7682\u767d\uff0c\u4fbf\u8981\u201c\u6349\u5978\u201d\uff01 \u3000\u3000\u9178\u98ce\u918b\u6d6a\u7ec8\u4e8e\u53d8\u6210\u4e86\u8840\u96e8\u8165\u98ce\uff0c\u8c37\u6db5\u865a\u3001\u4e25\u70f7\u903c\u5f97\u548c\u6b66\u5f53\u56db\u5927\u5f1f\u5b50\u52a8\u624b\u3002\u8c37\u6db5\u865a\u6253\u4f24\u4e86 \u4e54\u5143\u58ee\u3001\u5f20\u5143\u5409\uff0c\u53ef\u662f\u5f20\u5143\u5409\u7684\u5229\u5251\u4e5f\u5728\u4ed6\u7684\u8138\u4e0a\u5212\u51fa\u4e86\u51e0\u9053\u7eb5\u6a2a\u652f\u9519\u7684\u4f24\u75d5\uff0c\u628a\u672c\u6765\u8db3\u4ee5 \u79f0\u4e3a\u7f8e\u7537\u5b50\u7684\u8c37\u6db5\u8654\u53d8\u6210\u4e86\u4e00\u4e2a\u201c\u4e11\u516b\u602a\u201d\uff01\u8ddf\u7740\u4e25\u70f7\u7684\u7236\u6bcd\u6765\u5230\uff0c\u628a\u4e25\u70f7\u6349\u56de\u5bb6\u53bb\uff0c\u6b66\u5f53 \u56db\u5927\u5f1f\u5b50\u8d1f\u6c14\u800c\u8d70\uff0c\u8c37\u6db5\u865a\u5728\u4e25\u70f7\u7236\u6bcd\u7684\u76db\u6012\u4e4b\u4e0b\uff0c\u4e5f\u60df\u6709\u4e0e\u60c5\u4eba\u5206\u624b\uff0c\u8fdc\u8d70\u4ed6\u65b9\u3002\u4ee5\u4e3a\u7eb5 \u975e\u6b7b\u522b\uff0c\u4ea6\u662f\u751f\u79bb\u4e86\u3002 \u3000\u3000\u5e78\u4e8f\u4e25\u70f7\u662f\u4e2a\u975e\u5e38\u6709\u52c7\u6c14\u7684\u5973\u5b50\uff0c\u9003\u51fa\u6765\u627e\u4ed6\uff0c\u7ecf\u8fc7\u957f\u957f\u7684\u56db\u5e74\uff0c\u7ec8\u4e8e\u7ed9\u5979\u627e\u7740\u4e86\u3002\u7ecf \u8fc7\u56db\u5e74\u7684\u78e8\u6298\uff0c\u5927\u5bb6\u7684\u5fc3\u90fd\u6ca1\u6709\u53d8\uff0c\u4ed6\u4eec\u7684\u7231\u60c5\u4e5f\u6bd4\u4ee5\u524d\u66f4\u575a\u56fa\u4e86\u3002 \u3000\u3000\u8c37\u6db5\u865a\u672c\u4ee5\u4e3a\u4ece\u6b64\u662f\u96e8\u8fc7\u5929\u6674\uff0c\u82e6\u5c3d\u7518\u6765\uff0c\u53ef\u4ee5\u6446\u8131\u607c\u4eba\u7684\u5f80\u4e8b\u4e86\uff0c\u54ea\u77e5\u5374\u5728\u53f2\u7528\u5a01\u7684 \u5bb6\u91cc\uff0c\u53c8\u78b0\u4e0a\u4e86\u4e25\u70f7\u7684\u672a\u5a5a\u592b\u3002 \u3000\u3000\u5f20\u5143\u5409\u5f53\u65f6\u8d1f\u6c14\u51fa\u8d70\uff0c\u662f\u66fe\u7ecf\u58f0\u660e\u4e0d\u8981\u4e25\u70f7\u4e86\u7684\u3001\u4e0d\u8fc7\u5374\u672a\u6b63\u5f0f\u89e3\u9664\u5a5a\u7ea6\uff0c\u5728\u540d\u4e49\u4e0a\u4ed6 \u8fd8\u662f\u4e25\u70f7\u7684\u672a\u5a5a\u592b\uff01 \u3000\u3000\u201c\u6211\u8981\u4e0d\u8981\u907f\u5f00\u4ed6\u5462\uff1f\u201d\u8c37\u6db5\u865a\u6697\u81ea\u601d\u91cf\uff1a\u201c\u4e54\u5143\u58ee\u548c\u5f20\u5143\u5409\u90fd\u662f\u6c14\u91cf\u72ed\u7a84\u7684\u4eba\uff0c\u7ed9\u4ed6 \u4eec\u53d1\u73b0\u4e86\u6211\uff0c\u53ea\u6015\u53c8\u8981\u6380\u8d77\u4e00\u573a\u98ce\u6d6a\uff01\u4f46\u6211\u662f\u548c\u891a\u5e08\u5144\u6765\u4fa6\u67e5\u5978\u7ec6\u7684\uff0c\u8fd9\u662f\u4e00\u4ef6\u7d27\u8981\u7684\u4e8b \u60c5\uff0c\u53c8\u5c82\u53ef\u4e3a\u4e86\u79c1\u4eba\u7684\u4ec7\u6028\u800c\u628a\u5927\u4e8b\u629b\u5f00\uff1f\u201d \u3000\u3000\u5fc3\u5ff5\u672a\u5df2\uff0c\u53ea\u89c1\u53f2\u7528\u5a01\u5df2\u5c06\u4e54\u5143\u58ee\u7b49\u4eba\u8fce\u5165\u5bff\u5802\uff0c\u8083\u8bf7\u4e0a\u5750\u3002\u4e54\u5143\u58ee\u7eb5\u76ee\u56db\u987e\uff0c\u8bf4\u9053\uff1a \u201c\u4eca\u5929\u53ef\u8bf4\u5f97\u662f\u80dc\u53cb\u5982\u4e91\uff0c\u9ad8\u670b\u6ee1\u5ea7\u3002\u6211\u4eec\u80fd\u591f\u51d1\u4e0a\u8fd9\u4e2a\u70ed\u95f9\u771f\u662f\u5e78\u4f55\u5982\u4e4b\uff01\u201d \u3000\u3000\u53f2\u7528\u5a01\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8001\u673d\u8d31\u8fb0\uff0c\u5f97\u6b66\u5f53\u56db\u4fa0\u5149\u4e34\uff0c\u66f4\u662f\u4e0d\u80dc\u4e4b\u559c\u3002\u4e0d\u77e5\u56db\u4fa0\u662f\u8def\u8fc7\u8fd8\u662f \u7279\u5230\uff1f\u201d \u3000\u3000\u8981\u77e5\u6b66\u5f53\u6d3e\u4e43\u662f\u548c\u5c11\u6797\u6d3e\u9f50\u540d\u7684\u4e24\u5927\u5b97\u6d3e\uff0c\u5728\u6b66\u6797\u4e2d\u7aef\u7684\u662f\u53ef\u4ee5\u79f0\u4e3a\u6cf0\u5c71\u5317\u6597\u7684\u3002\u6545\u6b64 \u4e54\u5143\u58ee\u7b49\u4eba\u867d\u5c5e\u5c0f\u8f88\uff0c\u4f46\u56db\u4eba\u8054\u8882\u800c\u6765\uff0c\u5bf9\u53f2\u7528\u5a01\u6765\u8bf4\uff0c\u5374\u662f\u4e2a\u201c\u5929\u5927\u7684\u9762\u5b50\u201d\u4e86\u3002\u53f2\u7528\u5a01 \u867d\u662f\u4e2a\u65e9\u5df2\u6210\u540d\u7684\u4eba\u7269\uff0c\u4e5f\u4e0d\u7981\u6709\u53d7\u5ba0\u82e5\u60ca\u4e4b\u611f\u3002 \u3000\u3000\u4e54\u5143\u58ee\u9053\uff1a\u201c\u6211\u4eec\u662f\u7279\u5730\u6765\u7ed9\u8001\u82f1\u96c4\u62dc\u5bff\u7684\uff0c\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u53f2\u7528\u5a01\u9053\uff1a\u201c\u4e54\u5144\u6709\u8bdd\uff0c\u4f46\u8bf4\u65e0\u59a8\u3002\u201d\u4e54\u5143\u58ee\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u4e0d\u8fc7\u4e5f\u6709\u4e00\u4ef6\u79c1\u4e8b\uff0c\u60f3\u8bf7\u53f2 \u8001\u82f1\u96c4\u5e2e\u5fd9\u3002\u6211\u8fd9\u4f4d\u5f20\u5e08\u5f1f\u60f3\u627e\u4e00\u4f4d\u670b\u53cb\uff0c\u8001\u82f1\u96c4\u4ea4\u6e38\u4e86\u9614\uff0c\u6216\u8bb8\u4f1a\u77e5\u9053\u6b64\u4eba\u884c\u8e2a\u3002\u201d \u3000\u3000\u53f2\u7528\u5a01\u8f6c\u8fc7\u5934\u6765\u95ee\u5f20\u5143\u5409\u9053\uff1a\u201c\u4e0d\u77e5\u8d35\u53cb\u9ad8\u59d3\u5927\u540d\uff1f\u201d\u5f20\u5143\u5409\u9053\uff1a\u201c\u6b64\u4eba\u540d\u53eb\u8c37\u6db5\u865a\uff0c \u76f8\u8c8c\u5f88\u662f\u7279\u522b\uff0c\u8138\u4e0a\u6709\u51e0\u9053\u4f24\u75d5\u7684\u3002\u201d\u53f2\u7528\u5a01\u9053\uff1a\u201c\u597d\uff0c\u6211\u66ff\u4f60\u7559\u610f\u4fbf\u662f\u3002\u201d \u3000\u3000\u53f2\u7528\u5a01\u4e0e\u4e54\u5f20\u7b49\u4eba\u8c08\u8bdd\uff0c\u5916\u9762\u56f4\u6709\u4e09\u91cd\u591a\u4eba\uff0c\u7b2c\u4e00\u91cd\u662f\u4ed6\u7684\u81f3\u4eb2\u597d\u53cb\uff0c\u7b2c\u4e8c\u91cd\u662f\u4ed6\u7684\u95e8 \u4eba\u5f1f\u5b50\uff0c\u7b2c\u4e09\u91cd\u624d\u662f\u50cf\u94b1\u5b9d\u90a3\u6837\u7684\u60f3\u62cd\u9a6c\u5c41\u7684\u4eba\u3002\u5927\u5385\u4e0a\u4eba\u5934\u6324\u6324\uff0c\u5916\u56f4\u7684\u5bbe\u5ba2\u6839\u672c\u5c31\u542c\u4e0d \u89c1\u4ed6\u4eec\u5728\u8c08\u4e9b\u4ec0\u4e48\u3002 \u3000\u3000\u8c37\u6db5\u865a\u7ec3\u8fc7\u201c\u542c\u98ce\u8fa8\u5668\u201d\u7684\u529f\u592b\uff0c\u4ed6\u51dd\u795e\u9759\u542c\u4e4b\u4e0b\uff0c\u5728\u5608\u5608\u6742\u6742\u7684\u58f0\u97f3\u4e4b\u4e2d\uff0c\u5374\u662f\u628a\u5f20 \u5143\u5409\u8bf4\u7684\u6bcf\u4e00\u4e2a\u5b57\u90fd\u542c\u8fdb\u8033\u6735\u4e86\u3002\u8c37\u6db5\u865a\u53c8\u60ca\u53c8\u6012\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u53ae\u679c\u7136\u662f\u627e\u6211\u6765\u7684\uff0c \u54fc\uff0c\u6211\u4e0d\u60f3\u627e\u4ed6\u62a5\u4ec7\u3002\u4ed6\u5012\u60f3\u5411\u6211\u5bfb\u4ec7\uff01\u201d \u3000\u3000\u8981\u77e5\u8c37\u6db5\u865a\u672c\u662f\u4e2a\u7f8e\u7537\u5b50\uff0c\u5bb9\u8c8c\u6bc1\u5728\u5f20\u5143\u5409\u7684\u5251\u4e0b\uff0c\u4ed6\u4f55\u5c1d\u4e0d\u4e5f\u542b\u6068\u4e8e\u5fc3\uff1f\u53ea\u56e0\u542c\u4e86\u5e08 \u7236\u7684\u529d\u544a\uff0c\u540c\u65f6\u81ea\u5df1\u53c8\u83b7\u5f97\u4e86\u4e25\u70f7\uff0c\u8fd9\u624d\u4e0d\u60f3\u548c\u5f20\u5143\u5409\u8ba1\u8f83\u7684\u3002\u73b0\u5728\u542c\u4e86\u5f20\u5143\u5409\u7684\u8bdd\uff0c\u77e5\u9053 \u5f20\u5143\u5409\u672a\u5fd8\u5bbf\u6028\uff0c\u4ed6\u7684\u65e7\u6068\u4e5f\u5c31\u4e0d\u7981\u7ed9\u5f20\u5143\u5409\u7684\u8bdd\u6311\u8d77\u6765\u4e86\u3002 \u3000\u3000\u201c\u6211\u8981\u4e0d\u8981\u633a\u8eab\u800c\u51fa\u5462\uff1f\u201d\u8c37\u6db5\u865a\u6309\u637a\u4e0d\u4f4f\u6012\u706b\uff0c\u51e0\u4e4e\u5c31\u60f3\u6392\u4f17\u800c\u51fa\uff0c\u548c\u5f20\u5143\u5409\u7b97\u7b97\u65e7 \u5e10\u4e86\u3002\u6b63\u5728\u6b64\u9645\uff0c\u5ffd\u89c1\u891a\u4e91\u5cf0\u5411\u4ed6\u8d70\u6765\u3002\u8c37\u6db5\u865a\u76df\u7136\u4e00\u7701\uff0c\u8fd9\u624d\u6ca1\u6709\u8f7b\u7ee7\u5984\u52a8\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u6084\u58f0\u8bf4\u9053\uff1a\u201c\u8c37\u5e08\u5f1f\uff0c\u6211\u627e\u7740\u90a3\u4e2a\u4eba\u4e86\u3002\u201d\u8c37\u6db5\u865a\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\uff1f\u201d\u891a\u4e91\u5cf0\u9053\uff1a \u201c\u5c31\u662f\u548c\u9633\u5929\u96f7\u5bc6\u5ba4\u5b9a\u8c0b\u7684\u90a3\u4e2a\u4eba\u3002\u4f60\u77a7\uff0c\u4ed6\u73b0\u5728\u6b63\u8d70\u8fc7\u53bb\u548c\u90a3\u4e2a\u6b66\u5f53\u6d3e\u7684\u638c\u95e8\u5f1f\u5b50\u8bf4 \u8bdd\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u5b9a\u775b\u4e00\u770b\uff0c\u53ea\u89c1\u4e00\u4e2a\u4e09\u7ecd\u957f\u987b\u7684\u6c49\u5b50\u521a\u521a\u8d70\u8fdb\u90a3\u4e2a\u5708\u5b50\u3002\u53f2\u7528\u5a01\u8bf4\u9053\uff1a\u201c\u4e54\u5144\uff0c \u6211\u7ed9\u4f60\u4ecb\u7ecd\u4e00\u4f4d\u670b\u53cb\uff0c\u8fd9\u4f4d\u5362\u4e09\u54e5\uff0c\u5728\u5317\u4e94\u7701\u4eba\u9762\u5f88\u719f\uff0c\u4f60\u8981\u627e\u542c\u4ec0\u4e48\u4eba\uff0c\u4e0d\u59a8\u8bf7\u4ed6\u5e2e \u5fd9\u3002\u201d \u3000\u3000\u4e14\u8bf4\u94b1\u5b9d\u6324\u5728\u53f2\u95e8\u5f1f\u5b50\u4e4b\u4e2d\uff0c\u542c\u4e86\u4e54\u5143\u58ee\u63cf\u8ff0\u8c37\u6db5\u865a\u7684\u76f8\u8c8c\uff0c\u4e0d\u7981\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u91cc\u60f3 \u9053\uff1a\u201c\u8fd9\u4e0d\u6b63\u662f\u548c\u6211\u540c\u6765\u7684\u90a3\u4e2a\u59d3\u8c37\u7684\u6c49\u5b50\u5417\uff1f\u201d \u3000\u3000\u53f2\u7528\u5a01\u7684\u5927\u5f1f\u5b50\u5f20\u901b\u84e6\u5730\u60f3\u4e86\u8d77\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u8c37\u6db5\u865a\uff1f\u8fd9\u540d\u5b57\u6211\u597d\u50cf\u5728\u54ea\u91cc\u89c1\u8fc7\uff1f\u5bf9 \u5566\uff0c\u6b63\u662f\u521a\u624d\u6536\u5230\u7684\u4e00\u5f20\u62dc\u5e16\u4e0a\u7684\u540d\u5b57\u3002\u4ed6\u662f\u548c\u8c01\u540c\u6765\u7684\u5462\uff1f\u201d \u3000\u3000\u94b1\u5b9d\u5fcd\u4e0d\u4f4f\u4fbf\u54fc\u4e86\u51fa\u6765\uff1a\u201c\u4e54\u5927\u4fa0\uff0c\u5f20\u5927\u4fa0\uff0c\u6211\u9a6c\u4e0a\u8bf7\u4ed6\u8fc7\u6765\u548c\u4f60\u4eec\u76f8\u89c1\u3002\u201d\u94b1\u5b9d\u54ea\u91cc \u77e5\u9053\u8c37\u6db5\u865a\u662f\u5f20\u5143\u5409\u7684\u4ec7\u4eba\uff0c\u53ea\u9053\u5f53\u771f\u662f\u4ed6\u7684\u670b\u53cb\u3002\u56e0\u6b64\u5341\u5206\u5f97\u610f\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6b66\u5f53\u56db\u4fa0 \u7684\u670b\u53cb\u4e5f\u662f\u6211\u7684\u670b\u53cb\uff01\u8fd9\u56de\u6211\u53ef\u771f\u662f\u5927\u5927\u7684\u2018\u9732\u9762\u2019\uff0c\u6709\u9762\u5b50\u4e4b\u6781\u4e86\uff01\u201d \u3000\u3000\u891a\u4e91\u5cf0\u53d1\u73b0\u4e86\u90a3\u4e2a\u4eba\uff0c\u7acb\u5373\u62c9\u8c37\u6db5\u865a\u8fc7\u53bb\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u770b\u8fd9\u60c5\u5f62\uff0c\u8fd9\u4e2a\u59d3\u5362\u7684\u5978\u8d3c\u548c\u53f2\u7528\u5a01\u7684\u4ea4\u60c5\u975e\u540c\u6cdb\u6cdb\uff0c\u53f2\u8001\u82f1\u96c4\u4e00 \u5b9a\u4e0d\u4f1a\u76f8\u4fe1\u54b1\u4eec\u7684\u6d3b\u3002\u4e3a\u4e86\u907f\u514d\u6295\u9f20\u5fcc\u5668\uff0c\u54b1\u4eec\u53ea\u6709\u5148\u53d1\u5236\u4eba\uff0c\u628a\u4ed6\u62ff\u4e0b\u518d\u8bf4\uff01\u201d \u3000\u3000\u8c37\u6db5\u865a\u65e0\u6687\u548c\u5e08\u5144\u8bf4\u660e\u4ed6\u4e0e\u6b66\u5f53\u56db\u5927\u5f1f\u5b50\u6709\u4ec7\u4e4b\u4e8b\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u53cd\u6b63\u4eca\u5929\u662f\u8eb2\u4e0d\u5f00\u7684 \u4e86\uff0c\u5f20\u5143\u5409\u8981\u627e\u6211\u7b97\u5e10\uff0c\u90a3\u5c31\u4efb\u7531\u4ed6\u5427\uff01\u201d \u3000\u3000\u94b1\u5b9d\u773c\u5229\uff0c\u4e00\u773c\u770b\u89c1\u8c37\u6db5\u865a\u548c\u891a\u4e91\u5cf0\u8d70\u6765\uff0c\u5927\u559c\u53eb\u9053\uff1a\u201c\u8c37\u5144\uff0c\u4f60\u7684\u597d\u670b\u53cb\u6b66\u5f53\u5f20\u4e09\u4fa0 \u6b63\u5728\u627e\u4f60\u5462\uff01\u5feb\u6765\uff01\u5feb\u6765\uff01\u201d \u3000\u3000\u56f4\u7ed5\u5728\u53f2\u7528\u5a01\u65c1\u8fb9\u7684\u4eb2\u670b\u597d\u53cb\u95e8\u4eba\u5f1f\u5b50\u4e24\u8fb9\u5206\u5f00\uff0c\u5f20\u5143\u5409\u4e0e\u90a3\u59d3\u5362\u7684\u6c49\u5b50\u5219\u662f\u4e0d\u7ea6\u800c\u540c \u5730\u84e6\u5730\u7ad9\u4e86\u8d77\u6765\uff01 \u3000\u3000\u5f20\u5143\u5409\u7684\u773c\u4e2d\u597d\u50cf\u8981\u55b7\u51fa\u706b\u7130\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u8c37\u6db5\u865a\uff0c\u4f60\u60f3\u4e0d\u5230\u4eca\u65e5\u53c8\u662f\u964c\u8def\u76f8\u9022\u5427\uff1f \u54b1\u4eec\u8fd9\u7b14\u5e10\u5e94\u8be5\u5982\u4f55\u7b97\u6cd5\uff0c\u4f60\u8bf4\u3002\u201d \u3000\u3000\u51e0\u4e4e\u5728\u540c\u4e00\u4e2a\u65f6\u5019\uff0c\u891a\u4e91\u5cf0\u4e5f\u5728\u5411\u90a3\u59d3\u5362\u7684\u6c49\u5b50\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u60f3\u4e0d\u5230\u5728\u8fd9\u91cc\u78b0\u4e0a\u6211 \u5427\uff1f\u201d\u8bdd\u58f0\u4e00\u987f\uff0c\u84e6\u5730\u559d\u9053\uff1a\u201c\u51fa\u624b\uff01\u201d \u3000\u3000\u4e24\u8fb9\u90fd\u5728\u4e89\u7740\u8bf4\u8bdd\uff0c\u891a\u4e91\u5cf0\u672c\u6765\u662f\u53eb\u5e08\u5f1f\u51fa\u624b\u5236\u4f0f\u90a3\u59d3\u5362\u7684\u6c49\u5b50\uff0c\u5f20\u5143\u5409\u5374\u4ee5\u4e3a\u662f\u5bf9\u4ed8 \u4ed6\u4e86\u3002 \u3000\u3000\u8c37\u6db5\u865a\u547c\u5730\u4e00\u638c\u5411\u59d3\u5362\u7684\u6c49\u5b50\u62cd\u53bb\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u5f20\u5143\u5409\u4ea6\u662f\u5530\u5730\u4e00\u5251\u5411\u4ed6\u523a\u6765\uff0c \u5f20\u5143\u5409\u4e00\u51fa\u624b\uff0c\u4ed6\u7684\u4e09\u4e2a\u5e08\u5144\u7ed9\u5f53\u7136\u4e5f\u662f\u7acb\u5373\u8ddf\u7740\u51fa\u624b\u4e86\u3002 \u3000\u3000\u891a\u3001\u8c37\u4e8c\u4eba\u8054\u624b\u4f7f\u51fa\u4e86\u201c\u5929\u96f7\u529f\u201d\uff0c\u672c\u6765\u662f\u53ef\u4ee5\u5236\u4f0f\u90a3\u59d3\u5362\u7684\u6c49\u5b50\u6709\u4f59\uff0c\u53ef\u662f\u6b66\u5f53\u56db\u4fa0 \u4ea6\u662f\u8054\u540c\u51fa\u624b\uff0c\u56db\u67c4\u957f\u5251\u4f7f\u51fa\u4e86\u8fde\u73af\u593a\u547d\u5251\u6cd5\uff0c\u5206\u522b\u5411\u891a\u3001\u8c37\u4e8c\u4eba\u8eab\u4e0a\u523a\u6765\uff0c\u5251\u52bf\u4ea6\u662f\u51cc\u5389 \u4e4b\u6781\uff0c\u8c37\u6db5\u865a\u4e00\u4e2a\u6c89\u80a9\u7f29\u8098\uff0c\u8098\u5c16\u4e00\u649e\uff0c\u649e\u9000\u4e86\u5f20\u5143\u5409\uff0c\u624b\u81c2\u5df2\u7ed9\u5251\u5c16\u5212\u7834\u4e00\u9053\u4f24\u53e3\uff0c\u5e78\u800c \u4f24\u5f97\u6781\u6d45\u3002\u4f46\u90a3\u59d3\u5362\u7684\u6c49\u5b50\u5df2\u662f\u9000\u51fa\u4e09\u56db\u6b65\u4e86\u3002 \u3000\u3000\u201c\u5929\u96f7\u529f\u201d\u5a01\u529b\u7aef\u7684\u975e\u540c\u5c0f\u53ef\u3002\u53ea\u542c\u5f97\u201c\u6ce2\u201d\u7684\u4e00\u58f0\u54cd\uff0c\u4e54\u5143\u58ee\u3001\u5b63\u5143\u4f26\u3001\u6881\u5143\u732e\u7684\u4e09 \u67c4\u957f\u5251\u540c\u65f6\u7ed9\u4ed6\u4eec\u7684\u638c\u529b\u8361\u5f00\uff0c\u4f59\u6ce2\u6240\u53ca\uff0c\u90a3\u59d3\u5362\u7684\u6c49\u5b50\u867d\u662f\u5df2\u7ecf\u9000\u4e86\u4e09\u6b65\uff0c\u4ecd\u7136\u7acb\u8db3\u4e0d \u7a33\uff0c\u4e00\u8de4\u6454\u5012\uff0c\u8dcc\u4e86\u4e2a\u56db\u811a\u671d\u5929\u3002 \u3000\u3000\u53f2\u7528\u5a01\u5927\u6012\uff0c\u767b\u65f6\u79bb\u5ea7\u800c\u8d77\uff0c\u7528\u81ea\u5df2\u7684\u8eab\u4f53\u63a9\u62a4\u90a3\u59d3\u5362\u7684\u6c49\u5b50\uff0c\u53cc\u638c\u4e00\u7acb\uff0c\u559d\u9053\uff1a\u201c\u4f60 \u4eec\u4e24\u4eba\u662f\u6765\u7ed9\u6211\u62dc\u5bff\u7684\u8fd8\u662f\u7ed9\u6211\u6363\u4e71\u7684\uff1f\u597d\uff0c\u4f60\u4eec\u773c\u4e2d\u6ca1\u6709\u6211\u53f2\u7528\u5a01\uff0c\u90a3\u5c31\u5212\u51fa\u9053\u513f\u6765\u5427\uff01 \u6b66\u5f53\u56db\u4fa0\uff0c\u8bf7\u4f60\u4eec\u4e5f\u7ad9\u8fc7\u4e00\u8fb9\uff0c\u8001\u673d\u82e5\u662f\u4e0d\u6210\uff0c\u4f60\u4eec\u66ff\u6211\u62a5\u4ec7\u4fbf\u662f\uff01\u201d\u8fd9\u8bdd\u5373\u662f\u4ed6\u8981\u628a\u4e8b\u60c5 \u4e00\u80a1\u513f\u63fd\u5230\u81ea\u5df1\u8eab\u4e0a\u3002 \u3000\u3000\u8bf8\u3001\u8c37\u4e8c\u4eba\u89c1\u53f2\u7528\u5a01\u51fa\u5934\uff0c\u201c\u5929\u96f7\u529f\u201d\u53ea\u597d\u6536\u56de\u4e0d\u53d1\u3002\u891a\u4e91\u5cf0\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u53f2\u8001\u82f1\u96c4\u8bf7 \u522b\u8bef\u4f1a\uff0c\u8bf7\u542c\u6211\u4eec\u628a\u8bdd\u8bf4\u660e\u3002\u201d \u3000\u3000\u53f2\u7528\u5a01\u9053\uff1a\u201c\u597d\uff0c\u8001\u673d\u4e5f\u662f\u6b63\u60f3\u77e5\u9053\u771f\u76f8\uff0c\u4f46\u4e0d\u80fd\u53ea\u51ed\u4f60\u4eec\u7684\u8bf4\u8bdd\u3002\u5f20\u4e09\u4fa0\uff0c\u8fd9\u4f4d\u59d3\u8c37 \u7684\u670b\u53cb\u7a76\u7adf\u662f\u4ec0\u4e48\u8def\u9053\uff1f\u201d\u5f20\u5143\u5409\u8bf7\u53f2\u7528\u5a01\u5e2e\u5fd9\u4ed6\u6253\u542c\u8c37\u6db5\u865a\u7684\u884c\u8e2a\u4e4b\u65f6\uff0c\u7528\u7684\u662f\u201c\u670b\u53cb\u201d \u4e8c\u5b57\uff0c\u73b0\u5728\u5374\u548c\u8c37\u6db5\u8654\u52a8\u624b\u8d77\u6765\uff0c\u662f\u4ee5\u53f2\u7528\u5a01\u4e5f\u89c9\u5f97\u6709\u70b9\u8e4a\u8df7\u4e86\u3002 \u3000\u3000\u5f20\u5143\u5409\u6068\u6068\u8bf4\u9053\uff1a\u201c\u5b9e\u4e0d\u76f8\u7792\uff0c\u8fd9\u59d3\u8c37\u7684\u4e0e\u6211\u6709\u593a\u59bb\u4e4b\u4ec7\uff0c\u6298\u5251\u4e4b\u6068\uff0c\u4ed6\u4e0d\u4f46\u662f\u6211\u7684\u4ec7 \u4eba\uff0c\u4e5f\u662f\u6211\u4eec\u6b66\u5f53\u6d3e\u7684\u516c\u654c\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u5f20\u5143\u5409\uff0c\u54b1\u4eec\u7684\u5e10\u6162\u4e00\u6b65\u518d\u7b97\uff01\u53f2\u8001\u82f1\u96c4\uff0c\u5b9e\u4e0d\u76f8\u7792\uff0c\u6211\u4eec\u6765\u6b64\uff0c\u5e76\u975e\u4e3a \u4e86\u4ed6\u4eec\u56db\u4eba\uff0c\u800c\u662f\u51b2\u7740\u8fd9\u4f4d\u2018\u5362\u4e09\u7237\u2019\u6765\u7684\uff01\u201d \u3000\u3000\u53f2\u7528\u5a01\u5927\u5403\u4e8c\u60ca\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u539f\u6765\u4ed6\u662f\u6b66\u5f53\u6d3e\u7684\u4ec7\u4eba\u3002\u4f46\u6b66\u5f53\u6d3e\u662f\u6b66\u6797\u4e2d\u7684\u6cf0\u5c71\u5317 \u6597\uff0c\u6b66\u5f53\u6d3e\u7684\u4ec7\u4eba\u81ea\u8be5\u8ba9\u4ed6\u4eec\u6b66\u5f53\u56db\u4fa0\u5bf9\u4ed8\uff0c\u6211\u82e5\u63d2\u624b\uff0c\u90a3\u5c31\u53cd\u800c\u4e0d\u5408\u6b66\u6797\u89c4\u77e9\u4e86\u3002\u4f46\u8fd9\u4ef6 \u4e8b\u6211\u53ef\u4ee5\u8896\u624b\u65c1\u89c2\uff0c\u5362\u4e09\u54e5\u7684\u4e8b\u6211\u53ef\u4e0d\u80fd\u4e0d\u7ba1\uff01\u201d \u3000\u3000\u6b64\u65f6\u90a3\u59d3\u5362\u7684\u6c49\u5b50\u4e1a\u5df2\u722c\u8d77\u8eab\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u53f2\u5927\u54e5\uff0c\u522b\u542c\u4ed6\u4eec\u80e1\u8bf4\u516b\u9053\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u51b7\u7b11\u9053\uff1a\u201c\u6211\u8fd8\u672a\u66fe\u8bf4\u8bdd\u5462\uff0c\u4f60\u600e\u4e48\u77e5\u9053\u6211\u662f\u80e1\u8bf4\u516b\u9053\uff1f\u201d \u3000\u3000\u53f2\u7528\u5a01\u9053\uff1a\u201c\u597d\uff0c\u4f60\u8981\u8bf4\u4ec0\u4e48\uff0c\u8bf4\u5427\uff01\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u8bf7\u95ee\u8fd9\u4f4d\u2018\u5362\u4e09\u7237\u2019\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u53f2\u7528\u5a01\u9053\uff0c\u201c\u662f\u8001\u592b\u7684\u516b\u62dc\u4e4b\u4ea4\uff0c\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u8bf7\u95ee\u4ed6\u662f\u4e0d\u662f\u5728\u53bb\u5e74\u66fe\u7ed9\u53f2\u8001\u82f1\u96c4\u63a8\u8350\u4e00\u4f4d\u9ad8\u5f92\uff1f\u201d \u3000\u3000\u53f2\u7528\u5a01\u9053\uff1a\u201c\u662f\u53c8\u600e\u6837\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u8bf7\u90a3\u4f4d\u9ad8\u5f92\u51fa\u6765\uff01\u201d \u3000\u3000\u59d3\u5362\u90a3\u6c49\u5b50\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u539f\u6765\u4f60\u4eec\u662f\u8981\u8ba4\u8bc6\u6211\u7684\u4e16\u4f84\u5218\u5927\u4e3a\u5417\uff1f\u5927\u4e3a\u51fa\u6765\uff0c\u95ee\u95ee \u4ed6\u4eec\u627e\u4f60\u4f55\u4e8b\uff1f\u201d\u6b64\u8a00\u4e00\u51fa\uff0c\u891a\u4e91\u5cf0\u8fd9\u4e00\u60ca\u5f53\u771f\u662f\u975e\u540c\u5c0f\u53ef\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u96be\u9053\u5218\u5927\u4e3a\u7adf\u662f \u5978\u7ec6\uff1f\u4f46\u8fd9\u662f\u7edd\u4e0d\u53ef\u80fd\u7684\u4e8b\u5440\uff01\u201d\u56e0\u4e3a\u5218\u5927\u4e3a\u672c\u6765\u5c31\u662f\u4e49\u519b\u4e2d\u7684\u4e00\u4e2a\u91cd\u8981\u4eba\u7269\uff0c\u4ed6\u82e5\u7136\u5f53\u771f \u662f\u548c\u91d1\u864f\u79c1\u901a\uff0c\u7559\u5728\u4e49\u519b\u4e4b\u4e2d\u5c82\u4e0d\u66f4\u597d\uff1f\u4f55\u987b\u6df7\u8fdb\u53ea\u4e0d\u8fc7\u662f\u4e49\u519b\u5916\u56f4\u4eba\u7269\u7684\u53f2\u7528\u5a01\u5bb6\u4e2d\uff1f \u3000\u3000\u5218\u5927\u4e3a\u6bd4\u891a\u4e91\u5cf0\u5403\u60ca\u66f4\u751a\uff0c\u5931\u58f0\u53eb\u9053\uff1a\u201c\u891a\u5144\uff0c\u4e0d\u5bf9\u5427\uff01\u201d\u4ed6\u5df2\u7ecf\u731c\u60f3\u5f97\u5230\uff0c\u891a\u4e91\u5cf0\u6240 \u8981\u627e\u7684\u201c\u90a3\u4e2a\u4eba\u201d\u5c31\u662f\u59d3\u5362\u8fd9\u4eba\u4e86\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5362\u4e09\u7237\u600e\u4f1a\u662f\u5978\u7ec6\u5462\uff1f\u201d \u3000\u3000\u53f2\u7528\u5a01\u8be7\u9053\uff1a\u201c\u54a6\uff0c\u4f60\u4eec\u662f\u65e9\u5c31\u76f8\u8bc6\u7684\u5417\uff1f\u201d \u3000\u3000\u59d3\u5362\u7684\u90a3\u6c49\u5b50\u53c8\u662f\u54c8\u54c8\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u65e9\u5c31\u76f8\u8bc6\uff0c\u90a3\u5c31\u66f4\u597d\u529e\u4e86\u3002\u5927\u4e3a\uff0c\u544a\u8bc9\u4ed6\u4eec \u6211\u662f\u4ec0\u4e48\u4eba\u5427\uff0c\u7701\u5f97\u4ed6\u4eec\u5230\u5904\u627e\u4eba\u6253\u542c\u3002\u201d \u3000\u3000\u5218\u5927\u4e3a\u9053\uff1a\u201c\u8fd9\u4f4d\u5362\u4e09\u7237\u662f\u5bb6\u5e08\u7684\u516b\u62dc\u4e4b\u4ea4\uff0c\u4e5f\u662f\u548c\u5c0f\u5f1f\u6709\u901a\u5bb6\u4e4b\u597d\u7684\u4e16\u53d4\u3002\u5317\u4e94\u7701\u7684 \u4fa0\u4e49\u9053\u591a\u534a\u548c\u4ed6\u76f8\u8bc6\uff0c\u6df1\u77e5\u4ed6\u7684\u4e3a\u4eba\u3002\u201d\u6700\u540e\u8fd9\u4e24\u53e5\u8bdd\u4e0d\u557b\u662f\u5411\u891a\u4e91\u5cf0\u6697\u793a\uff1a\u8fd9\u4f4d\u201c\u5362\u5927 \u7237\u201d\u51b3\u4e0d\u53ef\u80fd\u5c31\u662f\u4ed6\u6240\u8981\u627e\u7684\u90a3\u4e2a\u5978\u4eba\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u4ed4\u7ec6\u518d\u671b\u4e86\u90a3\u4e2a\u201c\u5362\u4e09\u7237\u201d\u4e00\u773c\uff0c\u4e00\u70b9\u4e0d\u9519\uff0c\u786e\u5b9e\u5c31\u662f\u90a3\u65e5\u5728\u9633\u5929\u96f7\u7684\u5bc6\u5ba4\u91cc\u66fe \u89c1\u8fc7\u7684\u90a3\u4e2a\u4eba\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u601d\u7591\u4e0d\u5b9a\uff0c\u60f3\u4e86\u4e00\u60f3\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u5362\u4e09\u7237\u9664\u4e86\u5f15\u8350\u5218\u5144\u4e4b\u5916\uff0c\u662f\u5426\u8fd8\u6709\u522b \u4eba\uff1f\u201d\u5218\u5927\u4e3a\u7709\u5934\u4e00\u76b1\uff0c\u8bf4\u9053\uff1a\u201c\u636e\u6211\u6240\u77e5\uff0c\u4f3c\u4e4e\u5e76\u65e0\u522b\u4f4d\u540c\u95e8\u662f\u5362\u4e09\u7237\u5f15\u8350\u7684\u4e86\uff01\u201d\u6b63 \u662f\uff1a \u3000\u3000\u96be\u731c\u8986\u96e8\u7ffb\u4e91\u624b\uff0c\u4e0d\u89c9\u7591\u4e91\u6697\u6697\u751f\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b\u2014\u2014\u7b2c\u56db\u5341\u4e03\u56de\u3000\u96be\u6d88\u5bbf\u6028\u6392\u51f6\u9635\u3000\u4e3a\u91ca\u7591\u56e2\u8868\u5bf8\u5fc3 \u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b \u7b2c\u56db\u5341\u4e03\u56de\u3000\u96be\u6d88\u5bbf\u6028\u6392\u51f6\u9635\u3000\u4e3a\u91ca\u7591\u56e2\u8868\u5bf8\u5fc3 \u3000\u3000\u53f2\u7528\u5a01\u542c\u4e86\u8fd9\u8bdd\uff0c\u5374\u662f\u4e0d\u7981\u5fc3\u5934\u4e00\u52a8\u3002\u539f\u6765\u5728\u4ed6\u53bb\u5e74\u6240\u6536\u7684\u516d\u4e2a\u5f1f\u5b50\u4e4b\u4e2d\uff0c\u9664\u4e86\u5218\u5927\u4e3a \u4e4b\u5916\uff0c\u7684\u786e\u8fd8\u6709\u4e00\u4eba\u662f\u7531\u4e8e\u8fd9\u4f4d\u201c\u5362\u4e09\u7237\u201d\u7684\u5173\u7cfb\u624d\u80fd\u5217\u5165\u4ed6\u7684\u95e8\u5899\uff0c\u4e0d\u8fc7\u540d\u4e49\u4e0a\u7684\u4ecb\u7ecd\u4eba \u4e0d\u662f\u201c\u5362\u4e09\u7237\u201d\u800c\u5df2\u3002 \u3000\u3000\u8fd9\u4f4d\u201c\u5362\u4e09\u7237\u201d\u540d\u53eb\u5362\u9999\u4ead\uff0c\u548c\u53f2\u7528\u5a01\u4e43\u662f\u516b\u62dc\u4e4b\u4ea4\uff0c\u4ed6\u5728\u5317\u4e94\u7701\u7684\u4ea4\u6e38\u5e7f\u9614\uff0c\u4e5f\u4e0d\u5728 \u53f2\u7528\u5a01\u4e4b\u4e0b\u3002\u5362\u9999\u4ead\u6709\u4e2a\u5e08\u5f1f\uff0c\u540d\u53eb\u4e01\u7ecd\uff0c\u4e24\u4eba\u5e76\u975e\u4e00\u5e08\u6240\u6388\uff0c\u53ea\u662f\u540c\u51fa\u4e00\u95e8\u7684\u5e08\u5144\u5f1f\u3002\u5362 \u9999\u4ead\u7684\u5e08\u53d4\u4f2f\u5f88\u591a\uff0c\u53f2\u7528\u5a01\u4e5f\u5f04\u4e0d\u6e05\u695a\u4e01\u7ecd\u662f\u54ea\u4e00\u4f4d\u5e08\u4f2f\u6216\u5e08\u53d4\u7684\u5f1f\u5b50\u3002\u53f2\u7528\u5a01\u5c1a\u4e14\u5f04\u4e0d\u6e05 \u695a\uff0c\u65c1\u7684\u4eba\u77e5\u9053\u4ed6\u4eec\u662f\u5e08\u5144\u5f1f\u7684\u5c31\u66f4\u662f\u5be5\u5be5\u65e0\u51e0\u4e86\u3002 \u3000\u3000\u4e01\u7ecd\u5728\u6b66\u6797\u4e2d\u7684\u58f0\u540d\u8fdc\u4e0d\u53ca\u5362\u9999\u4ead\u4e4b\u5927\uff0c\u548c\u53f2\u7528\u5a01\u7684\u4ea4\u60c5\u4e5f\u8fdc\u4e0d\u53ca\u5362\u9999\u4ead\u4e4b\u6df1\uff0c\u4ed6\u6709\u4e2a \u4f84\u513f\u540d\u53eb\u4e01\u8fdb\uff0c\u60f3\u62dc\u5728\u53f2\u7528\u5a01\u95e8\u4e0b\uff0c\u6050\u6015\u81ea\u5df1\u4ea4\u60c5\u4e0d\u591f\uff0c\u8bf7\u5362\u9999\u4ead\u4ee3\u4e3a\u8bf4\u9879\u3002 \u3000\u3000\u8fd9\u4e2a\u4e01\u8fdb\u5c31\u662f\u5362\u9999\u4ead\u4e0e\u9633\u5929\u96f7\u5bc6\u5ba4\u5b9a\u8c0b\u6240\u8981\u627c\u9053\u7684\u90a3\u4e2a\u5230\u53f2\u5bb6\u201c\u5367\u5e95\u201d\u7684\u5978\u7ec6\u4e86\uff0c\u5f53\u7136 \u53f2\u7528\u5a01\u662f\u5e76\u4e0d\u77e5\u9053\uff0c\u751a\u81f3\u8fde\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u7684\u3002 \u3000\u3000\u5362\u9999\u4ead\u4e5f\u5f53\u771f\u662f\u72e1\u733e\u65e0\u6bd4\uff0c\u4ed6\u662f\u4ee5\u4f2a\u88c5\u7684\u201c\u4fa0\u4e49\u9053\u201d\u5728\u6b66\u6797\u4e2d\u6d3b\u52a8\u7684\uff0c\u548c\u4e49\u519b\u7684\u9886\u8896\u4e5f \u9887\u6709\u4ea4\u60c5\u3002\u4ed6\u77e5\u9053\u4e49\u519b\u8981\u6d3e\u4e00\u4e2a\u4eba\u5145\u5f53\u53f2\u7528\u5a01\u7684\u5f1f\u5b50\uff0c\u4f5c\u4e24\u8005\u4e4b\u95f4\u7684\u8054\u7edc\uff0c\u800c\u4ed6\u53c8\u4e0e\u5218\u5927\u4e3a \u6709\u901a\u5bb6\u4e4b\u597d\uff0c\u7b97\u662f\u5218\u5927\u529b\u7684\u201c\u4e16\u53d4\u201d\uff0c\u4e8e\u662f\u4fbf\u6168\u7136\u4ee5\u5218\u5927\u4e3a\u7684\u4ecb\u7ecd\u4eba\u81ea\u4efb\uff0c\u5c06\u5218\u5927\u4e3a\u8350\u7ed9\u53f2 \u7528\u5a01\u4f5c\u5f1f\u5b50\u3002\u81f3\u4e8e\u4e01\u8fdb\uff0c\u867d\u7136\u662f\u7ecf\u4ed6\u8bf4\u9879\u65b9\u80fd\u5217\u5165\u53f2\u5bb6\u95e8\u5899\uff0c\u4f46\u540d\u4e49\u4e0a\u7684\u4ecb\u7ecd\u5374\u4e0d\u662f\u4ed6\uff0c\u800c \u662f\u4e01\u8fdb\u7684\u53d4\u53d4\u4e01\u7ecd\u3002\u56e0\u6b64\u4e01\u8fdb\u5165\u53f2\u5bb6\u7684\u539f\u59d4\uff0c\u53ea\u6709\u53f2\u7528\u5a01\u81ea\u5df1\u660e\u767d\uff0c\u5218\u5927\u4e3a\u662f\u5e76\u4e0d\u77e5\u9053\u7684\u3002 \u4e0d\u8fc7\u53f2\u7528\u5a01\u542c\u4e86\u891a\u4e91\u5cf0\u7684\u8bf4\u8bdd\uff0c\u867d\u7136\u662f\u4e0d\u7981\u5fc3\u4e2d\u4e00\u52a8\uff0c\u4f46\u56e0\u4ed6\u548c\u5362\u9999\u4ead\u6709\u6570\u5341\u5e74\u7684\u4ea4\u60c5\uff0c\u6839 \u672c\u5c31\u4e0d\u4f1a\u60f3\u5230\u4ed6\u8fd9\u4f4d\u62dc\u5f1f\u662f\u91d1\u864f\u7684\u5978\u7ec6\uff0c\u66f4\u4e0d\u4f1a\u6000\u7591\u4ed6\u628a\u4e01\u8fdb\u4ecb\u7ecd\u8fdb\u6765\u662f\u5bf9\u81ea\u5df1\u6709\u751a\u9634\u8c0b\u3002 \u800c\u891a\u4e91\u5cf0\u4e43\u662f\u4e00\u4e2a\u201c\u6765\u5386\u4e0d\u660e\u201d\u7684\u5916\u4eba\uff0c\u4ed6\u5f53\u7136\u662f\u4e0d\u4f1a\u628a\u8fd9\u4ef6\u4e8b\u60c5\u5bf9\u891a\u4e91\u5cf0\u8bf4\u51fa\u6765\u4e86\u3002 \u3000\u3000\u4e14\u8bf4\u5362\u9999\u4ead\u5728\u5f97\u5230\u5218\u5927\u4e3a\u8bf4\u660e\u4e86\u4ed6\u7684\u8eab\u4efd\u4e4b\u540e\uff0c\u5f97\u610f\u6d0b\u6d0b\u5730\u4fbf\u5373\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u4f60\u8981\u6253\u542c \u6211\u662f\u4ec0\u4e48\u4eba\uff0c\u73b0\u5728\u4f60\u5df2\u7ecf\u77e5\u9053\u4e86\uff0c\u8be5\u8f6e\u5230\u6211\u95ee\u4f60\u5566\uff1f\u8bf7\u95ee\u4f60\u4e3a\u4ec0\u4e48\u8981\u5230\u5904\u627e\u4eba\u67e5\u95ee\u6211\u7684\u6839 \u5e95\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u601d\u7591\u4e0d\u5b9a\uff0c\u5fc3\u60f3\uff1a\u201c\u96be\u9053\u662f\u6211\u8ba4\u9519\u4e86\u4eba\uff1f\u201d\u4f46\u4ed6\u770b\u4e86\u53c8\u770b\uff0c\u8fd9\u4f4d\u201c\u5362\u4e09\u7237\u201d\u7684\u786e \u662f\u4ed6\u66fe\u7ecf\u5728\u9633\u5929\u96f7\u5bc6\u5ba4\u4e2d\u89c1\u8fc7\u7684\u90a3\u4e2a\u4eba\uff0c\u51b3\u4e0d\u4f1a\u9519\uff01 \u3000\u3000\u891a\u4e91\u5cf0\u628a\u5fc3\u4e00\u6a2a\uff0c\u6697\u81ea\u601d\u5fd6\uff1a\u201c\u4e8b\u5df2\u5982\u65af\uff0c\u4e5f\u53ea\u6709\u548c\u76d8\u6258\u51fa\u4e86\u3002\u201d\u5f53\u4e0b\u4e5f\u5c31\u8ddf\u7740\u51b7\u7b11\u8bf4 \u9053\uff1a\u201c\u5362\u4e09\u7237\uff0c\u54b1\u4eec\u662f\u66fe\u7ecf\u89c1\u8fc7\u9762\u7684\uff0c\u4f60\u4e0d\u8bb0\u5f97\u4e86\u4e48\uff1f\u201d \u3000\u3000\u5362\u9999\u4ead\u80dc\u7b97\u5728\u64cd\uff0c\u4e0d\u614c\u4e0d\u5fd9\u5730\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u6211\u89c1\u8fc7\u7684\u4eba\u4e0d\u77e5\u591a\u5c11\uff0c\u786e\u5b9e\u662f\u8bb0\u4e0d\u5f97\u4e86\u3002\u8bf7 \u95ee\u4f60\u662f\u5728\u54ea\u91cc\u89c1\u8fc7\u6211\u7684\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u91d1\u56fd\u56fd\u5e08\u9633\u5929\u96f7\u7684\u5bc6\u5ba4\u4e4b\u4e2d\uff01\u201d \u3000\u3000\u6b64\u8a00\u4e00\u51fa\uff0c\u6ee1\u5802\u5bbe\u5ba2\u7686\u662f\u5927\u5403\u4e00\u60ca\uff0c\u7247\u523b\u4e4b\u540e\uff0c\u9a82\u58f0\u56db\u8d77\uff0c\u4f46\u9a82\u7684\u5374\u4e0d\u662f\u5362\u9999\u4ead\u800c\u662f\u891a \u4e91\u5cf0\u3002\u56e0\u4e3a\u6ca1\u4eba\u76f8\u4fe1\u4ed6\u7684\u8bf4\u8bdd\u3002\u201c\u80e1\u8bf4\u516b\u9053\uff0c\u80e1\u8bf4\u516b\u9053\uff01\u5362\u4e09\u7237\u7109\u80fd\u4e0e\u9633\u5929\u96f7\u6709\u751a\u4ea4\u5f80\uff1f\u201d \u201c\u8fd9\u5c0f\u5b50\u542b\u8840\u55b7\u4eba\uff0c\u4e00\u5b9a\u4e0d\u662f\u597d\u4e1c\u897f\uff01\u201d \u3000\u3000\u891a\u4e91\u5cf0\u4e0d\u7406\u90a3\u4e9b\u4eba\u7684\u559d\u9a82\uff0c\u63d0\u9ad8\u4e86\u58f0\u97f3\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u4e0d\u4f46\u5982\u6b64\uff0c\u6211\u8fd8\u66fe\u542c\u5f97\u4ed6\u4eec\u5bc6\u5ba4\u5546 \u8bae\uff0c\u8981\u6d3e\u4e00\u4e2a\u5978\u7ec6\u6765\u53f2\u5bb6\u5367\u5e95\uff01\u8fd9\u4e2a\u5978\u7ec6\u662f\u53f2\u8001\u82f1\u96c4\u53bb\u5e74\u6240\u6536\u7684\u516d\u4e2a\u5f1f\u5b50\u4e4b\u4e2d\u7684\u4e00\u4e2a\uff01\u201d\u4ed6 \u7528\u4e0a\u4e86\u201c\u4f20\u97f3\u5165\u5bc6\u201d\u7684\u5185\u529f\uff0c\u5728\u5bbe\u5ba2\u4eec\u7684\u7eb7\u7eb7\u559d\u9a82\u58f0\u4e2d\uff0c\u4ecd\u662f\u6bcf\u4e00\u4e2a\u5b57\u90fd\u542c\u5f97\u5341\u5206\u6e05\u695a\u3002 \u3000\u3000\u4f46\u4ed6\u8bdd\u72b9\u672a\u4e86\uff0c\u53ea\u542c\u5f97\u4e00\u7247\u94ff\u94ff\u9535\u9535\u7684\u5200\u5251\u51fa\u9798\u4e4b\u58f0\uff0c\u53f2\u7528\u5a01\u7684\u5341\u516b\u4e2a\u5f1f\u5b50\uff0c\u9664\u4e86\u5218\u5927 \u4e3a\u4e00\u4eba\u4e4b\u5916\uff0c\u90fd\u62ff\u51fa\u4e86\u5175\u5668\uff0c\u5c06\u891a\u3001\u8c37\u4e8c\u4eba\u56f4\u5728\u5f53\u4e2d\u4e86\u3002 \u3000\u3000\u53f2\u7528\u5a01\u559d\u9053\uff1a\u201c\u4e14\u6162\uff0c\u5f85\u6211\u95ee\u4e2a\u6e05\u695a\uff01\u201d\u968f\u5373\u6307\u7740\u891a\u4e91\u5cf0\u51b7\u51b7\u8bf4\uff1a\u201c\u4f60\u8bf4\u5728\u9633\u5929\u96f7\u7684\u5bc6 \u5ba4\u4e4b\u4e2d\u89c1\u8fc7\u4ed6\u4eec\uff0c\u90a3\u4f60\u53c8\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u5c1a\u672a\u56de\u7b54\uff0c\u5362\u9999\u4ead\u5374\u5df2\u5728\u8bf4\u9053\uff1a\u201c\u8fd9\u8fd8\u7528\u95ee\uff0c\u4ed6\u80fd\u591f\u5728\u9633\u5929\u96f7\u7684\u5bc6\u5ba4\u4e4b\u4e2d\u8fdb\u51fa\uff0c \u5f53\u7136\u662f\u91d1\u864f\u7684\u9e70\u722a\u65e0\u7591\uff0c\u54fc\uff0c\u51ed\u4ed6\u521a\u624d\u663e\u9732\u7684\u90a3\u624b\u529f\u672a\uff0c\u6211\u5c31\u53ef\u4ee5\u77e5\u9053\u4ed6\u7684\u6765\u5386\uff01\u2026\u2026\u201d \u3000\u3000\u53f2\u7528\u5a01\u970d\u7136\u4e00\u7701\uff0c\u63d2\u53e3\u95ee\u9053\uff1a\u201c\u4ed6\u7528\u7684\u53ef\u662f\u5929\u96f7\u529f\uff1f\u201d \u3000\u3000\u5362\u9999\u4ead\u9053\uff1a\u201c\u4e00\u70b9\u4e0d\u9519\u3002\u542c\u8bf4\u9633\u5929\u96f7\u624b\u4e0b\u6709\u4e2a\u5e08\u4f84\uff0c\u540d\u53eb\u8bf8\u4e91\u5cf0\uff0c\u60f3\u5fc5\u5c31\u662f\u4ed6\u4e86\u3002\u201d \u3000\u3000\u53f2\u7528\u5a01\u9053\uff1a\u201c\u5927\u4e3a\uff0c\u4f60\u8ba4\u8bc6\u4ed6\uff0c\u4ed6\u662f\u4e0d\u662f\u53eb\u891a\u4e91\u5cf0\uff1f\u201d \u3000\u3000\u5218\u5927\u4e3a\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f46\uff0c\u4e0d\u8fc7\u2026\u2026\u201d\u53f2\u7528\u5a01\u9053\uff1a\u201c\u4e0d\u8fc7\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u4ea2\u58f0\u8bf4\u9053\uff1a\u201c\u5927\u4e08\u592b\u884c\u4e0d\u66f4\u540d\uff0c\u5750\u4e0d\u6539\u59d3\uff0c\u4e0d\u9519\uff0c\u6211\u5c31\u662f\u891a\u4e91\u5cf0\uff0c\u4f46\u6211\u662f\u4e49\u519b\u7684 \u670b\u53cb\uff0c\u5e76\u975e\u91d1\u864f\u7684\u9e70\u72ac\uff01\u91d1\u864f\u7684\u9e70\u72ac\u6b63\u662f\u8fd9\u4f4d\u2018\u5362\u4e09\u7237\u2019\uff01\u201d \u3000\u3000\u4e01\u8fdb\u6df7\u5728\u4eba\u4e1b\u4e4b\u4e2d\u559d\u9053\uff1a\u201c\u4e0d\u80fd\u8ba9\u8fd9\u5c0f\u5b50\u542b\u8840\u55b7\u4eba\uff0c\u8bec\u8511\u54b1\u4eec\u5e08\u7236\u516b\u62dc\u4e4b\u4ea4\u7684\u5362\u4e09\u53d4\uff01 \u5927\u4f19\u513f\u628a\u4ed6\u5bb0\u4e86\uff01\u201d \u3000\u3000\u5218\u5927\u4e3a\u8fde\u5fd9\u53eb\u9053\uff1a\u201c\u4e14\u6162\uff0c\u4e14\u6162\uff01\u201d \u3000\u3000\u53f2\u7528\u5a01\u9053\uff1a\u201c\u597d\uff0c\u5927\u4e3a\u4f60\u8bf4\uff0c\u8fd9\u59d3\u891a\u7684\u8bdd\u6709\u51e0\u5206\u662f\u771f\uff1f\u51e0\u5206\u662f\u5047\uff1f\u201d\u5e78\u4e8f\u5218\u5927\u4e3a\u5728\u4e49\u519b \u7684\u8eab\u4efd\u662f\u4e00\u4f17\u540c\u95e8\u90fd\u77e5\u9053\u7684\uff0c\u800c\u4ed6\u53c8\u662f\u5362\u9999\u4ead\u6240\u8350\u7684\u4eba\uff0c\u53f2\u7528\u5a01\u548c\u4e00\u4f17\u5f1f\u5b50\u65b9\u624d\u5bf9\u4ed6\u6ca1\u6709\u6000 \u7591\uff0c\u80af\u542c\u4ed6\u7684\u8bf4\u8bdd\u3002 \u3000\u3000\u5218\u5927\u4e3a\u8bb7\u8bb7\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u891a\u5144\u662f\u8bf4\u5f97\u4e0d\u891a\uff0c\u4ed6\u7684\u786e\u662f\u8868\u9762\u66ff\u9633\u5929\u96f7\u505a\u4e8b\uff0c\u6697\u4e2d\u5374\u5e2e\u52a9\u4e49 \u519b\u7684\u597d\u670b\u53cb\u3002\u4f46\u4ed6\u8bf4\u5362\u4e09\u7237\u548c\u9633\u5929\u96f7\u52fe\u7ed3\uff0c\u8fd9\u5374\u6050\u6015\u3001\u6050\u6015\u662f\u4ed6\u770b\u9519\u4eba\u4e86\uff01\u201d \u3000\u3000\u4e01\u8fdb\u51b7\u7b11\u9053\uff1a\u201c\u4ed6\u8bec\u8511\u5362\u4e09\u7237\u7684\u8bdd\u5f53\u7136\u662f\u5047\u7684\uff01\u5426\u5219\u4f60\u5218\u5927\u4e3a\u5c82\u975e\u4e5f\u662f\u5978\u7ec6\u4e86\uff1f\u201d \u3000\u3000\u5362\u9999\u4ead\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u5927\u4e3a\u8bf4\u7684\u5012\u4e5f\u6ca1\u9519\uff0c\u8fd9\u4e2a\u891a\u4e91\u5cf0\u7684\u786e\u662f\u66fe\u7ecf\u548c\u4e49\u519b\u6709\u8fc7\u6765\u5f80\u7684\uff0c\u4e0d \u8fc7\uff0c\u4ed6\u5374\u5e76\u4e0d\u662f\u4e49\u519b\u7684\u670b\u53cb\u3002\u5927\u4e3a\uff0c\u4f60\u4eec\u90fd\u4e0a\u4e86\u4ed6\u7684\u5f53\u4e86\uff01\u201d \u3000\u3000\u5218\u5927\u4e3a\u5927\u60ca\u9053\uff1a\u201c\u90a3\u4e48\u4ed6\u662f\u4ec0\u4e48\u4eba\uff1f\u201d\u8981\u77e5\u4ed6\u867d\u7136\u5bf9\u891a\u4e91\u5cf0\u7684\u5e95\u7ec6\u77e5\u9053\u5f97\u5341\u5206\u6e05\u695a\uff0c\u4f46 \u4ed6\u5bf9\u5362\u9999\u4ead\u4e5f\u662f\u4e0d\u6562\u6709\u6240\u6000\u7591\uff0c\u56e0\u6b64\u5728\u4ed6\u542c\u4e86\u4ed6\u7684\u8bf4\u8bdd\u4e4b\u540e\uff0c\u5c31\u4e0d\u7531\u5f97\u5927\u4e3a\u60f6\u60d1\uff01 \u3000\u3000\u53f2\u7528\u5a01\u5374\u662f\u5927\u559c\u9053\uff1a\u201c\u5bf9\u5566\uff0c\u4e09\u54e5\uff0c\u4f60\u7684\u6d88\u606f\u4e00\u5411\u7075\u901a\uff0c\u60f3\u5fc5\u662f\u5df2\u7ecf\u67e5\u6e05\u695a\u4e86\u4ed6\u7684\u6765 \u5386\uff1f\u201d \u3000\u3000\u5362\u9999\u4ead\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u53bb\u5e74\u6211\u5728\u5927\u90fd\uff0c\u5df2\u7ecf\u6478\u6e05\u695a\u4e86\u4ed6\u7684\u5e95\u7ec6\u3002\u4ed6\u7528\u7684\u662f\u82e6\u8089\u8ba1\uff0c \u5047\u88c5\u80cc\u53db\u9633\u5929\u96f7\u4ee4\u5f97\u4e49\u519b\u76f8\u4fe1\u4ed6\u7684\uff0c\u5176\u5b9e\u4ed6\u59cb\u7ec8\u90fd\u662f\u91d1\u864f\u7684\u9e70\u72ac\uff01\u201d \u3000\u3000\u5362\u9999\u4ead\u7684\u786e\u4e0d\u6127\u662f\u4e2a\u8001\u5978\u5de8\u733e\u4e4b\u8f88\uff0c\u53cd\u54ac\u4e00\u53e3\u628a\u891a\u4e91\u5cf0\u8bf4\u6210\u4e86\u4e00\u4e2a\u4e24\u9762\u4e09\u5200\u4e4b\u4eba\uff0c\u8fd9\u6837 \u4e00\u6765\uff0c\u5c31\u662f\u5218\u5927\u4e3a\u8981\u66ff\u891a\u4e91\u5cf0\u8fa9\u62a4\uff0c\u522b\u4eba\u4e5f\u4e0d\u4f1a\u76f8\u4fe1\uff0c\u53ea\u5f53\u5218\u5927\u4e3a\u662f\u53d7\u4e86\u7792\u9a97\u7684\u4e86\u3002 \u3000\u3000\u5f53\u7136\u5362\u9999\u4ead\u7684\u8bf4\u8bdd\u4e4b\u4e2d\u8fd8\u662f\u6709\u8bb8\u591a\u7834\u7efd\u7684\uff0c\u4f46\u5728\u7fa4\u60c5\u6c79\u6d8c\u4e4b\u4e0b\uff0c\u8c01\u4e5f\u4e0d\u4f1a\u90a3\u6837\u7ec6\u5fc3\u7684\u63a8 \u6572\uff0c\u5f53\u4e0b\u4eba\u4eba\u559d\u9a82\uff0c\u767b\u65f6\u5c31\u628a\u8bf8\u4e91\u5cf0\u548c\u8c37\u6db5\u865a\u5305\u56f4\u8d77\u6765\uff0c\u53ea\u7b49\u53f2\u7528\u5a01\u4e00\u58f0\u4ee4\u4e0b\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u662f\u4ec0\u4e48\u4eba\u548c\u4f60\u8bf4\u7684\uff1f\u4f60\u65e2\u7136\u77e5\u9053\u6211\u6000\u6709\u9634\u8c0b\uff0c\u4e3a\u4f55\u53c8\u4e0d\u65e9\u53bb\u901a\u77e5\u4e49 \u519b\uff1f\u201d\u8fd9\u4e24\u4e2a\u95ee\u9898\u6b63\u662f\u51fb\u4e2d\u4e86\u5362\u9999\u4ead\u7684\u8981\u5bb3\uff0c\u4f46\u53ef\u60dc\u4f17\u4eba\u90fd\u53d7\u4e86\u5362\u9999\u4ead\u201c\u5148\u5165\u4e3a\u4e3b\u201d\u7684\u8bf4\u8bdd \u5f71\u54cd\uff0c\u6012\u9a82\u5982\u96f7\uff0c\u628a\u4ed6\u7684\u58f0\u97f3\u90fd\u63a9\u76d6\u8fc7\u4e86\uff0c\u6839\u672c\u5c31\u4e0d\u60f3\u542c\u4ed6\u7684\u8bf4\u8bdd\u3002 \u3000\u3000\u4e01\u8fdb\u53eb\u9053\uff1a\u201c\u5e08\u7236\u7684\u5bff\u5802\u5c82\u80fd\u5bb9\u5f97\u8fd9\u4e24\u4e2a\u5978\u7ec6\u6df7\u5165\uff0c\u628a\u4ed6\u4eec\u4e71\u5200\u5bb0\u4e86\uff01\u201d \u3000\u3000\u53f2\u7528\u5a01\u9053\uff1a\u201c\u597d\uff0c\u5148\u628a\u4ed6\u4eec\u62ff\u4e0b\u518d\u8bf4\uff01\u201d\u53f2\u7528\u5a01\u662f\u4e2a\u6bd4\u8f83\u7ec6\u5fc3\u7684\u4eba\uff0c\u867d\u7136\u4ed6\u5bf9\u5362\u9999\u4ead\u4e5f \u662f\u4e0d\u6562\u6709\u6240\u6000\u7591\uff0c\u4f46\u5374\u9690\u9690\u611f\u5230\u4e8b\u60c5\u6709\u70b9\u8e4a\u8df7\uff0c\u662f\u4ee5\u60f3\u628a\u891a\u3001\u8c37\u4e8c\u4eba\u64d2\u4e0b\u4e4b\u540e\uff0c\u518d\u627e\u4eba\u8bc1\u5bf9 \u8d28\u3002 \u3000\u3000\u4e00\u58f0\u4ee4\u4e0b\uff0c\u53f2\u7528\u5a01\u7684\u5341\u516d\u540d\u5f1f\u5b50\uff0c\u9664\u4e86\u5218\u5927\u4e3a\u4e4b\u5916\u90fd\u4eae\u51fa\u4e86\u5175\u5200\uff0c\u4e00\u62e5\u800c\u4e0a\u3002\u6b66\u5f53\u56db\u5927 \u5f1f\u5b50\u4e5f\u90fd\u62d4\u5251\u51fa\u9798\uff0c\u5835\u585e\u4e86\u4ed6\u4eec\u7684\u9000\u8def\uff0c\u9632\u5907\u4ed6\u4eec\u7a81\u56f4\u800c\u51fa\u3002 \u3000\u3000\u662f\u675f\u624b\u53d7\u64d2\u8fd8\u662f\u594b\u8d77\u4e00\u6218\uff1f\u675f\u624b\u53d7\u64d2\u56fa\u7136\u53ef\u4ee5\u907f\u514d\u548c\u53f2\u7528\u5a01\u51b2\u7a81\uff0c\u4f46\u53ea\u6015\u4ed6\u4eec\u867d\u7136\u4e0d\u52a0 \u62b5\u5fa1\uff0c\u53f2\u95e8\u5f1f\u5b50\u4e4b\u4e2d\u4ecd\u662f\u6709\u4eba\u628a\u4ed6\u4eec\u7f6e\u4e4b\u6b7b\u5730\u7684\uff0c\u4e01\u8fdb\u90a3\u660e\u6643\u6643\u7684\u5229\u5200\u5c31\u5df2\u7ecf\u582a\u582a\u8981\u65ab\u5230\u891a \u4e91\u5cf0\u7684\u8eab\u4e0a\u4e86\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u84e6\u5730\u5fc3\u5934\u4e00\u52a8\uff1a\u201c\u8fd9\u53ae\u4e3a\u4f55\u7279\u522b\u51f6\u6076\uff1f\u201d\u5fc3\u5934\u4e00\u52a8\uff0c\u767b\u65f6\u51fa\u624b\uff0c\u4e00\u62db\u5927\u64d2\u62ff\u624b\u8fc5 \u5982\u95ea\u7535\uff0c\u6293\u7740\u4e86\u4e01\u8fdb\u7684\u624b\u8155\uff0c\u628a\u4ed6\u4e3e\u4e86\u8d77\u6765\u3002 \u3000\u3000\u53ea\u542c\u5f97\u4e00\u7247\u53ee\u53ee\u94db\u94db\u7684\u58f0\u54cd\uff0c\u539f\u6765\u53f2\u95e8\u7684\u90a3\u51e0\u5341\u4e2a\u5f1f\u5b50\u6765\u4e0d\u53ca\u6536\u624b\uff0c\u5175\u5203\u90fd\u5df2\u671d\u7740\u4ed6\u4eec \u65ab\u6765\u4e86\u3002\u53f2\u7528\u5a01\u548c\u6b66\u5f53\u56db\u5927\u5f1f\u5b50\u6050\u6015\u4f24\u4e86\u4e01\u8fdb\uff0c\u8fde\u5fd9\u51fa\u5251\u683c\u4f4f\uff0c\u53cd\u800c\u4e0d\u7528\u891a\u3001\u8c37\u4e8c\u4eba\u51fa\u624b \u4e86\u3002 \u3000\u3000\u53f2\u7528\u5a01\u559d\u9053\uff1a\u201c\u628a\u6211\u5f92\u513f\u653e\u4e0b\uff01\u201d\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u53f2\u8001\u82f1\u96c4\u6709\u547d\uff0c\u5c82\u6562\u4e0d\u4ece\uff1f\u4f46\u4f60\u8fd9\u4e2a\u5f92 \u5f1f\u7684\u6765\u5386\u5982\u4f55\uff0c\u548c\u8fd9\u4f4d\u5362\u4e09\u7237\u6709\u5426\u5173\u8fde\uff1f\u6211\u4eec\u5f88\u60f3\u77e5\u9053\uff0c\u5c1a\u76fc\u8001\u82f1\u96c4\u4e0d\u541d\u89c1\u544a\uff01\u201d \u3000\u3000\u53f2\u7528\u5a01\u5fc3\u4e2d\u4e00\u52a8\uff1a\u201c\u4ed6\u4eec\u4f3c\u4e4e\u77e5\u9053\u4e01\u8fdb\u662f\u4e09\u54e5\u5f15\u8350\u7684\uff0c\u8fd9\u4e8b\u786e\u662f\u6709\u70b9\u8e4a\u8df7\u3002\u201d\u4f46\u4ee5\u4ed6\u7684 \u8eab\u4efd\uff0c\u53c8\u5c82\u80fd\u53d7\u4eba\u631f\u6301\uff0c\u5f53\u4e0b\u6012\u9053\uff1a\u201c\u53f2\u67d0\u4eba\u6536\u5f92\u4e4b\u4e8b\uff0c\u4e0e\u4f60\u4eec\u65e0\u5173\uff01\u4f60\u4eec\u6349\u4e86\u4ed6\u624d\u5411\u6211 \u95ee\uff0c\u66f4\u5c5e\u65e0\u793c\uff01\u201d \u3000\u3000\u891a\u4e91\u5cf0\u7b11\u9053\uff1a\u201c\u6211\u8fd9\u4e00\u7740\u4e43\u662f\u51fa\u4e8e\u65e0\u53ef\u5948\u4f55\u3002\u8001\u82f1\u96c4\u65e2\u7136\u4e0d\u80af\u89c1\u544a\uff0c\u6211\u4eec\u53ea\u597d\u5c06\u4ee4\u5f92\u5e26 \u8d70\u4e86\u3002\u201d \u3000\u3000\u53f2\u95e8\u5927\u5f1f\u5b50\u5f20\u901b\u559d\u9053\uff1a\u201c\u4f60\u4eec\u628a\u6211\u4e00\u4e2a\u65b0\u5165\u95e8\u3001\u5b66\u827a\u672a\u7cbe\u7684\u5e08\u5f1f\u62ff\u4e3a\u4eba\u8d28\uff0c\u7b97\u5f97\u4ec0\u4e48\u597d \u6c49\uff1f\u201d \u3000\u3000\u8c37\u6db5\u865a\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u4ee5\u4f17\u51cc\u5be1\uff0c\u8fd9\u53c8\u7b97\u5f97\u4ec0\u4e48\u597d\u6c49\uff1f\u201d \u3000\u3000\u53f2\u7528\u5a01\u9762\u7ea2\u8033\u8d64\uff0c\u559d\u9053\uff1a\u201c\u597d\uff0c\u4f60\u4eec\u5148\u9000\u4e0b\u53bb\uff0c\u5f85\u6211\u548c\u8fd9\u4e24\u4f4d\u597d\u6c49\u5355\u6253\u72ec\u6597\u3002\u53ea\u8981\u4f60\u4eec \u54ea\u4e00\u4f4d\u8d62\u5f97\u6211\u7684\u8fd9\u53e3\u5927\u5200\uff0c\u6211\u6253\u5f00\u5927\u95e8\uff0c\u4efb\u51ed\u4f60\u4eec\u51fa\u53bb\u3002\u6211\u4eec\u5e08\u5f92\uff0c\u51b3\u4e0d\u963b\u62e6\uff01\u201d\u672c\u6765\uff0c\u4ed6 \u8bf4\u4e86\u6253\u5f00\u5927\u95e8\uff0c\u4efb\u51ed\u4f60\u4eec\u51fa\u53bb\uff0c\u8fd9\u5df2\u7ecf\u662f\u542b\u6709\u201c\u51b3\u4e0d\u963b\u62e6\u201d\u7684\u610f\u601d\u4e86\u3002\u4ed6\u4e4b\u6240\u4ee5\u4e0d\u5acc\u7d2f\u8d58\uff0c \u91cd\u590d\u4e00\u53e5\u8bdd\u4e2d\uff0c\u5b9e\u662f\u53e6\u6709\u610f\u601d\u3002\u56e0\u4e3a\u4ed6\u53ea\u63d0\u201c\u6211\u4eec\u5e08\u5f92\u201d\u56db\u5b57\uff0c\u201c\u6211\u4eec\u5e08\u5f92\u201d\u5f53\u7136\u662f\u4e0d\u5305\u62ec \u5bbe\u5ba2\u5728\u5185\u3002 \u3000\u3000\u6b66\u5f53\u6d3e\u638c\u95e8\u5927\u5f1f\u5b50\u4e54\u5143\u58ee\u7acb\u5373\u8bf4\u9053\uff1a\u201c\u5272\u9e21\u7109\u7528\u725b\u5200\uff0c\u6211\u4eec\u6b66\u5f53\u6d3e\u56db\u4eba\u548c\u8fd9\u4e2a\u59d3\u8c37\u7684\u6709 \u4ec7\uff0c\u613f\u4e3a\u53f2\u8001\u82f1\u96c4\u4e86\u5374\u6b64\u4e8b\uff01\u201d \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4f60\u5c31\u5212\u51fa\u9053\u513f\u6765\u5427\uff01\u201d \u3000\u3000\u4e54\u5143\u58ee\u9053\uff1a\u201c\u6211\u4eec\u56db\u4eba\u662f\u5e08\u5144\u5f1f\uff0c\u4f60\u4eec\u4e24\u4eba\u4e5f\u662f\u5e08\u5144\u5f1f\uff0c\u4eca\u65e5\u6b63\u597d\u4e00\u8f83\u9ad8\u4e0b\uff01\u201d \u3000\u3000\u8c37\u6db5\u865a\u51b7\u51b7\u7b11\u9053\uff1a\u201c\u539f\u6765\u4f60\u4eec\u56db\u4f4d\u8981\u548c\u6211\u4eec\u4e8c\u4eba\u51b3\u4e00\u80dc\u8d1f\u3002\u4e0d\u9519\uff0c\u8fd9\u4e5f\u662f\u5f88\u516c\u5e73\u5440\uff01\u201d \u3000\u3000\u4e54\u5143\u58ee\u9762\u4e0a\u4e00\u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u6211\u4eec\u6b66\u5f53\u6d3e\u7684\u516c\u654c\uff0c\u6211\u53ef\u4e0d\u80fd\u548c\u4f60\u8bb2\u4ec0\u4e48\u6c5f\u6e56\u89c4\u77e9\uff01\u5982 \u679c\u4f60\u8ba4\u4e3a\u6211\u4eec\u662f\u6043\u591a\u4e3a\u80dc\u7684\u8bdd\uff0c\u90a3\u4e5f\u53ef\u4ee5\u628a\u59d3\u891a\u7684\u6487\u5f00\uff0c\u4f60\u90a3\u59d3\u891a\u7684\u5e08\u5144\u7531\u53f2\u8001\u82f1\u96c4\u53d1\u843d\u3002 \u6211\u548c\u5f20\u5e08\u5f1f\u53ea\u4e0e\u4f60\u59d3\u8c37\u7684\u51b3\u4e00\u6b7b\u751f\u3002\u201d \u3000\u3000\u539f\u6765\u4e54\u5143\u58ee\u6df1\u77e5\u8c37\u6db5\u865a\u201c\u5929\u96f7\u529f\u201d\u7684\u5389\u5bb3\uff0c\u81ea\u5fd6\u5355\u6253\u72ec\u6597\uff0c\u51b3\u8ba1\u4e0d\u662f\u8c37\u6db5\u865a\u7684\u5bf9\u624b\u3002\u4f46 \u4ed6\u62c9\u5f20\u5143\u5409\u4f5c\u4ed6\u7684\u5e2e\u624b\uff0c\u5374\u4e5f\u4e0d\u80fd\u8bf4\u4ed6\u65e0\u7406\u3002\u56e0\u4e3a\u5f20\u5143\u5409\u4e0e\u8c37\u6db5\u865a\u6709\u593a\u59bb\u4e4b\u8fb1\uff0c\u6298\u5251\u4e4b\u4ec7\uff0c \u7528\u6c5f\u6e56\u672f\u8bed\u6765\u8bf4\uff0c\u4e43\u662f\u201c\u6b63\u4e3b\u513f\u201d\uff0c\u4ed6\u867d\u7136\u662f\u5728\u201c\u62a5\u4ec7\u201d\u8fd9\u4e00\u4ef6\u4e8b\u4e0a\u505a\u6587\u7ae0\uff0c\u5f53\u7136\u4e0d\u80fd\u5c11\u4e86 \u201c\u6b63\u4e3b\u513f\u201d\u5f20\u5143\u5409\u3002\u81f3\u4e8e\u4ed6\u81ea\u5df1\u5219\u56e0\u662f\u6b66\u5f53\u6d3e\u7684\u638c\u95e8\u5f1f\u5b50\uff0c\u65e2\u662f\u4e3a\u5e08\u95e8\u8363\u8fb1\u4f5c\u6218\uff0c\u4ed6\u5fc5\u987b\u4eb2 \u81ea\u51fa\u9a6c\uff0c\u8fd9\u4e5f\u662f\u7406\u6240\u5f53\u7136\u3002 \u3000\u3000\u8c37\u6db5\u865a\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u6ca1\u6709\u8bf4\u4f60\u4eec\u4e0d\u516c\u5e73\u5440\uff0c\u4f55\u987b\u6653\u6653\u7f6e\u8fa9\uff1f\u4f60\u5212\u51fa\u7684\u9053\u513f\uff0c\u6211 \u4eec\u4e00\u5f8b\u7167\u529e\u3002\u4f46\u6709\u4e00\u53e5\u8bdd\uff0c\u6211\u4eec\u5fc5\u987b\u95ee\u4e2a\u6e05\u695a\uff01\u201d \u3000\u3000\u4e54\u5143\u58ee\u9053\uff1a\u201c\u6709\u8bdd\u5feb\u8bf4\uff0c\u6709\u5c41\u5feb\u653e\uff01\u201d \u3000\u3000\u8c37\u6db5\u865a\u7b11\u9053\uff1a\u201c\u4e54\u5143\u58ee\uff0c\u4f60\u5634\u91cc\u5e72\u51c0\u4e00\u4e9b\u597d\u4e0d\u597d\uff1f\u54b1\u4eec\u662f\u624b\u5e95\u89c1\u8f93\u8d62\uff0c\u53ef\u4e0d\u662f\u548c\u4f60\u6597\u5634 \u7684\u5462\u3002\u6211\u53ea\u662f\u8981\u95ee\u4f60\uff1a\u4f60\u4eec\u6b66\u5f53\u6d3e\u7684\u66ff\u53f2\u8001\u82f1\u96c4\u51fa\u5934\uff0c\u662f\u5426\u4e5f\u80fd\u66ff\u53f2\u8001\u82f1\u96c4\u4f5c\u4e3b\uff1f\u6bd4\u5982\u8bf4\uff0c \u6211\u4eec\u82e5\u8d25\u4e86\uff0c\u5f53\u7136\u662f\u4efb\u51ed\u4f60\u4eec\u53d1\u843d\uff1b\u4f46\u5018\u82e5\u662f\u6211\u4eec\u4fa5\u5e78\u80dc\u4e86\uff0c\u90a3\u53c8\u5982\u4f55\uff1f\u662f\u5426\u8981\u6211\u4eec\u548c\u53f2\u8001 \u82f1\u96c4\u5e08\u5f92\u518d\u6597\u4e00\u573a\uff1f\u201d \u3000\u3000\u53f2\u7528\u5a01\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u6709\u591a\u5927\u672c\u9886\uff0c\u80dc\u5f97\u4e86\u6b66\u5f53\u56db\u4fa0\uff1f\u5982\u4eca\u6b66\u5f53\u56db\u4fa0\u8981\u548c\u4f60\u4eec\u7b97\u5e10\uff0c\u8001 \u592b\u5f53\u7136\u8981\u8ba9\u56db\u4fa0\u4f5c\u4e3b\uff0c\u4f55\u987b\u518d\u95ee\uff1f\u201d \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u4f60\u662f\u4e0d\u518d\u51fa\u624b\u7684\u4e86\uff1f\u201d \u3000\u3000\u53f2\u7528\u5a01\u6012\u9053\uff1a\u201c\u4f60\u4e5f\u5fd2\u5c0f\u89d1\u8001\u592b\u4e86\uff0c\u96be\u9053\u8001\u592b\u8fd8\u80fd\u7528\u8f66\u8f6e\u6218\u6765\u5bf9\u4ed8\u4f60\u4eec\uff1f\u53ea\u8981\u4f60\u4eec\u80fd\u591f \u5728\u6b66\u5f53\u56db\u4fa0\u7684\u5251\u4e0b\u9003\u51fa\u6027\u547d\uff0c\u8001\u592b\u6253\u5f00\u5927\u95e8\uff0c\u606d\u9001\u4f60\u4eec\u51fa\u53bb\uff01\u201d \u3000\u3000\u539f\u6765\u53f2\u7528\u5a01\u89c1\u8bc6\u4e86\u4ed6\u4eec\u7684\u5929\u96f7\u529f\uff0c\u81ea\u5fd6\u4e5f\u662f\u6ca1\u6709\u53d6\u80dc\u7684\u628a\u63e1\uff0c\u662f\u4ee5\u4e50\u5f97\u8ba9\u6b66\u5f53\u56db\u4fa0\u628a\u4e8b \u60c5\u62db\u63fd\u8fc7\u53bb\u3002\u5728\u4ed6\u7684\u60f3\u6cd5\uff0c\u4ee5\u6b66\u5f53\u56db\u4fa0\u7684\u5251\u6cd5\uff0c\u56db\u4eba\u8054\u624b\uff0c\u5f53\u7136\u5fc5\u80dc\u65e0\u7591\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u597d\uff0c\u541b\u5b50\u4e00\u8a00\uff0c\u5feb\u9a6c\u4e00\u97ad\uff0c\u5c31\u8fd9\u6837\uff01\u201d \u3000\u3000\u201c\u8fd9\u91cc\u53ef\u4e0d\u662f\u6bd4\u6b66\u7684\u597d\u573a\u6240\u3002\u201d \u3000\u3000\u4e54\u5143\u58ee\u9053\uff1a\u201c\u597d\uff0c\u54b1\u4eec\u5230\u5916\u9762\u53bb\uff0c\u514d\u5f97\u7834\u574f\u4e86\u53f2\u8001\u82f1\u96c4\u7684\u5bff\u92ae\u3002\u201d\u5f20\u5143\u5409\u53d1\u51fa\u563f\u563f\u7684\u51b7 \u7b11\uff1a\u201c\u8bf7\u5440\uff0c\u8bf7\u5440\uff0c\u54fc\uff0c\u4eca\u65e5\u662f\u4f60\u4eec\u81ea\u6295\u7f57\u7f51\uff0c\u6211\u4e5f\u4e0d\u6015\u4f60\u4eec\u9003\uff01\u201d\u4ed6\u7684\u773c\u775b\u597d\u50cf\u8981\u55b7\u51fa\u706b \u6765\uff0c\u4f46\u5728\u6124\u6012\u4e4b\u4e2d\uff0c\u5374\u4e5f\u542b\u6709\u51e0\u5206\u5feb\u610f\uff0c\u4f3c\u4e4e\u6781\u6709\u628a\u63e1\u5c31\u53ef\u4ee5\u624b\u5203\u4ec7\u4eba\u4e00\u6837\u3002\u8c37\u6db5\u865a\u5fc3\u4e2d\u4e00 \u52a8\uff0c\u60f3\u9053\uff1a\u201c\u4ed6\u662f\u6211\u624b\u4e0b\u8d25\u5c06\uff0c\u4f55\u4ee5\u5982\u6b64\u9a84\u72c2\uff0c\u83ab\u975e\u4ed6\u65b0\u8fd1\u7ec3\u6210\u4e86\u4ec0\u4e48\u5389\u5bb3\u7684\u672c\u9886\uff1f\u201d \u3000\u3000\u5f53\u4e0b\u4fbf\u7531\u53f2\u7528\u5a01\u5728\u5148\u5e26\u8def\uff0c\u628a\u53cc\u65b9\u5e26\u5230\u4e86\u540e\u56ed\u7684\u7ec3\u6b66\u573a\u4e2d\u3002 \u3000\u3000\u6b66\u5f53\u6d3e\u7684\u5251\u672f\u9a70\u540d\u5929\u4e0b\uff0c\u4f46\u8fd9\u6b21\u6b66\u5f53\u56db\u4fa0\u5374\u8981\u8054\u624b\u5bf9\u4ed8\u4e24\u4e2a\u6765\u5386\u4e0d\u660e\u7684\u5c11\u5e74\uff0c\u4f17\u5bbe\u5ba2\u90fd \u662f\u60ca\u8be7\u4e0d\u5df2\uff0c\u4eba\u4eba\u6000\u7740\u597d\u5947\u4e4b\u5fc3\uff0c\u5f53\u7136\u662f\u4e0d\u613f\u653e\u8fc7\u8fd9\u4e2a\u96be\u5f97\u7684\u673a\u4f1a\uff0c\u4e89\u5148\u5df1\u6050\u540e\u5730\u8ddf\u7740\u53bb\u770b \u4ed6\u4eec\u6bd4\u6b66\u4e86\u3002 \u3000\u3000\u6b66\u5f53\u56db\u4fa0\u56db\u65b9\u7ad9\u5b9a\uff0c\u628a\u891a\u3001\u8c37\u4e8c\u4eba\u56f4\u5728\u5f53\u4e2d\u3002\u4e54\u5143\u58ee\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4eba\u8fd8\u4e0d\u51fa\u62db\uff0c\u66f4\u5f85\u4f55 \u65f6\uff1f\u201d\u4ed6\u81ea\u4ee5\u4e3a\u7a33\u64cd\u9650\u5238\uff0c\u56e0\u6b64\u4e50\u5f97\u6545\u793a\u5927\u65b9\uff0c\u8ba9\u891a\u3001\u8c37\u4e8c\u4eba\u5148\u884c\u51fa\u62db\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u5411\u8c37\u6db5\u865a\u6253\u4e86\u4e00\u773c\u8272\uff0c\u8c37\u6db5\u865a\u7709\u5934\u7565\u76b1\uff0c\u4f46\u968f\u5373\u5c31\u70b9\u4e86\u70b9\u5934\uff0c\u8868\u793a\u660e\u767d\uff0c\u613f\u610f\u4f9d \u8ba4\u3002\u4e54\u5143\u58ee\u51b7\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u88c5\u795e\u5f04\u9b3c\uff0c\u4e5f\u662f\u65e0\u6d4e\u4e8e\u4e8b\u3002\u51fa\u62db\u5427\uff01\u201d \u3000\u3000\u8c37\u6db5\u865a\u51b7\u7b11\u9053\uff1a\u201c\u6211\u7684\u5e08\u5144\u662f\u6148\u60b2\u4e3a\u6000\uff0c\u53eb\u6211\u4e0d\u8981\u53d6\u4f60\u6027\u547d\uff0c\u4f60\u65e2\u7136\u80e1\u4e71\u731c\u7591\uff0c\u6211\u5c31\u7d22 \u6027\u548c\u4f60\u660e\u767d\u8bf4\u4e86\uff01\u597d\uff01\u63a5\u62db\uff01\u201d\u4e24\u4eba\u5530\u5730\u62d4\u51fa\u5251\u6765\uff0c\u5251\u4e2d\u631f\u638c\uff0c\u53cc\u5251\u53cc\u638c\uff0c\u540c\u65f6\u5e76\u53d1\u3002\u53ea\u89c1 \u5251\u5149\u5982\u7ec3\uff0c\u638c\u98ce\u547c\u547c\uff0c\u65b9\u5706\u6570\u4e08\u4e4b\u5185\uff0c\u6c99\u98de\u77f3\u8d70\u3002\u9760\u8fd1\u573a\u8fb9\u7684\u5bbe\u5ba2\uff0c\u90fd\u6709\u70b9\u7acb\u8db3\u4e0d\u7a33\u7684\u611f \u89c9\u3002 \u3000\u3000\u4e54\u5143\u58ee\u51b7\u7b11\u9053\uff1a\u201c\u5929\u96f7\u529f\u80fd\u5948\u6211\u4f55\u3002\u201d\u8bdd\u867d\u5982\u6b64\uff0c\u4ed6\u5411\u8c37\u6db5\u865a\u5e73\u80f8\u523a\u6765\u7684\u90a3\u4e00\u5251\uff0c\u5728 \u891a\u3001\u8c37\u4e8c\u4eba\u7684\u638c\u529b\u9707\u8361\u4e4b\u4e0b\uff0c\u5251\u5c16\u5df2\u662f\u6b6a\u8fc7\u4e00\u8fb9\uff01 \u3000\u3000\u8c37\u6db5\u865a\u6df1\u77e5\u4e54\u5143\u58ee\u5fc3\u80a0\u72ed\u7a84\uff0c\u96be\u4ee5\u7406\u55bb\uff0c\u60f3\u9053\uff1a\u201c\u64d2\u8d3c\u5148\u64d2\u738b\uff0c\u4e14\u628a\u8fd9\u53ae\u62ff\u4e0b\u518d\u8bf4\u3002\u4ed6 \u662f\u6b66\u5f53\u6d3e\u7684\u638c\u95e8\u5f1f\u5b50\uff0c\u62ff\u4e0b\u4e86\u4ed6\uff0c\u8fd9\u5251\u9635\u81ea\u5fc5\u74e6\u89e3\u3002\u201d \u3000\u3000\u4ed6\u7684\u7b97\u76d8\u6253\u5f97\u5982\u610f\uff0c\u5374\u6ca1\u60f3\u5230\uff0c\u4e54\u5143\u58ee\u6562\u4e8e\u7387\u9886\u5e08\u5f1f\u5411\u4ed6\u5bfb\u4ec7\uff0c\u5f53\u7136\u4e5f\u662f\u6709\u5907\u800c\u6765\uff0c\u5c82 \u80fd\u8ba9\u4ed6\u5982\u6b64\u8f7b\u6613\u53d6\u80dc\uff1f \u3000\u3000\u4e54\u5143\u58ee\u4e00\u5251\u523a\u7a7a\uff0c\u7acb\u5373\u8eab\u79fb\u6b65\u6362\uff0c\u63a5\u7740\u4e0a\u6765\u7684\u662f\u5b63\u5143\u4f26\u3002\u8c37\u6db5\u865a\u4e00\u638c\u6253\u53bb\uff0c\u53ea\u89c9\u8111\u540e\u98ce \u751f\uff0c\u5f20\u5143\u5409\u3001\u7cb1\u5143\u732e\u53cc\u5251\u540c\u65f6\u653b\u5230\u3002\u5f20\u5143\u5409\u7684\u5251\u5c16\u6307\u5411\u4ed6\u80c1\u4e0b\u7684\u201c\u6108\u6c14\u7a74\u201d\uff0c\u6881\u5143\u732e\u5219\u662f\u7528 \u201c\u5207\u659c\u8026\u201d\u7684\u62db\u5f0f\uff0c\u5251\u950b\u659c\u98de\uff0c\u524a\u5411\u4ed6\u7684\u819d\u76d6\u3002 \u3000\u3000\u8fd9\u4e24\u62db\u6765\u52bf\u51cc\u5389\uff0c\u6b63\u662f\u201c\u653b\u654c\u4e4b\u6240\u5fc5\u6551\u201d\u7684\u72e0\u8fa3\u5251\u6cd5\u3002\u8c37\u6db5\u865a\u5b63\u638c\u5212\u4e86\u4e00\u9053\u5706\u5f27\uff0c\u53cd\u5288 \u6881\u5143\u732e\u7684\u5c0f\u81c2\uff0c\u4ee5\u653b\u4e3a\u5b88\uff0c\u5316\u89e3\u4e86\u4ed6\u7684\u5251\u62db\uff0c\u540c\u65f6\u6a2a\u5251\u4e00\u622a\uff0c\u201c\u9e23\u201d\u7684\u4e00\u58f0\uff0c\u683c\u5f00\u4e86\u5f20\u5143\u5409 \u7684\u957f\u5251\u3002\u5728\u8fd9\u65f6\u95f4\uff0c\u5b63\u5143\u4f26\u548c\u9000\u800c\u590d\u4e0a\u7684\u4e54\u5143\u58ee\uff0c\u4e24\u67c4\u957f\u5251\u4ea6\u5df2\u540c\u65f6\u5411\u891a\u4e91\u5cf0\u5c55\u5f00\u653b\u51fb\u4e86\u3002 \u3000\u3000\u53ea\u542c\u5f97\u4e00\u7247\u53ee\u53ee\u94db\u94db\u4e4b\u58f0\uff0c\u4e0d\u7edd\u4e8e\u8033\uff01\u6b66\u5f53\u56db\u5927\u5f1f\u5b50\uff0c\u56db\u67c4\u957f\u5251\uff0c\u4ea4\u53c9\u7a7f\u62bd\uff0c\u597d\u50cf\u7ec4\u6210 \u4e86\u4e00\u5f20\u201c\u5251\u7f51\u201d\uff0c\u77ac\u606f\u4e4b\u95f4\uff0c\u4e54\u3001\u5b63\u3001\u5f20\u3001\u6881\u56db\u4eba\u90fd\u653b\u51fa\u4e86\u516d\u516d\u4e09\u5341\u516d\u62db\u3002 \u3000\u3000\u539f\u6765\u4ed6\u4eec\u56db\u4eba\u5728\u90a3\u6b21\u5403\u4e86\u8c37\u6db5\u865a\u7684\u5927\u4e8f\u4e4b\u540e\uff0c\u6df1\u77e5\u5355\u6253\u72ec\u6597\uff0c\u7edd\u4e0d\u662f\u8c37\u6db5\u8654\u7684\u5bf9\u624b\uff0c\u4e8e \u662f\u56de\u5c71\u5411\u5e08\u7236\u5e08\u53d4\u8bf7\u6559\uff0c\u82e6\u7ec3\u56db\u5e74\uff0c\u7ec3\u6210\u4e86\u4e00\u4e2a\u56db\u4eba\u914d\u5408\u7684\u5251\u9635\uff0c\u6309\u4e7e\u3001\u5764\u3001\u826e\u3001\u9f9a\u3001\u574e\u3001 \u79bb\u3001\u9707\u3001\u5151\u7684\u516b\u5366\u65b9\u4f4d\uff0c\u6bcf\u4e2a\u5f1f\u5b50\u4e00\u8fdb\u4e00\u9000\uff0c\u90fd\u662f\u6709\u6761\u4e0d\u7d0a\u3002 \u3000\u3000\u4f8b\u5982\u4e54\u5143\u58ee\u8ff8\u201c\u4e7e\u201d\u65b9\u9000\u201c\u5764\u201d\u4f4d\uff0c\u5b63\u5143\u4f26\u5c31\u8fdb\u201c\u826e\u201d\u65b9\u9000\u201c\u9f9a\u201d\u4f4d\uff0c\u5f20\u5143\u5409\u8fdb\u201c\u574e\u201d \u65b9\u9000\u201c\u79bb\u201d\u4f4d\uff0c\u7cb1\u5143\u732e\u5219\u8fdb\u201c\u9707\u201d\u65b9\u9000\u201c\u5151\u201d\u4f4d\u3002\u65c1\u4eba\u773c\u4e2d\uff0c\u53ea\u89c1\u4ed6\u4eec\u7a7f\u68ad\u6765\u5f80\uff0c\u4f46\u7ed9\u4ed6\u4eec \u56f0\u5728\u5251\u9635\u4e4b\u4e2d\u7684\u654c\u4eba\uff0c\u5374\u597d\u4f3c\u7ed9\u94c1\u58c1\u94dc\u5899\u56f4\u4f4f\uff0c\u65e0\u9699\u53ef\u94bb\u3002\u8981\u60f3\u7a81\u56f4\uff0c\u90a3\u66f4\u662f\u96be\u4e0a\u52a0\u96be\u7684 \u4e86\u3002 \u3000\u3000\u5251\u9635\u5c55\u5f00\uff0c\u970e\u65f6\u95f4\u53ea\u89c1\u6ee1\u573a\u90fd\u662f\u5251\u5149\uff0c\u5ffd\u4e1c\u5ffd\u897f\uff0c\u5ffd\u805a\u5ffd\u6563\uff0c\u5b9b\u5982\u6c34\u94f6\u6cfb\u5730\uff0c\u82b1\u7f51\u7f24 \u7eb7\uff01\u516d\u4e2a\u4eba\u5728\u573a\u4e2d\u53ae\u6740\uff0c\u7adf\u4f3c\u5343\u519b\u8ffd\u9010\u4e00\u822c\uff01\u770b\u5f97\u4f17\u5bbe\u5ba2\u90fd\u662f\u76ee\u77aa\u53e3\u5446\uff0c\u624b\u5fc3\u634f\u7740\u4e00\u628a\u51b7 \u6c57\uff01 \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u8c37\u5e08\u5f1f\uff0c\u5148\u5b88\u540e\u653b\uff01\u201d\u4e24\u4eba\u80cc\u8d34\u7740\u80cc\uff0c\u8fd0\u5251\u5982\u98ce\uff0c\u53ea\u542c\u5f97\u4e00\u7247\u91d1\u94c1\u4ea4\u9e23\u4e4b \u58f0\uff0c\u5982\u96f7\u9707\u8033\u3002\u8fc7\u4e86\u4e00\u76cf\u8336\u7684\u65f6\u95f4\uff0c\u4e24\u4eba\u970d\u5730\u4e00\u58f0\u5927\u559d\uff0c\u540c\u65f6\u51fa\u638c\uff0c\u5251\u4e2d\u631f\u638c\uff0c\u53d1\u51fa\u4e86\u201c\u5929 \u96f7\u529f\u201d\uff01 \u3000\u3000\u638c\u98ce\u5251\u5f71\u4e4b\u4e2d\uff0c\u6b66\u5f53\u56db\u5927\u5f1f\u5b50\u500f\u5730\u4e00\u9f50\u9000\u4e0b\uff0c\u4f46\u8f6c\u773c\u4e4b\u95f4\uff0c\u53c8\u4ece\u56db\u65b9\u653b\u4e0a\u3002\u4e54\u5143\u58ee\u559d \u9053\uff1a\u201c\u5feb\u5251\u6025\u653b\uff0c\u4e0d\u8ba9\u4ed6\u4eec\u817e\u51fa\u624b\u6765\uff01\u201d \u3000\u3000\u672c\u6765\u891a\u3001\u8c37\u4e8c\u4eba\u82e5\u662f\u4e00\u4e0a\u573a\u5c31\u4f7f\u51fa\u201c\u5929\u96f7\u529f\u201d\u4e2d\u5a01\u529b\u6700\u5f3a\u7684\u90a3\u4e00\u62db\u201c\u96f7\u7535\u4ea4\u8f70\u201d\uff0c\u5176\u65f6 \u5251\u9635\u5c1a\u672a\u5408\u56f4\u3002\u6b64\u62db\u4e00\u53d1\uff0c\u5bf9\u65b9\u529f\u529b\u8f83\u5f31\u7684\u6881\u5143\u732e\u548c\u5b63\u5143\u4f26\u5fc5\u5b9a\u53d7\u4f24\uff0c\u891a\u3001\u8c37\u4e8c\u4eba\u4e5f\u5c31\u53ef\u4ee5 \u7a33\u64cd\u80dc\u5238\u4e86\u3002 \u3000\u3000\u4f46\u6b63\u56e0\u4e3a\u8fd9\u4e00\u62db\u5a01\u529b\u592a\u5927\uff0c\u8bf8\u3001\u8c37\u4e8c\u4eba\u5374\u662f\u4e0d\u80fd\u4e0d\u6709\u987e\u5fcc\uff0c\u53ea\u6015\u91cd\u4f24\u4e86\u6216\u6253\u6b7b\u4e86\u6b66\u5f53\u7684 \u5f1f\u5b50\u4e4b\u540e\uff0c\u4e0e\u6b66\u5f53\u6d3e\u7684\u51a4\u4ec7\u90a3\u5c31\u66f4\u662f\u96be\u4ee5\u5316\u89e3\u4e86\u3002 \u3000\u3000\u5982\u4eca\u5bf9\u65b9\u7684\u5251\u9635\u5df2\u7ecf\u5c55\u5f00\uff0c\u891a\u3001\u8c37\u4e8c\u4eba\u4f7f\u51fa\u4e86\u201c\u5929\u96f7\u529f\u201d\u53ea\u80fd\u6682\u7f13\u5bf9\u65b9\u7684\u653b\u52bf\uff0c\u5374\u8bbe\u6cd5 \u7a81\u7834\u5251\u9635\u3002\u800c\u4e14\u4ed6\u4eec\u4e5f\u8fd8\u662f\u4e0d\u6562\u4f7f\u201c\u96f7\u7535\u4ea4\u8f70\u201d\u90a3\u4e00\u62db\u3002 \u3000\u3000\u4e54\u5143\u58ee\u5374\u662f\u201c\u5f97\u7406\u4e0d\u9976\u4eba\u201d\uff0c\u5360\u4e86\u4f18\u52bf\uff0c\u559d\u4ee4\u5e08\u5f1f\u201c\u51b3\u5251\u6025\u653b\u201d\uff0c\u5251\u9635\u66f4\u662f\u8d8a\u8f6c\u8d8a\u5feb\uff0c \u8d8a\u7f29\u8d8a\u7d27\u4e86\u3002 \u3000\u3000\u56db\u67c4\u957f\u5251\u4ea4\u53c9\u7a7f\u63d2\uff0c\u5374\u53c8\u5b9b\u5982\u4e00\u4f53\uff0c\u7b49\u4e8e\u662f\u4e00\u4e2a\u4eba\u4f7f\u51fa\u4e00\u5957\u975e\u5e38\u7e41\u590d\u7684\u5251\u6cd5\uff0c\u4f46\u56db\u9762\u516b \u65b9\uff0c\u5374\u53c8\u90fd\u662f\u6b66\u5f53\u5f1f\u5b50\u7684\u8eab\u5f71\uff01 \u3000\u3000\u8c37\u3001\u891a\u4e8c\u4eba\u679c\u7136\u7ed9\u4ed6\u4eec\u653b\u5f97\u900f\u4e0d\u8fc7\u6c14\u6765\uff0c\u4f46\u4e54\u5143\u58ee\u8981\u4ee4\u4ed6\u4eec\u4f7f\u4e0d\u51fa\u201c\u5929\u96f7\u529f\u201d\u4e5f\u8fd8\u672a\u80fd \u8fbe\u5230\u76ee\u7684\u3002\u4e0d\u8fc7\u4ed6\u4eec\u5fc5\u987b\u5728\u62b5\u654c\u6570\u5341\u62db\u4e4b\u540e\uff0c\u65b9\u80fd\u7f13\u4e00\u53e3\u6c14\uff0c\u89c5\u4e2a\u673a\u4f1a\u65bd\u5c55\uff0c\u800c\u6bcf\u6b21\u65bd\u5c55\uff0c \u4e5f\u53ea\u4e0d\u8fc7\u53ef\u4ee5\u6682\u65f6\u7f13\u548c\u4e00\u4e0b\u5bf9\u65b9\u7684\u653b\u52bf\u800c\u5df2\u3002\u662f\u4ee5\u4e54\u5143\u58ee\u867d\u7136\u672a\u80fd\u5b8c\u5168\u8fbe\u5230\u76ee\u7684\uff0c\u8fd9\u4e2a\u6218\u6cd5 \u4e5f\u662f\u5927\u5927\u7684\u6709\u6548\u4e86\u3002 \u3000\u3000\u4f17\u5bbe\u5ba2\u770b\u5f97\u76ee\u7729\u795e\u6447\uff0c\u7a88\u7a83\u79c1\u8bae\uff0c\u6709\u4eba\u8bf4\u9053\uff1a\u201c\u6bd5\u7adf\u662f\u6b66\u5f53\u6d3e\u7684\u8fde\u73af\u593a\u547d\u5251\u6cd5\u540d\u4e0d\u865a \u4f20\uff1b\u4f3c\u8fd9\u7b49\u5389\u5bb3\u7684\u5251\u9635\uff0c\u53ea\u6015\u5c31\u662f\u91d1\u56fd\u7684\u56fd\u5e08\u9633\u5929\u96f7\u4eb2\u81ea\u51fa\u9a6c\uff0c\u4ea6\u662f\u65e0\u6cd5\u7834\u9635\u7a81\u56f4\uff0c\u8fd9\u4e24\u4e2a \u5c0f\u5b50\u8981\u60f3\u9003\u8131\uff0c\u90a3\u662f\u4f53\u60f3\u7684\u4e86\uff01\u201d\u6709\u4eba\u5374\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u5c0f\u5b50\u80fd\u591f\u62b5\u654c\u8fd9\u8bb8\u591a\u65f6\u5019\uff0c\u4e5f\u7b97\u662f\u5341 \u5206\u96be\u5f97\u7684\u4e86\uff01\u201d \u3000\u3000\u53f2\u7528\u5a01\u5219\u662f\u5fe7\u5fc3\u5fe1\u4ef2\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u770b\u6765\u8fd9\u4e24\u4eba\u7684\u5929\u96f7\u529f\u4f3c\u4e4e\u5c1a\u672a\u5c3d\u5168\u529b\uff0c\u5f53\u7136\u6b66\u5f53\u6d3e \u8fd9\u4e2a\u5251\u9635\u4e5f\u662f\u5341\u5206\u5389\u5bb3\u7684\u3002\u4fd7\u82e5\u8fd9\u4e24\u4eba\u5168\u529b\u4f7f\u51fa\u4e86\u5929\u96f7\u529f\u7684\u8bdd\uff0c\u7eb5\u7136\u4e0d\u80fd\u7834\u5f97\u5251\u9635\uff0c\u53ea\u6015\u4e5f \u4f1a\u4e24\u8d25\u4ff1\u4f24\u3002\u201d \u3000\u3000\u53f2\u7528\u5a01\u5c1a\u672a\u77e5\u9053\u891a\u3001\u8c37\u4e8c\u4eba\u7684\u6765\u5386\uff0c\u891a\u3001\u8c37\u53d7\u4f24\uff0c\u5bf9\u4ed6\u6765\u8bf4\uff0c\u65e0\u5173\u7d27\u8981\u3002\u4f46\u6b66\u5f53\u5f1f\u5b50\u82e5 \u662f\u5728\u4ed6\u5bb6\u4e2d\u53d7\u4e86\u91cd\u4f24\uff0c\u4ed6\u5c31\u975e\u4f46\u662f\u6127\u5bf9\u6b66\u5f53\u6d3e\uff0c\u5728\u6b66\u6797\u4e2d\u4ed6\u4e5f\u662f\u5927\u5931\u9762\u5b50\u4e86\u3002 \u3000\u3000\u53f2\u7528\u5a01\u81ea\u6043\u65e0\u529b\u5316\u89e3\uff0c\u552f\u6709\u7a7a\u81ea\u7740\u6025\u3002 \u3000\u3000\u6fc0\u6218\u4e2d\u5ffd\u542c\u5f97\u4e00\u58f0\u88c2\u5e1b\uff0c\u8c37\u6db5\u865a\u7684\u534a\u622a\u8863\u8896\u7ed9\u5f20\u65e0\u5409\u4e00\u523d\u524a\u6389\uff0c\u788e\u5e03\u98de\u626c\uff0c\u5316\u6210\u4e86\u7247\u7247 \u745a\u8776\uff01\u539f\u6765\u4ed6\u8fd9\u4e00\u62db\u540d\u4e3a\u201c\u4e09\u8f6c\u6cd5\u8f6e\u201d\uff0c\u770b\u662f\u4e00\u62db\uff0c\u5176\u5b9e\u5df2\u662f\u5305\u542b\u4e09\u62db\u4e8c\u5341\u516d\u5f0f\uff01\u524a\u4e0b\u7684\u8896 \u5b50\u5728\u4ed6\u7684\u5feb\u5251\u8f6e\u8f6c\u4e4b\u4e2d\uff0c\u7ede\u5f97\u7247\u7247\u788e\u4e86\uff01 \u3000\u3000\u5f20\u5143\u5409\u8fd9\u4e00\u5251\u6ee1\u4ee5\u4e3a\u81f3\u5c11\u53ef\u4ee5\u524a\u6389\u8c37\u6db5\u865a\u7684\u4e00\u6761\u81c2\u818a\u7684\uff0c\u672a\u80fd\u5982\u613f\uff0c\u5fc3\u91cc\u6697\u6697\u53eb\u4e86\u4e00\u58f0 \u201c\u53ef\u60dc\uff01\u201d\u53cc\u773c\u706b\u7ea2\uff0c\u653b\u5f97\u66f4\u52a0\u75af\u72c2\u4e86\u3002 \u3000\u3000\u8c37\u6db5\u865a\u5fcd\u4e0d\u4f4f\u5fc3\u5934\u6012\u706b\uff0c\u9661\u5730\u4e00\u58f0\u559d\u9053\uff1a\u201c\u5e08\u5144\uff0c\u96f7\u7535\u4ea4\u8f70\uff01\u201d \u3000\u3000\u66b4\u559d\u58f0\u4e2d\uff0c\u8c37\u3001\u891a\u4e8c\u4eba\u638c\u631f\u98ce\u96f7\uff0c\u53cc\u638c\u9f50\u51fa\uff01\u6b64\u65f6\u4e54\u5143\u58ee\u548c\u5b63\u5143\u4f26\u521a\u597d\u4ece\u6b63\u9762\u653b\u6765\uff0c\u9661 \u89c9\u52b2\u98ce\u6251\u9762\uff0c\u80f8\u53e3\u5982\u53d7\u9524\u51fb\u3002\u4e54\u3001\u5b63\u4e8c\u4eba\u5927\u5403\u4e00\u60ca\uff0c\u8fde\u5fd9\u6325\u5251\u6025\u653b\uff0c\u62fc\u7740\u4e0e\u5bf9\u65b9\u540c\u5f52\u4e8e\u5c3d\u3002 \u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u5f20\u5143\u5409\u3001\u6881\u5143\u732e\u4ea6\u5df2\u4ece\u4e24\u7ffc\u6251\u4e0a\uff0c\u5f20\u5143\u5409\u7684\u957f\u5251\u6307\u5411\u8c37\u6db5\u865a\u540e\u5fc3\u7684\u547d\u95e8\u8981 \u7a74\u3002\u6881\u5143\u732e\u7684\u957f\u5251\u5219\u5411\u891a\u4e91\u5cf0\u53f3\u80a9\u7684\u7435\u7436\u9aa8\u523a\u53bb\uff01 \u3000\u3000\u773c\u770b\u53cc\u65b9\u5c31\u8981\u4e24\u8d25\u4ff1\u4f24\uff0c\u751a\u6216\u540c\u5f52\u4e8e\u5c3d\uff0c\u53f2\u7528\u5a01\u5413\u5f97\u5931\u58f0\u53eb\u9053\uff1a\u201c\u4e0d\u597d\uff01\u201d\u53ef\u662f\u81ea\u5fd6\u65e0 \u529b\u5316\u89e3\uff0c\u82e5\u7136\u4e0a\u53bb\uff0c\u53ea\u662f\u767d\u9001\u4e00\u6761\u6027\u547d\uff01 \u3000\u3000\u5c31\u5728\u8fd9\u5343\u94a7\u4e00\u53d1\u4e4b\u9645\uff0c\u4eba\u4e1b\u4e2d\u5ffd\u5730\u98de\u8d77\u4e24\u6761\u4eba\u5f71\uff0c\u95ea\u5c6f\u822c\u5730\u843d\u5728\u573a\u5fc3\uff0c\u4e00\u4e2a\u7ad9\u5728\u4e54\u5143\u58ee \u8fd9\u8fb9\uff0c\u53cc\u638c\u9f50\u51fa\uff0c\u52a9\u4ed6\u62b5\u5fa1\u201c\u5929\u96f7\u529f\u201d\u7684\u638c\u529b\uff0c\u4e00\u4e2a\u5374\u662f\u7ad9\u5728\u8c37\u6db5\u865a\u8fd9\u8fb9\uff0c\u8fd0\u5251\u5982\u98ce\uff0c\u52a9\u4ed6 \u5316\u89e3\u5f20\u5143\u5409\u7684\u5251\u62db\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u7a81\u5982\u5176\u6765\uff0c\u4f17\u4eba\u8fd8\u672a\u770b\u5f97\u6e05\u695a\uff0c\u53ea\u542c\u5f97\u201c\u84ec\u201d\u7684\u4e00\u58f0\uff0c\u891a\u3001\u8c37\u4e8c\u4eba\u8eab\u5f62\u90fd\u662f\u6643\u4e86 \u4e00\u6643\uff0c\u4e54\u5143\u58ee\u548c\u5b63\u5143\u4f26\u5219\u5404\u81ea\u9000\u4e86\u4e09\u6b65\uff0c\u5b63\u5143\u4f26\u6253\u4e86\u4e24\u4e2a\u76d8\u65cb\uff0c\u65b9\u59cb\u7a33\u4f4f\u4e86\u8eab\u5f62\u3002 \u3000\u3000\u53e6\u4e00\u8fb9\u5f20\u5143\u5409\u7684\u957f\u5251\u7ed9\u90a3\u4eba\u5feb\u5251\u4e00\u51fb\uff0c\u5251\u950b\u767b\u65f6\u5012\u5377\u56de\u53bb\uff0c\u5e78\u800c\u5f20\u5143\u5409\u6536\u5251\u5f97\u5feb\uff0c\u624d\u4e0d \u81f3\u4e8e\u4f24\u4e86\u81ea\u5df1\u3002\u90a3\u4eba\u7528\u7684\u62db\u6570\u975e\u5e38\u7cbe\u5999\uff0c\u4e00\u5251\u7834\u4e86\u5f20\u5143\u5409\u7684\u653b\u52bf\uff0c\u4fe1\u624b\u4e00\u8f6c\u5251\u950b\uff0c\u53c8\u628a\u6881\u5143 \u732e\u7684\u9752\u94a2\u5251\u62e8\u5f00\u4e86\uff01\u201d \u3000\u3000\u4e54\u5143\u58ee\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u82f1\u96c4\u63f4\u624b\uff01\u201d\u5f20\u5143\u5409\u5374\u662f\u5927\u6012\u559d\u9053\uff1a\u201c\u4f60\u662f\u4f55\u4eba\uff0c\u80c6\u6562\u548c\u6211\u4eec\u6b66\u5f53 \u5f1f\u5b50\u4f5c\u5bf9\uff1f\u201d \u3000\u3000\u8fd9\u65f6\u53f2\u7528\u5a01\u60ca\u9b42\u672a\u5b9a\uff0c\u770b\u6e05\u695a\u4e86\uff0c\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u53eb\u9053\uff1a\u201c\u4e24\u4f4d\u4e0d\u662f\u67f3\u8235\u4e3b\u548c\u5d14\u8235\u4e3b \u5417\uff1f\u4f60\u4eec\u51e0\u65f6\u6765\u7684\u3002\u201d \u3000\u3000\u90a3\u4e24\u4eba\u4e00\u4e2a\u7eb3\u5251\u4eba\u9798\uff0c\u4e00\u4e2a\u6b62\u6b65\u6536\u638c\u3002\u4f7f\u5251\u7684\u90a3\u4eba\u7b11\u9053\uff1a\u201c\u5f20\u4e09\u4fa0\u8bf7\u83ab\u52a8\u6012\uff0c\u6211\u4eec\u53ea\u662f \u6765\u505a\u9c81\u4ef2\u8fde\u800c\u5df2\uff0c\u5e76\u975e\u6562\u548c\u6b66\u5f53\u5f1f\u5b50\u4f5c\u5bf9\u3002\u5c0f\u53ef\u67f3\u6d1e\u5929\uff0c\u8fd9\u4f4d\u662f\u6211\u7684\u5e08\u5f1f\u5d14\u9547\u5c71\u3002\u201d \u3000\u3000\u539f\u6765\u67f3\u3001\u5d14\u4e8c\u4eba\u8fdb\u6765\u7684\u65f6\u5019\uff0c\u6b63\u662f\u573a\u4e2d\u6076\u6597\u65b9\u9163\u4e4b\u9645\uff0c\u662f\u4ee5\u8c01\u90fd\u6ca1\u6709\u7559\u610f\u4ed6\u4eec\u3002 \u3000\u3000\u67f3\u6d1e\u5929\u548c\u5d14\u9547\u5c71\u90fd\u662f\u5728\u90a3\u6b21\u7405\u739b\u5c71\u4e0a\u5f00\u7684\u7eff\u6797\u5927\u4f1a\u4e4b\u4e2d\uff0c\u548c\u891a\u4e91\u5cf0\u4ea4\u8fc7\u624b\u7684\u3002\u5f53\u65f6\u5d14\u9547 \u5c71\u4ee5\u5927\u529b\u91d1\u521a\u638c\u548c\u891a\u4e91\u5cf0\u6bd4\u8bd5\uff0c\u8d25\u4e86\u4e00\u62db\uff0c\u67f3\u6d1e\u5929\u4ee5\u4e09\u5341\u516d\u624b\u8fde\u73af\u593a\u547d\u5251\u6cd5\u4e0e\u891a\u4e91\u5cf0\u6bd4\u5251\uff0c \u5219\u8d62\u4e86\u4e00\u62db\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u90fd\u662f\u7eff\u6797\u4e2d\u54cd\u94db\u94db\u7684\u597d\u6c49\uff0c\u5f53\u65f6\u53ea\u56e0\u5374\u4e0d\u8fc7\u6df3\u4e8e\u5468\u7684\u60c5\u9762\uff0c\u800c\u53c8\u4e0d\u77e5\u6df3\u4e8e\u5468\u6697\u901a \u9791\u5b50\uff0c\u8fd9\u624d\u63a5\u53d7\u4e86\u4ed6\u7684\u9080\u8bf7\uff0c\u4e3a\u4ed6\u52a9\u9635\u7684\u3002\u4e8b\u60c5\u8fc7\u540e\uff0c\u4e24\u4eba\u90fd\u662f\u5341\u5206\u540e\u6094\uff0c\u5bf9\u891a\u4e91\u5cf0\u4e5f\u9887\u611f \u6b49\u610f\uff0c\u6545\u6b64\u8fd9\u6b21\u6070\u5de7\u78b0\u4e0a\u4e86\u891a\u4e91\u5cf0\u53d7\u6b66\u5f53\u5f1f\u5b50\u7684\u56f4\u653b\uff0c\u4e24\u4eba\u5c31\u4ee5\u9c81\u4ef2\u8fde\u81ea\u4efb\uff0c\u51fa\u6765\u4e3a\u4ed6\u4eec\u4f5c \u8c03\u505c\u4e86\u3002 \u3000\u3000\u67f3\u6d1e\u5927\u662f\u5251\u672f\u5927\u540d\u5bb6\uff0c\u8f7b\u6613\u5730\u5c31\u5316\u89e3\u4e86\u5f20\u5143\u5409\u7684\u5251\u62db\uff1b\u5d14\u9547\u5c71\u7684\u5927\u529b\u91d1\u521a\u638c\u4e5f\u4e0d\u8fc7\u4ec5\u900a \u891a\u4e91\u5cf0\uff0c\u6545\u6b64\u4ed6\u4e00\u51fa\u624b\uff0c\u548c\u4e54\u5143\u58ee\u3001\u5b63\u5143\u4f26\u5408\u529b\uff0c\u4e5f\u5c31\u52c9\u5f3a\u53ef\u4ee5\u654c\u5f97\u4f4f\u891a\u3001\u8c37\u4e8c\u4eba\u7684\u201c\u96f7\u7535 \u4ea4\u8f70\u201d\u4e86\u3002 \u3000\u3000\u4ed6\u4eec\u4e8c\u4eba\uff0c\u4e00\u4e2a\u5e2e\u5fd9\u4e54\u5143\u58ee\u62b5\u5fa1\u4e86\u201c\u5929\u96f7\u529f\u201d\uff0c\u4e00\u4e2a\u5e2e\u5fd9\u8c37\u6db5\u865a\u5316\u89e3\u4e86\u5f20\u5143\u5409\u7684\u6740\u7740\u5251 \u62db\uff0c\u786e\u5b9e\u53ef\u4ee5\u8bf4\u5f97\u662f\u5e76\u65e0\u504f\u8892\uff0c\u8bc1\u660e\u4e86\u4ed6\u4eec\u7684\u786e\u662f\u6765\u505a\u9c81\u4ef2\u8fde\u7684\u3002\u56e0\u6b64\u5f20\u5143\u5409\u867d\u7136\u5fc3\u6000\u6124 \u6068\uff0c\u5374\u4e5f\u65e0\u53ef\u5948\u4f55\u3002 \u3000\u3000\u67f3\u6d1e\u5929\u9053\uff1a\u201c\u4e0d\u77e5\u6b66\u5f53\u56db\u4fa0\u4f55\u6545\u4e0e\u891a\u3001\u8c37\u4e8c\u5144\u8d77\u4e86\u8bef\u4f1a\uff1f\u201d \u3000\u3000\u4e54\u5143\u58ee\u4ee5\u638c\u95e8\u5f1f\u5b50\u7684\u8eab\u4efd\u8bf4\u9053\uff1a\u201c\u8c37\u6db5\u865a\u884c\u4e3a\u4e0d\u7aef\uff0c\u593a\u4e86\u6211\u5e08\u5f1f\u7684\u672a\u5a5a\u59bb\uff0c\u53c8\u66fe\u6043\u5f3a\u6bb4 \u8fb1\u4e86\u6211\u7684\u53e6\u5916\u4e24\u4e2a\u5e08\u5f1f\u3002\u6b66\u5f53\u5f1f\u5b50\u5c82\u80fd\u5fcd\u53d7\u5947\u803b\u5927\u8fb1\uff1f\u4e24\u4f4d\u6765\u4f5c\u8c03\u4eba\uff0c\u76db\u60c5\u53ef\u611f\uff0c\u4f46\u8fd9\u4ef6\u4e8b \u60c5\uff0c\u8fd8\u662f\u8bf7\u4e24\u4f4d\u4e0d\u5fc5\u63d2\u624b\u7684\u597d\u3002\u201d \u3000\u3000\u67f3\u6d1e\u5929\u9053\uff1a\u201c\u5f20\u4e09\u4fa0\u7684\u672a\u5a5a\u59bb\u53ef\u662f\u5ddd\u897f\u4e25\u5927\u4fa0\u7684\u5973\u516c\u5b50\u4e48\uff1f\u201d \u3000\u3000\u5f20\u5143\u5409\u8e0c\u8e87\u672a\u7b54\uff0c\u4e54\u5143\u58ee\u8bf4\u9053\uff1a\u201c\u662f\u3002\u4e24\u4f4d\u65e2\u7136\u77e5\u9053\u5176\u4e2d\u539f\u59d4\uff0c\u90a3\u5c31\u5e94\u8be5\u77e5\u9053\u7406\u4e8f\u7684\u4e0d \u662f\u6211\u4eec\u4e86\u3002\u201d \u3000\u3000\u67f3\u6d1e\u5929\u9053\uff1a\u201c\u542c\u8bf4\u5f20\u4e09\u4fa0\u66fe\u5411\u4e25\u5927\u4fa0\u58f0\u8a00\u9000\u5a5a\uff0c\u4e0d\u77e5\u662f\u5426\u5c5e\u5b9e\uff1f\u201d \u3000\u3000\u5f20\u5143\u5409\u521a\u624d\u4e4b\u6240\u4ee5\u8e0c\u8e87\u4e0d\u7b54\uff0c\u5c31\u6b63\u662f\u4e3a\u4e86\u8fd9\u4e2a\u7f18\u6545\u3002\u5f53\u4e0b\u6124\u7136\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4ed6\u4eec\u4e00\u5bf9 \u72d7\u7537\u5973\u95f9\u51fa\u4e86\u90a3\u6837\u4e0d\u8981\u8138\u7684\u4e8b\u60c5\uff0c\u6211\u8fd8\u80fd\u8981\u8fd9\u4e2a\u8d31\u4eba\u5417\uff1f\u201d \u3000\u3000\u8c37\u6db5\u865a\u6012\u9053\uff1a\u201c\u5f20\u5143\u5409\uff0c\u4f60\u5634\u91cc\u653e\u5e72\u51c0\u4e9b\uff01\u201d \u3000\u3000\u67f3\u6d1e\u5929\u8fde\u5fd9\u8bf4\u9053\uff1a\u201c\u4e24\u4f4d\u8bf7\u522b\u5435\u5634\u3002\u5982\u6b64\u8bf4\u6765\uff0c\u90a3\u4f4d\u4e25\u5c0f\u59d0\u662f\u7518\u5fc3\u60c5\u613f\u8ddf\u8fd9\u4f4d\u8c37\u5144\u7684 \u4e86\uff0c\u5927\u4e08\u592b\u4f55\u60a3\u65e0\u59bb\uff0c\u4e25\u5c0f\u59d0\u65e2\u7136\u5fc3\u5411\u522b\u4eba\uff0c\u5f20\u5144\u53c8\u4f55\u5fc5\u653e\u5728\u5fc3\u4e0a\uff1f\u201d \u3000\u3000\u5f20\u5143\u5409\u6012\u9053\uff1a\u201c\u90a3\u8d31\u4eba\u6211\u53ef\u4ee5\u4e0d\u8981\uff0c\u8fd9\u53e3\u6c14\u5374\u662f\u975e\u51fa\u4e0d\u53ef\u3002\u201d \u3000\u3000\u4e54\u5143\u58ee\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u6b66\u5f53\u5f1f\u5b50\u540c\u4ec7\u654c\u5ffe\uff0c\u4e0d\u96ea\u6b64\u8fb1\uff0c\u6127\u5bf9\u5e08\u95e8\u3002\u201d \u3000\u3000\u67f3\u6d1e\u5929\u7b11\u9053\uff1a\u201c\u5982\u4eca\u5927\u654c\u5f53\u524d\uff0c\u79c1\u4eba\u6069\u6028\u662f\u5426\u53ef\u4ee5\u6682\u6401\u4e00\u8fb9\uff1f\u8bf7\u6b66\u5f53\u56db\u4fa0\u8003\u8651\u8003\u8651\u3002\u201d \u3000\u3000\u5d14\u9547\u5c71\u662f\u4e2a\u5fc3\u76f4\u53e3\u5feb\u7684\u6c49\u5b50\uff0c\u5fcd\u4e0d\u4f4f\u813e\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u5404\u4f4d\u82e5\u7136\u4e00\u5b9a\u4e0d\u80af\u542c\u4ece\u6211\u4eec\u7684\u529d \u89e3\uff0c\u90a3\u5c31\u91cd\u65b0\u518d\u6253\u5427\uff0c\u4e0d\u8fc7\uff0c\u4f9d\u6211\u770b\u6765\uff0c\u4f60\u4eec\u53cc\u65b9\u53ea\u6015\u90fd\u662f\u53d6\u80dc\u4e0d\u6613\uff0c\u53ea\u6709\u4e24\u8d25\u4ff1\u4f24\u800c \u5df2\u3002\u201d \u3000\u3000\u5d14\u9547\u5c71\u662f\u5e2e\u5fd9\u4e54\u5143\u58ee\u62b5\u5fa1\u4e86\u201c\u5929\u96f7\u529f\u201d\u7684\u4eba\uff0c\u5018\u82e5\u521a\u624d\u6ca1\u6709\u4ed6\u51fa\u638c\u76f8\u52a9\uff0c\u4e54\u5143\u58ee\u6050\u6015\u4e0d \u6b7b\u4e5f\u8981\u91cd\u4f24\u4e86\u3002\u4e54\u5143\u58ee\u4e0d\u80fd\u4e0d\u5356\u4ed6\u51e0\u5206\u9762\u5b50\u3002\u800c\u4e14\u5d14\u9547\u5c71\u8bf4\u7684\uff0c\u7684\u786e\u4e5f\u662f\u4e8b\u5b9e\u3002\u4e54\u5143\u58ee\u662f\u53ef \u4ee5\u63a5\u4efb\u638c\u95e8\u7684\u5927\u5f1f\u5b50\uff0c\u60f3\u5230\u4e86\u201c\u9526\u7ee3\u524d\u7a0b\u201d\uff0c\u81ea\u4e5f\u4e0d\u613f\u820d\u4e86\u6027\u547d\uff0c\u548c\u5bf9\u65b9\u540c\u5f52\u4e8e\u5c3d\u4e86\u3002 \u3000\u3000\u4e54\u5143\u58ee\u601d\u5ff5\u53ca\u6b64\uff0c\u53ea\u597d\u81ea\u4e0b\u53f0\u9636\uff0c\u8bf4\u9053\uff1a\u201c\u770b\u5728\u67f3\u8235\u4e3b\u548c\u5d14\u8235\u4e3b\u7684\u9762\u4e0a\uff0c\u8fd9\u8fc7\u8282\u6211\u4eec\u4eca \u65e5\u53ef\u4ee5\u6682\u65f6\u6401\u4e0b\uff0c\u4ee5\u540e\u5982\u4f3a\uff0c\u5f85\u6211\u4eec\u56de\u5c71\u7980\u544a\u5e08\u5c0a\u4e4b\u540e\u518d\u8bf4\u3002\u201d \u3000\u3000\u67f3\u6d1e\u5929\u62f1\u624b\u8bf4\u9053\uff1a\u201c\u5982\u6b64\u4e5f\u597d\uff0c\u591a\u8c22\u4e54\u5927\u4fa0\u80af\u7ed9\u6211\u4eec\u8fd9\u4e2a\u9762\u5b50\u4e86\u3002\u201d \u3000\u3000\u8fd9\u573a\u67b6\u65e2\u7136\u4e0d\u80fd\u518d\u6253\u4e0b\u53bb\uff0c\u4e54\u5143\u58ee\u81ea\u89c9\u65e0\u989c\uff0c\u4fbf\u5411\u53f2\u7528\u5a01\u544a\u8f9e\uff0c\u5e26\u9886\u4e09\u4e2a\u5e08\u5f1f\u7070\u6e9c\u6e9c\u5730 \u8d70\u4e86\u3002 \u3000\u3000\u67f3\u6d1e\u5929\u7b11\u9053\uff1a\u201c\u5982\u4eca\u96e8\u8fc7\u5929\u6674\uff0c\u54b1\u4eec\u8be5\u7ed9\u53f2\u8001\u82f1\u96c4\u62dc\u5bff\u4e86\u3002\u8bf8\u5144\u3001\u8c37\u5144\u8bf7\u8fc7\u6765\u4e0e\u4e3b\u4eba\u76f8 \u89c1\u5427\u3002\u201d \u3000\u3000\u67f3\u6d1e\u5929\u4ee5\u4e3a\u201c\u96e8\u8fc7\u5929\u775b\u201d\uff0c\u600e\u77e5\u201c\u4e00\u6ce2\u672a\u5e73\uff0c\u4e00\u6ce2\u53c8\u8d77\u201d\uff0c\u53f2\u7528\u5a01\u9762\u631f\u5bd2\u971c\uff0c\u6de1\u6de1\u8bf4 \u9053\uff1a\u201c\u4e14\u6162\uff01\u201d \u3000\u3000\u67f3\u6d1e\u5929\u6014\u4e86\u4e00\u6014\uff0c\u8bf4\u9053\uff1a\u201c\u53f2\u8001\u82f1\u96c4\u6709\u4f55\u89c1\u6559\uff1f\u201d \u3000\u3000\u53f2\u7528\u5a01\u9053\uff1a\u201c\u79c1\u4ec7\u53ef\u4e86\uff0c\u5927\u8282\u5982\u4f55\uff1f\u201d \u3000\u3000\u67f3\u6d1e\u5929\u9053\uff1a\u201c\u4ec0\u4e48\u5927\u8282\uff1f\u8bf7\u53f2\u8001\u82f1\u96c4\u660e\u767d\u89c1\u793a\u3002\u201d \u3000\u3000\u53f2\u7528\u5a01\u9053\uff1a\u201c\u891a\u4e91\u5cf0\u662f\u91d1\u56fd\u56fd\u5e08\u9633\u5929\u96f7\u7684\u5e08\u4f84\uff0c\u542c\u8bf4\u4ed6\u5728\u9633\u5929\u96f7\u95e8\u4e0b\u52a9\u7ea3\u4e3a\u8650\u5df2\u7ecf\u6709\u597d \u51e0\u5e74\u4e86\uff0c\u67f3\u8235\u4e3b\u7adf\u6beb\u65e0\u6240\u77e5\u4e48\uff1f\u201d \u3000\u3000\u67f3\u6d1e\u5929\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u539f\u6765\u4f60\u4eec\u8fd8\u6709\u8fd9\u4e2a\u8bef\u4f1a\uff01\u8fd9\u4ef6\u4e8b\u6211\u65e9\u5df2\u77e5\u9053\uff0c\u4f46\u6709\u4e00\u4e9b\u4e8b\u60c5\u6050\u6015\u53f2 \u8001\u82f1\u96c4\u8fd8\u4e0d\u77e5\u9053\u5427\uff1f\u201d \u3000\u3000\u53f2\u7528\u5a01\u9053\uff1a\u201c\u54ea\u4e9b\u4e8b\u60c5\uff1f\u201d \u3000\u3000\u67f3\u6d1e\u5929\u9053\uff1a\u201c\u891a\u4e91\u5cf0\u5982\u4eca\u662f\u4e49\u519b\u76df\u4e3b\u674e\u601d\u5357\u7684\u5de6\u53f3\u624b\uff0c\u4ee5\u524d\u4ed6\u5728\u9633\u5929\u96f7\u624b\u4e0b\uff0c\u4e43\u662f\u5949\u5e08 \u547d\u800c\u4e3a\uff0c\u5047\u610f\u52a9\u91d1\uff0c\u5176\u5b9e\u5219\u662f\u548c\u4e49\u519b\u6697\u901a\u6d88\u606f\u7684\u3002\u800c\u4e14\u636e\u6211\u6240\u77e5\uff0c\u4ed6\u65e9\u5df2\u53db\u51fa\u5927\u90fd\uff0c\u76ee\u524d\u6b63 \u5728\u51c6\u5907\u5e72\u4e00\u4ef6\u5927\u4e8b\u5462\uff01\u8fd9\u4ef6\u5927\u4e8b\u82e5\u80fd\u6210\u529f\uff0c\u5bf9\u4e49\u519b\u7684\u5e2e\u52a9\u66f4\u662f\u4e0d\u5c0f\uff01\u201d \u3000\u3000\u5f20\u901b\u9053\uff1a\u201c\u4ec0\u4e48\u5927\u4e8b\uff1f\u201d \u3000\u3000\u67f3\u6d1e\u5929\u9053\uff1a\u201c\u65f6\u673a\u672a\u5230\uff0c\u8bf7\u6055\u6211\u4e0d\u4fbf\u4ee3\u4ed6\u5f53\u4f17\u8bf4\u51fa\u6765\u4e86\u3002\u201d \u3000\u3000\u67f3\u6d1e\u5929\u8bf4\u891a\u4e91\u5cf0\u5728\u9633\u5929\u96f7\u624b\u4e0b\u4e43\u662f\u5047\u610f\u52a9\u91d1\uff0c\u8fd9\u8bf4\u6cd5\u548c\u5218\u5927\u4e3a\u7684\u8bf4\u6cd5\u76f8\u7b26\uff0c\u53f2\u7528\u5a01\u4e0d\u89c9 \u5403\u4e86\u4e00\u60ca\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u96be\u9053\u5362\u4e09\u54e5\u8bf4\u7684\u7adf\u662f\u8c0e\u8bdd\uff0c\u6709\u610f\u9677\u5bb3\u8fd9\u4e2a\u59d3\u891a\u7684\u4e0d\u6210\u3002\u201d \u3000\u3000\u4f46\u66f4\u4ee4\u4f17\u5bbe\u5ba2\u5403\u60ca\u7684\u662f\u67f3\u6d1e\u5929\u5e26\u6765\u7684\u65b0\u6d88\u606f\uff1a\u891a\u4e91\u5cf0\u7adf\u7136\u662f\u4e49\u519b\u76df\u4e3b\u674e\u601d\u5357\u7684\u5de6\u53f3\u624b\u3002 \u3000\u3000\u53f2\u7528\u5a01\u9053\uff1a\u201c\u8bf7\u6055\u8001\u673d\u591a\u8651\uff0c\u4f60\u8fd9\u4f4d\u891a\u670b\u53cb\u5373\u4f7f\u5982\u4eca\u662f\u5728\u4e49\u519b\u4e4b\u4e2d\uff0c\u7109\u77e5\u4ed6\u53c8\u4e0d\u662f\u4f5c \u4f2a\uff1f\u6709\u8c01\u662f\u771f\u6b63\u77e5\u9053\u4ed6\u7684\u6765\u5386\uff0c\u80fd\u591f\u7ed9\u4ed6\u8fd9\u6837\u62c5\u4fdd\u7684\u5417\uff1f\u90a3\u4e9b\u6d88\u606f\uff0c\u67f3\u8235\u4e3b\u4f60\u53c8\u662f\u4ece\u54ea\u91cc\u542c \u6765\u7684\u3002\u201d \u3000\u3000\u67f3\u6d1e\u5929\u9053\uff1a\u201c\u4e0d\u662f\u542c\u6765\u7684\uff0c\u800c\u662f\u6211\u4e5f\u66fe\u5728\u573a\u7684\u3002\u800c\u4e14\u6700\u8fd1\u6211\u8fd8\u66fe\u5230\u8fc7\u7405\u739b\u5c71\uff0c\u6240\u4ee5\u53f2\u8001 \u82f1\u96c4\u95ee\u8c01\u6562\u62c5\u4fdd\u4ed6\uff0c\u6211\u5c31\u6562\u62c5\u4fdd\u4ed6\uff01\u6216\u8bb8\u6211\u4eba\u5fae\u8a00\u8f7b\uff0c\u8001\u82f1\u96c4\u4e0d\u6562\u76f8\u4fe1\uff0c\u90a3\u4e48\u6211\u53ef\u4ee5\u8bf4\u51fa\u4e00 \u4e2a\u4eba\u3002\u201d \u3000\u3000\u53f2\u7528\u5a01\u9053\uff1a\u201c\u8c01\uff1f\u201d \u3000\u3000\u67f3\u6d1e\u5929\u9053\uff1a\u201c\u6c5f\u5357\u5927\u4fa0\u5b5f\u5c11\u521a\uff01\u891a\u4e91\u5cf0\u7684\u6765\u5386\u548c\u4ed6\u5c06\u8981\u8fdb\u884c\u7684\u5927\u4e8b\uff0c\u8fd9\u4e00\u4e9b\u90fd\u662f\u5b5f\u5927\u4fa0 \u4eb2\u53e3\u544a\u8bc9\u6211\u7684\uff01\u5404\u4f4d\u5927\u7ea6\u4fe1\u5f97\u8fc7\uff0c\u6211\u51b3\u4e0d\u6562\u4e71\u9020\u5b5f\u5927\u4fa0\u7684\u8c23\u8a00\u5427\uff01\u201d\u6b63\u662f\uff1a \u3000\u3000\u771f\u91d1\u4e0d\u6015\u6d2a\u7089\u706b\uff0c\u771f\u5047\u5206\u660e\u65e9\u4e0e\u8fdf\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b\u2014\u2014\u7b2c\u56db\u5341\u516b\u56de\u3000\u559c\u6709\u8d24\u5f92\u4f20\u5251\u6cd5\u3000\u8981\u8bdb\u9006\u8d3c\u62a4\u5e08\u95e8 \u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b \u7b2c\u56db\u5341\u516b\u56de\u3000\u559c\u6709\u8d24\u5f92\u4f20\u5251\u6cd5\u3000\u8981\u8bdb\u9006\u8d3c\u62a4\u5e08\u95e8 \u3000\u3000\u8c37\u6db5\u865a\u6697\u81ea\u60f3\u9053\uff1a\u201c\u539f\u6765\u4ed6\u4eec\u662f\u521a\u4ece\u7405\u739b\u5c71\u6765\u7684\uff0c\u602a\u4e0d\u5f97\u4ed6\u4eec\u77e5\u9053\u6211\u548c\u4e25\u70f7\u7684\u4e8b\u60c5\u3002\u201d \u3000\u3000\u67f3\u6d1e\u5929\u4ee5\u5251\u672f\u79f0\u96c4\u6b66\u6797\uff0c\u4ed6\u672c\u4eba\u5728\u6c5f\u6e56\u4e0a\u5df2\u662f\u751a\u6709\u58f0\u671b\uff0c\u518d\u52a0\u4e0a\u6c5f\u5357\u5927\u4fa0\u5b5f\u5c11\u521a\u7684\u540d \u5934\uff0c\u4e0d\u7531\u5f97\u4f17\u4eba\u4e0d\u4fe1\u4ed6\u7684\u8bf4\u8bdd\u3002 \u3000\u3000\u53f2\u7528\u5a01\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u53ea\u6015\u5f53\u771f\u662f\u6709\u8bef\u4f1a\u4e86\u3002\u54a6\uff0c\u5362\u4e09\u7237\u5462\uff1f\u201d \u3000\u3000\u53f2\u7528\u5a01\u662f\u60f3\u627e\u5362\u9999\u4ead\u51fa\u6765\uff0c\u597d\u6559\u53cc\u65b9\u8bf4\u4e2a\u660e\u767d\u7684\uff0c\u54ea\u77e5\u7adf\u662f\u904d\u5bfb\u4e0d\u89c1\u3002 \u3000\u3000\u4e0d\u4f46\u5362\u9999\u4ead\u4e0d\u89c1\uff0c\u7ecf\u4ed6\u5f15\u8350\u6df7\u5165\u53f2\u95e8\u7684\u90a3\u4e2a\u5f1f\u5b50\u4e01\u8fdb\u4e5f\u4e0d\u89c1\u3002\u539f\u6765\u4ed6\u4eec\u770b\u89c1\u67f3\u3001\u5d14\u4e8c\u4eba \u6765\u5230\uff0c\u60c5\u77e5\u4e0d\u5999\uff0c\u65e9\u5df2\u6e9c\u4e86\u3002 \u3000\u3000\u5218\u5927\u4e3a\u8fd9\u624d\u5927\u5403\u4e00\u60ca\uff0c\u548c\u4ed6\u8bf4\u9053\uff1a\u201c\u5e08\u7236\uff0c\u6211\u672c\u6765\u4e0d\u6562\u6000\u7591\u5362\u4e09\u53d4\u7684\uff0c\u4f46\u7167\u6b64\u60c5\u5f62\u770b \u6765\uff0c\u53ea\u6015\u3001\u53ea\u6015\u2026\u2026\u201d \u3000\u3000\u53f2\u7528\u5a01\u6bd5\u7adf\u662f\u4e2a\u6d1e\u660e\u4e16\u6545\u3001\u901a\u8fbe\u4eba\u60c5\u7684\u8001\u82f1\u96c4\uff0c\u5fc3\u91cc\u6697\u6697\u53eb\u4e86\u4e00\u58f0\u201c\u60ed\u6127\u3002\u201d\u8bf4\u9053\uff1a \u201c\u4e8b\u60c5\u5df2\u662f\u6c34\u843d\u77f3\u51fa\uff0c\u4e0d\u7528\u8bf4\u8fd9\u4e24\u4e2a\u4eba\u90fd\u662f\u5403\u91cc\u6252\u5916\u7684\u5978\u7ec6\u4e86\u3002\u602a\u53ea\u602a\u6211\u592a\u7cca\u6d82\uff0c\u51e0\u4e4e\u8bef\u4fe1 \u8c17\u8a00\uff0c\u5bb3\u4e86\u597d\u4eba\u3002\u201d\u8bf4\u7f62\uff0c\u5373\u5411\u891a\u3001\u8c37\u4e8c\u4eba\u8d54\u793c\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u753b\u864e\u753b\u76ae\u96be\u753b\u9aa8\uff0c\u77e5\u4eba\u77e5\u9762\u4e0d\u77e5\u5fc3\u3002\u9b3c\u8d3c\u4f0e\u4fe9\uff0c\u539f\u662f\u96be\u9632\uff0c\u665a\u8f88\u4f46\u6c42\u6c34\u843d \u77f3\u51fa\uff0c\u53d7\u70b9\u59d4\u5c48\uff0c\u53c8\u6709\u4f55\u59a8\uff1f\u201d \u3000\u3000\u53f2\u7528\u5a01\u9053\uff1a\u201c\u4f46\u6211\u53ef\u8981\u591a\u8c22\u4f60\u7684\u5927\u5fb7\u5462\uff0c\u82e5\u4e0d\u662f\u4f60\u6765\u63ed\u53d1\u6b64\u4e8b\uff0c\u6211\u4e00\u76f4\u88ab\u8499\u5728\u9f13\u91cc\uff0c\u6050 \u6015\u5c31\u8981\u7ed9\u4ed6\u4eec\u7d2f\u5f97\u8eab\u8d25\u540d\u88c2\u4e86\uff01\u201d \u3000\u3000\u5362\u3001\u4e01\u4e8c\u4eba\u4e00\u8d70\uff0c\u771f\u76f8\u5927\u767d\uff0c\u96e8\u8fc7\u5929\u6674\uff0c\u4f17\u4eba\u56de\u5230\u5bff\u5802\u7ed9\u53f2\u7528\u5a01\u62dc\u5bff\uff0c\u81ea\u6709\u4e00\u756a\u70ed\u95f9\uff0c \u4e0d\u5fc5\u7ec6\u8bf4\u3002 \u3000\u3000\u5bff\u7b75\u6563\u540e\uff0c\u67f3\u6d1e\u5929\u5bf9\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u5728\u8fd9\u91cc\u78b0\u89c1\u4e86\u4f60\uff0c\u6709\u4e00\u4ef6\u4e8b\u60c5\uff0c\u6211\u6b63\u60f3\u548c\u4f60\u5546 \u91cf\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\u60c5\uff1f\u201d \u3000\u3000\u67f3\u6d1e\u5929\u6084\u58f0\u9053\uff1a\u201c\u662f\u548c\u9633\u5929\u96f7\u6709\u5173\u7684\u4e8b\u60c5\u3002\u4f60\u4eec\u4e24\u4f4d\u8981\u4e3a\u8d35\u6d3e\u6e05\u7406\u5e08\u95e8\uff0c\u8fd9\u4ef6\u4e8b\u60c5\u5b5f\u5927 \u4fa0\u5df2\u7ecf\u544a\u8bc9\u6211\u4e86\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u559c\u51fa\u671b\u5916\uff0c\u5f53\u4e0b\u4fbf\u5411\u53f2\u7528\u5a01\u501f\u4e86\u4e00\u95f4\u5bc6\u5ba4\uff0c\u891a\u3001\u8c37\u3001\u5d14\u3001\u67f3\u56db\u4eba\uff0c\u540c\u8fdb\u8fd9\u95f4\u5bc6\u5ba4 \u4f1a\u5546\u3002 \u3000\u3000\u67f3\u6d1e\u5929\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u6211\u8d77\u521d\u4e5f\u662f\u6599\u60f3\u4e0d\u5230\u7684\u3002\u4f60\u731c\u662f\u4ec0\u4e48\u4e8b\u60c5\uff1f\u539f\u6765\u9633\u5929\u96f7\u8fd9\u53ae\uff0c \u7adf\u7136\u9b3c\u8ff7\u5fc3\u7a8d\uff0c\u4ee5\u4e3a\u6211\u548c\u5d14\u5144\u4e5f\u662f\u50cf\u4ed6\u90a3\u6837\u8d2a\u56fe\u5229\u7984\u7684\u4eba\uff0c\u5c45\u7136\u770b\u4e2d\u4e86\u6211\u4eec\uff0c\u627e\u4e0a\u95e8\u6765 \u4e86\uff01\u201d\u201d \u3000\u3000\u891a\u4e91\u5cf0\u6014\u4e86\u4e00\u6014\uff0c\u604d\u7136\u5927\u609f\uff0c\u7b11\u9053\uff1a\u201c\u5bf9\u4e86\uff0c\u4ed6\u5927\u7ea6\u662f\u56e0\u4e3a\u77e5\u9053\u4f60\u4eec\u66fe\u7ed9\u6df3\u4e8e\u5468\u52a9\u62f3\uff0c \u6545\u6b64\u8fd8\u4ee5\u4e3a\u4f60\u4eec\u4e5f\u662f\u53ef\u4ee5\u5229\u7528\u7684\u4e86\u3002\u201d \u3000\u3000\u67f3\u6d1e\u5929\u9053\uff1a\u201c\u6211\u4eec\u4e4b\u6240\u4ee5\u7ed9\u6df3\u4e8e\u5468\u53bb\u52a9\u62f3\uff0c\u90a3\u662f\u56e0\u4e3a\u5374\u4e0d\u8fc7\u60c5\u9762\u7684\u7f18\u6545\u3002\u5f53\u65f6\u6211\u4eec\u4e5f\u8fd8 \u672a\u77e5\u9053\u4ed6\u79c1\u901a\u9791\u5b50\u7684\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u6211\u660e\u767d\u3002\u67f3\u5144\uff0c\u4f60\u65e0\u9700\u89e3\u91ca\u4e86\u3002\u8bf7\u4f60\u544a\u8bc9\u6211\uff0c\u9633\u5929\u96f7\u662f\u600e\u6837\u6765\u627e\u4f60\u7684\uff1f\u4ed6 \u6765\u627e\u4f60\uff0c\u53c8\u8981\u4f60\u505a\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u67f3\u6d1e\u5929\u9053\uff1a\u201c\u4ed6\u6d3e\u4e86\u4e00\u4e2a\u5bc6\u4f7f\u6765\u548c\u6211\u4eec\u8054\u7edc\uff0c\u5c01\u5b98\u8bb8\u613f\uff0c\u8981\u6211\u4eec\u6697\u4e2d\u52a9\u4ed6\u3002\u201d \u3000\u3000\u5d14\u9547\u5c71\u7b11\u9053\uff1a\u201c\u8bf4\u5f97\u66f4\u660e\u767d\u70b9\uff0c\u5c31\u662f\u8981\u6211\u4eec\u4ecd\u7136\u4ee5\u672c\u6765\u7684\u9762\u76ee\uff0c\u548c\u6c5f\u6e56\u540c\u9053\u53ae\u6df7\uff0c\u6697\u5730 \u91cc\u7ed9\u4ed6\u505a\u5978\u7ec6\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u90a3\u4f60\u4eec\u5982\u4f55\u5e94\u4ed8\uff1f\u201d \u3000\u3000\u5d14\u9547\u5c71\u9053\uff1a\u201c\u5f53\u65f6\u6211\u672c\u6765\u5c31\u60f3\u628a\u90a3\u4e2a\u4f7f\u8005\u4e00\u5200\u4e24\u6bb5\uff0c\u4f46\u67f3\u5144\u4e0d\u8bb8\u6211\u8fd9\u6837\u505a\u3002\u201d \u3000\u3000\u67f3\u6d1e\u5929\u9053\uff1a\u201c\u6211\u662f\u60f3\u5230\u4e0d\u59a8\u5c06\u8ba1\u5c31\u8ba1\u3002\u56e0\u6b64\u6211\u5c31\u4e0d\u52a8\u58f0\u8272\uff0c\u597d\u597d\u5730\u62db\u5f85\u90a3\u4e2a\u4f7f\u8005\u3002\u6211\u8bf4 \u5179\u4e8b\u4f53\u5927\uff0c\u9700\u5f97\u4ece\u957f\u8ba1\u8bae\uff0c\u4e0d\u662f\u6211\u4fe1\u4e0d\u8fc7\u4f60\uff0c\u4f46\u6700\u597d\u80fd\u591f\u8bf7\u5f97\u9633\u56fd\u5e08\u524d\u6765\u5c0f\u5be8\uff0c\u548c\u6211\u9762 \u8c08\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u7b11\u9053\uff1a\u201c\u8fd9\u6b63\u662f\u98de\u9f99\u5c71\u4ee5\u524d\u90a3\u4e2a\u5be8\u4e3b\u7aa6\u5b89\u5e73\u7528\u8fc7\u7684\u4e00\u4e2a\u8ba1\u7b56\uff0c\u7aa6\u5b89\u5e73\u548c\u9633\u5929\u96f7\u6697 \u4e2d\u52fe\u901a\uff0c\u9a97\u674e\u76df\u4e3b\u5230\u4ed6\u7684\u5c71\u5be8\u53bb\u56fe\u8c0b\u52a0\u5bb3\u3002\u8fd9\u4ef6\u4e8b\u60f3\u5fc5\u4f60\u4eec\u5df2\u7ecf\u77e5\u9053\u4e86\uff1f\u201d \u3000\u3000\u5d14\u9547\u5c71\u9053\uff1a\u201c\u6211\u4eec\u662f\u5230\u4e86\u7405\u739b\u5c71\uff0c\u4f1a\u89c1\u4e86\u674e\u76df\u4e3b\u4e4b\u540e\u624d\u77e5\u9053\u7684\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u4ed6\u4eec\u7528\u8fc7\u7684\u8ba1\u7b56\uff0c\u6050\u6015\u9633\u5929\u96f7\u8fd9\u8001\u8d3c\u4e0d\u4f1a\u4e0a\u5f53\u5427\u3002\u201d \u3000\u3000\u67f3\u6d1e\u5929\u9053\uff1a\u201c\u674e\u76df\u4e3b\u4e5f\u662f\u8fd9\u4e48\u8bf4\u3002\u4e0d\u8fc7\u4e5f\u4e0d\u59a8\u8bd5\u8bd5\uff0c\u770b\u4ed6\u5982\u4f55\u56de\u590d\u3002\u201d \u3000\u3000\u5d14\u9547\u5c71\u9053\uff1a\u201c\u5f53\u65f6\u6211\u4eec\u548c\u90a3\u4e2a\u4f7f\u8005\u76f8\u7ea6\uff0c\u4e09\u4e2a\u6708\u5185\uff0c\u7b49\u5019\u4ed6\u4eec\u90a3\u8fb9\u7684\u56de\u590d\u3002\u90a3\u4e2a\u4f7f\u8005\u4e00 \u8d70\uff0c\u6211\u4eec\u5c31\u9a6c\u4e0a\u8d76\u5f80\u7405\u739b\u5c71\u5411\u674e\u76df\u4e3b\u7980\u544a\u6b64\u4e8b\u3002\u201d \u3000\u3000\u67f3\u6d1e\u5929\u9053\uff1a\u201c\u674e\u76df\u4e3b\u5df2\u7ecf\u7b54\u5e94\u6211\u4eec\uff0c\u5230\u65f6\u6d3e\u51e0\u4e2a\u9ad8\u624b\u524d\u6765\u76f8\u52a9\u3002\u5c31\u53ea\u6015\u9633\u5929\u96f7\u4e0d\u4e0a\u94a7\u3002 \u674e\u76df\u4e3b\u53c8\u8bf4\uff0c\u53ef\u60dc\u4f60\u4eec\u4e0d\u5728\u8fd9\u513f\uff0c\u5426\u5219\u5012\u662f\u4e00\u4e2a\u673a\u4f1a\u3002\u60f3\u4e0d\u5230\u5929\u968f\u4eba\u613f\uff0c\u5728\u8fd9\u91cc\u78b0\u4e0a\u4e86\u4f60 \u4eec\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u7b11\u9053\uff0c\u201c\u5018\u82e5\u9633\u5929\u96f7\u4e0a\u94a9\u7684\u8bdd\uff0c\u6211\u4eec\u5728\u8d35\u5be8\u57cb\u4f0f\uff0c\u4ee5\u9038\u5f85\u52b3\uff0c\u5012\u662f\u7701\u4e8b\u5f97\u591a\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u5c31\u662f\u4ed6\u4e0d\u4e0a\u94a9\uff0c\u4e5f\u5fc5\u4f1a\u6709\u56de\u590d\u7684\u3002\u770b\u4ed6\u7684\u56de\u590d\u5982\u4f55\uff0c\u6211\u4eec\u8fd8\u53ef\u4ee5\u5c06\u8ba1\u5c31 \u8ba1\u3002\u201d \u3000\u3000\u67f3\u6d1e\u5929\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u8001\u8d3c\u7eb5\u7136\u591a\u7591\uff0c\u4f46\u81f3\u5c11\u4ed6\u8fd8\u62ff\u4e0d\u51c6\u6211\u4eec\u662f\u52a9\u66f9\u52a9\u6c49\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u73b0\u5728\u8ddd\u79bb\u4e09\u6708\u4e4b\u524d\uff0c\u8fd8\u6709\u591a\u4e45\uff1f\u201d \u3000\u3000\u67f3\u6d1e\u5929\u9053\uff1a\u201c\u5982\u4eca\u672a\u8fc7\u4e00\u6708\uff0c\u8fd8\u6709\u4e24\u4e2a\u6708\u6709\u591a\u5462\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4e48\u65f6\u95f4\u8db3\u591f\u7528\u4e86\u3002\u5f85\u6211\u4eec\u56de\u5c71\u89c1\u8fc7\u4e86\u5bb6\u5e08\uff0c\u518d\u5230\u8d35\u5be8\u7b49\u5019\u6d88\u606f\u3002\u201d \u3000\u3000\u67f3\u6d1e\u5929\u84e6\u5730\u60f3\u8d77\u4e00\u4e8b\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u597d\uff01\u201d \u3000\u3000\u5d14\u9547\u5c71\u9053\uff1a\u201c\u4f55\u4e8b\u4e0d\u597d\uff1f\u201d \u3000\u3000\u67f3\u6d1e\u5929\u9053\uff1a\u201c\u54b1\u4eec\u5fd8\u8bb0\u5362\u9999\u4ead\u548c\u4e01\u8fdb\u8fd9\u4e24\u4e2a\u4eba\u4e86\uff0c\u4eca\u65e5\u6211\u4eec\u5728\u6b64\u5904\u7ed9\u891a\u5144\u8fa9\u51a4\uff0c\u8fd9\u4e24\u4e2a \u5978\u7ec6\u8fd8\u80fd\u4e0d\u8d76\u56de\u53bb\u5411\u9633\u5929\u96f7\u901a\u98ce\u62a5\u4fe1\u4e48\uff1f\u201d \u3000\u3000\u5d14\u9547\u5c71\u9053\uff1a\u201c\u53f2\u8001\u82f1\u96c4\u7edd\u4e0d\u4f1a\u8f7b\u6613\u653e\u8fc7\u8fd9\u4e24\u4e2a\u4eba\u7684\u3002\u4ed6\u95e8\u4e0b\u6709\u5341\u516d\u5f1f\u5b50\uff0c\u5ba2\u4eba\u4e2d\u66f4\u6709\u7684 \u662f\u5404\u8def\u82f1\u96c4\uff0c\u8c05\u8fd9\u4e24\u4e2a\u5978\u7ec6\u4e5f\u9003\u4e0d\u6389\u3002\u201d \u3000\u3000\u67f3\u6d1e\u5929\u9053\uff1a\u201c\u4f46\u4e07\u4e00\u7ed9\u4ed6\u4eec\u6f0f\u7f51\uff0c\u8dd1\u5230\u5927\u90fd\uff0c\u53ef\u5c31\u7cdf\u4e86\u3002\u201d \u3000\u3000\u5d14\u9547\u5c71\u6c89\u541f\u534a\u664c\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u8fd8\u6709\u4e00\u4e2a\u4e3b\u610f\u3002\u4f60\u5148\u56de\u5c71\u5be8\uff0c\u6211\u5feb\u9a6c\u8d76\u5f80\u5927\u90e8\uff0c\u5927\u90fd\u4e10\u5e2e \u5206\u8235\u7684\u8235\u4e3b\u4e0e\u6211\u76f8\u8bc6\uff0c\u6211\u8bf7\u4ed6\u4ee3\u4e3a\u7559\u5fc3\uff0c\u90a3\u4e24\u4e2a\u5978\u7ec6\u5018\u82e5\u9003\u5230\u5927\u90fd\uff0c\u4e10\u5e2e\u6d88\u606f\u7075\u901a\uff0c\u4e00\u5b9a\u4f1a \u6253\u542c\u51fa\u6765\u7684\u3002\u90a3\u65f6\u54b1\u4eec\u518d\u4f5c\u6253\u7b97\u3002\u201d \u3000\u3000\u67f3\u6d1e\u5929\u9053\uff1a\u201c\u8fd9\u53ea\u662f\u8865\u6551\u4e4b\u8ba1\uff0c\u4f46\u4e5f\u53ea\u597d\u5982\u6b64\u4e86\u3002\u201d \u3000\u3000\u8ba1\u8bae\u5df2\u5b9a\uff0c\u4ed6\u4eec\u56db\u4eba\u4fbf\u51fa\u6765\u5411\u53f2\u7528\u5a01\u8f9e\u884c\uff0c\u53f2\u7528\u5a01\u544a\u8bc9\u4ed6\u4eec\uff0c\u679c\u7136\u662f\u5df2\u6d3e\u4e86\u5341\u4e8c\u540d\u5f1f\u5b50 \u53bb\u8ffd\u8e2a\u90a3\u4e24\u4e2a\u5978\u7ec6\u4e86\uff0c\u5bbe\u5ba2\u4e4b\u4e2d\u4e5f\u679c\u7136\u6709\u8bb8\u591a\u81ea\u544a\u594b\u52c7\u5730\u5e2e\u5fd9\u53bb\u8ffd\u6355\u5978\u7ec6\u3002 \u3000\u3000\u5218\u5927\u4e3a\u9053\uff1a\u201c\u891a\u89c1\u3001\u8c37\u5144\uff0c\u6211\u9001\u4f60\u4eec\u4e00\u7a0b\u3002\u201d\u53f2\u7528\u5a01\u6d3e\u51fa\u7684\u5341\u4e8c\u540d\u5f1f\u5b50\u5e76\u65e0\u5218\u5927\u4e3a\u5728 \u5185\uff0c\u53ef\u80fd\u662f\u56e0\u4e3a\u8981\u4ed6\u907f\u5acc\u7684\u7f18\u6545\u3002 \u3000\u3000\u5218\u5927\u4e3a\u9001\u5230\u8def\u53e3\uff0c\u8bf4\u9053\uff1a\u201c\u891a\u5144\uff0c\u4ee4\u5e08\u53ef\u662f\u5728\u5317\u8292\u5c71\u9690\u5c45\uff1f\u201d\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u600e \u4e48\u77e5\u9053\u7684\uff1f\u201d\u4ed6\u8bb0\u5f97\u4ed6\u53ef\u6ca1\u6709\u548c\u5218\u5927\u4e3a\u8bf4\u8fc7\u5e08\u7236\u7684\u4e8b\u60c5\u3002 \u3000\u3000\u5218\u5927\u529b\u7b11\u9053\uff1a\u201c\u6211\u4e5f\u662f\u53bb\u5e74\u56de\u5bb6\u624d\u77e5\u9053\u7684\uff0c\u6211\u548c\u5bb6\u7236\u8bf4\u8d77\u9633\u5929\u96f7\u6709\u4e2a\u5e08\u4f84\u6697\u52a9\u4e49\u519b\uff0c\u5bb6 \u7236\u5c31\u731c\u5230\u4f60\u662f\u534e\u5927\u4fa0\u7684\u5f1f\u5b50\u4e86\u3002\u539f\u6765\u5bb6\u7236\u548c\u4ee4\u5e08\u4e5f\u662f\u76f8\u8bc6\u7684\uff0c\u4e0d\u8fc7\u8bb8\u591a\u5e74\u6ca1\u6709\u89c1\u9762\u7f62\u4e86\u3002\u524d \u5e74\u4ed6\u5230\u8fc7\u5317\u8292\u5c71\u62dc\u8bbf\u4ee4\u5e08\uff0c\u6ca1\u6709\u9047\u4e0a\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\u3002\u5bb6\u5e08\u524d\u5e74\u66fe\u5230\u5f00\u5c01\u8bbf\u53cb\uff0c\u5f53\u65f6\u6211\u8fd8\u5728\u5927\u90fd\uff0c\u4e5f\u662f\u540e\u6765\u624d\u77e5\u9053 \u7684\u3002\u201d \u3000\u3000\u5218\u5927\u4e3a\u9053\uff1a\u201c\u5c0f\u5f1f\u5bb6\u4f4f\u5e73\u8c37\u53bf\u53e4\u677e\u6750\uff0c\u5728\u5317\u8292\u5c71\u5317\u9762\u4e0d\u8fc7\u767e\u91cc\u4e4b\u9065\uff0c\u891a\u5144\u82e5\u662f\u6709\u7a7a\uff0c\u4e0d \u59a8\u5230\u820d\u4e0b\u4f4f\u4e24\u5929\u3002\u820d\u4e0b\u53ea\u6709\u5bb6\u7236\u548c\u5c0f\u59b9\uff0c\u891a\u5144\u5018\u82e5\u80fd\u7ed9\u5c0f\u5f1f\u634e\u4e2a\u5e73\u5b89\u4fe1\u513f\uff0c\u4ed6\u4eec\u4e00\u5b9a\u611f\u6fc0\u4e0d \u5c3d\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u6211\u4eec\u89c1\u4e86\u5bb6\u5e08\u4e4b\u540e\uff0c\u9a6c\u4e0a\u53c8\u8981\u8d76\u5f80\u67f3\u5be8\u4e3b\u90a3\u513f\uff0c\u4e0d\u77e5\u662f\u5426\u80fd\u62bd\u51fa\u7a7a\u6765\u3002\u4e0d \u8fc7\u5373\u4f7f\u5c0f\u5f1f\u4e0d\u80fd\u524d\u5f80\uff0c\u6211\u4e5f\u4f1a\u6258\u5bb6\u5e08\u7ed9\u8001\u4f2f\u634e\u4e2a\u4fe1\u7684\uff0c\u53cd\u6b63\u8ddd\u79bb\u4e0d\u662f\u592a\u8fdc\u3002\u201d \u3000\u3000\u5218\u5927\u4e3a\u597d\u50cf\u6709\u70b9\u5931\u671b\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u4e5f\u597d\uff0c\u591a\u8c22\u891a\u5144\u8d39\u795e\u4e86\u3002\u201d \u3000\u3000\u891a\u3001\u8c37\u4e8c\u4eba\u79bb\u5f00\u4e86\u53f2\u5bb6\u5e84\uff0c\u4fbf\u5373\u517c\u7a0b\u8d76\u8def\uff0c\u4e00\u8def\u65e0\u4e8b\uff0c\u8fd9\u65e5\u7ec8\u4e8e\u5230\u4e86\u5317\u8292\u5c71\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u5e26\u9886\u8c37\u6db5\u865a\u8d70\u5230\u5e08\u7236\u9690\u5c45\u4e4b\u6240\uff0c\u5c4b\u540e\u662f\u4e00\u7247\u6843\u6797\u3002\u5176\u65f6\u6b63\u662f\u9633\u6625\u4e09\u6708\uff0c\u653e\u773c\u671b \u53bb\uff0c\u53ea\u89c1\u6843\u82b1\u76db\u5f00\uff0c\u5b9b\u5982\u82b1\u6d77\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u5e08\u7236\u6b63\u5728\u6843\u6797\u7ec3\u5251\uff0c\u54b1\u4eec\u4e14\u522b\u5fd9\u60ca\u52a8\u4ed6\u3002\u201d \u3000\u3000\u4e24\u4eba\u6084\u6084\u8d70\u5165\u6843\u6797\uff0c\u53ea\u89c1\u4e00\u9053\u5251\u5149\uff0c\u77e2\u77eb\u5982\u9f99\uff0c\u5728\u6843\u6797\u4e2d\u98de\u821e\uff0c\u5251\u5149\u6307\u5904\uff0c\u98d2\u98d2\u98ce\u751f\uff0c \u679d\u5934\u6843\u82b1\uff0c\u7c0c\u7c0c\u800c\u843d\uff0c\u6ee1\u5730\u90fd\u662f\u82b1\u74e3\uff01 \u3000\u3000\u891a\u4e91\u5cf0\u6084\u58f0\u8bf4\u9053\uff1a\u201c\u8c37\u5e08\u5f1f\uff0c\u4f60\u77a7\u5f97\u51fa\u5999\u5904\u4e48\uff1f\u201d \u3000\u3000\u8c37\u6db5\u865a\u51dd\u795e\u89c2\u770b\uff0c\u53ea\u89c1\u534e\u5929\u8679\u8fdb\u82e5\u733f\u7334\u7a9c\u679d\uff0c\u9000\u82e5\u9f99\u86c7\u75be\u8d70\uff0c\u8d77\u5982\u9e70\u96bc\u51b2\u5929\uff0c\u843d\u82e5\u731b\u864e \u6251\u5730\u3002\u90a3\u5339\u7ec3\u4f3c\u7684\u5251\u5149\uff0c\u5728\u7e41\u679d\u5bc6\u53f6\u4e4b\u4e2d\u76d8\u65cb\u98de\u4e3e\u3002\u4f46\u53ea\u89c1\u6843\u82b1\u7247\u7247\u98d8\u843d\uff0c\u5076\u5c14\u4e5f\u6709\u51e0\u7247\u6811 \u53f6\u8ddf\u7740\u843d\u4e0b\u6765\u3002\u53ef\u662f\u7f00\u6ee1\u82b1\u6735\u7684\u6843\u679d\uff0c\u90fd\u662f\u4e00\u70b9\u4e5f\u4e0d\u6447\u52a8\u3002 \u3000\u3000\u62db\u5f0f\u7684\u7cbe\u5999\u4e0d\u5728\u8bdd\u4e0b\uff0c\u6700\u96be\u7684\u662f\u524a\u4e0b\u82b1\u6735\uff0c\u5374\u4e0d\u6447\u52a8\u6843\u679d\u3002\u8fd9\u5251\u672f\u7684\u8f7b\u7075\u5de7\u5999\uff0c\u5f53\u771f\u5230 \u4e86\u4ee4\u4eba\u96be\u4ee5\u76f8\u4fe1\u7684\u5730\u6b65\u3002 \u3000\u3000\u8c37\u6db5\u865a\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5b5f\u5927\u4fa0\u53f7\u79f0\u795e\u5251\u65e0\u654c\uff0c\u4f46\u8bba\u5230\u5251\u672f\u7684\u8f7b\u7075\uff0c\u6050\u6015\u4e5f\u8ffd\u4e0d\u4e0a\u534e\u5e08\u4f2f \u5462\uff0c\u81f3\u4e8e\u6b66\u5f53\u56db\u5927\u5f1f\u5b50\u7684\u8fde\u73af\u593a\u547d\u5251\u6cd5\uff0c\u90a3\u66f4\u662f\u671b\u5c18\u83ab\u53ca\u4e86\uff01\u201d\u770b\u5f97\u5fd8\u5f62\uff0c\u4e0d\u89c9\u53eb\u4e86\u51fa\u6765 \u201c\u5999\u5440\uff01\u201d \u3000\u3000\u534e\u5929\u8679\u500f\u7136\u6536\u5251\uff0c\u8bf4\u9053\uff1a\u201c\u4e91\u5cf0\u4f60\u56de\u6765\u4e86\u3002\u8fd9\u4f4d\u662f\u8c01\uff1f\u201d \u3000\u3000\u891a\u3001\u8c37\u4e8c\u4eba\u884c\u8fc7\u4e86\u5f1f\u5b50\u4e4b\u793c\uff0c\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u803f\u5e08\u53d4\u7684\u5f1f\u5b50\u8c37\u6db5\u865a\u3002\u201d \u3000\u3000\u534e\u5929\u8679\u5927\u559c\u9053\uff1a\u201c\u4f60\u627e\u7740\u4e86\u803f\u5e08\u53d4\u4e86\uff1f\u201d\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u803f\u5e08\u53d4\u5c1a\u5728\u6c5f\u6e56\uff0c\u8c37\u5e08\u5f1f\u5949\u4e86\u803f \u5e08\u53d4\u4e4b\u547d\u6765\u627e\u6211\u4eec\uff0c\u5f1f\u5b50\u662f\u6700\u8fd1\u624d\u548c\u4ed6\u76f8\u9047\u7684\u3002\u201d \u3000\u3000\u534e\u5927\u8679\u5411\u8c37\u6db5\u865a\u95ee\u4e86\u5e08\u5f1f\u7684\u666f\u51b5\uff0c\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u6211\u548c\u4f60\u7684\u5e08\u7236\u5df2\u7ecf\u6709\u4e8c\u5341\u5e74\u6ca1\u89c1\u9762\u4e86\u3002 \u6211\u6b63\u5728\u76fc\u671b\u7740\u4ed6\u80fd\u591f\u524d\u6765\uff0c\u80a9\u8d1f\u6e05\u7406\u5e08\u95e8\u4e4b\u8d23\u5462\u3002\u60f3\u4e0d\u5230\u4ed6\u53d7\u4e86\u91cd\u4f24\uff0c\u5929\u96f7\u529f\u5df2\u662f\u96be\u4ee5\u7ec3\u5f97 \u5927\u6210\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u5e08\u4f2f\u7684\u8fd9\u5957\u5251\u672f\u7cbe\u5999\u65e0\u6bd4\uff0c\u672a\u5fc5\u5c31\u4e0d\u662f\u9633\u5929\u96f7\u7684\u5bf9\u624b\u3002\u201d \u3000\u3000\u534e\u5929\u8679\u9053\uff1a\u201c\u6211\u5c31\u662f\u56e0\u4e3a\u529f\u529b\u6240\u9650\uff0c\u5373\u4f7f\u6bd5\u751f\u82e6\u7ec3\uff0c\u5929\u96f7\u529f\u4e5f\u662f\u96be\u4ee5\u53ca\u5f97\u4e0a\u90a3\u53ae\u7684\u4e86\uff0c \u56e0\u6b64\uff0c\u6211\u624d\u60f3\u5230\u7ec3\u8fd9\u5957\u5251\u6cd5\u7684\u3002 \u3000\u3000\u201c\u4f60\u4eec\u7684\u7956\u5e08\u751f\u524d\u53f7\u79f0\u5251\u638c\u53cc\u7edd\uff0c\u5929\u96f7\u529f\u56fa\u7136\u662f\u4ed6\u7684\u770b\u5bb6\u672c\u9886\uff0c\u5251\u672f\u4e5f\u662f\u8db3\u4ee5\u79f0\u96c4\u6b66 \u6797\u3002\u53ea\u53ef\u60dc\u95e8\u4e0b\u5f1f\u5b50\uff0c\u90fd\u662f\u65e0\u4eba\u5f97\u5230\u4ed6\u7684\u8863\u94b5\u771f\u4f20\u3002 \u3000\u3000\u201c\u6700\u8fd1\u6211\u5728\u540e\u5c71\u77f3\u5ba4\u4e4b\u4e2d\uff0c\u53d1\u73b0\u4e86\u4ed6\u523b\u4e0b\u7684\u8fd9\u5957\u5251\u6cd5\uff0c\u6211\u5df2\u7ec3\u4e86\u4e24\u5e74\uff0c\u5e0c\u671b\u80fd\u591f\u7528\u5251\u672f \u514b\u5236\u9633\u5929\u96f7\u7684\u638c\u529b\uff0c\u4f46\u7ec3\u5230\u73b0\u5728\uff0c\u5c1a\u672a\u5f97\u5230\u6210\u529f\uff01\u201d \u3000\u3000\u8c37\u6db5\u865a\u9a87\u7136\u9053\uff1a\u201c\u5e08\u4f2f\u7684\u5251\u672f\u5982\u6b64\u7cbe\u5999\uff0c\u8fd8\u8bf4\u672a\u66fe\u6210\u529f\u4e48\uff1f\u201d \u3000\u3000\u534e\u5929\u8679\u9053\uff1a\u201c\u4f60\u4eec\u521a\u624d\u4e0d\u662f\u89c1\u6211\u5728\u5251\u524a\u6843\u82b1\u4e4b\u9645\uff0c\u6811\u53f6\u4e5f\u8ddf\u7740\u843d\u5417\uff1f\u8981\u7ec3\u5230\u6811\u53f6\u4e00\u7247\u4e0d \u843d\uff0c\u65b9\u80fd\u8bf4\u662f\u6210\u529f\u5462\u3002 \u3000\u3000\u201c\u9633\u5929\u96f7\u8fd9\u53ae\u529f\u529b\u8fdc\u8fdc\u5728\u6211\u4e4b\u4e0a\uff0c\u8981\u60f3\u51ed\u7740\u5251\u672f\u6740\u4ed6\uff0c\u5fc5\u987b\u4ee5\u8fc5\u96f7\u4e0d\u53ca\u63a9\u8033\u7684\u624b\u6bb5\uff0c\u6700 \u5c11\u6211\u8fd8\u5f97\u518d\u7ec3\u4e09\u5e74\u3002\u4f46\u6211\u56e0\u5c11\u5e74\u4e4b\u65f6\u4e5f\u662f\u53d7\u8fc7\u4f24\u7684\uff0c\u5982\u4eca\u5e74\u7eaa\u5df2\u8001\uff0c\u4f53\u529b\u65e5\u8870\uff0c\u53ea\u6015\u4e09\u5e74\u4e4b \u540e\uff0c\u548c\u9633\u5929\u96f7\u5dee\u5f97\u66f4\u8fdc\u4e86\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u803f\u5e08\u53d4\u867d\u7136\u4e0d\u80fd\u4eb2\u81ea\u524d\u6765\uff0c\u4f46\u5df2\u5631\u6258\u8c37\u5e08\u5f1f\u66ff\u4ed6\u8d1f\u8d77\u6e05\u7406\u5e08\u95e8\u4e4b\u8d23\u4e86\u3002\u201d \u3000\u3000\u534e\u5929\u8679\u9053\uff1a\u201c\u54e6\uff0c\u4f60\u7684\u5e08\u7236\u53eb\u4f60\u4ee3\u4ed6\u8d1f\u8d77\u6e05\u7406\u5e08\u95e8\u4e4b\u8d23\uff0c\u90a3\u4e48\u4f60\u7684\u5929\u96f7\u529f\u60f3\u5fc5\u662f\u5df2\u7ec3\u6210 \u529f\u4e86\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u5c0f\u4f84\u8d44\u8d28\u9c81\u949d\uff0c\u6210\u529f\u4e8c\u5b57\uff0c\u5c82\u6562\u5984\u6500\uff0c\u4e0d\u8fc7\u6709\u4e00\u62db\u201c\u96f7\u7535\u4ea4\u8f70\u201d\u6211\u4e0e\u5e08\u5144 \u5408\u4f7f\uff0c\u8bd5\u8fc7\u51e0\u6b21\uff0c\u5012\u662f\u9887\u4e3a\u5f97\u5fc3\u5e94\u624b\u3002\u5c31\u4e0d\u77e5\u80fd\u4e0d\u80fd\u591f\u654c\u5f97\u8fc7\u9633\u5929\u96f7\u8fd9\u53ae\uff1f\u201d \u3000\u3000\u534e\u5929\u8679\u9053\uff1a\u201c\u4f60\u4eec\u8bd5\u7ed9\u6211\u770b\u770b\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u8bf7\u5e08\u4f2f\u6307\u6559\u3002\u201d\u5f53\u4e0b\u4e0e\u891a\u4e91\u5cf0\u5e76\u80a9\u4e00\u7acb\uff0c\u891a\u4e91\u5cf0\u53f3\u638c\u5212\u4e86\u4e00\u9053\u5706\u5f27\uff0c\u8c37\u6db5 \u865a\u5de6\u638c\u5212\u4e86\u4e00\u9053\u5706\u5f27\uff0c\u53cc\u638c\u540c\u65f6\u62cd\u51fa\uff0c\u53ea\u542c\u5f97\u201c\u8f70\u201d\u7684\u4e00\u58f0\uff0c\u4e00\u68f5\u5927\u6811\u9f50\u6839\u5012\u4e0b\uff0c\u65b9\u5706\u6570\u4e08 \u4e4b\u5185\uff0c\u6c99\u98de\u77f3\u8d70\uff01 \u3000\u3000\u534e\u5929\u8679\u5927\u559c\u8fc7\u671b\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u8fd9\u4e00\u62db\u2018\u96f7\u7535\u4ea4\u8f70\u2019\u6bd4\u6211\u5f3a\u5f97\u591a\u4e86\u3002\u4e91\u5cf0\uff0c\u4f60\u7684\u5185\u529f\u4e5f \u662f\u6709\u8fdb\u5883\uff0c\u51fa\u4e4e\u6211\u7684\u610f\u6599\u4e4b\u5916\uff01\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u8fd9\u90fd\u662f\u5b5f\u5927\u4fa0\u6307\u70b9\u4e4b\u529f\uff0c\u4ed6\u4f20\u4e86\u4e00\u5957\u6b63\u5b97\u7684\u5185\u529f\u5fc3\u6cd5\u7ed9\u6211\u4eec\u4e8c\u4eba\u3002\u201d \u3000\u3000\u534e\u5929\u8679\u9053\uff1a\u201c\u54e6\uff0c\u4f60\u4eec\u5df2\u7ecf\u89c1\u8fc7\u6c5f\u5357\u5927\u4fa0\u5b5f\u5c11\u521a\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u5b5f\u5927\u4fa0\u5982\u4eca\u5728\u7405\u739b\u5c71\u5c60\u767e\u57ce\u7684\u5c71\u5be8\uff0c\u4ed6\u4e5f\u5f88\u662f\u6302\u5ff5\u5e08\u7236\uff0c\u53eb\u5f1f\u5b50\u4ee3\u4e3a\u81f4 \u610f\u3002\u201d\u5f53\u4e0b\u5c06\u76c2\u5c11\u521a\u8bc6\u7834\u4ed6\u7684\u6765\u5386\u4ee5\u53ca\u4f20\u4ed6\u5185\u529f\u4e4b\u4e8b\uff0c\u4e00\u4e00\u5411\u5e08\u7236\u8bf4\u4e86\u3002 \u3000\u3000\u534e\u5929\u8679\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u4f60\u4eec\u8fd9\u4e00\u62db\u2018\u96f7\u7535\u4ea4\u8f70\u2019\u7adf\u6709\u5982\u6b64\u5a01\u529b\u3002\u4e0d\u8fc7\uff0c\u90a3\u8001\u8d3c\u7684\u5929\u96f7\u529f\u662f \u672c\u95e8\u767e\u5e74\u7f55\u89c1\u7684\u9020\u8be3\uff0c\u4f60\u4eec\u662f\u5426\u80dc\u5f97\u4e86\u4ed6\uff0c\u8fd8\u662f\u672a\u53ef\u77e5\u4e4b\u6570\u3002\u4f60\u4eec\u5728\u8fd9\u91cc\u591a\u4f4f\u51e0\u5929\uff0c\u5f85\u6211\u628a \u8fd9\u5957\u5251\u6cd5\u4f20\u7ed9\u4f60\u4eec\uff0c\u5251\u638c\u517c\u65bd\uff0c\u7528\u6765\u5bf9\u4ed8\u9633\u5929\u96f7\uff0c\u90a3\u5c31\u53ef\u4ee5\u591a\u4e00\u4e9b\u80dc\u7b97\u4e86\u3002\u201d \u3000\u3000\u8fd9\u5957\u5251\u6cd5\u9887\u4e3a\u7e41\u590d\uff0c\u5e78\u800c\u4ed6\u4eec\u5bf9\u672c\u95e8\u6b66\u529f\u6839\u5e95\u6df1\u539a\uff0c\u7ec3\u8d77\u6765\u4e5f\u4e0d\u600e\u6837\u8d39\u529b\u3002\u7ec3\u4e86\u4e03\u5929\uff0c \u628a\u8fd9\u5957\u5251\u6cd5\u7684\u53d8\u5316\u90fd\u5df2\u719f\u8bb0\u80f8\u4e2d\uff0c\u4ee5\u540e\u53ef\u4ee5\u81ea\u884c\u7ec3\u4e60\uff0c\u65e0\u987b\u5e08\u7236\u6307\u70b9\u4e86\u3002 \u3000\u3000\u891a\u3001\u8c37\u4e8c\u4eba\u56e0\u4e3a\u8fd8\u8981\u5230\u67f3\u6d1e\u5929\u90a3\u513f\u7b49\u5019\u6d88\u606f\uff0c\u5251\u6cd5\u7ec3\u6210\uff0c\u4fbf\u5411\u5e08\u7236\u3001\u5e08\u4f2f\u544a\u8f9e\u3002 \u3000\u3000\u4e34\u884c\u4e4b\u524d\uff0c\u534e\u5929\u8679\u5ffd\u5730\u60f3\u8d77\u4e00\u4e8b\uff0c\u7b11\u7740\u5bf9\u891a\u4e91\u5cf0\u8bf4\u9053\uff1a\u201c\u8fd9\u51e0\u5929\u6211\u5fd9\u4e8e\u6559\u4f60\u5251\u6cd5\uff0c\u6709\u4ef6 \u4e8b\u60c5\uff0c\u5fd8\u8bb0\u5bf9\u4f60\u8bf4\u4e86\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u8bf7\u5e08\u7236\u5429\u5490\u3002\u201d \u3000\u3000\u534e\u5929\u8679\u9053\uff1a\u201c\u8fd9\u662f\u4f60\u7684\u7ec8\u8eab\u5927\u4e8b\uff0c\u4f60\u73b0\u5728\u4e5f\u5230\u4e86\u6210\u5bb6\u7acb\u5ba4\u7684\u5e74\u7eaa\u4e86\u3002\u4f60\u6ca1\u6709\u7239\u5a18\uff0c\u505a\u5e08 \u7236\u7684\u5e94\u8be5\u7ed9\u4f60\u505a\u4e3b\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u5fc3\u5934\u201c\u535c\u901a\u201d\u4e00\u8df3\uff0c\u6b63\u60f3\u628a\u81ea\u5df1\u548c\u5b5f\u660e\u971e\u7684\u4e8b\u60c5\u544a\u8bc9\u5e08\u7236\uff0c\u534e\u5929\u8679\u5df2\u5728\u8bf4\u9053\uff1a \u201c\u6b66\u6797\u4e2d\u6709\u4e00\u4f4d\u91d1\u5200\u5218\u701a\u7ae0\uff0c\u95ed\u95e8\u5c01\u5200\u4e1a\u5df2\u591a\u5e74\uff0c\u4f60\u542c\u8fc7\u8fd9\u4f4d\u8001\u524d\u8f88\u7684\u540d\u5b57\u4e48\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u4e0d\u7981\u53c8\u662f\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u5218\u701a\u7ae0\u8001\u524d\u8f88\uff1f\u4ed6\u662f\u4e0d\u662f\u6709\u4e2a\u513f\u5b50\u53eb\u5218\u5927\u4e3a\uff1f\u201d \u3000\u3000\u534e\u5929\u8679\u70b9\u4e86\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u6b63\u662f\uff0c\u4f60\u548c\u5218\u5927\u4e3a\u5728\u4e49\u519b\u4e4b\u4e2d\u76f8\u8bc6\uff0c\u6b64\u4e8b\u5218\u701a\u7ae0\u4e5f\u5df2\u544a\u8bc9\u6211 \u4e86\u3002\u54c8\u54c8\uff0c\u8bf4\u6765\u4e5f\u771f\u6709\u8da3\uff0c\u6211\u548c\u5218\u701a\u7ae0\u672c\u6765\u662f\u8001\u670b\u53cb\uff0c\u4f46\u82e5\u4e0d\u662f\u4ed6\u524d\u6765\u627e\u6211\uff0c\u6211\u8fd8\u4e0d\u77e5\u4ed6\u5c31 \u4f4f\u5728\u9644\u8fd1\u5462\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u6211\u4e5f\u66fe\u542c\u5f97\u5927\u4e3a\u5e08\u5144\u8c08\u53ca\uff0c\u8bf4\u662f\u5728\u4e09\u5e74\u524d\u5218\u8001\u4f2f\u5c31\u66fe\u7ecf\u6765\u627e\u8fc7\u4f60\u8001\u4eba\u5bb6 \u4e86\u3002\u201d \u3000\u3000\u534e\u5927\u8679\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u54b1\u4eec\u5e08\u5f92\u548c\u5218\u5bb6\u7236\u5b50\u4e5f\u7b97\u5f97\u662f\u4e24\u4ee3\u4ea4\u60c5\u4e86\u3002\u8fd9\u66f4\u662f\u518d\u597d \u4e0d\u8fc7\u5566\uff01\u201d \u3000\u3000\u891a\u4e91\u5cf0\u6697\u6697\u5403\u60ca\uff0c\u4f6f\u4f5c\u4e0d\u89e3\uff0c\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\u518d\u597d\u4e0d\u8fc7\uff1f\u201d \u3000\u3000\u534e\u5929\u8679\u4e00\u634b\u957f\u987b\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u597d\u6559\u4f60\u5f97\u77e5\uff0c\u5218\u701a\u7ae0\u6709\u4e2a\u5973\u513f\uff0c\u8981\u8bb8\u914d\u7ed9\u4f60\u5462\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u8fd9\u624d\u604d\u7136\u5927\u609f\uff0c\u5fc3\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u5927\u4e3a\u8981\u6211\u53bb\u89c1\u4ed6\u7684\u7236\u4eb2\uff0c\u5e76\u4e14\u7279\u5730\u548c\u6211\u63d0\u8d77\u4ed6\u7684 \u59b9\u59b9\u3002\u201d\u5927\u60ca\u4e4b\u4e0b\uff0c\u5306\u5fd9\u8bf4\u9053\uff1a\u201c\u5e08\u7236\uff01\u8fd9\u2026\u2026\u201d\u5fc3\u4e2d\u7740\u6025\uff0c\u53cd\u800c\u671f\u671f\u827e\u827e\u5730\u8bf4\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u534e\u5929\u8679\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u96be\u5f97\u4f60\u548c\u5218\u4e16\u5144\u53c8\u662f\u597d\u670b\u53cb\uff0c\u8fd9\u4e0d\u662f\u66f4\u597d\u4e0d\u8fc7\u4e48\uff0c\u8fd9\u95e8\u5a5a \u4e8b\uff0c\u6211\u5df2\u7ecf\u66ff\u4f60\u4f5c\u4e3b\uff0c\u7b54\u5e94\u4e0b\u6765\u4e86\u3002\u54a6\uff0c\u96be\u9053\u4f60\u4e0d\u6ee1\u610f\uff1f\u201d\u6b64\u65f6\u4ed6\u65b9\u59cb\u53d1\u89c9\u5f92\u5f1f\u9762\u8272\u6709\u5f02\uff0c \u4e00\u526f\u60f6\u6025\u7684\u795e\u6c14\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u5b9a\u4e86\u5b9a\u795e\uff0c\u8bf4\u9053\uff1a\u201c\u5f1f\u5b50\u6ca1\u7239\u6ca1\u5a18\uff0c\u5e08\u7236\u66ff\u6211\u4f5c\u4e3b\uff0c\u6211\u5c82\u6562\u4e0d\u4ece\uff0c\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u534e\u5929\u8679\u9053\uff1a\u201c\u4e0d\u8fc7\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u5f1f\u5b50\u4e5f\u6709\u4e00\u4ef6\u4e8b\u60c5\uff0c\u8981\u7980\u544a\u5e08\u7236\u3002\u201d \u3000\u3000\u534e\u5929\u8679\u9053\uff1a\u201c\u597d\uff0c\u4ec0\u4e48\u4e8b\u60c5\uff0c\u4f60\u8bf4\u5427\uff01\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u5f1f\u5b50\u548c\u5b5f\u5927\u4fa0\u7684\u5343\u91d1\uff0c\u5b5f\u3001\u5b5f\u660e\u971e\u59d1\u5a18\uff0c\u5df2\u7ecf\u3001\u5df2\u7ecf\u2026\u2026\u201d \u3000\u3000\u6bd5\u5929\u8679\u4e5f\u5927\u5403\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u4f60\u548c\u5b5f\u5927\u4fa0\u7684\u5973\u513f\u5df2\u7ecf\u6210\u4e86\u4eb2\u4e48\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u987e\u4e0d\u5f97\u5bb3\u7f9e\uff0c\u8bf4\u9053\uff1a\u201c\u867d\u672a\u6210\u4eb2\uff0c\u4f46\u5df2\u6709\u5a5a\u59fb\u4e4b\u7ea6\u3002\u5f1f\u5b50\u4e0d\u80fd\u8d1f\u5979\uff0c\u8bf7\u5e08\u7236\u539f \u8c05\u3002\u201d \u3000\u3000\u534e\u5929\u8679\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u672c\u6765\u5b5f\u5927\u4fa0\u548c\u6211\u4e5f\u662f\u670b\u53cb\uff0c\u6211\u8fd8\u53d7\u8fc7\u4ed6\u7684\u6069\u60e0\uff0c\u548c\u4ed6\u7684\u4ea4 \u60c5\uff0c\u53ea\u6709\u6bd4\u5218\u701a\u7ae0\u66f4\u6df1\u3002\u53ef\u60dc\uff0c\u5218\u701a\u7ae0\u5148\u6765\u4e00\u6b65\uff0c\u6211\u5df2\u7ecf\u7b54\u5e94\u4ed6\u4e86\u3002\u54b1\u4eec\u6b66\u6797\u4e2d\u4eba\uff0c\u8bf4\u51fa\u7684 \u8bdd\u662f\u51b3\u4e0d\u80fd\u53cd\u6094\u7684\uff0c\u4f60\u53eb\u6211\u600e\u4e48\u529e\u5462\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u662f\u4e00\u5fc3\u5411\u7740\u5b5f\u660e\u971e\u7684\uff0c\u53ef\u662f\u5e08\u95e8\u6069\u91cd\uff0c\u4ed6\u4e5f\u4e0d\u80fd\u8ba9\u5e08\u7236\u592a\u8fc7\u4e3a\u96be\uff0c\u5f53\u4e0b\u60f3\u4e86\u4e00 \u60f3\uff0c\u8bf4\u9053\uff1a\u201c\u5f1f\u5b50\u548c\u8c37\u5e08\u5f1f\u5373\u5c06\u548c\u9633\u5929\u96f7\u8fd9\u53ae\u51b3\u4e00\u6b7b\u6218\uff0c\u5409\u51f6\u96be\u4ee5\u9006\u6599\uff0c\u8bf4\u4e0d\u5b9a\u4e24\u8d25\u4ff1\u4f24\uff0c \u751a\u6216\u5f1f\u5b50\u60e8\u906d\u6bd2\u624b\u3002\u5218\u5bb6\u8fd9\u95e8\u4eb2\u4e8b\uff0c\u8bf7\u5e08\u7236\u548c\u5218\u8001\u4f2f\u8bf4\uff0c\u6682\u4e14\u7f13\u63d0\u3002\u201d \u3000\u3000\u534e\u5929\u8679\u9053\uff1a\u201c\u6682\u65f6\u4e5f\u53ea\u597d\u5982\u6b64\u63a8\u642a\u4e00\u65f6\u4e86\uff0c\u4f46\u8fd9\u53ea\u4e0d\u8fc7\u662f\u7f13\u5175\u4e4b\u8ba1\u5462\u3002\u4e91\u5cf0\uff0c\u6211\u662f\u76fc\u671b \u4f60\u4eec\u9a6c\u5230\u6210\u529f\u7684\u3002\u5f85\u6e05\u7406\u5e08\u95e8\u4e4b\u4e8b\u4e86\u7ed3\u4e4b\u540e\uff0c\u6211\u8fd8\u662f\u4e0d\u80fd\u5931\u4fe1\u4e8e\u4eba\u7684\u554a\uff01\u201d \u3000\u3000\u891a\u4e91\u5cf0\u542c\u5e08\u7236\u7684\u53e3\u6c14\uff0c\u5df2\u6709\u51e0\u5206\u677e\u52a8\uff0c\u81f3\u5c11\u662f\u5e76\u4e0d\u5f3a\u8feb\u4ed6\u975e\u548c\u90a3\u4f4d\u5218\u59d1\u5a18\u6210\u4eb2\u4e0d\u53ef\u4e86\uff0c \u4fbf\u9053\uff1a\u201c\u5982\u679c\u5f1f\u5b50\u4fa5\u5e78\u6210\u529f\uff0c\u4e0d\u6b7b\u4e5f\u4e0d\u6b8b\u5e9f\u7684\u8bdd\uff0c\u90a3\u65f6\u518d\u8bf4\u5427\u3002\u201d \u3000\u3000\u891a\u3001\u8c37\u4e8c\u4eba\u5f53\u65e5\u79bb\u5c71\uff0c\u8fd9\u4ef6\u4e8b\u60c5\u5c31\u8fd9\u6837\u6682\u4e14\u62d6\u4e0b\u6765\u4e86\u3002 \u3000\u3000\u5317\u8292\u5c71\u5357\u9762\u662f\u4e00\u7247\u65e0\u4eba\u7684\u8352\u91ce\uff0c\u8981\u8d70\u51fa\u5341\u591a\u91cc\u5916\u65b9\u6709\u6751\u5e84\uff0c\u6240\u8c13\u201c\u6751\u5e84\u201d\u4e5f\u53ea\u4e0d\u8fc7\u662f\u5c71 \u6c9f\u5b50\u91cc\u7684\u51e0\u5bb6\u730e\u6237\u4eba\u5bb6\u800c\u5df2\u3002\u4f46\u5c71\u7684\u5317\u9762\uff0c\u5012\u6709\u51e0\u4e2a\u4eba\u70df\u8f83\u5bc6\u7684\u6751\u5e84\uff0c\u5218\u5927\u4e3a\u5bb6\u6240\u5728\u7684\u53e4\u677e \u6751\u5c31\u662f\u5176\u4e2d\u4e4b\u4e00\u3002\u5c71\u5357\u5c71\u5317\u6709\u4e00\u6761\u5c0f\u8def\u76f8\u901a\uff0c\u8d70\u8fd9\u6761\u8def\uff0c\u4e0d\u7528\u7ed5\u8fc7\u5927\u5c71\u3002\u4f46\u56e0\u5e73\u65e5\u5c11\u4eba\u884c \u8d70\uff0c\u91ce\u8349\u4e1b\u751f\uff0c\u82e5\u662f\u5916\u4eba\uff0c\u5373\u4f7f\u6709\u4eba\u6307\u70b9\uff0c\u4e5f\u662f\u5f88\u96be\u627e\u51fa\u8fd9\u6761\u8def\u7684\u3002 \u3000\u3000\u5218\u5bb6\u7684\u5a5a\u4e8b\uff0c\u891a\u4e91\u5cf0\u867d\u7136\u5f97\u5230\u5e08\u7236\u7684\u5e94\u5141\uff0c\u53ef\u4ee5\u6682\u65f6\u7f6e\u4e4b\u4e0d\u7406\uff0c\u4f46\u5fc3\u91cc\u603b\u662f\u4e0d\u514d\u8499\u4e0a\u4e00 \u91cd\u9634\u5f71\uff0c\u6709\u70b9\u513f\u95f7\u95f7\u4e0d\u4e50\u3002 \u3000\u3000\u4e00\u8def\u884c\u6765\uff0c\u4e0d\u77e5\u4e0d\u89c9\u8d70\u5230\u4e86\u5357\u5317\u4ea4\u754c\u7684\u8def\u53e3\uff0c\u891a\u4e91\u5cf0\u662f\u77e5\u9053\u8fd9\u6761\u6377\u5f84\u7684\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a \u201c\u82e5\u4e0d\u662f\u6709\u8fd9\u6869\u4ee4\u4eba\u5c34\u5c2c\u4e4b\u4e8b\uff0c\u6211\u5012\u662f\u4e0d\u59a8\u53bb\u62dc\u8bbf\u5927\u4e3a\u5144\u7684\u7236\u4eb2\uff0c\u534a\u5929\u529f\u592b\uff0c\u4e5f\u5c31\u591f\u4e86\u3002\u201d \u3000\u3000\u5fc3\u5ff5\u672a\u5df2\uff0c\u5ffd\u89c1\u6709\u4e24\u9a91\u4ece\u524d\u9762\u7684\u5c71\u5773\u8d70\u51fa\u6765\uff0c\u90a3\u4e24\u4e2a\u9a91\u8005\u5728\u8352\u91ce\u4e0a\u6e38\u76ee\u56db\u987e\uff0c\u53d1\u73b0\u4e86 \u891a\u3001\u8c37\u4e8c\u4eba\uff0c\u4fbf\u5373\u5feb\u9a6c\u52a0\u97ad\uff0c\u5411\u4ed6\u4eec\u8fd9\u8fb9\u8dd1\u6765\u3002 \u3000\u3000\u8fd9\u4e24\u4e2a\u4eba\u7a7f\u7684\u662f\u91d1\u56fd\u6b66\u58eb\u7684\u670d\u88c5\uff0c\u4f46\u891a\u4e91\u5cf0\u56e0\u4e3a\u548c\u91d1\u56fd\u6b66\u58eb\u53ae\u6df7\u8fc7\u51e0\u5e74\uff0c\u5f85\u4ed6\u4eec\u8d70\u8fd1\u4e86 \u4e00\u770b\uff0c\u51ed\u7740\u4ed6\u7684\u7ecf\u9a8c\uff0c\u4e00\u770b\u5374\u770b\u51fa\u4e86\u4ed6\u4eec\u662f\u8499\u53e4\u4eba\uff0c\u5192\u5145\u91d1\u56fd\u7684\u6b66\u58eb\u7684\u3002 \u3000\u3000\u5728\u8fd9\u6837\u8352\u51c9\u7684\u5c71\u6c9f\u5b50\u91cc\u53d1\u73b0\u5916\u4eba\u5df2\u662f\u7a00\u6709\u4e4b\u4e8b\uff0c\u66f4\u4f55\u51b5\u662f\u8499\u53e4\u4eba\uff0c\u53c8\u4f55\u51b5\u662f\u5192\u5145\u91d1\u56fd\u6b66 \u58eb\u7684\u8499\u53e4\u4eba\uff1f \u3000\u3000\u891a\u4e91\u5cf0\u5fc3\u5ff5\u4e00\u52a8\uff0c\u6697\u81ea\u601d\u91cf\uff1a\u201c\u8fd9\u4e24\u4e2a\u9791\u5b50\u4e3a\u4ec0\u4e48\u8dd1\u5230\u8352\u5c71\u91cc\u6765\uff1f\u83ab\u975e\u662f\u51b2\u7740\u6211\u7684\u5e08\u7236 \u6765\u7684\uff1f\u201d \u3000\u3000\u90a3\u4e24\u4e2a\u6b66\u58eb\u8d70\u5230\u4ed6\u4eec\u9762\u524d\u505c\u4e0b\uff0c\u4e3a\u9996\u7684\u4e00\u4e2a\u95ee\u9053\uff1a\u201c\u6211\u4eec\u8981\u5230\u53e4\u677e\u6751\u53bb\uff0c\u8bf7\u95ee\u600e\u6837\u8d70 \u6cd5\uff1f\u201d\u8bf4\u7684\u6c49\u8bed\uff0c\u5341\u5206\u751f\u786c\uff0c\u800c\u4e14\u542c\u5f97\u51fa\u662f\u8499\u53e4\u4eba\u7684\u53e3\u97f3\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u539f\u6765\u4e0d\u662f\u627e\u6211\u5e08\u7236\u7684\u3002\u4ed6\u4eec\u8981\u5230\u53e4\u677e\u6751\u53bb\uff0c\u4e0d\u7528\u8bf4\u4e00\u5b9a\u662f\u627e \u5218\u8001\u4f2f\u7684\u4e86\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u8981\u5230\u53e4\u677e\u6751\u53bb\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u90a3\u6b66\u58eb\u7709\u5934\u4e00\u76b1\uff0c\u4f3c\u4e4e\u5c31\u60f3\u53d1\u4f5c\uff0c\u5374\u53c8\u5fcd\u4f4f\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u53ea\u987b\u544a\u8bc9\u6211\u4eec\u5c31\u884c\u4e86\uff0c\u591a\u7ba1\u95f2 \u4e8b\u5e72\u561b\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u5411\u8c37\u6db5\u865a\u4f7f\u4e86\u4e00\u4e2a\u773c\u8272\uff0c\u6447\u4e86\u6447\u5934\u3002\u4ed6\u77e5\u9053\u8c37\u6db5\u865a\u813e\u6c14\u8f83\u4e3a\u66b4\u8e81\uff0c\u53ea\u6015\u8c37\u6db5\u865a\u5c31 \u8981\u52a8\u624b\uff0c\u6545\u6b64\u4f7f\u773c\u8272\u6b62\u4f4f\u4ed6\uff0c\u5fc3\u60f3\uff1a\u201c\u5bf9\u65b9\u6765\u610f\u672a\u660e\uff0c\u8499\u53e4\u4eba\u4e4b\u4e2d\uff0c\u4e5f\u672a\u5fc5\u90fd\u662f\u574f\u4eba\uff0c\u4e14\u95ee \u95ee\u4ed6\u4eec\u518d\u8bf4\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u6682\u4e14\u5fcd\u4f4f\uff0c\u53ea\u542c\u5f97\u891a\u4e91\u5cf0\u8bf4\u9053\uff1a\u201c\u4e0d\u662f\u6211\u4eec\u7231\u7ba1\u95f2\u4e8b\uff0c\u4f46\u5982\u4eca\u6b63\u662f\u5175\u8352\u9a6c\u4e71\u7684\u5e74 \u5934\uff0c\u6211\u4eec\u4e0d\u4fbf\u5e26\u751f\u4eba\u8fdb\u6751\u3002\u56e0\u6b64\u5fc5\u987b\u95ee\u4e2a\u6e05\u695a\u3002\u8bf7\u95ee\u4f60\u4eec\u5728\u53e4\u677e\u6751\u4e2d\u6709\u8c01\u76f8\u8bc6\uff1f\u201d \u3000\u3000\u53e6\u4e00\u4e2a\u8499\u53e4\u6b66\u58eb\u6309\u637a\u4e0d\u4f4f\uff0c\u559d\u9053\uff1a\u201c\u4f60\u4e0d\u8bf4\u4e5f\u5c31\u7f62\u4e86\uff0c\u6ca1\u89c1\u8fc7\u4f60\u8fd9\u6837\u7f57\u54e9\u7f57\u55e6\u7684\uff01\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u597d\uff0c\u90a3\u8bf7\u4fbf\u5427\uff01\u201d \u3000\u3000\u6ee1\u9762\u9aef\u987b\u90a3\u4e2a\u6b66\u58eb\u5ffd\u5730\u559d\u9053\uff1a\u201c\u4f60\u60f3\u8d70\u5f97\u90a3\u4e48\u5bb9\u6613\uff01\u201d\u5728\u9a6c\u80cc\u4e0a\u5c45\u9ad8\u4e34\u4e0b\uff0c\u4e00\u638c\u5c31\u5411\u891a \u4e91\u5cf0\u7684\u5929\u7075\u76d6\u5288\u4e0b\u6765\uff01\u5927\u559d\u58f0\u4e2d\uff0c\u53e6\u4e00\u4e2a\u6b66\u58eb\u4e5f\u662f\u5530\u7684\u4e00\u97ad\u5411\u8c37\u6db5\u865a\u6253\u53bb\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u6012\u4ece\u5fc3\u8d77\uff0c\u559d\u9053\uff1a\u201c\u6211\u672c\u6765\u4e0d\u60f3\u6740\u4f60\uff0c\u8fd9\u662f\u4f60\u81ea\u5df1\u627e\u6b7b\uff01\u201d\u5728\u638c\u5212\u4e86\u4e00\u9053\u5706\u5f27\uff0c \u638c\u631f\u98ce\u96f7\uff0c\u4f7f\u51fa\u4e86\u201c\u5929\u96f7\u529f\u201d\u4e2d\u7684\u6740\u624b\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u4ee5\u4e3a\u4f7f\u51fa\u4e86\u5929\u96f7\u529f\uff0c\u90a3\u4e2a\u8499\u53e4\u6b66\u58eb\u4e0d\u6b7b\u4ea6\u5fc5\u91cd\u4f24\u3002\u4e0d\u6599\u53cc\u65b9\u7684\u5288\u7a7a\u638c\u529b\u63a5\u89e6\uff0c\u53ea \u542c\u5f97\u201c\u84ec\u201d\u7684\u4e00\u58f0\uff0c\u90a3\u4e2a\u8499\u53e4\u6b66\u58eb\u4ece\u9a6c\u80cc\u4e0a\u201c\u98de\u201d\u4e86\u8d77\u6765\uff0c\u891a\u4e91\u5cf0\u4e5f\u4e0d\u7981\u8fde\u9000\u4e09\u6b65\uff0c\u811a\u6b65\u672a \u66fe\u7ad9\u7a33\uff0c\u9661\u7136\u95f4\u53ea\u89c9\u4e00\u80a1\u5927\u529b\u63a8\u6765\uff0c\u4e0d\u7981\u518d\u9000\u4e09\u6b65\uff0c\u65b9\u81ea\u5403\u60ca\uff0c\u8ddf\u7740\u53c8\u662f\u4e00\u80a1\u529b\u9053\u63a8\u538b\uff0c\u903c \u5f97\u4ed6\u7b2c\u4e09\u6b21\u518d\u9000\u4e09\u6b65\uff0c\u65b9\u59cb\u7ad9\u4f4f\u4e86\u8eab\u5f62\u3002 \u3000\u3000\u90a3\u4e2a\u8499\u53e4\u6b66\u571f\u4e0d\u8fc7\u53d1\u4e86\u4e00\u638c\uff0c\u891a\u4e91\u5cf0\u5c31\u8981\u9000\u4e86\u4e09\u6b21\u4e5d\u6b65\uff01\u8fd9\u4e00\u60ca\u5f53\u771f\u662f\u975e\u540c\u5c0f\u53ef\uff01 \u3000\u3000\u6b8a\u4e0d\u77e5\u891a\u4e91\u5cf0\u56fa\u7136\u5403\u60ca\uff0c\u90a3\u4e2a\u8499\u53e4\u6b66\u571f\u4e5f\u662f\u8ba8\u4e0d\u4e86\u4fbf\u5b9c\uff0c\u4ed6\u7ed9\u891a\u4e91\u5cf0\u7684\u5929\u96f7\u529f\u638c\u529b\u6253\u4e0b \u9a6c\u6765\uff0c\u53ea\u89c9\u80f8\u53e3\u5982\u53d7\u9524\u51fb\uff0c\u4e0d\u7531\u5f97\u5fc3\u5934\u5927\u9707\uff0c\u60f3\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u8fd9\u4e2a\u5357\u86ee\u5b50\u5c45\u7136\u80fd\u591f\u786c\u63a5\u6211\u7684 \u9f99\u8c61\u795e\u529f\uff01\u201d \u3000\u3000\u539f\u6765\u8fd9\u4e2a\u9aef\u987b\u6b66\u58eb\u540d\u53eb\u547c\u9ece\u5962\uff0c\u4e43\u662f\u8499\u53e4\u56fd\u5e08\u9f99\u8c61\u6cd5\u738b\u7684\u5927\u5f1f\u5b50\u3002\u9f99\u8c61\u6cd5\u738b\u7684\u201c\u9f99\u8c61\u795e \u529f\u201d\u6709\u4e5d\u91cd\u52b2\u9053\uff0c\u4e00\u638c\u53d1\u51fa\uff0c\u638c\u529b\u4fbf\u5982\u957f\u6c5f\u5927\u6cb3\u6eda\u6eda\u800c\u4e0a\uff0c\u4e00\u91cd\u6bd4\u4e00\u91cd\u5f3a\u52b2\u3002\u547c\u9ece\u5962\u53ea\u7ec3\u5230 \u4e00\u638c\u53ef\u53d1\u4e09\u91cd\u52b2\u9053\uff0c\u6545\u6b64\u62ff\u6765\u62b5\u654c\u891a\u4e91\u5cf0\u7684\u201c\u5929\u96f7\u529f\u201d\u4e43\u662f\u4e0d\u514d\u7a0d\u900a\u4e00\u7b79\u3002 \u3000\u3000\u4ed6\u90a3\u4e2a\u540c\u4f34\u540d\u53eb\u963f\u535c\u5362\uff0c\u4e43\u662f\u5341\u516b\u540d\u91d1\u5e10\u6b66\u58eb\u4e2d\u5750\u7b2c\u4e8c\u628a\u4ea4\u6905\u7684\u4eba\u7269\uff0c\u6b64\u65f6\u4e5f\u548c\u8c37\u6db5\u865a \u4ea4\u4e0a\u4e86\u624b\u3002 \u3000\u3000\u8c37\u6db5\u865a\u4e5f\u4f7f\u51fa\u4e86\u5929\u96f7\u529f\uff0c\u4e00\u6293\u6293\u4f4f\u4e86\u97ad\u68a2\uff0c\u559d\u58f0\u201c\u4e0b\u9a6c\uff01\u201d \u3000\u3000\u963f\u535c\u5362\u679c\u7136\u4e00\u4e2a\u7b4b\u6597\u4ece\u9a6c\u80cc\u4e0a\u7ffb\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u53ef\u662f\u8c37\u6db5\u865a\u4e5f\u6ca1\u6709\u5360\u5230\u4fbf\u5b9c\u3002\u539f\u6765\u963f\u535c\u5362\u5728\u91d1\u5e10\u6b66\u58eb\u4e2d\u540d\u5217\u7b2c\u4e8c\uff0c\u6454\u89d2\u7684\u529f\u592b\u5374\u662f\u8499\u53e4 \u7b2c\u4e00\u628a\u597d\u624b\uff0c\u4ed6\u811a\u672a\u7ad9\u5730\uff0c\u5df2\u662f\u4e00\u4e2a\u201c\u9965\u9e70\u6251\u5154\u201d\u5f0f\u53cc\u638c\u51cc\u7a7a\u6251\u51fb\uff0c\u53cd\u62ff\u8c37\u6db5\u865a\u7684\u8109\u95e8\uff01 \u3000\u3000\u8c37\u6db5\u865a\u6c89\u80a9\u7f29\u8098\uff0c\u8098\u6376\u4e00\u649e\uff0c\u963f\u535c\u5362\u808b\u9aa8\u7ed9\u4ed6\u649e\u4e2a\u6b63\u7740\uff0c\u65ad\u4e86\u4e00\u6839\uff0c\u8c37\u6db5\u865a\u5374\u7ed9\u4ed6\u6454\u4e86 \u4e00\u8de4\uff0c\u8dcc\u5f97\u4e5f\u662f\u751a\u91cd\uff01 \u3000\u3000\u8c37\u6db5\u865a\u4e00\u4e2a\u201c\u9ca4\u9c7c\u6253\u633a\u201d\u7ffb\u8d77\u8eab\u6765\uff0c\u521a\u597d\u891a\u4e91\u5cf0\u7ad9\u7a33\u4e86\u811a\u6b65\u3002 \u3000\u3000\u547c\u9ece\u5962\u4e0e\u963f\u535c\u5362\u5e76\u80a9\u4e00\u7acb\uff0c\u7528\u8499\u53e4\u8bdd\u559d\u9053\uff1a\u201c\u964d\u9f99\u4f0f\u864e\uff01\u201d\u891a\u4e91\u5cf0\u53f3\u638c\u5212\u4e00\u9053\u5706\u5f27\uff0c\u8c37 \u6db5\u865a\u5de6\u638c\u5212\u4e86\u4e00\u5706\u5f27\uff0c\u4e24\u4eba\u4e5f\u662f\u4e0d\u7ea6\u800c\u540c\u5730\u559d\u9053\uff1a\u201c\u96f7\u7535\u4ea4\u8f70\uff01\u201d \u3000\u3000\u201c\u964d\u9f99\u4f0f\u864e\u201d\u662f\u201c\u9f99\u8c61\u529f\u201d\u6700\u521a\u731b\u7684\u4e00\u62db\uff0c\u6b63\u50cf\u201c\u96f7\u7535\u4ea4\u8f70\u201d\u662f\u201c\u5929\u96f7\u529f\u201d\u6700\u5389\u5bb3\u7684\u6740 \u624b\u4e00\u6837\u3002 \u3000\u3000\u53cc\u65b9\u4f7f\u51fa\u4e86\u770b\u5bb6\u672c\u9886\uff0c\u53ea\u542c\u5f97\u201c\u8f70\u201d\u7684\u4e00\u58f0\uff0c\u6c99\u98de\u77f3\u8d70\uff0c\u58f0\u5982\u90c1\u96f3\u3002\u547c\u9ece\u5962\u3001\u963f\u535c\u5362\u7ed9 \u629b\u51fa\u4e00\u4e08\u5f00\u5916\uff0c\u5728\u5730\u4e0a\u6253\u4e86\u51e0\u4e2a\u6eda\uff0c\u65b9\u59cb\u7ad9\u5f97\u8d77\u6765\u3002\u4f46\u891a\u3001\u8c37\u4e8c\u4eba\u5374\u4e5f\u540c\u65f6\u5404\u81ea\u9000\u4e86\u4e5d\u6b65\uff01 \u3000\u3000\u6bd4\u8f83\u8d77\u6765\uff0c\u5f53\u7136\u8fd8\u662f\u8fd9\u4e24\u4e2a\u8499\u53e4\u6b66\u58eb\u5403\u4e8f\u66f4\u5927\u3002\u547c\u9ece\u5962\u77e5\u9053\u9047\u4e0a\u52b2\u654c\uff0c\u518d\u6253\u4e0b\u53bb\uff0c\u51f6\u591a \u5409\u5c11\uff0c\u4e00\u58f0\u53eb\u9053\uff1a\u201c\u626f\u547c\u201d\uff0c\u8d81\u7740\u891a\uff0c\u8c37\u4e8c\u4eba\u6b63\u5728\u540e\u9000\uff0c\u5c1a\u672a\u80fd\u591f\u6251\u6765\u4e4b\u9645\uff0c\u4e24\u4eba\u8de8\u4e0a\u9a6c \u80cc\uff0c\u7acb\u5373\u98de\u5954\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u89c1\u4ed6\u4eec\u53d7\u4e86\u201c\u96f7\u7535\u4ea4\u8f70\u201d\u7684\u91cd\u51fb\uff0c\u5c45\u7136\u5e76\u672a\u53d7\u4f24\uff0c\u8fd8\u80fd\u9a91\u9a6c\u98de\u8dd1\uff0c\u4e5f\u662f\u9887\u4e3a\u5403 \u60ca\uff0c\u4e0d\u6562\u8f7b\u654c\u3002 \u3000\u3000\u90a3\u4e24\u5339\u8499\u53e4\u9a8f\u9a6c\u8dd1\u5f97\u98de\u5feb\uff0c\u8f6c\u773c\u95f4\u5df2\u662f\u53bb\u5f97\u5f88\u8fdc\u4e86\u3002 \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u597d\u5389\u5bb3\u7684\u4e24\u4e2a\u9791\u5b50\uff0c\u5e08\u5144\uff0c\u4f60\u6ca1\u6709\u53d7\u4f24\u5427\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u6ca1\u6709\u3002\u4f60\u5462\uff1f\u201d\u8c37\u6db5\u865a\u9053\uff1a\u201c\u6211\u4e5f\u6ca1\u6709\u3002\u53ef\u60dc\u54b1\u4eec\u6ca1\u6709\u5750\u9a91\uff0c\u7ed9\u4ed6\u4eec\u8dd1 \u4e86\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u7b11\u9053\uff1a\u201c\u4ed6\u4eec\u5403\u4e86\u54b1\u4eec\u8fd9\u4e00\u62db\u96f7\u7535\u4ea4\u8f70\uff0c\u7eb5\u672a\u53d7\u4f24\uff0c\u4e5f\u591f\u4ed6\u4eec\u53d7\u4e86\u3002\u4f46\u53ea\u6015\u91d1\u5200 \u5218\u8001\u82f1\u96c4\u4e0d\u662f\u4ed6\u4eec\u7684\u5bf9\u624b\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u970d\u7136\u4e00\u7701\uff0c\u8bf4\u9053\uff1a\u201c\u5bf9\u5566\uff0c\u4ed6\u4eec\u662f\u8981\u53bb\u53e4\u677e\u6751\u7684\uff0c\u5218\u8001\u82f1\u96c4\u6b63\u662f\u4f4f\u5728\u90a3\u513f\uff0c\u4e0d\u7528 \u8bf4\u4ed6\u4eec\u662f\u56fe\u8c0b\u6709\u6240\u4e0d\u5229\u4e8e\u5218\u8001\u82f1\u96c4\u7684\u4e86\u3002\u54b1\u4eec\u5230\u53e4\u677e\u6751\u53bb\uff0c\u548c\u8fd9\u4e24\u4e2a\u9791\u5b50\u518d\u51b3\u96cc\u96c4\u3002\u201d \u3000\u3000\u8fd9\u4e00\u5c42\u891a\u4e91\u5cf0\u65e9\u5c31\u60f3\u5230\uff0c\u4e0d\u8fc7\u4ed6\u4e3a\u4e86\u6709\u5218\u5bb6\u8bae\u5a5a\u4e4b\u4e8b\uff0c\u4e0d\u514d\u6709\u70b9\u8e87\u8e87\u3002\u8c37\u6db5\u865a\u8bf4\u8bdd\u4e4b \u9645\uff0c\u4ed6\u5fc3\u91cc\u5df2\u662f\u8f6c\u4e86\u597d\u51e0\u6b21\u5ff5\u5934\uff0c\u7ec8\u4e8e\u5982\u6b64\u60f3\u9053\uff1a\u201c\u6551\u4eba\u6025\u96be\uff0c\u4e43\u662f\u6211\u8f88\u5206\u5f53\u6240\u4e3a\uff0c\u4f55\u51b5\u5218 \u8001\u82f1\u96c4\u662f\u5927\u4e3a\u5144\u7684\u7236\u4eb2\u5462\uff01\u201d\u5982\u6b64\u4e00\u60f3\uff0c\u5fc3\u610f\u7acb\u51b3\uff0c\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u6284\u5c0f\u8def\u524d\u53bb\u622a\u4ed6\uff01\u201d \u3000\u3000\u8c37\u6db5\u865a\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u6015\u5218\u8001\u82f1\u96c4\u7559\u4e0b\u4f60\u5417\uff1f\u201d\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u6211\u548c\u4ed6\u662f\u6ca1\u6709\u89c1\u8fc7\u9762\u7684\uff0c\u6211 \u60f3\u54b1\u4eec\u6682\u65f6\u4e0d\u5fc5\u8868\u9732\u8eab\u4efd\uff0c\u5230\u4e86\u53e4\u677e\u6751\u89c1\u673a\u800c\u4e3a\u3002\u201d \u3000\u3000\u8ba1\u8bae\u5df2\u5b9a\uff0c\u891a\u4e91\u5cf0\u524d\u5934\u5e26\u8def\uff0c\u5230\u4e86\u53e4\u677e\u6751\u7ea6\u83ab\u662f\u4e09\u66f4\u65f6\u5206\uff0c\u891a\u4e91\u5cf0\u51dd\u795e\u9759\u542c\uff0c\u9690\u9690\u542c\u5f97 \u897f\u5317\u89d2\u6709\u91d1\u94c1\u4ea4\u9e23\u4e4b\u58f0\u3002 \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u5e08\u5144\u6599\u5f97\u4e0d\u5dee\uff0c\u60f3\u5fc5\u662f\u90a3\u4e24\u4e2a\u9791\u5b50\u5df2\u7ecf\u548c\u5218\u8001\u82f1\u96c4\u52a8\u624b\u4e86\u3002\u201d\u5f53\u4e0b\u4e24\u4eba\u4fbf \u5373\u65bd\u5c55\u8f7b\u529f\u5411\u90a3\u4e2a\u65b9\u5411\u8d76\u53bb\u3002 \u3000\u3000\u53e4\u677e\u6751\u540d\u526f\u5176\u5b9e\uff0c\u8fd9\u4e2a\u6751\u5e84\u662f\u5728\u677e\u6797\u4e2d\u7684\uff0c\u6797\u6df1\u6811\u5bc6\uff0c\u8fd9\u665a\u867d\u6709\u6708\u5149\uff0c\u6797\u4e2d\u5374\u53ea\u89c1\u5e62\u5e62 \u9ed1\u5f71\u3002\u891a\u4e91\u5cf0\u6697\u6697\u6b22\u559c\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u6b63\u662f\u6700\u9002\u5b9c\u4e8e\u591c\u884c\u4eba\u51fa\u6ca1\u7684\u5929\u8272\uff0c\u6211\u548c\u8c37\u5e08\u5f1f\u4e00\u62db \u96f7\u7535\u4ea4\u8f70\uff0c\u628a\u8fd9\u4e24\u4e2a\u9791\u5b50\u6253\u53d1\u4e86\u9a6c\u4e0a\u5c31\u8d70\uff0c\u5218\u8001\u82f1\u96c4\u600e\u77e5\u6211\u4eec\u662f\u8c01\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u8fd9\u4e2a\u6253\u7b97\u672c\u6765\u662f\u5f88\u4e0d\u9519\u7684\uff0c\u4ed6\u521a\u624d\u8bd5\u8fc7\uff0c\u4ed6\u4eec\u4e8c\u4eba\u5408\u4f7f\u90a3\u62db\u201c\u96f7\u7535\u4ea4\u8f70\u201d\u5df2\u662f\u53ef \u4ee5\u7a33\u64cd\u80dc\u7b97\uff0c\u5218\u701a\u7ae0\u65e2\u7136\u662f\u4ed6\u5e08\u7236\u7684\u597d\u670b\u53cb\uff0c\u60f3\u5fc5\u4ea6\u975e\u5eb8\u624b\uff0c\u6839\u636e\u8fd9\u4e2a\u4f30\u8ba1\uff0c\u4ed6\u4eec\u7684\u786e\u662f\u6709 \u53ef\u80fd\u4e00\u51fb\u6210\u529f\u7684\u3002 \u3000\u3000\u4f46\u4e0d\u6599\u4e8b\u60c5\u7684\u53d8\u5316\u7adf\u7136\u5927\u5927\u51fa\u4e4e\u4ed6\u4eec\u7684\u610f\u6599\u4e4b\u5916\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u53ea\u60f3\u4e00\u638c\u6210\u529f\uff0c\u4fbf\u5373\u6084\u7136\u79bb\u53bb\uff0c\u6545\u6b64\u65e9\u5c31\u4e0e\u8c37\u6db5\u865a\u8bf4\u597d\uff0c\u4e0d\u5fc5\u626c\u58f0\u3002 \u3000\u3000\u5348\u591c\u677e\u6797\uff0c\u4f46\u89c1\u5e62\u5e62\u9ed1\u5f71\uff0c\u4ed6\u4eec\u521a\u521a\u8e0f\u8fdb\u6797\u5b50\uff0c\u4e00\u65f6\u95f4\u4e5f\u8fd8\u672a\u80fd\u8fa8\u522b\u654c\u6211\u4e24\u65b9\u3002\u8c37\u6db5\u865a \u7cbe\u4e8e\u201c\u542c\u98ce\u8fa8\u5668\u201d\u4e4b\u672f\uff0c\u6084\u58f0\u8bf4\u9053\uff1a\u201c\u597d\u50cf\u6709\u4e94\u4e2a\u4eba\uff01\u201d\u891a\u4e91\u5cf0\u51dd\u795e\u4e00\u542c\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c \u4e00\u8fb9\u4e24\u4e2a\uff0c\u4e00\u8fb9\u4e09\u4e2a\uff01\u201d \u3000\u3000\u891a\u3001\u8c37\u4e8c\u4eba\u542c\u51fa\u53cc\u65b9\u4eba\u6570\u4e4b\u540e\uff0c\u4e0d\u7531\u5f97\u90fd\u6709\u70b9\u8be7\u5f02\uff0c\u5218\u5bb6\u53ea\u6709\u7236\u5973\u4e8c\u4eba\uff0c\u8499\u53e4\u6b66\u58eb\u4e5f\u53ea \u662f\u4e24\u4e2a\uff0c\u8fd8\u6709\u4e00\u4e2a\u4eba\u662f\u8c01\u5462\uff1f \u3000\u3000\u5fc3\u5ff5\u672a\u5df2\uff0c\u53ea\u542c\u5f97\u4e00\u4e2a\u82cd\u8001\u7684\u58f0\u97f3\u559d\u9053\uff1a\u201c\u4f60\u4eec\u662f\u4ec0\u4e48\u4eba\uff0c\u4e3a\u4f55\u4e00\u58f0\u4e0d\u54cd\uff0c\u4fbf\u6765\u5077 \u88ad\uff1f\u201d\u891a\u4e91\u5cf0\u5fc3\u60f3\u8bf4\u8bdd\u8fd9\u4e2a\u4eba\u60f3\u5fc5\u662f\u5218\u701a\u7ae0\u4e86\u3002\u5f53\u4e0b\u52a0\u5feb\u811a\u6b65\uff0c\u4f7f\u51fa\u201c\u516b\u6b65\u8d76\u8749\u201d\u7684\u8f7b\u529f\u3002 \u3000\u3000\u679c\u7136\u891a\u4e91\u5cf0\u8fd8\u672a\u8d76\u5230\u6597\u573a\uff0c\u4fbf\u5373\u542c\u5f97\u66fe\u7ecf\u4e0e\u4ed6\u4ea4\u8fc7\u624b\u7684\u90a3\u4e2a\u9aef\u987b\u6b66\u58eb\u547c\u9ece\u5962\u5927\u58f0\u8bf4\u9053\uff1a \u201c\u597d\uff0c\u8bf4\u4e2a\u6e05\u695a\uff0c\u597d\u53eb\u4f60\u4eec\u6b7b\u5f97\u660e\u767d\uff0c\u6211\u4eec\u662f\u5949\u4e86\u5927\u91d1\u56fd\u5e08\u9633\u5929\u96f7\u4e4b\u547d\u6765\u53d6\u4f60\u9879\u4e0a\u4eba\u5934\u7684\uff01 \u5218\u701a\u7ae0\uff0c\u4f60\u8fd8\u4e0d\u77e5\u7f6a\u5417\uff1f\u201d \u3000\u3000\u547c\u9ece\u5962\u7684\u201c\u5973\u771f\u8bdd\u201d\u8bf4\u5f97\u6bd4\u4ed6\u7684\u6c49\u8bed\u6d41\u5229\u5f97\u591a\uff0c\u53ef\u662f\u542c\u8fdb\u891a\u4e91\u5cf0\u7684\u8033\u6735\u91cc\uff0c\u4ecd\u7136\u53ef\u4ee5\u542c \u5f97\u51fa\u4ed6\u539f\u6765\u7684\u8499\u53e4\u53e3\u97f3\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u6697\u81ea\u601d\u5fd6\uff1a\u201c\u9633\u5929\u96f7\u867d\u662f\u79c1\u901a\u8499\u53e4\u9791\u5b50\uff0c\u4f46\u8fd9\u662f\u7792\u7740\u91d1\u4e3b\u7684\u3002\u4ed6\u624b\u4e0b\u9ad8\u624b\u5982\u4e91\uff0c \u4f55\u5fc5\u8bf7\u4e24\u4e2a\u8499\u53e4\u6b66\u58eb\u6765\u5bf9\u4ed8\u5218\u701a\u7ae0\uff1f\u4e0d\u6015\u79d8\u5bc6\u6cc4\u6f0f\u5417\uff1f\u8fd9\u4e2a\u8499\u53e4\u9791\u5b50\u7684\u8bf4\u8bdd\u4e00\u5b9a\u662f\u5047\uff01\u201d \u3000\u3000\u5218\u701a\u7ae0\u5374\u4e0d\u77e5\u9053\u8fd9\u4e24\u4e2a\u201c\u91d1\u56fd\u6b66\u58eb\u201d\u662f\u8499\u53e4\u4eba\u5192\u5145\u7684\uff0c\u53ea\u9053\u4ed6\u4eec\u5f53\u771f\u662f\u9633\u5929\u96f7\u7684\u624b\u4e0b\uff0c \u5927\u6012\u559d\u9053\uff1a\u201c\u6211\u6709\u4ec0\u4e48\u7f6a\uff1f\u9633\u5929\u96f7\u80cc\u53db\u5e08\u95e8\uff0c\u5356\u53cb\u6c42\u8363\uff0c\u7518\u4e3a\u9e70\u72ac\uff0c\u9c7c\u8089\u767e\u59d3\uff0c\u4ed6\u624d\u662f\u5341\u6076 \u4e0d\u8d66\u7684\u7f6a\u4eba\uff01\u201d \u3000\u3000\u53e6\u4e00\u4e2a\u8499\u53e4\u6b66\u58eb\u963f\u535c\u5362\u559d\u9053\uff1a\u201c\u8001\u8d3c\u5927\u80c6\uff0c\u6b7b\u5230\u4e34\u5934\uff0c\u8fd8\u6562\u7316\u72c2\uff0c\u6211\u95ee\u4f60\uff0c\u4f60\u7684\u513f\u5b50\u662f \u53db\u519b\u4e2d\u7684\u4e00\u540d\u5c0f\u5934\u9886\uff0c\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u5218\u701a\u7ae0\u50b2\u7136\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u6b63\u4ee5\u6211\u6709\u8fd9\u6837\u4e00\u4e2a\u513f\u5b50\u4e3a\u8363\uff01\u201d \u3000\u3000\u963f\u535c\u5362\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u7684\u513f\u5b50\u9020\u53cd\u5df2\u662f\u6284\u5bb6\u4e4b\u7f6a\uff0c\u4f60\u8fd8\u6562\u6536\u7559\u5c60\u767e\u57ce\u7684\u513f\u5b50\uff0c\u4f60\u53ef\u77e5\u9053\u5c60 \u767e\u57ce\u4e00\u5bb6\u4eba\u90fd\u662f\u94a6\u72af\u5417\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u6b64\u65f6\u5df2\u662f\u6765\u5f97\u8fd1\u4e86\uff0c\u8ddd\u79bb\u5927\u7ea6\u53ea\u6709\u4e00\u4e8c\u5341\u6b65\u3002\u4e0d\u8fc7\u56e0\u4e3a\u6797\u6df1\u6708\u9ed1\uff0c\u4ed6\u7684\u8f7b\u529f\u53c8\u5f88 \u9ad8\u660e\uff0c\u4e24\u65b9\u9762\u7684\u4eba\u90fd\u6ca1\u6709\u53d1\u73b0\u4ed6\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u542c\u5f97\u6b64\u8a00\uff0c\u4e0d\u7981\u5927\u5403\u4e00\u60ca\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u53ae\u8bf4\u7684\u4e0d\u662f\u5c60\u9f99\u5417\uff1f\u600e\u7684\u5c60\u9f99\u5374\u4f1a\u4f4f \u5728\u5218\u8001\u82f1\u96c4\u7684\u5bb6\u91cc\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u805a\u62e2\u76ee\u5149\uff0c\u773c\u524d\u7684\u5e62\u5e62\u9ed1\u5f71\u5df2\u662f\u9690\u7ea6\u53ef\u4ee5\u5206\u8fa8\uff0c\u53ea\u89c1\u548c\u90a3\u4e24\u4e2a\u8499\u53e4\u6b66\u58eb\u4ea4\u624b\u7684\u4e09 \u4eba\uff0c\u4e00\u4e2a\u662f\u957f\u987b\u8001\u6c49\uff0c\u4e00\u4e2a\u662f\u68b3\u7740\u4e24\u6761\u8fab\u5b50\u7684\u5c11\u5973\uff0c\u8fd8\u6709\u4e00\u4e2a\u6b63\u662f\u5c60\u9f99\uff01\u6b63\u662f\uff1a \u3000\u3000\u9b51\u9b45\u4f0e\u4fe9\u96be\u9632\u5907\uff0c\u964c\u8def\u76f8\u9022\u53c8\u5bb3\u4eba\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b\u2014\u2014\u7b2c\u56db\u5341\u4e5d\u56de\u3000\u8986\u96e8\u7ffb\u4e91\u5145\u4fa0\u58eb\u3000\u60ca\u5929\u52a8\u5730\u5165\u91d1\u4eac \u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b \u7b2c\u56db\u5341\u4e5d\u56de\u3000\u8986\u96e8\u7ffb\u4e91\u5145\u4fa0\u58eb\u3000\u60ca\u5929\u52a8\u5730\u5165\u91d1\u4eac \u3000\u3000\u891a\u4e91\u5cf0\u8fde\u5fd9\u62c9\u7740\u8c37\u6db5\u865a\uff0c\u5728\u4ed6\u8033\u8fb9\u6084\u6084\u8bf4\u9053\uff1a\u201c\u6b64\u4e8b\u5b9a\u6709\u8e4a\u8df7\uff0c\u54b1\u4eec\u518d\u770b\u4e00\u770b\u3002\u201d\u5f53\u4e0b \u4e24\u4eba\u722c\u4e0a\u4e00\u68f5\u5927\u6811\uff0c\u5c45\u9ad8\u4e34\u4e0b\uff0c\u7ec6\u5bdf\u52a8\u9759\u3002 \u3000\u3000\u53ea\u542c\u5f97\u5c60\u9f99\u5927\u559d\u9053\uff1a\u201c\u5218\u8001\u4f2f\u4e0d\u5fc5\u4e0e\u4ed6\u4eec\u591a\u8bf4\uff0c\u4ed6\u4eec\u80c6\u6562\u53e3\u51fa\u72c2\u8a00\uff0c\u5c0f\u4f84\u66ff\u4f60\u628a\u4ed6\u4eec\u6253 \u53d1\u53bb\u89c1\u960e\u738b\u5c31\u662f\uff01\u201d \u3000\u3000\u547c\u9ece\u5962\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b50\u6709\u591a\u5927\u672c\u9886\uff0c\u7adf\u6562\u5982\u6b64\u72c2\u5984\uff0c\u597d\uff0c\u4e14\u770b\u662f\u8c01\u6253\u53d1\u8c01\u53bb\u89c1\u960e \u738b\uff01\u201d \u3000\u3000\u963f\u535c\u5362\u5374\u9053\uff1a\u201c\u5218\u701a\u7ae0\uff0c\u770b\u5728\u4f60\u5e74\u8001\u7cca\u6d82\uff0c\u6211\u7ed9\u4f60\u6307\u70b9\u4e00\u6761\u751f\u8def\uff0c\u8fd9\u4e2a\u59d3\u5c60\u7684\u5c0f\u5b50\u4f60\u4ea4 \u7ed9\u6211\u4eec\u6599\u7406\uff0c\u6b64\u4e8b\u5c31\u4e0e\u4f60\u65e0\u5173\u3002\u4f60\u5018\u80fd\u529d\u5f97\u4f60\u7684\u513f\u5b50\u5f52\u987a\u5927\u91d1\uff0c\u6211\u8fd8\u53ef\u4ee5\u4fdd\u5f97\u4f60\u4eec\u4e00\u5bb6\u8363\u534e \u5bcc\u8d35\uff01\u201d\u8fd9\u610f\u601d\u5374\u662f\u8981\u5218\u701a\u7ae0\u8896\u624b\u65c1\u89c2\u3002 \u3000\u3000\u5218\u701a\u7ae0\u5927\u6012\u559d\u9053\uff1a\u201c\u653e\u5c41\uff01\u201d\u91d1\u5200\u9ad8\u4e3e\uff0c\u91d1\u5149\u95ea\u52a8\uff0c\u7acb\u5373\u5411\u963f\u535c\u5362\u65ab\u53bb\u3002\u53e6\u4e00\u8fb9\uff0c\u90a3\u4e2a \u5c11\u5973\u6325\u821e\u4e00\u957f\u4e00\u77ed\u7684\u4e24\u628a\u67f3\u53f6\u5200\uff0c\u4e5f\u4e0e\u5c60\u9f99\u8054\u624b\uff0c\u5411\u547c\u9ece\u5962\u5c55\u5f00\u4e86\u5de6\u53f3\u5939\u653b\u3002 \u3000\u3000\u8c37\u6db5\u865a\u5c0f\u58f0\u8bf4\u9053\uff1a\u201c\u6b64\u4e8b\u53ef\u771f\u662f\u5947\u602a\u4e86\uff0c\u5c60\u9f99\u5206\u660e\u662f\u8499\u53e4\u9791\u5b50\u7684\u5978\u7ec6\uff0c\u5374\u600e\u7684\u5ffd\u7136\u53c8\u53d8 \u6210\u4e86\u5218\u8001\u82f1\u96c4\u7684\u670b\u53cb\uff0c\u5728\u8fd9\u91cc\u5145\u5f53\u597d\u6c49\u5462\uff1f\u6211\u53ef\u4e0d\u76f8\u4fe1\u5c60\u9f99\u4f1a\u53d8\u5f97\u8fd9\u6837\u5feb\uff01\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u76f8\u4fe1\uff01\u54b1\u4eec\u4e14\u770b\u4ed6\u662f\u5f04\u4ec0\u4e48\u7384\u865a\uff0c\u5c0f\u5fc3\u9632\u5907\u4ed6\u4e00\u4e9b\u3002\u201d\u4e24\u4eba\u638c\u5fc3\u5404 \u81ea\u6263\u4e86\u4e00\u679a\u94b1\u9556\uff0c\u53ea\u8981\u5c60\u9f99\u6709\u751a\u4e0d\u8f68\u4e3e\u52a8\uff0c\u5c31\u7acb\u5373\u53d1\u9556\u6253\u4ed6\u3002 \u3000\u3000\u4ed6\u4eec\u662f\u6050\u9632\u5c60\u9f99\u6697\u7b97\u5218\u5bb6\u7236\u5973\uff0c\u4f46\u51fa\u4e4e\u4ed6\u4eec\u7684\u610f\u6599\uff0c\u5c60\u9f99\u6253\u5f97\u7adf\u662f\u5341\u5206\u8ba4\u771f\uff0c\u6446\u51fa\u4e00\u526f \u548c\u90a3\u4e24\u4e2a\u8499\u53e4\u6b66\u58eb\u62fc\u547d\u7684\u6837\u5b50\u3002 \u3000\u3000\u5218\u701a\u7ae0\u5e74\u8001\u529b\u8870\uff0c\u4ed6\u7684\u5973\u513f\u5200\u6cd5\u867d\u7136\u9887\u4e3a\u7cbe\u5999\uff0c\u4f46\u4e5f\u5403\u4e8f\u5728\u7ecf\u9a8c\u7f3a\u4e4f\uff0c\u6c14\u529b\u4e0d\u52a0\uff0c\u7ed9\u90a3 \u4e24\u4e2a\u8499\u53e4\u6b66\u58eb\u903c\u5f97\u53ea\u6709\u62db\u67b6\u4e4b\u529f\uff0c\u6beb\u65e0\u8fd8\u624b\u4e4b\u529b\uff0c\u5012\u662f\u5c60\u9f99\u7684\u4e00\u67c4\u957f\u5251\u77eb\u82e5\u6e38\u9f99\uff0c\u4f7f\u5f00\u6765\u5934 \u5934\u662f\u9053\u3002\u5218\u5bb6\u7236\u5973\u8fed\u9047\u9669\u62db\uff0c\u90fd\u4e8f\u4e86\u5c60\u9f99\u7ed9\u4ed6\u4eec\u5316\u89e3\u3002 \u3000\u3000\u53cc\u65b9\u6597\u5230\u9163\u5904\uff0c\u6e10\u6e10\u53d8\u6210\u4e86\u5c60\u9f99\u4f5c\u4e3a\u4e3b\u5c06\uff0c\u529b\u654c\u8499\u53e4\u4e24\u540d\u9ad8\u624b\u7684\u5c40\u9762\u3002\u800c\u90a3\u4e24\u4e2a\u6b66\u58eb\u7adf \u7136\u4e5f\u7ed9\u4ed6\u6740\u5f97\u8fde\u8fde\u540e\u9000\uff01 \u3000\u3000\u891a\u4e91\u5cf0\u6df1\u77e5\u5c60\u9f99\u7684\u672c\u9886\uff0c\u5c60\u9f99\u7684\u672c\u9886\u6bd4\u4e0d\u4e0a\u4ed6\uff0c\u5018\u82e5\u548c\u8fd9\u4e24\u4e2a\u8499\u53e4\u6b66\u58eb\u5355\u6253\u72ec\u6597\u7684\u8bdd\uff0c \u53ea\u6015\u4e5f\u8fd8\u662f\u8f93\u591a\u8d62\u5c11\uff0c\u5982\u4ed6\u4ee5\u4e00\u4eba\u4e4b\u529b\uff0c\u62b5\u5fa1\u8fd9\u4e24\u4e2a\u6b66\u58eb\u516b\u6210\u4ee5\u4e0a\u7684\u653b\u52bf\uff0c\u8fd8\u628a\u8fd9\u4e24\u4e2a\u4eba\u6740 \u5f97\u8fde\u8fde\u540e\u9000\uff0c\u8fd9\u7b80\u76f4\u662f\u5b8c\u5168\u51fa\u4e4e\u5e38\u7406\u4e4b\u5916\u7684\u4e8b\u60c5\uff01 \u3000\u3000\u891a\u4e91\u5cf0\u8bf4\u9053\uff1a\u201c\u4e00\u5b9a\u6709\u8bc8\uff01\u8fd9\u4e24\u4e2a\u9791\u5b50\u662f\u6545\u610f\u8ba9\u4ed6\u7684\uff01\u201d\u8c37\u6db5\u865a\u604d\u7136\u5927\u609f\uff0c\u8bf4\u9053\uff1a\u201c\u6211 \u660e\u767d\u4e86\uff0c\u8fd9\u662f\u4ed6\u4eec\u505a\u6210\u7684\u5708\u5957\uff0c\u4f7f\u5f97\u5218\u8001\u82f1\u96c4\u76f8\u4fe1\u5c60\u9f99\u7684\uff01\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u90a3\u54b1\u4eec\u600e\u4e48\u529e\u5462\uff1f\u201d \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u5f53\u7136\u4e0d\u80fd\u8ba9\u5218\u8001\u82f1\u96c4\u4e0a\u5f53\uff01\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u4f46\u6b64\u9645\u5c60\u9f99\u6b63\u5728\u548c\u90a3\u4e24\u4e2a\u9791\u5b50\u6076\u6597\uff0c\u54b1\u4eec\u53ef\u4e0d\u80fd\u53cd\u800c\u53bb\u6253\u5c60\u9f99\u5440\uff01\u201d \u3000\u3000\u547c\u9ece\u5962\u6c14\u5598\u5401\u5401\u5730\u53eb\u9053\uff1a\u201c\u597d\u5389\u5bb3\u7684\u5c0f\u5b50\uff01\u597d\uff0c\u8ba9\u4f60\u4eca\u665a\u6682\u4e14\u79f0\u96c4\uff0c\u6162\u6162\u518d\u6536\u62fe\u4f60\uff01\u201d \u865a\u6643\u4e00\u638c\uff0c\u548c\u963f\u535c\u5362\u4e00\u9f50\u8f6c\u8eab\u3002 \u3000\u3000\u891a\u3001\u8c37\u4e8c\u4eba\u90fd\u662f\u6b66\u5b66\u7684\u5927\u884c\u5bb6\uff0c\u770b\u5f97\u51fa\u547c\u9ece\u5962\u662f\u6545\u610f\u8bc8\u8d25\uff0c\u6c14\u5598\u5401\u5401\u7684\u6837\u5b50\u4e5f\u662f\u5047\u88c5 \u7684\u3002 \u3000\u3000\u5c60\u9f99\u5f97\u610f\u4e4b\u6781\uff0c\u5927\u7b11\u559d\u9053\uff1a\u201c\u4f60\u4eec\u77e5\u9053\u5389\u5bb3\u4e86\u4e48\uff1f\u54ea\u91cc\u8d70\uff01\u201d \u3000\u3000\u5218\u701a\u7ae0\u7b4b\u75b2\u529b\u7aed\uff0c\u81ea\u6043\u65e0\u529b\u518d\u52a9\u5c60\u9f99\uff0c\u751f\u6015\u4ed6\u72ec\u81ea\u8ffd\u4e0a\u53bb\u9047\u9669\uff0c\u53eb\u9053\uff1a\u201c\u8d24\u4f84\uff0c\u7a77\u5bc7\u83ab \u8ffd\uff0c\u7531\u4ed6\u53bb\u5427\uff01\u201d \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u5e08\u5144\uff0c\u54b1\u4eec\u53ef\u4e0d\u80fd\u8ba9\u5c0f\u4eba\u7684\u5978\u8ba1\u5f97\u901e\uff01\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u597d\uff0c\u54b1\u4eec\u4e0b\u53bb\u63ed\u7a7f\u4ed6\u3002\u5148\u6253\u90a3\u4e24\u4e2a\u9791\u5b50\u3002\u201d\u891a\u4e91\u5cf0\u5230\u5e95\u662f\u8f83\u4e3a\u8001\u6210\u6301\u91cd\uff0c \u4ed6\u867d\u7136\u770b\u51fa\u8fd9\u662f\u5c60\u9f99\u6240\u8bbe\u7684\u5708\u5957\uff0c\u4f46\u4e5f\u6050\u9632\u4e07\u4e00\u6599\u9519\uff0c\u771f\u76f8\u672a\u660e\uff0c\u4e0d\u59a8\u6682\u4e14\u624b\u4e0b\u7559\u60c5\uff1b\u4e8c\u6765 \u4e5f\u662f\u770b\u5728\u5c60\u51e4\u7684\u4efd\u4e0a\uff0c\u4e0d\u60f3\u4fbf\u5373\u91cd\u4f24\u5c60\u9f99\u3002\u6545\u800c\u4ed6\u7279\u5730\u63d0\u9192\u5e08\u5f1f\uff0c\u5bf9\u5c60\u9f99\u53ea\u987b\u201c\u63ed\u7a7f\u201d\u4ed6\u7684 \u9634\u8c0b\uff0c\u5bf9\u90a3\u4e24\u4e2a\u9791\u5b50\u5219\u987b\u65bd\u5c55\u6740\u624b\u3002 \u3000\u3000\u891a\u3001\u8c37\u4e24\u4eba\u4ece\u6811\u4e0a\u4e00\u8dc3\u800c\u4e0b\uff0c\u6b63\u597d\u547c\u3001\u963f\u4e8c\u4eba\u4ece\u4e0b\u9762\u7ecf\u8fc7\uff0c\u4e24\u4eba\u4e00\u62db\u201c\u96f7\u7535\u4ea4\u8f70\u201d\uff0c\u547c \u9ece\u5962\u4e0e\u963f\u535c\u5362\u72e9\u4e0d\u53ca\u9632\uff0c\u53ea\u89c9\u4e00\u80a1\u6392\u5c71\u5012\u6d77\u822c\u7684\u5927\u529b\u6d8c\u6765\uff0c\u4e24\u4eba\u90fd\u4f3c\u76ae\u7403\u4f3c\u7684\uff0c\u4ece\u5c71\u5761\u4e0a\u9aa8 \u788c\u788c\u5730\u6eda\u4e0b\u53bb\u3002 \u3000\u3000\u5c60\u9f99\u559d\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\uff1f\u201d\u6325\u5251\u5288\u51fa\uff0c\u9661\u7136\u4e5f\u662f\u53ea\u89c9\u4e00\u80a1\u5927\u529b\u63a8\u6765\uff0c\u4e0d\u7531\u81ea\u5df1\u5730\u8dcc\u4e86\u4e2a\u56db \u811a\u671d\u5929\u3002\u8fd9\u8fd8\u662f\u7531\u4e8e\u5c60\u3001\u8c37\u4e8c\u4eba\u5df2\u7ecf\u6536\u56de\u4e86\u4e03\u6210\u52b2\u529b\uff0c\u5426\u5219\u5c60\u9f99\u7eb5\u7136\u4e0d\u6b7b\uff0c\u53ea\u6015\u4e5f\u8981\u5934\u7834\u8840 \u6d41\uff01 \u3000\u3000\u5c60\u9f99\u672c\u610f\u662f\u8981\u52a9\u547c\u3001\u963f\u4e8c\u4eba\u4e00\u81c2\u4e4b\u529b\u7684\uff0c\u60f3\u4e0d\u5230\u5403\u4e86\u5982\u6b64\u5927\u4e8f\u3002\u4f46\u5403\u4e86\u4e8f\u4e4b\u540e\uff0c\u4ed6\u4e5f\u5c31 \u77e5\u9053\u6765\u7684\u662f\u8c01\u4e86\u3002 \u3000\u3000\u5218\u701a\u7ae0\u5927\u60ca\u5931\u8272\uff0c\u8fde\u5fd9\u4e0e\u5973\u513f\u9f50\u4e0a\u3002\u4ed6\u7684\u5973\u513f\u6276\u8d77\u4e86\u5c60\u9f99\uff0c\u5218\u701a\u7ae0\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u6765\u7684\u662f \u54ea\u6761\u7ebf\u4e0a\u7684\u670b\u53cb\uff1f\u4e3a\u4f55\u4f60\u4eec\u4e0d\u5206\u7682\u767d\uff0c\u4e71\u6253\u4e00\u9505\u7ca5\uff1f\u201d \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u5218\u8001\u82f1\u96c4\uff0c\u4f60\u7ed9\u8fd9\u5978\u5f92\u9a97\u4e86\uff0c\u4ed6\u4eec\u6b63\u662f\u4e00\u4e18\u4e4b\u8c89\uff01\u201d \u3000\u3000\u5218\u701a\u7ae0\u53c8\u60ca\u53c8\u6012\uff0c\u559d\u9053\uff1a\u201c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u201d\u8981\u77e5\u4ed6\u521a\u624d\u8fed\u9047\u9669\u62db\uff0c\u90fd\u662f\u5c60\u9f99\u66ff\u4ed6\u5316\u89e3\uff0c\u4ed6 \u600e\u80af\u76f8\u4fe1\u5c60\u9f99\u548c\u90a3\u4e24\u4e2a\u201c\u91d1\u5ef7\u9e70\u722a\u201d\u4e43\u662f\u4e00\u4e18\u4e4b\u8c89\uff01\u5c60\u9f99\u722c\u4e86\u8d77\u6765\uff0c\u53eb\u9053\uff1a\u201c\u5218\u8001\u4f2f\u4f11\u542c\u4ed6 \u4eec\u8840\u53e3\u55b7\u4eba\uff01\u201d \u3000\u3000\u891a\u4e91\u5cf0\u51b7\u7b11\u9053\uff1a\u201c\u5c60\u9f99\uff0c\u4f60\u8981\u4e0d\u8981\u6211\u628a\u4f60\u7684\u5e95\u7ec6\u63ed\u51fa\u6765\uff1f\u201d \u3000\u3000\u8c37\u6db5\u865a\u6c14\u4ed6\u4e0d\u8fc7\uff0c\u559d\u9053\uff1a\u201c\u5c60\u9f99\uff0c\u4f60\u4e0d\u5411\u5218\u8001\u82f1\u96c4\u4ece\u5b9e\u62db\u4f9b\uff0c\u4eca\u665a\u53eb\u4f60\u77e5\u9053\u6211\u7684\u5389 \u5bb3\uff01\u201d \u3000\u3000\u5c60\u9f99\u5413\u5f97\u5fc3\u60ca\u80c6\u5bd2\uff0c\u60f3\u9053\uff1a\u201c\u7eb5\u7136\u5218\u5bb6\u7236\u5973\u7ad9\u5728\u6211\u8fd9\u4e00\u8fb9\uff0c\u4e5f\u662f\u6253\u4ed6\u4eec\u4e0d\u8fc7\u3002\u201d\u4ed6\u53ea\u9053 \u891a\u3001\u8c37\u4e8c\u4eba\u5f53\u771f\u662f\u8981\u53d6\u4ed6\u7684\u6027\u547d\uff0c\u8fde\u5fd9\u4e00\u4e2a\u8f6c\u8eab\uff0c\u6ca1\u547d\u98de\u9003\uff01 \u3000\u3000\u90a3\u5c11\u5973\u53eb\u9053\uff1a\u201c\u5c60\u5927\u54e5\uff0c\u5c60\u5927\u54e5\uff01\u201d\u4e00\u9762\u53eb\uff0c\u4e00\u9762\u8dd1\uff0c\u8dd1\u53bb\u8ffd\u8d76\u5c60\u9f99\u3002 \u3000\u3000\u5218\u701a\u7ae0\u5927\u6012\u559d\u9053\uff1a\u201c\u4f60\u4eec\u8981\u4f24\u5bb3\u5c60\u516c\u5b50\uff0c\u5148\u628a\u6211\u8fd9\u6761\u8001\u547d\u62ff\u53bb\uff01\u201d\u4e00\u62db\u201c\u591c\u6218\u516b\u65b9\u201d\uff0c \u91d1\u5200\u6325\u51fa\uff0c\u4e0d\u8ba9\u891a\u3001\u8c37\u4e8c\u4eba\u8fc7\u53bb\u3002 \u3000\u3000\u891a\u3001\u8c37\u4e8c\u4eba\u81ea\u662f\u4e0d\u4fbf\u5bf9\u4ed6\u65bd\u5c55\u5929\u96f7\u529f\uff0c\u891a\u4e91\u5cf0\u62d4\u5251\u51fa\u9798\uff0c\u201c\u94db\u201d\u7684\u4e00\u58f0\uff0c\u62e8\u5f00\u4ed6\u7684\u91d1 \u5200\uff0c\u8bf4\u9053\uff1a\u201c\u5218\u8001\u82f1\u96c4\uff0c\u4f60\u4e0a\u4e86\u5c60\u9f99\u7684\u5f53\u3002\u4f60\u9690\u5c45\u6df1\u5c71\uff0c\u4e0d\u95fb\u5916\u4e8b\uff0c\u5927\u7ea6\u4e0d\u77e5\u9053\u5c60\u9f99\u7684\u4e3a\u4eba \u5427\uff1f\u201d \u3000\u3000\u5218\u701a\u7ae0\u6014\u4e86\u4e00\u6014\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u4eba\u5012\u4f3c\u4e4e\u76f8\u5f53\u719f\u6089\u6211\u7684\u4e8b\u60c5\u3002\u201d\u8bf4\u9053\uff1a\u201c\u6211\u53ea\u77e5\u9053\u5c60 \u9f99\u662f\u5927\u4fa0\u5c60\u767e\u57ce\u7684\u516c\u5b50\uff0c\u5c60\u5927\u4fa0\u662f\u4e2a\u6297\u91d1\u7684\u5927\u82f1\u96c4\u3002\u4f60\u4eec\u5374\u662f\u4e9b\u4ec0\u4e48\u4eba\uff0c\u80c6\u6562\u5728\u6211\u7684\u9762\u524d\uff0c \u8bf4\u5c60\u516c\u5b50\u7684\u574f\u8bdd\uff01\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5c60\u5927\u4fa0\u662f\u4e2a\u6297\u91d1\u7684\u82f1\u96c4\uff0c\u4f46\u53ef\u60dc\u5c60\u9f99\u5374\u662f\u4e2a\u4e0d\u8096\u4e4b\u5b50\uff0c\u4ed6\u7684\u7236\u4eb2\u662f \u9633\u5929\u96f7\u5bb3\u6b7b\u7684\uff0c\u4ed6\u653e\u7740\u7236\u4ec7\u4e0d\u62a5\uff0c\u53cd\u800c\u5411\u6740\u7236\u7684\u4ec7\u4eba\u8ba8\u597d\uff0c\u800c\u4e14\u8fd8\u6f5c\u5f80\u8499\u53e4\uff0c\u627e\u4e0a\u95e8\u53bb\u5411\u9791 \u5b50\u5356\u8eab\uff01\u201d \u3000\u3000\u5218\u701a\u7ae0\u54ea\u80af\u76f8\u4fe1\uff0c\u559d\u9053\uff0c\u201c\u80e1\u8bf4\u516b\u9053\uff0c\u5c60\u516c\u5b50\u5c82\u80fd\u662f\u8fd9\u6837\u7684\u4eba\uff1f\u521a\u624d\u90a3\u4e24\u4e2a\u9791\u5b50\u5c31\u662f\u7ed9 \u4ed6\u6253\u8dd1\u7684\uff01\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u8fd9\u662f\u4ed6\u505a\u6210\u7684\u5708\u5957\u3002\u521a\u624d\u6211\u4eec\u6253\u90a3\u4e24\u4e2a\u9791\u5b50\uff0c\u4ed6\u4e0d\u4e5f\u662f\u6325\u5251\u5411\u6211\u4eec\u653b\u51fb \u5417\uff1f\u5206\u660e\u662f\u60f3\u6697\u52a9\u9791\u5b50\u3002\u201d \u3000\u3000\u5218\u701a\u7ae0\u51b7\u7b11\u9053\uff1a\u201c\u8001\u5b9e\u8bf4\uff0c\u4f60\u4eec\u5230\u5e95\u662f\u4ec0\u4e48\u6765\u5386\uff0c\u8001\u673d\u4e5f\u4e0d\u80fd\u65e0\u6240\u6000\u7591\uff01\u4f60\u4eec\u7a81\u5982\u5176 \u6765\uff0c\u5728\u90a3\u4ed3\u731d\u4e4b\u95f4\uff0c\u4ed6\u600e\u77e5\u4f60\u4eec\u662f\u52a9\u66f9\u52a9\u6c49\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u597d\uff0c\u8001\u82f1\u96c4\u65e2\u7136\u4e0d\u80af\u76f8\u4fe1\u6211\u4eec\u7684\u8bf4\u8bdd\uff0c\u6211\u4eec\u4e5f\u65e0\u8c13\u591a\u8bf4\u4e86\u3002\u4e0d\u8fc7\uff0c\u5c60\u9f99\u7684 \u4e8b\u60c5\u5374\u662f\u4e0d\u96be\u67e5\u660e\u771f\u76f8\uff0c\u4f60\u4e0d\u59a8\u95ee\u95ee\u4ee4\u90ce\u3002\u201d \u3000\u3000\u5218\u701a\u7ae0\u4e0d\u7981\u53c8\u662f\u4e00\u6014\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u7a76\u7adf\u662f\u4ec0\u4e48\u4eba\uff1f\u4f60\u548c\u5c0f\u513f\u76f8\u719f\uff1f\u201d \u3000\u3000\u53ef\u662f\u891a\u4e91\u5cf0\u8bf4\u4e86\u90a3\u51e0\u53e5\u8bdd\u4e4b\u540e\uff0c\u4fbf\u548c\u8c37\u6db5\u865a\u8d70\u4e86\u3002\u5218\u701a\u7ae0\u8bf4\u7684\u8bdd\u4ed6\u662f\u542c\u89c1\u4e86\u7684\uff0c\u4f46\u4ed6\u6ca1 \u6709\u56de\u7b54\u3002 \u3000\u3000\u891a\u3001\u8c37\u4e8c\u4eba\u8d70\u51fa\u677e\u6797\uff0c\u8c37\u6db5\u865a\u7b11\u9053\uff1a\u201c\u5e08\u5144\u8bf7\u83ab\u602a\u6211\u80e1\u8bf4\uff0c\u4f60\u8fd9\u4f4d\u672a\u5a5a\u59bb\u5b50\u4f3c\u4e4e\u7ed9\u5c60\u9f99 \u8ff7\u60d1\u4e86\u5462\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u7b11\u9053\uff1a\u201c\u6211\u5012\u662f\u5df4\u4e0d\u5f97\u90a3\u4f4d\u5218\u5c0f\u59d0\u627e\u5230\u4e2a\u5982\u610f\u90ce\u541b\uff0c\u53ef\u60dc\u5c60\u9f99\u5374\u662f\u4e2a\u4eba\u9762\u517d\u5fc3\u7684 \u5978\u8d3c\u3002\u82e5\u662f\u5f53\u771f\u5982\u4f60\u6240\u8bf4\uff0c\u6211\u5012\u4e0d\u5f97\u4e0d\u4e3a\u90a3\u4f4d\u5218\u5c0f\u59d0\u62c5\u5fc3\u4e86\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u4e0d\u662f\u6211\u80e1\u731c\u4e71\u6d4b\uff0c\u6211\u8d8a\u60f3\u8d8a\u662f\u53ef\u7591\u3002\u5c60\u9f99\u4eca\u665a\u4e4b\u4e8b\uff0c\u5012\u662f\u548c\u6211\u505a\u8fc7\u7684\u4e00\u4ef6 \u4e8b\u60c5\u6709\u70b9\u76f8\u4f3c\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u8be7\u9053\uff1a\u201c\u548c\u4f60\u505a\u8fc7\u7684\u4e8b\u60c5\u76f8\u4f3c\uff1f\u201d \u3000\u3000\u8c37\u6db5\u865a\u7b11\u9053\uff1a\u201c\u5f53\u7136\u8fd9\u53ea\u662f\u8868\u9762\u76f8\u4f3c\uff0c\u5b9e\u8d28\u5b8c\u5168\u4e0d\u540c\uff01\u4f60\u662f\u77e5\u9053\u6211\u548c\u4e25\u70f7\u7684\u4e8b\u60c5\u7684\uff0c\u6211 \u66fe\u5e2e\u8fc7\u5979\u7684\u7236\u4eb2\u6253\u8d25\u6ec7\u5357\u4e03\u864e\uff0c\u8bf4\u8001\u5b9e\u8bdd\uff0c\u5f53\u65f6\u6211\u4e5f\u662f\u5b58\u6709\u4e00\u70b9\u79c1\u5fc3\uff0c\u5e0c\u671b\u80fd\u56e0\u6b64\u83b7\u5f97\u5979\u7684 \u7236\u4eb2\u540c\u610f\u6211\u4eec\u7684\u5a5a\u4e8b\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u6ec7\u5357\u4e03\u864e\u662f\u540d\u526f\u5176\u5b9e\u5730\u5411\u4f60\u7684\u5cb3\u7236\u5bfb\u4ec7\uff0c\u7ed9\u4f60\u78b0\u4e0a\u3002\u5c60\u9f99\u548c\u8fd9\u4e24\u4e2a\u8499\u53e4\u9791 \u5b50\u5374\u662f\u505a\u6210\u7684\u5708\u5957\uff0c\u8bf1\u4f7f\u5218\u5bb6\u7236\u5973\u4e0a\u5f53\u7684\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u6240\u4ee5\u6211\u8bf4\u662f\u8868\u9762\u76f8\u4f3c\uff0c\u5b9e\u8d28\u5b8c\u5168\u4e0d\u540c\u3002\u4e0d\u8fc7\uff0c\u5c60\u9f99\u8fd9\u5c0f\u5b50\u5bf9\u54c4\u9a97\u5973\u4eba\u5012\u662f \u5f88\u6709\u624b\u6bb5\uff0c\u542c\u8bf4\u54b1\u4eec\u7684\u76df\u4e3b\u592b\u4eba\uff0c\u4e5f\u51e0\u4e4e\u66fe\u7ecf\u4e0a\u8fc7\u4ed6\u7684\u5f53\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u5fc3\u5ff5\u4e00\u52a8\uff0c\u970d\u7136\u7701\u8d77\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u597d\uff01\u201d\u8c37\u6db5\u865a\u9053\uff1a\u201c\u4ec0\u4e48\u4e0d\u597d\uff1f\u201d\u891a\u4e91\u5cf0\u9053\uff1a \u201c\u53ea\u6015\u5c60\u9f99\u505a\u6210\u7684\u8fd9\u4e2a\u5708\u5957\uff0c\u4e0d\u4ec5\u662f\u8981\u9a97\u5218\u701a\u7ae0\u7684\u5973\u513f\uff0c\u800c\u662f\u4e3a\u4e86\u4ed6\u7684\u513f\u5b50\u662f\u4e49\u519b\u5934\u76ee\u7684\u7f18 \u6545\u3002\u7eb5\u7136\u4ed6\u4e0d\u6562\u6df7\u8fdb\u4e49\u519b\u5145\u5f53\u5978\u7ec6\uff0c\u6700\u5c11\u4e5f\u5f97\u63d0\u9632\u4ed6\u51ed\u501f\u88d9\u5e26\u5173\u7cfb\uff0c\u5957\u53d6\u4e49\u519b\u7684\u6d88\u606f\u4e86\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u5e08\u5144\u8bf4\u5f97\u4e0d\u9519\uff0c\u53ef\u662f\u90a3\u5218\u8001\u5934\u513f\u4e0d\u80af\u76f8\u4fe1\u54b1\u4eec\u7684\u8bf4\u8bdd\uff0c\u6709\u4ec0\u4e48\u529e\u6cd5\u963b \u6b62\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u6c89\u541f\u534a\u664c\uff0c\u8bf4\u9053\uff1a\u201c\u5218\u5927\u4e3a\u539f\u6765\u6240\u5c5e\u7684\u8fd9\u652f\u4e49\u519b\u548c\u7405\u739b\u5c71\u4e5f\u662f\u6709\u6765\u5f80\u7684\uff0c\u5c31\u6015\u5c60 \u51e4\u672a\u66fe\u628a\u5979\u54e5\u54e5\u79c1\u901a\u9791\u5b50\u7684\u8fd9\u4ef6\u4e8b\u60c5\u904d\u544a\u5404\u8def\u4e49\u519b\u3002\u5f53\u4eca\u4e4b\u8ba1\uff0c\u53ea\u6709\u5f85\u54b1\u4eec\u89c1\u4e86\u67f3\u6d1e\u5929\u4e4b \u540e\uff0c\u8bf7\u4ed6\u6d3e\u4e00\u4e2a\u4eba\uff0c\u9a6c\u4e0a\u5230\u53f2\u5bb6\u5e84\u53bb\u901a\u77e5\u5218\u5927\u4e3a\u3002\u201d \u3000\u3000\u4e24\u4eba\u8fb9\u8d70\u8fb9\u8bf4\uff0c\u4e0d\u77e5\u4e0d\u89c9\u5df2\u7ecf\u51fa\u4e86\u5317\u8292\u5c71\uff0c\u8d70\u5728\u5927\u8def\u4e0a\u4e86\u3002\u5ffd\u89c1\u6709\u4e24\u9a91\u5feb\u9a6c\u8fce\u9762\u800c\u6765\uff0c \u8c37\u6db5\u865a\u62ac\u5934\u4e00\u770b\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u559d\u9053\uff1a\u201c\u4f60\u4eec\u8fd9\u4e24\u4e2a\u8d3c\u5b50\u8dd1\u5230\u8fd9\u91cc\u5e72\u5417\uff1f\u201d\u8fce\u5934\u8dd1\u53bb\uff0c\u4f7f\u51fa\u5929 \u96f7\u529f\uff0c\u201c\u547c\u201d\u7684\u5c31\u662f\u4e00\u638c\uff01 \u3000\u3000\u90a3\u4e24\u4e2a\u4eba\u89c1\u4e86\u8c37\u6db5\u865a\u4e5f\u662f\u5927\u5403\u4e00\u60ca\uff0c\u614c\u5fd9\u5730\u62e8\u8f6c\u9a6c\u5934\uff0c\u843d\u8352\u800c\u9003\u3002\u8fd9\u4e24\u9a91\u9a6c\u8dd1\u5f97\u98de\u5feb\uff0c \u8c37\u6db5\u865a\u7684\u201c\u5929\u96f7\u529f\u201d\u867d\u7136\u5389\u5bb3\uff0c\u8ddd\u79bb\u767e\u6b65\u4e4b\u5916\uff0c\u638c\u529b\u5374\u662f\u53ca\u4e0d\u4e0a\u4e86\u3002\u90a3\u4e24\u4e2a\u4eba\u5728\u9a6c\u80cc\u4e0a\u6643\u4e86 \u4e00\u6643\uff0c\u8fc5\u5373\u4e00\u4e2a\u201c\u6697\u91cc\u85cf\u8eab\u201d\uff0c\u907f\u8fc7\u638c\u529b\u7684\u4f59\u6ce2\uff0c\u8f6c\u77ac\u95f4\u90a3\u4e24\u9a91\u9a6c\u5df2\u662f\u53bb\u5f97\u8fdc\u4e86\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u8fd9\u4e24\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u8bf4\u6765\u4e5f\u5de7\uff0c\u5f53\u771f\u662f\u521a\u8bf4\u66f9\u64cd\uff0c\u66f9\u64cd\u4fbf\u5230\uff0c\u8fd9\u4e24\u4e2a\u4eba\u4e00\u4e2a\u662f\u6ec7\u5357\u4e03\u864e\u4e4b\u9996\u7684 \u6bb5\u70b9\u82cd\uff0c\u53e6\u4e00\u4e2a\u662f\u4ed6\u7684\u5e08\u5f1f\u98de\u8c79\u5b50\u891a\u9752\u5c71\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u5947\u602a\uff0c\u4ed6\u4eec\u8fdc\u5728\u6ec7\u5357\uff0c\u5374\u8dd1\u5230\u5317\u65b9\u6765\u5e72\u5417\uff1f\u201d \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u8c05\u5fc5\u6ca1\u6709\u4ec0\u4e48\u597d\u4e8b\u60c5\u3002\u53ef\u60dc\u54b1\u4eec\u53e6\u6709\u5927\u4e8b\u5728\u8eab\uff0c\u65e0\u6687\u53bb\u8ffd\u7a76\u4ed6\u4eec\u4e86\u3002\u201d\u5fc3 \u60f3\uff0c\u201c\u5f53\u5e74\u6b66\u5f53\u56db\u4fa0\u4e0e\u6211\u4e3a\u96be\uff0c\u7ed9\u6b66\u5f53\u56db\u4fa0\u901a\u98ce\u62a5\u4fe1\uff0c\u5e76\u6311\u62e8\u4ed6\u4eec\u4e0e\u6211\u4e3a\u96be\u7684\u4eba\u5c31\u662f\u8fd9\u98de\u8c79 \u5b50\u891a\u9752\u5c71\u3002\u83ab\u975e\u4ed6\u4eec\u5e08\u5144\u5f1f\u6b64\u6765\uff0c\u4e43\u662f\u56e0\u4e3a\u77e5\u9053\u4e86\u6b66\u5f53\u56db\u4fa0\u5df2\u5230\u5317\u65b9\uff0c\u4e0e\u6b66\u5f53\u56db\u4fa0\u6709\u4f55\u5173 \u7cfb\uff1f\u4e0d\u8fc7\uff0c\u4e3a\u4ec0\u4e48\u4ed6\u4eec\u5411\u5317\u8292\u5c71\u6765\u5462\uff1f\u55ef\uff0c\u6216\u8bb8\u662f\u4ed6\u4eec\u8d70\u4e0a\u4e86\u5c60\u9f99\u7684\u95e8\u8def\uff0c\u4e5f\u8bf4\u4e0d\u5b9a\u3002\u201d \u3000\u3000\u5f53\u5e74\u90a3\u4ef6\u4e8b\u60c5\uff0c\u7ed9\u8c37\u6db5\u865a\u7684\u6253\u51fb\u5b9e\u5728\u662f\u592a\u5927\u4e86\uff0c\u662f\u4ee5\u4ed6\u89c1\u4e86\u8fd9\u4e24\u4e2a\u4eba\uff0c\u5fc3\u91cc\u4e0d\u7981\u6709\u70b9\u6014 \u5fe1\u4e0d\u5b9a\u3002\u4f46\u4e3a\u4e86\u8981\u8d76\u56de\u53bb\u89c1\u67f3\u6d1e\u5929\uff0c\u4e5f\u53ea\u597d\u6682\u4e14\u6401\u5728\u4e00\u8fb9\u4e86\u3002 \u3000\u3000\u51fa\u4e86\u5317\u8292\u5c71\u4e4b\u540e\uff0c\u4e00\u8def\u4e0a\u5012\u662f\u5e73\u5b89\u65e0\u4e8b\u3002\u8fd9\u65e5\u62b5\u8fbe\u516b\u4ed9\u5251\u67f3\u6d1e\u5929\u7684\u5c71\u5be8\uff0c\u67f3\u6d1e\u5929\u4eb2\u81ea\u51fa \u8fce\u3002 \u3000\u3000\u67f3\u6d1e\u5929\u7b11\u9053\uff1a\u201c\u891a\u5144\uff0c\u53ef\u60dc\u4f60\u6765\u5f97\u7a0d\u8fdf\uff0c\u82e5\u662f\u65e9\u6765\u4e09\u5929\uff0c\u5c31\u53ef\u4ee5\u89c1\u7740\u4f60\u90a3\u4f4d\u5b5f\u59d1\u5a18 \u4e86\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u559c\u9053\uff1a\u201c\u5b5f\u5927\u4fa0\u7236\u5973\u66fe\u7ecf\u6765\u8fc7\u4e48\uff1f\u201d\u67f3\u6d1e\u5929\u9053\uff1a\u201c\u8fd8\u6709\u674e\u76df\u4e3b\u548c\u4ed6\u7684\u90a3\u4f4d\u6768\u59d1\u5a18 \u4e5f\u6765\u4e86\u5462\uff0c\u8fd9\u51e0\u5929\u5be8\u91cc\u70ed\u95f9\u5f97\u5f88\uff0c\u6211\u6b63\u76fc\u671b\u4f60\u4eec\u56de\u6765\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u9633\u5929\u96f7\u8fd9\u53ae\u53ef\u662f\u5df2\u7ecf\u6709\u7b54\u590d\u4e86\uff1f\u201d \u3000\u3000\u67f3\u6d1e\u5929\u9053\uff1a\u201c\u7b54\u590d\u662f\u6709\u4e86\uff0c\u4e0d\u8fc7\u2026\u2026\u201d\u8c37\u6db5\u865a\u9053\uff1a\u201c\u4e0d\u8fc7\u4ec0\u4e48\uff1f\u201d\u67f3\u6d1e\u5929\u9053\uff1a\u201c\u54b1\u4eec\u8fdb \u53bb\u6162\u6162\u518d\u8bf4\u3002\u201d \u3000\u3000\u5750\u5b9a\u4e4b\u540e\uff0c\u67f3\u6d1e\u5929\u8bf4\u9053\uff1a\u201c\u5b5f\u5927\u4fa0\u3001\u674e\u76df\u4e3b\u5927\u9a7e\u4eb2\u4e34\uff0c\u672c\u6765\u662f\u51c6\u5907\u9633\u5929\u96f7\u8fd9\u53ae\u524d\u6765\u4e0a\u94a9 \u7684\uff0c\u4e0d\u6599\u9633\u5929\u96f7\u8fd9\u53ae\u53cd\u800c\u8981\u54b1\u4eec\u53bb\u4e0a\u4ed6\u7684\u94a9\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u4ed6\u600e\u4e48\u8bf4\uff1f\u201d \u3000\u3000\u67f3\u6d1e\u5929\u9053\uff1a\u201c\u4ed6\u6d3e\u4eba\u9001\u4e86\u4e00\u5c01\u4fe1\u6765\uff0c\u8bf7\u6211\u4e0a\u5927\u90fd\u4f1a\u4ed6\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u4f60\u53bb\u4e0d\u53bb\uff1f\u201d \u3000\u3000\u67f3\u6d1e\u5929\u9053\uff1a\u201c\u4e0d\u5165\u864e\u7a74\uff0c\u7109\u5f97\u864e\u5b50\uff1f\u201d \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u4f60\u4e0d\u6015\u4ed6\u662f\u5e03\u6210\u7684\u5708\u5957\uff1f\u201d \u3000\u3000\u67f3\u6d1e\u5929\u9053\uff1a\u201c\u6211\u548c\u674e\u76df\u4e3b\u5546\u91cf\u8fc7\u4e86\u3002\u9633\u5929\u96f7\u4e0d\u6562\u5192\u9669\u524d\u6765\uff0c\u8fd9\u662f\u610f\u6599\u4e2d\u4e8b\u3002\u4f46\u4ed6\u8bf7\u6211\u524d \u53bb\u4f1a\u4ed6\uff0c\u662f\u5426\u5c31\u7b49\u4e8e\u4ed6\u5df2\u7ecf\u77e5\u9053\u6211\u662f\u7ad9\u5728\u4e49\u519b\u8fd9\u8fb9\u5462\uff0c\u8fd9\u5374\u672a\u80fd\u65ad\u5b9a\u3002\u591a\u534a\u662f\u4ed6\u6709\u4e86\u4e00\u70b9\u6000 \u7591\uff0c\u7528\u6765\u8bd5\u8bd5\u6211\u7684\u3002\u56e0\u6b64\u6211\u4e5f\u4e0d\u59a8\u5c06\u8ba1\u5c31\u8ba1\uff0c\u5c31\u53bb\u4f1a\u4ed6\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u674e\u76df\u4e3b\u600e\u4e48\u8bf4\uff1f\u201d \u3000\u3000\u67f3\u6d1e\u5929\u9053\uff1a\u201c\u674e\u76df\u4e3b\u6700\u521d\u4e3a\u6211\u62c5\u5fe7\uff0c\u6050\u6015\u98ce\u9669\u592a\u5927\u3002\u6211\u8bf4\u4e0d\u5165\u864e\u7a74\uff0c\u7109\u5f97\u864e\u5b50\uff1f\u9633\u5929\u96f7 \u662f\u54b1\u4eec\u7684\u5927\u654c\uff0c\u5018\u82e5\u6ca1\u6709\u63a5\u8fd1\u7684\u673a\u4f1a\uff0c\u8981\u9664\u6389\u4ed6\uff0c\u5b9e\u662f\u4e0d\u6613\u3002\u56e0\u6b64\uff0c\u83ab\u8bf4\u5192\u70b9\u98ce\u9669\uff0c\u5c31\u662f\u62fc \u4e86\u4e00\u6761\u6027\u547d\uff0c\u4e5f\u662f\u503c\u5f97\u7684\uff01\u201d \u3000\u3000\u8c37\u6db5\u865a\u542c\u5f97\u7709\u98de\u8272\u821e\uff0c\u51fb\u638c\u8d5e\u9053\uff1a\u201c\u58ee\u54c9\uff01\u201d \u3000\u3000\u67f3\u6d1e\u5929\u9053\uff1a\u201c\u540e\u6765\u5b5f\u5927\u4fa0\u548c\u674e\u76df\u4e3b\u4e5f\u8d5e\u540c\u4e86\u3002\u674e\u76df\u4e3b\u8fd8\u51c6\u5907\u4eb2\u81ea\u5230\u5927\u90fd\u4e00\u8d9f\u5462\uff01\u201d \u3000\u3000\u891a\u4e91\u5cf0\u5403\u60ca\u9053\uff1a\u201c\u4ed6\u662f\u54b1\u4eec\u7684\u9996\u9886\uff0c\u4f55\u5fc5\u4eb2\u81ea\u5192\u9669\uff1f\u201d \u3000\u3000\u67f3\u6d1e\u5929\u9053\uff1a\u201c\u6211\u4e5f\u662f\u8fd9\u4e48\u8bf4\uff0c\u4f46\u674e\u76df\u4e3b\u4e0d\u80af\u542c\u6211\u529d\u544a\uff0c\u4ed6\u8bf4\u4f60\u53ef\u4ee5\u5192\u9669\uff1f\u4e3a\u4ec0\u4e48\u6211\u5c31\u4e0d \u80fd\u5192\u9669\uff0c\u6211\u529d\u4ed6\u4e0d\u542c\uff0c\u4e5f\u662f\u6ca1\u6709\u529e\u6cd5\uff0c\u540e\u6765\u6211\u624d\u77e5\u9053\uff0c\u674e\u76df\u4e3b\u7684\u5f80\u5927\u90fd\uff0c\u4e00\u8981\u5bf9\u4ed8\u9633\u5929\u96f7\u56fa \u7136\u662f\u4e00\u4ef6\u5927\u4e8b\uff1b\u53e6\u5916\u8fd8\u6709\u4e00\u4ef6\u5927\u4e8b\uff0c\u662f\u4ed6\u8981\u548c\u5317\u4e10\u5e2e\u7684\u9646\u5e2e\u4e3b\u76f8\u4f1a\uff0c\u5171\u5546\u6297\u91d1\u590d\u56fd\u7684\u5927\u4e8b\u3002 \u9646\u5e2e\u4e3b\u5982\u4eca\u6b63\u5728\u5927\u90fd\u7684\u4e10\u5e2e\u5206\u8235\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u54b1\u4eec\u8fd9\u4f4d\u76df\u4e3b\u5f53\u771f\u662f\u6709\u52c7\u6709\u8c0b\uff0c\u4ee4\u4eba\u4f69\u670d\u3002\u90a3\u4e48\u5b5f\u5927\u4fa0\u5462\uff1f\u201d \u3000\u3000\u67f3\u6d1e\u5929\u7b11\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u60e6\u8bb0\u7740\u5b5f\u59d1\u5a18\uff0c\u4ed6\u4eec\u7236\u5973\u4e5f\u662f\u8981\u5f80\u5927\u90fd\u7684\uff0c\u4e0d\u8fc7\u53ef\u80fd\u7a0d\u8fdf\u4e00\u4e9b \u65b9\u624d\u52a8\u8eab\uff0c\u56e0\u4e3a\u7405\u739b\u5c71\u8fd8\u6709\u4e00\u70b9\u4e8b\u60c5\u9700\u8981\u5b5f\u5927\u4fa0\u56de\u53bb\u7167\u6599\u3002\u201d \u3000\u3000\u67f3\u6d1e\u5929\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u6240\u8c13\u2018\u8fdf\u4e9b\u2019\uff0c\u662f\u6307\u6bd4\u674e\u76df\u4e3b\u8fdf\u4e9b\u52a8\u8eab\uff0c\u5374\u4e0d\u4e00\u5b9a\u6bd4\u54b1\u4eec\u8fdf\uff0c\u54b1\u4eec \u5230\u4e86\u5927\u90fd\uff0c\u60f3\u6765\u53ef\u4ee5\u89c1\u7740\u4ed6\u4eec\u7236\u5973\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u5bf9\u9053\uff1a\u201c\u6211\u4eec\u8eab\u8d1f\u6e05\u7406\u5e08\u95e8\u4e4b\u8d23\uff0c\u5f53\u7136\u662f\u8981\u548c\u4f60\u540c\u5f80\u5927\u90fd\u3002\u4f60\u8ba9\u6211\u4eec\u5145\u5f53\u4f60\u7684\u968f \u4ece\u597d\u4e86\u3002\u201d \u3000\u3000\u67f3\u6d1e\u5929\u7b11\u9053\uff1a\u201c\u6211\u662f\u548c\u4f60\u8bf4\u7b11\u7684\uff0c\u4f60\u5f53\u7136\u4e0d\u662f\u53ea\u4e3a\u7740\u60f3\u89c1\u90a3\u4f4d\u5b5f\u59d1\u5a18\u800c\u53bb\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u6709\u4e00\u4ef6\u4e3a\u96be\u4e4b\u4e8b\uff0c\u6211\u5728\u9633\u5929\u96f7\u90a3\u513f\u66fe\u7ecf\u5f85\u8fc7\u4e09\u5e74\uff0c\u719f\u4eba\u592a\u591a\uff0c\u6050\u6015 \u6709\u4eba\u8ba4\u51fa\u3002\u4f46\u6211\u5374\u662f\u975e\u53bb\u4e0d\u53ef\uff01\u201d \u3000\u3000\u67f3\u6d1e\u5929\u9053\uff1a\u201c\u6211\u6709\u53ef\u4ee5\u6539\u5bb9\u6613\u8c8c\u7684\u836f\u7269\uff0c\u662f\u4e00\u4f4d\u524d\u8f88\u9ad8\u4eba\u9001\u7ed9\u6211\u7684\u3002\u6211\u7ed9\u4e86\u674e\u76df\u4e3b\u4e24 \u9897\uff0c\u521a\u597d\u8fd8\u5269\u4e00\u9897\uff0c\u7ed9\u4f60\u597d\u4e86\u3002\u81f3\u4e8e\u8c37\u5144\uff0c\u4ed6\u5728\u5927\u90fd\u5e76\u65e0\u719f\u4eba\uff0c\u76f8\u8c8c\u53c8\u8f83\u5e38\u4eba\u4e3a\u5f02\uff0c\u7a0d\u7ecf\u5316 \u88c5\uff0c\u60f3\u5fc5\u4e5f\u53ef\u4ee5\u6df7\u5f97\u8fc7\u53bb\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u7b11\u9053\uff1a\u201c\u6211\u672c\u6765\u662f\u4e2a\u4e11\u516b\u602a\uff0c\u6ca1\u4eba\u8ba4\u5f97\u6211\u7684\u672c\u6765\u9762\u76ee\u3002\u201d \u3000\u3000\u5f53\u4e0b\u891a\u4e91\u5cf0\u8bd5\u7528\u90a3\u9897\u6613\u5bb9\u4e39\uff0c\u626e\u6210\u4e00\u4e2a\u76f8\u8c8c\u5e73\u5eb8\u3001\u65e0\u751a\u7279\u70b9\u7684\u666e\u901a\u55bd\u5175\uff0c\u7528\u955c\u4e00\u7167\uff0c\u679c \u7136\u597d\u50cf\u53d8\u6210\u4e86\u53e6\u4e00\u4e2a\u4eba\uff0c\u891a\u4e91\u5cf0\u5927\u4e3a\u6b22\u559c\uff0c\u7b11\u9053\uff1a\u201c\u5999\u54c9\uff0c\u5999\u54c9\uff01\u6211\u81ea\u5df1\u90fd\u51e0\u4e4e\u8ba4\u4e0d\u5f97\u81ea\u5df1 \u4e86\uff0c\u9633\u5929\u96f7\u8fd9\u53ae\u6599\u4ed6\u770b\u4e0d\u51fa\u7834\u7efd\uff01\u201d \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u9633\u5929\u96f7\u6709\u5426\u548c\u4f60\u7ea6\u597d\u65e5\u671f\uff1f\u201d \u3000\u3000\u67f3\u6d1e\u5929\u9053\uff1a\u201c\u6ca1\u6709\u3002\u4ed6\u53ea\u662f\u5e0c\u671b\u6211\u80fd\u591f\u5728\u4e0b\u6708\u5341\u4e94\u4e4b\u524d\u53bb\u4f1a\u4ed6\u3002\u5230\u65f6\u53ea\u987b\u6295\u8fdb\u62dc\u5e16\uff0c\u4ed6 \u5c31\u4f1a\u63a5\u89c1\u6211\u4e86\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u51b7\u7b11\u9053\uff1a\u201c\u67b6\u5b50\u5012\u662f\u597d\u5927\u3002\u4e0d\u8fc7\uff0c\u8fd9\u7965\u6ca1\u6709\u9650\u5b9a\u4e00\u4e2a\u65e5\u671f\uff0c\u5012\u662f\u53ef\u4ee5\u8ba9\u54b1\u4eec\u8fdb\u4eac \u4e4b\u540e\uff0c\u6709\u4e2a\u4ece\u5bb9\u5e03\u7f6e\u7684\u673a\u4f1a\u3002\u201d \u3000\u3000\u67f3\u6d1e\u5929\u9053\uff1a\u201c\u662f\u5440\uff0c\u6211\u51c6\u5907\u5230\u4e86\u5927\u90fd\uff0c\u5148\u5f80\u4e10\u5e2e\u89c1\u5d14\u9547\u5c71\uff0c\u6253\u542c\u6253\u542c\u6d88\u606f\u3002\u8981\u662f\u5362\u9999\u4ead \u548c\u4e01\u8fdb\u8fd9\u4e24\u4e2a\u5978\u8d3c\u4e1a\u5df2\u5230\u4e86\u5927\u90fd\uff0c\u54b1\u4eec\u518d\u53e6\u4f5c\u6253\u7b97\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u70b9\u4e86\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u6837\u5b89\u6392\uff0c\u66f4\u59a5\u5f53\u4e86\u3002\u4e0d\u8fc7\uff0c\u8fd8\u6709\u4e00\u4e2a\u5978\u7ec6\uff0c\u4e5f\u5f97\u63d0\u9632 \u5462\u3002\u201d \u3000\u3000\u67f3\u6d1e\u5929\u9053\uff1a\u201c\u662f\u8c01\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u5c31\u662f\u5c60\u51e4\u7684\u54e5\u54e5\u5c60\u9f99\u3002\u201d \u3000\u3000\u67f3\u6d1e\u5929\u7b11\u9053\uff1a\u201c\u4ed6\u662f\u5978\u7ec6\u6211\u65e9\u5df2\u77e5\u9053\u4e86\u3002\u4f60\u5fd8\u8bb0\u7405\u739b\u5c71\u7eff\u6797\u5927\u4f1a\u4e4b\u65f6\uff0c\u674e\u76df\u4e3b\u63ed\u53d1\u4ed6\uff0c \u6211\u4e5f\u662f\u5728\u573a\u7684\u5417\uff1f\u4e0d\u8fc7\uff0c\u4ed6\u5374\u4e0d\u77e5\u9053\u6211\u7684\u5e95\u7ec6\uff0c\u5f53\u65f6\u6211\u662f\u7ed9\u6df3\u4e8e\u5468\u52a9\u62f3\u7684\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u6211\u4e0d\u662f\u8bf4\u4ed6\u8981\u8c0b\u5bb3\u4f60\uff0c\u6211\u6539\u4e86\u5bb9\u8c8c\uff0c\u4e5f\u4e0d\u6015\u4ed6\u8ba4\u5f97\u3002\u4e0d\u8fc7\uff0c\u4ed6\u73b0\u5728\u6b63\u5728\u8fdb \u884c\u4e00\u5b97\u9634\u8c0b\u5462\u3002\u201d\u5f53\u4e0b\u5c06\u5728\u5317\u8292\u5c71\u9047\u89c1\u5c60\u9f99\u7684\u4e8b\u60c5\uff0c\u548c\u81ea\u5df1\u5bf9\u4e8e\u8fd9\u4ef6\u4e8b\u60c5\u7684\u5224\u65ad\uff0c\u4e00\u4e94\u4e00\u5341 \u7684\u544a\u8bc9\u67f3\u6d1e\u5929\u3002 \u3000\u3000\u67f3\u6d1e\u5929\u9053\uff1a\u201c\u6709\u8fd9\u6837\u7684\u4e8b\uff0c\u5c60\u9f99\u8fd9\u53ae\u4e5f\u5fd2\u662f\u80c6\u5927\u5984\u4e3a\u4e86\u3002\u597d\uff0c\u6211\u7acb\u5373\u6d3e\u4eba\u5230\u53f2\u5bb6\u5e84\u53bb\u901a \u77e5\u5218\u5927\u4e3a\u3002\u201d \u3000\u3000\u8ba1\u8bae\u5df2\u5b9a\uff0c\u7b2c\u4e8c\u5929\u4e00\u65f1\uff0c\u67f3\u6d1e\u5929\u3001\u891a\u4e91\u5cf0\u3001\u8c37\u6db5\u865a\u4e09\u4eba\u4fbf\u5373\u8054\u8882\u8fdb\u4eac\u3002 \u3000\u3000\u4ed6\u4eec\u4e09\u4eba\u8054\u8882\u8fdb\u4eac\u7684\u65f6\u5019\uff0c\u674e\u601d\u5357\u548c\u6768\u5a49\u4e8c\u4eba\uff0c\u4ea6\u5df2\u662f\u5728\u524d\u5f80\u5927\u90fd\u7684\u8def\u4e0a\u4e86\u3002 \u3000\u3000\u82b1\u5f00\u4e24\u6735\uff0c\u5404\u8868\u4e00\u679d\u3002\u6682\u4e14\u6309\u4e0b\u891a\u4e91\u5cf0\u3001\u8c37\u6db5\u865a\u548c\u67f3\u6d1e\u5929\u4e09\u4eba\u4e4b\u4e8b\u4e0d\u8868\uff0c\u4e14\u8bf4\u674e\u601d\u5357\u4e0e \u6768\u5a49\u5728\u8def\u4e0a\u7684\u906d\u9047\u3002 \u3000\u3000\u4ed6\u4eec\u4e8c\u4eba\u7ecf\u5386\u8fc7\u8bb8\u591a\u78e8\u6298\uff0c\u65e0\u6570\u98ce\u6ce2\uff0c\u65b9\u624d\u805a\u9996\uff0c\u4e00\u8def\u540c\u884c\uff0c\u81ea\u662f\u6709\u8bf4\u4e0d\u5c3d\u7684\u67d4\u60c5\u871c \u610f\uff0c\u6cbf\u9014\u98ce\u5149\uff0c\u90a3\u4e5f\u4e0d\u5fc5\u4f5c\u4e66\u7684\u4eba\u4e00\u4e00\u63cf\u7ed8 \u3000\u3000\u8fd9\u65e5\u5230\u4e86\u84df\u5dde\u7684\u5bc6\u4e91\u53bf\uff0c\u8ddd\u79bb\u91d1\u56fd\u90fd\u95e8\u5df2\u4e0d\u8fc7\u662f\u53ea\u6709\u767e\u6570\u5341\u91cc\u4e4b\u9065\uff0c\u884c\u8d70\u95f4\u5ffd\u89c1\u5c18\u571f\u5927 \u8d77\uff0c\u6709\u4e00\u5f6a\u519b\u9a6c\u5f00\u6765\uff0c\u884c\u4eba\u7eb7\u7eb7\u8eb2\u907f\u3002 \u3000\u3000\u674e\u601d\u5357\u5b9a\u775b\u4e00\u770b\uff0c\u53ea\u89c1\u524d\u9762\u540e\u9762\u90fd\u662f\u91d1\u56fd\u7684\u9a91\u5175\uff0c\u4e2d\u95f4\u4e00\u5c0f\u961f\u4eba\u9a6c\uff0c\u5374\u662f\u8499\u53e4\u58eb\u5175\u7684\u670d \u9970\u3002 \u3000\u3000\u8fd9\u8fd8\u4e0d\u7b97\u5947\u602a\uff0c\u5947\u602a\u7684\u662f\u961f\u4f0d\u4e2d\u90a3\u51e0\u4e2a\u8499\u53e4\u201c\u8d35\u4eba\u201d\uff0c\u4e2d\u95f4\u9a91\u5728\u9ad8\u5934\u5927\u9a6c\u4e0a\u7684\uff0c\u662f\u8499\u53e4 \u795e\u7ffc\u8425\u7684\u7edf\u9886\u6728\u534e\u9ece\uff0c\u4e24\u65c1\u662f\u91d1\u5e10\u6b66\u58eb\u547c\u9ece\u5962\u548c\u963f\u535c\u5362\u3002\u548c\u6728\u534e\u9ece\u5e76\u8f94\u800c\u884c\uff0c\u7a0d\u5fae\u5728\u4ed6\u540e\u9762 \u4e00\u70b9\u7684\u662f\u4e00\u4e2a\u6ee1\u9762\u7ea2\u5149\u7684\u5927\u548c\u5c1a\u3002 \u3000\u3000\u770b\u6728\u534e\u9ece\u5728\u884c\u5217\u4e2d\u7684\u4f4d\u7f6e\uff0c\u4ed6\u5e94\u5f53\u662f\u8499\u53e4\u8fd9\u961f\u4eba\u9a6c\u7684\u9996\u8111\u4eba\u7269\u65e0\u7591\u3002\u53ef\u662f\u6df7\u5728\u961f\u4f0d\u4e2d\u7684 \u4e00\u4e2a\u8499\u53e4\u519b\u5b98\uff0c\u8d77\u521d\u674e\u601d\u5357\u6ca1\u6709\u600e\u6837\u7559\u610f\uff0c\u540e\u6765\u770b\u6e05\u695a\u4e86\u4e4b\u540e\uff0c\u5374\u4e0d\u7531\u5f97\u4ed6\u4e0d\u5927\u5403\u4e00\u60ca\u4e86\u3002 \u3000\u3000\u4f60\u9053\u8fd9\u4eba\u662f\u8c01\u3002\u539f\u6765\u7adf\u662f\u8499\u53e4\u7684\u56db\u738b\u5b50\u62d6\u96f7\uff01 \u3000\u3000\u6728\u534e\u9ece\u662f\u8499\u53e4\u7684\u5927\u5c06\uff0c\u5730\u4f4d\u5f53\u7136\u5f88\u9ad8\uff0c\u53ef\u662f\u6bd4\u8d77\u4e86\u66fe\u7ecf\u5f53\u8fc7\u201c\u76d1\u56fd\u201d\u7684\u56db\u738b\u5b50\u62d6\u96f7\uff0c\u90a3 \u53c8\u5dee\u5f97\u592a\u8fdc\u4e86\u3002 \u3000\u3000\u4f46\u73b0\u5728\u6728\u534e\u9ece\u9a91\u7740\u9ad8\u5934\u5927\u9a6c\uff0c\u8d70\u5728\u5f53\u4e2d\uff0c\u62d6\u96f7\u5374\u662f\u4e00\u4e2a\u5c0f\u519b\u5b98\u7684\u670d\u9970\uff0c\u6df7\u5728\u961f\u4f0d\u4e4b\u4e2d\uff0c \u8ddf\u5728\u4ed6\u7684\u540e\u9762\uff0c\u5206\u660e\u626e\u6f14\u7684\u662f\u6728\u534e\u9ece\u968f\u4ece\u7684\u89d2\u8272\uff0c\u8fd9\u4e0d\u662f\u592a\u5947\u602a\u4e86\u5417\uff1f \u3000\u3000\u674e\u601d\u5357\u548c\u6768\u5a49\u662f\u7528\u67f3\u6d1e\u5929\u6240\u9001\u7684\u6613\u5bb9\u4e39\u5316\u4e86\u88c5\u7684\uff0c\u4ed6\u4eec\u6253\u626e\u6210\u4e00\u5bf9\u519c\u5bb6\u592b\u5987\uff0c\u4f30\u91cf\u62d6\u96f7 \u4e0e\u6728\u534e\u9ece\u4e00\u5b9a\u8ba4\u4e0d\u51fa\u4ed6\u4eec\uff0c\u4e8e\u662f\u4e5f\u5c31\u8ddf\u7740\u884c\u4eba\u8d70\u907f\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u6728\u534e\u9ece\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u56de\u8fc7\u5934\u53bb\u548c\u90a3\u4e2a\u5927\u548c\u5c1a\u8bf4\u4e86\u51e0\u53e5\u4e0d\u77e5\u4ec0\u4e48\u8bdd\uff0c\u90a3\u4e2a\u548c\u5c1a\u7a81 \u7136\u5411\u674e\u601d\u5357\u6240\u8d70\u7684\u65b9\u5411\u53d1\u51fa\u4e86\u4e00\u8bb0\u5288\u7a7a\u638c\u3002 \u3000\u3000\u674e\u601d\u5357\u53ea\u89c9\u4e00\u80a1\u6392\u5c71\u5012\u6d77\u4f3c\u7684\u638c\u529b\u6d8c\u6765\uff0c\u4e0d\u7531\u81ea\u5df1\u7684\u5411\u524d\u8fde\u51b2\u51e0\u6b65\uff0c\u65b9\u59cb\u7a33\u5f97\u4f4f\u8eab\u5f62\u3002 \u3000\u3000\u674e\u601d\u5357\u4e0d\u8fc7\u662f\u9669\u4e9b\u6454\u4e86\u4e00\u8de4\u800c\u5df2\uff0c\u8def\u4e0a\u7684\u884c\u4eba\u53ef\u5c31\u60e8\u4e86\u3002\u53ea\u542c\u5f97\u51e0\u58f0\u6495\u5fc3\u88c2\u80ba\u7684\u547c\u53eb\uff0c \u5728\u674e\u601d\u5357\u540e\u9762\u7684\u51e0\u4e2a\u4eba\u5df2\u5012\u5728\u5730\u4e0a\uff0c\u4e03\u7a8d\u6d41\u8840\uff0c\u663e\u7136\u662f\u5df2\u7ecf\u6b7b\u4e86\u3002 \u3000\u3000\u91d1\u56fd\u7684\u4e00\u4e2a\u5c06\u519b\u7fd8\u8d77\u59c6\u6307\u8d5e\u9053\uff1a\u201c\u6cd5\u738b\u5f53\u771f\u662f\u795e\u529f\u76d6\u4e16\uff0c\u4e0d\u6127\u5929\u4e0b\u7b2c\u4e00\u9ad8\u624b\u7684\u79f0\u53f7\uff01\u4f69 \u670d\uff0c\u4f69\u670d\uff01\u201d \u3000\u3000\u90a3\u4e2a\u91d1\u56fd\u5c06\u519b\u53ea\u9053\u4ed6\u662f\u8981\u9a71\u6563\u9053\u4e0a\u884c\u4eba\uff0c\u5374\u4e0d\u77e5\u4ed6\u662f\u8981\u5bf9\u4ed8\u674e\u601d\u5357\uff0c\u884c\u4eba\u5df2\u7ecf\u8eb2\u907f\u4e00 \u7a7a\uff0c\u8fd9\u4e2a\u5c06\u519b\u81ea\u662f\u4e0d\u4f1a\u505c\u4e0b\u6765\u8ffd\u7a76\uff0c\u8f6c\u773c\u95f4\u8fd9\u5f6a\u519b\u9a6c\u4fbf\u8fc7\u53bb\u4e86\u3002 \u3000\u3000\u674e\u601d\u5357\u9ed8\u8fd0\u7384\u529f\uff0c\u8fd0\u6c14\u4e09\u8f6c\uff0c\u80f8\u53e3\u7684\u80c0\u95f7\u4e4b\u611f\uff0c\u65b9\u59cb\u7565\u89e3\u3002\u6768\u5a49\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u5357\u54e5\uff0c\u4f60 \u6ca1\u4e8b\u5427\uff1f\u201d\u674e\u601d\u5357\u9053\uff1a\u201c\u6ca1\u4e8b\uff0c\u8fd9\u548c\u5c1a\u597d\u5389\u5bb3\uff01\u201d\u6768\u5a49\u9053\uff1a\u201c\u90a3\u91d1\u56fd\u5c06\u519b\u53eb\u4ed6\u505a\u4ec0\u4e48\u6cd5\u738b\uff0c \u60f3\u5fc5\u662f\u8499\u53e4\u7684\u56fd\u5e08\u9f99\u8c61\u6cd5\u738b\u4e86\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u5728\u548c\u6797\u7684\u65f6\u5019\uff0c\u672a\u6709\u673a\u4f1a\u89c1\u5230\u9f99\u8c61\u6cd5\u738b\u3002\u4e0d\u8fc7\u9f99\u8c61\u6cd5\u738b\u6709\u51e0\u4e2a\u5f1f\u5b50\u662f\u6210\u5409\u601d\u6c57\u7684 \u91d1\u5e10\u6b66\u58eb\uff0c\u674e\u601d\u5357\u66fe\u7ecf\u548c\u4ed6\u4eec\u8bd5\u8fc7\u62db\uff0c\u5bf9\u4ed6\u4eec\u8fd9\u4e00\u6d3e\u7684\u201c\u9f99\u8c61\u529f\u201d\u5374\u662f\u8bc6\u5f97\u7684\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d \u9519\uff0c\u4e00\u5b9a\u662f\u90a3\u4e2a\u79c3\u9a74\uff01\u60f3\u4e0d\u5230\u6211\u5728\u8499\u53e4\u89c1\u4e0d\u7740\u4ed6\uff0c\u5728\u8fd9\u91cc\u5374\u9047\u4e0a\u4e86\u3002\u4ed6\u53f7\u79f0\u6b66\u529f\u5929\u4e0b\u7b2c\u4e00\uff0c \u7b2c\u4e00\u672a\u5fc5\uff0c\u4f46\u4e5f\u7684\u786e\u662f\u5341\u5206\u5389\u5bb3\uff01\u5c31\u4e0d\u77e5\u4ed6\u662f\u5426\u662f\u6709\u610f\u8981\u6740\u6211\u7684\uff1f\u201d \u3000\u3000\u6768\u5a49\u5fe7\u5fc3\u4ef2\u4ef2\uff0c\u8bf4\u9053\uff1a\u201c\u53ea\u6015\u662f\u62d6\u96f7\u548c\u6728\u534e\u9ece\u5df2\u7ecf\u8ba4\u51fa\u4e86\u4f60\uff0c\u53eb\u8fd9\u9f99\u8c61\u6cd5\u738b\u6740\u5bb3\u4f60\u7684\u3002 \u770b\u8fd9\u60c5\u5f62\uff0c\u4ed6\u4eec\u4e00\u5b9a\u4e5f\u662f\u524d\u5f80\u5927\u90fd\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u591a\u534a\u662f\u5982\u4f60\u6240\u6599\u3002\u4e0d\u8fc7\uff0c\u54b1\u4eec\u603b\u4e0d\u80fd\u77e5\u96be\u800c\u9000\uff0c\u5927\u90fd\u54b1\u4eec\u603b\u8fd8\u662f\u8981\u53bb \u7684\u3002\u201d \u3000\u3000\u90a3\u51e0\u4e2a\u884c\u4eba\u7ed9\u9f99\u8c61\u6cd5\u738b\u7684\u638c\u529b\u9707\u6bd9\u9053\u65c1\uff0c\u548c\u4ed6\u4eec\u76f8\u719f\u7684\u4eba\u4eec\u60ca\u9b42\u7a0d\u5b9a\uff0c\u6b64\u65f6\u5df2\u662f\u56f4\u62e2\u4e86 \u6765\uff0c\u6709\u7684\u5927\u54ed\uff0c\u6709\u7684\u75db\u9a82\u3002 \u3000\u3000\u674e\u601d\u5357\u542c\u5f97\u6709\u4e00\u4e2a\u4eba\u54ed\u7740\u9a82\u9053\uff1a\u201c\u6211\u4eec\u53ea\u9053\u53ef\u4ee5\u6709\u51e0\u5e74\u7684\u592a\u5e73\u65e5\u5b50\u8fc7\uff0c\u65e9\u77e5\u5982\u6b64\uff0c\u8fd9\u70ed \u95f9\u4e0d\u770b\u4e5f\u7f62\uff01\u5343\u5200\u4e07\u5250\u7684\u8499\u53e4\u9791\u5b50\uff0c\u548c\u91d1\u864f\u90fd\u662f\u4e00\u6837\u7684\u9b54\u541b\uff01\u54fc\uff0c\u4ed6\u4eec\u6253\u4ed7\u4e5f\u597d\uff0c\u8bb2\u548c\u4e5f \u597d\uff0c\u54b1\u4eec\u6c49\u4eba\u603b\u662f\u6ca1\u6709\u597d\u65e5\u5b50\u8fc7\u7684\u4e86\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u89c1\u4ed6\u54ed\u5f97\u54c0\u75db\uff0c\u4e0d\u4fbf\u95ee\u4ed6\uff0c\u95ee\u53e6\u4e00\u4e2a\u4eba\u9053\uff1a\u201c\u539f\u6765\u8fd9\u73ed\u8499\u53e4\u9791\u5b50\u662f\u6765\u8bb2\u548c\u7684 \u5417\uff1f\u201d \u3000\u3000\u90a3\u4e2a\u4eba\u8be7\u9053\uff1a\u201c\u4f60\u8fd8\u4e0d\u77e5\u9053\u5417\uff1f\u4eac\u5e08\u91cc\u65e9\u5df2\u4f20\u5f00\u4e86\u3002\u4e0d\u662f\u9791\u5b50\u8981\u8bb2\u548c\uff0c\u662f\u91d1\u56fd\u7684\u7687\u5e1d\u8981 \u8bb2\u548c\uff0c\u4ed6\u6253\u4e0d\u8fc7\u8499\u53e4\u9791\u5b50\uff0c\u53ea\u597d\u6c42\u548c\u5566\u3002\u6211\u4eec\u662f\u4f4f\u5728\u9644\u8fd1\u7684\u6751\u5b50\uff0c\u542c\u8bf4\u91d1\u56fd\u7687\u5e1d\u6d3e\u4eba\u8fce\u63a5\u8499 \u53e4\u524d\u6765\u8bae\u8c08\u7684\u4f7f\u8005\uff0c\u6545\u6b64\u8dd1\u51fa\u6765\u770b\u70ed\u95f9\uff0c\u60f3\u4e0d\u5230\u9047\u4e0a\u4e86\u8fd9\u6869\u7978\u4e8b\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u5341\u5206\u6124\u6168\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u8bf4\u5f97\u5bf9\uff0c\u5973\u771f\u9791\u5b50\u3001\u8499\u53e4\u9791\u5b50\uff0c\u90fd\u662f\u54b1\u4eec\u8001\u767e\u59d3\u7684\u5bf9 \u5934\uff0c\u53ea\u6709\u628a\u4ed6\u4eec\u6253\u8dd1\u4e86\uff0c\u8001\u767e\u59d3\u624d\u6709\u597d\u65e5\u5b50\u8fc7\uff01\u201d\u90a3\u4eba\u9053\uff1a\u201c\u4f60\u8bf4\u8bdd\u5c0f\u5fc3\u4e9b\uff0c\u5728\u8fd9\u91cc\u8bf4\u6ca1\u5173 \u7cfb\uff0c\u5230\u4e86\u5927\u90fd\uff0c\u53ef\u4e0d\u80fd\u4e71\u8bf4\u8bdd\u4e86\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u591a\u8c22\u4e86\u90a3\u4e2a\u4eba\uff0c\u548c\u6768\u5a49\u7ee7\u7eed\u8d76\u8def\uff0c\u7b2c\u4e8c\u5929\u5230\u8fbe\u5927\u90fd\u3002\u91d1\u5ef7\u7684\u201c\u4eac\u5146\u5c39\u201d\uff08\u76f8\u5f53\u4e8e \u73b0\u4ee3\u5b98\u5236\u7684\u9996\u90fd\u5e02\u957f\uff09\u4e3a\u4e86\u6b22\u8fce\u8499\u53e4\u7684\u4f7f\u8005\u5230\u6765\uff0c\u4e00\u5728\u901a\u8861\u8981\u9053\u642d\u8d77\u4e86\u8bb8\u591a\u5f69\u574a\uff0c\u4e61\u4e0b\u4eba\u8fdb \u57ce\u6765\u770b\u70ed\u95f9\u7684\u6709\u5982\u8fc7\u6c5f\u4e4b\u9cab\uff0c\u674e\u601d\u5357\u3001\u6768\u5a49\u626e\u4f5c\u4e00\u5bf9\u666e\u901a\u7684\u519c\u5bb6\u592b\u5987\uff0c\u6ca1\u4eba\u7559\u610f\u4ed6\u4eec\uff0c\u672a\u906d \u76d8\u95ee\u3002 \u3000\u3000\u674e\u601d\u5357\u548c\u6768\u5a49\u8fdb\u4e86\u91d1\u4eac\uff0c\u4fbf\u5373\u524d\u5f80\u4e10\u5e2e\u7684\u5927\u90fd\u5206\u8235\u3002 \u3000\u3000\u5927\u90fd\u4e10\u5e2e\u7684\u5206\u8235\u8235\u4e3b\u5218\u8d76\u9a74\u770b\u89c1\u4e86\u674e\u601d\u5357\u7684\u540d\u5e16\uff0c\u53c8\u60ca\u53c8\u559c\uff0c\u8fde\u5fd9\u8dd1\u51fa\u6765\u8fce\u63a5\uff0c\u65e9\u5df2\u6765 \u5230\u4e86\u4e10\u5e2e\u7684\u5d14\u9547\u5c71\u4e5f\u8ddf\u7740\u4ed6\u4e00\u9053\u51fa\u6765\u3002 \u3000\u3000\u5d14\u9547\u5c71\u662f\u5728\u7eff\u6797\u5927\u4f1a\u4e0a\u89c1\u8fc7\u674e\u601d\u5357\u7684\uff0c\u4e00\u89c1\u6765\u4eba\u548c\u674e\u601d\u5357\u7684\u76f8\u8c8c\u4e0d\u540c\uff0c\u4e0d\u89c9\u6014\u4e86\u4e00\u6014\u3002 \u674e\u601d\u5357\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5d14\u5144\uff0c\u4f60\u4e0d\u8ba4\u5f97\u6211\u4e86\u5417\uff1f\u67f3\u5be8\u4e3b\u7ed9\u4e86\u6211\u4e00\u9897\u6613\u5bb9\u4e39\uff0c\u770b\u6765\u7684\u786e\u662f\u5f88\u6709\u529f \u7528\u5462\uff0c\u7792\u5f97\u8fc7\u670b\u53cb\u7684\u773c\u775b\u4e86\u3002\u201d \u3000\u3000\u5d14\u9547\u5c71\u534a\u4fe1\u534a\u7591\uff0c\u4f38\u51fa\u624b\u6765\u4e0e\u674e\u601d\u5357\u76f8\u63e1\u3002\u8fd9\u4e00\u63e1\u4ed6\u662f\u7528\u4e0a\u4e86\u91d1\u521a\u638c\u529b\u7684\uff0c\u4e00\u63e1\u4e4b\u4e0b\uff0c \u53ea\u89c1\u674e\u601d\u5357\u795e\u8272\u81ea\u5982\uff0c\u81ea\u5df1\u6240\u7528\u7684\u90a3\u80a1\u521a\u731b\u4e4b\u6781\u7684\u91d1\u521a\u638c\u529b\uff0c\u7adf\u5982\u6ce5\u725b\u5165\u6d77\uff0c\u7ed9\u5bf9\u65b9\u8f7b\u63cf\u6de1 \u5199\u7684\u5c31\u5316\u89e3\u4e86\uff0c\u8fd9\u6b63\u662f\u5c11\u6797\u6d3e\u6b63\u5b97\u7684\u5185\u529f\u3002\u5d14\u9547\u5c71\u4e00\u8bd5\u4e4b\u540e\uff0c\u5e76\u65e0\u6000\u7591\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u539f\u6765 \u5982\u6b64\uff0c\u674e\u76df\u4e3b\uff0c\u4f60\u8fd9\u4e2a\u9669\u53ef\u662f\u5192\u5f97\u592a\u5927\u4e86\u554a\uff01\u8fd9\u4f4d\u60f3\u5fc5\u662f\u6768\u59d1\u5a18\u4e86\uff1f\u201d\u674e\u3001\u6768\u4e8c\u4eba\u548c\u5218\u8d76\u9a74 \u89c1\u8fc7\u4e86\u793c\uff0c\u674e\u601d\u5357\u4fbf\u5373\u95ee\u9053\uff1a\u201c\u542c\u8bf4\u8d35\u5e2e\u7684\u9646\u5e2e\u4e3b\u5df2\u7ecf\u6765\u5230\u4e86\u5927\u90fd\uff0c\u60f3\u5fc5\u662f\u4f4f\u5728\u8fd9\u513f\uff1f\u201d \u3000\u3000\u539f\u6765\u4e10\u5e2e\u7684\u5e2e\u4e3b\u9646\u6606\u4ed1\u662f\u674e\u601d\u5357\u95fb\u540d\u5df2\u4e45\u7684\u4e00\u4f4d\u6b66\u6797\u8c6a\u6770\uff0c\u4e10\u5e2e\u53c8\u662f\u5929\u4e0b\u7b2c\u4e00\u5927\u5e2e\uff0c\u674e \u601d\u5357\u8fd9\u6b21\u5192\u9669\u8fdb\u4eac\uff0c\u5c31\u662f\u60f3\u89c1\u9646\u6606\u4ed1\uff0c\u5546\u8c08\u548c\u4e10\u5e2e\u5408\u4f5c\u4e4b\u4e8b\uff0c\u5e2e\u5fd9\u891a\u3001\u8c37\u4e8c\u4eba\u9664\u6389\u9633\u5929\u96f7\u7684 \u4e8b\u60c5\u8fd8\u5728\u5176\u6b21\u3002 \u3000\u3000\u5218\u8d76\u9a74\u662f\u9646\u6606\u4ed1\u7684\u5e08\u4f84\uff0c\u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u655d\u5e08\u53d4\u6b63\u5728\u540e\u9762\u7684\u56ed\u5b50\u91cc\u548c\u4e00\u4f4d\u97e9\u8001\u82f1\u96c4\u4e0b\u68cb\uff0c \u6211\u5306\u5306\u51fa\u6765\uff0c\u6765\u4e0d\u53ca\u544a\u8bc9\u4ed6\uff0c\u8bf7\u76df\u4e3b\u6055\u7f6a\u3002\u54b1\u4eec\u8fd9\u5c31\u5230\u540e\u56ed\u53bb\u89c1\u4ed6\u8001\u4eba\u5bb6\u5427\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u8fd9\u4f4d\u97e9\u8001\u82f1\u96c4\u662f\u4e0d\u662f\u5728\u6d1b\u9633\u9690\u5c45\u7684\u90a3\u4f4d\u97e9\u5927\u4fa0\uff0c\u97e9\u5927\u7ef4\u3002\u201d \u3000\u3000\u5218\u8d76\u9a74\u9053\uff1a\u201c\u6b63\u662f\uff0c\u97e9\u8001\u82f1\u96c4\u548c\u5979\u7684\u5973\u513f\u90fd\u6765\u4e86\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u5927\u559c\u9053\uff1a\u201c\u8fd9\u5c31\u66f4\u597d\u4e86\u3002\u8fd9\u4f4d\u97e9\u8001\u82f1\u96c4\u4e5f\u662f\u6211\u5fc3\u4eea\u5df2\u4e45\u7684\u3002\u201d \u3000\u3000\u539f\u6765\u97e9\u5927\u7ef4\u662f\u548c\u674e\u601d\u5357\u5e08\u7236\u8c37\u5e73\u9633\u540c\u4e00\u73ed\u8f88\u7684\u4eba\u7269\uff0c\u8c37\u5e73\u9633\u5e38\u5e38\u548c\u674e\u601d\u5357\u63d0\u8d77\u7684\uff0c\u6b64\u4eba \u9887\u6709\u5bb6\u8d22\uff0c\u662f\u4ee5\u4e2d\u5e74\u4e4b\u540e\uff0c\u4fbf\u5373\u95ed\u95e8\u5c01\u5200\uff0c\u5728\u5bb6\u7eb3\u798f\uff0c\u4e0d\u518d\u884c\u8d70\u6c5f\u6e56\u3002\u6b64\u6b21\u662f\u56e0\u4e3a\u8499\u53e4\u7684\u5927 \u519b\u6253\u4e0b\u6d1b\u9633\uff0c\u4ed6\u624d\u548c\u5973\u513f\u9003\u8dd1\u51fa\u6765\u7684\u3002 \u3000\u3000\u5218\u8d76\u9a74\u672c\u6765\u662f\u6d1b\u9633\u7684\u4e10\u5e2e\u5206\u8235\u8235\u4e3b\uff0c\u4e5f\u662f\u56e0\u4e3a\u6d1b\u9633\u5931\u9677\uff0c\u65b9\u59cb\u7ed9\u4e10\u5e2e\u7684\u603b\u8235\uff0c\u5c06\u4ed6\u8c03\u4efb \u5927\u90fd\uff0c\u4e3b\u6301\u5206\u8235\u3002\u4ed6\u5728\u6d1b\u9633\u548c\u97e9\u5927\u7ef4\u4ea4\u60c5\u6781\u597d\uff0c\u6545\u6b64\u97e9\u5bb6\u7236\u5973\u6765\u6295\u5954\u4ed6\u3002\u5982\u4eca\u8499\u53e4\u5175\u867d\u7136\u662f \u51fa\u4e86\u6d1b\u9633\uff0c\u4f46\u97e9\u5927\u7ef4\u65e9\u5df2\u662f\u5bb6\u7834\u4eba\u4ea1\uff0c\u662f\u4ee5\u4e5f\u5c31\u7559\u5728\u5927\u90fd\uff0c\u4e0d\u56de\u53bb\u4e86\u3002 \u3000\u3000\u674e\u601d\u5357\u8ddf\u7740\u5218\u8d76\u9a74\u8fdb\u4e86\u540e\u56ed\uff0c\u53ea\u89c1\u4e00\u4e2a\u9752\u8863\u8001\u8005\u6b63\u5728\u548c\u4e00\u4e2a\u8eab\u6750\u9b41\u68a7\u7684\u4e2d\u5e74\u6c49\u5b50\u4e0b\u68cb\u3002 \u4ed6\u4eec\u4e0b\u68cb\u7684\u65b9\u6cd5\u53ef\u662f\u975e\u5e38\u53e4\u602a\uff0c\u674e\u6069\u5357\u89c1\u6240\u672a\u89c1\uff0c\u95fb\u6240\u672a\u95fb\u3002 \u3000\u3000\u4ed6\u4eec\u4e0b\u7684\u662f\u56f4\u68cb\uff0c\u4f46\u662f\u90a3\u5757\u68cb\u76d8\uff0c\u5374\u4e0d\u662f\u653e\u5728\u4ed6\u4eec\u7684\u9762\u524d\uff0c\u800c\u662f\u6302\u5728\u5bf9\u9762\u7684\u5899\u4e0a\u7684\u3002 \u3000\u3000\u5728\u90a3\u9752\u8863\u8001\u8005\u7684\u8eab\u8fb9\uff0c\u8fd8\u6709\u4e00\u4f4d\u5999\u9f84\u5c11\u5973\u3002\u674e\u601d\u5357\u6599\u60f3\u8fd9\u9752\u8863\u8001\u8005\u5fc5\u5b9a\u662f\u97e9\u5927\u7ef4\uff0c\u548c\u4ed6 \u5bf9\u5955\u7684\u90a3\u4e2a\u9b41\u68a7\u6c49\u5b50\u81ea\u662f\u4e10\u5e2e\u7684\u5e2e\u4e3b\u9646\u6606\u4ed1\u4e86\u3002\u4e00\u95ee\u5218\u8d76\u9a74\uff0c\u679c\u7136\u6240\u6599\u4e0d\u5dee\u3002\u5218\u8d74\u9a74\u9053\uff1a \u201c\u7ad9\u5728\u97e9\u5927\u7ef4\u8eab\u8fb9\u7684\u8fd9\u4e2a\u5c11\u5973\u662f\u4ed6\u7684\u5973\u513f\u97e9\u4f69\u745b\uff0c\u4e5f\u662f\u6c5f\u6e56\u4e0a\u4e00\u4f4d\u5f88\u6709\u540d\u6c14\u7684\u5973\u4fa0\u3002\u5979\u7236\u4eb2 \u95ed\u95e8\u5c01\u5200\uff0c\u5728\u5bb6\u7eb3\u798f\uff0c\u5979\u5012\u662f\u5e38\u5e38\u51fa\u6765\u8d70\u52a8\u7684\u3002\u201d\u674e\u601d\u5357\u9053\uff1a\u201c\u770b\u6765\u4ed6\u4eec\u6b63\u5728\u5f08\u5230\u7528\u795e\u4e4b \u5904\uff0c\u6682\u4e14\u4e0d\u8981\u60ca\u52a8\u4ed6\u4eec\u4e86\u3002\u201d \u3000\u3000\u53ea\u542c\u5f97\u9646\u6606\u4ed1\u8bf4\u9053\uff1a\u201c\u97e9\u5927\u53d4\uff0c\u8be5\u4f60\u4e0b\u5566\uff01\u201d\u97e9\u5927\u7ef4\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u9646\u8001\u5f1f\uff0c\u4f60\u4eca\u5929\u7684\u4e0b \u5b50\uff0c\u53d6\u52bf\u600e\u7684\u5982\u6b64\u51cc\u5389\uff0c\u6211\u8fd9\u7cdf\u8001\u5934\u513f\u53ea\u6015\u662f\u62db\u67b6\u4e0d\u4f4f\u4e86\u3002\u201d\u8bf4\u7f62\uff0c\u62c8\u8d77\u4e00\u679a\u767d\u5b50\uff0c\u5411\u90a3\u60ac \u4f73\u5728\u5899\u4e0a\u7684\u68cb\u76d8\u63b7\u53bb\uff0c\u53ea\u542c\u5f97\u201c\u556a\u201d\u7684\u4e00\u58f0\uff0c\u90a3\u679a\u767d\u5b50\u521a\u597d\u5d4c\u5728\u68cb\u76c8\u4e0a\u7eb5\u6a2a\u4e24\u9053\u9ed1\u7ebf\u7684\u4ea4\u53c9 \u4e4b\u70b9\u3002\u90a3\u662f\u7eb5\u5341\u4e94\u8def\uff0c\u6a2a\u56db\u8def\u7684\u4f4d\u7f6e\uff0c\u4f9d\u68cb\u52bf\u800c\u8bba\uff0c\u97e9\u5927\u7ef4\u8fd9\u4e00\u5b50\u7684\u7528\u610f\u4e43\u662f\u5728\u4e8e\u4fdd\u89d2\u3002 \u3000\u3000\u674e\u6069\u5357\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u6837\u7684\u4e0b\u68cb\uff0c\u4e0d\u4f46\u662f\u6bd4\u8d5b\u68cb\u529b\u3002\u800c\u4e14\u662f\u6bd4\u8d5b\u6697\u5668\u7684\u529f \u592b\u3002\u5185\u529b\u548c\u51c6\u5934\u7a0d\u5dee\uff0c\u5c31\u8981\u51fa\u9519\uff01\u201d \u3000\u3000\u9646\u6606\u4ed1\u7b11\u9053\uff1a\u201c\u97e9\u5927\u53d4\uff0c\u8fd9\u4e2a\u89d2\u6211\u53ef\u4e0d\u80fd\u8ba9\u4f60\u5360\u53bb\uff01\u201d\u62c8\u8d77\u4e00\u679a\u9ed1\u5b50\uff0c\u201c\u556a\u201d\u7684\u4e00\u58f0\uff0c \u63b7\u5728\u201c\u4e8c\u3001\u4e09\u201d\u8def\u4e0a\uff0c\u548c\u97e9\u5927\u7ef4\u5c55\u5f00\u4e86\u4e00\u9685\u4e4b\u5730\u7684\u4e89\u593a\u6218\u3002 \u3000\u3000\u674e\u601d\u5357\u770b\u4e86\u4e00\u4f1a\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u4e24\u4eba\u7684\u68cb\u529b\u5dee\u4e0d\u591a\uff0c\u6697\u5668\u529f\u592b\u4e5f\u662f\u4e0d\u76f8\u4f2f\u4ef2\u3002\u4f46\u9646\u5e2e \u4e3b\u53ea\u987e\u548c\u5bf9\u65b9\u4e89\u89d2\uff0c\u5916\u56f4\u4e4b\u52bf\u5982\u88ab\u97e9\u8001\u82f1\u96c4\u593a\u53bb\uff0c\u53ea\u6015\u662f\u6709\u70b9\u5f97\u4e0d\u507f\u5931\u4e86\u3002\u201d \u3000\u3000\u5fc3\u5ff5\u672a\u5df2\uff0c\u53ea\u542c\u5f97\u90a3\u5c11\u5973\u8bf4\u9053\uff1a\u201c\u7239\u7239\uff0c\u8fd9\u76d8\u68cb\u4f60\u6050\u6015\u8981\u8f93\u7ed9\u9646\u5e2e\u4e3b\u4e86\uff01\u201d\u97e9\u5927\u7ef4\u62c8\u987b \u5fae\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u662f\u5417\uff1f\u201d\u674e\u601d\u5357\u6697\u81ea\u60f3\u9053\uff1a\u201c\u97e9\u8001\u82f1\u96c4\u82e5\u7136\u6295\u5b50\u2018\u5929\u5143\u2019\uff08\u56f4\u68cb\u672f\u8bed\uff0c\u5373\u68cb \u76d8\u7684\u6b63\u4e2d\u592e\u4e4b\u70b9\uff09\uff0c\u6b64\u5c40\u5927\u7ea6\u53ef\u6709\u4e03\u6210\u80dc\u7b97\u3002\u201d \u3000\u3000\u679c\u7136\u97e9\u5927\u7ef4\u62c8\u8d77\u4e00\u679a\u767d\u5b50\uff0c\u201c\u556a\u201d\u7684\u4e00\u58f0\uff0c\u68cb\u5b50\u5c31\u521a\u597d\u5d4c\u5728\u68cb\u76d8\u4e0a\u7684\u201c\u5929\u5143\u201d\u4f4d\u7f6e\u3002\u674e \u601d\u5357\u4e5f\u662f\u4e00\u4e2a\u68cb\u8ff7\uff0c\u770b\u89c1\u97e9\u5927\u7ef4\u7684\u4e0b\u5b50\u5982\u4ed6\u6240\u6599\uff0c\u4e00\u65f6\u6b22\u559c\uff0c\u7981\u4e0d\u4f4f\u5c31\u5927\u58f0\u53eb\u597d\u8d77\u6765\uff01 \u3000\u3000\u9646\u6606\u4ed1\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5f53\u5c40\u8005\u8ff7\uff0c\u65c1\u89c2\u8005\u6e05\u3002\u6211\u53ea\u987e\u4e00\u9685\u4e4b\u5730\uff0c\u5fd8\u4e86\u8fdb\u53d6\u4e2d\u539f\u3002\u8fd9\u5c40\u68cb\u6211 \u662f\u8f93\u5b9a\u7684\u4e86\uff0c\u4e0d\u5fc5\u518d\u4e0b\u5566\u3002\u201d \u3000\u3000\u97e9\u5927\u7ef4\u9053\uff1a\u201c\u5218\u8d76\u9a74\uff0c\u6211\u5374\u4e0d\u77e5\u4f60\u4eec\u4e10\u5e2e\u4e4b\u4e2d\uff0c\u5c1a\u6709\u8fd9\u6837\u4e00\u4f4d\u9ad8\u624b\u3002\u600e\u4e48\u4e0d\u65e9\u70b9\u544a\u8bc9 \u6211\uff1f\u201d \u3000\u3000\u4e24\u4eba\u63a8\u79e4\u800c\u8d77\uff01\u5218\u8d76\u9a74\u7b11\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u5317\u4e94\u7701\u7684\u7eff\u6797\u76df\u4e3b\u674e\u5c11\u4fa0\uff0c\u521a\u521a\u5230\u7684\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u4e0a\u524d\u4e0e\u4e24\u4f4d\u524d\u8f88\u89c1\u8fc7\u4e86\u793c\uff0c\u8bf4\u9053\uff1a\u201c\u665a\u8f88\u674e\u601d\u5357\uff0c\u5bb6\u5e08\u662f\u5c11\u6797\u6d3e\u7684\u8c37\u5e73\u9633\uff0c\u665a\u8f88 \u662f\u5e38\u5e38\u542c\u5f97\u5bb6\u5e08\u8bf4\u8d77\u4e24\u4f4d\u524d\u8f88\u7684\uff0c\u6545\u6b64\u4eca\u65e5\u7279\u6765\u62dc\u8c12\u3002\u201d\u6b63\u662f\uff1a \u3000\u3000\u5f97\u9053\u7531\u6765\u591a\u52a9\u529b\uff0c\u68cb\u4e89\u4e00\u7740\u51b3\u96cc\u96c4\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b\u2014\u2014\u7b2c\u4e94\u5341\u56de\u3000\u68cb\u4e89\u5148\u7740\u4ea4\u8c6a\u6770\u3000\u9635\u5e03\u7384\u865a\u8bf1\u654c\u4eba \u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b \u7b2c\u4e94\u5341\u56de\u3000\u68cb\u4e89\u5148\u7740\u4ea4\u8c6a\u6770\u3000\u9635\u5e03\u7384\u865a\u8bf1\u654c\u4eba \u3000\u3000\u97e9\u5927\u7ef4\u559c\u9053\uff1a\u201c\u554a\uff0c\u539f\u6765\u4f60\u662f\u8001\u8c37\u7684\u5f92\u5f1f\uff0c\u602a\u4e0d\u5f97\u6709\u8fd9\u7b49\u672c\u4e8b\u3002\u201d\u674e\u601d\u5357\u5fc3\u60f3\uff1a\u201c\u6211\u4e0e \u4f60\u521a\u521a\u4f1a\u9762\uff0c\u4f60\u53c8\u600e\u77e5\u6211\u6709\u4ec0\u4e48\u672c\u4e8b\u3002\u201d \u3000\u3000\u97e9\u5927\u7ef4\u63a5\u4e0b\u53bb\u8bf4\u9053\uff1a\u201c\u5f53\u5e74\u6211\u548c\u4f60\u5e08\u7236\u66fe\u7ecf\u5e9f\u5bdd\u5fd8\u9910\uff0c\u8fde\u5f08\u5341\u65e5\uff0c\u6bd4\u5bf9\u662f\u4f60\u7684\u5e08\u7236\u591a\u80dc \u4e00\u5c40\u3002\u53ef\u60dc\u4ee5\u540e\u5c31\u6ca1\u6709\u673a\u4f1a\u518d\u548c\u4f60\u7684\u5e08\u7236\u4e0b\u68cb\u4e86\uff0c\u8fd9\u4e00\u5c40\u4e4b\u4ec7\uff0c\u59cb\u7ec8\u672a\u62a5\uff01\u201d\u8bf4\u7f62\u54c8\u54c8\u5927 \u7b11\u3002\u674e\u601d\u5357\u8fd9\u624d\u77e5\u9053\uff0c\u539f\u6765\u4ed6\u8bf4\u7684\u662f\u4e0b\u68cb\u7684\u672c\u4e8b\u3002 \u3000\u3000\u97e9\u5927\u7ef4\u8fd9\u4e2a\u4eba\u7684\u813e\u6c14\u662f\u9887\u4e3a\u6709\u70b9\u602a\u50fb\u7684\uff0c\u5bf9\u8bdd\u4e0d\u6295\u673a\u7684\u4eba\uff0c\u4ed6\u53ef\u4ee5\u6574\u5929\u4e0d\u8bf4\u4e00\u53e5\u8bdd\uff0c\u5408 \u4ed6\u5fc3\u610f\u7684\u4eba\uff0c\u4ed6\u5f20\u5f00\u4e86\u53e3\uff0c\u5c31\u6ed4\u6ed4\u4e0d\u7edd\u3002\u63a5\u7740\u53c8\u9053\uff1a\u201c\u4f60\u7684\u5e08\u7236\u4e0d\u4f46\u662f\u6211\u7684\u597d\u670b\u53cb\uff0c\u6211\u4eec\u8fd8 \u505a\u4e86\u4eb2\u5bb6\u5462\uff0c\u4f60\u77e5\u4e0d\u77e5\u9053\uff1f\u201d\u674e\u601d\u5357\u6014\u4e86\u4e00\u6014\uff0c\u4ed6\u53ef\u6ca1\u6709\u542c\u5f97\u5e08\u7236\u8bf4\u8fc7\u3002 \u3000\u3000\u97e9\u5927\u7ef4\u8bf4\u9053\uff1a\u201c\u626c\u5dde\u7684\u8c37\u82e5\u865a\u662f\u548c\u4f60\u5e08\u7236\u540c\u4e00\u652f\u7684\uff0c\u5c0f\u5973\u4f69\u745b\u5c31\u662f\u8bb8\u914d\u7ed9\u4ed6\u7684\u513f\u5b50\uff0c\u6240 \u4ee5\u4f60\u4e5f\u7b97\u5f97\u662f\u4f69\u745b\u7684\u5c0f\u53d4\u5462\u3002\u201d \u3000\u3000\u9646\u6606\u4ed1\u9053\uff1a\u201c\u97e9\u8001\u82f1\u96c4\u7684\u5973\u5a7f\u5c31\u662f\u8fd1\u5e74\u5728\u6c5f\u6e56\u4e0a\u58f0\u8a89\u9e4a\u8d77\u7684\u8c37\u5c11\u4fa0\u8c37\u5578\u98ce\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u6697\u6697\u597d\u7b11\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u5012\u662f\u7528\u7b97\u76d8\u624d\u6253\u5f97\u4e0a\u7684\u4eb2\u621a\u4e86\u3002\u201d\u539f\u6765\u8c37\u5bb6\u4e43\u662f\u5927\u65cf\uff0c\u4ed6 \u7684\u5e08\u7236\u8c37\u5e73\u9633\u5bb6\u4f4f\u6c5f\u5357\u5c6f\u6eaa\uff0c\u626c\u5dde\u5219\u662f\u6b63\u5f53\u7740\u957f\u6c5f\u5357\u5317\u7684\u4ea4\u754c\u4e4b\u5904\uff0c\u867d\u7136\u4e5f\u53ef\u7b97\u662f\u6c5f\u5357\uff0c\u4f46 \u5374\u662f\u9694\u7740\u4e00\u6761\u957f\u6c5f\u7684\u3002 \u3000\u3000\u5c6f\u6eaa\u5c5e\u5357\u5b8b\u7edf\u6cbb\uff0c\u626c\u5dde\u5219\u65e9\u5df2\u6ca6\u9677\uff0c\u5f52\u5c5e\u91d1\u56fd\u7684\u7248\u56fe\u4e86\uff0c\u8c37\u4e2d\u9633\u548c\u8c37\u82e5\u865a\u662f\u8981\u644a\u5f00\u65cf\u8c31 \u6765\u7b97\uff0c\u624d\u7b97\u5f97\u51fa\u662f\u5144\u5f1f\u6392\u884c\u7684\u3002\u4e0d\u8fc7\uff0c\u626c\u5dde\u8c37\u82e5\u865a\u5728\u6c5f\u6e56\u4e0a\u7684\u58f0\u540d\uff0c\u5374\u5012\u662f\u4e0d\u5728\u4ed6\u7684\u5e08\u7236\u8c37 \u5e73\u9633\u4e4b\u4e0b\u3002 \u3000\u3000\u97e9\u4f69\u745b\u7c89\u9762\u98de\u971e\uff0c\u8bf4\u9053\uff1a\u201c\u7239\u7239\uff0c\u674e\u5927\u54e5\u6765\u5230\u4e00\u5b9a\u662f\u6709\u4e8b\u8981\u548c\u9646\u5e2e\u4e3b\u5546\u91cf\uff0c\u4f60\u5374\u53ea\u987e\u548c \u4ed6\u5520\u53e8\u95f2\u8bdd\uff01\u201d \u3000\u3000\u97e9\u5927\u7ef4\u54c8\u54c8\u7b11\u9041\uff1a\u201c\u5bf9\uff0c\u5bf9\u3002\u6211\u53c8\u72af\u4e86\u8001\u6bdb\u75c5\u3002\u597d\uff0c\u7b49\u4f60\u4eec\u8bf4\u4e86\u6b63\u7ecf\u4e8b\u513f\uff0c\u6211\u518d\u548c\u4f60\u804a \u5427\u3002\u201d\u674e\u601d\u5357\u9053\uff1a\u201c\u5176\u5b9e\u5c0f\u4f84\u4e5f\u6ca1\u6709\u4ec0\u4e48\u7d27\u8981\u7684\u4e8b\u60c5\u3002\u201d \u3000\u3000\u97e9\u5927\u7ef4\u8bf4\u662f\u8981\u8ba9\u4ed6\u4eec\u8c08\u6b63\u7ecf\u4e8b\u513f\uff0c\u5374\u5fcd\u4e0d\u4f4f\u53c8\u8bf4\u4e86\u51e0\u53e5\u95f2\u8bdd\uff0c\u95ee\u674e\u601d\u5357\u9053\uff1a\u201c\u8fd9\u4f4d\u59d1\u5a18 \u662f\u2014\u2014\u201d\u5d14\u9547\u5c71\u4e5f\u662f\u4e2a\u5634\u5feb\u7684\u4eba\uff0c\u4ee3\u4ed6\u7b54\u9053\uff1a\u201c\u8fd9\u4f4d\u6768\u59d1\u5a18\u5c31\u662f\u6211\u4eec\u76df\u4e3b\u7684\u592b\u4eba\u3002\u201d\u6768\u5a49\u6ee1 \u9762\u901a\u7ea2\uff0c\u77aa\u4ed6\u4e00\u773c\uff0c\u5d14\u9547\u5c71\u7b11\u9053\uff1a\u201c\u867d\u672a\u6210\u4eb2\uff0c\u4f46\u4f60\u4eec\u8fd9\u676f\u559c\u9152\u6211\u603b\u662f\u559d\u5b9a\u4e86\u3002\u53eb\u4e00\u58f0\u76df\u4e3b \u592b\u4eba\uff0c\u53c8\u6709\u4f55\u59a8\uff1f\u201d \u3000\u3000\u97e9\u5927\u7ef4\u66f4\u662f\u6b22\u559c\uff0c\u8bf4\u9053\uff1a\u201c\u4f69\u745b\uff0c\u4f60\u5e94\u8be5\u548c\u8fd9\u4f4d\u6768\u59d1\u5a18\u591a\u4eb2\u70ed\u4e00\u4e9b\uff0c\u4f60\u4eec\u662f\u4e00\u5bb6\u4eba \u5462\u3002\u201d\u97e9\u4f69\u745b\u55d4\u9053\uff1a\u201c\u7239\u7239\uff0c\u4f60\u53c8\u4e71\u8bf4\u4e86\u3002\u201d\u97e9\u5927\u7ef4\u9053\uff1a\u201c\u6211\u600e\u4e48\u662f\u4e71\u8bf4\uff0c\u5c06\u6765\u2014\u2014\u201d\u97e9\u4f69 \u745b\u9053\uff1a\u201c\u597d\uff0c\u597d\uff0c\u6768\u59d0\u59d0\uff0c\u54b1\u4eec\u8fc7\u4e00\u8fb9\u8bf4\u8bdd\uff0c\u522b\u542c\u7239\u7684\u7f57\u55e6\u3002\u201d\u97e9\u5927\u7ef4\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u597d\uff0c \u597d\uff0c\u4e0d\u8bf4\u4e86\uff0c\u4e0d\u8bf4\u4e86\u3002\u4f60\u4eec\u5973\u5b69\u513f\u5bb6\u5c31\u662f\u61c2\u5f97\u5bb3\u7f9e\u3002\u201d \u3000\u3000\u97e9\u5927\u7ef4\u8bf4\u51fa\u4e86\u5973\u513f\u5df2\u6709\u5a46\u5bb6\u4e4b\u4e8b\uff0c\u4ed6\u7684\u5973\u513f\u602a\u4ed6\u591a\u8bdd\uff0c\u4f46\u542c\u8fdb\u6768\u5a49\u7684\u8033\u4e2d\uff0c\u5374\u662f\u5c11\u4e86\u4e00 \u5c42\u987e\u8651\u3002 \u3000\u3000\u6768\u5a49\u5f88\u5c11\u6709\u79f0\u5f97\u4e0a\u201c\u77e5\u5df1\u201d\u7684\u670b\u53cb\uff0c\u8fd9\u5012\u4e0d\u662f\u56e0\u4e3a\u5979\u843d\u843d\u96be\u5408\uff0c\u800c\u662f\u56e0\u4e3a\u5979\u51fa\u8eab\u540d\u95e8\u671b \u65cf\uff0c\u6c14\u8d28\u4e0e\u4e00\u822c\u51fa\u8eab\u8349\u83bd\u7684\u6c5f\u6e56\u513f\u5973\u4e0d\u540c\u4e4b\u6545\u3002\u4f8b\u5982\u5979\u548c\u5c60\u51e4\u53ca\u5b5f\u660e\u971e\u4e8c\u4eba\uff0c\u56fa\u7136\u53ef\u4ee5\u8bf4\u5f97 \u662f\u201c\u5fd7\u540c\u9053\u5408\u201d\uff0c\u4f46\u5374\u4ecd\u7136\u4e0d\u662f\u201c\u610f\u6c14\u76f8\u6295\u201d\u3002 \u3000\u3000\u97e9\u4f69\u745b\u51fa\u8eab\u4e0e\u5979\u76f8\u540c\uff0c\u540c\u5c5e\u5927\u5bb6\u95fa\u79c0\uff0c\u4e24\u4eba\u76f8\u8bc6\u4e4b\u540e\u9887\u6709\u4e00\u89c1\u5982\u6545\u4e4b\u611f\uff0c\u8c08\u5f97\u5f88\u662f\u6295 \u673a\u3002 \u3000\u3000\u674e\u601d\u5357\u548c\u97e9\u5927\u7ef4\u7684\u8c08\u8bdd\u544a\u4e86\u4e00\u4e2a\u6bb5\u843d\uff0c\u5f53\u4e0b\u4e5f\u5c31\u548c\u4e10\u5e2e\u7684\u9646\u5e2e\u4e3b\u8bf4\u53ca\u6b63\u4e8b\u3002 \u3000\u3000\u9646\u6606\u4ed1\u9053\uff1a\u201c\u539f\u6765\u4f60\u5728\u8def\u4e0a\u5df2\u7ecf\u78b0\u89c1\u8499\u53e4\u524d\u6765\u8c08\u548c\u7684\u4f7f\u8005\u4e86\u3002\u636e\u6211\u4eec\u6240\u77e5\uff0c\u8499\u53e4\u6765\u7684\u8fd9 \u73ed\u4eba\uff0c\u90fd\u4f4f\u5728\u9633\u5929\u96f7\u7684\u56fd\u5e08\u5e9c\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u891a\u4e91\u5cf0\u3001\u8c37\u6db5\u865a\u4e8c\u4eba\u5373\u5c06\u6765\u4eac\u6e05\u7406\u5e08\u95e8\uff0c\u90a3\u4e2a\u8499\u53e4\u56fd\u5e08\u7684\u6b66 \u529f\u975e\u540c\u5c0f\u53ef\uff0c\u6709\u8fd9\u4e00\u4e2a\u4eba\u5728\u4ed6\u90a3\u513f\uff0c\u6050\u6015\u66f4\u591a\u6ce2\u6298\u3002\u201d \u3000\u3000\u97e9\u5927\u7ef4\u9053\uff1a\u201c\u9f99\u8c61\u6cd5\u738b\u7684\u9f99\u8c61\u529f\u81ea\u5938\u5929\u4e0b\u65e0\u654c\uff0c\u4e5f\u672a\u5fc5\u5f53\u771f\u662f\u5929\u4e0b\u65e0\u654c\uff0c\u9646\u8001\u5f1f\uff0c\u5982\u679c \u4f60\u6211\u8054\u624b\uff0c\u4f9d\u6211\u770b\u6765\uff0c\u5012\u5927\u53ef\u548c\u4ed6\u4e00\u6597\u3002\u201d \u3000\u3000\u9646\u6606\u4ed1\u7b11\u9053\uff1a\u201c\u97e9\u8001\u524d\u8f88\uff0c\u4f60\u5c01\u5200\u591a\u5e74\uff0c\u96be\u5f97\u6709\u8fd9\u5174\u81f4\uff0c\u5230\u65f6\u4f60\u82e5\u53bb\u6597\u90a3\u9f99\u8c61\u6cd5\u738b\uff0c\u6211 \u4e00\u5b9a\u6267\u97ad\u968f\u94fe\u3002\u201d\u8fd9\u8bdd\u5373\u662f\u7b54\u5141\u4f5c\u4ed6\u7684\u52a9\u624b\u4e4b\u610f\u3002 \u3000\u3000\u97e9\u5927\u7ef4\u9053\uff1a\u201c\u8fd9\u4e0d\u662f\u4ec0\u4e48\u5174\u81f4\u4e0d\u5174\u81f4\u7684\u95ee\u9898\uff0c\u7ecf\u8fc7\u4e86\u8fd9\u4e00\u56de\u6218\u706b\uff0c\u6211\u624d\u77e5\u9053\u662f\u6211\u9519\u4e86\u3002 \u6211\u4e0d\u60f3\u7406\u5916\u95f4\u7684\u95f2\u4e8b\uff0c\u522b\u4eba\u5374\u8981\u2018\u7406\u2019\u5230\u6211\u7684\u5934\u4e0a\uff0c\u8499\u53e4\u9791\u5b50\u6bc1\u4e86\u6211\u7684\u5bb6\uff0c\u6211\u8fd8\u80fd\u6015\u9791\u5b50\u7684 \u56fd\u5e08\u5417\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u559c\u9053\uff1a\u201c\u6709\u4e24\u4f4d\u524d\u8f88\u51fa\u624b\uff0c\u90a3\u662f\u6700\u597d\u4e0d\u8fc7\u4e86\u3002\u201d \u3000\u3000\u9646\u6606\u4ed1\u9053\uff1a\u201c\u91d1\u864f\u5411\u8499\u53e4\u6c42\u548c\u4e4b\u4e8b\uff0c\u6211\u4eec\u65e9\u5df2\u77e5\u9053\u3002\u542c\u8bf4\u8499\u53e4\u7684\u5c06\u519b\u4eec\u672c\u6765\u8fd8\u662f\u8981\u8fdb\u5175 \u7684\uff0c\u662f\u4ed6\u4eec\u7684\u56db\u738b\u62d6\u96f7\u4e3b\u5f20\u8c08\u548c\uff0c\u8fd9\u4ef6\u4e8b\u65b9\u59cb\u5b9a\u593a\u7684\u3002\u53ef\u80fd\u662f\u56e0\u4e3a\u6210\u5409\u601d\u6c57\u901d\u4e16\uff0c\u62d6\u96f7\u610f\u6b32 \u5148\u628a\u5185\u90e8\u5b89\u5b9a\uff0c\u7136\u540e\u65b9\u59cb\u5bf9\u5916\u4e4b\u6545\u3002\u201d \u3000\u3000\u97e9\u5927\u7ef4\u9053\uff1a\u201c\u62d6\u96f7\u662f\u6210\u5409\u601d\u6c57\u56db\u4e2a\u513f\u5b50\u4e2d\u6700\u7cbe\u660e\u7684\u4e00\u4e2a\uff0c\u4ed6\u6b64\u6b21\u524d\u6765\u5927\u90fd\uff0c\u5b9a\u6709\u56fe\u8c0b\uff0c \u54b1\u4eec\u5012\u662f\u4e0d\u53ef\u5ffd\u89c6\u3002\u601d\u5357\u8001\u5f1f\uff0c\u4f60\u770b\u6e05\u695a\u4e86\uff0c\u5f53\u771f\u662f\u4ed6\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u51b3\u4e0d\u4f1a\u770b\u9519\u3002\u5f53\u65f6\u6211\u89c9\u5f97\u5f88\u662f\u5947\u602a\uff0c\u62d6\u96f7\u4e3a\u4ec0\u4e48\u626e\u4f5c\u4e00\u4e2a\u5c0f\u519b\u5b98\uff1f\u73b0\u5728\u6211 \u542c\u4e86\u97e9\u8001\u524d\u8f88\u7684\u8bdd\uff0c\u5012\u662f\u609f\u51fa\u8fd9\u4e2a\u9053\u7406\u4e86\u3002\u5916\u4eba\u4e0d\u77e5\u9053\u4ed6\u7684\u8eab\u4efd\uff0c\u90a3\u5c31\u66f4\u65b9\u4fbf\u4ed6\u7aa5\u63a2\u91d1\u56fd\u7684 \u865a\u5b9e\uff0c\u6709\u751a\u9634\u8c0b\u7684\u8bdd\uff0c\u5728\u6697\u5730\u91cc\u8fdb\u884c\uff0c\u4e5f\u662f\u5bb9\u6613\u5f97\u591a\u3002\u201d \u3000\u3000\u8c08\u8bdd\u4e4b\u95f4\uff0c\u4e0d\u77e5\u4e0d\u89c9\uff0c\u5230\u4e86\u9ec4\u660f\u65f6\u5206\u3002\u97e9\u5927\u7ef4\u770b\u770b\u5929\u8272\uff0c\u7b11\u9053\uff1a\u201c\u6b63\u7ecf\u4e8b\u8c08\u5b8c\u4e86\uff0c\u54b1\u4eec \u4e5f\u8be5\u5546\u91cf\u5982\u4f55\u5b89\u987f\u601d\u5357\u8001\u5f1f\u5566\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u6014\u4e86\u4e00\u6014\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6211\u65e2\u7136\u6765\u5230\u6b64\u5730\uff0c\u96be\u9053\u8fd8\u4e0d\u662f\u4f4f\u5728\u8fd9\u513f\uff1f\u201d\u8981\u77e5\u674e\u601d\u5357 \u4e43\u662f\u4e49\u519b\u76df\u4e3b\u7684\u8eab\u4efd\uff0c\u81ea\u662f\u4e0d\u4fbf\u6295\u5bbf\u5ba2\u5e97\uff0c\u6545\u800c\u4ed6\u4e00\u8fdb\u4eac\u57ce\uff0c\u4fbf\u5f84\u81ea\u6295\u5954\u4e10\u5e2e\u5206\u8235\uff0c\u6839\u672c\u5c31 \u6ca1\u6709\u8003\u8651\u5230\u4f4f\u5bbf\u7684\u95ee\u9898\u3002 \u3000\u3000\u9646\u6606\u4ed1\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u97e9\u5927\u53d4\uff0c\u4f60\u662f\u4e0d\u662f\u60f3\u548c\u6211\u4eec\u62a2\u8fd9\u4e1c\u9053\u4e3b\u505a\uff1f\u201d\u5218\u8d76\u9a74\u4e5f\u7b11 \u9053\uff1a\u201c\u4e10\u5e2e\u6ca1\u6709\u9ad8\u624b\uff0c\u4f60\u8001\u4eba\u5bb6\u662f\u60f3\u627e\u674e\u76df\u4e3b\u966a\u4f60\u4e0b\u68cb\u5427\u3002\u201d \u3000\u3000\u97e9\u5927\u7ef4\u6b63\u8272\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u7684\u5206\u8235\u51fa\u5165\u4eba\u591a\uff0c\u800c\u4e14\u5b98\u5e9c\u4e5f\u662f\u77e5\u9053\u4f60\u4eec\u8fd9\u4e2a\u5904\u6240\u7684\uff0c\u867d\u8bf4\u4f60 \u4eec\u53ef\u4ee5\u628a\u601d\u5357\u5b89\u7f6e\u5185\u5802\uff0c\u4e25\u52a0\u9632\u536b\uff0c\u4f46\u4e5f\u6050\u9632\u4eba\u591a\u5634\u6742\uff0c\u8bf4\u4e0d\u5b9a\u4f1a\u6cc4\u9732\u98ce\u58f0\u3002\u201d \u3000\u3000\u9646\u6606\u4ed1\u970d\u7136\u4e00\u7701\uff0c\u8bf4\u9053\uff1a\u201c\u8001\u524d\u8f88\u987e\u8651\u5f97\u662f\u3002\u65e2\u7136\u5982\u6b64\uff0c\u6211\u53ea\u597d\u8ba9\u8fd9\u4e1c\u9053\u4e3b\u7ed9\u4f60\u505a\u4e86\u3002 \u597d\u5728\u4e24\u5bb6\u8ddd\u79bb\u4e0d\u8fdc\uff0c\u54b1\u4eec\u4e5f\u53ef\u4ee5\u4e92\u901a\u6d88\u606f\u3002\u201d \u3000\u3000\u539f\u6765\u97e9\u5927\u7ef4\u907f\u96be\u6765\u4eac\uff0c\u5bb6\u4e1a\u867d\u6bc1\uff0c\u5374\u4e5f\u5e26\u6765\u4e86\u4e00\u70b9\u6d6e\u8d22\uff0c\u4e10\u5e2e\u662f\u53ea\u6709\u7537\u5f1f\u5b50\u6ca1\u6709\u5973\u5f1f\u5b50 \u7684\uff0c\u800c\u4e10\u5e2e\u7684\u5f1f\u5b50\u6309\u7167\u5e2e\u89c4\uff0c\u53c8\u90fd\u662f\u53eb\u5316\u5b50\u6253\u626e\uff0c\u97e9\u4f69\u82f1\u81ea\u662f\u4e0d\u4fbf\u4f4f\u5728\u53eb\u5316\u5b50\u5806\u91cc\u3002\u56e0\u6b64\u97e9 \u5927\u7ef4\u5728\u4e10\u5e2e\u5206\u8235\u7684\u9644\u8fd1\uff0c\u4e70\u6709\u4e00\u6240\u4f4f\u5b85\uff0c\u7236\u5973\u540c\u4f4f\u3002 \u3000\u3000\u674e\u601d\u5357\u60f3\u9053\uff1a\u201c\u5a49\u59b9\u6709\u8fd9\u4f4d\u97e9\u59d1\u5a18\u4f5c\u4f34\uff0c\u81ea\u662f\u6700\u597d\u4e0d\u8fc7\uff01\u201d\u5f53\u4e0b\u6b23\u7136\u5e94\u547d\uff0c\u548c\u6768\u5a49\u53bb\u4f5c \u97e9\u5bb6\u7236\u5973\u7684\u5ba2\u4eba\u3002 \u3000\u3000\u97e9\u5927\u7ef4\u8fd9\u624d\u54c8\u54c8\u7b11\u9053\uff01\u201d\u8001\u5f1f\uff0c\u6211\u8bf7\u4f60\u5c48\u5c45\u5bd2\u820d\uff0c\u56fa\u7136\u662f\u4e3a\u4e86\u907f\u4eba\u8033\u76ee\uff0c\u4f46\u5218\u8001\u5f1f\u4e5f\u6ca1 \u6709\u8bf4\u9519\uff0c\u53e6\u4e00\u534a\u539f\u56e0\uff0c\u6211\u7684\u786e\u662f\u60f3\u9886\u6559\u4f60\u7684\u9ad8\u660e\u68cb\u672f\u3002\u201d \u3000\u3000\u9646\u6606\u4ed1\u5927\u7b11\u9053\uff1a\u201c\u662f\u5427\uff1f\u6bd5\u7adf\u662f\u7ed9\u6211\u8fd9\u5e08\u4f84\u8bf4\u4e2d\u4e86\u3002\u674e\u76df\u4e3b\uff0c\u4f60\u53ef\u8981\u5f53\u5fc3\u4e00\u4e9b\uff0c\u97e9\u8001\u524d \u8f88\u662f\u8981\u5728\u4f60\u7684\u8eab\u4e0a\u62a5\u4ee4\u5e08\u7684\u2018\u4e00\u5c40\u4e4b\u4ec7\u2019\u5462\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u7b11\u9053\uff1a\u201c\u6211\u7684\u68cb\u529b\u548c\u5bb6\u5e08\u76f8\u5dee\u592a\u8fdc\uff0c\u6839\u672c\u5c31\u591f\u4e0d\u4e0a\u505a\u97e9\u8001\u524d\u8f88\u7684\u2018\u654c\u624b\u2019\uff1f\u54ea\u8c08 \u5f97\u4e0a\u2018\u62a5\u4ec7\u2019\u4e8c\u5b57\u3002\u563f\uff0c\u563f\uff0c\u97e9\u8001\u524d\u8f88\u548c\u6211\u4e0b\u68cb\uff0c\u6211\u53ea\u6709\u62f1\u624b\u8ba4\u8f93\uff0c\u7518\u62dc\u4e0b\u98ce\u3002\u201d \u3000\u3000\u97e9\u5927\u7ef4\u8bf4\u9053\uff1a\u201c\u601d\u5357\u8001\u5f1f\uff0c\u4f60\u522b\u5ba2\u6c14\uff0c\u4fd7\u8bed\u8bf4\u2018\u884c\u5bb6\u4e00\u51fa\u624b\uff0c\u5c31\u77e5\u6709\u6ca1\u6709\u3002\u2019\u4e0b\u68cb\u5462\uff0c \u4e0d\u7528\u51fa\u624b\uff0c\u4e00\u51fa\u53e3\u5c31\u77e5\u2018\u6709\u6ca1\u6709\u2019\u4e86\u3002\u4f60\u521a\u624d\u8bf4\u90a3\u53e5\u8bdd\uff0c\u6211\u5c31\u77e5\u9053\u4f60\u7684\u68cb\u4e00\u5b9a\u4e0b\u5f97\u4e0d\u9519\u3002\u201d \u3000\u3000\u9646\u6606\u4ed1\u7b11\u9053\uff1a\u201c\u4ed6\u4e0d\u662f\u5ba2\u6c14\uff0c\u4ed6\u662f\u5148\u7ad9\u597d\u4e86\u5730\u6b65\uff0c\u90a3\u4e48\u8f93\u4e86\u7ed9\u4f60\uff0c\u4e5f\u53ea\u662f\u5f92\u5f1f\u8f93\u4e86\uff0c\u4e0d \u5931\u4ed6\u5e08\u7236\u7684\u9762\u5b50\uff0c\u97e9\u5927\u7ef4\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8bf4\u5f97\u4e0d\u9519\uff0c\u601d\u5357\u8001\u5f1f\uff0c\u539f\u6765\u4f60\u8fd8\u6709\u8fd9\u4efd\u6df1\u6c89\u7684\u5fc3\u601d\u3002 \u597d\uff0c\u90a3\u4e48\u54b1\u4eec\u5c31\u5404\u81ea\u7b97\u5e10\uff0c\u4e0d\u5fc5\u7275\u8fde\u5230\u4f60\u5e08\u7236\u7684\u5e10\u4e0a\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u4f4f\u5230\u97e9\u5bb6\uff0c\u97e9\u5927\u7ef4\u679c\u7136\u6bcf\u5929\u90fd\u8981\u627e\u674e\u601d\u5357\u966a\u4ed6\u4e0b\u68cb\u3002\u674e\u601d\u5357\u53cd\u6b63\u662f\u4e0d\u60f3\u51fa\u53bb\u6e38\u73a9 \u7684\uff0c\u4e50\u5f97\u966a\u4ed6\u4e0b\u68cb\uff0c\u7b49\u5019\u891a\u3001\u8c37\u4e8c\u4eba\u7684\u6d88\u606f\u3002 \u3000\u3000\u6768\u5a49\u548c\u97e9\u4f69\u745b\u4e00\u8d77\uff0c\u4e5f\u662f\u6781\u4e3a\u6295\u5408\u3002\u4e0d\u8fc7\u97e9\u4f69\u745b\u662f\u4e2a\u597d\u52a8\u7684\u4eba\uff0c\u5374\u5c31\u4e0d\u80fd\u50cf\u7236\u4eb2\u4e00\u6837\u5f85 \u5728\u5bb6\u91cc\u4e86\u3002 \u3000\u3000\u4e00\u65e5\uff0c\u97e9\u4f69\u745b\u548c\u6768\u5a49\u8bf4\u9053\uff1a\u201c\u5a49\u59d0\uff0c\u4f60\u4e00\u5411\u6ca1\u5230\u8fc7\u4eac\u57ce\u7684\u5427\uff0c\u60f3\u4e0d\u60f3\u770b\u770b\u4eac\u57ce\u7684\u7e41 \u534e\uff1f\u201d \u3000\u3000\u6768\u5a49\u5728\u5c71\u6c9f\u91cc\u4f4f\u4e86\u51e0\u5e74\uff0c\u96be\u5f97\u6709\u8fd9\u673a\u4f1a\u6765\u5230\u91d1\u4eac\uff0c\u95fb\u8a00\u4e0d\u7981\u7830\u7136\u5fc3\u52a8\uff0c\u8bf4\u9053\uff1a\u201c\u6050\u6015\u4e0d \u5927\u597d\u5427\uff0c\u4e07\u4e00\u51fa\u4e86\u4e8b\u2026\u2026\u201d\u97e9\u4f69\u745b\u7b11\u9053\uff1a\u201c\u5927\u90fd\u53c8\u6ca1\u4eba\u8ba4\u5f97\u4f60\uff0c\u4f60\u6015\u4ec0\u4e48\uff1f\u6211\u4e00\u4e2a\u4eba\u4e5f\u662f\u5e38 \u5e38\u72ec\u81ea\u51fa\u53bb\u7684\uff0c\u4ece\u6ca1\u51fa\u8fc7\u4e8b\u3002\u201d \u3000\u3000\u6768\u5a49\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6211\u73b0\u5728\u5df2\u7ecf\u6539\u5bb9\u6613\u8c8c\uff0c\u83ab\u8bf4\u6b64\u5730\u6ca1\u6709\u719f\u4eba\uff0c\u5373\u4f7f\u78b0\u4e0a\u719f\u4eba\uff0c\u4ed6\u4e5f\u4e0d\u77e5 \u662f\u6211\u3002\u201d\u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u6211\u626e\u4f5c\u4f60\u7684\u4e2b\u73af\uff0c\u4f60\u5e26\u6211\u51fa\u53bb\uff0c\u4e0d\u8fc7\u2026\u2026\u201d\u97e9\u4f69\u745b\u9053\uff1a\u201c\u4e0d\u8fc7\u4ec0 \u4e48\uff1f\u201d\u6768\u5a49\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u53ea\u6015\u4f60\u7684\u7239\u7239\u4e0d\u653e\u5fc3\u5427\u3002\u54b1\u4eec\u53bb\u95ee\u95ee\u4ed6\u518d\u8bf4\u3002\u201d \u3000\u3000\u97e9\u4f69\u745b\u7b11\u9053\uff1a\u201c\u4f60\u662f\u6015\u674e\u5927\u54e5\u4e0d\u653e\u5fc3\uff0c\u4e0d\u8bb8\u4f60\u53bb\uff0c\u5bf9\u5417\uff1f\u597d\u59d0\u59d0\uff0c\u54b1\u4eec\u4e0d\u544a\u8bc9\u4ed6\u5c31 \u662f\uff0c\u201d\u6768\u5a49\u8fdf\u7591\u9053\uff1a\u201c\u8fd9\u4e0d\u5927\u597d\u5427\uff1f\u201d\u97e9\u4f69\u745b\u8bf4\u9053\uff1a\u201c\u4ed6\u4eec\u8ff7\u5934\u8ff7\u8111\u5730\u4e0b\u68cb\uff0c\u54ea\u987e\u5f97\u4f4f\u54b1 \u4eec\uff1f\u53ea\u6015\u54b1\u4eec\u56de\u6765\u4e86\uff0c\u4ed6\u4eec\u7684\u68cb\u8fd8\u672a\u4e0b\u5b8c\u5462\uff01\u201d \u3000\u3000\u679c\u7136\u662f\u7ed9\u97e9\u4f69\u745e\u8bf4\u4e2d\uff0c\u8fd9\u65e5\u97e9\u5927\u7ef4\u7167\u4f8b\u7684\u53c8\u662f\u548c\u674e\u601d\u5357\u5728\u68cb\u76d8\u4e0a\u53ae\u6740\u5f97\u5929\u660f\u5730\u6697\uff0c\u65e5\u6708 \u65e0\u5149\u3002\u4e0d\u77e5\u4e0d\u89c9\u5f53\u771f\u662f\u201c\u5929\u660f\u5730\u6697\u201d\u2014\u2014\u5230\u4e86\u9ec4\u660f\u7684\u65f6\u5206\u4e86\u3002 \u3000\u3000\u53ef\u662f\u51fa\u4e4e\u97e9\u4f69\u745b\u610f\u6599\u4e4b\u5916\u7684\u662f\uff1a\u5979\u4eec\u5374\u4e0d\u80fd\u4f9d\u65f6\u56de\u6765\u3002 \u3000\u3000\u97e9\u5927\u7ef4\u548c\u674e\u601d\u5357\u662f\u5728\u56ed\u5b50\u91cc\u7684\u4e00\u95f4\u51c9\u4ead\u4e0b\u68cb\u7684\uff0c\u7791\u8272\u56db\u5408\uff0c\u68cb\u76d8\u4e0a\u72ac\u7259\u4ea4\u9519\u7684\u5f62\u52bf\u5df2\u7ecf \u662f\u770b\u5f97\u4e0d\u5927\u6e05\u695a\u4e86\u3002\u674e\u601d\u5357\u7b11\u9053\uff1a\u201c\u8fd9\u76d8\u7b97\u6211\u8f93\u4e86\uff0c\u54b1\u4eec\u4e5f\u8be5\u8fdb\u53bb\u5566\u3002\u201d\u97e9\u5927\u7ef4\u9053\uff1a\u201c\u627f \u8ba9\uff0c\u627f\u8ba9\u3002\u8fd9\u4e00\u5c40\u68cb\u4f9d\u7406\u8bf4\u6211\u53ef\u4ee5\u8d62\u4f60\uff0c\u4e0d\u8fc7\u53ea\u6015\u4e5f\u5f97\u5927\u8d39\u5fc3\u529b\u4e86\uff0c\u6240\u4ee5\u6211\u8fd8\u662f\u8981\u9886\u4f60\u7684 \u60c5\uff01\u674e\u601d\u5357\u5fc3\u91cc\u6b63\u81ea\u597d\u7b11\uff0c\u8fd9\u8001\u5934\u513f\u597d\u80dc\uff0c\u5ffd\u542c\u5f97\u97e9\u5927\u7ef4\u201c\u54a6\u201d\u4e86\u4e00\u58f0\u3002 \u3000\u3000\u539f\u6765\u97e9\u5927\u7ef4\u5ffd\u5730\u77bf\u7136\u4e00\u7701\uff0c\u7701\u8d77\u4eca\u65e5\u4e0b\u5348\uff0c\u5973\u513f\u4ece\u672a\u6765\u8fc7\u3002 \u3000\u3000\u674e\u601d\u5357\u53ea\u9053\u4ed6\u662f\u770b\u51fa\u68cb\u76d8\u4e0a\u6709\u751a\u7834\u7efd\uff0c\u4fbf\u9053\uff1a\u201c\u7684\u786e\u662f\u6211\u8f93\u4e86\uff0c\u8001\u4f2f\u4e0d\u5fc5\u63a8\u6572\u5566\u3002\u201d\u97e9 \u5927\u7ef4\u9053\uff1a\u201c\u4e0d\u662f\u8fd9\u4e2a\u3002\u5582\uff0c\u601d\u5357\u8d24\u4f84\uff0c\u4f60\u53ef\u8bb0\u5f97\u6768\u59d1\u5a18\u6765\u8fc7\u6ca1\u6709\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u6ca1\u6709\u6765\u8fc7\u3002\u5979\u672c\u6765\u5c31\u4e0d\u5927\u559c\u6b22\u4e0b\u68cb\u7684\u3002\u201d\u97e9\u5927\u7ef4\u9053\uff1a\u201c\u53ef\u662f\u4f69\u745b\u6bcf\u5929\u90fd\u548c \u5979\u6765\u7684\u5462\uff0c\u5947\u602a\uff0c\u4eca\u5929\u600e\u4e48\u4e0d\u89c1\u5979\u4eec\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u8fd9\u624d\u7740\u4e86\u614c\uff0c\u8bf4\u9053\uff1a\u201c\u6216\u8bb8\u4ed6\u4eec\u4eca\u5929\u662f\u4eb2\u81ea\u4e0b\u53a8\uff0c\u5fd9\u7740\u7ed9\u54b1\u4eec\u5f04\u996d\u5427\uff1f\u201d \u3000\u3000\u4e24\u4eba\u8fde\u5fd9\u8fdb\u53bb\u4e00\u770b\uff0c\u54ea\u91cc\u627e\u5f97\u7740\u5979\u4eec\uff1f\u97e9\u5927\u7ef4\u5bb6\u91cc\u7684\u4f63\u4eba\u53ea\u6709\u4e00\u4e2a\u53a8\u5b50\uff0c\u4e00\u4e2a\u8001\u738b\uff0c\u8fd9 \u4e24\u4e2a\u4eba\u662f\u4e0d\u8ff8\u5185\u5802\u7684\uff0c\u5f53\u7136\u4e5f\u4e0d\u77e5\u9053\u5c0f\u59d0\u7684\u53bb\u5411\u3002 \u3000\u3000\u97e9\u5927\u7ef4\u5b89\u6170\u674e\u601d\u5357\u9053\uff1a\u201c\u60f3\u5fc5\u662f\u5c0f\u5973\u966a\u6768\u59d1\u5a18\u51fa\u53bb\u73a9\u4e86\uff0c\u5979\u5e73\u65e5\u4e5f\u662f\u5e38\u5e38\u51fa\u53bb\u7684\u3002\u6211\u60f3 \u5927\u7ea6\u662f\u4e0d\u4f1a\u51fa\u4e8b\u7684\u3002\u54b1\u4eec\u8fdf\u4e00\u4e9b\u5403\u996d\u3002\u201d\u4ed6\u8bdd\u662f\u8fd9\u6837\u8bf4\uff0c\u5fc3\u91cc\u53ef\u4e5f\u614c\u4e86\u3002 \u3000\u3000\u97e9\u4f69\u745b\u548c\u6768\u5a49\u5230\u4e86\u54ea\u91cc\u5462\uff1f \u3000\u3000\u6768\u5a49\u548c\u97e9\u4f69\u745b\u8d70\u4e0a\u5927\u8857\uff0c\u53ea\u89c1\u8f66\u5982\u6d41\u6c34\u9a6c\u5982\u9f99\uff0c\u7aef\u7684\u662f\u597d\u4e00\u756a\u70ed\u95f9\u666f\u8c61\u3002\u5728\u90a3\u7a7f\u68ad\u6765\u5f80 \u7684\u884c\u4eba\u4e4b\u4e2d\uff0c\u7eff\u5973\u7ea2\u7537\uff0c\u9ec4\u7ae5\u767d\u91cc\uff0c\u5404\u5f0f\u5404\u6837\u7684\u4eba\uff0c\u5e94\u6709\u5c3d\u6709\u3002\u539f\u6765\u5728\u91d1\u56fd\u7edf\u6cbb\u4e0b\u7684\u5927\u90fd\uff0c \u5987\u5973\u201c\u629b\u5934\u9732\u9762\u201d\u662f\u5e76\u4e0d\u5f53\u4f5c\u4e00\u56de\u4e8b\u7684\uff0c\u4e0d\u4f3c\u5357\u5b8b\uff0c\u5987\u5973\u5fc5\u987b\u8c2c\u5b88\u201c\u793c\u6559\u201d\uff0c\u4ee5\u201c\u8db3\u8ff9\u4e0d\u51fa \u95fa\u95e8\u201d\uff0c\u65b9\u624d\u7b97\u5f97\u662f\u6709\u6559\u517b\u7684\u201c\u826f\u5bb6\u5987\u5973\u201d\u3002\u6768\u5a49\u770b\u89c1\u884c\u4eba\u4e2d\u6709\u7537\u6709\u5973\uff0c\u4e5f\u6ca1\u53d1\u89c9\u6709\u4eba\u7279\u522b \u6ce8\u610f\u5979\uff0c\u65b9\u59cb\u653e\u4e0b\u4e86\u5fc3\u3002 \u3000\u3000\u97e9\u4f69\u745b\u5e26\u6768\u5a49\u5728\u7687\u57ce\u5916\u9762\u8d70\u4e86\u4e00\u5708\uff0c\u8ba9\u5979\u89c1\u8bc6\u7528\u91d1\u78a7\u8f89\u714c\u7684\u7409\u7483\u74e6\u6240\u76d6\u7684\u5bab\u6bbf\uff0c\u7136\u540e\u5230 \u57ce\u4e2d\u51e0\u5904\u98ce\u666f\u540d\u80dc\u4e4b\u5730\u6e38\u89c8\uff0c\u770b\u770b\u65e5\u5934\u8fc7\u5348\uff0c\u6768\u5a49\u6050\u6015\u674e\u601d\u5357\u6302\u5ff5\uff0c\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u8be5\u56de\u53bb\u4e86 \u5427\uff1f\u201d \u3000\u3000\u97e9\u4f69\u745b\u6e38\u5174\u672a\u9611\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u6025\u4ec0\u4e48\uff0c\u597d\u4e0d\u5bb9\u6613\u51fa\u6765\u4e00\u8d9f\uff0c\u8981\u73a9\u5c31\u73a9\u4e2a\u75db\u5feb\u3002\u5927\u90fd\u6700\u70ed \u95f9\u7684\u5730\u65b9\uff0c\u4f60\u8fd8\u6ca1\u6709\u53bb\u5462\u3002\u201d\u6768\u5a49\u9053\uff1a\u201c\u90a3\u662f\u4ec0\u4e48\u5730\u65b9\uff1f\u201d \u3000\u3000\u97e9\u4f69\u745b\u9053\uff1a\u201c\u90a3\u5730\u65b9\u540d\u53eb\u5929\u6865\uff0c\u6709\u4e00\u5927\u7247\u5e7f\u573a\uff0c\u573a\u4e2d\u6709\u5404\u5f0f\u5404\u6837\u7684\u6742\u800d\uff0c\u65e0\u6570\u5404\u5177\u7279\u8272 \u7684\u5c0f\u98df\u644a\u5b50\uff0c\u8fd8\u6709\u4f60\u6240\u60f3\u4e70\u7684\u80ed\u8102\u6c34\u7c89\u96d5\u523b\u65b9\u73a9\u4e4b\u7c7b\u7684\u6742\u8d27\uff0c\u751a\u81f3\u8fd8\u53ef\u4ee5\u7528\u5f88\u4fbf\u5b9c\u7684\u4ef7\u94b1\u4e70 \u5230\u4e3b\u4eba\u7684\u5b57\u753b\uff0c\u5176\u4e2d\u4e0d\u4e4f\u540d\u5bb6\u7684\u771f\u8ff9\uff0c\u5f53\u7136\u8fd9\u5c31\u8981\u770b\u673a\u7f18\u4e86\u3002\u201d \u3000\u3000\u6768\u5a49\u7ed9\u5979\u8bf4\u5f97\u6026\u7136\u5fc3\u52a8\uff0c\u60f3\u9053\uff1a\u201c\u65e5\u843d\u4e4b\u524d\u8d76\u56de\u53bb\uff0c\u60f3\u6765\u4ed6\u4eec\u7684\u68cb\u8fd8\u672a\u4e0b\u5b8c\u3002\u201d\u4fbf\u9053\uff1a \u201c\u4e5f\u597d\uff0c\u90a3\u4e48\u54b1\u4eec\u5c31\u53bb\u770b\u770b\u3002\u201d \u3000\u3000\u5230\u4e86\u5929\u6865\uff0c\u6768\u5a49\u4e70\u4e86\u51e0\u4ef6\u4e66\u623f\u7684\u5c0f\u6446\u8bbe\uff0c\u51c6\u5907\u5e26\u56de\u53bb\u9001\u7ed9\u674e\u601d\u5357\uff0c\u5ffd\u542c\u5f97\u9523\u58f0\u94db\u94db\uff0c\u539f \u6765\u662f\u6709\u4e24\u7236\u5973\u5728\u5979\u4eec\u7684\u9644\u8fd1\u5360\u4e86\u4e00\u4e2a\u573a\u5b50\u5356\u827a\uff0c\u521a\u521a\u5f00\u573a\u3002\u97e9\u4f69\u745b\u6027\u559c\u70ed\u95f9\uff0c\u770b\u4e86\u4e00\u773c\uff0c\u8bf4 \u9053\uff1a\u201c\u90a3\u5c0f\u59d1\u5a18\u4f3c\u4e4e\u6709\u70b9\u73a9\u827a\uff0c\u6837\u5b50\u4e5f\u957f\u5f97\u4e0d\u9519\uff0c\u54b1\u4eec\u8fc7\u53bb\u4ed4\u7ec6\u77a7\u77a7\u3002\u201d \u3000\u3000\u8fd9\u65f6\u5df2\u6709\u8bb8\u591a\u6e38\u4eba\u88ab\u5438\u5f15\u4e86\u6765\uff0c\u573a\u5b50\u7684\u5916\u9762\u56f4\u6210\u4e00\u4e2a\u5706\u5708\u4e86\u3002\u6768\u5a49\u76b1\u76b1\u7709\u5934\uff0c\u4f4e\u58f0\u8bf4 \u9053\uff1a\u201c\u548c\u90a3\u4e9b\u4eba\u6324\u5728\u4e00\u8d77\uff0c\u6211\u4e0d\u4e60\u60ef\u3002\u201d\u97e9\u4f69\u745b\u9053\uff1a\u201c\u597d\uff0c\u90a3\u54b1\u4eec\u5c31\u7ad9\u8fdc\u4e00\u70b9\u77a7\u3002\u201d \u3000\u3000\u6768\u5a49\u4e0d\u60f3\u592a\u8fc7\u62c2\u9006\u5979\u7684\u610f\u60f3\uff0c\u4fbf\u4e0e\u5979\u4e00\u540c\u8fc7\u53bb\u3002\u6b63\u884c\u8d70\u95f4\uff0c\u5ffd\u5730\u6709\u4e2a\u7537\u5b50\u659c\u523a\u649e\u6765\uff0c\u6768 \u5a49\u51b7\u4e0d\u53ca\u9632\uff0c\u7adf\u7136\u7ed9\u4ed6\u78b0\u7740\u3002 \u3000\u3000\u97e9\u4f69\u745b\u65a5\u9053\uff1a\u201c\u4f60\u8d70\u8def\u4e0d\u5e26\u773c\u5417\uff1f\u201d\u4e00\u638c\u5411\u4ed6\u63a8\u53bb\u3002\u4e0d\u6599\u65c1\u8fb9\u53c8\u6709\u4e00\u4e2a\u7537\u5b50\u7a81\u7136\u7a9c\u51fa\uff0c \u62e6\u5728\u5979\u7684\u9762\u524d\uff0c\u6325\u81c2\u62db\u67b6\uff0c\u97e9\u4f69\u745b\u90a3\u4e00\u638c\u63a8\u53bb\uff0c\u53ea\u89c9\u5f97\u4e00\u80a1\u52b2\u529b\u53cd\u63a8\u8fc7\u6765\u3002\u97e9\u4f69\u745b\u5403\u4e86\u4e00 \u60ca\uff0c\u60f3\u4e0d\u5230\u5728\u8fd9\u4e2a\u54c1\u6d41\u590d\u6742\u7684\u5730\u65b9\uff0c\u7adf\u7136\u78b0\u4e0a\u4e86\u4e00\u4e2a\u6b66\u529f\u9ad8\u624b\u3002 \u3000\u3000\u90a3\u4eba\u51b7\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u5730\u65b9\uff0c\u8c01\u90fd\u53ef\u4ee5\u6765\u73a9\uff0c\u4f60\u6015\u7ed9\u4eba\u8f7b\u8584\uff0c\u5c31\u522b\u51fa\u6765\uff01\u54fc\uff0c\u4f55\u51b5\u6b63\u4e3b\u513f \u90fd\u672a\u51fa\u58f0\uff0c\u8981\u4f60\u591a\u7ba1\u95f2\u4e8b\uff1f\u201d\u97e9\u4f69\u745b\u672c\u6765\u8fd8\u4ee5\u4e3a\u5148\u524d\u90a3\u4eba\u662f\u201c\u65e0\u5fc3\u4e4b\u5931\u201d\uff0c\u672a\u66fe\u60f3\u5230\u4ed6\u662f\u6709 \u5fc3\u8c03\u620f\u6768\u5a49\u7684\uff0c\u6b64\u65f6\u8fd9\u201c\u8f7b\u8584\u201d\u4e8c\u5b57\u4ece\u90a3\u4e2a\u4eba\u7684\u540c\u515a\u53e3\u4e2d\u8bf4\u51fa\uff0c\u97e9\u4f69\u745b\u65b9\u77e5\u4ed6\u4eec\u662f\u6709\u5fc3\u60f9 \u4e8b\uff0c\u4e0d\u89c9\u5927\u6012\u3002 \u3000\u3000\u6b63\u5f53\u97e9\u4f69\u745b\u53d1\u89c9\u90a3\u4eba\u662f\u4e2a\u6b66\u529f\u9ad8\u624b\uff0c\u5927\u5403\u4e00\u60ca\u4e4b\u9645\uff0c\u6768\u5a49\u4e5f\u8ba4\u51fa\u78b0\u649e\u5979\u7684\u90a3\u4e2a\u4eba\u662f\u8c01 \u4e86\uff0c\u6768\u5a49\u8fd9\u4e00\u60ca\u6bd4\u97e9\u4f69\u745b\u66f4\u751a\u3002 \u3000\u3000\u539f\u6765\u90a3\u4e2a\u4eba\u4e0d\u662f\u522b\u4eba\uff0c\u7adf\u662f\u8499\u53e4\u7684\u56db\u738b\u5b50\u62d6\u96f7\u3002\u4ed6\u6539\u6362\u4e86\u91d1\u56fd\u5e73\u6c11\u7684\u88c5\u675f\u3002 \u3000\u3000\u62d6\u96f7\u7b11\u563b\u563b\u7684\u4f5c\u63d6\u8bf4\u9053\uff1a\u201c\u6768\u59d1\u5a18\uff0c\u5e78\u4f1a\uff0c\u5e78\u4f1a\uff01\u60f3\u4e0d\u5230\u54b1\u4eec\u5728\u8fd9\u91cc\u53c8\u89c1\u7740\u4e86\u3002\u201d \u3000\u3000\u6768\u5a49\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u4f46\u5979\u4e5f\u662f\u4e2a\u673a\u7075\u7684\u4eba\uff0c\u4e00\u60ca\u4e4b\u540e\uff0c\u7acb\u5373\u60f3\u5230\uff1a\u201c\u62d6\u96f7\u8ba4\u51fa\u662f\u6211\uff0c \u5f53\u7136\u662f\u6709\u610f\u6765\u96be\u4e3a\u6211\u7684\u3002\u8981\u60f3\u8131\u9669\uff0c\u53ea\u6709\u5148\u4e0b\u624b\u4e3a\u5f3a\u4e86\u3002\u201d\u5979\u77e5\u9053\u62d6\u96f7\u867d\u7136\u7cbe\u5e72\u9a91\u5c04\uff0c\u6b66\u529f \u81ea\u5fd6\u8fd8\u53ef\u4ee5\u80dc\u5f97\u8fc7\u4ed6\uff0c\u5f53\u4e0b\u4e3b\u610f\u4e00\u5b9a\uff0c\u7acb\u5373\u4fbf\u662f\u4e00\u62db\u8fd1\u8eab\u7684\u5c0f\u64d2\u62ff\u624b\u6cd5\uff0c\u5411\u62d6\u96f7\u7684\u7435\u7436\u9aa8\u6293 \u4e0b\u3002 \u3000\u3000\u62d6\u96f7\u6b66\u529f\u4e0d\u53ca\u6768\u5a49\uff0c\u4e0d\u8fc7\u4ed6\u7684\u6454\u4ea4\u529f\u592b\uff0c\u5374\u662f\u5728\u8499\u53e4\u7684\u6b66\u58eb\u4e4b\u4e2d\u4e5f\u7b97\u5f97\u662f\u4e00\u540d\u9ad8\u624b\u7684\u3002 \u6768\u5a49\u4e00\u6293\u4e4b\u4e0b\uff0c\u62d6\u96f7\u4e00\u4e2a\u8e72\u8eab\u52fe\u817f\uff0c\u53f3\u638c\u75be\u51fa\uff0c\u53cd\u6263\u6768\u5a49\u7684\u624b\u8155\uff0c\u6768\u5a49\u7109\u80fd\u7ed9\u5979\u52fe\u8dcc\uff0c\u6ef4\u6e9c \u6e9c\u4e00\u4e2a\u8f6c\u8eab\uff0c\u638c\u950b\u659c\u63a0\uff0c\u53ea\u542c\u5f97\u201c\u55e4\u2019\u7684\u4e00\u58f0\uff0c\u62d6\u96f7\u7684\u80a9\u8863\u7ed9\u5979\u6495\u4e0b\u4e86\u4e00\u5e45\u3002\u53ef\u662f\u6768\u5a49\u60f3\u8981 \u6293\u788e\u4ed6\u7684\u7435\u7436\u9aa8\uff0c\u5374\u662f\u4e0d\u80fd\u5982\u613f\u4e86\u3002 \u3000\u3000\u62d6\u96f7\u9000\u51fa\u6570\u6b65\uff0c\u8bf4\u9053\uff1a\u201c\u6768\u59d1\u5a18\uff0c\u4f60\u8fd9\u6837\u5bf9\u5f85\u8001\u670b\u53cb\u672a\u514d\u592a\u8fc7\u8584\u60c5\u5427\uff1f\u201d\u628a\u624b\u4e00\u6325\uff0c\u53c8 \u4e00\u4e2a\u6c49\u5b50\u51b2\u4e86\u4e0a\u6765\uff0c\u7528\u8499\u53e4\u8bdd\u8bf4\u9053\uff1a\u201c\u8fd9\u4e2b\u5934\u4e0d\u8bc6\u62ac\u4e3e\uff0c\u56db\u6bbf\u4e0b\u8981\u5982\u4f55\u5bf9\u4ed8\u5979\uff0c\u8bf7\u5373\u5429\u5490\u5c0f \u4eba\u3002\u201d\u6768\u5a49\u4e00\u62db\u201c\u4e09\u73af\u5957\u6708\u201d\uff0c\u8fde\u73af\u8fdb\u638c\uff0c\u7ed9\u90a3\u4eba\u968f\u624b\u4e00\u62e8\uff0c\u7adf\u7136\u628a\u5979\u8fd9\u4e00\u62db\u62db\u91cc\u85cf\u62db\uff0c\u5f0f \u4e2d\u5957\u5f0f\u7684\u7cbe\u5999\u638c\u6cd5\uff0c\u8f7b\u63cf\u6de1\u5199\u5730\u5c31\u5316\u89e3\u5f00\u4e86\u3002 \u3000\u3000\u62d6\u96f7\u4fa7\u76ee\u659c\u89c6\uff0c\u76ef\u4e86\u6768\u5a49\u4e00\u773c\uff0c\u8bf4\u9053\uff1a\u201c\u6768\u59d1\u5a18\u867d\u7136\u4e0d\u591f\u670b\u53cb\uff0c\u6211\u53ef\u8fd8\u5f97\u987e\u5ff5\u6545\u4eba\u60c5 \u4e49\uff0c\u4f60\u66ff\u6211\u5c06\u5979\u64d2\u4e0b\uff0c\u53ef\u4e0d\u8bb8\u4f24\u4e86\u5979\uff01\u201d\u90a3\u4eba\u5e94\u4e86\u58f0\u201c\u662f\uff01\u201d\u5355\u638c\u5212\u4e86\u4e00\u9053\u5706\u5f27\uff0c\u7f13\u7f13\u63a8 \u51fa\u3002 \u3000\u3000\u6768\u5a49\u88ab\u90a3\u638c\u529b\u4e00\u538b\uff0c\u51e0\u4e4e\u900f\u4e0d\u8fc7\u6c14\u6765\uff0c\u60f3\u8981\u9003\u8dd1\uff0c\u4e0d\u8bba\u8d70\u5411\u4f55\u65b9\uff0c\u5982\u53c8\u90fd\u662f\u7ed9\u4ed6\u7684\u638c\u529b \u5c01\u4f4f\u3002\u6768\u5a49\u672c\u6765\u662f\u7cbe\u4e8e\u4f7f\u5200\u4f7f\u5251\uff0c\u4e0d\u5927\u7cbe\u4e8e\u638c\u529b\uff0c\u52a0\u4ee5\u529f\u529b\u53c8\u6bd4\u4e0d\u4e0a\u5bf9\u65b9\uff0c\u662f\u4ee5\u4ea4\u624b\u4e0d\u8fc7\u6570 \u62db\uff0c\u5df2\u662f\u96be\u8131\u56f0\u5883\u3002 \u3000\u3000\u539f\u6765\u548c\u6768\u5a49\u3001\u97e9\u4f69\u745b\u4ea4\u624b\u7684\u8fd9\u4e24\u4e2a\u6c49\u5b50\uff0c\u5c31\u662f\u891a\u4e91\u5cf0\u548c\u8c37\u6db5\u865a\u5728\u5317\u8292\u5c71\u5218\u5bb6\u6240\u906d\u9047\u7684\u90a3 \u4e24\u4e2a\u8499\u53e4\u6b66\u58eb\u3002\u6768\u5a49\u7684\u5bf9\u624b\u662f\u547c\u9ece\u5962\uff0c\u97e9\u4f69\u745b\u7684\u5bf9\u624b\u662f\u963f\u535c\u5362\u3002 \u3000\u3000\u547c\u3001\u963f\u4e8c\u4eba\u4e43\u662f\u9f99\u8c61\u6cd5\u738b\u7684\u5f97\u610f\u5f1f\u5b50\uff0c\u5f53\u65e5\u4ed6\u4eec\u66fe\u7ecf\u7528\u8fc7\u201c\u9f99\u8c61\u529f\u201d\u62b5\u654c\u891a\u3001\u8c37\u4e8c\u4eba\u7684 \u201c\u5929\u96f7\u529f\u201d\uff0c\u867d\u662f\u7a0d\u900a\u4e00\u7b79\uff0c\u4f46\u6bd4\u8d77\u6768\u5a49\u548c\u97e9\u4f69\u745b\u5374\u662f\u80dc\u5f97\u591a\u4e86\u3002 \u3000\u3000\u6b64\u65f6\u97e9\u4f69\u745b\u548c\u963f\u535c\u5362\u4ea6\u5df2\u5c55\u5f00\u6fc0\u6597\uff0c\u97e9\u4f69\u745b\u81ea\u5e7c\u884c\u8d70\u6c5f\u6e56\uff0c\u4e34\u654c\u7684\u7ecf\u9a8c\u6bd4\u6768\u5a49\u4e30\u5bcc\uff0c\u53d8 \u62db\u8fc5\u901f\uff0c\u8eab\u6cd5\u8f7b\u7075\uff0c\u6545\u6b64\u6bd4\u8f83\u8d77\u6765\uff0c\u4e0d\u4f3c\u6768\u5a49\u5403\u4e8f\u4e4b\u5927\u3002\u4f46\u56e0\u5979\u4e5f\u662f\u957f\u4e8e\u5251\u672f\uff0c\u62f3\u811a\u7684\u529f\u592b \u4e0d\u5927\u9ad8\u660e\uff0c\u6545\u6b64\u5728\u963f\u535c\u5362\u5f3a\u653b\u4e4b\u4e0b\uff01\u5979\u4e5f\u662f\u53ea\u6709\u62db\u67b6\u4e4b\u529f\uff0c\u5e76\u65e0\u8fd8\u624b\u4e4b\u529b\u3002 \u3000\u3000\u56f4\u5728\u90a3\u4e2a\u573a\u5b50\u5468\u56f4\u7684\u95f2\u4eba\uff0c\u521d\u65f6\u770b\u89c1\u4e24\u4e2a\u5927\u59d1\u5a18\u548c\u4eba\u6253\u67b6\uff0c\u89c9\u5f97\u5f88\u662f\u6709\u8da3\u3002\u4e0d\u6599\u5728\u4ed6\u4eec \u7684\u6076\u6597\u5c55\u5f00\u4e4b\u540e\uff0c\u62f3\u98ce\u864e\u864e\uff0c\u522e\u9762\u751f\u75db\uff0c\u6709\u51e0\u4e2a\u9760\u5f97\u8f83\u8fd1\u7684\u95f2\u4eba\u7adf\u7ed9\u9707\u5012\u5730\u4e0a\uff0c\u53d8\u4f5c\u4e86\u6eda\u5730 \u846b\u82a6\uff0c\u4f17\u4eba\u8fd9\u624d\u77e5\u5389\u5bb3\uff0c\u4e00\u54c4\u800c\u6563\u3002\u6709\u4eba\u53eb\u9053\uff1a\u201c\u522b\u95f9\u51fa\u4eba\u547d\u6765\uff0c\u5feb\u7980\u5b98\u5e9c\u3002\u201d \u3000\u3000\u97e9\u4f69\u745b\u767e\u5fd9\u4e2d\u62bd\u773c\u4e00\u77a5\uff1a\u89c1\u573a\u4e2d\u90a3\u5bf9\u7236\u5973\u6b63\u5728\u6536\u62fe\u5bb6\u4f19\uff0c\u5c1a\u672a\u8d70\u5f00\u3002\u97e9\u4f69\u745b\u5fc3\u5ff5\u4e00\u52a8\uff0c \u767b\u65f6\u4e00\u4e2a\u201c\u7ec6\u80f8\u5de7\u7ffb\u4e91\u201d\uff0c\u5012\u7eb5\u51fa\u6570\u4e08\u5f00\u5916\uff0c\u521a\u521a\u843d\u5728\u573a\u5fc3\u3002\u5979\u8fd9\u4e00\u62db\u8f7b\u529f\u8eab\u6cd5\u4f7f\u5f97\u5de7\u5999\u4e4b \u6781\uff0c\u963f\u535c\u5362\u7adf\u7136\u672a\u80fd\u53ca\u65f6\u963b\u6b62\u3002 \u3000\u3000\u97e9\u4f69\u745b\u843d\u5728\u573a\u5fc3\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u5927\u59d0\uff0c\u501f\u4f60\u8fd9\u4e24\u628a\u5200\u4e00\u7528\u3002\u201d\u90a3\u4e2a\u5c0f\u59d1\u5a18\u521a\u624d\u5356\u827a\uff0c\u521a\u521a \u4f7f\u4e86\u4e00\u5957\u5200\u6cd5\uff0c\u5979\u4f7f\u7684\u662f\u4e00\u957f\u4e00\u77ed\u7684\u67f3\u53f6\u5200\uff0c\u6b64\u65f6\u5200\u8fd8\u672a\u66fe\u5165\u9798\uff0c\u6401\u5728\u7bb1\u4e0a\u3002 \u3000\u3000\u97e9\u4f69\u745b\u4ee5\u8fc5\u6377\u5f02\u5e38\u7684\u624b\u6cd5\uff0c\u62ff\u8d77\u53cc\u5200\uff0c\u547c\u547c\u4e24\u5200\uff0c\u5411\u9053\u4e2d\u573a\u5b50\u7684\u963f\u535c\u5362\u65ab\u53bb\u3002\u97e9\u5bb6\u7684 \u201c\u60ca\u795e\u5251\u6cd5\u201d\u4e43\u662f\u6b66\u6797\u4e00\u7edd\uff0c\u97e9\u4f69\u745b\u4f7f\u5200\u867d\u7136\u4e0d\u5927\u987a\u624b\uff0c\u4f46\u5979\u628a\u201c\u60ca\u795e\u5251\u6cd5\u201d\u5316\u4e3a\u5200\u6cd5\uff0c\u62db \u6570\u4e4b\u51cc\u5389\u4ea6\u662f\u975e\u6bd4\u5bfb\u5e38\uff0c\u963f\u535c\u5362\u5fc3\u5934\u4e00\u51db\uff0c\u4e0d\u6562\u8e81\u8fdb\u3002\u97e9\u4f69\u745b\u53eb\u9053\uff1a\u201c\u5a49\u59d0\uff0c\u63a5\u5200\uff01\u201d\u4e00\u4e2a \u76d8\u5934\u75be\u821e\uff0c\u957f\u5200\u98de\u51fa\uff0c\u6768\u5a49\u773c\u660e\u624b\u5feb\uff0c\u63a5\u5230\u624b\u4e2d\u3002 \u3000\u3000\u6768\u5a49\u672c\u6765\u662f\u5200\u5251\u4e24\u4ff1\u64c5\u957f\u7684\uff0c\u6709\u4e86\u957f\u5200\u5728\u624b\uff0c\u4f7f\u51fa\u4e86\u5bb6\u4f20\u7684\u201c\u6768\u4ee4\u516c\u516d\u5341\u56db\u8def\u91d1\u5200\u5200 \u6cd5\u201d\uff0c\u4e00\u56e2\u5200\u5149\uff0c\u62a4\u7740\u8eab\u8eaf\uff0c\u6cfc\u6c34\u4e0d\u8fdb\uff0c\u547c\u9ece\u5962\u4e0d\u6562\u7528\u201c\u9f99\u8c61\u529f\u201d\u4f24\u5979\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u7adf\u662f\u5948 \u5979\u4e0d\u4f55\u3002 \u3000\u3000\u97e9\u4f69\u745b\u53eb\u9053\uff1a\u201c\u5a49\u59d0\uff0c\u54b1\u4eec\u56de\u53bb\u518d\u8bf4\u3002\u201d\u6768\u5a49\u970d\u7136\u4e00\u7701\uff0c\u60f3\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4eca\u65e5\u4e0d\u8bba\u80dc \u8d25\uff0c\u90fd\u662f\u5bf9\u6211\u6ca1\u6709\u597d\u5904\uff0c\u4f55\u5fc5\u604b\u6218\uff1f\u201d\u4e8e\u662f\u865a\u5288\u4e24\u5200\uff0c\u4fbf\u5373\u8f6c\u8eab\u3002 \u3000\u3000\u53ef\u662f\u5bf9\u65b9\u672c\u9886\u5728\u5979\u4e4b\u4e0a\uff0c\u5979\u8981\u7acb\u5373\u8131\u8eab\uff0c\u8c08\u4f55\u5bb9\u6613\uff1f\u53ea\u80fd\u4e14\u6218\u4e14\u8d70\uff01 \u3000\u3000\u5ffd\u89c1\u4e00\u5c0f\u961f\u91d1\u56fd\u7684\u58eb\u5175\u8dd1\u6765\uff0c\u4e3a\u9996\u7684\u519b\u5b98\u559d\u9053\uff1a\u201c\u662f\u8c01\u6562\u5728\u5929\u5b50\u811a\u4e0b\u95f9\u4e8b\uff1f\u90fd\u7ed9\u6211\u5230\u4e5d \u95e8\u63d0\u7763\u5b98\u8859\u6295\u6848\uff01\u201d \u3000\u3000\u539f\u6765\u91d1\u56fd\u7684\u5175\u5236\uff0c\u5728\u4eac\u5e08\u8bbe\u6709\u4e5d\u4e2a\u5de1\u9632\u8425\uff0c\u5206\u9a7b\u4e5d\u95e8\uff0c\u5f52\u4e5d\u95e8\u63d0\u7763\u7ba1\u8f96\u3002\u8fd9\u4e00\u5c0f\u961f\u58eb\u5175 \u4e43\u662f\u9a7b\u5728\u6700\u8fd1\u65e0\u6865\u7684\u4e00\u5ea7\u57ce\u95e8\uff0c\u542c\u5f97\u8fd9\u8fb9\u6709\u4eba\u201c\u95f9\u4e8b\u201d\uff0c\u5306\u5306\u8d76\u6765\u7684\u3002 \u3000\u3000\u62d6\u96f7\u4e0d\u614c\u4e0d\u5fd9\u4e0a\u524d\u4e0e\u90a3\u519b\u5b98\u62db\u547c\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u5973\u5b50\u662f\u4ece\u8499\u53e4\u9003\u51fa\u6765\u7684\u6c49\u4eba\uff0c\u6211\u662f\u8499 \u53e4\u94a6\u4f7f\u6728\u534e\u9ece\u5c06\u519b\u7684\u968f\u4ece\u3002\u201d \u3000\u3000\u90a3\u519b\u5b98\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u6b64\u8bdd\u5f53\u771f\uff1f\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u53ea\u89c1\u56db\u540d\u8499\u53e4\u6b66\u58eb\u548c\u4e00\u4e2a\u80a5\u5934\u80d6\u8033 \u7684\u5927\u548c\u5c1a\u6765\u5230\uff0c\u90a3\u548c\u5c1a\u5411\u62d6\u96f7\u5531\u4e86\u4e2a\u8bfa\uff0c\u56db\u540d\u6b66\u571f\u5219\u662f\u4e00\u9f50\u8eac\u8170\u884c\u793c\u3002\u8fd9\u519b\u5b98\u4e0d\u8ba4\u5f97\u62d6\u96f7\uff0c \u4f46\u5374\u77e5\u9053\u8fd9\u4e2a\u548c\u5c1a\u662f\u8499\u53e4\u7684\u56fd\u5e08\u9f99\u8c61\u6cd5\u738b\uff0c\u5f53\u4e0b\u54ea\u91cc\u8fd8\u6562\u591a\u4e8b\uff0c\u53cd\u800c\u6307\u6325\u5175\u58eb\u5e2e\u5fd9\u62d6\u96f7\u5835\u622a \u9003\u4eba\u3002\u62d6\u96f7\u8bf4\u9053\uff1a\u201c\u4e0d\u7528\u4f60\u4eec\u52a8\u624b\uff0c\u53ea\u8981\u4f60\u4eec\u4e0d\u7ed9\u8fd9\u4e24\u4e2a\u4e2b\u5934\u9003\u51fa\u53bb\u5c31\u6210\u4e86\u3002\u201d \u3000\u3000\u9f99\u8c61\u6cd5\u738b\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8981\u64d2\u8fd9\u4e24\u4e2a\u4e2b\u5934\u8fd8\u4e0d\u5bb9\u6613\uff0c\u4f60\u4eec\u9000\u4e0b\uff01\u201d \u3000\u3000\u547c\u9ece\u5962\u3001\u963f\u535c\u5362\u77e5\u9053\u5e08\u7236\u8981\u4f7f\u201c\u9f99\u8c61\u529f\u201d\uff0c\u614c\u5fd9\u8eb2\u5f00\uff0c\u9f99\u8c61\u6cd5\u738b\u4e00\u638c\u62cd\u51fa\uff0c\u8ddd\u79bb\u4e09\u4e08\u5f00 \u5916\uff0c\u638c\u529b\u5df2\u662f\u8fbe\u5230\u6768\u5a49\u548c\u97e9\u4f69\u745b\u7684\u8eab\u4e0a\u3002 \u3000\u3000\u9f99\u8c61\u6cd5\u738b\u7684\u201c\u9f99\u8c61\u529f\u201d\u65e9\u5df2\u7ec3\u5230\u6536\u53d1\u968f\u5fc3\uff0c\u7089\u706b\u7eaf\u9752\u7684\u5883\u754c\uff0c\u8fd9\u4e00\u638c\u7684\u529b\u9002\u5f53\u771f\u662f\u4f7f\u5f97 \u6070\u5230\u597d\u5904\uff0c\u6768\u3001\u97e9\u4e8c\u5973\u53ea\u89c9\u4e00\u9635\u9178\u9ebb\uff0c\u767b\u65f6\u762b\u5728\u5730\u4e0a\uff0c\u8eab\u4f53\u5e76\u6ca1\u53d7\u4f24\uff0c\u53ef\u662f\u5374\u4e0d\u80fd\u52a8\u5f39\u4e86\u3002 \u3000\u3000\u62d6\u96f7\u628a\u4e24\u540d\u6b66\u58eb\u53eb\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u597d\u670d\u4f8d\u8fd9\u4e24\u4f4d\u59d1\u5a18\uff0c\u4e0d\u53ef\u65e0\u793c\uff01\u201d\u8fd9\u4e24\u540d\u6b66\u58eb\u5c06\u6768\u5a49 \u548c\u97e9\u4f69\u745b\u62c9\u4e0a\u4e00\u8f86\u9a6c\u8f66\uff0c\u9a70\u56de\u9633\u5929\u96f7\u7684\u201c\u56fd\u5e08\u5e9c\u201d\u3002 \u3000\u3000\u5230\u4e86\u201c\u56fd\u5e08\u5e9c\u201d\uff0c\u62d6\u96f7\u8eac\u8170\u8bf4\u9053\uff1a\u201c\u6768\u59d1\u5a18\u4e0d\u7528\u5bb3\u6015\uff0c\u6211\u53ea\u662f\u60f3\u8bf7\u4f60\u4f5c\u6211\u7684\u5ba2\u4eba\uff01\u201d \u3000\u3000\u6768\u5a49\u65a5\u9053\uff1a\u201c\u8c01\u8981\u505a\u4f60\u7684\u5ba2\u4eba\uff0c\u4f60\u83ab\u4ee5\u4e3a\u6211\u4eec\u6c49\u4eba\u662f\u597d\u6b3a\u8d1f\u7684\uff0c\u5927\u4e0d\u4e86\u662f\u4e00\u4e2a\u6b7b\uff0c\u4f60\u60f3 \u600e\u6837\uff1f\u201d \u3000\u3000\u62d6\u96f7\u7b11\u9053\uff1a\u201c\u674e\u601d\u5357\u548c\u6211\u662f\u4ea4\u6362\u4e86\u2018\u54c8\u8fbe\u2019\u7684\u5144\u5f1f\u4e4b\u4ea4\uff0c\u6211\u600e\u80fd\u6b3a\u8d1f\u4f60\u5462\uff1f\u4f60\u653e\u5fc3\u5728 \u8fd9\uff0c\u5728\u8fd9\u4f4f\u51e0\u5929\u5427\u3002\u4f60\u82e5\u662f\u8fd8\u4e0d\u653e\u5fc3\uff0c\u6211\u53ef\u4ee5\u2014\u2014\u201d\u8bf4\u81f3\u6b64\u5904\uff0c\u7a81\u7136\u4f38\u624b\u62d4\u4e0b\u4e86\u6768\u5a49\u5934\u4e0a\u7684 \u7389\u7c2a\uff0c\u6768\u5a49\u5403\u4e86\u4e00\u60ca\uff0c\u5931\u58f0\u53eb\u9053\uff1a\u201c\u4f60\u5e72\u4ec0\u4e48\uff1f\u201d\u5979\u672c\u6765\u6253\u7b97\u62d6\u96f7\u82e5\u662f\u5bf9\u5979\u65e0\u793c\u7684\u8bdd\uff0c\u5979\u7acb \u5373\u5c31\u81ea\u65ad\u7ecf\u8109\u800c\u4ea1\u7684\u3002\u4f46\u56e0\u5979\u5185\u529f\u672a\u66fe\u6062\u590d\uff0c\u6b63\u5f85\u8fd0\u529f\uff0c\u5fc3\u5ff5\u65b9\u52a8\u4e4b\u9645\uff0c\u62d6\u96f7\u5df2\u7ecf\u628a\u624b\u7f29 \u56de\uff0c\u7b11\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\uff0c\u6211\u53ea\u662f\u6050\u6015\u4f60\u4e0d\u653e\u5fc3\uff0c\u6240\u4ee5\u60f3\u8bf7\u601d\u5357\u5144\u4e5f\u6765\u6b64\u95f4\u548c\u4f60\u4f5c\u4f34\u3002\u201d \u3000\u3000\u4e14\u8bf4\u97e9\u5927\u7ef4\u548c\u674e\u601d\u5357\u7b49\u5230\u5929\u9ed1\uff0c\u4ecd\u7136\u4e0d\u89c1\u5979\u4eec\u56de\u6765\uff0c\u5fc3\u91cc\u90fd\u662f\u6709\u70b9\u614c\u4e86\u3002\u97e9\u5927\u7ef4\u8bf4\u9053\uff1a \u201c\u6211\u5230\u4e10\u5e2e\u5206\u8235\u8bf7\u9646\u5e2e\u4e3b\u5e2e\u5fd9\u5bfb\u627e\uff0c\u4ed6\u4eec\u4e10\u5e2e\u7684\u6d88\u606f\u6700\u4e3a\u7075\u901a\uff0c\u5c31\u662f\u627e\u4e0d\u7740\u4e5f\u603b\u4f1a\u5bfb\u5230\u4e00\u70b9 \u7ebf\u7d22\u7684\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u72ec\u81ea\u7559\u5728\u97e9\u5bb6\u7b49\u5019\uff0c\u5230\u4e86\u4e8c\u66f4\u65f6\u5206\uff0c\u4e0d\u4f46\u6768\u5a49\u4e0e\u97e9\u4f69\u745b\u672a\u89c1\u56de\u6765\uff0c\u97e9\u5927\u7ef4\u4e5f\u6ca1\u6709 \u56de\u6765\u3002 \u3000\u3000\u674e\u601d\u5357\u5fc3\u70e6\u610f\u4e71\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u5a49\u59b9\u662f\u5df2\u7ecf\u6539\u5bb9\u6613\u8c8c\u4e86\u7684\uff0c\u800c\u4e14\u90a3\u4f4d\u97e9\u59d1\u5a18\u7684\u672c\u9886\u4ea6\u662f \u975e\u6bd4\u5bfb\u5e38\uff0c\u8be5\u4e0d\u81f3\u4e8e\u906d\u53d7\u610f\u5916\u5427\uff1f\u83ab\u975e\u662f\u8ff7\u8def\u4e86\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u5728\u623f\u4e2d\u8d70\u6765\u8d70\u53bb\uff0c\u4e0d\u77e5\u4e0d\u89c9\uff0c\u53ea\u89c9\u5f97\u8857\u4e0a\u503c\u591c\u7684\u66f4\u592b\u53eb\u51fb\u66f4\u4e4b\u58f0\uff0c\u5df2\u7ecf\u662f\u4e09\u66f4\u65f6 \u5206\u4e86\u3002\u674e\u601d\u5357\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4e10\u5e2e\u5206\u8235\u79bb\u6b64\u4e0d\u8fdc\uff0c\u97e9\u8001\u524d\u8f88\u5e94\u8be5\u56de\u6765\u4e86\uff0c\u600e\u7684\u5374\u4e5f\u8fd8\u4e0d\u89c1\u56de \u6765\uff1f\u201d\u60f3\u5230\u4e10\u5e2e\u6253\u542c\u6d88\u606f\uff0c\u53c8\u6015\u6768\u5a49\u56de\u6765\uff0c\u627e\u4e0d\u7740\u4ed6\uff0c\u4e07\u4e00\u5979\u4eec\u662f\u906d\u9047\u610f\u5916\uff0c\u53d7\u4f24\u5f52\u6765\u7684 \u8bdd\uff0c\u65e0\u4eba\u7167\u6599\u3002 \u3000\u3000\u6b63\u81ea\u5fc3\u4e71\u5982\u9ebb\uff0c\u5ffd\u89c1\u7a97\u5916\u4eba\u5f71\u4e00\u95ea\uff0c\u674e\u601d\u5357\u5927\u559c\u53eb\u9053\uff1a\u201c\u97e9\u8001\u524d\u8f88\uff0c\u4f60\u56de\u6765\u5566\uff1f\u201d\u5ffd\u542c \u5f97\u201c\u556a\u201d\u7684\u4e00\u58f0\uff0c\u4e00\u652f\u7eff\u6676\u6676\u7684\u4e1c\u897f\u5c04\u8fdb\u7a97\u6237\uff0c\u63d2\u5728\u51e0\u4e0a\uff0c\u674e\u601d\u5357\u5b9a\u775b\u4e00\u770b\uff0c\u8ba4\u5f97\u662f\u6768\u5a49\u6240 \u4f69\u7684\u7389\u7c2a\u3002 \u3000\u3000\u674e\u601d\u5357\u5403\u4e86\u4e00\u60ca\uff0c\u559d\u9053\uff1a\u201c\u6765\u8005\u4f55\u4eba\uff1f\u201d\u5916\u9762\u4e00\u4eba\u5e94\u9053\uff1a\u201c\u6211\u4eec\u662f\u7ed9\u6768\u59d1\u5a18\u62a5\u8baf\u6765\u7684\uff0c \u8bf7\u51fa\u6765\u5427\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u62d4\u5251\u51fa\u9798\uff0c\u821e\u5251\u9632\u8eab\uff0c\u4ece\u7a97\u53e3\u8df3\u51fa\uff0c\u53ea\u89c1\u82b1\u4e1b\u4e2d\u5e76\u6392\u7ad9\u7740\u4e24\u4e2a\u9ed1\u8863\u6c49\u5b50\uff0c\u8fd9\u665a\u6708 \u8272\u6726\u80e7\uff0c\u770b\u5f97\u4e0d\u5927\u6e05\u695a\uff0c\u4f46\u5374\u4e5f\u77e5\u9053\u662f\u4ece\u672a\u89c1\u8fc7\u7684\u964c\u751f\u4eba\u3002 \u3000\u3000\u5176\u4e2d\u4e00\u4eba\u9053\uff1a\u201c\u674e\u516c\u5b50\u4e0d\u5fc5\u591a\u7591\uff0c\u8bf7\u8ddf\u6211\u4eec\u8d70\u5427\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u63d2\u5251\u5165\u9798\uff0c\u8bf4\u9053\uff1a\u201c\u6768\u59d1\u5a18\u5728\u54ea\u91cc\uff0c\u5979\u51fa\u4e86\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u90a3\u4eba\u8bf4\u9053\uff1a\u201c\u4f60\u8ddf\u6211\u4eec\u53bb\uff0c\u53bb\u89c1\u4e86\u5979\uff0c\u81ea\u7136\u660e\u767d\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u4f60\u4eec\u4e0d\u53ef\u4ee5\u5148\u544a\u8bc9\u6211\uff1f\u201d \u3000\u3000\u53e6\u4e00\u4e2a\u4eba\u7684\u813e\u6c14\u4f3c\u4e4e\u6bd4\u4ed6\u7684\u540c\u4f34\u66b4\u8e81\u5f97\u591a\uff0c\u54fc\u4e86\u4e00\u58f0\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u674e\u516c\u5b50\uff0c\u4f60\u4fe1\u5f97\u8fc7 \u6211\u4eec\u5c31\u8bf7\u8ddf\u6765\uff0c\u6211\u4eec\u53ef\u6ca1\u6709\u529f\u592b\u548c\u4f60\u591a\u8bf4\uff01\u201d\u8bf4\u4e86\u8fd9\u8bdd\uff0c\u5c31\u4e0d\u518d\u7406\u776c\u674e\u601d\u5357\uff0c\u5f84\u81ea\u8dd1\u51fa\u56ed\u5b50 \u53bb\u4e86\u3002 \u3000\u3000\u674e\u601d\u5357\u672c\u6765\u662f\u4e2a\u80c6\u5927\u5fc3\u7ec6\u7684\u4eba\uff0c\u82e5\u5728\u5e73\u65f6\uff0c\u4ed6\u51b3\u4e0d\u4f1a\u8ddf\u4e24\u4e2a\u5f62\u8ff9\u53ef\u7591\u7684\u964c\u751f\u4eba\u8d70\u7684\uff0c\u4f46 \u5728\u6b64\u9645\u3001\u4ed6\u56e0\u4e3a\u7ed9\u6768\u5a49\u5931\u8e2a\u7684\u4e8b\u60c5\u5f04\u5f97\u795e\u8ff7\u610f\u4e71\uff0c\u5374\u662f\u65e0\u6687\u591a\u4f5c\u8003\u8651\u4e86\u3002 \u3000\u3000\u674e\u601d\u5357\u548c\u6768\u5a49\u4e0d\u77e5\u7ecf\u8fc7\u591a\u5c11\u98ce\u6ce2\u65b9\u5f97\u56e2\u5706\uff0c\u6709\u6b64\u7ebf\u7d22\u53ef\u4ee5\u627e\u5230\u6768\u5a49\uff0c\u4ed6\u7109\u80fd\u629b\u5f03\uff0c\u662f\u4ee5 \u4ed6\u53ea\u5f80\u597d\u5904\u7740\u60f3\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6211\u5728\u8499\u53e4\u7684\u65f6\u5019\uff0c\u53d7\u4e86\u4f59\u4e00\u4e2d\u4e4b\u9a97\uff0c\u5f53\u65f6\u4e5f\u662f\u5a49\u59b9\u7684\u54e5\u54e5\u628a \u6211\u5f15\u51fa\u53bb\uff0c\u6211\u624d\u80fd\u627e\u770b\u7239\u7239\u7684\u3002\u5f53\u65f6\u5979\u7684\u54e5\u54e5\u4e5f\u4e0d\u80af\u628a\u5176\u4e2d\u539f\u59d4\u5148\u544a\u8bc9\u6211\u3002\u201d\u5982\u6b64\u4e00\u60f3\uff0c\u81ea \u5df1\u66ff\u81ea\u5df1\u627e\u5230\u4e86\u4e00\u4e2a\u201c\u7406\u7531\u201d\uff0c\u5c31\u597d\u50cf\u9677\u5728\u6f29\u6da1\u7684\u4eba\u6293\u5230\u4e00\u6839\u7a3b\u8349\u4f3c\u7684\uff0c\u7adf\u7136\u4e0d\u518d\u601d\u91cf\uff0c\u5c31 \u8ddf\u90a3\u4e24\u4e2a\u4eba\u8d70\u4e86\u3002\u5c82\u77e5\u8fd9\u4e24\u4ef6\u4e8b\u60c5\uff0c\u8868\u9762\u76f8\u4f3c\uff0c\u5b9e\u8d28\u5374\u662f\u5927\u4e0d\u76f8\u540c\u3002\u8fd9\u4e24\u4e2a\u9ed1\u8863\u6c49\u5b50\u7684\u8f7b\u529f \u5f88\u662f\u4e0d\u5f31\uff0c\u674e\u601d\u5357\u7d27\u7d27\u8ddf\u5728\u4ed6\u4eec\u540e\u9762\uff0c\u8f6c\u8fc7\u8bb8\u591a\u6a2a\u8857\u5c0f\u5df7\uff0c\u5230\u4e86\u4e00\u5ea7\u5927\u5b85\u5b50\u7684\u540e\u56ed\u3002 \u3000\u3000\u6708\u8272\u6726\u80e7\u4e4b\u4e0b\uff0c\u674e\u601d\u5357\u62ac\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u5c4b\u9876\u91d1\u78a7\u8f89\u714c\uff0c\u539f\u6765\u662f\u7528\u7409\u7483\u74e6\u76d6\u7684\u3002\u91d1\u4eac\u89c4 \u77e9\uff0c\u53ea\u6709\u7687\u5bab\u6216\u8005\u738b\u4eb2\u56fd\u621a\u7684\u4eba\u5bb6\uff0c\u624d\u80fd\u7528\u7409\u7483\u74e6\u505a\u5c4b\u9876\u7684\uff0c\u4f46\u674e\u601d\u5357\u5374\u4e0d\u77e5\u9053\uff0c\u5fc3\u60f3\uff1a \u201c\u8fd9\u4e0d\u77e5\u662f\u4ec0\u4e48\u4eba\u5bb6\uff0c\u4f46\u770b\u8fd9\u6c14\u6d3e\uff0c\u5b9a\u662f\u975e\u5bcc\u5373\u8d35\u7684\u4e86\u3002\u5a49\u59b9\u600e\u7684\u4f1a\u5230\u8fd9\u91cc\u6765\u5462\uff1f\u201d \u3000\u3000\u5fc3\u5ff5\u65b9\u52a8\uff0c\u90a3\u4e24\u4e2a\u9ed1\u8863\u6c49\u5b50\u5df2\u7ecf\u8d8a\u8fc7\u56f4\u5899\uff0c\u8d70\u8fdb\u53bb\u4e86\u3002\u674e\u601d\u5357\u6b64\u9645\u867d\u7136\u5df2\u662f\u7a0d\u7a0d\u8d77\u4e86\u7591 \u5fc3\uff0c\u4f46\u62b1\u7740\u201c\u65e2\u6765\u4e4b\uff0c\u5219\u5b89\u4e4b\u201d\u7684\u5fc3\u7406\uff0c\u4e5f\u5c31\u8ddf\u4e86\u4ed6\u4eec\u8fdb\u53bb\u3002 \u3000\u3000\u56ed\u4e2d\u6709\u5ea7\u5b85\u5b50\uff0c\u7c89\u5899\u767e\u53e8\uff0c\u5bc6\u5e03\u6f20\u8521\uff0c\u4e24\u6247\u5927\u94c1\u95e8\u7d27\u7d27\u5173\u95ed\u3002\u674e\u601d\u5357\u770b\u89c1\u8fd9\u79cd\u60c5\u5f62\uff0c\u8d8a \u53d1\u8d77\u7591\u3002 \u3000\u3000\u90a3\u4e24\u4e2a\u9ed1\u8863\u4eba\u62cd\u4e86\u4e09\u4e0b\u624b\u638c\uff0c\u4e24\u957f\u4e00\u77ed\uff0c\u4e24\u6247\u94c1\u95e8\uff0c\u7f13\u7f13\u6253\u5f00\u3002\u90a3\u4e24\u4e2a\u9ed1\u8863\u4eba\u9053\uff1a \u201c\u597d\uff0c\u8bf7\u8fdb\u53bb\u5427\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4e0d\u5165\u864e\u7a74\uff0c\u7109\u5f97\u864e\u5b50\uff1f\u6211\u65e2\u7136\u6765\u5230\u8fd9\u91cc\uff0c\u5373\u4f7f\u4ed6\u4eec\u4e0d\u6000\u597d\u610f\uff0c\u6211\u4e5f \u662f\u975e\u8981\u770b\u4e2a\u7a76\u7adf\u4e0d\u53ef\u4e86\u3002\u201d \u3000\u3000\u90a3\u4e24\u4e2a\u9ed1\u8863\u6c49\u5b50\u524d\u9762\u5e26\u8def\uff0c\u8d70\u8fc7\u4e00\u6761\u957f\u957f\u7684\u51fd\u9053\uff0c\u548c\u674e\u601d\u5357\u8fdb\u5165\u4e00\u95f4\u623f\u5b50\uff0c\u90a3\u4e24\u4e2a\u9ed1\u8863 \u6c49\u5b50\u9053\uff1a\u201c\u8bf7\u674e\u516c\u5b50\u7a0d\u5750\u7247\u523b\uff0c\u6768\u59d1\u5a18\u5c31\u6765\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u534a\u4fe1\u534a\u7591\uff0c\u6000\u7740\u7b49\u5f85\u201c\u5947\u8ff9\u201d\u51fa\u73b0\u7684\u5e7b\u60f3\uff0c\u5750\u4e86\u4e00\u4f1a\uff0c\u5ffd\u542c\u5f97\u4e00\u4e2a\u719f\u6089\u7684\u58f0\u97f3\u8bf4 \u9053\uff1a\u201c\u601d\u5357\u5b89\u7b54\uff0c\u52b3\u4f60\u4e45\u5019\u4e86\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\u3002\u8fd9\u4e2a\u4eba\u4e0d\u662f\u522b\u4e2a\uff0c\u6b63\u662f\u62d6\u96f7\uff01\u8ddf\u7740\u62d6\u96f7\u8fdb\u6765\u7684\u8fd8\u6709\u4e00\u4e2a\u80a5\u5934\u80d6 \u8033\u7684\u548c\u5c1a\uff0c\u4e0d\u7528\u8bf4\u662f\u90a3\u4e2a\u8499\u53e4\u56fd\u5e08\u9f99\u8c61\u6cd5\u738b\u4e86\u3002 \u3000\u3000\u674e\u601d\u5357\u5e76\u4e0d\u7cca\u6d82\uff0c\u4e00\u89c1\u62d6\u96f7\uff0c\u5f53\u7136\u4e5f\u5c31\u77e5\u9053\u8fd9\u662f\u62d6\u96f7\u5b89\u6392\u4e0b\u7684\u9677\u9631\uff0c\u81ea\u5df1\u7c97\u5fc3\u5927\u610f\u4e0a\u4ed6 \u7684\u5f53\uff0c\u8dcc\u8fdb\u4ed6\u7684\u9677\u9631\u4e86\u3002\u5982\u679c\u53ea\u662f\u62d6\u96f7\u4e00\u4e2a\u4eba\u8fdb\u6765\u7684\u8bdd\uff0c\u674e\u601d\u5357\u53ef\u4ee5\u5c06\u4ed6\u62ff\u4f5c\u4eba\u8d28\uff0c\u5982\u4eca\u6709 \u9f99\u8c61\u6cd5\u738b\u966a\u7740\u4ed6\uff0c\u674e\u601d\u5357\u53ea\u597d\u6253\u6d88\u8fd9\u4e2a\u4f01\u56fe\uff0c\u9759\u89c2\u5176\u53d8\u3002 \u3000\u3000\u62d6\u96f7\u63a9\u4e0a\u623f\u95e8\uff0c\u7b11\u9053\uff1a\u201c\u601d\u5357\u5b89\u7b54\uff0c\u60f3\u4e0d\u5230\u54b1\u4eec\u80fd\u591f\u5728\u5927\u90fd\u89c1\u9762\u3002\u4f60\u5728\u548c\u6797\u4e0d\u544a\u800c\u522b\uff0c \u8fd9\u4e24\u5e74\u6765\u6211\u4e00\u76f4\u90fd\u5f88\u6302\u5ff5\u4f60\u3002\u201d\u674e\u601d\u5357\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u591a\u52b3\u6302\u5ff5\u3002\u201d \u3000\u3000\u62d6\u96f7\u53c8\u9053\uff1a\u201c\u542c\u8bf4\u4f60\u5982\u4eca\u505a\u4e86\u4e2d\u56fd\u5317\u65b9\u7684\u7eff\u6797\u76df\u4e3b\uff0c\u53ef\u559c\u53ef\u8d3a\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u5fcd\u8010\u4e0d\u4f4f\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e5f\u542c\u8bf4\u6768\u5a49\u5728\u4f60\u8fd9\u513f\uff0c\u4e0d\u77e5\u662f\u771f\u662f\u5047\uff1f\u201d \u3000\u3000\u62d6\u96f7\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u601d\u5357\u5b89\u7b54\u4e0d\u6127\u662f\u4e2a\u591a\u60c5\u79cd\u5b50\uff0c\u521a\u521a\u6765\u5230\uff0c\u5c31\u8feb\u4e0d\u53ca\u5f85\u7684\u8981\u89c1\u6768\u59d1\u5a18\u4e86 \u4e48\uff1f\u4e0d\u9519\uff0c\u5979\u662f\u5728\u6211\u8fd9\u513f\u3002\u8bf7\u4f60\u653e\u5fc3\uff0c\u6211\u5e76\u6ca1\u6709\u4e8f\u5f85\u5979\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u662f\u8981\u89c1\u5979\u3002\u4f60\u53e3\u53e3\u58f0\u58f0\u53eb\u6211\u4f5c\u2018\u5b89\u7b54\u2019\uff0c\u60f3\u5fc5\u4f60\u4e0d\u4f1a\u7981\u6b62\u6211\u89c1\u5979 \u5427\uff1f\u201d\u62d6\u96f7\u7b11\u9053\uff1a\u201c\u5f53\u7136\uff0c\u5f53\u7136\uff0c\u6211\u662f\u4e00\u5b9a\u4f1a\u8ba9\u4f60\u548c\u5979\u89c1\u9762\u7684\u3002\u4e0d\u8fc7\uff0c\u8bf7\u4f60\u4e0d\u8981\u8fd9\u6837\u5fc3\u6025\uff0c \u54b1\u4eec\u5148\u8c08\u4e00\u8c08\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u8c08\u4ec0\u4e48\uff1f\u6768\u59d1\u5a18\u662f\u6211\u7684\u672a\u5a5a\u59bb\u5b50\uff0c\u8fd9\u4e5f\u662f\u4f60\u5df2\u7ecf\u77e5\u9053\u7684\u4e86\u3002\u4f60\u5c06\u5979\u6349\u6765\uff0c \u610f\u6b32\u4f55\u4e3a\uff1f\u4f60\u4e0d\u653e\u56de\u6211\u7684\u672a\u5a5a\u59bb\u5b50\uff0c\u4f60\u6211\u4e4b\u95f4\uff0c\u8fd8\u6709\u4ec0\u4e48\u53ef\u4ee5\u8c08\u7684\uff1f\u201d \u3000\u3000\u62d6\u96f7\u7b11\u9053\uff1a\u201c\u6211\u82e5\u4e0d\u628a\u6768\u59d1\u5a18\u8bf7\u5230\u8fd9\u91cc\u4e86\uff0c\u53c8\u7109\u80fd\u8bf7\u5f97\u4f60\u6765\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4e48\u6211\u73b0\u5728\u5df2\u7ecf\u6765\u5230\u8fd9\u91cc\u4e86\uff0c\u4f60\u8981\u600e\u6837\uff0c\u8bf4\u5427\uff1f\u201d \u3000\u3000\u62d6\u96f7\u8bf4\u9053\uff1a\u201c\u601d\u5357\u5b89\u7b54\uff0c\u6211\u5c06\u4f60\u8bf7\u6765\uff0c\u4e00\u6765\u53d9\u53d9\u65e7\u60c5\uff0c\u4e8c\u6765\u4e5f\u516c\u79c1\u4e4b\u4e8b\u548c\u4f60\u76f8\u5546\u3002\u6211\u77e5 \u9053\u4f60\u60e6\u8bb0\u7740\u6768\u59d1\u5a18\uff0c\u597d\uff0c\u90a3\u4e48\u54b1\u4eec\u5c31\u5148\u8c08\u8c08\u79c1\u4e8b\u3002\u201d\u6b63\u662f\uff1a \u3000\u3000\u864e\u7a74\u9f99\u6f6d\u9022\u65e7\u53cd\uff0c\u516c\u79c1\u6069\u6028\u8981\u5206\u660e\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b\u2014\u2014\u7b2c\u4e94\u5341\u4e00\u56de\u3000\u67ad\u96c4\u81ea\u53e4\u5de5\u5fc3\u8ba1\u3000\u7ea2\u7c89\u5982\u4eca\u89c1\u631a\u60c5 \u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b \u7b2c\u4e94\u5341\u4e00\u56de\u3000\u67ad\u96c4\u81ea\u53e4\u5de5\u5fc3\u8ba1\u3000\u7ea2\u7c89\u5982\u4eca\u89c1\u631a\u60c5 \u3000\u3000\u62d6\u96f7\u559d\u4e86\u4e00\u53e3\u8336\uff0c\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u4f60\u8981\u6211\u628a\u6768\u59d1\u5a18\u4ea4\u8fd8\u7ed9\u4f60\uff0c\u90a3\u4e5f\u4e0d\u96be\u3002\u4e0d\u8fc7\uff0c\u4f60\u4e5f\u5f97\u653e \u56de\u6211\u7684\u4eba\u624d\u662f\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u4e00\u65f6\u672a\u660e\u5176\u610f\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u53ef\u5e76\u6ca1\u6709\u62ff\u4e86\u4f60\u7684\u4eba\u5440\u3002\u201d \u3000\u3000\u62d6\u96f7\u8bf4\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\u4e0d\u662f\u4f60\u6349\u53bb\u7684\u3002\u4e0d\u8fc7\uff0c\u8fd9\u4e2a\u4eba\u73b0\u5728\u5374\u662f\u5728\u4f60\u4eec\u90a3\u513f\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u8c01\uff1f\u201d \u3000\u3000\u62d6\u96f7\u8bf4\u9053\uff1a\u201c\u6211\u7684\u56db\u59b9\u963f\u9791\u6d77\u522b\u59ec\uff08\u660e\u6167\u516c\u4e3b\u7684\u8499\u53e4\u540d\u5b57\uff09\u3002\u771f\u4eba\u9762\u524d\u4e0d\u8bf4\u5047\u8bdd\uff0c\u6211\u77e5 \u9053\u5979\u9003\u51fa\u548c\u6797\uff0c\u662f\u4e3a\u4e86\u627e\u4f60\u3002\u4f60\u4eec\u60f3\u5fc5\u662f\u89c1\u8fc7\u9762\u4e86\uff1f\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u662f\u5728\u767d\u5bb6\u5e84\u8ddf\u968f\u891a\u4e91\u5cf0\u7b49\u4eba\u56de\u7405\u739b\u5c71\u7684\uff0c\u767d\u5bb6\u5e84\u4e4b\u5f79\uff0c\u9633\u5929\u96f7\u662f\u5728\u573a\u7684\u4eba\u4e4b \u4e00\uff0c\u62d6\u96f7\u5982\u4eca\u4f4f\u5728\u9633\u5929\u96f7\u7684\u201c\u56fd\u5e08\u5e9c\u201d\uff0c\u8fd9\u4e9b\u4e8b\u60c5\uff0c\u81ea\u662f\u7792\u4e0d\u8fc7\u4ed6\u3002 \u3000\u3000\u674e\u5357\u601d\u77e5\u9053\u7792\u4e0d\u8fc7\u4ed6\uff0c\u5766\u7136\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff1a\u6211\u662f\u66fe\u7ecf\u89c1\u8fc7\u4ee4\u59b9\u3002\u201d \u3000\u3000\u62d6\u96f7\u8bf4\u9053\uff1a\u201c\u660e\u6167\u7684\u5fc3\u4e8b\uff0c\u6211\u662f\u77e5\u9053\u7684\u3002\u672c\u6765\u4f60\u4eec\u662f\u5f88\u5408\u9002\u7684\u4e00\u5bf9\uff0c\u4e0d\u8fc7\uff0c\u4f60\u73b0\u5728\u5df2\u7136 \u6709\u4e86\u674e\u59d1\u5a18\uff0c\u660e\u6167\u516c\u4e3b\u548c\u9547\u56fd\u738b\u5b50\u7684\u5a5a\u7ea6\u4ea6\u672a\u89e3\u9664\uff0c\u6211\u4e3a\u4f60\u4eec\u8bbe\u60f3\uff0c\u8fd8\u662f\u8bf7\u4f60\u8ba9\u5979\u56de\u6765\u7684 \u597d\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u5fc3\u4e2d\u7740\u607c\uff0c\u8bf4\u9053\uff1a\u201c\u62d6\u96f7\uff0c\u4f60\u522b\u4ee5\u4e3a\u6211\u6709\u4ec0\u4e48\u6500\u9f99\u9644\u51e4\u7684\u5ff5\u5934\u3002\u4e0d\u8fc7\u660e\u6167\u516c\u4e3b\u80af \u4e0d\u80af\u56de\u53bb\uff0c\u8fd9\u662f\u5979\u7684\u4e8b\u60c5\uff0c\u6211\u53ef\u7ba1\u4e0d\u7740\u3002\u201d \u3000\u3000\u62d6\u96f7\u9053\uff1a\u201c\u5979\u4e0d\u662f\u5728\u7405\u739b\u5c71\u5417\uff1f\u53ea\u8981\u4f60\u80af\u7b54\u5e94\u6211\u7684\u8bf7\u6c42\uff0c\u5199\u4e00\u5c01\u4fe1\u7ed9\u5979\uff0c\u5979\u5fc5\u5b9a\u56de\u6765\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u4f60\u8981\u6211\u600e\u6837\uff1f\u201d \u3000\u3000\u62d6\u96f7\u9053\uff1a\u201c\u5f53\u7136\u662f\u529d\u5979\u56de\u6765\u4e86\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u6211\u600e\u80fd\u62c5\u4fdd\u5979\u4f1a\u4f9d\u4ece\uff1f\u201d \u3000\u3000\u62d6\u96f7\u8bf4\u9053\uff1a\u201c\u4f60\u628a\u4f60\u7684\u4e3a\u96be\u4e4b\u5904\u548c\u5979\u8bf4\u4e2a\u6e05\u695a\uff0c\u6211\u77e5\u9053\u6211\u8fd9\u4e2a\u59b9\u5b50\u7684\u813e\u6c14\uff0c\u5979\u4e00\u5b9a\u4e0d\u4f1a \u8ba9\u4f60\u4e3a\u96be\u7684\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u51b7\u7b11\u9053\uff1a\u201c\u90a3\u4e48\u6253\u5f00\u5929\u7a97\u8bf4\u4eae\u8bdd\uff0c\u4f60\u662f\u8981\u6211\u62ff\u660e\u6167\u516c\u4e3b\u6765\u4ea4\u6362\u6768\u5a49\u4e86\uff1f\u201d \u3000\u3000\u62d6\u96f7\u70b9\u4e86\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u4e00\u70b9\u4e0d\u9519\uff0c\u6b63\u662f\u8fd9\u6837\uff01\u201d \u3000\u3000\u539f\u6765\u62d6\u96f7\u4e4b\u6240\u4ee5\u8981\u7d22\u56de\u660e\u6167\u516c\u4e3b\uff0c\u5012\u4e0d\u662f\u4ec5\u4ec5\u4e3a\u4e86\u5144\u59b9\u4e4b\u60c5\uff0c\u800c\u662f\u4e3a\u4e86\u4ed6\u4eec\u8499\u53e4\u5927\u6c57\u56fd \u7684\u4f53\u9762\u3002\u4e00\u56fd\u7684\u516c\u4e3b\uff0c\u79c1\u5954\u6c49\u4eba\uff0c\u82e5\u7136\u4e0d\u627e\u56de\u6765\uff0c\u8fdf\u65e9\u4f1a\u7ed9\u4eba\u77e5\u9053\u3002\u62d6\u96f7\u4e5f\u6015\u660e\u6167\u516c\u4e3b\u95f9\u51fa \u7b11\u8bdd\u3002 \u3000\u3000\u53e6\u4e00\u4e2a\u539f\u56e0\u662f\u56e0\u4e3a\u9547\u56fd\u738b\u5b50\u638c\u63e1\u6709\u4e00\u90e8\u5206\u5175\u6743\uff0c\u4ed6\u903c\u7740\u62d6\u96f7\uff0c\u975e\u8981\u62d6\u96f7\u7ed9\u4ed6\u627e\u56de\u672a\u5a5a\u59bb \u4e0d\u53ef\u3002 \u3000\u3000\u674e\u601d\u5357\u662f\u4e2a\u6b63\u76f4\u7684\u4eba\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u662f\u4e00\u5b9a\u8981\u5a49\u59b9\u56de\u6765\u7684\u3002\u53ef\u662f\u56e0\u6b64\u8981\u6211\u5f3a\u8feb \u660e\u6167\u516c\u4e3b\u8fdd\u80cc\u81ea\u5df1\u7684\u610f\u601d\u56de\u56fd\u3001\u8fd9\u5c82\u662f\u5927\u4e08\u592b\u6240\u4e3a\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u54fc\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u79c1\u4e8b\u7f13\u8c08\uff0c\u4f60\u518d\u8bf4\u8bf4\u516c\u4e8b\u3002\u201d \u3000\u3000\u62d6\u96f7\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4e5f\u597d\uff0c\u90a3\u54b1\u4eec\u5c31\u5148\u8c08\u8c08\u516c\u4e8b\u3002\u601d\u5357\u5b89\u7b54\uff0c\u4f60\u505a\u4e86\u7eff\u6797\u76df\u4e3b\uff0c\u8fd9 \u7aef\u7684\u662f\u53ef\u559c\u53ef\u8d3a\u554a\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u4f60\u505a\u4e86\u8499\u53e4\u7684\u5175\u9a6c\u5927\u5143\u5e05\uff0c\u6211\u53ef\u8fd8\u6ca1\u6709\u5411\u4f60\u8d3a\u559c\u5462\u3002\u4f46\u4f60\u662f\u5143\u5e05\uff0c \u6211\u662f\u5f3a\u76d7\u5934\u5b50\uff0c\u54b1\u4eec\u4e4b\u95f4\uff0c\u53c8\u6709\u4ec0\u4e48\u2018\u516c\u4e8b\u2019\u597d\u8c08\uff1f\u201d \u3000\u3000\u62d6\u96f7\u7b11\u9053\uff1a\u201c\u601d\u5357\u5b89\u7b54\uff0c\u4f60\u7684\u6d88\u606f\u5012\u662f\u9887\u4e3a\u7075\u901a\uff0c\u77e5\u9053\u6211\u5f53\u4e86\u8499\u53e4\u7684\u5175\u9a6c\u5927\u5143\u5e05\u4e86\u3002 \u563f\uff0c\u563f\uff0c\u6b63\u56e0\u4e3a\u54b1\u4eec\u4e24\u4eba\u73b0\u5728\u7684\u8eab\u4efd\uff0c\u54b1\u4eec\u4e4b\u95f4\uff0c\u5c31\u5927\u6709\u5408\u4f5c\u7684\u53ef\u80fd\u4e86\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u54e6\uff0c\u4f60\u8981\u6211\u5982\u4f55\u4e0e\u4f60\u5408\u4f5c\uff1f\u8bf7\u9053\u5176\u8be6\u3002\u201d \u3000\u3000\u62d6\u96f7\u53c8\u662f\u54c8\u54c8\u4e00\u7b11\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u8499\u53e4\u548c\u91d1\u56fd\u4e43\u662f\u4e16\u4ec7\uff0c\u5148\u5927\u6c57\u5728\u65e5\uff0c\u66fe\u7edd\u77e2\u8a93\u706d \u91d1\uff0c\u8fd9\u4e5f\u662f\u4f60\u77e5\u9053\u7684\u3002\u6211\u4eec\u73b0\u5728\u548c\u91d1\u56fd\u8c08\u548c\uff0c\u4e0d\u8fc7\u662f\u6743\u5b9c\u4e4b\u8ba1\uff0c\u4e00\u6709\u673a\u4f1a\uff0c\u6211\u4eec\u8fd8\u662f\u8981\u8fdb\u5175 \u4e2d\u539f\u7684\u3002\u6211\u8fd9\u6b21\u6765\u5230\u5927\u90fd\uff0c\u4e00\u6765\u662f\u4e3a\u4e86\u8981\u5728\u5e55\u540e\u4e3b\u6301\u548c\u8c08\uff0c\u4e8c\u6765\u4e5f\u662f\u4e3a\u4e86\u7aa5\u63a2\u91d1\u56fd\u7684\u865a\u5b9e\uff0c \u7b56\u5212\u541e\u5e76\u4e2d\u539f\u7684\u5927\u4e8b\u3002\u8fd9\u4e2a\u7792\u5f97\u8fc7\u522b\u4eba\uff0c\u4f46\u7792\u4e0d\u8fc7\u4f60\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u8fd9\u53c8\u600e\u6837\uff1f\u201d \u3000\u3000\u62d6\u96f7\u8bf4\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u662f\u6c49\u4eba\uff0c\u662f\u4e2a\u7231\u56fd\u7684\u7537\u513f\uff0c\u90a3\u4e48\u6211\u8fd8\u53ef\u4ee5\u544a\u8bc9\u4f60\uff0c\u8499\u53e4\u548c\u5357\u5b8b\u5df2 \u7ecf\u79d8\u5bc6\u5b9a\u4e0b\u76df\u7ea6\uff0c\u4e24\u56fd\u8054\u5408\uff0c\u5171\u540c\u706d\u91d1\u3002\u8fd9\u4e5f\u5c31\u662f\u8bf4\uff0c\u91d1\u56fd\u662f\u6211\u4eec\u5171\u540c\u7684\u654c\u4eba\u4e86\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u4ecd\u7136\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u8fd9\u4e2a\u6211\u65e9\u5df2\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u62d6\u96f7\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u77e5\u9053\u5c31\u597d\u4e86\uff01\u91d1\u56fd\u65e2\u7136\u662f\u54b1\u4eec\u5171\u540c\u7684\u654c\u4eba\uff0c\u54b1\u4eec\u8fd8\u4e0d\u8be5\u5f7c\u6b64\u5408\u4f5c \u5417\uff1f\u5c06\u6765\u6211\u8fdb\u5175\u4e2d\u539f\u4e4b\u65f6\uff0c\u8bf7\u4f60\u52a9\u6211\u4e00\u81c2\u4e4b\u529b\uff0c\u6211\u4e5f\u4e0d\u4f1a\u4e8f\u5f85\u4f60\u7684\u3002\u5927\u529f\u544a\u6210\u4e4b\u540e\uff0c\u6211\u53ef\u4ee5 \u7acb\u4f60\u4e3a\u738b\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u51b7\u7b11\u9053\uff1a\u201c\u591a\u8c22\u4e86\u3002\u7b2c\u4e00\uff0c\u6211\u4e0d\u60f3\u79f0\u738b\u79f0\u9738\uff1b\u7b2c\u4e8c\uff0c\u4e2d\u539f\u672c\u662f\u6c49\u4eba\u4e4b\u5730\uff0c\u4e5f\u4e0d\u5bb9 \u4f60\u4eec\u8499\u53e4\u4eba\u64c5\u81ea\u5206\u5272\u3002\u201d \u3000\u3000\u62d6\u96f7\u6014\u4e86\u4e00\u6014\uff0c\u6253\u4e86\u4e2a\u54c8\u54c8\uff0c\u8bf4\u9053\uff1a\u201c\u601d\u5357\u5b89\u7b54\uff0c\u4f60\u6de1\u6cca\u540d\u5229\uff0c\u96be\u5f97\u96be\u5f97\u3002\u4f46\u60f3\u5fc5\u4f60\u4e5f \u662f\u8fd8\u8981\u5949\u5357\u5b8b\u53f7\u4ee4\u7684\u5427\uff1f\u8499\u53e4\u548c\u5b8b\u56fd\u5df2\u7ecf\u662f\u5b9a\u4e86\u76df\u7ea6\u7684\u5144\u5f1f\u4e4b\u90a6\uff0c\u4f60\u5e2e\u4e86\u6211\u7684\u5fd9\uff0c\u4e5f\u5c31\u662f\u5e2e \u4e86\u4f60\u4eec\u7687\u5e1d\u7684\u5fd9\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u51b7\u7b11\u9053\uff1a\u201c\u6211\u53ea\u77e5\u8981\u5e2e\u6211\u4eec\u6c49\u4eba\u767e\u59d3\u7684\u5fd9\u3002\u201d \u3000\u3000\u62d6\u96f7\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u6211\u4e0d\u6c42\u4f60\u5e2e\u5fd9\uff0c\u53ea\u60f3\u8bf7\u4f60\u5728\u6211\u4eec\u8fdb\u5175\u4e2d\u539f\u4e4b\u65f6\uff0c\u4f60\u4eec\u7684\u4eba\u4e24\u4e0d\u76f8\u52a9\uff0c \u8fd9\u603b\u53ef\u4ee5\u4e86\u5427\uff1f\u201d \u3000\u3000\u674e\u6069\u5357\u4ea2\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u662f\u4e0d\u52a9\u91d1\u864f\uff0c\u4e5f\u4e0d\u4f1a\u5e2e\u52a9\u4f60\u4eec\u8499\u53e4\u4eba\u7684\u3002\u4f46\u8c01\u5360\u6211\u4eec\u6c49\u4eba\u7684\u5730 \u65b9\uff0c\u6211\u4eec\u5c31\u975e\u8981\u62b5\u6297\u4e0d\u53ef\u3002\u4f60\u8981\u6211\u8896\u624b\u65c1\u89c2\uff0c\u90a3\u53ef\u4e0d\u6210\u3002\u201d \u3000\u3000\u62d6\u96f7\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u516c\u4e8b\u548c\u79c1\u4e8b\uff0c\u4f60\u90fd\u662f\u4e0d\u80af\u7b54\u5e94\u7684\u4e86\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u5fc3\u4e71\u5982\u9ebb\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u6211\u5927\u4e0d\u4e86\u662f\u62fc\u4e2a\u6b7b\uff0c\u90a3\u4e5f\u6ca1\u6709\u4ec0\u4e48\uff0c\u53ef\u662f\u5a49\u59b9\uff0c\u5a49 \u59b9\u2026\u2026\u201d\u60f3\u5230\u6768\u5a49\u65e0\u7236\u65e0\u6bcd\uff0c\u53ea\u6709\u4e00\u4e2a\u54e5\u54e5\uff0c\u53c8\u5df2\u56e0\u4e3a\u81ea\u5df1\u7684\u4e8b\u60c5\u4e27\u751f\u5f02\u57df\uff0c\u4e0d\u7531\u5f97\u6709\u70b9\u5fc3 \u4e2d\u96be\u8fc7\uff0c\u60f3\u9053\uff1a\u201c\u6211\u5df2\u7ecf\u8fde\u7d2f\u4e86\u5979\u7684\u54e5\u54e5\uff0c\u96be\u9053\u8fd8\u8981\u8fde\u7d2f\u5979\u4e48\uff1f\u201d \u3000\u3000\u62d6\u96f7\u4f3c\u4e4e\u770b\u51fa\u4e86\u674e\u601d\u5357\u5fc3\u610f\u8e0c\u8e87\uff0c\u6709\u4e9b\u628a\u6301\u4e0d\u5b9a\uff0c\u4fbf\u53c8\u8bf4\u9053\uff1a\u201c\u601d\u5357\u5b89\u7b54\uff0c\u4f60\u60f3\u6211\u628a\u6768 \u59d1\u5a18\u9001\u56de\u7ed9\u4f60\uff0c\u4f60\u4e5f\u603b\u5f97\u7b54\u5e94\u6211\u4e00\u4e9b\u4e8b\u60c5\u554a\uff01\u516c\u79c1\u4e24\u4e8b\uff0c\u968f\u4f60\u9009\u62e9\uff0c\u8981\u561b\uff0c\u4f60\u628a\u660e\u6167\u627e\u6765\u548c \u6211\u4ea4\u6362\uff1b\u8981\u561b\uff0c\u4f60\u5c31\u7b54\u5e94\u5728\u6211\u8fdb\u5175\u4e2d\u539f\u4e4b\u65f6\uff0c\u4e0d\u548c\u6211\u4e3a\u96be\uff01\u4f60\u8981\u77e5\u9053\uff0c\u6211\u80af\u8ba9\u4f60\u5728\u4e24\u8005\u4e4b\u4e2d \u4efb\u62e9\u5176\u4e00\uff0c\u8fd9\u5df2\u7ecf\u662f\u5ff5\u5728\u65e7\u60c5\uff0c\u683c\u5916\u901a\u878d\u7684\u4e86\uff01\u201d \u3000\u3000\u9f99\u8c61\u6cd5\u738b\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u4ef6\u4e8b\u60c5\uff0c\u4f60\u53ef\u4ee5\u7406\uff0c\u4e5f\u53ef\u4ee5\u4e0d\u7406\u3002\u201d\u674e\u601d\u5357\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\u60c5\uff1f\u201d \u9f99\u8c61\u6cd5\u738b\u9053\uff1a\u201c\u4f60\u5fd8\u4e86\u8fd8\u6709\u4e00\u4f4d\u97e9\u59d1\u5a18\u5728\u6211\u4eec\u8fd9\u91cc\u4e48\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u731b\u7136\u4e00\u7701\uff0c\u6012\u9053\uff1a\u201c\u90a3\u4f4d\u97e9\u59d1\u5a18\u662f\u5b8c\u5168\u65e0\u8f9c\u7684\uff0c\u4f60\u4eec\u5e94\u8be5\u5c06\u5979\u653e\u56de\u53bb\u624d\u662f\uff01\u201d \u3000\u3000\u9f99\u8c61\u6cd5\u738b\u9053\uff0c\u201c\u4e0d\u9519\uff0c\u5979\u662f\u5b8c\u5168\u53d7\u4e86\u4f60\u7684\u62d6\u7d2f\uff0c\u53ef\u662f\u6349\u864e\u5bb9\u6613\u653e\u864e\u96be\uff0c\u8fd9\u53e5\u4fd7\u8bdd\uff0c\u60f3\u5fc5 \u4f60\u4e5f\u77e5\u9053\u3002\u97e9\u59d1\u5a18\u4e0d\u662f\u8001\u864e\uff0c\u5979\u7684\u7236\u4eb2\u53ef\u662f\u4e00\u5934\u8001\u864e\uff0c\u6211\u4eec\u5c82\u80fd\u8f7b\u6613\u653e\u5979\u56de\u53bb\uff1f\u9664\u975e\u4f60\u7b54\u5e94 \u4e86\u6211\u4eec\u56db\u738b\u5b50\u7684\u6761\u4ef6\uff0c\u8fd8\u8981\u97e9\u5927\u7ef4\u56de\u8f6c\u6d1b\u9633\u624d\u884c\u3002\u201d\u8981\u77e5\u97e9\u5927\u7ef4\u5018\u82e5\u56de\u8f6c\u6d1b\u9633\uff0c\u90a3\u5c31\u662f\u5728\u8499 \u53e4\u4eba\u7684\u52bf\u529b\u4e4b\u4e0b\uff0c\u4e0d\u7531\u4ed6\u4e0d\u5c31\u8303\u4e86\u3002 \u3000\u3000\u674e\u601d\u5357\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u4f60\u4eec\u624b\u6bb5\u5982\u6b64\u6bd2\u8fa3\uff01\u201d \u3000\u3000\u9f99\u8c61\u6cd5\u738b\u7b11\u9053\uff1a\u201c\u8fd9\u4e5f\u662f\u4f60\u4eec\u6c49\u4eba\u7684\u6210\u8bed\uff0c\u8fd9\u53eb\u505a\u65e0\u6bd2\u4e0d\u4e08\u592b\uff01\u201d \u3000\u3000\u62d6\u96f7\u5374\u7b11\u9053\uff1a\u201c\u97e9\u5927\u7ef4\u4e0e\u4f60\u65e0\u751a\u4ea4\u60c5\uff0c\u4ed6\u7684\u5973\u513f\uff0c\u4f60\u4e0d\u7406\u4e5f\u884c\u3002\u4e0d\u8fc7\uff0c\u6211\u77e5\u9053\u4f60\u4e3a\u4eba\u4ed7 \u4e49\uff0c\u4f60\u82e5\u662f\u4e0d\u7406\u5979\u7684\u6b7b\u6d3b\uff0c\u53ea\u6015\u4e5f\u662f\u4e8e\u5fc3\u4e0d\u5b89\u5427\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u6012\u4ece\u5fc3\u8d77\uff0c\u60f3\u9053\uff1a\u201c\u5a49\u59b9\u5185\u67d4\u5916\u521a\uff0c\u6211\u82e5\u662f\u4e3a\u4e86\u5979\u7684\u7f18\u6545\uff0c\u7b54\u5e94\u4e86\u62d6\u96f7\u7684\u6761\u4ef6\uff0c \u83ab\u8bf4\u6211\u4ece\u6b64\u62ac\u4e0d\u8d77\u5934\uff0c\u5a49\u59b9\u4e5f\u4e00\u5b9a\u662f\u5b81\u6b7b\u4e5f\u4e0d\u613f\u610f\u6211\u8fd9\u6837\u505a\u7684\uff01\u81f3\u4e8e\u97e9\u8001\u524d\u8f88\uff0c\u4ed6\u5931\u4e86\u5973 \u513f\uff0c\u5b9a\u5fc5\u75db\u5fc3\u3002\u4e0d\u8fc7\u6211\u867d\u662f\u548c\u4ed6\u521d\u4ea4\uff0c\u4f46\u4e0b\u4e86\u8fd9\u51e0\u5929\u68cb\uff0c\u4ed6\u90a3\u4e0d\u7518\u8ba4\u8f93\u7684\u6027\u683c\u6211\u4e5f\u662f\u6df1\u6df1\u77e5 \u9053\u7684\u4e86\u3002\u4ed6\u4e0b\u68cb\u5c1a\u4e14\u4e0d\u7518\u8ba4\u8f93\uff0c\u53c8\u5c82\u80fd\u7518\u5fc3\u8f93\u7ed9\u9791\u5b50\uff1f\u201d \u3000\u3000\u60f3\u81f3\u6b64\u5904\uff0c\u674e\u601d\u5357\u5fc3\u610f\u7acb\u51b3\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u62d6\u96f7\uff0c\u4f60\u4eec\u5f15\u7528\u4e86\u6c49\u4eba\u7684\u6210\u8bed\uff0c\u90a3\u6211\u4e5f\u7ed9\u4f60 \u8bf4\u8bf4\u6211\u4eec\u6c49\u4eba\u5148\u8d24\u7684\u6559\u8bad\u5427\uff0c\u5b54\u66f0\u6210\u4ec1\uff0c\u5b5f\u66f0\u53d6\u4e49\u3002\u5b54\u5b50\u5b5f\u5b50\u7684\u6559\u8bad\u662f\u8981\u6211\u4eec\u5728\u5fc5\u8981\u7684\u5173\u5934 \u820d\u8eab\u6210\u4ec1\uff0c\u8f7b\u751f\u53d6\u4e49\uff0c\u6211\u8bfb\u4e66\u4e0d\u591a\uff0c\u8fd9\u4e24\u53e5\u8bdd\u6211\u662f\u8bb0\u5f97\uff0c\u5b9a\u5fc5\u5949\u884c\u7684\u3002\u597d\uff0c\u8a00\u5c3d\u4e8e\u6b64\uff0c\u4f60\u8bf4 \u4ec0\u4e48\u6211\u90fd\u4e0d\u7b54\u5e94\uff01\u4f60\u8981\u5c06\u6211\u5982\u4f55\uff0c\u4efb\u4ece\u5c0a\u610f\uff01\u201d \u3000\u3000\u62d6\u96f7\u5446\u4e86\u534a\u664c\uff0c\u5ffd\u5730\u53c8\u88c5\u51fa\u7b11\u5bb9\uff0c\u8bf4\u9053\uff1a\u201c\u601d\u5357\u5b89\u7b54\uff0c\u4f60\u5982\u4eca\u662f\u5728\u6012\u706b\u5934\u4e0a\uff0c\u8bf4\u8bdd\u6216\u8005 \u6709\u6b20\u601d\u91cf\uff0c\u6211\u8ba9\u4f60\u51b7\u9759\u7684\u60f3\u4e00\u60f3\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u6012\u9053\uff1a\u201c\u5927\u4e08\u592b\u8bf4\u8bdd\u65a9\u9489\u622a\u94c1\uff0c\u6211\u662f\u8bf4\u4e00\u4e0d\u4e8c\u7684\uff01\u6211\u770b\u5012\u662f\u4f60\u8981\u51b7\u9759\u7684\u60f3\u60f3\u624d \u5bf9\uff0c\u60f3\u60f3\u4f60\u4eec\u4e3a\u4ec0\u4e48\u8981\u6765\u4fb5\u5360\u6c49\u4eba\u7684\u5730\u65b9\uff1f\u5c0f\u86c7\u541e\u8c61\uff0c\u6c49\u4eba\u7684\u5730\u65b9\u5c82\u662f\u4f60\u4eec\u80fd\u591f\u541e\u5f97\u4e86\u7684 \u4e48\uff1f\u201d \u3000\u3000\u62d6\u96f7\u6253\u4e86\u4e2a\u54c8\u54c8\uff0c\u76ae\u7b11\u8089\u4e0d\u7b11\u5730\u8bf4\u9053\uff1a\u201c\u601d\u5357\u5b89\u7b54\uff0c\u6211\u73b0\u5728\u4e0d\u548c\u4f60\u4e89\u8bba\uff0c\u6211\u60f3\u8ba9\u4f60\u548c\u6768 \u59d1\u5a18\u5546\u91cf\u5546\u91cf\u4e4b\u540e\u518d\u8bf4\u3002\u6765\u4eba\u54ea\uff01\u201d\u963f\u535c\u5362\u3001\u547c\u9ece\u5962\u4e8c\u4eba\u5e94\u58f0\u800c\u5165\u3002 \u3000\u3000\u62d6\u96f7\u628a\u624b\u4e00\u6325\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u628a\u674e\u76df\u4e3b\u5e26\u4e0b\u53bb\uff0c\u8ba9\u4ed6\u548c\u90a3\u4e24\u4e2a\u5973\u7684\u8bf4\u8bdd\uff01\u201d\u963f\u3001\u547c\u4e8c\u4eba \u4e00\u4eba\u4e00\u8fb9\uff0c\u67b6\u4f4f\u674e\u601d\u5357\uff0c\u674e\u601d\u5357\u53cc\u81c2\u4e00\u632f\uff0c\u6012\u9053\uff1a\u201c\u6211\u81ea\u5df1\u4f1a\u8d70\uff01\u201d \u3000\u3000\u62d6\u96f7\u8bf4\u9053\uff1a\u201c\u601d\u5357\u5b89\u7b54\uff0c\u5bf9\u4e0d\u4f4f\uff0c\u53ef\u5f97\u4ee4\u4f60\u53d7\u70b9\u59d4\u5c48\u4e86\u3002\u4f60\u4ec0\u4e48\u65f6\u5019\u7b54\u5e94\uff0c\u4ec0\u4e48\u65f6\u5019\u51fa \u6765\uff01\u201d\u674e\u601d\u5357\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6211\u672c\u6765\u5c31\u4e0d\u6253\u7b97\u6d3b\u7740\u51fa\u53bb\u3002\u201d\u9f99\u8c61\u6cd5\u738b\u54fc\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u4e5f \u7531\u4f60\uff01\u201d \u3000\u3000\u547c\u3001\u963f\u4e8c\u4eba\u5c06\u674e\u601d\u5357\u5173\u5165\u4e00\u95f4\u56da\u623f\uff0c\u9014\u4e2d\u674e\u601d\u5357\u4e00\u76f4\u601d\u7591\u4e0d\u5b9a\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u62d6\u96f7\u90a3\u6837 \u8bf4\u6cd5\uff0c\u96be\u9053\u4ed6\u5f53\u771f\u80af\u8ba9\u6211\u4e0e\u5a49\u59b9\u89c1\u9762\u4e0d\u6210\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u8fdb\u4e86\u56da\u623f\uff0c\u91cc\u9762\u54ea\u6709\u6768\u5a49\u7684\u5f71\u5b50\u3002\u201c\u4e53\u201d\u7684\u4e00\u58f0\uff0c\u7262\u95e8\u5173\u4e0a\uff0c\u6708\u5149\u5173\u5728\u5916\u9762\uff0c\u9ed1 \u6697\u7b3c\u7f69\u7262\u623f\uff0c\u674e\u601d\u5357\u7684\u5fc3\u60c5\u4e5f\u662f\u4e00\u7247\u7070\u6697\u4e86\u3002 \u3000\u3000\u674e\u601d\u5357\u6b63\u81ea\u5fc3\u4e2d\u82e6\u7b11\uff1a\u201c\u62d6\u96f7\u5f53\u7136\u662f\u54c4\u9a97\u6211\u7684\uff0c\u6211\u600e\u80fd\u76f8\u4fe1\u4ed6\u7684\u8bf4\u8bdd\u3002\u201d\u5fc3\u5ff5\u672a\u5df2\uff0c\u5ffd \u542c\u5f97\u9694\u58c1\u623f\u95f4\u6709\u4eba\u8bf4\u8bdd\uff1a\u201c\u54a6\uff0c\u597d\u50cf\u53c8\u6709\u4ec0\u4e48\u4eba\u7ed9\u5173\u8fdb\u7262\u623f\u4e86\uff0c\u4e0d\u77e5\u662f\u8c01\uff1f\u201d\u597d\u50cf\u662f\u97e9\u4f69\u745b \u7684\u58f0\u97f3\u3002 \u3000\u3000\u674e\u601d\u5357\u6014\u4e86\u4e00\u6014\uff0c\u5fd9\u628a\u8033\u6735\u8d34\u7740\u5899\u58c1\uff0c\u8ddf\u7740\u4fbf\u542c\u5f97\u4e00\u4e2a\u6e05\u8106\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u81ea\u987e\u4e0d \u6687\uff0c\u7ba1\u4ed6\u662f\u8c01\uff1f\u201d\u8fd9\u53ef\u662f\u674e\u601d\u5357\u6700\u4e3a\u719f\u6089\u7684\u58f0\u97f3\u4e86\u3002\u97e9\u4f69\u745b\u9053\uff1a\u201c\u54a6\uff0c\u4f60\u600e\u4e48\u8fd9\u6837\u8bf4\uff1f\u6216\u8005 \u662f\u54b1\u4eec\u81ea\u5df1\u4eba\u5462\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u7981\u4e0d\u7740\u53c8\u60ca\u53c8\u559c\uff0c\u8fde\u5fd9\u6572\u6572\u5899\u58c1\uff0c\u8bf4\u9053\uff1a\u201c\u5a49\u59b9\uff0c\u662f\u6211\uff0c\u4f60\u542c\u5f97\u89c1\u6211\u4e48\uff1f\u201d \u3000\u3000\u62d6\u96f7\u66fe\u7ecf\u5bf9\u6768\u5a49\u8bf4\u8fc7\uff0c\u8bf4\u662f\u8981\u628a\u674e\u601d\u5357\u201c\u8bf7\u201d\u6765\u7684\u3002\u6545\u6b64\u5f53\u5979\u77e5\u9053\u6709\u4eba\u5173\u8fdb\u7262\u623f\u7684\u65f6 \u5019\uff0c\u5979\u7684\u5fc3\u60c5\u5b9e\u662f\u5341\u5206\u77db\u76fe\uff0c\u5bb3\u6015\u771f\u7684\u662f\u674e\u601d\u5357\u88ab\u6293\u4e86\u8fdb\u6765\uff0c\u4f46\u53c8\u6709\u70b9\u76fc\u671b\u662f\u4ed6\u3002 \u3000\u3000\u6768\u5a49\u6240\u5bb3\u6015\u7684\u6210\u4e3a\u4e86\u4e8b\u5b9e\uff0c\u5979\u5446\u4e86\u4e00\u5446\uff0c\u5931\u58f0\u53eb\u9053\uff1a\u201c\u5357\u54e5\uff0c\u771f\u7684\u662f\u4f60\uff1f\u6211\u8fd9\u662f\u505a\u68a6 \u4e48\uff1f\u4f46\u613f\u662f\u68a6\u624d\u597d\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u82e6\u7b11\u9053\uff1a\u201c\u8fd9\u4e0d\u662f\u68a6\uff0c\u5f53\u771f\u662f\u6211\u3002\u62d6\u96f7\u53eb\u4eba\u62ff\u4e86\u4f60\u7684\u90a3\u652f\u7389\u7c2a\uff0c\u628a\u6211\u9a97\u6765\u7684\u3002\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u4ed6\u4e3a\u4ec0\u4e48\u80af\u8ba9\u4f60\u548c\u6211\u4eec\u4f5c\u90bb\u5c45\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u4ed6\u8981\u6211\u548c\u4f60\u5546\u91cf\u3002\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u5546\u91cf\u4ec0\u4e48\uff1f\u4ed6\u6253\u7684\u4e00\u5b9a\u4e0d\u662f\u597d\u4e3b\u610f\u3002\u4f60\u53ef\u522b\u4e0a\u4ed6\u7684\u5f53\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u7b11\u9053\uff1a\u201c\u6211\u600e\u4f1a\u4e0a\u4ed6\u7684\u5f53\uff1f\u6211\u82e5\u662f\u90a3\u6837\u5bb9\u6613\u4e0a\u5f53\u7684\u8bdd\uff0c\u4ed6\u4e5f\u65e0\u987b\u628a\u6211\u5173\u8d77\u6765\u4e86\u3002\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u4ed6\u8981\u4f60\u548c\u6211\u5546\u91cf\u7684\u662f\u4ec0\u4e48\u4e8b\u60c5\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u4ed6\u7684\u82b1\u6837\u624d\u591a\u5462\uff0c\u6709\u516c\u4e8b\uff0c\u4e5f\u6709\u79c1\u4e8b\u3002\u201d \u3000\u3000\u6768\u5a49\u542c\u4e86\u674e\u601d\u5357\u8bf4\u62d6\u96f7\u6240\u63d0\u7684\u90a3\u4e24\u4e2a\u6761\u4ef6\u4e4b\u540e\uff0c\u8bf4\u9053\uff1a\u201c\u5357\u54e5\uff0c\u5e78\u4e8f\u4f60\u6ca1\u6709\u7b54\u5e94\uff0c\u5982\u679c \u6211\u540c\u610f\u4f60\u62ff\u660e\u6167\u516c\u4e3b\u6765\u4ea4\u6362\u6211\uff0c\u6211\u8fd8\u80fd\u7b97\u662f\u4eba\u5417\uff1f\u201d\u5979\u53ea\u8bf4\u201c\u79c1\u4e8b\u201d\uff0c\u4e0d\u8c08\u201c\u516c\u4e8b\u201d\uff0c\u4e43\u662f \u56e0\u4e3a\u77e5\u9053\u674e\u601d\u5357\u5728\u201c\u516c\u4e8b\u201d\u4e0a\u7edd\u4e0d\u4f1a\u6709\u4e1d\u6beb\u52a8\u6447\u4e4b\u6545\u3002 \u3000\u3000\u674e\u601d\u5357\u6697\u6697\u53eb\u4e86\u4e00\u58f0\u201c\u60ed\u6127\u201d\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5a49\u59b9\u597d\u50cf\u77e5\u9053\u6211\u5728\u79c1\u4e8b\u4e0a\u66fe\u7ecf\u6709\u8fc7\u5c11\u8bb8\u8e0c \u8e87\u3002\u201d\u53e6\u4e00\u65b9\u9762\u53c8\u662f\u751a\u4e3a\u6b22\u559c\uff0c\u60f3\u9053\uff1a\u201c\u5a49\u59b9\u679c\u7136\u5982\u6211\u6240\u6599\uff0c\u4e0d\u6127\u662f\u4e2a\u5916\u67d4\u5185\u521a\u7684\u5973\u4e2d\u4e08 \u592b\u3002\u5728\u548c\u6797\u7684\u65f6\u5019\uff0c\u660e\u6167\u516c\u4e3b\u5c5e\u610f\u4e8e\u6211\uff0c\u5979\u662f\u77e5\u9053\u7684\uff0c\u4e3a\u6b64\uff0c\u5979\u4e5f\u66fe\u4e00\u5ea6\u8d77\u8fc7\u7591\u5fc3\u3002\u5982\u4eca\u5979 \u80fd\u591f\u8fd9\u6837\uff0c\u8fd9\u5c31\u66f4\u52a0\u96be\u80fd\u53ef\u8d35\u4e86\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u6ee1\u6000\u6b22\u7545\uff0c\u8bf4\u9053\uff1a\u201c\u5a49\u59b9\uff0c\u96be\u5f97\u4f60\u6211\u7684\u60f3\u6cd5\u76f8\u540c\uff0c\u54b1\u4eec\u751f\u6b7b\u4e0e\u5171\uff0c\u6211\u4e5f\u65e0\u987b\u548c\u4f60 \u8bf4\u4e9b\u4ec0\u4e48\u5b89\u6170\u4f60\u7684\u8bdd\u4e86\u3002\u53ea\u662f\u6211\u89c9\u5f97\u5bf9\u97e9\u59d1\u5a18\u4e0d\u4f4f\uff0c\u7d2f\u5979\u65e0\u8f9c\u53d7\u5bb3\u3002\u201d \u3000\u3000\u97e9\u4f69\u745b\u9053\uff1a\u201c\u4f60\u8fd9\u662f\u8bf4\u7684\u4ec0\u4e48\u8bdd\uff0c\u53ea\u8bb8\u4f60\u4eec\u505a\u82f1\u96c4\u597d\u6c49\uff0c\u6211\u5c31\u5e94\u8be5\u662f\u4e2a\u8d2a\u751f\u6015\u6b7b\u4e4b\u4eba \u5417\uff1f\u201d\u674e\u601d\u5357\u9053\uff1a\u201c\u4e0d\uff0c\u6211\u7edd\u5bf9\u6ca1\u6709\u8fd9\u4e2a\u610f\u601d\uff0c\u53ea\u662f\uff0c\u53ea\u662f\u2026\u2026\u201d \u3000\u3000\u97e9\u4f69\u745b\u9053\uff1a\u201c\u4f60\u4e0d\u5fc5\u518d\u8bf4\u62b1\u6b49\u7684\u8bdd\u4e86\u3002\u771f\u4e2a\u8bf4\u8d77\u6765\uff0c\u5012\u662f\u6211\u5e94\u8be5\u5411\u4f60\u4eec\u62b1\u6b49\u5462\u3002\u90a3\u5929\u82e5 \u4e0d\u662f\u6211\u5f3a\u9080\u5a49\u59d0\u51fa\u6765\uff0c\u4e5f\u4e0d\u4f1a\u51fa\u8fd9\u4ef6\u4e8b\u3002\u201d\u6768\u5a49\u7b11\u9053\uff1a\u201c\u591a\u4e00\u6b21\u8fd9\u6837\u7684\u78e8\u7ec3\u4e5f\u4e0d\u9519\u5440\uff0c\u54b1\u4eec \u5982\u4eca\u53ef\u771f\u662f\u5171\u60a3\u96be\u7684\u597d\u59d0\u59b9\u4e86\uff01\u201d \u3000\u3000\u97e9\u4f69\u745b\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u76f8\u4fe1\u7239\u7239\u548c\u9646\u5e2e\u4e3b\u603b\u6709\u529e\u6cd5\u628a\u54b1\u4eec\u6551\u51fa\u53bb\u7684\u3002\u5c31\u662f\u6551\u4e0d\u51fa\u53bb\uff0c \u6709\u4f60\u8fd9\u6837\u4e00\u4f4d\u597d\u59d0\u59d0\u966a\u6211\uff0c\u6211\u548c\u4f60\u5728\u8fd9\u7262\u623f\u4e2d\u8fc7\u4e00\u4e16\uff0c\u90a3\u4e5f\u6ca1\u6709\u4ec0\u4e48\u3002\u201d \u3000\u3000\u62d6\u96f7\u672c\u6765\u662f\u5e0c\u671b\u674e\u601d\u5357\u4e0e\u6768\u5a49\u4ea4\u8c08\u4e4b\u540e\uff0c\u6768\u5a49\u7684\u773c\u6cea\u4f1a\u8f6f\u5316\u4ed6\u7684\u3002\u54ea\u77e5\u4ed6\u4ece\u6768\u5a49\u7684\u8bf4\u8bdd \u4e4b\u4e2d\uff0c\u66f4\u52a0\u5f97\u5230\u4e86\u9f13\u821e\uff0c\u589e\u5f3a\u4e86\u652f\u6301\u7684\u529b\u91cf\uff0c\u8fd9\u662f\u62d6\u96f7\u51b3\u8ba1\u6599\u60f3\u4e0d\u5230\u7684\u3002 \u3000\u3000\u97e9\u4f69\u745b\u6df1\u77e5\u4e10\u5e2e\u6d88\u606f\u7075\u901a\uff0c\u8fd9\u4ef6\u4e8b\u4e10\u5e2e\u8fdf\u65e9\u4e5f\u4f1a\u77e5\u9053\uff0c\u5979\u4e5f\u76f8\u4fe1\u5979\u7684\u7236\u4eb2\u4f1a\u6709\u529e\u6cd5\u628a\u5979 \u6551\u51fa\u53bb\u3002 \u3000\u3000\u5979\u6599\u5f97\u4e0d\u9519\uff0c\u4e10\u5e2e\u7684\u786e\u662f\u5728\u5979\u4eec\u51fa\u4e8b\u4e4b\u540e\uff0c\u4e0d\u5230\u4e00\u4e2a\u65f6\u8fb0\uff0c\u5c31\u63a5\u5230\u6d88\u606f\u3002 \u3000\u3000\u4f46\u662f\u5979\u60f3\u5f97\u8fd8\u662f\u672a\u514d\u592a\u8fc7\u7b80\u5355\uff0c\u97e9\u5927\u7ef4\u548c\u4e10\u5e2e\u7684\u9646\u5e2e\u4e3b\u5f53\u7136\u662f\u8981\u6551\u5979\u4eec\u7684\uff0c\u53ef\u662f\u76f4\u5230\u76ee \u524d\uff0c\u4ed6\u4eec\u8fd8\u662f\u675f\u624b\u65e0\u7b56\u3002 \u3000\u3000\u82b1\u5f00\u4e24\u6735\uff0c\u5404\u8868\u4e00\u6280\u3002\u4e14\u8bf4\u97e9\u5927\u7ef4\u90a3\u665a\u5306\u5306\u8d74\u5f80\u4e10\u5e2e\uff0c\u5e2e\u4e3b\u9646\u6606\u4ed1\u4e00\u89c1\u4e86\u4ed6\uff0c\u4fbf\u5373\u8bf4 \u9053\uff1a\u201c\u6211\u6b63\u8981\u8bf7\u4f60\u6765\uff0c\u4f60\u5927\u6982\u662f\u4e3a\u4e86\u4ee4\u5a9b\u7684\u4e8b\u60c5\u6765\u627e\u6211\u7684\uff0c\u662f\u4e48\uff1f\u201d \u3000\u3000\u97e9\u5927\u7ef4\u5403\u4e86\u4e00\u60ca\uff0c\u8fde\u5fd9\u95ee\u9053\uff1a\u201c\u4f69\u745b\u513f\u51fa\u4e86\u4ec0\u4e48\u4e8b\uff1f\u4f60\u4eec\u5df2\u7ecf\u77e5\u9053\u4e86\uff1f\u201d \u3000\u3000\u9646\u6606\u4ed1\u9053\uff1a\u201c\u97e9\u5927\u53d4\uff0c\u4f60\u83ab\u7740\u6025\uff0c\u8bf7\u8fdb\u91cc\u9762\u8bf4\u8bdd\u3002\u201d \u3000\u3000\u8fdb\u4e86\u4e00\u95f4\u5bc6\u5ba4\uff0c\u53ea\u89c1\u5218\u8d76\u9a74\u966a\u7740\u4e00\u4e2a\u8001\u5934\u548c\u4e00\u4e2a\u5c11\u5973\uff0c\u6b63\u5728\u91cc\u9762\u7b49\u5019\u4ed6\u4eec\u3002\u5218\u8d76\u9a74\u9053\uff1a \u201c\u8fd9\u4f4d\u8001\u5148\u751f\u5c31\u662f\u90a3\u4f4d\u97e9\u59d1\u5a18\u7684\u7236\u4eb2\u4e86\u3002\u201d\u90a3\u4e2a\u8001\u5934\u548c\u5c11\u5973\u77e5\u9053\u6765\u7684\u662f\u9f0e\u9f0e\u5927\u540d\u7684\u6b66\u6797\u524d\u8f88 \u97e9\u5927\u7ef4\uff0c\u90fd\u7ad9\u8d77\u8eab\u6765\u3002 \u3000\u3000\u97e9\u5927\u7ef4\u9053\uff1a\u201c\u4ed6\u4eec\u662f\u2014\u2014\u201d \u3000\u3000\u9646\u6606\u4ed1\u9053\uff1a\u201c\u4ed6\u4eec\u5c31\u662f\u521a\u521a\u6765\u5230\u7684\u901a\u98ce\u62a5\u8baf\u7684\u4eba\u3002\u201d \u3000\u3000\u97e9\u5927\u7ef4\u5fc3\u6025\u5982\u711a\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u5fc5\u5ba2\u6c14\uff0c\u8bf7\u4f60\u4eec\u5feb\u70b9\u544a\u8bc9\u6211\u5427\u3002\u201d \u3000\u3000\u90a3\u8001\u5934\u9053\uff1a\u201c\u6211\u4eec\u662f\u5728\u5929\u6865\u5356\u827a\u7684\uff0c\u8fd9\u4ef6\u4e8b\u53d1\u751f\u7684\u65f6\u5019\uff0c\u6211\u4eec\u6b63\u597d\u5728\u573a\u3002\u201d \u3000\u3000\u539f\u6765\u8fd9\u5bf9\u7236\u5973\u5c31\u662f\u97e9\u4f69\u745b\u62c9\u6768\u5a49\u8fc7\u53bb\u60f3\u7ed9\u4ed6\u4eec\u6367\u573a\u7684\u90a3\u5bf9\u7236\u5973\uff0c\u540e\u6765\u97e9\u4f69\u745b\u8fd8\u201c\u62a2\u201d\u4e86 \u90a3\u4e2a\u5356\u827a\u59d1\u5a18\u7684\u4e24\u628a\u67f3\u53f6\u5200\uff0c\u4e0e\u963f\u535c\u5362\u3001\u547c\u9ece\u5962\u5c55\u5f00\u6076\u6597\u7684\u3002 \u3000\u3000\u8fd9\u4e24\u7236\u5973\u867d\u7136\u4e0d\u662f\u4e10\u5e2e\u7684\u4eba\uff0c\u4f46\u5929\u6865\u4e09\u6559\u4e5d\u6d41\u7684\u4eba\u7269\uff0c\u90fd\u4e0e\u4e10\u5e2e\u6709\u6216\u591a\u6216\u5c11\u7684\u5173\u7cfb\uff0c\u8fd9 \u4e24\u7236\u5973\u66f4\u662f\u5e38\u5728\u4e10\u5e2e\u51fa\u5165\uff0c\u548c\u5927\u90fd\u7684\u4e10\u5e2e\u5206\u8235\u8235\u4e3b\u5218\u8d76\u9a74\u8ba4\u8bc6\u7684\u3002\u662f\u4ee5\u5728\u8fd9\u4ef6\u4e8b\u60c5\u8fc7\u540e\uff0c\u4ed6 \u4eec\u9a6c\u4e0a\u5c31\u8dd1\u6765\u4e10\u5e2e\u62a5\u544a\u4e86\u3002 \u3000\u3000\u97e9\u5927\u7ef4\u542c\u4e86\u4ed6\u4eec\u6240\u8bf4\u7684\u7ecf\u8fc7\uff0c\u8bf4\u9053\uff1a\u201c\u662f\u4e00\u4e2a\u80a5\u5934\u80d6\u8033\u7684\u548c\u5c1a\u6349\u4e86\u5979\u4eec\uff0c\u91d1\u5175\u53c8\u5e2e\u90a3\u4e9b \u4eba\u6b3a\u4fae\u6211\u4f69\u745b\u513f\uff1b\u540e\u6765\u53c8\u662f\u91d1\u5175\u5c06\u5979\u4eec\u62bc\u89e3\u56de\u53bb\u7684\u3002\u8fd9\u6837\u8bf4\u6765\uff0c\u8fd9\u4e2a\u548c\u5c1a\u5fc5\u5b9a\u662f\u8499\u53e4\u7684\u56fd\u5e08 \u9f99\u8c61\u50cf\u6cd5\u738b\uff01\u201d \u3000\u3000\u9646\u6606\u4ed1\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4ee5\u4f69\u745b\u4f84\u5973\u548c\u90a3\u4f4d\u6768\u59d1\u5a18\u7684\u672c\u9886\uff0c\u9664\u4e86\u8fd9\u4e2a\u79c3\u9a74\uff0c\u6050\u6015\u4e5f\u6ca1\u6709\u7b2c\u4e8c \u4e2a\u4eba\u80fd\u591f\u5728\u4e09\u62db\u4e24\u5f0f\u4e4b\u95f4\u5c06\u5979\u4eec\u62ff\u4e0b\u3002\u770b\u6765\u90a3\u4e2a\u5411\u6768\u59d1\u5a18\u6311\u8845\u7684\u5e26\u5916\u5730\u53e3\u97f3\u7684\u5c11\u5e74\uff0c\u6050\u6015\u4e5f \u5c31\u662f\u8499\u53e4\u7684\u56db\u738b\u5b50\u62d6\u96f7\u4e86\u3002\u201d \u3000\u3000\u97e9\u5927\u7ef4\u53c8\u662f\u5403\u60ca\uff0c\u53c8\u662f\u7740\u6025\uff0c\u8bf4\u9053\uff1a\u201c\u82e5\u7136\u54b1\u4eec\u6240\u6599\u4e0d\u5dee\uff0c\u5979\u4eec\u73b0\u5728\u4e00\u5b9a\u662f\u88ab\u56da\u5728\u9633\u5929 \u96f7\u7684\u56fd\u5e08\u5e9c\u91cc\uff0c\u600e\u4e48\u529e\u5462\uff1f\u201d \u3000\u3000\u9646\u6606\u4ed1\u9053\uff1a\u201c\u54b1\u4eec\u5148\u53bb\u548c\u674e\u76df\u4e3b\u5546\u91cf\u5546\u91cf\u518d\u8bf4\uff0c\u597d\u5417\uff1f\u201d \u3000\u3000\u54ea\u77e5\u4ed6\u4eec\u56de\u5230\u97e9\u5bb6\uff0c\u674e\u601d\u5357\u4e5f\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u7b49\u5230\u5929\u660e\uff0c\u4ecd\u7136\u672a\u89c1\u674e\u601d\u5357\u56de\u6765\uff0c\u97e9\u5927\u7ef4\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u770b\u8fd9\u60c5\u5f62\uff0c\u53ea\u6015\u662f\u601d\u5357\u8d24 \u4f84\u4ea6\u5df2\u906d\u4e86\u4ed6\u4eec\u7684\u6697\u7b97\uff01\u201d \u3000\u3000\u9646\u6606\u4ed1\u9053\uff1a\u201c\u6211\u4eec\u6709\u4e24\u4e2a\u4eba\u65e9\u5c31\u5b89\u63d2\u5728\u9633\u5929\u96f7\u7684\u2018\u56fd\u5e08\u5e9c\u2019\u4e2d\uff0c\u660e\u5929\u6211\u53eb\u4eba\u548c\u4ed6\u4eec\u8054 \u7edc\uff0c\u6253\u542c\u771f\u5b9e\u7684\u60c5\u5f62\uff0c\u518d\u4f5c\u8ba1\u8f83\u3002\u201d \u3000\u3000\u8fd9\u4e24\u4e2a\u7ed9\u4e10\u5e2e\u5728\u5728\u201c\u56fd\u5e08\u5e9c\u201d\u4f5c\u201c\u5367\u5e95\u201d\u7684\u4eba\uff0c\u4e00\u4e2a\u662f\u9a6c\u592b\u8eab\u4efd\uff0c\u4e00\u4e2a\u5219\u662f\u53a8\u5b50\uff0c\u4ed6\u4eec \u662f\u53ea\u80fd\u5728\u201c\u4e0b\u4eba\u201d\u4e4b\u4e2d\u6d3b\u52a8\uff0c\u4e0d\u80fd\u8d70\u8fdb\u5185\u5802\uff0c\u4e5f\u65e0\u6cd5\u4e0e\u201c\u56fd\u5e08\u5e9c\u201d\u4e2d\u804c\u4f4d\u8f83\u9ad8\u7684\u4eba\u63a5\u8fd1\u7684\u3002 \u3000\u3000\u8fd9\u4e24\u4e2a\u4eba\u597d\u4e0d\u5bb9\u6613\u6253\u542c\u5230\u4e00\u4e9b\u6d88\u606f\uff0c\u4f20\u9012\u51fa\u6765\uff0c\u4ed6\u4eec\u53ea\u77e5\u9053\u201c\u56fd\u5e08\u5e9c\u201d\u6700\u8fd1\u56da\u7981\u4e86\u4e24\u5973 \u4e00\u7537\uff0c\u4e09\u4e2a\u4eba\u90fd\u662f\u201c\u6765\u5934\u5f88\u5927\u201d\u7684\u4eba\u7269\uff0c\u5c24\u5176\u90a3\u4e2a\u7537\u7684\uff0c\u542c\u8bf4\u662f\u8499\u53e4\u5927\u6c57\u548c\u91d1\u56fd\u7687\u5e1d\u90fd\u8981\u902e \u6355\u7684\u4eba\uff0c\u662f\u4ee5\u6349\u5230\u4e86\u4ed6\u4e4b\u540e\uff0c\u201c\u56fd\u5e08\u5e9c\u201d\u4e2d\u66fe\u7ecf\u5f00\u8fc7\u4e00\u6b21\u5e86\u529f\u5bb4\u3002\u81f3\u4e8e\u90a3\u4eba\u662f\u8c01\uff0c\u548c\u8fd9\u4e09\u4e2a \u4eba\u5173\u5728\u4ec0\u4e48\u5730\u65b9\uff0c\u4ed6\u4eec\u53ef\u6ca1\u6cd5\u77e5\u9053\uff0c\u4e5f\u4e0d\u6562\u6253\u542c\u4e86\u3002\u8fd9\u4e24\u4e2a\u4eba\u65e2\u7136\u662f\u8fde\u674e\u3001\u6768\u7b49\u4eba\u5173\u5728\u4ec0\u4e48 \u5730\u65b9\u90fd\u4e0d\u77e5\u9053\uff0c\u5f53\u7136\u5c31\u66f4\u8c08\u4e0d\u4e0a\u53ef\u4ee5\u8bbe\u6cd5\u5c06\u4ed6\u4eec\u6551\u51fa\u6765\u4e86\u3002 \u3000\u3000\u4e0d\u8fc7\u8fd9\u6d88\u606f\u4e5f\u5f88\u6709\u7528\u5904\u3002\u7b2c\u4e00\uff0c\u8bc1\u5b9e\u4e86\u674e\u601d\u5357\u786e\u5b9e\u662f\u88ab\u5173\u5728\u9633\u5929\u96f7\u7684\u201c\u56fd\u5e08\u5e9c\u201d\u91cc\uff0c\u4e10 \u5e2e\u53ef\u4ee5\u4e0d\u5fc5\u832b\u65e0\u5934\u7eea\u7684\u53bb\u627e\u674e\u601d\u5357\u4e86\u3002\u7b2c\u4e8c\uff0c\u674e\u601d\u5357\u548c\u6768\u5a49\u3001\u97e9\u4f69\u745b\u90fd\u8fd8\u6d3b\u7740\uff0c\u53ef\u4ee5\u731c\u60f3\u5f97 \u5230\uff0c\u62d6\u96f7\u56da\u7981\u4ed6\u4eec\u7684\u7528\u610f\uff0c\u5f53\u7136\u662f\u60f3\u4ee4\u4ed6\u4eec\u5c48\u670d\uff0c\u76ee\u7684\u672a\u8fbe\uff0c\u7edd\u4e0d\u4f1a\u8f7b\u6613\u5c06\u4ed6\u4eec\u6740\u6389\u3002\u662f\u4ee5 \u97e9\u5927\u7ef4\u6682\u65f6\u4e5f\u53ef\u4ee5\u4e0d\u5fc5\u4e3a\u5973\u513f\u4e0e\u674e\u601d\u5357\u7684\u6027\u547d\u62c5\u5fe7\u4e86\u3002 \u3000\u3000\u9646\u6606\u4ed1\u9053\uff1a\u201c\u97e9\u5927\u53d4\uff0c\u6211\u77e5\u9053\u4f60\u5f88\u7740\u6025\uff0c\u6211\u4e5f\u7edd\u4e0d\u80fd\u8ba9\u4f84\u5973\u4e0e\u674e\u76df\u4e3b\u592b\u59bb\u9677\u8eab\u9b54\u7a9f\uff0c\u65e0 \u8bba\u5982\u4f55\uff0c\u90fd\u8981\u8bbe\u6cd5\u628a\u4ed6\u4eec\u6551\u51fa\u6765\u7684\uff0c\u4e0d\u8fc7\uff0c\u76ee\u524d\u8fd8\u6ca1\u6709\u9002\u5f53\u7684\u65f6\u673a\u3002\u6211\u7684\u610f\u601d\u662f\u82e5\u7136\u80fd\u591f\u907f \u514d\u6253\u8349\u60ca\u86c7\uff0c\u90a3\u5c31\u6700\u597d\u3002\u201d \u3000\u3000\u97e9\u5927\u7ef4\u9053\uff1a\u201c\u542c\u8bf4\u9633\u5929\u96f7\u7ea6\u4e86\u67f3\u6d1e\u5929\u3001\u5d14\u9547\u5c71\u4e8c\u4eba\u5728\u4e0b\u4e2a\u6708\u5341\u4e94\u65e5\u4e4b\u524d\u5728\u4ed6\u7684\u2018\u56fd\u5e08 \u5e9c\u2019\u4f1a\u9762\u3002\u201d \u3000\u3000\u9646\u6606\u4ed1\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u6240\u8bf4\u7684\u9002\u5f53\u65f6\u673a\u5c31\u662f\u6307\u8fd9\u4e00\u4ef6\u4e8b\u60c5\u3002\u636e\u674e\u76df\u4e3b\u5e26\u6765\u7684\u6d88\u606f\u8bf4\uff0c\u6709 \u4e24\u4e2a\u672c\u9886\u5f88\u4e3a\u4e86\u5f97\u7684\u5c11\u5e74\u82f1\u96c4\u4e5f\u5c06\u5192\u5145\u4ed6\u4eec\u7684\u968f\u4ece\u540c\u5f80\u56fd\u5e08\u5e9c\u7684\uff0c\u8fd9\u4e24\u4e2a\u4eba\u4e00\u4e2a\u53eb\u891a\u4e91\u5cf0\uff0c \u4e00\u4e2a\u53eb\u505a\u8c37\u6db5\u865a\uff0c\u4ed6\u4eec\u672c\u6765\u90fd\u662f\u9633\u5929\u96f7\u7684\u5e08\u4f84\uff0c\u6b64\u6b21\u524d\u6765\uff0c\u4e43\u662f\u4e3a\u4e86\u6e05\u7406\u5e08\u95e8\u3002\u201d \u3000\u3000\u97e9\u5927\u7ef4\u9053\uff1a\u201c\u54b1\u4eec\u73b0\u5728\u4eba\u624b\u4e0d\u591f\u3002\u4e0d\u8fc7\uff0c\u5c31\u662f\u4ed6\u4eec\u6765\u4e86\uff0c\u53ea\u6015\u4e5f\u53ea\u80fd\u5bf9\u4ed8\u9633\u5929\u96f7\uff0c\u5bf9\u4ed8 \u4e0d\u4e86\u9f99\u8c61\u6cd5\u738b\u5427\uff1f\u201d \u3000\u3000\u9646\u6606\u4ed1\u7b11\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u4f4d\u6c5f\u5357\u5927\u4fa0\u5b5f\u5c11\u521a\u542c\u8bf4\u4e5f\u8981\u6765\u5462\u3002\u201d \u3000\u3000\u97e9\u5927\u7ef4\u559c\u9053\uff1a\u201c\u82e5\u662f\u5b5f\u5c11\u521a\u6765\u4e86\uff0c\u4e8b\u60c5\u5c31\u6bd4\u8f83\u5bb9\u6613\u529e\u4e86\uff0c\u751a\u81f3\u6211\u53ef\u4ee5\u548c\u4ed6\u5230\u9633\u5929\u96f7\u7684\u56fd \u5e08\u5e9c\u91cc\uff0c\u7a81\u65bd\u6697\u88ad\uff0c\u5c06\u62d6\u96f7\u62ff\u6765\u4f5c\u4e3a\u4ea4\u6362\u7684\u4eba\u8d28\u3002\u201d \u3000\u3000\u67f3\u6d1e\u5929\u662f\u8981\u5728\u4f1a\u671f\u4e4b\u524d\u6709\u6240\u5b89\u6392\u7684\uff0c\u56e0\u6b64\u4ed6\u4eec\u548c\u9633\u5929\u96f7\u7ea6\u597d\u662f\u5728\u5341\u4e94\u65e5\u4e4b\u524d\u89c1\u9762\uff0c\u521d\u4e09 \u90a3\u5929\uff0c\u4ed6\u4eec\u4fbf\u6765\u5230\u5927\u90fd\u4e86\uff0c\u8fd9\u5929\u662f\u674e\u601d\u5357\u7b49\u4eba\u88ab\u56da\u7684\u7b2c\u516d\u5929\u3002 \u3000\u3000\u53ef\u662f\u5b5f\u5c11\u521a\u5374\u6ca1\u6709\u548c\u4ed6\u4eec\u540c\u6765\u3002 \u3000\u3000\u67f3\u6d1e\u5929\u3001\u891a\u4e91\u5cf0\u7b49\u4eba\u5230\u4e86\u4e10\u5e2e\uff0c\u542c\u5230\u4e86\u674e\u601d\u5357\u88ab\u56da\u7684\u6d88\u606f\uff0c\u8fd9\u4e00\u60ca\u5f53\u771f\u662f\u975e\u540c\u5c0f\u53ef\u3002 \u3000\u3000\u67f3\u6d1e\u5929\u8bf4\u9053\uff1a\u201c\u5b5f\u5927\u4fa0\u56e0\u4e3a\u6709\u70b9\u4e8b\u60c5\uff0c\u5148\u8981\u56de\u7405\u739b\u5c71\u53bb\uff0c\u4ed6\u662f\u8bf4\u8fc7\u8fd9\u4e2a\u6708\u5341\u4e94\u4e4b\u524d\uff0c\u5f53 \u53ef\u62b5\u8fbe\u5927\u90fd\u3002\u4e0d\u8fc7\uff0c\u82e5\u662f\u6709\u4ec0\u4e48\u610f\u5916\u803d\u6401\uff0c\u90a3\u5c31\u96be\u8bf4\u4e86\uff01\u201d \u3000\u3000\u9646\u6606\u4ed1\u9053\uff1a\u201c\u4f60\u4eec\u8fd9\u6b21\u6765\u5230\u5927\u90fd\uff0c\u9633\u5929\u96f7\u90a3\u8fb9\u7684\u53ef\u6709\u4eba\u77e5\u9053\uff1f\u201d \u3000\u3000\u67f3\u6d1e\u5929\u9053\uff1a\u201c\u6211\u4eec\u6628\u665a\u9ec4\u660f\u65f6\u5206\u6084\u6084\u8fdb\u57ce\uff0c\u4e00\u8def\u7559\u795e\uff0c\u5e76\u6ca1\u53d1\u73b0\u6709\u53ef\u7591\u7684\u4eba\u7269\u8ddf\u8e2a\u3002\u201d \u3000\u3000\u9646\u6606\u4ed1\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4f60\u4eec\u5c31\u8bbe\u6cd5\u62d6\u4ed6\u51e0\u5929\u3002\u6d3e\u4e00\u4e2a\u4eba\u9001\u4fe1\u7ed9\u9633\u5929\u96f7\uff0c\u8bf4\u662f\u4f60\u4eec\u6050\u6015\u4e0d\u80fd \u5982\u671f\u8d76\u5230\u3002\u5047\u5982\u5b5f\u5927\u4fa0\u5341\u4e94\u65e5\u4e4b\u524d\u6765\uff0c\u4f60\u4eec\u5c31\u4f9d\u65f6\u8d74\u7ea6\uff0c\u5426\u5219\u518d\u62d6\u4e0b\u53bb\u3002\u7b49\u5b5f\u5927\u4fa0\u6765\u4e86\u518d \u8bf4\u3002\u201d \u3000\u3000\u67f3\u6d1e\u5929\u9053\uff1a\u201c\u5b5f\u5927\u4fa0\u82e5\u7136\u77e5\u9053\u674e\u76df\u4e3b\u88ab\u56da\uff0c\u4ed6\u4e00\u5b9a\u4f1a\u706b\u901f\u8d76\u6765\u7684\u3002\u5bf9\u5566\uff0c\u9646\u5e2e\u4e3b\uff0c\u4f60\u53ef \u5411\u7405\u739b\u5c71\u62a5\u4fe1\u6ca1\u6709\uff1f\u201d \u3000\u3000\u9646\u6606\u4ed1\u9053\uff1a\u201c\u6211\u4ee5\u4e3a\u5b5f\u5927\u4fa0\u4e0e\u4f60\u4eec\u4e00\u8d77\u6765\u7684\uff0c\u662f\u4ee5\u5c1a\u672a\u6709\u4e13\u4eba\u9001\u4fe1\u3002\u4e0d\u8fc7\uff0c\u6211\u53ef\u4ee5\u7528\u98de \u9e3d\u4f20\u4e66\u7684\u65b9\u6cd5\uff0c\u9001\u5230\u8ddd\u79bb\u7405\u739b\u5c71\u6700\u8fd1\u7684\u4e00\u4e2a\u672c\u5e2e\u5206\u8235\uff0c\u53eb\u4ed6\u4eec\u7acb\u5373\u6d3e\u4eba\u5230\u7405\u739b\u5c71\u62a5\u8baf\u53bb\u3002\u201d \u3000\u3000\u201c\u98de\u9e3d\u4f20\u4fe1\u201d\u5f53\u7136\u662f\u6709\u70b9\u5192\u9669\u7684\uff0c\u201c\u4fe1\u9e3d\u201d\u53ef\u80fd\u7ed9\u4eba\u6253\u843d\uff0c\u4e5f\u53ef\u80fd\u98de\u4e0d\u5230\u76ee\u7684\u5730\u3002\u4e0d \u8fc7\uff0c\u5f53\u65f6\u65e2\u7136\u6ca1\u6709\u7b2c\u4e8c\u79cd\u66f4\u5feb\u6377\u7684\u9001\u4fe1\u529e\u6cd5\uff0c\u4e5f\u5c31\u53ea\u597d\u501a\u9760\u4fe1\u9e3d\u4e86\u3002 \u3000\u3000\u9646\u6606\u4ed1\u9053\uff1a\u201c\u6211\u4eec\u8fd8\u53ef\u4ee5\u53cc\u7ba1\u9f50\u4e0b\uff0c\u4e00\u9762\u50ac\u4fc3\u5b5f\u5927\u4fa0\u5feb\u6765\uff0c\u4e00\u9762\u52a0\u7d27\u8bbe\u6cd5\u6253\u542c\u674e\u76df\u4e3b\u548c \u6768\u59d1\u5a18\u88ab\u56da\u7684\u5904\u6240\uff0c\u5fc5\u8981\u4e4b\u65f6\uff0c\u5927\u4e3e\u52ab\u72f1\u4e5f\u987e\u4e0d\u5f97\u3002\u201d \u3000\u3000\u4e10\u5e2e\u5f1f\u5b50\u5728\u5927\u90fd\u7684\u6709\u6570\u5343\u4eba\u4e4b\u591a\uff0c\u5018\u8981\u5927\u4e3e\u52ab\u72f1\uff0c\u5e76\u975e\u6ca1\u6709\u8fd9\u4e2a\u53ef\u80fd\uff0c\u4e0d\u8fc7\u5982\u6b64\u4e00\u6765\uff0c \u4e10\u5e2e\u4e5f\u5c31\u4e0d\u80fd\u5728\u5927\u90fd\u7acb\u8db3\u4e86\uff0c\u8fd9\u662f\u7275\u8fde\u5230\u6570\u5343\u4eba\u7684\u4e8b\u60c5\uff0c\u662f\u4ee5\u9646\u6606\u4ed1\u4e0d\u80fd\u4e0d\u614e\u91cd\u8003\u8651\u3002 \u3000\u3000\u97e9\u5927\u7ef4\u867d\u7136\u7740\u6025\uff0c\u4f46\u4e5f\u662f\u4e2a\u7a33\u91cd\u7684\u4eba\uff0c\u4ed6\u4e0d\u80fd\u4e3a\u4e86\u8981\u6551\u5973\u513f\u7d2f\u5f97\u4e10\u5e2e\u4e0d\u80fd\u5728\u5927\u90fd\u7acb\u8db3\uff0c \u8bf4\u9053\uff1a\u201c\u82e5\u662f\u6253\u542c\u5230\u4ed6\u4eec\u88ab\u56da\u7684\u5904\u6240\uff0c\u6211\u62fc\u4e86\u8001\u547d\u5077\u5077\u53bb\u52ab\u72f1\u5c31\u662f\uff0c\u51b3\u4e0d\u80fd\u8ba9\u5927\u4f19\u513f\u5377\u5165\u65cb \u6da1\u3002\u201d \u3000\u3000\u201c\u56fd\u5e08\u5e9c\u201d\u5730\u65b9\u5f88\u5927\uff0c\u79d8\u5bc6\u673a\u5173\u4e5f\u591a\uff0c\u82e5\u4e0d\u662f\u6253\u542c\u5230\u88ab\u95ea\u7684\u5904\u6240\uff0c\u5c31\u8f7b\u4e3e\u5984\u52a8\u8fdb\u53bb\u52ab\u72f1 \u7684\u8bdd\uff0c\u4e00\u5b9a\u4e0d\u80fd\u6210\u529f\uff0c\u891a\u4e91\u5cf0\u66fe\u7ecf\u5728\u201c\u56fd\u5e08\u5e9c\u201d\u91cc\u5f53\u8fc7\u5dee\uff0c\u8fd9\u4e00\u70b9\u4ed6\u662f\u5341\u5206\u660e\u767d\u7684\u3002\u5f53\u4e0b\u5c31 \u95ee\u9646\u6606\u4ed1\u9053\uff1a\u201c\u9646\u5e2e\u4e3b\uff0c\u4f60\u53ef\u6709\u628a\u63e1\u6253\u542c\u5f97\u5230\u4ed6\u4eec\u88ab\u56da\u7684\u5904\u6240\u5417\uff1f\u201d \u3000\u3000\u9646\u6606\u4ed1\u9053\uff1a\u201c\u6211\u4eec\u6709\u4e24\u4e2a\u4eba\u5728\u2018\u56fd\u5e08\u5e9c\u2019\u5367\u5e95\uff0c\u4ed6\u4eec\u4e0d\u597d\u6253\u542c\uff0c\u4e0d\u8fc7\u4e5f\u53ef\u4ee5\u53eb\u4ed6\u4eec\u591a\u5148 \u7559\u795e\uff0c\u5e0c\u671b\u80fd\u591f\u5728\u65e0\u610f\u4e2d\u5f97\u5230\u6d88\u606f\u3002\u53e6\u65b9\u9762\u6211\u4e5f\u6b63\u5728\u8bbe\u6cd5\u591a\u5b89\u63d2\u51e0\u4e2a\u4eba\u8fdb\u53bb\u3002\u201d\u8fd9\u6837\u8bf4\u4ea6\u5373 \u662f\u628a\u63e1\u4e0d\u5927\uff0c\u5e0c\u671b\u751a\u5fae\u4e86\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u66fe\u7ecf\u5728\u201c\u56fd\u5e08\u5e9c\u201d\u91cc\u4f4f\u8fc7\u4e09\u5e74\uff0c\u91cc\u9762\u7684\u60c5\u5f62\u4ed6\u662f\u5341\u5206\u719f\u6089\u7684\uff0c\u542c\u4e86\u9646\u6606\u4ed1\u7684\u8a00 \u8bed\uff0c\u5fc3\u91cc\u6709\u4e86\u4e00\u4e2a\u5ff5\u5934\uff0c\u4e0d\u8fc7\u4ed6\u6ca1\u6709\u8bf4\u51fa\u6765\u3002 \u3000\u3000\u7b2c\u4e8c\u5929\u9646\u6606\u4ed1\u7ed9\u67f3\u6d1e\u5929\u627e\u4e86\u4e00\u4e2a\u4eba\u5f53\u4f5c\u662f\u4ed6\u4ece\u5c71\u5be8\u6d3e\u6765\u7684\u4f7f\u8005\uff0c\u9001\u4fe1\u7ed9\u9633\u5929\u96f7\u3002\u9633\u5927\u96f7 \u7684\u7b54\u590d\u662f\u53ef\u4ee5\u5ef6\u671f\u5230\u6708\u5e95\uff0c\u6708\u5e95\u4e4b\u524d\uff0c\u5fc5\u987b\u4f1a\u89c1\u3002\u5426\u5219\u8fd9\u4e2a\u7ea6\u4f1a\u5c31\u53ea\u80fd\u53d6\u6d88\u4e86\uff0c\u56e0\u4e3a\u4ed6\u4e0b\u4e2a \u6708\u8981\u505a\u91d1\u56fd\u7684\u4f7f\u8005\uff0c\u5230\u8499\u53e4\u201c\u7b54\u8c22\u201d\uff0c\u5f53\u7136\u662f\u4e0d\u80fd\u7b49\u5f85\u67f3\u6d1e\u5929\u4e86\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u6708\u5e95\u4e4b\u524d\uff0c\u5b5f\u5927\u4fa0\u5e94\u8be5\u53ef\u4ee5\u8d76\u5230\uff0c\u4e0d\u8fc7\u8def\u9014\u9065\u8fdc\uff0c\u9014\u4e2d\u6709\u5426\u610f\u5916\u803d\u6401\uff0c\u90a3 \u4e5f\u5f88\u96be\u8bf4\u3002\u82e5\u662f\u5b5f\u5927\u4fa0\u4e0d\u80fd\u5982\u671f\u8d76\u5230\uff0c\u54b1\u4eec\u5c31\u53ea\u6709\u53e6\u60f3\u529e\u6cd5\u4e86\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u542c\u4f60\u8fd9\u4e48\u8bf4\uff0c\u4f60\u4f3c\u4e4e\u5df2\u6709\u6210\u7af9\u5728\u80f8\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u6211\u4e5f\u6ca1\u6709\u60f3\u51fa\u4ec0\u4e48\u597d\u529e\u6cd5\uff0c\u4e0d\u8fc7\uff0c\u60f3\u51fa\u53bb\u78b0\u78b0\u8fd0\u6c14\uff0c\u627e\u627e\u719f\u4eba\u3002\u201d \u3000\u3000\u5176\u5b9e\u891a\u4e91\u5cf0\u662f\u5df2\u7ecf\u6709\u4e86\u4e00\u4e2a\u4e3b\u610f\u7684\uff0c\u4f46\u6015\u9646\u6606\u4ed1\u4e0d\u4f1a\u8d5e\u540c\uff01\u662f\u4ee5\u5728\u65f6\u673a\u672a\u66fe\u6210\u719f\u4e4b\u524d\uff0c \u4e0d\u4fbf\u8bf4\u51fa\u3002\u4ed6\u7684\u8fd9\u756a\u8bf4\u8bdd\uff0c\u4e43\u662f\u4e3a\u4e86\u51c6\u5907\u81ea\u5df1\u5c06\u6765\u5355\u72ec\u5916\u51fa\uff0c\u5411\u4e3b\u4eba\u5148\u4f5c\u4e00\u4e2a\u4ea4\u4ee3\u7684\u3002 \u3000\u3000\u7fa4\u8c6a\u5728\u5927\u90fd\u7126\u6025\u7684\u7b49\u5f85\u5b5f\u5c11\u521a\uff0c\u6682\u4e14\u6309\u4e0b\u4e0d\u8868\uff0c\u4e14\u8bf4\u660e\u6167\u516c\u4e3b\u5728\u7405\u739b\u5c71\u4e0a\u5e26\u53d1\u4fee\u884c\uff0c\u505a \u4e86\u4e86\u7f18\u5e08\u592a\u7684\u201c\u8bb0\u540d\u5f1f\u5b50\u201d\uff0c\u4e86\u7f18\u5e08\u592a\u672c\u662f\u5c60\u767e\u57ce\u592b\u5987\u7684\u597d\u53cb\uff0c\u5c60\u767e\u57ce\u751f\u524d\uff0c\u5979\u5c31\u6765\u4e86\u7405\u739b \u5c71\u7684\u3002\u5c60\u767e\u57ce\u628a\u6b64\u5c71\u8352\u5e9f\u4e86\u7684\u836f\u738b\u5e99\u8ba9\u7ed9\u5979\u505a\u5c3c\u59d1\u5eb5\uff0c\u5e73\u751f\u6ca1\u6709\u6536\u8fc7\u5f1f\u5b50\u3002\u660e\u6167\u516c\u4e3b\u672c\u6765\u60f3 \u524a\u53d1\u4e3a\u5c3c\uff0c\u6b63\u5f0f\u62dc\u5e08\u7684\u3002\u4e86\u7f18\u5e08\u592a\u77e5\u9053\u5979\u662f\u8499\u53e4\u516c\u4e3b\u7684\u8eab\u4efd\uff0c\u4e0d\u80af\u7b54\u5e94\uff0c\u5c60\u51e4\u7b49\u4eba\u4e5f\u52a0\u529d \u963b\uff0c\u82e6\u529d\u5979\u4e0d\u53ef\u524a\u53d1\u4e3a\u5c3c\u3002\u56e0\u6b64\u7ed3\u679c\u53ea\u662f\u5e26\u53d1\u4fee\u884c\uff0c\u4f5c\u4e3a\u4e86\u7f18\u5e08\u592a\u7684\u8bb0\u540d\u5f1f\u5b50\u3002 \u3000\u3000\u4e86\u7f18\u5e08\u5927\u662f\u5c60\u767e\u57ce\u592b\u5987\u7684\u670b\u53cb\uff0c\u5e74\u8f7b\u65f6\u5019\uff0c\u4e5f\u66fe\u662f\u6c5f\u6e56\u4e0a\u4e00\u4e2a\u6709\u540d\u7684\u5973\u4fa0\uff0c\u5728\u7405\u739b\u5c71\u4e0a \u95ed\u95e8\u4fee\u70bc\u4e86\u51e0\u5341\u5e74\uff0c\u6b66\u529f\u7cbe\u8fdb\uff0c\u5df2\u8fbe\u5230\u6df1\u4e0d\u53ef\u6d4b\u7684\u5730\u6b65\u3002\u660e\u6167\u516c\u4e3b\u867d\u7136\u53ea\u662f\u5979\u7684\u201c\u8bb0\u540d\u5f1f \u5b50\u201d\uff0c\u4f46\u56e0\u5979\u5e76\u65e0\u8863\u94b5\u4f20\u4eba\uff0c\u662f\u4ee5\u5728\u6b66\u5b66\u4e0a\u4ea6\u662f\u4e0d\u60dc\u503e\u56ca\u4f20\u6388\u3002 \u3000\u3000\u660e\u6167\u516c\u4e3b\u8ddf\u4e86\u5979\u65e9\u665a\u5ff5\u7ecf\uff0c\u65e5\u95f4\u4e60\u6b66\uff0c\u51e0\u4e2a\u6708\u6765\uff0c\u5f97\u76ca\u4e0d\u5c11\u3002\u5361\u6d1b\u4e1d\u5e38\u5e38\u6765\u966a\u4f34\u5979\uff0c\u65e5 \u5b50\u8fc7\u5f97\u5e76\u4e0d\u5bc2\u5bde\u3002 \u3000\u3000\u660e\u6167\u516c\u4e3b\u5f88\u6ee1\u8db3\u4e8e\u8fd9\u6837\u7684\u751f\u6d3b\uff0c\u89c9\u5f97\u6bd4\u5728\u8499\u53e4\u505a\u516c\u4e3b\u7684\u65f6\u5019\u597d\u5f97\u591a\u4e86\u3002 \u3000\u3000\u4e0d\u6599\u6b63\u5728\u5979\u5fc3\u60c5\u5e73\u9759\uff0c\u81ea\u613f\u4ee5\u9752\u8863\u6728\u9c7c\u4e86\u6b64\u4e00\u751f\u7684\u65f6\u5019\uff0c\u5374\u6709\u4e00\u4ef6\u610f\u60f3\u4e0d\u5230\u7684\u4e8b\u60c5\uff0c\u53c8 \u5728\u5979\u5e73\u9759\u7684\u5fc3\u6e56\u7ffb\u8d77\u6ce2\u6d6a\u3002 \u3000\u3000\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u5c31\u662f\u4e10\u5e2e\u7684\u98de\u9e3d\u4f20\u4e66\u4e86\u3002 \u3000\u3000\u8fd9\u4e00\u5929\u6070\u597d\u5c60\u51e4\u5230\u836f\u738b\u5e99\u6765\u63a2\u8bbf\u5979\uff0c\u4e24\u4eba\u6b63\u5728\u8c08\u5929\u8bf4\u5730\u4e4b\u9645\uff0c\u3002\u5ffd\u6709\u4e00\u4e2a\u4e2b\u9b1f\u6765\u7980\u62a5\uff0c \u8bf7\u5c60\u51e4\u7acb\u5373\u56de\u53bb\u3002 \u3000\u3000\u5c60\u51e4\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\u60c5\uff0c\u8fd9\u6837\u7740\u6025\uff1f\u201d \u3000\u3000\u90a3\u5934\u76ee\u9053\uff1a\u201c\u5c71\u5be8\u91cc\u6536\u5230\u4e86\u4e10\u5e2e\u7684\u98de\u9e3d\u4f20\u4e66\u3002\u201d \u3000\u3000\u5c60\u51e4\u8be7\u9053\uff1a\u201c\u4e10\u5e2e\u867d\u7136\u548c\u54b1\u4eec\u5e38\u6709\u6765\u5f80\uff0c\u5374\u65e0\u7279\u522b\u4ea4\u60c5\uff0c\u4f55\u4ee5\u4ed6\u4eec\u8981\u7528\u5230\u98de\u9e3d\u4f20\u4e66\u7ed9\u54b1 \u4eec\u9001\u4fe1\uff1f\u77f3\u526f\u5be8\u4e3b\u770b\u8fc7\u90a3\u5c01\u4fe1\u4e48\uff1f\u201d\u201c\u77f3\u526f\u5be8\u4e3b\u201d\u5373\u662f\u5c60\u51e4\u7684\u672a\u5a5a\u592b\u77f3\u749e\uff0c\u5c60\u51e4\u4e0d\u5728\u5c71\u5be8\u4e4b \u65f6\uff0c\u4e00\u5207\u4e8b\u60c5\u90fd\u7531\u4ed6\u4f5c\u4e3b\u7684\u3002 \u3000\u3000\u90a3\u5934\u76ee\u4e0d\u77e5\u907f\u5fcc\uff0c\u8bf4\u9053\uff1a\u201c\u770b\u8fc7\u4e86\uff0c\u542c\u8bf4\u662f\u5173\u4e8e\u674e\u76df\u4e3b\u7684\u4e8b\u60c5\u3002\u201d \u3000\u3000\u5c60\u51e4\u5403\u4e86\u4e00\u60ca\uff0c\u8fde\u5fd9\u95ee\u9053\uff1a\u201c\u674e\u76df\u4e3b\u7684\u4ec0\u4e48\u4e8b\u60c5\uff1f\u201d \u3000\u3000\u90a3\u5934\u76ee\u9053\uff1a\u201c\u542c\u8bf4\u4ed6\u548c\u6768\u59d1\u5a18\u5728\u5927\u90fd\u51fa\u4e86\u4e8b\uff0c\u8be6\u60c5\u8bf7\u4f60\u95ee\u77f3\u526f\u5be8\u4e3b\u5427\uff0c\u6211\u6ca1\u6709\u770b\u8fc7\u90a3\u5c01 \u4fe1\u3002\u201d \u3000\u3000\u5c60\u51e4\u7ad9\u8d77\u8eab\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u660e\u6167\u59d0\u59d0\uff0c\u5bf9\u4e0d\u4f4f\uff0c\u6211\u73b0\u5728\u53ef\u8981\u56de\u53bb\u4e86\uff0c\u6539\u5929\u518d\u6765\u770b\u4f60\u3002\u201d \u3000\u3000\u4e0d\u6599\u660e\u6167\u516c\u4e3b\u4e5f\u59d1\u8d77\u8eab\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u5c60\u59d0\u59d0\uff0c\u6768\u59d1\u5a18\u4e0e\u6211\u60c5\u5982\u59d0\u59b9\uff0c\u4f60\u662f\u77e5\u9053\u7684\u3002\u4f60\u53ef \u4ee5\u8ba9\u6211\u4e5f\u53bb\u542c\u542c\u4e48\uff1f\u201d \u3000\u3000\u5c60\u51e4\u5fc3\u91cc\u660e\u767d\uff0c\u5979\u56fa\u7136\u6302\u5ff5\u6768\u5a49\uff0c\u4f46\u4ee4\u5979\u66f4\u591a\u5fe7\u8651\u7684\u6050\u6015\u8fd8\u662f\u674e\u601d\u5357\uff0c\u5fc3\u91cc\u6697\u6697\u53f9\u4e86\u53e3 \u6c14\uff0c\u60f3\u9053\uff1a\u201c\u53ef\u53f9\u5979\u867d\u7136\u5e26\u53d1\u4fee\u884c\uff0c\u4ecd\u4e0d\u80fd\u5fd8\u60c5\u7269\u5916\u3002\u201d\u4e0d\u8fc7\u660e\u6167\u516c\u4e3b\u65e2\u7136\u5f00\u4e86\u53e3\uff0c\u5c60\u51e4\u81ea \u662f\u4e0d\u4fbf\u62d2\u7edd\u4e86\u3002 \u3000\u3000\u56de\u5230\u5c71\u5bd2\uff0c\u89c1\u4e86\u77f3\u749e\uff0c\u77f3\u749e\u770b\u89c1\u660e\u6167\u516c\u4e3b\u540c\u6765\uff0c\u9887\u662f\u6709\u4e9b\u5c34\u5c2c\uff0c\u8bf4\u9053\uff1a\u201c\u516c\u4e3b\uff0c\u8bf7\u4f60\u4e0d \u8981\u4ecb\u610f\uff0c\u674e\u76df\u4e3b\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u6050\u6015\u548c\u4ee4\u5144\u6709\u70b9\u5173\u7cfb\u3002\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u5927\u5403\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u4ed6\u4e0e\u6768\u59d1\u5a18\u53c8\u4e0d\u662f\u5728\u548c\u6797\uff0c\u4ed6\u4eec\u662f\u5728\u91d1\u4eac\u5927\u90fd\uff0c\u600e\u7684\u5374\u548c \u6211\u54e5\u54e5\u6709\u5173\uff1f\u201d \u3000\u3000\u77f3\u749e\u8bf4\u9053\uff1a\u201c\u4ee4\u5144\u62d6\u96f7\u5230\u4e86\u5927\u90fd\uff0c\u636e\u8bf4\u662f\u6765\u548c\u91d1\u56fd\u8c08\u548c\u7684\u3002\u674e\u76df\u4e3b\u548c\u6768\u59d1\u5a18\u4e0d\u77e5\u600e\u7684\u7ed9 \u4ee4\u5144\u7684\u624b\u4e0b\u6349\u4e86\u53bb\uff0c\u542c\u8bf4\u73b0\u5728\u662f\u5173\u5728\u9633\u5929\u96f7\u7684\u56fd\u5e08\u5e9c\u4e2d\uff0c\u5185\u91cc\u56e0\u7531\uff0c\u4e10\u5e2e\u7684\u4fe1\u5c31\u6ca1\u6709\u8be6\u7ec6\u53d9 \u8ff0\u4e86\u3002\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u7684\u9762\u8272\u201c\u5530\u201d\u7684\u4e00\u4e0b\u5b50\u53d8\u5f97\u82cd\u767d\u5982\u7eb8\uff0c\u773c\u89d2\u6709\u6676\u83b9\u7684\u6cea\u73e0\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u3001\u6211\u771f \u662f\u60ed\u6127\uff0c\u60f3\u4e0d\u5230\u6211\u7684\u56db\u54e5\u7adf\u4f1a\u8fd9\u6837\u3002\u201d \u3000\u3000\u5c60\u51e4\u5b89\u6170\u5979\u9053\uff1a\u201c\u54e5\u54e5\u662f\u54e5\u54e5\uff0c\u59b9\u59b9\u662f\u59b9\u59b9\uff0c\u6211\u4eec\u4e0d\u4f1a\u56e0\u6b64\u654c\u89c6\u4f60\u7684\u3002\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u542b\u6cea\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u53ef\u6709\u4ec0\u4e48\u8981\u6211\u5e2e\u5fd9\u7684\u4e48\uff1f\u201d \u3000\u3000\u5c60\u51e4\u8bf4\u9053\uff1a\u201c\u4e10\u5e2e\u7684\u4fe1\u662f\u50ac\u4fc3\u5b5f\u5927\u4fa0\u8d76\u5f80\u5927\u90fd\u7684\uff0c\u5b5f\u5927\u4fa0\u65e9\u5df2\u5728\u4e09\u5929\u524d\u52a8\u8eab\u4e86\uff0c\u4ed6\u5230\u4e86 \u5927\u90fd\uff0c\u4e00\u5b9a\u4f1a\u6709\u529e\u6cd5\u7684\uff0c\u516c\u4e3b\uff0c\u4f60\u4e0d\u5fc5\u6302\u5fc3\u3002\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u5f53\u7136\u4e0d\u4f1a\u77e5\u9053\u5979\u7684\u54e5\u54e5\u8981\u5979\u6765\u548c\u6768\u5a49\u4ea4\u6362\u4e4b\u4e8b\uff0c\u4f46\u5979\u5374\u60f3\u5230\u62d6\u96f7\u8fd9\u6b21\u524d\u6765\uff0c\u5176 \u4e2d\u7684\u4e00\u4e2a\u539f\u56e0\uff0c\u4e00\u5b9a\u662f\u8981\u627e\u5979\u56de\u53bb\u3002 \u3000\u3000\u660e\u6167\u516c\u4e3b\u6697\u81ea\u601d\u91cf\uff1a\u201c\u4ed6\u4eec\u5728\u8499\u53e4\u66fe\u7ecf\u5403\u8fc7\u8bb8\u591a\u82e6\u5934\uff0c\u8bf4\u8d77\u6765\uff0c\u6211\u4e5f\u662f\u6709\u7f6a\u8fc7\u7684\u3002\u56db\u54e5 \u5bf9\u5a49\u59d0\u66fe\u7ecf\u52a8\u8fc7\u5ff5\u5934\uff0c\u8fd9\u6b21\u5a49\u59d0\u843d\u5728\u4ed6\u7684\u624b\u4e2d\uff0c\u4e0d\u77e5\u4ed6\u53c8\u8981\u6253\u4ec0\u4e48\u574f\u4e3b\u610f\u4e86\u3002\u4ed6\u4eec\u60c5\u771f\u7231 \u631a\uff0c\u6211\u66fe\u7ecf\u5bf9\u4ed6\u4eec\u72af\u8fc7\u9519\uff0c\u8fd9\u6b21\u65e0\u8bba\u5982\u4f55\u662f\u4e0d\u80fd\u8ba9\u56db\u54e5\u62c6\u6563\u4ed6\u4eec\u8fd9\u4e00\u5bf9\u9e33\u9e2f\u4e86\u3002\u201d\u8ddf\u7740\u60f3 \u9053\uff1a\u201c\u56db\u54e5\u4e00\u5411\u5bf9\u6211\u5f88\u597d\uff0c\u6211\u5411\u4ed6\u6c42\u60c5\uff0c\u8bf4\u4e0d\u5b9a\u4ed6\u4f1a\u51c6\u8bb8\uff0c\u5927\u4e0d\u4e86\u6211\u8ddf\u4ed6\u56de\u8f6c\u548c\u6797\u3002\u4e3a\u4e86\u674e \u5927\u54e5\u548c\u6768\u59d1\u5a18\uff0c\u6211\u5c31\u62fc\u7740\u8fd9\u4e00\u751f\u53d7\u82e6\u53d7\u96be\u5427\u3002\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u7684\u5fc3\u4e8b\u5e76\u6ca1\u6709\u544a\u8bc9\u5c60\u51e4\uff0c\u5c60\u51e4\u662f\u4e2a\u8c6a\u8fc8\u5dfe\u5e3c\u82f1\u96c4\uff0c\u4e0d\u514d\u6709\u70b9\u7c97\u5fc3\u5927\u610f\u7684\u7f3a\u70b9\uff0c \u5979\u4e5f\u6ca1\u6709\u60f3\u5230\u660e\u6167\u516c\u4e3b\u6709\u8fd9\u6837\u590d\u6742\u7684\u4e8b\u3002\u6b63\u662f\uff1a \u3000\u3000\u4e00\u7f15\u67d4\u60c5\u65e0\u5904\u6258\uff0c\u4e3a\u916c\u77e5\u5df1\u5165\u6a0a\u7b3c\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b\u2014\u2014\u7b2c\u4e94\u5341\u4e8c\u56de\u3000\u798f\u6167\u53cc\u4fee\u6210\u68a6\u60f3\u3000\u6069\u4ec7\u4e00\u6289\u60dc\u4f73\u4eba \u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b \u7b2c\u4e94\u5341\u4e8c\u56de\u3000\u798f\u6167\u53cc\u4fee\u6210\u68a6\u60f3\u3000\u6069\u4ec7\u4e00\u6289\u60dc\u4f73\u4eba \u3000\u3000\u660e\u6167\u516c\u4e3b\u56de\u5230\u5eb5\u4e2d\uff0c\u5361\u6d1b\u4e1d\u6b63\u5de7\u6765\u627e\u5979\uff0c\u8bf4\u9053\uff1a\u201c\u542c\u8bf4\u4f60\u5230\u5c71\u5be8\u53bb\u4e86\uff0c\u6211\u521a\u60f3\u56de\u53bb\u627e\u4f60 \u5462\u3002\u4ec0\u4e48\u4e8b\u60c5\uff1f\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u9053\uff1a\u201c\u4f60\u5148\u522b\u95ee\uff0c\u8bf7\u4f60\u8d76\u5feb\u53eb\u4f60\u7684\u963f\u76d6\u6765\u6b64\u89c1\u6211\u3002\u201d\u9063\u8d70\u4e86\u5361\u6d1b\u4e1d\u4e4b\u540e\uff0c\u4fbf\u5373 \u53bb\u89c1\u4e86\u7f18\u5e08\u592a\u3002 \u3000\u3000\u4e86\u7f18\u5e08\u592a\u521a\u521a\u505a\u5b8c\u65e5\u5e38\u7684\u529f\u8bfe\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u6628\u65e5\u4f20\u6388\u4f60\u7684\u5185\u529f\u5fc3\u6cd5\uff0c\u4f60\u80fd\u591f\u8fd0\u7528\u4e86\u5417\uff1f \u4e0d\u5fc5\u8d2a\u591a\uff0c\u4f60\u591a\u7ec3\u51e0\u5929\uff0c\u6211\u518d\u6559\u4f60\u65b0\u7684\u3002\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u9053\uff0c\u201c\u5f1f\u5b50\u3001\u5f1f\u5b50\u4e0d\u662f\u4e3a\u4e86\u4f20\u529f\u4e60\u827a\u4e4b\u4e8b\u3002\u201d \u3000\u3000\u4e86\u7f18\u5e08\u592a\u6709\u70b9\u8be7\u5f02\uff0c\u62ac\u8d77\u5934\u6765\uff0c\u671b\u5979\u4e00\u773c\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u53c8\u662f\u4e3a\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u5583\u5583\u8bf4\u9053\uff1a\u201c\u5f1f\u5b50\u3001\u5f1f\u5b50\u51e1\u5fc3\u672a\u51c0\uff0c\u60f3\u8bf7\u5e08\u7236\u8bb8\u6211\u56de\u53bb\u3002\u201d \u3000\u3000\u4e86\u7f18\u5e08\u592a\u9053\uff1a\u201c\u4f60\u8981\u56de\u8499\u53e4\uff0c\u6765\u548c\u6211\u8f9e\u884c\uff1f\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u70b9\u4e86\u70b9\u5934\uff0c\u6cea\u6e7f\u53cc\u776b\uff0c\u8bf4\u9053\uff1a\u201c\u5f1f\u5b50\u4e0d\u6562\u6c42\u5e08\u7236\u539f\u8c05\uff0c\u5f1f\u5b50\u5b9e\u662f\u2026\u2026\u5b9e\u662f\u53e6\u6709 \u4e0d\u5f97\u5df2\u7684\u82e6\u8877\u3002\u201d \u3000\u3000\u4e86\u7f18\u5e08\u592a\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u8363\u534e\u5bcc\u8d35\uff0c\u672c\u6765\u662f\u5f88\u96be\u5f03\u5982\u655d\u5c65\u7684\u3002\u4f60\u8eab\u4e3a\u516c\u4e3b\uff0c\u4e5f\u602a\u4e0d \u5f97\u4f60\u8981\u56de\u53bb\u3002\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u5fc3\u4e2d\u9178\u695a\uff0c\u5374\u662f\u6709\u53e3\u96be\u8a00\u3002\u53ea\u5f97\u8bf4\u9053\uff1a\u201c\u5e08\u7236\uff0c\u6211\u8f9c\u8d1f\u4f60\u7684\u683d\u57f9\u4e86\u3002\u201d \u3000\u3000\u4e86\u7f18\u5e08\u592a\u9053\uff1a\u201c\u4f60\u5927\u6709\u7985\u5fc3\uff0c\u6211\u672c\u6765\u4ee5\u4e3a\u4f60\u53ef\u4ee5\u505a\u6211\u7684\u8863\u94b5\u4f20\u4eba\uff0c\u8c01\u6599\u4f60\u4ecd\u7136\u662f\u4e0e\u6211\u4f5b \u95e8\u65e0\u7f18\uff0c\u65e2\u7136\u51e1\u5fc3\u672a\u51c0\uff0c\u6b64\u5ff5\u4e00\u751f\uff0c\u4fbf\u4e0d\u80fd\u518d\u4f4f\u7a7a\u95e8\u4e86\u3002\u597d\uff0c\u4f60\u53bb\u5427\uff0c\u4f46\u613f\u4f60\u4e0d\u8981\u5fd8\u4e86\u672c\u6765 \u9762\u76ee\uff0c\u5760\u5c18\u6cbe\u6ce5\u3002\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u9053\uff1a\u201c\u591a\u8c22\u5e08\u7236\u7684\u6559\u8bad\uff01\u201d\u7ed9\u4e86\u7f18\u5e08\u592a\u53e9\u4e86\u4e2a\u5934\uff0c\u56de\u5230\u81ea\u5df1\u7684\u623f\u95f4\uff0c\u4e0d\u7981\u6cea\u4e0b \u5982\u96e8\u3002 \u3000\u3000\u963f\u76d6\u4e0e\u5361\u6d1b\u4e1d\u5306\u5306\u8dd1\u6765\uff0c\u5361\u6d1b\u4e1d\u542c\u89c1\u54ed\u58f0\uff0c\u5927\u5403\u4e00\u60ca\uff0c\u63a8\u5f00\u623f\u95e8\uff0c\u8fde\u5fd9\u95ee\u9053\uff1a\u201c\u516c\u4e3b\uff0c \u4f60\u600e\u4e48\u5566\uff1f\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u62b9\u5e72\u773c\u6cea\uff0c\u8bf4\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\uff0c\u6211\u60f3\u79bb\u5f00\u6b64\u5904\u3002\u4f60\u4eec\u613f\u610f\u548c\u6211\u4e00\u540c\u56de\u53bb\u4e48\uff1f\u201d \u3000\u3000\u963f\u76d6\u5403\u60ca\u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u516c\u4e3b\uff0c\u4f60\u8981\u56de\u53bb\uff1f\u4f60\u4e0d\u6015\u9547\u56fd\u738b\u5b50\u5c06\u4f60\u96be\u4e3a\uff1f\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u9053\uff1a\u201c\u4e5f\u987e\u4e0d\u5f97\u90a3\u4e48\u591a\u4e86\uff01\u201d \u3000\u3000\u5361\u6d1b\u4e1d\u9053\uff1a\u201c\u5c60\u59d1\u5a18\u5bf9\u54b1\u4eec\u5f88\u597d\uff0c\u516c\u4e3b\uff0c\u4f60\u53c8\u5e76\u6ca1\u524a\u53d1\uff0c\u968f\u65f6\u90fd\u53ef\u4ee5\u8fd8\u4fd7\u7684\uff0c\u5c06\u6765\u2014 \u2014\u201d\u5979\u672c\u6765\u60f3\u8bf4\u201c\u5c06\u6765\u8fd8\u53ef\u4ee5\u627e\u4e2a\u5982\u610f\u90ce\u541b\u201d\uff0c\u4f46\u5979\u6bd5\u7adf\u662f\u6df1\u77e5\u516c\u4e3b\u5fc3\u4e8b\u7684\u4eba\uff0c\u77e5\u9053\u5979\u8fd9\u4e00 \u751f\u662f\u51b3\u4e0d\u80fd\u5fd8\u60c5\u4e8e\u674e\u601d\u5357\u7684\u4e86\uff0c\u662f\u4ee5\u8bdd\u5230\u53e3\u8fb9\uff0c\u53c8\u541e\u56de\u53bb\u3002 \u3000\u3000\u660e\u6167\u516c\u4e3b\u9053\uff1a\u201c\u4f60\u4e0d\u8981\u8bf4\u4e86\uff0c\u6211\u77e5\u9053\u5c60\u59d1\u5a18\u5bf9\u6211\u5f88\u597d\u3002\u4f46\u6211\u662f\u975e\u79bb\u5f00\u6b64\u5730\u4e0d\u53ef\u3002\u201d\u963f \u76d6\u3001\u5361\u6d1b\u4e1d\u9f50\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u662f\u516c\u4e3b\u5e26\u6765\u7684\uff0c\u516c\u4e3b\u5230\u54ea\u91cc\u6211\u4eec\u5c31\u5230\u54ea\u91cc\uff0c\u90a3\u4e48\u662f\u4e0d\u662f\u54b1\u4eec\u73b0 \u5728\u5c31\u53bb\u5411\u5c60\u59d1\u5a18\u8f9e\u884c\uff1f\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u9053\uff1a\u201c\u4e0d\uff0c\u4e0d\u80fd\u8ba9\u5979\u77e5\u9053\u3002\u201d\u963f\u76d6\u5947\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u5361\u6d1b\u4e1d\u806a\u660e\u5f97\u591a\uff0c\u8bf4 \u9053\uff1a\u201c\u4e0d\u9519\uff0c\u7ed9\u5979\u77e5\u9053\uff0c\u5979\u5c31\u4e0d\u8ba9\u54b1\u4eec\u8d70\u4e86\u3002\u201d\u963f\u76d6\u8bf4\u9053\uff1a\u201c\u6211\u603b\u89c9\u5f97\u6709\u70b9\u8fc7\u610f\u4e0d\u53bb\u3002\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u5e7d\u5e7d\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u672c\u6765\u4e0d\u60f3\u8fd9\u6837\u7684\uff0c\u6211\u5df2\u7559\u4e0b\u4e00\u5c01\u4fe1\u7ed9\u5979\uff0c\u8bf4\u660e\u6211\u8fd9 \u4e0d\u5f97\u5df2\u7684\u82e6\u8877\u4e86\u3002\u4f60\u4eec\u73b0\u5728\u6682\u4e14\u4e0d\u5fc5\u591a\u95f4\uff0c\u5230\u4e86\u8def\u4e0a\uff0c\u6211\u518d\u548c\u4f60\u4eec\u8bf4\u5427\u3002\u201d \u3000\u3000\u4ed6\u4eec\u5728\u5c71\u4e0a\u6570\u6708\uff0c\u548c\u5927\u5c0f\u5934\u76ee\uff0c\u90fd\u5df2\u719f\u8bc6\uff0c\u660e\u6167\u516c\u4e3b\u6559\u5361\u6d1b\u4e1d\u634f\u9020\u4e00\u4e2a\u8c0e\u8a00\uff0c\u5bf9\u5de1\u5c71\u5934 \u76ee\u8bf4\u662f\u8981\u53bb\u6253\u730e\uff0c\u56e0\u4e3a\u5c60\u51e4\u4e8b\u5fd9\uff0c\u6545\u6b64\u4e0d\u60f3\u60ca\u52a8\u5979\u4e86\u3002\u5de1\u5c71\u5934\u76ee\u4fe1\u4ee5\u4e3a\u771f\uff0c\u8fd8\u501f\u7ed9\u4ed6\u4eec\u4e09\u5339 \u5750\u9a91\uff0c\u672c\u6765\u8fd8\u8981\u8ba9\u4ed6\u4eec\u628a\u730e\u72ac\u4e5f\u5e26\u53bb\u7684\uff0c\u963f\u76d6\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u8499\u53e4\u4eba\u6253\u730e\uff0c\u4ece\u6765\u4e0d\u7528\u730e\u72ac\u3002\u201d \u8fd9\u624d\u4f5c\u7f62\u3002 \u3000\u3000\u5230\u4e86\u8def\u4e0a\uff0c\u963f\u76d6\u7b11\u9053\uff1a\u201c\u6211\u662f\u4ece\u6765\u4e0d\u8bf4\u8c0e\u7684\u3002\u516c\u4e3b\uff0c\u8fd9\u6b21\u6211\u53ef\u662f\u4e3a\u4f60\u7834\u4f8b\u8bf4\u4e86\u8c0e\u4e86\uff0c\u4e0d \u8fc7\uff0c\u8bf4\u53e5\u5fc3\u91cc\u8bdd\uff0c\u6211\u4e5f\u662f\u5f88\u60f3\u56de\u5230\u54b1\u4eec\u7684\u8349\u539f\u4e0a\u6253\u730e\u7684\u3002\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u9053\uff1a\u201c\u6211\u4f1a\u8ba9\u4f60\u8fbe\u6210\u5fc3\u613f\u7684\u3002\u4e0d\u8fc7\uff0c\u6682\u65f6\u8fd8\u4e0d\u80fd\u8ba9\u4f60\u56de\u56fd\u3002\u201d \u3000\u3000\u963f\u76d6\u8be7\u9053\uff1a\u201c\u54b1\u4eec\u4e0d\u662f\u56de\u8499\u53e4\u53bb\u4e48\uff1f\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u9053\uff1a\u201c\u4e0d\uff0c\u54b1\u4eec\u662f\u53bb\u5927\u90fd\u3002\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u8fd9\u624d\u628a\u539f\u56e0\u544a\u8bc9\u4ed6\u4eec\uff0c\u963f\u76d6\u662f\u4e2a\u76f4\u5fc3\u80a0\u7684\u6c49\u5b50\uff0c\u8bf4\u9053\uff1a\u201c\u516c\u4e3b\uff0c\u4f60\u4f55\u4e0d\u65e9\u8bf4\uff1f \u5361\u6cbc\u4e1d\u4e0e\u6211\u53d7\u8fc7\u4ed6\u4eec\u7684\u5927\u6069\uff0c\u4e3a\u4e86\u674e\u516c\u5b50\u548c\u6768\u59d1\u5a18\uff0c\u6211\u662f\u706b\u91cc\u6765\u706b\u91cc\u53bb\uff0c\u6c34\u91cc\u6765\u6c34\u91cc\u53bb\u3002\u201d \u5361\u6d1b\u4e1d\u5374\u9053\uff1a\u201c\u82e5\u662f\u56db\u738b\u5b50\u4e0d\u7b54\u5141\u516c\u4e3b\u7684\u6c42\u60c5\uff0c\u90a3\u600e\u4e48\u529e\uff1f\u201d\u660e\u6167\u516c\u4e3b\u8e59\u7709\u8bf4\u9053\uff1a\u201c\u5c31\u662f\u4e0d \u6210\uff0c\u4e5f\u5f97\u8bd5\u8bd5\uff0c\u6c49\u4eba\u6709\u53e5\u8bdd\uff0c\u8bf4\u662f\u5c3d\u4eba\u4e8b\u800c\u542c\u5929\u547d\uff0c\u6211\u73b0\u5728\u4e5f\u53ea\u80fd\u4f5c\u8fd9\u6837\u7684\u6253\u7b97\u4e86\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u5728\u5927\u90fd\u4e5f\u662f\u62b1\u7740\u540c\u6837\u7684\u60f3\u6cd5\u3002 \u3000\u3000\u4e00\u5929\u4e00\u5929\u7684\u8fc7\u53bb\uff0c\u5b5f\u5c11\u521a\u8fd8\u672a\u89c1\u5230\u6765\uff0c\u8ddd\u79bb\u4e0e\u9633\u5929\u96f7\u7ea6\u4f1a\u7684\u6700\u540e\u65f6\u9650\u53ea\u6709\u5341\u5929\u5de6\u53f3\u4e86\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u51e1\u4e8b\u603b\u5f97\u4f5c\u6700\u574f\u7684\u6253\u7b97\uff0c\u4ee5\u5b5f\u5927\u4fa0\u7684\u6b66\u529f\uff0c\u5e94\u8be5\u662f\u4e0d\u81f3\u4e8e\u51fa\u4ec0\u4e48\u610f \u5916\u7684\u3002\u4f46\u4e07\u4e00\u6709\u751a\u610f\u5916\uff0c\u90a3\u65f6\u518d\u8bbe\u6cd5\u5c31\u8fdf\u4e86\u3002\u201d\u4ed6\u62b1\u7740\u8fd9\u6837\u7684\u5fc3\u601d\uff0c\u4fbf\u4e0e\u8c37\u6db5\u865a\u8bf4\u9053\uff1a\u201c\u5e08 \u5f1f\uff0c\u6211\u60f3\u5230\u9633\u5929\u96f7\u7684\u56fd\u5e08\u5e9c\u53bb\u8d70\u4e00\u8d9f\uff0c\u4f60\u4e0d\u8981\u8ba9\u9646\u5e2e\u4e3b\u77e5\u9053\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u4f60\u662f\u8981\u53bb\u63a2\u542c\u674e\u76df\u4e3b\u88ab\u56da\u7684\u5904\u6240\u5427\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u6709\u2018\u56fd\u5e08\u5e9c\u2019\u8fdb\u51fa\u7684\u91d1\u724c\uff0c\u5982\u4eca\u53c8\u5df2\u53d8\u4e86\u5bb9\u8c8c\uff0c\u665a\u4e0a\u5077\u5077\u8fdb\u53bb\uff0c \u4e07\u4e00\u7ed9\u4eba\u53d1\u73b0\uff0c\u4ed6\u4eec\u4e00\u65f6\u95f4\u672a\u5fc5\u5c31\u8ba4\u5f97\u51fa\u6211\u662f\u8c01\uff0c\u6709\u8fd9\u9762\u91d1\u724c\uff0c\u53ef\u4ee5\u8499\u6df7\u4e00\u65f6\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u4f60\u4e00\u4e2a\u4eba\u53bb\u592a\u8fc7\u5192\u9669\uff0c\u8fd8\u662f\u54b1\u4eec\u54e5\u513f\u4fe9\u4e00\u540c\u53bb\u5427\uff01\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u9633\u5929\u96f7\u2018\u56fd\u5e08\u5e9c\u2019\u91cc\u7684\u60c5\u5f62\u4f60\u5168\u4e0d\u719f\u6089\uff0c\u4e00\u540c\u53bb\u6050\u6015\u53cd\u800c\u4e0d\u59a5\uff0c\u8fd9\u7965\u5427\uff0c \u4f60\u5728\u2018\u56fd\u5e08\u5e9c\u2019\u9644\u8fd1\u7684\u4e00\u4e2a\u5c0f\u8336\u9986\u7b49\u6211\u3002\u8fd9\u4e2a\u5c0f\u8336\u9986\u662f\u8fc7\u4e86\u5348\u591c\u624d\u5173\u95e8\u7684\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u53ea\u597d\u7b54\u5e94\uff0c\u8bf4\u9053\uff1a\u201c\u5e08\u5144\uff0c\u4f60\u5c0f\u5fc3\u70b9\u513f\u3002\u201d\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u6211\u5728\u5e9c\u4e2d\u6709\u51e0\u4e2a\u53ef\u6258\u8179 \u5fc3\u7684\u670b\u53cb\uff0c\u5fc5\u8981\u65f6\u8fd8\u53ef\u4ee5\u627e\u4ed6\u4eec\u63a9\u62a4\uff0c\u5927\u6982\u4e0d\u4f1a\u6709\u4ec0\u4e48\u610f\u5916\u7684\u3002\u4f60\u7b49\u5230\u4e09\u66f4\u8fc7\u540e\uff0c\u82e5\u7136\u4e0d\u89c1 \u6211\u6765\uff0c\u4f60\u5c31\u56de\u5e84\u7980\u544a\u9646\u5e2e\u4e3b\u3002\u201d \u3000\u3000\u8fd9\u665a\u5929\u8272\u9634\u6c89\uff0c\u65e0\u661f\u65e0\u6708\u3002\u5c06\u8fd1\u201c\u56fd\u5e08\u5e9c\u201d\u7684\u65f6\u5019\uff0c\u5929\u4e0a\u98d8\u7740\u7f8a\u6bdb\u7ec6\u96e8\uff0c\u891a\u4e91\u5cf0\u6697\u6697\u6b22 \u559c\uff0c\u56e0\u4e3a\u8fd9\u6b63\u662f\u6700\u9002\u5b9c\u4e8e\u591c\u884c\u4eba\u51fa\u52a8\u7684\u5929\u6c14\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u719f\u6089\u5e9c\u4e2d\u9632\u536b\uff0c\u897f\u5317\u65b9\u7684\u540e\u95e8\u662f\u5de1\u903b\u5c11\u5230\u7684\u5730\u65b9\uff0c\u4e91\u5cf0\u5c31\u4ece\u90a3\u91cc\u6084\u6084\u5730\u8d8a\u5899\u800c \u5165\uff0c\u8fdb\u5165\u540e\u56ed\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u86c7\u884c\u5154\u4f0f\uff0c\u907f\u8fc7\u5de1\u903b\uff0c\u7a7f\u8fc7\u5047\u5c71\uff0c\u7ed5\u8fc7\u82b1\u6811\uff0c\u53ea\u89c1\u56ed\u4e2d\u4e00\u89d2\uff0c\u4e00\u95f4\u77f3\u5c4b\u900f\u51fa\u706f \u5149\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u5fc3\u5ff5\u4e00\u52a8\uff0c\u60f3\u9053\uff1a\u201c\u4e0d\u77e5\u9ed1\u4e09\u662f\u5426\u4f4f\u5728\u539f\u5904\uff0c\u4ed6\u662f\u6211\u53ef\u4ee5\u76f8\u4fe1\u5f97\u8fc7\u7684\uff0c\u4e0d\u5982\u5148\u53bb \u4e0e\u4ed6\u5546\u91cf\u3002\u201d \u3000\u3000\u201c\u9ed1\u4e09\u201d\u662f\u4e2a\u82b1\u5320\uff0c\u59d3\u5c91\uff0c\u6392\u884c\u7b2c\u4e09\uff0c\u56e0\u4e3a\u4ed6\u751f\u5f97\u9ed1\uff0c\u5927\u5bb6\u90fd\u53eb\u4ed6\u505a\u9ed1\u4e09\u3002 \u3000\u3000\u201c\u56fd\u5e08\u5e9c\u201d\u91cc\u6240\u7528\u7684\u4eba\uff0c\u6c49\u4eba\u5f88\u5c11\uff0c\u804c\u4f4d\u8f83\u9ad8\u7684\u5dee\u4e0d\u591a\u90fd\u662f\u5973\u771f\u65cf\u4eba\u62c5\u4efb\uff0c\u53ea\u6709\u5e72\u7c97\u6d3b \u7684\u548c\u53ae\u5f79\u4e4b\u8f88\u624d\u7528\u5c11\u6570\u6c49\u4eba\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u56e0\u4e3a\u662f\u6c49\u4eba\u7684\u7f18\u6545\uff0c\u5728\u5e9c\u4e2d\u7684\u65f6\u5019\uff0c\u8fd9\u4e9b\u4eba\u6bd4\u8f83\u80af\u548c\u4ed6\u63a5\u8fd1\u3002\u65e5\u5b50\u4e45\u4e86\uff0c\u5927\u5bb6\u8c08 \u8d77\u5fc3\u8179\u8bdd\u6765\uff0c\u891a\u4e91\u5cf0\u4ea4\u4e0a\u4e86\u51e0\u4e2a\u77e5\u5fc3\u7684\u670b\u53cb\uff0c\u8fd9\u201c\u9ed1\u4e09\u201d\u5c31\u662f\u5176\u4e2d\u4e4b\u4e00\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u56db\u987e\u65e0\u4eba\uff0c\u98d8\u8eab\u4e00\u95ea\uff0c\u95ea\u5165\u90a3\u95f4\u5c4b\u5b50\uff0c\u4f4e\u58f0\u53eb\u9053\uff1a\u201c\u4e09\u54e5\uff01\u201d \u3000\u3000\u9ed1\u4e09\u6b63\u5728\u4fee\u7406\u4e00\u67c4\u9504\u5934\uff0c\u542c\u5f97\u58f0\u97f3\u597d\u719f\uff0c\u62ac\u8d77\u5934\u6765\uff0c\u89c1\u662f\u4e00\u4e2a\u964c\u751f\u4eba\u7ad9\u5728\u4ed6\u7684\u9762\u524d\uff0c\u4e0d \u89c9\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u54ea\u4f4d\u5927\u54e5\uff1f\u6055\u6211\u773c\u62d9\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u7b11\u9053\uff1a\u201c\u4e09\u54e5\uff0c\u6211\u7684\u58f0\u97f3\u4f60\u90fd\u542c\u4e0d\u51fa\u6765\u4e86\u5417\uff1f\u6211\u662f\u891a\u4e91\u5cf0\u5440\uff01\u201d\u539f\u6765\u891a\u4e91\u5cf0\u4e43\u662f \u6539\u5bb9\u6613\u8c8c\u7684\uff0c\u6545\u6b64\u9ed1\u4e09\u9aa4\u773c\u4e00\u770b\uff0c\u4e0d\u6562\u76f8\u8ba4\u3002 \u3000\u3000\u9ed1\u4e09\u614c\u5fd9\u63a9\u4e0a\u5927\u95e8\uff0c\u62c9\u4ed6\u8fdb\u5165\u5185\u623f\uff0c\u8bf4\u9053\uff1a\u201c\u891a\u5927\u7237\uff0c\u4f60\u4e5f\u592a\u80c6\u5927\u4e86\uff0c\u4f60\u77e5\u9053\u9633\u5929\u96f7\u65e9 \u5df2\u4e0b\u4e86\u5bc6\u4ee4\uff0c\u8981\u7f09\u6355\u4f60\u5417\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u77e5\u9053\u3002\u6211\u662f\u660e\u77e5\u5c71\u6709\u864e\uff0c\u504f\u5411\u864e\u5c71\u884c\u3002\u4e09\u54e5\uff0c\u4f60\u6015\u4e0d\u6015\u6211\u8fde\u7d2f\u4f60\uff1f\u201d \u3000\u3000\u9ed1\u4e09\u53d8\u4e86\u9762\u8272\uff0c\u8bf4\u9053\uff1a\u201c\u891a\u7237\uff0c\u4f60\u8fd9\u6837\u8bf4\uff0c\u5fd2\u662f\u628a\u6211\u9ed1\u4e09\u5f53\u4f5c\u4e86\u5916\u4eba\uff0c\u6709\u4ec0\u4e48\u4e8b\u8981\u6211\u5e2e \u5fd9\u7684\u4f60\u53ea\u7ba1\u5f00\u53e3\uff0c\u4f60\u53ef\u4ee5\u4e3a\u670b\u53cb\u4e24\u808b\u63d2\u5200\uff0c\u96be\u9053\u6211\u5c31\u4e0d\u80fd\u4e3a\u670b\u53cb\u4e09\u5200\u516d\u6d1e\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u4f60\u731c\u5bf9\u4e86\uff0c\u6211\u6b63\u662f\u4e3a\u4e86\u4e00\u4f4d\u670b\u53cb\u800c\u6765\u3002\u201d \u3000\u3000\u9ed1\u4e09\u9053\uff1a\u201c\u4e0d\u77e5\u662f\u54ea\u4f4d\u670b\u53cb\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u8fd9\u4f4d\u670b\u53cb\u540d\u53eb\u674e\u601d\u5357\uff0c\u662f\u5317\u4e94\u7701\u4e49\u519b\u7684\u76df\u4e3b\uff0c\u542c\u8bf4\u5982\u4eca\u662f\u88ab\u56da\u5728\u8fd9\u56fd\u5e08\u5e9c \u91cc\u3002\u201d \u3000\u3000\u9ed1\u4e09\u9053\uff1a\u201c\u6211\u4e5f\u542c\u8bf4\u662f\u6709\u8fd9\u4e48\u4e00\u56de\u4e8b\uff0c\u534a\u4e2a\u591a\u6708\u524d\uff0c\u6709\u4e00\u4f4d\u6781\u91cd\u8981\u7684\u7eff\u6797\u9886\u8896\u88ab\u62ff\u8fdb\u5e9c \u4e2d\uff0c\u60f3\u5fc5\u5c31\u662f\u4f60\u8bf4\u7684\u8fd9\u4f4d\u674e\u76df\u4e3b\u4e86\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\u8fd9\u4eba\u5173\u5728\u4f55\u5904\uff1f\u201d \u3000\u3000\u9ed1\u4e09\u82e6\u7b11\u9053\uff1a\u201c\u8fd9\u662f\u4e00\u4ef6\u5341\u5206\u673a\u5bc6\u7684\u4e8b\u60c5\uff0c\u51ed\u6211\u8fd9\u4e2a\u82b1\u5320\u7684\u8eab\u4efd\uff0c\u7109\u80fd\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u597d\uff0c\u90a3\u6211\u4e0d\u6253\u6270\u4f60\u4e86\uff0c\u4f46\u613f\u540e\u4f1a\u6709\u671f\u3002\u201d \u3000\u3000\u9ed1\u4e09\u5374\u4e00\u628a\u5c06\u4ed6\u62c9\u7740\uff0c\u8bf4\u9053\uff1a\u201c\u891a\u7237\uff0c\u4f60\u7684\u51f3\u5b50\u8fd8\u672a\u5750\u6696\uff0c\u600e\u80fd\u5c31\u8d70\uff1f\u6211\u77e5\u9053\u4f60\u662f\u6015\u8fde \u7d2f\u6211\uff0c\u4e0d\u8fc7\u4f60\u53ef\u4ee5\u653e\u5fc3\uff0c\u6211\u8fd9\u4e2a\u5730\u65b9\u662f\u4ece\u672a\u6709\u8fc7\u4eba\u6765\u641c\u67e5\u7684\u3002\u800c\u4e14\u73b0\u5728\u6b63\u4e0b\u7740\u96e8\uff0c\u770b\u8fd9\u5929 \u8272\uff0c\u6050\u6015\u4f1a\u8fde\u7ef5\u4e0d\u65ad\u5230\u5929\u4eae\uff0c\u5230\u6211\u8fd9\u513f\uff0c\u8981\u7ecf\u8fc7\u4e00\u6761\u6ce5\u6cde\u7684\u82b1\u5f84\uff0c\u4ed6\u4eec\u4f8d\u536b\u5927\u7237\u662f\u4f1a\u4eab\u798f \u7684\uff0c\u8c01\u80af\u81ea\u8ba8\u82e6\u5403\u6765\u770b\u4e00\u4e2a\u5728\u5e9c\u4e2d\u505a\u4e86\u51e0\u5341\u5e74\u3001\u4ece\u672a\u72af\u8fc7\u5acc\u7591\u7684\u82b1\u5320\uff1f\u891a\u5927\u7237\uff0c\u4f60\u4eca\u665a\u5c31\u5728 \u8fd9\u91cc\u6b47\u4e00\u665a\u5427\uff0c\u6211\u4e5f\u5f88\u60f3\u542c\u4f60\u8bf4\u8bf4\u5916\u9762\u7684\u4e8b\u60c5\u5462\uff0c\u4e00\u6253\u4e94\u66f4\u4f60\u5c31\u8d70\uff0c\u51b3\u4e0d\u4f1a\u51fa\u4e8b\u7684\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u4e09\u54e5\uff0c\u6015\u6709\u70b9\u4e0d\u65b9\u4fbf\u5427\u3002\u6211\u8fd8\u662f\u53e6\u627e\u4e00\u4e2a\u5730\u65b9\u7684\u597d\u3002\u201d \u3000\u3000\u9ed1\u4e09\u9053\uff1a\u201c\u6709\u4ec0\u4e48\u4e0d\u65b9\u4fbf\uff1f\u6211\u90a3\u5973\u513f\u4eca\u665a\u662f\u4e0d\u4f1a\u56de\u5bb6\u7761\u7684\u3002\u201d \u3000\u3000\u891a\u8fd0\u5cf0\u9053\uff1a\u201c\u5bf9\u5566\uff0c\u6211\u6b63\u60f3\u95ee\u4f60\u5462\uff0c\u7389\u599e\u513f\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u9ed1\u4e09\u59bb\u5b50\u65e9\u5df2\u53bb\u4e16\uff0c\u53ea\u7559\u4e0b\u4e00\u4e2a\u5973\u513f\uff0c\u8fd9\u4e2a\u5973\u513f\u5374\u662f\u751f\u5f97\u7ec6\u76ae\u767d\u8089\uff0c\u548c\u5979\u90a3\u201c\u9ed1\u70ad\u5934\u201d \u4f3c\u7684\u7239\u7239\u5927\u4e0d\u76f8\u540c\uff0c\u5c0f\u540d\u4e9a\u7389\uff0c\u5e9c\u91cc\u7684\u4eba\u53eb\u5979\u505a\u201c\u767d\u7389\u513f\u201d\uff0c\u891a\u4e91\u5cf0\u521a\u6765\u7684\u65f6\u5019\uff0c\u767d\u7389\u513f\u8fd8 \u662f\u4e00\u4e2a\u62d6\u7740\u9f3b\u6d95\u7684\u59d1\u5a18\uff0c\u73b0\u5728\u5927\u7ea6\u6709\u5341\u56db\u4e94\u5c81\u5e74\u7eaa\u4e86\u3002 \u3000\u3000\u9ed1\u4e09\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u6211\u4e5f\u6b63\u60f3\u8bf4\u7ed9\u4f60\u77e5\u9053\uff0c\u4e8b\u60c5\u53ef\u662f\u6709\u70b9\u90aa\u95e8\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\u90aa\u95e8\uff1f\u201d \u3000\u3000\u9ed1\u4e09\u9053\uff1a\u201c\u524d\u4e24\u5929\uff0c\u7ba1\u56ed\u7684\u6267\u4e8b\u5149\u987e\u6211\u8fd9\u7834\u5c4b\uff0c\u4f60\u731c\u662f\u4ec0\u4e48\u4e8b\u60c5\uff0c\u539f\u6765\u4ed6\u662f\u8981\u7389\u599e\u53bb\u670d \u4f8d\u4e00\u4e2a\u4eba\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u66f4\u662f\u5403\u60ca\uff0c\u9053\uff1a\u201c\u670d\u4f8d\u4ec0\u4e48\u4eba\u5440\uff1f\u201d\u5fc3\u60f3\uff1a\u201c\u8fd9\u5c0f\u7389\u513f\u4e0d\u8fc7\u624d\u5341\u56db\u5c81\uff0c\u96be\u9053\u5c31\u6709 \u54ea\u4e2a\u4e0d\u8981\u8138\u7684\u7ba1\u4e8b\u770b\u4e0a\u5979\u4e86\uff1f\u201d \u3000\u3000\u9ed1\u4e09\u77e5\u9053\u891a\u4e91\u5cf0\u6709\u4e86\u8bef\u4f1a\uff0c\u7b11\u9053\uff1a\u201c\u5012\u4e0d\u662f\u6709\u4eba\u6253\u7389\u599e\u7684\u4e3b\u610f\uff0c\u4ed6\u4eec\u662f\u8981\u5979\u670d\u4f8d\u4e00\u4f4d\u59d1 \u5a18\uff0c\u8fd8\u662f\u6c49\u4eba\u59d1\u5a18\u5462\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u8be7\u9053\uff1a\u201c\u6c49\u4eba\u59d1\u5a18\uff0c\u90a3\u662f\u8c01\u5440\uff1f\u201d\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6050\u6015\u6ca1\u6709\u8fd9\u6837\u5de7\uff0c\u662f\u53eb\u5979\u53bb\u4f3a\u5019 \u6768\u5a49\u5427\uff1f\u201d \u3000\u3000\u9ed1\u4e09\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u77e5\u9053\u662f\u4ec0\u4e48\u4eba\uff0c\u53ea\u77e5\u9053\u662f\u59d3\u5218\u7684\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u6709\u70b9\u5931\u671b\uff0c\u8bf4\u9053\uff1a\u201c\u600e\u7684\u5e9c\u91cc\u5374\u6765\u4e86\u4e00\u4e2a\u6c49\u4eba\u59d1\u5a18\u4e86\uff1f\u201d \u3000\u3000\u9ed1\u4e09\u9053\uff1a\u201c\u662f\u5440\uff0c\u8fd8\u6709\u66f4\u90aa\u95e8\u7684\u5462\u3002\u4ed6\u4eec\u66fe\u7ecf\u8b66\u544a\u8fc7\u7389\u599e\u513f\uff0c\u4e0d\u8bb8\u5979\u8bf4\u51fa\u8fd9\u91cc\u662f\u91d1\u56fd\u7684 \u56fd\u5e08\u5e9c\u3002\u4f60\u8bf4\u602a\u4e0d\u602a\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u53ef\u662f\u5728\u8fd9\u4e2a\u5c4b\u5b50\u91cc\u7684\u5341\u4e5d\u90fd\u662f\u91d1\u4eba\uff0c\u8fd9\u4f4d\u5218\u59d1\u5a18\u7eb5\u7136\u4e0d\u77e5\u8fd9\u662f\u2018\u56fd\u5e08 \u5e9c\u2019\uff0c\u603b\u4f1a\u72af\u7591\u5427\uff1f\u201d \u3000\u3000\u9ed1\u4e09\u9053\uff1a\u201c\u4e0d\uff0c\u53ea\u6709\u6c49\u4eba\u624d\u80fd\u89c1\u5230\u8fd9\u4f4d\u59d1\u5a18\u7684\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u66f4\u662f\u7eb3\u7f55\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d \u3000\u3000\u9ed1\u4e09\u9053\uff1a\u201c\u8d77\u521d\u6211\u4e0d\u653e\u5fc3\u8ba9\u7389\u599e\u513f\u5230\u91cc\u9762\u53bb\uff0c\u540e\u6765\u90a3\u4e2a\u7ba1\u56ed\u5b50\u7684\u6267\u4e8b\u7b54\u5e94\u6211\u53ef\u4ee5\u6bcf\u9694\u4e09 \u5929\u53bb\u770b\u4e00\u8d9f\u7389\u599e\u513f\u3002\u4e0d\u8fc7\uff0c\u53ea\u8bb8\u6211\u5728\u5c4b\u5b50\u5916\u8fb9\u548c\u7389\u599e\u513f\u8bf4\u8bdd\uff0c\u4e07\u4e00\u78b0\u7740\u90a3\u4f4d\u5218\u59d1\u5a18\uff0c\u6211\u53ea\u80fd \u8bf4\u662f\u82b1\u5320\uff0c\u9664\u4e86\u82b1\u6728\u4e4b\u5916\u7684\u4e8b\u60c5\uff0c\u4ec0\u4e48\u90fd\u4e0d\u80fd\u8bf4\u3002\u4ed6\u4eec\u662f\u6709\u4eba\u6697\u4e2d\u76d1\u89c6\u6211\u7684\u3002 \u3000\u3000\u201c\u90a3\u5929\u6211\u53bb\u770b\u7389\u599e\u513f\uff0c\u5728\u5c4b\u5b50\u5916\u9762\u8c08\u4e86\u4e00\u4f1a\u5b50\u6d3b\uff0c\u8fd9\u6bb5\u65f6\u95f4\uff0c\u4e5f\u66fe\u6709\u51e0\u4e2a\u4eba\u8fdb\u51fa\uff0c\u90fd\u662f \u6c49\u4eba\u3002\u6211\u89c9\u5f97\u6709\u70b9\u5947\u602a\uff0c\u6084\u6084\u5730\u95ee\u4e86\u7389\u599e\u513f\uff0c\u624d\u77e5\u9053\u4e0d\u4f46\u90a3\u5ea7\u5c4b\u5b50\u4e0d\u8bb8\u91d1\u4eba\u8fdb\u5165\uff0c\u5c31\u662f\u5728\u5c4b \u5b50\u5468\u56f4\u7684\u90a3\u4e2a\u5927\u9662\u843d\u4e4b\u5185\uff0c\u4e5f\u662f\u53ea\u51c6\u6c49\u4eba\u8d70\u52a8\u7684\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u90a3\u4e2a\u5218\u59d1\u5a18\u4f4f\u5728\u4ec0\u4e48\u5904\u6240\uff1f\u201d \u3000\u3000\u9ed1\u4e09\u9053\uff1a\u201c\u4f4f\u5728\u7a3b\u9999\u6751\u3002\u201d \u3000\u3000\u201c\u56fd\u5e08\u5e9c\u201d\u7684\u82b1\u56ed\u5360\u5730\u751a\u5927\uff0c\u201c\u7a3b\u9999\u6751\u201d\u5728\u56ed\u4e2d\u4e00\u89d2\uff0c\u6309\u7167\u6c5f\u5357\u7684\u56ed\u6797\u98ce\u683c\u5e03\u7f6e\uff0c\u6709\u56f4 \u5899\u9694\u5f00\uff0c\u4e0d\u557b\u662f\u4e00\u4e2a\u5927\u82b1\u56ed\u91cc\u7684\u5c0f\u82b1\u56ed\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u5fc3\u4e2d\u4e00\u52a8\uff0c\u8bf4\u9053\uff1a\u201c\u4e09\u54e5\uff0c\u4e0d\u662f\u6211\u4e0d\u9886\u4f60\u7684\u60c5\uff0c\u6211\u8fd8\u8981\u627e\u4e00\u4e24\u4f4d\u670b\u53cb\u63a2\u542c\u674e\u76df\u4e3b \u7684\u4e0b\u843d\uff0c\u82e5\u662f\u627e\u4e0d\u7740\u670b\u53cb\uff0c\u6211\u518d\u56de\u6765\u770b\u4f60\u3002\u201d \u3000\u3000\u9ed1\u4e09\u77e5\u9053\u891a\u4e91\u5cf0\u662f\u4e2a\u6781\u91cd\u4e49\u6c14\u7684\u4eba\uff0c\u4ed6\u65e2\u7136\u8fd9\u6837\u8bf4\uff0c\u90a3\u662f\u975e\u8d70\u4e0d\u53ef\u7684\u4e86\uff0c\u4fbf\u9053\uff1a\u201c\u891a \u7237\uff0c\u4f60\u6709\u6b63\u7ecf\u4e8b\u513f\uff0c\u6211\u4e0d\u6562\u52c9\u5f3a\u7559\u4f60\uff0c\u4f46\u613f\u4f60\u5c0f\u5fc3\u70b9\u513f\uff0c\u6211\u7b49\u4f60\u7684\u6d88\u606f\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u8c22\u8fc7\u4e86\u9ed1\u4e09\uff0c\u7b11\u9053\uff1a\u201c\u4e09\u54e5\uff0c\u4f60\u65e9\u70b9\u7761\uff0c\u6211\u4e0d\u4f1a\u51fa\u4e8b\u7684\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u8d70\u51fa\u82b1\u5f84\uff0c\u96e8\u8fd8\u5728\u6dc5\u6dc5\u6ca5\u6ca5\u5730\u4e0b\u4e2a\u4e0d\u505c\uff0c\u4e94\u6b65\u4e4b\u5185\uff0c\u666f\u7269\u90fd\u662f\u96be\u4ee5\u770b\u5f97\u6e05\u695a\u3002\u891a \u4e91\u5cf0\u7779\u6697\u6b22\u559c\uff0c\u5728\u8fd9\u201c\u56fd\u5e08\u5e9c\u201d\u91cc\uff0c\u4ed6\u662f\u95ed\u7740\u773c\u775b\u4e5f\u80fd\u8d70\u8def\u7684\uff0c\u5f53\u4e0b\u4fbf\u6478\u9ed1\u8d70\u5230\u4e86\u201c\u7a3b\u9999 \u6751\u201d\u53bb\u3002 \u3000\u3000\u201c\u7a3b\u9999\u6751\u201d\u4e2d\u53ea\u6709\u4e00\u5e62\u5927\u5c4b\uff0c\u4f59\u5916\u4e24\u95f4\u5c0f\u5c4b\uff0c\u4e43\u662f\u4ec6\u5f79\u4f4f\u7684\uff0c\u90a3\u4e2a\u59d3\u5218\u7684\u59d1\u5a18\u5f53\u7136\u662f\u4f4f \u5728\u5927\u5c4b\u4e2d\u4e86\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u4f7f\u51fa\u8f7b\u529f\uff0c\u6084\u6084\u5730\u8fdb\u4e86\u9662\u5b50\uff0c\u542c\u5f97\u6709\u811a\u6b65\u58f0\uff0c\u891a\u4e91\u5cf0\u8eb2\u5728\u5c4b\u89d2\uff0c\u5077\u5077\u4e00\u770b\uff0c\u53ea\u89c1 \u6709\u4e00\u4e2a\u4eba\u5411\u5185\u91cc\u8d70\u8fdb\uff0c\u8fd9\u4e2a\u4eba\u7684\u80cc\u5f71\u7adf\u662f\u4f3c\u66fe\u76f8\u8bc6\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u5fc3\u9053\uff1a\u201c\u8fd9\u662f\u8c01\u5462\uff1f\u201d\u5f85\u8fd9\u4eba\u8fdb\u53bb\u4e4b\u540e\uff0c\u51ed\u7740\u201c\u542c\u58f0\u8fa8\u5668\u201d\u7684\u529f\u592b\uff0c\u6084\u6084\u5730\u8ddf\u5728 \u4ed6\u7684\u540e\u9762\u3002 \u3000\u3000\u8f6c\u8fc7\u4e00\u6761\u56de\u5eca\uff0c\u53ea\u89c1\u6709\u95f4\u623f\u95f4\uff0c\u706f\u706b\u72b9\u660e\u3002\u891a\u4e91\u5cf0\u8e51\u7740\u811a\u513f\u884c\uff0c\u8d70\u5230\u7a97\u4e0b\uff0c\u521a\u597d\u542c\u5f97\u90a3 \u5973\u7684\u8bf4\u9053\uff1a\u201c\u8fd9\u4e48\u665a\u4e86\u4f60\u8fd8\u6765\u6211\u8fd9\u513f\uff0c\u4e0d\u6015\u522b\u4eba\u8981\u8d77\u5acc\u7591\u4e48\uff1f\u201d \u3000\u3000\u90a3\u7537\u7684\u9053\uff1a\u201c\u8c01\u4e0d\u77e5\u54b1\u4eec\u662f\u5b9a\u4e86\u5a5a\u7684\u5c0f\u592b\u59bb\uff0c\u6015\u4ec0\u4e48\u5acc\u7591\uff1f\u670b\u53cb\u4eec\u90fd\u5728\u7b49\u5f85\u7740\u559d\u54b1\u4eec\u7684 \u559c\u9152\u5462\uff01\u201d \u3000\u3000\u8fd9\u7537\u7684\u4e00\u5f00\u53e3\u8bf4\u8bdd\uff0c\u5c31\u628a\u891a\u4e91\u5cf0\u5413\u4e86\u4e00\u8df3\uff0c\u539f\u6765\u8fd9\u4e2a\u4eba\uff0c\u4e0d\u662f\u522b\u4eba\uff0c\u6b63\u662f\u5c60\u9f99\uff01 \u3000\u3000\u90a3\u5973\u7684\u674f\u8138\u98de\u971e\uff0c\u8bf4\u9053\uff1a\u201c\u9f99\u54e5\uff0c\u4e0d\u8bb8\u4f60\u4e71\u56bc\u820c\u5934\u3002\u201d \u3000\u3000\u5c60\u9f99\u6b63\u8272\u8bf4\u9053\uff1a\u201c\u743c\u59d1\uff0c\u6211\u8bf4\u7684\u53ef\u662f\u6b63\u7ecf\u8bdd\u513f\uff0c\u96be\u9053\u4f60\u4e0d\u613f\u610f\uff1f\u201d \u3000\u3000\u4f0f\u5728\u7a97\u5916\u5077\u542c\u7684\u891a\u4e91\u5cf0\uff0c\u8fd9\u624d\u604d\u7136\u5927\u609f\u3002\u539f\u6765\u8fd9\u4e2a\u5973\u5b50\u6b63\u662f\u5218\u701a\u7ae0\u4e4b\u5973\u3001\u5218\u5927\u4e3a\u4e4b\u59b9\uff0c \u4ea6\u5373\u662f\u4ed6\u7684\u5e08\u7236\u7ed9\u4ed6\u8ba2\u4e0b\u7684\u672a\u5a5a\u59bb\u5b50\u5218\u743c\u59d1\uff0c\u7ed9\u5c60\u9f99\u9a97\u5230\u5927\u90fd\u6765\u4e86\u3002 \u3000\u3000\u5218\u743c\u59d1\u4f4e\u5782\u7c89\u9888\uff0c\u8f7b\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u613f\u610f\uff0c\u4e5f\u4e0d\u4f1a\u8ddf\u4f60\u51fa\u6765\u4e86\u3002\u53ef\u662f\uff0c\u53ef\u662f\u2026\u2026\u201d \u3000\u3000\u5c60\u9f99\u9053\uff1a\u201c\u53ef\u662f\u4ec0\u4e48\uff1f\u4f60\u6015\u891a\u4e91\u5cf0\u6765\u627e\u9ebb\u70e6\u4e48\uff1f\u53ea\u8981\u4f60\u6709\u51b3\u5fc3\uff0c\u4ed6\u603b\u4e0d\u80fd\u628a\u4ed6\u62a2\u4e86 \u53bb\u3002\u201d \u3000\u3000\u5218\u743c\u59d1\u9053\uff1a\u201c\u6211\u8ddf\u4f60\u79c1\u9003\uff0c\u8fd9\u662f\u8feb\u4e0d\u5f97\u5df2\u7684\u3002\u54b1\u4eec\u7684\u4e8b\u60c5\uff0c\u603b\u8fd8\u5f97\u7239\u7239\u70b9\u5934\u624d\u597d\u3002\u201d \u3000\u3000\u5c60\u9f99\u9053\uff1a\u201c\u4f60\u7684\u7239\u7239\u4e0d\u4f1a\u7b54\u5e94\u7684\u3002\u4ed6\u548c\u891a\u4e91\u5cf0\u7684\u5e08\u7236\u662f\u597d\u670b\u53cb\uff0c\u4f60\u82e5\u56de\u53bb\u6c42\u4ed6\uff0c\u4ed6\u4e00\u5b9a \u8981\u903c\u4f60\u5ac1\u7ed9\u891a\u4e91\u5cf0\u7684\uff0c\u6211\u544a\u8bc9\u4f60\uff0c\u891a\u4e91\u5cf0\u8fd9\u5c0f\u5b50\u4e0d\u662f\u4e2a\u597d\u4e1c\u897f\uff0c\u4ed6\u662f\u4e2a\u811a\u8e0f\u4e24\u5934\u8239\uff0c\u660e\u91cc\u5e2e \u5fd9\u4e49\u519b\uff0c\u5176\u5b9e\u5374\u662f\u6548\u5fe0\u91d1\u864f\u7684\u574f\u86cb\u3002\u4e0d\u8fc7\u4ed6\u7792\u4f4f\u5e08\u7236\uff0c\u4f60\u7684\u7239\u7239\u4e5f\u5c31\u76f8\u4fe1\u4ed6\u662f\u597d\u4eba\u4e86\u3002\u201d \u3000\u3000\u5218\u743c\u59d1\u9053\uff1a\u201c\u90a3\u59d3\u891a\u7684\u4e8b\u60c5\uff0c\u6211\u4e00\u70b9\u4e5f\u4e0d\u77e5\u9053\uff0c\u4e0d\u8fc7\u7239\u7239\u548c\u54e5\u54e5\u8bf4\u7684\u548c\u4f60\u53ef\u5927\u4e0d\u76f8 \u540c\u3002\u201d \u3000\u3000\u5c60\u9f99\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u662f\u76f8\u4fe1\u4f60\u7239\u7239\u548c\u4f60\u54e5\u54e5\u7684\u8bf4\u8bdd\u4e86\uff1f\u90a3\u4f60\u8fd8\u662f\u53bb\u4f5c\u891a\u5bb6\u7684\u5c11\u5976\u5976 \u5427\u3002\u201d \u3000\u3000\u5218\u743c\u59d1\u607c\u9053\uff1a\u201c\u6211\u7684\u5fc3\u90fd\u7ed9\u4e86\u4f60\uff0c\u4f60\u8fd8\u8bf4\u8fd9\u6837\u7684\u8bdd\u6c14\u6211\uff01\u891a\u4e91\u5cf0\u662f\u597d\u4eba\u4e5f\u597d\uff0c\u574f\u4eba\u4e5f \u597d\uff0c\u4e0d\u7ba1\u4ed6\u662f\u4ec0\u4e48\u4eba\uff0c\u6211\u7684\u5fc3\u4e0a\u90fd\u662f\u6ca1\u6709\u4ed6\u7684\uff0c\u6211\u5df2\u7136\u8ddf\u5b9a\u4e86\u4f60\uff0c\u4f60\u8fd8\u5403\u8fd9\u79cd\u83ab\u540d\u5176\u5999\u7684\u918b \u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5c60\u9f99\u8d54\u7b11\u9053\uff1a\u201c\u743c\u59d1\uff0c\u6211\u77e5\u9053\u4f60\u7684\u5fc3\u4e86\uff0c\u4f60\u522b\u7740\u607c\uff0c\u6211\u548c\u4f60\u8bf4\u7b11\u73a9\u7684\u3002\u201d \u3000\u3000\u5218\u743c\u59d1\u9053\uff1a\u201c\u8fd9\u79cd\u73a9\u7b11\u4e5f\u662f\u53ef\u4ee5\u968f\u4fbf\u5f00\u7684\u4e48\uff1f\u201d \u3000\u3000\u5c60\u9f99\u9053\uff1a\u201c\u743c\u59d1\uff0c\u54b1\u4eec\u8fd8\u662f\u65e9\u70b9\u6210\u4e86\u4eb2\u5427\uff0c\u4e5f\u7701\u5f97\u591c\u957f\u68a6\u591a\u3002\u201d \u3000\u3000\u5218\u743c\u59d1\u9053\uff1a\u201c\u6211\u60f3\u3001\u6211\u60f3\uff0c\u6211\u60f3\u8fd8\u662f\u591a\u7b49\u4e00\u4e9b\u65e5\u5b50\u7684\u597d\uff0c\u8bf4\u4e0d\u5b9a\u7239\u7239\u4f1a\u56de\u5fc3\u8f6c\u610f\u7684\uff0c\u54b1 \u4eec\u79c1\u81ea\u6210\u4eb2\uff0c\u603b\u662f\u4e0d\u597d\uff01\u201d \u3000\u3000\u5c60\u9f99\u9053\uff1a\u201c\u8981\u7b49\u4f60\u7239\u7239\u56de\u5fc3\u8f6c\u610f\uff0c\u90a3\u53ef\u5c31\u4e0d\u77e5\u8981\u7b49\u5230\u4ec0\u4e48\u65f6\u5019\u4e86\u3002\u4f60\u82e5\u6015\u79c1\u81ea\u6210\u5a5a\u4e0d \u59a5\uff0c\u6211\u5012\u6709\u4e2a\u597d\u4e3b\u610f\u3002\u201d \u3000\u3000\u5218\u743c\u59d1\u9053\uff1a\u201c\u4f60\u6709\u4ec0\u4e48\u597d\u4e3b\u610f\uff1f\u201d \u3000\u3000\u5c60\u9f99\u9053\uff1a\u201c\u4f60\u7684\u54e5\u54e5\u4e0d\u662f\u5f88\u75bc\u4f60\u7684\u5417\uff1f\u201d \u3000\u3000\u5218\u743c\u59d1\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4ed6\u5728\u5bb6\u7684\u65f6\u5019\u867d\u7136\u5f88\u5c11\uff0c\u6211\u4eec\u5144\u59b9\u7684\u611f\u60c5\u53ef\u662f\u5341\u5206\u597d\u7684\u3002\u53bb\u5e74\u4ed6\u672c \u6765\u8981\u5e26\u6211\u51fa\u53bb\uff0c\u53ea\u662f\u56e0\u4e3a\u7239\u7239\u5e74\u8001\uff0c\u8981\u6211\u4f5c\u4f34\uff0c\u4ed6\u987e\u5ff5\u7239\u7239\uff0c\u8fd9\u624d\u6253\u6d88\u539f\u8bae\u3002\u5509\uff0c\u8fd9\u6b21\u82e5\u4e0d \u662f\u56e0\u4e3a\u7239\u7239\u7ed9\u6211\u80e1\u4e71\u5b9a\u4e0b\u5a5a\u4e8b\uff0c\u6211\u662f\u820d\u4e0d\u5f97\u79bb\u5f00\u4ed6\u7684\u3002\u55ef\uff0c\u6211\u53c8\u628a\u8bdd\u9898\u626f\u8fdc\u4e86\uff0c\u4f60\u7684\u610f\u601d\u662f \u2014\u2014\u201d \u3000\u3000\u5c60\u9f99\u8bf4\u9053\uff1a\u201c\u6211\u7684\u610f\u601d\u662f\u60f3\u8bf7\u4f60\u7684\u54e5\u54e5\u4e3b\u5a5a\uff0c\u4ed6\u5f88\u75bc\u4f60\uff0c\u591a\u534a\u4f1a\u7b54\u5e94\u7684\u3002\u53ea\u8981\u4ed6\u80af\u5e94 \u627f\uff0c\u4f60\u7239\u7684\u9762\u524d\uff0c\u4e5f\u5c31\u6709\u4ed6\u53ef\u4ee5\u4ee3\u4e3a\u8bf4\u9879\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u542c\u5230\u8fd9\u91cc\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5c60\u9f99\u8fd9\u53ae\u5012\u662f\u80c6\u5927\u5305\u5929\uff0c\u96be\u9053\u4ed6\u4e0d\u77e5\u9053\u5218\u5927\u4e3a\u65e9\u5df2\u6e05\u695a \u4e86\u4ed6\u7684\u5e95\u7ec6\uff0c\u8fd8\u6562\u6c42\u4ed6\u4e3b\u5a5a\uff1f\u201d \u3000\u3000\u5218\u743c\u59d1\u9053\uff1a\u201c\u6211\u53ea\u77e5\u9053\u54e5\u54e5\u662f\u5728\u5317\u8292\u5c71\u7684\u4e49\u519b\u4e4b\u4e2d\uff0c\u53ef\u4e0d\u77e5\u9053\u786e\u5b9e\u5904\u6240\uff0c\u5982\u4f55\u627e\u7740\u4ed6\uff1f \u800c\u4e14\uff0c\u54b1\u4eec\u4eba\u751f\u5730\u4e0d\u719f\uff0c\u8dd1\u53bb\u5bfb\u4e49\u519b\uff0c\u6050\u6015\u4e5f\u4e0d\u59a5\u5f53\u5427\uff1f\u201d\u5c60\u9f99\u8bf4\u9053\uff1a\u201c\u50bb\u4e2b\u5934\uff0c\u6211\u600e\u80af\u53eb \u4f60\u5192\u9669\u53bb\u627e\u54e5\u54e5\u3002\u201d \u3000\u3000\u5218\u743c\u59d1\u9053\uff1a\u201c\u4f60\u53bb\u6211\u4e5f\u662f\u4e0d\u653e\u5fc3\u7684\uff01\u201d \u3000\u3000\u5c60\u9f99\u7b11\u9053\uff1a\u201c\u4e5f\u4e0d\u7528\u6211\u81ea\u5df1\u53bb\uff0c\u6211\u7684\u670b\u53cb\u4f1a\u5e2e\u5fd9\u6211\u7684\u3002\u4f60\u53ea\u987b\u5199\u4e00\u5c01\u4fe1\uff0c\u8bf7\u4f60\u54e5\u54e5\u5230\u8fd9 \u91cc\u6765\u5c31\u884c\u4e86\uff0c\u6211\u7684\u670b\u53cb\u95e8\u8def\u5f88\u591a\uff0c\u4ed6\u4f1a\u628a\u8fd9\u5c01\u4fe1\u9001\u5230\u5317\u8292\u5c71\u4f60\u7684\u54e5\u54e5\u624b\u4e2d\u3002\u5317\u8292\u5c71\u79bb\u6b64\u4e0d\u8fc7 \u51e0\u767e\u91cc\u8def\uff0c\u5feb\u9a6c\u9a70\u9001\uff0c\u4e09\u4e94\u5929\u5185\uff0c\u4f60\u54e5\u54e5\u5c31\u53ef\u4ee5\u5230\u8fd9\u513f\u6765\u4e86\u3002\u201d \u3000\u3000\u5218\u743c\u59d1\u9053\uff1a\u201c\u54e5\u54e5\u5230\u8fd9\u91cc\u6765\u6050\u6015\u4e5f\u4e0d\u65b9\u4fbf\u5427\uff1f\u201d \u3000\u3000\u5c60\u9f99\u9053\uff1a\u201c\u4f60\u653e\u5fc3\uff0c\u6211\u7684\u670b\u53cb\u90fd\u662f\u65e9\u5df2\u5411\u5f80\u4e49\u519b\uff0c\u53ea\u6068\u6ca1\u6709\u673a\u7f18\u6295\u5954\u3002\u8fd9\u4e2a\u5730\u65b9\u4e5f\u5f88\u79d8 \u5bc6\uff0c\u4e00\u5207\u4e8b\u60c5\u6709\u6211\u7684\u670b\u53cb\u6253\u70b9\uff0c\u7edd\u5bf9\u4e0d\u4f1a\u51fa\u4e8b\u7684\u3002\u201d\u987f\u4e86\u4e00\u987f\uff0c\u63a5\u7740\u53c8\u9053\uff1a\u201c\u743c\u59d1\uff0c\u6211\u7684\u5fc3 \u4e8b\u60f3\u5fc5\u4f60\u4ea6\u77e5\u9053\uff0c\u6211\u4e5f\u662f\u65e9\u5c31\u60f3\u6295\u5954\u4e49\u519b\u7684\uff0c\u8fd9\u6b21\u5018\u80fd\u8bf7\u4f60\u7684\u54e5\u54e5\u5230\u6765\uff0c\u8fd9\u6b63\u662f\u4e00\u4e3e\u4e24\u5f97\uff0c \u5a5a\u540e\uff0c\u54b1\u4eec\u5c31\u53ef\u4ee5\u4e00\u540c\u5f80\u5317\u8292\u5c71\u4e86\u3002\u201d \u3000\u3000\u56fe\u7a77\u5315\u73b0\uff0c\u891a\u4e91\u5cf0\u8fd9\u624d\u604d\u7136\u5927\u609f\u3002\u539f\u6765\u5c60\u9f99\u8bf4\u7684\u8981\u548c\u5218\u743c\u59d1\u65e9\u65e5\u6210\u4eb2\u662f\u5047\uff0c\u771f\u6b63\u7684\u76ee\u7684 \u5374\u662f\u60f3\u628a\u5979\u7684\u54e5\u54e5\u9a97\u6765\uff0c\u597d\u5957\u53d6\u4e49\u519b\u7684\u6d88\u606f\uff0c\u8bf4\u4e0d\u5b9a\u8fd8\u6709\u66f4\u9634\u6bd2\u7684\u624b\u6bb5\u6765\u5bf9\u4ed8\u5218\u5927\u4e3a\u3002 \u3000\u3000\u60f3\u660e\u4e86\u8fd9\u5c42\uff0c\u5176\u4ed6\u7684\u7591\u95ee\u4e5f\u5c31\u8fce\u5203\u800c\u89e3\u4e86\u3002\u4ed6\u4eec\u65e2\u7136\u8981\u8fdb\u884c\u8fd9\u6837\u7684\u9634\u8c0b\uff0c\u5f53\u7136\u5c31\u4e0d\u80fd\u8ba9 \u5218\u743c\u59d1\u77e5\u9053\u8fd9\u4e2a\u5730\u65b9\u662f\u201c\u56fd\u5e08\u5e9c\u201d\uff0c\u670d\u4f8d\u5979\u7684\u4eba\uff0c\u4e5f\u5c31\u53ea\u80fd\u7528\u6c49\u4eba\u4e86\u3002 \u3000\u3000\u5218\u743c\u59d1\u8e0c\u8e87\u83ab\u51b3\uff0c\u6697\u81ea\u601d\u91cf\uff1a\u201c\u54e5\u54e5\u4e3a\u56fd\u820d\u8eab\uff0c\u4ed6\u77e5\u9053\u8fd9\u91cc\u6709\u8fd9\u8bb8\u591a\u5fd7\u540c\u9053\u5408\u7684\u670b\u53cb\uff0c \u5192\u8fd9\u4e2a\u9669\u4ed6\u4e00\u5b9a\u662f\u8ba4\u4e3a\u503c\u5f97\u7684\u3002\u4e0d\u8fc7\u4e07\u4e00\u51fa\u4e8b\uff0c\u5374\u662f\u6211\u5bb3\u4ed6\u4e86\u3002\u201d\u60f3\u4e86\u4e00\u4f1a\uff0c\u8bf4\u9053\uff1a\u201c\u9f99 \u54e5\uff0c\u6211\u6765\u4e86\u51e0\u5929\uff0c\u4f60\u7684\u670b\u53cb\u662f\u4e9b\u4ec0\u4e48\u6837\u7684\u4eba\u6211\u8fd8\u4e0d\u77e5\u9053\u5462\uff0c\u8fd9\u4e2a\u5730\u65b9\u597d\u50cf\u7687\u5bab\u4e00\u6837\uff0c\u4f60\u7684\u670b \u53cb\u662f\u8c6a\u95e8\u5de8\u5bcc\u5417\uff1f\u53c8\u4e3a\u4ec0\u4e48\u4f60\u603b\u4e0d\u80af\u5e26\u6211\u5230\u8fd9\u56ed\u5b50\u5916\u9762\u901b\u901b\u5462\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u542c\u5230\u8fd9\u91cc\uff0c\u8138\u4e0a\u9732\u51fa\u5fae\u7b11\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u4f4d\u5218\u59d1\u5a18\u8fd8\u4e0d\u7b97\u592a\u8fc7\u7cca\u6d82\u3002\u201d \u3000\u3000\u201c\u743c\u59d1\uff0c\u4f60\u4e5f\u672a\u514d\u592a\u7cca\u6d82\u4e86\uff01\u201d\u5c60\u9f99\u8bf4\u9053\uff1a\u201c\u4f60\u6211\u66fe\u7ecf\u548c\u91d1\u864f\u7684\u9e70\u722a\u4ea4\u8fc7\u624b\uff0c\u600e\u80fd\u4e0d\u8c28 \u614e\u884c\u85cf\uff0c\u4ee5\u514d\u98ce\u58f0\u6cc4\u9732\u5462\u3002\u81f3\u4e8e\u6211\u7684\u670b\u53cb\uff0c\u4f60\u66f4\u4e0d\u7528\u731c\u7591\u3002\u4f60\u53ea\u8981\u76f8\u4fe1\u6211\u5c31\u884c\u4e86\uff0c\u96be\u9053\u4f60\u4e0d \u76f8\u4fe1\u6211\u4e48\uff1f\u201d \u3000\u3000\u5218\u743c\u59d1\u9053\uff1a\u201c\u6211\u4e0d\u662f\u4e0d\u76f8\u4fe1\u4f60\uff0c\u4e5f\u4e0d\u662f\u5bf9\u4f60\u7684\u670b\u53cb\u731c\u7591\uff0c\u6211\u53ea\u662f\u89c9\u5f97\u6709\u4e9b\u96be\u89e3\u3002\u201d \u3000\u3000\u5c60\u9f99\u7b11\u9053\uff1a\u201c\u8fd9\u91cc\u662f\u91d1\u56fd\u7684\u4eac\u57ce\uff0c\u548c\u5c71\u6c9f\u81ea\u662f\u4e0d\u80fd\u76f8\u6bd4\uff0c\u4f4f\u7684\u623f\u5c4b\u597d\u4e9b\uff0c\u90a3\u53c8\u6709\u4ec0\u4e48\u53ef \u7591\uff1f\u4f60\u4e5f\u6ca1\u6709\u771f\u6b63\u5230\u8fc7\u738b\u5bab\uff0c\u600e\u77e5\u6b64\u5904\u662f\u548c\u738b\u5bab\u4e00\u6837\uff1f\u201d \u3000\u3000\u5218\u743c\u59d1\u9762\u4e0a\u4e00\u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u6216\u8bb8\u6211\u662f\u4e95\u5e95\u4e4b\u86d9\uff0c\u4f46\u8fd9\u91cc\u7eb5\u7136\u4e0d\u80fd\u6bd4\u7f8e\u738b\u5bab\uff0c\u4e5f\u603b\u6bd4\u666e\u901a \u4eba\u5bb6\u597d\u5f97\u591a\u5427\uff1f\u201d\u5fc3\u4e2d\u6697\u81ea\u601d\u91cf\uff1a\u201c\u8bb0\u5f97\u54e5\u54e5\u8bf4\u8fc7\uff0c\u53cd\u91d1\u7684\u5fd7\u58eb\uff0c\u751f\u6d3b\u5927\u90fd\u662f\u8fc7\u5f97\u5f88\u6734\u7d20 \u7684\uff0c\u4e49\u519b\u4e2d\u4eba\uff0c\u90a3\u5c31\u66f4\u4e0d\u7528\u8bf4\u3002\u9f99\u54e5\u7684\u670b\u53cb\u5374\u8fc7\u5f97\u5982\u6b64\u8c6a\u534e\uff0c\u4ed6\u4eec\u662f\u505a\u4ec0\u4e48\u7684\u5462\uff1f\u201d \u3000\u3000\u5c60\u9f99\u6545\u4f5c\u795e\u79d8\u5730\u8bf4\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u5175\u6cd5\u4e0a\u6240\u4e91\u2018\u865a\u8005\u5b9e\u4e4b\uff0c\u5b9e\u8005\u865a\u4e4b\u2019\u7684\u9053\u7406\u4e86\u3002\u4f60\u60f3\uff0c \u4ed6\u4eec\u662f\u4f4f\u5728\u91d1\u56fd\u7684\u4eac\u57ce\u91cc\uff0c\u5728\u91d1\u56fd\u7684\u4eac\u57ce\u91cc\u5bc6\u8c0b\u53cd\u91d1\uff0c\u5c82\u80fd\u6709\u534a\u70b9\u53ef\u7591\u4e4b\u5904\u7ed9\u4eba\u53d1\u73b0\uff1f\u8981\u600e \u6837\u624d\u80fd\u4f7f\u4eba\u4e0d\u8d77\u7591\u5462\uff1f\u6700\u597d\u7684\u529e\u6cd5\u5c31\u662f\u88c5\u4f5c\u8c6a\u95e8\u5bcc\u6237\u4e86\uff0c\u8fd9\u4f60\u61c2\u4e86\u5427\uff1f\u201d \u3000\u3000\u5218\u743c\u59d1\u542c\u4e86\u4ed6\u8bf4\u5f97\u6709\u7406\uff0c\u7b11\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\u3002\u4f46\u8fd9\u6837\u660e\u91cc\u4e00\u5957\uff0c\u6697\u91cc\u4e00\u5957\u7684\u505a\u4eba\uff0c\u4e5f\u672a \u514d\u592a\u8f9b\u82e6\u4e86\u3002\u201d \u3000\u3000\u5c60\u9f99\u8bf4\u9053\uff1a\u201c\u6240\u4ee5\u4ed6\u4eec\u90fd\u662f\u548c\u6211\u4e00\u6837\uff0c\u60f3\u65e9\u65e5\u627e\u5230\u6295\u5954\u4e49\u519b\u7684\u95e8\u8def\uff0c\u5c31\u4e0d\u5fc5\u5728\u5927\u90fd\u53d7\u82e6 \u4e86\u3002\u5bf9\u5566\uff0c\u6211\u521a\u624d\u8bf4\u7684\u90a3\u5c01\u4fe1\uff0c\u4f60\u5feb\u70b9\u5199\u5427\u3002\u201d \u3000\u3000\u5218\u743c\u59d1\u542c\u5f97\u5916\u9762\u6253\u66f4\u4e4b\u58f0\uff0c\u9aa4\u7136\u4e00\u7701\uff0c\u8bf4\u9053\uff1a\u201c\u591c\u5df2\u6df1\u4e86\uff0c\u9f99\u54e5\uff0c\u4f60\u5148\u56de\u53bb\uff0c\u8fd9\u5c01\u4fe1\u6211 \u4eca\u591c\u5199\u597d\uff0c\u660e\u5929\u4e00\u65e9\u7ed9\u4f60\u3002\u201d \u3000\u3000\u5c60\u9f99\u563b\u76ae\u7b11\u8138\u5730\u8bf4\u9053\uff1a\u201c\u743c\u59d1\uff0c\u6211\u4e0d\u56de\u53bb\u4e86\u3002\u201d \u3000\u3000\u5218\u743c\u59d1\u9053\uff1a\u201c\u4f60\u6211\u867d\u6709\u592b\u59bb\u4e4b\u7ea6\uff0c\u591a\u5c11\u4e5f\u5f97\u907f\u70b9\u5acc\u7591\u3002\u8fd9\u5c01\u4fe1\u6211\u53c8\u4e0d\u80fd\u9a6c\u4e0a\u5199\u597d\u7684\uff0c\u4f60 \u542c\u5916\u9762\u5df2\u7ecf\u6253\u8fc7\u4e8c\u66f4\u4e86\uff0c\u4f60\u662f\u4e0d\u5e94\u8be5\u7559\u5728\u6211\u7684\u623f\u4e2d\u4e86\u3002\u201d \u3000\u3000\u5c60\u9f99\u5ffd\u5730\u5c06\u5979\u63fd\u5165\u6000\u4e2d\uff0c\u7b11\u563b\u563b\u5730\u8bf4\u9053\uff1a\u201c\u4eca\u665a\u6211\u4e0d\u8d70\u4e86\uff0c\u53cd\u6b63\u4f60\u603b\u8981\u505a\u6211\u7684\u59bb\u5b50\u7684\uff0c \u54b1\u4eec\u5148\u6210\u4eb2\u540e\u62dc\u5802\u90a3\u4e5f\u65e0\u59a8\uff01\u743c\u59b9\uff0c\u6211\u5b9e\u5728\u820d\u4e0d\u5f97\u79bb\u5f00\u4f60\uff0c\u4f60\u5c31\u53ef\u601c\u53ef\u601c\u6211\u5427\uff01\u201d \u3000\u3000\u5218\u743c\u59d1\u6ee1\u9762\u901a\u7ea2\uff0c\u84e6\u5730\u7528\u51fa\u5168\u8eab\u6c14\u529b\uff0c\u628a\u5c60\u9f99\u63a8\u5f00\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u628a\u6211\u5f53\u4f5c\u4ec0\u4e48\u4eba\u4e86\uff1f\u6211 \u505a\u4f60\u7684\u59bb\u5b50\u4e5f\u5f97\u5149\u660e\u6b63\u5927\u7684\u62dc\u5802\u6210\u4eb2\uff0c\u4f60\u8981\u903c\u6211\u82df\u5408\uff0c\u90a3\u662f\u4e07\u4e07\u4e0d\u80fd\uff01\u201d \u3000\u3000\u5c60\u9f99\u672c\u4ee5\u4e3a\u8f6f\u786c\u517c\u65bd\uff0c\u743c\u59d1\u5b9a\u662f\u534a\u63a8\u534a\u5c31\u7684\uff0c\u54ea\u77e5\u5979\u7a81\u7136\u7ffb\u8138\uff0c\u5012\u662f\u59cb\u6599\u4e4b\u6240\u4e0d\u53ca\uff0c\u5fc3 \u91cc\u60f3\u9053\uff1a\u201c\u542c\u5979\u4eca\u665a\u7684\u8a00\u8bed\uff0c\u5df2\u662f\u6709\u70b9\u8d77\u7591\uff0c\u53ea\u6709\u7c73\u5df2\u6210\u708a\uff0c\u624d\u80fd\u4ee4\u5979\u5fc5\u987b\u542c\u6211\u6446\u5e03\uff01\u201d \u3000\u3000\u743c\u59d1\u770b\u51fa\u4ed6\u7684\u795e\u60c5\u4e0d\u5bf9\uff0c\u500f\u5730\u62d4\u51fa\u5200\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u518d\u903c\u6211\uff0c\u6211\u5c31\u6b7b\u5728\u4f60\u7684\u8ddf\u524d\u3002\u201d \u3000\u3000\u5c60\u9f99\u60f3\u4e0d\u5230\u5979\u5982\u6b64\u521a\u70c8\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u743c\u59d1\uff0c\u4f55\u5fc5\u5982\u6b64\u52a8\u6012\uff1f\u6211\u5c82\u6562\u903c\u4f60\uff0c\u53ea\u662f \u5bf9\u4f60\u4e00\u7247\u75f4\u5fc3\u800c\u5df2\uff0c\u96be\u9053\u4f60\u4e0d\u6b22\u559c\u6211\u4e48\uff1f\u201d \u3000\u3000\u5218\u743c\u59d1\u67f3\u7709\u5012\u7ad6\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u82e5\u662f\u771f\u6b63\u7231\u60dc\u6211\uff0c\u8bf7\u4f60\u4e3a\u6211\u7740\u60f3\uff0c\u9a6c\u4e0a\u79bb\u5f00\uff01\u522b\u628a\u6211\u5f53\u6210 \u6deb\u8d31\u7684\u5973\u5b50\uff01\u201d \u3000\u3000\u4f0f\u5728\u7a97\u5916\u5077\u542c\u7684\u891a\u4e91\u5cf0\u6697\u81ea\u8d5e\u53f9\uff0c\u5fc3\u91cc\u60f3\u9053\uff0c\u201c\u597d\u4e00\u4e2a\u70c8\u6027\u5973\u5b50\uff0c\u4e0d\u6127\u662f\u5218\u8001\u82f1\u96c4\u7684\u5973 \u513f\uff01\u53ea\u53ef\u60dc\u5e74\u8f7b\u8bc6\u6d45\uff0c\u8bef\u4ea4\u532a\u4eba\uff0c\u53d7\u4e86\u5c60\u9f99\u7684\u6b3a\u9a97\uff01\u201d\u5f53\u4e0b\u6253\u5b9a\u4e3b\u610f\uff1a\u82e5\u662f\u5c60\u9f99\u7528\u5f3a\u7684\u8bdd\uff0c \u4ed6\u4fbf\u7acb\u5373\u8fdb\u53bb\u60e9\u6212\u5c60\u9f99\uff0c\u7eb5\u7136\u56e0\u6b64\u6cc4\u9732\u884c\u85cf\uff0c\u751a\u6216\u8eab\u906d\u4e0d\u6d4b\u4e4b\u7978\uff0c\u90a3\u4e5f\u987e\u4e0d\u5f97\u8fd9\u8bb8\u591a\u4e86\uff01 \u3000\u3000\u5c60\u9f99\u843d\u4e0d\u4e86\u53f0\uff0c\u5c34\u5c2c\u4e4b\u6781\uff01\u662f\u7d22\u6027\u4e00\u4e0d\u505a\u4e8c\u4e0d\u4f11\u5462\uff1f\u8fd8\u662f\u77e5\u96be\u800c\u9000\uff0c\u7ee7\u7eed\u7528\u6c34\u78e8\u7684\u529f\u592b \u6765\u9a97\u53d6\u5218\u8c05\u59d1\u7684\u4fe1\u4efb\u5462\uff1f\u4e00\u65f6\u4e4b\u95f4\uff0c\u4ed6\u5012\u662f\u8e0c\u8e87\u83ab\u51b3\u4e86\u3002 \u3000\u3000\u5c31\u5728\u6b64\u65f6\uff0c\u5ffd\u542c\u5f97\u6709\u4eba\u53eb\u9053\uff1a\u201c\u5c60\u516c\u5b50\uff0c\u96f7\u56db\u7237\u6709\u4e8b\u627e\u4f60\uff0c\u8bf7\u4f60\u8fc7\u53bb\uff01\u201d\u8fd9\u4eba\u662f\u201c\u56fd\u5e08 \u5e9c\u201d\u4e2d\u5c11\u6570\u7684\u6c49\u4eba\u4f8d\u536b\u4e4b\u4e00\uff0c\u6d3e\u5728\u8fd9\u91cc\u5192\u5145\u5c60\u9f99\u7684\u670b\u53cb\u7684\u4ec6\u5f79\u3002\u4ed6\u5206\u660e\u77e5\u9053\u5c60\u9f99\u662f\u5728\u5218\u743c\u59d1 \u7684\u623f\u4e2d\uff0c\u5374\u88c5\u4f5c\u4e0d\u77e5\uff0c\u7ad9\u5728\u8fc7\u9053\u4e0a\u53eb\u558a\uff0c\u891a\u4e91\u5cf0\u4e00\u542c\uff0c\u4fbf\u77e5\u8fd9\u201c\u96f7\u56db\u7237\u201d\u4e00\u5b9a\u662f\u8499\u53e4\u7684\u56db\u738b \u5b50\u62d6\u96f7\u65e0\u7591\u3002 \u3000\u3000\u5c60\u9f99\u4e58\u673a\u81ea\u4e0b\u53f0\u9636\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u743c\u59d1\uff0c\u522b\u751f\u6c14\u5566\uff0c\u539f\u8c05\u6211\u4eca\u665a\u7cca\u6d82\uff0c\u51e0\u4e4e\u505a\u4e86\u9519\u4e8b\uff0c \u8fd9\u4e5f\u662f\u4e3a\u4e86\u592a\u8fc7\u7231\u4f60\u7684\u7f18\u6545\u3002\u597d\u5566\uff0c\u6211\u73b0\u5728\u8d70\u4e86\uff0c\u4f60\u5c31\u5199\u90a3\u5c01\u4fe1\u5427\u3002\u201d\u5f53\u4e0b\u5306\u5306\u8d70\u51fa\u743c\u59d1\u7684 \u623f\u95f4\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u56db\u7687\u5b50\u6df1\u591c\u627e\u6211\uff0c\u4e0d\u77e5\u662f\u4e3a\u4e86\u4f55\u4e8b\uff1f\u201d \u3000\u3000\u5c60\u9f99\u8d70\u540e\uff0c\u5218\u743c\u59d1\u5fc3\u5934\u5140\u662f\u535c\u535c\u4e71\u8df3\uff0c\u867d\u7136\u6446\u8131\u4e86\u5c60\u9f99\u7684\u7ea0\u7f20\uff0c\u5fc3\u60c5\u5374\u662f\u4e0d\u80fd\u5e73\u9759\uff01 \u3000\u3000\u8fd9\u4ef6\u4e8b\u60c5\u592a\u51fa\u4e4e\u5979\u610f\u6599\u4e4b\u5916\u4e86\uff0c\u6b64\u65f6\u5979\u60ca\u9b42\u7a0d\u5b9a\uff0c\u4e0d\u7981\u60f3\u9053\uff1a\u201c\u9f99\u54e5\u4e00\u5411\u5bf9\u6211\u6e29\u6587\u6709 \u793c\uff0c\u600e\u7684\u4eca\u665a\u5374\u4f1a\u5982\u6b64\uff1f\u96be\u9053\u8fd9\u624d\u662f\u4ed6\u7684\u672c\u6027\uff0c\u8fc7\u53bb\u7684\u6e29\u6587\u4f53\u8d34\u5374\u662f\u5047\u88c5\u7684\u4e48\uff1f\u201d \u3000\u3000\u53ef\u601c\u5979\u8fd8\u53ea\u662f\u4e3a\u4e86\u5c60\u9f99\u7684\u65e0\u793c\u800c\u7740\u607c\uff0c\u6ca1\u60f3\u5230\u5c60\u9f99\u8fd9\u4e2a\u4eba\u6bd4\u5979\u6240\u60f3\u8c61\u7684\u8fd8\u8981\u574f\u5f97\u591a\uff01\u4f46 \u53ea\u662f\u5c60\u9f99\u4eca\u665a\u66b4\u9732\u7684\u8fd9\u4e2a\u4e11\u6076\u7684\u4e00\u9762\uff0c\u4ea6\u5df2\u8db3\u4ee5\u4ee4\u5979\u5fc3\u60b8\uff0c\u6000\u7591\u81ea\u5df1\u662f\u5426\u770b\u9519\u4eba\u4e86\uff01 \u3000\u3000\u6b63\u5728\u743c\u59d1\u5fc3\u4e71\u5982\u9ebb\u4e4b\u9645\uff0c\u5ffd\u6240\u5f97\u6709\u4eba\u8f7b\u8f7b\u6572\u4e86\u4e00\u4e0b\u7a97\u6237\uff0c\u8bf4\u9053\uff1a\u201c\u5bf9\u4e0d\u4f4f\uff0c\u8bf7\u4f60\u539f\u8c05\u6211 \u5192\u6627\u8fdb\u6765\uff0c\u6211\u6709\u7d27\u8981\u7684\u4e8b\u60c5\u548c\u4f60\u8bf4\uff01\u201d \u3000\u3000\u5218\u743c\u59d1\u5413\u4e86\u4e00\u8df3\uff0c\u53ea\u89c1\u4e00\u4e2a\u964c\u751f\u7684\u7537\u5b50\u5df2\u662f\u8d70\u8fdb\u5979\u7684\u623f\u95f4\uff0c\u7ad9\u5728\u5979\u7684\u9762\u524d\u4e86\u3002 \u3000\u3000\u5218\u743c\u59d1\u6c89\u58f0\u559d\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u6084\u6084\u8bf4\u9053\uff1a\u201c\u5218\u59d1\u5a18\uff0c\u8bf7\u4f60\u522b\u56b7\uff0c\u6211\u662f\u891a\u4e91\u5cf0\uff01\u6211\u5bf9\u4f60\u6beb\u65e0\u6076\u610f\u3002\u201d \u3000\u3000\u201c\u891a\u4e91\u5cf0\u201d\u4e09\u5b57\u542c\u8fdb\u5218\u743c\u59d1\u7684\u8033\u6735\uff0c\u767b\u65f6\u4ee4\u5f97\u5979\u76ee\u77aa\u53e3\u5446\uff0c\u8bf4\u4e0d\u51fa\u8bdd\u6765\u4e86\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u4f60\u4eec\u521a\u624d\u8bf4\u7684\u8bdd\uff0c\u6211\u90fd\u5df2\u542c\u89c1\u3002\u201d \u3000\u3000\u5218\u743c\u59d1\u5f3a\u81ea\u9547\u6151\u5fc3\u795e\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u4f60\u65e2\u7136\u542c\u89c1\u4e86\uff0c\u6211\u4e5f\u4e0d\u5fc5\u7792\u4f60\uff0c\u6211\u5c31\u548c\u4f60\u723d\u5feb\u5730\u8bf4 \u5427\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4f60\u4e0d\u7528\u5f00\u53e3\uff0c\u6211\u4e5f\u77e5\u9053\u4f60\u60f3\u8bf4\u7684\u662f\u4ec0\u4e48\u4e86\u3002\u201d\u4f46\u8fd9\u8bdd\u4ed6\u5f53\u7136\u4e5f\u662f\u4e0d \u4fbf\u8bf4\u51fa\u6765\uff01 \u3000\u3000\u679c\u7136\u4fbf\u542c\u5f97\u5218\u743c\u59d1\u8bf4\u9053\uff1a\u201c\u7239\u7239\u5c06\u6211\u8bb8\u914d\u4e0e\u4f60\uff0c\u4f46\u6211\u5fc3\u91cc\u559c\u6b22\u7684\u5374\u662f\u5c60\u9f99\u3002\u6211\u5e76\u975e\u770b\u4e0d \u8d77\u4f60\uff0c\u522b\u4eba\u8bf4\u7684\u4ec0\u4e48\uff0c\u6211\u4e5f\u5e76\u4e0d\u76f8\u4fe1\u3002\u4f46\u6211\u548c\u4f60\u4ece\u6765\u6ca1\u6709\u89c1\u8fc7\u9762\uff0c\u6211\u548c\u4ed6\u5219\u662f\u76f8\u8bc6\u5728\u5148\uff0c\u5982 \u679c\u4f60\u539f\u8c05\u6211\uff0c\u90a3\u5c31\u8bf7\u4f60\u79bb\u5f00\uff0c\u5982\u679c\u4f60\u4e0d\u80af\u539f\u8c05\u6211\uff0c\u90a3\u4f60\u5c31\u628a\u6211\u6740\u4e86\u5427\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u82e6\u7b11\u9053\uff0c\u201c\u5218\u59d1\u5a18\uff0c\u4f60\u8bef\u4f1a\u4e86\uff0c\u6211\u4e0d\u662f\u4e3a\u4e86\u8fd9\u4ef6\u4e8b\u60c5\u6765\u7684\uff01\u8fd9\u6869\u5a5a\u4e8b\uff0c\u6211\u4e5f\u672a\u66fe \u5e94\u627f\u8fc7\u4f60\u7684\u7239\u7239\uff0c\u4f60\u5f53\u4f5c\u6ca1\u6709\u8fd9\u56de\u4e8b\u597d\u4e86\uff0c\u4e0d\u5fc5\u82a5\u8482\u4e8e\u5fc3\u3002\u201d \u3000\u3000\u5218\u743c\u59d1\u8be7\u9053\uff1a\u201c\u90a3\u4f60\u53c8\u662f\u4e3a\u4e86\u4ec0\u4e48\u4e8b\u60c5\u6765\u7684\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u6211\u7684\u4e8b\u60c5\uff0c\u5f80\u540e\u518d\u8bf4\u3002\u6211\u53ea\u95ee\u4f60\uff0c\u4f60\u53ef\u77e5\u9053\u8fd9\u91cc\u662f\u4ec0\u4e48\u5730\u65b9\u5417\uff1f\u201d \u3000\u3000\u5218\u743c\u59d1\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u542c\u89c1\u6211\u4eec\u7684\u8bf4\u8bdd\u4e86\u4e48\uff1f\u8fd9\u91cc\u662f\u4ed6\u670b\u53cb\u7684\u5bb6\u5440\uff01\u201d \u3000\u3000\u891a\u652f\u5cf0\u9053\uff1a\u201c\u4e0d\uff0c\u6211\u8001\u5b9e\u544a\u8bc9\u4f60\u5427\uff0c\u8fd9\u91cc\u662f\u91d1\u56fd\u7684\u56fd\u5e08\u5e9c\uff01\u201d \u3000\u3000\u5218\u743c\u59d1\u5927\u5403\u4e00\u60ca\uff0c\u5413\u5f97\u8df3\u4e86\u8d77\u6765\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u3001\u4f60\u8bf4\u4ec0\u4e48\uff1f\u8fd9\u91cc\u662f\u56fd\u5e08\u5e9c\uff1f\u4f60\u80e1\u8bf4\uff01\u201d \u8981\u77e5\u5979\u867d\u7136\u5f00\u59cb\u6709\u70b9\u6000\u7591\u5c60\u9f99\u7684\u4eba\u54c1\uff0c\u4f46\u891a\u4e91\u5cf0\u6240\u8bf4\u7684\u4e8b\u60c5\uff0c\u5374\u8fd8\u662f\u5979\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u7684\uff01 \u3000\u3000\u891a\u4e91\u5cf0\u201c\u5618\u201d\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u58f0\uff0c\u522b\u7ed9\u4eba\u542c\u89c1\uff01\u4f60\u4e0d\u76f8\u4fe1\uff1f\u201d \u3000\u3000\u5218\u743c\u59d1\u9053\uff1a\u201c\u4f60\u6709\u4ec0\u4e48\u8bc1\u636e\uff0c\u8bf4\u8fd9\u91cc\u662f\u91d1\u56fd\u7684\u56fd\u5e08\u5e9c\uff1f\u6211\u6240\u89c1\u7684\u53ef\u5168\u662f\u6c49\u4eba\uff01\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u8fd9\u662f\u4ed6\u4eec\u7279\u5730\u5b89\u6392\u7684\u3002\u597d\uff0c\u8ba9\u6211\u60f3\u60f3\uff0c\u8981\u600e\u6837\u624d\u80fd\u8ba9\u4f60\u76f8\u4fe1\uff1f\u201d \u3000\u3000\u5218\u743c\u59d1\u9053\uff1a\u201c\u6211\u4e0d\u76f8\u4fe1\uff0c\u9664\u975e\u6211\u4eb2\u773c\u770b\u89c1\uff01\u597d\uff0c\u6211\u8dd1\u51fa\u8fd9\u4e2a\u56ed\u5b50\u53bb\u770b\uff0c\u770b\u770b\u6709\u6ca1\u6709\u91d1\u56fd \u9791\u5b50\uff01\u201d \u3000\u3000\u891a\u4e91\u5cf0\u6c89\u58f0\u9053\uff1a\u201c\u4e0d\u884c\uff0c\u8fd9\u4f60\u5c31\u662f\u81ea\u6295\u7f57\u7f51\u4e86\uff01\u201d \u3000\u3000\u5218\u743c\u59d1\u89c1\u4ed6\u8bf4\u5f97\u5982\u6b64\u90d1\u91cd\uff0c\u867d\u7136\u8fd8\u662f\u4e0d\u4fe1\u4ed6\u7684\u8bf4\u8bdd\uff0c\u4f46\u4e5f\u4e0d\u6562\u9c81\u83bd\u8dd1\u51fa\u53bb\u67e5\u770b\u4e86\u3002\u5979\u5750 \u4e86\u4e0b\u6765\uff0c\u5598\u7740\u6c14\u8bf4\u9053\uff1a\u201c\u6211\u600e\u77e5\u9053\u4f60\u662f\u4e0d\u662f\u9a97\u6211\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u5ffd\u9053\uff1a\u201c\u6709\u4e86\uff01\u201d\u8ddf\u7740\u5750\u4e86\u4e0b\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u662f\u4e0d\u662f\u6709\u4e2a\u670d\u4f8d\u4f60\u7684\u4e2b\u9b1f\u53eb\u505a\u5c0f\u7389\u513f \u7684\uff1f\u201d \u3000\u3000\u5218\u63a0\u59d1\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u4f60\u628a\u5979\u53eb\u6765\uff01\u201d \u3000\u3000\u5218\u743c\u59d1\u9053\uff1a\u201c\u4e09\u66f4\u534a\u591c\uff0c\u628a\u8fd9\u4e2b\u5934\u53eb\u6765\u505a\u4ec0\u4e48\uff1f\u4f60\u53c8\u5728\u8fd9\u91cc\uff01\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u5979\u6765\u4e86\uff0c\u6211\u8ba9\u5979\u4eb2\u53e3\u5bf9\u4f60\u8bf4\uff01\u201d \u3000\u3000\u5218\u743c\u59d1\u534a\u4fe1\u534a\u7591\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u6211\u53bb\u53eb\u5979\uff01\u201d \u3000\u3000\u891a\u4e91\u5cf0\u84e6\u5730\u60f3\u8d77\u4e00\u4e8b\uff0c\u8bf4\u9053\uff1a\u201c\u4e14\u6162\uff01\u201d\u5218\u743c\u59d1\u6115\u7136\u56de\u9996\uff0c\u8bf4\u9053\uff1a\u201c\u54a6\uff0c\u4f60\u8fd8\u6709\u4ec0\u4e48\u82b1 \u6837\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u4f4f\u7684\u662f\u95f4\u5f00\u7684\u4e00\u4e2a\u56db\u5408\u9662\u5b50\uff0c\u4f46\u6211\u4e0d\u77e5\u5728\u8fd9\u9662\u5b50\u91cc\u7684\uff0c\u9664\u4e86\u5c0f\u7389 \u513f\u4e4b\u5916\uff0c\u8fd8\u6709\u6ca1\u6709\u522b\u7684\u4ec6\u4eba\uff1f\u201d\u6b63\u662f\uff1a \u3000\u3000\u8bc6\u6d45\u582a\u4f24\u55df\u5931\u8db3\uff0c\u9752\u83b2\u4ecd\u672a\u67d3\u6c61\u6ce5\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b\u2014\u2014\u7b2c\u4e94\u5341\u4e09\u56de\u3000\u674e\u4ee3\u6843\u50f5\u60b2\u5f80\u4e8b\u3000\u60c5\u865a\u610f\u5047\u6597\u673a\u8c0b \u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b \u7b2c\u4e94\u5341\u4e09\u56de\u3000\u674e\u4ee3\u6843\u50f5\u60b2\u5f80\u4e8b\u3000\u60c5\u865a\u610f\u5047\u6597\u673a\u8c0b \u3000\u3000\u5218\u743c\u59d1\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u4e2a\u7537\u4ec6\uff0c\u662f\u4ed6\u7684\u670b\u53cb\u6d3e\u6765\u770b\u95e8\u7684\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u5c31\u662f\u521a\u624d\u53eb\u5c60\u9f99\u53bb\u89c1\u96f7\u56db\u7237\u7684\u90a3\u4e2a\u4eba\u5427\uff1f\u201d \u3000\u3000\u5218\u743c\u59d1\u9053\uff1a\u201c\u4e0d\u9519\u3002\u4f60\u95ee\u4ed6\u5e72\u561b\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u4ed6\u4e0d\u662f\u4ec6\u4eba\uff0c\u4ed6\u662f\u91d1\u56fd\u56fd\u5e08\u9633\u5929\u96f7\u624b\u4e0b\u5c11\u6570\u7684\u51e0\u4e2a\u6c49\u4eba\u4f8d\u536b\u4e4b\u4e00\uff0c\u90a3\u4e2a\u4ec0 \u4e48\u2018\u96f7\u56db\u7237\u2019\u5374\u662f\u8499\u53e4\u7684\u56db\u7687\u5b50\u62d6\u96f7\uff01\u201d \u3000\u3000\u5218\u743c\u59d1\u4e0d\u7981\u53c8\u662f\u4e00\u5446\uff0c\u5583\u5583\u8bf4\u9053\uff1a\u201c\u6211\u3001\u6211\u4e0d\u76f8\u4fe1\uff01\u4f60\u3001\u4f60\u8bf4\u5f97\u592a\u53ef\u6015\u4e86\uff01\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u4f60\u4e0d\u76f8\u4fe1\uff1f\u6211\u95ee\u4f60\uff0c\u5c0f\u7389\u513f\u662f\u4e0d\u662f\u4e0d\u6562\u548c\u4f60\u8bf4\u8bdd\uff1f\u4f60\u6709\u4ec0\u4e48\u95ee\u5979\u7684\u65f6\u5019\uff0c \u5979\u4e5f\u662f\u4e00\u95ee\u6447\u5934\u4e09\u4e0d\u77e5\u3002\u201d \u3000\u3000\u5218\u743c\u59d1\u5403\u4e86\u4e00\u60ca\uff0c\u98a4\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u600e\u7684\u597d\u50cf\u4ec0\u4e48\u90fd\u77e5\u9053\u4f3c\u7684\uff1f\u201d\u5fc3\u91cc\u5df2\u7ecf\u662f\u76f8\u4fe1\u4e09\u4e24\u5206 \u4e86\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u6211\u5f53\u7136\u77e5\u9053\u3002\u5c0f\u7389\u513f\u4e0d\u6562\u548c\u4f60\u8bf4\u8bdd\uff0c\u5c31\u662f\u56e0\u4e3a\u6015\u4ed6\u76d1\u89c6\u4e4b\u6545\u3002\u597d\uff0c\u4f60\u73b0\u5728 \u88c5\u4f5c\u60ca\u60f6\u7684\u6a21\u6837\uff0c\u53eb\u4e00\u58f0\u5c0f\u7389\u513f\uff0c\u5148\u628a\u8fd9\u4e2a\u4eba\u5f15\u6765\uff01\u201d \u3000\u3000\u5218\u743c\u59d1\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\u4e00\u5b9a\u4f1a\u6765\u5417\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u4f60\u9a6c\u4e0a\u5c31\u53ef\u4ee5\u77e5\u9053\u4e86\uff0c\u4f55\u987b\u518d\u95ee\u3002\u201d \u3000\u3000\u5218\u743c\u59d1\u60ca\u7591\u4e0d\u5b9a\uff0c\u5c16\u58f0\u53eb\u9053\uff1a\u201c\u5c0f\u7389\u513f\uff01\u201d\u4e0d\u7528\u5047\u88c5\uff0c\u58f0\u97f3\u5df2\u662f\u53d1\u6296\u3002 \u3000\u3000\u8fc7\u4e86\u7247\u523b\uff0c\u53ea\u89c1\u90a3\u4e2a\u201c\u4ec6\u4eba\u201d\u679c\u7136\u5306\u5306\u8dd1\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u5218\u5c0f\u59d0\uff0c\u6709\u4ec0\u4e48\u4e8b\u60c5\u53ef\u4ee5\u8ba9\u6211\u4ee3 \u52b3\u5417\uff1f\u5c0f\u7389\u513f\u5df2\u7ecf\u7761\u4e86\u3002\u201d \u3000\u3000\u5218\u743c\u59d1\u9053\uff1a\u201c\u4f60\u3001\u4f60\u8fdb\u6765\u5427\uff01\u201d \u3000\u3000\u891a\u4e91\u5cf0\u4f0f\u5728\u95e8\u540e\uff0c\u90a3\u4e2a\u4eba\u811a\u6b65\u521a\u4e00\u8e0f\u8fdb\uff0c\u891a\u4e91\u5cf0\u500f\u5730\u5c31\u6293\u7740\u4ed6\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u8fd8\u8ba4\u5f97 \u6211\u891a\u4e91\u5cf0\u5417\uff0c\u8981\u547d\u7684\u5feb\u8bf4\u5b9e\u8bdd\uff01\u201d \u3000\u3000\u8fd9\u4eba\u4e0e\u891a\u4e91\u5cf0\u540c\u4e8b\u591a\u5e74\uff0c\u6df1\u77e5\u4ed6\u7684\u5389\u5bb3\uff0c\u6b64\u65f6\u891a\u4e91\u5cf0\u4e00\u624b\u6293\u7740\u4ed6\uff0c\u4e00\u624b\u6309\u5728\u4ed6\u7684\u540e\u5fc3\uff0c \u53ea\u8981\u201c\u5929\u96f7\u529f\u201d\u4e00\u53d1\uff0c\u5c31\u53ef\u4ee5\u9707\u65ad\u4ed6\u7684\u5fc3\u8109\uff01 \u3000\u3000\u8fd9\u4eba\u5fc3\u5934\u5927\u9707\uff0c\u5f3a\u4f5c\u9547\u5b9a\uff0c\u8bf4\u9053\uff1a\u201c\u891a\u4e91\u5cf0\uff0c\u4f60\u6740\u4e86\u6211\u4e5f\u7edd\u4e0d\u80fd\u9003\u51fa\u8fd9\u91cc\uff01\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u8fd9\u4e2a\u4e0d\u7528\u4f60\u7ed9\u6211\u62c5\u5fc3\uff0c\u4f60\u5148\u66ff\u4f60\u7684\u6027\u547d\u6253\u7b97\u5427\uff01\u201d \u3000\u3000\u8fd9\u4eba\u8bf4\u9053\uff1a\u201c\u4f60\u8981\u6211\u8bf4\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u4f60\u8bf4\uff0c\u4f60\u662f\u4e0d\u662f\u91d1\u56fd\u56fd\u5e08\u5e9c\u7684\u4f8d\u536b\uff1f\u662f\u9633\u5929\u96f7\u628a\u4f60\u5b89\u63d2\u5728\u8fd9\u91cc\u5192\u5145\u4ec6\u4eba \u7684\u3002\u201d \u3000\u3000\u8fd9\u4eba\u8bf4\u9053\uff1a\u201c\u6211\u662f\u5949\u547d\u800c\u4e3a\uff0c\u8eab\u4e0d\u7531\u5df1\u3002\u201d\u6b64\u8bdd\u7b49\u4e8e\u662f\u5df2\u627f\u8ba4\u4e86\u891a\u4e91\u5cf0\u6240\u8bf4\u7684\u662f\u4e8b\u5b9e\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u5218\u59d1\u5a18\uff0c\u4f60\u542c\u89c1\u4e86\u5427\uff1f\u201d\u4e2d\u6307\u4e00\u6233\uff0c\u70b9\u4e86\u8fd9\u4eba\u7684\u7a74\u9053\uff0c\u5728\u4ed6\u8eab\u4e0a\u6478 \u51fa\u4e86\u4e00\u9762\u91d1\u724c\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u628a\u8fd9\u9762\u91d1\u724c\u9012\u7ed9\u5218\u743c\u59d1\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u82e5\u8fd8\u4e0d\u4fe1\uff0c\u53ef\u4ee5\u518d\u770b\u8fd9\u4e2a\uff0c\u8fd9\u662f\u51fa\u5165\u56fd\u5e08\u5e9c \u6240\u7528\u7684\u8170\u724c\uff01\u201d \u3000\u3000\u5218\u743c\u59d1\u5446\u5446\u7684\u628a\u91d1\u724c\u63a5\u5230\u624b\u4e2d\uff0c\u53ea\u89c1\u4e0a\u9762\u523b\u6709\u5f2f\u5f2f\u66f2\u66f2\u7684\u5973\u771f\u6587\u5b57\u3002 \u3000\u3000\u5218\u743c\u59d1\u867d\u7136\u4e0d\u8bc6\u5973\u771f\u6587\u5b57\uff0c\u4f46\u4e00\u4e2a\u4ec6\u4eba\u7684\u8eab\u4e0a\uff0c\u5c82\u80fd\u6709\u4e00\u9762\u91d1\u724c\uff1f\u5218\u743c\u59d1\u5fc3\u91cc\u60f3\u9053\uff1a \u201c\u8fd9\u4eba\u5373\u4f7f\u5728\u891a\u4e91\u5cf0\u7684\u5a01\u80c1\u4e4b\u4e0b\uff0c\u4e0d\u80fd\u4e0d\u7167\u4ed6\u7684\u8bdd\u8bf4\uff0c\u4f46\u8fd9\u9762\u91d1\u724c\uff0c\u5374\u662f\u94c1\u8bc1\u4e86\uff01\u201d \u3000\u3000\u5218\u743c\u59d1\u5446\u4e86\u4e00\u4f1a\uff0c\u5fc3\u4e2d\u518d\u60f3\uff1a\u201c\u8fd9\u4eba\u662f\u56fd\u5e08\u5e9c\u7684\u4f8d\u536b\uff0c\u90a3\u4e48\u891a\u4e91\u5cf0\u521a\u624d\u6307\u63a7\u5c60\u9f99\u7684\u90a3\u4e9b \u8bf4\u8bdd\u4e5f\u5c31\u662f\u771f\u7684\u4e86\u3002\u201d\u60f3\u81f3\u6b64\u5904\uff0c\u4e0d\u7531\u5f97\u5fc3\u4e71\u5982\u9ebb\uff0c\u516d\u795e\u65e0\u4e3b\uff0c\u60f3\u54ed\u4e5f\u54ed\u4e0d\u51fa\u6765\uff0c\u53ea\u662f\u91cd\u590d \u8bf4\u9053\uff1a\u201c\u6211\u600e\u4e48\u529e\uff1f\u6211\u5e94\u8be5\u600e\u4e48\u529e\u5462\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u597d\uff0c\u4f60\u73b0\u5728\u53ef\u4ee5\u628a\u5c0f\u7389\u513f\u53eb\u6765\u4e86\uff01\u201d\u8bf4\u7f62\uff0c\u95ea\u5230\u5c4f\u98ce\u540e\u9762\u3002 \u3000\u3000\u5218\u743c\u59d1\u597d\u50cf\u6728\u5076\u4e00\u6837\u542c\u4ed6\u6446\u5e03\uff0c\u501a\u7740\u623f\u95e8\uff0c\u6709\u6c14\u65e0\u529b\u7684\u8fde\u53eb\u51e0\u58f0\uff1a\u201c\u5c0f\u7389\u513f\uff0c\u5c0f\u7389\u513f\uff0c \u5c0f\u7389\u513f\uff01\u201d \u3000\u3000\u5c0f\u7389\u513f\u5e76\u6ca1\u6709\u7761\u7740\uff0c\u5176\u5b9e\u5979\u662f\u65e9\u5c31\u542c\u5f97\u5218\u743c\u59d1\u53eb\u5979\u7684\u4e86\u3002\u4f46\u56e0\u5979\u542c\u5f97\u90a3\u4eba\u7684\u811a\u6b65\u58f0\u5df2\u7ecf \u8d70\u53bb\uff0c\u662f\u4ee5\u4e0d\u6562\u51fa\u6765\u3002 \u3000\u3000\u6b64\u65f6\u5979\u542c\u5f97\u5218\u743c\u59d1\u8fde\u58f0\u547c\u5524\uff0c\u90a3\u4eba\u53c8\u6ca1\u6709\u51fa\u58f0\u62e6\u963b\uff0c\u5979\u8fd9\u624d\u6562\u5927\u7740\u80c6\u5b50\u51fa\u6765\u3002 \u3000\u3000\u5c0f\u7389\u513f\u8d70\u8ff8\u5218\u743c\u59d1\u7684\u5367\u623f\uff0c\u53ea\u89c1\u90a3\u4e2a\u4eba\u8eba\u5728\u5730\u4e0a\uff0c\u4e0d\u7531\u5f97\u731b\u5403\u4e00\u60ca\uff0c\u5931\u58f0\u53eb\u9053\uff1a\u201c\u5218\u59d1 \u5a18\uff0c\u8fd9\u3001\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u4ece\u5c4f\u98ce\u80cc\u540e\u51fa\u6765\uff0c\u7b11\u9053\uff1a\u201c\u5c0f\u7389\u513f\uff0c\u4e0d\u5fc5\u5bb3\u6015\uff0c\u4f60\u8fd8\u8ba4\u5f97\u6211\u4e48\uff1f\u201d\u4ed6\u5728\u5c4f\u98ce\u540e\u9762 \u4e1a\u5df2\u62b9\u53bb\u4e86\u8138\u4e0a\u7684\u6cb9\u5f69\uff0c\u9732\u51fa\u4e86\u672c\u6765\u7684\u9762\u76ee\u4e86\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u5728\u201c\u56fd\u5e08\u5e9c\u201d\u7684\u65f6\u5019\uff0c\u662f\u5e38\u5e38\u9017\u5c0f\u7389\u513f\u73a9\u7684\uff0c\u5c0f\u7389\u513f\u4e5f\u662f\u628a\u4ed6\u5f53\u4f5c\u5927\u54e5\u54e5\u4e00\u822c\uff0c \u6b64\u65f6\u7a81\u7136\u770b\u89c1\u4ed6\uff0c\u4e0d\u7531\u5f97\u5446\u4e86\u3002 \u3000\u3000\u5c0f\u7389\u513f\u5982\u5728\u68a6\u4e2d\uff0c\u53c8\u60ca\u53c8\u559c\uff0c\u8bf4\u9053\uff1a\u201c\u891a\u5927\u54e5\uff0c\u662f\u4f60\uff0c\u4f60\u600e\u4e48\u77e5\u9053\u6211\u5728\u8fd9\u513f\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u6211\u5df2\u7ecf\u89c1\u8fc7\u4f60\u7239\u7239\u4e86\uff0c\u4f60\u7239\u7239\u544a\u8bc9\u6211\u7684\u3002\u5c0f\u7389\u513f\uff0c\u4f60\u613f\u610f\u5e2e\u6211\u4eec\u4e00\u4e2a\u5fd9 \u5417\uff1f\u201d \u3000\u3000\u5c0f\u7389\u513f\u9053\uff1a\u201c\u891a\u5927\u54e5\uff0c\u662f\u4f60\u7684\u4e8b\u60c5\uff0c\u6211\u62fc\u7740\u4e00\u6761\u547d\uff0c\u4e5f\u662f\u975e\u5f97\u5e2e\u4f60\u4e0d\u53ef\uff01\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u597d\uff0c\u4f60\u5148\u628a\u5b9e\u60c5\u544a\u8bc9\u5218\u59d1\u5a18\u3002\u201d \u3000\u3000\u5218\u743c\u59d1\u542c\u4e86\u5c0f\u7389\u513f\u6240\u8bf4\u7684\u8bdd\uff0c\u66f4\u662f\u60ca\u5f97\u76ee\u77aa\u53e3\u5446\uff0c\u534a\u664c\u624d\u8bf4\u5f97\u51fa\u8bdd\u6765\uff1a\u201c\u891a\u5927\u54e5\uff0c\u591a\u4e8f \u4f60\u6765\uff0c\u8981\u4e0d\u7136\u6211\u53ef\u8981\u505a\u51fa\u5929\u5927\u7684\u9519\u4e8b\u4e86\u3002\u201d\u60f3\u8d77\u5c60\u9f99\u9a97\u5979\u5199\u90a3\u4e00\u5c01\u4fe1\u7684\u4e8b\u60c5\uff0c\u5982\u679c\u81ea\u5df1\u5f53\u771f \u662f\u5199\u4e86\u7684\u8bdd\uff0c\u90a3\u5c31\u4e0d\u4f46\u81ea\u5df1\u53d7\u5bb3\uff0c\u8fde\u54e5\u54e5\u4e5f\u53d7\u5bb3\u3002 \u3000\u3000\u5c0f\u7389\u513f\u9053\uff1a\u201c\u5218\u59d1\u5a18\uff0c\u4f60\u5feb\u70b9\u548c\u891a\u5927\u54e5\u79bb\u5f00\u8fd9\u4e2a\u9b54\u7a9f\u5427\uff01\u201d \u3000\u3000\u5218\u743c\u59d1\u9053\uff1a\u201c\u6211\u8d70\u4e86\u4f60\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u5c0f\u7389\u513f\uff0c\u8fd9\u5168\u8981\u9760\u4f60\u5e2e\u5fd9\u4e86\uff01\u201d \u3000\u3000\u5c0f\u7389\u513f\u6025\u9053\uff1a\u201c\u4ed6\u4eec\u6050\u6015\u5c31\u8981\u56de\u6765\u4e86\uff0c\u4f60\u8981\u6211\u5e2e\u5fd9\u4ec0\u4e48\u5feb\u70b9\u8bf4\u5427\uff01\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u5c0f\u7389\u513f\uff0c\u53ef\u8981\u8ba9\u4f60\u53d7\u70b9\u59d4\u5c48\u5462\u3002\u201d \u3000\u3000\u5c0f\u7389\u513f\u9000\uff1a\u201c\u891a\u5927\u54e5\uff0c\u6211\u62fc\u7740\u4e09\u5200\u516d\u6d1e\uff0c\u51b3\u4e0d\u76b1\u7709\uff0c\u53d7\u70b9\u59d4\u5c48\uff0c\u7b97\u5f97\u4e86\u4ec0\u4e48\uff0c\u4f60\u5feb\u8bf4 \u5427\uff01\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u5218\u59d1\u5a18\uff0c\u4f60\u6362\u4e0a\u5c0f\u7389\u513f\u7684\u8fd9\u8eab\u8863\u88f3\uff0c\u7136\u540e\u70b9\u5979\u7684\u54d1\u7a74\u548c\u9ebb\u7a74\u3002\u201d \u3000\u3000\u5218\u743c\u59d1\u597d\u50cf\u6b63\u5728\u60f3\u7740\u4e00\u4e9b\u4ec0\u4e48\uff0c\u5fc3\u795e\u4e0d\u5b9a\u5730\u6728\u7136\u8bf4\u9053\uff1a\u201c\u6211\u70b9\u5979\u7684\u7a74\u9053\uff1f\u201d \u3000\u3000\u5c0f\u7389\u513f\u604d\u7136\u5927\u609f\uff0c\u62cd\u638c\u8bf4\u9053\uff1a\u201c\u597d\u4e3b\u610f\uff0c\u6211\u660e\u767d\u4e86\u3002\u891a\u5927\u54e5\uff0c\u4f60\u662f\u6015\u8fde\u7d2f\u4e86\u6211\uff0c\u6240\u4ee5\u53eb \u5218\u59d1\u5a18\u70b9\u4e86\u6211\u7684\u7a74\u9053\uff0c\u624d\u597d\u9003\u8d70\u3002\u201d \u3000\u3000\u5218\u743c\u59d1\u832b\u7136\u5730\u671b\u7740\u891a\u4e91\u5cf0\uff0c\u534a\u664c\u8bf4\u9053\uff1a\u201c\u9003\u8d70\uff0c\u6211\u548c\u4f60\u4e00\u540c\u9003\u8d70\uff1f\u201d \u3000\u3000\u5c0f\u7389\u513f\u7b11\u9053\uff1a\u201c\u5f53\u7136\u662f\u548c\u891a\u5927\u54e5\u4e00\u540c\u8d70\u4e86\uff0c\u96be\u9053\u8fd8\u548c\u6211\u4e48\uff1f\u8fd9\u91cc\u662f\u56fd\u5e08\u5e9c\u7684\u82b1\u56ed\uff0c\u5728\u8fd9 \u4e2a\u5c0f\u56ed\u5b50\u5916\u9762\u8fd8\u5728\u5927\u56ed\u5b50\uff0c\u5904\u5904\u90fd\u6709\u57cb\u4f0f\uff0c\u6ca1\u6709\u719f\u4eba\u5e26\u8def\uff0c\u662f\u51b3\u8ba1\u8d70\u4e0d\u51fa\u53bb\u7684\u3002\u4e0d\u8fc7\uff0c\u4f60\u53ef \u4ee5\u653e\u5fc3\uff0c\u891a\u5927\u54e5\u662f\u8bc6\u9014\u8001\u9a6c\uff0c\u672c\u9886\u53c8\u9ad8\uff0c\u4ed6\u4e00\u5b9a\u4f1a\u628a\u4f60\u5e73\u5b89\u5e26\u5230\u5916\u9762\u7684\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u4eca\u665a\u6ca1\u6709\u6708\u4eae\uff0c\u73b0\u5728\u53c8\u6b63\u5728\u4e0b\u7740\u96e8\uff0c\u8fd9\u6b63\u662f\u5341\u5206\u96be\u5f97\u7684\u597d\u673a\u4f1a\u3002\u4f60\u6362\u4e0a\u5c0f \u7389\u513f\u7684\u8863\u88f3\uff0c\u6211\u6362\u4e0a\u8fd9\u4e2a\u4f8d\u536b\u7684\u8863\u88f3\uff0c\u5728\u8fd9\u9ed1\u591c\u91cc\u6211\u60f3\u662f\u53ef\u4ee5\u6df7\u5f97\u8fc7\u53bb\u7684\u3002\u90a3\u9762\u91d1\u724c\uff0c\u8bf7\u4f60 \u8d34\u8eab\u85cf\u597d\uff0c\u4e07\u4e00\u6709\u4ec0\u4e48\u610f\u5916\uff0c\u62ff\u51fa\u8fd9\u9762\u91d1\u813e\uff0c\u4e5f\u53ef\u4ee5\u6743\u5145\u62a4\u8eab\u7b26\u7684\u3002\u5230\u4e86\u5916\u9762\uff0c\u4f60\u559c\u6b22\u56de\u5bb6 \u4e5f\u597d\uff0c\u8ddf\u4f60\u54e5\u54e5\u4e5f\u597d\uff0c\u968f\u4f60\u7684\u4fbf\uff0c\u6211\u662f\u51b3\u4e0d\u4f1a\u52c9\u5f3a\u4f60\u7684\u3002\u201d\u8bdd\u4e2d\u4e4b\u610f\uff0c\u5373\u662f\u5411\u5218\u743c\u59d1\u6697\u793a\uff0c \u51b3\u4e0d\u5e72\u6d89\u5979\u7684\u884c\u52a8\u3002\u539f\u6765\u891a\u4e91\u5cf0\u542c\u4e86\u5979\u521a\u624d\u7684\u90a3\u53e5\u8bf4\u8bdd\uff0c\u4ee5\u4e3a\u5979\u662f\u4e0d\u613f\u610f\u4e0e\u4ed6\u540c\u8d70\uff0c\u4e0d\u89c9\u591a \u5c11\u6709\u4e86\u4e00\u70b9\u8bef\u4f1a\u3002\u5c82\u77e5\u5218\u743c\u59d1\u6240\u60f3\u7684\u5b8c\u5168\u4e0d\u662f\u8fd9\u4e00\u56de\u4e8b\u3002 \u3000\u3000\u5c0f\u7389\u513f\u9053\uff1a\u201c\u5bf9\uff0c\u5218\u59d1\u5a18\uff0c\u4f60\u5feb\u70b9\u548c\u6211\u6362\u8863\u88f3\u3002\u201d\u5ffd\u5730\u60f3\u8d77\u4e00\u4e8b\uff0c\u6307\u7740\u90a3\u4e2a\u88ab\u891a\u4e91\u5cf0\u70b9 \u4e86\u7a74\u9053\u7684\u536b\u58eb\u8bf4\u9053\uff1a\u201c\u8fd9\u4eba\u600e\u6837\u5904\u7f6e\uff1f\u201d\u5979\u662f\u6015\u5c60\u9f99\u56de\u6765\u4e4b\u540e\uff0c\u8fd9\u4e2a\u536b\u571f\u4f1a\u8910\u7a7f\u5979\u662f\u540c\u8c0b\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u8fd9\u5bb9\u6613\u529e\uff01\u201d\u7a81\u7136\u4e00\u638c\u5411\u90a3\u536b\u58eb\u62cd\u4e0b\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u79cd\u5974\u624d\uff0c\u6740\u4ed6\u4e5f\u4e0d\u51a4 \u6789\uff01\u201d \u3000\u3000\u5c0f\u7389\u513f\u5413\u4e86\u4e00\u8df3\uff0c\u53ea\u89c1\u8fd9\u536b\u58eb\u54fc\u4e5f\u4e0d\u54fc\u4e00\u58f0\uff0c\u8eab\u4e0a\u4e5f\u6ca1\u4f24\u75d5\uff0c\u5c31\u50cf\u50f5\u5c38\u4f3c\u7684\u8eba\u5728\u5730\u4e0a\uff0c \u9f3b\u5b54\u4e5f\u6ca1\u6709\u6c14\u51fa\u4e86\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u5c60\u9f99\u662f\u8bc6\u5f97\u6211\u8fd9\u5929\u96f7\u529f\u7684\uff0c\u6211\u6740\u8fd9\u53ae\uff0c\u4e00\u6765\u662f\u4e3a\u4e86\u6740\u9e21\u5413\u7334\uff0c\u4e8c\u672a\u4e5f\u53ef\u4ee5 \u52a9\u4f60\u7f6e\u8eab\u4e8b\u5916\u3002\u4f60\u53ef\u4ee5\u62db\u8ba4\u662f\u6211\u95ef\u8fdb\u6765\u6740\u4e86\u8fd9\u4eba\uff0c\u70b9\u4e86\u4f60\u7684\u7a74\u9053\uff0c\u53c8\u62a2\u4e86\u5218\u59d1\u5a18\u7684\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u628a\u4ed6\u7684\u8ba1\u5212\u8bf4\u4e86\u4e4b\u540e\uff0c\u6b63\u60f3\u8d70\u51fa\u53bb\u56de\u907f\uff0c\u597d\u8ba9\u5218\u743c\u59d1\u6362\u8863\u3002\u5218\u743c\u59d1\u5ffd\u9053\uff1a\u201c\u891a\u5927 \u54e5\uff0c\u591a\u8c22\u4f60\u7684\u597d\u610f\u3002\u6211\u4e0d\u9003\u8d70\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u6014\u4e86\u4e00\u6014\uff0c\u8bf4\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u5c0f\u7389\u513f\u9053\uff1a\u201c\u5218\u59d1\u5a18\uff0c\u96be\u9053\u4f60\u8fd8\u4e0d\u76f8\u4fe1\u6211\u4eec\u7684\u8bdd \u4e48\uff1f\u8fd8\u662f\u820d\u4e0d\u5f97\u79bb\u5f00\u5c60\u9f99\u8fd9\u53ae\uff1f\u201d\u5c0f\u7389\u513f\u4e0d\u61c2\u907f\u5fcc\uff0c\u51b2\u53e3\u800c\u51fa\uff0c\u5c31\u628a\u891a\u4e91\u5cf0\u5fc3\u7528\u6240\u60f3\u7684\u8bdd\u8bf4 \u51fa\u6765\u3002 \u3000\u3000\u5218\u743c\u59d1\u54ac\u7259\u8bf4\u9053\uff1a\u201c\u6211\u6068\u4e0d\u5f97\u5403\u8fd9\u53ae\u7684\u8089\uff0c\u6495\u8fd9\u53ae\u7684\u76ae\u3002\u201d \u3000\u3000\u5c0f\u7389\u513f\u9053\uff1a\u201c\u90a3\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u8d70\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u541b\u5b50\u62a5\u4ec7\uff0c\u5341\u5e74\u672a\u665a\u3002\u5218\u59d1\u5a18\uff0c\u4f60\u5343\u4e07\u4e0d\u53ef\u8f7b\u4e3e\u5984\u52a8\uff01\u201d\u4ed6\u7684\u89c1\u8bc6\u81ea\u662f\u8981 \u6bd4\u5c0f\u7389\u513f\u80dc\u8fc7\u8bb8\u591a\uff0c\u5fc3\u4e2d\u5df2\u662f\u9690\u9690\u731c\u60f3\u5f97\u5230\u5218\u743c\u59d1\u662f\u8981\u624b\u5203\u5c60\u9f99\uff0c\u4ee5\u96ea\u53d7\u9a97\u4e4b\u8fb1\u3002 \u3000\u3000\u5218\u743c\u59d1\u5374\u9053\uff1a\u201c\u891a\u5927\u54e5\uff0c\u4f60\u731c\u9519\u4e86\uff0c\u8fd9\u4ec7\u627e\u662f\u8981\u62a5\u7684\uff0c\u4f46\u6b64\u9645\u6211\u8fd8\u4e0d\u80fd\u79bb\u5f00\u8fd9\u53ae\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u83ab\u540d\u5947\u5999\uff0c\u8bf4\u9053\uff1a\u201c\u65e2\u7136\u4e0d\u662f\u60f3\u5728\u6b64\u523b\u62a5\u4ec7\uff0c\u90a3\u53c8\u4e3a\u4f55\u4e0d\u8d70\uff1f\u201d \u3000\u3000\u5218\u743c\u59d1\u9053\uff1a\u201c\u891a\u5927\u54e5\uff0c\u4f60\u521a\u624d\u8bf4\u662f\u4e3a\u4e86\u522b\u7684\u4e8b\u60c5\u6765\u7684\uff0c\u4ec0\u4e48\u4e8b\u60c5\uff0c\u53ef\u4ee5\u544a\u8bc9\u6211\u5417\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u5fc3\u4e2d\u4e00\u52a8\uff0c\u60f3\u9053\uff1a\u201c\u83ab\u975e\u5979\u4ece\u5c60\u9f99\u53e3\u4e2d\uff0c\u66fe\u542c\u5230\u4e86\u4ec0\u4e48\u6d88\u606f\u3002\u201d\u4e8e\u662f\u4fbf\u628a\u4ed6\u8981\u8425 \u6551\u674e\u601d\u5357\u7684\u56e0\u60c5\uff0c\u5982\u5b9e\u5730\u8bf4\u4e86\u51fa\u6765\u3002 \u3000\u3000\u5218\u743c\u59d1\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u662f\u4e0d\u662f\u5317\u4e94\u7701\u4e49\u519b\u76df\u4e3b\u674e\u601d\u5357\uff1f\u201d\u8981\u77e5\u674e\u601d\u5357\u65b0\u4efb\u76df\u4e3b\u4e4b \u540e\uff0c\u58f0\u540d\u8fdc\u64ad\uff0c\u5218\u5bb6\u7236\u5973\uff0c\u867d\u662f\u50fb\u5904\u6df1\u5c71\u7a77\u8c37\uff0c\u4e5f\u66fe\u542c\u4eba\u8bf4\u8fc7\u4ed6\u7684\u540d\u5b57\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u6b63\u662f\u3002\u5c60\u9f99\u53ef\u66fe\u900f\u9732\u8fc7\u4ec0\u4e48\u98ce\u58f0\u3002\u201d \u3000\u3000\u5218\u743c\u59d1\u9053\uff1a\u201c\u6ca1\u6709\u3002\u4e0d\u8fc7\uff0c\u6211\u82e5\u662f\u8dd1\u5230\u4e86\u5916\u9762\u5c31\u66f4\u4e0d\u5bb9\u6613\u6253\u542c\u4e86\uff01\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u6211\u8fd8\u53ef\u4ee5\u53e6\u60f3\u529e\u6cd5\u7684\uff0c\u4f60\u9677\u8eab\u9b54\u7a9f\uff0c\u8fd8\u662f\u65e9\u8d70\u4e3a\u5b9c\uff01\u54ce\u5440\uff0c\u4f60\uff0c\u4f60\u2014\u2014\u201d \u3000\u3000\u539f\u6765\u891a\u4e91\u5cf0\u8bdd\u72b9\u672a\u4e86\uff0c\u53ea\u89c1\u5200\u5149\u53e3\u95ea\uff0c\u5218\u743c\u59d1\u7a81\u7136\u62d4\u51fa\u5315\u9996\u523a\u5728\u81ea\u5df1\u7684\u8eab\u4e0a\uff0c\u891a\u4e91\u5cf0\u8fde \u5fd9\u62a2\u4e86\u5979\u7684\u5315\u9996\uff0c\u53ef\u662f\u5979\u7684\u53f3\u81c2\u65e9\u5df2\u5212\u5f00\u4e00\u9053\u4e09\u5bf8\u591a\u957f\u7684\u4f24\u53e3\u4e86\u3002 \u3000\u3000\u5218\u743c\u59d1\u9053\uff1a\u201c\u6211\u88c5\u4f5c\u662f\u62b5\u6297\u4f60\u800c\u53d7\u4f24\u7684\uff0c\u5c60\u9f99\u6599\u60f3\u4e0d\u4f1a\u8d77\u7591\u3002\u6700\u597d\u4f60\u518d\u6253\u6211\u4e00\u638c\uff0c\u7528\u5929 \u96f7\u529f\u4ea6\u662f\u65e0\u59a8\uff0c\u53ea\u8981\u4e0d\u6b7b\u4fbf\u884c\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u5341\u5206\u611f\u52a8\uff0c\u600e\u5fcd\u518d\u7528\u5929\u96f7\u529f\u4f24\u5979\uff0c\u8bf4\u9053\uff1a\u201c\u5218\u59d1\u5a18\uff0c\u4f60\u4e0d\u6127\u662f\u5973\u4e2d\u82f1\u6770\uff0c\u891a\u67d0\u4f69 \u670d\u5f97\u7d27\uff0c\u5374\u662f\u2026\u2026\u201d \u3000\u3000\u5218\u743c\u59d1\u9053\uff1a\u201c\u4f60\u4e0d\u7528\u4e3a\u6211\u62c5\u5fc3\uff0c\u6211\u53d7\u4e86\u4f24\uff0c\u4ed6\u4e0d\u4f1a\u518d\u903c\u6211\u4e86\u3002\u4f55\u51b5\u6551\u674e\u516c\u5b50\u662f\u4e00\u4ef6\u5927 \u4e8b\u3002\u5373\u6211\u820d\u4e86\u6027\u547d\uff0c\u6211\u4e5f\u662f\u613f\u610f\u7684\uff0c\u6211\u505a\u9519\u4e86\u4e8b\uff0c\u4f60\u5c31\u8ba9\u6211\u5c06\u529f\u8d4e\u7f6a\u5427\uff01\u5bf9\u4e0d\u4f4f\uff0c\u5c0f\u7389\u513f\uff0c \u8bf7\u4f60\u4e5f\u53d7\u70b9\u59d4\u5c48\u4e86\uff01\u201d\u8bf4\u7f62\uff0c\u4e2d\u6307\u4e00\u4f38\uff0c\u70b9\u4e86\u5c0f\u7389\u513f\u7684\u7a74\u9053\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u77e5\u9053\u5979\u5fc3\u610f\u5df2\u51b3\uff0c\u8bf4\u9053\uff1a\u201c\u5218\u59d1\u5a18\uff0c\u4f60\u591a\u591a\u4fdd\u91cd\uff0c\u4f46\u613f\u4f60\u80fd\u591f\u6210\u529f\u3002\u6211\uff0c\u6211\u8d70 \u4e86\uff01\u201d \u3000\u3000\u5218\u743c\u59d1\u9053\uff1a\u201c\u6211\u5f97\u5230\u6d88\u606f\uff0c\u5c31\u8bf7\u5c0f\u7389\u513f\u7684\u7239\u7239\u9001\u51fa\u53bb\u3002\u4f60\u7559\u4e2a\u5730\u5740\u7ed9\u6211\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u8bf4\u51fa\u4e10\u5e2e\u5728\u201c\u56fd\u5e08\u5e9c\u201d\u4e2d\u4e00\u4e2a\u5367\u5e95\u7684\u4eba\u7684\u540d\u5b57\uff0c\u8bf4\u9053\uff1a\u201c\u6709\u4ec0\u4e48\u6d88\u606f\uff0c\u53eb\u5c0f\u4e94\u513f \u7684\u7239\u4ea4\u7ed9\u6b64\u4eba\u3002\u201d\u8bf4\u7f62\u62fe\u8d77\u4e86\u5730\u4e0a\u7684\u5315\u9996\uff0c\u5411\u5218\u743c\u59d1\u6df1\u6df1\u4e00\u63d6\uff0c\u53ea\u89c1\u5218\u743c\u59d1\u7684\u773c\u89d2\u6709\u6676\u83b9\u7684 \u6cea\u73e0\uff0c\u891a\u4e91\u5cf0\u4e0d\u6562\u56de\u5934\uff0c\u4fbf\u5373\u8d70\u4e86\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u8d70\u51fa\u8fd9\u4e2a\u9662\u5b50\uff0c\u542c\u5f97\u201c\u7b03\u7b03\u7b03\u201d\u7684\u4e09\u4e0b\u58f0\u54cd\uff0c\u503c\u591c\u7684\u66f4\u592b\u6b63\u6253\u4e09\u66f4\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u5fc3\u60f3\uff1a\u201c\u6db5\u865a\u4e00\u5b9a\u7b49\u5f97\u5fc3\u7126\u4e86\uff01\u201d\u6b63\u8981\u6084\u6084\u5730\u6e9c\u51fa\u201c\u7a3b\u9999\u6751\u201d\uff0c\u5ffd\u53c8\u542c\u5f97\u4e00\u4e2a\u597d \u4f3c\u662f\u719f\u4eba\u7684\u58f0\u97f3\u3002 \u3000\u3000\u5218\u743c\u59d1\u4f4f\u7684\u8fd9\u5ea7\u9662\u5b50\u662f\u5728\u4e00\u4e2a\u5927\u56f4\u5899\u4e4b\u5185\u7684\uff0c\u8fd8\u6709\u4e24\u5ea7\u9662\u5b50\u5404\u5728\u4e00\u8fb9\uff0c\u5185\u4e2d\u53c8\u6709\u56f4\u5899\u9694 \u5f00\uff0c\u891a\u4e91\u5cf0\u7ec3\u8fc7\u201c\u542c\u58f0\u8fa8\u5668\u201d\u7684\u529f\u592b\uff0c\u51dd\u795e\u4e00\u542c\uff0c\u542c\u51fa\u4e86\u58f0\u97f3\u7684\u65b9\u5411\u6765\u81ea\u4e1c\u8fb9\u7684\u9662\u5b50\u3002\u891a\u4e91 \u5cf0\u8d70\u8fd1\u53bb\u542c\uff0c\u53ea\u542c\u5f97\u90a3\u4eba\u8bf4\u9053\uff1a\u201c\u97e9\u4e94\u7237\uff0c\u5c0f\u7684\u62dc\u5e16\u4e0d\u77e5\u9001\u7ed9\u4e86\u56fd\u5e08\u7237\u6ca1\u6709\uff1f\u56fd\u5e08\u7237\u9762\u524d\uff0c \u8bf7\u4f60\u8001\u591a\u591a\u7f8e\u8a00\u51e0\u53e5\uff01\u201d \u3000\u3000\u891a\u4e91\u5cf0\u542c\u5f97\u5206\u660e\uff0c\u4e0d\u7531\u5f97\u5927\u5403\u4e00\u60ca\u3002\u539f\u6765\u8fd9\u4e2a\u4eba\u6b63\u662f\u4ed6\u5728\u53f2\u5bb6\u5e84\u53d1\u73b0\u7684\u90a3\u4e2a\u5978\u7ec6\u5362\u9999 \u4ead\uff0c\u90a3\u4e2a\u6df7\u8fdb\u53f2\u5bb6\uff0c\u62dc\u5728\u53f2\u8001\u82f1\u96c4\u53f2\u7528\u5a01\u95e8\u4e0b\u7684\u5f1f\u5b50\u4e01\u8fdb\u5c31\u662f\u4ed6\u6240\u8350\u7684\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u53ae\u5012\u662f\u795e\u901a\u5e7f\u5927\uff0c\u5c45\u7136\u7792\u8fc7\u4e86\u4e10\u5e2e\u7684\u8033\u76ee\uff0c\u5230\u4e86\u5927\u90fd\uff0c\u8fdb\u4e86\u56fd\u5e08 \u5e9c\u4e86\u3002\u201d \u3000\u3000\u90a3\u4e2a\u201c\u97e9\u4e94\u7237\u201d\u4e5f\u662f\u201c\u56fd\u5e08\u5e9c\u201d\u4e2d\u5c48\u6307\u53ef\u6570\u7684\u51e0\u4e2a\u6c49\u4eba\u4f8d\u536b\u4e4b\u4e00\uff0c\u540d\u53eb\u97e9\u8d85\uff0c\u7cbe\u5e72\u9e70\u722a \u529f\uff0c\u5728\u6c49\u4eba\u4f8d\u536b\u4e4b\u4e2d\uff0c\u891a\u4e91\u5cf0\u4e0e\u4ed6\u4ea4\u60c5\u8f83\u597d\u3002 \u3000\u3000\u53ea\u542c\u5f97\u97e9\u8d85\u201c\u55ef\u201d\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u6025\u4ec0\u4e48\uff0c\u624d\u6765\u4e24\u5929\uff0c\u5c31\u60f3\u89c1\u56fd\u5e08\u7237\u4e86\uff1f\u201d \u3000\u3000\u5362\u9999\u4ead\u9053\uff1a\u201c\u8fd9\u662f\u4e00\u4ef6\u7d27\u8981\u7684\u4e8b\u60c5\uff0c\u8bf7\u4f60\u8001\u591a\u591a\u5e2e\u5fd9\u3002\u201d \u3000\u3000\u97e9\u8d85\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4ec0\u4e48\u7d27\u8981\u7684\u4e8b\u60c5\uff0c\u4e0d\u80fd\u548c\u6211\u4eec\u8bf4\u7684\u4e48\uff1f\u201d \u3000\u3000\u5362\u9999\u4ead\u9053\uff1a\u201c\u8bf7\u4f60\u8001\u5305\u6db5\uff0c\u8fd9\u4ef6\u4e8b\u6211\u60f3\u9762\u7980\u56fd\u5e08\u7237\u4e4b\u540e\uff0c\u518d\u5411\u4f60\u8001\u8bf7\u6559\u3002\u201d \u3000\u3000\u97e9\u8d85\u9053\uff1a\u201c\u4f60\u4e0d\u8bf4\u4e2a\u6e05\u695a\uff0c\u56fd\u5e08\u7237\u53ea\u6015\u6ca1\u7a7a\u89c1\u4f60\u5462\uff01\u201d \u3000\u3000\u53e6\u4e00\u4e2a\u5c11\u5e74\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u8bf7\u4e94\u7237\u591a\u4e3a\u7980\u544a\uff0c\u8bf4\u51fa\u6211\u662f\u53f2\u7528\u5a01\u7684\u95e8\u4e0b\u5f1f\u5b50\uff0c\u6216\u8005\u56fd\u5e08\u7237 \u4f1a\u89c1\u6211\u4eec\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u5fc3\u9053\uff1a\u201c\u539f\u6765\u59d3\u4e01\u8fd9\u4e2a\u5c0f\u5b50\u4e5f\u6765\u4e86\uff0c\u597d\uff0c\u8fd9\u53ef\u771f\u662f\u53eb\u505a\u5929\u7f51\u6062\u6062\uff0c\u758f\u800c\u4e0d\u6f0f\uff01\u201d \u3000\u3000\u8981\u77e5\u5362\u3001\u4e01\u4e8c\u4eba\u8d76\u6765\u5927\u90fd\uff0c\u4e43\u662f\u8981\u6765\u63ed\u53d1\u67f3\u6d1e\u5929\u7684\u79d8\u5bc6\u7684\uff0c\u5982\u4eca\u7ed9\u891a\u4e91\u5cf0\u649e\u4e0a\uff0c\u7109\u80fd\u653e \u8fc7\u4ed6\u4eec\uff1f\u662f\u4ee5\u891a\u4e91\u5cf0\u867d\u7136\u6025\u4e8e\u56de\u53bb\uff0c\u514d\u5f97\u8c37\u6db5\u865a\u6302\u8651\uff0c\u4f46\u89c1\u4e86\u8fd9\u4e24\u4e2a\u4eba\uff0c\u4e5f\u53ea\u597d\u6682\u4e14\u628a\u8d76\u56de \u53bb\u4f1a\u89c1\u8c37\u6db5\u8651\u7684\u4e8b\u60c5\u6401\u5728\u4e00\u8fb9\u4e86\u3002 \u3000\u3000\u97e9\u8d85\u53c8\u54fc\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u53f2\u7528\u5a01\u7684\u5f1f\u5b50\u53c8\u600e\u4e48\u6837\uff1f\u5e9c\u4e2d\u591a\u5c11\u5019\u89c1\u7684\u8d35\u5ba2\uff0c\u4f60\u77e5\u4e0d\u77e5 \u9053\uff1f\u201d \u3000\u3000\u53e6\u4e00\u4e2a\u6c49\u4eba\u4f8d\u536b\u5ffd\u5730\u7b11\u4e86\u8d77\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u4e94\u54e5\u522b\u4f5c\u5f04\u4ed6\u4eec\u4e86\uff0c\u4f60\u77a7\uff0c\u4ed6\u4eec\u4e8c\u4eba\u53ea\u5dee\u5411\u4f60 \u8dea\u4e0b\u6c42\u60c5\u5566\uff0c\u4f60\u4eec\u522b\u6025\uff0c\u6211\u544a\u8bc9\u4f60\u4eec\u5b9e\u8bdd\u5427\uff0c\u5c60\u9f99\u521a\u624d\u5949\u53ec\u8fdb\u53bb\uff0c\u6211\u4eec\u5df2\u8bf7\u4ed6\u5c06\u4f60\u7684\u4e8b\u60c5\u4ee3 \u4e3a\u7980\u544a\u4e86\u3002\u201d\u8fd9\u4e2a\u4f8d\u536b\u540d\u53eb\u7126\u9738\uff0c\u662f\u4e2a\u70ed\u8877\u540d\u5229\u7684\u5974\u624d\uff0c\u891a\u4e91\u5cf0\u4e00\u5411\u8ba8\u538c\u4ed6\u7684\u3002 \u3000\u3000\u5362\u9999\u4ead\u559c\u51fa\u671b\u5916\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u8bf4\u56db\u7687\u5b50\u53ec\u89c1\u5c60\u516c\u5b50\u7684\u4e48\uff1f\u201d \u3000\u3000\u7126\u9738\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f46\u54b1\u4eec\u7684\u56fd\u5e08\u7237\u5f53\u7136\u4e5f\u662f\u5728\u5ea7\u7684\u3002\u201d \u3000\u3000\u97e9\u8d85\u9053\uff1a\u201c\u7126\u9738\uff0c\u4f60\u6cc4\u6f0f\u5f97\u72ac\u591a\u4e86\u3002\u201d \u3000\u3000\u7126\u9738\u8d54\u7b11\u9053\uff1a\u201c\u90fd\u662f\u81ea\u5df1\u4eba\uff0c\u4e94\u54e5\uff0c\u4f60\u4e5f\u4e0d\u5fc5\u591a\u5fc3\u4e86\u3002\u201d\u4ed6\u4eec\u4e8c\u4eba\u867d\u662f\u4f8d\u536b\uff0c\u4f46\u97e9\u8d85\u5728 \u5e9c\u4e2d\u7684\u5730\u4f4d\u6bd4\u4ed6\u9ad8\u4e00\u7ea7\uff0c\u662f\u4ee5\u4ed6\u4e0d\u80fd\u4e0d\u8ba8\u597d\u97e9\u8d85\u3002\u4f46\u4ed6\u53c8\u60f3\u4ece\u5362\u9999\u4ead\u7684\u8fd9\u4ef6\u4e8b\u60c5\u5f97\u5230\u597d\u5904\uff0c \u6545\u6b64\u4e24\u8fb9\u5356\u597d\u3002 \u3000\u3000\u97e9\u8d85\u51b7\u7b11\u9053\uff1a\u201c\u8fd9\u4f4d\u5362\u4e09\u7237\u82e5\u662f\u628a\u54b1\u4eec\u5f53\u4f5c\u81ea\u5df1\u4eba\uff0c\u53c8\u4f55\u987b\u4e00\u5b9a\u8981\u9762\u89c1\u56fd\u5e08\u7237\u624d\u8bf4\u3002\u201d \u3000\u3000\u5362\u9999\u4ead\u9053\uff1a\u201c\u4e94\u7237\uff0c\u4f60\u522b\u591a\u5fc3\uff0c\u4e0d\u662f\u7792\u4f60\uff0c\u8fd9\uff0c\u8fd9\u2026\u2026\u201d\u97e9\u8d85\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4e0d\u8bf4\u4fbf\u7f62\uff0c \u54fc\uff0c\u4f60\u73b0\u5728\u8fd8\u672a\u89c1\u7740\u56fd\u5e08\u7237\u5462\uff01\u201d\u8a00\u4e0b\u5927\u6709\u8981\u631f\u4e4b\u610f\u3002 \u3000\u3000\u4f0f\u5728\u7a97\u5916\u6109\u542c\u7684\u891a\u4e91\u5cf0\u4e0d\u89c9\u5fc3\u4e2d\u4e00\u52a8\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u97e9\u8d85\u4f55\u4ee5\u4e00\u5b9a\u8981\u63a2\u542c\u4ed6\u7684\u79d8\u5bc6 \u5462\uff1f\u201d \u3000\u3000\u5362\u9999\u4ead\u662f\u4e2a\u8001\u72d0\u72f8\uff0c\u5f53\u7136\u542c\u5f97\u51fa\u97e9\u8d85\u7684\u8bdd\u4e2d\u4e4b\u610f\uff0c\u5fc3\u91cc\u4e0d\u89c9\u6697\u6697\u5403\u60ca\u3002 \u3000\u3000\u539f\u6765\u4ed6\u662f\u8981\u628a\u8fd9\u4e2a\u79d8\u5bc6\u5356\u7ed9\u9633\u5929\u96f7\uff0c\u4f5c\u8fdb\u8eab\u4e4b\u9636\u7684\u3002\u82e5\u5148\u5c31\u6cc4\u6f0f\u51fa\u53bb\uff0c\u4e0d\u4f46\u6050\u6015\u522b\u4eba\u5206 \u529f\uff0c\u751a\u81f3\u8fd8\u5f97\u63d0\u9632\u522b\u4eba\u5c06\u4ed6\u6487\u5f00\u3002 \u3000\u3000\u53ef\u662f\u97e9\u8d85\u8bed\u542b\u5a01\u80c1\u4e4b\u606f\uff0c\u5484\u5484\u903c\u4eba\uff0c\u4ed6\u53c8\u6015\u97e9\u8d85\u5f53\u771f\u4f7f\u51fa\u4ec0\u4e48\u624b\u6bb5\uff0c\u4ece\u4e2d\u7834\u574f\u3002\u5728\u60a3\u5f97 \u60a3\u5931\u7684\u5fc3\u60c5\u4e4b\u4e0b\uff0c\u6697\u81ea\u601d\u91cf\uff1a\u201c\u4e0d\u8bf4\u6050\u6015\u4e0d\u884c\uff0c\u6211\u7ed9\u4ed6\u8bf4\u4e00\u534a\u4fbf\u7f62\u3002\u201d \u3000\u3000\u6b63\u5728\u5362\u9999\u4ead\u5f20\u53e3\u8981\u8bf4\u4e4b\u9645\uff0c\u5ffd\u542c\u5f97\u97e9\u8d85\u559d\u9053\uff1a\u201c\u662f\u8c01\uff1f\u201d\u4e00\u4e2a\u4eba\u7a81\u7136\u95ef\u8fdb\uff01 \u3000\u3000\u4e0d\u7528\u8bf4\u8fd9\u4e2a\u4eba\u662f\u891a\u4e91\u5cf0\u4e86\uff01 \u3000\u3000\u891a\u4e91\u5cf0\u7a7f\u7740\u536b\u58eb\u7684\u8863\u88f3\uff0c\u5362\u9999\u4ead\u53ea\u9053\u4ed6\u662f\u9633\u5929\u96f7\u6d3e\u6765\u53ec\u89c1\u81ea\u5df1\u7684\u4eba\uff0c\u65b9\u4e3a\u6b22\u559c\uff0c\u8bf4\u65f6 \u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u891a\u4e91\u5cf0\u5df2\u662f\u500f\u5730\u5230\u4e86\u4ed6\u7684\u9762\u524d\uff0c\u4e00\u638c\u5c31\u5411\u4ed6\u6253\u4e0b\uff01 \u3000\u3000\u5362\u9999\u4ead\u7684\u6b66\u529f\u672c\u6765\u4e0d\u5f31\uff0c\u4f46\u4ed6\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u201c\u56fd\u5e08\u5e9c\u201d\u4e2d\u7684\u201c\u6b66\u58eb\u201d\u7adf\u7136\u4f1a\u5bf9\u4ed6\u7a81\u4f7f\u6740 \u624b\u3002\u53ea\u542c\u5f97\u201c\u5494\u5693\u201d\u4e00\u58f0\uff0c\u891a\u4e91\u5cf0\u4e00\u638c\u5288\u4e0b\u6765\uff0c\u5362\u9999\u4ead\u7684\u808b\u9aa8\u65ad\u4e86\u4e24\u6839\u3002\u4ed6\u7528\u7684\u6b63\u662f\u201c\u5929\u96f7 \u529f\u201d\u4e2d\u6700\u5389\u5bb3\u7684\u90a3\u62db\u6740\u624b\u2014\u2014\u201c\u96f7\u7535\u4ea4\u8f70\u201d\u3002 \u3000\u3000\u5362\u9999\u4ead\u597d\u50cf\u6728\u5934\u4f3c\u7684\u5012\u5c06\u4e0b\u53bb\uff0c\u53e3\u4e2d\u5140\u81ea\u5389\u58f0\u53eb\u9053\uff1a\u201c\u662f\u891a\u4e91\u5cf0\uff0c\u4ed6\uff0c\u4ed6\u548c\u2026\u2026\u201d \u3000\u3000\u201c\u96f7\u7535\u4ea4\u8f70\u201d\u4e00\u62db\u4e24\u5f0f\uff0c\u4ed6\u8bdd\u72b9\u672a\u4e86\uff0c\u891a\u4e91\u5cf0\u53f3\u638c\u53c8\u5df2\u5288\u4e86\u4e0b\u6765\uff0c\u767b\u65f6\u7ed3\u675f\u4e86\u4ed6\u7684\u6027 \u547d\uff01\u891a\u4e91\u5cf0\u6697\u6697\u53eb\u4e86\u4e00\u58f0\uff1a\u201c\u597d\u9669\uff01\u201d\u4ed6\u5fc3\u91cc\u660e\u767d\u5362\u9999\u4ead\u60f3\u8bf4\u7684\u4e00\u5b9a\u662f\u201c\u4ed6\u548c\u67f3\u6d1e\u5929\u662f\u4e00 \u515a\u201d\u8fd9\u4e00\u53e5\u8bdd\u3002 \u3000\u3000\u7126\u9738\u559d\u9053\uff1a\u201c\u891a\u4e91\u5cf0\u4f60\u597d\u5927\u80c6\uff01\u201d\u5927\u559d\u58f0\u4e2d\uff0c\u5df2\u662f\u62d4\u5200\u5411\u4ed6\u65ab\u6765\uff01 \u3000\u3000\u891a\u4e91\u5cf0\u53cd\u624b\u4e00\u6325\uff0c\u201c\u94ee\u201d\u7684\u4e00\u58f0\uff0c\u5f39\u5f00\u4e86\u4ed6\u7684\u6734\u5200\uff0c\u559d\u9053\uff1a\u201c\u4f60\u4eec\u4e5f\u662f\u6c49\u4eba\uff0c\u96be\u9053\u5c31\u7518 \u5fc3\u52a9\u7ea3\u4e3a\u8650\u4e48\uff1f\u201d \u3000\u3000\u7126\u9738\u53eb\u9053\uff1a\u201c\u4e94\u54e5\uff0c\u5feb\u52a8\u624b\u5440\uff01\u54fc\uff0c\u891a\u4e91\u5cf0\uff0c\u4eca\u65e5\u4f60\u662f\u81ea\u6295\u7f57\u7f51\uff0c\u8fd8\u6562\u53e3\u51fa\u5996\u8a00\uff0c\u5984\u56fe \u717d\u60d1\uff01\u201d \u3000\u3000\u97e9\u8d85\u559d\u9053\uff1a\u201c\u891a\u4e91\u5cf0\uff0c\u6211\u662f\u770b\u5728\u540c\u50da\u4efd\u4e0a\u4e0d\u4e3a\u5df2\u751a\uff0c\u4f60\u8fd8\u4e0d\u675f\u624b\u5c31\u64d2\uff0c\u5f53\u771f\u8981\u6211\u52a8\u624b \u4e48\uff1f\u201d\u891a\u4e91\u5cf0\u5927\u6012\u9053\uff1a\u201c\u6211\u4ee5\u4e3a\u4f60\u8fd8\u6709\u70b9\u826f\u5fc3\uff0c\u8c01\u77e5\u4f60\u4e5f\u662f\u4e00\u4e18\u4e4b\u8c89\uff01\u201d \u3000\u3000\u201c\u56fd\u5e08\u5e9c\u201d\u4e2d\u7684\u6c49\u4eba\u4f8d\u536b\u90fd\u6709\u8d85\u7fa4\u51fa\u4f17\u7684\u672c\u9886\uff0c\u5426\u5219\u9633\u5929\u96f7\u4e5f\u4e0d\u4f1a\u62db\u63fd\u4ed6\u4eec\u4e86\u3002\u891a\u516d\u5cf0 \u7684\u8fd9\u201c\u96f7\u7535\u4ea4\u8f70\u201d\u56e0\u4e3a\u662f\u5355\u72ec\u4f7f\u7528\uff0c\u5a01\u529b\u5c11\u4e86\u4e00\u534a\uff0c\u53cc\u638c\u4ea4\u53c9\u5288\u51fa\uff0c\u97e9\u8d85\u8eab\u5f62\u4e00\u6643\uff0c\u5374\u672a\u8dcc \u5012\u3002\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u4ed6\u4e00\u4e2a\u201c\u76d8\u9f99\u7ed5\u6b65\u201d\uff0c\u5df2\u662f\u4fa7\u8eab\u6251\u4e0a\uff0c\u4e00\u62db\u201c\u6e38\u9f99\u63c9\u722a\u201d\uff0c\u6293\u5411\u891a\u4e91 \u5cf0\u7684\u7435\u7436\u9aa8\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u77e5\u9053\u97e9\u8d85\u7684\u9e70\u722a\u529f\u975e\u540c\u5c0f\u53ef\uff0c\u5f53\u4e0b\u4e00\u4e2a\u6c89\u80a9\u7f29\u8098\uff0c\u8098\u9524\u649e\u51fa\uff0c\u97e9\u8d85\u4e00\u6293\u6293\u7a7a\uff0c\u95ea \u8fc7\u4e00\u8fb9\u3002\u7126\u9738\u7684\u6734\u5200\u53c8\u780d\u4e86\u5230\u6765\uff0c\u891a\u4e91\u5cf0\u817e\u7684\u98de\u8d77\u4e00\u817f\uff0c\u5c06\u4ed6\u903c\u9000\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u4ee5\u4e00\u654c\u4e8c\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8981\u6740\u8fd9\u4e24\u4e2a\u4eba\u539f\u4e5f\u4e0d\u96be\uff0c\u4f46\u6050\u6015\u4e5f\u5f97\u5728\u4e94\u5341\u62db\u5f00\u5916\uff0c \u201c\u56fd\u5e08\u5e9c\u201d\u4e2d\u9ad8\u624b\u5982\u4e91\uff0c\u82e5\u662f\u6597\u5230\u4e94\u5341\u62db\u5f00\u5916\uff0c\u5f53\u7136\u4f1a\u6709\u63f4\u5175\u6765\u5230\u3002\u891a\u4e91\u5cf0\u4e0d\u6562\u604b\u6218\uff0c\u903c\u9000 \u4e86\u7126\u9738\uff0c\u7acb\u5373\u201c\u7830\u201d\u7684\u4e00\u62f3\u6363\u51fa\uff0c\u628a\u901a\u82b1\u683c\u5b50\u671d\u7a97\u95e8\u6363\u5f97\u7a00\u70c2\uff0c\u4e00\u4e2a\u98de\u8eab\uff0c\u4fbf\u8df3\u51fa\u53bb\u3002 \u3000\u3000\u7126\u9738\u559d\u9053\uff1a\u201c\u54ea\u91cc\u8d70\uff01\u201d\u5982\u5f71\u968f\u5f62\u5730\u7a7f\u7a97\u800c\u51fa\uff0c\u4e00\u5200\u65ab\u6765\u3002\u4e01\u8fdb\u6b64\u65f6\u4ea6\u5df2\u4ece\u5927\u95e8\u8dd1\u51fa\uff0c \u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u6709\u523a\u5ba2\uff0c\u6765\u4eba\u5440\uff0c\u6765\u4eba\u5440\uff01\u201d\u4ed6\u81ea\u77e5\u672c\u9886\u4e0d\u6d4e\uff0c\u5e76\u4e0d\u53c2\u4e0e\u5835\u622a\u891a\u4e91\u5cf0\u7684\u640f\u6597\uff0c \u81ea\u987e\u81ea\u7684\u8dd1\u51fa\u53bb\u53eb\u6551\u5175\u4e86\uff01 \u3000\u3000\u891a\u4e91\u5cf0\u5fc3\u5934\u4e00\u51db\uff1a\u201c\u53ef\u4e0d\u80fd\u8ba9\u8fd9\u53ae\u9003\u8131\uff01\u201d\u8981\u77e5\u4e01\u8fdb\u548c\u5362\u9999\u4ead\u90fd\u662f\u77e5\u9053\u67f3\u6d1e\u5929\u7684\u79d8\u5bc6 \u7684\uff0c\u53ea\u6740\u4e86\u5362\u9999\u4ead\uff0c\u8fd9\u79d8\u5bc6\u8fd8\u662f\u4f1a\u6cc4\u6f0f\u51fa\u53bb\u7684\u3002 \u3000\u3000\u7126\u9738\u53eb\u9053\uff1a\u201c\u97e9\u4e94\u54e5\u5feb\u6765\uff01\u201d\u97e9\u8d85\u9053\uff0c\u201c\u6765\u4e86\uff01\u201d\u58f0\u5230\u4eba\u5230\uff0c\u622a\u4f4f\u891a\u4e91\u5cf0\u7684\u53bb\u8def\u3002 \u3000\u3000\u8fdc\u8fdc\u7684\u6240\u5f97\u4e00\u58f0\u957f\u5578\uff0c\u6709\u4eba\u559d\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\u8fd9\u6837\u5927\u80c6\uff0c\u7f20\u7740\u4ed6\uff0c\u5f85\u6211\u5c06\u4ed6\u62ff\u4e0b\u3002\u201d\u8fd9\u662f \u5c60\u9f99\u7684\u58f0\u97f3\uff0c\u4ed6\u521a\u597d\u56de\u6765\uff0c\u542c\u89c1\u4e86\u4e01\u8fdb\u7684\u53eb\u58f0\uff0c\u7acb\u5373\u52a0\u5feb\u811a\u6b65\uff0c\u542c\u8fd9\u58f0\u97f3\uff0c\u5c60\u9f99\u5927\u7ea6\u662f\u5728\u767e \u6b65\u4e4b\u5916\uff0c\u9694\u7740\u4e24\u5ea7\u5047\u5c71\uff0c\u95fb\u58f0\u5c1a\u672a\u89c1\u4eba\u3002\u4e0d\u8fc7\u4ee5\u4ed6\u7684\u8f7b\u529f\uff0c\u8fd9\u767e\u6b65\u4e4b\u9065\uff0c\u8f6c\u773c\u5c31\u53ef\u6765\u5230\uff01 \u3000\u3000\u891a\u4e91\u5cf0\u4ee5\u4e00\u654c\u4e8c\uff0c\u4e0d\u8fc7\u7565\u5360\u4e0a\u98ce\uff0c\u5018\u82e5\u5c60\u9f99\u6765\u5230\uff0c\u4ed6\u662f\u63d2\u7ffc\u96be\u9003\u7684\u4e86\uff0c\u7126\u9738\u5f97\u610f\u4e4b\u6781\uff0c \u54c8\u54c8\u7b11\u9053\uff1a\u201c\u770b\u4f60\u8fd8\u9003\u5f97\u51fa\u6211\u7684\u638c\u5fc3\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u4e00\u54ac\u7259\u6839\uff0c\u6b63\u8981\u62fc\u547d\uff0c\u4e00\u4ef6\u610f\u60f3\u4e0d\u5230\u7684\u4e8b\u60c5\u7a81\u7136\u53d1\u751f\u3002\u53ea\u89c1\u97e9\u8d85\u4e00\u6293\u5411\u7126\u9738\u7684\u9888 \u9879\u6293\u4e0b\u6765\uff0c\u201c\u5494\u5693\u201d\u4e00\u58f0\uff0c\u5c06\u4ed6\u7684\u8116\u5b50\u626d\u65ad\u3002\u97e9\u8d85\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u76df\u8c0a\u6c38\u56fa\uff0c\u5171\u4eab\u8363\u534e\u3002\u5feb \u8d70\uff01\u201d\u891a\u4e91\u5cf0\u53c8\u60ca\u53c8\u559c\uff0c\u8fd9\u624d\u77e5\u9053\u97e9\u8d85\u662f\u4e2a\u6709\u5fc3\u4eba\uff0c\u53ef\u80fd\u662f\u50cf\u81ea\u5df1\uff0c\u57cb\u4f0f\u5728\u201c\u56fd\u5e08\u5e9c\u201d\u4e2d\uff0c \u6709\u6240\u4f5c\u4e3a\u7684\u3002\u4f46\u4ed6\u6240\u8bf4\u7684\u8fd9\u516b\u4e2a\u5b57\u662f\u4ec0\u4e48\u610f\u601d\uff0c\u891a\u4e91\u5cf0\u4e00\u65f6\u4e4b\u95f4\u5374\u662f\u5f04\u4e0d\u660e\u767d\uff0c\u6b64\u65f6\u4ea6\u5df2\u65e0 \u6687\u5bb9\u4ed6\u601d\u7d22\u4e86\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u8f7b\u8f7b\u5730\u8bf4\u4e86\u4e00\u53e5\uff1a\u201c\u5927\u6069\u4e0d\u8a00\u62a5\uff0c\u4e94\u54e5\u4fdd\u91cd\uff01\u201d\u811a\u5c16\u4e00\u70b9\uff0c\u4e00\u4e2a\u201c\u9e5e\u5b50\u7ffb\u8eab\u201d\uff0c \u7ffb\u8fc7\u56f4\u5899\u3002\u5c31\u5728\u6b64\u65f6\uff0c\u53ea\u542c\u5f97\u5218\u743c\u59d1\u5728\u897f\u9762\u7684\u9662\u5b50\u91cc\u5c16\u58f0\u53eb\u9053\uff1a\u201c\u6551\u547d\uff0c\u6551\u547d\u5440\uff01\u201d \u3000\u3000\u5c60\u9f99\u5df2\u7136\u542c\u5f97\u4e1c\u9762\u9662\u5b50\u7684\u53ae\u6740\u58f0\uff0c\u4f46\u5bf9\u4ed6\u6765\u8bf4\uff0c\u6700\u7d27\u8981\u7684\uff0c\u5f53\u7136\u8fd8\u662f\u8981\u6551\u5218\u743c\u59d1\u7684\u6027 \u547d\uff0c\u5e76\u975e\u4ed6\u6709\u6240\u539a\u7231\u4e8e\u5218\u743c\u59d1\uff0c\u800c\u662f\u56e0\u4e3a\u6709\u4e86\u5218\u743c\u59d1\uff0c\u624d\u80fd\u8fdb\u884c\u7834\u574f\u4e49\u519b\u7684\u9634\u8c0b\u3002 \u3000\u3000\u5c60\u9f99\u5fc3\u60f3\uff1a\u201c\u4e1c\u9762\u6709\u97e9\u8d85\u3001\u7126\u9738\u4e8c\u4eba\uff0c\u6599\u60f3\u53ef\u4ee5\u62b5\u6321\u7247\u65f6\u3002\u201d\u7565\u4e00\u8e0c\u8e87\uff0c\u4fbf\u5411\u897f\u5954\u53bb\uff0c \u4e01\u8fdb\u9003\u8dd1\u7684\u65b9\u5411\uff0c\u6070\u6070\u548c\u4ed6\u76f8\u53cd\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u6b63\u6101\u96be\u4ee5\u5206\u8eab\uff0c\u4e00\u89c1\u5c60\u9f99\u7684\u5f71\u5b50\u5411\u897f\u5954\u53bb\uff0c\u5fc3\u4e2d\u5927\u559c\uff0c\u7acb\u5373\u65bd\u5c55\u516b\u6b65\u8d76\u8749\u7684\u8f7b \u529f\uff0c\u8d8a\u8fc7\u5047\u5c71\uff0c\u8ffd\u4e0a\u4e01\u8fdb\u3002\u8fd9\u665a\u65e0\u6708\u65e0\u661f\uff0c\u5c0f\u96e8\u70b9\u8fd8\u5728\u4e0b\u4e2a\u4e0d\u505c\uff0c\u9ed1\u6f06\u6f06\u7684\u591c\u91cc\uff0c\u4e01\u8fdb\u5c1a\u672a \u77e5\u9053\u662f\u4ed6\u8ffd\u6765\uff0c\u53eb\u9053\uff1a\u201c\u5feb\u5f80\u7a3b\u9999\u6751\u7684\u4e1c\u9662\u3002\u201d\u891a\u4e91\u5cf0\u6c89\u58f0\u559d\u9053\uff1a\u201c\u8d3c\u53ae\u9e1f\uff0c\u4f60\u770b\u770b\u6211\u662f \u8c01\uff1f\u201d\u4e01\u8fdb\u521a\u4e00\u56de\u5934\uff0c\u891a\u4e91\u5cf0\u4f7f\u51fa\u5929\u96f7\u529f\uff0c\u547c\u7684\u4e00\u638c\u4fbf\u7ed3\u675f\u4e86\u4ed6\u7684\u6027\u547d\uff01 \u3000\u3000\u56ed\u5b50\u91cc\u53ea\u89c1\u9ed1\u5f71\u5e62\u5e62\uff0c\u4e1c\u5357\u897f\u5317\uff0c\u90fd\u53d1\u73b0\u6709\u95fb\u58f0\u800c\u81f3\u7684\u536b\u58eb\u4e86\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u6697\u6697\u53eb\u4e86\u4e00\u4e00\u58f0\u201c\u82e6\u4e5f\uff01\u201d\u62b9\u4e86\u4e00\u628a\u70c2\u6ce5\uff0c\u671d\u8138\u4e0a\u4e00\u6d82\uff0c\u5fc3\u9053\uff1a\u201c\u53ea\u597d\u78b0\u78b0\u8fd0\u6c14 \u4e86\uff01\u201d\u86c7\u884c\u514d\u4f0f\uff0c\u8d70\u5f97\u4e0d\u8fdc\uff0c\u8fce\u9762\u6765\u4e86\u4e00\u4e2a\u91d1\u56fd\u6b66\u58eb\uff0c\u53d1\u89c9\u4e86\u4ed6\uff0c\u4e0d\u77e5\u662f\u5426\u89c9\u5f97\u5730\u5f62\u8ff9\u53ef \u7591\uff0c\u4e00\u4e2a\u8f6c\u5934\u671d\u4ed6\u8dd1\u6765\uff0c\u53eb\u9053\uff1a\u201c\u76df\u8c0a\u6c38\u56fa\uff01\u201d \u3000\u3000\u8fd9\u4e2a\u91d1\u56fd\u6b66\u58eb\u8bf4\u7684\u662f\u5973\u771f\u8bdd\uff0c\u891a\u4e91\u5cf0\u867d\u7136\u542c\u5f97\u61c2\uff0c\u53ef\u662f\u6025\u5207\u95f4\u672a\u80fd\u4f1a\u610f\uff0c\u90a3\u91d1\u56fd\u6b66\u58eb\u89c1 \u4ed6\u6ca1\u6709\u7acb\u5373\u7b54\u8bdd\uff0c\u53eb\u9053\uff1a\u201c\u5978\u7ec6\u5728\u3001\u5728\u2014\u2014\u201d\u201c\u8fd9\u91cc\u201d\u4e8c\u5b57\u672a\u66fe\u51fa\u53e3\uff0c\u891a\u4e91\u5cf0\u5df2\u662f\u62d4\u5251\u51fa \u9798\uff0c\u4ee5\u95ea\u7535\u822c\u7684\u624b\u6cd5\u5411\u4ed6\u523a\u53bb\uff0c\u5728\u4ed6\u8eab\u4e0a\u63b7\u4e86\u4e09\u4e2a\u900f\u660e\u7684\u7a9f\u7abf\uff01\u5728\u4ed6\u4e2d\u5251\u4e4b\u540e\uff0c\u65b9\u59cb\u8ba4\u51fa\u662f \u891a\u4e91\u5cf0\uff0c\u5df2\u662f\u8fdf\u4e86\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u604d\u7136\u5927\u609f\uff0c\u539f\u6765\u8fd9\u516b\u4e2a\u5b57\u4e43\u662f\u4eca\u665a\u7684\u6697\u8bed\u3002\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u62d6\u96f7\u4f4f\u5728\u56fd\u5e08\u5e9c\u4e2d\uff0c\u9633 \u5929\u96f7\u60f3\u5fc5\u662f\u4e3a\u4e86\u8981\u8ba8\u597d\u4ed6\uff0c\u5b9a\u4e0b\u4e86\u8fd9\u2018\u76df\u8c0a\u6c38\u56fa\u3001\u5171\u4eab\u8363\u534e\u2019\u7684\u516b\u4e2a\u5b57\u4f5c\u4e3a\u6697\u53f7\u3002\u8fd9\u6837\uff0c\u5728 \u9ed1\u591c\u4e4b\u4e2d\uff0c\u7528\u4e0d\u7740\u67e5\u770b\u91d1\u813e\uff0c\u4e00\u5bf9\u6697\u53f7\uff0c\u5c31\u53ef\u77e5\u9053\u5bf9\u65b9\u662f\u5426\u5978\u7ec6\u3002\u54fc\uff0c\u9633\u5929\u96f7\u8fd9\u53ae\u7684\u9632\u5907\u5012 \u4e5f\u771f\u662f\u5468\u5bc6\u5f97\u65e0\u4ee5\u590d\u52a0\uff0c\u5e78\u4e8f\u97e9\u8d85\u7ed9\u4e86\u6211\u8fd9\u4e2a\u6697\u53f7\uff01\u201d \u3000\u3000\u891a\u4e91\u5cf0\u5927\u7740\u80c6\u5b50\u4fbf\u5f80\u524d\u8dd1\uff0c\u78b0\u89c1\u6709\u4eba\uff0c\u4fbf\u5148\u559d\u9053\uff1a\u201c\u76df\u8c0a\u6c38\u56fa\uff01\u201d\u5bf9\u65b9\u679c\u7136\u90fd\u662f\u56de\u7b54 \u201c\u5171\u4eab\u8363\u534e\uff01\u201d\u9ed1\u591c\u4e2d\u90a3\u4e9b\u4eba\u770b\u4e0d\u6e05\u695a\uff0c\u53ea\u9053\u662f\u81ea\u5df1\u4eba\uff0c\u6697\u53f7\u4e00\u5bf9\uff0c\u4fbf\u5373\u75be\u8dd1\u8fc7\u53bb\u3002\u891a\u4e91\u5cf0 \u8f7b\u8f7b\u6613\u6613\u5730\u5c31\u6e9c\u51fa\u4e86\u201c\u56fd\u5e08\u5e9c\u201d\u3002 \u3000\u3000\u5c60\u9f99\u5306\u5306\u5fd9\u5fd9\u5730\u8dd1\u8fdb\u4ed6\u548c\u5218\u743c\u59d1\u4f4f\u7684\u90a3\u95f4\u623f\u5b50\uff0c\u770b\u89c1\u5218\u743c\u59d1\u8840\u67d3\u7f57\u8863\uff0c\u90a3\u4e2a\u536b\u58eb\u548c\u5c0f\u7389 \u513f\u8eba\u5728\u5730\u4e0a\u3002\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u8fde\u5fd9\u95ee\u9053\uff1a\u201c\u662f\u8c01\u6765\u5566\uff1f\u201d\u5218\u743c\u59d1\u547b\u541f\u9053\uff1a\u201c\u75db\u3001\u75db\u6b7b\u6211 \u4e86\uff0c\u4f60\u5feb\u7ed9\u6211\u6577\u4e0a\u91d1\u521b\u836f\uff01\u201d \u3000\u3000\u5c60\u9f99\u77e5\u9053\u5218\u743c\u59d1\u6ca1\u6709\u6027\u547d\u4e4b\u5fe7\uff0c\u653e\u4e0b\u4e86\u5fc3\u4e0a\u7684\u4e00\u5757\u77f3\u5934\uff0c\u5047\u610f\u4f5c\u51fa\u6e29\u67d4\u4f53\u8d34\u7684\u6837\u5b50\uff0c\u66ff \u5979\u6577\u4e0a\u4e86\u91d1\u521b\u836f\u3002\u5218\u743c\u59d1\u65b9\u59cb\u8bf4\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\u4e00\u51b2\u8fdb\u6765\uff0c\u4fbf\u5927\u9a82\u6211\u4e0d\u77e5\u5ec9\u803b\uff0c\u8981\u62a2\u6211\u8d70\u3002\u4ed6 \u7684\u6b66\u529f\u9ad8\u5f3a\u5f97\u5f88\uff0c\u8001\u8c22\u8dd1\u6765\u6551\u6211\uff0c\u7ed9\u4ed6\u4e00\u638c\u6253\u6655\uff01\u6211\u62fc\u6b7b\u4e0d\u4ece\uff0c\u7ed9\u4ed6\u523a\u4e86\u4e00\u5251\uff01\u5c0f\u7389\u513f\u62d6\u4ed6 \u7684\u811a\uff0c\u4e5f\u7ed9\u4ed6\u5bb3\u4e86\u3002\u201d \u3000\u3000\u5c60\u9f99\u5927\u5403\u4e00\u60ca\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u6765\u7684\u4e00\u5b9a\u662f\u891a\u4e91\u5cf0\u4e86\uff01\u201d\u4ed6\u662f\u4e2a\u6b66\u5b66\u7684\u5927\u884c \u5bb6\uff0c\u4ed4\u7ec6\u4e00\u770b\uff0c\u770b\u51fa\u90a3\u540d\u5192\u5145\u4ec6\u4eba\u7684\u536b\u58eb\u5df2\u7136\u6bd9\u547d\uff0c\u6240\u53d7\u7684\u81f4\u547d\u4e4b\u4f24\u6b63\u662f\u201c\u5929\u96f7\u529f\u201d\u9053\u6210 \u7684\u3002\u5c60\u9f99\u5c71\u5411\u5c0f\u7389\u513f\u4e00\u770b\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u5c0f\u4e2b\u5934\u53ef\u6ca1\u6709\u9047\u5bb3\u561b\uff01\u201d \u3000\u3000\u5218\u743c\u59d1\u9053\uff1a\u201c\u662f\u5417\uff1f\u6211\u5f53\u65f6\u53ea\u987e\u548c\u90a3\u8d3c\u4eba\u62fc\u547d\uff0c\u53ea\u9053\u5979\u5df2\u662f\u53d7\u5bb3\u4e86\u5462\uff01\u90a3\u4f60\u8d76\u5feb\u628a\u5979\u6551 \u56de\u6765\u5440\uff01\u8fd9\u4e2b\u5934\u4e3a\u4e3b\u820d\u8eab\uff0c\u5f53\u771f\u96be\u5f97\uff01\u201d \u3000\u3000\u5c60\u9f99\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u891a\u4e91\u5cf0\u4ee5\u4fa0\u4e49\u9053\u81ea\u5c45\uff0c\u4ed6\u4e0d\u6740\u8fd9\u4e2a\u4e2b\u5934\u5012\u662f\u4e0d\u7528\u6000\u7591\uff0c\u4f46\u4ed6\u4f55\u4ee5\u4e0d\u628a \u5218\u63a0\u59d1\u62a2\u8d70\u5462\uff1f\u62a2\u4e0d\u8d70\u4e5f\u8be5\u6740\u5979\u5440\uff01\u5bf9\uff0c\u60f3\u5fc5\u662f\u56e0\u4e3a\u5df2\u7ecf\u542c\u5230\u6211\u7684\u58f0\u97f3\uff0c\u6765\u4e0d\u53ca\u4e86\u3002\u4ed6\u4e0d\u6740 \u743c\u59d1\uff0c\u90a3\u662f\u56e0\u4e3a\u770b\u5728\u5979\u54e5\u54e5\u7684\u4efd\u4e0a\u3002\u201d\u5c60\u9f99\u81ea\u4ee5\u4e3a\u89e3\u91ca\u5f97\u5408\u60c5\u5408\u7406\uff0c\u975e\u4f46\u5bf9\u5218\u8c05\u59d1\u6ca1\u6709\u6000 \u7591\uff0c\u5fc3\u4e2d\u8fd8\u5728\u6697\u6697\u6b22\u559c\uff0c\u6b22\u559c\u5218\u743c\u59d1\u6b7b\u5fc3\u584c\u5730\u7684\u613f\u610f\u8ddf\u4ed6\u3002 \u3000\u3000\u5f53\u4e0b\u5c60\u9f99\u4fbf\u7ed9\u5c0f\u7389\u513f\u89e3\u5f00\u7a74\u9053\uff0c\u95ee\u5979\uff1a\u201c\u90a3\u4e2a\u4eba\u662f\u8c01\u3002\u201d\u5c0f\u7389\u513f\u4e5f\u771f\u591f\u673a\u7075\uff0c\u541e\u541e\u5410\u5410 \u5730\u8bf4\u9053\uff1a\u201c\u6211\uff0c\u6211\u4e0d\u6562\u8bf4\uff01\u201d\u670d\u89d2\u5374\u5411\u5218\u743c\u59d1\u77a7\u53bb\u3002\u5218\u743c\u59d1\u4f4e\u5934\u88f9\u4f24\uff0c\u88c5\u4f5c\u6ca1\u6709\u7559\u610f\u5979\u7684\u6837 \u5b50\u3002 \u3000\u3000\u5c65\u9f99\u970d\u7136\u4e00\u9192\uff0c\u5fc3\u9053\uff1a\u201c\u5bf9\uff0c\u6211\u600e\u4e48\u53ef\u4ee5\u5728\u8fd9\u91cc\u95ee\u5979\uff1f\u201d\u8bf4\u9053\uff1a\u201c\u5c0f\u7389\u513f\uff0c\u4f60\u53d7\u60ca\u4e86\uff0c \u6211\u9001\u4f60\u56de\u53bb\u5427\u3002\u8ba9\u4f60\u7239\u7239\u7167\u6599\u4f60\uff0c\u660e\u5929\u4f60\u4e0d\u7528\u6765\u8fd9\u91cc\u4e86\u3002\u201d \u3000\u3000\u5c60\u9f99\u628a\u5c0f\u7389\u513f\u62d6\u5230\u5c4b\u5916\u9762\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u90a3\u4eba\u662f\u8c01\u3002\u201d\u5c0f\u7389\u513f\u9053\uff1a\u201c\u4f3c\u4e4e\u662f\u4ece\u524d\u5728\u5e9c\u91cc \u5f53\u8fc7\u5dee\u7684\u90a3\u4e2a\u891a\u4e91\u5cf0\u3002\u4f60\u5728\u5218\u59d1\u5a18\u9762\u524d\u95ee\u6211\uff0c\u6240\u4ee5\u6211\u521a\u624d\u4e0d\u6562\u8bf4\u3002\u201d\u5c60\u9f99\u9053\uff1a\u201c\u6211\u660e\u767d\u4e86\uff0c \u4f60\u56de\u53bb\u5427\u3002\u201d\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u679c\u7136\u662f\u891a\u4e91\u5cf0\u3002\u201d \u3000\u3000\u5c60\u9f99\u6b63\u8981\u51fa\u53bb\u63a2\u542c\u6d88\u606f\uff0c\u97e9\u8d85\u6c14\u6025\u8d25\u574f\u5730\u8dd1\u6765\uff0c\u538b\u4f4e\u4e86\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u4e0d\u597d\uff01\u7126\u9738\u4e5f\u7ed9\u90a3 \u4eba\u6253\u6b7b\u4e86\u3002\u201d\u5c0f\u7389\u513f\u5c1a\u672a\u8d70\u51fa\u5927\u95e8\uff0c\u8fde\u5fd9\u8bf4\u9053\uff1a\u201c\u891a\u4e91\u5cf0\u8fd9\u6837\u5feb\u5c31\u5230\u4f60\u90a3\u8fb9\u4e86\u5417\uff1f\u201d \u3000\u3000\u97e9\u8d85\u662f\u4e2a\u5341\u5206\u7cbe\u660e\u7684\u4eba\uff0c\u4e00\u542c\u4fbf\u77e5\u5c0f\u7389\u513f\u662f\u6709\u5fc3\u70b9\u9192\u4ed6\uff0c\u4fbf\u9053\uff1a\u201c\u4e0d\u662f\u891a\u4e91\u5cf0\u3002\u4e0d\u8fc7\uff0c \u5947\u602a\u5f97\u5f88\uff0c\u8fd9\u4e2a\u4eba\u7684\u6b66\u529f\u8def\u5b50\u548c\u891a\u4e91\u5cf0\u5374\u662f\u5b8c\u5168\u4e00\u6837\uff0c\u4e5f\u4f1a\u4f7f\u5929\u96f7\u529f\u7684\uff01\u201d \u3000\u3000\u5c60\u9f99\u9053\uff1a\u201c\u54e6\uff0c\u6211\u77e5\u9053\u4e86\u3002\u891a\u4e91\u5cf0\u662f\u548c\u4ed6\u7684\u5e08\u5f1f\u8c37\u6db5\u865a\u4e00\u540c\u6765\u7684\uff0c\u4f60\u4eec\u78b0\u4e0a\u7684\u662f\u8c37\u6db5 \u865a\u3002\u201d \u3000\u3000\u97e9\u8d85\u9053\uff1a\u201c\u5916\u9762\u6b63\u5728\u641c\u67e5\u5978\u7ec6\uff0c\u54b1\u4eec\u53bb\u4e0d\u53bb\u5e2e\u5fd9\uff1f\u201d\u5c60\u9f99\u5c0f\u58f0\u8bf4\u9053\uff1a\u201c\u8c05\u8fd9\u4e24\u4e2a\u4eba\u6b64\u65f6 \u5fc5\u662f\u5fd9\u7740\u9003\u547d\uff0c\u51b3\u4e0d\u6562\u518d\u6765\u8fd9\u91cc\u3002\u4f60\u51fa\u53bb\u77e5\u4f1a\u4ed6\u4eec\uff0c\u4e0d\u8981\u8ba9\u5973\u771f\u6b66\u58eb\u8fdb\u8fd9\u9662\u5b50\u3002\u201d \u3000\u3000\u97e9\u8d85\u9053\uff1a\u201c\u6211\u7406\u4f1a\u5f97\uff0c\u82e5\u662f\u7ed9\u5973\u771f\u6b66\u58eb\u8fdb\u6765\uff0c\u54b1\u4eec\u7684\u8c0e\u8bdd\u5f53\u573a\u5c31\u8981\u7ed9\u5218\u5c0f\u59d0\u8bc6\u7834\u4e86\u3002\u201d \u3000\u3000\u97e9\u8d85\u4e0e\u5c0f\u7389\u513f\u76f8\u7ee7\u8d70\u540e\uff0c\u5c60\u9f99\u6697\u81ea\u60f3\u9053\uff1a\u201c\u891a\u4e91\u5cf0\u5e08\u5144\u5f1f\u8fdb\u6765\u95f9\u4e86\u8fd9\u573a\uff0c\u4e0d\u77e5\u4ed6\u4eec\u8fd8\u8bf4 \u4e9b\u4ec0\u4e48\u8bdd\uff1f\u83ab\u8981\u7ed9\u743c\u59d1\u77e5\u9053\u8fd9\u91cc\u662f\u201c\u56fd\u5e08\u5e9c\u201d\u624d\u597d\u3002\u201d \u3000\u3000\u5c60\u9f99\u6000\u7740\u5fd0\u5fd1\u4e0d\u5b89\u7684\u5fc3\u60c5\uff0c\u56de\u5230\u5218\u743c\u59d1\u7684\u623f\u4e2d\uff0c\u53ea\u89c1\u5218\u743c\u59d1\u6b63\u5728\u5305\u624e\u4f24\u53e3\uff0c\u7709\u5934\u6df1\u7410\uff0c \u4f3c\u662f\u5728\u5fcd\u4f4f\u75bc\u75db\u7684\u6a21\u6837\u3002 \u3000\u3000\u5c60\u9f99\u4e0d\u89c9\u4e5f\u662f\u7709\u5934\u4e00\u76b1\uff0c\u8bf4\u9053\uff1a\u201c\u8840\u5df2\u6b62\u4e86\uff0c\u8fd8\u662f\u75db\u5417\uff1f\u201d \u3000\u3000\u5218\u743c\u59d1\u9053\uff1a\u201c\u66f2\u6c60\u7a74\u4e0a\u9762\u7684\u4e00\u6761\u7b4b\u7ed9\u90a3\u53ae\u7684\u5251\u5c16\u6311\u65ad\u4e86\uff0c\u52a8\u4e00\u52a8\u5c31\u75db\u5f97\u8981\u547d\u3002\u201d \u3000\u3000\u5c60\u9f99\u9053\uff1a\u201c\u90a3\u4e48\u90a3\u4e00\u5c01\u4fe1\u2014\u2014\u201d \u3000\u3000\u5218\u8c05\u59d1\u82e6\u7b11\u9053\uff1a\u201c\u4f60\u5c31\u53ea\u77e5\u9053\u8981\u6211\u7ed9\u4f60\u505a\u4e8b\uff0c\u4f60\u770b\u6211\u7684\u53f3\u81c2\u4f24\u5f97\u8fd9\u6837\uff0c\u8fd8\u80fd\u63d0\u7b14\u5199\u5b57 \u5417\uff1f\u201d \u3000\u3000\u5c60\u9f99\u5927\u4e3a\u7740\u6025\uff0c\u53ef\u8fd8\u4e0d\u6562\u5f62\u4e4b\u8f9e\u8272\uff0c\u53ea\u597d\u5047\u4f5c\u6e29\u67d4\uff0c\u8d54\u7b11\u8bf4\u9053\uff1a\u201c\u743c\u59d1\uff0c\u4f60\u4e3a\u6211\u51e0\u4e4e\u4e22 \u4e86\u6027\u547d\uff0c\u6211\u5c82\u6709\u4e0d\u4f36\u60dc\u4f60\u7684\uff1f\u4f46\u662f\u8fd9\u5c01\u4fe1\u5173\u7cfb\u54b1\u4eec\u4e24\u4eba\u7684\u5a5a\u4e8b\uff0c\u6211\u81ea\u662f\u5e0c\u671b\u80fd\u591f\u5feb\u70b9\u9001\u5230\u4f60 \u54e5\u54e5\u7684\u624b\u4e2d\u3002\u201d \u3000\u3000\u5218\u743c\u59d1\u4e5f\u88c5\u4f5c\u611f\u6fc0\u7684\u6837\u5b50\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u660e\u767d\u3002\u9f99\u54e5\uff0c\u6211\u4f55\u5c1d\u4e0d\u60f3\u5feb\u70b9\u5462\uff1f\u4f46\u4e0d\u77e5\u8fd9\u4f24\u4ec0 \u4e48\u65f6\u5019\u624d\u597d\uff0c\u4f60\u627e\u4e2a\u61c2\u5f97\u7eed\u7b4b\u9a73\u9aa8\u7684\u5927\u592b\u7ed9\u6211\u770b\u5427\u3002\u201d \u3000\u3000\u5c60\u9f99\u5927\u611f\u4e3a\u96be\uff0c\u7eed\u7b4b\u9a73\u9aa8\u7684\u5927\u592b\u4e0d\u662f\u6ca1\u6709\uff0c\u56fd\u5e08\u5e9c\u91cc\u7684\u4eba\uff0c\u968f\u65f6\u53ef\u4ee5\u8bf7\u5f97\u5fa1\u533b\u6765\u6cbb\uff0c\u4f46 \u5fa1\u533b\u4e43\u662f\u5973\u771f\u65cf\u4eba\uff0c\u53ef\u662f\u4e0d\u80fd\u8ba9\u5218\u743c\u59d1\u770b\u89c1\u7684\u3002\u82e5\u662f\u8bf7\u5916\u9762\u7684\u5927\u592b\uff0c\u5c60\u9f99\u53c8\u6015\u6cc4\u6f0f\u4e86\u79d8\u5bc6\u3002 \u3000\u3000\u5c60\u9f99\u76b1\u4e00\u76b1\u7709\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4f1a\u7ed9\u4f60\u627e\u5927\u592b\u7684\uff0c\u4f46\u6025\u5207\u4e4b\u95f4\uff0c\u6050\u6015\u4e0d\u6613\u627e\u5230\u533b\u9053\u9ad8\u660e\u7684\u5927 \u592b\u3002\u4f60\u7684\u5de6\u624b\u80fd\u591f\u63d0\u81c2\u5199\u5b57\u5417\uff1f\u201d \u3000\u3000\u5218\u743c\u59d1\u6837\u4f5c\u7f9e\u6127\u7684\u6a21\u6837\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u77e5\u9053\u6211\u662f\u4e2a\u4e61\u4e0b\u5973\u5b50\uff0c\u6ca1\u6709\u8bfb\u8fc7\u51e0\u5e74\u4e66\uff0c\u53f3\u624b\u5199 \u5b57\uff0c\u5df2\u662f\u56f0\u96be\uff0c\u5de6\u624b\u5199\u5b57\uff0c\u6211\u4ece\u6765\u6ca1\u6709\u8bd5\u8fc7\u3002\u53ea\u6015\u7eb5\u7136\u80fd\u591f\u5199\u51fa\u5b57\u6765\u4e5f\u662f\u6b6a\u6b6a\u659c\u659c\uff0c\u5927\u54e5\u600e \u6562\u76f8\u4fe1\u662f\u6211\u7684\u7b14\u8ff9\u3002\u5f53\u7136\uff0c\u4f60\u6d3e\u4eba\u53bb\u9001\u4fe1\uff0c\u53ef\u4ee5\u8bf4\u660e\u6211\u662f\u53f3\u81c2\u53d7\u4e86\u4f24\uff0c\u4f46\u4f9d\u65e7\u53c8\u8981\u5f15\u8d77\u4ed6\u7684 \u7591\u5fc3\uff0c\u95ee\u957f\u95ee\u77ed\u7684\u4e86\u3002\u54b1\u4eec\u906d\u9047\u7684\u8fd9\u4e9b\u4e8b\u60c5\uff0c\u5916\u4eba\u53ef\u662f\u4e0d\u5bb9\u6613\u66ff\u54b1\u4eec\u8bf4\u4e2a\u660e\u767d\u7684\u554a\uff01\u201d \u3000\u3000\u5c60\u9f99\u4e00\u60f3\u4e5f\u662f\u9053\u7406\uff0c\u6413\u7740\u624b\u9053\uff1a\u201c\u600e\u4e48\u529e\u5462\uff1f\u5509\uff0c\u6211\u53ea\u597d\u649e\u649e\u8fd0\u6c14\uff0c\u8bf7\u670b\u53cb\u5e2e\u5fd9\uff0c\u8d76\u5feb \u7ed9\u4f60\u627e\u4e00\u4e2a\u9ad8\u660e\u7684\u8dcc\u6253\u5927\u592b\u4e86\u3002\u201d \u3000\u3000\u5218\u743c\u59d1\u4e0d\u77e5\u8bf7\u5927\u592b\u4e4b\u4e8b\uff0c\u5c60\u9f99\u5176\u5b9e\u4e5f\u662f\u891a\u591a\u987e\u5fcc\uff0c\u5e76\u65e0\u628a\u63e1\uff0c\u5bb3\u6015\u7ed9\u4ed6\u4eec\u7684\u5927\u592b\u4e00\u770b\uff0c \u8bf4\u4e0d\u5b9a\u5c31\u4f1a\u770b\u51fa\u4e86\u7834\u7efd\u3002\u5218\u743c\u59d1\u4eba\u6025\u667a\u751f\uff0c\u5ffd\u5730\u8bf4\u9053\uff1a\u201c\u6709\u5382\uff01\u201d \u3000\u3000\u5c60\u9f99\u559c\u9053\uff1a\u201c\u4f60\u60f3\u5230\u4ec0\u4e48\u4e3b\u610f\u4e86\uff1f\u201d\u6b63\u662f\uff1a \u3000\u3000\u5047\u4f5c\u9e33\u9e2f\u539f\u5f02\u68a6\uff0c\u8981\u5206\u5f84\u6e2d\u6597\u673a\u5fc3\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\u4e86\uff0c\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b\u2014\u2014\u7b2c\u4e94\u5341\u56db\u56de\u3000\u5815\u6eb7\u6cbe\u6ce5\u601c\u7389\u5973\u3000\u717d\u98ce\u70b9\u706b\u6068\u5978\u4eba \u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b \u7b2c\u4e94\u5341\u56db\u56de\u3000\u5815\u6eb7\u6cbe\u6ce5\u601c\u7389\u5973\u3000\u717d\u98ce\u70b9\u706b\u6068\u5978\u4eba \u3000\u3000\u5218\u743c\u59d1\u9053\uff1a\u201c\u4f60\u7ed9\u6211\u5199\u8fd9\u5c01\u4fe1\u3002\u201d \u3000\u3000\u5c60\u9f99\u9053\uff1a\u201c\u6211\u600e\u80fd\u5192\u5145\u4f60\u7684\u7b14\u8ff9\uff1f\u5373\u4f7f\u8bf4\u660e\u662f\u7531\u6211\u4ee3\u7b14\uff0c\u53ea\u6084\u4f60\u7684\u54e5\u54e5\u4e5f\u4f1a\u6709\u6240\u6000\u7591\uff0c \u4e0d\u6562\u8f7b\u6613\u76f8\u4fe1\u5427\uff1f\u201d \u3000\u3000\u5218\u743c\u59d1\u83ab\u9053\uff1a\u201c\u9f99\u54e5\uff0c\u4f60\u600e\u7684\u806a\u660e\u4e00\u4e16\uff0c\u5374\u7cca\u6d82\u4e00\u65f6\u4e86\uff1f\u201d \u3000\u3000\u5c60\u9f99\u9053\uff1a\u201c\u6211\u600e\u5730\u7cca\u6d82\u4e86\uff1f\u201d \u3000\u3000\u5218\u743c\u59d1\u9053\uff1a\u201c\u7b14\u8ff9\u53ef\u4ee5\u5047\u5192\uff0c\u53e3\u6c14\u662f\u4e0d\u80fd\u5047\u5192\u7684\u3002\u6211\u8bf7\u4f60\u7ed9\u6211\u5199\u4e0a\u4e00\u4e9b\u8bdd\uff0c\u53ea\u6709\u6211\u54e5\u54e5 \u77e5\u9053\u7684\uff0c\u4ed6\u8fd8\u80fd\u4e0d\u4fe1\u5417\uff1f\u201d \u3000\u3000\u5c60\u9f99\u8d5e\u9053\uff1a\u201c\u597d\uff0c\u597d\u4e3b\u610f\uff01\u201d\u5176\u5b9e\u4ed6\u65e9\u5df2\u60f3\u5230\u8fd9\u4e2a\u529e\u6cd5\uff0c\u4e0d\u8fc7\u4ed6\u8981\u8ba9\u5218\u743c\u59d1\u81ea\u5df1\u8bf4\u51fa \u6765\uff0c\u624d\u663e\u5f97\u81ea\u5df1\u4e0d\u662f\u52c9\u5f3a\u5979\u7684\u3002 \u3000\u3000\u5218\u743c\u59d1\u4f4e\u7709\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u96be\u5f97\u4f60\u79f0\u8d5e\u6211\uff0c\u597d\uff0c\u90a3\u4f60\u5c31\u52a8\u7b14\u5199\u5427\u3002\u201d \u3000\u3000\u5c60\u9f99\u78e8\u597d\u4e86\u58a8\uff0c\u94fa\u597d\u4e86\u7eb8\uff0c\u63d0\u8d77\u7b14\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u5ff5\uff0c\u6211\u5199\uff01\u201d \u3000\u3000\u5218\u743c\u59d1\u88c5\u4f5c\u601d\u7d22\u6587\u53e5\u7684\u6a21\u6837\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u522b\u6765\u4e09\u8f7d\uff0c\u65f6\u5207\u9a70\u601d\u2026\u2026\u201d \u3000\u3000\u5c60\u9f99\u9053\uff1a\u201c\u54e6\uff0c\u4f60\u4e09\u5e74\u6765\uff0c\u90fd\u6ca1\u6709\u89c1\u8fc7\u54e5\u54e5\u5417\uff1f\u201d \u3000\u3000\u5218\u743c\u59d1\u9053\uff1a\u201c\u662f\u5440\uff0c\u6211\u8bb0\u5f97\u4e0a\u6b21\u54e5\u54e5\u56de\u6765\uff0c\u6211\u521a\u597d\u8fc7\u5341\u516d\u751f\u65e5\uff0c\u518d\u8fc7\u51e0\u5929\uff0c\u6211\u5c31\u662f\u5341\u4e5d \u5c81\u4e86\uff0c\u4e0d\u662f\u6070\u597d\u4e09\u5e74\u5417\uff1f\u201d \u3000\u3000\u5176\u5b9e\u5218\u743c\u59d1\u548c\u54e5\u54e5\u5206\u624b\uff0c\u8fd8\u672a\u5230\u4e00\u5e74\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u8fd9\u5c01\u4fe1\u5230\u4e86\u54e5\u54e5\u624b\u4e2d\uff0c\u4ed6\u53ea\u8981\u662f\u770b \u5f00\u5934\u7684\u8fd9\u4e24\u53e5\u8bdd\uff0c\u5c31\u77e5\u9053\u662f\u5047\u7684\u4e86\u3002\u201d \u3000\u3000\u5c60\u9f99\u6ee1\u4ee5\u4e3a\u5218\u743c\u59d1\u662f\u4e2a\u6751\u59d1\uff0c\u4e00\u70b9\u4e5f\u4e0d\u7591\u5fc3\u5979\u4f1a\u5f04\u5047\uff0c\u542c\u4e86\u5979\u8fd9\u6837\u8bf4\uff0c\u5927\u4e3a\u5f97\u610f\u5730\u8bf4 \u9053\uff1a\u201c\u597d\uff0c\u597d\uff0c\u4f60\u7684\u5fc3\u601d\u771f\u662f\u7075\u654f\uff0c\u4f60\u4eec\u5144\u59b9\u522b\u6765\u591a\u4e45\uff0c\u5916\u4eba\u81ea\u662f\u4e0d\u4f1a\u77e5\u9053\u3002\u201d\u5fc3\u91cc\u5219\u5728\u6697 \u7b11\u5218\u743c\u59d1\u7684\u611a\u8822\uff0c\u7ed9\u81ea\u5df1\u73a9\u5f04\u4e8e\u80a1\u638c\u4e4b\u4e0a\u800c\u4e0d\u81ea\u77e5\u3002 \u3000\u3000\u6b8a\u4e0d\u77e5\u5218\u743c\u59d1\u4e5f\u6b63\u5728\u5fc3\u91cc\u6697\u6697\u597d\u7b11\uff0c\u60f3\u9053\uff1a\u201c\u9976\u4f60\u9b3c\u7cbe\u7075\uff0c\u4e5f\u5f97\u5403\u6211\u7684\u6d17\u811a\u6c34\u3002\u201d\u88c5\u4f5c \u7ed9\u4ed6\u6253\u65ad\u601d\u8def\u7684\u6837\u5b50\uff0c\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u4f60\u7684\u79f0\u8d5e\uff0c\u53ef\u662f\u7ed9\u4f60\u8fd9\u4e48\u4e00\u8d5e\uff0c\u6211\u60f3\u597d\u7684\u53e5\u5b50\u5374\u90fd\u5fd8 \u4e86\u3002\u201d \u3000\u3000\u5c60\u9f99\u7b11\u9053\uff1a\u201c\u4f60\u4e5f\u4e0d\u5fc5\u6316\u7a7a\u5fc3\u601d\u60f3\u8fd9\u4e9b\u6587\u76b1\u76b1\u7684\u5b57\u53e5\u4e86\uff0c\u4f60\u53ea\u987b\u628a\u4f60\u8981\u8bf4\u7684\u8bdd\u544a\u8bc9\u6211\u5c31 \u884c\uff0c\u6211\u4f1a\u66ff\u4f60\u5199\u7684\u3002\u201d \u3000\u3000\u5218\u743c\u59d1\u9053\uff1a\u201c\u4f60\u8bf4\u6211\u5f88\u6302\u5ff5\u4ed6\uff0c\u65f6\u523b\u8bb0\u7740\u4ed6\u5728\u5206\u624b\u4e4b\u65f6\u7ed9\u6211\u7684\u5429\u5490\uff0c\u53ef\u60dc\u6ca1\u6709\u80fd\u591f\u505a \u5230\uff0c\u975e\u5e38\u60ed\u6127\u3002\u201d \u3000\u3000\u5c60\u9f99\u9053\uff1a\u201c\u4f60\u54e5\u54e5\u5429\u5490\u4f60\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5218\u743c\u59d1\u9053\uff1a\u201c\u4ed6\u5429\u5490\u6211\u59b9\u4ee3\u5144\u8d23\uff0c\u4f8d\u5949\u7239\u7239\u3002\u201d \u3000\u3000\u5218\u743c\u59d1\u53ea\u6709\u8fd9\u4e2a\u54e5\u54e5\uff0c\u5979\u7684\u54e5\u54e5\u5728\u519b\u4e2d\uff0c\u4e0d\u80fd\u4f8d\u5949\u7236\u4eb2\uff0c\u90a3\u4e48\u8fd9\u6837\u7684\u5429\u5490\u662f\u5408\u60c5\u5408\u7406\u3002 \u5c60\u9f99\u7b11\u9053\uff1a\u201c\u4f60\u60ed\u6127\u4ec0\u4e48\uff1f\u201d\u5218\u743c\u59d1\u9053\uff1a\u201c\u54e5\u54e5\u5429\u5490\u6211\u4f8d\u5949\u7239\u7239\uff0c\u6211\u5374\u4e0e\u4f60\u79c1\u5954\uff0c\u4e22\u4e0b\u4ed6\u8001 \u4eba\u5bb6\u4e0d\u7ba1\uff0c\u4e0d\u60ed\u6127\u4e48\uff1f\u201d \u3000\u3000\u5c60\u9f99\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8fd9\u4e0d\u7528\u60ed\u6127\uff0c\u54b1\u4eec\u6210\u4e86\u4eb2\uff0c\u4e00\u540c\u56de\u53bb\uff0c\u7684\u7239\u7239\u8fd8\u591a\u4e86\u4e00\u4e2a\u513f\u5b50\u5462\u3002\u201d \u5218\u743c\u59d1\u542c\u4e86\u4ed6\u8fd9\u8089\u9ebb\u7684\u8a00\u8bed\uff0c\u60f3\u8981\u4f5c\u5455\uff0c\u5374\u4f6f\u7740\u5550\u4e86\u4e00\u53e3\uff0c\u8bf4\u9053\uff1a\u201c\u8d8a\u8bf4\u8d8a\u4e0d\u6b63\u7ecf\u4e86\uff01\u201d\u5c60 \u9f99\u7b11\u9053\uff1a\u201c\u4ec0\u4e48\u4e0d\u6b63\u7ecf\uff0c\u6211\u662f\u4ed6\u8001\u4eba\u5bb6\u7684\u5973\u5a7f\uff0c\u4e0d\u662f\u8be5\u5c3d\u534a\u5b50\u4e4b\u8d23\u4e48\uff1f\u597d\uff0c\u4f60\u4e0d\u7231\u542c\uff0c\u54b1\u4eec \u5c31\u95f2\u8bdd\u5c11\u8bf4\uff0c\u5148\u628a\u8fd9\u5c01\u4fe1\u5199\u597d\u3002\u201d \u3000\u3000\u5218\u743c\u59d1\u5fc3\u4e71\u5982\u9ebb\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u521a\u624d\u8bf4\u5230\u54ea\u91cc\uff1f\u201d \u3000\u3000\u5c60\u9f99\u542e\u542e\u7b14\u5c16\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8bf4\u60ed\u6127\u6ca1\u6709\u505a\u5230\u54e5\u54e5\u7684\u5429\u5490\u3002\u201d\u5218\u743c\u59d1\u773c\u5708\u4e00\u7ea2\uff0c\u8bf4\u9053\uff1a \u201c\u5bf9\uff0c\u6211\u59d4\u5b9e\u662f\u60ed\u6127\u5f97\u5f88\u3002\u4f60\u63a5\u7740\u5199\uff0c\u6211\u771f\u60f3\u80fd\u591f\u518d\u542c\u5230\u4ed6\u7684\u6559\u8bad\uff0c\u4f46\u53ea\u6015\u662f\u6b64\u751f\u65e0\u671b \u4e86\u3002\u201d \u3000\u3000\u539f\u6765\u5218\u743c\u59d1\u534a\u5e74\u524d\u4e0e\u54e5\u54e5\u5206\u624b\u4e4b\u65f6\uff0c\u5979\u7684\u54e5\u54e5\u7684\u786e\u662f\u6709\u4e00\u756a\u201c\u4e34\u522b\u8d60\u8a00\u201d\uff0c\u5374\u5e76\u4e0d\u662f\u5979 \u5bf9\u5c60\u9f99\u6240\u8bf4\u7684\u90a3\u4e9b\u8bdd\u3002 \u3000\u3000\u4e34\u522b\u4e4b\u65f6\uff0c\u5979\u5bf9\u54e5\u54e5\u8bf4\u51fa\u81ea\u5df1\u7684\u60f3\u671b\uff0c\u8bf4\u662f\u5f88\u60f3\u5230\u6c5f\u6e56\u4e0a\u957f\u4e9b\u89c1\u8bc6\uff0c\u57cb\u6028\u54e5\u54e5\u4e0d\u80af\u5e26\u5979 \u8d70\u3002 \u3000\u3000\u5176\u65f6\u5979\u7684\u54e5\u54e5\u5218\u5927\u4e3a\u5df2\u662f\u5728\u4e3a\u59b9\u59b9\u7684\u7ec8\u8eab\u5927\u4e8b\u7740\u60f3\u4e86\u3002\u4ed6\u5fc3\u76ee\u4e2d\u7684\u59b9\u5a7f\u5c31\u662f\u891a\u4e91\u5cf0\uff0c\u8fd9 \u4ef6\u4e8b\u4ed6\u4e5f\u66fe\u548c\u7236\u4eb2\u8bf4\u8fc7\uff0c\u53ea\u56e0\u5c1a\u5728\u8fdb\u884c\u4e4b\u4e2d\uff0c\u662f\u4ee5\u8fd8\u7792\u7740\u59b9\u59b9\u3002 \u3000\u3000\u5218\u5927\u4e3a\u542c\u4e86\u59b9\u59b9\u7684\u8bdd\uff0c\u5c31\u8bf4\uff1a\u201c\u4f60\u662f\u5e94\u8be5\u51fa\u5916\u8d70\u8d70\u7684\uff0c\u4e0d\u8fc7\uff0c\u6700\u597d\u662f\u5728\u4f60\u6210\u5a5a\u4e4b\u540e\u3002\u201d \u3000\u3000\u5218\u743c\u59d1\u7ea2\u4e86\u8138\u8bf4\u9053\uff1a\u201c\u54e5\u54e5\uff0c\u6211\u4e5f\u662f\u4e2a\u61c2\u5f97\u70b9\u6b66\u529f\u7684\u5973\u5b50\uff0c\u96be\u9053\u5c31\u4e0d\u80fd\u5355\u8eab\u5728\u6c5f\u6e56\u4e0a\u884c \u8d70\u5417\uff1f\u4e3a\u4f55\u8981\u626f\u5230\u6211\u7684\u5a5a\u4e8b\u6765\uff1f\u201d \u3000\u3000\u5218\u5927\u4e3a\u6b63\u8272\u8bf4\u9053\uff1a\u201c\u4f60\u4e00\u70b9\u6ca1\u6709\u6c5f\u6e56\u7ecf\u9a8c\uff0c\u6210\u4e86\u5a5a\uff0c\u6709\u4e2a\u53ef\u9760\u7684\u4eba\u548c\u4f60\u4f5c\u4f34\uff0c\u6211\u624d\u653e\u5fc3 \u4f60\u4eec\u592b\u59bb\u540c\u95ef\u6c5f\u6e56\u3002\u59b9\u59b9\uff0c\u4e0d\u7792\u4f60\u8bf4\uff0c\u6211\u8fd9\u6b21\u56de\u6765\uff0c\u5c31\u662f\u548c\u7239\u7239\u5546\u91cf\u4f60\u7684\u5a5a\u4e8b\uff0c\u8fd9\u4e2a\u4eba\u662f\u6211 \u7684\u597d\u670b\u53cb\uff0c\u5341\u5206\u53ef\u9760\uff0c\u800c\u4e14\u662f\u4e00\u4f4d\u6c5f\u6e56\u4e0a\u7684\u5927\u884c\u5bb6\uff0c\u4f60\u548c\u4ed6\u4e00\u8d77\uff0c\u6211\u5c31\u653e\u5fc3\u4e86\u3002\u201d \u3000\u3000\u5218\u743c\u59d1\u9053\uff1a\u201c\u6211\u4e0d\u8981\uff0c\u6211\u4e0d\u8981\uff01\u54e5\u54e5\uff0c\u4f60\u6b3a\u8d1f\u6211\uff01\u201d \u3000\u3000\u5218\u5927\u4e3a\u7b11\u9053\uff1a\u201c\u600e\u4e48\u662f\u6b3a\u8d1f\u4f60\u5462\uff1f\u201d \u3000\u3000\u5218\u743c\u59d1\u9053\uff1a\u201c\u4f60\u968f\u4fbf\u7ed9\u6211\u627e\u4e00\u4e2a\u964c\u751f\u7684\u7537\u5b50\uff0c\u5c31\u628a\u6211\u5ac1\u51fa\u53bb\uff0c\u8fd9\u4e0d\u662f\u6b3a\u8d1f\u6211\u4e48\uff1f\u201d\u539f\u6765 \u5979\u5bf9\u81ea\u5df1\u7684\u5a5a\u59fb\u4e4b\u4e8b\u5b58\u6709\u8bb8\u591a\u5e7b\u60f3\uff0c\u5176\u65f6\u4e5f\u6b63\u662f\u5979\u5f00\u59cb\u8ba4\u8bc6\u5c60\u9f99\u4e4b\u540e\u4e0d\u4e45\uff0c\u4f46\u8fd8\u6ca1\u6709\u544a\u8bc9\u7236 \u4eb2\u3002\u5728\u5979\u7684\u5fc3\u76ee\u4e4b\u4e2d\uff0c\u5979\u7684\u672a\u6765\u592b\u5a7f\u6700\u5c11\u4e5f\u662f\u50cf\u5c60\u9f99\u8fd9\u6837\u98ce\u5ea6\u7fe9\u7fe9\u7684\u7f8e\u5c11\u5e74\u3002 \u3000\u3000\u5218\u5927\u4e3a\u4e5f\u56e0\u4e3a\u8fd9\u5934\u5a5a\u4e8b\u4e0d\u77e5\u80fd\u4e0d\u80fd\u591f\u66ff\u59b9\u59b9\u64ae\u5408\u6210\u529f\uff0c\u6545\u6b64\u4e5f\u4e0d\u60f3\u592a\u65e9\u8bf4\u51fa\u891a\u4e91\u5cf0\u7684\u540d \u5b57\uff0c\u4ee5\u514d\u5c06\u6765\u5a5a\u4e8b\u4e0d\u6210\uff0c\u5f7c\u6b64\u5c34\u5c2c\u3002 \u3000\u3000\u5218\u5927\u4e3a\u542c\u4e86\u59b9\u59b9\u7684\u8bdd\u3001\u5fc3\u4e2d\u4e00\u52a8\uff0c\u8bf4\u9053\uff1a\u201c\u54e6\uff0c\u4f60\u4e0d\u559c\u6b22\u6211\u7ed9\u4f60\u6211\u7684\u5973\u5a7f\uff0c\u662f\u4e0d\u662f\u4f60\u53e6 \u6709\u4e86\u610f\u4e2d\u4eba\u4e86\uff1f\u201d \u3000\u3000\u5218\u743c\u59d1\u5f53\u7136\u4e0d\u80af\u627f\u8ba4\uff0c\u552f\u6709\u64a4\u5a07\u8bf4\u9053\uff1a\u201c\u6211\u4ec0\u4e48\u4eba\u90fd\u4e0d\u559c\u6b22\u3002\u6211\u8fd9\u4e00\u751f\u90fd\u4e0d\u5ac1\u4eba\uff0c\u6211\u5728 \u5bb6\u4e2d\u4f8d\u5019\u7239\u7239\u3002\u201d \u3000\u3000\u5218\u5927\u4e3a\u7b11\u9053\uff1a\u201c\u4f60\u521a\u624d\u8bf4\u8981\u53bb\u95ef\u8361\u6c5f\u6e56\uff0c\u5c31\u5df2\u5fd8\u8bb0\u7239\u7239\u4e86\uff1f\u201d \u3000\u3000\u5218\u743c\u59d1\u6ee1\u9762\u901a\u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u903c\u6211\u51fa\u5ac1\uff0c\u6211\u5c31\u5b81\u613f\u5728\u5bb6\u4f8d\u5949\u7239\u7239\u4e86\u3002\u201d\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u4e0d \u662f\u6211\u5fd8\u8bb0\u7239\u7239\uff0c\u7239\u7239\u4e5f\u66fe\u5bf9\u6211\u8bf4\u8fc7\u5462\uff0c\u4ed6\u8bf4\u4ed6\u73b0\u5728\u5df2\u7ecf\u627e\u5230\u4e86\u4e00\u4f4d\u8001\u670b\u53cb\uff0c\u8fd9\u4f4d\u8001\u670b\u53cb\u662f\u4f4f \u5728\u5317\u8292\u5c71\u7684\u534e\u5929\u8679\u8001\u4f2f\uff0c\u548c\u54b1\u4eec\u7684\u5bb6\u76f8\u8ddd\u4e0d\u5230\u4e09\u767e\u91cc\u3002\u4ed6\u8bf4\u4ed6\u5c06\u6765\u60f3\u8981\u642c\u53bb\u548c\u51af\u8001\u4f2f\u540c\u4f4f\uff0c \u90a3\u65f6\u6709\u4eba\u4f5c\u4f34\uff0c\u4ed6\u5c31\u53ef\u4ee5\u8ba9\u6211\u51fa\u53bb\u8ddf\u4f60\u4e86\u3002\u201d \u3000\u3000\u534e\u5929\u8679\u6b63\u662f\u891a\u4e91\u5cf0\u7684\u5e08\u7236\u3002\u539f\u6765\u5979\u7684\u7236\u4eb2\u548c\u5979\u8bf4\u8fd9\u756a\u8bdd\uff0c\u6b63\u662f\u4e3a\u5979\u672a\u6765\u7684\u5a5a\u4e8b\u6253\u7b97\u7684\uff0c \u5148\u900f\u9732\u4e00\u70b9\u6d88\u606f\u7ed9\u5979\u3002 \u3000\u3000\u5218\u5927\u4e3a\u53d1\u51fa\u4f1a\u5fc3\u7684\u5fae\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u5f88\u597d\u5440\uff0c\u90a3\u4f60\u5c31\u4e0d\u7528\u8fd9\u6837\u7740\u6025\u4e86\u3002\u5f85\u7239\u7239\u6709\u4e86\u5b89 \u987f\uff0c\u4f60\u53c8\u6210\u4e86\u5a5a\uff0c\u90a3\u65f6\u592b\u59bb\u540c\u6765\u627e\u6211\uff0c\u6211\u624d\u9ad8\u5174\u5462\u4e86\u3002\u201d \u3000\u3000\u5218\u743c\u59d1\u9053\uff1a\u201c\u4f60\u53c8\u628a\u8304\u5b50\u7f20\u5230\u80e1\u74dc\u4e0a\u4e86\u3002\u6211\u662f\u8981\u72ec\u81ea\u884c\u8d70\u6c5f\u6e56\u7684\uff0c\u6211\u8fd8\u8981\u591a\u901b\u51e0\u4e2a\u5730\u65b9 \u624d\u6765\u627e\u4f60\u5462\u3002\u201d \u3000\u3000\u5218\u5927\u4e3a\u60f3\u4e86\u4e00\u60f3\uff0c\u671b\u7740\u59b9\u59b9\u90d1\u91cd\u8bf4\u9053\uff1a\u201c\u6211\u5c31\u8981\u548c\u4f60\u5206\u624b\u4e86\uff0c\u6709\u51e0\u53e5\u8bdd\u6211\u5fc5\u987b\u548c\u4f60 \u8bf4\u3002\u201d \u3000\u3000\u5218\u743c\u59d1\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u8bf4\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5218\u5927\u4e3a\u9053\uff1a\u201c\u4f60\u8bf4\u4f60\u6ca1\u6709\u6709\u610f\u4e2d\u4eba\uff0c\u6211\u76f8\u4fe1\u4f60\u3002\u4e0d\u8fc7\u5728\u4f60\u884c\u8d70\u6c5f\u6e56\u7684\u65f6\u5019\uff0c\u4f60\u53ef\u5f97\u5343\u4e07 \u8bb0\u7740\uff1a\u4eba\u5fc3\u9669\u6076\uff0c\u5207\u83ab\u8f7b\u6613\u76f8\u4fe1\u522b\u4eba\uff0c\u4e0a\u4e86\u4eba\u5bb6\u7684\u5f53\u3002\u6709\u7684\u4eba\u76f8\u8c8c\u751f\u5f97\u5f88\u597d\uff0c\u5634\u5df4\u8bf4\u5f97\u5f88 \u751c\uff0c\u4f46\u5374\u5f80\u5f80\u662f\u6709\u7740\u4e00\u526f\u574f\u5fc3\u80a0\u7684\u3002\u201d \u3000\u3000\u5218\u5927\u4e3a\u672c\u6765\u4e43\u662f\u6cdb\u8bba\uff0c\u63d0\u9192\u4ed6\u59b9\u59b9\u6ce8\u610f\uff0c\u4ee5\u514d\u8d3b\u8bef\u7ec8\u8eab\u7684\u3002\u60f3\u4e0d\u5230\u4e8b\u9694\u534a\u5e74\uff0c\u7adf\u662f\u7ed9\u4ed6 \u4e0d\u5e78\u800c\u8a00\u4e2d\u4e86\u3002 \u3000\u3000\u5218\u743c\u59d1\u60f3\u8d77\u4e86\u54e5\u54e5\u7684\u8a00\u8bed\uff0c\u4e0d\u7981\u6cea\u54bd\u5fc3\u4f24\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u54e5\u54e5\u770b\u4e86\u6211\u4fe1\u4e2d\u7684\u8fd9\u51e0\u53e5\u8bdd\uff0c \u4ed6\u4e00\u5b9a\u4f1a\u731c\u60f3\u5f97\u5230\u6211\u73b0\u5728\u7684\u906d\u9047\uff01\u201d \u3000\u3000\u5c60\u9f99\u54ea\u77e5\u662f\u8ba1\uff0c\u6ee1\u6000\u6b22\u559c\u5730\u8bf4\u9053\uff1a\u201c\u54a6\uff0c\u8bf4\u5230\u54b1\u4eec\u6210\u4eb2\u4e4b\u4e8b\u4f60\u8fd8\u4e0d\u5f00\u5fc3\u4e48\uff1f\u597d\u7aef\u7aef\u7684\u53c8 \u54ed\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5218\u743c\u59d1\u9053\uff1a\u201c\u8c01\u77e5\u4f60\u662f\u771f\u5fc3\u8fd8\u662f\u5047\u610f\uff1f\u201d \u3000\u3000\u5c60\u9f99\u6307\u5929\u8a93\u65e5\u5730\u8bf4\u9053\uff1a\u201c\u6211\u82e5\u4e0d\u662f\u6025\u7740\u548c\u4f60\u6210\u5a5a\uff0c\u8fd8\u4f1a\u50ac\u4f60\u5199\u8fd9\u5c01\u4fe1\u4e48\uff1f\u201d\u4ed6\u6068\u4e0d\u5f97\u8fd9 \u5c01\u4fe1\u65e9\u70b9\u5199\u6210\uff0c\u8bdd\u9898\u53c8\u515c\u56de\u6765\u4e86\u3002 \u3000\u3000\u5218\u743c\u59d1\u9053\uff1a\u201c\u540e\u9762\u7684\u8bdd\u4f60\u7ed9\u6211\u5199\u5427\uff0c\u53cd\u6b63\u54e5\u54e5\u53ea\u987b\u770b\u4e86\u5f00\u5934\u7684\u90a3\u51e0\u53e5\u8bdd\uff0c\u4fbf\u77e5\u9053\u8fd9\u5c01\u4fe1 \u4e00\u5b9a\u662f\u6211\u5199\u7684\u4e86\u3002\u201d \u3000\u3000\u5c60\u9f99\u5199\u597d\u4e86\u4fe1\uff0c\u5174\u51b2\u51b2\u5730\u4fbf\u8981\u62ff\u53bb\u4ea4\u7ed9\u9633\u5929\u96f7\u3002 \u3000\u3000\u5218\u743c\u59d1\u7275\u7740\u4ed6\u7684\u8863\u8896\uff0c\u4f4e\u58f0\u53eb\u9053\uff1a\u201c\u9f99\u54e5\uff01\u201d \u3000\u3000\u5c60\u9f99\u7b11\u9053\uff1a\u201c\u600e\u4e48\uff0c\u4f60\u820d\u4e0d\u5f97\u6211\u8d70\uff1f\u4f60\u521a\u624d\u4e0d\u662f\u8981\u8d76\u6211\u7684\u4e48\uff1f\u201d \u3000\u3000\u5218\u743c\u59d1\u5fcd\u7740\u5fc3\u4e2d\u7684\u618e\u6076\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e3a\u4f60\u51e0\u4e4e\u4e22\u4e86\u6027\u547d\uff0c\u4f60\u8fd8\u5fcd\u5fc3\u53d6\u7b11\u6211\uff1f\u6211\u5bb3 \u6015\u2026\u2026\u201d\u5c60\u9f99\u9053\uff1a\u201c\u6015\u4ec0\u4e48\uff1f\u6015\u891a\u4e91\u5cf0\u53c8\u6765\u62a2\u4f60\u5417\uff1f\u201d\u5218\u743c\u59d1\u9053\uff1a\u201c\u4ed6\u8fd8\u6709\u4e00\u4e2a\u515a\u7fbd\u5462\uff0c\u5c31 \u662f\u4ed6\u4eec\u4e0d\u6562\u518d\u6765\uff0c\u6211\u4e5f\u662f\u6709\u70b9\u63d0\u5fc3\u540a\u80c6\u3002\u4e07\u4e00\u4ed6\u4eec\u6765\u4e86\uff0c\u6211\u3001\u6211\u53ef\u53c8\u662f\u53d7\u4e86\u4f24\u7684\u3002\u201d \u3000\u3000\u5c60\u9f99\u9053\uff1a\u201c\u597d\uff0c\u6211\u53eb\u97e9\u8d85\u628a\u8fd9\u5c01\u4fe1\u7ed9\u4f60\u9001\u53bb\uff0c\u4e00\u53bb\u9a6c\u4e0a\u56de\u6765\uff0c\u6446\u9152\u7ed9\u4f60\u538b\u60ca\u3002\u201d \u3000\u3000\u5218\u743c\u59d1\u5fc3\u91cc\u6253\u7740\u4e3b\u610f\uff0c\u5c60\u9f99\u5fc3\u91cc\u4e5f\u5728\u6253\u7740\u53e6\u4e00\u4e2a\u574f\u4e3b\u610f\u3002 \u3000\u3000\u56ed\u5b50\u91cc\u7684\u4f8d\u536b\u7531\u4e8e\u97e9\u8d85\u7684\u4ea4\u4ee3\uff0c\u6ca1\u6709\u8fdb\u5165\u8fd9\u5ea7\u9662\u5b50\uff0c\u4ed6\u4eec\u641c\u4e0d\u7740\u891a\u4e91\u5cf0\uff0c\u4e5f\u5c31\u5230\u522b\u5904\u53bb \u4e86\u3002 \u3000\u3000\u5916\u9762\u5608\u5608\u6742\u6742\u7684\u58f0\u97f3\u6e10\u6e10\u9759\u4e86\uff0c\u5218\u743c\u59d1\u7684\u5fc3\u60c5\u5374\u5140\u662f\u4e0d\u80fd\u5e73\u9759\u3002\u5979\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6211\u8fd8\u6709 \u4ec0\u4e48\u9762\u76ee\u89c1\u6211\u54e5\u54e5\uff1f\u65e0\u8bba\u5982\u4f55\uff0c\u62fc\u4e86\u8fd9\u6761\u6027\u547d\uff0c\u6211\u4e5f\u5f97\u66ff\u4e49\u519b\u505a\u4e00\u4ef6\u597d\u4e8b\uff0c\u5018\u82e5\u80fd\u591f\u628a\u4e49\u519b \u7684\u76df\u4e3b\u6551\u51fa\u6765\uff0c\u6211\u6b7b\u4e86\u4e5f\u80dc\u4e8e\u62b1\u6127\u5077\u751f\u3002\u5509\uff0c\u6211\u672c\u6765\u53ef\u4ee5\u6709\u4e00\u4e2a\u7f8e\u6ee1\u59fb\u7f18\u7684\uff0c\u5982\u4eca\u5374\u662f\u7ed9\u81ea \u5df1\u6bc1\u4e86\u3002\u4f46\u613f\u891a\u4e91\u5cf0\u4ed6\u80fd\u591f\u9003\u51fa\u53bb\u624d\u597d\uff01\u201d \u3000\u3000\u891a\u4e91\u5cf0\u6b64\u65f6\u65e9\u5df2\u662f\u795e\u4e0d\u77e5\u9b3c\u4e0d\u89c9\u5730\u9003\u51fa\u4e86\u56fd\u5e08\u5e9c\u3002\u5218\u743c\u59d1\u5728\u6302\u5ff5\u7740\u4ed6\uff0c\u4ed6\u4e5f\u5728\u6302\u5ff5\u7740\u5218 \u743c\u59d1\u3002\u5f53\u7136\uff0c\u4e24\u4eba\u7684\u5fc3\u60c5\u662f\u4e0d\u5c3d\u76f8\u540c\u7684\uff01\u4ed6\u53ea\u5728\u4e3a\u5218\u743c\u59d1\u60cb\u60dc\uff0c\u5374\u5e76\u6ca1\u6709\u81ea\u6028\u81ea\u827e\u7684\u5fc3\u60c5\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u8d70\u51fa\u4e86\u201c\u56fd\u5e08\u5e9c\u201d\uff0c\u56de\u5934\u4e00\u671b\uff0c\u5fc3\u4e2d\u6697\u6697\u53f9\u4e86\u53e3\u6c14\uff0c\u60f3\u9053\uff1a\u201c\u4e00\u5931\u8db3\u6210\u5343\u53e4\u6068\uff0c \u518d\u56de\u5934\u662f\u767e\u5e74\u8eab\uff0c\u8fd9\u4e24\u53e5\u8001\u8bdd\u5f53\u771f\u662f\u8bf4\u5f97\u4e0d\u9519\u3002\u743c\u59d1\u672c\u6765\u662f\u4e2a\u597d\u5973\u5b50\uff0c\u53ef\u60dc\u65ad\u9001\u5728\u5c60\u9f99\u7684\u624b \u91cc\u3002\u4e0d\u8fc7\uff0c\u6211\u867d\u7136\u4e0d\u80fd\u4e0e\u5979\u6210\u4e3a\u592b\u5987\uff0c\u4e5f\u8fd8\u662f\u4e0d\u5e94\u8be5\u8ba9\u5979\u4e27\u8eab\u9b54\u7a9f\u7684\uff0c\u5982\u4eca\u6211\u662f\u5b64\u638c\u96be\u9e23\uff0c \u4e14\u5148\u56de\u53bb\u4e0e\u8c37\u5e08\u5f1f\u5546\u91cf\uff0c\u603b\u5f97\u8bbe\u4e2a\u6cd5\u513f\u6551\u5979\u624d\u662f\u3002\u6551\u5979\u51fa\u6765\uff0c\u6211\u518d\u5411\u5979\u89e3\u91ca\uff0c\u6c42\u5979\u539f\u8c05\u3002\u201d \u60f3\u81f3\u6b64\u5904\uff0c\u62ac\u5934\u4e00\u770b\u5929\u8272\uff0c\u53ea\u89c1\u5fae\u96e8\u5df2\u6536\uff0c\u4e4c\u4e91\u6e10\u6563\uff0c\u4e00\u5f2f\u7709\u6708\u4ece\u5c42\u4e91\u4e2d\u94bb\u51fa\u6765\uff0c\u4f46\u5df2\u8fc7\u4e86 \u5929\u5fc3\uff0c\u6708\u5411\u897f\u659c\u4e86\u3002\u201c\u8c37\u5e08\u5f1f\u4e00\u5b9a\u7b49\u5f97\u975e\u5e38\u5fc3\u7126\u4e86\uff0c\u73b0\u5728\u5df2\u662f\u8fc7\u4e86\u4e09\u66f4\uff0c\u4e0d\u77e5\u4ed6\u53ef\u8fd8\u662f\u5728\u90a3 \u9152\u9986\u4e4b\u4e2d\uff1f\u201d \u3000\u3000\u4e14\u8bf4\u8c37\u6db5\u865a\u5728\u90a3\u9152\u9986\u4e2d\u81ea\u659f\u81ea\u914c\uff0c\u4e0d\u77e5\u4e0d\u89c9\u5df2\u662f\u4e09\u66f4\u65f6\u5206\uff0c\u9986\u5b50\u91cc\u53ea\u5269\u4e0b\u5be5\u5be5\u51e0\u4e2a\u5ba2\u4eba \u4e86\uff0c\u5140\u662f\u672a\u89c1\u891a\u4e91\u5cf0\u5230\u6765\uff0c\u5fc3\u91cc\u81ea\u662f\u6709\u70b9\u60ca\u7591\u4e0d\u5b9a\uff0c\u6050\u6015\u891a\u4e91\u5cf0\u906d\u4e86\u610f\u5916\u3002 \u3000\u3000\u4eac\u57ce\u91cc\u7684\u4e00\u822c\u8336\u697c\u9152\u9986\u5927\u90fd\u662f\u5728\u5929\u9ed1\u4e4b\u540e\u5c31\u6536\u5e02\u7684\uff0c\u4f46\u8fd9\u95f4\u9152\u9986\u5374\u662f\u4f8b\u5916\uff0c\u8981\u5230\u4e09\u66f4\u8fc7 \u540e\uff0c\u65b9\u59cb\u505c\u6b62\u8425\u4e1a\u3002\u539f\u6765\u5b83\u662f\u505a\u8d4c\u9986\u5ba2\u4eba\u7684\u751f\u610f\u7684\u3002 \u3000\u3000\u5728\u5b83\u9644\u8fd1\u7684\u4e24\u6761\u8857\u9053\uff0c\u6b63\u662f\u4eac\u57ce\u91cc\u8d4c\u9986\u6700\u591a\u7684\u5730\u65b9\u3002\u8fd9\u4e9b\u8d4c\u9986\u4e5f\u90fd\u662f\u5f97\u5230\u5b98\u5e9c\u7684\u5305\u5e87 \u7684\u3002 \u3000\u3000\u8c37\u6db5\u865a\u6b63\u81ea\u95f7\u996e\u65e0\u804a\uff0c\u5ffd\u89c1\u4e24\u4e2a\u6b6a\u6234\u7740\u5e3d\u5b50\uff0c\u6cfc\u76ae\u6d41\u6c13\u6a21\u6837\u7684\u4eba\u8d70\u4e86\u8fdb\u6765\u3002\u8fd9\u5929\u665a\u4e0a\u3001 \u4e00\u76f4\u662f\u65ad\u65ad\u7eed\u7eed\u5730\u4e0b\u7740\u7ec6\u96e8\uff0c\u5ba2\u4eba\u4e0d\u591a\uff0c\u9152\u5e97\u7684\u638c\u67dc\u672c\u6765\u662f\u60f3\u4e00\u6253\u4e09\u66f4\u5c31\u6536\u5e02\u7684\uff0c\u89c1\u8fd9\u4e24\u4e2a \u6cfc\u76ae\u8fdb\u6765\uff0c\u4fbf\u8d70\u4e0a\u524d\u8d54\u7b11\u8bf4\u9053\uff1a\u201c\u5ba2\u5b98\uff0c\u660e\u5929\u8bf7\u65e9\u5427\u3002\u201d \u3000\u3000\u8fd9\u4e24\u4e2a\u6cfc\u76ae\u8131\u4e0b\u5e3d\u5b50\u5f80\u684c\u4e0a\u4e00\u4e22\uff0c\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u6015\u8001\u5b50\u6ca1\u94b1\u7ed9\u4f60\u5417\uff1f\u8001\u5b50\u504f\u504f\u8981\u559d\u5230 \u5929\u4eae\uff0c\u4f60\u60f3\u6b47\u606f\uff0c\u53eb\u4f60\u8001\u5a46\u6765\u4f3a\u5019\u5927\u7237\uff01\u201d \u3000\u3000\u8c37\u6db5\u8651\u89c1\u8fd9\u4e24\u4e2a\u6cfc\u76ae\u5982\u6b64\u6a2a\u86ee\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u82e5\u4e0d\u662f\u6211\u8eab\u4e0a\u6709\u4e8b\uff0c\u975e\u5f97\u6559\u8bad\u4ed6\u4eec\u4e00\u987f\u4e0d \u53ef\uff0c\u4e0d\u8fc7\u4ed6\u4eec\u8fd9\u4e48\u4e00\u95f9\uff0c\u5bf9\u6211\u5012\u4e5f\u6709\u70b9\u597d\u5904\uff0c\u5426\u5219\u9152\u9986\u5173\u4e0a\u4e86\u95e8\uff0c\u891a\u5927\u54e5\u5c31\u6ca1\u5904\u627e\u6211\u4e86\u3002\u201d \u3000\u3000\u638c\u67dc\u7684\u679c\u7136\u7ed9\u8fd9\u4e24\u4e2a\u6cfc\u76ae\u5413\u4f4f\uff0c\u6da8\u7ea2\u4e86\u8138\u8bf4\u9053\uff1a\u201c\u4e24\u4f4d\u8bf4\u7b11\uff0c\u5c0f\u7684\u600e\u6562\u4e0d\u4f3a\u5019\u5ba2\u5b98\uff1f\u597d \u597d\uff0c\u96be\u5f97\u4e24\u4f4d\u9a7e\u4e34\uff0c\u968f\u4e24\u4f4d\u559c\u6b22\u5c31\u662f\uff0c\u5c0f\u7684\u8fd9\u5c31\u53bb\u7ed9\u4e24\u4f4d\u5927\u7237\u70eb\u9152\uff0c\u4e24\u4f4d\u559c\u6b22\u559d\u4ec0\u4e48\uff0c\u82b1\u96d5 \u8fd8\u662f\u6c7e\u9152\uff1f\u201d \u3000\u3000\u8fd9\u4e24\u4e2a\u6cfc\u76ae\u54fc\u4e86\u4e00\u58f0\uff0c\u5927\u9a6c\u91d1\u5200\u7684\u5750\u4e86\u4e0b\u6765\uff0c\u5411\u8c37\u6db5\u865a\u671b\u4e86\u671b\uff0c\u4ea4\u5934\u63a5\u8033\u8bf4\u4e86\u51e0\u53e5\uff0c\u5ffd \u5730\u6709\u4e00\u4e2a\u6cfc\u76ae\u53c8\u7ad9\u8d77\u6765\uff0c\u8d70\u5230\u8c37\u6db5\u865a\u8eab\u8fb9\u3002 \u3000\u3000\u8c37\u6db5\u865a\u77aa\u4ed6\u4e00\u773c\uff0c\u4ecd\u662f\u81ea\u659f\u81ea\u996e\uff0c\u770b\u4ed6\u6709\u4f55\u52a8\u9759\u3002\u90a3\u4e2a\u6cfc\u76ae\u563b\u76ae\u7b11\u8138\u5730\u8bf4\u9053\uff1a\u201c\u8001\u5144\uff0c \u4f60\u662f\u4e0d\u662f\u8f93\u4e86\u94b1\uff0c\u8138\u8272\u8fd9\u6837\u96be\u770b\uff1f\u201d\u8c37\u6db5\u865a\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u8f93\u4e5f\u597d\uff0c\u8d62\u4e5f\u597d\uff0c\u4e0e\u4f60\u4f55\u5173\uff1f\u201d \u3000\u3000\u90a3\u6cfc\u76ae\u6253\u4e86\u4e00\u4e2a\u54c8\u54c8\uff0c\u8bf4\u9053\uff1a\u201c\u8bdd\u53ef\u4e0d\u662f\u8fd9\u6837\u8bf4\uff0c\u56db\u6d77\u4e4b\u5185\u7686\u5144\u5f1f\u4e5f\uff0c\u8001\u5144\uff0c\u4f60\u5018\u82e5\u662f \u8f93\u4e86\u94b1\uff0c\u6211\u5012\u53ef\u4ee5\u7ed9\u4f60\u7ffb\u672c\uff0c\u6765\u3001\u6765\u3001\u6765\uff0c\u6211\u77e5\u9053\u6709\u95f4\u8d4c\u9986\u53ef\u4ee5\u8d4c\u4e2a\u901a\u5bb5\u7684\uff0c\u6211\u5e26\u4f60\u53bb\uff01\u201d \u53e3\u6cab\u6a2a\u98de\uff0c\u6307\u624b\u753b\u811a\uff0c\u8bf4\u7740\uff0c\u8bf4\u7740\uff0c\u7adf\u7136\u628a\u4e00\u53ea\u624b\u642d\u4e0a\u4e86\u8c37\u6db5\u865a\u7684\u80a9\u5934\u3002 \u3000\u3000\u8c37\u6db5\u865a\u8d77\u521d\u4ee5\u4e3a\u4ed6\u662f\u7ed9\u8d4c\u9986\u62db\u63fd\u751f\u610f\u7684\u201c\u8fdb\u5ba2\u201d\uff0c\u5f85\u5230\u4ed6\u7684\u624b\u642d\u4e0a\u4e86\u80a9\u5934\uff0c\u8fd9\u624d\u4e0d\u89c9\u5fc3 \u4e2d\u4e00\u51db\uff0c\u539f\u6765\u8fd9\u4e2a\u6cfc\u76ae\u4e94\u6307\u6240\u6309\u4e4b\u5904\u6b63\u662f\u4ed6\u7684\u7435\u7436\u9aa8\u3002 \u3000\u3000\u8c37\u6db5\u865a\u60f3\u9053\uff1a\u201c\u83ab\u975e\u4ed6\u662f\u8981\u8bd5\u6211\u7684\u6b66\u529f\uff1f\u597d\uff0c\u4e0d\u7ba1\u4ed6\u662f\u4ec0\u4e48\u4eba\uff0c\u4e14\u628a\u4ed6\u6454\u4e00\u8de4\uff0c\u8ba9\u4ed6\u5403 \u70b9\u82e6\u518d\u8bf4\uff01\u201d\u5fc3\u5ff5\u4e00\u52a8\uff0c\u7acb\u5373\u6697\u8fd0\u5185\u529b\uff0c\u4f7f\u51fa\u4e86\u201c\u6cbe\u8863\u5341\u516b\u8dcc\u201d\u7684\u529f\u592b\u3002 \u3000\u3000\u90a3\u6cfc\u76ae\u7ed9\u8c37\u6db5\u865a\u7684\u5185\u529b\u9661\u5730\u4e00\u9707\uff0c\u4e0d\u7531\u5f97\u4e00\u4e2a\u8dc4\u8e09\uff0c\u6b6a\u6b6a\u659c\u659c\u76f4\u8dcc\u51fa\u53bb\u3002\u5e78\u800c\u53ca\u65f6\u6276\u7740 \u4e00\u5f20\u684c\u5b50\uff0c\u8fd9\u624d\u6ca1\u6709\u8dcc\u5012\u3002 \u3000\u3000\u8fd9\u6cfc\u76ae\u6ca1\u6709\u8dcc\u5012\uff0c\u5012\u662f\u9887\u51fa\u8c37\u6db5\u865a\u610f\u6599\u4e4b\u5916\u3002\u4ed6\u8fd9\u6cbe\u8863\u5341\u516b\u8dcc\u7684\u5185\u529f\uff0c\u7b49\u95f2\u4e4b\u8f88\u4ea6\u662f\u7981 \u53d7\u4e0d\u8d77\u7684\u3002\u867d\u7136\u4ed6\u5e76\u4e0d\u6253\u7b97\u91cd\u4f24\u8fd9\u4e2a\u6cfc\u76ae\uff0c\u4f46\u4e5f\u7528\u4e0a\u51e0\u5206\u5185\u529b\uff0c\u6ee1\u4ee5\u4e3a\u6700\u5c11\u53ef\u4ee5\u8dcc\u4ed6\u4e00\u4e2a\u4ef0 \u516b\u53c9\u7684\u3002 \u3000\u3000\u6cfc\u76ae\u7ad9\u76f4\u4e86\u8eab\u5b50\uff0c\u767b\u65f6\u6012\u6c14\u51b2\u51b2\u5730\u8bf4\u9053\uff1a\u201c\u72d7\u54ac\u5415\u6d1e\u5bbe\uff0c\u4e0d\u8bc6\u597d\u4eba\u5fc3\uff01\u4f60\u662f\u4e0d\u662f\u8981\u548c\u8001 \u5b50\u6253\u67b6\uff1f\u201d \u3000\u3000\u8c37\u6db5\u865a\u51b7\u7b11\u9053\uff1a\u201c\u6211\u7684\u6307\u5934\u513f\u90fd\u6ca1\u52a8\u4e00\u4e0b\uff0c\u4f60\u81ea\u5df1\u8dcc\u51fa\u53bb\uff0c\u4e0e\u6211\u4f55\u5173\uff1f\u8981\u6253\u67b6\u5417\uff0c\u90a3\u4e5f \u968f\u4f60\u7684\u4fbf\uff01\u201d\u5fc3\u4e2d\u5df2\u7136\u96ea\u4eae\uff0c\u8fd9\u4e2a\u6cfc\u76ae\u662f\u7ec3\u8fc7\u6b66\u529f\u7684\u4eba\uff0c\u51b3\u975e\u666e\u901a\u7684\u65e0\u8d56\u53ef\u6bd4\u3002\u8bf4\u4e0d\u5b9a\u8fd8\u53ef \u80fd\u662f\u5b98\u5e9c\u7684\u722a\u7259\uff0c\u6709\u5fc3\u6765\u627e\u4ed6\u7684\u5c94\u5b50\u3002 \u3000\u3000\u90a3\u6cfc\u76ae\u5377\u8d77\u8863\u8896\uff0c\u63e1\u8d77\u638c\u5934\uff0c\u88c5\u6a21\u4f5c\u6837\u5730\u53eb\u9053\uff1a\u201c\u597d\uff0c\u4f60\u5f53\u8001\u5b50\u4e0d\u6562\u548c\u4f60\u6253\u67b6\u5417\uff1f\u6709\u79cd \u7684\u4f60\u4e0d\u8981\u8d70\uff01\u201d\u53e3\u91cc\u5927\u547c\u5c0f\u53eb\uff0c\u811a\u6b65\u5374\u662f\u4e0d\u6562\u5411\u524d\u3002\u4ed6\u90a3\u4e2a\u540c\u4f34\uff0c\u5374\u65e9\u5df2\u6084\u6084\u5730\u6e9c\u8d70\u4e86\u3002 \u3000\u3000\u4ed6\u53ea\u662f\u52a8\u53e3\u6ca1\u6709\u52a8\u624b\uff0c\u8c37\u6db5\u865a\u81ea\u662f\u8981\u987e\u4f4f\u8eab\u4efd\uff0c\u4e0d\u80fd\u4e0a\u53bb\u6253\u4ed6\u3002\u4e0d\u8fc7\u8c37\u6db5\u865a\u5374\u62ff\u4e0d\u51c6\u4ed6 \u90a3\u4e2a\u540c\u4f34\u662f\u56e0\u80c6\u5c0f\u800c\u6e9c\u8d70\u5462\uff0c\u8fd8\u662f\u51fa\u53bb\u8ba8\u6551\u5175\u3002 \u3000\u3000\u8c37\u6db5\u865a\u6697\u81ea\u601d\u91cf\uff1a\u201c\u5018\u82e5\u8fd9\u4e24\u4e2a\u5bb6\u4f19\u662f\u5b98\u5e9c\u7684\u722a\u7259\uff0c\u627e\u4e86\u4eba\u6765\uff0c\u5411\u6211\u6311\u8845\uff0c\u90a3\u5012\u662f\u6709\u70b9 \u4e0d\u5999\u3002\u53ef\u662f\u6211\u82e5\u73b0\u5728\u5c31\u8d70\uff0c\u891a\u5927\u54e5\u6765\u4e86\uff0c\u90a3\u4e0d\u662f\u66f4\u7cdf\uff1f\u201d\u4e8e\u662f\u4ecd\u7136\u5750\u7740\u559d\u9152\uff0c\u6de1\u6de1\u8bf4\u9053\uff1a \u201c\u8981\u6253\u67b6\u5c31\u5feb\u70b9\u6765\uff0c\u6211\u53ef\u6ca1\u529f\u592b\u966a\u4f60\u5435\u67b6\u3002\u201d\u90a3\u6cfc\u76ae\u9053\uff1a\u201c\u5fd9\u4ec0\u4e48\uff0c\u6211\u591a\u7ed9\u4f60\u4e00\u70b9\u529f\u592b\uff0c\u8ba9 \u4f60\u4ed4\u7ec6\u60f3\u60f3\uff0c\u6709\u4ec0\u4e48\u540e\u4e8b\u8981\u4ea4\u4ee3\u7684\u6ca1\u6709\uff1f\u4f60\u82e5\u6015\u8bf4\u4e0d\u6e05\u695a\uff0c\u6211\u8fd8\u53ef\u4f3c\u53eb\u638c\u67dc\u7684\u7ed9\u4f60\u7eb8\u7b14\uff0c\u8ba9 \u4f60\u4e00\u6761\u6761\u5199\u4e0b\u6765\uff0c\u6211\u62c5\u4fdd\u9001\u5230\u4f60\u8001\u5a46\u513f\u5b50\u7684\u624b\u4e2d\u3002\u201d \u3000\u3000\u8fd9\u6cfc\u76ae\u6ee1\u53e3\u80e1\u8a00\uff0c\u76ee\u7684\u5f53\u7136\u662f\u5728\u62d6\u5ef6\u65f6\u95f4\u3002\u8c37\u6db5\u865a\u4e5f\u4e0d\u7406\u4ed6\uff0c\u81ea\u987e\u81ea\u7684\u559d\u9152\u3002 \u3000\u3000\u679c\u7136\u4e0d\u8fc7\u4e00\u4f1a\uff0c\u4fbf\u770b\u89c1\u53e6\u5916\u7684\u90a3\u4e2a\u6cfc\u76ae\u5e26\u4e86\u4e24\u4e2a\u4eba\u8fdb\u6765\uff0c\u4e00\u8fdb\u6765\u4fbf\u6307\u7740\u8c37\u6db5\u865a\u8bf4\u9053\uff1a \u201c\u5c31\u662f\u8fd9\u4e2a\u5c0f\u5b50\uff01\u201d \u3000\u3000\u8c37\u6db5\u865a\u89c1\u5bf9\u65b9\u53ea\u642c\u6765\u4e24\u4e2a\u201c\u6551\u5175\u201d\uff0c\u672c\u6765\u662f\u4e0d\u4ee5\u4e3a\u610f\u7684\uff0c\u4f46\u5f53\u4ed6\u62ac\u5934\u4e00\u770b\uff0c\u770b\u6e05\u695a\u4e86\u90a3 \u4e24\u4e2a\u4eba\u4e4b\u540e\uff0c\u5374\u4e0d\u7981\u5927\u5403\u4e00\u60ca\u4e86\u3002 \u3000\u3000\u539f\u6765\u8fd9\u4e24\u4e2a\u4eba\uff0c\u4e00\u4e2a\u662f\u201c\u6ec7\u5357\u4e03\u864e\u201d\u4e4b\u9996\u7684\u63d2\u7ffc\u864e\u6bb5\u70b9\u82cd\uff0c\u4e00\u4e2a\u662f\u6bb5\u70b9\u82cd\u7684\u5e08\u5f1f\u98de\u8c79\u5b50 \u891a\u9752\u5c71\u3002 \u3000\u3000\u8fd9\u5bf9\u5e08\u5144\u5f1f\u90fd\u662f\u548c\u8c37\u6db5\u865a\u7ed3\u6709\u5f88\u6df1\u7684\u6881\u5b50\u3002 \u3000\u3000\u4e94\u5e74\u524d\u6ec7\u5357\u4e03\u864e\u5728\u5c0f\u91d1\u5ddd\u56f4\u653b\u4e25\u70f7\u7684\u7236\u4eb2\u5ddd\u897f\u5927\u4fa0\u4e25\u58f0\u6d9b\uff0c\u8c37\u6db5\u865a\u4e8b\u524d\u5f97\u5230\u98ce\u58f0\uff0c\u7279\u5730 \u8d76\u5f80\uff0c\u62d4\u5200\u76f8\u52a9\uff0c\u628a\u6ec7\u5357\u4e03\u864e\u6253\u5f97\u843d\u82b1\u6d41\u6c34\u3002\u4e25\u58f0\u6d9b\u4e2d\u4e86\u6bb5\u70b9\u82cd\u7684\u4e00\u679a\u6697\u5668\uff0c\u6bb5\u70b9\u82cd\u4e5f\u7ed9\u8c37 \u6db5\u865a\u523a\u4e86\u4e00\u5251\u3002 \u3000\u3000\u4e8b\u9694\u534a\u5e74\uff0c\u891a\u9752\u5c71\u66ff\u4ed6\u7684\u5e08\u5144\u51fa\u5934\uff0c\u6311\u62e8\u4e25\u70f7\u7684\u672a\u5a5a\u592b\u5f20\u5143\u5409\uff0c\u4e0e\u4ed6\u6b66\u5f53\u6d3e\u7684\u540c\u95e8\u5144\u5f1f \u800c\u6765\u627e\u8c37\u6db5\u865a\u7684\u6666\u6c14\uff0c\u90a3\u665a\u6070\u503c\u8c37\u6db5\u865a\u4e0e\u4e25\u70f7\u5728\u6797\u4e2d\u5e7d\u4f1a\uff0c\u7ed3\u679c\u95f9\u51fa\u4e86\u4e00\u573a\u6240\u8c13\u201c\u6349\u5978\u201d\u7684 \u4e11\u5267\uff0c\u5f04\u5f97\u8c37\u3001\u4e25\u4e8c\u4eba\u6b7b\u522b\u751f\u79bb\uff0c\u5927\u597d\u59fb\u7f18\uff0c\u51e0\u4e4e\u65ad\u9001\u5728\u4ed6\u7684\u624b\u91cc\u3002\u8c37\u6db5\u865a\u672c\u662f\u4e00\u4e2a\u82f1\u4fca\u5c11 \u5e74\uff0c\u4e5f\u662f\u56e0\u4e3a\u6b64\u4e8b\uff0c\u5728\u90a3\u5929\u665a\u4e0a\uff0c\u7ed9\u5f20\u5143\u5409\u6bc1\u4e86\u6c60\u7684\u5bb9\u8c8c\u7684\u3002 \u3000\u3000\u8ffd\u6e90\u7978\u59cb\uff0c\u4ee4\u5f97\u8c37\u6db5\u865a\u53d7\u5230\u5982\u6b64\u60e8\u75db\u4f24\u5bb3\u7684\u4eba\uff0c\u5c31\u662f\u8fd9\u4e00\u5bf9\u5e08\u5144\u5f1f\uff01 \u3000\u3000\u8c37\u6db5\u865a\u867d\u7136\u6539\u4e86\u88c5\u675f\uff0c\u4f46\u8138\u4e0a\u7684\u5200\u75a4\u5374\u662f\u7792\u4e0d\u8fc7\u719f\u4eba\u7684\u3002\u6bb5\u70b9\u82cd\u4e00\u773c\u8ba4\u51fa\u4e86\u6c60\uff0c\u54c8\u54c8\u7b11 \u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u5929\u5802\u6709\u8def\u4f60\u4e0d\u8d70\uff0c\u5730\u72f1\u65e0\u95e8\u4f60\u95ef\u8fdb\u6765\uff01\u8fd9\u91cc\u53ef\u6bd4\u4e0d\u5f97\u5c0f\u91d1\u5ddd\u4efb\u7531\u4f60\u901e\u80fd \u4e86\uff01\u201d \u3000\u3000\u539f\u6765\u6bb5\u70b9\u82cd\u548c\u5e08\u5f1f\u891a\u9752\u5c71\u4e43\u662f\u65b0\u8fd1\u6295\u5165\u9633\u5929\u96f7\u7684\u201c\u56fd\u5e08\u5e9c\u201d\uff0c\u5145\u5f53\u4e86\u8eab\u4efd\u4e0d\u516c\u5f00\u7684\u6c49\u4eba \u536b\u58eb\u7684\u3002 \u3000\u3000\u9633\u5929\u96f7\u4e00\u6765\u56e0\u4e3a\u62d6\u96f7\u4f4f\u5728\u4ed6\u7684\u5e9c\u4e2d\uff0c\u4e8c\u6765\u674e\u601d\u5357\u548c\u6768\u5a49\u4e5f\u662f\u56da\u5728\u4ed6\u90a3\u513f\uff0c\u6545\u6b64\u5206\u5916\u5c0f \u5fc3\uff0c\u52a0\u5f3a\u9632\u5907\uff0c\u9664\u4e86\u5e9c\u4e2d\u591a\u6d3e\u5de1\u591c\u7684\u5b88\u536b\u4e4b\u5916\uff0c\u6bcf\u665a\u8fd8\u6d3e\u4e86\u4e0d\u5c11\u8eab\u4efd\u672a\u66fe\u516c\u5f00\u7684\u722a\u7259\uff0c\u5728\u5927 \u8857\u5c0f\u5df7\u5de1\u67e5\uff0c\u6ce8\u610f\u53ef\u7591\u7684\u4eba\u7269\u3002\u6bb5\u70b9\u82cd\u3001\u891a\u9752\u5c71\u548c\u90a3\u4e24\u4e2a\u201c\u6cfc\u76ae\u201d\u5c31\u662f\u5176\u4e2d\u7684\u4e00\u80a1\u3002 \u3000\u3000\u8fd9\u6b63\u662f\u4ec7\u4eba\u76f8\u89c1\uff0c\u5206\u5916\u773c\u7ea2\u3002\u8c37\u6db5\u865a\u559d\u9053\uff1a\u201c\u597d\u5440\uff0c\u6211\u6b63\u8981\u627e\u4f60\u4eec\u7b97\u5e10\uff01\u201d\u201c\u4e53\u201d\u7684\u98de \u8d77\u4e00\u811a\uff0c\u628a\u4e00\u5f20\u684c\u5b50\u5411\u6bb5\u70b9\u82cd\u8e22\u53bb\uff0c\u63a5\u7740\u547c\u7684\u4e00\u62f3\u6363\u51fa\uff0c\u6253\u70c2\u7a97\u95e8\uff0c\u4e00\u4e2a\u201c\u71d5\u5b50\u7a7f\u5e18\u201d\u7a9c\u51fa \u8857\u5fc3\uff01\u9152\u9986\u8001\u677f\u54ed\u4e27\u7740\u8138\u53eb\u56b7\uff1a\u201c\u6c42\u6c42\u4f60\u4eec\u522b\u6bc1\u4e86\u5c0f\u5e97\uff01\u201d \u3000\u3000\u6bb5\u70b9\u82cd\u4e00\u638c\u51fb\u51fa\uff0c\u628a\u90a3\u5f20\u684c\u5b50\u6253\u5f97\u788e\u6210\u516b\u5757\uff0c\u6728\u5c51\u7eb7\u98de\uff0c\u891a\u9752\u5c71\u62d4\u5200\u51fa\u9798\uff0c\u559d\u9053\uff1a\u201c\u59d3 \u8c37\u7684\u5c0f\u5b50\uff0c\u54ea\u91cc\u8dd1\uff01\u201d \u3000\u3000\u8c37\u6db5\u865a\u559d\u9053\uff1a\u201c\u5916\u9762\u6253\u53bb\uff01\u201d\u6bb5\u70b9\u82cd\u7eb5\u58f0\u7b11\u9053\uff1a\u201c\u8c05\u4f60\u4e5f\u8dd1\u4e0d\u4e86\uff0c\u54ea\u91cc\u90fd\u884c\uff01\u201d \u3000\u3000\u4e24\u4eba\u8ddf\u8e2a\u8ffd\u51fa\uff0c\u8c37\u6db5\u865a\u5251\u4e2d\u7a81\u638c\uff0c\u5de6\u4e00\u62db\u201c\u4e07\u91cc\u98de\u971c\u201d\uff0c\u53f3\u4e00\u62db\u201c\u5343\u5c71\u843d\u6728\u201d\uff0c\u5251\u5c16\u4e0a \u5149\u8292\u95ea\u70c1\uff0c\u6070\u4f3c\u9ed1\u591c\u7e41\u661f\uff0c\u5343\u70b9\u4e07\u70b9\u6d12\u843d\u4e0b\u6765\u3002\u5de6\u638c\u5288\u51fa\uff0c\u66f4\u662f\u7528\u4e0a\u4e86\u4e5d\u6210\u529f\u529b\u7684\u201c\u5929\u96f7 \u529f\u201d\uff0c\u638c\u529b\u6392\u5c71\u5012\u6d77\u822c\u7684\u5411\u90a3\u4e24\u4eba\u653b\u53bb\u3002 \u3000\u3000\u6bb5\u70b9\u82cd\u633d\u4e86\u4e00\u6735\u5251\u82b1\uff0c\u5e73\u80f8\u523a\u51fa\uff0c\u559d\u9053\uff1a\u201c\u8981\u62fc\u547d\u4e48\uff1f\u53ef\u60dc\u4f60\u662f\u5b64\u638c\u96be\u9e23\uff01\u201d\u891a\u9752\u5c71\u4e5f \u662f\u5200\u4e2d\u5939\u638c\uff0c\u4e0e\u5e08\u5144\u8054\u624b\uff0c\u4e00\u5200\u4e00\u5251\uff0c\u67b6\u4f4f\u4e86\u8c37\u6db5\u865a\u7684\u957f\u5251\uff0c\u4e24\u4eba\u5408\u529b\uff0c\u4e5f\u8db3\u591f\u62b5\u5fa1\u5f97\u4e86\u8c37 \u6db5\u865a\u7684\u5929\u96f7\u529f\u3002 \u3000\u3000\u539f\u6765\u6bb5\u70b9\u82cd\u662f\u201c\u6ec7\u5357\u4e03\u864e\u201d\u4e4b\u9996\uff0c\u5728\u5251\u6cd5\u4e0a\u7684\u786e\u662f\u6709\u8fc7\u4eba\u7684\u9020\u8be3\uff0c\u6697\u5668\u7684\u529f\u592b\u5c24\u5176\u64c5 \u957f\uff0c\u8bba\u771f\u5b9e\u7684\u672c\u9886\u867d\u7136\u6bd4\u4e0d\u4e0a\u8c37\u6db5\u865a\uff0c\u5374\u4e5f\u76f8\u5dee\u4e0d\u8fdc\uff0c\u891a\u9752\u5c71\u51fa\u9053\u8f83\u8fdf\uff0c\u5e76\u4e0d\u5217\u540d\u201c\u6ec7\u5357\u4e03 \u864e\u201d\uff0c\u4f46\u6b63\u56e0\u4e3a\u4ed6\u51fa\u9053\u8f83\u8fdf\uff0c\u5728\u5e08\u95e8\u7684\u65e5\u5b50\u4e5f\u5c31\u8f83\u4e45\uff0c\u5185\u529f\u7684\u9020\u8be3\uff0c\u5374\u6bd4\u5e08\u5144\u8fd8\u5f3a\u3002 \u3000\u3000\u8c37\u6db5\u865a\u54ac\u7d27\u7259\u6839\uff0c\u72e0\u72e0\u62fc\u6597\u3002\u4f46\u6bb5\u3001\u891a\u4e8c\u4eba\u8054\u624b\uff0c\u5b9e\u529b\u4e0d\u4e9a\u4e8e\u6ec7\u5357\u4e03\u864e\u3002\u8c37\u6db5\u865a\u5f53\u65e5\u5728 \u5c0f\u91d1\u5ddd\u4e4b\u6240\u4ee5\u80dc\u5f97\u6ec7\u5357\u4e03\u864e\uff0c\u662f\u56e0\u4e3a\u6709\u5ddd\u897f\u5927\u4fa0\u4e25\u58f0\u6d9b\u548c\u4ed6\u8054\u624b\uff0c\u5982\u4eca\u4ed6\u4ee5\u4e00\u654c\u4e8c\uff0c\u6b63\u662f\u5e94 \u4e86\u6bb5\u70b9\u82cd\u90a3\u53e5\u8bdd\u201c\u5b64\u638c\u96be\u9e23\u201d\u3002\u4e8c\u5341\u62db\u4e00\u8fc7\uff0c\u653b\u52bf\u5df2\u662f\u7ed9\u5bf9\u65b9\u62a2\u53bb\u4e86\u4e03\u516b\u6210\uff01\u800c\u4e14\u8c37\u6db5\u865a\u8fd8 \u5f97\u540a\u80c6\u63d0\u5fc3\uff0c\u6050\u9632\u91d1\u5ef7\u7684\u536b\u58eb\u6765\u5230\u3002 \u3000\u3000\u6fc0\u6597\u4e2d\u5ffd\u89c1\u4e00\u6761\u9ed1\u5f71\u65cb\u98ce\u4e5f\u4f3c\u5730\u8dd1\u6765\uff0c\u8c37\u6db5\u865a\u65b9\u81ea\u5fc3\u5934\u4e00\u51db\uff0c\u53ea\u542c\u5f97\u90a3\u4eba\u5927\u53eb\u9053\uff1a\u201c\u8c37 \u5e08\u5f1f\uff0c\u4f60\u6ca1\u4e8b\u4e48\uff1f\u201d\u539f\u6765\u6b63\u662f\u891a\u4e91\u5cf0\u56de\u6765\u4e86\u3002 \u3000\u3000\u8c37\u6db5\u865a\u8fd9\u624d\u8f6c\u60ca\u4e3a\u559c\uff0c\u53eb\u9053\uff1a\u201c\u6ca1\u4e8b\uff01\u628a\u8fd9\u4e24\u4e2a\u5bb6\u4f19\u6536\u62fe\u4e86\u518d\u8bf4\u3002\u891a\u5e08\u5144\uff0c\u96f7\u7535\u4ea4 \u8f70\uff01\u201d \u3000\u3000\u53cc\u638c\u9f50\u51fa\uff0c\u54cd\u5982\u90c1\u96f7\uff01\u8fd9\u4e00\u62db\u201c\u96f7\u7535\u4ea4\u8f70\u201d\u672c\u6765\u5c31\u662f\u5929\u96f7\u529f\u4e2d\u6700\u5389\u5bb3\u7684\u4e00\u62db\u6740\u624b\uff0c\u891a\u3001 \u8c37\u4e8c\u4eba\u53c8\u7ecf\u8fc7\u4e86\u5b5f\u5c11\u521a\u548c\u534e\u5929\u8679\u4e24\u5927\u540d\u5e08\u7684\u6307\u70b9\u3002\u4e24\u4eba\u5408\u4f7f\uff0c\u5a01\u529b\u66f4\u662f\u500d\u589e\uff0c\u6bb5\u70b9\u82cd\u4e0e\u891a\u9752 \u5c71\u7684\u5185\u529f\u867d\u7136\u9887\u6709\u9020\u8be3\uff0c\u5374\u5982\u4f55\u7ecf\u53d7\u5f97\u8d77\uff1f \u3000\u3000\u53ea\u542c\u5f97\u201c\u5494\u5693\u201d\u4e00\u58f0\uff0c\u891a\u9752\u5c71\u808b\u9aa8\u65ad\u4e86\u4e24\u6839\uff0c\u9707\u51fa\u4e09\u4e08\u5f00\u5916\u3002\u6bb5\u70b9\u82cd\u5185\u529f\u4e0d\u53ca\u5e08\u5f1f\uff0c\u8dcc \u4e86\u4e2a\u56db\u811a\u671d\u5929\uff0c\u5df2\u662f\u4e00\u547d\u545c\u547c\u4e86\u3002 \u3000\u3000\u90a3\u4e24\u4e2a\u201c\u6cfc\u76ae\u201d\u53ea\u6068\u7239\u5a18\u751f\u5c11\u4e24\u6761\u817f\uff0c\u6ca1\u547d\u98de\u9003\uff0c\u8fb9\u8dd1\u8fb9\u53eb\uff1a\u201c\u6765\u4eba\u54ea\uff01\u6765\u4eba\u54ea\uff01\u201d\u891a \u4e91\u5cf0\u8fdc\u8fdc\u7684\u4e00\u638c\u5288\u53bb\uff0c\u5288\u7a7a\u638c\u529b\uff0c\u8fbe\u5230\u4e86\u4ed6\u4eec\u8eab\u4e0a\uff0c\u867d\u4e0d\u81f3\u4e8e\u6bd9\u547d\uff0c\u5374\u4e5f\u767b\u65f6\u6655\u8fc7\u53bb\u4e86\u3002\u4f46 \u5728\u9644\u8fd1\u8857\u9053\u5de1\u903b\u7684\u4e24\u80a1\u201c\u56fd\u5e08\u5e9c\u201d\u7684\u4eba\u9a6c\u5374\u5df2\u95fb\u58f0\u8d76\u81f3\u3002 \u3000\u3000\u891a\u9752\u5c71\u751a\u662f\u987d\u5f3a\uff0c\u65ad\u4e86\u4e24\u6839\u808b\u6e2d\uff0c\u5c45\u7136\u8fd8\u80fd\u652f\u6491\u5f97\u4f4f\uff0c\u5f84\u5f80\u524d\u5954\u3002\u8c37\u6db5\u865a\u5fc3\u9053\uff1a\u201c\u8fd9\u6076 \u8d3c\u8ba4\u5f97\u6211\u7684\u9762\u76ee\uff0c\u53ef\u662f\u5bb9\u4ed6\u4e0d\u5f97\uff01\u201d\u65e7\u4ec7\u65b0\u6068\uff0c\u90fd\u4e0a\u5fc3\u5934\uff0c\u5927\u8e0f\u6b65\u5730\u8ffd\u4e0a\u53bb\uff0c\u559d\u9053\uff1a\u201c\u5929\u5802 \u6709\u8def\u4f60\u4e0d\u8d70\uff0c\u5730\u72f1\u65e0\u95e8\u4f60\u95ef\u8fdb\u6765\uff01\u597d\uff01\u8fd9\u4e24\u53e5\u8bdd\u4eca\u65e5\u8981\u5e94\u5728\u4f60\u7684\u8eab\u4e0a\uff01\u201d\u8fd9\u4e24\u53e5\u8bdd\u6b63\u662f\u4ed6\u4eec \u521a\u624d\u8bf4\u7684\u3001\u6b64\u65f6\u540e\u6094\u5df2\u8fdf\uff0c\u8c37\u6db5\u865a\u201c\u5530\u201d\u7684\u4e00\u5251\uff0c\u5c31\u628a\u891a\u9752\u5c71\u4e86\u7ed3\u4e86\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u8df3\u4e0a\u6c11\u623f\u7684\u5c4b\u9876\uff0c\u63ed\u4e86\u4e00\u53e0\u74e6\uff0c\u53cc\u638c\u4e00\u62cd\uff0c\u4ee5\u201c\u5929\u5973\u6563\u82b1\u201d\u7684\u624b\u6cd5\u6253\u4e0b\u53bb\uff0c\u74e6\u7247 \u7eb7\u98de\uff0c\u6253\u5f97\u90a3\u73ed\u8ffd\u6765\u7684\u722a\u7259\u9762\u9752\u5507\u80bf\u3002\u891a\u4e91\u5cf0\u548c\u8c37\u6db5\u865a\u65e9\u5df2\u4f7f\u51fa\u4e0a\u4e58\u8f7b\u529f\uff0c\u4e00\u6e9c\u70df\u7684\u98de\u8dd1\u4e86 \u51e0\u95f4\u5c4b\u9876\uff0c\u6084\u6ca1\u58f0\u5730\u8d70\u4e86\u3002\u8fd9\u4e9b\u4eba\u8fde\u4ed6\u4eec\u7684\u5e90\u5c71\u771f\u9762\u76ee\u90fd\u6ca1\u89c1\u7740\u3002 \u3000\u3000\u5230\u4e86\u65e0\u4eba\u4e4b\u5904\uff01\u8c37\u6db5\u8651\u7eb5\u58f0\u7b11\u9053\uff1a\u201c\u75db\u5feb\uff0c\u75db\u5feb\uff01\u201d\u891a\u4e91\u5cf0\u62ac\u5934\u4e00\u770b\u5929\u8272\uff0c\u5c31\u9053\uff1a\u201c\u5929 \u5feb\u4eae\u4e86\uff0c\u54b1\u4eec\u53ef\u5f97\u8d76\u5feb\u56de\u53bb\u5566\uff01\u201d \u3000\u3000\u4e24\u4eba\u56de\u5230\u4e10\u5e2e\u5206\u8235\uff0c\u4e0d\u60f3\u60ca\u52a8\u4f17\u4eba\uff0c\u4ece\u540e\u56ed\u6084\u6084\u6e9c\u56de\u53bb\uff0c\u8fdb\u5165\u5367\u5ba4\uff0c\u4e0d\u6599\u811a\u4e00\u6cbe\u5730\uff0c\u5ffd \u5730\u6709\u4e2a\u4eba\u5c06\u4ed6\u4eec\u6293\u4f4f\uff01 \u3000\u3000\u891a\u3001\u8c37\u4e8c\u4eba\u5403\u4e86\u4e00\u60ca\uff0c\u521a\u8981\u6323\u624e\uff0c\u53ea\u542c\u5f97\u90a3\u4eba\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u600e\u7684\u5230\u8fd9\u4e2a\u65f6\u5019\u624d\u56de\u6765\uff0c\u54ea \u91cc\u53bb\u4e86\uff0c\u4ece\u5b9e\u62db\u6765\uff01\u201d \u3000\u3000\u539f\u6765\u8fd9\u4e2a\u4eba\u662f\u67f3\u6d1e\u5929\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u7b11\u9053\uff1a\u201c\u5012\u7ed9\u4f60\u5413\u4e86\u4e00\u8df3\u3002\u8bf4\u6765\u8bdd\u957f\u2014\u2014\u201d \u3000\u3000\u67f3\u6d1e\u5929\u7b11\u9053\uff1a\u201c\u65e2\u662f\u8bf4\u6765\u8bdd\u957f\uff0c\u90a3\u5c31\u4ee5\u540e\u6162\u6162\u518d\u8bf4\uff0c\u4f60\u53ef\u77e5\u9053\u6211\u5728\u8fd9\u91cc\u7b49\u4f60\u4eec\u591a\u4e45\uff1f\u8db3 \u6709\u4e00\u4e2a\u534a\u65f6\u8fb0\u5566\uff01\u201d \u3000\u3000\u891a\u4e91\u5cf0\u6014\u4e86\u4e00\u6014\u9053\uff1a\u201c\u6709\u4ec0\u4e48\u4e8b\u5417\uff1f\u201d \u3000\u3000\u67f3\u6d1e\u5929\u9053\uff1a\u201c\u6709\u4e09\u4f4d\u5ba2\u4eba\u7b49\u7740\u8981\u89c1\u4f60\u4eec\uff01\u201d \u3000\u3000\u891a\u4e91\u5cf0\u8be7\u9053\uff1a\u201c\u4ec0\u4e48\u5ba2\u4eba\uff1f\u201d \u3000\u3000\u67f3\u6d1e\u5929\u9053\uff1a\u201c\u662f\u4f60\u610f\u60f3\u4e0d\u5230\u7684\u5ba2\u4eba\uff0c\u4f60\u89c1\u4e86\u4ed6\u4eec\u81ea\u7136\u660e\u767d\uff01\u201d \u3000\u3000\u891a\u4e91\u5cf0\u7b11\u9053\uff1a\u201c\u5356\u4ec0\u4e48\u5173\u5b50\uff1f\u201d\u4fbf\u4e0e\u8c37\u6db5\u865a\u8ddf\u7740\u4ed6\u8d70\uff0c\u8d70\u8fc7\u4e86\u540e\u9662\u7684\u62f1\u95e8\uff0c\u53ea\u542c\u5f97\u4e10\u5e2e \u5e2e\u4e3b\u9646\u6606\u4ed1\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u5b5f\u59d1\u5a18\u4e0d\u5fc5\u62c5\u5fc3\uff0c\u6211\u5df2\u6d3e\u4eba\u56db\u4e0b\u5bfb\u627e\u4ed6\u4eec\u4e86\u3002\u201d \u3000\u3000\u201c\u5b5f\u59d1\u5a18\u201d\u8fd9\u4e09\u4e2a\u5b57\u4ece\u9646\u6606\u4ed1\u53e3\u4e2d\u8bf4\u51fa\uff0c\u542c\u5165\u891a\u4e91\u5cf0\u7684\u8033\u6735\uff0c\u891a\u4e91\u5cf0\u4e0d\u7981\u4e3a\u4e4b\u4e00\u6014\uff0c\u5fc3 \u91cc\u60f3\u9053\uff1a\u201c\u5b5f\u59d1\u5a18\uff1f\u96be\u9053\u662f\u660e\u971e\u6765\u4e86\uff1f\u201d \u3000\u3000\u5fc3\u5ff5\u672a\u5df2\uff0c\u679c\u7136\u4fbf\u542c\u5f97\u5b5f\u660e\u971e\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u662f\u62c5\u5fc3\uff0c\u4f46\u53ea\u6015\u4ed6\u4eec\u662f\u53bb\u4e86\u9633\u5929\u96f7\u7684 \u56fd\u5e08\u5e9c\u3002\u7239\u7239\u2026\u2026\u201d \u3000\u3000\u5b5f\u5c11\u521a\u7b11\u9053\uff1a\u201c\u4f60\u6025\u4ec0\u4e48\uff0c\u9646\u5e2e\u4e3b\u81ea\u6709\u5b89\u6392\u3002\u201d \u3000\u3000\u9646\u6606\u4ed1\u9053\uff1a\u201c\u4e14\u7b49\u5929\u4eae\u518d\u8bf4\u3002\u5929\u4eae\u4e86\u5018\u82e5\u8fd8\u6ca1\u6709\u4ed6\u4eec\u7684\u6d88\u606f\uff0c\u81ea\u5f53\u8bf7\u4ee4\u5c0a\u51fa\u9a6c\u3002\u201d \u3000\u3000\u67f3\u6d1e\u5929\u52a0\u5feb\u811a\u6b65\uff0c\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u4e0d\u5fc5\u5b5f\u5927\u4fa0\u51fa\u9a6c\u5566\uff0c\u6211\u628a\u4ed6\u4eec\u5e26\u6765\u4e86\uff01\u201d\u56de\u5934\u63a5\u7740\u7b11 \u9053\uff1a\u201c\u891a\u5144\uff0c\u8c37\u5144\uff0c\u4f60\u77a7\u6211\u4e0d\u662f\u9a97\u4f60\u4eec\u5427\uff1f\u662f\u4e0d\u662f\u4f60\u4eec\u610f\u60f3\u4e0d\u5230\u7684\u5ba2\u4eba\u6765\u4e86\uff1f\u201d \u3000\u3000\u539f\u6765\u90a3\u4e09\u4e2a\u5ba2\u4eba\uff0c\u9664\u4e86\u5b5f\u5c11\u521a\u3001\u5b5f\u660e\u971e\u7236\u5973\u4e4b\u5916\uff0c\u8fd8\u6709\u4e00\u4e2a\u4e25\u70f7\u3002 \u3000\u3000\u891a\u3001\u8c37\u4e8c\u4eba\u60f3\u4e0d\u5230\u5979\u4eec\u4e5f\u90fd\u548c\u5b5f\u5c11\u521a\u6765\u4e86\uff0c\u76f8\u89c1\u4e4b\u4e0b\uff0c\u81ea\u662f\u7686\u5927\u6b22\u559c\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u7b11\u9053\uff1a\u201c\u660e\u971e\uff0c\u4f60\u731c\u5f97\u4e0d\u9519\uff0c\u6211\u7684\u786e\u662f\u521a\u521a\u4ece\u9633\u5929\u96f7\u7684\u56fd\u5e08\u5e9c\u56de\u6765\u3002\u201d \u3000\u3000\u5b5f\u5c11\u521a\u6447\u4e86\u6447\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u4e91\u5cf0\uff0c\u4f60\u7684\u80c6\u5b50\u4e5f\u592a\u5927\u4e86\uff0c\u542c\u8bf4\u8499\u53e4\u7684\u56fd\u5e08\u9f99\u8c61\u6cd5\u738b\u4e5f\u5728\u90a3 \u513f\uff0c\u6b64\u4eba\u662f\u8499\u53e4\u7b2c\u4e00\u9ad8\u624b\uff0c\u6b66\u529f\u5c1a\u5728\u9633\u5929\u96f7\u4e4b\u4e0a\uff0c\u4f60\u6ca1\u6709\u78b0\u4e0a\u5427\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u4fa5\u5e78\u6ca1\u6709\u78b0\u4e0a\u3002\u82e5\u662f\u78b0\u4e0a\uff0c\u6211\u54ea\u91cc\u8fd8\u80fd\u591f\u56de\u6765\uff1f\u201d \u3000\u3000\u9646\u6606\u4ed1\u9053\uff1a\u201c\u4f60\u4eec\u53ef\u627e\u5230\u4e86\u4ec0\u4e48\u7ebf\u7d22\uff0c\u674e\u76df\u4e3b\u7684\u4e0b\u843d\u5982\u4f55\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u5c1a\u672a\u67e5\u5230\uff0c\u4e0d\u8fc7\u5374\u4e5f\u6709\u4e86\u4e00\u70b9\u7ebf\u7d22\uff0c\u6211\u5728\u56fd\u5e08\u5e9c\u53d1\u73b0\u4e00\u4e2a\u610f\u60f3\u4e0d\u5230\u7684 \u4eba\u3002\u201d \u3000\u3000\u9646\u6606\u4ed1\u9053\uff1a\u201c\u662f\u8c01\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u5c60\u9f99\uff01\u201d \u3000\u3000\u9646\u6606\u4ed1\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u5c60\u767e\u57ce\u4e00\u4e16\u82f1\u96c4\uff0c\u7adf\u7559\u4e0b\u8fd9\u6837\u4e00\u4e2a\u4e0d\u8096\u4e4b\u5b50\u3002\u4f46\u5c60\u9f99\u65e9\u5df2 \u826f\u5fc3\u6cef\u706d\uff0c\u8ff7\u9014\u96be\u8fd4\uff0c\u8d8a\u9677\u8d8a\u6df1\uff0c\u4ed6\u4eca\u65e5\u8dd1\u5230\u9633\u5929\u96f7\u7684\u56fd\u5e08\u5e9c\u91cc\u8ba4\u8d3c\u4f5c\u7236\uff0c\u90a3\u4e5f\u4e0d\u8db3\u4e3a \u5947\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u53ef\u662f\u8fd8\u6709\u4e00\u4e2a\u548c\u5c60\u9f99\u540c\u5728\u4e00\u8d77\u7684\u4eba\uff0c\u624d\u662f\u66f4\u8981\u4ee4\u4eba\u53f9\u606f\u5462\uff01\u201d \u3000\u3000\u9646\u6606\u4ed1\u9053\uff1a\u201c\u4eba\u4ee5\u7c7b\u805a\uff0c\u7269\u4ee5\u7fa4\u5206\uff0c\u5c60\u9f99\u7684\u670b\u53cb\u81ea\u5fc5\u548c\u4ed6\u662f\u4e00\u4e18\u4e4b\u8c89\uff0c\u53c8\u503c\u5f97\u4ec0\u4e48\u60cb \u60dc\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u4e0d\uff0c\u8fd9\u4e2a\u4eba\u662f\u4e0a\u4e86\u5c60\u9f99\u7684\u5f53\u3002\u5979\u662f\u5218\u701a\u7ae0\u7684\u5973\u513f\uff0c\u5218\u5927\u4e3a\u7684\u59b9\u59b9\u5218\u743c \u59d1\uff01\u201d \u3000\u3000\u9646\u6606\u4ed1\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u5218\u8001\u82f1\u96c4\u7684\u5973\u513f\u7adf\u7ed9\u5c60\u9f99\u9a97\u8fdb\u4e86\u56fd\u5e08\u5e9c\uff1f\u8fd9\u662f\u600e\u4e48\u4e00\u56de\u4e8b \u60c5\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u628a\u78b0\u89c1\u5218\u743c\u59d1\u7684\u4e8b\u60c5\u8bf4\u4e86\u4e00\u4e2a\u5927\u6982\uff0c\u7565\u53bb\u4ed6\u7684\u5e08\u7236\u66fe\u7ecf\u4e3a\u4ed6\u8ba2\u5a5a\u4e4b\u4e8b\u4e0d\u63d0\u3002\u4f46\u53ea \u662f\u90a3\u4e9b\u8bf4\u51fa\u6765\u7684\u4e8b\u5b9e\uff0c\u5df2\u662f\u8db3\u4ee5\u4ee4\u4eba\u60ca\u5fc3\u52a8\u9b44\uff0c\u4f17\u4eba\u542c\u4e86\uff0c\u65e0\u4e0d\u6168\u53f9\u3002 \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u4f60\u521a\u624d\u8bf4\u662f\u53d1\u73b0\u4e86\u610f\u60f3\u4e0d\u5230\u7684\u4eba\uff0c\u5176\u5b9e\u53d1\u73b0\u5c60\u9f99\u4e0d\u8db3\u4e3a\u5947\uff0c\u8fd9\u4e2a\u5218\u743c\u59d1\u624d \u771f\u662f\u610f\u60f3\u4e0d\u5230\u7684\u5462\uff01\u201d \u3000\u3000\u5b5f\u660e\u971e\u8bf4\u7684\u6b63\u662f\u891a\u4e91\u5cf0\u5fc3\u91cc\u7684\u8bf4\u8bdd\uff0c\u5176\u5b9e\u4ed6\u521a\u624d\u5148\u63d0\u5c60\u9f99\uff0c\u4e5f\u53ea\u4e0d\u8fc7\u662f\u62ff\u4ed6\u4f5c\u4e2a\u201c\u5f15 \u5b50\u201d\u800c\u5df2\u3002\u891a\u4e91\u5cf0\u542c\u5f97\u5b5f\u660e\u971e\u8fd9\u6837\u8bf4\uff0c\u5fc3\u91cc\u4e0d\u89c9\u6026\u7136\u4e00\u8df3\uff1a\u201c\u96be\u9053\u5979\u5df2\u542c\u5230\u4e86\u4ec0\u4e48\u98ce\u58f0\uff1f\u4f46 \u76f8\u4fe1\u5979\u4e00\u5b9a\u4f1a\u4fe1\u5f97\u8fc7\u6211\u7684\u3002\u201d \u3000\u3000\u5b5f\u5c11\u521a\u9053\uff1a\u201c\u8fd9\u4f4d\u5218\u59d1\u5a18\u5982\u6b64\u521a\u70c8\uff0c\u867d\u7136\u5e74\u5c11\u65e0\u77e5\uff0c\u4e0a\u4e86\u574f\u4eba\u7684\u5f53\uff0c\u4e5f\u8fd8\u662f\u503c\u5f97\u94a6\u4f69 \u7684\uff0c\u54b1\u4eec\u51b3\u4e0d\u80fd\u8ba9\u5979\u4e27\u8eab\u9b54\u7a9f\uff01\u201d \u3000\u3000\u9646\u6606\u4ed1\u9053\uff1a\u201c\u6211\u9a6c\u4e0a\u6d3e\u4eba\u5230\u5317\u8292\u5c71\u53bb\u901a\u77e5\u5979\u7684\u54e5\u54e5\uff01\u201d \u3000\u3000\u76c2\u5c11\u521a\u9053\uff1a\u201c\u54b1\u4eec\u53cc\u7ba1\u5f00\u4e0b\uff0c\u67f3\u8d24\u4f84\uff0c\u660e\u5929\u4f60\u5c31\u53bb\u8d74\u9633\u5929\u96f7\u4e4b\u7ea6\u5427\uff01\u201d \u3000\u3000\u67f3\u6d1e\u5929\u9053\uff1a\u201c\u660e\u5929\u6b63\u662f\u6211\u548c\u9633\u5929\u96f7\u7ea6\u5b9a\u7684\u671f\u9650\u7684\u6700\u540e\u4e00\u5929\uff0c\u6211\u53bb\u521a\u597d\u5408\u9002\uff0c\u4f46\u54b1\u4eec\u8fd8\u5f97 \u5546\u8bae\u4e00\u4e0b\u3002\u201d \u3000\u3000\u5546\u8bae\u7684\u7ed3\u679c\uff0c\u4ecd\u7136\u6309\u7167\u524d\u5b9a\u7684\u8ba1\u5212\uff0c\u7531\u891a\u4e91\u5cf0\u548c\u8c37\u6db5\u865a\u626e\u4f5c\u4ed6\u4e0e\u5d14\u9547\u5c71\u7684\u968f\u4ece\uff0c\u5907\u529e \u201c\u62dc\u5e16\u201d\uff0c\u4e2d\u5348\u65f6\u5206\uff0c\u524d\u5f80\u201c\u56fd\u5e08\u5e9c\u201d\uff0c\u201c\u8c12\u89c1\u201d\u9633\u5929\u96f7\u3002\u5b5f\u5c11\u521a\u4e0e\u97e9\u5927\u7ef4\u5219\u901a\u8fc7\u4e10\u5e2e\u7684\u5185 \u7ebf\u5b89\u6392\uff0c\u5929\u4eae\u4e4b\u540e\uff0c\u5148\u6df7\u8fdb\u201c\u56fd\u5e08\u5e9c\u201c\u8eb2\u85cf\uff0c\u76f8\u673a\u884c\u4e8b\u3002\u5f53\u7136\u8fd9\u8981\u5192\u4e00\u4e9b\u9669\uff0c\u4f46\u4ee5\u4ed6\u4eec\u4e8c\u4eba \u7684\u7edd\u9876\u6b66\u529f\uff0c\u5373\u4f7f\u906d\u9047\u610f\u5916\uff0c\u6599\u60f3\u4e5f\u8db3\u4ee5\u5e94\u4ed8\u88d5\u5982\u3002 \u3000\u3000\u8ba1\u8bae\u5df2\u5b9a\uff0c\u4e1c\u65b9\u4ea6\u5df2\u73b0\u51fa\u4e00\u7247\u9c7c\u809a\u767d\u4e86\u3002\u4e10\u5e2e\u4e0a\u4e0b\u8981\u5728\u4e00\u4e2a\u65f6\u8fb0\u4e4b\u5185\u51c6\u5907\u59a5\u5f53\uff0c\u5206\u5934\u884c \u4e8b\uff0c\u4e0d\u5fc5\u7ec6\u8868\u3002\u56de\u8f6c\u7b14\u6765\uff0c\u518d\u53d9\u5218\u743c\u59d1\u4e0e\u5c60\u9f99\u6628\u665a\u4e4b\u4e8b\u3002 \u3000\u3000\u76ee\u8bf4\u5218\u743c\u59d1\u5728\u891a\u4e91\u5cf0\u8d70\u540e\uff0c\u601d\u6f6e\u8d77\u4f0f\uff0c\u4e0d\u80fd\u81ea\u4f11\u3002\u521a\u521a\u6253\u597d\u4e3b\u610f\uff0c\u53ea\u89c1\u5c60\u9f99\u62ff\u4e86\u4e00\u5927\u58f6 \u9152\uff0c\u5df2\u662f\u56de\u5230\u5979\u7684\u623f\u4e2d\u3002 \u3000\u3000\u5218\u743c\u59d1\u9053\uff1a\u201c\u90a3\u4e24\u4e2a\u523a\u5ba2\u53ef\u62ff\u83b7\u4e86\uff1f\u201d \u3000\u3000\u5c60\u9f99\u8bf4\u9053\uff1a\u201c\u53ef\u60dc\u4ed6\u4eec\u6e9c\u8d70\u4e86\u3002\u4f46\u4f60\u4e5f\u4e0d\u5fc5\u62c5\u5fc3\uff0c\u8c05\u4ed6\u4eec\u4e5f\u4e0d\u6562\u518d\u6765\uff01\u201d \u3000\u3000\u5218\u743c\u59d1\u653e\u4e0b\u4e86\u5fc3\u4e0a\u7684\u4e00\u5757\u77f3\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u7684\u670b\u53cb\u5012\u662f\u4e0d\u5c11\u554a\uff0c\u521a\u624d\u6211\u542c\u5f97\u6ee1\u56ed\u5b50\u90fd\u662f \u811a\u6b65\u58f0\u4e86\u3002\u201d \u3000\u3000\u5c60\u9f99\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5e78\u4e8f\u5979\u6ca1\u6709\u770b\u89c1\u90a3\u4e9b\u5973\u771f\u6b66\u58eb\u3002\u201d\u8bf4\u9053\uff1a\u201c\u524d\u5929\u521a\u597d\u6709\u4e00\u73ed\u670b\u53cb\u6765\u6b64 \u805a\u4f1a\uff0c\u6211\u77e5\u9053\u4f60\u6015\u5e94\u916c\uff0c\u54b1\u4eec\u540d\u5206\u4e5f\u5c1a\u672a\u5b9a\uff0c\u6240\u4ee5\u6211\u6ca1\u6709\u7ed9\u4f60\u5f15\u89c1\u3002\u201d \u3000\u3000\u5218\u743c\u59d1\u9053\uff1a\u201c\u8fd9\u73ed\u670b\u53cb\u90fd\u662f\u548c\u4f60\u5fd7\u540c\u9053\u5408\u7684\u5417\uff1f\u201d \u3000\u3000\u5c60\u9f99\u9053\uff1a\u201c\u4e0d\u9519\u3002\u90fd\u662f\u50cf\u6211\u4e00\u6837\uff0c\u60f3\u8981\u6295\u5954\u4e49\u519b\u8fd8\u672a\u6709\u95e8\u8def\u7684\u3002\u6240\u4ee5\u6211\u624d\u8981\u6467\u4f60\u5199\u90a3\u5c01 \u4fe1\u3002\u201d \u3000\u3000\u5218\u63a0\u59d1\u5fc3\u91cc\u9a82\u9053\uff1a\u201c\u73b0\u5728\u4f60\u8fd8\u60f3\u8981\u9a97\u6211\uff01\u201d\u8138\u4e0a\u5374\u5806\u51fa\u7b11\u5bb9\uff0c\u8bf4\u9053\uff1a\u201c\u4fe1\u5df2\u9001\u51fa\u4e86 \u5417\uff1f\u201d\u5c60\u9f99\u9053\uff1a\u201c\u9001\u51fa\u53bb\u4e86\uff0c\u6211\u662f\u7279\u5730\u6765\u966a\u4f60\u559d\u538b\u60ca\u9152\uff01\u201d\u6b63\u662f\uff1a \u3000\u3000\u53ea\u9053\u7ea2\u989c\u5bb9\u6613\u9a97\uff0c\u8c01\u77e5\u5927\u7978\u5df2\u4e34\u5934\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b\u2014\u2014\u7b2c\u4e94\u5341\u4e94\u56de\u3000\u6e05\u7406\u5e08\u95e8\u9976\u80c6\u8bc6\u3000\u667a\u64d2\u53db\u8d3c\u8d5b\u987b\u7709 \u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b \u7b2c\u4e94\u5341\u4e94\u56de\u3000\u6e05\u7406\u5e08\u95e8\u9976\u80c6\u8bc6\u3000\u667a\u64d2\u53db\u8d3c\u8d5b\u987b\u7709 \u3000\u3000\u5218\u743c\u59d1\u4f6f\u4f5c\u7709\u5f00\u773c\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u9f99\u54e5\uff0c\u96be\u5f97\u4f60\u5bf9\u6211\u8fd9\u6837\u4f53\u8d34\u3002\u201d\u5c60\u9f99\u7b11\u9053\uff1a\u201c\u6211\u51e0\u65f6\u5bf9 \u4f60\u4e0d\u662f\u771f\u5fc3\u5b9e\u610f\uff0c\u4f60\u81ea\u5df1\u591a\u7591\u7f62\u4e86\uff01\u201d \u3000\u3000\u5218\u743c\u59d1\u659f\u4e86\u6ee1\u6ee1\u4e00\u676f\uff0c\u8bf4\u9053\uff1a\u201c\u4eca\u665a\u591a\u4e8f\u4e86\u4f60\uff0c\u6211\u5148\u656c\u4f60\u4e09\u676f\uff01\u201d\u5c60\u9f99\u9053\uff1a\u201c\u5e94\u8be5\u662f\u6211 \u5148\u656c\u4f60\uff0c\u7ed9\u4f60\u538b\u60ca\u3002\u201d\u5218\u743c\u59d1\u9053\uff1a\u201c\u4fd7\u8bed\u8bf4\u592b\u5531\u5987\u968f\uff0c\u54ea\u6709\u6211\u5148\u559d\u7684\u9053\u7406\uff0c\u9664\u975e\u4f60\u662f\u4e0d\u60f3\u5a36 \u6211\u4e3a\u59bb\u3002\u201d \u3000\u3000\u5c60\u9f99\u5fc3\u82b1\u6012\u653e\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8fd9\u5f20\u5c0f\u5634\u5012\u662f\u771f\u4f1a\u8bf4\u8bdd\uff0c\u597d\uff0c\u6211\u559d\uff0c\u6211\u559d\uff01\u201d\u5fc3\u91cc\u60f3\u9053\uff1a \u201c\u4eca\u665a\u4f60\u603b\u662f\u98de\u4e0d\u51fa\u6211\u7684\u624b\u5fc3\u7684\u4e86\uff0c\u6211\u4e14\u54c4\u4f60\u6b22\u559c\uff0c\u8ba9\u4f60\u4e56\u4e56\u5c31\u8303\u3002\u201d\u539f\u6765\u5c60\u9f99\u5b58\u5fc3\u5c06\u5979\u704c \u9189\uff0c\u5f85\u751f\u7c73\u716e\u6210\u719f\u996d\uff0c\u4e0d\u6101\u5218\u743c\u59d1\u4e0d\u542c\u4ed6\u7684\u6446\u5e03\u3002\u8fd9\u9152\u662f\u201c\u56fd\u5e08\u5e9c\u201d\u4e2d\u85cf\u4e86\u4e09\u5341\u5e74\u4ee5\u4e0a\u7684\u4f73 \u917f\uff0c\u9152\u6027\u751a\u70c8\uff0c\u5165\u53e3\u7518\u9999\uff0c\u4e0d\u8fc7\uff0c\u5c60\u9f99\u5012\u6ca1\u6709\u5728\u9152\u4e2d\u4e0b\u836f\u3002\u56e0\u4e3a\u4ed6\u6599\u60f3\u5218\u743c\u59d1\u7684\u9152\u91cf\u51b3\u4e0d\u5982 \u4ed6\uff0c\u65e0\u987b\u51fa\u6b64\u4e0b\u7b56\u3002 \u3000\u3000\u5c60\u9f99\u63a5\u8fde\u559d\u4e86\u4e09\u676f\uff0c\u8bf4\u9053\uff1a\u201c\u73b0\u5728\u8be5\u6211\u656c\u4f60\u4e86\u3002\u201d\u5218\u743c\u59d1\u9053\uff1a\u201c\u6211\u8981\u916c\u8c22\u4f60\u7684\u529f\u52b3\uff0c\u4f60 \u518d\u559d\u4e09\u676f\uff01\u201d\u5c60\u9f99\u9053\uff1a\u201c\u597d\uff0c\u4e00\u8a00\u4e3a\u5b9a\uff0c\u6211\u559d\u4e86\u8fd9\u4e09\u676f\uff0c\u4f60\u4e00\u5b9a\u8981\u966a\u6211\u559d\u4e86\uff01\u201d\u5218\u743c\u59d1\u7b11 \u9053\uff1a\u201c\u541b\u5b50\u4e00\u8a00\uff0c\u5feb\u9a6c\u4e00\u97ad\uff0c\u83ab\u4e0d\u6210\u4f60\u8fd8\u8981\u548c\u6211\u51fb\u638c\u7acb\u8a93\u4e48\uff1f\u201d \u3000\u3000\u5c60\u9f99\u7b11\u563b\u563b\u5730\u53c8\u559d\u4e86\u4e09\u676f\uff0c\u4e1a\u5df2\u6709\u4e86\u51e0\u5206\u9152\u610f\uff0c\u63d0\u8d77\u9152\u58f6\uff0c\u4fbf\u7ed9\u5218\u743c\u59d1\u659f\u9152\uff0c\u8bf4\u9053\uff1a \u201c\u6709\u8a00\u5728\u5148\uff0c\u8fd9\u6b21\u8be5\u4f60\u559d\u4e09\u676f\u4e86\uff01\u201d \u3000\u3000\u5218\u743c\u59d1\u77e5\u9053\u9152\u4e2d\u6ca1\u6709\u9ebb\u836f\uff0c\u653e\u4e0b\u4e86\u5fc3\uff0c\u5374\u88c5\u4f5c\u4e0d\u4f1a\u559d\u9152\u7684\u6837\u5b50\uff0c\u62ff\u8d77\u9152\u676f\uff0c\u6d45\u6d45\u5730\u5477\u4e86 \u4e00\u53e3\uff0c\u767b\u65f6\u51e0\u58f0\u54b3\u55fd\uff0c\u4fbf\u653e\u4e0b\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u884c\uff0c\u4e0d\u884c\uff0c\u8fd9\u9152\u592a\u5389\u5bb3\u4e86\uff01\u201d \u3000\u3000\u5c60\u9f99\u9053\uff1a\u201c\u55ef\uff0c\u4f60\u5fd8\u8bb0\u4e86\u4f60\u8bf4\u7684\u8bdd\u4e48\uff1f\u541b\u5b50\u4e00\u8a00\uff0c\u5feb\u9a6c\u4e00\u97ad\uff01\u201d \u3000\u3000\u5218\u743c\u59d1\u7b11\u9053\uff1a\u201c\u6211\u662f\u5973\u6d41\u4e4b\u8f88\uff0c\u53ef\u4e0d\u914d\u79f0\u4e3a\u541b\u5b50\u5462\uff01\u201d \u3000\u3000\u5c60\u9f99\u9053\uff1a\u201c\u4f60\u60f3\u6492\u8d56\uff1f\u597d\u5440\uff0c\u4f60\u4e0d\u559d\u6211\u53ef\u8981\u7528\u5f3a\u4e86\uff01\u201d \u3000\u3000\u5218\u743c\u59d1\u6447\u624b\u9053\uff1a\u201c\u522b\u80e1\u6765\uff0c\u6211\u607c\u5566\uff1f\u201d\u5c60\u9f99\u9053\uff1a\u201c\u90a3\u4f60\u559d\u5427\uff01\u201d\u5218\u743c\u59d1\u9053\uff1a\u201c\u6211\u4e0d\u662f\u6492 \u8d56\uff0c\u4f46\u6211\u521a\u624d\u662f\u600e\u6837\u8bf4\u7684\uff0c\u4f60\u8fd8\u8bb0\u5f97\u4e48\uff1f\u201d \u3000\u3000\u5c60\u9f99\u9053\uff1a\u201c\u4f60\u8bf4\u6211\u559d\u4e86\u8fd9\u4e09\u676f\uff0c\u4f60\u5c31\u966a\u6211\u559d\u7684\uff01\u201d \u3000\u3000\u5218\u743c\u59d1\u9053\uff1a\u201c\u662f\u5440\uff01\u6211\u53ea\u662f\u8bf4\u966a\u4f60\u559d\u9152\uff0c\u53ef\u6ca1\u6709\u8bf4\u4f60\u559d\u4e09\u676f\u6211\u5c31\u5f97\u966a\u4f60\u4e5f\u559d\u4e09\u676f\u5440\uff01\u4f60 \u77e5\u9053\u6211\u662f\u4e2a\u4e61\u4e0b\u59d1\u5a18\uff0c\u4e0d\u4f1a\u559d\u9152\u7684\uff0c\u600e\u6bd4\u5f97\u4e0a\u4f60\u4eec\u7537\u4eba\u6d77\u91cf\u3002\u201d \u3000\u3000\u5c60\u9f99\u9053\uff1a\u201c\u597d\u5427\uff0c\u90a3\u4e48\u6211\u559d\u4e09\u676f\uff0c\u4f60\u603b\u5f97\u559d\u4e00\u676f\u5427\uff01\u8fd9\u9152\u5f88\u5bb9\u6613\u5165\u53e3\u7684\uff0c\u9189\u4e86\u4e5f\u4e0d\u6253 \u7d27\u3002\u201d \u3000\u3000\u5218\u743c\u59d1\u4f6f\u4f5c\u52c9\u4e3a\u5176\u96be\u7684\u6837\u5b50\uff0c\u559d\u4e86\u4e00\u676f\uff0c\u4e0d\u8fc7\u7247\u523b\uff0c\u8138\u4e0a\u5df2\u662f\u5fae\u6cdb\u7ea2\u971e\uff0c\u7ad9\u8d77\u6765\u7ed9\u5c60\u9f99 \u659f\u9152\uff0c\u624b\u6307\u4e5f\u6709\u70b9\u6296\u98a4\u4e86\u3002 \u3000\u3000\u5c60\u9f99\u6697\u6697\u6b22\u559c\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u81f3\u8fdf\u4e09\u5929\uff0c\u4f60\u7684\u54e5\u54e5\u5c31\u4f1a\u6765\u4e86\u3002\u4eca\u665a\u82e5\u662f\u7c73\u5df2\u6210\u996d\uff0c\u5c31\u7b97 \u4f60\u8bc6\u7a7f\u6211\u7684\u8eab\u4efd\uff0c\u4f60\u4e5f\u975e\u5f97\u4f9d\u6211\u4e0d\u53ef\uff01\u201d\u539f\u6765\u5c60\u9f99\u867d\u7136\u5df2\u7ecf\u9a97\u5f97\u5218\u743c\u59d1\u7ed9\u4ed6\u5199\u4fe1\uff0c\u4f46\u4ed6\u8fd8\u60f3 \u518d\u8fdb\u4e00\u6b65\uff0c\u8981\u4f7f\u5f97\u5218\u743c\u59d1\u6b7b\u5fc3\u584c\u5730\u5730\u542c\u4ed6\u7684\u8bdd\uff0c\u4e32\u901a\u6765\u9a97\u5979\u7684\u54e5\u54e5\uff0c\u9a97\u53d6\u6709\u5173\u4e49\u519b\u7684\u79d8\u5bc6\u3002 \u3000\u3000\u5218\u743c\u59d1\u559d\u4e86\u4e00\u676f\uff0c\u8bf4\u9053\uff1a\u201c\u73b0\u5728\u8be5\u4f60\u518d\u559d\u4e09\u676f\u4e86\u3002\u201d \u3000\u3000\u5c60\u9f99\u9053\uff1a\u201c\u597d\uff0c\u4e0d\u8fc7\u8fd9\u6b21\u6211\u53ef\u8981\u548c\u4f60\u8bf4\u6e05\u695a\u4e86\uff0c\u6211\u559d\u4e86\u8fd9\u4e09\u676f\uff0c\u4f60\u4e00\u5b9a\u5f97\u5949\u966a\u4e00\u676f\uff01\u201d \u3000\u3000\u5218\u743c\u59d1\u88c5\u4f5c\u5df2\u6709\u4e09\u5206\u9189\u610f\uff0c\u5377\u7740\u820c\u5934\u8bf4\u9053\uff1a\u201c\u4f9d\u4f60\uff0c\u4f9d\u4f60\uff01\u4f46\u4f60\u53ef\u522b\u628a\u6211\u5f04\u9189\u624d\u597d\u3002\u201d \u5c60\u9f99\u7b11\u9053\uff1a\u201c\u8fd9\u9152\u7684\u9152\u6027\u5e73\u548c\uff0c\u559d\u51e0\u676f\u4e0d\u4f1a\u9189\u7684\u3002\u201d\u5fc3\u4e2d\u5374\u5728\u60f3\u9053\uff1a\u201c\u8fd9\u6837\u7684\u70c8\u9152\uff0c\u4f60\u662f\u4e2a \u4e0d\u4f1a\u559d\u9152\u7684\u4eba\uff0c\u559d\u4e0a\u4e09\u676f\uff0c\u975e\u9189\u4e0d\u53ef\uff01\u201d \u3000\u3000\u5c60\u9f99\u54ea\u91cc\u77e5\u9053\uff0c\u5218\u743c\u59d1\u975e\u4f46\u80fd\u591f\u559d\u9152\uff0c\u800c\u4e14\u9152\u91cf\u6bd4\u4ed6\u8fd8\u5927\uff0c\u539f\u6765\u5218\u743c\u59d1\u7684\u7236\u4eb2\u522b\u65e0\u55dc \u597d\uff0c\u5c31\u662f\u6700\u559c\u559d\u9152\uff0c\u5218\u743c\u59d1\u65f6\u5e38\u548c\u5979\u7236\u4eb2\u559d\u9152\uff0c\u65e5\u5b50\u4e45\u4e86\uff0c\u4e5f\u517b\u6210\u4e86\u5979\u4e00\u526f\u8fc7\u4eba\u7684\u9152\u91cf\u3002\u4e0d \u8fc7\u5979\u662f\u53ef\u4ee5\u559d\u4e5f\u53ef\u4ee5\u4e0d\u559d\u7684\uff0c\u7531\u4e8e\u5973\u5b69\u513f\u5bb6\u7684\u77dc\u6301\uff0c\u5979\u4e0d\u613f\u610f\u7ed9\u5c60\u9f99\u8bf4\u5979\u9157\u9152\uff0c\u662f\u4ee5\u81ea\u4ece\u4e0e \u5c60\u9f99\u79c1\u5954\u4e4b\u540e\uff0c\u5979\u5728\u5c60\u9f99\u7684\u9762\u524d\u4ece\u6765\u6ca1\u6709\u559d\u8fc7\u9152\u3002 \u3000\u3000\u4e8c\u4eba\u8f6e\u6d41\u559d\u9152\uff0c\u5218\u743c\u59d1\u559d\u4e86\u4e09\u676f\uff0c\u5c60\u9f99\u8fde\u540c\u6700\u5148\u559d\u7684\u4e09\u676f\uff0c\u5df2\u662f\u559d\u4e86\u4e00\u5171\u5341\u4e8c\u676f\u4e86\uff0c\u90a3 \u4e00\u58f6\u70c8\u9152\uff0c\u4e1a\u5df2\u7ed9\u4ed6\u4eec\u559d\u4e86\u4e00\u534a\u3002 \u3000\u3000\u5218\u743c\u59d1\u6697\u4e2d\u8fd0\u6c14\uff0c\u628a\u8138\u5b54\u903c\u5f97\u901a\u7ea2\uff0c\u88c5\u5f97\u5026\u773c\u60fa\u677e\uff0c\u9189\u6001\u53ef\u63ac\u3002\u5c60\u9f99\u5fc3\u9053\uff1a\u201c\u662f\u65f6\u5019 \u4e86\uff01\u201d\u8bf4\u9053\uff1a\u201c\u743c\u59d1\uff0c\u4f60\u6b47\u6b47\u5427\u3002\u201d\u628a\u8eab\u5b50\u6328\u8fc7\u53bb\u4fbf\u8981\u6276\u5979\u4e0a\u5e8a\u3002 \u3000\u3000\u5218\u743c\u59d1\u53cc\u773c\u4e00\u77aa\uff0c\u820c\u5934\u6253\u5377\uff0c\u55b7\u51fa\u4e00\u53e3\u9152\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u3001\u6211\u8fd8\u6ca1\u6709\u9189\u5462\uff0c\u6211\u3001\u6211\u548c\u4f60 \u559d\u9152\u3002\u600e\u4e48\uff0c\u4f60\u4e0d\u559d\uff1f\u6211\u77e5\u9053\u5566\uff0c\u4f60\u6015\u9189\u4e86\u8bf4\u771f\u8bdd\uff0c\u4f60\u6709\u4e00\u4ef6\u4e8b\u7792\u7740\u6211\uff01\u90a3\u59d3\u891a\u7684\u8bf4\u7684\uff01\u201d \u8bed\u6c14\u548c\u795e\u6001\uff0c\u90fd\u5341\u8db3\u50cf\u662f\u4e2a\u559d\u9189\u4e86\u7684\u4eba\u3002 \u3000\u3000\u5c60\u9f99\u521d\u65f6\u6697\u6697\u597d\u7b11\uff1a\u201c\u559d\u9189\u7684\u4eba\u603b\u4e0d\u80af\u627f\u8ba4\u81ea\u5df1\u662f\u559d\u9189\u7684\u3002\u201d\u5f85\u5230\u542c\u4e86\u5979\u6700\u540e\u7684\u90a3\u4e00\u53e5 \u8bdd\uff0c\u4e0d\u89c9\u5403\u4e86\u4e00\u60ca\uff0c\u8fde\u5fd9\u95ee\u9053\uff1a\u201c\u90a3\u59d3\u891a\u7684\u8bf4\u4e86\u4ec0\u4e48\u8bdd\uff1f\u201d\u5fc3\u9053\uff1a\u201c\u4e0d\u77e5\u5979\u5df2\u77e5\u9053\u4e86\u4ec0\u4e48\u79d8 \u5bc6\uff0c\u5e78\u4e8f\u5979\u9189\u4e86\u81ea\u5df1\u6cc4\u6f0f\u51fa\u6765\u3002\u201d \u3000\u3000\u5218\u743c\u59d1\u9053\uff1a\u201c\u4f60\u8bf4\u6211\u9189\uff0c\u6211\u7f5a\u4f60\u5148\u559d\u4e09\u676f\uff0c\u4e0d\u7136\u6211\u5c31\u4e0d\u548c\u4f60\u8bf4\uff01\u201d \u3000\u3000\u5c60\u9f99\u5176\u5b9e\u4ea6\u5df1\u6709\u4e86\u516d\u4e03\u5206\u9152\u610f\uff0c\u4e3a\u4e86\u8981\u77e5\u9053\u8fd9\u4e2a\u79d8\u5bc6\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u6211\u5c31\u559d\u4e09\u676f\uff01\u201d\u5218 \u743c\u59d1\u5f85\u4ed6\u559d\u4e86\u4e09\u676f\uff0c\u62a2\u7740\u81ea\u5df1\u4e5f\u559d\u4e86\u4e00\u676f\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8fd8\u5f97\u966a\u6211\u4e09\u676f\uff01\u201d\u5c60\u9f99\u9053\uff1a\u201c\u4f60\u4e0d\u8981 \u559d\u4e86\uff0c\u5feb\u8bf4\u5427\uff01\u201d\u5218\u743c\u59d1\u9053\uff1a\u201c\u4f60\u4e0d\u8bb8\u6211\u559d\uff0c\u90a3\u4f60\u5f97\u4e00\u5171\u559d\u516d\u676f\u5f97\u4e86\uff0c\u6211\u5c31\u544a\u8bc9\u4f60\uff01\u201d \u3000\u3000\u5c60\u9f99\u6015\u5979\u9189\u4e86\u4e0d\u4f1a\u8bf4\u8bdd\uff0c\u679c\u7136\u4e00\u53e3\u6c14\u559d\u4e86\u516d\u676f\uff0c\u8fd9\u516d\u676f\u9152\u4e0b\u809a\uff0c\u4ed6\u5df2\u7ecf\u9189\u4e86\u516b\u4e5d\u5206\u4e86\u3002 \u3000\u3000\u5c60\u9f99\u9152\u610f\u4e0a\u6d8c\uff0c\u6d41\u7740\u53e3\u6d8e\uff0c\u9f88\u7259\u9732\u9f7f\u5730\u7b11\u9053\uff1a\u201c\u5c0f\u4e56\u4e56\uff0c\u544a\u8bc9\u6211\u5427\uff01\u201d \u3000\u3000\u5218\u743c\u59d1\u51b7\u51b7\u5730\u76ef\u7740\u4ed6\uff0c\u5ffd\u9053\uff1a\u201c\u674e\u601d\u5357\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u5c60\u9f99\u9189\u4e2d\u8fd8\u6709\u4e24\u5206\u6e05\u9192\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u95ee\u4ed6\u5e72\u561b\uff1f\u201d\u5218\u743c\u59d1\u9053\uff1a\u201c\u4ed6\u662f\u4e0d\u662f\u5173 \u5728\u8fd9\u513f\uff1f\u201d\u5c60\u9f99\u9053\uff1a\u201c\u8c01\u3001\u8c01\u3001\u8c01\u8bf4\u7684\uff1f\u8c01\u8bf4\u7684\uff1f\u201d\u60ca\u60f6\u7684\u795e\u60c5\u5df2\u662f\u63a9\u9970\u4e0d\u4f4f\uff0c\u5218\u743c\u59d1\u4e00 \u770b\uff0c\u5c31\u77e5\u891a\u4e91\u5cf0\u6240\u8bf4\u7684\u4e0d\u662f\u865a\u8a00\u3002 \u3000\u3000\u5218\u743c\u59d1\u9053\uff1a\u201c\u90a3\u59d3\u891a\u7684\u8bf4\u7684\uff01\u201d \u3000\u3000\u5c60\u9f99\u9053\uff1a\u201c\u4ed6\u544a\u8bc9\u4f60\u674e\u601d\u5357\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u5218\u743c\u59d1\u9053\uff1a\u201c\u6211\u82e5\u7136\u77e5\u9053\uff0c\u4f55\u5fc5\u518d\u6765\u95ee\u4f60\uff1f\u90a3\u59d3\u891a\u7684\u4e00\u95ef\u8fdb\u6765\u5c31\u8bf4\u8981\u627e\u674e\u601d\u5357\uff0c\u6211\u8bf4\u6211 \u4e0d\u77e5\u9053\uff0c\u4ed6\u8fd9\u624d\u52a8\u624b\u4f24\u4e86\u6211\u7684\u3002\u201d \u3000\u3000\u5c60\u9f99\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u539f\u6765\u891a\u4e91\u5cf0\u662f\u4e3a\u4e86\u8425\u6551\u674e\u6069\u5357\u6765\u7684\uff0c\u6211\u9053\u4ed6\u600e\u5730\u4f1a\u77e5\u9053\u743c\u59d1\u4e5f\u5728\u8fd9 \u91cc\uff0c\u539f\u6765\u662f\u9002\u9022\u5176\u4f1a\u3002\u597d\u5728\u743c\u59d1\u8fd8\u672a\u77e5\u6653\u674e\u601d\u5357\u7684\u8eab\u4efd\u3002\u201d \u3000\u3000\u5c60\u9f99\u5b9a\u4e86\u5b9a\u795e\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u891a\u4e91\u5cf0\u8fd9\u5c0f\u5b50\u53ef\u9732\u5e95\u4e86\uff01\u201d \u3000\u3000\u5218\u743c\u59d1\u9053\uff1a\u201c\u9732\u4ec0\u4e48\u5e95\uff1f\u201d \u3000\u3000\u5c60\u9f99\u9053\uff1a\u201c\u6211\u4ee5\u524d\u542c\u8bf4\u4ed6\u662f\u79c1\u901a\u91d1\u864f\uff0c\u8fd8\u4e0d\u6562\u5341\u5206\u76f8\u4fe1\uff0c\u5982\u4eca\u4ed6\u8dd1\u6765\u627e\u674e\u601d\u5357\uff0c\u8fd9\u53ef\u5c31 \u662f\u8bc1\u636e\u786e\u51ff\u4e86\uff01\u201d \u3000\u3000\u5218\u743c\u59d1\u9053\uff1a\u201c\u674e\u601d\u5357\u662f\u2014\u2014\u201d \u3000\u3000\u5c60\u9f99\u9053\uff1a\u201c\u674e\u601d\u5357\u662f\u91d1\u56fd\u56fd\u5e08\u5e9c\u7684\u6c49\u4eba\u536b\u58eb\u3002\u201d\u4ed6\u5584\u4e8e\u8bf4\u8c0e\uff0c\u867d\u7136\u662f\u5728\u9189\u4e2d\uff0c\u8c0e\u8a00\u4ea6\u662f \u987a\u53e3\u800c\u51fa\uff0c\u88c5\u5f97\u4f3c\u6a21\u4f3c\u6837\u3001 \u3000\u3000\u5218\u743c\u59d1\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u8fd9\u4e2a\u674e\u601d\u5357\u4f60\u4eec\u786e\u662f\u5df2\u7ecf\u628a\u4ed6\u64d2\u72ed\u4e86\u4e48\uff1f\u201d \u3000\u3000\u5c60\u9f99\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u7ed9\u91d1\u5ef7\u4f5c\u5bc6\u63a2\uff0c\u524d\u51e0\u5929\u5c45\u7136\u80c6\u6562\u5230\u8fd9\u91cc\u4fa6\u67e5\uff0c\u662f\u7ed9\u6211\u7684\u670b\u53cb\u5c06\u4ed6\u62ff\u4e0b \u4e86\u3002\u201d \u3000\u3000\u5218\u743c\u59d1\u9053\uff1a\u201c\u4ed6\u5173\u5728\u54ea\u513f\uff0c\u4f60\u5e26\u6211\u53bb\u770b\u770b\u597d\u4e0d\u597d\uff0c\u54ce\uff0c\u4f60\u4e0d\u80af\u7b54\u5e94\uff1f\u4f60\u4e0d\u662f\u771f\u5fc3\u75bc \u6211\uff01\u201d\u88c5\u51fa\u4e00\u526f\u559d\u9189\u4e86\u9152\u6492\u5a07\u7684\u795e\u6c14\u3002 \u3000\u3000\u5c60\u9f99\u9053\uff1a\u201c\u597d\uff0c\u660e\u5929\u5e26\u4f60\u53bb\u770b\u3002\u5c0f\u4e56\u4e56\u51fa\u522b\u5435\uff0c\u4f60\u522b\u95f9\uff0c\u4e56\u4e56\u7684\u7761\u4e00\u89c9\uff01\u201d\u5fc3\u91cc\u5219\u5728\u60f3 \u9053\uff1a\u201c\u660e\u5929\u968f\u4fbf\u627e\u4e00\u4e2a\u4eba\u5192\u5145\u662f\u674e\u601d\u5357\u7ed9\u5979\u770b\u5c31\u662f\u3002\u4e5f\u8bf4\u4e0d\u5b9a\u660e\u5929\u5979\u8d77\u4e86\u5e8a\u5df2\u7ecf\u5fd8\u8bb0\u8fd9\u4ef6\u4e8b \u7684\u3002\u201d \u3000\u3000\u5218\u743c\u59d1\u6253\u4e86\u4e2a\u5475\u6b20\uff0c\u88c5\u4f5c\u9189\u6781\u5026\u6781\u7684\u795e\u6c14\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u597d\uff0c\u4f60\u8d70\uff0c\u4f60\u8d70\u5440\uff01\u6211\uff0c\u6211\u53ef\u771f \u662f\u8981\u7761\u4e86\u3002\u201d \u3000\u3000\u5c60\u9f99\u6d8e\u7740\u8138\u9053\uff1a\u201c\u6211\u670d\u4f8d\u4f60\u7761\u3002\u201d\u6b63\u8981\u6276\u5218\u743c\u59d1\u4e0a\u5e8a\uff0c\u4e0d\u6599\u5ffd\u89c9\u80c1\u4e0b\u4e00\u9ebb\uff0c\u5df2\u662f\u7ed9\u5218\u743c \u59d1\u70b9\u4e86\u7a74\u9053\uff01 \u3000\u3000\u672c\u6765\u5218\u743c\u59d1\u7684\u672c\u9886\u8fdc\u8fdc\u4e0d\u53ca\u5c60\u9f99\uff0c\u82e5\u4e0d\u662f\u4ed6\u559d\u9189\u4e86\u9152\uff0c\u5218\u743c\u59d1\u662f\u51b3\u8ba1\u4e0d\u80fd\u5f97\u624b\u7684\u3002 \u3000\u3000\u5c31\u5728\u5218\u743c\u59d1\u70b9\u4e86\u4ed6\u7684\u7a74\u9053\u8fd9\u4e00\u5239\u90a3\uff0c\u5ffd\u5730\u597d\u50cf\u542c\u5230\u7a97\u5916\u4f3c\u4e4e\u6709\u4eba\u8f7b\u8f7b\u7684\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff01 \u3000\u3000\u5218\u743c\u59d1\u5927\u5403\u4e00\u60ca\uff0c\u8fde\u5fd9\u62d4\u51fa\u5200\u6765\uff0c\u559d\u9053\uff1a\u201c\u662f\u8c01\uff1f\u201d\u51b2\u51fa\u53bb\u770b\uff0c\u53ea\u89c1\u4e00\u52fe\u6b8b\u6708\uff0c\u51e0\u70b9\u758f \u661f\uff0c\u54ea\u91cc\u6709\u4eba\u7684\u5f71\u5b50\uff1f \u3000\u3000\u5218\u743c\u59d1\u5fc3\u9053\uff1a\u201c\u6216\u8bb8\u662f\u6211\u591a\u7591\u4e86\uff1f\u4e0d\u7ba1\u5982\u4f55\uff0c\u4f7f\u662f\u5df2\u7ecf\u7ed9\u4eba\u53d1\u73b0\uff0c\u4e8b\u5df2\u5982\u65af\uff0c\u8fd9\u4e2a\u9669\u6211 \u4e5f\u662f\u975e\u5192\u4e0d\u53ef\uff01\u201d \u3000\u3000\u5c60\u9f99\u7ed9\u5979\u70b9\u4e86\u8f6f\u9ebb\u7a74\uff0c\u6c14\u529b\u5df2\u662f\u4f7f\u4e0d\u51fa\u6765\uff0c\u4f46\u8fd8\u80fd\u591f\u8bf4\u8bdd\u3002\u5218\u743c\u59d1\u56de\u5230\u623f\u4e2d\uff0c\u5c60\u9f99\u98a4\u58f0 \u8bf4\u9053\uff1a\u201c\u743c\u59d1\uff0c\u4f60\u8fd9\u662f\u5f00\u4ec0\u4e48\u73a9\u7b11\uff1f\u201d \u3000\u3000\u5218\u743c\u59d1\u674f\u773c\u5706\u7741\uff0c\u67f3\u7709\u5012\u7ad6\uff0c\u5c16\u5200\u6307\u7740\u4ed6\u7684\u80f8\u53e3\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u8c01\u548c\u4f60\u5f00\u73a9\u7b11\uff0c\u5e26\u6211\u53bb \u89c1\u674e\u601d\u5357\uff01\u201d \u3000\u3000\u5c60\u9f99\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u9152\u610f\u767b\u65f6\u9192\u4e86\u51e0\u5206\uff0c\u8bb7\u8bb7\u8bf4\u9053\uff1a\u201c\u4f60\u3001\u4f60\u2026\u2026\u201d \u3000\u3000\u5218\u743c\u59d1\u9053\uff1a\u201c\u4f60\u7741\u5f00\u4f60\u7684\u72d7\u773c\u770b\u6e05\u695a\u6211\u5427\uff01\u4f60\u4ee5\u4e3a\u6211\u662f\u4e00\u4e2a\u53ef\u4ee5\u4efb\u51ed\u4f60\u6b3a\u8d1f\u7684\u65e0\u77e5\u6751\u5973 \u5417\uff1f\u6211\u662f\u6297\u91d1\u8001\u82f1\u96c4\u5218\u701a\u7ae0\u7684\u5973\u513f\uff0c\u6211\u662f\u5ff5\u5728\u5f80\u65e5\u60c5\u5206\uff0c\u6211\u65e9\u5df2\u4e00\u5200\u5c06\u4f60\u6740\u4e86\uff01\u201d\u8bf4\u5230\u201c\u60c5 \u5206\u201d\u4e8c\u5b57\uff0c\u5979\u5fc3\u4e2d\u4e00\u9635\u9178\u75db\uff0c\u60f3\u9053\uff1a\u201c\u4f60\u5bf9\u6211\u8fd8\u6709\u4ec0\u4e48\u60c5\u5206\u53ef\u8a00\uff0c\u4f60\u4e0d\u8fc7\u662f\u60f3\u5229\u7528\u6211\u6765\u9677\u5bb3 \u6211\u7684\u54e5\u54e5\u800c\u5df2\u3002\u201d\u4f46\u5979\u4e3a\u4e86\u6050\u6015\u5c60\u9f99\u62fc\u6b7b\u4e0d\u80af\u4f9d\u4ece\uff0c\u53e3\u5934\u4e0a\u8fd8\u662f\u4e0d\u80fd\u8bf4\u5f97\u592a\u8fc7\u51b3\u7edd\u3002\u5fc3\u4e2d\u6697 \u6697\u6253\u5b9a\u4e3b\u610f\uff0c\u53ea\u8981\u80fd\u591f\u628a\u674e\u601d\u5357\u6551\u4e86\u51fa\u6765\uff0c\u5979\u5c31\u4e0e\u5c60\u9f99\u540c\u5f52\u4e8e\u5c3d\u3002 \u3000\u3000\u5c60\u9f99\u8bf4\u9053\uff1a\u201c\u743c\u59d1\uff0c\u4f60\u65e2\u7136\u77e5\u9053\u8fd9\u91cc\u662f\u56fd\u5e08\u5e9c\uff0c\u90a3\u4f60\u600e\u4e0d\u60f3\u60f3\uff0c\u6211\u82e5\u5e26\u4f60\u53bb\u89c1\u674e\u601d\u5357\uff0c \u90a3\u4e9b\u770b\u5b88\u4e0d\u4f1a\u76d8\u95ee\u6211\u5417\uff1f\u6050\u6015\u53ea\u8981\u8de8\u51fa\u8fd9\u4e2a\u9662\u5b50\uff0c\u5c31\u4f1a\u6709\u4eba\u8981\u6765\u67e5\u95ee\u4e86\u3002\u201d \u3000\u3000\u5218\u743c\u59d1\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u662f\u56fd\u5e08\u5e9c\u4e2d\u7684\u8d35\u5ba2\uff0c\u4f60\u4e00\u5411\u81ea\u8d1f\u806a\u660e\uff0c\u4ee5\u4f60\u7684\u806a\u660e\uff0c\u4f60\u8fd8\u4e0d\u4f1a\u5e94 \u4ed8\u5417\uff1f\u201d \u3000\u3000\u5c60\u9f99\u8bf4\u9053\uff1a\u201c\u6b64\u4e8b\u975e\u540c\u5c0f\u53ef\uff0c\u5371\u9669\u4e07\u5206\uff0c\u6211\u3001\u6211\u2014\u2014\u201d \u3000\u3000\u5218\u743c\u59d1\u9053\uff1a\u201c\u4f60\u60f3\u4e0d\u51fa\u529e\u6cd5\uff1f\u597d\uff0c\u90a3\u6211\u5c31\u4e0e\u4f60\u4e00\u540c\u6b7b\u5427\uff01\u201d\u5200\u950b\u8f7b\u8f7b\u4e00\u5212\uff0c\u523a\u5f97\u5c60\u9f99\u7684 \u80f8\u53e3\u9690\u9690\u4f5c\u75db\u3002 \u3000\u3000\u5c60\u9f99\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u5979\u7684\u6027\u5b50\u5982\u6b64\u521a\u70c8\uff0c\u82e5\u4e0d\u4f9d\u5979\uff0c\u53ea\u6015\u5979\u5f53\u771f\u5c31\u8981\u4e86\u6211\u7684\u6027\u547d\uff01 \u5509\uff0c\u4e5f\u53ea\u597d\u89c1\u4e00\u6b65\u8d70\u4e00\u6b65\u4e86\u3002\u201d \u3000\u3000\u5c60\u9f99\u6697\u81ea\u76d8\u7b97\u8131\u8eab\u4e4b\u8ba1\uff0c\u53e3\u4e2d\u5374\u5728\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u743c\u59d1\uff0c\u4e3a\u4e86\u4f60\uff0c\u6211\u7518\u613f\u820d\u4e86\u6027\u547d\u3002\u4e0d \u8fc7\uff0c\u4f60\u8981\u6211\u5e26\u4f60\u53bb\uff0c\u4f60\u4e5f\u603b\u5f97\u89e3\u5f00\u6211\u7684\u7a74\u9053\u624d\u884c\u3002\u201d \u3000\u3000\u5218\u743c\u59d1\u5728\u4ed6\u819d\u76d6\u7684\u201c\u73af\u8df3\u7a74\u201d\u4e00\u62cd\uff0c\u8ddf\u7740\u5374\u7528\u91cd\u624b\u6cd5\u5728\u4ed6\u5c0f\u8179\u7684\u201c\u6108\u6c14\u7a74\u201d\u4e00\u6233\uff0c\u8bf4 \u9053\uff1a\u201c\u597d\uff0c\u4f60\u73b0\u5728\u53ef\u4ee5\u8d70\u8def\u4e86\uff0c\u53bb\u5427\uff01\u201d\u5c60\u9f99\u82e6\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u5c31\u7b97\u662f\u89e3\u4e86\u6211\u7684\u7a74\u9053\u4e48\uff1f\u201d\u5218 \u743c\u59d1\u9053\uff1a\u201c\u4f60\u8be1\u8ba1\u591a\u7aef\uff0c\u6211\u4e0d\u80fd\u4e0d\u7a0d\u52a0\u9632\u5907\uff0c\u89c1\u4e86\u674e\u601d\u5357\uff0c\u6211\u518d\u66ff\u4f60\u90fd\u89e3\u5f00\u5c01\u95ed\u4e86\u7684\u7a74 \u9053\u3002\u201d \u3000\u3000\u539f\u6765\u5979\u53ea\u662f\u89e3\u5f00\u4e86\u5c60\u9f99\u7684\u9ebb\u7a74\u5374\u5728\u4ed6\u201c\u6108\u6c14\u7a74\u201d\u4e0a\u52a0\u4e0a\u91cd\u624b\u6cd5\u7684\u4e00\u6233\uff0c\u5374\u662f\u6bd4\u70b9\u4e86\u4ed6\u7684 \u9ebb\u7a74\u8fd8\u66f4\u5229\u5bb3\u3002\u5c60\u9f99\u867d\u7136\u53ef\u4ee5\u884c\u52a8\uff0c\u771f\u529b\u5374\u662f\u4f7f\u4e0d\u51fa\u6765\uff0c\u6bd4\u4e00\u4e2a\u666e\u901a\u4eba\u8fd8\u6bd4\u4e0d\u4e0a\u4e86\u3002\u8fd9\u79cd\u80fd \u4ee4\u5bf9\u65b9\u6d88\u5931\u771f\u529b\u7684\u70b9\u7a74\u529f\u592b\uff0c\u4e43\u662f\u5218\u5bb6\u7684\u72ec\u95e8\u624b\u6cd5\u3002 \u3000\u3000\u5218\u743c\u59d1\u8fd8\u4e0d\u653e\u5fc3\uff0c\u4e0e\u4ed6\u624b\u633d\u7740\u624b\uff0c\u5e76\u80a9\u8d70\u51fa\u9662\u5b50\uff0c\u5728\u4ed6\u8033\u8fb9\u8f7b\u58f0\u8bf4\u9053\u4e86\u4f60\u53ef\u522b\u60f3\u4ec0\u4e48\u574f \u4e3b\u610f\uff0c\u6211\u4e00\u53d1\u89c9\u4e0d\u5bf9\uff0c\u5c31\u634f\u788e\u4f60\u7684\u7ecf\u8109\u3002\u201d\u5979\u7684\u4e09\u4e2a\u6307\u5934\uff0c\u6b63\u662f\u6263\u7740\u5c60\u9f99\u7684\u8109\u95e8\u3002 \u3000\u3000\u5c60\u9f99\u82e6\u7b11\u9053\uff1a\u201c\u743c\u59d1\uff0c\u4f60\u4e5f\u5fd2\u591a\u7591\u4e86\u3002\u4e0d\u7ba1\u6211\u600e\u6837\u574f\uff0c\u6211\u5bf9\u4f60\u603b\u662f\u597d\u7684\u554a\uff01\u597d\uff0c\u8fd9\u5c31\u8d70 \u5427\u3002\u201d\u53e3\u5934\u8ba8\u597d\u743c\u59d1\uff0c\u5fc3\u4e2d\u5374\u5728\u6697\u6697\u5492\u9a82\u3002 \u3000\u3000\u8d70\u51fa\u4e86\u8fd9\u5ea7\u9662\u5b50\uff0c\u679c\u7136\u5c31\u6709\u4e24\u4e2a\u536b\u571f\u8d70\u6765\u76d8\u95ee\u3002\u8fd9\u4e24\u4e2a\u4eba\u6b63\u662f\u76d1\u89c6\u4ed6\u4eec\u7684\u3002 \u3000\u3000\u90a3\u4e24\u4eba\u9053\uff1a\u201c\u54a6\uff0c\u5c60\u76f8\u516c\uff0c\u8fd9\u4e48\u665a\u4e86\uff1f\u4f60\u8fd8\u548c\u5218\u59d1\u5a18\u5230\u54ea\u91cc\u53bb\uff1f\u201d\u4ed6\u4eec\u77e5\u9053\u9633\u5929\u96f7\u662f\u4e0d \u8bb8\u5218\u743c\u59d1\u8d70\u8fdb\u5185\u9662\u7684\u3002 \u3000\u3000\u5218\u743c\u59d1\u5728\u4ed6\u7684\u8109\u95e8\u8f7b\u8f7b\u634f\u4e86\u4e00\u4e0b\uff0c\u5c60\u9f99\u65e9\u5df2\u60f3\u597d\u8bf4\u8bdd\uff0c\u7b11\u9053\uff1a\u201c\u6211\u548c\u5218\u59d1\u5a18\u53bb\u89c1\u4f60\u4eec\u7684 \u56fd\u5e08\u7237\u5440\uff01\u201d\u90a3\u4e24\u4e2a\u536b\u58eb\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e2a\u2014\u2014\u201d\u5c60\u9f99\u9053\uff1a\u201c\u4f60\u4eec\u4e0d\u5fc5\u60ca\u7591\uff0c\u5218\u59d1\u5a18 \u73b0\u5728\u662f\u54b1\u4eec\u7684\u4eba\u4e86\u3002\u6211\u662f\u966a\u5979\u53bb\u548c\u9633\u56fd\u5e08\u5546\u91cf\u8981\u4e8b\u7684\u3002\u201d\u5218\u743c\u59d1\u63a5\u53e3\u8bf4\u9053\uff1a\u201c\u662f\u5440\uff0c\u4ed6\u7684\u4e8b \u60c5\u90fd\u5df1\u544a\u8bc9\u6211\u4e86\u3002\u6211\u3001\u6211\u662f\u2026\u2026\u201d\u8bf4\u81f3\u6b64\u5904\uff0c\u88c5\u4f5c\u5bb3\u7f9e\u7684\u6837\u5b50\uff0c\u7c89\u8138\u98de\u7ea2\u3002 \u3000\u3000\u90a3\u4e24\u4e2a\u536b\u58eb\u76f8\u89c6\u800c\u7b11\uff0c\u4e00\u4e2a\u8bf4\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u5982\u6b64\u3002\u4f60\u662f\u5ac1\u9e21\u968f\u9e21\uff0c\u5ac1\u72ac\u968f\u72ac\u3002\u201d\u53e6\u4e00 \u4e2a\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u8bdd\u53ef\u8bf4\u5f97\u4e0d\u5bf9\u4e86\uff0c\u5979\u662f\u5ac1\u864e\u968f\u864e\uff0c\u5ac1\u9f99\u968f\u9f99\u3002\u201d\u5c60\u9f99\u540d\u5b57\u6709\u4e00\u4e2a\u201c\u9f99\u201d\u5b57\uff0c \u90a3\u4e2a\u536b\u58eb\u81ea\u4ee5\u4e3a\u8bf4\u5f97\u5f88\u591f\u98ce\u8da3\uff0c\u54c8\u54c8\u4e00\u7b11\uff0c\u4fbf\u653e\u4ed6\u4eec\u8fc7\u53bb\u3002 \u3000\u3000\u5c60\u9f99\u5fc3\u4e2d\u6697\u9a82\uff1a\u201c\u8822\u6750\uff0c\u8822\u6750\uff01\u201d\u539f\u6765\u4ed6\u5728\u521a\u624d\u8bf4\u8bdd\u4e4b\u65f6\uff0c\u5df2\u7ecf\u4f7f\u4e86\u4e00\u4e2a\u773c\u8272\uff0c\u53ef\u662f\u90a3 \u4e24\u4e2a\u536b\u58eb\u5374\u662f\u4e0d\u80fd\u4f1a\u610f\u3002\u5c60\u9f99\u5e0c\u671b\u4ed6\u4eec\u80fd\u542c\u51fa\u8bdd\u4e2d\u7684\u7834\u7efd\uff0c\u8fd9\u4e2a\u5e0c\u671b\u73b0\u5728\u4e5f\u7834\u706d\u4e86\u3002 \u3000\u3000\u5c60\u9f99\u4e00\u9762\u8d70\u4e00\u9762\u76d8\u7b97\uff0c\u5ffd\u5730\u53c8\u5f97\u4e86\u4e00\u4e2a\u4e3b\u610f\uff0c\u60f3\u9053\uff1a\u201c\u6211\u4f55\u4e0d\u771f\u7684\u5e26\u5979\u53bb\u89c1\u9633\u5929\u96f7\u3002\u9633 \u5929\u96f7\u6b66\u529f\u5353\u7edd\uff0c\u4eba\u53c8\u673a\u7075\uff0c\u4e00\u89c1\u4e0d\u5bf9\uff0c\u5b9a\u6709\u529e\u6cd5\u6551\u6211\uff01\u201d \u3000\u3000\u9633\u5929\u96f7\u7684\u4f4f\u5904\u4e0e\u674e\u601d\u5357\u88ab\u56da\u7684\u5904\u6240\u65b9\u5411\u4e0d\u540c\uff0c\u4e00\u4e2a\u5728\u4e1c\uff0c\u4e00\u4e2a\u5728\u897f\uff0c\u5c60\u9f99\u521a\u5411\u4e1c\u9762\u8d70\u4e86 \u51e0\u6b65\uff0c\u5ffd\u5730\u4e00\u5757\u77f3\u5934\u98de\u6765\uff0c\u51e0\u4e4e\u662f\u64e6\u7740\u5c60\u9f99\u7684\u989d\u89d2\u98de\u8fc7\uff01 \u3000\u3000\u5c60\u9f99\u6b64\u65f6\u5df2\u662f\u6ca1\u6709\u62b5\u6321\u6697\u5668\u4e4b\u80fd\uff0c\u4f46\u6b66\u5b66\u7684\u89c1\u8bc6\u5374\u672a\u6d88\u5931\uff0c\u4e00\u542c\u8fd9\u679a\u77f3\u5b50\u7684\u7834\u7a7a\u4e4b\u58f0\uff0c \u4fbf\u77e5\u8db3\u4ee5\u6d1e\u7a7f\u4ed6\u7684\u8111\u888b\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6b64\u4eba\u7684\u6697\u5668\u529f\u592b\u5982\u6b64\u9ad8\u660e\uff0c\u82e5\u662f\u6709\u5fc3\u53d6\u6211\u6027\u547d\uff0c\u51b3\u4e0d \u4f1a\u5931\u51c6\u5934\uff0c\u53ea\u662f\u5728\u989d\u8fb9\u98de\u8fc7\uff01\u201d\u4e00\u60ca\u4e4b\u4e0b\uff0c\u7acb\u5373\u53eb\u9053\uff1a\u201c\u76df\u8c0a\u6c38\u56fa\u3002\u201d\u201c\u76df\u8c0a\u6c38\u56fa\uff0c\u5171\u4eab\u8363 \u534e\u201d\u4e43\u662f\u201c\u56fd\u5e08\u5e9c\u201d\u4eca\u665a\u6240\u53d1\u7684\u6697\u53f7\uff0c\u5018\u82e5\u662f\u81ea\u5df1\u4eba\u78b0\u4e0a\u4e86\uff0c\u4e00\u4e2a\u53eb\u201c\u76df\u8c0a\u6c38\u56fa\u201d\uff0c\u53e6\u4e00\u4e2a \u5c31\u8be5\u63a5\u7740\u53eb\u201c\u5171\u4eab\u8363\u534e\u201d\u3002\u5c60\u9f99\u4ee5\u4e3a\u53d1\u6697\u5668\u7684\u4eba\u662f\u5e9c\u4e2d\u9ad8\u624b\uff0c\u53ea\u6050\u4ed6\u5728\u9ed1\u6697\u4e2d\u8ba4\u4e0d\u51fa\u81ea\u5df1\uff0c \u6545\u800c\u8fde\u5fd9\u8bf4\u51fa\u6697\u53f7\u3002 \u3000\u3000\u4e0d\u6599\u5bf9\u65b9\u7adf\u7136\u6ca1\u6709\u7b54\u8bdd\uff0c\u53ea\u542c\u5f97\u201c\u55e4\u201d\u4e00\u58f0\uff0c\u53c8\u662f\u4e00\u679a\u77f3\u5b50\u6253\u6765\uff0c\u4f46\u8fd9\u6b21\u77f3\u5b50\u6240\u6253\u7684\u65b9 \u5411\u5374\u662f\u548c\u4e0a\u6b21\u76f8\u53cd\uff0c\u5728\u4ed6\u8eab\u65c1\u4e08\u8bb8\u4e4b\u5916\uff0c\u5411\u897f\u65b9\u98de\u53bb\u3002\u90a3\u4e2a\u65b9\u5411\uff0c\u6b63\u662f\u6307\u7740\u674e\u601d\u5357\u88ab\u56da\u7684\u5904 \u6240\uff01 \u3000\u3000\u5c60\u9f99\u5927\u5403\u4e00\u60ca\uff0c\u4ee5\u4ed6\u7684\u806a\u660e\u673a\u8b66\uff0c\u81ea\u662f\u731c\u5230\u4e86\u51e0\u5206\uff0c\u5fc3\u91cc\u6697\u53eb\u4e0d\u5999\u3002\u8981\u77e5\u5bf9\u65b9\u4e0d\u7b54\u6697 \u53f7\uff0c\u5374\u628a\u77f3\u5b50\u6307\u5411\u90a3\u4e2a\u65b9\u5411\uff0c\u4e0d\u7528\u8bf4\u5f53\u7136\u4e0d\u662f\u5e9c\u4e2d\u7684\u4f8d\u536b\u4e86\u3002 \u3000\u3000\u5c60\u9f99\u6c89\u58f0\u9053\uff1a\u201c\u662f\u54ea\u4f4d\u670b\u53cb\u548c\u6211\u5f00\u8fd9\u73a9\u7b11\uff1f\u201d\u5218\u743c\u59d1\u4e0d\u77e5\u662f\u5e2e\u5979\uff0c\u66f4\u4e3a\u60ca\u8be7\u3002\u624b\u91cc\u52a0\u4e00 \u628a\u52b2\uff0c\u634f\u7740\u5c60\u9f99\u7684\u8109\u95e8\uff0c\u5728\u4ed6\u8033\u8fb9\u4f4e\u58f0\u8b66\u544a\uff1a\u201c\u4e0d\u8bb8\u4e71\u8bf4\u4e71\u52a8\uff01\u201d \u3000\u3000\u5ffd\u89c1\u4e00\u4e2a\u5c11\u5973\u4ece\u82b1\u6811\u4e1b\u4e2d\u94bb\u4e86\u51fa\u6765\uff0c\u5c60\u9f99\u548c\u5218\u743c\u59d1\u90fd\u662f\u4e0d\u7981\u6014\u4e86\u4e00\u5f81\uff0c\u4e0d\u7ea6\u800c\u540c\u4ed6\u8bf4 \u9053\uff1a\u201c\u54a6\uff0c\u600e\u4e48\u662f\u4f60\uff1f\u201d\u539f\u6765\u8fd9\u4e2a\u5c11\u5973\u6b63\u662f\u670d\u4f8d\u5218\u743c\u59d1\u7684\u90a3\u4e2a\u5c0f\u4e2b\u5934\u767d\u7389\u513f\u3002 \u3000\u3000\u767d\u7389\u513f\u9053\uff1a\u201c\u5218\u59d1\u5a18\uff0c\u6709\u4e2a\u4eba\u53eb\u6211\u544a\u8bc9\u4f60\uff0c\u4f60\u6240\u8981\u53bb\u7684\u5730\u65b9\u662f\u6c34\u6708\u6d1e\uff0c\u5c60\u76f8\u516c\u77e5\u9053\u8fd9\u4e2a \u5730\u65b9\u7684\uff0c\u4f46\u4e0d\u77e5\u4ed6\u662f\u6709\u610f\u8fd8\u662f\u65e0\u610f\uff0c\u4ed6\u73b0\u5728\u5e26\u4f60\u8d70\u7684\u8fd9\u6761\u8def\uff0c\u5374\u662f\u8d70\u5f97\u4e0d\u5bf9\uff01\u201d \u3000\u3000\u5c60\u9f99\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u8bf4\u9053\uff1a\u201c\u767d\u7389\u513f\uff0c\u4f60\u3001\u4f60\u662f\u4ec0\u4e48\u4eba\uff1f\u90a3\u3001\u90a3\u4e2a\u4eba\u53c8\u662f\u2014\u2014\u201d \u3000\u3000\u767d\u7389\u513f\u4e0d\u5f85\u4ed6\u628a\u8bdd\u8bf4\u5b8c\uff0c\u54fc\u4e86\u4e00\u58f0\uff0c\u4fbf\u5373\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u522b\u7ba1\u6211\u662f\u4ec0\u4e48\u4eba\uff0c\u4f60\u4e5f\u4f11\u60f3\u5728 \u6211\u53e3\u4e2d\u5957\u51fa\u4ec0\u4e48\u3002\u6211\u53ea\u628a\u90a3\u4eba\u7684\u8bdd\u8f6c\u544a\u4f60\uff0c\u4ed6\u662f\u5728\u6697\u4e2d\u76d1\u89c6\u7684\uff0c\u529d\u4f60\u522b\u5f04\u82b1\u62db\u4e86\u3002\u4f60\u82e5\u7136\u518d \u6b21\u8d70\u9519\u8def\uff0c\u5c0f\u5fc3\u4f60\u7684\u8111\u888b\uff01\u201d \u3000\u3000\u5c60\u9f99\u5fd9\u4e0d\u8fed\u5730\u7b54\u9053\uff1a\u201c\u4e0d\u6562\uff0c\u4e0d\u6562\uff01\u201d\u53e3\u5fc3\u4e2d\u6697\u6697\u5492\u9a82\uff1a\u201c\u5f85\u6211\u56de\u6765\uff0c\u4e0d\u63ed\u4e86\u4f60\u4e00\u5c42\u76ae \u624d\u602a\uff01\u201d\u6b64\u65f6\u8ddd\u79bb\u4ed6\u7684\u7a74\u9053\u88ab\u70b9\uff0c\u5df2\u662f\u6709\u4e86\u534a\u6ce8\u9999\u65f6\u523b\u3002\u5c60\u9f99\u7684\u5185\u529f\u9020\u8be3\u8fdc\u6bd4\u5218\u743c\u59d1\u4e3a\u9ad8\uff0c \u88ab\u5c01\u7684\u7a74\u9053\uff0c\u5df2\u662f\u6ca1\u6709\u521d\u65f6\u90a3\u6837\u6c89\u91cd\u7684\u611f\u89c9\uff0c\u4ed6\u6697\u4e2d\u8fd0\u6c14\uff0c\u628a\u771f\u6c14\u4e00\u70b9\u4e00\u6ef4\u7684\u51dd\u805a\u8d77\u6765\u3002\u53ef \u662f\u4ed6\u56e0\u4e3a\u8981\u8d70\u8def\uff0c\u60f3\u8981\u81ea\u5df1\u8fd0\u6c14\u51b2\u5173\uff0c\u89e3\u5f00\u7a74\u9053\uff0c\u4e00\u65f6\u95f4\u5374\u4e5f\u8fd8\u672a\u80fd\u591f\u3002\u4ed6\u4e00\u9762\u6697\u4e2d\u8fd0\u6c14\uff0c \u4e00\u9762\u76d8\u7b97\u5176\u4ed6\u8131\u8eab\u7684\u65b9\u6cd5\u3002 \u3000\u3000\u767d\u7389\u513f\u8eb2\u8fdb\u82b1\u6811\u4e1b\u4e2d\uff0c\u770b\u89c1\u5c60\u9f99\u679c\u7136\u4e0d\u6562\u518d\u800d\u82b1\u62db\uff0c\u5411\u7740\u6c34\u6708\u6d1e\u90a3\u8fb9\u8d70\u53bb\uff0c\u8fd9\u624d\u56de\u8fc7\u5934 \u6765\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u97e9\u53d4\u53d4\uff0c\u6211\u53ef\u4ee5\u56de\u5bb6\u4e86\u4e48\uff1f\u201d \u3000\u3000\u4e00\u68f5\u8001\u69d0\u6811\u4e0a\u8df3\u4e0b\u4e00\u4e2a\u4eba\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u80fd\u56de\u5bb6\u4e86\uff0c\u8d81\u7740\u5929\u521a\u4eae\uff0c\u4f60\u8d76\u5feb\u9003\u8d70\u5427\uff01\u6211 \u5df2\u7ecf\u901a\u77e5\u4e86\u4f60\u7684\u7239\u7239\u5728\u4e1c\u9662\u7684\u540e\u95e8\u7b49\u4f60\u3002\u5b88\u95e8\u7684\u90a3\u4e2a\u4eba\u4e0e\u6211\u79c1\u4ea4\u751a\u597d\uff0c\u6211\u8fd9\u9762\u91d1\u724c\u7ed9\u4f60\uff0c\u4ed6 \u82e5\u662f\u95ee\u8d77\uff0c\u4f60\u5c31\u8bf4\u6211\u5dee\u9063\u4f60\u4eec\u51fa\u53bb\u529e\u4e8b\u7684\u3002\u201d\u8fd9\u4e2a\u4eba\u6b63\u662f\u4e3e\u547d\u76d1\u89c6\u5c60\u9f99\u4e0e\u5218\u743c\u59d1\u7684\u90a3\u4e2a\u6c49\u4eba \u536b\u58eb\u97e9\u8d85\u3002 \u3000\u3000\u767d\u7389\u513f\u9053\uff1a\u201c\u97e9\u53d4\u53d4\uff0c\u4f60\u7684\u91d1\u724c\u7ed9\u4e86\u6211\uff0c\u90a3\u4f60\u600e\u529e\uff1f\u201d\u5979\u867d\u7136\u5e74\u7eaa\u5c0f\uff0c\u4e5f\u77e5\u9053\u8fd9\u4e2a\u529e\u6cd5 \u53ea\u80fd\u7792\u8fc7\u53bb\uff0c\u8ffd\u7a76\u8d77\u6765\uff0c\u97e9\u8d85\u7684\u79d8\u5bc6\u7ec8\u8981\u88ab\u63ed\u7a7f\u3002\u770b\u95e8\u90a3\u4eba\u548c\u4ed6\u7eb5\u6709\u79c1\u4ea4\uff0c\u4e5f\u662f\u51b3\u8ba1\u4e0d\u6562\u5305 \u5e87\u4ed6\u7684\u3002 \u3000\u3000\u97e9\u8d85\u9053\uff1a\u201c\u6211\u81ea\u6709\u529e\u6cd5\u5e94\u4ed8\uff0c\u4f60\u4e0d\u5fc5\u591a\u7ba1\uff01\u201d\u5c0f\u7389\u513f\u9053\uff1a\u201c\u4f60\u8fd9\u8bdd\u5f53\u771f\uff1f\u201d\u97e9\u8d85\u6025\u9053\uff1a \u201c\u4f60\u51e0\u65f6\u89c1\u6211\u8bf4\u8fc7\u5047\u8bdd\uff1f\u5c0f\u7389\u513f\uff0c\u5feb\u8d70\u5427\uff0c\u522b\u7f57\u5506\u4e86\uff0c\u518d\u8fdf\u4f60\u5c31\u8dd1\u4e0d\u4e86\u5566\uff01\u6211\u4e5f\u8fd8\u8981\u53bb\u6697\u4e2d \u5e2e\u5fd9\u90a3\u8fb9\u5218\u59d1\u5a18\u5462\uff01\u201d \u3000\u3000\u539f\u6765\u97e9\u8d85\u4e5f\u662f\u50cf\u891a\u4e91\u5cf0\u4e00\u6837\uff0c\u4e43\u662f\u4e2a\u6297\u91d1\u7684\u5fd7\u58eb\uff0c\u4e3a\u4e86\u8981\u6253\u8fdb\u654c\u4eba\u7684\u5de2\u4eba\uff0c\u8fd9\u624d\u5fcd\u8fb1\u8d1f \u91cd\uff0c\u5047\u88c5\u6548\u5fe0\u9633\u5929\u96f7\u7684\u3002\u4e0d\u8fc7\u4ed6\u53ea\u662f\u4e2a\u4eba\u884c\u4e3a\uff0c\u8fd8\u6ca1\u6709\u548c\u4e49\u519b\u53d6\u5f97\u8054\u7edc\uff0c\u5728\u201c\u56fd\u5e08\u5e9c\u201d\u7684\u8fd9 \u51e0\u5e74\uff0c\u4ed6\u4e5f\u672a\u6562\u5411\u4efb\u4f55\u4eba\u5410\u9732\u4ed6\u7684\u5fc3\u4e8b\uff0c\u5305\u62ec\u891a\u4e91\u5cf0\u5728\u5185\uff0c\u867d\u7136\u4ed6\u65e9\u5df2\u731c\u7591\u891a\u4e91\u5cf0\u662f\u548c\u4ed6\u4e00 \u8def\u7684\u4eba\u3002 \u3000\u3000\u8fd9\u665a\u891a\u4e91\u5cf0\u5192\u4e86\u6027\u547d\u4e4b\u9669\uff0c\u5077\u8fdb\u6765\u8425\u6551\u674e\u601d\u5357\uff0c\u8fd9\u4ef6\u4e8b\u5927\u5927\u611f\u52a8\u4e86\u4ed6\u3002\u662f\u4ee5\u4ed6\u4e0d\u4f46\u6697\u4e2d \u5e2e\u5fd9\u891a\u4e91\u5cf0\u8131\u8138\uff0c\u800c\u4e14\u51b3\u5fc3\u66ff\u4ee3\u4ed6\u5b8c\u6210\u8fd9\u4ef6\u5de5\u4f5c\uff0c\u5373\u4f7f\u4e0d\u5e78\u727a\u7272\uff0c\u4e5f\u662f\u5728\u6240\u4e0d\u60dc\u7684\u3002 \u3000\u3000\u5c60\u9f99\u5f97\u4e86\u4e00\u4e2a\u4e3b\u610f\uff0c\u6545\u610f\u88c5\u4f5c\u7a74\u9053\u88ab\u5c01\uff0c\u6c14\u8840\u4e0d\u8212\u4ee5\u81f4\u65e0\u529b\u8d70\u8def\u7684\u6837\u5b50\uff0c\u8d70\u4e24\u6b65\uff0c\u505c\u4e00 \u505c\u3002\u5218\u743c\u59d1\u6263\u7740\u4ed6\u7684\u8109\u95e8\uff0c\u5374\u4e0d\u80fd\u62d6\u7740\u4ed6\u5feb\u8dd1\uff0c\u53c8\u4e0d\u6562\u89e3\u5f00\u4ed6\u7684\u7a74\u9053\uff0c\u5fc3\u4e2d\u7740\u6025\uff0c\u5374\u662f\u65e0\u53ef \u5948\u4f55\u3002 \u3000\u3000\u6b64\u65f6\u5929\u8272\u5df2\u4eae\uff0c\u97e9\u8d85\u4e5f\u53ea\u80fd\u8fdc\u8fdc\u8ddf\u8e2a\uff0c\u4e0d\u6562\u9732\u9762\u53bb\u5bf9\u4ed8\u5c60\u9f99\u3002\u5e78\u4e8f\u56ed\u4e2d\u7684\u536b\u58eb\u90fd\u662f\u8ba4\u8bc6 \u5c60\u9f99\u548c\u4ed6\u7684\uff0c\u6b64\u65f6\u4ed6\u4eec\u5df2\u7ecf\u8d70\u8fdb\u4e86\u5185\u9662\u7684\u82b1\u56ed\uff0c\u5f80\u6765\u7684\u536b\u58eb\u5e76\u6ca1\u6709\u76d1\u89c6\u5c60\u9f99\u4e0e\u5218\u743c\u59d1\u7684\u4efb \u52a1\uff0c\u8fd9\u4e9b\u536b\u571f\u90fd\u61c2\u5f97\u4e00\u4e2a\u7981\u5fcc\uff0c\u81ea\u5df1\u4e0d\u8be5\u77e5\u9053\u7684\u4e8b\u60c5\u5c11\u95ee\u4e3a\u5999\uff0c\u662f\u4ee5\u78b0\u4e0a\uff0c\u4e5f\u53ea\u662f\u6253\u4e2a\u62db \u547c\uff0c\u5012\u662f\u6ca1\u4eba\u76d8\u95ee\u3002\u6709\u4eba\u5728\u78b0\u4e0a\u5c60\u9f99\u4e4b\u540e\uff0c\u8ddf\u7740\u53d1\u73b0\u4ed6\uff0c\u4e5f\u53ea\u9053\u4ed6\u662f\u6765\u8ddf\u8e2a\u76d1\u89c6\u7684\uff0c\u6ca1\u6709\u60f3 \u5230\u4ed6\u5df2\u7ecf\u80cc\u53db\u4e86\u9633\u5929\u96f7\u3002 \u3000\u3000\u5c60\u9f99\u6545\u610f\u62d6\u5ef6\u65f6\u95f4\uff0c\u6697\u4e2d\u8fd0\u6c14\u89e3\u7a74\u3002\u8d70\u4e86\u5c06\u8fd1\u4e00\u4e2a\u65f6\u8fb0\uff0c\u65b9\u59cb\u8d70\u5230\u674e\u601d\u5357\u88ab\u56da\u7684\u5904\u6240\uff0c \u6b64\u65f6\u4ed6\u7684\u7a74\u9053\u867d\u7136\u4e5f\u8fd8\u672a\u80fd\u89e3\u5f00\uff0c\u4f46\u771f\u6c14\u5df2\u662f\u9010\u6e10\u51dd\u805a\uff0c\u6062\u590d\u51e0\u5206\u529f\u529b\u4e86\u3002 \u3000\u3000\u4e14\u8bf4\u891a\u4e91\u5cf0\u3001\u8c37\u6db5\u865a\u626e\u4f5c\u67f3\u6d1e\u5929\u3001\u5d14\u9547\u5c71\u7684\u968f\u4ece\uff0c\u5c06\u8fd1\u4e2d\u5348\u7684\u65f6\u5206\u5230\u4e86\u201c\u56fd\u5e08\u5e9c\u201d\u3002\u891a \u4e91\u5cf0\u7528\u6613\u5bb9\u4e39\u6539\u4e86\u5bb9\u8c8c\uff0c\u679c\u7136\u6ca1\u4eba\u8ba4\u5f97\u662f\u4ed6\u3002 \u3000\u3000\u9633\u5929\u96f7\u63a5\u4e86\u67f3\u3001\u5d14\u4e8c\u4eba\u7684\u62dc\u5e16\uff0c\u4f20\u4ee4\u5728\u5bc6\u5ba4\u63a5\u89c1\u4ed6\u4eec\u3002 \u3000\u3000\u891a\u3001\u8c37\u4e8c\u4eba\u8ddf\u7740\u8fdb\u53bb\uff0c\u5b88\u95e8\u7684\u536b\u58eb\u559d\u9053\uff0c\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u89c4\u77e9\uff1f\u56fd\u5e08\u53ea\u89c1\u4e24\u4f4d\u5be8\u4e3b\uff0c\u4e0b\u4eba \u53ea\u80fd\u5728\u5916\u9762\u7b49\u5019\uff01\u201d \u3000\u3000\u891a\u4e91\u5cf0\u6697\u6697\u53eb\u4e86\u4e00\u58f0\u201c\u82e6\u4e5f\uff01\u201d\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u89c1\u4e0d\u7740\u9633\u5929\u96f7\uff0c\u6211\u8fd9\u756a\u5fc3\u8840\u53ef\u662f\u767d\u8d39 \u4e86\uff01\u201d \u3000\u3000\u5bc6\u5ba4\u91cc\u6709\u4e00\u9762\u5c4f\u98ce\uff0c\u5c4f\u98ce\u4e0a\u5d4c\u6709\u4e00\u9762\u78e8\u5f97\u5149\u4eae\u7684\u94dc\u955c\uff0c\u9633\u5929\u96f7\u53ef\u4ee5\u770b\u89c1\u95e8\u5916\u7684\u4eba\uff0c\u95e8\u5916 \u7684\u4eba\u5374\u770b\u4e0d\u89c1\u4ed6\u3002 \u3000\u3000\u9633\u5929\u96f7\u770b\u89c1\u4e86\u891a\u4e91\u5cf0\uff0c\u4e0d\u89c9\u5fc3\u4e2d\u4e00\u52a8\uff0c\u201c\u8fd9\u4eba\u597d\u50cf\u662f\u6211\u66fe\u7ecf\u89c1\u8fc7\u7684\uff1f\u201d\u8981\u77e5\u4ed6\u662f\u891a\u4e91\u5cf0 \u7684\u5e08\u4f2f\uff0c\u8ba4\u8bc6\u4ed6\u7684\u65e5\u5b50\u8f83\u957f\uff0c\u4eba\u53c8\u6781\u4e3a\u7cbe\u660e\uff0c\u662f\u4ee5\u867d\u7136\u8fd8\u662f\u8ba4\u4e0d\u51fa\u4ed6\uff0c\u4ed6\u5df2\u662f\u5fc3\u4e2d\u8d77\u7591\u4e86\u3002 \u3000\u3000\u67f3\u3001\u5d14\u4e8c\u4eba\u8fdb\u4e86\u5bc6\u5ba4\uff0c\u5750\u8d70\u4e4b\u540e\uff0c\u9633\u5929\u96f7\u4fbf\u9053\uff1a\u201c\u8ddf\u4f60\u6765\u7684\u90a3\u4e24\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u67f3\u6d1e\u5929\u9053\uff1a\u201c\u662f\u655d\u5be8\u7684\u4e24\u4e2a\u5c0f\u5934\u76ee\uff0c\u529e\u4e8b\u4e5f\u8fd8\u5f97\u529b\uff0c\u662f\u4ee5\u5e26\u4e86\u4ed6\u4eec\u540c\u6765\uff0c\u4f5c\u4e2a\u4f7f\u5524\u3002\u201d \u3000\u3000\u9633\u5929\u96f7\u9053\uff1a\u201c\u5c0f\u5934\u76ee\uff0c\u6050\u6015\u4e0d\u5bf9\u5427\uff1f\u201d \u3000\u3000\u67f3\u6d1e\u5929\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u600e\u6562\u6b3a\u7792\u56fd\u5e08\uff1f\u201d \u3000\u3000\u9633\u5929\u96f7\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u82e5\u662f\u771f\u7684\uff0c\u90a3\u4f60\u5c31\u662f\u8d70\u773c\u4e86\uff01\u201d \u3000\u3000\u67f3\u6d1e\u5929\u624b\u5fc3\u91cc\u634f\u7740\u4e00\u628a\u6c57\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u8001\u8d3c\u662f\u4ece\u54ea\u91cc\u770b\u51fa\u7834\u7efd\u7684\u5462\uff1f\u4ed6\u6839\u672c\u8fd8\u6ca1\u89c1 \u7740\u4ed6\u4eec\uff01\u201d\u5f53\u4e0b\u4f6f\u4f5c\u8bda\u60f6\u8bda\u6050\u7684\u795e\u6c14\u8bf4\u9053\uff1a\u201c\u4e0d\u77e5\u56fd\u5e08\u662f\u4ec0\u4e48\u610f\u601d\uff0c\u8fd8\u8bf7\u660e\u767d\u8d50\u793a\u3002\u201d \u3000\u3000\u9633\u5929\u96f7\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u4e24\u4e2a\u5c0f\u5934\u76ee\u7684\u5185\u529f\u5f88\u4e0d\u9519\u554a\uff0c\u6240\u4ee5\u6211\u8bf4\uff0c\u5018\u82e5\u4ed6\u4eec\u771f\u662f\u5c0f\u5934\u76ee\u7684 \u8bdd\uff0c\u8fd9\u4f60\u53ef\u5c31\u662f\u5927\u6750\u5c0f\u7528\u4e86\u3002\u201d\u539f\u6765\u9633\u5927\u96f7\u542c\u4e86\u891a\u3001\u8c37\u4e8c\u4eba\u8bf4\u8bdd\u7684\u58f0\u97f3\uff0c\u4e2d\u6c14\u6781\u4e3a\u5145\u6c9b\u3002\u53c8 \u4ece\u94dc\u955c\u4e2d\u4ed4\u7ec6\u89c2\u5bdf\uff0c\u53d1\u73b0\u4ed6\u4eec\u7684\u592a\u9633\u7a74\u5fae\u5fae\u9686\u8d77\uff0c\u8fd9\u90fd\u662f\u5185\u529f\u7ec3\u5f97\u9887\u6709\u6839\u5e95\u7684\u8ff9\u8c61\u3002 \u3000\u3000\u67f3\u6d1e\u5929\u66f4\u662f\u5403\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u56fd\u5e08\u6cd5\u773c\uff0c\u4ee4\u4eba\u60ca\u4f69\uff0c\u6211\u7adf\u4e0d\u77e5\u9053\u4ed6\u4eec\u7ec3\u8fc7\u5185\u529f\uff0c\u8fd9\u53ef\u771f\u662f \u6709\u773c\u65e0\u73e0\u4e86\u3002\u201d \u3000\u3000\u9633\u5929\u96f7\u8d77\u4e86\u731c\u7591\uff0c\u88c5\u4f5c\u7231\u624d\u7684\u6837\u5b50\uff0c\u8bf4\u9053\uff1a\u201c\u4eba\u624d\u4e0d\u5e94\u57cb\u6ca1\uff0c\u6211\u4e5f\u4e0d\u6562\u8bf4\u81ea\u5df1\u8001\u773c\u660f \u82b1\uff0c\u4f60\u53eb\u4ed6\u4eec\u8fdb\u6765\u8ba9\u6211\u770b\u770b\uff01\u201d \u3000\u3000\u67f3\u6d1e\u5929\u53c8\u60ca\u53c8\u559c\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u7ba1\u4ed6\u662f\u770b\u7834\u4e5f\u597d\uff0c\u672a\u770b\u7834\u4e5f\u597d\uff0c\u53cd\u6b63\u662f\u8981\u548c\u4ed6\u5e72\u7684 \u4e86\uff01\u201d\u4e8e\u662f\u4fbf\u5373\u53eb\u9053\uff1a\u201c\u56fd\u5e08\u53eb\u4f60\u4eec\u8fdb\u6765\uff01\u201d \u3000\u3000\u9633\u5929\u96f7\u521a\u624d\u7684\u8bf4\u8bdd\uff0c\u5916\u9762\u5b88\u95e8\u7684\u536b\u58eb\u4eec\u4e5f\u542c\u89c1\u4e86\uff0c\u5927\u4e3a\u60ca\u5f02\uff0c\u8bf4\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u56fd\u5e08\u7237\u7adf \u8981\u4f60\u4eec\u8fdb\u89c1\uff0c\u8fd9\u53ef\u771f\u662f\u4f60\u4eec\u5929\u5927\u7684\u9020\u5316\u3002\u201d \u3000\u3000\u9633\u5929\u96f7\u518d\u4ed4\u7ec6\u5730\u6253\u91cf\u4e86\u891a\u4e91\u5cf0\u4e00\u773c\uff0c\u8bf4\u9053\uff1a\u201c\u5c0a\u5e08\u662f\u54ea\u4e00\u4f4d\uff1f\u4f60\u4eec\u6709\u8fd9\u6837\u7684\u597d\u529f\u592b\uff0c\u4f55 \u4ee5\u7518\u5fc3\u505a\u4e00\u4e2a\u5c71\u5be8\u7684\u5c0f\u5934\u76ee\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u80e1\u4e71\u634f\u9020\u4e86\u4e00\u4e2a\u5e08\u7236\u7684\u540d\u5b57\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u662f\u7ec3\u8fc7\u51e0\u5e74\u4e09\u811a\u732b\u7684\u529f\u592b\uff0c\u5f97\u67f3\u5be8\u4e3b\u63d0 \u62d4\u5df2\u662f\u611f\u6fc0\u4e0d\u5c3d\uff0c\u600e\u6562\u8bf4\u59d4\u5c48\u4e8c\u5b57\uff1f\u201d \u3000\u3000\u9633\u5929\u96f7\u6301\u4e00\u634b\u80e1\u987b\uff0c\u4e24\u9053\u9e70\u96bc\u4f3c\u7684\u9510\u5229\u76ee\u5149\uff0c\u5f84\u5c04\u8fc7\u53bb\uff0c\u4ece\u891a\u4e91\u5cf0\u8eab\u4e0a\u8f6c\u5230\u8c37\u6db5\u865a\u8eab \u4e0a\uff0c\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u5927\u667a\u82e5\u611a\uff0c\u6df1\u85cf\u82e5\u865a\u3002\u5c11\u5e74\u4eba\u80fd\u591f\u5982\u6b64\u8c26\u900a\uff0c\u96be\u5f97\u96be\u5f97\uff01\u201d \u3000\u3000\u8c37\u6db5\u865a\u7684\u547d\u540d\u5c31\u662f\u4ece\u8fd9\u4e24\u53e5\u6210\u8bed\u8131\u80ce\u51fa\u6765\u7684\uff0c\u9633\u5929\u96f7\u8bf4\u5230\u201c\u6df1\u85cf\u82e5\u865a\u201d\u8fd9\u56db\u4e2a\u5b57\u4e4b\u65f6\uff0c \u9510\u5229\u7684\u76ee\u5149\u6b63\u597d\u6ce8\u89c6\u7740\u8c37\u6db5\u865a\u7684\u9762\u5b54\uff0c\u663e\u7136\u662f\u8981\u89c2\u5bdf\u4ed6\u7684\u795e\u6e05\u7684\u53d8\u5316\u3002\u8c37\u6db5\u865a\u7981\u4e0d\u4f4f\u5fc3\u4e2d\u4e00 \u52a8\uff1a\u201c\u96be\u9053\u4ed6\u5df2\u7ecf\u77e5\u9053\u4e86\u6211\u7684\u771f\u540d\u5b9e\u59d3\u3001\u6765\u5386\u4ee5\u53ca\u8eab\u4efd\u4e0d\u6210\uff1f\u201d \u3000\u3000\u4e0d\u9519\uff0c\u9633\u5929\u96f7\u662f\u65e9\u5df2\u77e5\u9053\u8c37\u6db5\u865a\u8fd9\u4e2a\u540d\u5b57\uff0c\u6b64\u9645\u5fc3\u4e2d\u4e5f\u662f\u6b63\u5728\u731c\u7591\uff0c\u4f46\u8fd8\u4e0d\u6562\u65ad\u5b9a\u7ad9\u5728 \u4ed6\u7684\u9762\u524d\u8fd9\u4e2a\u4e11\u6c49\u5b50\u5c31\u662f\u8c37\u6db5\u8651\u3002 \u3000\u3000\u8981\u77e5\u9633\u5929\u96f7\u7684\u4f84\u5b50\u9633\u575a\u767d\u662f\u66fe\u7ecf\u548c\u8c37\u6db5\u8651\u4ea4\u8fc7\u624b\u7684\u3002\u4e0d\u8fc7\u4e24\u5ea6\u4ea4\u624b\uff0c\u8c37\u6db5\u865a\u90fd\u662f\u8499\u7740\u9762 \u5b54\uff0c\u662f\u4ee5\u9633\u575a\u767d\u53ea\u77e5\u4ed6\u662f\u672c\u6d3e\u4e2d\u4eba\uff0c\u8fd8\u672a\u89c1\u8fc7\u4ed6\u7684\u5e90\u5c71\u9762\u76ee\u3002 \u3000\u3000\u9633\u575a\u767d\u56de\u6765\u544a\u8bc9\u53d4\u7236\uff0c\u9633\u5927\u96f7\u60f3\u5c3d\u529e\u6cd5\u67e5\u63a2\uff0c\u7ec8\u4e8e\u4ece\u98de\u9f99\u5c71\u7aa6\u5b89\u5e73\u7684\u4e00\u4e2a\u9003\u51fa\u6765\u7684\u624b\u4e0b \u7684\u53e3\u4e2d\uff0c\u77e5\u9053\u4e86\u8c37\u6db5\u865a\u7684\u540d\u5b57\uff0c\u5e76\u4e14\u77e5\u9053\u8c37\u6db5\u865a\u662f\u4e00\u4e2a\u5947\u4e11\u7684\u6c49\u5b50\u3002\u4e0d\u8fc7\u90a3\u4e2a\u624b\u4e0b\u5982\u4eca\u5df2\u662f \u4e0d\u5728\u4ed6\u7684\u201c\u56fd\u5e08\u5e9c\u201d\u4e2d\uff0c\u65e0\u4ece\u5bf9\u8bc1\u3002 \u3000\u3000\u8c37\u6db5\u865a\u867d\u7136\u7ecf\u8fc7\u5316\u88c5\uff0c\u4f46\u8138\u4e0a\u7684\u4f24\u75a4\u8fd8\u662f\u63a9\u9970\u4e0d\u4e86\u7684\uff0c\u9633\u5929\u96f7\u8d77\u4e86\u601d\u7591\uff0c\u6697\u81ea\u60f3\u9053\uff1a \u201c\u5018\u82e5\u8fd9\u4e2a\u4e11\u516b\u602a\u662f\u8c37\u6db5\u865a\uff0c\u53e6\u4e00\u4e2a\u4eba\u5c31\u4e00\u5b9a\u662f\u891a\u4e91\u5cf0\u3002\u602a\u4e0d\u5f97\u6211\u89c9\u5f97\u4ed6\u4f3c\u66fe\u76f8\u8bc6\uff01\u201d \u3000\u3000\u9633\u5929\u96f7\u84e6\u5730\u5f97\u4e86\u4e00\u4e2a\u4e3b\u610f\uff0c\u4e8e\u662f\u5728\u8bf4\u4e86\u8fd9\u51e0\u53e5\u8bdd\u4e4b\u540e\uff0c\u4fbf\u4f38\u51fa\u624b\u4e0e\u891a\u4e91\u5cf0\u76f8\u63e1\u3002\u8868\u9762\u4e0a \u662f\u7ea1\u5c0a\u964d\u8d35\uff0c\u5bf9\u4e00\u4e2a\u6709\u4e3a\u7684\u9752\u5e74\u52a0\u4ee5\u9752\u773c\uff0c\u5176\u5b9e\u5374\u662f\u60f3\u7528\u201c\u5929\u96f7\u529f\u201d\u8bd5\u4ed6\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u770b\u51fa\u4e86\u9633\u5929\u96f7\u662f\u8981\u7528\u201c\u5929\u96f7\u529f\u201d\u6765\u5bf9\u4ed8\u4ed6\uff0c\u5927\u5403\u4e00\u60ca\uff0c\u7acb\u5373\u559d\u9053\uff1a\u201c\u96f7\u7535\u4ea4 \u8f70\uff01\u201d\u8c37\u6db5\u865a\u5e94\u58f0\u51fa\u638c\uff0c\u4e8c\u4eba\u56db\u638c\u540c\u65f6\u5404\u81ea\u753b\u4e86\u4e00\u9053\u5706\u5f27\uff0c\u5411\u9633\u5929\u96f7\u5f53\u5934\u5288\u4e0b\uff0c\u5e08\u5144\u5f1f\u8054 \u624b\uff0c\u548c\u9633\u5929\u96f7\u5bf9\u4e86\u4e00\u638c\uff01 \u3000\u3000\u8f70\u7136\u4e00\u9707\uff0c\u58f0\u5982\u90c1\u96f7\uff01\u891a\u3001\u8c37\u4e8c\u4eba\u90fd\u7ed9\u9707\u9000\u4e09\u6b65\uff0c\u9633\u5929\u96f7\u4e5f\u662f\u4e00\u4e2a\u8e09\u8dc4\uff0c\u8eab\u5f62\u6447\u6643\uff0c\u867d \u7136\u672a\u7ed9\u9707\u9053\uff0c\u5374\u4e5f\u662f\u811a\u6b65\u6709\u4e9b\u4e0d\u7a33\u4e86\uff01\u9633\u5929\u96f7\u5927\u5403\u4e00\u60ca\uff1a\u201c\u8fd9\u4e24\u4e2a\u5c0f\u8d3c\u8054\u624b\uff0c\u53ea\u6015\u6211\u662f\u8981\u5728 \u767e\u62db\u4e4b\u5916\uff0c\u65b9\u80fd\u80dc\u5f97\u4ed6\u4eec\u4e86\uff01\u201d \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u67f3\u6d1e\u5929\u548c\u5d14\u9547\u5c71\u4ea6\u5df2\u540c\u65f6\u51fa\u624b\uff0c\u67f3\u6d1e\u5929\u5530\u7684\u4e00\u5251\uff0c\u62db\u91cc\u85cf\u62db\uff0c\u5f0f\u4e2d\u5957 \u5f0f\uff0c\u95ea\u7535\u4e4b\u95f4\uff0c\u653b\u51fb\u4e86\u9633\u5929\u96f7\u7684\u516d\u4e03\u5904\u8981\u5bb3\uff0c\u5d14\u9547\u5c71\u4e5f\u4ee5\u5927\u529b\u91d1\u521a\u638c\u5411\u4ed6\u8eab\u4e0a\u62db\u547c\uff01 \u3000\u3000\u9633\u5929\u96f7\u771f\u4e2a\u662f\u5389\u5bb3\u65e0\u6bd4\uff0c\u811a\u6b65\u672a\u7a33\uff0c\u500f\u7136\u95f4\u5df2\u662f\u201c\u79fb\u5f62\u6613\u4f4d\u201d\uff0c\u4e00\u6325\u8896\u4f5b\u6b6a\u4e86\u67f3\u6d1e\u5929\u7684 \u5251\u5c16\uff0c\u5de6\u638c\u4e00\u6309\uff0c\u53c8\u628a\u5d14\u9547\u5c71\u9707\u5f97\u8fde\u8fde\u540e\u9000\u3002\u5e78\u4e8f\u4ed6\u8fd9\u4e00\u638c\u4e43\u662f\u5728\u521a\u521a\u548c\u891a\u3001\u8c37\u4e8c\u4eba\u786c\u62fc\u4e86 \u201c\u5929\u96f7\u529f\u201d\u4e4b\u540e\uff0c\u5426\u5219\u5d14\u9547\u5c71\u53ea\u6015\u4e0d\u6b7b\u4e5f\u8981\u53d7\u4f24\uff01 \u3000\u3000\u891a\u4e91\u5cf0\u3001\u8c37\u6db5\u865a\u9f50\u58f0\u559d\u9053\uff1a\u201c\u9633\u5929\u96f7\uff0c\u4f60\u6b3a\u5e08\u706d\u7956\uff0c\u4eca\u65e5\u6211\u4eec\u662f\u66ff\u5e08\u7236\u6e05\u7406\u5e08\u95e8\uff01\u201d\u559d \u58f0\u4e2d\u53c8\u662f\u4e00\u62db\u201c\u96f7\u7535\u4ea4\u8f70\u201d\uff0c\u9633\u5929\u96f7\u867d\u7136\u6b66\u529f\u5353\u7edd\uff0c\u5bf9\u4ed8\u56db\u540d\u9ad8\u624b\uff0c\u4ea6\u662f\u96be\u514d\u5de6\u652f\u53f3\u7ecc\u4e86\uff01 \u3000\u3000\u9633\u5929\u96f7\u53cc\u638c\u5f00\u51fa\uff0c\u53ea\u542c\u5f97\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u8c37\u3001\u891a\u4e8c\u4eba\u867d\u7136\u518d\u6b21\u7ed9\u4ed6\u9707\u9000\uff0c\u4f46\u7531\u4e8e\u4ed6\u65e0\u6687 \u517c\u987e\uff0c\u8863\u8896\u5df2\u662f\u7ed9\u67f3\u6d1e\u5929\u524a\u4e86\u4e00\u5e45\uff0c\u9669\u4e9b\u5c31\u8981\u524a\u6389\u4ed6\u7684\u624b\u6307\u3002 \u3000\u3000\u9633\u5929\u96f7\u53eb\u9053\uff1a\u201c\u767d\u8001\u5927\uff0c\u51fa\u6765\uff01\u201d\u58f0\u72b9\u672a\u4e86\uff0c\u5802\u4e0b\u7684\u6b66\u571f\uff0c\u5185\u5ba4\u7684\u4f0f\u5175\uff0c\u5df2\u662f\u4e00\u9f50\u6d8c \u73b0\u3002 \u3000\u3000\u9633\u5929\u96f7\u559d\u9053\uff1a\u201c\u7528\u4e0d\u4e86\u8fd9\u8bb8\u591a\u4eba\uff0c\u767d\u8001\u5927\uff0c\u575a\u513f\uff0c\u4f60\u4eec\u7559\u4e0b\uff0c\u5176\u4ed6\u7684\u4eba\u90fd\u9000\u51fa\u53bb\uff0c\u4e25\u9632 \u4ed6\u4eec\u8fd8\u6709\u515a\u7fbd\uff01\u201d \u3000\u3000\u5c31\u5728\u4ed6\u7684\u5927\u559d\u58f0\u4e2d\uff0c\u4e00\u4e2a\u7ea2\u5149\u6ee1\u9762\u7684\u8001\u8005\u5df2\u662f\u5411\u5d14\u9547\u5c71\u6251\u53bb\uff0c\u53e6\u5916\u4e00\u4e2a\u767d\u8863\u5c11\u5e74\u5219\u662f\u8fd0 \u5251\u5982\u98ce\uff0c\u6740\u5411\u67f3\u6d1e\u5929\uff01 \u3000\u3000\u539f\u6765\u8fd9\u4e2a\u7ea2\u5149\u6ee1\u9762\u7684\u9b41\u68a7\u8001\u8005\u5c31\u662f\u767d\u5bb6\u5e84\u7684\u5e84\u4e3b\u767d\u4e07\u96c4\uff0c\u4ed6\u662f\u5728\u767d\u5bb6\u5e84\u88ab\u674e\u601d\u5357\u6240\u7834\u4e4b \u540e\uff0c\u5e26\u4e86\u513f\u5b50\u767d\u5343\u80dc\u9003\u5230\u9633\u5929\u96f7\u7684\u201c\u56fd\u5e08\u5e9c\u201d\u7684\u3002 \u3000\u3000\u90a3\u4e2a\u767d\u8863\u5c11\u5e74\uff0c\u4e0d\u7528\u8bf4\u5c31\u662f\u4ed6\u7684\u4f84\u5b50\u9633\u575a\u767d\u4e86\u3002 \u3000\u3000\u9633\u5929\u96f7\u6df1\u77e5\u654c\u65b9\u8fd9\u56db\u4e2a\u4eba\u6b66\u827a\u4e0d\u51e1\uff0c\u5728\u4e00\u95f4\u623f\u5b50\u4e4b\u5185\u52a8\u624b\uff0c\u4eba\u591a\u53cd\u800c\u6ca1\u7528\uff0c\u653e\u800c\u628a\u591a\u4f59 \u7684\u4eba\u90fd\u9063\u51fa\u53bb\uff0c\u53ea\u7559\u4e0b\u6b66\u529f\u8f83\u5f3a\u7684\u767d\u4e07\u96c4\u548c\u4ed6\u7684\u4f84\u5b50\u9633\u575a\u767d\u3002\u81ea\u5fd6\u6709\u4e86\u4e24\u4e2a\u5e2e\u624b\uff0c\u5df2\u662f\u53ef\u4ee5 \u7a33\u64cd\u80dc\u5238\u3002 \u3000\u3000\u9633\u575a\u767d\u5728\u891a\u3001\u8c37\u4e8c\u4eba\u7684\u624b\u4e0b\u90fd\u5403\u8fc7\u4e8f\uff0c\u4e50\u5f97\u7531\u7236\u4eb2\u53bb\u5bf9\u4ed8\u4ed6\u4eec\uff0c\u81ea\u5df1\u5219\u4e0e\u67f3\u6d1e\u5929\u6597\u5251\uff01 \u3000\u3000\u4ed6\u4ee5\u4e3a\u67f3\u6d1e\u5929\u5bb9\u6613\u5e94\u4ed8\uff0c\u54ea\u77e5\u67f3\u6d1e\u5929\u4e43\u662f\u4e00\u4f4d\u6b66\u6797\u7684\u5927\u540d\u5bb6\uff0c\u5251\u6cd5\u4e4b\u7cbe\uff0c\u8fdc\u8fdc\u5728\u4ed6\u4e4b \u4e0a\u3002 \u3000\u3000\u6570\u62db\u4e00\u8fc7\uff0c\u9633\u575a\u767d\u53ea\u89c9\u5bf9\u65b9\u7684\u5251\u5c16\u597d\u4f3c\u5728\u4ed6\u7684\u9762\u524d\u7a7f\u6765\u63d2\u53bb\uff0c\u8000\u773c\u751f\u8f89\u3002\u9633\u575a\u767d\u5927\u60ca\u4e4b \u4e0b\uff0c\u8fde\u5fd9\u5251\u4e2d\u5939\u638c\uff0c\u4f7f\u51fa\u4e86\u201c\u5929\u96f7\u529f\u201d\u3002 \u3000\u3000\u67f3\u6d1e\u5929\u8eab\u5f62\u4e00\u6643\uff0c\u201c\u9189\u516b\u4ed9\u201d\u5251\u6cd5\u65bd\u5c55\u51fa\u6765\uff0c\u811a\u6b65\u8e09\u8dc4\uff0c\u5f53\u771f\u5c31\u4f3c\u9189\u6c49\u4e00\u822c\uff0c\u4e1c\u6b6a\u897f \u5012\u3002\u53ef\u662f\u5251\u62db\u5374\u662f\u6108\u51fa\u6108\u5947\uff0c\u6740\u5f97\u9633\u575a\u767d\u53ea\u80fd\u62db\u67b6\uff0c\u65e0\u529b\u8fd8\u653b\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u7531\u4e8e\u4ed6\u8981\u62b5\u5fa1\u5bf9\u65b9\u7684\u5929\u96f7\u529f\uff0c\u597d\u51e0\u62db\u51dd\u5389\u7684\u6740\u7740\uff0c\u5374\u4e5f\u4e0d\u80fd\u5f97\u5fc3\u5e94\u624b\u3002\u867d\u7136\u5360\u5c3d \u4e0a\u98ce\uff0c\u603b\u662f\u4e0d\u80fd\u5236\u5bf9\u65b9\u6b7b\u547d\u3002 \u3000\u3000\u767d\u4e07\u96c4\u631f\u6570\u5341\u5e74\u529f\u529b\uff0c\u4e0e\u5c11\u6797\u6d3e\u7684\u9ad8\u8db3\u5d14\u9547\u5c71\u6076\u6597\uff0c\u5374\u5360\u4e86\u4e00\u70b9\u4e0a\u98ce\u3002 \u3000\u3000\u5d14\u9547\u5c71\u7684\u5927\u529b\u91d1\u521a\u638c\u521a\u731b\u6709\u4f59\uff0c\u7a33\u5065\u4e0d\u8db3\uff0c\u767d\u4e07\u96c4\u4ee5\u7ef5\u638c\u52a0\u4e0a\u201c\u9e70\u722a\u624b\u201d\u6597\u4ed6\uff0c\u8f6c\u77ac\u4e4b \u95f4\uff0c\u638c\u5288\u6307\u6233\uff0c\u5df2\u662f\u628a\u4ed6\u7684\u8eab\u5f62\u7f69\u4f4f\uff0c\u4e0d\u8fc7\u767d\u4e07\u96c4\u5bf9\u4ed6\u7684\u521a\u52c7\uff0c\u4ea6\u662f\u5fcc\u60ee\u51e0\u5206\uff0c\u4e0d\u6562\u8fc7\u5206\u8fdb \u903c\u3002\u4e00\u5360\u4e0a\u98ce\uff0c\u4fbf\u5373\u91c7\u53d6\u6c89\u7a33\u7684\u6253\u6cd5\uff0c\u201c\u907f\u5176\u671d\u9510\uff0c\u51fb\u5176\u66ae\u5f52\u201d\uff0c\u6253\u7b97\u5728\u6d88\u8017\u5d14\u9547\u5c71\u51e0\u5206\u6c14 \u529b\u4e4b\u540e\uff0c\u518d\u65bd\u6740\u624b\u3002\u6b63\u662f\uff1a \u3000\u3000\u6b63\u90aa\u51b3\u6597\u60ca\u5fc3\u9b44\uff0c\u5251\u5f71\u5200\u5149\u8d77\u6740\u673a\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b\u2014\u2014\u7b2c\u4e94\u5341\u516d\u56de\u3000\u76ca\u89c1\u6df1\u60c5\u56da\u9ed1\u5ba4\u3000\u62fc\u5c06\u70ed\u8840\u6d17\u6c61\u540d \u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b \u7b2c\u4e94\u5341\u516d\u56de\u3000\u76ca\u89c1\u6df1\u60c5\u56da\u9ed1\u5ba4\u3000\u62fc\u5c06\u70ed\u8840\u6d17\u6c61\u540d \u3000\u3000\u9633\u5929\u96f7\u548c\u8bf8\u3001\u8c37\u4e8c\u4eba\u4ea4\u624b\uff0c\u53cc\u65b9\u628a\u201c\u5929\u96f7\u529f\u201d\u53d1\u6325\u5f97\u6dcb\u6f13\u5c3d\u81f4\uff0c\u638c\u98ce\u6fc0\u8361\uff0c\u53ea\u542c\u5f97\u547c\u547c \u8f70\u8f70\u4e4b\u58f0\uff0c\u5f53\u771f\u662f\u5982\u96f7\u9707\u8033\u3002\u638c\u98ce\u6240\u81f3\uff0c\u7a97\u6237\u6d1e\u5f00\uff0c\u67f1\u6881\u6447\u52a8\uff0c\u5c4b\u5b50\u90fd\u597d\u4f3c\u8981\u584c\u4e0b\u6765\u4f3c\u7684\u3002 \u90a3\u4e9b\u529f\u529b\u8f83\u5f31\u7684\u6b66\u58eb\uff0c\u65e9\u5df2\u8eb2\u5230\u5916\u9762\u53bb\u4e86\u3002 \u3000\u3000\u4e00\u4e2a\u6b66\u58eb\u53eb\u9053\uff1a\u201c\u8981\u4e0d\u8981\u8bf7\u9f99\u8c61\u6cd5\u738b\u524d\u6765\uff1f\u201d \u3000\u3000\u9633\u5929\u96f7\u773c\u89c2\u56db\u9762\uff0c\u8033\u542c\u516b\u65b9\uff0c\u53e6\u5916\u4e24\u5bf9\u53ae\u6740\u7684\u60c5\u5f62\uff0c\u4ed6\u53ea\u987b\u773c\u5149\u4e00\u77a5\uff0c\u542c\u90a3\u5175\u5668\u78b0\u51fb\u7684 \u58f0\u97f3\u3002\u8c01\u5f3a\u8c01\u5f31\uff0c\u4fbf\u5df2\u4e86\u5982\u6307\u638c\u4e86\u3002 \u3000\u3000\u9633\u5929\u96f7\u6697\u81ea\u601d\u91cf\uff1a\u201c\u575a\u513f\u548c\u67f3\u6d1e\u5929\u76f8\u6bd4\uff0c\u662f\u7a0d\u6709\u4e0d\u5982\uff0c\u4f46\u6700\u5c11\u4e5f\u53ef\u4ee5\u6253\u5230\u767e\u62db\u5f00\u5916\u3002\u767d \u4e07\u96c4\u548c\u5d14\u9547\u5c71\u6597\u638c\uff0c\u5219\u662f\u7a33\u80dc\u65e0\u7591\uff0c\u800c\u4e14\u770b\u6765\u4e0d\u5fc5\u7528\u5230\u767e\u62db\u3002\u201d\u8fd9\u6837\u7684\u5f62\u52bf\uff0c\u4ea6\u5373\u662f\u8bf4\uff0c\u53ea \u8981\u9633\u5929\u96f7\u6216\u8005\u767d\u4e07\u96c4\uff0c\u4efb\u4f55\u4e00\u4e2a\uff0c\u51fb\u8d25\u4e86\u5bf9\u65b9\uff0c\u5c31\u7acb\u5373\u53ef\u4ee5\u8fc7\u53bb\u5e2e\u52a9\u9633\u575a\u767d\u4e86\u3002\u8fd9\u8be6\u7684\u5f62 \u52bf\uff0c\u5f53\u7136\u662f\u9633\u5929\u96f7\u8fd9\u8fb9\u7edd\u5bf9\u6709\u5229\uff0c\u6781\u5360\u4e0a\u98ce\u3002 \u3000\u3000\u9633\u5929\u96f7\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u54b1\u4eec\u7684\u5bb6\u4e8b\uff0c\u4f55\u5fc5\u6c42\u52a9\u5ba2\u4eba\uff1f\u7ed9\u5916\u4eba\u770b\u5c0f\uff01\u4f60 \u4eec\u5bb3\u6015\uff0c\u8eb2\u5f97\u8fdc\u4e9b\uff0c\u77a7\u6211\u628a\u8fd9\u4e24\u4e2a\u5c0f\u5b50\u62ff\u4e0b\uff01\u201d \u3000\u3000\u8981\u77e5\u9053\u9633\u5929\u96f7\u4e43\u662f\u91d1\u56fd\u56fd\u5e08\u7684\u8eab\u4efd\uff0c\u9f99\u8c61\u6cd5\u738b\u5219\u662f\u8499\u53e4\u7684\u56fd\u5e08\uff0c\u4e24\u4eba\u7684\u8eab\u4efd\u662f\u76f8\u7b49\u7684\uff0c \u867d\u8bf4\u9633\u5929\u96f7\u5728\u9f99\u8c61\u6cd5\u738b\u7684\u9762\u524d\u4e0d\u80fd\u4e0d\u4f4e\u5934\u773c\u5c0f\uff0c\u4f46\u65e0\u8bba\u5982\u4f55\uff0c\u4e5f\u51b3\u4e0d\u80fd\u592a\u8fc7\u6709\u5931\u8eab\u4efd\uff0c\u4f55\u51b5 \u4ed6\u5982\u4eca\u4e43\u662f\u80dc\u5238\u7a33\u64cd\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u865a\u6643\u4e00\u638c\uff0c\u559d\u9053\uff1a\u201c\u770b\u5251\uff01\u201d\u5bd2\u5149\u7535\u95ea\uff0c\u5530\u7684\u5c31\u5411\u9633\u5929\u96f7\u523a\u53bb\u3002\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6 \u5feb\uff0c\u8c37\u6db5\u865a\u4ea6\u5df2\u62d4\u5251\u51fa\u9798\uff0c\u5251\u4e2d\u5939\u638c\uff0c\u5251\u950b\u75be\u4e0a\u3002\u5e08\u5144\u5f1f\u5251\u8d70\u8f7b\u7075\uff0c\u914d\u5408\u5f97\u5999\u5230\u6beb\u5dc5\uff01 \u3000\u3000\u9633\u5929\u96f7\u80cc\u8179\u53d7\u654c\uff0c\u50b2\u7136\u51b7\u7b11\uff0c\u201c\u4f60\u4eec\u5b66\u4e86\u591a\u5c11\u672c\u95e8\u529f\u592b\uff1f\u90fd\u62ff\u51fa\u6765\u5427\uff01\u54fc\uff0c\u54fc\uff01\u7c73\u7c92\u4e4b \u73e0\uff0c\u4e5f\u653e\u5149\u534e\uff01\u201d \u3000\u3000\u8bdd\u72b9\u672a\u4e86\uff0c\u891a\u4e91\u5cf0\u4e00\u5251\u4ece\u4ed6\u989d\u8fb9\u524a\u8fc7\uff0c\u51e0\u4e4e\u524a\u6389\u4e86\u4ed6\u7684\u5929\u7075\u76d6\uff1b\u8c37\u6db5\u865a\u63a5\u7740\u4e00\u5251\uff0c\u5947\u5e7b \u65e0\u6bd4\uff0c\u9633\u5929\u96f7\u6325\u8896\u4e00\u62c2\uff0c\u4e0d\u6599\u8c37\u6db5\u865a\u5251\u950b\u7a81\u7136\u659c\u8f6c\uff0c\u4ece\u4ed6\u610f\u60f3\u4e0d\u5230\u7684\u65b9\u4f4d\u653b\u6765\uff0c\u53ea\u542c\u5f97\u58f0\u5982 \u88c2\u5e1b\uff0c\u9633\u5929\u96f7\u7684\u534a\u8fb9\u8896\u5b50\uff0c\u5316\u6210\u4e86\u7247\u7247\u8774\u8776\u3002\u539f\u6765\u662f\u8c37\u6db5\u865a\u8fd9\u4e00\u62db\u201c\u4e09\u8f6c\u6cd5\u8f6e\u201d\u6240\u7ede\u788e\u7684\u3002 \u9633\u5929\u96f7\u53c8\u60ca\u53c8\u6012\uff0c\u4e00\u58f0\u5927\u559d\uff0c\u53cc\u638c\u9f50\u51fa\uff0c\u4e00\u62db\u201c\u91ce\u9a6c\u5206\u9b03\u201d\u628a\u891a\u3001\u8c37\u4e8c\u4eba\u95f4\u65f6\u903c\u9000\uff01 \u3000\u3000\u9633\u5929\u96f7\u51b7\u7b11\u9053\uff1a\u201c\u539f\u6765\u4f60\u4eec\u8fd8\u7ec3\u6210\u4e86\u672c\u95e8\u5251\u6cd5\uff0c\u4f46\u53c8\u80fd\u5948\u5f97\u6211\u4f55\uff1f\u201d\u8bdd\u662f\u8fd9\u6837\u8bf4\uff0c\u5fc3\u4e2d \u53ef\u662f\u6709\u4e86\u4e9b\u5fae\u7684\u602f\u610f\uff0c\u60f3\u9053\uff1a\u201c\u539f\u6765\u534e\u5929\u8679\u5728\u8352\u5c71\u9690\u5c45\uff0c\u5341\u4f59\u5e74\u95ed\u6237\u4e0d\u95ee\u5916\u4e8b\uff0c\u4e43\u662f\u4e3a\u4e86\u7ec3 \u8fd9\u5251\u6cd5\uff0c\u6559\u7ed9\u4ed6\u7684\u5f92\u5f1f\u5bf9\u4ed8\u6211\u7684\u3002\u891a\u4e91\u5cf0\u8fd9\u5c0f\u5b50\u4f7f\u7684\u5251\u6cd5\uff0c\u4f3c\u4e4e\u8fd8\u80dc\u8fc7\u5e08\u7956\u5f53\u5e74\u3002\u201d \u3000\u3000\u9633\u5929\u96f7\u957f\u4e8e\u5185\u529f\uff0c\u4e0d\u7cbe\u5251\u6cd5\uff0c\u53cc\u65b9\u5404\u4ee5\u81ea\u5df1\u6240\u957f\uff0c\u653b\u654c\u4e4b\u77ed\uff0c\u8fd9\u4e48\u4e00\u6765\uff0c\u6070\u6070\u548c\u891a\u3001\u8c37 \u4e8c\u4eba\u6253\u6210\u5e73\u624b\uff0c\u5927\u5bb6\u90fd\u662f\u5360\u4e0d\u5230\u4fbf\u5b9c\u3002 \u3000\u3000\u53ef\u662f\uff0c\u891a\u3001\u8c37\u4e8c\u4eba\u867d\u7136\u52c9\u5f3a\u548c\u9633\u5929\u96f7\u6273\u4e86\u5e73\u624b\uff0c\u5d14\u9547\u5c71\u548c\u767d\u4e07\u96c4\u7684\u6076\u6597\uff0c\u5374\u662f\u8d8a\u6765\u8d8a\u611f \u5403\u529b\u4e86\u3002\u53e6\u4e00\u5bf9\u67f3\u6d1e\u5929\u5219\u8fd8\u662f\u7565\u5360\u4e0a\u98ce\uff0c\u4f46\u5728\u6025\u5207\u4e4b\u95f4\uff0c\u65e0\u8bba\u5982\u4f55\u4e5f\u662f\u80dc\u4e0d\u4e86\u9633\u575a\u767d\u7684\uff01 \u3000\u3000\u891a\u4e91\u5cf0\u4e0d\u7531\u5f97\u5fc3\u4e2d\u70e6\u8e81\uff0c\u60f3\u9053\uff1a\u201c\u5b5f\u5927\u4fa0\u548c\u97e9\u8001\u82f1\u96c4\u600e\u4e48\u8fd8\u4e0d\u6765\u5462\uff1f\u201d \u3000\u3000\u5b5f\u5c11\u521a\u548c\u97e9\u5927\u7ef4\u662f\u4e00\u65e9\u5c31\u6df7\u8fdb\u4e86\u201c\u56fd\u5e08\u5e9c\u201d\u7684\uff0c\u6309\u8bf4\u4ed6\u4eec\u5728\u8fd9\u91cc\u7684\u9ad8\u547c\u9163\u6597\u4e4b\u58f0\uff0c\u5b5f\u3001 \u97e9\u4e8c\u4eba\u51b3\u6ca1\u6709\u542c\u4e0d\u89c1\u4e0d\u7406\uff01\u201c\u83ab\u975e\u4ed6\u4eec\u662f\u906d\u9047\u4e86\u4ec0\u4e48\u610f\u5916\uff1f\u4ed6\u4eec\u7684\u8bd5\u529f\uff0c\u4e5f\u7f55\u6709\u6bd4\u4f26\uff0c\u8be5\u4e0d \u81f3\u4e8e\u5427\uff0c\u53ef\u662f\u4e3a\u4ec0\u4e48\u76f4\u5230\u5982\u4eca\uff0c\u4ecd\u662f\u8e2a\u8ff9\u6e3a\u7136\uff1f\u201d\u5f3a\u63f4\u672a\u89c1\uff0c\u891a\u3001\u8c37\u4e8c\u4eba\u4e0d\u7531\u5f97\u90fd\u662f\u5fc3\u70e6\u610f \u4e71\uff0c\u521a\u521a\u6273\u6210\u4e86\u5e73\u624b\uff0c\u4e0d\u8fc7\u4e00\u4f1a\uff0c\u53c8\u7ed9\u9633\u5929\u96f7\u5360\u4e86\u4e0a\u98ce\uff0c\u62a2\u4e86\u653b\u52bf\u3002 \u3000\u3000\u4e14\u8bf4\u674e\u601d\u5357\u5728\u77f3\u7262\u4e4b\u4e2d\u6b63\u81ea\u9759\u5750\u8fd0\u529f\uff0c\u7262\u4e2d\u9ed1\u6f06\u6f06\u7684\u4e5f\u4e0d\u77e5\u662f\u767d\u5929\u8fd8\u662f\u591c\u665a\uff0c\u5ffd\u542c\u5f97\u6709 \u4e24\u4e2a\u4eba\u884c\u8d70\u7684\u811a\u6b65\u58f0\uff0c\u4f3c\u4e4e\u6b63\u662f\u5411\u7740\u8fd9\u8fb9\u884c\u8d70\uff0c\u7a81\u7136\u95f4\uff0c\u90a3\u4e24\u4e2a\u4eba\u8bf4\u8bdd\u7684\u58f0\u97f3\u4e5f\u542c\u5f97\u89c1\u4e86\uff0c \u674e\u601d\u5357\u4e0d\u7531\u5f97\u84e6\u5730\u4e00\u60ca\u4e86\uff01 \u3000\u3000\u8fd9\u58f0\u97f3\u597d\u719f\uff0c\u674e\u601d\u5357\u4e00\u542c\u5c31\u77e5\u9053\u662f\u5c60\u9f99\u7684\u58f0\u97f3\uff01 \u3000\u3000\u53ea\u542c\u5f97\u5c60\u9f99\u8bf4\u9053\uff1a\u201c\u8fd9\u4e2a\u770b\u5b88\u4e0d\u61c2\u6c49\u8bed\uff0c\u5f85\u6211\u53eb\u4ed6\u628a\u9501\u5319\u7ed9\u6211\uff0c\u6211\u8bbe\u6cd5\u5c06\u4ed6\u652f\u5f00\uff0c\u4f60\u770b \u53ef\u597d\uff1f\u201d \u3000\u3000\u8ddf\u7740\u4e00\u4e2a\u5c11\u5973\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u4f60\u53ef\u522b\u800d\u82b1\u62db\uff0c\u674e\u76df\u4e3b\u771f\u7684\u662f\u5173\u5728\u8fd9\u91cc\u5417\uff1f\u201d\u5979\u7684\u58f0\u97f3\u8bf4 \u5f97\u5f88\u8f7b\uff0c\u4f46\u674e\u601d\u5357\u7ec3\u8fc7\u201c\u542c\u98ce\u8fa8\u5668\u201d\u7684\u529f\u592b\uff0c\u5374\u662f\u542c\u5f97\u4e00\u6e05\u4e8c\u695a\u3002 \u3000\u3000\u201c\u8fd9\u5973\u5b50\u662f\u8c01\u5462\uff1f\u542c\u5979\u7684\u53e3\u6c14\uff0c\u4f3c\u4e4e\u662f\u60f3\u6765\u6551\u6211\u7684\u3002\u5c60\u9f99\u4f55\u4ee5\u53c8\u4f1a\u542c\u547d\u4e8e\u5979\uff1f\u771f\u662f\u5947 \u602a\uff01\u201d\u674e\u601d\u5357\u5fc3\u60f3\u3002\u4ece\u4ed6\u4eec\u8bf4\u8bdd\u7684\u58f0\u97f3\uff0c\u674e\u601d\u5357\u53ef\u4ee5\u731c\u6d4b\u5f97\u5230\uff0c\u4ed6\u4eec\u4e0e\u8fd9\u7262\u623f\u7684\u8ddd\u79bb\u5927\u7ea6\u8fd8 \u6709\u4e8c\u4e09\u5341\u6b65\u4e4b\u9065\uff0c\u8fd9\u5973\u5b50\u662f\u5728\u5c60\u9f99\u7684\u8033\u8fb9\u8bf4\u8bdd\u7684\u3002 \u3000\u3000\u5fc3\u5ff5\u672a\u5df2\uff0c\u53ea\u542c\u5f97\u5c60\u9f99\u6253\u4e86\u4e2a\u54c8\u54c8\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u653e\u5fc3\u8bf4\u8bdd\u4e5f\u4e0d\u6015\uff0c\u8fd9\u770b\u5b88\u542c\u4e0d\u61c2\u7684\u3002 \u55ef\uff0c\u743c\u59d1\uff0c\u6211\u600e\u80fd\u9a97\u4f60\u5462\uff1f\u6211\u8ba9\u4f60\u4eb2\u81ea\u6253\u5f00\u7262\u4eec\uff0c\u628a\u4f60\u6240\u4ef0\u52df\u7684\u5927\u82f1\u96c4\u5927\u8c6a\u6770\u674e\u601d\u5357\u653e\u51fa \u6765\uff01\u201d \u3000\u3000\u5218\u743c\u59d1\u51b7\u7b11\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u6015\u4f60\u9a97\u6211\uff0c\u9664\u975e\u4f60\u60f3\u4e0d\u8981\u6027\u547d\uff01\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u5df2\u662f\u6765\u5230\u7262\u623f\u4e4b\u524d\u3002\u90a3\u4e2a\u8499\u53e4\u6b66\u58eb\u8ba4\u5f97\u5c60\u9f99\uff0c\u5374\u672a\u89c1\u8fc7\u5218\u743c\u59d1\uff0c\u770b\u89c1\u4ed6\u4eec\u4e00 \u540c\u6765\u5230\uff0c\u9887\u4e3a\u60ca\u8be7\uff0c\u6b63\u8981\u5f00\u53e3\uff0c\u5c60\u9f99\u5df2\u662f\u62a2\u5148\u8bf4\u9053\uff1a\u201c\u628a\u9501\u5319\u7ed9\u6211\uff0c\u4f60\u7acb\u5373\u53bb\u8bf7\u9f99\u8c61\u6cd5\u738b \u6765\u3002\u4e0d\u5fc5\u591a\u95ee\uff0c\u5feb\u53bb\uff0c\u5feb\u53bb\uff01\u6211\u662f\u53d7\u4eba\u8981\u631f\u7684\uff01\u201d \u3000\u3000\u539f\u6765\u8fd9\u8499\u53e4\u770b\u5b88\u4e0d\u4f1a\u8bf4\u6c49\u8bed\uff0c\u662f\u4ee5\u5c60\u9f99\u62a2\u5148\u7528\u8499\u53e4\u8bdd\u548c\u4ed6\u8bf4\u7684\u3002 \u3000\u3000\u5c60\u9f99\u4e00\u65f6\u95f4\u5374\u6ca1\u60f3\u5230\uff0c\u5218\u743c\u59d1\u867d\u7136\u542c\u4e0d\u61c2\u4ed6\u8bf4\u7684\u8499\u53e4\u8bdd\uff0c\u5173\u5728\u7262\u623f\u7684\u674e\u601d\u5357\u5374\u662f\u542c\u5f97\u61c2 \u7684\uff01 \u3000\u3000\u6b64\u65f6\u674e\u601d\u5357\u5df2\u7ecf\u786e\u5b9e\u77e5\u9053\u5218\u743c\u59d1\u662f\u6765\u6551\u4ed6\u4e86\uff0c\u867d\u7136\u8fd8\u672a\u77e5\u9053\u5979\u662f\u4ec0\u4e48\u4eba\uff0c\u4f46\u5df2\u662f\u65e0\u6687\u601d \u7d22\uff0c\u7acb\u5373\u53eb\u9053\uff1a\u201c\u8d76\u5feb\u52a8\u624b\uff0c\u6740\u90a3\u770b\u5b88\uff01\u201d \u3000\u3000\u8bdd\u72b9\u672a\u4e86\uff0c\u90a3\u4e2a\u770b\u5b88\u5df2\u662f\u671d\u7740\u5218\u743c\u59d1\u5148\u6251\u8fc7\u6765\uff0c\u9a82\u9053\uff1a\u201c\u597d\u5927\u80c6\u7684\u81ed\u4e2b\u5934\uff01\u201d\u4ed6\u7684\u6c49\u8bed \u8bf4\u5f97\u751f\u786c\u4e4b\u6781\uff0c\u4f46\u5374\u8bc1\u660e\u4e86\u4ed6\u5e76\u975e\u4e0d\u61c2\u6c49\u8bed\u3002 \u3000\u3000\u5218\u743c\u59d1\u4fa7\u8eab\u4e00\u95ea\uff0c\u62bd\u51fa\u5200\u6765\uff0c\u4e00\u62db\u201c\u9f99\u98de\u51e4\u821e\u201d\uff0c\u5411\u90a3\u770b\u5b88\u65a9\u53bb\u3002\u5979\u672c\u6765\u662f\u6263\u7740\u5c60\u9f99\u7684 \u8109\u95e8\u7684\uff0c\u52a8\u8d77\u624b\u6765\uff0c\u5f53\u7136\u5c31\u4e0d\u80fd\u4e0d\u653e\u5f00\u5c60\u9f99\u4e86\u3002 \u3000\u3000\u8fd9\u4e2a\u770b\u5b88\u662f\u62d6\u96f7\u624b\u4e0b\u7684\u6b66\u58eb\uff0c\u7cbe\u5e72\u6454\u89d2\u4e4b\u6280\uff0c\u8fd1\u8eab\u640f\u6597\u6b63\u662f\u4ed6\u7684\u6240\u957f\u3002\u5218\u743c\u59d1\u4e00\u5200\u65a9 \u7a7a\uff0c\u8fd9\u4e2a\u770b\u5b88\u7acb\u5373\u4e00\u4e2a\u201c\u7a7f\u638c\u201d\u53cd\u5708\uff0c\u538b\u5979\u624b\u8155\uff0c\u540c\u65f6\u53f3\u8db3\u4e00\u52fe\uff0c\u60f3\u628a\u5218\u743c\u59d1\u8dcc\u7ffb\u3002 \u3000\u3000\u5218\u743c\u59d1\u7684\u5200\u6cd5\u4e5f\u662f\u751a\u4e3a\u72e0\u8fa3\uff0c\u5200\u968f\u8eab\u8f6c\uff0c\u4f7f\u51fa\u4e86\u56db\u65b9\u76d8\u65a9\u7684\u201c\u4e94\u864e\u65ad\u95e8\u5200\u201d\uff0c\u8fd9\u662f\u5979\u7684 \u5bb6\u4f20\u7edd\u6280\uff0c\u5b9c\u5b88\u5b9c\u653b\uff0c\u4e13\u7834\u64d2\u62ff\u624b\u7684\u3002\u4e0d\u8bba\u5bf9\u65b9\u4ece\u54ea\u4e2a\u65b9\u4f4d\u653b\u6765\uff0c\u90fd\u975e\u4e2d\u5200\u4e0d\u53ef\uff01 \u3000\u3000\u8499\u5360\u7684\u201c\u6454\u89d2\u201d\u548c\u4e2d\u539f\u6b66\u5b66\u4e2d\u7684\u201c\u64d2\u62ff\u624b\u201d\u624b\u6cd5\u867d\u7136\u6709\u5f02\uff0c\u539f\u7406\u5374\u662f\u76f8\u540c\uff0c\u5218\u743c\u59d1\u6b63\u81ea \u6b22\u559c\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u53ef\u662f\u4f60\u9001\u4e0a\u6765\u7ed9\u6211\u65a9\u7684\uff01\u201d\u4e0d\u6599\u5c31\u5728\u5979\u4e00\u5200\u65ab\u53bb\u4e4b\u65f6\uff0c\u5ffd\u89c9\u8111\u540e\u98ce \u751f\uff0c\u5c60\u9f99\u4e00\u638c\u5411\u5979\u80cc\u5fc3\u6253\u4e0b\u3002 \u3000\u3000\u5218\u743c\u59d1\u53ea\u89c9\u80cc\u5fc3\u4e00\u9635\u75bc\u75db\uff0c\u4f46\u5374\u6ca1\u6709\u53d7\u4f24\u3002\u539f\u6765\u5c60\u9f99\u6b64\u65f6\u521a\u597d\u8fd0\u6c14\u51b2\u5173\uff0c\u81ea\u884c\u89e3\u4e86\u7a74 \u9053\uff0c\u4f46\u529f\u529b\u5374\u53ea\u662f\u6062\u590d\u4e86\u4e00\u4e8c\u5206\u3002 \u3000\u3000\u5218\u743c\u59d1\u53cd\u624b\u4e00\u638c\uff0c\u628a\u5c60\u9f99\u6253\u7ffb\uff0c\u53f3\u624b\u7684\u67f3\u53f6\u5200\u4ecd\u7136\u4f7f\u51fa\u76d8\u65a9\u7684\u62db\u5f0f\u3002\u4f46\u5979\u7684\u6b66\u529f\u6bd5\u7adf\u706b \u5019\u672a\u7eaf\uff0c\u5fc3\u96be\u4e24\u7528\uff0c\u8fd9\u4e00\u62db\u56db\u65b9\u76d8\u65a9\u5c31\u4e0d\u80fd\u8fd0\u7528\u81ea\u5982\u3001\u6beb\u65e0\u7834\u7efd\u4e86\u3002 \u3000\u3000\u7cbe\u4e8e\u6454\u89d2\u7684\u597d\u624b\u5584\u4e8e\u5229\u7528\u5bf9\u65b9\u7684\u7834\u7efd\uff0c\u4e00\u89c1\u6709\u9699\u53ef\u4e58\uff0c\u7acb\u5373\u94bb\u5165\u3002\u5218\u743c\u59d1\u4e00\u5200\u4ece\u90a3\u6b66\u58eb \u7684\u989d\u65c1\u524a\u8fc7\uff0c\u76f8\u5dee\u6beb\u5398\uff0c\u6ca1\u6709\u65ab\u4e2a\u6b63\u7740\u3002\u5374\u7ed9\u90a3\u4e2a\u6b66\u58eb\u4f7f\u51fa\u4e86\u4e00\u4e2a\u201c\u80a9\u8f66\u5f0f\u201d\uff0c\u8eab\u8eaf\u4e00\u77ee\uff0c \u628a\u5218\u743c\u59d1\u4ece\u4ed6\u7684\u80a9\u5934\u6454\u8fc7\u53bb\u3002 \u3000\u3000\u90a3\u6b66\u571f\u54c8\u54c8\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u6f02\u4eae\u7684\u5c0f\u59d1\u5a18\uff0c\u6211\u5012\u662f\u820d\u4e0d\u5f85\u6740\u4f60\u5462\uff01\u201d\u6b63\u8981\u8fc7\u53bb\u64d2\u62ff\u5218 \u743c\u59d1\uff0c\u4e0d\u6599\u7b11\u58f0\u672a\u7edd\uff0c\u5ffd\u5730\u4e00\u9897\u5c0f\u5c0f\u7684\u77f3\u5b50\u98de\u6765\uff0c\u6b63\u4e2d\u4ed6\u819d\u76d6\u7684\u201c\u73af\u8df3\u7a74\u201d\uff0c\u767b\u65f6\u5c31\u628a\u4ed6\u53d8 \u4f5c\u4e86\u6eda\u5730\u846b\u82a6\u3002 \u3000\u3000\u5218\u743c\u59d1\u4e00\u4e2a\u201c\u9ca4\u9c7c\u6253\u633a\u201d\u8df3\u8d77\u8eab\u6765\uff0c\u53d1\u73b0\u90a3\u4e2a\u6b66\u571f\u5df2\u7ecf\u5012\u5728\u5730\u4e0a\uff0c\u8fd9\u624d\u77e5\u9053\u662f\u6709\u4eba\u6697\u4e2d \u5e2e\u5fd9\uff0c\u8fc5\u5373\u4e00\u5200\u780d\u4e0b\uff0c\u53d6\u4e86\u4ed6\u7684\u6027\u547d\u3002 \u3000\u3000\u5c60\u9f99\u53eb\u9053\uff1a\u201c\u743c\u59d1\uff0c\u5feb\u8d70\uff01\u6211\u662f\u4e3a\u4e86\u4f60\u597d\uff0c\u624d\u963b\u6b62\u4f60\u6740\u4e86\u8fd9\u6b66\u58eb\u7684\u3002\u5982\u4eca\u4f60\u5df2\u505a\u4e86\u51fa \u6765\uff0c\u518d\u4e0d\u8d70\u5c31\u8981\u9001\u547d\u4e86\u3002\u4f60\u53ef\u77e5\u9053\u8fd9\u9644\u8fd1\u57cb\u4f0f\u6709\u8bb8\u591a\u654c\u4eba\uff0c\u4e00\u7ed9\u4ed6\u4eec\u53d1\u89c9\uff0c\u4f60\u662f\u51b3\u8ba1\u96be\u9003\uff01 \u6551\u674e\u601d\u5357\u4e8b\uff0c\u4f60\u4ea4\u7ed9\u6211\u597d\u4e86\u3002\u5feb\u8d70\uff0c\u5feb\u8d70\u5427\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u53eb\u9053\uff1a\u201c\u522b\u4fe1\u4ed6\u7684\u8bdd\uff0c\u4ed6\u662f\u9a97\u4f60\u7684\uff01\u201d \u3000\u3000\u5c60\u9f99\u98a4\u58f0\u53eb\u9053\uff1a\u201c\u743c\u59d1\uff0c\u522b\u542c\u5916\u4eba\u7684\u6311\u62e8\uff0c\u54b1\u4eec\u5fc5\u7adf\u662f\u6709\u8fc7\u6d77\u8a93\u5c71\u76df\u7684\u554a\uff01\u201d \u3000\u3000\u8fd9\u8bdd\u542c\u8fdb\u674e\u601d\u5357\u7684\u8033\u4e2d\uff0c\u5012\u662f\u4e0d\u89c9\u7cca\u6d82\u4e86\u3002\u201c\u8fd9\u5973\u5b50\u662f\u4ed6\u7684\u4ec0\u4e48\u4eba\u5462\uff1f\u83ab\u975e\u4e5f\u662f\u50cf\u5a49\u59b9 \u4e00\u6837\uff0c\u66fe\u7ecf\u4e0a\u8fc7\u4ed6\u7684\u5f53\u7684\u3002\u201d \u3000\u3000\u5218\u743c\u59d1\u5fc3\u70e6\u610f\u4e71\uff0c\u559d\u9053\uff1a\u201c\u95ed\u4e0a\u4f60\u7684\u81ed\u5634\uff0c\u73b0\u5728\u6211\u8fd8\u6ca1\u6709\u529f\u592b\u6740\u4f60\uff01\u201d\u5c11\u5973\u5bf9\u7b2c\u4e00\u4e2a\u604b \u4eba\u603b\u662f\u96be\u514d\u6709\u70b9\u611f\u60c5\u7684\uff0c\u662f\u4ee5\u5979\u867d\u7136\u5341\u5206\u75db\u6068\u5c60\u9f99\uff0c\u5374\u8fd8\u662f\u4e0d\u5fcd\u4e0b\u624b\u3002\u5fc3\u70e6\u610f\u4e71\u4e4b\u9645\uff0c\u4e00\u65f6 \u95f4\u5374\u662f\u672a\u66fe\u60f3\u5230\uff0c\u5c60\u9f99\u672c\u6765\u662f\u7ed9\u5979\u7528\u72ec\u95e8\u624b\u6cd5\u70b9\u4e86\u7a74\u9053\u7684\uff0c\u4f55\u4ee5\u5374\u80fd\u81ea\u884c\u89e3\u4e86\u3002 \u3000\u3000\u5218\u743c\u59d1\u5728\u90a3\u770b\u5b88\u7684\u8eab\u4e0a\u641c\u51fa\u9501\u5319\uff0c\u4fbf\u53bb\u6253\u5f00\u7262\u95e8\uff0c\u5ffd\u89c9\u80cc\u540e\u5fae\u98ce\u98d2\u7136\uff0c\u80a9\u5934\u5ffd\u5730\u706b\u8fa3\u8fa3 \u7684\u4f5c\u75db\uff0c\u539f\u6765\u662f\u6709\u4e00\u4e2a\u4eba\u4ece\u5979\u540e\u9762\u6251\u6765\uff0c\u5c16\u5229\u7684\u6307\u7532\u63d2\u4f24\u4e86\u5979\u7684\u76ae\u8089\u3002 \u3000\u3000\u5218\u743c\u59d1\u5927\u60ca\u4e4b\u4e0b\uff0c\u4e00\u4e2a\u201c\u8131\u65cb\u89e3\u7532\u201d\u6323\u8131\u90a3\u4eba\u7684\u638c\u63e1\uff0c\u9a82\u9053\uff1a\u201c\u597d\u4e2a\u72e0\u5fc3\u7684\u8d3c\u5b50\uff0c\u4f60\u8fd8 \u60f3\u5bb3\u6211\u5417\uff01\u201d\u56de\u8fc7\u5934\u6765\uff0c\u53ea\u89c1\u662f\u4e00\u4e2a\u62ab\u5934\u6563\u53d1\u7684\u5973\u4eba\uff0c\u5e76\u975e\u5c60\u9f99\u3002 \u3000\u3000\u539f\u6765\u8fd9\u4e2a\u5973\u4eba\u662f\u90a3\u4e2a\u770b\u5b88\u7684\u59bb\u5b50\uff0c\u8d1f\u8d23\u770b\u5b88\u5973\u7262\u7684\u3002\u548c\u6768\u5a49\u4e0e\u97e9\u4f69\u745b\u88ab\u56da\u7684\u5973\u7262\u6b63\u662f\u548c \u674e\u601d\u5357\u8fd9\u95f4\u7262\u623f\u76f8\u90bb\u3002 \u3000\u3000\u5c60\u9f99\u53eb\u9053\uff1a\u201c\u743c\u59d1\uff0c\u4f60\u600e\u4e48\u8001\u662f\u7591\u5fc3\u6211\u8981\u5bb3\u4f60\u5462\uff0c\u6211\u5b9e\u5728\u662f\u8981\u6551\u4f60\uff0c\u53ef\u60dc\u6211\u73b0\u5728\u662f\u529b\u4e0d \u4ece\u5fc3\u3002\u4f60\u5feb\u8d70\u5462\uff0c\u5c31\u4f1a\u6709\u4eba\u6765\u7684\u4e86\uff01\u4f60\u8d70\u4e86\uff0c\u6211\u62fc\u4e86\u8fd9\u6761\u6027\u547d\u4e0e\u4f60\u62c5\u5f53\uff01\u201d \u3000\u3000\u5218\u743c\u59d1\u7ed9\u4ed6\u5435\u5f97\u5fc3\u4e2d\u70e6\u8e81\uff0c\u8fed\u9047\u9669\u62db\uff0c\u51e0\u4e4e\u7ed9\u90a3\u5973\u4eba\u6293\u4f24\u3002 \u3000\u3000\u597d\u5728\u90a3\u628a\u94c1\u9501\u4e1a\u5df2\u6253\u5f00\uff0c\u53ea\u542c\u5f97\u201c\u5494\u5693\u201d\u4e00\u58f0\uff0c\u94c1\u9501\u6389\u4e0b\uff0c\u7262\u95e8\u5f00\u5904\uff0c\u674e\u601d\u5357\u8d70\u4e86\u51fa \u6765\u3002 \u3000\u3000\u90a3\u4e2a\u5973\u4eba\u867d\u7136\u662f\u5728\u75af\u72c2\u7684\u72b6\u6001\u4e4b\u4e2d\uff0c\u4e5f\u77e5\u653e\u6389\u201c\u91cd\u72af\u201d\uff0c\u975e\u540c\u5c0f\u53ef\uff0c\u53e3\u4e2d\u5927\u53eb\uff1a\u201c\u6765 \u4eba\uff0c\u6765\u4eba\u54ea\uff01\u201d\u624b\u5e95\u653e\u677e\u743c\u59d1\uff0c\u500f\u5730\u5c31\u5411\u674e\u601d\u5357\u6251\u53bb\uff01 \u3000\u3000\u674e\u601d\u5357\u811a\u6b65\u4e00\u4e2a\u8dc4\u8e09\uff0c\u8e0f\u51fa\u4e86\u201c\u9189\u516b\u4ed9\u201d\u7684\u6b65\u6cd5\uff0c\u6ca1\u6709\u7ed9\u5979\u6293\u7740\uff0c\u90a3\u5973\u4eba\u6b63\u8981\u518d\u6251\u8fc7 \u53bb\uff0c\u4e00\u679a\u77f3\u5b50\u98de\u6765\uff0c\u521a\u597d\u6253\u7740\u4e86\u5979\u7684\u540e\u5fc3\u9ebb\u7a74\uff0c\u8fd9\u4e2a\u75af\u72c2\u7684\u5973\u4eba\u767b\u65f6\u4e5f\u50cf\u5979\u7684\u4e08\u592b\u521a\u624d\u90a3 \u6837\uff0c\u5012\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u5c60\u9f99\u89c1\u6b64\u60c5\u5f62\uff0c\u5fc3\u4e2d\u4e00\u559c\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u4ee5\u674e\u601d\u5357\u7684\u672c\u9886\uff0c\u51b3\u6ca1\u6709\u5bb3\u6015\u8fd9\u4e2a\u5973\u4eba\u7684\u9053 \u7406\uff0c\u4e3a\u4ec0\u4e48\u4ed6\u521a\u624d\u4e0d\u80fd\u8fd8\u624b\uff0c\u53ea\u80fd\u95ea\u5f00\uff1f\u83ab\u975e\u662f\u9f99\u8c61\u6cd5\u738b\u5df2\u7ecf\u5728\u4ed6\u7684\u8eab\u4e0a\u505a\u4e86\u5b50\u811a\uff0c\u8fde\u6211\u4e5f \u672a\u77e5\u9053\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u8d70\u4e86\u51fa\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u59d1\u5a18\uff0c\u8fd9\u4e2a\u4eba\u2014\u2014\u201d \u3000\u3000\u5218\u743c\u59d1\u9053\uff1a\u201c\u6211\u73b0\u5728\u624d\u77e5\u9053\u4ed6\u662f\u5978\u8d3c\uff0c\u674e\u76df\u4e3b\uff0c\u4f60\u610f\u6b32\u5982\u4f55\u5904\u7f6e\uff0c\u968f\u4f60\u7684\u4fbf\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u770b\u51fa\u5218\u743c\u59d1\u5e76\u65e0\u8981\u6740\u5c60\u9f99\u4e4b\u610f\uff0c\u8bf4\u9053\uff1a\u201c\u81ea\u4f5c\u5b7d\uff0c\u4e0d\u53ef\u6d3b\uff01\u5218\u59d1\u5a18\u4eca\u65e5\u9976\u4f60\u4e0d \u6b7b\uff0c\u4f60\u53ef\u5f97\u597d\u81ea\u4e3a\u4e4b\uff0c\u5426\u5219\u8fd9\u4e24\u53e5\u8bdd\u5c31\u8981\u5e94\u5728\u4f60\u7684\u8eab\u4e0a\u4e86\uff01\u201d\u51b7\u51b7\u7684\u4ece\u5c60\u9f99\u8eab\u8fb9\u8d70\u8fc7\uff0c\u53eb \u9053\uff1a\u201c\u5a49\u59b9\uff0c\u97e9\u59d1\u5a18\uff01\u201d \u3000\u3000\u6768\u5a49\u5e94\u9053\uff1a\u201c\u5357\u54e5\uff0c\u4f60\u6ca1\u4e8b\u5417\uff1f\u6211\uff0c\u6211\u548c\u745b\u59d0\u2014\u2014\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u6211\u77e5\u9053\u5566\u3002\u6211\u5df2\u7ecf\u51fa\u6765\u4e86\uff0c\u662f\u4e00\u4f4d\u5973\u82f1\u96c4\u201d\u8bf4\u81f3\u6b64\u5904\uff0c\u674e\u601d\u5357\u628a\u773c\u5411\u5218\u743c \u59d1\u770b\u53bb\uff0c\u5218\u743c\u59d1\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4e0d\u6562\u3002\u6211\u662f\u5218\u5927\u4e3a\u7684\u59b9\u59b9\u3002\u6211\u7684\u54e5\u54e5\u662f\u891a\u4e91\u5cf0\u7684\u597d\u670b\u53cb\uff0c\u891a\u4e91 \u5cf0\u6628\u665a\u4e5f\u5df2\u6765\u8fc7\u8fd9\u91cc\u4e86\u3002\u4ed6\u624d\u662f\u8d39\u5c3d\u5fc3\u529b\u8425\u6551\u4f60\u4eec\u7684\u4eba\uff0c\u6211\u53ea\u662f\u9002\u9022\u5176\u4f1a\u800c\u5df2\u3002\u201d\u201c\u5218\u59d1\u5a18 \u6765\u6551\u54b1\u4eec\u7684\u3002\u891a\u4e91\u5cf0\u4ea6\u5df2\u5230\u4e86\u5927\u90fd\u4e86\u3002\u4ed6\u4eec\u7b49\u4e00\u4f1a\u513f\uff0c\u6211\u627e\u5230\u9501\u5319\uff0c\u9a6c\u4e0a\u5c31\u6765\uff01\u201d \u3000\u3000\u8eb2\u5728\u6697\u5904\u7684\u97e9\u8d85\uff0c\u542c\u4e86\u674e\u601d\u5357\u548c\u6768\u5a49\u7684\u5bf9\u8bdd\uff0c\u4e0d\u89c9\u6014\u4e86\u4e00\u6014\uff0c\u7591\u4e91\u987f\u8d77\u3002 \u3000\u3000\u4ed6\u662f\u4e2a\u5fc3\u601d\u7cbe\u7ec6\u7684\u4eba\uff0c\u542c\u4e86\u8fd9\u8bdd\uff0c\u4e0d\u7981\u60f3\u9053\uff1a\u201c\u6768\u59d1\u5a18\u7b2c\u4e00\u53e5\u8bdd\u4e3a\u4ec0\u4e48\u5c31\u95ee\u4ed6\u6709\u6ca1\u6709\u4e8b \u5462\uff1f\u6709\u4ec0\u4e48\u4e8b\uff1f\u770b\u5b88\u592b\u59bb\u90fd\u5df2\u7ed9\u6211\u6253\u7740\u7a74\u9053\uff0c\u5012\u4e0b\u53bb\u4e86\u3002\u6768\u59d1\u5a18\u662f\u4e2a\u8eab\u6000\u8273\u6280\u7684\u5973\u4e2d\u8c6a\u6770\uff0c \u96be\u9053\u5979\u8fd8\u542c\u4e0d\u51fa\u6765\u674e\u601d\u5357\u6ca1\u4e8b\uff1f\u674e\u601d\u5357\u8bf4\uff1a\u201c\u6211\u77e5\u9053\u5566\u3002\u201d\u4ed6\u53c8\u662f\u77e5\u9053\u4ec0\u4e48\u5462\uff1f\u54ce\u5440\uff0c\u4e0d \u597d\uff0c\u6050\u6015\uff0c\u6050\u6015\u662f\u2014\u2014\u201d \u3000\u3000\u97e9\u8d85\u5fc3\u77e5\u4e0d\u5999\uff0c\u65e0\u6687\u4ed4\u7ec6\u63a8\u6572\uff0c\u8fde\u5fd9\u73b0\u51fa\u8eab\u5f62\uff0c\u5feb\u6b65\u8d76\u53bb\uff01 \u3000\u3000\u674e\u601d\u5357\u4ece\u5c60\u9f99\u8eab\u65c1\u8d70\u8fc7\uff0c\u8d70\u5230\u90a3\u4e2a\u5973\u770b\u5b88\u7684\u65c1\u8fb9\uff0c\u6b63\u5728\u5f2f\u4e0b\u8170\u6765\uff0c\u627e\u5bfb\u9501\u5319\uff0c\u5c60\u9f99\u5ffd\u5730 \u4e00\u8dc3\u800c\u8d77\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u7684\u56e2\u5706\u7f8e\u68a6\u4e5f\u672a\u514d\u505a\u5f97\u592a\u65e9\u4e86\uff01\u201d\u51b7\u7b11\u58f0\u4e2d\uff0c\u624b\u8155\u4e00\u6296\uff0c\u547c\u7684 \u4e00\u58f0\uff0c\u4e00\u652f\u6bd2\u9f99\u9556\u5df2\u662f\u5411\u674e\u601d\u5357\u6253\u53bb\u3002 \u3000\u3000\u97e9\u8d85\u559d\u9053\uff1a\u201c\u5978\u8d3c\u6562\u4e0b\u6bd2\u624b\uff01\u201d\u4e00\u6296\u624b\u98de\u51fa\u4e86\u4e09\u679a\u77f3\u5b50\uff0c\u4e00\u679a\u77f3\u5b50\u6253\u90a3\u6bd2\u9f99\u9556\uff0c\u4e24\u679a\u77f3 \u5b50\u6253\u5c60\u9f99\u7684\u7a74\u9053\u3002 \u3000\u3000\u4e0d\u6599\uff0c\u53ea\u542c\u5f97\u201c\u9e23\u201d\u7684\u4e00\u58f0\uff0c\u90a3\u652f\u6bd2\u9f99\u9556\u53ea\u662f\u51c6\u5934\u7565\u6b6a\uff0c\u4ecd\u7136\u5411\u674e\u601d\u5357\u98de\u53bb\uff0c\u90a3\u8fb9\u77f3 \u5b50\uff0c\u548c\u6bd2\u9f99\u9556\u4e00\u78b0\uff0c\u5374\u7ed9\u53cd\u5f39\u56de\u6765\u3002\u968f\u5373\u542c\u5f97\u53ee\u53ee\u4e24\u58f0\uff0c\u6253\u5411\u5c60\u9f99\u7684\u90a3\u4e24\u679a\u77f3\u5b50\uff0c\u90fd\u7ed9\u4ed6\u7528 \u201c\u5f39\u6307\u795e\u901a\u201d\u7684\u529f\u592b\u5f39\u843d\u3002 \u3000\u3000\u539f\u6765\u5c60\u9f99\u7684\u529f\u529b\u6070\u597d\u5728\u8fd9\u4e2a\u65f6\u5019\u6e10\u590d\uff0c\u88ab\u5c01\u95ed\u7684\u7a74\u9053\u5168\u90e8\u7ed9\u4ed6\u89e3\u5f00\u4e86\u3002 \u3000\u3000\u5e78\u597d\u90a3\u652f\u6bd2\u9f99\u9556\u5931\u4e86\u51c6\u5934\uff0c\u4ece\u674e\u601d\u5357\u989d\u8fb9\u98de\u8fc7\uff0c\u6ca1\u6709\u6253\u7740\u3002\u4f46\u674e\u601d\u5357\u56e0\u4e3a\u4e8b\u5148\u5e76\u4e0d\u77e5\u9053 \u6709\u4e2a\u97e9\u8d85\u6697\u4e2d\u76f8\u52a9\uff0c\u4e60\u6b66\u4e4b\u4eba\uff0c\u9661\u9047\u5371\u9669\uff0c\u95ea\u8eb2\u4e43\u662f\u51fa\u4e8e\u672c\u80fd\uff0c\u4ed6\u7eb5\u8eab\u4e00\u8dc3\uff0c\u7528\u529b\u8fc7\u5ea6\uff0c\u7adf \u7136\u8dcc\u5012\u4e86\u3002 \u3000\u3000\u539f\u6765\u62d6\u96f7\u6781\u529f\u5fc3\u8ba1\uff0c\u674e\u601d\u5357\u3001\u6768\u5a49\u6b66\u529f\u9ad8\u5f3a\uff0c\u4ed6\u662f\u7d20\u6765\u6291\u6155\u7684\uff0c\u4ed6\u5c82\u80fd\u653e\u5fc3\u8ba9\u4e00\u5bf9\u672c\u9886 \u5e73\u5eb8\u7684\u592b\u59bb\u770b\u5b88\u4ed6\u4eec\uff1f\u662f\u4ee5\u65e9\u5c31\u5728\u7ed9\u674e\u3001\u6768\u548c\u97e9\u4f69\u745b\u7684\u8336\u996d\u4e4b\u4e2d\u52a0\u8fdb\u836f\u7269\uff0c\u8fd9\u79cd\u836f\u54c1\u65e0\u8272\u65e0 \u5473\uff0c\u540d\u4e3a\u201c\u9165\u9aa8\u6563\u201d\uff0c\u80fd\u591f\u4ee4\u4eba\u7b4b\u9165\u9aa8\u8f6f\uff0c\u591a\u597d\u5185\u529f\u4e5f\u4f7f\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u674e\u601d\u5357\u4e00\u8de4\u8dcc\u5012\uff0c\u8bc1\u660e\u4ed6\u7684\u5185\u529b\u5df2\u7ecf\u6d88\u5931\uff0c\u5c60\u9f99\u77e5\u9053\u81ea\u5df1\u6240\u6599\u4e0d\u5dee\uff0c\u72c2\u559c\u5927\u7b11\uff0c\u53eb\u9053\uff1a \u201c\u674e\u601d\u5357\uff0c\u4eca\u65e5\u662f\u4f60\u6b7b\u671f\u5230\u4e86\uff01\u201d \u3000\u3000\u5c60\u9f99\u5f39\u843d\u4e86\u97e9\u8d85\u7684\u4e24\u679a\u77f3\u5b50\uff0c\u72de\u7b11\u559d\u9053\uff1a\u201c\u6765\u800c\u4e0d\u5f80\u975e\u793c\u4e5f\uff01\u201d\u4e00\u6296\u624b\uff0c\u4e5f\u662f\u53d1\u51fa\u4e86\u4e24 \u652f\u6bd2\u9f99\u9556\uff0c\u53cd\u624b\u63b7\u7684\u6bd2\u9556\u6253\u97e9\u8d85\uff0c\u6b63\u624b\u98de\u51fa\u7684\u6bd2\u9556\u5219\u662f\u5c04\u5411\u674e\u601d\u5357\u3002\u6b63\u53cd\u624b\u53d1\u9556\uff0c\u65b9\u5411\u6070\u6070 \u76f8\u53cd\uff0c\u4f46\u4e24\u652f\u98de\u9556\uff0c\u90fd\u662f\u5c04\u5411\u4eba\u8eab\u8981\u5bb3\uff0c\u53c8\u72e0\u53c8\u51c6\uff01\u4ed6\u7684\u6697\u5668\u529f\u592b\u867d\u7136\u8bf4\u4e0d\u4e0a\u7089\u706b\u7eaf\u9752\uff0c\u4e5f \u8bf4\u5f97\u662f\u975e\u51e1\u51fa\u4f17\u7684\u4e86\u3002 \u3000\u3000\u97e9\u8d85\u662f\u6c49\u4eba\u4f8d\u536b\u4e2d\u7684\u4e00\u6d41\u9ad8\u624b\uff0c\u63d0\u8d77\u4e86\u539a\u80cc\u6734\u5200\uff0c\u53cd\u624b\u4e00\u78d5\uff0c\u201c\u94db\u201d\u7684\u4e00\u58f0\uff0c\u5c06\u90a3\u652f\u6bd2 \u9f99\u9556\u51fb\u843d\u3002\u4f46\u8165\u98ce\u6251\u9f3b\uff0c\u4e5f\u662f\u4e0d\u7531\u5f97\u4e00\u9635\u660f\u7729\uff0c\u5fc3\u91cc\u6697\u6697\u53eb\u4e86\u4e00\u58f0\u201c\u597d\u5389\u5bb3\u7684\u6697\u5668\u201d\uff0c\u8fde\u5fd9 \u5410\u51fa\u6d4a\u6c14\uff0c\u5438\u4e86\u4e00\u53e3\u65b0\u9c9c\u7684\u7a7a\u6c14\uff0c\u5b9a\u4e86\u5b9a\u795e\uff0c\u6251\u4e0a\u524d\u53bb\u3002 \u3000\u3000\u97e9\u8d85\u53ef\u4ee5\u62b5\u6321\u98de\u9556\uff0c\u674e\u601d\u5357\u53ef\u662f\u4e0d\u80fd\u4e86\uff0c\u4ed6\u7740\u4e86\u201c\u915e\u9aa8\u6563\u201d\u7684\u6bd2\uff0c\u5185\u529f\u6d88\u5931\uff0c\u771f\u529b\u65e0\u6cd5 \u8fd0\u7528\uff0c\u548c\u5bfb\u5e38\u4eba\u5df2\u662f\u5dee\u4e0d\u4e86\u591a\u5c11\uff0c\u4e00\u8de4\u6454\u5012\uff0c\u5c1a\u672a\u722c\u5f97\u8d77\u6765\u3002 \u3000\u3000\u6697\u5668\u7834\u7a7a\u4e4b\u58f0\u5c16\u9510\u6025\u901f\uff0c\u773c\u770b\u5c31\u8981\u6253\u5230\u674e\u601d\u5357\u7684\u8eab\u4e0a\uff0c\u674e\u601d\u5357\u5fc3\u5934\u4e00\u51c9\uff1a\u201c\u60f3\u4e0d\u5230\u6211\u5374 \u662f\u4e27\u5728\u8fd9\u5978\u8d3c\u624b\u4e0a\u3002\u201d \u3000\u3000\u97e9\u8d85\u6b64\u65f6\u65b9\u59cb\u91cd\u6444\u5fc3\u795e\uff0c\u98de\u6b65\u8dd1\u6765\u3002\u4f46\u4ed6\u4e0e\u674e\u601d\u5357\u4e4b\u95f4\u7684\u8ddd\u79bb\u5c1a\u5728\u5341\u6570\u4e08\u5916\uff0c\u60f3\u8981\u633d\u6551 \u674e\u601d\u5357\u7684\u6027\u547d\uff0c\u90a3\u5374\u662f\u51b3\u8ba1\u4e0d\u80fd\u7684\u4e86\u3002 \u3000\u3000\u773c\u770b\u674e\u601d\u5357\u5c31\u8981\u4e27\u547d\u6bd2\u9556\u4e4b\u4e0b\uff0c\u5ffd\u89c1\u4e00\u4e2a\u4eba\u5982\u7bad\u79bb\u5f26\uff01\u7a81\u7136\u6251\u5728\u674e\u601d\u5357\u7684\u8eab\u4e0a\u3002\u8fd9\u4e2a\u4eba \u4e0d\u662f\u522b\u4e2a\uff0c\u6b63\u662f\u7ad9\u5728\u674e\u601d\u5357\u8eab\u65c1\u4e0d\u8fdc\u4e4b\u5904\u7684\u5218\u743c\u59d1\uff01 \u3000\u3000\u5218\u743c\u59d1\u662f\u820d\u4e86\u6027\u547d\u6765\u536b\u62a4\u674e\u601d\u5357\u7684\uff0c\u5f53\u7136\u5979\u4e0d\u613f\u610f\u8ba9\u6bd2\u9556\u6253\u7740\u81ea\u5df1\uff0c\u5728\u548c\u8eab\u6251\u4e0b\u4e4b\u65f6\uff0c \u67f3\u53f6\u5200\u5df2\u662f\u98de\u4e86\u51fa\u53bb\uff0c\u548c\u90a3\u652f\u6bd2\u9f99\u9556\u78b0\u4e2a\u6b63\u7740\uff01 \u3000\u3000\u53ef\u60dc\u53cc\u65b9\u7684\u529f\u529b\u6bd5\u7adf\u662f\u76f8\u5dee\u751a\u8fdc\uff0c\u867d\u7136\u78b0\u4e2a\u6b63\u7740\uff0c\u5374\u662f\u67f3\u53f6\u5200\u843d\u4e86\u4e0b\u6765\uff0c\u6bd2\u9f99\u9556\u5374\u4ecd\u7136 \u5411\u524d\u98de\u53bb\u3002 \u3000\u3000\u8fd9\u672c\u6765\u4e5f\u662f\u5728\u5218\u743c\u59d1\u610f\u6599\u4e4b\u4e2d\uff0c\u5979\u5c31\u662f\u6050\u6015\u81ea\u5df1\u6253\u4e0d\u843d\u6bd2\u9f99\u9556\u8fd9\u624d\u6251\u5728\u674e\u601d\u5357\u8eab\u4e0a\u7684\u3002 \u3000\u3000\u674e\u601d\u5357\u4e00\u54ac\u820c\u5c16\uff0c\u4f7f\u51fa\u6b8b\u5b58\u7684\u6c14\u529b\uff0c\u4e00\u4e2a\u9e5e\u5b50\u7ffb\u8eab\uff0c\u60f3\u628a\u5218\u743c\u59d1\u538b\u5728\u4e0b\u9762\uff0c\u81ea\u5df1\u7ffb\u4e86\u4e0a \u6765\uff0c\u62b5\u53d7\u8fd9\u652f\u6bd2\u9556\u3002\u53ef\u662f\u5df2\u7ecf\u8fdf\u4e86\uff0c\u90a3\u652f\u6bd2\u9f99\u9556\u5df2\u7ecf\u4ece\u5218\u743c\u59d1\u7684\u80a9\u5934\u64e6\u8fc7\uff0c\u9556\u5c16\u5df2\u7ecf\u5212\u4f24\u5979 \u7684\u76ae\u8089\u4e86\u3002\u8fd9\u8fd8\u5e78\u4e8f\u662f\u7531\u4e8e\u6709\u4e86\u674e\u601d\u5357\u7684\u8fd9\u4e00\u6323\u624e\uff0c\u5426\u5219\u8fd9\u652f\u6bd2\u9556\u53ea\u6015\u5df2\u7ecf\u63d2\u8fdb\u4e86\u5218\u743c\u59d1\u7684 \u5589\u5499\uff01 \u3000\u3000\u6bd2\u9f99\u9556\u662f\u89c1\u8840\u5c01\u5589\u7684\u6697\u5668\uff0c\u5218\u743c\u59d1\u5636\u58f0\u53eb\u9053\uff1a\u201c\u5c60\u9f99\uff0c\u4f60\uff0c\u4f60\u597d\u72e0\uff01\u201d\u53cc\u624b\u4e00\u677e\uff0c\u6eda\u8fc7 \u4e86\u4e00\u8fb9\uff0c\u674e\u601d\u5357\u7ad9\u4e86\u8d77\u6765\uff0c\u4e0d\u7531\u5f97\u5446\u4e86\uff01 \u3000\u3000\u5c60\u9f99\u51b7\u7b11\u9053\uff1a\u201c\u743c\u59d1\uff0c\u8fd9\u53ef\u602a\u4e0d\u5f97\u6211\u3002\u8c01\u53eb\u4f60\u8fd9\u6837\u50bb\uff01\u7adf\u8981\u820d\u4e86\u81ea\u5df1\u7684\u6027\u547d\u536b\u62a4\u8fd9\u5c0f\u5b50 \u5462\uff1f\u4f60\u597d\u597d\u53bb\u5427\uff0c\u6211\u6740\u4e86\u8fd9\u5c0f\u5b50\uff0c\u53eb\u4f60\u4eec\u53ef\u4ee5\u5728\u9ec4\u6cc9\u8def\u4e0a\u4f5c\u4f34\uff0c\u6210\u5168\u4f60\u7684\u5fc3\u613f\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u5446\u82e5\u6728\u9e21\uff0c\u770b\u89c1\u5c60\u9f99\u6251\u4e86\u4e0a\u6765\uff0c\u84e6\u5730\u4e00\u9192\uff0c\u201c\u5478\u201d\u7684\u4e00\u58f0\u65a5\u9053\uff1a\u201c\u597d\uff0c\u597d\u5a01\u98ce \u554a\uff01\u6211\u73b0\u5728\u4e0d\u662f\u4f60\u7684\u5bf9\u624b\uff0c\u4f60\u6740\u4e86\u6211\u5427\uff01\u201d \u3000\u3000\u5c60\u9f99\u72de\u7b11\u9053\uff1a\u201c\u4f60\u8981\u6b7b\u8fd8\u4e0d\u5bb9\u6613\uff0c\u4f46\u8001\u5b50\u5374\u8fd8\u4e0d\u60f3\u6740\u4f60\uff01\u201d\u8981\u77e5\u674e\u601d\u5357\u662f\u62d6\u96f7\u7684\u4eba\u8d28\uff0c \u5c60\u9f99\u662f\u53ea\u6562\u4f24\u4ed6\uff0c\u53ef\u8fd8\u4e0d\u6562\u6740\u4ed6\u7684\u3002\u4ed6\u521a\u624d\u53d1\u51fa\u6bd2\u9f99\u9556\u5c04\u674e\u601d\u5357\uff0c\u5fc3\u91cc\u5df2\u7ecf\u76d8\u7b97\u597d\u4e86\uff0c\u51c6\u5907 \u674e\u601d\u5357\u4e00\u53d7\u4e86\u4f24\uff0c\u5c31\u7ed9\u4ed6\u89e3\u836f\u7684\u3002\u867d\u7136\u65e9\u6709\u51c6\u5907\uff0c\u53d1\u9556\u4e4b\u65f6\uff0c\u4ed6\u5fc3\u4e2d\u53ef\u4e5f\u8fd8\u662f\u6709\u70b9\u5fd0\u5fd1\u4e0d \u5b89\uff0c\u6050\u6015\u4e07\u4e00\u89e3\u6551\u4e0d\u53ca\uff0c\u674e\u601d\u5357\u6b7b\u4e86\uff0c\u4ed6\u5728\u62d6\u96f7\u9762\u524d\u53ef\u662f\u4e0d\u597d\u4ea4\u4ee3\u3002\u6b64\u9645\uff0c\u674e\u601d\u5357\u5df2\u7ecf\u5728\u4ed6 \u638c\u63e1\u4e4b\u4e2d\uff0c\u4ed6\u81ea\u662f\u7528\u4e0d\u7740\u6025\u6025\u5c31\u4e0b\u6bd2\u624b\u4e86\u3002 \u3000\u3000\u4e5f\u662f\u5e78\u4e8f\u5c60\u9f99\u6709\u6b64\u987e\u8651\uff0c\u4e0d\u6562\u4fbf\u4e0b\u6740\u624b\u3002\u5c31\u5728\u4ed6\u6b63\u8981\u6b3a\u5230\u674e\u601d\u5357\u8eab\u524d\uff0c\u70b9\u674e\u601d\u5357\u7a74\u9053\u4e4b \u9645\uff0c\u97e9\u8d85\u4e1a\u5df2\u8d76\u5230\uff01 \u3000\u3000\u97e9\u8d85\u6df1\u6050\u5c60\u9f99\u4f24\u4e86\u674e\u601d\u5357\u7684\u6027\u547d\uff0c\u4eba\u672a\u5230\uff0c\u98de\u5200\u5148\u5230\uff0c\u5c60\u9f99\u62d4\u51fa\u5251\u6765\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u97e9 \u8d85\uff0c\u4f60\u5f53\u6211\u73b0\u5728\u8fd8\u6015\u4f60\u5417\u3002\u201d\u53cd\u624b\u4e00\u62e8\uff0c\u628a\u90a3\u70b3\u98de\u5200\u78b0\u5f97\u53cd\u6253\u56de\u53bb\uff0c\u968f\u5373\u5927\u58f0\u53eb\u9053\uff1a\u201c\u6709\u5978 \u7ec6\uff0c\u6765\u4eba\u54ea\uff01\u201d \u3000\u3000\u98de\u5200\u662f\u97e9\u8d85\u53d1\u51fa\u53bb\u7684\uff0c\u53cd\u6253\u56de\u6765\u7684\u90a3\u80a1\u529b\u9053\u5374\u662f\u6bd4\u5730\u5c04\u51fa\u7684\u5200\u9053\u66f4\u5927\uff0c\u97e9\u8d85\u4e00\u542c\u8fd9\u53d1\u9556 \u4e4b\u58f0\uff0c\u77e5\u9053\u5c60\u9f99\u529f\u529b\u786e\u662f\u5728\u4ed6\u4e4b\u4e0a\uff0c\u4e0d\u6562\u786c\u63a5\uff0c\u970d\u7684\u4e00\u4e2a\u201c\u98ce\u70b9\u5934\u201d\uff0c\u98de\u5200\u4ece\u4ed6\u5934\u9876\u98de\u8fc7\uff0c \u5c06\u4ed6\u5934\u4e0a\u6234\u7684\u6b66\u58eb\u5e3d\u4e5f\u524a\u843d\u4e86\u3002 \u3000\u3000\u5c60\u9f99\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u77e5\u9053\u5389\u5bb3\u4e86\u5417\uff1f\u4f60\u8981\u8ba8\u6b7b\uff0c\u5f88\u597d\uff0c\u90a3\u6211\u4e5f\u53ef\u4ee5\u4e00\u5e76\u6210\u5168\u4f60\uff01\u201d\u97e9\u8d85\u559d \u9053\uff1a\u201c\u4eca\u65e5\u4e0d\u662f\u4f60\u6b7b\uff0c\u4fbf\u662f\u6211\u4ea1\uff01\u201d\u4e00\u62db\u201c\u4e94\u5cb3\u5f00\u5c71\u201d\uff0c\u72e0\u72e0\u7684\u5411\u5c60\u9f99\u5288\u53bb\uff01\u660e\u77e5\u4e0d\u654c\uff0c\u53cd \u800c\u6ca1\u6709\u987e\u5fcc\uff0c\u51fa\u624b\u5168\u7528\u653b\u62db\uff0c\u594b\u4e0d\u987e\u8eab\uff0c\u62fc\u7740\u4e0e\u5c60\u9f99\u4e24\u8d25\u4ff1\u4f24\u3002 \u3000\u3000\u5c60\u9f99\u53cd\u624b\u4e00\u5251\uff0c\u5212\u4e86\u4e00\u9053\u5706\u5f27\uff0c\u8f7b\u63cf\u6de1\u5199\u7684\u5316\u89e3\u4e86\u97e9\u8d85\u7684\u653b\u52bf\u3002\u4f46\u97e9\u8d85\u7684\u7b2c\u4e8c\u5200\u7b2c\u4e09\u5200 \u4ecd\u7136\u662f\u6beb\u4e0d\u9632\u5b88\u7ee7\u7eed\u5411\u4ed6\u72e0\u653b\uff0c\u653b\u52bf\u6709\u5982\u957f\u6c5f\u5927\u6cb3\uff0c\u6eda\u6eda\u800c\u4e0a\uff0c\u4ed6\u8fd9\u6837\u62fc\u547d\u7684\u6253\u6cd5\uff0c\u5012\u662f\u4f7f \u5f97\u5c60\u9f99\u4e0d\u80fd\u4e0d\u6709\u70b9\u987e\u5fcc\u4e86\u3002 \u3000\u3000\u5c60\u9f99\u53e0\u58f0\u53eb\u9053\uff1a\u201c\u6765\u4eba\uff0c\u5feb\u6765\u4eba\u5440\uff01\u201d\u5fc3\u60f3\uff1a\u201c\u5e78\u4e8f\u674e\u601d\u5357\u5185\u529f\u5df2\u5931\uff0c\u8981\u8dd1\u4e5f\u8dd1\u4e0d\u6389 \u7684\uff01\u201d \u3000\u3000\u56da\u7981\u674e\u601d\u5357\u7684\u5730\u65b9\u5341\u5206\u79d8\u5bc6\uff0c\u5728\u201c\u56fd\u5e08\u5e9c\u201d\u4e2d\u4e5f\u662f\u5217\u4e3a\u201c\u7981\u5730\u201d\u7684\u3002\u662f\u4ee5\u4ed6\u4eec\u867d\u7136\u5df2\u7ecf \u5f00\u59cb\u6253\u6597\uff0c\u5c60\u9f99\u5728\u653e\u58f0\u5927\u558a\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u5374\u8fd8\u662f\u672a\u6709\u4eba\u6765\u3002\u4e0d\u8fc7\u97e9\u8d85\u5fc3\u4e2d\u6e05\u695a\uff0c\u90a3\u4e9b\u8499\u53e4\u6b66 \u58eb\uff0c\u542c\u51fa\u4e86\u662f\u5c60\u9f99\u7684\u53eb\u58f0\u4e4b\u540e\uff0c\u81ea\u7136\u8fd8\u662f\u4f1a\u6765\u7684\uff0c\u97e9\u8d85\u4e00\u9762\u731b\u6253\uff0c\u4e00\u9762\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u674e\u76df \u4e3b\uff0c\u4f60\u6682\u65e5\u8eb2\u4e00\u8eb2\u5427\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u53ea\u6068\u81ea\u5df1\u5e2e\u4e0d\u4e86\u4ed6\u7684\u5fd9\uff0c\u4f46\u8981\u4ed6\u8eb2\u5f00\uff0c\u4ed6\u5374\u662f\u4e0d\u613f\u7684\u3002\u674e\u601d\u8d70\u8fc7\u53bb\u6276\u8d77\u743c\u59d1\uff0c\u8bf4 \u9053\uff1a\u201c\u5218\u59d1\u5a18\uff0c\u4f60\u8eab\u4e0a\u662f\u5426\u6709\u521b\u836f\uff01\u201d \u3000\u3000\u5218\u743c\u59d1\u9762\u5982\u91d1\u7eb8\uff0c\u5636\u58f0\u8bf4\u9053\uff1a\u201c\u522b\u7406\u6211\uff0c\u8d76\u5feb\u627e\u5f00\u5973\u7262\u7684\u7262\u95e8\u8981\u7d27\uff0c\u82b1\u738b\u4e4b\u5973\u5c0f\u7389\u513f\u662f \u54b1\u4eec\u81ea\u5df1\u4eba\u3002\u201d\u5979\u7684\u610f\u601d\u662f\u60f3\u674e\u601d\u5357\u548c\u6768\u5a49\u3001\u97e9\u4f69\u745b\u4e09\u4eba\u8d76\u5feb\u8eb2\u85cf\u8d77\u6765\uff0c\u627e\u5c0f\u7389\u513f\u5e2e\u5fd9\u63a9 \u62a4\uff0c\u4f46\u5374\u4e0d\u77e5\u5c0f\u7389\u513f\u6b64\u65f6\u5df2\u662f\u548c\u7236\u4eb2\u9003\u51fa\u5e9c\u4e2d\u53bb\u4e86\u3002 \u3000\u3000\u674e\u601d\u5357\u89c1\u5979\u8fd9\u526f\u6837\u5b50\uff0c\u77e5\u9053\u5df2\u662f\u65e0\u53ef\u6551\u6cbb\uff0c\u5fc3\u75db\u5982\u7ede\uff01 \u3000\u3000\u674e\u601d\u5357\u5f3a\u5fcd\u60b2\u75db\uff0c\u8bf4\u9053\uff1a\u201c\u5218\u59d1\u5a18\uff0c\u4f60\u53ef\u6709\u4ec0\u4e48\u4e8b\u8981\u6211\u505a\u7684\uff1f\u201d\u8fd9\u662f\u8bf7\u5979\u5429\u5490\u540e\u4e8b\u4e4b\u610f \u4e86\u3002 \u3000\u3000\u5218\u743c\u59d1\u9053\uff1a\u201c\u8bf7\u4f60\u544a\u8bc9\u891a\u4e91\u5cf0\uff0c\u4ed6\u4f1a\u77e5\u9053\u600e\u6837\u6599\u7406\u6211\u7684\u540e\u4e8b\u3002\u6211\u5bf9\u4e0d\u4f4f\u5730\uff0c\u4f46\u6211\u5df2\u7ecf\u5c3d \u4e86\u6211\u7684\u529b\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u4e0d\u77e5\u5185\u91cc\u56e0\u7531\uff0c\u53ea\u80fd\u70b9\u5934\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u6211\u4e00\u5b9a\u66ff\u4f60\u628a\u8bdd\u9001\u5230\u3002\u201d\u63a9\u9762\u56de\u8eab\uff0c\u5728\u90a3 \u5973\u770b\u5b88\u7684\u8eab\u4e0a\u641c\u51fa\u9501\u5319\uff0c\u4fbf\u5f80\u5973\u7262\u8dd1\u53bb\uff0c\u51c6\u5907\u6253\u5f00\u7262\u95e8\u3002 \u3000\u3000\u5c60\u9f99\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4e0d\u77e5\u6768\u5a49\u7684\u529f\u529b\u662f\u5426\u4ea6\u5df2\u6d88\u5931\uff1f\u201d\u5251\u6cd5\u4e00\u7d27\uff0c\u5530\u5530\u5530\u8fde\u73af\u4e09\u5251\uff0c\u903c\u5f00 \u97e9\u8d85\uff0c\u559d\u9053\uff1a\u201c\u674e\u601d\u5357\uff0c\u4f60\u7ed9\u6211\u8eba\u4e0b\u5427\uff01\u201d\u98de\u8eab\u75be\u63a0\uff0c\u4e00\u628a\u5411\u674e\u601d\u5357\u7684\u7435\u7436\u9aa8\u6293\u4e0b\u3002 \u3000\u3000\u9661\u542c\u5f97\u8111\u540e\u91d1\u5203\u5288\u98ce\u4e4b\u58f0\uff0c\u97e9\u8d85\u594b\u4e0d\u987e\u8eab\u7684\u53c8\u6251\u4e86\u4e0a\u6765\uff0c\u5c60\u9f99\u51b7\u7b11\u9053\uff1a\u201c\u6211\u770b\u4f60\u662f\u6d3b\u5f97 \u4e0d\u8010\u70e6\u4e86\uff01\u201d\u4ee5\u9038\u5f85\u52b3\uff0c\u957f\u5251\u5e73\u80f8\u523a\u51fa\uff0c\u8fd9\u4e00\u62db\u62ff\u634f\u65f6\u5019\uff0c\u6070\u5230\u597d\u5904\uff0c\u53ea\u542c\u5f97\u201c\u9e23\u201d\u7684\u4e00 \u58f0\uff0c\u97e9\u8d85\u864e\u53e3\u4e2d\u5251\uff0c\u539a\u80cc\u6734\u5200\u8131\u624b\u800c\u51fa\u3002 \u3000\u3000\u5c60\u9f99\u98de\u8d77\u4e00\u811a\uff0c\u628a\u97e9\u8d85\u8e22\u4e86\u4e00\u4e2a\u5927\u7ffb\uff0c\u6454\u51fa\u4e86\u6570\u4e08\u5f00\u5916\uff0c\u5c31\u5728\u6b64\u65f6\uff0c\u4eba\u58f0\u811a\u6b65\u58f0\u5608\u5608\u6742 \u6742\uff0c\u5df2\u662f\u6709\u4e94\u516d\u4e2a\u8499\u53e4\u6b66\u58eb\u8d76\u6765\u4e86\u3002 \u3000\u3000\u5c60\u9f99\u8ba4\u5f97\u4e3a\u9996\u7684\u4e24\u4e2a\u6b66\u58eb\u662f\u9f99\u8c61\u6cd5\u738b\u7684\u5f1f\u5b50\u963f\u535c\u5362\u548c\u547c\u9ece\u5962\uff0c\u5fc3\u4e2d\u5927\u559c\uff0c\u60f3\u9053\uff1a\u201c\u5373\u4f7f \u6768\u5a49\u529f\u529b\u4ecd\u5728\uff0c\u5979\u4e5f\u662f\u9003\u4e0d\u51fa\u6211\u7684\u638c\u5fc3\u7684\u4e86\uff01\u201d \u3000\u3000\u97e9\u8d85\u521a\u521a\u4e00\u4e2a\u201c\u9ca4\u9c7c\u6253\u633a\u201d\u8df3\u8d77\u8eab\u6765\uff0c\u963f\u535c\u5362\u5df2\u662f\u5411\u4ed6\u6251\u5230\uff0c\u97e9\u8d85\u53eb\u9053\uff1a\u201c\u76df\u4e3b\u5feb \u8dd1\uff01\u201d\u84e6\u5730\u4e00\u53e3\u9c9c\u8840\u5411\u963f\u535c\u5362\u55b7\u53bb\uff0c\u55b7\u5f97\u4ed6\u6ee1\u5934\u6ee1\u9762\uff0c\u963f\u535c\u5362\u5403\u4e86\u4e00\u60ca\uff0c\u97e9\u8d85\u7acb\u5373\u6251\u4e0a\uff0c\u7d27 \u7d27\u7684\u62b1\u7740\u4ed6\uff0c\u5927\u559d\u4e00\u58f0\uff0c\u628a\u6bd5\u751f\u7684\u529f\u529b\u4ed8\u4e4b\u6700\u540e\u7684\u4e00\u51fb\uff0c\u963f\u535c\u5362\u5389\u58f0\u60e8\u547c\uff0c\u808b\u9aa8\u7ed9\u4ed6\u6298\u65ad\u4e86 \u4e24\u6839\uff0c\u6655\u4e86\u8fc7\u53bb\uff0c\u4f46\u97e9\u8d85\u7ed9\u4ed6\u7684\u9f99\u8c61\u529f\u4e00\u9707\uff0c\u5374\u662f\u4f24\u5f97\u66f4\u91cd\uff0c\u9c9c\u8840\u72c2\u55b7\u51fa\u6765\uff0c\u7ec8\u4e8e\u6162\u6162\u7684\u5012 \u4e86\u4e0b\u53bb\uff0c\u4e0d\u80fd\u6d3b\u4e86\u3002 \u3000\u3000\u8499\u53e4\u6b66\u58eb\u89c1\u4ed6\u5982\u6b64\u51f6\u608d\uff0c\u90fd\u662f\u4e0d\u7981\u76ee\u77aa\u53e3\u5446\uff0c\u547c\u9ece\u5962\u53c8\u662f\u5403\u60ca\uff0c\u53c8\u662f\u4f69\u670d\uff0c\u7fd8\u8d77\u62c7\u6307\u8bf4 \u9053\uff1a\u201c\u8fd9\u4e2a\u5357\u86ee\u5b50\u5012\u4e5f\u7b97\u5f97\u4e00\u6761\u597d\u6c49\uff0c\u4e8b\u60c5\u8fc7\u540e\uff0c\u4ee5\u793c\u846c\u4ed6\u3002\u201d\u4fef\u8eab\u66ff\u5e08\u5144\u6577\u836f\u88f9\u4f24\uff0c\u4e00\u65f6 \u95f4\u5012\u662f\u65e0\u6687\u53bb\u5411\u5c60\u9f99\u76d8\u95ee\u51fa\u4e86\u4f55\u4e8b\u3002 \u3000\u3000\u5c31\u5728\u6b64\u65f6\uff0c\u5218\u743c\u59d1\u5ffd\u5730\u5636\u58f0\u53eb\u9053\uff1a\u201c\u5c60\u90ce\uff0c\u5c60\u90ce\uff0c\u6211\u8981\u53bb\u4e86\uff0c\u4f60\u4e5f\u4e0d\u6765\u770b\u6211\u4e48\uff1f\u6709\u4e00\u53e5 \u7d27\u8981\u7684\u8bdd\u6211\u8fd8\u6ca1\u6709\u544a\u8bc9\u4f60\u5462\uff01\u201d \u3000\u3000\u5c60\u9f99\u6599\u60f3\u674e\u3001\u6768\u7b49\u4eba\u63d2\u7ffc\u96be\u98de\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u743c\u59d1\u5c45\u7136\u5bf9\u6211\u5c1a\u672a\u5fd8\u60c5\uff0c\u4e0d\u77e5\u5979\u6709\u4ec0\u4e48\u8bdd \u8981\u544a\u8bc9\u6211\uff1f\u4e14\u5f85\u6211\u770b\u770b\u5979\u7684\u4f24\u52bf\uff0c\u8981\u662f\u8fd8\u80fd\u6551\u6cbb\u7684\u8bdd\uff0c\u5012\u4e5f\u4e0d\u59a8\u7559\u5979\u8fd9\u6761\u6027\u547d\u3002\u201d \u3000\u3000\u5218\u743c\u59d1\u662f\u4e2d\u4e86\u4ed6\u7684\u89c1\u8840\u5c01\u5589\u7684\u201c\u6bd2\u9f99\u9556\u201d\u7684\uff0c\u662f\u4ee5\u5c60\u9f99\u4e1d\u6beb\u4e5f\u4e0d\u63d0\u9632\uff0c\u5f2f\u4e0b\u4e86\u8170\uff0c\u8bf4 \u9053\uff1a\u201c\u6211\u5728\u8fd9\u513f\uff0c\u6709\u8bdd\u5bf9\u6211\u8bf4\u5427\uff0c\u4f60\u522b\u614c\uff0c\u6211\u4f1a\u628a\u89e3\u836f\u7ed9\u4f60\u7684\uff01\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u5218\u743c\u59d1\u5ffd\u5730\u4e00 \u8dc3\u800c\u8d77\uff0c\u201c\u535c\u201d\u7684\u4e00\u58f0\u54cd\uff0c\u5c06\u90a3\u652f\u6bd2\u9f99\u9556\u63d2\u5165\u4e86\u5c60\u9f99\u7684\u80f8\u819b\uff01 \u3000\u3000\u539f\u6765\u5218\u743c\u59d1\u521a\u624d\u4e2d\u9556\u4e4b\u65f6\uff0c\u7531\u4e8e\u674e\u601d\u5357\u5c06\u5979\u6380\u4e86\u4e00\u4e0b\uff0c\u8eab\u5f62\u7565\u4fa7\uff0c\u662f\u4ee5\u867d\u7136\u4e2d\u9556\uff0c\u4f46\u53ea \u662f\u5212\u4f24\u76ae\u8089\uff0c\u5e76\u975e\u8981\u5bb3\u3002\u6bd2\u9f99\u9556\u4e43\u662f\u89c1\u8840\u5c01\u5589\u7684\u6697\u5668\uff0c\u5018\u82e5\u662f\u666e\u901a\u4eba\u4e2d\u4e86\u4e00\u9556\uff0c\u4e0d\u662f\u8981\u5bb3\uff0c \u4e5f\u4f1a\u81f4\u547d\u7684\u3002\u5218\u743c\u59d1\u7684\u5185\u529f\u4e5f\u5e76\u4e0d\u9ad8\uff0c\u4f46\u5374\u6709\u76f8\u5f53\u9020\u8be3\uff0c\u6545\u6b64\u867d\u7136\u4e2d\u6bd2\u751a\u6df1\uff0c\u4f46\u5374\u4e0d\u662f\u5c60\u9f99 \u6240\u60f3\u8c61\u90a3\u6837\u7684\u4e25\u91cd\u3002 \u3000\u3000\u5218\u743c\u59d1\u62b1\u4e86\u5fc5\u6b7b\u4e4b\u5fc3\uff0c\u628a\u5c60\u9f99\u8bf1\u5230\u8ddf\u524d\uff0c\u7acb\u5373\u62d4\u51fa\u90a3\u652f\u6bd2\u9f99\u9556\u6253\u4ed6\uff0c\u6bd2\u9556\u4e0d\u504f\u4e0d\u501a\u7684\u6b63 \u597d\u63d2\u8fdb\u4e86\u5c60\u9f99\u7684\u5fc3\u7a9d\uff0c\u5c60\u9f99\u5185\u529f\u5373\u4f7f\u6bd4\u5218\u743c\u59d1\u9ad8\u51fa\u5341\u500d\uff0c\u90a3\u4e5f\u662f\u4e0d\u80fd\u6d3b\u547d\u7684\u4e86\uff01 \u3000\u3000\u4f46\u5218\u743c\u59d1\u7531\u4e8e\u62d4\u8d77\u6bd2\u9556\uff0c\u4f7f\u51fa\u4e86\u6700\u540e\u7684\u6c14\u529b\uff0c\u4f24\u53e3\u6269\u5927\uff0c\u6bd2\u6c14\u767b\u65f6\u53d1\u6563\uff0c\u6740\u4e86\u5c60\u9f99\u4e4b \u540e\uff0c\u81ea\u5df1\u4ea6\u5df2\u652f\u6301\u4e0d\u4f4f\u4e86\u3002 \u3000\u3000\u53d8\u8d77\u4ed3\u731d\uff0c\u5927\u51fa\u8fd9\u73ed\u8499\u53e4\u6b66\u58eb\u7684\u610f\u6599\u4e4b\u5916\uff0c\u547c\u9ece\u5962\u521a\u53eb\u5f97\u4e00\u58f0\u201c\u4e5f\u597d\uff01\u201d\u5c60\u9f99\u5df2\u662f\u5012\u5730 \u8eab\u4ea1\uff01\u4f17\u6b66\u4e0a\u5927\u60ca\u4e4b\u4e0b\uff0c\u7eb7\u7eb7\u6251\u4e0a\uff0c\u5218\u743c\u59d1\u53ea\u89c9\u773c\u524d\u4e00\u7247\u6f06\u9ed1\uff0c\u7eb5\u58f0\u7b11\u9053\uff1a\u201c\u8fd9\u5978\u8d3c\u6b7b\u4e86 \u4e48\uff1f\u597d\uff0c\u597d\uff0c\u90a3\u6211\u4ea6\u53ef\u4ee5\u6b7b\u800c\u65e0\u61be\u4e86\uff01\u201d\u56de\u8f6c\u67f3\u53f6\u5200\u5f80\u9888\u9879\u4e00\u62b9\uff0c\u8840\u55b7\u5982\u6cc9\uff0c\u4f17\u6b66\u58eb\u672a\u66fe\u6251 \u5230\uff0c\u5979\u5df2\u662f\u7389\u6b92\u9999\u6d88\u3002 \u3000\u3000\u674e\u601d\u5357\u6253\u5f00\u4e86\u7262\u95e8\uff0c\u6768\u5a49\u3001\u97e9\u4f69\u745b\u521a\u521a\u8d70\u51fa\u6765\uff0c\u6b63\u597d\u89c1\u7740\u8fd9\u4e00\u5e55\u60e8\u70c8\u7684\u666f\u8c61\uff0c\u6768\u5a49\u53c8\u662f \u5403\u60ca\uff0c\u53c8\u662f\u656c\u4f69\uff0c\u5931\u58f0\u53eb\u9053\uff1a\u201c\u597d\u59d0\u59d0\uff0c\u4f60\u66ff\u6211\u62a5\u4e86\u4ec7\uff0c\u6211\u5374\u65e0\u6cd5\u62a5\u7b54\u4f60\u4e86\uff01\u201d\u6251\u4e0a\u53bb\u62b1\u7740 \u5218\u743c\u59d1\u7684\u5c38\u4f53\uff0c\u6cea\u73e0\u6eda\u6eda\u800c\u4e0b\u3002\u4f17\u6b66\u58eb\u5728\u65c1\u73af\u4f3a\uff0c\u5979\u7adf\u662f\u89c6\u800c\u4e0d\u89c1\uff0c\u542c\u800c\u4e0d\u95fb\u3002 \u3000\u3000\u8499\u5360\u6b66\u58eb\u6700\u4e3a\u4f69\u670d\u82f1\u96c4\uff0c\u5728\u8fd9\u5239\u90a3\u95f4\uff0c\u4e0d\u7ea6\u800c\u540c\u5730\u8c01\u4e5f\u4e0d\u4e0a\u524d\u4f24\u5979\uff0c\u811a\u6b65\u7a81\u7136\u505c\u4e0b\uff0c\u5f7c \u6b64\u9762\u9762\u76f8\u89d1\u3002\u6709\u4e2a\u6b66\u58eb\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u6c49\u4eba\u82e5\u7136\u90fd\u662f\u50cf\u5979\u4e00\u6837\uff0c\u54b1\u4eec\u5c31\u4e0d\u7528\u5984\u60f3\u541e\u5e76\u4e2d \u539f\u3002\u201d \u3000\u3000\u547c\u9ece\u5962\u970d\u7136\u4e00\u7701\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u4eba\u662f\u4e2d\u539f\u7684\u6b66\u6797\u76df\u4e3b\uff0c\u4e0d\u5c06\u4ed6\u964d\u670d\uff0c\u54b1\u4eec\u53ef\u5c31\u96be\u4ee5 \u541e\u5e76\u4e2d\u539f\u3002\u201d \u3000\u3000\u5148\u524d\u90a3\u4e2a\u6b66\u58eb\u8bf4\u9053\uff1a\u201c\u6211\u656c\u4f60\u4eec\u662f\u82f1\u96c4\u597d\u6c49\uff0c\u4e0d\u60f3\u4e0e\u4f60\u4eec\u4e3a\u96be\u3002\u8bf7\u4f60\u4eec\u8fd8\u662f\u5404\u81ea\u56de\u5230\u7262 \u623f\u91cc\u53bb\uff01\u201d \u3000\u3000\u6768\u5a49\u7f13\u7f13\u5730\u7ad9\u4e86\u8d77\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u5357\u54e5\uff0c\u4f60\u662f\u4e0d\u662f\u4e5f\u7740\u4e86\u62d6\u96f7\u7684\u6697\u7b97\uff1f\u201d\u674e\u601d\u5357\u70b9\u4e86\u70b9 \u5934\u3002\u6768\u5a49\u8bf4\u9053\uff1a\u201c\u5357\u54e5\uff0c\u54b1\u4eec\u4e0d\u80fd\u518d\u53d7\u5c48\u8fb1\uff0c\u628a\u8fd9\u4f4d\u597d\u59d0\u59d0\u5f53\u4f5c\u54b1\u4eec\u7684\u699c\u6837\u5427\u3002\u201d\u62ff\u8d77\u4e86\u5218 \u743c\u59d1\u90a3\u628a\u67f3\u53f6\u5200\uff0c\u6b63\u8981\u81ea\u520e\uff0c\u674e\u601d\u5357\u5927\u58f0\u53eb\u9053\uff1a\u201c\u4e0d\uff0c\u54b1\u4eec\u5373\u4f7f\u6253\u4e0d\u8fc7\u654c\u4eba\uff0c\u4e5f\u51b3\u4e0d\u80fd\u81ea\u5df1 \u8f7b\u751f\uff01\u8fd9\u4f4d\u597d\u59d0\u59d0\u662f\u6740\u4e86\u5c60\u9f99\u4e4b\u540e\u65b9\u59cb\u727a\u7272\u7684\uff0c\u8981\u5b66\u5c31\u5f97\u5b66\u5979\u8fd9\u6837\uff01\u201d \u3000\u3000\u8fd9\u4e00\u559d\u628a\u6768\u5a49\u559d\u5f97\u6e05\u9192\u8fc7\u6765\uff0c\u90a3\u53e3\u67f3\u53f6\u5200\u6307\u5230\u54bd\u5589\u53c8\u653e\u4e0b\u6765\u4e86\uff0c\u4f46\u5374\u51c4\u7136\u8bf4\u9053\uff1a\u201c\u5357 \u54e5\uff0c\u4f60\u662f\u7537\u5b50\uff0c\u4f60\u662f\u53ef\u4ee5\u62fc\u7684\u3002\u6211\u5374\u4e0d\u60f3\u843d\u5728\u4ed6\u4eec\u624b\u4e0a\uff0c\u4f60\u8fd8\u662f\u8ba9\u6211\u6b7b\u5427\uff01\u201d\u6b64\u65f6\u5979\u8fd8\u662f\u6709 \u70b9\u795e\u667a\u672a\u6e05\uff0c\u4e5f\u4e0d\u77e5\u81ea\u5df1\u5982\u679c\u8fd9\u6837\u505a\u662f\u5bf9\u4e5f\u4e0d\u5bf9\u3002 \u3000\u3000\u547c\u9ece\u5962\u5fc3\u4e2d\u70e6\u8e81\uff0c\u559d\u9053\uff1a\u201c\u6211\u5e76\u4e0d\u8981\u53d6\u4f60\u4eec\u6027\u547d\uff0c\u4f60\u4eec\u81ea\u5df1\u627e\u6b7b\u6211\u4e5f\u6ca1\u6cd5\uff0c\u7ba1\u4ed6\u662f\u6b7b\u662f \u6d3b\uff0c\u5c06\u4ed6\u4eec\u62ff\u4e0b\uff01\u201d\u628a\u624b\u4e00\u6325\uff0c\u4f17\u6b66\u58eb\u4e00\u62e5\u800c\u4e0a\u3002 \u3000\u3000\u5c31\u5728\u6b64\u65f6\uff0c\u5ffd\u542c\u5f97\u9739\u96f7\u4f3c\u7684\u4e00\u58f0\u559d\u9053\uff1a\u201c\u8c01\u6562\u6b3a\u4fae\u6211\u7684\u5973\u513f\uff01\u201d\u53ea\u89c1\u4e24\u6761\u4eba\u5f71\u4ef2\u5982\u5de8\u9e1f \u7a7f\u4e91\uff0c\u4ece\u7a7a\u800c\u964d\uff01 \u3000\u3000\u8fd9\u4e24\u4e2a\u4ece\u5c4b\u9876\u8df3\u4e0b\u6765\u7684\u4eba\uff0c\u4e00\u4e2a\u5934\u62ab\u5927\u56f4\u5dfe\uff0c\u53a8\u5b50\u6253\u626e\uff0c\u4e00\u4e2a\u7682\u8863\u5c0f\u5e3d\uff0c\u662f\u201c\u56fd\u5e08\u5e9c\u201d \u8001\u4ec6\u4eba\u7684\u88c5\u675f\uff0c\u4f46\u6b66\u529f\u5374\u662f\u597d\u5f97\u51fa\u5947\uff01\u90a3\u53a8\u5b50\u6a21\u6837\u7684\u4eba\u8dd1\u5230\u97e9\u4f69\u745b\u8eab\u8fb9\uff0c\u53cc\u81c2\u4e00\u632f\uff0c\u5c31\u628a\u6251 \u4e0a\u6765\u7684\u4e24\u4e2a\u6b66\u58eb\uff0c\u4e00\u624b\u4e00\u4e2a\uff0c\u50cf\u6349\u5c0f\u9e21\u4e00\u6837\u7684\u63d0\u4e86\u8d77\u6765\uff0c\u4e00\u4e2a\u65cb\u98ce\u75be\u821e\uff0c\u4e24\u4e2a\u6c34\u725b\u822c\u8eab\u8eaf\u7684 \u8499\u53e4\u6b66\u58eb\u7adf\u7ed9\u4ed6\u629b\u51fa\u6570\u4e08\u5f00\u5916\uff0c\u649e\u5f97\u5934\u7834\u8840\u6d41\uff01\u7b2c\u4e09\u4e2a\u6b66\u58eb\u5927\u5403\u4e00\u60ca\uff0c\u521a\u8981\u9000\u4e0b\uff0c\u4ff1\u56e0\u662f\u6025 \u5954\u4e4b\u52bf\uff0c\u811a\u6b65\u672a\u80fd\u7acb\u5373\u505c\u6b62\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u53c8\u5df2\u7ed9\u4ed6\u6293\u4f4f\u3002 \u3000\u3000\u97e9\u4f69\u745b\u4e00\u770b\uff0c\u89c1\u662f\u521a\u624d\u90a3\u4e2a\u66ff\u4ed6\u4eec\u8bf4\u60c5\u7684\u6b66\u58eb\uff0c\u53eb\u9053\uff1a\u201c\u7239\u7239\uff0c\u8fd9\u4eba\u5e76\u4e0d\u592a\u574f\uff01\u201d\u90a3 \u201c\u53a8\u5b50\u201d\u9053\uff1a\u201c\u662f\u4e48\uff1f\u201d\u632f\u81c2\u4e00\u629b\uff0c\u4f7f\u4e86\u4e2a\u5de7\u52b2\uff0c\u90a3\u540d\u6b66\u58eb\u5728\u534a\u7a7a\u4e2d\u7ffb\u4e86\u4e2a\u7b4b\u6597\uff0c\u843d\u4e0b\u5730 \u6765\uff0c\u521a\u597d\u662f\u811a\u5c16\u7740\u5730\uff0c\u5c45\u7136\u6beb\u53d1\u65e0\u4f24\uff0c\u77e5\u662f\u5bf9\u65b9\u624b\u4e0b\u7559\u60c5\uff0c\u4e0d\u7531\u5f97\u5446\u82e5\u6728\u9e21\uff0c\u505a\u58f0\u4e0d\u5f97\uff01 \u3000\u3000\u547c\u9ece\u8457\u5411\u90a3\u4e2a\u4ec6\u4eba\u88c5\u675f\u7684\u4eba\u6251\u53bb\uff0c\u53cc\u638c\u865a\u62b1\uff0c\u5212\u4e86\u4e00\u9053\u5706\u5f27\uff0c\u4f7f\u51fa\u4e86\u770b\u5bb6\u672c\u9886\u7684\u9f99\u8c61 \u529f\uff0c\u90a3\u4eba\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u53ef\u4e0d\u503c\u5f97\u6211\u52a8\u624b\uff01\u201d\u53ea\u542c\u5f97\u201c\u84ec\u201d\u7684\u4e00\u58f0\uff0c\u547c\u9ece\u5962\u53cc\u638c\u6253\u5728\u4ed6\u7684\u8eab \u4e0a\uff0c\u5982\u51fb\u76ae\u9f13\uff0c\u5012\u4e0b\u53bb\u7684\u5374\u4e0d\u662f\u8fd9\u4e2a\u201c\u4ec6\u4eba\u201d\uff0c\u547c\u9ece\u5962\u50cf\u76ae\u7403\u822c\u7684\u5f39\u4e86\u8d77\u6765\uff0c\u53cd\u800c\u662f\u4ed6\u5012\u4e0b \u53bb\u4e86\u3002 \u3000\u3000\u4f17\u6b66\u58eb\u8fd9\u4e00\u60ca\uff0c\u662f\u975e\u540c\u5c0f\u53ef\uff0c\u8fd8\u6709\u8c01\u6562\u4e0a\u524d\u3002\u674e\u6069\u5357\u60ca\u559c\u4ea4\u96c6\uff0c\u53eb\u9053\uff1a\u201c\u5b5f\u5927\u4fa0\u4f60\u6765 \u4e86\uff01\u201d \u3000\u3000\u97e9\u4f69\u745b\u5219\u5411\u90a3\u201c\u53a8\u5b50\u201d\u8bc9\u8bf4\uff1a\u201c\u7239\u7239\uff0c\u6211\u4eec\u53d7\u4e86\u6697\u7b97\uff0c\u4e0d\u77e5\u4ed6\u4eec\u7528\u7684\u662f\u4ec0\u4e48\u6bd2\u836f\uff0c\u7ed9\u6211 \u4eec\u5403\u4e86\uff0c\u6c14\u529b\u4f7f\u4e0d\u51fa\u6765\u3002\u201d \u3000\u3000\u539f\u6765\u8fd9\u4e2a\u201c\u53a8\u5b50\u201d\u88c5\u675f\u7684\u4eba\u662f\u5979\u7684\u7236\u4eb2\u97e9\u5927\u7ef4\uff0c\u90a3\u4e2a\u4f5c\u201c\u4ec6\u4eba\u201d\u6253\u626e\u7684\u5219\u662f\u6c5f\u5357\u5927\u4fa0\u5b5f \u5c11\u521a\u3002\u4ed6\u4eec\u662f\u4e00\u65e9\u5c31\u6df7\u8fdb\u4e86\u201c\u56fd\u5e08\u5e9c\u201d\u7684\uff0c\u4e3a\u4e86\u4ffa\u4eba\u8033\u76ee\uff0c\u4e0d\u5f97\u4e0d\u4e54\u88c5\u6253\u626e\u3002 \u3000\u3000\u97e9\u5927\u7ef4\u9053\uff1a\u201c\u4f60\u4eec\u80fd\u591f\u8d70\u5417\u3002\u201d\u97e9\u4f69\u745b\u9053\uff1a\u201c\u53ef\u4ee5\u3002\u201d\u97e9\u5927\u7ef4\u9053\uff1a\u201c\u597d\uff0c\u90a3\u5c31\u884c\u4e86\u3002\u4f60 \u4eec\u968f\u7740\u6211\u8d70\uff0c\u8c01\u6562\u963b\u62e6\uff1f\u201d \u3000\u3000\u8bdd\u72b9\u672a\u4e86\uff0c\u5ffd\u542c\u5f97\u5341\u5206\u9a84\u50b2\u7684\u58f0\u97f3\u7528\u751f\u786c\u7684\u6c49\u8bed\u8bf4\u9053\uff1a\u201c\u6211\u6562\u963b\u62e6\u3002\u54fc\uff0c\u53ea\u987b\u6211\u4e00\u4e2a\u4eba \u5728\u6b64\uff0c\u4f60\u4eec\u5c31\u8dd1\u4e0d\u4e86\u3002\u201d\u58f0\u97f3\u4e0d\u5927\uff0c\u8fdc\u8fdc\u4f20\u6765\uff0c\u4f46\u5982\u9707\u5f97\u8033\u9f13\u55e1\u55e1\u4f5c\u54cd\uff0c\u97e9\u5927\u7ef4\u5fc3\u4e2d\u4e00\u51db\uff0c \u62ac\u5934\u4e00\u671b\uff0c\u53ea\u89c1\u4e00\u4e2a\u62ab\u7740\u5927\u7ea2\u8888\u88df\u7684\u756a\u50e7\u7387\u9886\u56db\u540d\u6b66\u58eb\u6765\u5230\u3002 \u3000\u3000\u97e9\u5927\u7ef4\u6599\u662f\u9f99\u8c61\u6cd5\u738b\uff0c\u559d\u9053\uff1a\u201c\u4f60\u5c31\u662f\u8499\u53e4\u9791\u5b50\u7684\u56fd\u5e08\u5417\uff0c\u597d\uff0c\u6211\u4e0e\u4f60\u8f83\u91cf\u8f83\u91cf\uff01\u201d \u3000\u3000\u9f99\u8c61\u6cd5\u738b\u9053\uff1a\u201c\u4e0d\u7528\u5982\u6b64\u9ebb\u70e6\uff0c\u4f60\u4eec\u4e24\u4eba\u5e76\u80a9\u5b50\u4e0a\u5427\uff01\u201d\u6b63\u662f\uff1a \u3000\u3000\u65e0\u654c\u795e\u529f\u5938\u6d77\u53e3\uff0c\u5c82\u77e5\u4eba\u5916\u6709\u9ad8\u4eba\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b\u2014\u2014\u7b2c\u4e94\u5341\u4e03\u56de\u3000\u9e3e\u98d8\u51e4\u6cca\u82b3\u5fc3\u788e\u3000\u864e\u6597\u9f99\u4e89\u5251\u6c14\u5bd2 \u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b \u7b2c\u4e94\u5341\u4e03\u56de\u3000\u9e3e\u98d8\u51e4\u6cca\u82b3\u5fc3\u788e\u3000\u864e\u6597\u9f99\u4e89\u5251\u6c14\u5bd2 \u3000\u3000\u97e9\u5927\u7ef4\u6c14\u5f97\u4e03\u7a8d\u751f\u70df\uff0c\u5374\u7eb5\u58f0\u7b11\u9053\uff1a\u201c\u6211\u6d3b\u4e86\u534a\u8f88\u5b50\uff0c\u5012\u8fd8\u6ca1\u6709\u89c1\u8fc7\u5982\u6b64\u72c2\u5984\u4e4b\u5f92\uff01 \u597d\uff0c\u4f60\u6015\u9ebb\u70e6\uff0c\u6211\u66f4\u4e0d\u613f\u591a\u8d39\u529f\u592b\uff0c\u4f60\u4eec\u6709\u591a\u5c11\u4eba\uff0c\u4e00\u9f50\u6765\u5427\uff01\u4f60\u4eec\u5341\u4e2a\u4eba\u4e0a\uff0c\u6211\u4eec\u4e24\u4e2a\u4eba \u5bf9\u4ed8\uff0c\u4f60\u4eec\u4e00\u767e\u4e2a\u4eba\u9f50\u4e0a\uff0c\u6211\u4eec\u4e5f\u662f\u4e24\u4e2a\u4eba\u5bf9\u4ed8\uff01\u201d \u3000\u3000\u9f99\u8c61\u6cd5\u738b\u628a\u624b\u4e00\u6325\uff0c\u5bf9\u90a3\u56db\u4e2a\u5f1f\u5b50\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u9000\u8fc7\u4e00\u8fb9\uff0c\u6211\u548c\u8fd9\u4e24\u4e2a\u8001\u5339\u592b\u8f83\u91cf\u4e4b \u65f6\uff0c\u4e0d\u8bb8\u4f60\u4eec\u63d2\u624b\u3002\u201d \u3000\u3000\u5b5f\u5c11\u521a\u529d\u9053\uff1a\u201c\u97e9\u5144\uff0c\u8ba9\u6211\u5148\u4e0a\uff0c\u4ee4\u5a9b\u548c\u76df\u4e3b\u592b\u5987\u53d7\u4e86\u6697\u7b97\uff0c\u7ed9\u4ed6\u4eec\u533b\u6cbb\uff0c\u8fd9\u4e5f\u662f\u4e00\u4ef6 \u7d27\u8981\u7684\u4e8b\u60c5\u3002\u201d \u3000\u3000\u97e9\u5927\u7ef4\u867d\u7136\u597d\u80dc\uff0c\u6bd5\u7adf\u4e5f\u662f\u6d3b\u4e86\u51e0\u5341\u5c81\u7684\u4eba\uff0c\u542c\u4e86\u8fd9\u8bdd\uff0c\u970d\u7136\u4e00\u7701\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4e0d \u9519\uff0c\u4ed6\u4eec\u7684\u529f\u529b\u672a\u80fd\u6062\u590d\uff0c\u53ea\u51ed\u6211\u4eec\u5f00\u8def\uff0c\u53ea\u6015\u4e5f\u662f\u96be\u4ee5\u95ef\u51fa\u91cd\u56f4\u3002\u5b5f\u5927\u4fa0\u5251\u672f\u901a\u795e\uff0c\u672c\u9886 \u8fdc\u80dc\u4e8e\u6211\uff0c\u8ba9\u4ed6\u5bf9\u4ed8\u8fd9\u4e2a\u8499\u53e4\u56fd\u5e08\uff0c\u5c31\u662f\u6bd4\u8f83\u6709\u628a\u63e1\u5f97\u591a\u3002\u201d\u5982\u6b64\u4e00\u60f3\uff0c\u4e5f\u5c31\u5fc3\u5e73\u6c14\u548c\u5730\u9000 \u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u5b5f\u5c11\u521a\u7f13\u6b65\u5411\u524d\uff0c\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u7d20\u4ef0\u56fd\u5e08\u662f\u897f\u57df\u7b2c\u4e00\u9ad8\u624b\uff0c\u5b5f\u67d0\u5f85\u6765\u8bf7\u6559\u3002\u5b5f\u67d0\u82e5\u7136\u8f93 \u4e86\uff0c\u4ece\u6b64\u7edd\u8ff9\u6b66\u6797\uff0c\u4f46\u4e07\u4e00\u5b5f\u67d0\u4fa5\u5e78\u80dc\u4e86\uff0c\u56fd\u5e08\u4f60\u53c8\u5982\u4f55\uff1f\u201d \u3000\u3000\u9f99\u8c61\u6cd5\u738b\u89c1\u5b5f\u5c11\u521a\u8bf4\u5f97\u5ba2\u6c14\uff0c\u5f53\u4e0b\u4e5f\u5c31\u6536\u655b\u6c14\u7130\u8bf4\u9053\uff1a\u201c\u597d\u8bf4\uff0c\u597d\u8bf4\uff0c\u5b5f\u5927\u4fa0\u7684\u58f0\u540d\uff0c \u6211\u5728\u8499\u53e4\u4e5f\u662f\u4e45\u4ef0\u7684\u4e86\u3002\u4eca\u65e5\u8001\u8872\u82e5\u662f\u8d25\u5728\u4f60\u7684\u5251\u4e0b\uff0c\u4ece\u6b64\u8db3\u8ff9\u4e0d\u5c65\u4e2d\u539f\uff01\u201d \u3000\u3000\u5b5f\u5c11\u521a\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4e48\u6211\u5e26\u4ed6\u4eec\u8d70\u4f60\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u9f99\u8c61\u6cd5\u738b\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5df2\u7ecf\u7b54\u5e94\u4f60\u4e86\uff0c\u6211\u82e5\u8f93\u4e86\uff0c\u4ece\u6b64\u8db3\u8ff9\u4e0d\u5c65\u4e2d\u539f\uff0c\u90a3\u8fd8\u4f1a \u7ba1\u4f60\u4eec\u8fd9\u4e9b\u95f2\u4e8b\uff1f\u4e0d\u8fc7\uff0c\u4f60\u73b0\u5728\u5c31\u8bf4\u8fd9\u8bdd\uff0c\u672a\u514d\u662f\u8bf4\u5f97\u65e9\u4e86\u70b9\u5427\uff1f\u201d \u3000\u3000\u5b5f\u5c11\u521a\u7b11\u9053\uff1a\u201c\u6211\u4eec\u6c49\u4eba\u6700\u91cd\u627f\u8bfa\uff0c\u8fd8\u662f\u5148\u8bf4\u5b9a\u7684\u597d\u3002\u201d \u3000\u3000\u9f99\u8c61\u6cd5\u738b\u9053\uff1a\u201c\u597d\uff0c\u4f60\u8bf4\u7684\u8001\u8872\u90fd\u4f9d\u4f60\uff0c\u4f60\u4eae\u5251\u8fdb\u62db\u5427\uff01\u201d\u5b5f\u5c11\u521a\u624b\u6309\u5251\u67c4\uff0c\u811a\u6b65\u4e0d\u4e03 \u4e0d\u516b\u7684\u7ad9\u5728\u9f99\u8c61\u6cd5\u738b\u5bf9\u9762\uff0c\u4f46\u5374\u5e76\u4e0d\u62d4\u51fa\u5251\u6765\uff0c\u53ea\u662f\u628a\u4e00\u5bf9\u70af\u70af\u6709\u795e\u7684\u773c\u775b\u7262\u7262\u7684\u76ef\u7740\u9f99\u8c61 \u6cd5\u738b\u3002\u9f99\u8c61\u6cd5\u738b\u5fc3\u5934\u4e00\u51db\uff0c\u60f3\u9053\uff1a\u201c\u8fd9\u4eba\u679c\u7136\u4e0d\u662f\u6613\u4e0e\u4e4b\u8f88\uff01\u201d\u5f53\u4e0b\u4e5f\u51dd\u795e\u9759\u6c14\uff0c\u53cc\u773c\u6ce8\u89c6 \u7740\u5b5f\u5c11\u521a\u3002\u4e24\u4e2a\u4eba\u5c31\u50cf\u6597\u9e21\u4f3c\u7684\uff0c\u672a\u4ea4\u950b\u4e4b\u524d\uff0c\u5f7c\u6b64\u90fd\u662f\u5168\u795e\u6ce8\u89c6\u5bf9\u65b9\uff0c\u770b\u5f97\u90a3\u4e00\u73ed\u8499\u53e4\u6b66 \u571f\u6697\u6697\u7eb3\u7f55\u3002 \u3000\u3000\u539f\u6765\u53cc\u65b9\u5fc3\u91cc\u90fd\u662f\u660e\u767d\uff0c\u8fd9\u6b21\u78b0\u4e0a\u7684\u4e43\u662f\u5e73\u751f\u6700\u5f3a\u7684\u5bf9\u624b\uff01\u662f\u4ee5\u5927\u5bb6\u90fd\u4e0d\u6562\u8f7b\u6613\u53d1\u62db\uff0c \u51c6\u5907\u4e58\u6687\u62b5\u9699\uff0c\u5f85\u5230\u6709\u5229\u7684\u65f6\u673a\u65b9\u59cb\u7a81\u7136\u53d1\u96be\uff0c\u8fd9\u79cd\u4e0a\u4e58\u6b66\u5b66\u4e2d\u7684\u540e\u53d1\u5236\u4eba\u7684\u5965\u4e49\uff0c\u8fd9\u73ed\u8499 \u53e4\u6b66\u58eb\u5f53\u7136\u662f\u4e0d\u61c2\u7684\u4e86\u3002 \u3000\u3000\u97e9\u5927\u7ef4\u66ff\u5973\u513f\u628a\u4e86\u628a\u8109\uff0c\u8bf4\u9053\uff1a\u201c\u539f\u6765\u4f60\u662f\u7740\u4e86\u9165\u9aa8\u6563\u7684\u6697\u7b97\uff0c\u4e0d\u7528\u60ca\u614c\uff0c\u7239\u7239\u53ef\u4ee5\u4e3a \u4f60\u6062\u590d\u529f\u529b\u3002\u201d \u3000\u3000\u97e9\u4f69\u745b\u7b11\u9053\uff1a\u201c\u7239\u7239\uff0c\u522b\u5fd9\u7ed9\u6211\u533b\u6cbb\uff0c\u6211\u8981\u770b\u5b5f\u4f2f\u4f2f\u548c\u8fd9\u756a\u50e7\u6bd4\u6b66\u3002\u201d\u6768\u5a49\u8ddf\u7740\u8bf4\u9053\uff1a \u201c\u4e0d\u9519\uff0c\u8fd9\u6837\u7cbe\u5f69\u7684\u6bd4\u6b66\uff0c\u4e00\u751f\u4e4b\u4e2d\u53ea\u6015\u4e5f\u662f\u96be\u5f97\u4e00\u89c1\u3002\u53ef\u4e0d\u80fd\u9519\u8fc7\u4e86\u8fd9\u4e2a\u773c\u798f\u3002\u201d\u539f\u6765\u5979 \u4eec\u90fd\u662f\u62b1\u7740\u540c\u4e00\u6837\u7684\u5fc3\u601d\uff0c\u60f3\u97e9\u5927\u7ef4\u5148\u7ed9\u674e\u601d\u5357\u533b\u6cbb\u3002 \u3000\u3000\u97e9\u5927\u7ef4\u970d\u7136\u4e00\u7701\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u53ef\u771f\u662f\u8001\u7cca\u6d82\u4e86\uff0c\u601d\u5357\u8eab\u8d1f\u91cd\u4efb\uff0c\u662f\u8be5\u7ed9\u4ed6\u5148 \u6cbb\u624d\u5bf9\u3002\u6211\u5374\u53ea\u662f\u8bb0\u6302\u7740\u81ea\u5df1\u7684\u5973\u513f\u3002\u201d\u6697\u6697\u53eb\u4e86\u4e00\u58f0\u201c\u60ed\u6127\u201d\uff0c\u7acb\u5373\u6349\u4f4f\u674e\u601d\u5357\u53cc\u624b\uff0c\u8bf4 \u9053\uff1a\u201c\u4f60\u4e0e\u6211\u638c\u5fc3\u76f8\u62b5\uff0c\u95ed\u76ee\u8fd0\u529f\u3002\u65e0\u8bba\u5916\u95f4\u6709\u751a\u51f6\u9669\u4e4b\u4e8b\uff0c\u4f60\u90fd\u4e0d\u8bb8\u5206\u5fc3\uff01\u201d\u674e\u601d\u5357\u60f3\u8981 \u8c26\u8ba9\uff0c\u53ea\u89c9\u4e00\u80a1\u70ed\u6c14\u5df2\u662f\u4ece\u638c\u5fc3\u4f20\u8fdb\u4f53\u5185\uff0c\u53ea\u5f97\u4f9d\u97e9\u5927\u7ef4\u6240\u6559\uff0c\u76d8\u819d\u800c\u5750\uff0c\u773c\u89c2\u9f3b\u9f3b\u89c2\u5fc3\u7684 \u95ed\u76ee\u8fd0\u529f\u3002 \u3000\u3000\u97e9\u5927\u7ef4\u5401\u4e86\u53e3\u6c14\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u745b\u513f\uff0c\u6211\u8863\u888b\u91cc\u6709\u4e2a\u5c0f\u94f6\u74f6\uff0c\u94f6\u74f6\u91cc\u6709\u4e09\u7c92\u78a7\u7075\u4e39\uff0c\u4f60 \u4eec\u6b63\u597d\u4e00\u4eba\u4e00\u7c92\u3002\u4f60\u638f\u51fa\u6765\uff0c\u548c\u6768\u59d1\u5a18\u5148\u884c\u670d\u4e0b\uff0c\u53e6\u5916\u4e00\u7c92\uff0c\u73b0\u5728\u4e5f\u53ef\u4ee5\u7ed9\u4f60\u7684\u674e\u5927\u54e5\u670d \u4e86\u3002\u201d \u3000\u3000\u539f\u6765\u97e9\u5927\u7ef4\u4e0d\u4f46\u5185\u529f\u6df1\u6e5b\uff0c\u533b\u5b66\u4e0a\u4e5f\u9887\u6709\u9020\u8be3\uff0c\u8fd9\u78a7\u7075\u4e39\u5c31\u662f\u4ed6\u79d8\u5236\u7684\u4e00\u79cd\u80fd\u89e3\u767e\u6bd2\u7684 \u836f\u4e38\uff0c\u914d\u5236\u78a7\u7075\u4e39\u7684\u4e3b\u836f\u662f\u5929\u5c71\u96ea\u83b2\uff0c\u5341\u5206\u96be\u5f97\uff0c\u521a\u597d\u5269\u4e0b\u8fd9\u4e09\u9897\u3002 \u3000\u3000\u78a7\u7075\u4e39\u5e76\u975e\u9165\u9aa8\u6563\u7684\u5bf9\u75c7\u89e3\u836f\uff0c\u4f46\u7ecf\u8fc7\u97e9\u5927\u7ef4\u4ee5\u7cbe\u7eaf\u6df1\u539a\u7684\u5185\u529f\u7ed9\u4e2d\u6bd2\u8005\u8212\u7b4b\u6d3b\u8840\uff0c\u78a7 \u7075\u4e39\u7684\u836f\u529b\u5c31\u53ef\u4ee5\u5168\u90e8\u53d1\u6325\uff0c\u800c\u4e14\u53ef\u4ee5\u5728\u6700\u77ed\u7684\u65f6\u95f4\u89c1\u6548\uff0c\u52a0\u4e0a\u4e86\u4e2d\u6bd2\u8005\u672c\u8eab\u7684\u529f\u529b\uff0c\u4e5f\u5c31 \u7b49\u4e8e\u5bf9\u75c7\u7684\u89e3\u836f\u4e86\uff0c\u97e9\u5927\u7ef4\u56e0\u4e3a\u8981\u5728\u7a0d\u540e\u7684\u65f6\u95f4\u65b9\u80fd\u7ed9\u6768\u5a49\u548c\u5973\u513f\u533b\u6cbb\uff0c\u6545\u800c\u53eb\u5979\u4eec\u5148\u884c\u670d \u836f\u3002 \u3000\u3000\u97e9\u4f69\u745b\u5374\u628a\u4e24\u9897\u78a7\u7075\u4e39\u5206\u7ed9\u6768\u5a49\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u670d\u4f8d\u674e\u5927\u54e5\u5427\u3002\u201d\u6768\u5a49\u628a\u7075\u4e39\u7eb3\u5165\u674e\u601d\u5357 \u53e3\u4e2d\uff0c\u82b3\u5fc3\u60f4\u63e3\uff0c\u6ce8\u89c6\u7740\u4ed6\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5e78\u4e8f\u6211\u521a\u624d\u6ca1\u6709\u81ea\u5bfb\u77ed\u89c1\uff0c\u5426\u5219\u5357\u54e5\u8fd8\u7109\u80fd\u5b9a\u4e0b \u5fc3\u795e\u8fd0\u529f\uff0c\u63a5\u53d7\u97e9\u8001\u524d\u8f88\u7684\u6cbb\u7597\uff1f\u201d \u3000\u3000\u4e24\u4eba\u5fc3\u610f\u76f8\u901a\uff0c\u674e\u601d\u5357\u53ea\u89c9\u5f97\u5fc3\u91cc\u4e5f\u597d\u50cf\u6696\u70d8\u70d8\u7684\uff0c\u7cbe\u795e\u9661\u632f\uff0c\u8f6c\u77ac\u4e4b\u95f4\uff0c\u771f\u6c14\u5df1\u662f\u6c89 \u805a\u4e39\u7530\uff0c\u6e10\u6e10\u80fd\u591f\u8fd0\u7528\u4e86\u3002 \u3000\u3000\u5b5f\u5c11\u521a\u4e0e\u9f99\u8c61\u6cd5\u738b\u5bf9\u7acb\u51dd\u89c6\uff0c\u5927\u5bb6\u90fd\u662f\u52a8\u4e5f\u4e0d\u52a8\u3002\u9661\u7136\u95f4\u5ffd\u542c\u5f97\u9f99\u8c61\u6d3b\u738b\u4e00\u58f0\u5927\u559d\uff0c\u4e24 \u4e2a\u4eba\u540c\u65f6\u6251\u8d77\uff01 \u3000\u3000\u5b5f\u5c11\u521a\u51fa\u624b\u5947\u5feb\uff0c\u5251\u5149\u5982\u7ec3\uff0c\u75be\u523a\u9f99\u8c61\u6cd5\u738b\u80f8\u53e3\u7684\u7f06\u5de9\u7a74\uff0c\u5c0f\u8179\u7684\u5f52\u85cf\u7a74\uff0c\u80c1\u4e0b\u7684\u6108\u6c14 \u7a74\u3002\u8fd9\u4e00\u62db\u4e09\u5f0f\uff0c\u4e43\u662f\u5b5f\u5c11\u521a\u5f97\u610f\u7684\u7edd\u62db\uff0c\u53ea\u8981\u7ed9\u4ed6\u523a\u7740\u4e00\u5904\uff0c\u9f99\u8c61\u6cd5\u738b\u4e0d\u6b7b\u4e5f\u5f97\u91cd\u4f24\uff01 \u3000\u3000\u9f99\u8c61\u6cd5\u738b\u4e5f\u7aef\u7684\u662f\u5389\u5bb3\u4e4b\u6781\uff0c\u4ed6\u53cc\u624b\u7a7a\u7a7a\uff0c\u5e76\u65e0\u5175\u5668\uff0c\u5c31\u5728\u4ed6\u8eab\u4e0a\u6240\u62ab\u7684\u90a3\u4ef6\u5927\u7ea2\u8888\u88df \u5f53\u4f5c\u5175\u5668\uff0c\u8888\u88df\u4e00\u6296\uff0c\u767b\u65f6\u5c31\u50cf\u5e73\u5730\u6d8c\u8d77\u4e00\u7247\u7ea2\u971e\uff0c\u5411\u5b5f\u5c11\u521a\u75be\u5377\u8fc7\u6765\uff0c\u8000\u773c\u751f\u8f89\uff01 \u3000\u3000\u53ea\u542c\u5f97\u201c\u55e4\u201d\u7684\u4e00\u58f0\u54cd\uff0c\u5251\u5c16\u4ece\u8888\u88df\u5212\u8fc7\uff0c\u8888\u88df\u53ea\u662f\u7a7f\u4e86\u4e2a\u9488\u9f3b\u822c\u7684\u5c0f\u5b54\uff0c\u82e5\u4e0d\u662f\u4ed4\u7ec6 \u89c2\u5bdf\uff0c\u65c1\u4eba\u6839\u672c\u5c31\u770b\u4e0d\u51fa\u6765\u3002\u5b5f\u5c11\u521a\u8fd9\u4e00\u62db\u51cc\u5389\u4e4b\u6781\u7684\u6740\u624b\u7edd\u62db\uff0c\u7adf\u7ed9\u4ed6\u8f7b\u63cf\u6de1\u5199\u7684\u5316\u89e3\u5f00 \u53bb\uff0c\u6307\u5c16\u201c\u6ed1\u201d\u8fc7\u4e86\u4e00\u8fb9\u3002 \u3000\u3000\u5b5f\u5c11\u521a\u5927\u5403\u4e00\u60ca\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u8fd9\u5927\u548c\u5c1a\u5982\u6b64\u72c2\u5984\uff0c\u4ed6\u786e\u5b9e\u662f\u6709\u771f\u624d\u5b9e\u5b66\uff0c\u5185\u529f \u7684\u7cbe\u7eaf\uff0c\u53ea\u6015\u5f53\u4e16\u662f\u65e0\u4eba\u80fd\u53ca\u7684\u4e86\u3002\u201d \u3000\u3000\u6b8a\u4e0d\u77e5\u5b5f\u5c11\u521a\u56fa\u7136\u5403\u60ca\uff0c\u4ed6\u7684\u5bf9\u4e8e\u9f99\u8c61\u6cd5\u738b\u4ea6\u662f\u4e0d\u7531\u5f97\u5fc3\u5934\u4e00\u51db\u3002\u539f\u6765\u4ed6\u8fd9\u4ef6\u88df\u88df\u4e43\u662f \u4e00\u4ef6\u5b9d\u7269\uff0c\u7528\u963f\u5c14\u6cf0\u5c71\u7279\u4ea7\u7684\u5929\u8695\u4e1d\u5236\u7684\uff0c\u4e00\u6761\u8695\u4e1d\u62c9\u957f\u4e86\u8db3\u6709\u4e00\u4e08\u591a\u957f\uff0c\u97e7\u529b\u4e4b\u5f3a\u53ef\u60f3\u800c \u77e5\u3002\u4ed6\u7528\u88ad\u88df\u4f5c\u4e3a\u6b66\u5668\uff0c\u5e73\u751f\u4e0d\u77e5\u632b\u8d25\u8fc7\u591a\u5c11\u5f3a\u654c\uff0c\u4ece\u6765\u6ca1\u6709\u8fc7\u635f\u4f24\u3002\u5982\u4eca\u867d\u7136\u53ea\u662f\u7a7f\u4e86\u4e00 \u4e2a\u53e3\uff0c\u5df2\u7ecf\u662f\u5927\u5927\u51fa\u4e4e\u4ed6\u7684\u610f\u6599\u4e4b\u5916\u4e86\u3002 \u3000\u3000\u9f99\u8c61\u6cd5\u738b\u5fc3\u5934\u4e00\u51db\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u542c\u8bf4\u6b64\u4eba\u5251\u672f\u5929\u4e0b\u65e0\u53cc\uff0c\u679c\u7136\u662f\u540d\u4e0d\u865a\u4f20\u3002\u6211\u5df2\u7ecf\u4f7f \u51fa\u4e86\u9f99\u8c61\u529f\uff0c\u8888\u88df\u4ecd\u7136\u7ed9\u4ed6\u523a\u7834\uff0c\u5018\u4e0d\u5168\u529b\u65bd\u4e3a\uff0c\u53ea\u6015\u662f\u96be\u64cd\u80dc\u5238\uff01\u201d \u3000\u3000\u53cc\u65b9\u5404\u5177\u6212\u5fc3\uff0c\u5f7c\u6b64\u90fd\u662f\u628a\u5e73\u751f\u6240\u5b66\u65bd\u5c55\u51fa\u6765\uff0c\u4f46\u89c1\u4e00\u5e45\u7ea2\u4e91\u88f9\u4f4f\u4e00\u9053\u767d\u5149\uff0c\u76d8\u65cb\u98de \u821e\uff0c\u770b\u5f97\u4f17\u4eba\u773c\u82b1\u64a9\u4e71\u3002 \u3000\u3000\u9f99\u8c61\u6cd5\u738b\u7684\u5185\u529f\uff0c\u5df2\u81fb\u5316\u5883\uff0c\u5168\u529b\u65bd\u4e3a\uff0c\u8888\u88df\u6296\u5f97\u547c\u547c\u98ce\u54cd\uff0c\u5982\u540c\u6da8\u6ee1\u7684\u98ce\u5e06\u4e00\u6837\uff0c\u4e00 \u4ef6\u67d4\u82e5\u65e0\u7269\u7684\u8888\u88df\uff0c\u7f69\u5c06\u4e0b\u6765\uff0c\u5374\u4f3c\u5343\u65a4\u538b\u9876\uff0c\u9976\u662f\u5b5f\u5c11\u521a\u8fd9\u6837\u9ad8\u5f3a\u7684\u672c\u9886\uff0c\u4e5f\u611f\u5230\u6709\u70b9\u900f \u4e0d\u8fc7\u6c14\u6765\uff01 \u3000\u3000\u5b5f\u5c11\u521a\u5251\u6cd5\u4e00\u53d8\uff0c\u628a\u8f7b\u7075\u7fd4\u52a8\u7684\u4e0a\u4e58\u5251\u672f\u53d1\u6325\u5f97\u6dcb\u6f13\u5c3d\u81f4\uff0c\u5f53\u771f\u662f\u77eb\u82e5\u6e38\u9f99\uff0c\u7fe9\u5982\u60ca \u9e3f\uff0c\u77bb\u4e4b\u5728\u524d\uff0c\u5ffd\u7109\u5728\u540e\uff0c\u77bb\u4e4b\u5728\u5de6\uff0c\u5ffd\u7109\u5728\u53f3\uff0c\u867d\u7136\u4ed6\u7684\u5251\u5c16\u4e00\u78b0\u4e0a\u8888\u88df\uff0c\u5c31\u7ed9\u8361\u5f00\uff0c\u4f46 \u9f99\u8c61\u6cd5\u738b\u5374\u4e5f\u4e0d\u80fd\u4e0d\u63d0\u5fc3\u540a\u80c6\uff0c\u751f\u6015\u4ed6\u4e58\u9699\u800c\u5165\u4e86\u3002 \u3000\u3000\u97e9\u5927\u7ef4\u4e0e\u674e\u601d\u5357\u62b5\u638c\u800c\u5750\uff0c\u5404\u81ea\u8fd0\u529f\uff0c\u5bf9\u8eab\u8fb9\u7684\u8fd9\u573a\u6076\u6597\uff0c\u604d\u82e5\u89c6\u800c\u4e0d\u89c1\uff0c\u542c\u800c\u4e0d\u95fb\u3002 \u8fc7\u4e86\u4e00\u4f1a\uff0c\u97e9\u5927\u7ef4\u7684\u5934\u9876\u6563\u53d1\u51fa\u70ed\u817e\u817e\u7684\u767d\u6c14\uff0c\u674e\u601d\u5357\u7684\u8138\u8272\u6e10\u6e10\u7ea2\u6da6\u3002 \u3000\u3000\u9f99\u8c61\u6cd5\u738b\u7684\u90a3\u56db\u4e2a\u5f1f\u5b50\u4e5f\u662f\u8bc6\u8d27\u7684\u4eba\uff0c\u77e5\u9053\u97e9\u5927\u7ef4\u6b63\u4ee5\u672c\u8eab\u7684\u771f\u529b\u52a9\u674e\u601d\u5357\u805a\u6c14\u6d3b\u8840\uff0c \u6d88\u9664\u4f53\u4e2d\u6bd2\u7d20\uff0c\u5230\u4e86\u7d27\u8981\u7684\u5173\u5934\u3002\u4e8e\u662f\u4ed6\u4eec\u56db\u4eba\u79c1\u4e0b\u5546\u8bae\uff0c\u4e00\u4e2a\u8bf4\u9053\uff1a\u201c\u5e08\u7236\u53ea\u662f\u4e0d\u8bb8\u6211\u4eec \u63d2\u624b\uff0c\u5e76\u6ca1\u6709\u8bf4\u4e0d\u8bb8\u6211\u4eec\u6349\u62ff\u56da\u72af\u3002\u201d\u4e00\u4e2a\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u59d3\u97e9\u7684\u8001\u513f\u672c\u9886\u975e\u51e1\uff0c\u674e\u601d\u5357 \u662f\u4e2d\u539f\u7684\u6b66\u6797\u76df\u4e3b\uff0c\u6b66\u529f\u60f3\u5fc5\u66f4\u4e3a\u4e86\u5f97\uff0c\u82e5\u7136\u7ed9\u4ed6\u6062\u590d\u4e86\u529f\u529b\uff0c\u654c\u65b9\u5c31\u662f\u5982\u864e\u6dfb\u7ffc\u4e86\u3002\u201d\u4e00 \u4e2a\u8bf4\u9053\uff1a\u201c\u53ea\u4e0d\u77e5\u54b1\u4eec\u6253\u4e0d\u6253\u5f97\u8fc7\u8fd9\u8001\u513f\uff1f\u201d\u4e00\u4e2a\u8bf4\u9053\uff1a\u201c\u4e00\u5bf9\u4e00\u6253\u4ed6\u4e0d\u8fc7\uff0c\u54b1\u4eec\u56db\u4e2a\u4eba\u4e00 \u9f50\u4e0a\u5440\uff01\u201d \u3000\u3000\u97e9\u4f69\u745b\u89c1\u4ed6\u4eec\u4ea4\u5934\u63a5\u8033\uff0c\u8fde\u5fd9\u8bf4\u9053\uff1a\u201c\u7239\u7239\u5c0f\u5fc3\uff0c\u53ea\u6015\u4ed6\u4eec\u8981\u6765\u2014\u2014\u201d\u201c\u5077\u88ad\u201d\u4e8c\u5b57\u672a \u66fe\u51fa\u53e3\uff0c\u8fd9\u56db\u4e2a\u4eba\u679c\u7136\u5c31\u4e00\u9f50\u6251\u4e0a\u6765\u3002 \u3000\u3000\u97e9\u5927\u7ef4\u6b64\u5b66\u6df1\u6e5b\uff0c\u867d\u7136\u4e0d\u662f\u773c\u89c2\u56db\u9762\uff0c\u5374\u662f\u8033\u542c\u516b\u65b9\uff0c\u542c\u5f97\u80cc\u540e\u52b2\u98ce\u88ad\u6765\uff0c\u5934\u4e5f\u4e0d\u56de\uff0c \u817e\u51fa\u5de6\u624b\uff0c\u53cd\u624b\u4fbf\u662f\u4e00\u638c\uff01 \u3000\u3000\u53ea\u542c\u5f97\u201c\u8f70\u201d\u7684\u4e00\u58f0\uff0c\u6700\u524d\u6251\u4e0a\u7684\u90a3\u4e24\u4e2a\u6b66\u58eb\u56db\u638c\u9f50\u51fa\uff0c\u4ecd\u7136\u7ed9\u97e9\u5927\u7ef4\u9707\u9000\u4e86\u51e0\u6b65\uff0c\u53e6 \u5916\u4e24\u540d\u6b66\u58eb\uff0c\u4e00\u4e2a\u7528\u5200\uff0c\u4e00\u4e2a\u7528\u5251\uff0c\u5de6\u53f3\u9f50\u4e0a\uff0c\u4e5f\u662f\u7ed9\u4ed6\u7684\u638c\u98ce\u8361\u6b6a\u4e86\u5175\u5668\uff0c\u5927\u5403\u4e00\u60ca\u4e4b \u4e0b\uff0c\u4e0d\u6562\u8e81\u8fdb\uff0c\u5148\u9000\u4e24\u6b65\u3002 \u3000\u3000\u6b8a\u4e0d\u77e5\u8fd9\u56db\u4e2a\u9f99\u8c61\u6cd5\u738b\u7684\u5f1f\u5b50\u56fa\u7136\u5403\u60ca\uff0c\u97e9\u5927\u7ef4\u66f4\u662f\u5403\u60ca\u4e0d\u5c0f\uff0c\u4ed6\u4ee5\u4e3a\u8fd9\u4e00\u638c\u6700\u5c11\u53ef\u4ee5 \u51fb\u5012\u5bf9\u65b9\u4e00\u4e8c\u4eba\u7684\uff0c\u54ea\u77e5\u8fd9\u56db\u4e2a\u4eba\u90fd\u53ea\u662f\u8eab\u5f62\u7565\u6643\uff0c\u7a0d\u9000\u51e0\u6b65\u800c\u5df2\u3002 \u3000\u3000\u97e9\u5927\u7ef4\u5206\u51fa\u4e86\u4e00\u534a\u529f\u529b\u62b5\u5fa1\u654c\u4eba\uff0c\u53f3\u638c\u4ecd\u7136\u62b5\u7740\u674e\u601d\u5357\u5de6\u638c\u638c\u5fc3\uff0c\u53ea\u89c9\u674e\u601d\u5357\u7684\u638c\u5fc3\u5fae \u5fae\u53d1\u70ed\uff0c\u90a3\u662f\u771f\u6c14\u79ef\u805a\uff0c\u672a\u80fd\u758f\u5bfc\u4e4b\u6545\u3002\u97e9\u5927\u7ef4\u6697\u53eb\u4e0d\u5999\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6211\u82e5\u662f\u7528\u5230\u4e03\u516b\u6210 \u4ee5\u4e0a\u7684\u529f\u529b\u53bb\u5bf9\u4ed8\u8fd9\u56db\u4e2a\u4eba\uff0c\u81ea\u662f\u53ef\u4ee5\u5c06\u4ed6\u4eec\u51fb\u8d25\uff0c\u4f46\u8fd9\u6837\u4e00\u6765\uff0c\u53ea\u6015\u5374\u662f\u8981\u8fde\u7d2f\u674e\u601d\u5357 \u4e86\u3002\u201d \u3000\u3000\u539f\u6765\u8fd9\u56db\u4e2a\u4eba\u90fd\u662f\u9f99\u8c61\u6cd5\u738b\u5f97\u610f\u9ad8\u8db3\uff0c\u6bcf\u4e00\u4e2a\u4eba\u7684\u672c\u9886\u90fd\u5728\u521a\u624d\u7684\u963f\u535c\u5362\u4e0e\u547c\u9ece\u5962\u4e4b \u4e0a\uff0c\u56db\u4eba\u8054\u624b\uff0c\u97e9\u5927\u7ef4\u8981\u5206\u51fa\u5fc3\u795e\u7167\u987e\u674e\u601d\u5357\uff0c\u81ea\u662f\u96be\u4ee5\u4f24\u5f97\u4ed6\u4eec\uff0c\u53cd\u800c\u88ab\u8feb\u8981\u6539\u53d6\u5b88\u52bf \u4e86\u3002 \u3000\u3000\u97e9\u4f69\u745b\u201c\u5478\u201d\u7684\u4e00\u58f0\u9a82\u9053\uff1a\u201c\u4e0d\u8981\u8138\uff0c\u5802\u5802\u4e00\u4e2a\u56fd\u5e08\uff0c\u8bf4\u4e86\u8bdd\u4e5f\u4e0d\u7b97\u6570\uff01\u201d \u3000\u3000\u5b5f\u5c11\u521a\u5530\u5530\u5530\u8fde\u73af\u4e09\u5251\uff0c\u8feb\u4f7f\u9f99\u8c61\u6cd5\u738b\u6321\u4e86\u4e24\u62db\uff0c\u677e\u4e86\u53e3\u6c14\uff0c\u559d\u9053\uff1a\u201c\u5927\u4e08\u592b\u4e00\u8a00\u65e2 \u51fa\uff0c\u9a77\u9a6c\u96be\u8ffd\uff0c\u9f99\u8c61\u6cd5\u738b\uff0c\u4f60\u521a\u624d\u662f\u600e\u4e48\u8bf4\u7684\uff1f\u201d \u3000\u3000\u9f99\u8c61\u6cd5\u738b\u88df\u8888\u4e00\u4e2a\u76d8\u5934\u75be\u821e\uff0c\u7f69\u5c06\u4e0b\u6765\uff0c\u8fd8\u4e86\u4e24\u62db\uff0c\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u8bf4\uff0c\u4f60\u82e5\u80dc \u5f97\u4e86\u6211\uff0c\u4f60\u4eec\u8981\u8d70\uff0c\u6211\u51b3\u4e0d\u963b\u62e6\uff0c\u4ece\u6b64\u4e4b\u540e\uff0c\u6211\u4e5f\u51b3\u4e0d\u8db3\u5c65\u4e2d\u539f\u3002\u4f46\u6211\u6ca1\u6709\u8bf4\u8fc7\uff0c\u4e0d\u8bb8\u522b\u4eba \u53bb\u6349\u62ff\u56da\u72af\u5440\uff01\u54c8\u54c8\uff0c\u4f55\u51b5\u4f60\u73b0\u5728\u4e5f\u80dc\u4e0d\u4e86\u6211\uff01\u201d \u3000\u3000\u9f99\u8c61\u6cd5\u738b\u4e4b\u8a00\u867d\u662f\u53d6\u5de7\uff0c\u4f46\u5374\u4e5f\u80fd\u591f\u81ea\u5706\u5176\u8bf4\uff0c\u4e0d\u7b97\u8fdd\u80cc\u8bfa\u8a00\u3002\u5b5f\u5c11\u521a\u5251\u672f\u5929\u4e0b\u65e0\u53cc\uff0c \u529f\u529b\u5374\u662f\u7a0d\u6709\u4e0d\u53ca\uff0c\u5206\u795e\u8bf4\u8bdd\uff0c\u767b\u65f6\u5c31\u7ed9\u9f99\u8c61\u6cd5\u738b\u62a2\u4e86\u5148\u624b\uff0c\u5f62\u52bf\u5728\u4ed6\u8888\u88df\u7b3c\u7f69\u4e4b\u4e0b\uff0c\u51e0\u4e4e \u96be\u4ee5\u4f38\u5c55\u3002\u5b5f\u5c11\u521a\u5fc3\u4e2d\u5492\u9a82\uff0c\u5374\u662f\u65e0\u53ef\u5948\u4f55\uff0c\u53ea\u597d\u91cd\u6444\u5fc3\u795e\uff0c\u5168\u529b\u5e94\u4ed8\u5bf9\u65b9\u7684\u653b\u52bf\u3002 \u3000\u3000\u9f99\u8c61\u6cd5\u738b\u7684\u56db\u4e2a\u5f1f\u5b50\u89c1\u5e08\u7236\u5e76\u4e0d\u8d23\u602a\uff0c\u5b9e\u9645\u662f\u5728\u9f13\u52b1\u4ed6\u4eec\uff0c\u5fc3\u4e2d\u5927\u559c\uff0c\u653b\u5f97\u66f4\u52a0\u51f6\u731b \u4e86\u3002 \u3000\u3000\u97e9\u5927\u7ef4\u96be\u4ee5\u517c\u987e\uff0c\u53ea\u597d\u4ee5\u4e09\u56db\u6210\u7684\u529f\u529b\u81ea\u4fdd\uff0c\u8fd9\u56db\u4e2a\u6b66\u58eb\u60f3\u8981\u653b\u8fdb\u4ed6\u7684\u9632\u5fa1\u5708\u5b50\uff0c\u5374\u4e5f \u4e0d\u80fd\u3002\u5176\u4e2d\u4e00\u4eba\u6700\u653b\u5fc3\u8ba1\u3002\u5ffd\u5730\u8bf4\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u5c0f\u5a18\u513f\u4e5f\u662f\u56da\u72af\uff0c\u4e0d\u80fd\u7ed9\u5979\u4eec\u8dd1\u4e86\uff01\u201d\u53e6\u4e00 \u4e2a\u7acb\u5373\u8bf4\u9053\uff1a\u201c\u5bf9\uff0c\u5148\u628a\u5979\u4eec\u62ff\u4e86\uff01\u201d \u3000\u3000\u53ea\u542c\u5f97\u201c\u55e4\u201d\u7684\u4e00\u58f0\u54cd\uff0c\u97e9\u4f69\u745b\u7684\u8863\u89d2\u7ed9\u4e00\u540d\u6b66\u58eb\u6495\u4e86\u4e00\u5e45\uff0c\u5e78\u800c\u5979\u8eab\u6cd5\u8f7b\u7075\uff0c\u6b66\u529f\u867d \u5931\uff0c\u811a\u6b65\u4e00\u6162\uff0c\u53ca\u65f6\u907f\u4e86\u5f00\u53bb\u3002 \u3000\u3000\u6768\u5a49\u6709\u4e00\u628a\u9632\u8eab\u7684\u5315\u9996\uff0c\u88ab\u64d2\u4e4b\u65f6\uff0c\u56e0\u4e3a\u62d6\u96f7\u4e0d\u6562\u5bf9\u5979\u65e0\u793c\uff0c\u662f\u4ee5\u4ecd\u7136\u85cf\u5728\u8eab\u5185\uff0c\u672a\u7ed9 \u641c\u53bb\u3002\u9f99\u8c61\u6cd5\u738b\u7684\u7b2c\u4e09\u540d\u5f1f\u5b50\u6251\u6765\u6293\u5979\uff0c\u6768\u5a49\u638c\u5fc3\u5f80\u5916\u4e00\u767b\uff0c\u9732\u51fa\u4e86\u65e9\u5c31\u63e1\u5728\u624b\u4e2d\u7684\u5315\u9996\u3002 \u3000\u3000\u6768\u5a49\u7684\u5bb6\u4f20\u5200\u6cd5\u4e43\u662f\u6b66\u6797\u4e00\u7edd\uff0c\u6b64\u65f6\u4ee5\u77ed\u5315\u4f5c\u8fd1\u8eab\u7684\u640f\u51fb\uff0c\u8fd9\u4e00\u62db\u201c\u7384\u9e1f\u5212\u7802\u201d\u66f4\u662f\u51cc \u5389\u975e\u5e38\uff01 \u3000\u3000\u8fd9\u540d\u6b66\u571f\u672c\u6765\u662f\u77e5\u9053\u6768\u5a49\u5185\u529b\u5df2\u5931\u7684\uff0c\u4f46\u5728\u8fd9\u4ed3\u731d\u4e4b\u95f4\uff0c\u7a81\u7136\u770b\u89c1\u5200\u5149\u8000\u76ee\uff0c\u4e00\u652f\u5315\u9996 \u5411\u4ed6\u54bd\u5589\u5212\u6765\uff0c\u5374\u662f\u4e0d\u7531\u5f97\u4e0d\u9aa4\u5403\u4e00\u60ca\uff0c\u8fde\u5fd9\u8eb2\u95ea\u4e86\u3002 \u3000\u3000\u6768\u5a49\u4f53\u5f31\u6c14\u865a\uff0c\u4e00\u5200\u523a\u7a7a\uff0c\u51b2\u523a\u8fc7\u6025\uff0c\u811a\u6b65\u4e00\u4e2a\u8dc4\u8e09\uff0c\u9669\u4e9b\u8dcc\u5012\u3002\u8fd9\u6b66\u58eb\u84e6\u5730\u7701\u89c9\uff0c\u54c8 \u54c8\u7b11\u9053\uff1a\u201c\u7f8e\u4eba\u513f\uff0c\u4f60\u522b\u614c\uff0c\u56db\u7687\u5b50\u6b22\u559c\u4f60\uff0c\u6211\u4e0d\u4f1a\u4f24\u4f60\u7684\uff0c\u4f60\u4e5f\u7528\u4e0d\u7740\u548c\u6211\u62fc\u547d\u201d\uff01\u201c\u62fc \u547d\u201d\u4e8c\u5b57\u521a\u521a\u51fa\u53e3\uff0c\u5ffd\u89c9\u4e00\u80a1\u5927\u529b\u63a8\u6765\uff0c\u4ed6\u672c\u6765\u662f\u6b63\u5728\u518d\u6b21\u6251\u4e0a\u7684\uff0c\u7ed9\u8fd9\u80a1\u5927\u529b\u4e00\u63a8\uff0c\u767b\u65f6 \u53c8\u63a5\u8fde\u9000\u4e86\u4e09\u6b65\u3002 \u3000\u3000\u539f\u6765\u8fd9\u662f\u97e9\u5927\u7ef4\u4ee5\u5288\u7a7a\u638c\u66ff\u6768\u5a49\u89e3\u5371\uff0c\u4ed6\u8fd9\u6b21\u7528\u4e86\u4e94\u6210\u4ee5\u4e0a\u7684\u529f\u529b\uff0c\u65b9\u80fd\u5728\u8ddd\u79bb\u4e03\u6b65\u4e4b \u5185\uff0c\u5c06\u90a3\u4eba\u903c\u9000\u3002\u674e\u601d\u5357\u5728\u5bfc\u6c14\u7eb3\u5165\u4e39\u7530\uff0c\u201c\u5916\u63f4\u201d\u5ffd\u5730\u51cf\u5f31\uff0c\u674e\u601d\u5357\u5fc3\u5934\u4e00\u9707\uff0c\u542c\u5230\u4e86\u6768 \u5a49\u7684\u5c16\u53eb\u4e4b\u58f0\u3002 \u3000\u3000\u674e\u601d\u5357\u672c\u6765\u662f\u5728\u95ed\u76ee\u8fd0\u529f\uff0c\u5bf9\u5916\u95f4\u4e00\u5207\u542c\u800c\u4e0d\u95fb\u7684\uff0c\u6b64\u65f6\u7531\u4e8e\u5fc3\u795e\u4e00\u5206\uff0c\u7a81\u7136\u542c\u5230\u4e86\u6768 \u5a49\u7684\u53eb\u58f0\uff0c\u4e0d\u7531\u5f97\u5927\u5403\u4e00\u60ca\uff0c\u53eb\u9053\uff1a\u201c\u5a49\u59b9\uff0c\u4f60\u600e\u4e48\u5566\u3002\u201d \u3000\u3000\u97e9\u5927\u7ef4\u8fde\u5fd9\u558a\u9053\uff1a\u201c\u5feb\u8eb2\u5230\u8fd9\u513f\uff01\u201d\u5de6\u638c\u8fde\u6325\uff0c\u547c\u547c\u62cd\u51fa\u4e09\u638c\uff0c\u5c06\u90a3\u56db\u540d\u6b66\u58eb\u903c\u51fa\u516b\u5c3a \u5f00\u5916\uff0c\u6768\u5a49\u3001\u97e9\u4f69\u745b\u8eab\u5f62\u4e00\u98d8\u4e00\u95ea\uff0c\u95ea\u8fdb\u4e86\u97e9\u5927\u7ef4\u638c\u529b\u7684\u5708\u5b50\u3002 \u3000\u3000\u97e9\u5927\u7ef4\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u601d\u5357\uff0c\u5207\u52ff\u5206\u795e\uff0c\u4f60\u8981\u5206\u795e\uff0c\u4e0d\u4f46\u5bb3\u4e86\u81ea\u5df1\uff0c\u4e5f\u8981\u5bb3\u4e86\u6768\u59d1\u5a18 \u4e86\uff01\u201d \u3000\u3000\u90a3\u56db\u540d\u6b66\u58eb\u9000\u800c\u590d\u4e0a\uff0c\u4e3a\u9996\u7684\u5927\u5e05\u5144\u72de\u7b11\u9053\uff1a\u201c\u97e9\u8001\u5934\u513f\uff0c\u4f60\u6b66\u529f\u518d\u5f3a\uff0c\u53ea\u6015\u4e5f\u662f\u96be\u4f3c \u987e\u5f97\u5468\u5168\u5427\uff01\u4f60\u4e0d\u60f3\u4ee4\u5973\u513f\u9001\u547d\uff0c\u53ea\u8981\u4fdd\u8fd9\u5c0f\u5b50\u548c\u8fd9\u4f4d\u6768\u59d1\u5a18\u7684\u5b89\u5168\uff0c\u9664\u975e\u662f\u7acb\u5373\u5411\u6211\u4eec\u6295 \u964d\uff0c\u5426\u5219\u53eb\u4f60\u540e\u6094\u83ab\u53ca\uff01\u201d \u3000\u3000\u97e9\u5927\u7ef4\u4e0d\u6562\u52a8\u6012\uff0c\u6c89\u4f4f\u4e86\u6c14\uff0c\u5355\u638c\u5e94\u4ed8\u3002\u4ed6\u8981\u7ed9\u674e\u601d\u5357\u8fd0\u529f\u7597\u529f\uff0c\u672c\u6765\u5df2\u662f\u96be\u4ee5\u517c\u987e\uff0c \u6b64\u65f6\u66f4\u8981\u540c\u65f6\u7167\u987e\u4e24\u4e2a\u5df2\u7ecf\u5931\u4e86\u6b66\u529f\u7684\u5973\u5b50\uff0c\u679c\u7136\u7ed9\u90a3\u4eba\u8bf4\u4e2d\uff0c\u53ea\u611f\u5230\u5403\u529b\u975e\u5e38\uff0c\u5de6\u652f\u53f3\u7ecc \u4e86\u3002 \u3000\u3000\u6768\u5a49\u8f7b\u8f7b\u5730\u4f0f\u5728\u674e\u601d\u5357\u7684\u80a9\u5934\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u82e5\u662f\u5357\u54e5\u6709\u4ec0\u4e48\u4e0d\u6d4b\uff0c\u6211\u8fd8\u600e\u80fd\u518d\u6d3b\uff1f\u8981 \u6b7b\u6211\u4e5f\u5f97\u5148\u53bb\u4e00\u6b65\uff01\u201d\u5979\u7528\u8eab\u4f53\u63a9\u62a4\u674e\u601d\u5357\uff0c\u5b58\u4e86\u5fc5\u6b7b\u4e4b\u5fc3\uff0c\u53cd\u800c\u5fc3\u5883\u5e73\u9759\uff0c\u5766\u7136\u65e0\u60e7\u3002 \u3000\u3000\u6768\u5a49\u867d\u7136\u53ea\u662f\u8f7b\u8f7b\u5730\u4f0f\u5728\u674e\u601d\u5357\u7684\u80a9\u5934\uff0c\u4e0d\u6562\u538b\u7740\u4ed6\uff0c\u674e\u601d\u5357\u5df2\u662f\u611f\u5230\u4e86\u5979\u7684\u8eab\u4f53\u6e29\u6696 \u4e86\u3002\u4e24\u4eba\u5fc3\u610f\u76f8\u901a\uff0c\u674e\u601d\u5357\u5927\u4e3a\u611f\u52a8\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6211\u51b3\u4e0d\u80fd\u6b7b\uff0c\u6211\u5f97\u8d76\u5feb\u6062\u590d\u529f\u529b\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u53d7\u4e86\u9f13\u821e\uff0c\u51b3\u610f\u6c42\u751f\uff0c\u5fc3\u5883\u767b\u65f6\u4e5f\u5c31\u5e73\u9759\u4e0b\u672a\u3002\u5f53\u4e0b\u91cd\u53c8\u51dd\u795e\u8fd0\u529f\uff0c\u5f53\u771f\u662f\u8fdb\u5165 \u4e86\u89c6\u800c\u4e0d\u89c1\uff0c\u542c\u800c\u4e0d\u95fb\u7684\u5883\u754c\u4e86\u3002 \u3000\u3000\u5b5f\u5c11\u521a\u4e0e\u9f99\u8c61\u6cd5\u738b\u672c\u6765\u662f\u5404\u6709\u6240\u957f\uff0c\u96be\u5206\u9ad8\u4e0b\u7684\uff0c\u6b64\u65f6\u89c1\u97e9\u5927\u7ef4\u4e0e\u674e\u601d\u5357\u7b49\u4eba\u5747\u5df2\u8eab\u5904 \u9669\u5883\uff0c\u9976\u662f\u4ed6\u6b66\u5b66\u5982\u4f55\u6df1\u6e5b\uff0c\u5982\u4f55\u80fd\u591f\u81ea\u5236\uff0c\u5fc3\u4e2d\u4e5f\u662f\u4e0d\u7981\u6709\u70b9\u614c\u4e71\u4e86\u3002 \u3000\u3000\u9ad8\u624b\u6bd4\u6597\uff0c\u90a3\u5bb9\u5f97\u5fc3\u795e\u614c\u4e71\uff1f\u9f99\u8c61\u6cd5\u738b\u62a2\u4e86\u4e0a\u98ce\uff0c\u8888\u88df\u62db\u5c55\uff0c\u604d\u82e5\u7ea2\u4e91\u8986\u76d6\uff0c\u53c8\u4f3c\u6cf0\u5c71 \u538b\u9876\uff0c\u5b5f\u5c11\u521a\u7684\u957f\u5251\u6307\u4e1c\u6253\u897f\uff0c\u6307\u5357\u6253\u5317\uff0c\u4e00\u9053\u767d\u5149\uff0c\u5de6\u51b2\u53f3\u7a81\uff0c\u603b\u662f\u51b2\u4e0d\u7834\u90a3\u4e00\u7247\u201c\u7ea2 \u4e91\u201d\uff0c\u5c31\u4f3c\u9ec4\u660f\u7684\u843d\u65e5\u4f59\u6656\uff0c\u88ab\u6df9\u6ca1\u5728\u4e91\u6d77\u4e4b\u4e2d\u4e00\u6837\u3002 \u3000\u3000\u97e9\u5927\u7ef4\u76ee\u5149\u4e00\u77a5\uff0c\u5411\u5b5f\u5c11\u4e1a\u521a\u90a3\u8fb9\u6295\u4e86\u4e00\u773c\uff0c\u4e0d\u7531\u5f97\u6697\u6697\u53f9\u4e86\u53e3\u6c14\uff0c\u5fc3\u91cc\u81ea\u601d\uff1a\u201c\u60f3\u4e0d \u5230\u6211\u548c\u5b5f\u5927\u4fa0\u7adf\u7136\u8981\u540c\u4e00\u65e5\u4e27\u751f\u6b64\u5730\uff0c\u8fd8\u8fde\u7d2f\u4e86\u674e\u601d\u5357\uff01\u4e0e\u5176\u675f\u624b\u5f85\u6bd9\uff0c\u4e0d\u5982\u6b7b\u91cc\u6c42\u751f\uff1f\u201d \u4f46\u4ed6\u6b66\u5b66\u6df1\u6e5b\uff0c\u7acb\u5373\u4fbf\u53c8\u60f3\u5230\uff1a\u201c\u6211\u6216\u8005\u53ef\u4ee5\u6b7b\u91cc\u6c42\u751f\uff0c\u4f46\u6211\u4e00\u7ad9\u8d77\u6765\u5168\u529b\u548c\u4ed6\u4eec\u640f\u6597\uff0c\u674e \u601d\u5357\u5374\u662f\u5fc5\u6b7b\u65e0\u7591\uff0c\u4f69\u745b\u513f\u548c\u6768\u59d1\u5a18\u4e5f\u5b9a\u7136\u9003\u4e0d\u51fa\u53bb\uff01\u201d \u3000\u3000\u97e9\u5927\u7ef4\u6b64\u65f6\u5df2\u662f\u7ed9\u653b\u5f97\u5e94\u4ed8\u4e0d\u6687\uff0c\u6b63\u81ea\u8e0c\u8e87\u83ab\u51b3\u4e4b\u9645\uff0c\u5ffd\u89c1\u674e\u601d\u5357\u53cc\u76ee\u4e00\u5f20\uff0c\u4e00\u58f0\u957f \u5578\uff0c\u5410\u51fa\u4e86\u80f8\u4e2d\u6d4a\u6c14\uff0c\u968f\u5373\u4fbf\u6536\u56de\u53cc\u638c\uff0c\u7ad9\u4e86\u8d77\u6765\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u884c\u5566\uff0c\u97e9\u8001\u524d\u8f88\uff0c\u4f60\u8d76\u5feb \u7ed9\u4ee4\u5a9b\u533b\u6cbb\uff01\u201d \u3000\u3000\u97e9\u5927\u7ef4\u542c\u4ed6\u90a3\u58f0\u957f\u5578\uff0c\u5b9b\u82e5\u9f99\u541f\uff0c\u4e2d\u6c14\u5145\u6c9b\uff0c\u77e5\u9053\u4ed6\u786e\u5b9e\u60ed\u590d\u4e86\u6b66\u529f\uff0c\u5927\u559c\u8bf4\u9053\uff1a \u201c\u597d\uff0c\u6211\u5148\u52a9\u4f60\u4e00\u81c2\u4e4b\u529b\uff01\u201d\u53cc\u638c\u9f50\u53d1\uff0c\u58f0\u5982\u90c1\u96f7\uff0c\u90a3\u56db\u540d\u6b66\u58eb\u5927\u60ca\u4e4b\u4e0b\uff0c\u8fde\u5fd9\u540c\u65f6\u4f7f\u51fa\u9f99 \u8c61\u529f\u5e94\u4ed8\uff0c\u56db\u4eba\u5168\u529b\uff0c\u4ecd\u662f\u4e0d\u7981\u8fde\u8fde\u540e\u9000\u3002 \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u97e9\u8001\u524d\u8f88\uff0c\u7701\u70b9\u6c14\u529b\uff0c\u7ed9\u4ee4\u5a9b\u6551\u6cbb\u8981\u7d27\u3002\u5a49\u59b9\uff0c\u8fd9\u628a\u5315\u9996\u7ed9\u6211\u3002\u201d\u62ff\u4e86\u6768 \u5a49\u7684\u5315\u9996\uff0c\u4e00\u8dc3\u800c\u51fa\uff0c\u7acb\u5373\u548c\u90a3\u56db\u540d\u6b66\u571f\u6076\u6597\u8d77\u6765\uff01 \u3000\u3000\u97e9\u5927\u7ef4\u5438\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4eba\u90fd\u5750\u4e0b\u6765\uff01\u201d\u97e9\u4f69\u745b\u3001\u6768\u5a49\u4f9d\u8a00\u5404\u5750\u4e00\u8fb9\uff0c\u97e9\u5927\u7ef4 \u4f38\u51fa\u53cc\u638c\uff0c\u540c\u65f6\u7ed9\u5979\u4eec\u4e8c\u4eba\u8fd0\u529f\u9a71\u6bd2\u3002 \u3000\u3000\u6768\u5a49\u672c\u6765\u60f3\u8ba9\u4ed6\u4e13\u5fc3\u533b\u6cbb\u97e9\u4f69\u745b\u7684\uff0c\u97e9\u4f69\u745b\u597d\u4f3c\u77e5\u9053\u5979\u7684\u5fc3\u601d\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u80af\u533b\uff0c\u6211 \u4e5f\u966a\u4f60\u3002\u201d\u6768\u5a49\u611f\u5979\u59d0\u59b9\u60c5\u6df1\uff0c\u4e5f\u5c31\u4e0d\u62d8\u5c0f\u674e\u4e86\u3002 \u3000\u3000\u90a3\u56db\u540d\u6b66\u58eb\u77e5\u9053\u673a\u4f1a\u7a0d\u7eb5\u5373\u901d\uff0c\u5168\u529b\u5411\u674e\u601d\u5357\u653b\u51fb\u3002\u674e\u601d\u5357\u4ee5\u5315\u9996\u4f7f\u51fa\u8fbe\u6469\u5251\u6cd5\uff0c\u4f7f\u6765 \u81ea\u662f\u4e0d\u80fd\u5f97\u5fc3\u5e94\u624b\uff0c\u6709\u4e00\u4e2a\u6b66\u571f\u8981\u4ece\u4ed6\u8eab\u8fb9\u62e3\u8fc7\uff0c\u674e\u601d\u5357\u5927\u559d\u4e00\u58f0\uff0c\u731b\u7684\u4e00\u638c\u51fb\u4e0b\uff0c\u53e6\u4e00\u4e2a \u6b66\u58eb\u53cc\u638c\u9f50\u51fa\uff0c\u63a5\u4e86\u8fd9\u4e00\u62db\uff0c\u201c\u54c7\u201d\u7684\u4e00\u53e3\u9c9c\u8840\u55b7\u4e86\u51fa\u6765\u3002\u539f\u6765\u4ed6\u7684\u9f99\u8c61\u529f\u867d\u7136\u6709\u4e86\u76f8\u5f53\u706b \u5019\uff0c\u4f7f\u51fa\u6765\u4ea6\u662f\u9887\u4e3a\u521a\u731b\uff0c\u4f46\u4ecd\u7136\u53ca\u4e0d\u4e0a\u674e\u601d\u5357\u7684\u5c11\u6797\u6d3e\u5ae1\u4f20\u7684\u5185\u5bb6\u529f\u529b\uff01 \u3000\u3000\u53ef\u662f\u674e\u601d\u5357\u63a5\u4e86\u8fd9\u4e00\u638c\u751a\u4e3a\u521a\u731b\u7684\u9f99\u8c61\u529f\uff0c\u7531\u4e8e\u4ed6\u521a\u521a\u6062\u590d\u529f\u529b\uff0c\u4ea6\u662f\u9887\u53d7\u5f71\u54cd\uff0c\u8eab\u5f62 \u8fde\u6643\uff0c\u8fed\u9047\u9669\u62db\uff01 \u3000\u3000\u5b5f\u5c11\u521a\u548c\u674e\u601d\u5357\u5728\u8fd9\u8fb9\u9047\u9669\uff0c\u53e6\u4e00\u8fb9\u5728\u9633\u5929\u96f7\u90a3\u5bc6\u5ba4\u4e4b\u4e2d\u548c\u891a\u4e91\u5cf0\u3001\u8c37\u6db5\u865a\u3001\u67f3\u6d1e\u5929\u3001 \u5d14\u9547\u5c71\u56db\u4eba\uff0c\u66f4\u662f\u5f62\u52bf\u6076\u52a3\uff0c\u4e1a\u5df2\u9677\u4e8e\u56f0\u5883\u4e4b\u4e2d\uff01 \u3000\u3000\u891a\u3001\u8c37\u4e8c\u4eba\u8054\u624b\u5408\u6597\uff0c\u628a\u201c\u5929\u96f7\u529f\u201d\u53d1\u6325\u5f97\u6dcb\u6f13\u5c3d\u81f4\uff0c\u4f46\u6bd5\u7adf\u662f\u6280\u900a\u4e00\u7b79\u3002\u6597\u5230\u4e86\u4e94\u5341 \u62db\u5f00\u5916\uff0c\u891a\u3001\u8c37\u4e8c\u4eba\u90a3\u4e00\u62db\u201c\u96f7\u7535\u4ea4\u8f70\u201d\u53cd\u590d\u4f7f\u7528\u4e86\u5341\u6570\u6b21\u4e4b\u591a\uff0c\u6bcf\u4f7f\u4e0a\u6b21\u5c31\u591a\u8017\u4e00\u5206\u529b\uff0c \u4e24\u4eba\u90fd\u662f\u6c57\u5982\u96e8\u4e0b\uff0c\u5934\u4e0a\u53d1\u51fa\u4e86\u70ed\u817e\u817e\u7684\u767d\u6c14\u3002 \u3000\u3000\u5d14\u9547\u5c71\u548c\u767d\u4e07\u96c4\u6076\u6597\uff0c\u66f4\u662f\u5c8c\u5c8c\u53ef\u5371\u3002\u767d\u4e07\u96c4\u7684\u4e03\u5341\u4e8c\u62db\u9e70\u722a\u624b\u6709\u6570\u5341\u5e74\u7684\u82e6\u7ec3\u4e4b\u529f\uff0c \u5dee\u4e0d\u591a\u5230\u4e86\u7089\u706b\u7eaf\u9752\u7684\u5883\u754c\uff0c\u6bcf\u4f7f\u4e00\u62db\uff0c\u6309\u3001\u62cd\u3001\u64d2\u3001\u62ff\u3001\u6495\u3001\u6293\u3001\u5288\uff0c\u6233\uff0c\u90fd\u662f\u653b\u5411\u654c\u4eba \u7684\u5173\u8282\u8981\u5bb3\u3002\u5d14\u9547\u5c71\u7684\u5927\u529b\u91d1\u521a\u638c\u672c\u662f\u521a\u731b\u7edd\u4f26\u7684\u5c11\u6797\u5bfa\u9547\u5c71\u638c\u6cd5\uff0c\u53ef\u662f\uff0c\u6b63\u6240\u8c13\u68cb\u9ad8\u4e00 \u7740\uff0c\u675f\u624b\u675f\u811a\uff0c\u6597\u5230\u4e86\u4e94\u5341\u62db\u8fc7\u540e\uff0c\u4ed6\u7684\u6c14\u529b\u7ed9\u767d\u4e07\u96c4\u6d88\u8017\u4e86\u4e00\u5927\u534a\uff0c\u4e0d\u4ec5\u6ca1\u6709\u8fd8\u653b\u4e4b\u529b\uff0c \u8fde\u62db\u67b6\u90fd\u6709\u70b9\u52c9\u5f3a\u4e86\u3002\u5f62\u52bf\u4e4b\u6076\uff0c\u6bd4\u891a\u3001\u8c37\u4e8c\u4eba\u5c24\u751a\uff01 \u3000\u3000\u53ea\u6709\u67f3\u6d1e\u5929\u548c\u9633\u575a\u767d\u4e4b\u6218\uff0c\u8fd8\u80fd\u7a0d\u7a0d\u5360\u4e86\u4e00\u70b9\u4e0a\u98ce\uff0c\u4f46\u540c\u60c5\u5f62\u52bf\u4e0d\u4f73\uff0c\u4ed6\u7684\u5fc3\u60c5\u81ea\u4e5f\u4e0d \u80fd\u4e0d\u53d7\u5230\u5f71\u54cd\u3002\u9633\u575a\u767d\u7684\u5251\u4e2d\u5939\u638c\uff0c\u5251\u6cd5\u867d\u662f\u6bd4\u5bf9\u65b9\u4e0d\u4e0a\uff0c\u638c\u529b\u5374\u662f\u6709\u5929\u96f7\u529f\u4f5c\u57fa\u7840\u7684\uff0c\u4ee5 \u638c\u529b\u6d4e\u8865\u5251\u6cd5\u4e4b\u4e0d\u8db3\uff0c\u67f3\u6d1e\u5929\u8981\u60f3\u6446\u8131\u4ed6\u7684\u7ea0\u7f20\uff0c\u817e\u51fa\u624b\u6765\u53bb\u63f4\u52a9\u5d14\u9547\u5c71\uff0c\u8bd5\u4e86\u51e0\u6b21\uff0c\u90fd\u4e0d \u6210\u529f\uff0c\u5fc3\u4e2d\u4e00\u6025\uff0c\u672c\u6765\u662f\u5360\u4e86\u4f18\u52bf\u7684\uff0c\u4e5f\u6e10\u6e10\u7ed9\u9633\u575a\u767d\u6273\u6210\u5e73\u624b\u4e86\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u548c\u8c37\u6db5\u865a\u5148\u540e\u53d1\u4e86\u51e0\u58f0\u957f\u5578\uff0c\u90fd\u6ca1\u56de\u97f3\uff0c\u5fc3\u91cc\u4ff1\u662f\u60f3\u9053\uff1a\u201c\u5b5f\u5927\u4fa0\u5e94\u8be5\u542c\u5f97\u89c1 \u4e86\uff0c\u600e\u7684\u8fd8\u6ca1\u6709\u6765\uff1f\u201d \u3000\u3000\u9633\u5929\u96f7\u7eb5\u58f0\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u9b3c\u568e\u4ec0\u4e48\uff1f\u6ca1\u6709\u4eba\u80fd\u591f\u6551\u4f60\u4eec\u7684\u4e86\uff01\u8981\u6b7b\u8981\u6d3b\uff0c\u5982\u4eca\u53ea\u662f\u5168\u5728 \u4f60\u4eec\u7684\u4e00\u5ff5\u4e4b\u95f4\uff01\u54fc\uff0c\u4f60\u4eec\u8fd8\u4e0d\u61c2\u5f97\u6211\u7684\u610f\u601d\u5417\uff1f\u201d \u3000\u3000\u8c37\u3001\u891a\u4e8c\u4eba\u95f7\u58f0\u4e0d\u54cd\uff0c\u54ac\u7259\u82e6\u6597\uff0c\u63a5\u8fde\u53c8\u653b\u4e86\u4e24\u62db\u201c\u96f7\u7535\u4ea4\u8f70\u201d\uff0c\u9633\u5929\u96f7\u51b7\u51b7\u8bf4\u9053\uff1a \u201c\u56f0\u517d\u4e4b\u6597\uff0c\u53c8\u6709\u4f55\u7528\uff1f\u5ff5\u5728\u4f60\u4eec\u662f\u6211\u7684\u5e08\u4f84\u4efd\u4e0a\uff0c\u53ea\u8981\u4f60\u4eec\u4e56\u4e56\u7684\u5411\u6211\u78d5\u5934\u8bf7\u7f6a\uff0c\u6211\u8fd8\u53ef \u4ee5\u9976\u4e86\u4f60\u4eec\uff01\u201d\u891a\u3001\u8c37\u4e8c\u4eba\u6012\u6781\u6c14\u6781\uff0c\u4f46\u8981\u5168\u795e\u5bf9\u4ed8\uff0c\u5374\u5df2\u4e0d\u6562\u5206\u5fc3\u8bf4\u8bdd\u3002 \u3000\u3000\u5176\u5b9e\u9633\u5929\u96f7\u867d\u8bf4\u4ed6\u4eec\u662f\u56f0\u517d\u4e4b\u6597\uff0c\u4ed6\u81ea\u5df1\u4e5f\u662f\u6709\u70b9\u5bb3\u6015\uff0c\u6697\u6697\u53eb\u82e6\u7684\u3002\u4e0d\u9519\uff0c\u4ed6\u662f\u5360\u4e86 \u4e0a\u98ce\uff0c\u4f46\u5929\u96f7\u529f\u6700\u4e3a\u8017\u635f\u771f\u529b\uff0c\u4e45\u6218\u4e0b\u53bb\uff0c\u53ea\u6015\u7eb5\u7136\u80fd\u591f\u6253\u6b7b\u4e24\u4e2a\u5e08\u4f84\uff01\u81ea\u5df1\u4e5f\u5c11\u4e0d\u4e86\u8981\u5927 \u75c5\u4e00\u573a\uff0c\u6298\u635f\u5341\u5e74\u529f\u529b\uff0c\u5f04\u5f97\u4e0d\u597d\uff0c\u751a\u81f3\u4e5f\u53ef\u80fd\u6709\u6027\u547d\u4e4b\u5371\uff01\u9633\u5929\u96f7\u81ea\u5fd6\u80dc\u5238\u53ef\u64cd\uff0c\u5fc3\u91cc\u53ef \u662f\u4e0d\u613f\u548c\u4ed6\u4eec\u786c\u62fc\uff01 \u3000\u3000\u504f\u504f\u891a\u3001\u8c37\u4e8c\u4eba\u5b81\u6298\u4e0d\u5f2f\uff0c\u5b9a\u8981\u548c\u4ed6\u62fc\u547d\uff0c\u9633\u5929\u96f7\u6697\u6697\u53eb\u82e6\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u4e0d\u77e5 \u6b7b\u6d3b\u7684\u5c0f\u5b50\uff0c\u5012\u662f\u62ff\u4ed6\u4eec\u6ca1\u6709\u529e\u6cd5\uff01\u6211\u548c\u4ed6\u4eec\u4e92\u6597\u5929\u96f7\u529f\uff0c\u65c1\u4eba\u662f\u51b3\u8ba1\u62c6\u89e3\u4e0d\u4e86\u7684\uff0c\u9664\u975e\u9f99 \u8c61\u6cd5\u738b\u5230\u6765\uff01\u5426\u5219\u53ea\u6015\u96be\u514d\u4e24\u8d25\u4ff1\u4f24\u4e86\u3002\u201d \u3000\u3000\u4e00\u4e2a\u5728\u76fc\u671b\u9f99\u8c61\u6cd5\u738b\u524d\u6765\u62c6\u89e3\uff0c\u53e6\u4e00\u65b9\u5219\u5728\u76fc\u671b\u5b5f\u5c11\u521a\u6765\u63f4\uff0c\u53ef\u662f\u540c\u6837\u7684\u5931\u671b\u4e86\uff01 \u3000\u3000\u5ffd\u542c\u53ae\u6740\u4e4b\u58f0\u9690\u9690\u4f20\u6765\uff0c\u9633\u5929\u96f7\u548c\u891a\u4e91\u5cf0\u90fd\u542c\u5f97\u89c1\u4e86\u3002 \u3000\u3000\u9633\u5929\u96f7\u5403\u4e86\u4e00\u60ca\uff0c\u53ea\u89c1\u4e00\u4e2a\u536b\u58eb\u8dd1\u6765\uff0c\u505c\u5728\u95e8\u5916\uff0c\u5927\u58f0\u62a5\u9053\uff1a\u201c\u5b5f\u5c11\u521a\u548c\u97e9\u5927\u7ef4\u56fe\u8c0b\u52ab \u72f1\uff0c\u5982\u4eca\u5df2\u7ed9\u9f99\u8c61\u6cd5\u738b\u56f4\u56f0\uff0c\u8bf7\u56fd\u5e08\u653e\u5fc3\u3002\u201d\u9633\u5929\u96f7\u9053\uff1a\u201c\u674e\u601d\u5357\u5462\uff1f\u201d\u90a3\u536b\u58eb\u9053\uff1a\u201c\u4ed6\u8dd1 \u4e0d\u6389\u7684\uff0c\u6cd5\u738b\u7684\u56db\u4e2a\u5f1f\u5b50\u6b63\u5728\u62ff\u4ed6\uff0c\u6050\u6015\u5982\u4eca\u5df2\u662f\u5c31\u64d2\u4e86\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u6697\u6697\u53eb\u58f0\uff1a\u201c\u82e6\u4e5f\uff01\u201d\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6211\u6307\u671b\u5b5f\u5927\u4fa0\u6765\u63f4\uff0c\u60f3\u4e0d\u5230\u4ed6\u5374\u5df2\u8eab\u9677\u56f0 \u5883\u3002\u6211\u672c\u6765\u662f\u4e0d\u6253\u7b97\u6d3b\u7740\u51fa\u53bb\u7684\uff0c\u4f46\u674e\u76df\u4e3b\u5374\u5982\u4f55\u662f\u597d\uff1f\u201d \u3000\u3000\u9633\u5929\u96f7\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u891a\u5e08\u4f84\uff0c\u4f60\u7edd\u671b\u4e86\u5427\uff1f\u8fd8\u4e0d\u6295\u964d\uff0c\u66f4\u5f85\u4f55\u65f6\uff01\u201d \u3000\u3000\u891a\u4e91\u5cf0\u201c\u5478\u201d\u4e86\u4e00\u58f0\uff0c\u9a82\u9053\uff1a\u201c\u4f60\u8fd9\u4e0d\u77e5\u7f9e\u803b\u7684\u6b3a\u5e08\u706d\u7956\u4e4b\u5f92\uff0c\u8c01\u662f\u4f60\u7684\u5e08\u4f84\uff1f\u4eca\u65e5\u6211 \u82e5\u4e0d\u80fd\u6e05\u7406\u5e08\u95e8\uff0c\u552f\u6709\u4e00\u6b7b\u800c\u5df2\uff01\u6211\u6b7b\u4e86\u6599\u4f60\u4e5f\u6ca1\u6709\u597d\u4e0b\u573a\uff01\u201d\u8c37\u6db5\u865a\u559d\u9053\uff1a\u201c\u4eca\u65e5\u4e0d\u662f\u4f60 \u6b7b\uff0c\u4fbf\u662f\u6211\u4ea1\uff01\u4f11\u8981\u7f57\u55e6\uff01\u201d\u4e24\u4eba\u62fc\u4e86\u5fc5\u6b7b\u4e4b\u5fc3\uff0c\u4e00\u54ac\u7259\u6839\uff0c\u7eb5\u7136\u6c14\u529b\u4e0d\u52a0\uff0c\u5374\u662f\u8d8a\u6218\u8d8a \u52c7\u3002 \u3000\u3000\u9633\u5929\u96f7\u51dd\u795e\u4e00\u542c\uff0c\u5ffd\u53c8\u542c\u5f97\u91d1\u94c1\u4ea4\u9e23\u4e4b\u58f0\uff0c\u597d\u4f3c\u662f\u4ece\u66f4\u8fdc\u7684\u540e\u56ed\u4f20\u6765\uff0c\u9633\u5929\u96f7\u5fc3\u795e\u4e0d \u5b9a\uff0c\u60f3\u9053\uff1a\u201c\u4ed6\u4eec\u54ea\u91cc\u6765\u7684\u8fd9\u8bb8\u591a\u4eba\uff1f\u9f99\u8c61\u6cd5\u738b\u4e0d\u77e5\u662f\u5426\u80fd\u80dc\u5f97\u4e86\u5b5f\u5c11\u521a\uff1f\u201d\u4ed6\u542c\u5f97\u51fa\u5728\u540e \u56ed\u4e43\u662f\u8bb8\u591a\u4eba\u7684\u6df7\u6218\uff0c\u79bb\u674e\u601d\u5357\u88ab\u56f0\u4e4b\u5904\u76f8\u5f53\u8fdc\u7684\uff0c\u663e\u7136\u4e0d\u662f\u5b5f\u5c11\u521a\u8fd9\u4e00\u4f19\u3002 \u3000\u3000\u9633\u5929\u96f7\u672c\u6765\u5df2\u7ecf\u5927\u5360\u4e0a\u98ce\uff0c\u7531\u4e8e\u5fc3\u795e\u4e0d\u5b9a\uff0c\u891a\u3001\u8c37\u4e8c\u4eba\u62fc\u6b7b\u594b\u6218\uff0c\u5f62\u52bf\u53cd\u800c\u6bd4\u521a\u624d\u597d\u4e86 \u4e00\u4e9b\u4e86\u3002 \u3000\u3000\u4f46\u674e\u601d\u5357\u6b64\u9645\uff0c\u5374\u662f\u78b0\u4e0a\u4e86\u60ca\u9669\u7edd\u4f26\u7684\u573a\u9762\u3002 \u3000\u3000\u4e14\u8bf4\u674e\u601d\u5357\u4ee5\u4e00\u628a\u5315\u9996\uff0c\u529b\u654c\u9f99\u8c61\u6cd5\u738b\u7684\u56db\u4e2a\u5f1f\u5b50\uff0c\u548c\u5927\u5f1f\u5b50\u786c\u62fc\u4e86\u4e00\u638c\uff0c\u867d\u7136\u903c\u9000\u4e86 \u4ed6\uff0c\u53ef\u662f\u5728\u4ed6\u7684\u9f99\u8c61\u529f\u9707\u64bc\u4e4b\u4e0b\uff0c\u80f8\u4e2d\u4ea6\u662f\u611f\u5230\u6c14\u8840\u7ffb\u6d8c\u3002 \u3000\u3000\u5927\u5f1f\u5b50\u559d\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u4e0d\u77e5\u6b7b\u6d3b\uff0c\u5984\u56fe\u987d\u6297\uff0c\u54b1\u4eec\u4e5f\u4e0d\u5fc5\u987e\u5fcc\u4e86\uff0c\u628a\u4ed6\u6bd9\u4e86\u5427\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u529f\u529b\u521d\u590d\uff0c\u7528\u7684\u5315\u9996\u53c8\u4e0d\u80fd\u5f97\u5fc3\u5e94\u624b\uff0c\u5728\u90a3\u56db\u4eba\u5206\u8fdb\u5408\u51fb\u4e4b\u4e0b\uff0c\u9000\u4e86\u4e00\u6b65\u53c8\u662f\u4e00 \u6b65\u3002\u4e0d\u77e5\u4e0d\u89c9\u5df2\u9000\u5230\u4e86\u79bb\u5f00\u97e9\u5927\u7ef4\u4e0e\u6768\u5a49\u3001\u97e9\u4f69\u745b\u7684\u8eab\u524d\u4e08\u8bb8\u4e4b\u5730\u3002 \u3000\u3000\u6768\u5a49\u3001\u97e9\u4f69\u745b\u7684\u5185\u529f\u9020\u8be3\u8f83\u5f31\uff0c\u97e9\u5927\u7ef4\u4ee5\u672c\u8eab\u771f\u529b\u540c\u65f6\u5e2e\u52a9\u5979\u4eec\u795b\u6bd2\u3001\u6d3b\u8840\u3001\u901a\u5173\uff0c\u6b63 \u662f\u5230\u4e86\u6700\u7d27\u8981\u7684\u5173\u5934\uff0c\u4e1d\u6beb\u4e5f\u4e0d\u80fd\u677e\u61c8\u3002 \u3000\u3000\u7236\u5973\u75db\u75d2\u76f8\u5173\uff0c\u97e9\u5927\u7ef4\u4e0d\u7531\u5f97\u4e5f\u90fd\u614c\u4e86\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6211\u82e5\u6b64\u65f6\u8d77\u6765\uff0c\u52a9\u674e\u601d\u5357\u4e00\u81c2\u4e4b \u529b\uff0c\u53ea\u6015\u745b\u513f\u6027\u547d\u4e0d\u4fdd\uff0c\u8fd9\u5374\u5982\u4f55\u662f\u597d\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9000\u5230\u8ddd\u79bb\u4ed6\u4eec\u4e08\u8bb8\u4e4b\u5904\uff0c\u970d\u7136\u4e00\u7701\uff0c\u4ed6\u662f\u6df1\u77e5\u6b64\u4e2d\u5173\u952e\u7684\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u97e9\u8001\u524d \u8f88\u4e0d\u53ef\u5206\u5fc3\uff0c\u6211\u8fd8\u53ef\u4ee5\u62b5\u6321\u5f97\u4f4f\uff01\u201d\u5fc3\u91cc\u5219\u5728\u60f3\u9053\uff1a\u201c\u6211\u62fc\u4e86\u8fd9\u6761\u547d\uff0c\u62d6\u5f97\u4e00\u65f6\u5c31\u662f\u4e00\u65f6\u3002 \u53ea\u8981\u5979\u4eec\u6062\u590d\u4e86\u529f\u529b\uff0c\u5a49\u59b9\u5c31\u6709\u9003\u751f\u4e4b\u671b\uff01\u201d \u3000\u3000\u8fd9\u56db\u4eba\u7ed9\u674e\u601d\u5357\u6321\u4f4f\uff0c\u6025\u5207\u4e4b\u95f4\u5012\u662f\u95ef\u4e0d\u8fc7\u53bb\u3002\u4e8c\u5f1f\u5b50\u9a82\u9053\uff1a\u201c\u6602\u683c\u91cc\u91cc\u516b\u592b\u91cc\u6d1e\u683c \u592b\uff01\u201d\uff08\u8499\u8bed\uff1a\u771f\u662f\u4e2a\u4e0d\u77e5\u6b7b\u6d3b\u7684\u86ee\u5b50\uff01\uff09\u63c9\u8eab\u8de8\u6b65\u4fa7\u88ad\uff0c\u4ed6\u7528\u7684\u662f\u4e00\u67c4\u8499\u53e4\u6b66\u58eb\u60ef\u5e38\u4e60\u7528 \u7684\u6708\u7259\u5f2f\u5200\uff0c\u6708\u7259\u5f2f\u5200\u548c\u6c49\u4eba\u7684\u957f\u5200\u4e0d\u540c\uff0c\u5200\u950b\u5982\u94a9\uff0c\u7a81\u7136\u95f4\u653b\u5230\u674e\u601d\u5357\u610f\u60f3\u4e0d\u5230\u7684\u65b9\u4f4d\uff01 \u3000\u3000\u53ea\u542c\u5f97\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u674e\u601d\u5357\u7684\u5315\u9996\u4ece\u90a3\u4eba\u7684\u989d\u89d2\u5212\u8fc7\uff0c\u628a\u4ed6\u7684\u7126\u76ae\u5e3d\u5b50\u4e5f\u524a\u843d\u4e86\uff01\u53ef \u662f\u674e\u601d\u5357\u7684\u5de6\u81c2\u5374\u5df2\u7ed9\u4ed6\u7684\u5200\u950b\u5272\u7834\uff0c\u867d\u4f24\u53e3\u4e0d\u6df1\uff0c\u4f46\u9c9c\u7ea2\u7684\u8840\u6db2\u5374\u5df2\u67d3\u7ea2\u4e86\u8863\u88f3\uff01 \u3000\u3000\u674e\u601d\u5357\u7d27\u63a5\u7740\u547c\u7684\u4e00\u638c\uff0c\u53c8\u548c\u5de6\u9762\u653b\u6765\u7684\u5927\u5f1f\u5b50\u786c\u62fc\u4e86\u4e00\u62db\uff0c\u628a\u4ed6\u903c\u9000\u3002\u674e\u601d\u5357\u5fcd\u7740\u75bc \u75db\uff0c\u54fc\u4e5f\u4e0d\u54fc\u4e00\u58f0\uff0c\u4f46\u4ed6\u7684\u8eab\u4f53\u4e0d\u662f\u94c1\u6253\u7684\uff0c\u53d7\u4f24\u4e4b\u540e\uff0c\u63a5\u8fde\u53d7\u5230\u9f99\u8c61\u529f\u7684\u9707\u64bc\uff0c\u53ea\u89c9\u80f8\u53e3 \u5982\u53d7\u91cd\u529b\uff0c\u4e94\u810f\u516d\u8151\u90fd\u597d\u50cf\u7ffb\u8f6c\u4e86\u8fc7\u6765\u4f3c\u7684\uff0c\u4e0d\u5f97\u4e0d\u53c8\u9000\u4e86\u4e24\u6b65\u3002 \u3000\u3000\u9f99\u8c61\u6cd5\u738b\u7684\u4e8c\u5f1f\u5b50\u7ed9\u5315\u9996\u4ece\u989d\u89d2\u5212\u8fc7\uff0c\u53ea\u89c9\u51c9\u610f\u4e60\u4e60\uff0c\u867d\u7136\u53ea\u662f\u7ed9\u524a\u4e86\u5e3d\u7b52\uff0c\u4ea6\u5df2\u5413\u5f97 \u9b42\u98de\u9b44\u6563\uff0c\u614c\u5fd9\u540e\u9000\uff0c\u5148\u907f\u654c\u950b\u3002\u674e\u601d\u5357\u6697\u6697\u53eb\u4e86\u4e00\u58f0\u201c\u4fa5\u5e78\uff01\u201d\u5047\u5982\u8fd9\u4eba\u4e0d\u662f\u5fc3\u614c\u7684\u8bdd\uff0c \u548c\u5927\u5e08\u5144\u53ea\u8981\u518d\u8fdb\u4e00\u62db\uff0c\u674e\u601d\u5357\u5df2\u662f\u65e0\u529b\u62db\u67b6\u4e86\uff0c\u674e\u601d\u5357\u9000\u4e86\u4e24\u6b65\uff0c\u5728\u8fd9\u77ac\u606f\u4e4b\u95f4\uff0c\u5df2\u662f\u8fd0 \u6c14\u4e09\u8f6c\uff0c\u6296\u8d77\u7cbe\u795e\uff0c\u91cd\u53c8\u7ad9\u7a33\u811a\u6b65\u4e86\u3002 \u3000\u3000\u6768\u5a49\u672a\u80fd\u505a\u5230\u201c\u89c6\u800c\u4e0d\u89c1\uff0c\u542c\u800c\u4e0d\u95fb\u201d\u7684\u5730\u6b65\uff0c\u5413\u5f97\u82b1\u5bb9\u5931\u8272\uff0c\u5c16\u53eb\u4e00\u58f0\uff0c\u8eab\u8eaf\u98a4\u6218\uff0c \u6447\u6447\u6b32\u5760\u3002\u597d\u5728\u5979\u662f\u76d8\u819d\u800c\u5750\u7684\uff0c\u8fd9\u624d\u4e0d\u81f3\u4e8e\u5012\u4e86\u4e0b\u53bb\u3002\u97e9\u5927\u7ef4\u771f\u529b\u51dd\u805a\u638c\u5fc3\uff0c\u7528\u4e86\u4e00\u4e2a \u201c\u7c98\u201d\u5b57\u8bc0\uff0c\u5c06\u5979\u7684\u624b\u638c\u7275\u5f15\u8fc7\u6765\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u522b\u614c\u4e71\uff0c\u4f60\u4e00\u614c\u4e71\uff0c\u53ea\u6015\u601d\u5357\u66f4\u8981\u7cdf \u4e86\u3002\u201d \u3000\u3000\u5927\u5f1f\u5b50\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u4e0d\u884c\u4e86\uff0c\u522b\u6015\u4ed6\uff0c\u5feb\u4e0a\u5440\uff01\u201d\u4e8c\u5f1f\u5b50\u60ca\u9b42\u7a0d\u5b9a\uff0c\u53c8\u9a82\u4e86\u4e00\u53e5 \u201c\u6602\u683c\u91cc\u91cc\u516b\u592b\u91cc\u6d1e\u683c\u592b\uff01\u201d\u6708\u7259\u5f2f\u5200\u4e00\u6325\uff0c\u9000\u800c\u590d\u4e0a\uff0c\u7ad9\u597d\u4e86\u539f\u6765\u7684\u65b9\u4f4d\uff0c\u4e0e\u5927\u5e08\u5144\u914d \u5408\uff0c\u8054\u624b\u518d\u653b\u3002 \u3000\u3000\u674e\u601d\u5357\u6d74\u8840\u82e6\u6597\uff0c\u9690\u9690\u542c\u5f97\u8fdc\u5904\u4f3c\u6709\u91d1\u94c1\u4ea4\u9e23\u4e4b\u58f0\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u83ab\u975e\u662f\u891a\u4e91\u5cf0\u4ed6\u4eec\u6765 \u4e86\uff0c\u4e0d\u77e5\u5f97\u624b\u4e86\u6ca1\u6709\uff1f\u201d \u3000\u3000\u5b5f\u5c11\u521a\u8fd0\u5251\u5982\u98ce\uff0c\u9e70\u7fd4\u96bc\u523a\uff0c\u4f46\u5728\u9f99\u8c61\u6cd5\u738b\u7684\u638c\u529b\u7b3c\u7f69\u4e4b\u4e0b\uff0c\u53ea\u80fd\u52c9\u5f3a\u6253\u4e2a\u5e73\u624b\uff0c\u8981\u4ed6 \u7a81\u7834\u4ed6\u7684\u5c01\u9501\uff0c\u8fc7\u53bb\u52a9\u674e\u601d\u5357\u4e00\u81c2\u4e4b\u529b\uff0c\u5374\u662f\u65e0\u6cd5\u505a\u5230\u4e86\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u6709\u4eba\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u601d\u5357\u5b89\u7b54\uff0c\u6211\u5bf9\u4f60\u5e76\u65e0\u6076\u610f\uff0c\u53ea\u8981\u4f60\u7b54\u5e94\u6211\u7684\u6761\u4ef6\uff0c\u4f60\u613f\u610f\u7559 \u6211\u5c31\u4e0e\u4f60\u5171\u4eab\u8363\u534e\uff0c\u4f60\u613f\u610f\u8d70\uff0c\u6211\u5c31\u4eb2\u81ea\u9001\u4f60\u51fa\u53bb\u3002\u4f60\u53c8\u4f55\u82e6\u62ff\u81ea\u5df1\u7684\u6027\u547d\u5f53\u4f5c\u513f\u620f\uff1f\u201d \u3000\u3000\u6765\u7684\u6b63\u662f\u8499\u53e4\u7684\u56db\u7687\u5b50\u62d6\u96f7\u3002\u674e\u601d\u5357\u51b7\u7b11\u9053\uff1a\u201c\u5927\u4e08\u592b\u867d\u6b7b\u4f55\u60e7\uff1f\u4f60\u53eb\u4f60\u624b\u4e0b\u7684\u6b66\u58eb\u6740 \u4e86\u6211\u5427\uff0c\u53c8\u4f55\u5fc5\u5047\u60fa\u60fa\u5462\uff01\u201d \u3000\u3000\u62d6\u96f7\u6447\u4e86\u6447\u5934\uff0c\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u601d\u5357\u5b89\u7b54\uff0c\u4f60\u4e0d\u542c\u597d\u8a00\uff0c\u5b9e\u662f\u4ee4\u6211\u4e3a\u96be\uff0c\u6ca1\u529e\u6cd5\uff0c\u53ea\u80fd\u7565 \u5c3d\u6545\u4eba\u7684\u60c5\u8c0a\uff0c\u4eb2\u81ea\u6765\u9001\u4f60\u5f52\u5929\u4e86\uff01\u201d \u3000\u3000\u8bf4\u7f62\uff0c\u62d6\u96f7\u8f6c\u8fc7\u5934\u6765\uff0c\u5411\u9f99\u8c61\u6cd5\u738b\u548c\u4f17\u6b66\u58eb\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u6709\u51e0\u4e2a\u5357\u86ee\u5b50\u6df7\u8fdb\u56fd\u5e08\u5e9c\uff0c\u5982 \u4eca\u5df2\u7ecf\u7ed9\u9633\u5929\u96f7\u56f0\u5728\u6597\u5ba4\u4e4b\u4e2d\uff0c\u8c05\u4ed6\u4eec\u63d2\u7fc5\u4e5f\u662f\u96be\u98de\u7684\u4e86\u3002\u4f60\u4eec\u4e0d\u5fc5\u60ca\u7591\uff0c\u54b1\u4eec\u8499\u53e4\u7684\u6b66\u58eb \u6b63\u597d\u548c\u4ed6\u4eec\u5973\u771f\u7684\u6b66\u58eb\u6bd4\u4e00\u6bd4\uff0c\u770b\u770b\u662f\u8c01\u80fd\u591f\u5148\u628a\u654c\u4eba\u64d2\u83b7\u3002\u201d\u6709\u4e2a\u6b66\u571f\u8bf4\u9053\uff1a\u201c\u7980\u56db\u6bbf \u4e0b\uff0c\u7528\u957f\u5251\u8fd9\u4eba\u662f\u6c5f\u5357\u7b2c\u4e00\u5251\u5ba2\u5b5f\u5c11\u521a\u3002\u5750\u5728\u5730\u4e0a\u7684\u90a3\u4e2a\u8001\u5934\u513f\u540d\u53eb\u97e9\u5927\u7ef4\uff0c\u4e5f\u662f\u4e2d\u539f\u6b66\u6797 \u4e2d\u9876\u5c16\u513f\u7684\u89d2\u8272\uff01\u201d\u4ed6\u8bf4\u8fd9\u8bdd\u7684\u7528\u610f\uff0c\u4e00\u6765\u662f\u5411\u62d6\u96f7\u89e3\u91ca\u4f55\u4ee5\u9f99\u8c61\u6cd5\u738b\u5018\u672a\u80fd\u591f\u5f97\u80dc\uff0c\u4e8c\u6765 \u662f\u6697\u793a\u81ea\u5df1\u63d2\u4e0d\u8fdb\u624b\uff0c\u6050\u6015\u62d6\u96f7\u8981\u4ed6\u4e0a\u53bb\u5728\u5b5f\u5c11\u521a\u7684\u5251\u4e0b\u53d7\u4e86\u4f24\u6b8b\u3002 \u3000\u3000\u9f99\u8c61\u6cd5\u738b\u9053\uff1a\u201c\u6bbf\u4e0b\u653e\u5fc3\uff0c\u7528\u4e0d\u4e86\u534a\u67f1\u9999\u7684\u65f6\u523b\uff0c\u6211\u5c31\u53ef\u4ee5\u628a\u8fd9\u4f4d\u6c5f\u5357\u7b2c\u4e00\u5251\u5ba2\u64d2\u6765\u732e \u7ed9\u6bbf\u4e0b\uff01\u201d \u3000\u3000\u5b5f\u5c11\u521a\u5927\u6012\u9053\uff1a\u201c\u653e\u4f60\u7684\u5c41\uff0c\u6211\u5012\u8981\u770b\u4f60\u5982\u4f55\u64d2\u6211\uff01\u201d\u5530\u7684\u4e00\u62db\u201c\u6458\u6597\u6469\u661f\u201d\uff0c\u5251\u5149\u8000 \u773c\uff0c\u5f53\u771f\u4f3c\u662f\u9ed1\u591c\u7e41\u661f\uff0c\u5343\u70b9\u4e07\u70b9\u6d12\u843d\u4e0b\u6765\uff0c\u970e\u7136\u95f4\u65b9\u5706\u6570\u4e08\u4e4b\u5185\uff0c\u90fd\u662f\u51b7\u68ee\u68ee\u7684\u5251\u6c14\uff0c\u62d6 \u96f7\u867d\u7136\u7ad9\u5f97\u751a\u8fdc\uff0c\u4e5f\u662f\u4e0d\u7981\u5403\u4e86\u4e00\u60ca\uff0c\u4e0d\u77e5\u4e0d\u89c9\u5730\u9000\u4e86\u51e0\u6b65\u3002 \u3000\u3000\u9f99\u8c61\u6cd5\u738b\u4e0d\u614c\u4e0d\u5fd9\u5730\u628a\u5927\u7ea2\u8888\u88df\u821e\u5f97\u547c\u547c\u98ce\u54cd\uff0c\u5c31\u50cf\u6da8\u6ee1\u7684\u98ce\u5e06\u4e00\u6837\uff0c\u4e00\u5377\u3001\u4e00\u7f69\u3001\u4e00 \u6251\uff0c\u628a\u5b5f\u5c11\u521a\u7684\u5251\u5149\u538b\u7f29\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u53ea\u8981\u4ed6\u6c89\u4e0d\u4f4f\u6c14\uff0c\u8bf4\u4e0d\u5b9a\u8fd8\u7528\u4e0d\u5230\u534a\u67f1\u9999\u7684\u65f6 \u523b\uff01\u201d\u8981\u77e5\u9ad8\u624b\u6bd4\u6597\uff0c\u6700\u5fcc\u5fe7\u4f24\u548c\u52a8\u6012\uff0c\u9f99\u8c61\u6cd5\u738b\u6b63\u662f\u6709\u5fc3\u60f3\u6fc0\u6012\u5b5f\u5c11\u521a\u7684\u3002 \u3000\u3000\u62d6\u96f7\u5b9a\u4e86\u5b9a\u795e\uff0c\u7eb5\u58f0\u7b11\u9053\uff1a\u201c\u597d\uff0c\u597d\uff01\u654c\u4eba\u8d8a\u5f3a\uff0c\u8d8a\u663e\u5f97\u54b1\u4eec\u8499\u53e4\u6b66\u58eb\u7684\u672c\u9886\uff01\u8fd9\u59d3\u5b5f \u7684\u8ba9\u6cd5\u738b\u5bf9\u4ed8\uff0c\u4f46\u4f60\u4eec\u4e5f\u4e0d\u8981\u95f2\u4f4f\u554a\uff0c\u628a\u90a3\u4e24\u4e2a\u5973\u5a03\u513f\u66ff\u6211\u62ff\u4e0b\u5427\uff01\u201d \u3000\u3000\u8fd9\u51e0\u4e2a\u65c1\u89c2\u7684\u6b66\u58eb\u4e00\u6765\u662f\u7531\u4e8e\u672c\u9886\u8f83\u5f31\uff0c\u5bf9\u674e\u601d\u5357\u7684\u52c7\u731b\u51cc\u5389\u4e0d\u65e0\u987e\u5fcc\uff0c\u4e8c\u6765\u4ed6\u4eec\u8fd8\u591a \u5c11\u6709\u70b9\u6b66\u58eb\u7684\u6c14\u8d28\uff0c\u89c9\u5f97\u53bb\u6b3a\u8d1f\u4e24\u4e2a\u5931\u4e86\u62b5\u6297\u80fd\u529b\u7684\u5c0f\u59d1\u5a18\uff0c\u672a\u514d\u6709\u70b9\u5931\u6b66\u58eb\u7684\u8eab\u4efd\u3002\u4f46\u73b0 \u5728\u5728\u62d6\u96f7\u7684\u547d\u4ee4\u4e4b\u4e0b\uff0c\u5fc3\u91cc\u867d\u4e0d\u613f\u610f\uff0c\u4e5f\u4e0d\u80fd\u4e0d\u4e00\u62e5\u800c\u4e0a\u4e86\u3002 \u3000\u3000\u674e\u601d\u5357\u559d\u9053\uff1a\u201c\u8c01\u6562\u8fc7\u53bb\uff0c\u6211\u5c31\u548c\u4ed6\u62fc\u4e86\uff01\u201d\u5315\u9996\u7ffb\u98de\uff0c\u5de6\u638c\u52a9\u653b\uff0c\u633a\u8eab\u7ad9\u5728\u97e9\u5927\u7ef4\u7684 \u524d\u9762\uff0c\u5b81\u6b7b\u4e0d\u80af\u9000\u540e\u4e00\u6b65\u3002\u6b66\u5b66\u6709\u4e91\uff1a\u201c\u4e00\u5bf8\u77ed\uff0c\u4e00\u5bf8\u9669\uff01\u201d\u674e\u601d\u5357\u4ee5\u4e00\u5c3a\u591a\u957f\u7684\u77ed\u5315\u9996\u548c \u654c\u4eba\u4f5c\u8fd1\u8eab\u640f\u6597\uff0c\u56fa\u7136\u662f\u60ca\u9669\u975e\u5e38\uff0c\u4f46\u56f4\u653b\u4ed6\u7684\u8fd9\u4e9b\u4eba\u82e5\u7136\u7a0d\u6709\u4e0d\u614e\uff0c\u4e5f\u5b9a\u6709\u8840\u6e85\u5c18\u57c3\u4e4b \u9669\uff01 \u3000\u3000\u62d6\u96f7\u53c8\u518d\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u5b9a\u8981\u8fd9\u6837\u987d\u6297\uff0c\u6211\u53ef\u662f\u65e0\u6cd5\u987e\u5ff5\u5b89\u7b54\u4e4b\u60c5\u4e86\u3002\u55ef\uff0c\u4f60\u4eec \u80fd\u591f\u4ee4\u4ed6\u4f24\u800c\u4e0d\u6b7b\u56fa\u7136\u6700\u597d\uff0c\u82e5\u4e0d\u80fd\u591f\uff0c\u90a3\u5c31\u5c06\u4ed6\u6740\u4e86\u5427\uff01\u201d \u3000\u3000\u9f99\u8c61\u6cd5\u738b\u7684\u5927\u5f1f\u5b50\u8bf4\u9053\uff1a\u201c\u9075\u547d\uff01\u201d\u4e00\u638c\u51fb\u4e0b\uff0c\u6b63\u597d\u5288\u7740\u4e86\u674e\u601d\u5357\u624b\u8155\uff0c\u77ed\u5315\u9996\u8131\u624b\u98de \u51fa\u3002\u6b63\u662f\uff1a \u3000\u3000\u8bf4\u751a\u6545\u4eba\u60c5\u4e49\u91cd\uff0c\u72f0\u72de\u9762\u76ee\u9732\u65e0\u9057\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b\u2014\u2014\u7b2c\u4e94\u5341\u516b\u56de\u3000\u516c\u4e3b\u60c5\u4e49\u5f92\u6005\u6005\u3000\u826f\u670b\u4e49\u91cd\u6068\u7ef5\u7ef5 \u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b \u7b2c\u4e94\u5341\u516b\u56de\u3000\u516c\u4e3b\u60c5\u4e49\u5f92\u6005\u6005\u3000\u826f\u670b\u4e49\u91cd\u6068\u7ef5\u7ef5 \u3000\u3000\u5c31\u5728\u6b64\u5bf9\uff0c\u5ffd\u542c\u5f97\u4eba\u58f0\u5608\u6742\uff0c\u54d7\u5566\u5566\u5730\u4e71\u6210\u4e00\u7247\uff01\u6709\u4eba\u559d\u9053\uff1a\u201c\u963f\u76d6\uff0c\u4f60\u53cd\u4e86\u5417\uff1f\u4e0d\u8bb8 \u8fdb\u53bb\uff01\u201d\u6709\u4eba\u53eb\u9053\uff1a\u201c\u54a6\uff0c\u8fd9\u4e0d\u662f\u4e09\u516c\u4e3b\u5417\uff1f\u516c\u4e3b\u6765\u4e86\uff0c\u4f4f\u624b\uff0c\u4f4f\u624b\uff01\u201d\u7533\u65a5\u7684\u559d\u58f0\u4e0e\u8be7\u5f02 \u7684\u53eb\u58f0\u6df7\u5408\u5728\u4e00\u8d77\uff0c\u968f\u5373\u542c\u5f97\u201c\u84ec\uff0c\u84ec\u201d\u4e24\u58f0\uff0c\u662f\u4e24\u540d\u6b66\u58eb\u7ed9\u963f\u76d6\u6454\u5012\u5730\u4e0a\u7684\u58f0\u97f3\uff01 \u3000\u3000\u62d6\u96f7\u5403\u4e86\u4e00\u60ca\uff0c\u62ac\u5934\u770b\u65f6\uff0c\u53ea\u89c1\u660e\u6167\u516c\u4e3b\u548c\u963f\u76d6\u592b\u5987\u4e1a\u5df2\u786c\u95ef\u8fdb\u6765\uff0c\u963f\u76d6\u4e00\u9a6c\u5f53\u5148\uff0c\u6325 \u821e\u957f\u7ef3\u5f00\u9053\uff01 \u3000\u3000\u8fd9\u4e00\u4e0b\u5927\u51fa\u62d6\u96f7\u610f\u6599\u4e4b\u5916\uff0c\u5931\u58f0\u53eb\u9053\uff1a\u201c\u4e09\u59b9\u3001\u4f60\u600e\u4e48\u6765\u4e86\uff1f\u201d \u3000\u3000\u5916\u9762\u7684\u6b66\u571f\u4e0d\u6562\u963b\u62e6\u660e\u6167\u516c\u4e3b\uff0c\u91cc\u9762\u90a3\u56db\u4e2a\u6b66\u571f\u4ecd\u5728\u56f4\u653b\u674e\u601d\u5357\uff0c\u9f99\u8c61\u6cd5\u738b\u548c\u5b5f\u5c11\u521a\u7684 \u6076\u6597\u4e5f\u672a\u505c\u6b62\u3002 \u3000\u3000\u6b64\u65f6\u6b63\u662f\u674e\u601d\u5357\u906d\u9047\u9669\u62db\u4e4b\u9645\uff0c\u660e\u6167\u516c\u4e3b\u987e\u4e0d\u5f97\u56de\u7b54\u54e5\u54e5\uff0c\u4e5f\u6765\u4e0d\u53ca\u8dd1\u8fdb\u53bb\u963b\u6b62\u90a3\u56db\u4e2a \u6b66\u58eb\uff0c\u6025\u4e2d\u751f\u667a\uff0c\u6697\u8155\u4e00\u7529\uff0c\u628a\u4e00\u67c4\u8131\u4e86\u9798\u7684\u5b9d\u5251\u63b7\u8fdb\u5708\u5b50\uff0c\u53eb\u9053\uff1a\u201c\u5feb\uff0c\u63a5\u7740\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u5315\u9996\u521a\u7ed9\u9f99\u8c61\u6cd5\u738b\u7684\u5927\u5f1f\u5b50\u6253\u843d\uff0c\u53cd\u624b\u4e00\u638c\uff0c\u903c\u9000\u4e86\u4e8c\u5f1f\u5b50\uff0c\u9f99\u8c61\u6cd5\u738b\u7684\u4e09\u5f1f\u5b50 \u53c8\u662f\u4e00\u638c\u5288\u8fc7\u6765\u4e86\u3002 \u3000\u3000\u674e\u601d\u5357\u542c\u5f97\u660e\u6167\u516c\u4e3b\u7684\u53eb\u58f0\uff0c\u624b\u6025\u773c\u5feb\uff0c\u98de\u8eab\u4e00\u8dc3\uff0c\u628a\u5b9d\u5251\u63a5\u5230\u624b\u4e2d\uff0c\u767b\u65f6\u5982\u864e\u6dfb\u7ffc\uff0c \u7acb\u5373\u4fbf\u662f\u4e00\u62db\u201c\u591c\u6218\u516b\u65b9\u201d\u7684\u62db\u5f0f\uff0c\u9f99\u8c61\u738b\u4e09\u5f1f\u5b50\u7684\u6708\u624d\u5f2f\u5200\u6b63\u5728\u671d\u7740\u4ed6\u7684\u5929\u7075\u76d6\u5288\u4e0b\uff0c\u53ea \u542c\u5f97\u201c\u94db\u201d\u7684\u4e00\u58f0\uff0c\u6708\u7259\u5f2f\u5200\u7adf\u7ed9\u5b9d\u5251\u524a\u6389\u4e86\u5200\u5934\uff01 \u3000\u3000\u8fd9\u628a\u5b9d\u5251\u6b63\u662f\u660e\u6167\u516c\u4e3b\u4ee5\u524d\u9001\u7ed9\u6768\u5a49\u7684\u90a3\u628a\u5b9d\u5251\uff0c\u6768\u5a49\u88ab\u64d2\u4e4b\u540e\uff0c\u53c8\u7ed9\u62d6\u96f7\u7f34\u4e86\u53bb\u7684\u3002 \u3000\u3000\u539f\u6765\u660e\u6167\u516c\u4e3b\u4e0e\u963f\u76d6\u3001\u5361\u6d1b\u4e1d\u5230\u4e86\u201c\u56fd\u5e08\u5e9c\u201d\uff0c\u5176\u65f6\u6b63\u662f\u5e9c\u4e2d\u5927\u6df7\u6218\u4e4b\u9645\uff0c\u5b88\u95e8\u7684\u536b\u58eb \u542c\u8bf4\u662f\u8499\u53e4\u7684\u516c\u4e3b\u5230\u4e86\uff0c\u7531\u4e8e\u9633\u5929\u96f7\u548c\u9f99\u8c61\u6cd5\u738b\u90fd\u5728\u6fc0\u6218\u4e4b\u4e2d\uff0c\u4ed6\u4eec\u4e0d\u6562\u4f5c\u4e3b\uff0c\u53ea\u597d\u614c\u91cc\u614c \u5f20\u5730\u627e\u4e86\u4e00\u4e2a\u62d6\u96f7\u7684\u968f\u4ece\u51fa\u6765\u8ba4\u4eba\u3002 \u3000\u3000\u8fd9\u4e2a\u968f\u4ece\u5f53\u7136\u4e0d\u6562\u963b\u62e6\u516c\u4e3b\uff0c\u4f46\u53c8\u4e0d\u613f\u610f\u7ed9\u5979\u89c1\u5230\u674e\u601d\u5357\uff0c\u53ea\u597d\u628a\u660e\u6167\u516c\u4e3b\u5e26\u5230\u62d6\u96f7\u7684 \u4f4f\u5904\u3002 \u3000\u3000\u62d6\u96f7\u6b64\u65f6\u5df2\u7ecf\u79bb\u5f00\u623f\u95f4\uff0c\u8dd1\u5230\u674e\u601d\u5357\u88ab\u56da\u7684\u6240\u5728\u53bb\u770b\u51fa\u4e86\u4ec0\u4e48\u4e8b\uff0c\u8fd9\u4e2a\u968f\u4ece\u5c1a\u672a\u77e5\u9053\u3002 \u3000\u3000\u670d\u4f8d\u62d6\u96f7\u7684\u4e00\u4e2a\u5bab\u5973\u662f\u660e\u6167\u516c\u4e3b\u4ee5\u524d\u7528\u8fc7\u7684\u4e2b\u9b1f\uff0c\u660e\u6167\u516c\u4e3b\u89c1\u62d6\u96f7\u4e0d\u5728\uff0c\u5fc3\u77e5\u6709\u5f02\uff0c\u4e25 \u8bcd\u903c\u95ee\u4e4b\u4e0b\uff0c\u90a3\u5bab\u5973\u8bf4\u51fa\u4e86\u62d6\u96f7\u521a\u521a\u542c\u5f97\u674e\u601d\u5357\u4f01\u56fe\u8d8a\u72f1\u7684\u6d88\u606f\u56e0\u800c\u8dd1\u4e86\u51fa\u53bb\u7684\u4e8b\u60c5\u3002\u660e\u6167 \u516c\u4e3b\u5927\u5403\u4e00\u60ca\uff0c\u8fde\u5fd9\u5728\u623f\u4e2d\u641c\u51fa\u90a3\u67c4\u5b9d\u5251\uff0c\u8feb\u4f7f\u62d6\u96f7\u7684\u968f\u4ece\u7ed9\u5979\u5e26\u8def\uff01 \u3000\u3000\u8fd9\u70b3\u5b9d\u5251\u6709\u65ad\u91d1\u524a\u94c1\u4e4b\u80fd\uff0c\u5439\u6bdb\u7acb\u65ad\u4e4b\u5229\uff0c\u674e\u601d\u5357\u5b9d\u5251\u5728\u624b\uff0c\u5438\u4e00\u53e3\u6c14\uff0c\u5c55\u5f00\u4e86\u5c11\u6797\u6d3e \u5ae1\u4f20\u7684\u8fbe\u6469\u5251\u6cd5\uff0c\u5251\u5149\u56db\u9762\u8361\u5f00\uff0c\u4f17\u6b66\u58eb\u7eb7\u7eb7\u540e\u9000\u3002 \u3000\u3000\u62d6\u96f7\u5927\u4e3a\u7740\u607c\uff0c\u8bf4\u9053\uff1a\u201c\u4e09\u59b9\uff0c\u4f60\u8fd9\u662f\u600e\u4e48\u5566\uff1f\u4f60\u5fd8\u8bb0\u4e86\u4f60\u662f\u54b1\u4eec\u8499\u53e4\u7684\u516c\u4e3b\u5417\uff1f\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u9053\uff1a\u201c\u6b63\u56e0\u4e3a\u6211\u6ca1\u6709\u5fd8\u8bb0\u6211\u662f\u6210\u5409\u601d\u6c57\u7684\u5973\u513f\uff0c\u6211\u624d\u56de\u5230\u8fd9\u91cc\u3002\u201d \u3000\u3000\u62d6\u96f7\u9053\uff1a\u201c\u597d\u5440\uff0c\u90a3\u4f60\u4e3a\u4ec0\u4e48\u80f3\u818a\u53cd\u800c\u5411\u5916\u5f2f\u4e86\uff1f\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u9053\uff1a\u201c\u7b2c\u4e00\uff0c\u674e\u601d\u5357\u548c\u4f60\u662f\u6362\u4e86\u54c8\u8fbe\u7684\u5b89\u7b54\uff0c\u4e0d\u7b97\u5916\u4eba\uff0c\u54b1\u4eec\u8499\u53e4\u6709\u53e5\u4fd7\u8bed\uff1a \u597d\u670b\u53cb\u7684\u60c5\u8c0a\u5c31\u50cf\u963f\u5c14\u6cf0\u5c71\u5ca9\u77f3\u4e00\u6837\u7684\u575a\u56fa\uff0c\u4f60\u53c8\u600e\u4e48\u5fd8\u4e86\uff1f\u201d \u3000\u3000\u62d6\u96f7\u6012\u9053\uff1a\u201c\u6211\u4e0d\u80fd\u56e0\u79c1\u5e9f\u516c\uff01\u9664\u975e\u4f60\u80fd\u4f7f\u5f97\u601d\u5357\u5b89\u7b54\u5f52\u987a\u4e8e\u6211\uff0c\u52a9\u6211\u5e73\u5b9a\u4e2d\u539f\uff01\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u9053\uff1a\u201c\u6211\u4e0d\u61c2\u5f97\u4f60\u4eec\u7684\u519b\u56fd\u5927\u4e8b\uff0c\u6211\u53ea\u77e5\u9053\u54b1\u4eec\u8499\u53e4\u6709\u4e86\u65e0\u57a0\u7684\u7267\u573a\uff1b\u8001\u767e\u59d3 \u517b\u725b\u7267\u7f8a\uff0c\u4e5f\u5c3d\u53ef\u4ee5\u8fc7\u6d3b\uff0c\u7528\u4e0d\u7740\u4fb5\u5360\u6c49\u4eba\u7684\u5730\u65b9\uff0c\u5927\u5bb6\u780d\u6740\u4e0d\u4f11\uff0c\u9020\u6210\u591a\u5c11\u5b64\u513f\u5be1\u5987\uff01\u201d \u3000\u3000\u62d6\u96f7\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4e0d\u662f\u770b\u5728\u4f60\u662f\u6211\u59b9\u5b50\u7684\u4efd\u4e0a\uff0c\u8bf4\u51fa\u8fd9\u7b49\u6447\u52a8\u519b\u5fc3\u7684\u8bf4\u8bdd\uff0c\u5148\u5c31\u8981\u628a\u4f60 \u6cbb\u7f6a\uff01\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u9053\uff1a\u201c\u6211\u82e5\u662f\u6015\u4f60\u6cbb\u7f6a\uff0c\u6211\u4e5f\u4e0d\u4f1a\u5230\u8fd9\u91cc\u6765\u4e86\uff01\u201d \u3000\u3000\u62d6\u96f7\u6012\u9053\uff1a\u201c\u597d\u5440\uff0c\u90a3\u4e48\u4f60\u8bf4\uff0c\u4f60\u8981\u600e\u6837\uff1f\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u9053\uff1a\u201c\u674e\u601d\u5357\u4e8e\u6211\u6709\u6551\u547d\u4e4b\u6069\uff0c\u6211\u8981\u62a5\u7b54\u4ed6\u7684\u6069\u4e49\uff0c\u6c42\u4f60\u5c06\u4ed6\u4eec\u653e\u4e86\uff01\u201d \u3000\u3000\u62d6\u96f7\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u8fd8\u662f\u8981\u6c42\u5230\u6211\u4e48\uff1f\u54fc\uff0c\u653e\u4e86\uff1f\u6709\u8fd9\u6837\u5bb9\u6613\uff01\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u653e\u4ed6\u4e5f\u884c\uff0c\u4f46\u6211\u662f\u4e2a\u6069\u6028\u5206\u660e\u7684\u4eba\uff0c\u62a5\u7b54\u4e0d\u4e86\u6069\u4eba\uff0c\u6211\u53ea\u6709 \u548c\u4ed6\u540c\u6b7b\u3002\u201d \u3000\u3000\u62d6\u96f7\u5927\u6012\u9053\uff1a\u201c\u4f60\u7adf\u6562\u8bf4\u51fa\u8fd9\u6837\u7684\u8bdd\u6765\uff01\u4f60\u662f\u91d1\u679d\u7389\u53f6\uff0c\u7adf\u7136\u3001\u7adf\u7136\u8981\u4e3a\u4e00\u4e2a\u666e\u901a\u7684\u6c49 \u4eba\u6b89\u60c5\u4e48\uff1f\u201d \u3000\u3000\u62d6\u96f7\u53e3\u4e0d\u62e9\u8a00\uff0c\u8bf4\u51fa\u201c\u6b89\u60c5\u201d\u4e8c\u5b57\uff0c\u660e\u6167\u516c\u4e3b\u53c8\u7f9e\u53c8\u607c\uff0c\u8bf4\u9053\uff1a\u201c\u56db\u54e5\uff0c\u4f60\u3001\u4f60\u8fd9\u662f\u4ec0 \u4e48\u8bdd\uff1f\u4f60\u4e0d\u987e\u6211\u7684\u4f53\u9762\uff0c\u6211\u4e5f\u53ea\u597d\u4e0d\u987e\u4f60\u7684\u4f53\u9762\uff0c\u6211\u5c31\u4ee5\u516c\u4e3b\u7684\u8eab\u4efd\uff0c\u7559\u5728\u6c49\u4eba\u7684\u5730\u65b9\uff0c\u4f60 \u53c8\u5982\u4f55\uff1f\u201d \u3000\u3000\u62d6\u96f7\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u53ea\u6015\u4f60\u4eca\u5929\u6765\u4e86\u5c31\u8d70\u4e0d\u4e86\uff01\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u4e5f\u662f\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u90a3\u4e5f\u6ca1\u6709\u4ec0\u4e48\uff0c\u53cd\u6b63\u6211\u5e76\u4e0d\u6253\u7b97\u6d3b\u7740\u56de\u53bb\u3002\u6211\u53cd\u6297\u4e0d\u4e86\u4f60\uff0c \u96be\u9053\u6211\u81ea\u5df1\u5bfb\u6b7b\u8fd8\u4e0d\u5bb9\u6613\uff1f\u6211\u6b7b\u4e86\u4e5f\u597d\u53eb\u5927\u5bb6\u77e5\u9053\u662f\u4f60\u903c\u6b7b\u6211\u7684\uff01\u201d \u3000\u3000\u9f99\u8c61\u6cd5\u738b\u4e00\u62db\u201c\u8986\u56f0\u7ffb\u4e91\u201d\u628a\u5b5f\u5c11\u521a\u903c\u9000\u6570\u6b65\uff0c\u529d\u89e3\u9053\uff1a\u201c\u56db\u6bbf\u4e0b\uff0c\u4f60\u4eec\u5144\u59b9\u6709\u8bdd\u597d\u597d \u7684\u8bf4\u5427\uff01\u201d \u3000\u3000\u539f\u6765\u9f99\u8c61\u6cd5\u738b\u662f\u4e2a\u6b66\u5b66\u7684\u5927\u884c\u5bb6\uff0c\u4ed6\u89c1\u97e9\u5927\u7ef4\u5934\u4e0a\u5192\u51fa\u70ed\u817e\u817e\u7684\u767d\u6c14\uff0c\u6768\u5a49\u548c\u97e9\u4f69\u745b\u7684 \u8138\u8272\u5df2\u7ecf\u9010\u6e10\u7ea2\u6da6\uff0c\u77e5\u9053\u4ed6\u4eec\u5373\u5c06\u5927\u529f\u544a\u6210\uff0c\u81ea\u5df1\u4e0e\u5b5f\u5c11\u521a\u68cb\u9022\u654c\u624b\uff0c\u8981\u6446\u8131\u5b5f\u5c11\u521a\u53bb\u963b\u6b62 \u97e9\u5927\u7ef4\u8fd0\u529f\u4e5f\u662f\u4e0d\u6613\uff0c\u5018\u82e5\u5f85\u5230\u97e9\u5927\u7ef4\u80fd\u591f\u817e\u51fa\u624b\u6765\uff0c\u53ea\u6015\u4eca\u65e5\u4e4b\u6218\uff0c\u5c31\u96be\u8ba8\u597d\u4e86\uff0c\u4ed6\u8981\u4fdd \u4f4f\u5929\u4e0b\u7b2c\u4e00\u9ad8\u624b\u7684\u9762\u5b50\uff0c\u6545\u800c\u4e5f\u662f\u6709\u70b9\u4e0d\u5927\u613f\u610f\u518d\u6253\u4e0b\u53bb\u3002 \u3000\u3000\u62d6\u96f7\u5fcd\u4f4f\u6012\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u660e\u6167\uff0c\u6211\u53ef\u4ee5\u4f9d\u4f60\u4e4b\u8a00\uff0c\u4f46\u4f60\u4e5f\u5f97\u542c\u6211\u8bf4\u8bdd\uff01\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u9053\uff1a\u201c\u4f60\u8bf4\uff01\u201d \u3000\u3000\u62d6\u96f7\u8bf4\u9053\uff1a\u201c\u6211\u653e\u4e86\u4ed6\uff0c\u4f60\u53ef\u5f97\u4e56\u4e56\u8ddf\u6211\u56de\u53bb\uff0c\u4e0d\u8bb8\u518d\u80e1\u95f9\u4e86\u3002\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u4e00\u54ac\u94f6\u7259\uff0c\u8bf4\u9053\uff0c\u201c\u597d\uff0c\u4f9d\u4f60\uff01\u201d \u3000\u3000\u62d6\u96f7\u6c89\u58f0\u559d\u9053\uff1a\u201c\u4f4f\u624b\uff01\u201d\u56f4\u653b\u674e\u601d\u5357\u7684\u90a3\u73ed\u6b66\u58eb\u4e00\u9f50\u9000\u4e0b\uff0c\u9f99\u8c61\u6cd5\u738b\u548c\u5b5f\u5c11\u521a\u4e5f\u5404\u81ea \u8dc3\u51fa\u5708\u5b50\u3002 \u3000\u3000\u9f99\u8c61\u6cd5\u738b\u62ab\u8d77\u8888\u88df\uff0c\u53ea\u89c1\u88df\u88df\u4e0a\u7684\u5c0f\u5b54\u5bc6\u5bc6\u9ebb\u9ebb\uff0c\u597d\u4f3c\u8702\u5de2\u4e00\u6837\uff0c\u5fc3\u4e2d\u4e0d\u7981\u9a87\u7136\uff0c\u6697\u81ea \u60f3\u9053\uff1a\u201c\u8fd9\u4eba\u53f7\u79f0\u795e\u5251\u65e0\u654c\uff0c\u679c\u7136\u540d\u4e0d\u865a\u4f20\uff0c\u4e45\u6218\u4e0b\u53bb\uff0c\u6211\u7eb5\u7136\u80fd\u591f\u51fb\u6bd9\u4ed6\uff0c\u4f46\u82e5\u7a0d\u6709\u4e0d \u614e\uff0c\u53ea\u6015\u4e5f\u4fdd\u4e0d\u4f4f\u8981\u7ed9\u4ed6\u5728\u8eab\u4e0a\u523a\u4e00\u4e2a\u900f\u660e\u7684\u7a9f\u7abf\uff01\u201d\u5b5f\u5c11\u521a\u63d2\u5251\u5f52\u9798\uff0c\u53ea\u89c9\u80f8\u4e2d\u6c14\u8840\u7ffb \u6d8c\uff0c\u8111\u888b\u9635\u9635\u6655\u7729\uff0c\u4e5f\u662f\u597d\u751f\u9a87\u7136\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5018\u82e5\u4e45\u6218\u4e0b\u53bb\uff0c\u6211\u7eb5\u7136\u80fd\u591f\u523a\u4f24\u4e86\u4ed6\uff0c\u53ea \u6015\u4e5f\u662f\u96be\u514d\u8981\u5927\u75c5\u4e00\u573a\u4e86\uff01\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u628a\u5251\u9798\u63b7\u7ed9\u674e\u601d\u5357\uff0c\u8bf4\u9053\uff1a\u201c\u674e\u516c\u5b50\uff0c\u6211\u4e0d\u80fd\u559d\u4f60\u7684\u559c\u9152\u3002\u8fd9\u628a\u5251\u672c\u6765\u662f\u6211\u9001 \u7ed9\u5a49\u59d0\u7684\uff0c\u5982\u4eca\u7269\u5f52\u539f\u4e3b\uff0c\u4e5f\u5c31\u6743\u5f53\u6211\u7ed9\u4f60\u4eec\u7684\u793c\u7269\u4e86\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u7eb3\u5251\u5165\u9798\uff0c\u62ac\u8d77\u5934\u6765\uff0c\u53ea\u89c1\u660e\u6167\u516c\u4e3b\u773c\u89d2\u6709\u9897\u6676\u83b9\u7684\u6cea\u73e0\uff0c\u674e\u6069\u5357\u53c8\u662f\u611f\u6fc0\uff0c\u53c8 \u662f\u4e3a\u5979\u60b2\u4f24\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u4e5f\u4e0d\u77e5\u8bf4\u4e9b\u4ec0\u4e48\u624d\u597d\u3002 \u3000\u3000\u97e9\u5927\u7ef4\u5618\u4e86\u53e3\u6c14\uff0c\u53cc\u638c\u4e00\u6536\u8bf4\u9053\uff1a\u201c\u884c\u5566\u3002\u201d\u6768\u5a49\u4e00\u8dc3\u800c\u8d77\uff0c\u5954\u5411\u660e\u6167\u516c\u4e3b\uff0c\u53eb\u9053\uff1a \u201c\u660e\u6167\u59d0\u59d0\uff0c\u4f60\u5bf9\u6211\u592a\u597d\u4e86\uff0c\u6211\u4e0d\u77e5\u5e94\u8be5\u600e\u6837\u62a5\u7b54\u4f60\u624d\u662f\uff0c\u4f46\u6211\u53ef\u4e0d\u653e\u5fc3\u8ba9\u4f60\u56de\u53bb\uff01\u201d\u4e24\u540d \u8499\u53e4\u6b66\u58eb\u4f38\u51fa\u957f\u77db\u5c06\u5979\u62e6\u4f4f\u3002 \u3000\u3000\u660e\u6167\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u4eba\u751f\u6709\u7f18\u76f8\u805a\uff0c\u7f18\u5c3d\u5219\u6563\uff0c\u8fd9\u51e0\u4e2a\u6708\u6765\u6211\u9752\u706f\u793c\u4f5b\uff0c\u603b\u7b97\u53c2\u609f\u4e86\u8fd9\u70b9 \u4f5b\u7406\uff0c\u4f60\u4eec\u6c49\u4eba\u4e0d\u4f46\u662f\u6709\u53e5\u4fd7\u8bed\u4e48\uff0c\u5929\u4e0b\u65e0\u4e0d\u6563\u4e4b\u7b75\u5e2d\uff0c\u6211\u662f\u8499\u53e4\u4eba\uff0c\u5982\u4eca\uff0c\u662f\u53f6\u843d\u5f52\u6839\uff0c \u4f60\u4e5f\u4e0d\u5fc5\u4e3a\u6211\u96be\u8fc7\u4e86\uff0c\u4f46\u613f\u4f60\u4eec\u9c7c\u6c34\u548c\u8c10\uff0c\u767d\u5934\u5230\u8001\uff01\u201d \u3000\u3000\u62d6\u96f7\u9053\uff1a\u201c\u4e09\u59b9\uff0c\u8d70\u5427\u3002\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u9053\uff1a\u201c\u4e14\u6162\uff01\u201d \u3000\u3000\u62d6\u96f7\u76b1\u8d77\u7709\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8fd8\u6709\u4ec0\u4e48\u8bdd\u8981\u8bf4\uff1f\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u9053\uff1a\u201c\u8bf7\u4f60\u628a\u5f13\u7bad\u624b\u64a4\u53bb\uff01\u201d \u3000\u3000\u539f\u6765\u5728\u521a\u624d\u6fc0\u6218\u4e4b\u9645\uff0c\u6728\u534e\u9ece\u5df2\u7ecf\u628a\u968f\u4ece\u62d6\u96f7\u7684\u6b66\u58eb\u5c3d\u90fd\u62db\u6765\u3002\u6728\u534e\u9ece\u662f\u6709\u540d\u7684\u795e\u7bad \u624b\uff0c\u6b64\u6b21\u524d\u6765\u5927\u90fd\u6248\u4ece\u62d6\u96f7\u7684\u4e00\u6279\u6b66\u58eb\uff0c\u6b63\u662f\u4ed6\u4eb2\u81ea\u8bad\u7ec3\u7684\u201c\u795e\u7bad\u8425\u201d\u6b66\u58eb\uff0c\u4eba\u4eba\u90fd\u662f\u7cbe\u4e8e \u9a91\u5c04\u7684\u3002 \u3000\u3000\u8499\u53e4\u6b66\u58eb\u6240\u7528\u7684\u201c\u795e\u81c2\u5f13\u201d\uff0c\u80fd\u591f\u540c\u65f6\u53d1\u51fa\u5341\u4e8c\u652f\u5229\u7bad\uff0c\u5f53\u65f6\u8499\u53e4\u4eba\u521a\u521a\u53d1\u660e\u706b\u70ae\uff0c\u5c1a \u672a\u666e\u904d\u4f7f\u7528\uff0c\u8fd9\u79cd\u201c\u795e\u81c2\u5f13\u201d\u53ef\u8bf4\u662f\u6700\u5389\u5bb3\u7684\u6b66\u5668\u4e86\uff0c\u6728\u534e\u9ece\u548c\u8fd9\u73ed\u795e\u7bad\u624b\u57cb\u4f0f\u5728\u56ed\u4e2d\u7684\u6811 \u6728\u5c71\u77f3\u4e4b\u95f4\uff0c\u53ea\u542c\u62d6\u96f7\u4e00\u58f0\u4ee4\u4e0b\uff0c\u5c31\u8981\u53d1\u7bad\u4f24\u4eba\u7684\u3002 \u3000\u3000\u62d6\u96f7\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6211\u653e\u8fc7\u4ed6\u4eec\uff0c\u9633\u5929\u96f7\u4e5f\u672a\u5fc5\u80af\u653e\u8fc7\u4ed6\u4eec\u3002\u5982\u4eca\u6211\u8981\u4e09\u59b9\u56de\u53bb\uff0c\u4e5f\u4e0d\u5fc5 \u5fd9\u5728\u6b64\u65f6\u6765\u5bf9\u4ed8\u4ed6\u4eec\u3002\u201d \u3000\u3000\u62d6\u96f7\u5fc3\u91cc\u6697\u6000\u9b3c\u80ce\uff0c\u53e3\u91cc\u5374\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4e09\u59b9\uff0c\u4f60\u4e5f\u5fd2\u591a\u7591\u4e86\uff0c\u6211\u5df2\u7ecf\u7b54\u5e94\u4e86\u4f60\uff0c\u5c82\u80fd \u4f24\u5bb3\u601d\u5357\u7ecf\u7b54\uff1f\u6728\u534e\u9ece\u5c06\u519b\uff0c\u8bf7\u4f60\u64a4\u4e86\u5f13\u7bad\u624b\uff0c\u8fc7\u6765\u4e0e\u516c\u4e3b\u76f8\u89c1\u3002\u201d \u3000\u3000\u6728\u534e\u9ece\u8c12\u89c1\u4e86\u660e\u6167\u516c\u4e3b\uff0c\u62d6\u96f7\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u4e0e\u4f60\u4e00\u540c\u79bb\u5f00\uff0c\u8fd9\u4f60\u603b\u53ef\u4ee5\u653e\u5fc3\u4e86\u5427\u3002\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u5b9a\u4e86\u5b9a\u795e\uff0c\u9690\u9690\u542c\u5f97\u6709\u53ae\u6740\u4e4b\u58f0\uff0c\u5fc3\u91cc\u8d77\u7591\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u91cc\u662f\u91d1\u56fd\u7684\u56fd\u5e08\u5e9c\uff0c \u6211\u8fd8\u662f\u4e0d\u80fd\u653e\u5fc3\uff01\u201d \u3000\u3000\u62d6\u96f7\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6211\u662f\u8499\u53e4\u7684\u738b\u5b50\uff0c\u53ef\u7ba1\u4e0d\u7740\u91d1\u56fd\u7684\u4e8b\u60c5\u3002\u6211\u53ea\u80fd\u7b54\u5e94\u4f60\uff0c\u54b1\u4eec\u8499\u53e4\u7684 \u6b66\u58eb\u51b3\u4e0d\u4f24\u5bb3\u8fd9\u51e0\u4e2a\u6c49\u4eba\uff0c\u81f3\u4e8e\u4ed6\u4eec\u6709\u6ca1\u6709\u672c\u9886\u95ef\u51fa\u8fd9\u5ea7\u56fd\u5e08\u5e9c\uff0c\u90a3\u5c31\u662f\u4ed6\u4eec\u7684\u4e8b\u4e86\u3002\u201d \u3000\u3000\u5f53\u7136\u8fd9\u662f\u62d6\u96f7\u7684\u6545\u610f\u4e3a\u96be\uff0c\u5982\u679c\u4ed6\u80af\u51fa\u5934\u7684\u8bdd\uff0c\u9633\u5929\u96f7\u5c82\u6562\u4e0d\u5356\u4ed6\u7684\u60c5\u9762\u3002 \u3000\u3000\u674e\u601d\u5357\u50b2\u6c14\u52c3\u53d1\uff0c\u864e\u7709\u4e00\u7ad6\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u56fd\u5e08\u5e9c\u8c05\u4e5f\u4e0d\u662f\u864e\u7a74\u9f99\u6f6d\uff0c\u6211\u4eec\u6765\u5f97\uff0c\u5c31\u80fd\u53bb \u5f97\uff01\u591a\u8c22\u516c\u4e3b\u597d\u610f\uff0c\u8bf7\u516c\u4e3b\u5584\u81ea\u73cd\u91cd\uff0c\u4e0d\u5fc5\u4e3a\u6211\u4eec\u62c5\u5fc3\u4e86\u3002\u201d \u3000\u3000\u62d6\u96f7\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u597d\u6c49\u5b50\uff0c\u660e\u6167\uff0c\u4eba\u5bb6\u5df2\u7ecf\u9886\u4e86\u4f60\u7684\u60c5\u4e86\uff0c\u8fd9\u53ef\u8be5\u8d70\u4e86\u5427\uff1f\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u9053\uff1a\u201c\u4e14\u6162\uff01\u201d\u62d6\u96f7\u76b1\u7709\u9053\uff1a\u201c\u8fd8\u6709\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u56de\u8fc7\u5934\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u963f\u76d6\u5927\u54e5\uff0c\u5361\u6d1b\u4e1d\u59d0\u59d0\u2014\u2014\u201d \u3000\u3000\u963f\u76d6\u592b\u5987\u8d70\u5230\u660e\u6167\u516c\u4e3b\u8eab\u65c1\uff0c\u8bf4\u9053\uff1a\u201c\u516c\u4e3b\u6709\u4f55\u5429\u5490\uff1f\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u8ddf\u6211\u4e00\u573a\uff0c\u5171\u540c\u60a3\u96be\uff0c\u5c31\u50cf\u5144\u5f1f\u59d0\u59b9\u4e00\u822c\u3002\u4f46\u73b0\u5728\u6211\u4e0e\u54e5\u54e5\u56de \u56fd\uff0c\u53ef\u4e0d\u80fd\u548c\u4f60\u4eec\u5728\u4e00\u8d77\u4e86\u3002\u4f60\u4eec\u613f\u610f\u5230\u54ea\u513f\u5c31\u5230\u54ea\u513f\uff0c\u54b1\u4eec\u5c31\u6b64\u5206\u624b\u3002\u201d \u3000\u3000\u963f\u76d6\u4e00\u65f6\u672a\u61c2\u660e\u6167\u516c\u4e3b\u7684\u7528\u610f\uff0c\u8eac\u8170\u8bf4\u9053\uff1a\u201c\u516c\u4e3b\u56de\u56fd\uff0c\u6211\u4eec\u5f53\u7136\u662f\u62a4\u9001\u516c\u4e3b\u56de\u56fd\u3002\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u5659\u7740\u773c\u6cea\u8bf4\u9053\uff1a\u201c\u4e0d\u662f\u6211\u4e0d\u8981\u4f60\uff0c\u54b1\u4eec\u8fd8\u662f\u5206\u624b\u7684\u597d\u3002\u4f60\u8981\u56de\u56fd\uff0c\u53ef\u4ee5\u5148\u8d70\uff0c \u6211\u6709\u8fd9\u8bb8\u591a\u6b66\u58eb\u62a4\u9001\uff0c\u7528\u4e0d\u7740\u4f60\u4eec\u4e86\uff01\u201d \u3000\u3000\u539f\u6765\u660e\u6167\u516c\u4e3b\u6df1\u77e5\u62d6\u96f7\u7684\u6027\u683c\uff0c\u62d6\u96f7\u5916\u8868\u8c6a\u653e\u5bbd\u539a\uff0c\u5176\u5b9e\u5374\u662f\u5fcc\u523b\u4e4b\u5fc3\u751a\u91cd\u7684\u3002\u963f\u76d6\u5728 \u519b\u4e2d\u79c1\u9003\u51fa\u6765\u8ddf\u4ece\u81ea\u5df1\uff0c\u6b64\u9645\u62d6\u96f7\u788d\u7740\u81ea\u5df1\u7684\u9762\u5b50\uff0c\u5bf9\u963f\u76d6\u592b\u5987\u53ea\u80fd\u6682\u65f6\u5bb9\u5fcd\uff0c\u56de\u56fd\u4e4b\u540e\u4e00 \u5b9a\u4e0d\u4f1a\u653e\u8fc7\u4ed6\u4eec\u3002\u662f\u4ee5\u660e\u6167\u516c\u4e3b\u5fc5\u987b\u628a\u4ed6\u4eec\u5148\u884c\u9063\u8d70\u3002 \u3000\u3000\u963f\u76d6\u4e0d\u61c2\uff0c\u5361\u6d1b\u4e1d\u5df2\u662f\u660e\u767d\u4e86\u516c\u4e3b\u7684\u82e6\u5fc3\uff0c\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u516c\u4e3b\uff0c\u4f60\u521a\u624d\u8bf4\u5f97\u597d\uff0c\u5929\u4e0b\u65e0 \u4e0d\u6563\u4e4b\u7b75\u5e2d\uff0c\u963f\u76d6\uff0c\u54b1\u4eec\u8d70\u5427\u3002\u201d\u8bf4\u8bdd\u4e4b\u9645\uff0c\u8f7b\u8f7b\u5728\u963f\u76d6\u624b\u5fc3\u634f\u4e86\u4e00\u4e0b\uff0c\u963f\u76d6\u604d\u7136\u5927\u609f\uff0c\u4e0d \u7981\u864e\u76ee\u6d41\u6cea\uff0c\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u516c\u4e3b\u5904\u5904\u4e3a\u6211\u4eec\u7740\u60f3\uff0c\u4eca\u540e\u53ea\u6015\u76f8\u89c1\u65e0\u671f\uff0c\u613f\u516c\u4e3b\u591a\u591a\u4fdd\u91cd \u4e86\u3002\u201d \u3000\u3000\u963f\u76d6\u592b\u5987\u8d70\u540e\uff0c\u6768\u5a49\u54bd\u7740\u773c\u6cea\u548c\u660e\u6167\u516c\u4e3b\u9053\u522b\uff0c\u5927\u5bb6\u5fc3\u91cc\u660e\u767d\uff0c\u8fd9\u4e00\u522b\u5c31\u662f\u751f\u79bb\u6b7b\u522b \u4e86\u3002 \u3000\u3000\u6768\u5a49\u8bf4\u9053\uff1a\u201c\u516c\u4e3b\uff0c\u4f46\u613f\u4f60\u5409\u4eba\u5929\u76f8\uff0c\u9047\u96be\u5448\u7965\u3002\u6211\u4f1a\u6c38\u8fdc\u8bb0\u7740\u4f60\u7684\uff0c\u53ea\u662f\u4f60\u7684\u5927\u6069\uff0c \u53ea\u6015\u6211\u662f\u4e0d\u80fd\u62a5\u7b54\u4e86\u3002\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u9053\uff1a\u201c\u54b1\u4eec\u76f8\u4ea4\u4ee5\u5fc3\uff0c\u6211\u4e5f\u6ca1\u6709\u4ec0\u4e48\u8bdd\u8bf4\u4e86\u3002\u5b5f\u59d1\u5a18\u548c\u5c60\u59d1\u5a18\u5c31\u8981\u6765\u7684\uff0c\u89c1\u4e86 \u5979\u4eec\uff0c\u8bf7\u4f60\u4e3a\u6211\u4ee3\u81f4\u6b49\u610f\uff0c\u539f\u8c05\u6211\u4e0d\u8f9e\u800c\u884c\uff0c\u597d\uff0c\u795d\u4f60\u4eec\u592b\u59bb\u767d\u5934\u5055\u8001\uff0c\u65e0\u590d\u4ee5\u6211\u4e3a\u5ff5\u3002\u201d \u8bf4\u81f3\u6b64\u5904\uff0c\u7a81\u7136\u60f3\u8d77\u5728\u548c\u6797\u4e4b\u65f6\u4e0e\u674e\u601d\u5357\u76f8\u5904\u7684\u90a3\u6bb5\u65e5\u5b50\uff0c\u4e0d\u6562\u56de\u5934\uff0c\u54bd\u4e0b\u773c\u6cea\uff0c\u5c31\u8ddf\u62d6\u96f7 \u8d70\u4e86\u3002 \u3000\u3000\u8fd9\u73ed\u8499\u53e4\u6b66\u58eb\u8ddf\u7740\u62d6\u96f7\u79bb\u5f00\uff0c\u7acb\u523b\u4fbf\u6709\u91d1\u56fd\u6b66\u58eb\u591a\u4eba\u6765\u5230\uff0c\u9f50\u58f0\u5450\u558a\uff0c\u56f4\u62e2\u4e0a\u6765\u3002 \u3000\u3000\u5b5f\u5c11\u521a\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u503c\u4e0d\u5f97\u6c61\u6211\u5b9d\u5251\uff0c\u4e14\u53eb\u4f60\u4eec\u77e5\u9053\u6211\u7684\u5389\u5bb3\uff01\u201d\u957f\u5251\u4e00\u6325\uff0c\u767b\u65f6\u95ea \u8d77\u4e86\u5343\u767e\u9053\u5149\u8292\uff0c\u53ea\u542c\u5f97\u201c\u54ce\u54df\uff01\u54ce\u54df\uff01\u201d\u4e4b\u58f0\u4e0d\u7edd\u4e8e\u8033\uff0c\u8f6c\u773c\u4e4b\u95f4\uff0c\u5df2\u6709\u5341\u51e0\u4e2a\u91d1\u56fd\u6b66\u58eb \u5012\u4e86\u4e0b\u53bb\uff0c\u4f46\u53ea\u662f\u5012\u5728\u5730\u4e0a\u4e0d\u80fd\u52a8\u5f39\uff0c\u5e76\u6ca1\u4e00\u4eba\u6d41\u8840\uff0c\u539f\u6765\u662f\u7ed9\u5b5f\u5c11\u521a\u4ee5\u6700\u4e0a\u4e58\u7684\u523a\u7a74\u5251\u6cd5 \u70b9\u4e86\u7a74\u9053\u3002\u5b5f\u5c11\u521a\u7684\u5b9d\u5251\u679c\u7136\u4e1d\u6beb\u4e0d\u53d7\u8840\u6c61\u3002 \u3000\u3000\u97e9\u5927\u7ef4\u7b11\u9053\uff1a\u201c\u5b5f\u5144\uff0c\u4f60\u4e5f\u8be5\u8ba9\u6211\u8fc7\u4e00\u8fc7\u763e\uff01\u201d\u4ed6\u521a\u624d\u5fcd\u53d7\u8499\u53e4\u6b66\u58eb\u7684\u56f4\u653b\uff0c\u672a\u80fd\u4e00\u5c55 \u8eab\u624b\uff0c\u6b64\u65f6\u624d\u628a\u8fd9\u53e3\u95f7\u6c14\u53d1\u6cc4\u51fa\u6765\uff0c\u53ea\u89c1\u4ed6\u4f7f\u51fa\u4e86\u5927\u6454\u7891\u7684\u529f\u592b\uff0c\u5c31\u50cf\u997f\u9e70\u6251\u5154\u4e00\u822c\uff0c\u90a3\u4e9b \u6b66\u58eb\u4e00\u78b0\u4e0a\u4ed6\uff0c\u5c31\u7ed9\u4ed6\u6293\u7740\u9888\u9879\u6454\u51fa\u53bb\uff0c\u4e0d\u6d88\u7247\u523b\uff0c\u6570\u5341\u540d\u6b66\u58eb\u6216\u662f\u7ed9\u5b5f\u5c11\u521a\u70b9\u4e86\u7a74\u9053\uff0c\u6216 \u662f\u7ed9\u4ed6\u6454\u5012\uff0c\u4f59\u4f17\u4e00\u54c4\u800c\u6563\u3002 \u3000\u3000\u674e\u601d\u5357\u5ffd\u9053\uff1a\u201c\u4e0d\u597d\uff01\u201d\u6768\u5a49\u8be7\u9053\uff1a\u201c\u8fd9\u4e9b\u91d1\u72d7\u7ed9\u54b1\u4eec\u6740\u5f97\u671b\u98ce\u62ab\u9761\uff0c\u8fd8\u6709\u4ec0\u4e48\u4e0d \u597d\u3002\u201d\u674e\u601d\u5357\u9053\uff1a\u201c\u8fd9\u4e9b\u6b66\u58eb\u90fd\u662f\u4e8c\u4e09\u6d41\u7684\u8d27\u8272\uff0c\u9633\u5929\u96f7\u7684\u56fd\u5e08\u5e9c\u600e\u4f1a\u6ca1\u6709\u80fd\u4eba\uff1f\u201d\u6768\u5a49\u970d \u7136\u4e00\u7701\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u90a3\u4e9b\u9ad8\u624b\u54ea\u91cc\u53bb\u4e86\uff1f\u201d\u674e\u601d\u5357\u8bf4\u9053\uff1a\u201c\u62d6\u96f7\u521a\u624d\u8bf4\u6709\u54b1\u4eec\u7684\u4eba\u7ed9\u9633 \u5929\u96f7\u7684\u624b\u4e0b\u5305\u56f4\uff0c\u53ea\u6015\u662f\u771f\u7684\u4e86\uff1f\u201d \u3000\u3000\u6b64\u65f6\u5728\u4ed6\u4eec\u5468\u56f4\u7684\u6b66\u58eb\u90fd\u5df2\u6563\u4e86\uff0c\u5b5f\u5c11\u521a\u51dd\u795e\u4e00\u542c\uff0c\u8bf4\u9053\uff1a\u201c\u897f\u5357\u89d2\u548c\u4e1c\u5317\u89d2\u90fd\u6709\u4eba\u53ae \u6740\uff0c\u897f\u5357\u89d2\u662f\u9ad8\u624b\u62fc\u6597\uff0c\u4e1c\u5317\u89d2\u4f3c\u662f\u6df7\u6218\uff01\u201d\u97e9\u5927\u7ef4\u9053\uff1a\u201c\u6df7\u6218\u7684\u6050\u6015\u5fc5\u662f\u4e10\u5e2e\u5f1f\u5b50\u3002\u897f\u5357 \u89d2\u4e00\u5b9a\u662f\u891a\u4e91\u5cf0\u548c\u8c37\u6db5\u865a\u4ed6\u4eec\u3002\u6211\u4e0e\u745b\u513f\u53bb\u52a9\u9646\u5e2e\u4e3b\u4e00\u81c2\u4e4b\u529b\uff0c\u5c11\u521a\u5144\uff0c\u4f60\u4e0e\u601d\u5357\u8d24\u4f84\u53bb\u6597 \u4e00\u6597\u9633\u5929\u96f7\u5427\u3002\u201d \u3000\u3000\u201c\u56fd\u5e08\u5e9c\u201d\u5360\u5730\u6570\u5341\u5357\uff0c\u4e1c\u5317\u89d2\u4e0e\u897f\u5357\u89d2\u76f8\u8ddd\u6709\u6570\u91cc\u4e4b\u9065\uff0c\u674e\u601d\u5357\u88ab\u56da\u4e4b\u5904\u5219\u662f\u56ed\u5b50\u5f53 \u4e2d\u7684\u4e00\u4e2a\u504f\u50fb\u5904\u6240\uff0c\u5b5f\u5c11\u521a\u80fd\u591f\u542c\u51fa\u4f55\u5904\u662f\u9ad8\u624b\u6bd4\u62fc\uff0c\u4f55\u5904\u662f\u6df7\u6218\uff0c\u8fd9\u6837\u7684\u542c\u58f0\u8fa8\u5668\u672c\u9886\u786e \u662f\u9ad8\u660e\u4e4b\u6781\uff0c\u4f17\u4eba\u65e0\u4e0d\u4f69\u670d\uff01 \u3000\u3000\u674e\u601d\u5357\u6302\u5ff5\u891a\u3001\u8c37\u4e8c\u4eba\u7684\u5b89\u5371\uff0c\u8d76\u5fd9\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u54b1\u4eec\u73b0\u5728\u5206\u9053\u626c\u9556\uff0c\u7a81\u56f4\u4e4b\u540e\uff0c\u5728\u4e1c \u5317\u89d2\u4f1a\u5408\u3002\u201d \u3000\u3000\u4e14\u8bf4\u891a\u3001\u8c37\u4e8c\u4eba\u4e0e\u9633\u5929\u96f7\u62fc\u6597\uff0c\u6b64\u65f6\u6b63\u662f\u5230\u4e86\u6700\u5403\u7d27\u7684\u5173\u5934\uff0c\u9633\u5929\u96f7\u72de\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u80c6 \u6562\u72af\u4e0a\u4f5c\u4e71\uff0c\u5982\u4eca\u77e5\u9053\u6211\u7684\u5389\u5bb3\u4e86\u5417\uff1f\u4f60\u4eec\u670d\u662f\u4e0d\u670d\uff0c\u5426\u5219\u660e\u5e74\u4eca\u65e5\uff0c\u5c31\u662f\u4f60\u4eec\u7684\u5fcc\u8fb0 \u4e86\uff01\u201d \u3000\u3000\u891a\u3001\u8c37\u4e8c\u4eba\u5f00\u58f0\u8bf4\u9053\uff1a\u201c\u6740\u8eab\u6210\u4ec1\uff0c\u820d\u8eab\u53d6\u4e49\uff0c\u5927\u4e08\u5927\u6b7b\u800c\u4f55\uff01\u7eb5\u4f7f\u6211\u4eec\u4eca\u65e5\u4e0d\u80fd\u6e05\u7406 \u5e08\u95e8\uff0c\u4f60\u4e5f\u7ec8\u987b\u96be\u9003\u516c\u9053\uff01\u201d \u3000\u3000\u9633\u5929\u96f7\u51b7\u7b11\u9053\uff1a\u201c\u6b7b\u5230\u4e34\u5934\uff0c\u8fd8\u8981\u53e3\u54cd\uff0c\u54fc\uff0c\u4f60\u4eec\u8981\u6740\u8eab\u6210\u4ec1\u662f\u4e0d\u662f\uff1f\u597d\uff0c\u6211\u5c31\u6210\u5168\u4f60 \u4eec\u7684\u5fc3\u613f\u5427\uff01\u201d\u8bf4\u5230\u201c\u6210\u5168\u201d\u4e8c\u5b57\uff0c\u547c\u7684\u4e00\u638c\u5288\u6765\u3002\u8fd9\u4e00\u638c\u662f\u4ed6\u7559\u4f5c\u81f4\u547d\u7684\u4e00\u51fb\u7684\uff0c\u5168\u8eab\u6c14 \u529b\u90fd\u5df2\u5728\u8fd9\u4e00\u638c\u4e0a\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u7d27\u54ac\u820c\u5934\uff0c\u55b7\u51fa\u4e00\u53e3\u9c9c\u8840\uff0c\u8c37\u6db5\u865a\u7ea2\u4e86\u773c\u775b\uff0c\u773c\u4e2d\u4e5f\u597d\u4f3c\u8981\u55b7\u51fa\u706b\u6765\uff0c\u4e24\u4eba\u5404\u51fa \u4e00\u638c\uff0c\u5408\u6210\u4e00\u9053\u5706\u5f27\uff0c\u4e5f\u662f\u4f7f\u51fa\u4e86\u6700\u540e\u4e00\u62db\u7684\u6740\u624b\u201c\u96f7\u7535\u4ea4\u8f70\u201d\uff01 \u3000\u3000\u9633\u5929\u96f7\u53ea\u9053\u4ed6\u4eec\u5df2\u7ecf\u7b4b\u75b2\u529b\u6e34\uff0c\u4e0d\u6599\u891a\u4e91\u5cf0\u54ac\u820c\u55b7\u8840\uff0c\u7adf\u80fd\u628a\u6b8b\u4f59\u7684\u7cbe\u5200\u90fd\u51dd\u805a\u8d77\u6765\uff0c \u548c\u4ed6\u4f5c\u6700\u540e\u7684\u4e00\u62fc\uff01\u52a0\u4e0a\u4e86\u4e00\u4e2a\u5c1a\u6709\u4e94\u516d\u5206\u529f\u529b\u7684\u8c37\u6db5\u865a\uff0c\u8fd9\u4e00\u62db\u201c\u96f7\u7535\u4ea4\u8f70\u201d\uff0c\u5b9e\u662f\u975e\u540c \u5c0f\u53ef\u3002 \u3000\u3000\u9633\u5929\u96f7\u4e0d\u7981\u5fc3\u5934\u4e00\u51db\uff0c\u60f3\u9053\uff1a\u201c\u6211\u8fd9\u4e00\u638c\u7eb5\u7136\u51fb\u6bd9\u4e86\u4ed6\u4eec\uff01\u53ea\u6015\u4e5f\u662f\u96be\u514d\u8981\u5927\u75c5\u4e00\u573a\uff0c \u8017\u635f\u4e00\u5e74\u529f\u529b\uff01\u201d\u4f46\u6b64\u65f6\u53cc\u65b9\u90fd\u5728\u79bb\u5f26\u4e4b\u7bad\uff0c\u8c01\u4e5f\u4e0d\u80fd\u907f\u5f00\uff01 \u3000\u3000\u773c\u770b\u5c31\u8981\u4e24\u8d25\u4ff1\u4f24\uff0c\u800c\u891a\u3001\u8c37\u4e8c\u4eba\u5b9a\u5c06\u4f24\u5f97\u66f4\u91cd\uff0c\u5c31\u5728\u8fd9\u95f4\u4e0d\u5bb9\u53d1\u4e4b\u9645\uff0c\u5ffd\u89c1\u767d\u5203\u8000 \u773c\uff0c\u4e00\u6761\u4eba\u5f71\u95ea\u7535\u4f3c\u5730\u63a0\u6765\uff0c\u6765\u7684\u6b63\u662f\u6c5f\u5357\u5927\u4fa0\u5b5f\u5c11\u521a\u3002 \u3000\u3000\u5b5f\u5c11\u521a\u63d2\u5728\u4e2d\u95f4\uff0c\u5530\u7684\u4e00\u5251\u5411\u9633\u5929\u96f7\u523a\u53bb\uff0c\u5de6\u638c\u8f7b\u8f7b\u4e00\u63a8\uff0c\u4f7f\u4e86\u4e00\u4e2a\u5de7\u52b2\uff0c\u5c06\u891a\u4e91\u5cf0\u63a8 \u5f00\uff0c\u53f3\u811a\u4e00\u62e8\uff0c\u53c8\u62e7\u5f00\u8c37\u6db5\u865a\u7684\u8eab\u5f62\u62e8\u5f97\u6253\u4e86\u4e00\u4e2a\u76d8\u65cb\uff0c\u6070\u6070\u8131\u51fa\u4e86\u9633\u5929\u96f7\u638c\u529b\u7684\u7b3c\u7f69\u3002 \u3000\u3000\u53ea\u542c\u5f97\u8f70\u7684\u4e00\u58f0\uff0c\u5b5f\u5c11\u521a\u8eab\u5f62\u8fde\u6643\uff0c\u659c\u9000\u6570\u6b65\uff0c\u9633\u5929\u96f7\u7684\u8863\u8896\u5374\u7ed9\u4ed6\u7684\u5b9d\u5251\u524a\u53bb\u4e86\u4e00 \u622a\uff0c\u5728\u5251\u5149\u75be\u7ede\u4e4b\u4e0b\uff0c\u5316\u6210\u4e86\u7247\u7247\u8774\u8776\u3002 \u3000\u3000\u539f\u6765\u5b5f\u5c11\u521a\u56e0\u4e3a\u4e0e\u9f99\u8c61\u6cd5\u738b\u6076\u6597\u4e86\u4e00\u573a\uff0c\u5185\u529b\u5df2\u662f\u8017\u635f\u4e0d\u5c0f\uff0c\u9633\u5929\u96f7\u6fc0\u6218\u891a\u3001\u8c37\u4e8c\u4eba\uff0c \u5185\u529b\u867d\u7136\u4ea6\u6709\u8017\u635f\uff0c\u6bd5\u7adf\u4e0d\u5982\u4ed6\u8017\u635f\u4e4b\u751a\uff0c\u800c\u5b5f\u5c11\u521a\u53c8\u8981\u5206\u51fa\u5185\u529b\u6551\u4eba\u3002\u662f\u4ee5\u4ed6\u672c\u6765\u53ef\u4ee5\u7a0d \u80dc\u9633\u5927\u96f7\u4e00\u7b79\u7684\uff0c\u7ed3\u679c\u5374\u53d8\u6210\u4e86\u4ed6\u4f3c\u4e4e\u5403\u4e8f\u66f4\u5927\u4e86\u3002 \u3000\u3000\u8fd9\u65f6\u624d\u542c\u5f97\u201c\u54ce\u54df\uff0c\u54ce\u54df\u2019\u4e4b\u58f0\u6b64\u8d77\u5f7c\u843d\uff0c\u539f\u6765\u662f\u7ed9\u5b5f\u5c11\u521a\u95ef\u8fdb\u95e8\u6765\u4e4b\u9645\uff0c\u7528\u5feb\u5251\u523a\u4f24 \u7684\u51e0\u4e2a\u6b66\u58eb\uff0c\u6b64\u65f6\u65b9\u59cb\u5012\u5728\u4e86\u5730\u4e0a\uff0c\u53eb\u51fa\u58f0\u6765\u3002 \u3000\u3000\u9633\u5927\u96f7\u867d\u7136\u5e76\u672a\u5403\u4e8f\uff0c\u751a\u81f3\u8fd8\u7565\u5360\u4e0a\u98ce\uff0c\u4f46\u770b\u89c1\u5b5f\u5c11\u521a\u7684\u5251\u6cd5\u5982\u6b64\u7cbe\u5999\uff0c\u4e5f\u662f\u4e0d\u7981\u5fc3\u5934 \u4e00\u9707\uff0c\u5728\u5927\u60ca\u4e4b\u4e0b\uff0c\u540c\u65f6\u53c8\u662f\u6697\u6697\u53eb\u4e86\u58f0\u201c\u4fa5\u5e78\u201d\u3002\u8981\u77e5\u5018\u82e5\u4e0d\u662f\u5b5f\u5c11\u521a\u6070\u597d\u5728\u8fd9\u6700\u7d27\u8981\u7684 \u5173\u5934\u6765\u5230\uff0c\u5c06\u891a\u3001\u8c37\u4e8c\u4eba\u62c9\u5f00\uff0c\u8c01\u4eba\u80fd\u591f\u5316\u89e3\u4ed6\u4eec\u7684\u8fd9\u4e00\u62db\u6b7b\u6597\uff01 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u5c31\u5728\u53d7\u4f24\u7684\u6b66\u58eb\u58f0\u58f0\u60e8\u53eb\u58f0\u4e2d\uff0c\u674e\u601d\u5357\u3001\u6768\u5a49\u4e8c\u4eba\u4e5f\u6740\u4e86\u8fdb\u6765\uff01 \u3000\u3000\u9633\u5929\u96f7\u8fd9\u95f4\u5bc6\u5ba4\uff0c\u6709\u5bcc\u8d35\u4eba\u5bb6\u7684\u5730\u65b9\u90a3\u4e48\u5927\uff0c\u4f46\u6bd5\u7adf\u4e5f\u8fd8\u662f\u5730\u65b9\u6709\u9650\uff0c\u53ea\u80fd\u5bb9\u5f97\u5341\u6570\u4e2a \u4eba\u6349\u5bf9\u513f\u53ae\u6740\uff0c\u9634\u5929\u96f7\u521a\u624d\u6ca1\u6709\u51fa\u58f0\uff0c\u90a3\u662f\u56e0\u4e3a\u4e00\u6765\u5f62\u52bf\u4e8e\u4ed6\u6709\u5229\uff0c\u4e8c\u6765\u4ed6\u548c\u891a\u3001\u8c37\u4e8c\u4eba\u62fc \u6597\u5185\u529f\uff0c\u8c01\u90fd\u4e0d\u80fd\u6446\u8131\u7684\u7f18\u6545\uff0c\u5982\u4eca\u5b5f\u5c11\u521a\u5df2\u5c06\u4ed6\u4eec\u62c6\u89e3\uff0c\u9633\u5929\u96f7\u8981\u51fa\u53bb\u65e0\u4eba\u80fd\u591f\u963b\u62e6\uff0c\u5f62 \u52bf\u9006\u8f6c\uff0c\u5bf9\u4ed6\u6765\u8bf4\uff0c\u4e5f\u662f\u4e0e\u5176\u56f0\u5728\u5ba4\u4e2d\uff0c\u4e0d\u5982\u95ef\u51fa\u53bb\uff0c\u6765\u573a\u5927\u6df7\u6218\u7684\u6709\u5229\u4e86\u3002 \u3000\u3000\u674e\u601d\u5357\u3001\u6768\u5a49\u53cc\u5251\u5408\u58c1\uff0c\u5377\u8d77\u4e86\u4e00\u9053\u94f6\u8679\uff0c\u5411\u9633\u5929\u96f7\u75be\u523a\u8fc7\u53bb\uff0c\u5b5f\u5c11\u521a\u559d\u9053\uff1a\u201c\u54ea\u91cc \u8d70\uff01\u201d\u957f\u5251\u4e00\u6a2a\uff0c\u622a\u4f4f\u9633\u5929\u96f7\u7684\u53bb\u8def\uff0c\u4ed6\u987e\u4f4f\u8eab\u4efd\uff0c\u6ca1\u6709\u548c\u674e\u3001\u6768\u8054\u624b\uff0c\u4f46\u4ed6\u622a\u4f4f\u53bb\u8def\uff0c\u7b49 \u4e8e\u662f\u7ed9\u674e\u601d\u5357\u62bc\u9635\uff0c\u9633\u5929\u96f7\u7109\u5f97\u4e0d\u614c\uff1f \u3000\u3000\u201c\u4e09\u5341\u516d\u8ba1\u8d70\u4e3a\u4e0a\u8ba1\u201d\uff0c\u9633\u5929\u96f7\u6253\u5b9a\u4e3b\u610f\uff0c\u53cc\u638c\u5f00\u51fa\uff0c\u547c\u547c\u98ce\u54cd\uff0c\u8361\u5f00\u4e86\u674e\u601d\u5357\u548c\u6768\u5a49 \u7684\u4e24\u67c4\u957f\u5251\uff0c\u7a81\u7136\u4e00\u4e2a\u9000\u6b65\u62bd\u8eab\uff0c\u53ea\u542c\u5f97\u201c\u8f70\u201d\u7684\u4e00\u58f0\uff0c\u54cd\u82e5\u96f7\u9e23\uff0c\u5728\u4ed6\u8eab\u540e\u90a3\u5835\u5899\u58c1\uff0c\u7adf \u7136\u7ed9\u4ed6\u7528\u5929\u96f7\u529f\u649e\u5f00\u4e00\u4e2a\u5927\u6d1e\uff0c\u521a\u597d\u80fd\u591f\u5bb9\u4ed6\u7a7f\u8fc7\u3002\u6ce5\u5757\u7eb7\u98de\uff0c\u7816\u74e6\u96e8\u843d\u4e4b\u4e2d\uff0c\u9633\u5929\u96f7\u5df2\u662f \u5230\u4e86\u5916\u9762\u4e86\u3002 \u3000\u3000\u5b5f\u5c11\u521a\u6050\u6015\u65e0\u4eba\u80fd\u591f\u5236\u4ed6\uff0c\u5982\u5f71\u968f\u5f62\u5730\u7acb\u5373\u8ffd\u53bb\uff0c\u891a\u3001\u8c37\u4e8c\u4eba\u5598\u8fc7\u53e3\u6c14\uff0c\u89c1\u9633\u5929\u96f7\u7a81\u7136 \u7834\u58c1\u800c\u51fa\uff0c\u5446\u4e86\u4e00\u5446\uff0c\u4e5f\u5c31\u9a6c\u4e0a\u8ddf\u7740\u8ffd\u51fa\u53bb\u4e86\u3002 \u3000\u3000\u6b64\u65f6\u5728\u623f\u5b50\u91cc\u7684\u8fd8\u6709\u4e24\u5bf9\u53ae\u6740\u672a\u4f11\uff0c\u4e00\u5bf9\u662f\u67f3\u6d1e\u5929\u548c\u9633\u575a\u767d\u6597\u5251\uff0c\u67f3\u6d1e\u5929\u7a0d\u5360\u4e0a\u98ce\uff1b\u4e00 \u5bf9\u662f\u5d14\u9547\u5c71\u4e0e\u767d\u4e07\u96c4\u62fc\u638c\uff0c\u5374\u662f\u5d14\u9547\u5c71\u5341\u5206\u4e0d\u5229\u3002 \u3000\u3000\u674e\u601d\u5357\u4e00\u58f0\u53f1\u54a4\uff0c\u5b9d\u5251\u626c\u7a7a\u4e00\u95ea\uff0c\u5530\u7684\u4fbf\u662f\u4e00\u62db\u201c\u767d\u8679\u8d2f\u65e5\u201d\u5251\u5149\u5982\u7ec3\uff0c\u5411\u81ea\u4e07\u96c4\u5f84\u523a \u8fc7\u53bb\uff0c\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u8001\u8d3c\uff0c\u4f60\u4ece\u524d\u5728\u767d\u5bb6\u5e84\u662f\u600e\u6837\u5bf9\u6211\u8bf4\u7684\uff1f\u4f60\u8bf4\u4f60\u4ece\u6b64\u91d1\u76c6\u6d17\u624b\uff0c\u9769\u9762\u6d17 \u5fc3\uff0c\u4e3a\u4f55\u5982\u4eca\u53c8\u6765\u6b64\u52a9\u7ea3\u4e3a\u8650\uff1f\u201d \u3000\u3000\u767d\u4e07\u96c4\u594b\u529b\u89e3\u4e86\u4e09\u62db\uff0c\u53eb\u9053\uff1a\u201c\u674e\u76df\u4e3b\u9ad8\u62ac\u8d35\u624b\uff0c\u5bb9\u6211\u56de\u4e61\uff0c\u4ee5\u540e\u6211\u518d\u4e5f\u4e0d\u6562\u4e86\u3002\u201d\u674e \u601d\u5357\u51b7\u7b11\u9053\uff1a\u201c\u81ea\u4f5c\u5b7d\u4e0d\u53ef\u6d3b\uff01\u8c01\u8fd8\u80fd\u591f\u76f8\u4fe1\u4f60\u7684\u8bf4\u8bdd\uff1f\u201d \u3000\u3000\u767d\u4e07\u96c4\u9661\u5730\u4e00\u4e2a\u98de\u8eab\u75be\u6251\uff0c\u8d81\u7740\u674e\u601d\u5357\u8bf4\u8bdd\u4e4b\u9645\uff0c\u5411\u5d14\u9547\u5c71\u731b\u4e0b\u6740\u624b\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u53ea \u8981\u6293\u5f97\u4f4f\u8fd9\u4eba\uff0c\u674e\u601d\u5357\u4e0d\u80fd\u4e0d\u6295\u9f20\u5fcc\u5668\u3002\u201d\u54ea\u77e5\u4ed6\u5feb\uff0c\u674e\u601d\u5357\u7684\u5251\u62db\u66f4\u5feb\u3002\u674e\u601d\u5357\u89c1\u4ed6\u5982\u6b64 \u72e0\u8fa3\uff01\u6012\u4ece\u5fc3\u8d77\uff0c\u4ee5\u8fc5\u96f7\u4e0d\u53ca\u63a9\u8033\u7684\u624b\u6cd5\uff0c\u89d1\u4e2a\u6b63\u7740\uff0c\u5530\u7684\u4e00\u5251\uff0c\u5c31\u5251\u7a7f\u4e86\u4ed6\u7684\u7435\u7436\u9aa8\uff01\u767d \u4e07\u96c4\u50cf\u6839\u6728\u5934\u4f3c\u7684\uff0c\u6643\u4e86\u4e24\u6643\uff0c\u201c\u535c\u901a\u201d\u5012\u5730\uff0c\u674e\u601d\u5357\u559d\u9053\uff1a\u201c\u9976\u4f60\u4e0d\u6b7b\uff0c\u4ee5\u540e\u597d\u597d\u505a\u4eba \u5427\uff01\u201d\u767d\u4e07\u96c4\u7435\u7436\u9aa8\u524a\u65ad\uff0c\u6b66\u529f\u88ab\u5e9f\uff0c\u5df2\u662f\u6210\u4e86\u5e9f\u4eba\u3002\u674e\u601d\u5357\u624b\u72e0\u5fc3\u6148\uff0c\u6545\u800c\u9976\u4ed6\u4e00\u547d\u3002 \u3000\u3000\u53e6\u4e00\u8fb9\uff0c\u6768\u5a49\u5bf9\u9633\u575a\u767d\u4ea6\u5df1\u51fa\u624b\u3002\u5979\u66fe\u53d7\u8fc7\u9633\u575a\u767d\u7684\u6b3a\u4fae\uff01\u6b64\u65f6\u6b63\u662f\u4ec7\u4eba\u89c1\u9762\uff0c\u5206\u5916\u773c \u7ea2\uff0c\u5251\u8d70\u8fde\u73af\uff0c\u62db\u62db\u90fd\u662f\u6740\u624b\u3002 \u3000\u3000\u9633\u575a\u767d\u5bf9\u4ed8\u67f3\u6d1e\u5929\u5df2\u662f\u529b\u4e0d\u4ece\u5fc3\uff0c\u6b64\u65f6\u52a0\u4e0a\u4e86\u4e00\u4e2a\u672c\u9886\u548c\u4ed6\u5dee\u4e0d\u591a\u7684\u6768\u5a49\uff0c\u5982\u4f55\u8fd8\u80fd\u5e94 \u4ed8\uff1f\u4e0d\u8fc7\u5341\u6570\u62db\uff0c\u53ea\u542c\u5f97\u201c\u5494\u5693\u201d\u4e00\u58f0\uff0c\u9633\u575a\u767d\u5de6\u624b\u4e94\u6839\u6307\u5934\uff0c\u5168\u7ed9\u6768\u5a49\u524a\u65ad\uff01 \u3000\u3000\u9633\u575a\u767d\u4e00\u58f0\u60e8\u53eb\uff0c\u53f3\u624b\u957f\u5251\u63b7\u51fa\uff0c\u67f3\u6d1e\u5929\u4e00\u62db\u201c\u6a2a\u4e91\u65ad\u5cf0\u201d\uff0c\u51fb\u843d\u4ed6\u7684\u957f\u5251\uff0c\u8ffd\u4e0a\u53bb\u5f85 \u8981\u53d6\u4ed6\u6027\u547d\uff0c\u9633\u575a\u767d\u5df2\u7ecf\u9003\u51fa\u53bb\u4e86\u3002\u91d1\u56fd\u6b66\u58eb\u8702\u62e5\u800c\u4e0a\uff0c\u62e6\u4f4f\u67f3\u3001\u6768\u4e8c\u4eba\u3002\u9633\u575a\u767d\u5728\u91cd\u4f24\u4e4b \u4e0b\uff0c\u5c45\u7136\u9003\u51fa\u4e86\u6027\u547d\uff0c\u4e5f\u53ef\u7b97\u5f97\u662f\u4fa5\u5e78\u4e4b\u6781\u4e86\u3002 \u3000\u3000\u9633\u5929\u96f7\u9003\u4e86\u51fa\u53bb\uff0c\u4e00\u58f0\u957f\u5578\uff0c\u53ec\u96c6\u624b\u4e0b\uff0c\u5ffd\u89c1\u4e00\u4e2a\u5fa1\u6797\u519b\u519b\u5b98\u5306\u5306\u8dd1\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u51e0 \u4e2a\u5c0f\u8d3c\u4e0d\u8db3\u4e3a\u60a3\uff0c\u5916\u9762\u6765\u4e86\u4e24\u5e2e\u4eba\u9a6c\uff0c\u4e1a\u5df2\u653b\u4e86\u8fdb\u6765\uff0c\u8bf7\u56fd\u5e08\u524d\u5f80\u7763\u9635\u8981\u7d27\uff0c\u8fd9\u91cc\u6709\u4e00\u8425\u5f13 \u7bad\u624b\u6599\u60f3\u5df2\u662f\u8db3\u4ee5\u5bf9\u4ed8\u3002\u201d \u3000\u3000\u9633\u5929\u96f7\u5927\u5403\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u897f\u5317\u89d2\u4e0a\u53ae\u6740\u4e4b\u58f0\u5728\u8fd9\u91cc\u4e5f\u542c\u5f97\u6e05\u6e05\u695a\u695a\u3002\u201d\u8bf4\u9053\uff1a \u201c\u54ea\u91cc\u6765\u7684\u4e24\u5e2e\u4eba\u9a6c\uff1f\u201d\u90a3\u519b\u5b98\u9053\uff1a\u201c\u4e00\u5e2e\u662f\u53eb\u5316\u5b50\uff0c\u53e6\u4e00\u5e2e\u5374\u4e0d\u77e5\u662f\u54ea\u91cc\u6765\u7684\u8349\u5bc7\u3002\u201d \u3000\u3000\u9633\u5929\u96f7\u9053\uff1a\u201c\u597d\uff0c\u6211\u9a6c\u4e0a\u5c31\u53bb\uff01\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u5047\u5c71\u77f3\u540e\u5ffd\u5730\u8df3\u51fa\u4e00\u4e2a\u8001\u53eb\u5316\uff0c\u559d\u9053\uff1a \u201c\u4e0d\u7528\u8d39\u795e\uff0c\u8001\u53eb\u5316\u6765\u627e\u4f60\u4e86\uff01\u201d \u3000\u3000\u9633\u5929\u96f7\u8ba4\u5f97\u662f\u4e10\u5e2e\u7684\u5e2e\u4e3b\u9646\u6606\u4ed1\uff0c\u53c8\u60ca\u53c8\u6012\uff0c\u559d\u9053\uff1a\u201c\u671d\u5ef7\u5bb9\u8bb8\u4f60\u4eec\u8fd9\u73ed\u53eb\u5316\u5b50\u5728\u5927\u90fd \u7acb\u8db3\uff0c\u4f60\u4eec\u4e0d\u611f\u6069\u8fd8\u8981\u9053\u53cd\uff01\u201d\u9646\u6606\u4ed1\u65a5\u9053\uff1a\u201c\u653e\u4f60\u5988\u7684\u5c41\uff0c\u6211\u8fd9\u6253\u72d7\u68d2\u8ba4\u5f97\u4f60\uff01\u201d\u9633\u5929\u96f7 \u4e00\u638c\u5288\u51fa\uff0c\u9646\u6606\u4ed1\u6367\u4e2d\u5939\u638c\uff0c\u68d2\u6253\u80eb\u9aa8\uff0c\u638c\u5288\u80f8\u819b\u3002 \u3000\u3000\u53ea\u542c\u5f97\u201c\u84ec\u201d\u7684\u4e00\u58f0\uff0c\u63a5\u7740\u201c\u535c\u201d\u7684\u4e00\u54cd\uff0c\u53cc\u638c\u76f8\u4ea4\uff0c\u9646\u6606\u4ed1\u62b5\u6321\u4e0d\u4f4f\u4ed6\u7684\u201c\u5929\u96f7 \u529f\u201d\uff0c\u7ed9\u9707\u9000\u4e00\u4e08\u5f00\u5916\u3002\u4f46\u9646\u6606\u4ed1\u7684\u6253\u72d7\u6367\u6cd5\u795e\u5999\u65e0\u6bd4\uff0c\u9633\u5929\u96f7\u5728\u8fde\u756a\u5267\u6218\u4e4b\u540e\uff0c\u5374\u662f\u95ea\u907f \u4e0d\u5f00\uff0c\u7ed9\u4ed6\u7ed3\u7ed3\u5b9e\u5b9e\u5730\u5728\u819d\u76d6\u6253\u4e86\u4e00\u4e0b\u3002 \u3000\u3000\u5c31\u5728\u6b64\u9645\uff0c\u5f13\u7bad\u624b\u5df2\u7ecf\u8c03\u6765\uff0c\u9633\u5929\u96f7\u559d\u9053\uff1a\u201c\u653e\u7bad\uff01\u201d\u767b\u65f6\u7bad\u5982\u96e8 \u3000\u3000\u5b5f\u5c11\u521a\u3001\u674e\u601d\u5357\u3001\u6768\u5a49\u7b49\u4eba\u4e1a\u5df2\u4f1a\u96c6\u4e00\u8d77\uff0c\u770b\u89c1\u9646\u6606\u4ed1\u6765\u5230\uff0c\u53c8\u559c\u53c8\u60ca\uff0c\u5b5f\u5c11\u521a\u8fd0\u5251\u5982 \u98ce\uff0c\u8361\u5f00\u7bad\u56f4\uff0c\u51b2\u8fc7\u53bb\u4e0e\u4ed6\u4f1a\u5408\uff0c\u8bf4\u9053\uff1a\u201c\u97e9\u5927\u54e5\u7236\u5973\u5462\uff1f\u201d\u9646\u6602\u4ed1\u9053\uff1a\u201c\u6ca1\u6709\u89c1\u770b\u3002\u4e10\u5e2e \u5144\u5f1f\u7ed9\u6321\u5728\u5916\u9762\uff0c\u6211\u662f\u4e00\u4e2a\u4eba\u6740\u8fdb\u6765\u7684\u3002\u201d \u3000\u3000\u539f\u6765\u4e10\u5e2e\u539f\u5b9a\u7684\u8ba1\u5212\u662f\u5e76\u4e0d\u51c6\u5907\u5174\u5e05\u52a8\u4f17\u88ad\u51fb\u201c\u56fd\u5e08\u5e9c\u201d\u7684\uff0c\u4f46\u89c1\u65e5\u5df2\u843d\u5c71\uff0c\u5b5f\u5c11\u521a\u7b49 \u4eba\u5c1a\u672a\u56de\u6765\uff0c\u60c5\u77e5\u6709\u53d8\uff0c\u6545\u800c\u4e0d\u80fd\u4e0d\u5192\u9669\u6765\u63f4\u3002 \u3000\u3000\u201c\u56fd\u5e08\u5e9c\u201d\u4e0e\u7687\u5bab\u76f8\u90bb\uff0c\u4ed6\u4eec\u521a\u521a\u653b\u7834\u82b1\u56ed\u7684\u4e24\u9053\u95e8\u6237\uff0c\u5fa1\u4f11\u519b\u5c31\u5f00\u6765\u4e86\uff0c\u4e10\u5e2e\u5f1f\u5b50\u5584 \u4e8e\u5404\u81ea\u4e3a\u6218\uff0c\u4e0d\u8bc6\u9635\u6cd5\uff0c\u800c\u4e14\u4f17\u5be1\u60ac\u6b8a\uff0c\u662f\u4ee5\u867d\u7136\u653b\u4e86\u8fdb\u6765\uff0c\u5374\u9677\u5165\u4e86\u5fa1\u6797\u519b\u4e0e\u201c\u56fd\u5e08\u5e9c\u201d \u536b\u58eb\u7684\u5305\u56f4\u4e4b\u4e2d\u3002\u9646\u6606\u4ed1\u662f\u8c41\u51fa\u4e86\u6027\u547d\u72ec\u81ea\u8fdb\u6765\u60f3\u6551\u674e\u601d\u5357\u7684\u3002 \u3000\u3000\u4f17\u4eba\u4f1a\u5408\u4e00\u8d77\uff0c\u674e\u601d\u5357\u8bf4\u9053\uff1a\u201c\u9646\u5e2e\u4e3b\uff0c\u4e3a\u4e86\u6211\u8fde\u7d2f\u8d35\u5e2e\u5144\u5f1f\uff0c\u53eb\u6211\u5982\u4f55\u80fd\u591f\u5fc3\u5b89\uff1f\u201d \u5b5f\u5c11\u521a\u9053\uff1a\u201c\u73b0\u5728\u4e0d\u662f\u8bf4\u5ba2\u6c14\u8bdd\u7684\u65f6\u5019\uff0c\u6740\u51fa\u53bb\u8981\u7d27\uff01\u201d \u3000\u3000\u8fd9\u8425\u5f13\u7bad\u624b\u90fd\u662f\u91d1\u56fd\u7684\u5584\u5c04\u4e4b\u58eb\uff0c\u5176\u4e2d\u8fd8\u6709\u6570\u5341\u5f20\u4ece\u62d6\u96f7\u90a3\u91cc\u501f\u6765\u7684\u795e\u81c2\u5f13\uff0c\u4e00\u53d1\u5c31\u662f \u5341\u4e8c\u652f\uff0c\u5f3a\u5f13\u786c\u5f29\uff0c\u751a\u662f\u4e0d\u6613\u62b5\u6321\uff01 \u3000\u3000\u5b5f\u5c11\u521a\u594b\u8d77\u795e\u5a01\uff0c\u559d\u9053\uff1a\u201c\u6765\u800c\u4e0d\u5f80\u975e\u793c\u4e5f\uff0c\u8ba9\u4f60\u4eec\u4e5f\u89c1\u8bc6\u89c1\u8bc6\u6211\u7684\u7bad\u6cd5\u201d\u8fd9\u4e00\u559d\u604d\u5982 \u6674\u5929\u9739\u96f3\uff0c\u5e73\u5730\u7126\u96f7\uff0c\u628a\u90a3\u8425\u5fa1\u6797\u519b\u7684\u5406\u559d\u58f0\u90fd\u538b\u4e86\u4e0b\u53bb\uff0c\u9707\u5f97\u4ed6\u4eec\u7684\u8033\u9f13\u55e1\u55e1\u4f5c\u54cd\uff0c\u4e0d\u7531 \u5f97\u90fd\u662f\u5927\u5403\u4e00\u60ca\uff01\u4f46\u5b5f\u5c11\u521a\u624b\u4e2d\u5e76\u65e0\u5f13\u7bad\uff0c\u5374\u5982\u4f55\u201c\u89c1\u8bc6\u201d\u4ed6\u7684\u7bad\u6cd5\uff1f\u662f\u4ee5\u654c\u65b9\u867d\u7136\u5403\u60ca\uff0c \u53ea\u5f53\u5b5f\u5c11\u521a\u662f\u865a\u58f0\u606b\u5413\u3002 \u3000\u3000\u5927\u559d\u58f0\u4e2d\uff0c\u5b5f\u5c11\u521a\u628a\u624b\u4e00\u6284\uff0c\u5c06\u795e\u81c2\u5f13\u5c04\u6765\u7684\u4e09\u652f\u957f\u7bad\u63a5\u5230\u624b\u4e2d\uff0c\u632f\u81c2\u4e00\u7529\uff0c\u559d\u9053\uff1a \u201c\u770b\u7bad\uff01\u6211\u8fd9\u4e09\u652f\u7bad\u8981\u7a7f\u8fc7\u4f60\u4eec\u4e09\u540d\u795e\u7bad\u5f13\u7bad\u624b\u7684\u54bd\u5589\uff01\u201d \u3000\u3000\u8bdd\u72b9\u672a\u4e86\uff0c\u53ea\u542c\u5f97\u58f0\u5982\u9739\u9700\uff0c\u7bad\u4f3c\u6d41\u661f\uff0c\u5728\u5047\u5c71\u4e0a\u5f53\u4e2d\u7684\u4e09\u540d\u795e\u81c2\u5f13\u7bad\u624b\uff0c\u679c\u7136\u90fd\u7ed9\u5229 \u7bad\u7a7f\u5589\u800c\u8fc7\uff01 \u3000\u3000\u5b5f\u5c11\u521a\u4ee5\u7529\u7bad\u6cd5\u63b7\u51fa\u7684\u7bad\uff0c\u8155\u529b\u4e4b\u5f3a\uff0c\u7adf\u7136\u80dc\u8fc7\u7528\u5f39\u7c27\u53d1\u5c04\u7684\u957f\u81c2\u5f13\uff0c\u8fd9\u4e00\u4e0b\u767b\u65f6\u5413\u5f97 \u90a3\u4e9b\u5f13\u7bad\u624b\u9b42\u98de\u9b44\u6563\uff01 \u3000\u3000\u674e\u601d\u5357\u63c9\u8eab\u75be\u4e0a\uff0c\u622a\u4f4f\u4e00\u540d\u6389\u961f\u7684\u5175\u571f\uff0c\u62a2\u4e86\u4ed6\u7684\u94c1\u80ce\u5f13\uff0c\u559d\u9053\uff1a\u201c\u6211\u8fd9\u4e00\u7bad\u8981\u5c04\u778e\u4f60 \u4eec\u7edf\u5e26\u7684\u773c\u775b\uff01\u201d \u3000\u3000\u90a3\u540d\u5fa1\u6797\u519b\u519b\u5b98\u6b63\u5728\u547c\u559d\u624b\u4e0b\u4e0d\u8bb8\u614c\u4e71\uff0c\u542c\u5f97\u6b64\u8a00\uff0c\u5927\u5403\u4e00\u60ca\uff0c\u521a\u521a\u8e72\u8eab\u907f\u7bad\u4e4b\u65f6\uff0c\u90a3 \u652f\u7bad\u5df2\u662f\u5c04\u4e2a\u6b63\u7740\uff0c\u6070\u6070\u5c04\u778e\u4e86\u4ed6\u53f3\u8fb9\u7684\u773c\u775b\uff01\u674e\u601d\u5357\u53d1\u7bad\u4e4b\u9645\uff0c\u65e9\u6599\u5230\u4ed6\u8981\u8e72\u4e0b\u8eab\u5b50\uff0c\u7bad \u6cd5\u7684\u795e\u5999\uff0c\u5f53\u771f\u662f\u65e0\u4ee5\u590d\u52a0\uff01\u800c\u4e14\u4ed6\u7528\u7684\u4e0d\u8fc7\u662f\u4e00\u628a\u5bfb\u5e38\u7684\u94c1\u80ce\u5f13\uff0c\u5c04\u7a0b\u672c\u6765\u662f\u8fdc\u4e0d\u53ca\u795e\u81c2 \u5f13\u7684\uff0c\u4f46\u4ece\u674e\u601d\u5357\u7684\u624b\u4e2d\u5c04\u51fa\uff0c\u52b2\u9053\u5374\u6bd4\u795e\u81c2\u5f13\u8fd8\u5f3a\u3002\u8fd9\u624b\u529f\u592b\u4e00\u53d1\uff0c\u90a3\u4e9b\u672c\u6765\u5c31\u5df2\u614c\u4e71\u4e86 \u7684\u5f13\u7bad\u624b\uff0c\u54ea\u91cc\u8fd8\u80fd\u538b\u5f97\u4f4f\u9635\u811a\u3002 \u3000\u3000\u5b5f\u5c11\u521a\u3001\u674e\u601d\u5357\u5f00\u58f0\u559d\u9053\uff1a\u201c\u8c01\u6562\u653e\u7bad\uff0c\u6211\u5c31\u5c04\u8c01\uff01\u201d\u970e\u65f6\u95f4\u6570\u767e\u5f20\u5f13\u90fd\u505c\u4e86\u4e0b\u6765\uff0c\u9e26 \u96c0\u65e0\u58f0\uff01\u5b5f\u5c11\u521a\u8fd9\u4e00\u884c\u4eba\u98ce\u9a70\u7535\u63a3\u822c\u7684\u4fbf\u786c\u95ef\u8fc7\u53bb\u4e86\u3002 \u3000\u3000\u5176\u5b9e\u8fd9\u8425\u5f13\u7bad\u624b\u82e5\u4e0d\u614c\u4e71\u7684\u8bdd\uff0c\u51e0\u767e\u5f20\u5f13\u5f00\u53d1\uff0c\u4ed6\u4eec\u672c\u9886\u518d\u5f3a\uff0c\u4e5f\u51b3\u8ba1\u96be\u4fdd\u4e0d\u4e2d\u4e00\u7bad\uff0c \u53ea\u56e0\u602f\u610f\u4e00\u751f\uff0c\u4eba\u591a\u4e5f\u6ca1\u7528\u3002 \u3000\u3000\u5b5f\u3001\u674e\u7b49\u4eba\u7a81\u56f4\u800c\u51fa\uff0c\u6740\u5230\u4e86\u897f\u5317\u89d2\u3002\u53ea\u89c1\u5fa1\u6797\u519b\u548c\u201c\u56fd\u5e08\u5e9c\u201d\u7684\u536b\u58eb\uff0c\u5e03\u6210\u4e00\u9053\u9632 \u7ebf\uff0c\u4e10\u5e2e\u7684\u5f1f\u5b50\u548c\u53e6\u4e00\u5e2e\u4eba\u6b63\u5728\u52c7\u731b\u8fdb\u653b\uff0c\u4f46\u56e0\u654c\u4f17\u6211\u5be1\uff0c\u9632\u7ebf\u6ca1\u6709\u653b\u7834\uff0c\u4f24\u4ea1\u5728\u654c\u65b9\u7bad\u4e0b \u7684\u5df2\u662f\u4e0d\u5c11\u3002 \u3000\u3000\u674e\u601d\u5357\u4e00\u51fa\u73b0\uff0c\u4e10\u5e2e\u5f1f\u5b50\u8ba4\u8bc6\u4ed6\u7684\u7eb7\u7eb7\u544a\u8bc9\u540c\u4f34\uff0c\u767b\u65f6\u7206\u51fa\u4e86\u8f70\u5929\u4f3c\u7684\u5450\u558a\uff1a\u201c\u76df\u4e3b\u6765 \u5566\uff01\u201d\u5b5f\u5c11\u521a\u548c\u674e\u601d\u5357\u4e24\u67c4\u957f\u5251\u5f00\u8def\uff0c\u91cc\u5e94\u5916\u5408\uff0c\u4e0d\u6d88\u7247\u523b\uff0c\u6740\u51fa\u91cd\u56f4\u3002 \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u9646\u5e2e\u4e3b\uff0c\u4e3a\u6211\u4e00\u4eba\uff0c\u7d2f\u4e86\u8d35\u5e2e\u8bb8\u591a\u5144\u5f1f\uff0c\u5feb\u5feb\u4e0b\u4ee4\u64a4\u9000\u5427\uff01\u201d\u4e10\u5e2e\u4e4b\u4f17\uff0c \u672c\u662f\u4e3a\u6551\u674e\u601d\u5357\u800c\u6765\u7684\uff0c\u5982\u4eca\u674e\u601d\u5357\u5df2\u7ecf\u8131\u9669\uff0c\u5f53\u7136\u662f\u53ef\u4ee5\u64a4\u9000\u4e86\u3002 \u3000\u3000\u9646\u6606\u4ed1\u9053\uff1a\u201c\u76df\u4e3b\u4f11\u8981\u5982\u6b64\u8bf4\uff0c\u5f1f\u5144\u867d\u6709\u4f24\u4ea1\uff0c\u9791\u5b50\u7684\u635f\u5931\u5374\u6bd4\u54b1\u4eec\u66f4\u5927\uff0c\u8fd9\u4e00\u4ed7\u54b1\u4eec \u53ef\u5e76\u6ca1\u6709\u8d54\u672c\uff01\u201d \u3000\u3000\u5f53\u4e0b\u9646\u6606\u4ed1\u4e0b\u4ee4\u64a4\u9000\uff0c\u5fa1\u6797\u519b\u4f24\u4ea1\u7684\u66f4\u591a\uff0c\u4f46\u6c42\u4ed6\u4eec\u9000\u8d70\uff0c\u54ea\u91cc\u8fd8\u6562\u8ffd\u51fb\uff1f \u3000\u3000\u4e24\u5e2e\u4eba\u9000\u51fa\u4e86\u201c\u56fd\u5e08\u5e9c\u201d\uff0c\u4e00\u4e2a\u6d53\u7709\u5927\u773c\u7684\u6c49\u5b50\u8d70\u8fc7\u6765\u8bf4\u9053\uff1a\u201c\u5c5e\u4e0b\u5218\u5927\u4e3a\u8c12\u89c1\u76df \u4e3b\uff01\u201d\u674e\u601d\u5357\u4e00\u770b\u8fd9\u4eba\uff0c\u5374\u662f\u4e0d\u8ba4\u5f97\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u559c\u51fa\u671b\u5916\uff0c\u8dd1\u8fc7\u6765\u9053\uff1a\u201c\u5927\u4e3a\u5144\uff0c\u539f\u6765\u662f\u4f60\uff01\u201d\u5f53\u4e0b\u7ed9\u674e\u601d\u5357\u4ecb\u7ecd\uff0c\u674e\u601d\u5357\u8fd9\u624d \u77e5\u9053\u4ed6\u662f\u5317\u8292\u5c71\u7684\u4e49\u519b\u9996\u9886\u4e4b\u4e00\uff0c\u4e5f\u662f\u5218\u743c\u59d1\u7684\u54e5\u54e5\u3002 \u3000\u3000\u5218\u5927\u4e3a\u8fde\u5fd9\u95ee\u9053\uff1a\u201c\u6211\u7684\u59b9\u59b9\u600e\u4e48\u6837\uff1f\u201d\u674e\u601d\u5357\u5f3a\u6291\u60b2\u75db\uff0c\u8bf4\u9053\uff1a\u201c\u5218\u5144\uff0c\u6211\u8fd9\u6b21\u5f97\u56de \u6027\u547d\uff0c\u90fd\u662f\u4e8f\u4e86\u4ee4\u59b9\uff0c\u4ee4\u59b9\u867d\u6b7b\u72b9\u751f\uff01\u201d\u5f53\u4e0b\u5c06\u5218\u743c\u59d1\u58ee\u70c8\u727a\u7272\u7684\u7ecf\u8fc7\uff0c\u7b80\u7565\u5730\u544a\u8bc9\u4e86\u5979\u7684 \u54e5\u54e5\u3002 \u3000\u3000\u6768\u5a49\u54bd\u54fd\u8bf4\u9053\uff1a\u201c\u4ee4\u59b9\u4e34\u7ec8\u4e4b\u9645\uff0c\u6258\u6211\u4f20\u8bdd\u7ed9\u4f60\uff0c\u8bf4\u5979\u5e76\u6ca1\u8fb1\u6ca1\u5bb6\u95e8\uff0c\u6c42\u4f60\u539f\u8c05\uff0c\u5979\u8fd8 \u53eb\u6211\u4ee3\u5979\u95ee\u5019\u891a\u5927\u54e5\u3002\u201d \u3000\u3000\u5218\u5927\u4e3a\u6376\u80f8\u6ce3\u9053\uff1a\u201c\u53ea\u602a\u6211\u6765\u8fdf\u4e86\u4e00\u6b65\u3002\u201d\u539f\u6765\u5c60\u9f99\u6628\u665a\u8fde\u591c\u53eb\u4eba\u628a\u5218\u743c\u59d1\u90a3\u5c01\u4fe1\u9001\u5230 \u5317\u8292\u5c71\u53bb\uff0c\u5374\u4e0d\u77e5\u8fd9\u5c01\u4fe1\u5185\u6709\u7384\u865a\uff0c\u5218\u5927\u4e3a\u4e00\u770b\u5c31\u77e5\u59b9\u59b9\u662f\u5728\u88ab\u4eba\u631f\u6301\u4e4b\u4e0b\u5199\u7684\uff0c\u4e8e\u662f\u628a\u90a3 \u4f7f\u8005\u4e00\u5200\u4e24\u6bb5\uff0c\u7acb\u5373\u5e26\u4e86\u4e00\u961f\u4eba\u9a6c\u5feb\u9a6c\u9a70\u6765\u3002\u4e5f\u5e78\u4e8f\u6709\u4ed6\u8fd9\u961f\u4eba\u9a6c\u53ca\u65f6\u8d76\u5230\uff0c\u4e10\u5e2e\u7684\u5f1f\u5b50\u624d \u4e0d\u81f3\u4e8e\u7ed9\u654c\u4eba\u6d88\u706d\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u66f4\u6700\u4f24\u75db\uff0c\u8bf4\u9053\uff1a\u201c\u5218\u5144\uff0c\u6211\u5bf9\u4e0d\u4f4f\u4ee4\u59b9\uff0c\u6628\u665a\u6211\u5df2\u7ecf\u89c1\u7740\u5979\u7684\uff0c\u53ef\u6068\u6211\u4e0d\u64c5\u8a00 \u8f9e\uff0c\u529d\u4e0d\u52a8\u4ee4\u59b9\u548c\u6211\u4e00\u540c\u9003\u51fa\u9b54\u7a9f\u3002\u201d\u5218\u5927\u4e3a\u54bd\u4e0b\u60b2\u75db\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u600e\u80fd\u602a\u5f97\u891a\u5144\uff0c\u6211\u8fd9\u59b9 \u59b9\u4e00\u5411\u6027\u5b50\u5014\u5f3a\uff0c\u4e0d\u8fc7\u5979\u8fd9\u6b21\u867d\u7136\u884c\u5dee\u8e0f\u9519\uff0c\u6700\u540e\u5374\u80fd\u624b\u5203\u4ec7\u4eba\uff0c\u4e5f\u4e0d\u6127\u662f\u6211\u5218\u5bb6\u7684\u5973\u513f \u4e86\u3002\u5509\uff0c\u53ea\u662f\u53ef\u60dc\u5979\u6ca1\u6709\u798f\u6c14\u3002\u201d\u8bf4\u7f62\uff0c\u5fcd\u4e0d\u4f4f\u53c8\u662f\u864e\u76ee\u6d41\u6cea\u3002 \u3000\u3000\u9646\u6606\u4ed1\u9053\uff1a\u201c\u5218\u5144\u3001\u891a\u5144\uff0c\u73b0\u5728\u8fd8\u4e0d\u662f\u60b2\u75db\u7684\u65f6\u5019\uff0c\u54b1\u4eec\u987b\u5f97\u8d76\u5feb\u95ef\u51fa\u4eac\u57ce\uff0c\u8fd9\u624d\u80fd\u591f \u7ed9\u6b7b\u96be\u7684\u5144\u5f1f\u548c\u4ee4\u59b9\u62a5\u4ec7\u3002\u201d \u3000\u3000\u8981\u77e5\u4e10\u5e2e\u8fd9\u6b21\u53c2\u52a0\u56f4\u653b\u201c\u56fd\u5e08\u5e9c\u201d\u4e4b\u540e\uff0c\u5728\u91d1\u56fd\u7684\u4eac\u57ce\u81ea\u662f\u4e0d\u80fd\u7acb\u8db3\uff0c\u6545\u800c\u5fc5\u987b\u628a\u603b\u8235 \u8f6c\u79fb\uff0c\u5e78\u597d\u5fa1\u6797\u519b\u5927\u90e8\u5206\u5728\u201c\u56fd\u5e08\u5e9c\u201d\u4e0d\u6562\u8ffd\u6765\uff0c\u5c0f\u90e8\u5206\u8981\u4fdd\u62a4\u7687\u5bab\uff0c\u4e5f\u4e0d\u6562\u51fa\u52a8\u3002\u5b88\u57ce\u95e8 \u7684\u4e00\u8425\u5175\u5352\u62e6\u4e0d\u4f4f\u8fd9\u7fa4\u597d\u4f3c\u4e0b\u5c71\u731b\u864e\u7684\u4e10\u5e2e\u5f1f\u5b50\u4e0e\u5317\u8292\u5c71\u4e49\u519b\uff0c\u4ed6\u4eec\u662f\u4ece\u4e1c\u95e8\u95ef\u51fa\u53bb\u7684\uff0c\u5f85 \u5f97\u4e5d\u57ce\u5175\u9a6c\u8c03\u6765\uff0c\u4ed6\u4eec\u65e9\u5df2\u53bb\u5f97\u8fdc\u4e86\u3002 \u3000\u3000\u5728\u79bb\u5927\u90fd\u4e00\u767e\u591a\u91cc\u7684\u5bc6\u4e91\u53bf\u7684\u4e00\u4e2a\u540d\u53eb\u201c\u9ec4\u7af9\u5751\u201d\u7684\u5c71\u6751\uff0c\u6ca1\u6709\u4e10\u5e2e\u7684\u5206\u8235\uff0c\u6b64\u5730\u5728\u7fa4 \u5c71\u4e4b\u4e2d\uff0c\u751a\u4e3a\u8352\u50fb\uff0c\u4ece\u5927\u90fd\u64a4\u9000\u51fa\u6765\u7684\u5f1f\u5b50\u6b63\u597d\u5728\u6b64\u5b89\u8eab\u3002 \u3000\u3000\u5b89\u987f\u505c\u59a5\u4e4b\u540e\uff0c\u7b2c\u4e8c\u65e5\u5404\u4eba\u5206\u9053\u626c\u9573\uff0c\u5218\u5927\u4e3a\u7387\u9886\u90a3\u961f\u4e49\u519b\u56de\u5317\u8292\u5c71\uff0c\u97e9\u5927\u7ef4\u7236\u5973\u5219\u5f80 \u626c\u5dde\u6295\u4eb2\uff0c\u674e\u601d\u5357\u4e0e\u5b5f\u5c11\u521a\u5546\u8bae\u662f\u5426\u5373\u56de\u7405\u739b\u5c71\uff0c\u6768\u5a49\u60f3\u8d77\u4e00\u4e8b\uff0c\u8bf4\u9053\uff1a\u201c\u660e\u6167\u516c\u4e3b\u4e34\u8d70\u4e4b \u65f6\uff0c\u8bf4\u662f\u5c60\u59d0\u59d0\u548c\u5b5f\u59d0\u59d0\u5c31\u8981\u6765\u7684\uff0c\u54b1\u4eec\u4e0d\u5982\u591a\u7559\u51e0\u5929\uff0c\u514d\u5f97\u5f7c\u6b64\u9519\u8fc7\u3002\u201d\u6b63\u662f\uff1a \u3000\u3000\u8fed\u8d77\u98ce\u6ce2\u8c01\u628a\u63e1\uff0c\u826f\u670b\u672a\u81f3\u8d77\u5fe7\u7591\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b\u2014\u2014\u7b2c\u4e94\u5341\u4e5d\u56de\u3000\u67ad\u96c4\u8fa3\u624b\u8bdb\u738b\u5b50\u3000\u5927\u4fa0\u6a2a\u5200\u6597\u6076\u618e \u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b \u7b2c\u4e94\u5341\u4e5d\u56de\u3000\u67ad\u96c4\u8fa3\u624b\u8bdb\u738b\u5b50\u3000\u5927\u4fa0\u6a2a\u5200\u6597\u6076\u618e \u3000\u3000\u891a\u4e91\u5cf0\u5403\u4e86\u4e00\u60ca\uff0c\u5931\u58f0\u53eb\u9053\uff1a\u201c\u7cdf\u7cd5\uff0c\u7cdf\u7cd5\uff01\u201d \u3000\u3000\u6768\u5a49\u83ab\u540d\u5176\u5999\uff0c\u8bf4\u9053\uff1a\u201c\u4ec0\u4e48\u7cdf\u7cd5\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u77bf\u7136\u4e00\u7701\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u54b1\u4eec\u53ef\u5f97\u60f3\u6cd5\u8865\u6551\u624d\u884c\u3002\u201d\u6768\u5a49\u604d\u7136\u5927\u609f\uff0c\u8bf4\u9053\uff1a \u201c\u6211\u660e\u767d\u4e86\uff0c\u4f60\u662f\u6015\u5979\u4eec\u627e\u4e0d\u7740\u8fd9\u4e2a\u5730\u65b9\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u9053\uff1a\u201c\u5979\u4eec\u82e5\u7136\u6765\u5230\u5927\u90fd\uff0c\u4e00\u5b9a\u662f\u5148\u5230\u4e10\u5e2e\u7684\u603b\u8235\u6253\u542c\u54b1\u4eec\u7684\u6d88\u606f\uff0c\u627e\u4e0d\u7740\u54b1\u4eec \u8fd8\u4e0d\u6253\u7d27\uff0c\u53ea\u6015\u53cd\u800c\u662f\u81ea\u6295\u7f57\u7f51\u4e86\u3002\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u8fd9\u600e\u4e48\u597d\uff0c\u4e10\u5e2e\u603b\u8235\u867d\u5df2\u8fc1\u79fb\uff0c\u4f46\u9633\u5929\u96f7\u4e00\u5b9a\u8fd8\u4f1a\u6d3e\u9063\u624b\u4e0b\u5728\u9644\u8fd1\u6697\u4e2d\u76d1\u89c6 \u7684\uff0c\u54b1\u4eec\u7684\u4eba\u53ef\u4e0d\u80fd\u7559\u5728\u91cc\u9762\u7b49\u5019\u4ed6\u4eec\u3002\u201d \u3000\u3000\u9646\u6606\u4ed1\u9053\uff1a\u201c\u5404\u4f4d\u4e0d\u5fc5\u62c5\u5fe7\uff0c\u4e10\u5e2e\u7684\u5f1f\u5b50\u867d\u7136\u64a4\u51fa\u5927\u90fd\uff0c\u4f46\u6211\u4eec\u8fd8\u6709\u8bb8\u591a\u4e09\u6559\u4e5d\u6d41\u7684\u670b \u53cb\u53ef\u6d3e\u7528\u573a\uff0c\u6211\u53ef\u4ee5\u8bbe\u6cd5\u4e0e\u4ed6\u4eec\u8054\u7edc\uff0c\u53eb\u4ed6\u4eec\u5728\u5404\u4e2a\u57ce\u95e8\u9644\u8fd1\u626e\u4f5c\u533b\u535c\u661f\u76f8\uff0c\u5145\u5f53\u54b1\u4eec\u7684\u773c \u7ebf\u3002\u5b5f\u59d1\u5a18\u3001\u5c60\u59d1\u5a18\u6765\u4e86\uff0c\u81ea\u4f1a\u6709\u4eba\u548c\u5979\u4eec\u6697\u901a\u6d88\u606f\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u529e\u6cd5\u5f53\u7136\u5e76\u975e\u4e07\u5168\u4e4b\u8ba1\uff0c\u4f46\u65e2\u7136\u6ca1\u6709\u522b\u7684\u66f4\u597d\u529e\u6cd5\uff0c\u4e5f\u5c31\u53ea\u597d\u5982\u6b64\u4e86\u3002\u201d \u3000\u3000\u6643\u773c\u8fc7\u4e86\u51e0\u5929\uff0c\u5140\u662f\u6ca1\u6709\u5979\u4eec\u7684\u6d88\u606f\uff0c\u4e5f\u4e0d\u77e5\u5979\u4eec\u4e2d\u9014\u51fa\u4e8b\u8fd8\u662f\u4e1a\u5df2\u8fdb\u4e86\u5927\u90fd\uff0c\u4f46\u4e10\u5e2e \u7684\u90a3\u4e9b\u4e09\u6559\u4e5d\u6d41\u7684\u670b\u53cb\u6ca1\u6709\u53d1\u73b0\uff0c\u4f17\u4eba\u65e0\u4e0d\u5fc3\u4e2d\u7740\u6025\uff0c\u6697\u81ea\u62c5\u5fe7\u3002 \u3000\u3000\u5230\u4e86\u7b2c\u4e03\u5929\uff0c\u597d\u4e0d\u5bb9\u6613\u624d\u76fc\u5230\u4e10\u5e2e\u7684\u4e00\u4e2a\u670b\u53cb\u5230\u6765\uff0c\u5e26\u6765\u4e00\u4e2a\u6d88\u606f\u3002 \u3000\u3000\u90a3\u4eba\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u8bf4\u7684\u90a3\u4e24\u4f4d\u59d1\u5a18\u672a\u89c1\u5230\u6765\uff0c\u4e0d\u8fc7\u5374\u6709\u4eba\u7ed9\u5979\u4eec\u634e\u6765\u4e00\u4e2a\u53e3\u4fe1\u3002\u201d \u3000\u3000\u5b5f\u5c11\u521a\u6709\u70b9\u5947\u602a\uff0c\u8bf4\u9053\uff1a\u201c\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u90a3\u4eba\u8bf4\u9053\uff1a\u201c\u662f\u4e00\u4e2a\u8d70\u6c5f\u6e56\u7684\u90ce\u4e2d\uff0c\u6211\u4eec\u6709\u4eba\u548c\u4ed6\u76f8\u8bc6\u7684\u3002\u201d\u8bf4\u51fa\u4e86\u90a3\u4e2a\u90ce\u4e2d\u7684\u540d\u5b57\uff0c \u5b5f\u5c11\u521a\u5374\u4e0d\u77e5\u9053\uff0c\u4e0d\u7531\u5f97\u66f4\u4e3a\u5947\u602a\uff0c\u8be7\u9053\uff1a\u201c\u4ed6\u600e\u7684\u8ba4\u8bc6\u5c0f\u5973\uff1f\u201d \u3000\u3000\u90a3\u4eba\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u90ce\u4e2d\u5176\u5b9e\u53ea\u662f\u4e2a\u2018\u8499\u53e4\u5927\u592b\u2019\uff0c\u4ed6\u5f53\u7136\u4e0d\u4f1a\u8ba4\u8bc6\u4ee4\u5a9b\uff0c\u4ed6\u4e5f\u662f\u53d7\u4eba\u6240 \u6258\u7684\u3002\u6258\u4ed6\u634e\u53e3\u4fe1\u7684\u90a3\u4e2a\u4eba\u7ed9\u4ed6\u5341\u4e24\u94f6\u5b50\uff0c\u548c\u4ed6\u5957\u4e0a\u4e86\u6c5f\u6e56\u7684\u4e49\u6c14\uff0c\u4ed6\u4e5f\u5c31\u4e0d\u95ee\u4eba\u5bb6\u7684\u6765 \u5386\uff0c\u4fbf\u628a\u53e3\u4fe1\u634e\u6765\u4e86\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u4e0d\u7981\u76b1\u8d77\u7709\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u6837\u7684\u8f97\u8f6c\u76f8\u6258\uff0c\u634e\u6765\u7684\u53e3\u4fe1\u4e5f\u4e0d\u77e5\u662f\u771f\u662f\u5047\uff01\u201d \u3000\u3000\u5b5f\u5c11\u521a\u9053\uff1a\u201c\u4e14\u5148\u542c\u4e86\u518d\u8bf4\u3002\u201d \u3000\u3000\u90a3\u4eba\u8bf4\u9053\uff1a\u201c\u90a3\u53e3\u4fe1\u8bf4\u6709\u4e09\u4f4d\u59d1\u5a18\u8bf7\u4f60\u4eec\u5230\u5288\u5929\u5d16\u7684\u9752\u9f99\u53e3\u548c\u5979\u4eec\u89c1\u9762\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u6709\u4e09\u4f4d\u59d1\u5a18\uff1f\u201d \u3000\u3000\u9646\u6606\u4ed1\u9053\uff1a\u201c\u5288\u5929\u5d16\u7684\u9752\u9f99\u53e3\u5728\u4ec0\u4e48\u5730\u65b9\uff0c\u6211\u4ece\u6765\u6ca1\u6709\u542c\u8fc7\u8fd9\u4e2a\u5730\u540d\u3002\u201d \u3000\u3000\u5b5f\u5c11\u521a\u4e0e\u674e\u601d\u5357\u5374\u662f\u4e0d\u7ea6\u800c\u540c\u5730\u559c\u51fa\u671b\u5916\u9f50\u58f0\u8bf4\u9053\uff1a\u201c\u5288\u5929\u5d16\u7684\u9752\u9f99\u53e3\uff01\u554a\uff0c\u8fd9\u4e48\u8bf4 \u6765\uff0c\u8fd9\u53e3\u4fe1\u4e00\u5b9a\u4e0d\u662f\u5047\u7684\u4e86\u3002\u201d \u3000\u3000\u90a3\u4eba\u8bf4\u9053\uff1a\u201c\u8fd9\u4e09\u4f4d\u59d1\u5a18\u4e00\u4e2a\u59d3\u5c60\uff0c\u4e00\u4e2a\u59d3\u5b5f\uff0c\u4e00\u4e2a\u59d3\u4e25\u3002\u59d3\u5c60\u7684\u59d1\u5a18\u60f3\u5fc5\u662f\u5c60\u767e\u57ce\u7684 \u5973\u513f\uff0c\u59d3\u5b5f\u7684\u59d1\u5a18\u60f3\u5fc5\u5c31\u662f\u5b5f\u5927\u4fa0\u7684\u4ee4\u5a9b\u4e86\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u63a5\u7740\u7b11\u9053\uff1a\u201c\u8c37\u5e08\u5f1f\uff0c\u8fd9\u4f60\u53ef\u8be5\u6b22\u559c\u5566\uff0c\u59d3\u4e25\u7684\u90a3\u59d1\u5a18\u4e00\u5b9a\u662f\u4e25\u70f7\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u5288\u5929\u5d16\u7684\u9752\u9f99\u53e3\u5728\u4ec0\u4e48\u5730\u65b9\uff0c\u8fd9\u5730\u540d\u542c\u6765\u5012\u662f\u9669\u5cfb\u5f97\u9a87\u4eba\uff0c\u5979\u4eec\u4e3a\u4ec0\u4e48\u7ea6 \u54b1\u4eec\u5230\u8fd9\u4e2a\u5730\u65b9\u76f8\u4f1a\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u5730\u65b9\u7684\u786e\u662f\u4e00\u4e2a\u5341\u5206\u9669\u5cfb\u7684\u6240\u5728\uff0c\u5b83\u662f\u5728\u8499\u53e4\u548c\u91d1\u56fd\u4ea4\u754c\u7684\u4e00\u5ea7 \u5c71\u4e0a\uff0c\u524d\u9762\u662f\u5927\u6208\u58c1\uff0c\u540e\u9762\u662f\u8fde\u7ef5\u4e0d\u65ad\u7684\u7941\u8fde\u5c71\u8109\u3002\u4ece\u91d1\u56fd\u548c\u897f\u590f\u524d\u5f80\u8499\u53e4\uff0c\u90fd\u8981\u7ecf\u8fc7\u8fd9\u4e2a \u5288\u5929\u5d16\u7684\u9752\u9f99\u53e3\u3002\u201d \u3000\u3000\u5b5f\u5c11\u521a\u9053\uff1a\u201c\u5c60\u51e4\u548c\u660e\u971e\u66fe\u7ecf\u5230\u8fc7\u8499\u53e4\uff0c\u82e5\u7136\u4e0d\u662f\u5979\u4eec\uff0c\u8c05\u4e5f\u8bf4\u4e0d\u51fa\u8fd9\u4e2a\u5730\u540d\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u66f4\u4e3a\u7eb3\u7f55\uff0c\u8bf4\u9053\uff1a\u201c\u5979\u4eec\u4e3a\u4ec0\u4e48\u4e0d\u6765\u8fd9\u91cc\uff0c\u5374\u8981\u54b1\u4eec\u53bb\u8499\u53e4\u7684\u5730\u65b9\u76f8\u4f1a\uff1f\u201d \u3000\u3000\u76c2\u5c11\u521a\u8bf4\u9053\uff1a\u201c\u9646\u5e2e\u4e3b\uff0c\u6628\u5929\u4f60\u662f\u4e0d\u662f\u63a5\u5230\u4e00\u4e2a\u6d88\u606f\uff0c\u8bf4\u662f\u8499\u53e4\u7684\u4f7f\u8005\u4e0d\u65e5\u5c31\u8981\u56de\u56fd\uff0c \u9633\u5929\u96f7\u8981\u966a\u4f34\u4ed6\u4eec\u5230\u548c\u6797\u62a5\u8058\u3002\u201d \u3000\u3000\u9646\u6606\u4ed1\u9053\uff1a\u201c\u4e0d\u9519\u3002\u8fd9\u6d88\u606f\u5c31\u662f\u4ece\u2018\u56fd\u5e08\u5e9c\u2019\u4e2d\u7ed9\u655d\u5e2e\u505a\u2018\u5367\u5e95\u2019\u7684\u4eba\u4f20\u51fa\u6765\u7684\uff0c\u5927\u7ea6 \u53ef\u9760\u3002\u201d \u3000\u3000\u891a\u4e91\u5cf0\u604d\u7136\u5927\u609f\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u660e\u767d\u4e86\uff0c\u5979\u4eec\u662f\u60f3\u5728\u8fd9\u4e2a\u5730\u65b9\u622a\u51fb\u654c\u4eba\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u9053\uff1a\u201c\u5c60\u51e4\u7684\u7236\u4eb2\u5c60\u767e\u57ce\u4e27\u5728\u9633\u5929\u96f7\u4e4b\u624b\uff0c\u5e2e\u51f6\u7684\u8fd8\u6709\u9f99\u8c61\u6cd5\u738b\u7684\u5f1f\u5b50\uff0c\u60f3\u5fc5\u5979 \u4eec\u4ea6\u5df2\u77e5\u9053\u9633\u5929\u96f7\u8981\u5230\u548c\u6797\u62a5\u8058\u7684\u6d88\u606f\u4e86\u3002\u5728\u8fd9\u79cd\u9669\u5cfb\u7684\u5730\u65b9\u622a\u51fb\uff0c\u654c\u4eba\u867d\u591a\uff0c\u4e5f\u96be\u65bd\u5c55\uff0c \u6b63\u662f\u6700\u7406\u60f3\u7684\u62a5\u4ec7\u4e4b\u6240\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u9053\uff1a\u201c\u4f46\u6211\u8fd8\u662f\u6709\u4e00\u70b9\u60f3\u4e0d\u660e\u767d\uff0c\u5979\u4eec\u600e\u5730\u4f1a\u77e5\u9053\u8fd9\u4e2a\u6d88\u606f\uff1f\u7ed9\u5979\u4eec\u6258\u90a3\u90ce\u4e2d\u634e \u53e3\u4fe1\u7684\u53c8\u662f\u8c01\u5462\uff1f\u201d \u3000\u3000\u5b5f\u5c11\u521a\u9053\uff1a\u201c\u6216\u8bb8\u662f\u4e0d\u613f\u9732\u9762\u7684\u6c5f\u6e56\u5f02\u4eba\uff0c\u6b64\u4eba\u6d88\u606f\u7075\u901a\uff0c\u800c\u548c\u6211\u53c8\u662f\u5f7c\u6b64\u95fb\u540d\u751a\u6216\u662f \u76f8\u8bc6\u7684\u4e5f\u8bf4\u4e0d\u5b9a\u3002\u4ed6\u77e5\u9053\u971e\u513f\u8981\u5f80\u5927\u90fd\uff0c\u4fbf\u5728\u4e2d\u9014\u963b\u6b62\u5979\u4eec\uff0c\u5e76\u7ed9\u4ed6\u4eec\u51fa\u4e86\u8fd9\u4e2a\u4e3b\u610f\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u7b11\u9053\uff1a\u201c\u82e5\u7136\u771f\u662f\u5982\u6b64\uff0c\u8fd9\u4e2a\u4eba\u4e5f\u592a\u795e\u79d8\u4e86\u3002\u201d \u3000\u3000\u5b5f\u5c11\u521a\u9053\uff1a\u201c\u6211\u4e5f\u53ea\u662f\u8fd9\u6837\u731c\u60f3\u800c\u5df2\uff0c\u771f\u76f8\u5982\u4f55\uff0c\u6050\u6015\u53ea\u6709\u5230\u4e86\u5288\u5929\u5d16\u624d\u80fd\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u56e0\u4e3a\u4eba\u591a\u524d\u5f80\u53cd\u800c\u4e0d\u4fbf\uff0c\u5546\u8bae\u7684\u7ed3\u679c\uff0c\u51b3\u5b9a\u53ea\u662f\u5b5f\u5c11\u521a\u3001\u9646\u6606\u4ed1\u548c\u674e\u601d\u5357\u3001\u6768\u5a49\u53ca\u891a\u4e91 \u5cf0\u3001\u8c37\u6db5\u865a\u4e00\u5171\u516d\u4eba\u8d74\u7ea6\u3002\u4f30\u8ba1\u53cc\u65b9\u5b9e\u529b\uff0c\u5b5f\u5c11\u521a\u53ef\u4ee5\u654c\u5f97\u4f4f\u9f99\u8c61\u6cd5\u738b\uff0c\u891a\u3001\u8c37\u4e8c\u4eba\u52a0\u4e0a\u4e00 \u4e2a\u9646\u6606\u4ed1\u5bf9\u4ed8\u9633\u5929\u96f7\u4fbf\u53ef\u7a33\u64cd\u80dc\u7b97\uff0c\u4f59\u4e0b\u7684\u4eba\u4e5f\u8db3\u53ef\u4ee5\u5bf9\u4ed8\u5f97\u4e86\u62d6\u96f7\u7684\u624b\u4e0b\u4e86\u3002 \u3000\u3000\u8ba1\u8bae\u5df2\u5b9a\uff0c\u5f53\u65e5\u4fbf\u5373\u52a8\u8eab\u3002\u891a\u3001\u8c37\u4e8c\u4eba\u5e0c\u671b\u4e0d\u5047\u5916\u529b\u81ea\u5df1\u80fd\u591f\u6e05\u7406\u5e08\u95e8\uff0c\u4e00\u8def\u4e0a\u53ea\u8981\u7a0d \u6709\u7a7a\u6687\uff0c\u4fbf\u5373\u52e4\u7ec3\u5185\u529f\u3001\u5251\u6cd5\uff0c\u5e76\u5411\u5b5f\u5c11\u521a\u8bf7\u6559\u3002 \u3000\u3000\u4e14\u8bf4\u62d6\u96f7\u5728\u90a3\u65e5\u653e\u8d70\u4e86\u674e\u601d\u5357\u4e4b\u540e\uff0c\u5df2\u662f\u65e0\u5fc3\u7559\u5728\u5927\u90fd\uff0c\u91d1\u56fd\u4e0e\u8499\u53e4\u8c08\u548c\u7684\u6761\u4ef6\u5b9a\u59a5\u4e4b \u540e\uff0c\u4e00\u884c\u4eba\u4fbf\u5373\u542f\u7a0b\u56de\u56fd\u4e86\uff0c\u91d1\u4e3b\u4e3a\u4e86\u8868\u793a\u81e3\u670d\u201c\u4e0a\u56fd\u201d\u7684\u8bda\u610f\uff0c\u7279\u9053\u201c\u56fd\u5e08\u201d\u9633\u5929\u96f7\u4f34\u540c \u62d6\u96f7\uff0c\u524d\u5f80\u548c\u6797\u62a5\u8058\u3002 \u3000\u3000\u4e00\u8def\u65e0\u4e8b\uff0c\u8fd9\u65e5\u5230\u4e86\u90a3\u5ea7\u5c71\u4e0b\uff0c\u62ac\u5934\u5df2\u662f\u770b\u5f97\u89c1\u5288\u5929\u5d16\u4e86\u3002 \u3000\u3000\u62d6\u96f7\u4e0b\u4ee4\u5728\u5c71\u811a\u624e\u8425\uff0c\u8fc7\u4e86\u4e00\u665a\u3002\u7b2c\u4e8c\u65e5\u660e\u6167\u516c\u4e3b\u4e00\u65e9\u8d77\u6765\uff0c\u51c6\u5907\u51fa\u53d1\uff0c\u68b3\u6d17\u8fc7\u540e\uff0c\u7b49 \u4e86\u53c8\u7b49\uff0c\u4e0d\u77e5\u4e0d\u89c9\u5df2\u662f\u65e5\u4e0a\u4e09\u7aff\u65f6\u5206\uff0c\u4ecd\u7136\u672a\u89c1\u52a8\u9759\u3002 \u3000\u3000\u8499\u53e4\u58eb\u5175\u7684\u884c\u519b\u4e60\u60ef\u4e43\u662f\u201c\u4e24\u5934\u4e0d\u89c1\u65e5\u201d\u7684\uff0c\u65e9\u4e0a\u592a\u9633\u672a\u51fa\u4fbf\u5373\u52a8\u8eab\uff0c\u665a\u4e0a\u592a\u9633\u843d\u5c71\u65b9 \u59cb\u6b47\u606f\uff0c\u5982\u4eca\u65e5\u4e0a\u4e09\u7aff\u4ecd\u7136\u672a\u89c1\u52a8\u9759\uff0c\u660e\u6167\u516c\u4e3b\u4e0d\u7531\u5f97\u6697\u6697\u7eb3\u7f55\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4eca\u662f\u8981\u8de8\u8fc7 \u8fd9\u5ea7\u9ad8\u5c71\uff0c\u66f4\u5e94\u8be5\u65e9\u70b9\u52a8\u8eab\u624d\u5bf9\u3002\u56db\u54e5\u4e0d\u77e5\u6253\u7684\u662f\u4ec0\u4e48\u4e3b\u610f\uff0c\u4f55\u4ee5\u8fdf\u8fdf\u5c1a\u672a\u4e0b\u4ee4\u542f\u7a0b\uff1f\u201d\u8f6c \u5ff5\u4e00\u60f3\uff0c\u53c8\u4e0d\u7981\u54d1\u7136\u5931\u7b11\uff0c\u60f3\u9053\uff1a\u201c\u4ed6\u90fd\u4e0d\u6025\uff0c\u6211\u6025\u4ec0\u4e48\uff01\u7239\u7239\u6b7b\u4e86\uff0c\u6545\u4e61\u65e9\u5df2\u6ca1\u6709\u503c\u5f97\u6211 \u6302\u5ff5\u7684\u4eba\uff0c\u8352\u5c71\u4e5f\u597d\uff0c\u6208\u58c1\u4e5f\u597d\uff0c\u548c\u6797\u4e5f\u597d\uff0c\u5bf9\u6211\u90fd\u662f\u4e00\u6837\u3002\u6211\u53ea\u662f\u4e00\u7247\u6d6e\u840d\uff0c\u4efb\u5b83\u6f02\u6d41\u662f \u4e86\u3002\u65e9\u65e5\u56de\u5230\u5bb6\u4e61\uff01\u66f4\u591a\u4e00\u65e5\u7684\u82e6\u75db\u3002\u201d \u3000\u3000\u6b63\u81ea\u4f24\u5fc3\uff0c\u5ffd\u89c1\u62d6\u96f7\u63ed\u5f00\u5e10\u5e55\uff0c\u8d70\u4e86\u8fdb\u6765\uff0c\u7b11\u9053\uff1a\u201c\u4e09\u59b9\uff0c\u660e\u65e9\u8de8\u8fc7\u8fd9\u5ea7\u5c71\uff0c\u5c31\u662f\u54b1\u4eec \u8499\u53e4\u7684\u5730\u65b9\u4e86\uff0c\u4f60\u6b22\u4e0d\u6b22\u559c\uff1f\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u9053\uff1a\u201c\u5947\u602a\uff0c\u4f60\u5012\u5173\u5fc3\u8d77\u6211\u6765\u4e86\uff1f\u6211\u53ea\u662f\u4f60\u6446\u5e03\u7684\u4e00\u5177\u6728\u5076\uff0c\u8bf4\u5f97\u4e0a\u4ec0\u4e48\u559c\u4e0d \u559c\u6b22\uff1f\u201d \u3000\u3000\u62d6\u96f7\u7b11\u9053\uff1a\u201c\u4e09\u59b9\uff0c\u522b\u8fd9\u6837\u8bf4\u3002\u5728\u5144\u5f1f\u59d0\u59b9\u4e4b\u4e2d\uff0c\u6211\u7d20\u6765\u5bf9\u4f60\u6700\u597d\uff0c\u6211\u600e\u80fd\u4e0d\u5173\u5fc3\u4f60 \u5462\uff1f\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u90a3\u662f\u4ece\u524d\u7684\u4e8b\u60c5\uff0c\u73b0\u5728\u4f60\u5173\u5fc3\u7684\u53ea\u662f\u81ea\u5df1\u7684\u6743\u4f4d\uff0c\u5426\u5219\u4f60\u4e5f\u4e0d\u4f1a \u903c\u6211\u56de\u6765\u4e86\u3002\u201d \u3000\u3000\u62d6\u96f7\u6709\u70b9\u5c34\u5c2c\uff0c\u6253\u4e86\u4e2a\u54c8\u54c8\u8bf4\u9053\uff1a\u201c\u4e09\u59b9\uff0c\u6211\u77e5\u9053\u4f60\u8fd8\u5728\u60f3\u5ff5\u7740\u601d\u5357\u5b89\u7b54\uff0c\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u9762\u8272\u4e00\u6c89\uff0c\u8bf4\u9053\uff1a\u201c\u56db\u54e5\uff0c\u6211\u4e0d\u60f3\u542c\u4f60\u80e1\u8bf4\u516b\u9053\u3002\u6ca1\u6709\u4ec0\u4e48\u4e8b\uff0c\u8bf7\u4f60\u8d70\u5427\u3002\u201d \u3000\u3000\u62d6\u96f7\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u522b\u8bf4\u4ed6\u4e86\u3002\u54b1\u4eec\u8bf4\u6b63\u7ecf\u7684\u4e8b\u513f\u3002\u56de\u56fd\u4e4b\u540e\uff0c\u4f60\u7684\u5a5a\u4e8b\u6050\u6015\u662f\u4e0d\u80fd\u62d6\u5ef6 \u7684\u4e86\uff0c\u4f60\u613f\u4e0d\u613f\u610f\u5ac1\u7ed9\u9547\u56fd\u738b\u5b50\uff1f\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u9053\uff1a\u201c\u613f\u610f\u53c8\u600e\u4e48\u6837\uff1f\u4e0d\u613f\u610f\u53c8\u600e\u4e48\u6837\uff1f\u96be\u9053\u8fd8\u80fd\u7531\u6211\u4f5c\u4e3b\u5417\uff1f\u201d \u3000\u3000\u62d6\u96f7\u6014\u4e86\u4e00\u6014\uff0c\u4ed6\u53ea\u9053\u660e\u6167\u516c\u4e3b\u4e00\u5b9a\u4e0d\u80af\u4f9d\u4ece\u7684\uff0c\u8fd9\u6837\u7684\u56de\u7b54\uff0c\u5012\u662f\u9887\u51fa\u4ed6\u610f\u6599\u4e4b\u5916\uff0c \u60f3\u4e86\u4e00\u60f3\uff0c\u7b11\u9053\uff1a\u201c\u4e09\u59b9\u679c\u731c\u9519\u4e86\uff0c\u6211\u8fd9\u6b21\u6765\u7684\u7528\u610f\uff0c\u5c31\u662f\u8981\u8ba9\u4f60\u81ea\u5df1\u4f5c\u4e3b\u3002\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u6728\u7136\u6beb\u65e0\u8868\u60c5\uff0c\u4e5f\u4e0d\u7b54\u8bdd\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4e8b\u5230\u5982\u4eca\uff0c\u4f60\u8fd8\u8981\u54c4\u6211\u3002\u201d \u3000\u3000\u62d6\u96f7\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u4e0d\u613f\u610f\u5ac1\u7ed9\u4ed6\u7684\u3002\u8bf4\u5b9e\u5728\u8bdd\u5ac1\u7ed9\u4ed6\uff0c\u7684\u786e\u50cf\u662f\u4e00\u6735\u9c9c\u82b1\u63d2\u5728 \u725b\u7caa\u4e0a\uff0c\u6211\u4e5f\u4e0d\u613f\u610f\u5982\u6b64\u59d4\u5c48\u4f60\u7684\u3002\u6211\u613f\u610f\u6210\u5168\u4f60\u7684\u5fc3\u613f\uff0c\u4e0d\u8fc7\u4f60\u53ef\u5f97\u542c\u6211\u7684\u8bdd\u3002\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u4ecd\u7136\u4e0d\u58f0\u4e0d\u54cd\uff0c\u62d6\u96f7\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u9547\u56fd\u738b\u5b50\u7b49\u4e0b\u4f1a\u6765\u8fce\u63a5\u4f60\uff0c\u4f60\u53ef\u5f97\u5bf9\u4ed6\u7a0d\u5047 \u8f9e\u8272\uff0c\u6211\u624d\u80fd\u591f\u5e2e\u4f60\u7684\u5fd9\u3002\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6211\u4ece\u6765\u5c31\u4e0d\u61c2\u5f97\u600e\u6837\u8ba8\u4eba\u5bb6\u7684\u559c\u6b22\u3002\u201d \u3000\u3000\u62d6\u96f7\u201c\u5509\u201d\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u4e09\u59b9\uff0c\u4f60\u4e0d\u61c2\uff0c\u6211\u8fd9\u6b21\u771f\u662f\u60f3\u5e2e\u5fd9\u4f60\u7684\uff0c\u597d\uff0c\u4f60\u4e0d\u613f\u88c5\u51fa \u7b11\u8138\uff0c\u90a3\u5c31\u50cf\u5e73\u5e38\u90a3\u6837\u5bf9\u4ed6\u597d\u3002\u4e0d\u8fc7\uff0c\u4ed6\u6765\u7684\u65f6\u5019\uff0c\u6211\u8981\u8bf7\u4f60\u51fa\u8425\u63a5\u4ed6\uff0c\u81f3\u5c11\u4e5f\u5f97\u8ba9\u4ed6\u89c1\u4e00 \u89c1\u4f60\u3002\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u4e0d\u8010\u70e6\u4ed6\u7684\u7ea0\u7f20\uff0c\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u597d\u4e86\uff0c\u597d\u4e86\u3002\u6211\u53ea\u662f\u4f60\u6446\u5e03\u7684\u6728\u5076\uff0c\u6211\u8981\u6211\u600e \u6837\u51fa\u5c31\u600e\u6837\u3002\u4f60\u6ee1\u610f\u4e86\u5427\uff1f\u201d \u3000\u3000\u62d6\u96f7\u6447\u4e86\u6447\u5934\uff0c\u6b63\u60f3\u8bf4\u8bdd\uff0c\u5ffd\u542c\u5f97\u545c\u545c\u7684\u53f7\u89d2\u58f0\u54cd\uff0c\u51e0\u4e2a\u536b\u58eb\u8fdb\u6765\u62a5\u9053\uff1a\u201c\u9547\u56fd\u738b\u5b50\u6765 \u4e86\u3002\u201d \u3000\u3000\u62d6\u96f7\u9053\uff1a\u201c\u4ed6\u5e26\u4e86\u591a\u5c11\u4eba\u6765\uff1f\u201d\u90a3\u536b\u58eb\u9053\uff1a\u201c\u5927\u7ea6\u6709\u4e09\u4e94\u5341\u9a91\u3002\u201d \u3000\u3000\u62d6\u96f7\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u4f60\u53eb\u6728\u534e\u9ece\u5c06\u519b\u6b3e\u5f85\u4ed6\u7684\u968f\u4ece\u3002\u4e09\u59b9\uff0c\u54b1\u4eec\u51fa\u53bb\u5427\u3002\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u65e9\u5df2\u6253\u5b9a\u4e3b\u610f\uff0c\u9547\u56fd\u738b\u5b50\u5018\u82e5\u903c\u5979\u6210\u5a5a\uff0c\u5979\u5c31\u5728\u201c\u559c\u65e5\u201d\u90a3\u5929\u81ea\u5c3d\u3002\u662f\u4ee5\u62d6\u96f7 \u65e2\u7136\u4e00\u5b9a\u8981\u5979\u540c\u53bb\u8fce\u63a5\uff0c\u5979\u4e5f\u5c31\u62b1\u7740\u201c\u770b\u4f60\u4eec\u80fd\u591f\u628a\u6211\u600e\u6837\u201d\u7684\u5fc3\u60c5\uff0c\u8ddf\u7740\u62d6\u96f7\u51fa\u53bb\u3002 \u3000\u3000\u9547\u56fd\u738b\u5b50\u8d70\u4e0b\u5c71\u8170\uff0c\u8fdc\u8fdc\u7684\u770b\u89c1\u4e86\u660e\u6167\u516c\u4e3b\u548c\u62d6\u96f7\u5e76\u80a9\u7ad9\u5728\u7ee3\u7740\u5140\u9e70\u7684\u8499\u53e4\u519b\u65d7\u4e0b\u9762\uff0c \u559c\u51fa\u671b\u5916\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u62d6\u96f7\u679c\u7136\u6ca1\u6709\u9a97\u6211\uff0c\u660e\u6167\u771f\u7684\u662f\u56de\u5fc3\u8f6c\u610f\uff0c\u56de\u6765\u548c\u6211\u6210\u4eb2\u4e86\u3002\u201d \u3000\u3000\u5f53\u4e0b\u9547\u56fd\u738b\u5b50\u5feb\u9a6c\u75be\u9a70\uff0c\u6765\u5230\u62d6\u96f7\u4e0e\u516c\u4e3b\u8ddf\u524d\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4e0d\u6562\u6709\u52b3\u516c\u4e3b\u8fce\u63a5\uff0c\u4e00\u8def \u8f9b\u82e6\u4e86\u554a\uff01\u201d \u3000\u3000\u62d6\u96f7\u89c1\u660e\u6167\u516c\u4e3b\u867d\u7136\u6ca1\u6709\u7b11\u5bb9\uff0c\u4f46\u4e5f\u6ca1\u6709\u607c\u6012\u7684\u795e\u6c14\uff0c\u677e\u4e86\u53e3\u6c14\uff0c\u4ee3\u5979\u7b54\u9053\uff1a\u201c\u4f60\u4e5f\u8f9b \u82e6\u4e86\u554a\uff0c\u63a5\u98ce\u9152\u5df2\u7ecf\u51c6\u5907\u597d\u4e86\uff0c\u5c31\u5f85\u4f60\u6765\uff0c\u8fdb\u53bb\u559d\u5427\u3002\u201d \u3000\u3000\u9547\u56fd\u738b\u5b50\u5472\u7259\u54a7\u5634\u5730\u7b11\u9053\uff1a\u201c\u5e94\u8be5\u662f\u6211\u7ed9\u516c\u4e3b\u63a5\u98ce\u624d\u5bf9\u3002\u201d\u660e\u6167\u516c\u4e3b\u867d\u6ca1\u548c\u4ed6\u8bf4\u8bdd\uff0c\u4f46 \u80af\u51fa\u6765\u8fce\u63a5\u4ed6\uff0c\u4ed6\u5df2\u7ecf\u662f\u6b22\u559c\u4e4b\u6781\u4e86\u3002 \u3000\u3000\u62d6\u96f7\u9053\uff1a\u201c\u4e5f\u8bf4\u4e0d\u4e0a\u662f\u4ec0\u4e48\u63a5\u98ce\u9152\uff0c\u54b1\u4eec\u4e00\u5bb6\u5b50\u6b22\u805a\u6b22\u805a\u3002\u201d \u3000\u3000\u9547\u56fd\u738b\u5b50\u542c\u4e86\u201c\u4e00\u5bb6\u5b50\u201d\u8fd9\u4e09\u4e2a\u5b57\uff0c\u5fc3\u4e2d\u66f4\u662f\u559c\u6b22\u3002\u6728\u534e\u9ece\u62db\u5f85\u4ed6\u7684\u968f\u4ece\u5728\u5916\u5e10\u996e\u9152\uff0c \u4ed6\u5728\u6ee1\u809a\u5bc6\u5708\uff0c\u4ee5\u4e3a\u62d6\u96f7\u662f\u8981\u548c\u4ed6\u5546\u8c08\u5a5a\u4e8b\u7684\u60c5\u5f62\u4e4b\u4e0b\uff0c\u4fbf\u4e5f\u4e0d\u52a0\u6212\u5907\uff0c\u6452\u5f03\u968f\u4ece\uff0c\u8ddf\u62d6\u96f7 \u5165\u5185\u5e10\u559d\u63a5\u98ce\u9152\u4e86\u3002 \u3000\u3000\u9547\u56fd\u738b\u5b50\u6709\u4e00\u642d\u6ca1\u4e00\u642d\u7684\u548c\u660e\u6167\u516c\u4e3b\u8bf4\u8bdd\uff0c\u660e\u6167\u516c\u4e3b\u7231\u7406\u4e0d\u7406\uff0c\u5076\u5c14\u4e5f\u7b54\u4ed6\u4e00\u4e24\u53e5\u3002\u9547 \u56fd\u738b\u5b50\u5fc3\u75d2\u96be\u71ac\uff0c\u8bf4\u9053\uff1a\u201c\u516c\u4e3b\uff0c\u96be\u5f97\u4f60\u56de\u6765\u4e86\uff0c\u6765\u5230\u548c\u6797\uff0c\u54b1\u4eec\u5c31\u529e\u559c\u4e8b\uff0c\u597d\u4e0d\u597d\uff1f\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u6728\u7136\u4e0d\u8bed\uff0c\u62d6\u96f7\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5979\u5230\u8fc7\u6c49\u4eba\u7684\u5730\u65b9\uff0c\u5df2\u7ecf\u6cbe\u67d3\u4e86\u6c49\u4eba\u5973\u5b50\u5bb3\u7f9e\u7684 \u98ce\u6c14\u4e86\uff0c\u4f60\u600e\u80fd\u8fd9\u6837\u95ee\u5979\uff1f\u201d \u3000\u3000\u9547\u56fd\u738b\u5b50\u7b11\u9053\uff1a\u201c\u5bf9\uff0c\u5bf9\uff0c\u8fd9\u5a5a\u4e8b\u5f53\u7136\u7531\u4f60\u505a\u54e5\u54e5\u7684\u4f5c\u4e3b\u3002\u201d \u3000\u3000\u62d6\u96f7\u9053\uff1a\u201c\u660e\u6167\uff0c\u4f60\u656c\u738b\u5b50\u4e00\u676f\uff0c\u4f60\u770b\u4ed6\u591a\u4e48\u75bc\u4f60\uff01\u201d \u3000\u3000\u9547\u56fd\u738b\u5b50\u54a7\u5f00\u4e86\u8840\u76c6\u5927\u5634\uff0c\u8fde\u5fd9\u8bf4\u9053\uff1a\u201c\u4e0d\u6562\u5f53\uff0c\u4e0d\u6562\u5f53\u3002\u201d \u3000\u3000\u62d6\u96f7\u738b\u5b50\u5219\u9053\uff1a\u201c\u8981\u7684\uff0c\u8981\u7684\uff01\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u7aef\u5750\u4e0d\u52a8\uff0c\u62d6\u96f7\u9053\uff1a\u201c\u597d\uff0c\u59b9\u5b50\u4f60\u6015\u7f9e\uff0c\u6211\u5c31\u4ee3\u4f60\u656c\u5427\u3002\u201d \u3000\u3000\u9547\u56fd\u738b\u5b50\u6709\u70b9\u5931\u671b\uff0c\u53cc\u624b\u4e00\u63a8\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u738b\u66f4\u5f53\u4e0d\u8d77\u76d1\u56fd\u656c\u6d12\u3002\u201d \u3000\u3000\u62d6\u96f7\u987a\u7740\u4ed6\u4e00\u63a8\u4e4b\u52bf\uff0c\u7a81\u7136\u628a\u9152\u676f\u4e00\u6454\uff0c\u559d\u9053\uff1a\u201c\u597d\uff0c\u656c\u9152\u4e0d\u5403\uff0c\u90a3\u4f60\u5c31\u5403\u7f5a\u9152\u5427\uff01\u201d \u3000\u3000\u8bdd\u72b9\u672a\u4e86\uff0c\u65e9\u6709\u4e24\u4e2a\u5728\u65c1\u8fb9\u4f3a\u5019\u7684\u6b66\u571f\u6251\u4e0a\u524d\u6765\uff0c\u4e00\u4e2a\u5361\u7740\u9547\u56fd\u738b\u5b50\u7684\u5589\u5499\uff0c\u4e00\u4e2a\u53cd\u6263 \u4ed6\u7684\u53cc\u8155\u3002\u9547\u56fd\u738b\u5b50\u5929\u751f\u795e\u529b\uff0c\u672c\u6765\u52c7\u6b66\u975e\u51e1\uff0c\u53cc\u81c2\u4e00\u632f\uff0c\u53ea\u542c\u5f97\u201c\u5494\u5693\u201d\u4e00\u58f0\uff0c\u90a3\u6263\u7740\u4ed6 \u7684\u53cc\u8155\u7684\u6b66\u58eb\u53cd\u800c\u81ea\u8eab\u53cc\u81c2\u8131\u81fc\uff0c\u4f46\u9547\u56fd\u738b\u5b50\u864e\u53e3\u9707\u88c2\uff0c\u6c14\u529b\u4e5f\u662f\u767b\u65f6\u53d1\u4e0d\u51fa\u6765\u4e86\u3002\u5361\u7740\u4ed6 \u7684\u5589\u5499\u7684\u90a3\u4e2a\u6b66\u58eb\uff0c\u5341\u6307\u5982\u94a9\uff0c\u7528\u529b\u4e00\u634f\u3001\u9547\u56fd\u738b\u5b50\u820c\u5934\u5410\u51fa\u4e09\u5bf8\u591a\u957f\uff0c\u767b\u65f6\u6c14\u7edd\uff01 \u3000\u3000\u539f\u6765\u62d6\u96f7\u52a9\u4e09\u54e5\u7a9d\u9614\u53f0\u593a\u5f97\u53ef\u6c57\u5b9d\u5ea7\u4e4b\u540e\uff0c\u603b\u63fd\u5175\u6743\uff0c\u4e3a\u6240\u6b32\u4e3a\uff0c\u6743\u529b\u5c3d\u5f52\u53ef\u6c57\u3002\u6240\u5fcc \u60ee\u7684\u5c31\u53ea\u6709\u9547\u56fd\u738b\u5b50\u4e00\u4eba\u4e86\u3002\u9547\u56fd\u738b\u5b50\u662f\u5175\u529b\u6700\u5f3a\u7684\u4e00\u8def\u5143\u5e05\uff0c\u6240\u5c5e\u7684\u90e8\u843d\uff0c\u53c8\u662f\u8457\u540d\u7684\u80fd \u5f81\u60ef\u6218\u7684\u90e8\u843d\uff0c\u62d6\u96f7\u8d77\u521d\u672c\u60f3\u7528\u59b9\u59b9\u7b3c\u7edc\u4ed6\u7684\uff0c\u4f46\u4e00\u6765\u660e\u6167\u516c\u4e3b\u672a\u5fc5\u80af\u4ece\uff0c\u4e8c\u6765\u9547\u56fd\u738b\u5b50\u8dcb \u6248\u56a3\u5f20\uff0c\u53ea\u6015\u4ed6\u4e5f\u672a\u5fc5\u80fd\u591f\u771f\u7684\u5fc3\u60a6\u8bda\u670d\uff0c\u4e0e\u5176\u7b3c\u7edc\uff0c\u4e0d\u5982\u5c06\u4ed6\u6740\u6389\uff0c\u4e00\u52b3\u6c38\u9038\uff0c\u65a9\u8349\u9664 \u6839\uff01 \u3000\u3000\u9547\u56fd\u738b\u5b50\u5927\u519b\u9a7b\u624e\u5728\u4e0e\u91d1\u56fd\u4ea4\u754c\u7684\u6208\u58c1\u8fb9\u7f18\uff0c\u4e2d\u95f4\u9694\u7740\u4e00\u5ea7\u5927\u5c71\u3002\u8981\u9664\u6389\u9547\u56fd\u738b\u5b50\u51b3\u4e0d \u80fd\u5728\u4ed6\u519b\u4e2d\u52a8\u624b\uff0c\u53ea\u80fd\u5c06\u4ed6\u7528\u8ba1\u8bf1\u6765\uff0c \u3000\u3000\u62d6\u96f7\u7684\u5999\u8ba1\u5c31\u662f\u4ee5\u660e\u6167\u516c\u4e3b\u4e3a\u9975\uff0c\u8bf1\u4ed6\u524d\u6765\u8fce\u63a5\u3002\u4ed6\u5f53\u7136\u662f\u4e0d\u80fd\u5e26\u9886\u5927\u519b\u8d8a\u8fc7\u9ad8\u5c71\u7684\uff0c \u8981\u6765\u8fce\u63a5\u516c\u4e3b\uff0c\u53ea\u80fd\u5e26\u9886\u51e0\u5341\u540d\u968f\u4ece\uff0c\u62d6\u96f7\u738b\u5b50\u8fd8\u6015\u4ed6\u4e0d\u80af\u4e0b\u5c71\uff0c\u6545\u800c\u8981\u660e\u6167\u516c\u4e3b\u966a\u4ed6\u51fa\u8425 \u201c\u4eae\u76f8\u201d\u3002 \u3000\u3000\u90a3\u4e24\u4e2a\u627c\u6bd9\u9547\u56fd\u738b\u5b50\u7684\u6b66\u58eb\u6b63\u662f\u9f99\u8c61\u6cd5\u738b\u7684\u5f97\u610f\u5f1f\u5b50\u963f\u535c\u5362\u4e0e\u547c\u9ece\u5962\uff0c\u9547\u56fd\u738b\u5b50\u867d\u6709\u5929 \u751f\u795e\u529b\uff0c\u4e5f\u654c\u4e0d\u4f4f\u4ed6\u4eec\u540c\u65f6\u4f7f\u51fa\u7684\u9f99\u8c61\u529f\u3002 \u3000\u3000\u963f\u535c\u5362\u4f7f\u51fa\u4e86\u9f99\u8c61\u529f\uff0c\u4e5f\u4ecd\u7136\u7ed9\u9547\u56fd\u738b\u5b50\u6298\u65ad\u4ed6\u7684\u53cc\u8155\uff0c\u9976\u662f\u4ed6\u6709\u94c1\u6c49\u4e4b\u79f0\uff0c\u4e5f\u7981\u4e0d\u4f4f \u51b7\u6c57\u8ff8\u6d41\uff0c\u9762\u5982\u571f\u8272\uff0c\u55f7\u55f7\u547c\u75db\u3002 \u3000\u3000\u62d6\u96f7\u6b22\u559c\u4e4b\u6781\uff0c\u4e3a\u4e86\u7b3c\u7edc\u4eba\u5fc3\uff0c\u5148\u5047\u610f\u5b89\u5c09\u963f\u535c\u5362\u9053\uff1a\u201c\u4f60\u7acb\u4e86\u5927\u529f\uff0c\u6211\u5b9a\u6709\u91cd\u8d4f\u3002\u201d \u4eb2\u624b\u7ed9\u4ed6\u6577\u4e0a\u4e86\u91d1\u521b\u836f\uff0c\u53eb\u547c\u9ece\u5962\u6276\u4ed6\u5230\u540e\u5e10\u533b\u6cbb\u3002\u7136\u540e\u53eb\u4e00\u540d\u6b66\u58eb\u8fdb\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u5feb\u70b9\u7ed9 \u6211\u5c06\u6728\u534e\u9ece\u5c06\u519b\u548c\u9f99\u8c61\u6cd5\u7389\u3001\u9633\u56fd\u5e08\u4e09\u4eba\u8bf7\u6765\u3002\u201d \u3000\u3000\u62d6\u96f7\u5f85\u4e8b\u5904\u7f6e\u505c\u5f53\uff0c\u8fd9\u624d\u56de\u8fc7\u5934\u6765\uff0c\u7b11\u7740\u5bf9\u660e\u6167\u516c\u4e3b\u8bf4\u9053\uff1a\u201c\u4e09\u59b9\uff0c\u6211\u8bf4\u8fc7\u8981\u6210\u5168\u4f60\u7684 \u5fc3\u613f\uff0c\u8ba9\u4f60\u4e0d\u5fc5\u5ac1\u7ed9\u8fd9\u4e2a\u4e11\u516b\u602a\uff0c\u73b0\u5728\u4f60\u770b\uff0c\u6211\u53ef\u6ca1\u6709\u9a97\u4f60\u5427\uff01\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u672c\u6765\u662f\u618e\u538c\u9547\u56fd\u738b\u5b50\u5230\u4e86\u6781\u70b9\u7684\uff0c\u4f46\u89c1\u62d6\u96f7\u624b\u6bb5\u5982\u6b64\u72e0\u8fa3\uff0c\u4e5f\u662f\u4e0d\u7981\u6bdb\u9aa8\u8038 \u7136\u3002 \u3000\u3000\u4e0d\u8fc7\u4e00\u4f1a\uff0c\u6728\u534e\u9ece\u548c\u9f99\u8c61\u6cd5\u738b\u3001\u9633\u5929\u96f7\u4e09\u4eba\u5df2\u7136\u6765\u5230\u3002\u62d6\u96f7\u95ee\u9053\uff1a\u201c\u6728\u5c06\u519b\uff0c\u90a3\u51e0\u5341\u540d \u968f\u4ece\u4f60\u53ef\u5c06\u4ed6\u4eec\u5904\u7f6e\u4e86\u6ca1\u6709\uff1f\u201d \u3000\u3000\u6728\u534e\u9ece\u7b11\u9053\uff1a\u201c\u4ed7\u6bbf\u4e0b\u7684\u5999\u8ba1\uff0c\u6211\u5728\u9152\u4e2d\u4e0b\u4e86\u8499\u6c57\u836f\uff0c\u53eb\u4ed6\u4eec\u4e2a\u4e2a\u559d\u5f97\u70c2\u9189\u5982\u6ce5\uff0c\u4e0d\u8d39 \u5439\u7070\u4e4b\u529b\uff0c\u5c31\u628a\u4ed6\u4eec\u90fd\u6d3b\u57cb\u4e86\uff01\u201d \u3000\u3000\u62d6\u96f7\u7fd8\u8d77\u62c7\u6307\u54c8\u54c8\u5927\u7b11\uff0c\u8d5e\u9053\uff1a\u201c\u5e72\u5f97\u597d\uff0c\u5e72\u5f97\u597d\uff01\u201d \u3000\u3000\u6728\u534e\u9ece\u9053\uff1a\u201c\u9547\u56fd\u738b\u5b50\u7684\u90e8\u4e0b\u7ea6\u6709\u4e94\u4e07\u4e4b\u4f17\uff0c\u5728\u90a3\u8fb9\u5c71\u4e0b\u9a7b\u624e\uff0c\u82e5\u7136\u77e5\u9053\u4e3b\u5e05\u88ab\u5bb3\uff0c\u53ea \u6015\u4f1a\u95f9\u51fa\u4e8b\u6765\u3002\u54b1\u4eec\u987b\u5f97\u65e9\u70b9\u8bbe\u6cd5\u5bf9\u4ed8\uff01\u201d \u3000\u3000\u62d6\u96f7\u9053\uff1a\u201c\u6b63\u662f\u4e3a\u6b64\uff0c\u6211\u624d\u8bf7\u4f60\u4eec\u4e00\u540c\u5546\u8bae\u7684\u3002\u4f9d\u4f60\u4e4b\u89c1\u5982\u4f55\uff1f\u201d \u3000\u3000\u6728\u534e\u9ece\u9053\uff1a\u201c\u90a3\u51e0\u5341\u540d\u968f\u4ece\uff0c\u662f\u9547\u56fd\u738b\u5b50\u7684\u5fc3\u8179\u6b7b\u571f\uff0c\u54b1\u4eec\u81ea\u662f\u5e94\u5f53\u5c06\u4ed6\u4eec\u5168\u90fd\u5e72\u6389\u3002 \u4f46\u8fd9\u51e0\u4e07\u5927\u519b\uff0c\u53ef\u662f\u52a8\u5b83\u4e0d\u5f97\u3002\u4f9d\u5c0f\u5c06\u4e4b\u89c1\uff0c\u6bbf\u4e0b\u5e94\u5f53\u6069\u5a01\u5e76\u7528\uff0c\u6536\u4e70\u4eba\u5fc3\u3002\u201d \u3000\u3000\u62d6\u96f7\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u4ef6\u4e8b\u5c31\u8bf7\u4f60\u53bb\u529e\u5427\uff01\u201d\u6728\u534e\u9ece\u9053\uff1a\u201c\u8bf7\u6bbf\u4e0b\u8d50\u793a\u3002\u201d\u62d6\u96f7\u9053\uff1a\u201c\u4f60 \u5272\u4e0b\u9547\u56fd\u738b\u5b50\u7684\u4eba\u5934\uff0c\u8bf7\u6cd5\u738b\u548c\u9633\u56fd\u5e08\u4fdd\u62a4\u4f60\u540c\u5f80\u519b\u4e2d\uff0c\u6653\u55bb\u4ed6\u4eec\uff0c\u670d\u4ece\u6211\u7684\uff0c\u5c06\u5b98\u5347\u4e00 \u7ea7\uff0c\u58eb\u5175\u641e\u8d4f\u5341\u4e24\u94f6\u5b50\uff0c\u4e0d\u670d\u7684\u5c31\u628a\u4ed6\u6740\u6389\u3002\u6599\u60f3\u4ed6\u4eec\u4e0d\u6562\u53db\u53d8\uff01\u201d \u3000\u3000\u6728\u534e\u9ece\u6709\u70b9\u8e0c\u8e87\uff0c\u8bf4\u9053\uff1a\u201c\u5c31\u53ea\u6211\u4eec\u4e09\u4e2a\u4eba\u53bb\u4e48\uff1f\u201d \u3000\u3000\u62d6\u96f7\u53cc\u773c\u4e00\u7ffb\uff0c\u8bf4\u9053\uff1a\u201c\u600e\u4e48\uff0c\u4f60\u80c6\u602f\u4e86\uff1f\u597d\uff0c\u4f60\u4e0d\u6562\u53bb\uff0c\u6211\u5355\u9a91\u5f80\u629a\uff01\u201d \u3000\u3000\u6728\u534e\u9ece\u8fde\u5fd9\u8bf4\u9053\uff1a\u201c\u6bbf\u4e0b\u8d1f\u6c57\u56fd\u91cd\u8d23\uff0c\u5c82\u80fd\u5192\u4e0d\u6d4b\u4e4b\u9669\uff1f\u5c0f\u5c06\u8d74\u6c64\u8e48\u706b\uff0c\u4ea6\u6240\u4e0d\u8f9e\uff0c\u53ea \u662f\u60f3\u8bf7\u793a\u6bbf\u4e0b\uff0c\u662f\u5426\u5e94\u8be5\u591a\u5e26\u6570\u5341\u540d\u7cbe\u608d\u7684\u6b66\u58eb\u800c\u5df2\u3002\u201d \u3000\u3000\u62d6\u96f7\u8fd9\u624d\u8f6c\u6012\u4e3a\u559c\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8eab\u4e3a\u5927\u5c06\uff0c\u81ea\u5fc5\u901a\u6653\u7528\u5175\u4e4b\u9053\uff0c\u8fd9\u6b21\u4f60\u4eec\u53bb\u6536\u670d\u4eba\u5fc3\uff0c \u5fc5\u987b\u51fa\u5947\u5236\u80dc\uff0c\u5230\u4e86\u519b\u4e2d\uff0c\u8fd9\u624d\u62ff\u51fa\u4eba\u5934\uff0c\u6069\u5a01\u5e76\u7528\uff0c\u6765\u4e00\u4e2a\u5feb\u5200\u65a9\u4e71\u9ebb\u7684\u624b\u6bb5\uff0c\u624d\u80fd\u6210 \u529f\uff0c\u5c82\u80fd\u591a\u5e26\u4eba\u9a6c\uff0c\u4ee4\u4ed6\u4eec\u5148\u8d77\u7591\u5fc3\uff1f\u201d \u3000\u3000\u6728\u534e\u9ece\u8fde\u58f0\u8bf4\u9053\uff1a\u201c\u662f\uff0c\u662f\u3002\u201d\u62d6\u96f7\u54c8\u54c8\u4e00\u7b11\uff0c\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u6cd5\u738b\u4e0e\u9633\u56fd\u5e08\u90fd\u662f\u6b66\u529f\u7edd \u4e16\u3002\u5373\u4f7f\u6709\u751a\u610f\u5916\uff0c\u4f60\u4e5f\u4e0d\u7528\u62c5\u5fe7\uff01\u201d \u3000\u3000\u9f99\u8c61\u6cd5\u738b\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6bbf\u4e0b\u5938\u5956\u4e86\u3002\u4e0d\u8fc7\u6709\u9633\u56fd\u5e08\u76f8\u52a9\uff0c\u5c0f\u50e7\u6562\u5938\u6d77\u53e3\uff0c\u5b9a\u80fd\u4fdd\u5f97\u6728\u5c06 \u519b\u6709\u53bb\u6709\u56de\u3002\u201d \u3000\u3000\u62d6\u96f7\u8bf4\u9053\uff1a\u201c\u9633\u56fd\u5e08\uff0c\u672c\u6765\u4f60\u662f\u5ba2\u4eba\uff0c\u6211\u4e0d\u6562\u52b3\u70e6\u4f60\u7684\uff0c\u4f46\u542c\u8bf4\u4f60\u6709\u5fc3\u6295\u6548\u6211\u56fd\uff0c\u6211\u4e5f \u5c31\u628a\u4f60\u5f53\u4f5c\u81ea\u5df1\u4eba\u4e00\u6837\u770b\u5f85\u4e86\u3002\u201d \u3000\u3000\u9633\u5929\u96f7\u5fd9\u9053\uff1a\u201c\u591a\u8499\u6bbf\u4e0b\u63a8\u5fc3\u7f6e\u8179\uff0c\u8fd9\u662f\u6211\u7acb\u529f\u76f8\u62a5\u7684\u673a\u4f1a\uff0c\u5c82\u6562\u63a8\u8f9e\u3002\u201d \u3000\u3000\u62d6\u96f7\u6325\u4e00\u6325\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4f60\u4eec\u53bb\u5427\uff01\u201d \u3000\u3000\u6728\u534e\u9ece\u62c5\u5fc3\u5728\u9547\u56fd\u738b\u5b50\u7684\u519b\u4e2d\u53d7\u56f0\uff0c\u5c82\u77e5\u5371\u9669\u5e76\u975e\u662f\u5728\u9547\u56fd\u738b\u5b50\u7684\u519b\u4e2d\uff0c\u800c\u662f\u5728\u5288\u5929\u5d16 \u4e0a\u3002 \u3000\u3000\u5288\u5929\u5d16\u4e0a\uff0c\u5b5f\u5c11\u521a\u3001\u674e\u601d\u5357\u7b49\u4eba\u65e9\u5df2\u5728\u90a3\u91cc\u7b49\u5019\u4ed6\u4eec\u3002 \u3000\u3000\u5b5f\u3001\u674e\u7b49\u4eba\u662f\u6628\u5929\u665a\u4e0a\u5c31\u5230\u4e86\u5288\u5929\u5d16\u7684\uff0c\u53ef\u662f\u4ed6\u4eec\u5374\u5e76\u6ca1\u6709\u89c1\u7740\u5b5f\u660e\u971e\u548c\u5c60\u51e4\u3001\u4e25\u70f7\u3002 \u3000\u3000\u4ed6\u4eec\u4e0a\u5c71\u4e4b\u65f6\uff0c\u6b63\u662f\u62d6\u96f7\u5728\u5c71\u4e0a\u624e\u8425\u7684\u65f6\u5019\uff0c\u9a6c\u5636\u4e4b\u58f0\uff0c\u5c71\u4e0a\u9690\u9690\u6240\u95fb\u3002 \u3000\u3000\u8352\u5c71\u91ce\u5cad\uff0c\u7791\u8272\u56db\u5408\uff0c\u9ed1\u591c\u6765\u4e34\uff0c\u8981\u60f3\u627e\u4eba\uff0c\u8c08\u4f55\u5bb9\u6613\uff1f\u5b5f\u5c11\u521a\u672c\u6765\u53ef\u4ee5\u7528\u4e0a\u4e58\u7684\u5185\u529f \u53d1\u51fa\u957f\u5578\u8ba9\u5979\u4eec\u77e5\u9053\u7684\uff0c\u4f46\u82e5\u7136\u53d1\u51fa\u957f\u5578\uff0c\u5b5f\u660e\u971e\u5979\u4eec\u542c\u5f97\u89c1\uff0c\u5c71\u4e0b\u7684\u9f99\u8c61\u6cd5\u738b\u4ed6\u4eec\u4e5f\u4e00\u5b9a \u4f1a\u542c\u5f97\u89c1\u7684\uff0c\u5982\u6b64\u4e00\u6765\uff0c\u5c82\u4e0d\u662f\u6cc4\u4e86\u884c\u85cf\uff0c\u8bef\u4e86\u5927\u4e8b\uff01\u662f\u4ee5\u4ed6\u4eec\u53ea\u597d\u5728\u5c71\u5934\u57cb\u4f0f\uff0c\u5c0f\u5fc3\u7559 \u610f\uff0c\u7b49\u5f85\u5b5f\u660e\u971e\u5979\u4eec\u6765\u5230\uff0c\u5e0c\u671b\u80fd\u591f\u53d1\u73b0\u5979\u4eec\u3002 \u3000\u3000\u4e00\u4e2a\u665a\u4e0a\u8fc7\u53bb\uff0c\u534a\u4e2a\u767d\u5929\u4e5f\u8fc7\u53bb\u4e86\uff0c\u6ca1\u6709\u7b49\u7740\u5b5f\u660e\u971e\u5979\u4eec\uff0c\u5374\u5148\u89c1\u7740\u9f99\u8c61\u6cd5\u738b\u3001\u9633\u5929\u96f7 \u548c\u6728\u534e\u9ece\u4e09\u4eba\u8054\u9a91\u4e0a\u5c71\u3002 \u3000\u3000\u674e\u601d\u5357\u559c\u51fa\u671b\u5916\uff0c\u8bf4\u9053\uff1a\u201c\u5947\u602a\uff0c\u4e0d\u77e5\u4f55\u6545\u62d6\u96f7\u7684\u5927\u961f\u4eba\u9a6c\u8fd8\u6ca1\u51fa\u53d1\uff0c\u53ea\u89c1\u4ed6\u4eec\u4e09\u4eba\u5148 \u884c\uff1f\u4f46\u53ea\u662f\u4ed6\u4eec\u4e09\u4eba\uff0c\u8fd9\u5374\u662f\u54b1\u4eec\u62a5\u4ec7\u7684\u597d\u673a\u4f1a\u4e86\u3002\u201d \u3000\u3000\u6768\u5a49\u9053\uff1a\u201c\u5b5f\u59d0\u59d0\u548c\u5c60\u59d0\u59d0\u4e0d\u77e5\u4f55\u6545\u4e5f\u8fd8\u6ca1\u6709\u6765\u5230\uff1f\u201d \u3000\u3000\u891a\u4e91\u5cf0\u6302\u5ff5\u7740\u5b5f\u660e\u971e\uff0c\u4f46\u66f4\u6025\u4e8e\u6e05\u7406\u5e08\u95e8\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u65f6\u673a\u7d27\u8feb\uff0c\u4e0d\u80fd\u7b49\u5979\u4eec\u4e86\uff0c\u601d \u5357\u5144\uff0c\u4f60\u7684\u7bad\u6cd5\u597d\uff0c\u5f85\u4ed6\u4eec\u6765\u5230\u767e\u6b65\u4e4b\u5185\uff0c\u5148\u5c04\u4ed6\u4eec\u7684\u5750\u9a91\u3002\u55ef\uff0c\u6765\u4e86\uff0c\u6765\u4e86\uff01\u5664\u58f0\uff0c\u5664 \u58f0\uff01\u201d \u3000\u3000\u674e\u601d\u5357\u62ff\u8d77\u94c1\u80ce\u5f13\uff0c\u642d\u4e0a\u4e09\u652f\u957f\u7bad\uff0c\u8eb2\u5728\u5371\u5ca9\u540e\u9762\uff0c\u53ea\u7b49\u4ed6\u4eec\u6765\u5230\u5c04\u7a0b\u4e4b\u5185\uff0c\u4fbf\u5373\u5f20\u5f13 \u53d1\u7bad\u3002\u8fd9\u526f\u5f13\u7bad\uff0c\u662f\u6210\u5409\u601d\u6c57\u8d50\u7ed9\u4ed6\u7684\uff0c\u5f13\u529b\u4e4b\u5f3a\u51fa\u4e8e\u7528\u673a\u62ec\u53d1\u5c04\u7684\u94c1\u80ce\u5f13\uff0c\u767e\u6b65\u4e4b\u5185\uff0c\u4e2d \u4eba\u7acb\u6bd9\u3002\u9f99\u8c61\u6cd5\u738b\u548c\u9633\u5929\u96f7\u662f\u5f53\u4e16\u6570\u4e00\u6570\u4e8c\u7684\u9ad8\u624b\uff0c\u5c04\u6740\u4ed6\u4eec\u867d\u662f\u4e0d\u80fd\uff0c\u4f46\u5728\u5077\u88ad\u7684\u60c5\u5f62\u4e4b \u4e0b\uff0c\u8981\u5c04\u6bd9\u4ed6\u4eec\u7684\u5750\u9a91\uff0c\u5374\u662f\u751a\u6709\u628a\u63e1\u3002 \u3000\u3000\u6b63\u5728\u201c\u4e07\u6728\u65e0\u58f0\u5f85\u96e8\u6765\u201d\u4e4b\u9645\uff0c\u773c\u770b\u6728\u534e\u9ece\u4e09\u4eba\u5c31\u8981\u6765\u5230\u767e\u6b65\u7684\u5c04\u7a0b\u4e4b\u5185\u4e86\uff0c\u5ffd\u542c\u5f97 \u201c\u8f70\u9686\uff0c\u8f70\u9686\uff01\u201d\u7684\u5de8\u54cd\uff0c\u9707\u64bc\u5c71\u8c37\uff0c\u4e24\u5757\u5706\u684c\u822c\u5927\u7684\u5de8\u77f3\u4ece\u5c71\u4e0a\u6eda\u4e0b\u6765\uff01\u5c71\u5934\u4e0a\u51fa\u73b0\u4e86\u4e09 \u4e2a\u5c11\u5973\u7684\u5f71\u5b50\u3002 \u3000\u3000\u891a\u4e91\u5cf0\u5931\u58f0\u53eb\u9053\uff1a\u201c\u660e\u971e\uff0c\u660e\u971e\uff0c\u4f60\u4eec\u6765\u4e86\uff01\u201d \u3000\u3000\u8fd9\u4e09\u4e2a\u5c11\u5973\u6b63\u662f\u5b5f\u660e\u971e\u3001\u5c60\u51e4\u548c\u4e25\u70f7\uff01\u539f\u6765\u5979\u4eec\u4e5f\u662f\u50cf\u674e\u601d\u5357\u7b49\u4eba\u4e00\u6837\uff0c\u65e9\u5df2\u5728\u5c71\u5934\u7406 \u4f0f\uff0c\u53ea\u662f\u56e0\u4e3a\u5728\u6ca1\u6709\u53d1\u73b0\u654c\u4eba\u4e4b\u524d\uff0c\u5979\u4eec\u4e5f\u4e0d\u6562\u8f7b\u9732\u884c\u85cf\u800c\u5df2\u3002 \u3000\u3000\u5b5f\u5c11\u521a\u662f\u6df1\u77e5\u5979\u4eec\u4e09\u4eba\u7684\u672c\u9886\uff0c\u90fd\u662f\u957f\u4e8e\u5251\u6cd5\u800c\u6b20\u7f3a\u5185\u529b\u7684\uff0c\u770b\u89c1\u8fd9\u4e48\u5927\u7684\u4e24\u5757\u5de8\u77f3\u4ece \u5c71\u4e0a\u6eda\u4e0b\u6765\uff0c\u5374\u662f\u4e0d\u7981\u6709\u70b9\u8be7\u5f02\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u4e24\u5757\u5de8\u77f3\uff0c\u90fd\u662f\u91cd\u903e\u5343\u65a4\uff0c\u5979\u4eec\u4e09\u4eba\u5408 \u529b\uff0c\u4e5f\u672a\u5fc5\u80fd\u591f\u63a8\u52a8\uff0c\u83ab\u975e\u662f\u8fd8\u6709\u4eba\u5728\u6697\u4e2d\u5e2e\u5fd9\uff1f\u201d \u3000\u3000\u90a3\u4e24\u5757\u5de8\u77f3\u5728\u5c71\u8170\u4e0e\u51b0\u5ca9\u4e00\u78b0\uff0c\u629b\u8d77\u6570\u4e08\u6765\u9ad8\uff0c\u6d41\u661f\u6b92\u77f3\u822c\uff0c\u76f4\u98de\u4e0b\u6765\uff0c\u9f99\u8c61\u6cd5\u738b\u4e0e\u9633 \u5929\u96f7\u5e76\u8f94\u540c\u884c\uff0c\u8d70\u5728\u6728\u534e\u9ece\u7684\u524d\u9762\uff0c\u9996\u5f53\u5176\u51b2\u3002\u4ed6\u4eec\u4e8c\u4eba\u867d\u7136\u6b66\u529f\u5353\u7edd\uff0c\u4e5f\u662f\u4e0d\u6562\u7ed9\u5de8\u77f3\u78b0 \u4e2a\u6b63\u7740\u3002\u4f46\u5c71\u9053\u72ed\u7a84\uff0c\u8981\u907f\u4e5f\u907f\u4e0d\u5f00\uff01 \u3000\u3000\u9f99\u8c61\u6cd5\u738b\u75be\u5fd9\u4ece\u9a6c\u80cc\u4e0a\u4e00\u8dc3\u800c\u8d77\uff0c\u907f\u5f00\u6b63\u9762\uff0c\u53cc\u638c\u4e00\u6452\uff0c\u5c06\u90a3\u5757\u5de8\u77f3\u7684\u65b9\u5411\u62e8\u659c\uff0c\u53ea\u542c \u5f97\u201c\u8f70\u9686\u201d\u5de8\u54cd\uff0c\u58f0\u82e5\u96f7\u9e23\uff0c\u8fd9\u5757\u5de8\u77f3\u5760\u4e0b\u4e86\u767e\u4e08\u5e7d\u8c37\uff01\u53e6\u4e00\u58f0\u5de8\u77f3\u4e5f\u7ed9\u9633\u5929\u96f7\u4ee5\u5929\u96f7\u529f\u9707 \u843d\uff0c\u6070\u597d\u4ece\u4ed6\u8eab\u8fb9\u6eda\u8fc7\uff0c\u9aa8\u788c\u788c\u5730\u6eda\u4e0b\u5761\uff0c\u8f70\u8f70\u9686\u9686\u4e4b\u58f0\uff0c\u4e45\u4e45\u4e0d\u7edd\uff01 \u3000\u3000\u9f99\u8c61\u6cd5\u738b\u5927\u6012\u559d\u9053\uff1a\u201c\u597d\u5927\u80c6\u7684\u51e0\u4e2a\u5c0f\u4e2b\u5934\uff0c\u5c45\u7136\u6562\u5bf9\u6211\u4eec\u5077\u65bd\u6697\u7b97\uff01\u201d\u6b63\u8981\u8dd1\u4e0a\u53bb\u62ff \u4eba\uff0c\u6728\u534e\u9ece\u9053\uff1a\u201c\u5f85\u6211\u53d6\u5979\u4eec\u6027\u547d\uff01\u201d \u3000\u3000\u6728\u534e\u9ece\u4e0e\u54f2\u522b\u662f\u5e76\u9a7e\u9f50\u540d\u7684\u795e\u7bad\u624b\uff0c\u6709\u5fc3\u5728\u6cd5\u738b\u9762\u524d\u70bc\u8000\u672c\u9886\uff0c\u7acb\u5373\u5f20\u5f13\u642d\u7bad\uff0c\u4e00\u53d1\u5c31 \u662f\u4e09\u652f\uff0c\u8fd9\u4e09\u652f\u7bad\u79bb\u5f26\u4e4b\u540e\uff0c\u5728\u534a\u7a7a\u4e2d\u6210\u54c1\u5b57\u5f62\u7684\u6563\u5f00\uff0c\u95ea\u7535\u822c\u5730\u5411\u5c71\u5934\u4e0a\u7684\u4e09\u4e2a\u5c11\u5973\u5c04 \u53bb\u3002 \u3000\u3000\u674e\u601d\u5357\u559d\u9053\uff1a\u201c\u597d\uff0c\u6211\u5c31\u4e0e\u4f60\u6bd4\u6bd4\u7bad\u6cd5\uff01\u201d\u5f13\u5982\u9739\u96f7\uff0c\u7bad\u4f3c\u6d41\u661f\uff0c\u4e00\u53d1\u4e5f\u662f\u4e09\u652f\uff0c\u53ea\u542c \u5f97\u94b5\u950b\u950b\u4e09\u58f0\u54cd\u8fc7\uff0c\u516d\u652f\u7bad\u5728\u7a7a\u4e2d\u78b0\u4e2a\u6b63\u7740\uff0c\u90fd\u6389\u4e0b\u6765\u3002 \u3000\u3000\u674e\u601d\u5357\u4e00\u4e0d\u505a\u4e8c\u4e0d\u4f11\uff0c\u55d6\u55d6\u4e24\u652f\u8fde\u73e0\u7bad\u53c8\u5411\u9633\u5929\u96f7\u4e0e\u9f99\u8c61\u6cd5\u738b\u7684\u5750\u9a91\u5c04\u53bb\u3002\u4ed6\u4eec\u6b64\u65f6\u90fd \u5df1\u662f\u4e0b\u4e86\u9a6c\u80cc\u7684\uff0c\u8fd9\u4e24\u5339\u5750\u9a91\u7ed9\u5de8\u77f3\u6eda\u4e0b\u7684\u58f0\u52bf\u5413\u5f97\u6b63\u4f3c\u6ca1\u5934\u4e4c\u8747\u5730\u4e71\u8dd1\uff0c\u4e00\u5339\u5411\u4e1c\uff0c\u4e00\u5339 \u5411\u897f\uff0c\u4f46\u674e\u601d\u5357\u7684\u8fde\u73e0\u7bad\u5de6\u53f3\u5f00\u5f13\uff0c\u5374\u662f\u5c04\u4e2a\u6b63\u7740\uff01\u7bad\u6cd5\u7684\u7cbe\u5999\uff0c\u6728\u534e\u9ece\u89c1\u4e86\u4e5f\u4e0d\u7981\u5927\u5403\u4e00 \u60ca\uff0c\u81ea\u6127\u4e0d\u5982\uff01 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u6c5f\u5357\u5927\u4fa0\u5b5f\u5c11\u521a\u3001\u4e10\u5e2e\u5e2e\u4e3b\u9646\u6602\u4ed1\u3001\u891a\u4e91\u5cf0\u3001\u8c37\u6db5\u865a\u3001\u6768\u5a49\u7b49\u4eba\u90fd\u5df2 \u73b0\u51fa\u8eab\u5f62\uff0c\u5b5f\u5c11\u521a\u3001\u9646\u6606\u4ed1\u4e24\u4eba\u75be\u98ce\u822c\u5730\u5148\u6251\u4e0a\u53bb\uff0c\u5b5f\u5c11\u521a\u559d\u9053\uff1a\u201c\u9f99\u8c61\u6cd5\u738b\uff0c\u90a3\u65e5\u56fd\u5e08\u5e9c \u4e2d\u672a\u5206\u80dc\u8d1f\uff0c\u4eca\u65e5\u6211\u4e0e\u4f60\u518d\u51b3\u96cc\u96c4\uff01\u201d\u9646\u6606\u4ed1\u559d\u9053\uff1a\u201c\u9633\u5929\u96f7\uff0c\u4eca\u65e5\u8001\u53eb\u5316\u4e0e\u4f60\u518d\u62fc\u4e00 \u638c\uff01\u201d \u3000\u3000\u9f99\u8c61\u6cd5\u738b\u89c1\u5bf9\u65b9\u5171\u6709\u516d\u4eba\u4e4b\u591a\uff0c\u9664\u4e86\u6768\u5a49\u6b66\u529f\u8f83\u5f31\u4e4b\u5916\uff0c\u4e2a\u4e2a\u90fd\u662f\u9ad8\u624b\uff0c\u4e0d\u7981\u5fc3\u5934\u4e00 \u51db\uff0c\u559d\u9053\uff1a\u201c\u5b5f\u5c11\u521a\uff0c\u6709\u80c6\u7684\u4f60\u5c31\u4e0e\u6211\u5355\u6253\u72ec\u6597\uff01\u201d\u5b5f\u5c11\u521a\u7eb5\u58f0\u7b11\u9053\uff1a\u201c\u5b5f\u67d0\u4eba\u5c82\u662f\u6043\u591a\u4e3a \u80dc\u4e4b\u4eba\uff01\u201d\u58f0\u5230\u4eba\u5230\uff0c\u559d\u58f0\u201c\u770b\u5251\uff01\u201d\u5251\u5149\u5982\u7ec3\uff0c\u4e00\u62db\u201c\u94f6\u6c49\u6d6e\u6413\u201d\uff0c\u5df2\u662f\u6307\u5230\u4e86\u9f99\u8c61\u6cd5\u738b \u7684\u80f8\u524d\u3002 \u3000\u3000\u9f99\u8c61\u6cd5\u738b\u63d0\u8d77\u7985\u6756\uff0c\u559d\u9053\uff1a\u201c\u5927\u4e08\u592b\u4e00\u8a00\u65e2\u51fa\uff0c\u9a77\u9a6c\u96be\u8ffd\uff01\u201d\u94db\u4e00\u58f0\uff0c\u5c06\u5b5f\u5c11\u521a\u7684\u957f\u5251 \u62e8\u8fc7\u4e00\u8fb9\uff01 \u3000\u3000\u9633\u5929\u96f7\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u8001\u53eb\u5316\u4e5f\u6562\u4e0e\u6211\u5355\u6253\u72ec\u6597\uff1f\u201d\u547c\u7684\u4e00\u638c\u51fb\u51fa\uff0c\u5c06\u9646\u6606\u4ed1\u9707\u9000\u4e09 \u6b65\u3002\u891a\u4e91\u5cf0\u3001\u8c37\u6db5\u865a\u540c\u65f6\u8d76\u5230\uff01\u9f50\u58f0\u8bf4\u9053\uff1a\u201c\u9646\u5e2e\u4e3b\uff0c\u8fd9\u53ae\u662f\u672c\u95e8\u53db\u5f92\uff0c\u4e0d\u6562\u6709\u52b3\u5916\u4eba\u52a8 \u624b\uff0c\u8bf7\u8ba9\u6211\u6e05\u7406\u95e8\u6237\uff01\u201d \u3000\u3000\u9646\u6606\u4ed1\u529f\u529b\u8f83\u900a\u4e00\u7b79\uff0c\u7ed9\u5929\u96f7\u529f\u4e00\u9707\uff0c\u80f8\u53e3\u9690\u9690\u4f5c\u75db\uff0c\u53ea\u597d\u9000\u8fc7\u4e00\u8fb9\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6e05 \u7406\u5e08\u95e8\uff0c\u5916\u4eba\u672c\u6765\u662f\u4e0d\u4fbf\u63d2\u624b\u7684\u3002\u4f46\u8fd9\u9633\u8001\u8d3c\u7684\u6b66\u529f\u5b9e\u5728\u592a\u8fc7\u5389\u5bb3\uff0c\u5018\u82e5\u891a\u4e91\u5cf0\u548c\u8c37\u6db5\u865a\u6253 \u4e0d\u8fc7\u4ed6\uff0c\u6211\u8fd8\u662f\u975e\u4e0a\u4e0d\u53ef\uff01\u201d\u5f53\u4e0b\u9000\u8fc7\u4e00\u8fb9\uff0c\u51dd\u795e\u6ce8\u89c6\uff0c\u5e76\u5728\u6697\u4e2d\u8fd0\u6c14\uff0c\u5316\u89e3\u6240\u53d7\u7684\u4e00\u9707\u4f59 \u6ce2\uff0c\u4ee5\u6c42\u5c3d\u5feb\u6062\u590d\u7cbe\u795e\u3002 \u3000\u3000\u6728\u534e\u9ece\u4e43\u662f\u5927\u5c06\u4e4b\u6750\uff0c\u867d\u7136\u5a34\u719f\u5f13\u9a6c\uff0c\u6b66\u529f\u5219\u751a\u5e73\u5e38\u3002\u4ed6\u89c1\u654c\u65b9\u90fd\u662f\u9ad8\u624b\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a \u201c\u6211\u7559\u5728\u8fd9\u91cc\uff0c\u4ea6\u662f\u65e0\u6d4e\u4e8e\u4e8b\uff0c\u4e0d\u5982\u56de\u53bb\u642c\u53d6\u6551\u5175\u3002\u201d\u4e09\u652f\u8fde\u73e0\u7bad\u521a\u7ed9\u674e\u601d\u5357\u7684\u795e\u7bad\u78b0\u843d\uff0c \u7acb\u5373\u62e8\u8f6c\u9a6c\u5934\uff01 \u3000\u3000\u674e\u601d\u5357\u970d\u7136\u4e00\u7701\uff1a\u201c\u4e0d\u80fd\u8ba9\u4ed6\u56de\u53bb\uff01\u201d\u62c9\u5f97\u5f13\u5982\u6ee1\u6708\uff0c\u55d6\u3001\u55d6\u3001\u55d6\u53c8\u662f\u4e09\u652f\u8fde\u73e0\u7bad\u5c04 \u51fa\u3002\u53ef\u662f\u6728\u534e\u9ece\u7684\u5f13\u9a6c\u529f\u592b\u4e0e\u4ed6\u4e43\u662f\u5728\u4f2f\u4ef2\u4e4b\u95f4\uff0c\u674e\u601d\u5357\u90a3\u8fb9\u4e09\u7bad\u98de\u6765\uff0c\u4ed6\u8fd9\u8fb9\u4e5f\u662f\u4e09\u7bad\u8fde \u53d1\uff0c\u674e\u601d\u5357\u7684\u94c1\u80ce\u5f13\u52b2\u9053\u8f83\u5f3a\uff0c\u4e24\u652f\u7bad\u7ed9\u4ed6\u6253\u843d\uff0c\u8fd8\u6709\u4e00\u652f\u7bad\u5c04\u4e86\u5230\u6765\uff0c\u4f46\u6728\u534e\u9ece\u4e00\u4e2a\u956b\u91cc \u85cf\u8eab\uff0c\u4e5f\u907f\u5f00\u4e86\u3002 \u3000\u3000\u6728\u534e\u9ece\u7684\u5750\u9a91\u4e43\u662f\u65e5\u884c\u5e72\u91cc\u7684\u9a8f\u9a6c\uff0c\u674e\u601d\u5357\u53d1\u51fa\u7684\u7b2c\u56db\u652f\u7bad\uff0c\u5df2\u662f\u8ffd\u5b83\u4e0d\u4e0a\u3002 \u3000\u3000\u9f99\u8c61\u6cd5\u738b\u8fd9\u6b21\u6539\u7528\u7985\u6756\u6765\u5bf9\u4ed8\u5b5f\u5c11\u521a\u7684\u5b9d\u5251\uff0c\u5728\u5175\u5668\u4e0a\u5148\u4e0d\u5403\u4e8f\u3002\u6bd4\u8d77\u4e0a\u6b21\uff0c\u7528\u8888\u88df\u4e0e \u5b5f\u5c11\u521a\u76f8\u6597\uff0c\u66f4\u89c1\u529f\u529b\uff01 \u3000\u3000\u7985\u6756\u662f\u7cbe\u94a2\u6253\u9053\u7684\u91cd\u5175\u5668\uff0c\u800c\u4e14\u9f99\u8c61\u6cd5\u738b\u7684\u529f\u529b\u8fd8\u7565\u80dc\u4e00\u7b79\uff0c\u5b5f\u5c11\u521a\u7684\u5b9d\u5251\u81ea\u662f\u524a\u4e4b\u4e0d \u52a8\u3002 \u3000\u3000\u5b5f\u5c11\u521a\u4f7f\u51fa\u4e0a\u4e58\u5251\u6cd5\u4e2d\u4f3c\u865a\u4f3c\u5b9e\u7684\u62db\u6570\uff0c\u817e\u632a\u95ea\u5c55\uff0c\u58f0\u4e1c\u51fb\u897f\uff0c\u63a5\u8fde\u653b\u4e86\u5341\u6570\u62db\uff0c\u6270\u4e71 \u9f99\u8c61\u6cd5\u738b\u7684\u773c\u795e\uff0c\u89d1\u4e2a\u771f\u5207\uff0c\u5251\u5149\u75be\u95ea\uff0c\u9661\u7136\u7531\u865a\u5316\u5b9e\uff0c\u4e00\u62db\u201c\u767d\u8679\u8d2f\u65e5\u201d\uff0c\u5f84\u5411\u9f99\u8c61\u6cd5\u738b \u5f53\u80f8\u731b\u88ad\u3002\u8fd9\u4e00\u62db\u672c\u662f\u5b5f\u5c11\u521a\u7684\u6740\u624b\u7edd\u62db\uff0c\u4f46\u53ef\u60dc\u7528\u6765\u5bf9\u4ed8\u9f99\u8c61\u6cd5\u738b\uff0c\u5374\u662f\u7a0d\u5acc\u529f\u529b\u4e0d\u8db3 \u4e86\uff01 \u3000\u3000\u9f99\u8c61\u6cd5\u738b\u8eab\u8eaf\u9661\u7136\u4e00\u7f29\uff0c\u5c31\u5728\u5251\u5c16\u773c\u770b\u4fbf\u8981\u6cbe\u8863\u4e4b\u9645\uff0c\u7897\u53e3\u5927\u7684\u7985\u6756\u5df2\u662f\u65cb\u98ce\u822c\u5730\u8f6c\u4e86 \u4e00\u5708\uff0c\u201c\u94db\u201d\u7684\u4e00\u58f0\uff0c\u53c8\u628a\u5b5f\u5c11\u521a\u7684\u5b9d\u5251\u62d4\u5f00\u3002\u5b5f\u5c11\u521a\u864e\u53e3\u9690\u9690\u4f5c\u75db\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u91cc\u60f3 \u9053\uff1a\u201c\u53ef\u60dc\u6211\u8fd9\u4e00\u5251\u8fd8\u662f\u6162\u4e86\u70b9\u513f\uff0c\u8981\u518d\u627e\u8fd9\u6837\u4e00\u4e2a\u7834\u7efd\uff0c\u53c8\u4e0d\u77e5\u8981\u7b49\u5230\u4ec0\u4e48\u65f6\u5019\u4e86\u3002\u201d \u3000\u3000\u9f99\u8c61\u6cd5\u738b\u4e5f\u662f\u9887\u4e3a\u5fcc\u60ee\u5b5f\u5c11\u521a\u795e\u5999\u65e0\u65b9\u7684\u5251\u6cd5\uff0c\u4e0d\u6562\u8ba9\u4ed6\u62a2\u653b\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u731b\u7684 \u4e00\u58f0\u5927\u543c\uff0c\u6756\u5f71\u5982\u4e91\uff0c\u5df2\u662f\u5f53\u5934\u538b\u4e0b\u3002 \u3000\u3000\u5b5f\u5c11\u521a\u4e00\u4e2a\u201c\u9ec4\u9e4a\u51b2\u9704\u201d\uff0c\u8eab\u5f62\u5e73\u5730\u62d4\u8d77\uff0c\u5251\u5c16\u5728\u6756\u5934\u8f7b\u8f7b\u4e00\u70b9\uff0c\u501f\u4ed6\u8fd9\u4e00\u6756\u7684\u731b\u529b\uff0c \u6574\u4e2a\u8eab\u5b50\u53cd\u5f39\u8d77\u6765\uff0c\u5012\u7ffb\u51fa\u6570\u4e08\u5f00\u5916\uff0c\u9f99\u8c61\u6cd5\u738b\u559d\u9053\uff1a\u201c\u54ea\u91cc\u8d70\uff1f\u201d\u5b5f\u5c11\u521a\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60 \u4ee5\u4e3a\u4f60\u5df2\u7ecf\u8d62\u4e86\u6211\u4e48\uff1f\u201d\u4e00\u4e2a\u98de\u8eab\uff0c\u659c\u7ed5\u63a0\u8fc7\uff0c\u8fce\u4e0a\u4e86\u9f99\u8c61\u6cd5\u738b\u53d1\u62db\uff0c\u6756\u5f71\u5982\u5c71\uff0c\u5251\u5149\u4f3c \u7ec3\uff0c\u767b\u65f6\u53c8\u662f\u6740\u5f97\u4e2a\u96be\u89e3\u96be\u5206\u3002 \u3000\u3000\u674e\u601d\u5357\u770b\u5f97\u624b\u91cc\u634f\u4e86\u4e00\u628a\u6c57\uff0c\u56de\u5934\u4e00\u770b\uff0c\u891a\u4e91\u5cf0\u4e0e\u8c37\u6db5\u865a\u8054\u624b\u548c\u9633\u5929\u96f7\u7684\u8fd9\u4e00\u573a\u6076\u6597\uff0c \u66f4\u662f\u4ee4\u4ed6\u89e6\u76ee\u60ca\u5fc3\uff01\u53ea\u89c1\u4e09\u6761\u4eba\u5f71\uff0c\u500f\u5408\u500f\u5206\uff0c\u8fdb\u5982\u733f\u7334\u7a9c\u679d\uff0c\u9000\u82e5\u9f99\u86c7\u75be\u8d70\uff0c\u8d77\u5982\u9e70\u96bc\u98de \u5929\uff0c\u843d\u82e5\u731b\u864e\u6251\u5730\uff0c\u65b9\u5706\u6570\u4e08\u4e4b\u5185\uff0c\u6c99\u98de\u77f3\u8d70\uff0c\u638c\u98ce\u6fc0\u8361\uff0c\u547c\u547c\u8f70\u8f70\uff0c\u58f0\u5982\u90c1\u96f7\uff01\u9976\u662f\u674e\u601d \u5357\u6b66\u529f\u8d85\u5353\uff0c\u5b9a\u529b\u575a\u5f3a\uff0c\u4e5f\u662f\u4e0d\u7981\u770b\u5f97\u80c6\u6218\u5fc3\u60ca\uff0c\u773c\u82b1\u7f2d\u4e71\uff01 \u3000\u3000\u539f\u6765\u9633\u5929\u96f7\u4e0e\u891a\u3001\u8c37\u4e8c\u4eba\u5728\u201c\u56fd\u5e08\u5e9c\u201d\u4e00\u6218\u4e4b\u540e\uff0c\u891a\u3001\u8c37\u4e8c\u4eba\u56fa\u7136\u662f\u671d\u5915\u7528\u529f\uff0c\u591a\u65b9\u8ba8 \u6559\uff0c\u82e6\u5fc3\u78e8\u7ec3\uff0c\u9633\u5929\u96f7\u4e5f\u662f\u7528\u5c3d\u5fc3\u601d\uff0c\u94bb\u7814\u5982\u4f55\u53ef\u4ee5\u514b\u654c\u5236\u80dc\u4e4b\u9053\u3001 \u3000\u3000\u672c\u6765\u4ee5\u4ed6\u4eec\u4e09\u4eba\u7684\u6b66\u529f\uff0c\u82e5\u662f\u7167\u539f\u6765\u7684\u6253\u6cd5\uff0c\u9633\u5929\u96f7\u4e5f\u8fd8\u662f\u7a0d\u80dc\u4e00\u7b79\u7684\uff0c\u4f46\u867d\u662f\u7a0d\u80dc\u4e00 \u7b79\uff0c\u672c\u8eab\u771f\u529b\u8017\u635f\u592a\u751a\uff0c\u7ed3\u679c\u4ecd\u5c06\u662f\u4e0d\u514d\u843d\u4e2a\u4e24\u8d25\u4ff1\u4f24\u3002 \u3000\u3000\u9633\u5929\u96f7\u4e0d\u6127\u662f\u4e2a\u6b66\u5b66\u5927\u5e08\uff0c\u6f5c\u5fc3\u7422\u78e8\uff0c\u5c45\u7136\u7ed9\u4ed6\u7422\u78e8\u51fa\u4e86\u4e00\u4e2a\u53ef\u4ee5\u901f\u80dc\u4e4b\u6cd5\u3002\u8fd9\u4e2a\u529e\u6cd5 \u5c31\u662f\u5404\u4e2a\u51fb\u7834\uff0c\u4e0d\u8ba9\u4ed6\u4eec\u8054\u624b\u540c\u65f6\u53d1\u51fa\u90a3\u4e00\u62db\u201c\u96f7\u7535\u4ea4\u8f70\u201d\u7684\u6740\u624b\u7edd\u62db\u3002 \u3000\u3000\u4e09\u4eba\u7528\u7684\u662f\u540c\u4e00\u6d3e\u7684\u529f\u592b\uff0c\u9633\u5929\u96f7\u662f\u5e08\u4f2f\uff0c\u6bd4\u4ed6\u4eec\u81ea\u662f\u7cbe\u719f\u5f97\u591a\uff0c\u891a\u3001\u8c37\u4e8c\u4eba\u53d1\u62db\uff0c\u4ed6 \u5df2\u6599\u5230\u4ed6\u4eec\u8981\u53d1\u7684\u662f\u54ea\u4e00\u62db\u4e86\u3002\u5c31\u5728\u891a\u4e91\u5cf0\u80a9\u5934\u5fae\u8038\uff0c\u53f3\u638c\u5c06\u8981\u5212\u51fa\u5f27\u5f62\u4e4b\u9645\uff0c\u9633\u5929\u96f7\u731b\u7684 \u5c31\u662f\u4e00\u58f0\u559d\u9053\uff1a\u201c\u96f7\u7535\u4ea4\u8f70\u201d\uff0c\u5168\u529b\u5411\u4ed6\u51fb\u53bb\uff0c\u62fc\u7740\u53d7\u8c37\u6db5\u865a\u7684\u4e00\u638c\uff0c\u4e5f\u8981\u53d6\u4ed6\u6027\u547d\u3002 \u3000\u3000\u5728\u8fd9\u6837\u7684\u60c5\u5f62\u4e4b\u4e0b\uff0c\u8c37\u6db5\u865a\u5f53\u7136\u4e0d\u80fd\u653b\u654c\uff0c\u53ea\u80fd\u6551\u53cb\uff0c\u4ed6\u4eec\u201c\u96f7\u7535\u4ea4\u8f70\u201d\u8fd9\u4e00\u62db\u5c31\u4e0d\u80fd \u540c\u65f6\u53d1\u51fa\u4e86\u3002\u4e24\u4eba\u7684\u638c\u529b\u5408\u5728\u4e00\u8d77\uff0c\u53ea\u80fd\u52c9\u5f3a\u62b5\u6321\u9633\u5929\u96f7\u5df2\u7528\u5230\u516b\u4e5d\u5206\u529b\u9053\u7684\u5929\u96f7\u529f\u3002 \u3000\u3000\u9633\u5929\u96f7\u5360\u4e86\u4e0a\u98ce\uff0c\u5927\u4e3a\u5f97\u610f\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u770b\u6765\u6211\u53ea\u987b\u6d88\u8017\u4e09\u6210\u771f\u529b\uff0c\u5927\u6982\u4e5f\u53ef\u4ee5\u51fb\u6bd9 \u4ed6\u4eec\u4e86\u3002\u9f99\u8c61\u6cd5\u738b\u5373\u4f7f\u80dc\u4e0d\u4e86\u5b5f\u5c11\u521a\uff0c\u4ea6\u662f\u51b3\u8ba1\u4e0d\u4f1a\u843d\u8d25\u3002\u53ea\u8981\u4ed6\u548c\u5b5f\u5c11\u521a\u6253\u6210\u5e73\u624b\uff0c\u5269\u4e0b \u7684\u674e\u601d\u5357\u548c\u6768\u5a49\u4e8c\u4eba\uff0c\u6211\u8db3\u53ef\u4ee5\u5bf9\u4ed8\u6709\u4f59\u3002\u201d\u6b63\u81ea\u6ee1\u809a\u5bc6\u5708\uff0c\u56fe\u8c0b\u901f\u80dc\u4e4b\u9645\uff0c\u5ffd\u89c9\u891a\u4e91\u5cf0\u7684 \u638c\u529b\u4f3c\u5b9e\u5374\u865a\uff0c\u4ed6\u521a\u731b\u4e4b\u6781\u7684\u638c\u529b\u51fb\u53bb\uff0c\u7a81\u7136\u7ed9\u891a\u4e91\u5cf0\u5378\u53bb\u4e86\u51e0\u5206\u529b\u9053\u3002\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c \u891a\u3001\u8c37\u4e8c\u4eba\u4e58\u4ed6\u4e00\u6014\u4e4b\u9645\uff0c\u5df2\u662f\u53cc\u5251\u51fa\u9798\uff0c\u5251\u4e2d\u5939\u638c\uff0c\u91cd\u53d6\u653b\u52bf\u3002 \u3000\u3000\u539f\u6765\u891a\u4e91\u5cf0\u5728\u8fd9\u5927\u534a\u4e2a\u6708\u6765\uff0c\u5f97\u5b5f\u5c11\u521a\u7684\u6089\u5fc3\u6307\u70b9\uff0c\u5df2\u662f\u9886\u609f\u4e86\u4e0d\u5c11\u4e0a\u4e58\u5185\u529f\u5fc3\u6cd5\u7684\u8bc0 \u7a8d\uff0c\u5f00\u59cb\u61c2\u5f97\u4ee5\u67d4\u514b\u521a\u7684\u95e8\u9053\u4e86\u3002\u867d\u7136\u4ed6\u7684\u5185\u529f\u8fd8\u662f\u6bd4\u4e0d\u4e0a\u9633\u5929\u96f7\uff0c\u4f46\u7a81\u7136\u95f4\u4f7f\u51fa\u65b0\u7684\u6253 \u6cd5\uff0c\u5374\u4ee4\u5f97\u9633\u5929\u96f7\u4e0d\u7981\u5403\u4e86\u4e00\u60ca\uff0c\u591a\u5c11\u6709\u70b9\u987e\u5fcc\u4e86\u3002 \u3000\u3000\u891a\u3001\u8c37\u4e8c\u4eba\u7684\u5251\u6cd5\u5f97\u81ea\u534e\u5929\u8679\u6240\u4f20\uff0c\u534e\u5929\u8679\u672c\u95e8\u7684\u5251\u6cd5\u8fdc\u8fdc\u5728\u9633\u5929\u96f7\u4e4b\u4e0a\uff0c\u800c\u4e14\u5251\u6cd5\u4e4b \u4e2d\u4e0d\u5c11\u662f\u4ed6\u81ea\u521b\u7684\u65b0\u62db\uff0c\u9633\u5929\u96f7\u89c1\u4e5f\u6ca1\u6709\u89c1\u8fc7\u7684\u3002\u8fd9\u6837\u4e00\u6765\uff0c\u5f97\u5230\u534e\u5929\u8679\u5251\u6cd5\u771f\u4f20\u7684\u891a\u3001\u8c37 \u4e8c\u4eba\uff0c\u4ee5\u5251\u4e2d\u5939\u638c\u7684\u6253\u6cd5\uff0c\u6e10\u6e10\u53c8\u53ef\u4ee5\u548c\u9633\u5929\u96f7\u6253\u6210\u5e73\u624b\u4e86\u3002 \u3000\u3000\u674e\u601d\u5357\u4e00\u76f4\u624b\u5fc3\u634f\u7740\u628a\u6c57\uff0c\u770b\u5230\u6b64\u65f6\uff0c\u65b9\u59cb\u7a0d\u7a0d\u5b9a\u4e0b\u5fc3\u795e\u3002\u6b63\u662f\uff1a \u3000\u3000\u6e05\u7406\u5e08\u95e8\u9f99\u864e\u6597\uff0c\u5288\u5929\u5d16\u4e0a\u5251\u5149\u5bd2\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b\u2014\u2014\u7b2c\u516d\u5341\u56de\u3000\u6b32\u5f97\u4ed9\u69ce\u901a\u701a\u6d77\u3000\u4e14\u770b\u8c6a\u6770\u632f\u96c4\u98ce \u6881\u7fbd\u751f\u300a\u701a\u6d77\u96c4\u98ce\u300b \u7b2c\u516d\u5341\u56de\u3000\u6b32\u5f97\u4ed9\u69ce\u901a\u701a\u6d77\u3000\u4e14\u770b\u8c6a\u6770\u632f\u96c4\u98ce \u3000\u3000\u53cc\u65b9\u5728\u8fd9\u4e00\u4e2a\u6708\u6765\uff0c\u6b66\u827a\u90fd\u6709\u8fdb\u5883\uff0c\u7ed3\u679c\u53d8\u6210\u4e86\u891a\u3001\u8c37\u4e8c\u4eba\u56fa\u7136\u662f\u4e0d\u80fd\u540c\u65f6\u53d1\u51fa\u201c\u96f7\u518d \u4ea4\u8f70\u201d\u7684\u6740\u624b\u7edd\u62db\uff0c\u4f46\u9633\u5929\u96f7\u5e0c\u56fe\u901f\u6218\u901f\u51b3\u5404\u4e2a\u51fb\u7834\u7684\u6218\u7565\uff0c\u4ea6\u662f\u4e0d\u80fd\u8fbe\u5230\u76ee\u7684\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u7a76\u7adf\u8fd8\u662f\u9633\u5929\u96f7\u7a0d\u5fae\u5360\u4e86\u4e00\u70b9\u529f\u529b\u6df1\u539a\u3001\u7ecf\u9a8c\u4e30\u5bcc\u7684\u4fbf\u5b9c\uff0c\u53cc\u65b9\u7684\u5f62\u52bf\uff0c\u7adf\u662f\u548c \u4e0a\u6b21\u5728\u201c\u56fd\u5e08\u5e9c\u201d\u4e2d\u7684\u4ea4\u624b\u5b8c\u5168\u4e00\u6837\uff0c\u9633\u5929\u96f7\u5728\u767e\u62db\u4e4b\u540e\uff0c\u53ef\u4ee5\u51fb\u6bd9\u4ed6\u4eec\uff0c\u4f46\u81ea\u5df1\u4e5f\u5fc5\u5c06\u56e0 \u4e3a\u771f\u529b\u8017\u635f\uff0c\u8fc7\u540e\u5b9a\u8981\u5927\u75c5\u4e00\u573a\u3002 \u3000\u3000\u891a\u3001\u8c37\u4e8c\u4eba\u81ea\u77e5\u201c\u6e05\u7406\u5e08\u95e8\u201d\u4e4b\u613f\uff0c\u5df2\u662f\u96be\u4ee5\u8fbe\u5230\uff0c\u552f\u6709\u62fc\u6b7b\u529b\u6218\uff0c\u9633\u5929\u96f7\u4ea6\u662f\u5fc3\u4e2d\u6697 \u6697\u53eb\u82e6\uff0c\u62c5\u5fe7\u4e24\u8d25\u4ff1\u4f24\u3002 \u3000\u3000\u674e\u601d\u5357\u770b\u5230\u891a\u3001\u8c37\u4e8c\u4eba\u518d\u5ea6\u843d\u5728\u4e0b\u98ce\uff0c\u5fc3\u60c5\u81ea\u662f\u4e0d\u7981\u53c8\u518d\u7d27\u5f20\u8d77\u6765\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u82e5\u662f \u4ed6\u4eec\u4e8c\u4eba\u652f\u6301\u4e0d\u4f4f\uff0c\u8bf4\u4e0d\u5f97\u6211\u4e5f\u53ea\u597d\u4e0d\u987e\u6c5f\u6e56\u89c4\u77e9\u4e86\u3002\u201d \u3000\u3000\u5c31\u5728\u6b64\u9645\uff0c\u5ffd\u542c\u5f97\u6768\u5a49\u53eb\u9053\uff1a\u201c\u5b5f\u59d0\u59d0\uff0c\u5c60\u59d0\u59d0\uff0c\u4e25\u59d0\u59d0\uff0c\u4f60\u4eec\u6765\u5f97\u6b63\u597d\uff01\u5357\u54e5\uff0c\u4f60\u8fc7 \u6765\u5440\uff01\u201d \u3000\u3000\u539f\u6765\u5b5f\u660e\u971e\u3001\u5c60\u51e4\u3001\u4e25\u70f7\u4e09\u4eba\uff0c\u6b64\u65f6\u5df2\u4ece\u5c71\u9876\u4e0a\u8dd1\u4e0b\u6765\u4e86\u3002\u674e\u601d\u5357\u5168\u795e\u8d2f\u6ce8\u7684\u89c2\u6218\uff0c\u5bf9 \u5979\u4eec\u7684\u6765\u5230\uff0c\u7adf\u4f3c\u89c6\u800c\u4e0d\u89c1\uff0c\u542c\u800c\u4e0d\u95fb\u3002 \u3000\u3000\u76f4\u5230\u6768\u5a49\u5728\u4ed6\u8eab\u8fb9\u5927\u58f0\u53eb\u4ed6\uff0c\u674e\u601d\u5357\u65b9\u59cb\u5982\u68a6\u521d\u89c9\uff0c\u56de\u8fc7\u5934\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u554a\uff0c\u4f60\u4eec\u90fd\u6765 \u4e86\uff01\u53ef\u662f\u891a\u5927\u54e5\u548c\u8c37\u5927\u54e5\u4ed6\u4eec\u8981\u4eb2\u624b\u6e05\u7406\u5e08\u95e8\u2014\u2014\u201d \u3000\u3000\u5b5f\u660e\u971e\u9053\uff1a\u201c\u548c\u8fd9\u8001\u8d3c\u8bb2\u4ec0\u4e48\u6c5f\u6e56\u89c4\u77e9\uff1f\u201d\u5c60\u51e4\u66f4\u662f\u6c14\u607c\uff0c\u53eb\u9053\uff1a\u201c\u9633\u5929\u96f7\u4e0e\u6211\u6709\u6740\u7236 \u4e4b\u4ec7\uff0c\u5c82\u80fd\u4e0d\u62a5\uff01\u201d\u5530\u5530\u4e24\u58f0\uff0c\u4e24\u4eba\u540c\u65f6\u62d4\u5251\u51fa\u9798\u3002 \u3000\u3000\u9633\u5929\u96f7\u563f\u563f\u51b7\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4f60\u4eec\u5c31\u90fd\u6765\u5427\uff0c\u9633\u67d0\u672c\u6765\u5c31\u4e0d\u6253\u7b97\u6d3b\u7740\u4e0b\u5c71\uff0c\u6b63\u597d\u627e \u51e0\u4e2a\u966a\u846c\uff01\u201d \u3000\u3000\u891a\u4e91\u5cf0\u5fc3\u5934\u4e00\u51db\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u5c60\u59d1\u5a18\uff0c\u8bf7\u7a0d\u5f85\u4e9b\u65f6\uff0c\u5f85\u6211\u4eec\u5206\u51fa\u80dc\u8d1f\uff0c\u4f60\u518d\u548c\u8fd9\u8001\u8d3c \u7b97\u5e10\u4e0d\u8fdf\u3002\u660e\u971e\uff0c\u8fd9\u662f\u6211\u672c\u95e8\u7684\u4e8b\u60c5\uff0c\u8bf7\u4f60\u4e0d\u8981\u63d2\u624b\uff01\u201d \u3000\u3000\u891a\u4e91\u5cf0\u4e0d\u8bb8\u5979\u4eec\u63d2\u624b\uff0c\u4e00\u6765\u56fa\u7136\u662f\u56e0\u4e3a\u8981\u9075\u5b88\u5e08\u95e8\u6212\u5f8b\uff0c\u4e0d\u613f\u501f\u52a9\u5916\u4eba\u526a\u9664\u53db\u5f92\uff1b\u4e8c\u6765 \u4e5f\u662f\u6015\u5979\u4eec\u4f24\u5728\u9633\u5929\u96f7\u7684\u5929\u96f7\u529f\u4e4b\u4e0b\uff0c\u90a3\u65f6\u7eb5\u7136\u6740\u4e86\u4ed6\uff0c\u81ea\u5df1\u4ea6\u8981\u9057\u61be\u7ec8\u751f\u4e86\u3002\u662f\u4ee5\u4ed6\u5b81\u613f \u4e0e\u9633\u5929\u96f7\u62fc\u4e2a\u4e24\u8d25\u4ff1\u4f24\uff0c\u90a3\u65f6\u5c60\u51e4\u51fa\u624b\uff0c\u624d\u662f\u65f6\u673a\u3002 \u3000\u3000\u674e\u601d\u5357\u6697\u81ea\u5bfb\u601d\uff1a\u201c\u891a\u3001\u8c37\u4e8c\u4eba\u6709\u8a00\u5728\u5148\uff0c\u6211\u82e6\u63d2\u624b\uff0c\u53ef\u574f\u4ed6\u4eec\u597d\u6c49\u5b50\u7684\u58f0\u540d\uff01\u53ef\u662f\u82e5 \u4e0d\u63d2\u624b\uff0c\u53ea\u6015\u4ed6\u4eec\u4e8c\u4eba\u7ec8\u662f\u96be\u9003\u4e00\u8d25\u3002\u4f55\u51b5\u6728\u534e\u9ece\u53c8\u5df2\u9003\u4e86\u56de\u53bb\uff0c\u9633\u5929\u96f7\u7684\u6551\u5175\u968f\u65f6\u4f1a\u5230\uff0c \u518d\u4e0d\u51fa\u624b\uff0c\u6050\u6015\u5c31\u8981\u5750\u5931\u65f6\u673a\u4e86\u3002\u201d \u3000\u3000\u6b63\u5728\u8e0c\u8e87\u4e4b\u9645\uff0c\u5ffd\u542c\u5f97\u4e24\u4e2a\u4eba\u7684\u58f0\u97f3\u540c\u65f6\u8bf4\u9053\uff1a\u201c\u9633\u5929\u96f7\uff01\u4f60\u7741\u5f00\u773c\u775b\u770b\u770b\uff0c\u6211\u4eec\u662f \u8c01\uff1f\u6211\u4eec\u603b\u6709\u8d44\u683c\u6e05\u7406\u95e8\u6237\u3002\u201d \u3000\u3000\u8bdd\u58f0\u4f20\u51fa\uff0c\u8fd9\u4e24\u4eba\u7acb\u5373\u540c\u65f6\u73b0\u51fa\u8eab\u5f62\u3002\u891a\u3001\u8c37\u4e8c\u4eba\u53c8\u60ca\u53c8\u559c\uff0c\u539f\u6765\u8fd9\u4e24\u4e2a\u4eba\u6b63\u662f\u4ed6\u4eec\u7684 \u5e08\u7236\u534e\u5929\u8679\u548c\u803f\u5929\u98ce\u3002 \u3000\u3000\u9633\u5929\u96f7\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u534e\u5929\u8679\u51fa\u73b0\u5c1a\u4e0d\u8db3\u4e3a\u9a87\uff0c\u5982\u4eca\u4ed6\u610f\u60f3\u4e0d\u5230\u7684\u662f\u803f\u5929\u98ce\u8fd8\u6d3b\u5728\u4e16 \u4e0a\u3002 \u3000\u3000\u534e\u3001\u803f\u4e8c\u4eba\u540c\u65f6\u53eb\u9053\uff1a\u201c\u4e91\u5cf0\u9000\u4e0b\uff01\u201d\u201c\u6db5\u865a\u9000\u4e0b\uff01\u201d \u3000\u3000\u9633\u5929\u96f7\u6697\u81ea\u60f3\u9053\uff1a\u201c\u534e\u5929\u8679\u7684\u672c\u9886\u8fdc\u4e0d\u53ca\u6211\uff0c\u803f\u5929\u98ce\u90a3\u5e74\u7ed9\u6211\u6253\u5f97\u91cd\u4f24\uff0c\u5982\u4eca\u867d\u7136\u8fc7\u4e86 \u4e8c\u5341\u5e74\uff0c\u4ed6\u5f97\u4ee5\u4fa5\u5e78\u4e0d\u6b7b\uff0c\u4e5f\u672a\u5fc5\u5c31\u80fd\u6062\u590d\u5f97\u4e86\u539f\u6765\u7684\u529f\u529b\u3002\u4ed6\u4eec\u4e8c\u4eba\u8054\u624b\uff0c\u53ea\u6015\u8fd8\u672a\u5fc5\u6bd4 \u5f97\u4e0a\u6211\u548c\u4ed6\u4eec\u7684\u5f92\u5f1f\u5462\u3002\u6211\u53ea\u987b\u518d\u652f\u6301\u534a\u4e2a\u65f6\u8fb0\uff0c\u6551\u5175\u5927\u6982\u4e5f\u53ef\u4ee5\u6765\u5230\u4e86\u3002\u201d\u5982\u6b64\u4e00\u60f3\uff0c\u5fc3 \u4e2d\u602f\u610f\u51cf\u4e86\u51e0\u5206\uff0c\u72de\u7b11\u8bf4\u9053\uff1a\u201c\u597d\u5440\uff0c\u4f60\u4eec\u8981\u6e05\u7406\u5e08\u95e8\uff0c\u6211\u4e5f\u6b63\u8981\u60e9\u5904\u4f60\u8fd9\u4e24\u4e2a\u80c6\u6562\u4e0d\u9075\u638c \u95e8\u4e4b\u547d\u7684\u5e08\u5f1f\uff01\u6709\u80c6\u7684\u4e0d\u8981\u5916\u4eba\u63d2\u624b\uff0c\u54b1\u4eec\u5c31\u6765\u5206\u4e2a\u5f3a\u5f31\u5b58\u4ea1\uff01\u201d \u3000\u3000\u803f\u5929\u98ce\u89c1\u4e86\u5e73\u751f\u6700\u75db\u6068\u7684\u5927\u4ec7\u4eba\uff0c\u773c\u4e2d\u5c31\u50cf\u8981\u55b7\u51fa\u706b\u6765\uff0c\u8fc8\u6b65\u5411\u524d\uff0c\u559d\u9053\uff1a\u201c\u4e8c\u5341\u5e74 \u524d\uff0c\u4f60\u5bb3\u6b7b\u6211\u7684\u59bb\u5b50\uff0c\u903c\u6b7b\u6211\u7684\u6bcd\u4eb2\uff0c\u6253\u5f97\u6211\u51e0\u4e4e\u53d8\u6210\u6b8b\u5e9f\uff0c\u4f60\u8fd8\u8bb0\u5f97\u4e48\uff01\u201d \u3000\u3000\u9633\u5929\u96f7\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u5f97\u4ee5\u4e0d\u6b7b\uff0c\u5df2\u5c5e\u4fa5\u5e78\uff0c\u5e9f\u8bdd\u5c11\u8bf4\uff0c\u4f60\u8981\u5982\u4f55\uff1f\u201d \u3000\u3000\u803f\u5929\u98ce\u559d\u9053\uff1a\u201c\u6211\u8981\u5556\u4f60\u7684\u8089\uff0c\u5bdd\u4f60\u7684\u76ae\uff0c\u4e3a\u6bcd\u4eb2\u3001\u59bb\u5b50\u62a5\u4ec7\uff01\u534e\u5e08\u5144\uff0c\u8bf7\u4f60\u8ba9\u6211\u5148 \u4e0a\u3002\u201d\u8bf4\u5230\u4e00\u4e2a\u201c\u4e0a\u201d\u5b57\uff0c\u9e70\u7fd4\u96bc\u523a\u4e00\u822c\u5c31\u8df3\u6251\u8fc7\u53bb\uff01 \u3000\u3000\u9633\u5929\u96f7\u89c1\u4ed6\u53ea\u662f\u4e00\u4e2a\u4eba\u51b2\u6251\u8fc7\u6765\uff0c\u66f4\u4e0d\u653e\u5728\u5fc3\u4e0a\uff0c\u559d\u58f0\uff1a\u201c\u6765\u5f97\u597d\uff01\u201d\u53cc\u638c\u5f00\u51fa\uff0c\u5212\u4e86 \u4e00\u9053\u5706\u5f27\uff0c\u767b\u65f6\u5c31\u4f7f\u51fa\u4e86\u5929\u96f7\u529f\u7684\u6740\u624b\uff01 \u3000\u3000\u9633\u5927\u96f7\u6ee1\u4ee5\u4e3a\u8fd9\u4e00\u62db\u201c\u96f7\u7535\u4ea4\u8f70\u201d\u4e00\u51fb\u4e4b\u4e0b\uff0c\u5c31\u53ef\u4ee5\u8981\u4e86\u803f\u5929\u98ce\u7684\u6027\u547d\uff0c\u54ea\u77e5\u56db\u638c\u76f8 \u4ea4\uff0c\u53ea\u89c1\u803f\u5929\u98ce\u6643\u4e86\u4e00\u6643\uff0c\u7acb\u5373\u659c\u8eab\u63a0\u51fa\uff0c\u5c45\u7136\u9762\u4e0d\u7ea2\uff0c\u6c14\u4e0d\u5598\uff0c\u795e\u8272\u5982\u5e38\uff01 \u3000\u3000\u539f\u6765\u803f\u5929\u98ce\u82e6\u7ec3\u4e8c\u5341\u5e74\uff0c\u6700\u8fd1\u65b9\u59cb\u5927\u529f\u544a\u6210\uff0c\u7ec3\u6210\u4e86\u5378\u529b\u5316\u52b2\u7684\u4e0a\u4e58\u5185\u529f\uff0c\u6070\u597d\u53ef\u4ee5\u8865 \u672c\u8eab\u5185\u529b\u7684\u4e0d\u8db3\u3002 \u3000\u3000\u9633\u5929\u96f7\u731b\u7684\u4e00\u638c\u5288\u53bb\uff0c\u53ea\u89c9\u5bf9\u65b9\u67d4\u82e5\u65e0\u529b\uff0c\u4f46\u5374\u4f24\u4e0d\u7740\u5bf9\u65b9\uff0c\u5fc3\u4e2d\u4ea6\u81ea\u4e00\u51db\uff0c\u5f53\u4e0b\u53cc\u638c \u4ea4\u9519\uff0c\u5de6\u638c\u659c\u62cd\uff0c\u53f3\u638c\u4e00\u5e26\uff0c\u628a\u803f\u5929\u98ce\u90a3\u80a1\u7c98\u52b2\u5316\u5f00\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u606d\u559c\u4f60\u7ec3\u6210\u4e86\u4e00\u95e8\u65b0\u7684 \u7edd\u6280\uff0c\u53ef\u60dc\u8981\u80dc\u6211\u53ea\u6015\u8fd8\u5f97\u518d\u7ec3\u5341\u5e74\u5427\uff01\u201d \u3000\u3000\u9633\u5929\u96f7\u7684\u8bdd\u867d\u662f\u6709\u70b9\u5938\u5927\uff0c\u5012\u4e5f\u4e0d\u7b97\u592a\u8fc7\u72c2\u5984\uff0c\u6b66\u5b66\u4e4b\u4e2d\u867d\u6709\u4ee5\u67d4\u514b\u521a\u4e4b\u8bf4\uff0c\u4f46\u90a3\u662f\u5fc5 \u987b\u53cc\u65b9\u90fd\u7ec3\u5230\u4e86\u6781\u9ad8\u5883\u754c\u4e4b\u65f6\u65b9\u59cb\u5982\u6b64\u3002\u5982\u4eca\u9633\u5929\u96f7\u6bd5\u7adf\u8fd8\u662f\u529f\u529b\u7a0d\u80dc\u4e00\u7b79\uff0c\u803f\u5929\u98ce\u5316\u89e3\u5f97 \u4e86\u5730\u7684\u5929\u96f7\u529f\uff0c\u8981\u60f3\u53cd\u51fb\uff0c\u4ecd\u662f\u672a\u80fd\u3002 \u3000\u3000\u53cc\u65b9\u8fd9\u4e00\u4ea4\u624b\uff0c\u53ea\u89c1\u8863\u8882\u98d8\u98d8\uff0c\u4e1c\u632a\u897f\u95ea\uff0c\u4e00\u4e2a\u53cc\u81c2\u5ef6\u6005\uff0c\u5f3a\u653b\u731b\u6251\u4e0a\u3002\u803f\u5929\u98ce\u5c31\u50cf\u6012 \u6d77\u4e2d\u7684\u5c0f\u821f\u4e00\u6837\uff0c\u968f\u7740\u6ce2\u6d6a\u8d77\u4f0f\uff01\u53ef\u662f\u4f9d\u7136\u80fd\u591f\u64cd\u7eb5\u81ea\u5982\uff0c\u95ea\u8fc7\u4e86\u4e00\u6d6a\u53c8\u662f\u4e00\u6d6a\uff01 \u3000\u3000\u891a\u3001\u8c37\u4e8c\u4eba\u770b\u5f97\u4e00\u9897\u5fc3\u90fd\u597d\u50cf\u8981\u4ece\u53e3\u8154\u91cc\u8df3\u51fa\u6765\uff0c\u770b\u4e86\u4e00\u4f1a\uff0c\u8fd9\u624d\u7a0d\u7a0d\u5b9a\u5f97\u4e0b\u5fc3\u795e\u3002\u891a \u4e91\u5cf0\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u539f\u6765\u4e0a\u4e58\u5185\u529f\u7684\u539f\u7406\uff0c\u90fd\u662f\u76f8\u901a\u7684\uff0c\u803f\u5e08\u53d4\u6240\u7528\u7684\u4ee5\u67d4\u514b\u521a\u4e4b\u6cd5\u548c\u5b5f\u5927\u4fa0 \u6559\u6211\u7684\u7adf\u6709\u8bb8\u591a\u53ef\u4ee5\u6697\u5408\u4e4b\u5904\u3002\u201d \u3000\u3000\u534e\u5929\u8679\u559d\u9053\uff1a\u201c\u9633\u5929\u96f7\uff0c\u4f60\u903c\u5f97\u6211\u4e8c\u5341\u5e74\u6765\u85cf\u533f\u6df1\u5c71\uff0c\u8fd8\u8981\u9677\u6211\u4e8e\u4e0d\u4e49\uff0c\u8fd9\u7b14\u5e10\u4eca\u65e5\u6211 \u4e5f\u662f\u975e\u7b97\u4e0d\u53ef\uff01\u201d\u4e00\u58f0\u201c\u770b\u5251\u201d\u5530\u7684\u4fbf\u5411\u9633\u5929\u96f7\u523a\u53bb\u3002 \u3000\u3000\u534e\u5927\u8679\u5728\u540c\u95e8\u4e4b\u4e2d\u6700\u7cbe\u4e8e\u5251\u6cd5\uff0c\u7ecf\u8fc7\u4e86\u4e8c\u5341\u5e74\u7684\u6f5c\u5fc3\u82e6\u7ec3\uff0c\u7cbe\u76ca\u6c42\u7cbe\uff0c\u4e0d\u4f46\u5c3d\u7aa5\u672c\u95e8\u5251 \u6cd5\u7684\u79d8\u5965\uff0c\u800c\u4e14\u81ea\u521b\u4e86\u8bb8\u591a\u65b0\u62db\u3002\u4e00\u5251\u523a\u51fa\uff0c\u8f7b\u7075\u7fd4\u52a8\uff0c\u4f3c\u865a\u4f3c\u5b9e\uff0c\u5b9c\u5b88\u5b9c\u653b\u3002\u9633\u5929\u96f7\u4f7f\u51fa \u6d51\u8eab\u672c\u9886\uff0c\u594b\u529b\u89e3\u4e86\u6570\u5341\u62db\uff0c\u4e0d\u89c9\u5df2\u662f\u6c57\u5982\u96e8\u4e0b\u3002 \u3000\u3000\u674e\u601d\u5357\u89c1\u534e\u3001\u803f\u4e8c\u4eba\u5df1\u662f\u667a\u73e0\u5728\u63e1\uff0c\u80dc\u5238\u7a33\u64cd\uff0c\u8fd9\u624d\u653e\u4e0b\u4e86\u5fc3\uff0c\u8d70\u8fc7\u53bb\u548c\u5c60\u51e4\u7b49\u4eba\u62db \u547c\u3002 \u3000\u3000\u5c60\u51e4\u8bf4\u9053\uff1a\u201c\u5e78\u4e8f\u534e\u8001\u524d\u8f88\u8bc6\u5f97\u660e\u971e\u59d0\u59d0\uff0c\u6211\u4eec\u4e2d\u9014\u78b0\u4e0a\uff0c\u8fd9\u624d\u4e0d\u81f3\u4e8e\u8bef\u6295\u7f57\u7f51\u3002\u201d \u3000\u3000\u674e\u601d\u5357\u7b11\u9053\uff1a\u201c\u539f\u6765\u4f60\u4eec\u662f\u78b0\u4e0a\u4e86\u8fd9\u4e24\u4f4d\u8001\u524d\u8f88\uff0c\u602a\u4e0d\u5f97\u6211\u4eec\u731c\u6765\u731c\u53bb\uff0c\u90fd\u6ca1\u6709\u731c \u7740\u3002\u201d \u3000\u3000\u539f\u6765\u803f\u5929\u98ce\u7ec3\u6210\u4e86\u4e0a\u4e58\u5185\u529f\u4e4b\u540e\uff0c\u5230\u5317\u8292\u5c71\u627e\u7740\u4e86\u5e08\u5144\uff0c\u4e24\u4eba\u4fbf\u5373\u5077\u5165\u5927\u90fd\uff0c\u51c6\u5907\u884c\u523a \u9633\u5929\u96f7\u3002\u4e0d\u5de7\u4ed6\u4eec\u6765\u5230\u4e4b\u65f6\uff0c\u6b63\u662f\u5728\u5b5f\u5c11\u521a\u3001\u97e9\u5927\u7ef4\uff0c\u9646\u6606\u4ed1\u7b49\u4eba\u5927\u95f9\u201c\u56fd\u5e08\u5e9c\u201d\u4e4b\u540e\u7684\u7b2c \u4e8c\u5929\uff0c\u4eac\u57ce\u6212\u5907\u68ee\u4e25\uff0c\u201c\u56fd\u5e08\u5e9c\u201d\u90a3\u662f\u66f4\u4e0d\u7528\u8bf4\u4e86\u3002 \u3000\u3000\u597d\u5728\u534e\u5929\u8679\u5728\u5927\u90fd\u6709\u51e0\u4e2a\u53ef\u6258\u8179\u5fc3\u7684\u8001\u670b\u53cb\uff0c\u7ed9\u4ed6\u4eec\u63a9\u62a4\uff0c\u5e76\u4e14\u6253\u542c\u5230\u4e86\u9633\u5929\u96f7\u5c06\u8981 \u201c\u62a5\u8058\u201d\u548c\u6797\u7684\u6d88\u606f\u3002\u534e\u3001\u803f\u4e8c\u4eba\u89c1\u5728\u91d1\u4eac\u96be\u6709\u4f5c\u4e3a\uff0c\u8fd9\u624d\u5b9a\u4e0b\u4e86\u5728\u5288\u5929\u5d16\u4f0f\u51fb\u7684\u8ba1\u5212\uff0c\u4ed6 \u4eec\u662f\u51fa\u4e86\u5927\u90fd\u4e4b\u540e\uff0c\u78b0\u4e0a\u4e86\u5c60\u51e4\u3001\u5b5f\u660e\u971e\u5979\u4eec\u7684\u3002 \u3000\u3000\u5b5f\u660e\u971e\u7b11\u9053\uff1a\u201c\u4e0d\u51fa\u767e\u62db\uff0c\u4f60\u4eec\u7684\u5e08\u7236\u4e00\u5b9a\u6740\u5f97\u4e86\u9633\u5929\u96f7\u8fd9\u4e2a\u8001\u8d3c\uff0c\u4f60\u4eec\u8fd8\u5bb3\u6015\u4ec0 \u4e48\uff1f\u201d\u891a\u4e91\u5cf0\u548c\u8c37\u6db5\u865a\u56de\u8fc7\u5934\u6765\uff0c\u7b11\u9053\uff1a\u201c\u6211\u4eec\u4e0d\u662f\u62c5\u5fe7\uff0c\u4f46\u8fd9\u6837\u7cbe\u91c7\u7684\u6076\u6597\uff0c\u4e0d\u770b\u5c82\u975e\u5931 \u4e86\u773c\u798f\uff1f\u201d \u3000\u3000\u6768\u5a49\u7b11\u9053\uff1a\u201c\u8c37\u5927\u54e5\uff0c\u4e25\u59d0\u59d0\u662f\u7279\u5730\u6765\u770b\u4f60\u7684\uff0c\u4f60\u4e0d\u770b\u5979\uff0c\u5374\u53ea\u987e\u7740\u770b\u534e\u8001\u524d\u8f88\u7684\u5251 \u6cd5\uff0c\u8bf4\u5f97\u8fc7\u53bb\u5417\uff1f\u201d \u3000\u3000\u4e25\u70f7\u8138\u6cdb\u6843\u82b1\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u522b\u542c\u5979\u778e\u56bc\u820c\u5934\u3002\u201d\u6768\u5a49\u7b11\u9053\uff1a\u201c\u4f60\u77e5\u9053\u4ed6\u4eec\u5927\u95f9\u56fd\u5e08\u5e9c\u7684 \u6d88\u606f\u4e4b\u540e\uff0c\u90a3\u526f\u62c5\u5fe7\u7684\u795e\u6001\uff0c\u8981\u4e0d\u8981\u6211\u5bf9\u4ed6\u4eec\u4ed4\u7ec6\u523b\u5212\u4e86\u3002\u201d \u3000\u3000\u8c37\u6db5\u865a\u7b11\u9053\uff1a\u201c\u70f7\u59b9\uff0c\u6211\u771f\u60f3\u4e0d\u5230\u4f60\u4e5f\u4f1a\u6765\u7684\u3002\u6211\u6709\u8bb8\u591a\u4e8b\u60c5\u8981\u544a\u8bc9\u4f60\u5462\uff0c\u6162\u6162\u8bf4\u7ed9\u4f60 \u542c\u5427\u3002\u201d\u4ed6\u4eec\u662f\u7ecf\u8fc7\u4e86\u65e0\u6570\u6298\u78e8\u7684\u7231\u4fa3\uff0c\u6b64\u65f6\u5728\u8fd9\u9669\u5730\u91cd\u9022\uff0c\u773c\u524d\u7684\u5251\u5f71\u5200\u5149\uff0c\u4e5f\u5316\u6210\u4e86\u67d4 \u60c5\u871c\u610f\u4e86\u3002 \u3000\u3000\u9633\u5929\u96f7\u9690\u9690\u542c\u5f97\u5c71\u4e0b\u6709\u519b\u9a6c\u9a70\u9aa4\u7684\u58f0\u97f3\uff0c\u5fc3\u5934\u4e00\u559c\uff0c\u60f3\u9053\uff1a\u201c\u53ea\u987b\u6211\u652f\u6301\u5f97\u5230\u6551\u5175\u6765 \u5230\uff0c\u5c31\u53ef\u53cd\u8d25\u4e3a\u80dc\uff01\u201d\u731b\u7684\u4e00\u54ac\u820c\u5c16\uff0c\u55b7\u51fa\u4e00\u53e3\u9c9c\u8840\uff0c\u5c06\u6b8b\u4f59\u7684\u7cbe\u529b\u51dd\u805a\u8d77\u6765\uff0c\u63a5\u8fde\u5288\u51fa\u4e86 \u8fde\u73af\u4e03\u638c\uff01\u5929\u96f7\u529f\u7684\u5a01\u529b\u7adf\u7136\u66f4\u80dc\u4ece\u524d\uff01 \u3000\u3000\u9f99\u8c61\u6cd5\u738b\u4e0e\u5b5f\u5c11\u521a\u6597\u5f97\u66f4\u662f\u9a87\u4eba\u5fc3\u9b44\u3002\u53ea\u89c1\u9f99\u8c61\u6cd5\u738b\u62a1\u8d77\u7897\u53e3\u822c\u7c97\u5927\u7684\u7985\u6756\uff0c\u6a2a\u6311\u76f4 \u683c\uff0c\u5de6\u6321\u53f3\u67b6\uff0c\u4e0a\u4e0b\u7ffb\u98de\uff0c\u5b9b\u5982\u4e00\u6761\u6bd2\u9f99\uff0c\u5f20\u7259\u821e\u722a\uff0c\u547c\u547c\u8f70\u8f70\uff0c\u56db\u9762\u516b\u65b9\uff0c\u90fd\u662f\u6756\u5f71\uff0c\u771f \u6709\u6392\u5c71\u5012\u6d77\u4e4b\u52bf\uff0c\u98ce\u96f7\u5939\u51fb\u4e4b\u5a01\uff01 \u3000\u3000\u5b5f\u5c11\u521a\u7684\u4e00\u67c4\u957f\u5251\uff0c\u6307\u4e1c\u6253\u897f\uff0c\u6307\u5357\u6253\u5317\uff0c\u98d2\u98d2\u8fde\u58f0\uff0c\u7adf\u4f3c\u95ea\u8d77\u5343\u767e\u9053\u7cbe\u8292\u51b7\u7535\uff0c\u770b\u5f97 \u4eba\u773c\u82b1\u64a9\u4e71\uff01\u53cc\u65b9\u90fd\u662f\u91c7\u53d6\u653b\u52bf\uff0c\u5251\u5149\u6756\u5f71\uff0c\u500f\u5408\u500f\u5206\uff0c\u8c01\u4eba\u7a0d\u6709\u4e0d\u614e\uff0c\u4fbf\u6709\u8840\u6e85\u5c18\u57c3\u4e4b \u9669\uff01 \u3000\u3000\u6b64\u65f6\u90a3\u4e00\u8425\u8499\u53e4\u9a91\u5175\u7684\u5148\u5934\u90e8\u961f\uff0c\u5df2\u6709\u6570\u5341\u9a91\u51b2\u4e0a\u5c71\u6765\uff0c\u5b5f\u660e\u971e\u9053\uff1a\u201c\u76df\u4e3b\uff0c\u5f62\u52bf\u7d27 \u8feb\uff0c\u4e0d\u8d76\u5feb\u6b7c\u706d\u5f3a\u654c\uff0c\u54b1\u4eec\u5c31\u6709\u88ab\u9677\u91cd\u56f4\u4e4b\u9669\uff01\u4e91\u5cf0\uff0c\u4f60\u5148\u4e0a\u53bb\u52a9\u5e08\u7236\u4e00\u81c2\u4e4b\u529b\u5427\uff01\u201d \u3000\u3000\u891a\u4e91\u5cf0\u89c1\u9633\u5929\u96f7\u53e3\u55b7\u8840\u4e4b\u540e\uff0c\u5929\u96f7\u529f\u7684\u5a01\u529b\u7a81\u7136\u53c8\u518d\u52a0\u5f3a\uff0c\u4e5f\u662f\u5403\u60ca\u4e0d\u5c0f\uff0c\u8bf4\u9053\uff1a\u201c\u8c37 \u5e08\u5f1f\uff0c\u54b1\u4eec\u4e0a\u53bb\u7ed9\u4ed6\u4e09\u62db\u96f7\u7535\u4ea4\u8f70\uff01\u201d \u3000\u3000\u891a\u3001\u8c37\u4e8c\u4eba\u521a\u8981\u4e0a\u524d\uff0c\u5ffd\u542c\u5f97\u534e\u5929\u8679\u559d\u58f0\u201c\u7740\uff01\u201d\u803f\u5929\u98ce\u53eb\u9053\uff1a\u201c\u7528\u4e0d\u7740\u4f60\u4eec\uff01\u201d\u5c31\u5728 \u4e24\u4eba\u53eb\u559d\u58f0\u4e2d\uff0c\u53ea\u89c1\u767d\u5149\u4e00\u95ea\uff0c\u534e\u5929\u8679\u5530\u7684\u4e00\u5251\uff0c\u5df2\u662f\u523a\u7740\u4e86\u9633\u5929\u96f7\u819d\u76d6\u7684\u201c\u73af\u8df3\u7a74\u201d\uff0c\u9633 \u5929\u96f7\u8df3\u8d77\u4e00\u4e08\u591a\u9ad8\uff0c\u53cc\u638c\u731b\u5730\u51fb\u4e0b\uff0c\u803f\u5929\u98ce\u4e00\u638c\u201c\u96c1\u843d\u5e73\u6c99\u201d\uff0c\u559d\u58f0\u201c\u53bb\uff01\u201d\u638c\u52bf\u82d1\u5982\u96c1\u7fc5 \u659c\u63a0\uff0c\u4e0e\u9633\u5929\u96f7\u7684\u5de6\u638c\u78b0\u4e2a\u6b63\u770b\uff0c\u4e00\u6452\u4e00\u63a8\uff0c\u9633\u5929\u96f7\u5e9e\u5927\u7684\u8eab\u8eaf\u98de\u51fa\u4e86\u6570\u4e08\u5f00\u5916\uff01 \u3000\u3000\u539f\u6765\u9633\u5929\u96f7\u65e9\u5df2\u6597\u5230\u4e86\u6cb9\u5c3d\u706f\u67af\u7684\u5883\u5730\uff0c\u4ed6\u54ac\u7834\u820c\u5934\u55b7\u51fa\u9c9c\u8840\uff0c\u53ea\u662f\u628a\u6b8b\u4f59\u7684\u7cbe\u529b\u51dd\u805a \u8d77\u6765\uff0c\u4f5c\u6700\u540e\u7684\u640f\u6597\u800c\u5df2\u3002\u7b49\u4e8e\u662f\u75c5\u4eba\u7684\u201c\u56de\u5149\u8fd4\u7167\u201d\uff0c\u53ea\u53ef\u4ee5\u82df\u5ef6\u6b8b\u5598\uff0c\u51b3\u4e0d\u80fd\u6301\u4e45\u7684 \u4e86\u3002 \u3000\u3000\u9633\u5929\u96f7\u201c\u73af\u8df3\u7a74\u201d\u4e2d\u5251\uff0c\u771f\u6c14\u6da3\u6563\uff0c\u7ed9\u803f\u5929\u98ce\u7528\u4ee5\u67d4\u514b\u521a\u7684\u4e0a\u4e58\u5185\u529f\uff0c\u4e00\u638c\u5c06\u4ed6\u63a8\u51fa\u6570 \u4e08\u5f00\u5916\uff0c\u843d\u4e0b\u5730\u65f6\uff0c\u6070\u597d\u5728\u5c60\u51e4\u7684\u8eab\u65c1\u3002 \u3000\u3000\u5c60\u51e4\u559d\u9053\uff1a\u201c\u8001\u8d3c\u5f80\u54ea\u91cc\u8dd1\uff01\u201d\u9633\u5929\u96f7\u5927\u543c\u4e00\u58f0\uff0c\u72de\u7b11\u559d\u9053\uff1a\u201c\u81ed\u4e2b\u5934\u4e5f\u6562\u6765\u6b3a\u8d1f \u6211\uff01\u201d\u5f53\u771f\u5c31\u4f3c\u4e00\u53ea\u53d7\u4f24\u7684\u91ce\u517d\u4e00\u822c\uff0c\u53cc\u81c2\u7b95\u5f20\uff0c\u5411\u5c60\u51e4\u5f53\u5934\u6251\u4e0b\uff01 \u3000\u3000\u6768\u5a49\u53eb\u9053\uff1a\u201c\u4e0d\u597d\uff01\u201d\u4e0e\u674e\u601d\u5357\u53cc\u5251\u9f50\u4e0a\uff0c\u58f0\u72b9\u672a\u4e86\uff0c\u4eba\u672a\u8d76\u5230\uff0c\u53ea\u542c\u5f97\u6495\u5fc3\u88c2\u80ba\u7684\u60e8 \u547c\uff0c\u53ea\u89c1\u9633\u5929\u96f7\u4e00\u4e2a\u5012\u5934\u7b4b\u6597\uff0c\u5728\u7a7a\u4e2d\u7ffb\u4e86\u4e24\u7ffb\uff0c\u6070\u597d\u843d\u5728\u6073\u5d16\u4e4b\u4e0a\uff0c\u811a\u5c16\u521a\u521a\u6cbe\u5730\uff0c\u7a81\u7136 \u4e00\u4e2a\u8e09\u8dc4\uff0c\u5c31\u8dcc\u4e0b\u53bb\u4e86\uff01\u8fc7\u4e86\u7247\u523b\uff0c\u8c37\u5e95\u4f20\u6765\u4e00\u58f0\u5c16\u53eb\uff0c\u8ddf\u7740\u4fbf\u542c\u5f97\u4f17\u5175\u58eb\u7684\u54d7\u7136\u60ca\u547c\uff1a \u201c\u9633\u56fd\u5e08\uff01\u9633\u56fd\u5e08\uff01\u201d\u201c\u554a\uff0c\u9633\u56fd\u5e08\u6b7b\u4e86\uff01\u201d\u60ca\u547c\u4e4b\u58f0\uff0c\u6ce2\u6d6a\u822c\u5730\u4f20\u51fa\u53bb\uff0c\u78b0\u7740\u56db\u8fb9\u5c71\u58c1\uff0c \u56de\u58f0\u4e45\u4e45\u4e0d\u7edd\uff01 \u3000\u3000\u534e\u3001\u803f\u4e8c\u4eba\u6740\u4e86\u80cc\u53db\u5e08\u95e8\u7684\u5e08\u5144\uff0c\u89c1\u4e86\u5982\u6b64\u60e8\u5389\u7684\u666f\u8c61\uff0c\u4e5f\u662f\u4e0d\u7981\u4e3a\u4e4b\u6005\u7136\uff0c\u5fc3\u91cc\u90fd\u5728 \u60f3\u9053\uff1a\u201c\u672c\u6765\u4ee5\u4ed6\u7684\u806a\u660e\u9020\u8be3\uff0c\u672c\u53ef\u4ee5\u5149\u5927\u95e8\u6237\uff0c\u53ef\u60dc\u4ed6\u8d70\u4e0a\u6b67\u9014\uff0c\u8fd9\u771f\u662f\u81ea\u4f5c\u5b7d\u4e0d\u53ef\u6d3b \u4e86\uff01\u201d \u3000\u3000\u5c60\u51e4\u5927\u4ec7\u5f97\u62a5\uff0c\u4ea6\u5df2\u5413\u51fa\u4e00\u8eab\u51b7\u6c57\u3002 \u3000\u3000\u539f\u6765\u5c60\u51e4\u65e9\u5c31\u5728\u638c\u5fc3\u91cc\u6263\u7740\u4e00\u652f\u6bd2\u9f99\u9556\uff0c\u9633\u5929\u96f7\u5927\u8d25\u4e4b\u4e0b\uff0c\u60f3\u8981\u6293\u5979\u4f5c\u4e3a\u4eba\u8d28\uff0c\u4e00\u65f6\u5fd8 \u4e86\u5979\u662f\u5c60\u767e\u57ce\u7684\u5973\u513f\u3002\u5c60\u5bb6\u7684\u6bd2\u9f99\u9556\u4e43\u662f\u89c1\u8840\u5c01\u5589\u7684\u6697\u5668\uff0c\u6251\u5c06\u8fc7\u53bb\uff0c\u51b7\u4e0d\u53ca\u9632\u5c31\u7740\u4e86\u9053 \u513f\u3002\u4e5f\u5e78\u800c\u5c60\u51e4\u9556\u53d1\u5f97\u5feb\uff0c\u5426\u5219\u53ea\u6015\u8fd8\u6709\u4e00\u573a\u56f0\u517d\u4e4b\u6597\u3002 \u3000\u3000\u90a3\u51e0\u5341\u540d\u51b2\u4e0a\u5c71\u6765\u7684\u9a91\u5175\uff0c\u90fd\u662f\u62d6\u96f7\u624b\u4e0b\u7684\u7cbe\u608d\u6b66\u58eb\uff0c\u4f46\u9661\u7136\u770b\u89c1\u9633\u5929\u96f7\u4ece\u767e\u4e08\u60ac\u5d16\u4e0a \u8dcc\u4e0b\u6765\uff0c\u9976\u662f\u5982\u4f55\u80c6\u5927\uff0c\u4e5f\u90fd\u4e0d\u7981\u4eba\u4eba\u5927\u5403\u4e00\u60ca\uff0c\u5fc3\u5bd2\u6c14\u9981\uff01 \u3000\u3000\u5c31\u5728\u6b64\u65f6\uff0c\u5ffd\u542c\u5f97\u8f70\u8f70\u9686\u9686\u4e4b\u58f0\uff0c\u9707\u64bc\u5c71\u8c37\uff0c\u65e0\u6570\u78e8\u76d8\u5927\u7684\u77f3\u5934\uff0c\u4ece\u5c71\u4e0a\u6eda\u4e0b\u6765\u3002\u8fd9\u961f \u9a91\u5175\u9996\u5f53\u5176\u51b2\uff0c\u767b\u65f6\u6709\u4e03\u516b\u9a91\u7ed9\u6025\u6eda\u800c\u4e0b\u7684\u5927\u77f3\u6253\u7ffb\uff0c\u8fde\u4eba\u5e26\u9a6c\uff0c\u6eda\u4e0b\u5c71\u5761\uff0c\u60e8\u53f7\u4e4b\u58f0\uff0c\u6b64 \u8d77\u5f7c\u843d\u3002\u4f59\u4e0b\u4e4b\u4eba\uff0c\u53ea\u6068\u9a6c\u513f\u6ca1\u957f\u516b\u6761\u817f\uff0c\u614c\u5fd9\u62d4\u8f6c\u9a6c\u5934\uff0c\u5982\u98de\u9003\u8dd1\uff01 \u3000\u3000\u5c71\u9876\u4e0a\u73b0\u51fa\u4e00\u4e2a\u548c\u5c1a\u548c\u4e00\u4e2a\u9053\u58eb\uff0c\u548c\u4e00\u4e2a\u5e74\u7ea6\u4e94\u65ec\u7684\u9ed1\u8863\u6c49\u5b50\u3002 \u3000\u3000\u674e\u601d\u5357\u5446\u4e86\u4e00\u5446\uff0c\u51e0\u4e4e\u4e0d\u6562\u76f8\u4fe1\u81ea\u5df1\u7684\u773c\u775b\uff0c\u84e6\u5730\u5c16\u58f0\u53eb\u9053\uff1a\u201c\u5e08\u7236\uff0c\u5e08\u7236\uff01\u4f60\u8001\u4eba\u5bb6 \u6765\u4e86\uff01\u201d\u539f\u6765\u90a3\u4e2a\u548c\u5c1a\u5c31\u662f\u4ed6\u7684\u5e08\u7236\u8c37\u5e73\u9633\u3002\u8c37\u5e73\u9633\u672c\u6765\u662f\u5c11\u6797\u6d3e\u7684\u4fd7\u5bb6\u5f1f\u5b50\uff0c\u4e0d\u77e5\u4e3a\u4ec0\u4e48 \u505a\u4e86\u548c\u5c1a\u3002 \u3000\u3000\u4e25\u70f7\u66f4\u662f\u5982\u540c\u8eab\u5728\u68a6\u4e2d\uff0c\u5446\u4e86\u4e00\u5446\uff0c\u8fd9\u624d\u731b\u5730\u53eb\u9053\uff1a\u201c\u7239\u7239\uff0c\u7239\u7239\uff01\u6211\u4e0d\u662f\u505a\u68a6\u5427\uff1f \u554a\uff0c\u7239\u7239\uff0c\u771f\u7684\u662f\u4f60\u6765\u4e86\uff01\u201d\u539f\u6765\u90a3\u4e2a\u9ed1\u8863\u6c49\u5b50\u6b63\u662f\u5979\u7684\u7236\u4eb2\u2014\u2014\u5ddd\u897f\u5927\u4fa0\u4e25\u58f0\u6d9b\u3002 \u3000\u3000\u8c37\u6db5\u865a\u7741\u5927\u4e86\u773c\u775b\uff0c\u5fc3\u4e2d\u5374\u662f\u60ca\u7591\u4e0d\u5b9a\uff0c\u9053\uff1a\u201c\u5f20\u5143\u5409\u7684\u5e08\u7236\u4e3a\u4ec0\u4e48\u548c\u4e25\u70f7\u7684\u7236\u4eb2\u540c \u6765\uff0c\u96be\u9053\u4ed6\u4eec\u8fd8\u662f\u8981\u6765\u963b\u6320\u6211\u4eec\u7684\u5a5a\u4e8b\uff1f\u201d\u539f\u6765\u90a3\u4e2a\u9053\u58eb\u6b63\u662f\u6b66\u5f53\u6d3e\u7684\u638c\u95e8\u7389\u865a\u9053\u4eba\u3002 \u3000\u3000\u9f99\u8c61\u6cd5\u738b\u548c\u5b5f\u5c11\u521a\u6076\u6597\uff0c\u4e00\u4e2a\u529f\u529b\u6df1\u539a\uff0c\u4e00\u4e2a\u5251\u6cd5\u7cbe\u5947\uff0c\u672c\u6765\u662f\u5404\u64c5\u80dc\u573a\uff0c\u96be\u5206\u8f69\u8f7b \u7684\u3002\u4f46\u6b64\u65f6\u9633\u5929\u96f7\u6bd9\u547d\uff0c\u5c71\u9876\u4e0a\u53c8\u540c\u65f6\u51fa\u73b0\u4e86\u4e09\u4e2a\u9ad8\u624b\uff0c\u98de\u77f3\u4e0b\u5c71\uff0c\u963b\u51fb\u8499\u53e4\u9a91\u5175\uff0c\u663e\u7136\u662f \u5b5f\u5c11\u521a\u8fd9\u8fb9\u7684\u4eba\uff0c\u9f99\u8c61\u6cd5\u738b\u9976\u662f\u5982\u4f55\u672c\u9886\u9ad8\u8d85\uff0c\u4ea6\u662f\u4e0d\u7981\u5fc3\u795e\u4e71\u4e86\u3002 \u3000\u3000\u6756\u98ce\u5251\u5f71\uff0c\u53ea\u542c\u5f97\u201c\u55e4\u201d\u7684\u4e00\u58f0\u54cd\uff0c\u5b5f\u5c11\u521a\u5251\u5149\u95ea\u5904\uff0c\u5212\u7834\u4e86\u4ed6\u7684\u8888\u88df\uff1b\u9f99\u8c61\u6cd5\u738b\u5927\u543c \u4e00\u58f0\uff0c\u6a2a\u6756\u683c\u5f00\u5b5f\u5c11\u521a\u7684\u957f\u5251\uff0c\u8df3\u51fa\u5708\u5b50\uff0c\u51b7\u83ab\u8bf4\u9053\uff1a\u201c\u4eca\u65e5\u4e0d\u662f\u4f60\u6211\u51b3\u6218\u7684\u65f6\u673a\uff01\u5b5f\u5c11 \u521a\uff0c\u4f60\u53f7\u79f0\u6c5f\u5357\u7b2c\u4e00\u5251\u5ba2\uff0c\u5251\u672f\u786e\u662f\u4e0d\u51e1\uff0c\u4f46\u4f60\u81ea\u95ee\u80dc\u4e0d\u80dc\u5f97\u4e86\u6211\u3002\u563f\u563f\uff0c\u4f60\u82e5\u662f\u4e2a\u597d\u6c49\uff0c \u5c31\u8bf7\u5230\u548c\u6797\uff0c\u54b1\u4eec\u627e\u4e2a\u6ca1\u4eba\u7684\u5730\u65b9\u518d\u51b3\u80dc\u8d1f\uff01\u4f60\u82e5\u6015\u8f93\u7ed9\u6211\uff0c\u90a3\u4f60\u5c31\u4ed7\u7740\u4eca\u65e5\u4eba\u591a\u52a9\u9635\uff0c\u518d \u6597\u4e0b\u53bb\uff01\u6211\u7eb5\u7136\u62fc\u4e86\u4e00\u6b7b\uff0c\u4e5f\u51b3\u4e0d\u4f69\u670d\u4f60\u4e2d\u539f\u7684\u6b66\u529f\uff01\u201d\u9ad8\u624b\u73af\u4f3a\uff0c\u9f99\u8c61\u6cd5\u738b\u867d\u662f\u5927\u8a00\u708e \u708e\uff0c\u5374\u5df2\u9732\u51fa\u4e86\u602f\u610f\u4e86\u3002 \u3000\u3000\u5b5f\u5c11\u521a\u5012\u6709\u70b9\u60fa\u60fa\u76f8\u60dc\u4e4b\u610f\uff0c\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u662f\u80dc\u4e0d\u4e86\u4f60\uff0c\u4f46\u4f60\u4e5f\u80dc\u4e0d\u4e86\u6211\uff0c\u54b1\u4eec\u518d \u6253\u5341\u573a\uff0c\u53ea\u6015\u4e5f\u8fd8\u662f\u5982\u6b64\uff0c\u4f55\u987b\u518d\u6218\uff1f\u8981\u77e5\u9053\u4f60\u4e0d\u8fc7\u6015\u6211\u4e0d\u80af\u653e\u4f60\u56de\u53bb\u7f62\u4e86\uff0c\u597d\uff0c\u4f60\u653e\u5fc3\u8d70 \u5427\uff0c\u59d3\u5b5f\u7684\u51b3\u4e0d\u963b\u62e6\u3002\u4f46\u548c\u6797\u4e4b\u7ea6\uff0c\u8bf7\u6055\u6211\u4e0d\u5949\u966a\u4e86\uff01\u201d \u3000\u3000\u9f99\u8c61\u6cd5\u738b\u559c\u51fa\u671b\u5916\uff0c\u5012\u62d6\u7985\u4ed7\uff0c\u8fc8\u6b65\u4fbf\u8d70\uff0c\u5ffd\u542c\u5f97\u4e00\u58f0\u559d\u9053\uff1a\u201c\u4e14\u6162\uff01\u201d\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6 \u5feb\uff0c\u8c37\u5e73\u9633\u5df2\u662f\u622a\u4f4f\u4ed6\u7684\u53bb\u8def\uff01 \u3000\u3000\u9f99\u8c61\u6cd5\u738b\u5fc3\u60f3\uff1a\u201c\u4e0d\u4fe1\u4e2d\u539f\u8fd8\u6709\u4e00\u4e2a\u5f3a\u4f3c\u5b5f\u5c11\u521a\u7684\u4eba\uff0c\u53ea\u8981\u8fd9\u59d3\u5b5f\u7684\u4e0d\u63d2\u624b\uff0c\u8c01\u80fd\u62e6\u5f97 \u4e86\u6211\uff1f\u201d\u5f53\u4e0b\u51dd\u8eab\u6b62\u6b65\uff0c\u76ef\u7740\u8c37\u5e73\u9633\u51b7\u51b7\u5730\u8bf4\u9053\uff1a\u201c\u9601\u4e0b\u4e5f\u60f3\u4e0e\u6211\u6597\u4e00\u6597\u4e48\uff1f\u201d \u3000\u3000\u8c37\u5e73\u9633\u9053\uff1a\u201c\u6211\u53ea\u8981\u4f60\u63a5\u6211\u4e00\u62db\uff0c\u4f60\u80dc\u5f97\u4e86\u6211\uff0c\u6211\u56fa\u7136\u653e\u4f60\u8fc7\u53bb\uff0c\u4f60\u8d25\u7ed9\u4e86\u6211\uff0c\u6211\u4e5f\u653e \u4f60\u8fc7\u53bb\uff01\u8fd9\u6837\uff0c\u4f60\u603b\u4e0d\u80fd\u8bf4\u6211\u662f\u4e58\u4eba\u4e4b\u5371\u4e86\u5427\uff1f\u201d\u539f\u6765\u8c37\u5e73\u9633\u7684\u7528\u610f\uff0c\u53ea\u662f\u8981\u7ed9\u4ed6\u4e00\u70b9\u6559 \u8bad\uff0c\u53eb\u4ed6\u4ee5\u540e\u4e0d\u6562\u76ee\u4e2d\u65e0\u4eba\u3002 \u3000\u3000\u9f99\u8c61\u6cd5\u738b\u6012\u9053\uff1a\u201c\u597d\uff0c\u83ab\u8bf4\u4e00\u62db\uff0c\u5341\u62db\u53c8\u6709\u4f55\u59a8\uff1f\u201d\u4ed6\u89c1\u8c37\u5e73\u9633\u53cc\u624b\u7a7a\u7a7a\uff0c\u4e0d\u60f3\u81ea\u8d2c\u8eab \u4efd\uff0c\u5f53\u4e0b\u6756\u4ea4\u5de6\u624b\uff0c\u5927\u543c\u4e00\u58f0\uff0c\u5355\u638c\u5288\u51fa\uff01\u867d\u662f\u5355\u638c\u5288\u51fa\uff0c\u4f46\u5df2\u7528\u5230\u4e86\u7b2c\u4e5d\u91cd\u7684\u201c\u9f99\u8c61 \u529f\u201d\u3002 \u3000\u3000\u8c37\u5e73\u9633\u9053\uff1a\u201c\u6765\u5f97\u597d\uff01\u201d\u8f7b\u8f7b\u5730\u4e00\u638c\u62cd\u51fa\uff0c\u82e5\u4e0d\u7ecf\u610f\uff0c\u4f7f\u7684\u5374\u662f\u5c11\u6797\u6d3e\u7684\u7edd\u9876\u795e\u529f\u2014\u2014 \u8fbe\u6469\u7956\u5e08\u771f\u4f20\u7684\u91d1\u521a\u638c\uff01\u8fd9\u91d1\u521a\u638c\u4e43\u662f\u5929\u4e0b\u6700\u731b\u7684\u638c\u529b\uff0c\u8f7b\u8f7b\u4e00\u638c\u62cd\u51fa\uff0c\u8574\u85cf\u4e86\u4e09\u91cd\u5185\u529b\u3002 \u3000\u3000\u53cc\u65b9\u7528\u7684\u90fd\u662f\u6700\u521a\u731b\u7684\u638c\u529b\uff0c\u542c\u5f97\u58f0\u5982\u90c1\u96f7\uff0c\u9f99\u8c61\u6cd5\u738b\u63a5\u8fde\u9000\u4e86\u4e09\u6b65\uff0c\u65b9\u59cb\u7ad9\u7a33\u811a\u6b65\uff0c \u4f46\u521a\u521a\u7ad9\u7a33\u811a\u6b65\uff0c\u5374\u5ffd\u5730\u611f\u5230\u53c8\u6709\u4e00\u80a1\u5185\u529b\u63a8\u6765\uff0c\u4f46\u8c37\u5e73\u9633\u5374\u662f\u7ad9\u5728\u539f\u5730\uff0c\u5e76\u672a\u52a8\u624b\u3002\u8fd9\u80a1 \u5185\u529b\u7adf\u662f\u65e0\u58f0\u65e0\u606f\u5730\u6765\u5230\u7684\uff01 \u3000\u3000\u9f99\u8c61\u6cd5\u738b\u4e0d\u7981\u53c8\u662f\u8fde\u9000\u4e09\u6b65\uff0c\u672a\u66fe\u7ad9\u7a33\uff0c\u7a81\u7136\u53c8\u611f\u5230\u4e00\u80a1\u5185\u529b\u63a8\u6765\uff0c\u5982\u662f\u8005\u8fde\u9000\u4e09\u6b21\uff0c \u4e00\u5171\u9000\u51fa\u4e86\u4e5d\u6b65\uff0c\u8fd9\u624d\u7a33\u5f97\u4f4f\u8eab\u5f62\uff0c\u4e0d\u518d\u611f\u5230\u5bf9\u65b9\u5185\u529b\u7684\u88ad\u51fb\uff0c\u539f\u6765\u8fd9\u662f\u8c37\u5e73\u9633\u91d1\u521a\u638c\u6240\u8574 \u85cf\u7684\u4e09\u91cd\u5185\u529b\u7684\u5999\u7528\u3002 \u3000\u3000\u9f99\u8c61\u6cd5\u738b\u9762\u7ea2\u8fc7\u8033\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6211\u5373\u4f7f\u672a\u66fe\u4e0e\u5b5f\u5c11\u521a\u6597\u4e86\u4e00\u573a\uff0c\u9f99\u8c61\u529f\u4e5f\u672a\u5fc5\u6bd4\u5f97\u8fc7 \u4ed6\u7684\u91d1\u521a\u638c\u529b\u3002\u201d \u3000\u3000\u8c37\u5e73\u9633\u5fc3\u9053\uff1a\u201c\u8fd9\u53ae\u63a5\u4e86\u6211\u7684\u5927\u529b\u91d1\u521a\u638c\uff0c\u5c45\u7136\u6ca1\u6709\u6454\u5012\uff0c\u4e5f\u603b\u7b97\u96be\u4e3a\u4ed6\u4e86\u3002\u201d\u5f53\u4e0b\u51b7 \u7b11\u8bf4\u9053\uff1a\u201c\u4e2d\u539f\u6b66\u5b66\u5982\u4f55\uff1f\u4f60\u73b0\u5728\u8fd8\u6562\u8bf4\u8981\u63a5\u6211\u5341\u62db\u5417\uff1f\u4e0d\u8fc7\uff0c\u6211\u6709\u8a00\u5728\u5148\uff0c\u4f60\u867d\u662f\u8d25\u5728\u6211 \u7684\u638c\u4e0b\uff0c\u4e5f\u5c3d\u7ba1\u8d70\u5427\uff01\u201d \u3000\u3000\u9f99\u8c61\u6cd5\u738b\u5782\u5934\u4e27\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u7684\u638c\u529b\u662f\u7a0d\u80dc\u4e8e\u6211\uff0c\u4f46\u65e5\u540e\u82e5\u6709\u673a\u7f18\uff0c\u6211\u8fd8\u60f3\u9886 \u6559\u4f60\u7684\u5175\u5203\u529f\u592b\uff01\u201d \u3000\u3000\u6b66\u5f53\u638c\u95e8\u7389\u865a\u9053\u4eba\u8d70\u4e86\u4e0a\u6765\uff0c\u7b11\u9053\uff1a\u201c\u8c37\u5927\u4fa0\u5e73\u751f\u4e0d\u7528\u5175\u5668\uff0c\u4e0d\u8fc7\u4f60\u60f3\u9886\u6559\u4e2d\u539f\u6b66\u5b66\u7684 \u5175\u5203\u529f\u592b\uff0c\u90a3\u4e5f\u4e0d\u96be\uff01\u201d \u3000\u3000\u7389\u865a\u9053\u4eba\u9762\u9ec4\u808c\u7626\uff0c\u8eab\u4e0a\u5e76\u6ca1\u4f69\u6234\u5175\u5203\uff0c\u53ea\u6709\u9888\u540e\u63d2\u7740\u4e00\u652f\u62c2\u5c18\uff0c\u9f99\u8c61\u6cd5\u738b\u4e0d\u77e5\u4ed6\u662f\u6b66 \u5f53\u638c\u95e8\uff0c\u89c1\u4ed6\u8c8c\u4e0d\u60ca\u4eba\uff0c\u54ea\u91cc\u653e\u5728\u5fc3\u4e0a\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6211\u5e73\u751f\u4ece\u672a\u9047\u8fc7\u654c\u624b\uff0c\u4e0d\u4fe1\u5728\u4e00\u65e5\u4e4b \u95f4\uff0c\u7adf\u6709\u4e09\u4e2a\u4eba\u80fd\u591f\u80dc\u5f97\u8fc7\u6211\u3002\u201d\u5f53\u4e0b\u63d0\u8d77\u7985\u6756\uff0c\u8bf4\u9053\uff1a\u201c\u9053\u957f\u65e2\u7136\u5b9a\u8981\u4f38\u91cf\u5c0f\u50e7\uff0c\u90a3\u5c31\u8bf7 \u4eae\u51fa\u5175\u5203\u6765\u5427\u3002\u201d\u4ed6\u63a5\u8fde\u7ed9\u5b5f\u5c11\u521a\u4e0e\u8c37\u5e73\u9633\u632b\u6298\uff0c\u867d\u7136\u770b\u4e0d\u8d77\u7389\u865a\u9053\u4eba\uff0c\u6c14\u7130\u5df2\u662f\u4e0d\u6562\u50cf\u521a \u624d\u90a3\u6837\u56a3\u5f20\u3002 \u3000\u3000\u7389\u865a\u9053\u4eba\u5fae\u5fae\u4e00\u7b11\uff0c\u53d6\u4e0b\u62c2\u5c18\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u8d2b\u9053\u7684\u5175\u5668\uff0c\u5927\u548c\u5c1a\u8bf7\u8fdb\u62db\u5427\uff01\u201d \u3000\u3000\u9f99\u8c61\u6cd5\u738b\u4e43\u662f\u5185\u5916\u517c\u4fee\u7684\u9ad8\u624b\uff0c\u89c1\u5bf9\u65b9\u7adf\u6562\u7528\u4e00\u652f\u62c2\u5c18\u62b5\u6321\u4ed6\u7684\u91cd\u5175\u5668\uff0c\u5012\u4e5f\u4e0d\u6562\u8f7b \u654c\uff0c\u5f53\u4e0b\u6697\u8fd0\u9f99\u8c61\u529f\uff0c\u529b\u8d2f\u6756\u5c16\uff0c\u559d\u9053\uff1a\u201c\u597d\uff0c\u90a3\u5c31\u8bf7\u9053\u957f\u63a5\u62db\u5427\uff01\u201d\u4e00\u62db\u82cd\u9f99\u51fa\u6d77\uff0c\u6a2a\u626b \u7389\u865a\u9053\u4eba\u53cc\u8db3\uff0c\u6756\u5c16\u4e00\u7fd8\uff0c\u53c8\u70b9\u5411\u4ed6\u80f8\u819b\u7684\u201c\u7389\u5e9c\u7a74\u201d\uff0c\u201c\u7389\u5e9c\u7a74\u201d\u4e43\u662f\u4eba\u8eab\u4e8c\u5341\u56db\u4e2a\u6b7b\u7a74 \u4e4b\u4e00\u3002 \u3000\u3000\u7389\u865a\u9053\u4eba\u559d\u58f0\uff1a\u201c\u6765\u5f97\u597d\uff01\u201d\u4e0d\u614c\u4e0d\u5fd9\uff0c\u6325\u52a8\u62c2\u5c18\uff0c\u8f7b\u8f7b\u4e00\u62c2\uff0c\u5c31\u628a\u4ed6\u7684\u7985\u6756\u62e8\u8fc7\u4e00 \u8fb9\uff01 \u3000\u3000\u6b66\u5b66\u4e2d\u6709\u201c\u56db\u4e24\u62e8\u5343\u65a4\u201d\u7684\u5999\u8bc0\uff0c\u4f46\u771f\u6b63\u80fd\u591f\u8fbe\u5230\u8fd9\u6837\u7684\u9020\u8be3\uff0c\u5f53\u4e16\u4e5f\u6ca1\u6709\u51e0\u4eba\u3002\u9f99\u8c61 \u6cd5\u738b\u8fd9\u624d\u5927\u5403\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u8fd9\u4e2a\u9053\u58eb\u7684\u5185\u529f\uff0c\u7adf\u7136\u8fd0\u7528\u5f97\u5982\u6b64\u795e\u5999\uff01\u201d \u3000\u3000\u9f99\u8c61\u6cd5\u738b\u4e5f\u771f\u4e0d\u6127\u662f\u4e00\u7b49\u4e00\u7684\u9ad8\u624b\uff0c\u7985\u6756\u65b9\u81ea\u8361\u5f00\uff0c\u9661\u7136\u4e00\u4e2a\u7ffb\u8eab\uff0c\u7acb\u5373\u53c8\u662f\u4e00\u62db\u201c\u4e94 \u4e01\u5f00\u5c71\u201d\uff0c\u7985\u6756\u81ea\u5de6\u800c\u53f3\u7684\u5212\u4e86\u4e00\u76f4\u5706\u5f27\uff0c\u53c8\u5411\u7389\u865a\u9053\u4eba\u51fb\u4e0b\uff01\u8fd9\u4e00\u62db\u7684\u529b\u9053\u66f4\u80dc\u4e8e\u524d\u3002 \u3000\u3000\u7389\u865a\u9053\u4eba\u89c1\u4ed6\u5728\u8fde\u756a\u6076\u6597\u4e4b\u540e\uff0c\u5c45\u7136\u8fd8\u6709\u5982\u6b64\u529f\u529b\uff0c\u4e5f\u662f\u6709\u70b9\u4f69\u670d\uff0c\u5f53\u4e0b\u518d\u5c55\u62c2\u5c18\uff0c\u62d4 \u5f00\u4ed6\u7684\u7985\u6756\uff0c\u8fd9\u6b21\u4e0d\u518d\u7b49\u4ed6\u53d8\u62db\uff0c\u7acb\u5373\u91c7\u53d6\u653b\u52bf\uff0c\u6b3a\u8eab\u8fdb\u903c\uff0c\u62c2\u5411\u4ed6\u7684\u9762\u95e8\uff0c\u9f99\u8c61\u6cd5\u738b\u541e\u80f8 \u5438\u8179\uff0c\u8eab\u5f62\u9661\u5730\u632a\u540e\u534a\u5bf8\uff0c\u7985\u6756\u53cd\u5708\u56de\u6765\uff0c\u963b\u51fb\u7389\u865a\u9053\u4eba\u3002 \u3000\u3000\u7389\u865a\u9053\u4eba\u7109\u80fd\u7ed9\u4ed6\u51fb\u4e2d\uff0c\u559d\u4e00\u58f0\uff1a\u201c\u7740\uff01\u201d\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u62c2\u5c18\u53cd\u7ed5\uff0c\u5df2\u662f\u628a\u4ed6\u7684\u7985 \u6756\u7f20\u4f4f\u3002\u9976\u662f\u9f99\u8c61\u6cd5\u738b\u6709\u64d2\u9f99\u4f0f\u864e\u4e4b\u80fd\uff0c\u9738\u738b\u6253\u9f0e\u4e4b\u529b\uff0c\u7adf\u662f\u4e0d\u80fd\u79fb\u52a8\u534a\u5206\u3002 \u3000\u3000\u7389\u865a\u9053\u4eba\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5927\u548c\u5c1a\u8fd8\u8981\u518d\u6bd4\u4e48\uff1f\u201d\u62c2\u5c18\u4e00\u6536\uff0c\u9f99\u8c61\u6cd5\u738b\u8dc4\u8dc4\u8e09\u8e09\u5730\u5411 \u524d\u51b2\u51fa\u6570\u6b65\u3002 \u3000\u3000\u9f99\u8c61\u6cd5\u738b\u6ee1\u9762\u901a\u7ea2\uff0c\u6697\u81ea\u601d\u5fd6\uff1a\u201c\u6211\u5373\u4f7f\u672a\u7ecf\u6076\u6597\uff0c\u6700\u591a\u4e5f\u4e0d\u8fc7\u80fd\u654c\u4ed6\u4e94\u5341\u62db\u3002\u201d\u5f53\u4e0b \u5c31\u50cf\u53ea\u6597\u8d25\u516c\u9e21\u4e00\u6837\u957f\u53f9\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u5982\u4eca\u6211\u65b9\u59cb\u77e5\u9053\uff0c\u4e2d\u539f\u7684\u6b66\u5b66\uff0c\u679c\u7136\u662f\u6df1\u4e0d\u53ef\u6d4b\u3002 \u4ece\u524d\u7684\u6211\uff0c\u53ea\u662f\u53ea\u4e95\u5e95\u4e4b\u86d9\uff01\u201d\u5012\u62d6\u7985\u6756\uff0c\u5782\u5934\u4e27\u6c14\u5730\u8d70\u4e86\u3002 \u3000\u3000\u674e\u601d\u5357\u3001\u8c37\u6db5\u865a\u3001\u4e25\u70f7\u7b49\u4eba\u8dd1\u8fc7\u6765\u4e0e\u5e08\u7236\u3001\u7236\u4eb2\u76f8\u4f1a\u3002\u5ddd\u897f\u5927\u4fa0\u5de6\u624b\u62c9\u4ed6\u5973\u513f\uff0c\u53f3\u624b\u62c9 \u7740\u8c37\u6db5\u865a\uff0c\u53c8\u662f\u6b22\u559c\uff0c\u53c8\u662f\u6709\u70b9\u5c34\u5c2c\uff0c\u8bf4\u9053\uff1a\u201c\u90fd\u662f\u4e3a\u7236\u505a\u4e8b\u4e0d\u5f53\uff0c\u7d2f\u4f60\u4eec\u53d7\u4e86\u8bb8\u591a\u82e6\uff01\u201d \u3000\u3000\u4e25\u70f7\u559c\u6781\u800c\u6ce3\uff0c\u6cea\u73e0\u6eda\u6eda\u800c\u4e0b\uff0c\u4f46\u5374\u662f\u7b11\u5f00\u4e86\u5634\u8bf4\u9053\uff1a\u201c\u7239\u7239\uff0c\u4f60\u80af\u539f\u8c05\u6211\u4eec\u4e86\uff1f\u201d\u4e25 \u58f0\u6d9b\u8f7b\u629a\u5973\u513f\u79c0\u53d1\uff0c\u8bf4\u9053\uff1a\u201c\u662f\u6211\u5e94\u5f53\u6c42\u4f60\u4eec\u539f\u8c05\u624d\u5bf9\u3002\u6211\u4e0d\u8be5\u56fa\u6267\u5df1\u89c1\uff0c\u786c\u8981\u5206\u5f00\u4f60\u4eec \u7684\u3002\u201d \u3000\u3000\u7389\u865a\u9053\u4eba\u4e5f\u548c\u8c37\u6db5\u865a\u8bf4\u9053\uff1a\u201c\u5c0f\u5f92\u4e0d\u8bc6\u5927\u4f53\uff0c\u516c\u62a5\u79c1\u4ec7\uff0c\u8fd9\u90fd\u662f\u6211\u7ba1\u6559\u4e0d\u4e25\u4e4b\u6545\u3002\u6211\u5df2 \u7ecf\u8d23\u9a82\u8fc7\u4ed6\u4eec\u4e86\uff0c\u8bf7\u8c37\u5c11\u4fa0\u770b\u5728\u6211\u7684\u8001\u9762\uff0c\u522b\u8bb0\u4ed6\u4eec\u7684\u6068\u3002\u201d \u3000\u3000\u539f\u6765\u4e25\u58f0\u6d9b\u5931\u4e86\u5973\u513f\uff0c\u5fc3\u4e2d\u4ea6\u662f\u540e\u6094\u3002\u803f\u5929\u98ce\u662f\u8c37\u6db5\u865a\u7684\u5e08\u7236\uff0c\u77e5\u9053\u6b64\u4e8b\u7ecf\u8fc7\uff0c\u4e8e\u662f\u7ea6 \u4e86\u4e00\u4e2a\u548c\u7389\u865a\u9053\u4eba\u548c\u4e25\u58f0\u6d9b\u90fd\u76f8\u719f\u7684\u670b\u53cb\u51fa\u9762\uff0c\u4e3a\u5f92\u513f\u8bf4\u60c5\u3002 \u3000\u3000\u7389\u865a\u9053\u4eba\u662f\u4e2a\u201c\u6709\u9053\u4e4b\u58eb\u201d\uff0c\u660e\u7406\u4e4b\u4eba\uff0c\u77e5\u9053\u5a5a\u59fb\u4e0d\u53ef\u52c9\u5f3a\uff0c\u4e00\u8bf4\u4fbf\u901a\uff0c\u7389\u865a\u9053\u4eba\u65e2\u7136 \u80af\u4ee5\u5f20\u5143\u5409\u5e08\u7236\u7684\u8eab\u4efd\u7b54\u5e94\u9000\u5a5a\uff0c\u4e25\u58f0\u6d9b\u5f53\u7136\u66f4\u662f\u76fc\u671b\u7236\u5973\u56e2\u5706\u4e86\u3002 \u3000\u3000\u8c37\u6db5\u865a\u8c22\u8fc7\u4e86\u7389\u865a\u9053\u4eba\uff0c\u8ba4\u4e86\u5cb3\u7236\uff0c\u5b5f\u5c11\u4fa0\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6709\u60c5\u4eba\u90fd\u6210\u7737\u5c5e\uff0c\u8fd9\u6b63\u662f\u518d\u597d \u4e5f\u4e0d\u8fc7\u7684\u4e86\u3002\u8fc7\u53bb\u7684\u4e8b\uff0c\u4f11\u8981\u518d\u63d0\uff01\u201d \u3000\u3000\u9646\u6606\u4ed1\u9053\uff1a\u201c\u53c8\u6709\u4e00\u652f\u9791\u5b50\u7684\u9a91\u5175\u6765\u4e86\uff0c\u54b1\u4eec\u8fd8\u662f\u8d76\u5feb\u7ffb\u5c71\u8d70\u5427\uff01\u201d \u3000\u3000\u53ea\u89c1\u5c71\u4e0b\u5c18\u5934\u5927\u8d77\uff0c\u5ce1\u8c37\u95f4\u6740\u51fa\u4e00\u5f6a\u519b\u9a6c\uff0c\u770b\u6765\u7adf\u4f3c\u6709\u6570\u5343\u4eba\u4e4b\u591a\uff01\u82e5\u7136\u7ed9\u4ed6\u4eec\u51b2\u4e0a\u5c71 \u6765\uff0c\u5b5f\u5c11\u521a\u3001\u8c37\u5e73\u9633\u7b49\u4eba\u7eb5\u7136\u6b66\u529f\u5353\u7edd\uff0c\u4e5f\u51b3\u96be\u62b5\u5fa1\u5f97\u4e86\u4ed6\u4eec\u7684\u4e71\u7bad\u6512\u5c04\uff01 \u3000\u3000\u674e\u601d\u5357\u5ffd\u9053\uff1a\u201c\u54a6\uff0c\u4ed6\u4eec\u7adf\u7136\u4e92\u76f8\u6b8b\u4f59\u8d77\u6765\uff0c\u8fd9\u662f\u4ec0\u4e48\u9053\u7406\uff1f\u201d\u4f17\u4eba\u5b9a\u773c\u770b\u53bb\uff0c\u53ea\u89c1\u540e \u6765\u7684\u8fd9\u652f\u9a91\u5175\uff0c\u679c\u7136\u50cf\u6f6e\u6c34\u822c\u7684\u5411\u62d6\u96f7\u90a3\u4e00\u8425\u536b\u58eb\u731b\u6251\uff0c\u54cd\u7bad\u5212\u8fc7\u957f\u7a7a\uff0c\u6740\u58f0\u5982\u96f7\u9707\u8033\u3002 \u3000\u3000\u539f\u6765\u8fd9\u652f\u9a91\u5175\u662f\u9547\u56fd\u738b\u5b50\u9884\u5148\u57cb\u4f0f\u5728\u5288\u5929\u5d16\u4e0b\u5e7d\u6df1\u7684\u5ce1\u8c37\u4e4b\u4e2d\u7684\u3002\u8981\u77e5\u9053\u9547\u56fd\u738b\u5b50\u867d\u7136 \u662f\u4e2a\u83bd\u592b\uff0c\u4f46\u4e5f\u5e76\u975e\u6beb\u65e0\u5fc3\u8ba1\uff0c\u800c\u4ed6\u7684\u90e8\u4e0b\u4e5f\u8fd8\u662f\u6709\u8c0b\u58eb\u7684\u3002\u6210\u5409\u601d\u6c57\u6b7b\u540e\uff0c\u62d6\u96f7\u5fcc\u4ed6\u5206\u4e86 \u5175\u6743\uff0c\u8fd9\u4e2a\u4ed6\u4e5f\u77e5\u9053\u7684\u3002\u56e0\u6b64\u4ed6\u5728\u8d74\u4f1a\u4e4b\u524d\uff0c\u8fde\u591c\u5b89\u6392\u4e86\u4e00\u652f\u4f0f\u5175\uff0c\u5429\u5490\u7edf\u5175\u7684\u5fc3\u8179\u5c06\u9886\uff0c \u82e5\u662f\u7b2c\u4e8c\u5927\u8fd8\u4e0d\u89c1\u4ed6\u56de\u6765\uff0c\u5c31\u6740\u51fa\u53bb\u5411\u62d6\u96f7\u8ba8\u4eba\u3002\u9ad8\u5c71\u5cfb\u5cad\uff0c\u5927\u519b\u884c\u8fdb\uff0c\u4e0d\u6613\u7792\u4eba\u8033\u76ee\uff0c\u6545 \u6b64\u8fd9\u652f\u4f0f\u5175\u6709\u4e09\u5343\uff0c\u662f\u5728\u591c\u95f4\u7528\u539a\u5e03\u5305\u7740\u9a6c\u8e44\uff0c\u6084\u6084\u5730\u5f00\u8fdb\u5e7d\u6df1\u7684\u5c71\u8c37\u3002\u867d\u7136\u53ea\u6709\u4e09\u5343\uff0c\u4f46 \u5df1\u662f\u6bd4\u62d6\u96f7\u7684\u90a3\u4e00\u8425\u536b\u58eb\u591a\u4e86\u4e09\u500d\u4e86\u3002 \u3000\u3000\u4e0d\u8fc7\u9547\u56fd\u738b\u5b50\u548c\u4ed6\u7684\u8c0b\u58eb\u5374\u6599\u9519\u4e86\u4e00\u4ef6\u6700\u5173\u7d27\u8981\u7684\u4e8b\u60c5\uff0c\u62d6\u96f7\u624b\u6bb5\u7684\u72e0\u8fa3\u8d85\u4e4e\u4ed6\u4eec\u7684\u4f30 \u8ba1\u3002\u4ed6\u4ee5\u4e3a\u62d6\u96f7\u5373\u4f7f\u662f\u7528\u59b9\u59b9\u9a97\u4ed6\uff0c\u6700\u591a\u4e5f\u4e0d\u8fc7\u5c06\u4ed6\u6263\u7559\uff0c\u903c\u4ed6\u4ea4\u51fa\u5175\u6743\uff0c\u4e0d\u6599\u62d6\u96f7\u7adf\u7136\u628a \u4ed6\u6740\u4e86\u3002 \u3000\u3000\u8fd9\u652f\u9a91\u5175\u662f\u9547\u56fd\u738b\u5b50\u7684\u65cf\u4eba\u7ec4\u6210\uff0c\u5230\u4e86\u671f\u9650\uff0c\u4e0d\u89c1\u4e3b\u5e05\u56de\u6765\uff0c\u679c\u7136\u5c31\u6740\u51fa\u53bb\u628a\u62d6\u96f7\u5305\u56f4 \u8d77\u6765\u4e86\u3002 \u3000\u3000\u4e24\u519b\u6df7\u6218\uff0c\u5bf9\u65b9\u53d1\u73b0\u4e86\u660e\u6167\u516c\u4e3b\uff0c\u7acb\u5373\u6709\u4e00\u5c0f\u961f\u4eba\u9a6c\u8dd1\u6765\u8981\u6349\u62ff\u5979\u3002 \u3000\u3000\u7edf\u7387\u8fd9\u961f\u4eba\u9a6c\u7684\u767e\u592b\u957f\u53eb\u9053\uff1a\u201c\u516c\u4e3b\u662f\u54b1\u4eec\u7684\u738b\u5983\uff0c\u738b\u5b50\u4e0d\u56de\u6765\uff0c\u738b\u5983\u4e5f\u8981\u63a5\u56de\u53bb\u3002\u201d \u526f\u961f\u957f\u63a5\u7740\u53eb\u9053\uff1a\u201c\u5bf9\uff0c\u4e0d\u7ba1\u738b\u5b50\u662f\u6b7b\u662f\u6d3b\uff0c\u738b\u5983\u90fd\u662f\u54b1\u4eec\u8fd9\u4e00\u65cf\u7684\u4eba\u3002\u201d\u660e\u6167\u516c\u4e3b\u867d\u7136\u8eab \u6709\u6b66\u529f\uff0c\u4f46\u542c\u5f97\u558a\u58f0\u5982\u96f7\uff0c\u6570\u5341\u9a91\u5411\u5979\u51b2\u6765\uff0c\u4ea6\u662f\u5413\u5f97\u614c\u4e86\u3002\u62d6\u96f7\u8eab\u9677\u91cd\u56f4\uff0c\u5df2\u662f\u65e0\u6687\u987e \u5979\u3002 \u3000\u3000\u674e\u601d\u5357\u548c\u6768\u5a49\u5728\u5c71\u4e0a\u770b\u89c1\u8fd9\u4e2a\u60c5\u5f62\uff0c\u4ea6\u662f\u5413\u5f97\u614c\u4e86\u3002\u6768\u5a49\u8bf4\u9053\uff1a\u201c\u600e\u4e48\u529e\uff0c\u5357\u54e5\uff0c\u5979\u662f \u6211\u4eec\u7684\u6069\u4eba\uff0c\u6211\u4eec\u5c82\u80fd\u5750\u89c6\u4e0d\u6551\uff1f\u201d \u3000\u3000\u674e\u601d\u5357\u6025\u51fa\u4e86\u6ee1\u5934\u5927\u6c57\uff0c\u8bf4\u9053\uff1a\u201c\u5f85\u54b1\u4eec\u8dd1\u5230\u5c71\u4e0b\uff0c\u65e9\u5df2\u8fdf\u4e86\u3002\u201d\u5c60\u51e4\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u53ea \u6709\u8fd9\u51e0\u4e2a\u4eba\uff0c\u8dd1\u4e0b\u53bb\u53ea\u6015\u4e5f\u662f\u81ea\u6295\u7f57\u7f51\uff0c\u53ea\u597d\uff0c\u53ea\u597d\u2014\u2014\u201d \u3000\u3000\u8bdd\u72b9\u672a\u4e86\uff0c\u5ffd\u89c1\u524d\u9762\u7684\u51e0\u4e2a\u9a91\u5175\u7a81\u7136\u4ece\u9a6c\u80cc\u4e0a\u8dcc\u4e0b\u6765\uff0c\u4e00\u5339\u6bdb\u8272\u706b\u7ea2\u7684\u5065\u9a6c\u98de\u5954\u800c\u81f3\uff0c \u9a6c\u80cc\u4e0a\u4e00\u4e2a\u94c1\u642d\u822c\u7684\u5927\u6c49\u6325\u821e\u957f\u7ef3\uff0c\u5c31\u50cf\u6349\u5c0f\u9e21\u4e00\u6837\u628a\u7ef3\u5b50\u629b\u51fa\uff0c\u5957\u5728\u5bf9\u65b9\u7684\u9888\u4e0a\uff0c\u8f6c\u773c\u95f4 \u5f04\u5f97\u4e03\u516b\u4e2a\u4eba\u4eba\u4ef0\u9a6c\u7ffb\uff0c\u90a3\u767e\u592b\u957f\u4e5f\u5728\u5176\u5185\u3002 \u3000\u3000\u674e\u601d\u5357\u5927\u559c\u8bf4\u9053\uff1a\u201c\u6709\u6551\u4e86\uff0c\u8fd9\u662f\u963f\u76d6\uff01\u201d \u3000\u3000\u963f\u76d6\u662f\u8499\u53e4\u6709\u540d\u7684\u52c7\u58eb\uff0c\u767e\u592b\u957f\u7ed9\u4ed6\u7528\u7ef3\u5708\u5957\u5f97\u6c14\u7edd\u800c\u4ea1\uff0c\u540e\u9762\u7684\u4eba\u4e0d\u7531\u5f97\u5fc3\u60ca\u80c6\u6218\uff0c \u5341\u4e4b\u4e03\u516b\u62e8\u8f6c\u4e86\u9a6c\u5934\u3002 \u3000\u3000\u963f\u76d6\u8bf4\u9053\uff1a\u201c\u516c\u4e3b\uff0c\u8bf7\u8ddf\u6211\u6765\uff0c\u6211\u77e5\u9053\u6709\u6761\u5c0f\u8def\uff0c\u5361\u6d1b\u4e1d\u5728\u524d\u9762\u7b49\u7740\u6211\u4eec\u3002\u4e0d\u8fc7\uff0c\u4ece\u4eca \u4e4b\u540e\uff0c\u516c\u4e3b\uff0c\u4f60\u6050\u6015\u662f\u4e0d\u80fd\u56de\u548c\u6797\u4e86\uff0c\u4f60\u613f\u610f\u4e48\uff1f\u201d \u3000\u3000\u660e\u6167\u516c\u4e3b\u9053\uff1a\u201c\u548c\u4f60\u4eec\u592b\u59bb\u5728\u8349\u539f\u4e0a\u7267\u7f8a\uff0c\u65e0\u62d8\u65e0\u675f\uff0c\u8fd9\u6b63\u662f\u6211\u6c42\u4e4b\u4e0d\u5f97\u7684\u4e8b\uff01\u201d \u3000\u3000\u9547\u56fd\u738b\u5b50\u7684\u624b\u4e0b\u5fd7\u5728\u62d6\u96f7\uff0c\u660e\u6167\u516c\u4e3b\u9003\u8dd1\uff0c\u4ed6\u4eec\u66f4\u662f\u5168\u529b\u5411\u62d6\u96f7\u8fdb\u903c\u3002\u7edf\u5175\u7684\u5c06\u9886\u559d \u9053\uff1a\u201c\u62d6\u96f7\uff0c\u4f60\u4e0d\u628a\u6211\u4eec\u7684\u738b\u5b50\u653e\u56de\u6765\uff0c\u53ef\u4f11\u602a\u6211\u4eec\u5bf9\u4f60\u4e0d\u5229\uff01\u201d \u3000\u3000\u4ed6\u6b63\u5f20\u5f00\u5589\u5499\uff0c\u201c\u4e0d\u5229\u201d\u4e8c\u5b57\u521a\u521a\u51fa\u53e3\uff0c\u7a81\u7136\u4e00\u652f\u5229\u7bad\u5c04\u6765\uff0c\u7a7f\u5589\u800c\u8fc7\uff0c\u62d6\u96f7\u5c04\u6b7b\u5bf9\u65b9 \u7684\u9996\u9886\uff0c\u7acb\u5373\u53eb\u6728\u534e\u9ece\u5c06\u9547\u56fd\u738b\u5b50\u7684\u5934\u9885\u6302\u5728\u957f\u77db\u4e0a\uff0c\u9ad8\u9ad8\u4e3e\u8d77\uff0c\u559d\u9053\uff1a\u201c\u4f60\u4eec\u542c\u7740\uff0c\u9547\u56fd \u738b\u5b50\u56fe\u8c0b\u4f5c\u4e71\uff0c\u5df2\u7ecf\u7ed9\u6211\u6740\u4e86\u3002\u4f60\u4eec\u987a\u4ece\u6211\uff0c\u6211\u6055\u4f60\u4eec\u65e0\u7f6a\u3002\u5426\u5219\u4f60\u4eec\u7684\u59bb\u5b50\u513f\u5973\u90fd\u8981\u906d\u53d7 \u65a9\u5c3d\u6740\u7edd\u4e4b\u7978\uff01\u201d \u3000\u3000\u58eb\u5175\u4eec\u90fd\u6709\u5bb6\u4eba\u7559\u5728\u56fd\u5185\uff0c\u5fc3\u4e2d\u4e00\u60f3\uff0c\u7eb5\u7136\u6740\u4e86\u62d6\u96f7\uff0c\u5bb6\u4eba\u4e5f\u96be\u9003\u8131\uff0c\u4f55\u51b5\u9547\u56fd\u738b\u5b50\u53c8 \u5df2\u6b7b\u4e86\uff0c\u5f53\u4e0b\u5c31\u6709\u4e00\u5927\u534a\u4eba\u653e\u4e0b\u5175\u5668\uff0c\u613f\u610f\u987a\u4ece\uff0c\u5176\u4ed6\u4e00\u5c0f\u534a\u4eba\u4e5f\u90fd\u6563\u4e86\u3002 \u3000\u3000\u674e\u601d\u5357\u7b49\u4eba\u7ffb\u8fc7\u5c71\u5934\uff0c\u53ea\u89c1\u660e\u6167\u516c\u4e3b\u548c\u963f\u76d6\u592b\u59bb\u4e09\u9a91\u9a6c\u6b63\u5728\u6c99\u6f20\u4e0a\u524d\u8fdb\uff0c\u6e10\u884c\u6e10\u8fdc\uff0c\u53d8 \u6210\u4e09\u4e2a\u5c0f\u5c0f\u7684\u9ed1\u70b9\uff0c\u6700\u540e\u8fde\u5f71\u5b50\u4e5f\u770b\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u6768\u5a49\u53f9\u9053\uff1a\u201c\u8fd9\u6837\u4e5f\u597d\uff0c\u4f46\u613f\u5979\u5e73\u5b89\u8fc7\u8fd9\u4e00\u751f\u3002\u53ea\u53ef\u60dc\u5979\u6ca1\u6709\u770b\u89c1\u54b1\u4eec\u3002\u201d\u5979\u5374\u4e0d\u77e5\uff0c \u660e\u6167\u516c\u4e3b\u662f\u5df2\u7ecf\u770b\u89c1\u5979\u548c\u674e\u601d\u5357\u4e86\uff0c\u6b64\u9645\u5979\u7684\u5fc3\u4e2d\u6b63\u5728\u9ed8\u5ff5\u51e0\u53e5\u7ecf\u4e49\uff1a\u201c\u4e00\u5207\u4e16\u95f4\u6cd5\uff0c\u5982\u68a6 \u5982\u5e7b\uff0c\u5982\u96fe\u5982\u7535\uff0c\u5982\u955c\u4e2d\u82b1\uff0c\u5982\u6c34\u4e2d\u6708\u3002\u65e2\u65e0\u6267\u8457\uff0c\u592b\u590d\u4f55\u6c42\uff1f\u201d\u6b63\u662f\uff1a \u3000\u3000\u4e2d\u539f\u5317\u671b\u60c5\u4f55\u9650\uff0c\u4e07\u91cc\u897f\u98ce\u701a\u6d77\u6c99\u3002 \u3000\u3000\uff08\u5168\u4e66\u5b8c\uff09"}),showOnHomepage:!0}]}}]);