"use strict";(self.webpackChunkwebsite=self.webpackChunkwebsite||[]).push([[99695],{28353:(e,n,t)=>{t.r(n),t.d(n,{default:()=>o});var a=t(74848);const o=[{name:"\u7b2c\u4e00\u7ae0\u3000\u5927\u96e8\u5546\u5bb6\u5821",content:(0,a.jsx)(a.Fragment,{children:"\u201c\u80e1\u4e00\u5200\uff0c\u66f2\u6c60\uff0c\u5929\u67a2\uff01\u201d \u3000\u3000\u201c\u82d7\u4eba\u51e4\uff0c\u5730\u4ed3\uff0c\u5408\u8c37\uff01\u201d \u3000\u3000\u4e00\u4e2a\u5636\u54d1\u7684\u55d3\u5b50\u4f4e\u6c89\u5730\u53eb\u7740\u3002\u53eb\u58f0\u4e2d\u5145\u6ee1\u7740\u6028\u6bd2\u548c\u6124\u6012\uff0c\u8bed\u58f0\u4ece\u7259\u9f7f\u7f1d\u4e2d\u8ff8\u51fa\u6765\uff0c\u4f3c\u662f\u5343\u5e74\u4e07\u5e74\u3001\u6c38\u6052\u7684\u8bc5\u5492\uff0c\u6bcf\u4e00\u4e2a\u5b57\u97f3\u4e0a\u6d82\u7740\u8840\u548c\u4ec7\u6068\u3002 \u3000\u3000\u7a81\u7a81\u7a81\u7a81\u56db\u58f0\u54cd\uff0c\u56db\u9053\u91d1\u5149\u95ea\u52a8\uff0c\u56db\u679d\u91d1\u9556\u8fde\u73e0\u53d1\u51fa\uff0c\u5c04\u5411\u4e24\u5757\u6728\u724c\u3002 \u3000\u3000\u6bcf\u5757\u6728\u724c\u7684\u6b63\u9762\u53cd\u9762\u90fd\u7ed8\u7740\u4e00\u4e2a\u5168\u8eab\u4eba\u5f62\uff0c\u4e00\u5757\u4e0a\u7ed8\u7684\u662f\u4e2a\u6d53\u9aef\u7c97\u8c6a\u7684\u5927\u6c49\uff0c\u65c1\u6ce8\u201c\u80e1\u4e00\u5200\u201d\u4e09\u5b57\uff1b\u53e6\u4e00\u5757\u4e0a\u7ed8\u7684\u662f\u4e2a\u7626\u957f\u6c49\u5b50\uff0c\u65c1\u6ce8\u201c\u82d7\u4eba\u51e4\u201d\u4e09\u5b57\uff0c\u4eba\u5f62\u4e0a\u4e66\u660e\u4eba\u4f53\u5468\u8eab\u7a74\u9053\u3002\u6728\u724c\u4e0b\u9762\u63a5\u6709\u4e00\u67c4\uff0c\u4e24\u4e2a\u8eab\u624b\u77eb\u6377\u7684\u58ee\u6c49\u5404\u6301\u4e00\u724c\uff0c\u5728\u7ec3\u6b66\u5385\u4e2d\u6ee1\u5385\u6e38\u8d70\u3002 \u3000\u3000\u5927\u5385\u4e1c\u5317\u89d2\u4e00\u5f20\u6905\u5b50\u4e2d\u5750\u7740\u4e00\u4e2a\u4e94\u5341\u6765\u5c81\u7684\u767d\u53d1\u5a46\u5a46\uff0c\u53e3\u4e2d\u558a\u7740\u80e1\u4e00\u5200\u6216\u82d7\u4eba\u51e4\u7a74\u9053\u7684\u540d\u79f0\u3002\u4e00\u4e2a\u4e8c\u5341\u6765\u5c81\u7684\u82f1\u4fca\u5c11\u5e74\u52b2\u88c5\u7ed3\u675f\uff0c\u9556\u56ca\u4e2d\u5e26\u7740\u5341\u51e0\u679d\u91d1\u9556\uff0c\u542c\u5f97\u90a3\u5a46\u5a46\u558a\u51fa\u7a74\u9053\u540d\u79f0\uff0c\u53f3\u624b\u4e00\u626c\uff0c\u5c31\u662f\u4e00\u9053\u91d1\u5149\u5c04\u51fa\uff0c\u9489\u5411\u6728\u724c\u3002\u4e24\u4e2a\u6301\u724c\u58ee\u6c49\u5934\u6234\u94a2\u4e1d\u7f69\u5b50\uff0c\u4e0a\u8eab\u7a7f\u4e86\u539a\u68c9\u8884\u518d\u7f69\u725b\u76ae\u80cc\u5fc3\uff0c\u552f\u6050\u5c11\u5e74\u5931\u4e86\u51c6\u5934\uff0c\u91d1\u9556\u62db\u547c\u5230\u4ed6\u4eec\u8eab\u4e0a\u3002\u4e24\u4eba\u7a9c\u9ad8\u4f0f\u4f4e\uff0c\u6447\u6446\u6728\u724c\uff0c\u8981\u8ba9\u4ed6\u4e0d\u6613\u6253\u4e2d\u3002 \u3000\u3000\u5927\u5385\u5916\u7684\u7a97\u53e3\uff0c\u4f0f\u7740\u4e00\u4e2a\u5c11\u5973\u3001\u4e00\u4e2a\u9752\u5e74\u6c49\u5b50\u3002\u4e24\u4eba\u5728\u7a97\u7eb8\u4e0a\u6316\u7834\u4e86\u4e24\u4e2a\u5c0f\u5b54\uff0c\u5404\u7528\u53f3\u773c\u51d1\u7740\u5411\u91cc\u5077\u7aa5\u3002\u4e24\u4eba\u89c1\u90a3\u5c11\u5e74\u8eab\u624b\u4e0d\u51e1\uff0c\u53d1\u9556\u751a\u51c6\uff0c\u4e0d\u7531\u5f97\u4e92\u76f8\u5bf9\u671b\u4e86\u4e00\u773c\uff0c\u8138\u4e0a\u90fd\u9732\u51fa\u8bb6\u5f02\u7684\u795e\u8272\u3002 \u3000\u3000\u5929\u7a7a\u9ed1\u6c89\u6c89\u5730\u5806\u6ee1\u4e86\u4e4c\u4e91\u3002\u5927\u96e8\u503e\u76c6\u800c\u4e0b\uff0c\u5939\u7740\u4e00\u9635\u9635\u7684\u7535\u95ea\u96f7\u8f70\uff0c\u52bf\u9053\u5413\u4eba\u3002\u9ec4\u8c46\u5927\u7684\u96e8\u70b9\u6253\u5728\u5730\u4e0b\uff0c\u76f4\u6e85\u5230\u7a97\u5916\u4e24\u4e2a\u5c11\u5e74\u7537\u5973\u7684\u8eab\u4e0a\u3002 \u3000\u3000\u4ed6\u4eec\u90fd\u8eab\u62ab\u6cb9\u5e03\u96e8\u8863\uff0c\u5bf9\u5385\u4e0a\u7684\u4e8b\u5f88\u611f\u597d\u5947\uff0c\u53c8\u518d\u51d1\u773c\u5230\u7a97\u6d1e\u4e0a\u53bb\u770b\u65f6\uff0c\u53ea\u542c\u5f97\u90a3\u5a46\u5a46\u8bf4\u9053\uff1a\u201c\u51c6\u5934\u8fd8\u53ef\u5c06\u5c31\uff0c\u5c31\u662f\u6ca1\u52b2\u513f\uff0c\u4eca\u65e5\u5c31\u7ec3\u5230\u8fd9\u91cc\u3002\u201d\u8bf4\u7740\u6162\u6162\u7ad9\u8d77\u8eab\u6765\u3002 \u3000\u3000\u5c11\u5973\u62c9\u4e86\u90a3\u6c49\u5b50\u4e00\u628a\uff0c\u6025\u5fd9\u8f6c\u8eab\uff0c\u5411\u5916\u9662\u8d70\u53bb\u3002\u90a3\u6c49\u5b50\u4f4e\u58f0\u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\u73a9\u610f\u513f\uff1f\u201d\u90a3\u5c11\u5973\u9053\uff1a\u201c\u4ec0\u4e48\u73a9\u610f\u513f\uff1f\u81ea\u7136\u662f\u7ec3\u9556\u4e86\u3002\u8fd9\u4eba\u7684\u51c6\u5934\u7b97\u662f\u5f88\u4e0d\u9519\u7684\u4e86\u3002\u201d\u90a3\u6c49\u5b50\u9053\uff1a\u201c\u96be\u9053\u7ec3\u9556\u6211\u4e5f\u4e0d\u61c2\uff1f\u53ef\u662f\u6728\u724c\u4e0a\u5e72\u4e48\u5199\u4e86\u4ec0\u4e48\u80e1\u4e00\u5200\u3001\u82d7\u4eba\u51e4\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5973\u9053\uff1a\u201c\u8fd9\u5c31\u6709\u70b9\u90aa\u95e8\u3002\u4f60\u4e0d\u61c2\uff0c\u6211\u600e\u4e48\u5c31\u61c2\u4e86\uff1f\u54b1\u4eec\u95ee\u7239\u7239\u53bb\u3002\u201d \u3000\u3000\u8fd9\u5c11\u5973\u5341\u516b\u4e5d\u5c81\u5e74\u7eaa\uff0c\u4e00\u5f20\u5706\u5706\u7684\u9e45\u86cb\u8138\uff0c\u773c\u73e0\u5b50\u9ed1\u6f06\u6f06\u7684\uff0c\u4e24\u988a\u6655\u7ea2\uff0c\u5468\u8eab\u900f\u7740\u4e00\u80a1\u9752\u6625\u6d3b\u6cfc\u7684\u6c14\u606f\u3002\u90a3\u6c49\u5b50\u6d53\u7709\u5927\u773c\uff0c\u6bd4\u90a3\u5c11\u5973\u5927\u7740\u516d\u4e03\u5c81\uff0c\u795e\u60c5\u7c97\u8c6a\uff0c\u8138\u4e0a\u751f\u6ee1\u7d2b\u8272\u5c0f\u75ae\uff0c\u76f8\u8c8c\u867d\u7136\u6709\u70b9\u4e11\u964b\uff0c\u4f46\u6b65\u5c65\u8f7b\u5065\uff0c\u7cbe\u795e\u9971\u6ee1\uff0c\u5374\u4e5f\u82f1\u6c14\u52c3\u52c3\u3002 \u3000\u3000\u4e24\u4eba\u7a7f\u8fc7\u9662\u5b50\uff0c\u96e8\u8d8a\u4e0b\u8d8a\u5927\uff0c\u6cfc\u5f97\u4e24\u4eba\u8138\u4e0a\u90fd\u662f\u6c34\u73e0\u3002\u5c11\u5973\u53d6\u51fa\u624b\u5e15\u62b9\u53bb\u8138\u4e0a\u6c34\u6ef4\uff0c\u7ea2\u7ea2\u767d\u767d\u7684\u8138\u7ecf\u6c34\u4e00\u6d17\uff0c\u66f4\u662f\u663e\u5f97\u5a07\u5ae9\u3002\u90a3\u6c49\u5b50\u5446\u5446\u5730\u671b\u7740\u5979\uff0c\u4e0d\u7531\u5f97\u5446\u4e86\u3002\u5c11\u5973\u4fa7\u8fc7\u5934\u6765\uff0c\u6545\u610f\u6b6a\u4e86\u96e8\u7b20\uff0c\u8ba9\u7af9\u7b20\u4e0a\u7684\u96e8\u6c34\u90fd\u6d41\u5165\u4e86\u4ed6\u7684\u8863\u9886\u3002\u90a3\u6c49\u5b50\u770b\u5f97\u51fa\u4e86\u795e\uff0c\u7adf\u81ea\u4e0d\u89c9\u3002\u90a3\u5c11\u5973\u6251\u54e7\u4e00\u7b11\uff0c\u8f7b\u8f7b\u53eb\u4e86\u58f0\uff1a\u201c\u50bb\u74dc\uff01\u201d\u8d70\u8fdb\u82b1\u5385\u3002 \u3000\u3000\u5385\u4e2d\u4e1c\u9996\u751f\u4e86\u597d\u5927\u4e00\u5806\u706b\uff0c\u4e8c\u5341\u591a\u4e2a\u4eba\u56e2\u56e2\u56f4\u7740\uff0c\u5728\u706b\u65c1\u70d8\u70e4\u7ed9\u96e8\u6dcb\u6e7f\u4e86\u7684\u8863\u7269\u3002\u8fd9\u7fa4\u4eba\u8eab\u7a7f\u7384\u8272\u6216\u84dd\u8272\u77ed\u8863\uff0c\u6709\u7684\u8eab\u4e0a\u5e26\u7740\u5175\u5203\uff0c\u662f\u4e00\u7fa4\u9556\u5ba2\u3001\u8d9f\u5b50\u624b\u548c\u811a\u592b\u3002\u5385\u4e0a\u7ad9\u7740\u4e09\u4e2a\u6b66\u5b98\u6253\u626e\u7684\u6c49\u5b50\u3002\u8fd9\u4e09\u4eba\u521a\u8fdb\u6765\u907f\u96e8\uff0c\u6b63\u5728\u89e3\u53bb\u6e7f\u8863\uff0c\u7a81\u7136\u89c1\u5230\u8fd9\u660e\u8273\u7167\u4eba\u7684\u5c11\u5973\uff0c\u4e0d\u7531\u5f97\u773c\u775b\u90fd\u662f\u4e00\u4eae\u3002 \u3000\u3000\u90a3\u5c11\u5973\u8d70\u5230\u70e4\u706b\u7684\u4eba\u7fa4\u4e2d\u95f4\uff0c\u628a\u4e00\u4e2a\u7cbe\u5e72\u7626\u524a\u7684\u8001\u4eba\u62c9\u5728\u4e00\u65c1\uff0c\u5c06\u9002\u624d\u5728\u540e\u5385\u89c1\u5230\u7684\u4e8b\u6084\u58f0\u8bf4\u4e86\u3002\u90a3\u8001\u4eba\u7ea6\u83ab\u4e94\u5341\u6765\u5c81\uff0c\u7cbe\u795e\u5065\u65fa\uff0c\u5934\u4e0a\u5fae\u89c1\u82b1\u767d\uff0c\u8eab\u9ad8\u4e0d\u8fc7\u4e94\u5c3a\uff0c\u4f46\u76ee\u5149\u70af\u70af\uff0c\u51db\u7136\u6709\u5a01\u3002\u4ed6\u542c\u4e86\u90a3\u5c11\u5973\u7684\u8bdd\uff0c\u7709\u5934\u4e00\u76b1\uff0c\u4f4e\u58f0\u5475\u8d23\u9053\uff1a\u201c\u53c8\u53bb\u60f9\u4e8b\u751f\u975e\uff01\u82e5\u662f\u8ba9\u4eba\u5bb6\u77e5\u89c9\u4e86\uff0c\u5c82\u4e0d\u662f\u81ea\u8ba8\u6ca1\u8da3\uff1f\u201d\u90a3\u5c11\u5973\u4f38\u4f38\u820c\u5934\uff0c\u7b11\u9053\uff1a\u201c\u7239\uff0c\u8fd9\u8d9f\u966a\u4f60\u8001\u4eba\u5bb6\u51fa\u6765\u8d70\u9556\uff0c\u8fd9\u53ef\u662f\u7b2c\u5341\u516b\u56de\u6328\u9a82\u5566\u3002\u201d\u90a3\u8001\u4eba\u9053\uff1a\u201c\u6211\u6559\u4f60\u7ec3\u529f\u592b\u65f6\uff0c\u65c1\u4eba\u6765\u5077\u77a7\uff0c\u90a3\u600e\u4e48\u5566\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5973\u672c\u6765\u5b09\u76ae\u7b11\u8138\uff0c\u542c\u7236\u4eb2\u8bf4\u4e86\u8fd9\u53e5\u8bdd\uff0c\u4e0d\u7981\u5fc3\u5934\u4e00\u6c89\u3002\u5979\u60f3\u8d77\u53bb\u5e74\u6709\u4eba\u6084\u6084\u5728\u573a\u5916\u5077\u77a7\u5979\u7236\u4eb2\u6f14\u6b66\uff0c\u7236\u4eb2\u660e\u660e\u77e5\u9053\uff0c\u5374\u4e0d\u8bf4\u7834\uff0c\u5728\u8bd5\u53d1\u8896\u7bad\u4e4b\u65f6\uff0c\u7a81\u7136\u4e00\u7bad\uff0c\u5c06\u90a3\u4eba\u6253\u778e\u4e86\u4e00\u53ea\u773c\u775b\u3002\u603b\u7b97\u4ed6\u624b\u4e0b\u5bb9\u60c5\uff0c\u52b2\u9053\u6ca1\u4f7f\u8db3\uff0c\u5426\u5219\u8896\u7bad\u7a7f\u8111\u800c\u8fc7\uff0c\u90a3\u91cc\u8fd8\u6709\u547d\u5728\uff1f\u7236\u4eb2\u540e\u6765\u8bf4\uff0c\u5077\u5e08\u7a83\u827a\uff0c\u4e43\u662f\u6b66\u6797\u4e2d\u7684\u5927\u5fcc\uff0c\u6bd4\u5077\u7a83\u8d22\u7269\u66f4\u4e3a\u4eba\u75db\u6068\u767e\u500d\u3002 \u3000\u3000\u90a3\u5c11\u5973\u4e00\u60f3\uff0c\u5012\u6709\u4e9b\u540e\u6094\uff0c\u9002\u624d\u4e0d\u8be5\u5077\u770b\u65c1\u4eba\u7ec3\u6b66\uff0c\u4f46\u59d1\u5a18\u7684\u813e\u6c14\u8981\u5f3a\u597d\u80dc\uff0c\u5634\u4e0a\u4e0d\u80af\u670d\u8f93\uff0c\u8bf4\u9053\uff1a\u201c\u7239\uff0c\u90a3\u4eba\u7684\u9556\u6cd5\u4e5f\u5e73\u5e38\u5f97\u5f88\uff0c\u4fdd\u7ba1\u6ca1\u4eba\u5077\u5b66\u4e86\u3002\u201d\u8001\u8005\u8138\u4e00\u6c89\uff0c\u65a5\u9053\uff1a\u201c\u4f60\u8fd9\u4e2b\u5934\uff0c\u600e\u4e48\u5f00\u53e3\u5c31\u8bf4\u65c1\u4eba\u7684\u73a9\u610f\u513f\u4e0d\u6210\uff1f\u201d\u90a3\u5c11\u5973\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8c01\u53eb\u6211\u662f\u767e\u80dc\u795e\u62f3\u9a6c\u8001\u9556\u5934\u7684\u5973\u513f\u5462\uff1f\u201d \u3000\u3000\u4e09\u4e2a\u6b66\u5b98\u70e4\u706b\uff0c\u4e0d\u65f6\u659c\u773c\u779f\u5411\u90a3\u7f8e\u8c8c\u5c11\u5973\uff0c\u53ea\u662f\u4ed6\u7236\u5973\u4fe9\u8bdd\u58f0\u5f88\u4f4e\uff0c\u542c\u4e0d\u5230\u8bf4\u4e9b\u4ec0\u4e48\u3002\u90a3\u5c11\u5973\u6700\u540e\u4e00\u53e5\u8bdd\u8bf4\u5f97\u5927\u58f0\u4e86\uff0c\u4e00\u4e2a\u6b66\u5b98\u542c\u5230\u201c\u767e\u80dc\u795e\u62f3\u9a6c\u8001\u9556\u5934\u7684\u5973\u513f\u201d\u51e0\u4e2a\u5b57\uff0c\u77a7\u77a7\u8fd9\u77ed\u5c0f\u7626\u524a\u3001\u9aa8\u5934\u6ca1\u51e0\u4e24\u91cd\u7684\u5e72\u762a\u8001\u5934\uff0c\u53c8\u6a2a\u7740\u773c\u4e00\u626b\u63d2\u5728\u5385\u53e3\u90a3\u679d\u9ec4\u5e95\u9ed1\u4e1d\u7ebf\u7ee3\u7740\u4e00\u5339\u63d2\u7fc5\u98de\u9a6c\u7684\u9556\u65d7\uff0c\u9f3b\u4e2d\u54fc\u4e86\u4e00\u58f0\uff0c\u5fc3\u60f3\uff1a\u201c\u767e\u80dc\u795e\u62f3\uff1f\u5439\u5f97\u597d\u5927\u7684\u6c14\u513f\uff01\u201d \u3000\u3000\u539f\u6765\u8fd9\u8001\u8005\u59d3\u9a6c\uff0c\u540d\u884c\u7a7a\uff0c\u6c5f\u6e56\u4e0a\u5916\u53f7\u53eb\u4f5c\u201c\u767e\u80dc\u795e\u62f3\u201d\u3002\u90a3\u5c11\u5973\u662f\u4ed6\u7684\u72ec\u751f\u7231\u5973\u9a6c\u6625\u82b1\u3002\u8fd9\u540d\u5b57\u900f\u7740\u6709\u4e9b\u513f\u4fd7\u6c14\uff0c\u53ef\u662f\u6c5f\u6e56\u4e0a\u7684\u6b66\u4eba\uff0c\u4e5f\u53ea\u80fd\u7ed9\u59d1\u5a18\u53d6\u4e2a\u4ec0\u4e48\u6625\u554a\u82b1\u554a\u7684\u540d\u5b57\u3002 \u3000\u3000\u8ddf\u5979\u4e00\u8d77\u5077\u770b\u4eba\u5bb6\u7ec3\u9556\u7684\u6c49\u5b50\u59d3\u5f90\uff0c\u5355\u540d\u4e00\u4e2a\u94ee\u5b57\uff0c\u662f\u9a6c\u884c\u7a7a\u7684\u5f92\u5f1f\u3002 \u3000\u3000\u5f90\u94ee\u8e72\u5728\u706b\u5806\u65c1\u70e4\u706b\uff0c\u89c1\u90a3\u6b66\u5b98\u4e0d\u4f4f\u7528\u773c\u779f\u7740\u5e08\u59b9\uff0c\u4e0d\u7531\u5f97\u5fc3\u5934\u6709\u6c14\uff0c\u5411\u4ed6\u6012\u76ee\u77aa\u4e86\u4e00\u773c\u3002\u90a3\u6b66\u5b98\u521a\u597d\u56de\u8fc7\u5934\u6765\uff0c\u4e0e\u4ed6\u76ee\u5149\u767b\u65f6\u5c31\u5bf9\u4e0a\u4e86\uff0c\u5fc3\u60f3\u4f60\u8fd9\u5c0f\u5b50\u6a2a\u7709\u6012\u76ee\u5e72\u4e48\uff0c\u4e5f\u662f\u6076\u72e0\u72e0\u5730\u77aa\u4e86\u4ed6\u4e00\u773c\u3002\u5f90\u94ee\u672c\u5c31\u662f\u9739\u96f3\u706b\u7206\u7684\u813e\u6c14\uff0c\u773c\u89c1\u5bf9\u65b9\u65e0\u793c\uff0c\u5f53\u4e0b\u864e\u8d77\u4e86\u8138\uff0c\u76ee\u4e0d\u8f6c\u775b\u5730\u77aa\u7740\u90a3\u6b66\u5b98\u3002 \u3000\u3000\u90a3\u6b66\u5b98\u7ea6\u83ab\u4e09\u5341\u6765\u5c81\uff0c\u8eab\u9ad8\u8180\u5bbd\uff0c\u4e00\u8138\u7cbe\u608d\u4e4b\u8272\u3002\u4ed6\u54c8\u54c8\u4e00\u7b11\uff0c\u5411\u5de6\u8fb9\u7684\u540c\u4f34\u9053\uff1a\u201c\u4f60\u77a7\u8fd9\u5c0f\u5b50\u6597\u9e21\u513f\u4f3c\u7684\uff0c\u662f\u4f60\u5077\u4e86\u4ed6\u5a46\u5a18\u8fd8\u662f\u600e\u5730\uff1f\u201d\u90a3\u4e24\u4e2a\u6b66\u5b98\u5bf9\u7740\u5f90\u94ee\u54c8\u54c8\u5927\u7b11\u3002 \u3000\u3000\u5f90\u94ee\u5927\u6012\uff0c\u970d\u5730\u7ad9\u8d77\u6765\uff0c\u559d\u9053\uff1a\u201c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u201d\u90a3\u6b66\u5b98\u7b11\u541f\u541f\u5730\u9053\uff1a\u201c\u6211\u8bf4\uff0c\u5c0f\u5b50\u5509\uff0c\u6211\u8bf4\u9519\u5566\uff0c\u6211\u8ddf\u4f60\u966a\u4e0d\u662f\u3002\u201d\u5f90\u94ee\u6027\u5b50\u76f4\uff0c\u542c\u5230\u4eba\u5bb6\u966a\u4e0d\u662f\uff0c\u4e5f\u5c31\u7b97\u4e86\uff0c\u6b63\u8981\u5750\u4e0b\uff0c\u90a3\u4eba\u7b11\u9053\uff1a\u201c\u6211\u77e5\u9053\u4eba\u5bb6\u4e0d\u662f\u5077\u4e86\u4f60\u5a46\u5a18\uff0c\u51c6\u662f\u5077\u4e86\u4f60\u59b9\u5b50\u3002\u201d \u3000\u3000\u5f90\u94ee\u4e00\u8dc3\u800c\u8d77\uff0c\u4fbf\u8981\u6251\u4e0a\u53bb\u52a8\u624b\uff0c\u9a6c\u884c\u7a7a\u559d\u9053\uff1a\u201c\u94ee\u513f\uff0c\u5750\u4e0b\u3002\u201d\u5f90\u94ee\u4e00\u6115\uff0c\u8138\u5b54\u80c0\u5f97\u901a\u7ea2\uff0c\u9053\uff1a\u201c\u5e08\u7236\uff0c\u4f60\u2026\u2026\u4f60\u6ca1\u542c\u89c1\uff1f\u201d\u9a6c\u884c\u7a7a\u6de1\u6de1\u5730\u9053\uff1a\u201c\u4eba\u5bb6\u5b98\u8001\u7237\u4eec\uff0c\u7231\u8bf4\u51e0\u53e5\u7b11\u8bdd\u513f\uff0c\u53c8\u5e72\u4f60\u4ec0\u4e48\u4e8b\u4e86\uff1f\u201d\u5f90\u94ee\u5bf9\u5e08\u7236\u7684\u8bdd\u5411\u6765\u534a\u53e5\u4e0d\u6562\u8fdd\u62d7\uff0c\u72e0\u72e0\u77aa\u7740\u90a3\u4e2a\u6b66\u5b98\uff0c\u5374\u6162\u6162\u5750\u4e86\u4e0b\u6765\u3002\u90a3\u4e09\u4e2a\u6b66\u5b98\u53c8\u662f\u4e00\u9635\u5927\u7b11\uff0c\u66f4\u662f\u8086\u65e0\u5fcc\u60ee\u5730\u77a7\u7740\u9a6c\u6625\u82b1\uff0c\u76ee\u5149\u4e2d\u5c3d\u662f\u6deb\u90aa\u4e4b\u610f\u3002 \u3000\u3000\u9a6c\u6625\u82b1\u89c1\u8fd9\u4e09\u4eba\u65e0\u793c\uff0c\u8981\u5f85\u53d1\u4f5c\uff0c\u5374\u77e5\u7239\u7239\u7d20\u6765\u4e0d\u80af\u5f97\u7f6a\u5b98\u5e9c\uff0c\u5bfb\u601d\u600e\u751f\u60f3\u4e2a\u6cd5\u513f\uff0c\u8ddf\u8fd9\u4e09\u4e2a\u81ed\u5b98\u513f\u6253\u4e00\u573a\u67b6\u3002\u7a81\u7136\u7535\u5149\u4e00\u95ea\uff0c\u7167\u5f97\u6ee1\u5385\u5149\u4eae\uff0c\u63a5\u7740\u4e00\u4e2a\u7126\u96f7\uff0c\u9707\u5f97\u5404\u4eba\u8033\u6735\u55e1\u55e1\u53d1\u54cd\uff0c\u8fd9\u9739\u96f3\u4fbf\u50cf\u662f\u6253\u5728\u8fd9\u5385\u4e0a\u4e00\u822c\u3002\u5929\u4e0a\u5c31\u4f3c\u5f00\u4e86\u4e2a\u7f3a\u53e3\uff0c\u96e8\u6c34\u5927\u7247\u5927\u7247\u5730\u6cfc\u5c06\u4e0b\u6765\u3002 \u3000\u3000\u96e8\u58f0\u4e2d\u53ea\u542c\u5f97\u95e8\u53e3\u4e00\u4eba\u8bf4\u9053\uff1a\u201c\u8fd9\u96e8\u5b9e\u5728\u5927\u5f97\u72e0\u4e86\uff0c\u53ea\u5f97\u501f\u5149\u5728\u5b9d\u5e84\u907f\u4e00\u907f\u3002\u201d\u5e84\u4e0a\u4e00\u540d\u7537\u4ec6\u8bf4\u9053\uff1a\u201c\u5385\u4e0a\u6709\u706b\uff0c\u5927\u7237\u8bf7\u8fdb\u5427\u3002\u201d \u3000\u3000\u5385\u95e8\u63a8\u5f00\uff0c\u8fdb\u6765\u4e86\u4e00\u7537\u4e00\u5973\uff0c\u7537\u7684\u957f\u8eab\u7389\u7acb\uff0c\u6c14\u5b87\u8f69\u6602\uff0c\u80cc\u4e0a\u8d1f\u7740\u4e00\u4e2a\u5305\u88f9\uff0c\u4e09\u5341\u4e03\u516b\u5c81\u5e74\u7eaa\u3002\u5973\u7684\u7ea6\u83ab\u5eff\u4e8c\u4e09\u5c81\uff0c\u80a4\u5149\u80dc\u96ea\uff0c\u7709\u76ee\u5982\u753b\uff0c\u7adf\u662f\u4e00\u4e2a\u7edd\u8272\u4e3d\u4eba\u3002\u9a6c\u6625\u82b1\u672c\u6765\u7b97\u5f97\u662f\u4e2a\u7f8e\u5973\uff0c\u4f46\u8fd9\u4e3d\u4eba\u4e00\u5230\uff0c\u7acb\u65f6\u5c31\u6bd4\u4e86\u4e0b\u53bb\u3002\u4e24\u4eba\u6ca1\u7a7f\u96e8\u8863\uff0c\u90a3\u5c11\u5987\u8eab\u4e0a\u62ab\u7740\u7537\u5b50\u7684\u5916\u8863\uff0c\u5df2\u7136\u5168\u8eab\u5c3d\u6e7f\u3002\u90a3\u7537\u5b50\u643a\u7740\u5c11\u5987\u7684\u624b\uff0c\u4e24\u4eba\u795e\u6001\u4eb2\u5bc6\uff0c\u4f3c\u662f\u4e00\u5bf9\u65b0\u5a5a\u592b\u5987\u3002\u90a3\u7537\u5b50\u627e\u4e86\u4e00\u6346\u9ea6\u79c6\uff0c\u5728\u5730\u4e0a\u94fa\u5e73\u4e86\uff0c\u6276\u7740\u5c11\u5987\u5750\u4e0b\uff0c\u663e\u5f97\u5341\u5206\u7684\u6e29\u67d4\u4f53\u8d34\u3002 \u3000\u3000\u8fd9\u4e8c\u4eba\u8863\u9970\u90fd\u5f88\u534e\u8d35\uff0c\u5c11\u5987\u5934\u4e0a\u63d2\u7740\u4e00\u679d\u9576\u73e0\u7684\u9ec4\u91d1\u51e4\u5934\u9497\uff0c\u770b\u90a3\u73cd\u73e0\u51e0\u6709\u5c0f\u6307\u5934\u5927\u5c0f\uff0c\u5149\u6ed1\u6d51\u5706\uff0c\u751a\u662f\u73cd\u8d35\u3002\u9a6c\u884c\u7a7a\u5fc3\u4e2d\u6697\u6697\u7eb3\u7f55\uff1a\u201c\u8fd9\u4e00\u5e26\u9053\u4e0a\u751a\u4e0d\u592a\u5e73\uff0c\u5f3a\u5f92\u51fa\u6ca1\uff0c\u8fd9\u4e00\u5bf9\u592b\u5987\u975e\u5bcc\u5373\u8d35\uff0c\u4e3a\u4f55\u4e0d\u5e26\u4e00\u540d\u4f8d\u4ece\uff0c\u4e24\u4e2a\u513f\u5b64\u5b64\u5355\u5355\u5730\u8d76\u9053\uff1f\u201d\u9976\u662f\u4ed6\u5728\u6c5f\u6e56\u4e0a\u6df7\u4e86\u4e00\u4e16\uff0c\u5374\u4e5f\u731c\u4e0d\u900f\u8fd9\u4e8c\u4eba\u7684\u6765\u8def\u3002 \u3000\u3000\u9a6c\u6625\u82b1\u89c1\u90a3\u5c11\u5987\u795e\u60c5\u59d4\u987f\uff0c\u53cc\u76ee\u7ea2\u80bf\uff0c\u81ea\u662f\u9014\u4e2d\u9047\u4e0a\u5927\u96e8\uff0c\u5341\u5206\u8f9b\u82e6\uff0c\u8fd9\u822c\u7a7f\u4e86\u6e7f\u8863\u70e4\u706b\uff0c\u6e7f\u6c14\u903c\u5230\u4f53\u5185\uff0c\u975e\u751f\u4e00\u573a\u5927\u75c5\u4e0d\u53ef\uff0c\u5f53\u4e0b\u6253\u5f00\u8863\u7bb1\uff0c\u53d6\u51fa\u4e00\u5957\u81ea\u5df1\u7684\u8863\u670d\uff0c\u8d70\u8fd1\u53bb\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u5a18\u5b50\uff0c\u6211\u8fd9\u5957\u7c97\u5e03\u8863\u670d\uff0c\u4f60\u6362\u4e00\u6362\uff0c\u5f85\u4f60\u70d8\u5e72\u8863\u886b\uff0c\u518d\u6362\u56de\u5427\u3002\u201d\u90a3\u5c11\u5987\u597d\u751f\u611f\u6fc0\uff0c\u5411\u5979\u4e00\u7b11\uff0c\u7ad9\u8d77\u8eab\u6765\uff0c\u76ee\u5149\u4e2d\u4f3c\u4e4e\u5728\u5411\u4e08\u592b\u8be2\u95ee\u3002\u90a3\u7537\u5b50\u70b9\u70b9\u5934\uff0c\u4e5f\u5411\u9a6c\u6625\u82b1\u4e00\u7b11\u793a\u8c22\u3002\u90a3\u5c11\u5987\u62c9\u4e86\u9a6c\u6625\u82b1\u7684\u624b\uff0c\u4e24\u4e2a\u5973\u5b50\u5230\u540e\u5385\u53bb\u501f\u623f\u6362\u8863\u3002 \u3000\u3000\u4e09\u4e2a\u6b66\u5b98\u4e92\u76f8\u4e00\u671b\uff0c\u8138\u4e0a\u73b0\u51fa\u7279\u5f02\u795e\u8272\uff0c\u5fc3\u4e2d\u90fd\u5728\u60f3\u50cf\u90a3\u5c11\u5987\u6362\u8863\u4e4b\u65f6\uff0c\u5b9a\u7136\u7f8e\u4e0d\u53ef\u8a00\u3002\u9002\u624d\u548c\u5f90\u94ee\u6597\u53e3\u7684\u90a3\u4e2a\u6b66\u5b98\u6700\u662f\u5927\u80c6\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u6211\u77a7\u77a7\u53bb\u3002\u201d\u53e6\u4e00\u4e2a\u7b11\u9053\uff1a\u201c\u8001\u4f55\uff0c\u522b\u80e1\u95f9\u3002\u201d\u90a3\u59d3\u4f55\u7684\u6b66\u5b98\u773c\u775b\uff0c\u7ad9\u8d77\u8eab\u6765\uff0c\u8de8\u51fa\u51e0\u6b65\uff0c\u4e00\u8f6c\u5ff5\uff0c\u4ece\u5730\u4e0b\u62fe\u8d77\u8170\u5200\uff0c\u6302\u5728\u8eab\u4e0a\u3002 \u3000\u3000\u5f90\u94ee\u53d7\u4e86\u4ed6\u7684\u7f9e\u8fb1\uff0c\u5fc3\u4e2d\u4e00\u76f4\u6c14\u6124\uff0c\u89c1\u4ed6\u8d70\u5411\u540e\u9662\uff0c\u8f6c\u5934\u5411\u5e08\u7236\u671b\u4e86\u4e00\u773c\uff0c\u53ea\u89c1\u9a6c\u884c\u7a7a\u95ed\u7740\u773c\u775b\u5728\u517b\u795e\uff0c\u53c8\u89c1\u621a\u6768\u4e24\u4f4d\u9556\u5934\u3001\u4e94\u4e2a\u8d9f\u5b50\u624b\u548c\u5341\u591a\u540d\u811a\u592b\u5b88\u5728\u9556\u8f66\u4e4b\u65c1\uff0c\u4e25\u884c\u6212\u5907\uff0c\u51b3\u4e0d\u81f4\u51fa\u4e86\u4e71\u5b50\uff0c\u4e8e\u662f\u8ddf\u968f\u5728\u90a3\u6b66\u5b98\u8eab\u540e\u3002 \u3000\u3000\u90a3\u6b66\u5b98\u542c\u5230\u80cc\u540e\u811a\u6b65\u54cd\uff0c\u8f6c\u8fc7\u5934\u6765\uff0c\u89c1\u662f\u5f90\u94ee\uff0c\u54a7\u5634\u4e00\u7b11\u9053\uff1a\u201c\u5c0f\u5b50\uff0c\u4f60\u597d\uff01\u201d\u5f90\u94ee\u9053\uff1a\u201c\u81ed\u5b98\u513f\uff0c\u4f60\u597d\u3002\u201d\u90a3\u6b66\u5b98\u7b11\u9053\uff1a\u201c\u60f3\u6328\u63cd\uff0c\u662f\u4e0d\u662f\uff1f\u201d\u5f90\u94ee\u9053\uff1a\u201c\u662f\u554a\u3002\u6211\u5e08\u7236\u4e0d\u8bb8\u6253\u4f60\u3002 \u3000\u3000\u54b1\u4eec\u6084\u6084\u5730\u6253\u4e00\u67b6\uff0c\u597d\u4e0d\u597d\uff1f\u201d\u90a3\u6b66\u5b98\u81ea\u6043\u6b66\u827a\u4e86\u5f97\uff0c\u6ca1\u5c06\u8fd9\u695e\u5c0f\u5b50\u77a7\u5728\u773c\u91cc\uff0c\u53ea\u662f\u89c1\u4ed6\u9556\u884c\u4eba\u591a\uff0c\u5df1\u65b9\u53ea\u6709\u4e09\u4eba\uff0c\u82e5\u662f\u7fa4\u6bb4\uff0c\u5b9a\u8981\u5403\u4e8f\uff0c\u8fd9\u695e\u5c0f\u5b50\u8981\u6084\u6084\u6253\u67b6\uff0c\u90a3\u662f\u518d\u597d\u4e5f\u6ca1\u6709\uff0c\u4fbf\u7b11\u7740\u70b9\u5934\u9053\uff1a\u201c\u597d\u554a\uff0c\u54b1\u4eec\u8d70\u5f97\u8fdc\u4e9b\u3002\u82e5\u7ed9\u4f60\u5e08\u7236\u542c\u89c1\u4e86\uff0c\u8fd9\u67b6\u5c31\u6253\u4e0d\u6210\u3002\u201d \u3000\u3000\u4e24\u4eba\u7a7f\u8fc7\u5929\u4e95\uff0c\u8981\u5bfb\u4e2a\u6ca1\u4eba\u7684\u6240\u5728\u52a8\u624b\uff0c\u5ffd\u89c1\u56de\u5eca\u4e0a\u8f6c\u51fa\u4e00\u4e2a\u4eba\u6765\u3002\u90a3\u4eba\u8eab\u7a7f\u7ef8\u888d\uff0c\u7709\u6e05\u76ee\u79c0\uff0c\u6b63\u662f\u9002\u624d\u7ec3\u9556\u7684\u5c11\u5e74\u3002\u5f90\u94ee\u5fc3\u4e2d\u4e00\u52a8\uff1a\u201c\u501f\u4ed6\u7684\u6b66\u5385\u6253\u67b6\u6700\u597d\u4e0d\u8fc7\u3002\u201d\u4e8e\u662f\u4e0a\u524d\u4e00\u62b1\u62f3\uff0c\u8bf4\u9053\uff1a\u201c\u5144\u957f\u8bf7\u4e86\u3002\u201d\u90a3\u5c11\u5e74\u8fd8\u4e86\u4e00\u63d6\uff0c\u8bf4\u9053\uff1a\u201c\u8fbe\u5b98\u6709\u4f55\u5429\u5490\uff1f\u201d\u5f90\u94ee\u6307\u7740\u6b66\u5b98\u9053\uff1a\u201c\u5728\u4e0b\u8ddf\u8fd9\u4e2a\u603b\u7237\u6709\u70b9\u5c0f\u8fc7\u8282\uff0c\u60f3\u501f\u5144\u957f\u7684\u7ec3\u6b66\u5385\u4e00\u7528\u3002\u201d\u90a3\u5c11\u5e74\u597d\u751f\u5947\u602a\uff0c\u5fc3\u9053\uff1a\u201c\u4f60\u600e\u77e5\u6211\u5bb6\u6709\u7ec3\u6b66\u5385\uff1f\u201d\u4f46\u5b66\u6b66\u4e4b\u4eba\uff0c\u542c\u5230\u65c1\u4eba\u8981\u6bd4\u6b66\u6253\u67b6\uff0c\u53ef\u6bd4\u4ec0\u4e48\u90fd\u6b22\u559c\uff0c\u5f53\u5373\u7b54\u9053\uff1a\u201c\u597d\u6781\uff0c\u597d\u6781\uff01\u201d\u5f53\u4e0b\u9886\u4e86\u4e8c\u4eba\u8d70\u8fdb\u7ec3\u6b66\u5385\u3002 \u3000\u3000\u8fd9\u65f6\u8001\u5a46\u5a46\u548c\u5e84\u4e01\u7b49\u90fd\u5df2\u6563\u53bb\uff0c\u7ec3\u6b66\u5385\u4e0a\u66f4\u65e0\u65c1\u4eba\u3002\u90a3\u6b66\u5b98\u89c1\u56db\u58c1\u519b\u5668\u67b6\u4e0a\u5200\u67aa\u5251\u621f\u4e00\u5e94\u4ff1\u5168\uff0c\u6b64\u5916\u6c99\u5305\u3001\u7bad\u9776\u3001\u77f3\u9501\u3001\u77f3\u9f13\u653e\u5f97\u6ee1\u5730\uff0c\u897f\u9996\u5730\u4e0b\u8fd8\u5b89\u7740\u4e03\u5341\u4e8c\u6839\u6885\u82b1\u6869\uff0c\u6697\u6697\u70b9\u5934\uff0c\u5fc3\u60f3\uff1a\u201c\u539f\u6765\u8fd9\u4e00\u5bb6\u4eba\u4f1a\u6b66\uff0c\u53ea\u6015\u529f\u592b\u8fd8\u4e0d\u9519\u3002\u201d\u4e8e\u662f\u5411\u90a3\u5c11\u5e74\u4e00\u62b1\u62f3\uff0c\u8bf4\u9053\uff1a\u201c\u5728\u4e0b\u6765\u8d35\u5e84\u907f\u96e8\uff0c\u8fd8\u6ca1\u8bf7\u6559\u4e3b\u4eba\u9ad8\u59d3\u5927\u540d\u3002\u201d\u90a3\u5c11\u5e74\u5fd9\u5373\u8fd8\u793c\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u4eba\u59d3\u5546\uff0c\u540d\u5b9d\u9707\u3002\u4e24\u4f4d\u9ad8\u59d3\u5927\u540d\uff1f\u201d\u5f90\u94ee\u62a2\u7740\u9053\uff1a\u201c\u6211\u53eb\u5f90\u94ee\uff0c\u6211\u5e08\u7236\u662f\u98de\u9a6c\u9556\u5c40\u603b\u9556\u5934\uff0c\u767e\u80dc\u795e\u62f3\u9a6c\u884c\u7a7a\u3002\u201d\u8bf4\u7740\u5411\u6b66\u5b98\u77aa\u4e86\u4e00\u773c\uff0c\u5fc3\u9053\uff1a\u201c\u4f60\u542c\u4e86\u6211\u5e08\u7236\u7684\u540d\u5934\uff0c\u53ef\u77e5\u9053\u5389\u5bb3\u4e86\u5417\uff1f\u201d \u3000\u3000\u5546\u5b9d\u9707\u62f1\u624b\u9053\uff1a\u201c\u4e45\u4ef0\uff0c\u4e45\u4ef0\u3002\u8bf7\u6559\u8fd9\u4e00\u4f4d\u3002\u201d\u90a3\u6b66\u5b98\u9053\uff1a\u201c\u5728\u4e0b\u662f\u5fa1\u524d\u4f8d\u536b\u4f55\u601d\u8c6a\u3002\u201d\u5546\u5b9d\u9707\u9053\uff1a\u201c\u539f\u6765\u662f\u4e00\u4f4d\u4f8d\u536b\u5927\u4eba\u3002\u5c0f\u4eba\u7d20\u95fb\u4eac\u5e08\u6709\u5927\u5185\u5341\u516b\u9ad8\u624b\uff0c\u60f3\u6765\u4f55\u5927\u4eba\u90fd\u662f\u77e5\u4ea4\u3002\u201d\u4f55\u601d\u8c6a\u9053\uff1a\u201c\u90a3\u5927\u534a\u4e5f\u76f8\u719f\u7684\u3002\u201d\u5176\u5b9e\u7687\u5e1d\u8eab\u8fb9\u7684\u4f8d\u536b\u5171\u5206\u56db\u7b49\uff0c\u4f8d\u536b\u73ed\u9886\uff0c\u4ec0\u957f\uff0c\u4e00\u3001\u4e8c\u3001\u4e09\u7b49\u53ca\u84dd\u7fce\u4f8d\u536b\uff0c\u90fd\u7531\u6b63\u9ec4\u3001\u9576\u9ec4\u3001\u6b63\u767d\u5185\u4e09\u65d7\u7684\u5b97\u5ba4\u4eb2\u8d35\u5b50\u5f1f\u5145\u4efb\u3002\u6c49\u4f8d\u536b\u5c5e\u4e8e\u7b2c\u56db\u7b49\uff0c\u8fd9\u4f55\u601d\u8c6a\u5728\u4f8d\u536b\u5904\u4e2d\u53ea\u662f\u6700\u672b\u7b49\u7684\u84dd\u7fce\u4f8d\u536b\uff0c\u6240\u8c13\u5927\u5185\u5341\u516b\u9ad8\u624b\uff0c\u90a3\u662f\u4ed6\u8bc6\u5f97\u4eba\u5bb6\uff0c\u4eba\u5bb6\u5c31\u4e0d\u8bc6\u5f97\u4ed6\u4e86\u3002 \u3000\u3000\u5f90\u94ee\u5927\u58f0\u9053\uff1a\u201c\u5546\u516c\u5b50\uff0c\u4f60\u5c31\u7ed9\u505a\u4e2a\u516c\u8bc1\u3002\u6211\u8ddf\u8fd9\u59d3\u4f55\u7684\u516c\u516c\u5e73\u5e73\u6253\u4e00\u67b6\uff0c\u4e0d\u7ba1\u662f\u8c01\u8f93\u8c01\u8d62\uff0c\u90fd\u4e0d\u8bb8\u5411\u65c1\u4eba\u8bf4\u8d77\u3002\u201d\u4ed6\u662f\u751f\u6015\u5e08\u7236\u77e5\u9053\u4e86\u8d23\u9a82\u3002\u4f55\u601d\u8c6a\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u80dc\u4e86\u4f60\u8fd9\u695e\u5c0f\u5b50\u4e0d\u8db3\u4e3a\u6b66\uff0c\u8fd8\u503c\u5f97\u5411\u65c1\u4eba\u5439\u5927\u6c14\u7684\u4e48\uff1f\u695e\u5c0f\u5b50\uff0c\u4e0a\u554a\u3002\u201d\u4e00\u634b\u957f\u888d\uff0c\u62c9\u8d77\u888d\u89d2\uff0c\u5728\u8170\u5e26\u4e2d\u585e\u597d\u3002\u5f90\u94ee\u8131\u4e0b\u957f\u888d\uff0c\u5c06\u8fab\u5b50\u76d8\u5728\u5934\u9876\uff0c\u6446\u4e2a\u201c\u5bf9\u62f3\u201d\uff0c\u53cc\u8db3\u5e76\u62e2\uff0c\u53cc\u624b\u63e1\u62f3\u76f8\u5bf9\uff0c\u5012\u662f\u795e\u5b9a\u6c14\u95f2\u3002 \u3000\u3000\u4f55\u601d\u8c6a\u89c1\u4ed6\u8fd9\u59ff\u5f0f\u662f\u201c\u67e5\u62f3\u201d\u95e8\u4eba\u548c\u4eba\u52a8\u624b\u7684\u8d77\u624b\u5f0f\uff0c\u5df2\u653e\u4e0b\u4e86\u4e00\u5927\u534a\u5fc3\uff0c\u5fc3\u9053\uff1a\u201c\u4ec0\u4e48\u767e\u80dc\u795e\u62f3\uff01\u8fd9\u67e5\u62f3\u4e09\u5c81\u5c0f\u5b69\u513f\u4e5f\u4f1a\uff0c\u6709\u4ec0\u4e48\u5e0c\u7f55\uff1f\u201d\u539f\u6765\u201c\u6f6d\u3001\u67e5\u3001\u82b1\u3001\u6d2a\u201d\uff0c\u5411\u79f0\u5317\u62f3\u56db\u5927\u5bb6\uff0c\u6307\u6f6d\u817f\u3001\u67e5\u62f3\u3001\u82b1\u62f3\u3001\u6d2a\u95e8\u56db\u6d3e\u62f3\u672f\u800c\u8a00\uff0c\u5728\u5317\u65b9\u6d41\u4f20\u6781\u5e7f\uff0c\u4efb\u4f55\u7ec3\u62f3\u4e4b\u4eba\u90fd\u7565\u77e5\u4e00\u4e8c\uff0c\u7b97\u5f97\u662f\u62f3\u672f\u4e2d\u7684\u5165\u95e8\u529f\u592b\u3002\u4f55\u601d\u8c6a\u89c1\u5bf9\u624b\u62f3\u6cd5\u5e73\u5e38\uff0c\u5411\u5546\u5b9d\u9707\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u732e\u4e11\uff01\u201d\u4e00\u62db\u201c\u4e0a\u6b65\u91ce\u9a6c\u5206\u9b03\u201d\uff0c\u5411\u5f90\u94ee\u6253\u4e86\u8fc7\u53bb\uff0c\u4ed6\u4f7f\u7684\u662f\u592a\u6781\u62f3\u3002 \u3000\u3000\u5176\u65f6\u592a\u6781\u95e8\u7684\u6b66\u529f\u58f0\u52bf\u751a\u76db\uff0c\u4eba\u4eba\u5747\u77e5\u662f\u6781\u5389\u5bb3\u7684\u5185\u5bb6\u62f3\u6cd5\u3002 \u3000\u3000\u5f90\u94ee\u4e0d\u6562\u6020\u6162\uff0c\u5de6\u811a\u5411\u540e\u8e0f\u51fa\uff0c\u4e0a\u8eab\u8f6c\u6210\u5750\u76d8\u5f0f\uff0c\u53f3\u624b\u6309\u3001\u5de6\u624b\u64a9\uff0c\u4e00\u62db\u201c\u540e\u53c9\u6b65\u64a9\u638c\u201d\u51fa\u624b\u6781\u662f\u5feb\u6377\u3002\u4f55\u601d\u8c6a\u89c1\u6765\u62db\u52b2\u9053\u4e0d\u5f31\uff0c\u5fd9\u4f7f\u4e00\u62db\u201c\u8f6c\u8eab\u62b1\u864e\u5f52\u5c71\u201d\uff0c\u907f\u5f00\u4e86\u8fd9\u4e00\u64a9\u3002 \u3000\u3000\u5f90\u94ee\u4f7f\u4e00\u62db\u201c\u5f13\u6b65\u67b6\u6253\u201d\uff0c\u53f3\u62f3\u547c\u7684\u4e00\u58f0\u51fb\u51fa\uff0c\u76f4\u6251\u5bf9\u65b9\u9762\u95e8\u3002 \u3000\u3000\u4f55\u601d\u8c6a\u4e0d\u53ca\u907f\u8ba9\uff0c\u4f7f\u4e00\u62db\u201c\u5982\u5c01\u4f3c\u95ed\u201d\uff0c\u53cc\u638c\u4e00\u5c01\u3002\u4e8c\u4eba\u62f3\u638c\u76f8\u4ea4\uff0c\u4f55\u601d\u8c6a\u53ea\u611f\u624b\u8155\u9690\u9690\u751f\u75bc\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u86ee\u529b\u5012\u5927\u3002\u201d \u3000\u3000\u970e\u65f6\u4e4b\u95f4\uff0c\u4e8c\u4eba\u5404\u5c55\u62f3\u6cd5\uff0c\u62c6\u4e86\u5341\u4f59\u62db\u3002\u5546\u5b9d\u9707\u7ad9\u7740\u65c1\u89c2\uff0c\u89c1\u5f90\u94ee\u811a\u6b65\u6c89\u7a33\uff0c\u51fa\u62f3\u6709\u529b\uff0c\u4f55\u601d\u8c6a\u5374\u662f\u8eab\u5f62\u98d8\u5ffd\uff0c\u663e\u7136\u8f7b\u529f\u9887\u6709\u6839\u57fa\u3002 \u3000\u3000\u6597\u5230\u9163\u5904\uff0c\u4f55\u601d\u8c6a\u54c8\u54c8\u4e00\u7b11\uff0c\u4e00\u638c\u51fb\u4e2d\u5f90\u94ee\u80a9\u5934\u3002\u5f90\u94ee\u98de\u811a\u8e22\u53bb\uff0c\u4f55\u601d\u8c6a\u4fa7\u8eab\u95ea\u907f\uff0c\u4e00\u62db\u201c\u7389\u5973\u7a7f\u68ad\u201d\uff0c\u62cd\u7684\u4e00\u58f0\uff0c\u53c8\u51fb\u4e2d\u5f90\u94ee\u624b\u81c2\u3002\u5f90\u94ee\u66f4\u4e0d\u7406\u4f1a\uff0c\u62a1\u62f3\u6025\u653b\uff0c\u7a81\u7136\u76f4\u51fa\u4e00\u62f3\uff0c\u4e00\u62db\u201c\u5f13\u6b65\u5288\u6253\u201d\uff0c\u7830\u7684\u4e00\u54cd\uff0c\u6253\u4e2d\u5bf9\u65b9\u80f8\u53e3\u3002\u8fd9\u4e00\u62f3\u7740\u529b\u6781\u6c89\uff0c\u4f55\u601d\u8c6a\u811a\u6b65\u8e09\u8dc4\uff0c\u5411\u540e\u9000\u4e86\u51e0\u6b65\uff0c\u7ec8\u4e8e\u4e00\u4ea4\u5750\u5012\u3002\u53ea\u542c\u65c1\u8fb9\u4e00\u4e2a\u5973\u5b50\u58f0\u97f3\u5a07\u58f0\u53eb\u9053\uff1a\u201c\u597d\uff01\u201d \u3000\u3000\u5546\u5b9d\u9707\u56de\u8fc7\u5934\u53bb\uff0c\u53ea\u89c1\u4e24\u4e2a\u5973\u5b50\u7ad9\u5728\u5385\u53e3\uff0c\u4e00\u662f\u5c11\u5987\uff0c\u53e6\u4e00\u4e2a\u5374\u662f\u4e2a\u95fa\u5973\u3002\u4ed6\u5148\u524d\u51dd\u795e\u89c2\u6597\uff0c\u4e0d\u77e5\u8eab\u540e\u6709\u4eba\u3002\u539f\u6765\u9a6c\u6625\u82b1\u548c\u90a3\u5c11\u5987\u6362\u4e86\u8863\u670d\u7ecf\u8fc7\u6b64\u5904\uff0c\u542c\u5230\u547c\u53f1\u6bd4\u6b66\u4e4b\u58f0\uff0c\u5728\u5385\u53e3\u4e00\u671b\uff0c\u7adf\u662f\u5e08\u5144\u548c\u90a3\u6b66\u5b98\u6253\u67b6\uff0c\u8fd9\u65f6\u89c1\u5e08\u5144\u5f97\u80dc\uff0c\u4e0d\u7531\u5f97\u51fa\u58f0\u559d\u5f69\u3002 \u3000\u3000\u4f55\u601d\u8c6a\u7ed9\u8fd9\u4e00\u62f3\u6253\u5f97\u597d\u4e0d\u75bc\u75db\uff0c\u5728\u5973\u5b50\u9762\u524d\u4e22\u8138\u51fa\u4e11\uff0c\u66f4\u662f\u8001\u7f9e\u6210\u6012\uff0c\u5f53\u5373\u4e00\u8dc3\u800c\u8d77\uff0c\u4e58\u7740\u8df3\u8dc3\u4e4b\u52bf\uff0c\u5df2\u62bd\u8170\u5200\u5728\u624b\uff0c\u4e0a\u6b65\u76f4\u5288\u3002\u5f90\u94ee\u6beb\u4e0d\u754f\u60e7\uff0c\u4ecd\u4ee5\u201c\u67e5\u62f3\u201d\u7a7a\u624b\u548c\u4ed6\u76f8\u6597\uff0c\u53ea\u662f\u5fcc\u60ee\u5bf9\u65b9\u5175\u5668\u950b\u5229\uff0c\u5df2\u662f\u95ea\u907f\u591a\uff0c\u8fdb\u653b\u5c11\u4e86\u3002\u9a6c\u6625\u82b1\u89c1\u8fd9\u6b66\u5b98\u8138\u4e0a\u795e\u60c5\u72e0\u6076\uff0c\u5e76\u975e\u5bfb\u5e38\u6253\u67b6\uff0c\u5df2\u662f\u62da\u547d\u4e00\u822c\uff0c\u4e0d\u7531\u5f97\u6709\u4e9b\u803d\u5fc3\u3002\u90a3\u5c11\u5987\u626f\u626f\u5979\u7684\u8863\u8896\uff0c\u9053\uff1a\u201c\u54b1\u4eec\u8d70\u5427\uff01\u6211\u6700\u6068\u4eba\u52a8\u5200\u5b50\u51fa\u62f3\u5934\u3002\u201d \u3000\u3000\u5f53\u6b64\u60c5\u52bf\uff0c\u9a6c\u6625\u82b1\u54ea\u91cc\u80af\u8d70\uff0c\u53ea\u9053\uff1a\u201c\u518d\u770b\u4e00\u4f1a\u513f\u3002\u201d\u90a3\u5c11\u5987\u7709\u5934\u4e00\u76b1\uff0c\u7adf\u81ea\u8d70\u4e86\u3002 \u3000\u3000\u5546\u5b9d\u9707\u51dd\u795e\u770b\u7740\u90a3\u6b66\u5b98\u7684\u5200\u52bf\uff0c\u53c8\u7559\u5fc3\u5f90\u94ee\u95ea\u907f\u548c\u4e0a\u6b65\u62a2\u653b\u4e4b\u6cd5\uff0c\u624b\u4e0a\u6697\u6263\u4e00\u679d\u91d1\u9556\uff0c\u82e5\u90a3\u6b66\u5b98\u7528\u5200\u4f24\u4eba\uff0c\u4ed6\u5c31\u8981\u4f38\u624b\u76f8\u6551\u3002\u4f46\u89c1\u5f90\u94ee\u53cc\u76ee\u7d27\u7d27\u76ef\u4f4f\u5200\u950b\uff0c\u5200\u950b\u5411\u4e1c\uff0c\u4ed6\u773c\u775b\u8ddf\u5230\u4e1c\uff0c\u5200\u950b\u524a\u5411\u897f\uff0c\u773c\u775b\u4e5f\u8ddf\u5230\u897f\uff0c\u773c\u89c1\u4e00\u5200\u8fce\u9762\u780d\u6765\uff0c\u4ed6\u8eab\u5b50\u7565\u95ea\uff0c\u98de\u811a\u5411\u654c\u4eba\u624b\u8155\u4e0a\u8e22\u53bb\u3002\u4f55\u601d\u8c6a\u56de\u5200\u524a\u8db3\uff0c\u5f90\u94ee\u957f\u81c2\u6025\u4f38\uff0c\u7830\u7684\u4e00\u54cd\uff0c\u4e00\u62f3\u6b63\u4e2d\u4ed6\u9f3b\u6881\u3002\u4f55\u601d\u8c6a\u5927\u75db\uff0c\u624b\u811a\u7565\u7f13\uff0c\u5f90\u94ee\u5de6\u624b\u6325\u51fa\uff0c\u6293\u4f4f\u4ed6\u53f3\u8155\u4e00\u62ff\u4e00\u626d\uff0c\u5c06\u8170\u5200\u593a\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u4f55\u601d\u8c6a\u6015\u4ed6\u987a\u52bf\u6325\u5200\u524a\u6765\uff0c\u5fd9\u5411\u540e\u8dc3\uff0c\u4e3e\u624b\u5f80\u8138\u4e0a\u4e00\u62b9\uff0c\u6ee1\u624b\u662f\u8840\u3002\u5f90\u94ee\u5c06\u8170\u5200\u5f80\u5730\u4e0b\u4e00\u6454\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8fd8\u6562\u778e\u7740\u773c\u775b\u9a82\u4eba\uff1f\u201d\u4f55\u601d\u8c6a\u6ee1\u8138\u7f9e\u60ed\uff0c\u4e0d\u6562\u4f5c\u58f0\u3002 \u3000\u3000\u5546\u5b9d\u9707\u4f38\u624b\u4e00\u62c9\u5f90\u94ee\u540e\u895f\uff0c\u4f7f\u4e2a\u773c\u8272\u3002\u5f90\u94ee\u5c1a\u672a\u4f1a\u610f\uff0c\u5546\u5b9d\u9707\u5df2\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u53cc\u65b9\u4e0d\u5206\u80dc\u8d25\u3002\u597d\u5566\uff0c\u5927\u5bb6\u6b66\u529f\u4e00\u822c\u9ad8\u660e\uff0c\u5c0f\u5f1f\u4f69\u670d\u5f97\u7d27\u2026\u2026\u201d\u5f90\u94ee\u6025\u9053\uff1a\u201c\u600e\u2026\u2026\u600e\u662f\u4e0d\u5206\u80dc\u8d25\uff1f\u201d\u5546\u5b9d\u9707\u9053\uff1a\u201c\u4e24\u4f4d\u6b66\u529f\u5404\u6709\u72ec\u5230\u4e4b\u5904\u3002\u5f90\u5144\u7684\u67e5\u62f3\u7eaf\u719f\u3002\u4f55\u5927\u4eba\u7684\u592a\u6781\u62f3\u548c\u592a\u6781\u5200\u66f4\u662f\u5389\u5bb3\u4e4b\u6781\u3002\u5f90\u5144\uff0c\u4f60\u4e00\u65f6\u4fa5\u5e78\uff0c\u5176\u5b9e\u8bb2\u771f\u529f\u592b\uff0c\u8fd8\u5f97\u7b97\u4f55\u5927\u4eba\u3002\u201d\u4e00\u9762\u8bf4\uff0c\u4e00\u9762\u53d6\u51fa\u624b\u5e15\uff0c\u5e2e\u4f55\u601d\u8c6a\u62b9\u53bb\u9f3b\u8840\u3002\u5f90\u94ee\u8fd8\u8981\u518d\u4e89\uff0c\u9a6c\u6625\u82b1\u9053\uff1a\u201c\u5e08\u54e5\uff0c\u522b\u7406\u4ed6\u3002\u54b1\u4eec\u51fa\u53bb\u3002\u201d \u3000\u3000\u5f90\u94ee\u6253\u4e86\u4f55\u601d\u8c6a\u4e24\u62f3\uff0c\u4e00\u53e3\u6076\u6c14\u5df2\u7ecf\u51fa\u4e86\uff0c\u4f46\u5546\u5b9d\u9707\u8bf4\u8bdd\u542b\u7cca\uff0c\u660e\u660e\u8892\u62a4\u5bf9\u65b9\uff0c\u5012\u4f3c\u81ea\u5df1\u8f93\u4e86\uff0c\u8d8a\u60f3\u8d8a\u6012\uff0c\u72e0\u72e0\u671b\u4e86\u4ed6\u4e00\u773c\uff0c\u968f\u7740\u5e08\u59b9\u51fa\u53bb\u3002\u8d70\u5230\u5929\u4e95\uff0c\u5929\u7a7a\u8f70\u9686\u9686\u4e00\u7247\u96f7\u58f0\u8fc7\u53bb\uff0c\u96f7\u58f0\u4e2d\u5939\u7740\u5546\u5b9d\u9707\u3001\u4f55\u601d\u8c6a\u7684\u5927\u7b11\u4e4b\u58f0\uff0c\u663e\u7136\u8fd9\u4e8c\u4eba\u5728\u80cc\u540e\u7b11\u4ed6\u3002 \u3000\u3000\u4ed6\u867d\u6253\u67b6\u83b7\u80dc\uff0c\u4f46\u8d8a\u60f3\u8d8a\u662f\u4e0d\u5fff\uff0c\u6c14\u9f13\u9f13\u5730\u5750\u5728\u706b\u65c1\u3002\u53ea\u89c1\u5e08\u7236\u53cc\u76ee\u4f3c\u5f00\u4f3c\u95ed\uff0c\u7761\u610f\u751a\u6d53\u3002\u8fc7\u4e86\u4e00\u4f1a\uff0c\u4f55\u601d\u8c6a\u8d70\u4e86\u51fa\u6765\uff0c\u4e0d\u77e5\u8ddf\u90a3\u4e24\u4e2a\u6b66\u5b98\u8bf4\u4e9b\u4ec0\u4e48\u7325\u4eb5\u8a00\u8bed\uff0c\u4e09\u4eba\u4e00\u9f50\u54c8\u54c8\u5927\u7b11\uff0c\u4e0d\u65f6\u659c\u76ee\u779f\u90a3\u7f8e\u8c8c\u5c11\u5987\u3002 \u3000\u3000\u9a6c\u884c\u7a7a\u6162\u6162\u7ad9\u8d77\uff0c\u4f38\u4e86\u4e2a\u61d2\u8170\uff0c\u8d70\u5230\u9556\u8f66\u65c1\u8fb9\u68c0\u89c6\uff0c\u5ffd\u7136\u53eb\u9053\uff1a\u201c\u94ee\u513f\uff0c\u8fc7\u6765\uff0c\u4f60\u77a7\u8fd9\u513f\u600e\u4e48\u5566\uff1f\u201d\u5f90\u94ee\u542c\u5e08\u7236\u53eb\u4ed6\uff0c\u8d76\u5fd9\u8d77\u8eab\u8fc7\u53bb\u3002\u9a6c\u884c\u7a7a\u4fa7\u8fc7\u8eab\u5b50\uff0c\u9762\u5411\u5899\u58c1\uff0c\u4f38\u624b\u6574\u7406\u9556\u8f66\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u4e0d\u957f\u8fdb\u7684\u4e1c\u897f\uff0c\u4f60\u90a3\u62db\u2018\u57ab\u6b65\u8e39\u817f\u2019\u600e\u4e48\u8e39\u504f\u4e86\uff1f \u3000\u3000\u5426\u5219\u54ea\u7528\u8ddf\u4ed6\u7f20\u6597\u8fd9\u4e48\u4e45\uff1f\u201d\u5f90\u94ee\u5413\u4e86\u4e00\u8df3\uff0c\u98a4\u58f0\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u8001\u4eba\u5bb6\u90fd\u77a7\u89c1\u5566\uff1f\u201d\u9a6c\u884c\u7a7a\u9053\uff1a\u201c\u54fc\uff0c\u4f60\u83ab\u60f3\u5728\u5e08\u7236\u9762\u524d\u6363\u9b3c\u3002\u4ed6\u4f7f\u90a3\u62db\u2018\u63d0\u6b65\u9ad8\u63a2\u9a6c\u2019\u65f6\uff0c\u4f60\u5e72\u4e48\u4e0d\u4f7f\u2018\u5f13\u6b65\u53cc\u63a8\u638c\u2019\uff1f\u8fce\u9762\u76f4\u51fb\uff0c\u65e9\u5c31\u80dc\u4e86\u3002\u4f60\u5c31\u662f\u80c6\u5c0f\u6015\u6b7b\u3002\u201d\u5f90\u94ee\u56de\u60f3\u9002\u624d\u76f8\u6597\u4e4b\u65f6\uff0c\u521d\u65f6\u4e0d\u77e5\u654c\u4eba\u865a\u5b9e\uff0c\u679c\u7136\u6709\u4e9b\u5bb3\u6015\uff0c\u6709\u51e0\u62db\u4f7f\u5f97\u592a\u8fc7\u7a33\u91cd\u4e86\u4e9b\u3002\u770b\u6765\u5e08\u7236\u88c5\u4f5c\u4e0d\u77e5\uff0c\u5176\u5b9e\u662f\u8eb2\u5728\u7a97\u5916\u89c2\u770b\u3002 \u3000\u3000\u9a6c\u884c\u7a7a\u53c8\u9053\uff1a\u201c\u5feb\u8fdb\u53bb\u8c22\u8c22\u90a3\u59d3\u5546\u7684\u5427\u3002\u4eba\u5bb6\u5e74\u7eaa\u6bd4\u4f60\u8f7b\uff0c\u53ef\u6709\u591a\u7cbe\u660e\u80fd\u5e72\u3002\u201d\u5f90\u94ee\u5927\u4e3a\u8be7\u5f02\uff0c\u9053\uff1a\u201c\u5e08\u7236\uff0c\u8c22\u4ec0\u4e48\uff1f\u8fd9\u59d3\u5546\u7684\u504f\u5fc3\uff0c\u4e0d\u662f\u597d\u4eba\u3002\u201d\u9a6c\u884c\u7a7a\u51b7\u7b11\u9053\uff1a\u201c\u662f\u554a\uff0c\u4ed6\u662f\u504f\u5fc3\u5462\u3002\u53ef\u662f\u4ed6\u504f\u5fc3\u7ef4\u62a4\u4f60\u5f90\u5927\u7237\u54ea\u3002\u201d\u5f90\u94ee\u6ee1\u5fc3\u80e1\u6d82\uff0c\u6014\u6014\u5730\u671b\u7740\u5e08\u7236\u3002\u9a6c\u884c\u7a7a\u4f4e\u58f0\u9053\uff1a\u201c\u4f60\u6253\u7684\u662f\u4ec0\u4e48\u4eba\uff1f\u4ed6\u662f\u5fa1\u524d\u4f8d\u536b\u3002 \u3000\u3000\u54b1\u4eec\u5462\uff0c\u90a3\u662f\u51ed\u4eba\u5bb6\u8d4f\u53e3\u996d\u5403\u7684\u8d70\u9556\u7684\u3002\u5b98\u8001\u7237\u5f53\u771f\u8ddf\u4f60\u4e3a\u8d77\u96be\u6765\uff0c\u54b1\u4eec\u8fd8\u4e0d\u662f\u5403\u4e0d\u4e86\u515c\u7740\u8d70\u4e48\uff1f\u90a3\u5c11\u5e74\u62a4\u4f4f\u4e86\u4ed6\u9762\u5b50\uff0c\u53eb\u4f60\u8fd9\u695e\u5c0f\u5b50\u5c11\u4e86\u4e00\u6869\u540e\u60a3\u3002\u201d \u3000\u3000\u5f90\u94ee\u604d\u7136\u5927\u609f\uff0c\u8fde\u79f0\uff1a\u201c\u662f\uff0c\u662f\uff01\u201d\u5954\u5230\u540e\u9662\u7ec3\u6b66\u5385\u4e2d\uff0c\u53ea\u89c1\u5546\u5b9d\u9707\u62ac\u624b\u8e22\u817f\uff0c\u6b63\u5728\u7ec3\u4e00\u62db\u201c\u67e5\u62f3\u201d\u4e2d\u7684\u201c\u5f13\u6b65\u5288\u6253\u201d\uff0c\u6b63\u662f\u5f90\u94ee\u9002\u624d\u7528\u4ee5\u51fb\u4e2d\u4f55\u601d\u8c6a\u90a3\u4e00\u624b\u3002\u4ed6\u89c1\u5f90\u94ee\u8fdb\u6765\uff0c\u8138\u4e0a\u4e00\u7ea2\uff0c\u6025\u5fd9\u6536\u62f3\u3002 \u3000\u3000\u5f90\u94ee\u62b1\u62f3\u9053\uff1a\u201c\u5546\u516c\u5b50\uff0c\u6211\u5e08\u7236\u53eb\u6211\u8ddf\u4f60\u9053\u8c22\u6765\u5566\u3002\u6211\u8d77\u521d\u4e0d\u660e\u767d\u4f60\u662f\u597d\u610f\uff0c\u5fc3\u91cc\u8fd8\u602a\u4f60\u5462\u3002\u201d\u5546\u5b9d\u9707\u9053\uff1a\u201c\u5f90\u5927\u54e5\uff0c\u4f60\u6b66\u529f\u80dc\u8fc7\u90a3\u4e2a\u4f8d\u536b\u4f55\u6b62\u5341\u500d\uff1f\u5c0f\u5f1f\u4f69\u670d\u5f97\u7d27\u3002\u201d\u5f90\u94ee\u542c\u4ed6\u79f0\u8d5e\u81ea\u5df1\uff0c\u751a\u662f\u9ad8\u5174\uff0c\u5f53\u5373\u8ddf\u4ed6\u8c08\u4e86\u8d77\u6765\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u7ec3\u7684\u662f\u54ea\u4e00\u95e8\u529f\u592b\uff1f\u201d\u5546\u5b9d\u9707\u9053\uff1a\u201c\u5c0f\u5f1f\u521d\u5b66\uff0c\u4ec0\u4e48\u4e5f\u6ca1\u5b66\u4f1a\uff0c\u8c08\u4e0d\u4e0a\u662f\u54ea\u4e00\u95e8\u54ea\u4e00\u6d3e\u3002\u9002\u624d\u89c1\u5f90\u5927\u54e5\u7528\u8fd9\u4e00\u62db\u6253\u4ed6\uff0c\u662f\u4e0d\u662f\u8fd9\u6837\uff1f\u201d \u3000\u3000\u8bf4\u7740\u53f3\u8db3\u8e0f\u51fa\uff0c\u53f3\u62f3\u5288\u6253\uff0c\u5de6\u624b\u5fc3\u5411\u4e0a\u6258\u4f4f\u53f3\u81c2\u3002 \u3000\u3000\u5f90\u94ee\u521a\u624d\u4ee5\u6b64\u62db\u53d6\u80dc\uff0c\u89c1\u4ed6\u6bd4\u5212\u81ea\u5df1\u7684\u5f97\u610f\u4e4b\u4f5c\uff0c\u81ea\u7136\u5174\u9ad8\u91c7\u70c8\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e00\u62db\u6709\u4e24\u53e5\u53e3\u8bc0\uff0c\u53eb\u4f5c\u2018\u9646\u6d77\u8fce\u95e8\u4e09\u4e0d\u987e\uff0c\u5288\u62f3\u6311\u6253\u4e0d\u5bb9\u5bbd\u3002\u2019\u201d\u8fd9\u4e24\u53e5\u987a\u53e3\u8bf4\u51fa\uff0c\u5ffd\u7136\u60f3\u8d77\uff0c\u8fd9\u662f\u5e08\u95e8\u6240\u4f20\u5fc3\u6cd5\uff0c\u600e\u80fd\u80e1\u4e71\u8bf4\u4e0e\u5916\u4eba\u77e5\u6653\uff0c\u5fd9\u8f6c\u53e3\u9053\uff1a\u201c\u4f60\u6bd4\u5f97\u5f88\u5bf9\uff0c\u5c31\u662f\u8fd9\u62db\u3002\u201d \u3000\u3000\u5546\u5b9d\u9707\u9053\uff1a\u201c\u4ec0\u4e48\u53eb\u4f5c\u2018\u9646\u6d77\u8fce\u95e8\u4e09\u4e0d\u987e\u2019\u5462\uff1f\u201d\u5f90\u94ee\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u6211\u53ef\u4e5f\u5fd8\u4e86\u3002\u201d\u4ed6\u4e0d\u5584\u6492\u8c0e\uff0c\u8fd9\u4e00\u53e5\u8bdd\u51fa\u53e3\uff0c\u8138\u4e5f\u7ea2\u4e86\u3002\u5546\u5b9d\u9707\u77e5\u4ed6\u4e0d\u80af\u8bf4\uff0c\u4e5f\u5c31\u4e0d\u518d\u591a\u95ee\uff0c\u53ea\u662f\u7740\u610f\u7ed3\u7eb3\uff0c\u5c06\u4ed6\u6367\u5f97\u5168\u8eab\u8f7b\u98d8\u98d8\u7684\u5982\u5728\u4e91\u96fe\u3002 \u3000\u3000\u5f90\u94ee\u9053\uff1a\u201c\u5546\u8001\u5f1f\uff0c\u54b1\u4eec\u4e5f\u522b\u95f9\u865a\u6587\u3002\u4f60\u4f7f\u4e00\u5957\u62f3\u811a\u7ed9\u6211\u77a7\u77a7\uff0c\u82e5\u662f\u6709\u4ec0\u4e48\u4e0d\u5230\u7684\u5730\u65b9\uff0c\u6211\u8ddf\u4f60\u8bf4\u8bf4\uff0c\u4e5f\u4e0d\u6789\u4e86\u4eca\u65e5\u7ed3\u4ea4\u4e00\u573a\u3002\u201d\u5546\u5b9d\u9738\u5927\u559c\uff0c\u9053\uff1a\u201c\u90a3\u518d\u597d\u4e5f\u6ca1\u6709\u4e86\u3002\u201d\u5f53\u4e0b\u62c9\u5f00\u67b6\u5b50\uff0c\u5728\u573a\u4e2d\u6253\u8d77\u62f3\u6765\uff0c\u4f46\u89c1\u4ed6\u201c\u5934\u8d9f\u7ef3\u6302\u4e00\u6761\u97ad\uff0c\u4e8c\u8d9f\u5341\u5b57\u7ed5\u4e09\u5c16\u201d\uff0c\u4f7f\u7684\u662f\u5341\u4e8c\u8def\u6f6d\u817f\u3002 \u3000\u3000\u8fd9\u8def\u62f3\u811a\u4f7f\u5f97\u5012\u662f\u7eaf\u719f\uff0c\u4f46\u51fa\u62f3\u4e0d\u6b63\uff0c\u811a\u6b65\u6d6e\u865a\uff0c\u867d\u7136\u888d\u8896\u751f\u98ce\uff0c\u59ff\u5f0f\u534e\u4e3d\uff0c\u82e5\u662f\u4e0e\u4eba\u52a8\u624b\uff0c\u5374\u534a\u70b9\u7ba1\u4e0d\u5f97\u4e8b\u3002\u53ea\u628a\u5f90\u94ee\u770b\u5f97\u6697\u6697\u6447\u5934\uff0c\u7b49\u4ed6\u6253\u5b8c\u201c\u5341\u4e8c\u8d9f\u7280\u725b\u671b\u6708\u8f6c\u56de\u8fd8\u201d\uff0c\u5fcd\u4e0d\u4f4f\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u5144\u5f1f\uff0c\u83ab\u602a\u6211\u76f4\u8a00\uff0c\u6559\u4f60\u6b66\u827a\u7684\u5e08\u7236\u662f\u803d\u8bef\u4e86\u4f60\u5566\u3002\u201d\u6b63\u8981\u5f80\u4e0b\u89e3\u91ca\uff0c\u5ffd\u89c1\u9a6c\u6625\u82b1\u5728\u5385\u53e3\u4e00\u63a2\u5934\uff0c\u53eb\u9053\uff1a\u201c\u5e08\u54e5\uff0c\u7239\u53eb\u4f60\u3002\u201d \u3000\u3000\u5f90\u94ee\u5fd9\u5411\u5546\u5b9d\u9707\u544a\u8f9e\uff0c\u56de\u5230\u5385\u4e0a\u3002\u53ea\u89c1\u706b\u5806\u65c1\u53c8\u591a\u4e86\u4e24\u4e2a\u907f\u96e8\u4e4b\u4eba\u3002\u4e00\u4e2a\u662f\u6ca1\u4e86\u53f3\u81c2\u7684\u72ec\u81c2\u4eba\uff0c\u4e00\u6761\u6781\u957f\u7684\u5200\u75a4\u4ece\u53f3\u7709\u8d77\u659c\u8fc7\u9f3b\u5b50\uff0c\u4e00\u76f4\u5ef6\u4f38\u5230\u5de6\u8fb9\u5634\u89d2\uff0c\u5728\u706b\u5149\u7167\u8000\u4e0b\u663e\u5f97\u9762\u76ee\u6781\u662f\u53ef\u6016\uff1b\u53e6\u4e00\u4e2a\u662f\u4e2a\u5341\u4e09\u56db\u5c81\u7684\u7537\u5b69\uff0c\u9ec4\u9ec4\u7626\u7626\u3002\u4e24\u4eba\u8863\u886b\u90fd\u5f88\u8934\u891b\u3002 \u3000\u3000\u5f90\u94ee\u5411\u4e24\u4eba\u671b\u4e86\u4e00\u773c\uff0c\u4e5f\u4e0d\u5728\u610f\uff0c\u8d70\u5230\u9a6c\u884c\u7a7a\u9762\u524d\uff0c\u53eb\u4e86\u58f0\uff1a\u201c\u5e08\u7236\uff01\u201d\u9a6c\u884c\u7a7a\u8138\u4e00\u6c89\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u53bb\u4e86\u8fd9\u4e48\u4e45\uff0c\u53c8\u5728\u5356\u5f04\u6b66\u827a\u4e86\uff0c\u662f\u4e0d\u662f\uff1f\u201d\u5f90\u94ee\u9053\uff1a\u201c\u5f1f\u5b50\u4e0d\u6562\u3002\u8fd9\u91cc\u59d3\u5546\u7684\u4e3b\u4eba\u9556\u6cd5\u4e0d\u9519\uff0c\u54ea\u77e5\u62f3\u811a\u4e00\u70b9\u513f\u4e5f\u4e0d\u6210\u3002\u201d\u9a6c\u884c\u7a7a\u9053\uff1a\u201c\u50bb\u5c0f\u5b50\uff0c\u4f60\u7ed9\u4eba\u5bb6\u51a4\u5566\u3002\u51ed\u4f60\u8fd9\u70b9\u529f\u592b\uff0c\u4e24\u4e2a\u4e5f\u4e0d\u662f\u4eba\u5bb6\u7684\u5bf9\u624b\u3002\u201d\u5f90\u94ee\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u90a3\u6015\u4e0d\u89c1\u5f97\u3002\u4ed6\u5e08\u7236\u6559\u7684\u5341\u4e8c\u8def\u6f6d\u817f\uff0c\u5c3d\u662f\u597d\u770b\u4e0d\u7ba1\u7528\u3002\u201d\u9a6c\u884c\u7a7a\u9053\uff1a\u201c\u4f60\u77e5\u4ed6\u5e08\u7236\u662f\u8c01\uff1f\u201d \u3000\u3000\u5f90\u94ee\u5fc3\u4e2d\u6697\u5947\uff1a\u201c\u6211\u5e08\u7236\u6ca1\u8ddf\u90a3\u59d3\u5546\u7684\u89c1\u8fc7\u9762\uff0c\u53c8\u6ca1\u89c1\u4ed6\u7ec3\u8fc7\u62f3\u811a\uff0c\u600e\u4e48\u8fde\u4ed6\u5e08\u7236\u662f\u8c01\u4e5f\u77e5\u9053\u4e86\uff1f\u201d\u5f53\u4e0b\u7b54\u9053\uff1a\u201c\u5f1f\u5b50\u4e0d\u77e5\uff0c\u60f3\u6765\u662f\u4e2a\u4e0d\u4e2d\u7528\u7684\u6df7\u6df7\u3002\u201d\u9a6c\u884c\u7a7a\u51b7\u7b11\u4e00\u58f0\uff0c\u4f4e\u6c89\u7740\u58f0\u97f3\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u4e2d\u7528\u7684\u6df7\u6df7\uff01\u54fc\uff0c\u5341\u4e94\u5e74\u524d\uff0c\u4f60\u5e08\u7236\u7ed9\u4eba\u780d\u8fc7\u4e00\u5200\uff0c\u5288\u8fc7\u4e00\u638c\uff0c\u517b\u4e86\u4e09\u5e74\u4f24\u65b9\u5f97\u5eb7\u590d\u3002\u90a3\u4eba\u662f\u8c01\uff1f\u201d\u5f90\u94ee\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u516b\u5366\u5200\u5546\u5251\u9e23\u3002\u201d\u9a6c\u884c\u7a7a\u4f4e\u58f0\u9053\uff1a\u201c\u534a\u70b9\u513f\u4e5f\u4e0d\u9519\u3002\u90a3\u5546\u5251\u9e23\u662f\u5c71\u4e1c\u6b66\u5b9a\u53bf\u4eba\uff0c\u8fd9\u91cc\u53ef\u6b63\u662f\u6b66\u5b9a\u53bf\uff0c\u4e3b\u4eba\u5bb6\u59d3\u5546\u3002\u54b1\u4eec\u80e1\u4e71\u8fdb\u6765\u907f\u96e8\uff0c\u521d\u65f6\u5e76\u6ca1\u7559\u5fc3\uff0c\u4f60\u77a7\uff0c\u6b63\u6881\u4e0a\u7ed8\u7740\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5f90\u94ee\u62ac\u8d77\u5934\u6765\uff0c\u53ea\u89c1\u6b63\u6881\u4e0a\u91d1\u6f06\u6f06\u7740\u4e00\u4e2a\u516b\u5366\u56fe\u5f62\uff0c\u4e0d\u7531\u5f97\u5927\u5403\u4e00\u60ca\uff0c\u5fd9\u9053\uff1a\u201c\u5e08\u7236\uff0c\u5feb\u6284\u5bb6\u4f19\uff0c\u54b1\u4eec\u649e\u5230\u4ec7\u5bb6\u7a9d\u91cc\u6765\u5566\u3002\u201d\u9a6c\u884c\u7a7a\u6de1\u6de1\u5730\u9053\uff1a\u201c\u5012\u4e0d\u7528\u5fd9\u3002\u5546\u5251\u9e23\u65e9\u7ed9\u4eba\u6740\u5566\uff01\u201d \u3000\u3000\u5f90\u94ee\u66fe\u542c\u5e08\u7236\u8bf4\u8fc7\u5f53\u5e74\u5927\u8d25\u5728\u4e00\u4eba\u624b\u91cc\uff0c\u90a3\u5c31\u662f\u5c71\u4e1c\u5927\u8c6a\u516b\u5366\u5200\u5546\u5251\u9e23\uff0c\u53ea\u56e0\u8fd9\u662f\u5e08\u95e8\u7684\u5947\u803b\u5927\u8fb1\uff0c\u5e08\u7236\u540e\u6765\u4e0d\u63d0\uff0c\u4e5f\u5c31\u4ece\u6b64\u4e0d\u6562\u591a\u95ee\u4e00\u53e5\uff0c\u5374\u4e0d\u77e5\u5546\u5251\u9e23\u539f\u6765\u5df2\u6b7b\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u662f\u4f60\u8001\u4eba\u5bb6\u540e\u6765\u62a5\u4e86\u4ec7\uff1f\u201d\u9a6c\u884c\u7a7a\u54fc\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5546\u5251\u9e23\u7684\u6b66\u529f\uff0c\u6211\u518d\u7ec3\u4e00\u8f88\u5b50\u4e5f\u8d76\u4e0d\u4e0a\uff0c\u51ed\u6211\u8fd9\u70b9\u73a9\u827a\u513f\uff0c\u54ea\u6740\u5f97\u4e86\u4ed6\uff1f\u201d \u3000\u3000\u5f90\u94ee\u5927\u5947\uff0c\u95ee\u9053\uff1a\u201c\u90a3\u4e48\u662f\u8c01\u6740\u4e86\u4ed6\uff1f\u201d\u9a6c\u884c\u7a7a\u9053\uff1a\u201c\u90a3\u5c11\u5e74\u7528\u91d1\u9556\u6253\u6728\u724c\u4e0a\u7684\u4eba\u5f62\uff0c\u5546\u5251\u9e23\u5c31\u662f\u7ed9\u8fd9\u4e24\u4e2a\u4eba\u6740\u7684\u3002\u201d \u3000\u3000\u5f90\u94ee\u7741\u5927\u4e86\u773c\u775b\uff0c\u9053\uff1a\u201c\u80e1\u4e00\u5200\u548c\u82d7\u4eba\u51e4\uff1f\u201d \u3000\u3000\u9a6c\u884c\u7a7a\u70b9\u4e86\u70b9\u5934\uff0c\u8138\u4e0a\u795e\u8272\u9634\u90c1\uff0c\u4fbf\u5982\u5c4b\u5916\u7684\u5929\u7a7a\u90a3\u822c\u9ed1\u6c89\u6c89\u5730\u3002 \u3000\u3000\u5f90\u94ee\u5e73\u7d20\u5bf9\u5e08\u7236\u4f69\u670d\u5f97\u4e94\u4f53\u6295\u5730\uff0c\u4ee5\u4e3a\u5f53\u4e16\u4e4b\u95f4\uff0c\u8bf4\u5230\u6b66\u529f\uff0c\u6781\u5c11\u6709\u4eba\u80fd\u5f3a\u5f97\u8fc7\u767e\u80dc\u795e\u62f3\u9a6c\u8001\u9556\u5934\u4e86\uff0c\u5c82\u77e5\u8fd9\u65f6\u542c\u5230\u5e08\u7236\u8a00\u9053\uff0c\u975e\u4f46\u516b\u5366\u5200\u5546\u5251\u9e23\u6b66\u529f\u8fdc\u80dc\u4e8e\u4ed6\uff0c\u80e1\u4e00\u5200\u4e0e\u82d7\u4eba\u51e4\u7684\u529f\u592b\u53c8\u5728\u5546\u5251\u9e23\u4e4b\u4e0a\uff0c\u4e0d\u7531\u5f97\u5927\u4e3a\u60ca\u8be7\uff0c\u4f4e\u58f0\u95ee\u9053\uff1a\u201c\u90a3\u80e1\u4e00\u5200\u4e0e\u82d7\u4eba\u51e4\u662f\u4f55\u7b49\u6837\u7684\u4eba\u7269\uff1f\u201d\u9a6c\u884c\u7a7a\u9053\uff1a\u201c\u80e1\u4e00\u5200\u7684\u6b66\u529f\u5f3a\u6211\u5341\u500d\uff0c\u53ea\u53ef\u60dc\u5728\u5341\u591a\u5e74\u524d\u6b7b\u4e86\u3002\u201d\u5f90\u94ee\u8212\u4e86\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u60f3\u662f\u75c5\u6b7b\u7684\u4e86\uff1f\u201d\u9a6c\u884c\u7a7a\u9053\uff1a\u201c\u7ed9\u4eba\u6740\u6b7b\u7684\u3002\u201d\u5f90\u94ee\u7741\u5927\u4e86\u773c\u775b\uff0c\u9053\uff1a\u201c\u80e1\u4e00\u5200\u8fd9\u4e48\u5389\u5bb3\uff0c\u6709\u8c01\u6740\u5f97\u4e86\u4ed6\uff1f\u201d\u9a6c\u884c\u7a7a\u9053\uff1a\u201c\u6253\u904d\u5929\u4e0b\u65e0\u654c\u624b\u91d1\u9762\u4f5b\u82d7\u4eba\u51e4\u3002\u201d \u3000\u3000\u8fd9\u201c\u6253\u904d\u5929\u4e0b\u65e0\u654c\u624b\u91d1\u9762\u4f5b\u82d7\u4eba\u51e4\u201d\u5341\u4e09\u4e2a\u5b57\u4e00\u53e3\u6c14\u8bf4\u5c06\u51fa\u6765\uff0c\u58f0\u97f3\u867d\u4f4e\uff0c\u5374\u662f\u5927\u5177\u5a01\u4e25\u3002\u5f90\u94ee\u80f8\u53e3\u4e00\u6c89\uff0c\u6b63\u5f85\u8bf4\u8bdd\uff0c\u731b\u542c\u5f97\u95e8\u5916\u9690\u9690\u9a6c\u8e44\u58f0\u54cd\uff0c\u5927\u96e8\u4e2d\u5341\u4f59\u5339\u9a6c\u6025\u5954\u800c\u6765\u3002 \u3000\u3000\u90a3\u9762\u76ee\u82f1\u4fca\u7684\u9752\u5e74\u4e0e\u90a3\u7f8e\u8c8c\u5c11\u5987\u542c\u5230\u9a6c\u8e44\u58f0\u97f3\uff0c\u4e92\u671b\u4e00\u773c\uff0c\u4f3c\u5728\u5f3a\u81ea\u9547\u5b9a\uff0c\u4f46\u8138\u4e0a\u7ec8\u7a76\u9732\u51fa\u4e86\u60ca\u60f6\u4e4b\u8272\u3002\u90a3\u9752\u5e74\u62c9\u7740\u5c11\u5987\u7684\u624b\uff0c\u632a\u52a8\u5ea7\u4f4d\uff0c\u4f3c\u662f\u6015\u706b\u5806\u7099\u70ed\uff0c\u79fb\u8fdc\u4e86\u4e9b\u3002 \u3000\u3000\u5341\u591a\u5339\u9a6c\u5954\u5230\u5e84\u524d\uff0c\u621b\u7136\u800c\u6b62\u3002\u4f46\u542c\u5f97\u6570\u58f0\u553f\u54e8\uff0c\u4e03\u516b\u5339\u9a6c\u7ed5\u5230\u4e86\u5e84\u540e\u3002 \u3000\u3000\u9a6c\u884c\u7a7a\u4e00\u542c\u54e8\u58f0\uff0c\u8138\u4e0a\u53d8\u8272\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5b9a\u7740\u70b9\u513f\u3002\u201d\u5f90\u94ee\u6781\u662f\u5174\u594b\uff0c\u58f0\u97f3\u53d1\u98a4\uff0c\u95ee\u9053\uff1a\u201c\u90a3\u8bdd\u513f\u6765\u4e86\uff1f\u201d\u9a6c\u884c\u7a7a\u4e0d\u518d\u56de\u7b54\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u5927\u4f19\u513f\u6284\u5bb6\u4f19\uff0c\u62a4\u9556\uff01\u201d\u8fd9\u53e5\u8bdd\u4e00\u559d\uff0c\u9556\u884c\u4eba\u4f17\u767b\u65f6\u5927\u4e71\uff0c\u77e5\u9053\u6709\u52ab\u9556\u7684\u9ed1\u9053\u5f3a\u4eba\u5230\u6765\uff0c\u5f53\u5373\u8dc3\u8d77\u3002\u621a\u6768\u4e24\u540d\u9556\u5934\u548c\u4e94\u540d\u8d9f\u5b50\u624b\u6307\u6325\u8f66\u592b\uff0c\u5c06\u5341\u4f59\u8f86\u9556\u8f66\u56f4\u6210\u4e00\u5806\u3002 \u3000\u3000\u9a6c\u6625\u82b1\u53cd\u800c\u8138\u6709\u559c\u8272\uff0c\u62d4\u51fa\u67f3\u53f6\u5200\uff0c\u9053\uff1a\u201c\u7239\uff0c\u662f\u54ea\u4e00\u8def\u7684\uff1f\u201d \u3000\u3000\u9a6c\u884c\u7a7a\u76b1\u7709\u9053\uff1a\u201c\u8fd8\u4e0d\u77e5\u9053\u3002\u201d\u63a5\u7740\u81ea\u8a00\u81ea\u8bed\uff1a\u201c\u8fd9\u4e00\u8def\u670b\u53cb\u597d\u602a\uff0c\u9053\u4e0a\u4e5f\u4e0d\u8e29\u76d8\u5b50\uff0c\u5c31\u8fd9\u4e48\u8bf4\u5230\u4fbf\u5230\u3002\u201d \u3000\u3000\u4e00\u8a00\u65b9\u7f62\uff0c\u53ea\u542c\u5f97\u56f4\u5899\u4e0a\u6258\u6258\u6258\u63a5\u8fde\u58f0\u54cd\uff0c\u516b\u540d\u5927\u6c49\u4e00\u8272\u9ed1\u8863\u6253\u626e\uff0c\u624b\u6267\u5175\u5203\uff0c\u4e00\u5b57\u6392\u5f00\u5730\u7ad9\u5728\u5899\u5934\u3002\u9a6c\u6625\u82b1\u626c\u8d77\u53f3\u81c2\uff0c\u5c31\u60f3\u4e00\u679d\u8896\u7bad\u5c04\u51fa\u3002\u9a6c\u884c\u7a7a\u8138\u8272\u51dd\u91cd\uff0c\u4f4e\u58f0\u559d\u9053\uff1a\u201c\u522b\u80e1\u6765\uff01\u77a7\u6211\u773c\u8272\u884c\u4e8b\u3002\u201d\u516b\u540d\u9ed1\u8863\u5927\u6c49\u671b\u7740\u5385\u4e0a\u4f17\u4eba\uff0c\u4e00\u8a00\u4e0d\u53d1\u3002 \u3000\u3000\u7830\u7684\u4e00\u58f0\uff0c\u5927\u95e8\u63a8\u5f00\uff0c\u8fdb\u6765\u4e00\u4e2a\u6c49\u5b50\uff0c\u8eab\u7a7f\u5b9d\u84dd\u8272\u7f0e\u888d\uff0c\u8863\u670d\u751a\u662f\u534e\u4e3d\uff0c\u4f46\u9762\u8c8c\u59d4\u7410\uff0c\u7f29\u5934\u7f29\u8111\uff0c\u4e0e\u4e00\u8eab\u8863\u670d\u6781\u4e0d\u76f8\u79f0\u3002\u8fd9\u4eba\u62ac\u5934\u671b\u4e86\u671b\u5929\uff0c\u4f46\u89c1\u5927\u96e8\u503e\u76c6\u800c\u4e0b\uff0c\u563f\u7684\u4e00\u58f0\u7b11\uff0c\u8db3\u5c16\u4e00\u70b9\uff0c\u500f\u5730\u7a7f\u8fc7\u4e86\u9662\u5b50\uff0c\u7ad9\u5728\u5385\u53e3\u3002\u8fd9\u4e00\u4e0b\u98de\u8dc3\u8eab\u5f62\u5feb\u6781\uff0c\u5927\u96e8\u867d\u5bc6\uff0c\u5374\u53ea\u5728\u4ed6\u80a9\u5934\u6253\u6e7f\u4e86\u6570\u70b9\u3002\u5f90\u94ee\u4e0e\u9a6c\u6625\u82b1\u5bf9\u6b64\u4eba\u672c\u6765\u4e0d\u4ee5\u4e3a\u610f\uff0c\u7a81\u7136\u89c1\u4ed6\u9732\u4e86\u8fd9\u624b\u8f7b\u529f\uff0c\u8fd9\u624d\u751f\u5fcc\u60ee\u4e4b\u5fc3\uff0c\u5411\u9a6c\u884c\u7a7a\u671b\u4e86\u4e00\u773c\u3002 \u3000\u3000\u9a6c\u884c\u7a7a\u53f3\u624b\u63e1\u7740\u70df\u888b\uff0c\u62f1\u624b\u8bf4\u9053\uff1a\u201c\u8bf7\u6055\u8001\u6c49\u773c\u62d9\uff0c\u6ca1\u66fe\u62dc\u4f1a\u3002\u670b\u53cb\u5c0a\u59d3\u5927\u540d\uff0c\u5b9d\u5be8\u6b47\u9a6c\u4f55\u5904\uff1f\u201d \u3000\u3000\u5546\u5bb6\u5821\u5c11\u4e3b\u4eba\u5546\u5b9d\u9707\u542c\u5230\u9a6c\u8e44\u58f0\u54cd\uff0c\u5f53\u5373\u6697\u85cf\u91d1\u9556\uff0c\u8170\u60ac\u5229\u5200\uff0c\u6765\u5230\u5385\u524d\u3002\u53ea\u89c1\u90a3\u76d7\u9b41\u624b\u6234\u78a7\u7389\u6212\u6307\uff0c\u957f\u888d\u4e0a\u95ea\u8000\u7740\u51e0\u7c92\u9ec4\u91d1\u6263\u5b50\uff0c\u5de6\u624b\u62ff\u7740\u4e00\u4e2a\u7fe1\u7fe0\u9f3b\u70df\u58f6\uff0c\u4e0d\u5e26\u5175\u5668\uff0c\u795e\u60c5\u6253\u626e\uff0c\u5c31\u5982\u662f\u4e2a\u66b4\u53d1\u6237\u5bcc\u5546\u3002\u53ea\u542c\u4ed6\u8bf4\u9053\uff1a\u201c\u5728\u4e0b\u59d3\u960e\u540d\u57fa\uff0c\u8001\u82f1\u96c4\u81ea\u662f\u767e\u80dc\u795e\u62f3\u9a6c\u884c\u7a7a\u4e86\uff1f\u201d \u3000\u3000\u9a6c\u884c\u7a7a\u62b1\u62f3\u9053\uff1a\u201c\u4e0d\u6562\uff0c\u8fd9\u5916\u53f7\u662f\u6c5f\u6e56\u670b\u53cb\u7ed9\u5728\u4e0b\u8138\u4e0a\u8d34\u91d1\u3002\u6d6a\u5f97\u865a\u540d\uff0c\u4e0d\u8db3\u6302\u9f7f\u3002\u201d\u5fc3\u4e2d\u6697\u5fd6\uff1a\u201c\u960e\u57fa\uff1f\u90a3\u662f\u4ec0\u4e48\u4eba\uff1f\u6ca1\u542c\u8fc7\u6c5f\u6e56\u4e0a\u6709\u8fd9\u53f7\u4eba\u7269\u3002\u201d \u3000\u3000\u960e\u57fa\u54c8\u54c8\u4e00\u7b11\uff0c\u6307\u7740\u7ad9\u5728\u5899\u5934\u7684\u4e00\u5217\u9ed1\u8863\u6c49\u5b50\uff0c\u8bf4\u9053\uff1a\u201c\u5f1f\u5144\u4eec\u997f\u4e86\u51e0\u5929\u809a\u5b50\uff0c\u60f3\u8bf7\u9a6c\u8001\u82f1\u96c4\u8d4f\u53e3\u996d\u5403\u3002\u201d\u9a6c\u884c\u7a7a\u9053\uff1a\u201c\u960e\u5be8\u4e3b\u8a00\u91cd\u4e86\u3002\u94ee\u513f\uff0c\u53d6\u4e94\u5341\u4e24\u94f6\u5b50\uff0c\u8bf7\u960e\u5be8\u4e3b\u8d4f\u8d50\u5f1f\u5144\u3002\u201d \u3000\u3000\u4ed6\u8fd9\u662f\u6309\u7740\u6c5f\u6e56\u89c4\u77e9\u884c\u4e8b\uff0c\u4f46\u77a7\u5bf9\u65b9\u7684\u795e\u60c5\u58f0\u52bf\uff0c\u51b3\u975e\u4e94\u5341\u4e24\u94f6\u5b50\u6240\u80fd\u6253\u53d1\u3002 \u3000\u3000\u679c\u7136\u960e\u57fa\u4ef0\u5929\u54c8\u54c8\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u9a6c\u8001\u82f1\u96c4\u4fdd\u9556\uff0c\u4e00\u4fdd\u5c31\u662f\u4e09\u5341\u4e07\u4e24\u3002\u59d3\u960e\u7684\u773c\u754c\u867d\u5c0f\uff0c\u533a\u533a\u4e94\u5341\u4e24\uff0c\u5012\u8fd8\u4e0d\u5728\u773c\u5185\u3002\u201d \u3000\u3000\u9a6c\u884c\u7a7a\u5fc3\u4e2d\u5600\u5495\uff1a\u201c\u6b64\u4eba\u4fe1\u606f\u5012\u7075\uff0c\u600e\u4e48\u6253\u542c\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u77e5\u9053\u6211\u4fdd\u4e86\u4e09\u5341\u4e07\u4e24\u9556\u94f6\uff1f\u201d\u7709\u5934\u4e00\u76b1\uff0c\u4ecd\u6309\u6c5f\u6e56\u89c4\u77e9\u8bf4\u9053\uff1a\u201c\u60f3\u9a6c\u67d0\u6709\u4ec0\u4e48\u672c\u4e8b\uff0c\u5168\u51ed\u9053\u4e0a\u670b\u53cb\u7ed9\u8138\u7f62\u4e86\u3002\u960e\u5be8\u4e3b\u4eca\u65e5\u867d\u662f\u521d\u89c1\uff0c\u54b1\u4eec\u4e1c\u8fb9\u4e0d\u4f1a\u897f\u8fb9\u4f1a\uff0c\u9a6c\u67d0\u6709\u5e78\uff0c\u4eca\u65e5\u53c8\u4ea4\u4e00\u4f4d\u670b\u53cb\u3002\u4e0d\u77e5\u960e\u5be8\u4e3b\u6709\u4ec0\u4e48\u5429\u5490\uff1f\u201d \u3000\u3000\u960e\u57fa\u9053\uff1a\u201c\u5429\u5490\u662f\u4e0d\u6562\u5f53\u7684\uff0c\u53ea\u662f\u5728\u4e0b\u751f\u6765\u89c1\u8d22\u5f00\u773c\uff0c\u4e09\u5341\u4e07\u9556\u94f6\u6253\u4ece\u9f3b\u5b50\u4e0b\u8fc7\uff0c\u4e0d\u53d6\u6709\u4f24\u9634\u5fb7\u3002\u4f46\u9a6c\u8001\u9556\u5934\u65e2\u7136\u5f00\u53e3\u670b\u53cb\uff0c\u95ed\u53e3\u670b\u53cb\uff0c\u8fd9\u6837\u5427\uff0c\u5728\u4e0b\u53ea\u53d6\u4e00\u534a\uff0c\u4e8c\u4e00\u6dfb\u4f5c\u4e94\uff0c\u5c31\u501f\u5341\u4e94\u4e07\u4e24\u94f6\u5b50\u82b1\u5dee\u82b1\u5dee\u597d\u4e86\u3002\u201d\u4e5f\u4e0d\u5f85\u9a6c\u884c\u7a7a\u7b54\u8bdd\uff0c\u5de6\u624b\u4e00\u6325\uff0c\u5899\u5934\u516b\u540d\u5927\u6c49\u4e00\u4e00\u8dc3\u4e0b\uff0c\u5954\u5230\u5385\u53e3\u3002\u6709\u4eba\u95ee\u9053\uff1a\u201c\u4e00\u9f50\u53d6\u4e86\uff1f\u201d\u960e\u57fa\u9053\uff1a\u201c\u4e0d\uff0c\u62ff\u4e00\u534a\uff0c\u7559\u4e00\u534a\uff01\u6709\u5c4e\u5927\u5bb6\u62c9\uff0c\u6709\u996d\u5927\u5bb6\u5403\uff01\u201d\u4f17\u5927\u6c49\u8f70\u7136\u7b54\u5e94\uff0c\u5c31\u5f80\u9556\u8f66\u8d70\u53bb\u3002 \u3000\u3000\u9a6c\u884c\u7a7a\u52c3\u7136\u5927\u6012\uff0c\u89c1\u90a3\u4e9b\u5927\u6c49\u4ece\u5899\u5934\u8dc3\u4e0b\u65f6\u8eab\u624b\u5446\u6ede\uff0c\u5e76\u65e0\u4e00\u4e2a\u9ad8\u624b\u5728\u5185\uff0c\u5df2\u65e0\u62c5\u5fe7\u4e4b\u5fc3\uff0c\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u960e\u5be8\u4e3b\u662f\u4e0d\u80af\u7559\u4e00\u70b9\u4f59\u5730\u4e86\uff1f\u201d\u960e\u57fa\u6115\u7136\u9053\uff1a\u201c\u600e\u4e48\u4e0d\u7559\u4f59\u5730\uff1f\u6211\u4e0d\u662f\u8bf4\u53d6\u4e00\u534a\uff0c\u7559\u4e00\u534a\uff1f\u54e5\u513f\u4fe9\u6709\u5546\u6709\u91cf\uff0c\u516c\u5e73\u4ea4\u6613\u3002\u201d \u3000\u3000\u5f90\u94ee\u518d\u4e5f\u5fcd\u8010\u4e0d\u4f4f\uff0c\u62a2\u4e0a\u4e24\u6b65\uff0c\u4f38\u624b\u6307\u7740\u960e\u57fa\uff0c\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u4e8f\u4f60\u5728\u9ed1\u9053\u4e0a\u884c\u8d70\uff0c\u6ca1\u542c\u8fc7\u98de\u9a6c\u9556\u5c40\u7684\u5a01\u540d\u4e48\uff1f\u201d \u3000\u3000\u960e\u57fa\u9053\uff1a\u201c\u6211\u7684\u5c0f\u517b\u5ab3\u5987\u513f\u542c\u89c1\u8fc7\uff0c\u4ed6\u5988\u7684\uff0c\u8001\u5b50\u53ef\u662f\u7b2c\u4e00\u6b21\u542c\u89c1\u3002\u201d\u8eab\u5f62\u4e00\u89c1\uff0c\u5ffd\u5730\u6b3a\u5230\u5385\u53f3\uff0c\u62d4\u4e0b\u63d2\u5728\u8f66\u67b6\u4e0a\u7684\u98de\u9a6c\u9556\u65d7\uff0c\u5c06\u65d7\u6746\u4e00\u6298\u4e24\u6bb5\uff0c\u63b7\u5728\u5730\u4e0b\uff0c\u968f\u5373\u4f38\u811a\u5728\u65d7\u4e0a\u4e00\u8e0f\u3002 \u3000\u3000\u8fd9\u4ef6\u4e8b\u5f53\u771f\u662f\u72af\u4e86\u6c5f\u6e56\u5927\u5fcc\uff0c\u52ab\u9556\u7684\u4e8b\u60c5\u5e38\u6709\uff0c\u5374\u6781\u5c11\u6709\u5982\u6b64\u505a\u5f97\u7edd\u7684\uff0c\u5982\u975e\u53cc\u65b9\u6709\u89e3\u4e0d\u5f00\u7684\u6b7b\u4ec7\uff0c\u90a3\u662f\u51b3\u5fc3\u4ee5\u6027\u547d\u76f8\u62da\u4e86\u3002\u9556\u884c\u4eba\u4f17\u4e00\u89c1\u4e4b\u4e0b\uff0c\u767b\u65f6\u5927\u54d7\u3002 \u3000\u3000\u5f90\u94ee\u66f4\u4e0d\u6253\u8bdd\uff0c\u51b2\u4e0a\u53bb\u4e00\u62db\u201c\u8e0f\u6b65\u51fb\u638c\u201d\uff0c\u5de6\u638c\u5411\u4ed6\u80f8\u53e3\u731b\u51fb\u8fc7\u53bb\u3002\u960e\u57fa\u4fa7\u8eab\u95ea\u907f\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u5b50\uff0c\u8bb2\u6253\u4e48\uff1f\u201d\u5de6\u638c\u4e00\u6c89\uff0c\u6025\u6293\u4ed6\u7684\u624b\u8155\u3002\u5f90\u94ee\u53d8\u201c\u540e\u63d2\u6b65\u6446\u638c\u201d\uff0c\u5de6\u624b\u5411\u540e\u52fe\u6302\uff0c\u53f3\u638c\u4e00\u6325\uff0c\u5411\u4e0a\u6446\u4e3e\uff0c\u5f84\u51fb\u654c\u4eba\u4e0b\u989a\u3002\u960e\u57fa\u5934\u4e00\u504f\uff0c\u53f3\u62f3\u76f4\u51fb\u4e0b\u6765\u3002\u8fd9\u4e00\u62f3\u6765\u8def\u6781\u602a\uff0c\u5f90\u94ee\u6025\u5fd9\u6446\u5934\u8ba9\u5f00\uff0c\u7830\u7684\u4e00\u58f0\uff0c\u80a9\u5934\u5df2\u4e2d\u4e86\u4e00\u62f3\uff0c\u4f46\u89c9\u62f3\u529b\u6c89\u91cd\uff0c\u53ea\u9707\u5f97\u80f8\u80cc\u9690\u9690\u4f5c\u75db\u3002\u5f90\u94ee\u811a\u6b65\u6447\u6643\uff0c\u9669\u4e9b\u6454\u5012\uff0c\u5e78\u4ed6\u8eab\u5f3a\u529b\u58ee\uff0c\u4e0b\u76d8\u9a6c\u6b65\u624e\u5f97\u6781\u7a33\uff0c\u5fd9\u53d8\u201c\u4ec6\u817f\u7a7f\u638c\u201d\uff0c\u8eab\u5b50\u4e00\u77ee\uff0c\u53f3\u817f\u5c48\u819d\u8e72\u4e0b\uff0c\u5de6\u638c\u7a7f\u51fa\uff0c\u90a3\u662f\u5378\u529b\u53cd\u653b\uff0c\u201c\u67e5\u62f3\u201d\u7684\u9ad8\u660e\u62db\u6570\u3002 \u3000\u3000\u960e\u57fa\u5e76\u4e0d\u7406\u4f1a\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u5de6\u817f\u53cd\u94a9\uff0c\u5411\u540e\u5012\u8e22\u3002\u8fd9\u4e00\u817f\u6765\u5f97\u66f4\u662f\u53e4\u602a\u3002\u5f90\u94ee\u5927\u9a87\uff0c\u6025\u5fd9\u7a9c\u4e0a\u8dc3\u907f\u3002\u960e\u57fa\u53f3\u62f3\u76f4\u51fb\uff0c\u559d\u9053\uff1a\u201c\u606d\u559c\u53d1\u8d22\uff01\u201d\u7830\u7684\u4e00\u54cd\uff0c\u6b63\u4e2d\u5f90\u94ee\u80f8\u53e3\u3002\u8fd9\u4e00\u62f3\u597d\u751f\u5389\u5bb3\uff0c\u5f90\u94ee\u4ef0\u5929\u4e00\u4ea4\u8dcc\u5012\uff0c\u5728\u5730\u4e0a\u8fde\u6253\u4e86\u51e0\u4e2a\u6eda\uff0c\u54c7\u7684\u4e00\u58f0\u5410\u51fa\u4e00\u53e3\u9c9c\u8840\uff0c\u6781\u786c\u6717\u7684\u4e00\u4e2a\u5c0f\u4f19\u5b50\uff0c\u7adf\u7ed9\u8fd9\u4e00\u62f3\u6253\u5f97\u7ad9\u4e0d\u8d77\u8eab\u3002\u7fa4\u76d7\u8f70\u7136\u559d\u5f69\uff0c\u53eb\u9053\uff1a\u201c\u8fd9\u4e00\u62f3\u591f\u8fd9\u5c0f\u5b50\u6328\u7684\u3002\u201d \u3000\u3000\u9556\u884c\u4e2d\u4eba\u89c1\u960e\u57fa\u51fa\u624b\u5982\u6b64\u72e0\u8fa3\uff0c\u5747\u662f\u53c8\u60ca\u53c8\u6012\u3002\u9a6c\u6625\u82b1\u4f38\u624b\u53bb\u6276\u5e08\u54e5\uff0c\u6025\u5f97\u8981\u54ed\uff0c\u8fde\u95ee\uff1a\u201c\u600e\u4e48\u5566\uff1f\u201d\u9a6c\u884c\u7a7a\u4e00\u751f\u8d70\u6c5f\u6e56\uff0c\u4e0d\u77e5\u89c1\u8fc7\u591a\u5c11\u5927\u98ce\u5927\u6d6a\uff0c\u4f46\u8fd9\u76d7\u9b41\u4f7f\u7684\u662f\u4ec0\u4e48\u62f3\u811a\uff0c\u5374\u534a\u70b9\u4e5f\u8ba4\u4e0d\u51fa\u6765\u3002\u4e09\u4e2a\u4f8d\u536b\u4e5f\u5728\u4f4e\u58f0\u8bae\u8bba\uff1a\u201c\u70b9\u5b50\u662f\u54ea\u4e00\u6d3e\u7684\uff1f\u201d\u201c\u77a7\u4e0d\u51fa\u6765\uff0c\u6709\u70b9\u50cf\u4e94\u884c\u62f3\u3002\u201d\u201c\u4e0d\uff0c\u4e94\u884c\u62f3\u6ca1\u90a3\u6837\u90aa\u95e8\u3002\u201d \u3000\u3000\u9a6c\u884c\u7a7a\u8d70\u4e0a\u4e24\u6b65\uff0c\u62b1\u62f3\u9053\uff1a\u201c\u960e\u5be8\u4e3b\u679c\u7136\u597d\u6b66\u827a\uff0c\u591a\u8c22\u6559\u8bad\u4e86\u5c0f\u5f92\uff0c\u4e5f\u597d\u8ba9\u4ed6\u77e5\u9053\u6c5f\u6e56\u4e0a\u5c3d\u591a\u80fd\u4eba\u3002\u201d\u960e\u57fa\u7b11\u9053\uff1a\u201c\u6211\u8fd9\u51e0\u4e0b\u4e09\u811a\u732b\u7b97\u4ec0\u4e48\u73a9\u610f\u513f\uff0c\u7ed9\u4f60\u9a6c\u82f1\u96c4\u63d0\u978b\u76ae\u3001\u5012\u4fbf\u58f6\u4e5f\u8fd8\u6328\u4e0d\u4e0a\u8fb9\u513f\u3002\u5149\u68cd\u522b\u7684\u4e0d\u4f1a\uff0c\u5c31\u4f1a\u8fd9\u4e2a\u3002\u8fd9\u5c31\u8bf7\u6559\u4f60\u9a6c\u8001\u82f1\u96c4\u7684\u767e\u80dc\u795e\u62f3\u3002\u201d\u9a6c\u884c\u7a7a\u89c1\u4ed6\u6ee1\u8138\u6cb9\u5149\uff0c\u8bf4\u8bdd\u8d2b\u5634\u6ed1\u820c\uff0c\u4e0d\u6298\u4e0d\u6263\u662f\u4e2a\u6cfc\u76ae\u65e0\u8d56\uff0c\u600e\u5730\u53c8\u7ec3\u5c31\u4e86\u8fd9\u6837\u4e00\u8eab\u602a\u5f02\u6b66\u529f\uff0c\u5b9e\u662f\u5947\u602a\uff0c\u5fc3\u4e2d\u6253\u5b9a\u4e86\u4e3b\u610f\uff0c\u6682\u4e14\u53ea\u5b88\u4e0d\u653b\uff0c\u5f85\u8ba4\u6e05\u4ed6\u7684\u62f3\u8def\u518d\u8bf4\uff0c\u5f53\u4e0b\u51dd\u795e\u659c\u7acb\uff0c\u53cc\u624b\u865a\u63e1\u3002 \u3000\u3000\u4e09\u540d\u4f8d\u536b\u3001\u5546\u5b9d\u9707\u3001\u9556\u884c\u4f17\u4eba\u4e00\u9f50\u51dd\u795e\u89c2\u6597\uff0c\u90fd\u77e5\u8fd9\u4e00\u573a\u4e89\u6597\u4e0d\u4f46\u5173\u7cfb\u7740\u4e09\u5341\u4e07\u9556\u94f6\u7684\u5b89\u5371\uff0c\u4e5f\u662f\u9a6c\u884c\u7a7a\u8eab\u5bb6\u6027\u547d\u3001\u4e00\u751f\u5a01\u671b\u4e4b\u6240\u7cfb\u3002\u5927\u5385\u4e2d\u4eba\u4eba\u8083\u9759\uff0c\u53ea\u542c\u5f97\u706b\u5806\u4e2d\u67f4\u70ad\u7206\u88c2\uff0c\u53d1\u51fa\u8f7b\u8f7b\u7684\u5fc5\u535c\u4e4b\u58f0\u3002\u9662\u5b50\u4e2d\u5927\u96e8\u5982\u6ce8\uff0c\u7adf\u65e0\u534a\u5206\u505c\u606f\u4e4b\u610f\u3002 \u3000\u3000\u90a3\u534e\u670d\u76f8\u516c\u81ea\u548c\u5c11\u5987\u5e76\u80a9\u4f4e\u58f0\u8bf4\u8bdd\uff0c\u5bf9\u9a6c\u960e\u7684\u4e89\u6597\u6beb\u6ca1\u7559\u5fc3\u3002 \u3000\u3000\u960e\u57fa\u4ece\u6000\u4e2d\u53d6\u51fa\u4e00\u4e2a\u91d1\u5149\u707f\u70c2\u7684\u9ec4\u91d1\u9f3b\u70df\u58f6\uff0c\u5438\u4e86\u4e00\u53e3\u9f3b\u70df\uff0c\u4ed6\u4e5f\u77e5\u9a6c\u884c\u7a7a\u662f\u4e2a\u52b2\u654c\uff0c\u5c06\u8fab\u5b50\u5728\u5934\u9876\u76d8\u4e86\u4e2a\u5708\uff0c\u53eb\u9053\uff1a\u201c\u5149\u68cd\u7956\u4e0a\u4e0d\u79ef\u5fb7\uff0c\u5403\u996d\u5c31\u5f97\u9760\u62da\u547d\uff01\u4ed6\u5976\u5976\u7684\u8fd9\u5c31\u62da\u554a\uff01\u201d \u3000\u3000\u5ffd\u5730\u7331\u8eab\u76f4\u4e0a\uff0c\u5de6\u62f3\u731b\u51fa\uff0c\u5411\u9a6c\u884c\u7a7a\u51fb\u53bb\u3002\u9a6c\u884c\u7a7a\u5f85\u4ed6\u62f3\u5934\u79bb\u80f8\u534a\u5c3a\uff0c\u4e00\u4e2a\u201c\u767d\u9e64\u4eae\u7fc5\u201d\uff0c\u8eab\u5b50\u5df2\u5411\u5de6\u8f6c\u6210\u5f13\u7bad\u6b65\uff0c\u4e24\u81c2\u5411\u540e\u6210\u94a9\u624b\uff0c\u547c\u7684\u4e00\u58f0\u8f7b\u54cd\uff0c\u5012\u6325\u51fa\u6765\uff0c\u5e73\u4e3e\u53cd\u51fb\uff0c\u4f7f\u7684\u4ecd\u662f\u5c11\u6797\u6d3e\u4e2d\u6781\u4e3a\u5bfb\u5e38\u7684\u201c\u67e5\u62f3\u201d\uff0c\u4f46\u67b6\u5f0f\u51dd\u7a33\uff0c\u51fa\u624b\u62ac\u817f\u4e4b\u9645\uff0c\u751a\u662f\u8001\u7ec3\u72e0\u8fa3\u3002 \u3000\u3000\u90a3\u76f8\u516c\u5bf9\u9556\u5ba2\u4e0e\u5f3a\u4eba\u7684\u4e89\u6597\u672c\u6765\u5e76\u4e0d\u5728\u610f\uff0c\u5076\u7136\u659c\u773c\u4e00\u77a5\u4e4b\u4e0b\uff0c\u6b63\u89c1\u5230\u960e\u57fa\u4e00\u8db3\u53cd\u8e22\uff0c\u62db\u5f0f\u9887\u4e3a\u5947\u7279\uff0c\u4e0d\u7531\u5f97\u7559\u795e\u89c2\u770b\u3002\u90a3\u7f8e\u5987\u53eb\u9053\uff1a\u201c\u5f52\u519c\uff0c\u5f52\u519c\u3002\u201d\u90a3\u76f8\u516c\u968f\u53e3\u6f2b\u5e94\uff0c\u76ee\u5149\u5374\u8d2f\u6ce8\u5728\u4e8c\u4eba\u7684\u62da\u6597\u4e4b\u4e0a\u3002\u90a3\u7f8e\u5987\u4f38\u624b\u6447\u4e86\u6447\u4ed6\u80a9\u8180\uff0c\u8bf4\u9053\uff1a\u201c\u4e00\u4e2a\u7cdf\u8001\u513f\uff0c\u4e00\u4e2a\u6cfc\u76ae\u6df7\u6df7\u6253\u67b6\uff0c\u5f53\u771f\u5c31\u8fd9\u4e48\u597d\u770b\u3002\u201d\u90a3\u76f8\u516c\u542c\u5979\u8bdd\u4e2d\u5927\u6709\u4e0d\u60a6\u4e4b\u610f\uff0c\u5fd9\u8f6c\u5934\u7b11\u9053\uff1a\u201c\u8fd9\u6cfc\u76ae\u7684\u62f3\u811a\u5f88\u662f\u53e4\u602a\u3002\u201d\u90a3\u7f8e\u5987\u53f9\u9053\uff1a\u201c\u5509\uff0c\u4f60\u4eec\u7537\u4eba\uff0c\u5929\u4e0b\u6700\u8981\u7d27\u7684\u4e8b\u513f\u5c31\u662f\u6740\u4eba\u6253\u67b6\u3002\u201d\u90a3\u76f8\u516c\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u8bb8\u6211\u770b\uff0c\u6211\u5c31\u4e0d\u770b\u3002\u90a3\u4f60\u5411\u7740\u6211\uff0c\u8ba9\u6211\u628a\u4f60\u7f8e\u4e3d\u7684\u8138\u86cb\u513f\u77a7\u4e2a\u9971\u3002\u201d\u90a3\u7f8e\u5987\u4f4e\u4f4e\u4e00\u7b11\uff0c\u6781\u662f\u5a07\u5a9a\uff0c\u679c\u771f\u62ac\u8d77\u4e86\u5934\u671b\u4ed6\u3002\u4e24\u4eba\u56db\u76ee\u4ea4\u6295\uff0c\u8138\u4e0a\u90fd\u5145\u6ee1\u4e86\u67d4\u60c5\u871c\u610f\u3002 \u3000\u3000\u8fd9\u65f6\u9a6c\u884c\u7a7a\u4e0e\u90a3\u76d7\u9b41\u5374\u5df2\u6597\u5f97\u5982\u706b\u5982\u837c\uff0c\u751a\u662f\u6fc0\u70c8\u3002\u9a6c\u884c\u7a7a\u7684\u4e00\u8def\u67e5\u62f3\u582a\u582a\u6253\u5b8c\uff0c\u4ecd\u662f\u5360\u4e0d\u5230\u534a\u70b9\u4e0a\u98ce\uff0c\u90a3\u960e\u57fa\u7684\u62f3\u811a\u6765\u6765\u53bb\u53bb\u53ea\u6709\u5341\u51e0\u62db\uff0c\u6216\u4f38\u62f3\u76f4\u51fb\uff0c\u6216\u94a9\u817f\u53cd\u8e22\uff0c\u6216\u6c89\u65f6\u64d2\u62ff\uff0c\u6216\u5288\u638c\u5939\u817f\u3002\u4e09\u540d\u6b66\u5b98\u770b\u4e86\u4e00\u9635\uff0c\u65e9\u5bdf\u89c9\u4ed6\u62db\u6570\u6709\u9650\uff0c\u4f46\u9a6c\u884c\u7a7a\u5c45\u7136\u6218\u4ed6\u4e0d\u4e0b\uff0c\u90fd\u89c9\u597d\u7b11\u3002 \u3000\u3000\u773c\u89c1\u9a6c\u884c\u7a7a\u4f7f\u4e00\u62db\u201c\u9a6c\u6863\u63a8\u62f3\u201d\uff0c\u8de8\u817f\u6210\u9a91\u9a6c\u52bf\uff0c\u53f3\u624b\u62bd\u56de\uff0c\u5de6\u624b\u5411\u524d\u731b\u63a8\u3002\u4f55\u601d\u8c6a\u53eb\u9053\uff1a\u201c\u6c89\u8098\u64d2\u62ff\u3002\u201d\u679c\u7136\u4e0d\u51fa\u6240\u6599\u3002\u960e\u57fa\u624b\u8098\u4e00\u6c89\uff0c\u5c31\u65bd\u64d2\u62ff\u624b\u6293\u4ed6\u624b\u8155\u3002\u9a6c\u884c\u7a7a\u6025\u5fd9\u53d8\u62db\uff0c\u624b\u81c2\u7f29\u56de\uff0c\u5fae\u5fae\u8f6c\u8eab\u3002\u4f55\u601d\u8c6a\u7b11\u9053\uff1a\u201c\u94a9\u817f\u53cd\u8e22\uff01\u201d\u960e\u57fa\u679c\u7136\u94a9\u8d77\u53f3\u817f\uff0c\u5411\u540e\u53cd\u8e22\u3002\u9a6c\u884c\u7a7a\u7684\u6b66\u529f\u9ad8\u51fa\u4f55\u601d\u8c6a\u4e0d\u77e5\u591a\u5c11\uff0c\u4f55\u601d\u8c6a\u65e2\u5df2\u4e8b\u5148\u77a7\u51fa\uff0c\u4ed6\u5c82\u6709\u6599\u4e0d\u5230\u4e4b\u7406\uff1f\u4f46\u8bf4\u4e5f\u5947\u602a\uff0c\u660e\u77e5\u5bf9\u624b\u8981\u94a9\u817f\u53cd\u8e22\uff0c\u7adf\u7136\u65e0\u6cd5\u4ee5\u4f0f\u7740\u7834\u89e3\u3002 \u3000\u3000\u9a6c\u884c\u7a7a\u53f7\u79f0\u201c\u767e\u80dc\u795e\u62f3\u201d\uff0c\u5c11\u6797\u6d3e\u5404\u8def\u62f3\u672f\uff0c\u5168\u90e8\u70c2\u719f\u4e8e\u80f8\uff0c\u773c\u89c1\u67e5\u62f3\u5948\u4f55\u4e0d\u5f97\u5bf9\u65b9\uff0c\u62db\u6570\u4e00\u53d8\uff0c\u7a81\u7136\u5feb\u6253\u5feb\u8e22\uff0c\u62f3\u52bf\u5982\u98ce\uff0c\u65c1\u89c2\u8005\u767b\u65f6\u76ee\u4e3a\u4e4b\u7729\uff0c\u4ed6\u4f7f\u7684\u662f\u4e00\u8def\u201c\u71d5\u9752\u62f3\u201d\u3002 \u3000\u3000\u90a3\u71d5\u9752\u662f\u5b8b\u671d\u6881\u5c71\u6cca\u4e0a\u597d\u6c49\uff0c\u5f53\u5e74\u76f8\u6251\u4e4b\u6280\uff0c\u5929\u4e0b\u65e0\u5bf9\u3002 \u3000\u3000\u8fd9\u4e00\u8def\u62f3\u6cd5\u4f20\u5c06\u4e0b\u6765\uff0c\u8bb2\u7a76\u7eb5\u8dc3\u8d77\u4f0f\uff0c\u76d8\u62d7\u6311\u6253\uff0c\u5168\u662f\u8fdb\u624b\u62db\u6570\u3002\u9a6c\u884c\u7a7a\u5e74\u7eaa\u867d\u8001\uff0c\u8eab\u624b\u4ecd\u662f\u77eb\u6377\u5f02\u5e38\uff0c\u7a9c\u9ad8\u4f0f\u4f4e\uff0c\u5b9b\u5982\u72f8\u732b\u76f8\u4f3c\u3002\u960e\u57fa\u773c\u89c1\u654c\u4eba\u53d8\u62db\uff0c\u7adf\u662f\u6beb\u4e0d\u7406\u4f1a\uff0c\u4ecd\u65e7\u662f\u90a3\u5341\u51e0\u62db\u53c8\u7b28\u62d9\u53c8\u96be\u770b\u7684\u62f3\u811a\u7ffb\u6765\u8986\u53bb\u5730\u4f7f\u7528\u3002 \u3000\u3000\u5546\u5b9d\u9707\u3001\u5f90\u94ee\u3001\u9a6c\u6625\u82b1\uff0c\u4ee5\u53ca\u621a\u9556\u5934\u3001\u6768\u9556\u5934\u89c1\u8fd9\u76d7\u9b41\u7684\u6b66\u529f\u5982\u6b64\u53e4\u602a\uff0c\u90fd\u662f\u8be7\u5f02\u4e07\u5206\u3002\u6bcf\u4e2a\u4eba\u5230\u8fd9\u65f6\u90fd\u5df2\u6599\u5230\u4ed6\u4e0b\u4e00\u62db\u662f\u4f38\u62f3\u76f4\u51fb\uff0c\u8fd8\u662f\u5288\u638c\u5939\u817f\uff0c\u4e0d\u7981\u968f\u7740\u4f55\u601d\u8c6a\u53eb\u4e86\u51fa\u6765\uff0c\u4f46\u9a6c\u884c\u7a7a\u7adf\u7136\u59cb\u7ec8\u5948\u4f55\u4ed6\u4e0d\u5f97\u3002\u53ea\u89c1\u9a6c\u8001\u9556\u5934\u201c\u4e0a\u6b65\u8fdb\u8098\u63b4\u8eab\u62f3\u201d\uff0c\u201c\u8fce\u9762\u62a2\u5feb\u6253\u4e09\u62f3\u201d\uff0c\u201c\u5de6\u53f3\u8de8\u6253\u201d\uff0c\u201c\u53cd\u8eab\u683d\u9524\u201d\uff0c\u201c\u8e22\u817f\u64a9\u9634\u5341\u5b57\u62f3\u201d\uff0c\u4e00\u62db\u63a5\u4e00\u62db\uff0c\u62f3\u811a\u4e4b\u5feb\uff0c\u72b9\u5982\u95e8\u5916\u7684\u72c2\u98ce\u66b4\u96e8\u4e00\u822c\u3002\u4f46\u960e\u57fa\u53ea\u662f\u4e00\u62db\u6bdb\u624b\u6bdb\u811a\u7684\u4f38\u81c2\u76f4\u51fb\uff0c\u5c31\u5c06\u4ed6\u6240\u6709\u5de7\u5999\u7684\u62db\u5f0f\u5c3d\u6570\u7834\u89e3\u4e86\u3002 \u3000\u3000\u90a3\u72ec\u81c2\u4eba\u548c\u9ec4\u7626\u5c0f\u5b69\u4e00\u76f4\u7f29\u5728\u5c4b\u89d2\u4e4b\u4e2d\uff0c\u77a7\u7740\u9a6c\u884c\u7a7a\u548c\u960e\u57fa\u6bd4\u6b66\u3002\u72ec\u81c2\u4eba\u4f4e\u58f0\u9053\uff1a\u201c\u5c0f\u7237\uff0c\u4f60\u4ed4\u7ec6\u77a7\u90a3\u4e2a\u76d7\u9b41\uff0c\u8981\u77a7\u5f97\u4ed4\u7ec6\uff0c\u5343\u4e07\u522b\u5fd8\u4e86\u4ed6\u7684\u76f8\u8c8c\u3002\u201d\u5c0f\u5b69\u9053\uff1a\u201c\u5e72\u4e48\u554a\uff1f\u5e72\u4e48\u8981\u77a7\u4ed6\uff1f\u201d\u72ec\u81c2\u4eba\u9053\uff1a\u201c\u4f60\u8bb0\u7740\u8fd9\u4eba\uff0c\u6c34\u8fdc\u522b\u5fd8\u8bb0\u4e86\u3002\u201d\u5c0f\u5b69\u9053\uff1a\u201c\u4ed6\u662f\u4e2a\u5927\u574f\u4eba\u4e48\uff1f\u201d\u72ec\u81c2\u4eba\u54ac\u7259\u5207\u9f7f\u5730\u9053\uff1a\u201c\u9634\u5dee\u9633\u9519\uff0c\u53eb\u54b1\u4eec\u5728\u8fd9\u91cc\u649e\u89c1\u4e86\u4ed6\u3002\u4f60\u77a7\u6e05\u695a\u4e86\uff0c\u53ef\u522b\u8ba9\u4ed6\u77e5\u89c9\u3002\u201d \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u72ec\u81c2\u4eba\u53c8\u9053\uff1a\u201c\u4f60\u603b\u8bf4\u529f\u592b\u7ec3\u5f97\u4e0d\u5bf9\uff0c\u4f60\u4ed4\u7ec6\u77a7\u7740\u4ed6\uff0c\u8bb8\u5c31\u7ec3\u5bf9\u4e86\u3002\u201d\u5c0f\u5b69\u9053\uff1a\u201c\u5e72\u4e48\u5440\uff1f\u201d\u72ec\u81c2\u4eba\u773c\u4e2d\u5fae\u6709\u6cea\u5149\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u73b0\u5728\u8fd8\u4e0d\u80fd\u8bf4\uff0c\u7b49\u4f60\u5e74\u7eaa\u5927\u4e86\uff0c\u6b66\u827a\u7ec3\u597d\u4e86\uff0c\u6211\u539f\u539f\u672c\u672c\u5730\u8bf4\u7ed9\u4f60\u542c\u3002\u201d\u5c0f\u5b69\u770b\u960e\u57fa\u62f3\u6253\u811a\u8e22\uff0c\u59ff\u5f0f\u6781\u5176\u96be\u770b\uff0c\u4f46\u9690\u9690\u4f3c\u6709\u6240\u609f\uff0c\u5ffd\u5730\u5927\u53eb\u4e00\u58f0\uff1a\u201c\u56db\u53d4\uff01\u201d\u72ec\u81c2\u4eba\u5fd9\u9053\uff1a\u201c\u522b\u5927\u58f0\u56b7\u56b7\u3002\u201d\u5c0f\u5b69\u55ef\u4e86\u4e00\u58f0\u7b54\u5e94\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\u7684\u62f3\u811a\u6211\u6709\u4e9b\u61c2\u5566\u3002\u201d\u72ec\u81c2\u4eba\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u597d\u597d\u77a7\u7740\u3002\u4f60\u90a3\u672c\u62f3\u7ecf\u5200\u8c31\uff0c\u524d\u9762\u7f3a\u4e86\u4e24\u9875\uff0c\u6240\u4ee5\u4f60\u603b\u662f\u8bf4\u77a7\u4e0d\u61c2\u3002\u90a3\u7f3a\u4e86\u7684\u4e24\u9875\uff0c\u5c31\u5728\u8fd9\u960e\u57fa\u8eab\u4e0a\u3002\u201d \u3000\u3000\u5c0f\u5b69\u5403\u4e86\u4e00\u60ca\uff0c\u9ec4\u9ec4\u7626\u7626\u7684\u5c0f\u8138\u86cb\u513f\u4e0a\u73b0\u51fa\u4e00\u4e9b\u7ea2\u6655\uff0c\u76ee\u4e0d\u8f6c\u77ac\u5730\u671b\u7740\u960e\u57fa\uff0c\u53c8\u95ee\uff1a\u201c\u600e\u4e48\u4f1a\u5728\u4ed6\u8eab\u4e0a\uff1f\u201d\u72ec\u81c2\u4eba\u9053\uff1a\u201c\u5c06\u6765\u81ea\u4f1a\u8ddf\u4f60\u8bf4\u3002\u8fd9\u5bb6\u4f19\u672c\u6765\u4e0d\u4f1a\u4ec0\u4e48\u6b66\u529f\uff0c\u4f46\u5f97\u4e86\u4e24\u9875\u62f3\u7ecf\uff0c\u5b66\u4f1a\u4e86\u5341\u51e0\u62db\u6b8b\u7f3a\u4e0d\u5168\u7684\u62f3\u6cd5\uff0c\u5c45\u7136\u80fd\u8ddf\u7b2c\u4e00\u6d41\u7684\u62f3\u5e08\u6253\u6210\u5e73\u624b\u3002\u4f60\u60f3\u60f3\uff0c\u90a3\u62f3\u7ecf\u5200\u8c31\u5171\u6709\u4e09\u767e\u591a\u9875\uff0c\u7b49\u4f60\u5c06\u6765\u5b66\u4f1a\u4e86\uff0c\u5b66\u5168\u4e86\uff0c\u80fd\u6709\u591a\u5927\u7684\u672c\u4e8b\u3002\u201d\u90a3\u5c0f\u5b69\u542c\u4e86\u751a\u662f\u6fc0\u52a8\uff0c\u773c\u775b\u4e2d\u95ea\u8000\u7740\u5174\u594b\u7684\u5149\u8292\u3002 \u3000\u3000\u573a\u4e2d\u867d\u662f\u4e24\u4eba\u6bd4\u6b66\uff0c\u4f46\u53ef\u770b\u7684\u5374\u53ea\u6709\u4e00\u4eba\u3002\u960e\u57fa\u6765\u6765\u53bb\u53bb\u8fd9\u5341\u51e0\u62db\uff0c\u5927\u5bb6\u5b9e\u5728\u90fd\u770b\u5f97\u817b\u4e86\u3002\u9a6c\u884c\u7a7a\u7684\u62f3\u62db\u5374\u662f\u53d8\u5e7b\u767e\u51fa\u3002 \u3000\u3000\u4e00\u5957\u201c\u71d5\u9752\u62f3\u201d\u5948\u4f55\u4e0d\u4e86\u5bf9\u65b9\uff0c\u5ffd\u7136\u62f3\u6cd5\u53c8\u53d8\uff0c\u4f7f\u51fa\u4e00\u5957\u201c\u9c81\u667a\u6df1\u9189\u8dcc\u201d\uff0c\u4f46\u89c1\u4ed6\u5982\u75af\u5982\u766b\uff0c\u4f3c\u9189\u4f3c\u72c2\uff0c\u5ffd\u800c\u5367\u5012\uff0c\u5ffd\u800c\u8dc3\u8d77\uff0c\u201c\u7f57\u6c49\u659c\u5367\u201d\uff0c\u201c\u4ed9\u4eba\u6e34\u76f9\u201d\uff0c\u8fd9\u8def\u62f3\u6cd5\u4f3c\u4e4e\u867d\u4e71\u6253\u4e71\u8e22\u4e00\u822c\uff0c\u5176\u5b9e\u662f\u7cbe\u5f69\u4e4b\u6781\u3002\u8fd9\u65f6\u960e\u57fa\u90a3\u5341\u51e0\u62db\u7b28\u62f3\u5374\u6e10\u6e10\u4e0d\u7ba1\u4e8b\u4e86\uff0c\u5bf9\u65b9\u62f3\u811a\u6765\u8def\u4e5f\u770b\u4e0d\u660e\u767d\uff0c\u4e0d\u7531\u5f97\u5fc3\u4e0b\u7740\u614c\u3002\u731b\u542c\u5f97\u9a6c\u884c\u7a7a\u559d\u4e00\u58f0\uff1a\u201c\u7740\uff01\u201d\u4e00\u811a\u201c\u9ca4\u9c7c\u7ffb\u8eab\u6405\u4e1d\u817f\u201d\uff0c\u6b63\u597d\u8e22\u5728\u4ed6\u7684\u8170\u95f4\u3002\u960e\u57fa\u75db\u5f97\u5f2f\u4e0b\u4e86\u8170\u3002 \u3000\u3000\u9a6c\u884c\u7a7a\u77e5\u9053\u5bf9\u65b9\u529f\u592b\u4e86\u5f97\uff0c\u8fd9\u4e00\u811a\u867d\u4e2d\u8981\u5bb3\uff0c\u53ea\u6015\u4ecd\u7136\u96be\u4ee5\u4f7f\u4ed6\u8eab\u5e26\u91cd\u4f24\u3002\u82e5\u662f\u5e73\u5e38\u6bd4\u6b66\u8f83\u91cf\uff0c\u80dc\u4e86\u8fd9\u4e00\u817f\u81ea\u7136\u53ef\u4ee5\u6536\u624b\uff0c\u4f46\u8fd9\u756a\u4e89\u6597\u5173\u8054\u4e09\u5341\u4e07\u4e24\u9556\u94f6\uff0c\u600e\u5bb9\u5f97\u654c\u4eba\u5598\u606f\u7247\u523b\uff1f\u82e5\u662f\u4e89\u7aef\u91cd\u8d77\uff0c\u4e5f\u672a\u5fc5\u5b9a\u80fd\u518d\u80dc\uff0c\u5f53\u4e0b\u5f97\u7406\u4e0d\u8ba9\u4eba\uff0c\u7eb5\u8eab\u4e0a\u524d\uff0c\u4e00\u817f\u201c\u62d0\u5b50\u811a\u201d\uff0c\u53c8\u5f80\u4ed6\u540e\u5fc3\u8e22\u53bb\u3002 \u3000\u3000\u7fa4\u76d7\u9f50\u58f0\u5927\u54d7\u3002\u960e\u57fa\u5ffd\u5730\u4e00\u811a\u94a9\u817f\u53cd\u8e22\uff0c\u6765\u52bf\u53d8\u5e7b\u65e0\u65b9\uff0c\u9a6c\u884c\u7a7a\u867d\u7136\u9605\u5386\u4e30\u5bcc\uff0c\u4e00\u65f6\u7adf\u89c1\u4e0d\u53ca\u6b64\uff0c\u88ab\u4ed6\u8fd9\u4e00\u817f\u8e22\u5728\u5c0f\u8179\u4e4b\u4e0a\uff0c\u4ef0\u5929\u4e00\u4ea4\u76f4\u6454\u51fa\u53bb\u3002\u9a6c\u6625\u82b1\u4e0e\u5f90\u94ee\u53cc\u53cc\u62a2\u4e0a\u6276\u8d77\u3002\u4f46\u89c1\u4ed6\u9762\u5982\u767d\u7eb8\uff0c\u8fde\u58f0\u54b3\u55fd\uff0c\u53ea\u8bf4\uff1a\u201c\u62da\u6b7b\u62a4\u9556\uff01\u201d \u3000\u3000\u5f90\u94ee\u4e0e\u9a6c\u6625\u82b1\u5404\u6301\u5355\u5200\uff0c\u62a4\u5728\u9a6c\u884c\u7a7a\u4e24\u65c1\u3002\u960e\u57fa\u8170\u91cc\u4e5f\u75db\u5f97\u5389\u5bb3\uff0c\u53f3\u624b\u6325\u4e86\u51e0\u4e0b\uff0c\u4e24\u540d\u9ed1\u8863\u5927\u6c49\u8d70\u4e86\u4e0a\u6765\u3002\u960e\u57fa\u53eb\u9053\uff1a\u201c\u53d6\u9556\u5427\uff01\u8fd8\u7b49\u4ec0\u4e48\uff1f\u201d\u7fa4\u76d7\u5404\u51fa\u5175\u5203\uff0c\u9f50\u5411\u9556\u5ba2\u6740\u53bb\u3002\u9a6c\u6625\u82b1\u3001\u5f90\u94ee\u3001\u621a\u9556\u5934\u3001\u6768\u9556\u5934\u5927\u547c\u8fce\u654c\u3002 \u3000\u3000\u7fa4\u76d7\u4eba\u591a\uff0c\u9664\u960e\u57fa\u5916\u867d\u65e0\u9ad8\u624b\uff0c\u4f46\u9a6c\u6625\u82b1\u4e0e\u5f90\u94ee\u8981\u5206\u5fc3\u7167\u6599\u7236\u4eb2\uff0c\u7ed9\u7fa4\u76d7\u4e24\u4e0b\u91cc\u4e00\u653b\uff0c\u60c5\u52bf\u767b\u89c1\u5371\u6025\u3002\u5546\u5b9d\u9707\u62d4\u51fa\u5355\u5200\uff0c\u53eb\u9053\uff1a\u201c\u4e09\u4f4d\u4f8d\u536b\u5927\u4eba\uff0c\u54b1\u4eec\u52a8\u624b\u5427\uff01\u201d\u4f55\u601d\u8c6a\u9053\uff1a\u201c\u597d\uff0c\u8d76\u8d70\u5f3a\u76d7\u518d\u8bf4\u3002\u201d\u56db\u4e2a\u751f\u529b\u519b\u52a0\u5165\u6218\u56e2\u3002 \u3000\u3000\u5546\u5b9d\u9707\u89c1\u9a6c\u6625\u82b1\u7ed9\u4e24\u540d\u76d7\u8d3c\u7528\u5175\u5668\u5c01\u4f4f\u4e86\uff0c\u6e10\u6e10\u65bd\u5c55\u4e0d\u5f00\u624b\u811a\uff0c\u5f53\u5373\u62a2\u5c06\u4e0a\u53bb\uff0c\u559d\u9053\uff1a\u201c\u7537\u5b50\u6c49\u6b3a\u4fae\u59d1\u5a18\uff0c\u8fd8\u662f\u4e24\u4e2a\u6597\u4e00\u4e2a\uff0c\u4e0d\u5bb3\u81ca\u4e48\uff1f\u201d\u5237\u7684\u4e00\u5200\uff0c\u5f80\u90a3\u9ad8\u4e2a\u513f\u7684\u76d7\u8d3c\u5934\u4e0a\u780d\u53bb\u3002 \u3000\u3000\u90a3\u4eba\u56de\u97ad\u62db\u67b6\uff0c\u51e0\u4e2a\u56de\u5408\uff0c\u5546\u5b9d\u9707\u5200\u4e2d\u5939\u638c\uff0c\u5de6\u624b\u4e00\u638c\u62b9\u5728\u4ed6\u80f8\u53e3\uff0c\u5c06\u4ed6\u51fb\u5f97\u76f4\u63bc\u51fa\u53bb\u3002\u9a6c\u6625\u82b1\u5598\u606f\u9053\uff1a\u201c\u884c\u4e86\uff0c\u8fd9\u4e00\u4e2a\u8ba9\u6211\u6765\u6599\u7406\u3002\u201d\u5546\u5b9d\u9707\u4e00\u7b11\u9000\u5f00\uff0c\u5f84\u53bb\u5e2e\u52a9\u5f90\u94ee\uff0c\u4e09\u5200\u4e24\u638c\uff0c\u53c8\u6253\u53d1\u4e86\u4e00\u540d\u76d7\u8d3c\u3002\u5f90\u94ee\u611f\u6fc0\u4e4b\u4f59\uff0c\u751a\u662f\u94a6\u4f69\u5e08\u7236\u773c\u5149\uff0c\u8fd9\u5c11\u5e74\u7684\u6b66\u529f\u679c\u5728\u81ea\u5df1\u4e4b\u4e0a\u3002 \u3000\u3000\u8fd9\u4e48\u4e00\u6765\uff0c\u5385\u4e0a\u60c5\u52bf\u53d8\u6362\uff0c\u7fa4\u76d7\u7eb7\u7eb7\u8d25\u9000\uff0c\u62a2\u7740\u5f80\u95e8\u53e3\u5954\u51fa\u3002\u731b\u542c\u5f97\u4e00\u4eba\u6e05\u58f0\u957f\u5578\uff0c\u53eb\u9053\uff1a\u201c\u5927\u5bb6\u4f4f\u624b\uff0c\u6211\u6709\u8bdd\u8bf4\u3002\u201d \u3000\u3000\u4f17\u4eba\u6597\u5f97\u751a\u7d27\uff0c\u65e0\u4eba\u7406\u4f1a\u3002\u5546\u5b9d\u9707\u7a81\u89c1\u4eba\u5f71\u4e00\u89c1\uff0c\u4e00\u4eba\u4f38\u638c\u5728\u9762\u524d\u4e00\u6447\uff0c\u5f53\u5373\u4e3e\u5200\u524a\u53bb\uff0c\u90a3\u4eba\u53f3\u624b\u4e00\u94a9\u4e00\u5e26\uff0c\u5df2\u5c06\u4ed6\u5355\u5200\u593a\u4e0b\uff0c\u5f80\u5730\u4e0b\u4e00\u6454\u3002\u5546\u5b9d\u9707\u5927\u60ca\uff0c\u6025\u5fd9\u8dc3\u540e\uff0c\u77a7\u90a3\u4eba\u65f6\uff0c\u5374\u662f\u90a3\u670d\u9970\u534e\u8d35\u7684\u76f8\u516c\u3002 \u3000\u3000\u90a3\u76f8\u516c\u5927\u8e0f\u6b65\u8d70\u5165\u4eba\u4e1b\uff0c\u53cc\u624b\u94a9\u62ff\u62cd\u6253\uff0c\u53ea\u542c\u53ee\u53ee\u5f53\u5f53\uff0c\u54cd\u58f0\u4e0d\u7edd\uff0c\u5175\u5203\u843d\u4e86\u4e00\u5730\uff0c\u539f\u6765\u90fd\u88ab\u4ed6\u65bd\u5c55\u5c0f\u64d2\u62ff\u624b\u6cd5\uff0c\u593a\u8fc7\u6765\u629b\u4e0b\u3002\u7fa4\u76d7\u4e0e\u4f17\u9556\u5ba2\u60ca\u9a87\u4e4b\u4e0b\uff0c\u5404\u81ea\u8dc3\u5f00\uff0c\u5446\u5446\u5730\u671b\u7740\u4ed6\u3002\u960e\u57fa\u4e00\u6115\uff0c\u5ffd\u7136\u8bb0\u8d77\u4e86\u5341\u4f59\u5e74\u524d\u4e4b\u4e8b\uff0c\u53eb\u9053\uff1a\u201c\u7530\u76f8\u516c\uff01\u662f\u4f60\uff1f\u201d \u3000\u3000\u90a3\u76f8\u516c\u60f3\u4e0d\u8d77\u4ed6\u662f\u8c01\uff0c\u5947\u9053\uff1a\u201c\u4f60\u8ba4\u5f97\u6211\uff1f\u201d\u960e\u57fa\u7b11\u9053\uff1a\u201c\u5341\u4e09\u5e74\u524d\u5728\u6ca7\u5dde\u5e9c\uff0c\u5c0f\u7684\u66fe\u670d\u4f8d\u8fc7\u4f60\u8001\u3002\u201d\u90a3\u76f8\u516c\u4f4e\u5934\u4e00\u60f3\uff0c\u604d\u7136\u8bb0\u8d77\uff0c\u8bf4\u9053\uff1a\u201c\u662f\u4e86\uff0c\u4f60\u5c31\u662f\u90a3\u4e2a\u8dcc\u6253\u533b\u751f\u3002\u600e\u4e48\u5b66\u4f1a\u4e86\u4e00\u8eab\u6b66\u529f\uff0c\u505a\u8d77\u5be8\u4e3b\u6765\u5566\uff1f\u201d\u960e\u57fa\u4e0a\u524d\u8bf7\u4e86\u4e2a\u5b89\uff0c\u8bf4\u9053\uff1a\u201c\u5168\u51ed\u4f60\u8001\u683d\u57f9\u3002\u201d\u539f\u6765\u8fd9\u76f8\u516c\u6253\u626e\u4e4b\u4eba\uff0c\u6b63\u662f\u5929\u9f99\u95e8\u5317\u5b97\u638c\u95e8\u4eba\u7530\u5f52\u519c\u3002 \u3000\u3000\u9556\u884c\u4eba\u4f17\u773c\u89c1\u5df2\u53ef\u9a71\u9000\u7fa4\u76d7\uff0c\u54ea\u77e5\u8fd9\u7530\u76f8\u516c\u4e0d\u4f46\u6b66\u529f\u5f3a\u6781\uff0c\u8fd8\u4e0e\u76d7\u9b41\u662f\u65e7\u4ea4\uff0c\u8fd9\u4e00\u4e0b\u53ef\u7cdf\u7cd5\u5df2\u6781\u3002\u9a6c\u884c\u7a7a\u4f4e\u58f0\u5631\u5490\uff0c\u53eb\u5927\u4f19\u513f\u62a4\u4f4f\u9556\u8f66\uff0c\u77a7\u4ed6\u773c\u8272\u884c\u4e8b\u3002 \u3000\u3000\u7530\u5f52\u519c\u53cc\u76ee\u81ea\u5de6\u81f3\u53f3\u5728\u4f17\u4eba\u8138\u4e0a\u6a2a\u626b\u4e00\u904d\uff0c\u7136\u540e\u53c8\u81ea\u53f3\u81f3\u5de6\u5730\u6a2a\u626b\u8fc7\u6765\uff0c\u518d\u5411\u5929\u4e95\u4e2d\u503e\u76c6\u800c\u4e0b\u7684\u5927\u96e8\u671b\u4e86\u4e00\u773c\uff0c\u773c\u5149\u7ec8\u4e8e\u505c\u5728\u9556\u8f66\u4e4b\u4e0a\uff0c\u8bf4\u9053\uff1a\u201c\u960e\u5144\u3001\u4eca\u65e5\u7684\u4e70\u5356\u4f60\u53ef\u662f\u8d54\u5b9a\u5566\u3002\u201d\u960e\u57fa\u966a\u7b11\u9053\uff1a\u201c\u4f60\u8001\u4eba\u5bb6\u522b\u89c1\u602a\uff0c\u4e5f\u662f\u5f1f\u5144\u4eec\u5c11\u53e3\u996d\u5403\uff0c\u8d70\u6295\u65e0\u8def\uff0c\u8fd9\u624d\u5e72\u8d77\u8fd9\u6ca1\u672c\u94b1\u4e70\u5356\u6765\u3002\u6211\u4eec\u5b9a\u5f53\u6539\u8fc7\u81ea\u65b0\uff0c\u4e0d\u6562\u5fd8\u4e86\u7530\u76f8\u516c\u4eca\u65e5\u7684\u6069\u5fb7\u3002\u201d\u7530\u5f52\u519c\u54c8\u54c8\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u600e\u4e48\u8ddf\u6211\u95f9\u8d77\u865a\u6587\u6765\u5566\uff1f\u8001\u960e\uff0c\u4f60\u62ff\u4e94\u4e07\u4e24\u9556\u94f6\uff0c\u591f\u4e0d\u591f\u4f7f\u4e86\uff1f\u201d \u3000\u3000\u960e\u57fa\u4e00\u6014\uff0c\u966a\u7b11\u9053\uff1a\u201c\u4f60\u8001\u4eba\u5bb6\u5f00\u73a9\u7b11\u5566\u3002\u201d\u7530\u5f52\u519c\u9053\uff1a\u201c\u5f00\u4ec0\u4e48\u73a9\u7b11\uff1f\u8fd9\u91cc\u4e09\u5341\u4e07\u9556\u94f6\uff0c\u6211\u53d6\u4e00\u534a\u5341\u4e94\u4e07\uff0c\u4f59\u4e0b\u7684\u4f60\u53d6\u4e94\u4e07\uff0c\u8fd8\u6709\u5341\u4e07\u4e24\u4f60\u8bf4\u600e\u4e48\u5206\uff1f\u201d \u3000\u3000\u960e\u57fa\u559c\u51fa\u671b\u5916\uff0c\u5fd9\u9053\uff1a\u201c\u4f60\u8001\u4eba\u5bb6\u4e00\u5e76\u53d6\u53bb\u5c31\u662f\u4e86\uff0c\u8fd8\u5206\u4ec0\u4e48\uff1f\u201d\u7530\u5f52\u519c\u6447\u5934\u9053\uff1a\u201c\u90a3\u4e0d\u6210\u8bdd\uff0c\u8fd9\u54ea\u91cc\u8fd8\u6709\u6c5f\u6e56\u4e49\u6c14\uff1f\u9002\u624d\u6211\u4eec\u8fdb\u6765\u907f\u96e8\uff0c\u6211\u2026\u2026\u6211\u2026\u2026\u6211\u5a18\u5b50\u8863\u670d\u6e7f\u4e86\u2026\u2026\u201d\u90a3\u7f8e\u5987\u542c\u4ed6\u8bf4\u201c\u6211\u5a18\u5b50\u201d\u4e09\u5b57\uff0c\u8138\u4e0a\u4e00\u7ea2\uff0c\u795e\u6001\u5fae\u73b0\u5ff8\u6029\uff0c\u5411\u7530\u5f52\u519c\u5fae\u5fae\u4e00\u7b11\u3002\u7530\u5f52\u519c\u62a5\u4ee5\u4e00\u7b11\uff0c\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u9556\u884c\u8fd9\u4f4d\u59d1\u5a18\u501f\u8863\u670d\u7ed9\u5979\uff0c\u8fd9\u4e00\u756a\u60c5\u5206\u4e0d\u80fd\u4e0d\u62a5\uff0c\u54b1\u4eec\u7ed9\u9a6c\u59d1\u5a18\u7559\u4e94\u4e07\u4e24\u3002 \u3000\u3000\u8fd8\u6709\uff0c\u8fd9\u91cc\u4e09\u4f4d\u4f8d\u536b\u5927\u4eba\u5728\u6b64\uff0c\u5e38\u8a00\u9053\u89c1\u8005\u6709\u4efd\uff0c\u6bcf\u4eba\u5206\u4e00\u4e07\u4e24\u3002\u4f59\u4e0b\u4e8c\u4e07\uff0c\u5c31\u9001\u7ed9\u6b64\u95f4\u4e3b\u4eba\u3002\u4f60\u8bf4\u6211\u8fd9\u6837\u5206\u6cd5\u516c\u4e0d\u516c\u9053\uff1f\u201d\u960e\u57fa\u8fde\u8fde\u9f13\u638c\uff0c\u5927\u53eb\uff1a\u201c\u516c\u9053\u4e4b\u6781\uff0c\u516c\u9053\u4e4b\u6781\uff01\u6211\u65e9\u8bf4\u4f60\u7530\u76f8\u516c\u662f\u5929\u4e0b\u7b2c\u4e00\u7b49\u6177\u6168\u7684\u5927\u82f1\u96c4\u3002\u201d \u3000\u3000\u9a6c\u884c\u7a7a\u3001\u5f90\u94ee\u3001\u9a6c\u6625\u82b1\u7b49\u542c\u7530\u5f52\u519c\u4f83\u4f83\u800c\u8c08\uff0c\u65c1\u82e5\u65e0\u4eba\uff0c\u5012\u4f3c\u8fd9\u4e09\u5341\u4e07\u4e24\u94f6\u5df2\u662f\u4ed6\u56ca\u4e2d\u4e4b\u7269\u4e00\u822c\u3002\u9a6c\u884c\u7a7a\u8eab\u53d7\u91cd\u4f24\uff0c\u8fd9\u4e48\u4e00\u6c14\uff0c\u66f4\u662f\u9669\u6b32\u6655\u53bb\u3002\u5f90\u94ee\u773c\u671b\u5e08\u7236\uff0c\u53ea\u95ee\uff1a\u201c\u600e\u4e48\u529e\uff1f\u600e\u4e48\u529e\uff1f\u201d\u9a6c\u6625\u82b1\u6012\u9053\uff1a\u201c\u4ec0\u4e48\u600e\u4e48\u529e\uff1f\u201d\u5f2f\u8170\u62fe\u8d77\u5730\u4e0b\u7684\u5355\u5200\uff0c\u53eb\u9053\uff1a\u201c\u59d3\u7530\u7684\uff0c\u4f60\u5f53\u6211\u4eec\u662f\u6b7b\u4eba\u8fd8\u662f\u6d3b\u4eba\uff1f\u201d\u8bf4\u7740\u626c\u8d77\u5355\u5200\uff0c\u5f84\u5f80\u7530\u5f52\u519c\u6251\u53bb\u3002 \u3000\u3000\u7530\u5f52\u519c\u7b11\u9053\uff1a\u201c\u4f60\u522b\u903c\u6211\u52a8\u624b\uff0c\u6211\u5a18\u5b50\u53ef\u8981\u559d\u918b\u3002\u201d\u90a3\u7f8e\u5987\u5550\u4e86\u4e00\u53e3\uff0c\u7b11\u9a82\uff1a\u201c\u8d2b\u5634\uff01\u201d\u4f46\u4f3c\u5bf9\u4ed6\u7684\u8f7b\u8584\u53e3\u543b\u751a\u4e3a\u559c\u7231\u3002 \u3000\u3000\u9a6c\u6625\u82b1\u542c\u4ed6\u8a00\u8bed\u65e0\u793c\uff0c\u66f4\u662f\u607c\u6012\uff0c\u4e0a\u6b65\u4e00\u5200\uff0c\u62e6\u8170\u6a2a\u780d\u3002\u7530\u5f52\u519c\u7b11\u9053\uff1a\u201c\u5509\u54df\uff0c\u4e0d\u597d\uff0c\u6211\u5a18\u5b50\u53ef\u4e0d\u8bb8\u6211\u8ddf\u5973\u4eba\u6253\u67b6\u3002\u201d\u624b\u6307\u5728\u5979\u5200\u80cc\u4e0a\u4e00\u51fb\uff0c\u9a6c\u6625\u82b1\u62ff\u634f\u4e0d\u4f4f\uff0c\u8131\u624b\u64a4\u5200\u3002\u7530\u5f52\u519c\u624b\u6cd5\u5feb\u6781\uff0c\u53f3\u624b\u62a2\u8fc7\u5200\u67c4\uff0c\u5de6\u624b\u5df2\u62ff\u4f4f\u5979\u624b\u8155\uff0c\u4e3e\u8d77\u5200\u6765\uff0c\u4f5c\u52bf\u8981\u5f80\u5979\u5934\u9888\u4e2d\u780d\u4e0b\uff0c\u53e3\u4e2d\u5374\u53f9\u9053\uff1a\u201c\u4f3c\u8fd9\u822c\u5982\u82b1\u5982\u6708\u8c8c\uff0c\u600e\u53eb\u6211\u4e0d\u4f5c\u60dc\u7389\u601c\u9999\u4eba\uff01\u201d \u3000\u3000\u5546\u5b9d\u9707\u548c\u5f90\u94ee\u89c1\u4ed6\u620f\u5f04\u9a6c\u6625\u82b1\uff0c\u53cc\u53cc\u62a2\u51fa\u3002\u5546\u5b9d\u9707\u53f3\u624b\u4e00\u626c\uff0c\u4e00\u679d\u91d1\u9556\u53d6\u4ed6\u5de6\u76ee\u3002\u5f90\u94ee\u6025\u4e86\uff0c\u6765\u4e0d\u53ca\u62fe\u53d6\u5730\u4e0b\u5175\u5203\uff0c\u98de\u811a\u5c31\u8e22\u4ed6\u540e\u5fc3\u3002\u7530\u5f52\u519c\u500f\u5730\u56de\u8eab\uff0c\u64a4\u5200\u64d2\u62ff\uff0c\u6293\u4f4f\u4ed6\u7684\u8db3\u8e1d\uff0c\u5f80\u4e0a\u4e00\u63d0\u3002\u5f90\u94ee\u8eab\u5b50\u5012\u8f6c\uff0c\u53ea\u611f\u817f\u4e0a\u4e00\u9635\u5267\u75db\uff0c\u5931\u58f0\u5927\u53eb\uff0c\u539f\u6765\u90a3\u679d\u91d1\u9556\u6253\u8fdb\u4e86\u4ed6\u53f3\u817f\u3002\u7530\u5f52\u519c\u6325\u624b\u4e00\u6296\uff0c\u5f90\u94ee\u7684\u8eab\u5b50\u72b9\u5982\u4e00\u67c4\u626b\u5e1a\u822c\u6a2a\u626b\u51fa\u53bb\uff0c\u6b63\u649e\u5728\u9a6c\u6625\u82b1\u817f\u4e0a\uff0c\u4e24\u4eba\u8dcc\u5728\u4e00\u8d77\u3002\u4f17\u4eba\u89c1\u4ed6\u620f\u800d\u4e8c\u4eba\uff0c\u5982\u5f04\u5a74\u513f\uff0c\u54ea\u91cc\u8fd8\u6562\u4e0a\u524d\uff1f \u3000\u3000\u7530\u5f52\u519c\u9053\uff1a\u201c\u960e\u5144\uff0c\u4f60\u628a\u9556\u94f6\u5c31\u7167\u9002\u624d\u6211\u8bf4\u7684\u90a3\u4e48\u5206\u4e86\uff0c\u5957\u4e00\u8f86\u5927\u8f66\u7ed9\u6211\uff0c\u6211\u4eec\u4e24\u53e3\u5b50\u8eab\u6709\u6025\u4e8b\uff0c\u987b\u5f97\u5192\u96e8\u8d76\u8def\u3002\u201d\u960e\u57fa\u5927\u559c\uff0c\u8fde\u58f0\u7b54\u5e94\u3002\u7fa4\u76d7\u4ece\u9556\u8f66\u4e2d\u53d6\u51fa\u94f6\u9798\uff0c\u4e94\u4e07\u4e24\u7684\u5806\u6210\u4e00\u5806\uff0c\u4e09\u4e07\u4e24\u3001\u4e8c\u4e07\u4e24\u53c8\u5404\u4f5c\u4e00\u5806\uff0c\u5206\u522b\u5806\u5728\u5730\u4e0b\uff0c\u5411\u4f17\u8f66\u592b\u559d\u9053\uff1a\u201c\u4e56\u4e56\u5730\u8d76\u8def\u3002\u201d \u3000\u3000\u5317\u9053\u4e0a\u6709\u4e2a\u89c4\u77e9\uff0c\u7eff\u6797\u8c6a\u5ba2\u52ab\u9556\u62a2\u94f6\uff0c\u5374\u4e0d\u4f24\u5bb3\u8f66\u592b\uff0c\u751a\u81f3\u811a\u529b\u9152\u94b1\u4e5f\u4f9d\u5e38\u4f8b\u7167\u7ed9\uff0c\u4f46\u82e5\u8f66\u592b\u4e0d\u542c\u5631\u5490\uff0c\u81ea\u7136\u53c8\u4f5c\u522b\u8bba\u3002\u4f17\u8f66\u592b\u89c1\u4e86\u8fd9\u7b49\u60c5\u52bf\uff0c\u54ea\u6562\u4e0d\u4f9d\uff0c\u5192\u7740\u5927\u96e8\uff0c\u5c06\u94f6\u8f66\u4e00\u8f86\u8f86\u63a8\u51fa\u53bb\u3002 \u3000\u3000\u9a6c\u884c\u7a7a\u89c1\u94f6\u8f66\u51fa\u53bb\u4e00\u8f86\uff0c\u5fc3\u91cc\u5c31\u53d1\u4e00\u9635\u75bc\uff0c\u53ea\u89c1\u4e00\u8f86\u9aa1\u8f66\u8d76\u5230\u5ead\u524d\uff0c\u7530\u5f52\u519c\u6276\u7740\u5a18\u5b50\u4fbf\u8981\u4e0a\u8f66\u3002\u53ea\u8981\u9aa1\u8f66\u4e00\u884c\uff0c\u9a6c\u884c\u7a7a\u5c31\u662f\u8eab\u8d25\u540d\u88c2\uff0c\u4e00\u4e16\u8f9b\u82e6\u4ed8\u4e8e\u6d41\u6c34\u4e86\u3002\u4ed6\u98a4\u5dcd\u5dcd\u5730\u7ad9\u8d77\u8eab\u6765\uff0c\u7a81\u7136\u7eb5\u8d77\uff0c\u53eb\u9053\uff1a\u201c\u6211\u548c\u4f60\u62da\u4e86\uff01\u201d\u53cc\u624b\u72b9\u5982\u94c1\u94a9\uff0c\u731b\u5f80\u7530\u5f52\u519c\u8138\u4e0a\u6293\u53bb\u3002\u90a3\u7f8e\u5987\u751a\u662f\u5bb3\u6015\uff0c\u5413\u5f97\u53eb\u4e86\u4e00\u58f0\u3002\u7530\u5f52\u519c\u4fa7\u8eab\u51fa\u638c\uff0c\u51fb\u5411\u4ed6\u80a9\u5934\u3002\u9a6c\u884c\u7a7a\u82e5\u662f\u672a\u53d7\u91cd\u4f24\uff0c\u8fd9\u4e00\u638c\u81ea\u7136\u6253\u4ed6\u4e0d\u7740\uff0c\u4f46\u6b64\u65f6\u5168\u8eab\u7b4b\u9aa8\u4e0d\u542c\u4f7f\u5524\uff0c\u773c\u89c1\u638c\u5230\uff0c\u7adf\u7136\u4e0d\u80fd\u95ea\u907f\uff0c\u7830\u7684\u4e00\u58f0\uff0c\u8eab\u5b50\u98de\u8d77\uff0c\u5411\u9662\u5b50\u4e2d\u8dcc\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u731b\u542c\u5f97\u4e00\u4eba\u55d3\u5b50\u4f4e\u6c89\uff0c\u563f\u563f\u563f\u4e09\u4e0b\u51b7\u7b11\u3002 \u3000\u3000\u8fd9\u4e09\u58f0\u51b7\u7b11\u4f20\u8fdb\u5385\u6765\uff0c\u7530\u5f52\u519c\u548c\u90a3\u7f8e\u5987\u767b\u65f6\u4fbf\u5982\u542c\u89c1\u4e86\u4e16\u4e0a\u6700\u53ef\u6015\u7684\u58f0\u97f3\u4e00\u822c\uff0c\u4e8c\u4eba\u9762\u5982\u767d\u7eb8\uff0c\u8eab\u5b50\u53d1\u98a4\u3002\u7530\u5f52\u519c\u7528\u529b\u4e00\u63a8\uff0c\u5c06\u90a3\u7f8e\u5987\u63a8\u5165\u8f66\u4e2d\uff0c\u98de\u8eab\u800c\u8d77\uff0c\u8de8\u4e0a\u4e86\u9aa1\u80cc\uff0c\u53cc\u817f\u6025\u5939\uff0c\u6325\u97ad\u50ac\u9aa1\u5feb\u8d70\u3002\u54ea\u77e5\u4ed6\u8fde\u8fde\u6325\u97ad\uff0c\u8fd9\u9aa1\u5b50\u53ea\u8de8\u51fa\u4e24\u6b65\uff0c\u7a81\u7136\u505c\u4f4f\uff0c\u518d\u4e5f\u4e0d\u80fd\u5411\u524d\u534a\u5c3a\u3002 \u3000\u3000\u4f17\u4eba\u7ad9\u5728\u5385\u53e3\uff0c\u4ece\u6c34\u5e18\u4e00\u822c\u7684\u5927\u96e8\u4e2d\u671b\u5c06\u51fa\u53bb\u3002\u53ea\u89c1\u4e00\u4e2a\u53c8\u9ad8\u53c8\u7626\u7684\u5927\u6c49\uff0c\u5de6\u624b\u62b1\u7740\u4e00\u4e2a\u5305\u88f9\uff0c\u53f3\u624b\u62c9\u4f4f\u4e86\u5927\u8f66\u7684\u8f66\u8f95\u3002\u90a3\u9aa1\u5b50\u7ed9\u7530\u5f52\u519c\u50ac\u5f97\u6025\u4e86\uff0c\u4f4e\u5934\u5f13\u8170\uff0c\u56db\u8e44\u4e00\u9f50\u53d1\u52b2\uff0c\u4f46\u5927\u6c49\u62c9\u7740\u8f66\u8f95\uff0c\u5927\u8f66\u7adf\u4f3c\u9489\u7262\u5728\u5730\u4e0a\u4e00\u822c\uff0c\u52a8\u4e5f\u4e0d\u52a8\u3002\u6b64\u4eba\u795e\u529b\uff0c\u5b9e\u8db3\u60ca\u4eba\u3002 \u3000\u3000\u90a3\u5927\u6c49\u53c8\u51b7\u7b11\u4e86\u4e00\u58f0\u3002\u7530\u5f52\u519c\u5c1a\u81ea\u8fdf\u7591\uff0c\u8f66\u4e2d\u7684\u7f8e\u5987\u5374\u5df2\u8de8\u51fa\u8f66\u6765\uff0c\u5411\u90a3\u5927\u6c49\u77a7\u4e5f\u4e0d\u77a7\uff0c\u6602\u7136\u8d70\u8fdb\u5385\u53bb\u3002\u7530\u5f52\u519c\u6162\u6162\u8de8\u4e0b\u9aa1\u80cc\uff0c\u4e5f\u8ddf\u7740\u8fdb\u5385\u3002\u4ed6\u5168\u8eab\u88ab\u96e8\u6dcb\u5f97\u6e7f\u900f\uff0c\u5374\u4f3c\u4e1d\u6beb\u4e0d\u89c9\uff0c\u76ee\u5149\u5446\u6ede\uff0c\u5931\u9b42\u843d\u9b44\u4e00\u822c\u3002\u90a3\u7f8e\u5987\u62db\u624b\u53eb\u4ed6\u8fc7\u53bb\uff0c\u5750\u5728\u5979\u7684\u8eab\u8fb9\u3002 \u3000\u3000\u90a3\u9ad8\u7626\u5927\u6c49\u5927\u8e0f\u6b65\u8fdb\u5385\uff0c\u5750\u5728\u706b\u5806\u4e4b\u65c1\uff0c\u5411\u65c1\u4eba\u4e00\u773c\u4e0d\u77a7\uff0c\u6253\u5f00\u5305\u88f9\uff0c\u539f\u6765\u91cc\u9762\u662f\u4e2a\u4e24\u5c81\u5927\u7684\u5973\u5b69\u3002\u90a3\u5927\u6c49\u6015\u51b7\u574f\u4e86\u5b69\u5b50\uff0c\u62b1\u7740\u5979\u5728\u706b\u8fb9\u70e4\u706b\u3002\u90a3\u5973\u5b69\u6b63\u81ea\u6c89\u6c89\u719f\u7761\uff0c\u5706\u5706\u7684\u773c\u65c1\u5374\u6302\u7740\u4e24\u9897\u6cea\u73e0\u3002 \u3000\u3000\u9a6c\u6625\u82b1\u3001\u5f90\u94ee\u548c\u5546\u5b9d\u9707\u4e09\u4eba\u6276\u7740\u9a6c\u884c\u7a7a\u8d77\u6765\uff0c\u89c1\u7530\u5f52\u519c\u5bf9\u90a3\u9ad8\u7626\u5927\u6c49\u5982\u6b64\u5bb3\u6015\uff0c\u90fd\u662f\u53c8\u60ca\u53c8\u559c\u3002\u9a6c\u6625\u82b1\u9053\uff0c\u201c\u7239\uff0c\u4f60\u4f24\u5904\u8fd8\u597d\u4e48\uff1f\u8fd9\u2026\u2026\u8fd9\u4eba\u662f\u8c01\uff1f\u201d\u9a6c\u884c\u7a7a\u9053\uff1a\u201c\u4ed6\u2026\u2026\u4ed6\u662f\u2026\u2026\u6253\u904d\u5929\u4e0b\u65e0\u654c\u624b\u2026\u2026\u91d1\u2026\u2026\u91d1\u9762\u4f5b\u82d7\u4eba\u51e4\u2026\u2026\u201d\u4e00\u53e5\u8bdd\u521a\u8bf4\u5b8c\uff0c\u5df2\u75db\u5f97\u6655\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u5927\u5385\u4e4b\u4e0a\uff0c\u98de\u9a6c\u9556\u5c40\u7684\u9556\u5934\u548c\u8d9f\u5b50\u624b\u96c6\u5728\u4e1c\u9996\uff0c\u960e\u57fa\u4e0e\u7fa4\u76d7\u96c6\u5728\u897f\u9996\uff0c\u4e09\u540d\u4f8d\u536b\u4e0e\u5546\u5b9d\u9707\u7ad9\u5728\u6905\u5b50\u4e4b\u540e\uff0c\u5404\u4eba\u76ee\u5149\u90fd\u77a7\u7740\u82d7\u4eba\u51e4\u3001\u7530\u5f52\u519c\u4e0e\u7f8e\u5987\u4e09\u4eba\u3002 \u3000\u3000\u82d7\u4eba\u51e4\u51dd\u89c6\u6000\u4e2d\u7684\u5e7c\u5973\uff0c\u8138\u4e0a\u7231\u601c\u6a2a\u6ea2\uff0c\u5145\u6ee1\u7740\u6148\u7231\u548c\u67d4\u60c5\uff0c\u4f17\u4eba\u82e5\u4e0d\u662f\u9002\u624d\u89c1\u5230\u4ed6\u4e00\u624b\u6293\u4f4f\u5927\u8f66\uff0c\u8fde\u5065\u9aa1\u4e5f\u65e0\u6cd5\u62c9\u52a8\u7684\u60ca\u4eba\u795e\u529b\uff0c\u771f\u96be\u76f8\u4fe1\u6b64\u4eba\u8eab\u8d1f\u7edd\u4e16\u6b66\u529f\u3002 \u3000\u3000\u90a3\u7f8e\u5987\u795e\u6001\u81ea\u82e5\uff0c\u5446\u5446\u671b\u7740\u706b\u5806\uff0c\u5634\u89d2\u8fb9\u6302\u7740\u4e00\u4e1d\u51b7\u7b11\uff0c\u53ea\u6709\u6781\u7ec6\u5fc3\u4e4b\u4eba\uff0c\u624d\u77a7\u5f97\u5979\u5634\u5507\u5fae\u5fae\u98a4\u52a8\uff0c\u663e\u5f97\u5fc3\u91cc\u751a\u662f\u4e0d\u5b89\u3002 \u3000\u3000\u7530\u5f52\u519c\u8138\u5982\u767d\u7eb8\uff0c\u770b\u7740\u9662\u5b50\u4e2d\u7684\u5927\u96e8\u3002 \u3000\u3000\u4e09\u4e2a\u4eba\u7684\u76ee\u5149\u77a7\u7740\u4e09\u5904\uff0c\u8c01\u4e5f\u4e0d\u77a7\u8c01\u4e00\u773c\uff0c\u5404\u81ea\u5b89\u5b89\u9759\u9759\u5730\u5750\u7740\uff0c\u4e00\u8a00\u4e0d\u53d1\u3002\u4f46\u4e09\u4eba\u5fc3\u4e2d\uff0c\u5374\u5982\u6ce2\u6d9b\u6c79\u6d8c\uff0c\u6709\u5927\u6b22\u559c\uff0c\u6709\u5927\u54c0\u6101\uff0c\u6709\u5927\u6124\u6012\uff0c\u4e5f\u6709\u5927\u6050\u60e7\u3002 \u3000"}),showOnHomepage:!0},{name:"\u7b2c\u4e8c\u7ae0\u3000\u5b9d\u5200\u548c\u67d4\u60c5",content:(0,a.jsx)(a.Fragment,{children:"\u82d7\u4eba\u51e4\u671b\u7740\u6000\u91cc\u5e7c\u5973\u90a3\u751c\u7f8e\u6587\u79c0\u7684\u5c0f\u8138\uff0c\u8111\u6d77\u4e2d\u51fa\u73b0\u4e86\u4e09\u5e74\u4e4b\u524d\u7684\u5f80\u4e8b\u3002\u8fd9\u4ef6\u4e8b\u5df2\u8fc7\u4e86\u4e09\u5e74\uff0c\u4f46\u5c31\u50cf\u662f\u521a\u8fc7\u4e86\u4e09\u5929\u4e00\u822c\uff0c\u4e00\u5207\u5168\u6e05\u6e05\u695a\u695a\uff0c\u773c\u524d\u4e0b\u7740\u503e\u76c6\u5927\u96e8\uff0c\u4e09\u5e74\u524d\u7684\u90a3\u4e00\u5929\uff0c\u5374\u4e0b\u7684\u662f\u96ea\uff0c\u662f\u6f2b\u5929\u9e45\u6bdb\u4e00\u822c\u7eb7\u7eb7\u6492\u7740\u7684\u5927\u96ea\u3002 \u3000\u3000\u90a3\u662f\u5728\u6cb3\u5317\u6ca7\u5dde\u9053\u4e0a\uff0c\u65f6\u8fd1\u5c81\u665a\uff0c\u9053\u4e0a\u884c\u4eba\u7a00\u5c11\uff0c\u82d7\u4eba\u98ce\u9a91\u7740\u4e00\u5339\u9ad8\u5934\u957f\u817f\u7684\u9ec4\u9a6c\uff0c\u6309\u8f94\u5317\u884c\u3002 \u3000\u3000\u5341\u5e74\u524d\u7684\u814a\u6708\uff0c\u4ed6\u4e0e\u8fbd\u4e1c\u5927\u4fa0\u80e1\u4e00\u5200\u5728\u6ca7\u5dde\u6bd4\u6b66\uff0c\u4ee5\u6bd2\u5200\u8bef\u4f24\u4e86\u80e1\u4e00\u5200\u3002 \u3000\u3000\u80e1\u592b\u4eba\u81ea\u520e\u6b89\u592b\u3002\u4ed6\u4e0e\u80e1\u4e00\u5200\u6b66\u529f\u76f8\u82e5\uff0c\u8c6a\u6c14\u76f8\u60bb\uff0c\u4e24\u4eba\u5316\u654c\u4e3a\u53cb\uff0c\u76f8\u656c\u76f8\u91cd\uff0c\u5c82\u77e5\u4e00\u62db\u4e4b\u5931\uff0c\u7adf\u5c14\u4f24\u4e86\u8fd9\u4f4d\u751f\u5e73\u552f\u4e00\u7684\u77e5\u5df1\u3002\u4ed6\u53f7\u79f0\u201c\u6253\u904d\u5929\u4e0b\u65e0\u654c\u624b\u201d\uff0c\u7eb5\u6a2a\u6d77\u5185\uff0c\u53ea\u6709\u9047\u5230\u4e86\u8fd9\u4f4d\u8fbd\u4e1c\u5927\u4fa0\uff0c\u4e8c\u4eba\u6bd4\u6b66\u4e94\u65e5\uff0c\u8054\u5e8a\u591c\u8bdd\uff0c\u8fd9\u624d\u662f\u9047\u5230\u4e86\u771f\u6b63\u654c\u624b\uff0c\u8fd9\u624d\u662f\u771f\u6b63\u7684\u809d\u80c6\u76f8\u7167\uff0c\u503e\u5fc3\u76f8\u8bb8\u2026\u2026\u82d7\u4eba\u51e4\u4e3a\u4e86\u6b64\u4e8b\uff0c\u5341\u5e74\u6765\u59cb\u7ec8\u803f\u803f\u4e8e\u6000\uff0c\u90c1\u90c1\u5be1\u6b22\u3002 \u3000\u3000\u80e1\u4e00\u5200\u592b\u5987\u901d\u4e16\u5341\u5e74\u4e4b\u671f\u5c06\u5c4a\uff0c\u82d7\u4eba\u98ce\u5343\u91cc\u8fe2\u8fe2\u5730\u4ece\u6d59\u5357\u8d76\u6765\uff0c\u4ed6\u662f\u8981\u5230\u4ea1\u53cb\u5893\u524d\u4eb2\u796d\u3002 \u3000\u3000\u98ce\u96ea\u6b8b\u5e74\uff0c\u9a6c\u4e0a\u9ec4\u660f\uff0c\u82d7\u4eba\u51e4\u6108\u8fd1\u6ca7\u5dde\uff0c\u5fc3\u5934\u6108\u662f\u6c89\u91cd\uff0c\u4ed6\u7eb5\u9a6c\u7f13\u884c\uff0c\u5fc3\u4e2d\u5728\u60f3\uff1a\u201c\u5f53\u5e74\u82e5\u4e0d\u662f\u4e00\u62db\u5931\u624b\uff0c\u4eca\u65e5\u4e0e\u80e1\u6c0f\u592b\u5987\u4e09\u9a91\u6f2b\u6e38\u5929\u4e0b\uff0c\u53eb\u8d2a\u5b98\u6076\u540f\u3001\u571f\u8c6a\u5de8\u5bc7\uff0c\u65e0\u4e0d\u5fc3\u60ca\u80c6\u843d\uff0c\u90a3\u662f\u4f55\u7b49\u7684\u5feb\u4e8b\uff1f\u201d \u3000\u3000\u6b63\u81ea\u51fa\u795e\uff0c\u5ffd\u542c\u8eab\u540e\u8f66\u8f6e\u538b\u96ea\uff0c\u4e00\u4e2a\u8f66\u592b\u5377\u7740\u820c\u5934\u201c\u5f97\u513f\u4e00\u4e00\u201d\u58f0\u54cd\uff0c\u50ac\u8d76\u9aa1\u5b50\uff0c\u51fb\u97ad\u5288\u62cd\u4f5c\u58f0\uff0c\u4e00\u8f86\u5927\u8f66\u4ece\u767d\u832b\u832b\u7684\u96ea\u539f\u4e0a\u75be\u884c\u800c\u6765\u3002\u62c9\u8f66\u7684\u5065\u9aa1\u53e3\u55b7\u767d\u6c14\uff0c\u51b2\u98ce\u5192\u96ea\uff0c\u653e\u8e44\u6025\u5954\u3002 \u3000\u3000\u5927\u8f66\u4ece\u82d7\u4eba\u51e4\u8eab\u65c1\u63a0\u8fc7\uff0c\u5ffd\u542c\u8f66\u4e2d\u4e00\u4e2a\u5a07\u67d4\u7684\u5973\u5b50\u58f0\u97f3\u9001\u5e7f\u51fa\u6765\uff1a\u201c\u7239\uff0c\u5230\u4e86\u4eac\u91cc\uff0c\u4f60\u5c31\u966a\u6211\u53bb\u4e70\u5bab\u82b1\u513f\u6234\u2026\u2026\u201d\u4e0b\u9762\u7684\u8bdd\u513f\u5374\u542c\u4e0d\u89c1\u4e86\uff0c\u8fd9\u662f\u6c5f\u5357\u59d1\u5a18\u6781\u67d4\u6781\u6e05\u7684\u8bed\u58f0\uff0c\u5728\u8fd9\u5317\u65b9\u83bd\u83bd\u5e73\u539f\u7684\u98ce\u96ea\u4e4b\u4e2d\uff0c\u5374\u662f\u6781\u4e0d\u76f8\u886c\u3002 \u3000\u3000\u7a81\u7136\u4e4b\u95f4\uff0c\u9aa1\u5b50\u5de6\u8db3\u8e0f\u8fdb\u4e86\u4e00\u4e2a\u7a7a\u6d1e\uff0c\u767b\u65f6\u5411\u524d\u4e00\u8e76\u3002\u90a3\u8f66\u592b\u8eab\u5b50\u524d\u503e\uff0c\u968f\u4e0b\u4e00\u63d0\uff0c\u9aa1\u5b50\u501f\u529b\u63d0\u8db3\uff0c\u7ee7\u7eed\u524d\u5954\u3002 \u3000\u3000\u82d7\u4eba\u51e4\u6697\u6697\u8be7\u5f02\uff1a\u201c\u90a3\u8f66\u592b\u8fd9\u4e00\u503e\u4e00\u63d0\uff0c\u597d\u4fca\u7684\u8eab\u624b\uff0c\u597d\u5f3a\u7684\u8182\u529b\uff0c\u884c\u6765\u662f\u4f4d\u98ce\u5c18\u5947\u58eb\uff0c\u600e\u4e48\u53bb\u505a\u4e86\u8d76\u5927\u8f66\u7684\uff1f\u201d \u3000\u3000\u601d\u5ff5\u672a\u5b9a\uff0c\u53ea\u542c\u5f97\u811a\u6b65\u58f0\u54cd\uff0c\u540e\u9762\u4e00\u4e2a\u811a\u592b\u6311\u4e86\u4e00\u62c5\u884c\u674e\uff0c\u8fc8\u5f00\u5927\u6b65\u8d76\u4e86\u4e0a\u6765\u3002\u8fd9\u62c5\u884c\u674e\u538b\u5f97\u4e00\u6839\u67a3\u6728\u6241\u62c5\u76f4\u5f2f\u4e0b\u53bb\uff0c\u663e\u5f97\u9887\u4e3a\u6c89\u91cd\uff0c\u4f46\u90a3\u811a\u592b\u884c\u82e5\u65e0\u4e8b\uff0c\u5728\u96ea\u5730\u91cc\u5feb\u6b65\u800c\u884c\uff0c\u843d\u811a\u751a\u8f7b\u3002 \u3000\u3000\u82d7\u4eba\u51e4\u66f4\u662f\u5947\u602a\uff1a\u201d\u8fd9\u811a\u592b\u975e\u4f46\u529b\u5927\uff0c\u800c\u4e14\u8f7b\u529f\u66f4\u662f\u4e86\u5f97\u3002\u201d\u4ed6\u77e5\u9053\u5176\u4e2d\u5fc5\u6709\u8e22\u8df7\uff1a\u201d\u8fd9\u811a\u592b\u4f3c\u5728\u8ffd\u8e2a\u90a3\u8f66\u592b\uff0c\u770b\u6765\u6709\u4ec0\u4e48\u51f6\u6740\u5bfb\u4ec7\u4e4b\u4e8b\u3002\u201d\u5f53\u4e0b\u63d0\u7740\u9a6c\u97ad\uff0c\u4e0d\u75be\u4e0d\u5f90\u7684\u9065\u9065\u5730\u8ddf\u5728\u5927\u8f66\u4e4b\u540e\uff0c\u8981\u4f8d\u9752\u4e2a\u7a76\u7adf\u3002 \u3000\u3000\u884c\u51fa\u6570\u91cc\uff0c\u89c1\u90a3\u811a\u592b\u867d\u7136\u80a9\u4e0a\u538b\u7740\u6c89\u91cd\u884c\u674e\uff0c\u4ecd\u662f\u5954\u8dd1\u5982\u98de\uff0c\u5ffd\u542c\u8eab\u540e\u94dc\u7247\u513f\u53ee\u53ee\u5f53\u5f53\u54cd\u4eae\uff0c\u4e00\u6761\u6c49\u5b50\u6311\u7740\u4e00\u526f\u8865\u9505\u7684\u62c5\u513f\uff0c\u865a\u98d8\u98d8\u5730\u8d76\u6765\u3002\u8fd9\u4eba\u5728\u96ea\u4e2d\u884c\u8d70\uff0c\u843d\u6b65\u66f4\u8f7b\uff0c\u867d\u7136\u8bf4\u4e0d\u4e0a\u8e0f\u96ea\u65e0\u75d5\uff0c\u4f46\u8f7b\u529f\u4e4b\u4f73\uff0c\u6b66\u6797\u4e2d\u751a\u662f\u7f55\u89c1\u3002\u82d7\u4eba\u51e4\u5bfb\u601d\uff1a\u201c\u53c8\u591a\u4e86\u4e00\u4e2a\u3002\u8fd9\u4eba\u662f\u54ea\u4e00\u6d3e\u7684\uff1f\u201d\u4f46\u89c1\u4ed6\u6597\u7b20\u548c\u84d1\u8863\u4e0a\u7f69\u6ee1\u4e86\u767d\u96ea\uff0c\u5728\u98ce\u4e2d\u4e00\u6643\u4e00\u98d8\uff0c\u8d70\u5f97\u6b6a\u6b6a\u659c\u659c\uff0c\u767b\u65f6\u7701\u8d77\uff1a\u201d\u8fd9\u8eab\u5948\u4f55\u529f\u662f\u9102\u5317\u9b3c\u89c1\u6101\u9524\u5bb6\u7684\u529f\u592b\u3002\u201d \u3000\u3000\u884c\u4e86\u4e03\u516b\u91cc\u8def\uff0c\u5929\u8272\u9ed1\u5c06\u4e0b\u6765\uff0c\u6765\u5230\u4e00\u4e2a\u5c0f\u5c0f\u5e02\u96c6\u3002\u82d7\u4eba\u51e4\u89c1\u5927\u8f66\u505c\u5728\u4e00\u5bb6\u5ba2\u5e97\u524d\u9762\uff0c\u4e8e\u662f\u8fdb\u5e97\u501f\u5bbf\u3002\u5ba2\u5e97\u751a\u5c0f\uff0c\u96c6\u4e0a\u5c31\u6b64\u4e00\u5bb6\u3002\u4f17\u5ba2\u5546\u90fd\u6324\u5728\u5385\u4e0a\u70e4\u706b\u559d\u767d\u5e72\uff0c\u8f66\u592b\u3001\u811a\u592b\u3001\u8865\u9505\u5320\u90fd\u5728\u5176\u5185\u3002 \u3000\u3000\u82d7\u4eba\u51e4\u867d\u7136\u540d\u6ee1\u5929\u4e0b\uff0c\u4f46\u8fd1\u5341\u5e74\u6765\u9690\u5c45\u6d59\u5357\uff0c\u6b66\u6797\u4e2d\u8bc6\u5f97\u4ed6\u7684\u4eba\u4e0d\u591a\u3002 \u3000\u3000\u90a3\u811a\u592b\u3001\u8f66\u592b\u548c\u8865\u9505\u5320\u4ed6\u90fd\u4e0d\u76f8\u8bc6\uff0c\u5f53\u4e0b\u9ed8\u7136\u5750\u5728\u4e00\u5f20\u5c0f\u684c\u4e4b\u65c1\uff0c\u8981\u4e86\u9152\u996d\uff0c\u89c1\u90a3\u4e09\u4eba\u5206\u522b\u559d\u9152\u7528\u996d\uff0c\u77a7\u6765\u5e76\u975e\u4e00\u8def\u3002 \u3000\u3000\u5ffd\u542c\u5185\u9662\u4e00\u4e2a\u4eba\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u5357\u5927\u4eba\u3001\u5c0f\u59d0\uff0c\u5c0f\u5730\u65b9\u59d4\u5c48\u70b9\u513f\uff0c\u53ea\u597d\u5728\u5916\u8fb9\u5385\u4e0a\u7528\u996d\u3002\u201d\u68c9\u5e18\u6380\u5f00\uff0c\u5e97\u4f34\u5f15\u7740\u4e00\u4f4d\u5b98\u5458\u3001\u4e00\u4f4d\u5c0f\u59d0\u6765\u5230\u5385\u4e0a\u3002\u672c\u6765\u5750\u7740\u7684\u4f17\u5ba2\u5546\u89c1\u5230\u5b98\u5458\uff0c\u7eb7\u7eb7\u8d77\u7acb\u3002\u82d7\u4eba\u51e4\u5e76\u4e0d\u7406\u4f1a\uff0c\u81ea\u7ba1\u559d\u9152\u3002\u53ea\u89c1\u90a3\u5b98\u5458\u7a7f\u7740\u9171\u8272\u7f0e\u9762\u72d0\u76ae\u888d\u5b50\uff0c\u767d\u767d\u80d6\u80d6\uff0c\u4e00\u526f\u798f\u76f8\uff0c\u90a3\u5c0f\u59d0\u76f8\u8c8c\u5a07\u7f8e\uff0c\u80a4\u8272\u767d\u817b\uff0c\u522b\u8bf4\u5317\u5730\u7f55\u6709\u5982\u6b64\u4f73\u4e3d\uff0c\u5373\u4ee4\u6c5f\u5357\u4e5f\u6781\u4e3a\u5c11\u6709\u3002\u5979\u8eab\u7a7f\u4e00\u4ef6\u8471\u7eff\u7ec7\u9526\u7684\u76ae\u8884\uff0c\u989c\u8272\u751a\u662f\u9c9c\u8273\uff0c\u4f46\u5728\u5979\u5bb9\u5149\u6620\u7167\u4e4b\u4e0b\uff0c\u518d\u707f\u70c2\u7684\u9526\u7f0e\u4e5f\u5df2\u663e\u5f97\u9eef\u7136\u65e0\u8272\u3002 \u3000\u3000\u4f17\u4eba\u773c\u524d\u4e00\u4eae\uff0c\u4e0d\u7531\u5f97\u90fd\u6709\u81ea\u60ed\u5f62\u79fd\u4e4b\u611f\uff0c\u6709\u7684\u8baa\u8baa\u5730\u7adf\u81ea\u9000\u5230\u4e86\u5eca\u4e0b\uff0c\u5385\u4e0a\u767b\u65f6\u7a7a\u51fa\u4e00\u5927\u7247\u5730\u65b9\u6765\u3002 \u3000\u3000\u90a3\u5e97\u4f34\u4e00\u53e0\u8fde\u58f0\u7684\u201c\u5927\u4eba\u3001\u5c0f\u59d0\u201d\uff0c\u9001\u996d\u9001\u9152\uff0c\u6781\u662f\u6bb7\u52e4\uff0c\u82d7\u4eba\u51e4\u542c\u4ed6\u53eb\u558a\u9152\u83dc\u4e4b\u65f6\uff0c\u4e2d\u6c14\u5145\u6c9b\uff0c\u4e0d\u89c9\u7559\u795e\uff0c\u4e00\u77a7\u4ed6\u8eab\u5f62\u6b65\u6cd5\uff0c\u5374\u4e0d\u662f\u4f1a\u5bb6\u5b50\u662f\u4ec0\u4e48\uff1f\u53c8\u89c1\u4ed6\u4e24\u8fb9\u592a\u9633\u7a74\u5fae\u5fae\u51f8\u51fa\uff0c\u7adf\u7136\u5185\u529f\u6709\u9887\u6df1\u9020\u8be3\uff0c\u4e0d\u7531\u5f97\u66f4\u662f\u5947\u602a\uff0c\u5fc3\u9053\uff1a\u201d\u8fd9\u6279\u4eba\u5fc5\u6709\u91cd\u5927\u56fe\u8c0b\uff0c\u5de6\u53f3\u95f2\u7740\uff0c\u5c31\u77a7\u77a7\u70ed\u95f9\uff0c\u4e14\u770b\u4ed6\u4eec\u5e72\u7684\u662f\u597d\u4e8b\u8fd8\u662f\u6b79\u4e8b\u3002\u4e0d\u77e5\u8ddf\u8fd9\u5b98\u513f\u6709\u5e72\u7cfb\u6ca1\u6709\uff1f\u201d \u3000\u3000\u8fd9\u4e00\u7559\u795e\uff0c\u4e0d\u514d\u5411\u90a3\u5b98\u513f\u4e0e\u5c0f\u59d0\u591a\u770b\u4e86\u51e0\u773c\u3002\u90a3\u5b98\u513f\u5ffd\u5730\u4e00\u62cd\u684c\u5b50\uff0c\u53d1\u4f5c\u8d77\u6765\uff0c\u6307\u7740\u82d7\u4eba\u51e4\u9a82\u9053\uff1a\u201c\u4f60\u662f\u4ec0\u4e48\u4e1c\u897f\uff1f\u89c1\u4e86\u5b98\u5e9c\u4e0d\u56de\u907f\u4e5f\u5c31\u7f62\u4e86\uff0c\u8d3c\u773c\u8fd8\u9aa8\u6e9c\u6e9c\u5730\u77a7\u4e2a\u4e0d\u4f53\u3002\u6211\u770b\u4f60\u7c97\u624b\u5927\u811a\u3002\u751f\u6210\u4e00\u526f\u8d3c\u76f8\uff0c\u518d\u77a7\u4e00\u773c\uff0c\u62ff\u7247\u5b50\u9001\u5230\u53bf\u91cc\u53bb\u6253\u4f60\u4e2a\u76ae\u5f00\u8089\u7efd\u3002\u201d\u82d7\u4eba\u51e4\u4f4e\u5934\u559d\u9152\uff0c\u5e76\u4e0d\u7406\u4f1a\u3002\u90a3\u5b98\u513f\u66f4\u52a0\u6012\u4e86\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u8bf7\u5b89\u966a\u793c\u4e5f\u4e0d\u4f1a\u4e48\uff1f\u8fd9\u4e48\u5927\u523a\u523a\u5730\u5750\u7740\u3002\u201d \u3000\u3000\u90a3\u5c0f\u59d0\u67d4\u58f0\u529d\u9053\uff1a\u201c\u7239\uff0c\u4f60\u72af\u5f97\u7740\u751f\u8fd9\u4e48\u5927\u6c14\uff1f\u4e61\u4e0b\u4eba\u4e0d\u61c2\u89c4\u77e9\uff0c\u4e5f\u662f\u6709\u7684\u3002\u4f55\u5fc5\u8ddf\u8fd9\u4e9b\u7c97\u4eba\u4e00\u822c\u89c1\u8bc6\uff1f\u54ea\uff0c\u559d\u4e86\u8fd9\u676f\u5427\u3002\u201d\u8bf4\u7740\u5c06\u4e00\u676f\u9152\u9012\u5230\u4ed6\u7684\u5634\u8fb9\u3002\u90a3\u5b98\u513f\u9aa8\u561f\u4e00\u53e3\u559d\u5e72\uff0c\u4f3c\u4e4e\u5c06\u6012\u6c14\u548c\u9152\u541e\u670d\u4e86\uff0c\u6a2a\u4e86\u82d7\u4eba\u51e4\u4e00\u773c\uff0c\u89c1\u4ed6\u4f4e\u5934\u4e0d\u8bed\uff0c\u60f3\u662f\u6015\u4e86\uff0c\u4e8e\u662f\u81ea\u659f\u81ea\u996e\u5730\u8ddf\u5973\u513f\u8bf4\u7b11\u8d77\u6765\u3002\u8bdd\u4e2d\u8bf4\u7684\u90fd\u662f\u5230\u4e86\u5317\u4eac\u4e4b\u540e\uff0c\u8865\u4e0a\u4e86\u5b98\u4fbf\u600e\u6837\u600e\u6837\uff0c\u77a7\u795e\u60c5\u662f\u4e00\u540d\u8d74\u4eac\u8c0b\u5e72\u5dee\u4f7f\u7684\u5019\u8865\u5b98\u513f\u3002 \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u5927\u95e8\u63a8\u5f00\uff0c\u98d8\u8fdb\u4e00\u7247\u98ce\u96ea\uff0c\u8ddf\u7740\u8d70\u8fdb\u4e00\u4f4d\u5b98\u5458\u6765\u3002\u8fd9\u4eba\u9ec4\u76ae\u7cbe\u7626\uff0c\u8fdc\u6ca1\u5148\u524d\u90a3\u5b98\u513f\u7684\u6c14\u6d3e\u5341\u8db3\u3002\u4ed6\u5927\u58f0\u7b11\u9053\uff1a\u201c\u4eba\u751f\u4f55\u5904\u4e0d\u76f8\u9022\u3001\u53c8\u4e0e\u4ec1\u901a\u5144\u5728\u8fd9\u91cc\u649e\u89c1\uff0c\u771f\u662f\u5de7\u4e4b\u6781\u77e3\uff01\u201d\u8bf4\u7740\u62a2\u4e0a\u6765\u4e0e\u90a3\u59d3\u5357\u7684\u5b98\u513f\u5357\u4ec1\u901a\u884c\u793c\u53ae\u89c1\u3002 \u3000\u3000\u5357\u6c0f\u7236\u5973\u4e00\u9f50\u7ad9\u8d77\uff0c\u5357\u4ec1\u901a\u62f1\u624b\u9053\uff1a\u201c\u8c03\u4faf\u5144\uff0c\u5e78\u4f1a\u5e78\u4f1a\uff01\u4e00\u8d77\u5750\u7f62\u3002\u201d \u3000\u3000\u90a3\u201c\u8c03\u4faf\u5144\u201d\u8c22\u4e86\uff0c\u5750\u5728\u684c\u8fb9\u3002\u5e97\u4f34\u6dfb\u4e0a\u676f\u7b77\uff0c\u4f20\u9152\u547c\u83dc\u3002 \u3000\u3000\u82d7\u4eba\u51e4\u5fc3\u9053\uff1a\u201c\u8fde\u8fd9\u4e2a\u8c03\u4faf\u5144\uff0c\u4e00\u5171\u662f\u4e94\u4e2a\u9ad8\u624b\u4e86\u3002\u8fd9\u59d3\u5357\u7684\u7236\u5973\u770b\u4e0d\u51fa\u6709\u4ec0\u4e48\u6b66\u529f\u3002\u4f1a\u4e0d\u4f1a\u5927\u667a\u82e5\u611a\uff0c\u7adf\u8ba9\u6211\u8d70\u4e86\u773c\u5462\uff1f\u201d\u60f3\u5230\u6b64\u5904\uff0c\u4e0d\u7981\u6697\u81ea\u8b66\u6212\uff0c\u4e0d\u6562\u5411\u4ed6\u4eec\u591a\u77a7\u4e00\u773c\u3002\u8981\u77e5\u4ed6\u90a3\u201c\u6253\u904d\u5929\u4e0b\u65e0\u654c\u624b\u201d\u7684\u5916\u53f7\uff0c\u5b9e\u662f\u72af\u4e86\u6b66\u6797\u5927\u5fcc\uff0c\u5929\u4e0b\u82f1\u96c4\u597d\u6c49\uff0c\u54ea\u4e00\u4e2a\u4e0d\u60f3\u5c06\u8fd9\u5934\u8854\u6458\u4e86\u4e0b\u6765\u3002\u4ed6\u4e00\u751f\u6240\u5386\u98ce\u9669\u591a\u8fc7\u5e38\u4eba\u767e\u500d\uff0c\u7686\u62dc\u8fd9\u5916\u53f7\u4e4b\u6240\u8d50\u3002\u6b64\u523b\u5fc3\u60f3\uff1a\u201c\u8fd9\u51e0\u4eba\u8bf4\u4e0d\u5b9a\u662f\u51b2\u7740\u6211\u800c\u6765\u3002\u4ed6\u4eec\u6210\u7fa4\u7ed3\u515a\uff0c\u4e00\u9f50\u4e0a\u6765\u5012\u662f\u96be\u6597\u3002\u4e0d\u77e5\u524d\u9762\u662f\u5426\u66f4\u6709\u9ad8\u624b\u57cb\u4f0f\uff1f\u201d \u3000\u3000\u53ea\u542c\u90a3\u201c\u8c03\u4faf\u5144\u201d\u4e0e\u5357\u4ec1\u901a\u9ad8\u8c08\u9614\u8bba\uff0c\u8bf4\u7684\u90fd\u662f\u4e9b\u5b98\u573a\u4e2d\u5347\u8fc1\u964d\u6ef4\u7684\u8f6f\u95fb\u3002\u5eca\u4e0b\u90a3\u811a\u592b\u548c\u8865\u9505\u5320\u5374\u5927\u58f0\u5435\u56b7\u8d77\u6765\uff0c\u4e24\u4eba\u4e89\u7684\u662f\u4e16\u4e0a\u6709\u6ca1\u6709\u5f53\u771f\u524a\u94c1\u5982\u6ce5\u7684\u5b9d\u5251\u5b9d\u5200\u3002\u90a3\u811a\u592b\u9053\uff1a\u201c\u4ec0\u4e48\u524a\u94c1\u5982\u6ce5\uff0c\u90fd\u662f\u5439\u5927\u6c14\uff01\u90a3\u5b9d\u5200\u4e5f\u4e0d\u8fc7\u950b\u5229\u70b9\u513f\uff0c\u5f53\u771f\u5c31\u8fd9\u4e48\u795e\uff1f\u201d\u8865\u9505\u5320\u9053\uff1a\u201c\u4f60\u89c1\u8fc7\u591a\u5c11\u4e16\u9762\u4e86\uff1f\u77e5\u9053\u4ec0\u4e48\uff1f \u3000\u3000\u5b9d\u5200\u5c31\u662f\u5b9d\u5200\uff0c\u82e5\u4e0d\u662f\u6015\u5413\u574f\u4e86\u4f60\uff0c\u6211\u5c31\u62ff\u4e00\u53e3\u8ba9\u4f60\u5f00\u5f00\u773c\u754c\u3002\u201d\u811a\u592b\u56b7\u9053\uff1a\u201c\u4f60\u6709\u5b9d\u5200\uff1f\u5478\uff0c\u522b\u53d1\u4f60\u7684\u6e05\u79cb\u5927\u68a6\u5427\uff01\u6709\u5b9d\u5200\u4e5f\u4e0d\u8865\u9505\u513f\u5566\uff01\u53ea\u6015\u78e8\u4e0d\u5229\u7684\u949d\u67f4\u5200\u3001\u9508\u83dc\u5200\uff0c\u5012\u6709\u201d\u8fd9\u4e48\u4e00\u628a\u4e24\u628a\uff01\u201d\u4f17\u4eba\u542c\u7740\u90fd\u5927\u7b11\u8d77\u6765\u3002 \u3000\u3000\u8865\u9505\u5320\u6c14\u9f13\u9f13\u5730\u4ece\u62c5\u513f\u91cc\u53d6\u51fa\u4e00\u628a\u5200\u6765\uff0c\u7eff\u76ae\u9798\u5b50\u91d1\u541e\u53e3\uff0c\u6a21\u6837\u751a\u662f\u4e0d\u51e1\u3002\u4ed6\u5237\u5730\u62d4\u5200\u51fa\u9798\uff0c\u5bd2\u5149\u903c\u4eba\uff0c\u679c\u7136\u662f\u597d\u4e00\u53e3\u5229\u5203\u3002\u4f17\u4eba\u90fd\u8d5e\u4e86\u4e00\u58f0\uff1a\u201c\u597d\u5200\uff01\u201d\u8865\u9505\u5320\u62ff\u8d77\u5200\u6765\uff0c\u4e00\u5200\u4f5c\u52bf\u5411\u811a\u592b\u780d\u53bb\u3002\u811a\u592b\u62b1\u5934\u5927\u53eb\uff1a\u201c\u6211\u7684\u5988\u5440\uff01\u201d\u6025\u5fd9\u907f\u5f00\uff0c\u4f17\u4eba\u53c8\u662f\u4e00\u9635\u8f70\u7b11\u3002 \u3000\u3000\u82d7\u4eba\u98ce\u77a7\u4e86\u4e8c\u4eba\u4f38\u60c5\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u4e24\u4eba\u679c\u662f\u4e00\u8def\u3002\u8fd9\u4e48\u4e32\u620f\uff0c\u5374\u4e0d\u662f\u6f14\u7ed9\u6211\u770b\u7684\u4e86\u3002\u201d \u3000\u3000\u8865\u9505\u5320\u9053\uff1a\u201c\u6709\u4e0a\u597d\u83dc\u5200\u67f4\u5200\uff0c\u8bf7\u501f\u4e00\u628a\u3002\u201d\u90a3\u5e97\u4f34\u5e94\u58f0\u5165\u53a8\uff0c\u53d6\u4e86\u4e00\u628a\u83dc\u5200\u51fa\u6765\u3002\u8865\u9505\u5320\u9053\uff1a\u201c\u4f60\u62ff\u7a33\u4e86\uff01\u201d\u90a3\u5e97\u4f34\u5c06\u83dc\u5200\u9ad8\u9ad8\u4e3e\u8d77\u3002\u8865\u9505\u5320\u6a2a\u5200\u6325\u53bb\uff0c\u5f53\u7684\u4e00\u58f0\uff0c\u83dc\u5200\u65ad\u4e3a\u4e24\u622a\u3002 \u3000\u3000\u4f17\u4eba\u9f50\u58f0\u559d\u91c7\uff1a\u201c\u679c\u662f\u5b9d\u5200\uff01\u201d \u3000\u3000\u8865\u9505\u5320\u5f97\u610f\u6d0b\u6d0b\uff0c\u5927\u58f0\u5439\u5618\uff0c\u8bf4\u4ed6\u8fd9\u67c4\u5200\u5982\u4f55\u5389\u5bb3\uff0c\u5982\u4f55\u540d\u8d35\u3002\u5eca\u4e0b\u4f17\u4eba\u8138\u73b0\u4ef0\u6155\u4e4b\u8272\uff0c\u6d25\u6d25\u6709\u5473\u5730\u542c\u7740\u3002\u5357\u4ec1\u901a\u542c\u4ed6\u8bf4\u4e86\u4e00\u4f1a\uff0c\u5fcd\u4e0d\u4f4f\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u8138\u73b0\u4e0d\u5c51\u4e4b\u8272\u3002 \u3000\u3000\u90a3\u201c\u8c03\u4faf\u5144\u201d\u9053\uff1a\u201c\u4ec1\u901a\u5144\uff0c\u8fd9\u67c4\u5200\u786e\u4e5f\u79f0\u5f97\u4e0a\u4e2a\u2018\u5b9d\u2019\u5b57\u4e86\uff0c\u60f3\u4e0d\u5230\u8d29\u592b\u8d70\u5352\u4e4b\u5f92\uff0c\u5c45\u7136\u8eab\u6000\u8fd9\u7b49\u5229\u5668\u3002\u201d\u5357\u4ec1\u901a\u9053\uff1a\u201c\u5229\u5219\u5229\u77e3\uff0c\u5b9d\u5219\u672a\u5fc5\u3002\u201d \u3000\u3000\u201c\u8c03\u4faf\u5144\u201d\u9053\uff1a\u201c\u6211\u5144\u6b64\u8a00\u5dee\u77e3\uff01\u4f60\u77a7\u6b64\u5200\u524a\u94c1\u5982\u6ce5\uff0c\u4e16\u4e0a\u54ea\u91cc\u66f4\u6709\u80dc\u4e8e\u6b64\u5200\u7684\u5462\uff1f\u201d\u5357\u4ec1\u901a\u9053\uff1a\u201c\u543e\u5144\u672a\u514d\u5c11\u89c1\u591a\u602a\uff0c\u5144\u5f1f\u5c31\u2026\u201d\u8fd8\u4f8d\u518d\u8bf4\u4e0b\u53bb\uff0c\u5357\u5c0f\u59d0\u5ffd\u7136\u63d2\u53e3\u9053\uff1a\u201c\u7239\uff0c\u4f60\u559d\u5f97\u591a\u5566\uff0c\u5feb\u5403\u4e86\u996d\u53bb\u7761\u5427\u3002\u201d \u3000\u3000\u5357\u4ec1\u901a\u7b11\u9053\uff1a\u201c\u563f\uff0c\u5973\u5b69\u513f\u5c31\u7231\u7ba1\u4f60\u7239\u7239\u3002\u201d\u8bf4\u7740\u5374\u771f\u7684\u8981\u996d\u5403\uff0c\u4e0d\u518d\u559d\u9152\u3002\u90a3\u201c\u8c03\u4faf\u5144\u201d\u53c8\u9053\uff1a\u201c\u5144\u5f1f\u4eca\u65e5\u603b\u7b97\u5f00\u4e86\u773c\u754c\uff0c\u8fd9\u7b49\u5b9d\u5200\uff0c\u543e\u5144\u60f3\u6765\u4e5f\u662f\u751f\u5e73\u7b2c\u4e00\u6b21\u89c1\u5230\u3002\u201d\u5357\u4ec1\u901a\u51b7\u7b11\u9053\uff1a\u201c\u80dc\u4e8e\u6b64\u5200\u5341\u500d\u7684\uff0c\u5144\u5f1f\u4e5f\u5e38\u5e38\u89c1\u5230\u3002\u201d\u201c\u8c03\u4faf\u5144\u201d\u54c8\u54c8\u5927\u7b11\uff0c\u9053\uff1a\u201c\u53d6\u7b11\u53d6\u7b11\uff01\u543e\u5144\u662f\u4f4d\u6587\u5b98\uff0c\u53c8\u89c1\u8fc7\u4ec0\u4e48\u5b9d\u5200\u6765\uff1f\u201d \u3000\u3000\u8865\u9505\u5320\u542c\u5230\u4e86\u4e8c\u4eba\u5bf9\u7b54\uff0c\u5927\u58f0\u9053\uff1a\u201c\u4e16\u4e0a\u82e5\u6709\u66f4\u80dc\u5f97\u6b64\u5200\u7684\u5b9d\u5200\uff0c\u6211\u5b81\u613f\u628a\u5934\u5272\u4e0b\u6765\u9001\u4ed6\u3002\u5439\u5927\u6c14\u53c8\u8c01\u4e0d\u4f1a\u5566\uff1f\u563f\uff0c\u6211\u8bf4\u6211\u3000\u513f\u5b50\u4e5f\u505a\u4e2a\u4e94\u54c1\u5b98\u5462\uff0c\u4f60\u4eec\u4fe1\u4e0d\u4fe1\u5566\uff0c\uff1f\u201d\u4f17\u4eba\u5fd9\u559d\uff1a\u201c\u80e1\u8bf4\uff0c\u5feb\u95ed\u84b2\uff01\u201d \u3000\u3000\u5357\u4ec1\u901a\u6c14\u5f97\u8138\u4e5f\u767d\u4e86\uff0c\u970d\u5730\u7ad9\u8d77\uff0c\u5927\u8e0f\u6b65\u8d70\u5411\u623f\u4e2d\u3002\u5357\u5c0f\u59d0\u8fde\u53eb\uff1a\u201c\u7239\u7239\uff01\u201d\u4ed6\u54ea\u91cc\u7406\u4f1a\uff0c\u7247\u523b\u95f4\u6367\u4e86\u4e00\u67c4\u4e09\u5c3a\u6765\u957f\u7684\u5f2f\u5200\u51fa\u6765\uff0c\u4f46\u89c1\u5200\u9798\u4e4c\u6c89\u6c89\u7684\uff0c\u4e5f\u65e0\u5f02\u5904\uff0c\u4ed6\u5927\u58f0\u9053\uff1a\u201d\u5582\uff0c\u8865\u9505\u513f\u7684\uff0c\u6211\u8fd9\u91cc\u6709\u628a\u5200\uff0c\u8ddf\u4f60\u7684\u6bd4\u4e00\u4e0b\uff0c\u4f60\u8f93\u4e86\u53ef\u5f97\u5272\u8111\u888b\u3002\u201d\u8865\u9505\u5320\u9053\uff1a\u201d\u82e5\u662f\u8001\u7237\u8f93\u4e86\u5462\uff0c\u201d\u5357\u4ec1\u901a\u6c14\u9053\uff1a\u201c\u6211\u4e5f\u628a\u8111\u888b\u5272\u4e0e\u4f60\uff0c\u201d\u5357\u5c0f\u59d0\u9053\uff1a\u201c\u7239\uff0c\u4f60\u559d\u591a\u5566\uff0c\u8ddf\u4ed6\u4eec\u6709\u4ec0\u4e48\u8bf4\u7684\uff1f\u56de\u623f\u53bb\u5427\uff01\u201d\u5357\u4ec1\u901a\u82e5\u6709\u6240\u609f\uff0c\u54fc\u4e86\u4e00\u58f0\uff0c\u6367\u7740\u5200\u8f6c\u8eab\u56de\u623f\u3002 \u3000\u3000\u8865\u9505\u5320\u89c1\u4ed6\u610f\u6b32\u8fdb\u623f\uff0c\u53c8\u6fc0\u4e00\u53e5\uff1a\u201c\u82e5\u662f\u8001\u7237\u8f93\u4e86\uff0c\u5c0f\u4eba\u600e\u6562\u8981\u8001\u7237\u7684\u8111\u888b\uff1f\u4e0d\u5982\u8001\u7237\u62db\u5c0f\u4eba\u505a\u5973\u5a7f\u5427\uff01\u201d\u4f17\u4eba\u6709\u7684\u54d7\u7b11\uff0c\u8ba2\u7684\u65a5\u4ed6\u80e1\u8bf4\u3002\u5357\u5c0f\u59d0\u6c14\u5f97\u6ee1\u8138\u901a\u7ea2\uff0c\u4e0d\u518d\u76f8\u529d\uff0c\u8d4c\u6c14\u56de\u623f\u53bb\u4e86\u5357\u4ec1\u901a\u7f13\u7f13\u62bd\u5200\u51fa\u9798\uff0c\u5203\u53e3\u53ea\u9732\u51fa\u534a\u5c3a\uff0c\u5df2\u89c1\u51b7\u68ee\u68ee\u7684\u4e00\u9053\u9752\u5149\u6fc0\u5c04\u800c\u51fa\uff0c\u5f85\u90a3\u5200\u5203\u62d4\u51fa\u9798\u6765\uff0c\u5bd2\u5149\u95ea\u70c1\u4e0d\u5b9a\uff0c\u8000\u5f97\u4f17\u4eba\u773c\u4e5f\u82b1\u4e86\u3002\u5357\u4ec1\u901a\u9053\uff1a\u201c\u6211\u8fd9\u53e3\u5200\uff0c\u6709\u4e2a\u540d\u76ee\uff0c\u53eb\u4f5c\u2018\u51b7\u6708\u5b9d\u5200\u2019\uff0c\u4f60\u77a7\u6e05\u695a\u4e86\u201d \u3000\u3000\u8865\u9505\u5320\u51d1\u8fd1\u4e00\u884c\uff0c\u89c1\u5200\u67c4\u4e0a\u7528\u91d1\u4e1d\u94f6\u4e1d\u9576\u7740\u4e00\u94a9\u7709\u6bdb\u6708\u4e4b\u5f62\uff0c\u8bf4\u9053\uff1a\u201c\u8001\u7237\u7684\u5200\u597d\uff0c\u90a3\u4e0d\u7528\u6bd4\u4e86\u3002\u201d \u3000\u3000\u82d7\u4eba\u51e4\u89c1\u4f17\u4eba\u8a00\u8bed\u76f8\u6fc0\uff0c\u5357\u4ec1\u901a\u53d6\u51fa\u5b9d\u5200\u5fc3\u4e0b\u5df2\u81ea\u4e86\u7136\uff0c\u539f\u6765\u8fd9\u51e0\u4eba\u5747\u662f\u4e3a\u8fd9\u53e3\u5b9d\u5200\u800c\u6765\uff0c\u5b66\u6b66\u4e4b\u58eb\u628a\u5b9d\u5251\u5229\u5203\u5f97\u6709\u5982\u6027\u547d\u4e00\u822c\uff0c\u8eab\u6000\u5229\u5668\uff0c\u7b49\u4e8e\u6b66\u529f\u589e\u5f3a\u6570\u500d\u3002\u4ed6\u6709\u5982\u6b64\u4e00\u67c4\u5b9d\u5200\uff0c\u65e0\u602a\u4f17\u4eba\u773c\u7ea2\u3002\u4e0d\u8fc7\u4ed6\u662f\u6587\u5b98\uff0c\u8fd9\u5200\u5374\u4ece\u4f55\u5904\u5f97\u6765\u4e86\u8fd9\u4e9b\u4eba\u5374\u53c8\u5982\u4f55\u77e5\u6653\uff1f\u82d7\u4eba\u51e4\u521d\u65f6\u63d0\u9632\u8fd9\u51e0\u4eba\u9634\u8c0b\u5bf9\u4ed8\u81ea\u5df1\uff0c\u4e00\u76f4\u6df1\u81ea\u6212\u5907\uff0c\u73b0\u4e0b\u65e2\u77e5\u4ed6\u4eec\u662f\u60f3\u593a\u5b9d\u5200\uff0c\u5fc3\u4e0b\u4f46\u7136\uff0c\u767b\u65f6\u4ece\u5c40\u4e2d\u4eba\u53d8\u6210\u4e86\u65c1\u89c2\u5ba2\u3002\u4f46\u89c1\u5b9d\u5200\u4e00\u51fa\u9798\uff0c\u90a3\u201c\u8c03\u4faf\u5144\u201d\u3001\u5e97\u4f34\u3001\u811a\u592b\u3001\u8f66\u592b\u3001\u8865\u9505\u5320\u4e00\u9f50\u51d1\u62e2\u3002\u82d7\u4eba\u51e4\u77e5\u9053\u8fd9\u4e94\u4eba\u5747\u6b32\u5f97\u5200\uff0c\u53ea\u662f\u788d\u7740\u65c1\u4eba\u6b66\u529f\u4e86\u5f97\uff0c\u8fd9\u5bf9\u4e0d\u6562\u8d38\u7136\u52a8\u624b\uff0c\u5426\u5219\u4ee5\u5357\u4ec1\u901a\u624b\u65e0\u7f1a\u9e21\u4e4b\u529b\uff0c\u8fd9\u628a\u5200\u65e9\u5df2\u88ab\u4eba\u593a\u53bb\uff0c\u54ea\u91cc\u7b49\u5f97\u5230\u4eca\u65e5\uff1f \u3000\u3000\u5357\u4ec1\u901a\u6068\u90a3\u8865\u9505\u5320\u53e3\u9f7f\u8f7b\u8584\uff0c\u672c\u8981\u6bd4\u8bd5\uff0c\u4f46\u89c1\u4ed6\u90a3\u628a\u5200\u950b\u9510\u65e0\u6bd4\uff0c\u4e5f\u975e\u5e38\u7269\uff0c\u82e5\u662f\u6597\u4e2a\u4e24\u8d25\u4ff1\u4f24\uff0c\u5c82\u975e\u635f\u4f24\u4e86\u81f3\u5b9d\uff1f\u4e8e\u662f\u8bf4\u9053\uff1a\u201c\u4f60\u77e5\u9053\u4e86\u5c31\u597d\uff0c\u4e0b\u6b21\u53ef\u8fd8\u6562\u80e1\u8bf4\u516b\u9053\u4e48\uff1f\u201d\u6b63\u8981\u8fd8\u5200\u5165\u9798\uff0c\u90a3\u2018\u8c03\u4faf\u5144\u201d\u7a81\u7136\u4e00\u4f38\u624b\uff0c\u5c06\u5200\u593a\u8fc7\uff0c\u64e6\u7684\u4e00\u58f0\u8f7b\u54cd\uff0c\u4e0e\u8865\u9505\u5320\u624b\u4e2d\u5229\u5203\u76f8\u4ea4\uff0c\u8865\u9505\u5320\u7684\u5200\u5203\u65ad\u4e3a\u4e24\u622a\uff0c\u63a5\u7740\u53c8\u662f\u5f53\u7684\u4e00\u54cd\uff0c\u5200\u5934\u843d\u5728\u5730\u4e0b\uff0c\u8865\u9505\u5320\u3001\u811a\u592b\u3001\u8f66\u592b\u3001\u5e97\u4f34\u56db\u4eba\u5c06\u201c\u8c03\u4faf\u5144\u201d\u56db\u4e0b\u56f4\u4f4f\uff0c\u7acb\u65f6\u5c31\u8981\u52a8\u624b\u3002\u2018\u8c03\u4faf\u5144\u201d\u867d\u7136\u5b9d\u5200\u5728\u624b\uff0c\u5374\u662f\u4f17\u5be1\u4e0d\u654c\uff0c\u5f53\u5373\u5c06\u5200\u8fd8\u7ed9\u4e86\u5357\u4ec1\u901a\uff0c\u7fd8\u62c7\u6307\u8bf4\u9053\uff1a\u201c\u597d\u5200\uff0c\u597d\u5200\uff01\u201d\u5357\u4ec1\u901a\u8138\u4e0a\u53d8\u8272\uff0c\u8d23\u5907\u9053\uff1a\u201c\u54b3\uff0c\u4f60\u4e5f\u592a\u8fc7\u9c81\u83bd\u4e86\uff01\u201d\u89c1\u5b9d\u5200\u65e0\u6059\uff0c\u8fd9\u624d\u559c\u5b5c\u5b5c\u5730\u8fd8\u5200\u5165\u9798\uff0c\u56de\u623f\u5b89\u7761\u3002 \u3000\u3000\u82d7\u4eba\u51e4\u77e5\u9053\u9002\u624d\u4e94\u4eba\u6fc0\u5357\u4ec1\u901a\u53d6\u5200\u76f8\u8bd5\uff0c\u90a3\u662f\u8981\u9a8c\u660e\u5b9d\u5200\u7684\u6b63\u8eab\uff0c\u4e0d\u51fa\u4e00\u65e5\uff0c\u4e94\u4eba\u5c31\u6709\u4e00\u573a\u6d41\u8840\u4e89\u6597\u3002\u4ed6\u867d\u4fa0\u4e49\u4e3a\u6000\uff0c\u4f46\u89c1\u90a3\u5357\u4ec1\u901a\u6a2a\u884c\u9738\u9053\uff0c\u4e0d\u662f\u597d\u4eba\uff0c\u8fd9\u628a\u5200\u53ea\u6015\u4e5f\u662f\u5de7\u53d6\u8c6a\u593a\u800c\u5f97\uff0c\u5fc3\u60f3\u6211\u81ea\u53bb\u796d\u5893\uff0c\u4e0d\u5fc5\u7406\u4f1a\u4ed6\u4eec\u5982\u4f55\u9ed1\u5403\u9ed1\u7684\u593a\u5200\u3002 \u3000\u3000\u6b21\u65e5\u7edd\u65e9\u8d77\u6765\uff0c\u53ea\u89c1\u5357\u4ec1\u901a\u5df2\u7136\u8d77\u884c\uff0c\u8865\u9505\u5320\u7b49\u56fa\u7136\u90fd\u5df2\u4e0d\u5728\u5e97\u5185\uff0c\u8fde\u90a3\u5e97\u4f34\u4e5f\u5df2\u79bb\u53bb\u3002\u4e00\u95ee\u4e4b\u4e0b\uff0c\u8fd9\u4eba\u679c\u7136\u662f\u6628\u5929\u508d\u665a\u624d\u5230\u7684\u6076\u5ba2\uff0c\u7ed9\u4e86\u5341\u4e24\u94f6\u5b50\uff0c\u8981\u4e54\u88c5\u5e97\u4f34\uff0c\u82d7\u4eba\u51e4\u6697\u6697\u53f9\u606f\uff1a\u201c\u5e38\u8a00\u9053\uff1a\u8c29\u85cf\u6d77\u76d7\uff0c\u679c\u7136\u4e00\u70b9\u513f\u4e0d\u9519\u3002\u201d \u3000\u3000\u7ed3\u4e86\u5e97\u5e10\uff0c\u4e0a\u9a6c\u4fbf\u884c\u3002 \u3000\u3000\u9a70\u51fa\u4e8c\u5341\u4f59\u91cc\uff0c\u5ffd\u542c\u897f\u9762\u5c71\u8c37\u4e2d\u4e00\u4e2a\u5973\u5b50\u58f0\u97f3\u60e8\u547c\uff1a\u201c\u6551\u547d\uff01\u6551\u547d\uff01\u201d \u3000\u3000\u6b63\u662f\u5357\u5c0f\u59d0\u7684\u58f0\u97f3\u3002\u82d7\u4eba\u51e4\u5fc3\u60f3\uff1a\u201c\u8fd9\u4e9b\u6076\u8d3c\u593a\u4e86\u5200\u8fd8\u60f3\u6740\u4eba\uff0c\u8fd9\u53ef\u4e0d\u80fd\u4e0d\u7ba1\u3002\u201d\u4e00\u8dc3\u4e0b\u9a6c\uff0c\u5c55\u5f00\u8f7b\u8eab\u529f\u592b\u5faa\u58f0\u8d76\u53bb\uff0c\u8f6c\u8fc7\u4e24\u4e2a\u5f2f\uff0c\u53ea\u89c1\u96ea\u5730\u91cc\u6bb7\u7ea2\u4e00\u7247\uff0c\u5357\u4ec1\u901a\u8eab\u9996\u5f02\u5904\uff0c\u6b7b\u5728\u5f53\u5730\u3002\u90a3\u201c\u51b7\u6708\u5b9d\u5200\u201d\u6a2a\u5728\u4ed6\u8eab\u7554\uff0c\u4e94\u4e2a\u4eba\u8c01\u4e5f\u4e0d\u6562\u4f38\u624b\u5148\u62ff\u3002\u5357\u5c0f\u59d0\u5374\u7ed9\u8865\u9505\u5320\u6293\u4f4f\u4e86\u53cc\u624b\uff0c\u6323\u624e\u4e0d\u5f97\u3002 \u3000\u3000\u82d7\u4eba\u98ce\u9690\u8eab\u4e00\u5757\u5927\u77f3\u4e4b\u540e\uff0c\u5bdf\u770b\u52a8\u9759\uff0c\u53ea\u542c\u201c\u8c03\u4faf\u5144\u201d\u9053\uff1a\u201c\u5b9d\u5200\u53ea\u6709\u4e00\u628a\uff0c\u5374\u6709\u4e94\u4e2a\u4eba\u60f3\u8981\uff0c\u600e\u4e48\u529e\uff1f\u201d\u90a3\u811a\u592b\u9053\uff1a\u201c\u51ed\u529f\u592b\u5206\u4e0a\u4e0b\uff0c\u80dc\u8005\u5f97\u5200\uff0c\u516c\u5e73\u4ea4\u6613\u3002\u201d\u201c\u8c03\u4faf\u5144\u201d\u5411\u5357\u5c0f\u59d0\u77a7\u4e86\u4e00\u773c\uff0c\u8bf4\u9053\uff1a\u201c\u5b9d\u5200\u7f8e\u4eba\uff0c\u90fd\u662f\u96be\u5f97\u4e4b\u7269\u3002\u201d\u8865\u9505\u5320\u9053\uff1a\u201c\u6211\u4e0d\u4e89\u5b9d\u5200\uff0c\u8981\u4e86\u5979\u5c31\u662f\u5566\u3002\u201d\u5e97\u4f34\u51b7\u7b11\u9053\uff1a\u201c\u4e5f\u4e0d\u89c1\u5f97\u6709\u8fd9\u4e48\u4fbf\u5b9c\u4e8b\u513f\u3002\u6b66\u529f\u7b2c\u4e00\u7684\u5f97\u5b9d\u5200\uff0c\u7b2c\u4e8c\u7684\u5f97\u7f8e\u4eba\u3002\u201d\u811a\u592b\u3001\u8f66\u592b\u9f50\u58f0\u9053\uff1a\u201c\u5bf9\uff0c\u5c31\u662f\u8fd9\u4e48\u7740\u3002\u201d\u5e97\u4f34\u5411\u8865\u9505\u5320\u9053\uff0c\u201c\u8001\u5144\uff0c\u52b3\u9a7e\u653e\u5f00\u624b\uff0c\u8bf4\u4e0d\u5b9a\u5728\u4e0b\u529f\u592b\u7b2c\u4e8c\uff0c\u8fd9\u662f\u6211\u7684\u8001\u5a46\uff01\u201d\u201c\u8c03\u4faf\u5144\u201d\u7b11\u9053\uff1a\u201c\u6b63\u662f\uff01\u201d\u8f6c\u5934\u5389\u58f0\u5411\u5357\u5c0f\u59d0\u9053\uff1a\u201c\u4f60\u6562\u518d\u56b7\u4e00\u58f0\uff0c\u5148\u65a9\u4f60\u4e00\u5200\u518d\u8bf4\uff01\u201d\u8865\u9505\u5320\u653e\u5f00\u4e86\u624b\u3002\u5357\u5c0f\u59d0\u4f0f\u5728\u7236\u4eb2\u5c38\u8eab\u4e4b\u4e0a\uff0c\u62bd\u62bd\u564e\u564e\u5730\u54ed\u6ce3\u3002 \u3000\u3000\u90a3\u8f66\u592b\u7b11\u9053\uff1a\u201c\u5c0f\u59d0\uff0c\u522b\u54ed\u5566\u3002\u5f85\u4f1a\u513f\u5c31\u6709\u4f60\u4e50\u7684\u5566\uff01\u201d\u4f38\u624b\u53bb\u6478\u5979\u8138\uff0c\u795e\u8272\u6781\u662f\u8f7b\u8584\u3002 \u3000\u3000\u82d7\u4eba\u51e4\u77a7\u5230\u6b64\u5904\uff0c\u518d\u4e5f\u5fcd\u8010\u4e0d\u6ce8\uff0c\u5927\u8e0f\u6b65\u4ece\u77f3\u540e\u8d70\u4e86\u51fa\u6765\uff0c\u4f4e\u6c89\u7740\u55d3\u5b50\u559d\u9053\uff1a\u201c\u4e0b\u6d41\u4e1c\u897f\uff0c\u90fd\u7ed9\u6211\u6eda\uff01\u201d\u90a3\u4e94\u4eba\u5403\u4e86\u4e00\u60ca\uff0c\u9f50\u58f0\u559d\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u201d \u3000\u3000\u82d7\u4eba\u51e4\u751f\u6027\u4e0d\u7231\u591a\u8bdd\uff0c\u6325\u4e86\u6325\u624b\uff0c\u9053\uff1a\u201c\u4e00\u9f50\u6eda\uff01\u201d\u8865\u9505\u5320\u6027\u5b50\u6700\u662f\u66b4\u8e81\uff0c\u7eb5\u8eab\u8dc3\u8d77\uff0c\u53cc\u638c\u5f53\u80f8\u51fb\u53bb\uff0c\u559d\u9053\uff1a\u201c\u4f60\u7ed9\u6211\u6eda\uff01\u201d\u82d7\u4eba\u51e4\u5de6\u638c\u6325\u51fa\uff0c\u4ee5\u786c\u529b\u63a5\u4ed6\u786c\u529b\uff0c\u4e00\u63a8\u4e00\u6325\uff0c\u90a3\u8865\u9505\u5320\u817e\u7a7a\u76f4\u98de\u51fa\u53bb\uff0c\u6454\u5728\u4e08\u8bb8\u4e4b\u5916\uff0c\u534a\u5929\u722c\u4e0d\u8d77\u6765\u3002 \u3000\u3000\u5176\u4f59\u56db\u4eba\u89c1\u4ed6\u5982\u6b64\u795e\u52c7\uff0c\u65e0\u4e0d\u9a87\u7136\uff0c\u8fc7\u4e86\u534a\u664c\uff0c\u4e0d\u7ea6\u800c\u540c\u5730\u95ee\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u201d\u82d7\u4eba\u51e4\u4ecd\u662f\u6325\u4e86\u6325\u624b\uff0c\u8fd9\u6b21\u8fde\u201c\u6eda\u201d\u5b57\u4e5f\u4e0d\u8bf4\u4e86\u3002 \u3000\u3000\u90a3\u8f66\u592b\u4ece\u8170\u95f4\u53d6\u51fa\u4e00\u6839\u8f6f\u97ad\uff0c\u811a\u592b\u6a2a\u8fc7\u6241\u62c5\uff0c\u5de6\u53f3\u6251\u4e0a\u3002\u82d7\u4eba\u51e4\u77e5\u9053\u8fd9\u4e94\u4eba\u90fd\u662f\u52b2\u654c\uff0c\u82e5\u662f\u8054\u624b\u653b\u6765\uff0c\u4e00\u65f6\u4e4b\u95f4\u4e0d\u6613\u53d6\u80dc\uff0c\u5f53\u4e0b\u4e00\u51fa\u624b\u5c31\u662f\u6781\u5389\u5bb3\u7684\u72e0\u62db\uff0c\u4fa7\u8eab\u907f\u5f00\u8f6f\u97ad\uff0c\u53f3\u624b\u75be\u4f38\uff0c\u5df2\u6293\u4f4f\u6241\u62c5\u4e00\u7aef\uff0c\u8fd0\u529b\u4e00\u6296\uff0c\u5580\u5587\u4e00\u54cd\uff0c\u67a3\u6728\u6241\u62c5\u65ad\u6210\u4e24\u622a\uff0c\u5de6\u811a\u7a81\u7136\u98de\u51fa\uff0c\u5c06\u90a3\u8f66\u592b\u8e22\u4e86\u4e00\u4e2a\u7b4b\u6597\u3002\u90a3\u811a\u592b\u6b32\u5f85\u9000\u5f00\uff0c\u82d7\u4eba\u51e4\u957f\u81c2\u4f38\u5904\uff0c\u5df2\u6293\u4f4f\u4ed6\u7684\u540e\u9886\uff0c\u5927\u559d\u4e00\u58f0\uff0c\u594b\u529b\u63b7\u51fa\uff0c\u90a3\u811a\u592b\u72b9\u4f3c\u98ce\u7b5d\u65ad\u7ebf\uff0c\u7adf\u8dcc\u51fa\u6570\u4e08\u4e4b\u5916\uff0c\u817e\u7684\u4e00\u54cd\uff0c\u7ed3\u7ed3\u5b9e\u5b9e\u5730\u6454\u5728\u96ea\u5730\u4e4b\u4e2d\u3002 \u3000\u3000\u90a3\u201c\u8c03\u4faf\u5144\u201d\u77e5\u9053\u96be\u654c\uff0c\u8bf4\u9053\uff1a\u201c\u4f69\u670d\uff0c\u4f69\u670d\uff0c\u8fd9\u5b9d\u5200\u8be5\u5f53\u9601\u4e0b\u6240\u6709\u3002\u201d \u3000\u3000\u4e00\u9762\u8bf4\u4e00\u9762\u4fef\u8eab\u62ac\u8d77\u5b9d\u5200\uff0c\u53cc\u624b\u9012\u4e86\u8fc7\u6765\u3002\u82d7\u4eba\u51e4\u9053\uff1a\u201c\u6211\u4e0d\u8981\uff0c\u4f60\u8fd8\u7ed9\u539f\u4e3b\uff01\u201d\u90a3\u201c\u8c03\u4faf\u5144\u201d\u4e00\u6014\uff0c\u5fc3\u60f3\uff1a\u201c\u4e16\u4e0a\u54ea\u6709\u8fd9\u6837\u7684\u597d\u4eba\uff1f\u201d\u4e00\u62ac\u5934\uff0c\u53ea\u89c1\u4ed6\u8138\u5982\u91d1\u7eb8\uff0c\u795e\u5a01\u51db\u51db\uff0c\u7a81\u7136\u60f3\u8d77\uff0c\u8bf4\u9053\uff1a\u201c\u539f\u6765\u9601\u4e0b\u662f\u91d1\u9762\u4f5b\u82d7\u5927\u4fa0\uff1f\u201d \u3000\u3000\u82d7\u4eba\u51e4\u70b9\u4e86\u70b9\u5934\u3002\u201d\u201c\u8c03\u4faf\u5144\u201d\u9053\uff1a\u201c\u6211\u4eec\u6709\u773c\u4e0d\u8bc6\u6cf0\u5c71\uff0c\u683d\u5728\u82d7\u5927\u4fa0\u624b\u91cc\uff0c\u8fd8\u6709\u4ec0\u4e48\u8bdd\u8bf4\uff1f\u201d\u5f53\u4e0b\u53c8\u5c06\u5b9d\u5200\u9012\u4e0a\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u4eba\u848b\u8c03\u4faf\uff0c\u4e09\u751f\u6709\u5e78\uff0c\u5f97\u9022\u5f53\u4e16\u5927\u4fa0\uff0c\u8fd9\u5b9d\u5200\u8bf7\u82d7\u5927\u4fa0\u5904\u7f6e\u5427\uff01\u201d\u82d7\u4eba\u51e4\u6700\u4e0d\u559c\u522b\u4eba\u647d\u5506\uff0c\u5fc3\u60f3\u62ff\u8fc7\u4e4b\u540e\u518d\u4ea4\u7ed9\u5357\u5c0f\u59d0\u4fbf\u662f\uff0c\u5f53\u4e0b\u4f38\u624b\u63e1\u4f4f\u4e86\u5200\u67c4\u3002 \u3000\u3000\u4ed6\u6b63\u8981\u63d0\u624b\uff0c\u7a81\u542c\u55e4\u55e4\u4e24\u58f0\u8f7b\u54cd\uff0c\u817f\u4e0a\u5fae\u5fae\u4e00\u75bc\u3002\u848b\u8c03\u4faf\u8dc3\u5f00\u4e08\u4f59\uff0c\u5411\u524d\u98de\u8dd1\uff0c\u53eb\u9053\uff1a\u201c\u4ed6\u4e2d\u4e86\u6211\u7684\u7edd\u95e8\u6bd2\u9488\uff0c\u5feb\u7f20\u4f4f\u4ed6\u3002\u201d\u82d7\u4eba\u51e4\u542c\u5230\u201c\u7edd\u95e8\u6bd2\u9488\u201d\u56db\u5b57\uff0c\u53e3\u4e2d\u201c\u54e6\u201d\u4e86\u4e00\u58f0\uff0c\u6697\u9053\uff1a\u201c\u4e91\u5357\u848b\u6c0f\u6bd2\u9488\u5929\u4e0b\u95fb\u540d\uff0c\u4eca\u756a\u4e2d\u4e86\u4ed6\u7684\u8be1\u8ba1\u3002\u201d\u5fc3\u77e5\u8fd9\u6697\u5668\u5267\u6bd2\u65e0\u6bd4\uff0c\u5f53\u4e0b\u6df1\u5438\u4e00\u53e3\u6c14\uff0c\u98de\u5954\u800c\u524d\uff0c\u9877\u523b\u65f6\u8d76\u4e0a\u848b\u8c03\u4faf\uff0c\u4e00\u628a\u6293\u4f4f\uff0c\u4f38\u6307\u5728\u4ed6\u80c1\u4e0b\u4e00\u6233\uff0c\u5df2\u95ed\u4f4f\u4e86\u4ed6\u7684\u7a74\u9053\uff0c\u629b\u5728\u5730\u4e0b\u3002 \u3000\u3000\u811a\u592b\u3001\u8f66\u592b\u7b49\u672c\u5df2\u4e00\u8d25\u6d82\u5730\uff0c\u5ffd\u542c\u5f97\u654c\u4eba\u4e2d\u4e86\u6bd2\u9488\uff0c\u65e0\u4e0d\u559c\u51fa\u671b\u5916\uff0c\u8fdc\u8fdc\u56f4\u7740\uff0c\u5747\u4e0d\u903c\u8fd1\uff0c\u8981\u5f85\u4ed6\u6bd2\u53d1\u81ea\u6bd9\u3002\u82d7\u4eba\u51e4\u4e00\u53e3\u6c14\u4e0d\u6562\u541e\u5410\uff0c\u5c55\u5f00\u8f7b\u529f\uff0c\u75be\u5411\u811a\u592b\u8d76\u53bb\u3002\u90a3\u811a\u592b\u5413\u5f97\u9b42\u98de\u9b44\u6563\uff0c\u820d\u547d\u72c2\u5954\uff0c\u82d7\u4eba\u51e4\u8d76\u5230\u8eab\u540e\uff0c\u53f3\u638c\u51fb\u53bb\uff0c\u767b\u65f6\u5c06\u4ed6\u4e94\u810f\u9707\u88c2\u3002\u6b64\u638c\u51fb\u51fa\u540e\u811a\u4e0b\u7247\u523b\u4e0d\u505c\uff0c\u77ac\u606f\u95f4\u8ffd\u5230\u8f66\u592b\u8eab\u524d\u3002 \u3000\u3000\u90a3\u8f66\u592b\u6325\u52a8\u8f6f\u97ad\u62a4\u8eab\uff0c\u53ea\u76fc\u62b5\u6321\u5f97\u5341\u62db\u516b\u62db\uff0c\u6328\u5230\u4ed6\u8eab\u4e0a\u6bd2\u6027\u53d1\u4f5c\u3002\u82d7\u4eba\u51e4\u54ea\u91cc\u4e0e\u4ed6\u62c6\u4ec0\u4e48\u62db\uff0c\u84b2\u6247\u822c\u7684\u5927\u624b\u4f38\u51fa\uff0c\u6293\u4f4f\u8f6f\u97ad\u97ad\u68a2\uff0c\u795e\u529b\u5230\u5904\uff0c\u4e00\u593a\u4e00\u6325\uff0c\u8f6f\u97ad\u5012\u8f6c\u8fc7\u6765\uff0c\u5c06\u4ed6\u6253\u5f97\u8111\u6d46\u8ff8\u88c2\u3002 \u3000\u3000\u82d7\u4eba\u51e4\u8fde\u6bd9\u4e8c\u4eba\uff0c\u811a\u4e0a\u5df2\u81ea\u53d1\u9ebb\uff0c\u6b64\u662f\u751f\u6b7b\u5173\u5934\uff0c\u4e0d\u5bb9\u6709\u7247\u523b\u5598\u606f\uff0c\u4f46\u89c1\u5e97\u4f34\u4e0e\u8865\u9505\u5320\u90fd\u5df2\u5728\u6570\u5341\u4e08\u5916\uff0c\u4e8c\u4eba\u662f\u4e00\u822c\u7684\u5fc3\u601d\uff0c\u5c3d\u529b\u8fdc\u8fdc\u9003\u5f00\uff0c\u4ee5\u5f85\u654c\u4eba\u4e0d\u652f\u3002\u82d7\u4eba\u51e4\u672c\u6765\u4e0d\u6b32\u4f24\u4eba\u6027\u547d\uff0c\u4f46\u6b64\u65f6\u53ea\u8981\u7559\u4e0b\u4e00\u4e2a\u6d3b\u53e3\uff0c\u81ea\u5df1\u6bd2\u53d1\u8dcc\u5012\uff0c\u90a3\u5c31\u662f\u628a\u81ea\u5df1\u6027\u547d\u4ea4\u5728\u3000\u4ed6\u7684\u624b\u91cc\u3002\u5f53\u4e0b\u54ac\u7d27\u7259\u5173\uff0c\u624b\u63e1\u8f6f\u97ad\uff0c\u8ffd\u8d76\u5e97\u4f34\u3002\u90a3\u5e97\u4f34\u6781\u662f\u72e1\u733e\uff0c\u5c3d\u62e3\u6ce5\u6c9f\u9677\u5751\u4e2d\u5954\u8dd1\u3002\u4f46\u82d7\u4eba\u51e4\u7684\u8f7b\u529f\u4f55\u7b49\u4e86\u5f97\uff0c\u4e00\u8f6c\u773c\u5df2\u8089\u8ffd\u4e0a\uff0c\u90a3\u5e97\u4f34\u773c\u89c1\u96be\u9003\uff0c\u63d0\u7740\u5315\u9996\u6251\u5c06\u8fc7\u6765\uff0c\u82d7\u4eba\u98ce\u7acb\u523b\u56de\u5934\u8f6c\u8eab\uff0c\u5411\u540e\u4e00\u811a\u5012\u5598\uff0c\u77a7\u4e5f\u4e0d\u77a7\uff0c\u7acb\u5373\u63d0\u6c14\u8ffd\u8d76\u8865\u9505\u5320\u3002\u8fd9\u4e00\u811a\u679c\u7136\u6b63\u4e2d\u5e97\u4f34\u5fc3\u7a9d\uff0c\u8e22\u5f97\u4ed6\u53e3\u4e2d\u72c2\u55b7\u9c9c\u8840\uff0c\u4ef0\u5929\u7acb\u6bd9\u3002 \u3000\u3000\u90a3\u8865\u9505\u5320\u6b66\u529f\u867d\u4e0d\u751a\u5f3a\uff0c\u4f46\u9102\u5317\u9b3c\u89c1\u6101\u9524\u5bb6\u6240\u4f20\u8f7b\u529f\u5374\u662f\u6b66\u6797\u4e2d\u4e00\u7edd\u3002 \u3000\u3000\u82d7\u4eba\u98ce\u8ffd\u5954\u9010\u5317\uff0c\u6bd2\u6c14\u53d1\u4f5c\u5f97\u66f4\u5feb\uff0c\u811a\u6b65\u5df2\u81ea\u8e52\u8dda\uff0c\u7adf\u7136\u8ffd\u8d76\u4e0d\u4e0a\u3002\u8865\u9505\u5320\u89c1\u4ed6\u4e00\u98a0\u4e00\u9890\uff0c\u5fc3\u4e2d\u5927\u559c\uff0c\u6697\u60f3\uff1a\u201c\u8001\u5929\u4fdd\u4f51\uff0c\u53eb\u6211\u5782\u624b\u800c\u5f97\u5b9d\u5200\u7f8e\u4eba\u3002\u201d \u3000\u3000\u601d\u5ff5\u672a\u5b9a\uff0c\u7a81\u542c\u534a\u7a7a\u547c\u547c\u98ce\u54cd\uff0c\u4e00\u6761\u9ed1\u9edd\u9edd\u7684\u4e1c\u897f\u6a2a\u7a7a\u800c\u81f3\uff0c\u5f85\u6b32\u95ea\u8eb2\uff0c\u5df2\u81ea\u4e0d\u53ca\u3002\u539f\u6765\u82d7\u4eba\u51e4\u77e5\u9053\u8ffd\u4ed6\u4e0d\u4e0a\uff0c\u6700\u540e\u594b\u8d77\u4f38\u529b\uff0c\u63b7\u51fa\u8f6f\u97ad\u3002\u8fd9\u6761\u94a2\u94f8\u8f6f\u97ad\u4ece\u9762\u95e8\u76f4\u6253\u5230\u5c0f\u8179\uff0c\u8865\u9505\u5320\u7acb\u65f6\u5c38\u6a2a\u96ea\u5730\u3002\u6b64\u65f6\u82d7\u4eba\u51e4\u4e5f\u5df2\u652f\u6301\u4e0d\u4f4f\uff0c\u4e00\u4ea4\u6454\u5012\u3002 \u3000\u3000\u5357\u5c0f\u59d0\u4f0f\u5728\u7236\u4eb2\u5c38\u4e0a\uff0c\u773c\u89c1\u8fd9\u573a\u60ca\u5fc3\u52a8\u9b44\u7684\u6076\u6218\uff0c\u5413\u5f97\u5446\u4e86\u3002\u6700\u540e\u89c1\u82d7\u4eba\u51e4\u5012\u4e0b\uff0c\u5fd9\u8d70\u8fd1\u76f8\u6276\uff0c\u4f46\u82d7\u4eba\u51e4\u8eab\u8eaf\u9ad8\u5927\uff0c\u5979\u5a07\u5f31\u65e0\u529b\uff0c\u54ea\u91cc\u6276\u5f97\u8d77\u6765\uff0c\u82d7\u4eba\u51e4\u4f38\u667a\u5c1a\u6e05\uff0c\u4e0b\u534a\u8eab\u5374\u5df2\u9ebb\u6728\uff0c\u6307\u7740\u848b\u8c03\u4faf\u9053\uff1a\u201c\u641c\u4ed6\u8eab\u8fb9\uff0c\u53d6\u89e3\u836f\u7ed9\u6211\u670d\u3002\u201d\u5357\u5c0f\u59d0\u4f9d\u8a00\u641c\u7d22\uff0c\u679c\u7136\u627e\u5230\u4e00\u4e2a\u5c0f\u5c0f\u74f7\u74f6\uff0c\u95ee\u82d7\u4eba\u51e4\u9053\uff1a\u201d\u662f\u8fd9\u4e2a\u4e48\uff1f\u201d\u82d7\u4eba\u51e4\u660f\u660f\u6c89\u6c89\uff0c\u5df2\u81ea\u96be\u8fa8\uff0c\u9053\uff1a\u201c\u4e0d\u7ba1\u662f\u4e0d\u662f\uff0c\u670d\u2026\u2026\u670d\u4e86\u518d\u8bf4\u3002\u201d \u3000\u3000\u5357\u5c0f\u59d0\u62d4\u5f00\u74f6\u585e\uff0c\u5c06\u5c0f\u534a\u74f6\u9ec4\u8272\u836f\u7c89\u5012\u5728\u5de6\u638c\uff0c\u9001\u5165\u82d7\u4eba\u51e4\u53e3\u91cc\u3002 \u3000\u3000\u82d7\u4eba\u51e4\u7528\u529b\u541e\u4e0b\uff0c\u8bf4\u9053\uff1a\u201c\u5feb\u5c06\u4ed6\u6740\u4e86\uff01\u201d\u5357\u5c0f\u59d0\u5927\u5403\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u6211\u2026\u2026 \u3000\u3000\u6211\u4e0d\u6562\u2026\u2026\u6740\u4eba\u3002\u201d\u82d7\u4eba\u51e4\u5389\u58f0\u9053\uff1a\u201c\u4ed6\u662f\u4f60\u6740\u7236\u4ec7\u4eba\u3002\u201d\u5357\u5c0f\u59d0\u4ecd\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u4e0d\u6562\u2026\u2026\u201d\u82d7\u4eba\u51e4\u9053\uff1a\u201c\u518d\u8fc7\u51e0\u4e2a\u65f6\u8fb0\uff0c\u4ed6\u7a74\u9053\u81ea\u89e3\u3002\u6211\u53d7\u4f24\u5f88\u91cd\u2026\u2026\u90a3\u65f6\u54b1\u4e24\u4eba\u6b7b\u65e0\u846c\u8eab\u4e4b\u5730\u3002\u201d \u3000\u3000\u5357\u5c0f\u59d0\u53cc\u624b\u63d0\u8d77\u5b9d\u5200\uff0c\u62d4\u51fa\u5200\u9798\uff0c\u773c\u89c1\u848b\u8c03\u4faf\u773c\u4e2d\u9732\u51fa\u54c0\u6c42\u4e4b\u8272\uff0c\u5979\u81ea\u5c0f\u6740\u9e21\u6740\u9c7c\u4e5f\u662f\u4e0d\u6562\uff0c\u8fd9\u6740\u4eba\u7684\u4e00\u5200\u5982\u4f55\u780d\u5f97\u4e0b\u53bb\uff1f \u3000\u3000\u82d7\u4eba\u51e4\u5927\u559d\uff1a\u201c\u4f60\u4e0d\u6740\u4ed6\uff0c\u5c31\u662f\u6740\u6211\uff01\u201d\u5357\u5c0f\u59d0\u5403\u4e86\u4e00\u60ca\uff0c\u5b50\u4e00\u98a4\uff0c\u5b9d\u5200\u8131\u624b\u6389\u4e0b\u3002\u8fd9\u5200\u780d\u91d1\u65ad\u7389\uff0c\u5203\u53e3\u6b63\u597d\u5bf9\u51c6\u848b\u8c03\u4faf\u7684\u8111\u888b\uff0c\u53ea\u542c\u5f97\u5357\u5c0f\u59d0\u4e0e\u848b\u8c03\u4faf\u540c\u58f0\u5927\u53eb\uff0c\u4e00\u4e2a\u660f\u5012\uff0c\u8dcc\u5728\u82d7\u4eba\u98ce\u8eab\u4e0a\uff0c\u53e6\u4e00\u4e2a\u7684\u8111\u888b\u5df2\u88ab\u5b9d\u5200\u5288\u5f00\u3002 \u3000\u3000\u82d7\u4eba\u51e4\u60f3\u5230\u6b64\u5904\uff0c\u6000\u4e2d\u5e7c\u5973\u5ffd\u7136\u5624\u7684\u4e00\u58f0\u9192\u6765\uff0c\u54ed\u9053\uff1a\u201c\u7238\u7238\uff0c\u5988\u5462\uff1f\u6211\u8981\u5988\u3002\u201d\u82d7\u4eba\u51e4\u8fd8\u672a\u56de\u7b54\uff0c\u90a3\u5973\u5b69\u4e00\u8f6c\u5934\uff0c\u89c1\u5230\u706b\u5806\u65c1\u7684\u7f8e\u5987\uff0c\u5f20\u5f00\u53cc\u81c2\uff0c\u5927\u53eb\uff1a\u201c\u5988\u5988\uff0c\u5988\u5988\uff0c\u5170\u5170\u627e\u4f60\uff01\u201d\u6b22\u7136\u559c\u8dc3\uff0c\u8981\u90a3\u7f8e\u5987\u6765\u62b1\u3002 \u3000\u3000\u56db\u5468\u4f17\u4eba\u542c\u90a3\u5e7c\u5973\u5148\u53eb\u82d7\u4eba\u51e4\u201c\u7238\u7238\u201d\uff0c\u53c8\u53eb\u90a3\u7f8e\u5987\u201c\u5988\u5988\u201d\uff0c\u90fd\u662f\u5927\u611f\u60ca\u5f02\uff0c\u5fc3\u60f3\u8fd9\u7f8e\u5987\u660e\u660e\u662f\u7530\u5f52\u519c\u4e4b\u59bb\uff0c\u600e\u4e48\u53c8\u4f1a\u662f\u82d7\u4eba\u51e4\u4e4b\u5973\u7684\u6bcd\u4eb2\uff1f\u90a3\u5973\u5b69\u8fd9\u4e24\u58f0\u201c\u5988\u5988\u201d\u4e00\u53eb\uff0c\u5927\u5385\u4e2d\u7d27\u5f20\u7684\u6c14\u52bf\u53c8\u81ea\u6d53\u4e86\u51e0\u5206\u3002\u51e0\u5341\u4e2a\u5927\u4eba\u4e2a\u4e2a\u795e\u8272\u4e25\u91cd\uff0c\u53ea\u6709\u4e00\u4e2a\u5b69\u5b50\u5374\u6b22\u8dc3\u4e0d\u5df2\u3002 \u3000\u3000\u90a3\u7f8e\u5987\u7ad9\u8d77\u8eab\u6765\uff0c\u8d70\u5230\u82d7\u4eba\u51e4\u8eab\u65c1\u62b1\u8fc7\u5b69\u5b50\u3002\u90a3\u5973\u5b69\u7b11\u9053\uff1a\u201c\u5988\u5988\uff0c\u5170\u5170\u627e\u4f60\uff0c\u4f60\u56de\u5bb6\u4e86\u3002\u201d\u90a3\u7f8e\u5987\u7d27\u7d27\u6402\u7740\u5979\uff0c\u4e24\u5f20\u7f8e\u4e3d\u7684\u8138\u5e9e\u504e\u501a\u5728\u4e00\u8d77\u3002\u5973\u5b69\u5728\u68a6\u4e2d\u6d41\u7684\u6cea\u6c34\u8fd8\u6ca1\u5e72\uff0c\u8fd9\u65f6\u8138\u988a\u4e0a\u53c8\u6dfb\u4e86\u3000\u6bcd\u4eb2\u7684\u773c\u6cea\u3002 \u3000\u3000\u8138\u6709\u5200\u75a4\u7684\u72ec\u81c2\u602a\u6c49\u4e00\u76f4\u7f29\u8eab\u5385\u89d2\uff0c\u9759\u89c2\u5404\u4eba\u3002\u8fd9\u65f6\u8f7b\u8f7b\u7ad9\u8d77\uff0c\u8d70\u5230\u76d7\u9b41\u960e\u57fa\u8eab\u524d\uff0c\u5728\u4ed6\u8033\u8fb9\u6084\u6084\u8bf4\u4e86\u51e0\u53e5\u8bdd\u3002\u960e\u57fa\u795e\u8272\u5927\u53d8\uff0c\u5ffd\u5730\u7ad9\u8d77\u3002\u5411\u82d7\u4eba\u51e4\u671b\u4e86\u4e00\u773c\uff0c\u8138\u4e0a\u5927\u6709\u60e7\u8272\uff0c\u7f13\u7f13\u4f38\u624b\u5165\u6000\uff0c\u53d6\u51fa\u4e00\u4e2a\u6cb9\u7eb8\u5c0f\u5305\uff0c\u72ec\u81c2\u4eba\u5939\u624b\u593a\u8fc7\uff0c\u6253\u5f00\u4e00\u770b\uff0c\u89c1\u91cc\u9762\u662f\u4e24\u5f20\u7126\u9ec4\u7684\u7eb8\u7247\u3002\u4ed6\u70b9\u4e86\u70b9\u5934\uff0c\u5305\u597d\u4e86\u653e\u5165\u6000\u5185\uff0c\u91cd\u884c\u56de\u5230\u5385\u89d2\u5750\u4e0b\u3002 \u3000\u3000\u90a3\u7f8e\u5987\u4f38\u8863\u8896\u62b9\u4e86\u62b9\u773c\u6cea\uff0c\u7a81\u7136\u5728\u5973\u5b69\u8138\u4e0a\u6df1\u6df1\u4e00\u543b\uff0c\u773c\u5708\u4e00\u7ea2\uff0c\u53c8\u8981\u6d41\u51fa\u6cea\u6765\uff0c\u7ec8\u4e8e\u5f3a\u884c\u5fcd\u4f4f\uff0c\u970d\u5730\u7ad9\u8d77\uff0c\u628a\u5973\u5b69\u4ea4\u8fd8\u7ed9\u4e86\u82d7\u4eba\u51e4\u3002\u90a3\u5973\u5b69\u5927\u53eb\uff1a\u201c\u5988\u5988\uff0c\u5988\u5988\uff0c\u62b1\u62b1\u5170\u5170\u3002\u201d\u90a3\u7f8e\u5987\u80cc\u5411\u7740\u5979\uff0c\u5b9b\u4f3c\u50f5\u4e86\u4e00\u822c\uff0c\u59cb\u7ec8\u4e0d\u8f6c\u8fc7\u8eab\u6765\u3002 \u3000\u3000\u82d7\u4eba\u51e4\u8010\u7740\u6027\u5b50\u7b49\u5f85\uff0c\u7b49\u90a3\u7f8e\u5987\u7b54\u5e94\u4e00\u58f0\uff0c\u7b49\u5979\u56de\u8fc7\u5934\u6765\u518d\u77a7\u5973\u513f\u4e00\u773c\u2026\u2026 \u3000\u3000\u5728\u82d7\u4eba\u51e4\u5fc3\u4e2d\uff0c\u4ed6\u65e9\u5df2\u8981\u5c06\u4e00\u4e2a\u4eba\u62c9\u8fc7\u6765\u8e0f\u5728\u811a\u4e0b\uff0c\u4e00\u638c\u6253\u6b7b\uff0c\u4f46\u4ed6\u77e5\u9053\uff0c\u4e00\u5b9a\u4f1a\u6709\u4eba\u820d\u547d\u963b\u6b62\u3002\u4ed6\u7684\u6b66\u529f\u662f\u6253\u904d\u5929\u4e0b\u65e0\u654c\u624b\uff0c\u4f46\u4ed6\u7684\u5fc3\u80a0\u5374\u5f88\u8106\u5f31\uff0c\u53ea\u56e0\u4e3a\u4ed6\u662f\u6781\u6df1\u5730\u7231\u7740\u773c\u524d\u8fd9\u4e2a\u7f8e\u5987\u3002 \u3000\u3000\u4ed6\u542c\u89c1\u5973\u513f\u5728\u54ed\u53eb\uff1a\u201c\u5988\u5988\uff0c\u5988\u5988\uff0c\u62b1\u62b1\u5170\u5170\uff01\u201d\u5973\u513f\u5728\u4ed6\u6000\u4e2d\u6323\u624e\u7740\u8981\u5230\u6bcd\u4eb2\u90a3\u91cc\u3002\u4ed6\u8010\u7740\u6027\u5b50\u7b49\u5f85\uff0c\u7b49\u90a3\u7f8e\u5987\u7b54\u5e94\u4e00\u58f0\uff0c\u7b49\u5979\u56de\u8fc7\u5934\u6765\u518d\u77a7\u5973\u513f\u4e00\u773c\u2026\u2026 \u3000\u3000\u90a3\u7f8e\u5987\u662f\u8033\u804b\u4e86\uff1f\u8fd8\u662f\u5979\u7684\u5fc3\u50cf\u94c1\u4e00\u822c\u521a\u786c\uff1f\u5c0f\u5973\u5b69\u5728\u8fde\u58f0\u54c0\u6c42\uff1a\u201c\u5988\u5988\uff0c\u62b1\u62b1\u5170\u5170\uff01\u201d\u4f46\u5988\u5988\u4e00\u52a8\u4e5f\u4e0d\u52a8\uff0c\u80cc\u5fc3\u6ca1\u4e00\u70b9\u513f\u98a4\u6296\uff0c\u8fde\u8863\u886b\u4e5f\u6ca1\u4e00\u70b9\u6446\u52a8\u3002 \u3000\u3000\u82d7\u4eba\u98ce\u5168\u8eab\u7684\u8840\u5728\u6cb8\u817e\uff0c\u4ed6\u7684\u5fc3\u8981\u7ed9\u5973\u513f\u53eb\u5f97\u788e\u4e86\uff0c\u4e8e\u662f\u4e09\u5e74\u4e4b\u524d\uff0c\u6ca7\u5dde\u96ea\u5730\u91cc\u7684\u4e8b\u53c8\u6d8c\u4e0a\u4e86\u5fc3\u5934\uff1a \u3000\u3000\u96ea\u5730\u91cc\u6a2a\u7740\u516d\u5177\u5c38\u8eab\uff0c\u82d7\u4eba\u51e4\u817f\u4e0a\u4e2d\u4e86\u848b\u8c03\u4faf\u7684\u4e24\u679a\u7edd\u95e8\u6bd2\u9488\uff0c\u4e0b\u534a\u8eab\u9ebb\u75f9\uff0c\u52a8\u5f39\u4e0d\u5f97\u3002\u5357\u5c0f\u59d0\u6162\u6162\u9192\u8f6c\uff0c\u89c1\u81ea\u5df1\u8dcc\u5728\u82d7\u4eba\u51e4\u6000\u91cc\uff0c\u6025\u5fd9\u7ad9\u8d77\uff0c\u53cc\u811a\u4e00\u8f6f\uff0c\u53c8\u5750\u5012\u5728\u96ea\u5730\u91cc\u3002\u5979\u60ca\u60f6\u5df2\u6781\uff0c\u8fde\u54ed\u4e5f\u54ed\u4e0d\u51fa\u58f0\u6765\u3002 \u3000\u3000\u82d7\u4eba\u51e4\u9053\uff1a\u201c\u628a\u90a3\u5339\u9a6c\u7275\u8fc7\u6765\u3002\u201d\u58f0\u97f3\u5f88\u4e25\u5389\uff0c\u5357\u5c0f\u59d0\u53ea\u6709\u9075\u4f9d\u7684\u4efd\u513f\u3002 \u3000\u3000\u5979\u5c06\u9a6c\u7275\u5230\u82d7\u4eba\u51e4\u8eab\u8fb9\uff0c\u4f38\u51fa\u67d4\u8f6f\u7684\u624b\uff0c\u63e1\u6ce8\u4e86\u4ed6\u84b2\u6247\u4e00\u822c\u7684\u624b\u638c\uff0c\u60f3\u62c9\u4ed6\u8d77\u6765\u3002 \u3000\u3000\u82d7\u4eba\u51e4\u9053\uff1a\u201c\u4f60\u8d70\u5f00\uff01\u201d\u5fc3\u60f3\uff1a\u201c\u4f60\u600e\u4e48\u62c9\u5f97\u8d77\u6211\uff1f\u201d\u8fd9\u65f6\u4ed6\u4e24\u817f\u5df2\u96be\u4ee5\u884c\u52a8\uff0c\u5f53\u4e0b\u62ac\u8d77\u4e0a\u8eab\uff0c\u4f38\u53f3\u624b\u63e1\u4f4f\u9a6c\u6a59\uff0c\u624b\u81c2\u5fae\u4e00\u8fd0\u52b2\uff0c\u8eab\u5b50\u5012\u7ffb\u4e0a\u4e86\u9a6c\u80cc\uff0c\u8bf4\u9053\uff1a\u201c\u62ff\u4e86\u90a3\u67c4\u5200\uff01\u201d\u5357\u5c0f\u59d0\u5931\u9b42\u843d\u9b44\u822c\u62ac\u4e86\u5b9d\u5200\u3002\u82d7\u4eba\u51e4\u4f38\u5de6\u624b\u5728\u5979\u8170\u95f4\u8f7b\u8f7b\u4e00\u5e26\uff0c\u5c06\u5979\u63d0\u4e0a\u4e86\u9a6c\u80cc\u3002\u4e24\u4eba\u5e76\u9a91\uff0c\u6162\u6162\u56de\u5230\u5c0f\u5ba2\u5e97\u4e2d\u3002 \u3000\u3000\u82d7\u4eba\u51e4\u8fd0\u8db3\u529f\u52b2\uff0c\u624d\u6ca1\u5728\u9a6c\u4e0a\u660f\u6655\u8fc7\u53bb\uff0c\u4f46\u4e00\u5230\u5e97\u524d\uff0c\u518d\u4e5f\u652f\u6301\u4e0d\u4f4f\uff0c\u7ffb\u8eab\u843d\u5728\u96ea\u5730\u3002\u4e24\u540d\u5e97\u5c0f\u4e8c\u5954\u51fa\u6765\u6276\u4e86\u4ed6\u8fdb\u53bb\u3002 \u3000\u3000\u82d7\u4eba\u51e4\u5377\u8d77\u88e4\u811a\uff0c\u5c06\u4e24\u679a\u6bd2\u9488\u62d4\u4e86\u51fa\u6765\uff0c\u4ed6\u53eb\u5e97\u5c0f\u4e8c\u66ff\u4ed6\u5438\u51fa\u817f\u4e0a\u6bd2\u8840\uff0c\u867d\u7136\u8bb8\u4ee5\u91cd\u916c\uff0c\u5e97\u5c0f\u4e8c\u4ecd\u662f\u5bb3\u6015\u8e0c\u8e87\u3002 \u3000\u3000\u5357\u5c0f\u59d0\u5c06\u67d4\u5ae9\u7684\u5c0f\u53e3\u51d1\u5728\u4ed6\u817f\u4e0a\uff0c\u5c06\u6bd2\u8840\u4e00\u53e3\u4e00\u53e3\u5730\u5438\u51fa\u6765\u3002\u5979\u5f88\u6e05\u695a\u5730\u77e5\u9053\uff1a\u4e24\u4eba\u7684\u808c\u80a4\u8fd9\u4e48\u4e00\u63a5\u89e6\uff0c\u81ea\u5df1\u5c31\u662f\u4ed6\u7684\u4eba\u4e86\u3002\u4ed6\u662f\u5927\u76d7\u4e5f\u597d\uff0c\u662f\u5267\u8d3c\u4e5f\u597d\uff0c\u518d\u4e5f\u6ca1\u7b2c\u4e8c\u6761\u8def\uff0c\u5979\u5df2\u51b3\u5fc3\u8ddf\u7740\u4ed6\u3002 \u3000\u3000\u82d7\u4eba\u51e4\u4e5f\u77e5\u9053\uff1a\u8fd9\u51e0\u53e3\u6bd2\u8840\u4e00\u5438\uff0c\u81ea\u5df1\u65e0\u7275\u65e0\u6302\u3001\u7eb5\u6a2a\u6c5f\u6e56\u7684\u65e5\u5b50\u662f\u5b8c\u7ed3\u5566\u3002\u4ed6\u987b\u5f97\u7ec8\u8eab\u4fdd\u62a4\u8fd9\u5973\u5b50\u3002\u8fd9\u4e2a\u5343\u91d1\u5c0f\u59d0\u7684\u5feb\u4e50\u548c\u5fe7\u6101\uff0c\u4ece\u6b64\u5c31\u662f\u81ea\u5df1\u7684\u5feb\u4e50\u4e0e\u5fe7\u6101\u3002 \u3000\u3000\u4ed6\u53ca\u65f6\u670d\u4e86\u848b\u8c03\u4faf\u7684\u89e3\u836f\uff0c\u6027\u547d\u662f\u53ef\u4fdd\u7684\u4e86\uff0c\u4f46\u7edd\u95e8\u6bd2\u9488\u975e\u540c\u5c0f\u53ef\uff0c\u4e0d\u8c03\u6cbb\u5341\u5929\u534a\u6708\uff0c\u4e24\u817f\u65e0\u6cd5\u4f7f\u5524\u3002\u4ed6\u53d6\u51fa\u94f6\u5b50\uff0c\u547d\u5e97\u5c0f\u4e8c\u53bb\u6536\u6b93\u4e86\u5357\u5c0f\u59d0\u7684\u7236\u4eb2\uff0c\u4e5f\u6536\u6b93\u4e86\u90a3\u4e94\u4e2a\u4f01\u56fe\u62a2\u593a\u5b9d\u5200\u7684\u8c6a\u5ba2\u3002 \u3000\u3000\u5357\u5c0f\u59d0\u4e0e\u4ed6\u540c\u4f4f\u5728\u4e00\u95f4\u623f\u91cc\uff0c\u670d\u4f8d\u4ed6\u3001\u966a\u4f34\u4ed6\u3002\u7ecf\u8fc7\u4e86\u8fd9\u573a\u60ca\u5fc3\u52a8\u9b44\u7684\u53d8\u6545\uff0c\u5357\u5c0f\u59d0\u4e00\u95ed\u773c\u5c31\u770b\u5230\u96ea\u5730\u91cc\u90a3\u573a\u60e8\u5267\uff0c\u770b\u5230\u7236\u4eb2\u88ab\u8d3c\u4eba\u6740\u6b7b\uff0c\u770b\u5230\u81ea\u5df1\u624b\u4e2d\u7684\u5b9d\u5200\u6389\u4e0b\u53bb\uff0c\u6740\u6b7b\u4e86\u4e00\u4e2a\u4eba\u3002\u5979\u5e38\u5e38\u5728\u7761\u68a6\u4e2d\u54ed\u9192\u3002 \u3000\u3000\u82d7\u4eba\u51e4\u4e0d\u559c\u8a00\u8f9e\uff0c\u4ece\u6765\u4e0d\u8bf4\u4e00\u53e5\u5b89\u6170\u7684\u8a00\u8bed\u3002\u4f46\u5357\u5c0f\u59d0\u53ea\u8981\u89c1\u5230\u4ed6\u6c89\u9759\u9547\u5b9a\u7684\u8138\u8272\u3001\u540c\u60c5\u7684\u773c\u5149\uff0c\u5c31\u4e0d\u518d\u5bb3\u6015\u4e86\u3002 \u3000\u3000\u5979\u8ddf\u4ed6\u8bf4\uff0c\u5979\u7236\u4eb2\u5357\u4ec1\u901a\u5728\u6c5f\u5357\u505a\u5b98\uff0c\u6349\u5230\u4e86\u4e00\u540d\u6c5f\u6d0b\u5927\u76d7\uff0c\u5f97\u5230\u8fd9\u67c4\u201c\u51b7\u6708\u5b9d\u5200\u201d\u3002\u4e0d\u4e45\u5357\u4ec1\u901a\u8c03\u8865\u4eac\u5b98\uff0c\u4ed6\u8981\u5c06\u5b9d\u5200\u732e\u7ed9\u5f53\u9053\uff0c\u6ee1\u5fc3\u60f3\u98de\u9ec4\u817e\u8fbe\uff0c\u4e0d\u6599\u5374\u56e0\u6b64\u6789\u81ea\u9001\u4e86\u6027\u547d\u3002 \u3000\u3000\u82d7\u4eba\u51e4\u95ee\u8d77\u90a3\u6c5f\u6d0b\u5927\u76d7\u7684\u59d3\u540d\uff0c\u5357\u5c0f\u59d0\u5374\u8bf4\u4e0d\u4e0a\u6765\uff0c\u5979\u53ea\u77e5\u9053\u8fd9\u5927\u76d7\u662f\u5728\u72f1\u4e2d\u75c5\u6b7b\u7684\u3002\u4ed6\u60f3\uff1a\u4e0d\u77e5\u662f\u54ea\u4e00\u4e2a\u597d\u6c49\uff0c\u4e0d\u660e\u4e0d\u767d\u5730\u53c8\u7ed9\u5bb3\u6b7b\u4e86\u3002\u90a3\u4e94\u540d\u593a\u5200\u7684\u8c6a\u5ba2\uff0c\u5fc5\u5b9a\u8bc6\u5f97\u8fd9\u4e2a\u5927\u76d7\uff0c\u77e5\u9053\u5927\u76d7\u6709\u4e00\u67c4\u5b9d\u5200\uff0c\u4e8e\u662f\u4e00\u8def\u8ddf\u8e2a\u4e0b\u6765\u3002 \u3000\u3000\u7b2c\u4e94\u5929\u665a\u4e0a\uff0c\u5357\u5c0f\u59d0\u7aef\u4e86\u4e00\u7897\u836f\u7ed9\u82d7\u4eba\u51e4\u559d\u3002\u4ed6\u6b63\u8981\u4f38\u4e0b\u53bb\u63a5\uff0c\u5ffd\u542c\u5f97\u7a97\u5916\u7c41\u7c41\u51e0\u4e0b\u54cd\u58f0\u3002\u4ed6\u4e0d\u52a8\u58f0\u8272\uff0c\u63a5\u8fc7\u836f\u7897\u6765\u6162\u6162\u559d\u4e86\u4e0b\u53bb\u3002\u4ed6\u77e5\u7a97\u5916\u6709\u4eba\u7aa5\u63a2\uff0c\u4f46\u9707\u4e8e\u81ea\u5df1\u7684\u5a01\u540d\uff0c\u4e0d\u6562\u8d38\u7136\u52a8\u624b\u3002\u6697\u81ea\u76d8\u7b97\uff1a\u201c\u8fd9\u591a\u534a\u662f\u593a\u5200\u4e94\u4eba\u7684\u540e\u63f4\uff0c\u518d\u8fc7\u4e94\u516d\u65e5\uff0c\u90a3\u5c31\u4e0d\u8db3\u4e3a\u60e7\uff0c\u82e6\u4e8e\u8fd9\u51e0\u65e5\u4e24\u817f\u5140\u81ea\u9178\u8f6f\u65e0\u529b\uff0c\u82e5\u6709\u5f3a\u654c\u5230\u6765\uff0c\u5012\u662f\u4e0d\u6613\u5bf9\u4ed8\u3002\u201d \u3000\u3000\u53ea\u542c\u5f97\u62cd\u7684\u4e00\u58f0\uff0c\u767d\u5149\u95ea\u52a8\uff0c\u7a97\u5916\u63b7\u8fdb\u4e00\u67c4\u5315\u9996\uff0c\u9489\u5728\u684c\u4e0a\uff0c\u5fae\u5fae\u98a4\u52a8\u3002 \u3000\u3000\u5315\u9996\u4e0a\u9644\u7740\u4e00\u5f20\u767d\u7eb8\u3002\u5357\u5c0f\u59d0\u201c\u554a\u201d\u7684\u4e00\u58f0\u60ca\u547c\uff0c\u5954\u5230\u4ed6\u8eab\u8fb9\u3002 \u3000\u3000\u82d7\u4eba\u51e4\u7761\u5728\u7095\u4e0a\uff0c\u4f38\u624b\u591f\u4e0d\u7740\u5315\u9996\u3002\u4ed6\u51b7\u7b11\u4e00\u58f0\uff0c\u5de6\u638c\u5728\u684c\u5b50\u8fb9\u7f18\u4e00\u62cd\uff0c\u5315\u9996\u672c\u6765\u63d2\u8fdb\u684c\u9762\u6570\u5bf8\uff0c\u8fd9\u4e00\u62cd\u4e4b\u4e0b\uff0c\u767b\u65f6\u8df3\u8d77\uff0c\u5f39\u8d77\u5c3a\u8bb8\uff0c\u8dcc\u5728\u4ed6\u624b\u65c1\uff0c\u7a97\u5916\u6709\u4eba\u8d5e\u9053\uff1a\u201c\u91d1\u9762\u4f5b\u540d\u4e0d\u865a\u4f20\uff0c\u679c\u7136\u4e86\u5f97\uff01\u201d\u811a\u6b65\u8f7b\u54cd\uff0c\u4e24\u4e2a\u4eba\u8d8a\u5899\u51fa\u5916\u3002\u63a5\u7740\u9a6c\u8e44\u54cd\u8d77\uff0c\u4e24\u9a91\u9a6c\u8fdc\u8fdc\u53bb\u4e86\u3002 \u3000\u3000\u82d7\u4eba\u51e4\u62ff\u8d77\u767d\u7eb8\uff0c\u89c1\u5199\u7740\u4e00\u884c\u5b57\u9053\uff1a\u201c\u9102\u5317\u9524\u5146\u6587\u3001\u9524\u5146\u82f1\u3001\u9524\u5146\u80fd\u987f\u9996\u767e\u62dc\u3002\u201d \u3000\u3000\u5357\u5c0f\u59d0\u89c1\u4ed6\u8138\u8272\u6728\u7136\uff0c\u4e0d\u77e5\u662f\u5fe7\u662f\u6012\uff0c\u95ee\u9053\uff1a\u201c\u662f\u654c\u4eba\u627e\u4e0a\u6765\u4e86\u5417\uff1f\u201d \u3000\u3000\u82d7\u4eba\u51e4\u70b9\u70b9\u5934\u3002\u5357\u5c0f\u59d0\u9053\uff1a\u201c\u4f60\u5728\u684c\u4e0a\u8fd9\u4e48\u4e00\u62cd\uff0c\u4ed6\u4eec\u5c31\u5413\u8d70\u4e86\uff0c\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u82d7\u4eba\u51e4\u6447\u5934\u9053\uff1a\u201c\u4ed6\u4eec\u662f\u6765\u9001\u4fe1\u7684\u3002\u201d \u3000\u3000\u5357\u5c0f\u59d0\u9053\uff1a\u201c\u4f60\u8fd9\u4e48\u5927\u672c\u4e8b\uff0c\u4ed6\u4eec\u4e00\u5b9a\u5bb3\u6015\u3002\u201d\u82d7\u4eba\u51e4\u4e0d\u8bed\uff0c\u5fc3\u60f3\uff1a\u201c\u9102\u5317\u9b3c\u89c1\u6101\u9524\u6c0f\u4e09\u5144\u5f1f\uff0c\u65e2\u7136\u627e\u4e0a\u6765\u4e86\uff0c\u5c31\u4e0d\u5bb3\u6015\u3002\u201d\u5357\u5c0f\u59d0\u8bdd\u662f\u8fd9\u4e48\u8bf4\uff0c\u5fc3\u4e2d\u4e5f\u81ea\u62c5\u5fe7\uff0c\u8fc7\u4e86\u534a\u664c\uff0c\u8f7b\u58f0\u8bf4\u9053\uff1a\u201c\u5927\u54e5\uff0c\u54b1\u4eec\u73b0\u4e0b\u9a91\u9a6c\u8d70\u4e86\u5427\uff0c\u4ed6\u4eec\u627e\u4e0d\u7740\u7684\u3002\u201d\u82d7\u4eba\u51e4\u6447\u6447\u5934\uff0c\u9ed8\u7136\u4e0d\u8bed\u3002 \u3000\u3000\u6253\u904d\u5929\u4e0b\u65e0\u654c\u624b\u91d1\u9762\u4f5b\u82d7\u4eba\u51e4\uff0c\u600e\u80fd\u5728\u654c\u4eba\u9762\u524d\u9003\u8d70\uff1f\u5c31\u7b97\u4e3a\u4e86\u5357\u5c0f\u59d0\u800c\u6682\u4e14\u5fcd\u8fb1\u8eb2\u907f\uff0c\u4f46\u9b3c\u89c1\u6101\u9524\u6c0f\u4e09\u5144\u5f1f\u53c8\u600e\u80fd\u8ba9\u4eba\u8eb2\u5f97\u5f00\uff1f\u8fd9\u4e9b\u4e8b\u5357\u5c0f\u59d0\u662f\u4e0d\u4f1a\u61c2\u7684\u3002\u4ed6\u5411\u6765\u4e0d\u7231\u591a\u8bf4\u8bdd\uff0c\u51b5\u4e14\uff0c\u8fd9\u4e9b\u4e8b\u53c8\u4f55\u5fc5\u8ddf\u5979\u591a\u8bf4\u3002 \u3000\u3000\u8fd9\u4e00\u665a\u5357\u5c0f\u59d0\u7ffb\u6765\u8986\u53bb\u5730\u7761\u4e0d\u5b89\u7a33\u3002\u5979\u5df2\u5728\u5168\u5fc3\u5168\u610f\u5730\u5173\u6000\u8fd9\u4e2a\u7c97\u624b\u5927\u811a\u7684\u4e61\u4e0b\u4eba\uff0c\u4f46\u82d7\u4eba\u51e4\u5374\u7761\u5f97\u5f88\u6c89\u3002 \u3000\u3000\u53ea\u4e0d\u8fc7\u4ed6\u505a\u4e86\u4e00\u4e2a\u68a6\uff0c\u68a6\u89c1\u4e00\u9876\u82b1\u8f7f\uff0c\u4e00\u961f\u5439\u9f13\u624b\uff0c\u53c8\u68a6\u89c1\u4e00\u4e2a\u5934\u4e0a\u62ab\u7740\u7ea2\u5dfe\u7684\u65b0\u5a18\u5b50\u3002\u90a3\u662f\u5f88\u4e45\u5f88\u4e45\u4ee5\u524d\u7ae5\u5e74\u65f6\u77a7\u89c1\u8fc7\u7684\uff0c\u4ed6\u65e9\u5df2\u5fd8\u4e86\uff0c\u8fd9\u65f6\u5374\u5ffd\u7136\u68a6\u5230\u4e86\u3002\u9192\u6765\u7684\u65f6\u5019\uff0c\u4f3c\u4e4e\u8fd8\u9690\u9690\u542c\u5230\u68a6\u4e2d\u9f13\u4e50\u7684\u58f0\u97f3\u3002\u9eef\u6de1\u7684\u6447\u66f3\u7684\u70db\u5149\uff0c\u7167\u5728\u65c1\u8fb9\u5e8a\u4e0a\u5357\u5c0f\u59d0\u50cf\u8299\u84c9\u82b1\u90a3\u6837\u67d4\u548c\u3001\u90a3\u6837\u5a07\u8273\u7684\u8138\u4e0a\u3002\u8fd9\u6735\u82b1\u5374\u4e0d\u5728\u7b11\u3002\u5979\u7761\u7740\u7684\u65f6\u5019\uff0c\u4e5f\u662f\u6050\u60e7\uff0c\u4e5f\u662f\u5728\u611f\u5230\u75db\u82e6\u3002\u5979\u8138\u4e0a\u6709\u70db\u5149\uff0c\u5374\u6709\u66f4\u591a\u7684\u9634\u5f71\u3002 \u3000\u3000\u6b21\u65e5\u6e05\u6668\uff0c\u82d7\u4eba\u51e4\u547d\u5e97\u5c0f\u4e8c\u505a\u4e00\u5927\u7897\u9762\u5403\u4e86\uff0c\u7aef\u5f20\u6905\u5b50\uff0c\u5750\u5728\u5385\u4e2d\uff0c\u51b7\u6708\u5b9d\u5200\u653e\u5728\u8eab\u65c1\u3002\u4ed6\u751f\u5e73\u4e0d\u7231\u4e8b\u5148\u7b79\u5212\uff0c\u56e0\u4e3a\u9884\u6599\u7684\u4e8b\u513f\u591a\u534a\u4f5c\u4e0d\u4e86\u51c6\uff0c\u5b81\u53ef\u968f\u673a\u5e94\u53d8\u3002\u5357\u5c0f\u59d0\u89c1\u4e86\u4ed6\u7684\u795e\u60c5\uff0c\u5fc3\u4e2d\u5f88\u662f\u5bb3\u6015\uff0c\u95ee\u4e86\u4ed6\u51e0\u53e5\uff0c\u82d7\u4eba\u51e4\u5e76\u4e0d\u56de\u7b54\uff0c\u4e8e\u662f\u5979\u5c31\u4e0d\u6562\u518d\u95ee\u3002 \u3000\u3000\u8fb0\u724c\u65f6\u5206\uff0c\u9a6c\u8e44\u58f0\u54cd\uff0c\u4e09\u4e58\u9a6c\u5728\u5ba2\u5e97\u524d\u505c\u4f4f\uff0c\u8fdb\u6765\u4e86\u4e09\u4e2a\u5ba2\u4eba\u3002\u5ba2\u5e97\u4e2d\u4eba\u89c1\u4e86\u8fd9\u4e09\u4eba\u7684\u6253\u626e\uff0c\u90fd\u662f\u5413\u4e86\u4e00\u8df3\u3002\u539f\u6765\u4e09\u4eba\u90fd\u8eab\u7a7f\u767d\u8272\u7c97\u9ebb\u5e03\u8863\u670d\uff0c\u767d\u5e3d\u767d\u978b\uff0c\u8863\u670d\u8fb9\u4e0a\u9732\u7740\u6bdb\u5934\uff0c\u7adf\u662f\u521a\u6b7b\u4e86\u7236\u6bcd\u7684\u5b5d\u5b50\u670d\u8272\u3002\u4f46\u4e09\u8eab\u5b5d\u670d\u5df2\u7a7f\u5f97\u534a\u65b0\u4e0d\u65e7\uff0c\u82e5\u8bf4\u670d\u7684\u70ed\u5b5d\uff0c\u5374\u53c8\u4e0d\u50cf\u3002 \u3000\u3000\u82d7\u4eba\u51e4\u77e5\u9053\u9102\u5317\u9b3c\u89c1\u6101\u953a\u95e8\u96c4\u9738\u8346\u8944\uff0c\u6b66\u529f\u5b9e\u6709\u72ec\u5230\u7684\u9020\u8be3\uff0c\u90a3\u8865\u9505\u5320\u662f\u9524\u6c0f\u95e8\u5f92\uff0c\u6b66\u827a\u5df2\u81ea\u4e0d\u5f31\uff0c\u773c\u4e0b\u9524\u6c0f\u4e09\u5144\u5f1f\u4eb2\u81ea\u5230\u6765\uff0c\u6b64\u4e8b\u5f53\u771f\u751a\u662f\u68d8\u624b\u3002 \u3000\u3000\u53ea\u89c1\u4e09\u4eba\u4e00\u822c\u7684\u76f8\u8c8c\uff0c\u90fd\u662f\u8138\u8272\u60e8\u767d\uff0c\u9f3b\u5b50\u53c8\u6241\u53c8\u5927\uff0c\u9f3b\u5b54\u671d\u5929\uff0c\u53ea\u662f\u51ed\u80e1\u5b50\u5206\u522b\u5e74\u7eaa\uff0c\u6599\u6765\u7070\u767d\u5c0f\u80e1\u5b50\u7684\u662f\u5927\u54e5\u953a\u5146\u6587\uff0c\u9ed1\u80e1\u5b50\u7684\u662f\u4e8c\u54e5\u953a\u5146\u82f1\uff0c\u6ca1\u7559\u80e1\u5b50\u7684\u662f\u4e09\u5f1f\u953a\u5146\u80fd\uff0c\u4e09\u4eba\u8fdb\u6765\u65f6\u811a\u6b65\u8f7b\u98d8\u98d8\u7684\u5b9b\u5982\u8db3\u4e0d\u70b9\u5730\uff0c\u679c\u7136\u662f\u52b2\u654c\u5230\u4e86\u3002\u82d7\u4eba\u51e4\u4e00\u751f\u4e4b\u4e2d\uff0c\u654c\u4eba\u6108\u5f3a\uff0c\u7cbe\u795e\u6108\u632f\uff0c\u4e00\u89c1\u4e09\u4eba\u58f0\u52bf\u4e0d\u540c\u51e1\u4fd7\uff0c\u4e0d\u7531\u5f97\u5168\u8eab\u9aa8\u9abc\u8f7b\u8f7b\u4f5c\u54cd\u3002 \u3000\u3000\u953a\u6c0f\u4e09\u5144\u5f1f\u4e0a\u524d\u540c\u65f6\u4e00\u63d6\u5230\u5730\uff0c\u9f50\u58f0\u8bf4\u9053\uff1a\u201c\u82d7\u5927\u4fa0\u8bf7\u4e86\u3002\u201d\u82d7\u4eba\u51e4\u62f1\u624b\u8fd8\u793c\uff0c\u8bf4\u9053\uff1a\u201c\u8bf7\u4e86\uff0c\u6055\u5728\u4e0b\u817f\u4e0a\u6709\u4f24\uff0c\u4e0d\u80fd\u8d77\u7acb\u3002\u201d\u953a\u5146\u6587\u9053\uff1a\u201c\u82d7\u5927\u4fa0\u4f60\u5bb6\u817f\u4e0a\u4e0d\u4fbf\uff0c\u539f\u672c\u4e0d\u8be5\u6253\u6270\uff0c\u53ea\u662f\u6740\u5f92\u4e4b\u4ec7\uff0c\u4e0d\u80fd\u4e0d\u62a5\uff0c\u8bf7\u82d7\u5927\u4fa0\u4f60\u5bb6\u6055\u7f6a\u3002\u201d\u4ed6\u201c\u4f60\u5bb6\uff0c\u4f60\u5bb6\u201d\uff0c\u6ee1\u53e3\u6e56\u5317\u571f\u8154\uff0c\u82d7\u4eba\u51e4\u70b9\u70b9\u5934\uff0c\u4e0d\u518d\u7b54\u8bdd\u3002 \u3000\u3000\u953a\u5146\u6587\u9053\uff1a\u201c\u82d7\u5927\u4fa0\u5a01\u9707\u5929\u4e0b\uff0c\u6211\u4eec\u4e09\u5144\u5f1f\u5355\u6253\u72ec\u6597\uff0c\u975e\u4f60\u5bb6\u654c\u624b\uff0c\u8001\u4e8c\u3001\u8001\u4e09\uff0c\u54b1\u54e5\u513f\u4e00\u9f50\u4e0a\u554a\uff01\u201d\u953a\u5146\u82f1\u3001\u953a\u5146\u80fd\u602a\u58f0\u7b54\u5e94\uff0c\u53eb\u9053\uff1a\u201c\u8001\u5927\uff0c\u54b1\u54e5\u513f\u4e00\u9f50\u4e0a\u554a\uff01\u201d\u8fd9\u4e09\u5144\u5f1f\u662f\u6b66\u6797\u4e2d\u7684\u6210\u540d\u4eba\u7269\uff0c\u867d\u7136\u602a\u58f0\u602a\u6c14\uff0c\u602a\u6a21\u602a\u6837\uff0c\u5728\u6c5f\u6e56\u4e0a\u5374\u662f\u8f88\u4efd\u751a\u9ad8\uff0c\u884c\u4e8b\u6301\u91cd\uff0c\u6b66\u529f\u53c8\u5f3a\uff0c\u56e0\u6b64\u4e0a\u5728\u4e24\u6e56\u4e00\u5e26\u5df2\u95ef\u4e0b\u6781\u5927\u7684\u57fa\u4e1a\u3002\u4e09\u4eba\u602a\u58f0\u4e00\u4f5c\uff0c\u545b\u5577\u5577\u54cd\u58f0\u4e0d\u7edd\uff0c\u5404\u4ece\u8eab\u8fb9\u53d6\u51fa\u4e00\u5bf9\u5224\u5b98\u7b14\u3002 \u3000\u3000\u5ba2\u5e97\u4e2d\u4f19\u4f34\u5ba2\u4eba\u89c1\u8fd9\u4e09\u4eba\u5230\u6765\uff0c\u5df2\u77e5\u4e0d\u5999\uff0c\u8fd9\u65f6\u89c1\u53d6\u51fa\u5175\u5203\uff0c\u4eba\u4eba\u8fdc\u907f\uff0c\u767b\u65f6\u5927\u5385\u4e0a\u7a7a\u8361\u8361\u7684\u4e00\u7247\u3002 \u3000\u3000\u5357\u5c0f\u59d0\u5173\u5fc3\u82d7\u4eba\u51e4\u5b89\u5371\uff0c\u5374\u7559\u5728\u5385\u89d2\u4e4b\u4e2d\u3002\u82d7\u4eba\u51e4\u89c1\u5979\u4e00\u4e2a\u5a07\u602f\u5f31\u5973\uff0c\u5c45\u7136\u6709\u6b64\u80c6\u91cf\uff0c\u5fc3\u4e2d\u5927\u662f\u559c\u6170\u3002\u53ea\u56e0\u5357\u5c0f\u59d0\u5728\u5385\u89d2\u8fd9\u4e48\u4e00\u7ad9\uff0c\u82d7\u4eba\u51e4\u81ea\u6b64\u5bf9\u5979\u751f\u6b7b\u4ee5\u4e4b\uff0c\u503e\u5fc3\u76f8\u7231\uff0c\u5f53\u4e0b\u5411\u5979\u5fae\u5fae\u4e00\u7b11\uff0c\u62bd\u51fa\u51b7\u6708\u5b9d\u5200\u3002 \u3000\u3000\u953a\u6c0f\u5144\u5f1f\u89c1\u90a3\u5200\u9752\u5149\u95ea\u52a8\uff0c\u5bd2\u6c14\u903c\u4eba\uff0c\u540c\u58f0\u8d5e\u9053\uff1a\u201c\u597d\u5200\uff01\u201d \u3000\u3000\u4e09\u5144\u5f1f\u9f50\u58f0\u602a\u53eb\u3002\u953a\u5146\u6587\u53cc\u7b14\u5f53\u80f8\u76f4\u6307\uff0c\u5146\u82f1\u653b\u5de6\uff0c\u5146\u80fd\u88ad\u53f3\u3002\u82d7\u4eba\u51e4\u7aef\u5750\u6905\u4e2d\uff0c\u6a2a\u5200\u4e0d\u52a8\uff0c\u4f8d\u516d\u679d\u9554\u94c1\u5224\u5b98\u7b14\u7684\u7b14\u5c16\u582a\u582a\u70b9\u5230\u8eab\u8fb9\uff0c\u7a81\u7136\u5b9d\u5200\u4e00\u6325\uff0c\u547c\u547c\u98ce\u54cd\uff0c\u5411\u4e09\u4eba\u5404\u780d\u4e00\u5200\u3002\u953a\u6c0f\u4e09\u5144\u5f1f\u679c\u7136\u8eab\u8d1f\u7edd\u827a\uff0c\u89c1\u4ed6\u5200\u52bf\u6765\u5f97\u5947\u7279\uff0c\u5404\u81ea\u8eab\u5f62\u98d8\u52a8\uff0c\u8ba9\u4e86\u5f00\u53bb\u3002\u4ed6\u4eec\u53ea\u77e5\u82d7\u5bb6\u5251\u6cd5\u72ec\u6b65\u5929\u4e0b\uff0c\u4e0d\u6599\u4ed6\u5200\u6cd5\u7ade\u4e5f\u5982\u6b64\u7cbe\u5947\u3002\u82d7\u4eba\u51e4\u6b64\u65f6\u6240\u7528\u662f\u80e1\u4e00\u5200\u6240\u6388\u7684\u80e1\u5bb6\u5200\u6cd5\uff0c\u53d8\u5316\u5965\u5999\uff0c\u7075\u52a8\u7edd\u4f26\uff0c\u5c31\u53ea\u5403\u4e8f\u5728\u8eab\u5b50\u4e0d\u80fd\u79fb\u52a8\uff0c\u4e00\u5200\u780d\u51fa\uff0c\u96be\u4ee5\u8fde\u7eed\u8ffd\u51fb\u3002 \u3000\u3000\u56db\u4eba\u4e00\u52a8\u4e0a\u624b\uff0c\u5927\u5385\u4e2d\u5200\u5149\u7b14\u5f71\uff0c\u767b\u65f6\u6597\u5f97\u51f6\u9669\u5f02\u5e38\u3002\u953a\u6c0f\u4e09\u5144\u5f1f\u8f7b\u529f\u751a\u662f\u4e86\u5f97\uff0c\u4e09\u4eba\u5206\u8fdb\u5408\u51fb\uff0c\u6b64\u6765\u5f7c\u5f80\uff0c\u516d\u679d\u5224\u5b98\u7b14\u5b9b\u5982\u5341\u4e8c\u679d\u76f8\u4f3c\u3002\u82d7\u4eba\u51e4\u4f7f\u5f00\u5200\u6cd5\uff0c\u653b\u62d2\u524a\u780d\uff0c\u4e1d\u6beb\u4e0d\u843d\u4e0b\u98ce\u3002\u4ed6\u60f3\u4eca\u65e5\u4e4b\u6597\u52a1\u987b\u731b\u4e0b\u6740\u624b\uff0c\u91cd\u4f24\u4ed6\u5144\u5f1f\u4e09\u4eba\uff0c\u5426\u5219\u81ea\u5df1\u4e0e\u5357\u5c0f\u59d0\u6027\u547d\u96be\u4ee5\u5468\u5168\u3002\u53ea\u662f\u7d20\u77e5\u953a\u6c0f\u4e09\u5144\u5f1f\u5b89\u4efd\u5b88\u5df1\uff0c\u5e76\u65e0\u6b79\u884c\u52a3\u8ff9\uff0c\u6c5f\u6e56\u4e0a\u58f0\u540d\u751a\u597d\uff0c\u5374\u4e0d\u5fc5\u53d6\u4ed6\u4eec\u6027\u547d\u3002\u3000\u773c\u89c1\u4e09\u5144\u5f1f\u7684\u62db\u6570\u6108\u6765\u6108\u7d27\uff0c\u6bcf\u4e00\u62db\u90fd\u70b9\u6253\u4ed6\u4e0a\u8eab\u5927\u7a74\uff0c\u53ea\u8981\u7a0d\u4e00\u758f\u795e\uff0c\u4e0d\u4f46\u4e00\u4e16\u82f1\u540d\u4ed8\u4e8e\u6d41\u6c34\uff0c\u8fde\u8fd9\u5a07\u8273\u6e29\u67d4\u7684\u5357\u5c0f\u59d0\u4e5f\u5f97\u843d\u5165\u654c\u624b\u53d7\u82e6\u3002\u60f3\u5230\u6b64\u5904\uff0c\u5200\u62db\u52a0\u6c89\uff0c\u731b\u529b\u780d\u524a\u3002 \u3000\u3000\u4e09\u5144\u5f1f\u6015\u4ed6\u529b\u5927\u5200\u5229\uff0c\u4e0d\u6562\u8ba9\u5175\u5203\u7ed9\u4ed6\u5b9d\u5200\u78b0\u5230\u4e86\uff0c\u56f4\u653b\u7684\u5708\u5b50\u6e10\u6e10\u653e\u8fdc\u3002 \u3000\u3000\u953a\u5146\u82f1\u773c\u89c1\u96be\u4ee5\u53d6\u80dc\uff0c\u7a81\u7136\u4e00\u58f0\u602a\u53eb\uff0c\u8eab\u5b50\u659c\u6251\uff0c\u7740\u5730\u6eda\u53bb\uff0c\u7adf\u5230\u82d7\u4eba\u51e4\u80cc\u540e\u653b\u4ed6\u4e0b\u76d8\u3002\u8fd9\u4e00\u7740\u751a\u662f\u9669\u6bd2\uff0c\u60f3\u82d7\u4eba\u51e4\u5750\u5728\u6905\u4e0a\u4e0d\u80fd\u8f6c\u52a8\uff0c\u654c\u4eba\u653b\u4ed6\u80cc\u540e\u6905\u811a\uff0c\u5982\u4f55\u62a4\u5b88\u5f97\u7740\uff1f\u953a\u5146\u82f1\u8fde\u653b\u6570\u62db\uff0c\u4e00\u7b14\u6a2a\u7838\uff0c\u5580\u7684\u4e00\u58f0\uff0c\u5c06\u6905\u811a\u6253\u65ad\u4e86\u4e00\u6839\u3002\u6905\u5b50\u4e00\u4fa7\uff0c\u82d7\u4eba\u98ce\u8eab\u5b50\u8ddf\u7740\u503e\u4fa7\u3002\u5357\u5c0f\u59d0\u201c\u554a\u201d\u7684\u4e00\u58f0\uff0c\u60ca\u547c\u51fa\u6765\u3002\u82d7\u4eba\u51e4\u5de6\u624b\u500f\u5730\u63a2\u51fa\uff0c\u5f80\u953a\u5146\u82f1\u8138\u4e0a\u6293\u53bb\u3002\u953a\u5146\u82f1\u5927\u60ca\uff0c\u6025\u5fd9\u6eda\u5f00\u76f8\u907f\uff0c\u53ea\u542c\u5f97\u5f53\u5f53\u4e24\u54cd\uff0c\u4ed6\u4e0e\u953a\u5146\u80fd\u624b\u4e2d\u7684\u5224\u5b98\u7b14\u5df2\u5404\u6709\u4e00\u6280\u88ab\u5b9d\u5200\u524a\u65ad\uff0c\u953a\u5146\u6587\u80a9\u5934\u5267\u75db\uff0c\u5374\u88ab\u5200\u5203\u5212\u4e86\u4e00\u9053\u53e3\u5b50\u3002\u82d7\u4eba\u51e4\u4e00\u5200\u540c\u65f6\u653b\u903c\u4e09\u654c\uff0c\u8fd9\u4e00\u62db\u53eb\u505a\u201c\u4e91\u9f99\u4e09\u73b0\u201d\uff0c\u4e43\u662f\u80e1\u5bb6\u5200\u6cd5\u4e2d\u7684\u7cbe\u5999\u62db\u6570\u3002 \u3000\u3000\u953a\u6c0f\u4e09\u5144\u5f1f\u5404\u5c55\u8f7b\u529f\u8dc3\u5f00\uff0c\u4e09\u4eba\u4e92\u76f8\u671b\u4e86\u4e00\u773c\uff0c\u8138\u4e0a\u90fd\u6709\u60ca\u9a87\u4e4b\u8272\u3002\u953a\u5146\u82f1\u9053\uff1a\u201c\u8001\u5927\uff0c\u6302\u4e86\u5f69\u5566\uff1f\u201d\u953a\u5146\u6587\u9053\uff1a\u201c\u4e0d\u788d\u4e8b\u3002\u201d\u4ed6\u89c1\u82d7\u4eba\u51e4\u6905\u5b50\u659c\u503e\uff0c\u5750\u5f97\u6447\u6447\u6b32\u5760\uff0c\u5fc3\u60f3\u5982\u6b64\u826f\u673a\uff0c\u65e5\u540e\u518d\u96be\u76f8\u9022\uff0c\u53ea\u662f\u5fcc\u60ee\u4ed6\u5b9d\u5200\u950b\u5229\uff0c\u5200\u6cd5\u7cbe\u5947\uff0c\u4e8e\u662f\u62b1\u62f3\u8bf4\u9053\uff1a\u201c\u5175\u5203\u4e0a\u6211\u4e09\u5144\u5f1f\u4e0d\u662f\u654c\u624b\uff0c\u6211\u4eec\u518d\u9886\u6559\u4f60\u5bb6\u62f3\u62db\u638c\u6cd5\u3002\u201d\u8fd9\u8bdd\u513f\u8bf4\u5f97\u51a0\u5195\u5802\u7687\uff0c\u5374\u662f\u4e0d\u6000\u597d\u610f\uff0c\u662f\u8981\u654c\u4eba\u81ea\u53bb\u5176\u957f\u3002\u4ed6\u4e09\u4eba\u6b64\u6765\u4e58\u4eba\u4e4b\u5371\uff0c\u4e43\u662f\u4ec7\u6740\u62fc\u547d\uff0c\u5e76\u975e\u6bd4\u6b66\u8f83\u827a\uff0c\u8fd9\u756a\u8bf4\u8bdd\u82d7\u4eba\u51e4\u672c\u6765\u5927\u53ef\u4e0d\u5fc5\u7406\u4f1a\uff0c\u4f46\u4ed6\u827a\u9ad8\u4eba\u80c6\u5927\uff0c\u4e00\u58f0\u51b7\u7b11\uff0c\u5b9d\u5200\u5f52\u9798\uff0c\u70b9\u4e86\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff01\u201d \u3000\u3000\u4e09\u5144\u5f1f\u629b\u4e0b\u5224\u5b98\u7b14\uff0c\u8e66\u8df3\u7a9c\u8dc3\uff0c\u653b\u4e86\u4e0a\u6765\u3002\u4e09\u4eba\u6bcf\u4e00\u6b65\u90fd\u662f\u8df3\u8dc3\uff0c\u7adf\u65e0\u4e00\u6b65\u8e0f\u884c\uff0c\u82d7\u4eba\u51e4\u7684\u638c\u6cd5\u4f55\u7b49\u5a01\u731b\uff0c\u4e00\u7ecf\u65bd\u5c55\uff0c\u4e09\u5144\u5f1f\u6b3a\u4e0d\u8fd1\u516b\u5c3a\u4ee5\u5185\uff0c\u4e5f\u662f\u953a\u95e8\u6b66\u529f\u5353\u7136\u6210\u5bb6\uff0c\u5426\u5219\u5355\u662f\u7ed9\u4ed6\u638c\u529b\u4e00\u9707\uff0c\u5df2\u53d7\u91cd\u4f24\u3002\u953a\u5146\u82f1\u4eba\u6700\u673a\u7075\uff0c\u89c1\u4ed6\u6905\u811a\u65ad\u4e86\u4e00\u53ea\uff0c\u5df2\u96be\u5750\u7a33\uff0c\u5fc3\u60f3\u4f9d\u6837\u846b\u82a6\uff0c\u518d\u6253\u65ad\u4e00\u53ea\u6905\u811a\uff0c\u975e\u53eb\u4ed6\u6454\u5012\u4e0d\u53ef\uff0c\u5f53\u4e0b\u53c8\u4f7f\u51fa\u5730\u515a\u62f3\u6cd5\uff0c\u6eda\u5411\u82d7\u4eba\u51e4\u6905\u540e\uff0c\u731b\u5730\u53f3\u817f\u6a2a\u626b\uff0c\u5580\u5587\u4e00\u54cd\uff0c\u679c\u7136\u53c8\u5c06\u6905\u811a\u8e22\u65ad\u4e86\u4e00\u53ea\u3002 \u3000\u3000\u90a3\u6905\u5b50\u672c\u5df2\u503e\u4fa7\uff0c\u6b64\u65f6\u6025\u5411\u540e\u5012\u3002\u82d7\u4eba\u51e4\u4f38\u624b\u5728\u6905\u80cc\u4e00\u6309\uff0c\u4eba\u5df2\u8dc3\u8d77\u3002 \u3000\u3000\u4ed6\u607c\u6068\u953a\u5146\u82f1\u72e1\u8bc8\uff0c\u4ece\u534a\u7a7a\u4e2d\u5982\u5927\u9e70\u822c\u5411\u4ed6\u6251\u51fb\u4e0b\u6765\u3002\u953a\u5146\u82f1\u5413\u5f97\u5fc3\u60ca\u80c6\u6218\uff0c\u5927\u53eb\uff1a\u201c\u8001\u5927\uff0c\u8001\u4e09\uff01\u201d\u5146\u6587\u3001\u5146\u80fd\u53cc\u53cc\u4ece\u65c1\u6765\u6551\u3002\u82d7\u4eba\u51e4\u53cc\u638c\u53d1\u529b\uff0c\u5de6\u638c\u6253\u5728\u953a\u5146\u6587\u80a9\u5934\uff0c\u53f3\u638c\u62cd\u5728\u953a\u5146\u80fd\u80f8\u53e3\u3002\u4e24\u4eba\u7ecf\u53d7\u4e0d\u8d77\uff0c\u53cc\u53cc\u5411\u5916\u8dcc\u51fa\u3002\u953a\u5146\u82f1\u4e58\u673a\u51e0\u4e2a\u7ffb\u8eab\u9003\u51fa\u5385\u95e8\uff0c\u770b\u82d7\u4eba\u51e4\u65f6\uff0c\u4e5f\u5df1\u6454\u5012\u5728\u5730\u3002 \u3000\u3000\u4e09\u5144\u5f1f\u89c1\u4ed6\u5982\u6b64\u795e\u52c7\uff0c\u54ea\u6562\u8fdb\u6765\u518d\u6597\uff1f\u953a\u5146\u82f1\u77a5\u89c1\u5e97\u95e8\u65c1\u5806\u6ee1\u9a74\u9a6c\u7684\u8349\u6599\uff0c\u5fc3\u5ff5\u4e00\u52a8\uff0c\u53d6\u51fa\u706b\u6298\u70b9\u7740\u4e86\uff0c\u5c31\u5728\u8349\u6599\u4e0a\u4e00\u70b9\u3002\u90a3\u9ea6\u79c6\u5e72\u5f97\u900f\u516b\u767b\u65f6\u8d77\u706b\uff0c\u987a\u98ce\u70e7\u5411\u5e97\u5802\uff0c\u5ba2\u5e97\u4e2d\u5e97\u4f19\u5ba2\u5546\u4e00\u89c1\u706b\u5934\uff0c\u4e00\u9635\u5927\u4e71\uff0c\u7eb7\u7eb7\u5954\u51fa\u3002\u4e09\u5144\u5f1f\u62ff\u7740\u5224\u5b98\u7b14\u5728\u95e8\u53e3\u76d1\u89c6\uff0c\u53eb\u9053\uff1a\u201c\u8c01\u6551\u90a3\u574f\u4e86\u817f\u7684\u5ba2\u4eba\uff0c\u8001\u5b50\u6253\u5f00\u4ed6\u7684\u8111\u888b\u74dc\u5b50\uff01\u201d\u4f17\u4eba\u81ea\u9003\u6027\u547d\u4e0d\u53ca\uff0c\u53c8\u6709\u8c01\u6562\u53bb\u6551\u4eba\uff1f \u3000\u3000\u82d7\u4eba\u51e4\u89c1\u970e\u65f6\u4e4b\u95f4\u98ce\u52a9\u706b\u52bf\uff0c\u6d53\u70df\u706b\u820c\u5377\u8fdb\u5385\u6765\uff0c\u81ea\u5df1\u53cc\u817f\u4e0d\u80fd\u884c\u8d70\uff0c\u654c\u4eba\u53c8\u5b88\u5728\u95e8\u53e3\uff0c\u6697\u9053\uff1a\u201c\u96be\u9053\u6211\u4e00\u4e16\u82f1\u96c4\uff0c\u4eca\u65e5\u7adf\u6d3b\u6d3b\u70e7\u6b7b\u5728\u8fd9\u91cc\u4e0d\u6210\uff1f\u201d \u3000\u3000\u4e00\u8f6c\u773c\u89c1\u5357\u5c0f\u59d0\u5df2\u968f\u4f17\u4eba\u9003\u51fa\uff0c\u5fc3\u4e0b\u7565\u5bbd\uff0c\u706b\u5149\u4e2d\u53ea\u89c1\u5c4b\u89d2\u91cc\u653e\u7740\u4e00\u6346\u7c97\u7d22\uff0c\u6697\u53eb\uff1a\u201c\u5929\u53ef\u601c\u89c1\uff01\u201d\u722c\u7740\u8fc7\u53bb\u6296\u5f00\u7ef3\u7d22\uff0c\u5728\u624b\u81c2\u4e0a\u7ed5\u4e86\u5341\u6765\u5708\u3002 \u3000\u3000\u953a\u6c0f\u5144\u5f1f\u773c\u89c1\u70df\u706b\u56f4\u95e8\uff0c\u8fd9\u4e2a\u5f53\u4e16\u65e0\u654c\u7684\u82d7\u4eba\u51e4\u52bf\u5fc5\u846c\u8eab\u706b\u7a9f\uff0c\u4e09\u4eba\u5fc3\u4e2d\u5927\u559c\uff0c\u76f8\u89c6\u800c\u7b11\u3002 \u3000\u3000\u5357\u5c0f\u59d0\u5f53\u5371\u6025\u65f6\u593a\u95e8\u800c\u51fa\uff0c\u6b64\u65f6\u5374\u60f3\u8d77\u82d7\u4eba\u51e4\u5c1a\u5728\u5e97\u5185\uff0c\u4ed6\u4e3a\u76f8\u6551\u81ea\u5df1\u800c\u53d7\u4f24\u4e27\u751f\uff0c\u4e0d\u7981\u5927\u4e3a\u96be\u53d7\uff0c\u73e0\u6cea\u76c8\u7736\uff0c\u6b63\u81ea\u96be\u5fcd\uff0c\u731b\u542c\u5f97\u5e97\u5802\u5185\u4e00\u58f0\u5927\u559d\uff0c\u4e00\u6761\u7ef3\u7d22\u4ece\u706b\u7130\u4e2d\u7a9c\u5c06\u51fa\u6765\uff0c\u4e00\u7aef\u5df2\u5377\u4f4f\u95e8\u5916\u90a3\u682a\u5927\u94f6\u674f\u7684\u6811\u5e72\u3002\u63a5\u7740\u7ef3\u5b50\u4e00\u8361\uff0c\u82d7\u4eba\u51e4\u53c8\u9ad8\u53c8\u7626\u7684\u8eab\u8eaf\u5df2\u98de\u4e86\u51fa\u6765\u3002 \u3000\u3000\u4f17\u4eba\u89c1\u4ed6\u7a81\u4f3c\u98de\u5c06\u519b\u81ea\u5929\u800c\u964d\uff0c\u65e0\u4e0d\u9a87\u7136\u3002\u82d7\u4eba\u51e4\u5de6\u624b\u6293\u7ef3\uff0c\u8eab\u4e8e\u81ea\u7a7a\u4e2d\u5411\u953a\u6c0f\u4e09\u5144\u5f1f\u6251\u53bb\u3002\u4e09\u953a\u5413\u5f97\u9b42\u98de\u5929\u5916\uff0c\u5df2\u65e0\u6597\u5fd7\uff0c\u5f53\u5373\u53d1\u8db3\u5954\u9003\u3002\u4ed6\u4e09\u4eba\u8f7b\u529f\u867d\u9ad8\uff0c\u7ec8\u4e0d\u53ca\u82d7\u4eba\u51e4\u62c9\u7740\u7ef3\u5b50\u98de\u8361\u8fc5\u901f\uff0c\u88ab\u4ed6\u4f38\u51fa\u84b2\u6247\u5927\u7684\u4e0b\u638c\uff0c\u4e00\u63b7\u4e00\u6293\uff0c\u4e00\u6293\u4e00\u63b7\uff0c\u4e09\u5144\u5f1f\u90fd\u98de\u8eab\u800c\u5165\u706b\u5806\u3002\u603b\u7b97\u4e09\u4eba\u6b66\u529f\u5747\u9ad8\uff0c\u4e00\u5165\u706b\u5806\uff0c\u6025\u5fd9\u9003\u51fa\uff0c\u4f46\u5df2\u70e7\u5f97\u987b\u7709\u5c3d\u7126\uff0c\u72fc\u72c8\u4e0d\u582a\u3002\u5230\u6b64\u5730\u6b65\uff0c\u4e09\u5144\u5f1f\u54ea\u6562\u9017\u7559\uff0c\u9a6c\u5339\u4e5f\u4e0d\u8981\u4e86\uff0c\u5411\u5357\u6025\u5954\u800c\u53bb\uff0c\u4f46\u542c\u82d7\u4eba\u51e4\u8c6a\u8fc8\u723d\u6717\u7684\u5927\u7b11\u58f0\uff0c\u4e0d\u7edd\u4ece\u8eab\u540e\u4f20\u6765\u3002 \u3000\u3000\u82d7\u4eba\u51e4\u60f3\u5230\u5f53\u5e74\u529b\u6218\u9b3c\u89c1\u6101\u953a\u6c0f\u4e09\u96c4\u7684\u60c5\u666f\uff0c\u5634\u89d2\u4e0a\u4e0d\u81ea\u7981\u51fa\u73b0\u4e86\u4e00\u4e1d\u7b11\u610f\uff0c\u7136\u800c\u8fd9\u662f\u6101\u82e6\u4e2d\u7684\u4e00\u4e1d\u5fae\u7b11\uff0c\u662f\u4f24\u5fc3\u4e2d\u4e00\u95ea\u5373\u901d\u7684\u6b22\u6b23\u3002\u4e8e\u662f\u4ed6\u60f3\u5230\u817f\u4e0a\u4f24\u6108\u4e4b\u540e\uff0c\u4e0e\u5357\u5c0f\u59d0\u7ed3\u6210\u592b\u5987\uff0c\u8fd9\u4e2a\u523b\u9aa8\u94ed\u5fc3\u3001\u503e\u5fc3\u76f8\u7231\u7684\u59bb\u5b50\uff0c\u5c31\u662f\u773c\u524d\u8fd9\u4e2a\u7f8e\u5987\u4eba\u3002\u5979\u5728\u8eab\u524d\u4e0d\u8fc7\u4e94\u5c3a\uff0c\u4e94\u5c3a\u5374\u6bd4\u4e94\u5343\u91cc\u3001\u4e94\u4e07\u91cc\u7684\u8def\u7a0b\u66f4\u52a0\u9065\u8fdc\u3002 \u3000\u3000\u4e8e\u662f\uff0c\u4ed6\u60f3\u5230\u4e24\u4eba\u65b0\u5a5a\u540e\u90a3\u6bb5\u6b22\u4e50\u7684\u65e5\u5b50\uff0c\u4ed6\u5e26\u7740\u4ed6\u7684\u5170\uff08\u5357\u5c0f\u59d0\u540d\u5b57\u53eb\u505a\u5357\u5170\uff09\u4e00\u540c\u53bb\u62dc\u796d\u80e1\u4e00\u5200\u592b\u5987\u7684\u5893\uff0c\u4ed6\u628a\u51b7\u6708\u5b9d\u5200\u5c01\u5728\u575f\u571f\u4e4b\u4e2d\uff0c\u5fc3\u91cc\u60f3\uff1a\u4e16\u4e0a\u9664\u4e86\u80e1\u4e00\u5200\u5916\uff0c\u518d\u4e5f\u65e0\u4eba\u914d\u7528\u8fd9\u628a\u5b9d\u5200\u3002\u4ed6\u65e2\u7136\u4e0d\u5728\u4e16\u4e0a\u4e86\uff0c\u5b9d\u5200\u5c31\u8be5\u966a\u7740\u4ed6\u3002 \u3000\u3000\u4e8e\u662f\u5728\u80e1\u4e00\u5200\u7684\u5893\u524d\uff0c\u4ed6\u628a\u5f53\u5e74\u8fd9\u573a\u6bd4\u6b66\u4e0e\u8bef\u4f24\u7684\u7ecf\u8fc7\u8bf4\u7ed9\u59bb\u5b50\u542c\u3002\u4ed6\u4ece\u6765\u4e0d\u7231\u591a\u8bf4\u8bdd\uff0c\u8fd9\u4e00\u5929\u5374\u662f\u8bf4\u5f97\u6ed4\u6ed4\u4e0d\u7edd\u3002\u8fd9\u4ef6\u4e8b\u5728\u4ed6\u5fc3\u4e2d\u90c1\u79ef\u4e86\u5341\u5e74\uff0c\u76f4\u5230\u4eca\u5929\uff0c\u65b9\u5728\u6700\u4eb2\u8fd1\u7684\u4eba\u9762\u524d\u53d1\u6cc4\u51fa\u6765\u3002\u4ed6\u529e\u4e86\u8bb8\u591a\u9152\u83dc\u6765\u796d\u5960\u80e1\u4e00\u5200\uff0c\u6446\u6ee1\u4e86\u4e00\u684c\uff0c\u5c31\u50cf\u5f53\u5e74\u80e1\u592b\u4eba\u5728\u4ed6\u4eec\u6bd4\u6b66\u65f6\u505a\u4e86\u4e00\u684c\u83dc\u90a3\u6837\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u559d\u4e86\u4e0d\u5c11\u9152\uff0c\u597d\u50cf\u8fd9\u4f4d\u751f\u5e73\u552f\u4e00\u7684\u77e5\u5df1\u590d\u6d3b\u4e86\uff0c\u4e0e\u4ed6\u4e00\u8d77\u6b22\u8c08\u7545\u996e\uff0c\u4ed6\u6108\u662f\u559d\u5f97\u591a\uff0c\u6108\u662f\u8bf4\u5f97\u591a\u3002\u8bf4\u5230\u5bf9\u8fd9\u4f4d\u8fbd\u4e1c\u5927\u4fa0\u7684\u94a6\u4f69\u4e0e\u5d07\u4ef0\uff0c\u8bf4\u5230\u9020\u5316\u5c0f\u513f\u7684\u5f04\u4eba\uff0c\u4eba\u4e16\u7684\u65e0\u5e38\uff0c\u8bf4\u5230\u80e1\u592b\u4eba\u5bf9\u4e08\u592b\u7684\u60c5\u7231\uff0c\u4ed6\u8bf4\uff1a\u201d\u50cf\u8fd9\u6837\u7684\u5973\u4eba\uff0c\u8981\u662f\u4e08\u592b\u5728\u706b\u91cc\uff0c\u5979\u4e00\u5b9a\u4e5f\u5728\u706b\u91cc\uff0c\u4e08\u592b\u5728\u6c34\u91cc\uff0c\u5979\u4e5f\u5728\u6c34\u91cc\u2026\u2026\u201d \u3000\u3000\u4e8e\u662f\u7a81\u7136\u4e4b\u95f4\uff0c\u770b\u5230\u81ea\u5df1\u7684\u65b0\u5a18\u8138\u8272\u53d8\u4e86\uff0c\u63a9\u7740\u8138\u8fdc\u8fdc\u5954\u5f00\uff0c\u4ed6\u8ffd\u4e0a\u53bb\u60f3\u8981\u89e3\u91ca\uff0c\u4f46\u4ed6\u662f\u9189\u4e86\uff0c\u4ed6\u4e0d\u4f1a\u8bf4\u8bdd\uff0c\u4f55\u51b5\uff0c\u4ed6\u5fc3\u4e2d\u786e\u662f\u8bb0\u5f97\u5ba2\u5e97\u4e2d\u953a\u6c0f\u4e09\u96c4\u706b\u653b\u7684\u90a3\u4e00\u5e55\u2026\u2026\u4ed6\u662f\u5728\u706b\u91cc\uff0c\u800c\u5979\u5374\u72ec\u81ea\u5148\u9003\u4e86\u51fa\u53bb\u2026\u2026 \u3000\u3000\u4ed6\u4e00\u751f\u6177\u6168\u8c6a\u4fa0\uff0c\u7d20\u6765\u4e0d\u7406\u4f1a\u5c0f\u8282\uff0c\u7136\u800c\u8fd9\u662f\u4ed6\u751f\u6b7b\u4ee5\u4e4b\u76f8\u7231\u7684\u4eba\u2026\u2026 \u3000\u3000\u5728\u4ed6\u8111\u5b50\u91cc\uff0c\u4e00\u76f4\u89c9\u5f97\u5357\u5170\u5e94\u8be5\u9003\u51fa\u53bb\uff0c\u5979\u662f\u5973\u4eba\uff0c\u4e0d\u4f1a\u534a\u70b9\u6b66\u529f\uff0c\u89c1\u5230\u4e86\u6d53\u70df\u70c8\u706b\u81ea\u7136\u5bb3\u6015\uff0c\u5979\u90a3\u65f6\u53c8\u4e0d\u662f\u4ed6\u7684\u59bb\u5b50\uff0c\u966a\u7740\u4ed6\u6b7b\u4e86\uff0c\u53c8\u6709\u4ec0\u4e48\u597d\u5904\uff1f\u2026\u2026\u4f46\u5728\u5fc3\u91cc\uff0c\u4ed6\u6df1\u6df1\u76fc\u671b\u5728\u81ea\u5df1\u9047\u5230\u5371\u96be\u4e4b\u65f6\uff0c\u6709\u4e2a\u5fc3\u7231\u7684\u4eba\u5b88\u5728\u8eab\u65c1\uff0c\u76fc\u671b\u5fc3\u7231\u7684\u4eba\u4e0d\u8981\u5f03\u4ed6\u800c\u5148\u9003\u2026\u2026\u4ed6\u4e00\u76f4\u7fa1\u6155\u80e1\u4e00\u5200\uff0c\u5fc3\u60f3\u4ed6\u6709\u4e00\u4e2a\u771f\u5fc3\u76f8\u7231\u7684\u592b\u4eba\uff0c\u81ea\u5df1\u53ef\u6ca1\u6709\u3002\u80e1\u4e00\u5200\u867d\u7136\u65e9\u6b7b\uff0c\u8fd9\u4e00\u751f\u5374\u6bd4\u81ea\u5df1\u8fc7\u5f97\u5feb\u6d3b\u3002 \u3000\u3000\u4e8e\u662f\u5728\u9152\u9189\u4e4b\u540e\uff0c\u5728\u80e1\u4e00\u5200\u7684\u5893\u524d\uff0c\u65e0\u610f\u4e2d\u8bf4\u9519\u4e86\u4e00\u53e5\u8bdd\uff0c\u4e5f\u53ef\u8bf4\u662f\u65e0\u610f\u4e2d\u6d41\u9732\u4e86\u771f\u5fc3\u3002\u8fd9\u53e5\u8bdd\u9020\u6210\u4e86\u592b\u59bb\u95f4\u6c38\u96be\u5f25\u8865\u7684\u88c2\u75d5\u3002\u867d\u7136\uff0c\u82d7\u4eba\u51e4\u59cb\u7ec8\u662f\u6781\u6df1\u539a\u6781\u8bda\u631a\u5730\u7231\u7740\u59bb\u5b50\u3002 \u3000\u3000\u4ed6\u6c38\u8fdc\u4e0d\u518d\u63d0\u5230\u8fd9\u4ef6\u4e8b\uff0c\u751a\u81f3\u8fde\u80e1\u4e00\u5200\u7684\u540d\u5b57\u4e5f\u4e0d\u63d0\uff0c\u5357\u5170\u81ea\u7136\u4e5f\u4e0d\u4f1a\u63d0\u3002 \u3000\u3000\u540e\u6765\u5973\u513f\u82e5\u5170\u51fa\u4e16\u4e86\uff0c\u50cf\u6bcd\u4eb2\u4e00\u822c\u7684\u7f8e\u4e3d\uff0c\u50cf\u6bcd\u4eb2\u4e00\u822c\u7684\u5a07\u5ae9\u3002\u592b\u59bb\u95f4\u7684\u611f\u60c5\u52a0\u6df1\u4e86\u4e00\u5c42\u3002\u7136\u800c\uff0c\u4ed6\u662f\u51fa\u8eab\u8d2b\u5bb6\u7684\u6c5f\u6e56\u8c6a\u6770\uff0c\u59bb\u5b50\u5374\u662f\u5b98\u5bb6\u7684\u5343\u91d1\u5c0f\u59d0\u3002\u4ed6\u5929\u6027\u6c89\u9ed8\u5be1\u8a00\uff0c\u6574\u5929\u677f\u7740\u8138\uff0c\u59bb\u5b50\u5374\u9700\u8981\u6e29\u67d4\u4f53\u8d34\uff0c\u4f4e\u58f0\u4e0b\u6c14\u7684\u5b89\u6170\u3002\u5979\u8981\u7537\u4eba\u98ce\u96c5\u65af\u6587\u3001\u61c2\u5f97\u5973\u4eba\u7684\u5c0f\u6027\u513f\uff0c\u8981\u7537\u4eba\u4f1a\u8bf4\u7b11\uff0c\u4f1a\u8c03\u60c5\u2026\u2026\u82d7\u4eba\u51e4\u7a7a\u5177\u4e00\u8eab\u6253\u904d\u5929\u4e0b\u65e0\u654c\u624b\u7684\u6b66\u529f\uff0c\u59bb\u5b50\u6240\u8981\u7684\u4e00\u5207\u5374\u5168\u6ca1\u6709\u3002\u5982\u679c\u5357\u5c0f\u59d0\u4f1a\u6b66\u529f\uff0c\u6216\u8bb8\u4f1a\u4f69\u670d\u4e08\u592b\u7684\u672c\u4e8b\uff0c\u4f1a\u61c2\u5f97\u4ed6\u4e3a\u4ec0\u4e48\u662f\u5f53\u4e16\u4e00\u4f4d\u9876\u5929\u7acb\u5730\u7684\u5947\u7537\u5b50\u3002\u4f46\u5979\u538b\u6839\u513f\u77a7\u4e0d\u8d77\u6b66\u529f\uff0c\u751a\u81f3\u4ece\u5fc3\u5e95\u91cc\u538c\u618e\u6b66\u529f\u3002\u56e0\u4e3a\uff0c\u5979\u7236\u4eb2\u662f\u7ed9\u6b66\u4eba\u5bb3\u6b7b\u7684\uff0c\u8d77\u56e0\u662f\u5728\u4e8e\u4e00\u628a\u5200\uff1b\u53c8\u56e0\u4e3a\uff0c\u5979\u5ac1\u4e86\u4e00\u4e2a\u4e0d\u7406\u4f1a\u81ea\u5df1\u5fc3\u4e8b\u7684\u7537\u4eba\uff0c\u8d77\u56e0\u662f\u5728\u4e8e\u8fd9\u7537\u4eba\u7528\u6b66\u529f\u6551\u4e86\u81ea\u5df1\u3002 \u3000\u3000\u5979\u4e00\u751f\u4e2d\u66fe\u6709\u4e00\u6bb5\u77ed\u77ed\u7684\u65f6\u5149\uff0c\u5bf9\u6b66\u529f\u611f\u5230\u4e86\u4e00\u70b9\u5174\u8da3\uff0c\u90a3\u662f\u4e08\u592b\u7684\u4e00\u4e2a\u670b\u53cb\u6765\u4f5c\u5ba2\u7684\u65f6\u5019\u3002\u90a3\u5c31\u662f\u8fd9\u4e2a\u82f1\u4fca\u6f47\u6d12\u7684\u7530\u5f52\u8863\uff0c\u4ed6\u6ca1\u4e00\u53e5\u8bdd\u4e0d\u5728\u8ba8\u4eba\u6b22\u559c\uff0c\u6ca1\u4e00\u4e2a\u773c\u8272\u4e0d\u662f\u8f6f\u7ef5\u7ef5\u7684\u53eb\u4eba\u60f3\u8d77\u4e86\u5c31\u4f1a\u5fc3\u8df3\u3002\u4f46\u5947\u602a\u5f97\u5f88\uff0c\u4e08\u592b\u5bf9\u8fd9\u4f4d\u7530\u76f8\u516c\u5374\u4e0d\u5927\u77a7\u5f97\u8d77\uff0c\u5bf9\u4ed6\u7231\u7406\u4e0d\u7406\u7684\uff0c\u4e8e\u662f\u62db\u5f85\u5ba2\u4eba\u7684\u4e8b\u513f\u5c31\u843d\u5728\u5979\u8eab\u4e0a\u3002\u76f8\u89c1\u7684\u7b2c\u4e00\u5929\u665a\u4e0a\uff0c\u5979\u7761\u5728\u5e8a\u4e0a\uff0c\u7741\u5927\u4e86\u773c\u775b\u671b\u7740\u9ed1\u6697\u7684\u7a97\u5916\uff0c\u5fcd\u4e0d\u4f4f\u6697\u6697\u4f24\u5fc3\uff1a\u4e3a\u4ec0\u4e48\u5f53\u65e5\u6551\u5979\u7684\u4e0d\u662f\u8fd9\u4f4d\u98ce\u6d41\u4fca\u4fcf\u7684\u7530\u76f8\u516c\uff0c\u504f\u751f\u662f\u8fd9\u4e2a\u6728\u5934\u4e00\u822c\u7761\u5728\u8eab\u65c1\u7684\u4e08\u592b\uff1f \u3000\u3000\u8fc7\u4e86\u51e0\u5929\uff0c\u7530\u5f52\u519c\u8ddf\u5979\u8c08\u8bba\u6b66\u529f\uff0c\u53d1\u89c9\u5979\u4e00\u70b9\u513f\u4e5f\u4e0d\u4f1a\uff0c\u4e8e\u662f\u6559\u4e86\u5979\u51e0\u8def\u62f3\u811a\u3002\u5979\u5b66\u5f97\u5f88\u8d77\u52b2\uff0c\u867d\u7136\u5979\u8fd8\u662f\u4e0d\u559c\u6b22\u6b66\u529f\uff0c\u53ea\u56e0\u662f\u4ed6\u6559\u7684\uff0c\u4e8e\u662f\u5c31\u5174\u81f4\u52c3\u52c3\u5730\u5b66\u4e86\u3002 \u3000\u3000\u7ec8\u4e8e\u6709\u4e00\u5929\uff0c\u5979\u5bf9\u4ed6\u8bf4\uff1a\u201c\u4f60\u8ddf\u6211\u4e08\u592b\u7684\u540d\u5b57\u8be5\u5f53\u8c03\u4e00\u4e0b\u624d\u914d\u3002\u4ed6\u6700\u597d\u662f\u5f52\u519c\u79cd\u7530\uff0c\u4f60\u624d\u771f\u6b63\u662f\u4eba\u4e2d\u7684\u51e4\u51f0\u3002\u201d\u4e5f\u4e0d\u77e5\u662f\u4ed6\u65e9\u6709\u5b58\u5fc3\uff0c\u8fd8\u662f\u56e0\u4e3a\u53d7\u5230\u4e86\u8fd9\u53e5\u8bdd\u7684\u8bbd\u55bb\uff0c\u7ec8\u4e8e\uff0c\u5728\u4e00\u4e2a\u70ed\u60c5\u7684\u591c\u665a\uff0c\u5bbe\u5ba2\u4fae\u8fb1\u4e86\u4e3b\u4eba\uff0c\u59bb\u5b50\u4fae\u8fb1\u4e86\u4e08\u592b\uff0c\u6bcd\u4eb2\u4fae\u8fb1\u4e86\u5973\u513f\u3002 \u3000\u3000\u90a3\u65f6\u82d7\u4eba\u51e4\u5728\u6708\u4e0b\u7ec3\u5251\uff0c\u4ed6\u4eec\u7684\u5973\u513f\u82d7\u82e5\u5170\u751c\u751c\u5730\u7761\u7740\u5357\u5170\u5934\u4e0a\u7684\u91d1\u51e4\u73e0\u9497\u8dcc\u5230\u4e86\u5e8a\u524d\u5730\u4e0a\uff0c\u7530\u5f52\u519c\u7ed9\u5979\u62fe\u4e86\u8d77\u6765\uff0c\u6e29\u67d4\u5730\u7ed9\u5979\u63d2\u5728\u5934\u4e0a\uff0c\u51e4\u9497\u7684\u5934\u8f7b\u67d4\u5730\u5fae\u5fae\u98a4\u52a8\u2026\u2026 \u3000\u3000\u5979\u4e8e\u662f\u4e0b\u4e86\u51b3\u5fc3\u3002\u4e08\u592b\u3001\u5973\u513f\u3001\u5bb6\u56ed\u3001\u540d\u58f0\u2026\u2026\u4e00\u5207\u5168\u522b\u4e86\uff0c\u5979\u8981\u6e29\u67d4\u7684\u7231\uff0c\u8981\u70ed\u60c5\u3002\u4e8e\u662f\u5979\u8ddf\u7740\u8fd9\u4f4d\u4fca\u4fcf\u7684\u76f8\u516c\u4ece\u5bb6\u91cc\u9003\u4e86\u51fa\u6765\uff0c\u4e8e\u662f\u4e08\u592b\u62b1\u7740\u5973\u513f\u4ece\u5927\u98ce\u96e8\u4e2d\u8ffd\u8d76\u4e86\u6765\uff0c\u5973\u513f\u5728\u54ed\uff0c\u5728\u6c42\uff0c\u5728\u53eb\u201c\u5988\u5988\u201d\u3002\u4f46\u5979\u5df2\u7ecf\u4e0b\u4e86\u51b3\u5fc3\uff0c\u53ea\u8981\u548c\u5f52\u519c\u5728\u4e00\u8d77\uff0c\u53ea\u8fc7\u77ed\u77ed\u7684\u51e0\u5929\u4e5f\u662f\u597d\u7684\uff0c\u53ea\u8981\u548c\u5f52\u519c\u5728\u4e00\u8d77\uff0c\u7ed9\u4e08\u592b\u6740\u4e86\u4e5f\u7f62\uff0c\u5250\u4e86\u4e5f\u7f62\u3002\u5979\u5f88\u7231\u5973\u513f\uff0c\u7136\u800c\u8fd9\u662f\u82d7\u4eba\u51e4\u7684\u5973\u513f\uff0c\u4e0d\u662f\u7530\u5f52\u519c\u548c\u5979\u751f\u7684\u5973\u513f\u3002 \u3000\u3000\u5979\u542c\u5230\u5973\u513f\u7684\u54ed\u6c42\uff0c\u4f46\u5728\u773c\u89d2\u4e2d\uff0c\u5979\u770b\u5230\u4e86\u7530\u5f52\u519c\u52a8\u4eba\u5fc3\u9b44\u7684\u5fae\u7b11\uff0c\u56e0\u6b64\u5979\u4e0d\u56de\u8fc7\u5934\u6765\u3002 \u3000\u3000\u82d7\u4eba\u51e4\u5728\u60f3\uff1a\u53ea\u76fc\u5979\u8ddf\u7740\u6211\u56de\u5bb6\u53bb\uff0c\u8fd9\u4ef6\u4e8b\u4ee5\u540e\u6211\u4e00\u5b9a\u4e00\u53e5\u4e0d\u63d0\uff0c\u6211\u53ea\u6709\u52a0\u500d\u7231\u5979\uff0c\u53ea\u8981\u5979\u56de\u5fc3\u8f6c\u610f\uff0c\u6211\u8981\u5979\uff0c\u5973\u513f\u8981\u5979\uff01 \u3000\u3000\u82d7\u592b\u4eba\u5728\u60f3\uff1a\u4ed6\u4f1a\u4e0d\u4f1a\u6253\u6b7b\u5f52\u519c\uff1f\u4ed6\u5f88\u7231\u6211\uff0c\u4e0d\u4f1a\u6253\u6211\u7684\uff0c\u4f46\u4f1a\u4e0d\u4f1a\u6253\u6b7b\u5f52\u519c\uff1f \u3000\u3000\u82d7\u82e5\u5170\u5c0f\u5c0f\u7684\u5fc3\u7075\u4e2d\u5728\u60f3\uff1a\u5988\u5988\u4e3a\u4ec0\u4e48\u4e0d\u7406\u6211\uff1f\u4e0d\u80af\u62b1\u6211\uff1f\u6211\u4e0d\u4e56\u5417\uff1f \u3000\u3000\u7530\u5f52\u519c\u4e5f\u5728\u60f3\u4ed6\u7684\u5fc3\u4e8b\u3002\u4ed6\u7684\u5fc3\u4e8b\u662f\u6df1\u6c89\u7684\u3002\u4ed6\u60f3\u5230\u95ef\u738b\u6240\u7559\u4e0b\u7684\u65e0\u7a77\u65e0\u5c3d\u7684\u8d22\u5b9d\uff0c\u82d7\u592b\u4eba\u662f\u6253\u5f00\u8fd9\u5b9d\u5e93\u7684\u94a5\u5319\uff0c\u5f53\u7136\uff0c\u5979\u5f88\u7f8e\u4e3d\uff0c\u5a07\u5a9a\u65e0\u4f26\uff0c\u4f46\u66f4\u91cd\u8981\u7684\u662f\u95ef\u738b\u7684\u5b9d\u5e93\uff0c\u82d7\u4eba\u51e4\u4f1a\u4e0d\u4f1a\u6253\u6b7b\u6211\u5462\uff1f \u3000\u3000\u82d7\u4eba\u51e4\u5728\u7b49\u5f85\uff0c\u5385\u4e0a\u7684\u9556\u5ba2\u3001\u7fa4\u76d7\u3001\u4f8d\u536b\u3001\u5546\u5bb6\u5821\u7684\u4e3b\u4eba\uff0c\u72ec\u81c2\u4eba\u548c\u5c0f\u5b69\uff0c\u5927\u5bb6\u90fd\u5728\u7b49\u5f85\u3002\u5385\u4e0a\u6709\u5f88\u591a\u4eba\uff0c\u4f46\u8c01\u4e5f\u4e0d\u8bf4\u8bdd\uff0c\u53ea\u542c\u5230\u4e00\u4e2a\u5c0f\u5973\u5b69\u5728\u54ed\u53eb\uff1a\u201c\u5988\u5988\uff01\u5988\u5988\uff01\u62b1\u62b1\u5170\u5170\uff01\u201d \u3000\u3000\u5373\u4f7f\u662f\u6700\u786c\u5fc3\u80a0\u7684\u4eba\uff0c\u4e5f\u76fc\u671b\u5979\u56de\u8fc7\u8eab\u6765\u62b1\u4e00\u62b1\u5973\u513f\u3002 \u3000\u3000\u81ea\u4ece\u8d70\u8fdb\u5546\u5bb6\u5821\u5927\u5385\uff0c\u82d7\u4eba\u51e4\u59cb\u7ec8\u6ca1\u8bf4\u8fc7\u4e00\u4e2a\u5b57\uff0c\u4e00\u53cc\u773c\u50cf\u9e70\u4e00\u822c\u671b\u7740\u59bb\u5b50\u3002 \u3000\u3000\u5916\u9762\u5728\u4e0b\u7740\u503e\u76c6\u5927\u96e8\uff0c\u7535\u5149\u95ea\u8fc7\uff0c\u63a5\u7740\u4fbf\u662f\u9686\u9686\u7684\u96f7\u58f0\u3002\u5927\u96e8\u4e1d\u6beb\u6ca1\u505c\uff0c\u96f7\u58f0\u4e5f\u662f\u4e0d\u6b47\u7684\u54cd\u7740\u3002 \u3000\u3000\u7ec8\u4e8e\uff0c\u82d7\u592b\u4eba\u7684\u5934\u5fae\u5fae\u4e00\u4fa7\u3002\u82d7\u4eba\u51e4\u7684\u5fc3\u731b\u5730\u4e00\u8df3\uff0c\u4ed6\u770b\u5230\u59bb\u5b50\u5728\u5fae\u7b11\uff0c\u773c\u5149\u4e2d\u9732\u51fa\u6e29\u67d4\u7684\u6b3e\u6b3e\u6df1\u60c5\u3002\u5979\u662f\u5728\u77a7\u7740\u7530\u5f52\u519c\u3002\u8fd9\u6837\u6df1\u60c5\u7684\u773c\u8272\uff0c\u5979\u4ece\u6765\u6ca1\u5411\u81ea\u5df1\u77a7\u8fc7\u4e00\u773c\uff0c\u5373\u4f7f\u5728\u65b0\u5a5a\u4e2d\u4e5f\u4ece\u6765\u6ca1\u6709\u8fc7\uff0c\u8fd9\u662f\u4ed6\u751f\u5e73\u7b2c\u4e00\u6b21\u77a7\u89c1\u3002 \u3000\u3000\u82d7\u4eba\u51e4\u7684\u5fc3\u6c89\u4e86\u4e0b\u53bb\uff0c\u4ed6\u4e0d\u518d\u76fc\u671b\uff0c\u7f13\u7f13\u7ad9\u4e86\u8d77\u6765\uff0c\u7528\u6cb9\u5e03\u7ec6\u5fc3\u5730\u59a5\u8d34\u5730\u88f9\u597d\u4e86\u5973\u513f\uff0c\u653e\u5728\u81ea\u5df1\u80f8\u524d\uff0c\u4ed6\u975e\u5e38\u975e\u5e38\u7684\u5c0f\u5fc3\uff0c\u56e0\u4e3a\u4e16\u754c\u4e0a\u518d\u6ca1\u6709\u8fd9\u6837\u6148\u7231\u3001\u8fd9\u6837\u4f24\u5fc3\u7684\u7236\u4eb2\u3002 \u3000\u3000\u4ed6\u5927\u8e0f\u6b65\u8d70\u51fa\u5385\u53bb\uff0c\u59cb\u7ec8\u6ca1\u8bf4\u4e00\u53e5\u8bdd\uff0c\u4e5f\u4e0d\u56de\u5934\u518d\u671b\u4e00\u6b21\uff0c\u56e0\u4e3a\u4ed6\u5df2\u7ecf\u89c1\u5230\u4e86\u59bb\u5b50\u90a3\u6df1\u60c5\u7684\u773c\u8272\u3002 \u3000\u3000\u5927\u96e8\u843d\u5728\u4ed6\u58ee\u5065\u7684\u5934\u4e0a\uff0c\u843d\u5728\u4ed6\u7c97\u5927\u7684\u80a9\u4e0a\uff0c\u96f7\u58f0\u5728\u4ed6\u7684\u5934\u9876\u54cd\u7740\u3002 \u3000\u3000\u5c0f\u5973\u5b69\u7684\u54ed\u58f0\u8fd8\u5728\u9690\u9690\u4f20\u6765\uff0c\u4f46\u82d7\u4eba\u51e4\u5927\u8e0f\u6b65\u53bb\u4e86\u3002\u4ed6\u62b1\u7740\u5973\u513f\uff0c\u5728\u5927\u98ce\u5927\u96e8\u4e2d\u5927\u8e0f\u6b65\u8d70\u7740\u3002 \u3000\u3000\u4ed6\u4eec\u6ca1\u6709\u56de\u5bb6\u53bb\u3002\u8fd9\u4e2a\u5bb6\uff0c\u4ee5\u540e\u8c01\u4e5f\u6ca1\u6709\u56de\u53bb\u2026\u2026 \u3000"}),showOnHomepage:!0},{name:"\u7b2c\u4e09\u7ae0\u3000\u3000\u82f1\u96c4\u5e74\u5c11",content:(0,a.jsx)(a.Fragment,{children:"\u82d7\u4eba\u51e4\u62b1\u7740\u5973\u513f\uff0c\u5728\u5927\u98ce\u96e8\u4e2d\u79bb\u5f00\u4e86\u5546\u5bb6\u5821\u3002\u4fa0\u58eb\u867d\u53bb\uff0c\u4f59\u5a01\u72b9\u5b58\uff1a\u4ed6\u8fdb\u5385\u51fa\u5385\uff0c\u5e76\u65e0\u4e00\u8a00\u534a\u8bed\uff0c\u4f46\u7fa4\u8c6a\u9707\u6151\uff0c\u4e0d\u8bba\u8bc6\u4e0e\u4e0d\u4ee5\uff0c\u65e0\u4e0d\u51db\u7136\uff0c\u4f17\u4eba\u6216\u60ca\u6216\u6127\uff0c\u6216\u656c\u6216\u60e7\uff0c\u8fc7\u4e86\u826f\u4e45\uff0c\u4ecd\u662f\u65e0\u4eba\u8bf4\u8bdd\uff0c\u5404\u81ea\u51dd\u601d\u3002 \u3000\u3000\u82d7\u592b\u4eba\u7f13\u7f13\u7ad9\u8d77\uff0c\u5634\u89d2\u8fb9\u5e26\u7740\u5f3a\u7b11\uff0c\u4f46\u6cea\u6c34\u5728\u773c\u7736\u4e2d\u6eda\u4e86\u51e0\u8f6c\uff0c\u7ec8\u4e8e\u4ece\u767d\u7389\u4e00\u822c\u7684\u816e\u8fb9\u6eda\u4e86\u4e0b\u6765\u3002\u7530\u5f52\u519c\u500f\u5730\u8d77\u8eab\uff0c\u5de6\u624b\u63e1\u4f4f\u8170\u95f4\u957f\u5251\u5251\u67c4\uff0c\u62c9\u51fa\u4e94\u5bf8\uff0c\u94ee\u7684\u4e00\u58f0\uff0c\u91cd\u5f52\u5251\u9798\uff0c\u8fd9\u4e00\u4e0b\u624b\u52bf\u6f47\u6d12\u5229\u843d\u5df2\u6781\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5170\u59b9\uff0c\u8d70\u5427\u3002\u201d\u53cc\u773c\u671b\u7740\u5927\u8f66\u4e2d\u4e00\u9798\u9798\u7684\u94f6\u9798\u3002\u795e\u6001\u867d\u662f\u4e0d\u51cf\u4fca\u96c5\u98ce\u6d41\uff0c\u4f46\u8bed\u58f0\u5fae\u6296\uff0c\u63a9\u4e0d\u4e86\u672a\u66fe\u5c3d\u53bb\u7684\u6050\u60e7\u4e4b\u5fc3\u3002 \u3000\u3000\u9a6c\u884c\u7a7a\u89c1\u7530\u5f52\u519c\u4ecd\u60f3\u52ab\u9556\uff0c\u5f3a\u81ea\u6491\u8d77\uff0c\u53eb\u9053\uff1a\u201c\u6625\u513f\uff0c\u53d6\u5175\u5203\u6765\uff01\u201d\u9a6c\u6625\u82b1\u89c1\u7236\u4eb2\u53d7\u4f24\u975e\u8f7b\uff0c\u542b\u6cea\u9053\uff0c\u201d\u7239\uff01\u201d\u9a6c\u884c\u7a7a\u58f0\u97f3\u5a01\u4e25\uff0c\u8bf4\u9053\uff1a\u201c\u51b3\u53d6\u6765\u3002\u201d \u3000\u3000\u9a6c\u6625\u82b1\u4ece\u80cc\u56ca\u4e2d\u53d6\u51fa\u968f\u7740\u7236\u4eb2\u8d70\u4e86\u6570\u5341\u5e74\u9556\u7684\u91d1\u4e1d\u8f6f\u97ad\uff0c\u6b63\u8981\u9012\u8fc7\uff0c\u7a81\u7136\u540e\u5802\u54b3\u55fd\u4e00\u58f0\uff0c\u8d70\u51fa\u4e00\u4e2a\u8001\u5987\uff0c\u8eab\u7a7f\u9752\u5e03\u68c9\u8884\uff0c\u4e0b\u7cfb\u9ed1\u88d9\uff0c\u810a\u6881\u5fae\u9a7c\uff0c\u4e24\u9b13\u5168\u767d\uff0c\u9876\u5fc3\u7684\u5934\u53d1\u5374\u662f\u4e00\u7247\u6f06\u9ed1\u3002\u5546\u5b9d\u9707\u867d\u88ab\u7530\u5f52\u519c\u6253\u5012\uff0c\u53d7\u4f24\u4e0d\u91cd\uff0c\u62a2\u4e0a\u53bb\u53eb\u9053\uff1a\u201c\u5988\uff0c\u8fd9\u91cc\u7684\u4e8b\u4f60\u8001\u4eba\u5bb6\u522b\u7ba1\uff0c\u8bf7\u56de\u53bb\u4f11\u606f\u5427\u3002\u201d\u539f\u6765\u8fd9\u8001\u5987\u6b63\u662f\u5546\u5b9d\u9707\u7684\u6bcd\u4eb2\u3002 \u3000\u3000\u5546\u8001\u592a\u70b9\u4e86\u70b9\u5934\uff0c\u4e0d\u52a8\u58f0\u8272\u5730\u9053\uff1a\u201c\u683d\u5728\u4eba\u5bb6\u624b\u91cc\u5566\uff1f\u201d\u8bed\u58f0\u5636\u54d1\uff0c\u751a\u662f\u96be\u542c\u3002\u5546\u5b9d\u9707\u8138\u9732\u60ed\u8272\uff0c\u5782\u9996\u9053\uff1a\u201c\u513f\u5b50\u4e0d\u4e2d\u7528\uff0c\u4e0d\u662f\u8fd9\u59d3\u7530\u7684\u5bf9\u624b\u3002\u201d \u3000\u3000\u8bf4\u7740\u5411\u7530\u5f52\u519c\u4e00\u6307\uff0c\u4e0d\u7981\u6127\u6124\u4ea4\u96c6\u3002 \u3000\u3000\u5546\u8001\u592a\u53cc\u773c\u534a\u5f20\u534a\u5f00\uff0c\u9eef\u6de1\u65e0\u5149\uff0c\u6728\u7136\u5411\u7530\u5f52\u519c\u671b\u4e86\u4e00\u4e0b\uff0c\u53c8\u5411\u82d7\u592b\u4eba\u671b\u4e86\u4e00\u4e0b\uff0c\u5583\u5583\u9053\uff1a\u201c\u597d\u4e2a\u7f8e\u4eba\u513f\uff01\u201d \u3000\u3000\u7a81\u7136\u95f4\u4e00\u4e2a\u9ec4\u7626\u7537\u5b69\u4ece\u4eba\u4e1b\u4e2d\u94bb\u4e86\u51fa\u6765\uff0c\u6307\u7740\u82d7\u592b\u4eba\u53eb\u9053\uff1a\u201c\u4f60\u5973\u513f\u8981\u4f60\u62b1\uff0c\u5e72\u4e48\u4f60\u4e0d\u776c\u5979\uff1f\u4f60\u505a\u5988\u5988\u7684\uff0c\u600e\u4e48\u4e00\u70b9\u826f\u5fc3\u4e5f\u6ca1\u6709\uff1f\u201d \u3000\u3000\u8fd9\u51e0\u53e5\u8bdd\u4eba\u4eba\u5fc3\u4e2d\u90fd\u60f3\u5230\u4e86\uff0c\u53ef\u662f\u5374\u7531\u4e00\u4e2a\u4e5e\u513f\u6a21\u6837\u7684\u9ec4\u7626\u5c0f\u513f\u8bf4\u51fa\u53e3\u6765\uff0c\u4f17\u4eba\u5fc3\u4e2d\u90fd\u662f\u4e00\u6014\u3002\u53ea\u542c\u8f70\u8f70\u9686\u9686\u96f7\u58f0\u8fc7\u53bb\uff0c\u90a3\u7537\u5b69\u5927\u58f0\u9053\uff1a\u201c\u4f60\u826f\u5fc3\u4e0d\u597d\uff0c\u96f7\u516c\u5288\u6b7b\u4f60\uff01\u201d\u621f\u6307\u6012\u65a5\uff0c\u4e00\u4e2a\u8863\u886b\u8934\u891b\u7684\u5b69\u7ae5\uff0c\u970e\u65f6\u95f4\u7adf\u662f\u5927\u6709\u5a01\u52bf\u3002 \u3000\u3000\u7530\u5f52\u519c\u4e00\u6014\uff0c\u5237\u7684\u4e00\u58f0\uff0c\u957f\u5251\u51fa\u9798\uff0c\u559d\u9053\uff1a\u201c\u5c0f\u53eb\u5316\uff0c\u4f60\u80e1\u8bf4\u516b\u9053\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u90a3\u76d7\u9b41\u960e\u57fa\u62a2\u4e86\u4e0a\u6765\uff0c\u559d\u9053\uff1a\u201c\u51b3\u7ed9\u7530\u76f8\u516c\u2026\u2026\u592b\u2026\u2026\u592b\u4eba\u78d5\u5934\u3002\u201d\u90a3\u7537\u5b69\u4e0d\u53bb\u7406\u4ed6\uff0c\u8138\u4e0a\u6b63\u6c14\u51db\u7136\uff0c\u4ecd\u662f\u6307\u7740\u82d7\u592b\u4eba\u53eb\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u597d\u6ca1\u826f\u5fc3\uff01\u201d \u3000\u3000\u7530\u5f52\u519c\u63d0\u8d77\u957f\u5251\uff0c\u6b63\u8981\u5206\u5fc3\u523a\u53bb\uff0c\u82d7\u592b\u4eba\u7a81\u7136\u201c\u54c7\u201d\u7684\u4e00\u58f0\uff0c\u63a9\u9762\u800c\u54ed\uff0c\u5728\u5927\u96e8\u4e2d\u76f4\u5954\u4e86\u51fa\u53bb\u3002\u7530\u5f52\u519c\u987e\u4e0d\u5f97\u6740\u90a3\u7537\u5b69\uff0c\u63d0\u5251\u8ffd\u51fa\u3002\u4ed6\u4e00\u7a9c\u4e00\u8dc3\uff0c\u5df2\u8ffd\u5230\u82d7\u592b\u4eba\u8eab\u65c1\uff0c\u529d\u9053\uff1a\u201c\u5170\u59b9\uff0c\u8fd9\u5c0f\u53eb\u5316\u80e1\u8bf4\u516b\u9053\uff0c\u522b\u7406\u4ed6\u3002\u201d\u82d7\u592b\u4eba\u54fd\u54bd\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u786e\u662f\u826f\u5fc3\u4e0d\u597d\u3002\u201d\u54ed\u7740\u8bf4\u8bdd\uff0c\u811a\u4e0b\u4e1d\u6beb\u4e0d\u505c\u3002\u7530\u5f52\u519c\u4f38\u624b\u633d\u5979\u81c2\u8180\uff0c\u82d7\u592b\u4eba\u7528\u529b\u4e00\u6323\uff0c\u7530\u5f52\u519c\u82e5\u662f\u5b9a\u8981\u633d\u4f4f\uff0c\u82d7\u592b\u4eba\u518d\u82e6\u7ec3\u5341\u5e74\u6b66\u529f\u4e5f\u6323\u624e\u4e0d\u8131\uff0c\u4f46\u4ed6\u4e0d\u6562\u7528\u5f3a\uff0c\u53ea\u5f97\u653e\u5f00\u4e86\u624b\uff0c\u8f6f\u8bed\u529d\u544a\u3002 \u3000\u3000\u4f46\u89c1\u4e8c\u4eba\u5728\u5927\u96e8\u4e2d\u8d8a\u884c\u8d8a\u8fdc\uff0c\u6cbf\u7740\u5927\u8def\u8f6c\u4e86\u4e2a\u5f2f\uff0c\u7ed9\u4e00\u6392\u5927\u67f3\u6811\u6321\u4f4f\u540e\u5f71\u3002\u96e8\u70b9\u6e85\u5730\uff0c\u6c34\u82b1\u56db\u821e\uff0c\u4e8c\u4eba\u518d\u4e0d\u8f6c\u56de\u3002 \u3000\u3000\u4f17\u4eba\u5401\u4e86\u4e00\u53e3\u6c14\uff0c\u8f6c\u773c\u671b\u90a3\u5b69\u7ae5\uff0c\u5fc3\u60f3\u8fd9\u4eba\u5c0f\u5c0f\u5e74\u7eaa\uff0c\u597d\u5927\u7684\u80c6\u6c14\uff0c\u8fd9\u6761\u547d\u5374\u4e0d\u662f\u6361\u6765\u7684\uff1f \u3000\u3000\u960e\u57fa\u51b7\u7b11\u4e00\u58f0\uff0c\u559d\u9053\uff1a\u201c\u90a3\u5f53\u771f\u518d\u7f8e\u4e0d\u8fc7\uff0c\u960e\u5927\u7237\u72ec\u996e\u80a5\u6c64\uff0c\u5c82\u4e0d\u5999\u54c9\uff01 \u3000\u3000\u5144\u5f1f\u4eec\uff0c\u5feb\u642c\u94f6\u9798\u554a\uff01\u201d\u7fa4\u76d7\u8f70\u7136\u7b54\u5e94\uff0c\u6563\u5f00\u6765\u5c31\u8981\u52a8\u624b\u3002\u960e\u57fa\u5de6\u8db3\u98de\u8d77\uff0c\u5c06\u90a3\u7537\u5b69\u8e22\u4e86\u4e2a\u7b4b\u6597\uff0c\u987a\u624b\u6380\u4f4f\u4e86\u72ec\u81c2\u6c49\u5b50\uff0c\u559d\u9053\uff1a\u201c\u8fd8\u7ed9\u6211\uff01\u201d \u3000\u3000\u5546\u8001\u592a\u592a\u5636\u54d1\u7740\u55d3\u5b50\uff0c\u95ee\u9053\uff1a\u201c\u960e\u8001\u5927\uff0c\u8fd9\u513f\u662f\u5546\u5bb6\u5821\u4e0d\u662f\uff1f\u201d\u960e\u57fa\u9053\uff1a\u201c\u662f\u554a\uff0c\u5546\u5bb6\u5821\u600e\u4e48\u5566\uff1f\u201d\u5546\u8001\u592a\u9053\uff1a\u201c\u6211\u662f\u5546\u5bb6\u5821\u7684\u4e3b\u4eba\u4e0d\u662f\uff1f\u201d\u960e\u57fa\u4e00\u53ea\u624b\u4ecd\u662f\u6380\u4f4f\u72ec\u81c2\u6c49\u80f8\u53e3\uff0c\u4ef0\u5929\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5546\u8001\u5a46\u5b50\uff0c\u4f60\u7ed5\u7740\u5f2f\u513f\u8ddf\u6211\u8bf4\u4ec0\u4e48\u554a\uff1f\u4f60\u5546\u5bb6\u5821\u5899\u9ad8\u95e8\u5bbd\uff0c\u8d22\u7269\u5b9a\u662f\u4e0d\u5c11\uff0c\u53ef\u662f\u60f3\u9001\u70b9\u513f\u6cb9\u6c34\u7ed9\u5144\u5f1f\u4eec\u4f7f\u4f7f\uff1f\u201d\u7fa4\u76d7\u968f\u58f0\u9644\u548c\uff0c\u53eb\u56b7\u54c4\u7b11\u3002\u5546\u5b9d\u9707\u6c14\u5f97\u8138\u4e5f\u767d\u4e86\uff0c\u9053\uff1a\u201c\u5988\uff0c\u522b\u8ddf\u4ed6\u591a\u8bf4\u3002\u513f\u5b50\u548c\u4ed6\u62da\u4e86\u3002\u201d\u4ece\u9556\u884c\u8d9f\u5b50\u624b\u4e2d\u62a2\u8fc7\u4e00\u67c4\u5355\u5200\uff0c\u6307\u7740\u960e\u57fa\u53eb\u9635\u3002 \u3000\u3000\u960e\u57fa\u5c06\u72ec\u81c2\u6c49\u4e00\u63a8\uff0c\u72e0\u72e0\u8bf4\u9053\uff1a\u201c\u5c0f\u5b50\u522b\u8d70\uff0c\u8001\u5b50\u5f85\u4f1a\u8ddf\u4f60\u7b97\u5e10\u3002\u201d\u53cc\u624b\u4e00\u62cd\uff0c\u5411\u7740\u5546\u5b9d\u9707\u659c\u773c\u800c\u8131\uff0c\u8138\u4e0a\u6d41\u6c14\u5341\u8db3\uff0c\u663e\u7136\u538b\u6839\u513f\u6ca1\u5c06\u4ed6\u653e\u5728\u773c\u91cc\u3002 \u3000\u3000\u5546\u8001\u592a\u9053\uff1a\u201c\u960e\u8001\u5927\uff0c\u4f60\u8ddf\u6211\u6765\uff0c\u6211\u6709\u8bdd\u5bf9\u4f60\u8bf4\u3002\u201d\u960e\u57fa\u4e00\u6014\uff0c\u6cb9\u5634\u6ed1\u820c\u5730\u9053\uff1a\u201c\u5230\u54ea\u513f\u554a\uff1f\u5973\u4eba\u7684\u623f\u91cc\u59d3\u960e\u7684\u53ef\u4e0d\u53bb\u3002\u201d\u5546\u8001\u592a\u5c31\u4f3c\u6ca1\u6709\u542c\u89c1\uff0c\u4ecd\u9053\uff1a\u201c\u6211\u6709\u8981\u7d27\u8bdd\u8ddf\u4f60\u8bf4\u3002\u201d \u3000\u3000\u960e\u57fa\u5fc3\u60f3\uff1a\u201c\u8fd9\u8001\u592a\u5a46\u5012\u6709\u51e0\u5206\u53e4\u602a\uff0c\u4e0d\u77e5\u5979\u53eb\u6211\u53bb\u54ea\u91cc\uff1f\u201d\u6b63\u5f85\u8bf4\uff1a \u3000\u3000\u201c\u960e\u5927\u7237\u6ca1\u7a7a\u8ddf\u4f60\u647d\u5506\u3002\u201d\u5546\u8001\u592a\u5df2\u8f6c\u8eab\u8d70\u5411\u5185\u5802\uff0c\u54d1\u58f0\u9053\uff1a\u201c\u4f60\u6ca1\u80c6\u5b50\uff0c\u4e5f\u5c31\u662f\u4e86\u3002\u201d\u960e\u57fa\u4ef0\u5929\u6253\u4e2a\u54c8\u54c8\uff0c\u7b11\u9053\uff1a\u201c\u6211\u6ca1\u80c6\u5b50\uff1f\u201d\u62d4\u811a\u8ddf\u53bb\u3002\u4e8c\u5be8\u4e3b\u4e3a\u4eba\u7ec6\u5fc3\uff0c\u5c06\u960e\u57fa\u7684\u9b3c\u5934\u5200\u9012\u8fc7\uff0c\u960e\u57fa\u5de6\u624b\u5012\u63d0\u4e86\u3002\u5546\u5b9d\u9707\u4e0d\u77e5\u6bcd\u4eb2\u53eb\u4ed6\u5165\u5185\u662f\u4f55\u7528\u610f\uff0c\u8ddf\u968f\u5728\u540e\u3002\u5546\u8001\u592a\u867d\u4e0d\u56de\u5934\uff0c\u5374\u542c\u51fa\u4e86\u513f\u5b50\u7684\u811a\u6b65\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u9707\u513f\u7559\u5728\u8fd9\u513f\uff01\u960e\u8001\u5927\uff0c\u4f60\u53eb\u5f1f\u5144\u4eec\u6682\u522b\u52a8\u624b\u3002\u201d\u8bf4\u8fd9\u51e0\u53e5\u8bdd\u65f6\u5411\u513f\u5b50\u548c\u960e\u57fa\u4e00\u773c\u4e5f\u6ca1\u77a7\uff0c\u4f46\u8bed\u97f3\u4e2d\u81ea\u6709\u4e00\u80a1\u5a01\u4e25\uff0c\u4f3c\u662f\u53d1\u53f7\u65bd\u4ee4\u4e00\u822c\u3002\u960e\u57fa\u9053\uff1a\u201c\u8fd9\u8bdd\u4e0d\u9519\uff0c\u5927\u4f19\u513f\u522b\u52a8\uff0c\u7b49\u6211\u56de\u6765\u53d1\u843d\u3002\u201d\u7fa4\u76d7\u8f70\u7136\u7b54\u5e94\uff0c\u4e8c\u5be8\u4e3b\u7528\u9ed1\u8bdd\u5406\u559d\u53d1\u4ee4\uff0c\u5206\u6d3e\u4eba\u624b\u76d1\u89c6\u6f02\u5ba2\uff0c\u9632\u4ed6\u4eec\u6709\u4f55\u5f02\u52a8\u3002 \u3000\u3000\u672c\u6765\u5546\u5b9d\u9707\u548c\u4e09\u4e2a\u4f8d\u536b\u52a9\u7740\u9556\u884c\uff0c\u7fa4\u76d7\u5df2\u843d\u4e0b\u98ce\uff0c\u4f46\u5546\u5b9d\u9707\u548c\u5f90\u94ee\u4e3a\u7530\u5f52\u519c\u6240\u4f24\uff0c\u9a6c\u884c\u7a7a\u6328\u4e86\u960e\u57fa\u4e00\u811a\u540e\uff0c\u518d\u7ed9\u7530\u5f52\u519c\u6253\u4e86\u4e00\u638c\uff0c\u4f24\u52bf\u66f4\u91cd\uff0c\u5f62\u52bf\u53c8\u81ea\u9006\u8f6c\u3002\u7fa4\u76d7\u65e2\u4e0d\u52ab\u9556\uff0c\u9556\u884c\u4eba\u4f17\u4e5f\u5c31\u9759\u4ee5\u4f8d\u53d8\u3002 \u3000\u3000\u960e\u57fa\u8ddf\u968f\u5728\u5546\u8001\u592a\u80cc\u540e\uff0c\u53ea\u89c1\u5979\u80cc\u810a\u5f13\u8d77\uff0c\u811a\u6b65\u8e52\u8dda\uff0c\u539f\u5148\u5fc3\u4e2d\u5b58\u7740\u4e09\u5206\u63d0\u9632\u4e4b\u610f\uff0c\u6b64\u65f6\u5c3d\u6570\u629b\u5374\uff0c\u7b11\u95ee\uff1a\u201d\u5546\u8001\u5a46\u5b50\uff0c\u53eb\u6211\u8fdb\u6765\u53ef\u662f\u732e\u5b9d\u4e48\uff1f\u201d \u3000\u3000\u5546\u8001\u592a\u9053\uff1a\u201d\u4e0d\u9519\uff0c\u662f\u732e\u5b9d\u3002\u201d\u960e\u57fa\u5fc3\u4e2d\u4e00\u52a8\uff0c\u4ed6\u4e00\u751f\u6700\u662f\u8d2a\u8d22\uff0c\u77a7\u8fd9\u5546\u5bb6\u5821\u4e00\u526f\u5927\u5bb6\u6c14\u6d3e\uff0c\u5e95\u5b50\u751a\u662f\u6bb7\u5b9e\uff0c\u8bf4\u4e0d\u5b9a\u90a3\u5546\u8001\u592a\u4e00\u89c1\u5f3a\u4eba\u964d\u4e34\uff0c\u5413\u7834\u4e86\u80c6\uff0c\u81ea\u884c\u732e\u4e0a\u73e0\u5b9d\u8d4e\u547d\uff0c\u4e5f\u662f\u6709\u7684\uff0c\u4e0d\u7531\u5f97\u53c8\u60ca\u53c8\u559c\u3002\u53ea\u89c1\u5979\u4e00\u76f4\u5411\u540e\u8fdb\u8d70\u53bb\uff0c\u63a5\u8fde\u7a7f\u8fc7\u4e09\u9053\u9662\u5b50\uff0c\u5230\u4e86\u6700\u540e\u9762\u7684\u4e00\u95f4\u5c4b\u5916\uff0c\u5440\u7684\u4e00\u58f0\u628a\u95e8\u63a8\u5f00\uff0c\u81ea\u5df1\u5148\u8d70\u4e86\u8fdb\u53bb\uff0c\u8bf4\u9053\uff1a\u201d\u8bf7\u8fdb\u6765\u5427\uff01\u201d \u3000\u3000\u95f4\u4ece\u4f38\u5934\u5411\u623f\u91cc\u4e00\u63a2\uff0c\u89c1\u662f\u4e00\u95f4\u4e24\u4e08\u89c1\u65b9\u7684\u7816\u623f\uff0c\u91cc\u9762\u7a7a\u7a7a\u8361\u8361\uff0c\u53ea\u89c1\u4e00\u5f20\u65b9\u684c\uff0c\u66f4\u65e0\u522b\u7269\uff0c\u5fae\u611f\u8df7\u8e4a\uff0c\u63d0\u6b65\u8fdb\u53bb\uff0c\u5927\u58f0\u9053\u3002\u201c\u6709\u8bdd\u5feb\u8bf4\uff0c\u53ef\u522b\u88c5\u795e\u5f04\u9b3c\u7684\u3002\u201d\u5546\u8001\u592a\u4e0d\u7b54\uff0c\u4f38\u4e0b\u5173\u4e0a\u6728\u95e8\uff0c\u53c8\u4e0a\u4e86\u95e8\u95e9\u3002\u960e\u57fa\u5927\u5947\uff0c\u56db\u4e0b\u6253\u91cf\uff0c\u53ea\u89c1\u684c\u4e0a\u653e\u7977\u4e00\u5757\u7075\u724c\uff0c\u4e0a\u4e66\u201c\u5148\u592b\u5546\u5251\u9e23\u4e4b\u7075\u4f4d\u201d\u3002\u960e\u57fa\u5fc3\u60f3\uff1a\u201c\u5546\u5251\u9e23\uff0c\u5546\u5251\u9e23\uff0c\u8fd9\u540d\u5b57\u597d\u719f\uff0c\u90a3\u662f\u8c01\u554a\uff1f\u201d\u4e00\u65f6\u5374\u60f3\u4e0d\u8d77\u6765\u3002 \u3000\u3000\u5546\u8001\u592a\u7f13\u7f13\u8bf4\u9053\uff1a\u201d\u4f60\u7adf\u6562\u4e0a\u5546\u5bb6\u5821\u6765\u653e\u8086\uff0c\u53ef\u7b97\u5f97\u5927\u80c6\u3002\u82e5\u662f\u5148\u592b\u5728\u4e16\uff0c\u5341\u4e2a\u960e\u57fa\u4e5f\u65e9\u780d\u4e86\u3002\u4eca\u65e5\u5546\u5bb6\u5821\u867d\u53ea\u5269\u4e0b\u5b64\u513f\u5be1\u5987\uff0c\u5374\u4e5f\u5bb9\u4e0d\u5f97\u72d7\u76d7\u9f20\u7a83\u4e4b\u8f88\u4e0a\u95e8\u6b3a\u4fae\u3002\u201d\u51e0\u53e5\u8bdd\u8bf4\u5b8c\uff0c\u7a81\u7136\u8170\u677f\u4e00\u633a\uff0c\u53cc\u76ee\u70af\u70af\u653e\u5149\uff0c\u51db\u7136\u903c\u89c6\uff0c\u4e00\u4e2a\u8e52\u8dda\u9f99\u949f\u7684\u8001\u5987\uff0c\u970e\u65f6\u95f4\u53d8\u5f97\u82f1\u6c14\u52c3\u52c3\u3002 \u3000\u3000\u960e\u57fa\u5fae\u5fae\u4e00\u60ca\uff0c\u5fc3\u60f3\uff1a\u201c\u539f\u6765\u8fd9\u5a46\u5a18\u662f\u6545\u610f\u88c5\u8001\u3002\u201d\u4f46\u60f3\u5230\u4e00\u4e2a\u5973\u6d41\u4e4b\u8f88\uff0c\u53c8\u6709\u4f55\u60e7\uff0c\u7b11\u9053\uff1a\u201c\u4e0a\u95e8\u4e5f\u4e0a\uff0c\u6b3a\u4eba\u4e5f\u6b3a\u4e86\uff0c\u4f60\u53c8\u54ac\u6211\u4e00\u53e3\uff1f\u201d \u3000\u3000\u5546\u8001\u592a\u970d\u5730\u8d70\u5230\u684c\u65c1\uff0c\u4ece\u7075\u724c\u540e\u9762\u6367\u51fa\u4e00\u4e2a\u9ec4\u8272\u5305\u88b1\uff0c\u90a3\u5305\u88b1\u7070\u5c18\u5806\u79ef\uff0c\u653e\u5728\u7075\u724c\u4e4b\u540e\u6beb\u4e0d\u62a2\u773c\u3002\u5979\u4e5f\u4e0d\u62cd\u53bb\u7070\u5c18\uff0c\u987a\u624b\u89e3\u4e86\u7ed3\u5b50\uff0c\u6253\u5f00\u5305\u88b1\uff0c\u53ea\u89c1\u7d2b\u5149\u95ea\u95ea\uff0c\u51b7\u6c14\u68ee\u68ee\uff0c\u5374\u662f\u4e00\u67c4\u539a\u80cc\u8584\u5203\u7d2b\u91d1\u516b\u5366\u5200\u3002\u960e\u57fa\u66ae\u5730\u91cc\u8bb0\u8d77\u5341\u4f59\u5e74\u524d\u7684\u4e00\u4ef6\u5f80\u4e8b\uff0c\u5012\u9000\u4e24\u6b65\uff0c\u5de6\u624b\u5012\u63d0\u7740\u7684\u9b3c\u5934\u5200\u4ea4\u4e0e\u53f3\u624b\uff0c\u53eb\u9053\uff1a\u201c\u516b\u5366\u5200\u5546\u5251\u9e23\uff01\u201d \u3000\u3000\u5546\u8001\u592a\u8138\u8272\u4e00\u6c89\uff0c\u53eb\u9053\uff1a\u201c\u8c6a\u6770\u867d\u901d\u94a2\u5200\u5728\uff01\u59be\u8eab\u5c31\u51ed\u5148\u592b\u8fd9\u628a\u516b\u5366\u5200\uff0c\u8981\u9886\u6559\u960e\u8001\u5927\u7684\u9ad8\u62db\u3002\u201d\u5ffd\u5730\u6293\u4f4f\u5200\u67c4\uff0c\u4e00\u62db\u201c\u7ae5\u5b50\u62dc\u4f5b\u201d\uff0c\u5411\u7075\u4f4d\u884c\u4e86\u4e00\u793c\uff0c\u56de\u8fc7\u8eab\u6765\uff0c\u5df2\u6210\u516b\u5366\u5200\u6cd5\u4e2d\u7684\u7b2c\u4e00\u62db\u201c\u4e0a\u52bf\u5de6\u624b\u62b1\u5200\u201d\u3002\u4f46\u89c1\u5979\u6c89\u80a9\u5760\u65f6\uff0c\u6c14\u655b\u795e\u805a\uff0c\u54ea\u91cc\u6709\u534a\u5206\u8870\u8fc8\u8001\u6001\uff1f \u3000\u3000\u960e\u57fa\u867d\u7136\u5fae\u5b58\u6212\u5fc3\uff0c\u4f46\u60f3\u4ee5\u767e\u80dc\u795e\u62f3\u9a6c\u884c\u7a7a\u8fd9\u7b49\u82f1\u96c4\uff0c\u5c1a\u4e14\u8d25\u5728\u81ea\u5df1\u624b\u91cc\uff0c\u82e5\u662f\u5546\u5251\u9e23\u590d\u751f\uff0c\u6216\u8bb8\u8981\u60e7\u4ed6\u51e0\u5206\uff0c\u8fd9\u5546\u8001\u592a\u672c\u9886\u518d\u9ad8\u4e5f\u662f\u6709\u9650\uff0c\u5f53\u4e0b\u9b3c\u5934\u5200\u5728\u7a7a\u4e2d\u865a\u5288\u4e00\u62db\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u8981\u6bd4\u8bd5\u5200\u6cd5\uff0c\u4f55\u4e0d\u5c31\u5728\u5927\u5385\u4e4b\u4e2d\uff1f\u5df4\u5df4\u5730\u5230\u8fd9\u513f\u6765\uff0c\u96be\u9053\u5b9a\u8981\u4e08\u592b\u7684\u6b7b\u4eba\u724c\u4f4d\u7ed9\u5728\u4e00\u65c1\u77a7\u7740\uff0c\u624d\u663e\u5f97\u51fa\u672c\u4e8b\u4e48\uff1f\u201d \u3000\u3000\u5546\u8001\u592a\u51db\u7136\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5148\u592b\u5a01\u7075\uff0c\u9707\u6151\u9f20\u8f88\u3002\u201d\u960e\u57fa\u4e0d\u81ea\u7981\u5730\u5411\u90a3\u7075\u724c\u671b\u4e86\u4e00\u773c\uff0c\u5fc3\u4e2d\u6709\u4e9b\u53d1\u6bdb\uff0c\u6025\u6b32\u4e86\u7ed3\u6b64\u4e8b\uff0c\u8d70\u51fa\u8fd9\u95f4\u51b7\u51b0\u51b0\u3001\u9ed1\u6c89\u6c89\u7684\u7075\u5802\uff0c\u8bf4\u9053\uff1a\u201c\u5546\u8001\u592a\uff0c\u4f60\u53d1\u62db\u5427\u3002\u201d\u5546\u8001\u592a\u9053\uff1a\u201c\u4f60\u662f\u5ba2\u4eba\uff0c\u960e\u5be8\u4e3b\u5148\u8bf7\u3002\u201d\u5979\u542c\u4ed6\u6539\u4e86\u79f0\u547c\uff0c\u53e3\u5934\u4e0a\u5ba2\u6c14\u4e86\u4e9b\uff0c\u4e8e\u662f\u4e5f\u79f0\u4ed6\u4e00\u58f0\u201c\u5be8\u4e3b\u201d\u3002 \u3000\u3000\u960e\u57fa\u9053\uff1a\u201c\u5728\u4e0b\u8ddf\u5546\u5bb6\u5821\u65e0\u51a4\u65e0\u4ec7\uff0c\u8fd9\u6b21\u52ab\u9556\uff0c\u4e43\u662f\u51b2\u7740\u9a6c\u8001\u5934\u513f\u800c\u6765\u3002 \u3000\u3000\u5546\u8001\u592a\u65e2\u7136\u5b9a\u8981\u51fa\u5934\uff0c\u54b1\u4eec\u70b9\u5230\u4e3a\u6b62\uff0c\u4e0d\u5fc5\u771f\u780d\u771f\u6740\u3002\u201d\u5546\u8001\u592a\u53cc\u7709\u7ad6\u8d77\uff0c\u4f4e\u6c89\u7740\u55d3\u5b50\u9053\uff1a\u201c\u6ca1\u90a3\u4e48\u5bb9\u6613\uff01\u5546\u5251\u9e23\u4e00\u751f\u82f1\u96c4\uff0c\u4ed6\u5efa\u4e0b\u7684\u5546\u5bb6\u5821\u5c82\u5bb9\u4eba\u8bf4\u8fdb\u4fbf\u8fdb\uff0c\u8bf4\u51fa\u4fbf\u51fa\uff1f\u201d\u960e\u57fa\u4e5f\u81ea\u607c\u4e86\uff0c\u9053\uff1a\u201c\u4f9d\u4f60\u8bf4\u4fbf\u600e\u5730\uff1f\u201d\u5546\u8001\u592a\u9053\uff1a\u201c\u4f60\u8d25\u4e86\u6211\u624b\u4e2d\u94a2\u5200\uff0c\u5c06\u6211\u4eba\u5934\u5272\u53bb\uff0c\u8fde\u6211\u513f\u5b50\u4e5f\u4e00\u5e76\u6740\u4e86\u2026\u2026\u201d\u960e\u57fa\u5413\u4e86\u4e00\u8df3\uff0c\u5fc3\u60f3\uff1a\u201c\u6211\u8ddf\u4f60\u53c8\u65e0\u6df1\u51a4\u5927\u4ec7\uff0c\u53ea\u4e0d\u8fc7\u65e0\u610f\u5192\u72af\uff0c\u4f55\u5fc5\u8fd9\u4e48\u6027\u547d\u76f8\u62fc\uff1f\u201d \u3000\u3000\u53ea\u542c\u5979\u53c8\u9053\uff1a\u201c\u82e5\u662f\u59be\u8eab\u80dc\u5f97\u4e00\u62db\u534a\u5f0f\uff0c\u960e\u5be8\u4e3b\u9888\u4e0a\u8111\u888b\u4e5f\u5f97\u7559\u4e0b\u3002\u201d\u6b64\u8a00\u4e00\u51fa\uff0c\u8ddf\u7740\u559d\u9053\uff1a\u201c\u8fdb\u62db\uff01\u201d \u3000\u3000\u960e\u57fa\u6c14\u5f80\u4e0a\u51b2\uff0c\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u8981\u4f60\u6bcd\u5b50\u6027\u547d\u4f55\u7528\uff1f\u53ea\u8981\u4f60\u8fd9\u5ea7\u8fde\u7530\u8fde\u5b85\u7684\u5546\u5bb6\u5821\u3002\u201d\u8bf4\u7740\u5c06\u5200\u4e00\u6643\uff0c\u6b32\u5f85\u8fdb\u62db\uff0c\u5546\u8001\u592a\u4e00\u62db\u201c\u671d\u9633\u5200\u201d\u5df2\u5288\u4e86\u8fc7\u6765\u3002\u8fd9\u4e00\u5200\u53c8\u5feb\u53c8\u731b\uff0c\u960e\u57fa\u6025\u5fd9\u4fa7\u5934\uff0c\u53ea\u542c\u547c\u7684\u4e00\u54cd\uff0c\u9707\u5f97\u53f3\u8033\u4e2d\u55e1\u55e1\u4f5c\u58f0\uff0c\u90a3\u5200\u4ece\u53f3\u816e\u8fb9\u76f4\u524a\u4e0b\u53bb\uff0c\u76f8\u8ddd\u4e0d\u8fc7\u5bf8\u4f59\uff0c\u53ea\u8981\u95ea\u907f\u6162\u5f97\u4e00\u970e\uff0c\u8fd9\u8111\u888b\u5c82\u4e0d\u662f\u7ed9\u5979\u5288\u6210\u4e24\u534a\uff1f \u3000\u3000\u8fd9\u4e00\u5200\u5148\u58f0\u593a\u4eba\uff0c\u960e\u57fa\u7ed9\u5979\u7684\u731b\u780d\u6076\u6740\u5413\u5f97\u4e3a\u4e4b\u4e00\u6014\uff0c\u77e5\u5979\u7b2c\u4e8c\u62db\u5b9a\u662f\u56de\u5200\u524a\u8170\uff0c\u5fd9\u6c89\u9b3c\u5934\u5200\u4e00\u67b6\uff0c\u5f53\u7684\u4e00\u54cd\uff0c\u53cc\u5200\u76f8\u4ea4\uff0c\u706b\u5149\u56db\u6e85\u3002\u960e\u57fa\u89c9\u5979\u8182\u529b\u5e73\u5e73\uff0c\u8fdc\u900a\u4e8e\u5df1\uff0c\u672c\u5df2\u63d0\u8d77\u7684\u5fc3\u53c8\u653e\u4e86\u4e0b\u6765\uff0c\u4e8e\u662f\u4e00\u62db\u201c\u63a8\u5200\u5272\u5589\u201d\uff0c\u63a8\u4e86\u8fc7\u53bb\u3002\u5546\u8001\u592a\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u4fa7\u8eab\u907f\u8fc7\uff0c\u9053\uff1a\u201c\u56db\u95e8\u5200\u6cd5\uff0c\u4e0d\u8db3\u4e3a\u5947\u3002\u201d \u3000\u3000\u960e\u57fa\u7b11\u9053\uff1a\u201c\u5e73\u5e73\u65e0\u5947\uff0c\u5374\u8981\u80dc\u4f60\u3002\u201d\u8bed\u58f0\u672a\u6bd5\uff0c\u8e0f\u6b65\u4e0a\u524d\uff0c\u4f7f\u51fa\u4e00\u62db\u201c\u8fdb\u624b\u8fde\u73af\u5200\u201d\u3002\u5546\u8001\u592a\u4e0d\u67b6\u4e0d\u8ba9\uff0c\u7adf\u62a2\u5bf9\u653b\uff0c\u201c\u524a\u8033\u64a9\u816e\u201d\uff0c\u4e3e\u5200\u659c\u780d\u3002 \u3000\u3000\u960e\u57fa\u5927\u60ca\uff0c\u5fc3\u60f3\uff1a\u201c\u600e\u4e48\u62da\u547d\u4e86\uff1f\u201d\u672c\u6765\u6b66\u672f\u4e2d\u539f\u6709\u4e0d\u6551\u81ea\u8eab\u3001\u53cd\u51fb\u654c\u4eba\u7684\u62db\u6570\uff0c\u4f46\u8fd9\u79cd\u62fc\u7740\u4e24\u8d25\u4ff1\u4f24\u7684\u6253\u6cd5\uff0c\u603b\u662f\u5e26\u7740\u4e5d\u5206\u5192\u9669\uff0c\u975e\u81f3\u654c\u62db\u96be\u89e3\u3001\u4e07\u4e0d\u5f97\u5df2\u4e4b\u9645\u51b3\u8ba1\u4e0d\u7528\u3002\u6b64\u65f6\u5546\u8001\u592a\u53ea\u8981\u4e3e\u5200\u4e00\u6321\uff0c\u5c31\u80fd\u67b6\u5f00\u654c\u62db\uff0c\u54ea\u77e5\u5979\u7adf\u884c\u9669\u7740\uff0c\u4e0d\u987e\u6027\u547d\u5730\u5bf9\u653b\u3002 \u3000\u3000\u5979\u4e0d\u987e\u6027\u547d\uff0c\u960e\u57fa\u5374\u4e0d\u5f97\u4e0d\u987e\uff0c\u5371\u6025\u4e2d\u6251\u5730\u4e00\u6eda\uff0c\u53cd\u8eab\u4e00\u817f\u3002\u8fd9\u4e00\u817f\u53bb\u52bf\u5947\u5999\uff0c\u5546\u8001\u592a\u624b\u8155\u9669\u88ab\u8e22\u4e2d\uff0c\u516b\u5366\u5200\u6025\u5fd9\u7ffb\u8fc7\uff0c\u960e\u57fa\u624d\u6536\u817f\u8f6c\u8eab\u3002\u539f\u6765\u4ed6\u7ec3\u719f\u4e86\u5341\u4f59\u62db\u602a\u5f02\u62f3\u811a\uff0c\u8fd1\u5e74\u6765\u5728\u6c5f\u6e56\u4e0a\u6218\u65e0\u4e0d\u80dc\uff0c\u5200\u6cd5\u5374\u662f\u5e73\u5e73\uff0c\u4f46\u4ed6\u53e6\u6709\u5947\u7740\uff0c\u5c06\u90a3\u5341\u51e0\u8def\u5947\u62f3\u602a\u817f\u5939\u5728\u5200\u6cd5\u4e4b\u4e2d\uff0c\u4e00\u8def\u7b2c\u4e09\u56db\u6d41\u7684\u56db\u9762\u5200\u767b\u65f6\u5316\u8150\u673d\u4e3a\u795e\u5947\uff0c\u5c45\u7136\u4e5f\u6253\u8d25\u4e86\u4e0d\u5c11\u82f1\u96c4\u597d\u6c49\uff0c\u6b64\u523b\u65bd\u5c06\u51fa\u6765\uff0c\u6bcf\u5f53\u5200\u6cd5\u4e0a\u4e00\u8d70\u4e0b\u98ce\uff0c\u62f3\u811a\u4e00\u52a8\uff0c\u7acb\u65f6\u6273\u8f6c\u52a3\u52bf\u3002 \u3000\u3000\u9877\u523b\u4e4b\u95f4\u4e00\u4e2a\u8001\u5987\uff0c\u4e00\u4e2a\u76d7\u9b41\uff0c\u53cc\u5200\u75be\u821e\uff0c\u5728\u7816\u623f\u4e2d\u6597\u5f97\u5c18\u571f\u98de\u626c\u3002\u960e\u57fa\u89c1\u5546\u8001\u592a\u5200\u6cd5\u7cbe\u5999\uff0c\u81ea\u5df1\u82e5\u975e\u9760\u90a3\u5341\u4f59\u62db\u62f3\u811a\u6551\u9a7e\u4fdd\u547d\uff0c\u65e9\u5df2\u4e27\u751f\u4e8e\u516b\u5366\u5200\u4e0b\uff0c\u4e00\u4e2a\u8001\u5987\u5c45\u7136\u6709\u6b64\u6b66\u529f\uff0c\u4e0d\u7531\u5f97\u6697\u6697\u79f0\u5947\uff0c\u5fc3\u9053\uff1a\u201c\u5982\u6b64\u4e45\u6218\u4e0b\u53bb\uff0c\u82e5\u662f\u4e00\u4e2a\u758f\u5ffd\uff0c\u7ed9\u5979\u524a\u53bb\u534a\u8fb9\u8111\u888b\uff0c\u90a3\u53ef\u4e0d\u662f\u73a9\u7684\u3002\u201d\u5f53\u4e0b\u7528\u957f\u85cf\u62d9\uff0c\u4e0d\u4f4f\u5730\u62f3\u6253\u8db3\u8e22\uff0c\u5076\u7136\u624d\u780d\u4e0a\u51e0\u5200\u3002\u8fd9\u6cd5\u513f\u679c\u7136\u751f\u6548\uff0c\u5546\u8001\u592a\u96be\u4ee5\u62b5\u6321\uff0c\u4e0d\u65ad\u9000\u907f\u3002\u960e\u57fa\u6d0b\u6d0b\u5f97\u610f\uff0c\u7b11\u9053\uff1a\u201c\u563f\u563f\uff0c\u5546\u5251\u9e23\u4ec0\u4e48\u82f1\u96c4\u4e86\u5f97\uff0c\u516b\u5366\u5200\u6cd5\u4e5f\u4e0d\u8fc7\u5982\u6b64\u3002\u201d \u3000\u3000\u5546\u8001\u592a\u5bf9\u5148\u592b\u656c\u82e5\u5929\u795e\uff0c\u6b64\u8a00\u72af\u4e86\u5979\u7684\u5927\u5fcc\uff0c\u7a81\u7136\u95f4\u76ee\u9732\u51f6\u5149\uff0c\u5200\u6cd5\u4e00\u53d8\uff0c\u56db\u4e0b\u6e38\u8d70\uff0c\u767d\u5149\u95ea\u95ea\uff0c\u56db\u9762\u516b\u65b9\u653b\u4e86\u4e0a\u53bb\u3002\u6b64\u523b\u5979\u6bcf\u4e00\u62db\u90fd\u662f\u62fc\u547d\uff0c\u6bcf\u4e00\u62db\u90fd\u662f\u62a2\u653b\uff0c\u65e9\u5c06\u81ea\u5df1\u751f\u6b7b\u7f6e\u4e4b\u5ea6\u5916\u3002\u960e\u57fa\u5927\u53eb\uff1a\u201c\u4f60\u75af\u4e86\u4e48\uff1f\u5582\uff0c\u5546\u8001\u592a\uff0c\u4f60\u4e08\u592b\u53ef\u4e0d\u662f\u6211\u6740\u7684\uff0c\u4f60\u8ddf\u6211\u62fc\u547d\u5e72\u4e48\uff1f\u5582\u5582\uff0c\u4f60\u542c\u89c1\u6211\u8bf4\u8bdd\u6ca1\u6709\uff1f\u201d \u3000\u3000\u4e00\u9762\u53eb\u56b7\uff0c\u4e00\u9762\u9003\u7a9c\u3002 \u3000\u3000\u4ed6\u6597\u5fd7\u4e00\u5931\uff0c\u5546\u8001\u592a\u66f4\u662f\u780d\u6740\u5f97\u5982\u706b\u5982\u8336\uff0c\u51fa\u5200\u8d8a\u6765\u8d8a\u5feb\uff0c\u6b64\u65f6\u960e\u57fa\u7684\u602a\u5f02\u62f3\u811a\u5df2\u6765\u4e0d\u53ca\u4f7f\u7528\uff0c\u53ea\u60f3\u62d4\u5f00\u95e8\u95e9\uff0c\u9003\u51fa\u5c4b\u53bb\u3002\u9762\u4e34\u4e00\u53ea\u75af\u4e86\u7684\u6bcd\u5927\u866b\uff0c\u4ed6\u54ea\u91cc\u8fd8\u60f3\u5230\u4ec0\u4e48\u80dc\u8d1f\u8363\u8fb1\uff0c\u552f\u4e00\u7684\u5ff5\u5934\u53ea\u662f\u5982\u4f55\u9003\u547d\u3002 \u3000\u3000\u4ed6\u6570\u6b21\u8981\u53bb\u62d4\u5f00\u95e8\u95e9\uff0c\u603b\u662f\u7ed9\u5546\u8001\u592a\u903c\u5f97\u7edd\u65e0\u4f59\u6687\uff0c\u773c\u89c1\u5979\u201c\u591c\u53c9\u63a2\u6d77\u201d\uff0c\u201c\u4e0a\u6b65\u64a9\u5200\u201d\uff0c\u201c\u4ed9\u4eba\u6307\u8def\u201d\uff0c\u4e00\u5200\u731b\u4f3c\u4e00\u5200\uff0c\u960e\u57fa\u628a\u5fc3\u4e00\u6a2a\uff0c\u53cd\u80cc\u4e00\u817f\u8e22\u51fa\uff0c\u53eb\u58f0\u201c\u5931\u966a\uff01\u201d\u5de6\u8db3\u7528\u52b2\uff0c\u7a9c\u8eab\u4ece\u7a97\u53e3\u8dc3\u4e86\u51fa\u53bb\u3002\u5c82\u77e5\u5546\u8001\u592a\u62da\u7740\u53d7\u4ed6\u8fd9\u4e00\u817f\uff0c\u5982\u5f71\u968f\u5f62\uff0c\u8ddf\u7740\u4e00\u5200\u780d\u4e86\u8fc7\u53bb\u3002\u53ea\u542c\u4e8c\u4eba\u540c\u58f0\u201c\u554a\u54df\u201d\uff0c\u4e00\u9f50\u8dcc\u5728\u7a97\u4e0b\u3002 \u3000\u3000\u5546\u8001\u592a\u7acb\u5373\u8dc3\u8d77\uff0c\u80a9\u5934\u867d\u88ab\u8e22\u4e2d\uff0c\u672a\u53d7\u91cd\u4f24\u3002\u960e\u57fa\u7684\u5927\u817f\u4e0a\u5374\u7ed9\u7ed3\u7ed3\u5b9e\u5b9e\u7684\u4e00\u5200\u780d\u7740\uff0c\u518d\u4e5f\u96be\u4ee5\u7ad9\u7acb\u3002 \u3000\u3000\u8fd9\u4e00\u4e0b\u4ed6\u5413\u5f97\u9b42\u98de\u5929\u5916\uff0c\u53ea\u89c1\u5546\u8001\u592a\u773c\u5e03\u7ea2\u4e1d\uff0c\u94a2\u5200\u8ddf\u7740\u5288\u4e0b\uff0c\u5fd9\u4f38\u53cc\u624b\u63e1\u4f4f\u4e86\u5979\u5c0f\u817f\uff0c\u5927\u53eb\uff1a\u201c\u9976\u547d\uff01\u201d \u3000\u3000\u5546\u8001\u592a\u5e7c\u65f6\u966a\u4f34\u7236\u4eb2\u3001\u5a5a\u540e\u8ddf\u968f\u4e08\u592b\u95ef\u8361\u6c5f\u6e56\uff0c\u6bd5\u751f\u4f1a\u8fc7\u65e0\u6570\u6b66\u6797\u8c6a\u6770\uff0c\u5982\u773c\u524d\u8fd9\u822c\u6ca1\u51fa\u606f\u7684\u6df7\u86cb\uff0c\u5374\u662f\u4ece\u672a\u89c1\u8fc7\uff0c\u5fc3\u4e2d\u4e00\u6014\uff0c\u8fd9\u4e00\u5200\u5c31\u780d\u4e0d\u4e0b\u53bb\u3002 \u3000\u3000\u960e\u57fa\u7d22\u6027\u722c\u5728\u5730\u4e0b\uff0c\u51ac\u51ac\u51ac\u5730\u5927\u78d5\u54cd\u5934\uff0c\u6c42\u9053\uff1a\u201c\u5927\u4eba\u4e0d\u8bb0\u5c0f\u4eba\u8fc7\uff01\u6211\u662f\u72d7\u5a18\u517b\u7684\u738b\u516b\u86cb\uff01\u8001\u592a\u592a\u8981\u62bd\u7b4b\u5265\u76ae\uff0c\u6089\u4ece\u5c0a\u4fbf\uff0c\u8fd9\u4e00\u5200\u52a1\u6073\u7559\u4ed6\u4e00\u7559\u3002\u201d \u3000\u3000\u5546\u8001\u592a\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u597d\uff0c\u547d\u4fbf\u9976\u4f60\u3002\u4f60\u8bb0\u4f4f\u4e86\uff0c\u4eca\u65e5\u6bd4\u6b66\u4e4b\u4e8b\uff0c\u4e0d\u8bb8\u6f0f\u51fa\u4e00\u5b57\u3002\u201d\u960e\u57fa\u6c42\u4e4b\u4e0d\u5f97\uff0c\u8fde\u58f0\u7b54\u5e94\u3002\u5546\u8001\u592a\u9053\uff1a\u201c\u53bb\u5427\uff01\u201d\u960e\u57fa\u966a\u4e2a\u7b11\u8138\uff0c\u53c8\u78d5\u4e86\u4e24\u4e2a\u5934\uff0c\u722c\u5c06\u8d77\u6765\uff0c\u7528\u5200\u62c4\u5728\u5730\u4e0b\uff0c\u4e00\u8df7\u4e00\u62d0\u5730\u8d70\u51fa\u3002\u5546\u8001\u592a\u5389\u58f0\u8bf4\u9053\uff1a\u201c\u7ad9\u4f4f\uff01\u54b1\u4eec\u62da\u5200\u4e4b\u524d\uff0c\u8bf4\u8fc7\u4efb\u8c01\u8f93\u4e86\uff0c\u5c31\u5f97\u5728\u5546\u5bb6\u5821\u7559\u4e0b\u8111\u888b\u3002 \u3000\u3000\u4f60\u8bf4\u8bdd\u4e0d\u7b97\u6570\uff0c\u96be\u9053\u6211\u4e5f\u540c\u4f60\u4e00\u822c\u6df7\u5e10\uff1f\u201d \u3000\u3000\u960e\u57fa\u5413\u4e86\u4e00\u8df3\uff0c\u56de\u8fc7\u5934\u6765\uff0c\u53ea\u89c1\u5546\u8001\u592a\u8138\u4e0a\u72b9\u4f3c\u7f69\u7740\u4e00\u5c42\u4e25\u971c\uff0c\u663e\u662f\u5e76\u975e\u8bf4\u7b11\uff0c\u54c0\u6c42\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u4e0d\u662f\u9976\u4e86\u6211\u4e48\uff1f\u201d\u5546\u8001\u592a\u9053\uff1a\u201c\u9976\u5f97\u4f60\u6027\u547d\uff0c\u9976\u4e0d\u5f97\u4f60\u8111\u888b\u3002\u201d\u8bf4\u7740\u624b\u4e2d\u516b\u5366\u5200\u4e00\u626c\uff0c\u5389\u58f0\u9053\uff1a\u201c\u5546\u5251\u9e23\u516b\u5366\u5200\u51fa\u624b\uff0c\u7d20\u4e0d\u7a7a\u56de\uff0c\u8fc7\u6765\uff01\u201d\u960e\u57fa\u5495\u51ac\u4e00\u58f0\uff0c\u53cc\u819d\u843d\u5730\uff0c\u5546\u8001\u592a\u624b\u6cd5\u597d\u5feb\uff0c\u5de6\u624b\u63d0\u8d77\u4ed6\u7684\u8fab\u5b50\uff0c\u53f3\u624b\u516b\u5366\u5200\u4e00\u6325\uff0c\u5df2\u5c06\u4ed6\u8fab\u5b50\u5272\u4e0b\uff0c\u559d\u9053\uff1a\u201c\u8fab\u5b50\u7559\u5728\u5546\u5bb6\u5821\uff0c\u4ece\u4eca\u540e\u524a\u53d1\u4e3a\u50e7\uff0c\u4e0d\u5f97\u518d\u5728\u9ed1\u9053\u4e2d\u53ae\u6df7\uff01\u201d\u960e\u57fa\u558f\u558f\u8fde\u58f0\u3002\u5546\u8001\u592a\u9053\uff1a\u201c\u4f60\u88f9\u597d\u817f\u4f24\uff0c\u6234\u4e0a\u5e3d\u5b50\uff0c\u518d\u5230\u5385\u4e0a\u62db\u547c\u4f60\u7684\u624b\u4e0b\u6eda\u51fa\u5546\u5bb6\u5821\u3002\u201d \u3000\u3000\u5927\u5385\u4e0a\u4f17\u4eba\u4f60\u77a7\u6211\uff0c\u6211\u77a7\u4f60\uff0c\u4e0d\u77e5\u4e8c\u4eba\u5728\u5185\u5802\u8bf4\u4e9b\u4ec0\u4e48\uff0c\u7b49\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u624d\u89c1\u5546\u8001\u4eba\u98a4\u5dcd\u5dcd\u5730\u51fa\u6765\u3002\u960e\u57fa\u8ddf\u5728\u540e\u9762\uff0c\u6162\u541e\u541e\u5730\u8d70\u51fa\uff0c\u53eb\u9053\uff1a\u201c\u4f17\u5144\u5f1f\uff0c\u94f6\u4e24\u4e0d\u8981\u4e86\uff0c\u5927\u4f19\u513f\u56de\u5be8\u53bb\u3002\u201d \u3000\u3000\u6b64\u8a00\u4e00\u51fa\uff0c\u4f17\u4eba\u65e0\u4e0d\u5927\u4e3a\u60ca\u6115\u3002\u4e8c\u5be8\u4e3b\u9053\uff1a\u201c\u5927\u54e5\u2026\u2026\u201d\u960e\u57fa\u9053\uff1a\u201c\u56de\u5be8\u8bf4\u8bdd\u3002\u201d\u5c06\u624b\u4e00\u6325\uff0c\u8d70\u51fa\u5385\u53bb\u3002\u4ed6\u4e0d\u6562\u9732\u51fa\u817f\u4e0a\u53d7\u4f24\u75d5\u8ff9\uff0c\u5f3a\u884c\u652f\u6491\uff0c\u54ac\u7d27\u7259\u5173\u51fa\u53bb\u3002\u4f17\u76d7\u4e0d\u6562\u8fdd\u62d7\uff0c\u5411\u7740\u4e00\u9798\u9798\u5df2\u7ecf\u5230\u624b\u7684\u94f6\u5b50\u72e0\u72e0\u671b\u4e86\u51e0\u773c\uff0c\u8f6c\u8eab\u9000\u51fa\u3002\u7247\u523b\u4e4b\u95f4\uff0c\u7fa4\u76d7\u9000\u5f97\u4e8e\u5e72\u51c0\u51c0\u3002 \u3000\u3000\u9976\u662f\u9a6c\u884c\u7a7a\u89c1\u591a\u8bc6\u5e7f\uff0c\u5374\u4e5f\u731c\u4e0d\u900f\u5176\u4e2d\u7684\u5965\u5999\uff0c\u53ea\u89c1\u960e\u57fa\u884c\u8fc7\u4e4b\u5904\uff0c\u5730\u4e0a\u70b9\u70b9\u6ef4\u6ef4\u7559\u4e0b\u4e00\u884c\u8840\u8ff9\uff0c\u6599\u60f3\u4ed6\u5728\u5185\u5802\u662f\u53d7\u4e86\u4f24\uff0c\u770b\u6765\u5546\u5bb6\u5821\u5185\u6697\u4f0f\u80fd\u4eba\uff0c\u5374\u54ea\u91cc\u6599\u5f97\u7740\u773c\u524d\u8fd9\u9f99\u949f\u8001\u5987\uff0c\u9002\u624d\u7adf\u548c\u4ed6\u62da\u4e86\u4e00\u573a\u751f\u6b7b\u51b3\u6218\u3002\u4ed6\u6276\u7740\u5973\u513f\u7684\u80a9\u5934\u7ad9\u8d77\u5f85\u8981\u65bd\u8c22\uff0c\u5546\u8001\u592a\u9053\uff1a\u201c\u9707\u513f\uff0c\u8ddf\u6211\u8fdb\u6765\uff01\u201d\u9a6c\u884c\u7a7a\u4e00\u6115\uff0c\u53ea\u89c1\u4ed6\u6bcd\u5b50\u4e8c\u4eba\u5f84\u81ea\u8fdb\u4e86\u5185\u5802\u3002 \u3000\u3000\u8fd9\u4e00\u4e0b\u9556\u884c\u4eba\u4f17\u4e0e\u4e09\u540d\u4f8d\u536b\u90fd\u7eb7\u7eb7\u8bae\u8bba\u8d77\u6765\uff0c\u6709\u7684\u8bf4\u5546\u8001\u592a\u65e7\u65f6\u5fc5\u4e0e\u90a3\u76d7\u9b41\u76f8\u8bc6\uff0c\u66fe\u6709\u6069\u4e8e\u4ed6\uff1b\u6709\u7684\u8bf4\u5546\u8001\u592a\u4e00\u987f\u529d\u55bb\uff0c\u52a8\u4ee5\u5229\u5bb3\uff0c\u90a3\u76d7\u9b41\u60f3\u5230\u4e0e\u5fa1\u524d\u4f8d\u536b\u4e3a\u654c\uff0c\u975e\u540c\u5c0f\u53ef\uff0c\u7ec8\u4e8e\u60ac\u5d16\u52d2\u9a6c\u3002\u6b63\u81ea\u778e\u731c\uff0c\u5546\u5b9d\u9707\u8d70\u4e86\u51fa\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u5bb6\u6bcd\u8bf7\u9a6c\u8001\u9556\u5934\u5185\u5802\u5949\u8336\u3002\u201d \u3000\u3000\u5185\u5802\u53d9\u8bdd\uff0c\u5546\u8001\u592a\u529d\u9a6c\u884c\u7a7a\u7559\u5728\u5546\u5bb6\u5821\u517b\u4f24\uff0c\u4e00\u9762\u6d3e\u4eba\u5230\u9644\u8fd1\u9556\u5c40\u9080\u540c\u884c\u76f8\u52a9\uff0c\u8f6c\u4fdd\u9556\u94f6\u524d\u5f80\u91d1\u9675\u3002\u7ecf\u6b64\u4e00\u5f79\uff0c\u9a6c\u884c\u7a7a\u96c4\u5fc3\u5168\u6d88\uff0c\u201c\u767e\u80dc\u795e\u62f3\u201d\u7684\u540d\u53f7\u54cd\u4e86\u6570\u5341\u5e74\uff0c\u5230\u5934\u6765\u5374\u6298\u5728\u4e00\u4e2a\u5e02\u4e95\u6d41\u6c13\u822c\u7684\u76d7\u8d3c\u624b\u4e2d\uff0c\u5bf9\u8d70\u9556\u7684\u5fc3\u767b\u65f6\u6de1\u4e86\u3002\u5546\u8001\u592a\u62a4\u9556\u4e0d\u5931\uff0c\u6069\u60c5\u592a\u91cd\uff0c\u5979\u7684\u610f\u601d\u4e0d\u6562\u4e0d\u9075\uff0c\u540c\u65f6\u4ed6\u5fc3\u5e95\u8fd8\u5b58\u4e86\u4e00\u4e2a\u5ff5\u5934\uff0c\u6781\u60f3\u89c1\u4e00\u89c1\u90a3\u4f4d\u632b\u8d25\u960e\u57fa\u7684\u6b66\u6797\u9ad8\u624b\u3002\u5f53\u4e0b\u8c22\u4e86\u5546\u8001\u592a\u7684\u597d\u610f\uff0c\u4e00\u53e3\u7b54\u5e94\u7167\u529e\u3002 \u3000\u3000\u508d\u665a\u65f6\u5206\uff0c\u5927\u96e8\u6b62\u4e86\uff0c\u4e09\u540d\u5fa1\u524d\u4f8d\u536b\u9053\u4e86\u6405\u6270\u522b\u8fc7\uff0c\u5546\u5b9d\u9707\u76f8\u9001\u5230\u5927\u95e8\u4e4b\u5916\u3002 \u3000\u3000\u90a3\u72ec\u81c2\u4eba\u643a\u4e86\u7537\u5b69\u4e4b\u624b\uff0c\u4e5f\u5f85\u544a\u8f9e\uff0c\u5546\u8001\u592a\u5411\u90a3\u7537\u5b69\u77a7\u4e86\u4e00\u773c\uff0c\u60f3\u8d77\u4ed6\u6012\u65a5\u82d7\u592b\u4eba\u65f6\u90a3\u6b63\u6c14\u51db\u7136\u7684\u795e\u60c5\uff0c\u81ea\u5fd6\uff1a\u201c\u8fd9\u5c0f\u5c0f\u5b69\u7ae5\uff0c\u5c45\u7136\u6709\u6b64\u80c6\u8bc6\uff0c\u5012\u4e5f\u5c11\u89c1\u3002\u201d\u4e8e\u662f\u95ee\u9053\uff1a\u201c\u4e24\u4f4d\u8981\u4e0a\u4f55\u5904\uff1f\u8def\u4e0a\u76d8\u7f20\u53ef\u591f\u7528\u4e86\uff1f\u201d\u72ec\u81c2\u4eba\u9053\uff1a\u201c\u5c0f\u4eba\u53d4\u4f84\u6d41\u843d\u6c5f\u6e56\uff0c\u56db\u6d77\u4e3a\u5bb6\uff0c\u8bf4\u4e0d\u4e0a\u5f80\u54ea\u91cc\u53bb\u3002\u201d\u5546\u8001\u592a\u5411\u90a3\u5b69\u7ae5\u7ec6\u7ec6\u6253\u91cf\uff0c\u6c89\u541f\u534a\u664c\uff0c\u9053\uff1a\u201c\u4e24\u4f4d\u82e5\u4e0d\u538c\u5f03\uff0c\u5c31\u5728\u8fd9\u513f\u5e2e\u5fd9\u5e72\u4e9b\u6d3b\u513f\u3002\u54b1\u4eec\u5e84\u5b50\u5927\uff0c\u4e5f\u4e0d\u4e89\u591a\u4e24\u53e3\u4eba\u5403\u996d\u3002\u201d\u90a3\u72ec\u81c2\u4eba\u5fc3\u4e2d\u53e6\u6709\u6253\u7b97\uff0c\u4e00\u542c\u5927\u559c\uff0c\u5f53\u5373\u4e0a\u524d\u62dc\u8c22\u3002\u5546\u8001\u592a\u95ee\u8d77\u59d3\u540d\uff0c\u72ec\u81c2\u4eba\u81ea\u79f0\u540d\u5e73\u56db\uff0c\u90a3\u5b69\u7ae5\u662f\u4ed6\u4f84\u513f\uff0c\u53eb\u4f5c\u5e73\u6590\u3002 \u3000\u3000\u5f53\u665a\u5e73\u56db\u53d4\u4f84\u4fe9\u7531\u7ba1\u5bb6\u5206\u6d3e\uff0c\u4f4f\u5728\u897f\u504f\u9662\u65c1\u7684\u4e00\u95f4\u5c0f\u5c4b\u4e2d\u3002\u4e8c\u4eba\u5173\u4e0a\u95e8\u7a97\uff0c\u5e73\u56db\u4e11\u964b\u7684\u8138\u4e0a\u6ee1\u662f\u559c\u8272\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5c0f\u7237\uff0c\u4f60\u8fc7\u4e16\u7684\u7239\u5a18\u4fdd\u4f51\uff0c\u8fd9\u4e24\u5f20\u62f3\u7ecf\u7ec8\u4e8e\u56de\u5230\u4f60\u7684\u624b\u4e0a\uff0c\u771f\u662f\u8001\u5929\u7237\u6709\u773c\u3002\u201d\u5e73\u6590\u9053\uff1a\u201c\u5e73\u56db\u53d4\uff0c\u4f60\u5343\u4e07\u522b\u518d\u53eb\u6211\u5c0f\u7237\uff0c\u4e00\u4e2a\u4e0d\u614e\u7ed9\u4eba\u542c\u89c1\u4e86\uff0c\u5e73\u767d\u5730\u60f9\u4eba\u7591\u5fc3\u3002\u201d\u5e73\u56db\u8fde\u58f0\u79f0\u662f\uff0c\u4ece\u6000\u4e2d\u638f\u51fa\u90a3\u6cb9\u7eb8\u5c0f\u5305\uff0c\u53cc\u624b\u606d\u606d\u656c\u656c\u5730\u9012\u7ed9\u5e73\u6590\u3002\u4ed6\u5012\u4e0d\u662f\u5bf9\u8fd9\u5b69\u5b50\u5982\u6b64\u606d\u656c\uff0c\u5374\u662f\u60f3\u8d77\u4e86\u9057\u4e0b\u4e24\u9875\u62f3\u7ecf\u7684\u90a3\u4f4d\u6069\u4eba\u3002 \u3000\u3000\u5e73\u6590\u95ee\u9053\uff1a\u201c\u5e73\u56db\u53d4\uff0c\u4f60\u8ddf\u90a3\u960e\u57fa\u8bf4\u4e86\u51e0\u53e5\u4ec0\u4e48\u8bdd\uff0c\u4ed6\u5c31\u5fc3\u7518\u60c5\u613f\u5730\u4ea4\u8fd8\u4e86\u62f3\u7ecf\uff1f\u201d\u5e73\u56db\u9053\uff1a\u201c\u6211\u8bf4\uff1a\u2018\u4f60\u6495\u53bb\u7684\u4e24\u9875\u62f3\u7ecf\u5462\uff1f\u82d7\u5927\u4fa0\u53eb\u4f60\u8fd8\u51fa\u6765\uff01\u2019\u5c31\u8fd9\u4e48\u4e24\u53e5\u8bf4\u8bdd\u3002\u90a3\u65f6\u82d7\u5927\u4fa0\u4fbf\u5728\u4ed6\u773c\u524d\uff0c\u8fd9\u662f\u5343\u8f7d\u96be\u9022\u7684\u826f\u673a\uff0c\u4ed6\u5c31\u6709\u5929\u5927\u7684\u80c6\u5b50\uff0c\u4e5f\u4e0d\u6562\u4e0d\u8fd8\u3002\u201d\u5e73\u6590\u6c89\u541f\u4e00\u4f1a\uff0c\u9053\uff1a\u201c\u8fd9\u4e24\u9875\u62f3\u7ecf\u4e3a\u4ec0\u4e48\u5728\u4ed6\u90a3\u91cc\uff1f\u4f60\u4e3a\u4ec0\u4e48\u53eb\u6211\u8bb0\u7740\u4ed6\u7684\u76f8\u8c8c\uff1f\u4ed6\u4e3a\u4ec0\u4e48\u89c1\u82d7\u5927\u4fa0\u8fd9\u6837\u5bb3\u6015\uff1f\u201d \u3000\u3000\u5e73\u56db\u4e0d\u7b54\uff0c\u4e00\u5f20\u8138\u62bd\u6410\u5f97\u66f4\u52a0\u96be\u770b\uff0c\u6cea\u6c34\u5728\u773c\u7736\u4e2d\u6eda\u6765\u6eda\u53bb\uff0c\u5f3a\u5fcd\u7740\u4e0d\u8ba9\u6389\u4e0b\u3002\u5e73\u6590\u9053\uff1a\u201c\u56db\u53d4\uff0c\u6211\u4e0d\u95ee\u5566\u3002\u4f60\u8bf4\u8fc7\u7b49\u6211\u957f\u5927\u4e86\uff0c\u5b66\u6210\u4e86\u6b66\u529f\uff0c\u518d\u6e90\u6e90\u672c\u672c\u4ed6\u8bf4\u7ed9\u6211\u542c\u3002\u6211\u8fd9\u5c31\u597d\u597d\u5730\u5b66\u3002\u201d \u3000\u3000\u4e8e\u662f\u53d4\u4f84\u4fe9\u5728\u5546\u5bb6\u5821\u5b9a\u5c45\u4e86\u4e0b\u6765\u3002\u5e73\u56db\u5728\u83dc\u56ed\u4e2d\u6311\u7caa\u79cd\u83dc\uff0c\u5e73\u6590\u5374\u5728\u7ec3\u6b66\u5385\u91cc\u626b\u5730\u62b9\u67aa\u3002 \u3000\u3000\u9a6c\u884c\u7a7a\u5728\u5546\u5bb6\u5821\u517b\u4f24\uff0c\u95f2\u7740\u5c31\u548c\u5973\u513f\u3001\u5f92\u513f\u3001\u5546\u5b9d\u9707\u4e09\u4eba\u8bb2\u8bba\u62f3\u811a\u3002\u4ed6\u4eec\u5728\u6f14\u6b66\u7ec3\u62f3\u7684\u5f53\u513f\uff0c\u5e73\u6590\u5076\u7136\u77a7\u4e0a\u4e00\u773c\uff0c\u4f46\u7edd\u4e0d\u591a\u770b\u3002 \u3000\u3000\u4ed6\u4eec\u77e5\u9053\u8fd9\u9ec4\u9ec4\u7626\u7626\u7684\u5b69\u5b50\u5f88\u5927\u80c6\uff0c\u5374\u4ece\u6ca1\u60f3\u5230\u4ed6\u8eab\u6709\u6b66\u529f\uff0c\u56e0\u6b64\u5f53\u4ed6\u5076\u5c14\u770b\u4e0a\u4e00\u773c\u7684\u65f6\u5019\uff0c\u4e0d\u8bba\u662f\u6709\u6570\u5341\u5e74\u6c5f\u6e56\u7ecf\u5386\u7684\u9a6c\u884c\u7a7a\uff0c\u8fd8\u662f\u806a\u660e\u4f36\u4fd0\u7684\u5546\u5b9d\u9707\uff0c\u4ece\u6765\u4e0d\u66fe\u7591\u5fc3\u8fc7\u4ed6\u662f\u5728\u7559\u610f\u62f3\u6cd5\u7684\u5965\u5999\u3002 \u3000\u3000\u4f46\u4ed6\u51b3\u4e0d\u662f\u5077\u5b66\u6b66\u827a\u3002\u4ed6\u5fc3\u4e2d\u6240\u8f6c\u7684\u5ff5\u5934\uff0c\u9a6c\u884c\u7a7a\u4ed6\u4eec\u662f\u66f4\u52a0\u60f3\u4e0d\u5230\u4e86\u3002 \u3000\u3000\u56e0\u4e3a\u6bcf\u5f53\u4ed6\u770b\u4e86\u4ed6\u4eec\u6240\u8bf4\u7684\u5947\u62db\u5999\u7740\u4e4b\u540e\uff0c\u5fc3\u91cc\u603b\u60f3\uff1a\u201c\u90a3\u6709\u4ec0\u4e48\u4e86\u4e0d\u8d77\uff1f \u3000\u3000\u8fd9\u6837\u7684\u62db\u6570\u53ea\u80fd\u5bf9\u4ed8\u5eb8\u624d\uff0c\u5374\u6253\u4e0d\u5230\u82f1\u96c4\u597d\u6c49\u3002\u201d \u3000\u3000\u56e0\u4e3a\u4ed6\u5176\u5b9e\u5e76\u4e0d\u59d3\u5e73\uff0c\u800c\u662f\u59d3\u80e1\uff0c\u4ed6\u7684\u59d3\u540d\u4e0d\u662f\u5e73\u6590\u800c\u662f\u80e1\u6590\uff1b\u56e0\u4e3a\u4ed6\u662f\u80e1\u4e00\u5200\u7684\u513f\u5b50\uff0c\u90a3\u4e2a\u548c\u82d7\u4eba\u51e4\u6253\u4e86\u4e94\u65e5\u4e0d\u5206\u80dc\u8d1f\u7684\u8fbd\u4e1c\u5927\u4fa0\u80e1\u4e00\u5200\u7684\u513f\u5b50\uff1b\u56e0\u4e3a\u4ed6\u7236\u4eb2\u66fe\u9057\u7ed9\u4ed6\u8bb0\u8f7d\u7740\u6b66\u6797\u7edd\u5b66\u7684\u4e00\u672c\u62f3\u7ecf\u5200\u8c31\uff0c\u90a3\u4fbf\u662f\u80e1\u5bb6\u62f3\u6cd5\u548c\u5200\u6cd5\u7684\u7cbe\u4e49\u3002 \u3000\u3000\u8fd9\u672c\u62f3\u7ecf\u5200\u8c31\u672c\u6765\u5c11\u4e86\u5934\u4e0a\u4e24\u9875\uff0c\u7f3a\u4e86\u624e\u6839\u57fa\u7684\u5165\u95e8\u529f\u592b\uff0c\u7f3a\u4e86\u62f3\u6cd5\u5200\u6cd5\u7684\u603b\u8bc0\uff0c\u4e8e\u662f\u4e0d\u8bba\u4ed6\u591a\u4e48\u806a\u660e\u7528\u529f\uff0c\u603b\u662f\u4e0d\u80fd\u5165\u95e8\u3002\u73b0\u4e0b\u673a\u7f18\u5de7\u5408\uff0c\u7ed9\u960e\u57fa\u5077\u53bb\u7684\u603b\u8bc0\u627e\u56de\u6765\u4e86\uff0c\u4e8e\u662f\u4e00\u52a0\u878d\u4f1a\u8d2f\u901a\uff0c\u6b66\u529f\u8fdb\u5883\u4e00\u65e5\u5343\u91cc\u3002 \u3000\u3000\u960e\u57fa\u51ed\u7740\u4e24\u9875\u62f3\u7ecf\u4e0a\u7684\u5be5\u5be5\u5341\u4f59\u62db\u602a\u62db\uff0c\u5c31\u80fd\u79f0\u96c4\u6b66\u6797\uff0c\u8fde\u767e\u80dc\u795e\u62f3\u9a6c\u8001\u9556\u5934\u4e5f\u8d25\u5728\u4ed6\u7684\u624b\u4e0b\u3002\u80e1\u6590\u5374\u662f\u4ece\u5934\u81f3\u5c3e\u5b66\u5168\u4e86\u7684\u3002 \u3000\u3000\u5f53\u7136\uff0c\u4ed6\u5e74\u7eaa\u8fd8\u5c0f\uff0c\u529f\u529b\u5f88\u6d45\uff0c\u8bb8\u591a\u7cbe\u5fae\u4e4b\u5904\u8fd8\u96be\u4ee5\u4e86\u89e3\u3002\u4f46\u51ed\u7740\u8fd9\u672c\u62f3\u7ecf\u5200\u8c31\uff0c\u4ed6\u7ec3\u4e00\u5929\u62b5\u5f97\u5f90\u6323\u4ed6\u4eec\u7ec3\u4e00\u4e2a\u6708\u3002\u4f55\u51b5\uff0c\u5373\u4f7f\u4ed6\u4eec\u7ec3\u4e0a\u5341\u5e74\u4e8c\u5341\u5e74\uff0c\u4e5f\u4e0d\u4f1a\u5b66\u5230\u8fd9\u5929\u4e0b\u7edd\u827a\u7684\u80e1\u5bb6\u62f3\u548c\u80e1\u5bb6\u5200\u3002 \u3000\u3000\u6bcf\u5929\u534a\u591c\u91cc\uff0c\u4ed6\u5c31\u6084\u6084\u6e9c\u51fa\u5e84\u53bb\uff0c\u5728\u8352\u91ce\u91cc\u7ec3\u62f3\u7ec3\u5200\u3002\u4ed6\u7528\u4e00\u67c4\u6728\u5934\u524a\u6210\u7684\u5200\u6765\u7ec3\u4e60\uff0c\u6bcf\u780d\u4e00\u5200\uff0c\u5c31\u60f3\u50cf\u8fd9\u8981\u780d\u53bb\u6740\u7236\u4ec7\u4eba\u7684\u8111\u888b\uff0c\u867d\u7136\uff0c\u4ed6\u5e76\u4e0d\u77e5\u9053\u4ec7\u4eba\u5230\u5e95\u662f\u8c01\u3002\u4f46\u5e73\u56db\u53d4\u5c06\u6765\u4f1a\u8bf4\u7684\uff0c\u7b49\u4ed6\u957f\u5927\u6210\u4eba\u3001\u6b66\u827a\u7ec3\u597d\u4e4b\u540e\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u7ec3\u5f97\u66f4\u52a0\u70ed\u5207\uff0c\u60f3\u5f97\u66f4\u52a0\u6df1\u523b\u3002\u56e0\u4e3a\u6700\u4e0a\u4e58\u7684\u6b66\u529f\uff0c\u662f\u7528\u8111\u5b50\u6765\u7ec3\u800c\u4e0d\u662f\u7528\u8eab\u5b50\u7ec3\u7684\u3002 \u3000\u3000\u8fd9\u6837\u8fc7\u4e86\u4e03\u516b\u4e2a\u6708\uff0c\u9a6c\u884c\u7a7a\u7684\u4f24\u65e9\u5c31\u75ca\u6108\u4e86\uff0c\u4f46\u5546\u8001\u592a\u548c\u5546\u5b9d\u9707\u70ed\u8bda\u7559\u5ba2\u3002\u9a6c\u884c\u7a7a\u7684\u9556\u884c\u5df2\u6b47\u4e86\u4e1a\uff0c\u773c\u89c1\u4e3b\u4eba\u6bb7\u52e4\uff0c\u4e5f\u5c31\u4f4f\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u5546\u5b9d\u9707\u6ca1\u62dc\u4ed6\u4e3a\u5e08\uff0c\u56e0\u4e3a\u5546\u8001\u5927\u6709\u8fd9\u4e48\u4e00\u80a1\u50b2\u6c14\uff0c\u516b\u5366\u5200\u5546\u5251\u9e23\u5bb6\u4f20\u7edd\u827a\uff0c\u600e\u80fd\u53bb\u6295\u5916\u6d3e\u5e08\u7236\uff1f\u4f46\u9a6c\u884c\u7a7a\u611f\u5ff5\u4ed6\u5bb6\u62a4\u9556\u7684\u6069\u60c5\uff0c\u5bf9\u5546\u5b9d\u9707\u5982\u540c\u5f1f\u5b50\u4e00\u822c\u770b\u5f85\uff0c\u53ea\u8981\u662f\u81ea\u5df1\u4f1a\u7684\uff0c\u4ed6\u60f3\u5b66\u4ec0\u4e48\uff0c\u5c31\u6559\u4ec0\u4e48\uff0c\u5c06\u62f3\u6280\u7684\u7cbe\u8981\u503e\u56ca\u4ee5\u6388\uff0c\u767e\u80dc\u795e\u62f3\u7684\u5916\u53f7\u6b8a\u975e\u5e78\u81f4\uff0c\u62f3\u672f\u4e0a\u786e\u6709\u72ec\u5230\u9020\u8be3\uff0c\u8fd9\u4e03\u516b\u4e2a\u6708\u4e2d\uff0c\u5546\u5b9d\u9707\u5b9e\u662f\u83b7\u76ca\u826f\u591a\u3002 \u3000\u3000\u9a6c\u884c\u7a7a\u4e5f\u5df2\u770b\u51fa\u6765\uff0c\u5546\u5bb6\u5821\u5e76\u975e\u5367\u864e\u85cf\u9f99\uff0c\u53e6\u6709\u9ad8\u4eba\uff0c\u53ea\u662f\u90a3\u4e00\u65e5\u960e\u57fa\u4e3a\u4f55\u5306\u5306\u800c\u53bb\uff0c\u5374\u662f\u767e\u601d\u4e0d\u5f97\u5176\u89e3\u3002\u6709\u4e00\u6b21\u4ed6\u5076\u7136\u628a\u8bdd\u9898\u5e26\u5230\u8fd9\u4ef6\u4e8b\u4e0a\uff0c\u5546\u8001\u592a\u5fae\u5fae\u4e00\u7b11\uff0c\u987e\u800c\u8a00\u4ed6\u3002\u9a6c\u884c\u7a7a\u77e5\u9053\u4e3b\u4eba\u4e0d\u80af\u5410\u9732\uff0c\u4ece\u6b64\u7edd\u53e3\u4e0d\u63d0\u3002 \u3000\u3000\u9a6c\u884c\u7a7a\u5e74\u8001\u8840\u4e8f\uff0c\u665a\u4e0a\u7761\u5f97\u4e0d\u6c89\u3002\u6709\u4e00\u65e5\u4e09\u66f4\u65f6\u5206\uff0c\u5ffd\u542c\u5f97\u5899\u5916\u5580\u5587\u4e00\u54cd\uff0c\u662f\u8c01\u65e0\u610f\u4e2d\u8e0f\u65ad\u4e86\u4e00\u6839\u67af\u679d\u3002\u9a6c\u8001\u9556\u5934\u4e00\u751f\u95ef\u8361\u6c5f\u6e56\uff0c\u58f0\u4e00\u5165\u8033\uff0c\u5373\u77e5\u6709\u591c\u884c\u4eba\u5728\u5c4b\u5916\u7ecf\u8fc7\uff0c\u4f46\u53ea\u8fd9\u4e48\u4e00\u54cd\u4e4b\u540e\uff0c\u518d\u65e0\u58f0\u606f\uff0c\u7adf\u542c\u4e0d\u51fa\u90a3\u4eba\u662f\u5411\u4e1c\u5411\u897f\uff0c\u8fd8\u662f\u8eb2\u5728\u5899\u4e0a\u7aa5\u4f3a\u3002\u4ed6\u867d\u5728\u5546\u5bb6\u5821\u4f5c\u5ba2\uff0c\u4f46\u4e3b\u4eba\u4e8e\u5df1\u6709\u6069\uff0c\u5e73\u65e5\u76f8\u4f8d\u60c5\u610f\u6df1\u539a\uff0c\u4ed6\u5df2\u628a\u5546\u5bb6\u5821\u7684\u5b89\u5371\u77a7\u5f97\u6bd4\u81ea\u5df1\u7684\u5bb6\u8fd8\u91cd\uff0c\u5f53\u4e0b\u6084\u6084\u722c\u8d77\uff0c\u4ece\u6795\u5e95\u53d6\u51fa\u91d1\u4e1d\u8f6f\u97ad\u7f20\u5728\u8170\u95f4\uff0c\u8f7b\u8f7b\u6253\u5f00\u623f\u95e8\uff0c\u8dc3\u4e0a\u5899\u5934\uff0c\u7a81\u89c1\u5821\u5916\u9ed1\u5f71\u6643\u52a8\uff0c\u6709\u4eba\u5954\u5411\u540e\u5c71\u800c\u53bb\u3002 \u3000\u3000\u4ed6\u4e00\u77a5\u4e4b\u4e0b\uff0c\u89c1\u6b64\u4eba\u8f7b\u529f\u9887\u4e3a\u4e86\u5f97\uff0c\u5fc3\u4e0b\u5bfb\u601d\uff1a\u201c\u83ab\u975e\u90a3\u960e\u57fa\u5fc3\u72b9\u672a\u6b7b\uff0c\u53c8\u6765\u4f5c\u602a\uff1f\u6b64\u4e8b\u7531\u6211\u8eab\u4e0a\u800c\u8d77\uff0c\u59d3\u9a6c\u7684\u5c82\u80fd\u8896\u624b\u4e0d\u987e\uff1f\u201d\u4e8e\u662f\u8dc3\u51fa\u5899\u5916\uff0c\u811a\u4e0b\u52a0\u5feb\uff0c\u5411\u90a3\u9ed1\u5f71\u53bb\u8def\u6025\u8ffd\uff0c\u4f46\u5954\u51fa\u6570\u5341\u4e08\uff0c\u5df2\u81ea\u4e0d\u89c1\u4e86\u9ed1\u5f71\u7684\u8e2a\u8ff9\u3002\u4ed6\u5fc3\u4e2d\u4e00\u52a8\uff1a\u201c\u4e0d\u597d\uff0c\u522b\u8981\u4e2d\u4e86\u654c\u4eba\u8c03\u864e\u79bb\u5c71\u4e4b\u8ba1\u3002\u201d\u6025\u5fd9\u98de\u6b65\u6251\u56de\u5546\u5bb6\u5821\u3002\u6765\u5230\u5821\u5899\u4e4b\u5916\uff0c\u4f46\u542c\u56db\u4e0b\u91cc\u5bc2\u9759\u65e0\u58f0\uff0c\u8fd9\u624d\u653e\u5fc3\uff0c\u5fc3\u4e0b\u5374\u662f\u7591\u60d1\u66f4\u751a\uff1a\u201c\u9002\u624d\u6b64\u4eba\u8eab\u624b\u4e0d\u51e1\uff0c\u5b9e\u662f\u52b2\u654c\u3002\u4f46\u77a7\u4ed6\u8eab\u5f62\u7626\u5c0f\uff0c\u4e0e\u90a3\u76d7\u9b41\u960e\u57fa\u5927\u4e0d\u76f8\u540c\uff0c\u4e0d\u77e5\u662f\u6c5f\u6e56\u4e0a\u4ec0\u4e48\u597d\u624b\u5230\u4e86\uff1f\u201d \u3000\u3000\u4ed6\u6293\u4f4f\u8f6f\u97ad\uff0c\u5728\u638c\u4e0a\u76d8\u4e86\u51e0\u8f6c\uff0c\u5f13\u8eab\u5411\u5e84\u540e\u8d70\u53bb\uff0c\u8981\u5bdf\u770b\u4e00\u4e2a\u7a76\u7adf\u3002\u7a9c\u51fa\u5341\u4f59\u4e08\uff0c\u5c06\u5230\u5e84\u9662\u5c3d\u5934\uff0c\u5ffd\u542c\u897f\u9996\u9690\u9690\u6709\u91d1\u5203\u5288\u98ce\u4e4b\u58f0\u3002\u9a6c\u884c\u7a7a\u6697\u53eb\u4e00\u58f0\uff1a \u3000\u3000\u201c\u60ed\u6127\uff0c\u679c\u7136\u6709\u4eba\u6765\u88ad\uff0c\u5374\u4e0d\u77e5\u8ddf\u8c01\u52a8\u4e0a\u4e86\u624b\uff1f\u201d\u53cc\u8db3\u4e00\u70b9\uff0c\u8eab\u5f62\u7eb5\u8d77\uff0c\u767e\u80dc\u795e\u62f3\u5e74\u7eaa\u867d\u8001\uff0c\u8eab\u624b\u4ecd\u662f\u6781\u4e3a\u77eb\u6377\uff0c\u5de6\u624b\u5728\u5899\u5934\u4e00\u642d\uff0c\u4e00\u4e2a\u5012\u7ffb\u8eab\uff0c\u8f7b\u8f7b\u843d\u5728\u5899\u5185\uff0c\u5faa\u58f0\u8fc7\u53bb\uff0c\u542c\u5f97\u58f0\u97f3\u662f\u4ece\u540e\u8fdb\u7684\u4e00\u95f4\u7816\u5c4b\u4e2d\u53d1\u51fa\u3002\u4f46\u8bf4\u4e5f\u5947\u602a\uff0c\u4e8c\u4eba\u4e00\u5473\u54d1\u6597\uff0c\u65e2\u65e0\u534a\u58f0\u5406\u559d\u53eb\u9a82\uff0c\u5175\u5203\u4ea6\u4e0d\u78b0\u649e\u3002\u4ed6\u5fc3\u77e5\u4e2d\u95f4\u5fc5\u6709\u8df7\u8e4a\uff0c\u5148\u4e0d\u51b2\u8fdb\u76f8\u52a9\uff0c\u51d1\u773c\u5230\u7a97\u7f1d\u4e2d\u4e00\u5f20\uff0c\u9669\u4e9b\u4e0d\u7981\u5931\u7b11\u3002 \u3000\u3000\u4f46\u89c1\u5c4b\u4e2d\u7a7a\u7a7a\u8361\u8361\uff0c\u684c\u4e0a\u4e00\u706f\u5982\u8c46\uff0c\u4e24\u4e2a\u4eba\u5404\u6267\u94a2\u5200\uff0c\u76d8\u65cb\u6765\u53bb\u5730\u6fc0\u6597\uff0c\u4e00\u4e2a\u662f\u5c11\u4e3b\u4eba\u5546\u5b9d\u9707\uff0c\u53e6\u4e00\u4e2a\u5374\u662f\u4ed6\u6bcd\u4eb2\u5546\u8001\u592a\u592a\uff0c\u539f\u6765\u6bcd\u5b50\u4fe9\u6b63\u5728\u4e60\u7ec3\u5200\u6cd5\u3002 \u3000\u3000\u4ed6\u53ea\u77a7\u4e86\u7247\u523b\uff0c\u4e0d\u7531\u5f97\u5012\u62bd\u4e00\u53e3\u51c9\u6c14\uff0c\u53ea\u89c1\u5546\u8001\u592a\u592a\u51fa\u624b\u72e0\u8fa3\uff0c\u5200\u6cd5\u7cbe\u5999\uff0c\u56fa\u4e0e\u65e5\u95f4\u7684\u9f99\u949f\u8001\u6001\u5927\u4e0d\u76f8\u540c\uff0c\u800c\u5546\u5b9d\u9707\u4e00\u8def\u516b\u5366\u5200\u4f7f\u5c06\u51fa\u6765\uff0c\u4e5f\u662f\u864e\u864e\u751f\u98ce\u3002\u539f\u6765\u975e\u4f46\u5546\u8001\u592a\u5e73\u65f6\u6df1\u85cf\u4e0d\u9732\uff0c\u5546\u5b9d\u9707\u4e5f\u662f\u6545\u610f\u9690\u7792\u4e86\u6b66\u529f\u3002\u4ed6\u5e73\u65e5\u6559\u5546\u5b9d\u9707\u7684\u53ea\u662f\u62f3\u811a\uff0c\u5200\u6cd5\u81ea\u5df1\u5e76\u4e0d\u64c5\u957f\uff0c\u5546\u5b9d\u9707\u4e5f\u4ece\u6765\u4e0d\u63d0\uff0c\u60f3\u4e0d\u5230\u8fd9\u5c11\u5e74\u5175\u5203\u4e0a\u7684\u9020\u8be3\u7740\u5b9e\u4e0d\u4f4e\u3002\u4ed6\u6084\u7acb\u534a\u664c\uff0c\u60f3\u8d77\u5341\u4e94\u5e74\u524d\u5728\u7518\u51c9\u9053\u4e0a\u4e0e\u5546\u5b9d\u9707\u7684\u7236\u4eb2\u5546\u5251\u9e23\u52a8\u624b\uff0c\u88ab\u4ed6\u780d\u4e86\u4e00\u5200\uff0c\u5288\u4e86\u4e00\u638c\uff0c\u517b\u4e86\u4e09\u5e74\u4f24\u65b9\u5f97\u5eb7\u590f\uff0c\u81ea\u77e5\u4e0e\u4ed6\u529f\u592b\u76f8\u5dee\u592a\u8fdc\uff0c\u6b64\u4ec7\u96be\u62a5\uff0c\u7518\u51c9\u9053\u4e00\u8def\u4ece\u6b64\u7edd\u8db3\u4e0d\u8d70\u3002\u6b64\u65f6\u5546\u5251\u9e23\u5df2\u6b7b\uff0c\u5546\u8001\u592a\u4e8e\u5df1\u6709\u6069\uff0c\u6614\u65e5\u7684\u5c0f\u5c0f\u5acc\u9699\u65e9\u5df2\u4e0d\u653e\u5728\u5fc3\u4e0a\uff0c\u54ea\u77e5\u4eca\u65e5\u4e2d\u591c\uff0c\u53c8\u89c1\u4ec7\u4eba\u7684\u9057\u8815\u5b64\u513f\u5404\u4f7f\u516b\u5366\u5200\u5bf9\u62db\u3002 \u3000\u3000\u4ed6\u601d\u6f6e\u8d77\u4f0f\uff1a\u201c\u5546\u8001\u592a\u7684\u6b66\u529f\u5b9e\u4e0d\u5728\u6211\u4e4b\u4e0b\uff0c\u4f55\u4ee5\u5979\u7adf\u7136\u534a\u70b9\u4e0d\u9732\u75d5\u8ff9\uff1f \u3000\u3000\u5979\u7559\u6211\u7236\u5973\u5728\u5e84\uff0c\u662f\u5426\u53e6\u6709\u522b\u60c5\uff1f\u201d\u51dd\u601d\u7247\u523b\uff0c\u518d\u51d1\u773c\u5230\u7a97\u7f1d\u4e2d\u65f6\uff0c\u89c1\u6bcd\u5b50\u4e8c\u4eba\u5200\u6cd5\u5df2\u53d8\uff0c\u5404\u4f7f\u516b\u5366\u6e38\u8eab\u5200\u6cd5\uff0c\u6ee1\u5ba4\u6e38\u8d70\uff0c\u5200\u4e2d\u5939\u638c\uff0c\u638c\u4e2d\u5939\u5200\uff0c\u8d8a\u6253\u8d8a\u5feb\uff0c\u6253\u5230\u7b2c\u516d\u5341\u56db\u62db\u201c\u6536\u52bf\u201d\uff0c\u4e8c\u4eba\u5411\u540e\u8dc3\u5f00\uff0c\u6bcd\u5b50\u4fe9\u4f9d\u8db3\u4e86\u89c4\u77e9\uff0c\u5404\u81ea\u4e3e\u5200\u81f4\u656c\uff0c\u8fd9\u624d\u5782\u4e0b\u5200\u6765\u3002\u5546\u8001\u592a\u4e0d\u52a8\u58f0\u8272\uff0c\u5728\u9752\u706f\u4e4b\u4e0b\u8138\u6cdb\u7eff\u5149\u3002\u5546\u5b9d\u9707\u5374\u5df2\u6ee1\u8138\u901a\u7ea2\uff0c\u547c\u547c\u5598\u6c14\u3002 \u3000\u3000\u5546\u8001\u592a\u6c89\u7740\u8138\u9053\uff1a\u201c\u4f60\u7684\u547c\u5438\u603b\u662f\u96be\u4ee5\u8c03\u5300\uff0c\u8fdb\u5883\u5982\u6b64\u4e4b\u6162\uff0c\u4f55\u5e74\u4f55\u6708\u624d\u80fd\u62a5\u5f97\u4f60\u7239\u7239\u7684\u5927\u4ec7\uff1f\u201d\u9a6c\u884c\u7a7a\u5fc3\u4e2d\u4e00\u51db\uff0c\u53ea\u89c1\u5546\u5b9d\u9707\u4f4e\u4e0b\u4e86\u5934\uff0c\u751a\u6709\u6127\u8272\u3002\u5546\u8001\u592a\u53c8\u9053\uff1a\u201c\u90a3\u82d7\u4eba\u51e4\u7684\u6b66\u529f\u4f60\u867d\u6ca1\u89c1\u5230\uff0c\u4ed6\u62c9\u8f66\u7684\u795e\u529b\u603b\u662f\u4eb2\u773c\u76ee\u7779\u7684\u4e86\u3002\u80e1\u4e00\u5200\u7684\u529f\u592b\u4e0d\u5728\u82d7\u4eba\u51e4\u4e4b\u4e0b\u3002\u8fd9\u82d7\u80e1\u4e8c\u8d3c\u7684\u6b66\u529f\uff0c\u4f60\u6b64\u523b\u8ddf\u4ed6\u4eec\u5929\u5dee\u5730\u8fdc\uff0c\u4f46\u53ea\u8981\u52e4\u5b66\u82e6\u7ec3\uff0c\u6bcf\u8fc7\u5f97\u4e00\u65e5\uff0c\u4f60\u6b66\u529f\u957f\u4e00\u5206\uff0c\u8fd9\u4e8c\u8d3c\u5374\u8870\u8001\u4e86\u4e00\u5206\uff0c\u7ec8\u6709\u4e00\u65e5\uff0c\u8981\u5c06\u4e8c\u8d3c\u5728\u516b\u5366\u5200\u4e0b\u788e\u5c38\u4e07\u6bb5\u3002\u201d\u9a6c\u884c\u7a7a\u5fc3\u60f3\uff1a\u201c\u8fd9\u6bcd\u5b50\u4e8c\u4eba\u95ed\u95e8\u4e60\u6b66\uff0c\u4e0d\u77e5\u80e1\u4e00\u5200\u65e9\u4e8e\u5341\u591a\u5e74\u524d\u4fbf\u6b7b\u4e86\u3002\u201d\u53ea\u542c\u5546\u8001\u592a\u53f9\u4e86\u53e3\u957f\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u5509\uff0c\u4f60\u8fd9\u5b69\u5b50\uff0c\u6211\u77a7\u4f60\u554a\uff0c\u8fd9\u51e0\u65e5\u4e3a\u90a3\u9a6c\u5bb6\u7684\u4e2b\u5934\u795e\u9b42\u98a0\u5012\uff0c\u8fde\u7ec3\u529f\u592b\u4e5f\u4e0d\u8d77\u52b2\u4e86\u3002\u201d \u3000\u3000\u9a6c\u884c\u7a7a\u4e00\u60ca\uff1a\u201c\u96be\u9053\u6211\u90a3\u6625\u513f\u548c\u4ed6\u6709\u751a\u82df\u4e14\u4e4b\u4e8b\uff1f\u201d\u4f46\u89c1\u5546\u5b9d\u9707\u6ee1\u8138\u901a\u7ea2\uff0c\u8fa9\u9053\uff1a\u201c\u5988\uff0c\u6211\u89c1\u4e86\u9a6c\u59d1\u5a18\u603b\u662f\u89c4\u89c4\u77e9\u77e9\u7684\uff0c\u8bdd\u4e5f\u6ca1\u8ddf\u5979\u591a\u8bf4\u51e0\u53e5\u3002\u201d \u3000\u3000\u5546\u8001\u592a\u54fc\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u5403\u8c01\u7684\u5976\u957f\u5927\uff1f\u5fc3\u91cc\u6253\u4ec0\u4e48\u4e3b\u610f\uff0c\u96be\u9053\u6211\u8fd8\u4e0d\u660e\u767d\uff1f\u4f60\u770b\u4e2d\u9a6c\u5bb6\u59d1\u5a18\uff0c\u90a3\u4e0d\u9519\uff0c\u5979\u4eba\u54c1\u6b66\u827a\uff0c\u6211\u5fc3\u4e2d\u5f88\u5408\u610f\u3002\u201d\u5546\u5b9d\u9707\u5f88\u662f\u9ad8\u5174\uff0c\u53eb\u4e86\u58f0\uff1a\u201c\u5988\uff01\u201d\u5546\u8001\u592a\u5de6\u624b\u4e00\u6325\uff0c\u6c89\u7740\u55d3\u5b50\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u4ed6\u7239\u662f\u8c01\uff1f\u201d\u5546\u5b9d\u9707\u4e00\u6115\u9053\uff1a\u201c\u96be\u9053\u4e0d\u662f\u9a6c\u8001\u9556\u5934\uff1f\u201d\u5546\u8001\u592a\u9053\uff1a\u201c\u8c01\u8bf4\u4e0d\u662f\uff1f\u4f60\u5374\u53ef\u77e5\u9a6c\u8001\u9556\u5934\u8ddf\u54b1\u5bb6\u6709\u751a\u7275\u8fde\uff1f\u201d\u5546\u5b9d\u9707\u6447\u6447\u5934\u3002\u5546\u8001\u592a\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4ed6\u662f\u4f60\u7239\u7239\u7684\u5927\u4ec7\u4eba\u3002\u201d\u5546\u5b9d\u9707\u5927\u51fa\u610f\u6599\u4e4b\u5916\uff0c\u4e0d\u7531\u5f97\u201c\u554a\u201d\u4e86\u4e00\u58f0\u3002 \u3000\u3000\u9a6c\u884c\u7a7a\u4e0d\u7981\u53d1\u6296\uff0c\u4f46\u542c\u5546\u8001\u592a\u53c8\u9053\uff1a\u201c\u5341\u4e94\u5e74\u524d\uff0c\u4f60\u7239\u7239\u5728\u7518\u51c9\u9053\u4e0a\u8ddf\u9a6c\u884c\u7a7a\u52a8\u624b\u3002\u60f3\u4f60\u7239\u7239\u82f1\u96c4\u76d6\u4e16\uff0c\u90a3\u59d3\u9a6c\u7684\u7109\u662f\u4ed6\u7684\u5bf9\u624b\uff1f\u4f60\u7239\u7239\u780d\u4e86\u4ed6\u4e00\u5200\uff0c\u5288\u4e86\u4ed6\u4e00\u638c\uff0c\u5c06\u4ed6\u6253\u5f97\u91cd\u4f24\u3002\u4f46\u90a3\u59d3\u9a6c\u7684\u4ea6\u975e\u5e73\u5eb8\u4e4b\u8f88\uff0c\u4f60\u7239\u7239\u5728\u8fd9\u573a\u6bd4\u6b66\u4e2d\u4e5f\u53d7\u4e86\u5185\u4f24\u3002\u4ed6\u56de\u5f97\u5bb6\u6765\uff0c\u4f24\u672a\u5e73\u590d\uff0c\u54b1\u4eec\u7684\u5bf9\u5934\u80e1\u4e00\u5200\u6df1\u591c\u8d76\u4e0a\u95e8\u6765\uff0c\u5c06\u4f60\u7239\u7239\u5bb3\u6b7b\u3002\u82e5\u975e\u4f60\u7239\u7239\u8ddf\u90a3\u59d3\u9a6c\u7684\u4e8b\u5148\u6709\u8fd9\u4e00\u573a\u8f83\u91cf\uff0c\u563f\u563f\uff0c\u516b\u5366\u5200\u5a01\u9707\u6c5f\u6e56\uff0c\u8c05\u90a3\u80e1\u4e00\u5200\u600e\u80fd\u5bb3\u5f97\u4f60\u7239\u7239\uff1f\u201d \u3000\u3000\u5979\u8bf4\u5230\u6700\u540e\u8fd9\u51e0\u53e5\u8bdd\u65f6\u8bed\u97f3\u60e8\u5389\uff0c\u55d3\u5b50\u5636\u54d1\uff0c\u542c\u6765\u6781\u662f\u53ef\u6016\u3002 \u3000\u3000\u9a6c\u884c\u7a7a\u4e00\u751f\u7ecf\u8fc7\u4e0d\u5c11\u5927\u98ce\u5927\u6d6a\uff0c\u6b64\u65f6\u542c\u6765\u5374\u4e5f\u662f\u4e0d\u5bd2\u800c\u6817\uff0c\u5fc3\u60f3\uff1a\u201c\u80e1\u4e00\u5200\u4f55\u7b49\u7684\u529f\u592b\uff0c\u4f60\u5546\u5251\u9e23\u5c31\u7b97\u8eab\u4e0a\u65e0\u4f24\uff0c\u4e5f\u662f\u96be\u9003\u6b64\u52ab\u3002\u8001\u5a46\u5b50\u5fc3\u4f24\u4e08\u592b\u60e8\u6b7b\uff0c\u7adf\u7136\u8fc1\u6012\u4e8e\u6211\u3002\u201d \u3000\u3000\u53ea\u542c\u5546\u8001\u592a\u53c8\u9053\uff1a\u201c\u9634\u5dee\u9633\u9519\uff0c\u8fd9\u8001\u513f\u7adf\u4f1a\u8d76\u9556\u6295\u5230\u6211\u5bb6\u6765\u3002\u8fd9\u5546\u5bb6\u5821\u662f\u4f60\u7239\u7239\u4eb2\u624b\u6240\u5efa\u9020\uff0c\u600e\u5bb9\u9f20\u8f88\u5728\u6b64\u653e\u8086\u52ab\u6f02\uff1f\u4f46\u4f60\u53ef\u77e5\u6211\u7559\u59d3\u9a6c\u7684\u7236\u5973\u5728\u6b64\uff0c\u6709\u4f55\u6253\u7b97\uff1f\u201d\u5546\u5b9d\u9707\u58f0\u97f3\u53d1\u98a4\uff0c\u9053\uff1a\u201c\u5988\u2026\u2026\u4f60\u2026\u2026\u4f60\u8981\u6211\u4e3a\u7239\u7239\u590d\u4ec7\uff1f\u201d \u3000\u3000\u5546\u8001\u592a\u5389\u58f0\u9053\uff1a\u201c\u4f60\u4e0d\u80af\uff0c\u662f\u4e0d\u662f\uff1f\u4f60\u662f\u770b\u4e0a\u4e86\u90a3\u59d3\u9a6c\u7684\u4e2b\u5934\uff0c\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u5546\u5b9d\u9707\u89c1\u6bcd\u4eb2\u773c\u4e2d\u5982\u8981\u55b7\u51fa\u706b\u6765\uff0c\u9000\u540e\u4e86\u4e24\u6b65\uff0c\u4e0d\u6562\u56de\u7b54\u3002 \u3000\u3000\u5546\u8001\u592a\u51b7\u7b11\u9053\uff1a\u201c\u5f88\u597d\u3002\u8fc7\u51e0\u5929\u6211\u7ed9\u4f60\u8ddf\u90a3\u59d3\u9a6c\u7684\u63d0\u4eb2\uff0c\u4ee5\u4f60\u7684\u5bb6\u4e16\u54c1\u8c8c\uff0c\u8c05\u4ed6\u51b3\u65e0\u4e0d\u5141\u3002\u201d \u3000\u3000\u8fd9\u51e0\u53e5\u8bdd\u5374\u53eb\u9a6c\u884c\u7a7a\u548c\u5546\u5b9d\u9707\u90fd\u662f\u5927\u51fa\u610f\u6599\u4e4b\u5916\u3002\u9a6c\u884c\u7a7a\u9694\u7a97\u770b\u5230\u5546\u8001\u592a\u8138\u4e0a\u5207\u9f7f\u75db\u6068\u7684\u795e\u6c14\uff0c\u5fae\u4e00\u7422\u78e8\uff0c\u5168\u8eab\u5bd2\u6bdb\u6839\u6839\u76f4\u7ad6\uff1a\u201c\u8fd9\u8001\u592a\u5a46\u7528\u5fc3\u597d\u4e0d\u72e0\u6bd2\uff01\u5979\u6740\u6211\u5c1a\u4e0d\u8db3\u4ee5\u6cc4\u6124\uff0c\u5374\u8981\u5c06\u6211\u82b1\u4e00\u822c\u7684\u95fa\u5973\u5a36\u4f5c\u5ab3\u5987\uff0c\u6298\u78e8\u5f97\u5979\u6c42\u751f\u4e0d\u5f97\uff0c\u6c42\u6b7b\u4e0d\u80fd\u3002\u5929\u53ef\u601c\u89c1\uff0c\u53eb\u6211\u4eca\u665a\u9694\u7a97\u542c\u5f97\u5979\u6bcd\u5b50\u8fd9\u756a\u8bf4\u8bdd\uff0c\u5426\u5219\u2026\u2026 \u3000\u3000\u6211\u90a3\u82e6\u547d\u7684\u6625\u513f\u2026\u2026\u201d \u3000\u3000\u5546\u5b9d\u9707\u5e74\u8f7b\u8bc6\u6d45\uff0c\u5374\u5168\u4e0d\u660e\u767d\u6bcd\u4eb2\u8fd9\u756a\u6df1\u610f\uff0c\u53ea\u89c9\u53c8\u662f\u6b22\u559c\u53c8\u662f\u8be7\u5f02\uff0c\u60f3\u5230\u6bcd\u4eb2\u80af\u4e3a\u81ea\u5df1\u4e3b\u6301\u8fd9\u95e8\u4eb2\u4e8b\uff0c\u6b22\u559c\u5012\u6709\u4e5d\u5206\uff0c\u53ea\u5269\u4e0b\u4e00\u5206\u8be7\u5f02\u3002 \u3000\u3000\u9a6c\u884c\u7a7a\u53ea\u6015\u518d\u542c\u4e0b\u53bb\u7ed9\u5546\u8001\u592a\u53d1\u89c9\uff0c\u51dd\u795e\u63d0\u6c14\uff0c\u6084\u6084\u8d70\u8fdc\uff0c\u56de\u5230\u81ea\u5df1\u5c4b\u4e2d\u65f6\u62b9\u4e86\u989d\u5934\u4e00\u628a\u51b7\u6c57\uff0c\u731b\u7136\u7701\u8d77\uff1a\u201c\u90a3\u5954\u5230\u540e\u5c71\u7684\u7626\u5c0f\u9ed1\u5f71\u5374\u53c8\u662f\u8c01\uff1f\u201d \u3000\u3000\u7b2c\u4e8c\u5929\u5348\u540e\uff0c\u9a6c\u884c\u7a7a\u7a7f\u4e86\u957f\u888d\u9a6c\u8902\uff0c\u547d\u5546\u5b9d\u9707\u8bf7\u6bcd\u4eb2\u51fa\u6765\uff0c\u6709\u51e0\u53e5\u8bdd\u5546\u91cf\u3002\u5546\u5b9d\u9707\u53c8\u60ca\u53c8\u559c\uff0c\u5fc3\u60f3\uff1a\u201c\u96be\u9053\u6bcd\u4eb2\u8fd9\u4e48\u5feb\u5c31\u5df2\u8ddf\u4ed6\u63d0\u4e86\u4eb2\uff1f\u77a7\u4ed6\u8fd9\u526f\u795e\u6c14\u6253\u626e\uff0c\u90a3\u53ef\u4e0d\u540c\u5bfb\u5e38\u3002\u201d\u4e8e\u662f\u76f8\u8bf7\u6bcd\u4eb2\uff0c\u6765\u5230\u540e\u5385\uff0c\u548c\u9a6c\u884c\u7a7a\u5206\u5bbe\u4e3b\u5750\u4e0b\uff0c\u81ea\u5df1\u4e0b\u9996\u76f8\u966a\u3002\u4ed6\u671b\u671b\u6bcd\u4eb2\uff0c\u53c8\u671b\u671b\u9a6c\u884c\u7a7a\uff0c\u4e00\u9897\u5fc3\u6026\u6026\u76f4\u8df3\uff0c\u4f46\u542c\u9a6c\u8001\u9556\u5934\u9053\u8c22\u62a4\u9556\u4e4b\u5fb7\uff0c\u4e1c\u9053\u4e4b\u8c0a\uff0c\u5546\u8001\u592a\u6ee1\u53e3\u8c26\u865a\uff0c\u53ea\u76fc\u4ed6\u4e8c\u4eba\u8bf4\u5230\u6b63\u9898\uff0c\u4f46\u4e24\u4e2a\u8a00\u6765\u8bed\u53bb\uff0c\u5c3d\u662f\u5ba2\u5957\u3002 \u3000\u3000\u8bf4\u4e86\u597d\u4e00\u4f1a\uff0c\u9a6c\u884c\u7a7a\u624d\u9053\uff1a\u201c\u5c0f\u5973\u6625\u82b1\u8fd9\u4e2b\u5934\u7684\u5e74\u7eaa\u4e5f\u4e0d\u5c0f\u4e86\uff0c\u6211\u60f3\u8ddf\u5546\u8001\u592a\u5546\u91cf\u4e00\u4ef6\u4e8b\u3002\u201d\u5546\u5b9d\u9707\u5fc3\u4e2d\u6026\u7684\u4e00\u4e0b\u5927\u8df3\u3002\u5546\u8001\u592a\u5927\u662f\u5947\u602a\uff1a\u201c\u5374\u4e5f\u6ca1\u542c\u8bf4\u5973\u5bb6\u5148\u5f00\u53e3\u6765\u6c42\u4eb2\u7684\u3002\u201d\u8bf4\u9053\uff1a\u201c\u9a6c\u8001\u5e08\u5c3d\u8bf4\u4e0d\u59a8\uff0c\u54b1\u4eec\u81ea\u5df1\u4eba\uff0c\u8fd8\u62d8\u4ec0\u4e48\u793c\u6570\uff1f\u201d\u9a6c\u884c\u7a7a\u9053\uff1a\u201c\u6211\u9664\u4e86\u8fd9\u4e2b\u5934\uff0c\u4e00\u751f\u5c31\u6536\u5f97\u4e00\u4e2a\u5f92\u5f1f\u3002\u4ed6\u5929\u8d44\u611a\u949d\uff0c\u6027\u5b50\u53c8\u5364\u83bd\uff0c\u4f46\u6211\u4ece\u5c0f\u5c31\u5f53\u4ed6\u4eb2\u513f\u5b50\u4e00\u822c\u770b\u5f85\u3002\u8fd9\u5b69\u5b50\u8ddf\u6625\u513f\u4e5f\u633a\u5408\u5f97\u6765\uff0c\u6211\u5c31\u60f3\u5728\u8d35\u5e84\u7ed9\u4ed6\u4e8c\u4eba\u8ba2\u4e86\u8fd9\u5934\u4eb2\u4e8b\u3002\u201d \u3000\u3000\u5546\u5b9d\u9707\u8d8a\u542c\u8d8a\u4e0d\u5bf9\uff0c\u542c\u5230\u6700\u540e\u4e00\u53e5\u8bdd\u65f6\uff0c\u4e0d\u81ea\u7981\u5730\u7ad9\u8d77\u8eab\u6765\u3002\u5546\u8001\u592a\u5fc3\u4e0b\u5927\u6012\uff1a\u201d\u8fd9\u8001\u513f\u597d\u751f\u5389\u5bb3\uff0c\u5b9a\u662f\u6211\u90a3\u4e0d\u4e2d\u7528\u7684\u513f\u5b50\u9732\u4e86\u7834\u7efd\u3002\u201d\u5f53\u4e0b\u6ee1\u8138\u5806\u6b22\uff0c\u8fde\u58f0\u201c\u606d\u559c\u201d\uff0c\u53c8\u53eb\uff1a\u201c\u5b69\u513f\uff0c\u5feb\u7ed9\u9a6c\u8001\u4f2f\u9053\u559c\uff01\u201d\u5546\u5b9d\u9707\u8111\u4e2d\u80e1\u6d82\u4e00\u7247\uff0c\u5446\u4e86\u4e00\u5446\uff0c\u76f4\u5954\u51fa\u5916\u3002 \u3000\u3000\u9a6c\u884c\u7a7a\u53c8\u548c\u5546\u8001\u592a\u5ba2\u6c14\u597d\u4e00\u9635\u5b50\uff0c\u624d\u56de\u5c4b\u4e2d\uff0c\u5c06\u5973\u513f\u548c\u5f92\u513f\u53eb\u6765\uff0c\u8bf4\u4eca\u65e5\u8981\u7ed9\u4e8c\u4eba\u8ba2\u4eb2\u3002\u5f90\u94ee\u5927\u559c\u8fc7\u671b\uff0c\u7b11\u5f97\u5408\u4e0d\u62e2\u5634\u6765\uff0c\u9a6c\u6625\u82b1\u7ea2\u6655\u53cc\u988a\uff0c\u8f6c\u8fc7\u4e86\u5934\u4e0d\u4f5c\u58f0\u3002\u9a6c\u884c\u7a7a\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u5728\u8fd9\u513f\u5148\u8ba2\u4e86\u4eb2\u3002\u81f3\u4e8e\u4eb2\u4e8b\u561b\uff0c\u90a3\u662f\u5f97\u56de\u81ea\u4e2a\u5bb6\u53bb\u529e\u7684\u4e86\u3002\u201d\u4ed6\u77e5\u5973\u513f\u548c\u5f92\u513f\u5fc3\u4e2d\u85cf\u4e0d\u4f4f\u4e8b\uff0c\u6628\u665a\u6240\u95fb\u6240\u89c1\uff0c\u7adf\u662f\u534a\u53e5\u4e0d\u63d0\u3002 \u3000\u3000\u9a6c\u6625\u82b1\u5a07\u61a8\u6d3b\u6cfc\uff0c\u660e\u8273\u52a8\u4eba\uff0c\u5728\u5546\u5bb6\u5821\u8fd9\u4e48\u516b\u4e2a\u6708\u4e00\u4f4f\uff0c\u5546\u5b9d\u9707\u548c\u5979\u65e5\u65e5\u76f8\u89c1\uff0c\u7adf\u53eb\u4ed6\u4e00\u7f15\u60c5\u4e1d\uff0c\u7262\u7262\u5730\u7f1a\u5728\u8fd9\u4f4d\u59d1\u5a18\u8eab\u4e0a\u3002\u4ed6\u521a\u5f97\u6bcd\u4eb2\u7b54\u5e94\u8981\u7ed9\u81ea\u5df1\u63d0\u4eb2\uff0c\u6599\u60f3\u4e8b\u65e0\u4e0d\u8c10\uff0c\u6b63\u5728\u6ee1\u6000\u559c\u60a6\u4e4b\u9645\uff0c\u7a81\u7136\u542c\u5230\u4e86\u9a6c\u884c\u7a7a\u90a3\u51e0\u53e5\u6674\u5929\u9739\u96f3\u4e00\u822c\u7684\u8a00\u8bed\u3002\u4ed6\u72ec\u81ea\u5750\u5728\u623f\u4e2d\uff0c\u4ece\u7a97\u4e2d\u671b\u51fa\u53bb\uff0c\u5446\u5446\u5730\u77a7\u7740\u9662\u5b50\u4e2d\u4e00\u682a\u94f6\u674f\uff0c\u771f\u96be\u76f8\u4fe1\u9002\u624d\u542c\u5230\u7684\u8bdd\u7adf\u4f1a\u662f\u9a6c\u884c\u7a7a\u53e3\u4e2d\u8bf4\u51fa\u6765\u7684\u3002 \u3000\u3000\u4ed6\u4e27\u9b42\u843d\u9b44\uff0c\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u5c11\u65f6\u5019\uff0c\u76f4\u81f3\u4e00\u540d\u5bb6\u4e01\u8d70\u8fdb\u623f\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u5c11\u7237\uff0c\u7ec3\u6b66\u7684\u65f6\u5019\u5230\u5566\uff0c\u8001\u592a\u592a\u7b49\u4e86\u4f60\u534a\u5929\u5462\u3002\u201d\u5546\u5b9d\u9707\u4e00\u60ca\uff0c\u6697\u53eb\uff1a\u201c\u7cdf\u7cd5\uff0c\u80e1\u91cc\u80e1\u6d82\u7684\u8bef\u4e86\u7ec3\u6b66\u65f6\u5019\uff0c\u987b\u8ba8\u4e00\u987f\u597d\u9a82\u3002\u201d\u4ece\u58c1\u4e0a\u6458\u4e0b\u4e86\u9556\u56ca\uff0c\u5feb\u6b65\u5954\u5230\u7ec3\u6b66\u5385\u4e2d\u3002\u53ea\u89c1\u5546\u8001\u592a\u5750\u5728\u6905\u4e2d\uff0c\u795e\u8272\u5982\u5e38\uff0c\u8bf4\u9053\uff1a\u201c\u4eca\u513f\u7ec3\u7763\u8109\u80cc\u5fc3\u5404\u7a74\u3002\u201d \u3000\u3000\u8f6c\u5934\u5411\u4e24\u540d\u6301\u724c\u7684\u5bb6\u4e01\u53eb\u9053\uff1a\u201c\u5c06\u724c\u513f\u62ff\u7a33\u4e86\uff0c\u8d70\u52a8\uff01\u201d\u5546\u5b9d\u9707\u6697\u6697\u7eb3\u7f55\uff1a \u3000\u3000\u201c\u9a6c\u8001\u5e08\u8bf4\u8fd9\u7b49\u8bdd\uff0c\u600e\u5730\u5988\u6beb\u4e0d\u5728\u4e4e\uff1f\u201d\u4f46\u5546\u8001\u592a\u5e73\u65e5\u8bad\u5b50\u6781\u4e25\uff0c\u7ec3\u6b66\u4e4b\u9645\u5c24\u5176\u6ca1\u534a\u70b9\u5047\u501f\uff0c\u7a0d\u4e00\u4e0d\u614e\uff0c\u6253\u9a82\u968f\u4e4b\uff0c\u5546\u5b9d\u9707\u53d6\u91d1\u9556\u6263\u5728\u624b\u4e2d\uff0c\u4e0d\u6562\u80e1\u601d\u4e71\u60f3\uff0c\u51dd\u795e\u542c\u7740\u6bcd\u4eb2\u53eb\u7a74\u3002 \u3000\u3000\u53ea\u542c\u5546\u8001\u592a\u53eb\u9053\uff1a\u201c\u82d7\u4eba\u51e4\uff0c\u547d\u95e8\u3001\u9676\u9053\uff01\u201d\u5546\u5b9d\u9707\u53f3\u624b\u53cc\u9556\u98de\u51fa\uff0c\u6b63\u4e2d\u6728\u724c\u4e0a\u6240\u7ed8\u4eba\u5f62\u80cc\u5fc3\u4e24\u7a74\u3002\u5546\u8001\u592a\u53c8\u53eb\uff1a\u201c\u80e1\u4e00\u5200\uff0c\u5927\u690e\u3001\u9633\u5173\uff01\u201d\u5546\u5b9d\u9707\u5de6\u624b\u626c\u8d77\uff0c\u8ba4\u660e\u7a74\u9053\uff0c\u767b\u767b\u4e24\u58f0\u53d1\u51fa\uff0c\u201c\u5927\u690e\u7a74\u201d\u6253\u51c6\u4e86\uff0c\u201c\u9633\u5173\u7a74\u201d\u5374\u662f\u7a0d\u504f\uff0c\u7a81\u7136\u95f4\u89c1\u5230\u6728\u724c\u6709\u5f02\uff0c\u201c\u54a6\u201d\u7684\u4e00\u58f0\uff0c\u5b9a\u775b\u4e00\u770b\uff0c\u53ea\u89c1\u6728\u724c\u4e0a\u539f\u6765\u5199\u7740\u7684\u201c\u80e1\u4e00\u5200\u201d\u4e09\u4e2a\u9ed1\u5b57\u5df2\u7136\u4e0d\u89c1\u3002\u4ed6\u62db\u624b\u53eb\u90a3\u6301\u724c\u5bb6\u4e01\u8fc7\u6765\uff0c\u5f85\u90a3\u6728\u724c\u62ff\u8fd1\uff0c\u770b\u6e05\u695a\u201c\u80e1\u4e00\u5200\u201d\u4e09\u5b57\u5df2\u88ab\u4eba\u7528\u5229\u5668\u522e\u53bb\uff0c\u5374\u7528\u5200\u5c16\u523b\u4e86\u6b6a\u6b6a\u659c\u659c\u7684\u201c\u5546\u5251\u9e23\u201d\u4e09\u4e2a\u5b57\uff0c\u8fd9\u4e00\u6765\u9002\u624d\u8fd9\u4e24\u9556\u4e0d\u662f\u6253\u4e86\u4ec7\u4eba\uff0c\u5374\u662f\u6253\u4e2d\u4e86\u81ea\u5df1\u7236\u4eb2\u3002 \u3000\u3000\u5546\u5b9d\u9707\u53c8\u6025\u53c8\u6012\uff0c\u53cd\u624b\u4e00\u638c\uff0c\u5c06\u90a3\u5bb6\u4e01\u6253\u843d\u4e24\u679a\u7259\u9f7f\uff0c\u8ddf\u7740\u4e00\u811a\uff0c\u5c06\u4ed6\u8e22\u5012\u5728\u5730\u3002 \u3000\u3000\u5546\u8001\u592a\u53eb\u9053\uff1a\u201c\u4e14\u4f4f\uff01\u201d\u5fc3\u60f3\u8fd9\u5e84\u4e01\u81ea\u5e7c\u5728\u5e84\u4e2d\u957f\u5927\uff0c\u600e\u80fd\u5982\u6b64\u5927\u80c6\uff0c\u6b64\u4e8b\u5b9a\u662f\u5916\u4eba\u6240\u4e3a\uff0c\u5fc3\u5ff5\u4e00\u52a8\uff0c\u7acb\u65f6\u60f3\u5230\u4e86\u9a6c\u884c\u7a7a\u5e08\u5f92\u4e09\u4eba\uff0c\u8bf4\u9053\uff1a\u201c\u8bf7\u9a6c\u8001\u5e08\u6765\u8bf4\u8bdd\u3002\u201d\u5546\u5b9d\u9707\u672c\u6765\u4e3a\u4eba\u7cbe\u7ec6\uff0c\u4eca\u65e5\u5a5a\u4e8b\u4e0d\u6210\uff0c\u5931\u610f\u4e4b\u4e0b\uff0c\u5364\u83bd\u51fa\u624b\uff0c\u4e00\u542c\u6bcd\u4eb2\u53eb\u8bf7\u9a6c\u8001\u5e08\uff0c\u7acb\u65f6\u4f1a\u610f\u6253\u9519\u4e86\u4eba\uff0c\u5fd9\u5c06\u90a3\u5e84\u4e01\u62c9\u8d77\uff0c\u8bf4\u9053\uff1a\u201c\u6253\u9519\u4e86\u4f60\uff0c\u522b\u89c1\u602a\u3002\u201d\u4f38\u624b\u53bb\u62d4\u724c\u4e0a\u4eba\u5f62\u7a74\u9053\u4e2d\u7684\u91d1\u9556\u3002\u5546\u8001\u592a\u4f38\u624b\u62e6\u4f4f\uff0c\u8bf4\u9053\uff1a\u201c\u6162\u7740\uff01\u5c31\u8ba9\u4ed6\u5f97\u610f\u4e00\u4e0b\uff0c\u53c8\u6709\u4f55\u59a8\u3002\u201d\u8f6c\u5934\u5429\u5490\u5e84\u4e01\uff0c\u5230\u8001\u7237\u7075\u5802\u4e2d\u53d6\u7d2b\u91d1\u516b\u5366\u5200\u6765\u3002 \u3000\u3000\u9a6c\u884c\u7a7a\u5e08\u5f92\u4e09\u4eba\u8d70\u8fdb\u5385\u6765\uff0c\u89c1\u7ec3\u6b66\u5385\u4e0a\u4eba\u4eba\u795e\u8272\u6709\u5f02\u3002\u9a6c\u884c\u7a7a\u6697\u5403\u4e00\u60ca\uff1a \u3000\u3000\u201c\u8fd9\u8001\u5a46\u5b50\u597d\u5389\u5bb3\uff0c\u4e00\u65f6\u4e09\u523b\u5c31\u8981\u7ffb\u8138\u3002\u201d\u5f53\u4e0b\u53cc\u624b\u4e00\u62f1\uff0c\u8bf4\u9053\uff1a\u201c\u8001\u592a\u592a\u547c\u5524\uff0c\u4e0d\u77e5\u4f55\u4e8b\uff1f\u201d\u5546\u8001\u592a\u51b7\u7b11\u9053\uff1a\u201c\u5148\u592b\u5df2\u7136\u901d\u4e16\uff0c\u9a6c\u8001\u5e08\u5f80\u65e5\u867d\u6709\u8fc7\u8282\uff0c\u5374\u4e5f\u4e0d\u8be5\u62ff\u6b7b\u4eba\u6765\u51fa\u6c14\u554a\u3002\u201d\u9a6c\u884c\u7a7a\u4e00\u5446\uff0c\u7b11\u9053\uff1a\u201c\u5728\u4e0b\u611a\u9c81\uff0c\u8bf7\u5546\u8001\u592a\u660e\u793a\u3002\u201d\u5546\u8001\u592a\u5411\u90a3\u6728\u724c\u4e0a\u4e00\u6307\uff0c\u9053\uff1a\u201c\u9a6c\u8001\u5e08\u4e43\u662f\u6c5f\u6e56\u4e0a\u54cd\u5f53\u5f53\u7684\u6c49\u5b50\uff0c\u8fd9\u822c\u5351\u9119\u884c\u5f84\uff0c\u60f3\u6765\u4e5f\u4e0d\u5c51\u4e3a\uff0c\u8bf7\u95ee\u662f\u4ee4\u7231\u6240\u5e72\u7684\u5462\uff0c\u8fd8\u662f\u8d24\u9ad8\u5f92\u7684\u624b\u7b14\uff1f\u201d \u3000\u3000\u8bf4\u7740\u53cc\u76ee\u95ea\u95ea\u751f\u5149\uff0c\u5411\u9a6c\u5bb6\u4e09\u4eba\u8138\u4e0a\u6765\u56de\u626b\u89c6\u3002\u9a6c\u6625\u82b1\u4ece\u672a\u89c1\u8fc7\u5979\u5982\u6b64\u51db\u7136\u6709\u5a01\uff0c\u751a\u662f\u60ca\u8be7\u3002 \u3000\u3000\u9a6c\u884c\u7a7a\u89c1\u6728\u724c\u4e0a\u6539\u4e86\u4eba\u540d\uff0c\u4e5f\u662f\u5927\u4e3a\u9a87\u5f02\uff0c\u6717\u58f0\u9053\uff1a\u201c\u5c0f\u5973\u4e0e\u5c0f\u5f92\u867d\u7136\u8822\u7b28\uff0c\u4f46\u51b3\u4e0d\u6562\u5982\u6b64\u80e1\u95f9\u3002\u201d\u5546\u8001\u592a\u5927\u58f0\u9053\uff1a\u201c\u90a3\u4e48\u4f9d\u9a6c\u8001\u5e08\u4e4b\u89c1\uff0c\u8fd9\u662f\u5546\u5bb6\u5821\u81ea\u5df1\u4eba\u5e72\u7684\u52fe\u5f53\u4e86\uff1f\u201d\u9a6c\u884c\u7a7a\u60f3\u8d77\u6628\u665a\u6240\u89c1\u7684\u90a3\u7626\u5c0f\u4eba\u5f62\uff0c\u8bf4\u9053\uff1a\u201c\u53ea\u6015\u662f\u5916\u4eba\u6478\u8fdb\u5e84\u6765\uff0c\u4e5f\u662f\u6709\u7684\u3002\u5728\u4e0b\u6628\u665a\u2026\u2026\u201d\u5546\u8001\u592a\u62e6\u65ad\u8bdd\u5934\uff0c\u5389\u58f0\u559d\u9053\uff1a\u201c\u96be\u9053\u4f1a\u662f\u80e1\u4e00\u5200\u90a3\u72d7\u8d3c\u81ea\u5df1\uff0c\u6765\u505a\u8fd9\u9b3c\u795f\u7684\u52fe\u5f53\uff1f\u201d \u3000\u3000\u4e00\u8a00\u752b\u6bd5\uff0c\u7a81\u7136\u4eba\u5708\u5916\u4e00\u4eba\u63a5\u7740\u53eb\u9053\uff1a\u201c\u4e0d\u6562\u53bb\u627e\u771f\u4eba\u52a8\u624b\uff0c\u5374\u5c06\u4eba\u5bb6\u7684\u540d\u5b57\u5199\u5728\u724c\u4e0a\u51fa\u6c14\uff0c\u8fd9\u624d\u662f\u5351\u9119\u884c\u5f84\uff0c\u9b3c\u795f\u52fe\u5f53\uff01\u201d \u3000\u3000\u5546\u8001\u592a\u5750\u5728\u6905\u4e0a\uff0c\u77a7\u4e0d\u89c1\u8bf4\u8bdd\u4e4b\u4eba\u662f\u8c01\uff0c\u4f46\u542c\u5230\u4ed6\u58f0\u97f3\u5c16\u7ec6\uff0c\u53eb\u9053\uff1a\u201c\u662f\u8c01\u8bf4\u8bdd\uff1f\u4f60\u8fc7\u6765\uff01\u201d\u53ea\u89c1\u4e24\u540d\u5e84\u4e01\u88ab\u4eba\u63a8\u7740\u5411\u4e24\u65c1\u4e00\u5206\uff0c\u4e00\u4e2a\u7626\u5c11\u5e74\u8d70\u4e0a\u524d\u6765\uff0c\u6b63\u662f\u80e1\u6590\u3002 \u3000\u3000\u8fd9\u4e00\u4e0b\u5f53\u771f\u662f\u5947\u5cf0\u7a81\u8d77\uff0c\u4eba\u4eba\u65e0\u4e0d\u5927\u51fa\u610f\u5916\u3002\u5546\u8001\u592a\u53cd\u800c\u653e\u4f4e\u4e86\u55d3\u5b50\uff0c\u8bf4\u9053\uff1a\u201c\u963f\u6590\uff0c\u539f\u6765\u662f\u4f60\u3002\u201d\u80e1\u6590\u70b9\u5934\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u662f\u6211\u5e72\u7684\u3002\u9a6c\u8001\u5e08\u4ed6\u4eec\u5168\u4e0d\u77e5\u60c5\u3002\u201d\u5546\u8001\u592a\u95ee\u9053\uff1a\u201c\u4f60\u8fd9\u4e48\u5e72\uff0c\u4e3a\u4e86\u4ec0\u4e48\uff1f\u201d\u80e1\u6590\u9053\uff1a\u201c\u6211\u77a7\u4e0d\u8fc7\u773c\uff01\u662f\u82f1\u96c4\u597d\u6c49\uff0c\u5c31\u4e0d\u8be5\u5982\u6b64\u3002\u201d\u5546\u8001\u592a\u70b9\u5934\u9053\uff1a\u201c\u4f60\u8bf4\u5f97\u5f88\u5bf9\uff0c\u597d\u5b69\u5b50\uff0c\u4f60\u5f88\u6709\u9aa8\u6c14\uff0c\u4f60\u8fc7\u6765\uff0c\u8ba9\u6211\u597d\u597d\u5730\u77a7\u77a7\u4f60\u3002\u201d\u8bf4\u7740\u7f13\u7f13\u4f38\u51fa\u624b\u53bb\u3002 \u3000\u3000\u80e1\u6590\u5012\u4e0d\u6599\u5979\u7adf\u4f1a\u4e0d\u6012\uff0c\u4fbf\u8d70\u8fd1\u8eab\u53bb\u3002\u5546\u8001\u592a\u8f7b\u8f7b\u63e1\u4f4f\u4ed6\u53cc\u624b\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u597d\u5b69\u5b50\uff0c\u771f\u662f\u597d\u5b69\u5b50\uff01\u201d\u7a81\u7136\u95f4\u53cc\u624b\u4e00\u7ffb\uff0c\u4e00\u624b\u6263\u4f4f\u4ed6\u5de6\u8155\u201c\u4f1a\u5b97\u7a74\u201d\uff0c\u4e00\u624b\u6263\u4f4f\u4ed6\u53f3\u8155\u201c\u5916\u5173\u7a74\u201d\u3002 \u3000\u3000\u5979\u8fd9\u4e00\u7ffb\u5b9b\u4f3c\u7535\u5149\u77f3\u706b\uff0c\u80e1\u6590\u5168\u672a\u9632\u5907\uff0c\u767b\u65f6\u5168\u8eab\u9178\u9ebb\uff0c\u52a8\u5f39\u4e0d\u5f97\u3002\u82e5\u51ed\u4ed6\u6b64\u65f6\u6b66\u529f\uff0c\u5546\u8001\u592a\u54ea\u80fd\u64d2\u5f97\u4ed6\u4f4f\uff1f\u4f46\u4ed6\u7a76\u7adf\u5168\u65e0\u4e34\u654c\u7ecf\u9a8c\uff0c\u4e0d\u77e5\u4eba\u5fc3\u9669\u8bc8\uff0c\u53cc\u8155\u65e2\u5165\u4eba\u624b\uff0c\u7a7a\u6709\u5468\u8eab\u672c\u4e8b\uff0c\u5374\u5df2\u534a\u5206\u65bd\u5c55\u4e0d\u51fa\u3002\u5546\u8001\u592a\u552f\u6050\u4ed6\u6323\u624e\uff0c\u98de\u811a\u53c8\u8e22\u4e2d\u4ed6\u7684\u201c\u6881\u95e8\u7a74\u201d\uff0c\u547d\u5e84\u4e01\u53d6\u8fc7\u94c1\u94fe\u9ebb\u7ef3\uff0c\u7262\u7262\u5c06\u4ed6\u624b\u8db3\u53cd\u7ed1\u4e86\uff0c\u540a\u5728\u7ec3\u6b66\u5385\u4e2d\u3002 \u3000\u3000\u5546\u5b9d\u9707\u53d6\u8fc7\u4e00\u6839\u76ae\u97ad\uff0c\u5939\u5934\u5939\u8111\u5148\u6253\u4e86\u4ed6\u4e00\u987f\u3002\u80e1\u6590\u95ed\u53e3\u4e0d\u54cd\uff0c\u65e2\u4e0d\u547b\u541f\uff0c\u66f4\u4e0d\u8ba8\u9976\u3002\u5546\u5b9d\u9707\u8fde\u95ee\uff1a\u201c\u662f\u8c01\u6d3e\u4f60\u6765\u505a\u5978\u7ec6\u7684\uff1f\u201d\u95ee\u4e00\u53e5\uff0c\u62bd\u4e00\u97ad\uff0c\u53c8\u547d\u5e84\u4e01\u53bb\u770b\u4f4f\u5e73\u963f\u56db\uff0c\u522b\u8ba9\u4ed6\u8dd1\u4e86\u3002\u4ed6\u6ee1\u8154\u6124\u6068\u5931\u610f\uff0c\u7adf\u4f3c\u8981\u5c3d\u6570\u5728\u80e1\u6590\u8eab\u4e0a\u53d1\u6cc4\u3002 \u3000\u3000\u9a6c\u6625\u82b1\u548c\u5f90\u94ee\u89c1\u80e1\u6590\u5df2\u5168\u8eab\u662f\u8840\uff0c\u5fc3\u4e0b\u4e0d\u5fcd\uff0c\u51e0\u6b21\u60f3\u5f00\u53e3\u529d\u963b\uff0c\u4f46\u9a6c\u884c\u7a7a\u8fde\u4f7f\u773c\u8272\uff0c\u795e\u8272\u4e25\u5389\uff0c\u547d\u4e8c\u4eba\u4e0d\u53ef\u7406\u4f1a\u3002 \u3000\u3000\u5546\u5b9d\u9707\u8db3\u8db3\u62bd\u4e86\u4e09\u767e\u4f59\u97ad\uff0c\u7ec8\u7a76\u95ee\u4e0d\u5230\u4e3b\u4f7f\u4e4b\u4eba\uff0c\u773c\u89c1\u518d\u6253\u4e0b\u53bb\u4fbf\u8981\u628a\u4ed6\u6d3b\u6d3b\u6253\u6b7b\uff0c\u8fd9\u624d\u629b\u4e0b\u97ad\u5b50\uff0c\u9a82\u9053\uff1a\u201c\u5c0f\u8d3c\uff0c\u662f\u5978\u8d3c\u80e1\u4e00\u5200\u6d3e\u4f60\u6765\u7684\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u80e1\u6590\u7a81\u7136\u5f20\u5634\u54c8\u54c8\u5927\u7b11\u3002\u4ed6\u8fd9\u6837\u4e00\u4e2a\u8840\u4eba\u513f\uff0c\u5c45\u7136\u5c1a\u6709\u5fc3\u60c5\u53d1\u7b11\uff0c\u800c\u4e14\u7b11\u5f97\u751a\u662f\u6b22\u7545\u5c3d\u610f\uff0c\u5e76\u65e0\u505a\u4f5c\uff0c\u53c8\u662f\u5927\u51fa\u4f17\u4eba\u610f\u6599\u4e4b\u5916\u3002\u5546\u5b9d\u9707\u62a2\u8d77\u97ad\u5b50\uff0c\u53c8\u5f85\u518d\u6253\uff0c\u9a6c\u6625\u82b1\u518d\u4e5f\u5fcd\u8010\u4e0d\u4f4f\uff0c\u5927\u53eb\u9053\uff1a\u201c\u4e0d\u8981\u6253\u4e86\uff01\u201d\u5546\u5b9d\u9707\u7684\u76ae\u97ad\u4e3e\u5728\u534a\u7a7a\uff0c\u671b\u7740\u9a6c\u6625\u82b1\u7684\u8138\u8272\uff0c\u7ec8\u4e8e\u7f13\u7f13\u5782\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u80e1\u6590\u8eab\u4e0a\u6bcf\u5403\u4e00\u97ad\uff0c\u5c31\u6068\u4e00\u6b21\u81ea\u5df1\u611a\u8822\uff0c\u7adf\u7136\u4e0d\u52a0\u9632\u5907\u800c\u81ea\u843d\u654c\u4eba\u4e4b\u624b\uff0c\u5f53\u65f6\u5168\u8eab\u76ae\u5f00\u8089\u7efd\uff0c\u75db\u5f97\u51e0\u6b32\u660f\u53bb\uff0c\u5ffd\u542c\u9a6c\u6625\u82b1\u201c\u4e0d\u8981\u6253\u4e86\u201d\u56db\u5b57\u51fa\u53e3\uff0c\u7741\u5f00\u773c\u6765\uff0c\u53ea\u89c1\u5979\u8138\u4e0a\u6ee1\u662f\u540c\u60c5\u601c\u60dc\u4e4b\u8272\uff0c\u4e0d\u7531\u5f97\u5927\u662f\u611f\u6fc0\u3002 \u3000\u3000\u5546\u8001\u592a\u89c1\u513f\u5b50\u4e3a\u5973\u8272\u6240\u8ff7\uff0c\u53ea\u51ed\u4eba\u5bb6\u59d1\u5a18\u4e00\u53e5\u8bdd\u4fbf\u5373\u4f4f\u624b\u505c\u97ad\uff0c\u5fc3\u4e2d\u607c\u6012\u5f02\u5e38\uff0c\u9f3b\u5b54\u4e2d\u5fae\u5fae\u4e00\u54fc\uff0c\u5374\u4e0d\u8bf4\u8bdd\u3002\u9a6c\u884c\u7a7a\u9053\uff1a\u201c\u5546\u8001\u592a\uff0c\u4f60\u597d\u597d\u62f7\u6253\u76d8\u67e5\uff0c\u603b\u8981\u95ee\u4e2a\u6c34\u843d\u77f3\u51fa\u3002\u6625\u513f\u3001\u94ee\u513f\uff0c\u54b1\u4eec\u51fa\u53bb\u5427\uff01\u201d\u5f53\u4e0b\u5411\u5546\u8001\u592a\u4e00\u62b1\u62f3\uff0c\u9886\u7740\u5973\u513f\u5f92\u5f1f\uff0c\u8d70\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u9a6c\u6625\u82b1\u51fa\u4e86\u7ec3\u6b66\u5385\uff0c\u57cb\u6028\u7236\u4eb2\u9053\uff1a\u201c\u7239\uff0c\u6253\u5f97\u8fd9\u4e48\u60e8\uff0c\u4f60\u600e\u4e48\u89c1\u6b7b\u4e0d\u6551\uff0c\u8fd8\u53eb\u5979\u597d\u597d\u62f7\u6253\uff1f\u201d\u9a6c\u884c\u7a7a\u9053\uff1a\u201c\u6c5f\u6e56\u4e0a\u4eba\u5fc3\u9669\u6076\uff0c\u5973\u5b69\u513f\u5bb6\u61c2\u5f97\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5bf9\u7236\u4eb2\u8fd9\u51e0\u53e5\u8bdd\uff0c\u9a6c\u6625\u82b1\u786e\u662f\u4e0d\u61c2\uff0c\u8fd9\u5929\u665a\u4e0a\u60f3\u5230\u80e1\u6590\u5168\u8eab\u662f\u8840\u7684\u60e8\u72b6\uff0c\u603b\u662f\u96be\u53d7\uff0c\u7761\u5230\u534a\u591c\uff0c\u7ffb\u6765\u8986\u53bb\u5730\u518d\u4e5f\u7761\u4e0d\u7740\u4e86\uff0c\u6084\u6084\u722c\u8d77\u8eab\u6765\uff0c\u4ece\u767e\u5b9d\u56ca\u4e2d\u53d6\u51fa\u4e00\u5305\u91d1\u521b\u836f\uff0c\u51fa\u623f\u95e8\u5411\u7ec3\u6b66\u5385\u8d70\u53bb\u3002 \u3000\u3000\u8d70\u5230\u5eca\u4e0b\uff0c\u53ea\u89c1\u4e00\u4e2a\u4eba\u5f71\uff0c\u8e31\u6765\u8e31\u53bb\u53d1\u51fa\u58f0\u58f0\u957f\u53f9\uff0c\u542c\u58f0\u97f3\u6b63\u662f\u5546\u5b9d\u9707\u3002 \u3000\u3000\u8fd9\u65f6\u4ed6\u4e5f\u77a7\u89c1\u4e86\u9a6c\u6625\u82b1\uff0c\u505c\u6b65\u4e0d\u52a8\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u9a6c\u59d1\u5a18\uff0c\u662f\u4f60\u4e48\uff1f\u201d\u9a6c\u6625\u82b1\u9053\uff1a\u201c\u662f\u554a\uff01\u4f60\u600e\u4e48\u8fd8\u4e0d\u7761\uff1f\u201d \u3000\u3000\u5546\u5b9d\u9707\u6447\u5934\u9053\uff1a\u201c\u906d\u9022\u4eca\u65e5\u4e4b\u4e8b\uff0c\u6211\u600e\u4e48\u7761\u5f97\u7740\uff1f\u4f60\u600e\u4e48\u4e0d\u7761\uff1f\u201d\u9a6c\u6625\u82b1\u8bf4\u9053\uff1a\u201c\u6211\u8ddf\u4f60\u4e00\u6837\uff0c\u4e5f\u7275\u6302\u7740\u4eca\u65e5\u4e4b\u4e8b\uff0c\u5fc3\u91cc\u96be\u53d7\u3002\u201d\u5979\u6240\u8bf4\u7684\u201c\u4eca\u65e5\u4e4b\u4e8b\u201d\uff0c\u662f\u6307\u80e1\u6590\u88ab\u6253\u3002\u5546\u5b9d\u9707\u6240\u8bf4\u7684\u5374\u662f\u6307\u5979\u7684\u7ec8\u8eab\u53e6\u8bb8\u4ed6\u4eba\uff0c\u8fd9\u65f6\u542c\u5979\u8bf4\u201c\u5fc3\u4e2d\u96be\u53d7\u201d\uff0c\u4e0d\u7531\u5f97\u8eab\u5b50\u53d1\u6296\uff0c\u6697\u60f3\uff1a\u201c\u5979\u679c\u7136\u5bf9\u6211\u751a\u6709\u60c5\u610f\uff0c\u5979\u88ab\u8bb8\u914d\u7ed9\u90a3\u59d3\u5f90\u7684\u8822\u624d\uff0c\u5b9e\u662f\u8feb\u4e8e\u7236\u547d\uff0c\u65e0\u53ef\u5948\u4f55\u3002\u201d\u5f53\u4e0b\u5927\u7740\u80c6\u5b50\uff0c\u4e0a\u524d\u4e00\u6b65\uff0c\u67d4\u58f0\u53eb\u9053\uff1a\u201c\u9a6c\u59d1\u5a18\uff01\u201d \u3000\u3000\u9a6c\u6625\u82b1\u9053\uff1a\u201c\u55ef\uff0c\u5546\u5c11\u7237\uff0c\u6211\u60f3\u6c42\u4f60\u4e00\u4ef6\u4e8b\u3002\u201d\u5546\u5b9d\u9707\u9053\uff1a\u201c\u4f60\u4f55\u5fc5\u6c42\uff1f \u3000\u3000\u4f60\u8981\u6211\u505a\u4ec0\u4e48\uff0c\u6211\u5c31\u7ed9\u4f60\u505a\u4ec0\u4e48\uff0c\u5c31\u662f\u8981\u6211\u5f53\u573a\u6b7b\u4e86\uff0c\u628a\u6211\u7684\u5fc3\u638f\u51fa\u6765\u7ed9\u4f60\u770b\uff0c\u90a3\u4e5f\u6210\u554a\u3002\u201d\u8fd9\u51e0\u53e5\u8bdd\u8bf4\u5f97\u60c5\u70ed\u5982\u6cb8\uff0c\u5176\u5b9e\u4ed6\u5fc3\u4e2d\u60f3\u8bf4\u5df2\u4e45\uff0c\u5374\u4e00\u76f4\u4e0d\u6562\u542f\u5507\uff0c\u8fd9\u65f6\u60f3\u5230\u597d\u4e8b\u6210\u7a7a\uff0c\u5979\u53c8\u81ea\u884c\u534a\u591c\u91cc\u51fa\u6765\u7ec6\u8bc9\u8877\u60c5\uff0c\u7ec8\u4e8e\u518d\u4e5f\u5fcd\u8010\u4e0d\u4f4f\u3002 \u3000\u3000\u9a6c\u6625\u82b1\u542c\u4ed6\u8fd9\u4e48\u8bf4\uff0c\u4e0d\u7981\u6115\u7136\uff0c\u5e73\u65e5\u4f46\u89c1\u4ed6\u5bf9\u81ea\u5df1\u6e29\u6587\u6709\u793c\uff0c\u53ea\u9053\u4ed6\u662f\u5927\u5bb6\u516c\u5b50\uff0c\u751f\u6027\u5982\u6b64\uff0c\u5b9e\u4e0d\u77e5\u5bf9\u81ea\u5df1\u7adf\u6000\u6709\u5982\u6b64\u6df1\u60c5\uff0c\u5446\u4e86\u4e00\u5446\uff0c\u7b11\u9053\uff1a\u201c\u6211\u8981\u4f60\u6b7b\u5e72\u4ec0\u4e48\uff1f\u201d\u5546\u5b9d\u9707\u56db\u4e0b\u4e00\u671b\uff0c\u53ea\u6015\u5728\u6b64\u5904\u803d\u5f97\u4e45\u4e86\u7ed9\u65c1\u4eba\u89c1\u5230\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u8fd9\u91cc\u8bf4\u8bdd\u4e0d\u4fbf\uff0c\u54b1\u4eec\u5230\u5899\u5916\u53bb\u3002\u201d\u9a6c\u6625\u82b1\u70b9\u70b9\u5934\uff0c\u4e24\u4eba\u8d8a\u5899\u800c\u51fa\u3002 \u3000\u3000\u5546\u5b9d\u9707\u643a\u7740\u5979\u624b\uff0c\u8d70\u5230\u4e00\u6392\u5927\u69d0\u6811\u4e0b\u5e76\u80a9\u5750\u4e0b\u3002\u9a6c\u6625\u82b1\u8f7b\u8f7b\u5c06\u624b\u7f29\u56de\uff0c\u9053\uff1a\u201c\u5546\u5c11\u7237\uff0c\u90a3\u4f60\u662f\u80af\u7b54\u5141\u6211\u4e86\uff1f\u201d\u5546\u5b9d\u9707\u4f38\u51fa\u624b\u53bb\u63e1\u4f4f\u5979\u624b\uff0c\u9053\uff1a\u201c\u4f60\u8bf4\u4fbf\u662f\uff0c\u4f55\u5fc5\u95ee\u6211\uff1f\u201d\u9a6c\u6625\u82b1\u53c8\u5c06\u624b\u4ece\u4ed6\u624b\u4e2d\u7f29\u56de\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u8bf7\u4f60\u53bb\u653e\u4e86\u963f\u6590\uff0c\u522b\u518d\u96be\u4e3a\u4ed6\u4e86\u3002\u201d \u3000\u3000\u8fd9\u65f6\u6811\u9876\u4e0a\u7c41\u7c41\u4e00\u52a8\uff0c\u4f46\u4e8c\u4eba\u5747\u672a\u5728\u610f\u3002\u5979\u6b64\u8a00\u51fa\u53e3\u4e4b\u5148\uff0c\u5546\u5b9d\u9707\u5c3d\u60f3\u7740\u7530\u5f52\u519c\u548c\u82d7\u592b\u4eba\u7684\u79c1\u60c5\uff0c\u6ee1\u8154\u70ed\u671b\uff0c\u53ea\u76fc\u5979\u6c42\u81ea\u5df1\u4e5f\u5e26\u5979\u79c1\u5954\u9003\u8d70\uff0c\u5c82\u77e5\u5979\u6240\u6c42\u7684\u7adf\u662f\u53bb\u653e\u90a3\u4e2a\u5c0f\u8d3c\uff0c\u4e0d\u7981\u5927\u662f\u5931\u671b\uff0c\u9eef\u7136\u4e0d\u8bed\u3002\u9a6c\u6625\u82b1\u9053\uff1a\u201c\u600e\u4e48\uff1f \u3000\u3000\u4f60\u4e0d\u80af\u7b54\u5141\u4e48\uff1f\u201d\u5546\u5b9d\u9707\u9053\uff1a\u201c\u4f60\u65e2\u559c\u6b22\uff0c\u6211\u603b\u7b54\u5141\u7684\uff0c\u62fc\u7740\u7ed9\u5988\u8d23\u9a82\u4fbf\u662f\u4e86\u3002\u201d\u9a6c\u6625\u82b1\u5927\u559c\uff0c\u9053\uff1a\u201c\u8c22\u8c22\u4f60\uff0c\u8c22\u8c22\u4f60\uff01\u201d\u7ad9\u8d77\u8eab\u6765\uff0c\u9053\uff1a\u201c\u90a3\u4e48\u54b1\u4eec\u53bb\u653e\u4ed6\u5427\u3002\u201d\u5546\u5b9d\u9707\u6c42\u9053\uff1a\u201c\u518d\u5728\u8fd9\u513f\u591a\u5750\u4e00\u4f1a\u3002\u201d\u9a6c\u6625\u82b1\u89c9\u4ed6\u65e2\u7136\u7b54\u5141\u653e\u4eba\uff0c\u4e0d\u4fbf\u62c2\u4ed6\u4e4b\u610f\uff0c\u91cd\u53c8\u5750\u56de\u3002\u5546\u5b9d\u9707\u9053\uff1a\u201c\u4f60\u7684\u624b\u8ba9\u6211\u63e1\u4e00\u4f1a\u513f\u3002\u201d\u9a6c\u6625\u82b1\u60f3\u5230\u4ed6\u60c5\u75f4\u4e00\u7247\uff0c\u4e5f\u751a\u53ef\u601c\uff0c\u4e8e\u662f\u5ae3\u7136\u4e00\u7b11\uff0c\u4f38\u624b\u8ba9\u4ed6\u63e1\u7740\u3002 \u3000\u3000\u5546\u5b9d\u9707\u8f7b\u8f7b\u63e1\u7740\u5979\u67d4\u817b\u6da6\u6ed1\u7684\u5c0f\u624b\uff0c\u5fc3\u4e2d\u611f\u6168\u4e07\u7aef\uff0c\u9669\u4e9b\u8981\u6389\u4e0b\u6cea\u6765\u3002 \u3000\u3000\u8fc7\u4e86\u534a\u664c\uff0c\u9a6c\u6625\u82b1\u9053\uff1a\u201c\u963f\u6590\u7ed9\u4f60\u540a\u7740\uff0c\u591a\u53ef\u601c\u7684\uff0c\u4f60\u5148\u53bb\u653e\u4e86\u4ed6\uff0c\u6211\u518d\u7ed9\u4f60\u63e1\u4e00\u4f1a\u513f\uff0c\u597d\u4e0d\u597d\uff1f\u201d\u8bf4\u7740\u7f29\u624b\u7ad9\u8d77\u3002\u5546\u5b9d\u9707\u53f9\u4e86\u53e3\u6c14\uff0c\u8ddf\u7740\u7ad9\u4e86\u8d77\u6765\u3002 \u3000\u3000\u7a81\u542c\u5f97\u6811\u9876\u98d2\u7136\u6709\u58f0\uff0c\u4e00\u56e2\u9ed1\u5f71\u98de\u8dc3\u800c\u4e0b\uff0c\u7ad9\u5728\u4e24\u4eba\u9762\u524d\uff0c\u7b11\u9053\uff1a\u201c\u4e0d\u7528\u4f60\u653e\uff0c\u6211\u65e9\u51fa\u6765\u5566\uff01\u201d\u9a6c\u5546\u4e8c\u4eba\u5927\u5403\u4e00\u60ca\uff0c\u5f85\u5f97\u77a7\u6e05\u695a\u773c\u524d\u4e4b\u4eba\u7626\u7626\u5c0f\u5c0f\uff0c\u7adf\u662f\u80e1\u6590\uff0c\u5fc3\u4e2d\u7684\u60ca\u9a87\u90fd\u53d8\u6210\u4e86\u5947\u602a\uff0c\u9f50\u58f0\u95ee\u9053\uff1a\u201c\u8c01\u653e\u4f60\u7684\uff1f\u201d\u80e1\u6590\u7b11\u9053\uff1a\u201c\u6211\u4f55\u5fc5\u8981\u4eba\u653e\uff01\u6211\u7231\u51fa\u6765\u4fbf\u51fa\u6765\u4e86\u3002\u201d \u3000\u3000\u539f\u6765\u4ed6\u88ab\u5546\u8001\u592a\u70b9\u4e86\u7a74\u9053\uff0c\u8fc7\u4e86\u56db\u4e2a\u65f6\u8fb0\uff0c\u7a74\u9053\u81ea\u89e3\uff0c\u90a3\u94c1\u94fe\u9ebb\u7ef3\u5374\u518d\u4e5f\u7f1a\u4ed6\u4e0d\u4f4f\u3002\u4ed6\u4f7f\u51fa\u6536\u808c\u7f29\u9aa8\u4e4b\u6cd5\uff0c\u4ece\u94fe\u7d22\u4e2d\u8f7b\u8f7b\u8131\u4e86\u51fa\u6765\uff0c\u5e78\u597d\u97ad\u5b50\u6253\u5f97\u867d\u91cd\uff0c\u5374\u90fd\u662f\u808c\u80a4\u4e4b\u4f24\uff0c\u5e76\u672a\u635f\u5230\u7b4b\u9aa8\u3002\u4ed6\u6d3b\u52a8\u4e86\u4e00\u4e0b\u624b\u8db3\uff0c\u5f85\u8981\u53bb\u6551\u5e73\u963f\u56db\uff0c\u5374\u542c\u5f97\u9a6c\u5546\u4e8c\u4eba\u8bf4\u8bdd\u548c\u8d8a\u5899\u51fa\u5916\u4e4b\u58f0\uff0c\u4e8e\u662f\u62a2\u5728\u5934\u91cc\uff0c\u8eb2\u5728\u6811\u9876\u5077\u542c\u3002 \u3000\u3000\u4ed6\u8f7b\u529f\u9ad8\u8d85\uff0c\u90a3\u4e8c\u4eba\u53c8\u5728\u5168\u795e\u8d2f\u6ce8\u4ed6\u8bf4\u8bdd\uff0c\u662f\u4ee5\u5e76\u672a\u77e5\u89c9\u3002 \u3000\u3000\u5546\u5b9d\u9707\u542c\u4ed6\u8bf4\u81ea\u5df1\u51fa\u6765\uff0c\u54ea\u91cc\u80af\u4fe1\uff0c\u5f53\u4e0b\u7591\u5fc3\u5927\u8d77\uff1a\u201c\u5b9a\u662f\u53c8\u6709\u5978\u7ec6\u6df7\u5165\u4e86\u5546\u5bb6\u5821\u6765\uff1f\u201d\u62a2\u4e0a\u53bb\u6293\u4ed6\u80f8\u53e3\u3002\u80e1\u6590\u5403\u4e86\u4ed6\u51e0\u767e\u97ad\u5b50\uff0c\u8fd9\u53e3\u6028\u6c14\u5982\u4f55\u4e0d\u51fa\uff1f\u8eab\u5f62\u4e00\u6643\uff0c\u5de6\u53f3\u5f00\u5f13\uff0c\u62cd\u62cd\u62cd\u62cd\uff0c\u970e\u65f6\u4e4b\u95f4\u8fde\u6253\u4e86\u4ed6\u56db\u4e2a\u8033\u5149\u3002 \u3000\u3000\u5546\u5b9d\u9707\u6025\u5fd9\u4f38\u624b\u62db\u67b6\uff0c\u80e1\u6590\u5de6\u624b\u4e00\u6643\uff0c\u5f15\u5f97\u4ed6\u4f38\u624b\u6765\u683c\uff0c\u53f3\u624b\u7830\u7684\u4e00\u62f3\uff0c\u8fce\u9762\u6b63\u4e2d\u4ed6\u7684\u9f3b\u5b50\uff0c\u7acb\u65f6\u9c9c\u8840\u957f\u6d41\u3002\u5546\u5b9d\u9707\u201c\u554a\u201d\u7684\u4e00\u58f0\uff0c\u80e1\u6590\u8ddf\u7740\u8d77\u811a\u4e00\u94a9\uff0c\u5546\u5b9d\u9707\u6025\u5fd9\u8dc3\u8d77\u4e24\u4e08\uff0c\u54ea\u77e5\u5bf9\u624b\u8fde\u73af\u811a\u8e22\u51fa\uff0c\u4e58\u4ed6\u4eba\u5728\u534a\u7a7a\uff0c\u4e0b\u76d8\u65e0\u636e\uff0c\u8ddf\u7740\u4e00\u811a\uff0c\u5c06\u4ed6\u8e22\u4e86\u4e00\u4e2a\u7b4b\u6597\u3002\u8fd9\u51e0\u4e0b\u5feb\u6377\u65e0\u4f26\uff0c\u5f85\u5f97\u9a6c\u6625\u82b1\u770b\u6e05\u695a\u65f6\uff0c\u5546\u5b9d\u9707\u5df2\u8fde\u4e2d\u62f3\u811a\uff0c\u7ed9\u8e22\u7ffb\u5728\u5730\u3002 \u3000\u3000\u80e1\u6590\u6c14\u72b9\u672a\u6cc4\uff0c\u788d\u7740\u9a6c\u6625\u82b1\u5728\u65c1\uff0c\u518d\u6253\u4e0b\u53bb\u5979\u5b9a\u8981\u51fa\u9762\u5e72\u9884\uff0c\u5979\u5bf9\u81ea\u5df1\u4e00\u7247\u597d\u5fc3\uff0c\u5927\u4e08\u592b\u6069\u6028\u5206\u660e\uff0c\u53ea\u8981\u5979\u4e00\u53e5\u8bdd\uff0c\u81ea\u5df1\u7109\u80fd\u4e0d\u542c\uff1f\u5f53\u5373\u62cd\u624b\u53eb\u9053\uff1a\u201c\u59d3\u5546\u7684\u5c0f\u72d7\u8d3c\uff0c\u4f60\u6562\u8ffd\u6211\u4e48\uff1f\u201d\u8bf4\u7740\u8f6c\u8eab\u4fbf\u9003\u3002 \u3000\u3000\u5546\u5b9d\u9707\u83ab\u540d\u5176\u5999\u5730\u4e2d\u4e86\u4ed6\u7684\u62f3\u811a\uff0c\u53ea\u56e0\u5bf9\u65b9\u51fa\u624b\u592a\u5feb\uff0c\u8fd8\u9053\u81ea\u5df1\u758f\u795e\uff0c\u4e0d\u4fe1\u4ed6\u4e00\u4e2a\u5c0f\u5c0f\u5b69\u7ae5\uff0c\u7adf\u6709\u80dc\u4e8e\u81ea\u5df1\u5bb6\u4f20\u516b\u5366\u95e8\u7684\u795e\u5999\u6b66\u529f\uff0c\u517c\u4e4b\u5fc3\u4e0a\u4eba\u5728\u65c1\uff0c\u8fd9\u4e2a\u8138\u5982\u4f55\u4e22\u5f97\u4e0b\uff1f\u5f53\u4e0b\u53d1\u8db3\u4fbf\u8ffd\u3002 \u3000\u3000\u80e1\u6590\u8f7b\u529f\u8fdc\u80dc\u4e8e\u4ed6\uff0c\u9003\u4e00\u9635\uff0c\u505c\u4e00\u4f1a\uff0c\u5f85\u4ed6\u8ffd\u8fd1\uff0c\u53c8\u5411\u524d\u5954\uff0c\u8f6c\u773c\u95f4\u4fbf\u5954\u51fa\u4e03\u516b\u91cc\u5730\uff0c\u89c1\u9a6c\u6625\u82b1\u867d\u7136\u8ddf\u6765\uff0c\u5374\u5df2\u8fdc\u8fdc\u629b\u5728\u540e\u9762\uff0c\u4e8e\u662f\u7acb\u5b9a\u811a\u6b65\uff0c\u8bf4\u9053\uff1a\u201c\u59d3\u5546\u7684\uff0c\u4eca\u65e5\u5c0f\u7237\u4e2d\u4e86\u4f60\u6bcd\u4eb2\u7684\u5978\u8ba1\uff0c\u8fd9\u624d\u53d7\u8fb1\uff0c\u73b0\u4e0b\u8ba9\u4f60\u89c1\u8bc6\u89c1\u8bc6\u5c0f\u7237\u7684\u672c\u4e8b\u3002\u201d\u8bf4\u7740\u8eab\u5f62\u98de\u8d77\uff0c\u5982\u4e00\u53ea\u5927\u9e1f\u822c\u75be\u6251\u8fc7\u53bb\u3002 \u3000\u3000\u5546\u5b9d\u9707\u4ece\u672a\u89c1\u8fc7\u8fd9\u822c\u6253\u6cd5\uff0c\u5413\u5f97\u6025\u5fd9\u95ea\u907f\u3002\u80e1\u6590\u5de6\u8db3\u5728\u5730\u4e0b\u5fae\u5fae\u4e00\u70b9\uff0c\u8eab\u5b50\u5df2\u8f6c\u8fc7\u65b9\u5411\uff0c\u8ddf\u7740\u8fdb\u6251\u3002\u8fd9\u65f6\u5546\u5b9d\u9707\u5f85\u8981\u518d\u8ba9\uff0c\u5374\u5df2\u4e0d\u53ca\uff0c\u5f53\u4e0b\u559d\u9053\uff1a\u201c\u6765\u5f97\u597d\uff01\u201d\u53cc\u638c\u5e76\u51fb\uff0c\u6b63\u662f\u4ed6\u5bb6\u4f20\u516b\u5366\u638c\u7684\u5389\u5bb3\u5bb6\u6570\u3002\u80e1\u6590\u5de6\u624b\u5728\u4ed6\u638c\u4e0a\u4e00\u642d\uff0c\u4e00\u62c9\u4e00\u626d\uff0c\u5546\u5b9d\u9707\u624b\u8155\u5267\u75db\uff0c\u82e5\u4e0d\u662f\u7f29\u624b\u5f97\u5feb\uff0c\u53cc\u624b\u624b\u8155\u7acb\u88ab\u626d\u65ad\u3002 \u3000\u3000\u80e1\u6590\u5de6\u62f3\u5e73\u4f38\uff0c\u7830\u7684\u4e00\u58f0\uff0c\u51fb\u4e2d\u4ed6\u7684\u53f3\u80f8\uff0c\u8ddf\u7740\u8d77\u811a\uff0c\u53c8\u8e22\u4e2d\u4ed6\u7684\u5c0f\u8179\u3002\u80e1\u6590\u4e60\u7ec3\u7236\u4eb2\u6240\u9057\u62f3\u7ecf\uff0c\u4eca\u65e5\u521d\u8bd5\u8eab\u624b\uff0c\u7adf\u7136\u5927\u83b7\u5168\u80dc\u3002 \u3000\u3000\u6b64\u523b\u5546\u5b9d\u9707\u5168\u8eab\u7f29\u62e2\uff0c\u53cc\u624b\u62a4\u4f4f\u5934\u8138\uff0c\u53ea\u6709\u6328\u6253\u7684\u4efd\u513f\uff0c\u82e6\u7ec3\u4e86\u5341\u591a\u5e74\u6b66\u529f\uff0c\u5728\u8fd9\u5c11\u5e74\u624b\u4e0b\uff0c\u7adf\u662f\u534a\u70b9\u65bd\u5c55\u4e0d\u51fa\u3002\u80e1\u6590\u5de6\u817f\u865a\u6643\uff0c\u4f8d\u4ed6\u907f\u5411\u53f3\u65b9\uff0c\u53f3\u811a\u500f\u5730\u8e22\u51fa\uff0c\u6b63\u4e2d\u4ed6\u53f3\u8170\u201c\u4eac\u95e8\u7a74\u201d\u3002\u5546\u5b9d\u9707\u7ad9\u7acb\u4e0d\u4f4f\uff0c\u6251\u5730\u5012\u4e86\u3002\u80e1\u6590\u5265\u4e0b\u4ed6\u957f\u886b\uff0c\u6495\u6210\u51e0\u7247\uff0c\u5c06\u4ed6\u624b\u811a\u53cd\u8f6c\u7f1a\u4f4f\uff0c\u672c\u8981\u5c06\u4ed6\u540a\u5728\u8def\u65c1\u7684\u67f3\u6811\u4e4b\u4e0a\uff0c\u4f46\u4ed6\u4eba\u5c0f\uff0c\u529b\u6c14\u4e0d\u591f\u63d0\u4e0a\u6811\u53bb\uff0c\u4e8e\u662f\u770b\u51c6\u4e86\u4e00\u4e2a\u5927\u6860\u679d\uff0c\u6293\u8d77\u5546\u5b9d\u9707\u6765\uff0c\u5927\u559d\u4e00\u58f0\uff1a\u201c\u53bb\u4f60\u7684\uff01\u201d\u529b\u8d2f\u53cc\u81c2\uff0c\u5c06\u4ed6\u63b7\u4e86\u4e0a\u53bb\uff0c\u6b63\u597d\u6401\u5728\u6860\u679d\u4e4b\u95f4\u3002 \u3000\u3000\u80e1\u6590\u6298\u4e0b\u4e03\u516b\u6839\u67f3\u6761\uff0c\u5f53\u4f5c\u97ad\u5b50\uff0c\u4e00\u97ad\u97ad\u5f80\u4ed6\u5934\u4e0a\u62bd\u53bb\uff0c\u5546\u5b9d\u9707\u53c8\u60ca\u53c8\u6012\uff0c\u77e5\u4ed6\u4e00\u62a5\u8fd8\u4e00\u62a5\uff0c\u53ea\u5f97\u54ac\u7d27\u7259\u5173\u5fcd\u53d7\u3002\u582a\u582a\u6253\u4e86\u4e09\u56db\u5341\u97ad\uff0c\u9a6c\u6625\u82b1\u6025\u5954\u8d76\u5230\uff0c\u4e00\u89c1\u4e8c\u4eba\u60c5\u666f\uff0c\u5927\u662f\u60ca\u8be7\uff0c\u4e00\u65f6\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u80e1\u6590\u7b11\u9053\uff1a\u201c\u9a6c\u59d1\u5a18\uff0c\u6211\u4e0d\u7528\u4f60\u6c42\u544a\uff0c\u5c31\u9976\u4e86\u4ed6\uff01\u201d\u8bf4\u7740\u54c8\u54c8\u5927\u7b11\uff0c\u867d\u662f\u4e00\u4e2a\u5341\u4f59\u5c81\u7684\u5c11\u5e74\uff0c\u4f46\u8a00\u8bed\u4e3e\u6b62\uff0c\u7adf\u7136\u8c6a\u6c14\u903c\u4eba\u3002\u4ed6\u968f\u624b\u5c06\u67f3\u679d\u8fdc\u8fdc\u629b\u51fa\uff0c\u5927\u8e0f\u6b65\u4fbf\u8d70\u3002\u9a6c\u6625\u82b1\u53eb\uff1a\u201c\u5c0f\u670b\u53cb\uff0c\u4f60\u5230\u5e95\u662f\u8c01\uff1f\u201d \u3000\u3000\u80e1\u6590\u8f6c\u8fc7\u5934\u6765\uff0c\u6717\u58f0\u7b54\u9053\uff1a\u201c\u59d1\u5a18\u89c1\u95ee\uff0c\u4e0d\u5f97\u4e0d\u8bf4\u3002\u6211\u662f\u5927\u4fa0\u80e1\u4e00\u5200\u7684\u513f\u5b50\u80e1\u6590\u4fbf\u662f\u3002\u201d\u8bf4\u7f62\u7eb5\u58f0\u957f\u7b11\uff0c\u7247\u523b\u95f4\u80cc\u5f71\u5df2\u5728\u67f3\u6811\u540e\u9690\u6ca1\u3002 \u3000\u3000\u201c\u6211\u662f\u5927\u4fa0\u80e1\u4e00\u5200\u7684\u513f\u5b50\u80e1\u6590\u4fbf\u662f\uff01\u201d \u3000\u3000\u4eba\u5df2\u8fdc\u53bb\uff0c\u8bdd\u58f0\u4f59\u97f3\u8885\u8885\uff0c\u5140\u81ea\u9e23\u54cd\u3002\u6811\u4e0a\u5546\u5b9d\u9707\uff0c\u6811\u4e0b\u9a6c\u6625\u82b1\uff0c\u90fd\u662f\u60ca\u8bb6\u4e0d\u5df2\u3002 \u3000\u3000\u8fc7\u4e86\u7247\u523b\uff0c\u9a6c\u6625\u82b1\u53eb\u9053\uff1a\u201c\u5546\u5c11\u7237\uff0c\u4f60\u80fd\u4e0b\u6765\u4e48\uff01\u201d\u5546\u5b9d\u9707\u7528\u529b\u6323\u624e\uff0c\u6323\u4e0d\u8131\u811a\u4e0a\u7684\u7ed1\u7f1a\uff0c\u5927\u662f\u7f9e\u60ed\uff0c\u660e\u660e\u662f\u4e0d\u80fd\u4e0b\u6765\uff0c\u8fd9\u53e5\u8bdd\u5374\u53c8\u600e\u80fd\u51fa\u53e3\uff1f\u53ea\u80c0\u7ea2\u4e86\u8138\u4e0d\u4f5c\u58f0\u3002\u9a6c\u6625\u82b1\u9053\uff1a\u201c\u4f60\u522b\u52a8\uff0c\u5c0f\u5fc3\u6454\u4e0b\u6765\u3002\u6211\u4e0a\u6765\u52a9\u4f60\u3002\u201d\u7eb5\u8eab\u8dc3\u9ad8\uff0c\u60f3\u8981\u62c9\u4f4f\u6811\u5e72\u6500\u4e0a\uff0c\u4f46\u90a3\u6811\u5e72\u751a\u9ad8\uff0c\u8fd9\u4e00\u8dc3\u6ca1\u80fd\u6293\u4f4f\uff0c\u5f53\u4e0b\u624b\u8db3\u5e76\u7528\uff0c\u4ece\u6811\u5e72\u722c\u4e0a\u6811\u53bb\u3002 \u3000\u3000\u722c\u5230\u6811\u5e72\u4e2d\u95f4\uff0c\u5ffd\u542c\u5f97\u9a6c\u8e44\u58f0\u54cd\uff0c\u4e00\u884c\u4eba\u81ea\u5317\u800c\u6765\u3002\u6b64\u65f6\u6668\u5149\u71b9\u5fae\uff0c\u5929\u5c06\u9ece\u660e\uff0c\u9a6c\u6625\u82b1\u5fc3\u9053\uff1a\u201c\u600e\u5730\u8fd9\u65e9\u5c31\u6709\u4eba\u8d76\u8def\uff1f\u201d\u8f6c\u77ac\u4e4b\u95f4\uff0c\u4e00\u884c\u4eba\u5df2\u6765\u5230\u6811\u4e0b\uff0c\u5171\u662f\u4eba\u9a6c\u4e5d\u4e58\u3002\u90a3\u4e5d\u4eba\u89c1\u4e00\u4e2a\u5927\u59d1\u5a18\u722c\u5728\u9ad8\u6811\u4e4b\u4e0a\uff0c\u90fd\u611f\u8be7\u5f02\uff0c\u52d2\u9a6c\u89c2\u770b\u3002\u9a6c\u6625\u82b1\u55d4\u9053\uff1a\u201c\u6709\u4ec0\u4e48\u597d\u77a7\u7684\uff1f\u8d70\u4f60\u4eec\u7684\u5427\uff01\u201d\u90a3\u4e5d\u4eba\u4e5f\u4e0d\u7406\u776c\uff0c\u518d\u770b\u5230\u6811\u9876\u7ed1\u7740\u4e00\u4e2a\u9752\u5e74\u7537\u5b50\uff0c\u66f4\u662f\u5947\u602a\u3002 \u3000\u3000\u9a6c\u6625\u82b1\u672a\u5230\u6811\u9876\uff0c\u63d0\u6c14\u4e0a\u8dc3\uff0c\u5de6\u624b\u5df2\u5728\u534a\u7a7a\u4e2d\u6293\u4f4f\u4e00\u6839\u6811\u679d\uff0c\u4e00\u62c9\u4e4b\u4e0b\uff0c\u501f\u52bf\u7ffb\u4e0a\uff0c\u7a9c\u5230\u4e86\u5546\u5b9d\u9707\u8eab\u65c1\u3002\u6811\u5e95\u4e0b\u4e24\u4e2a\u7537\u4eba\u9f50\u58f0\u559d\u91c7\uff1a\u201c\u597d\u4fca\u7684\u8f7b\u8eab\u529f\u592b\uff01\u201d\u9a6c\u6625\u82b1\u5c06\u5546\u5b9d\u9707\u624b\u811a\u4e0a\u7684\u5e03\u6761\u89e3\u5f00\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u6ca1\u53d7\u4f24\u4e48\uff1f\u201d\u5979\u8fd9\u53e5\u67d4\u58f0\u76f8\u8be2\uff0c\u5546\u5b9d\u9707\u542c\u4e86\u5927\u6170\uff0c\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\u3002\u201d\u62c9\u4f4f\u6811\u679d\u4e00\u8361\uff0c\u4ece\u6570\u4e08\u9ad8\u5904\u8f7b\u8f7b\u8dc3\u4e0b\u3002\u9a6c\u6625\u82b1\u8ddf\u7740\u4e0b\u6765\uff0c\u89c1\u9a6c\u4e0a\u4e5d\u4eba\u6307\u6307\u70b9\u70b9\uff0c\u8086\u65e0\u5fcc\u60ee\u7684\u597d\u751f\u65e0\u793c\uff0c\u4e0d\u7981\u5fc3\u4e0b\u607c\u6012\uff0c\u5411\u4ed6\u4eec\u6a2a\u4e86\u4e00\u773c\u3002 \u3000\u3000\u53ea\u89c1\u4e5d\u4eba\u6709\u8001\u6709\u5c11\uff0c\u8863\u9970\u90fd\u9887\u534e\u8d35\uff0c\u4e2a\u4e2a\u8170\u633a\u80cc\u76f4\uff0c\u8c6a\u5065\u527d\u608d\u3002\u53ea\u5c45\u4e2d\u4e00\u4f4d\u9752\u5e74\u516c\u5b50\u8138\u5982\u51a0\u7389\uff0c\u4e30\u795e\u4fca\u6717\uff0c\u5bb9\u6b62\u90fd\u96c5\uff0c\u7ea6\u83ab\u4e09\u5341\u4e8c\u4e09\u5c81\u5e74\u7eaa\uff0c\u8eab\u7a7f\u4e00\u4ef6\u5b9d\u84dd\u8272\u957f\u888d\uff0c\u5934\u6234\u74dc\u76ae\u5c0f\u5e3d\uff0c\u5e3d\u5b50\u6b63\u4e2d\u7f1d\u7740\u4e00\u5757\u5bf8\u8bb8\u89c1\u65b9\u7684\u7f8e\u7389\u3002\u9a6c\u6625\u82b1\u4ece\u5c0f\u5c31\u5728\u9556\u884c\uff0c\u81ea\u8bc6\u5f97\u73e0\u5b9d\uff0c\u4f46\u89c1\u76f8\u9694\u6570\u4e08\uff0c\u4ecd\u53ee\u770b\u5230\u90a3\u5757\u7f8e\u7389\u83b9\u7136\u751f\u5149\uff0c\u77e5\u9053\u5b9e\u662f\u4ef7\u503c\u8fde\u57ce\u7684\u5b9d\u7269\uff0c\u4ed6\u8fd9\u4e48\u968f\u968f\u4fbf\u4fbf\u5730\u7f1d\u5728\u5e3d\u4e0a\uff0c\u4e5f\u4e0d\u6015\u5931\u843d\uff0c\u5fc3\u4e2d\u597d\u5947\uff0c\u4e0d\u7531\u5f97\u5411\u4ed6\u591a\u671b\u4e86\u4e00\u773c\u3002 \u3000\u3000\u90a3\u516c\u5b50\u89c1\u5979\u660e\u8273\u7167\u4eba\uff0c\u8eab\u624b\u77eb\u6377\uff0c\u5fc3\u4e2d\u4e5f\u662f\u4e00\u52a8\uff0c\u5411\u8eab\u65c1\u4e00\u4e2a\u4e2d\u5e74\u6c49\u5b50\u4f4e\u58f0\u8bf4\u4e86\u51e0\u53e5\u3002\u90a3\u6c49\u5b50\u70b9\u70b9\u5934\uff0c\u7a81\u7136\u7eb5\u58f0\u5927\u7b11\uff0c\u9ad8\u58f0\u9053\uff1a\u201c\u4f60\u5c0f\u8d3c\u5b9a\u662f\u5077\u4e86\u4eba\u5bb6\u4e1c\u897f\uff0c\u7ed9\u9ad8\u9ad8\u540a\u5728\u6811\u4e0a\u3002\u201d\u4e00\u4e2a\u8001\u8005\u7b11\u9053\uff1a\u201c\u4f60\u8bf4\u5077\u4e86\u4ec0\u4e48\uff1f\u600e\u4e48\u4ed6\u59b9\u5b50\u53c8\u8fd9\u4e48\u5df4\u5df4\u5730\u6765\u6551\u4ed6\uff1f\u201d\u4ed6\u8bed\u5e26\u8f7b\u8584\uff0c\u795e\u8272\u751a\u662f\u6d6e\u6ed1\u3002 \u3000\u3000\u5546\u5b9d\u9707\u672c\u5df2\u6ee1\u8154\u6012\u706b\u96be\u4ee5\u53d1\u6cc4\uff0c\u542c\u4e86\u8fd9\u4e9b\u8a00\u8bed\uff0c\u7a81\u7136\u7eb5\u8eab\u4e0a\u53bb\uff0c\u62cd\u7684\u4e00\u58f0\uff0c\u6253\u4e86\u8fd9\u8001\u8005\u4e00\u4e2a\u8033\u5149\u3002\u90a3\u8001\u8005\u9a91\u5728\u9a6c\u4e0a\uff0c\u548c\u4ed6\u76f8\u9694\u4e08\u4f59\uff0c\u4ed6\u4e00\u8dc3\u4e4b\u95f4\u5c31\u6253\u5230\u4eba\u5bb6\u8033\u5149\uff0c\u5012\u4e5f\u5927\u51fa\u8bf8\u4eba\u610f\u6599\u4e4b\u5916\u3002\u4f17\u4eba\u4e0d\u81ea\u7981\u5730\u52d2\u9a6c\u9000\u540e\uff0c\u6115\u7136\u76f8\u987e\u3002 \u3000\u3000\u90a3\u8001\u8005\u4e0d\u63d0\u9632\u53d7\u8fb1\uff0c\u5982\u4f55\u5fcd\u5f97\u4e0b\u8fd9\u53e3\u6c14\uff1f\u7acb\u5373\u95ea\u8eab\u4e0b\u9a6c\uff0c\u4f38\u624b\u6765\u6293\u4ed6\u8863\u895f\u3002 \u3000\u3000\u5546\u5b9d\u9707\u53cd\u624b\u4e00\u52fe\uff0c\u62ff\u4ed6\u624b\u8155\u3002\u90a3\u8001\u8005\u4e5f\u662f\u8eab\u6709\u6b66\u529f\uff0c\u4ee5\u6293\u53d8\u638c\uff0c\u638c\u5e95\u7a7f\u62f3\u3002 \u3000\u3000\u4e8c\u4eba\u5728\u5927\u8def\u65c1\u6597\u4e86\u8d77\u6765\u3002 \u3000\u3000\u5546\u5b9d\u9707\u867d\u88ab\u80e1\u6590\u6253\u4e86\u4e00\u987f\uff0c\u5374\u4e5f\u6ca1\u4f24\u5230\u7b4b\u9aa8\uff0c\u4e00\u6765\u610f\u4e2d\u4eba\u5728\u65c1\u89c2\u6597\uff0c\u4e8c\u6765\u5c48\u6c14\u96be\u4f38\uff0c\u5c06\u5bb6\u4f20\u516b\u5366\u638c\u7edd\u827a\u65bd\u5c55\u51fa\u6765\uff0c\u8d8a\u6765\u8d8a\u72e0\u3002\u90a3\u8001\u8005\u4e00\u62db\u63a5\u4e0d\u4f4f\uff0c\u80a9\u5934\u4e2d\u638c\uff0c\u8e09\u8e09\u8dc4\u8dc4\u5730\u9000\u5f00\u51e0\u6b65\u3002\u4ed6\u4e00\u5b9a\u795e\u4f8d\u8981\u518d\u4e0a\uff0c\u9a6c\u4e0a\u4e00\u4eba\u53eb\u9053\uff1a\u201c\u8001\u5f20\u4f60\u9000\u4e0b\uff0c\u8fd9\u5c0f\u5b50\u6709\u70b9\u513f\u90aa\u95e8\u3002\u201d \u3000\u3000\u8bdd\u58f0\u752b\u6bd5\uff0c\u4e00\u4e2a\u4eba\u5f71\u8f7b\u98d8\u98d8\u5730\u4ece\u9a6c\u80cc\u4e0a\u8dc3\u4e86\u4e0b\u6765\u3002\u90a3\u8001\u8005\u5f53\u5373\u95ea\u5f00\u3002\u5546\u5b9d\u9707\u548c\u9a6c\u6625\u82b1\u89c1\u6b64\u4eba\u8eab\u624b\u4e86\u5f97\uff0c\u4e0d\u7981\u90fd\u7559\u4e0a\u4e86\u795e\u3002\u4f46\u89c1\u4ed6\u4e00\u5f20\u7d2b\u819b\u8138\uff0c\u795e\u6001\u5a01\u731b\uff0c\u8eab\u6750\u9b41\u68a7\uff0c\u7ad9\u7740\u6bd4\u5546\u5b9d\u9707\u8981\u9ad8\u51fa\u5927\u534a\u4e2a\u5934\u3002\u4ed6\u53cc\u624b\u8d1f\u5728\u80cc\u540e\uff0c\u5411\u5546\u5b9d\u9707\u6253\u91cf\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u662f\u516b\u5366\u95e8\u7684\u4e48\uff1f\u4f60\u5e08\u7236\u59d3\u891a\u8fd8\u662f\u59d3\u5546\uff1f\u201d\u4e00\u526f\u50b2\u6162\u7684\u795e\u8272\uff0c\u5168\u6ca1\u628a\u5bf9\u65b9\u653e\u5728\u773c\u91cc\u3002 \u3000\u3000\u5546\u5b9d\u9707\u5927\u6012\uff0c\u559d\u9053\uff1a\u201c\u4f60\u7ba1\u5f97\u7740\u4e48\uff1f\u201d\u90a3\u4eba\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5929\u4e0b\u53ea\u8981\u662f\u516b\u5366\u95e8\u7684\uff0c\u6211\u4eec\u5c31\u7ba1\u5f97\u7740\u3002\u201d\u5546\u5b9d\u9707\u4e3a\u4eba\u672c\u6765\u7cbe\u7ec6\uff0c\u4f46\u6b64\u65e5\u8fde\u53d7\u632b\u6298\uff0c\u76db\u6012\u4e4b\u4e0b\uff0c\u6ca1\u7ec6\u60f3\u4ed6\u8a00\u8bed\u4e2d\u7684\u542b\u610f\uff0c\u4e00\u62db\u201c\u5288\u96f7\u5760\u5730\u201d\uff0c\u5f80\u4ed6\u819d\u76d6\u4e0a\u51fb\u53bb\uff0c\u51fa\u624b\u751a\u662f\u8fc5\u75be\u3002 \u3000\u3000\u90a3\u4eba\u5fae\u5fae\u4e00\u7b11\uff0c\u53f3\u624b\u8f7b\u8f7b\u4e00\u6325\uff0c\u5411\u5de6\u8e0f\u4e86\u4e00\u6b65\uff0c\u767b\u65f6\u5c06\u4ed6\u8fd9\u4e00\u51fb\u5316\u89e3\u4e86\u3002 \u3000\u3000\u5546\u5b9d\u9707\u7684\u201c\u6e38\u8eab\u516b\u5366\u638c\u201d\u4e00\u65bd\u51fa\uff0c\u518d\u4e0d\u505c\u7559\uff0c\u811a\u4e0b\u6bcf\u4e00\u6b65\u90fd\u6309\u7740\u5148\u5929\u516b\u5366\u7684\u56fe\u5f0f\uff0c\u8f6c\u6298\u5982\u610f\uff0c\u56db\u68a2\u5f52\u4e00\uff0c\u7ed5\u7740\u5bf9\u65b9\u8eab\u5b50\u6025\u901f\u5954\u8dd1\uff0c\u4e00\u638c\u4e00\u638c\u8d8a\u6253\u8d8a\u5feb\u3002 \u3000\u3000\u90a3\u5927\u6c49\u53cc\u624b\u51fa\u62db\u6781\u77ed\uff0c\u53ea\u662f\u6bd4\u7740\u62db\u5f0f\uff0c\u59cb\u7ec8\u4e0d\u4e0e\u5546\u5b9d\u9707\u624b\u638c\u76f8\u89e6\uff0c\u4f46\u4ed6\u6240\u51fa\u7684\u6bcf\u4e00\u62db\uff0c\u5374\u65e0\u4e00\u4e0d\u662f\u5546\u5b9d\u9707\u638c\u6cd5\u7684\u514b\u661f\uff0c\u5f80\u5f80\u4f7f\u5546\u5b9d\u9707\u62db\u5f0f\u672a\u66fe\u4f7f\u5168\uff0c\u4fbf\u8feb\u5f97\u6536\u638c\u53d8\u52bf\u3002\u970e\u65f6\u4e4b\u95f4\uff0c\u5546\u5b9d\u9707\u6253\u51fa\u4e86\u56db\u5341\u4f59\u638c\uff0c\u7adf\u6ca1\u4e00\u638c\u5e26\u5230\u4ed6\u4e00\u70b9\u8863\u89d2\u3002\u65c1\u89c2\u4f17\u4eba\u89c1\u90a3\u5927\u6c49\u5982\u6b64\u4e86\u5f97\uff0c\u65e0\u4e0d\u8d5e\u670d\u3002 \u3000\u3000\u5546\u5b9d\u9707\u7126\u8e81\u8d77\u6765\uff0c\u5954\u8dd1\u66f4\u901f\uff0c\u638c\u6cd5\u50ac\u7d27\u3002\u90a3\u5927\u6c49\u4ecd\u7136\u597d\u6574\u4ee5\u6687\uff0c\u9762\u9732\u5fae\u7b11\uff0c\u53cc\u638c\u6216\u6325\u6216\u6309\uff0c\u4fbf\u5982\u662f\u72ec\u4e2a\u513f\u7ec3\u62f3\u4e00\u822c\u3002\u6b64\u65f6\u5546\u5b9d\u9707\u5df2\u7136\u77a7\u51fa\uff0c\u5bf9\u65b9\u51fa\u62db\u867d\u7136\u6781\u77ed\uff0c\u811a\u4e0b\u5374\u4e5f\u6309\u7740\u5148\u5929\u516b\u5366\u7684\u56fe\u5f0f\uff0c\u65b9\u4f4d\u4e1d\u6beb\u4e0d\u4e71\u3002\u4ed6\u66fe\u542c\u6bcd\u4eb2\u8bf4\u8fc7\uff0c\u516b\u5366\u95e8\u4e2d\u6709\u4e00\u9879\u6781\u7cbe\u6df1\u7684\u201c\u5185\u516b\u5366\u529f\u592b\u201d\uff0c\u975e\u5c06\u5916\u516b\u5366\u7ec3\u81f3\u767b\u5cf0\u9020\u6781\uff0c\u51b3\u4e0d\u80fd\u52a8\uff0c\u4f46\u53ea\u8981\u4e00\u7ec3\u6210\uff0c\u90a3\u65f6\u4ee5\u9759\u5236\u52a8\uff0c\u514b\u654c\u673a\u5148\uff0c\u5dee\u4e0d\u591a\u5c31\u662f\u65e0\u654c\u4e8e\u5929\u4e0b\u4e86\u3002\u773c\u524d\u6b64\u4eba\u660e\u660e\u662f\u8ba9\u7740\u81ea\u5df1\uff0c\u53ea\u8981\u4ed6\u5f53\u771f\u4e00\u51fa\u624b\uff0c\u4e00\u62db\u4e4b\u95f4\u5c31\u80fd\u5c06\u81ea\u5df1\u6253\u5012\u3002\u4ed6\u8d8a\u60f3\u8d8a\u662f\u60f6\u6050\uff0c\u7a81\u7136\u5411\u540e\u8dc3\u5f00\uff0c\u62b1\u62f3\u8bf4\u9053\uff1a\u201c\u665a\u8f88\u6709\u773c\u4e0d\u8bc6\u6cf0\u5c71\uff0c\u539f\u6765\u662f\u672c\u95e8\u524d\u8f88\u5230\u4e86\uff01\u201d \u3000\u3000\u90a3\u4eba\u5fae\u5fae\u4e00\u7b11\uff0c\u4ecd\u7136\u95ee\u9053\uff1a\u201c\u4f60\u5e08\u7236\u59d3\u891a\u8fd8\u662f\u59d3\u5546\uff1f\u201d\u5546\u5b9d\u9707\u66fe\u5f97\u6bcd\u4eb2\u5631\u5490\uff0c\u5728\u4eba\u524d\u5343\u4e07\u4e0d\u53ef\u5410\u9732\u8eab\u5206\uff0c\u4ee5\u9632\u5bf9\u5934\u77e5\u6089\uff0c\u96be\u9042\u62a5\u4ec7\u5927\u4e8b\uff0c\u4e0d\u7981\u8e0c\u8e87\u4e0d\u7b54\u3002\u90a3\u4eba\u7b11\u9053\uff1a\u201c\u4f60\u638c\u6cd5\u95e8\u6237\u5f00\u9614\uff0c\u77a7\u6765\u662f\u5546\u5251\u9e23\u5e08\u5144\u4e00\u6d3e\u4e86\u3002\u5927\u54e5\uff0c\u4f60\u8bf4\u662f\u4e0d\u662f\uff1f\u201d\u6700\u540e\u4e00\u53e5\u8bdd\u662f\u5411\u9a6c\u4e0a\u4e00\u4e2a\u8001\u8005\u800c\u8bf4\u3002 \u3000\u3000\u90a3\u8001\u8005\u5e74\u8fd1\u4e94\u5341\uff0c\u7ffb\u8eab\u4e0b\u9a6c\uff0c\u5411\u5546\u5b9d\u9707\u9053\uff1a\u201c\u4f60\u5e08\u7236\u5462\uff1f\u5f15\u6211\u4eec\u53bb\u89c1\u89c1\u3002 \u3000\u3000\u6211\u662f\u4f60\u738b\u5e08\u4f2f\uff0c\u8fd9\u4f4d\u662f\u6211\u5144\u5f1f\uff0c\u4f60\u62dc\u5e08\u53d4\u5427\u3002\u201d\u8bf4\u7740\u54c8\u54c8\u5927\u7b11\u3002 \u3000\u3000\u5546\u5b9d\u9707\u77e5\u9053\u7236\u4eb2\u7684\u5e08\u7236\u662f\u5a01\u9707\u6cb3\u6714\u738b\u7ef4\u626c\uff0c\u4e43\u662f\u5317\u4eac\u9547\u8fdc\u9556\u5c40\u7684\u603b\u9556\u5934\uff0c\u773c\u524d\u8fd9\u4eba\u81ea\u79f0\u59d3\u738b\uff0c\u53c8\u662f\u516b\u5366\u95e8\u7684\u9ad8\u624b\uff0c\u770b\u6765\u662f\u81ea\u5df1\u5e08\u4f2f\u3001\u5e08\u53d4\uff0c\u5b9a\u7136\u4e0d\u5047\u7684\u4e86\u3002\u4f46\u4ed6\u751f\u6027\u7cbe\u7ec6\uff0c\u52a0\u95ee\u4e00\u53e5\uff1a\u201c\u4e24\u4f4d\u8ddf\u5a01\u9707\u6cb3\u6714\u738b\u8001\u9556\u5934\u662f\u600e\u751f\u79f0\u547c\uff1f\u201d\u738b\u6c0f\u5144\u5f1f\u76f8\u987e\u4e00\u7b11\u3002\u90a3\u8001\u8005\u9053\uff1a\u201c\u90a3\u662f\u54b1\u54e5\u513f\u4fe9\u7684\u5148\u7236\u3002\u4f60\u8fd8\u4e0d\u4fe1\u4e48\uff1f \u3000\u3000\u5546\u5e08\u5f1f\u5462\uff1f\u201d \u3000\u3000\u5546\u5b9d\u9707\u66f4\u65e0\u8fdf\u7591\uff0c\u6251\u5012\u5728\u5730\uff0c\u78d5\u4e86\u51e0\u4e2a\u5934\uff0c\u53e3\u79f0\u5e08\u4f2f\u5e08\u53d4\uff0c\u8bf4\u9053\uff1a\u201c\u5148\u7236\u65e9\u5df2\u53bb\u4e16\uff0c\u5e08\u4f2f\u5e08\u53d4\u5f53\u5e74\u6ca1\u63a5\u5230\u8ba3\u544a\u4e48\uff1f\u201d \u3000\u3000\u90a3\u5e74\u8001\u7684\u6b66\u5e08\u540d\u53eb\u738b\u5251\u82f1\uff0c\u4ed6\u5144\u5f1f\u540d\u53eb\u738b\u5251\u6770\uff0c\u90fd\u662f\u738b\u7ef4\u626c\u7684\u513f\u5b50\u3002\u738b\u7ef4\u626c\u5f53\u5e74\u51ed\u4e00\u5bf9\u516b\u5366\u638c\u3001\u4e00\u628a\u516b\u5366\u5200\u5a01\u9707\u6c5f\u6e56\u7eff\u6797\u3002\u9ed1\u9053\u4e2d\u6709\u4e00\u53e5\u8bdd\u9053\uff1a\u201c\u5b81\u78b0\u960e\u738b\uff0c\u83ab\u78b0\u8001\u738b\u201d\uff0c\u7aef\u7684\u662f\u540d\u626c\u5929\u4e0b\uff0c\u73b0\u65f6\u65e9\u5df2\u901d\u4e16\u591a\u5e74\u3002 \u3000\u3000\u5546\u5251\u9e23\u867d\u662f\u4ed6\u7684\u95e8\u4e0b\uff0c\u4f46\u5e08\u5f92\u95f4\u60c5\u8c0a\u751a\u662f\u5e73\u5e38\uff0c\u79bb\u5e08\u95e8\u540e\u5c11\u901a\u97f3\u95ee\u3002\u738b\u6c0f\u5144\u5f1f\u53c8\u5728\u5b98\u5e9c\u5f53\u5dee\uff0c\u9752\u4e91\u5f97\u610f\uff0c\u4ece\u6765\u5c31\u6ca1\u5c06\u8fd9\u4e2a\u8eab\u5728\u8349\u91ce\u7684\u540c\u95e8\u5e08\u5144\u5f1f\u653e\u5728\u5fc3\u4e0a\u3002\u56e0\u6b64\u5c71\u4e1c\u548c\u5317\u4eac\u867d\u76f8\u9694\u4e0d\u8fdc\uff0c\u5546\u5251\u9e23\u901d\u4e16\u7684\u8baf\u606f\u738b\u6c0f\u5144\u5f1f\u7adf\u7136\u4e0d\u77e5\u3002 \u3000\u3000\u5f53\u4e0b\u738b\u5251\u82f1\u53f9\u4e86\u53e3\u6c14\uff0c\u56de\u8eab\u5411\u90a3\u9752\u5e74\u516c\u5b50\u4f4e\u58f0\u8bf4\u4e86\u51e0\u53e5\u8bdd\u3002\u90a3\u516c\u5b50\u773c\u89d2\u5411\u9a6c\u6625\u82b1\u659c\u7768\u4e00\u773c\uff0c\u6b22\u7136\u70b9\u5934\u3002\u738b\u5251\u82f1\u5411\u5546\u5b9d\u9707\u9053\uff1a\u201c\u4f60\u5bb6\u4f4f\u6b64\u4e0d\u8fdc\u5427\uff1f\u4f60\u5e26\u6211\u5144\u5f1f\u5230\u4f60\u7236\u4eb2\u7075\u524d\u4e00\u796d\u3002\u6211\u4eec\u5e08\u5144\u5f1f\u4e00\u522b\u4e8c\u5341\u4f59\u5e74\uff0c\u60f3\u4e0d\u5230\u518d\u65e0\u76f8\u89c1\u4e4b\u671f\u3002\u201d\u4ed6\u987f\u4e86\u4e00\u987f\uff0c\u4f38\u624b\u5411\u90a3\u516c\u5b50\u4e00\u5f20\uff0c\u9053\uff1a\u201c\u4f60\u6765\u62dc\u89c1\u798f\u516c\u5b50\uff0c\u6211\u4eec\u90fd\u5728\u516c\u5b50\u624b\u4e0b\u5f53\u5dee\u3002\u201d \u3000\u3000\u5546\u5b9d\u9707\u89c1\u90a3\u516c\u5b50\u6c14\u5ea6\u9ad8\u534e\uff0c\u60f3\u662f\u4eac\u4e2d\u7684\u8d35\u4ecb\u516c\u5b50\uff0c\u8fd9\u624d\u6536\u5f97\u738b\u6c0f\u5144\u5f1f\u8fd9\u7b49\u8c6a\u6770\u66ff\u4ed6\u5f53\u5dee\uff0c\u5f53\u4e0b\u4e0a\u524d\u8eac\u8eab\u4e0b\u62dc\u3002\u798f\u516c\u5b50\u53ea\u6446\u6446\u624b\uff0c\u8bf4\u58f0\uff1a\u201c\u8bf7\u8d77\uff01\u201d \u3000\u3000\u5374\u4e0d\u56de\u793c\u3002\u5546\u5b9d\u9707\u5fc3\u4e2d\u5fae\u5fae\u6709\u6c14\uff1a\u201c\u597d\u5927\u7684\u67b6\u5b50\uff01\u4f60\u5f53\u771f\u662f\u7687\u5e1d\u8001\u5b50\u4e0d\u6210\uff1f\u201d \u3000\u3000\u4e00\u884c\u4eba\u6765\u5230\u5546\u5bb6\u5821\u65f6\uff0c\u5821\u4e2d\u5df2\u53d1\u89c9\u80e1\u6590\u9003\u8d70\uff0c\u6b63\u5728\u5230\u5904\u627e\u5bfb\u3002\u5546\u5b9d\u9707\u5165\u5185\u62a5\u8baf\uff0c\u5546\u8001\u592a\u542c\u8bf4\u5148\u592b\u7684\u540c\u95e8\u5144\u5f1f\u6765\u5230\uff0c\u53c8\u60ca\u53c8\u559c\uff0c\u6025\u5fd9\u51fa\u8fce\uff0c\u5c06\u80e1\u6590\u7684\u4e8b\u629b\u5728\u4e00\u65c1\u3002 \u3000\u3000\u738b\u5251\u82f1\u7ed9\u5546\u8001\u592a\u5f15\u89c1\u3002\u539f\u6765\u8fd9\u4e5d\u4eba\u4e4b\u4e2d\uff0c\u5012\u6709\u4e94\u4e2a\u662f\u6b66\u6797\u4e2d\u7684\u4e00\u6d41\u9ad8\u624b\uff0c\u9664\u738b\u6c0f\u5144\u5f1f\u5916\uff0c\u8fd8\u6709\u592a\u6781\u95e8\u7684\u9648\u79b9\uff0c\u5c11\u6797\u6d3e\u7684\u53e4\u822c\u82e5\uff0c\u5929\u9f99\u95e8\u5357\u5b97\u7684\u6bb7\u4ef2\u7fd4\u3002 \u3000\u3000\u9648\u79b9\u548c\u6bb7\u4ef2\u7fd4\u5728\u6c5f\u6e56\u4e0a\u540d\u58f0\u65e9\u663e\uff0c\u53e4\u822c\u82e5\u5e74\u7eaa\u8f7b\u4e9b\uff0c\u4f46\u89c1\u4ed6\u53cc\u76ee\u6709\u795e\uff0c\u4f38\u51fa\u624b\u6765\u5e72\u5982\u67af\u6728\uff0c\u624b\u6307\u575a\u633a\uff0c\u5b9a\u662f\u5916\u5bb6\u7684\u4e00\u628a\u597d\u624b\u3002\u5176\u4f59\u4e09\u4eba\u662f\u798f\u516c\u5b50\u7684\u4eb2\u968f\u4f8d\u4ec6\uff0c\u90a3\u53d7\u4e86\u5546\u5b9d\u9707\u6bb4\u51fb\u7684\u8001\u8005\u59d3\u5f20\uff0c\u5927\u5bb6\u53eb\u4ed6\u505a\u5f20\u603b\u7ba1\uff0c\u81ea\u662f\u798f\u516c\u5b50\u5e9c\u4e2d\u6709\u6743\u52bf\u7684\u4eba\u7269\u4e86\u3002 \u3000\u3000\u81f3\u4e8e\u798f\u516c\u5b50\u662f\u4ec0\u4e48\u8eab\u5206\uff0c\u738b\u5251\u82f1\u5374\u4e00\u53e5\u4e0d\u63d0\uff0c\u53ea\u662f\u79f0\u4ed6\u4e3a\u201c\u798f\u516c\u5b50\u201d\u3002 \u3000\u3000\u738b\u5251\u82f1\u3001\u5251\u6770\u5144\u5f1f\u95ee\u8d77\u5546\u5251\u9e23\u7684\u6b7b\u56e0\u3002\u5546\u8001\u592a\u505a\u5fc3\u6781\u76db\uff0c\u4e0d\u80af\u8bf4\u662f\u80e1\u4e00\u5200\u6240\u6740\uff0c\u53ea\u662f\u8bf4\u5f97\u75c5\u8eab\u4ea1\u3002\u5979\u51b3\u610f\u8981\u548c\u513f\u5b50\u4e00\u540c\u4eb2\u5203\u4ec7\u4eba\uff0c\u51b3\u4e0d\u80af\u5047\u624b\u65c1\u4eba\u590d\u4ec7\u3002 \u3000\u3000\u9a6c\u6625\u82b1\u89c1\u5546\u8001\u592a\u3001\u5546\u5b9d\u9707\u7b49\u540c\u95e8\u53d9\u8bdd\uff0c\u56de\u5230\u5c4b\u91cc\uff0c\u5c06\u9002\u624d\u7684\u89c1\u95fb\u5411\u7236\u4eb2\u8bf4\u4e86\u3002\u9a6c\u884c\u7a7a\u542c\u8bf4\u90a3\u80e1\u6590\u7adf\u662f\u5927\u4fa0\u80e1\u4e00\u5200\u7684\u513f\u5b50\uff0c\u5927\u662f\u60ca\u8bb6\uff0c\u4f46\u542c\u8fd9\u5c0f\u5c0f\u5b69\u7ae5\u7684\u6b66\u529f\u7adf\u80dc\u8fc7\u5546\u5b9d\u9707\uff0c\u5374\u662f\u534a\u4fe1\u534a\u7591\u3002\u5f90\u94ee\u5728\u65c1\u9ed8\u9ed8\u542c\u7740\uff0c\u8138\u4e0a\u9752\u4e00\u9635\u3001\u7ea2\u4e00\u9635\uff0c\u5e76\u4e0d\u63d2\u5634\u3002 \u3000\u3000\u7236\u5973\u4fe9\u8bf4\u4e86\u4e00\u9635\u5b50\u8bdd\uff0c\u9a6c\u6625\u82b1\u56de\u5230\u81ea\u5df1\u623f\u91cc\u3002\u5f90\u94ee\u8ddf\u4e86\u51fa\u6765\uff0c\u53eb\u58f0\uff1a\u201c\u5e08\u59b9\uff01\u201d\u9a6c\u6625\u82b1\u8138\u4e0a\u4e00\u7ea2\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u201d\u5f90\u6323\u89c1\u5979\u8138\u82e5\u671d\u971e\uff0c\u5fc3\u4e2d\u60c5\u52a8\uff0c\u5c06\u672c\u6765\u8981\u95ee\u7684\u8bdd\u6309\u637a\u4e86\u4e0d\u8bf4\uff0c\u4f38\u624b\u53bb\u62c9\u5979\u7684\u624b\u3002\u9a6c\u6625\u82b1\u5c06\u624b\u6454\u8131\uff0c\u55d4\u9053\uff1a\u201c\u7ed9\u4eba\u5bb6\u77a7\u89c1\u4e86\uff0c\u600e\u597d\u610f\u601d\uff1f\u201d \u3000\u3000\u5f90\u94ee\u7ec8\u4e8e\u6c89\u4e0d\u4f4f\u6c14\uff0c\u6124\u7136\u9053\uff1a\u201c\u54fc\uff0c\u4e0d\u597d\u610f\u601d\uff01\u4f60\u534a\u591c\u4e09\u66f4\uff0c\u8ddf\u90a3\u59d3\u5546\u7684\u5c0f\u5b50\u5230\u5916\u9762\u53bb\uff0c\u9b3c\u9b3c\u795f\u795f\u7684\u5e72\u4ec0\u4e48\u4e86\uff1f\u201d\u9a6c\u6625\u82b1\u4e00\u6014\uff0c\u542c\u4ed6\u8bed\u610f\u4e0d\u5584\uff0c\u6012\u9053\uff1a\u201c\u4f60\u95ee\u8fd9\u6d3b\u662f\u4ec0\u4e48\u7528\u610f\uff1f\u201d\u5f90\u94ee\u9053\uff1a\u201c\u4f60\u8ddf\u90a3\u5c0f\u5b50\u51fa\u53bb\u662f\u4ec0\u4e48\u7528\u610f\uff0c\u6211\u95ee\u8fd9\u8bdd\u5c31\u662f\u4ec0\u4e48\u7528\u610f\u3002\u201d \u3000\u3000\u4ed6\u5bf9\u5e08\u59b9\u5411\u6765\u4f53\u8d34\u8ba8\u597d\uff0c\u4f46\u4eca\u65e5\u4e00\u65e9\u89c1\u5979\u4e0e\u5546\u5b9d\u9707\u4ece\u5916\u9762\u56de\u6765\uff0c\u542c\u5979\u8a00\u4e2d\u53d9\u8ff0\uff0c\u53c8\u662f\u534a\u591c\u91cc\u5728\u5916\u9762\u9047\u5230\u80e1\u6590\uff0c\u81ea\u662f\u918b\u610f\u5927\u76db\uff0c\u54ea\u60f3\u5f97\u5230\u5979\u662f\u6015\u7236\u4eb2\u8d23\u602a\uff0c\u5c06\u6c42\u5546\u5b9d\u9707\u91ca\u653e\u80e1\u6590\u4e4b\u4e8b\u7792\u8fc7\u4e86\u4e0d\u8bf4\u3002\u9a6c\u884c\u7a7a\u90a3\u665a\u9694\u7a97\u542c\u5230\u5546\u8001\u592a\u6bcd\u5b50\u5bf9\u7b54\uff0c\u5f97\u77e5\u5546\u5b9d\u9707\u770b\u4e2d\u81ea\u5df1\u5973\u513f\uff0c\u8fd8\u9053\u4ed6\u4e8c\u4eba\u786e\u6709\u79c1\u60c5\uff0c\u591c\u4e2d\u76f8\u4f1a\uff0c\u788d\u7740\u5f92\u513f\u5728\u65c1\uff0c\u4e0d\u4fbf\u8ffd\u95ee\u3002\u4f46\u5f90\u6323\u542c\u6765\uff0c\u5fc3\u4e2d\u9178\u6e9c\u6e9c\u7684\u6ee1\u4e0d\u662f\u5473\u513f\u3002\u4ed6\u751f\u6027\u5364\u83bd\uff0c\u6b64\u65f6\u5e08\u59b9\u53c8\u6210\u4e86\u4ed6\u672a\u8fc7\u95e8\u7684\u59bb\u5b50\uff0c\u4e0d\u7981\u75be\u8a00\u5389\u8272\u5730\u8ffd\u95ee\u8d77\u6765\u3002 \u3000\u3000\u9a6c\u6625\u82b1\u95ee\u5fc3\u65e0\u6127\uff0c\u8fd9\u5e08\u54e5\u5bf9\u81ea\u5df1\u53c8\u7d20\u6765\u4f9d\u987a\u5bb9\u8ba9\uff0c\u60f3\u4e0d\u5230\u6628\u5929\u7236\u4eb2\u521a\u628a\u81ea\u5df1\u7ec8\u8eab\u76f8\u8bb8\uff0c\u4ed6\u5c31\u8fd9\u4e48\u5f3a\u6a2a\u9738\u9053\u8d77\u6765\uff0c\u65e5\u540e\u6210\u4e86\u592b\u59bb\uff0c\u5c82\u975e\u6574\u65e5\u53d7\u4ed6\u6b3a\u8fb1\uff1f \u3000\u3000\u672c\u6765\u8fd9\u4ef6\u4e8b\u5979\u53ea\u8981\u76f4\u8a00\u76f8\u544a\uff0c\u5f90\u6323\u4e00\u7ecf\u660e\u767d\uff0c\u81ea\u65e0\u8bdd\u8bf4\u3002\u4f46\u5979\u8d4c\u6c14\u504f\u504f\u4e0d\u8bf4\uff0c\u9053\uff1a\u201c\u6211\u7231\u8ddf\u8c01\u5077\u5077\u51fa\u53bb\uff0c\u5c31\u8ddf\u8c01\u51fa\u53bb\uff0c\u4f60\u7ba1\u5f97\u7740\u4e48\uff1f\u201d \u3000\u3000\u4e00\u4e2a\u4eba\u5992\u610f\u4e00\u8d77\uff0c\u518d\u65e0\u7406\u6027\uff0c\u5f90\u94ee\u6ee1\u8138\u80c0\u5f97\u901a\u7ea2\uff0c\u8fde\u8116\u5b50\u4e5f\uff0c\u7c97\u4e86\uff0c\u5927\u58f0\u9053\uff1a\u201c\u4ece\u524d\u6211\u7ba1\u4e0d\u7740\uff0c\u4eca\u513f\u5c31\u7ba1\u5f97\u7740\u3002\u201d\u9a6c\u6625\u82b1\u6c14\u5f97\u6d41\u4e0b\u6cea\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u73b0\u4e0b\u4f60\u5df2\u8fd9\u6837\u4e86\uff0c\u5c06\u6765\u8fd8\u6307\u671b\u4f60\u5f85\u6211\u597d\u5417\uff1f\u201d\u5f90\u94ee\u89c1\u5979\u6d41\u6cea\uff0c\u5fc3\u4e2d\u53c8\u662f\u8f6f\u4e86\uff0c\u4f46\u60f3\u5230\u5979\u548c\u5546\u5b9d\u9707\u6df1\u5bb5\u51fa\u5916\u5e7d\u4f1a\uff0c\u4e00\u53e3\u6c14\u600e\u54bd\u5f97\u4e0b\u53bb\uff1f\u5927\u58f0\u9053\uff1a\u201c\u4f60\u51fa\u53bb\u5230\u5e95\u5e72\u4ec0\u4e48\u6765\u7740\uff1f\u4f60\u8bf4\uff0c\u4f60\u8bf4\uff01\u201d\u9a6c\u6625\u82b1\u5fc3\u9053\uff1a\u201d\u4f60\u8d8a\u662f\u6a2a\u86ee\uff0c\u6211\u8d8a\u662f\u4e0d\u8bf4\u3002\u201d \u3000\u3000\u5c31\u5728\u6b64\u65f6\uff0c\u5546\u5b9d\u9707\u5949\u6bcd\u4eb2\u4e4b\u547d\uff0c\u8fc7\u6765\u8bf7\u9a6c\u884c\u7a7a\u53bb\u548c\u738b\u6c0f\u5144\u5f1f\u7b49\u53ae\u89c1\uff0c\u53ea\u89c1\u5f90\u950b\u548c\u9a6c\u6625\u82b1\u5728\u5eca\u4e0b\u5927\u58f0\u4e89\u95f9\uff0c\u4e0d\u7531\u5f97\u505c\u4e86\u811a\u6b65\u3002\u5f90\u94ee\u65e9\u662f\u4e00\u809a\u5b50\u706b\uff0c\u6ee1\u5fc3\u60f3\u6253\u672a\u5a5a\u59bb\u5b50\u4e00\u4e2a\u8033\u62ec\u5b50\uff0c\u5374\u53c8\u672a\u6562\uff0c\u773c\u89c1\u5546\u5b9d\u9707\u8fc7\u6765\uff0c\u6b63\u5408\u5fc3\u610f\uff0c\u9a82\u9053\uff1a\u201c\u6211\u6253\u4f60\u8fd9\u4e2a\u72d7\u5a18\u517b\u7684\u5c0f\u5b50\uff01\u201d\u51b2\u4e0a\u53bb\u5c31\u662f\u4e00\u62f3\u3002\u5546\u5b9d\u9707\u4e00\u8ba9\uff0c\u6115\u7136\u9053\uff1a\u201c\u4f60\u5e72\u4ec0\u4e48\uff1f\u201d\u5f90\u94ee\u8ddf\u7740\u53c8\u662f\u4e00\u62f3\uff0c\u5546\u5b9d\u9707\u6765\u4e0d\u53ca\u95ea\u8ba9\uff0c\u7ed9\u4ed6\u4e00\u62f3\u6b63\u4e2d\u80f8\u53e3\uff0c\u5f85\u4ed6\u7b2c\u4e09\u62f3\u6253\u6765\u65f6\uff0c\u56de\u638c\u76f8\u683c\u3002\u4e24\u4eba\u4fbf\u5728\u5eca\u4e0b\u52a8\u8d77\u624b\u6765\u3002 \u3000\u3000\u9a6c\u6625\u82b1\u6ee1\u8179\u6028\u6012\uff0c\u5e76\u4e0d\u7406\u4ed6\u4e8c\u4eba\u6253\u5f97\u5982\u4f55\uff0c\u4e00\u626d\u5934\u7adf\u81ea\u8d70\u4e86\u3002\u56de\u5230\u623f\u91cc\u54ed\u4e86\u4e00\u573a\uff0c\u5a62\u5973\u6765\u53eb\u5403\u996d\uff0c\u5979\u4e5f\u4e0d\u7406\u4f1a\uff0c\u8ff7\u8ff7\u7cca\u7cca\u5730\u4fbf\u7761\u7740\u4e86\u3002\u4e00\u89c9\u9192\u6765\uff0c\u5df2\u662f\u508d\u665a\u65f6\u5206\uff0c\u4fe1\u6b65\u8d70\u5230\u540e\u82b1\u56ed\u4e2d\uff0c\u5750\u5728\u77f3\u51f3\u4e0a\u5446\u5446\u51fa\u795e\uff0c\u5fc3\u4e2d\u53ea\u662f\u60f3\uff1a\u201c\u96be\u9053\u6211\u7684\u7ec8\u8eab\uff0c\u5c31\u7b97\u8fd9\u4e48\u8bb8\u7ed9\u4e86\u8fd9\u86ee\u4e0d\u8bb2\u7406\u7684\u5e08\u5144\u4e48\uff1f\u7239\u7239\u8fd8\u5728\u8eab\u8fb9\uff0c\u4ed6\u5c31\u5bf9\u6211\u8fd9\u4e48\u51f6\u72e0\uff0c\u65e5\u540e\u4e0d\u77e5\u66f4\u8981\u5f85\u6211\u600e\u6837\uff1f\u201d\u4e0d\u7531\u5f97\u6014\u6014\u5730\u6389\u4e0b\u6cea\u6765\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u5750\u4e86\u591a\u5c11\u65f6\u5019\uff0c\u5ffd\u542c\u5f97\u8427\u58f0\u5e7d\u54bd\uff0c\u4ece\u82b1\u4e1b\u5916\u4f20\u51fa\u3002\u9a6c\u6625\u82b1\u6b63\u81ea\u96be\u53d7\uff0c\u8fd9\u8427\u58f0\u5374\u5982\u6709\u4eba\u5728\u67d4\u58f0\u76f8\u6170\uff0c\u7ec6\u8bed\u503e\u8bc9\uff0c\u542c\u4e86\u53c8\u89c9\u4f24\u5fc3\uff0c\u53c8\u662f\u6b22\u559c\uff0c\u4e0d\u7531\u5f97\u5c31\u50cf\u559d\u9189\u4e86\u9152\u4e00\u822c\u8ff7\u8ff7\u7cca\u7cca\u3002\u5979\u542c\u4e86\u4e00\u9635\uff0c\u8d8a\u542c\u8d8a\u662f\u51fa\u795e\uff0c\u7ad9\u8d77\u8eab\u6765\u5411\u82b1\u4e1b\u5916\u8d70\u51fa\uff0c\u53ea\u89c1\u6d77\u68e0\u6811\u4e0b\u5750\u7740\u4e00\u4e2a\u84dd\u886b\u7537\u5b50\uff0c\u624b\u6301\u7389\u8427\u5439\u594f\uff0c\u624b\u81ea\u5982\u7389\uff0c\u548c\u7389\u8427\u989c\u8272\u96be\u5206\uff0c\u6b63\u662f\u6668\u95f4\u6240\u9047\u5230\u7684\u798f\u516c\u5b50\u3002 \u3000\u3000\u798f\u516c\u5b50\u542b\u7b11\u70b9\u9996\uff0c\u793a\u610f\u8981\u5979\u8fc7\u53bb\uff0c\u8427\u58f0\u4ecd\u662f\u4e0d\u505c\u3002\u4ed6\u795e\u6001\u4e4b\u4e2d\uff0c\u81ea\u6709\u4e00\u80a1\u5a01\u4e25\uff0c\u4e00\u80a1\u5f15\u529b\uff0c\u76f4\u662f\u53eb\u4eba\u6297\u62d2\u4e0d\u5f97\u3002\u9a6c\u6625\u82b1\u7ea2\u7740\u8138\u513f\uff0c\u6162\u6162\u8d70\u8fd1\uff0c\u4f46\u542c\u8427\u58f0\u7f20\u7ef5\u5a49\u8f6c\uff0c\u4e00\u58f0\u58f0\u90fd\u662f\u60c5\u8bdd\uff0c\u7981\u4e0d\u5f97\u5fc3\u795e\u8361\u6f3e\u3002 \u3000\u3000\u9a6c\u6625\u82b1\u968f\u624b\u4ece\u8eab\u65c1\u73ab\u7470\u4e1b\u4e0a\u6458\u4e0b\u6735\u82b1\u513f\uff0c\u653e\u5728\u9f3b\u8fb9\u55c5\u4e86\u55c5\u3002\u8427\u58f0\u82b1\u9999\uff0c\u5915\u9633\u9ec4\u660f\uff0c\u773c\u524d\u662f\u8fd9\u4e48\u4e00\u4e2a\u4fca\u96c5\u7f8e\u79c0\u7684\u9752\u5e74\u7537\u5b50\uff0c\u773c\u4e2d\u9732\u51fa\u6765\u7684\u795e\u8272\u53c8\u662f\u6e29\u67d4\uff0c\u53c8\u662f\u9ad8\u8d35\u3002 \u3000\u3000\u5979\u84e6\u5730\u91cc\u60f3\u5230\u4e86\u5f90\u94ee\uff0c\u4ed6\u662f\u8fd9\u4e48\u7684\u7c97\u9c81\uff0c\u8fd9\u4e48\u7684\u4f1a\u559d\u5e72\u918b\uff0c\u548c\u773c\u524d\u8fd9\u8d35\u516c\u5b50\u76f8\u6bd4\uff0c\u771f\u662f\u4e00\u4e2a\u5728\u5929\u4e0a\uff0c\u4e00\u4e2a\u5728\u6ce5\u6d82\u3002 \u3000\u3000\u4e8e\u662f\u5979\u7528\u6e29\u67d4\u7684\u8138\u8272\u671b\u7740\u90a3\u4e2a\u8d35\u516c\u5b50\uff0c\u5979\u4e0d\u60f3\u95ee\u4ed6\u662f\u4ec0\u4e48\u4eba\uff0c\u4e0d\u60f3\u77e5\u9053\u4ed6\u53eb\u81ea\u5df1\u8fc7\u53bb\u5e72\u4ec0\u4e48\uff0c\u53ea\u89c9\u5f97\u7ad9\u5728\u4ed6\u9762\u524d\u662f\u8bf4\u4e0d\u51fa\u7684\u5feb\u4e50\uff0c\u53ea\u8981\u548c\u4ed6\u4eb2\u8fd1\u4e00\u4f1a\uff0c\u4e5f\u662f\u597d\u7684\u3002 \u3000\u3000\u8fd9\u8d35\u516c\u5b50\u4f3c\u4e4e\u6ca1\u5f15\u8bf1\u5979\uff0c\u53ea\u662f\u5979\u5c11\u5973\u7684\u5e7b\u60f3\u548c\u65e0\u77e5\uff0c\u624d\u5728\u6625\u5929\u7684\u9ec4\u660f\u6fc0\u53d1\u4e86\u8fd9\u6bb5\u70ed\u60c5\uff0c\u5176\u5b9e\u4e0d\u662f\u7684\u3002\u5982\u679c\u798f\u516c\u5b50\u4e0d\u662f\u770b\u5230\u5979\u7684\u7f8e\u8c8c\uff0c\u51b3\u4e0d\u4f1a\u4e0a\u5546\u5bb6\u5821\u6765\u9017\u7559\uff0c\u624b\u4e0b\u6b66\u5e08\u4e00\u4e2a\u8fc7\u4e16\u4e86\u7684\u5e08\u5144\u5f1f\uff0c\u80fd\u5c48\u5f97\u4ed6\u7684\u5927\u9a7e\u4e48\uff1f\u5982\u679c\u4ed6\u4e0d\u662f\u5f97\u5230\u7980\u62a5\uff0c\u5f97\u77e5\u5979\u5728\u82b1\u56ed\u4e2d\u72ec\u81ea\u53d1\u5446\uff0c\u51b3\u4e0d\u4f1a\u5230\u82b1\u4e1b\u5916\u5439\u8427\u3002\u8981\u77e5\u798f\u516c\u5b50\u7684\u8427\u58f0\u662f\u4eac\u5e08\u4e00\u7edd\uff0c\u5c31\u7b97\u662f\u738b\u516c\u4eb2\u8d35\uff0c\u7b49\u95f2\u4e5f\u96be\u5f97\u542c\u4ed6\u5439\u594f\u4e00\u66f2\u3002 \u3000\u3000\u4ed6\u8138\u4e0a\u7684\u795e\u60c5\u663e\u73b0\u4e86\u6e29\u67d4\u7684\u604b\u6155\uff0c\u4ed6\u7684\u773c\u8272\u5410\u9732\u4e86\u70ed\u5207\u7684\u60c5\u610f\uff0c\u7528\u4e0d\u5230\u8bf4\u4e00\u53e5\u8bdd\uff0c\u5374\u80dc\u4e8e\u5343\u8a00\u4e07\u8bed\u7684\u8f7b\u601c\u5bc6\u7231\uff0c\u5343\u8a00\u4e07\u8bed\u7684\u5c71\u76df\u6d77\u8a93\u3002 \u3000\u3000\u798f\u516c\u5b50\u6401\u4e0b\u4e86\u7389\u8427\uff0c\u4f38\u51fa\u624b\u53bb\u6402\u5979\u7684\u7ea4\u8170\u3002\u9a6c\u6625\u82b1\u5a07\u7f9e\u5730\u907f\u5f00\u4e86\uff0c\u7b2c\u4e8c\u6b21\u53ea\u5fae\u5fae\u8ba9\u4e86\u4e00\u8ba9\uff0c\u4f46\u5f53\u4ed6\u7b2c\u4e09\u6b21\u4f38\u624b\u8fc7\u53bb\u65f6\uff0c\u5979\u5df2\u9676\u9189\u5728\u4ed6\u8eab\u4e0a\u6563\u53d1\u51fa\u6765\u7684\u7537\u5b50\u6c14\u606f\u4e4b\u4e2d\u3002 \u3000\u3000\u5915\u9633\u5c06\u73ab\u7470\u82b1\u7684\u679d\u53f6\u7167\u5f97\u6492\u5728\u5730\u4e0a\uff0c\u53d8\u6210\u957f\u957f\u7684\u4e00\u6761\u6761\u5f71\u5b50\u3002\u5728\u82b1\u5f71\u65c1\u8fb9\uff0c\u4e00\u5bf9\u9752\u5e74\u7537\u5973\u7684\u5f71\u5b50\u6e10\u6e10\u504e\u501a\u5728\u4e00\u8d77\uff0c\u7ec8\u4e8e\u4e0d\u518d\u5206\u5f97\u51fa\u662f\u4ed6\u7684\u8fd8\u662f\u5979\u7684\u5f71\u5b50\u3002\u592a\u9633\u5feb\u843d\u5c71\u4e86\uff0c\u5f71\u5b50\u53d8\u5f97\u5f88\u957f\uff0c\u659c\u659c\u7684\u5f88\u96be\u770b\u3002 \u3000\u3000\u5509\uff0c\u9752\u5e74\u7537\u5973\u7684\u70ed\u60c5\uff0c\u4e0d\u4e00\u5b9a\u662f\u7f8e\u4e3d\u7684\u3002 \u3000\u3000\u9a6c\u6625\u82b1\u65e9\u5df2\u6c89\u9189\u4e86\uff0c\u4e0d\u518d\u60f3\u5230\u522b\u7684\uff0c\u6ca1\u60f3\u5230\u90a3\u4f1a\u6709\u4ec0\u4e48\u540e\u679c\uff0c\u66f4\u6ca1\u60f3\u5230\u6709\u4ec0\u4e48\u4eba\u95ef\u5230\u82b1\u56ed\u91cc\u6765\u3002\u798f\u516c\u5b50\u5374\u5728\u8fdb\u82b1\u56ed\u4e4b\u524d\u65e9\u5c31\u60f3\u5230\u4e86\u3002\u6240\u4ee5\u4ed6\u6d3e\u592a\u6781\u95e8\u7684\u9648\u79b9\u53bb\u966a\u9a6c\u884c\u7a7a\u8bf4\u8bdd\uff0c\u6d3e\u738b\u6c0f\u5144\u5f1f\u53bb\u548c\u5546\u6c0f\u6bcd\u5b50\u8c08\u8bba\uff0c\u6d3e\u5c11\u6797\u6d3e\u7684\u53e4\u822c\u82e5\u53bb\u7a33\u4f4f\u5f90\u94ee\uff0c\u6d3e\u5929\u9f99\u95e8\u5357\u5b97\u7684\u6bb7\u4ef2\u7fd4\u5b88\u5728\u82b1\u56ed\u95e8\u53e3\uff0c\u8c01\u4e5f\u4e0d\u8bb8\u8fdb\u6765\u3002 \u3000\u3000\u4e8e\u662f\uff0c\u8c01\u4e5f\u6ca1\u6709\u8fdb\u6765\u3002 \u3000\u3000\u767e\u80dc\u795e\u62f3\u9a6c\u884c\u7a7a\u7684\u5973\u513f\uff0c\u5728\u7236\u4eb2\u5c06\u5979\u7ec8\u8eab\u8bb8\u914d\u7ed9\u5979\u5e08\u54e5\u7684\u7b2c\u4e8c\u5929\uff0c\u505a\u4e86\u522b\u4eba\u7684\u60c5\u5987\u3002 \u3000\u3000\u5f53\u665a\u5546\u5bb6\u5821\u5927\u6446\u7b75\u5e2d\uff0c\u5bb4\u8bf7\u798f\u516c\u5b50\u3002\u56e0\u4e3a\u5ea7\u4e2d\u90fd\u662f\u6b66\u6797\u4eba\u58eb\uff0c\u4e5f\u4e0d\u5fc5\u6709\u7537\u5973\u4e4b\u522b\uff0c\u6240\u4ee5\u5546\u8001\u592a\u548c\u9a6c\u6625\u82b1\u90fd\u548c\u4f17\u4eba\u540c\u5e2d\u3002 \u3000\u3000\u9a6c\u884c\u7a7a\u5f53\u5e74\u8bc6\u5f97\u738b\u6c0f\u5144\u5f1f\u7684\u7236\u4eb2\u738b\u7ef4\u626c\uff0c\u81ea\u738b\u7ef4\u626c\u8fc7\u4e16\u3001\u738b\u6c0f\u5144\u5f1f\u6295\u8eab\u5b98\u5e9c\u4e4b\u540e\uff0c\u9547\u8fdc\u94ee\u5c40\u65e9\u5df2\u6b47\u4e1a\uff0c\u56e0\u6b64\u4e0a\u5df2\u4e0d\u80fd\u8bf4\u662f\u540c\u884c\u3002\u4f46\u738b\u6c0f\u5144\u5f1f\u5374\u4e5f\u77e5\u9053\u9a6c\u884c\u7a7a\u7684\u540d\u5934\uff0c\u5bf9\u4ed6\u9887\u6709\u51e0\u5206\u656c\u610f\u3002 \u3000\u3000\u9a6c\u6625\u82b1\u8138\u6cdb\u7ea2\u6f6e\uff0c\u7709\u6a2a\u6625\u8272\uff0c\u4f4e\u4e0b\u4e86\u5934\u8c01\u4e5f\u4e0d\u77a7\u3002\u65c1\u4eba\u53ea\u9053\u5979\u662f\u5c11\u5973\u5a07\u7f9e\uff0c\u5176\u5b9e\u5979\u5fc3\u4e2d\u662f\u5145\u6ee1\u4e86\u67d4\u60c5\u871c\u610f\u3002\u5979\u5e76\u6ca1\u907f\u5f00\u5f90\u94ee\u7684\u773c\u5149\uff0c\u4e5f\u6ca1\u907f\u5f00\u5546\u5b9d\u9707\u7684\u773c\u5149\u3002\u7136\u800c\u8fd9\u4e24\u4eba\u548c\u5979\u7684\u773c\u5149\u76f8\u63a5\u89e6\u65f6\uff0c\u534a\u70b9\u4e5f\u77a7\u4e0d\u51fa\u5979\u7684\u5fc3\u4e8b\u3002\u4ed6\u4eec\u60f3\uff1a\u201c\u5979\u5fc3\u4e2d\u5230\u5e95\u5bf9\u6211\u600e\u6837\uff1f\u201d\u5979\u5634\u89d2\u8fb9\u5e26\u7740\u5fae\u7b11\uff0c\u4f46\u8fd9\u4e0d\u662f\u4e3a\u4ed6\u4e8c\u4eba\u7b11\u7684\u3002 \u3000\u3000\u5979\u770b\u5230\u4e86\u4ed6\u4eec\uff0c\u5374\u5168\u7136\u6ca1\u770b\u89c1\u4ed6\u4eec\uff0c\u5979\u53ea\u662f\u5728\u60f3\u7740\u9002\u624d\u7684\u5e78\u798f\u548c\u751c\u871c\u3002 \u3000\u3000\u798f\u516c\u5b50\u5e38\u5e38\u5411\u5979\u5077\u770b\u4e00\u773c\u4e24\u773c\uff0c\u4f46\u5979\u51b3\u4e0d\u6562\u56de\u770b\uff0c\u56e0\u4e3a\u5979\u5f88\u660e\u767d\uff0c\u53ea\u8981\u56de\u770b\u4ed6\u4e00\u773c\uff0c\u56db\u76ee\u4ea4\u6295\uff0c\u518d\u4e5f\u5206\u62c6\u4e0d\u5f00\u4e86\u3002 \u3000\u3000\u996e\u98df\u4e4b\u95f4\uff0c\u4e00\u540d\u5bb6\u4e01\u5306\u5306\u8d70\u5230\u5546\u8001\u592a\u8eab\u8fb9\uff0c\u5728\u5979\u8033\u65c1\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u90a3\u59d3\u5e73\u7684\u8d3c\u5b50\u7ed9\u4eba\u6551\u53bb\u4e86\u3002\u201d\u5546\u8001\u592a\u4e00\u60ca\uff0c\u968f\u5373\u795e\u8272\u5982\u5e38\uff0c\u4e3e\u676f\u5411\u4f17\u4eba\u529d\u996e\uff0c\u5fc3\u60f3\u8fd9\u4ef6\u4e8b\u4e0d\u5fc5\u8ba9\u5ba2\u4eba\u77e5\u9053\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u66ae\u5730\u91cc\u7830\u7684\u4e00\u58f0\uff0c\u4e24\u6247\u5385\u95e8\u8131\u67a2\u98de\u8d77\uff0c\u7830\u81a8\u3001\u7830\u81a8\u51e0\u54cd\uff0c\u843d\u5728\u5730\u4e0a\uff0c\u4e00\u4e2a\u7626\u7626\u5c0f\u5c0f\u7684\u4eba\u5f62\u63d2\u8170\u800c\u7acb\uff0c\u7ad9\u5728\u5385\u53e3\u3002 \u3000\u3000\u738b\u6c0f\u5144\u5f1f\u7b49\u867d\u5728\u5e2d\u95f4\uff0c\u4e0d\u5fd8\u4e86\u4fdd\u62a4\u798f\u516c\u5b50\u7684\u804c\u8d23\u91cd\u5927\uff0c\u968f\u8eab\u90fd\u5e26\u5175\u5203\u3002 \u3000\u3000\u53d8\u6545\u4e00\u8d77\uff0c\u51e0\u4e2a\u4eba\u7acb\u5373\u4e00\u9f50\u79bb\u5ea7\uff0c\u5728\u798f\u516c\u5b50\u56db\u5468\u7ad9\u5b9a\uff0c\u53ca\u81f3\u770b\u6e05\u695a\u8fdb\u6765\u7684\u53ea\u662f\u4e00\u4e2a\u5c0f\u5b69\uff0c\u8eab\u8fb9\u5e76\u65e0\u522b\u4eba\uff0c\u4e0d\u7981\u76f8\u987e\u60ca\u8be7\uff1a\u201c\u96be\u9053\u9707\u98de\u5385\u95e8\u7684\uff0c\u7adf\u662f\u8fd9\u4e2a\u5c0f\u5b69\uff1f\u201d \u3000\u3000\u8fd9\u5c0f\u5b69\u6b63\u662f\u80e1\u6590\uff0c\u4ed6\u6551\u4e86\u5e73\u963f\u56db\u51fa\u5821\u540e\uff0c\u60f3\u8d77\u5546\u5b9d\u9707\u97ad\u6253\u4e4b\u4ec7\u867d\u62a5\uff0c\u5546\u8001\u592a\u6697\u7b97\u4e4b\u6068\u672a\u590d\uff0c\u4e8e\u662f\u53c8\u8d76\u56de\u5927\u5385\uff0c\u5927\u58f0\u56b7\u9053\uff1a\u201c\u5546\u8001\u592a\uff0c\u4f60\u6709\u672c\u4e8b\u518d\u6293\u4f4f\u6211\u4e48\uff1f\u201d\u4ed6\u8bf4\u8fd9\u8bdd\u65f6\u795e\u6001\u8c6a\u8fc8\uff0c\u4f46\u6bd5\u7adf\u4e0d\u8131\u5c0f\u5b69\u5b50\u58f0\u53e3\uff0c\u4f3c\u4e4e\u548c\u5979\u95f9\u7740\u73a9\u4e00\u822c\u3002 \u3000\u3000\u5546\u8001\u592a\u4e00\u89c1\u4ec7\u4eba\u4e4b\u5b50\uff0c\u773c\u4e2d\u5982\u8981\u55b7\u706b\uff0c\u4f4e\u58f0\u5411\u513f\u5b50\u9053\uff1a\u201c\u622a\u4f4f\u4ed6\u540e\u8def\uff0c\u522b\u8ba9\u5c0f\u8d3c\u9003\u4e86\u3002\u201d\u53c8\u5411\u8eab\u540e\u7684\u5bb6\u4e01\u9053\uff1a\u201c\u5feb\u53d6\u6211\u5200\u6765\u3002\u201d\u5979\u7f13\u7f13\u79bb\u5ea7\uff0c\u5389\u58f0\u9053\uff1a\u201c\u662f\u8c01\u653e\u8d70\u4f60\u7684\uff1f\u662f\u8fd9\u4f4d\u9a6c\u8001\u62f3\u5e08\u4e0d\u662f\uff1f\u201d\u5979\u51b3\u4e0d\u4fe1\u8fd9\u5b69\u5b50\u81ea\u5df1\u80fd\u8131\u5374\u94c1\u94fe\u4e4b\u7f1a\uff0c\u5b9a\u662f\u5821\u4e2d\u6709\u5978\u7ec6\u76f8\u6551\u3002 \u3000\u3000\u80e1\u6590\u6447\u5934\u9053\uff1a\u201c\u4e0d\u662f\u3002\u201d\u5546\u8001\u592a\u6307\u7740\u5f90\u6323\u9053\uff1a\u201c\u662f\u4ed6\uff1f\u201d\u80e1\u6590\u4ecd\u662f\u6447\u5934\u3002 \u3000\u3000\u5546\u8001\u592a\u6307\u7740\u9a6c\u6625\u82b1\u9053\uff1a\u201c\u90a3\u4e48\u5b9a\u662f\u8fd9\u2026\u2026\u8fd9\u4f4d\u59d1\u5a18\u4e86\uff1f\u201d\u80e1\u6590\u5fc3\u60f3\uff1a\u201c\u8fd9\u4f4d\u59d1\u5a18\u672c\u60f3\u6551\u6211\uff0c\u867d\u7136\u6ca1\u6551\uff0c\u4f46\u6211\u611f\u5979\u7684\u6069\u60c5\u5374\u662f\u4e00\u6837\u3002\u201d\u4e8e\u662f\u7b11\u7740\u70b9\u4e86\u70b9\u5934\uff0c\u5927\u58f0\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u4f4d\u59d1\u5a18\u662f\u6211\u7684\u6551\u547d\u6069\u4eba\u3002\u201d\u4ed6\u8fd9\u8bdd\u662f\u8bf4\u7ed9\u9a6c\u6625\u82b1\u542c\u7684\uff0c\u5728\u4ed6\u5b69\u5b50\u7684\u5fc3\u4e2d\uff0c\u539f\u662f\u4e00\u756a\u611f\u6fc0\u4e4b\u610f\uff0c\u6ca1\u60f3\u5230\u8fd9\u53e5\u8bdd\u4f1a\u7ed9\u5979\u5e26\u6765\u5927\u7978\u3002 \u3000\u3000\u5546\u8001\u592a\u9634\u6c89\u6c89\u5730\u5411\u9a6c\u6625\u82b1\u671b\u4e86\u4e00\u773c\u3002\u8fd9\u65f6\u5e84\u4e01\u5df2\u53d6\u4e86\u5200\u6765\u3002\u5546\u8001\u592a\u5de6\u624b\u63d0\u5200\uff0c\u53f3\u624b\u6307\u7740\u80e1\u6590\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u7239\u7239\u80e1\u4e00\u5200\u600e\u4e48\u4e0d\u6765\uff1f\u201d\u738b\u6c0f\u5144\u5f1f\u7b49\u542c\u8bf4\u773c\u524d\u8fd9\u5b69\u5b50\u7adf\u662f\u8fbd\u4e1c\u5927\u4fa0\u80e1\u4e00\u5200\u4e4b\u5b50\uff0c\u65e0\u4e0d\u8038\u52a8\u3002 \u3000\u3000\u80e1\u6590\u9053\uff1a\u201c\u6211\u7239\u7239\u65e9\u5df2\u8fc7\u4e16\u3002\u4f60\u8981\u62a5\u4ec7\uff0c\u5c31\u627e\u6211\u5427\u3002\u201d\u5546\u8001\u592a\u8138\u5982\u6b7b\u7070\uff0c\u559d\u9053\uff1a\u201c\u6b64\u8bdd\u5f53\u771f\uff1f\u201d\u80e1\u6590\u9053\uff1a\u201c\u6211\u7239\u7239\u82e5\u662f\u5728\u4e16\uff0c\u4f60\u6562\u6253\u6211\u4e00\u97ad\u4e48\uff1f\u201d\u5546\u8001\u592a\u9ad8\u4e3e\u7d2b\u91d1\u516b\u5366\u5200\uff0c\u7a81\u7136\u653e\u58f0\u5927\u54ed\uff0c\u53eb\u9053\uff1a\u201c\u80e1\u4e00\u5200\uff0c\u80e1\u4e00\u5200\uff0c\u4f60\u6b7b\u5f97\u597d\u65e9\u554a\uff01\u4f60\u4e0d\u8be5\u8fd9\u4e48\u65e9\u5c31\u6b7b\u554a\uff01\u201d\u80e1\u6590\u6115\u7136\u4e0d\u89e3\uff1a\u201c\u600e\u4e48\u8fd9\u8001\u592a\u5a46\u5ffd\u8d77\u597d\u5fc3\uff0c\u54ed\u8d77\u6211\u7239\u7239\u6765\uff1f\u201d \u3000\u3000\u5546\u8001\u592a\u5927\u52b1\u4e09\u58f0\uff0c\u7a81\u7136\u6b62\u6cea\uff0c\u4f38\u8896\u5b50\u5728\u8138\u4e0a\u4e00\u62b9\uff0c\u5de6\u8db3\u8e0f\u4e0a\u4e00\u6b65\uff0c\u6479\u5730\u91cc\u6a2a\u8fc7\u7d2b\u91d1\u5200\uff0c\u8eab\u5b50\u75be\u8f6c\uff0c\u547c\u7684\u4e00\u58f0\uff0c\u6a2a\u5200\u5411\u80e1\u6590\u9888\u4e2d\u524a\u53bb\u3002 \u3000\u3000\u8fd9\u4e00\u4e0b\u4eba\u4eba\u51fa\u4e8e\u610f\u6599\u4e4b\u5916\uff0c\u798f\u516c\u5b50\u3001\u9a6c\u6625\u82b1\u3001\u5f90\u94ee\u90fd\u60ca\u53eb\u8d77\u6765\u3002 \u3000\u3000\u5546\u8001\u592a\u8fd9\u4e00\u62db\u201c\u56de\u8eab\u5288\u5c71\u5200\u201d\u4e43\u516b\u5366\u5200\u7edd\u6280\u4e4b\u4e00\uff0c\u53c8\u662f\u51fa\u5176\u4e0d\u610f\uff0c\u83ab\u8bf4\u773c\u524d\u53ea\u662f\u4e2a\u5c0f\u513f\uff0c\u5c31\u662f\u6c5f\u6e56\u597d\u624b\uff0c\u4e5f\u672a\u5fc5\u8eb2\u95ea\u5f97\u4e86\u3002\u5c82\u77e5\u80e1\u6590\u8eab\u6cd5\u597d\u5feb\uff0c\u8eab\u5b50\u4e00\u4fa7\uff0c\u8ba9\u5f00\u5200\u950b\uff0c\u968f\u5373\u4f38\u624b\u62ff\u5979\u624b\u8155\u3002\u4ed6\u5728\u4e00\u62db\u4e4b\u95f4\u7acb\u5373\u53cd\u624b\u62a2\u653b\uff0c\u7fa4\u8c6a\u65e0\u4e0d\u60ca\u8bb6\u3002\u5546\u8001\u592a\u4e00\u5200\u4e0d\u4e2d\uff0c\u60f3\u4e5f\u4e0d\u60f3\uff0c\u7b2c\u4e8c\u5200\u8ddf\u7740\u5288\u51fa\u3002 \u3000\u3000\u83ab\u770b\u5546\u8001\u592a\u8001\u6001\u9f99\u949f\uff0c\u51fa\u624b\u4e4b\u9645\u5200\u5200\u72e0\u8fa3\u3002\u5979\u60f3\u5230\u4ec7\u4eba\u5df2\u6b7b\uff0c\u4eca\u751f\u62a5\u4ec7\u65e0\u671b\uff0c\u552f\u4e00\u7684\u6307\u671b\u5c31\u662f\u6740\u4e86\u773c\u524d\u7684\u5c0f\u513f\u3002\u5979\u5f53\u4e08\u592b\u901d\u4e16\u4e4b\u540e\uff0c\u6240\u4ee5\u4e0d\u81ea\u520e\u6b89\u592b\uff0c\u5168\u56e0\u5fc3\u4e2d\u5b58\u7740\u590d\u4ec7\u4e00\u5ff5\uff0c\u6b64\u65f6\u751f\u65e0\u53ef\u604b\uff0c\u62db\u62db\u7adf\u662f\u4e0e\u654c\u4eba\u540c\u5f52\u4e8e\u5c3d\u7684\u6740\u6cd5\u3002\u80e1\u6590\u521d\u9022\u5f3a\u654c\uff0c\u7cbe\u795e\u5927\u632f\uff0c\u4e0d\u4f5c\u6e38\u6597\uff0c\u5374\u5728\u5200\u7f1d\u4e4b\u4e2d\u4f38\u638c\u62a2\u653b\uff0c\u7adf\u662f\u534a\u62db\u4e5f\u4e0d\u9000\u8ba9\u3002\u654c\u4eba\u6325\u5200\u72e0\u780d\u72e0\u6740\uff0c\u4ed6\u65bd\u5c55\u5927\u64d2\u62ff\u624b\u9f99\u5f62\u722a\uff0c\u4e5f\u662f\u72e0\u51fb\u72e0\u6253\u3002 \u3000\u3000\u70db\u5149\u4e4b\u4e0b\uff0c\u4f46\u89c1\u4e00\u4e2a\u767d\u53d1\u8001\u5987\uff0c\u4e00\u4e2a\u9ec4\u53e3\u5c0f\u513f\uff0c\u6027\u547d\u76f8\u6251\uff0c\u6597\u5f97\u731b\u6076\u5f02\u5e38\u3002 \u3000\u3000\u738b\u6c0f\u5144\u5f1f\u521d\u89c1\u5546\u8001\u592a\u4e00\u4e0a\u6765\u5c31\u731b\u4f7f\u6740\u624b\uff0c\u5fc3\u4e2d\u8fd8\u6697\u602a\u5979\u5c06\u516b\u5366\u95e8\u7684\u529f\u592b\u6ee5\u7528\u4e86\uff0c\u5bf9\u5c0f\u5b69\u513f\u90fd\u4f7f\u7edd\u62db\uff0c\u9022\u5230\u4e00\u6d41\u9ad8\u624b\u90a3\u600e\u4e48\u529e\uff1f\u5c82\u77e5\u8d8a\u770b\u8d8a\u662f\u60ca\u8bb6\u3002 \u3000\u3000\u5546\u8001\u592a\u7684\u4e00\u8def\u516b\u5366\u4e43\u4f7f\u5f97\u7ef5\u5bc6\u72e0\u8fa3\uff0c\u7edd\u65e0\u7834\u7efd\uff0c\u867d\u8bf4\u672a\u81fb\u7089\u706b\u7eaf\u9752\u4e4b\u5883\uff0c\u4f46\u52a0\u4e0a\u5979\u4e0d\u987e\u6027\u547d\u7684\u90a3\u80a1\u72e0\u52b2\uff0c\u5bf9\u624b\u518d\u5f3a\uff0c\u672c\u4e5f\u96be\u4ee5\u62b5\u654c\uff0c\u5c82\u77e5\u4e00\u4e2a\u5341\u6765\u5c81\u7684\u5c11\u5e74\u548c\u5979\u7a7a\u624b\u76f8\u640f\uff0c\u7adf\u7136\u6e10\u5360\u4e0a\u98ce\u3002\u518d\u62c6\u6570\u5408\uff0c\u5546\u8001\u592a\u5df2\u5168\u5728\u80e1\u6590\u638c\u98ce\u7b3c\u7f69\u4e4b\u4e0b\uff0c\u7a81\u7136\u62cd\u7684\u4e00\u58f0\uff0c\u5979\u5de6\u988a\u4e0a\u5403\u4e86\u4e00\u8bb0\u8033\u5149\uff0c\u63a5\u7740\u53f3\u988a\u53c8\u662f\u4e00\u8bb0\u3002 \u3000\u3000\u738b\u5251\u6770\u9053\uff1a\u201c\u5546\u5bb6\u5ac2\u5b50\u9000\u4e0b\uff0c\u6211\u6765\u5bf9\u4ed8\u8fd9\u5c0f\u5b50\uff01\u201d\u624b\u6301\u5927\u5200\uff0c\u8e0f\u6b65\u4e0a\u524d\u3002 \u3000\u3000\u53ea\u542c\u201c\u554a\u54df\u201d\u4e00\u58f0\uff0c\u5546\u8001\u592a\u5df2\u6eda\u5728\u4e00\u65c1\uff0c\u738b\u5251\u6770\u773c\u524d\u7a81\u7136\u9752\u5149\u4e00\u95ea\uff0c\u4e00\u5200\u8fce\u9762\u5288\u5230\uff0c\u6025\u5fd9\u4e3e\u5200\u76f8\u67b6\u3002\u90a3\u5200\u6539\u780d\u4e3a\u524a\uff0c\u4ece\u6a2a\u91cc\u524a\u6765\uff0c\u5f85\u5f97\u659c\u6321\uff0c\u90a3\u5200\u53c8\u5feb\u6377\u65e0\u4f26\u5730\u6539\u4e3a\u64a9\u5200\u3002 \u3000\u3000\u539f\u6765\u80e1\u6590\u6253\u4e86\u5546\u8001\u592a\u4e24\u8bb0\u8033\u5149\uff0c\u5fc3\u613f\u5df2\u8db3\uff0c\u4e00\u52fe\u4e00\u62ff\uff0c\u6263\u4f4f\u4e86\u5979\u7684\u624b\u8155\uff0c\u968f\u5373\u98de\u8d77\u4e00\u817f\uff0c\u5c06\u5979\u8e22\u4e86\u4e00\u4e2a\u7b4b\u6597\uff0c\u5df2\u5c06\u5979\u7d2b\u91d1\u5200\u62a2\u5728\u624b\u91cc\uff0c\u4e0d\u5f85\u738b\u5251\u6770\u8d70\u8fd1\uff0c\u5237\u5237\u5237\u8fde\u73af\u4e09\u5200\uff0c\u5c06\u4ed6\u780d\u4e86\u4e2a\u624b\u5fd9\u811a\u4e71\u3002\u60f3\u90a3\u738b\u5251\u6770\u662f\u516b\u5366\u95e8\u7684\u4e00\u6d41\u9ad8\u624b\uff0c\u6b64\u65f6\u9020\u8be3\uff0c\u5df2\u4e0d\u5728\u5f53\u5e74\u5546\u5251\u9e23\u4e4b\u4e0b\uff0c\u53ea\u56e0\u5fc3\u4e2d\u5b58\u4e86\u8f7b\u89c6\u4e4b\u5fc3\uff0c\u7adf\u7ed9\u654c\u4eba\u62a2\u4e86\u5148\u7740\u3002\u4e09\u62db\u4e00\u8fc7\uff0c\u624d\u77e5\u773c\u524d\u7684\u5c0f\u5b69\u5b9e\u662f\u52b2\u654c\uff0c\u6025\u655b\u72c2\u505a\u4e4b\u6c14\uff0c\u6c89\u7740\u5e94\u6218\uff0c\u5c06\u95e8\u6237\u5b88\u5f97\u4e25\u5bc6\u5f02\u5e38\uff0c\u8981\u5148\u77a7\u6e05\u8fd9\u5c0f\u5b69\u6240\u4f7f\u662f\u54ea\u4e00\u5bb6\u54ea\u4e00\u6d3e\u7684\u5200\u6cd5\u3002 \u3000\u3000\u70db\u5f71\u6447\u7ea2\uff0c\u5200\u5149\u6cdb\u78a7\u3002\u7fa4\u8c6a\u7d27\u63e1\u5175\u5203\uff0c\u77a7\u7740\u4e24\u4eba\u5bf9\u5200\u3002 \u3000\u3000\u798f\u516c\u5b50\u89c1\u8fd9\u6837\u4e00\u4e2a\u8863\u7740\u655d\u964b\u7684\u9ec4\u7626\u5c0f\u513f\uff0c\u7adf\u4e0e\u81ea\u5df1\u5e9c\u4e2d\u7684\u4e00\u6d41\u597d\u624b\u6597\u4e86\u4e2a\u65d7\u9f13\u76f8\u5f53\uff0c\u5fc3\u4e2d\u53c8\u662f\u8be7\u5f02\uff0c\u53c8\u611f\u6709\u8da3\uff0c\u8d1f\u624b\u80cc\u540e\uff0c\u51dd\u795e\u89c2\u6597\u3002\u7a81\u7136\u95f4\u95fb\u5230\u6de1\u6de1\u7684\u4e00\u9635\u8102\u7c89\u9999\uff0c\u773c\u5149\u4e00\u659c\uff0c\u53ea\u89c1\u9a6c\u6625\u82b1\u5df2\u7ad9\u5728\u8eab\u65c1\u3002\u4ed6\u6328\u8fd1\u4e00\u6b65\uff0c\u4f38\u8fc7\u624b\u53bb\u63e1\u4f4f\u4e86\u5979\u624b\u3002\u8fd9\u65f6\u4eba\u4eba\u90fd\u6ce8\u89c6\u7740\u5385\u4e2d\u6fc0\u6597\uff0c\u8c01\u4e5f\u6ca1\u6765\u7559\u5fc3\u4ed6\u4e8c\u4eba\uff0c\u53ef\u662f\u5927\u5ead\u5e7f\u4f17\u4e4b\u95f4\uff0c\u7adf\u7136\u5982\u6b64\u8086\u65e0\u5fcc\u60ee\u7684\u4eb2\u70ed\uff0c\u6bd5\u7adf\u662f\u5927\u80c6\u4e4b\u6781\u3002\u798f\u516c\u5b50\u6ca1\u5c06\u8c01\u653e\u5728\u773c\u91cc\uff0c\u9a6c\u6625\u82b1\u5374\u662f\u5c11\u5973\u521d\u604b\uff0c\u60c5\u6d53\u4e4b\u9645\uff0c\u4e0d\u80fd\u81ea\u5df2\u3002 \u3000\u3000\u738b\u5251\u6770\u8fde\u5288\u6570\u5200\uff0c\u80e1\u6590\u90fd\u4ee5\u5de7\u5999\u8eab\u6cd5\u907f\u8fc7\u3002\u738b\u5251\u6770\u7aed\u529b\u8fa8\u8ba4\u4ed6\u6b66\u529f\u95e8\u6d3e\uff0c\u59cb\u7ec8\u6349\u6478\u4e0d\u5b9a\uff0c\u5fc3\u60f3\u4ed6\u81ea\u79f0\u662f\u80e1\u4e00\u5200\u4e4b\u5b50\uff0c\u867d\u542c\u7236\u4eb2\u8bf4\u8fc7\u80e1\u4e00\u5200\u7684\u540d\u5934\uff0c\u4f46\u80e1\u5bb6\u5200\u6cd5\u7a76\u7adf\u662f\u600e\u4e48\u4e00\u822c\u5bb6\u6570\uff0c\u662f\u521a\u662f\u67d4\uff1f\u5916\u95e8\u5185\u5bb6\uff1f\u5374\u662f\u4e1d\u6beb\u4e0d\u77e5\uff0c\u4f46\u89c1\u8fd9\u5c11\u5e74\u7684\u62db\u6570\u5ffd\u800c\u51dd\u91cd\u5982\u5c71\uff0c\u5ffd\u800c\u6d41\u8f6c\u4f3c\u6c34\uff0c\u4e0e\u4e00\u822c\u5200\u6cd5\u5168\u4e0d\u76f8\u540c\u3002 \u3000\u3000\u53c8\u6597\u6570\u5408\uff0c\u738b\u5251\u6770\u7126\u8e81\u8d77\u6765\uff0c\u5fc3\u60f3\u81ea\u5df1\u5728\u798f\u516c\u5b50\u5e9c\u4e2d\u4f55\u7b49\u8eab\u5206\uff0c\u4eca\u65e5\u6597\u4e00\u4e2a\u5c0f\u513f\u4e5f\u8981\u62c6\u5230\u6570\u5341\u62db\u4e4b\u5916\uff0c\u82e5\u518d\u7ea0\u7f20\u4e0b\u53bb\uff0c\u7eb5\u7136\u5c06\u4ed6\u6740\u4e86\uff0c\u4e5f\u5df2\u8138\u4e0a\u65e0\u5149\uff0c\u5f53\u4e0b\u5200\u6cd5\u4e00\u7d27\uff0c\u8fc8\u5f00\u811a\u6b65\uff0c\u7ed5\u7740\u4ed6\u8eab\u5b50\u6025\u8f6c\u3002 \u3000\u3000\u8981\u77e5\u738b\u6c0f\u516b\u5366\u95e8\u7684\u201c\u516b\u5366\u6e38\u8eab\u201d\u529f\u592b\u5411\u662f\u6b66\u6797\u4e2d\u4e00\u7edd\uff0c\u5f53\u5e74\u738b\u7ef4\u626c\u66fe\u4ee5\u6b64\u8fce\u6597\u201c\u706b\u624b\u5224\u5b98\u201d\u5f20\u53ec\u91cd\u3002\u8fd9\u4e00\u53d1\u8db3\u5954\u884c\uff0c\u5f53\u771f\u662f\u201c\u77bb\u4e4b\u5728\u524d\uff0c\u5ffd\u7109\u4e8e\u540e\u201d\uff0c\u5f85\u5f97\u654c\u4eba\u8f6c\u8fc7\u8eab\u6765\uff0c\u53c8\u65e9\u5df2\u7ed5\u5230\u4ed6\u7684\u80cc\u540e\uff0c\u81ea\u5df1\u811a\u4e0b\u6309\u7740\u516b\u5366\u65b9\u4f4d\uff0c\u6216\u524d\u6216\u540e\uff0c\u5ffd\u5de6\u7ed5\u3001\u5ffd\u53f3\u65cb\uff0c\u4e0d\u52a0\u601d\u7d22\uff0c\u654c\u4eba\u5374\u7ed9\u4ed6\u8f6c\u5f97\u5934\u6655\u773c\u82b1\u3002\u4f46\u82e5\u654c\u4eba\u4e0d\u8ddf\u7740\u8f6c\u52a8\uff0c\u4ed6\u7acb\u5373\u653b\u654c\u80cc\u5fc3\uff0c\u654c\u4eba\u5982\u4f55\u62b5\u6321\uff1f\u786e\u662f\u5341\u5206\u5de7\u5999\u5341\u5206\u5389\u5bb3\u3002\u738b\u5251\u6770\u81ea\u5e7c\u5728\u7236\u4eb2\u76d1\u7763\u4e4b\u4e0b\uff0c\u6bcf\u65e5\u6e05\u6668\u6025\u5954\u4e09\u6b21\uff0c\u6bcf\u6b21\u7edd\u4e0d\u505c\u7559\u5730\u5954\u7ed5\u4e94\u767e\u4e00\u5341\u4e8c\u4e2a\u5708\u5b50\uff0c\u4e34\u7761\u4e4b\u65f6\u53c8\u662f\u6025\u5954\u4e09\u6b21\u3002\u8fd9\u529f\u592b\u4ece\u4e0d\u95f4\u65ad\uff0c\u6bcf\u5929\u5927\u5708\u5b50\u3001\u4e2d\u5708\u5b50\u3001\u5c0f\u5708\u5b50\u4e00\u5171\u8981\u7ed5\u4e09\u5343\u4f59\u8f6c\uff0c\u4e8c\u5341\u4f59\u5e74\u7ec3\u5c06\u4e0b\u6765\uff0c\u811a\u6b65\u5168\u5df2\u6210\u81ea\u7136\uff0c\u53ea\u987b\u987e\u5230\u624b\u4e0a\u53d1\u62db\u4fbf\u884c\u3002 \u3000\u3000\u672c\u6765\u7ed5\u5708\u5b50\u65f6\u624b\u4e0a\u53d1\u638c\uff0c\u6b64\u65f6\u6539\u7528\u5200\u5288\uff0c\u4f46\u89c1\u4ed6\u4eba\u5f71\u98de\u9a70\uff0c\u5200\u5149\u95ea\u52a8\uff0c\u970e\u65f6\u95f4\u5c06\u80e1\u6590\u88f9\u5728\u6838\u5fc3\u3002\u80e1\u6590\u4e4d\u9022\u52b2\u654c\uff0c\u5fd9\u65bd\u5c55\u8f7b\u529f\u95ea\u8eb2\uff0c\u4ed6\u8eab\u5f62\u7075\u5de7\uff0c\u8f7b\u529f\u53c8\u9ad8\uff0c\u5c45\u7136\u5728\u5200\u98ce\u4e4b\u4e2d\u7eb5\u6a2a\u6765\u53bb\uff0c\u907f\u8fc7\u4e86\u6570\u5341\u5200\u7684\u780d\u524a\u65a9\u5288\u3002 \u3000\u3000\u9a6c\u884c\u7a7a\u770b\u5f97\u5927\u662f\u60ca\u5947\uff0c\u5fc3\u4e2d\u6697\u53eb\uff1a\u201c\u60ed\u6127\uff01\u524d\u665a\u89c1\u5230\u7684\u7626\u5c0f\u4eba\u5f71\u539f\u6765\u662f\u4ed6\uff0c\u82e5\u975e\u89c1\u5230\u8fd9\u4e2a\u5c11\u5e74\uff0c\u7109\u80fd\u53d1\u89c9\u5546\u8001\u592a\u7684\u6bd2\u5fc3\uff1f\u53ea\u662f\u5546\u5bb6\u5821\u4e2d\u5367\u864e\u85cf\u9f99\u5e76\u975e\u522b\u4eba\uff0c\u5374\u662f\u8fd9\u4e2a\u9ec4\u7626\u5c0f\u5b69\uff0c\u6789\u81ea\u6211\u4e00\u751f\u95ef\u8361\u6c5f\u6e56\uff0c\u5230\u8001\u6765\u7adf\u8d70\u4e86\u773c\u4e86\u3002\u201d \u3000\u3000\u4e00\u77a5\u773c\u5ffd\u7136\u4e0d\u89c1\u4e86\u5973\u513f\uff0c\u53c8\u89c1\u5f90\u94ee\u4e5f\u5df2\u4e0d\u5728\u5385\u4e2d\uff0c\u5fae\u611f\u6e29\u6012\uff1a\u201c\u5982\u8fd9\u7b49\u9ad8\u624b\u6bd4\u6b66\uff0c\u4e00\u751f\u4e2d\u80fd\u6709\u51e0\u6b21\u89c1\u5f97\uff1f\u5c11\u5e74\u4eba\u771f\u4e0d\u77e5\u597d\u6b79\uff0c\u4e00\u6e9c\u5b50\u5c31\u53bb\u8c08\u60c5\u3002\u65e5\u540e\u6210\u4e86\u592b\u59bb\uff0c\u8fd8\u6015\u8c08\u4e0d\u591f\u4e48\uff1f\u201d \u3000\u3000\u4ed6\u54ea\u77e5\u5973\u513f\u867d\u7136\u786e\u662f\u51fa\u53bb\u8c08\u60c5\u8bf4\u7231\uff0c\u8ddf\u5979\u7f20\u7ef5\u7684\u5374\u4e0d\u662f\u5979\u7684\u672a\u5a5a\u592b\u5a7f\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u5f53\u7684\u4e00\u58f0\u5927\u54cd\uff0c\u706b\u82b1\u56db\u6e85\uff0c\u80e1\u6590\u4e0e\u738b\u5251\u6770\u53cc\u5200\u76f8\u4ea4\u3002\u8fd9\u4e00\u54cd\u4e4b\u540e\uff0c\u63a5\u7740\u54cd\u4e4b\u4e0d\u5df2\u3002\u539f\u6765\u738b\u5251\u6770\u8d8a\u8f6c\u8d8a\u5feb\uff0c\u8d8a\u780d\u8d8a\u662f\u51cc\u5389\u3002\u80e1\u6590\u6bd5\u7adf\u662f\u5e74\u5e7c\u8bc6\u6d45\uff0c\u4e0d\u660e\u4ed6\u5200\u6cd5\u8def\u6570\uff0c\u5230\u540e\u6765\u95ea\u907f\u4e0d\u53ca\uff0c\u53ea\u5f97\u4e3e\u5200\u8fd8\u683c\u3002\u53cc\u5200\u4e00\u4ea4\uff0c\u738b\u5251\u6770\u5fc3\u4e2d\u6697\u559c\uff1a\u201c\u8fd9\u5c0f\u5b50\u6b66\u529f\u867d\u7136\u4e0d\u574f\uff0c\u529b\u6c14\u7a76\u5c0f\uff0c\u518d\u780d\u51e0\u5200\uff0c\u4ed6\u5175\u5203\u975e\u8131\u624b\u4e0d\u53ef\u3002\u201d \u3000\u3000\u5f53\u4e0b\u4e00\u8def\u6025\u780d\u731b\u6240\uff0c\u80e1\u6590\u88ab\u8feb\u786c\u63a5\uff0c\u4e94\u516d\u5200\u8fc7\u540e\uff0c\u624b\u81c2\u9707\u5f97\u6e10\u611f\u9178\u9ebb\u3002\u5546\u5251\u9e23\u7684\u7d2b\u91d1\u5200\u9887\u4e3a\u6c89\u91cd\uff0c\u80e1\u6590\u529b\u5c0f\uff0c\u4f7f\u52a8\u65f6\u672c\u5df2\u4e0d\u5927\u987a\u624b\uff0c\u8fd9\u65f6\u66f4\u611f\u5403\u529b\u3002 \u3000\u3000\u738b\u5251\u6770\u8eab\u6750\u9b41\u68a7\uff0c\u80e1\u6590\u7684\u5934\u8fd8\u53ca\u4e0d\u5230\u4ed6\u5934\u9888\uff0c\u4e00\u4e2a\u5c45\u9ad8\u4e34\u4e0b\uff0c\u4e00\u4e2a\u4ef0\u5934\u63a5\u62db\uff0c\u5f3a\u5f31\u4e4b\u52bf\u66f4\u662f\u60ac\u6b8a\u3002\u80e1\u6590\u773c\u89c1\u4e0d\u654c\uff0c\u7a81\u7136\u7075\u673a\u4e00\u52a8\uff0c\u5c06\u4ed6\u4e00\u5200\u67b6\u5f00\uff0c\u8df3\u51fa\u5708\u5b50\uff0c\u53eb\u9053\uff1a\u201c\u4e14\u6162\uff01\u201d\u738b\u5251\u6770\u4e0e\u4ed6\u672c\u65e0\u4ec7\u6028\uff0c\u89c1\u4ed6\u5c0f\u5c0f\u5e74\u7eaa\uff0c\u5c45\u7136\u80fd\u63a5\u4e0b\u81ea\u5df1\u6570\u5341\u62db\uff0c\u5fc3\u4e2d\u52a8\u4e86\u7231\u624d\u4e4b\u5ff5\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u5427\uff0c\u4f60\u8ba4\u8f93\u4fbf\u662f\uff0c\u6211\u5c31\u9976\u4f60\u4e00\u547d\u3002\u201d \u3000\u3000\u80e1\u6590\u7b11\u9053\uff1a\u201c\u8c01\u8ba4\u8f93\u4e86\uff1f\u4f60\u4e0d\u8fc7\u80dc\u5728\u751f\u5f97\u725b\u9ad8\u9a6c\u5927\uff0c\u8eab\u6750\u4e0a\u5360\u4e86\u4fbf\u5b9c\uff0c\u90a3\u53c8\u7b97\u5f97\u4ec0\u4e48\u672c\u4e8b\uff1f\u4f60\u7b49\u4e00\u4e0b\u3002\u201d\u8bf4\u7740\u642c\u8fc7\u4e00\u5f20\u957f\u51f3\uff0c\u5f80\u5927\u5385\u4e2d\u5fc3\u4e00\u653e\uff0c\u7eb5\u8eab\u4e0a\u51f3\uff0c\u53eb\u9053\uff1a\u201c\u54b1\u4eec\u518d\u6765\u6bd4\u8fc7\u3002\u201d\u738b\u5251\u6770\u53c8\u662f\u597d\u6c14\uff0c\u53c8\u662f\u597d\u7b11\uff0c\u9053\uff1a\u201c\u90a3\u7b97\u4ec0\u4e48\uff1f\u201d\u80e1\u6590\u9053\uff1a\u201c\u54b1\u4eec\u8bdd\u8bf4\u660e\u5728\u5148\uff0c\u4f60\u53ef\u4e0d\u8bb8\u8e22\u52a8\u6211\u7684\u957f\u51f3\uff0c\u5426\u5219\u5c31\u7b97\u4f60\u8f93\u4e86\u3002\u201d\u738b\u5251\u6770\u5478\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5929\u4e0b\u54ea\u6709\u8fd9\u822c\u6bd4\u6b66\u6cd5\u5b50\uff1f\u201d\u80e1\u6590\u7b11\u9053\uff1a\u201c\u6211\u4eba\u672a\u957f\u8db3\uff0c\u81ea\u662f\u6ca1\u4f60\u9ad8\u3002\u4f60\u82e5\u4e0d\u613f\uff0c\u4e94\u5e74\u540e\u7b49\u6211\u957f\u5f97\u8ddf\u4f60\u4e00\u822c\u9ad8\u4e86\uff0c\u518d\u6765\u51b3\u4e2a\u80dc\u8d25\u3002\u201d \u3000\u3000\u80e1\u6590\u5e73\u65f6\u542c\u5e73\u963f\u56db\u8c08\u8bba\u4ed6\u7236\u4eb2\u80e1\u4e00\u5200\u7684\u5a01\u98ce\uff0c\u53ea\u9053\u5b66\u5f97\u7236\u4eb2\u9057\u4e66\u4e0a\u7684\u6b66\u529f\u4e4b\u540e\uff0c\u4e5f\u53ef\u5982\u7236\u4eb2\u4e00\u822c\u6240\u5411\u65e0\u6545\uff0c\u5c82\u77e5\u4e00\u4e0a\u624b\u5c31\u7ed9\u5546\u8001\u592a\u6263\u4f4f\u8109\u95e8\uff0c\u7ed3\u7ed3\u5b9e\u5b9e\u5730\u6328\u4e86\u4e00\u987f\u597d\u6253\u3002\u90a3\u8fd8\u53ef\u8bf4\u81ea\u5df1\u4e00\u65f6\u4e0d\u9632\uff0c\u8fd9\u65f6\u8ddf\u738b\u5251\u6770\u4e00\u52a8\u624b\uff0c\u624d\u77e5\u81ea\u5df1\u867d\u7136\u5200\u6cd5\u5927\u80dc\u4e8e\u4ed6\uff0c\u529f\u529b\u5374\u548c\u4ed6\u5dee\u5f97\u592a\u8fdc\uff0c\u56e0\u800c\u4ea4\u4ee3\u4e86\u8fd9\u51e0\u53e5\u8bdd\uff0c\u5c31\u60f3\u4e58\u673a\u8131\u8eab\u3002 \u3000\u3000\u54ea\u77e5\u5de5\u5251\u6770\u4e00\u6765\u4e22\u4e0d\u8d77\u8fd9\u4e2a\u8138\uff0c\u4e8c\u6765\u81ea\u6043\u5fc5\u80dc\uff0c\u9a82\u9053\uff1a\u201c\u5c0f\u7334\u513f\u5d3d\u5b50\uff0c\u4e0d\u8e22\u4f60\u8fd9\u51f3\u53c8\u600e\u4e48\u4e86\uff1f\u6015\u8001\u7237\u5288\u4e0d\u6b7b\u4f60\u4e48\uff1f\u201d\u8bf4\u7740\u6325\u5200 99 \u5411\u4ed6\u8170\u95f4\u524a\u53bb\u3002 \u80e1\u6590\u6a2a\u5200\u4e00\u5c01\uff0c\u4e8c\u4eba\u53c8\u4ea4\u4e0a\u4e86\u624b\uff0c\u6b64\u65f6\u80e1\u6590\u5374\u5df2\u9ad8\u8fc7\u4e86\u5bf9\u65b9\uff0c\u4ed6\u5728\u957f\u51f3\u4e0a\u5954\u5de6\u7a9c\u53f3\uff0c\u62a1\u5200\u800c\u6218\uff0c\u90a3\u51f3\u5b50\u6709\u4e94\u5c3a\u6765\u957f\uff0c\u738b\u5251\u6770\u82e5\u518d\u7ed5\u7740\u8f6c\u52a8\uff0c\u8f6c\u7684\u5708\u5b50\u592a\u5927\uff0c\u8ddf\u4ed6\u4e8c\u5341\u591a\u5e74\u6765\u6240\u7ec3\u7684\u5708\u5b50\u5927\u5c0f\u4e0d\u540c\uff0c\u8fd9\u662f\u719f\u7ec3\u4e86\u7684\u529f\u592b\uff0c\u4e34\u65f6\u6539\u53d8\u4e0d\u6765\uff0c\u5f53\u4e0b\u6539\u4f7f\u4e00\u5957\u5200\u4e2d\u5939\u638c\u3001\u638c\u4e2d\u5939\u5200\u7684\u6b66\u529f\uff0c\u8981\u4ee5\u521a\u731b\u7684\u5200\u98ce\u638c\u529b\uff0c\u5c06\u5bf9\u65b9\u9707\u4e0b\u51f3\u6765\u3002\u80e1\u6590\u77e5\u4ed6\u5fc3\u610f\uff0c\u4e0d\u505c\u7eb5\u8dc3\u7a9c\u907f\uff0c\u4e0d\u518d\u786c\u63a5\u3002\u738b\u5251\u6770\u867d\u662f\u4e13\u4fee\u516b\u5366\u4e00\u95e8\u6b66\u529f\uff0c\u4f46\u90a3\u516b\u5366\u95e8\u4e2d\u6b66\u529f\u4e5f\u751a\u7e41\u590d\uff0c\u5355\u662f\u5200\u6cd5\uff0c\u5c31\u6709\u5927\u67b6\u3001\u5c0f\u67b6\u3001\u5185\u67b6\u3001\u5916\u67b6\u8bf8\u9879\u53d8\u5f62\u3002\u4ed6\u5200\u6cd5\u4e00\u53d8\uff0c\u5de6\u6325\u53f3\u524a\uff0c\u4e13\u780d\u654c\u624b\u4e0b\u76d8\u3002\u80e1\u6590\u8dc3\u8d77\u8eb2\u95ea\u3002\u738b\u5251\u6770\u524a\u5f97\u6570\u5200\uff0c\u89c1\u80e1\u6590\u53c8\u5df2\u8dc3\u8d77\uff0c\u4e0d\u5f85\u4ed6\u843d\u4e0b\uff0c\u8ddf\u7740\u4e00\u5200\u8d34\u51f3\u6a2a\u524a\uff0c\u6536\u5200\u65f6\u81ea\u5de6\u5411\u53f3\u62d6\u8f6c\uff0c\u80e1\u6590\u5982\u843d\u811a\u8e0f\u4e0a\u957f\u51f3\uff0c\u4e00\u8db3\u975e\u7ed9\u524a\u65ad\u4e0d\u53ef\uff0c\u8981\u907f\u8fc7\u8fd9\u4e24\u524a\uff0c\u53ea\u6709\u79bb\u51f3\u843d\u5730\u3002 \u3000\u3000\u597d\u80e1\u6590\uff0c\u5f53\u771f\u662f\u8ba1\u8c0b\u767e\u51fa\uff0c\u773c\u89c1\u52bf\u5728\u4e24\u96be\uff0c\u7a81\u7136\u4f38\u811a\u5c16\u5728\u957f\u51f3\u5de6\u7aef\u7528\u529b\u4e00\u70b9\uff0c\u501f\u52bf\u4e0a\u8dc3\uff0c\u90a3\u957f\u51f3\u6479\u5730\u7ad6\u7acb\u3002\u8fd9\u4e00\u4e0b\u771f\u51fa\u5176\u4e0d\u610f\uff0c\u7830\u7684\u4e00\u58f0\uff0c\u957f\u51f3\u7ffb\u4e0a\u6765\u7684\u53f3\u7aef\uff0c\u6b63\u597d\u649e\u4e2d\u738b\u5251\u6770\u4e0b\u5df4\uff0c\u52bf\u9053\u53ef\u8fd8\u7740\u5b9e\u4e0d\u8f7b\u3002\u80e1\u6590\u5374\u5df2\u7ad9\u5728\u7ad6\u8d77\u7684\u957f\u51f3\u9876\u7aef\uff0c\u5c45\u9ad8\u4e34\u4e0b\uff0c\u62a1\u5200\u780d\u5c06\u4e0b\u6765\u3002\u8fd9\u4e00\u4e0b\u53d8\u6545\u751a\u662f\u6ed1\u7a3d\uff0c\u65c1\u89c2\u4f17\u4eba\u5fcd\u4e0d\u4f4f\u5931\u7b11\u3002 \u3000\u3000\u738b\u5251\u6770\u5927\u6012\uff0c\u6325\u5200\u780d\u4e86\u51e0\u62db\uff0c\u53ea\u56e0\u80e1\u6590\u5728\u9ad8\uff0c\u81ea\u5df1\u5927\u5904\u52a3\u52bf\uff0c\u4e5f\u987e\u4e0d\u5f97\u66fe\u7b54\u5e94\u4e0d\u52a8\u4ed6\u7684\u957f\u51f3\uff0c\u5de6\u817f\u98de\u51fa\uff0c\u8e22\u7ffb\u957f\u51f3\uff0c\u8ddf\u7740\u4e00\u5200\u201c\u4e0a\u6b65\u5288\u5c71\u201d\uff0c\u5411\u80e1\u6590\u80f8\u53e3\u5241\u53bb\u3002\u80e1\u6590\u4eba\u672a\u843d\u5730\uff0c\u6a2a\u5200\u4e00\u67b6\uff0c\u501f\u7740\u4ed6\u4e00\u5241\u4e4b\u52bf\uff0c\u7a9c\u51fa\u534a\u4e08\uff0c\u4e00\u4fef\u8eab\uff0c\u5de6\u624b\u4e3e\u8d77\u957f\u51f3\uff0c\u5f53\u4f5c\u4e00\u6761\u957f\u5f62\u76fe\u724c\uff0c\u4ee5\u957f\u51f3\u6321\u67b6\u654c\u5200\uff0c\u53f3\u624b\u7684\u7d2b\u91d1\u5200\u5374\u4e00\u5200\u5200\u5730\u9012\u5c06\u51fa\u53bb\u3002 \u3000\u3000\u738b\u5251\u82f1\u89c1\u5144\u5f1f\u4e45\u6218\u4e0d\u4e0b\uff0c\u65e9\u5df2\u76b1\u8d77\u4e86\u7709\u5934\uff0c\u65c1\u89c2\u4f17\u4eba\u4e2d\u9648\u79b9\u3001\u6bb7\u4ef2\u7fd4\u3001\u53e4\u822c\u82e5\u3001\u9a6c\u884c\u7a7a\u7b49\u5747\u662f\u6c5f\u6e56\u597d\u624b\uff0c\u773c\u89c1\u6218\u5c40\u53d8\u5e7b\uff0c\u80e1\u6590\u65e9\u5df2\u843d\u8d25\uff0c\u738b\u5251\u6770\u5374\u59cb\u7ec8\u62fe\u4ed6\u4e0d\u4e0b\uff0c\u5747\u662f\u6697\u6697\u79f0\u5947\u3002 \u3000\u3000\u6b64\u65f6\u80e1\u6590\u5de6\u51f3\u53f3\u5200\uff0c\u5175\u5203\u4e0a\u5927\u5360\u4fbf\u5b9c\u3002\u90a3\u957f\u51f3\u662f\u7ea2\u6728\u6240\u9020\uff0c\u751a\u662f\u575a\u786c\uff0c\u88ab\u738b\u5251\u6770\u8fde\u780d\u51e0\u5200\uff0c\u5374\u780d\u4e4b\u4e0d\u65ad\u3002\u80e1\u6590\u8eb2\u5728\u51f3\u540e\uff0c\u53cd\u800c\u4e0d\u4f4f\u62a2\u653b\u3002\u738b\u5251\u6770\u9a82\u9053\uff1a\u201c\u5c0f\u7334\u513f\uff0c\u8001\u7237\u53eb\u4f60\u77e5\u9053\u5389\u5bb3\uff01\u201d\u731b\u5730\u91cc\u4e00\u62db\u201c\u4e0a\u6b6a\u95e8\u201d\uff0c\u6325\u5200\u659c\u780d\uff0c\u767b\u7684\u4e00\u58f0\uff0c\u4e00\u5200\u780d\u4e2d\u5728\u51f3\u6b63\u4e2d\uff0c\u5c82\u77e5\u8fd9\u4e00\u4e0b\u4f7f\u529b\u592a\u5f3a\uff0c\u5200\u5203\u6df1\u5165\u51f3\u5185\uff0c\u56de\u624b\u4e00\u62d4\u7adf\u7136\u62d4\u4e0d\u51fa\u6765\u3002\u4ed6\u6b63\u8981\u52a0\u529b\u56de\u593a\uff0c\u7a81\u89c1\u7d2b\u5149\u4e00\u95ea\uff0c\u5bf9\u624b\u7684\u5200\u5c16\u5df2\u523a\u5411\u81ea\u5df1\u5c0f\u8179\u3002\u8fd9\u4e00\u62db\u72b9\u5982\u6d41\u6c34\u884c\u4e91\uff0c\u6765\u5f97\u597d\u5feb\uff0c\u738b\u5251\u6770\u4e00\u60ca\uff0c\u53ea\u5f97\u6492\u624b\u653e\u5200\u3002\u4f46\u4ed6\u660e\u660e\u5df2\u7ecf\u5f97\u80dc\uff0c\u88ab\u8fd9\u5c0f\u5b69\u80e1\u6df7\u593a\u53bb\u5175\u5203\uff0c\u5fc3\u4e2d\u7109\u80af\u7518\u670d\uff1f\u5f53\u5373\u7a7a\u624b\u8fdb\u51fb\uff0c\u8fd9\u4f4d\u516b\u5366\u5200\u540d\u5bb6\u7adf\u8981\u4ee5\u4e00\u53cc\u8089\u638c\u633d\u56de\u8138\u9762\u3002 \u3000\u3000\u53ea\u89c1\u4ed6\u70b9\u6253\u6233\u62ff\uff0c\u5288\u51fb\u538b\u649e\uff0c\u53cc\u638c\u5728\u5200\u7f1d\u4e2d\u62a2\u653b\u800c\u524d\uff0c\u5a01\u52bf\u7adf\u662f\u4e0d\u4e0b\u4e8e\u4f7f\u5200\u4e4b\u65f6\u3002\u80e1\u6590\u529b\u5f31\uff0c\u633a\u7740\u4e00\u53ea\u7b28\u91cd\u7684\u957f\u51f3\uff0c\u5982\u4f55\u80fd\u4e0e\u4ed6\u8f7b\u6377\u7684\u7a7a\u624b\u76f8\u654c\uff1f \u3000\u3000\u7728\u773c\u95f4\u8fde\u9047\u9669\u62db\uff0c\u62cd\u7684\u4e00\u54cd\uff0c\u80a9\u5934\u88ab\u4ed6\u4e00\u638c\u51fb\u4e2d\uff0c\u9669\u4e9b\u8dcc\u5012\u3002\u65c1\u89c2\u4f17\u4eba\u4e00\u9f50\u53eb\u4e86\u8d77\u6765\u3002 \u3000\u3000\u80e1\u6590\u5fcd\u4f4f\u75bc\u75db\uff0c\u5de6\u624b\u5c06\u957f\u51f3\u4e00\u9001\u4e00\u653e\uff0c\u968f\u5373\u6293\u4f4f\u51f3\u9762\u4e0a\u7684\u5355\u5200\u5200\u67c4\uff0c\u53f3\u8db3\u5728\u51f3\u4e0a\u731b\u8e22\u4e00\u817f\uff0c\u957f\u51f3\u79bb\u5200\uff0c\u5411\u738b\u5251\u6770\u649e\u53bb\u3002\u738b\u5251\u6770\u89c1\u4ed6\u62fc\u6597\u4e0d\u4f9d\u5e38\u6cd5\uff0c\u4e00\u5473\u80e1\u6df7\uff0c\u5927\u6709\u76f8\u8fb1\u4e4b\u610f\uff0c\u5fc3\u4e2d\u8d8a\u6012\uff0c\u53cc\u638c\u75be\u5411\u957f\u51f3\u5288\u53bb\u3002\u8fd9\u957f\u51f3\u5148\u524d\u5df2\u53d7\u5200\u780d\uff0c\u518d\u52a0\u638c\u529b\u4e00\u9707\uff0c\u5580\u5587\u4e00\u54cd\uff0c\u767b\u65f6\u65ad\u4e3a\u4e24\u622a\u3002\u80e1\u6590\u5374\u5df2\u53cc\u5200\u5728\u624b\uff0c\u7740\u5730\u5377\u6765\u3002 \u3000\u3000\u738b\u5251\u6770\u7a7a\u624b\u5bf9\u53cc\u5200\uff0c\u4e1d\u6beb\u4e0d\u60e7\uff0c\u53f3\u624b\u62ff\uff0c\u5de6\u624b\u94a7\uff0c\u7a81\u7136\u95f4\u80e1\u6590\u60ca\u53eb\u4e00\u58f0\uff0c\u5de6\u624b\u5200\u5df2\u88ab\u4ed6\u5939\u624b\u593a\u53bb\u3002\u738b\u5251\u6770\u5c06\u94a2\u5200\u5f80\u5730\u4e0b\u4e00\u6454\uff0c\u4ecd\u662f\u7a7a\u624b\u5bf9\u5200\u3002\u4ed6\u5728\u638c\u6cd5\u4e0a\u6d78\u6deb\u4e8c\u5341\u4f59\u5e74\uff0c\u4f7f\u5c06\u51fa\u6765\u679c\u7136\u51cc\u5389\u5df2\u6781\u3002\u5546\u5b9d\u9707\u5728\u65c1\u77a7\u5f97\u53c8\u662f\u6cae\u4e27\u53c8\u662f\u559c\u6b22\uff0c\u6cae\u4e27\u7684\u662f\u81ea\u5df1\u81ea\u5e7c\u82e6\u5b66\uff0c\u53ea\u9053\u5df2\u7aa5\u5802\u5965\uff0c\u4f46\u4e0e\u8fd9\u4f4d\u5e08\u53d4\u76f8\u8f83\uff0c\u4e0d\u77e5\u4f55\u5e74\u4f55\u6708\u65b9\u80fd\u7ec3\u5230\u4ed6\u8fd9\u6837\u7684\u529f\u592b\uff0c\u559c\u6b22\u7684\u662f\u672c\u95e8\u6b66\u529f\u5982\u6b64\u795e\u5999\uff0c\u53ea\u8981\u4e0d\u65ad\u4fee\u4e60\uff0c\u524d\u9014\u81ea\u662f\u4e0d\u53ef\u9650\u91cf\u3002\u731b\u542c\u5f97\u738b\u5251\u6770\u66b4\u559d\u4e00\u58f0\uff1a\u201c\u53bb\uff01\u201d\u80e1\u6590\u7d2b\u91d1\u5200\u8131\u624b\u98de\u51fa\uff0c\u5fd9\u5411\u540e\u8dc3\u5f00\u3002 \u3000\u3000\u738b\u5251\u6770\u53cc\u638c\u4e00\u5e76\uff0c\u6392\u5c71\u5012\u6d77\u822c\u51fb\u5c06\u8fc7\u6765\u3002\u80e1\u6590\u773c\u89c1\u62b5\u6321\u4e0d\u6ce8\uff0c\u60c5\u6025\u667a\u751f\uff0c\u5ffd\u5730\u6307\u7740\u4ed6\u54c8\u54c8\u5927\u7b11\u3002\u738b\u5251\u6770\u7ed9\u4ed6\u7b11\u5f97\u83ab\u540d\u5176\u5999\uff0c\u6536\u638c\u4e0d\u53d1\uff0c\u695e\u4e86\u4e00\u695e\uff0c\u9a82\u9053\uff1a\u201c\u5c0f\u5b50\uff0c\u4f60\u7b11\u4ec0\u4e48\uff1f\u201d\u80e1\u6590\u7b11\u9053\uff1a\u201c\u6211\u5e2e\u624b\u6765\u5566\uff0c\u4e0d\u518d\u6015\u4f60\u4eec\u8fd9\u8bb8\u591a\u5927\u4eba\u9f50\u5fc3\u5408\u529b\u6b3a\u4fae\u6211\u4e00\u4e2a\u5b69\u5b50\u3002\u201d\u738b\u5251\u6770\u4e00\u6115\uff0c\u81ea\u5fd6\uff1a\u201c\u6211\u662f\u6c5f\u6e56\u4e0a\u7684\u6210\u540d\u4eba\u7269\uff0c\u8ddf\u8fd9\u5c0f\u9b3c\u5934\u4e00\u822c\u89c1\u8bc6\uff0c\u5230\u5e95\u8be5\u662f\u4e0d\u8be5\uff1f\u201d\u80e1\u6590\u7b11\u9053\uff1a\u201c\u6211\u8fd9\u5c31\u63a5\u6211\u5e2e\u624b\u53bb\uff0c\u4f60\u4eec\u90fd\u5728\u8fd9\u91cc\u7b49\u7740\uff0c\u53ef\u522b\u5bb3\u6015\u4e86\u9003\u8d70\u3002\u201d\u4e58\u7740\u738b\u5251\u6770\u8fdf\u7591\u672a\u5b9a\uff0c\u6025\u6b65\u5411\u5385\u95e8\u8d70\u51fa\uff0c\u4fbf\u60f3\u4e58\u673a\u6e9c\u5f00\u3002 \u3000\u3000\u5546\u8001\u592a\u5df2\u62fe\u8d77\u7d2b\u91d1\u516b\u5366\u5200\uff0c\u7eb5\u4e0a\u62e6\u4f4f\uff0c\u559d\u9053\uff1a\u201c\u5c0f\u6742\u79cd\uff0c\u4f60\u60f3\u9003\u4e48\uff1f\u201d \u3000\u3000\u53ef\u662f\u5979\u77e5\u8fd9\u5c0f\u5b69\u7684\u6b66\u529f\u5728\u81ea\u5df1\u4e4b\u4e0a\uff0c\u5374\u4e5f\u4e0d\u6562\u5341\u5206\u903c\u8fd1\u3002 \u3000\u3000\u5c31\u5728\u6b64\u65f6\uff0c\u5ffd\u542c\u5f97\u8fdc\u5904\u9a6c\u8e44\u58f0\u54cd\uff0c\u6025\u9a70\u800c\u6765\u3002\u9759\u591c\u4e4b\u4e2d\uff0c\u8e44\u58f0\u5f02\u5e38\u6e05\u6670\uff0c\u672c\u6765\u5feb\u9a6c\u72c2\u5954\uff0c\u8e44\u58f0\u7e41\u5bc6\uff0c\u4e5f\u662f\u5e38\u4e8b\uff0c\u4f46\u8bf4\u4e5f\u5947\u602a\uff0c\u8fd9\u5339\u9a6c\u843d\u8e44\u4e4b\u58f0\u72b9\u5982\u6025\u96e8\uff0c\u5f97\u5f97\u5f97\u5f97\uff0c\u5f97\u5f97\u5f97\u5f97\uff0c\u6bd4\u4e24\u5339\u9a6c\u540c\u65f6\u5954\u8dd1\u7684\u8e44\u58f0\u8fd8\u66f4\u7d27\u5bc6\u3002\u5385\u4e0a\u8bf8\u4eba\u591a\u534a\u662f\u6c5f\u6e56\u4e0a\u7684\u5927\u884c\u5bb6\uff0c\u94a2\u5200\u5feb\u9a6c\uff0c\u539f\u662f\u5bb6\u5e38\u4fbf\u996d\uff0c\u4f46\u542c\u5f97\u8e44\u58f0\u622a\u7136\u6709\u5f02\uff0c\u4e0d\u7981\u8138\u4e0a\u5747\u73b0\u8be7\u5f02\u4e4b\u8272\u3002\u970e\u65f6\u4e4b\u95f4\uff0c\u90a3\u9a6c\u5df2\u5954\u5230\u4e86\u5821\u524d\uff0c\u4f46\u542c\u5e84\u4e01\u547c\u53f1\u58f0\uff0c\u5821\u95e8\u63a8\u5f00\u58f0\uff0c\u5e84\u4e01\u7ffb\u8dcc\u58f0\uff0c\u5175\u5203\u843d\u5730\u58f0\u63a5\u7740\u54cd\u8d77\u3002\u4f17\u4eba\u6115\u7136\u76f8\u987e\u4e4b\u9645\uff0c\u5385\u53e3\u5df2\u591a\u4e86\u4e00\u4eba\u3002 \u3000\u3000\u8e44\u58f0\u521d\u8d77\u662f\u5728\u4e09\u6570\u91cc\u5916\uff0c\u4f46\u9877\u523b\u4e4b\u95f4\uff0c\u6b64\u4eba\u5df2\u95ef\u8fdb\u5821\u6765\uff0c\u73b0\u8eab\u5385\u53e3\uff0c\u5176\u8fc5\u96f7\u4e0d\u53ca\u63a9\u8033\u7684\u795e\u901f\uff0c\u771f\u662f\u7f55\u89c1\u7f55\u95fb\uff0c\u5821\u4e2d\u4e00\u95fb\u8b66\u8baf\uff0c\u4fbf\u8981\u8f6c\u4e2a\u5fa1\u654c\u7684\u5ff5\u5934\u4e5f\u6765\u4e0d\u53ca\uff0c\u522b\u8bf4\u5206\u6d3e\u4eba\u624b\u4e86\u3002\u7fa4\u8c6a\u8038\u52a8\u4e4b\u4e0b\uff0c\u76ee\u5149\u4e00\u9f50\u6ce8\u89c6\u5728\u6765\u4eba\u8eab\u4e0a\u3002 \u3000\u3000\u53ea\u89c1\u90a3\u4eba\u4e94\u5341\u5c81\u5de6\u53f3\u5e74\u7eaa\uff0c\u7a7f\u4e00\u4ef6\u8170\u8eab\u5bbd\u5927\u7684\u5e03\u888d\uff0c\u4e0a\u5507\u5fae\u9b13\uff0c\u5934\u53d1\u5df2\u73b0\u82b1\u767d\uff0c\u4e2d\u7b49\u8eab\u6750\uff0c\u7565\u89c1\u80a5\u80d6\uff0c\u7b11\u541f\u541f\u7684\u9762\u76ee\u751a\u662f\u6148\u7965\uff0c\u53f3\u624b\u643a\u7740\u4e00\u4e2a\u5341\u4e8c\u4e09\u5c81\u7684\u5973\u5b69\u3002\u77a7\u4ed6\u6a21\u6837\uff0c\u5c31\u4f3c\u662f\u4e00\u4e2a\u4e61\u4e0b\u7684\u571f\u8d22\u4e3b\uff0c\u53c8\u4f3c\u662f\u5c0f\u9547\u4e0a\u5546\u5e97\u7684\u638c\u67dc\uff0c\u968f\u53e3\u5c31\u8981\u8bf4\u51fa\u201c\u606d\u559c\u53d1\u8d22\u201d\u7684\u8bdd\u6765\uff0c\u867d\u7136\u7565\u89c9\u4fd7\u6c14\uff0c\u5374\u662f\u795e\u6001\u53ef\u4eb2\uff0c\u4e0e\u8fdb\u5821\u65f6\u90a3\u80a1\u527d\u608d\u51cc\u5389\u7684\u52bf\u9053\u5168\u4e0d\u76f8\u7b26\u3002 \u3000\u3000\u80e1\u6590\u8bf4\u6709\u5e2e\u624b\u5230\u6765\uff0c\u539f\u662f\u4fe1\u53e3\u5f00\u6cb3\uff0c\u53ea\u76fc\u4f17\u4eba\u4e00\u4e2a\u4e0d\u63d0\u9632\uff0c\u5c31\u6b64\u6e9c\u8d70\uff0c\u5c82\u77e5\u4e8b\u6709\u51d1\u5de7\uff0c\u521a\u597d\u6709\u4eba\u8d76\u8fdb\u5821\u6765\u3002\u4ed6\u4e58\u7740\u4f17\u4eba\u7fa4\u76f8\u6ce8\u89c6\u90a3\u80d6\u5b50\u4e4b\u9645\uff0c\u7ed5\u5230\u5404\u4eba\u80cc\u540e\uff0c\u6162\u6162\u8d70\u5411\u5385\u95e8\u3002 \u3000\u3000\u4f46\u65c1\u4eba\u4e00\u65f6\u5fd8\u8bb0\u4e86\u4ed6\uff0c\u5546\u8001\u592a\u53ef\u6ca1\u5fd8\u8bb0\uff0c\u5979\u53ea\u5728\u80d6\u5b50\u521d\u8fdb\u6765\u65f6\u77a7\u4e86\u4e00\u773c\uff0c\u76ee\u5149\u59cb\u7ec8\u4e0d\u79bb\u80e1\u6590\uff0c\u89c1\u4ed6\u8981\u9003\uff0c\u7acb\u65f6\u5389\u58f0\u559d\u547c\uff0c\u7eb5\u8eab\u800c\u524d\uff0c\u4f38\u638c\u5f80\u4ed6\u80cc\u5fc3\u62cd\u53bb\uff0c\u8fd9\u4e00\u638c\u6b63\u662f\u516b\u5366\u638c\u7edd\u62db\u4e4b\u4e00\u7684\u201c\u80cc\u5fc3\u9489\u201d\uff0c\u53ea\u8981\u62cd\u4e2d\u4e86\uff0c\u5f53\u573a\u8981\u53eb\u4ed6\u9aa8\u65ad\u810f\u88c2\uff0c\u5455\u8840\u800c\u6b7b\u3002\u90a3\u80d6\u5b50\u89c1\u5979\u4ee5\u5982\u6b64\u6bd2\u8fa3\u624b\u6cd5\u5bf9\u4ed8\u4e00\u4e2a\u5b69\u5b50\uff0c\u201c\u566b\u201d\u4e86\u4e00\u58f0\uff0c\u6b63\u8981\u51fa\u624b\u76f8\u6551\uff0c\u5374\u89c1\u80e1\u6590\u8eab\u5f62\u4e00\u52a8\uff0c\u5de6\u624b\u5012\u94a9\uff0c\u5e26\u7740\u5979\u624b\u638c\u5f80\u65c1\u4e00\u7529\uff0c\u4fbf\u5c06\u8fd9\u8bb0\u7edd\u62db\u5316\u89e3\u4e86\u3002\u5546\u8001\u592a\u4e00\u4e2a\u8e09\u8dc4\uff0c\u8dcc\u51fa\u4e09\u6b65\u65b9\u624d\u7ad9\u5b9a\u3002\u90a3\u80d6\u5b50\u89c1\u80e1\u6590\u7626\u7626\u5c0f\u5c0f\u7684\u4e00\u4e2a\u5b69\u5b50\u5c45\u7136\u6709\u6b64\u6b66\u529f\uff0c\u5927\u662f\u60ca\u5947\uff0c\u4e0d\u7531\u5f97\u8fde\u8fde\u5411\u4ed6\u671b\u4e86\u51e0\u773c\u3002 \u3000\u3000\u738b\u5251\u82f1\u89c1\u4e86\u8fd9\u4e2a\u80d6\u5b50\uff0c\u4f9d\u7a00\u6709\u4e9b\u9762\u719f\uff0c\u4e00\u65f6\u5374\u60f3\u4e0d\u8d77\u6765\uff0c\u62b1\u62f3\u8bf4\u9053\uff1a\u201c\u5c0a\u9a7e\u9ad8\u59d3\u5927\u540d\uff1f\u66ae\u591c\u5149\u4e34\uff0c\u6709\u4f55\u89c1\u6559\uff1f\u201d\u90a3\u80d6\u5b50\u62b1\u62f3\u8fd8\u793c\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u6562\uff0c\u5144\u5f1f\u59d3\u8d75\u3002\u201d\u738b\u5251\u82f1\u731b\u5730\u7701\u8d77\uff0c\u8bf4\u9053\uff1a\u201c\u554a\uff0c\u539f\u6765\u662f\u7ea2\u82b1\u4f1a\u8d75\u4e09\u7237\u5149\u4e34\uff0c\u771f\u5f97\u6055\u5c0f\u5f1f\u773c\u62d9\u3002\u201d\u7fa4\u8c6a\u4e00\u542c\uff0c\u773c\u524d\u6b64\u4eba\u7adf\u662f\u7ea2\u82b1\u4f1a\u7684\u5927\u5934\u9886\u5343\u624b\u5982\u6765\u8d75\u534a\u5c71\uff0c\u65e0\u4e0d\u8038\u7136\u52a8\u5bb9\u3002 \u3000\u3000\u516d\u5e74\u524d\u7ea2\u82b1\u4f1a\u82f1\u96c4\u706b\u70e7\u96cd\u548c\u5bab\uff0c\u5927\u95f9\u7d2b\u7981\u57ce\uff0c\u4e43\u662f\u8f70\u52a8\u6b66\u6797\u7684\u5927\u4e8b\uff0c\u5929\u4e0b\u7686\u77e5\uff08\u8bf7\u53c2\u9605\u62d9\u4f5c\u300a\u4e66\u5251\u6069\u4ec7\u5f55\u300b\uff09\u3002\u6b64\u540e\u7ea2\u82b1\u4f1a\u4fbf\u9ed8\u9ed8\u65e0\u95fb\uff0c\u6c5f\u6e56\u4e0a\u4f20\u8a00\uff0c\u7fa4\u96c4\u8c79\u9690\u56de\u7586\uff0c\u4e0d\u6599\u8d75\u534a\u5c71\u7a81\u7136\u5728\u6b64\u51fa\u73b0\u3002\u738b\u5251\u82f1\u5e74\u8f7b\u65f6\u66fe\u5728\u6f02\u5c40\u4e2d\u89c1\u8fc7\u4ed6\u4e00\u9762\uff0c\u4f46\u4e8b\u9694\u4e8c\u5341\u4f59\u5e74\uff0c\u8d75\u534a\u5c71\u65e9\u5df2\u975e\u590d\u65e7\u65f6\u5bb9\u989c\uff0c\u56e0\u6b64\u521d\u89c1\u9762\u65f6\u7adf\u7136\u96be\u4ee5\u5fc6\u53ca\u3002\u6b64\u65f6\u4ed6\u52a0\u500d\u7559\u795e\uff0c\u6ee1\u8138\u5806\u6b22\u4ed6\u8bf4\u9053\uff1a\u201c\u8d75\u4e09\u7237\u662f\u4e00\u4eba\u524d\u6765\u5c71\u4e1c\uff0c\u8fd8\u662f\u7ea2\u82b1\u4f1a\u4f17\u4f4d\u82f1\u96c4\u4e00\u9f50\u51fa\u5c71\u4e86\uff1f\u5148\u7236\u751f\u524d\u5e38\u63d0\u53ca\u7ea2\u82b1\u4f1a\u4f17\u4f4d\u82f1\u96c4\uff0c\u597d\u751f\u8bb0\u6302\u3002\u201d \u3000\u3000\u8d75\u534a\u5c71\u6027\u5b50\u6148\u548c\uff0c\u80f8\u65e0\u57ce\u5e9c\uff0c\u8ddf\u8c01\u90fd\u5408\u5f97\u6765\uff0c\u968f\u53e3\u7b54\u9053\uff1a\u201c\u662f\u5c0f\u5f1f\u4e00\u4eba\u6709\u70b9\u79c1\u4e8b\uff0c\u6765\u5230\u5c71\u4e1c\u3002\u8bf7\u95ee\u4ee4\u5c0a\u662f\u2026\u2026\u201d\u738b\u5251\u82f1\u542c\u5f97\u4ed6\u53ea\u6709\u4e00\u4eba\uff0c\u653e\u4e0b\u4e86\u4e00\u5927\u534a\u5fc3\uff0c\u6697\u9053\uff1a\u201c\u82e5\u662f\u4ed6\u4f1a\u4e2d\u5144\u5f1f\u503e\u5de2\u800c\u51fa\uff0c\u5728\u8fd9\u91cc\u649e\u89c1\u4e86\u53ef\u4e0d\u597d\u529e\u3002\u201d\u4e8e\u662f\u7b54\u9053\uff1a\u201c\u5148\u7236\u662f\u9547\u8fdc\u9556\u5c40\u2026\u2026\u201d\u8d75\u534a\u5c71\u63a5\u53e3\u9053\uff1a\u201c\u554a\uff0c\u539f\u6765\u662f\u738b\u8001\u9556\u5934\u7684\u8d24\u90ce\uff0c\u600e\u4e48\u8001\u9576\u5934\u4ed9\u6e38\u4e86\u5566\uff1f\u201d\u8138\u4e0a\u795e\u8272\u9eef\u7136\uff0c\u5374\u662f\u771f\u6b63\u7684\u96be\u8fc7\u3002\u738b\u5251\u82f1\u9053\uff1a\u201c\u5148\u7236\u5df2\u53bb\u4e16\u4e94\u5e74\u4e86\u3002\u8fd9\u662f\u820d\u5f1f\u5251\u6770\u3002\u201d\u4ed6\u8f6c\u5934\u5411\u738b\u5251\u6770\u8bf4\u9053\uff1a\u201c\u8d75\u4e09\u7237\u592a\u6781\u62f3\u3001\u592a\u6781\u5251\u3001\u6697\u5668\u529f\u592b\uff0c\u4e09\u7edd\u5929\u4e0b\u65e0\u53cc\uff0c\u4eca\u65e5\u771f\u662f\u5e78\u4f1a\u3002\u201d \u3000\u3000\u4ed6\u6b63\u8981\u66ff\u5404\u4eba\u5f15\u89c1\uff0c\u738b\u5251\u6770\u5fc3\u76f4\u53e3\u51b3\uff0c\u5df2\u63a5\u53e3\u9053\uff1a\u201c\u8fd9\u4f4d\u9648\u5144\u4e5f\u662f\u592a\u6781\u95e8\u7684\uff0c\u4e24\u4f4d\u672c\u6765\u76f8\u8bc6\u4e48\uff1f\u201d\u8bf4\u7740\u5411\u592a\u6781\u624b\u9648\u79b9\u4e00\u6307\u3002 \u3000\u3000\u8d75\u534a\u5c71\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u6148\u548c\u7684\u8138\u4e0a\u767b\u65f6\u73b0\u51fa\u4e00\u5c42\u9ed1\u6c14\uff0c\u5411\u9648\u79b9\u4ece\u5934\u770b\u5230\u811a\uff0c\u53c8\u4ece\u811a\u884c\u5230\u5934\uff0c\u7ec6\u7ec6\u6253\u91cf\u3002\u9648\u79b9\u89c1\u4ed6\u8138\u8272\u5ffd\u53d8\uff0c\u5fae\u89c9\u5c40\u4fc3\u4e0d\u5b89\uff0c\u7ed9\u4ed6\u8fd9\u4e48\u4e00\u77a7\uff0c\u66f4\u662f\u5c34\u5c2c\uff0c\u8d75\u534a\u5c71\u643a\u6765\u7684\u5973\u5b69\u7a81\u7136\u4f38\u624b\u6307\u7740\u4ed6\uff0c\u5927\u58f0\u9053\uff1a\u201c\u8d75\u53d4\u53d4\uff0c\u5c31\u662f\u4ed6\uff0c\u5c31\u662f\u4ed6\uff01\u201d\u58f0\u97f3\u5c16\u7ec6\uff0c\u8bed\u58f0\u4e2d\u5145\u6ee1\u4e86\u6124\u6012\u3002 \u3000\u3000\u9648\u79b9\u89c1\u8fd9\u5c0f\u5973\u5b69\u80a4\u8272\u5fae\u9ed1\uff0c\u8138\u4e0a\u6ee1\u662f\u75db\u6068\u4e4b\u8272\uff0c\u81ea\u5df1\u5374\u4ece\u672a\u89c1\u8fc7\uff0c\u5f53\u4e0b\u8f6c\u8fc7\u5934\u5411\u738b\u5251\u6770\u9053\uff1a\u201c\u8d75\u4e09\u7237\u662f\u5357\u6d3e\u6e29\u5dde\u592a\u6781\u95e8\uff0c\u5144\u5f1f\u662f\u76f4\u96b6\u5e7f\u5e73\u5e9c\u592a\u6781\u95e8\uff0c\u6211\u4eec\u662f\u540c\u6d3e\u4e0d\u540c\u5b97\u3002\u8d75\u4e09\u7237\u662f\u6211\u4eec\u524d\u8f88\uff0c\u5144\u5f1f\u5411\u6765\u4ef0\u6155\u5f97\u7d27\u3002\u201d\u8bf4\u7740\u8d70\u8fd1\u8eab\u53bb\uff0c\u62b1\u62f3\u4e3a\u793c\uff0c\u795e\u8272\u751a\u662f\u606d\u8c28\u3002 \u3000\u3000\u54ea\u77e5\u8d75\u534a\u5c71\u5b9b\u5982\u4e0d\u89c1\uff0c\u53cc\u624b\u8d1f\u5728\u80cc\u540e\uff0c\u5bf9\u4ed6\u4e0d\u7406\u4e0d\u776c\uff0c\u8f6c\u8eab\u5411\u738b\u5251\u82f1\u9053\uff1a\u201c\u738b\u5144\uff0c\u5144\u5f1f\u4eca\u65e5\u6765\u5f97\u9c81\u83bd\uff0c\u5148\u5411\u5404\u4f4d\u8c22\u8fc7\u3002\u201d\u8bf4\u7740\u56e2\u56e2\u4f5c\u63d6\u3002\u4f17\u4eba\u8fde\u5fd9\u8fd8\u793c\uff0c\u90fd\u9053\uff1a\u201d\u597d\u8bf4\u597d\u8bf4\uff0c\u8d75\u4e09\u7237\u592a\u5ba2\u6c14\u4e86\u3002\u201d\u53ea\u628a\u9648\u79b9\u6c14\u5f97\u534a\u8eab\u51b0\u51c9\uff0c\u62f1\u7740\u7684\u624b\u4e00\u65f6\u653e\u4e0d\u4e0b\u6765\uff0c\u50f5\u5728\u5f53\u5730\uff0c\u5fc3\u9053\uff1a\u201c\u6211\u51e0\u65f6\u5f97\u7f6a\u4f60\u4e86\uff1f\u4f60\u540d\u5934\u867d\u5927\uff0c\u96be\u9053\u6211\u5f53\u771f\u6015\u4e86\u4f60\u4e0d\u6210\uff1f\u201d \u3000\u3000\u738b\u5251\u82f1\u6307\u7740\u80e1\u6590\u9053\uff1a\u201c\u8fd9\u4f4d\u5c0f\u5144\u5f1f\u8ddf\u6211\u5f1f\u59b9\u6709\u70b9\u8fc7\u8282\uff0c\u90a3\u4e5f\u662f\u4ed6\u4e0a\u4ee3\u7ed3\u4e0b\u6765\u7684\u6881\u5b50\u3002\u73b0\u4e0b\u6211\u5e08\u5f1f\u4eba\u4e5f\u8fc7\u4e16\u591a\u5e74\u4e86\uff0c\u6211\u4eec\u51b2\u7740\u8d75\u4e09\u7237\u7684\u91d1\u9762\uff0c\u8fd9\u4ef6\u4e8b\u63ed\u8fc7\u4e0d\u63d0\u3002\u5927\u5bb6\u7f62\u624b\u5982\u4f55\uff1f\u201d\u8bf4\u7740\u54c8\u54c8\u5927\u7b11\u3002\u539f\u6765\u4ed6\u4e0e\u5546\u5251\u9e23\u5411\u6765\u4e0d\u548c\uff0c\u672c\u5c31\u65e0\u610f\u4e3a\u4ed6\u62a5\u4ec7\uff0c\u6b64\u65f6\u66f4\u60f3\u5356\u8d75\u534a\u5c71\u4e00\u4e2a\u597d\u3002\u8d75\u534a\u5c71\u6115\u7136\u4e0d\u89e3\u3002\u5546\u8001\u592a\u5374\u5df2\u53eb\u4e86\u8d77\u6765\uff0c\u9a82\u9053\uff1a\u201c\u4ec0\u4e48\u8d75\u534a\u5c71\uff0c\u8d75\u4e00\u5c71\uff0c\u5230\u5f97\u5546\u5bb6\u5821\u6765\uff0c\u8c01\u90fd\u522b\u60f3\u6492\u91ce\uff01\u201d \u3000\u3000\u8d75\u534a\u5c71\u9053\uff1a\u201c\u738b\u5144\u8bf4\u7684\u662f\u4ec0\u4e48\uff0c\u5c0f\u5f1f\u53ef\u4e0d\u660e\u767d\u3002\u201d\u738b\u5251\u82f1\u9053\uff1a\u201c\u6211\u8fd9\u5f1f\u59b9\u662f\u5987\u9053\u4eba\u5bb6\uff0c\u8d75\u4e09\u7237\u522b\u7406\u4f1a\u5979\u3002\u6765\u6765\u6765\uff0c\u5c0f\u5f1f\u501f\u82b1\u732e\u4f5b\uff0c\u656c\u8d75\u4e09\u7237\u4e00\u676f\u3002\u201d\u8bf4\u7740\u4fbf\u53bb\u659f\u9152\u3002 \u3000\u3000\u80e1\u6590\u77e5\u9053\u518d\u8bf4\u4e0b\u53bb\uff0c\u81ea\u5df1\u7684\u8c0e\u8bdd\u7acb\u65f6\u5c31\u8981\u62c6\u7a7f\uff0c\u4e8e\u662f\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u8d75\u4e09\u7237\uff0c\u8fd9\u4e9b\u996d\u6876\u5439\u725b\uff0c\u90a3\u4e5f\u7f62\u4e86\u3002\u4ed6\u4eec\u5374\u8bf4\u7ea2\u82b1\u4f1a\u4e2a\u4e2a\u90fd\u662f\u8113\u5305\uff0c\u53c8\u8bf4\u516b\u5366\u638c\u7684\u529f\u592b\u5929\u4e0b\u65e0\u654c\uff0c\u8bf4\u4ed6\u4eec\u95e8\u4e2d\u7684\u8001\u82f1\u96c4\u5355\u51ed\u4e00\u67c4\u516b\u5366\u5200\uff0c\u6253\u8d25\u4e86\u7ea2\u82b1\u4f1a\u6240\u6709\u4eba\u7269\u3002\u5c0f\u7684\u542c\u4e0d\u8fc7\u4e86\uff0c\u56e0\u6b64\u51fa\u6765\u8bad\u65a5\u3002\u4ed6\u4eec\u5374\u504f\u751f\u4e0d\u670d\uff0c\u8ddf\u6211\u52a8\u624b\u3002\u8d75\u4e09\u7237\uff0c\u4f60\u8bf4\u6c14\u4eba\u4e0d\u6c14\u4eba\uff1f\u8fd9\u4e2a\u7406\u8981\u8bf7\u4f60\u6765\u8bc4\u4e00\u8bc4\u4e86\u3002\u201d \u3000\u3000\u8d75\u534a\u5c71\u5168\u4e0d\u77e5\u4ed6\u4eec\u4e89\u4e9b\u4ec0\u4e48\uff0c\u4f46\u5f53\u5e74\u738b\u7ef4\u626c\u66fe\u548c\u7ea2\u82b1\u4f1a\u5bf9\u654c\uff0c\u8fd9\u4ef6\u4e8b\u5374\u662f\u6709\u7684\uff0c\u7ea2\u82b1\u4f1a\u4e5f\u6ca1\u51ed\u6b66\u529b\u80dc\u4ed6\uff0c\u53ea\u662f\u4f7f\u8ba1\u903c\u5f97\u4ed6\u670d\u8f93\uff0c\u60f3\u6765\u738b\u5251\u82f1\u3001\u5251\u6770\u5144\u5f1f\u8bf4\u8d77\u6b64\u4e8b\u65f6\uff0c\u5b9a\u662f\u5938\u4ed6\u7236\u4eb2\u82f1\u96c4\u4e86\u5f97\uff0c\u90a3\u4e5f\u662f\u4eba\u60c5\u4e4b\u5e38\uff0c\u4e8e\u662f\u4fbf\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u738b\u8001\u9556\u5934\u6b66\u529f\u9ad8\u5f3a\uff0c\u6211\u4eec\u4f17\u5144\u5f1f\u4e2a\u4e2a\u90fd\u662f\u5341\u5206\u4f69\u670d\u7684\u3002\u201d\u7a81\u7136\u95f4\u76ee\u5149\u5982\u7535\uff0c\u5c04\u5411\u9648\u79b9\uff0c\u8bf4\u9053\uff1a\u201c\u9648\u5e08\u5085\uff0c\u8bf7\u4f60\u8ddf\u6211\u51fa\u53bb\uff0c\u54b1\u4eec\u501f\u4e00\u6b65\u8bf4\u8bdd\u3002\u201d \u3000\u3000\u9648\u79b9\u5fc3\u4e2d\u4e00\u51db\uff0c\u8bf4\u9053\uff1a\u201c\u5728\u4e0b\u548c\u8d75\u4e09\u7237\u7d20\u4e0d\u76f8\u8bc6\uff0c\u4e0d\u77e5\u6709\u4f55\u5429\u5490\uff1f\u8fd9\u513f\u5404\u4f4d\u670b\u53cb\u90fd\u662f\u5149\u660e\u78ca\u843d\u7684\u597d\u6c49\u5b50\uff0c\u6709\u8bdd\u5c31\u8bf7\u5728\u6b64\u660e\u8bf4\u4e0d\u59a8\u3002\u201d\u8d75\u534a\u5c71\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8fd9\u662f\u6211\u592a\u6781\u95e8\u95e8\u6237\u4e4b\u803b\uff0c\u4f55\u5fc5\u8ba9\u65c1\u4eba\u77e5\u6653\uff1f\u201d\u9648\u79b9\u8138\u4e0a\u53d8\u8272\uff0c\u9000\u540e\u4e00\u6b65\uff0c\u6717\u58f0\u9053\uff1a\u201c\u4f60\u662f\u6e29\u5dde\u592a\u6781\uff0c\u6211\u662f\u5e7f\u5e73\u592a\u6781\u3002\u54b1\u4eec\u540c\u6d3e\u4e0d\u540c\u5b97\u3002\u6211\u7ba1\u4e0d\u7740\u4f60\uff0c\u4f60\u4e5f\u7ba1\u4e0d\u7740\u6211\u3002\u201d\u8d75\u534a\u5c71\u9053\uff1a\u201c\u5c31\u53ea\u4e3a\u9648\u5144\u624b\u6bb5\u592a\u8fc7\u5389\u5bb3\uff0c\u5e7f\u5e73\u5e9c\u592a\u6781\u95e8\u6ca1\u4eba\u6562\u51fa\u5934\uff0c\u5144\u5f1f\u624d\u4e07\u91cc\u8fe2\u8fe2\u5730\u4ece\u56de\u7586\u8d76\u6765\u3002\u5144\u5f1f\u5230\u4e86\u5317\u4eac\uff0c\u542c\u8bf4\u9648\u5144\u5230\u5c71\u4e1c\u6765\u5566\uff0c\u4e00\u8def\u5bfb\u8bbf\u800c\u6765\uff0c\u603b\u7b97\u662f\u5929\u7f51\u6062\u6062\u3002\u201d \u3000\u3000\u4f17\u4eba\u542c\u4ed6\u7528\u5230\u201c\u5929\u7f51\u6062\u6062\u201d\u56db\u5b57\uff0c\u90fd\u662f\u5403\u4e86\u4e00\u60ca\uff0c\u4e0d\u77e5\u9648\u79b9\u5728\u95e8\u6237\u4e2d\u5e72\u4e86\u4ec0\u4e48\u6b79\u4e8b\uff0c\u7d2f\u5f97\u8fd9\u4f4d\u8d75\u4e09\u5f53\u5bb6\u4e07\u91cc\u8ffd\u5bfb\u3002 \u3000\u3000\u9648\u79b9\u7cbe\u660e\u5f3a\u5e72\uff0c\u5728\u6c5f\u6e56\u4e0a\u6210\u540d\u591a\u5e74\uff0c\u540d\u5934\u56fa\u4e0d\u53ca\u8d75\u534a\u5c71\u54cd\u4eae\uff0c\u5374\u4e5f\u662f\u5317\u6d3e\u592a\u6781\u95e8\u7684\u4f7c\u997a\u8005\uff0c\u4f55\u51b5\u8ddf\u4e86\u798f\u516c\u5b50\u540e\uff0c\u6709\u4e86\u6781\u5f3a\u7684\u9760\u5c71\uff0c\u5bf9\u8d75\u534a\u5c71\u6beb\u4e0d\u754f\u60e7\uff0c\u5389\u58f0\u9053\uff1a\u201c\u6211\u5148\u524d\u5c0a\u4f60\u4e00\u58f0\u524d\u8f88\uff0c\u90a3\u662f\u77a7\u5728\u4f60\u7684\u5e74\u7eaa\u4efd\u4e0a\u3002\u4f60\u6211\u5357\u5317\u592a\u6781\u5404\u6709\u6240\u957f\uff0c\u51ed\u4f60\u5c31\u80fd\u538b\u5f97\u4e86\u6211\u5417\uff1f\u201d\u8bed\u58f0\u752b\u6bd5\uff0c\u4e00\u62db\u201c\u7389\u5973\u7a7f\u68ad\u201d\uff0c\u731b\u5411\u4ed6\u80a9\u5934\u62cd\u53bb\u3002 \u3000\u3000\u8d75\u534a\u5c71\u8ffd\u5954\u6570\u6708\uff0c\u8f9b\u52b3\u4e07\u91cc\uff0c\u4e3a\u7684\u5c31\u662f\u773c\u524d\u8fd9\u4e00\u62db\uff0c\u4e00\u89c1\u9648\u79b9\u51fa\u624b\uff0c\u4ece\u8fd9\u62db\u201c\u7389\u5973\u7a7f\u68ad\u201d\u4e4b\u4e2d\uff0c\u4e8e\u4ed6\u6b66\u529f\u4fee\u4e3a\u5df2\u4e86\u7136\u4e8e\u80f8\uff0c\u5f53\u4e0b\u8eab\u8eaf\u5fae\u8e72\uff0c\u4e00\u62db\u201c\u4e91\u624b\u201d\uff0c\u5e26\u4f4f\u4ed6\u7684\u624b\u8155\u5411\u53f3\u4e00\u5f15\u3002\u9648\u79b9\u7acb\u8db3\u4e0d\u5b9a\uff0c\u767b\u65f6\u5168\u8eab\u53d7\u5236\u3002\u8981\u77e5\u5404\u6d3e\u592a\u6781\uff0c\u62f3\u62db\u90fd\u662f\u5927\u540c\u5c0f\u5f02\uff0c\u5f3a\u5f31\u5dee\u522b\u5168\u5728\u5404\u4eba\u7684\u609f\u6027\u4e0e\u529f\u529b\u4e0d\u540c\u3002 \u3000\u3000\u5929\u9f99\u95e8\u597d\u624b\u6bb7\u4ef2\u7fd4\u662f\u9648\u79b9\u81f3\u4ea4\uff0c\u5f53\u8d75\u9648\u4e8c\u4eba\u53e3\u5934\u76f8\u4e89\u4e4b\u65f6\uff0c\u4ed6\u5df2\u62d4\u5251\u5728\u624b\uff0c\u8dc3\u8dc3\u6b32\u8bd5\uff0c\u773c\u89c1\u9648\u79b9\u4e00\u62db\u5373\u8d25\uff0c\u4fbf\u5373\u633a\u5251\u5411\u8d75\u534a\u5c71\u8eab\u540e\u523a\u53bb\uff0c\u559d\u9053\uff1a\u201c\u653e\u624b\uff01\u201d\u8d75\u534a\u5c71\u66f4\u4e0d\u56de\u8eab\uff0c\u987a\u624b\u5728\u9648\u79b9\u8170\u95f4\u62bd\u51fa\u4f69\u5251\uff0c\u56de\u5251\u4e00\u6321\u3002\u8fd9\u4e00\u4e0b\u5206\u5bf8\u62ff\u634f\u5f97\u6070\u5230\u597d\u5904\uff0c\u53cc\u5251\u4e00\u4ea4\uff0c\u5f53\u7684\u4e00\u58f0\uff0c\u6bb7\u4ef2\u7fd4\u7684\u957f\u5251\u5df2\u65ad\u6210\u4e24\u622a\u3002\u8d75\u534a\u5c71\u53f3\u624b\u4e00\u9001\uff0c\u53c8\u5c06\u957f\u5251\u63d2\u5165\u9648\u79b9\u8170\u95f4\u5251\u9798\u3002 \u3000\u3000\u7fa4\u8c6a\u89c1\u4ed6\u4e00\u62db\u5236\u4f4f\u592a\u6781\u95e8\u597d\u624b\u9648\u79b9\uff0c\u4e00\u5251\u9707\u65ad\u4e86\u5929\u9f99\u95e8\u597d\u624b\u6bb7\u4ef2\u7fd4\u957f\u5251\uff0c\u5236\u654c\u62f3\u6cd5\u4e4b\u7cbe\uff0c\u62d4\u5251\u51fa\u624b\u4e4b\u5feb\uff0c\u65ad\u5251\u529f\u529b\u4e4b\u7eaf\uff0c\u8fd8\u5251\u773c\u529b\u4e4b\u51c6\uff0c\u7686\u662f\u751f\u5e73\u7f55\u89c1\uff0c\u4e0d\u7531\u5f97\u5c3d\u7686\u5931\u8272\u3002 \u3000\u3000\u8d75\u534a\u5c71\u5411\u9648\u79b9\u51b7\u7136\u9053\uff1a\u201c\u600e\u4e48\uff1f\u4f60\u51fa\u4e0d\u51fa\u53bb\uff1f\u201d\u9648\u79b9\u8138\u4e0a\u9752\u4e00\u9635\u7ea2\u4e00\u9635\uff0c\u60ca\u60f6\u4e0d\u5b9a\u3002 \u3000\u3000\u7a81\u7136\u95f4\u91d1\u5149\u95ea\u52a8\uff0c\u4e03\u679d\u91d1\u9556\u5206\u4ece\u4e0a\u4e0b\u5de6\u53f3\u5411\u80e1\u6590\u6025\u5c04\u8fc7\u53bb\u3002\u539f\u6765\u5546\u8001\u592a\u773c\u89c1\u62a5\u4ec7\u4e4b\u671b\u884c\u5c06\u6210\u7a7a\uff0c\u89c1\u4f17\u4eba\u6ce8\u76ee\u8d75\u9648\u4e8c\u4eba\uff0c\u6b63\u662f\u826f\u673a\uff0c\u731b\u5730\u4e00\u53e3\u6c14\u540c\u65f6\u53d1\u51fa\u4e03\u679d\u91d1\u9556\u3002\u5979\u4e0e\u80e1\u6590\u76f8\u8ddd\u4e0d\u8fc7\u4e08\u8bb8\uff0c\u8fd9\u4e00\u4e0b\u9661\u7136\u53d1\u96be\uff0c\u5bf9\u65b9\u8981\u80fd\u5c06\u4e03\u679d\u91d1\u7f25\u5c3d\u6570\u8eb2\u8fc7\uff0c\u5f53\u771f\u662f\u5343\u96be\u4e07\u96be\u3002\u5979\u5341\u4f59\u5e74\u6765\u5904\u5fc3\u79ef\u8651\u5730\u8981\u4e3a\u4e08\u592b\u590d\u4ec7\uff0c\u77e5\u9053\u82d7\u4eba\u51e4\u4e0e\u80e1\u4e00\u5200\u6b66\u529f\u5353\u7edd\uff0c\u5149\u660e\u6b63\u5927\u7684\u52a8\u624b\uff0c\u7edd\u96be\u53d6\u80dc\uff0c\u56e0\u6b64\u9556\u4e0a\u90fd\u5582\u4e86\u89c1\u8840\u5c01\u5589\u7684\u5267\u6bd2\u3002 \u3000\u3000\u8fd9\u4e00\u4e0b\u7a81\u5982\u5176\u6765\uff0c\u80e1\u6590\u53eb\u58f0\uff1a\u201c\u554a\u54df\uff01\u201d\u6025\u5fd9\u6251\u5012\uff0c\u4e0a\u9762\u4e09\u679d\u7f25\u867d\u80fd\u907f\u8fc7\uff0c\u6253\u5411\u4ed6\u5c0f\u8179\u548c\u4e0b\u76d8\u7684\u56db\u679d\u9556\u5374\u518d\u4e5f\u65e0\u6cd5\u95ea\u8eb2\u3002 \u3000\u3000\u8d75\u534a\u5c71\u8de8\u4e0a\u4e00\u6b65\uff0c\u4f38\u51fa\u957f\u81c2\uff0c\u4e00\u635e\u4e00\u6284\uff0c\u534a\u8def\u4e0a\u5c06\u4e03\u679d\u7f25\u5c3d\u6570\u63a5\u5728\u624b\u4e2d\u3002 \u3000\u3000\u4ed6\u5916\u53f7\u53eb\u505a\u201c\u5343\u624b\u5982\u6765\u201d\uff0c\u201c\u5982\u6765\u201d\u662f\u8bf4\u4ed6\u9762\u548c\u5fc3\u6148\uff0c\u201c\u5343\u624b\u201d\u5374\u662f\u8bf4\u4ed6\u53d1\u6697\u5668\u3001\u63a5\u6697\u5668\uff0c\u5c31\u50cf\u751f\u4e86\u4e00\u5343\u53ea\u624b\u4e00\u822c\uff0c\u8fd9\u6284\u63a5\u6697\u5668\uff0c\u6b63\u662f\u4ed6\u751f\u5e73\u6700\u64c5\u957f\u7684\u7edd\u6280\u3002\u4f17\u4eba\u53ea\u89c9\u773c\u524d\u4e00\u82b1\uff0c\u4e5f\u6ca1\u770b\u6e05\u4ed6\u5982\u4f55\u51fa\u624b\uff0c\u4e03\u679d\u91d1\u7f25\u5df2\u5230\u4e86\u4ed6\u624b\u4e2d\u3002 \u3000\u3000\u522b\u8bf4\u4e03\u679d\uff0c\u5c31\u4e03\u4e03\u56db\u5341\u4e5d\u679d\u91d1\u7f25\u9f50\u53d1\uff0c\u4ed6\u4e5f\u4e0d\u653e\u5728\u773c\u4e2d\u3002\u70db\u5149\u4e0b\u89c1\u9556\u5934\u5e26\u7740\u6697\u7ea2\u4e4b\u8272\uff0c\u62ff\u5230\u9f3b\u8fb9\u4e00\u55c5\uff0c\u679c\u6709\u4e00\u80a1\u751c\u9999\uff0c\u77e5\u9053\u9556\u5c16\u5e26\u6709\u5267\u6bd2\u3002\u4ed6\u662f\u4f7f\u6697\u5668\u7684\u5927\u9ad8\u624b\uff0c\u5374\u6700\u6068\u65c1\u4eba\u5728\u6697\u5668\u4e4b\u4e0a\u5582\u6bd2\uff0c\u5e38\u8a00\u9053\uff1a\u201d\u6697\u5668\u539f\u662f\u6b63\u6d3e\u5175\u5668\uff0c\u4ee5\u5c0f\u53ca\u8fdc\uff0c\u4e0e\u62f3\u811a\u5668\u68b0\uff0c\u540c\u4e3a\u6b66\u5b66\u4e09\u5927\u95e8\u4e4b\u4e00\uff0c\u53ea\u662f\u7ed9\u65e0\u803b\u5c0f\u4eba\u4e00\u5582\u6bd2\uff0c\u8fd9\u624d\u8ba9\u4eba\u77a7\u4f4e\u4e86\u3002\u201d \u3000\u3000\u4ed6\u56de\u8fc7\u5934\u6765\uff0c\u5411\u5546\u8001\u592a\u72e0\u72e0\u671b\u4e86\u4e00\u773c\uff0c\u8bf4\u9053\uff1a\u201c\u738b\u7ef4\u626c\u738b\u8001\u7237\u5b50\u4f55\u7b49\u82f1\u96c4\uff0c\u4ed6\u6559\u4eba\u6697\u5668\u5582\u6bd2\u4e48\uff1f\u6559\u4eba\u8fd9\u822c\u5351\u9119\u5077\u88ad\u4e48\uff1f\u66f4\u4f55\u51b5\u4ee5\u8fd9\u822c\u624b\u6bb5\u5bf9\u4ed8\u4e00\u4e2a\u5c0f\u5b69\u3002\u201d\u8fd9\u51e0\u53e5\u8bdd\u5927\u4e49\u51db\u7136\uff0c\u738b\u6c0f\u5144\u5f1f\u4e0d\u7531\u5f97\u6697\u81ea\u60ed\u6127\u3002 \u3000\u3000\u5546\u8001\u592a\u89c1\u738b\u6c0f\u5144\u5f1f\u4f4e\u4e0b\u4e86\u5934\uff0c\u5927\u58f0\u9053\uff1a\u201c\u4f60\u662f\u4ec0\u4e48\u4e1c\u897f\uff0c\u7adf\u7136\u4e0a\u5546\u5bb6\u5821\u6765\u6b3a\u4eba\uff1f\u53ea\u53ef\u53f9\u6211\u5148\u592b\u5546\u5251\u9e23\u6b7b\u540e\uff0c\u516b\u5366\u95e8\u4e2d\u518d\u65e0\u82f1\u96c4\u597d\u6c49\u3002\u6211\u513f\u5b50\u5e74\u5e7c\uff0c\u8001\u5a46\u5b50\u662f\u5973\u6d41\u4e4b\u8f88\uff0c\u53ea\u597d\u5bb9\u5f97\u4f60\u6b3a\u4fae\u3002\u201d\u5ffd\u7136\u653e\u58f0\u54ed\u9053\uff1a\u201c\u5251\u9e23\u554a\uff0c\u4f60\u4e00\u6b7b\u4e4b\u540e\uff0c\u516b\u5366\u95e8\u5c31\u53ea\u5269\u4e0b\u4e00\u6279\u72d7\u718a\u4e86\uff0c\u53ea\u77e5\u9053\u5949\u627f\u5916\u4eba\uff0c\u518d\u6ca1\u534a\u4e2a\u6709\u9aa8\u6c14\u4e4b\u4eba\uff0c\u80fd\u7ed9\u95e8\u6237\u4e89\u4e00\u53e3\u6c14\u3002\u5251\u9e23\u554a\uff0c\u8d76\u660e\u513f\u8d77\uff0c\u6211\u53eb\u4f60\u513f\u5b50\u6539\u6295\u592a\u6781\u95e8\uff0c\u522b\u8ba9\u4ed6\u5728\u6c5f\u6e56\u4e0a\u7070\u5934\u571f\u8138\uff0c\u4e00\u8f88\u5b50\u8ba9\u4eba\u770b\u8f7b\u4e86\u3002\u5251\u9e23\u554a\uff0c\u60f3\u5f53\u5e74\u4f60\u4f55\u7b49\u82f1\u96c4\uff0c\u65e9\u77e5\u4eca\u65e5\u5982\u6b64\uff0c\u8fd9\u67c4\u516b\u5366\u5200\u4f60\u5c31\u8be5\u5e26\u5165\u68fa\u6750\uff0c\u4e5f\u514d\u5f97\u5728\u8fd9\u91cc\u51fa\u4e11\u9732\u4e56\u3002\u201d\u5979\u54ed\u4e00\u58f0\uff0c\u9a82\u51e0\u53e5\uff0c\u5c06\u516b\u5366\u5200\u629b\u5728\u5730\u4e0b\uff0c\u53c8\u7528\u811a\u8e0f\uff0c\u53c8\u5410\u553e\u6cab\u3002\u53ea\u6c14\u5f97\u738b\u6c0f\u5144\u5f1f\u6ee1\u8154\u6012\u706b\uff0c\u53ef\u53c8\u4e0d\u80fd\u5f53\u7740\u5916\u4eba\u4e4b\u9762\u548c\u5979\u4e89\u5435\u3002 \u3000\u3000\u8d75\u534a\u5c71\u6025\u6b32\u5e26\u7740\u9648\u79b9\u79bb\u53bb\uff0c\u53ea\u662f\u89c1\u5546\u8001\u592a\u4ee5\u5982\u6b64\u6bd2\u8fa3\u624b\u6bb5\u5bf9\u4ed8\u80e1\u6590\uff0c\u81ea\u5df1\u4e00\u53bb\uff0c\u8fd9\u5c0f\u5b69\u5fc5\u906d\u6bd2\u624b\u3002\u4ed6\u867d\u4e0e\u80e1\u6590\u6beb\u65e0\u74dc\u845b\uff0c\u4f46\u4e8b\u89c1\u4e0d\u5e73\uff0c\u7109\u80fd\u8896\u624b\u4e0d\u7406\uff1f\u5411\u738b\u6c0f\u5144\u5f1f\u62b1\u62f3\u9053\uff1a\u201d\u8fd9\u5b69\u5b50\u6211\u4eca\u65e5\u5c31\u5e26\u4e86\u53bb\uff0c\u65e5\u540e\u518d\u8c22\u4e8c\u4f4d\u76db\u60c5\u3002\u201d \u3000\u3000\u738b\u5251\u82f1\u8fd8\u672a\u7b54\u8bdd\uff0c\u5546\u8001\u592a\u5374\u53c8\u54ed\u53eb\u8d77\u6765\uff1a\u201c\u5251\u9e23\u554a\uff0c\u4f60\u65e9\u65e9\u6b7b\u4e86\u5012\u4e5f\u5e72\u51c0\uff0c\u4e0d\u5fc5\u89c1\u5230\u8fd9\u822c\u4e22\u4eba\u73b0\u773c\u4e4b\u4e8b\u3002\u4f60\u5e08\u5f1f\u53f7\u79f0\u516b\u5366\u95e8\u9ad8\u624b\uff0c\u5374\u6597\u4e0d\u8fc7\u4e00\u4e2a\u5341\u591a\u5c81\u7684\u5b69\u5b50\uff0c\u8fde\u770b\u5bb6\u95e8\u7684\u4e00\u67c4\u5200\u4e5f\u8ba9\u4eba\u5bb6\u593a\u4e86\u3002\u4f60\u5e08\u5144\u66f4\u52a0\u6015\u90a3\u5c0f\u5b69\uff0c\u53ea\u76fc\u4ed6\u5feb\u4e9b\u8fdc\u8fdc\u79bb\u5f00\u2026\u2026\u201d \u3000\u3000\u738b\u5251\u82f1\u7ed9\u5979\u6fc0\u5f97\u518d\u4e5f\u5fcd\u8010\u4e0d\u4f4f\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u4f4f\u5634\uff01\u201d\u8f6c\u8eab\u5411\u8d75\u534a\u5c71\u9053\uff1a\u201c\u8d75\u4e09\u7237\uff0c\u9002\u624d\u6211\u5f1f\u59b9\u4e4b\u8a00\uff0c\u4f60\u90fd\u542c\u89c1\u5566\u3002\u4eca\u65e5\u4e0d\u662f\u5728\u4e0b\u4e0d\u7ed9\u8d75\u4e09\u7237\u8fd9\u4e2a\u9762\u5b50\uff0c\u53ea\u662f\u82e5\u51ed\u8fd9\u5c0f\u5b69\u5982\u6b64\u800c\u53bb\uff0c\u516b\u5366\u95e8\u5728\u6c5f\u6e56\u518d\u552f\u7acb\u8db3\uff0c\u5144\u5f1f\u4e5f\u6ca1\u8138\u505a\u4eba\u3002\u201d \u3000\u3000\u8d75\u534a\u5c71\u5fc3\u60f3\uff1a\u201c\u8fd9\u6d3b\u5012\u4e5f\u662f\u5b9e\u60c5\u3002\u201d\u4e8e\u662f\u5411\u80e1\u6590\u8bf4\u9053\uff1a\u201d\u5b69\u5b50\uff0c\u4f60\u600e\u5730\u5f97\u7f6a\u4e24\u4f4d\u738b\u5e08\u5085\u4e86\uff1f\u5feb\u78d5\u4e2a\u5934\u966a\u4e86\u793c\uff0c\u968f\u6211\u51fa\u53bb\u3002\u201d \u3000\u3000\u8d75\u534a\u5c71\u89c1\u8bc6\u8001\u5230\uff0c\u8fd9\u4e00\u6b21\u5374\u8bf4\u9519\u4e86\u8bdd\uff0c\u4ed6\u89c1\u80e1\u6590\u9002\u624d\u5c06\u5546\u8001\u592a\u8fd9\u4e00\u5e26\uff0c\u8eab\u4e0b\u867d\u7136\u4e0d\u5f31\uff0c\u603b\u662f\u4e2a\u5b69\u5b50\uff0c\u54ea\u77e5\u80e1\u6590\u5929\u751f\u8c6a\u8fc8\uff0c\u5c82\u80af\u8f7b\u6613\u5411\u4eba\u4f4e\u5934\uff1f\u7b11\u9053\uff1a\u201c\u8d75\u4e09\u7237\uff0c\u4f60\u53eb\u4ed6\u5411\u6211\u78d5\u5934\uff1f\u8fd9\u4e2a\u6211\u53ef\u4e0d\u6562\u5f53\uff0c\u201d\u8d75\u534a\u5c71\u4e00\u6123\uff0c\u5fc3\u9053\uff1a\u201d\u8fd9\u4e2a\u5b50\u600e\u5730\u5982\u6b64\u8d2b\u5634\uff1f\u201d \u3000\u3000\u738b\u5251\u82f1\u672c\u60f3\u80e1\u6590\u4e00\u966a\u793c\uff0c\u5c31\u6b64\u4e0b\u53f0\uff0c\u542c\u4ed6\u5982\u6b64\u56de\u7b54\uff0c\u5fc3\u4e2d\u6012\u6781\uff0c\u4f46\u4e0d\u613f\u5728\u8d75\u534a\u5c71\u9762\u524d\u663e\u5f97\u5c11\u4e86\u6db5\u517b\uff0c\u5f53\u4e0b\u4ecd\u662f\u4e0d\u52a8\u58f0\u8272\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4f60\u6b66\u529f\u679c\u7136\u4e0d\u9519\uff0c\u4e5f\u602a\u4e0d\u5f97\u4f60\u72c2\u5984\u3002\u6765\u6765\u6765\uff0c\u738b\u67d0\u9886\u6559\u4f60\u51e0\u62db\u3002\u201d \u3000\u3000\u80e1\u6590\u8dc3\u5230\u5385\u5fc3\uff0c\u547c\u7684\u4e00\u62f3\uff0c\u8fce\u9762\u5c31\u5f80\u738b\u5251\u82f1\u9f3b\u5b50\u4e0a\u6253\u53bb\u3002\u738b\u5251\u82f1\u5fae\u5fae\u4e00\u7b11\uff0c\u987a\u624b\u8fd8\u4e86\u4e00\u638c\u3002 \u3000\u3000\u738b\u5251\u82f1\u8fd9\u4e00\u638c\u62cd\u51fa\u53bb\u65f6\u8f7b\u8f7b\u5de7\u5de7\uff0c\u4f46\u638c\u5230\u534a\u8def\uff0c\u5df2\u662f\u631f\u7740\u4e00\u80a1\u75be\u98ce\uff0c\u5411\u80e1\u6590\u6251\u9762\u51fb\u53bb\u3002\u8d75\u534a\u5c71\u5fc3\u9053\uff1a\u201c\u8fd9\u59d3\u738b\u7684\u5bb6\u5b66\u6e0a\u6e90\uff0c\u638c\u4e0a\u52b2\u529b\u679c\u7136\u975e\u540c\u51e1\u54cd\u3002\u201d \u3000\u3000\u4ed6\u751f\u6015\u8fd9\u4e00\u638c\u5c31\u5c06\u80e1\u6590\u51fb\u5f97\u91cd\u4f24\uff0c\u5f53\u5373\u8eab\u5b50\u5fae\u5411\u524d\u503e\uff0c\u9884\u62df\u4e8e\u5371\u6025\u4e4b\u65f6\uff0c\u51fa\u638c\u62cd\u5411\u738b\u5251\u82f1\u540e\u5fc3\uff0c\u4ee5\u5378\u638c\u529b\u3002 \u3000\u3000\u54ea\u77e5\u5c0f\u80e1\u6590\u8eab\u6cd5\u5947\u5feb\uff0c\u4e0a\u8eab\u4e00\u4fa7\uff0c\u738b\u5251\u82f1\u4e00\u638c\u5df2\u7136\u6253\u504f\u3002\u4f46\u738b\u5251\u82f1\u662f\u5f53\u4e16\u516b\u5366\u95e8\u4e2d\u7b2c\u4e00\u9ad8\u624b\uff0c\u5de6\u638c\u6253\u6b6a\uff0c\u53f3\u638c\u6beb\u4e0d\u505c\u7559\uff0c\u5df2\u81ea\u53f3\u4e0a\u5411\u5de6\u4e0b\u659c\u5288\u4e0b\u53bb\u3002 \u3000\u3000\u80e1\u6590\u53cc\u62f3\u4e00\u4e3e\uff0c\u62cd\u7684\u4e00\u54cd\uff0c\u8fd9\u4e00\u638c\u6b63\u597d\u5288\u5728\u4ed6\u7684\u62f3\u4e0a\u3002 \u3000\u3000\u80e1\u6590\u53eb\u9053\uff1a\u201c\u554a\u54df\uff0c\u597d\u75db\uff01\u201d\u6479\u5730\u91cc\u201c\u6c89\u65f6\u64d2\u62ff\u201d\uff0c\u4f38\u624b\u6293\u4ed6\u5de6\u624b\u201c\u66f2\u6c60\u7a74\u201d\uff0c\u8fd9\u4e00\u62db\u6781\u5176\u602a\u5f02\uff0c\u738b\u5251\u82f1\u4e00\u6014\uff0c\u5411\u540e\u8dc3\u5f00\u4e00\u6b65\u3002\u5546\u8001\u592a\u4e0e\u9a6c\u884c\u7a7a\u5bf9\u671b\u4e86\u4e00\u773c\uff0c\u5fc3\u4e2d\u5747\u9053\uff1a\u201c\u600e\u4e48\u8fd9\u5b69\u5b50\u4e5f\u4f1a\u4f7f\u8fd9\u602a\u62db\uff1f\u201d\u539f\u6765\u5f53\u65e5\u960e\u57fa\u52ab\u9556\uff0c\u4e0e\u9a6c\u884c\u7a7a\u52a8\u6b66\uff0c\u5341\u4f59\u62db\u602a\u62db\u4e4b\u4e2d\uff0c\u5c31\u662f\u6709\u8fd9\u62db\u201c\u6c89\u65f6\u64d2\u62ff\u201d\u3002 \u3000\u3000\u738b\u5251\u82f1\u4e00\u9000\u53c8\u8fdb\uff0c\u4f7f\u62db\u201c\u731b\u864e\u4f0f\u6869\u201d\uff0c\u63a2\u638c\u5207\u80e1\u6590\u5de6\u81c2\u3002\u80e1\u6590\u534a\u8f6c\u8eab\u5b50\uff0c\u201c\u94a9\u817f\u53cd\u8e22\u201d\uff0c\u53c8\u662f\u4e00\u8bb0\u602a\u62db\u3002\u8fd9\u4e00\u6765\uff0c\u9a6c\u884c\u7a7a\u7b49\u56fa\u7136\u66f4\u662f\u8be7\u5f02\uff0c\u8fde\u89c1\u591a\u8bc6\u5e7f\u7684\u8d75\u534a\u5c71\u4e5f\u6697\u89c9\u5947\u602a\u3002\u738b\u5251\u82f1\u89c1\u4ed6\u62db\u6cd5\u4e2d\u9690\u542b\u76f8\u8fb1\u4e4b\u610f\uff0c\u5fc3\u9053\uff1a\u201c\u82e5\u4e0d\u7ed9\u4f60\u5403\u70b9\u82e6\u5934\uff0c\u53ef\u53eb\u4eba\u5bb6\u5c0f\u770b\u4e86\u516b\u5366\u95e8\u3002\u201d\u4ed6\u867d\u4e0e\u80e1\u6590\u52a8\u6b66\uff0c\u5fc3\u4e2d\u5374\u54ea\u5c06\u8fd9\u5b69\u5b50\u5f53\u4f5c\u5bf9\u624b\uff0c\u4e00\u62db\u4e00\u5f0f\uff0c\u5168\u662f\u9732\u7ed9\u8eab\u65c1\u7684\u5927\u540d\u5bb6\u8d75\u534a\u5c71\u89c2\u770b\uff0c\u56e0\u4e4b\u51fa\u624b\u51dd\u91cd\uff0c\u5706\u8f6c\u5982\u610f\uff0c\u4e0d\u6562\u5931\u4e86\u534a\u70b9\u540d\u5bb6\u7684\u8eab\u5206\uff0c\u53ea\u56e0\u5fc3\u6709\u65c1\u5c5e\uff0c\u62db\u6570\u4e0a\u7adf\u662f\u4e0d\u6c42\u72e0\u8fa3\uff0c\u552f\u6050\u8ba9\u8d75\u534a\u5c71\u5c0f\u89d1\u4e86\uff0c\u8bf4\u4e00\u53e5\uff1a\u201c\u540d\u95e8\u9ad8\u5f1f\uff0c\u5c82\u80fd\u5982\u6b64\u6d6e\u56a3\uff1f\u201d\u8fd9\u4e48\u4e00\u6765\uff0c\u4ed6\u638c\u6cd5\u4e2d\u56fa\u7136\u662f\u6ca1\u534a\u70b9\u7834\u7efd\uff0c\u4f46\u8981\u6570\u62db\u4e4b\u95f4\u5236\u4f4f\u5bf9\u65b9\uff0c\u7adf\u4e5f\u4e0d\u80fd\u3002 \u3000\u3000\u5546\u5b9d\u9707\u81ea\u5e7c\u82e6\u7ec3\u8fc7\u516b\u5366\u638c\uff0c\u53ea\u89c1\u8fd9\u4f4d\u5927\u5e08\u4f2f\u51fa\u624b\u5e73\u6de1\u65e0\u5947\uff0c\u4f7f\u7684\u5168\u662f\u516b\u5366\u638c\u4e2d\u6700\u6d45\u8fd1\u7684\u62db\u6570\uff0c\u8fd8\u9053\u4ed6\u5fcc\u60ee\u8d75\u534a\u5c71\uff0c\u5b58\u5fc3\u6577\u884d\uff0c\u65e0\u610f\u771f\u4e0e\u7236\u4eb2\u590d\u4ec7\uff0c\u5fc3\u4e0b\u6697\u6697\u607c\u6012\u3002\u4ed6\u54ea\u77e5\u738b\u5251\u82f1\u8fd9\u4e9b\u5e73\u6de1\u65e0\u5947\u7684\u638c\u6cd5\u4e4b\u4e2d\u8574\u542b\u6570\u5341\u5e74\u82e6\u529f\uff0c\u80e1\u6590\u521d\u65f6\u8df3\u8df3\u8e66\u8e66\uff0c\u602a\u62db\u8fed\u51fa\uff0c\u5230\u5f97\u540e\u6765\uff0c\u5df2\u5168\u5728\u5bf9\u65b9\u638c\u98ce\u7b3c\u7f69\u4e4b\u4e0b\u3002\u738b\u5251\u82f1\u638c\u529b\u50ac\u52a8\uff0c\u6e10\u6e10\u5c06\u80e1\u6590\u5236\u4f4f\uff0c\u4f7f\u4ed6\u6bcf\u4e00\u62f3\u6253\u51fa\uff0c\u6bcf\u4e00\u811a\u8e22\u51fa\uff0c\u7acb\u65f6\u53d7\u5230\u516b\u5366\u638c\u638c\u529b\u7684\u53cd\u63a8\u3002\u6b64\u65f6\u4ed6\u82e5\u8981\u53d1\u52b2\u6253\u4f24\u80e1\u6590\uff0c\u539f\u5df2\u4e0d\u96be\uff0c\u4f46\u4ed6\u6709\u610f\u5728\u8d75\u534a\u5c71\u9762\u524d\u663e\u793a\u8eab\u624b\uff0c\u8981\u7d2f\u5f97\u80e1\u6590\u7b4b\u75b2\u529b\u5c3d\uff0c\u8dea\u5730\u6c42\u9976\uff0c\u81ea\u5df1\u5374\u59cb\u7ec8\u6f47\u6d12\u81ea\u5982\uff0c\u884c\u82e5\u65e0\u4e8b\u3002\u987b\u77e5\u6b66\u672f\u6700\u96be\u4f01\u53ca\u7684\u5883\u754c\uff0c\u4e43\u662f\u4e3e\u91cd\u82e5\u8f7b\uff0c\u8981\u4f7f\u529b\u800c\u4e0d\u89c1\u8d39\u529b\uff0c\u53d1\u52b2\u800c\u4e0d\u89c1\u7528\u52b2\u3002\u6bcf\u4e00\u4e2a\u6b66\u5b66\u540d\u5bb6\u7ec3\u5230\u6700\u540e\uff0c\u90fd\u662f\u5411\u8fd9\u5883\u754c\u81f4\u529b\u3002\u81f3\u4e8e\u545b\u559d\u9163\u6597\uff0c\u6325\u6c57\u5598\u6c14\uff0c\u90a3\u81ea\u662f\u6700\u4e0b\u4e58\u7684\u4e86\u3002 \u3000\u3000\u8d75\u534a\u5c71\u77e5\u4ed6\u7528\u610f\uff0c\u5fc3\u60f3\u65e2\u7136\u5982\u6b64\uff0c\u8fd9\u5c0f\u5b69\u6682\u65e0\u6027\u547d\u4e4b\u5fe7\uff0c\u4e14\u770b\u4ed6\u652f\u6301\u5f97\u51e0\u65f6\u3002\u773c\u89c1\u80e1\u6590\u5df2\u662f\u8eab\u4e0d\u7531\u4e3b\u5730\u4e3a\u5bf9\u65b9\u638c\u529b\u5e26\u52a8\uff0c\u811a\u6b65\u8e09\u8dc4\uff0c\u7a81\u7136\u95ee\u4e00\u4e2a\u7b4b\u6597\u7ffb\u51fa\uff0c\u53f3\u624b\u5728\u5730\u4e0b\u4e00\u6491\uff0c\u53cc\u817f\u540c\u65f6\u6a2a\u626b\u3002\u8fd9\u4e00\u4e0b\u53c8\u662f\u4e00\u8bb0\u602a\u62db\uff0c\u738b\u5251\u82f1\u8dc3\u8d77\u907f\u8fc7\uff0c\u80e1\u6590\u5f80\u5730\u4e0b\u4e00\u5750\uff0c\u53cc\u817f\u8fde\u73af\u4e0a\u8e22\uff0c\u970e\u65f6\u4e4b\u95f4\u7adf\u8e22\u4e86\u4e03\u516b\u817f\uff0c\u53c8\u662f\u8be1\u5f02\uff0c\u53c8\u662f\u8fc5\u6377\u3002\u62f3\u6cd5\u4e2d\u539f\u6709\u201c\u8fde\u73af\u9e33\u9e2f\u817f\u201d\u7684\u62db\u6570\uff0c\u4f46\u5de6\u811a\u8e22\u51fa\u4e4b\u540e\uff0c\u53f3\u811a\u8ddf\u7740\u98de\u8e22\uff0c\u518d\u8981\u8e22\u7b2c\u4e09\u817f\u65f6\uff0c\u7ec8\u987b\u6709\u4e00\u811a\u5148\u884c\u7740\u5730\uff0c\u7eb5\u5feb\u4e5f\u6709\u9650\u5ea6\uff0c\u6b64\u65f6\u80e1\u6590\u5750\u5728\u5730\u4e0a\uff0c\u53cc\u811a\u51cc\u7a7a\uff0c\u5f7c\u843d\u6b64\u8d77\uff0c\u51fa\u817f\u5982\u7535\uff0c\u7ade\u5c06\u738b\u5251\u82f1\u8e22\u4e86\u4e2a\u624b\u5fd9\u811a\u4e71\u3002 \u3000\u3000\u9a6c\u884c\u7a7a\u4e0e\u5546\u8001\u592a\u53c8\u662f\u4e92\u89c6\u4e86\u4e00\u773c\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u8bb0\u602a\u62db\u5374\u975e\u960e\u57fa\u6240\u4f1a\uff0c\u770b\u6765\u8fd9\u5c0f\u5b69\u6240\u5b66\u7684\u6b66\u529f\uff0c\u8fd8\u8f83\u960e\u57fa\u4e3a\u591a\u3002\u201d\u679c\u7136\u4e0d\u51fa\u4e8c\u4eba\u6240\u6599\uff0c\u80e1\u6590\u4e00\u7ffb\u8eab\uff0c\u7acb\u65f6\u53cc\u65f6\u63a8\u540e\uff0c\u6b64\u65f6\u4ed6\u4e0e\u738b\u5251\u82f1\u80cc\u810a\u5bf9\u7740\u80cc\u810a\uff0c\u4ed6\u8eab\u5b50\u65e2\u77ee\uff0c\u51fa\u62db\u53c8\u5feb\uff0c\u8fd9\u4e24\u4e0b\u65f6\u9524\uff0c\u7adf\u90fd\u649e\u5728\u738b\u5251\u82f1\u7684\u5c41\u80a1\u4e4b\u4e0a\u3002\u81c0\u4e0a\u591a\u8089\uff0c\u4ed6\u53c8\u4eba\u5c0f\u529b\u5f31\uff0c\u8fd9\u4e24\u8bb0\u65f6\u9524\u81ea\u662f\u4f24\u4e0d\u5230\u5bf9\u65b9\uff0c\u4f46\u65c1\u89c2\u4f17\u4eba\u5374\u5fcd\u4e0d\u4f4f\u5931\u7b11\u3002 \u3000\u3000\u738b\u5251\u82f1\u5927\u6012\uff0c\u56de\u8eab\u547c\u7684\u4e00\u638c\uff0c\u5f53\u80f8\u5288\u53bb\uff0c\u4f46\u89c1\u4ed6\u8138\u8272\u72f0\u72de\uff0c\u5df2\u987e\u4e0d\u5f97\u4ec0\u4e48\u6f47\u6d12\uff0c\u4ec0\u4e48\u98ce\u5ea6\u3002\u8d75\u534a\u5c71\u5fc3\u4e2d\u6697\u53f9\uff1a\u201c\u5a01\u9707\u6cb3\u6714\u738b\u7ef4\u626c\u7684\u513f\u5b50\uff0c\u4e0d\u53ca\u4e43\u7236\u591a\u77e3\uff01\u201d\u4ed6\u4e00\u9762\u89c2\u6597\uff0c\u773c\u89d2\u95f4\u5374\u59cb\u7ec8\u6ca1\u4e00\u523b\u79bb\u5f00\u4e86\u9648\u79b9\uff0c\u51b3\u4e0d\u5bb9\u4ed6\u4fdf\u673a\u9003\u8131\u3002 \u3000\u3000\u80e1\u6590\u89c1\u5bf9\u65b9\u53cc\u638c\u72b9\u5982\u75be\u98ce\u66b4\u96e8\u822c\u88ad\u6765\uff0c\u5fc3\u4e0b\u4e5f\u4e0d\u81ea\u7981\u9a87\u6015\uff0c\u5bf9\u65b9\u7a76\u662f\u6b66\u6797\u4e2d\u7684\u4e00\u6d41\u9ad8\u624b\uff0c\u81ea\u5df1\u5168\u9760\u62f3\u8c31\u4e2d\u4e00\u4e9b\u5bb6\u4f20\u602a\u62db\uff0c\u4ed7\u7740\u5bf9\u65b9\u4e0d\u8bc6\uff0c\u51fa\u624b\u6709\u6240\u987e\u5fcc\uff0c\u8fd9\u624d\u52c9\u529b\u652f\u6491\u4e86\u8fd9\u4e9b\u65f6\u5019\uff0c\u5df2\u5c5e\u6781\u5ea6\u96be\u80fd\u3002\u5176\u5b9e\u80e1\u5bb6\u62f3\u8c31\u4e0a\u8fd9\u4e9b\u602a\u62db\u4e43\u662f\u7ec3\u529f\u6240\u7528\uff0c\u65e8\u5728\u953b\u70bc\u8eab\u624b\uff0c\u4e0d\u6c42\u514b\u654c\u5236\u80dc\uff0c\u771f\u6b63\u4e0e\u4eba\u52a8\u624b\u7684\u62db\u6570\uff0c\u5f55\u5728\u62f3\u8c31\u7684\u6700\u521d\u6570\u9875\u4e4b\u540e\u3002\u80e1\u6590\u529f\u529b\u672a\u5230\uff0c\u96be\u4ee5\u9886\u4f1a\uff0c\u53ea\u5f97\u65bd\u5c55\u8fd9\u4e9b\u7ec3\u529f\u7528\u7684\u624e\u6839\u57fa\u62db\u5f0f\u3002\u60f3\u90a3\u98de\u5929\u72d0\u72f8\u3001\u80e1\u4e00\u5200\u7b49\u5747\u662f\u4e00\u4ee3\u5927\u4fa0\uff0c\u82e5\u662f\u4e0e\u4eba\u52a8\u624b\u4e4b\u9645\u4e5f\u662f\u8fd9\u822c\u4e0d\u8bba\u4e0d\u7c7b\u3001\u602a\u6a21\u602a\u6837\uff0c\u5c82\u975e\u5927\u5931\u8eab\u5206\uff1f \u3000\u3000\u53c8\u6597\u5341\u4f59\u62db\uff0c\u80e1\u6590\u5de6\u652f\u53f3\u7ecc\uff0c\u5927\u611f\u72fc\u72c8\uff0c\u7a81\u89c1\u738b\u5251\u82f1\u5de6\u638c\u5f80\u5916\u4e00\u7a7f\uff0c\u5f53\u5373\u95ea\u8eab\u5411\u53f3\u907f\u8fc7\uff0c\u738b\u5251\u82f1\u53f3\u638c\u201c\u6e38\u7a7a\u63a2\u722a\u201d\uff0c\u659c\u5288\u4e0b\u6765\u3002\u8fd9\u4e00\u4e0b\u597d\u4e0d\u52b2\u6025\uff0c\u80e1\u6590\u5fd9\u77ee\u8eab\u6c89\u80a9\uff0c\u867d\u5c06\u8fd9\u4e00\u638c\u4e4b\u529b\u5378\u4e0b\u4e86\u4e03\u6210\uff0c\u8fd8\u662f\u88ab\u4ed6\u638c\u529b\u9707\u5f97\u4e00\u4ea4\u6454\u5012\u3002 \u3000\u3000\u4f17\u4eba\u60ca\u547c\u58f0\u4e2d\uff0c\u738b\u5251\u82f1\u53c8\u662f\u4e00\u638c\u5288\u4e86\u4e0b\u53bb\u3002\u8d75\u534a\u5c71\u5927\u6012\uff0c\u5fc3\u9053\uff1a\u201c\u4e8f\u4f60\u4e5f\u7b97\u662f\u4e2a\u6210\u540d\u4eba\u7269\uff0c\u5c0f\u5b69\u5b50\u5df2\u7ed9\u4f60\u6253\u5012\uff0c\u600e\u4e48\u8fd8\u4e0b\u6bd2\u624b\uff1f\u201d\u4ed6\u592a\u6781\u62f3\u7684\u529f\u592b\u8bb2\u7a76\u8fdf\u51fa\u5148\u81f3\uff0c\u540e\u53d1\u5236\u4eba\uff0c\u654c\u4eba\u62db\u6570\u8d8a\u662f\u7528\u8001\uff0c\u51fa\u624b\u65f6\u6536\u6548\u8d8a\u5927\uff0c\u53ea\u7b49\u738b\u5251\u82f1\u638c\u7f18\u6328\u8fd1\u80e1\u6590\u8eab\u4e0a\uff0c\u7acb\u5373\u53d1\u62db\u76f8\u6551\u3002 \u3000\u3000\u7a81\u7136\u9752\u5149\u4e00\u95ea\uff0c\u738b\u5251\u82f1\u75be\u6536\u5de6\u638c\uff0c\u4fa7\u8eab\u8d77\u817f\u3002\u539f\u6765\u80e1\u6590\u8dcc\u5012\u4e4b\u65f6\uff0c\u89c1\u8eab\u65c1\u6709\u534a\u622a\u5251\u5934\uff0c\u6b63\u662f\u6bb7\u4ef2\u7fd4\u88ab\u9707\u6298\u7684\u65ad\u5251\uff0c\u60c5\u6025\u4e4b\u4e0b\uff0c\u4f38\u624b\u6293\u8d77\uff0c\u5411\u654c\u4eba\u62cd\u4e0b\u6765\u7684\u638c\u5fc3\u523a\u53bb\u3002\u8fd9\u4e00\u4e0b\u7ae0\u6cd5\u53d8\u5e7b\uff0c\u82e5\u975e\u738b\u5251\u82f1\u8eb2\u95ea\u5f97\u5feb\uff0c\u638c\u5fc3\u7ed9\u4ed6\u523a\u4e2a\u7a9f\u7abf\u4e5f\u4e0d\u5e0c\u5947\u3002\u80e1\u6590\u4e00\u62db\u5f97\u624b\uff0c\u7acb\u5373\u4e00\u4e2a\u6253\u6eda\uff0c\u5de6\u624b\u5728\u5730\u4e0b\u4e00\u635e\uff0c\u53f3\u624b\u7528\u65ad\u5251\u5272\u4e0b\u4e00\u5757\u8863\u895f\uff0c\u88f9\u4e86\u6298\u65ad\u7684\u5251\u5203\uff0c\u7b11\u9053\uff1a\u201c\u738b\u5927\u7237\uff0c\u6211\u7684\u624b\u77ed\uff0c\u4f60\u7684\u624b\u957f\uff0c\u54b1\u4e8c\u4eba\u6bd4\u6b66\u592a\u4e0d\u516c\u5e73\u3002\u6211\u628a\u53f3\u624b\u63a5\u957f\u70b9\u513f\uff0c\u4f60\u82e5\u5bb3\u6015\uff0c\u5c31\u53d6\u51fa\u516b\u5366\u5200\u6765\u597d\u4e86\u3002\u201d \u3000\u3000\u81ea\u4ece\u201c\u98de\u5929\u72d0\u72f8\u201d\u4ee5\u964d\uff0c\u80e1\u5bb6\u5386\u4f20\u5404\u4ee3\u90fd\u662f\u667a\u8ba1\u8fc7\u4eba\u3002\u80e1\u6590\u5fc3\u77e5\u7a7a\u624b\u6253\u4ed6\u4e0d\u8fc7\uff0c\u4e58\u673a\u62fe\u8d77\u65ad\u5251\u7528\u4f5c\u5175\u5668\uff0c\u4f46\u6015\u5bf9\u65b9\u4f7f\u5175\u5203\uff0c\u5374\u62a2\u5148\u6fc0\u4ed6\u4e00\u6fc0\u3002\u738b\u5251\u82f1\u4f55\u7b49\u8eab\u5206\uff0c\u660e\u77e5\u5403\u4e8f\uff0c\u54ea\u80af\u8ddf\u4ed6\u5e73\u624b\u5bf9\u5200\uff0c\u6599\u5b9a\u4ed6\u591a\u62ff\u4e00\u67c4\u65ad\u5251\u4e5f\u7ba1\u4e0d\u4e86\u7528\uff0c\u53ea\u54fc\u4e86\u4e00\u58f0\uff0c\u516b\u5366\u638c\u4e2d\u5939\u7740\u64d2\u62ff\u624b\uff0c\u5f84\u6765\u6293\u4ed6\u63e1\u7740\u65ad\u5251\u7684\u624b\u8155\uff0c\u5de6\u638c\u53d1\u52b2\uff0c\u5288\u5411\u4ed6\u7684\u9762\u95e8\u3002 \u3000\u3000\u80e1\u6590\u8f6c\u52a8\u5251\u5934\uff0c\u5f53\u4f5c\u86fe\u7709\u523a\u4f7f\uff0c\u4e00\u9762\u9012\u62db\uff0c\u5de6\u624b\u5ffd\u5730\u5f80\u5934\u9876\u4e00\u62c9\uff0c\u53d6\u4e0b\u6be1\u5e3d\uff0c\u7b11\u9053\uff1a\u201c\u6211\u53f3\u624b\u6709\u5251\u5934\uff0c\u5de6\u624b\u6709\u76fe\u724c\uff0c\u77a7\u4f60\u5948\u4f55\u5f97\u4e86\u6211\uff1f\u201d\u5c06\u6be1\u5e3d\u5f53\u4f5c\u76fe\u724c\uff0c\u5f80\u4ed6\u5de6\u638c\u4e00\u6321\u3002\u738b\u5251\u82f1\u5fc3\u9053\uff1a\u201c\u81ed\u5c0f\u5b50\uff0c\u8fd9\u4e48\u4e00\u6321\uff0c\u4f60\u5de6\u8155\u975e\u65ad\u4e0d\u53ef\u3002\u201d\u638c\u4e0a\u53c8\u52a0\u4e86\u4e09\u5206\u52b2\u9053\uff0c\u5411\u7834\u6be1\u5e3d\u4e0a\u51fb\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u738b\u5251\u82f1\u201c\u554a\u201d\u7684\u4e00\u58f0\u5927\u53eb\uff0c\u5411\u540e\u8dc3\u5f00\u4e08\u4f59\uff0c\u8fd9\u4e00\u58f0\u53eb\u558a\uff0c\u58f0\u97f3\u60e8\u5389\uff0c\u7adf\u4f3c\u53d7\u4e86\u91cd\u4f24\u6a21\u6837\u3002\u4f17\u4eba\u4e00\u9f50\u671b\u7740\u4ed6\uff0c\u53ea\u89c1\u4ed6\u5de6\u638c\u5fc3\u4e2d\u9c9c\u8840\u6dcb\u6f13\uff0c\u4e0d\u77e5\u56e0\u4f55\u53d7\u7684\u4f24\u3002\u738b\u5251\u82f1\u6012\u6781\uff0c\u621f\u6307\u80e1\u6590\u559d\u9053\uff1a\u201c\u4f60\uff0c\u4f60\u2026\u2026\u4f60\u8fd9\u70c2\u6be1\u5e3d\u4e2d\u85cf\u7740\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u80e1\u6590\u5c06\u6be1\u5e3d\u6234\u56de\u5934\u4e0a\uff0c\u5de6\u624b\u4e2d\u8d6b\u7136\u63e1\u7740\u4e00\u679d\u91d1\u9556\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u662f\u4f60\u516b\u5366\u95e8\u7684\u6697\u5668\uff0c\u987b\u4e0d\u662f\u6211\u5e26\u6765\u7684\u3002\u6211\u968f\u624b\u5728\u5730\u4e0b\u6361\u4e86\u4e00\u679d\uff0c\u60f3\u5077\u5077\u62ff\u56de\u53bb\u73a9\u513f\uff0c\u4f60\u5374\u5b9a\u8981\u63ed\u7a7f\u6211\u7684\u5e95\u513f\uff0c\u597d\u5427\uff0c\u8fd9\u4e00\u679d\u5c0f\u5c0f\u91d1\u9556\u6211\u4e5f\u4e0d\u5e0c\u7f55\u3002\u201d\u8bf4\u7740\u624b\u4e00\u626c\uff0c\u5bf9\u51c6\u4ed6\u80f8\u53e3\u5c04\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u738b\u5251\u82f1\u4fa7\u8fc7\u8eab\u5b50\uff0c\u4f38\u624b\u4e00\u6284\uff0c\u8981\u5c06\u91d1\u7f25\u6284\u5728\u624b\u91cc\u3002\u4ed6\u5148\u4fa7\u8eab\uff0c\u518d\u4f38\u624b\uff0c\u90a3\u662f\u5bf9\u80e1\u6590\u5df2\u5b58\u4e86\u5fcc\u60ee\u4e4b\u610f\uff0c\u6015\u4ed6\u53d1\u9556\u7684\u624b\u6cd5\u53c8\u662f\u5341\u5206\u602a\u5f02\uff0c\u4e00\u4e2a\u6284\u63a5\u4e0d\u5230\uff0c\u4e0d\u514d\u6253\u4e2d\u4e86\u80f8\u53e3\u3002\u5c82\u77e5\u4ed6\u8fd9\u4e00\u4f38\u624b\u5374\u63a5\u4e86\u4e2a\u7a7a\u3002\u80e1\u6590\u624b\u52bf\u662f\u5411\u524d\u53d1\u7f25\uff0c\u5176\u5b9e\u624b\u6307\u4e0a\u4f7f\u4e86\u4e00\u80a1\u53cd\u52b2\uff0c\u5c06\u91d1\u7f25\u5c04\u5411\u8eab\u540e\u3002 \u3000\u3000\u7ad9\u5728\u4ed6\u80cc\u540e\u7684\u6b63\u662f\u5546\u8001\u592a\uff0c\u7a81\u89c1\u91d1\u5149\u4e00\u95ea\uff0c\u9556\u5df2\u5230\u9762\u524d\uff0c\u6025\u5fd9\u7f29\u5934\uff0c\u5657\u7684\u4e00\u58f0\uff0c\u90a3\u679d\u91d1\u9556\u6253\u8fdb\u5979\u7684\u9afb\u5b50\uff0c\u98a4\u5dcd\u5dcd\u5730\u6643\u4e86\u51e0\u6643\u3002\u5546\u5b9d\u9707\u53ea\u5413\u5f97\u5fc3\u60ca\u8089\u8df3\uff0c\u6251\u5230\u6bcd\u4eb2\u8ddf\u524d\uff0c\u53eb\u9053\uff1a\u201c\u5988\uff0c\u53ef\u4f24\u7740\u4f60\u4e48\uff1f\u201d \u3000\u3000\u81ea\u80e1\u6590\u51fa\u624b\u4ee5\u6765\uff0c\u51e0\u4e4e\u6bcf\u4e00\u62db\u6bcf\u4e00\u5f0f\u90fd\u662f\u5f02\u60f3\u5929\u5f00\uff0c\u53eb\u4eba\u9632\u4e0d\u80dc\u9632\uff0c\u8fd9\u4e00\u4e0b\u82b1\u5de7\u5f02\u5e38\u7684\u53d1\u9556\uff0c\u66f4\u662f\u7729\u4eba\u5fc3\u76ee\u3002\u773c\u89c1\u5546\u8001\u592a\u5728\u95f4\u4e0d\u5bb9\u53d1\u4e4b\u4e2d\u6b7b\u91cc\u9003\u751f\uff0c\u4eba\u4eba\u5c3d\u7686\u9a87\u7136\u3002\u8d75\u534a\u5c71\u637b\u987b\u5fae\u7b11\uff0c\u5fc3\u60f3\u8fd9\u822c\u524d\u626c\u540e\u53d1\u7684\u9556\u6cd5\uff0c\u81ea\u5df1\u539f\u4e5f\u64c5\u957f\uff0c\u82e5\u662f\u81ea\u5df1\u51fa\u624b\uff0c\u5c31\u6709\u5341\u4e2a\u5546\u8001\u592a\uff0c\u4e5f\u4e00\u9f50\u6253\u6b7b\u4e86\uff0c\u53ea\u662f\u8fd9\u5c0f\u5b69\u88c5\u6a21\u4f5c\u6837\u7684\u903c\u771f\u795e\u6001\uff0c\u5374\u8fdc\u975e\u81ea\u5df1\u6240\u53ca\u3002 \u3000\u3000\u8d75\u534a\u5c71\u968f\u5373\u60f3\u8d77\uff0c\u53eb\u9053\uff1a\u201c\u738b\u5e08\u5144\uff0c\u5feb\u634f\u4f4f\u8109\u95e8\uff0c\u9556\u4e0a\u6709\u6bd2\u3002\u201d\u5546\u5b9d\u9707\u4e00\u51db\uff0c\u53eb\u9053\uff1a\u201c\u6211\u53bb\u53d6\u89e3\u836f\uff01\u201d\u8bf4\u7740\u98de\u5954\u5165\u5185\u3002 \u3000\u3000\u738b\u5251\u82f1\u4e00\u526f\u6267\u62d7\u7684\u72e0\u52b2\uff0c\u5012\u4e0e\u4ed6\u8fc7\u4e16\u7684\u7236\u4eb2\u5dee\u4e0d\u591a\uff0c\u638c\u5fc3\u4e00\u53d7\u9556\u4f24\uff0c\u53ea\u89c9\u5de6\u624b\u9ebb\u75d2\uff0c\u542c\u5f97\u8d75\u534a\u5c71\u8fd9\u4e48\u4e00\u53eb\uff0c\u53f3\u624b\u62c9\u65ad\u8863\u5e26\uff0c\u7d27\u7d27\u7f20\u4f4f\u5de6\u8155\uff0c\u8138\u8272\u94c1\u9752\u3002\u738b\u5251\u6770\u624b\u8db3\u5173\u5fc3\uff0c\u62a2\u8fc7\u6765\u5e2e\u4ed6\u7f20\u8155\u3002\u738b\u5251\u82f1\u5de6\u624b\u4e00\u7529\uff0c\u559d\u9053\uff1a\u201c\u8d70\u5f00\uff01\u201d \u3000\u3000\u738b\u5251\u6770\u4e0d\u63d0\u9632\u7ed9\u4ed6\u731b\u529b\u4e00\u7529\uff0c\u9000\u5f00\u4e24\u6b65\uff0c\u6115\u7136\u76f8\u987e\uff0c\u53eb\u9053\uff1a\u201c\u5927\u54e5\uff01\u201d\u738b\u5251\u82f1\u6325\u8d77\u4f24\u638c\uff0c\u547c\u7684\u4e00\u58f0\uff0c\u75be\u5f80\u80e1\u6590\u5934\u9876\u62cd\u5230\uff0c\u811a\u4e0b\u98de\u8dd1\uff0c\u7adf\u7136\u4f7f\u51fa\u201c\u6e38\u8eab\u516b\u5366\u638c\u201d\u7684\u7edd\u62db\uff0c\u6b64\u65f6\u518d\u4e0d\u5bb9\u60c5\uff0c\u51b3\u610f\u8981\u53d6\u8fd9\u53ef\u6076\u7684\u72e1\u7ae5\u6027\u547d\u3002 \u3000\u3000\u80e1\u6590\u5b66\u6210\u6b66\u827a\u4e4b\u540e\uff0c\u521d\u6b21\u662f\u4e0e\u5546\u5b9d\u9707\u5bf9\u654c\uff0c\u5176\u540e\u5bf9\u6218\u5546\u8001\u592a\u548c\u738b\u5251\u6770\uff0c\u6b64\u65f6\u4e0e\u738b\u5251\u82f1\u5bf9\u638c\uff0c\u5df2\u662f\u7b2c\u56db\u4e2a\u5bf9\u624b\u3002\u8d8a\u6218\u5f97\u4e45\uff0c\u4ed6\u5fc3\u601d\u8d8a\u662f\u5f00\u6717\uff0c\u602f\u610f\u65e2\u53bb\uff0c\u5c3d\u529b\u5f04\u5de7\u4ee5\u8865\u529f\u529b\u4e4b\u4e0d\u8db3\u3002\u8fd9\u201c\u6e38\u8eab\u516b\u5366\u638c\u201d\u66fe\u5728\u738b\u5251\u6770\u624b\u4e0b\u9886\u6559\u8fc7\uff0c\u5f53\u65f6\u624b\u5fd9\u811a\u4e71\uff0c\u9669\u4e9b\u547d\u4e27\u5200\u5e95\uff0c\u6b64\u523b\u5df2\u660e\u767d\u5176\u4e2d\u5965\u5999\u6240\u5728\uff0c\u5fc3\u77e5\u82e5\u662f\u8ddf\u4ed6\u4e71\u8f6c\uff0c\u5fc5\u5b9a\u7d2f\u5f97\u5934\u6655\u773c\u82b1\u3002\u6643\u773c\u4e4b\u95f4\uff0c\u738b\u5251\u82f1\u5df2\u8f6c\u5230\u81ea\u5df1\u8eab\u540e\uff0c\u7a81\u7136\u60f3\u8d77\u80e1\u5bb6\u62f3\u8c31\u4e0a\u6709\u4e00\u95e8\u201c\u56db\u8c61\u6b65\u201d\uff0c\u6b65\u6cd5\u867d\u662f\u5355\u7eaf\uff0c\u5374\u4f3c\u5927\u53ef\u7528\u5f97\uff0c\u5f53\u4e0b\u4e0d\u53ca\u7ec6\u52a0\u601d\u7d22\uff0c\u4e00\u89c1\u654c\u4eba\u8f6c\u5230\u8eab\u540e\uff0c\u7acb\u5373\u5411\u524d\u8de8\u4e86\u4e00\u6b65\u3002\u5c31\u5728\u8fd9\u65f6\u5019\uff0c\u738b\u5251\u82f1\u547c\u7684\u4e00\u638c\uff0c\u4e5f\u5df2\u51fb\u5411\u4ed6\u7684\u540e\u5fc3\u3002 \u3000\u3000\u4f17\u4eba\u773c\u89c1\u80e1\u6590\u80cc\u540e\u95e8\u6237\u6d1e\u5f00\uff0c\u5168\u65e0\u9632\u5fa1\uff0c\u4e0d\u7981\u4e3a\u4ed6\u62c5\u5fc3\uff0c\u4e0d\u6599\u4ed6\u8f7b\u8f7b\u5de7\u5de7\u5730\u5927\u6b65\u8de8\u524d\uff0c\u738b\u5251\u82f1\u8fd9\u4e00\u638c\u7adf\u5c14\u6253\u7a7a\u3002\u90a3\u201c\u6e38\u8eab\u516b\u5366\u638c\u201d\u53ea\u8981\u4e00\u4f7f\u52a8\uff0c\u518d\u65e0\u505c\u6b47\uff0c\u4e0d\u7ba1\u51fa\u638c\u662f\u5426\u6253\u4e2d\uff0c\u811a\u4e0b\u7edd\u4e0d\u505c\u7559\uff0c\u4e00\u638c\u638c\u5730\u8fde\u7ef5\u53d1\u51fa\u3002\u80e1\u6590\u9762\u5411\u5385\u95e8\uff0c\u89c1\u738b\u5251\u82f1\u62a2\u5230\u53f3\u8fb9\uff0c\u767b\u65f6\u5411\u5de6\u8de8\u4e86\u4e00\u6b65\uff0c\u4ed6\u811a\u4e0b\u8de8\u6b65\uff0c\u6b63\u4e0e\u738b\u5251\u82f1\u53d1\u638c\u540c\u65f6\u800c\u4f5c\uff0c\u4f7f\u5f97\u8fd9\u4e00\u638c\u53c8\u662f\u6253\u7a7a\u3002 \u3000\u3000\u8981\u77e5\u592a\u6781\u751f\u4e24\u4eea\uff0c\u4e24\u4eea\u751f\u56db\u8c61\uff0c\u56db\u8c61\u751f\u516b\u5366\uff0c\u8fd9\u201c\u56db\u8c61\u6b65\u201d\u4e0e\u201c\u516b\u5366\u638c\u201d\uff0c\u5176\u7406\u539f\u6709\u5171\u901a\u4e4b\u5904\u3002\u80e1\u5bb6\u62f3\u8c31\u4e0a\u7684\u201c\u56db\u8c61\u6b65\u201d\u4e43\u7ec3\u4e60\u62f3\u811a\u5668\u68b0\u7684\u5165\u95e8\u6b65\u6cd5\uff0c\u5e76\u4e0d\u80fd\u7528\u4ee5\u4f24\u654c\uff0c\u80e1\u6590\u65e9\u5df2\u7ec3\u5f97\u6781\u662f\u7eaf\u719f\u3002\u6597\u5230\u540e\u6765\uff0c\u4ed6\u7d22\u6027\u53cc\u624b\u53c9\u8170\uff0c\u51dd\u795e\u6ce8\u89c6\u5bf9\u624b\uff0c\u4e5f\u4e0d\u7406\u738b\u5251\u82f1\u662f\u5426\u53d1\u62db\uff0c\u53ea\u8981\u4ed6\u5954\u5230\u5de6\u65b9\uff0c\u5c31\u5411\u53f3\u4e00\u6b65\uff0c\u5954\u5230\u524d\u65b9\uff0c\u5c31\u9000\u540e\u4e00\u6b65\u3002\u4e0d\u8bba\u5bf9\u65b9\u5982\u4f55\u5ffd\u524d\u5ffd\u540e\uff0c\u5ffd\u4e1c\u5ffd\u897f\uff0c\u4ed6\u603b\u662f\u597d\u6574\u4ee5\u6687\u5730\u524d\u4e00\u6b65\u3001\u540e\u4e00\u6b65\u3001\u5de6\u4e00\u6b65\u3001\u53f3\u4e00\u6b65\uff0c\u6765\u6765\u53bb\u53bb\u53ea\u662f\u56db\u6b65\uff0c\u5999\u5728\u62ff\u634f\u5206\u5bf8\u6070\u5230\u597d\u5904\uff0c\u800c\u8fd9\u6b65\u6cd5\u53c8\u4e0e\u516b\u5366\u638c\u6b65\u6cd5\u7684\u516b\u5366\u65b9\u4f4d\u4e1d\u4e1d\u5165\u6263\uff0c\u6bcf\u4e00\u8de8\u6b65\uff0c\u5747\u4e0e\u5bf9\u624b\u7684\u884c\u52a8\u82e5\u5408\u7b26\u8282\uff0c\u5012\u4f3c\u4e0e\u738b\u5251\u82f1\u957f\u671f\u5171\u4e60\uff0c\u7ec3\u6210\u4e86\u5957\u5b50\u4e00\u822c\u3002 \u3000\u3000\u90a3\u201c\u6e38\u8eab\u516b\u5366\u638c\u201d\u4e00\u51fa\u624b\u5c31\u662f\u8fde\u7eed\u4e0d\u65ad\u7684\u56db\u516b\u4e09\u5341\u4e8c\u62db\uff0c\u738b\u5251\u82f1\u8d8a\u6253\u8d8a\u662f\u7126\u8e81\uff0c\u5374\u8fde\u624b\u6307\u5c16\u4e5f\u78b0\u4e0d\u5230\u80e1\u6590\u8eab\u4e0a\u3002\u8d75\u534a\u5c71\u770b\u5f97\u6697\u81ea\u53f9\u606f\uff1a\u201c\u8fd9\u4eba\u5f92\u5b66\u7236\u827a\uff0c\u53ea\u77e5\u58a8\u5b88\u6210\u6cd5\uff0c\u4e34\u654c\u65f6\u4e0d\u80fd\u968f\u673a\u5e94\u53d8\uff0c\u53e6\u521b\u65b0\u610f\uff0c\u770b\u6765\u738b\u7ef4\u626c\u662f\u540e\u7ee7\u65e0\u4eba\u4e86\u3002\u201d\u773c\u89c1\u4ed6\u7b2c\u4e8c\u8282\u7684\u4e09\u5341\u4e8c\u62db\u516b\u5366\u638c\u4e5f\u5df2\u4f7f\u5b8c\uff0c\u5546\u5b9d\u9707\u53d6\u6765\u89e3\u836f\uff0c\u53eb\u9053\uff1a\u201c\u5927\u5e08\u4f2f\uff0c\u670d\u4e86\u836f\u518d\u6536\u62fe\u90a3\u5c0f\u5b50\u3002\u201d\u8fd9\u65f6\u738b\u5251\u82f1\u7684\u5de6\u81c2\u5df2\u6e10\u6e10\u4e0d\u542c\u4f7f\u5524\uff0c\u77e5\u9053\u6bd2\u6c14\u4e0a\u884c\uff0c\u5f53\u4e0b\u8dc3\u51fa\u5708\u5b50\uff0c\u63a5\u8fc7\u89e3\u836f\u541e\u670d\u3002 \u3000\u3000\u8d75\u534a\u5c71\u9053\uff1a\u201c\u738b\u5e08\u5144\uff0c\u6211\u77a7\u2026\u2026\u201d\u738b\u5251\u82f1\u77e5\u4ed6\u5b9a\u662f\u51fa\u8a00\u529d\u89e3\uff0c\u4f8d\u4ed6\u8bdd\u4e00\u51fa\u53e3\uff0c\u81ea\u5df1\u82e5\u4e0d\u542c\u4ece\uff0c\u5012\u663e\u5f97\u4e0d\u7ed9\u4ed6\u9762\u5b50\uff0c\u5f53\u5373\u6447\u4e86\u6447\u624b\uff0c\u62a2\u4e0a\u524d\u53c8\u4e3e\u638c\u5411\u80e1\u6590\u51fb\u53bb\u3002\u53ea\u89c1\u4ed6\u6b65\u6cd5\u6781\u5c0f\uff0c\u51fa\u638c\u4e5f\u751a\u51dd\u91cd\uff0c\u539f\u6765\u662f\u4f7f\u51fa\u516b\u5366\u95e8\u4e2d\u6700\u5389\u5bb3\u7684\u201c\u5185\u516b\u5366\u638c\u6cd5\u201d\u6765\u3002\u5148\u524d\u738b\u5251\u6770\u53ea\u865a\u4f7f\u5185\u516b\u5366\u77ed\u67b6\uff0c\u5c31\u5236\u5f97\u5546\u5b9d\u9707\u65e0\u6cd5\u52a8\u624b\uff0c\u738b\u5251\u82f1\u7684\u529f\u592b\uff0c\u53c8\u6bd4\u4e43\u5f1f\u7cbe\u6e5b\u5f97\u591a\uff0c\u8fd9\u5185\u516b\u5366\u638c\u6cd5\uff0c\u51fa\u624b\u867d\u77ed\uff0c\u6bcf\u4e00\u638c\u90fd\u662f\u51cc\u5389\u72e0\u8fa3\u3002 \u3000\u3000\u80e1\u6590\u786c\u63a5\u4e86\u4e09\u62db\uff0c\u767b\u611f\u4e0d\u652f\uff0c\u5fc3\u4e2d\u6697\u53eb\uff1a\u201c\u7cdf\u7cd5\uff01\u201d\u773c\u89c1\u5bf9\u65b9\u6b65\u5b50\u5411\u5de6\u8de8\u51fa\uff0c\u731b\u5730\u63d0\u811a\u5f80\u4ed6\u5de6\u811a\u80cc\u540e\u4e0a\u8e29\u843d\u3002\u738b\u5251\u82f1\u9a82\u9053\uff1a\u201c\u4f60\u4f5c\u6b7b\u4e48\uff1f\u201d\u811a\u4e00\u7f29\uff0c\u53f3\u811a\u8e0f\u51fa\u65f6\u5c31\u9519\u4e86\u516b\u5366\u65b9\u4f4d\u3002\u738b\u7ef4\u626c\u6559\u5b50\u4e60\u827a\u4e4b\u65f6\uff0c\u89c4\u5b9a\u6781\u4e3a\u4e25\u5389\uff0c\u4e0d\u5f97\u6709\u5206\u6beb\u5dee\u5931\uff0c\u504f\u751f\u8fd9\u5927\u513f\u5b50\u53c8\u662f\u5929\u6027\u56fa\u6267\uff0c\u4e34\u654c\u65f6\u811a\u4e0b\u5b9a\u987b\u8e0f\u6b63\u65b9\u4f4d\uff0c\u624d\u80af\u51fa\u62db\u3002\u5f85\u4ed6\u53cc\u811a\u79fb\u6b63\uff0c\u80e1\u6590\u53c8\u662f\u4e00\u811a\u5bf9\u51c6\u4ed6\u811a\u80cc\u8e29\u4e86\u4e0b\u53bb\u3002\u8fd9\u822c\u80e1\u95f9\u7684\u6253\u6cd5\uff0c\u539f\u662f\u4efb\u4f55\u6210\u540d\u7684\u82f1\u96c4\u6240\u4e0d\u5c51\u4e3a\uff0c\u80e1\u6590\u5374\u4e00\u5473\u987d\u76ae\u53d6\u95f9\uff0c\u8fde\u8e29\u51e0\u811a\uff0c\u738b\u5251\u82f1\u5fc3\u795e\u5fae\u4e71\u3002\u80e1\u6590\u89c1\u5230\u6709\u673a\u53ee\u4e58\uff0c\u731b\u5730\u4e00\u638c\uff0c\u5c31\u5f80\u4ed6\u5c0f\u8179\u4e0a\u51fb\u53bb\u3002\u738b\u5251\u82f1\u53eb\u58f0\uff1a \u3000\u3000\u201c\u597d\uff01\u201d\u53cc\u638c\u9f50\u51fa\uff0c\u63a8\u5728\u4ed6\u7684\u638c\u4e0a\u3002 \u3000\u3000\u8fd9\u662f\u786c\u78b0\u786c\u7684\u5bf9\u638c\uff0c\u518d\u65e0\u8ba8\u5de7\u4e4b\u5904\uff0c\u80e1\u6590\u5168\u8eab\u4e00\u9707\uff0c\u5de6\u638c\u8ddf\u9996\u529b\u63a8\uff0c\u4f46\u4ecd\u611f\u5bf9\u65b9\u538b\u529b\u6c89\u91cd\u65e0\u6bd4\uff0c\u6b64\u65f6\u82e5\u7a0d\u4e00\u9000\u8ba9\uff0c\u5185\u810f\u7acb\u4e3a\u5bf9\u65b9\u638c\u529b\u6240\u4f24\uff0c\u53ea\u5f97\u594b\u529b\u62b5\u6321\u3002 \u3000\u3000\u8d75\u534a\u5c71\u89c1\u80e1\u6590\u5df2\u7136\u8f93\u5b9a\uff0c\u7b11\u9053\uff1a\u201d\u5b69\u5b50\uff0c\u4f60\u8f93\u5566\uff0c\u8fd8\u6bd4\u62da\u4ec0\u4e48\uff1f\u201d\u4f38\u624b\u5728\u4ed6\u80cc\u4e0a\u8f7b\u8f7b\u4e00\u62cd\uff0c\u4e00\u80a1\u5185\u529b\u4ece\u4ed6\u8eab\u4e0a\u4f20\u5c06\u8fc7\u53bb\u3002\u738b\u5251\u82f1\u53cc\u81c2\u4e00\u9178\uff0c\u80f8\u53e3\u5fae\u70ed\uff0c\u6025\u5fd9\u64a4\u638c\u540e\u9000\u3002\u8d75\u534a\u5c71\u9053\uff1a\u201d\u738b\u5144\uff0c\u4f60\u7684\u529f\u529b\u81ea\u6bd4\u8fd9\u5b69\u5b50\u9ad8\u5f97\u591a\uff0c\u90a3\u8fd8\u7528\u6bd4\u4ec0\u4e48\uff1f\u201d\u4ed6\u8f7b\u62cd\u80e1\u6590\u7684\u80a9\u5934\uff0c\u8d5e\u9053\uff1a\u201c\u4e86\u4e0d\u8d77\uff0c\u4e86\u4e0d\u8d77\uff0c\u518d\u8fc7\u4e94\u516d\u5e74\uff0c\u8fde\u6211\u4e5f\u4e0d\u662f\u4f60\u7684\u654c\u4e4e\u5566\u3002\u201d\u8a00\u4e0b\u81ea\u7136\u662f\u8bf4\uff1a\u4f60\u738b\u8001\u5144\u66f4\u52a0\u4e0d\u7528\u63d0\u4e86\u3002 \u3000\u3000\u738b\u5251\u82f1\u8138\u4e0a\u4e00\u70ed\uff0c\u81ea\u77e5\u529f\u592b\u4e0e\u8d75\u534a\u5c71\u5dee\u5f97\u592a\u8fdc\uff0c\u8981\u5f85\u4ea4\u4ee3\u51e0\u53e5\u573a\u9762\u8bdd\uff0c\u8ddf\u8fd9\u5b69\u5b50\u5374\u53c8\u4e0d\u77e5\u4ece\u4f55\u8bf4\u8d77\uff0c\u4e0d\u7531\u5f97\u6014\u5728\u5f53\u5730\uff0c\u4e00\u8a00\u4e2a\u53d1\u3002\u738b\u5251\u6770\u89c1\u5144\u957f\u7684\u5de6\u638c\u7d2b\u9ed1\uff0c\u4e2d\u6bd2\u751a\u6df1\uff0c\u5411\u5546\u8001\u592a\u9053\uff1a\u201c\u6709\u6ca1\u6709\u5916\u6577\u7684\u89e3\u6bd2\u836f\uff1f\u201d\u5546\u8001\u4eba\u6447\u6447\u5934\u3002\u8d75\u534a\u5c71\u4ece\u6000\u4e2d\u53d6\u51fa\u4e00\u4e2a\u7ea2\u8272\u5c0f\u74f6\uff0c\u62d4\u5f00\u6b23\u585e\uff0c\u8bf4\u9053\uff1a\u201c\u5144\u5f1f\u81ea\u5408\u7684\u89e3\u6bd2\u836f\uff0c\u5f88\u6709\u70b9\u513f\u529f\u6548\u3002\u201d\u738b\u5251\u6770\u77e5\u4ed6\u662f\u4f7f\u6697\u5668\u7684\u5927\u884c\u5bb6\uff0c\u8eab\u4e0a\u4e0d\u5e26\u89e3\u6bd2\u836f\u5219\u5df1\uff0c\u82e5\u662f\u643a\u5e26\uff0c\u5b9a\u7136\u5e94\u9a8c\u5982\u795e\uff0c\u4ed6\u6302\u5ff5\u5144\u957f\u5b89\u5371\uff0c\u4f38\u51fa\u624b\u638c\u3002\u8d75\u534a\u5c71\u5728\u4ed6\u638c\u5fc3\u5012\u4e86\u5c11\u8bb8\uff0c\u7b11\u9053\uff1a\u201c\u5c3d\u591f\u7528\u4e86\u3002\u201d\u8fd9\u4e00\u6765\uff0c\u738b\u6c0f\u5144\u5f1f\u65e0\u8bba\u5982\u4f55\u4e0d\u80fd\u518d\u5bf9\u80e1\u6590\u7559\u96be\u3002 \u3000"}),showOnHomepage:!0},{name:"\u7b2c\u56db\u7ae0\u3000\u94c1\u5385\u70c8\u706b",content:(0,a.jsx)(a.Fragment,{children:"\u8d75\u534a\u5c71\u53cc\u624b\u8d1f\u5728\u80cc\u540e\uff0c\u5728\u5385\u4e2d\u7f13\u6b65\u6765\u53bb\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u5b66\u6b66\u7684\uff0c\u529f\u592b\u81ea\u7136\u6709\u9ad8\u6709\u4e0b\uff0c\u4f46\u53ea\u8981\u5fc3\u5730\u5149\u660e\u78ca\u843d\uff0c\u884c\u4e8b\u65e0\u6127\u4e8e\u5927\u5730\uff0c\u90a3\u4e48\u529f\u592b\u9ad8\u7684\u56fa\u7136\u597d\uff0c\u6b66\u827a\u4f4e\u4e5f\u662f\u4e00\u822c\u53d7\u4eba\u656c\u91cd\u3002\u6211\u8d75\u67d0\u4eba\u751f\u5e73\u6700\u6068\u7684\u5c31\u662f\u884c\u4e8b\u6b79\u6bd2\u3001\u5351\u9119\u65e0\u803b\u7684\u5c0f\u4eba\u3002\u201d\u4ed6\u8d8a\u8bf4\u58f0\u97f3\u8d8a\u662f\u4e25\u5389\uff0c\u53cc\u76ee\u77aa\u7740\u9648\u79b9\u4e0d\u52a8\u3002 \u3000\u3000\u9648\u79b9\u4f4e\u4e0b\u4e86\u5934\uff0c\u76ee\u5149\u4e0d\u6562\u4e0e\u4ed6\u76f8\u63a5\uff0c\u7a81\u7136\u4e00\u77a5\u773c\u4e4b\u95f4\uff0c\u5413\u4e86\u4e00\u8df3\u3002\u539f\u6765\u5546\u8001\u592a\u53d1\u51fa\u4e03\u679d\u91d1\u9556\uff0c\u7ed9\u8d75\u534a\u5c71\u63a5\u4f4f\u540e\u63b7\u5728\u5730\u4e0b\u3002\u80e1\u6590\u7528\u4e00\u6280\u7f25\u523a\u4f24\u738b\u5251\u82f1\u540e\uff0c\u63a5\u7740\u5bf9\u638c\uff0c\u90a3\u679d\u9556\u4ecd\u662f\u4e22\u843d\u5728\u5730\u3002\u8fd9\u65f6\u8d75\u534a\u5c71\u5728\u5385\u4e2d\u6765\u53bb\uff0c\u8db3\u4e0b\u6697\u6697\u4f7f\u52b2\uff0c\u7adf\u5c06\u4e03\u679d\u91d1\u9556\u8e0f\u5f97\u5d4c\u5165\u4e86\u65b9\u7816\u4e4b\u4e2d\uff0c\u9556\u4e0e\u7816\u9f50\uff0c\u751a\u662f\u5e73\u6574\u3002\u4f17\u4eba\u89c1\u9648\u79b9\u8138\u4e0a\u53d8\u8272\uff0c\u987a\u7740\u4ed6\u773c\u5149\u4e00\u770b\uff0c\u90fd\u662f\u5927\u4e3a\u60ca\u5947\uff0c\u77e5\u9053\u4ed6\u9732\u8fd9\u624b\u529f\u592b\uff0c\u4e00\u6765\u662f\u8b66\u544a\u5546\u8001\u592a\u4e0d\u5f97\u518d\u4f7f\u6b79\u6bd2\u6697\u5668\uff0c\u4e8c\u6765\u662f\u8981\u903c\u9648\u79b9\u51fa\u53bb\u7b97\u5e10\uff0c\u53eb\u65c1\u4eba\u4e0d\u6562\u963b\u62e6\u3002 \u3000\u3000\u9648\u79b9\u56db\u4e0b\u4e00\u671b\uff0c\u4f46\u89c1\u738b\u6c0f\u5144\u5f1f\u5fd9\u7740\u88f9\u4f24\uff0c\u5546\u8001\u592a\u4e0e\u5546\u5b9d\u9707\u54ac\u7259\u5207\u9f7f\uff0c\u9a6c\u884c\u7a7a\u5fae\u5fae\u70b9\u5934\uff0c\u6bb7\u4ef2\u7fd4\u8138\u5982\u6b7b\u7070\uff0c\u77e5\u9053\u6ca1\u4e00\u4e2a\u6562\u51fa\u624b\u76f8\u52a9\uff0c\u5c06\u5fc3\u4e00\u6a2a\uff0c\u5927\u58f0\u9053\uff1a\u201c\u597d\u554a\uff0c\u5e73\u7d20\u79f0\u5144\u9053\u5f1f\uff0c\u90fd\u662f\u597d\u670b\u53cb\uff0c\u4eca\u65e5\u6211\u59d3\u9648\u7684\u8eab\u53d7\u5de8\u8d3c\u80c1\u8feb\uff0c\u597d\u670b\u53cb\u5374\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u59d3\u8d75\u7684\uff0c\u54b1\u4eec\u4e5f\u4e0d\u7528\u51fa\u53bb\uff0c\u5c31\u5728\u8fd9\u91cc\u52a8\u624b\u5427\u3002\u201d\u8d75\u534a\u5c71\u521a\u8bf4\u5f97\u4e00\u4e2a\u201c\u597d\u201d\u5b57\uff0c\u5ffd\u542c\u80cc\u540e\u98ce\u58f0\u54cd\u52a8\uff0c\u77e5\u6709\u6697\u5668\u6765\u88ad\uff0c\u63a5\u7740\u542c\u5f97\u4e00\u58f0\u559d\u9053\uff1a\u201c\u597d\u670b\u53cb\u6765\u5566\uff01\u201d \u3000\u3000\u8d75\u534a\u5c71\u4e5f\u4e0d\u56de\u5934\uff0c\u53cd\u8fc7\u624b\u53bb\u4e24\u6307\u4e00\u5939\uff0c\u63a5\u4f4f\u4e86\u4e00\u628a\u5c0f\u5c0f\u7684\u98de\u5200\uff0c\u4f46\u89c9\u90a3\u98de\u5200\u5c04\u6765\u52bf\u9053\u52b2\u6025\uff0c\u5168\u662f\u9633\u521a\u4e4b\u529b\uff0c\u63a5\u5728\u624b\u4e0a\u65f6\u5200\u8eab\u5fae\u5fae\u4e00\u9707\uff0c\u548c\u798f\u5efa\u8386\u7530\u5c11\u6797\u6d3e\u53d1\u5c04\u6697\u5668\u7684\u624b\u6cd5\u53c8\u81ea\u4e0d\u540c\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u4f4d\u597d\u670b\u53cb\u539f\u6765\u662f\u5d69\u5c71\u5c11\u6797\u5bfa\u7684\uff0c\u53ef\u662f\u4e0d\u7591\u5927\u5e08\u7684\u9ad8\u8db3\u5417\uff1f\u201d \u3000\u3000\u53d1\u5c04\u8fd9\u67c4\u98de\u5200\u7684\uff0c\u6b63\u662f\u5d69\u5c71\u5c11\u6797\u6d3e\u7684\u9752\u5e74\u597d\u624b\u53e4\u822c\u82e5\u3002\u738b\u6c0f\u5144\u5f1f\u3001\u6bb7\u4ef2\u7fd4\u3001\u9648\u79b9\u7b49\u90fd\u662f\u4e00\u60ca\uff0c\u4f46\u89c1\u8d75\u534a\u5c71\u5e76\u672a\u56de\u8eab\uff0c\u5c1a\u672a\u89c1\u5230\u53e4\u822c\u82e5\u7684\u4eba\u5f71\uff0c\u5374\u5c06\u4ed6\u7684\u95e8\u6d3e\u5e08\u627f\u731c\u5f97\u4e00\u70b9\u513f\u4e0d\u9519\u3002 \u3000\u3000\u8d75\u534a\u5c71\u5fc3\u4e2d\u5374\u60f3\uff0c\u6211\u7ea2\u82b1\u4f1a\u53ea\u50fb\u5904\u56de\u7586\u6570\u5e74\uff0c\u79bb\u4e2d\u539f\u5e76\u65e0\u591a\u65f6\uff0c\u770b\u6765\u540d\u5934\u5df2\u4e0d\u53ca\u5f80\u65f6\u7684\u54cd\u4eae\uff0c\u6211\u8981\u4fdd\u62a4\u4e00\u4e2a\u5b69\u5b50\uff0c\u53eb\u4e00\u4e2a\u4eba\u51fa\u5916\uff0c\u5c45\u7136\u4e0d\u65ad\u6709\u4eba\u524d\u6765\u963b\u624b\u963b\u811a\uff0c\u4eca\u65e5\u82e5\u4e0d\u7acb\u5a01\uff0c\u5012\u53eb\u540e\u751f\u5c0f\u5b50\u4eec\u5c06\u7ea2\u82b1\u4f1a\u77a7\u5f97\u5c0f\u4e86\uff0c\u5f53\u4e0b\u6717\u58f0\u8bf4\u9053\uff1a\u201d\u4f60\u8fd9\u4f4d\u597d\u670b\u53cb\u7ad9\u7740\u53ef\u522b\u52a8\u3002\u201d\u4e0d\u7b49\u53e4\u822c\u82e5\u56de\u7b54\uff0c\u53cc\u624b\u5411\u540e\u626c\u4e86\u51e0\u626c\uff0c\u8ddf\u7740\u8f6c\u8fc7\u8eab\u6765\uff0c\u4e24\u624b\u8fde\u6325\uff0c\u4f17\u4eba\u4e00\u9635\u773c\u82b1\u9563\u4e71\uff0c\u4f46\u89c1\u98de\u5200\u3001\u91d1\u7f25\u3001\u8896\u7bad\u3001\u80cc\u5f2f\u3001\u94c1\u83e9\u63d0\u3001\u98de\u8757\u77f3\u3001\u94c1\u83b2\u5b50\u3001\u91d1\u94b1\u7f25\uff0c\u53ee\u53ee\u5f53\u5f53\u54cd\u58f0\u4e0d\u7edd\uff0c\u9f50\u5411\u53e4\u822c\u82e5\u5c04\u53bb\u3002 \u3000\u3000\u738b\u5251\u82f1\u5927\u9a87\uff0c\u53eb\u9053\uff1a\u201c\u8d75\u5144\u624b\u4e0b\u5bb9\u60c5\u3002\u201d\u8d75\u534a\u5c71\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u81ea\u8be5\u624b\u4e0b\u5bb9\u60c5\u3002\u201d \u3000\u3000\u4f17\u4eba\u77a7\u53e4\u822c\u82e5\u65f6\uff0c\u65e0\u4e0d\u76ee\u77aa\u53e3\u5446\u3002\u4f46\u89c1\u4ed6\u80cc\u9760\u5899\u58c1\uff0c\u5468\u8eab\u9489\u6ee1\u4e86\u6697\u5668\uff0c\u5374\u65e0\u4e00\u679a\u4f24\u5230\u4ed6\u7684\u8eab\u5b50\u3002\u53e4\u822c\u82e5\u534a\u664c\u60ca\u9b42\u4e0d\u5b9a\uff0c\u9694\u4e86\u597d\u4e00\u9635\uff0c\u8fd9\u624d\u79bb\u5f00\u5899\u58c1\uff0c\u56de\u8fc7\u5934\u6765\uff0c\u53ea\u89c1\u767e\u4f59\u679a\u6697\u5668\u6253\u5728\u5899\u4e0a\uff0c\u9690\u9690\u4f9d\u7740\u81ea\u5df1\u8eab\u5b50\uff0c\u5d4c\u6210\u4e00\u4e2a\u4eba\u5f62\u3002 \u3000\u3000\u4ed6\u60e8\u7136\u65e0\u8bed\uff0c\u5411\u8d75\u534a\u5c71\u4e00\u63d6\u5230\u5730\uff0c\u76f4\u51fa\u5927\u95e8\uff0c\u4e5f\u4e0d\u5411\u798f\u516c\u5b50\u8f9e\u522b\uff0c\u5f84\u81ea\u8d70\u4e86\u3002 \u3000\u3000\u8d75\u534a\u5c71\u6b64\u624b\u4e00\u9732\uff0c\u5373\u662f\u5904\u4e86\u9648\u79b9\u6b7b\u5211\uff0c\u66f4\u8fd8\u6709\u8c01\u6562\u51fa\u5934\u5e72\u9884\uff1f\u4f46\u9648\u79b9\u4e34\u6b7b\u8fd8\u662f\u5f3a\u53e3\uff0c\u8bf4\u9053\uff1a\u201c\u81ea\u6765\u5b98\u532a\u4e0d\u4e24\u7acb\uff0c\u6211\u4e00\u6b7b\u62a5\u7b54\u798f\u516c\u5b50\uff0c\u90a3\u4fbf\u662f\u4e86\u3002\u201d \u3000\u3000\u8d75\u534a\u5c71\u5927\u6012\uff0c\u5411\u738b\u5251\u82f1\u7b49\u8bf4\u9053\uff1a\u201c\u672c\u6765\u592a\u6781\u95e8\u4e2d\u51fa\u6b64\u8d25\u7c7b\uff0c\u662f\u5728\u4e0b\u95e8\u6237\u4e4b\u7f9e\uff0c\u539f\u60f3\u79c1\u4e0b\u4e86\u7ed3\uff0c\u53ef\u662f\u4ed6\u975e\u53eb\u6211\u6296\u4e2a\u4e00\u6e05\u4e8c\u695a\u4e0d\u53ef\u3002\u201d\u9648\u79b9\u81ea\u5df1\u5374\u4e5f\u771f\u4e0d\u77e5\u9053\uff0c\u4ec0\u4e48\u4e8b\u4e0a\u5f97\u7f6a\u4e86\u8fd9\u4f4d\u7ea2\u82b1\u4f1a\u4e09\u5f53\u5bb6\uff0c\u8981\u77e5\u4ed6\u4e3a\u4eba\u60c5\u660e\u5706\u6ed1\uff0c\u539f\u662f\u4e0d\u6613\u4e0e\u4eba\u7ed3\u6028\u7684\uff0c\u4fbf\u63a5\u53e3\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5929\u4e0b\u4e8b\u62ac\u4e0d\u8fc7\u4e00\u4e2a\u7406\u5b57\u3002\u4f60\u8bf4\u4e86\u51fa\u6765\uff0c\u8bf7\u5927\u5bb6\u8bc4\u4e2a\u9053\u7406\u3002\u201d \u3000\u3000\u8d75\u534a\u5c71\u201c\u54fc\u201d\u7684\u4e00\u58f0\uff0c\u6307\u7740\u90a3\u4e2a\u9ed1\u80a4\u5927\u773c\u7684\u5c0f\u59d1\u5a18\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u4e0d\u8ba4\u5f97\u8fd9\u5c0f\u59b9\u59b9\u4e48\uff1f\u201d\u9648\u79b9\u6447\u5934\u9053\uff1a\u201c\u4e0d\u8ba4\u5f97\uff0c\u4ece\u6765\u6ca1\u89c1\u8fc7\u3002\u201d\u8d75\u534a\u5c71\u9053\uff1a\u201c\u5c31\u53ef\u60dc\u4f60\u8ba4\u5f97\u5979\u7236\u4eb2\u3002\u5979\u662f\u5e7f\u5e73\u5e9c\u5415\u5e0c\u8d24\u7684\u5973\u513f\u3002\u201d \u3000\u3000\u6b64\u8a00\u4e00\u51fa\uff0c\u9648\u79b9\u672c\u6765\u60e8\u767d\u7684\u8138\u8272\u66f4\u52a0\u767d\u5f97\u5f97\u6015\u3002\u4f17\u4eba\u201c\u54e6\u201d\u7684\u4e00\u58f0\uff0c\u9f50\u5411\u8fd9\u5973\u5b69\u671b\u53bb\u3002\u8fd9\u5973\u5b69\u53ea\u6709\u5341\u4e8c\u4e09\u5c81\uff0c\u4f46\u6ee1\u8138\u98ce\u971c\uff0c\u663e\u662f\u5c0f\u5c0f\u7684\u4e00\u751f\u4e4b\u4e2d\u5df2\u53d7\u8fc7\u8bb8\u591a\u56f0\u82e6\u6298\u78e8\u3002\u5979\u6307\u7740\u9648\u79b9\uff0c\u5389\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u6ca1\u89c1\u8fc7\u6211\uff0c\u6211\u53ef\u89c1\u8fc7\u4f60\u3002 \u3000\u3000\u90a3\u5929\u665a\u4e0a\u4f60\u6740\u6211\u5144\u5f1f\uff0c\u6740\u6211\u7239\u7239\uff0c\u6211\u5728\u7a97\u5916\u770b\u5f97\u6e05\u6e05\u695a\u695a\u3002\u6211\u6bcf\u5929\u665a\u4e0a\u505a\u68a6\uff0c\u6ca1\u4e00\u6b21\u4e0d\u89c1\u5230\u4f60\u3002\u201d\u8fd9\u51e0\u53e5\u8bdd\u8bf4\u5f97\u65a9\u9489\u622a\u94c1\uff0c\u9648\u79b9\u53c8\u662f\u786e\u66fe\u505a\u8fc7\u90a3\u4ef6\u4e8b\uff0c\u5f20\u53e3\u7ed3\u820c\u5730\u201c\u554a\uff0c\u554a\u201d\u51e0\u58f0\uff0c\u6ca1\u518d\u5206\u8fa9\u3002 \u3000\u3000\u8d75\u534a\u5c71\u5411\u4f17\u4eba\u53cc\u624b\u4e00\u62f1\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u59d3\u9648\u7684\u8bf4\u5f97\u597d\uff0c\u5929\u4e0b\u4e8b\u62ac\u4e0d\u8fc7\u4e00\u4e2a\u7406\u5b57\u3002\u6211\u628a\u8fd9\u4ef6\u4e8b\u7684\u524d\u56e0\u540e\u679c\uff0c\u8bf4\u51fa\u6765\u8bf7\u5927\u5bb6\u8bc4\u4e2a\u9053\u7406\u3002\u5404\u4f4d\u60f3\u5fc5\u90fd\u77e5\u9053\uff0c\u5e7f\u5e73\u5e9c\u592a\u6781\u95e8\u5e08\u5144\u5f1f\u4e09\u4eba\uff0c\u6b66\u529f\u4ee5\u5c0f\u5e08\u5f1f\u5415\u5e0c\u8d24\u6700\u5f3a\u3002\u8fd9\u59d3\u9648\u7684\uff0c\u4f60\u79f0\u5415\u5e0c\u8d24\u4ec0\u4e48\u554a\uff1f\u201d\u9648\u79b9\u4f4e\u4e0b\u4e86\u5934\uff0c\u9053\uff1a\u201c\u4ed6\u662f\u6211\u5e08\u53d4\u3002\u201d\u5fc3\u60f3\u8d75\u534a\u5c71\u8ff0\u8bf4\u5f80\u4e8b\uff0c\u4e5f\u4e0d\u5fc5\u8ddf\u4ed6\u5206\u8fa9\uff0c\u5fc3\u4e2d\u6697\u6253\u8131\u8eab\u9003\u8d70\u7684\u4e3b\u610f\u3002 \u3000\u3000\u8d75\u534a\u5c71\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5415\u5e0c\u8d24\u662f\u4ed6\u5e08\u53d4\u3002\u8bf4\u9053\u5415\u5e0c\u8d24\u8fd9\u4eba\uff0c\u5728\u4e0b\u53ef\u4e0e\u4ed6\u7d20\u4e0d\u76f8\u8bc6\uff0c\u4ed6\u662f\u5317\u4eac\u738b\u5e9c\u7684\u6559\u5e08\u7237\uff0c\u54b1\u4eec\u4e61\u4e0b\u4eba\u54ea\u91cc\u9ad8\u6500\u5f97\u4e0a\uff1f\u201d\u8a00\u4e0b\u4e4b\u610f\uff0c\u7ade\u662f\u900f\u7740\u5341\u5206\u4e0d\u6ee1\uff0c\u53ea\u662f\u4ed6\u5b58\u5fc3\u539a\u9053\uff0c\u53c8\u662f\u788d\u7740\u90a3\u5c0f\u59d1\u5a18\u7684\u9762\u5b50\uff0c\u53ea\u8bf4\u5230\u6b64\u5904\u4e3a\u6b62\uff0c\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u5728\u4e0b\u9690\u5c45\u56de\u7586\uff0c\u4e2d\u539f\u6b66\u6797\u7684\u6069\u6028\u539f\u672c\u4e0d\u95fb\u4e0d\u95ee\uff0c\u53ef\u662f\u6709\u4e00\u65e5\u8fd9\u5c0f\u59d1\u5a18\u5bfb\u5230\u4e86\u5728\u4e0b\uff0c\u54ed\u62dc\u5728\u5730\uff0c\u8bf4\u8981\u8bf7\u6211\u4e3b\u6301\u516c\u9053\u3002\u5c0f\u59d1\u5a18\uff0c\u4f60\u5c06\u90a3\u4e24\u4ef6\u4e1c\u897f\u53d6\u51fa\u6765\uff0c\u7ed9\u5404\u4f4d\u53d4\u4f2f\u4eec\u77a7\u77a7\u3002\u201d \u3000\u3000\u90a3\u5973\u5b69\u89e3\u4e0b\u80cc\u540e\u7684\u5305\u88f9\uff0c\u73cd\u800c\u91cd\u4e4b\u5730\u53d6\u51fa\u4e00\u4e2a\u5e03\u5305\u6253\u5f00\uff0c\u70db\u5149\u4e0b\u5404\u4eba\u77a7\u5f97\u660e\u767d\uff0c\u8d6b\u7136\u662f\u4e00\u5bf9\u5e72\u67af\u7684\u4eba\u624b\uff0c\u65c1\u8fb9\u8fd8\u6709\u4e00\u5757\u767d\u5e03\uff0c\u6ee1\u5199\u7740\u8840\u5b57\u3002\u8d75\u534a\u5c71\u9053\uff1a\u201c\u4f60\u8bf4\u7ed9\u5404\u4f4d\u542c\u5427\u3002\u201d \u3000\u3000\u90a3\u5c0f\u59d1\u5a18\u6367\u7740\u4e00\u53cc\u4eba\u624b\uff0c\u6cea\u5982\u96e8\u4e0b\uff0c\u54fd\u54bd\u9053\uff1a\u201c\u6211\u7239\u7239\u751f\u4e86\u75c5\uff0c\u5df2\u597d\u4e45\u8eba\u7740\u4e0d\u80fd\u8d77\u6765\u3002\u6709\u4e00\u5929\uff0c\u8fd9\u59d3\u9648\u7684\u7a81\u7136\u5e26\u4e86\u53e6\u5916\u4e09\u4e2a\u6076\u4eba\uff0c\u534a\u591c\u91cc\u6765\u5230\u6211\u5bb6\uff0c\u8bf4\u662f\u5949\u738b\u7237\u4e4b\u547d\uff0c\u8981\u7239\u7239\u8bf4\u592a\u6781\u62f3\u4ec0\u4e48\u4e5d\u8bc0\u7684\u79d8\u5965\uff0c\u4e0d\u77e5\u600e\u6837\uff0c\u4ed6\u4eec\u4e89\u5435\u8d77\u6765\u3002\u6211\u5f1f\u5f1f\u5413\u5f97\u54ed\u53eb\u51fa\u58f0\uff0c\u8fd9\u59d3\u9648\u7684\u6293\u4f4f\u4e86\u4ed6\uff0c\u626c\u8d77\u5b9d\u5251\u5a01\u5413\u6211\u7239\u7239\uff0c\u8bf4\u9053\u8981\u662f\u4e0d\u8bf4\uff0c\u5c31\u5c06\u6211\u5f1f\u5f1f\u4e00\u5251\u6740\u6b7b\u3002\u6211\u7239\u7239\u8bf4\u4e86\u51e0\u53e5\u8bdd\uff0c\u6211\u4e5f\u4e0d\u61c2\uff0c\u4ed6\u2026\u2026\u4ed6\u2026\u2026 \u3000\u3000\u5c31\u5c06\u6211\u5f1f\u5f1f\u6740\u6b7b\u4e86\u3002\u201d\u8bf4\u5230\u8fd9\u91cc\uff0c\u773c\u6cea\u66f4\u662f\u4e0d\u7edd\u6d41\u4e0b\u3002 \u3000\u3000\u80e1\u6590\u53eb\u9053\uff1a\u201c\u8fd9\u6837\u7684\u6076\u4eba\uff0c\u8fd8\u4e0d\u5feb\u5bb0\u4e86\u3002\u201d\u90a3\u5c0f\u59d1\u5a18\u63d0\u8d77\u8863\u8896\u62b9\u4e86\u62b9\u773c\u6cea\uff0c\u8bf4\u9053\uff1a\u201c\u540e\u6765\u6211\u7239\u7239\u8ddf\u4ed6\u4eec\u52a8\u624b\uff0c\u4ed6\u4eec\u4eba\u591a\uff0c\u6211\u7239\u7239\u53c8\u751f\u7740\u75c5\uff0c\u5c31\u7ed9\u8fd9\u574f\u4eba\u5bb3\u6b7b\u4e86\u3002\u540e\u6765\u5b59\u4f2f\u4f2f\u6765\u5230\u6211\u5bb6\u91cc\uff0c\u6211\u5c31\u8ddf\u4ed6\u8bf4\u2026\u2026\u201d\u5c0f\u59d1\u5a18\u4e0d\u61c2\u6b66\u6797\u4e4b\u4e2d\u7684\u6069\u6028\u5173\u8282\uff0c\u8bf4\u8d77\u6765\u6709\u70b9\u4e0d\u660e\u4e0d\u767d\u3002 \u3000\u3000\u8d75\u534a\u5c71\u63d2\u53e3\u9053\uff1a\u201c\u5979\u8bf4\u7684\u5b59\u4f2f\u4f2f\uff0c\u5c31\u662f\u5e7f\u5e73\u5e9c\u592a\u6781\u95e8\u7684\u638c\u95e8\u4eba\u5b59\u521a\u5cf0\u3002\u201d \u3000\u3000\u8fd9\u4e2a\u4eba\u7684\u540d\u5934\u5927\u5bb6\u662f\u77e5\u9053\u7684\uff0c\u4e8e\u662f\u90fd\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u90a3\u5c0f\u59d1\u5a18\u53c8\u9053\uff1a\u201c\u5b59\u4f2f\u4f2f\u60f3\u4e86\u51e0\u5929\uff0c\u5ffd\u7136\u53eb\u6211\u8fc7\u53bb\uff0c\u4ed6\u62ff\u51fa\u5200\u6765\uff0c\u4e00\u5200\u780d\u4e0b\u4e86\u81ea\u5df1\u7684\u5de6\u624b\uff0c\u8638\u4e86\u8840\u5199\u6210\u8fd9\u5c01\u8840\u4e66\uff0c\u53c8\u5c06\u5200\u5b50\u653e\u5728\u684c\u5b50\u4e0a\uff0c\u7528\u529b\u628a\u53f3\u624b\u6325\u5728\u5200\u53e3\u4e0a\uff0c\u53c8\u780d\u4e0b\u4e86\u53f3\u624b\uff0c\u53eb\u6211\u2026\u2026\u53eb\u6211\u2026\u2026\u9001\u53bb\u56de\u7586\u7ed9\u8d75\u4f2f\u4f2f\uff0c\u8bf4\u592a\u6781\u95e8\u4e2d\u9664\u4e86\u8d75\u4f2f\u4f2f\uff0c\u518d\u65e0\u65c1\u4eba\u62a5\u5f97\u6211\u7239\u7239\u8840\u4ec7\u2026\u2026\u201d\u4f17\u4eba\u542c\u5f97\u9762\u9762\u76f8\u89d1\uff0c\u53ea\u89c9\u5f97\u8fd9\u771f\u662f\u4eba\u95f4\u7684\u4e00\u4ef6\u6781\u5927\u60e8\u4e8b\uff0c\u53ea\u662f\u90a3\u5c0f\u59d1\u5a18\u8bf4\u5f97\u592a\u4e0d\u6e05\u695a\uff0c\u5b9e\u5728\u4e0d\u61c2\u3002 \u3000\u3000\u8d75\u534a\u5c71\u9053\uff1a\u201c\u8fd9\u5b59\u521a\u5cf0\u5728\u4e0b\u662f\u8bc6\u5f97\u7684\uff0c\u5f53\u5e74\u4ed6\u77a7\u4e0d\u8d77\u6211\u8d75\u534a\u5c71\uff0c\u66fe\u6765\u6e29\u5dde\u8ddf\u6211\u6253\u8fc7\u4e00\u573a\u67b6\uff0c\u60f3\u4e0d\u5230\u7adf\u56e0\u5982\u6b64\uff0c\u5fc3\u4e2d\u6709\u4e86\u6211\u8d75\u67d0\u4eba\u7684\u5f71\u5b50\u3002\u201d\u4f17\u4eba\u5fc3\u60f3\uff1a\u201c\u8fd9\u4e00\u573a\u67b6\uff0c\u5b9a\u662f\u5b59\u521a\u5cf0\u8f93\u4e86\u3002\u201d \u3000\u3000\u8d75\u534a\u5c71\u53c8\u9053\uff1a\u201c\u5b59\u521a\u5cf0\u8fd9\u5c01\u8840\u4e66\u4e0a\u8bf4\uff0c\u4ed6\u662f\u5e7f\u5e73\u592a\u6781\u95e8\u638c\u95e8\uff0c\u81ea\u6127\u65e0\u80fd\uff0c\u6536\u62fe\u4e0d\u4e0b\u8fd9\u59d3\u9648\u7684\u53db\u5f92\uff0c\u56e0\u6b64\u780d\u4e0b\u53cc\u624b\uff0c\u9001\u7ed9\u6211\u8d75\u67d0\u4eba\uff0c\u4fe1\u4e0a\u8bf4\u4ec0\u4e48\u2018\u4e45\u6155\u8d75\u7237\u4e91\u5929\u9ad8\u4e49\uff0c\u6025\u4eba\u4e4b\u96be\u2019\u4e91\u4e91\u3002\u563f\uff0c\u4ed6\u9001\u6211\u4e00\u5bf9\u624b\u638c\u3002\u518d\u52a0\u4e00\u9876\u5927\u5e3d\u5b50\uff0c\u8d75\u67d0\u4eba\u867d\u8ddf\u4ed6\u6ca1\u534a\u70b9\u4ea4\u60c5\uff0c\u8fd9\u4ef6\u4e8b\u53ef\u4e0d\u80fd\u4e0d\u7ed9\u4ed6\u529e\u4e86\uff0c\u201d \u3000\u3000\u9648\u79b9\u60e8\u767d\u7740\u8138\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u5c01\u8840\u4e66\uff0c\u672a\u5fc5\u662f\u6211\u5b59\u5e08\u4f2f\u7684\u4eb2\u7b14\uff0c\u6211\u5f97\u77a7\u77a7\u3002\u201d \u3000\u3000\u8bf4\u7740\u6162\u6162\u8d70\u5230\u5c0f\u59d1\u5a18\u8eab\u65c1\uff0c\u53bb\u53d6\u8840\u4e66\uff0c\u7a81\u7136\u624b\u8155\u4e00\u7ffb\uff0c\u5bd2\u5149\u95ea\u5904\uff0c\u53f3\u624b\u4e2d\u4e00\u67c4\u5315\u9996\u5df2\u6307\u7740\u5c0f\u59d1\u5a18\u7684\u540e\u5fc3\uff0c\u53eb\u9053\uff1a\u201c\u597d\uff0c\u90a3\u5c31\u540c\u5f52\u4e8e\u5c3d\u3002\u201d \u3000\u3000\u8fd9\u4e00\u4e0b\u53d8\u751f\u4e0d\u6d4b\uff0c\u4f17\u4eba\u5747\u672a\u6599\u53ca\u3002\u8d75\u534a\u5c71\u62a2\u4e0a\u4e24\u6b65\uff0c\u5f85\u8981\u593a\u4eba\uff0c\u5374\u89c1\u9648\u79b9\u5de6\u81c2\u7d27\u7d27\u627c\u5728\u5415\u5c0f\u59b9\u9888\u4e2d\uff0c\u4f4e\u6c89\u7740\u55d3\u5b50\u559d\u9053\uff1a\u201c\u4f60\u518d\u4e0a\u524d\u4e00\u6b65\uff0c\u8fd9\u5973\u5a03\u5b50\u7684\u6027\u547d\u5c31\u662f\u4f60\u5bb3\u7684\u3002\u201d\u8d75\u534a\u5c71\u4e00\u60ca\uff0c\u81ea\u7136\u800c\u7136\u5730\u5012\u9000\u4e00\u6b65\uff0c\u4e00\u65f6\u508d\u60f6\u65e0\u8ba1\uff0c\u5fc3\u60f3\uff1a\u201c\u90a3\u4fbf\u5982\u4f55\u662f\u597d\uff1f\u82e5\u662f\u4e03\u5f1f\u5728\u6b64\uff0c\u4ed6\u5b9a\u6709\u8ba1\u8f83\u3002\u201d\u8981\u77e5\u8d75\u534a\u5c71\u5fe0\u539a\u8001\u5b9e\uff0c\u5bf9\u4ed8\u5978\u8bc8\u5c0f\u4eba\u5b9e\u975e\u5176\u957f\uff0c\u5904\u6b64\u56f0\u5883\uff0c\u4e0d\u81ea\u7981\u60f3\u8d77\u90a3\u8db3\u667a\u591a\u8c0b\u7684\u4e03\u5f1f\u6b66\u8bf8\u845b\u5f90\u5929\u5b8f\u6765\u3002 \u3000\u3000\u9648\u79b9\u53f3\u624b\u7684\u5315\u9996\u523a\u7834\u5415\u5c0f\u59b9\u540e\u5fc3\u8863\u670d\uff0c\u5203\u5c16\u62b5\u53ca\u76ae\u8089\uff0c\u8981\u4f7f\u8d75\u534a\u5c71\u65e0\u6cd5\u7528\u6697\u5668\u6253\u843d\u5315\u9996\uff0c\u53cc\u76ee\u77aa\u4f4f\u4e86\u8d75\u534a\u5c71\uff0c\u8bf4\u9053\uff1a\u201c\u8d75\u4e09\u7237\uff0c\u4f60\u6211\u5f80\u65e5\u65e0\u6028\uff0c\u8fd1\u65e5\u65e0\u4ec7\u3002\u4f60\u5c31\u662f\u53d1\u6697\u5668\u6253\u778e\u6211\u8fd9\u53cc\u62db\u5b50\uff0c\u59d3\u9648\u7684\u51b3\u4e0d\u8fd8\u624b\u3002\u201d\u8d75\u534a\u5c71\u624b\u4e2d\u6263\u4e86\u4e24\u679a\u94b1\u9556\uff0c\u672c\u62df\u5c04\u4ed6\u53cc\u76ee\uff0c\u53ea\u8981\u4ed6\u77ee\u8eab\u4e00\u8eb2\u6216\u662f\u4f38\u624b\u4e00\u62a4\uff0c\u5c31\u53ef\u4fdf\u673a\u6551\u4eba\uff0c\u5c82\u77e5\u6b64\u4eba\u89c1\u4e8b\u5f97\u5feb\uff0c\u5148\u884c\u51fa\u8a00\u70b9\u7834\u4e86\u81ea\u5df1\u7684\u7528\u610f\u3002 \u3000\u3000\u4e00\u65f6\u4e4b\u95f4\u5927\u5385\u4e0a\u767b\u6210\u50f5\u5c40\u3002 \u3000\u3000\u9648\u79b9\u76ee\u4e0d\u8f6c\u77ac\u5730\u77aa\u7740\u8d75\u534a\u5c71\uff0c\u9632\u4ed6\u6709\u751a\u5f02\u52a8\uff0c\u53e3\u4e2d\u5374\u5728\u5bf9\u738b\u6c0f\u5144\u5f1f\u8bf4\u8bdd\uff1a \u3000\u3000\u201c\u738b\u5927\u54e5\uff0c\u738b\u4e8c\u54e5\uff0c\u8d75\u4e09\u7237\u4eca\u513f\u8ddf\u5144\u5f1f\u8fc7\u4e0d\u53bb\uff0c\u4f60\u4e8c\u4f4d\u53ef\u77e5\u5176\u4e2d\u539f\u7531\uff1f\u201d\u738b\u6c0f\u5144\u5f1f\u4e0e\u4ed6\u540c\u5e9c\u5f53\u5dee\uff0c\u867d\u7136\u5e76\u4e0d\u600e\u4e48\u4ea4\u597d\uff0c\u4f46\u9648\u79b9\u751f\u6027\u5706\u6ed1\uff0c\u5e73\u65e5\u4eba\u7f18\u751a\u597d\uff0c\u82e5\u4e0d\u662f\u4e8c\u738b\u5fcc\u60ee\u8d75\u534a\u5c71\u6b66\u529f\u4e86\u5f97\uff0c\u65e9\u5df2\u51fa\u8a00\u529d\u89e3\u3002\u738b\u5251\u82f1\u63a5\u53e3\u9053\uff1a\u201c\u542c\u8d75\u4e09\u7237\u8bf4\uff0c\u4ed6\u4e5f\u662f\u53d7\u4eba\u4e4b\u6258\uff0c\u672a\u5fc5\u660e\u767d\u771f\u76f8\u3002\u53ea\u6015\u8fd9\u4e2d\u95f4\u6709\u4ec0\u4e48\u8bef\u4f1a\uff0c\u4e5f\u662f\u6709\u7684\u3002\u201d \u3000\u3000\u9648\u79b9\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8bef\u4f1a\u5012\u662f\u6ca1\u6709\u3002\u738b\u5927\u54e5\uff0c\u5144\u5f1f\u8fdb\u798f\u516c\u5b50\u5e9c\u4e4b\u524d\uff0c\u662f\u5728\u5b9a\u4eb2\u738b\u5e9c\u5f53\u5dee\uff0c\u8fd9\u4e2a\u4f60\u662f\u77e5\u9053\u7684\u4e86\uff1f\u201d\u738b\u5251\u82f1\u9053\uff1a\u201d\u662f\u554a\uff0c\u4f60\u662f\u5b9a\u738b\u7237\u63a8\u8350\u7ed9\u798f\u516c\u5b50\u7684\u3002\u738b\u7237\u5927\u5927\u5938\u4f60\u7cbe\u660e\u80fd\u5e72\u54ea\u3002\u201d\u9648\u79b9\u9053\uff1a\u201c\u9002\u624d\u8d75\u4e09\u7237\u8bf4\u9053\uff0c\u5144\u5f1f\u4f24\u4e86\u8fd9\u5c0f\u59d1\u5a18\u7684\u7236\u4eb2\uff0c\u8fd9\u4ef6\u4e8b\u662f\u6709\u7684\u3002\u53ef\u662f\u5144\u5f1f\u662f\u5949\u4e86\u738b\u7237\u4e4b\u547d\uff0c\u4f60\u6211\u540c\u662f\u5403\u5e9c\u95e8\u996d\u7684\u4eba\uff0c\u4e3b\u4eba\u5bb6\u6709\u5dee\u4f7f\u4ea4\u4e0b\u6765\uff0c\u4f60\u80fd\u8fdd\u547d\u4e48\uff1f\u201d\u738b\u5251\u82f1\u8fd9\u624d\u660e\u767d\uff0c\u4ed6\u501f\u7740\u4e0e\u81ea\u5df1\u4e00\u95ee\u4e00\u7b54\uff0c\u662f\u5728\u5411\u8d75\u534a\u5c71\u89e3\u8bf4\u8fd9\u56de\u4e8b\u7684\u6765\u9f99\u53bb\u8109\uff0c\u4e8e\u662f\u53c8\u63a5\u4e00\u53e5\uff1a\u201c\u8fd9\u53eb\u505a\u5949\u547d\u5dee\u9063\uff0c\u6168\u4e0d\u7531\u5df1\uff0c\u90a3\u4e5f\u602a\u4e0d\u5f97\u4f60\u9648\u5144\u5f1f\u3002\u201d \u3000\u3000\u8d75\u534a\u5c71\u5728\u56de\u7586\u63a5\u5230\u5b59\u521a\u5cf0\u7684\u8840\u4e66\uff0c\u7acb\u5373\u5e26\u540c\u5415\u5c0f\u59b9\u8d76\u5230\u5e7f\u5e73\u5e9c\uff0c\u4f46\u65e0\u6cd5\u627e\u7740\u5b59\u521a\u5cf0\uff0c\u5f53\u4e0b\u53c8\u5230\u5317\u4eac\u627e\u4eba\uff0c\u4e00\u67e5\u4e4b\u4e0b\uff0c\u5f97\u6089\u9648\u79b9\u5df2\u968f\u540c\u798f\u516c\u5b50\u5357\u4e0b\u3002 \u3000\u3000\u4ed6\u80ef\u4e0b\u6240\u9a91\uff0c\u662f\u9a86\u51b0\u90a3\u5339\u94f6\u971c\u9010\u7535\u9a79\uff0c\u4e0d\u8fc7\u4e24\u5929\u529f\u592b\uff0c\u5df2\u4ece\u5317\u4eac\u8ffd\u5230\u5546\u5bb6\u5821\u6765\u3002\u9648\u79b9\u5982\u4f55\u5bb3\u6b7b\u5415\u5e0c\u8d24\u7236\u6c0f\u4ed6\u786e\u662f\u4e0d\u751a\u4e86\u4e86\u3002\u5415\u5c0f\u59b9\u5e74\u5e7c\uff0c\u539f\u5df2\u8bf4\u4e0d\u660e\u767d\uff0c\u591a\u95ee\u5f97\u51e0\u53e5\uff0c\u5979\u5c31\u773c\u7736\u4e00\u7ea2\uff0c\u5c0f\u5634\u4e00\u6241\uff0c\u62bd\u62bd\u564e\u564e\u5730\u54ed\u4e2a\u4e0d\u505c\u3002\u8fd9\u65f6\u542c\u9648\u79b9\u8981\u8a00\u660e\u6b64\u4e8b\u6839\u7531\uff0c\u6b63\u4e2d\u4e0b\u6000\uff0c\u9053\uff1a\u201c\u597d\uff0c\u4f60\u66fe\u8bf4\u8fc7\uff0c\u5929\u4e0b\u4e4b\u4e8b\u62ac\u4e0d\u8fc7\u4e00\u4e2a\u7406\u5b57\u3002\u4f60\u5012\u8bf4\u8bf4\u76f2\u3002\u90a3\u5415\u5e0c\u8d24\u662f\u4f60\u5e08\u53d4\uff0c\u5c31\u7b97\u4ed6\u72af\u4e86\u5f25\u5929\u5927\u7f6a\uff0c\u4e5f\u4e0d\u80fd\u7531\u4f60\u4e0b\u624b\uff0c\u81f4\u4ed6\u4e8e\u6b7b\u5730\u3002\u201d \u3000\u3000\u9648\u79b9\u6b64\u65f6\u6709\u6043\u65e0\u6050\uff0c\u6599\u60f3\u4eca\u65e5\u5df2\u4e0d\u96be\u9003\u547d\uff0c\u4f46\u8d75\u534a\u5c71\u51b3\u4e0d\u80af\u5c31\u6b64\u7f62\u624b\uff0c\u65e5\u540e\u7ee7\u7eed\u8ffd\u5bfb\uff0c\u5374\u662f\u96be\u4ee5\u62b5\u6321\uff0c\u5fc3\u60f3\u603b\u987b\u8bf4\u5f97\u4ed6\u8896\u624b\u4e0d\u987e\uff0c\u65b9\u65e0\u540e\u60a3\uff0c\u4e8e\u662f\u8bf4\u9053\uff1a\u201c\u8d75\u4e09\u7237\uff0c\u4f60\u662f\u5149\u660e\u78ca\u843d\u7684\u82f1\u96c4\u597d\u6c49\uff0c\u5e38\u8a00\u9053\u541b\u4e8e\u53ef\u6b3a\u4ee5\u65b9\uff0c\u4f60\u8fd9\u4e00\u56de\u53ee\u662f\u4e0a\u4e86\u5b59\u521a\u5cf0\u7684\u4eba\u5f53\u5566\u3002\u201d\u8d75\u534a\u5c71\u4e00\u6115\uff0c\u9053\uff1a\u201d\u600e\u4e48\uff1f\u4e0a\u4e86\u4ec0\u4e48\u5f53\uff1f\u201d \u3000\u3000\u9648\u79b9\u9053\uff1a\u201c\u6211\u4eec\u5e7f\u5e73\u592a\u6781\u95e8\u59d3\u5b59\u7684\u7956\u5e08\u7237\u4f20\u4e86\u5f1f\u5b50\u4e09\u4eba\uff0c\u5b59\u5e08\u4f2f\u662f\u5927\u5f1f\u5b50\uff0c\u5148\u7236\u5c45\u6b21\uff0c\u5415\u5e08\u53d4\u7b2c\u4e09\u3002\u4ed6\u5e08\u5144\u5f1f\u4e09\u4eba\u5411\u6765\u4e0d\u7766\uff0c\u8d75\u4e09\u7237\u4f60\u662f\u660e\u767d\u7684\u4e86\uff1f\u201d \u3000\u3000\u8d75\u534a\u5c71\u672c\u6765\u4e1d\u6beb\u4e0d\u77e5\uff0c\u4f46\u60f3\u81ea\u5df1\u63d2\u624b\u7ba1\u4ed6\u95e8\u6237\u4e4b\u4e8b\uff0c\u82e5\u8bf4\u4e00\u5207\u4e0d\u77e5\uff0c\u672a\u514d\u4e8e\u7406\u6709\u4e8f\uff0c\u5f53\u4e0b\u4e0d\u7f6e\u53ef\u5426\uff0c\u9053\uff1a\u201c\u90a3\u4fbf\u600e\u6837\uff1f\u201d \u3000\u3000\u9648\u79b9\u9053\uff1a\u201c\u5415\u5e08\u53d4\u662f\u592a\u6781\u5317\u5b97\u4e00\u628a\u54cd\u5f53\u5f53\u7684\u597d\u624b\uff0c\u6211\u5bf9\u4ed6\u8001\u4eba\u5bb6\u7d20\u6765\u662f\u5341\u5206\u656c\u4ef0\u7684\u3002\u4ed6\u5728\u5b9a\u738b\u5e9c\u5f53\u6559\u5e08\u7237\uff0c\u592a\u6781\u62f3\u7684\u79d8\u5965\u5374\u534a\u70b9\u4e0d\u4f20\u7ed9\u738b\u7237\u3002\u5b9a\u738b\u7237\u751f\u6027\u597d\u6b66\uff0c\u89c1\u4ed6\u85cf\u5978\uff0c\u5fc3\u4e2d\u81ea\u662f\u4e0d\u5feb\uff0c\u8fde\u95ee\u4e86\u51e0\u6b21\uff0c\u5415\u5e08\u53d4\u5403\u903c\u4e0d\u8fc7\uff0c\u7adf\u7136\u8f9e\u53bb\u4e86\u5dee\u4f7f\u3002\u4e8e\u662f\u5b9a\u738b\u7237\u5c06\u5728\u4e0b\u627e\u53bb\uff0c\u8981\u6211\u89e3\u91ca\u592a\u6781\u62f3\u4e2d\u7684\u4ec0\u4e48\u4e71\u73af\u8bc0\u3001\u9634\u9633\u8bc0\u3002\u53ef\u662f\u5148\u7236\u6b66\u529f\u672c\u5c31\u5e73\u5e38\uff0c\u53c8\u901d\u4e16\u5f97\u65e9\uff0c\u6ca1\u4ec0\u4e48\u529f\u592b\u4f20\u4e0b\u6765\uff0c\u5728\u4e0b\u61c2\u5f97\u4ec0\u4e48\uff1f\u5b9a\u738b\u7237\u4fbf\u7740\u843d\u5728\u4e0b\uff0c\u53bb\u5411\u5415\u5e08\u53d4\u8bf7\u95ee\u660e\u767d\u3002\u201d \u3000\u3000\u8d75\u534a\u5c71\u5fc3\u60f3\uff1a\u201c\u592a\u6781\u95e8\u5357\u5317\u4e24\u5b97\u5404\u6709\u95e8\u89c4\uff0c\u672c\u95e8\u6b66\u529f\u79d8\u5965\u4e0d\u5f97\u4f20\u4e8e\u6ee1\u4eba\u3002 \u3000\u3000\u5415\u5e0c\u8d24\u4e0d\u6388\u79d8\u51b3\uff0c\u6b64\u4e8b\u5927\u81f4\u4e0d\u5047\u3002\u201d\u4e8e\u662f\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u9648\u79b9\u8138\u8272\u663e\u5f97\u5341\u5206\u8bda\u6073\uff0c\u8bf4\u9053\uff1a\u201c\u5728\u4e0b\u5949\u738b\u7237\u4e4b\u547d\uff0c\u4e0e\u4e09\u4f4d\u5f53\u5dee\u7684\u5144\u5f1f\u5230\u5415\u5e08\u53d4\u5e9c\u4e0a\u53bb\u3002\u90a3\u65f6\u4ed6\u8eab\u4e0a\u6709\u75c5\uff0c\u809d\u706b\u5927\u65fa\uff0c\u4e09\u8a00\u4e24\u8bed\u5c31\u5bf9\u6211\u75db\u4e0b\u8fa3\u624b\u3002 \u3000\u3000\u8d75\u4e09\u7237\u4f60\u60f3\uff0c\u4ee5\u6211\u8fd9\u70b9\u70b9\u7a00\u677e\u5e73\u5e38\u7684\u6b66\u529f\uff0c\u600e\u80fd\u5bb3\u5f97\u4e86\u5e7f\u5e73\u592a\u6781\u95e8\u7684\u7b2c\u4e00\u628a\u597d\u624b\uff1f\u201d\u8d75\u534a\u5c71\u9053\uff1a\u201c\u90a3\u4ed6\u662f\u600e\u4e48\u6b7b\u7684\uff1f\u201d\u9648\u79b9\u9053\uff1a\u201d\u5415\u5e08\u53d4\u672c\u5df2\u6709\u75c5\uff0c\u5728\u4e0b\u7684\u8a00\u8bed\u53c8\u91cd\u4e86\u4e00\u4e9b\u3002\u5415\u5e08\u53d4\u75f0\u6c14\u4e0a\u6d8c\uff0c\u5931\u8db3\u6454\u4e86\u4e00\u4ea4\uff0c\u5728\u4e0b\u8fde\u5fd9\u65bd\u6551\uff0c\u5df2\u81ea\u4e0d\u53ca\u3002\u201d \u3000\u3000\u8fd9\u756a\u8a00\u8bed\u4e4b\u4e2d\u7834\u7efd\u751a\u591a\uff0c\u8d75\u534a\u5c71\u6b63\u5f85\u9a73\u65a5\uff0c\u5415\u5c0f\u59b9\u5df2\u53eb\u4e86\u8d77\u6765\uff1a\u201c\u7239\u7239\u662f\u4ed6\u6253\u6b7b\u7684\uff0c\u7239\u7239\u662f\u4ed6\u2026\u2026\u201d\u7b2c\u4e8c\u53e5\u8bdd\u6ca1\u8bf4\u5b8c\uff0c\u9648\u79b9\u627c\u7740\u5979\u8116\u5b50\u7684\u624b\u4e00\u7d27\uff0c\u5c06\u5979\u540e\u534a\u53e5\u8bdd\u5236\u4f4f\u4e86\u3002\u8d75\u534a\u5c71\u5927\u6012\uff0c\u559d\u9053\uff1a\u201c\u4f60\u65e2\u8bf4\u4ed6\u6709\u75c5\uff0c\u600e\u5730\u53c8\u6597\u4e0d\u8fc7\u4ed6\uff1f\u518d\u8bf4\uff0c\u4ed6\u5c0f\u513f\u5b50\u4e0e\u4f60\u65e0\u6028\u65e0\u4ec7\uff0c\u53c8\u4f55\u4ee5\u4f24\u5bb3\u65e0\u8f9c\uff1f\u5feb\u653e\u624b\uff01\u201d \u3000\u3000\u9648\u79b9\u9053\uff1a\u201c\u8d75\u4e09\u7237\uff0c\u4f60\u8eab\u5728\u4e07\u91cc\u4e4b\u5916\uff0c\u600e\u77e5\u6211\u95e8\u6237\u4e2d\u4e4b\u4e8b\uff1f\u6211\u529d\u4f60\u8fd8\u662f\u5404\u4eba\u81ea\u626b\u95e8\u524d\u96ea\u7684\u597d\u3002\u201d\u4ed6\u4e00\u9762\u8bf4\uff0c\u4e00\u9762\u79fb\u52a8\u8eab\u5b50\uff0c\u6162\u6162\u9000\u5411\u5385\u53e3\u3002\u8d75\u534a\u5c71\u53cc\u76ee\u5982\u8981\u55b7\u706b\uff0c\u53ea\u662f\u773c\u89c1\u6b64\u4eba\u5fc3\u72e0\u624b\u8fa3\uff0c\u82e5\u771f\u4e0a\u524d\u62e6\u963b\uff0c\u4ed6\u5b9a\u8981\u4f24\u5bb3\u5415\u5c0f\u59b9\u6027\u547d\u3002\u8fd9\u5973\u5b69\u5e74\u7eaa\u867d\u5c0f\uff0c\u6027\u683c\u5374\u6781\u662f\u575a\u6bc5\uff0c\u5b64\u8eab\u4e00\u4eba\uff0c\u7adf\u7136\u95f4\u5173\u4e07\u91cc\u3001\u5386\u5c3d\u82e6\u8f9b\u5730\u5bfb\u5230\u56de\u7586\u3002\u4ee5\u8fd9\u4e00\u6761\u8def\u4e0a\u65c5\u9014\u4e4b\u8270\u96be\uff0c\u522b\u8bf4\u662f\u8fd9\u6837\u4e00\u4e2a\u5c0f\u5c0f\u5b64\u5973\uff0c\u5c31\u662f\u4e2a\u58ee\u5e74\u6c49\u5b50\uff0c\u4e5f\u662f\u5341\u5206\u4e0d\u6613\u3002\u8d75\u534a\u5c71\u6bc5\u7136\u63d2\u624b\u7ba1\u8fd9\u4ef6\u4e8b\uff0c\u56fa\u7136\u662f\u4e3a\u4e86\u5b59\u521a\u5cf0\u65a9\u624b\u76f8\u6258\uff0c\u53ef\u6709\u4e00\u5c0f\u534a\u4e5f\u77a7\u5728\u8fd9\u5b64\u5973\u7684\u5b5d\u5fc3\u4efd\u4e0a\u3002\u540e\u6765\u4e0e\u5979\u5171\u9a91\u4e1c\u6765\uff0c\u65f6\u65e5\u4e00\u4e45\uff0c\u5df2\u89c6\u5979\u72b9\u5982\u5973\u513f\u4e00\u822c\u3002 \u3000\u3000\u53ea\u89c1\u9648\u79b9\u518d\u9000\u51e0\u6b65\uff0c\u4fbf\u8981\u51fa\u5385\uff0c\u8d75\u534a\u5c71\u7a7a\u6709\u4e00\u8eab\u6697\u5668\uff0c\u7adf\u7136\u4e0d\u6562\u5411\u4ed6\u53d1\u5c04\u4e00\u679a\uff0c\u5fc3\u4e0b\u76d8\u7b97\uff1a\u201c\u82e5\u7528\u4e00\u679a\u6700\u91cd\u7684\u86c7\u5934\u9525\u6253\u4ed6\u8111\u95e8\uff0c\u81ea\u80fd\u53eb\u4ed6\u7acb\u65f6\u4e27\u547d\uff0c\u4f46\u4ed6\u4e34\u6b7b\u4e4b\u524d\u53ea\u8981\u624b\u81c2\u4e00\u9001\uff0c\u5415\u5c0f\u59b9\u5c31\u662f\u6027\u547d\u4e0d\u4fdd\u4e86\u3002\u201d \u3000\u3000\u53ea\u89c1\u4ed6\u53c8\u9000\u4e86\u4e00\u6b65\uff0c\u6b64\u65f6\u684c\u4e0a\u4e00\u679a\u5927\u7ea2\u70db\u6240\u7ed3\u7684\u4e00\u4e2a\u706f\u82b1\uff0c\u7a81\u7136\u535c\u7684\u4e00\u58f0\u7206\u4e86\u5f00\u6765\uff0c\u70db\u5149\u4e00\u6697\uff0c\u5f85\u5f97\u70db\u706b\u518d\u660e\uff0c\u9648\u79b9\u8eab\u540e\u5ffd\u5df1\u591a\u4e86\u4e00\u4e2a\u8001\u8005\u3002 \u3000\u3000\u53ea\u89c1\u90a3\u8001\u8005\u4e24\u624b\u5e73\u4e3e\u80f8\u524d\uff0c\u4f46\u5149\u79c3\u79c3\u53ea\u6709\u4e24\u6839\u8155\u9aa8\uff0c\u624b\u638c\u5df2\u9f50\u8155\u65a9\u53bb\uff0c\u8eab\u7a7f\u9752\u5e03\u957f\u888d\uff0c\u5f62\u5bb9\u67af\u69c1\uff0c\u53cc\u76ee\u6df1\u9677\uff0c\u98a7\u9aa8\u9ad8\u8038\uff0c\u8138\u4e0a\u7070\u6251\u6251\u7684\u751a\u662f\u6015\u4eba\u3002 \u3000\u3000\u9648\u79b9\u89c1\u4f17\u4eba\u4e00\u9f50\u671b\u7740\u81ea\u5df1\u8eab\u540e\uff0c\u795e\u60c5\u751a\u662f\u5f02\u6837\uff0c\u4e0d\u7531\u5f97\u56de\u8fc7\u5934\u53bb\u3002\u7a81\u89c1\u90a3\u4eba\u7684\u4e24\u6839\u8155\u9aa8\u5df2\u4f38\u5230\u81ea\u5df1\u8138\u524d\uff0c\u9669\u4e9b\u78b0\u5230\uff0c\u4e00\u60ca\u4e4b\u4e0b\uff0c\u5fd9\u8ba9\u5f00\u4e86\u4e00\u6b65\uff0c\u53eb\u9053\uff1a\u201c\u5b59\u5e08\u4f2f\uff0c\u662f\u4f60\uff01\u201d \u3000\u3000\u90a3\u4eba\u7adf\u4e0d\u7406\u4f1a\uff0c\u62c9\u8d77\u957f\u888d\uff0c\u62a2\u4e0a\u4e00\u6b65\uff0c\u5411\u8d75\u534a\u5c71\u62dc\u4e86\u4e0b\u53bb\uff0c\u8bf4\u9053\uff1a\u201c\u8d75\u4e09\u7237\uff0c\u4f60\u7684\u6069\u60c5\uff0c\u5b59\u521a\u5cf0\u53ea\u597d\u6765\u751f\u8865\u62a5\u4e86\u3002\u201d\u8d75\u534a\u5c71\u6025\u5fd9\u7b54\u793c\uff0c\u53cc\u773c\u5374\u4e0d\u79bb\u9648\u79b9\u3002\u9648\u79b9\u6025\u9000\u4e24\u6b65\uff0c\u6b63\u8981\u62e5\u7740\u5415\u5c0f\u59b9\u62a2\u51fa\u5385\u95e8\uff0c\u5b59\u521a\u5cf0\u8eab\u5f62\u4e00\u6643\uff0c\u62a2\u5148\u5835\u4f4f\u4e86\u95e8\uff0c\u559d\u9053\uff1a\u201c\u56de\u53bb\uff01\u201d\u9648\u79b9\u9053\uff1a\u201c\u4f60\u8ba9\u4e0d\u8ba9\u8def\uff1f\u201d\u5b59\u521a\u5cf0\u9053\uff1a\u201c\u4f60\u5df2\u5bb3\u8fc7\u5415\u5bb6\u4e8c\u547d\uff0c\u59d3\u5b59\u7684\u65e9\u5c31\u6ca1\u60f3\u6d3b\u7740\u3002\u201d\u8f6c\u5411\u8d75\u534a\u5c71\u9053\uff1a\u201c\u8d75\u4e09\u7237\uff0c\u8fd9\u4f4d\u9648\u7237\u7684\u8bdd\uff0c\u5728\u4e0b\u5728\u95e8\u5916\u5df2\u542c\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u5f53\u771f\u662f\u4e00\u6d3e\u80e1\u8a00\u3002\u6211\u5415\u5e08\u5f1f\u662f\u4e3a\u4e86\u4e71\u73af\u8bc0\u4e0e\u9634\u9633\u8bc0\u800c\u6b7b\u5728\u8fd9\u5978\u8d3c\u624b\u4e0b\u7684\u3002\u201d \u3000\u3000\u8d75\u534a\u5c71\u5411\u9648\u79b9\u4fa7\u76ee\u659c\u8131\uff0c\u54fc\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u539f\u6765\u9648\u7237\u7cbe\u7814\u6211\u4eec\u7684\u8fd9\u4e24\u5927\u79d8\u8bc0\uff0c\u5144\u5f1f\u5012\u8981\u9886\u6559\u3002\u201d\u5b59\u521a\u5cf0\u9053\uff1a\u201c\u8fd9\u5012\u4e0d\u662f\u3002\u8fd9\u4f4d\u9648\u7237\u77e5\u9053\u6211\u592a\u6781\u62f3\u6709\u4e5d\u5927\u79d8\u8bc0\uff0c\u800c\u4e71\u73af\u8bc0\u4e0e\u9634\u9633\u8bc0\u53c8\u662f\u62f3\u6cd5\u5173\u952e\uff0c\u53ea\u53ef\u60dc\u4ed6\u7236\u4eb2\u8fc7\u4e16\u5f97\u65e9\uff0c\u6ca1\u6765\u5f97\u53ca\u4f20\u4ed6\u3002\u4ed6\u5343\u65b9\u767e\u8ba1\u8981\u6211\u548c\u5415\u5e08\u5f1f\u5410\u9732\uff0c\u6211\u5e08\u5144\u5f1f\u77e5\u4ed6\u5fc3\u672f\u4e0d\u6b63\uff0c\u5c31\u6ca1\u80af\u8bf4\u3002\u4e8e\u662f\u4ed6\u7528\u5b9a\u738b\u7237\u7684\u52bf\u3002\u529b\u76f8\u538b\uff0c\u5415\u5e08\u5f1f\u4ecd\u662f\u4e0d\u8bf4\u3002\u5230\u540e\u6765\u4ed6\u4e58\u7740\u5415\u5e08\u5f1f\u6709\u75c5\uff0c\u591c\u4e2d\u95ef\u5230\u5415\u5e08\u5f1f\u7684\u75c5\u69bb\u4e4b\u524d\uff0c\u6293\u4f4f\u4ed6\u4e00\u8109\u5355\u4f20\u7684\u4e00\u4e2a\u5a03\u513f\uff0c\u8bf4\u9053\u82e5\u4e0d\u5410\u9732\u4e71\u73af\u3001\u9634\u9633\u4e8c\u8bc0\uff0c\u5c31\u5c06\u5b69\u5b50\u4e00\u5200\u6740\u4e86\u2026\u2026\u59d3\u9648\u7684\uff0c\u6211\u8fd9\u8bdd\u662f\u771f\u54ea\uff0c\u8fd8\u662f\u5047\u54ea\uff1f\u201d \u3000\u3000\u9648\u79b9\u94c1\u9752\u7740\u8138\uff0c\u4e00\u8a00\u4e0d\u53d1\uff0c\u5fc3\u4e2d\u53c8\u60ca\u53c8\u6012\uff0c\u773c\u89c1\u5df2\u53ef\u8131\u8eab\uff0c\u8fd9\u59d3\u5b59\u7684\u8001\u5bb6\u4f19\u504f\u504f\u5728\u8fd9\u65f6\u5019\u95ef\u4e86\u8fdb\u6765\u3002\u53ea\u542c\u5b59\u521a\u5cf0\u57c2\u54bd\u7740\u53c8\u9053\uff1a\u201c\u4e8e\u662f\u4e00\u4e2a\u806a\u660e\u4f36\u4fd0\u7684\u5a03\u513f\uff0c\u4fbf\u4e27\u751f\u5728\u4ed6\u5229\u5251\u4e4b\u4e0b\u3002\u5415\u5e08\u5f1f\u62b1\u75c5\u4e0e\u4ed6\u62fc\u547d\uff0c\u53c8\u7ed9\u4ed6\u4f7f\u4e91\u624b\u529f\u592b\uff0c\u62d6\u5f97\u7cbe\u75b2\u529b\u5c3d\uff0c\u865a\u8131\u800c\u6b7b\u3002\u8d75\u4e09\u7237\uff0c\u5b59\u521a\u5cf0\u6127\u4e3a\u638c\u95e8\uff0c\u5e74\u8001\u65e0\u80fd\uff0c\u6211\u5317\u5b97\u53c8\u662f\u4eba\u624d\u51cb\u96f6\uff0c\u773c\u4e0b\u53ea\u6709\u8fd9\u59d3\u9648\u7684\u6b66\u529f\u6700\u5f3a\uff0c\u53ea\u6709\u8001\u7740\u8138\u76ae\uff0c\u8bf7\u5357\u5b97\u4e3b\u6301\u516c\u9053\u3002\u201d \u3000\u3000\u4ed6\u8f6c\u5411\u9648\u79b9\u9053\uff1a\u201c\u9648\u5927\u7237\uff0c\u6211\u7684\u8bdd\u6ca1\u534a\u53e5\u51a4\u4f60\u5427\uff1f\u201d \u3000\u3000\u8d75\u534a\u5c71\u76f4\u542c\u5f97\u4e49\u6124\u586b\u81ba\uff0c\u5927\u6b65\u8e0f\u4e86\u4e0a\u53bb\uff0c\u8bf4\u9053\uff1a\u201c\u8981\u5b66\u62f3\u672f\u7684\u79d8\u5965\uff0c\u81ea\u53e4\u4ee5\u6765\u53ea\u6709\u6c42\u5e08\u8bbf\u53cb\uff0c\u4ece\u6765\u6ca1\u542c\u8bf4\u8fc7\u5982\u4f60\u8fd9\u7b49\u79bd\u517d\u4e4b\u884c\u3002\u201d\u9648\u79b9\u559d\u9053\uff1a\u201c\u4f60\u522b\u52a8\uff0c\u7ed9\u6211\u7ad9\u7740\u3002\u201d\u8bf4\u7740\u624b\u81c2\u4e00\u7d27\uff0c\u5415\u5c0f\u59b9\u5440\u7684\u4e00\u58f0\u53eb\u4e86\u51fa\u6765\u3002\u8d75\u534a\u5c71\u679c\u7136\u7ad9\u5b9a\u811a\u6b65\uff0c\u4e0d\u6562\u518d\u52a8\u3002\u9648\u79b9\u6717\u58f0\u9053\uff1a\u201c\u59d3\u8d75\u7684\uff0c\u4f60\u8981\u627e\u6211\uff0c\u5c3d\u7ba1\u5230\u5317\u4eac\u798f\u516c\u5b50\u5e9c\u6765\u3002\u4eca\u65e5\u8bf7\u4f60\u53eb\u4ed6\u8ba9\u8ba9\u9053\u3002\u201d\u8d75\u534a\u5c71\u65e0\u5948\uff0c\u53ea\u5f97\u5411\u5b59\u521a\u5cf0\u9053\uff1a\u201c\u5b59\u5e08\u5144\uff0c\u4eca\u65e5\u54b1\u4eec\u5c31\u6682\u4e14\u9976\u4ed6\uff01\u201d \u3000\u3000\u5b59\u521a\u5cf0\u5927\u6025\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8bf4\u4eca\u513f\u2026\u2026\u4eca\u513f\u9976\u2026\u2026\u9976\u4e86\u4ed6\uff1f\u201d\u8d75\u534a\u5c71\u9053\uff1a\u201c\u5b59\u7237\uff0c\u4f60\u653e\u5fc3\uff0c\u8d75\u67d0\u65e2\u7136\u62c9\u626f\u4e0a\u4e86\u8fd9\u56de\u5b50\u4e8b\uff0c\u5b9a\u662f\u6709\u59cb\u6709\u7ec8\u3002\u201d\u5b59\u521a\u5cf0\u6025\u5f97\u8bf4\u4e0d\u51fa\u8bdd\u6765\uff0c\u53ea\u8bf4\uff1a\u201c\u4f60\u2026\u2026\u4f60\u2026\u2026\u201d\u8d75\u534a\u5c71\uff1a\u201c\u8ba9\u8def\u7ed9\u4ed6\u5427\u3002\u59d3\u8d75\u7684\u82e5\u662f\u6599\u7406\u4e0d\u4e86\u8fd9\u56de\u4e8b\uff0c\u6211\u65a9\u8fd9\u4e00\u53cc\u624b\u8fd8\u4f60\uff01\u201d\u8fd9\u51e0\u53e5\u8bdd\u8bf4\u5f97\u65a9\u9489\u622a\u94c1\uff0c\u5b59\u521a\u5cf0\u518d\u65e0\u8bdd\u8bf4\uff0c\u8eab\u5b50\u5f80\u65c1\u8fb9\u4e00\u8ba9\uff0c\u773c\u7741\u7741\u5730\u76ef\u7740\u9648\u79b9\uff0c\u76ee\u5149\u4e2d\u5145\u6ee1\u4e86\u6028\u6bd2\u3002 \u3000\u3000\u9648\u79b9\u5fc3\u9053\uff1a\u201c\u4eca\u65e5\u6211\u8131\u5374\u6b64\u96be\uff0c\u7acb\u65f6\u9ad8\u98de\u8fdc\u8d70\uff0c\u5929\u4e0b\u4e4b\u5927\uff0c\u4f55\u5904\u4e0d\u662f\u5bb9\u8eab\u4e4b\u6240\uff1f\u53ea\u8981\u6211\u9690\u59d3\u57cb\u540d\uff0c\u4f60\u627e\u4e00\u767e\u5e74\u4e5f\u627e\u4e0d\u7740\u8001\u5b50\u3002\u201d\u8138\u4e0a\u4e0d\u81ea\u7981\u9732\u51fa\u4e00\u4e1d\u5f97\u610f\u7684\u795e\u8272\uff0c\u8bf4\u9053\uff1a\u201c\u8d75\u4e09\u7237\uff0c\u4f60\u6211\u540e\u4f1a\u6709\u671f\u3002\u5b59\u5e08\u4f2f\u8bf4\u5f97\u4e0d\u9519\uff0c\u6211\u786e\u60f3\u5b66\u4e00\u5b66\u592a\u6781\u95e8\u4e2d\u4e71\u73af\u8bc0\u4e0e\u9634\u9633\u8bc0\u7684\u7a8d\u95e8\u3002\u4f60\u4e0a\u4eac\u6765\uff0c\u505a\u5144\u5f1f\u7684\u8981\u597d\u597d\u8bf7\u4f60\u6307\u70b9\u6307\u70b9\u3002\u201d\u8d75\u534a\u5c71\u53c8\u662f\u54fc\u4e86\u4e00\u58f0\uff0c\u54ea\u53bb\u7406\u4ed6\u3002 \u3000\u3000\u9648\u79b9\u4e0d\u6562\u8f6c\u8eab\uff0c\u631f\u7740\u5415\u5c0f\u59b9\u59b9\u4e00\u6b65\u6b65\u5730\u5012\u9000\uff0c\u7ecf\u8fc7\u5b59\u521a\u5cf0\u8eab\u4fa7\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u5de6\u8db3\u8de8\u51fa\u4e86\u95e8\u69db\u3002 \u3000\u3000\u80e1\u6590\u81ea\u4e0e\u738b\u5251\u82f1\u6bd4\u638c\u4e4b\u540e\uff0c\u4e00\u76f4\u5728\u65c1\u51dd\u795e\u6ce8\u89c6\u8d75\u534a\u5c71\u3001\u9648\u79b9\u3001\u5b59\u521a\u5cf0\u4e09\u4eba\uff0c\u6b64\u65f6\u773c\u89c1\u9648\u79b9\u72e1\u8ba1\u5f97\u901e\uff0c\u5fc3\u9053\uff1a\u201c\u8d75\u4e09\u7237\u5e2e\u4e86\u6211\u8fd9\u4e2a\u5927\u5fd9\uff0c\u773c\u4e0b\u4ed6\u9047\u4e0a\u96be\u4e8b\uff0c\u6211\u5982\u4f55\u4e0d\u52a0\u7406\u4f1a\uff1f\u201d\u4ed6\u5934\u8111\u7075\u654f\uff0c\u4eba\u53c8\u987d\u76ae\uff0c\u5fc3\u5ff5\u4e00\u52a8\uff0c\u65e9\u6709\u8ba1\u8f83\uff0c\u8fd0\u6c14\u5c06\u4e00\u6ce1\u5c3f\u903c\u5230\u5c3f\u9053\u53e3\uff0c\u89e3\u5f00\u4e86\u88e4\u5b50\uff0c\u89c1\u9648\u79b9\u5373\u5c06\u8e0f\u51fa\u5385\u95e8\uff0c\u7a81\u7136\u7aef\u8d77\u4e00\u5f20\u6905\u5b50\uff0c\u8bf4\u9053\uff1a\u201c\u9648\u79b9\uff0c\u6211\u6709\u4e00\u4e8b\u8bf7\u6559\u3002\u201d\u9648\u79b9\u4e00\u5446\uff0c\u5374\u6ca1\u5c06\u8fd9\u5b69\u5b50\u653e\u5728\u773c\u5185\uff0c\u5e76\u4e0d\u7406\u776c\u3002\u80e1\u6590\u5c06\u6905\u5b50\u5728\u4ed6\u8eab\u524d\u4e00\u653e\uff0c\u8df3\u4e0a\u6905\u5b50\uff0c\u7a81\u7136\u4e00\u6ce1\u6025\u5c3f\uff0c\u5f80\u4ed6\u773c\u4e2d\u75be\u5c04\u8fc7\u53bb\u3002 \u3000\u3000\u9648\u79b9\u6025\u6012\u4e4b\u4e0b\uff0c\u4f38\u5de6\u624b\u5728\u773c\u524d\u4e00\u6321\uff0c\u963b\u4f4f\u4ed6\u5c04\u8fc7\u6765\u7684\u5c3f\u6c34\uff0c\u53f3\u624b\u4e00\u5315\u9996\u5c31\u5f80\u80e1\u6590\u80f8\u53e3\u5241\u53bb\u3002\u80e1\u6590\u89e3\u88e4\u4e4b\u524d\uff0c\u65e9\u5c31\u7b79\u5212\u597d\u4e86\u4e0b\u4e00\u6b65\uff0c\u773c\u89c1\u5315\u9996\u523a\u5230\uff0c\u53cc\u624b\u63e1\u8d77\u6905\u5b50\uff0c\u8eab\u5b50\u4e00\u8dc3\uff0c\u4eba\u5728\u534a\u7a7a\uff0c\u6905\u5b50\u5df2\u5411\u4ed6\u5934\u9876\u731b\u7838\u4e0b\u53bb\u3002\u9648\u79b9\u4f38\u624b\u683c\u5f00\uff0c\u6012\u9a82\uff1a\u201c\u5c0f\u8d3c\uff01\u201d\u80e1\u6590\u4eba\u672a\u843d\u5730\uff0c\u5df2\u5411\u524d\u4e00\u6251\uff0c\u62b1\u4f4f\u5415\u5c0f\u59b9\u4e00\u4e2a\u6253\u6eda\uff0c\u6eda\u5f00\u534a\u4e08\u3002 \u3000\u3000\u9648\u79b9\u5927\u60ca\uff0c\u7eb5\u4e0a\u62a2\u593a\uff0c\u80e1\u6590\u94a9\u811a\u53cd\u8e22\uff0c\u968f\u5373\u7ad9\u8d77\u8eab\u6765\uff0c\u65bd\u5c55\u7a7a\u624b\u5165\u767d\u5203\u529f\u592b\uff0c\u62a2\u4ed6\u624b\u4e2d\u5315\u9996\u3002\u9648\u79b9\u5fc3\u77e5\u4e0d\u5999\uff0c\u4e0d\u6562\u604b\u6218\uff0c\u731b\u6233\u4e00\u5200\uff0c\u7acb\u5373\u8f6c\u8eab\u51fa\u5385\uff0c\u5374\u89c1\u8d75\u534a\u5c71\u53cc\u624b\u53c9\u8170\uff0c\u795e\u5a01\u51db\u51db\u5730\u7ad9\u5728\u5385\u53e3\u3002 \u3000\u3000\u80e1\u6590\u54c8\u54c8\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e00\u6ce1\u5c3f\u8fd8\u6ca1\u6492\u5b8c\u5462\uff01\u201d\u8fd9\u4e00\u4e0b\u53d8\u5316\uff0c\u8d75\u534a\u5c71\u56fa\u662f\u4e07\u4e07\u731c\u60f3\u4e0d\u5230\uff0c\u5385\u4e0a\u4f17\u4eba\u4e5f\u65e0\u4e00\u4e0d\u662f\u5927\u51fa\u610f\u6599\u4e4b\u5916\u3002\u5f85\u5f97\u5404\u4eba\u660e\u767d\u4ed6\u7684\u7528\u610f\uff0c\u5415\u5c0f\u59b9\u65e9\u5df2\u83b7\u6551\uff0c\u9648\u79b9\u4ea6\u5df2\u56f0\u5165\u91cd\u56f4\u3002\u8fd9\u4e00\u6765\u5546\u8001\u592a\u66f4\u589e\u6068\u610f\uff0c\u738b\u6c0f\u5144\u5f1f\u5992\u5ff5\u8f6c\u6df1\uff0c\u9a6c\u884c\u7a7a\u6697\u53eb\u60ed\u6127\uff0c\u6bb7\u4ef2\u7fd4\u54fa\u54fa\u6012\u9a82\uff0c\u4f46\u4e0d\u8bba\u662f\u6068\u662f\u5992\uff0c\u662f\u5080\u662f\u9a82\uff0c\u5404\u4eba\u5fc3\u4e2d\uff0c\u5747\u5e26\u7740\u4e09\u5206\u60ca\u4f69\u8d5e\u53f9\uff1a\u201c\u82e5\u975e\u8fd9\u5c0f\u5b50\u51fa\u6b64\u602a\u62db\uff0c\u600e\u80fd\u5c06\u9648\u79b9\u622a\u5f97\u4e0b\u6765\uff1f\u201d \u3000\u3000\u8d75\u534a\u5c71\u5fc3\u4e2d\u5bf9\u80e1\u6590\u5927\u662f\u611f\u6fc0\uff0c\u8138\u4e0a\u5374\u4e0d\u52a8\u58f0\u8272\uff0c\u5bf9\u9648\u79b9\u6de1\u6de1\u9053\uff1a\u201c\u9648\u7237\uff0c\u4f60\u4e3a\u4e86\u5b66\u4e71\u73af\u8bc0\u548c\u9634\u9633\u8bc0\uff0c\u4f24\u4e86\u4e24\u6761\u4eba\u547d\uff0c\u5176\u5b9e\u5927\u53ef\u4e0d\u5fc5\u8fd9\u4e48\u8d39\u4e8b\u3002\u8fd9\u4e24\u7bc7\u6b4c\u8bc0\uff0c\u5728\u592a\u6781\u95e8\u4e2d\u4e5f\u7b97\u4e0d\u5f97\u662f\u4ec0\u4e48\u4e86\u4e0d\u8d77\u7684\u4e0d\u4f20\u4e4b\u79d8\uff0c\u8d75\u67d0\u4e0d\u624d\uff0c\u5012\u8fd8\u8bb0\u5f97\u3002 \u3000\u3000\u4f60\u8bf4\u8fc7\u8981\u5411\u8d75\u67d0\u8ba8\u6559\uff0c\u4eca\u65e5\u5c31\u4f20\u4e86\u4e8e\u4f60\uff0c\u4e5f\u81ea\u4e0d\u59a8\u3002\u201d\u4f17\u4eba\u4e00\u5446\uff0c\u5747\u60f3\uff1a\u201c\u4ed6\u5df2\u96be\u9003\u4f60\u7684\u638c\u63e1\uff0c\u5374\u6765\u8bf4\u53cd\u8bdd\u3002\u201d \u3000\u3000\u5374\u542c\u8d75\u534a\u5c71\u53c8\u9053\uff1a\u201c\u6211\u5148\u8bf4\u4e71\u73af\u8bc0\u4e0e\u4f60\uff0c\u597d\u597d\u8bb0\u4e0b\u4e86\u3002\u201d\u4e8e\u662f\u6717\u58f0\u5ff5\u9053\uff1a\u201c\u4e71\u73af\u672f\u6cd5\u6700\u96be\u901a\uff0c\u4e0a\u4e0b\u968f\u5408\u5999\u65e0\u7a77\u3002\u9677\u654c\u6df1\u5165\u4e71\u73af\u5185\uff0c\u56db\u4e24\u80fd\u62e8\u5343\u65a4\u52a8\u3002 \u3000\u3000\u624b\u811a\u9f50\u8fdb\u7ad6\u627e\u6a2a\uff0c\u638c\u4e2d\u4e71\u73af\u843d\u4e0d\u7a7a\u3002\u6b32\u77e5\u73af\u4e2d\u6cd5\u4f55\u5728\uff0c\u53d1\u843d\u70b9\u5bf9\u5373\u6210\u529f\u3002\u201d \u3000\u3000\u8fd9\u516b\u53e5\u4e00\u5ff5\uff0c\u5b59\u521a\u5cf0\u548c\u9648\u79b9\u9762\u9762\u76f8\u89d1\uff0c\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002\u539f\u6765\u8fd9\u516b\u53e5\u8bd7\u4e0d\u50cf\u8bd7\u3001\u6b4c\u4e0d\u50cf\u6b4c\u7684\u8bdd\uff0c\u6b63\u662f\u592a\u6781\u95e8\u4e2d\u7684\u201c\u4e71\u73af\u8bc0\u201d\u3002\u9648\u79b9\u5e7c\u65f6\u4e5f\u4f9d\u7a00\u542c\u7236\u4eb2\u8bf4\u8d77\u8fc7\uff0c\u53ea\u662f\u5168\u7136\u4e0d\u61c2\u5176\u4e2d\u5965\u5999\uff0c\u4e07\u60f3\u4e0d\u5230\u8d75\u534a\u5c71\u771f\u80fd\u539f\u539f\u672c\u672c\u5730\u5ff5\u7ed9\u81ea\u5df1\u542c\u3002 \u3000\u3000\u4ed6\u628a\u5fc3\u4e00\u6a2a\uff0c\u751f\u6b7b\u7f6e\u4e4b\u5ea6\u5916\uff0c\u9053\uff1a\u201c\u5176\u4e2d\u542b\u4e49\uff0c\u8fd8\u8bf7\u8d75\u4e09\u7237\u6307\u70b9\u3002\u201d \u3000\u3000\u8d75\u534a\u5c71\u9053\uff1a\u201c\u672c\u95e8\u592a\u6781\u529f\u592b\uff0c\u51fa\u624b\u62db\u62db\u6210\u73af\u3002\u6240\u8c13\u4e71\u73af\uff0c\u4fbf\u662f\u8bf4\u62f3\u62db\u867d\u6709\u5b9a\u578b\uff0c\u53d8\u5316\u5374\u5b58\u4e4e\u5176\u4eba\u3002\u624b\u6cd5\u867d\u5747\u6210\u73af\uff0c\u5374\u6709\u9ad8\u4f4e\u3001\u8ff8\u9000\u3001\u51fa\u5165\u3001\u653b\u5b88\u4e4b\u522b\u3002\u5708\u6709\u5927\u5708\u3001\u5c0f\u5708\u3001\u5e73\u5708\u3001\u7acb\u5708\u3001\u659c\u5708\u3001\u6b63\u5708\u3001\u6709\u5f62\u5708\u53ca\u65e0\u5f62\u5708\u4e4b\u5206\u3002\u4e34\u654c\u4e4b\u9645\uff0c\u987b\u5f97\u4ee5\u5927\u514b\u5c0f\u3001\u4ee5\u659c\u514b\u6b63\u3001\u4ee5\u65e0\u5f62\u514b\u6709\u5f62\uff0c\u6bcf\u4e00\u62db\u53d1\u51fa\uff0c\u5747\u987b\u6697\u84c4\u73af\u52b2\u3002\u201d\u4ed6\u4e00\u9762\u8bf4\uff0c\u4e00\u9762\u6bd4\u5212\u5404\u9879\u5708\u73af\u7684\u5f62\u72b6\uff0c\u53c8\u9053\uff1a\u201c\u6211\u4ee5\u73af\u5f62\u4e4b\u529b\uff0c\u63a8\u5f97\u654c\u4eba\u8fdb\u6211\u65e0\u5f62\u5708\u5185\uff0c\u90a3\u65f6\u6b32\u5176\u5de6\u5219\u5de6\uff0c\u6b32\u5176\u53f3\u5219\u53f3\u3002\u7136\u540e\u4ee5\u56db\u4e24\u5fae\u529b\uff0c\u62e8\u52a8\u654c\u65b9\u5343\u65a4\u3002\u52a1\u987b\u4ee5\u6211\u7ad6\u529b\uff0c\u51fb\u654c\u6a2a\u4fa7\u3002\u592a\u6781\u62f3\u80dc\u8d1f\u4e4b\u6570\uff0c\u5728\u4e8e\u627e\u5bf9\u53d1\u70b9\uff0c\u51fb\u51c6\u843d\u70b9\u3002\u201d \u3000\u3000\u4ed6\u6240\u8bf4\u7684\u62f3\u7406\u660e\u767d\u6d45\u663e\uff0c\u4eba\u4eba\u80fd\u89e3\uff0c\u4f46\u5176\u4e2d\u5b9e\u662f\u542b\u6709\u81f3\u7406\u3002 \u3000\u3000\u5385\u4e0a\u4f17\u4eba\u5747\u662f\u6b66\u5b66\u597d\u624b\uff0c\u542c\u4ed6\u53e3\u4e2d\u8bb2\u8ff0\uff0c\u624b\u811a\u6bd4\u62df\uff0c\u65e0\u4e0d\u51fa\u795e\u3002\u8981\u77e5\u80fd\u542c\u5230\u8fd9\u6837\u4e00\u4f4d\u6b66\u5b66\u540d\u5bb6\u8bb2\u8ff0\u62f3\u7406\u7cbe\u4e49\uff0c\u5b9e\u662f\u4e00\u751f\u4e4b\u4e2d\u53ef\u9047\u800c\u4e0d\u53ef\u6c42\u7684\u826f\u673a\u3002 \u3000\u3000\u8d75\u534a\u5c71\u8bf4\u7684\u662f\u592a\u6781\u62f3\u79d8\u8bc0\uff0c\u521d\u65f6\u738b\u6c0f\u5144\u5f1f\u3001\u5546\u8001\u592a\u3001\u9a6c\u884c\u7a7a\u3001\u6bb7\u4ef2\u7fd4\u7b49\u8fd8\u53ea\u5b58\u7740\u89c2\u6469\u4e0e\u5207\u78cb\u4e4b\u5fc3\uff0c\u4f46\u540e\u6765\u542c\u4ed6\u8d8a\u8bf4\u8d8a\u662f\u900f\u5f7b\uff0c\u8bb8\u591a\u81ea\u5e7c\u79ef\u5728\u5fc3\u4e2d\u7684\u7591\u96be\uff0c\u5e08\u7236\u89e3\u8bf4\u4e0d\u51fa\u3001\u81ea\u5df1\u82e6\u601d\u4e0d\u660e\uff0c\u5374\u51ed\u4ed6\u4e09\u8a00\u4e24\u8bed\uff0c\u767b\u65f6\u8c41\u7136\u800c\u901a\u3002 \u3000\u3000\u8d75\u534a\u5c71\u89e3\u6bd5\u201c\u4e71\u73af\u8bc0\u201d\uff0c\u8bf4\u9053\uff1a\u201c\u53e3\u8bc0\u53ea\u662f\u51e0\u53e5\u8bdd\uff0c\u8fd9\u659c\u5708\u65e0\u5f62\u5708\u4f7f\u5f97\u5bf9\u4e0d\u5bf9\uff0c\u53d1\u70b9\u4e0e\u843d\u70b9\u51c6\u4e0d\u51c6\uff0c\u53ef\u662f\u6bd5\u751f\u7684\u529f\u529b\u3002\u4f60\u61c2\u4e86\u4e48\uff1f\u201d\u9648\u79b9\u76fc\u671b\u8fd9\u201c\u4e71\u73af\u8bc0\u201d\u76fc\u4e86\u4e00\u751f\uff0c\u6b64\u65f6\u542c\u5f97\u660e\u767d\uff0c\u61c2\u5f97\u900f\u5f7b\uff0c\u77e5\u9053\u53ea\u8981\u518d\u52a0\u5341\u4f59\u5e74\u82e6\u7ec3\uff0c\u51ed\u6b64\u4e00\u8bc0\uff0c\u4fbf\u53ef\u6210\u6b66\u5b66\u5927\u5e08\uff0c\u4e0d\u7531\u5f97\u6ee1\u5fc3\u6b22\u559c\uff0c\u53c8\u95ee\uff1a\u201c\u8bf7\u95ee\u8d75\u7237\u90a3\u9634\u9633\u8bc0\u53c8\u662f\u5982\u4f55\uff1f\u201d \u3000\u3000\u8d75\u534a\u5c71\u9053\uff1a\u201d\u9634\u9633\u51b3\u4e5f\u662f\u516b\u53e5\u6b4c\uff0c\u4f60\u8bb0\u597d\u4e86\u3002\u201d\u9648\u79b9\u542c\u5f97\u51fa\u795e\uff0c\u5c31\u4f3c\u5f53\u5e74\u542c\u7236\u4eb2\u4f20\u6388\u6b66\u529f\u4e00\u822c\uff0c\u968f\u53e3\u5e94\u9053\uff1a\u201c\u662f\uff0c\u5b69\u513f\u7528\u5fc3\u8bb0\u7740\u3002\u201d\u5f85\u5f97\u4e00\u8a00\u51fa\u53e3\u8fd9\u624d\u60ca\u89c9\uff0c\u4e0d\u7531\u5f97\u6ee1\u8138\u901a\u7ea2\uff0c\u4f46\u4f17\u4eba\u90fd\u5728\u503e\u542c\u8d75\u534a\u5c71\u8bb2\u6b66\uff0c\u8c01\u4e5f\u6ca1\u7559\u610f\u4ed6\u8bf4\u4e9b\u4ec0\u4e48\uff0c\u5374\u65e0\u4e00\u4e2a\u5931\u7b11\u3002\u53ea\u542c\u8d75\u534a\u5c71\u6717\u58f0\u5ff5\u9053\uff1a\u201c\u592a\u6781\u9634\u9633\u5c11\u4eba\u4fee\uff0c\u541e\u5410\u5f00\u5408\u95ee\u521a\u67d4\u3002\u6b63\u9685\u6536\u653e\u4efb\u541b\u8d70\uff0c\u52a8\u9759\u53d8\u91cc\u4f55\u987b\u6101\uff1f\u751f\u514b\u4e8c\u6cd5\u968f\u7740\u7528\uff0c\u95ea\u8fdb\u5168\u5728\u52a8\u4e2d\u6c42\u3002\u8f7b\u91cd\u865a\u5b9e\u600e\u7684\u662f\uff1f\u91cd\u91cc\u73b0\u8f7b\u52ff\u7a0d\u7559\u3002\u201d \u3000\u3000\u8fd9\u53e3\u8bc0\u9648\u79b9\u5374\u4ece\u6ca1\u542c\u89c1\u8fc7\uff0c\u4f46\u4ed6\u6b64\u65f6\u5168\u65e0\u6000\u7591\uff0c\u7528\u5fc3\u8bb0\u5fc6\u3002\u53ea\u89c1\u8d75\u534a\u5c71\u62c9\u5f00\u67b6\u5f0f\uff0c\u6bd4\u7740\u62f3\u8def\uff0c\u8bf4\u9053\uff1a\u201c\u4e07\u7269\u90fd\u5206\u9634\u9633\u3002\u62f3\u6cd5\u4e2d\u7684\u9634\u9633\u5305\u542b\u6b63\u53cd\u3001\u8f6f\u786c\u3001\u521a\u67d4\u3001\u4f38\u5c48\u3001\u4e0a\u4e0b\u3001\u5de6\u53f3\u3001\u524d\u540e\u7b49\u7b49\u3002\u4f38\u662f\u9633\uff0c\u5c48\u662f\u9634\uff1b\u4e0a\u662f\u9633\uff0c\u4e0b\u662f\u9634\u3002\u6563\u624b\u4ee5\u541e\u6cd5\u4e3a\u5148\uff0c\u7528\u521a\u52b2\u8fdb\u51fb\uff0c\u5982\u86c7\u5438\u98df\uff1b\u5408\u624b\u4ee5\u5410\u6cd5\u4e3a\u5148\uff0c\u7528\u67d4\u52b2\u9677\u5165\uff0c\u4f3c\u725b\u5410\u8349\u3002\u5747\u987b\u51b7\u3001\u6025\u3001\u5feb\u3001\u8106\u3002\u81f3\u4e8e\u6b63\uff0c\u90a3\u662f\u56db\u4e2a\u6b63\u9762\uff0c\u9685\u662f\u56db\u89d2\u3002 \u3000\u3000\u4e34\u654c\u4e4b\u9645\uff0c\u52a1\u987b\u4ee5\u6211\u4e4b\u6b63\u51b2\u654c\u4e4b\u9685\u3002\u5018\u82e5\u6b63\u5bf9\u6b63\uff0c\u90a3\u4fbf\u51b2\u649e\uff0c\u4fbf\u662f\u4ee5\u786c\u529b\u6324\u786c\u529b\u3002\u82e5\u662f\u5e74\u5e7c\u529b\u5f31\uff0c\u529f\u529b\u4e0d\u53ca\u5bf9\u624b\uff0c\u5b9a\u7136\u5403\u4e8f\u3002\u201d \u3000\u3000\u80e1\u6590\u4e00\u76f4\u5728\u51dd\u795e\u542c\u4ed6\u8bb2\u89e3\u62f3\u7406\uff0c\u542c\u5230\u6b64\u5904\uff0c\u5fc3\u4e2d\u4e00\u51db\uff1a\u201c\u96be\u9053\u8fd9\u53e5\u8bdd\u662f\u8bf4\u7ed9\u6211\u542c\u7684\u4e48\uff1f\u662f\u8bf4\u6211\u4e0e\u738b\u5251\u82f1\u4ee5\u529b\u62fc\u529b\u7684\u9519\u5904\u4e48\uff1f\u201d \u3000\u3000\u5374\u89c1\u8d75\u534a\u5c71\u4e00\u773c\u4e0d\u671b\u81ea\u5df1\uff0c\u624b\u811a\u4e0d\u505c\uff0c\u53e3\u4e2d\u4e5f\u4e1d\u6beb\u4e0d\u505c\uff1a\u201c\u82e5\u662f\u4ee5\u89d2\u51b2\u89d2\uff0c\u62f3\u6cd5\u4e0a\u53eb\u4f5c\uff1a\u2018\u8f7b\u5bf9\u8f7b\uff0c\u5168\u843d\u7a7a\u2019\u3002\u5fc5\u987b\u4ee5\u6211\u4e4b\u91cd\uff0c\u51fb\u654c\u4e4b\u8f7b\uff1b\u4ee5\u6211\u4e4b\u8f7b\uff0c\u907f\u654c\u4e4b\u91cd\u3002\u518d\u8bf4\u5230\u2018\u95ea\u8fdb\u2019\u4e8c\u5b57\uff0c\u5f53\u95ea\u907f\u654c\u65b9\u8fdb\u51fb\u4e4b\u65f6\uff0c\u4e5f\u987b\u540c\u65f6\u53cd\u653b\uff0c\u8fd9\u662f\u5b88\u4e2d\u6709\u653b\uff1b\u800c\u81ea\u5df1\u653b\u51fb\u4e4b\u65f6\uff0c\u4e5f\u987b\u540c\u65f6\u95ea\u907f\u654c\u65b9\u8fdb\u62db\uff0c\u8fd9\u662f\u653b\u4e2d\u6709\u5b88\uff0c\u6b64\u6240\u8c13\u2018\u9022\u95ea\u5fc5\u8fdb\uff0c\u9022\u8fdb\u5fc5\u95ea\u2019\u3002\u62f3\u8bc0\u4e2d\u8a00\u9053\uff1a\u2018\u4f55\u8c13\u6253\uff1f\u4f55\u8c13\u987e\uff1f\u6253\u5373\u987e\uff0c\u987e\u5373\u6253\uff0c\u53d1\u624b\u4fbf\u662f\u3002\u4f55\u8c13\u95ea\uff1f\u4f55\u8c13\u8fdb\uff1f\u8fdb\u5373\u95ea\uff0c\u95ea\u5373\u8fdb\uff0c\u4e0d\u5fc5\u8fdc\u6c42\u3002\u2019\u82e5\u662f\u653b\u5b88\u6709\u522b\uff0c\u90a3\u4fbf\u4e0d\u662f\u4e0a\u4e58\u7684\u6b66\u529f\u3002\u201d\u8fd9\u756a\u8bdd\u53ea\u5c06\u80e1\u6590\u542c\u5f97\u72b9\u4f3c\u5927\u68a6\u521d\u9192\uff0c\u5fc3\u9053\uff1a\u201c\u82e5\u662f\u6211\u65e9\u77e5\u6b64\u7406\uff0c\u9002\u624d\u4e0e\u738b\u6c0f\u5144\u5f1f\u6bd4\u6b66\uff0c\u672a\u5fc5\u5c31\u8f93\u3002\u201d\u5fc3\u4e2d\u5bf9\u8d75\u534a\u5c71\u94a6\u4f69\u5230\u4e86\u6781\u5904\u3002 \u3000\u3000\u8d75\u534a\u5c71\u53c8\u9053\uff1a\u201c\u6b66\u529f\u4e2d\u7684\u52b2\u529b\u5343\u53d8\u4e07\u5316\uff0c\u4f46\u5927\u522b\u53ea\u6709\u4e09\u822c\u52b2\uff0c\u5373\u8f7b\u3001\u91cd\u3001\u7a7a\u3002\u7528\u91cd\u4e0d\u5982\u7528\u8f7b\uff0c\u7528\u8f7b\u4e0d\u5982\u7528\u7a7a\u3002\u62f3\u8bc0\u8a00\u9053\uff1a\u2018\u53cc\u91cd\u884c\u4e0d\u901a\uff0c\u5355\u91cd\u5012\u6210\u529f\u2019\u3002 \u3000\u3000\u53cc\u91cd\u662f\u529b\u4e0e\u529b\u4e89\uff0c\u6211\u6b32\u53bb\uff0c\u4f60\u6b32\u6765\uff0c\u7ed3\u679c\u662f\u5927\u529b\u5236\u5c0f\u529b\u3002\u5355\u91cd\u5374\u662f\u4ee5\u6211\u5c0f\u529b\uff0c\u51fb\u654c\u65e0\u529b\u4e4b\u5904\uff0c\u90a3\u4fbf\u80fd\u4e00\u53d1\u6210\u529f\u3002\u8981\u4f7f\u5f97\u654c\u4eba\u7684\u5927\u529b\u5904\u5904\u843d\u7a7a\uff0c\u6211\u5185\u529b\u867d\u5c0f\uff0c\u5374\u80fd\u80dc\u654c\uff0c\u8fd9\u624d\u7b97\u662f\u6b66\u5b66\u9ad8\u624b\u3002\u201d \u3000\u3000\u53ea\u89c1\u4ed6\u51fa\u624b\u6bd4\u5212\uff0c\u8bb8\u591a\u62f3\u6cd5\u7adf\u662f\u80e1\u6590\u521a\u624d\u4e0e\u738b\u5251\u82f1\u5bf9\u638c\u65f6\u6240\u7528\u3002\u4ed6\u8be6\u52a0\u89e3\u91ca\uff0c\u8fd9\u4e00\u62db\u5982\u4f55\u53ef\u4f7f\u654c\u62db\u7528\u7a7a\uff0c\u8fd9\u4e00\u62db\u5982\u4f55\u65b9\u59cb\u89c1\u529f\u3002\u80e1\u6590\u542c\u5230\u6b64\u5904\uff0c\u65b9\u59cb\u5927\u609f\uff1a\u201c\u539f\u6765\u8d75\u4e09\u7237\u8d39\u4e86\u8fd9\u4e48\u5927\u7684\u529b\u6c14\uff0c\u5374\u662f\u5728\u6307\u70b9\u6211\u7684\u6b66\u529f\u3002\u201d \u3000\u3000\u8981\u77e5\u9648\u79b9\u662f\u53db\u95e8\u72af\u4e0a\u7684\u5978\u5f92\uff0c\u8d75\u534a\u5c71\u600e\u80fd\u6388\u4ed6\u592a\u6781\u79d8\u6cd5\uff1f\u53ea\u662f\u4ed6\u89c1\u80e1\u6590\u62f3\u62db\u6781\u5c3d\u5947\u5999\uff0c\u4e34\u654c\u4e4b\u9645\u5374\u662f\u51ed\u7740\u4e00\u5df1\u7684\u806a\u660e\u751f\u53d8\uff0c\u62f3\u7406\u7684\u6839\u672c\u5c1a\u672a\u660e\u767d\uff0c\u60f3\u662f\u672a\u9047\u660e\u5e08\u6307\u70b9\u3002\u6b66\u6797\u4e4b\u4e2d\u89c4\u77e9\u6781\u591a\uff0c\u82e5\u662f\u522b\u95e8\u522b\u6d3e\u7684\u5f1f\u5b50\uff0c\u7eb5\u4f7f\u4ed6\u865a\u5fc3\u8bf7\u76ca\u6c42\u6559\uff0c\u4e5f\u672a\u4fbf\u7387\u5c14\u6307\u6559\uff0c\u5426\u5219\u6781\u6613\u60f9\u8d77\u4ed6\u672c\u95e8\u5e08\u957f\u7684\u4e0d\u5feb\uff0c\u8bb8\u591a\u7ea0\u7eb7\u7978\u60a3\uff0c\u5e38\u7531\u6b64\u800c\u8d77\u3002\u4ed6\u5b9e\u4e0d\u77e5\u80e1\u6590\u65e0\u5e08\u81ea\u901a\uff0c\u53ea\u51ed\u4e86\u7956\u4f20\u7684\u4e00\u90e8\u62f3\u7ecf\uff0c\u81ea\u884c\u4e60\u7ec3\u800c\u6210\uff0c\u773c\u89c1\u4ed6\u826f\u6750\u7f8e\u8d28\uff0c\u672a\u52a0\u96d5\u7422\uff0c\u751a\u662f\u53ef\u60dc\uff0c\u6599\u60f3\u4ed6\u5e08\u957f\u672a\u660e\u6b66\u5b66\u81f3\u7406\uff0c\u56e0\u6b64\u501f\u7740\u9648\u79b9\u8bf7\u95ee\u4e71\u73af\u8bc0\u4e0e\u9634\u9633\u8bc0\u7684\u673a\u4f1a\uff0c\u5c06\u6b66\u5b66\u7684\u57fa\u672c\u9053\u7406\u597d\u597d\u89e3\u8bf4\u4e00\u901a\uff0c\u6bcf\u4e00\u53e5\u8bdd\u90fd\u662f\u5207\u4e2d\u80e1\u6590\u62f3\u6cd5\u4e2d\u7684\u5f0a\u7aef\uff0c\u8bf4\u5f97\u4e0a\u662f\u503e\u56ca\u4ee5\u6388\u3002\u4ed6\u77e5\u80e1\u6590\u806a\u660e\u8fc7\u4eba\uff0c\u5fc5\u80fd\u4f53\u4f1a\uff0c\u81f3\u4e8e\u738b\u5251\u82f1\u3001\u9a6c\u884c\u7a7a\u7b49\u4eba\u867d\u4e5f\u542c\u5230\u4e86\uff0c\u4f46\u8fd9\u4e9b\u4eba\u5e74\u7eaa\u5df2\u5927\uff0c\u7eb5\u660e\u5176\u7406\uff0c\u4e5f\u672a\u5fc5\u80fd\u518d\u4e0b\u82e6\u529f\uff0c\u7ec3\u5230\u8fd9\u6b65\u7530\u5730\u3002 \u3000\u3000\u7ecf\u6b64\u4e00\u756a\u6307\u70b9\uff0c\u80e1\u6590\u65e5\u540e\u59cb\u5f97\u6210\u4e3a\u4e00\u4ee3\u6b66\u5b66\u9ad8\u624b\uff0c\u53ea\u662f\u5982\u6b64\u4f20\u6388\u529f\u51b3\uff0c\u5728\u6b66\u6797\u4e2d\u4e5f\u53ef\u8bf4\u662f\u522b\u5f00\u751f\u9762\u4e86\u3002 \u3000\u3000\u8d75\u534a\u5c71\u8bb2\u89e3\u5df2\u6bd5\uff0c\u5411\u9648\u79b9\u9053\uff1a\u201c\u6211\u8bf4\u7684\u53ef\u5bf9\u4e48\uff1f\u201d\u9648\u79b9\u9053\uff1a\u201c\u627f\u8499\u6307\u70b9\uff0c\u8305\u585e\u987f\u5f00\u3002\u65e9\u77e5\u5982\u6b64\uff0c\u5728\u4e0b\u4e5f\u4e0d\u5fc5\u5411\u5b59\u5415\u4e8c\u4eba\u82e6\u82e6\u54c0\u6c42\u4e86\u3002\u201d\u8d75\u534a\u5c71\u51b7\u7136\u9053\uff1a\u201c\u662f\u554a\uff0c\u65e9\u77e5\u5982\u6b64\uff0c\u90a3\u4e5f\u4e0d\u5fc5\u5bb3\u6b7b\u4e24\u6740\u4eba\u547d\u4e86\u3002\u9648\u79b9\u4e00\u60ca\uff0c\u53ea\u89c9\u4e00\u9053\u51c9\u610f\u4ece\u80cc\u810a\u4e0a\u76f4\u900f\u4e0b\u53bb\uff0c\u5fc3\u60f3\uff1a\u201c\u4ed6\u597d\u7aef\u7aef\u4f20\u6211\u62f3\u8bc0\uff0c\u600e\u5730\u53c8\u63d0\u6b64\u4e8b\uff1f\u201d\u5411\u738b\u6c0f\u5144\u5f1f\u3001\u6bb7\u4ef2\u7fd4\u7b49\u4eba\u4e00\u671b\uff0c\u4f46\u89c1\u5404\u4eba\u8138\u4e0a\u5747\u73b0\u8ff7\u60d8\u4e4b\u8272\u3002 \u3000\u3000\u8d75\u534a\u5c71\u9053\uff1a\u201c\u9648\u7237\uff0c\u8fd9\u4e24\u4e2a\u62f3\u51b3\u6211\u662f\u4f20\u4e8e\u4f60\u4e86\uff0c\u5982\u4f55\u4f7f\u7528\uff0c\u53ea\u6015\u4f60\u8fd8\u9886\u4f1a\u4e0d\u5230\uff0c\u6765\uff0c\u54b1\u4eec\u6765\u63a8\u63a8\u624b\u3002\u201d\u90a3\u63a8\u624b\u662f\u592a\u6781\u540c\u95e8\u7ec3\u6b66\u7684\u4e00\u79cd\u5bfb\u5e38\u624b\u6cd5\uff0c\u9648\u79b9\u5fc3\u4e2d\u867d\u5b58\u7591\u60e7\uff0c\u5374\u4e5f\u4e0d\u4fbf\u76f8\u62d2\uff0c\u8bf4\u9053\uff1a\u201c\u8d75\u4e09\u7237\uff0c\u5728\u4e0b\u6280\u827a\u5e73\u5e38\uff0c\u4f60\u591a\u5305\u6db5\u7740\u70b9\u513f\u3002\u201d\u8d75\u534a\u5c71\u94c1\u9752\u7740\u8138\u9053\uff1a\u201c\u592a\u6781\u5317\u5b97\u7b2c\u4e00\u9ad8\u624b\u5415\u5e0c\u8d24\u90fd\u6b7b\u5728\u9601\u4e0b\u638c\u5e95\uff0c\u600e\u8bf4\u5f97\u4e0a\u6280\u827a\u5e73\u5e38\uff1f\u770b\u62db\u5427\uff01\u201d\u4e00\u62db\u201c\u624b\u6325\u7435\u7436\u201d\uff0c\u5411\u4ed6\u51fb\u53bb\u3002\u9648\u79b9\u4e00\u60ca\uff0c\u5fd9\u4ee5\u201c\u5982\u5c01\u4f3c\u95ed\u201d\u5b88\u4f4f\u6b63\u4e2d\uff0c\u4f46\u6570\u62db\u4e4b\u95f4\uff0c\u62f3\u8def\u5df2\u5168\u53d7\u654c\u4eba\u4e4b\u5236\u3002\u4e24\u4eba\u4f7f\u7684\u592a\u6781\u62f3\u867d\u6709\u5357\u5317\u4e4b\u5206\uff0c\u62f3\u8def\u5176\u5b9e\u5927\u540c\u5c0f\u5f02\uff0c\u53ef\u662f\u529f\u529b\u6df1\u6d45\u6709\u522b\uff0c\u53c8\u62c6\u6570\u62db\uff0c\u9648\u79b9\u7684\u53cc\u638c\u4f3c\u4e4e\u5168\u7ed9\u8d75\u534a\u5c71\u7c98\u4f4f\u4e86\u3002 \u3000\u3000\u76f4\u5230\u6b64\u65f6\uff0c\u5b59\u521a\u5cf0\u5fc3\u5934\u4e00\u5757\u5927\u77f3\u65b9\u59cb\u843d\u5730\uff0c\u53ea\u542c\u8d75\u534a\u5c71\u95ee\u9053\uff1a\u201c\u5b59\u5144\uff0c\u4f60\u8bf4\u5415\u5e0c\u8d24\u662f\u7ed9\u4ed6\u7528\u2018\u4e91\u624b\u2019\u7d2f\u6b7b\u7684\uff1f\u201d\u5b59\u521a\u5cf0\u5fd9\u9053\uff1a\u201c\u662f\u554a\u3002\u6211\u89c1\u5230\u5415\u5e08\u5f1f\u7684\u5c38\u9996\uff0c\u663e\u662f\u7b4b\u9aa8\u8131\u529b\u3002\u201d\u9648\u79b9\u8d8a\u6597\u8d8a\u60ca\uff0c\u8bf4\u9053\uff1a\u201d\u8d75\u4e09\u7237\uff0c\u5728\u4e0b\u4e0d\u662f\u4f60\u7684\u5bf9\u624b\uff0c\u54b1\u4eec\u7f62\u624b\u5566\u3002\u201d\u8d75\u534a\u5c71\u9053\uff1a\u201d\u597d\uff0c\u4f60\u518d\u63a5\u6211\u4e00\u62db\u3002\u201d\u5de6\u4e0b\u5e26\u7740\u4ed6\u7684\u53f3\u624b\uff0c\u8f6c\u4e86\u4e00\u4e2a\u4eba\u5708\uff0c\u4e00\u80a1\u6781\u5f3a\u7684\u87ba\u65cb\u529b\u5e26\u52a8\u4ed6\u5de6\u624b\uff0c\u6b63\u662f\u592a\u6781\u4e91\u624b\u3002\u8fd9\u4e91\u624b\u8fde\u7ef5\u4e0d\u65ad\uff0c\u4e00\u5708\u8fc7\u540e\uff0c\u53c8\u662f\u4e00\u9601\uff0c\u5f53\u65e5\u9648\u79b9\u5bb3\u6b7b\u5415\u5e0c\u8d24\uff0c\u4f7f\u7684\u6b63\u662f\u8fd9\u4e00\u8def\u624b\u6cd5\u3002\u9648\u79b9\u60f3\u5230\u5415\u5e0c\u8d24\u6b7b\u65f6\u7684\u60e8\u72b6\uff0c\u60f3\u5230\u4ed6\u8fde\u58f0\u54c0\u544a\u800c\u81ea\u5df1\u5374\u7edd\u4e0d\u677e\u52b2\uff0c\u60f3\u5230\u4ed6\u8fde\u6700\u540e\u4e00\u5206\u529b\u6c14\u4e5f\u7ed9\u81ea\u5df1\u903c\u4e86\u51fa\u6765\uff0c\u4e0d\u7531\u5f97\u6c57\u5982\u96e8\u4e0b\u3002 \u3000\u3000\u8d75\u534a\u5c71\u89c1\u4ed6\u8138\u4e0a\u73b0\u51fa\u60ca\u60e7\u81f3\u6781\u4e4b\u8272\uff0c\u5fc3\u80a0\u4e00\u8f6f\uff0c\u5b9e\u611f\u4e0d\u5fcd\uff0c\u52b2\u529b\u4e00\u677e\uff0c\u7c98\u529b\u5378\u53bb\uff0c\u6e29\u8a00\u9053\uff1a\u201c\u5927\u4e08\u592b\u4e00\u8eab\u4f5c\u4e8b\u4e00\u8eab\u5f53\uff0c\u65e2\u884c\u6076\u4e8b\uff0c\u81ea\u6709\u6076\u679c\uff0c\u4f60\u597d\u597d\u60f3\u4e00\u60f3\u5427\u3002\u201d\u4ed6\u751f\u6027\u4ec1\u5584\uff0c\u867d\u77e5\u9648\u79b9\u6b7b\u6709\u5e94\u5f97\uff0c\u5374\u4e0d\u613f\u89c1\u4ed6\u5982\u5415\u5e0c\u8d24\u4e00\u822c\u60e8\u53d7\u6790\u78e8\u800c\u6b7b\u3002 \u3000\u3000\u4ed6\u8f6c\u8fc7\u8eab\u5b50\uff0c\u8d1f\u624b\u80cc\u540e\uff0c\u4ef0\u5929\u53f9\u9053\uff1a\u201c\u4e00\u4e2a\u4eba\u6240\u4ee5\u5b66\u6b66\uff0c\u82e5\u4e0d\u80fd\u536b\u56fd\u5fa1\u4fae\uff0c\u4e5f\u5f53\u884c\u4fa0\u4ed7\u4e49\uff0c\u6d4e\u5371\u6276\u56f0\uff1a\u82e5\u662f\u4ee5\u6b66\u6d4e\u6076\uff0c\u90a3\u662f\u8fdc\u4e0d\u5982\u4f5c\u4e2a\u5bfb\u5e38\u519c\u592b\uff0c\u79cd\u7530\u8fc7\u6d3b\u4e86\u3002\u201d\u8fd9\u51e0\u53e5\u5176\u5b9e\u4e5f\u662f\u8bf4\u7ed9\u80e1\u6590\u542c\u7684\uff0c\u751f\u6015\u4ed6\u65e5\u540e\u4e3a\u806a\u660e\u6240\u8bef\uff0c\u8d70\u5165\u6b67\u9014\uff0c\u4ed6\u4e00\u751f\u4e4b\u4e2d\uff0c\u4ece\u672a\u89c1\u8fc7\u80e1\u6590\u8fd9\u7b49\u7f8e\u8d28\uff0c\u5fc3\u4e2d\u5bf9\u4e4b\u7231\u6781\uff0c\u81ea\u5fd6\u6b64\u4e8b\u4e00\u4e86\uff0c\u968f\u5373\u897f\u5f52\u56de\u7586\uff0c\u65e5\u540e\u672a\u5fc5\u518d\u80fd\u4e0e\u4e4b\u76f8\u89c1\uff0c\u56e0\u6b64\u4f20\u6388\u4e0a\u4e58\u6b66\u5b66\u4e4b\u540e\uff0c\u590d\u8c06\u8c06\u76f8\u8beb\uff0c\u529d\u5176\u52c9\u529b\u5b66\u597d\u3002 \u3000\u3000\u80e1\u6590\u5982\u4f55\u4e0d\u61c2\u4ed6\u8a00\u4e2d\u4e4b\u610f\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u59d3\u9648\u7684\uff0c\u4e00\u4e2a\u4eba\u505a\u4e86\u6076\u4e8b\uff0c\u5c31\u7b97\u65c1\u4eba\u4e0d\u95ee\uff0c\u4e5f\u4e0d\u5982\u81ea\u5c3d\u4e86\u7684\u597d\uff0c\u514d\u5f97\u73b7\u6c61\u4e86\u7956\u5b97\u7684\u82f1\u540d\u3002\u201d\u4ed6\u8fd9\u51e0\u53e5\u5176\u5b9e\u662f\u7b54\u590d\u8d75\u534a\u5c71\u7684\u3002 \u3000\u3000\u8d75\u534a\u5c71\u6781\u662f\u559c\u6170\uff0c\u8f6c\u5934\u671b\u7740\u4ed6\uff0c\u795e\u8272\u751a\u662f\u5609\u8bb8\u3002\u80e1\u6590\u773c\u4e2d\u5374\u6ee1\u662f\u611f\u6fc0\u4e4b\u60c5\u3002 \u3000\u3000\u6b63\u5f53\u4e00\u8001\u4e00\u5c11\u60fa\u60fa\u76f8\u60dc\u3001\u5fc3\u60c5\u4e92\u901a\u4e4b\u9645\uff0c\u9648\u79b9\u89c1\u8d75\u534a\u5c71\u540e\u5fc3\u95e8\u6237\u5927\u5f00\uff0c\u5168\u65e0\u9632\u5907\uff0c\u81ea\u5df1\u4e0e\u4ed6\u76f8\u8ddd\u4e0d\u5230\u4e8c\u5c3a\uff0c\u5fc3\u60f3\uff1a\u201c\u4e0d\u662f\u4f60\u6b7b\uff0c\u4fbf\u662f\u6211\u4ea1\uff01\u201d\u8fd0\u52b2\u53f3\u81c2\uff0c\u594b\u8d77\u5168\u8eab\u4e4b\u529b\uff0c\u4e00\u62db\u201c\u8fdb\u6b65\u642c\u62e6\u6376\u201d\uff0c\u5f80\u8d75\u534a\u5c71\u80cc\u5fc3\u51fb\u53bb\u3002 \u3000\u3000\u9648\u79b9\u8fd9\u4e00\u62f3\uff0c\u4e43\u662f\u4ed6\u6bd5\u751f\u529f\u529b\u4e4b\u6240\u805a\uff0c\u81ea\u77e5\u8fd9\u4e00\u62db\u82e5\u4e0d\u80fd\u5236\u654c\u4e8e\u6b7b\u547d\uff0c\u81ea\u5df1\u5c31\u65e0\u6d3b\u547d\u4e4b\u673a\uff0c\u5f53\u771f\u662f\u62f3\u53bb\u5982\u98ce\uff0c\u52bf\u82e5\u8fc5\u96f7\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u7535\u5149\u77f3\u706b\u7684\u4e00\u77ac\u4e4b\u95f4\uff0c\u8d75\u534a\u5c71\u8eab\u5b50\u4e00\u5f13\uff0c\u6b63\u662f\u592a\u6781\u62f3\u4e2d\u201c\u767d\u9e64\u4eae\u7fc5\u201d\u7684\u524d\u534a\u62db\uff0c\u9648\u79b9\u8fd9\u4e00\u62f3\u7684\u52b2\u529b\u767b\u65f6\u843d\u7a7a\u3002\u8d75\u534a\u5c71\u8170\u95f4\u4e00\u626d\uff0c\u4f7f\u51fa\u201c\u63fd\u96c0\u5c3e\u201d\u7684\u524d\u534a\u62db\uff0c\u8f6c\u8fc7\u8eab\u6765\uff0c\u53cc\u638c\u7f13\u7f13\u63a8\u51fa\uff0c\u7528\u7684\u662f\u592a\u6781\u62f3\u4e2d\u7684\u201c\u6309\u201d\u52b2\u3002\u4ed6\u4ee5\u534a\u62db\u5316\u89e3\u654c\u52bf\uff0c\u7b2c\u4e8c\u4e2a\u534a\u62db\u5df2\u7acb\u5373\u53cd\u653b\uff0c\u53ea\u4e24\u4e2a\u534a\u62db\uff0c\u9648\u79b9\u5168\u8eab\u5df2\u5728\u4ed6\u638c\u529b\u7b3c\u7f69\u4e4b\u4e0b\u3002 \u3000\u3000\u592a\u6781\u62f3\u4e43\u662f\u6781\u5bfb\u5e38\u7684\u62f3\u672f\uff0c\u6b66\u5b66\u4e4b\u58eb\u4eba\u4eba\u8bc6\u5f97\u3002\u4f17\u4eba\u89c1\u8d75\u534a\u5c71\u4e00\u5b88\u4e00\u653b\u90fd\u53ea\u4f7f\u4e86\u534a\u62db\uff0c\u5c31\u80fd\u968f\u5fc3\u6240\u6b32\uff0c\u7684\u662f\u540d\u5bb6\u624b\u6bb5\uff0c\u975e\u540c\u51e1\u4fd7\uff0c\u65e0\u4e0d\u5927\u4e3a\u53f9\u670d\u3002 \u3000\u3000\u6b64\u65f6\u9648\u79b9\u54ac\u7d27\u7259\u5173\uff0c\u62fc\u7740\u751f\u5e73\u6240\u5b66\uff0c\u4e0e\u8d75\u534a\u5c71\u76f8\u6297\uff0c\u521d\u4e00\u63a5\u62db\uff0c\u53ea\u89c9\u5bf9\u65b9\u529b\u9053\u4e5f\u4e0d\u751a\u5f3a\uff0c\u4e8e\u662f\u624b\u4e0a\u52a0\u52b2\u3002\u4f46\u53d1\u529b\u4e00\u589e\uff0c\u7acb\u89c9\u5bf9\u65b9\u53cd\u51fb\u7684\u529b\u9053\u4e5f\u76f8\u5e94\u5927\u589e\uff0c\u4e00\u60ca\u4e4b\u4e0b\uff0c\u6025\u5fd9\u677e\u52b2\uff0c\u5bf9\u65b9\u7684\u53cd\u529b\u5c45\u7136\u4e5f\u5373\u677e\u4e86\uff0c\u7136\u800c\u8981\u8131\u51fa\u4ed6\u7275\u5f15\u4e4b\u529b\uff0c\u5374\u4e5f\u4e0d\u80fd\u3002 \u3000\u3000\u80e1\u6590\u9ed8\u9ed8\u60f3\u7740\u8d75\u534a\u5c71\u9002\u624d\u6240\u6388\u7684\u201c\u4e71\u73af\u8bc0\u201d\u4e0e\u201c\u9634\u9633\u8bc0\u201d\uff0c\u51dd\u795e\u89c2\u770b\u4e8c\u4eba\u8fc7\u62db\uff0c\u5370\u8bc1\u8d75\u534a\u5c71\u6240\u8bf4\u7684\u62f3\u51b3\u8981\u4e49\u3002\u4f46\u89c1\u9648\u79b9\u53d1\u62f3\u63a8\u638c\uff0c\u52b2\u529b\u867d\u5f3a\uff0c\u53ef\u662f\u53ea\u8981\u7ed9\u8d75\u534a\u5c71\u4e00\u62e8\u4e00\u5e26\uff0c\u638c\u52bf\u7684\u65b9\u4f4d\u767b\u65f6\u53d8\u4e86\uff0c\u90a3\u6b63\u662f\u201c\u4e71\u73af\u8bc0\u201d\u4e2d\u6240\u8c13\u201c\u9677\u654c\u6df1\u5165\u4e71\u73af\u5185\uff0c\u56db\u4e24\u80fd\u62e8\u5343\u65a4\u52a8\u201d\u7684\u5e94\u7528\u3002\u4ed6\u77a7\u4e86\u4e00\u4f1a\uff0c\u7b11\u9053\uff1a\u201c\u9648\u8001\u5144\uff0c\u4f60\u5df2\u7ecf\u6df1\u9677\u8d75\u4e09\u7237\u7684\u4e71\u73af\u4e4b\u5185\u4e86\uff0c\u6211\u77a7\u4f60\u4eca\u65e5\u8981\u5f52\u4f4d\u3002\u201d \u3000\u3000\u9648\u79b9\u5168\u795e\u8d2f\u6ce8\u5730\u5e94\u4ed8\u654c\u62db\uff0c\u80e1\u6590\u8fd9\u51e0\u53e5\u8bdd\u5b8c\u5168\u6ca1\u6709\u542c\u89c1\u3002\u53c8\u62c6\u6570\u62db\uff0c\u80e1\u6590\u77a7\u51fa\u9648\u79b9\u62f3\u62db\u4e2d\u9732\u51fa\u7834\u7efd\uff0c\u53eb\u9053\uff1a\u201c\u8d75\u4f2f\u4f2f\uff0c\u4ed6\u5de6\u808b\u7a7a\u865a\uff0c\u4f55\u4e0d\u51fb\u4ed6\uff1f\u201d \u3000\u3000\u8d75\u534a\u5c71\u7b11\u9053\uff1a\u201c\u6b63\u662f\uff01\u201d\u62f3\u968f\u58f0\u81f3\uff0c\u653b\u5411\u4ed6\u7684\u5de6\u808b\u3002\u9648\u79b9\u6025\u5fd9\u95ea\u907f\uff0c\u80e1\u6590\u53c8\u9053\uff1a\u201c\u653b\u4ed6\u53f3\u80a9\u3002\u201d\u8d75\u534a\u5c71\u9053\uff1a\u201c\u597d\uff01\u201d\u4e00\u638c\u5411\u4ed6\u53f3\u80a9\u62cd\u53bb\u3002 \u3000\u3000\u9648\u79b9\u6c89\u80a9\u53cd\u638c\u67b6\u5f00\u3002\u8d75\u534a\u5c71\u7b11\u95ee\u9053\uff1a\u201c\u4e0b\u4e00\u62db\u600e\u5730\uff1f\u201d\u80e1\u6590\u9053\uff1a\u201c\u8e22\u4ed6\u8170\u95f4\u3002\u201d\u8d75\u534a\u5c71\u5de6\u638c\u4e00\u5e26\uff0c\u9648\u79b9\u62ff\u52b2\u7a33\u4f4f\u8eab\u5b50\uff0c\u8d75\u534a\u5c71\u679c\u3002\u7136\u98de\u811a\u8e22\u4ed6\u8170\u95f4\u3002 \u3000\u3000\u80e1\u6590\u8fde\u53eb\u6570\u4e0b\uff0c\u6bcf\u4e00\u62db\u90fd\u8bf4\u7684\u5934\u5934\u662f\u9053\u3002\u8d75\u534a\u5c71\u8d5e\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4f60\u8bf4\u7684\u5927\u6709\u9053\u7406\u3002\u201d\u80e1\u6590\u7a81\u7136\u53eb\u9053\uff1a\u201c\u62cd\u4ed6\u80cc\u5fc3\u3002\u201d \u3000\u3000\u8fd9\u65f6\u8d75\u534a\u5c71\u6b63\u4e0e\u9648\u79b9\u76f8\u5bf9\uff0c\u5fc3\u4e2d\u4e00\u6014\uff1a\u201c\u8fd9\u4e00\u62db\u53ef\u53eb\u5f97\u4e0d\u5bf9\u4e86\uff0c\u6211\u4e0e\u654c\u4eba\u6b63\u9762\u76f8\u6301\uff0c\u600e\u80fd\u653b\u4ed6\u80cc\u5fc3\uff1f\u201d\u4f46\u5fae\u4e00\u8fdf\u7591\uff0c\u7acb\u65f6\u7701\u609f\uff1a\u201c\u539f\u6765\u8fd9\u5b69\u5b50\u662f\u51fa\u4e86\u4e2a\u96be\u9898\u7ed9\u6211\u505a\u3002\u201d\u5f53\u4e0b\u8eab\u5b50\u534a\u659c\uff0c\u53f3\u638c\u5411\u5916\u62d6\u5f15\uff0c\u9648\u79b9\u4e5f\u5373\u659c\u8eab\u5e94\u62db\u3002\u8d75\u534a\u5c71\u5de6\u638c\u518d\u5411\u53f3\u4e00\u5e26\uff0c\u9648\u79b9\u7684\u8eab\u5b50\u53c8\u659c\u4e86\u51e0\u5206\uff0c\u80cc\u5fc3\u7b97\u662f\u5356\u7ed9\u4e86\u4eba\u5bb6\u3002\u8d75\u534a\u5c71\u8f7b\u8f7b\u4e00\u638c\u62cd\u51fa\uff0c\u6b63\u51fb\u4ed6\u7684\u80cc\u810a\u3002\u8fd9\u4e00\u638c\u53ea\u8981\u53bb\u5f97\u7a0d\u5feb\uff0c\u529b\u9053\u7565\u5f3a\uff0c\u9648\u79b9\u5df2\u81ea\u6bd9\u547d\uff0c\u4ed6\u5927\u9a87\u4e4b\u4e0b\uff0c\u6025\u5fd9\u8f6c\u8eab\uff0c\u8138\u4e0a\u60e8\u65e0\u4eba\u8272\u3002 \u3000\u3000\u8d75\u534a\u5c71\u56de\u5934\u7b11\u9053\uff1a\u201c\u5bf9\u4e0d\u5bf9\u554a\uff1f\u201d\u80e1\u6590\u5927\u62c7\u6307\u4e00\u7fd8\uff0c\u8d5e\u9053\uff1a\u201c\u597d\u6781\u4e86\uff01\u201d \u3000\u3000\u9648\u79b9\u6b7b\u91cc\u9003\u751f\uff0c\u4f46\u7a76\u662f\u540d\u5bb6\u5f1f\u5b50\uff0c\u867d\u662f\u60ca\u9b42\u672a\u5b9a\uff0c\u5374\u5df2\u89c1\u5230\u53ef\u4e58\u4e4b\u673a\uff0c\u53ea\u89c1\u8d75\u534a\u5c71\u56de\u8eab\u4e0e\u80e1\u6590\u8bf4\u8bdd\uff0c\u4e0b\u76d8\u7a7a\u865a\uff0c\u5fc3\u60f3\uff1a\u201c\u6211\u6025\u653b\u4e24\u62db\uff0c\u77a7\u6765\u5c31\u80fd\u9003\u547d\u3002\u201d\u98de\u817f\u201c\u8f6c\u8eab\u8e6c\u811a\u201d\uff0c\u731b\u5411\u8d75\u534a\u5c71\u8e22\u53bb\uff0c\u89c1\u4ed6\u4fa7\u8eab\u4e00\u9000\uff0c\u5927\u559d\u4e00\u58f0\uff0c\u4e00\u62db\u201c\u624b\u6325\u7435\u7436\u201d\uff0c\u659c\u51fb\u654c\u4eba\u5de6\u80a9\u3002\u4ed6\u8fd9\u4e24\u62db\u8fde\u73af\u800c\u51fa\uff0c\u52bf\u5982\u72c2\u98ce\u9aa4\u96e8\uff0c\u7528\u610f\u4e0d\u5728\u4f24\u654c\uff0c\u53ea\u6c42\u8d75\u534a\u5c71\u518d\u9000\u4e00\u6b65\uff0c\u4ed6\u5c31\u80fd\u593a\u95e8\u800c\u9003\uff0c\u81ea\u6043\u5e74\u8f7b\u529b\u58ee\uff0c\u817f\u957f\u811a\u5feb\uff0c\u8d75\u534a\u5c71\u8eab\u5b50\u80a5\u80d6\uff0c\u62f3\u672f\u867d\u9ad8\uff0c\u8bf4\u5230\u8dd1\u8def\uff0c\u603b\u80dc\u4e0d\u4e86\u81ea\u5df1\u3002 \u3000\u3000\u8d75\u534a\u5c71\u89c1\u4ed6\u8d77\u817f\uff0c\u4fbf\u5df2\u731c\u5230\u4ed6\u7684\u7528\u610f\uff0c\u5f85\u4ed6\u201c\u624b\u6325\u7435\u7436\u201d\u4e00\u62db\u6253\u5230\uff0c\u7adf\u4e0d\u540e\u9000\uff0c\u8e0f\u4e0a\u4e00\u6b65\uff0c\u4e5f\u662f\u4e00\u62db\u201c\u624b\u6325\u7435\u7436\u201d\u3002\u8fd9\u4e00\u62db\u4ee5\u529b\u78b0\u529b\uff0c\u62db\u6570\u76f8\u540c\u800c\u5904\u4e8e\u9006\u52bf\uff0c\u539f\u662f\u592a\u6781\u62f3\u4e2d\u7684\u5927\u5fcc\uff0c\u4e0e\u4ed6\u9002\u624d\u6240\u8bf4\u201c\u53cc\u91cd\u884c\u4e0d\u901a\u201d\u7684\u62f3\u7406\u622a\u7136\u76f8\u53cd\uff0c\u5373\u4ee4\u662f\u9ad8\u624b\u9022\u7740\u4f4e\u624b\uff0c\u4e5f\u662f\u975e\u8d25\u4e0d\u53ef\u3002\u65c1\u89c2\u4f17\u4eba\u5012\u6709\u534a\u6570\u8f7b\u8f7b\u201c\u566b\u201d \u3000\u3000\u7684\u4e00\u58f0\u3002\u9648\u79b9\u53cd\u638c\u4e00\u63a2\uff0c\u5df2\u6293\u7740\u8d75\u534a\u5c71\u7684\u624b\u8155\uff0c\u5c31\u52bf\u4e00\u5e26\uff0c\u5c06\u4ed6\u5e9e\u5927\u7684\u8eab\u8eaf\u4e3e\u4e86\u8d77\u6765\uff0c\u968f\u5373\u7529\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u5b59\u521a\u5cf0\u4e0e\u5415\u5c0f\u59b9\u9f50\u58f0\u5927\u53eb\uff1a\u201c\u554a\u54df\uff01\u201d\u80e1\u6590\u5374\u7b11\u7740\u53eb\u9053\uff1a\u201d\u5999\u6781\uff0c\u5999\u6781\uff01\u201d \u3000\u3000\u8d75\u534a\u5c71\u8eab\u5728\u534a\u7a7a\uff0c\u5fc3\u4e2d\u6697\u53f9\uff1a\u201c\u65e0\u602a\u5317\u5b97\u592a\u6781\u76db\u6781\u4e2d\u8870\u3002\u5b59\u521a\u5cf0\u5728\u4e3a\u4e00\u6d3e\u638c\u95e8\uff0c\u5374\u4e0d\u53ca\u4e00\u4e2a\u5c0f\u5c0f\u5b69\u5b50\uff0c\u7adf\u7136\u77a7\u4e0d\u51fa\u6211\u6b64\u62db\u7684\u5999\u7528\uff0f\u8ddf\u7740\u4e00\u9635\u559c\u6b22\uff1a \u3000\u3000\u201c\u8fd9\u5b69\u5b50\u9886\u609f\u4e86\u6211\u6307\u70b9\u7684\u62f3\u7406\u7cbe\u4e49\uff0c\u7acb\u5373\u80fd\u591f\u53d8\u901a\uff0c\u5f53\u771f\u96be\u5f97\u3002\u201d \u3000\u3000\u9648\u79b9\u5c06\u654c\u4eba\u6293\u8d77\uff0c\u5fc3\u4e2d\u53c8\u60ca\u53c8\u559c\uff0c\u8fd9\u4e00\u4e0b\u6210\u529f\uff0c\u5374\u662f\u4ed6\u59cb\u6599\u6240\u4e0d\u53ca\uff0c\u7528\u529b\u4e00\u7529\u4e4b\u4e0b\uff0c\u6ee1\u62df\u5c31\u7b97\u4e0d\u80fd\u4f24\u654c\uff0c\u4e5f\u53ef\u5168\u8eab\u800c\u51fa\u5546\u5bb6\u5821\u4e86\u3002\u54ea\u77e5\u4e3e\u81c2\u4e00\u6325\uff0c\u8d75\u534a\u5c71\u624b\u638c\u4e00\u7ffb\uff0c\u53cd\u800c\u5c06\u4ed6\u624b\u8155\u62ff\u4f4f\uff0c\u8fd9\u4e00\u7529\u7adf\u6ca1\u5c06\u4ed6\u6454\u51fa\u3002 \u3000\u3000\u9648\u79b9\u4e00\u60ca\uff0c\u5de6\u638c\u968f\u5373\u5411\u4e0a\u6325\u51fb\uff0c\u8d75\u534a\u5c71\u5c45\u9ad8\u4e34\u4e0b\uff0c\u53f3\u51fb\u6309\u843d\u3002\u62cd\u7684\u4e00\u58f0\uff0c\u53cc\u638c\u76f8\u4ea4\uff0c\u4e24\u53ea\u624b\u638c\u5c31\u4f3c\u7528\u6781\u7c98\u7684\u80f6\u6c34\u7c98\u5f80\u4e86\u3002\u9648\u79b9\u5de6\u638c\u524d\u4f38\uff0c\u8d75\u534a\u5c71\u53f3\u638c\u4fbf\u540e\u7f29\uff0c\u9648\u79b9\u82e5\u662f\u56de\u593a\uff0c\u4ed6\u4fbf\u8ddf\u8fdb\uff0c\u4e00\u4e2a\u80d6\u80d6\u7684\u8eab\u8eaf\uff0c\u5374\u4ecd\u662f\u53cc\u8db3\u79bb\u5730\uff0c\u88ab\u9648\u79b9\u4e3e\u5728\u534a\u7a7a\u3002 \u3000\u3000\u6309\u7167\u5e38\u7406\uff0c\u4e00\u4eba\u8eab\u5b50\u4e34\u7a7a\uff0c\u5931\u4e86\u51ed\u501f\uff0c\u90a3\u5df2\u662f\u5904\u4e8e\u5fc5\u8d25\u4e4b\u5730\uff0c\u4f46\u8d75\u534a\u5c71\u77e5\u5df1\u77e5\u5f7c\uff0c\u6599\u5b9a\u5bf9\u65b9\u529f\u529b\u4e0e\u81ea\u5df1\u76f8\u5dee\u592a\u8fdc\uff0c\u662f\u4ee5\u6545\u884c\u9669\u7740\uff0c\u8981\u5c06\u5e73\u751f\u6240\u609f\u5230\u6700\u7cbe\u5965\u7684\u62f3\u7406\uff0c\u6307\u70b9\u7ed9\u80e1\u6590\u77e5\u6653\uff0c\u8981\u53eb\u4ed6\u4e34\u654c\u65f6\u4e0d\u53ef\u62d8\u6ce5\u4e00\u683c\uff0c\u7528\u6b63\u4e3a\u6839\u57fa\uff0c\u7528\u5947\u4e3a\u53d8\u7740\uff0c\u514d\u5f97\u5982\u738b\u5251\u82f1\u3001\u738b\u5251\u6770\u5144\u5f1f\u4e00\u822c\uff0c\u80f6\u67f1\u9f13\u745f\uff0c\u4e0d\u61c2\u201c\u51fa\u5947\u5236\u80dc\u201d \u3000\u3000\u7684\u9053\u7406\u3002 \u3000\u3000\u4ed6\u5de6\u624b\u4e0e\u9648\u79b9\u53f3\u624b\u76f8\u63a5\uff0c\u53f3\u624b\u4e0e\u4ed6\u5de6\u624b\u76f8\u63a5\uff0c\u4e0d\u8bba\u9648\u79b9\u5982\u4f55\u72c2\u7529\u731b\u6454\uff0c\u59cb\u7ec8\u4e0d\u80fd\u4f7f\u4ed6\u6709\u4e00\u8db3\u7740\u5730\u3002 \u3000\u3000\u8d75\u534a\u5c71\u8eab\u5b50\u80a5\u80d6\uff0c\u4e8c\u767e\u6765\u65a4\u7684\u4efd\u91cf\u538b\u5728\u5bf9\u65b9\u53cc\u81c2\u4e4b\u4e0a\u3002\u521d\u65f6\u9648\u79b9\u5c1a\u4e0d\u89c9\u5f97\u600e\u6837\uff0c\u65f6\u523b\u7a0d\u4e45\uff0c\u4f46\u89c9\u8180\u5b50\u4e0a\u7684\u538b\u529b\u8d8a\u6765\u8d8a\u91cd\uff0c\u5c31\u4f3c\u4e3e\u4e86\u4e00\u5757\u4e8c\u767e\u591a\u65a4\u7684\u5927\u77f3\u7ec3\u529f\u4e00\u822c\u3002\u82e5\u771f\u662f\u6781\u91cd\u7684\u4e00\u5757\u5927\u77f3\uff0c\u4e5f\u5c31\u7f62\u4e86\uff0c\u4f46\u8d75\u534a\u5c71\u4eba\u5728\u7a7a\u4e2d\uff0c\u53cc\u8db3\u81ea\u7531\uff0c\u4e0d\u7edd\u5bfb\u6687\u62b5\u9699\uff0c\u8e22\u4ed6\u5934\u8138\u4e0e\u53cc\u76ee\u3002 \u3000\u3000\u9648\u79b9\u53c8\u652f\u6301\u7247\u523b\uff0c\u5df2\u662f\u989d\u5934\u89c1\u6c57\uff0c\u731b\u5730\u4e00\u4e2a\u7bad\u6b65\uff0c\u7eb5\u5411\u67f1\u8fb9\uff0c\u6325\u624b\u8fd0\u529b\uff0c\u60f3\u5c06\u654c\u4eba\u7684\u8eab\u5b50\u5f80\u67f1\u5b50\u4e0a\u6325\u53bb\u3002\u4f46\u8d75\u534a\u5c71\u5c82\u80fd\u7740\u4e86\u4ed6\u7684\u9053\u513f\uff0c\u53f3\u8db3\u65e9\u51fa\uff0c\u6491\u5728\u67f1\u4e0a\u3002\u5148\u524d\u4ed6\u8eab\u5b50\u5728\u534a\u7a7a\uff0c\u538b\u5728\u9648\u79b9\u8180\u4e0a\u7684\u53ea\u80fd\u662f\u81ea\u8eab\u91cd\u91cf\uff0c\u8981\u52a0\u4e0a\u4e00\u4e24\u4e00\u94b1\u7684\u529b\u9053\u4e5f\u662f\u7edd\u4e0d\u80fd\u591f\uff0c\u6b64\u65f6\u8db3\u4e0a\u501f\u4e86\u67f1\u5b50\u4e4b\u529b\uff0c\u767b\u65f6\u4e00\u80a1\u5f3a\u529b\uff0c\u5982\u6cf0\u5c71\u538b\u9876\u822c\u76d6\u5c06\u4e0b\u6765\u3002\u9648\u79b9\u53cc\u81c2\u683c\u683c\u4f5c\u54cd\uff0c\u5982\u6b32\u65ad\u6298\uff0c\u6697\u53eb\uff1a\u201c\u4e0d\u5999\uff01\u201d\u6025\u5fd9\u8dc3\u5f00\u3002 \u3000\u3000\u8fd9\u65f6\u4ed6\u5168\u8eab\u5927\u6c57\u6dcb\u6f13\uff0c\u6e10\u6e10\u6e7f\u900f\u8863\u886b\uff0c\u4e0d\u8bba\u4f7f\u5730\u5802\u62f3\u7740\u5730\u6253\u6eda\uff0c\u6216\u662f\u7eb5\u6a2a\u8df3\u8dc3\uff0c\u8d75\u534a\u5c71\u603b\u662f\u8eab\u5728\u534a\u7a7a\uff0c\u5c06\u81ea\u8eab\u91cd\u91cf\u538b\u5728\u4ed6\u7684\u8eab\u4e0a\u3002 \u3000\u3000\u80e1\u6590\u89c1\u8d75\u534a\u5c71\u7684\u6b66\u529f\u5982\u6b64\u795e\u5999\uff0c\u4e0d\u7981\u53c8\u662f\u60ca\u5947\uff0c\u53c8\u662f\u559c\u6b22\uff0c\u89c1\u4ed6\u4e0b\u76d8\u51ed\u865a\uff0c\u5168\u7136\u501f\u654c\u4eba\u4e4b\u529b\u53cd\u51fb\u3002\u53ea\u89c1\u9648\u79b9\u8eab\u4e0a\u6c57\u6c34\u4e00\u6ef4\u6ef4\u5730\u843d\u5728\u5730\u4e0b\uff0c\u5c31\u50cf\u662f\u5728\u4e00\u573a\u503e\u76c6\u5927\u96e8\u4e0b\u6dcb\u4e86\u534a\u5929\u4e00\u822c\u3002\u4e0d\u591a\u4e00\u4f1a\uff0c\u6ee1\u5730\u90fd\u662f\u6c34\u6e0d\u3002 \u3000\u3000\u80e1\u6590\u8fd8\u9053\u4ed6\u662f\u51fa\u5c3d\u5168\u529b\uff0c\u75b2\u7d2f\u8fc7\u751a\u3002\u9a6c\u884c\u7a7a\u3001\u738b\u5251\u82f1\u7b49\u884c\u5bb6\uff0c\u5374\u77e5\u9648\u79b9\u6bcf\u6d41\u4e00\u6ef4\u6c57\u6c34\uff0c\u529f\u529b\u4fbf\u6d88\u8017\u2026\u4e00\uff0c\u5f85\u5f97\u6c57\u6c34\u6d41\u65e0\u53ef\u6d41\uff0c\u90a3\u4fbf\u662f\u6cb9\u5c3d\u706f\u67af\u3001\u6bd9\u547d\u4e4b\u65f6\u4e86\u3002 \u3000\u3000\u9648\u79b9\u81ea\u5df1\u4e5f\u4f55\u5c1d\u4e0d\u77e5\uff0c\u53ea\u89c9\u5f97\u5168\u8eab\u9178\u8f6f\uff0c\u80f8\u53e3\u7a7a\u6d1e\u6d1e\u5730\u96be\u53d7\u4e4b\u6781\uff0c\u731b\u5730\u60f3\u8d77\uff1a\u201c\u6211\u4f7f\u4e91\u624b\u7d2f\u6b7b\u5415\u5e0c\u8d24\u4e4b\u65f6\uff0c\u4ed6\u8eab\u4e0a\u6240\u53d7\u3001\u5fc3\u4e2d\u6240\u611f\uff0c\u5b9a\u4e0e\u6211\u6b64\u65f6\u4e00\u822c\u65e0\u7591\u3002\u8fd9\u53eb\u505a\u81ea\u4f5c\u81ea\u53d7\uff0c\u773c\u524d\u62a5\u5e94\u3002\u201d\u4e00\u60f3\u5230\u6027\u547d\u96be\u9003\uff0c\u4e0d\u7981\u5bb3\u6015\u4e4b\u6781\uff0c\u521a\u52c7\u4e4b\u6c14\u4e00\u8870\uff0c\u518d\u65e0\u534a\u5206\u529b\u9053\u4e0e\u5bf9\u624b\u76f8\u6297\uff0c\u7a81\u7136\u95f4\u53cc\u819d\u8dea\u4e0b\uff0c\u53eb\u9053\uff1a\u201c\u8d75\u4e09\u7237\u9976\u547d\uff01\u201d \u3000\u3000\u8d75\u534a\u5c71\u8eab\u5728\u534a\u7a7a\uff0c\u5168\u51ed\u654c\u4eba\u7684\u529b\u6c14\u652f\u6301\uff0c\u9648\u79b9\u7a81\u7136\u5730\u6c14\u7aed\u8dea\u5012\uff0c\u4ed6\u8f7b\u8f7b\u5411\u540e\u4e00\u7eb5\uff0c\u4f38\u51fa\u53f3\u638c\uff0c\u559d\u9053\uff1a\u201d\u7559\u7740\u4f60\u8fd9\u5978\u5f92\u4f55\u7528\uff1f\u201d\u6b63\u8981\u4e00\u638c\u5411\u4ed6\u5929\u7075\u76d6\u51fb\u843d\uff0c\u5374\u89c1\u4ed6\u4ef0\u8138\u54c0\u6c42\uff0c\u6ee1\u9762\u60ca\u60e7\u4e4b\u8272\u3002 \u3000\u3000\u8d75\u534a\u5c71\u7d20\u6765\u5fc3\u80a0\u4ec1\u6148\uff0c\u7eb5\u9047\u7a77\u51f6\u6781\u6076\u7684\u795e\u597d\u5de8\u61a8\uff0c\u53ea\u8981\u4e0d\u662f\u6b63\u597d\u649e\u5230\u4ed6\u5728\u80e1\u4f5c\u975e\u4e3a\uff0c\u5e38\u8d77\u601c\u60af\u4e4b\u5fc3\uff0c\u64d2\u4f4f\u4e86\u53eb\u8bad\u4e00\u987f\uff0c\u5373\u884c\u91ca\u653e\uff0c\u4f7f\u4ed6\u65e5\u540e\u80fd\u591f\u6539\u8fc7\u8fc1\u5584\u3002\u6b64\u65f6\u9648\u79b9\u7b4b\u8109\u6563\u4e71\uff0c\u5168\u8eab\u6b66\u529f\u5df2\u5931\uff0c\u5df2\u4e0e\u5e9f\u4eba\u65e0\u5f02\uff0c\u5c31\u7b97\u4e0d\u80af\u75db\u6539\u524d\u975e\uff0c\u4e5f\u5df2\u4e0d\u80fd\u4f5c\u6076\uff0c\u773c\u89c1\u4ed6\u795e\u60c5\u53ef\u601c\uff0c\u4e00\u638c\u505c\u5728\u534a\u7a7a\u4e2d\u5374\u4e0d\u51fb\u4e0b\uff0c\u8f6c\u5934\u5411\u5b59\u521a\u5cf0\u9053\uff1a\u201c\u5b59\u5144\uff0c\u6b64\u4eba\u7684\u529f\u592b\u5df2\u7ecf\u5e9f\u4e86\uff0c\u51ed\u4f60\u5904\u7f6e\u5427\u3002\u53ea\u662f\u5c0f\u5f1f\u6c42\u4e00\u4e2a\u60c5\uff0c\u7559\u4ed6\u4e00\u6761\u6027\u547d\u3002\u201d \u3000\u3000\u5b59\u521a\u5cf0\u671b\u671b\u8d75\u534a\u5c71\uff0c\u53c8\u671b\u671b\u9648\u79b9\uff0c\u5fc3\u4e0b\u751a\u662f\u4e3a\u96be\uff0c\u5bfb\u601d\uff1a\u201c\u8fd9\u597d\u8d3c\u7f6a\u5927\u6076\u6781\uff0c\u6211\u62fc\u7740\u65a9\u65ad\u53cc\u624b\uff0c\u65b9\u80fd\u5c06\u4f60\u8bf7\u5230\uff0c\u600e\u53ef\u9976\u4ed6\uff1f\u4f46\u8fd9\u5978\u8d3c\u53c8\u662f\u7531\u4f60\u5236\u670d\uff0c\u4f60\u65e2\u51fa\u8a00\u7559\u4ed6\u6027\u547d\uff0c\u6211\u53c8\u600e\u80fd\u62d2\u5374\uff1f\u201d\u8f6c\u5934\u770b\u5415\u5c0f\u59b9\u65f6\uff0c\u53ea\u89c1\u5979\u53cc\u76ee\u4e2d\u55b7\u51fa\u6012\u706b\uff0c\u6068\u6068\u5730\u77aa\u7740\u9648\u79b9\uff0c\u767b\u65f6\u6709\u4e86\u4e3b\u610f\uff0c\u5f53\u5373\u6251\u7ffb\u8eab\u8eaf\uff0c\u5411\u8d75\u534a\u5c71\u4fbf\u62dc\uff0c\u8bf4\u9053\uff1a\u201c\u8d75\u4e09\u7237\uff0c\u4eca\u65e5\u4f60\u4e3a\u6211\u5317\u5b97\u6e05\u7406\u95e8\u6237\uff0c\u5b59\u67d0\u6c38\u611f\u5927\u5fb7\u3002\u201d\u8bf4\u7740\u8fde\u8fde\u78d5\u5934\u3002 \u3000\u3000\u8d75\u534a\u5c71\u5fd9\u4e5f\u8dea\u4e0b\u8fd8\u793c\uff0c\u8bf4\u9053\uff1a\u201c\u5b59\u5144\u4e0d\u5fc5\u591a\u793c\u3002\u8def\u89c1\u4e0d\u5e73\uff0c\u62d4\u5200\u76f8\u52a9\uff0c\u4e43\u662f\u6211\u4fa0\u4e49\u9053\u672c\u4efd\u4e4b\u4e8b\u3002\u4f55\u51b5\u4f60\u6211\u540c\u95e8\uff0c\u4f11\u621a\u76f8\u5173\uff0c\u4f55\u52b3\u8a00\u8c22\u3002\u201d\u53ea\u89c1\u5b59\u521a\u5cf0\u7ad9\u8d77\u8eab\u6765\uff0c\u53e3\u4e2d\u5374\u6a2a\u54ac\u7740\u660e\u6643\u6643\u7684\u4e00\u67c4\u5c16\u5200\u3002 \u3000\u3000\u8d75\u534a\u5c71\u7ad9\u76f4\u8eab\u5b50\uff0c\u7a81\u7136\u89c1\u5230\u5c16\u5200\uff0c\u4e0d\u7981\u4e00\u60ca\uff0c\u9000\u4e86\u4e00\u6b65\u3002 \u3000\u3000\u539f\u6765\u8fd9\u67c4\u5315\u9996\u662f\u9648\u79b9\u6240\u6709\uff0c\u4ed6\u672c\u6765\u7528\u4ee5\u6307\u4f4f\u5415\u5c0f\u59b9\uff0c\u5176\u540e\u80e1\u6590\u65bd\u5de7\u8ba1\u6551\u4eba\uff0c\u76f8\u6597\u4e4b\u9645\uff0c\u5c06\u5315\u9996\u593a\u4e0b\u63b7\u5728\u5730\u4e0a\u3002\u540e\u6765\u8d75\u534a\u5c71\u53e3\u6388\u62f3\u8bc0\uff0c\u4e00\u4ef6\u4e8b\u7d27\u8ddf\u7740\u4e00\u4ef6\uff0c\u9648\u79b9\u59cb\u7ec8\u65e0\u6687\u62fe\u56de\u5315\u9996\u3002\u5b59\u521a\u5cf0\u6ca1\u4e86\u53cc\u624b\uff0c\u5374\u4e58\u7740\u78d5\u5934\u4e4b\u65f6\uff0c\u7528\u53e3\u8854\u4e86\u8d77\u6765\u3002\u4ed6\u8e0f\u524d\u4e24\u6b65\u3001\u8d70\u5230\u5415\u5c0f\u59b9\u8eab\u524d\uff0c\u5f2f\u8170\u5c06\u5315\u9996\u9001\u4e86\u8fc7\u53bb\uff0c\u5415\u5c0f\u59b9\u4f38\u624b\u63e1\u4f4f\u5200\u67c4\uff0c\u76ee\u5149\u4e2d\u610f\u5b58\u8be2\u95ee\u3002 \u3000\u3000\u5b59\u521a\u5cf0\u677e\u5f00\u7259\u9f7f\uff0c\u8bf4\u9053\uff1a\u201c\u8d75\u4e09\u7237\uff0c\u4f60\u8bf4\u4ec0\u4e48\uff0c\u505a\u5144\u5f1f\u7684\u4e0d\u6562\u9a73\u56de\u534a\u53e5\u3002 \u3000\u3000\u4f46\u5415\u5c0f\u59b9\u7684\u7236\u4eb2\u662f\u7ed9\u8fd9\u5978\u8d3c\u6d3b\u6d3b\u6253\u6b7b\u7684\uff0c\u5979\u5144\u5f1f\u662f\u8fd9\u5978\u8d3c\u4eb2\u624b\u6740\u7684\u3002\u9976\u4e0d\u9976\u4eba\uff0c\u9664\u4e86\u5c0f\u59b9\u81ea\u5df1\uff0c\u5929\u4e0b\u518d\u65e0\u7b2c\u4e8c\u4e2a\u4eba\u505a\u5f97\u4e86\u4e3b\u3002\u8d75\u4e09\u7237\uff0c\u4f60\u8bf4\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u8d75\u534a\u5c71\u53f9\u53e3\u6c14\uff0c\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u5b59\u521a\u5cf0\u5411\u5415\u5c0f\u59b9\u5389\u58f0\u9053\uff1a\u201c\u5c0f\u59b9\uff0c\u4f60\u8981\u62a5\u4ec7\uff0c\u6709\u80c6\u5b50\u5c31\u5c06\u8fd9\u5978\u8d3c\u6740\u4e86\u3002 \u3000\u3000\u4f60\u82e5\u662f\u5fc3\u8f6f\u5bb3\u6015\uff0c\u5c31\u653e\u4ed6\u8d70\u4e86\u5427\uff01\u201d \u3000\u3000\u4f17\u4eba\u76ee\u5149\u4e00\u9f50\u6ce8\u89c6\u5728\u5415\u5c0f\u59b9\u8138\u4e0a\u3002\u6709\u7684\u5fc3\u60f3\u5979\u65e2\u6709\u575a\u5fd7\u6bc5\u529b\u8fdc\u8d74\u56de\u7586\u6c42\u63f4\uff0c\u590d\u4ec7\u4e4b\u5fc3\u6781\u4e3a\u575a\u51b3\uff0c\u81ea\u6709\u80c6\u91cf\u6740\u4eba\uff1b\u6709\u7684\u5374\u89c1\u3000\u5979\u7626\u5c0f\u602f\u5f31\uff0c\u63d0\u7740\u660e\u6643\u6643\u7684\u4e00\u67c4\u5c16\u5200\uff0c\u5168\u8eab\u5df2\u4e0d\u4f4f\u53d1\u6296\uff0c\u53ea\u6015\u672a\u5fc5\u6562\u53bb\u6740\u9648\u79b9\u8fd9\u957f\u5927\u6c49\u5b50\u3002 \u3000\u3000\u5415\u5c0f\u59b9\u8eab\u5b50\u6253\u6218\uff0c\u5fc3\u4e2d\u5374\u65e0\u534a\u5206\u8fdf\u7591\u4e4b\u610f\uff0c\u63d0\u7740\u5c16\u5200\uff0c\u5f84\u81ea\u8d70\u5411\u9648\u79b9\u3002 \u3000\u3000\u5979\u8eab\u9ad8\u8fd8\u4e0d\u5230\u9648\u79b9\u80f8\u53e3\uff0c\u5c16\u5200\u5411\u524d\u4e00\u9001\uff0c\u6b63\u597d\u523a\u5411\u4ed6\u7684\u5c0f\u8179\u3002 \u3000\u3000\u8fd9\u65f6\u9648\u79b9\u56db\u80a2\u9178\u9ebb\uff0c\u80fd\u591f\u76f4\u7acb\u4e0d\u5012\uff0c\u5df2\u662f\u4e07\u5206\u52c9\u5f3a\uff0c\u773c\u89c1\u5c0f\u59b9\u4e00\u5200\u523a\u6765\uff0c\u5927\u53eb\u4e00\u58f0\uff0c\u56de\u5934\u5c31\u8d70\u3002\u5415\u5c0f\u59b9\u867d\u66fe\u7ec3\u8fc7\u4e00\u4e9b\u62f3\u811a\uff0c\u7a76\u7adf\u6b66\u529f\u6781\u6d45\uff0c\u7ed9\u4ed6\u4e00\u7f29\u8eab\uff0c\u4e00\u5200\u767b\u65f6\u523a\u7a7a\uff0c\u5f53\u4e0b\u63d0\u7740\u5c16\u5200\uff0c\u968f\u540e\u8ffd\u53bb\u3002 \u3000\u3000\u9648\u79b9\u811a\u6b65\u7792\u73ca\uff0c\u5954\u5411\u5385\u95e8\uff0c\u7a81\u89c1\u5927\u5385\u4e4b\u95e8\u5df2\u4e8e\u4e0d\u77e5\u4f55\u65f6\u7d27\u95ed\uff0c\u6025\u5fd9\u4f38\u624b\u53bb\u63a8\uff0c\u54ea\u77e5\u5927\u95e8\u7adf\u7136\u5947\u70ed\uff0c\u55e4\u55e4\u51e0\u58f0\u54cd\uff0c\u5192\u51fa\u767d\u70df\uff0c\u4e24\u53ea\u624b\u638c\u5df2\u88ab\u5927\u95e8\u7c98\u4f4f\u3002 \u3000\u3000\u4ed6\u5927\u60ca\u4e4b\u4e0b\uff0c\u594b\u529b\u56de\u593a\uff0c\u53ea\u662f\u5168\u8eab\u52b2\u529b\u65e9\u5931\uff0c\u4e00\u4e2a\u8e09\u8dc4\uff0c\u8eab\u5b50\u53cd\u800c\u9760\u4e86\u4e0a\u53bb\uff0c\u7c98\u5728\u95e8\u4e0a\uff0c\u60e8\u547c\u4e00\u58f0\uff0c\u968f\u5373\u5168\u65e0\u58f0\u606f\u3002 \u3000\u3000\u8fd9\u4e00\u4e0b\u53d8\u6545\u53ef\u6ca1\u4e00\u4eba\u6599\u60f3\u5f97\u5230\u3002\u4f17\u4eba\u4e00\u5446\u4e4b\u4e0b\uff0c\u4e00\u9f50\u6d8c\u5230\u95e8\u524d\uff0c\u9f3b\u4e2d\u53ea\u95fb\u5230\u4e00\u9635\u7126\u81ed\uff0c\u539f\u6765\u90a3\u5385\u95e8\u7adf\u662f\u4e00\u6247\u6781\u539a\u7684\u94c1\u95e8\uff0c\u4e0d\u77e5\u662f\u8c01\u5728\u5916\u5df2\u5c06\u95e8\u70e7\u5f97\u70bd\u70ed\u3002\u9648\u79b9\u88ab\u7c98\u5728\u95e8\u4e0a\uff0c\u7247\u523b\u95f4\u5df2\u7136\u70eb\u6b7b\u3002 \u3000\u3000\u4f17\u4eba\u770b\u660e\u771f\u76f8\uff0c\u60ca\u8be7\u66f4\u751a\u3002\u738b\u5251\u82f1\u53eb\u9053\uff1a\u201c\u5f1f\u59b9\uff0c\u600e\u4e48\u4e00\u56de\u4e8b\uff1f\u201d\u5374\u4e0d\u542c\u89c1\u5546\u8001\u592a\u56de\u7b54\uff0c\u8f6c\u8eab\u5bfb\u4eba\u65f6\uff0c\u4e0d\u4f46\u5546\u8001\u592a\u6bcd\u5b50\u5f71\u8e2a\u4e0d\u89c1\uff0c\u8fde\u5385\u4e2d\u4f20\u9001\u9152\u83dc\u7684\u4ec6\u4eba\u4e5f\u5df2\u4e2a\u4e2a\u8eb2\u5f97\u4e0d\u77e5\u53bb\u5411\u3002\u738b\u5251\u82f1\u8138\u4e0a\u7a81\u7136\u906e\u4e0a\u4e00\u9053\u9634\u5f71\uff0c\u6025\u6b65\u8d70\u5411\u5185\u5802\uff0c\u53ea\u89c1\u901a\u5411\u5185\u5802\u4e4b\u95e8\u4e5f\u5df2\u7d27\u95ed\u3002\u90a3\u95e8\u6b63\u4e2d\u7ed8\u4e86\u4e00\u4e2a\u516b\u5366\uff0c\u4e4c\u6c89\u6c89\u7684\u4f3c\u4e4e\u4e5f\u662f\u94a2\u94c1\u6240\u94f8\u3002\u4ed6\u4e0d\u6562\u4f38\u624b\u53bb\u63a8\uff0c\u53ea\u8d70\u4e0a\u4e24\u6b65\uff0c\u767b\u89c9\u4e00\u80a1\u70ed\u6c14\u6251\u9762\u800c\u81f3\u3002\u539f\u6765\u540e\u95e8\u4e5f\u7ed9\u70e4\u70ed\u4e86\u3002 \u3000\u3000\u738b\u5251\u6770\u5927\u58f0\u53eb\u9053\uff1a\u201c\u5546\u5bb6\u5ac2\u5b50\uff0c\u4f60\u5728\u6363\u4ec0\u4e48\u9b3c\u554a\uff0c\u5feb\u4e9b\u51fa\u6765\uff01\u201d\u4ed6\u58f0\u97f3\u6d2a\u4eae\uff0c\u56db\u58c1\u56de\u97f3\u53cd\u9707\uff0c\u66f4\u52a0\u54cd\u4eae\u3002\u4f17\u4eba\u81ea\u7136\u800c\u7136\u5730\u62ac\u8d77\u5934\u6765\uff0c\u4f46\u89c1\u90a3\u5385\u7adf\u65e0\u4e00\u6247\u7a97\u5b50\uff0c\u524d\u540e\u94c1\u95e8\u4e00\u95ed\uff0c\u5173\u5f97\u5bc6\u4e0d\u901a\u98ce\uff0c\u8fde\u82cd\u8747\u4e5f\u98de\u4e0d\u51fa\u53bb\u3002 \u3000\u3000\u4f17\u4eba\u9762\u9762\u76f8\u89d1\uff0c\u8fd9\u624d\u7701\u609f\uff0c\u539f\u6765\u5546\u5bb6\u5821\u8fd9\u5ea7\u5927\u5385\u5efa\u9020\u4e4b\u65f6\u5df2\u662f\u522b\u5177\u7528\u5fc3\uff0c\u95e8\u7528\u94c1\u94f8\uff0c\u4e0d\u8bbe\u7a97\u6237\uff0c\u77a7\u6765\u5899\u58c1\u4e5f\u662f\u6781\u5176\u575a\u539a\uff0c\u975e\u94c1\u5373\u77f3\u4e86\u3002\u9a6c\u884c\u7a7a\u63d0\u8d77\u4e00\u6761\u957f\u51f3\uff0c\u53cc\u81c2\u8fd0\u52b2\uff0c\u201d\u563f\u201d\u7684\u4e00\u58f0\uff0c\u5f80\u5899\u4e0a\u649e\u53bb\uff0c\u957f\u51f3\u4ece\u4e2d\u65ad\u4e3a\u4e24\u622a\uff0c\u5899\u4e0a\u767d\u7c89\u7c41\u7c41\u843d\u4e0b\u51e0\u5757\uff0c\u9732\u51fa\u5185\u91cc\u7684\u82b1\u5c97\u77f3\u6765\u3002 \u3000\u3000\u738b\u5251\u82f1\u6446\u4e2a\u9a6c\u6b65\uff0c\u8fd0\u52b2\u4e8e\u638c\uff0c\u53cc\u638c\u5411\u5899\u58c1\u6392\u51fb\u8fc7\u53bb\u3002\u4ee5\u4ed6\u8fd9\u4e00\u51fb\u4e4b\u529b\uff0c\u5bfb\u5e38\u5899\u58c1\u7eb5\u4e0d\u6d1e\u7a7f\uff0c\u4e5f\u8981\u6253\u5f97\u571f\u5d29\u7816\u88c2\uff0c\u4f46\u8fd9\u5899\u58c1\u663e\u662f\u4ee5\u6781\u539a\u6781\u91cd\u7684\u5ca9\u77f3\u780c\u6210\uff0c\u5728\u738b\u5251\u82f1\u53cc\u638c\u5e76\u51fb\u4e4b\u4e0b\uff0c\u5374\u662f\u7eb9\u4e1d\u4e0d\u52a8\u3002 \u3000\u3000\u738b\u5251\u6770\u5fc3\u8c0e\u610f\u4e71\uff0c\u4e0d\u6ce8\u53eb\u56b7\uff1a\u201c\u5546\u5bb6\u5ac2\u5b50\uff0c\u4f60\u5e72\u4ec0\u4e48\uff1f\u5feb\u5f00\u95e8\uff01\u5feb\u5f00\u95e8\uff01\u201d \u3000\u3000\u8d75\u534a\u5c71\u6c89\u6ce8\u4e86\u6c14\uff0c\u6b32\u5bfb\u51fa\u8def\uff0c\u4f46\u60f3\uff1a\u201c\u8fd9\u5927\u5385\u5982\u6b64\u5efa\u9020\uff0c\u672c\u610f\u5c31\u8981\u5bb3\u4eba\uff0c\u5c4b\u9876\u4e0a\u4e5f\u5fc5\u5e03\u7f6e\u4e25\u5bc6\uff0c\u51b2\u4e0d\u51fa\u53bb\u3002\u201d \u3000\u3000\u738b\u5251\u6770\u53eb\u4e86\u51e0\u58f0\uff0c\u5fc3\u4e2d\u5bb3\u6015\u8d77\u6765\uff0c\u4f4f\u53e3\u4e0d\u53eb\u4e86\uff0c\u671b\u7740\u5144\u957f\uff0c\u6ca1\u534a\u70b9\u4e3b\u610f\u3002 \u3000\u3000\u8fd9\u65f6\u5385\u4e2d\u7559\u7740\u7684\u662f\u8d75\u534a\u5c71\u3001\u80e1\u6590\u3001\u5b59\u521a\u5cf0\u3001\u5415\u5c0f\u59b9\u3001\u738b\u6c0f\u5144\u5f1f\u3001\u9a6c\u884c\u7a7a\u3001\u5f90\u6323\u3001\u6bb7\u4ef2\u7fd4\uff0c\u4e00\u5171\u4e5d\u4eba\uff0c\u8fd8\u52a0\u9648\u79b9\u4e00\u5177\u5c38\u4f53\u3002\u9664\u4e86\u5415\u5c0f\u59b9\u5916\uff0c\u5176\u4f59\u516b\u4eba\u90fd\u7b97\u5f97\u662f\u6b66\u6797\u597d\u624b\uff0c\u4f46\u56f0\u5728\u8fd9\u5ea7\u94c1\u94f8\u77f3\u780c\u7684\u5385\u4e2d\uff0c\u7a7a\u6709\u5168\u8eab\u6b66\u529f\uff0c\u5374\u65e0\u534a\u70b9\u65bd\u5c55\u4e4b\u6cd5\uff0c\u4e00\u65f6\u4f60\u671b\u6211\uff0c\u6211\u671b\u4f60\uff0c\u4e0d\u77e5\u5982\u4f55\u662f\u597d\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u4e00\u4e2a\u9634\u4fa7\u4fa7\u7684\u58f0\u97f3\u7740\u5730\u4f20\u6765\uff1a\u201c\u4f60\u4eec\u81ea\u547d\u82f1\u96c4\u597d\u6c49\uff0c\u4eca\u65e5\u60f3\u9003\u51fa\u6211\u5546\u5bb6\u5821\u7684\u94c1\u5385\uff0c\u90a3\u53eb\u505a\u5343\u96be\u4e07\u96be\u3002\u8fd9\u94c1\u5385\u662f\u5148\u592b\u5546\u5251\u9e23\u4eb2\u624b\u6240\u5efa\uff0c\u4ed6\u867d\u6b7b\u53bb\u591a\u5e74\uff0c\u8fd8\u80fd\u5236\u4f60\u4eec\u7684\u6b7b\u547d\u3002\u4f17\u4f4d\u5927\u82f1\u96c4\uff0c\u4f60\u4eec\u53ef\u670d\u4e86\u4e48\uff1f\u201d\u8bf4\u7740\u54c8\u54c8\u5927\u7b11\u3002 \u3000\u3000\u4f17\u4eba\u542c\u5f97\u6bdb\u9aa8\u609a\u7136\uff0c\u4e0d\u5bd2\u800c\u6817\u3002\u5bfb\u58f0\u671b\u53bb\uff0c\u539f\u6765\u5546\u8001\u592a\u8fd9\u756a\u8bdd\u662f\u4ece\u5899\u811a\u8fb9\u4e00\u4e2a\u72d7\u6d1e\u4e2d\u4f20\u8fdb\u6765\u7684\u3002 \u3000\u3000\u738b\u5251\u82f1\u4fef\u4e0b\u8eab\u6765\uff0c\u5bf9\u7740\u72d7\u6d1e\u53eb\u9053\uff1a\u201c\u5f1f\u59b9\uff0c\u6211\u5144\u5f1f\u4e0e\u5251\u9e23\u5e08\u5f1f\u540c\u95e8\u5171\u5e08\uff0c\u6709\u6069\u65e0\u4ec7\u3002\u4f60\u628a\u54b1\u5144\u5f1f\u4e5f\u5173\u5728\u8fd9\u91cc\uff0c\u90a3\u7b97\u600e\u4e48\u4e00\u56de\u4e8b\uff1f\u201d\u5546\u8001\u592a\u53c8\u662f\u9634\u4fa7\u4fa7\u5730\u7b11\u4e86\u51e0\u4e0b\u3002\u72d7\u6d1e\u4e2d\u4f20\u8fdb\u6765\u67f4\u706b\u7206\u88c2\u65f6\u7684\u6bd5\u535c\u4e4b\u58f0\uff0c\u663e\u662f\u5916\u9762\u706b\u5934\u70e7\u5f97\u6781\u731b\u3002 \u3000\u3000\u53ea\u542c\u5546\u8001\u592a\u67af\u54d1\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u5251\u9e23\u4e0d\u5e78\u4e3a\u597d\u5978\u80e1\u4e00\u5200\u6240\u5bb3\uff0c\u4f60\u65e2\u4e0e\u4ed6\u6709\u540c\u95e8\u4e4b\u8c0a\uff0c\u5c31\u8be5\u8bbe\u6cd5\u62a5\u4ec7\u3002\u4eca\u65e5\u9047\u4e0a\u4ec7\u4eba\u4e4b\u5b50\uff0c\u4f60\u5144\u5f1f\u4fe9\u5374\u6015\u4e86\u5916\u4eba\uff0c\u8896\u624b\u4e0d\u987e\uff0c\u8fd9\u7b49\u4e0d\u4ec1\u4e0d\u4e49\u4e4b\u4eba\uff0c\u6d3b\u5728\u4e16\u4e0a\u4f55\u7528\uff1f\u201d\u738b\u5251\u82f1\u9053\uff1a\u201c\u5251\u9e23\u5e08\u5f1f\u7684\u6b7b\u8baf\uff0c\u6211\u4eec\u4eca\u65e5\u624d\u542c\u5230\uff0c\u66f4\u4e0d\u77e5\u662f\u80e1\u4e00\u5200\u6240\u5bb3\u7684\u3002\u82e5\u662f\u65e9\u77e5\uff0c\u81ea\u7136\u5df2\u4e3a\u4ed6\u62a5\u4e86\u5927\u4ec7\u3002\u201d\u5546\u8001\u592a\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u6627\u4e86\u826f\u5fc3\uff0c\u8bf4\u8fd9\u7b49\u9b3c\u8bdd\u3002\u201d\u738b\u5251\u82f1\u8bf4\u9053\uff1a\u201c\u521a\u624d\u6211\u624b\u4e0a\u53d7\u4f24\u4e2d\u6bd2\uff0c\u4e0d\u4e5f\u662f\u4e3a\u4e86\u2026\u2026\u4e3a\u4e86\u2026\u2026\u201d\u4e00\u8a00\u672a\u6bd5\uff0c\u53ea\u542c\u98d5\u7684\u4e00\u58f0\uff0c\u72d7\u6d1e\u4e2d\u5c04\u8fdb\u4e00\u6280\u7bad\u6765\uff0c\u82e5\u975e\u738b\u5251\u6770\u773c\u5feb\uff0c\u62a2\u4e0a\u4e00\u6b65\u8e0f\u4f4f\uff0c\u4f0f\u5728\u5730\u4e0b\u7684\u738b\u5251\u82f1\u8fd8\u5f97\u4e2d\u7bad\u53d7\u4f24\u3002 \u3000\u3000\u6bb7\u4ef2\u7fd4\u81ea\u957f\u5251\u88ab\u8d75\u534a\u5c71\u9707\u65ad\u540e\uff0c\u4e00\u76f4\u9ed8\u4e0d\u4f5c\u58f0\uff0c\u5fc3\u60f3\u81ea\u5df1\u4e0e\u6b64\u4e8b\u5168\u7136\u65e0\u6d89\uff0c\u5374\u5728\u8fd9\u91cc\u966a\u7740\u9001\u547d\uff0c\u4e5f\u53ef\u7b97\u5f97\u6781\u51a4\uff0c\u95ee\u9053\uff1a\u201c\u5546\u5251\u9e23\u9020\u8fd9\u5ea7\u94c1\u5385\uff0c\u60f3\u5bb3\u4ec0\u4e48\u4eba\uff1f\u201d\u738b\u5251\u82f1\u6012\u9053\uff1a\u201c\u8fd9\u4eba\u8ddf\u5148\u7236\u5b66\u827a\u4e4b\u65f6\uff0c\u4e3a\u4eba\u5c31\u4e0d\u6b63\u6d3e\uff0c\u9b3c\u9b3c\u795f\u795f\u5730\u9020\u8d77\u8fd9\u79cd\u623f\u5c4b\uff0c\u8fd8\u80fd\u5b89\u4ec0\u4e48\u597d\u5fc3\u773c\u4e86\uff1f\u201d \u3000\u3000\u80e1\u6590\u5fc3\u60f3\uff1a\u201c\u90a3\u5546\u5251\u9e23\u6253\u4e0d\u8fc7\u6211\u7239\u7239\uff0c\u4e8e\u662f\u9020\u4e86\u8fd9\u5ea7\u94c1\u5385\u60f3\u6765\u5bb3\u4ed6\uff0c\u54ea\u77e5\u8fd9\u4e2a\u8113\u5305\u8fd8\u662f\u6b7b\u5728\u6211\u7239\u7239\u4e0b\u91cc\u3002\u201d\u4ed6\u5fc3\u4e2d\u60f3\u5230\uff0c\u53e3\u91cc\u5374\u4e0d\u8bf4\u8bdd\uff0c\u56db\u4e0b\u5bdf\u770b\uff0c\u627e\u5bfb\u8131\u8eab\u4e4b\u8ba1\u3002 \u3000\u3000\u80e1\u6590\u7684\u63a8\u60f3\u5374\u4e5f\u9519\u4e86\u3002\u5546\u5251\u9e23\u4e0e\u80e1\u4e00\u5200\u7d20\u4e0d\u76f8\u8bc6\uff0c\u4ed6\u662f\u4e0e\u82d7\u4eba\u51e4\u7ed3\u4e0b\u4e86\u6df1\u4ec7\uff0c\u77e5\u9053\u8fd9\u4f4d\u53f7\u79f0\u201c\u6253\u904d\u5929\u4e0b\u65e0\u654c\u624b\u201d\u7684\u91d1\u9762\u4f5b\u6781\u4e0d\u597d\u60f9\uff0c\u603b\u6709\u4e00\u65e5\u8981\u627e\u4e0a\u95e8\u6765\uff0c\u82e5\u662f\u6bd4\u6b66\u4e0d\u80dc\uff0c\u5c31\u53ee\u7528\u8fd9\u94c1\u5385\u5236\u4ed6\u3002\u54ea\u77e5\u627e\u4e0a\u95e8\u6765\u7684\u4e0d\u662f\u82d7\u4eba\u98ce\u800c\u662f\u80e1\u4e00\u5200\u3002\u5546\u5251\u9e23\u4e00\u5411\u81ea\u8d1f\uff0c\u5168\u4e0d\u5c06\u80e1\u4e00\u5200\u653e\u5728\u773c\u91cc\uff0c\u4e00\u6218\u4e4b\u4e0b\uff0c\u4e0d\u53ca\u4f7f\u7528\u94c1\u5385\uff0c\u9996\u7ea7\u5df2\u88ab\u5272\u53bb\u3002 \u3000\u3000\u8fd9\u6bb5\u4ec7\u6068\u5546\u8001\u592a\u65f6\u523b\u5728\u5fc3\uff0c\u65e2\u77e5\u80e1\u4e00\u5200\u5df2\u6b7b\u800c\u4ed6\u7684\u513f\u4e86\u80e1\u6590\u6b66\u529f\u53c8\u6781\u662f\u5389\u5bb3\uff0c\u773c\u89c1\u5927\u4ec7\u96be\u590d\uff0c\u4e58\u7740\u8d75\u534a\u5c71\u4e0e\u9648\u79b9\u76f8\u6597\u3001\u4f17\u4eba\u51dd\u795e\u89c2\u6218\u4e4b\u9645\uff0c\u5979\u6084\u6084\u4e0e\u513f\u5b50\u51fa\u5385\uff0c\u6084\u6084\u5173\u4e0a\u4e86\u524d\u540e\u94c1\u95e8\uff0c\u7136\u540e\u6307\u6325\u5bb6\u4e01\uff0c\u5806\u67f4\u711a\u70e7\u3002\u8fd9\u5ea7\u94c1\u5385\u95e8\u575a\u5899\u539a\uff0c\u5916\u9762\u70e7\u706b\uff0c\u5385\u4e2d\u5404\u4eba\u7ade\u672a\u77e5\u89c9\uff0c\u5f85\u5f97\u9648\u79b9\u70e7\u6b7b\u5728\u94c1\u95e8\u4e4b\u4e0a\uff0c\u5404\u4eba\u5df2\u5982\u7b3c\u4e2d\u4e4b\u9e1f\uff0c\u63d2\u7fc5\u51c6\u98de\u4e86\u3002 \u3000\u3000\u4f17\u4eba\u5728\u5385\u4e2d\u7ed5\u8d70\u508d\u60f6\uff0c\u597d\u5728\u90a3\u5385\u6781\u5927\uff0c\u94c1\u95e8\u867d\u7136\u70e7\u7ea2\uff0c\u70ed\u6c14\u8fd8\u53ef\u5fcd\u8010\u3002 \u3000\u3000\u8d75\u534a\u5c71\u9053\uff1a\u201c\u54b1\u4eec\u603b\u4e0d\u80fd\u5728\u8fd9\u513f\u751f\u751f\u56f0\u6b7b\uff0c\u5927\u4f19\u513f\u9f50\u5fc3\u5408\u529b\uff0c\u6398\u4e00\u6761\u5730\u9053\u51fa\u53bb\u3002\u201d\u6bb7\u4ef2\u7fd4\u76b1\u7709\u9053\uff1a\u201c\u6b64\u5904\u53c8\u65e0\u94c1\u94f2\u9504\u5934\uff0c\u5f85\u5f97\u6398\u51fa\uff0c\u4eba\u90fd\u70e4\u719f\u4e86\u3002\u201d\u5f90\u6323\u4e00\u76f4\u62c5\u5fc3\u672a\u5a5a\u59bb\u5b50\u9a6c\u6625\u82b1\u9694\u5728\u5385\u5916\uff0c\u4e0d\u77e5\u6709\u4f55\u51f6\u9669\uff0c\u4ed6\u662f\u4e2a\u83bd\u592b\uff0c\u7a7a\u81ea\u7126\u6025\uff0c\u60f3\u4e0d\u51fa\u534a\u70b9\u6cd5\u5b50\uff0c\u8fd9\u65f6\u542c\u8d75\u534a\u5c71\u8bf4\u5230\u6398\u5730\u9053\uff0c\u5927\u58f0\u9053\uff1a\u201c\u8d75\u4e09\u7237\u8bf4\u5f97\u5bf9\uff0c\u603b\u662f\u80dc\u8fc7\u675f\u624b\u5f85\u6bd9\u3002\u201d\u62d4\u51fa\u5355\u5200\uff0c\u5c06\u5730\u4e0b\u7684\u4e00\u5757\u5927\u9752\u7816\u6316\u8d77\uff0c\u7a81\u89c1\u4e00\u80a1\u70ed\u6c14\u5192\u5c06\u4e0a\u6765\u3002 \u3000\u3000\u4ed6\u5413\u4e86\u4e00\u8df3\uff0c\u4f38\u5200\u5728\u70ed\u6c14\u4e0a\u5347\u5904\u4e00\u51fb\uff0c\u53ea\u542c\u5f53\u7684\u4e00\u54cd\uff0c\u7adf\u662f\u91d1\u94c1\u649e\u51fb\u4e4b\u58f0\u3002\u4f17\u4eba\u66f4\u662f\u60ca\u8be7\u3002\u738b\u5251\u6770\u9053\uff1a\u201c\u5730\u5e95\u4e5f\u662f\u94c1\u94f8\u7684\uff1f\u201d\u7528\u5200\u63a5\u8fde\u64ac\u8d77\u51e0\u5757\u9752\u7816\uff0c\u679c\u7136\u4e0b\u9762\u8fde\u6210\u4e00\u7247\uff0c\u6574\u4e2a\u5385\u5e95\u4e43\u662f\u4e00\u5757\u5927\u94a2\u94c1\u3002\u6398\u5730\u9053\u56fa\u7136\u4e0d\u7528\u8bf4\u4e86\uff0c\u66f4\u552c\u4eba\u7684\u662f\uff0c\u5730\u9762\u4e0a\u7684\u70ed\u6c14\u8d8a\u5192\u8d8a\u65fa\u3002 \u3000\u3000\u5f90\u6323\u9a82\u9053\uff1a\u201c\u5988\u5df4\u7f94\u5b50\uff0c\u8fd9\u8001\u8654\u5a46\u5728\u5730\u5e95\u4e0b\u751f\u706b\uff0c\u8fd9\u5385\u5b50\u539f\u6765\u662f\u4e00\u53ea\u5927\u94c1\u9992\u3002\u201d\u80e1\u6590\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8001\u5a46\u5b50\u8981\u628a\u54b1\u4eec\u4e5d\u4e2a\u4eba\u716e\u719f\u6765\u5403\u4e86\u3002\u201d \u3000\u3000\u4f17\u4eba\u773c\u89c1\u70ed\u6c14\u8885\u8885\u4e0a\u5192\uff0c\u65e0\u4e0d\u5fc3\u60ca\u3002\u8fc7\u5f97\u7247\u523b\uff0c\u5934\u9876\u4e5f\u89c1\u5230\u4e86\u70ed\u6c14\uff0c\u539f\u6765\u5385\u9876\u4e5f\u662f\u94c1\u677f\uff0c\u4e0a\u9762\u663e\u7136\u4e5f\u5806\u4e86\u67f4\u70ad\uff0c\u6b63\u5728\u711a\u70e7\u3002 \u3000\u3000\u738b\u5251\u82f1\u7a81\u7136\u53c8\u4f0f\u5728\u72d7\u6d1e\u4e4b\u524d\uff0c\u53eb\u9053\uff1a\u201c\u5546\u5bb6\u5f1f\u59b9\uff0c\u4f60\u653e\u6211\u4eec\u51fa\u6765\uff0c\u6211\u5144\u5f1f\u4e3a\u4f60\u53d6\u90a3\u59d3\u80e1\u7684\u5c0f\u6742\u79cd\u6027\u547d\u3002\u201d\u80e1\u6590\u542c\u4ed6\u51fa\u8a00\u4e0d\u900a\uff0c\u63d0\u8d77\u811a\u6765\u5f80\u4ed6\u5c41\u80a1\u4e0a\u8e22\u53bb\u3002\u8d75\u534a\u5c71\u62c9\u4f4f\u4ed6\u624b\u81c2\u5411\u540e\u4e00\u626f\uff0c\u8fd9\u4e00\u8e22\u767b\u65f6\u843d\u7a7a\u3002\u8d75\u534a\u5c71\u4f4e\u58f0\u9053\uff1a\u201c\u8fd9\u91cc\u5927\u4f19\u513f\u987b\u5f97\u540c\u821f\u5171\u6d4e\uff0c\u81ea\u5df1\u4eba\u83ab\u5435\uff0c\u987b\u5f97\u5148\u60f3\u6cd5\u5b50\u51fa\u53bb\u3002\u201d\u5fc3\u60f3\uff1a\u201c\u53ea\u8981\u5546\u8001\u592a\u80af\u653e\u738b\u6c0f\u5144\u5f1f\uff0c\u4fbf\u6709\u8131\u8eab\u4e4b\u673a\u3002\u201d \u3000\u3000\u5374\u542c\u5546\u8001\u592a\u8bf4\u9053\uff1a\u201c\u5c0f\u6742\u79cd\u7684\u6027\u547d\u65e9\u5df2\u5728\u6211\u624b\u4e2d\uff0c\u4f55\u5fc5\u8981\u4f60\u5047\u60fa\u60fa\u76f8\u52a9\uff1f \u3000\u3000\u518d\u8fc7\u534a\u4e2a\u65f6\u8fb0\uff0c\u4f60\u4eec\u4eba\u4eba\u90fd\u5316\u6210\u7126\u70ad\u3002\u54c8\u54c8\uff0c\u8fd9\u91cc\u9762\u6ca1\u4e00\u4e2a\u662f\u597d\u4eba\u3002\u59d3\u80e1\u7684\u5c0f\u6742\u79cd\uff0c\u9a6c\u8001\u5934\u5b50\uff0c\u5385\u4e0a\u597d\u98ce\u51c9\u5427\uff1f\u201d \u3000\u3000\u9a6c\u884c\u7a7a\u76b1\u7709\u4e0d\u7b54\uff0c\u5546\u8001\u592a\u53c8\u67ad\u557c\u822c\u7b11\u4e86\u51e0\u58f0\uff0c\u53eb\u9053\uff1a\u201c\u9a6c\u8001\u5934\u5b50\uff0c\u4f60\u7684\u5973\u513f\u6211\u4f1a\u597d\u597d\u7167\u6599\u5979\uff0c\u4f60\u653e\u5fc3\uff0c\u6211\u7ed9\u5979\u627e\u4e00\u5343\u4e2a\u4e00\u4e07\u4e2a\u597d\u5973\u5a7f\u3002\u201d\u9a6c\u884c\u7a7a\u5fc3\u5982\u5200\u5272\uff0c\u4ed6\u5e74\u7eaa\u5df2\u5927\uff0c\u5bf9\u81ea\u5df1\u6027\u547d\u5012\u4e0d\u600e\u4e48\u987e\u60dc\uff0c\u53ea\u662f\u72ec\u751f\u7231\u5973\u5374\u843d\u5728\u5916\u9762\uff0c\u53d7\u8fd9\u6076\u6bd2\u7684\u8001\u5a46\u5b50\u6298\u78e8\u8d77\u6765\uff0c\u90a3\u53ef\u662f\u82e6\u4e0d\u582a\u8a00\u3002 \u3000\u3000\u738b\u5251\u82f1\u7ad9\u8d77\u8eab\u6765\uff0c\u5728\u5144\u5f1f\u8033\u8fb9\u8bf4\u4e86\u51e0\u53e5\u8bdd\uff0c\u738b\u5251\u6770\u70b9\u4e86\u70b9\u5934\u3002\u738b\u5251\u82f1\u5411\u8d75\u534a\u5c71\u62f1\u4e86\u62f1\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u8d75\u4e09\u7237\uff0c\u54b1\u4eec\u540c\u5728\u96be\u4e2d\uff0c\u5144\u5f1f\u53ef\u6709\u53e5\u4e0d\u4e2d\u542c\u7684\u8a00\u8bed\u3002\u201d\u8d75\u534a\u5c71\u62c9\u7740\u80e1\u6590\u7684\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u4e00\u5207\u5168\u51ed\u738b\u5927\u54e5\u5429\u5490\u3002\u53ef\u662f\u8981\u4f38\u624b\u52a0\u5bb3\u8fd9\u5c0f\u5144\u5f1f\uff0c\u5374\u529e\u4e0d\u5230\u3002\u201d\u539f\u6765\u8d75\u534a\u5c71\u89c1\u738b\u6c0f\u5144\u5f1f\u4ea4\u5934\u63a5\u8033\uff0c\u5df2\u77e5\u4e8c\u4eba\u4e3a\u4e86\u6d3b\u547d\uff0c\u60f3\u5148\u6740\u80e1\u6590\uff0c\u518d\u5411\u5546\u8001\u592a\u6c42\u60c5\u3002 \u3000\u3000\u738b\u5251\u82f1\u88ab\u4ed6\u4e00\u8a00\u70b9\u7834\u4e86\u5fc3\u4e8b\uff0c\u8138\u5e26\u6740\u6c14\uff0c\u5389\u58f0\u9053\uff1a\u201c\u8d75\u4e09\u7237\uff0c\u5546\u8001\u592a\u7684\u5bf9\u5934\u53ea\u6709\u8fd9\u5b69\u5b50\u4e00\u4eba\u3002\u51a4\u6709\u5934\uff0c\u503a\u6709\u4e3b\uff01\u5927\u4f19\u513f\u72af\u4e0d\u7740\u4e00\u9f50\u966a\u4e00\u4e2a\u5b69\u5b50\u505a\u9b3c\u3002\u201d \u3000\u3000\u4ed6\u5411\u4f17\u4eba\u9010\u4e00\u671b\u53bb\uff0c\u8bf4\u9053\uff1a\u201c\u5404\u4f4d\u8bf4\u51a4\u662f\u4e0d\u51a4\uff1f\u201d\u6bb7\u4ef2\u7fd4\u7acb\u5373\u63a5\u53e3\uff1a\u201c\u9664\u4e86\u8fd9\u5b69\u5b50\uff0c\u5927\u4f19\u513f\u8ddf\u8fd9\u4ef6\u4e8b\u5168\u6ca1\u7275\u8fde\u3002\u201d\u738b\u5251\u82f1\u9053\uff1a\u201c\u9a6c\u8001\u9556\u5934\uff0c\u4f60\u600e\u4e48\u8bf4\uff1f\u201d \u3000\u3000\u9a6c\u884c\u7a7a\u81ea\u5fd6\u5546\u8001\u592a\u4e0e\u5df1\u6709\u4ec7\uff0c\u672a\u5fc5\u80fd\u653e\u8fc7\u81ea\u5df1\u5e08\u5f92\uff0c\u4f46\u773c\u524d\u60c5\u52bf\u5371\u6025\u5f02\u5e38\uff0c\u53ea\u6709\u8bbe\u6cd5\u8131\u8eab\u5148\u8bf4\uff0c\u80e1\u6590\u662f\u6b7b\u662f\u6d3b\uff0c\u539f\u4e5f\u4e0d\u653e\u5728\u5fc3\u4e0a\uff0c\u4e8e\u662f\u8bf4\u9053\uff1a\u201c\u738b\u5927\u7237\u8bf4\u5f97\u662f\uff0c\u6b64\u4e8b\u539f\u4e0e\u65c1\u4eba\u65e0\u6d89\u3002\u201d \u3000\u3000\u738b\u5251\u82f1\u9053\uff1a\u201c\u5b59\u5927\u54e5\uff0c\u4f60\u6765\u8d76\u8fd9\u8e5a\u6d51\u6c34\uff0c\u90a3\u66f4\u662f\u72af\u4e0d\u7740\u3002\u59d3\u9648\u7684\u5df2\u7ecf\u70e7\u6b7b\uff0c\u4f60\u4e0e\u5415\u5bb6\u5c0f\u59b9\u59b9\u7684\u4ec7\u5df2\u7ecf\u62a5\u4e86\u3002\u201d\u5b59\u521a\u5cf0\u89c9\u5f97\u4ed6\u7684\u8bdd\u5f88\u6709\u7406\uff0c\u53ea\u662f\u5fc3\u4e2d\u6781\u611f\u8d75\u534a\u5c71\u4e4b\u60c5\uff0c\u5b9e\u4e0d\u4fbf\u516c\u7136\u4e0e\u4ed6\u4f5c\u5bf9\uff0c\u4e8e\u662f\u529d\u9053\uff1a\u201c\u8d75\u4e09\u7237\uff0c\u4e0d\u662f\u5144\u5f1f\u4e0d\u987e\u4e49\u6c14\uff0c\u5018\u662f\u4f60\u8d75\u4e09\u7237\u2026\u2026\u201d \u3000\u3000\u8d75\u534a\u5c71\u5389\u58f0\u559d\u9053\uff1a\u201c\u4f60\u4eec\u6709\u516d\u4e2a\uff0c\u6211\u4eec\u53ea\u6709\u4e24\u4eba\u3002\u54b1\u4eec\u5012\u5148\u77a7\u77a7\uff0c\u662f\u59d3\u8d75\u59d3\u80e1\u7684\u5148\u6b7b\u5462\uff0c\u8fd8\u662f\u4f60\u4eec\u59d3\u738b\u59d3\u6bb7\u7684\u5148\u6b7b\u3002\u201d\u8bf4\u7740\u6321\u5728\u80e1\u6590\u8eab\u524d\uff0c\u795e\u5a01\u51db\u51db\u3002\u4ed6\u5e73\u65f6\u9762\u76ee\u6148\u7965\uff0c\u8bf4\u8bdd\u6e29\u548c\uff0c\u5fc3\u80a0\u53c8\u662f\u6781\u8f6f\uff0c\u53ef\u662f\u9762\u4e34\u751f\u6b7b\u5173\u5934\uff0c\u201c\u4ec1\u4fa0\u201d\u4e8c\u5b57\u5374\u662f\u987e\u5f97\u6781\u7d27\uff0c\u8fd9\u51e0\u53e5\u8bdd\u8bf4\u5f97\u65a9\u9489\u622a\u94c1\uff0c\u7adf\u4e0d\u7559\u534a\u5206\u4f59\u5730\u3002 \u3000\u3000\u738b\u6c0f\u5144\u5f1f\u7b49\u4e00\u6765\u5fcc\u4ed6\u6b66\u529f\u4e86\u5f97\uff0c\u4e8c\u6765\u53c8\u89c9\u81ea\u5df1\u8d2a\u751f\u6015\u6b7b\uff0c\u8ff9\u8fd1\u65e0\u4e49\u5c0f\u4eba\uff0c\u5012\u4e5f\u4e0d\u6562\u4e00\u62e5\u800c\u4e0a\u52a8\u624b\u3002\u4f46\u4e00\u4e2a\u4eba\u5230\u4e86\u751f\u6b7b\u4e4b\u9645\uff0c\u9762\u76ee\u5168\u9732\uff0c\u5b9e\u662f\u534a\u70b9\u5047\u501f\u4e0d\u5f97\u3002\u5404\u4eba\u53ea\u89c9\u811a\u5e95\u8d8a\u6765\u8d8a\u662f\u70bd\u70ed\uff0c\u518d\u4e5f\u7ad9\u7acb\u4e0d\u4f4f\uff0c\u90fd\u62d6\u4e86\u4e00\u5f20\u957f\u51f3\u6216\u662f\u6905\u5b50\uff0c\u8e0f\u5728\u4e0a\u9762\u3002\u738b\u5251\u6770\u516b\u5366\u5200\u4e00\u626c\uff0c\u53eb\u9053\uff1a\u201c\u8d75\u4e09\u7237\uff0c\u5144\u5f1f\u4eca\u65e5\u8981\u5f97\u7f6a\u4e86\u3002\u201d \u3000\u3000\u5de6\u624b\u5411\u6bb7\u4ef2\u7fd4\u3001\u9a6c\u884c\u7a7a\u3001\u5f90\u94ee\u4e00\u62db\u624b\uff0c\u559d\u9053\uff1a\u201c\u5e76\u80a9\u5b50\u4e0a\u554a\uff01\u201d\u4ed6\u77e5\u5b59\u521a\u5cf0\u51b3\u4e0d\u80fd\u76f8\u52a9\u81ea\u5df1\u4e0e\u8d75\u534a\u5c71\u4e3a\u654c\uff0c\u4f46\u5df1\u65b9\u4e94\u4eba\u654c\u4ed6\u4e00\u8001\u4e00\u5c0f\uff0c\u4e5f\u5927\u6709\u53ef\u80dc\u4e4b\u673a\u3002 \u3000\u3000\u5404\u4eba\u5175\u5203\u7eb7\u7eb7\u51fa\u624b\uff0c\u53ea\u5f85\u8d75\u534a\u5c71\u8eab\u5b50\u4e00\u52a8\uff0c\u4e94\u4eba\u7684\u5200\u5251\u8981\u540c\u65f6\u780d\u523a\u51fa\u53bb\u3002 \u3000\u3000\u8fd9\u4e00\u756a\u53ea\u8981\u52a8\u4e0a\u4e86\u624b\uff0c\u90a3\u662f\u4eba\u4eba\u62fc\u547d\uff0c\u773c\u89c1\u5385\u4e2d\u8d8a\u6765\u8d8a\u70ed\uff0c\u591a\u6328\u4e00\u523b\uff0c\u4fbf\u662f\u591a\u4e00\u5206\u5371\u9669\u3002 \u3000\u3000\u80e1\u6590\u5fc3\u4e2d\u5374\u60f3\uff1a\u201c\u53ea\u662f\u4e3a\u6211\u4e00\u4eba\uff0c\u5374\u966a\u4e0a\u8fd9\u51e0\u4e2a\u4eba\u3002\u738b\u6c0f\u5144\u5f1f\u7b49\u6b7b\u4e0d\u8db3\u60dc\uff0c\u8d75\u4e09\u7237\u662f\u5927\u5927\u7684\u82f1\u96c4\u597d\u6c49\uff0c\u5982\u4f55\u80fd\u8ba9\u4ed6\u4e3a\u6211\u800c\u6b7b\uff1f\u8fd9\u51e0\u4eba\u62e5\u5c06\u8fc7\u6765\uff0c\u7eb5\u7136\u8d75\u4e09\u7237\u548c\u6211\u5c06\u4ed6\u4eec\u6740\u4e86\uff0c\u6211\u4eec\u4ecd\u662f\u96be\u9003\u6027\u547d\u3002\u77a7\u6765\u53ea\u6709\u6211\u81ea\u5df1\u6b7b\u5728\u5546\u8001\u592a\u624b\u91cc\uff0c\u624d\u80fd\u6551\u5f97\u8d75\u4e09\u7237\u7684\u6027\u547d\u3002\u201d\u773c\u89c1\u738b\u6c0f\u5144\u5f1f\u8dc3\u8dc3\u6b32\u52a8\uff0c\u53ea\u662f\u65e0\u4e00\u4eba\u6562\u5148\u53d1\u96be\uff0c\u5f53\u4e0b\u5fc3\u5ff5\u5df2\u51b3\uff0c\u6717\u58f0\u9053\uff1a\u201c\u5927\u5bb6\u4e14\u83ab\u52a8\u624b\u3002\u201d\u4e00\u4fef\u8eab\uff0c\u5c06\u5934\u94bb\u51fa\u72d7\u6d1e\uff0c\u53eb\u9053\uff1a\u201c\u5546\u8001\u592a\uff0c\u6211\u5728\u8fd9\u91cc\u4e0d\u52a8\uff0c\u4f60\u4e00\u9556\u6253\u6b7b\u6211\u5427\uff01\u5feb\u5f00\u95e8\u653e\u8d75\u4e09\u7237\u51fa\u6765\u3002\u201d \u3000\u3000\u5546\u8001\u592a\u4ef0\u5929\u5927\u7b11\uff0c\u4ece\u6000\u4e2d\u638f\u51fa\u91d1\u9556\uff0c\u53eb\u9053\uff1a\u201c\u5251\u9e23\uff0c\u5251\u9e23\uff0c\u4eca\u65e5\u6211\u7ed9\u4f60\u4eb2\u624b\u62a5\u4ec7\uff01\u201d\u53f3\u624b\u4e00\u626c\uff0c\u4e00\u679a\u5582\u6709\u5267\u6bd2\u7684\u91d1\u9556\u5bf9\u51c6\u80e1\u6590\u7684\u9762\u95e8\u6025\u5c04\u8fc7\u53bb\u3002 \u3000\u3000\u80e1\u6590\u773c\u89c1\u91d1\u5149\u95ea\u52a8\uff0c\u91d1\u9556\u5411\u7740\u81ea\u5df1\u7709\u5fc3\u6025\u5c04\u8fc7\u6765\uff0c\u53cc\u76ee\u4e00\u95ed\uff0c\u5fc3\u60f3\uff1a\u201c\u5546\u8001\u592a\u5c06\u6211\u6253\u6b7b\uff0c\u9042\u4e86\u5fc3\u613f\u3002\u5979\u4e0e\u8d75\u4f2f\u4f2f\u65e0\u4ec7\uff0c\u81ea\u4f1a\u653e\u4ed6\u51fa\u6765\u3002\u201d\u5c31\u5728\u6b64\u65f6\uff0c\u7a81\u89c9\u53f3\u8db3\u88ab\u4eba\u4e00\u626f\uff0c\u8eab\u5b50\u5411\u540e\u6fc0\u5c04\u3002\u4ed6\u7741\u5f00\u773c\u6765\uff0c\u8eab\u5b50\u5df2\u5728\u534a\u7a7a\uff0c\u5f53\u5373\u5de6\u81c2\u957f\u51fa\uff0c\u5728\u67f1\u4e0a\u4e00\u62b9\uff0c\u8f7b\u8f7b\u843d\u4e0b\u5730\u6765\uff0c\u53ea\u89c1\u8d75\u534a\u5c71\u624b\u4e2d\u63a5\u4e86\u4e00\u679d\u91d1\u9556\uff0c\u539f\u6765\u53c8\u662f\u4ed6\u6551\u4e86\u81ea\u5df1\u6027\u547d\u3002 \u3000\u3000\u738b\u5251\u82f1\u773c\u89c1\u80e1\u6590\u820d\u8eab\u6551\u4eba\uff0c\u8d75\u534a\u5c71\u7adf\u4ece\u4e2d\u963b\u6320\uff0c\u4e0d\u7981\u5927\u6012\uff0c\u53eb\u9053\uff1a\u201c\u59d3\u8d75\u7684\uff0c\u5927\u4e08\u592b\u6069\u6028\u5206\u660e\uff0c\u6b64\u4e8b\u539f\u672c\u4e0e\u4f60\u6211\u65e0\u5e72\u3002\u4ed6\u65e2\u81ea\u613f\u5c31\u6b7b\uff0c\u53c8\u8981\u4f60\u6a2a\u52a0\u63d2\u624b\u5e72\u4e48\uff1f\u201d \u3000\u3000\u8d75\u534a\u5c71\u5fae\u7b11\u4e0d\u7b54\uff0c\u8f6c\u5934\u5411\u80e1\u6590\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u9002\u624d\u4f60\u8111\u888b\u94bb\u51fa\u4e86\u72d7\u6d1e\u4e4b\u5916\uff0c\u662f\u4e48\uff1f\u201d\u80e1\u6590\u9053\uff1a\u201c\u662f\u554a\u3002\u201d\u89c1\u4ed6\u795e\u60c5\u9547\u5b9a\uff0c\u7b11\u5bb9\u53ef\u63ac\uff0c\u4f3c\u4e4e\u5df2\u6709\u4e86\u8131\u8eab\u4e4b\u8ba1\uff0c\u8bf4\u9053\uff1a\u201c\u8d75\u4f2f\u4f2f\uff0c\u8bf7\u4f60\u5429\u5490\u3002\u201d\u8d75\u534a\u5c71\u9053\uff1a\u201c\u8111\u888b\u662f\u786c\u7684\uff0c\u65e0\u6cd5\u7f29\u5c0f\uff0c\u80a9\u8180\u4e0e\u8eab\u5b50\u5374\u662f\u8f6f\u7684\u3002\u201d\u80e1\u6590\u7acb\u65f6\u9886\u609f\uff0c\u53eb\u9053\uff1a\u201c\u662f\u4e86\uff0c\u8111\u888b\u65e2\u94bb\u5f97\u51fa\uff0c\u8eab\u5b50\u4fbf\u4e5f\u94bb\u5f97\u51fa\u3002\u201d\u5f53\u5373\u8131\u4e0b\u68c9\u8884\uff0c\u88f9\u6210\u4e00\u56e2\uff0c\u9876\u5728\u5934\u4e0a\uff0c\u4e00\u6765\u662f\u6613\u4e8e\u94bb\u51fa\uff0c\u4e8c\u6765\u662f\u62b5\u6321\u5546\u8001\u5927\u7684\u5582\u6bd2\u91d1\u9556\u3002 \u3000\u3000\u8d75\u534a\u5c71\u9053\uff1a\u201c\u4f60\u4e14\u9000\u540e\uff0c\u6211\u7ed9\u4f60\u5f00\u8def\u3002\u201d\u5f90\u94ee\u53eb\u9053\uff1a\u201c\u4e0d\u884c\uff0c\u4f60\u8fd9\u4e48\u80a5\u80d6\uff0c\u600e\u94bb\u5f97\u51fa\u53bb\uff1f\u201d\u8d75\u534a\u5c71\u54c8\u54c8\u4e00\u7b11\uff0c\u4e0d\u53bb\u7406\u4ed6\uff0c\u4fef\u4e0b\u8eab\u5b50\uff0c\u53f3\u624b\u4e00\u626c\uff0c\u4e00\u679a\u8896\u7bad\u4ece\u72d7\u6d1e\u4e2d\u6fc0\u5c04\u800c\u51fa\uff0c\u53ea\u542c\u5916\u9762\u4e00\u540d\u5e84\u4e01\u5927\u58f0\u547c\u75db\uff0c\u53eb\u9053\uff1a\u201c\u811a\uff0c\u811a\uff0c\u6211\u7684\u811a\uff01\u201d\u663e\u662f\u4ed6\u7684\u811a\u7ed9\u8896\u7bad\u6253\u4e2d\u4e86\u3002\u8d75\u534a\u5c71\u5de6\u624b\u5fae\u52a8\uff0c\u53c8\u5c06\u5546\u8001\u592a\u7684\u91d1\u9556\u53d1\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u8fd9\u4e00\u6b21\u5916\u9762\u5374\u65e0\u52a8\u9759\uff0c\u60f3\u662f\u5404\u4eba\u5747\u5df2\u907f\u5f00\u3002\u6709\u4eba\u53eb\u9053\uff1a\u201c\u5feb\uff0c\u5feb\u628a\u72d7\u6d1e\u5835\u6b7b\u3002\u201d\u5546\u8001\u592a\u559d\u9053\uff1a\u201c\u4e0d\u8bb8\u52a8\uff0c\u6211\u8981\u542c\u4ed6\u4eec\u70eb\u6b7b\u65f6\u7684\u547c\u53eb\u3002\u5927\u5bb6\u907f\u5728\u4e00\u65c1\u4fbf\u662f\uff0c\u6697\u5668\u80fd\u62d0\u5f2f\u4e48\uff1f\u201d\u8d75\u534a\u5c71\u53cc\u624b\u8fde\u626c\uff0c\u5341\u4f59\u679a\u6697\u5668\u63a5\u8fde\u5c04\u51fa\uff0c\u53bb\u52bf\u52b2\u6025\u5f02\u5e38\uff0c\u90fd\u5c04\u51fa\u5341\u4e08\u4ee5\u5916\u3002 \u3000\u3000\u53d1\u5230\u5c06\u8fd1\u4e8c\u5341\u679a\uff0c\u4ed6\u5de6\u624b\u5728\u80e1\u6590\u80cc\u540e\u8f7b\u8f7b\u4e00\u63a8\u3002\u80e1\u6590\u5411\u524d\u4e00\u6251\uff0c\u5149\u5c06\u68c9\u8884\u9001\u4e86\u51fa\u53bb\u3002\u5546\u8001\u592a\u65e9\u5df2\u9632\u5230\u8fd9\u7740\uff0c\u706b\u5149\u4e0b\u89c1\u9ed1\u9edd\u9edd\u7684\u4e00\u56e2\u4ece\u72d7\u6d1e\u4e2d\u94bb\u51fa\uff0c\u7d2b\u91d1\u516b\u5366\u5200\u547c\u7684\u4e00\u5200\u780d\u5c06\u4e0b\u6765\uff0c\u6b63\u4e2d\u68c9\u8884\uff0c\u4f46\u89c9\u7740\u5200\u4e4b\u5904\u8f6f\u7ef5\u7ef5\u5730\uff0c\u5fc3\u77e5\u4e0d\u5bf9\uff0c\u6025\u5fd9\u63d0\u5200\u3002\u80e1\u6590\u53f3\u624b\u5148\u51fa\uff0c\u624b\u638c\u4e00\u7ffb\uff0c\u5df2\u6293\u4f4f\u5979\u624b\u8155\uff0c\u8ddf\u7740\u8111\u888b\u4ece\u72d7\u6d1e\u4e2d\u94bb\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u5546\u8001\u592a\u5927\u53eb\u4e00\u58f0\u3002\u5546\u5b9d\u9707\u7eb5\u4e86\u8fc7\u6765\uff0c\u4e00\u5200\u5411\u7740\u80e1\u6590\u5934\u9876\u780d\u843d\uff0c\u6b64\u65f6\u80e1\u6590\u7684\u80a9\u5934\u4e5f\u5df2\u8131\u51fa\u72d7\u6d1e\uff0c\u53ea\u662f\u90a3\u72d7\u6d1e\u6781\u4e3a\u72ed\u5c0f\uff0c\u631f\u4f4f\u4ed6\u80f8\u53e3\u4e0e\u5de6\u624b\uff0c\u4e00\u65f6\u7a9c\u4e0d\u51fa\u6765\uff0c\u53ea\u5f97\u501f\u52b2\u5c06\u5546\u8001\u592a\u7684\u624b\u8155\u6325\u53bb\uff0c\u5f53\u7684\u4e00\u54cd\uff0c\u6bcd\u5b50\u4fe9\u53cc\u5200\u76f8\u4ea4\u3002\u8fd9\u4e00\u4e0b\u624b\u6cd5\uff0c\u6b63\u662f\u8d75\u534a\u5c71\u9002\u624d\u6240\u6388\u7684\u501f\u529b\u6253\u529b\u529f\u592b\uff0c\u4e5f\u662f\u4ed6\u806a\u660e\u8fc7\u4eba\uff0c\u4e00\u5b66\u5373\u80fd\u4f7f\u7528\uff0c\u5426\u5219\u975e\u4e27\u547d\u4e8e\u5546\u5b9d\u9707\u5200\u4e0b\u4e0d\u53ef\u3002 \u3000\u3000\u8d75\u534a\u5c71\u542c\u5230\u53cc\u5200\u76f8\u4ea4\u4e4b\u58f0\uff0c\u5374\u89c1\u80e1\u6590\u8eab\u5b50\u5c1a\u672a\u94bb\u51fa\uff0c\u8fd0\u8d77\u592a\u6781\u67d4\u52b2\uff0c\u5728\u4ed6\u5927\u817f\u4e0a\u4e00\u63a8\u3002\u80e1\u6590\u8eab\u4e0d\u7531\u4e3b\uff0c\u817e\u7a7a\u800c\u8d77\u3002\u6b63\u597d\u5546\u5b9d\u9707\u7b2c\u4e8c\u5200\u590d\u53c8\u780d\u4e0b\uff0c\u8fd9\u4e00\u5200\u52b2\u529b\u597d\u5927\uff0c\u6b63\u780d\u5728\u5899\u57fa\u7684\u82b1\u5c97\u77f3\u4e0a\uff0c\u706b\u661f\u56db\u6e85\uff0c\u5200\u53e3\u4e5f\u5377\u4e86\u8d77\u6765\u3002\u80e1\u6590\u5728\u7a7a\u4e2d\u6253\u4e86\u4e2a\u65cb\u5b50\uff0c\u706b\u5149\u4e2d\u89c1\u5546\u8001\u592a\u6a2a\u5200\u5411\u81ea\u5df1\u8db3\u4e0a\u524a\u6765\uff0c\u6025\u4f7f\u4e2a\u201c\u5343\u65a4\u5760\u201d\uff0c\u8eab\u5b50\u9aa4\u843d\uff0c\u53ea\u542c\u5f97\u547c\u7684\u4e00\u58f0\uff0c\u516b\u5366\u5200\u4ece\u5934\u9876\u63a0\u8fc7\u3002\u4ed6\u8db3\u672a\u843d\u5730\uff0c\u5de6\u638c\u7ffb\u8d77\uff0c\u4ee5\u7a7a\u624b\u5165\u767d\u5203\u529f\u592b\u53bb\u593a\u5546\u8001\u592a\u624b\u4e2d\u91d1\u5200\u3002 \u3000\u3000\u5546\u8001\u592a\u89c1\u4ec7\u4eba\u5c45\u7136\u6b7b\u91cc\u9003\u751f\uff0c\u773c\u90e8\u7ea2\u4e86\uff0c\u516b\u5366\u5200\u76f4\u4e0a\u76f4\u4e0b\uff0c\u72c2\u6240\u731b\u5288\uff0c\u80e1\u6590\u7a7a\u624b\u62a2\u653b\u6570\u62db\uff0c\u7ade\u662f\u4e1d\u6beb\u5360\u4e0d\u5230\u4fbf\u5b9c\uff0c\u4f46\u542c\u5f97\u4f17\u5e84\u4e01\u5927\u58f0\u5450\u558a\uff0c\u70df\u706b\u91cc\u5546\u5b9d\u9707\u63d0\u5200\u53c8\u4e0a\u3002\u80e1\u6590\u5fc3\u60f3\u6b64\u65f6\u5385\u4e0a\u5df2\u70e7\u5f97\u70bd\u70ed\u5f02\u5e38\uff0c\u65f6\u5019\u7a0d\u957f\uff0c\u8d75\u534a\u5c71\u7b49\u6027\u547d\u96be\u4fdd\uff0c\u5385\u4e0a\u516b\u6761\u4eba\u547d\uff0c\u5168\u51ed\u81ea\u5df1\u80fd\u5426\u4e8e\u6781\u77ed\u65f6\u523b\u4e4b\u5185\u51fb\u8d25\u5546\u6c0f\u6bcd\u5b50\u3001\u6740\u6563\u5e84\u4e01\u800c\u6253\u5f00\u5385\u95e8\u3002\u4ed6\u5fc3\u4e2d\u7126\u6025\uff0c\u4e00\u53cc\u8089\u638c\u5728\u4e24\u67c4\u5927\u5200\u4e4b\u95f4\u7a7f\u6765\u63d2\u53bb\uff0c\u72e0\u547d\u76f8\u6251\u3002\u5546\u6c0f\u6bcd\u5b50\u4e5f\u77e5\u8fd9\u4e00\u6218\u4e43\u662f\u751f\u6b7b\u5b58\u4ea1\u4e4b\u6240\u7cfb\uff0c\u53cc\u5200\u547c\u547c\uff0c\u5c31\u5982\u4e24\u5934\u5927\u866b\u822c\u7ed5\u7740\u80e1\u6590\u56f4\u653b\u3002 \u3000\u3000\u5927\u5385\u4e2d\u8d75\u534a\u5c71\u3001\u738b\u6c0f\u5144\u5f1f\u7b49\u516b\u4eba\u4e00\u9f50\u4fef\u8033\u72d7\u6d1e\u4e4b\u65c1\uff0c\u503e\u542c\u80e1\u6590\u4e0e\u5546\u6c0f\u6bcd\u5b50\u76f8\u6597\u7684\u80dc\u8d25\u3002\u738b\u6c0f\u5144\u5f1f\u867d\u5bf9\u80e1\u6590\u9887\u4e3a\u618e\u6068\uff0c\u4f46\u6b64\u65f6\u5374\u4e0e\u8d75\u534a\u5c71\u7684\u5fc3\u601d\u5e76\u65e0\u4e8c\u81f4\uff0c\u53ea\u76fc\u80e1\u6590\u5feb\u4e9b\u6740\u8d25\u5546\u6c0f\u6bcd\u5b50\u3002\u5385\u4e0a\u70ed\u6c14\u8d8a\u6765\u8d8a\u662f\u96be\u71ac\uff0c\u684c\u6905\u5fc5\u5265\u4f5c\u54cd\uff0c\u8721\u70db\u9047\u70ed\u7194\u5c3d\uff0c\u767b\u65f6\u9ed1\u6f06\u4e00\u56e2\u3002\u7a81\u7136\u706b\u5149\u4e00\u65fa\uff0c\u5374\u662f\u5899\u58c1\u4e0a\u6302\u7740\u7684\u5c4f\u6761\u5b57\u753b\u9047\u70ed\u71c3\u70e7\uff0c\u4f46\u7247\u523b\u70e7\u5c3d\uff0c\u53c8\u662f\u4f38\u624b\u4e0d\u89c1\u4e94\u6307\uff0c\u518d\u8fc7\u4e0d\u4e45\uff0c\u53ea\u6015\u684c\u6905\u4e5f\u8981\u70e7\u7740\u4e86\u201d\u3002 \u3000\u3000\u4f17\u4eba\u5fc3\u4e2d\u6025\u5f97\u4e5f\u5982\u70c8\u706b\u711a\u70e7\uff0c\u5374\u662f\u51c6\u4e5f\u4e0d\u51fa\u58f0\uff0c\u51dd\u795e\u503e\u542c\u5916\u9762\u4e09\u4eba\u76f8\u6597\u7684\u58f0\u97f3\u3002 \u3000\u3000\u738b\u5251\u82f1\u7a81\u7136\u5728\u6d1e\u53e3\u53eb\u9053\uff1a\u201c\u80e1\u5bb6\u5c0f\u5144\u5f1f\uff0c\u5feb\u653b\u5546\u8001\u592a\u4e0b\u76d8\u3002\u5979\u8fd9\u8def\u5200\u6cd5\u4e0b\u4e09\u8def\u4e0d\u7a33\u3002\u201d\u4ed6\u5728\u516b\u5366\u5200\u4e0a\u6d78\u6deb\u6570\u5341\u5e74\uff0c\u542c\u7740\u5200\u98ce\u7684\u58f0\u97f3\uff0c\u4fbf\u77e5\u5979\u5982\u4f55\u4f7f\u5200\u3002 \u3000\u3000\u80e1\u6590\u6b63\u82e6\u4e8e\u4e00\u65f6\u4e0d\u80fd\u53d6\u80dc\uff0c\u542c\u5f97\u738b\u5251\u82f1\u7684\u53eb\u58f0\uff0c\u5fc3\u4e2d\u5927\u559c\uff0c\u8eab\u5b50\u4e00\u5f13\uff0c\u4f38\u62f3\u5f80\u5546\u8001\u592a\u817f\u4e0a\u51fb\u53bb\u3002\u5546\u8001\u592a\u7adf\u7136\u4e0d\u907f\uff0c\u4e3e\u5200\u5f80\u4ed6\u80cc\u5fc3\u76f4\u5288\uff0c\u5979\u53ea\u6c42\u4f24\u654c\uff0c\u5df2\u7136\u4e0d\u987e\u81ea\u8eab\u3002\u80e1\u6590\u626d\u8170\u4fa7\u8eab\uff0c\u8ba9\u5f00\u4e86\u8fd9\u4e00\u5200\uff0c\u5546\u8001\u592a\u7b2c\u4e8c\u5200\u8fde\u7ef5\u800c\u4e0a\u3002\u5979\u660e\u542c\u5f97\u738b\u5251\u82f1\u53eb\u654c\u4eba\u653b\u51fb\u81ea\u5df1\u4e0b\u76d8\uff0c\u5374\u504f\u504f\u4e0d\u53bb\u5b88\u5fa1\u3002\u738b\u5251\u82f1\u5927\u53eb\uff1a\u201c\u5979\u662f\u5728\u60c5\u6025\u62fc\u547d\uff0c\u4f60\u593a\u4e0d\u4e0b\u5979\u91d1\u5200\u7684\u3002\u5feb\u60f3\u522b\u6cd5\u5427\u3002\u201d\u80e1\u6590\u5fc3\u60f3\uff1a\u201c\u8fd9\u4e2a\u6211\u65e9\u77e5\u9053\uff0c\u4f55\u5fc5\u4f60\u6765\u63d0\u9192\u3001\u9047\u5230\u8fd9\u6837\u4e00\u4e2a\u75af\u5a46\u5b50\uff0c\u6709\u4ec0\u4e48\u6cd5\u5b50\uff1f\u201d \u3000\u3000\u72d7\u6d1e\u4e4b\u5916\u6218\u6597\u6fc0\u70c8\uff0c\u80e1\u6590\u4ee5\u4e00\u654c\u4e8c\uff0c\u6e10\u6e10\u5360\u5230\u4e0a\u98ce\uff0c\u4f46\u8981\u53d6\u80dc\uff0c\u53ea\u6015\u8fd8\u5728\u767e\u4f59\u56de\u5408\u4e4b\u540e\u3002\u5546\u8001\u592a\u77a7\u51fa\u60c5\u52bf\u4e0d\u5229\uff0c\u53c8\u542c\u5f97\u738b\u5251\u82f1\u4e0d\u4f4f\u53eb\u56b7\u6307\u70b9\u654c\u4eba\uff0c\u5c06\u7834\u89e3\u516b\u5366\u5200\u7684\u8bc0\u7a8d\uff0c\u4e00\u70b9\u4e00\u70b9\u4ed6\u8bf4\u4e86\u51fa\u6765\uff0c\u5fc3\u4e2d\u607c\u6012\u5f02\u5e38\uff0c\u6697\u9053\uff1a\u201c\u4f60\u4e0d\u7ed9\u540c\u95e8\u5e08\u5f1f\u62a5\u4ec7\uff0c\u5df2\u662f\u5927\u5927\u4e0d\u8be5\uff0c\u5374\u53cd\u800c\u6765\u76f8\u52a9\u654c\u4eba\uff0c\u5f53\u771f\u662f\u72fc\u5fc3\u72d7\u80ba\u7684\u5978\u8d3c\u3002\u201d\u5979\u5374\u4e0d\u60f3\u738b\u5251\u82f1\u8eab\u5904\u7edd\u5883\uff0c\u82e5\u4e0d\u53cd\u52a9\u80e1\u6590\uff0c\u6027\u547d\u5df2\u6d3b\u4e0d\u8fc7\u4e00\u65f6\u4e09\u523b\u3002 \u3000\u3000\u5979\u72c2\u6012\u4e4b\u4e0b\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u5c0f\u6742\u79cd\u6b66\u827a\u9ad8\u5f3a\uff0c\u65e2\u7136\u9003\u4e86\u51fa\u6765\uff0c\u53ea\u6015\u96be\u4ee5\u6740\u4ed6\u3002 \u3000\u3000\u90a3\u4e48\u70e7\u6b7b\u4e86\u5385\u4e2d\u8fd9\u6279\u5978\u4eba\uff0c\u4e5f\u7a0d\u51fa\u6211\u5fc3\u4e2d\u6076\u6c14\u3002\u201d\u4e8e\u662f\u5927\u58f0\u547c\u559d\u5e84\u4e01\uff0c\u6025\u901f\u591a\u52a0\u67f4\u70ad\u711a\u70e7\u3002 \u3000\u3000\u6bb7\u4ef2\u7fd4\u4e0d\u6ce8\u8dcc\u811a\uff0c\u57cb\u6028\u80e1\u6590\u65e0\u7528\u3002\u738b\u5251\u6770\u9053\uff1a\u201c\u8d75\u4e09\u7237\uff0c\u5feb\u53d1\u6697\u5668\u76f8\u52a9\u3002\u201d \u3000\u3000\u8d75\u534a\u5c71\u624b\u4e2d\u65e9\u6263\u4e86\u5341\u4f59\u679a\u6697\u5668\uff0c\u4f46\u5546\u8001\u4eba\u7b49\u4e09\u4eba\u5728\u72d7\u6d1e\u4e4b\u65c1\u6076\u6597\uff0c\u6697\u5668\u65e0\u6cd5\u62d0\u5f2f\u3002\u4ed6\u7684\u98de\u71d5\u94f6\u68ad\u7b49\u51e0\u79cd\u72ec\u95e8\u6697\u5668\u867d\u80fd\u7ed5\u6210\u5f27\u5f62\u4f24\u4eba\uff0c\u4f46\u80e1\u6590\u4e0e\u5546\u6c0f\u6bcd\u5b50\u77ed\u5175\u76f8\u63a5\uff0c\u8d34\u8eab\u800c\u6218\uff0c\u77a7\u4e0d\u89c1\u51c6\u5934\u800c\u51ed\u865a\u53d1\u5c04\u51fa\u53bb\u3001\u600e\u80fd\u4fdd\u5f97\u5b9a\u4e0d\u4f1a\u6253\u4e2d\u80e1\u6590\uff1f\u5c0f\u80e1\u6590\u5fc3\u601d\u673a\u654f\uff0c\u65e9\u5df2\u60f3\u5230\u8fd9\u8282\uff0c\u6570\u6b21\u8981\u5f15\u5546\u8001\u592a\u5230\u72d7\u6d1e\u4e4b\u5916\u3002\u53ef\u662f\u5546\u8001\u4eba\u5fcc\u60ee\u8d75\u534a\u5c71\u6697\u5668\u4e86\u5f97\uff0c\u59cb\u7ec8\u4e0d\u4e0a\u8fd9\u5f53\u3002 \u3000\u3000\u8fd9\u65f6\u5385\u4e0a\u7126\u81ed\u6e10\u6d53\uff0c\u5148\u662f\u5404\u4eba\u7684\u5934\u53d1\u80e1\u5b50\u9b08\u66f2\u70e7\u7126\uff0c\u63a5\u7740\u8863\u670d\u8fb9\u7f18\u90fd\u5377\u4e86\u8d77\u6765\uff0c\u5404\u4eba\u547c\u5438\u4e5f\u6e10\u611f\u8270\u96be\u3002\u5415\u5c0f\u59b9\u62b5\u53d7\u4e0d\u4f4f\u7099\u70ed\uff0c\u4eba\u5df2\u534a\u6655\u3002\u5f90\u94ee\u60c5\u6025\u4e4b\u4e0b\uff0c\u4f38\u5934\u62fc\u547d\u5411\u72d7\u6d1e\u786c\u6324\uff0c\u4f46\u6d1e\u5c0f\u5934\u4eba\uff0c\u5982\u4f55\u94bb\u5f97\u51fa\u53bb\uff1f\u90a3\u72d7\u6d1e\u56db\u89d2\u5747\u662f\u6781\u539a\u6781\u91cd\u7684\u82b1\u5c97\u77f3\uff0c\u4ed6\u53cc\u624b\u6273\u4f4f\u7528\u529b\u6447\u64bc\uff0c\u7adf\u662f\u52a8\u4e0d\u4e86\u534a\u5206\u3002 \u3000\u3000\u738b\u5251\u6770\u731b\u5730\u60f3\u8d77\uff1a\u201c\u5c0f\u80e1\u6590\u82e5\u6709\u5175\u5203\uff0c\u5546\u8001\u592a\u5c82\u662f\u4ed6\u7684\u654c\u624b\uff1f\u6211\u5982\u4f55\u4e0d\u65f1\u60f3\u5230\uff1f\u201d\u5f53\u5373\u4f38\u4e0b\u53bb\u62fe\u81ea\u5df1\u629b\u5728\u5730\u4e0b\u7684\u516b\u5366\u5200\u3002\u54ea\u77e5\u8fd9\u67c4\u5200\u7684\u5200\u5934\u4e0e\u5730\u4e0b\u94c1\u677f\u78b0\u5230\uff0c\u65e9\u5df2\u70e4\u5f97\u7099\u70ed\u65e0\u6bd4\uff0c\u4ed6\u4e00\u6293\u4e4b\u4e0b\uff0c\u767b\u65f6\u75bc\u5f97\u5927\u53eb\u4e00\u58f0\u3002\u8fd9\u65f6\u5728\u94c1\u5385\u4e0a\u7247\u523b\u4e5f\u5ef6\u6328\u4e0d\u5f97\uff0c\u4ed6\u5fcd\u7740\u624b\u4e0a\u70eb\u4f24\uff0c\u6495\u4e0b\u4e00\u5757\u8863\u895f\uff0c\u88f9\u5728\u5200\u67c4\u4e4b\u4e0a\uff0c\u5de6\u4e0b\u5c06\u5f90\u94ee\u62c9\u5f00\uff0c\u53eb\u9053\uff1a\u201c\u5c0f\u80e1\u6590\uff0c\u5175\u5200\u6765\u4e86\uff0c\u5feb\u63a5\u7740\u3002\u201d\u624b\u4e00\u6325\uff0c\u5c06\u94a2\u5200\u4ece\u72d7\u6d1e\u4e2d\u629b\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u80e1\u6590\u56de\u8eab\u6765\u63a5\uff0c\u5546\u5b9d\u9707\u4e5f\u542c\u5230\u4e86\u53eb\u58f0\uff0c\u540c\u65f6\u8fc7\u6765\u62a2\u593a\u3002\u53ea\u542c\u5f97\u4e24\u4eba\u540c\u65f6\u60ca\u547c\u4e00\u58f0\uff0c\u545b\u5577\u4e00\u54cd\uff0c\u4e24\u67c4\u5200\u90e8\u8dcc\u5728\u5730\u4e0b\u3002 \u3000\u3000\u539f\u6765\u80e1\u6590\u62a2\u5148\u6293\u5230\u738b\u5251\u6770\u7684\u5355\u5200\uff0c\u4f46\u5200\u67c4\u5947\u70ed\uff0c\u4e00\u5b64\u7acb\u5373\u6492\u4e0b\u3002\u5546\u5b9d\u9707\u8dc3\u5230\u72d7\u6d1e\u4e4b\u524d\uff0c\u5374\u7ed9\u8d75\u534a\u5c71\u4e00\u6280\u91d1\u94b1\u9556\u6253\u4e2d\u624b\u8155\uff0c\u624b\u4e2d\u94a2\u5200\u4e5f\u629b\u4e86\u4e0b\u6765\u3002\u80e1\u6590\u4e00\u6293\u4e0d\u4e2d\uff0c\u5546\u8001\u5927\u7684\u516b\u5366\u5200\u5df2\u88ad\u5230\u540e\u5fc3\uff0c\u4ed6\u8eab\u5b50\u4e00\u4fa7\uff0c\u62a2\u5230\u5546\u5b9d\u9707\u8eab\u65c1\uff0c\u731b\u5730\u4f7f\u4e00\u62db\u201c\u6380\u725b\u559d\u6c34\u201d\uff0c\u4e3e\u638c\u6380\u4f4f\u4ed6\u540e\u9888\uff0c\u4e00\u8fd0\u52b2\uff0c\u5546\u5b9d\u9707\u7ed9\u4ed6\u76f4\u6380\u4e0b\u53bb\uff0c\u9762\u988a\u4fef\u5730\uff0c\u6b63\u597d\u78b0\u5230\u738b\u5251\u6770\u90a3\u67c4\u70e7\u5f97\u534a\u7ea2\u7684\u5355\u5200\uff0c\u55e4\u7684\u4e00\u58f0\uff0c\u8ddf\u7740\u4e00\u58f0\u60e8\u547c\uff0c\u534a\u8fb9\u4fca\u4fcf\u7684\u8138\u5e9e\u4e0a\u5df2\u70eb\u51fa\u4e00\u6761\u957f\u957f\u7684\u7126\u75d5\u3002 \u3000\u3000\u8fd9\u4e00\u58f0\u60e8\u53eb\uff0c\u5385\u4e0a\u5404\u4eba\u90fd\u662f\u4e00\u559c\uff0c\u53ea\u9053\u5546\u5b9d\u9707\u5df2\u88ab\u80e1\u6590\u6253\u4f24\u3002\u5546\u8001\u592a\u590d\u4ec7\u4e4b\u5fc3\u4e0e\u6bcd\u5b50\u4e4b\u60c5\u5728\u80f8\u4e2d\u7565\u4e00\u4ea4\u6218\uff0c\u7adf\u5c14\u4e0d\u987e\u513f\u5b50\uff0c\u4e3e\u5200\u6025\u5f80\u80e1\u6590\u80a9\u5934\u5288\u4e0b\u3002 \u3000\u3000\u5f53\u7684\u4e00\u58f0\uff0c\u80e1\u6590\u5374\u4e0d\u95ea\u907f\uff0c\u7ffb\u8155\u6a2a\u5200\u67b6\u5f00\uff0c\u539f\u6765\u4ed6\u5df2\u4e58\u9699\u5c06\u5546\u5b9d\u9707\u7684\u516b\u5366\u5200\u62a2\u5728\u624b\u4e2d\u3002 \u3000\u3000\u5385\u4e0a\u4f17\u4eba\u8eab\u5904\u9ed1\u6697\u4e0e\u5947\u70ed\u4e4b\u4e2d\uff0c\u4f46\u542c\u5f97\u53cc\u5200\u76f8\u4ea4\uff0c\u53ee\u53ee\u5f53\u5f53\u4e71\u54cd\uff0c\u77e5\u9053\u80e1\u6590\u5df2\u62a2\u5f97\u5175\u5203\uff0c\u6b63\u5728\u731b\u529b\u6025\u653b\uff0c\u5fc3\u4e2d\u5404\u81ea\u591a\u4e86\u4e00\u4e1d\u6307\u671b\uff0c\u738b\u5251\u82f1\u5927\u53eb\uff1a\u201c\u780d\u5979\u53f3\u80a9\uff0c\u780d\u5979\u53f3\u80a9\u3002\u201d\u9a6c\u884c\u7a7a\u53eb\u9053\uff1a\u201c\u5148\u6740\u6563\u52a0\u6dfb\u67f4\u706b\u7684\u5e84\u4e01\u3002\u201d\u5b59\u521a\u5cf0\u53eb\u9053\uff1a\u201c\u522b\u8ddf\u8001\u592a\u5a46\u7ea0\u7f20\uff0c\u8bbe\u6cd5\u6253\u5f00\u5385\u95e8\u8981\u7d27\u3002\u201d\u5f90\u94ee\u653e\u58f0\u5927\u55e5\uff1a\u201c\u70ed\u6b7b\u5566\uff0c\u70ed\u6b7b\u5566\uff01\u201d\u4f17\u4eba\u4e71\u6210\u4e00\u7247\u3002 \u3000\u3000\u80e1\u6590\u4f55\u5c1d\u4e0d\u77e5\u8bbe\u6cd5\u6253\u5f00\u5385\u95e8\u4e43\u662f\u7b2c\u4e00\u8981\u52a1\uff0c\u4f46\u5546\u8001\u592a\u62fc\u6b7b\u7ea0\u7f20\uff0c\u59cb\u7ec8\u7f13\u4e0d\u51fa\u624b\u811a\u3002\u4ed6\u5200\u6cd5\u9ad8\u51fa\u5546\u8001\u592a\u751a\u591a\uff0c\u53ea\u662f\u6b64\u65f6\u5c40\u52bf\u7279\u5f02\uff0c\u4ed6\u5e74\u7eaa\u5e7c\u5c0f\uff0c\u96be\u4ee5\u9547\u5b9a\u5e94\u4ed8\uff0c\u6570\u6b21\u5f97\u5230\u53ef\u4e58\u4e4b\u673a\uff0c\u5374\u90fd\u7ed9\u5546\u8001\u592a\u7528\u62fc\u547d\u7684\u72e0\u62db\u89e3\u6551\u5f00\u53bb\u3002 \u3000\u3000\u4e8c\u4eba\u72fc\u6597\u4e03\u516b\u56de\u5408\uff0c\u5546\u8001\u592a\u4e0d\u4f4f\u540e\u9000\u3002\u5546\u5b9d\u9707\u4ece\u5bb6\u4e01\u624b\u4e2d\u63a5\u8fc7\u4e00\u67c4\u5355\u5200\uff0c\u518d\u884c\u4e0a\u524d\u5939\u653b\u3002\u4f17\u5e84\u4e01\u521d\u89c1\u4e3b\u6bcd\u4e0e\u5c0f\u4e3b\u4eba\u624b\u6709\u5175\u5203\uff0c\u5bf9\u4ed8\u4e00\u4e2a\u7a7a\u624b\u7684\u5b69\u5b50\uff0c\u53ea\u9053\u7a33\u53ef\u5f97\u80dc\uff0c\u6b64\u523b\u89c1\u4e3b\u6bcd\u5934\u53d1\u6563\u4e71\u3002\u4e0d\u4f4f\u540e\u9000\uff0c\u663e\u662f\u4e0d\u654c\uff0c\u5404\u4eba\u6301\u5200\u633a\u67aa\uff0c\u7eb7\u7eb7\u52a0\u5165\u6218\u56e2\u3002\u4f17\u5e84\u4e01\u6b66\u827a\u4f4e\u5fae\uff0c\u7ed9\u80e1\u6590\u5200\u780d\u8db3\u8e22\uff0c\u970e\u65f6\u95f4\u4f24\u4e86\u6570\u4eba\uff0c\u4f46\u5546\u5bb6\u5821\u7684\u5e84\u4e01\u4e2a\u4e2a\u52c7\u608d\uff0c\u8d1f\u4f24\u4e4b\u4e0b\uff0c\u4ecd\u662f\u62d2\u6218\u4e0d\u9000\u3002\u4f46\u542c\u5f97\u5450\u558a\u58f0\u3001\u5175\u5203\u649e\u51fb\u58f0\u3001\u547c\u559d\u65a5\u9a82\u58f0\u3001\u67f4\u706b\u7206\u88c2\u58f0\uff0c\u54cd\u6210\u4e00\u7247\u3002 \u3000\u3000\u5927\u5385\u4e0a\u5404\u4eba\u542c\u5f97\u5916\u9762\u6108\u6253\u6108\u4e71\uff0c\u5fc3\u60f3\u80e1\u6590\u4e00\u4eba\u867d\u52c7\uff0c\u4ee5\u4e00\u4e2a\u5c0f\u5b69\u5b50\u5bf9\u654c\u5546\u5bb6\u5821\u5168\u5821\u4e0a\u4e0b\uff0c\u5982\u4f55\u80fd\u80dc\uff1f\u4e8e\u662f\u6709\u7684\u5492\u9a82\uff0c\u6709\u7684\u957f\u53f9\uff0c\u6709\u7684\u60b2\u53f7\uff0c\u5608\u6742\u4e4b\u4e2d\u53c8\u52a0\u4e0a\u5608\u6742\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u4e00\u4e2a\u58f0\u97f3\u53eb\u9053\uff1a\u201d\u5c0f\u80e1\u6590\u542c\u7740\uff0c\u4ee5\u9634\u9633\u8bc0\u5148\u53d6\u4e3b\u8111\uff0c\u4ee5\u4e71\u73af\u8bc0\u6563\u5176\u9644\u4ece\u3002\u201d\u8fd9\u58f0\u97f3\u4e2d\u6c14\u5145\u6c9b\uff0c\u76d6\u8fc7\u4e86\u4e00\u5207\u6742\u58f0\uff0c\u4e00\u4e2a\u5b57\u4e00\u4e2a\u5b57\u8bf4\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u6b63\u662f\u8d75\u534a\u5c71\u7684\u8bdd\u58f0\u3002 \u3000\u3000\u80e1\u6590\u89c1\u654c\u4eba\u8d8a\u6218\u8d8a\u591a\uff0c\u672c\u5df2\u5fc3\u795e\u70e6\u8e81\uff0c\u4e0d\u77e5\u5982\u4f55\u662f\u597d\uff0c\u5ffd\u542c\u5f97\u8d75\u534a\u5c71\u8fd9\u51e0\u53e5\u8bdd\uff0c\u5fc3\u60f3\u8d75\u4f2f\u4f2f\u82f1\u96c4\u76d6\u4e16\uff0c\u6240\u8bf4\u5fc5\u5b9a\u4e0d\u9519\uff0c\u4e0d\u7531\u5f97\u7cbe\u795e\u4e3a\u4e4b\u4e00\u632f\uff0c\u94a2\u5200\u547c\u547c\u547c\u4e09\u5200\uff0c\u5f80\u5546\u8001\u592a\u4e2d\u76d8\u780d\u65ab\u3002\u4ed6\u8fd9\u5200\u53d6\u81ea\u5546\u5b9d\u9707\uff0c\u5203\u53e3\u867d\u5df2\u5377\u8fb9\uff0c\u4f46\u53ea\u8981\u780d\u4e2d\u4e86\uff0c\u4ecd\u80fd\u81f4\u547d\u3002\u5546\u8001\u592a\u89c1\u4ed6\u6765\u52bf\u731b\u6076\uff0c\u6a2a\u5200\u6025\u67b6\uff0c\u53cc\u5200\u78b0\u649e\u65f6\u5f53\u5f53\u54cd\u4e86\u4e24\u4e0b\uff0c\u7b2c\u4e09\u4e0b\u80e1\u6590\u4ece\u521a\u52b2\u7a81\u8f6c\u67d4\u52b2\uff0c\u81ea\u9633\u53d8\u9634\uff0c\u4e00\u6536\u4e00\u6325\uff0c\u624b\u8155\u5ffd\u5730\u8f6c\u4e86\u4e09\u4e2a\u5708\u5b50\u3002 \u3000\u3000\u4ed6\u662f\u987a\u52bf\u800c\u8f6c\uff0c\u5546\u8001\u592a\u7684\u624b\u81c2\u5374\u662f\u9006\u8f6c\u5708\u5b50\uff0c\u5230\u7b2c\u4e8c\u4e2a\u5708\u5b50\u65f6\u5979\u624b\u81c2\u5df2\u8f6c\u4e0d\u8fc7\u6765\uff0c\u4f46\u89c9\u65f6\u9aa8\u5267\u75db\uff0c\u53ea\u5f97\u6492\u624b\u653e\u5200\u3002\u90a3\u516b\u5366\u7d2b\u91d1\u5200\u6fc0\u98de\u800c\u8d77\uff0c\u5c04\u5165\u5929\u7a7a\u3002\u80e1\u6590\u201c\u9634\u9633\u8bc0\u201d\u5efa\u529f\uff0c\u8ddf\u7740\u4e00\u5200\u5f80\u5979\u80a9\u5934\u76f4\u5288\u4e0b\u53bb\u3002\u5200\u950b\u8ddd\u5979\u80a9\u5934\u7ea6\u6709\u534a\u5c3a\uff0c\u53ea\u89c1\u5979\u81ea\u53d1\u62ab\u80a9\uff0c\u534a\u8fb9\u8138\u4e0a\u6ee1\u67d3\u8840\u6c61\uff0c\u4e00\u4e2a\u5ff5\u5934\u5728\u5fc3\u4e2d\u4e00\u95ea\uff1a\u201c\u8fd9\u8001\u5a46\u5b50\u59d4\u5b9e\u53ef\u601c\uff0c\u600e\u80fd\u4e00\u5200\u5c06\u5979\u780d\u6b7b\uff1f\u201d\u75be\u5fd9\u5200\u8eab\u7ffb\u8f6c\uff0c\u60f3\u7528\u5200\u80cc\u649e\u5979\u80a9\u8180\uff0c\u4f7f\u5979\u65e0\u529b\u518d\u6597\uff0c\u4fbf\u5373\u8d76\u53bb\u5f00\u95e8\u6551\u4eba\u3002 \u3000\u3000\u4e0d\u6599\u5546\u8001\u592a\u91d1\u5200\u8131\u624b\uff0c\u5fc3\u4e2d\u7acb\u65f6\u4fbf\u5b58\u4e86\u4e0e\u4ec7\u4eba\u540c\u5f52\u4e8e\u5c3d\u7684\u5ff5\u5934\uff0c\u660e\u89c1\u80e1\u6590\u4e3e\u5200\u780d\u4e0b\uff0c\u6beb\u4e0d\u95ea\u907f\uff0c\u53cd\u800c\u62a2\u4e0a\u4e00\u6b65\u6eda\u5165\u4ed6\u7684\u6000\u91cc\uff0c\u53f3\u624b\u6263\u4f4f\u4ed6\u524d\u80f8\u201c\u795e\u5c01\u7a74\u201d\uff0c\u5de6\u624b\u6263\u4f4f\u4ed6\u5c0f\u8179\u201d\u4e2d\u6ce8\u7a74\u201d\u7262\u7262\u6293\u5b9a\u3002\u80e1\u6590\u5927\u60ca\uff0c\u5200\u80cc\u7528\u529b\u51fb\u4e0b\u3002 \u3000\u3000\u5546\u8001\u592a\u201c\u563f\u201d\u7684\u4e00\u58f0\uff0c\u80a9\u9aa8\u788e\u88c2\uff0c\u4f46\u5979\u4e0d\u987e\u4e00\u5207\uff0c\u6293\u4f4f\u4e86\u80e1\u6590\u7a74\u9053\u6b7b\u4e5f\u4e0d\u653e\uff0c\u540c\u65f6\u53f3\u8db3\u529b\u52fe\uff0c\u4e8c\u4eba\u4e00\u9f50\u5012\u5730\u3002 \u3000\u3000\u80e1\u6590\u76f4\u81f3\u6b64\u65e5\u65b9\u6709\u4e34\u654c\u5bf9\u6218\u7684\u7ecf\u9a8c\uff0c\u7edd\u4e0d\u77e5\u654c\u4eba\u62fc\u547d\u4e4b\u65f6\u7ade\u6709\u5982\u6b64\u7684\u72e0\u6cd5\uff0c\u88ab\u5979\u6293\u4f4f\u4e4b\u540e\u53ea\u5f97\u51fa\u529b\u6323\u624e\u3002\u5546\u8001\u592a\u4e00\u5f20\u53e3\uff0c\u53c8\u54ac\u4f4f\u4e86\u4ed6\u524d\u80f8\u8863\u670d\uff0c\u51e0\u4e2a\u6253\u6eda\uff0c\u4e8c\u4eba\u7adf\u9f50\u5f80\u5927\u706b\u5806\u4e2d\u6eda\u53bb\u3002\u80e1\u6590\u5927\u53eb\uff1a\u201c\u5feb\u653e\u5f00\uff0c\u4f60\u4e0d\u6015\u70e7\u6b7b\u4e48\uff1f\u201d \u3000\u3000\u4ed6\u5fc3\u795e\u4e00\u4e71\uff0c\u7ade\u5fd8\u4e86\u8be5\u4f7f\u201c\u5c0f\u64d2\u62ff\u624b\u201d\u5378\u8131\u8fd9\u6837\u8d34\u8eab\u7684\u7ea0\u7f20\uff0c\u53ea\u662f\u731b\u529b\u56de\u593a\u3002 \u3000\u3000\u4e8c\u4eba\u53c8\u6eda\u4e24\u4e0b\uff0c\u7ec8\u4e8e\u6eda\u8fdb\u4e86\u706b\u5806\u3002 \u3000\u3000\u5546\u5b9d\u9707\u5927\u53eb\uff1a\u201c\u5988\uff01\u201d\u98de\u8eab\u6765\u6551\uff0c\u63d0\u8d77\u5355\u5200\u7684\u5200\u67c4\uff0c\u5bf9\u51c6\u80e1\u6590\u5929\u7075\u76d6\u51ff\u4e86\u4e0b\u53bb\u3002\u80e1\u6590\u504f\u5934\u4e00\u907f\uff0c\u8fd9\u4e00\u5200\u67c4\u8fd8\u662f\u6253\u4e2d\u4e86\u989d\u89d2\uff0c\u75bc\u5f97\u9669\u4e9b\u513f\u6655\u53bb\u3002\u5546\u5b9d\u9707\u751f\u6015\u6bcd\u4eb2\u53d7\u4f24\uff0c\u6025\u5fd9\u4f38\u624b\u5c06\u4e8c\u4eba\u4ece\u706b\u5806\u4e2d\u63d0\u4e86\u51fa\u6765\uff0c\u770b\u51c6\u80e1\u6590\u80cc\u5fc3\uff0c\u4e00\u5200\u75be\u780d\u800c\u4e0b\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u5343\u94a7\u4e00\u53d1\u7684\u5f53\u53e3\uff0c\u80e1\u6590\u795e\u667a\u500f\u5730\u6e05\u660e\uff0c\u53cd\u8e22\u4e00\u811a\uff0c\u4e0a\u4e2d\u5546\u5b9d\u9707\u624b\u8155\uff0c\u7b2c\u4e8c\u817f\u8ddf\u7740\u8e22\u51fa\uff0c\u8fd9\u4e00\u817f\u51fa\u5c3d\u5168\u529b\uff0c\u7ade\u8e22\u5f97\u4ed6\u8dcc\u51fa\u4e94\u516d\u4e08\u5916\uff0c\u4e00\u65f6\u722c\u4e0d\u8d77\u6765\u3002 \u3000\u3000\u80e1\u6590\u8863\u670d\u7740\u706b\uff0c\u989d\u89d2\u53c8\u662f\u75bc\u75db\u6b32\u88c2\uff0c\u5927\u559d\u4e00\u58f0\uff0c\u53cc\u81c2\u75be\u632f\uff0c\u683c\u683c\u4e24\u54cd\uff0c\u5df2\u6446\u8131\u4e86\u5546\u8001\u592a\u7684\u7ea0\u7f20\uff0c\u5728\u5730\u4e0a\u4e00\u4e2a\u6253\u6eda\uff0c\u6eda\u7184\u8863\u4e0a\u706b\u7130\u3002\u5546\u8001\u592a\u5e74\u8001\uff0c\u7ed9\u70df\u706b\u4e00\u85b0\uff0c\u5df2\u6655\u4e86\u8fc7\u53bb\u3002\u51e0\u540d\u5e84\u4e01\u5fd9\u7ed9\u5979\u6253\u6251\u8eab\u4e0a\u706b\u5934\u3002 \u3000\u3000\u80e1\u6590\u7a7a\u624b\u5954\u5165\u5e84\u4e01\u4e1b\u4e2d\uff0c\u5fc3\u4e2d\u5bf9\u81ea\u5df1\u6781\u662f\u607c\u6012\uff1a\u201c\u5728\u8fd9\u820d\u751f\u5fd8\u6b7b\u3001\u72e0\u547d\u6251\u6597\u7684\u5f53\u513f\uff0c\u6211\u8fd8\u8981\u53bb\u53ef\u601c\u654c\u4eba\uff0c\u9002\u624d\u6ca1\u9001\u4e86\u5c0f\u547d\uff0c\u5f53\u771f\u662f\u65e0\u5929\u7406\u3002\u201d\u6b64\u65f6\u518d\u4e0d\u5bb9\u60c5\uff0c\u5939\u624b\u593a\u8fc7\u4e00\u67c4\u5355\u5200\uff0c\u62f3\u6253\u8db3\u8e22\uff0c\u5200\u5288\u65f6\u649e\uff0c\u72b9\u5982\u864e\u5165\u7f8a\u7fa4\uff0c\u7247\u523b\u95f4\u5c06\u4f17\u5e84\u4e01\u6253\u5f97\u4e1c\u9003\u897f\u7a9c\u3002 \u3000\u3000\u4ed6\u5954\u5230\u5385\u95e8\u4e4b\u524d\uff0c\u4ece\u5e84\u4e01\u624b\u4e2d\u593a\u8fc7\u4e00\u67c4\u706b\u53c9\uff0c\u5c06\u5806\u5728\u95e8\u524d\u7684\u67f4\u70ad\u4e00\u9635\u4e71\u6311\u4e71\u62e8\uff0c\u53ea\u89c1\u94c1\u95e8\u5df2\u70e7\u5f97\u901a\u7ea2\uff0c\u4e0d\u7981\u5927\u60ca\uff1a\u201c\u82e5\u662f\u95e8\u94ae\u4e0e\u94c1\u95e8\u70e7\u5f97\u710a\u6210\u4e00\u7247\uff0c\u8fd9\u95e8\u5c31\u6253\u4e0d\u5f00\u4e86\u3002\u201d\u5371\u6025\u4e2d\u4e0d\u53ca\u591a\u60f3\uff0c\u63d0\u8d77\u5355\u5200\uff0c\u5c06\u5168\u8eab\u529f\u52b2\u8fd0\u4e8e\u53f3\u81c2\uff0c\u594b\u529b\u76f4\u780d\u4e0b\u53bb\uff0c\u54af\u7684\u4e00\u58f0\uff0c\u95e8\u94ae\u5e94\u624b\u800c\u843d\uff0c\u8fd9\u4e00\u780d\u7528\u529b\u8fc7\u5de8\uff0c\u5355\u5200\u7ade\u5411\u4e0a\u7fd8\u8d77\uff0c\u5f2f\u6210\u4e86\u4e00\u628a\u66f2\u5c3a\u3002\u4ed6\u629b\u4e0b\u5355\u5200\uff0c\u7528\u706b\u53c9\u94a9\u6ce8\u95e8\u73af\u5411\u5916\u62c9\u626f\uff0c\u7adf\u7136\u4e0d\u52a8\u3002\u80e1\u6590\u6025\u5f97\u5fc3\u4e2d\u6026\u6026\u4e71\u8df3\uff1a\u201c\u83ab\u8981\u529f\u4e8f\u4e00\u7bd1\uff0c\u5230\u6700\u540e\u94c1\u95e8\u7adf\u7136\u62c9\u4e0d\u5f00\u6765\u3002\u201d\u53c8\u662f\u7528\u529b\u4e00\u62c9\uff0c\u4f46\u542c\u5f97\u8f67\u8f67\u8fde\u58f0\uff0c\u94c1\u95e8\u7f13\u7f13\u5f00\u4e86\uff0c\u9ed1\u70df\u5939\u7740\u706b\u5934\uff0c\u4ece\u95e8\u4e2d\u76f4\u6251\u51fa\u6765\u3002 \u3000\u3000\u4ed6\u60f3\u4e0d\u5230\u5385\u4e2d\u5df2\u70e7\u5f97\u8fd9\u822c\u5389\u5bb3\uff0c\u6025\u53eb\uff1a\u201d\u8d75\u4f2f\u4f2f\uff0c\u5feb\u51fa\u6765\uff01\u201d\u53ea\u89c1\u70df\u96fe\u7315\u6f2b\u4e4b\u4e2d\uff0c\u4e00\u4eba\u5f53\u5148\u62a2\u51fa\uff0c\u6b63\u662f\u738b\u5251\u82f1\uff0c\u63a5\u7740\u6bb7\u4ef2\u7fd4\u3001\u5f90\u94b5\u3001\u9a6c\u884c\u7a7a\u3001\u5b59\u521a\u5cf0\u5148\u540e\u5954\u51fa\uff0c\u6700\u540e\u624d\u662f\u8d75\u534a\u5c71\u62b1\u7740\u5415\u5c0f\u59b9\u51fa\u6765\u3002\u5404\u4eba\u8863\u886b\u7126\u70c2\uff0c\u72fc\u72c8\u4e0d\u582a\u3002 \u3000\u3000\u8fd9\u65f6\u5385\u4e2d\u6728\u6750\u90fd\u5df2\u7740\u706b\uff0c\u684c\u6905\u56fa\u5df2\u70e7\u7740\uff0c\u8fde\u6881\u67f1\u4e5f\u5df2\u5927\u706b\u718a\u718a\u3002\u8fd9\u65f6\u673a\u771f\u662f\u76f8\u5dee\u4e0d\u5f97\u7247\u523b\uff0c\u5018\u82e5\u80e1\u6590\u518d\u8fdf\u4e00\u76cf\u8336\u7684\u65f6\u5206\u7834\u95e8\uff0c\u5fc5\u5b9a\u6709\u4eba\u4e27\u547d\u3002 \u3000\u3000\u80e1\u6590\u89c1\u8d75\u534a\u5c71\u5b89\u7136\u65e0\u6059\uff0c\u6251\u4e86\u4e0a\u53bb\uff0c\u8fde\u53eb\uff1a\u201c\u8d75\u4f2f\u4f2f\uff0c\u8d75\u4f2f\u4f2f\u3002\u201d\u8d75\u534a\u5c71\u987b\u7709\u5c3d\u7126\uff0c\u4f46\u4ecd\u662f\u9547\u5b9a\u5982\u6052\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u8d5e\u9053\uff1a\u201c\u597d\u5b69\u5b50\uff01\u201d\u5ffd\u542c\u5f97\u738b\u5251\u82f1\u53eb\u9053\uff1a\u201c\u5251\u6770\uff01\u5251\u6770\uff01\u4f60\u5728\u54ea\u91cc\uff1f\u201d\u8d75\u534a\u5c71\u56db\u4e0b\u4e00\u77a7\uff0c\u679c\u7136\u4e0d\u89c1\u738b\u5251\u6770\uff0c\u60ca\u9053\uff1a\u201c\u96be\u9053\u4ed6\u6ca1\u51fa\u6765\uff1f\u201d\u738b\u5251\u82f1\u5927\u53eb\uff1a\u201c\u6211\u5144\u5f1f\u6ca1\u51fa\u6765\u554a\uff0c\u6ca1\u51fa\u6765\u554a\u3002\u201d \u3000\u3000\u6b64\u65f6\u5385\u4e2d\u6881\u67f1\u4e1c\u4e00\u6761\u897f\u4e00\u6761\uff0c\u6a2a\u4e03\u7ad6\u516b\u5730\u5012\u584c\uff0c\u5df2\u70e7\u6210\u4e00\u4e2a\u706b\u7a9f\uff0c\u738b\u5251\u82f1\u867d\u662f\u624b\u8db3\u60c5\u6bb7\uff0c\u5374\u4e5f\u4e0d\u6562\u8fdb\u53bb\u76f8\u6551\uff0c\u53ea\u662f\u5927\u53eb\uff1a\u201c\u5251\u6770\uff0c\u5feb\u51fa\u6765\uff0c\u5feb\u51fa\u6765\uff01\u201d \u3000\u3000\u8d75\u534a\u5c71\u4e0e\u80e1\u6590\u540c\u65f6\u60f3\u5230\uff1a\u201c\u4ed6\u82e5\u80fd\u591f\u51fa\u6765\uff0c\u5c82\u6709\u4e0d\u51fa\u6765\u4e4b\u7406\uff1f\u201d\u4ed6\u4e8c\u4eba\u4ff1\u662f\u5929\u751f\u7684\u4fa0\u4e49\u5fc3\u80a0\uff0c\u5f53\u4e0b\u66f4\u4e0d\u591a\u60f3\uff0c\u4e00\u8001\u4e00\u5c11\uff0c\u4e0d\u7ea6\u800c\u540c\u5730\u51b2\u8fdb\u706b\u7a9f\u4e4b\u4e2d\uff0c\u5192\u70df\u7a81\u706b\uff0c\u6765\u5bfb\u738b\u5251\u6770\u3002\u80e1\u6590\u8e0f\u5728\u70e7\u5f97\u7099\u70ed\u7684\u7816\u4e0a\uff0c\u4e0d\u7981\u70eb\u5f97\u53cc\u8db3\u4e71\u8df3\u3002\u8d75\u534a\u5c71\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u5feb\u51fa\u53bb\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u4e0d\uff0c\u8d75\u4f2f\u4f2f\uff0c\u4f60\u5feb\u51fa\u53bb\u3002\u201d\u4ed6\u521a\u8bf4\u4e86\u8fd9\u53e5\u8bdd\uff0c\u5ffd\u5730\u53eb\u9053\uff1a\u201c\u5728\u8fd9\u91cc\u4e86\uff01\u201d\u4fef\u8eab\u5c06\u738b\u5251\u6770\u62c9\u8d77\uff0c\u98de\u5954\u51fa\u5916\uff0c\u539f\u6765\u738b\u5251\u6770\u6328\u4e0d\u4f4f\u70bd\u70ed\uff0c\u5c06\u53e3\u9f3b\u51d1\u5728\u72d7\u6d1e\u4e0a\u5438\u6c14\uff0c\u4e0d\u6599\u4e00\u9635\u9ed1\u70df\u81ea\u5916\u51b2\u8fdb\uff0c\u5c06\u4ed6\u718f\u5f97\u6655\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u80e1\u6590\u7ed9\u70df\u545b\u5f97\u5927\u58f0\u54b3\u55fd\uff0c\u738b\u5251\u6770\u8eab\u6750\u9b41\u68a7\uff0c\u96be\u4ee5\u6a2a\u62b1\uff0c\u53ea\u597d\u62c9\u4e86\u4ed6\u7740\u5730\u62d6\u5c06\u51fa\u53bb\uff0c\u5c06\u5230\u95e8\u53e3\uff0c\u95e8\u5916\u4f17\u4eba\u7a81\u7136\u5927\u58f0\u60ca\u547c\uff0c\u4f46\u89c1\u5c4b\u9876\u4e00\u6839\u706b\u6881\u76f4\u8dcc\u4e0b\u6765\uff0c\u538b\u5411\u80e1\u6590\u5934\u9876\u3002\u80e1\u6590\u52a0\u7d27\u811a\u6b65\uff0c\u60f3\u8981\u62a2\u51fa\u5385\u95e8\uff0c\u4f46\u90a3\u6881\u6728\u751a\u957f\uff0c\u5176\u52bf\u5df2\u7136\u4e0d\u53ca\u3002\u8d75\u534a\u5c71\u54fc\u4e86\u4e00\u58f0\uff0c\u8e0f\u4e0a\u534a\u6b65\uff0c\u4e00\u62db\u201c\u6247\u901a\u80cc\u201d\uff0c\u53f3\u638c\u5df2\u6258\u4f4f\u706b\u6881\u3002\u8fd9\u6881\u6728\u672c\u8eab\u4e4b\u91cd\u4e0d\u4e0b\u56db\u4e94\u767e\u65a4\uff0c\u4ece\u4e0a\u9762\u8dcc\u5c06\u4e0b\u6765\uff0c\u52bf\u9053\u66f4\u662f\u60ca\u4eba\u3002\u8d75\u534a\u5c71\u53cc\u817f\u9a6c\u6b65\u7a33\u51dd\u4e0d\u52a8\uff0c\u53f3\u638c\u8fd9\u4e00\u6258\uff0c\u706b\u6881\u53cd\u800c\u5411\u4e0a\u4e00\u62ac\uff0c\u90a3\u201c\u95ea\u901a\u80cc\u201d\u7684\u4e0b\u534a\u62db\u8ddf\u7740\u53d1\u51fa\uff0c\u5de6\u638c\u642d\u5728\u6881\u6728\u4e0a\u5411\u5916\u4e00\u9001\uff0c\u53ea\u89c1\u4e00\u6761\u706b\u9f99\u4ece\u5385\u53e3\u6fc0\u98de\u800c\u51fa\uff0c\u592d\u77eb\u5165\u7a7a\uff0c\u76f4\u98de\u51fa\u516d\u4e03\u4e08\u5916\uff0c\u65b9\u59cb\u843d\u5730\u3002 \u3000\u3000\u5385\u95e8\u5916\u4f17\u4eba\u89c1\u4ed6\u9732\u4e86\u8fd9\u624b\u529f\u592b\uff0c\u5446\u4e86\u534a\u664c\uff0c\u8fd9\u624d\u9707\u5929\u4ef7\u54cd\u559d\u8d77\u91c7\u6765\uff0c\u8fde\u5546\u5bb6\u5821\u7684\u5e84\u4e01\uff0c\u4e5f\u4e0d\u81ea\u7981\u5730\u7ad9\u5728\u8fdc\u5904\u53eb\u597d\u3002 \u3000\u3000\u738b\u5251\u82f1\u6276\u7740\u5144\u5f1f\uff0c\u5fd9\u7740\u66ff\u4ed6\u6251\u7184\u8863\u4e0a\u706b\u7130\uff0c\u5fc3\u4e2d\u6697\u81ea\u60ed\u6127\uff1a\u201c\u6211\u81ea\u5df1\u4eb2\u5144\u5f1f\u6709\u96be\uff0c\u5374\u8981\u65c1\u4eba\u76f8\u6551\u3002\u201d \u3000\u3000\u9a6c\u884c\u7a7a\u4e0e\u5f90\u94b5\u51fa\u4e86\u94c1\u5385\uff0c\u7acb\u5373\u627e\u5bfb\u9a6c\u6625\u82b1\uff0c\u4f46\u4e1c\u5f20\u897f\u671b\uff0c\u4e0d\u89c1\u5979\u7684\u5f71\u8e2a\u3002 \u3000\u3000\u5f90\u94ee\u5fc3\u4e0b\u8d77\u7591\uff1a\u201d\u5979\u5b9a\u662f\u4e0e\u59d3\u5546\u7684\u5c0f\u5b50\u5230\u4ec0\u4e48\u5730\u65b9\u6363\u9b3c\u53bb\u4e86\u3002\u201d\u4ed6\u8eab\u51fa\u706b\u57df\uff0c\u5fc3\u4e2d\u5992\u706b\u53c8\u65fa\uff0c\u53eb\u9053\uff1a\u201c\u5e08\u7236\uff0c\u6211\u53bb\u627e\u5979\u3002\u201d\u62d4\u6b65\u98de\u5954\u3002 \u3000\u3000\u9a6c\u884c\u7a7a\u5e74\u7eaa\u4e00\u5927\uff0c\u7a76\u5df2\u4e0d\u5982\u5c0f\u4f19\u5b50\u786c\u6717\uff0c\u7ed9\u70df\u706b\u7099\u5f97\u5934\u6655\u773c\u82b1\uff0c\u53ea\u60f3\u627e\u4e2a\u5730\u65b9\u4f11\u606f\u4e00\u4f1a\uff0c\u7a81\u89c9\u80cc\u540e\u6709\u638c\u98ce\u88ad\u5230\u3002\u8fd9\u4e00\u4e0b\u7a81\u88ad\u5168\u7136\u51fa\u4ed6\u610f\u6599\u4e4b\u5916\uff0c\u90a3\u4e00\u638c\u6765\u5f97\u53c8\u7094\u53c8\u52b2\uff0c\u9a6c\u884c\u7a7a\u4e0d\u53ca\u62db\u67b6\uff0c\u53ea\u5f97\u5438\u6c14\u786c\u63a5\uff0c\u7830\u7684\u4e00\u54cd\uff0c\u8eab\u5b50\u7ed9\u6253\u5f97\u6447\u6447\u6643\u6643\uff0c\u4f46\u89c9\u773c\u524d\u4e00\u9ed1\uff0c\u5168\u8eab\u53d1\u8f6f\uff0c\u63a5\u7740\u81c0\u4e0a\u53c8\u88ab\u4eba\u8e22\u4e86\u4e00\u817f\uff0c\u8eab\u4e0d\u7531\u4e3b\u5730\u5411\u94c1\u5385\u7684\u706b\u7a9f\u4e2d\u8dcc\u53bb\uff0c\u8ff7\u7cca\u4e2d\u53ea\u542c\u5f97\u5546\u8001\u592a\u7eb5\u58f0\u5927\u7b11\uff0c\u53eb\u9053\uff1a\u201c\u5251\u9e23\uff0c\u5251\u9e23\uff0c\u6211\u7ec8\u4e8e\u7ed9\u4f60\u62a5\u4e86\u4e00\u70b9\u513f\u4ec7\u2026\u2026\u201d\u4e00\u9635\u70ed\u6c14\u88f9\u4f4f\u5168\u8eab\uff0c\u767b\u65f6\u4ec0\u4e48\u4e5f\u4e0d\u77e5\u9053\u4e86\u3002 \u3000\u3000\u8d75\u534a\u5c71\u521a\u5c06\u5415\u5c0f\u59b9\u6551\u9192\uff0c\u5ffd\u89c1\u5546\u8001\u592a\u7a81\u7136\u4ece\u70df\u706b\u91cc\u94bb\u51fa\u6765\uff0c\u5c06\u9a6c\u884c\u7a7a\u6253\u5165\u706b\u7a9f\uff0c\u4e0d\u7981\u4e00\u5446\u3002\u53ea\u89c1\u5546\u8001\u592a\u5f13\u8eab\u8d70\u5165\u5385\u95e8\uff0c\u5bf9\u718a\u718a\u5927\u706b\u7adf\u662f\u89c6\u82e5\u65e0\u7779\uff0c\u4ed6\u5927\u53eb\uff1a\u201c\u51b3\u51fa\u6765\uff0c\u4f60\u8fd9\u4e0d\u662f\u9001\u6b7b\u4e48\uff1f\u201d \u3000\u3000\u4ed6\u4e00\u8a00\u65b9\u6bd5\uff0c\u53c8\u662f\u4e00\u6761\u6781\u5927\u706b\u6881\u843d\u4e86\u4e0b\u6765\uff0c\u817e\u7684\u4e00\u58f0\u5de8\u54cd\uff0c\u706b\u7130\u56db\u4e0b\u98de\u821e\uff0c\u5df2\u5c06\u5385\u95e8\u5c01\u4f4f\u3002\u5546\u8001\u592a\u6000\u62b1\u7d2b\u91d1\u516b\u5366\u5200\uff0c\u8138\u9732\u7b11\u5bb9\uff0c\u7aef\u5750\u5728\u706b\u7130\u4e4b\u4e2d\uff0c\u5168\u8eab\u8863\u670d\u5934\u53d1\u5747\u5df2\u7740\u706b\uff0c\u5374\u7adf\u4f3c\u4e0d\u89c9\u75db\u82e6\u3002\u5979\u5fc3\u4e2d\u5728\u60f3\uff1a\u201c\u590d\u4ec7\u7684\u5fc3\u613f\u867d\u7136\u96be\u4e86\uff0c\u6211\u5374\u4e0d\u4e45\u5c31\u53ef\u4e0e\u5251\u9e23\u76f8\u4f1a\u4e86\u3002\u201d \u3000\u3000\u8d75\u534a\u5c71\u957f\u53f9\u4e00\u58f0\uff0c\u5fc3\u60f3\u6b64\u4f4d\u8001\u592a\u592a\u867d\u662f\u5973\u6d41\uff0c\u6027\u5b50\u521a\u70c8\uff0c\u80dc\u4e8e\u987b\u7709\uff0c\u53c8\u60f3\u6b64\u756a\u4e1c\u6765\u4e4b\u4e8b\u5df2\u4e86\uff0c\u65e0\u610f\u4e2d\u7ed3\u8bc6\u4e86\u4e00\u4e2a\u5c11\u5e74\u82f1\u96c4\uff0c\u4e5f\u7b97\u6b64\u884c\u4e0d\u865a\uff0c\u89c1\u5b59\u521a\u5cf0\u3001\u738b\u5251\u82f1\u7b49\u5404\u81ea\u6b63\u5728\u5fd9\u788c\uff0c\u4e8e\u662f\u8f6c\u5934\u5411\u80e1\u6590\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u54b1\u4eec\u8d70\u5427\uff0c\u4e00\u8d77\u8d70\u4e00\u7a0b\u5982\u4f55\uff1f\u201d\u80e1\u6590\u9053\uff1a\u201c\u597d\u6781\uff0c\u597d\u6781\uff01\u201d \u3000\u3000\u5728\u4ed6\u5e7c\u5c0f\u7684\u5fc3\u7075\u4e4b\u4e2d\uff0c\u60f3\u5230\u4e86\u4e16\u95f4\u8bb8\u8bb8\u591a\u591a\u53d8\u5e7b\u96be\u6d4b\u4e4b\u4e8b\uff0c\u60f3\u5230\u5415\u5c0f\u59b9\u7684\u62a5\u4ec7\u662f\u5982\u6b64\uff0c\u800c\u5546\u8001\u592a\u7684\u62a5\u4ec7\u5374\u53c8\u5982\u6b64\u3002\u4ed6\u4e0e\u8d75\u534a\u5c71\u643a\u624b\u540c\u884c\uff0c\u9ed8\u9ed8\u60f3\u7740\u5fc3\u4e8b\uff0c\u8d70\u51fa\u91cc\u8bb8\uff0c\u56de\u5934\u4e00\u671b\uff0c\u53ea\u89c1\u5546\u5bb6\u5821\u5140\u767d\u70e7\u5f97\u534a\u5929\u901a\u7ea2\u3002 \u3000\u3000\u8d75\u534a\u5c71\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4eca\u5929\u7684\u4e8b\u5f88\u60e8\uff0c\u662f\u4e0d\u662f\uff1f\u5546\u8001\u592a\u7684\u6027\u5b50\uff0c\u5509\uff01\u201d \u3000\u3000\u8bf4\u7740\u6447\u4e86\u6447\u5934\u3002\u80e1\u6590\u9053\uff1a\u201c\u8d75\u4f2f\u4f2f\u2026\u2026\u201d \u3000\u3000\u8d75\u534a\u5c71\u8f6c\u8fc7\u5934\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4f60\u6211\u4eca\u65e5\u840d\u6c34\u76f8\u9022\uff0c\u610f\u6c14\u76f8\u6295\uff0c\u867d\u7136\u6211\u5e74\u7eaa\u5927\u4e86\u51e0\u5c81\uff0c\u4f46\u6211\u89c1\u4f60\u4fa0\u4e49\u4ec1\u539a\uff0c\u5b9e\u662f\u76f8\u656c\u3002\u4ed6\u65e5\u4f60\u5fc5\u540d\u626c\u5929\u4e0b\uff0c\u6211\u4f55\u6562\u4ee5\u957f\u8f88\u81ea\u5c45\uff1f\u201d\u6b64\u65f6\u4e1c\u65b9\u521d\u767d\uff0c\u8d75\u534a\u5c71\u7684\u8138\u8272\u5728\u671d\u66e6\u7167\u8000\u4e4b\u4e0b\u663e\u5f97\u53c8\u662f\u5e84\u4e25\uff0c\u53c8\u662f\u8bda\u6073\u3002 \u3000\u3000\u80e1\u6590\u4e00\u5f20\u5c0f\u8138\u4e0a\u6ee1\u662f\u70ad\u7070\u8840\u6e0d\uff0c\u542c\u4e86\u4ed6\u8fd9\u51e0\u53e5\u8bdd\uff0c\u4e0d\u7981\u80c0\u5f97\u901a\u7ea2\uff0c\u53c8\u9053\uff1a\u201c\u8d75\u4f2f\u4f2f\u2026\u2026\u201d\u8d75\u534a\u5c71\u6447\u4e86\u6447\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u8d75\u4f2f\u4f2f\u4e09\u5b57\uff0c\u4eca\u540e\u4f11\u5f97\u518d\u51fa\u4f60\u53e3\u3002 \u3000\u3000\u6211\u4e0e\u4f60\u7ed3\u4e49\u4e3a\u5f02\u59d3\u5144\u5f1f\uff0c\u53ef\u597d\uff1f\u201d \u3000\u3000\u60f3\u5343\u624b\u5982\u6765\u8d75\u534a\u5c71\u5728\u6c5f\u6e56\u4e0a\u662f\u4f55\u7b49\u7684\u5a01\u540d\uff0c\u4f55\u7b49\u7684\u8eab\u5206\uff0c\u4eca\u65e5\u7ade\u8981\u4e0e\u4e00\u4e2a\u5341\u4f59\u5c81\u7684\u5b69\u7ae5\u4e49\u7ed3\u91d1\u5170\uff0c\u5b9e\u662f\u4e8b\u975e\u5bfb\u5e38\u3002\u4ed6\u5012\u4e0d\u662f\u77a7\u5728\u80e1\u6590\u6b66\u529f\u7684\u4efd\u4e0a\uff0c\u800c\u662f\u656c\u91cd\u4ed6\u820d\u8eab\u6551\u4eba\u7684\u4ec1\u4fa0\u5fc3\u80a0\uff0c\u89c9\u5f97\u4ed6\u5e74\u7eaa\u867d\u5c0f\uff0c\u4f46\u6240\u4f5c\u6240\u4e3a\uff0c\u4e0e\u7ea2\u82b1\u4f1a\u4f17\u5144\u5f1f\u5df2\u5e76\u65e0\u4e8c\u81f4\u3002 \u3000\u3000\u80e1\u6590\u542c\u4e86\u6b64\u8a00\uff0c\u4e0d\u7531\u5f97\u611f\u6fc0\u4e0d\u80dc\uff0c\u4e24\u9053\u6cea\u6c34\u4ece\u773c\u4e2d\u6d41\u4e0b\uff0c\u6251\u7ffb\u8eab\u8eaf\uff0c\u7eb3\u5934\u4fbf\u62dc\uff0c\u53eb\u9053\uff1a\u201c\u8d75\u2026\u2026\u8d75\u2026\u2026\u201d\u8d75\u534a\u5c71\u8dea\u4e0b\u7b54\u793c\uff0c\u8bf4\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u4ece\u4eca\u540e\u4f60\u53eb\u6211\u4e09\u54e5\u4fbf\u4e86\u3002\u201d \u3000\u3000\u4e8e\u662f\u4e00\u8001\u4e00\u5c11\u4e24\u4f4d\u82f1\u96c4\uff0c\u5728\u65f7\u91ce\u4e2d\u64ae\u4e0a\u4e3a\u9999\uff0c\u62dc\u4e86\u516b\u62dc\u3002 \u3000\u3000\u8d75\u534a\u5c71\u5fc3\u4e2d\u5feb\u6170\uff0c\u64ae\u53e3\u957f\u5578\uff0c\u53ea\u542c\u5f97\u897f\u9762\u9a6c\u8e44\u58f0\u6025\uff0c\u90a3\u767d\u9a6c\u594b\u9b23\u626c\u8e44\u800c\u6765\uff0c\u7247\u523b\u95f4\u5954\u5230\u4e86\u8eab\u524d\u3002\u80e1\u6590\u8d5e\u9053\uff1a\u201c\u8fd9\u9a6c\u771f\u597d\u3002\u201d\u8d75\u534a\u5c71\u5fc3\u60f3\uff1a\u201c\u53ef\u60dc\u6b64\u9a6c\u4e43\u56db\u5f1f\u59b9\u6240\u6709\uff0c\u5979\u7231\u82e5\u6027\u547d\uff0c\u5426\u5219\u7ecf\u4f60\u8fd9\u4e48\u4e00\u8d5e\uff0c\u6211\u81ea\u7136\u9001\u4f60\u3002\u201d\u5f53\u4e0b\u5fae\u5fae\u4e00\u7b11\uff0c\u4e5f\u4e0d\u89e3\u91ca\uff0c\u95ee\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u4f60\u5728\u6b64\u95f4\u53ef\u8fd8\u6709\u4ec0\u4e48\u672a\u4e86\u4e4b\u4e8b\uff1f\u201d\u80e1\u6590\u9053\uff1a\u201c\u6211\u53bb\u8ddf\u5e73\u56db\u53d4\u8bf4\u4e00\u58f0\uff0c\u5f53\u9001\u4e09\u54e5\u4e00\u7a0b\u3002\u201d\u8d75\u534a\u5c71\u4e5f\u4e0d\u820d\u5f97\u7acb\u5373\u4e0e\u4ed6\u5206\u522b\uff0c\u9053\uff1a\u201c\u90a3\u518d\u597d\u6ca1\u6709\u3002\u201d \u3000\u3000\uff20\uff20\u7275\u4e86\u7586\u7ef3\uff0c\u548c\u80e1\u6590\u5e76\u80a9\u800c\u884c\u3002 \u3000\u3000\u8f6c\u8fc7\u4e00\u4e2a\u5c71\u5761\uff0c\u5ffd\u89c1\u4e00\u682a\u5927\u6811\u540e\u9762\u7ad9\u7740\u4e00\u4eba\uff0c\u63a2\u5934\u63a2\u8111\u5730\u5728\u4e0d\u4f4f\u7aa5\u63a2\u3002 \u3000\u3000\u80e1\u6590\u8ba4\u5f97\u4ed6\u7684\u80cc\u5f71\uff0c\u4f4e\u58f0\u9053\uff1a\u201d\u8fd9\u662f\u5f90\u94ee\uff01\u201d\u5fc3\u60f3\u4ed6\u5e08\u7236\u60e8\u906d\u711a\u6b7b\uff0c\u4ed6\u8eb2\u5728\u6b64\u5904\u4e0d\u77e5\u9b3c\u9b3c\u795f\u795f\u5730\u5e72\u4ec0\u4e48\u52fe\u5f53\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u8fc7\u53bb\u77a7\u77a7\u3002\u201d\u6084\u6084\u8d70\u4e0a\u524d\u53bb\uff0c\u5728\u4ed6\u8eab\u540e\u5411\u524d\u4e00\u5f20\u3002\u5f90\u6323\u6b63\u77a7\u5f97\u51fa\u795e\uff0c\u4e0d\u77e5\u8eab\u540e\u6765\u4e86\u65c1\u4eba\u3002 \u3000\u3000\u53ea\u89c1\u524d\u9762\u4e8c\u5341\u4f59\u4e08\u4e00\u682a\u6768\u6811\u4e4b\u4e0b\uff0c\u4e00\u7537\u4e00\u5973\uff0c\u76f8\u4e92\u504e\u501a\u5728\u4e00\u8d77\uff0c\u795e\u60c5\u5f02\u5e38\u4eb2\u5bc6\u3002\u80e1\u6590\u51dd\u4f38\u4e00\u770b\uff0c\u539f\u6765\u7537\u7684\u662f\u5546\u5bb6\u5821\u4f5c\u5ba2\u7684\u798f\u516c\u5b50\uff0c\u5973\u7684\u7adf\u662f\u9a6c\u6625\u82b1\u3002 \u3000\u3000\u4f46\u89c1\u798f\u516c\u5b50\u4e00\u624b\u6402\u7740\u5979\u8170\uff0c\u4e0d\u4f4f\u4eb2\u5979\u9762\u988a\u3002\u9a6c\u6625\u82b1\u8f6f\u6d0b\u6d0b\u5730\u9760\u5728\u4ed6\u6000\u91cc\uff0c\u4f4e\u58f0\u4e0d\u77e5\u8bf4\u4e9b\u4ec0\u4e48\u3002\u80e1\u6590\u5e74\u5e7c\uff0c\u8fd8\u4e0d\u5927\u660e\u767d\u7537\u5973\u4e4b\u4e8b\uff0c\u53ea\u662f\u77a7\u5f97\u6709\u8da3\uff0c\u5fc3\u4e2d\u6697\u6697\u597d\u7b11\uff1a\u201c\u9a6c\u59d1\u5a18\u548c\u8fd9\u516c\u5b50\u53ea\u76f8\u8bc6\u4e00\u5929\uff0c\u4fbf\u8fd9\u822c\u8981\u597d\u3002\u201d\u5374\u542c\u5f97\u5f90\u94ee\u53e3\u4e2d\u53d1\u51fa\u53fd\u53fd\u683c\u683c\u7684\u602a\u58f0\uff0c\u539f\u6765\u662f\u5728\u54ac\u7259\u5207\u9f7f\uff0c\u53c8\u4e3e\u8d77\u62f3\u5934\uff0c\u4e0d\u4f4f\u6376\u6253\u81ea\u5df1\u80f8\u53e3\uff0c\u5df2\u662f\u6124\u6012\u5230\u4e86\u6781\u70b9\u3002 \u3000\u3000\u80e1\u6590\u7b11\u9053\uff1a\u201c\u5f90\u5927\u54e5\uff0c\u4f60\u5728\u8fd9\u91cc\u5e72\u4ec0\u4e48\uff1f\u201d\u5f90\u94ee\u5168\u795e\u8d2f\u6ce8\u5728\u9a6c\u6625\u82b1\u8eab\u4e0a\uff0c\u5bf9\u80e1\u6590\u7684\u8bdd\u7adf\u662f\u5168\u6ca1\u542c\u89c1\u3002\u7a81\u7136\u4e4b\u95f4\uff0c\u4ed6\u5927\u53eb\u4e00\u58f0\uff1a\u201c\u6211\u548c\u4f60\u62da\u4e86\uff01\u201d\u62d4\u51fa\u8170\u95f4\u5355\u5200\uff0c\u5411\u798f\u516c\u5b50\u51b2\u53bb\u3002 \u3000\u3000\u80e1\u6590\u867d\u7136\u806a\u660e\u4f36\u4fd0\uff0c\u5bf9\u8fd9\u79cd\u79c1\u60c5\u7ea0\u845b\u5374\u662f\u5168\u7136\u4e0d\u89e3\uff0c\u9690\u9690\u7ea6\u7ea6\u53ea\u77e5\u9053\u9a6c\u6625\u82b1\u751f\u5f97\u7f8e\u4e3d\uff0c\u6240\u4ee5\u524d\u65e5\u665a\u95f4\u5546\u5b9d\u9707\u5bf9\u5979\u8fd9\u6837\uff0c\u800c\u4eca\u65e5\u798f\u516c\u5b50\u548c\u5f90\u94ee\u53c8\u662f\u4e3a\u5979\u6253\u67b6\u3002 \u3000\u3000\u798f\u516c\u5b50\u548c\u9a6c\u6625\u82b1\u5728\u5927\u5385\u4e0a\u6e9c\u4e86\u51fa\u6765\uff0c\u552f\u6050\u7ed9\u4eba\u89c1\u5230\uff0c\u8fdc\u8fdc\u8eb2\u5230\u8fd9\u682a\u5927\u6768\u6811\u4e0b\u504e\u501a\u871c\u8bed\u3002\u7537\u6b22\u5973\u7231\uff0c\u4e0d\u77e5\u4e1c\u65b9\u4e4b\u65e2\u767d\u3002\u5546\u5bb6\u5821\u95f9\u5f97\u5929\u7ffb\u5730\u8986\uff0c\u4ed6\u4e8c\u4eba\u7adf\u662f\u534a\u70b9\u4e5f\u4e0d\u77e5\u9053\uff0c\u7a81\u89c1\u5f90\u94ee\u5168\u8eab\u70e7\u7126\u3001\u62ab\u5934\u6563\u53d1\u5730\u63d0\u5200\u6740\u6765\uff0c\u540c\u65f6\u5927\u60ca\u7ad9\u8d77\u3002 \u3000\u3000\u5f90\u94ee\u53cc\u76ee\u5982\u6b32\u55b7\u51fa\u706b\u6765\uff0c\u8fd9\u4e00\u5200\u780d\u4e0b\u53bb\u529b\u9053\u6781\u731b\u3002\u798f\u516c\u5b50\u6b66\u827a\u5e73\u5eb8\uff0c\u773c\u89c1\u94a2\u5200\u8fce\u5934\u780d\u5230\uff0c\u6025\u5fd9\u540e\u9000\u3002\u5f90\u94ee\u8fd9\u4e00\u5200\u7528\u529b\u5927\u4e86\uff0c\u767b\u7684\u4e00\u58f0\u5374\u780d\u5728\u5927\u6768\u6811\u4e0a\uff0c\u6025\u5207\u95f4\u62d4\u4e0d\u51fa\u6765\u3002\u9a6c\u6625\u82b1\u6025\u9053\uff1a\u201c\u4f60\u5e72\u4ec0\u4e48\uff1f\u4f60\u5e72\u4ec0\u4e48\uff1f\u201d\u5f90\u94ee\u6012\u559d\uff1a \u3000\u3000\u201c\u5e72\u4ec0\u4e48\uff1f\u6211\u8981\u6740\u4e86\u8fd9\u5c0f\u5b50\uff01\u201d\u7528\u529b\u4e00\u62d4\uff0c\u90a3\u5200\u8131\u5374\u6768\u6811\uff0c\u53cd\u5f39\u4e0a\u6765\uff0c\u7830\u7684\u4e00\u4e0b\uff0c\u5200\u80cc\u649e\u4e0a\u4ed6\u7684\u989d\u5934\u3002 \u3000\u3000\u9a6c\u6625\u82b1\u5403\u4e86\u4e00\u60ca\uff0c\u53eb\u9053\uff1a\u201c\u5c0f\u5fc3\uff01\u53ef\u649e\u75db\u4e86\u4e48\uff1f\u201d\u5f90\u94ee\u4f38\u624b\u4f7f\u52b2\u5c06\u5979\u63a8\u5f00\uff0c\u9053\uff1a\u201c\u4e0d\u7528\u4f60\u5047\u60fa\u60fa\u505a\u597d\u4eba\u3002\u201d\u8ddf\u7740\u8d76\u4e0a\u524d\u53bb\uff0c\u4e3e\u5200\u53c8\u5411\u798f\u516c\u5b50\u780d\u4e0b\u3002 \u3000\u3000\u9a6c\u6625\u82b1\u89c1\u8fd9\u4e2a\u5e73\u65e5\u5bf9\u81ea\u5df1\u4ece\u6765\u4e0d\u6562\u8fdd\u62d7\u534a\u70b9\u7684\u5e08\u54e5\uff0c\u6b64\u65f6\u7a81\u7136\u53d1\u75af\u4e00\u822c\uff0c\u77e5\u4ed6\u5992\u706b\u4e2d\u70e7\uff0c\u4e0d\u53ef\u6291\u5236\uff0c\u5fc3\u4e2d\u53c8\u662f\u7f9e\u6127\uff0c\u53c8\u662f\u7126\u6025\uff0c\u62a2\u8fc7\u53bb\u62e6\u5728\u4ed6\u9762\u524d\uff0c\u53cc\u624b\u53c9\u8170\uff0c\u8bf4\u9053\uff1a\u201c\u5e08\u54e5\uff0c\u4f60\u8981\u6740\u4eba\uff0c\u5148\u6740\u4e86\u6211\u5427\u3002\u201d \u3000\u3000\u5f90\u94ee\u89c1\u5979\u4e00\u610f\u7ef4\u62a4\u798f\u516c\u5b50\uff0c\u66f4\u662f\u5927\u6012\u82e5\u72c2\uff0c\u5389\u58f0\u9053\uff1a\u201c\u6211\u5148\u6740\u4ed6\uff0c\u518d\u6765\u6740\u4f60\u3002\u201d\u5de6\u624b\u5728\u5979\u80a9\u5934\u4e00\u63a8\u3002\u9a6c\u6625\u82b1\u4e00\u4e2a\u8e09\u8dc4\uff0c\u9669\u9669\u8dcc\u5012\uff0c\u968f\u624b\u62a2\u8d77\u5730\u4e0b\u4e00\u6839\u6854\u679d\uff0c\u6321\u67b6\u4ed6\u7684\u5355\u5200\uff0c\u4e00\u9762\u8f6c\u5934\u5411\u798f\u516c\u5b50\u53eb\u9053\uff1a\u201d\u4f60\u5feb\u8d70\uff0c\u5feb\u8d70\u554a\u3002\u201d\u798f\u516c\u5b50\u4e0d\u77e5\u5979\u548c\u5f90\u94ee\u4e43\u662f\u672a\u5a5a\u592b\u5987\uff0c\u5927\u58f0\u9053\uff1a\u201c\u8fd9\u4eba\u75af\u4e86\uff0c\u4f60\u53ef\u8981\u5c0f\u5fc3\u3002\u201d\u4e00\u9762\u8fdc\u8fdc\u8eb2\u5f00\u3002 \u3000\u3000\u5f90\u6323\u821e\u52a8\u5355\u5200\uff0c\u6570\u62db\u4e4b\u95f4\uff0c\u5df2\u5c06\u9a6c\u6625\u82b1\u624b\u4e2d\u6854\u679d\u780d\u65ad\uff0c\u559d\u9053\uff1a\u201c\u4f60\u518d\u4e0d\u8ba9\u5f00\uff0c\u53ee\u83ab\u602a\u6211\u65e0\u60c5\u4e86\u3002\u201d\u9a6c\u6625\u82b1\u5c06\u534a\u622a\u6854\u679d\u5f80\u5730\u4e0b\u4e00\u4e22\uff0c\u8f6c\u8fc7\u4e86\u5934\uff0c\u5c06\u8116\u5b50\u5411\u7740\u4ed6\u5200\u53e3\uff0c\u8bf4\u9053\uff1a\u201c\u5e08\u54e5\uff0c\u8fd9\u4e00\u751f\u4e00\u4e16\uff0c\u6211\u7ec8\u7a76\u662f\u4e0d\u80fd\u505a\u4f60\u59bb\u5b50\u7684\u4e86\u3002 \u3000\u3000\u4f60\u4e00\u5200\u5c06\u6211\u6740\u4e86\u5427\u3002\u201d\u5f90\u94ee\u6ee1\u8138\u7d2b\u80c0\uff0c\u6012\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u2026\u2026\u201d\u5de6\u624b\u7528\u529b\u6293\u80f8\uff0c\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u80e1\u6590\u89c1\u4ed6\u5355\u5200\u4e0a\u4e0b\u6325\u8361\uff0c\u795e\u8272\u72c2\u6012\uff0c\u53ea\u6015\u4e00\u4e2a\u514b\u5236\u4e0d\u4f4f\uff0c\u987a\u624b\u4fbf\u5f80\u9a6c\u6625\u82b1\u8eab\u4e0a\u780d\u4e86\u4e0b\u53bb\uff0c\u5f53\u5373\u62a2\u4e0a\u524d\u53bb\uff0c\u9694\u5728\u4e8c\u4eba\u4e4b\u95f4\uff0c\u5de6\u638c\u8d77\u5904\uff0c\u5df2\u6309\u5728\u5f90\u94ee\u80f8\u524d\uff0c\u5fae\u4e00\u53d1\u52b2\uff0c\u5c06\u4ed6\u63a8\u5f97\u9000\u540e\u4e09\u6b65\uff0c\u7b11\u9053\uff1a\u201c\u5f90\u5927\u54e5\uff0c\u5929\u4e0b\u6709\u8c01\u60f3\u52a8\u9a6c\u59d1\u5a18\u4e00\u6839\u6beb\u6bdb\uff0c\u9664\u975e\u5148\u5c06\u6211\u80e1\u6590\u6740\u4e86\u201d\u5f90\u6323\u4e00\u6115\uff0c\u6012\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u2026\u2026\u8fde\u4f60\u8fd9\u4e73\u81ed\u672a\u5e72\u7684\u5b69\u5b50\uff0c\u5979\u4e5f\u52fe\u642d\u4e0a\u4e86\uff1f\u201d \u3000\u3000\u53ea\u542c\u556a\u7684\u4e00\u58f0\uff0c\u9a6c\u6625\u82b1\u7eb5\u4e0a\u524d\u6765\u6253\u4e86\u4ed6\u4e00\u8bb0\u8033\u5149\u3002\u5f90\u94ee\u4e00\u6765\u662f\u76db\u6012\u4e4b\u4e0b\u795e\u667a\u4e0d\u6e05\uff0c\u4e8c\u6765\u80e1\u6590\u5939\u5728\u4e2d\u95f4\uff0c\u6321\u4f4f\u4e86\u4ed6\u7684\u773c\u5149\uff0c\u8fd9\u4e00\u5df4\u638c\u7adf\u662f\u6ca1\u80fd\u907f\u5f00\uff0c\u7ed3\u7ed3\u5b9e\u5b9e\u5730\uff0c\u6253\u5f97\u4ed6\u534a\u8fb9\u8138\u988a\u4e5f\u80bf\u4e86\u3002 \u3000\u3000\u80e1\u6590\u5374\u4e0d\u61c2\u5f90\u94ee\u8fd9\u53e5\u8bdd\u662f\u4ec0\u4e48\u610f\u601d\uff0c\u4e5f\u4e0d\u660e\u767d\u9a6c\u6625\u82b1\u4f55\u4ee5\u5927\u6012\u3002\u5728\u4ed6\u5fc3\u4e2d\uff0c\u81ea\u5df1\u7ed9\u5546\u8001\u592a\u64d2\u4f4f\u62f7\u6253\u4e4b\u65f6\uff0c\u9a6c\u6625\u82b1\u66fe\u5411\u5546\u5b9d\u9707\u6c42\u60c5\uff0c\u540e\u6765\u53c8\u6c42\u4ed6\u91ca\u653e\u81ea\u5df1\uff0c\u867d\u7136\u81ea\u5df1\u5df2\u7ecf\u5148\u8131\u6346\u7f1a\uff0c\u4f46\u5bf9\u5979\u8fd9\u756a\u7737\u5ff5\u4e4b\u6069\uff0c\u5374\u662f\u94ed\u611f\u4e8e\u5fc3\u3002\u6b64\u65f6\u9a6c\u6625\u82b1\u4e0e\u5e08\u54e5\u8d77\u4e86\u4e89\u6267\uff0c\u4ed6\u81ea\u662f\u5168\u529b\u7ef4\u62a4\u3002 \u3000\u3000\u5f90\u6323\u89c1\u8fc7\u80e1\u6590\u4e0e\u738b\u6c0f\u5144\u5f1f\u52a8\u624b\uff0c\u8bba\u5230\u6b66\u529f\uff0c\u81ea\u77e5\u4e0e\u4ed6\u53ef\u5dee\u5f97\u592a\u8fdc\uff0c\u4f46\u5fc3\u60c5\u6fc0\u52a8\u4e4b\u4e0b\uff0c\u8fde\u6027\u547d\u4e5f\u4e0d\u7406\u4f1a\u4e86\uff0c\u8fd8\u987e\u4ec0\u4e48\u80dc\u8d1f\uff1f\u4e00\u67c4\u5355\u5200\u76f4\u4e0a\u76f4\u4e0b\u5730\u5f80\u4ed6\u5934\u4e0a\u3001\u9888\u4e2d\u3001\u80a9\u5934\u8fde\u8fde\u780d\u53bb\u3002\u80e1\u6590\u65e2\u4e0d\u8fc8\u6b65\uff0c\u4ea6\u4e0d\u540e\u9000\uff0c\u53ea\u662f\u7ad9\u5728\u5f53\u5730\uff0c\u5728\u4ed6\u5200\u7f1d\u95f4\u4fa7\u8eab\u95ea\u907f\uff0c\u7a81\u7136\u5de6\u624b\u4f38\u51fa\uff0c\u4e00\u62f3\u5411\u4ed6\u9f3b\u6881\u6253\u53bb\u3002\u5f90\u94ee\u4e3e\u5200\u6a2a\u524a\uff0c\u65ab\u4ed6\u624b\u81c2\u3002\u80e1\u6590\u8fd9\u4e00\u62f3\u6253\u5230\u4e00\u534a\uff0c\u624b\u81c2\u62d0\u5f2f\uff0c\u7ffb\u638c\u6293\u4f4f\u4ed6\u624b\u8155\uff0c\u987a\u52bf\u4e00\u626d\uff0c\u5df2\u5c06\u5355\u5200\u593a\u5728\u624b\u4e2d\uff0c\u8ddf\u7740\u8f6c\u8fc7\u8eab\u53bb\uff0c\u5c06\u5200\u4ea4\u7ed9\u9a6c\u6625\u82b1\u3002\u4ed6\u5c06\u80cc\u810a\u5411\u7740\u5f90\u6323\uff0c\u5f53\u771f\u662f\u827a\u9ad8\u4eba\u80c6\u5927\uff0c\u5bf9\u4e4b\u4e1d\u6beb\u4e0d\u52a0\u63d0\u9632\u3002 \u3000\u3000\u5f90\u94ee\u77e5\u9053\u518d\u6597\u4e5f\u662f\u65e0\u7528\uff0c\u957f\u53f9\u4e00\u58f0\uff0c\u518d\u4e5f\u5fcd\u8010\u4e0d\u4f4f\uff0c\u5ffd\u5730\u5927\u653e\u60b2\u58f0\uff0c\u53eb\u9053\uff1a\u201c\u5e08\u7236\uff0c\u5e08\u7236\uff0c\u4f60\u8001\u4eba\u5bb6\u6b7b\u5f97\u597d\u60e8\u3002\u201d\u56de\u8eab\u63a9\u9762\u4fbf\u8d70\u3002 \u3000\u3000\u9a6c\u6625\u82b1\u731b\u5403\u4e00\u60ca\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u201d\u63d0\u5200\u8d76\u53bb\u3002\u5f90\u94ee\u4e0d\u7b54\uff0c\u4f4e\u9996\u75be\u884c\u3002\u9a6c\u6625\u82b1\u8fde\u95ee\uff1a\u201c\u7239\u7239\u600e\u4e48\u4e86\uff1f\u4f60\u8bf4\u4ec0\u4e48\u6b7b\u5f97\u597d\u60e8\uff1f\u201d\u4e00\u8def\u5728\u540e\u9762\u8ffd\u8d76\u3002 \u3000\u3000\u798f\u516c\u5b50\u7ad9\u5f97\u8fdc\u8fdc\u7684\uff0c\u6ca1\u542c\u6e05\u695a\u4ed6\u5e08\u5144\u59b9\u7684\u5bf9\u7b54\uff0c\u53ea\u89c1\u9a6c\u6625\u82b1\u8ffd\u8d76\u5f90\u94ee\u800c\u53bb\uff0c\u5fc3\u4e2d\u6025\u4e86\uff0c\u53eb\u9053\uff1a\u201c\u6625\u59b9\uff0c\u6625\u59b9\uff0c\u56de\u6765\uff0c\u522b\u7406\u4ed6\u3002\u201d\u9a6c\u6625\u82b1\u6302\u5ff5\u7236\u4eb2\uff0c\u4e0d\u7406\u4f1a\u798f\u516c\u5b50\u7684\u53eb\u558a\uff0c\u53ea\u662f\u8ffd\u95ee\u5f90\u94ee\u3002\u798f\u516c\u5b50\u89c1\u94a2\u5200\u5df2\u5230\u4e86\u9a6c\u6625\u82b1\u624b\u4e2d\uff0c\u4e0d\u518d\u60e7\u6015\u5f90\u94ee\uff0c\u5feb\u6b65\u8d76\u4e0a\u3002 \u3000\u3000\u8ffd\u51fa\u5341\u4f59\u6b65\uff0c\u5ffd\u89c1\u4e00\u682a\u5927\u6811\u540e\u8f6c\u51fa\u4e00\u4eba\uff0c\u4e94\u5341\u4f59\u5c81\u5e74\u7eaa\uff0c\u8eab\u5f62\u5fae\u80d6\uff0c\u5507\u7559\u5fae\u9aed\uff0c\u6b63\u662f\u7ea2\u82b1\u4f1a\u7684\u4e09\u5f53\u5bb6\u5343\u624b\u5982\u6765\u8d75\u534a\u5c71\u3002 \u3000\u3000\u798f\u516c\u5b50\u548c\u4ed6\u4e00\u671d\u76f8\uff0c\u53ea\u5413\u5f97\u9762\u5982\u4e0a\u8272\uff0c\u534a\u664c\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u8d75\u534a\u5c71\u7b11\u9053\uff1a\u201c\u798f\u516c\u5b50\uff0c\u4f60\u597d\u554a\uff01\u201d\u798f\u516c\u5b50\u53cc\u624b\u4e00\u62f1\uff0c\u52c9\u5f3a\u9053\uff1a\u201c\u8d75\u4e09\u5f53\u5bb6\uff0c\u4f60\u597d\u3002\u201d\u518d\u4e5f\u987e\u4e0d\u5f97\u9a6c\u6625\u82b1\u5982\u4f55\uff0c\u8f6c\u8fc7\u8eab\u6765\uff0c\u98de\u6b65\u4fbf\u884c\uff0c\u4e00\u76f4\u5954\u51fa\u5341\u4f59\u4e08\uff0c\u56de\u5934\u5411\u8d75\u534a\u5c71\u4e00\u671b\uff0c\u811a\u6b65\u66f4\u52a0\u5feb\u4e86\u3002 \u3000\u3000\u970e\u65f6\u4e4b\u95f4\uff0c\u798f\u516c\u5b50\u5411\u5317\uff0c\u5f90\u94ee\u4e0e\u9a6c\u6625\u82b1\u5411\u5357\uff0c\u4ff1\u5df2\u5954\u5f97\u5f71\u8e2a\u4e0d\u89c1\uff0c\u53ea\u6709\u8d75\u534a\u5c71\u8138\u5e26\u5fae\u7b11\uff0c\u80e1\u6590\u795e\u8272\u8ff0\u832b\uff0c\u76f8\u5411\u7ad9\u5728\u9ad8\u5761\u4e4b\u4e0a\u3002 \u3000\u3000\u80e1\u6590\u9053\uff1a\u201c\u4e09\u54e5\uff0c\u8fd9\u798f\u516c\u5b50\u8ba4\u5f97\u4f60\u554a\uff0c\u4ed6\u597d\u50cf\u5f88\u6015\u4f60\u3002\u201d\u8d75\u534a\u5c71\u5fae\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4ed6\u66fe\u843d\u5728\u6211\u4eec\u624b\u4e2d\uff0c\u5f88\u5403\u4e86\u4e9b\u82e6\u5934\u3002\u201d \u3000\u3000\u539f\u6765\u8fd9\u798f\u516c\u5b50\uff0c\u6b63\u662f\u5f53\u4eca\u4e7e\u9686\u7687\u5e1d\u9a7e\u524d\u7b2c\u4e00\u7ea2\u4eba\u798f\u5eb7\u5b89\u3002\u4ed6\u662f\u4e7e\u9686\u7684\u79c1\u751f\u513f\u5b50\uff0c\u662f\u4ee5\u7687\u5e1d\u5bf9\u4ed6\u6069\u9047\u9686\u539a\uff0c\u7fa4\u81e3\u83ab\u53ca\u3002\u4ed6\u66fe\u88ab\u7ea2\u82b1\u4f1a\u7fa4\u96c4\u64d2\u4f4f\uff0c\u903c\u5f97\u4e7e\u9686\u91cd\u4fee\u5c11\u6797\u5bfa\uff0c\u4e0d\u6562\u4e0e\u7ea2\u82b1\u4f1a\u4e3a\u96be\u3002\u6b64\u65f6\u4e8b\u9694\u6570\u5e74\uff0c\u5ffd\u7136\u53c8\u4e0e\u8d75\u534a\u5c71\u76f8\u9047\uff0c\u4ed6\u53ea\u9053\u7ea2\u82b1\u4f1a\u7fa4\u96c4\u4ece\u56de\u7586\u5927\u4e3e\u4e1c\u6765\uff0c\u53ea\u5413\u5f97\u9b42\u98de\u9b44\u6563\uff0c\u54ea\u6562\u518d\u8ffd\u67e5\u9a6c\u6625\u82b1\u5230\u4e86\u4f55\u5904\uff1f\u4e0e\u738b\u5251\u82f1\u7b49\u4f1a\u5408\u540e\uff0c\u7247\u523b\u4e0d\u6562\u505c\u7559\uff0c\u6025\u6025\u56de\u5317\u4eac\u53bb\u4e86\u3002 \u3000\u3000\u80e1\u6590\u89c1\u798f\u5eb7\u5b89\u4e0d\u4f1a\u6b66\u827a\uff0c\u5bf9\u4ed6\u672a\u52a0\u7559\u610f\uff0c\u6ca1\u518d\u8ffd\u95ee\u4ed6\u7684\u6765\u5386\u3002\u8d75\u534a\u5c71\u4f38\u51fa\u53f3\u624b\uff0c\u63e1\u4f4f\u4ed6\u624b\uff0c\u4e8c\u4eba\u643a\u624b\u540c\u884c\uff0c\u8d70\u4e86\u91cc\u8bb8\uff0c\u6765\u5230\u8def\u65c1\u4e00\u6240\u8336\u94fa\u4e4b\u524d\u3002\u8d75\u534a\u5c71\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u9001\u541b\u5343\u91cc\uff0c\u7ec8\u987b\u4e00\u522b\uff0c\u4f60\u6211\u5c31\u6b64\u522b\u8fc7\u3002\u201d\u80e1\u6590\u867d\u662f\u604b\u604b\u4e0d\u820d\uff0c\u4f46\u4ed6\u662f\u8c41\u8fbe\u8c6a\u8fc8\u4e4b\u4eba\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u4e09\u54e5\uff0c\u8fc7\u51e0\u5e74\u7b49\u6211\u957f\u5f97\u51e0\u5c81\uff0c\u5230\u56de\u7586\u6765\u5bfb\u4f60\u76f8\u4f1a\u3002\u201d\u8d75\u534a\u5c71\u70b9\u5934\u9053\uff1a\u201c\u6211\u5728\u56de\u7586\u7b49\u4f60\u4fbf\u4e86\u3002\u201d\u8bf4\u7740\u4ece\u6000\u4e2d\u53d6\u51fa\u4e00\u6735\u7ea2\u7ed2\u624e\u6210\u7684\u5927\u7ea2\u82b1\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u5929\u4e0b\u6c5f\u6e56\u597d\u6c49\uff0c\u4e00\u89c1\u6b64\u82b1\uff0c\u4fbf\u77e5\u662f\u4f60\u4e09\u54e5\u7684\u4fe1\u7269\u3002\u4f60\u82e5\u9047\u4e0a\u6025\u9700\uff0c\u8981\u4eba\u8981\u94b1\uff0c\u51ed\u7740\u6b64\u82b1\uff0c\u5411\u5404\u5904\u670b\u53cb\u5c3d\u7ba1\u8981\u4fbf\u662f\u3002\u201d \u3000\u3000\u80e1\u6590\u63a5\u8fc7\u4e86\u653e\u5728\u6000\u5185\uff0c\u597d\u751f\u7fa1\u6155\uff0c\u5fc3\u60f3\u65e5\u540e\u5b66\u5230\u4e09\u54e5\u7684\u672c\u9886\u672a\u5fc5\u4e3a\u96be\uff0c\u4f46\u8981\u5b66\u5230\u4ed6\u670b\u53cb\u904d\u5929\u4e0b\u7684\u4ea4\u60c5\uff0c\u5374\u662f\u5927\u5927\u7684\u4e0d\u6613\u3002\u8d75\u534a\u5c71\u5230\u8336\u94fa\u5012\u4e86\u4e24\u5927\u7897\u8336\uff0c\u5c06\u4e00\u7897\u9012\u7ed9\u80e1\u6590\uff0c\u8bf4\u9053\uff1a\u201c\u4ee5\u8336\u4ee3\u9152\uff0c\u4f60\u6211\u559d\u4e86\u8fd9\u7897\u522b\u9152\u5427\u3002\u201d\u4e8c\u4eba\u4e3e\u8d77\u7897\u6765\uff0c\u4ef0\u5934\u996e\u5e72\u3002 \u3000\u3000\u8d75\u534a\u5c71\u6401\u4e0b\u8336\u7897\uff0c\u4e00\u624b\u7275\u4f4f\u9a6c\u7586\uff0c\u8bf4\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u4e34\u522b\u4e4b\u9645\uff0c\u505a\u54e5\u54e5\u7684\u95ee\u4f60\u4e00\u53e5\u8bdd\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u4e09\u54e5\u8bf7\u95ee\u4fbf\u662f\u3002\u201d\u8d75\u534a\u5c71\u9053\uff1a\u201c\u9664\u4e86\u5546\u5bb6\u5821\u4e4b\u5916\uff0c\u8d24\u5f1f\u662f\u5426\u8fd8\u6709\u4ec0\u4e48\u5389\u5bb3\u7684\u4ec7\u4eba\u5bf9\u5934\uff1f\u201d\u80e1\u6590\u4e00\u51db\uff0c\u5fc3\u9053\uff1a\u201c\u6211\u7239\u7239\u4e0d\u77e5\u662f\u8c01\u5bb3\u7684\uff0c\u6b64\u4eba\u65e2\u6740\u5f97\u6211\u7239\u7239\uff0c\u81ea\u7136\u6b66\u529f\u975e\u540c\u5c0f\u53ef\u3002\u82e5\u662f\u4e09\u54e5\u77e5\u6211\u5927\u4ec7\u672a\u62a5\uff0c\u7ade\u67e5\u5230\u6211\u4ec7\u4eba\u7684\u59d3\u540d\uff0c\u4ed6\u4e49\u6c14\u4e3a\u91cd\uff0c\u524d\u53bb\u627e\u4ed6\u62fc\u6597\uff0c\u4e00\u6765\u6211\u6740\u7236\u5927\u4ec7\u4e0d\u80fd\u53eb\u4eba\u4ee3\u62a5\uff0c\u4e8c\u6765\u7109\u80fd\u8ba9\u4e09\u54e5\u5192\u6b64\u51f6\u9669\uff1f\u201d\u4ed6\u5e74\u7eaa\u867d\u5c0f\uff0c\u5374\u662f\u6ee1\u8179\u7684\u50b2\u6c14\uff0c\u4ef0\u5934\u9053\uff1a\u201c\u4e0d\u52b3\u4e09\u54e5\u6302\u6000\uff0c\u4fbf\u662f\u6709\u4ec0\u4e48\u4ec7\u654c\u5bf9\u5934\uff0c\u5c0f\u5f1f\u4e5f\u6599\u7406\u5f97\u4e86\u3002\u201d\u8d75\u534a\u5c71\u54c8\u54c8\u5927\u7b11\uff0c\u7fd8\u8d77\u5927\u62c7\u6307\u8d5e\u9053\uff1a\u201c\u597d\uff01\u201d\u98de\u8eab\u4e0a\u9a6c\uff0c\u5411\u897f\u75be\u9a70\u800c\u53bb\uff0c\u53ea\u542c\u4ed6\u8fdc\u8fdc\u8bf4\u9053\uff1a\u201c\u77f3\u4e0a\u7684\u5c0f\u5305\uff0c\u54e5\u54e5\u9001\u4e86\u7ed9\u4f60\u3002\u201d \u3000\u3000\u80e1\u6590\u56de\u8fc7\u5934\u6765\uff0c\u53ea\u89c1\u5927\u77f3\u4e0a\u653e\u7740\u4e00\u4e2a\u5305\u88f9\uff0c\u672c\u6765\u662f\u8d75\u534a\u5c71\u6302\u5728\u767d\u9a6c\u80cc\u4e0a\u7684\u3002\u4ed6\u4f38\u624b\u4e00\u63d0\uff0c\u53ea\u89c9\u6c89\u7538\u7538\u7684\u6709\u4e9b\u538b\u624b\uff0c\u6025\u5fd9\u89e3\u5f00\uff0c\u4f46\u89c1\u91d1\u5149\u8000\u773c\uff0c\u5374\u662f\u4e8c\u5341\u679a\u4e8c\u5341\u4e24\u91cd\u7684\u91d1\u952d\uff0c\u4e00\u5171\u662f\u9ec4\u91d1\u56db\u767e\u4e24\u3002\u80e1\u6590\u54c8\u54c8\u4e00\u7b11\uff0c\u5fc3\u9053\uff1a\u201c\u6211\u8d2b\u4f60\u5bcc\uff0c\u82e5\u662f\u8d60\u6211\u9ec4\u91d1\uff0c\u6211\u4e5f\u4e0d\u80fd\u62d2\u5374\u3002\u4e09\u54e5\u6015\u6211\u63a8\u8f9e\uff0c\u8d60\u91d1\u4e4b\u540e\u6025\u6025\u9a70\u8d70\uff0c\u672a\u514d\u5c06\u6211\u80e1\u6590\u5f53\u4f5c\u5c0f\u5b69\u5b50\u4e86\u3002\u201d \u3000\u3000\u56de\u5934\u671b\u89c1\u9a6c\u8e44\u6e85\u8d77\u4e00\u8def\u5c18\u571f\uff0c\u6570\u91cc\u4e0d\u6b47\uff0c\u60f3\u8d77\u4eca\u65e5\u7adf\u4ea4\u4e0a\u4e86\u8fd9\u6837\u4e00\u4f4d\u809d\u80c6\u76f8\u7167\u7684\u597d\u53cb\uff0c\u4e0d\u7531\u5f97\u559c\u4e0d\u81ea\u80dc\uff0c\u63d0\u4e86\u9ec4\u91d1\uff0c\u9ad8\u58f0\u5531\u7740\u5c71\u6b4c\uff0c\u5927\u8e0f\u6b65\u800c\u884c\u3002 \u3000\u3000\u80e1\u6590\u627e\u7740\u5e73\u963f\u56db\u540e\uff0c\u5206\u4e86\u4e8c\u767e\u4e24\u9ec4\u91d1\u7ed9\u4ed6\uff0c\u8981\u4ed6\u56de\u6ca7\u5dde\u5c45\u4f4f\uff0c\u81ea\u5df1\u5374\u9080\u6e38\u5929\u4e0b\uff0c\u6bcf\u65e5\u91cc\u4e60\u62f3\u7ec3\u5200\uff0c\u6253\u71ac\u6c14\u529b\uff0c\u53c2\u7167\u8d75\u534a\u5c71\u6240\u6388\u7684\u6b66\u5b66\u8981\u8bc0\uff0c\u94bb\u7814\u62f3\u7ecf\u5200\u8c31\u4e0a\u7684\u5bb6\u4f20\u6b66\u529f\u3002 \u3000"}),showOnHomepage:!0},{name:"\u7b2c\u4e94\u7ae0\u3000\u3000\u8840\u5370\u77f3",content:(0,a.jsx)(a.Fragment,{children:"\u6570\u5e74\u4e4b\u95f4\uff0c\u4ed6\u8eab\u6750\u957f\u9ad8\u4e86\uff0c\u529b\u6c14\u957f\u5927\u4e86\uff0c\u89c1\u8bc6\u6b66\u529f\uff0c\u4e5f\u662f\u4e0e\u65e5\u4ff1\u8fdb\u3002\u56db\u6d77\u4e3a\u5bb6\uff0c\u5012\u4e5f\u60a0\u7136\u81ea\u5f97\uff0c\u5230\u5904\u884c\u4fa0\u4ed7\u4e49\uff0c\u6276\u5371\u6d4e\u56f0\uff0c\u5374\u4e5f\u8bf4\u4e0d\u5c3d\u8fd9\u8bb8\u591a\u3002\u53ea\u662f\u4ed6\u51fa\u624b\u8c6a\u9614\uff0c\u8d75\u534a\u5c71\u6240\u8d60\u7684\u4e8c\u767e\u4e24\u9ec4\u91d1\uff0c\u5374\u5df2\u4f7f\u5f97\u8361\u7136\u65e0\u5b58\u4e86\u3002 \u3000\u3000\u4e00\u65e5\u60f3\u8d77\uff0c\u5e38\u542c\u4eba\u8bf4\uff0c\u5e7f\u4e1c\u5bcc\u5eb6\u7e41\u76db\uff0c\u9887\u6709\u8c6a\u4fa0\u4e4b\u58eb\uff0c\u5de6\u53f3\u65e0\u4e8b\uff0c\u4e8e\u662f\u9a91\u4e86\u4e00\u5339\u52a3\u9a6c\uff0c\u5f84\u5f80\u5cad\u5357\u800c\u6765\u3002 \u3000\u3000\u8fd9\u4e00\u65e5\u5230\u4e86\u5e7f\u4e1c\u7684\u5927\u9547\u4f5b\u5c71\u9547\u3002\u90a3\u4f5b\u5c71\u81ea\u6765\u4e0e\u6731\u4ed9\u3001\u666f\u5fb7\u3001\u6c49\u53e3\u5e76\u79f0\u5929\u4e0b\u56db\u5927\u9547\uff0c\u7aef\u7684\u662f\u6c11\u4e30\u7269\u961c\uff0c\u5e02\u5edb\u7e41\u534e\u3002\u80e1\u6590\u5230\u5f97\u9547\u4e0a\uff0c\u5df2\u662f\u5df2\u672b\u5348\u521d\uff0c\u8179\u4e2d\u9965\u997f\uff0c\u89c1\u8def\u5357\u6709\u5ea7\u4e09\u5f00\u95f4\u95e8\u9762\u7684\u5927\u9152\u697c\uff0c\u62db\u724c\u4e0a\u5199\u7740\u201c\u82f1\u96c4\u697c\u201d\u4e09\u4e2a\u91d1\u6f06\u5927\u5b57\uff0c\u4e24\u8fb9\u655e\u7740\u7a97\u6237\uff0c\u9152\u697c\u91cc\u5200\u6784\u4e71\u54cd\uff0c\u9152\u8089\u9999\u6c14\u9635\u9635\u55b7\u51fa\u3002\u80e1\u6590\u5fc3\u9053\uff1a\u201c\u8fd9\u9152\u697c\u7684\u62db\u724c\u8d77\u5f97\u5012\u602a\u3002\u201d\u4e00\u6478\u8eab\u8fb9\uff0c\u53ea\u5269\u4e0b\u767e\u5341\u6765\u6587\u94b1\uff0c\u5fc3\u60f3\u4eca\u65e5\u559d\u9152\u662f\u4e0d\u6210\u7684\u4e86\uff0c\u5403\u4e00\u5927\u7897\u9762\u9971\u9971\u809a\u518d\u8bf4\u3002\u5f53\u4e0b\u5c06\u9a6c\u62f4\u5728\u9152\u697c\u524d\u7684\u6728\u6869\u4e0a\uff0c\u5f84\u884c\u4e0a\u697c\u3002 \u3000\u3000\u9152\u697c\u4e2d\u4f19\u8ba1\u89c1\u4ed6\u8863\u886b\u655d\u65e7\uff0c\u6ee1\u8138\u7684\u4e0d\u559c\uff0c\u4f38\u624b\u62e6\u4f4f\uff0c\u8bf4\u9053\uff1a\u201c\u5ba2\u5b98\uff0c\u697c\u4e0a\u662f\u96c5\u5ea7\uff0c\u4f60\u4e0d\u5acc\u4ef7\u94b1\u8d35\u4e48\uff1f\u201d\u80e1\u6590\u4e00\u542c\uff0c\u6c14\u5f80\u4e0a\u51b2\uff0c\u5fc3\u9053\uff1a\u201c\u4f60\u8fd9\u62db\u724c\u53eb\u505a\u82f1\u96c4\u697c\uff0c\u5bf9\u5f85\u7a77\u670b\u53cb\u5374\u662f\u8fd9\u822c\u72d7\u718a\u6c14\u6982\u3002\u6211\u4e0d\u5403\u4f60\u4e00\u4e2a\u4eba\u4ef0\u9a6c\u7ffb\uff0c\u80e1\u6590\u4fbf\u5728\u79f0\u82f1\u96c4\u4e86\u3002\u201d\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u53ea\u8981\u9152\u83dc\u7cbe\u7f8e\uff0c\u5374\u4e0d\u6015\u4ef7\u94b1\u8d35\u3002\u201d\u90a3\u4f19\u8ba1\u5c06\u4fe1\u5c06\u7591\uff0c\u659c\u7740\u773c\u7531\u4ed6\u4e0a\u697c\u3002 \u3000\u3000\u697c\u4e0a\u684c\u6905\u6d01\u51c0\u3002\u5ea7\u4e2d\u5ba2\u4eba\u8863\u9970\u8c6a\u5962\uff0c\u5341\u4e5d\u662f\u5bcc\u5546\u5927\u8d3e\u3002\u4f19\u8ba1\u77a7\u4e86\u4ed6\u7684\u6a21\u6837\uff0c\u6599\u5f97\u6ca1\u751a\u6cb9\u6c34\u751f\u53d1\uff0c\u7adf\u662f\u534a\u5929\u4e0d\u8fc7\u6765\u62db\u547c\u3002\u80e1\u6590\u6697\u6697\u5bfb\u601d\uff0c\u8981\u751f\u4e2a\u4ec0\u4e48\u5ff5\u5934\uff0c\u767d\u5403\u4ed6\u4e00\u987f\uff0c\u5ffd\u542c\u5f97\u8857\u5fc3\u4e00\u9635\u5927\u4e71\uff0c\u4e00\u4e2a\u5973\u4eba\u58f0\u97f3\u54c8\u54c8\u5927\u7b11\uff0c\u62cd\u624b\u800c\u6765\u3002 \u3000\u3000\u80e1\u6590\u6b63\u5750\u5728\u7a97\u8fb9\uff0c\u501a\u7a97\u5411\u8857\u5fc3\u671b\u53bb\uff0c\u89c1\u4e00\u4e2a\u5987\u4eba\u5934\u53d1\u6563\u4e71\uff0c\u8138\u4e0a\u3001\u8863\u4e0a\u3001\u624b\u4e0a\u5168\u662f\u9c9c\u8840\uff0c\u624b\u4e2d\u6293\u7740\u4e00\u67c4\u83dc\u5200\uff0c\u54ed\u4e00\u9635\uff0c\u7b11\u4e00\u9635\uff0c\u6307\u624b\u5212\u811a\uff0c\u539f\u6765\u662f\u4e2a\u75af\u5b50\u3002\u65c1\u89c2\u4e4b\u4eba\u8fdc\u8fdc\u7ad9\u7740\uff0c\u8138\u4e0a\u6216\u73b0\u6050\u60e7\uff0c\u6216\u663e\u51b7\u60af\uff0c\u65e0\u4eba\u6562\u8d70\u8fd1\u5979\u8eab\u65c1\u3002 \u3000\u3000\u53ea\u89c1\u5979\u6307\u7740\u201c\u82f1\u96c4\u697c\u201d\u7684\u62db\u724c\u62cd\u624b\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u51e4\u8001\u7237\uff0c\u4f60\u957f\u547d\u767e\u5c81\uff0c\u5bcc\u8d35\u53cc\u5168\u554a\uff0c\u6211\u8001\u5a46\u5b50\u7ed9\u4f60\u78d5\u5934\uff0c\u53eb\u8001\u5929\u7237\u751f\u773c\u775b\u4fdd\u4f51\u4f60\u554a\u3002\u201d\u8bf4\u7740\u8dea\u5012\u5728\u5730\uff0c\u767b\u767b\u767b\u7684\u78d5\u5934\uff0c\u649e\u5f97\u989d\u5934\u5168\u662f\u9c9c\u8840\uff0c\u5374\u4f3c\u4e1d\u6beb\u4e0d\u89c9\u75bc\u75db\uff0c\u4e00\u9762\u78d5\u5934\uff0c\u4e00\u9762\u547c\u53eb\uff1a\u201c\u51e4\u8001\u7237\uff0c\u4f60\u65e5\u8fdb\u4e00\u6597\u91d1\uff0c\u591c\u8fdb\u4e00\u6597\u94f6\uff0c\u5927\u5bcc\u5927\u8d35\uff0c\u767e\u5b50\u5343\u5b59\u554a\u3002\u201d \u3000\u3000\u9152\u697c\u4e2d\u95ea\u51fa\u4e00\u4eba\uff0c\u624b\u6267\u957f\u70df\u888b\uff0c\u4f3c\u662f\u638c\u67dc\u6a21\u6837\uff0c\u6307\u7740\u90a3\u5987\u4eba\u9a82\u9053\uff1a\u201c\u953a\u56db\u5ac2\uff0c\u4f60\u8981\u5356\u75af\uff0c\u56de\u81ea\u5df1\u7a9d\u513f\u53bb\uff0c\u522b\u5728\u8fd9\u513f\u6270\u4e86\u8d35\u5ba2\u4eec\u5403\u559d\u7684\u5174\u5934\u3002\u201d\u90a3\u953a\u56db\u5ac2\u5168\u6ca1\u7406\u4f1a\uff0c\u4ecd\u662f\u53c8\u54ed\u53c8\u7b11\uff0c\u5411\u7740\u9152\u697c\u78d5\u5934\uff0c\u638c\u67dc\u7684\u4e00\u6325\u624b\uff0c\u9152\u697c\u4e2d\u8d70\u51fa\u4e24\u540d\u7c97\u58ee\u6c49\u5b50\uff0c\u4e00\u4e2a\u5939\u624b\u62a2\u8fc7\u5979\u624b\u4e2d\u83dc\u5200\uff0c\u53e6\u4e00\u4e2a\u7528\u529b\u4e00\u63a8\u3002\u9524\u56db\u5ac2\u767b\u65f6\u6454\u4e86\u4e00\u4e2a\u7b4b\u6597\uff0c\u6eda\u8fc7\u8857\u5fc3\uff0c\u6323\u624e\u7740\u722c\u8d77\u540e\u75f4\u75f4\u5446\u5446\u5730\u7ad9\u7740\uff0c\u534a\u664c\u4e0d\u8a00\u4e0d\u8bed\uff0c\u7a81\u7136\u6376\u80f8\u5927\u54ed\uff0c\u53f7\u53eb\u8fde\u58f0\uff1a\u201c\u6211\u90a3\u5c0f\u4e09\u5b9d\u8d1d\u513f\u554a\uff0c\u4f60\u6b7b\u5f97\u597d\u82e6\u554a\u3002\u8001\u5929\u7237\u751f\u773c\u775b\uff0c\u4f60\u53ef\u6ca1\u5077\u4eba\u5bb6\u7684\u9e45\u5403\u554a\u3002\u201d \u3000\u3000\u62a2\u4e86\u83dc\u5200\u7684\u90a3\u6c49\u5b50\u4e3e\u8d77\u5200\u6765\uff0c\u559d\u9053\uff1a\u201c\u4f60\u518d\u5728\u8fd9\u91cc\u80e1\u8bf4\u516b\u9053\uff0c\u6211\u5c31\u7ed9\u4f60\u4e00\u5200\u3002\u201d\u953a\u56db\u5ac2\u6beb\u4e0d\u5bb3\u6015\uff0c\u4ecd\u662f\u54ed\u53eb\u3002\u638c\u67dc\u7684\u89c1\u8857\u574a\u4f17\u4eba\u8138\u4e0a\u90fd\u6709\u4e0d\u4ee5\u4e3a\u7136\u4e4b\u8272\uff0c\u547c\u565c\u547c\u565c\u7684\u62bd\u4e86\u51e0\u53e3\u70df\uff0c\u55b7\u51fa\u4e00\u80a1\u767d\u70df\uff0c\u5c06\u624b\u4e00\u6325\uff0c\u4e0e\u4e24\u540d\u6c49\u5b50\u56de\u8fdb\u4e86\u9152\u697c\u3002 \u3000\u3000\u80e1\u6590\u89c1\u4e24\u4e2a\u6c49\u5b50\u6b3a\u4fae\u4e00\u4e2a\u5987\u9053\u4eba\u5bb6\uff0c\u672c\u611f\u6c14\u607c\uff0c\u4f46\u60f3\u8fd9\u5987\u4eba\u662f\u4e2a\u75af\u5b50\uff0c\u539f\u4e5f\u4e0d\u53ef\u7406\u55bb\uff0c\u5ffd\u542c\u5f97\u5750\u5728\u8eab\u540e\u684c\u8fb9\u4e24\u540d\u9152\u5ba2\u6084\u58f0\u8bae\u8bba\u3002\u4e00\u4e2a\u9053\uff1a\u201c\u51e4\u8001\u7237\u8fd9\u4ef6\u4e8b\uff0c\u505a\u5f97\u4e5f\u592a\u6025\u8e81\u4e86\u4e9b\uff0c\u6d3b\u751f\u751f\u903c\u6b7b\u4e00\u6761\u4eba\u547d\uff0c\u53ea\u6015\u5c06\u6765\u8981\u906d\u62a5\u5e94\u3002\u201d \u3000\u3000\u80e1\u6590\u542c\u5230\u201c\u6d3b\u751f\u751f\u903c\u6b7b\u4e00\u6761\u4eba\u547d\u201d\u8fd9\u4e5d\u4e2a\u5b57\uff0c\u5fc3\u4e2d\u4e00\u51db\u3002\u53ea\u542c\u53e6\u4e00\u4eba\u9053\uff1a\u201c\u90a3\u4e5f\u4e0d\u80fd\u8bf4\u662f\u51e4\u8001\u7237\u7684\u8fc7\u9519\uff0c\u5bb6\u91cc\u4e0d\u89c1\u4e86\u4e1c\u897f\uff0c\u95ee\u4e00\u58f0\u4e5f\u662f\u5341\u5206\u5e73\u5e38\uff0c\u8c01\u53eb\u8fd9\u5973\u4eba\u5931\u5fc3\u75af\u4e86\uff0c\u7adf\u628a\u81ea\u5df1\u7684\u4eb2\u751f\u513f\u5b50\u5256\u5f00\u4e86\u809a\u5b50\u3002\u201d\u80e1\u6590\u542c\u5230\u6700\u540e\u8fd9\u53e5\u8bdd\uff0c\u54ea\u91cc\u8fd8\u5fcd\u8010\u5f97\u6ce8\uff0c\u731b\u5730\u8f6c\u8fc7\u8eab\u6765\uff0c\u53ea\u89c1\u8bf4\u8bdd\u7684\u4e8c\u4eba\u90fd\u662f\u56db\u5341\u5de6\u53f3\u5e74\u7eaa\uff0c\u4e00\u4e2a\u80a5\u80d6\uff0c\u4e00\u4e2a\u7626\u524a\uff0c\u7a7f\u7684\u90fd\u662f\u7ef8\u7f0e\u957f\u888d\uff0c\u77a7\u8fd9\u6253\u626e\uff0c\u5747\u662f\u5e97\u4e1c\u5bcc\u5546\u3002\u4e8c\u4eba\u89c1\u4ed6\u56de\u5934\uff0c\u76f8\u89c6\u4e00\u773c\uff0c\u767b\u65f6\u4f4f\u53e3\u4e0d\u8bf4\u4e86\u3002 \u3000\u3000\u80e1\u6590\u77e5\u9053\u8fd9\u79cd\u4eba\u6700\u662f\u80c6\u5c0f\u6015\u4e8b\uff0c\u82e5\u662f\u5584\u8a00\u76f8\u95ee\uff0c\u5fc5\u5b9a\u63a8\u8bf4\u4e0d\u77e5\uff0c\u51b3\u4e0d\u80af\u4f46\u76f4\u4ee5\u544a\uff0c\u5f53\u4e0b\u7ad9\u8d77\u8eab\u6765\uff0c\u4f5c\u4e86\u4e2a\u63d6\uff0c\u6ee1\u8138\u5806\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4e24\u4f4d\u8001\u677f\uff0c\u8089\u5728\u5e7f\u5dde\u4e00\u522b\uff0c\u5df2\u6709\u6570\u5e74\u4e0d\u89c1\u4e86\uff0c\u4e24\u4f4d\u597d\u554a\uff1f\u201d\u90a3\u4e8c\u4eba\u548c\u4ed6\u7d20\u4e0d\u76f8\u8bc6\uff0c\u542c\u4ed6\u53e3\u97f3\u53c8\u662f\u5916\u7701\u4eba\uff0c\u5fc3\u4e2d\u5747\u611f\u5947\u602a\uff0c\u4f46\u751f\u610f\u4eba\u8bb2\u7a76\u548c\u6c14\u751f\u8d22\uff0c\u5f53\u5373\u62f1\u624b\u8fd8\u793c\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u597d\uff0c\u4f60\u597d\u3002\u201d\u80e1\u6590\u7b11\u9053\uff1a\u201c\u5c0f\u5f1f\u8fd9\u6b21\u5230\u4f5b\u5c71\u6765\uff0c\u5e26\u4e86\u4e00\u4e07\u4e24\u94f6\u5b50\uff0c\u60f3\u529e\u4e00\u6279\u8d27\uff0c\u53ea\u662f\u4eba\u5730\u751f\u758f\uff0c\u597d\u751f\u4e3a\u96be\u3002\u4eca\u65e5\u4e0e\u4e24\u4f4d\u5de7\u9047\uff0c\u90a3\u518d\u597d\u4e5f\u6ca1\u6709\u4e86\uff0c\u6b63\u597d\u8bf7\u4e24\u4f4d\u5e2e\u5fd9\u3002\u201d\u4e8c\u4eba\u4e00\u542c\u5230\u201c\u4e00\u4e07\u4e24\u94f6\u5b50\u201d\u4e94\u4e2a\u5b57\uff0c\u767b\u65f6\u4ece\u5fc3\u7a9d\u91cc\u7b11\u4e86\u51fa\u6765\uff0c\u867d\u89c1\u4ed6\u8863\u7740\u4e0d\u4f3c\u6709\u94b1\u4eba\uff0c\u4f46\u201c\u4e00\u4e07\u4e24\u94f6\u5b50\u201d\u975e\u540c\u5c0f\u53ef\uff0c\u5c82\u80fd\u4ea4\u81c2\u5931\u4e4b\uff1f \u3000\u3000\u9f50\u9053\uff1a\u201c\u90a3\u662f\u8be5\u5f53\u7684\uff0c\u8bf7\u8fc7\u6765\u5171\u996e\u4e00\u676f\uff0c\u6162\u6162\u7ec6\u8c08\u5982\u4f55\uff1f\u201d \u3000\u3000\u80e1\u6590\u4e0a\u8981\u4ed6\u4e8c\u4eba\u8bf4\u8fd9\u53e5\u6d3b\uff0c\u54ea\u91cc\u8fd8\u6709\u5ba2\u6c14\uff0c\u5f53\u5373\u8d70\u5c06\u8fc7\u53bb\uff0c\u6253\u6a2a\u91cc\u5750\u4e86\uff0c\u5f00\u95e8\u89c1\u5c71\u7684\u95ee\u9053\uff1a\u201d\u9002\u624d\u542c\u4e24\u4f4d\u8a00\u9053\uff0c\u4ec0\u4e48\u6d3b\u751f\u751f\u7684\u903c\u6b7b\u4e86\u4e00\u6761\u4eba\u547d\uff0c\u5012\u8981\u8bf7\u6559\u3002\u201d\u90a3\u4e8c\u4eba\u8138\u4e0a\u5fae\u5fae\u53d8\u8272\uff0c\u6b63\u6b32\u63a8\u642a\uff0c\u80e1\u6590\u4f38\u51fa\u5de6\u624b\uff0c\u5728\u684c\u5e95\u81ea\u5de6\u81f3\u53f3\u7684\u4e00\u79fb\uff0c\u5df2\u5c06\u6bcf\u4eba\u4e00\u53ea\u624b\u8155\u6293\u4f4f\uff0c\u63e1\u5728\u624b\u638c\u4e4b\u4e2d\uff0c\u7565\u4e00\u7528\u52b2\uff0c\u4e8c\u4eba\u201c\u554a\u201d\u7684\u4e00\u58f0\u53eb\u4e86\u51fa\u6765\uff0c\u7acb\u65f6\u8138\u8272\u60e8\u767d\u3002\u697c\u5934\u7684\u4f19\u8ba1\u4e0e\u4f17\u9152\u5ba2\u542c\u5230\u53eb\u58f0\uff0c\u4e00\u9f50\u56de\u5934\u8fc7\u6765\u3002\u80e1\u6590\u4f4e\u58f0\u9053\uff1a\u201c\u4e0d\u8bb8\u51fa\u58f0\uff01\u201d\u4e8c\u4eba\u4e0d\u6562\u8fdd\u62d7\uff0c\u53ea\u5f97\u540c\u65f6\u82e6\u7b11\u3002\u65c1\u4eba\u89c1\u65e0\u522b\u4e8b\uff0c\u5c31\u6ca1\u518d\u770b\u3002 \u3000\u3000\u8fd9\u4e8c\u4eba\u624b\u8155\u88ab\u80e1\u6590\u6293\u5728\u638c\u4e2d\uff0c\u5b9b\u5982\u7ed9\u94c1\u7b8d\u7262\u7262\u7b8d\u4f4f\u4e86\u4e00\u822c\uff0c\u54ea\u91cc\u8fd8\u52a8\u5f39\u5f97\u534a\u5206\uff1f\u80e1\u6590\u4f4e\u58f0\u9053\uff1a\u201c\u6211\u672c\u662f\u4e2a\u6740\u4eba\u4e0d\u7728\u773c\u7684\u5927\u76d7\uff0c\u73b0\u4e0b\u6539\u90aa\u5f52\u6b63\uff0c\u5b66\u505a\u751f\u610f\uff0c\u8981\u4e00\u4e07\u4e24\u94f6\u5b50\u529e\u8d27\uff0c\u53ef\u662f\u77ed\u4e86\u672c\u94b1\uff0c\u53ea\u5f97\u5411\u4e8c\u4f4d\u5404\u501f\u4e94\u5343\u4e24\u3002\u201d\u4e8c\u4eba\u5927\u5403\u4e00\u60ca\uff0c\u9f50\u58f0\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u6ca1\u6709\u554a\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u597d\uff0c\u4f60\u4eec\u628a\u51e4\u8001\u7237\u903c\u6b7b\u4eba\u547d\u7684\u4e8b\uff0c\u8bf4\u7ed9\u6211\u542c\u3002\u54ea\u4e00\u4f4d\u8bf4\u5f97\u660e\u767d\u4ed4\u7ec6\uff0c\u6211\u4fbf\u4e0d\u5411\u4ed6\u501f\u94b1\u3002\u8fd9\u4e00\u4e07\u4e24\u94f6\u5b50\uff0c\u53ea\u597d\u7740\u843d\u5728\u53e6\u4e00\u4f4d\u8eab\u4e0a\u3002\u201d\u4e8c\u4eba\u5fd9\u9053\uff1a\u201c\u6211\u6765\u8bf4\uff0c\u6211\u6765\u8bf4\u3002\u201d\u5148\u524d\u8c01\u90fd\u4e0d\u80af\u8bf4\uff0c\u8fd9\u65f6\u751f\u6015\u72ec\u529b\u8d1f\u62c5\uff0c\u505a\u4e86\u5355\u5934\u503a\u4e3b\uff0c\u7adf\u7136\u4e89\u5148\u6050\u540e\u8d77\u6765\u3002 \u3000\u3000\u80e1\u6590\u89c1\u8fd9\u4e2a\u6bd4\u8d5b\u7684\u6cd5\u513f\u6536\u6548\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u542c\u90a3\u80d6\u5b50\u8bf4\u5317\u65b9\u6d3b\u53e3\u97f3\u8f83\u6b63\uff0c\u4fbf\u6307\u7740\u4ed6\u9053\uff1a\u201c\u80d6\u7684\u5148\u8bf4\uff0c\u4f8d\u4f1a\u518d\u53eb\u7626\u7684\u8bf4\u3002\u54ea\u4e00\u4f4d\u8bf4\u5f97\u4e0d\u6e05\u695a\uff0c\u90a3\u4fbf\u662f\u6211\u7684\u503a\u4e3b\u8001\u7237\u4e86\u3002\u201d\u8bf4\u7740\u653e\u8131\u4e86\u4e8c\u4eba\u624b\u8155\uff0c\u53d6\u4e0b\u80cc\u4e0a\u5305\u88f9\uff0c\u6253\u4e86\u5f00\u6765\uff0c\u9732\u51fa\u4e00\u67c4\u660e\u6643\u6643\u7684\u94a2\u5200\uff0c\u62ff\u8d77\u684c\u4e0a\u4e00\u53cc\u8c61\u7259\u7b77\u5b50\uff0c\u5728\u5200\u53e3\u8f7b\u8f7b\u4e00\u63a0\uff0c\u7b77\u5b50\u767b\u65f6\u65ad\u4e3a\u56db\u622a\u3002\u8fd9\u4e8c\u4eba\u9762\u9762\u76f8\u89d1\uff0c\u5f20\u5927\u4e86\u53e3\u5408\u4e0d\u62e2\u6765\uff0c\u4e24\u9897\u5fc3\u5374\u662f\u6026\u6026\u5730\u8df3\u4e2a\u4e0d\u4f4f\u3002 \u3000\u3000\u80e1\u6590\u4f38\u51fa\u53cc\u624b\uff0c\u5728\u4e8c\u4eba\u540e\u9888\u6478\u4e86\u6478\uff0c\u597d\u4f3c\u5728\u5bfb\u627e\u4e0b\u5200\u7684\u90e8\u4f4d\u4e00\u822c\uff0c\u5c06\u4e8c\u4eba\u66f4\u662f\u5413\u5f97\u9762\u5982\u571f\u8272\u3002\u80e1\u6590\u70b9\u70b9\u5934\uff0c\u81ea\u8a00\u81ea\u8bed\u5730\u9053\uff1a\u201c\u597d\uff0c\u597d\uff01\u201d\u53c8\u5c06\u5305\u88f9\u5305\u4e0a\u3002 \u3000\u3000\u90a3\u80d6\u5546\u4eba\u5fd9\u9053\uff1a\u201c\u5c0f\u7237\uff0c\u6211\u8bf4\uff0c\u4fdd\u7ba1\u6bd4\u2026\u2026\u6bd4\u4ed6\u8bf4\u5f97\u660e\u767d\u2026\u2026\u201d\u90a3\u7626\u5546\u4eba\u62a2\u7740\u9053\uff1a\u201c\u90a3\u4e5f\u4e0d\u89c1\u5f97\uff0c\u8ba9\u6211\u5148\u8bf4\u5427\u3002\u201d\u80e1\u6590\u8138\u4e00\u6c89\uff0c\u9053\uff1a\u201c\u6211\u8bf4\u8fc7\u8981\u5148\u542c\u4ed6\u8bf4\uff0c\u4f60\u5fd9\u4ec0\u4e48\uff1f\u201d\u90a3\u7626\u5546\u4eba\u5fd9\u9053\uff1a\u201c\u662f\uff0c\u662f\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u4f60\u4e0d\u9075\u6211\u5429\u5490\uff0c\u8981\u7f5a\uff01\u201d\u90a3\u7626\u5546\u4eba\u5413\u5f97\u9b42\u4e0d\u9644\u4f53\uff0c\u80d6\u5546\u4eba\u5374\u8138\u6709\u5f97\u8272\u3002 \u3000\u3000\u80e1\u6590\u9053\uff1a\u201c\u9152\u5fae\u83dc\u5be1\uff0c\u600e\u662f\u656c\u5ba2\u7684\u9053\u7406\uff1f\u5feb\u53eb\u4e00\u684c\u4e0a\u7b49\u9152\u5e2d\u6765\u3002\u201d\u7626\u5546\u4eba\u4e00\u542c\u5904\u7f5a\u751a\u8f7b\uff0c\u5982\u9022\u5927\u8d66\uff0c\u5fd9\u53eb\u4f19\u8ba1\u8fc7\u6765\uff0c\u5429\u5490\u4ed6\u5373\u523b\u505a\u4e00\u5e2d\u4e94\u4e24\u94f6\u5b50\u7684\u6700\u4e0a\u7b49\u9152\u83dc\u3002\u90a3\u4f19\u8ba1\u89c1\u80e1\u6590\u548c\u4ed6\u4eec\u5750\u5728\u4e00\u8d77\uff0c\u751a\u662f\u8be7\u5f02\uff0c\u542c\u5230\u6709\u4e94\u4e24\u94f6\u5b50\u7684\u4e70\u5356\uff0c\u5f53\u5373\u7709\u5f00\u773c\u7b11\u5730\u8fde\u58f0\u7b54\u5e94\u3002 \u3000\u3000\u80e1\u6590\u5728\u7a97\u53e3\u63a2\u5934\u4e00\u671b\uff0c\u89c1\u90a3\u649e\u56db\u5ac2\u62ab\u5934\u6563\u53d1\u5730\u5750\u5728\u5bf9\u8857\u5730\u4e0b\uff0c\u62ac\u5934\u671b\u5929\uff0c\u53e3\u4e2d\u54fa\u54fa\u7684\u81ea\u8a00\u81ea\u8bed\uff0c\u4e0d\u77e5\u8bf4\u4e9b\u4ec0\u4e48\u3002 \u3000\u3000\u90a3\u80d6\u5546\u4eba\u9053\uff1a\u201c\u5c0f\u7237\uff0c\u8fd9\u4ef6\u4e8b\u6211\u8bf4\u4fbf\u8bf4\u4e86\uff0c\u53ef\u4e0d\u80fd\u8ba9\u4eba\u77e5\u9053\u662f\u6211\u8bf4\u7684\u3002\u201d \u3000\u3000\u80e1\u6590\u7709\u5934\u4e00\u76b1\uff0c\u9053\uff1a\u201c\u4f60\u4e0d\u8bf4\u4e5f\u7f62\uff0c\u90a3\u5c31\u8ba9\u4ed6\u8bf4\u3002\u201d\u8bf4\u7740\u8f6c\u5934\u5411\u7626\u5546\u4eba\u3002\u80d6\u5546\u4eba\u5fd9\u9053\uff1a\u201c\u6211\u8bf4\uff0c\u6211\u8bf4\u3002\u5c0f\u7237\uff0c\u8fd9\u4f4d\u51e4\u8001\u7237\u540d\u5b57\u53eb\u4f5c\u51e4\u5929\u5357\uff0c\u4e43\u662f\u4f5b\u5c71\u9547\u4e0a\u7684\u5927\u8d22\u4e3b\uff0c\u6709\u4e00\u4e2a\u7ef0\u53f7\uff0c\u53eb\u4f5c\u2026\u2026\u201d\u7626\u5546\u4eba\u63a5\u53e3\u9053\uff1a\u201d\u53eb\u4f5c\u5357\u9738\u5929\u3002\u201d\u80e1\u6590\u559d\u9053\uff1a\u201c\u53c8\u4e0d\u662f\u8bf4\u76f8\u58f0\uff0c\u4f60\u63d2\u53e3\u5e72\u4e48\uff1f\u201d\u7626\u5546\u4eba\u4f4e\u4e0b\u4e86\u5934\uff0c\u4e0d\u6562\u518d\u8a00\u8bed\u4e86\u3002 \u3000\u3000\u90a3\u80d6\u5546\u4eba\u9053\uff1a\u201c\u51e4\u8001\u7237\u5728\u4f5b\u5c71\u9547\u4e0a\u5f00\u4e86\u4e00\u5bb6\u5927\u5178\u5f53\uff0c\u53eb\u4f5c\u82f1\u96c4\u5f53\u94fa\uff1b\u4e00\u5bb6\u9152\u697c\uff0c\u4fbf\u662f\u8fd9\u5bb6\u82f1\u96c4\u697c\uff1a\u53c8\u6709\u4e00\u5bb6\u5927\u8d4c\u573a\uff0c\u53eb\u4f5c\u82f1\u96c4\u4f1a\u9986\uff0c\u4ed6\u8d22\u96c4\u52bf\u5927\uff0c\u4ea4\u6e38\u5e7f\u9614\uff0c\u6b66\u827a\u7b97\u5f97\u5168\u5e7f\u4e1c\u7b2c\u4e00\u3002\u9547\u4e0a\u7684\u4eba\u79c1\u4e0b\u91cc\u8fd8\u8bf4\uff0c\u6bcf\u4e2a\u6708\u6709\u4eba\u4ece\u7ca4\u4e1c\u3001\u7ca4\u897f\u3001\u7ca4\u5317\u4e09\u5904\u9001\u94f6\u5b50\u6765\u5b5d\u656c\u4ed6\uff0c\u542c\u8bf4\u4ed6\u662f\u4ec0\u4e48\u4e94\u864e\u6d3e\u7684\u638c\u95e8\u4eba\uff0c\u51e1\u662f\u4e94\u864e\u6d3e\u7684\u5f1f\u5144\u4eec\u5728\u5404\u5904\u53d1\u8d22\uff0c\u4fbf\u5f97\u62bd\u4e2a\u4efd\u513f\u7ed9\u4ed6\u3002\u8fd9\u4e9b\u6c5f\u6e56\u4e0a\u7684\u4e8b\uff0c\u5c0f\u7684\u4e5f\u5f04\u4e0d\u660e\u767d\u3002\u201d\u80e1\u6590\u70b9\u5934\u9053\uff1a\u201c\u662f\u4e86\uff0c\u4ed6\u662f\u5927\u8d22\u4e3b\uff0c\u53c8\u662f\u5750\u5730\u5206\u8d43\u7684\u5927\u5f3a\u76d7\u3002\u201d\u4e8c\u4eba\u5411\u4ed6\u671b\u4e86\u4e00\u773c\uff0c\u5fc3\u60f3\uff1a\u201c\u90a3\u4f60\u4e0e\u4ed6\u662f\u540c\u884c\u54ea\u3002\u201d\u80e1\u6590\u65e9\u5df2\u660e\u767d\u4ed6\u4eec\u7684\u5fc3\u610f\uff0c\u7b11\u9053\uff0c\u201c\u5e38\u8a00\u9053\u540c\u884c\u662f\u51a4\u5bb6\u3002\u6211\u8ddf\u8fd9\u4f4d\u51e4\u8001\u7237\u4e0d\u662f\u670b\u53cb\u3002\u4f60\u4eec\u6709\u597d\u8bf4\u597d\uff0c\u6709\u6b79\u8bf4\u6b79\uff0c\u4e0d\u5fc5\u9690\u7792\u3002\u201d \u3000\u3000\u90a3\u80d6\u5546\u4eba\u9053\uff1a\u201c\u8fd9\u51e4\u8001\u7237\u7684\u5b85\u5b50\u4e00\u8fde\u4e94\u8fdb\uff0c\u672c\u6765\u5df2\u591f\u5927\u5566\uff0c\u53ef\u662f\u4ed6\u65b0\u8fd1\u5a36\u4e86\u4e00\u623f\u4e03\u59e8\u592a\uff0c\u53c8\u8981\u5728\u540e\u8fdb\u65c1\u8fb9\u8d77\u4e00\u5ea7\u4ec0\u4e48\u516d\u51e4\u697c\uff0c\u7ed9\u8fd9\u4f4d\u65b0\u59e8\u592a\u592a\u4f4f\u3002 \u3000\u3000\u4ed6\u770b\u4e2d\u7684\u5730\u76ae\uff0c\u4fbf\u662f\u9524\u56db\u5ac2\u5bb6\u4f20\u7684\u83dc\u56ed\u3002\u8fd9\u5757\u5730\u53ea\u6709\u4e24\u4ea9\u51e0\u5206\uff0c\u4f46\u9524\u963f\u56db\u79cd\u83dc\u4e3a\u751f\uff0c\u4e00\u5bb6\u4e94\u53e3\u5168\u9760\u7740\u8fd9\u83dc\u56ed\u5b50\u5403\u996d\u3002\u51e4\u8001\u7237\u628a\u9524\u963f\u56db\u53eb\u53bb\uff0c\u8bf4\u7ed9\u4e94\u4e24\u94f6\u5b50\u4e70\u4ed6\u7684\u5730\u3002\u9524\u963f\u56db\u81ea\u7136\u4e0d\u80af\u3002\u51e4\u8001\u7237\u52a0\u5230\u5341\u4e24\u3002\u9524\u963f\u56db\u8fd8\u662f\u4e0d\u80af\uff0c\u8bf4\u9053\u4fbf\u662f\u4e00\u767e\u4e24\u94f6\u5b50\uff0c\u4e5f\u5403\u5f97\u5b8c\uff0c\u53ef\u662f\u5728\u8fd9\u83dc\u56ed\u5b50\u6252\u6252\u571f\u3001\u6d47\u6d47\u6c34\uff0c\u53ea\u8981\u529b\u6c14\u82b1\u4e0a\u53bb\uff0c\u4e00\u5bb6\u513f\u53e3\u4fbf\u997f\u4e0d\u6b7b\u4e86\u3002\u51e4\u8001\u7237\u607c\u4e86\uff0c\u5c06\u4ed6\u8d76\u4e86\u51fa\u6765\uff0c\u6628\u5929\u4fbf\u8d77\u4e86\u8fd9\u5077\u9e45\u7684\u4e8b\u513f\u3002 \u3000\u3000\u201c\u539f\u6765\u51e4\u8001\u7237\u540e\u9662\u4e2d\u517b\u4e86\u5341\u53ea\u80a5\u9e45\uff0c\u6628\u5929\u5ffd\u7136\u4e0d\u89c1\u4e86\u4e00\u53ea\u5bb6\u4e01\u8bf4\u662f\u9524\u5bb6\u7684\u5c0f\u4e8c\u5b50\u3001\u5c0f\u4e09\u5b50\u5144\u5f1f\u4fe9\u5077\u4e86\uff0c\u5bfb\u5230\u4ed6\u83dc\u56ed\u5b50\u91cc\uff0c\u679c\u7136\u89c1\u83dc\u5730\u91cc\u6709\u8bb8\u591a\u9e45\u6bdb\u3002 \u3000\u3000\u9524\u56db\u5ac2\u53eb\u8d77\u5c48\u6765\uff0c\u8bf4\u5979\u4e24\u4e2a\u513f\u5b50\u5411\u6765\u89c4\u77e9\uff0c\u51b3\u4e0d\u4f1a\u5077\u4eba\u5bb6\u7684\u4e1c\u897f\uff0c\u8fd9\u9e45\u6bdb\u51c6\u662f\u65c1\u4eba\u4e22\u5728\u83dc\u56ed\u5b50\u91cc\u7684\u3002 \u3000\u3000\u5bb6\u4e01\u4eec\u627e\u5c0f\u4e8c\u5c0f\u4e09\u53bb\u95ee\uff0c\u4e24\u4e2a\u90fd\u8bf4\u6ca1\u5077\u3002\u51e4\u8001\u7237\u95ee\u9053\uff1a\u2018\u4eca\u513f\u65e9\u6668\u4f60\u4eec\u5403\u4e86\u4ec0\u4e48\uff1f\u2019\u5c0f\u4e09\u5b50\u9053\uff1a\u201d\u5403\u6211\uff0c\u5403\u6211\u3002\u2019\u51e4\u8001\u7237\u62cd\u684c\u5927\u9a82\uff0c\u8bf4\uff1a\u2018\u5c0f\u4e09\u5b50\u81ea\u5df1\u90fd\u62db\u4e86\uff0c\u8fd8\u8bf4\u6ca1\u5077\uff1f\u2019\u4e8e\u662f\u53eb\u4eba\u5230\u5de1\u68c0\u8859\u95e8\u53bb\u544a\u4e86\u4e00\u72b6\uff0c\u5dee\u5f79\u4fbf\u6765\u5c06\u9524\u963f\u56db\u9501\u4e86\u53bb\u3002 \u3000\u3000\u201c\u9524\u56db\u5ac2\u77e5\u9053\u81ea\u5df1\u5bb6\u91cc\u867d\u7a77\uff0c\u4e24\u4e2a\u513f\u5b50\u5374\u4e56\uff0c\u5e73\u65f6\u4e00\u5bb6\u53c8\u5f88\u60e7\u6015\u51e4\u5bb6\uff0c\u51b3\u4e0d\u4f1a\u53bb\u5077\u4ed6\u4eec\u7684\u9e45\u5403\uff0c\u4fbf\u5230\u51e4\u5bb6\u53bb\u7406\u8bba\uff0c\u5374\u7ed9\u51e4\u8001\u7237\u7684\u5bb6\u4e01\u8e22\u4e86\u51fa\u6765\u3002\u5979\u8d76\u5230\u5de1\u68c0\u8859\u95e8\u53bb\u53eb\u51a4\uff0c\u4e5f\u7ed9\u5dee\u5f79\u8f70\u51fa\u3002\u5de1\u68c0\u8001\u7237\u53d7\u4e86\u51e4\u8001\u7237\u7684\u5631\u6258\uff0c\u53c8\u662f\u677f\u5b50\uff0c\u53c8\u662f\u5939\u68cd\uff0c\u65e9\u5df2\u5c06\u9524\u963f\u56db\u6574\u6cbb\u5f97\u5944\u5944\u4e00\u606f\u3002\u9524\u56db\u5ac2\u53bb\u63a2\u76d1\uff0c\u89c1\u4e08\u592b\u6ee1\u8eab\u8840\u8089\u6a21\u7cca\uff0c\u8bdd\u4e5f\u8bf4\u4e0d\u51fa\u4e86\uff0c\u53ea\u662f\u80e1\u91cc\u80e1\u6d82\u5730\u53eb\u9053\uff1a\u2018\u4e0d\u5356\u5730\uff0c\u4e0d\u5356\u5730\uff01\u6ca1\u6709\u5077\uff0c\u6ca1\u6709\u5077\u3002\u2019\u9524\u56db\u5ac2\u5fc3\u91cc\u4e00\u6025\uff0c\u4fbf\u6a2a\u4e86\u5fc3\u3002\u5979\u8d76\u56de\u5bb6\u91cc\uff0c\u4e00\u624b\u62d6\u4e86\u5c0f\u4e09\u5b50\uff0c\u4e00\u624b\u62ff\u4e86\u67c4\u83dc\u5200\uff0c\u53eb\u4e86\u5de6\u53f3\u4e61\u90bb\uff0c\u4e00\u9f50\u4e0a\u7956\u5e99\u53bb\u3002\u4e61\u90bb\u4eec\u53ea\u9053\u5979\u8981\u5728\u795e\u524d\u53d1\u8a93\uff0c\u4fbf\u540c\u53bb\u4f5c\u4e2a\u89c1\u8bc1\u3002\u5c0f\u4eba\u548c\u5979\u4f4f\u5f97\u8fd1\uff0c\u4e5f\u8ddf\u53bb\u77a7\u77a7\u70ed\u95f9\u3002 \u3000\u3000\u201c\u9524\u56db\u5ac2\u5728\u5317\u5e1d\u7237\u7237\u5ea7\u524d\u78d5\u4e86\u51e0\u4e2a\u54cd\u5934\uff0c\u8bf4\u9053\uff1a\u2018\u5317\u5e1d\u7237\u7237\uff0c\u6211\u5b69\u5b50\u51b3\u4e0d\u80fd\u5077\u4eba\u5bb6\u7684\u9e45\u3002\u4ed6\u4eca\u5e74\u8fd8\u53ea\u56db\u5c81\uff0c\u5201\u5634\u62d7\u820c\uff0c\u8bf4\u4e0d\u6e05\u695a\uff0c\u5728\u8d22\u4e3b\u7237\u9762\u524d\u8bf4\u4ec0\u4e48\u5403\u6211\uff0c\u5403\u6211\uff01\u5c0f\u5987\u4eba\u4e00\u5bb6\u6a2a\u906d\u4e0d\u767d\uff0c\u8d43\u5b98\u53d7\u4e86\u8d3f\uff0c\u65ad\u4e8b\u4e0d\u660e\uff0c\u53ea\u6709\u8bf7\u5317\u5e1d\u7237\u7237\u4f38\u51a4\uff01\u2019\u8bf4\u7740\u63d0\u8d77\u5200\u6765\uff0c\u4e00\u5200\u4fbf\u5c06\u5c0f\u4e09\u5b50\u7684\u809a\u5b50\u5256\u4e86\u3002\u201d \u3000\u3000\u80e1\u6590\u4e00\u8def\u542c\u4e0b\u6765\uff0c\u65e9\u5df2\u76ee\u7726\u6b32\u88c2\uff0c\u542c\u5230\u6b64\u5904\uff0c\u4e0d\u7981\u5927\u53eb\u4e00\u58f0\uff0c\u970d\u5730\u7ad9\u8d77\uff0c\u7830\u7684\u4e00\u638c\uff0c\u6253\u5f97\u684c\u4e0a\u7897\u76cf\u8dc3\u8d77\uff0c\u6c64\u6c41\u98de\u6e85\uff0c\u53eb\u9053\uff1a\u201c\u7adf\u6709\u6b64\u4e8b\uff1f\u201d \u3000\u3000\u80d6\u7626\u4e8c\u5546\u4eba\u89c1\u4ed6\u795e\u5a01\u51db\u51db\uff0c\u4e00\u9f50\u98a4\u58f0\u9053\uff1a\u201c\u6b64\u4e8b\u5343\u771f\u4e07\u786e\uff01\u201d\u80e1\u6590\u53f3\u8db3\u8e0f\u5728\u957f\u51f3\u4e4b\u4e0a\uff0c\u4ece\u5305\u88b1\u4e2d\u62bd\u51fa\u5355\u5200\uff0c\u63d2\u5728\u684c\u4e0a\uff0c\u53eb\u9053\uff1a\u201c\u51b3\u8bf4\u4e0b\u53bb\uff01\u201d\u80d6\u5546\u4eba\u9053\uff1a\u201c\u8fd9\u2026\u2026\u8fd9\u4e0d\u5173\u6211\u4e8b\u3002\u201d \u3000\u3000\u9152\u697c\u4e0a\u7684\u9152\u5ba2\u4f19\u8ba1\u89c1\u80e1\u6590\u51f6\u795e\u6076\u715e\u4e00\u822c\uff0c\u4e2a\u4e2a\u80c6\u6218\u5fc3\u60ca\u3002\u80c6\u5c0f\u7684\u9152\u5ba2\u4e0d\u7b49\u5403\u5b8c\uff0c\u4e00\u4e2a\u4e2a\u4fbf\u6e9c\u4e0b\u697c\u53bb\u3002\u4f17\u4f19\u8ba1\u8fdc\u8fdc\u7ad9\u7740\uff0c\u8c01\u90fd\u4e0d\u6562\u8fc7\u6765\u3002 \u3000\u3000\u80e1\u6590\u53eb\u9053\uff1a\u201c\u51b3\u8bf4\uff0c\u5c0f\u4e09\u5b50\u809a\u4e2d\u53ef\u6709\u9e45\u8089\uff1f\u201d\u90a3\u80d6\u5546\u4eba\u9053\uff1a\u201c\u6ca1\u6709\u9e45\u8089\uff0c\u6ca1\u6709\u9e45\u8089\u3002\u4ed6\u809a\u8179\u4e4b\u4e2d\uff0c\u5168\u662f\u4e00\u9897\u9897\u87ba\u8089\u3002\u539f\u6765\u9524\u5bb6\u5bb6\u4e2d\u8d2b\u5bd2\uff0c\u6ca1\u4ec0\u4e48\u4e1c\u897f\u88f9\u8179\uff0c\u5c0f\u4e8c\u5c0f\u4e09\u54e5\u513f\u4fe9\u5c31\u5230\u7530\u91cc\u6478\u7530\u87ba\u5403\u3002\u87ba\u8089\u5f88\u786c\uff0c\u5c0f\u4e09\u5b50\u54ac\u4e0d\u70c2\uff0c\u4e00\u9897\u9897\u90fd\u56eb\u56f5\u7684\u541e\u4e86\u4e0b\u53bb\uff0c\u56e0\u6b64\u9694\u4e86\u5927\u534a\u5929\u8fd8\u6ca1\u5316\u3002\u4ed6\u8bf4\uff0c\u2018\u5403\u6211\uff0c\u5403\u6211\uff01\u2019\u5374\u662f\u8bf4\u7684\u2018\u5403\u87ba\uff01\u2019\u5509\uff0c\u597d\u597d\u4e00\u4e2a\u5b69\u5b50\uff0c\u4fbf\u8fd9\u4e48\u6b7b\u5728\u7956\u5e99\u4e4b\u4e2d\u3002\u9524\u56db\u5ac2\u4e5f\u5c31\u6b64\u75af\u4e86\u3002\u201d\uff08\u6309\uff1a\u5403\u87ba\u8bef\u4e3a\u5403\u9e45\uff0c\u7956\u5e99\u7834\u513f\u8179\u660e\u51a4\uff0c\u4e43\u786e\u6709\u5176\u4e8b\uff0c\u4f5b\u5c71\u9547\u8001\u4eba\u65e0\u4e00\u4e0d\u77e5\u3002\u4eca\u65e5\u6cb8\u5c71\u7956\u5e99\u4e4b\u4e2d\uff0c\u5317\u5e1d\u795e\u50cf\u4e4b\u524d\u6709\u8840\u5370\u77f3\u4e00\u65b9\uff0c\u5c1a\u6709\u9690\u9690\u8840\u8ff9\uff0c\u5373\u4e3a\u6b64\u5343\u53e4\u5947\u51a4\u4e4b\u89c1\u8bc1\u3002\u4f5c\u8005\u66fe\u4eb2\u773c\u89c1\u5230\u3002\u8bfb\u8005\u5982\u8d74\u4f5b\u5c71\uff0c\u53ef\u5f80\u53c2\u89c2\u3002\u552f\u6b64\u4e8b\u4e4b\u5e74\u4ee3\u53ca\u4eba\u7269\u59d3\u540d\uff0c\u5e74\u4e45\u5931\u4f20\u3002\u4f5c\u8005\u5f53\u65f6\u5411\u4f5b\u5c71\u9547\u4e0a\u6587\u5316\u754c\u4eba\u58eb\u8be6\u52a0\u6253\u542c\uff0c\u65e0\u4eba\u77e5\u6089\uff0c\u56e0\u6b64\u6587\u4e2d\u4eba\u540d\u53ca\u5176\u4ed6\u6545\u4e8b\u5747\u5c5e\u865a\u6784\u3002 \u3000\u3000\u80e1\u6590\u62d4\u8d77\u5355\u5200\uff0c\u53eb\u9053\uff1a\u201c\u8fd9\u59d3\u51e4\u7684\u4f4f\u5728\u54ea\u91cc\uff1f\u201d\u90a3\u80d6\u5546\u4eba\u8fd8\u672a\u56de\u7b54\uff0c\u5ffd\u542c\u5f97\u8fdc\u5904\u9690\u9690\u4f20\u6765\u4e00\u9635\u72ac\u5420\u4e4b\u58f0\uff0c\u7626\u5546\u4eba\u53f9\u9053\uff1a\u201c\u4f5c\u5b7d\uff0c\u4f5c\u5b7d\uff01\u201d\u80e1\u6590\u9053\uff1a\u201c\u8fd8\u6709\u4ec0\u4e48\u4e8b\uff1f\u201d\u7626\u5546\u4eba\u9053\uff1a\u201c\u90a3\u662f\u51e4\u8001\u7237\u7684\u5bb6\u4e01\u5e26\u4e86\u6076\u72d7\uff0c\u6b63\u5728\u8ffd\u62ff\u9524\u5bb6\u7684\u5c0f\u4e8c\u5b50\u3002\u201d\u80e1\u6590\u6012\u9053\uff1a\u201c\u51a4\u6789\u5df2\u7136\u8fa8\u660e\uff0c\u8fd8\u62ff\u4eba\u5e72\u4ec0\u4e48\uff1f\u201d\u7626\u5546\u4eba\u9053\uff1a\u201c\u51e4\u8001\u7237\u8a00\u9053\uff1a\u5c0f\u4e09\u5b50\u65e2\u7136\u6ca1\u5403\uff0c\u5b9a\u662f\u5c0f\u4e8c\u5b50\u5403\u4e86\uff0c\u56e0\u6b64\u8981\u62ff\u4ed6\u53bb\u8ffd\u95ee\u3002\u90bb\u5c45\u77e5\u9053\u51e4\u8001\u7237\u607c\u7f9e\u6210\u6012\uff0c\u975e\u628a\u8fd9\u4ef6\u51a4\u6789\u5957\u5728\u5c0f\u4e8c\u5b50\u5934\u4e0a\u4e0d\u53ef\uff0c\u6697\u6697\u53eb\u5c0f\u4e8c\u5b50\u9003\u8d70\u3002 \u3000\u3000\u4eca\u65e5\u51e4\u8001\u7237\u7684\u5bb6\u4e01\u5df2\u5230\u5904\u641c\u62ff\u4e86\u534a\u5929\u5462\u3002\u201d \u3000\u3000\u6b64\u65f6\u80e1\u6590\u53cd\u800c\u6291\u4f4f\u6012\u6c14\uff0c\u7b11\u9053\uff1a\u201c\u597d\u597d\uff0c\u4e24\u4f4d\u8bf4\u5f97\u660e\u767d\uff0c\u8fd9\u4e00\u4e07\u4e24\u94f6\u5b50\u6211\u4fbf\u5411\u51e4\u8001\u7237\u501f\u53bb\u3002\u201d\u8bf4\u7740\u63d0\u8d77\u9152\u58f6\u5c31\u53e3\u4fbf\u559d\uff0c\u5c06\u4e09\u58f6\u9152\u559d\u5f97\u6d93\u6ef4\u4e0d\u5269\uff0c\u4e00\u53e0\u58f0\u50ac\u4f19\u8ba1\u62ff\u9152\u6765\u3002 \u3000\u3000\u4f46\u542c\u5f97\u72d7\u5420\u58f0\u5406\u559d\u58f0\u8d8a\u6765\u8d8a\u8fd1\uff0c\u54cd\u5230\u4e86\u8857\u5934\u3002\u80e1\u6590\u9760\u5230\u7a97\u53e3\uff0c\u53ea\u89c1\u4e00\u4e2a\u5341\u4e8c\u4e09\u5c81\u7684\u5b69\u5b50\u4ece\u8f6c\u89d2\u5904\u6ca1\u547d\u5730\u5954\u6765\u3002\u4ed6\u8d64\u7740\u53cc\u8db3\uff0c\u8863\u88e4\u5df2\u88ab\u6076\u72d7\u7684\u722a\u7259\u6495\u5f97\u7a00\u70c2\uff0c\u8eab\u540e\u4e00\u8def\u6ef4\u7740\u9c9c\u8840\uff0c\u4e0d\u77e5\u4ed6\u4e0e\u4f17\u6076\u72ac\u5982\u4f55\u53ae\u6597\uff0c\u65b9\u80fd\u9003\u5230\u8fd9\u91cc\u3002\u4ed6\u8eab\u540e\u4e03\u516b\u4e08\u8fdc\u5904\uff0c\u5341\u4f59\u6761\u8c7a\u72fc\u822c\u7684\u731b\u72ac\u72c2\u53eb\u7740\u8ffd\u6765\uff0c\u773c\u89c1\u518d\u8fc7\u987b\u81fe\uff0c\u4fbf\u8981\u6251\u5230\u9524\u5c0f\u4e8c\u8eab\u4e0a\u3002 \u3000\u3000\u9524\u5c0f\u4e8c\u6b64\u65f6\u5df2\u662f\u7b4b\u75b2\u529b\u5c3d\uff0c\u7a81\u7136\u89c1\u5230\u6bcd\u4eb2\uff0c\u53eb\u4e00\u58f0\uff1a\u201d\u5988\uff01\u201d\u53cc\u817f\u4e00\u8f6f\uff0c\u6454\u5012\u5728\u5730\uff0c\u518d\u4e5f\u722c\u4e0d\u8d77\u6765\u3002\u9524\u56db\u5ac2\u867d\u7136\u795e\u667a\u80e1\u6d82\uff0c\u5374\u8ba4\u5f97\u513f\u5b50\uff0c\u731b\u5730\u7ad9\u8d77\uff0c\u51b2\u4e86\u8fc7\u53bb\uff0c\u6321\u5728\u4f17\u6076\u72ac\u4e4b\u524d\uff0c\u62a4\u4f4f\u513f\u5b50\u3002\u4f17\u6076\u72ac\u767b\u65f6\u4e00\u9f50\u7ad9\u5b9a\uff0c\u9732\u51fa\u767d\u68ee\u68ee\u7684\u7259\u9f7f\uff0c\u545c\u545c\u53d1\u5a01\u3002 \u3000\u3000\u8fd9\u4e9b\u6076\u72ac\u53ea\u53ea\u51f6\u731b\u5f02\u5e38\uff0c\u5e73\u65f6\u8ddf\u7740\u51e4\u8001\u7237\u6253\u730e\uff0c\u8fde\u8001\u864e\u5927\u718a\u4e5f\u6562\u4e0e\u4e4b\u640f\u6597\uff0c\u4f46\u89c1\u4e86\u9524\u56db\u5ac2\u8fd9\u80a1\u62fc\u6b7b\u62a4\u5b50\u7684\u795e\u6001\uff0c\u4e00\u65f6\u7adf\u7136\u4e0d\u6562\u903c\u8fd1\u3002\u4f17\u5bb6\u4e01\u5927\u58f0\u5406\u559d\uff0c\u50ac\u4fc3\u6076\u72ac\u3002\u53ea\u542c\u5f97\u545c\u545c\u51e0\u58f0\uff0c\u4e24\u5934\u51f6\u72fc\u822c\u7684\u5927\u72ac\u8dc3\u8d77\u8eab\u6765\uff0c\u5411\u5367\u5728\u5730\u4e0a\u7684\u9524\u5c0f\u4e8c\u54ac\u53bb\u3002 \u3000\u3000\u9524\u56db\u5ac2\u6251\u5728\u513f\u5b50\u8eab\u4e0a\uff0c\u7b2c\u4e00\u5934\u5927\u72ac\u5f20\u5f00\u5229\u53e3\uff0c\u54ac\u4f4f\u5979\u7684\u80a9\u5934\u3002\u7b2c\u4e8c\u5934\u6076\u72ac\u5374\u54ac\u4e2d\u5979\u7684\u5de6\u817f\u3002\u53cc\u72ac\u7528\u529b\u62c9\u626f\uff0c\u5c31\u4f3c\u6253\u730e\u65f6\u64d2\u7740\u767d\u5154\u82b1\u9e7f\u4e00\u822c\u3002\u4f17\u5bb6\u4e01\u547c\u559d\u52a9\u5a01\uff0c\u9524\u56db\u5ac2\u4e0d\u987e\u81ea\u8eab\u75bc\u75db\uff0c\u4ecd\u662f\u62a4\u4f4f\u513f\u5b50\uff0c\u4e0d\u8ba9\u4ed6\u53d7\u6076\u72ac\u7684\u4fb5\u88ad\u3002\u9524\u5c0f\u4e8c\u4ece\u6bcd\u4eb2\u8eab\u4e0b\u722c\u4e86\u51fa\u6765\uff0c\u4e00\u9762\u54ed\u558a\uff0c\u4e00\u9762\u548c\u4f17\u6076\u72ac\u53ae\u6253\uff0c\u6551\u62a4\u6bcd\u4eb2\u3002\u970e\u65f6\u4e4b\u95f4\uff0c\u5341\u4f59\u6761\u6076\u72ac\u4ece\u56db\u9762\u516b\u65b9\u56f4\u653b\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u8857\u5934\u770b\u70ed\u95f9\u7684\u95f2\u4eba\u867d\u6b22\uff0c\u4f46\u8feb\u4e8e\u51e4\u8001\u7237\u7684\u5a01\u52bf\uff0c\u4e2a\u4e2a\u6562\u6012\u800c\u4e0d\u6562\u8a00\u3002\u8981\u77e5\u5f53\u6b64\u60c5\u666f\u4e4b\u4e0b\uff0c\u53ea\u8981\u6709\u8c01\u7a0d\u7a0d\u60f9\u607c\u4e86\u8fd9\u4e9b\u5bb6\u4e01\uff0c\u4e00\u4e2a\u624b\u52bf\u4e4b\u4e0b\uff0c\u4f17\u6076\u72ac\u7acb\u65f6\u6251\u4e0a\u8eab\u6765\u3002\u6709\u7684\u4e0d\u5fcd\u5352\u7779\u8fd9\u573a\u60e8\u5267\uff0c\u63a9\u9762\u907f\u5f00\u3002\u4f17\u5bb6\u4e01\u5374\u662f\u5174\u9ad8\u91c7\u70c8\uff0c\u72b9\u4f3c\u6355\u83b7\u5230\u4e86\u5927\u730e\u7269\u4e00\u822c\u3002 \u3000\u3000\u80e1\u6590\u5728\u9152\u697c\u4e0a\u77a7\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u4ed6\u8fdf\u8fdf\u4e0d\u51fa\u624b\u6551\u4eba\uff0c\u662f\u8981\u4eb2\u773c\u770b\u660e\u767d\u90a3\u51e4\u5929\u5357\u662f\u5426\u771f\u5982\u8fd9\u4e24\u4e2a\u5546\u4eba\u6240\u8bf4\u7684\u90a3\u4e48\u6b79\u6bd2\uff0c\u4ee5\u514d\u8bef\u4fe1\u4eba\u8a00\uff0c\u51a4\u6789\u65e0\u8f9c\u3002\u521d\u65f6\u4ed6\u542c\u80d6\u5546\u4eba\u8ff0\u8bf4\u8fd9\u4ef6\u60e8\u4e8b\uff0c\u5fc3\u4e2d\u6781\u5176\u607c\u6012\uff0c\u540e\u6765\u542c\u8bf4\u90a3\u51e4\u5929\u5357\u65e2\u5df2\u5e73\u767d\u65e0\u7aef\u5730\u903c\u6b7b\u4e86\u4e00\u6761\u4eba\u547d\uff0c\u8fd8\u6d3e\u6076\u72ac\u8ffd\u6355\u53e6\u4e00\u4e2a\u5b69\u5b50\uff0c\u89c9\u5f97\u4e16\u4e0a\u7eb5\u6709\u72e0\u6076\u4e4b\u4eba\uff0c\u4ea6\u4e0d\u8be5\u5982\u6b64\u8fc7\u4efd\uff0c\u5012\u6709\u4e9b\u5c06\u4fe1\u5c06\u7591\u8d77\u6765\uff0c\u76f4\u5230\u4eb2\u89c1\u6076\u72ac\u6251\u54ac\u9524\u6c0f\u6bcd\u5b50\uff0c\u90a3\u65f6\u66f4\u65e0\u6000\u7591\uff0c\u773c\u89c1\u8857\u5934\u8840\u8089\u6a2a\u98de\uff0c\u518d\u8fdf\u5f97\u7247\u523b\uff0c\u8fd9\u4e00\u53cc\u6148\u6bcd\u5b5d\u5b50\u4e0d\u514d\u6b7b\u4e8e\u5f53\u573a\uff0c\u5f53\u4e0b\u6293\u8d77\u684c\u4e0a\u4e09\u53cc\u7b77\u5b50\uff0c\u52b2\u900f\u53f3\u81c2\uff0c\u4e00\u679a\u679a\u7684\u63b7\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u4f46\u542c\u5f97\u6c6a\u6c6a\u6c6a\u3001\u545c\u545c\u545c\u51e0\u58f0\u6e17\u53eb\uff0c\u516d\u5934\u6076\u72ac\u5747\u88ab\u7b77\u5b50\u6253\u4e2d\u8111\u95e8\uff0c\u4f0f\u5730\u800c\u6b7b\uff0c\u5176\u4f59\u6076\u72ac\u5446\u5728\u5f53\u5730\uff0c\u4e0d\u77e5\u8be5\u5f53\u7ee7\u7eed\u6251\u54ac\uff0c\u8fd8\u662f\u8f6c\u8eab\u9003\u53bb\u3002\u80e1\u6590\u53c8\u62ff\u8d77\u684c\u4e0a\u7684\u9152\u676f\uff0c\u98de\u63b7\u4e0b\u8857\uff0c\u5f53\u771f\u662f\u5dee\u4e0d\u5931\u5bf8\uff0c\u52b2\u529b\u900f\u9aa8\uff0c\u6bcf\u4e00\u53ea\u9152\u676f\u7684\u676f\u5e95\u90fd\u51fb\u4e2d\u5728\u6bcf\u4e00\u5934\u6076\u72ac\u7684\u9f3b\u5934\u4e0a\u3002\u4e09\u5934\u5927\u72d7\u53eb\u4e5f\u6ca1\u53eb\u4e00\u58f0\uff0c\u767b\u65f6\u7ffb\u8eab\u800c\u6b7b\u3002\u4f59\u4e0b\u51e0\u6761\u6076\u72ac\u5c06\u5c3e\u5df4\u631f\u5728\u540e\u817f\u4e4b\u95f4\uff0c\u8f6c\u773c\u9003\u5f97\u4e0d\u77e5\u53bb\u5411\u3002 \u3000\u3000\u5e26\u72d7\u7684\u5bb6\u4e01\u5171\u6709\u516d\u4eba\uff0c\u4ed7\u7740\u51e4\u5929\u5357\u7684\u5a01\u52bf\uff0c\u5728\u4f5b\u5c71\u9547\u4e0a\u4e00\u5411\u51f6\u6a2a\u60ef\u4e86\u7684\uff0c\u773c\u89c1\u80e1\u6590\u65bd\u5c55\u7edd\u6280\u6740\u72d7\uff0c\u7adf\u7136\u4e0d\u77e5\u6b7b\u6d3b\uff0c\u4e00\u9f50\u6012\u559d\uff1a\u201c\u4ec0\u4e48\u4eba\u5230\u4f5b\u5c71\u9547\u6765\u6492\u91ce\uff1f\u6253\u6b7b\u4e86\u51e4\u8001\u7237\u7684\u72d7\uff0c\u8981\u4f60\u8fd9\u5c0f\u5b50\u507f\u547d\u3002\u201d\u5404\u4eba\u8eab\u4e0a\u90fd\u5e26\u7740\u5355\u5200\u94c1\u94fe\uff0c\u7eb7\u7eb7\u53d6\u51fa\uff0c\u8702\u62e5\u7740\u62a2\u4e0a\u697c\u6765\u3002 \u3000\u3000\u4f17\u9152\u5ba2\u89c1\u5230\u8fd9\u526f\u9635\u4ed7\uff0c\u767b\u65f6\u4e00\u9635\u5927\u4e71\u3002\u90a3\u201c\u82f1\u96c4\u697c\u201d\u662f\u51e4\u5929\u5357\u7684\u4ea7\u4e1a\uff0c\u638c\u67dc\u7684\u3001\u7ad9\u5802\u7684\u3001\u9001\u83dc\u7684\u3001\u5927\u53a8\u4e8c\u53a8\uff0c\u4e00\u89c1\u51e4\u5e9c\u5bb6\u4e01\u4e0a\u697c\u62ff\u4eba\uff0c\u5404\u81ea\u6284\u8d77\u706b\u53c9\u3001\u83dc\u5200\u3001\u94c1\u68d2\uff0c\u90fd\u8981\u76f8\u5e2e\u52a8\u624b\u3002\u80e1\u6590\u77a7\u5728\u773c\u91cc\uff0c\u53ea\u662f\u5fae\u5fae\u51b7\u7b11\u3002 \u3000\u3000\u4f46\u89c1\u516d\u540d\u5bb6\u4e01\u5954\u5230\u8eab\u524d\uff0c\u4e3a\u9996\u4e00\u4eba\u5c06\u94c1\u94fe\u545b\u5577\u5577\u4e00\u6296\uff0c\u559d\u9053\uff1a\u201c\u81ed\u5c0f\u5b50\uff0c\u8ddf\u8001\u7237\u8d70\u5427\u3002\u201d\u80e1\u6590\u5fc3\u60f3\uff1a\u201c\u4e00\u4e2a\u4e61\u7ec5\u7684\u5bb6\u4e01\uff0c\u4e5f\u6562\u62ff\u94c1\u94fe\u9501\u4eba\uff0c\u8fd9\u59d3\u51e4\u7684\u5e9c\u4e2d\uff0c\u96be\u9053\u5c31\u662f\u4f5b\u5c71\u9547\u7684\u8859\u95e8\uff1f\u201d\u4ed6\u4e5f\u4e0d\u7ad9\u8d77\uff0c\u53cd\u624b\u4e00\u638c\uff0c\u6b63\u4e2d\u90a3\u5bb6\u4e86\u7684\u5de6\u8138\uff0c\u624b\u638c\u7f29\u56de\u65f6\uff0c\u987a\u624b\u5728\u4ed6\u524d\u9888\u201c\u7d2b\u5bab\u201d\u3001\u540e\u8111\u201c\u98ce\u5e9c\u201d\u4e24\u7a74\u5404\u70b9\u4e86\u4e00\u4e0b\u3002 \u3000\u3000\u8fd9\u662f\u4eba\u8eab\u7684\u4e24\u5904\u5927\u7a74\uff0c\u90a3\u5bb6\u4e01\u767b\u65f6\u5446\u5446\u7ad9\u7740\uff0c\u52a8\u5f39\u4e0d\u5f97\u3002 \u3000\u3000\u5176\u65f6\u7b2c\u4e8c\u3001\u7b2c\u4e09\u4e2a\u5bb6\u4e01\u5c1a\u672a\u77a7\u5f97\u660e\u767d\uff0c\u5404\u633a\u5355\u5200\u4ece\u5de6\u53f3\u88ad\u4e0a\u3002\u80e1\u6590\u89c1\u4e8c\u4eba\u53cc\u5200\u780d\u6765\u65f6\u9887\u6709\u52b2\u529b\uff0c\u663e\u662f\u7ec3\u8fc7\u51e0\u5e74\u6b66\u529f\uff0c\u5012\u975e\u5bfb\u5e38\u72d0\u5047\u864e\u5a01\u7684\u6076\u5974\u53ef\u6bd4\uff0c\u6b63\u662f\u5982\u6b64\uff0c\u66f4\u53ef\u60f3\u89c1\u90a3\u51e4\u5929\u5357\u7684\u51f6\u6a2a\uff0c\u5f53\u4e0b\u5982\u6cd5\u70ae\u5236\uff0c\u556a\u556a\u4e24\u8bb0\u5df4\u638c\uff0c\u6253\u5f97\u90a3\u4e24\u540d\u5bb6\u4e86\u6123\u6123\u7684\u7ad9\u7740\u3002\u4f59\u4e0b\u4e09\u540d\u5bb6\u4e01\u77a7\u51fa\u52bf\u5934\u4e0d\u5bf9\uff0c\u4e00\u4e2a\u8f6c\u8eab\u6b32\u8d70\uff0c\u53e6\u4e00\u4e2a\u53eb\u9053\uff1a\u201c\u51e4\u4e03\u7237\uff0c\u4f60\u6765\u77a7\u77a7\u8fd9\u662f\u4ec0\u4e48\u90aa\u95e8\u3002\u201d \u3000\u3000\u90a3\u51e4\u4e03\u662f\u51e4\u5929\u5357\u7684\u8fdc\u623f\u65cf\u5f1f\uff0c\u5c31\u5728\u8fd9\u82f1\u96c4\u9152\u697c\u5f53\u638c\u67dc\uff0c\u6b66\u529f\u662f\u6ca1\u6709\u4ec0\u4e48\uff0c\u4e3a\u4eba\u5374\u6781\u662f\u673a\u7075\uff0c\u8fd9\u65f6\u5df2\u7ad9\u5728\u697c\u5934\uff0c\u77a7\u51fa\u80e1\u6590\u6b66\u529f\u751a\u662f\u4e86\u5f97\uff0c\u5f53\u5373\u62a2\u4e0a\u4e24\u6b65\uff0c\u62b1\u62f3\u8bf4\u9053\uff1a\u201c\u539f\u6765\u4eca\u65e5\u82f1\u96c4\u9a7e\u5230\uff0c\u6055\u51e4\u67d0\u6709\u773c\u4e0d\u8bc6\u6cf0\u5c71\u2026\u2026\u201d \u3000\u3000\u80e1\u6590\u89c1\u4e09\u540d\u5bb6\u4e01\u6162\u6162\u5411\u697c\u5934\u79fb\u6b65\uff0c\u60f3\u4e58\u673a\u6e9c\u8d70\uff0c\u5f53\u5373\u4ece\u8eab\u8fb9\u7ad9\u7740\u4e0d\u52a8\u7684\u5bb6\u4e01\u624b\u4e2d\u53d6\u8fc7\u94c1\u94fe\uff0c\u7740\u5730\u5377\u53bb\uff0c\u56de\u52b2\u4e00\u626f\uff0c\u94c1\u94fe\u5df2\u5377\u4f4f\u4e09\u540d\u5bb6\u4e01\u516d\u53ea\u811a\uff0c\u4f46\u542c\u5f97\u201c\u554a\u54df\uff0c\u554a\u54df\u201d\u58f0\u4e2d\uff0c\u4e09\u4e2a\u4eba\u6a2a\u5012\u5728\u5730\uff0c\u8dcc\u6210\u4e00\u5806\uff0c\u4e00\u9f50\u7ed9\u4ed6\u62d6\u5c06\u8fc7\u6765\u3002 \u3000\u3000\u80e1\u6590\u62ff\u8d77\u94c1\u94fe\u4e24\u7aef\uff0c\u6253\u4e86\u4e00\u4e2a\u6b7b\u7ed3\uff0c\u5bf9\u51e4\u4e03\u6beb\u4e0d\u7406\u776c\uff0c\u81ea\u659f\u81ea\u996e\u3002 \u3000\u3000\u82f1\u96c4\u697c\u4f17\u4f19\u8ba1\u867d\u89c1\u80e1\u6590\u51fa\u624b\u5389\u5bb3\uff0c\u4f46\u60f3\u597d\u6c49\u654c\u4e0d\u8fc7\u4eba\u591a\uff0c\u5404\u6267\u5bb6\u4f19\uff0c\u5e03\u6210\u9635\u52bf\uff0c\u53ea\u5f85\u51e4\u4e03\u7237\u4e00\u58f0\u4ee4\u4e0b\uff0c\u4fbf\u5373\u4e00\u62e5\u800c\u4e0a\u3002 \u3000\u3000\u80e1\u6590\u559d\u4e86\u4e00\u676f\u9152\uff0c\u95ee\u9053\uff1a\u201d\u51e4\u5929\u5357\u662f\u4f60\u4ec0\u4e48\u4eba\uff1f\u201d\u51e4\u4e03\u7b11\u9053\uff1a\u201c\u51e4\u8001\u7237\u662f\u5728\u4e0b\u7684\u65cf\u5144\uff0c\u5c0a\u9a7e\u53ef\u8ba4\u5f97\u4ed6\u4e48\uff1f\u201d\u80e1\u6590\u9053\uff1a\u201c\u4e0d\u8ba4\u5f97\uff0c\u4f60\u53bb\u53eb\u4ed6\u6765\u89c1\u6211\u3002\u201d \u3000\u3000\u51e4\u4e03\u5fc3\u4e2d\u6709\u6c14\uff0c\u6697\u9053\uff1a\u201c\u51ed\u4f60\u8fd9\u5c0f\u5b50\u4e5f\u8bf7\u5f97\u52a8\u51e4\u8001\u7237\uff1f\u4fbf\u662f\u4f60\u767b\u95e8\u78d5\u5934\uff0c\u4e5f\u4e0d\u77e5\u4ed6\u8001\u4eba\u5bb6\u89c1\u4e0d\u89c1\u4f60\u5462\uff1f\u201d\u4f46\u8138\u4e0a\u4ecd\u662f\u7b11\u563b\u563b\u5730\u9053\uff1a\u201c\u8bf7\u6559\u5c0a\u9a7e\u8d35\u59d3\u5927\u540d\uff0c\u597d\u5f97\u901a\u62a5\u3002\u201d \u3000\u3000\u80e1\u6590\u9053\uff1a\u201c\u6211\u59d3\u62d4\uff0c\u6740\u9e21\u62d4\u6bdb\u7684\u62d4\u3002\u201d\u51e4\u4e03\u6697\u81ea\u54fa\u5495\uff1a\u201c\u600e\u4e48\u6709\u8fd9\u4e2a\u602a\u59d3\u513f\uff1f\u201d\u966a\u7b11\u9053\uff1a\u201c\u539f\u6765\u662f\u62d4\u7237\uff0c\u7269\u4ee5\u7a00\u4e3a\u8d35\uff0c\u62d4\u7237\u7684\u59d3\u6570\uff0c\u5357\u65b9\u5012\u662f\u5c11\u6709\u3002\u201d \u3000\u3000\u80e1\u6590\u9053\uff1a\u201c\u662f\u554a\uff0c\u4fd7\u8bed\u9053\u7269\u4ee5\u7a00\u4e3a\u8d35\uff0c\u6389\u53e5\u6587\u4fbf\u662f\u2018\u51e4\u6bdb\u9e9f\u89d2\u2019\uff0c\u5728\u4e0b\u7684\u540d\u5b57\u4fbf\u53eb\u4f5c\u2018\u51e4\u6bdb\u2019\u3002\u201d\u51e4\u4e03\u7b11\u9053\uff1a\u201c\u9ad8\u96c5\uff0c\u9ad8\u96c5\uff01\u201d\u7a81\u7136\u8f6c\u5ff5\uff1a\u201c\u4e0d\u5bf9\uff0c\u4ed6\u8fd9\u2018\u62d4\u51e4\u6bdb\u2019\u4e09\u5b57\uff0c\u5c82\u4e0d\u662f\u6709\u610f\u6765\u5bfb\u6666\u6c14\uff0c\u627e\u5c94\u5b50\uff1f\u201d\u8138\u8272\u4e00\u53d8\uff0c\u5389\u58f0\u9053\uff1a\u201c\u5c0a\u9a7e\u5230\u5e95\u662f\u8c01\uff1f\u5230\u4f5b\u5c71\u9547\u6709\u4f55\u8d35\u5e72\uff1f\u201d\u80e1\u6590\u7b11\u9053\uff1a\u201c\u65e9\u5c31\u542c\u8bf4\u4f5b\u5c71\u9547\u6709\u51e0\u53ea\u6076\u51e4\u51f0\uff0c\u6211\u65e2\u7136\u540d\u53eb\u62d4\u51e4\u6bdb\uff0c\u4fbf\u5f97\u6765\u62d4\u51e0\u6839\u6bdb\u513f\u800d\u800d\u3002\u201d \u3000\u3000\u51e4\u4e03\u9000\u540e\u4e00\u6b65\uff0c\u545b\u5577\u4e00\u54cd\uff0c\u4ece\u8170\u95f4\u53d6\u51fa\u4e00\u6761\u8f6f\u97ad\uff0c\u5de6\u624b\u4e00\u6446\uff0c\u53eb\u624b\u4e0b\u4f17\u4eba\u5c0f\u5fc3\u5728\u610f\uff0c\u53f3\u8155\u6296\u52a8\uff0c\u8f6f\u97ad\u631f\u7740\u4e00\u80a1\u52b2\u98ce\uff0c\u5411\u80e1\u6590\u5934\u4e0a\u731b\u51fb\u4e0b\u6765\u3002 \u3000\u3000\u80e1\u6590\u5fc3\u4e2d\u76d8\u7b97\u5df2\u5b9a\uff1a\u201c\u5355\u51ed\u98ce\u5929\u5357\u4e00\u4eba\uff0c\u4e5f\u4e0d\u80fd\u5982\u6b64\u4f5c\u6076\u591a\u7aef\u3002\u4ed6\u624b\u4e0b\u7684\u5e2e\u51f6\u4e4b\u8f88\uff0c\u4e2a\u4e2a\u6b7b\u6709\u4f59\u8f9c\u3002\u4eca\u65e5\u4e0b\u624b\u4e0d\u5fc5\u5bb9\u60c5\u3002\u201d\u773c\u89c1\u8f6f\u97ad\u6253\u5230\uff0c\u53cd\u624b\u4e00\u5e26\uff0c\u5df2\u6293\u4f4f\u97ad\u5934\uff0c\u8f7b\u8f7b\u5411\u5185\u4e00\u626f\uff0c\u51e4\u4e03\u7acb\u8db3\u4e0d\u4f4f\uff0c\u5411\u524d\u51b2\u4e86\u8fc7\u6765\u3002\u80e1\u6590\u5de6\u624b\u5728\u4ed6\u80a9\u5934\u4e00\u62cd\uff0c\u51e4\u4e03\u4f46\u89c9\u4e00\u80a1\u6781\u5927\u529b\u91cf\u5f80\u4e0b\u6324\u8feb\uff0c\u4e0d\u7531\u81ea\u4e3b\u7684\u53cc\u819d\u4e00\u8f6f\uff0c\u8dea\u5012\u5728\u5730\u3002\u80e1\u6590\u7b11\u9053\uff1a\u201c\u4e0d\u6562\u5f53\uff01\u201d\u987a\u4e0b\u5c06\u90a3\u5341\u4e09\u8282\u8f6f\u97ad\u5f80\u4ed6\u8eab\u4e0a\u4e00\u5377\uff0c\u5df2\u5c06\u4ed6\u7f1a\u5728\u4e00\u5f20\u516b\u4ed9\u684c\u684c\u811a\u4e0a\u3002 \u3000\u3000\u9152\u697c\u4f17\u4f19\u8ba1\u6b63\u8981\u6251\u4e0a\u52a8\u624b\uff0c\u7a81\u89c1\u5982\u6b64\u53d8\u6545\uff0c\u5413\u5f97\u4e00\u9f50\u505c\u6b65\u3002 \u3000\u3000\u80e1\u6590\u6307\u7740\u4e00\u4e2a\u80a5\u80a5\u7684\u53a8\u5b50\u53eb\u9053\uff1a\u201c\u5582\uff0c\u5c06\u83dc\u5200\u62ff\u6765\u3002\u201d\u90a3\u80a5\u53a8\u5b50\u5f20\u5927\u4e86\u5634\uff0c\u4e0d\u6562\u8fdd\u62d7\uff0c\u5c06\u4e0b\u4e2d\u63e1\u7740\u7684\u83dc\u5200\u9012\u4e86\u8fc7\u53bb\u3002\u80e1\u6590\u9053\uff1a\u201c\u7092\u91cc\u810a\u7528\u4ec0\u4e48\u6750\u6599\uff1f\u201d \u3000\u3000\u80a5\u53a8\u5b50\u9053\uff1a\u201c\u7528\u732a\u80cc\u4e0a\u810a\u9aa8\u4e24\u65c1\u7684\u4e0a\u597d\u7cbe\u8089\uff0c\u4f60\u662f\u8981\u5403\u7cd6\u918b\u3001\u6912\u76d0\u3001\u6cb9\u70b8\uff0c\u8fd8\u662f\u6e05\u7092\uff1f\u201d\u80e1\u6590\u4f38\u4e0b\u4e00\u626f\uff0c\u55e4\u7684\u4e00\u54cd\uff0c\u5c06\u51e4\u4e03\u80cc\u4e0a\u7684\u8863\u670d\u6495\u7834\u3002\u9732\u51fa\u80a5\u80a5\u767d\u767d\u7684\u80cc\u810a\u6765\uff0c\u6478\u6478\u4ed6\u7684\u810a\u6881\uff0c\u9053\uff1a\u201d\u662f\u4e0d\u662f\u8fd9\u91cc\u4e0b\u5200\uff1f\u201d\u90a3\u80a5\u53a8\u5b50\u7684\u5927\u53e3\u5f20\u5f97\u66f4\u5927\uff0c\u54ea\u6562\u56de\u7b54\uff1f\u51e4\u4e03\u8fde\u8fde\u78d5\u5934\uff0c\u53eb\u9053\uff1a\u201c\u82f1\u96c4\u9976\u547d\uff01\u201d\u80e1\u6590\u5fc3\u60f3\uff1a\u201c\u9976\u4f60\u6027\u547d\u53ef\u4ee5\uff0c\u4f46\u4e0d\u7ed9\u4f60\u5403\u4e9b\u82e6\u5934\uff0c\u5c82\u4e0d\u662f\u4f5c\u6076\u6ca1\u6709\u62a5\u5e94\uff1f\u201d\u83dc\u5200\u4e00\u8d77\uff0c\u5728\u4ed6\u810a\u9aa8\u65c1\u5212\u4e86\u4e00\u6761\u957f\u957f\u7684\u4f24\u53e3\uff0c\u95ee\u9053\uff1a\u201c\u534a\u65a4\u591f\u4e86\u4e48\uff1f\u201d\u53a8\u5b50\u5446\u5934\u5446\u8111\u5730\u9053\uff1a\u201c\u4e00\u4e2a\u4eba\u5403\uff0c\u5df2\u7ecf\u591f\u5566\uff01\u201d \u3000\u3000\u98ce\u4e03\u5413\u5f97\u9b42\u98de\u5929\u5916\uff0c\u4f46\u89c9\u80cc\u4e0a\u5267\u75db\uff0c\u53ea\u9053\u771f\u7684\u5df2\u7ed9\u4ed6\u5272\u4e86\u534a\u65a4\u91cc\u810a\u8089\u53bb\uff0c\u53ea\u542c\u80e1\u6590\u53c8\u95ee\uff1a\u201d\u7092\u732a\u809d\u7528\u4ec0\u4e48\u4f5c\u6599\uff1f\u6e05\u84b8\u732a\u8111\u7528\u4ec0\u4e48\u4f5c\u6599\uff1f\u201d\u51e4\u4e03\u5fc3\u60f3\uff1a \u3000\u3000\u201c\u7092\u91cc\u810a\u90a3\u8fd8\u7f62\u4e86\uff0c\u8fd9\u7092\u732a\u809d\u3001\u84b8\u732a\u8111\u4e24\u6837\u4e00\u4f5c\uff0c\u6211\u8fd9\u6761\u8001\u547d\uff0c\u8fd8\u5269\u5f97\u4e0b\u4e48\uff1f\u201d \u3000\u3000\u62da\u547d\u7684\u78d5\u5934\uff0c\u53ea\u628a\u697c\u677f\u78d5\u5f97\u51ac\u51ac\u76f4\u54cd\uff0c\u53eb\u9053\uff1a\u201c\u82f1\u96c4\u6709\u4e8b\u4fbf\u8bf7\u5429\u5490\uff0c\u53ea\u6c42\u9976\u4e86\u5c0f\u4eba\u4e00\u547d\u3002\u201d \u3000\u3000\u80e1\u6590\u89c1\u5413\u5f97\u4ed6\u4e5f\u591f\u4e86\uff0c\u559d\u9053\uff1a\u201d\u4f60\u8fd8\u6562\u5e2e\u90a3\u98ce\u5929\u5357\u4f5c\u6076\u4e48\uff1f\u201d\u51e4\u4e03\u5fd9\u9053\uff1a\u201c\u5c0f\u4eba\u4e0d\u6562\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u597d\uff0c\u5feb\u8d76\u8d70\u697c\u4e0a\u4e0e\u96c5\u5ea7\u7684\u5ba2\u4eba\uff0c\u5927\u5802\u4e0e\u697c\u4e0b\u7684\u5ba2\u4eba\u4e00\u4e2a\u4e5f\u4e0d\u8bb8\u8d70\u3002\u201d\u51e4\u4e03\u53eb\u9053\uff1a\u201c\u4f19\u8ba1\uff0c\u5feb\u9075\u7167\u8fd9\u3000\u4f4d\u597d\u6c49\u7237\u7684\u5429\u5490\uff0c\u5feb\uff01\u5feb\uff01\u201d \u3000\u3000\u697c\u4e0a\u4f17\u9152\u5ba2\u4e0d\u662f\u8d22\u4e3b\uff0c\u4fbf\u662f\u5bcc\u5546\uff0c\u4e2a\u4e2a\u6015\u4e8b\uff0c\u4e00\u89c1\u6253\u67b6\uff0c\u65e9\u60f3\u6e9c\u8d70\uff0c\u82e6\u4e8e\u68af\u53e3\u7ed9\u4e0b\u6267\u5175\u5203\u7684\u4f17\u4f19\u8ba1\u5b88\u4f4f\uff0c\u6b32\u884c\u4e0d\u5f97\uff0c\u8fd9\u65f6\u4e5f\u4e0d\u7528\u4eba\u8d76\uff0c\u65e9\u5fc3\u6025\u614c\u5fd9\u5730\u8d70\u4e86\u3002\u697c\u4e0b\u5927\u5802\u7684\u5ba2\u4eba\u90fd\u662f\u7a77\u6c49\uff0c\u5341\u4e2a\u4e2d\u5012\u6709\u4e03\u516b\u4e2a\u5403\u8fc7\u51e4\u4e03\u7684\u4e8f\uff0c\u89c1\u4eca\u65e5\u6709\u4eba\u4e0a\u95e8\u5bfb\u4e8b\uff0c\u5b9e\u5728\u8bf4\u4e0d\u51fa\u7684\u75db\u5feb\uff0c\u90fd\u8981\u7559\u4e0b\u6765\u77a7\u77a7\u70ed\u95f9\u3002 \u3000\u3000\u80e1\u6590\u53eb\u9053\uff1a\u201c\u4eca\u65e5\u6211\u8bf7\u5ba2\uff0c\u670b\u53cb\u4eec\u7684\u9152\u996d\u94b1\uff0c\u90fd\u7b97\u5728\u6211\u5e10\u4e0a\uff0c\u4f60\u4e0d\u8bb8\u6536\u4e00\u6587\u94b1\uff0c\u5feb\u62ac\u9152\u575b\u5b50\u51fa\u6765\uff0c\u505a\u6700\u597d\u7684\u83dc\u80b4\u656c\u5ba2\uff0c\u628a\u8857\u4e0a\u4e5d\u53ea\u6076\u72d7\u5bb0\u4e86\uff0c\u70e7\u72d7\u8089\u8bf7\u5927\u5bb6\u5403\u3002\u201d\u4ed6\u5429\u5490\u4e00\u53e5\uff0c\u51e4\u4e03\u7b54\u5e94\u4e00\u53e5\u3002 \u3000\u3000\u4f17\u4f19\u8ba1\u884c\u52a8\u7a0d\u8fdf\uff0c\u80e1\u6590\u4fbf\u626c\u8d77\u83dc\u5200\uff0c\u95ee\u90a3\u80a5\u53a8\u5b50\uff1a\u201c\u7ea2\u70e7\u5927\u80a0\u7528\u4ec0\u4e48\u4f5c\u6599\uff1f\u7092\u8170\u82b1\u7528\u4ec0\u4e48\u4f5c\u6599\uff1f\u201d\u90a3\u53a8\u5b50\u636e\u5b9e\u56de\u7b54\uff0c\u7528\u7684\u662f\u5927\u80a0\u4e00\u526f\uff0c\u8170\u5b50\u4e24\u679a\u3002 \u3000\u3000\u53ea\u628a\u51e4\u4e03\u60ca\u5f97\u8138\u65e0\u4eba\u8272\uff0c\u4e0d\u4f4f\u53e3\u7684\u50ac\u4fc3\u3002 \u3000\u3000\u90a3\u516d\u540d\u5bb6\u4e01\u89c1\u80e1\u6590\u5982\u6b64\u51f6\u72e0\uff0c\u4e0d\u77e5\u4ed6\u8981\u5982\u4f55\u5bf9\u4ed8\u81ea\u5df1\uff0c\u5fc3\u4e2d\u90fd\u5982\u5341\u4e94\u53ea\u540a\u6876\u6253\u6c34\uff0c\u4e03\u4e0a\u516b\u843d\uff0c\u5077\u77a7\u80e1\u6590\u7684\u8138\u8272\u4e00\u773c\uff0c\u53c8\u4e92\u76f8\u5bf9\u671b\u4e00\u773c\uff0c\u5fc3\u4e2d\u53ea\u662f\u7126\u6025\uff1a\u201c\u51e4\u8001\u7237\u600e\u5730\u8fd8\u4e0d\u8fc7\u6765\u6551\u4eba\uff1f\u518d\u8fdf\u7247\u523b\uff0c\u8fd9\u51f6\u795e\u4fbf\u8981\u6765\u5bf9\u4ed8\u6211\u4eec\u4e86\u3002\u201d \u3000\u3000\u80e1\u6590\u89c1\u4f17\u4f19\u8ba1\u5df2\u7167\u81ea\u5df1\u5429\u5490\uff0c\u4e00\u4e00\u529e\u7406\u4e0d\u8bef\uff0c\u5927\u6b65\u8d70\u5230\u697c\u4e0b\uff0c\u5012\u4e86\u4e00\u5927\u7897\u9152\uff0c\u8bf4\u9053\uff1a\u201c\u4eca\u65e5\u5c0f\u5f1f\u8bf7\u5ba2\uff0c\u5404\u4f4d\u653e\u91cf\u996e\u9152\uff0c\u60f3\u5403\u4ec0\u4e48\uff0c\u4fbf\u53eb\u4ec0\u4e48\uff0c\u9152\u697c\u4e0a\u82e5\u6709\u4e1d\u6beb\u6020\u6162\uff0c\u56de\u5934\u6211\u4e00\u628a\u706b\u5c06\u5b83\u70e7\u4e86\u3002\u201d\u4f17\u9152\u5ba2\u6b22\u7136\u5403\u559d\uff0c\u53ea\u662f\u5728\u51e4\u5bb6\u79ef\u5a01\u4e4b\u4e0b\uff0c\u8c01\u4e5f\u4e0d\u6562\u63a5\u53e3\u3002 \u3000\u3000\u80e1\u6590\u56de\u5230\u697c\u4e0a\uff0c\u89e3\u5f00\u4e86\u4e09\u540d\u5bb6\u4e01\u7684\u7a74\u9053\uff0c\u5c06\u94c1\u94fe\u5206\u522b\u5957\u5728\u5404\u4eba\u9888\u91cc\uff0c\u8fde\u7740\u53e6\u5916\u4e09\u540d\u5bb6\u4e01\uff0c\u5c06\u516d\u4e2a\u4eba\u4e00\u9f50\u62c9\u4e0b\u697c\u6765\uff0c\u95ee\u9053\uff1a\u201c\u51e4\u5929\u5357\u5f00\u7684\u5f53\u94fa\u5728\u54ea\u91cc\uff1f \u3000\u3000\u6211\u8981\u5f53\u516d\u53ea\u6076\u72d7\u3002\u201d\u4fbf\u6709\u9152\u5ba2\u6307\u70b9\u9014\u5f84\uff0c\u8bf4\u9053\uff1a\u201c\u5411\u4e1c\u518d\u8fc7\u4e09\u6761\u6a2a\u8857\uff0c\u90a3\u4e00\u5835\u9ad8\u5899\u4fbf\u662f\u3002\u201d\u80e1\u6590\u8bf4\u58f0\uff1a\u201c\u591a\u8c22\uff01\u201d\u7275\u4e86\u516d\u4eba\u4fbf\u8d70\u3002\u4e00\u7fa4\u77a7\u70ed\u95f9\u7684\u4eba\u8fdc\u8fdc\u8ddf\u7740\uff0c\u8981\u77a7\u6d3b\u4eba\u5982\u4f55\u5f53\u6cd5\u3002 \u3000\u3000\u80e1\u6590\u4e00\u4e0b\u62c9\u6ce8\u516d\u6839\u94c1\u94fe\uff0c\u6765\u5230\u201c\u82f1\u96c4\u5178\u5f53\u201d\u4e4b\u524d\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u82f1\u96c4\u5f53\u72d7\u6765\u5566\uff01\u201d\u7275\u4e86\u516d\u540d\u5bb6\u4e01\uff0c\u8d70\u5230\u9ad8\u9ad8\u7684\u67dc\u53f0\u4e4b\u524d\uff0c\u8bf4\u9053\uff1a\u201c\u671d\u5949\uff0c\u5f53\u516d\u6761\u6076\u72d7\uff0c\u6bcf\u6761\u4e00\u5343\u4e24\u94f6\u5b50\u3002\u201d \u3000\u3000\u5750\u67dc\u7684\u671d\u5949\u5927\u5403\u4e00\u60ca\uff0c\u4f5b\u5c71\u9547\u4e0a\u4eba\u4eba\u77e5\u9053\uff0c\u8fd9\u201c\u82f1\u96c4\u5178\u5f53\u201d\u662f\u51e4\u8001\u7237\u6240\u5f00\uff0c\u5341\u591a\u5e74\u6765\u51c6\u4e5f\u4e0d\u6562\u524d\u6765\u80e1\u6df7\uff0c\u600e\u4e48\u4eca\u65e5\u7ade\u6709\u4e2a\u5931\u5fc3\u75af\u7684\u6c49\u5b50\u6765\u5f53\u4eba\uff1f\u51dd\u4f38\u4e00\u770b\uff0c\u8ba4\u51fa\u90a3\u516d\u4e2a\u88ab\u4ed6\u7275\u7740\u7684\u7ade\u662f\u51e4\u5e9c\u5bb6\u4e01\uff0c\u8fd9\u4e00\u6765\u66f4\u662f\u60ca\u8bb6\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u2026\u2026\u4f60\u5f53\u4ec0\u4e48\uff1f\u201d\u80e1\u6590\u559d\u9053\uff1a\u201c\u4f60\u751f\u4e0d\u751f\u8033\u6735\uff1f\u6211\u5f53\u516d\u6761\u6076\u72d7\uff0c\u6bcf\u6761\u4e00\u5343\u4e24\uff0c\u5171\u662f\u516d\u5343\u4e24\u94f6\u5b50\uff0c\u8fd9\u7b14\u751f\u610f\u4fbf\u5b9c\u4f60\u5566\u3002\u201d \u3000\u3000\u90a3\u671d\u5949\u77e5\u4ed6\u6709\u610f\u6765\u6df7\u95f9\uff0c\u6084\u58f0\u5411\u65c1\u8fb9\u7684\u671d\u5949\u8bf4\u4e86\u4e00\u58f0\uff0c\u547d\u4ed6\u5feb\u53bb\u547c\u5524\u62a4\u9662\u6b66\u5e08\u6765\u6253\u53d1\u8fd9\u75af\u5b50\uff0c\u4e00\u9762\u5411\u80e1\u6590\u5ba2\u5ba2\u6c14\u6c14\u5730\u9053\uff1a\u201c\u5178\u5f53\u7684\u884c\u89c4\uff0c\u6d3b\u4e1c\u897f\u662f\u4e0d\u80fd\u5f53\u7684\uff0c\u8bf7\u5c0a\u9a7e\u539f\u8c05\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u597d\uff0c\u6d3b\u72d7\u4f60\u4eec\u4e0d\u6536\uff0c\u90a3\u6211\u4fbf\u5f53\u6b7b\u72d7\u3002\u201d \u3000\u3000\u516d\u540d\u5bb6\u4e01\u5927\u60ca\uff0c\u4e00\u9f50\u53eb\u9053\uff1a\u201c\u4fde\u5e08\u7237\uff0c\u4f60\u5feb\u6536\u4e0b\u6765\uff0c\u6551\u547d\u8981\u7d27\u3002\u201d \u3000\u3000\u4f46\u5178\u5f53\u7684\u671d\u5949\u505a\u4e8b\u4f55\u7b49\u7cbe\u660e\u628a\u7ec6\uff0c\u5c82\u80af\u968f\u968f\u4fbf\u4fbf\u7684\u9001\u516d\u5343\u4e24\u94f6\u5b50\u51fa\u53bb\uff0c\u53ea\u662f\u966a\u7b11\u9053\uff1a\u201c\u4f60\u8001\u8bf7\u5750\u554a\uff0c\u7528\u676f\u8336\u4e0d\u7528\uff1f\u201d\u80e1\u6590\u9053\uff1a\u201c\u5148\u628a\u6d3b\u72d7\u5f04\u6210\u6b7b\u72d7\uff0c\u518d\u559d\u4f60\u7684\u8336\u3002\u201d\u56db\u4e0b\u4e00\u77a7\uff0c\u5fc3\u4e0b\u5df2\u6709\u4e86\u8ba1\u8f83\uff0c\u4e24\u6b65\u8d70\u5230\u5927\u95e8\u65c1\uff0c\u6293\u6ce8\u95e8\u7f18\u5411\u4e0a\u4e00\u6258\uff0c\u5df2\u5c06\u4e00\u6247\u9ed1\u6f06\u5927\u95e8\u62ac\u4e86\u4e0b\u6765\u3002\u90a3\u4fde\u671d\u5949\u89c1\u4e8b\u60c5\u8d8a\u52a0\u4e0d\u5bf9\uff0c\u53eb\u9053\uff1a\u201c\u5582\uff0c\u5582\uff0c\u4f60\u8fd9\u4f4d\u5ba2\u4eba\u5e72\u4ec0\u4e48\u554a\uff1f\u201d\u80e1\u6590\u4e0d\u53bb\u7406\u4ed6\uff0c\u5de6\u4e00\u817f\uff0c\u53f3\u4e00\u817f\uff0c\u5c06\u516d\u540d\u5bb6\u4e01\u8e22\u5012\u5728\u5730\uff0c\u6a2a\u8f6c\u95e8\u677f\uff0c\u538b\u5728\u516d\u4eba\u8eab\u4e0a\u3002\u4fde\u671d\u5949\u53eb\u9053\uff1a\u201c\u5509\uff0c\u4e0d\u8981\u80e1\u95f9\uff0c\u4f60\u53ef\u77e5\u8fd9\u662f\u4ec0\u4e48\u5730\u65b9\uff1f\u8fd9\u5178\u5f53\u662f\u8c01\u7684\u4ea7\u4e1a\uff1f\u201d \u3000\u3000\u80e1\u6590\u5fc3\u60f3\uff1a\u201c\u77a7\u4f60\u8fd9\u526f\u5c16\u9178\u523b\u8584\u7684\u6837\u513f\uff0c\u4f5b\u5c71\u9547\u4e0a\u5b9a\u6709\u4e0d\u5c11\u7a77\u4eba\u5403\u8fc7\u4f60\u7684\u82e6\u5934\u3002\u201d\u8d70\u5230\u67dc\u53f0\u4e4b\u524d\uff0c\u5939\u624b\u4e00\u628a\u6293\u4f4f\u4ed6\u7684\u8fab\u5b50\uff0c\u4ece\u9ad8\u9ad8\u7684\u67dc\u53f0\u540e\u9762\u63ea\u5c06\u51fa\u6765\uff0c\u4e5f\u538b\u5728\u95e8\u677f\u4e4b\u4e0b\uff0c\u63a5\u7740\u8d70\u5230\u95e8\u53e3\uff0c\u62b1\u8d77\u95e8\u8fb9\u90a3\u53ea\u53c8\u9ad8\u53c8\u5927\u7684\u77f3\u9f13\uff0c\u7830\u7684\u4e00\u58f0\uff0c\u6454\u4e0a\u4e86\u95e8\u677f\u3002\u8fd9\u77f3\u9f13\u4f55\u6b62\u4e94\u767e\u65a4\u91cd\uff0c\u8fd9\u4e00\u6454\u4e0a\u53bb\uff0c\u95e8\u677f\u4e0b\u4e03\u4eba\u9f50\u58f0\u60e8\u547c\uff0c\u6709\u7684\u66f4\u662f\u75db\u5f97\u5c4e\u5c3f\u9f50\u6d41\u3002\u95e8\u5916\u95f2\u4eba\u4e0e\u67dc\u53f0\u5185\u7684\u4f17\u671d\u5949\u4e5f\u662f\u540c\u58f0\u60ca\u53eb\u8d77\u6765\u3002 \u3000\u3000\u80e1\u6590\u53c8\u62b1\u8d77\u53e6\u4e00\u53ea\u77f3\u9f13\uff0c\u53eb\u9053\uff1a\u201c\u6076\u72d7\u8fd8\u6ca1\u6b7b\uff0c\u5f97\u518d\u52a0\u4e00\u4e2a\u77f3\u9f13\uff01\u201d\u8bf4\u7740\u5c06\u90a3\u77f3\u9f13\u5f80\u7a7a\u4e2d\u4e00\u629b\uff0c\u773c\u770b\u53c8\u8981\u5f80\u95e8\u677f\u4e0a\u843d\u53bb\uff0c\u4f46\u542c\u5f97\u4f17\u4eba\u9f50\u58f0\u5927\u53eb\uff0c\u4ed6\u53cc\u624b\u73af\u62b1\uff0c\u500f\u5730\u5c06\u77f3\u9f13\u62b1\u4f4f\uff0c\u53c8\u538b\u5728\u95e8\u677f\u4e4b\u4e0a\u3002\u8fd9\u65f6\u95e8\u677f\u4e0a\u5df2\u538b\u4e86\u4e00\u5343\u4f59\u65a4\uff0c\u867d\u7531\u4e03\u4eba\u5206\u62c5\uff0c\u4f46\u4eba\u4eba\u5df2\u538b\u5f97\u7b4b\u9aa8\u6b32\u65ad\u3002\u4fde\u671d\u5949\u5927\u53eb\u9053\uff1a\u201c\u597d\u6c49\u7237\u9976\u547d\uff01\u5feb\u53d6\u94f6\u5b50\u51fa\u6765\uff01\u201d\u80e1\u6590\u9053\uff1a\u201d\u4ec0\u4e48\uff1f\u4f60\u8fd8\u8981\u6211\u53d6\u94f6\u5b50\u51fa\u6765\uff1f\u201d\u4fde\u671d\u5949\u8eab\u5b50\u7626\u5f31\uff0c\u65e9\u5df2\u7ed9\u538b\u5f97\u4e0a\u6c14\u4e0d\u63a5\u4e0b\u6c14\uff0c\u5fd9\u9053\uff1a\u201c\u4e0d\u2026\u2026\u4e0d\u2026\u2026\u6211\u662f\u53eb\u5f53\u91cc\u53d6\u94f6\u5b50\u51fa\u6765\u5178\u5f53\u91cc\u4f17\u671d\u5949\u89c1\u60c5\u52bf\u9669\u6076\uff0c\u53ea\u5f97\u5c06\u4e00\u5c01\u5c01\u94f6\u5b50\u6367\u4e86\u51fa\u6765\uff0c\u4e00\u767e\u4e24\u4e00\u5c01\uff0c\u5171\u662f\u516d\u5341\u5c01\uff0c\u80e1\u6590\u5c06\u94f6\u5b50\u90fd\u5806\u5728\u95e8\u677f\u4e4b\u4e0a\uff0c\u8bf4\u9053\uff1a\u201c\u516d\u6761\u6076\u72d7\u5f53\u516d\u5343\u4e24\uff0c\u8fd8\u6709\u4e00\u4e2a\u671d\u5949\u5462\uff1f\u96be\u9053\u5802\u5802\u82f1\u96c4\u5178\u5f53\u7684\u4e00\u4f4d\u5927\u671d\u5949\uff0c\u8fd8\u4e0d\u53ca\u4e00\u6761\u6076\u72ac\u5417\uff1f\u81f3\u5c11\u5f97\u5f53\u4e09\u5343\u4e24\u3002\u201d\u8fd9\u516d\u5343\u4e24\u94f6\u5b50\uff0c\u8db3\u8db3\u6709\u4e09\u767e\u4e03\u5341\u4f59\u65a4\uff0c\u53c8\u538b\u5728\u95e8\u677f\u4e0a\uff0c\u4e0b\u9762\u4e03\u4eba\u66f4\u662f\u62b5\u53d7\u4e0d\u4f4f\u3002 \u3000\u3000\u6b63\u4e71\u95f4\uff0c\u5ffd\u7136\u95e8\u5916\u6709\u4eba\u53eb\u9053\uff1a\u201c\u54ea\u4e00\u4e2a\u6742\u79cd\u5403\u4e86\u8c79\u5b50\u80c6\uff0c\u6765\u51e4\u8001\u7237\u7684\u94fa\u5b50\u6df7\u95f9\uff1f\u201d\u4eba\u7fa4\u5f80\u4e24\u65c1\u4e00\u5206\uff0c\u95ef\u8fdb\u6765\u4e24\u6761\u6c49\u5b50\u3002\u4e24\u4eba\u4e00\u822c\u7684\u9ad8\u5927\u9b41\u4f1f\uff0c\u9ed1\u8863\u9ed1\u88e4\uff0c\u5bc6\u6392\u767d\u8272\u6263\u5b50\uff0c\u6b66\u5e08\u6253\u626e\u3002\u80e1\u6590\u8eab\u5f62\u4e00\u6643\uff0c\u7a9c\u5230\u4e24\u4eba\u80cc\u540e\uff0c\u4e00\u624b\u4e00\u4e2a\uff0c\u5df2\u6293\u4f4f\u4e86\u4e24\u4eba\u540e\u9888\u3002\u90a3\u4e24\u4eba\u6b63\u662f\u82f1\u96c4\u5178\u5f53\u7684\u62a4\u9662\uff0c\u95f2\u7740\u65e0\u4e8b\uff0c\u5374\u5728\u8d4c\u573a\u8d4c\u535a\uff0c\u542c\u5f97\u5f53\u94fa\u4e2d\u6709\u4eba\u6df7\u95f9\uff0c\u8fd9\u624d\u5306\u5306\u8d76\u56de\uff0c\u54ea\u77e5\u8fd8\u6ca1\u77a7\u6e05\u695a\u5bf9\u624b\u7684\u8eab\u5f62\u9762\u76ee\uff0c\u5df2\u88ab\u4ed6\u6293\u4f4f\u8981\u5bb3\uff0c\u63d0\u4e86\u8d77\u6765\u3002 \u3000\u3000\u80e1\u6590\u53cc\u624b\u4e00\u6296\uff0c\u4e00\u4e2a\u8eab\u4e0a\u843d\u4e0b\u4e03\u516b\u5f20\u5929\u4e5d\u724c\uff0c\u53e6\u4e00\u4e2a\u624b\u4e2d\u5374\u6389\u4e0b\u4e24\u7c92\u9ab0\u5b50\u3002\u80e1\u6590\u7b11\u9053\uff1a\u201c\u597d\u554a\uff0c\u539f\u6765\u662f\u4e24\u4e2a\u8d4c\u9b3c\uff01\u201d\u5c06\u4e24\u4eba\u5934\u5bf9\u5934\u4e00\u649e\uff0c\u817e\u817e\u4e24\u58f0\uff0c\u5c06\u4e24\u4eba\u6454\u5728\u95e8\u677f\u4e4b\u4e0a\u3002\u8fd9\u4e24\u4e2a\u62a4\u9662\u6b66\u5e08\u6b66\u529f\u867d\u7136\u5e73\u5e73\uff0c\u8eab\u5b50\u7684\u91cd\u91cf\u5374\u662f\u8db3\u65a4\u52a0\u4e09\u3002\u95e8\u677f\u4e0a\u53c8\u52a0\u4e86\u56db\u767e\u6765\u65a4\uff0c\u53ea\u538b\u5f97\u4e0b\u9762\u4e03\u4eba\u60f3\u547b\u541f\u4e00\u53e5\u4e5f\u662f\u6709\u58f0\u65e0\u6c14\u3002 \u3000\u3000\u5178\u5f53\u7684\u5927\u638c\u67dc\u53ea\u6015\u95f9\u51fa\u4eba\u547d\uff0c\u5fd9\u547d\u4f19\u8ba1\u53c8\u6367\u51fa\u4e09\u5343\u4e24\u94f6\u5b50\u3000\u6765\uff0c\u4e0d\u4f4f\u5411\u80e1\u6590\u6253\u8eac\u4f5c\u63d6\uff0c\u966a\u7b11\u8bf4\u597d\u8bdd\uff0c\u5fc3\u4e0b\u7eb3\u95f7\uff1a\u201c\u600e\u5730\u51e4\u8001\u7237\u4e0d\u4eb2\u6765\u6599\u7406\uff1f\u201d \u3000\u3000\u80e1\u6590\u5728\u9152\u697c\u4e2d\u547d\u4eba\u70f9\u72d7\uff0c\u5230\u5178\u5f53\u4e2d\u6765\u5f53\u4eba\uff0c\u7528\u610f\u672c\u662f\u8981\u6fc0\u51e4\u5929\u5357\u51fa\u6765\u3002 \u3000\u3000\u4ed6\u81ea\u4ece\u5c11\u5e74\u65f6\u5728\u5546\u5bb6\u5821\u94c1\u5385\u9047\u9669\u4e4b\u540e\uff0c\u884c\u4e8b\u6781\u4e3a\u8c28\u614e\uff0c\u5fc3\u60f3\u8fd9\u51e4\u5929\u5357\u65e2\u7136\u53f7\u79f0\u201c\u5357\u9738\u5927\u201d\uff0c\u5bb6\u4e2d\u7684\u5e03\u7f6e\u53ea\u6015\u6bd4\u5546\u5bb6\u5821\u66f4\u4e3a\u5389\u5bb3\uff0c\u5e38\u8a00\u9053\uff1a\u201c\u5f3a\u9f99\u4e0d\u6597\u5730\u5934\u86c7\u3002\u201d\u82e5\u662f\u4e0a\u95e8\u53bb\u4e0e\u4ed6\u4e3a\u96be\uff0c\u53ea\u6015\u4e2d\u4e86\u4ed6\u7684\u6bd2\u8ba1\uff0c\u662f\u4ee5\u5148\u95f9\u9152\u697c\uff0c\u518d\u95f9\u5f53\u94fa\uff0c\u54ea\u77e5\u51e4\u5929\u5357\u59cb\u7ec8\u4e0d\u9732\u9762\uff0c\u5012\u4e5f\u5927\u51fa\u610f\u6599\u4e4b\u5916\u3002\u4ed6\u89c1\u53c8\u6709\u4e09\u5343\u4e24\u94f6\u5b50\u642c\u5230\uff0c\u5934\u4e00\u6446\uff0c\u9053\uff1a\u201c\u4e00\u9f50\u653e\u5728\u95e8\u677f\u4e0a\u3002\u201d\u4f17\u4f19\u8ba1\u660e\u77e5\u4e00\u653e\u4e0a\u53bb\uff0c\u53c8\u662f\u52a0\u4e0a\u4e00\u767e\u516b\u4e5d\u5341\u65a4\uff0c\u4f46\u4e0d\u6562\u8fdd\u62d7\uff0c\u53ea\u5f97\u4e00\u5305\u5305\u8f7b\u8f7b\u5730\u653e\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u80e1\u6590\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u8fd9\u5178\u5f53\u662f\u7687\u5e1d\u8001\u5b50\u5f00\u7684\u4e48\uff1f\u600e\u6837\u505a\u4e8b\u8fd9\u7b49\u6a2a\u6cd5\uff1f\u201d\u5927\u638c\u67dc\u966a\u7b11\u9053\uff1a\u201c\u4e0d\u6562\uff0c\u4e0d\u6562\u3002\u597d\u6c49\u7237\u8fd8\u6709\u4ec0\u4e48\u5429\u5490\uff1f\u201d\u80e1\u6590\u9053\uff1a\u201c\u5f53\u4e1c\u897f\u7684\u6ca1\u5f53\u7968\u4e48\uff1f\u201d\u90a3\u5927\u638c\u67dc\u5fc3\u60f3\u8fd9\u516d\u4e2a\u5bb6\u4e01\u76ae\u7c97\u8089\u539a\uff0c\u538b\u4e8c\u4f1a\u513f\u8fd8\u4e0d\u600e\u6837\uff0c\u8fd9\u4fde\u671d\u5949\u53ea\u6015\u8f6c\u773c\u5c31\u8981\u4e00\u547d\u545c\u547c\uff0c\u4e00\u53e0\u8fde\u58f0\u5730\u53eb\u9053\uff1a\u201c\u51b3\u5199\u5f53\u7968\u3002\u201d \u3000\u3000\u67dc\u9762\u7684\u671d\u5949\u4e0d\u77e5\u5982\u4f55\u843d\u7b14\uff0c\u89c1\u5927\u638c\u67dc\u50ac\u5f97\u7d27\uff0c\u53ea\u5f97\u63d0\u7b14\u5199\u9053\uff1a\u201c\u4eca\u62bc\u5230\u51e4\u5e9c\u5bb6\u4e01\u516d\u540d\uff0c\u4fde\u671d\u5949\u4e00\u540d\uff0c\u76ae\u7834\u8089\u70c2\uff0c\u624b\u8db3\u6b8b\u7f3a\uff0c\u5f53\u8db3\u8272\u7eb9\u94f6\u4e5d\u5343\u4e24\u6574\u3002 \u3000\u3000\u5e74\u606f\u4e8c\u5206\uff0c\u51ed\u7968\u53d6\u8d4e\u3002\u866b\u8681\u9f20\u54ac\uff0c\u5175\u706b\u635f\u5931\uff0c\u5404\u5b89\u5929\u547d\uff0c\u4e0d\u5f97\u4e89\u8bba\u3002\u4e09\u5e74\u4e3a\u671f\uff0c\u4e0d\u8d4e\u65ad\u5f53\u3002\u201d\u539f\u6765\u5929\u4e0b\u5f53\u94fa\u7684\u89c4\u77e9\uff0c\u5c31\u7b97\u4f60\u5f53\u7684\u662f\u5168\u65b0\u5b8c\u6574\u4e4b\u7269\uff0c\u4ed6\u4e5f\u8981\u5199\u4e0a\u201c\u6b8b\u7f3a\u7834\u70c2\u201d\u7684\u5b57\u6837\uff0c\u4ee5\u514d\u8d4e\u5f53\u65f6\u6709\u6240\u4e89\u6267\u3002\u5f53\u94fa\u5f53\u6d3b\u4eba\uff0c\u90a3\u662f\u4ece\u6240\u672a\u6709\u4e4b\u4e8b\uff0c\u90a3\u671d\u5949\u5199\u5f97\u60ef\u4e86\uff0c\u4e5f\u7ed9\u52a0\u4e0a\u201c\u76ae\u7834\u8089\u70c2\uff0c\u624b\u8db3\u6b8b\u7f3a\u201d\u516b\u5b57\u8bc4\u8bed\u3002 \u3000\u3000\u5927\u638c\u67dc\u5c06\u5f53\u7968\u606d\u606d\u656c\u656c\u9012\u4e86\u8fc7\u53bb\uff0c\u80e1\u6590\u4e00\u7b11\u6536\u4e0b\uff0c\u63d0\u8d77\u4e24\u540d\u6b66\u5e08\uff0c\u559d\u9053\uff1a\u201c\u5c06\u77f3\u9f13\u53d6\u4e0b\u6765\u3002\u201d\u4e24\u540d\u6b66\u5e08\u5140\u81ea\u5934\u6655\u773c\u82b1\uff0c\u5374\u81ea\u77e5\u4e00\u4eba\u642c\u4e00\u4e2a\u77f3\u9f13\u4e0d\u591f\u529b\u6c14\uff0c\u53ea\u5f97\u4e8c\u4eba\u5408\u529b\uff0c\u4e00\u4e2a\u4e2a\u7684\u62ac\u4e86\u4e0b\u6765\u3002\u80e1\u6590\u9053\uff1a\u201c\u597d\uff0c\u54b1\u4eec\u5230\u8d4c\u573a\u53bb\u901b\u901b\uff0c\u4f60\u4e24\u6761\u5927\u6c49\uff0c\u62ac\u7740\u672c\u94b1\u8ddf\u6211\u6765\u3002\u201d \u3000\u3000\u4e24\u540d\u6b66\u5e08\u7ed9\u4ed6\u6cbb\u5f97\u670d\u670d\u5e16\u5e16\uff0c\u4e00\u524d\u4e00\u540e\u62ac\u82e6\u95e8\u677f\uff0c\u7aef\u4e86\u4e5d\u5343\u4e24\u7eb9\u94f6\uff0c\u8ddf\u5728\u80e1\u6590\u540e\u9762\u3002\u770b\u70ed\u95f9\u7684\u95f2\u4eba\u89c1\u4ed6\u8d64\u4e0b\u7a7a\u62f3\uff0c\u6597\u8d62\u4e86\u4f5b\u5c71\u9547\u4e0a\u7b2c\u4e00\u5bb6\u5927\u5178\u5f53\uff0c\u65e0\u4e0d\u5174\u9ad8\u91c7\u70c8\uff0c\u4f46\u6015\u51e4\u8001\u7237\u89c1\u602a\uff0c\u5374\u4e0d\u6562\u8d70\u8fd1\u548c\u4ed6\u8bf4\u8bdd\uff0c\u542c\u4ed6\u8bf4\u8fd8\u8981\u53bb\u5927\u95f9\u8d4c\u573a\uff0c\u66f4\u662f\u4eba\u4eba\u7cbe\u795e\u767e\u500d\uff0c\u8ddf\u5728\u540e\u9762\u7684\u4eba\u8d8a\u6765\u8d8a\u591a\u3002 \u3000\u3000\u90a3\u8d4c\u573a\u5f00\u8bbe\u5728\u4f5b\u5c71\u9547\u5934\u4e00\u5ea7\u7834\u8d25\u7684\u5e99\u5b87\u91cc\uff0c\u5927\u95e8\u4e0a\u5199\u7740\u201c\u82f1\u96c4\u4f1a\u9986\u201d\u56db\u4e2a\u5927\u5b57\uff0c\u80e1\u6590\u5927\u8e0f\u6b65\u8d70\u8fdb\u95e8\u53bb\uff0c\u53ea\u89c1\u5927\u6bbf\u4e0a\u56f4\u7740\u9ed1\u538b\u538b\u4e00\u5806\u4eba\uff0c\u6b63\u5728\u63b7\u9ab0\u5b50\u62bc\u5927\u5c0f\u3002 \u3000\u3000\u5f00\u5b9d\u7684\u5b9d\u5b98\u6d53\u7709\u5927\u773c\uff0c\u7a7f\u7740\u4f5b\u5c71\u9547\u7684\u540d\u4ea7\u80f6\u7ef8\u886b\u88e4\uff0c\u655e\u5f00\u80f8\u819b\uff0c\u9732\u51fa\u9ed1\u6bf5\u6bf5\u7684\u4e24\u4e1b\u957f\u6bdb\uff0c\u89c1\u5230\u80e1\u6590\u8fdb\u6765\uff0c\u540e\u9762\u8ddf\u7740\u4e24\u540d\u6b66\u5e08\uff0c\u62ac\u7740\u4e00\u5757\u5927\u95e8\u677f\uff0c\u653e\u7740\u8fd1\u767e\u5c01\u94f6\u5b50\uff0c\u5fc3\u91cc\u4e00\u6014\uff0c\u53eb\u9053\uff1a\u201c\u86c7\u76ae\u5f20\uff0c\u4f60\u505a\u4ec0\u4e48\uff1f\u201d\u90a3\u59d3\u5f20\u7684\u6b66\u5e08\u52aa\u4e00\u52aa\u5634\uff0c\u9053\uff1a\u201c\u8fd9\u4f4d\u597d\u6c49\u7237\u8981\u6765\u73a9\u4e00\u624b\u3002\u201d \u3000\u3000\u90a3\u5b9d\u5b98\u542c\u86c7\u76ae\u5f20\u8bf4\u5f97\u606d\u656c\uff0c\u7d20\u77e5\u51e4\u8001\u7237\u4ea4\u6e38\u5e7f\u9614\uff0c\u773c\u524d\u8fd9\u4eba\u5e74\u7eaa\u867d\u8f7b\uff0c\u591a\u534a\u662f\u4ed6\u8001\u4eba\u5bb6\u7684\u670b\u53cb\uff0c\u5fc3\u60f3\uff1a\u201c\u597d\u54c7\uff0c\u4f60\u662f\u62ac\u4e86\u94f6\u5b50\u7ed9\u6211\u4eec\u573a\u91cc\u9001\u6765\u5566\u3002 \u3000\u3000\u5f00\u996d\u5e97\u7684\u4e0d\u6015\u5927\u809a\u6c49\uff0c\u5f00\u8d4c\u573a\u7684\u5c82\u6015\u8d22\u4e3b\u7237\uff1f\u518d\u62ac\u4e86\u4e24\u95e8\u677f\u6765\u4e5f\u4e0d\u5acc\u591a\u3002\u201d \u3000\u3000\u54a7\u5634\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u670b\u53cb\u8d35\u59d3\uff1f\u8bf7\u5750\u8bf7\u5750\u3002\u201d \u3000\u3000\u80e1\u6590\u5927\u523a\u523a\u7684\u5750\u4e86\u4e0b\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u59d3\u62d4\uff0c\u540d\u5b57\u53eb\u4f5c\u98ce\u6bdb\u3002\u201d\u90a3\u5b9d\u5b98\u4e00\u6123\uff0c\u5fc3\u9053\uff1a\u201c\u554a\uff0c\u4f60\u662f\u5b58\u5fc3\u6765\u8ddf\u6211\u4eec\u8fc7\u4e0d\u53bb\u4e86\u201d\u62ff\u8d77\u9ab0\u76c5\u4e00\u6447\uff0c\u653e\u4e0b\u6765\u5408\u5728\u684c\u4e0a\uff0c\u56db\u5468\u6570\u5341\u540d\u8d4c\u5ba2\u7eb7\u7eb7\u4e0b\u6ce8\uff0c\u6709\u7684\u62bc\u201c\u5927\u201d\uff0c\u6709\u7684\u62bc\u201c\u5c0f\u201d\u3002 \u3000\u3000\u80e1\u6590\u6709\u610f\u8981\u5ef6\u6328\u65f6\u523b\uff0c\u7b49\u90a3\u51e4\u5929\u5357\u4eb2\u81ea\u51fa\u6765\uff0c\u597d\u4e0e\u4ed6\u76f8\u6597\uff0c\u5f53\u4e0b\u7b11\u563b\u563b\u7684\u5750\u7740\uff0c\u5e76\u4e0d\u4e0b\u6ce8\u3002\u53ea\u89c1\u5b9d\u5b98\u63ed\u5f00\u76c5\u6765\uff0c\u4e09\u679a\u9ab0\u5b50\u5171\u662f\u5341\u4e00\u70b9\uff0c\u4e70\u201c\u5927\u201d\u7684\u8d4c\u5ba2\u7eb7\u7eb7\u6b22\u547c\uff0c\u4e70\u5c0f\u7684\u5374\u662f\u5782\u5934\u4e27\u6c14\u3002\u90a3\u5b9d\u5b98\u8fde\u5f00\u4e09\u6b21\uff0c\u90e8\u662f\u201c\u5927\u201d\u3002 \u3000\u3000\u80e1\u6590\u5fc3\u60f3\uff1a\u201c\u5341\u8d4c\u4e5d\u9a97\uff0c\u8fd9\u51e4\u5929\u5357\u65e2\u7136\u5982\u6b64\u6a2a\u6cd5\uff0c\u6240\u5f00\u7684\u8d4c\u573a\u9b3c\u82b1\u6837\u5fc5\u591a\uff0c\u5f85\u6211\u67e5\u51fa\u5f0a\u7aef\uff0c\u5927\u95f9\u4ed6\u4e00\u573a\u3002\u201d\u5f53\u4e0b\u6ce8\u76ee\u770b\u90a3\u9ab0\u76c5\uff0c\u53c8\u503e\u542c\u9ab0\u5b50\u843d\u4e0b\u7684\u58f0\u97f3\uff0c\u8981\u67e5\u7a76\u9ab0\u4e2d\u662f\u5426\u704c\u94c5\uff0c\u542c\u4e86\u7247\u523b\uff0c\u89c9\u5f97\u9ab0\u5b50\u5012\u65e0\u82b1\u5de7\u3002\u4ed6\u7ec3\u8fc7\u6697\u5668\u542c\u98ce\u672f\uff0c\u8033\u97f3\u6781\u7cbe\uff0c\u7eb5\u5728\u9ed1\u6697\u4e4b\u4e2d\uff0c\u82e5\u6709\u6697\u5668\u6765\u88ad\uff0c\u4e00\u542c\u58f0\u97f3\uff0c\u7acb\u77e5\u6697\u5668\u6765\u52bf\u65b9\u4f4d\uff0c\u662f\u4f55\u79cd\u7c7b\uff0c\u624b\u52b2\u5982\u4f55\u3002\u5982\u8d75\u534a\u5c71\u8fd9\u7b49\u5927\u884c\u5bb6\uff0c\u5f53\u65e5\u5728\u5546\u5bb6\u5821\u4e2d\u4e00\u542c\u5230\u8eab\u540e\u6697\u5668\u5c04\u5230\uff0c\u5373\u731c\u5230\u5bf9\u65b9\u662f\u5d69\u5c71\u5c11\u6797\u5bfa\u4e0d\u7591\u5927\u5e08\u7684\u5f1f\u5b50\uff0c\u6697\u5668\u542c\u98ce\u4e4b\u672f\uff0c\u4e00\u7cbe\u81f3\u65af\u3002\u80e1\u6590\u7684\u8033\u97f3\u8f83\u4e4b\u8d75\u534a\u5c71\u867d\u7136\u5c1a\u6709\u4e0d\u53ca\uff0c\u4f46\u542c\u4e86\u4e00\u9635\uff0c\u7adf\u5df2\u542c\u51fa\u4e09\u679a\u9ab0\u5b50\u5411\u5929\u7684\u662f\u4ec0\u4e48\u70b9\u6570\u3002\u8981\u77e5\u9ab0\u5b50\u5171\u6709\u516d\u9762\uff0c\u6bcf\u9762\u70b9\u6570\u4e0d\u540c\uff0c\u4e00\u70b9\u7684\u4e00\u9762\u4e0e\u516d\u70b9\u7684\u4e00\u9762\u843d\u4e0b\u4e4b\u65f6\uff0c\u58f0\u97f3\u7565\u6709\u5dee\u522b\uff0c\u867d\u7136\u6240\u5dee\u5fae\u7ec6\u4e4b\u6781\uff0c\u4f46\u5728\u5185\u529b\u7cbe\u6df1\u3001\u6697\u5668\u529f\u592b\u6781\u4f73\u4e4b\u4eba\u542c\u6765\uff0c\u81ea\u80fd\u5206\u8fa8\u3002 \u3000\u3000\u80e1\u6590\u53c8\u8ba9\u4ed6\u5f00\u4e86\u51e0\u76c5\uff0c\u8bd5\u5f97\u65e0\u8bef\uff0c\u7b11\u9053\uff1a\u201c\u5b9d\u5b98\uff0c\u9650\u6ce8\u4e48\uff1f\u201d\u90a3\u5b9d\u5b98\u5927\u58f0\u9053\uff1a\u201c\u5e7f\u4e1c\u901a\u7701\u90fd\u77e5\uff0c\u5357\u9738\u5929\u7684\u8d4c\u573a\u51b3\u4e0d\u9650\u6ce8\uff0c\u5426\u5219\u8fd8\u80fd\u53eb\u82f1\u96c4\u4f1a\u9986\u4e48\uff1f\u201d \u3000\u3000\u80e1\u6590\u5fae\u5fae\u4e00\u7b11\uff0c\u4f38\u51fa\u5927\u62c7\u6307\u4e00\u7fd8\uff0c\u9053\uff1a\u201c\u662f\u554a\uff0c\u82e5\u662f\u9650\u6ce8\uff0c\u5c82\u4e0d\u6210\u4e86\u72d7\u718a\u4f1a\u9986\uff1f\u201d\u542c\u4ed6\u9ab0\u5b50\u843d\u5b9a\uff0c\u4e43\u662f\u5341\u516d\u70b9\uff0c\u56de\u5934\u53eb\u9053\uff1a\u201c\u86c7\u76ae\u5f20\uff0c\u62bc\u4e00\u5343\u4e24\u2018\u5927\u2019\u3002\u201d \u3000\u3000\u90a3\u5b9d\u5b98\u867d\u5728\u8d4c\u573a\u4e2d\u6df7\u4e86\u6570\u5341\u5e74\uff0c\u4f46\u9ab0\u5b50\u5230\u5e95\u5f00\u5927\u5f00\u5c0f\uff0c\u4e5f\u662f\u8981\u5230\u63ed\u76c5\u624d\u77e5\uff0c\u89c1\u4ed6\u4e00\u62bc\u4fbf\u662f\u4e00\u5343\u4e24\uff0c\u4e0d\u7531\u5f97\u4e00\u6014\uff0c\u63ed\u5f00\u76c5\u6765\uff0c\u53ea\u89c1\u4e09\u679a\u9ab0\u4e8e\u4e24\u679a\u516d\u70b9\uff0c\u4e00\u679a\u56db\u70b9\uff0c\u4e0d\u7531\u5f97\u8138\u90fd\u767d\u4e86\uff0c\u5f53\u4e0b\u7531\u4e0b\u624b\u8d54\u4e86\u4e00\u5343\u4e24\uff0c\u63a5\u4e0b\u53bb\u6447\u9ab0\u65f6\u58f0\u97f3\u9519\u843d\uff0c\u80e1\u6590\u542c\u4e0d\u660e\u767d\uff0c\u8896\u624b\u4e0d\u4e0b\uff0c\u5f00\u51fa\u6765\u662f\u4e2a\u516b\u70b9\u5c0f\u3002\u8ddf\u7740\u4ed6\u62bc\u4e86\u4e8c\u5343\u4e24\u201c\u5c0f\u201d\uff0c\u76c5\u5b50\u63ed\u8d77\uff0c\u679c\u7136\u662f\u56db\u5360\u201c\u5c0f\u201d\u3002 \u3000\u3000\u5982\u6b64\u53ea\u62bc\u5f97\u4e94\u516d\u6b21\uff0c\u573a\u4e2d\u5df2\u8d54\u4e86\u4e00\u4e07\u4e00\u5343\u4e24\u3002\u90a3\u5b9d\u5b98\u6ee1\u624b\u662f\u6c57\uff0c\u4e3e\u8d77\u9ab0\u76c5\u731b\u6447\u3002\u80e1\u6590\u542c\u5f97\u660e\u767d\u3002\u76c5\u4e2d\u6b63\u662f\u5341\u56db\u70b9\uff0c\u8bf4\u9053\uff1a\u201c\u86c7\u76ae\u5f20\uff0c\u628a\u4e8c\u4e07\u4e24\u90fd\u7ed9\u62bc\u4e0a\u2018\u5927\u2019\uff01\u201d\u4e24\u540d\u6b66\u5e08\u5c06\u95e8\u677f\u4e0a\u7684\u94f6\u5b50\u4e00\u5c01\u5c01\u7684\u5c3d\u5f80\u684c\u4e0a\u9001\u3002\u5b9d\u5b98\u6380\u8d77\u9ab0\u76c5\u4e00\u8fb9\uff0c\u773c\u89d2\u4e00\u5f20\uff0c\u5df2\u770b\u5230\u9ab0\u5b50\u5171\u662f\u5341\u56db\u70b9\u3002\u4ed6\u624b\u811a\u4e5f\u771f\u5229\u843d\uff0c\u5c0f\u6307\u5728\u76c5\u8fb9\u8f7b\u8f7b\u4e00\u63a8\uff0c\u76c5\u8fb9\u5728\u9ab0\u5b50\u4e0a\u4e00\u78b0\uff0c\u4e00\u679a\u516d\u70b9\u7684\u9ab0\u5b50\u7ffb\u4e86\u4e00\u8f6c\uff0c\u5341\u56db\u70b9\u53d8\u6210\u4e5d\u70b9\uff0c\u90a3\u662f\u201c\u5c0f\u201d\u4e86\u201d\u3002\u8fd9\u4e00\u5df1\u624b\u6cd5\uff0c\u82e5\u4e0d\u662f\u6570\u5341\u5e74\u82e6\u529f\uff0c\u4e5f\u771f\u4e0d\u80fd\u7ec3\u6210\uff0c\u6bd4\u4e4b\u4e8e\u6b66\u529f\uff0c\u53ef\u7b97\u5f97\u662f\u5389\u5bb3\u4e4b\u6781\u7684\u7edd\u62db\u3002 \u3000\u3000\u90a3\u5b9d\u5b98\u89c1\u4ed6\u6d51\u7136\u4e0d\u89c9\uff0c\u5fc3\u60f3\u8fd9\u6b21\u80dc\u5b9a\u4f60\u4e86\uff0c\u5f97\u610f\u6d0b\u6d0b\u7684\u9053\uff1a\u201c\u5927\u5bb6\u4e0b\u5b9a\u6ce8\u4e86\uff1f\u201d\u80e1\u6590\u5de6\u624b\u5c06\u4e00\u5927\u5806\u94f6\u5b50\u5f80\u684c\u5b50\u4e2d\u5fc3\u4e00\u63a8\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u91cc\u662f\u4e8c\u4e07\u4e24\u94f6\u5b50\uff0c\u662f\u2018\u5c0f\u2019\u4f60\u4fbf\u5c3d\u6570\u5403\u53bb\u3002\u201d\u5b9d\u5b98\u53eb\u9053\uff1a\u201c\u597d\uff01\u597d\uff01\u5403\u4e86\uff01\u201d\u63ed\u5f00\u5b9d\u76c5\uff0c\u4e0d\u7981\u5f20\u5927\u4e86\u53e3\u5408\u4e0d\u62e2\u6765\uff0c\u53ea\u89c1\u4e09\u679a\u9ab0\u5b50\u5171\u662f\u5341\u4e8c\u70b9\u3002 \u3000\u3000\u4f17\u8d4c\u5ba2\u65e9\u5df2\u7f62\u624b\u4e0d\u8d4c\uff0c\u671b\u7740\u684c\u4e0a\u8fd9\u6570\u5341\u5c01\u94f6\u4e24\uff0c\u65e0\u4e0d\u60ca\u5fc3\u52a8\u9b44\uff0c\u7a81\u89c1\u5f00\u51fa\u6765\u7684\u662f\u201c\u5927\u201d\uff0c\u4e0d\u7ea6\u800c\u540c\u7684\u9f50\u58f0\u60ca\u547c\uff1a\u201d\u554a\uff01\u201d\u8fd9\u58f0\u97f3\u4e2d\u53c8\u662f\u60ca\u5947\uff0c\u53c8\u662f\u8273\u7fa1\u3002\u8981\u77e5\u4ed6\u4eec\u4e00\u751f\u4e4b\u4e2d\uff0c\u4ece\u672a\u89c1\u8fc7\u5982\u6b64\u7684\u5927\u8d4c\uff0c\u80e1\u6590\u54c8\u54c8\u4eba\u7b11\uff0c\u4e00\u53ea\u811a\u63d0\u8d77\u6765\u8e0f\u5728\u51f3\u4e0a\uff0c\u53eb\u9053\uff1a\u201d\u4e8c\u4e07\u4e24\u94f6\u5b50\uff0c\u5feb\u8d54\u6765\uff01\u201d \u3000\u3000\u539f\u6765\u90a3\u5b9d\u5b98\u4f5c\u5f0a\u4e4b\u65f6\uff0c\u624b\u811a\u867d\u5feb\uff0c\u5374\u54ea\u91cc\u7792\u5f97\u8fc7\u80e1\u6590\u7684\u773c\u5149\uff1f\u4ed6\u867d\u77a7\u4e0d\u51fa\u90a3\u5b9d\u5b98\u5982\u4f55\u6363\u9b3c\uff0c\u4f46\u6599\u5b9a\u4e09\u679a\u9ab0\u5b50\u5b9a\u662f\u7ed9\u4ed6\u4ece\u201c\u5927\u201d\u6362\u6210\u4e86\u201c\u5c0f\u201d\uff0c\u4ed6\u5de6\u624b\u63a8\u52a8\u94f6\u4e24\u4e4b\u9645\uff0c\u53f3\u624b\u4f38\u5230\u684c\u5e95\uff0c\u9694\u7740\u684c\u9762\u5728\u76c5\u5e95\u8f7b\u8f7b\u4e00\u5f39\u3002\u4e09\u679a\u9ab0\u5b50\u672c\u6765\u4e00\u679a\u662f\u4e09\uff0c\u4e00\u679a\u662f\u4e00\uff0c\u4e00\u679a\u662f\u4e94\uff0c\u5408\u5171\u4e5d\u70b9\uff0c\u4ed6\u8fd9\u4e00\u5f39\u529b\u9053\u7528\u5f97\u6070\u5230\u597d\u5904\u3002 \u3000\u3000\u4e09\u679a\u9ab0\u5b50\u4e00\u9f50\u7ffb\u4e86\u4e2a\u8eab\uff0c\u53d8\u6210\u56db\u70b9\u3001\u516d\u70b9\u3001\u4e24\u70b9\uff0c\u5408\u6210\u5341\u4e8c\u70b9\u201c\u5927\u201d\u3002 \u3000\u3000\u90a3\u5b9d\u5b98\u8138\u5982\u571f\u8272\uff0c\u7830\u7684\u4e00\u4e0b\uff0c\u4f38\u4e0b\u5728\u684c\u4e0a\u4e00\u62cd\uff0c\u559d\u9053\uff1a\u201c\u86c7\u76ae\u5f20\uff0c\u8fd9\u4eba\u662f\u4ec0\u4e48\u8def\u6570\uff1f\u5230\u51e4\u8001\u7237\u7684\u573a\u5b50\u6765\u6405\u5c40\uff1f\u201d\u86c7\u76ae\u5f20\u54ed\u4e27\u7740\u8138\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u2026\u2026 \u3000\u3000\u4e5f\u4e0d\u77e5\u9053\u554a\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u5feb\u8d54\uff0c\u5feb\u8d54\uff0c\u4e8c\u4e07\u4e24\u94f6\u5b50\uff0c\u8001\u7237\u8d62\u5f97\u591f\u4e86\uff0c\u6536\u624b\u4e0d\u8d4c\u5566\uff01\u201d\u90a3\u5b9d\u5b98\u5728\u684c\u4e0a\u53c8\u662f\u7830\u7684\u4e00\u51fb\uff0c\u9a82\u9053\uff1a\u201c\u5951\u5f1f\uff0c\u4f60\u641e\u9b3c\u51fa\u8001\u5343\uff0c\u5f53\u8001\u5b50\u4e0d\u77e5\u9053\u4e48\uff1f\u201d\u80e1\u6590\u867d\u4e0d\u660e\u767d\u4ed6\u9a82\u4eba\u7684\u8a00\u8bed\uff0c\u6599\u60f3\u51b3\u975e\u597d\u8bdd\uff0c\u7b11\u9053\uff1a\u201c\u597d\uff0c\u4f60\u7231\u62cd\u684c\u5b50\uff0c\u54b1\u4eec\u8d4c\u62cd\u684c\u5b50\u4e5f\u6210\uff01\u201d\u53f3\u624b\u5728\u684c\u5b50\u89d2\u4e0a\u4e00\u62cd\uff0c\u684c\u5b50\u89d2\u513f\u5e94\u624b\u800c\u843d\uff0c\u8ddf\u7740\u3000\u5de6\u624b\u4e00\u6298\uff0c\u53e6\u4e00\u53ea\u89d2\u53c8\u6389\u5728\u5730\u4e0a\u3002 \u3000\u3000\u8fd9\u4e00\u624b\u60ca\u4eba\u6b66\u529f\u663e\u4e86\u51fa\u6765\uff0c\u8fd9\u5b9d\u5b98\u54ea\u91cc\u8fd8\u6562\u51f6\u6a2a\uff1f\u7a81\u7136\u98de\u8d77\u4e00\u811a\uff0c\u8981\u60f3\u5c06\u684c\u5b50\u8e22\u7ffb\uff0c\u4e58\u4e71\u6e9c\u8d70\u3002\u51e0\u4e2a\u5730\u75de\u8d4c\u5ba2\u8ddf\u7740\u8d77\u54c4\uff1a\u201c\u62a2\u94f6\u5b50\u554a\uff01\u201d\u80e1\u6590\u53f3\u624b\u4e00\u4f38\uff0c\u5df2\u5c06\u5b9d\u5b98\u8e22\u51fa\u7684\u4e00\u811a\u6293\u4f4f\uff0c\u5012\u63d0\u8d77\u6765\uff0c\u5c06\u4ed6\u5934\u9876\u5f80\u684c\u9762\u4e00\u6869\u3002\u8fd9\u4e00\u4e0b\u529b\u9053\u5947\u91cd\uff0c\u684c\u9762\u767b\u65f6\u7ed9\u4ed6\u8111\u95e8\u649e\u7834\u4e00\u6d1e\uff0c\u8111\u888b\u63d2\u5230\u4e86\u684c\u9762\u4e4b\u4e0b\uff0c\u80a9\u8180\u4ee5\u4e0a\u7684\u8eab\u5b50\u5374\u5012\u683d\u5728\u684c\u4e0a\uff0c\u624b\u811a\u4e71\u821e\uff0c\u851a\u4e3a\u5947\u89c2\u3002 \u3000\u3000\u4f17\u8d4c\u5ba2\u9f50\u58f0\u60ca\u53eb\uff0c\u7eb7\u7eb7\u9000\u5f00\u3002\u7a81\u7136\u5927\u95e8\u4e2d\u62a2\u8fdb\u4e00\u4e2a\u9752\u5e74\uff0c\u4e8c\u5341\u5c81\u4e0a\u4e0b\u5e74\u7eaa\uff0c\u8eab\u7a7f\u84dd\u7ef8\u957f\u886b\uff0c\u53f3\u624b\u6447\u7740\u6298\u6247\uff0c\u53eb\u9053\uff1a\u201c\u662f\u54ea\u4e00\u4e2a\u597d\u670b\u53cb\u5149\u964d\uff0c\u5c0f\u53ef\u672a\u66fe\u8fdc\u8fce\uff0c\u8981\u8bf7\u6055\u7f6a\u554a\uff01\u201d\u80e1\u6590\u89c1\u8fd9\u4eba\u6b65\u5c65\u8f7b\u6377\uff0c\u8138\u4e0a\u82f1\u6c14\u52c3\u52c3\uff0c\u663e\u662f\u6b66\u529f\u4e0d\u5f31\uff0c\u4e0d\u89c9\u5fae\u5fae\u4e00\u6014\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u6536\u62e2\u6298\u6247\uff0c\u5411\u80e1\u6590\u4e00\u63d6\uff0c\u8bf4\u9053\uff1a\u201c\u5c0a\u5144\u8d35\u59d3\u5927\u540d\uff1f\u201d\u80e1\u6590\u89c1\u4ed6\u5f6c\u5f6c\u6709\u793c\uff0c\u4fbf\u8fd8\u4e86\u4e00\u63d6\uff0c\u9053\uff1a\u201c\u6ca1\u8bf7\u6559\u9601\u4e0b\u5c0a\u59d3\u3002\u201d\u90a3\u5c11\u5e74\u9053\uff1a\u201c\u5c0f\u5f1f\u59d3\u51e4\u3002\u201d \u3000\u3000\u80e1\u6590\u53cc\u7709\u4e00\u7ad6\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u9053\uff0c\u5728\u4e0b\u7684\u59d3\u540d\u672a\u514d\u5931\u656c\u4e86\u3002\u6211\u59d3\u62d4\uff0c\u540d\u53eb\u51e4\u6bdb\u3002\u8001\u5144\u4e0e\u51e4\u5929\u5357\u600e\u751f\u79f0\u547c\uff1f\u201d\u90a3\u5c11\u5e74\u9053\uff1a\u201c\u90a3\u662f\u5bb6\u7236\u3002\u5bb6\u7236\u542c\u8bf4\u5c0a\u9a7e\u5149\u4e34\uff0c\u672c\u8be5\u4eb2\u6765\u8fce\u63a5\uff0c\u4e0d\u5de7\u6070\u6709\u8981\u52a1\u7f20\u8eab\uff0c\u7279\u547d\u5c0f\u5f1f\u524d\u6765\u5c48\u9a7e\uff0c\u8bf7\u5230\u820d\u4e0b\u559d\u4e00\u676f\u6c34\u9152\u3002\u201d \u3000\u3000\u4ed6\u8f6c\u5934\u5411\u82f1\u96c4\u5f53\u94fa\u7684\u4e24\u540d\u62a4\u9662\u559d\u9053\uff1a\u201c\u5b9a\u662f\u4f60\u4eec\u5bf9\u62d4\u7237\u65e0\u793c\uff0c\u60f9\u5f97\u4ed6\u8001\u4eba\u5bb6\u751f\u6c14\uff0c\u8fd8\u4e0d\u8d54\u7f6a\uff1f\u201d\u90a3\u4e24\u4f4d\u62a4\u9662\u558f\u558f\u8fde\u58f0\uff0c\u4e00\u9f50\u6253\u8eac\u8bf7\u5b89\uff0c\u9053\uff1a\u201c\u5c0f\u4eba\u6709\u773c\u4e0d\u8bc6\u6cf0\u5c71\u3002\u201d\u80e1\u6590\u5fae\u5fae\u51b7\u7b11\uff0c\u5fc3\u60f3\uff1a\u201d\u77a7\u4f60\u4eec\u95f9\u4e9b\u4ec0\u4e48\u7384\u865a\u3002\u201d \u3000\u3000\u90a3\u5b9d\u5b98\u7684\u8111\u888b\u63d2\u5728\u8d4c\u684c\u4e0a\uff0c\u5140\u81ea\u53cc\u811a\u4e71\u821e\uff0c\u554a\u554a\u5927\u53eb\u3002\u90a3\u5c11\u5e74\u6293\u4f4f\u4ed6\u80cc\u5fc3\uff0c\u8f7b\u8f7b\u5411\u4e0a\u4e00\u63d0\uff0c\u5c06\u4ed6\u5012\u8fc7\u8eab\u6765\uff0c\u90a3\u684c\u5b50\u5374\u4ecd\u65e7\u8fde\u5728\u4ed6\u9879\u9888\u4e4b\u4e2d\uff0c\u53ea\u662f\u56db\u53ea\u684c\u811a\u5411\u5929\uff0c\u72b9\u4f3c\u9888\u4e2d\u6234\u4e86\u4e00\u4e2a\u5927\u67b7\u3002\u90a3\u5b9d\u5b98\u53cc\u624b\u6258\u4f4f\u684c\u5b50\uff0c\u8fd9\u60c5\u72b6\u5f53\u771f\u662f\u5341\u5206\u6ed1\u7a3d\uff0c\u5341\u5206\u72fc\u72c8\uff0c\u5411\u90a3\u5c11\u5e74\u9053\uff1a\u201c\u5927\u7237\uff0c\u4f60\u6765\u5f97\u6b63\u597d\uff0c\u4ed6\u2026\u2026\u4ed6\u2026\u2026\u201d \u3000\u3000\u773c\u671b\u80e1\u6590\uff0c\u5374\u4e0d\u6562\u518d\u8bf4\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u80e1\u6590\u9053\uff1a\u201c\u4f60\u4e0d\u8d4c\u4e86\uff0c\u662f\u4e0d\u662f\uff1f\u90a3\u4e5f\u6210\uff0c\u6211\u8d62\u7684\u94b1\u5462\uff1f\u82f1\u96c4\u4f1a\u9986\u60f3\u8d56\u5e10\u4e48\uff1f\u201d\u90a3\u5c11\u5e74\u9a82\u5b9d\u5b98\u9053\uff1a\u201c\u62d4\u7237\u8d62\u4e86\u591a\u5c11\u94f6\u5b50\uff0c\u5feb\u53d6\u51fa\u6765\uff01\u6162\u541e\u541e\u7684\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u8bf4\u7740\u6293\u6ce8\u684c\u5b50\u4e24\u89d2\uff0c\u53cc\u624b\u5411\u5916\u4e00\u5206\uff0c\u5580\u7684\u4e00\u54cd\uff0c\u684c\u9762\u7adf\u88ab\u4ed6\u6495\u6210\u4e86\u4e24\u8fb9\u3002\u8fd9\u4e00\u624b\u529f\u592b\u751a\u662f\u5e72\u51c0\u5229\u843d\uff0c\u8d4c\u573a\u4e2d\u5404\u4eba\u4e00\u9f50\u559d\u91c7\u3002 \u3000\u3000\u90a3\u5b9d\u5b98\u6709\u5c0f\u4e3b\u6491\u8170\uff0c\u80c6\u5b50\u53c8\u5927\u4e86\u8d77\u6765\uff0c\u5411\u80e1\u6590\u6076\u72e0\u72e0\u5730\u671b\u4e86\u4e94\u773c\uff0c\u9053\uff1a\u201c\u8fd9\u4eba\u51fa\u8001\u5343\u3002\u201d\u90a3\u5c11\u5e74\u53f1\u9053\uff1a\u201d\u80e1\u8bf4\uff01\u4eba\u5bb6\u662f\u82f1\u96c4\u597d\u6c49\u3002\u600e\u4f1a\u51fa\u8001\u5343\uff1f\u9986\u91cc\u94f6\u5b50\u591f\u4e48\uff1f\u82e5\u662f\u4e0d\u591f\uff0c\u5feb\u53eb\u4eba\u5f80\u5f53\u94fa\u53d6\u53bb\u3002\u201d\u80e1\u6590\u4e0d\u61c2\u201c\u51fa\u8001\u5343\u201d\u4e09\u5b57\u662f\u4f55\u610f\u601d\uff0c\u4f46\u60f3\u6765\u591a\u534a\u662f\u201c\u6b3a\u9a97\u4f5c\u5f0a\u201d\u4e4b\u610f\uff0c\u5fc3\u60f3\uff1a\u201d\u8fd9\u5c11\u5e74\u6b66\u529f\u4e0d\u5f31\uff0c\u884c\u4e8b\u4e5f\u6709\u62c5\u5f53\uff0c\u6211\u53ef\u4e0d\u80fd\u4e1d\u6beb\u5927\u610f\u4e86\u3002\u201d\u53ea\u542c\u90a3\u5c11\u5e74\u9053\uff1a\u201c\u62d4\u7237\u7684\u94f6\u5b50\uff0c\u51b3\u4e0d\u6562\u77ed\u4e86\u534a\u6587\u3002\u8fd9\u4e9b\u5e02\u4e95\u5c0f\u4eba\u76ee\u5149\u5982\u8c46\uff0c\u4ece\u6765\u6ca1\u89c1\u8fc7\u771f\u597d\u6c49\u5927\u82f1\u96c4\u7684\u6c14\u6982\uff0c\u62d4\u7237\u4e0d\u5fc5\u7406\u4f1a\u3002\u73b0\u4e0b\u4fbf\u8bf7\u62d4\u7237\u79fb\u7389\u820d\u4e0b\u5982\u4f55\uff1f\u201d \u3000\u3000\u4ed6\u660e\u77e5\u201c\u62d4\u51e4\u6bdb\u201d\u4e09\u5b57\u51b3\u975e\u771f\u540d\uff0c\u4e43\u662f\u5b58\u5fc3\u6765\u5411\u51e4\u5bb6\u5bfb\u4e8b\u751f\u975e\uff0c\u4f46\u8fd8\u662f\u62d4\u7237\u524d\uff0c\u62d4\u7237\u540e\uff0c\u4e1d\u6beb\u4e0d\u4ee5\u4e3a\u610f\u3002\u80e1\u6590\u9053\uff1a\u201c\u4f60\u4eec\u8fd9\u91cc\u51e4\u51f0\u592a\u591a\uff0c\u4e0d\u77e5\u5927\u7237\u7684\u5c0a\u53f7\u5982\u4f55\u79f0\u547c\uff1f\u201d\u90a3\u5c11\u5e74\u4f3c\u4e4e\u6ca1\u542c\u51fa\u4ed6\u8a00\u8bed\u610f\u542b\u8ba5\u8bbd\uff0c\u8fde\u8bf4\uff1a\u201c\u4e0d\u6562\uff0c\u4e0d\u6562\u3002\u5c0f\u5f1f\u540d\u53eb\u4e00\u9e23\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u5728\u4e0b\u8d4c\u5f97\u5174\u8d77\uff0c\u8fd8\u8981\u5728\u8fd9\u91cc\u73a9\u51e0\u4e2a\u65f6\u8fb0\uff0c\u4e0d\u5982\u8bf7\u4f60\u7238\u7238\u5230\u8fd9\u91cc\u4f1a\u9762\u5427\u3002\u201d\u90a3\u5b9d\u5b98\u542c\u4ed6\u8bf4\u8fd8\u8981\u8d4c\uff0c\u5413\u5f97\u9762\u5982\u571f\u8272\uff0c\u5fd9\u9053\uff1a\u201c\u4e0d\uff0c\u4e0d\u2026\u2026\u201d \u3000\u3000\u51e4\u4e00\u9e23\u8138\u4e00\u6c89\uff0c\u53f1\u9053\uff1a\u201c\u6211\u4eec\u5728\u8bf4\u8bdd\uff0c\u4e5f\u6709\u4f60\u63d2\u5634\u7684\u4efd\u513f\uff1f\u201d\u8f6c\u5934\u5411\u80e1\u6590\u966a\u7b11\u9053\uff1a\u201c\u5bb6\u7236\u5bf9\u670b\u53cb\u4ece\u6765\u4e0d\u6562\u5931\u793c\uff0c\u5f97\u77e5\u62d4\u7237\u5149\u4e34\u4f5b\u5c71\uff0c\u5fc3\u4e2d\u559c\u6b22\u5f97\u4e86\u4e0d\u5f97\uff0c\u6068\u4e0d\u5f97\u7acb\u65f6\u8fc7\u6765\u76f8\u89c1\uff0c\u53ea\u662f\u6070\u597d\u4eca\u65e5\u4eac\u4e2d\u6765\u4e86\u4e24\u4f4d\u5fa1\u524d\u4f8d\u536b\uff0c\u5bb6\u7236\u987b\u5f97\u966a\u4f34\uff0c\u5b9e\u662f\u5206\u8eab\u4e0d\u5f00\u3002\u8bf7\u62d4\u7237\u5305\u6db5\u539f\u8c05\u3002\u201d\u80e1\u6590\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5fa1\u524d\u4f8d\u536b\uff0c\u679c\u7136\u662f\u597d\u5927\u7684\u5b98\u513f\u3002\u4e00\u9e23\u5144\uff0c\u5c0f\u5f1f\u5728\u6c5f\u6e56\u4e0a\u6709\u4e2a\u5916\u53f7\uff0c\u4f60\u60f3\u5fc5\u77e5\u9053\u3002\u201d \u3000\u3000\u51e4\u4e00\u9e23\u6b63\u81ea\u5600\u5495\uff1a\u201c\u4e0d\u77e5\u6b64\u4eba\u771f\u59d3\u540d\u7a76\u662f\u4ec0\u4e48\uff0c\u82e5\u80fd\u6478\u6e05\u4ed6\u51e0\u5206\u5e95\u7ec6\uff0c\u5bf9\u4ed8\u8d77\u6765\u5c31\u5bb9\u6613\u5f97\u591a\u4e86\u3002\u201d\u542c\u4ed6\u63d0\u8d77\u5916\u53f7\uff0c\u5fd9\u9053\uff1a\u201c\u5c0f\u5f1f\u5b64\u964b\u5be1\u95fb\uff0c\u8bf7\u62d4\u7237\u544a\u77e5\u3002\u201d \u3000\u3000\u80e1\u6590\u201c\u54fc\u201d\u7684\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4e8f\u4f60\u4e5f\u662f\u6b66\u6797\u4e2d\u4eba\uff0c\u600e\u5730\u8fde\u5927\u540d\u9f0e\u9f0e\u7684\u2018\u6740\u5b98\u6bb4\u540f\u62d4\u51e4\u6bdb\u2019\u4e5f\u4e0d\u77e5\u9053\uff1f\u201d\u51e4\u4e00\u9e23\u4e00\u6014\uff0c\u9053\uff1a\u201c\u53d6\u7b11\u4e86\u3002\u201d \u3000\u3000\u80e1\u6590\u5de6\u624b\u500f\u5730\u4f38\u51fa\uff0c\u6293\u6ce8\u4ed6\u7684\u8863\u895f\uff0c\u559d\u9053\uff1a\u201c\u54a6\uff0c\u597d\u5927\u7684\u80c6\u5b50\uff01\u4f60\u600e\u6562\u5c06\u6211\u7684\u4e00\u5757\u51e4\u51f0\u8089\u5403\u4e0b\u4e86\u809a\u4e2d\u3002\u201d\u51e4\u4e00\u9e23\u518d\u4e5f\u5fcd\u8010\u4e0d\u4f4f\uff0c\u53f3\u624b\u865a\u51fa\u4e00\u638c\uff0c\u5de6\u624b\u4fbf\u6765\u62ff\u4ed6\u624b\u8155\u3002\u80e1\u6590\u624b\u638c\u75be\u7ffb\uff0c\u5f53\u771f\u5feb\u5982\u7535\u706b\uff0c\u53eb\u4eba\u731d\u4e0d\u53ca\u9632\uff0c\u556a\u7684\u4e00\u58f0\uff0c\u51e4\u4e00\u9e23\u5de6\u988a\u5df2\u5403\u4e86\u4e00\u8bb0\u5df4\u638c\uff0c\u987a\u624b\u5c06\u4ed6\u53f3\u624b\u62ff\u4f4f\uff0c\u559d\u9053\uff1a\u201d\u8fd8\u6211\u7684\u51e4\u51f0\u8089\u6765\u3002\u201d \u3000\u3000\u51e4\u4e00\u9e23\u5bb6\u5b66\u6e0a\u6e90\uff0c\u6b66\u529f\u7adf\u81ea\u4e0d\u5f31\uff0c\u53ea\u89c9\u81ea\u5df1\u53f3\u638c\u5b9b\u4f3c\u843d\u5165\u4e86\u4e00\u53cc\u94c1\u94b3\u4e4b\u4e2d\uff0c\u7b4b\u9aa8\u90e8\u6b32\u788e\u88c2\uff0c\u6025\u5fd9\u98de\u8d77\u53f3\u8db3\uff0c\u5411\u80e1\u6590\u5c0f\u8179\u4e0a\u8e22\u53bb\uff0c\u80e1\u6590\u63d0\u8d77\u811a\u6765\uff0c\u4ece\u7a7a\u4e00\u8db3\u8e0f\u843d\uff0c\u6b63\u597d\u8e0f\u6ce8\u4ed6\u7684\u8db3\u80cc\u3002\u51e4\u4e00\u9e23\u811a\u4e0a\u53c8\u5982\u88ab\u94c1\u9524\u4e00\u51fb\uff0c\u5fcd\u4e0d\u4f4f\u201c\u554a\u201d \u3000\u3000\u7684\u4e00\u58f0\u53eb\u4e86\u51fa\u6765\u3002\u80e1\u6590\u5de6\u624b\u53cd\u624b\u4e00\u638c\uff0c\u51e4\u4e00\u9e23\u53f3\u988a\u65e9\u7740\uff0c\u53cc\u988a\u5c31\u5982\u732a\u809d\u822c\u53c8\u7ea2\u53c8\u80bf\u3002 \u3000\u3000\u80e1\u6590\u5927\u58f0\u53eb\u9053\uff1a\u201c\u5404\u4f4d\u597d\u670b\u53cb\u542c\u7740\uff0c\u6211\u5343\u91cc\u8fe2\u8fe2\u5730\u4ece\u5317\u65b9\u6765\u5230\u4f5b\u5c71\uff0c\u5411\u8fd9\u91cc\u7684\u9524\u963f\u56db\u9524\u8001\u5144\u4e70\u5230\u4e00\u5757\u51e4\u51f0\u8089\uff0c\u5374\u8ba9\u8fd9\u53ae\u4e00\u53e3\u5077\u5403\u4e86\u3002\u4f60\u4eec\u8bf4\u8be5\u6253\u4e0d\u8be5\u6253\uff1f\u201d\u8d4c\u573a\u4e2d\u4f17\u4eba\u9762\u9762\u76f8\u89d1\uff0c\u4e0d\u6562\u8bf4\u8bdd\uff0c\u5fc3\u4e2d\u90fd\u77e5\u4ed6\u662f\u5728\u4e3a\u88ab\u903c\u6b7b\u7684\u9524\u5c0f\u4e09\u51fa\u6c14\u4f38\u51a4\u3002\u51e4\u4e00\u9e23\u7ed9\u4ed6\u8e0f\u4f4f\u4e00\u8db3\uff0c\u63e1\u4f4f\u4e00\u624b\uff0c\u5df2\u662f\u5168\u8eab\u65e0\u6cd5\u52a8\u5f39\u3002 \u3000\u3000\u53ea\u89c1\u4eba\u4e1b\u4e2d\u8f6c\u51fa\u4e00\u4e2a\u8001\u8005\uff0c\u624b\u4e2d\u62ff\u7740\u4e00\u6839\u77ed\u70df\u888b\uff0c\u6b63\u662f\u82f1\u96c4\u5f53\u94fa\u7684\u5927\u638c\u67dc\uff0c\u4ed6\u7ed9\u80e1\u6590\u903c\u53bb\u4e86\u4e5d\u5343\u4e24\u94f6\u5b50\uff0c\u54ea\u91cc\u4fbf\u80af\u7f62\u4f11\uff1f\u4e00\u9762\u547d\u4eba\u6025\u62a5\u51e4\u5929\u5357\uff0c\u4e00\u9762\u6084\u6084\u8ddf\u5230\u82f1\u96c4\u4f1a\u9986\u6765\u77a7\u4ed6\u7684\u52a8\u9759\uff0c\u8fd9\u65f6\u89c1\u5c0f\u4e3b\u4eba\u88ab\u8150\uff0c\u5fd9\u4e0a\u524d\u966a\u7b11\u9053\uff1a\u201c\u597d\u6c49\u7237\uff0c\u8fd9\u662f\u6211\u4eec\u51e4\u8001\u7237\u7684\u72ec\u751f\u7231\u5b50\uff0c\u51e4\u8001\u7237\u5f53\u4ed6\u72b9\u5982\u6027\u547d\u4e00\u822c\u3002\u597d\u6c49\u7237\u8981\u94f6\u5b50\u4f7f\u7528\uff0c\u5c3d\u7ba1\u5429\u5490\uff0c\u201d\u53ef\u8bf7\u5feb\u653e\u4e86\u6211\u4eec\u5c11\u4e3b\u4eba\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u8c01\u53eb\u4ed6\u5077\u5403\u4e86\u6211\u7684\u51e4\u51f0\u8089\uff1f\u662f\u51e4\u8001\u7237\u7684\u72ec\u751f\u7231\u5b50\uff0c\u4fbf\u80fd\u5077\u5403\u4eba\u5bb6\u4e1c\u897f\u4e48\uff1f\u201d\u5927\u638c\u67dc\u7b11\u9053\uff1a\u201c\u597d\u6c49\u53d6\u7b11\u4e86\u3002\u5929\u4e0b\u54ea\u6709\u4ec0\u4e48\u51e4\u51f0\u8089\uff1f\u4fbf\u7b97\u6709\uff0c\u6211\u4eec\u5c0f\u4e3b\u4eba\u4e5f\u51b3\u4e0d\u4f1a\u5077\u5403\u3002\u201d \u3000\u3000\u80e1\u6590\u559d\u9053\uff1a\u201c\u8fd9\u51e4\u51f0\u8089\u4e43\u5927\u8865\u4e4b\u5242\uff0c\u771f\u662f\u65e0\u4ef7\u4e4b\u5b9d\uff0c\u4e00\u5403\u4e0b\u809a\uff0c\u7acb\u65f6\u6ee1\u9762\u901a\u7ea2\uff0c\u80a5\u80d6\u8d77\u6765\u3002\u4f60\u4eec\u5927\u5bb6\u770b\uff0c\u4ed6\u7684\u8138\u662f\u5426\u6bd4\u5e73\u65f6\u7ea2\u4e86\u80d6\u4e86\uff1f\u8fd8\u8bf4\u6ca1\u5077\u5403\u6211\u7684\u51e4\u51f0\u8089\u4e48\uff1f\u201d\u5927\u638c\u67dc\u966a\u7b11\u9053\uff1a\u201d\u8fd9\u662f\u597d\u6c49\u7237\u4e0b\u624b\u6253\u80bf\u7684\uff0c\u4e0d\u4e0e\u51e4\u51f0\u8089\u76f8\u5e72\u3002\u201d \u3000\u3000\u80e1\u6590\u9053\uff1a\u201c\u5927\u5bb6\u6765\u8bc4\u4e2a\u7406\uff0c\u8fd9\u5c0f\u5b50\u53ef\u5077\u5403\u4e86\u6211\u7684\u51e4\u51f0\u8089\u4e48\uff1f\u201d \u3000\u3000\u5728\u8d4c\u573a\u4e2d\u80e1\u6df7\u4e4b\u4eba\uff0c\u4e00\u5c0f\u534a\u662f\u51e4\u5929\u5357\u7684\u624b\u4e0b\uff0c\u53e6\u4e00\u534a\u4e0d\u662f\u5730\u75de\u6d41\u6c13\uff0c\u4fbf\u662f\u7834\u843d\u6237\u5b50\u5f1f\uff0c\u4eba\u4eba\u754f\u60e7\u51e4\u5929\u5357\u7684\u5a01\u52bf\uff0c\u542c\u80e1\u6590\u5982\u6b64\u8be2\u95ee\uff0c\u4e03\u5634\u516b\u820c\u4ed6\u8bf4\u9053\uff1a\u201c\u6ca1\u89c1\u5230\u4f60\u6709\u4ec0\u4e48\u51e4\u51f0\u8089\u3002\u201d\u201c\u51e4\u5927\u7237\u51b3\u4e0d\u80fd\u5077\u4f60\u4e1c\u897f\u5403\u3002\u201d\u201c\u51e4\u8001\u7237\u5e9c\u4e0a\u7684\u4e1c\u897f\u8fd8\u6015\u5c11\u4e86\u4e48\uff1f\u600e\u80fd\u5077\u4eba\u4e1c\u897f\uff1f\u201d\u201c\u7b11\u8bdd\u7b11\u8bdd\uff01\u201d\u201c\u597d\u6c49\u5feb\u653e\u4e86\u4ed6\uff0c\u522b\u95f9\u51fa\u5927\u4e8b\u6765\u3002\u201d \u3000\u3000\u80e1\u6590\u9053\uff1a\u201c\u597d\uff0c\u4f60\u4eec\u5927\u5bb6\u8bf4\u4ed6\u6ca1\u5077\u5403\uff0c\u6211\u96be\u9053\u8d56\u4e86\u4ed6\uff1f\u54b1\u4eec\u5230\u5317\u5e1d\u5e99\u5224\u4e2a\u7406\u53bb\u3002\u201d \u3000\u3000\u4f17\u4eba\u4e00\u6014\uff0c\u7acb\u65f6\u60f3\u8d77\u9524\u56db\u5ac2\u5728\u5317\u5e1d\u5e99\u4e2d\u5200\u5256\u513f\u8179\u4e4b\u4e8b\u3002\u90a3\u5927\u638c\u67dc\u6697\u6697\u5403\u60ca\uff0c\u5fc3\u60f3\uff1a\u201c\u4e00\u5230\u5317\u5e1d\u5e99\uff0c\u90a3\u53ef\u8981\u95f9\u5f97\u4e0d\u53ef\u6536\u62fe\u3002\u201d\u4e0d\u4f4f\u5411\u80e1\u6590\u6253\u8eac\u4f5c\u63d6\uff0c\u9053\uff1a\u201c\u597d\u6c49\u7237\u8bf4\u5f97\u5bf9\uff0c\u6211\u4eec\u90fd\u9519\u4e86\u3002\u5c11\u4e3b\u4eba\u5403\u4e86\u597d\u6c49\u7684\u51e4\u51f0\u8089\uff0c\u597d\u6c49\u8981\u600e\u4e48\u8d54\uff0c\u4fbf\u600e\u6837\u8d54\u5c31\u662f\u3002\u201d\u80e1\u6590\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u5012\u8bf4\u5f97\u5bb9\u6613\u3002\u8fd9\u91cc\u4eba\u4eba\u4e0d\u670d\uff0c\u4e0d\u5230\u5317\u5e1d\u5e99\u8bc4\u4e2a\u660e\u767d\uff0c\u6211\u4eca\u540e\u8fd8\u6709\u8138\u89c1\u4eba\u4e48\uff1f\u201d\u8bf4\u7740\u5c06\u51e4\u4e00\u9e23\u631f\u5728\u814b\u4e0b\uff0c\u94f6\u5b50\u4e5f\u4e0d\u8981\u4e86\uff0c\u5927\u8e0f\u6b65\u8d70\u51fa\u8d4c\u573a\uff0c\u5411\u9014\u4eba\u95ee\u4e86\u8def\uff0c\u5f84\u5411\u5317\u5e1d\u5e99\u800c\u6765\u3002 \u3000\u3000\u90a3\u5317\u5e1d\u5e99\u5efa\u6784\u5b8f\u4f1f\uff0c\u597d\u5927\u4e00\u5ea7\u795e\u7960\uff0c\u8fdb\u95e8\u9662\u5b50\u4e2d\u4e00\u4e2a\u5927\u6c34\u5858\uff0c\u5858\u4e2d\u77f3\u9f9f\u77f3\u86c7\uff0c\u6602\u7136\u76d8\u8e1e\u3002 \u3000\u3000\u80e1\u6590\u62c9\u7740\u51e4\u4e00\u9e23\u6765\u5230\u5927\u6bbf\uff0c\u53ea\u89c1\u795e\u50cf\u77f3\u677f\u4e0a\u8840\u8ff9\u6bb7\u7136\uff0c\u60f3\u8d77\u9524\u56db\u5ac2\u88ab\u903c\u5207\u5256\u513f\u8179\u7684\u60e8\u4e8b\uff0c\u80f8\u95f4\u70ed\u8840\u4e0a\u51b2\uff0c\u5c06\u51e4\u4e00\u9e23\u5f80\u5730\u4e0a\u4e00\u63a8\uff0c\u62ac\u5934\u5411\u7740\u5317\u5e1d\u795e\u50cf\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u5317\u5e1d\u7237\uff0c\u5317\u5e1d\u7237\uff0c\u4f60\u5a01\u7075\u663e\u8d6b\uff0c\u66ff\u5c0f\u6c11\u6709\u51a4\u4f38\u51a4\uff0c\u6709\u4ec7\u62a5\u4ec7\u3002 \u3000\u3000\u8fd9\u8d3c\u53ae\u9e1f\u5077\u5403\u4e86\u6211\u7684\u51e4\u51f0\u8089\uff0c\u4f46\u65c1\u4eba\u90fd\u8bf4\u4ed6\u6ca1\u5403\u2026\u2026\u201d \u3000\u3000\u4ed6\u8bdd\u672a\u8bf4\u5b8c\uff0c\u731b\u89c9\u80cc\u540e\u98ce\u58f0\u98d2\u7136\uff0c\u5de6\u53f3\u6709\u4eba\u53cc\u53cc\u6765\u88ad\u3002\u4ed6\u5934\u4e00\u4f4e\uff0c\u8eab\u5b50\u4e00\u7f29\uff0c\u90a3\u4e8c\u4eba\u5df2\u7136\u6251\u7a7a\u3002\u4ed6\u53cc\u624b\u5206\u522b\u5728\u4e8c\u4eba\u80cc\u4e0a\u4e00\u63a8\uff0c\u7830\u7684\u4e00\u58f0\uff0c\u4e8c\u4eba\u8138\u5bf9\u8138\u731b\u5730\u4e00\u649e\uff0c\u767b\u65f6\u6655\u53bb\u3002\u53ea\u542c\u5f97\u4e00\u4eba\u9ad8\u58f0\u6012\u543c\uff0c\u53c8\u6251\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u80e1\u6590\u542c\u4ed6\u811a\u6b65\u6c89\u91cd\uff0c\u6765\u52bf\u5a01\u731b\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u4eba\u529f\u592b\u5012\u4e5f\u4e0d\u5f31\u3002\u201d\u4e00\u4fa7\u8eab\u95f4\uff0c\u4e58\u52bf\u4e00\u5e26\uff0c\u53ea\u89c1\u5200\u5149\u95ea\u52a8\uff0c\u4e00\u6761\u80a5\u6c34\u726f\u4f3c\u7684\u7c97\u58ee\u5927\u6c49\u5df2\u5728\u8eab\u65c1\u63a0\u8fc7\uff0c\u4e00\u5200\u5f84\u5411\u51e4\u4e00\u9e23\u5934\u9876\u780d\u843d\uff0c\u603b\u7b97\u4ed6\u6b66\u529f\u4e0d\u4f4e\uff0c\u5371\u6025\u4e4b\u9645\u624b\u81c2\u4e00\u504f\uff0c\u4e00\u5200\u780d\u5728\u5730\u4e0b\u9752\u7816\u4e4b\u4e0a\uff0c\u7816\u5c51\u7eb7\u98de\u3002\u80e1\u6590\u53eb\u9053\uff1a\u201c\u5999\u6781\uff01\u201d\u5de6\u8db3\u4f38\u51fa\uff0c\u5df2\u8e0f\u4f4f\u4ed6\u7684\u624b\u8098\u3002 \u3000\u3000\u90a3\u5927\u6c49\u72c2\u543c\u4e00\u58f0\uff0c\u653e\u624b\u6492\u5200\u3002\u80e1\u6590\u53f3\u8db3\u4e00\u6311\uff0c\u5355\u5200\u98de\u5c06\u8d77\u6765\uff0c\u987a\u624b\u63a5\u8fc7\uff0c\u7b11\u9053\uff1a\u201c\u6211\u6b63\u6101\u6ca1\u5200\u5256\u4ed6\u809a\u5b50\uff0c\u4f60\u5df4\u5df4\u7684\u8d76\u6765\u9001\u5200\uff0c\u5f53\u771f\u6709\u52b3\u4e86\u3002\u201d \u3000\u3000\u90a3\u5927\u6c49\u6012\u6781\uff0c\u4f7f\u529b\u6323\u624e\u3002\u80e1\u6590\u5de6\u817f\u4e00\u677e\uff0c\u7adf\u88ab\u4ed6\u7ffb\u8eab\u8dc3\u8d77\uff0c\u539f\u6765\u8fd9\u5927\u6c49\u86ee\u529b\u8fc7\u4eba\u3002\u4ed6\u53f3\u8db3\u4e00\u6491\uff0c\u53cc\u624b\u5341\u6307\u5982\u94a9\uff0c\u5728\u7a7a\u4e2d\u5f84\u5411\u80e1\u6590\u6251\u5230\u3002\u80e1\u6590\u4e00\u8f6c\u8eab\uff0c\u5df2\u7ed5\u5230\u4ed6\u7684\u8eab\u540e\uff0c\u5de6\u624b\u642d\u4ed6\u80a5\u81c0\u4e4b\u4e0a\uff0c\u501f\u529b\u4e00\u9001\uff0c\u559d\u9053\uff1a\u201c\u4e0a\u5929\u5427\uff01\u201d\u8fd9\u4e00\u9001\u6709\u516b\u6210\u5012\u662f\u501f\u4e86\u90a3\u5927\u6c49\u672c\u8eab\u7eb5\u8dc3\u4e4b\u52bf\u3002\u90a3\u5927\u6c49\u8eab\u4e0d\u7531\u4e3b\uff0c\u5411\u4e0a\u75be\u98de\uff0c\u65c1\u89c2\u4f17\u4eba\u5927\u53eb\u58f0\u4e2d\uff0c\u773c\u89c1\u8981\u7a7f\u7834\u5e99\u9876\u800c\u51fa\u3002\u4ed6\u5fd9\u4f38\u51fa\u53cc\u624b\uff0c\u62b1\u4f4f\u4e86\u5927\u6bbf\u6b63\u4e2d\u7684\u6a2a\u6881\uff0c\u603b\u7b97\u6ca1\u649e\u7834\u8111\u95e8\uff0c\u4f46\u5c31\u8fd9\u4e48\u6302\u5728\u534a\u7a7a\uff0c\u5411\u4e0b\u4e00\u671b\uff0c\u79bb\u5730\u6570\u4e08\u3002\u4ed6\u6ca1\u7ec3\u8fc7\u8f7b\u529f\uff0c\u8eab\u5b50\u53c8\u91cd\uff0c\u5916\u5bb6\u786c\u529f\u867d\u7136\u4e0d\u5f31\uff0c\u5374\u4e0d\u6562\u8dc3\u4e0b\u3002\u8fd9\u5927\u6c49\u5728\u4e94\u864e\u95e8\u4e2d\u4f4d\u5c45\u7b2c\u4e09\uff0c\u4e43\u662f\u51e4\u5929\u5357\u7684\u5f97\u529b\u52a9\u624b\uff0c\u4f5b\u5c71\u9547\u4e0a\u4eba\u4eba\u60e7\u6015\uff0c\u8fd9\u65f6\u6302\u5728\u6881\u4e0a\uff0c\u4e0a\u4e0d\u5f97\uff0c\u4e0b\u4e0d\u6765\uff0c\u6781\u662f\u72fc\u72c8\u3002 \u3000\u3000\u80e1\u6590\u62c9\u4f4f\u51e4\u4e00\u9e23\u7684\u8863\u895f\uff0c\u5411\u4e0a\u4e00\u626f\uff0c\u55e4\u7684\u4e00\u54cd\uff0c\u9732\u51fa\u809a\u8179\u808c\u80a4\uff0c\u6a2a\u8fc7\u5200\u950b\uff0c\u5411\u6324\u5728\u6bbf\u4e0a\u7684\u4f17\u4eba\u53eb\u9053\uff1a\u201c\u4ed6\u662f\u5426\u5403\u4e86\u51e4\u51f0\u8089\uff0c\u5927\u5bb6\u7741\u5927\u773c\u775b\u77a7\u4e2a\u660e\u767d\uff0c\u522b\u8bf4\u6211\u51a4\u6789\u4e86\u597d\u4eba\u3002\u201d \u3000\u3000\u65c1\u8fb9\u56db\u4e94\u4e2a\u4e61\u7ec5\u6a21\u6837\u7684\u4eba\u4e00\u9f50\u6765\u529d\uff0c\u90fd\u9053\uff1a\u201c\u597d\u6c49\u7237\u9ad8\u62ac\u8d35\u624b\uff0c\u82e5\u662f\u5256\u4e86\u809a\u5b50\uff0c\u4eba\u6b7b\u4e0d\u80fd\u590d\u751f\uff0c\u90a3\u53ef\u4e0d\u5f97\u4e86\u3002\u201d\u80e1\u6590\u5fc3\u60f3\uff1a\u201c\u8fd9\u4e9b\u4eba\u9b3c\u9b3c\u795f\u795f\uff0c\u5b9a\u4e0e\u51e4\u5929\u5357\u4e00\u9f3b\u5b54\u51fa\u6c14\u3002\u201d\u56de\u5934\u6012\u559d\uff1a\u201c\u90a3\u9524\u56db\u5ac2\u5256\u5b69\u5b50\u809a\u5b50\uff0c\u4f60\u4eec\u4f55\u4ee5\u4fbf\u4e0d\u529d\u4e86\uff1f\u6709\u94b1\u5b50\u5f1f\u7684\u6027\u547d\u503c\u94b1\uff0c\u7a77\u4eba\u7684\u5b69\u5b50\u4fbf\u4e0d\u662f\u6027\u547d\uff1f\u4f60\u4eec\u5feb\u56de\u5bb6\u53bb\uff0c\u6bcf\u4eba\u628a\u81ea\u5df1\u7684\u513f\u5b50\u9001\u4e00\u4e2a\u6765\uff0c\u82e5\u4e0d\u9001\u5230\uff0c\u6211\u81ea\u5df1\u4e0a\u95e8\u627e\u5bfb\u3002\u6211\u7684\u51e4\u51f0\u8089\u82e5\u4e0d\u662f\u4ed6\u5403\u7684\uff0c\u4fbf\u662f\u4f60\u4eec\u513f\u5b50\u5403\u516b\u6211\u4e00\u4e2a\u4e2a\u5256\u5f00\u809a\u4e8e\u6765\uff0c\u67e5\u4e2a\u660e\u767d\u3002\u201d\u8fd9\u51e0\u53e5\u8bdd\u76f4\u628a\u90a3\u513f\u4e2a\u4e61\u7ec5\u5413\u5f97\u9b42\u4e0d\u9644\u4f53\uff0c\u518d\u4e5f\u4e0d\u6562\u5f00\u53e3\u3002 \u3000\u3000\u6b63\u4e71\u95f4\uff0c\u5e99\u95e8\u5916\u4e00\u9635\u55a7\u54d7\uff0c\u62a2\u8fdb\u4e00\u7fa4\u4eba\u6765\u3002\u5f53\u5148\u4e00\u4eba\u8eab\u6750\u9ad8\u5927\uff0c\u7a7f\u4e00\u4ef6\u53e4\u94dc\u8272\u7f0e\u888d\uff0c\u53cc\u624b\u4e00\u5206\uff0c\u5927\u6bbf\u4e0a\u5df2\u6709\u4e03\u516b\u4eba\u5411\u4e24\u65c1\u8dcc\u51fa\u6570\u5c3a\u3002 \u3000\u3000\u80e1\u6590\u89c1\u4e86\u4ed6\u8fd9\u7b49\u6c14\u6d3e\u5a01\u52bf\uff0c\u53c8\u662f\u5982\u6b64\u6a2a\u6cd5\uff0c\u5fc3\u60f3\uff1a\u201d\u554a\u54c8\uff0c\u6b63\u70b9\u5b50\u7ec8\u4e8e\u5230\u4e86\u3002\u201d\u773c\u5149\u5411\u4ed6\u4ece\u5934\u4e0a\u77a7\u5230\u811a\u4e0b\uff0c\u53c8\u4ece\u811a\u4e0b\u770b\u5230\u5934\u4e0a\u3002\u53ea\u89c1\u4ed6\u4e0a\u5507\u7559\u7740\u4e24\u6487\u82b1\u767d\u5c0f\u9aed\uff0c\u7ea6\u83ab\u4e94\u5341\u6765\u5c81\u5e74\u7eaa\uff0c\u53f3\u8155\u6234\u4e00\u53ea\u6c49\u7389\u9505\uff0c\u5de6\u4e0b\u62ff\u7740\u4e00\u4e2a\u7fe1\u7fe0\u9f3b\u70df\u58f6\uff0c\u4fe8\u7136\u662f\u4e2a\u517b\u5c0a\u5904\u4f18\u7684\u5927\u4e61\u7ec5\u6a21\u6837\uff0c\u5b9e\u4e0d\u4f3c\u4e2a\u5750\u5730\u5206\u8d43\u7684\u6b66\u6797\u6076\u9738\uff0c\u53ea\u662f\u811a\u6b65\u51dd\u7a33\uff0c\u53cc\u76ee\u6709\u5a01\uff0c\u591a\u534a\u6b66\u529f\u9ad8\u5f3a\u3002 \u3000\u3000\u8fd9\u4eba\u6b63\u662f\u4e94\u864e\u95e8\u638c\u95e8\u4eba\u5357\u9738\u5929\u51e4\u5929\u5357\uff0c\u4ed6\u966a\u7740\u4eac\u91cc\u6765\u7684\u4e24\u540d\u4f8d\u536b\u5728\u5e9c\u5185\u996e\u5bb4\uff0c\u542c\u5f97\u4e0b\u4eba\u4e00\u8fde\u4e32\u7684\u6765\u62a5\uff0c\u6709\u4eba\u6df7\u95f9\u9152\u697c\u3001\u5f53\u94fa\u3001\u8d4c\u573a\u3002\u4ed6\u4e0d\u613f\u5728\u5fa1\u524d\u4f8d\u536b\u8ddf\u524d\u5931\u4e86\u6c14\u6d3e\uff0c\u4e00\u76f4\u7f6e\u4e4b\u4e0d\u7406\uff0c\u5fc3\u60f3\u8fd9\u4e9b\u5c0f\u4e8b\uff0c\u624b\u4e0b\u4eba\u5b9a\u80fd\u6253\u53d1\uff0c\u76f4\u542c\u5230\u513f\u5b50\u906d\u64d2\uff0c\u88ab\u62ff\u5230\u5317\u5e1d\u5e99\u4e2d\u8981\u5f00\u819b\u5256\u809a\uff0c\u8fd9\u624d\u5306\u5306\u8d76\u6765\u3002\u4ed6\u8fd8\u9053\u662f\u6781\u5389\u5bb3\u7684\u5bf9\u5934\u6765\u5230\u5bfb\u4ec7\uff0c\u54ea\u77e5\u4e00\u770b\u80e1\u6590\uff0c\u7adf\u662f\u4e2a\u7d20\u4e0d\u76f8\u8bc6\u7684\u4e61\u4e0b\u5c11\u5e74\uff0c\u5f53\u4e0b\u66f4\u4e0d\u6253\u8bdd\uff0c\u4fef\u8eab\u4fbf\u8981\u6276\u8d77\u513f\u5b50\u3002 \u3000\u3000\u80e1\u6590\u5fc3\u60f3\uff1a\u201c\u8fd9\u8001\u5bb6\u4f19\u597d\u72c2\uff0c\u7ade\u5c06\u6211\u89c6\u5982\u65e0\u7269\u3002\u201d\u5f85\u4ed6\u5f2f\u8170\u4fef\u8eab\uff0c\u4e00\u638c\u4fbf\u5f80\u4ed6\u8170\u95f4\u62cd\u53bb\uff0c\u51e4\u5929\u5357\u7adf\u4e0d\u56de\u8eab\uff0c\u5de6\u624b\u56de\u638c\uff0c\u60f3\u5c06\u4ed6\u624b\u638c\u683c\u5f00\uff0c\u80e1\u6590\u4e00\u50ac\u52b2\u529b\uff0c\u556a\u7684\u4e00\u58f0\uff0c\u53cc\u638c\u76f8\u4ea4\uff0c\u51e4\u5929\u5357\u8eab\u5b50\u4e00\u6643\uff0c\u9669\u4e9b\u8dcc\u5728\u513f\u5b50\u8eab\u4e0a\uff0c\u624d\u77e5\u8fd9\u4e61\u4e0b\u5c11\u5e74\u539f\u6765\u662f\u4e2a\u52b2\u654c\u3002\u5f53\u4e0b\u987e\u4e0d\u5f97\u53bb\u6276\u513f\u5b50\uff0c\u53f3\u624b\u6a2a\u62f3\uff0c\u731b\u51fb\u80e1\u6590\u8170\u773c\u3002 \u3000\u3000\u80e1\u6590\u89c1\u4ed6\u53d8\u62db\u8fc5\u6377\uff0c\u62f3\u6765\u5982\u98ce\uff0c\u679c\u7136\u662f\u540d\u5bb6\u8eab\u624b\uff0c\u6325\u5200\u5f80\u4ed6\u62f3\u5934\u4e0a\u75be\u780d\u4e0b\u53bb\u3002 \u3000\u3000\u8fd9\u4e00\u5200\u867d\u7136\u51f6\u731b\uff0c\u51e4\u5929\u5357\u4e5f\u53ea\u987b\u4e00\u7f29\u624b\u4fbf\u80fd\u907f\u8fc7\uff0c\u4f46\u51e4\u4e00\u9e23\u6a2a\u5367\u5728\u5730\uff0c\u4ed6\u7f29\u624b\u4e0d\u6253\u7d27\uff0c\u513f\u5b50\u5374\u8981\u53d7\u4e86\u8fd9\u4e00\u5200\u3002\u5f53\u6b64\u5371\u6025\u4e4b\u9645\uff0c\u4ed6\u5e94\u53d8\u5012\u4e5f\u5947\u901f\uff0c\u4e00\u626f\u795e\u575b\u524d\u7684\u684c\u62ab\uff0c\u5012\u5377\u4e0a\u6765\uff0c\u683c\u5f00\u4e86\u8fd9\u4e00\u5200\u3002\u80e1\u6590\u53eb\u9053\uff1a\u201c\u597d\uff01\u201d\u5de6\u624b\u4f38\u51fa\uff0c\u5df2\u6293\u4f4f\u684c\u62ab\u4e00\u7aef\u3002\u4e24\u4eba\u540c\u65f6\u5411\u5916\u62c9\u626f\uff0c\u556a\u5566\u7684\u4e00\u54cd\uff0c\u684c\u62ab\u4ece\u4e2d\u65ad\u4e3a\u4e24\u622a\u3002 \u3000\u3000\u6b64\u65f6\u51e4\u5929\u5357\u54ea\u91cc\u8fd8\u6709\u534a\u70b9\u5c0f\u89d1\u4e4b\u5fc3\uff1f\u5411\u540e\u8dc3\u5f00\u534a\u4e08\uff0c\u65e9\u6709\u5f1f\u5b50\u5c06\u4ed6\u7684\u5175\u5203\u9ec4\u91d1\u68cd\u9001\u5728\u624b\u4e2d\u3002\u8fd9\u91d1\u68cd\u957f\u8fbe\u4e03\u5c3a\uff0c\u5f84\u4e00\u5bf8\u6709\u534a\uff0c\u901a\u4f53\u9ec4\u91d1\u94f8\u6210\uff0c\u53ef\u7b97\u5f97\u6b66\u6797\u4e2d\u7b2c\u4e00\u8c6a\u9614\u5bcc\u4e3d\u7684\u6c89\u91cd\u5175\u5668\u3002\u4ed6\u5c06\u91d1\u68cd\u4e00\u6296\uff0c\u6307\u7740\u80e1\u6590\u8bf4\u9053\uff1a\u201c\u9601\u4e0b\u662f\u54ea\u4e00\u4f4d\u8001\u5e08\u7684\u95e8\u4e0b\uff1f\u51e4\u67d0\u4ec0\u4e48\u5730\u65b9\u5f97\u7f6a\u4e86\u9601\u4e0b\uff0c\u5374\u8981\u8bf7\u6559\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u6211\u4e00\u5757\u51e4\u51f0\u8089\u7ed9\u4f60\u513f\u5b50\u5077\u5403\u4e86\uff0c\u975e\u5256\u5f00\u4ed6\u809a\u5b50\u77a7\u4e2a\u660e\u767d\u4e0d\u53ef\u3002\u201d \u3000\u3000\u51e4\u5929\u5357\u51ed\u4e00\u6761\u719f\u94dc\u68cd\u6253\u904d\u5cad\u5357\u65e0\u654c\u624b\uff0c\u8fd9\u624d\u624b\u521b\u4e94\u864e\u95e8\uff0c\u5728\u4f5b\u5c71\u9547\u5b9a\u5c45\uff0c\u5bb6\u4e1a\u5927\u53d1\u4e4b\u540e\uff0c\u5c06\u719f\u94dc\u68cd\u6539\u4e3a\u9ec4\u91d1\u68cd\u3002\u6b66\u5bb6\u6240\u7528\u4e4b\u68cd\uff0c\u4ee5\u9f50\u7709\u6700\u4e3a\u5bfb\u5e38\uff0c\u4f9d\u8eab\u6750\u4f38\u7f29\uff0c\u77ed\u8005\u4e94\u5c3a\u4e0d\u8db3\uff0c\u957f\u8005\u516d\u5c3a\u6709\u4f59\uff0c\u51e4\u5929\u5357\u8fd9\u6761\u68cd\u5374\u957f\u8fbe\u4e03\u5c3a\uff0c\u9ec4\u91d1\u53c8\u8f83\u9547\u94c1\u91cd\u8fd1\u4e24\u500d\uff0c\u4ed7\u7740\u4ed6\u8182\u529b\u8fc7\u4eba\uff0c\u4f7f\u5f00\u6765\u4e24\u4e08\u4e4b\u5185\u4e00\u56e2\u9ec4\u5149\uff0c\u7aef\u7684\u662f\u5389\u5bb3\u4e4b\u6781\u3002 \u3000\u3000\u4ed6\u542c\u4e86\u80e1\u6590\u4e4b\u8a00\uff0c\u77e5\u9053\u4eca\u65e5\u5df2\u4e0d\u80fd\u5584\u7f62\uff0c\u91d1\u68cd\u8d77\u5904\uff0c\u624b\u8155\u6296\u4e86\u4e24\u6296\uff0c\u68cd\u7aef\u5c06\u795e\u575b\u4e0a\u4e24\u70b9\u70db\u706b\u70b9\u7184\u4e86\uff0c\u53eb\u9053\uff1a\u201c\u5728\u4e0b\u7d20\u6765\u7231\u4ea4\u670b\u53cb\uff0c\u4e0e\u5c0a\u9a7e\u7d20\u4e0d\u76f8\u8bc6\uff0c\u4f55\u82e6\u4e3a\u4e00\u4e2a\u7a77\u5bb6\u5c0f\u5b50\u4f24\u4e86\u6c5f\u6e56\u4e49\u6c14\uff1f\u662f\u53cb\u662f\u654c\uff0c\u4f46\u51ed\u5c0a\u9a7e\u4e00\u8a00\u800c\u51b3\u3002\u201d \u3000\u3000\u8981\u77e5\u91d1\u68cd\u4e43\u6781\u6c89\u91cd\u7684\u5175\u5668\uff0c\u4ed6\u4e00\u6296\u68cd\u82b1\u800c\u6253\u706d\u70db\u706b\uff0c\u5999\u5728\u4e0d\u78b0\u635f\u534a\u70b9\u8721\u70db\uff0c\u70db\u53f0\u6beb\u4e0d\u6447\u6643\uff0c\u624b\u6cd5\u4e4b\u51c6\uff0c\u53ef\u8bf4\u662f\u6781\u7f55\u89c1\u7684\u529f\u592b\u3002\u4ed6\u8a00\u8bed\u4e2d\u8f6f\u91cc\u5e26\u786c\uff0c\u8981\u80e1\u6590\u77e5\u96be\u800c\u9000\uff0c\u4e0d\u5fc5\u591a\u7ba1\u95f2\u4e8b\u3002\u80e1\u6590\u7b11\u9053\uff1a\u201c\u662f\u554a\uff0c\u4f60\u7684\u8bdd\u518d\u5bf9\u4e5f\u6ca1\u6709\uff0c\u4f60\u53ea\u987b\u5272\u4e00\u5757\u51e4\u51f0\u8089\u8d54\u6211\uff0c\u6211\u7acb\u5373\u62cd\u62cd\u7070\u5c18\u8d70\u8def\uff0c\u4f60\u770b\u53ef\u597d\uff1f\u201d\u51e4\u5929\u5357\u8138\u4e00\u6c89\uff0c\u559d\u9053\uff1a\u201c\u65e2\u662f\u5982\u6b64\uff0c\u54b1\u4eec\u5175\u5203\u4e0a\u5206\u9ad8\u4e0b\u4fbf\u4e86\u3002\u201d\u8bf4\u7740\u63d0\u68cd\u8dc3\u5411\u9662\u5b50\u3002 \u3000\u3000\u80e1\u6590\u63d0\u8d77\u51e4\u4e00\u9e23\u5f80\u5730\u4e0b\u4e00\u6454\uff0c\u5c06\u5355\u5200\u63d2\u5728\u4ed6\u7684\u8eab\u65c1\uff0c\u559d\u9053\uff1a\u201c\u4f60\u82e5\u662f\u9003\u8d70\uff0c\u4fbf\u8981\u4f60\u8001\u5b50\u62b5\u547d\uff01\u201d\u7a7a\u624b\u8d70\u51fa\uff0c\u5927\u58f0\u9053\uff1a\u201c\u8001\u7237\u884c\u4e0d\u6539\u59d3\uff0c\u5750\u4e0d\u6539\u540d\uff0c\u5927\u540d\u9f0e\u9f0e\u2018\u6740\u5b98\u6bb4\u540f\u62d4\u51e4\u6bdb\u2019\u4fbf\u662f\u3002\u51e4\u6bdb\u62d4\u4e0d\u5230\uff0c\u81ed\u9e21\u81ed\u9e2d\u7684\u5c41\u80a1\u6bdb\u62d4\u513f\u6839\u4e5f\u662f\u597d\u7684\u3002\u5927\u5bb6\u77a7\u6e05\u695a\u4e86\u3002\u201d\u4e00\u8a00\u752b\u6bd5\uff0c\u7a81\u7136\u5de6\u624b\u63a2\u51fa\uff0c\u5f84\u6765\u6293\u5bf9\u65b9\u68cd\u5934\u3002 \u3000\u3000\u51e4\u5929\u5357\u77e5\u4ed6\u6b66\u529f\u5389\u5bb3\uff0c\u5fc3\u60f3\u4f60\u81ea\u5df1\u6258\u5927\uff0c\u4e0d\u7528\u5175\u5203\uff0c\u90a3\u53ef\u602a\u4e0d\u5f97\u6211\uff0c\u773c\u89c1\u4ed6\u51fa\u624b\u4fbf\u593a\u5175\u5203\uff0c\u7adf\u5bf9\u81ea\u5df1\u85d0\u89c6\u5df2\u6781\uff0c\u5f53\u4e0b\u68cd\u5c3e\u6296\u8d77\uff0c\u4e00\u62db\u201c\u9a71\u4e91\u626b\u6708\u201d\uff0c\u5411\u4ed6\u5934\u9888\u6a2a\u626b\u8fc7\u6765\u3002 \u3000\u3000\u8fd9\u4e00\u62db\u867d\u4ee5\u6a2a\u626b\u4e3a\u4e3b\uff0c\u4f46\u540e\u7740\u4e2d\u6709\u70b9\u6709\u6253\uff0c\u6709\u7f20\u6709\u6311\uff0c\u6240\u8c13\u201c\u5355\u5934\u53cc\u5934\u7f20\u5934\uff0c\u5934\u5934\u662f\u9053\uff1b\u6b63\u9762\u4fa7\u9762\u80cc\u9762\uff0c\u9762\u9762\u7686\u7075\u201d\uff0c\u7684\u662f\u6b66\u5b66\u4e2d\u7684\u6781\u4e0a\u4e58\u68cd\u6cd5\u3002 \u3000\u3000\u80e1\u6590\u8eab\u968f\u68cd\u8f6c\uff0c\u8fd8\u4e86\u4e00\u638c\u3002 \u3000\u3000\u4f17\u4eba\u51dd\u795e\u5c4f\u606f\uff0c\u6ce8\u89c6\u4e8c\u4eba\u6fc0\u6597\u3002\u98ce\u5929\u5357\u624b\u4e0b\u4eba\u6570\u867d\u4f17\uff0c\u4f46\u4e0d\u5f97\u4ed6\u7684\u793a\u610f\uff0c\u8c01\u4e5f\u4e0d\u6562\u63d2\u624b\u76f8\u52a9\uff0c\u4f55\u51b5\u4e8c\u4eba\u7eb5\u8dc3\u5982\u98ce\uff0c\u65c1\u4eba\u6b66\u529f\u8fdc\u8fdc\u4e0d\u53ca\uff0c\u4fbf\u8981\u76f8\u52a9\uff0c\u4e5f\u662f\u65e0\u4ece\u7740\u624b\u3002 \u3000\u3000\u4e8c\u4eba\u6076\u6597\u6b63\u9163\uff0c\u5e99\u95e8\u4e2d\u53c8\u95ef\u8fdb\u4e09\u4e2a\u4eba\u6765\u3002\u5f53\u5148\u4e00\u4e2a\u5987\u4eba\u4e71\u53d1\u62ab\u8eab\uff0c\u6ee1\u8eab\u8840\u6c61\uff0c\u6b63\u662f\u9524\u56db\u5ac2\u3002\u5979\u4e00\u8def\u78d5\u5934\uff0c\u4e00\u8def\u722c\u7740\u8fdb\u6765\uff0c\u8eab\u540e\u8ddf\u7740\u4e8c\u4eba\uff0c\u4e00\u4e2a\u662f\u5979\u4e08\u592b\u9524\u963f\u56db\uff0c\u4e00\u4e2a\u662f\u5979\u513f\u5b50\u9524\u5c0f\u4e8c\u3002 \u3000\u3000\u9524\u56db\u5ac2\u8dea\u5728\u5730\u4e0b\uff0c\u4e0d\u4f4f\u5411\u51e4\u5927\u5357\u78d5\u5934\uff0c\u54c8\u54c8\u5927\u7b11\uff0c\u53eb\u9053\uff1a\u201d\u51e4\u8001\u7237\u4f60\u5927\u4ec1\u5927\u4e49\uff0c\u5317\u5e1d\u7237\u7237\u4fdd\u4f51\u4f60\u591a\u798f\u591a\u5bff\uff0c\u4fdd\u4f51\u4f60\u91d1\u7389\u6ee1\u5802\uff0c\u56db\u5b63\u53d1\u8d22\uff0c\u6211\u5c0f\u4e09\u5b50\u5728\u960e\u738b\u7237\u9762\u524d\u544a\u4e86\u4f60\u4e00\u72b6\uff0c\u960e\u738b\u7237\u8bf4\u4f60\u5927\u5bcc\u5927\u8d35\uff0c\u540e\u798f\u65e0\u7a77\u54ea\u3002\u201d\u5979\u75af\u75af\u766b\u766b\u5730\u53c8\u8dea\u53c8\u62dc\uff0c\u53c8\u54ed\u53c8\u7b11\u3002\u9524\u963f\u56db\u5374\u94c1\u9752\u7740\u8138\uff0c\u4e00\u58f0\u4e0d\u4f5c\u3002 \u3000\u3000\u51e4\u5929\u5357\u4e0e\u80e1\u6590\u62c6\u4e86\u5341\u4f59\u62db\uff0c\u65e9\u5df2\u5168\u7136\u843d\u5728\u4e0b\u98ce\u3002\u91d1\u68cd\u6325\u6210\u7684\u9676\u5b50\u8d8a\u6765\u8d8a\u5c0f\uff0c\u89c1\u9524\u56db\u5ac2\u4f3c\u75af\u975e\u75af\u5730\u5411\u7740\u81ea\u5df1\u8dea\u62dc\uff0c\u66f4\u662f\u5fc3\u795e\u4e0d\u5b81\uff0c\u77e5\u9053\u518d\u6597\u4e0b\u53bb\u5b9a\u8981\u4e00\u8d25\u4e0d\u53ef\u6536\u62fe\uff0c\u5f53\u4e0b\u52b2\u8d2f\u53cc\u81c2\uff0c\u4f7f\u4e00\u62db\u201c\u626c\u7709\u5410\u6c14\u201d\uff0c\u5f80\u80e1\u6590\u4e0b\u989a\u6311\u53bb\u3002 \u3000\u3000\u8fd9\u4e00\u68cd\u52bf\u5939\u52b2\u98ce\uff0c\u91d1\u5149\u8000\u773c\uff0c\u80e1\u6590\u5374\u4e0d\u95ea\u4e0d\u7f29\uff0c\u4f38\u624b\u7adf\u7136\u786c\u3000\u593a\u4ed6\u7684\u91d1\u68cd\u3002 \u3000\u3000\u51e4\u5929\u5357\u53c8\u60ca\u53c8\u559c\uff0c\u5fc3\u60f3\uff1a\u201c\u4f60\u8fd9\u53ea\u624b\u722a\u5b50\u5c31\u7b97\u662f\u94c1\u94f8\u7684\uff0c\u4e5f\u6253\u6298\u4e86\u4f60\u3002\u201d\u5f53\u4e0b\u529b\u900f\u624b\u8155\uff0c\u6025\u6311\u4e4b\u529b\u66f4\u5927\uff0c\u80e1\u6590\u624b\u638c\u4e0e\u68cd\u5934\u4e00\u642d\u7740\uff0c\u8f7b\u8f7b\u5411\u540e\u4e00\u7f29\uff0c\u5df2\u5c06\u4ed6\u6311\u529b\u5378\u53bb\uff0c\u624b\u6307\u5f2f\u8fc7\uff0c\u6293\u4f4f\u4e86\u68cd\u5934\u3002\u603b\u7b97\u98ce\u5929\u5357\u5728\u8fd9\u6761\u68cd\u4e0a\u5df2\u4e0b\u4e86\u4e09\u5341\u4f59\u5e74\u82e6\u529f\uff0c\u5fd9\u4f7f\u4e00\u62db\u201c\u4e0a\u6ed1\u4e0b\u52ab\u201d\uff0c\u8ddf\u7740\u4e00\u62db\u201c\u7ffb\u5929\u5f7b\u5730\u201d\uff0c\u4ee5\u6781\u521a\u731b\u7684\u5916\u52b2\u786c\u593a\u56de\u53bb\u3002\u80e1\u6590\u53eb\u9053\uff1a\u201d\u62d4\u81ed\u9e21\u6bdb\u4e86\uff01\u201d\u53cc\u624b\u5185\u5916\u5411\u5185\u5708\u8f6c\u3002\u5374\u6765\u634f\u4ed6\u54bd\u5589\uff0c\u4e5f\u4e0d\u77e5\u4ed6\u5982\u4f55\u79fb\u52a8\u8eab\u5f62\uff0c\u7adf\u5728\u8fd9\u4e00\u6293\u4e00\u593a\u4e4b\u9645\uff0c\u987a\u52bf\u653b\u8fdb\u4e86\u95e8\u6237\u3002\u51e4\u5929\u5357\u7684\u91d1\u68cd\u53cd\u5728\u5916\u6863\uff0c\u5df1\u7136\u6253\u4ed6\u4e0d\u7740\u3002 \u3000\u3000\u51e4\u5929\u5357\u5927\u9a87\u4e4b\u4e0b\uff0c\u6025\u5fd9\u4f4e\u5934\uff0c\u540c\u65f6\u4f38\u51fa\u4e0b\u62a4\u9888\uff0c\u80e1\u6590\u5de6\u624b\u5728\u4ed6\u5929\u7075\u76d6\u4e0a\u8f7b\u8f7b\u4e00\u62cd\uff0c\u9664\u4e0b\u4ed6\u7684\u5e3d\u5b50\uff0c\u53f3\u4e0b\u5df2\u6293\u4f4f\u4ed6\u7684\u8fab\u5b50\u5c3e\u7aef\uff0c\u53eb\u9053\uff1a\u201c\u8fd9\u4e00\u638c\u6682\u4e0d\u6740\u4f60\uff01\u201d\u5de6\u4e0b\u5df2\u7136\u6293\u4f4f\u8fab\u6839\uff0c\u53cc\u624b\u5411\u5916\u4e00\u5206\uff0c\u8e66\u7684\u4e00\u58f0\uff0c\u4e00\u6761\u8fab\u5b50\u65ad\u6210\u4e86\u4e24\u622a\u3002\u51e4\u5929\u5357\u5413\u5f97\u9762\u5982\u4e0a\u8272\uff0c\u6025\u5fd9\u8dc3\u5f00\uff0c\u80e1\u6590\u53f3\u624b\u4e00\u626c\uff0c\u51e4\u5929\u5357\u7684\u5e3d\u5b50\u98de\u51fa\uff0c\u521a\u597d\u5957\u5728\u77f3\u86c7\u5934\u4e0a\uff0c\u8ddf\u7740\u8e0f\u4e0a\u4e24\u6b65\uff0c\u4e00\u638c\u51fb\u5728\u77f3\u9f9f\u6602\u8d77\u7684\u5934\u9876\uff0c\u7830\u7684\u4e00\u54cd\uff0c\u6c34\u82b1\u56db\u6e85\uff0c\u77f3\u9f9f\u4e4b\u5934\u9f50\u9888\u800c\u65ad\uff0c\u843d\u5165\u6c34\u5858\u3002\u80e1\u6590\u54c8\u54c8\u4e00\u7b11\uff0c\u5c06\u51e4\u5929\u5357\u90a3\u6761\u957f\u8fab\u7ed5\u5728\u77f3\u9f9f\u9888\u4e2d\uff0c\u53cc\u624b\u5f39\u4e00\u5f39\u8eab\u4e0a\u7070\u5c18\uff0c\u7b11\u9053\uff1a\u201d\u8fd8\u6253\u4e48\uff1f\u201d \u3000\u3000\u65c1\u89c2\u4f17\u4eba\u89c1\u4ed6\u663e\u4e86\u8fd9\u624b\u529f\u592b\uff0c\u4eba\u4eba\u8138\u4e0a\u53d8\u8272\u3002\u51e4\u5929\u5357\u77e5\u4ed6\u9002\u624d\u8fd9\u4e00\u638c\u786e\u662f\u4e0b\u4e0b\u7559\u6084\uff0c\u5426\u5219\u4ee5\u638c\u51fb\u77f3\u9f9f\u4e4b\u529b\u51fb\u5728\u8089\u5df1\u5934\u9876\uff0c\u54ea\u91cc\u8fd8\u6709\u547d\u5728\uff1f\u4f46\u65ad\u8fab\u7ed5\u9f9f\uff0c\u98de\u5e3d\u6234\u86c7\uff0c\u5982\u6b64\u7684\u5947\u803b\u5927\u8fb1\u5982\u4f55\u5fcd\u8010\u5f97\u4e86\uff1f\u821e\u52a8\u91d1\u68cd\uff0c\u4e00\u62db\u201c\u9752\u9f99\u5377\u5c3e\u201d\uff0c\u731b\u626b\u800c\u81f3\u3002\u8fd9\u65f6\u4ed6\u5df2\u662f\u6027\u547d\u76f8\u62da\uff0c\u518d\u975e\u4ee5\u638c\u95e8\u4eba\u8eab\u5206\u4e0e\u4eba\u6bd4\u6b66\u8fc7\u62db\u3002 \u3000\u3000\u80e1\u6590\u5fc3\u60f3\uff1a\u201c\u6b64\u4eba\u5e73\u7d20\u6a2a\u5f97\u53ef\u4ee5\uff0c\u4eca\u65e5\u82e5\u4e0d\u626b\u5c3d\u4ed6\u7684\u989c\u9762\uff0c\u4f5b\u5c71\u4e00\u9547\u4e4b\u4eba\u51a4\u6c14\u96be\u51fa\u3002\u201d\u89c1\u4ed6\u91d1\u68cd\u4e0a\u5a01\u529b\u867d\u589e\uff0c\u68cd\u6cd5\u5374\u5df2\u4e0d\u5982\u9002\u624d\u7075\u52a8\uff0c\u7a7a\u624b\u62c6\u4e86\u51e0\u62db\uff0c\u89c1\u4ed6\u4f7f\u4e00\u62db\u201d\u94c1\u725b\u8015\u5730\u201d\uff0c\u7740\u5730\u5377\u5230\uff0c\u5f53\u4e0b\u770b\u51c6\u68cd\u7aef\uff0c\u53f3\u8db3\u4e00\u811a\u8e39\u4e86\u4e0b\u53bb\uff0c\u68cd\u5934\u7740\u5730\uff0c\u7ed9\u4ed6\u8e0f\u5728\u811a\u4e0b\u3002\u51e4\u5929\u5357\u6025\u5fd9\u8fd0\u52b2\u540e\u593a\uff0c\u80e1\u6590\u51fa\u811a\u5947\u5feb\uff0c\u521a\u89c9\u53f3\u811a\u4e0b\u6709\u4e9b\u677e\u52a8\uff0c\u5de6\u8db3\u5df2\u8e0f\u5728\u68cd\u8170\uff0c\u731b\u529b\u4f4f\u4e0b\u4e00\u8e6c\u3002\u51e4\u5929\u5357\u518d\u4e5f\u62ff\u634f\u4e0d\u4f4f\uff0c\u53cc\u624b\u4e00\u677e\uff0c\u68cd\u5c3e\u6b63\u597d\u6253\u4e2d\u4ed6\u53f3\u8db3\u8db3\u80cc\uff0c\u4e24\u6839\u5c0f\u9aa8\u767b\u65f6\u65ad\u6298\u3002 \u3000\u3000\u8fd9\u4e00\u4e0b\u75db\u5f97\u4ed6\u8138\u5982\u91d1\u7eb8\uff0c\u4f46\u4ed6\u54ac\u7d27\u7259\u5173\uff0c\u4e00\u58f0\u4e0d\u54fc\uff0c\u53cc\u624b\u53cd\u5728\u80cc\u540e\uff0c\u521a\u58f0\u8bf4\u9053\uff1a\u201d\u6211\u5b66\u827a\u4e0d\u7cbe\uff0c\u65e0\u8bdd\u53ef\u8bf4\uff0c\u4f60\u8981\u6740\u8981\u5250\uff0c\u6089\u542c\u5c0a\u4fbf\u3002\u201d\u9524\u56db\u5ac2\u5374\u8fd8\u662f\u4e0d\u4f4f\u5411\u4ed6\u78d5\u5934\uff0c\u54ed\u53eb\uff1a\u201c\u591a\u8c22\u51e4\u8001\u7237\u6210\u5168\u4e86\u6211\u5bb6\u5c0f\u4e09\u5b50\uff0c\u4ed6\u771f\u662f\u5077\u5403\u4e86\u4f60\u7684\u9e45\u4e48\uff1f\u201d \u3000\u3000\u80e1\u6590\u89c1\u51e4\u5929\u5357\u8d25\u5f97\u5982\u6b64\u72fc\u72c8\uff0c\u5b9e\u4e0d\u60f3\u518d\u6298\u8fb1\u4e8e\u4ed6\u3002\u4f46\u89c1\u5230\u9524\u56db\u5ac2\u53d1\u75af\u7684\u60e8\u72b6\uff0c\u795e\u575b\u524d\u77f3\u677f\u4e0a\u7684\u8840\u8ff9\uff0c\u5fc3\u601d\u8fd9\u5357\u9738\u5929\u9664\u4e86\u6b64\u4e8b\u4e4b\u5916\uff0c\u8fd9\u8bb8\u591a\u5e74\u6765\u5b9a\u662f\u66f4\u6709\u4e0d\u5c11\u6076\u884c\uff0c\u65e2\u649e\u5728\u6211\u7684\u624b\u91cc\uff0c\u5c82\u80fd\u8f7b\u9976\uff1f\u5f53\u4e0b\u5927\u8e0f\u6b65\u8fc7\u53bb\uff0c\u5c06\u98ce\u4e00\u9e23\u4e00\u628a\u63d0\u8d77\uff0c\u62d4\u8d77\u63d2\u5728\u5730\u4e0b\u7684\u5355\u5200\uff0c\u8f6c\u5934\u5411\u51e4\u5929\u5357\u9053\uff1a\u201c\u51e4\u8001\u7237\uff0c\u6211\u548c\u4f60\u65e0\u51a4\u65e0\u4ec7\uff0c\u53ef\u662f\u4ee4\u90ce\u5077\u5403\u4e86\u6211\u7684\u51e4\u51f0\u8089\uff0c\u5b9e\u5728\u592a\u4e0d\u8bb2\u7406\uff0c\u8fd9\u91cc\u4f5b\u5c71\u9547\u7684\u4eba\u90fd\u62a4\u7740\u4f60\uff0c\u6211\u51a4\u5c48\u96be\u660e\uff0c\u53ea\u597d\u5256\u5f00\u4ee4\u90ce\u7684\u809a\u5b50\uff0c\u8ba9\u5217\u4f4d\u77a7\u77a7\u3002\u201d\u8bf4\u7740\u5200\u950b\u5728\u51e4\u4e00\u9e23\u7684\u809a\u5b50\u4e0a\u8f7b\u8f7b\u4e00\u62d6\uff0c\u96ea\u767d\u7684\u808c\u80a4\u4e0a\u767b\u65f6\u73b0\u51fa\u4e00\u6761\u8840\u75d5\u3002 \u3000\u3000\u51e4\u5929\u5357\u56fa\u7136\u4f5c\u6076\u591a\u7aef\uff0c\u5374\u9887\u6709\u6c5f\u6e56\u6c49\u5b50\u7684\u6c14\u6982\uff0c\u8d25\u5728\u80e1\u6590\u624b\u4e0b\u4e4b\u540e\uff0c\u4ecd\u662f\u5341\u5206\u521a\u786c\uff0c\u4e0d\u5931\u638c\u95e8\u4eba\u7684\u8eab\u5206\uff0c\u4f46\u4e00\u89c1\u72ec\u751f\u7231\u5b50\u8981\u60e8\u88ab\u4ed6\u5f00\u819b\u5256\u8179\uff0c\u4e0d\u7531\u5f97\u5a01\u98ce\u5c3d\u592b\uff0c\u50b2\u6c14\u5168\u6d88\uff0c\u53eb\u9053\uff1a\u201c\u4e14\u6162\uff01\u201d\u4ece\u8eab\u65c1\u624b\u4e0b\u4eba\u624b\u4e2d\uff0c\u62a2\u8fc7\u4e00\u67c4\u5355\u5200\u3002 \u3000\u3000\u80e1\u6590\u7b11\u9053\uff1a\u201d\u4f60\u8fd8\u4e0d\u670d\u6c14\uff0c\u8981\u5f85\u518d\u6253\u4e00\u573a\uff1f\u201d\u51e4\u5929\u5357\u60e8\u7136\u9053\uff1a\u201c\u4e00\u8eab\u505a\u4e8b\u4e00\u8eab\u5f53\uff0c\u51e4\u67d0\u884c\u4e8b\u4e0d\u5f53\uff0c\u60f9\u5f97\u5c0a\u9a7e\u6253\u8fd9\u4e2a\u62b1\u4e0d\u5e73\uff0c\u8fd9\u4e0e\u5c0f\u513f\u53ef\u4e0d\u76f8\u5e72\u3002\u51e4\u67d0\u4e0d\u6562\u518d\u6d3b\uff0c\u4f46\u6c42\u9976\u4e86\u5c0f\u513f\u6027\u547d\u3002\u201d\u8bf4\u7740\u6a2a\u8fc7\u5355\u5200\uff0c\u4fbf\u5f80\u9888\u4e2d\u520e\u53bb\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u5c4b\u6881\u4e0a\u4e00\u4eba\u5927\u53eb\uff1a\u201c\u51e4\u5927\u54e5\uff0c\u4f7f\u4e0d\u5f97\uff01\u201d\u539f\u6765\u90a3\u4e2a\u7c97\u58ee\u5927\u6c49\u5140\u81ea\u53cc\u624b\u62b1\u4f4f\u6a2a\u6881\uff0c\u60ac\u8eab\u534a\u7a7a\u3002 \u3000\u3000\u51e4\u5929\u5357\u8138\u9732\u82e6\u7b11\uff0c\u6325\u5200\u6025\u780d\u3002\u4f17\u4eba\u5927\u5403\u4e00\u60ca\u4e4b\u4e0b\uff0c\u8c01\u4e5f\u4e0d\u6562\u963b\u62e6\uff0c\u773c\u89c1\u4ed6\u5355\u5200\u6a2a\u9888\uff0c\u7acb\u65f6\u8981\u8840\u6e85\u5f53\u573a\u3001\u5c38\u6a2a\u7956\u5e99\uff0c\u5ffd\u542c\u5f97\u55e4\u55e4\u58f0\u54cd\uff0c\u4e00\u4ef6\u6697\u5668\u4ece\u6bbf\u95e8\u5916\u81ea\u9ad8\u800c\u4e0b\u7684\u98de\u5c04\u8fc7\u6765\uff0c\u94ee\u7684\u4e00\u58f0\uff0c\u5728\u5355\u5200\u4e0a\u4e00\u78b0\u3002\u51e4\u5929\u5357\u624b\u4e00\u8361\uff0c\u5355\u5200\u7acb\u65f6\u6b6a\u4e86\uff0c\u4f46\u8fd8\u662f\u5728\u5de6\u80a9\u4e0a\u5212\u4e86\u4e00\u9053\u53e3\u5b50\uff0c\u9c9c\u8840\u8ff8\u6d41\u3002 \u3000\u3000\u80e1\u6590\u5b9a\u775b\u4e00\u770b\uff0c\u53ea\u89c1\u5c04\u4e0b\u7684\u6697\u5668\u5374\u662f\u4e00\u679a\u5973\u5b50\u624b\u4e0a\u6240\u6234\u7684\u6307\u73af\u3002\u51e4\u5929\u5357\u8182\u529b\u751a\u5f3a\uff0c\u8fd9\u5c0f\u5c0f\u4e00\u679a\u9996\u9970\uff0c\u5c45\u7136\u80fd\u5c06\u4ed6\u624b\u4e2d\u5e74\u5200\u8361\u5f00\uff0c\u90a3\u6295\u63b7\u6307\u73af\u4e4b\u4eba\u7684\u6b66\u529f\uff0c\u53ea\u6015\u4e0d\u5728\u81ea\u5df1\u4e4b\u4e0b\u3002\u4ed6\u5fc3\u4e2d\u60ca\u8be7\uff0c\u7eb5\u8eab\u62a2\u5230\u5929\u4e95\uff0c\u8dc3\u4e0a\u5c4b\u9876\uff0c\u4f46\u89c1\u897f\u5357\u89d2\u4e0a\u4eba\u5f71\u4e00\u95ea\uff0c\u500f\u5ffd\u95f4\u5931\u4e86\u8e2a\u8ff9\uff0c\u80e1\u6590\u53f3\u8db3\u4e00\u70b9\uff0c\u6251\u4e86\u8fc7\u53bb\uff0c\u66ae\u8272\u82cd\u832b\u4e4b\u4e2d\uff0c\u56db\u987e\u6084\u7136\uff0c\u7adf\u65e0\u4eba\u5f71\uff0c\u4ed6\u5fc3\u4e2d\u5600\u5495\uff1a\u201d\u8fd9\u80cc\u5f71\u5c0f\u5de7\u82d7\u6761\uff0c\u4f3c\u662f\u5973\u5b50\u6a21\u6837\uff0c\u96be\u9053\u4e16\u95f4\u5973\u5b50\u4e4b\u4e2d\uff0c\u7adf\u6709\u8fd9\u7b49\u9ad8\u624b\uff1f\u201d \u3000\u3000\u4ed6\u751f\u6015\u51e4\u5929\u5357\u7236\u5b50\u9003\u8d70\uff0c\u4e0d\u6562\u5728\u5c4b\u9876\u4e45\u803d\uff0c\u968f\u5373\u8f6c\u8eab\u56de\u6bbf\uff0c\u53ea\u89c1\u51e4\u5929\u5357\u7236\u5b50\u6402\u62b1\u5728\u4e00\u8d77\u3002\u51e4\u5929\u5357\u8138\u4e0a\u8001\u6cea\u7eb5\u6a2a\uff0c\u4e5f\u4e0d\u77e5\u662f\u7231\u662f\u601c\uff0c\u662f\u75db\u662f\u6094\uff1f \u3000\u3000\u80e1\u6590\u89c1\u4e86\u8fd9\u526f\u60c5\u666f\uff0c\u5012\u8d77\u4e86\u9976\u6055\u4ed6\u7236\u5b50\u4e4b\u610f\u3002\u51e4\u5929\u5357\u653e\u8131\u513f\u5b50\uff0c\u8d70\u5230\u80e1\u6590\u8ddf\u524d\uff0c\u6251\u5730\u8dea\u4e0b\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u8fd9\u6761\u8001\u547d\u4ea4\u5728\u4f60\u624b\u91cc\uff0c\u4f46\u671b\u9ad8\u62ac\u8d35\u4e0b\uff0c\u9976\u4e86\u6211\u513f\u5b50\u6027\u547d\u3002\u201d\u51e4\u4e00\u9e23\u62a2\u4e0a\u6765\u8bf4\u9053\uff1a\u201d\u4e0d\uff0c\u4e0d\uff01\u4f60\u6740\u6211\u597d\u4e86\u3002\u4f60\u8981\u66ff\u59d3\u9524\u7684\u62a5\u4ec7\uff0c\u5256\u6211\u809a\u5b50\u4fbf\u662f\u3002\u201d \u3000\u3000\u80e1\u6590\u4e00\u65f6\u5012\u4e0d\u77e5\u5982\u4f55\u53d1\u843d\uff0c\u82e5\u8981\u6740\u4e86\u4e8c\u4eba\uff0c\u6709\u4e9b\u4e0d\u5fcd\u4e0b\u624b\uff0c\u5018\u662f\u7ed9\u4ed6\u7236\u5b50\u4fe9\u4e00\u54ed\u4e00\u8dea\uff0c\u4fbf\u5373\u9976\u6055\uff0c\u53c8\u672a\u514d\u592a\u4fbf\u5b9c\u4e86\u4ed6\u4eec\u3002\u6b63\u81ea\u8e0c\u8e87\uff0c\u9524\u963f\u56db\u7a81\u7136\u8d70\u4e0a\u524d\u6765\uff0c\u5411\u80e1\u6590\u9053\uff1a\u201c\u597d\u6c49\u7237\u6551\u4e86\u5c0f\u4eba\u7684\u59bb\u513f\uff0c\u53c8\u66ff\u5c0f\u4eba\u4e00\u5bb6\u660e\u51a4\u96ea\u6068\uff0c\u5927\u6069\u5927\u5fb7\uff0c\u5c0f\u4eba\u7c89\u8eab\u96be\u62a5\u3002\u201d\u4e00\u9762\u8bf4\uff0c\u4e00\u9762\u6251\u7ffb\u5728\u5730\uff0c\u51ac\u51ac\u51ac\u51ac\uff0c\u78d5\u4e86\u51e0\u4e2a\u54cd\u5934\uff0c\u80e1\u6590\u8fde\u5fd9\u6276\u8d77\u3002 \u3000\u3000\u9524\u963f\u56db\u8f6c\u8fc7\u8eab\u6765\uff0c\u8138\u8272\u94c1\u9752\uff0c\u671b\u7740\u51e4\u5929\u5357\u9053\uff1a\u201c\u51e4\u8001\u7237\uff0c\u4eca\u65e5\u5728\u5317\u5e1d\u7237\u7237\u795e\u524d\uff0c\u4f60\u51ed\u826f\u5fc3\u8bf4\u4e00\u53e5\uff0c\u6211\u5bb6\u5c0f\u4e09\u5b50\u6709\u6ca1\u5077\u4f60\u7684\u9e45\u5403\uff1f\u201d\u51e4\u5929\u5357\u4e3a\u80e1\u6590\u7684\u5a01\u52bf\u6240\u6151\uff0c\u4f4e\u5934\u9053\uff1a\u201c\u6ca1\u6709\u3002\u662f\u2026\u2026\u662f\u6211\u5f04\u9519\u4e86\u3002\u201d\u9524\u963f\u56db\u53c8\u9053\uff1a\u201d\u51e4\u8001\u7237\uff0c\u4f60\u518d\u51ed\u826f\u5fc3\u8bf4\uff0c\u4f60\u53eb\u5b98\u5e9c\u6253\u6211\u5173\u6211\uff0c\u903c\u6b7b\u6211\u7684\u513f\u5b50\uff0c\u5168\u662f\u4e3a\u4e86\u8981\u5360\u6211\u7684\u83dc\u56ed\uff0c\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u98ce\u5929\u5357\u5411\u4ed6\u8138\u4e0a\u671b\u4e86\u4e00\u773c\uff0c\u53ea\u89c1\u8fd9\u4e2a\u5e73\u65f6\u5fe0\u539a\u8001\u5b9e\u7684\u83dc\u519c\uff0c\u54ac\u7d27\u7259\u5173\uff0c\u76ee\u55b7\u6012\u706b\uff0c\u795e\u60c5\u6781\u662f\u53ef\u6015\uff0c\u4e0d\u7531\u5f97\u4f4e\u4e0b\u4e86\u5934\uff0c\u4e0d\u6562\u56de\u7b54\uff0c\u9524\u963f\u56db\u9053\uff1a\u201c\u4f60\u5feb\u8bf4\uff0c\u662f\u4e5f\u4e0d\u662f\uff1f\u201d\u51e4\u5929\u5357\u62ac\u8d77\u5934\u6765\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6740\u4eba\u507f\u547d\uff0c\u4f60\u6740\u6211\u4fbf\u4e86\u3002\u201d \u3000\u3000\u5ffd\u542c\u5e99\u95e8\u5916\u4e00\u4eba\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u81ea\u79f0\u62d4\u51e4\u6bdb\u7684\u5c0f\u8d3c\uff0c\u4f60\u6562\u4e0d\u6562\u51fa\u6765\u6597\u4e09\u767e\u56de\u5408\uff1f\u4f60\u5728\u5317\u5e1d\u5e99\u4e2d\u7f29\u5934\u7f29\u9888\uff0c\u5e72\u4e48\u4e0d\u6562\u51fa\u6765\u554a\uff1f\u201d\u8fd9\u51e0\u53e5\u8bdd\u6781\u662f\u54cd\u4eae\uff0c\u5927\u6bbf\u4e0a\u4eba\u4eba\u76f8\u987e\u6115\u7136\uff0c\u542c\u90a3\u58f0\u97f3\u7c97\u9c81\u91cd\u6d4a\uff0c\u6ee1\u662f\u65e0\u8d56\u5730\u75de\u7684\u53e3\u6c14\u3002 \u3000\u3000\u80e1\u6590\u4e00\u6014\u4e4b\u4e0b\uff0c\u62a2\u51fa\u5e99\u95e8\uff0c\u53ea\u89c1\u524d\u9762\u4e09\u9a91\u9a6c\u5411\u897f\u6025\u9a70\uff0c\u9a6c\u4e0a\u4e00\u4eba\u56de\u5934\u53eb\u9053\uff1a\u201c\u7f29\u5934\u4e4c\u9f9f\uff0c\u6599\u4f60\u4e5f\u4e0d\u6562\u548c\u8001\u5b50\u52a8\u624b\u3002\u201d\u80e1\u6590\u5927\u6012\uff0c\u89c1\u5e99\u95e8\u65c1\u4e00\u682a\u5927\u7ea2\u68c9\u6811\u4e0b\u7cfb\u7740\u4e24\u5339\u9a6c\uff0c\u7eb5\u8eab\u8fc7\u53bb\u4e00\u8dc3\u4e0a\u9a6c\uff0c\u62c9\u65ad\u50f5\u7ef3\uff0c\u53cc\u817f\u4e00\u5939\uff0c\u50ac\u52a8\u5750\u9a91\uff0c\u5411\u90a3\u4e09\u4eba\u6025\u8ffd\u4e0b\u53bb\u3002 \u3000\u3000\u8fdc\u8fdc\u671b\u89c1\u4e09\u4e58\u9a6c\u5411\u897f\u6cbf\u7740\u6cb3\u5cb8\u6025\u5954\uff0c\u77a7\u90a3\u4e09\u4eba\u5750\u5728\u9a6c\u80cc\u4e0a\u7684\u59ff\u5f0f\uff0c\u624b\u811a\u7b28\u62d9\uff0c\u9a91\u672f\u66f4\u52a3\uff0c\u4e0d\u77e5\u662f\u5426\u6709\u610f\u505a\u4f5c\uff0c\u4f46\u80ef\u4e0b\u6240\u4e58\u5374\u662f\u826f\u9a6c\uff0c\u80e1\u6590\u8d76\u51fa\u91cc\u8bb8\uff0c\u59cb\u7ec8\u6ca1\u80fd\u8ffd\u4e0a\u3002\u542c\u90a3\u4e09\u4eba\u4e0d\u65f6\u9ad8\u58f0\u53eb\u9a82\uff0c\u8086\u65e0\u5fcc\u60ee\uff0c\u5bf9\u81ea\u5df1\u6beb\u4e0d\u754f\u60e7\uff0c\u5b9e\u4f3c\u80cc\u540e\u6709\u6781\u5389\u5bb3\u4e4b\u4eba\u6491\u8170\uff0c\u4ed6\u7126\u8e81\u8d77\u6765\uff0c\u4fef\u8eab\u5728\u5730\u4e0b\u6293\u8d77\u51e0\u5757\u77f3\u5b50\uff0c\u624b\u8155\u6296\u5904\uff0c\u4e94\u516d\u5757\u77f3\u5b50\u98de\u4e86\u51fa\u53bb\uff0c\u53ea\u542c\u5f97\u201c\u554a\u54df\u201d\u201c\u5988\u5440\u201d\u4e4b\u58f0\u4e0d\u7edd\uff0c\u4e09\u4e2a\u6c49\u5b50\u540c\u65f6\u6253\u4e2d\uff0c\u4e00\u9f50\u6454\u4e0b\u9a6c\u6765\u3002 \u3000\u3000\u4e24\u4e2a\u4eba\u4e00\u8dcc\u4e0b\u6765\uff0c\u8db4\u5728\u5730\u4e0a\u5927\u53eb\uff0c\u7b2c\u4e09\u4eba\u5374\u5de6\u8db3\u5957\u5728\u9a6c\u956b\u4e4b\u4e2d\uff0c\u88ab\u9a6c\u62d6\u7740\u76f4\u5954\uff0c\u970e\u65f6\u4e4b\u95f4\u5df2\u8f6c\u5165\u67f3\u836b\u6df1\u5904\u3002 \u3000\u3000\u80e1\u6590\u8df3\u4e0b\u9a6c\u6765\uff0c\u53ea\u89c1\u90a3\u4e8c\u4eba\u6309\u4f4f\u8170\u81c0\uff0c\u54fc\u54fc\u5527\u5527\u7684\u53eb\u75db\u3002\u80e1\u6590\u5728\u4e00\u4eba\u8eab\u4e0a\u8e22\u4e86\u4e00\u811a\uff0c\u559d\u9053\uff1a\u201c\u4f60\u8bf4\u8981\u548c\u6211\u6597\u4e09\u767e\u56de\u5408\uff0c\u600e\u4e0d\u8d77\u8eab\u6765\u6597\uff1f\u201d\u90a3\u4eba\u722c\u8d77\u8eab\u6765\uff0c\u8bf4\u9053\uff1a\u201d\u6b20\u4e86\u8d4c\u503a\u4e0d\u8fd8\uff0c\u8fd8\u8fd9\u4e48\u6a2a\uff01\u603b\u6709\u4e00\u65e5\u51e4\u8001\u7237\u4eb2\u81ea\u6536\u62fe\u4f60\u3002\u201d \u3000\u3000\u80e1\u6590\u4e00\u6014\uff0c\u95ee\u9053\uff1a\u201c\u8c01\u6b20\u4e86\u8d4c\u503a\u4e0d\u8fd8\uff1f\u201d \u3000\u3000\u53e6\u4e00\u4eba\u731b\u5730\u91cc\u8df3\u5c06\u8d77\u6765\uff0c\u8fce\u9762\u4e00\u62f3\u5f80\u80e1\u6590\u51fb\u53bb\u3002\u8fd9\u4e00\u62f3\u867d\u6709\u51e0\u65a4\u86ee\u529b\uff0c\u4f46\u51fa\u62f3\u4e0d\u6210\u7ae0\u6cd5\uff0c\u663e\u662f\u5168\u65e0\u6b66\u529f\u3002\u80e1\u6590\u5fae\u5fae\u4e00\u7b11\uff0c\u6325\u624b\u8f7b\u5e26\u3002\u90a3\u4eba\u4e00\u62f3\u6253\u504f\uff0c\u7830\u7684\u4e00\u58f0\uff0c\u6b63\u597d\u6253\u4e2d\u540c\u4f34\u7684\u9f3b\u5b50\uff0c\u767b\u65f6\u9f3b\u8840\u957f\u6d41\u3002\u51fa\u62f3\u4e4b\u4eba\u5413\u4e86\u4e00\u8df3\uff0c\u4e0d\u660e\u767d\u600e\u5730\u8fd9\u4e00\u62f3\u53bb\u52bf\u5168\u7136\u4e0d\u5bf9\uff0c\u53ea\u629a\u7740\u62f3\u5934\u53d1\u5446\u3002\u88ab\u51fb\u4e4b\u4eba\u5927\u6012\uff0c\u559d\u9053\uff1a\u201d\u72d7\u5a18\u517b\u7684\uff0c\u6253\u8d77\u8001\u5b50\u6765\u5566\uff01\u201d\u98de\u8d77\u4e00\u817f\uff0c\u8e22\u5728\u4ed6\u7684\u8170\u91cc\u3002\u90a3\u4eba\u56de\u5b50\u76f8\u6bb4\uff0c\u7830\u7830\u562d\u562d\uff0c\u767b\u65f6\u6253\u5f97\u5341\u5206\u70ed\u95f9\uff0c\u4e0d\u518d\u7406\u4f1a\u80e1\u6590\u3002 \u3000\u3000\u80e1\u6590\u89c1\u8fd9\u4e8c\u4eba\u786e\u5b9e\u4e0d\u4f1a\u6b66\u529f\uff0c\u5c45\u7136\u6562\u5411\u81ea\u5df1\u53eb\u9635\uff0c\u5176\u4e2d\u5927\u6709\u8e4a\u8df7\uff0c\u53cc\u624b\u5206\u522b\u6293\u4f4f\u4e24\u4eba\u5934\u9888\uff0c\u5f80\u540e\u4e00\u626f\uff0c\u5c06\u4e24\u4eba\u5206\u4e86\u5f00\u6765\u3002\u4f46\u4e24\u4eba\u6253\u5f97\u773c\u7ea2\u4e86\uff0c\u4e0d\u4f4f\u53e3\u7684\u6c61\u8a00\u79fd\u8bed\u4e92\u76f8\u8fb1\u9a82\uff0c\u4e00\u4e2a\u9a82\u5bf9\u65b9\u4e13\u5077\u4eba\u5bb6\u841d\u535c\uff0c\u53e6\u4e00\u4e2a\u8bf4\u5bf9\u65b9\u662f\u4f5b\u5c71\u7684\u5077\u9e21\u597d\u624b\uff0c\u770b\u6765\u4e24\u4eba\u90fd\u662f\u5e02\u4e95\u65e0\u8d56\uff0c\u5fc3\u4e2d\u8d8a\u52a0\u8d77\u7591\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u8c01\u53eb\u4f60\u4eec\u6765\u9a82\u6211\u7684\uff1f\u201d\u8bf4\u6614\u53cc\u624b\u4e00\u6446\uff0c\u7830\u7684\u4e00\u4e0b\uff0c\u5c06\u4e24\u4eba\u989d\u89d2\u5bf9\u989d\u89d2\u7684\u4e00\u649e\uff0c\u767b\u65f6\u53d8\u4e86\u4e24\u6761\u6012\u76ee\u76f8\u5411\u7684\u72ec\u89d2\u9f99\u3002 \u3000\u3000\u90a3\u5077\u9e21\u8d3c\u80c6\u5b50\u6781\u5c0f\uff0c\u4e00\u5403\u5230\u82e6\u5934\uff0c\u8fde\u58f0\uff1a\u201c\u7237\u7237\uff0c\u516c\u516c\uff0c\u6211\u662f\u4f60\u8001\u4eba\u5bb6\u7684\u7070\u5b59\u5b50\u3002\u201d\u80e1\u6590\u559d\u9053\uff1a\u201c\u5478\uff0c\u6211\u6709\u4f60\u8fd9\u7b49\u8d31\u5b59\u5b50\uff1f\u5feb\u8bf4\u3002\u201d\u90a3\u5077\u9e21\u8d3c\u9053\uff1a\u201d \u3000\u3000\u82f1\u96c4\u4f1a\u9986\u5f00\u5b9d\u7684\u909d\u5b9d\u5b98\u8bf4\uff0c\u4f60\u6b20\u4e86\u4f1a\u9986\u91cc\u7684\u8d4c\u503a\u4e0d\u8fd8\uff0c\u53eb\u6211\u4eec\u4e09\u4e2a\u5f15\u4f60\u51fa\u6765\u6253\u4e00\u987f\u3002\u4ed6\u7ed9\u4e86\u6211\u4eec\u6bcf\u4eba\u4e94\u94b1\u94f6\u5b50\uff0c\u8fd9\u5750\u9a91\u4e5f\u662f\u4ed6\u501f\u7684\u3002\u4f60\u8d4c\u503a\u8fd8\u4e0d\u8fd8\uff0c\u4e0d\u5173\u6211\u4e8b\u2026\u2026\u201d \u3000\u3000\u80e1\u6590\u542c\u5230\u8fd9\u5904\uff0c\u201c\u554a\u201d\u7684\u4e00\u58f0\u5927\u53eb\uff0c\u5fc3\u9053\uff1a\u201c\u7cdf\u5566\uff0c\u7cdf\u5566\uff01\u6211\u6041\u5730\u80e1\u6d82\uff0c\u7adf\u4e2d\u4e86\u654c\u4eba\u8c03\u864e\u79bb\u5c71\u4e4b\u8ba1\u3002\u201d\u53cc\u624b\u5f80\u5916\u4e00\u9001\uff0c\u5c06\u4e24\u540d\u65e0\u8d56\u53cc\u53cc\u8dcc\u4e86\u4e2a\u72d7\u5403\u5c4e\uff0c\u98de\u8eab\u4e0a\u9a6c\u80cc\uff0c\u6025\u5f80\u6765\u8def\u9a70\u56de\uff0c\u5fc3\u60f3\uff1a\u201c\u51e4\u5929\u5357\u7236\u5b50\u5b9a\u7136\u8eb2\u4e86\u8d77\u6765\uff0c\u504c\u5927\u4e00\u5ea7\u4f5b\u5c71\u9547\uff0c\u6211\u5374\u5f80\u54ea\u91cc\u627e\u53bb\uff1f\u597d\u5728\u4ed6\u641c\u522e\u9738\u5360\u7684\u4ea7\u4e1a\u751a\u591a\uff0c\u6211\u4e00\u5904\u5904\u7684\u95f9\u5c06\u8fc7\u53bb\uff0c\u6405\u4ed6\u4e2a\u5929\u7ffb\u5730\u8986\uff0c\u77a7\u4ed6\u8eb2\u5f97\u5230\u51e0\u65f6\uff1f\u201d \u3000\u3000\u4e0d\u591a\u65f6\u5df2\u56de\u5230\u5317\u5e1d\u5e99\u524d\uff0c\u5e99\u5916\u672c\u6709\u8bb8\u591a\u4eba\u56f4\u7740\u77a7\u70ed\u95f9\uff0c\u8fd9\u65f6\u5df2\u8d70\u5f97\u5e72\u5e72\u51c0\u51c0\uff0c\u8fde\u5b69\u5b50\u4e5f\u6ca1\u7559\u4e0b\u4e00\u4e2a\u3002\u80e1\u6590\u5fc3\u60f3\uff1a\u201c\u90a3\u51e4\u5929\u5357\u679c\u7136\u8d70\u4e86\u3002\u201d\u7ffb\u8eab\u4e0b\u9a6c\uff0c\u5927\u8e0f\u6b65\u8d70\u5411\u5e99\u4e2d\uff0c\u4e00\u6b65\u8de8\u8fdb\u5927\u6bbf\uff0c\u4e0d\u7531\u5f97\u5012\u62bd\u4e00\u53e3\u51c9\u6c14\uff0c\u80f8\u53e3\u547c\u5438\u767b\u65f6\u51dd\u4f4f\uff0c\u53ea\u5413\u5f97\u8eab\u5b50\u6447\u6447\u6446\u6446\uff0c\u9669\u4e9b\u8981\u5750\u5012\u5728\u5730\u3002 \u3000\u3000\u539f\u6765\u5317\u5e1d\u5e99\u5927\u6bbf\u4e0a\u6ee1\u5730\u9c9c\u8840\uff0c\u8840\u6cca\u4e2d\u4e09\u5177\u5c38\u8eab\uff0c\u6b63\u662f\u9524\u963f\u56db\u3001\u9524\u56db\u5ac2\u3001\u9524\u5c0f\u4e8c\u4e09\u4eba\u3002\u6bcf\u4eba\u8eab\u4e0a\u90fd\u662f\u4e71\u5200\u780d\u65a9\u7684\u4f24\u53e3\uff0c\u8840\u8089\u6a21\u7cca\uff0c\u60e8\u4e0d\u5fcd\u7779\u3002 \u3000\u3000\u80e1\u6590\u5446\u4e86\u534a\u664c\uff0c\u4e00\u80a1\u70ed\u8840\u4ece\u80f8\u95f4\u76f4\u51b2\u4e0a\u6765\uff0c\u7981\u4e0d\u4f4f\u4f0f\u5728\u5927\u6bbf\u5730\u4e0a\uff0c\u653e\u58f0\u5927\u54ed\uff0c\u53eb\u9053\uff1a\u201c\u9524\u56db\u54e5\u56db\u5ac2\uff0c\u9524\u5bb6\u5144\u5f1f\uff0c\u662f\u6211\u80e1\u6590\u65e0\u80fd\uff0c\u7adf\u7136\u5bb3\u4e86\u4f60\u4eec\u6027\u547d\u3002\u201d \u3000\u3000\u53ea\u89c1\u4e09\u4eba\u867d\u6b7b\uff0c\u773c\u775b\u4e0d\u95ed\uff0c\u8138\u4e0a\u5145\u6ee1\u6124\u6012\u4e4b\u8272\uff0c\u4ed6\u7ad9\u8d77\u8eab\u6765\uff0c\u6307\u7740\u5317\u5e1d\u795e\u50cf\u8bf4\u9053\uff1a\u201c\u5317\u5e1d\u7237\u7237\uff0c\u4eca\u65e5\u8981\u4f60\u4f5c\u4e2a\u89c1\u8bc1\uff0c\u6211\u80e1\u6590\u82e5\u4e0d\u6740\u51e4\u5929\u5357\u7236\u5b50\u7ed9\u9524\u5bb6\u6ee1\u95e8\u62a5\u4ec7\uff0c\u6211\u56de\u6765\u5728\u4f60\u5ea7\u524d\u81ea\u520e\u3002\u201d\u8bf4\u7740\u7830\u7684\u4e00\u638c\uff0c\u5c06\u795e\u6848\u4e00\u89d2\u6253\u5f97\u7c89\u788e\uff0c\u6848\u4e0a\u4f9b\u5949\u7684\u9999\u7089\u70db\u53f0\u90e8\u9707\u5728\u5730\u4e0b\u3002 \u3000\u3000\u4ed6\u5b9a\u795e\u4e00\u60f3\uff0c\u5230\u5e99\u95e8\u5916\u7275\u8fdb\u9a6c\u5339\uff0c\u5c06\u4e09\u5177\u5c38\u8eab\u90fd\u653e\u4e0a\u9a6c\u80cc\uff0c\u5fc3\u4e2d\u6094\u6068\u4e0d\u5df2\uff1a\u201c\u6211\u5e74\u5e7c\u65e0\u77e5\uff0c\u4e0d\u660e\u6c5f\u6e56\u4e0a\u7684\u9b3c\u872e\u4f0e\u4fe9\uff0c\u5374\u6765\u51fa\u5934\u6253\u62b1\u4e0d\u5e73\uff0c\u6789\u81ea\u53c8\u5bb3\u4e86\u4e09\u6761\u4eba\u547d\u3002\u90a3\u59d3\u51e4\u7684\u5bb6\u4e2d\u4fbf\u662f\u5e03\u6ee1\u4e86\u5200\u5c71\u6cb9\u9505\uff0c\u4eca\u65e5\u4e5f\u8981\u95ef\u8fdb\u53bb\u6740\u4ed6\u4e2a\u843d\u82b1\u6d41\u6c34\u3002\u201d\u5f53\u4e0b\u7275\u4e86\u9a6c\u5339\uff0c\u5f80\u5927\u8857\u800c\u6765\u3002 \u3000\u3000\u4eec\u89c1\u5bb6\u5bb6\u5e97\u94fa\u90e8\u5173\u4e0a\u4e86\u5927\u95e8\uff0c\u8857\u4e0a\u9759\u6084\u6084\u7684\u7adf\u65e0\u4e00\u4e2a\u4eba\u5f71\uff0c\u53ea\u542c\u5f97\u9a6c\u8e44\u5f97\u5f97\uff0c\u5728\u77f3\u677f\u8def\u4e0a\u4e00\u8def\u54cd\u5c06\u8fc7\u53bb\u3002 \u3000\u3000\u80e1\u6590\u6765\u5230\u82f1\u96c4\u5f53\u94fa\u548c\u82f1\u96c4\u9152\u697c\uff0c\u9010\u4e00\u8e22\u5f00\u5927\u95e8\uff0c\u5747\u662f\u5bc2\u7136\u65e0\u4eba\uff0c\u4f3c\u4e4e\u970e\u65f6\u4e4b\u95f4\uff0c\u4f5b\u5c71\u9547\u4e0a\u6570\u4e07\u4eba\u5ffd\u5730\u5c3d\u6570\u6d88\u5931\uff0c\u53ea\u662f\u5f53\u94fa\u4e0e\u9152\u697c\u5404\u5904\u5806\u6ee1\u67f4\u8349\uff0c\u4e0d\u77e5\u662f\u4f55\u7528\u610f\u3002\u518d\u53bb\u8d4c\u573a\uff0c\u4e5f\u662f\u4e00\u4e2a\u4eba\u4e5f\u6ca1\u6709\uff0c\u6210\u4e07\u4e24\u94f6\u5b50\u5374\u5140\u81ea\u653e\u5728\u95e8\u677f\u4e4b\u4e0a\uff0c\u6ca1\u4e00\u4eba\u6562\u52a8\u3002 \u3000\u3000\u80e1\u6590\u968f\u4e0b\u53d6\u4e86\u51e0\u767e\u4e24\u653e\u5165\u5305\u88b1\uff0c\u5fc3\u4e2d\u6697\u6697\u60ca\u8bb6\uff1a\u201c\u8fd9\u51e4\u5929\u5357\u5b9a\u7136\u6446\u4e0b\u9b3c\u8ba1\uff0c\u5bf9\u4ed8\u4e8e\u6211\uff0c\u5f7c\u4f17\u6211\u5be1\uff0c\u83ab\u8981\u518d\u4e0a\u4ed6\u7684\u5f53\u3002\u201d \u3000\u3000\u4ed6\u6b65\u6b65\u7559\u795e\uff0c\u6cbf\u8857\u8d70\u53bb\uff0c\u8f6c\u4e86\u51e0\u4e2a\u5f2f\uff0c\u53ea\u89c1\u4e00\u5ea7\u767d\u5899\u9ed1\u74e6\u7684\u5927\u5b85\u7b2c\uff0c\u95e8\u4e0a\u60ac\u7740\u4e00\u9762\u5927\u533e\uff0c\u5199\u7740\u201c\u5357\u6d77\u51e4\u7b2c\u201d\u56db\u4e2a\u5927\u5b57\u3002\u90a3\u5b85\u7b2c\u4e00\u8fde\u4e94\u8fdb\uff0c\u6c14\u8c61\u5b8f\u4f1f\u3002 \u3000\u3000\u5927\u95e8\u3001\u4e2d\u95e8\u4e00\u6247\u6247\u90fd\u5927\u5f00\u7740\uff0c\u5b85\u4e2d\u7a7a\u7a7a\u8361\u8361\u7684\u4f3c\u4e4e\u4e5f\u65e0\u4e00\u4eba\u3002\u80e1\u6590\u5fc3\u9053\uff1a\u201c\u5c31\u7b97\u4f60\u673a\u5173\u4e07\u5343\uff0c\u6211\u4e00\u628a\u706b\u70e7\u4e86\u4f60\u7684\u9f9f\u6d1e\uff0c\u77a7\u4f60\u51fa\u4e0d\u51fa\u6765\u3002\u201d\u6b63\u8981\u53bb\u89c5\u67f4\u8349\u653e\u706b\uff0c\u5ffd\u89c1\u5c4b\u5b50\u540e\u8fdb\u548c\u4e24\u4fa7\u90e8\u6709\u70df\u706b\u5192\u5c06\u4e0a\u6765\uff0c\u4e00\u6014\u4e4b\u95f4\uff0c\u5df2\u660e\u5176\u7406\uff1a\u201c\u8fd9\u51e4\u5929\u5357\u597d\u5389\u5bb3\u7684\u624b\u6bb5\uff0c\u7adf\u7136\u820d\u5374\u5bb6\u4e1a\u4e0d\u8981\uff0c\u81ea\u5df1\u4e00\u628a\u706b\u70e7\u4e2a\u5e72\u51c0\u3002\u5982\u6b64\u770b\u6765\uff0c\u4ed6\u5b9a\u8981\u9ad8\u98de\u8fdc\u8d70\u3002\u82e5\u4e0d\u6025\u901f\u8ffd\u8d76\uff0c\u53ea\u6015\u7ed9\u4ed6\u8eb2\u5f97\u65e0\u5f71\u65e0\u8e2a\u3002\u201d \u3000\u3000\u4e8e\u662f\u5c06\u9a6c\u5339\u7275\u5230\u51e4\u5b85\u65c1\u9524\u5bb6\u83dc\u56ed\uff0c\u627e\u4e86\u4e00\u67c4\u9504\u5934\uff0c\u5c06\u9524\u963f\u56db\u592b\u5987\u7236\u5b50\u4e09\u4eba\u846c\u4e86\u3002\u53ea\u89c1\u83dc\u56ed\u4e2d\u841d\u535c\u767d\u83dc\u957f\u5f97\u751a\u4e3a\u80a5\u7f8e\uff0c\u83dc\u7566\u65c1\u4e22\u7740\u4e00\u9876\u5c0f\u5b69\u5e3d\u5b50\uff0c\u4e00\u4e2a\u7c97\u9676\u5a03\u5a03\u3002\u80e1\u6590\u8d8a\u770b\u8d8a\u662f\u4f24\u5fc3\u607c\u6012\uff0c\u4f0f\u5730\u62dc\u4e86\u51e0\u62dc\uff0c\u6697\u6697\u795d\u7977\uff1a\u201c\u9524\u5bb6\u5144\u5ac2\uff0c\u4f60\u82e5\u5728\u5929\u6709\u7075\uff0c\u52a1\u987b\u52a9\u6211\uff0c\u4e0d\u80fd\u8ba9\u90a3\u51f6\u624b\u8d70\u8131\u4e86\u201d \u3000\u3000\u5ffd\u542c\u5f97\u8857\u4e0a\u811a\u6b65\u58f0\u54cd\uff0c\u6570\u5341\u4eba\u9f50\u58f0\u5450\u558a\uff1a\u201c\u6349\u62ff\u6740\u4eba\u653e\u706b\u7684\u51f6\u624b\uff01\u201d\u201c\u83ab\u8d70\u4e86\u65e0\u6cd5\u65e0\u5929\u7684\u6c5f\u6d0b\u5927\u76d7\uff01\u201d\u201c\u90a3\u5c0f\u5f3a\u76d7\u4fbf\u5728\u8fd9\u91cc\u3002\u201d\u80e1\u6590\u7ed5\u5230\u4e00\u682a\u5927\u6811\u4e4b\u540e\uff0c\u5411\u5916\u4e00\u5f20\uff0c\u53ea\u89c1\u4e8c\u4e09\u5341\u540d\u8859\u5f79\u5175\u4e01\uff0c\u624b\u6267\u5f13\u7bad\u5200\u67aa\u3001\u94c1\u5c3a\u94c1\u94fe\uff0c\u5728\u51e4\u5b85\u5916\u865a\u5f20\u58f0\u52bf\u5730\u53eb\u558a\u3002\u4ed6\u51dd\u795e\u4e00\u770b\uff0c\u4eba\u7fa4\u4e2d\u5e76\u65e0\u98ce\u5bb6\u7236\u5b50\u5728\u5185\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u51e4\u5929\u5357\u60ca\u52a8\u5b98\u5e9c\uff0c\u660e\u77e5\u62ff\u6211\u4e0d\u4f4f\uff0c\u5374\u662f\u8981\u6321\u6211\u4e00\u9635\u3002\u201d\u5f53\u4e0b\u7eb5\u8eab\u4e0a\u9a6c\uff0c\u5411\u8352\u50fb\u5904\u75be\u9a70\u800c\u53bb\u3002 \u3000\u3000\u51fa\u5f97\u9547\u6765\uff0c\u56de\u5934\u671b\u65f6\uff0c\u53ea\u89c1\u51e4\u5b85\u7684\u706b\u7130\u8d8a\u7a9c\u8d8a\u9ad8\uff0c\u540c\u65f6\u5f53\u94fa\u3001\u9152\u697c\u3001\u8d4c\u573a\u5404\u5904\u4e5f\u5747\u5192\u4e0a\u706b\u5934\u3002\u770b\u6765\u51e4\u5929\u5357\u51b3\u610f\u5c06\u4f5b\u5c71\u9547\u4e0a\u7684\u57fa\u4e1a\u5c3d\u6570\u6bc1\u5374\uff0c\u90a3\u662f\u6c38\u8fdc\u4e0d\u518d\u56de\u5934\u7684\u4e86\u3002\u80e1\u6590\u5fc3\u4e2d\u607c\u6068\uff0c\u5374\u4e5f\u4e0d\u7981\u4f69\u670d\u8fd9\u4eba\u9634\u9e37\u72e0\u8fa3\uff0c\u52c7\u65ad\u660e\u51b3\uff0c\u7adf\u7136\u4e0d\u60dc\u5c06\u5341\u6765\u5e74\u7684\u7ecf\u8425\u4ed8\u4e4b\u4e00\u70ac\uff0c\u5fc3\u60f3\uff1a\u201c\u6b64\u4eba\u8fd9\u822c\u5de5\u4e8e\u5fc3\u8ba1\uff0c\u5b9a\u6709\u85cf\u8eab\u907f\u7978\u7684\u5999\u7b56\uff0c\u8be5\u5f53\u5230\u4f55\u5904\u627e\u4ed6\u624d\u662f\uff1f\u201d\u4e00\u65f6\u7acb\u9a6c\u4f5b\u5c71\u9547\u5916\uff0c\u5fac\u5fa8\u4e0d\u5b9a\u3002 \u3000\u3000\u8fdc\u8fdc\u542c\u5f97\u4eba\u58f0\u5608\u6742\uff0c\u6551\u706b\u6c34\u9f99\u5728\u77f3\u677f\u8def\u4e0a\u9686\u9686\u5954\u9a70\u3002\u80e1\u6590\u5fc3\u60f3\uff1a\u201c\u9002\u624d\u8ffd\u90a3\u4e09\u4e2a\u65e0\u8d56\uff0c\u6765\u53bb\u4e0d\u5230\u534a\u4e2a\u65f6\u8fb0\u3002\u8fd9\u51e4\u5929\u5357\u5bb6\u5927\u4e1a\u5927\uff0c\u5c82\u80fd\u5728\u7247\u523b\u4e4b\u95f4\u6599\u7406\u6e05\u695a\uff1f\u4ed6\u4eca\u665a\u82e5\u4e0d\u4eb2\u81ea\u56de\u6765\u5206\u65ad\uff0c\u5b9a\u6709\u5fc3\u8179\u4eb2\u4fe1\u53bb\u4ed6\u85cf\u8eab\u7684\u6240\u5728\u8bf7\u793a\u3002\u6211\u53ea\u5b88\u4f4f\u8def\u53e3\u4fbf\u4e86\u3002\u201d \u3000\u3000\u6599\u60f3\u767d\u65e5\u5b9a\u7136\u65e0\u4eba\u9732\u9762\uff0c\u4e8e\u662f\u5728\u50fb\u9759\u5904\u627e\u4e86\u682a\u5927\u6811\uff0c\u722c\u4e0a\u6811\u53bb\u95ed\u76ee\u517b\u4f38\uff0c\u60f3\u5230\u9524\u5bb6\u56db\u53e3\u7834\u5bb3\u7684\u60e8\u72b6\uff0c\u60b2\u6124\u96be\u5e73\uff0c\u5fc3\u4e2d\u7ffb\u6765\u8986\u53bb\u5730\u8d77\u8a93\uff1a\u201c\u82e5\u4e0d\u6740\u90a3\u51e4\u8d3c\u5168\u5bb6\uff0c\u6211\u80e1\u6590\u5728\u81ea\u751f\u4e8e\u5929\u5730\u4e4b\u95f4\u3002\u201d\u7b49\u5230\u66ae\u8272\u82cd\u832b\uff0c\u4ed6\u8d70\u5230\u5927\u8def\u4e4b\u65c1\uff0c\u4f0f\u5728\u957f\u8349\u4e2d\u5b88\u5019\uff0c\u7741\u5927\u4e86\u773c\u56db\u5904\u89c2\u671b\uff0c\u51e0\u4e2a\u65f6\u8fb0\u8fc7\u53bb\uff0c\u7adf\u662f\u6ca1\u70b9\u52a8\u9759\uff0c\u76f4\u5230\u5929\u8272\u5927\u660e\uff0c\u9664\u4e86\u5356\u83dc\u6311\u7caa\u7684\u4e61\u519c\u4e4b\u5916\uff0c\u65e0\u4eba\u8fdb\u51fa\u4f5b\u5c71\u3002 \u3000\u3000\u6b63\u611f\u6c14\u6cae\uff0c\u5ffd\u542c\u9a6c\u8e44\u58f0\u54cd\uff0c\u4e24\u4e58\u5feb\u9a6c\u4ece\u9547\u4e0a\u5954\u4e86\u51fa\u6765\uff0c\u9a6c\u4e0a\u4e58\u5ba2\u7a7f\u7740\u6b66\u5b98\u670d\u8272\uff0c\u5374\u662f\u4eac\u4e2d\u4f8d\u536b\u7684\u6253\u626e\u3002 \u3000\u3000\u80e1\u6590\u5fc3\u4e2d\u4e00\u52a8\uff0c\u8bb0\u8d77\u51e4\u4e00\u9e23\u66fe\u9053\uff0c\u4ed6\u7236\u4eb2\u56e0\u8981\u966a\u4f34\u5fa1\u524d\u4f8d\u536b\uff0c\u4e0d\u80fd\u5206\u8eab\u6765\u89c1\uff0c\u8fd9\u4e24\u540d\u4f8d\u536b\u5b9a\u4e0e\u51e4\u5929\u5357\u6709\u6240\u5e72\u8fde\u3002\u5fc3\u5ff5\u752b\u8d77\uff0c\u4e24\u9a91\u9a6c\u5df2\u63a0\u8fc7\u4ed6\u4f0f\u8eab\u4e4b\u6240\uff0c\u5f53\u5373\u6361\u8d77\u4e00\u5757\u5c0f\u77f3\uff0c\u4f38\u6307\u5f39\u51fa\uff0c\u6ce2\u7684\u4e00\u58f0\u8f7b\u54cd\uff0c\u4e00\u5339\u9a6c\u7684\u540e\u817f\u65e9\u7740\u3002\u77f3\u5b50\u6b63\u597d\u6253\u4e2d\u90a3\u9a6c\u540e\u817f\u7684\u5173\u8282\uff0c\u90a3\u9a6c\u5954\u8dd1\u6b62\u901f\uff0c\u7a81\u7136\u540e\u817f\u4e00\u66f2\uff0c\u5411\u540e\u5750\u5012\uff0c\u90a3\u817f\u767b\u65f6\u65ad\u6298\u3002\u9a6c\u4e0a\u4e58\u5ba2\u9a91\u672f\u751a\u7cbe\uff0c\u8fd9\u4e00\u4e0b\u53d8\u6545\u7a81\u8d77\uff0c\u4ed6\u63d0\u8eab\u8dc3\u8d77\uff0c\u8f7b\u8f7b\u843d\u5728\u9053\u65c1\uff0c\u89c1\u9a6c\u5339\u65ad\u4e86\u540e\u817f\uff0c\u8fde\u58f0\u54c0\u9e23\uff0c\u4e0d\u7531\u5f97\u76b1\u8d77\u7709\u5934\uff0c\u53eb\u9053\uff1a\u201c\u7cdf\u7cd5\uff0c\u7cdf\u7cd5\uff0c\u201d \u3000\u3000\u80e1\u6590\u79bb\u7740\u4ed6\u6709\u4e03\u516b\u4e08\u8fdc\uff0c\u53ea\u89c1\u53e6\u4e00\u540d\u4f8d\u536b\u52d2\u9a6c\u56de\u5934\uff0c\u95ee\u9053\uff1a\u201c\u600e\u4e48\u5566\uff1f\u201d \u3000\u3000\u90a3\u4f8d\u536b\u9053\uff1a\u201d\u8fd9\u755c\u7272\u5ffd\u7136\u5931\u8e44\uff0c\u6298\u65ad\u4e86\u817f\uff0c\u53ea\u6015\u4e0d\u4e2d\u7528\u5566\u3002\u201d\u80e1\u6590\u542c\u4e86\u4ed6\u8bf4\u8bdd\u7684\u58f0\u97f3\uff0c\u731b\u7136\u60f3\u8d77\u8fd9\u4eba\u59d3\u4f55\uff0c\u6570\u5e74\u524d\u5728\u5546\u5bb6\u5821\u4e2d\u66fe\u7ecf\u89c1\u8fc7\u3002 \u3000\u3000\u53e6\u4e00\u540d\u4f8d\u536b\u9053\uff1a\u201c\u53eb\u4eec\u56de\u4f5b\u5c71\u53bb\uff0c\u53e6\u8981\u4e00\u5934\u7272\u53e3\u3002\u201d\u90a3\u59d3\u4f55\u7684\u4f8d\u536b\u6b63\u662f\u5f53\u5e74\u548c\u5f90\u94ee\u6253\u8fc7\u4e00\u67b6\u7684\u4f55\u601d\u8c6a\uff0c\u8bf4\u9053\uff1a\u201c\u51e4\u5929\u5357\u8d70\u5f97\u4e0d\u77e5\u53bb\u5411\uff0c\u4f5b\u5c71\u9547\u4e0a\u4e71\u6210\u4e00\u56e2\uff0c\u6ca1\u4eba\u7406\u4e8b\uff0c\u8fd8\u662f\u53bb\u5411\u5357\u6d77\u53bf\u8981\u9a6c\u5427\u3002\u201d\u8bf4\u7740\u62d4\u51fa\u5315\u9996\uff0c\u5728\u9a6c\u8111\u888b\u4e2d\u4e00\u5251\u63d2\u8fdb\uff0c\u514d\u5f97\u90a3\u9a6c\u591a\u53d7\u75db\u82e6\u3002 \u3000\u3000\u90a3\u4f8d\u536b\u9053\uff1a\u201d\u54b1\u4eec\u5408\u9a91\u4e00\u5339\u9a6c\u5427\uff0c\u6162\u6162\u5230\u5357\u6d77\u53bf\u53bb\u3002\u4f55\u5927\u54e5\uff0c\u4f60\u8bf4\u51e4\u5929\u5357\u5f53\u771f\u4e0d\u56de\u4f5b\u5c71\u4e86\uff1f\u201d\u4f55\u601d\u8c6a\u9053\uff1a\u201c\u4ed6\u6bc1\u5bb6\u907f\u7978\uff0c\u600e\u80fd\u56de\u53bb\uff1f\u201d\u90a3\u4f8d\u536b\u9053\uff1a\u201c\u8fd9\u6b21\u5357\u6765\uff0c\u4e0d\u4f46\u767d\u8f9b\u82e6\u4e00\u8d9f\uff0c\u8fd8\u5bb3\u6b7b\u4e86\u4f60\u4e00\u5339\u597d\u9a6c\u3002\u201d \u3000\u3000\u4f55\u601d\u8c6a\u8de8\u4e0a\u9a6c\u80cc\uff0c\u8bf4\u9053\uff1a\u201c\u4e5f\u4e0d\u4e00\u5b9a\u662f\u767d\u8f9b\u82e6\u3002\u798f\u5927\u5e05\u5e9c\u91cc\u7684\u5929\u4e0b\u638c\u95e8\u4eba\u5927\u4f1a\uff0c\u662f\u4f55\u7b49\u76db\u4e8b\uff0c\u51e4\u5929\u5357\u662f\u4e94\u864e\u95e8\u638c\u95e8\uff0c\u672a\u5fc5\u4e0d\u5230\u3002\u201d\u8bf4\u7740\u4f38\u4e0b\u5728\u9a6c\u81c0\u4e0a\u4e00\u62cd\u3002\u90a3\u9a6c\u80cc\u4e0a\u4e58\u4e86\u4e24\u4eba\uff0c\u4e0d\u80fd\u5feb\u8dd1\uff0c\u53ea\u6709\u8fc8\u6b65\u7f13\u884c\u3002 \u3000\u3000\u80e1\u6590\u542c\u4e86\u201c\u798f\u5927\u5e05\u5e9c\u91cc\u7684\u5929\u4e0b\u638c\u95e8\u4eba\u5927\u4f1a\u201d\u8fd9\u51e0\u4e2a\u5b57\uff0c\u5fc3\u91cc\u4e00\u559c\uff0c\u6697\u60f3\uff1a \u3000\u3000\u201c\u5929\u4e0b\u638c\u95e8\u4eba\u805a\u4f1a\uff0c\u90a3\u53ef\u70ed\u95f9\u5f97\u7d27\u54ea\u3002\u51e4\u5929\u5357\u4fbf\u7b97\u4e0d\u53bb\uff0c\u4ed6\u843d\u811a\u4f55\u65b9\uff0c\u591a\u5c11\u4e5f\u80fd\u5728\u4f1a\u4e2d\u6253\u542c\u5230\u4e00\u4e9b\u6d88\u606f\u3002\u4f46\u4e0d\u77e5\u90a3\u798f\u5927\u5e05\u9080\u4f1a\u5404\u6d3e\u5b66\u95e8\u4eba\uff0c\u5374\u662f\u4e3a\u4e86\u4f55\u4e8b\uff1f\u201d \u3000"}),showOnHomepage:!0},{name:"\u7b2c\u516d\u7ae0\u3000\u7d2b\u8863\u5973\u90ce",content:(0,a.jsx)(a.Fragment,{children:"\u80e1\u6590\u56de\u5230\u5927\u6811\u5e95\u4e0b\u7275\u8fc7\u9a6c\u5339\uff0c\u7eb5\u9a91\u5411\u5317\uff0c\u4e00\u8def\u4e0a\u7559\u5fc3\u51e4\u5929\u5357\u548c\u4e94\u864e\u95e8\u7684\u8e2a\u8ff9\uff0c\u5374\u662f\u534a\u70b9\u5f71\u5b50\u4e5f\u65e0\u3002\u8fd9\u4e00\u65e5\u8fc7\u4e86\u4e94\u5cad\uff0c\u5df2\u5165\u6e56\u5357\u7701\u5883\uff0c\u53ea\u89c1\u6cbf\u8def\u90fd\u662f\u7ea2\u571f\uff0c\u8f83\u4e4b\u5cad\u5357\u98ce\u7269\uff0c\u5927\u5f02\u5176\u8da3\u3002 \u3000\u3000\u80e1\u6590\u7eb5\u9a6c\u75be\u9a70\uff0c\u8fc7\u9a6c\u5bb6\u94fa\u540e\uff0c\u5c06\u81f3\u6816\u51e4\u6e21\u53e3\uff0c\u731b\u542c\u5f97\u8eab\u540e\u4f20\u6765\u4e00\u9635\u8fc5\u6377\u5f02\u5e38\u7684\u9a6c\u8e44\u58f0\u54cd\uff0c\u56de\u5934\u4e00\u671b\uff0c\u53ea\u89c1\u4e00\u5339\u767d\u9a6c\u594b\u9b23\u626c\u8e44\uff0c\u98ce\u9a70\u800c\u6765\uff0c\u5f53\u5373\u52d2\u9a6c\u8ba9\u5728\u9053\u65c1\u3002\u521a\u7ad9\u5b9a\uff0c\u8033\u8845\u547c\u7684\u4e00\u54cd\uff0c\u90a3\u767d\u9a6c\u5df2\u4ece\u8eab\u65c1\u4e00\u7a9c\u800c\u8fc7\uff0c\u56db\u8e44\u7ade\u4f3c\u4e0d\u7740\u5730\u4e00\u822c\u3002\u9a6c\u80cc\u4e0a\u4e58\u7740\u4e00\u4e2a\u7d2b\u8863\u5973\u5b50\uff0c\u53ea\u56e0\u90a3\u9a6c\u5b9e\u5728\u8dd1\u5f97\u592a\u5feb\uff0c\u5973\u5b50\u7684\u9762\u8c8c\u6ca1\u77a7\u6e05\u695a\uff0c\u4f46\u89c1\u5979\u80cc\u5f71\u82d7\u6761\uff0c\u7a33\u7a33\u5730\u7aef\u5750\u9a6c\u80cc\u3002 \u3000\u3000\u80e1\u6590\u5403\u4e86\u4e00\u60ca\uff1a\u201c\u8fd9\u767d\u9a6c\u4f3c\u662f\u8d75\u4e09\u54e5\u7684\u5750\u9a91\uff0c\u600e\u4e48\u53c8\u6765\u5230\u4e2d\u539f\uff1f\u201d\u4ed6\u5fc3\u4e2d\u8bb0\u6302\u8d75\u534a\u5c71\uff0c\u60f3\u8981\u8ffd\u4e0a\u53bb\u95ee\u4e2a\u660e\u767d\uff0c\u521a\u5f20\u53e3\u53eb\u4e86\u58f0\uff1a\u201c\u5582\uff01\u201d\u90a3\u767d\u9a6c\u5df2\u5954\u5f97\u8fdc\u4e86\uff0c\u5782\u67f3\u5f71\u4e0b\uff0c\u4f9d\u7a00\u89c1\u90a3\u7d2b\u8863\u5973\u5b50\u56de\u5934\u671b\u4e86\u4e00\u773c\uff0c\u767d\u9a6c\u811a\u6b65\u4e0d\u505c\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u5df2\u5954\u5f97\u65e0\u5f71\u65e0\u8e2a\u3002 \u3000\u3000\u80e1\u6590\u597d\u751f\u5947\u602a\uff0c\u50ac\u9a6c\u8d76\u8def\uff0c\u4f46\u767d\u9a6c\u811a\u7a0b\u5982\u6b64\u8fc5\u901f\uff0c\u7eb5\u7136\u81ea\u5df1\u7684\u5750\u9a91\u518d\u5feb\u4e00\u500d\uff0c\u5c31\u7b97\u65e5\u591c\u4e0d\u505c\u5954\u9a70\uff0c\u4e5f\u51b3\u8ba1\u8d76\u5979\u4e0d\u4e0a\uff0c\u50ac\u9a6c\u8ffd\u8d76\uff0c\u4e5f\u53ea\u662f\u804a\u5c3d\u4eba\u4e8b\u800c\u5df2\u3002 \u3000\u3000\u7b2c\u4e09\u65e5\u5230\u4e86\u8861\u9633\u3002\u90a3\u8861\u9633\u662f\u6e58\u5357\u91cd\u9547\uff0c\u79bb\u5357\u5dbd\u8861\u5c71\u5df2\u4e0d\u5728\u8fdc\u3002\u4e00\u8def\u4e0a\u53e4\u677e\u5939\u9053\uff0c\u767d\u4e91\u7ed5\u5c71\uff0c\u4ee4\u4eba\u80f8\u895f\u4e3a\u4e4b\u4e00\u723d\u3002 \u3000\u3000\u80e1\u6590\u521a\u5165\u8861\u9633\u5357\u95e8\uff0c\u7a81\u89c1\u4e00\u5bb6\u996d\u94fa\u5eca\u4e0b\u7cfb\u7740\u4e00\u5339\u767d\u9a6c\uff0c\u8eab\u957f\u817f\u9ad8\uff0c\u8c8c\u76f8\u795e\u9a8f\uff0c\u6b63\u662f\u9014\u4e2d\u6240\u9047\u7684\u90a3\u5339\u5feb\u9a6c\u3002\u80e1\u6590\u5c11\u5e74\u65f6\u4e0e\u8d75\u534a\u5c71\u7f14\u4ea4\uff0c\u5bf9\u4ed6\u7684\u767d\u9a6c\u77a7\u5f97\u6781\u662f\u4ed4\u7ec6\uff0c\u6b64\u65f6\u4e00\u89c1\uff0c\u4fe8\u662f\u6545\u7269\uff0c\u4e0d\u7981\u5927\u559c\uff0c\u5fd9\u8d70\u5230\u996d\u94fa\u4e2d\uff0c\u60f3\u627e\u90a3\u7d2b\u8863\u5973\u5b50\uff0c\u5374\u662f\u4e0d\u89c1\u4eba\u5f71\u3002 \u3000\u3000\u80e1\u6590\u8981\u5f85\u5411\u5e97\u4f19\u8be2\u95ee\uff0c\u8f6c\u5ff5\u4e00\u60f3\uff0c\u516c\u7136\u6253\u63a2\u4e00\u4e2a\u4e0d\u76f8\u8bc6\u5973\u5b50\u7684\u884c\u8e2a\uff0c\u5927\u662f\u4e0d\u4fbf\uff0c\u4e8e\u662f\u5750\u5728\u95e8\u53e3\uff0c\u8981\u4e86\u9152\u996d\u3002 \u3000\u3000\u5c11\u505c\u9152\u83dc\u9001\u4e0a\uff0c\u6e56\u5357\u4eba\u5403\u996d\uff0c\u7b77\u6781\u957f\uff0c\u7897\u6781\u5927\uff0c\u65e0\u83dc\u4e0d\u8fa3\uff0c\u6bcf\u5473\u7686\u6d53\uff0c\u9887\u6709\u8c6a\u8fc8\u4e4b\u98ce\uff0c\u5f88\u914d\u80e1\u6590\u7684\u6027\u5b50\u3002\u4ed6\u6162\u6162\u559d\u9152\uff0c\u5bfb\u601d\u5c11\u5f85\u5982\u4f55\u542f\u9f7f\u548c\u90a3\u7d2b\u8863\u5973\u5b50\u8bf4\u8bdd\uff0c\u731b\u5730\u60f3\u8d77\uff1a\u201c\u6b64\u4eba\u65e2\u4e58\u8d75\u4e09\u54e5\u7684\u767d\u9a6c\uff0c\u5fc5\u548c\u4ed6\u6709\u6781\u6df1\u7684\u6e0a\u6e90\uff0c\u4f55\u4e0d\u5c06\u8d75\u4e09\u54e5\u6240\u8d60\u7684\u7ea2\u82b1\u653e\u5728\u684c\u4e0a\uff1f\u5979\u81ea\u4f1a\u6765\u5bfb\u6211\u8bf4\u8bdd\u3002\u201d\u4ed6\u53f3\u624b\u62ff\u7740\u9152\u676f\uff0c\u53cd\u4f38\u5de6\u624b\u53bb\u53d6\u5305\u88b1\uff0c\u5374\u6478\u4e86\u4e2a\u7a7a\uff0c\u56de\u8fc7\u5934\u4e00\u770b\uff0c\u5305\u88b1\u7adf\u5df2\u4e0d\u77e5\u53bb\u5411\u3002 \u3000\u3000\u5305\u88b1\u660e\u660e\u653e\u5728\u8eab\u540e\u684c\u4e0a\uff0c\u600e\u5730\u4e00\u8f6c\u773c\u4fbf\u4e0d\u89c1\uff1f\u5411\u996d\u94fa\u4e2d\u5404\u4eba\u4e00\u671b\uff0c\u5e76\u65e0\u5f02\u6837\u4eba\u7269\uff0c\u5fc3\u4e2d\u6697\u6697\u79f0\u5947\uff1a\u201c\u82e5\u662f\u5bfb\u5e38\u76d7\u8d3c\u987a\u624b\u7275\u7f8a\uff0c\u6211\u51b3\u4e0d\u80fd\u4e0d\u77e5\u3002\u6b64\u4eba\u65e2\u80fd\u65e0\u58f0\u65e0\u606f\u5730\u53d6\u53bb\uff0c\u5018\u5728\u80cc\u540e\u7a81\u65bd\u6697\u7b97\uff0c\u6211\u4e5f\u5fc5\u900d\u6bd2\u624b\uff0c\u77a7\u6765\u4eca\u65e5\u662f\u5728\u6e56\u5357\u9047\u4e0a\u9ad8\u4eba\u4e86\u3002\u201d\u5f53\u4e0b\u95ee\u5e97\u4f19\u9053\uff1a\u201c\u6211\u7684\u5305\u88b1\u653e\u5728\u684c\u65c1\uff0c\u600e\u5730\u4e0d\u89c1\u4e86\uff1f\u4f60\u89c1\u5230\u6709\u4eba\u53d6\u53bb\u6ca1\u6709\uff1f\u201d \u3000\u3000\u90a3\u5e97\u4f19\u542c\u8bf4\u5ba2\u4eba\u5c11\u4e86\u4e1c\u897f\uff0c\u767b\u65f6\u5927\u8d77\u5fd9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u8d35\u5ba2\u94b1\u7269\uff0c\u6982\u8bf7\u95e8\u7406\uff0c\u9664\u975e\u4ea4\u5728\u67dc\u4e0a\uff0c\u5426\u5219\u5c0f\u5e97\u6055\u4e0d\u8d1f\u8d23\u3002\u201d\u80e1\u6590\u7b11\u9053\uff1a\u201c\u8c01\u8981\u4f60\u8d54\u4e86\uff1f\u6211\u53ea\u95ee\u4f60\u77a7\u89c1\u6709\u4eba\u62ff\u4e86\u6ca1\u6709\u3002\u201d\u90a3\u5e97\u4f19\u9053\uff1a\u201c\u6ca1\u6709\uff0c\u6ca1\u6709\u3002\u6211\u4eec\u5e97\u91cc\u600e\u4f1a\u6709\u8d3c\uff1f \u3000\u3000\u5ba2\u5b98\u5343\u4e07\u4e0d\u53ef\u4e71\u8bf4\u3002\u201d\u80e1\u6590\u77e5\u9053\u8ddf\u4ed6\u7f20\u4e0d\u6e05\u695a\uff0c\u53c8\u60f3\u8fde\u81ea\u5df1\u4e5f\u6ca1\u5bdf\u89c9\uff0c\u90a3\u5e97\u4f19\u600e\u80fd\u77a7\u89c1\uff1f\u6b63\u81ea\u6c89\u541f\uff0c\u90a3\u5e97\u4f19\u9053\uff1a\u201c\u5ba2\u5b98\u6240\u7528\u9152\u996d\uff0c\u5171\u662f\u4e00\u94b1\u4e94\u5206\u94f6\u5b50\uff0c\u8bf7\u4f1a\u949e\u5427\u3002\u201d \u3000\u3000\u90a3\u5305\u88b1\u4e4b\u4e2d\uff0c\u5c1a\u6709\u4ece\u51e4\u5929\u5357\u8d4c\u573a\u4e2d\u53d6\u6765\u7684\u6570\u767e\u4e24\u94f6\u5b50\uff0c\u4ed6\u8eab\u8fb9\u53ef\u662f\u4e0d\u540d\u4e00\u6587\uff0c\u89c1\u5e97\u4f19\u50ac\u5e10\uff0c\u4e0d\u7531\u5f97\u4e00\u7a98\u3002\u90a3\u5e97\u4f19\u51b7\u7b11\u9053\uff1a\u201c\u5ba2\u5b98\u82e5\u662f\u624b\u5934\u4e0d\u4fbf\uff0c\u4e5f\u4e0d\u7528\u8d56\u8bf4\u4e0d\u89c1\u4e86\u5305\u88b1\u554a\u3002\u201d \u3000\u3000\u80e1\u6590\u61d2\u5f97\u548c\u4ed6\u5206\u8fa9\uff0c\u5230\u5eca\u4e0b\u53bb\u7275\u8fc7\u81ea\u5df1\u5750\u9a91\uff0c\u5374\u89c1\u90a3\u5339\u767d\u9a6c\u5df2\u4e0d\u77e5\u53bb\u5411\uff0c\u4e0d\u7531\u5f97\u4e00\u6014\uff1a\u201c\u8fd9\u767d\u9a6c\u8ddf\u5077\u6211\u5305\u88b1\u4e4b\u4eba\u5fc5\u6709\u5e72\u8fde\u3002\u201d \u3000\u3000\u8fd9\u4e48\u4e00\u6765\uff0c\u5bf9\u90a3\u7d2b\u8863\u5973\u5b50\u767b\u65f6\u591a\u4e86\u4e00\u5c42\u6212\u5907\u4e4b\u5fc3\uff0c\u4e8e\u662f\u5c06\u5750\u9a91\u4ea4\u7ed9\u5e97\u4f19\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u5934\u7272\u53e3\u5c11\u8bf4\u4e5f\u503c\u5f97\u516b\u4e5d\u4e24\u94f6\u5b50\uff0c\u4e14\u62bc\u5728\u67dc\u4e0a\uff0c\u5f85\u6211\u53d6\u5f97\u94f6\u5b50\uff0c\u8fde\u7272\u53e3\u7684\u8349\u6599\u94b1\u4e00\u5e76\u6765\u8d4e\u3002\u201d\u90a3\u5e97\u4f19\u7acb\u65f6\u6362\u4e86\u4e00\u526f\u8138\u8272\uff0c\u966a\u7b11\u9053\uff1a\u201d\u4e0d\u5fd9\u4e0d\u5fd9\uff0c\u5ba2\u5b98\u8d70\u597d\u3002\u201d \u3000\u3000\u80e1\u6590\u4e0a\u8981\u53bb\u8ffd\u5bfb\u767d\u9a6c\u7684\u8e2a\u8ff9\uff0c\u90a3\u5e97\u4f19\u8d76\u4e86\u4e0a\u6765\uff0c\u7b11\u9053\uff1a\u201c\u5ba2\u5b98\uff0c\u4eca\u65e5\u4f60\u4e5f\u65e0\u94b1\u5403\u996d\uff0c\u6211\u6307\u70b9\u4f60\u4e00\u6761\u8def\uff0c\u5305\u4f60\u6709\u5403\u6709\u4f4f\u3002\u201d\u80e1\u6590\u5acc\u4ed6\u647d\u5506\uff0c\u6b63\u8981\u65a5\u9000\uff0c\u8f6c\u5ff5\u4e00\u60f3\uff1a\u201c\u4ec0\u4e48\u8def\u5b50\uff1f\u662f\u6307\u70b9\u6211\u53bb\u5bfb\u5305\u88b1\u4e48\uff1f\u201d\u4e8e\u662f\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u90a3\u5e97\u4f19\u7b11\u9053\uff1a\u201c\u8fd9\u79cd\u4e8b\u60c5\u4e00\u767e\u5e74\u4e5f\u672a\u5fc5\u9047\u5f97\u4e0a\uff0c\u504f\u751f\u5ba2\u5b98\u4ea4\u4e86\u8fd0\uff0c\u67ab\u53f6\u5e84\u4e07\u8001\u62f3\u5e08\u4e0d\u8fdf\u4e0d\u65e9\uff0c\u521a\u597d\u5728\u4e03\u65e5\u524d\u53bb\u4e16\uff0c\u4eca\u65e5\u6b63\u662f\u5934\u4e03\u5f00\u4e27\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u90a3\u8ddf\u6211\u6709\u751a\u76f8\u5e72\uff1f\u201d\u90a3\u5e97\u4f19\u7b11\u9053\uff1a\u201c\u5927\u5927\u7684\u76f8\u5e72\u3002\u201d\u8f6c\u8eab\u5230\u67dc\u4e0a\u53d6\u4e86\u4e00\u5bf9\u7d20\u70db\uff0c\u4e00\u7b52\u7ebf\u9999\uff0c\u4ea4\u7ed9\u80e1\u6590\uff0c\u8bf4\u9053\uff1a\u201c\u4ece\u6b64\u4e00\u76f4\u5411\u5317\uff0c\u4e0d\u5230\u4e09\u91cc\u5730\uff0c\u51e0\u767e\u68f5\u67ab\u6811\u56f4\u7740\u4e00\u5ea7\u5927\u5e84\u9662\uff0c\u4fbf\u662f\u67ab\u53f6\u5e84\u4e86\u3002\u5ba2\u5b98\u62ff\u8fd9\u526f\u9999\u70db\u53bb\u540a\u4e27\uff0c\u5728\u4e07\u8001\u62f3\u5e08\u7684\u7075\u524d\u78d5\u51e0\u4e2a\u54cd\u5934\uff0c\u5e84\u4e0a\u975e\u7ba1\u5403\u7ba1\u4f4f\u4e0d\u53ef\u3002\u660e\u513f\u4f60\u8bf4\u77ed\u4e86\u76d8\u7f20\uff0c\u5e84\u4e0a\u5c11\u8bf4\u4e5f\u5f97\u9001\u4f60\u4e00\u4e24\u94f6\u5b50\u8def\u8d39\u3002\u201d \u3000\u3000\u80e1\u6590\u542c\u8bf4\u6b7b\u8005\u53eb\u505a\u201c\u4e07\u8001\u62f3\u5e08\u201d\uff0c\u5fc3\u60f3\u540c\u662f\u6b66\u6797\u4e00\u8109\uff0c\u5148\u6709\u51e0\u5206\u613f\u610f\uff0c\u95ee\u9053\uff1a\u201d\u90a3\u67ab\u53f6\u5e84\u600e\u5730\u5982\u6b64\u597d\u5ba2\uff1f\u201d\u90a3\u5e97\u4f19\u9053\uff1a\u201c\u6e56\u5357\u51e0\u767e\u91cc\u5185\uff0c\u8c01\u4e0d\u77e5\u4e07\u8001\u62f3\u5e08\u6177\u6168\u4ed7\u4e49\uff1f\u4e0d\u8fc7\u4ed6\u751f\u524d\u4e13\u7231\u7ed3\u4ea4\u82f1\u96c4\u597d\u6c49\uff0c\u50cf\u5ba2\u5b98\u4e0d\u4f1a\u6b66\u827a\uff0c\u6b63\u597d\u4e58\u4ed6\u6b7b\u540e\u53bb\u6253\u6253\u79cb\u98ce\u4e86\u3002\u201d\u80e1\u6590\u5148\u6012\u540e\u7b11\uff0c\u62b1\u62f3\u7b11\u9053\uff1a\u201c\u591a\u627f\u6307\u70b9\u3002\u201d\u95ee\u9053\uff1a\u201c\u90a3\u4e48\u4e07\u8001\u62f3\u5e08\u751f\u524d\u7684\u82f1\u96c4\u670b\u53cb\uff0c\u4eca\u5929\u90fd\u8981\u8d76\u6765\u540a\u4e27\uff1f\u201d\u90a3\u5e97\u4f19\u9053\uff1a\u201c\u8c01\u8bf4\u4e0d\u662f\u5462\uff1f\u5ba2\u5b98\u4fbf\u53bb\u5f00\u5f00\u773c\u754c\u4e5f\u662f\u597d\u7684\u3002\u201d\u80e1\u6590\u4e00\u542c\u6b63\u4e2d\u4e0b\u6000\uff0c\u63a5\u8fc7\u7d20\u70db\u7ebf\u9999\uff0c\u5f84\u5f80\u5317\u53bb\u3002 \u3000\u3000\u4e0d\u51fa\u4e09\u91cc\uff0c\u679c\u5982\u90a3\u5e97\u4f19\u6240\u8a00\uff0c\u6570\u767e\u682a\u67ab\u6811\u73af\u62b1\u7740\u4e00\u5ea7\u5927\u5e84\u9662\uff0c\u5e84\u5916\u60ac\u7740\u767d\u5e95\u84dd\u5b57\u7684\u706f\u7b3c\uff0c\u5927\u95e8\u4e0a\u9489\u4e86\u9ebb\u5e03\u3002 \u3000\u3000\u80e1\u6590\u4e00\u8fdb\u95e8\uff0c\u9f13\u624b\u5439\u8d77\u8fce\u5bbe\u4e50\u66f2\uff0c\u4f46\u89c1\u597d\u5927\u4e00\u5ea7\u7075\u5802\uff0c\u4e24\u53a2\u6302\u6ee1\u7d20\u5e5b\u633d\u8054\u3002\u4ed6\u8d70\u5230\u7075\u524d\uff0c\u8dea\u4e0b\u78d5\u5934\uff0c\u5fc3\u60f3\uff1a\u201c\u4e0d\u7ba1\u4f60\u662f\u8c01\uff0c\u603b\u662f\u6b66\u6797\u524d\u8f88\uff0c\u53d7\u6211\u51e0\u4e2a\u5934\u60f3\u6765\u4e5f\u5f53\u5f97\u8d77\u3002\u201d \u3000\u3000\u4ed6\u8dea\u62dc\u4e4b\u65f6\uff0c\u4e09\u4e2a\u62ab\u9ebb\u7a7f\u767d\u7684\u5b5d\u5b50\u8dea\u5728\u5730\u4e0b\u78d5\u5934\u8fd8\u793c\u3002\u80e1\u6590\u7ad9\u8d77\u8eab\u6765\uff0c\u4e09\u4e2a\u5b5d\u5b50\u5411\u4ed6\u4f5c\u63d6\u81f4\u8c22\u3002\u80e1\u6590\u4e5f\u662f\u4e00\u63d6\uff0c\u53ea\u89c1\u4e09\u4eba\u4e2d\u4e24\u4e2a\u8eab\u6750\u7c97\u58ee\uff0c\u53e6\u4e00\u4eba\u77ed\u5c0f\u7cbe\u608d\uff0c\u76f8\u8c8c\u5404\u4e0d\u76f8\u540c\uff0c\u5fc3\u9053\uff1a\u201c\u4e07\u8001\u62f3\u5e08\u8fd9\u4e09\u4e2a\u513f\u5b50\uff0c\u5b9a\u7136\u4e0d\u662f\u4e00\u6bcd\u6240\u751f\uff0c\u591a\u534a\u662f\u4e09\u4e2a\u59bb\u59be\u5404\u4ea7\u4e00\u5b50\u4e86\u3002\u201d\u56de\u8eab\u8fc7\u6765\uff0c\u4f46\u89c1\u5927\u5385\u4e0a\u6324\u6ee1\u4e86\u540a\u5ba2\uff0c\u4e00\u5c0f\u534a\u4f3c\u662f\u5f53\u5730\u7684\u4e61\u90bb\u571f\u7ec5\uff0c\u5927\u534a\u5219\u662f\u6b66\u6797\u8c6a\u58eb\u3002\u80e1\u6590\u9010\u4e00\u770b\u53bb\uff0c\u5e76\u65e0\u4e00\u4e2a\u76f8\u8bc6\uff0c\u51e4\u5929\u5357\u7236\u5b50\u56fa\u4e0d\u5728\u5185\uff0c\u90a3\u7d2b\u8863\u5973\u5b50\u4e5f\u65e0\u5f71\u8e2a\uff0c\u5bfb\u601d\uff1a\u201c\u6b64\u95f4\u7fa4\u8c6a\u805a\u4f1a\uff0c\u6211\u82e5\u7559\u795e\u6216\u80fd\u542c\u5230\u4e00\u4e9b\u4e94\u864e\u95e8\u51e4\u5bb6\u7236\u5b50\u7684\u6d88\u606f\u3002\u201d \u3000\u3000\u5c11\u9877\u5f00\u51fa\u7d20\u5e2d\uff0c\u5927\u5385\u4e0e\u4e1c\u897f\u53a2\u5385\u4e0a\u4e00\u5171\u5f00\u4e86\u4e03\u5341\u6765\u684c\u3002\u80e1\u6590\u5750\u5728\u504f\u5e2d\uff0c\u7559\u5fc3\u4f17\u540a\u5ba2\u7684\u52a8\u9759\u3002\u4f46\u89c1\u5e74\u8001\u7684\u591a\u5e26\u621a\u5bb9\u54c0\u8272\uff0c\u5e74\u8f7b\u7684\u5374\u9ad8\u8c08\u9614\u8bba\uff0c\u8a00\u7b11\u81ea\u82e5\uff0c\u4f3c\u662f\u591f\u4e0d\u4e0a\u8ddf\u4e07\u8001\u62f3\u5e08\u6709\u4ec0\u4e48\u4ea4\u60c5\uff0c\u4e5f\u4e0d\u56e0\u4ed6\u901d\u4e16\u800c\u60b2\u4f24\u4e86\u3002 \u3000\u3000\u6b63\u77a7\u95f4\uff0c\u53ea\u89c1\u4e09\u4e2a\u5b5d\u5b50\u606d\u606d\u656c\u656c\u5730\u966a\u7740\u4e24\u4e2a\u6b66\u5b98\uff0c\u8ba9\u5411\u9996\u5e2d\uff0c\u5750\u4e86\u5411\u5916\u7684\u4e24\u4e2a\u9996\u5ea7\u3002\u4e24\u4e2a\u6b66\u5b98\u7a7f\u7684\u662f\u5fa1\u524d\u4f8d\u536b\u670d\u8272\u3002\u80e1\u6590\u4e00\u6014\uff0c\u8ba4\u5f97\u8fd9\u4e8c\u4eba\u6b63\u662f\u4f55\u601d\u8c6a\u548c\u4ed6\u540c\u4f34\u3002\u9996\u5e2d\u4e0a\u53e6\u5916\u8fd8\u5750\u4e86\u4e09\u4e2a\u8001\u5e74\u6b66\u5e08\uff0c\u60f3\u6765\u5747\u662f\u6b66\u6797\u4e2d\u7684\u524d\u8f88\u3002 \u3000\u3000\u4e09\u4e2a\u5b5d\u5b50\u5750\u5728\u4e0b\u9996\u4f5c\u966a\u3002 \u3000\u3000\u4f17\u5ba2\u5750\u5b9a\u540e\uff0c\u90a3\u8eab\u6750\u77ee\u5c0f\u7684\u5b5d\u5b50\u7ad9\u8d77\u8eab\u6765\uff0c\u4e3e\u676f\u8c22\u5ba2\u4eba\u540a\u4e27\u3002\u4ed6\u8c22\u8fc7\u4e4b\u540e\uff0c\u7b2c\u4e8c\u4e2a\u5b5d\u5b50\u4e5f\u8c22\u4e00\u904d\uff0c\u63a5\u7740\u7b2c\u4e09\u4e2a\u53c8\u8c22\u4e00\u904d\uff0c\u8a00\u8f9e\u4e3e\u52a8\u4e00\u6a21\u4e00\u6837\uff0c\u4f17\u5ba2\u4eba\u4e00\u800c\u518d\u3001\u518d\u800c\u4e09\u5730\u8d77\u7acb\u8fd8\u793c\uff0c\u4e0d\u7531\u5f97\u9887\u611f\u817b\u70e6\u3002 \u3000\u3000\u80e1\u6590\u6b63\u89c9\u53e4\u602a\uff0c\u542c\u5f97\u540c\u684c\u4e00\u4e2a\u540e\u751f\u4f4e\u58f0\u9053\uff1a\u201c\u4e09\u4e2a\u5b5d\u5b50\u4e00\u9f50\u8c22\u4e00\u6b21\u4e5f\u5c31\u591f\u4e86\uff0c\u5018\u82e5\u4e07\u8001\u62f3\u5e08\u6709\u5341\u4e2a\u513f\u5b50\uff0c\u8fd9\u822c\u5e72\u6cd5\uff0c\u4e0d\u662f\u8981\u8fde\u8c22\u5341\u6b21\u4e48\uff1f\u201d\u4e00\u4e2a\u4e2d\u5e74\u6b66\u5e08\u51b7\u7b11\u9053\uff1a\u201c\u4e07\u9e64\u58f0\u6709\u4e00\u4e2a\u513f\u5b50\u4e5f\u5c31\u597d\u4e86\uff0c\u8fd8\u8bf4\u5341\u4e2a\uff1f\u201d\u90a3\u540e\u751f\u5947\u9053\uff1a\u201c\u96be\u9053\u8fd9\u4e09\u4e2a\u5b5d\u5b50\u4e0d\u662f\u4ed6\u513f\u5b50\u4e48\uff1f\u201d\u4e2d\u5e74\u6b66\u5e08\u9053\uff1a\u201d\u539f\u6765\u5c0f\u54e5\u8ddf\u4e07\u8001\u62f3\u5e08\u975e\u4eb2\u975e\u6545\uff0c\u5c45\u7136\u524d\u6765\u540a\u4e27\uff0c\u8fd9\u4efd\u53e4\u9053\u70ed\u80a0\uff0c\u53ef\u771f\u662f\u96be\u5f97\u4e4b\u6781\u4e86\u3002\u201d\u90a3\u540e\u751f\u80c0\u7ea2\u4e86\u8138\uff0c\u4f4e\u4e0b\u5934\u4e0d\u518d\u8bf4\u8bdd\u3002\u80e1\u6590\u6697\u6697\u597d\u7b11\uff1a\u201c\u6b64\u541b\u548c\u6211\u4e00\u822c\uff0c\u4e5f\u662f\u6253\u79cb\u98ce\u5403\u767d\u98df\u6765\u7684\u3002\u201d \u3000\u3000\u90a3\u4e2d\u5e74\u6b66\u5e08\u9053\uff1a\u201c\u8bf4\u7ed9\u4f60\u542c\u4e5f\u4e0d\u59a8\uff0c\u514d\u5f97\u6709\u4eba\u95ee\u8d77\uff0c\u4f60\u5168\u7136\u63a5\u4e0d\u4e0a\u69ab\u5934\uff0c\u90a3\u53ef\u8138\u4e0a\u4e0b\u4e0d\u6765\u3002\u4e07\u8001\u62f3\u5e08\u540d\u6210\u4e1a\u5c31\uff0c\u5c31\u53ef\u60dc\u819d\u4e0b\u65e0\u513f\u3002\u4ed6\u6536\u4e86\u4e09\u4e2a\u5f92\u5f1f\uff0c\u90a3\u8eab\u6750\u77ee\u5c0f\u7684\u53eb\u505a\u5b59\u4f0f\u864e\uff0c\u662f\u8001\u62f3\u5e08\u7684\u5927\u5f1f\u5b50\u3002\u8fd9\u767d\u8138\u819b\u7684\u6c49\u5b50\u540d\u53eb\u5c09\u8fdf\u8fde\uff0c\u662f\u4e8c\u5f1f\u5b50\u3002\u7ea2\u8138\u819b\u9152\u7cdf\u9f3b\u7684\u5927\u6c49\uff0c\u540d\u53eb\u6768\u5bbe\uff0c\u662f\u4ed6\u7684\u7b2c\u4e09\u5f1f\u5b50\u3002\u8fd9\u4e09\u4eba\u5404\u5f97\u8001\u62f3\u5e08\u4e4b\u4e00\u827a\uff0c\u6b66\u529f\u662f\u5f88\u4e0d\u5dee\u7684\uff0c\u53ea\u662f\u7c97\u4eba\u4e0d\u660e\u793c\u8282\uff0c\u662f\u4ee5\u5927\u5e08\u5144\u8c22\u516b\u4e8c\u5e08\u5144\u4e5f\u8c22\uff0c\u4e09\u5e08\u5f1f\u6015\u5931\u793c\uff0c\u8ddf\u7740\u4e5f\u6765\u8c22\u4e00\u6b21\u3002\u201d\u90a3\u540e\u751f\u7ea2\u9752\u8138\uff0c\u70b9\u5934\u9886\u6559\u3002 \u3000\u3000\u5176\u5b9e\u4e09\u4e2a\u5e08\u5144\u5f1f\u5404\u8c22\u4e00\u6b21\uff0c\u771f\u6b63\u7684\u539f\u56e0\u5374\u4e0d\u662f\u7c97\u4eba\u4e0d\u660e\u793c\u8282\u3002 \u3000\u3000\u80e1\u6590\u8ddf\u9996\u5e2d\u5750\u5f97\u867d\u4e0d\u751a\u8fd1\uff0c\u4f46\u7559\u795e\u503e\u542c\uff0c\u76fc\u671b\u4e24\u540d\u4f8d\u536b\u5728\u8c08\u8bdd\u4e4b\u4e2d\u4f1a\u63d0\u5230\u4e94\u864e\u95e8\uff0c\u900f\u9732\u4e00\u4e9b\u51e4\u5929\u5357\u7236\u5b50\u884c\u8e2a\u7684\u7ebf\u7d22\u3002\u53ea\u542c\u4f55\u601d\u8c6a\u6717\u58f0\u9053\uff1a\u201c\u5144\u5f1f\u5949\u798f\u5927\u5e05\u4e4b\u547d\uff0c\u6765\u8bf7\u5a01\u9707\u6e58\u5357\u7684\u4e07\u8001\u62f3\u5e08\u8fdb\u4eac\uff0c\u53c2\u4e0e\u5929\u4e0b\u638c\u95e8\u4eba\u5927\u4f1a\uff0c\u597d\u8ba9\u5c11\u6797\u97e6\u9640\u95e8\u7684\u6b66\u529f\u5728\u5929\u4e0b\u6b66\u5e08\u4e4b\u524d\u5927\u4eba\u9732\u8138\uff0c\u60f3\u4e0d\u5230\u4e07\u8001\u62f3\u5e08\u4e00\u4e00\u75c5\u4e0d\u8d77\uff0c\u5f53\u771f\u53ef\u60dc\u4e4b\u6781\u4e86\u201d\u4f17\u4eba\u9644\u548c\u53f9\u606f\uff0c\u4f55\u601d\u8c6a\u53c8\u9053\uff1a\u201c\u4e07\u8001\u62f3\u5e08\u867d\u7136\u8fc7\u4e16\uff0c\u4ff1\u5c11\u6797\u97e6\u9640\u95e8\u662f\u6b66\u6797\u4e2d\u6709\u540d\u7684\u5b97\u6d3e\uff0c\u638c\u95e8\u4eba\u4e0d\u53ef\u4e0d\u5230\uff0c\u4e0d\u77e5\u8d35\u95e8\u7684\u638c\u95e8\u4eba\u7531\u54ea\u4e00\u4f4d\u7ee7\u4efb\uff1f\u201d \u3000\u3000\u5b59\u4f0f\u864e\u7b49\u5e08\u5144\u5f1f\u4e09\u4eba\u4e92\u89c6\u4e00\u773c\uff0c\u5404\u4e0d\u4f5c\u58f0\u3002\u8fc7\u4e86\u534a\u664c\u4e09\u5e08\u5f1f\u6768\u5bbe\u8bf4\u9053\uff1a\u201c\u5e08\u7236\u5f97\u7684\u662f\u4e2d\u98ce\u4e4b\u75c7\uff0c\u4e00\u53d1\u4f5c\u4fbf\u4eba\u4e8b\u4e0d\u77e5\uff0c\u662f\u4ee5\u6ca1\u7559\u4e0b\u9057\u8a00\u3002\u201d\u53e6\u4e00\u540d\u4f8d\u536b\u9053\uff1a\u201c\u55ef\uff0c\u55ef\u3002\u8d35\u95e8\u7684\u524d\u8f88\u5c0a\u957f\uff0c\u5b9a\u662f\u6709\u4e00\u756a\u4e3b\u610f\u4e86\u3002\u201d\u4e8c\u5f1f\u5b50\u5c09\u8fdf\u8fde\u9053\uff1a\u201c\u6211\u4eec\u51e0\u4f4d\u5e08\u4f2f\u53d4\u6563\u5904\u5404\u5730\uff0c\u5411\u6765\u4e0d\u901a\u97f3\u95ee\u3002\u201d\u90a3\u4f8d\u536b\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u7acb\u638c\u95e8\u4e4b\u4e8b\uff0c\u5012\u8fd8\u5f97\u8d39\u4e00\u756a\u5468\u6298\u3002\u798f\u5927\u5e05\u4e3b\u6301\u7684\u638c\u95e8\u4eba\u5927\u4f1a\uff0c\u5b9a\u5728\u516b\u6708\u4e2d\u79cb\uff0c\u8ddd\u4eca\u8fd8\u6709\u4e24\u4e2a\u6708\uff0c\u8d35\u95e8\u987b\u5f97\u53ca\u65e9\u4e3a\u8ba1\u624d\u597d\u3002\u201d\u5e08\u5144\u5f1f\u4e09\u4eba\u9f50\u58f0\u79f0\u662f\u3002 \u3000\u3000\u4e00\u540d\u8001\u6b66\u5e08\u9053\uff1a\u201c\u81ea\u6765\u4e0d\u7acb\u8d24\u4fbf\u7acb\u957f\uff0c\u4e07\u8001\u62f3\u5e08\u65e2\u65e0\u9057\u8a00\uff0c\u90a3\u638c\u95e8\u4e00\u5e2d\uff0c\u81ea\u975e\u5927\u5f1f\u5b50\u5b59\u5e08\u5144\u83ab\u5c5e\u3002\u201d\u5b59\u4f0f\u864e\u7b11\u4e86\u7b11\uff0c\u795e\u8272\u4e4b\u95f4\u751a\u662f\u5f97\u610f\u3002\u53e6\u4e00\u540d\u8001\u6b66\u5e08\u9053\uff1a\u201c\u7acb\u957f\u4e4b\u8a00\u662f\u4e0d\u9519\u7684\u3002\u53ef\u662f\u5b59\u5e08\u5144\u867d\u7136\u5165\u95e8\u8f83\u65e9\uff0c\u8bba\u5e74\u5c81\u5374\u662f\u8fd9\u4f4d\u5c09\u8fdf\u5e08\u5144\u5927\u7740\u4e00\u5c81\u3002\u5c09\u8fdf\u5e08\u5144\u8001\u6210\u7cbe\u5e72\uff0c\u97e6\u9640\u95e8\u82e5\u662f\u7531\u4ed6\u63a5\u638c\uff0c\u5b9a\u80fd\u53d1\u626c\u5149\u5927\uff0c\u4e07\u8001\u62f3\u5e08\u5728\u5929\u4e4b\u7075\uff0c\u4e5f\u5fc5\u6781\u4e3a\u6b23\u6170\u516b\u201d\u5c09\u8fdf\u8fde\u4f38\u8896\u64e6\u4e86\u64e6\u773c\uff0c\u663e\u5f97\u6000\u5ff5\u5e08\u7236\uff0c\u5fc3\u4e2d\u60b2\u621a\u3002\u7b2c\u4e09\u540d\u8001\u6b66\u5e08\u8fde\u8fde\u6447\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u7136\u4e0d\u7136\uff0c\u82e5\u5728\u5e73\u65e5\uff0c\u8001\u673d\u539f\u65e0\u8bdd\u53ef\u8bf4\u3002\u4f46\u8fd9\u756a\u5317\u4eac\u5927\u4f1a\uff0c\u5404\u95e8\u5404\u6d3e\u9f50\u663e\u795e\u901a\u3002\u97e6\u9640\u95e8\u638c\u95e8\u4eba\u5982\u4e0d\u80fd\u827a\u538b\u5f53\u573a\uff0c\u5c82\u4e0d\u662f\u574f\u4e86\u97e6\u9640\u95e8\u6570\u767e\u5e74\u7684\u82f1\u540d\uff1f\u56e0\u6b64\u4ee5\u8001\u673d\u4e4b\u89c1\uff0c\u8fd9\u4f4d\u638c\u95e8\u4eba\u987b\u5f97\u662f\u97e6\u9640\u95e8\u4e2d\u6b66\u529f\u7b2c\u4e00\u7684\u597d\u535e\u624b\uff0c\u65b9\u80fd\u62c5\u5f53\u3002\u201d\u8fd9\u756a\u8bdd\u8bf4\u5f97\u4f17\u4eba\u8fde\u8fde\u70b9\u9996\uff0c\u9f50\u58f0\u79f0\u662f\u3002 \u3000\u3000\u90a3\u8001\u6b66\u5e08\u53c8\u9053\uff1a\u201c\u4e09\u4f4d\u5e08\u5144\u90fd\u662f\u4e07\u8001\u62f3\u5e08\u7684\u5f97\u610f\u95e8\u751f\uff0c\u5404\u64c5\u7edd\u827a\uff0c\u6b66\u6797\u4e2d\u4eba\u4eba\u90fd\u662f\u5341\u5206\u94a6\u4f69\u7684\u3002\u4e0d\u8fc7\u8bf4\u5230\u51fa\u4e4e\u5176\u7c7b\uff0c\u62d4\u4e4e\u5176\u8403\uff0c\u90a3\u8fd8\u662f\u540e\u6765\u5c45\u4e0a\uff0c\u987b\u63a8\u5c0f\u5e08\u5f1f\u6768\u5bbe\u4e86\u3002\u201d\u7b2c\u4e00\u540d\u8001\u6b66\u5e08\u54fc\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u90a3\u4e5f\u672a\u5fc5\u3002\u6b66\u5b66\u4e4b\u9053\uff0c\u591a\u7ec3\u4e00\u5e74\uff0c\u529f\u592b\u4fbf\u6df1\u4e00\u5e74\uff0c\u6768\u5e08\u5144\u867d\u7136\u5929\u8d44\u806a\u9896\uff0c\u4f46\u5c31\u529f\u529b\u800c\u8a00\uff0c\u90a3\u662f\u8fdc\u8fdc\u4e0d\u53ca\u5b59\u5e08\u5144\u4e86\u3002\u5200\u67aa\u62f3\u811a\u4e0a\u89c1\u529f\u592b\uff0c\u8fd9\u662f\u4e1d\u6beb\u52c9\u5f3a\u6765\u7684\u3002\u201d\u7b2c\u4e8c\u540d\u8001\u6b66\u5e08\u9053\uff1a\u201c\u8bf4\u5230\u4e34\u9635\u53d6\u80dc\uff0c\u6597\u667a\u4e3a\u4e0a\uff0c\u6597\u529b\u5176\u6b21\uff0c\u5144\u5f1f\u867d\u662f\u5916\u4eba\uff0c\u4f46\u5e73\u5fc3\u800c\u8bba\uff0c\u8db3\u667a\u591a\u8c0b\uff0c\u8fd8\u8be5\u63a8\u5c09\u8fdf\u5e08\u5144\u3002\u201d \u3000\u3000\u4ed6\u4e09\u4eba\u4f60\u4e00\u53e5\uff0c\u6211\u4e00\u53e5\uff0c\u8d77\u521d\u8a00\u8bed\u4e2d\u90e8\u8fd8\u5ba2\u6c14\uff0c\u5230\u540e\u6765\u6e10\u6e10\u9762\u7ea2\u8033\u8d64\uff0c\u58f0\u97f3\u4e5f\u8d8a\u8bf4\u8d8a\u5927\u3002\u51e0\u5341\u684c\u7684\u5ba2\u4eba\u505c\u676f\u4e0d\u996e\uff0c\u542c\u4ed6\u4e09\u4eba\u4e89\u8bba\u3002\u80e1\u6590\u5fc3\u9053\uff1a\u201c\u539f\u6765\u4e09\u4e2a\u8001\u6b66\u5e08\u90fd\u662f\u53d7\u4eba\u4e4b\u6258\uff0c\u6765\u4f5c\u8bf4\u5ba2\u7684\uff0c\u8bf4\u4e0d\u5b9a\u8fd8\u5206\u522b\u53d7\u4e86\u4e09\u540d\u5f1f\u5b50\u7684\u597d\u5904\u3002\u201d \u3000\u3000\u540a\u5ba2\u4e4b\u4e2d\uff0c\u6709\u767e\u4f59\u4eba\u662f\u97e6\u9640\u95e8\u7684\u95e8\u4eba\uff0c\u5927\u90fd\u662f\u4e07\u8001\u62f3\u5e08\u7684\u518d\u4f20\u5f1f\u5b50\uff0c\u5404\u4eba\u62e5\u6234\u81ea\u5df1\u5e08\u7236\uff0c\u5148\u662f\u4f4e\u58f0\u8ba5\u8bbd\u4e89\u8fa9\uff0c\u5230\u540e\u6765\u5fcd\u4e0d\u4f4f\u5927\u58f0\u5435\u56b7\u8d77\u6765\u3002\u5404\u4eb2\u670b\u5bbe\u5ba2\u6216\u5206\u89e3\u529d\u963b\uff0c\u6216\u5404\u6292\u5df1\u89c1\uff0c\u6216\u8892\u62a4\u4ea4\u597d\uff0c\u6216\u6307\u65a5\u5bf9\u65b9\uff0c\u5927\u5385\u4e0a\u767b\u65f6\u4e71\u6210\u4e00\u7247\uff0c\u6709\u51e0\u4e2a\u813e\u6c14\u66b4\u8e81\u3001\u4e92\u6709\u5fc3\u75c5\u4e4b\u4eba\uff0c\u7adf\u62cd\u684c\u76f8\u9a82\u8d77\u6765\uff0c\u773c\u89c1\u4fbf\u8981\u62a1\u5200\u4f7f\u62f3\u3002\u4e07\u8001\u62f3\u5e08\u5c38\u9aa8\u672a\u5bd2\uff0c\u95e8\u4e0b\u7684\u5f92\u5f1f\u4fbf\u8981\u4e3a\u638c\u95e8\u4e00\u5e2d\u800c\u540c\u5ba4\u64cd\u6208\u4e86\u3002 \u3000\u3000\u90a3\u5750\u5728\u9996\u5e2d\u7684\u4f8d\u536b\u542c\u7740\u5404\u4eba\u4e89\u5435\uff0c\u5e76\u4e0d\u8bf4\u8bdd\uff0c\u671b\u7740\u4e07\u8001\u62f3\u5e08\u7684\u7075\u4f4d\uff0c\u53ea\u662f\u5fae\u7b11\uff0c\u773c\u89c1\u5404\u4eba\u8d8a\u95f9\u8d8a\u662f\u5389\u5bb3\uff0c\u7a81\u7136\u7ad9\u8d77\u8eab\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u5404\u4f4d\u4e14\u83ab\u4e89\u5435\uff0c\u8bf7\u542c\u5144\u5f1f\u4e00\u8a00\u3002\u201d\u4f17\u4eba\u656c\u4ed6\u662f\u5b98\uff0c\u4e00\u9f50\u4f4f\u53e3\u3002\u90a3\u6043\u536b\u9053\uff1a\u201c\u9002\u624d\u8fd9\u4f4d\u8001\u5e08\u8bf4\u5f97\u4e0d\u9519\uff0c\u97e6\u9640\u95e8\u638c\u95e8\u4eba\uff0c\u987b\u5f97\u662f\u672c\u95e8\u6b66\u529f\u4e4b\u9996\uff0c\u8fd9\u4e00\u8282\u5404\u4f4d\u90fd\u662f\u8d5e\u540c\u7684\u4e86\uff1f\u201d \u3000\u3000\u5927\u5bb6\u9f50\u58f0\u79f0\u662f\u3002\u90a3\u4f8d\u536b\u9053\uff1a\u201c\u6b66\u529f\u8c01\u9ad8\u51c6\u4f4e\uff0c\u5634\u5df4\u91cc\u662f\u4e89\u4e0d\u51fa\u6765\u7684\u3002\u5200\u67aa\u62f3\u811a\u4e00\u6bd4\uff0c\u7acb\u65f6\u4fbf\u5224\u5f3a\u5f31\u3002\u597d\u5728\u4e09\u4f4d\u662f\u540c\u95e8\u5e08\u5144\u5f1f\uff0c\u4e0d\u8bba\u80dc\u8d1f\uff0c\u90fd\u4e0d\u4f1a\u5931\u4e86\u548c\u6c14\uff0c\u66f4\u4e0d\u4f1a\u6298\u4e86\u97e6\u9640\u95e8\u7684\u5a01\u98ce\u3002\u54b1\u4eec\u4fbf\u8bf7\u4e07\u8001\u62f3\u5e08\u7684\u7075\u4f4d\u4e3b\u6301\u8fd9\u573a\u6bd4\u6b66\uff0c\u7531\u4ed6\u8001\u4eba\u5bb6\u5728\u5929\u4e4b\u7075\u62e9\u5b9a\u638c\u95e8\uff0c\u5012\u662f\u4e00\u6bb5\u6b66\u6797\u4f73\u8bdd\u5462\u3002\u201d \u3000\u3000\u4f17\u4eba\u542c\u4e86\uff0c\u4e00\u9f50\u559d\u91c7\uff0c\u7eb7\u7eb7\u9053\uff1a\u201c\u8fd9\u4e2a\u6700\u516c\u5e73\u4e0d\u8fc7\u3002\u201d\u201c\u8ba9\u5927\u5bb6\u89c1\u8bc6\u89c1\u8bc6\u97e6\u9640\u95e8\u7684\u7edd\u827a\u3002\u201d\u201c\u51ed\u6b66\u529f\u5206\u80dc\u8d25\uff0c\u4e8b\u540e\u518d\u65e0\u4e89\u8bba\u3002\u201d\u201c\u7a76\u7adf\u662f\u5317\u4eac\u6765\u7684\u4f8d\u536b\u8001\u7237\uff0c\u89c1\u8bc6\u9ad8\u4eba\u4e00\u7b49\u3002\u201d \u3000\u3000\u90a3\u4f8d\u536b\u89c1\u4f17\u4eba\u4e00\u81f4\u9644\u548c\u5176\u8bf4\uff0c\u795e\u60c5\u751a\u662f\u5f97\u610f\uff0c\u8bf4\u9053\uff1a\u201c\u540c\u95e8\u5e08\u5144\u5f1f\u8f83\u827a\u6bd4\u6b66\uff0c\u90a3\u662f\u5e73\u5e38\u4e4b\u6781\u7684\u4e8b\uff0c\u5144\u5f1f\u5374\u8981\u8bf7\u4e09\u4f4d\u5f53\u4f17\u7b54\u5141\u4e00\u4ef6\u4e8b\u3002\u201d\u5c09\u8fdf\u8fde\u5728\u5e08\u5144\u5f1f\u4e09\u4eba\u4e4b\u4e2d\u6700\u662f\u7cbe\u660e\u5e72\u7ec3\uff0c\u5f53\u5373\u8bf4\u9053\uff1a\u201c\u4f46\u51ed\u5927\u4eba\u5429\u5490\uff0c\u6211\u4eec\u5e08\u5144\u5f1f\u81ea\u5f53\u9075\u4ece\u3002\u201d\u90a3\u4f8d\u536b\u9053\uff1a\u201c\u65e2\u662f\u51ed\u6b66\u529f\u5206\u4e0a\u4e0b\uff0c\u90a3\u4e48\u6b66\u529f\u6700\u9ad8\u7684\u4fbf\u4e3a\u638c\u95e8\uff0c\u4e8b\u540e\u4efb\u51c6\u4e0d\u5f97\u518d\u6709\u5f02\u8a00\uff0c\u66f4\u8d77\u7eb7\u4e89\u3002\u201d\u4e09\u4eba\u9f50\u58f0\u9053\uff1a\u201d\u8fd9\u4e2a\u81ea\u7136\u3002\u201d\u4ed6\u4e09\u4eba\u6b66\u529f\u5404\u6709\u6240\u957f\uff0c\u5e38\u8a00\u9053\uff1a\u201d\u6587\u65e0\u7b2c\u4e00\uff0c\u6b66\u65e0\u7b2c\u4e8c\u3002\u201d\u5404\u4eba\u81ea\u5fd6\u867d\u7136\u5e76\u65e0\u5fc5\u80dc\u628a\u63e1\uff0c\u4f46\u594b\u529b\u4e00\u6218\uff0c\u672a\u59cb\u4fbf\u4e0d\u80fd\u538b\u670d\u4e24\u4e2a\u540c\u95e8\u3002 \u3000\u3000\u90a3\u4f8d\u536b\u9053\uff1a\u201c\u65e2\u662f\u5982\u6b64\uff0c\u5927\u4f19\u513f\u4fbf\u632a\u5730\u65b9\u51fa\u6765\uff0c\u8ba9\u5927\u5bb6\u77bb\u4ef0\u97e6\u9640\u95e8\u7684\u7cbe\u5999\u529f\u592b\u3002\u201d\u4f17\u4eba\u4e03\u624b\u516b\u811a\u642c\u5f00\u684c\u6905\uff0c\u5728\u7075\u4f4d\u524d\u817e\u51fa\u8001\u5927\u4e00\u7247\u7a7a\u5730\u3002\u773c\u89c1\u597d\u620f\u5f53\u524d\uff0c\u5404\u4eba\u5747\u5df2\u65e0\u5fc3\u996e\u98df\uff0c\u53ea\u6709\u5c11\u6570\u9955\u992e\u4e4b\u5f92\uff0c\u5140\u81ea\u4f4e\u5934\u5927\u56bc\u3002 \u3000\u3000\u90a3\u4f8d\u536b\u9053\uff1a\u201c\u54ea\u4e24\u4f4d\u5148\u4e0a\uff1f\u662f\u5b59\u5e08\u5144\u4e0e\u5c09\u8fdf\u5e08\u5144\u4e48\uff1f\u201d\u5b59\u4f0f\u864e\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u5144\u5f1f\u732e\u4e11\u3002\u201d\u65e9\u6709\u4ed6\u5f1f\u5b50\u9001\u4e0a\u4e00\u67c4\u5355\u5200\u3002\u5b59\u4f0f\u864e\u63a5\u5200\u5728\u624b\uff0c\u8d70\u5230\u5e08\u7236\u7075\u524d\u78d5\u4e86\u4e09\u4e2a\u5934\uff0c\u8f6c\u8eab\u8bf4\u9053\uff1a\u201c\u5c09\u8fdf\u5e08\u5f1f\u8bf7\u3000\u4e0a\u5427\u3002\u201d \u3000\u3000\u5c09\u8fdf\u8fde\u5fc3\u60f3\u82e5\u662f\u5148\u4e0e\u5927\u5e08\u5144\u52a8\u624b\uff0c\u80dc\u4e86\u4e4b\u540e\u8fd8\u5f97\u5bf9\u4ed8\u4e09\u5e08\u5f1f\uff0c\u4e0d\u5982\u8ba9\u4ed6\u4eec\u4e8c\u4eba\u5148\u6597\u4e2a\u7b4b\u75b2\u529b\u5c3d\uff0c\u81ea\u5df1\u518d\u6765\u535e\u5e84\u523a\u864e\uff0c\u6361\u4e2a\u73b0\u6210\uff0c\u4e8e\u662f\u62f1\u624b\u9053\uff1a\u201c\u5144\u5f1f\u6b66\u827a\u65e2\u4e0d\u53ca\u5e08\u5144\uff0c\u4e5f\u4e0d\u53ca\u5e08\u5f1f\uff0c\u8fd9\u4e2a\u638c\u95e8\u539f\u662f\u4e0d\u6562\u4e89\u7684\u3002\u53ea\u662f\u5404\u4f4d\u8001\u5e08\u6709\u547d\uff0c\u4e0d\u5f97\u4e0d\u52c9\u5f3a\u966a\u5e08\u5144\u5e08\u5f1f\u5582\u62db\uff0c\u8fd8\u662f\u6768\u5e08\u5f1f\u5148\u4e0a\u5427\u3002\u201d \u3000\u3000\u6768\u5bbe\u813e\u6c14\u66b4\u8e81\uff0c\u5927\u58f0\u9053\uff1a\u201c\u597d\uff0c\u7531\u6211\u5148\u4e0a\u4fbf\u4e86\u3002\u201d\u4ece\u5f1f\u5b50\u624b\u4e2d\u63a5\u8fc7\u5355\u5200\uff0c\u5927\u8e0f\u6b65\u4e0a\u524d\u3002\u4ed6\u4e5f\u4e0d\u77e5\u8be5\u5f53\u5148\u5411\u5e08\u7236\u7075\u4f4d\u78d5\u5934\uff0c\u5f53\u4e0b\u7acb\u4e2a\u95e8\u6237\uff0c\u53f3\u624b\u6301\u5200\u6a2a\u7f6e\u5de6\u80a9\uff0c\u5de6\u4e4e\u6210\u94a9\uff0c\u52b2\u5750\u53f3\u817f\uff0c\u5de6\u811a\u865a\u51fa\uff0c\u4e43\u662f\u516d\u5408\u5200\u6cd5\u7684\u8d77\u624b\u201c\u62a4\u80a9\u5200\u201d\u3002 \u3000\u3000\u5c11\u6797\u97e6\u9640\u95e8\u62f3\u3001\u5200\u3001\u67aa\u4e09\u7edd\uff0c\u5168\u5b88\u516d\u5408\u4e4b\u6cd5\u3002\u6240\u8c13\u516d\u5408\uff0c\u201c\u7cbe\u6c14\u795e\u201d\u4e3a\u5185\u4e09\u5408\uff0c\u201c\u624b\u773c\u8eab\u201d\u4e3a\u5916\u4e09\u5408\uff0c\u5176\u7528\u4e3a\u201c\u773c\u4e0e\u5fc3\u5408\uff0c\u5fc3\u4e0e\u6c14\u5408\uff0c\u6c14\u4e0e\u8eab\u5408\uff0c\u8eab\u4e0e\u624b\u5408\uff0c\u624b\u4e0e\u811a\u5408\uff0c\u811a\u4e0e\u80ef\u5408\u3002\u201d\u5168\u8eab\u5185\u5916\uff0c\u6d51\u7136\u4e00\u4f53\u3002\u5bbe\u5ba2\u4e2d\u6709\u4e0d\u5c11\u662f\u6b66\u5b66\u884c\u5bb6\uff0c\u89c1\u6768\u5bbe\u6a2a\u5200\u4e00\u7acb\uff0c\u795e\u5b9a\u6c14\u51dd\uff0c\u5747\u60f3\uff1a\u201c\u6b64\u4eba\u6b66\u529f\u4e0d\u5f31\u3002\u201d\u5b59\u4f0f\u864e\u5200\u85cf\u53f3\u4fa7\uff0c\u5de6\u624b\u6210\u638c\uff0c\u81ea\u6000\u91cc\u7ffb\u51fa\uff0c\u4f7f\u4e00\u62db\u201d\u201c\u6eda\u624b\u523a\u624e\u201d\uff0c\u8bf4\u9053\uff1a\u201c\u5e08\u5f1f\u8bf7\uff01\u201d \u3000\u3000\u4e0e\u80e1\u6590\u540c\u684c\u7684\u90a3\u4e2d\u5e74\u6b66\u5e08\u5356\u5f04\u5185\u884c\uff0c\u5411\u8eab\u65c1\u540e\u751f\u9053\uff1a\u201c\u5355\u5200\u770b\u7684\u662f\u624b\uff0c\u53cc\u5200\u770b\u7684\u662f\u8d70\u3002\u4f7f\u5355\u5200\u7684\u53f3\u624b\u6709\u5200\uff0c\u5200\u6709\u5200\u6cd5\uff0c\u5de6\u624b\u65e0\u7269\uff0c\u90a3\u4fbf\u5b89\u987f\u4e3a\u96be\u3002 \u3000\u3000\u56e0\u6b64\u770b\u4e00\u4eba\u7684\u5200\u4e0a\u529f\u592b\uff0c\u53ea\u8981\u77a7\u4ed6\u5de6\u624b\u51fa\u638c\u662f\u5426\u5389\u5bb3\uff0c\u4fbf\u77e5\u9ad8\u4f4e\u3002\u4f60\u77a7\u5b59\u5e08\u5144\u8fd9\u4e00\u638c\u7ffb\u5c06\u51fa\u6765\uff0c\u5b88\u4e2d\u6709\u653b\uff0c\u529f\u529b\u4f55\u7b49\u6df1\u539a\uff1f\u201d\u80e1\u6590\u542c\u4ed6\u8bf4\u5f97\u4e0d\u9519\uff0c\u5fae\u5fae\u70b9\u5934\u3002 \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u5e08\u5144\u5f1f\u4fe9\u5df2\u4ea4\u4e0a\u4e86\u624b\uff0c\u53cc\u5200\u76f8\u78b0\uff0c\u4e0d\u65f6\u53d1\u51fa\u53ee\u5f53\u4e4b\u58f0\u3002\u90a3\u4e2d\u5e74\u6b66\u5e08\u53c8\u9053\uff1a\u201c\u8fd9\u4e8c\u4eba\u5200\u6cd5\uff0c\u7528\u7684\u90fd\u662f\u2018\u5c55\u3001\u62b9\u3001\u94a9\u3001\u5241\u3001\u780d\u3001\u5288\u2019\u516d\u5b57\u8bc0\uff0c\u6cd5\u5ea6\u662f\u5f88\u4e0d\u9519\u7684\u3002\u201d\u90a3\u540e\u751f\u9053\uff1a\u201c\u4ec0\u4e48\u53eb\u505a\u94bb\u6bcd\u94a9\u809a\uff1f\u201d\u4e2d\u5e74\u6b66\u5e08\u51b7\u7b11\u4e00\u58f0\u9053\uff1a\u201c\u5200\u6cd5\u4e4b\u4e2d\uff0c\u8fd8\u6709\u94bb\u4ed6\u5988\u5988\u3001\u94a9\u4f60\u809a\u5b50\u4e48\uff1f\u5203\u53e3\u5411\u5916\u53eb\u505a\u5c55\uff0c\u5411\u5185\u4e3a\u62b9\uff0c\u66f2\u5203\u4e3a\u94a9\uff0c\u8fc7\u9876\u4e3a\u780d\uff0c\u53cc\u624b\u4e3e\u5200\u4e0b\u65a9\u53eb\u505a\u5288\uff0c\u5e73\u624b\u4e0b\u65a9\u79f0\u4e3a\u5241\u3002\u201d\u90a3\u540e\u751f\u80c0\u7ea2\u4e86\u8138\uff0c\u518d\u4e5f\u4e0d\u6562\u591a\u95ee\u3002 \u3000\u3000\u80e1\u6590\u867d\u7136\u5200\u6cd5\u7cbe\u5947\uff0c\u4f46\u4ed6\u7956\u4f20\u5200\u8c31\u4e4b\u4e2d\uff0c\u5168\u4e0d\u63d0\u8fd9\u4e9b\u7ec6\u81f4\u5206\u522b\uff0c\u6ce8\u91cd\u7684\u53ea\u662f\u62a4\u8eab\u4f24\u654c\u8bf8\u822c\u7cbe\u5999\u53d8\u62db\uff0c\u8fd9\u65f6\u542c\u90a3\u4e2d\u5e74\u6b66\u5e08\u8bf4\u5f97\u5934\u5934\u662f\u9053\uff0c\u5fc3\u60f3\uff1a\u201c\u539f\u6765\u5200\u6cd5\u4e4b\u4e2d\u8fd8\u6709\u8fd9\u8bb8\u591a\u8bb2\u7a76\u3002\u4f46\u77a7\u8fd9\u5e08\u5144\u5f1f\u4fe9\u7684\u5200\u62db\uff0c\u4e5f\u65e0\u4ec0\u4e48\u7279\u5f02\u4e4b\u5904\u3002\u201d \u3000\u3000\u773c\u89c1\u4e8c\u4eba\u8d8a\u6597\u8d8a\u7d27\uff0c\u5b59\u4f0f\u864e\u77eb\u6377\u7075\u6d3b\uff0c\u6768\u5bbe\u5374\u80dc\u5728\u8155\u529b\u6c89\u96c4\uff0c\u4e00\u65f6\u5012\u4e5f\u96be\u5206\u4e0a\u4e0b\u3002\u6b63\u6597\u4e4b\u95f4\uff0c\u5927\u95e8\u5916\u7a81\u7136\u8d70\u8fdb\u4e00\u4eba\uff0c\u5c16\u58f0\u8bf4\u9053\uff1a\u201c\u97e6\u9640\u95e8\u7684\u5200\u6cd5\uff0c\u54ea\u6709\u8fd9\u7b49\u8113\u5305\u7684\uff0c\u7094\u522b\u73b0\u4e16\u4e86\u5427\uff01\u201d\u5b59\u6768\u4e8c\u4eba\u4e00\u60ca\uff0c\u540c\u65f6\u6536\u5200\u8dc3\u5f00\u3002 \u3000\u3000\u80e1\u6590\u65e9\u5df2\u770b\u6e05\u6765\u4eba\u662f\u4e2a\u5999\u9f84\u5c11\u5973\uff0c\u4f46\u89c1\u5979\u8eab\u7a7f\u7d2b\u8863\uff0c\u8eab\u6750\u82d7\u6761\uff0c\u6b63\u662f\u9014\u4e2d\u6240\u9047\u90a3\u4e2a\u9a91\u767d\u9a6c\u7684\u5973\u5b50\u3002\u5979\u80cc\u4e0a\u8d1f\u7740\u4e00\u4e2a\u5305\u88b1\uff0c\u5374\u4e0d\u662f\u81ea\u5df1\u5728\u996d\u94fa\u4e2d\u6240\u5931\u7684\u662f\u4ec0\u4e48\uff1f\u53ea\u89c1\u5979\u4e00\u5f20\u74dc\u5b50\u8138\uff0c\u53cc\u7709\u4fee\u957f\uff0c\u80a4\u8272\u867d\u7136\u5fae\u9ed1\uff0c\u5374\u63a9\u4e0d\u4e86\u59ff\u5f62\u79c0\u4e3d\uff0c\u5bb9\u5149\u7167\u4eba\uff0c\u4e0d\u7981\u5927\u662f\u60ca\u8bb6\uff1a\u201c\u8fd9\u5973\u5b50\u5e74\u7eaa\u548c\u6211\u76f8\u82e5\uff0c\u96be\u9053\u4fbf\u6709\u4e00\u8eab\u6781\u9ad8\u6b66\u529f\uff0c\u5982\u6b64\u8f7b\u8f7b\u5de7\u5de7\u5730\u53d6\u53bb\u5305\u88b1\uff0c\u7adf\u4f7f\u6211\u4e1d\u6beb\u4e0d\u89c9\uff1f\u201d \u3000\u3000\u5b59\u6768\u4e8c\u4eba\u542c\u6765\u4eba\u53e3\u51fa\u72c2\u8a00\uff0c\u672c\u6765\u5747\u5df2\u5927\u6012\uff0c\u4f46\u505c\u5200\u4e00\u770b\uff0c\u5374\u662f\u4e2a\u5a09\u5a77\u8885\u5a1c\u7684\u5973\u90ce\uff0c\u6115\u7136\u4e4b\u4e0b\uff0c\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u90a3\u5973\u90ce\u9053\uff1a\u201c\u516d\u5408\u5200\u6cd5\uff0c\u7cbe\u8981\u5168\u5728\u2018\u865a\u3001\u5b9e\u3001\u5de7\u3001\u6253\u2019\u56db\u5b57\u3002\u4f60\u4eec\u8fd9\u822c\u7b28\u5288\u86ee\u780d\uff0c\u8fd8\u63d0\u4ec0\u4e48\u97e6\u9640\u95e8\uff1f\u4ec0\u4e48\u516d\u53f0\u5200\uff1f\u60f3\u4e0d\u5230\u4e07\u8001\u62f3\u5e08\u82f1\u540d\u8fdc\u64ad\uff0c\u7adf\u8c03\u6559\u4e86\u8fd9\u7b49\u5f1f\u5b50\u51fa\u6765\u3002\u201d\u5979\u58f0\u97f3\u723d\u8106\u6e05\u4eae\uff0c\u4eba\u4eba\u5747\u89c9\u52a8\u542c\u4e4b\u81f3\u3002 \u3000\u3000\u8bf4\u8fd9\u756a\u8bdd\u7684\u5982\u662f\u4e00\u4e2a\u6c49\u5b50\uff0c\u5b59\u6768\u4e8c\u4eba\u65e9\u5df2\u53d1\u8bdd\u52a8\u624b\uff0c\u7136\u800c\u89c1\u8fd9\u5973\u90ce\u7ea4\u8170\u524a\u80a9\uff0c\u5b9b\u4f3c\u5f31\u4e0d\u7981\u98ce\uff0c\u54ea\u91cc\u662f\u4e2a\u4f1a\u6b66\u4e4b\u4eba\uff1f\u4f46\u542c\u5979\u8bf4\u51fa\u516d\u5408\u5200\u6cd5\u90a3\u201c\u865a\u3001\u5b9e\u3001\u5de7\u3001\u6253\u201d\u56db\u5b57\u8bc0\uff0c\u5374\u53c8\u4e00\u70b9\u4e0d\u9519\uff0c\u4e00\u65f6\u4e0d\u77e5\u5982\u4f55\u5bf9\u541d\u3002 \u3000\u3000\u5c09\u8fdf\u8fde\u8d70\u4e0a\u524d\u53bb\uff0c\u62b1\u62f3\u8bf4\u9053\uff1a\u201c\u8bf7\u6559\u59d1\u5a18\u5c0a\u59d3\u5927\u540d\u3002\u201d\u90a3\u5973\u90ce\u54fc\u4e86\u4e00\u58f0\uff0c\u5e76\u4e0d\u56de\u7b54\u3002\u5c09\u8fdf\u8fde\u9053\uff1a\u201c\u655d\u95e8\u4eca\u65e5\u5728\u5148\u5e08\u7075\u524d\u9009\u7acb\u638c\u95e8\u3002\u8bf7\u59d1\u5a18\u4e0a\u5750\u89c2\u793c\u3002\u201d \u3000\u3000\u8bf4\u7740\u53f3\u624b\u4e00\u4f38\uff0c\u8bf7\u5979\u5c31\u5750\u3002 \u3000\u3000\u90a3\u5973\u90ce\u79c0\u7709\u5fae\u7ad6\uff0c\u8bf4\u9053\uff1a\u201c\u5c11\u6797\u97e6\u9640\u95e8\u662f\u6b66\u6797\u4e2d\u6709\u540d\u95e8\u6d3e\uff0c\u5374\u4ece\u8fd9\u4e9b\u4eba\u4e2d\u9009\u7acb\u638c\u95e8\uff0c\u5c82\u4e0d\u5815\u4e86\u65e0\u76f8\u5927\u5e08\u4ee5\u4e0b\u5217\u7956\u7684\u5a01\u540d\uff1f\u201d\u6b64\u8a00\u4e00\u51fa\uff0c\u5385\u4e0a\u6c5f\u6e56\u524d\u8f88\u90fd\u662f\u5fae\u5fae\u4e00\u60ca\u3002\u539f\u6765\u65e0\u76f8\u5927\u5e08\u662f\u5c11\u6797\u5bfa\u7684\u5f97\u9053\u9ad8\u50e7\uff0c\u5f53\u5e74\u7cbe\u7814\u97e6\u9640\u6775\u548c\u516d\u5408\u62f3\u6cd5\uff0c\u4e43\u662f\u97e6\u9640\u95e8\u7684\u5f00\u5c71\u7956\u5e08\uff0c\u60f3\u4e0d\u5230\u8fd9\u4e00\u4e2a\u5f31\u8d28\u5c11\u5973\uff0c\u7adf\u4e5f\u77e5\u9053\u8fd9\u4ef6\u6b66\u6797\u638c\u6545\u3002 \u3000\u3000\u5c09\u8fdf\u8fde\u62b1\u62f3\u9053\uff1a\u201c\u59d1\u5a18\u5949\u54ea\u4e00\u4f4d\u524d\u8f88\u4e4b\u547d\u800c\u6765\uff1f\u5bf9\u655d\u95e8\u6709\u4f55\u6307\u6559\uff1f\u201d\u4ed6\u4e00\u76f4\u8bf4\u8bdd\u5ba2\u6c14\uff0c\u4f46\u5b59\u4f0f\u864e\u4e0e\u6768\u5bbe\u65e9\u5df2\u5927\u4e0d\u8010\u70e6\uff0c\u53ea\u662f\u542c\u90a3\u5973\u90ce\u51fa\u8bed\u60ca\u4eba\uff0c\u8fd9\u624d\u6682\u4e0d\u53d1\u4f5c\u3002 \u3000\u3000\u90a3\u5973\u90ce\u9053\uff1a\u201c\u6211\u81ea\u5df1\u8981\u6765\u4fbf\u6765\uff0c\u4f55\u5fc5\u5949\u4eba\u4e4b\u547d\uff1f\u6211\u548c\u97e6\u9640\u95e8\u6709\u70b9\u513f\u6e0a\u6e90\uff0c\u89c1\u8fd9\u91cc\u95f9\u5f97\u592a\u4e0d\u6210\u8bdd\uff0c\u4e0d\u5f97\u4e0d\u6765\u8bf4\u51e0\u53e5\u8bdd\u3002\u201d \u3000\u3000\u8fd9\u65f6\u6768\u5bbe\u518d\u4e5f\u5fcd\u8010\u4e0d\u4f4f\uff0c\u5927\u58f0\u9053\uff1a\u201c\u4f60\u8ddf\u97e6\u9640\u95e8\u6709\u4ec0\u4e48\u6e0a\u6e90\uff1f\u8c01\u4e5f\u4e0d\u8ba4\u5f97\u4f60\u662f\u8001\u51e0\u3002\u6211\u4eec\u6b63\u6709\u8981\u4e8b\uff0c\u5feb\u7ad9\u5f00\u4e9b\uff0c\u522b\u5728\u8fd9\u513f\u788d\u624b\u788d\u811a\uff01\u201d\u8f6c\u5934\u5411\u5b59\u4f0f\u864e\u9053\uff1a\u201c\u5927\u5e08\u5144\uff0c\u54b1\u54e5\u513f\u4fe9\u80dc\u8d25\u672a\u5206\uff0c\u518d\u6765\u5427\u3002\u201d\u5de6\u6b65\u8e0f\u51fa\uff0c\u5355\u5200\u5e73\u7f6e\u8170\u9645\uff0c\u4fbf\u6b32\u51fa\u62db\u3002 \u3000\u3000\u90a3\u5973\u90ce\u9053\uff1a\u201c\u8fd9\u4e00\u62db\u2018\u6a2a\u8eab\u62e6\u8170\u65a9\u201d\uff0c\u865a\u6b65\u8e0f\u5f97\u592a\u5b9e\uff0c\u51dd\u6b65\u53c8\u7ad9\u5f97\u4e0d\u7a33\uff0c\u76ee\u5149\u4e0d\u770b\u5bf9\u65b9\uff0c\u5374\u659c\u89c6\u77a7\u7740\u6211\u3002\u9519\u4e86\uff0c\u9519\u4e86\u3002\u201d\u5b59\u4f0f\u864e\u3001\u5c09\u8fdf\u8fde\u3001\u6768\u5bbe\u4e09\u4eba\u5747\u662f\u4e00\u6014\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u51e0\u53e5\u8bdd\u5bf9\u95e8\u5bf9\u8def\uff0c\u6b63\u5982\u5f53\u65e5\u5e08\u7236\u6559\u62db\u7684\u8bf4\u8bdd\uff0c\u83ab\u975e\u5979\u771f\u4f1a\u516d\u5408\u5200\u6cd5\u5417\uff1f\u201d \u3000\u3000\u4f55\u601d\u8c6a\u542c\u90a3\u5973\u90ce\u4e0e\u5c09\u8fdf\u8fde\u5bf9\u7b54\uff0c\u4e00\u76f4\u9ed8\u4e0d\u4f5c\u58f0\uff0c\u8fd9\u65f6\u63d2\u53e3\u8bf4\u9053\uff1a\u201c\u59d1\u5a18\u6765\u6b64\u6709\u4f55\u8d35\u5e72\uff1f\u5c0a\u5e08\u662f\u54ea\u4e00\u4f4d\uff1f\u201d\u90a3\u5973\u90ce\u5e76\u4e0d\u56de\u7b54\u4ed6\u7684\u95ee\u8bdd\uff0c\u5374\u53cd\u95ee\u9053\uff1a\u201c\u4eca\u65e5\u5c11\u6797\u97e6\u9640\u95e8\u9009\u7acb\u638c\u95e8\uff0c\u662f\u4e5f\u4e0d\u662f\uff1f\u201d\u4f55\u601d\u8c6a\u9053\uff1a\u201c\u662f\u554a\uff01\u201d\u90a3\u5973\u90ce\u53c8\u9053\uff1a\u201c\u53ea\u8981\u662f\u672c\u95e8\u4e2d\u4eba\uff0c\u8c01\u7684\u6b66\u529f\u6700\u5f3a\uff0c\u8c01\u4fbf\u6267\u638c\u95e8\u6d3e\uff0c\u65c1\u4eba\u4e0d\u5f97\u5f02\u8a00\uff0c\u662f\u4e5f\u4e0d\u662f\uff1f\u201d\u4f55\u601d\u8c6a\u9053\uff1a\u201d\u6b63\u662f\uff01\u201d\u90a3\u5973\u90ce\u9053\uff1a\u201c\u5f88\u597d\uff01\u6211\u4eca\u65e5\u662f\u62a2\u97e6\u9640\u95e8\u7684\u638c\u95e8\u4eba\u6765\u5566\u3002\u201d \u3000\u3000\u4f17\u4eba\u89c1\u5979\u8138\u8272\u90d1\u91cd\uff0c\u8bf4\u5f97\u4e00\u672c\u6b63\u7ecf\uff0c\u4e0d\u7981\u6115\u7136\u76f8\u987e\u3002\u4f55\u601d\u8c6a\u89c1\u8fd9\u5973\u90ce\u751f\u5f97\u7f8e\u4e3d\uff0c\u5012\u8d77\u4e86\u4e00\u756a\u60dc\u7389\u601c\u9999\u4e4b\u610f\uff0c\u7b11\u9053\uff1a\u201c\u59d1\u5a18\u82e5\u662f\u4e5f\u7ec3\u8fc7\u6b66\u827a\uff0c\u5f85\u4f1a\u8bf7\u4f60\u6f14\u4e00\u8def\u62f3\u811a\uff0c\u597d\u8ba9\u5927\u5bb6\u5f00\u5f00\u773c\u754c\u3002\u73b0\u4e0b\u5148\u8ba9\u4ed6\u4eec\u4e09\u4f4d\u5e08\u5144\u5f1f\u5206\u4e2a\u9ad8\u4f4e\u5982\u4f55\uff1f\u201d \u3000\u3000\u90a3\u5973\u90ce\u54fc\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4ed6\u4eec\u4e0d\u5fc5\u518d\u6bd4\u4e86\uff0c\u4e00\u4e2a\u4e2a\u8ddf\u6211\u6bd4\u4fbf\u662f\uff0c\u201d\u5979\u4e0b\u6307\u97e6\u9640\u95e8\u7684\u4e00\u540d\u5f1f\u5b50\uff0c\u8bf4\u9053\uff1a\u201c\u628a\u5200\u60dc\u7ed9\u6211\u4e00\u7528\u3002\u201d\u5979\u867d\u5e74\u8f7b\u7ea4\u5f31\uff0c\u4f46\u8bf4\u8bdd\u7684\u795e\u6001\u4e4b\u4e2d\u81ea\u6709\u4e00\u80a1\u5a01\u4e25\uff0c\u7adf\u4ee4\u4eba\u4e0d\u6613\u6297\u62d2\u3002\u90a3\u5f1f\u5b50\u7a0d\u4e00\u8fdf\u7591\uff0c\u5c06\u5200\u9012\u4e86\u8fc7\u53bb\uff0c\u53ef\u662f\u4ed6\u4e95\u975e\u5012\u8f6c\u5200\u67c4\uff0c\u800c\u662f\u5200\u5c16\u5411\u7740\u5973\u90ce\u3002 \u3000\u3000\u90a3\u5973\u90ce\u4f38\u51fa\u4e24\u6307\uff0c\u8f7b\u8f7b\u631f\u4f4f\u5200\u80cc\uff0c\u8f7b\u8f7b\u63d0\u8d77\uff0c\u4e00\u6839\u5c0f\u6307\u5fae\u5fae\u7fd8\u51fa\uff0c\u5012\u4f3c\u662f\u95fa\u4e2d\u523a\u7ee3\u65f6\u7684\u5170\u82b1\u624b\u4e00\u822c\u3002 \u3000\u3000\u5979\u4e24\u6307\u60ac\u7a7a\u63d0\u7740\u5355\u5200\uff0c\u51b7\u7136\u9053\uff1a\u201c\u662f\u4e24\u4f4d\u4e00\u8d77\u4e0a\u4e48\uff1f\u201d \u3000\u3000\u6768\u5bbe\u867d\u7136\u9c81\u83bd\uff0c\u4f46\u81ea\u6765\u77a7\u4e0d\u8d77\u5973\u5b50\uff0c\u5fc3\u60f3\u597d\u7537\u4e0d\u4e0e\u5973\u6597\uff0c\u6211\u5802\u5802\u7537\u5b50\u6c49\uff0c\u5c82\u80fd\u8ddf\u5a18\u513f\u4eec\u52a8\u624b\uff1f\u4f55\u51b5\u8fd9\u5973\u90ce\u75af\u75af\u766b\u766b\uff0c\u5012\u6709\u51e0\u5206\u90aa\u95e8\uff0c\u8fd8\u662f\u522b\u7406\u5979\u4e3a\u5999\uff0c\u4e8e\u662f\u63d0\u5200\u9000\u5f00\uff0c\u8bf4\u9053\uff1a\u201c\u5927\u5e08\u54e5\uff0c\u4f60\u6253\u53d1\u4e86\u5979\u5427\uff01\u201d\u5b59\u4f19\u864e\u4e5f\u81ea\u72b9\u8c6b\uff0c\u9053\uff1a\u201c\u4e0d\uff0c\u4e0d\u2026\u2026\u201d \u3000\u3000\u4ed6\u4e00\u8a00\u672a\u6bd5\uff0c\u90a3\u5973\u90ce\u53eb\u9053\uff1a\u201c\u71d5\u5b50\u63a0\u6c34\uff01\u201d\u53f3\u624b\u4e24\u6839\u624b\u6307\u4e00\u677e\uff0c\u5355\u5200\u4e0b\u6389\uff0c\u624b\u638c\u4e00\u6c89\uff0c\u5df2\u6293\u4f4f\u4e86\u5200\u67c4\uff0c\u5de6\u624b\u6276\u7740\u53f3\u8155\uff0c\u5203\u53e3\u81ea\u4e0b\u5411\u4e0a\u63a0\u8d77\uff0c\u5de6\u624b\u6210\u94a9\uff0c\u8eab\u5b50\u5fae\u5fae\u5411\u540e\u4e00\u5750\u3002\u8fd9\u4e00\u5200\u6b63\u662f\u97e6\u9640\u95e8\u6b63\u5b97\u7684\u516d\u5408\u5200\u6cd5\u3002 \u3000\u3000\u5b59\u4f0f\u864e\u6599\u4e0d\u5230\u5979\u51fa\u62db\u5982\u6b64\u8fc5\u6377\uff0c\u4f46\u8fd9\u4e00\u8def\u5200\u6cd5\u4ed6\u6d78\u6deb\u4e8c\u5341\u4f59\u5e74\uff0c\u5df2\u7ec3\u5f97\u719f\u5230\u65e0\u53ef\u518d\u719f\uff0c\u5f53\u4e0b\u8fd8\u4e86\u4e00\u62db\u201c\u91d1\u9501\u5760\u5730\u201d\u3002\u90a3\u5973\u90ce\u9053\uff1a\u201c\u5173\u5e73\u732e\u5370\u3002\u201d\u7ffb\u8f6c\u5200\u5203\uff0c\u5411\u4e0a\u633a\u4e3e\u3002\u6309\u7406\u5979\u65e2\u4f7f\u4e86\u201c\u71d5\u5b50\u63a0\u6c34\u201d\u5355\u5200\u81ea\u4e0b\u5411\u4e0a\uff0c\u90a3\u4e48\u63a5\u4e0b\u53bb\u7684\u7b2c\u4e8c\u62db\u4e07\u4e07\u4e0d\u8be5\u518d\u4f7f\u201c\u5173\u5e73\u732e\u5370\u201d\uff0c\u4ecd\u662f\u81ea\u4e0b\u5411\u4e0a\u3002\u54ea\u77e5\u5979\u8fd9\u4e00\u62db\u5200\u8eab\u5fae\u659c\uff0c\u4e3e\u5200\u8fc7\u9876\uff0c\u7a81\u7136\u751f\u51fa\u5947\u62db\uff0c\u5203\u53e3\u9661\u6a2a\u3002\u5b59\u4f0f\u864e\u5413\u4e86\u4e00\u8df3\uff0c\u6025\u5fd9\u4f4e\u5934\u3002\u90a3\u5973\u90ce\u53c8\u53eb\u9053\uff1a\u201c\u51e4\u51f0\u65cb\u7a9d\uff01\u201d\u5de6\u624b\u500f\u51fa\uff0c\u5728\u5b59\u4f0f\u864e\u5b50\u8155\u4e0a\u4e00\u51fb\uff0c\u5355\u5200\u81ea\u4e0a\u5411\u4e0b\u6025\u65a9\u3002 \u3000\u3000\u53ea\u542c\u5f53\u7684\u4e00\u58f0\uff0c\u5b59\u4f0f\u864e\u5355\u5200\u843d\u5730\uff0c\u5973\u90ce\u7684\u5355\u5200\u5374\u5df2\u67b6\u5728\u4ed6\u7684\u9888\u4e2d\uff0c\u65c1\u89c2\u4f17\u4eba\u201c\u554a\u201d\u7684\u4e00\u4e0b\uff0c\u9f50\u58f0\u60ca\u547c\uff0c\u773c\u89c1\u5979\u4e00\u5200\u6025\u65a9\uff0c\u5b59\u4f0f\u864e\u4fbf\u8981\u4eba\u5934\u843d\u5730\u3002\u54ea\u77e5\u8fd9\u4e00\u5200\u75be\u6325\u800c\u4e0b\uff0c\u52bf\u9053\u6781\u731b\u70c8\uff0c\u5374\u5ffd\u5730\u6536\u4f4f\uff0c\u5203\u53e3\u521a\u597d\u4e0e\u4ed6\u5934\u9888\u76f8\u89e6\uff0c\u8fde\u9888\u76ae\u4e5f\u4e0d\u5212\u7834\u534a\u70b9\u3002\u8fd9\u624b\u529f\u592b\u771f\u662f\u532a\u5937\u6240\u601d\u3002 \u3000\u3000\u80e1\u6590\u53ea\u77a7\u5f97\u5fc3\u4e2d\u6026\u6026\u4e71\u8df3\uff0c\u81ea\u5fd6\u8981\u4e09\u62db\u4e4b\u5185\u6253\u8d25\u5b59\u4f0f\u864e\u5e76\u4e0d\u4e3a\u96be\uff0c\u4f46\u6700\u540e\u4e00\u5200\u52b2\u529b\u62ff\u634f\u5982\u6b64\u4e4b\u51c6\uff0c\u81ea\u5df1\u53ea\u6015\u5c1a\u662f\u6709\u6240\u4e0d\u53ca\u3002\u5385\u4e0a\u4f17\u4eba\u4e4b\u4e2d\uff0c\u4e2a\u6765\u53ea\u6709\u4ed6\u4e00\u4eba\u77e5\u9053\u90a3\u5973\u90ce\u6b66\u529f\u5e7f\u5f97\uff0c\u4f46\u7ecf\u6b64\u4e09\u62db\uff0c\u4eba\u4eba\u6865\u820c\u4e0d\u4e0b\u3002 \u3000\u3000\u5b59\u4f0f\u864e\u5934\u4e00\u6c89\uff0c\u60f3\u8981\u907f\u5f00\u5203\u950b\uff0c\u5c82\u77e5\u5973\u90ce\u7684\u5355\u5200\u987a\u52bf\u8ddf\u4e86\u4e0b\u6765\uff1a\u5b59\u4f0f\u864e\u672c\u5df2\u5f2f\u8170\u4f4e\u5934\uff0c\u6b64\u65f6\u989d\u89d2\u513f\u6b32\u89e6\u5730\uff0c\u72b9\u4f3c\u5411\u90a3\u5973\u90ce\u78d5\u5934\u3002\u4ed6\u7a7a\u6709\u4e00\u8eab\u6b66\u529f\uff0c\u5229\u5203\u52a0\u9888\uff0c\u7ade\u662f\u534a\u5206\u52a8\u5f39\u4e0d\u5f97\u3002 \u3000\u3000\u90a3\u5973\u90ce\u5411\u4f17\u4eba\u73af\u89c6\u4e00\u773c\uff0c\u6536\u8d77\u5355\u5200\uff0c\u9053\uff1a\u201c\u4f60\u7ec3\u8fc7\u51e4\u51f0\u65cb\u7a9d\u2019\u8fd9\u4e00\u62db\u6ca1\u6709\uff1f\u201d\u5b59\u4f0f\u864e\u7ad9\u76f4\u8eab\u5b50\uff0c\u4f4e\u5934\u9053\uff1a\u201c\u7ec3\u8fc7\u3002\u201d\u5fc3\u60f3\uff1a\u201c\u8fd9\u4e00\u62db\u6211\u751f\u5e73\u4e0d\u77e5\u4f7f\u8fc7\u51e0\u5343\u51e0\u4e07\u904d\uff0c\u4f46\u4ece\u6765\u6ca1\u8fd9\u6837\u7528\u6cd5\uff0c\u201d\u60ca\u7591\u4e4b\u4e0b\uff0c\u5fc3\u4e2d\u4e71\u6210\u4e00\u7247\uff0c\u63d0\u5200\u9000\u5f00\u3002 \u3000\u3000\u6768\u5bbe\u89c1\u90a3\u5973\u90ce\u4e09\u62db\u4fbf\u5c06\u5927\u5e08\u5144\u5236\u670d\uff0c\u7a81\u7136\u8d77\u4e86\u7591\u5fc3\uff1a\u201c\u83ab\u975e\u5927\u5e08\u5144\u6446\u4e0b\u8be1\u8ba1\uff0c\u8981\u593a\u638c\u95e8\uff0c\u6545\u610f\u548c\u8fd9\u5973\u5b50\u4e32\u901a\u4e86\u6765\u88c5\u795e\u88c5\u9b3c\uff1f\u201d\u4ed6\u8d8a\u60f3\u8d8a\u5bf9\uff0c\u5927\u58f0\u8d28\u95ee\u9053\uff1a\u201c\u5927\u5e08\u54e5\uff0c\u4f60\u4e09\u62db\u4fbf\u8ba9\u4e86\u4eba\u5bb6\uff0c\u90a3\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u6211\u97e6\u9640\u95e8\u7684\u5a01\u540d\u4e5f\u4e0d\u987e\u4e86\u5417\uff1f\u201d\u5b59\u4f0f\u864e\u60ca\u9b42\u672a\u5b9a\uff0c\u4e5f\u4e0d\u77e5\u600e\u5730\u80e1\u91cc\u80e1\u6d82\u7684\u4fbf\u8ba9\u4eba\u5bb6\u5236\u5728\u5730\u4e0b\uff0c\u4e00\u65f6\u65e0\u8a00\u53ef\u7b54\uff0c\u53ea\u662f\u7ed3\u7ed3\u5df4\u5df4\u5730\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u2026\u2026\u201d\u6768\u5bbe\u6012\u9053\uff1a\u201c\u6211\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u63d0\u5200\u8dc3\u51fa\uff0c\u621f\u6307\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u2026\u2026\u201d \u3000\u3000\u53ea\u8bf4\u4e86\u4e24\u4e2a\u5b57\uff0c\u773c\u524d\u7a81\u89c1\u767d\u5149\u4e00\u95ea\uff0c\u90a3\u5973\u90ce\u7684\u5355\u5200\u81ea\u4e0b\u800c\u4e0a\u63a0\u4e86\u8fc7\u6765\uff0c\u5979\u5200\u6cd5\u592a\u5feb\uff0c\u7adf\u662f\u77a7\u4e0d\u6e05\u695a\uff0c\u4f9d\u7a00\u4f3c\u662f\u4e00\u62db\u201c\u71d5\u5b50\u63a0\u6c34\u201d\u3002\u6768\u5bbe\u5fd9\u4e71\u4e4b\u4e2d\uff0c\u987a\u624b\u8fd8\u4e86\u4e00\u62db\u201c\u91d1\u9501\u5760\u5730\u201d\uff0c\u8fd9\u662f\u4ed6\u5728\u5e08\u95e8\u4e2d\u7ec3\u719f\u4e86\u7684\u5957\u5b50\u3002\u90a3\u5973\u90ce\u4e0d\u7b49\u53cc\u5203\u76f8\u4ea4\uff0c\u5355\u5200\u53c8\u662f\u4e00\u4e3e\uff0c\u53d8\u4e3a\u201c\u5173\u5e73\u732e\u5370\u201d\uff0c\u8ddf\u7740\u659c\u5200\u6a2a\u51fa\u3002\u6768\u5bbe\u5413\u4e86\u4e00\u8df3\uff0c\u5927\u53eb\u9053\uff1a\u201c\u51e4\u51f0\u65cb\u7a9d\u3002\u201d\u8bed\u58f0\u672a\u6bd5\uff0c\u53ea\u89c9\u624b\u8155\u4e00\u9ebb\uff0c\u624b\u4e2d\u5355\u5200\u843d\u5730\uff0c\u5bf9\u65b9\u7684\u94a2\u5200\u5df2\u67b6\u5728\u81ea\u5df1\u9888\u4e0a\u3002 \u3000\u3000\u90a3\u5973\u90ce\u8fd9\u4e09\u62db\u4e0e\u9002\u624d\u5bf9\u4ed8\u5b59\u4f0f\u864e\u7684\u5200\u6cd5\u4e00\u6a21\u4e00\u6837\uff0c\u53ea\u662f\u51fa\u624b\u66f4\u5feb\uff0c\u66f4\u662f\u4eca\u4eba\u731d\u4e0d\u53ca\u9632\uff0c\u800c\u8fd9\u4e00\u5200\u65a9\u4e0b\uff0c\u79bb\u5730\u4e0d\u5230\u4e09\u5c3a\uff0c\u6768\u5bbe\u7684\u989d\u5934\u51e0\u6b32\u89e6\u53ca\u5730\u4e0a\u3002 \u3000\u3000\u90a3\u5973\u90ce\u51b7\u7136\u9053\uff1a\u201c\u670d\u4e0d\u670d\u4e86\uff0c\u201d\u6768\u5bbe\u6ee1\u8154\u6012\u706b\uff0c\u5927\u58f0\u9053\uff1a\u201c\u4e0d\u670d\u3002\u201d\u90a3\u5973\u90ce\u624b\u4e0a\u5fae\u5fae\u4f7f\u52b2\uff0c\u5200\u5203\u5411\u4e0b\u7a0d\u538b\u3002\u5c82\u77e5\u6768\u5bbe\u6781\u662f\u5f3a\u9879\uff0c\u5fc3\u60f3\uff1a\u201c\u4f60\u4fbf\u662f\u5c06\u6211\u8111\u888b\u65a9\u4e0b\uff0c\u6211\u989d\u5934\u4e5f\u4e0d\u70b9\u5730\u3002\u201d\u5934\u9888\u53cd\u800c\u4e00\u633a\u3002 \u3000\u3000\u90a3\u5973\u90ce\u65e0\u610f\u4f24\u4ed6\u6027\u547d\uff0c\u5c06\u5355\u5200\u7a0d\u7a0d\u63d0\u8d77\uff0c\u9053\uff1a\u201c\u4f60\u8981\u600e\u5730\u624d\u80af\u670d\u4e86\uff1f\u201d \u3000\u3000\u6768\u5bbe\u5fc3\u60f3\u5979\u7684\u5200\u6cd5\u6709\u4e9b\u90aa\u95e8\uff0c\u4f46\u771f\u5b9e\u6b66\u529f\u51b3\u8ba1\u4e0d\u80fd\u80dc\u6211\uff0c\u4e8e\u662f\u5927\u58f0\u9053\uff1a\u201c\u4f60\u6709\u80c6\u5b50\uff0c\u5c31\u8ddf\u6211\u6bd4\u67aa\u3002\u201d\u90a3\u5973\u90ce\u9053\uff1a\u201c\u597d\uff01\u201d\u6536\u8d77\u5355\u5200\uff0c\u5411\u501f\u5200\u7684\u5f1f\u5b50\u629b\u4e86\u8fc7\u53bb\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u77a7\u77a7\u4f60\u7684\u516d\u5408\u67aa\u6cd5\u7ec3\u5f97\u5982\u4f55\uff1f\u201d \u3000\u3000\u6768\u5bbe\u8df3\u8d77\u8eab\u6765\uff0c\u4ed6\u8138\u8272\u672c\u7ea2\uff0c\u8fd9\u65f6\u76db\u6012\u4e4b\u4e0b\uff0c\u66f4\u662f\u80c0\u5f97\u7d2b\u9171\u4e00\u822c\uff0c\u5927\u53eb\u9053\uff1a\u201c\u5feb\u53d6\u67aa\u6765\uff0c\u5feb\u53d6\u67aa\u6765\uff01\u201d\u4e00\u540d\u5f1f\u5b50\u5230\u7ec3\u6b66\u5385\u53bb\u53d6\u4e86\u4e00\u67c4\u67aa\u6765\u3002\u6768\u5bbe\u5927\u6012\u82e5\u72c2\uff0c\u53cd\u624b\u4fbf\u662f\u4e00\u4e2a\u8033\u62ec\u5b50\uff0c\u9a82\u9053\uff1a\u201c\u8fd9\u5973\u4eba\u8981\u548c\u6211\u6bd4\u67aa\u6cd5\uff0c\u4f60\u6ca1\u542c\u89c1\u4e48\uff1f\u201d \u3000\u3000\u8fd9\u5f1f\u5b50\u7ed9\u4ed6\u4e00\u5df4\u638c\u6253\u5f97\u660f\u5934\u660f\u8111\uff0c\u4e00\u65f6\u4f1a\u4e0d\u8fc7\u610f\u6765\u3002\u53e6\u4e00\u540d\u5f1f\u5b50\u6015\u4ed6\u518d\u4f38\u624b\u6253\u4eba\uff0c\u5fd9\u9053\uff1a\u201c\u5f1f\u5b50\u53bb\u518d\u62ff\u4e00\u628a\u3002\u201d\u5954\u5165\u5185\u5802\uff0c\u53c8\u53d6\u4e86\u4e00\u628a\u67aa\u6765\u3002 \u3000\u3000\u90a3\u5973\u90ce\u63a5\u8fc7\u957f\u67aa\uff0c\u8bf4\u9053\uff1a\u201c\u63a5\u62db\u5427\uff01\u201d\u63d0\u67aa\u5411\u524d\u4e00\u9001\uff0c\u4f7f\u7684\u662f\u4e00\u62db\u201c\u56db\u5937\u5bbe\u773c\u201d\u3002\u8fd9\u4e00\u62db\u662f\u516d\u5408\u67aa\u4e2d\u6700\u7cbe\u5999\u7684\u62db\u6570\uff0c\u79f0\u4e3a\u4e8c\u5341\u56db\u5f0f\u4e4b\u9996\uff0c\u5176\u4e2d\u5999\u53d8\u65e0\u7a77\uff0c\u4e43\u662f\u4e2d\u5e73\u67aa\u6cd5\u3002 \u3000\u3000\u80e1\u6590\u7cbe\u7814\u5355\u5200\u62f3\u811a\uff0c\u5bf9\u5176\u4f59\u5175\u5203\u5747\u4e0d\u719f\u6089\uff0c\u5411\u90a3\u4e2d\u5e74\u6b66\u5e08\u671b\u4e86\u4e00\u773c\uff0c\u76ee\u5149\u4e2d\u542b\u6709\u8bf7\u6559\u4e4b\u610f\u3002\u8fd9\u6b66\u5e08\u6b66\u529f\u5e73\u5e73\uff0c\u4f46\u8ddf\u968f\u4e07\u8001\u62f3\u5e08\u591a\u5e74\uff0c\u5bf9\u516d\u5408\u95e8\u7684\u5668\u68b0\u62f3\u811a\u5374\u770b\u5f97\u591a\u3001\u542c\u5f97\u591a\u4e86\uff0c\u4e8e\u662f\u80cc\u8bf5\u6b4c\u51b3\u9053\uff1a\u201c\u4e2d\u5e73\u67aa\uff0c\u67aa\u4e2d\u738b\uff0c\u9ad8\u4f4e\u8fdc\u8fd1\u90fd\u4e0d\u59a8\uff1b\u53bb\u5982\u7bad\uff0c\u6765\u5982\u7ebf\u4ed6\u6b4c\u8bc0\u5c1a\u672a\u80cc\u5b8c\uff0c\u4f46\u89c1\u6768\u5bbe\u8fd8\u4e86\u4e00\u62db\u3002\u90a3\u5973\u90ce\u67aa\u5c16\u5411\u4e0b\u4e00\u538b\u3002\u90a3\u6b66\u5e08\u9053\uff1a\u201c\u8fd9\u62db\u2018\u7f8e\u4eba\u8ba4\u9488\u2019\uff0c\u62db\u6570\u4e5f\u8fd8\u5e73\u5e73\uff0c\u5979\u67aa\u6cd5\u53ea\u6015\u4e0d\u53ca\u6768\u5e08\u5144\u2026\u2026\u201d\u7a81\u89c1\u90a3\u5973\u90ce\u53cc\u624b\u4e00\u637a\uff0c\u67aa\u5c16\u5411\u4e0b\uff0c\u5df1\u5c06\u6768\u5bbe\u7684\u67aa\u5934\u538b\u6ce8\uff0c\u6b63\u662f\u516d\u5408\u67aa\u6cd5\u4e2d\u7684\u201c\u7075\u732b\u6355\u9f20\u201d\u3002\u8fd9\u4e00\u62db\u79f0\u4e3a\u201c\u65e0\u4e2d\u751f\u6709\u67aa\u201d\uff0c\u4e43\u662f\u4ece\u865a\u5f0f\u4e4b\u4e2d\uff0c\u53d8\u51fa\u6781\u5389\u5bb3\u7684\u5bb6\u6570\u3002 \u3000\u3000\u53ea\u4e09\u62db\u4e4b\u95f4\uff0c\u6768\u5bbe\u53c8\u5df2\u88ab\u5236\uff0c\u4ed6\u529b\u900f\u53cc\u81c2\uff0c\u543c\u58f0\u5982\u96f7\uff0c\u731b\u529b\u4e3e\u67aa\u4e0a\u5d29\uff0c\u90a3\u5973\u90ce\u63d0\u67aa\u4e00\u6296\uff0c\u5580\u7684\u4e00\u58f0\uff0c\u6768\u5bbe\u67aa\u5934\u5df2\u88ab\u9707\u65ad\u3002\u90a3\u5973\u90ce\u67aa\u5c16\u7ffb\u8d77\uff0c\u6307\u5728\u4ed6\u5c0f\u8179\u4e4b\u4e0a\uff0c\u8f7b\u58f0\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d \u3000\u3000\u4f17\u4eba\u7684\u773c\u5149\u4e00\u9f50\u671b\u7740\u6768\u5bbe\uff0c\u4f46\u89c1\u4ed6\u732a\u809d\u822c\u7684\u8138\u4e0a\u500f\u5730\u8840\u8272\u5168\u65e0\uff0c\u60e8\u767d\u5982\u7eb8\uff0c\u8eab\u5b50\u4e00\u98a4\uff0c\u62cd\u7684\u4e00\u58f0\uff0c\u5c06\u67aa\u6746\u629b\u5728\u5730\u4e0b\uff0c\u53eb\u9053\uff1a\u201c\u7f62\u4e86\uff0c\u7f62\u4e86\uff01\u201d\u8f6c\u8eab\u5411\u5916\u6025\u5954\u3002\u4ed6\u4e00\u540d\u5f1f\u5b50\u53eb\u9053\uff1a\u201c\u5e08\u7236\uff0c\u5e08\u7236\uff01\u201d\u8ffd\u8fd1\u8eab\u53bb\u3002\u6768\u5bbe\u98de\u8d77\u4e00\u817f\uff0c\u5c06\u5f1f\u5b50\u8e22\u4e86\u4e2a\u7b4b\u6597\uff0c\u5934\u4e5f\u4e0d\u56de\u5730\u5954\u51fa\u5927\u95e8\u53bb\u4e86\u3002 \u3000\u3000\u5927\u5385\u4e0a\u4f17\u4eba\u65e0\u4e0d\u60ca\u8bb6\u83ab\u540d\u3002\u8fd9\u5973\u90ce\u6240\u4f7f\u5200\u6cd5\u67aa\u6cd5\uff0c\u786e\u662f\u97e6\u9640\u95e8\u6b63\u5b97\u6b66\u529f\u3002 \u3000\u3000\u5b59\u4f0f\u864e\u4e0e\u6768\u5bbe\u90fd\u662f\u97e6\u9640\u95e8\u4e2d\u8457\u540d\u597d\u624b\uff0c\u4f46\u4e0d\u8bba\u5200\u67aa\uff0c\u90fd\u662f\u4e0d\u8fc7\u4e09\u62db\uff0c\u4fbf\u7ed9\u5979\u5236\u5f97\u66f4\u65e0\u62db\u67b6\u4f59\u5730\u3002 \u3000\u3000\u5c09\u8fdf\u8fde\u65e9\u6536\u8d77\u4e86\u5bf9\u90a3\u5973\u90ce\u7684\u8f7b\u89c6\u4e4b\u610f\uff0c\u5fc3\u4e2d\u6253\u5b9a\u4e86\u4e3b\u610f\uff0c\u62b1\u62f3\u4e0a\u524d\uff0c\u8bf4\u9053\uff1a\u201c\u59d1\u5a18\u6b66\u529f\u7cbe\u5999\u7edd\u4f26\uff0c\u5728\u4e0b\u81ea\u7136\u4e0d\u662f\u5bf9\u624b\uff0c\u4e0d\u8fc7\u2026\u2026\u201d\u90a3\u5973\u90ce\u79c0\u7709\u5fae\u8e69\uff0c\u9053\uff1a\u201c\u4f60\u8bdd\u513f\u5f88\u591a\uff0c\u6211\u4e5f\u4e0d\u8010\u70e6\u542c\uff0c\u4f60\u82e5\u662f\u53e3\u670d\u5fc3\u670d\uff0c\u4fbf\u62e5\u6211\u4e3a\u638c\u95e8\uff0c\u82e5\u662f\u4e0d\u670d\uff0c\u723d\u723d\u5feb\u5feb\u7684\u52a8\u624b\u4fbf\u662f\u3002\u201d\u5c09\u8fdf\u8fde\u8138\u4e0a\u5fae\u5fae\u4e00\u7ea2\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u5973\u5b50\u624b\u4e0a\u8fa3\uff0c\u53e3\u4e0a\u4e5f\u8fa3\u5f97\u7d27\uff0c\u201d\u4e8e\u662f\u8bf4\u9053\uff1a\u201c\u6211\u5e08\u5144\u5e08\u5f1f\u90fd\u5df2\u670d\u8f93\uff0c\u5728\u4e0b\u4e0d\u732e\u732e\u4e11\u662f\u4e0d\u6210\u7684\u4e86\u90a3\u5973\u90ce\u622a\u4f4f\u8bdd\u5934\uff0c\u9053\uff1a\u201c\u597d\uff0c\u4f60\u7231\u6bd4\u4ec0\u4e48\uff1f\u201d\u5c09\u8fdf\u8fde\u9053\uff1a\u2018\u97e6\u9640\u95e8\u81ea\u6765\u53f7\u79f0\u62f3\u5200\u67aa\u4e09\u7edd\u2026\u2026\u201d\u90a3\u5973\u90ce\u4e5f\u771f\u723d\u7094\uff0c\u5c06\u5927\u67aa\u4e00\u629b\uff0c\u9053\uff1a\u201c\u6664\uff0c\u90a3\u4f60\u662f\u8981\u6bd4\u62f3\u811a\u4e86\uff0c\u6765\u5427\uff01\u201d\u5c09\u8fdf\u8fde\u9053\uff1a\u201c\u54b1\u4eec\u6b63\u5b97\u7684\u516d\u5408\u62f3\u662f\u4e0d\u7528\u6bd4\u4e86\uff0c\u6211\u81ea\u7136\u548c\u59d1\u5a18\u5dee\u5f97\u8fdc\uff0c\u5728\u4e0b\u60f3\u8bf7\u6559\u4e00\u5957\u8d64\u5c3b\u2026\u2026\u201d\u90a3\u5973\u90ce\u8138\u8272\u66f4\u662f\u4e0d\u8c6b\uff0c\u9053\uff1a\u201c\u54fc\uff0c\u4f60\u7cbe\u7814\u8d64\u5c3b\u8fde\u62f3\uff0c\u90a3\u4e5f\u6210\uff01\u201d\u53f3\u638c\u4e00\u8d77\uff0c\u4fbf\u5411\u4ed6\u80a9\u5934\u7435\u7436\u9aa8\u4e0a\u65a9\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u539f\u6765\u8fd9\u201c\u8d64\u5c3b\u8fde\u62f3\u201d\u4e5f\u662f\u97e6\u9640\u95e8\u7684\u62f3\u6cd5\u4e4b\u4e00\uff0c\u4ee5\u516d\u5408\u62f3\u4e3a\u6839\u57fa\uff0c\u4ee5\u7334\u62f3\u4e3a\u5f62\uff0c\u4e43\u662f\u4e00\u5957\u8fd1\u8eab\u7f20\u6597\u7684\u5c0f\u64d2\u62ff\u624b\u6cd5\uff0c\u6bcf\u4e00\u62db\u4e0d\u662f\u62ff\u6293\u52fe\u9501\uff0c\u4fbf\u662f\u70b9\u7a74\u6253\u7a74\u3002\u5c09\u8fdf\u8fde\u89c1\u5979\u5200\u67aa\u62db\u6570\u5389\u5bb3\uff0c\u81ea\u6043\u8fd9\u5957\u8d64\u5c3b\u8fde\u62f3\u7ec3\u5f97\u6781\u662f\u7eaf\u719f\uff0c\u5fc3\u60f3\u5979\u6b66\u529f\u518d\u5f3a\uff0c\u5c0f\u59d1\u5a18\u8182\u529b\u603b\u4e0d\u53ca\u6211\uff0c\u4f55\u51b5\u8d34\u8eab\u8fd1\u6218\uff0c\u5973\u5b69\u513f\u5bb6\u6709\u8bb8\u591a\u987e\u5fcc\u4e4b\u5904\uff0c\u81ea\u5df1\u4fbf\u53ef\u4e58\u673a\u53d6\u80dc\u3002 \u3000\u3000\u90a3\u5973\u90ce\u77e5\u9053\u4ed6\u7684\u5fc3\u610f\uff0c\u4e00\u8d77\u624b\u4fbf\u51fa\u638c\u800c\u65a9\u3002\u5c09\u8fdf\u8fde\u5de6\u624b\u6325\u51fa\uff0c\u60f3\u683c\u5f00\u5979\u53f3\u638c\uff0c\u987a\u624b\u56de\u70b9\u80a9\u4e95\u7a74\u3002\u90a3\u5973\u90ce\u624b\u8155\u7adf\u4e0d\u4e0e\u4ed6\u76f8\u78b0\uff0c\u624b\u638c\u4e00\u504f\uff0c\u6307\u5934\u5df2\u504f\u5411\u5de6\u4fa7\uff0c\u5f84\u70b9\u4ed6\u5de6\u80f8\u7a74\u9053\uff0c\u5c09\u8fdf\u8fde\u5927\u559c\uff0c\u53f3\u638c\u56de\u683c\uff0c\u5de6\u624b\u62ff\u5411\u5979\u7684\u8170\u95f4\u3002\u90a3\u5973\u90ce\u53f3\u817f\u7a81\u7136\u4ece\u540e\u7ed5\u8fc7\u81ea\u5df1\u5de6\u817f\uff0c\u7830\u7684\u4e00\u817f\uff0c\u5c06\u4ed6\u8e22\u5f97\u76f4\u98de\u51fa\u53bb\uff0c\u6454\u5728\u5929\u4e95\u7684\u77f3\u677f\u4e4b\u4e0a\uff0c\u8138\u988a\u4e0a\u9c9c\u8840\u76f4\u6d41\u3002\u90a3\u5973\u90ce\u4f7f\u7684\u62db\u5f0f\u6b63\u662f\u8d64\u5c3b\u8fde\u62f3\uff0c\u4f46\u7adf\u662f\u4e0d\u5bb9\u4ed6\u8fd1\u8eab\u3002\u4e09\u4e2a\u5e08\u5144\u5f1f\u4e4b\u4e2d\uff0c\u5012\u662f\u8fd9\u5c09\u8fdf\u8fde\u53d7\u4f24\u89c1\u8840\u3002 \u3000\u3000\u4f55\u601d\u8c6a\u89c1\u90a3\u5973\u90ce\u6b66\u529f\u5982\u6b64\u9ad8\u5f3a\uff0c\u5fc3\u4e2d\u751a\u559c\uff0c\u6ee1\u6ee1\u659f\u4e86\u4e00\u676f\u9152\uff0c\u606d\u606d\u656c\u656c\u5730\u9001\u8fc7\u53bb\uff0c\u8bf4\u9053\uff1a\u201c\u59d1\u5a18\u827a\u538b\u5f53\u573a\uff0c\u5373\u4ee4\u4e07\u8001\u62f3\u5e08\u590d\u751f\uff0c\u4e5f\u672a\u5fc5\u6709\u6b64\u6b66\u529f\u3002 \u3000\u3000\u59d1\u5a18\u4eca\u65e5\u51fa\u4efb\u638c\u95e8\uff0c\u773c\u89c1\u97e6\u9640\u95e8\u5927\u5927\u5174\u65fa\u3002\u5b9e\u662f\u53ef\u559c\u53ef\u8d3a\u3002\u201d \u3000\u3000\u90a3\u5973\u90ce\u63a5\u8fc7\u9152\u676f\uff0c\u6b63\u8981\u653e\u5230\u53e3\u8fb9\uff0c\u5385\u89d2\u5ffd\u6709\u4e00\u4eba\u602a\u58f0\u602a\u6c14\u4ed6\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u59d1\u5a18\u662f\u97e6\u9640\u95e8\u7684\u4e48\uff1f\u6211\u770b\u4e0d\u89c1\u5f97\u5427\u3002\u201d\u90a3\u5973\u90ce\u8f6c\u5934\u5f80\u58f0\u97f3\u6765\u5904\u770b\u53bb\uff0c\u53ea\u89c1\u4eba\u4eba\u5750\u7740\uff0c\u9694\u5f97\u8fdc\u4e86\uff0c\u4e0d\u77e5\u8bf4\u8bdd\u7684\u662f\u8c01\uff0c\u4e8e\u662f\u51b7\u7b11\u9053\uff1a\u201c\u54ea\u4e00\u4f4d\u4e0d\u670d\uff0c\u8bf7\u51fa\u6765\u8bf4\u8bdd\u3002\u201d \u3000\u3000\u9694\u4e86\u7247\u523b\uff0c\u5385\u89d2\u4e2d\u5bc2\u7136\u65e0\u58f0\u3002\u4f55\u601d\u8c6a\u9053\uff1a\u201c\u54b1\u4eec\u6d3b\u5df2\u8bf4\u660e\u5728\u5148\uff0c\u638c\u95e8\u4eba\u4e00\u5e2d\u51ed\u6b66\u529f\u800c\u5b9a\u3002\u8fd9\u4f4d\u59d1\u5a18\u4f7f\u7684\u662f\u97e6\u9640\u95e8\u6b63\u5b97\u529f\u592b\uff0c\u5200\u67aa\u62f3\u811a\uff0c\u5927\u5bb6\u90fd\u4eb2\u773c\u89c1\u5230\u4e86\uff0c\u53ef\u6ca1\u4e00\u70b9\u542b\u7cca\u3002\u672c\u95e8\u5f1f\u5b50\u4e4b\u4e2d\uff0c\u6709\u8c01\u81ea\u4fe1\u80dc\u5f97\u8fc7\u8fd9\u4f4d\u59d1\u5a18\u7684\uff0c\u5c3d\u53ef\u4e0a\u6765\u6bd4\u8bd5\u3002\u5144\u5f1f\u5949\u798f\u5927\u5e05\u4e4b\u547d\uff0c\u9080\u8bf7\u5929\u4e0b\u82f1\u96c4\u8c6a\u6770\u8fdb\u4eac\uff0c\u9080\u5230\u7684\u4eba\u6b66\u827a\u8d8a\u9ad8\uff0c\u5144\u5f1f\u8d8a\u6709\u9762\u5b50\uff0c\u8fd9\u4e2d\u95f4\u53ef\u51b3\u65e0\u504f\u8892\u554a\u3002\u201d\u8bf4\u7740\u5e72\u7b11\u4e86\u51e0\u58f0\u3002 \u3000\u3000\u4ed6\u89c1\u65e0\u4eba\u63a5\u53e3\uff0c\u5411\u90a3\u5973\u90ce\u9053\uff1a\u201c\u4f17\u4eba\u65e2\u65e0\u5f02\u8a00\uff0c\u8fd9\u638c\u95e8\u4e00\u5e2d\uff0c\u81ea\u662f\u59d1\u5a18\u7684\u4e86\u3002\u6b66\u6797\u4e4b\u4e2d\uff0c\u5404\u95e8\u5404\u6d3e\u7684\u638c\u95e8\u4eba\u5144\u5f1f\u4e5f\u89c1\u8fc7\u4e0d\u5c11\uff0c\u53ef\u662f\u4ece\u65e0\u4e00\u4f4d\u5982\u6b64\u5e74\u8f7b\uff0c\u5982\u6b64\u7f8e\u2026\u2026\u54b3\u54b3\uff0c\u5982\u6b64\u5e74\u8f7b\u4e4b\u4eba\uff0c\u5f53\u771f\u662f\u82f1\u96c4\u51fa\u5728\u5e74\u5c11\uff0c\u6709\u5fd7\u4e0d\u5728\u5e74\u9ad8\u3002 \u3000\u3000\u54b1\u4eec\u8bf4\u4e86\u534a\u5929\u8bdd\uff0c\u8fd8\u6ca1\u8bf7\u6559\u59d1\u5a18\u5c0a\u59d3\u5927\u540d\u5462\u3002\u201d \u3000\u3000\u90a3\u5973\u90ce\u5fae\u4e00\u8fdf\u7591\uff0c\u60f3\u8981\u8bf4\u8bdd\uff0c\u5374\u53c8\u505c\u53e3\uff0c\u4f55\u601d\u8c6a\u9053\uff1a\u201c\u97e6\u9640\u95e8\u7684\u5f1f\u5b50\uff0c\u4eca\u5929\u5230\u4e86\u5341\u4e4b\u516b\u4e5d\uff0c\u5f85\u4f1a\u4fbf\u8981\u62dc\u89c1\u638c\u95e8\uff0c\u59d1\u5a18\u7684\u5927\u540d\uff0c\u4ed6\u4eec\u53ef\u4e0d\u80fd\u4e0d\u77e5\u554a\u3002\u201d \u3000\u3000\u90a3\u5973\u90ce\u70b9\u5934\u9053\uff1a\u201c\u8bf4\u7684\u662f\u3002\u6211\u59d3\u8881\u2026\u2026\u540d\u53eb\u2026\u2026\u540d\u53eb\u7d2b\u8863\u3002\u201d\u4f55\u601d\u8c6a\u6b66\u529f\u5e73\u5e73\uff0c\u5374\u662f\u89c1\u591a\u8bc6\u5e7f\uff0c\u77a7\u5979\u8bf4\u8bdd\u7684\u795e\u60c5\uff0c\u5fc3\u60f3\u8fd9\u672a\u5fc5\u662f\u771f\u540d\uff0c\u5979\u8eab\u7a7f\u7d2b\u886b\uff0c\u968f\u53e3\u4fbf\u8bcc\u4e86\u201c\u7d2b\u8863\u201d\u4e24\u5b57\uff0c\u4f46\u4e5f\u4e0d\u4fbf\u8bf4\u7834\uff0c\u7b11\u9053\uff1a\u201c\u8881\u59d1\u5a18\u4fbf\u8bf7\u4e0a\u5750\uff0c\u6211\u8fd9\u9996\u5e2d\u8981\u8ba9\u7ed9\u4f60\u624d\u662f\u5462\u3002\u201d \u3000\u3000\u6309\u7167\u793c\u6570\uff0c\u4f55\u601d\u8c6a\u65e2\u662f\u4eac\u4e2d\u804c\u4f4d\u4e0d\u5c0f\u7684\u6b66\u5b98\uff0c\u53c8\u662f\u97e6\u9640\u95e8\u7684\u5ba2\u4eba\uff0c\u8881\u7d2b\u8863\u4fbf\u7b97\u63a5\u4efb\u638c\u95e8\uff0c\u4e5f\u5f97\u5728\u672a\u5ea7\u4e3b\u4f4d\u76f8\u966a\u3002\u4f46\u5979\u6beb\u4e0d\u8c26\u900a\uff0c\u89c1\u4f55\u601d\u8c6a\u8ba9\u5ea7\uff0c\u5f53\u5373\u5927\u6a21\u5927\u6837\u5730\u5728\u9996\u5e2d\u4f4d\u4e0a\u5750\u4e0b\u4e86\u3002 \u3000\u3000\u5ffd\u542c\u5385\u89d2\u4e2d\u90a3\u602a\u58f0\u602a\u6c14\u7684\u58f0\u97f3\u54ed\u4e86\u8d77\u6765\uff0c\u4e00\u9762\u54ed\uff0c\u4e00\u9762\u8bf4\u9053\uff1a\u201c\u97e6\u9640\u95e8\u6614\u5e74\u5a01\u9707\u5f53\u4e16\uff0c\u4eca\u65e5\u600e\u5730\u5982\u6b64\u8870\u8d25\uff1f\u7adf\u8ba9\u4e00\u4e2a\u4e73\u81ed\u672a\u5e72\u7684\u5973\u5a03\u5a03\u4e0a\u95e8\u6b3a\u4fae\u554a\uff01\u54e6\u54e6\uff0c\u54c7\u54c7\u54c7\uff01\u201d\u4ed6\u54ed\u5f97\u771f\u60c5\u6d41\u9732\uff0c\u5012\u5e76\u4e0d\u662f\u6709\u610f\u8c03\u4f83\u3002 \u3000\u3000\u8881\u7d2b\u8863\u5927\u58f0\u9053\uff1a\u201c\u4f60\u8bf4\u6211\u4e73\u81ed\u672a\u5e72\uff0c\u51fa\u6765\u89c1\u8fc7\u9ad8\u4f4e\u4fbf\u4e86\u3002\u201d\u8fd9\u4e00\u6b21\u5979\u77a7\u6e05\u695a\u4e86\u53d1\u8bdd\u4e4b\u4eba\uff0c\u662f\u4e2a\u516d\u5341\u6765\u5c81\u7684\u8001\u8005\uff0c\u8eab\u5f62\u67af\u7626\uff0c\u7559\u7740\u4e00\u6487\u9f20\u5c3e\u987b\uff0c\u5934\u6234\u74dc\u76ae\u5c0f\u5e3d\uff0c\u8111\u540e\u62d6\u7740\u4e00\u6839\u7a00\u7a00\u677e\u677e\u7684\u5c0f\u8fab\u5b50\uff0c\u5934\u53d1\u5df2\u767d\u4e86\u4e5d\u6210\u3002\u4ed6\u4f0f\u5728\u684c\u4e0a\uff0c\u53f7\u9676\u5927\u54ed\uff0c\u53eb\u9053\uff1a\u201c\u4e07\u9e64\u58f0\u554a\u4e07\u9e64\u58f0\uff0c\u4eba\u5bb6\u8bf4\u4f60\u4fbf\u662f\u6b7b\u800c\u590d\u751f\uff0c\u4e5f\u654c\u4e0d\u8fc7\u8fd9\u4f4d\u5982\u6b64\u5e74\u8f7b\u3001\u5982\u6b64\u8c8c\u7f8e\u7684\u59d1\u5a18\uff0c\u5f53\u771f\u662f\u4f73\u4eba\u51fa\u5728\u5e74\u5c11\uff0c\u8c8c\u7f8e\u4e0d\u53ef\u5e74\u9ad8\u554a\u3002\u201d \u3000\u3000\u4ed6\u6700\u540e\u8fd9\u51e0\u53e5\u8bdd\uff0c\u663e\u662f\u8ba5\u523a\u4f55\u601d\u8c6a\u7684\u4e86\u3002\u5385\u4e2d\u51e0\u4e2a\u5e74\u8f7b\u4eba\u5fcd\u4e0d\u4f4f\u7b11\u51fa\u58f0\u6765\u3002\u53ea\u542c\u8fd9\u8001\u8005\u53c8\u54ed\u9053\uff1a\u201c\u6b66\u6797\u4e4b\u4e2d\uff0c\u5404\u95e8\u5404\u6d3e\u7684\u82f1\u96c4\u597d\u6c49\u5144\u5f1f\u4e5f\u89c1\u8fc7\u4e0d\u5c11\uff0c\u53ef\u662f\u4ece\u65e0\u4e00\u4f4d\u5982\u6b64\u4e0d\u8981\u8138\u7684\u5b98\u8001\u7237\u554a\uff01\u201d\u8fd9\u4e24\u53e5\u8bdd\u4e00\u8bf4\uff0c\u5385\u4e0a\u7fa4\u60c5\u8038\u52a8\uff0c\u4eba\u4eba\u77e5\u4ed6\u662f\u51fa\u8a00\u6b63\u9762\u5411\u4f55\u601d\u8c6a\u6311\u6218\u4e86\u3002 \u3000\u3000\u4f55\u601d\u8c6a\u5982\u4f55\u5fcd\u5f97\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u6709\u79cd\u7684\u4fbf\u6eda\u51fa\u6765\uff0c\u9b3c\u9b3c\u795f\u795f\u5730\u7f29\u5728\u5c4b\u89d2\u91cc\u505a\u4e4c\u9f9f\u4e48\uff1f\u201d\u90a3\u8001\u8005\u4ecd\u662f\u653e\u58f0\u800c\u54ed\uff0c\u8bf4\u9053\uff1a\u201c\u5144\u5f1f\u5949\u960e\u7f57\u738b\u4e4b\u547d\uff0c\u9080\u8bf7\u5b98\u8001\u7237\u4eec\u5230\u9634\u4e16\u5927\u4f1a\uff0c\u9080\u5230\u7684\u4eba\u5b98\u513f\u505a\u5f97\u8d8a\u5927\uff0c\u5144\u5f1f\u8d8a\u6709\u9762\u5b50\u554a\u3002\u201d\u4f55\u601d\u8c6a\u970d\u5730\u7ad9\u8d77\uff0c\u5411\u5385\u89d2\u6025\u5954\u8fc7\u53bb\uff0c\u5de6\u638c\u865a\u6643\uff0c\u53f3\u624b\u4fbf\u5f80\u8001\u8005\u5934\u9888\u91cc\u6293\u53bb\u3002\u90a3\u8001\u8005\u54ed\u58f0\u4e0d\u505c\uff0c\u4f17\u4eba\u7ad9\u8d77\u6765\u770b\u65f6\uff0c\u7a81\u7136\u4e00\u9053\u9ed1\u5f71\u4ece\u5385\u89d2\u91cc\u76f4\u98de\u51fa\u6765\uff0c\u7830\u7684\u4e00\u58f0\uff0c\u6454\u5728\u5f53\u5730\uff0c\u6b63\u662f\u4f55\u601d\u8c6a\u3002\u4f17\u4eba\u90fd\u6ca1\u77a7\u660e\u767d\u4ed6\u662f\u5982\u4f55\u6454\u7684\uff0c\u53e6\u4e00\u540d\u4f8d\u536b\u89c1\u540c\u4f34\u5931\u5229\uff0c\u62d4\u51fa\u8170\u5200\u62a2\u4e0a\u524d\u53bb\uff0c\u5385\u4e0a\u767b\u65f6\u4e00\u9635\u5927\u4e71\uff0c\u4f46\u89c1\u9ed1\u5f71\u4e00\u5e4c\uff0c\u98ce\u58f0\u54cd\u5904\uff0c\u8fd9\u4f8d\u536b\u53c8\u662f\u7830\u7684\u4e00\u58f0\u6454\u5728\u5e2d\u524d\u3002 \u3000\u3000\u80e1\u6590\u4e00\u76f4\u5728\u7559\u795e\u90a3\u8001\u8005\uff0c\u89c1\u4ed6\u6454\u8dcc\u8fd9\u4e24\u540d\u4f8d\u536b\u624b\u6cd5\u5e72\u51c0\u5229\u843d\uff0c\u4f7f\u7684\u4fbf\u662f\u5c09\u8fdf\u8fde\u4e0e\u8881\u7d2b\u8863\u9002\u624d\u8fc7\u62db\u7684\u201c\u8d64\u5c3b\u8fde\u62f3\u201d\uff0c\u770b\u6765\u8fd9\u8001\u8005\u4e5f\u662f\u97e6\u9640\u95e8\u7684\uff0c\u53ea\u662f\u4ed6\u6b66\u529f\u9ad8\u51fa\u5c09\u8fdf\u8fde\u4f55\u6b62\u500d\u84f0\uff0c\u5b9a\u662f\u4ed6\u4eec\u672c\u95e8\u7684\u540d\u624b\u3002\u4ed6\u5bf9\u6e05\u5ef7\u4f8d\u536b\u7d20\u65e0\u597d\u611f\uff0c\u89c1\u8fd9\u4e8c\u4eba\u6454\u5f97\u72fc\u72c8\uff0c\u9694\u4e86\u597d\u4e00\u9635\u65b9\u624d\u722c\u8d77\uff0c\u5fc3\u4e2d\u6697\u81ea\u9ad8\u5174\u3002 \u3000\u3000\u8881\u7d2b\u8863\u89c1\u5230\u4e86\u52b2\u654c\uff0c\u79bb\u5e2d\u800c\u8d77\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u6709\u4f55\u89c1\u6559\uff0c\u723d\u723d\u5feb\u5feb\u4ed6\u8bf4\u5427\uff0c\u6211\u53ef\u89c1\u4e0d\u5f97\u4eba\u88c5\u795e\u5f04\u9b3c\u3002\u201d\u90a3\u8001\u8005\u4ece\u5385\u89d2\u91cc\u7f13\u7f13\u51fa\u6765\uff0c\u8138\u4e0a\u4ecd\u662f\u4e00\u628a\u773c\u6cea\u4e00\u628a\u9f3b\u6d95\u3002\u8881\u7d2b\u8863\u89c1\u4ed6\u9762\u5bb9\u67af\u9ec4\uff0c\u98a7\u9aa8\u9ad8\u8d77\uff0c\u53cc\u988a\u6df1\u9677\uff0c\u5012\u4f3c\u662f\u4e2a\u9648\u5e74\u7684\u75e8\u75c5\u9b3c\uff0c\u4f46\u53cc\u76ee\u70af\u70af\u6709\u795e\uff0c\u5f53\u4e0b\u4e0d\u6562\u6020\u6162\uff0c\u51dd\u795e\u4ee5\u5f85\u3002 \u3000\u3000\u90a3\u8001\u8005\u4e0d\u518d\u8ba5\u523a\uff0c\u6b63\u8272\u8bf4\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u4e0d\u662f\u6211\u95e8\u4e2d\u4eba\u3002\u97e6\u9640\u95e8\u8ddf\u4f60\u65e0\u51a4\u65e0\u4ec7\uff0c\u4f60\u4f55\u82e6\u6765\u62c6\u8fd9\u4e2a\u6863\u5b50\uff1f\u201d\u8881\u7d2b\u8863\u9053\uff1a\u201c\u96be\u9053\u4f60\u4fbf\u662f\u97e6\u9640\u95e8\u7684\uff1f\u4f60\u59d3\u4ec0\u4e48\uff1f\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u201d\u90a3\u8001\u8005\u9053\uff1a\u201c\u6211\u59d3\u5218\uff0c\u540d\u53eb\u5218\u9e64\u771f\u3002\u2018\u97e6\u9640\u53cc\u9e64\u2019\u7684\u540d\u5934\u4f60\u542c\u89c1\u8fc7\u4e48\uff1f\u6211\u82e5\u4e0d\u662f\u97e6\u9640\u95e8\u7684\u5f1f\u5b50\uff0c\u600e\u80fd\u4e0e\u4e07\u9e64\u58f0\u5408\u79f0\u2018\u97e6\u9640\u53cc\u9e64\u2019\uff1f\u201d \u3000\u3000\u201c\u97e6\u9640\u53cc\u9e64\u201d\u8fd9\u56db\u4e2a\u5b57\uff0c\u5385\u4e0a\u5e74\u5c81\u8f83\u5927\u4e4b\u4eba\u5012\u90fd\u542c\u89c1\u8fc7\u7684\uff0c\u4f46\u5927\u534a\u53ea\u8ba4\u5f97\u4e07\u9e64\u58f0\uff0c\u90fd\u77e5\u4ed6\u4e3a\u4eba\u4efb\u4fa0\u597d\u4e49\uff0c\u6c5f\u6e56\u4e0a\u58f0\u540d\u751a\u597d\uff0c\u53e6\u4e00\u53ea\u201c\u9e64\u201d\u662f\u8c01\uff0c\u5c31\u4e0d\u5927\u4e86\u7136\u3002\u8fd9\u65f6\u542c\u8fd9\u4e2a\u7cdf\u8001\u5934\u513f\u81ea\u79f0\u662f\u201c\u53cc\u9e64\u201d\u4e4b\u4e00\uff0c\u53c8\u4eb2\u773c\u89c1\u4ed6\u4e00\u4e3e\u4e0b\u4fbf\u5c06\u4e24\u4e2a\u4f8d\u536b\u6253\u5f97\u52a8\u5f39\u4e0d\u5f97\uff0c\u4e00\u65f6\u7fa4\u76f8\u6ce8\u76ee\uff0c\u7a83\u7a83\u79c1\u8bae\u3002\u53ea\u662f\u8c01\u90fd\u4e0d\u77e5\u4ed6\u7684\u5e95\u7ec6\uff0c\u4e5f\u8bf4\u4e0d\u51fa\u4e00\u4e2a\u6240\u4ee5\u7136\u6765\u3002 \u3000\u3000\u8881\u7d2b\u8863\u6447\u5934\u9053\uff1a\u201c\u4ec0\u4e48\u53cc\u9e64\u53cc\u9e2d\uff0c\u6ca1\u542c\u89c1\u8fc7\uff0c\u4f60\u8981\u60f3\u505a\u638c\u95e8\uff0c\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u5218\u9e64\u771f\u9053\uff1a\u201c\u4e0d\u662f\uff0c\u4e0d\u662f\uff0c\u5343\u4e07\u4e0d\u53ef\u51a4\u6789\u3002\u6211\u662f\u5e08\u5144\uff0c\u4e07\u9e64\u58f0\u662f\u5e08\u5f1f\u3002\u6211\u8981\u505a\u638c\u95e8\uff0c\u5f53\u5e74\u4fbf\u505a\u4e86\uff0c\u4f55\u5fc5\u7b49\u5230\u4eca\u65e5\uff1f\u201d\u8881\u7d2b\u8863\u5c0f\u5634\u4e00\u6241\uff0c\u9053\uff1a\u201c\u54fc\uff0c\u80e1\u8bf4\u516b\u9053\uff0c\u8c01\u4fe1\u4f60\u7684\u8bdd\uff1f\u90a3\u4f60\u8981\u5e72\u4ec0\u4e48\uff1f\u201d\u5218\u9e64\u771f\u9053\uff1a\u201c\u7b2c\u4e00\u3001\u97e6\u9640\u95e8\u7684\u638c\u95e8\uff0c\u8be5\u7531\u672c\u95e8\u771f\u6b63\u7684\u5f1f\u5b50\u6765\u5f53\u3002\u7b2c\u4e8c\u3001\u4e0d\u8bba\u8c01\u638c\u95e8\uff0c\u4e0d\u8bb8\u8d8b\u708e\u9644\u52bf\uff0c\u5230\u4eac\u91cc\u7ed3\u4ea4\u6743\u8d35\u3002\u6211\u4eec\u662f\u5b66\u6b66\u7684\u7c97\u4eba\uff0c\u4e61\u5df4\u4f6c\u513f\uff0c\u600e\u914d\u8ddf\u5b98\u8001\u7237\u4eec\u4ea4\u670b\u53cb\u54ea\uff1f\u201d\u4ed6\u4e00\u53cc\u4e09\u89d2\u773c\u5411\u4f17\u4eba\u6a2a\u626b\u4e86\u4e00\u773c\uff0c\u8bf4\u9053\uff1a\u201c\u7b2c\u4e09\u3001\u4ee5\u6b66\u529f\u5b9a\u638c\u95e8\uff0c\u8fd9\u8bdd\u5148\u5c31\u4e0d\u901a\u3002 \u3000\u3000\u4e0d\u8bba\u5b66\u6587\u5b66\u6b66\uff0c\u90fd\u662f\u4eba\u54c1\u7b2c\u4e00\u3002\u82e5\u662f\u4e00\u4e2a\u5351\u9119\u5c0f\u4eba\u6b66\u529f\u6700\u5f3a\uff0c\u5927\u4f19\u51e0\u4e5f\u63a8\u4ed6\u505a\u638c\u95e8\u4e48\uff1f\u201d \u3000\u3000\u6b64\u8a00\u4e00\u51fa\uff0c\u4eba\u7fa4\u4e2d\u4fbf\u6709\u8bb8\u591a\u4eba\u6697\u6697\u70b9\u5934\uff0c\u89c9\u5f97\u4ed6\u867d\u7136\u884c\u6b62\u53e4\u602a\uff0c\u5f62\u8c8c\u59d4\u7410\uff0c\u8bf4\u7684\u8bdd\u5012\u9887\u6709\u9053\u7406\u3002 \u3000\u3000\u8881\u7d2b\u8863\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u7b2c\u4e00\u3001\u7b2c\u4e8c\u3001\u7b2c\u4e09\uff0c\u6211\u4e00\u4ef6\u4e5f\u4e0d\u4f9d\uff0c\u90a3\u4fbf\u600e\u6837\uff1f\u201d \u3000\u3000\u5218\u9e64\u771f\u9053\uff1a\u201c\u90a3\u53c8\u80fd\u600e\u6837\u4e86\uff1f\u53ea\u597d\u8ba9\u6211\u51e0\u6839\u67af\u7626\u7cbe\u5e72\u7684\u8001\u9aa8\u5934\uff0c\u6765\u6328\u59d1\u5a18\u7684\u7c89\u62f3\u7f62\u5566\uff01\u201d \u3000\u3000\u80e1\u6590\u89c1\u4e8c\u4eba\u8bf4\u50f5\u4e86\u4fbf\u8981\u52a8\u624b\uff0c\u4ed6\u81ea\u957f\u6210\u4ee5\u6765\uff0c\u6e38\u4fa0\u6c5f\u6e56\uff0c\u6570\u89c1\u6e05\u5ef7\u5b98\u540f\u6b3a\u538b\u767e\u59d3\uff0c\u6a2a\u66b4\u8d2a\u8650\uff0c\u5fc3\u4e2d\u7d20\u6765\u6068\u6076\uff0c\u8fd9\u65f6\u89c1\u5218\u9e64\u771f\u516c\u7136\u6298\u8fb1\u6e05\u5ef7\u4f8d\u536b\uff0c\u8a00\u8bed\u4e4b\u4e2d\u9887\u6709\u6b63\u6c14\uff0c\u6697\u6697\u76fc\u4ed6\u5f97\u80dc\u3002\u53ea\u662f\u90a3\u7d2b\u8863\u5c11\u5973\u51fa\u624b\u654f\u6377\uff0c\u5b9e\u662f\u4e2a\u6781\u5389\u5bb3\u7684\u597d\u624b\uff0c\u751f\u6015\u5218\u9e64\u771f\u672a\u5fc5\u654c\u5f97\u5979\u8fc7\u3002 \u3000\u3000\u8881\u7d2b\u8863\u795e\u8272\u50b2\u6162\uff0c\u7adf\u662f\u5168\u4e0d\u5c06\u5218\u9e64\u771f\u653e\u5728\u773c\u5185\uff0c\u51b7\u7136\u8bf4\u9053\uff1a\u201c\u4f60\u8981\u6bd4\u62f3\u811a\u5462\uff0c\u8fd8\u662f\u6bd4\u5200\u67aa\uff1f\u201d\u5218\u9e64\u771f\u9053\uff1a\u201c\u59d1\u5a18\u65e2\u7136\u81ea\u79f0\u662f\u5c11\u6797\u97e6\u9640\u95e8\u7684\u5f1f\u5b50\uff0c\u54b1\u4eec\u5c31\u6bd4\u97e6\u9640\u95e8\u7684\u9547\u95e8\u4e4b\u5b9d\u3002\u201d\u8881\u7d2b\u8863\u9053\uff1a\u201c\u4ec0\u4e48\u9547\u95e8\u4e4b\u5b9d\uff1f\u8bf4\u8bdd\u723d\u723d\u5feb\u5feb\uff0c\u6211\u6700\u8ba8\u538c\u662f\u515c\u7740\u5708\u5b50\u78e8\u8017\u3002\u201d\u5218\u9e64\u771f\u4ef0\u5929\u6253\u4e2a\u54c8\u54c8\uff0c\u9053\uff1a\u201c\u8fde\u672c\u95e8\u7684\u9547\u95e8\u4e4b\u5b9d\u4e5f\u4e0d\u77e5\u9053\uff0c\u600e\u80fd\u62c5\u5f53\u638c\u95e8\uff1f\u201d \u3000\u3000\u8881\u7d2b\u8863\u8138\u4e0a\u5fae\u9732\u7a98\u6001\uff0c\u4f46\u8fd9\u53ea\u662f\u4e00\u77ac\u95f4\u4e4b\u4e8b\uff0c\u7acb\u5373\u5e73\u9759\u5982\u6052\uff0c\u9053\uff1a\u201c\u672c\u95e8\u6b66\u529f\u535a\u5927\u7cbe\u6df1\uff0c\u7ec3\u5230\u6700\u9ad8\u5883\u754c\uff0c\u5373\u4ee4\u662f\u6700\u5e73\u5e38\u7684\u4e00\u62db\u4e00\u5f0f\uff0c\u4e5f\u80fd\u6a2a\u884c\u5929\u4e0b\u3002 \u3000\u3000\u516d\u5408\u5200\u4e5f\u597d\uff0c\u516d\u5408\u67aa\u4e5f\u597d\uff0c\u54ea\u4e00\u4ef6\u4e0d\u662f\u672c\u95e8\u4e4b\u5b9d\uff1f\u201d \u3000\u3000\u5218\u9e64\u771f\u4e0d\u7981\u6697 1 \u4f69\u670d\uff0c\u5979\u660e\u660e\u4e0d\u77e5\u672c\u95e8\u7684\u9547\u95e8\u4e4b\u5b9d\u662f\u4ec0\u4e48\u6b66\u529f\uff0c\u7136\u800c\u8fd9 \u756a\u8bdd\u51a0\u5195\u5802\u7687\uff0c\u4ee4\u4eba\u96be\u4ee5\u8fa9\u9a73\uff0c\u60f3\u6765\u672c\u95e8\u5f1f\u5b50\u4eba\u4eba\u542c\u5f97\u5fc3\u670d\uff0c\u4e8e\u662f\u5de6\u624b\u6478\u4e86\u6478\u4e0a\u5507\u7126\u9ec4\u7684\u80e1\u9aed\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u5427\uff0c\u6211\u6559\u4f60\u4e00\u4e2a\u4e56\u3002\u672c\u95e8\u7684\u9547\u95e8\u4e4b\u5b9d\uff0c\u4e43\u662f\u5929\u7f61\u6885\u82b1\u6869\uff0c\u4f60\u603b\u7ec3\u8fc7\u5427\uff1f\u201d \u3000\u3000\u8881\u7d2b\u8863\u51b7\u7b11\u9053\uff1a\u201c\u563f\u563f\uff0c\u8fd9\u4e5f\u7b97\u662f\u4ec0\u4e48\u5b9d\u8d1d\u4e86\uff1f\u6211\u6559\u4f60\u4e00\u4e2a\u4e56\u3002\u6b66\u529f\u4e4b\u4e2d\uff0c\u8d8a\u662f\u5927\u8def\u5e73\u5b9e\u7684\uff0c\u8d8a\u662f\u8d35\u91cd\u6709\u7528\uff0c\u4ec0\u4e48\u6885\u82b1\u6869\uff0c\u5c16\u5200\u9635\uff0c\u8fd9\u4e9b\u82b1\u5de7\u628a\u5f0f\uff0c\u90fd\u662f\u5413\u552c\u4eba\u3001\u9a97\u5b69\u5b50\u7684\u73a9\u610f\u513f\u3002\u4e0d\u8fc7\u4e0d\u8ddf\u4f60\u8bd5\u8bd5\uff0c\u8c05\u4f60\u5fc3\u4e2d\u4e0d\u670d\u3002\u4f60\u7684\u6885\u82b1\u6869\u6446\u5728\u54ea\u51e0\uff1f\u201d \u3000\u3000\u5218\u9e64\u771f\u62ff\u8d77\u684c\u4e0a\u4e00\u53ea\u9152\u7897\uff0c\u4ef0\u8116\u5b50\u559d\u5e72\uff0c\u968f\u624b\u5f80\u5730\u4e0b\u4e00\u6454\u3002\u4f17\u4eba\u90fd\u662f\u4e00\u6014\uff0c\u5747\u60f3\u8fd9\u4e00\u4e0b\u5b9a\u662f\u545b\u537f\u4e00\u54cd\uff0c\u6253\u5f97\u7c89\u788e\uff0c\u54ea\u77e5\u4ed6\u8fd9\u4e00\u6454\uff0c\u52b2\u529b\u7528\u5f97\u6070\u5230\u597d\u5904\uff0c\u9152\u7897\u5728\u5730\u4e0b\u8f7b\u8f7b\u4e00\u6ed1\uff0c\u4e0b\u6389\u7684\u529b\u9053\u767b\u65f6\u6d88\u4e86\uff0c\u5e73\u5e73\u7a33\u7a33\u7684\u5408\u5728\u5385\u5802\u7684\u65b9\u7816\u4e4b\u4e0a\uff0c\u7adf\u662f\u4e1d\u6beb\u65e0\u635f\u3002\u4ed6\u4e00\u6454\u4e4b\u540e\uff0c\u968f\u5373\u53c8\u62ff\u8d77\u7b2c\u4e8c\u53ea\u9152\u7897\u5f80\u5730\u4e0b\u6454\u53bb\uff0c\u53cc\u624b\u63a5\u8fde\u4e0d\u65ad\uff0c\u5018\u662f\u7a7a\u7897\uff0c\u4fbf\u987a\u624b\u629b\u51fa\uff0c\u7897\u4e2d\u82e5\u662f\u6709\u9152\uff0c\u4e0d\u8bba\u662f\u6ee1\u7897\u8fd8\u662f\u534a\u7897\uff0c\u90fd\u662f\u4e00\u53e3\u559d\u5e72\u3002 \u3000\u3000\u7247\u523b\u4e4b\u95f4\uff0c\u5730\u4e0b\u5df1\u5e03\u6ee1\u4e86\u9152\u7897\uff0c\u5171\u662f\u4e09\u5341\u516d\u53ea\u7897\u6563\u7f6e\u8986\u5408\u3002\u4f17\u4eba\u89c1\u4ed6\u6454\u7897\u7684\u624b\u6cd5\u56fa\u7136\u5de7\u52b2\u60ca\u4eba\uff0c\u800c\u9152\u91cf\u4e5f\u662f\u5927\u5f97\u5f02\u4e4e\u5bfb\u5e38\uff0c\u8fd9\u4e00\u756a\u8fde\u559d\u8fde\u63b7\uff0c\u5c11\u8bf4\u4e5f\u559d\u4e86\u5341\u4e8c\u4e09\u7897\u70c8\u9152\u3002\u4f46\u89c1\u4ed6\u9152\u8d8a\u559d\u5f97\u591a\uff0c\u8138\u8272\u8d8a\u9ec4\uff0c\u8eab\u5b50\u4e00\u6643\uff0c\u8f7b\u98d8\u98d8\u7eb5\u51fa\uff0c\u53f3\u8db3\u865a\u63d0\uff0c\u5de6\u8db3\u8e0f\u5728\u4e00\u53ea\u9152\u7897\u7684\u7897\u5e95\uff0c\u53cc\u624b\u4e00\u62f1\uff0c\u8bf4\u9053\uff1a\u201c\u9886\u6559\u3002\u201d \u3000\u3000\u8881\u7d2b\u8863\u5b9e\u4e0d\u77e5\u8fd9\u5929\u7f61\u6885\u82b1\u6869\u662f\u5982\u4f55\u7ec3\u6cd5\uff0c\u4f46\u4ed7\u7740\u8f7b\u529f\u9020\u8be3\u751a\u9ad8\uff0c\u5fc3\u4e0b\u5e76\u4e0d\u754f\u60e7\uff0c\u5de6\u8db3\u4e00\u70b9\uff0c\u4e5f\u8dc3\u4e0a\u4e86\u4e00\u53ea\u9152\u7897\u7684\u7897\u5e95\uff0c\u5979\u5f84\u81ea\u7ad9\u5728\u4e0a\u9996\uff0c\u53cc\u624b\u5fae\u62ac\uff0c\u5374\u4e0d\u53d1\u62db\uff0c\u8981\u77a7\u5bf9\u65b9\u5982\u4f55\u51fa\u624b\uff0c\u8fd9\u624d\u968f\u673a\u5e94\u53d8\uff0c\u53ea\u662f\u89c1\u4e86\u4ed6\u6454\u63b7\u9152\u7897\u8fd9\u756a\u5de7\u52b2\uff0c\u77e5\u4ed6\u4e0e\u5b59\u4f19\u864e\u7b49\u4e0d\u53ef\u540c\u65e5\u800c\u8bed\uff0c\u5df2\u65e0\u534a\u5206\u8f7b\u654c\u4e4b\u610f\u3002 \u3000\u3000\u5218\u9e64\u771f\u53f3\u8db3\u8e0f\u4e0a\u4e00\u6b65\uff0c\u53f3\u62f3\u5288\u9762\u5411\u8881\u7d2b\u8863\u6253\u5230\uff0c\u6b63\u662f\u516d\u5408\u62f3\u201c\u4e09\u73af\u5957\u6708\u201d \u3000\u3000\u4e2d\u7684\u7b2c\u4e00\u5f0f\uff0c\u8881\u7d2b\u8863\u89c1\u5bf9\u65b9\u62f3\u5230\uff0c\u81ea\u98df\u6307\u4ee5\u81f3\u5c0f\u6307\uff0c\u56db\u6307\u63e1\u5f97\u53c2\u5dee\u4e0d\u9f50\uff0c\u751f\u51fa\u4e09\u7247\u68f1\u89d2\uff0c\u77e5\u9053\u8fd9\u4e09\u89d2\u62f3\u6cd5\u7528\u4ee5\u51fb\u6253\u4eba\u8eab\u7a74\u9053\uff0c\u6b64\u4eba\u81ea\u662f\u6253\u7a74\u597d\u624b\uff0c\u5f53\u4e0b\u5de6\u8db3\u659c\u9000\u4e00\u6b65\uff0c\u8fd8\u4e86\u4e00\u62db\u516d\u5408\u62f3\u4e2d\u7684\u201c\u683d\u9524\u201d\uff0c\u53f3\u624b\u63e1\u7684\u4e5f\u662f\u4e09\u89d2\u62f3\u3002 \u3000\u3000\u5218\u9e64\u771f\u89c1\u5979\u8eab\u6cd5\u3001\u6b65\u6cd5\u3001\u62f3\u6cd5\u3001\u5916\u5f62\uff0c\u65e0\u4e00\u4e0d\u662f\u672c\u95e8\u6b63\u5b97\u529f\u592b\uff0c\u4eec\u9002\u624d\u6298\u670d\u5b59\u4f0f\u864e\u7b49\u4e09\u4eba\uff0c\u6240\u4f7f\u53d8\u5316\u5fc3\u6cd5\uff0c\u7edd\u975e\u672c\u95e8\u6240\u4f20\u53ea\u4e0d\u8fc7\u5176\u4e2d\u5dee\u5f02\uff0c\u82e5\u975e\u672c\u95e8\u7684\u4e00\u6d41\u9ad8\u4e0b\u5374\u4e5f\u77a7\u4e0d\u51fa\u6765\uff0c\u5fc3\u4e2d\u53c8\u662f\u60ca\u5f02\uff0c\u53c8\u662f\u607c\u6012\uff0c\u5f53\u4e0b\u8e0f\u4e0a\u5de6\u6b65\uff0c\u51fb\u51fa\u4e00\u62db\u201c\u53cd\u8eac\u95e8\u7701\u201d\u3002\u8fd9\u4e00\u62f3\u4ee5\u624b\u80cc\u51fb\u4eba\uff0c\u5728\u516d\u5408\u62f3\u4e2d\u79f0\u4e3a\u201c\u82e6\u607c\u62f3\u201d\uff0c\u56e0\u62f3\u6cd5\u6781\u96be\uff0c\u7ec3\u4e60\u4e4b\u9645\u82e6\u607c\u5f02\u5e38\uff0c\u6545\u6709\u6b64\u540d\u3002 \u3000\u3000\u8fd9\u82e6\u607c\u81f3\u5177\u6709\u6781\u5927\u5a01\u529b\uff0c\u975e\u5341\u4f59\u5e74\u4ee5\u4e0a\u529f\u529b\u4e0d\u529e\uff0c\u8881\u7d2b\u3000\u8863\u65e0\u6b64\u4fee\u4e3a\uff0c\u4e8e\u662f\u907f\u96be\u8d8b\u6613\uff0c\u8fd8\u4e86\u4e00\u62db\u201c\u6454\u624b\u7a7f\u638c\u201d\uff0c\u53f3\u624b\u51fa\u7684\u662f\u6454\u7891\u4e0b\uff0c\u5de6\u624b\u51fa\u7684\u662f\u67f3\u53f6\u638c\uff0c\u90a3\u4e5f\u662f\u516d\u5408\u62f3\u4e2d\u7684\u6b63\u5b97\u529f\u592b\u3002 \u3000\u3000\u4e24\u4eba\u5728\u4e09\u5341\u516d\u53ea\u9152\u7897\u7897\u5e95\u4e4b\u4e0a\u76d8\u65cb\u6765\u53bb\uff0c\u4f7f\u7684\u90fd\u662f\u516d\u5408\u62f3\u6cd5\u3002\u5728\u8fd9\u5929\u7f61\u6885\u82b1\u6869\u4e0a\u52a8\u624b\u8fc7\u62db\uff0c\u8981\u65e8\u662f\u62a2\u5f97\u4e2d\u6869\uff0c\u5c06\u654c\u624b\u903c\u81f3\u5916\u7f18\uff0c\u5982\u662f\u5219\u4e00\u6709\u673a\u4f1a\uff0c\u51fa\u624b\u7a0d\u91cd\uff0c\u654c\u4e0b\u65e0\u8def\u53ef\u9000\uff0c\u53ea\u6709\u8dcc\u843d\u6869\u4e0b\u3002\u5218\u9e64\u771f\u81ea\u5e7c\u4fbf\u5bf9\u8fd9\u8def\u6b66\u529f\u6df1\u6709\u5fc3\u5f97\uff0c\u5728\u8fd9\u6869\u4e0a\u5df2\u82e6\u7ec3\u6570\u5341\u5e74\uff0c\u5de6\u53f3\u8fdb\u9000\uff0c\u6bcf\u4e00\u6b65\u8e0f\u4e0b\u53bb\u5b9e\u65e0\u5206\u6beb\u4e4b\u5dee\uff0c\u6570\u62db\u4e4b\u95f4\uff0c\u4fbf\u5df2\u62a2\u5f97\u4e2d\u6869\uff0c\u4e8e\u662f\u62f3\u529b\u9010\u6b65\u52a0\u91cd\u3002\u4ed6\u77e5\u8fd9\u5c11\u5973\u5e74\u7eaa\u867d\u8f7b\uff0c\u6b66\u529f\u5b9e\u5f97\u9ad8\u4eba\u4f20\u6388\uff0c\u5374\u4e5f\u4e0d\u6562\u8d38\u7136\u8ff8\u72af\uff0c\u5fc3\u60f3\u53ea\u8981\u5b88\u4f4f\u4e2d\u6869\uff0c\u4fbf\u5df2\u7a33\u64cd\u80dc\u7b97\u3002 \u3000\u3000\u8881\u7d2b\u8863\u4e0e\u5b59\u4f0f\u864e\u3001\u5c09\u8fdf\u8fde\u7b49\u52a8\u624b\uff0c\u867d\u8bf4\u662f\u4e09\u62db\u53d6\u80dc\uff0c\u5176\u5b9e\u5728\u7b2c\u4e00\u62db\u4e2d\u5df2\u662f\u5236\u654c\u673a\u5148\uff0c\u4f46\u6b64\u65f6\u5728\u6885\u82b1\u6869\u4e0a\u4e0e\u5218\u9e64\u771f\u6bd4\u62f3\uff0c\u6bcf\u4e00\u638c\u6bcf\u4e00\u62f3\u51fb\u5c06\u51fa\u53bb\uff0c\u5747\u9047\u5230\u6781\u91cd\u6781\u539a\u7684\u529b\u9053\u53cd\u51fb\uff0c\u5979\u8db3\u5e95\u8e0f\u7684\u662f\u9152\u7897\uff0c\u53ea\u8981\u7740\u529b\u7a0d\u91cd\uff0c\u9152\u7897\u7acb\u7834\uff0c\u8fd9\u573a\u6bd4\u6b66\u4fbf\u7b97\u662f\u8f93\u4e86\uff0c\u56e0\u6b64\u4e0a\u4e00\u6cbe\u5373\u8d70\uff0c\u4ece\u65e0\u4e00\u62db\u6562\u7a0d\u7a0d\u7528\u8001\uff0c\u773c\u89c1\u654c\u4eba\u5b88\u5f97\u6781\u7a33\uff0c\u96be\u4ee5\u64bc\u52a8\uff0c\u53ea\u5f97\u4ee5\u4e0a\u4e58\u8f7b\u529f\u70b9\u8e0f\u9152\u7897\uff0c\u56f4\u7740\u5bf9\u624b\u8eab\u5468\u6e38\u52a8\uff0c\u53ea\u76fc\u627e\u5230\u654c\u65b9\u7834\u7efd\u3002\u4e24\u4eba\u62c6\u5230\u4e09\u5341\u4f59\u62db\uff0c\u4e00\u5957\u516d\u5408\u62f3\u6cd5\u7684\u62db\u6570\u5747\u5df2\u4f7f\u5b8c\uff0c\u4f46\u89c1\u5218\u9e64\u771f\u7626\u7626\u7684\u8eab\u5f62\u5c79\u7acb\u5982\u5c71\uff0c\u62f3\u98ce\u6e10\u54cd\uff0c\u663e\u89c1\u52b2\u529b\u6b62\u81ea\u52a0\u5f3a\u3002 \u3000\u3000\u5404\u95e8\u6b66\u529f\u4e4b\u4e2d\uff0c\u5747\u6709\u6869\u4e0a\u6bd4\u6b66\u4e4b\u6cd5\uff0c\u53ea\u662f\u6869\u5b50\u5374\u53d8\u5f02\u767e\u7aef\uff0c\u6216\u7ad6\u7acb\u6728\u6869\uff0c\u6216\u690d\u4ee5\u9752\u7af9\uff0c\u6216\u53e0\u79ef\u7816\u77f3\uff0c\u751a\u81f3\u662f\u4ee5\u5229\u5203\u63d2\u5730\uff0c\u4f46\u8fd9\u822c\u5728\u5730\u4e0b\u8986\u7897\u4ee5\u4ee3\u6885\u82b1\u6869\uff0c\u5385\u4e0a\u4f17\u6b66\u5e08\u5374\u4ece\u672a\u89c1\u8fc7\u3002\u5218\u9e64\u771f\u8fd9\u4e09\u5341\u516d\u53ea\u9152\u7897\u4f3c\u4e4e\u6563\u653e\u4e71\u7f6e\uff0c\u5e76\u975e\u6574\u6574\u9f50\u9f50\u5730\u5217\u6210\u6885\u82b1\u4e4b\u5f62\uff0c\u4f46\u5176\u4e2d\u81ea\u6709\u89c4\u8303\uff0c\u4ed6\u65e9\u5df2\u4e60\u7ec3\u7eaf\u719f\uff0c\u5373\u4f7f\u95ed\u76ee\u800c\u6597\uff0c\u4e5f\u662f\u4e00\u6b65\u4e0d\u4f1a\u8e0f\u9519\u3002\u8881\u7d2b\u8863\u5374\u662f\u6bcf\u4e00\u6b65\u90fd\u987b\u5148\u5411\u5730\u4e0b\u4e00\u671b\uff0c\u77a7\u5b9a\u9152\u7897\u65b9\u4f4d\uff0c\u8fd9\u624d\u51fa\u8db3\uff0c\u5982\u6b64\u65f6\u5019\u4e00\u957f\uff0c\u62f3\u811a\u4e0a\u7adf\u662f\u6e10\u843d\u4e0b\u98ce\u3002 \u3000\u3000\u5218\u9e64\u771f\u5fc3\u4e2d\u6697\u559c\uff0c\u62f3\u6cd5\u6e10\u53d8\uff0c\u53f3\u624b\u4e09\u89d2\u62f3\u7740\u7740\u6253\u5411\u5bf9\u65b9\u8eab\u4e0a\u5404\u5904\u5927\u7a74\uff0c\u5de6\u624b\u82e6\u607c\u62f3\u5374\u4ee5\u539a\u91cd\u4e4b\u529b\uff0c\u62e6\u5c01\u6a2a\u95e9\uff0c\u4f7f\u7684\u5168\u662f\u622a\u624b\u6cd5\u3002\u8881\u7d2b\u8863\u773c\u89c1\u4e0d\u654c\uff0c\u5de6\u624b\u7a81\u7136\u95f4\u8089\u638c\u53d8\u6307\uff0c\u500f\u5730\u5411\u524d\u523a\u51fa\uff0c\u7adf\u662f\u516d\u5408\u67aa\u6cd5\u4e2d\u7684\u201c\u56db\u5937\u5bbe\u670d\u201d\u3002\u5218\u9e64\u771f\u5403\u4e86\u4e00\u60ca\uff0c\u4e0d\u53ca\u601d\u7d22\uff0c\u6025\u5fd9\u4fa7\u8eab\u907f\u8fc7\uff0c\u5c82\u77e5\u8881\u7d2b\u8863\u53f3\u624b\u6a2a\u65a9\uff0c\u51fa\u62db\u662f\u516d\u5408\u5200\u6cd5\u4e2d\u7684\u4e00\u62db\u201c\u94a9\u6302\u8fdb\u6b65\u8fde\u73af\u5200\u201d\uff0c\u5218\u9e64\u771f\u60f3\u4e0d\u5230\u5979\u62f3\u6cd5\u7adf\u4f1a\u4e00\u53d8\u800c\u6210\u5200\u6cd5\uff0c\u5fae\u4e00\u614c\u4e71\uff0c\u80a9\u5934\u5df2\u88ab\u65a9\u4e2d\uff0c\u4ed6\u80a9\u5934\u6025\u6c89\uff0c\u4e8e\u77ac\u606f\u4e4b\u95f4\u5c06\u65a9\u529b\u5378\u53bb\u4e86\u516b\u6210\uff0c\u8ddf\u7740\u8fd8\u51fb\u4e00\u62f3\u3002\u8881\u7d2b\u8863\u5de6\u624b\u201c\u767d\u733f\u732e\u6843\u201d\u81ea\u4e0b\u800c\u4e0a\u524a\u51fa\uff0c\u90a3\u662f\u53cc\u624b\u90fd\u4f7f\u5200\u6cd5\uff0c\u6240\u7528\u7684\u4e0d\u4f46\u662f\u5355\u5200\uff0c\u4e14\u662f\u53cc\u5200\u4e86\u3002 \u3000\u3000\u8fd9\u4e00\u4e0b\u638c\u5200\u65a9\u81f3\uff0c\u5218\u9e64\u771f\u518d\u96be\u907f\u8fc7\uff0c\u7830\u7684\u4e00\u54cd\uff0c\u80c1\u4e0b\u4e2d\u638c\uff0c\u8eab\u5b50\u4e00\u6643\uff0c\u8dcc\u4e0b\u7897\u6765\u3002 \u3000\u3000\u80e1\u6590\u5728\u65c1\u77a7\u5f97\u660e\u767d\uff0c\u5fc3\u60f3\u8fd9\u4f4d\u6b66\u5b66\u9ad8\u624b\u5982\u6b64\u8d25\u4e8e\u5bf9\u65b9\u602a\u62db\u4e4b\u4e0b\uff0c\u5927\u662f\u53ef\u60dc\uff0c\u968f\u624b\u6293\u8d77\u5e2d\u4e0a\u4e24\u53ea\u7a7a\u9152\u7897\uff0c\u5b66\u7740\u5218\u9e64\u771f\u7684\u624b\u6cd5\uff0c\u5411\u5730\u4e0b\u659c\u6454\u8fc7\u53bb\u3002\u4e24\u53ea\u9152\u7897\u8f7b\u8f7b\u4e00\u6ed1\uff0c\u6b63\u597d\u505c\u5728\u5218\u9e64\u771f\u7684\u811a\u4e0b\u3002 \u3000\u3000\u5218\u9e64\u771f\u8fd9\u4e00\u8dcc\u4e0b\u6885\u82b1\u6869\u6765\uff0c\u53ea\u9053\u5df2\u7136\u8d25\u5b9a\uff0c\u731b\u89c9\u5f97\u811a\u5e95\u591a\u4e86\u4e24\u53ea\u9152\u7897\uff0c\u4e00\u6014\u4e4b\u4e0b\uff0c\u5df2\u77e5\u6709\u9ad8\u4eba\u81ea\u65c1\u6697\u52a9\u3002\u4f17\u4eba\u76ee\u5149\u90fd\u96c6\u4e8e\u76f8\u6597\u7684\u4e24\u4eba\uff0c\u80e1\u6590\u8f7b\u63b7\u9152\u7897\uff0c\u7adf\u6ca1\u4e00\u4eba\u7559\u610f\u3002 \u3000\u3000\u8881\u7d2b\u8863\u4ee5\u6307\u5316\u67aa\uff0c\u4ee5\u624b\u53d8\u5200\uff0c\u51fa\u7684\u867d\u7136\u4ecd\u662f\u516d\u5408\u67aa\u3001\u516d\u5408\u5200\u7684\u529f\u592b\uff0c\u4f46\u662f\u97e6\u9640\u95e8\u4e4b\u4e2d\uff0c\u4ece\u65e0\u5982\u6b64\u602a\u5f02\u7684\u62db\u6570\u3002\u5218\u9e64\u771f\u60ca\u7591\u4e0d\u5b9a\uff0c\u62b1\u62f3\u8bf4\u9053\uff1a\u201c\u59d1\u5a18\u6b66\u529f\u795e\u5999\uff0c\u5728\u4e0b\u4ece\u6240\u672a\u89c1\uff0c\u6562\u95ee\u59d1\u5a18\u662f\u54ea\u4e00\u95e8\u54ea\u4e00\u6d3e\u9ad8\u4eba\u6240\u6388\uff1f\u201d\u8881\u7d2b\u8863\u9053\uff1a\u201c\u54fc\uff0c\u4f60\u5b9a\u7136\u4e0d\u8ba4\u6211\u662f\u672c\u95e8\u5f1f\u5b50\u3002\u4e5f\u7f62\uff0c\u5018\u82e5\u6211\u53ea\u7528\u516d\u5408\u62f3\u80dc\u4f60\uff0c\u90a3\u4fbf\u600e\u5730\uff1f\u201d \u3000\u3000\u5218\u9e64\u771f\u6b63\u8981\u5979\u8bf4\u8fd9\u53e5\u8bdd\uff0c\u606d\u606d\u656c\u656c\u5730\u7b54\u9053\uff1a\u201c\u59d1\u5a18\u5982\u771f\u7528\u672c\u95e8\u6b66\u529f\u6298\u670d\u5728\u4e0b\uff0c\u90a3\u662f\u5149\u5927\u672c\u95e8\u7684\u5929\u5927\u559c\u4e8b\u3002\u5c0f\u8001\u513f\u4fbf\u662f\u8ddf\u59d1\u5a18\u63d0\u9a6c\u97ad\u513f\uff0c\u4e5f\u6240\u7518\u613f\u3002\u201d \u3000\u3000\u4ed6\u9002\u624d\u9886\u6559\u4e86\u8881\u7d2b\u8863\u7684\u6b66\u529f\uff0c\u72c2\u50b2\u4e4b\u6c14\u767b\u655b\uff0c\u8ddf\u7740\u8f6c\u5934\u5411\u80e1\u6590\u90a3\u65b9\u4f4d\u62f1\u624b\u8bf4\u9053\uff1a\u201c\u5c0f\u8001\u513f\u732e\u4e11\u3002\u201d\u8fd9\u4e00\u62f1\u624b\u662f\u76f8\u8c22\u80e1\u6590\u63b7\u7897\u4e4b\u5fb7\uff0c\u4ed6\u867d\u4e0d\u77e5\u63f4\u624b\u7684\u662f\u8c01\uff0c\u4f46\u77e5\u8fd9\u4e24\u53ea\u9152\u7897\u662f\u4ece\u8be5\u5904\u63b7\u6765\u3002 \u3000\u3000\u8881\u7d2b\u8863\u5f53\u5218\u9e64\u771f\u8ffd\u95ee\u5979\u95e8\u6d3e\u4e4b\u65f6\uff0c\u5df2\u60f3\u597d\u4e86\u80dc\u4ed6\u4e4b\u6cd5\uff0c\u89c1\u5218\u9e64\u771f\u62b1\u62f3\u5f52\u4e00\uff0c\u8e0f\u6b65\u53c8\u62a2\u4e2d\u6869\uff0c\u5f53\u5373\u51fa\u4e00\u62db\u201c\u6eda\u624b\u864e\u5750\u201d\uff0c\u4f7f\u7684\u679c\u7136\u662f\u516d\u5408\u62f3\u6b63\u8def\u6b66\u529f\u3002 \u3000\u3000\u6570\u62db\u4e00\u8fc7\uff0c\u5218\u9e64\u771f\u53c8\u6e10\u62a2\u4e0a\u98ce\u3002\u6b64\u65f6\u4ed6\u51fa\u62f3\u62ac\u817f\u4e4b\u9645\uff0c\u6bd4\u5148\u524d\u66f4\u52a0\u4e86\u4e00\u5206\u5c0f\u5fc3\u8c28\u614e\uff0c\u751f\u6015\u5979\u5728\u62f3\u62db\u4e4b\u4e2d\u53c8\u8d77\u82b1\u6837\uff0c\u518d\u62c6\u6570\u62db\uff0c\u89c1\u5bf9\u65b9\u62f3\u6cd5\u65e0\u53d8\uff0c\u5fc3\u4e2d\u7565\u611f\u5bbd\u6170\uff0c\u773c\u89c1\u5979\u4f7f\u7684\u662f\u4e00\u62db\u201c\u6253\u864e\u5f0f\u201d\uff0c\u5f53\u5373\u53f3\u8db3\u5411\u524d\u865a\u70b9\uff0c\u51fa\u4e00\u62db\u201d \u3000\u3000\u4e4c\u9f99\u63a2\u6d77\u201d\uff0c\u7a81\u89c9\u53f3\u811a\u4e0b\u6709\u4e9b\u5f02\u6837\uff0c\u773c\u5149\u5411\u4e0b\u4e00\u77a5\uff0c\u4e0d\u7531\u5f97\u4e00\u60ca\u3002\u53ea\u89c1\u672c\u6765\u5408\u8986\u7740\u7684\u9152\u7897\uff0c\u4e0d\u77e5\u5982\u4f55\u8fd9\u65f6\u7adf\u8f6c\u800c\u4ef0\u5929\u3002\u5e78\u597d\u4ed6\u53f3\u8db3\u53ea\u662f\u865a\u70b9\uff0c\u8fd9\u4e00\u6b65\u82e5\u662f\u8e0f\u5b9e\u4e86\uff0c\u52bf\u5fc5\u8e0f\u5728\u7897\u5fc3\uff0c\u9152\u7897\u56fa\u7136\u975e\u7834\u4e0d\u53ef\uff0c\u540c\u65f6\u8eab\u5b50\u5411\u524d\u4e00\u51b2\uff0c\u7109\u5f97\u4e0d\u8d25\uff1f \u3000\u3000\u4ed6\u4e00\u60ca\u4e4b\u4e0b\uff0c\u6025\u5fd9\u534a\u7a7a\u79fb\u6b65\uff0c\u53e6\u8e0f\u4e00\u7897\uff0c\u8eab\u5b50\u6643\u52a8\uff0c\u80cc\u4e0a\u5df2\u51fa\u4e86\u4e00\u8eab\u51b7\u6c57\uff0c\u659c\u773c\u770b\u65f6\uff0c\u53ea\u89c1\u8881\u7d2b\u8863\u5de6\u8db3\u63d0\u8d77\u65f6\u5c06\u9152\u7897\u8f7b\u8f7b\u5e26\u8d77\uff0c\u4e5f\u4e0d\u77e5\u5979\u8db3\u5e95\u5982\u4f55\u4f7f\u52b2\uff0c\u653e\u4e0b\u65f6\u90a3\u9152\u7897\u5df2\u7ffb\u4e86\u8fc7\u6765\uff0c\u5979\u5de6\u8db3\u987a\u52bf\u8e0f\u5728\u7897\u53e3\uff0c\u53f3\u8db3\u63d0\u8d77\uff0c\u53c8\u5c06\u53e6\u4e00\u53ea\u9152\u7897\u7ffb\u8f6c\uff0c\u8fd9\u4e00\u624b\u8f7b\u529f\u81ea\u5df1\u5982\u4f55\u80fd\u53ca\uff1f\u5fc3\u60f3\uff1a\u201c\u53ea\u6709\u6025\u4f7f\u91cd\u4e0b\uff0c\u4e58\u7740\u5979\u672a\u5c06\u9152\u7897\u5c3d\u6570\u7ffb\u8f6c\uff0c\u5148\u5c06\u5979\u6253\u4e0b\u6869\u53bb\u3002\u201d\u5f53\u4e0b\u50ac\u52a8\u638c\u529b\uff0c\u52a0\u5feb\u8fdb\u903c\u3002\u54ea\u77e5\u8881\u7d2b\u8863\u4e0d\u518d\u4e0e\u4ed6\u6b63\u9762\u5bf9\u62f3\uff0c\u53ea\u662f\u6765\u5f80\u6e38\u8d70\uff0c\u8eab\u6cd5\u5feb\u6377\u5f02\u5e38\uff0c\u5728\u7897\u53e3\u4e0a\u4e00\u7740\u8db3\u7acb\u5373\u6362\u6b65\uff0c\u7adf\u65e0\u970e\u65f6\u4e4b\u95f4\u505c\u7559\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u5df2\u5c06\u4e09\u5341\u516b\u53ea\u9152\u7897\u7ffb\u4e86\u4e09\u5341\u516d\u53ea\uff0c\u53ea\u5269\u4e0b\u5218\u9e64\u771f\u53cc\u811a\u6240\u8e0f\u7684\u4e24\u53ea\u5c1a\u672a\u7ffb\u8f6c\u3002\u82e5\u4e0d\u662f\u80e1\u6590\u9002\u624d\u63b7\u4e86\u4e24\u53ea\u7897\u8fc7\u53bb\uff0c\u4ed6\u662f\u8fde\u7acb\u8db3\u4e4b\u5904\u4e5f\u6ca1\u6709\u4e86\u3002 \u3000\u3000\u5f53\u6b64\u60c5\u52bf\uff0c\u5218\u9e64\u771f\u53ea\u8981\u4e00\u51fa\u8db3\u7acb\u65f6\u8e0f\u7834\u9152\u7897\uff0c\u53ea\u6709\u7ad9\u5728\u4e24\u53ea\u9152\u7897\u4e4b\u4e0a\uff0c\u4e0d\u80fd\u79fb\u52a8\u534a\u6b65\uff0c\u5446\u7acb\u5c11\u65f6\uff0c\u8138\u8272\u51c4\u60e8\uff0c\u8bf4\u9053\uff1a\u201c\u662f\u59d1\u5a18\u80dc\u4e86\u3002\u201d\u4e3e\u6b65\u843d\u5730\uff0c\u8138\u4e0a\u66f4\u662f\u9ec4\u5f97\u5b9b\u5982\u91d1\u7eb8\u4e00\u822c\u3002 \u3000\u3000\u8881\u7d2b\u8863\u5927\u662f\u5f97\u610f\uff0c\u95ee\u9053\uff1a\u2019\u8fd9\u638c\u95e8\u662f\u6211\u505a\u4e86\u5427\uff1f\u201d\u5218\u9e64\u771f\u9eef\u7136\u9053\uff1a\u201c\u5c0f\u8001\u513f\u662f\u670d\u4e86\u4f60\u5566\uff0c\u4f46\u4e0d\u77e5\u65c1\u4eba\u6709\u4f55\u8bdd\u8bf4\uff1f\u201d\u8881\u7d2b\u8863\u6b63\u8981\u53d1\u8a00\u8be2\u95ee\u4f17\u4eba\uff0c\u5ffd\u542c\u5f97\u95e8\u5916\u9a6c\u8e44\u58f0\u6025\u4fc3\u5f02\u5e38\uff0c\u5411\u5317\u75be\u9a70\uff1b\u542c\u8fd9\u9a6c\u8e44\u843d\u5730\u4e4b\u58f0\uff0c\u4e16\u95f4\u9664\u4e86\u81ea\u5df1\u7684\u767d\u9a6c\u4e4b\u5916\uff0c\u66f4\u65e0\u522b\u9a79\u3002 \u3000\u3000\u5979\u8138\u8272\u5fae\u53d8\uff0c\u62a2\u6b65\u51fa\u95e8\uff0c\u53ea\u89c1\u767d\u9a6c\u7684\u80cc\u5f71\u521a\u5728\u67ab\u6797\u8fb9\u8f6c\u8fc7\uff0c\u9a6c\u80cc\u4e0a\u9a91\u7740\u4e00\u4e2a\u7070\u8863\u7537\u5b50\uff0c\u6b63\u662f\u81ea\u5df1\u5077\u4e86\u4ed6\u5305\u88b1\u7684\u80e1\u6590\u3002 \u3000\u3000\u5979\u7eb5\u58f0\u5927\u53eb\uff1a\u201c\u5077\u9a6c\u8d3c\uff0c\u5feb\u505c\u4e0b\uff01\u201d\u80e1\u6590\u53e3\u5934\u7b11\u9053\uff1a\u201c\u5077\u5305\u8d3c\uff0c\u54b1\u4eec\u6389\u6362\u4e86\u5427\uff01\u201d\u8bf4\u7740\u54c8\u54c8\u5927\u7b11\uff0c\u7b56\u9a6c\u6025\u9a70\u3002 \u3000\u3000\u8881\u7d2b\u8863\u5927\u6012\uff0c\u63d0\u6c14\u72c2\u5954\u3002\u5979\u8f7b\u529f\u867d\u7136\u4e86\u5f97\uff0c\u5374\u600e\u53ca\u5f97\u4e0a\u8fd9\u5339\u65e5\u884c\u5343\u91cc\u7684\u5feb\u9a6c\uff1f\u5954\u4e86\u4e00\u9635\uff0c\u4f46\u89c1\u4eba\u9a6c\u7684\u5f71\u5b50\u8d8a\u6765\u8d8a\u5c0f\uff0c\u7ec8\u4e8e\u518d\u4e5f\u77a7\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u8fd9\u4e00\u4e2a\u632b\u6298\uff0c\u5c06\u5979\u8fde\u80dc\u97e6\u9640\u95e8\u56db\u540d\u597d\u624b\u7684\u5f97\u610f\u4e4b\u60c5\u767b\u65f6\u6d88\u5f97\u5e72\u5e72\u51c0\u51c0\u3002 \u3000\u3000\u5979\u5fc3\u4e0b\u6c14\u607c\uff0c\u5374\u53c8\u5947\u602a\uff1a\u201c\u8fd9\u767d\u9a6c\u5927\u6709\u7075\u6027\uff0c\u600e\u80fd\u5bb9\u8fd9\u5c0f\u8d3c\u5077\u4e86\u4fbf\u8dd1\uff0c\u6beb\u4e0d\u53cd\u6297\uff1f\u201d \u3000\u3000\u5979\u5954\u51fa\u6570\u91cc\uff0c\u6765\u5230\u4e00\u4e2a\u5c0f\u9547\uff0c\u77e5\u9053\u518d\u4e5f\u8d76\u4e0d\u4e0a\u767d\u9a6c\uff0c\u8981\u5f85\u627e\u5bb6\u8336\u94fa\u559d\u8336\u4f11\u606f\uff0c\u5ffd\u542c\u5f97\u9547\u5934\u4e00\u58f0\u957f\u5636\uff0c\u58f0\u97f3\u751a\u719f\uff0c\u6b63\u662f\u767d\u9a6c\u7684\u53eb\u58f0\u3002\u5979\u6025\u6b65\u8d76\u53bb\uff0c\u8f6c\u4e86\u4e00\u4e2a\u5f2f\uff0c\u4f46\u89c1\u80e1\u6590\u9a91\u7740\u767d\u9a6c\uff0c\u56de\u5934\u5411\u5979\u5fae\u7b11\u62db\u624b\u3002 \u3000\u3000\u8881\u7d2b\u8863\u5927\u6012\uff0c\u968f\u4e0b\u62fe\u8d77\u4e00\u5757\u77f3\u5b50\uff0c\u5411\u4ed6\u80cc\u5fc3\u6295\u63b7\u8fc7\u53bb\u3002\u80e1\u6590\u9664\u4e0b\u5934\u4e0a\u5e3d\u5b50\uff0c\u53cd\u624b\u4e00\u515c\uff0c\u5c06\u77f3\u5b50\u515c\u5728\u5e3d\u4e2d\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u8fd8\u6211\u5305\u88b1\u4e0d\u8fd8\uff1f\u201d\u8881\u7d2b\u8863\u7eb5\u8eab\u5411\u524d\uff0c\u8981\u53bb\u62a2\u593a\u767d\u9a6c\uff0c\u7a81\u542c\u547c\u7684\u4e00\u54cd\uff0c\u4e00\u4ef6\u6697\u5668\u6765\u52bf\u52b2\u6025\uff0c\u8fce\u9762\u63b7\u5c06\u8fc7\u6765\u3002 \u3000\u3000\u5979\u4f38\u5de6\u624b\u63a5\u4f4f\uff0c\u6b63\u662f\u767d\u5df1\u6295\u8fc7\u53bb\u7684\u90a3\u5757\u77f3\u5b50\uff0c\u5c31\u8fd9\u4e48\u7f13\u5f97\u4e00\u7f13\uff0c\u53ea\u89c1\u80e1\u6590\u53cc\u817f\u4e00\u5939\uff0c\u767d\u9a6c\u5954\u817e\u800c\u8d77\uff0c\u500f\u5ffd\u5df2\u5728\u5341\u6570\u4e08\u5916\u3002 \u3000\u3000\u8881\u7d2b\u8863\u6012\u6781\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u5c0f\u5b50\u5982\u6b64\u53ef\u6076\uff0c\u201d\u5979\u4e0d\u602a\u81ea\u5df1\u5148\u76d7\u4eba\u5bb6\u5305\u88b1\uff0c\u5374\u607c\u4ed6\u4e24\u6b21\u620f\u5f04\uff0c\u53ea\u6068\u767d\u9a6c\u811a\u7a0b\u592a\u5feb\uff0c\u5426\u5219\u8ffd\u4e0a\u4e86\u4ed6\uff0c\u593a\u8fd8\u767d\u9a6c\u4e0d\u7b97\uff0c\u4e0d\u72e0\u72e0\u63cd\u4ed6\u4e00\u987f\uff0c\u4e5f\u771f\u96be\u51fa\u5fc3\u5934\u4e4b\u6c14\uff0c\u53ea\u89c1\u4e00\u5ea7\u5c4b\u5b50\u6a90\u4e0b\u7cfb\u7740\u4e00\u5339\u9752\u9a6c\uff0c\u5979\u4e0d\u7ba1\u4e09\u4e03\u4e8c\u5341\u4e00\uff0c\u5954\u8fc7\u53bb\u89e3\u5f00\u7f30\u7ef3\uff0c\u98de\u8eab\u800c\u4e0a\uff0c\u5411\u80e1\u6590\u7684\u53bb\u8def\u75be\u8ffd\uff0c\u4f8d\u5f97\u9a6c\u4e3b\u60ca\u89c9\uff0c\u5927\u53eb\u5927\u9a82\u5730\u8ffd\u51fa\u6765\u65f6\uff0c\u5979\u65e9\u5df2\u53bb\u5f97\u8fdc\u4e86\u3002 \u3000\u3000\u8881\u7d2b\u8863\u867d\u6709\u5750\u9a91\uff0c\u4f46\u8bf4\u8981\u8ffd\u4e0a\u80e1\u6590\uff0c\u5374\u662f\u4f11\u60f3\uff0c\u4e00\u53e3\u6c14\u5168\u51fa\u5728\u7272\u53e3\u8eab\u4e0a\uff0c\u4e0d\u4f4f\u7684\u4e71\u97ad\u4e71\u8e22\u3002\u90a3\u9752\u9a6c\u5176\u5b9e\u5df2\u662f\u7aed\u5c3d\u5168\u529b\uff0c\u5979\u4ecd\u5acc\u8dd1\u5f97\u592a\u6162\u3002\u9a70\u51fa\u6570\u91cc\uff0c\u9752\u9a6c\u547c\u547c\u5598\u6c14\uff0c\u6e10\u611f\u4e0d\u652f\u3002\u5c06\u8fd1\u4e00\u7247\u6811\u6797\uff0c\u53ea\u89c1\u4e00\u68f5\u5927\u677e\u6811\u4e0b\u6709\u4e00\u4ef6\u767d\u8272\u4e4b\u7269\uff0c\u5f85\u5f97\u9a70\u8fd1\uff0c\u5374\u4e0d\u662f\u90a3\u767d\u9a6c\u662f\u4ec0\u4e48\uff1f \u3000\u3000\u5979\u5fc3\u4e2d\u5927\u559c\uff0c\u4f46\u6015\u80e1\u6590\u5b89\u6392\u4e0b\u8be1\u8ba1\uff0c\u5f15\u81ea\u5df1\u4e0a\u5f53\uff0c\u56db\u4e0b\u91cc\u4e00\u671b\uff0c\u4e0d\u89c1\u6b64\u4eba\u5f71\u8e2a\uff0c\u8fd9\u624d\u7eb5\u9a6c\u6ce8\u677e\u6811\u4e0b\u5954\u53bb\u3002\u79bb\u90a3\u767d\u9a6c\u7ea6\u6709\u6570\u4e08\uff0c\u7a81\u89c1\u677e\u6811\u4e0a\u4e00\u4e2a\u4eba\u5f71\u843d\u4e86\u4e0b\u6765\uff0c\u6b63\u597d\u9a91\u5728\u767d\u9a6c\u80cc\u4e0a\uff0c\u54c8\u54c8\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u54c0\u59d1\u5a18\uff0c\u54b1\u4eec\u518d\u8d5b\u4e00\u7a0b\u3002\u201d \u3000\u3000\u8fd9\u65f6\u8881\u7d2b\u8863\u54ea\u518d\u5bb9\u4ed6\u9003\u8131\uff0c\u53cc\u8db3\u5728\u9a6c\u9563\u4e0a\u4e00\u767b\uff0c\u8eab\u5b50\u7a81\u5730\u98de\u8d77\uff0c\u5982\u4e00\u53ea\u5927\u9e1f\u822c\u5411\u80e1\u6590\u6251\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u80e1\u6590\u6599\u4e0d\u5230\u5979\u7ade\u6562\u5982\u6b64\u884c\u9669\uff0c\u5728\u7a7a\u4e2d\u98de\u6251\u800c\u81f3\uff0c\u82e5\u662f\u81ea\u5df1\u51fb\u51fa\u4e00\u638c\uff0c\u5979\u5728\u534a\u7a7a\u4e2d\u5982\u4f55\u80fd\u907f\uff1f\u5f53\u5373\u4e00\u52d2\u9a6c\u7f30\uff0c\u8981\u5750\u9a91\u5411\u65c1\u907f\u5f00\uff0c\u5c82\u77e5\u767d\u9a6c\u8ba4\u4e3b\uff0c\u53e3\u4e2d\u4f4e\u58f0\u6b22\u5636\uff0c\u975e\u4f46\u4e0d\u907f\uff0c\u53cd\u800c\u5411\u524d\u8fce\u4e0a\u4e24\u6b65\u3002 \u3000\u3000\u8881\u7d2b\u8863\u5728\u534a\u7a7a\u4e2d\u53f3\u638c\u5411\u80e1\u6590\u5934\u9876\u51fb\u843d\uff0c\u5de6\u624b\u5f80\u4ed6\u80a9\u5934\u6293\u53bb\u3002\u80e1\u6590\u4e00\u751f\u4e4b\u4e2d\uff0c\u4ece\u672a\u548c\u5e74\u8f7b\u5973\u5b50\u52a8\u8fc7\u624b\uff0c\u8fd9\u6b21\u76d7\u5979\u767d\u9a6c\uff0c\u4e00\u6765\u8ba4\u5f97\u8fd9\u662f\u8d75\u534a\u5c71\u7684\u5750\u9a91\uff0c\u8981\u95ee\u5979\u4e00\u4e2a\u660e\u767d\uff0c\u4e8c\u6765\u602a\u5979\u53d6\u53bb\u81ea\u5df1\u5305\u88b1\uff0c\u663e\u6709\u8f7b\u4fae\u4e4b\u610f\uff0c\u8981\u5c0f\u5c0f\u62a5\u590d\u4e00\u4e0b\uff0c\u4f46\u7a81\u7136\u89c1\u5979\u5f53\u771f\u52a8\u624b\uff0c\u4e0d\u7981\u8138\u4e0a\u4e00\u7ea2\uff0c\u8eab\u5b50\u4e00\u504f\uff0c\u8dc3\u79bb\u9a6c\u80cc\uff0c\u4ece\u5979\u8eab\u65c1\u63a0\u8fc7\uff0c\u5df2\u9a91\u4e0a\u4e86\u9752\u9a6c\u3002 \u3000\u3000\u4e8c\u4eba\u5728\u7a7a\u4e2d\u4ea4\u5dee\u800c\u8fc7\u3002\u80e1\u6590\u53f3\u624b\u4f38\u51fa\uff0c\u6f5c\u8fd0\u6307\u529b\uff0c\u626f\u65ad\u5979\u80cc\u4e0a\u5305\u88b1\u7684\u7cfb\u7ef3\uff0c\u5df2\u5c06\u5305\u88b1\u53d6\u5728\u624b\u4e2d\u3002\u8881\u7d2b\u8863\u593a\u8fd8\u767d\u9a6c\uff0c\u4f59\u6012\u672a\u6d88\uff0c\u53c8\u89c1\u5305\u88b1\u7ed9\u4ed6\u53d6\u56de\uff0c\u53eb\u9053\uff1a\u201c\u5c0f\u80e1\u6590\uff0c\u4f60\u600e\u6562\u5982\u6b64\u65e0\u793c\uff1f\u201d\u80e1\u6590\u4e00\u60ca\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u600e\u77e5\u6211\u540d\u5b57\uff1f\u201d \u3000\u3000\u8881\u7d2b\u8863\u5c0f\u5634\u5fae\u6241\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u8d75\u4e09\u53d4\u5938\u4f60\u82f1\u96c4\u4e86\u5f97\uff0c\u6211\u77a7\u4e5f\u7a00\u677e\u5e73\u5e38\u3002\u201d \u3000\u3000\u80e1\u6590\u542c\u5230\u201c\u8d75\u4e09\u53d4\u201d\u4e09\u5b57\uff0c\u5fc3\u4e2d\u5927\u559c\uff0c\u5fd9\u9053\uff1a\u201c\u4f60\u8bc6\u5f97\u8d75\u534a\u5c71\u8d75\u4e09\u54e5\u4e48\uff1f \u3000\u3000\u4ed6\u5728\u54ea\u91cc\uff1f\u201d\u8881\u7d2b\u8863\u4fcf\u8138\u4e0a\u66f4\u589e\u4e86\u4e00\u5c42\u6012\u6c14\uff0c\u559d\u9053\uff1a\u201c\u59d3\u80e1\u7684\u5c0f\u5b50\uff0c\u4f60\u6562\u8ba8\u6211\u4fbf\u5b9c\uff1f\u201d\u80e1\u6590\u6115\u7136\u9053\uff1a\u201c\u6211\u8ba8\u4ec0\u4e48\u4fbf\u5b9c\u4e86\uff1f\u201d\u8881\u7d2b\u8863\u9053\uff1a\u201c\u600e\u4e48\u6211\u53eb\u8d75\u4e09\u53d4\uff0c\u4f60\u4fbf\u53eb\u8d75\u4e09\u54e5\uff0c\u8fd9\u4e0d\u662f\u60f3\u505a\u6211\u957f\u8f88\u4e48\u300b\uff1f\u201d \u3000\u3000\u80e1\u6590\u81ea\u5c0f\u751f\u6027\u6ed1\u7a3d\uff0c\u4f38\u4e86\u4f38\u820c\u5934\uff0c\u7b11\u9053\uff1a\u201c\u4e0d\u6562\uff0c\u4e0d\u6562\uff01\u4f60\u5f53\u771f\u53eb\u4ed6\u8d75\u4e09\u53d4\uff1f\u201d\u8881\u7d2b\u8863\u9053\uff1a\u201c\u96be\u9053\u9a97\u4f60\u4e86\uff1f\u201d\u80e1\u6590\u5c06\u8138\u4e00\u677f\uff0c\u9053\uff1a\u201c\u597d\uff0c\u90a3\u6211\u4fbf\u957f\u4f60\u4e00\u8f88\u3002\u4f60\u53eb\u6211\u80e1\u53d4\u53d4\u5427\uff0c\u5582\uff0c\u7d2b\u8863\uff0c\u8d75\u4e09\u54e5\u5728\u54ea\u91cc\u554a\uff1f\u201d \u3000\u3000\u8881\u7d2b\u8863\u5374\u4ece\u6765\u4e0d\u7231\u65c1\u4eba\u5f00\u5979\u73a9\u7b11\uff0c\u5979\u867d\u77e5\u80e1\u6590\u4e0e\u8d75\u534a\u5c71\u4e49\u7ed3\u5144\u5f1f\uff0c\u4e43\u662f\u5343\u771f\u4e07\u786e\u4e4b\u4e8b\uff0c\u53ea\u89c1\u4ed6\u5e74\u7eaa\u4e0e\u81ea\u5df1\u76f8\u82e5\uff0c\u5374\u539a\u8d77\u8138\u76ae\u4e0e\u8d75\u534a\u5c71\u79f0\u5144\u9053\u5f1f\uff0c\u5f3a\u5c45\u957f\u8f88\uff0c\u66f4\u662f\u6709\u6c14\uff0c\u5237\u7684\u4e00\u58f0\uff0c\u4ece\u8170\u95f4\u62bd\u51fa\u4e00\u6761\u8f6f\u97ad\uff0c\u559d\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u80e1\u8bf4\u516b\u9053\uff0c\u6211\u6559\u8bad\u6559\u8bad\u4f60\u3002\u201d \u3000\u3000\u80e1\u6590\u89c1\u5979\u8fd9\u6761\u8f6f\u97ad\u4e43\u94f6\u4e1d\u7f20\u5c31\uff0c\u97ad\u7aef\u6709\u4e00\u679a\u5c0f\u5c0f\u91d1\u7403\uff0c\u6a21\u6837\u751a\u662f\u7f8e\u89c2\uff0c\u5979\u5c06\u8f6f\u97ad\u5728\u7a7a\u4e2d\u6325\u5382\u4e2a\u5708\u5b50\uff0c\u592a\u9633\u7167\u5c04\u4e4b\u4e0b\uff0c\u91d1\u94f6\u95ea\u707f\uff0c\u53d8\u5e7b\u5947\u4e3d\u3002\u5979\u672c\u60f3\u4e0b\u9a6c\u548c\u80e1\u6590\u52a8\u624b\uff0c\u4f46\u4e00\u8f6c\u5ff5\u95f4\uff0c\u6015\u80e1\u6590\u8be1\u8ba1\u591a\u7aef\uff0c\u53c8\u8981\u593a\u9a6c\uff0c\u4e8e\u662f\u50ac\u9a6c\u4e0a\u524d\uff0c\u6325\u97ad\u5f80\u80e1\u6590\u5934\u9876\u51fb\u843d\u3002\u8fd9\u8f6f\u97ad\u5c55\u5f00\u6765\u6709\u4e00\u4e08\u4e00\u5c3a\u957f\uff0c\u7ed5\u8fc7\u80e1\u6590\u8eab\u540e\uff0c\u97ad\u5934\u5f2f\u8f6c\uff0c\u91d1\u7403\u5f84\u81ea\u51fb\u5411\u4ed6\u80cc\u5fc3\u4e0a\u7684\u201c\u5927\u690e\u7a74\u201d\u3002 \u3000\u3000\u80e1\u6590\u4e0a\u8eab\u4e00\u5f2f\uff0c\u4f0f\u5728\u9a6c\u80cc\uff0c\u53ea\u9053\u4f9d\u7740\u8f6f\u97ad\u8fd9\u4e00\u63a0\u4e4b\u52bf\uff0c\u97ad\u5b50\u5fc5\u5728\u80cc\u810a\u4e0a\u63a0\u8fc7\u3002\u731b\u542c\u5f97\u98ce\u58f0\u6709\u5f02\uff0c\u77e5\u9053\u4e0d\u5999\uff0c\u5de6\u5b50\u62bd\u51fa\u5355\u5200\uff0c\u4e0d\u53ca\u56de\u5934\u77a7\u90a3\u8f6f\u97ad\u6765\u52bf\uff0c\u968f\u624b\u4e00\u5200\u53cd\u6325\uff0c\u5f53\u7684\u4e00\u58f0\uff0c\u5355\u5200\u4e0e\u91d1\u7403\u76f8\u649e\uff0c\u5df1\u5c06\u8881\u7d2b\u8863\u7684\u8f6f\u97ad\u53cd\u8361\u4e86\u5f00\u53bb\u3002 \u3000\u3000\u539f\u6765\u5979\u8f6f\u97ad\u63a0\u8fc7\u80e1\u6590\u80cc\u5fc3\uff0c\u8ddf\u7740\u624b\u8155\u4e00\u6c89\uff0c\u91d1\u7403\u5ffd\u5730\u8f6c\u5411\uff0c\u6253\u5411\u4ed6\u53f3\u80a9\u7684\u201c\u5de8\u9aa8\u7a74\u201d\u3002\u5979\u773c\u89c1\u80e1\u6590\u4f0f\u5728\u9a6c\u80cc\uff0c\u53ea\u9053\u8fd9\u4e00\u4e0b\u5b9a\u5df2\u6253\u4e2d\u4ed6\u7684\u7a74\u9053\uff0c\u8981\u53eb\u4ed6\u7acb\u65f6\u534a\u8eab\u9ebb\u8f6f\u3002\u54ea\u77e5\u4ed6\u542c\u98ce\u51fa\u62db\uff0c\u7adf\u4f3c\u80cc\u540e\u751f\u4e86\u773c\u775b\uff0c\u5200\u97ad\u76f8\u4ea4\uff0c\u53ea\u9707\u5f97\u5979\u624b\u81c2\u5fae\u5fae\u9178\u9ebb\u3002 \u3000\u3000\u80e1\u6590\u62ac\u8d77\u5934\u6765\uff0c\u563b\u563b\u4e00\u7b11\uff0c\u5fc3\u4e2d\u5374\u60ca\u5f02\u8fd9\u5973\u90ce\u7684\u6b66\u529f\u597d\u751f\u4e86\u5f97\uff0c\u5979\u4ee5\u8f6f\u97ad\u97ad\u68a2\u6253\u7a74\uff0c\u5df2\u662f\u6b66\u5b66\u4e2d\u5341\u5206\u96be\u5f97\u7684\u529f\u592b\uff0c\u4f55\u51b5\u4e2d\u9014\u53d8\u62db\uff0c\u5c06\u4e00\u6761\u53c8\u957f\u53c8\u8f6f\u7684\u5175\u5200\u4f7f\u5f97\u5b9b\u5982\u624b\u6307\u4e00\u822c\uff0c\u51fb\u6253\u7a74\u9053\uff0c\u7adf\u5c24\u5398\u6beb\u4e4b\u5dee\uff0c\u540c\u65f6\u4e0d\u7981\u6697\u8089\u60ed\u6127\uff0c\u5e78\u597d\u5979\u6253\u7a74\u529f\u592b\u6781\u5176\u9ad8\u5f3a\uff0c\u95e9\u5df1\u624d\u4e0d\u53d7\u4f24\u3002 \u3000\u3000\u539f\u6765\u4ed6\u867d\u89c1\u8881\u7d2b\u8863\u8fde\u8d25\u97e6\u9640\u95e8\u56db\u597d\u624b\uff0c\u6b66\u529f\u9ad8\u5f3a\uff0c\u4f46\u4ecd\u9053\u5979\u827a\u4e0d\u5982\u5df1\uff0c\u5bf9\u62db\u4e4b\u9645\uff0c\u4e0d\u514d\u5b58\u4e86\u4e09\u5206\u8f7b\u89c6\u4e4b\u5fc3\uff0c\u5c82\u77e5\u5979\u8f6f\u97ad\u6253\u7a74\uff0c\u8fc7\u80cc\u56de\u80a9\uff0c\u7740\u7740\u5927\u51fa\u4e8e\u81ea\u5df1\u610f\u6599\u4e4b\u5916\uff0c\u9002\u624d\u53cd\u624b\u8fd9\u4e00\u5200\uff0c\u6599\u5b9a\u5979\u662f\u51fb\u5411\u81ea\u5df1\u5de8\u9aa8\u7a74\uff0c\u8fd9\u624d\u5f97\u4ee5\u5c06\u5979\u97ad\u68a2\u8361\u5f00\uff0c\u82e5\u662f\u5979\u6280\u4e1b\u7565\u5dee\uff0c\u6253\u7a74\u7a0d\u6709\u4e0d\u51c6\uff0c\u8fd9\u4e00\u5200\u81ea\u662f\u780d\u4e0d\u4e2d\u5979\u97ad\u68a2\uff0c\u90a3\u4e48\u8089\u5df1\u80cc\u4e0a\u4fbf\u4f1a\u91cd\u91cd\u5403\u4e86\u4e00\u4e0b\uff0c\u867d\u7136\u4e0d\u4e2d\u7a74\u9053\uff0c\u4e00\u4e0b\u5267\u75db\u52bf\u5fc5\u96be\u514d\u3002 \u3000\u3000\u8881\u7d2b\u8863\u4f46\u89c1\u4ed6\u795e\u8272\u81ea\u82e5\uff0c\u5b9e\u4e0d\u77e5\u4ed6\u5fc3\u4e2d\u5df2\u662f\u5927\u4e3a\u5403\u60ca\uff0c\u4e0d\u7531\u5f97\u5fae\u611f\u6c14\u9981\uff0c\u957f\u97ad\u5728\u534a\u7a7a\u4e2d\u4e00\u6296\uff0c\u556a\u7684\u4e00\u58f0\u7206\u54cd\uff0c\u97ad\u68a2\u53c8\u5411\u4ed6\u5934\u4e0a\u51fb\u53bb\u3002 \u3000\u3000\u80e1\u6590\u5fc3\u5ff5\u4e00\u529f\uff1a\u201c\u6211\u8981\u5411\u5979\u6253\u542c\u8d75\u4e09\u54e5\u7684\u6d88\u606f\uff0c\u773c\u89c1\u8fd9\u59d1\u5a18\u6027\u513f\u9ad8\u505a\uff0c\u82e5\u4e0d\u5360\u4e9b\u4fbf\u5b9c\uff0c\u600e\u80af\u660e\u767d\u8ddf\u6211\u8bf4\u51fa\uff1f\u8bf4\u4e0d\u5f97\uff0c\u77a7\u5728\u8d75\u4e09\u54e5\u9762\u4e0a\uff0c\u4fbf\u8ba9\u5979\u4e00\u62db\u3002 \u3000\u3000\u89c1\u97ad\u68a2\u582a\u582a\u51fb\u5230\u5934\u9876\uff0c\u5c06\u5934\u5411\u5de6\u4e00\u8ba9\uff0c\u8fd9\u4e00\u8ba9\u65b9\u4f4d\u662f\u6070\u5230\u597d\u5904\uff0c\u65f6\u523b\u5374\u7565\u8fdf\u4e00\u970e\u4e4b\u95f4\uff0c\u4f46\u542c\u5f97\u6ce2\u7684\u4e00\u58f0\uff0c\u5934\u4e0a\u5e3d\u5b50\u5df2\u88ab\u97ad\u68a2\u5377\u4e0b\u3002\u80e1\u6590\u53cc\u817f\u4e00\u5939\uff0c\u7eb5\u9a6c\u7a9c\u5f00\u4e08\u8bb8\uff0c\u8fd8\u5200\u5165\u9798\uff0c\u56de\u5934\u7b11\u9053\uff1a\u201c\u59d1\u5a18\u8f6f\u97ad\u4f38\u6280\uff0c\u80e1\u6590\u4f69\u670d\u5f97\u5f88\u3002\u8d75\u4e09\u54e5\u4ed6\u8eab\u5b50\u53ef\u597d\uff1f\u4ed6\u773c\u4e0b\u662f\u5728\u56de\u7586\u5462\u8fd8\u662f\u5230\u4e86\u4e2d\u539f\uff1f\u201d \u3000\u3000\u4ed6\u82e5\u662f\u771f\u5fc3\u76f8\u8ba9\uff0c\u8881\u7d2b\u8863\u80dc\u4e86\u8fd9\u4e00\u62db\uff0c\u5fc3\u4e2d\u4e00\u5f97\u610f\uff0c\u8bf4\u4e0d\u5b9a\u4fbf\u5c06\u8d75\u534a\u5c71\u7684\u8baf\u606f\u76f8\u544a\u3002\u504f\u751f\u4ed6\u5e74\u5c11\u6c14\u76db\uff0c\u4e5f\u662f\u4e2a\u6781\u597d\u80dc\u4e4b\u4eba\uff0c\u8fd9\u4e00\u62db\u8ba9\u662f\u8ba9\u4e86\uff0c\u5374\u8ba9\u5f97\u592a\u8fc7\u660e\u663e\uff0c\u4f8d\u5979\u97ad\u5230\u4e34\u5934\uff0c\u65b9\u624d\u95ea\u907f\uff0c\u800c\u5e3d\u5b50\u88ab\u5377\uff0c\u8138\u4e0a\u4e0d\u9732\u4e1d\u6beb\u7f9e\u6127\u4e4b\u8272\uff0c\u53cd\u800c\u542b\u7b11\u76f8\u8be2\uff0c\u7b80\u76f4\u6709\u70b9\u957f\u8f88\u620f\u800d\u5c0f\u8f88\u6a21\u6837\u3002\u8881\u7d2b\u8863\u65e9\u5df2\u4e00\u773c\u770b\u51fa\uff0c\u51b7\u7136\u9053\uff1a\u201c\u4f60\u6545\u610f\u76f8\u8ba9\uff0c\u5f53\u6211\u4e0d\u77e5\u9053\u4e48\uff1f\u5e3d\u5b50\u8fd8\u4f60\u5427\uff01\u201d\u8bf4\u7740\u957f\u97ad\u8f7b\u8f7b\u4e00\u6296\uff0c\u5377\u7740\u5e3d\u5b50\u5f80\u4ed6\u5934\u4e0a\u6234\u53bb\u3002 \u3000\u3000\u80e1\u6590\u5fc3\u60f3\uff1a\u201c\u5979\u82e5\u80fd\u7528\u8f6f\u97ad\u53c8\u5c06\u5e3d\u5b50\u7ed9\u6211\u6234\u4e0a\uff0c\u8fd9\u5206\u529f\u592b\u4e5f\u5c31\u5947\u5999\u5f97\u7d27\uff0c\u6211\u5982\u4f38\u624b\u53bb\u63a5\uff0c\u53cd\u800c\u963b\u4e86\u5979\u7684\u5174\u5934\u3002\u201d\u4e8e\u662f\u542b\u7b11\u4e0d\u52a8\uff0c\u77a7\u5979\u662f\u5426\u771f\u80fd\u5c06\u8fd9\u4e08\u4f59\u957f\u7684\u94f6\u4e1d\u8f6f\u97ad\uff0c\u8fd0\u7528\u5f97\u5982\u81c2\u4f7f\u624b\u3002\u4f3c\u89c1\u97ad\u68a2\u5377\u7740\u5e3d\u5b50\uff0c\u987a\u7740\u4ed6\u80f8\u53e3\u4ece\u4e0b\u800c\u4e0a\u515c\u5c06\u4e0a\u6765\uff0c\u53ea\u56e0\u4e0a\u52bf\u592a\u6162\uff0c\u5c06\u4e0e\u4ed6\u8138\u5e73\u4e4b\u65f6\uff0c\u97ad\u68a2\u4e0a\u515c\u7684\u52b2\u529b\u5df2\u8870\uff0c\u97ad\u5c3e\u4e00\u8f6f\uff0c\u5e3d\u5b50\u4e0b\u843d\uff0c\u80e1\u6590\u5fd9\u4f38\u624b\u53bb\u63a5\uff0c\u7a81\u89c1\u773c\u524d\u767d\u5149\u4e00\u95ea\uff0c\u5fc3\u77e5\u4e0d\u5999\uff0c\u53ea\u542c\u62cd\u7684\u4e00\u54cd\uff0c\u773c\u524d\u91d1\u661f\u4e71\u5192\uff0c\u534a\u8fb9\u8138\u988a\u5947\u75db\u900f\u9aa8\u3002\u4ed6\u77e5\u5df2\u4e2d\u4e86\u6697\u7b97\uff0c\u7acb\u5373\u53f3\u8db3\u529b\u6491\uff0c\u5de6\u8db3\u4e00\u677e\uff0c\u4eba\u5df2\u4ece\u5de6\u65b9\u94bb\u5230\u4e86\u9a6c\u8179\u4e4b\u4e0b\uff0c\u4f46\u542c\u5f97\u62cd\u7684\u4e00\u54cd\uff0c\u6728\u5c51\u7eb7\u98de\uff0c\u9a6c\u978d\u5df2\u88ab\u8f6f\u97ad\u51fb\u5f97\u7c89\u788e\uff0c\u90a3\u9a6c\u5403\u75db\u54c0\u5636\u3002 \u3000\u3000\u80e1\u6590\u5728\u9a6c\u8179\u5e95\u907f\u8fc7\u5979\u8fd9\u8fde\u73af\u4e00\u51fb\uff0c\u987a\u52bf\u62bd\u51fa\u5355\u5200\uff0c\u5f85\u5f97\u4ece\u9a6c\u4eba\u7ffb\u4e0a\u9a6c\u80cc\uff0c\u5355\u5200\u5df2\u4ece\u5de6\u624b\u4ea4\u5411\u53f3\u624b\uff0c\u53f3\u988a\u5140\u81ea\u5267\u75db\uff0c\u4f38\u624b\u4e00\u6478\u3002\u53ea\u89c1\u6ee1\u4e0b\u9c9c\u8840\uff0c\u8fd9\u4e00\u97ad\u5b9e\u662f\u6253\u5f97\u4e0d\u8f7b\u3002 \u3000\u3000\u8881\u7d2b\u8863\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u8fd8\u6562\u5192\u5145\u957f\u8f88\u4e48\uff1f\u59d1\u5a18\u8fd9\u4e00\u97ad\u82e5\u4e0d\u662f\u624b\u4e0b\u7559\u60c5\uff0c\u4e0d\u6253\u4e0b\u4f60\u5341\u4e03\u516b\u9897\u7259\u9f7f\u624d\u602a\u3002\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u5012\u975e\u865a\u8bed\uff0c\u5979\u5077\u88ad\u6210\u529f\uff0c\u8fd9\u4e00\u97ad\u5018\u662f\u4f7f\u4e0a\u5168\u529b\u3002\u80e1\u6590\u503e\u9aa8\u975e\u788e\u4e0d\u53ef\uff0c\u5de6\u8fb9\u7259\u9f7f\u4e5f\u52bf\u5fc5\u5c3d\u6570\u6253\u843d\uff0c\u4f46\u9976\u662f\u5982\u6b64\u5df2\u662f\u4ed6\u3000\u827a\u6210\u4ee5\u6765\u4ece\u6240\u672a\u6709\u4e4b\u5927\u8d25\uff0c\u4e0d\u7531\u5f97\u6012\u706b\u76f4\u51b2\uff0c\u5706\u7741\u53cc\u76ee\uff0c\u4e3e\u5200\u5f80\u5979\u80a9\u5934\u76f4\u5288\u4e0b\u53bb\u3002\u8881\u7d2b\u8863\u5fc3\u4e2d\u5fae\u611f\u5bb3\u6015\uff0c\u77e5\u9053\u5bf9\u624b\u5b9e\u975e\u6613\u4e0e\uff0c\u8fd9\u4e00\u6b21\u4ed6\u5403\u4e86\u5927\u4e8f\uff0c\u52a8\u8d77\u4e0b\u6765\u5b9a\u7136\u5168\u529b\u65bd\u4e3a\uff0c\u4e8e\u662f\u821e\u52a8\u957f\u97ad\uff0c\u52b2\u900f\u97ad\u68a2\uff0c\u5c06\u80e1\u6590\u6321\u5728\u4e24\u4e08\u4e4b\u5916\uff0c\u8981\u53eb\u4ed6\u6b3a\u4e0d\u8fd1\u8eab\u6765\u3002 \u3000\u3000\u5c31\u5728\u6b64\u65f6\uff0c\u53ea\u542c\u5f97\u5927\u8def\u4e0a\u9e3e\u94c3\u54cd\u52a8\uff0c\u4e09\u9a91\u9a6c\u7f13\u7f13\u9a70\u6765\uff0c\u89c1\u5230\u6709\u4eba\u52a8\u624b\uff0c\u4e00\u9f50\u9a7b\u9a6c\u800c\u89c2\u3002\u80e1\u6590\u548c\u8881\u7d2b\u8863\u540c\u65f6\u5411\u4e09\u4eba\u671b\u4e86\u4e00\u773c\uff0c\u53ea\u89c1\u4e24\u4e2a\u7a7f\u7684\u662f\u6e05\u5ef7\u4f8d\u536b\u670d\u8272\uff0c\u4e2d\u95f4\u4e00\u4eba\u7a7f\u7684\u662f\u5e38\u670d\uff0c\u8eab\u6750\u9b41\u4f1f\uff0c\u7ea6\u83ab\u56db\u5341\u6765\u5c81\u5e74\u7eaa\u3002 \u3000\u3000\u97ad\u957f\u5200\u77ed\uff0c\u5175\u5203\u4e0a\u80e1\u6590\u5148\u5df2\u5403\u4e8f\uff0c\u4f55\u51b5\u4ed6\u9a91\u7684\u53c8\u662f\u4e00\u5339\u53d7\u4e86\u4f24\u7684\u52a3\u9a6c\u3002 \u3000\u3000\u8881\u7d2b\u8863\u7684\u5750\u9a91\u5374\u662f\u795e\u9a8f\u65e0\u4f26\uff0c\u5979\u9a91\u672f\u53c8\u7cbe\uff0c\u7adf\u4f3c\u4ece\u5c0f\u4fbf\u5728\u9a6c\u80cc\u4e0a\u957f\u5927\u4e00\u822c\uff0c\u56e0\u6b64\u62c6\u5230\u5341\u62db\u4ee5\u5916\uff0c\u80e1\u6590\u4ecd\u662f\u6b3a\u4e0d\u8fd1\u8eab\u53bb\u3002 \u3000\u3000\u4ed6\u5200\u6cd5\u4e00\u53d8\uff0c\u6b63\u8981\u5168\u529b\u62a2\u653b\uff0c\u5ffd\u542c\u5f97\u4e00\u4e2a\u4f8d\u536b\u8bf4\u9053\uff1a\u201c\u8fd9\u5973\u5a03\u5b50\u6a21\u6837\u513f\u65e2\u5999\uff0c\u624b\u4e0b\u4e5f\u5f88\u6765\u5f97\u554a\u3002\u201d\u53e6\u4e00\u4e2a\u4f8d\u536b\u7b11\u9053\uff1a\u201c\u66f9\u5927\u54e5\u4f60\u82e5\u662f\u77a7\u4e0a\u4e86\uff0c\u4e0d\u5982\u5c31\u4f38\u624b\uff0c\u522b\u8ba9\u8fd9\u5c0f\u5b50\u5148\u5f97\u4e86\u751c\u5934\u3002\u201d\u90a3\u59d3\u66f9\u7684\u4f8d\u536b\u4ef0\u5929\u54c8\u54c8\u5927\u7b11\u3002 \u3000\u3000\u80e1\u6590\u607c\u8fd9\u4e24\u4eba\u51fa\u8a00\u8f7b\u8584\uff0c\u6012\u76ee\u6a2a\u4e86\u4ed6\u4eec\u4e00\u773c\u3002\u8881\u7d2b\u8863\u4e58\u9699\u6325\u97ad\u51fb\u5230\uff0c\u80e1\u6590\u5934\u4e00\u4f4e\uff0c\u4ece\u8f6f\u97ad\u5e95\u4e0b\u94bb\u8fdb\uff0c\u62a2\u524d\u6570\u5c3a\u3002\u53ea\u89c1\u8881\u7d2b\u8863\u7ea4\u8170\u4e00\u626d\uff0c\u90a3\u767d\u9a6c\u731b\u5730\u5411\u5de6\u75be\u51b2\u3002 \u3000\u3000\u8fd9\u4e00\u4e0b\u53bb\u52bf\u6781\u5feb\uff0c\u4f46\u89c1\u94f6\u5149\u95ea\u70c1\uff0c\u90a3\u59d3\u66f9\u7684\u4f8d\u536b\u80a9\u4e0a\u5df2\u91cd\u91cd\u5403\u4e86\u4e00\u97ad\u3002 \u3000\u3000\u5979\u56de\u97ad\u62bd\u5411\u80e1\u6590\u5934\u9876\uff0c\u80e1\u6590\u6a2a\u5200\u67b6\u5f00\u3002\u90a3\u767d\u9a6c\u5df2\u5728\u53e6\u4e00\u540d\u4f8d\u536b\u8eab\u65c1\u63a0\u8fc7\uff0c\u53ea\u89c1\u5979\u7d20\u4e0b\u4e00\u4f38\uff0c\u5df2\u6293\u4f4f\u90a3\u4f8d\u536b\u540e\u9888\u201c\u5929\u67f1\u7a74\u201d\u3002\u90a3\u767d\u9a6c\u4e00\u51b2\u4e4b\u52bf\u529b\u9053\u5947\u5927\uff0c\u5979\u5e76\u4e0d\u4f7f\u529b\uff0c\u987a\u4e0b\u5df2\u5c06\u90a3\u4f8d\u536b\u62c9\u4e0b\u9a6c\u6765\uff0c\u6454\u5728\u5730\u4e0b\uff0c\u5979\u4e5f\u4e0d\u56de\u8eab\uff0c\u957f\u97ad\u4ece\u80a9\u5934\u7529\u8fc7\uff0c\u5411\u540e\u62bd\u51fb\u7b2c\u4e09\u4e2a\u5927\u6c49\u3002 \u3000\u3000\u8fd9\u56db\u4e0b\u5154\u8d77\u9e4a\u843d\uff0c\u8fc5\u6377\u65e0\u4f26\uff0c\u80e1\u6590\u5fc3\u4e2d\u4e0d\u7981\u6697\u6697\u559d\u4e86\u58f0\u5f69\uff0c\u5fc3\u60f3\u8fd9\u5927\u6c49\u867d\u7136\u672a\u51fa\u4e00\u58f0\uff0c\u4f46\u65e2\u4e0e\u8fd9\u4e24\u540d\u4f8d\u536b\u7ed3\u4f34\u540c\u884c\uff0c\u5c11\u4e0d\u514d\u4e5f\u8981\u53d7\u4e00\u97ad\u65e0\u5984\u4e4b\u707e\u3002 \u3000\u3000\u54ea\u77e5\u9053\u8fd9\u5927\u6c49\u53ea\u662f\u4e00\u52d2\u9a6c\u5934\uff0c\u7a7a\u624b\u7adf\u6765\u6293\u5979\u94f6\u97ad\u7684\u97ad\u5934\u3002 \u3000\u3000\u8881\u7d2b\u8863\u89c1\u4ed6\u51fa\u624b\u5982\u94a9\uff0c\u7adf\u662f\u4e2a\u52b2\u654c\uff0c\u5f53\u5373\u624b\u8155\u4e00\u632f\uff0c\u97ad\u68a2\u7529\u8d77\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u9601\u4e0b\u53ef\u662f\u53bb\u4eac\u5e08\u53c2\u4e0e\u638c\u95e8\u4eba\u5927\u4f1a\u4e48\uff1f\u201d \u3000\u3000\u90a3\u5927\u6c49\u4e00\u6115\uff0c\u9053\uff1a\u201c\u59d1\u5a18\u4f55\u4ee5\u77e5\u9053\uff1f\u201d\u8881\u7d2b\u8863\u9053\uff1a\u201c\u77a7\u4f60\u6a21\u6837\uff0c\u7a0d\u7a0d\u6709\u70b9\u638c\u95e8\u4eba\u7684\u5473\u513f\u3002\u4f60\u53eb\u4ec0\u4e48\u540d\u5b57\uff0c\u662f\u54ea\u4e00\u95e8\u54ea\u4e00\u6d3e\u7684\u638c\u95e8\uff1f\u201d\u8fd9\u4e24\u53e5\u8bdd\u95ee\u5f97\u65e0\u793c\u4e4b\u6781\uff0c\u90a3\u5927\u6c49\u54fc\u4e86\u4e00\u58f0\uff0c\u5e76\u4e0d\u7406\u4f1a\u3002\u90a3\u59d3\u66f9\u7684\u6043\u536b\u72fc\u72c8\u722c\u8d77\uff0c\u5927\u53eb\u9053\uff1a\u201c\u84dd\u5e08\u5085\uff0c\u6559\u8bad\u6559\u8bad\u8fd9\u81ed\u5973\u5a03\u5b50\uff01\u201d \u3000\u3000\u8881\u7d2b\u8863\u817f\u4e0a\u5fae\u5fae\u4f7f\u52b2\uff0c\u767d\u9a6c\u7a81\u5730\u5411\u90a3\u59d3\u66f9\u7684\u4f8d\u536b\u51b2\u53bb\u3002\u767d\u9a6c\u8fd9\u4e00\u4e0b\u7a81\u7136\u53d1\u8db3\uff0c\u76f4\u662f\u53eb\u4eba\u51fa\u5176\u4e0d\u610f\u3002\u59d3\u66f9\u4f8d\u536b\u5927\u9a87\uff0c\u6025\u5fd9\u5411\u5de6\u907f\u8ba9\uff0c\u8881\u7d2b\u8863\u7684\u94f6\u97ad\u5374\u5df2\u6253\u5230\u80cc\u5fc3\u3002\u90a3\u5927\u6c49\u89c1\u60c5\u52bf\u6025\u8feb\uff0c\u62bd\u51fa\u8170\u4e2d\u77ed\u5251\uff0c\u4e00\u62db\u201c\u62e6\u8170\u53d6\u6c34\u56db\u95e8\u5251\u201d\uff0c\u4ee5\u659c\u63a8\u6b63\u5df2\u5c06\u97ad\u68a2\u62e8\u5f00\u3002 \u3000\u3000\u8881\u7d2b\u8863\u8db3\u5c16\u70b9\u7740\u8e0f\u956b\u8f7b\u8f7b\u5411\u540e\u4e00\u63a8\uff0c\u767d\u9a6c\u731b\u5730\u540e\u9000\u6570\u6b65\uff0c\u8fd9\u9a6c\u75be\u8d8b\u75be\u9000\uff0c\u7adf\u662f\u540c\u6837\u7684\u8fc5\u6377\u3002\u90a3\u5927\u6c49\u9ad8\u58f0\u559d\u5f69\uff1a\u201c\u597d\u9a6c\uff01\u201d \u3000\u3000\u8881\u7d2b\u8863\u51b7\u7b11\u9053\uff1a\u201c\u6211\u9053\u662f\u8c01\uff0c\u539f\u6765\u662f\u5e7f\u897f\u68a7\u5dde\u516b\u4ed9\u5251\u7684\u638c\u95e8\u4eba\u84dd\u79e6\u3002\u201d \u3000\u3000\u8fd9\u5927\u6c49\u6b63\u662f\u84dd\u79e6\uff0c\u773c\u89c1\u8fd9\u5c11\u5973\u4e0d\u8fc7\u4e8c\u5341\u5de6\u53f3\u5e74\u7eaa\uff0c\u5bb9\u8272\u5982\u82b1\uff0c\u867d\u7136\u51fa\u4e4e\u8fc5\u6377\uff0c\u4f46\u80fd\u6709\u591a\u4eba\u6c5f\u6e56\u9605\u5386\uff0c\u600e\u5730\u53ea\u89c1\u4e00\u62db\uff0c\u4fbf\u9053\u51fa\u81ea\u5df1\u7684\u59d3\u540d\u8eab\u5206\uff1f\u4ed6\u5fc3\u4e2d\u60ca\u8be7\uff0c\u4e00\u9762\u5374\u4e5f\u4e0d\u7981\u5f97\u610f\uff0c\u6697\u9053\uff1a\u201c\u84dd\u67d0\u867d\u7136\u50fb\u5904\u5357\u7586\uff0c\u5c45\u7136\u8fde\u4e00\u4e2a\u5e74\u8f7b\u5c11\u5973\u4e5f\u77e5\u6211\u5a01\u540d\u3002\u201d\u5fae\u5fae\u4e00\u7b11\uff0c\u95ee\u9053\uff1a\u201c\u59d1\u5a18\u600e\u77e5\u5728\u4e0b\u59d3\u540d\uff1f\u201d\u8881\u7d2b\u8863\u9053\uff1a\u201c\u6211\u6b63\u8981\u627e\u4f60\uff0c\u5728\u8fd9\u91cc\u649e\u89c1\uff0c\u90a3\u662f\u518d\u597d\u4e5f\u6ca1\u6709\u3002\u201d\u84dd\u79e6\u66f4\u611f\u5947\u602a\uff0c\u5fc3\u60f3\u6211\u548c\u4f60\u7d20\u4e0d\u76f8\u8bc6\u554a\uff0c\u95ee\u9053\uff1a\u201c\u59d1\u5a18\u9ad8\u59d3\u5927\u540d\uff0c\u627e\u84dd\u67d0\u6709\u4f55\u6307\u6559\uff1f\u201d\u8881\u7d2b\u8863\u9053\uff1a\u201c\u6211\u53eb\u4f60\u4e0d\u7528\u4e0a\u4eac\u53bb\u5566\uff0c\u7531\u6211\u4ee3\u4f60\u53bb\u4fbf\u662f\u3002\u201d\u84dd\u79e6\u66f4\u662f\u6478\u4e0d\u7740\u5934\u8111\uff0c\u95ee\u9053\uff1a\u201c\u6b64\u8bdd\u600e\u8bb2\uff1f\u201d\u8881\u7d2b\u8863\u9053\uff1a\u201c\u54fc\uff0c\u8fd9\u8fd8\u4e0d\u660e\u767d\uff1f\u6211\u53eb\u4f60\u628a\u516b\u4ed9\u5251\u7684\u638c\u95e8\u4e4b\u4f4d\u8ba9\u4e86\u7ed9\u6211\uff01\u201d \u3000\u3000\u84dd\u79e6\u542c\u5979\u8a00\u8bed\u65e0\u793c\uff0c\u4e0d\u7531\u5f97\u5927\u662f\u607c\u6012\uff0c\u4f46\u9002\u624d\u89c1\u5979\u8fde\u88ad\u56db\u4eba\uff0c\u624b\u6cd5\u5de7\u5999\u4e4b\u6781\uff0c\u8fde\u81ea\u5df1\u4e5f\u6ca1\u77a7\u6e05\uff0c\u5426\u5219\u4fbf\u80fd\u62a4\u4f4f\u8eab\u65c1\u4f8d\u536b\uff0c\u4e0d\u8ba9\u4ed6\u5982\u6b64\u72fc\u72c8\u5730\u6454\u4e0b\u9a6c\u6765\u3002\u4ed6\u751f\u6027\u8c28\u7ec6\uff0c\u5fc3\u60f3\u5979\u53e3\u51fa\u5927\u8a00\uff0c\u5fc5\u6709\u6240\u6043\uff0c\u5f53\u4e0b\u5374\u4e0d\u53d1\u4f5c\uff0c\u62b1\u62f3\u8bf4\u9053\uff1a\u201c\u59d1\u5a18\u5c0a\u59d3\u5927\u540d\uff1f\u4ee4\u5e08\u662f\u8c01\uff1f\u201d \u3000\u3000\u8881\u7d2b\u8863\u9053\uff1a\u201c\u6211\u53c8\u4e0d\u8ddf\u4f60\u5957\u4ea4\u60c5\uff0c\u95ee\u6211\u59d3\u540d\u5e72\u4e48\uff1f\u6211\u5e08\u7236\u7684\u540d\u5934\u66f4\u52a0\u4e0d\u80fd\u8bf4\u7ed9\u4f60\u77e5\u3002\u6211\u5e08\u7236\u66fe\u8ddf\u4f60\u6709\u4e00\u9762\u4e4b\u7f18\u3002\u82e5\u662f\u63d0\u8d77\u5f80\u4e8b\uff0c\u6211\u5012\u4e0d\u4fbf\u786c\u8981\u4f60\u8ba9\u8fd9\u638c\u95e8\u4e4b\u4f4d\u4e86\u3002\u201d \u3000\u3000\u84dd\u79e6\u7709\u5934\u7d27\u8e59\uff0c\u60f3\u4e0d\u8d77\u76f8\u8bc6\u7684\u6b66\u6797\u540d\u5bbf\u4e4b\u4e2d\uff0c\u6709\u54ea\u4e00\u4f4d\u662f\u4f7f\u8f6f\u97ad\u7684\u80fd\u624b\u3002 \u3000\u3000\u4e24\u540d\u6043\u536b\u4e00\u4e2a\u5403\u4e86\u4e00\u97ad\uff0c\u4e00\u4e2a\u88ab\u626f\u4e0b\u9a6c\uff0c\u81ea\u662f\u5747\u6781\u607c\u6012\u3002\u4ed6\u4eec\u4e00\u5411\u6a2a\u884c\u60ef\u4e86\u7684\uff0c\u5403\u4e86\u8fd9\u4e8f\u54ea\u80af\u5c31\u6b64\u7f62\u4f11\uff1f\u4e24\u4eba\u9f50\u58f0\u553f\u54e8\uff0c\u4e00\u4e2a\u9a6c\u4e0a\uff0c\u4e00\u4e2a\u6b65\u4e0b\uff0c\u540c\u65f6\u5411\u8881\u7d2b\u8863\u6251\u53bb\u3002\u4e24\u4eba\u624b\u4e2d\u672c\u6765\u7a7a\u7740\uff0c\u5f53\u4e0b\u4e00\u4e2a\u62d4\u5200\uff0c\u4e00\u4e2a\u4fbf\u4f38\u624b\u53bb\u62bd\u8170\u4e2d\u957f\u5251\u3002 \u3000\u3000\u8881\u7d2b\u8863\u8f6f\u97ad\u6643\u52a8\uff0c\u62cd\u7684\u4e00\u54cd\uff0c\u62d4\u5200\u7684\u4f8d\u536b\u53f3\u8155\u4e0a\u5df2\u91cd\u91cd\u5403\u4e86\u4e00\u8bb0\u3002\u4ed6\u624b\u6307\u6293\u4f4f\u5200\u67c4\uff0c\u4f46\u89c9\u624b\u8155\u5267\u75db\u5165\u9aa8\uff0c\u518d\u4e5f\u65e0\u529b\u62d4\u51fa\u8170\u5200\u3002\u8881\u7d2b\u8863\u8fd9\u94f6\u4e1d\u8f6f\u97ad\u53c8\u957f\u53c8\u7ec6\uff0c\u4e0e\u4e00\u822c\u8f6f\u97ad\u5927\u4e0d\u76f8\u540c\uff0c\u4e00\u62db\u6253\u4e2d\u90a3\u4f8d\u536b\u7684\u624b\u8155\uff0c\u97ad\u68a2\u6beb\u4e0d\u505c\u7559\uff0c\u5feb\u5982\u7535\u5149\u77f3\u706b\u822c\u4e00\u5410\uff0c\u53c8\u5df2\u5377\u4f4f\u4e86\u90a3\u59d3\u66f9\u4f8d\u536b\u7684\u5251\u67c4\uff0c\u987a\u52bf\u4e0a\u63d0\u3002\u8fd9\u4e00\u4e0b\u771f\u662f\u5feb\u5f97\u51fa\u5947\uff0c\u6bd4\u90a3\u4f8d\u536b\u4f38\u4e0b\u53bb\u63e1\u5251\u8fd8\u8981\u62a2\u5148\u4e00\u6b65\u3002\u59d3\u66f9\u7684\u4f46\u89c1\u94f6\u5149\u4e00\u95ea\uff0c\u81ea\u5df1\u624b\u6307\u5c1a\u672a\u78b0\u5230\u5251\u67c4\uff0c\u5251\u5df2\u51fa\u9798\uff0c\u5927\u9a87\u4e4b\u4e0b\uff0c\u6025\u5fd9\u6325\u624b\u5916\u7529\uff0c\u9976\u662f\u5982\u6b64\uff0c\u5251\u950b\u5df2\u5728\u4ed6\u624b\u638c\u5fc3\u5212\u8fc7\uff0c\u767b\u65f6\u9c9c\u8840\u6dcb\u6f13\u3002 \u3000\u3000\u519c\u7d2b\u8863\u8f6f\u97ad\u4e00\u632f\uff0c\u957f\u5251\u6fc0\u98de\u4e0a\u5929\uff0c\u7adf\u6709\u6570\u5341\u4e08\u9ad8\uff0c\u5979\u5c06\u8f6f\u97ad\u7f20\u56de\u8170\u95f4\uff0c\u4fbf\u5982\u7d2b\u8863\u5916\u7cfb\u5382\u4e00\u6761\u94f6\u8272\u4e1d\u7ee6\uff0c\u65c1\u4eba\u4e00\u77a5\u4e4b\u4e0b\uff0c\u54ea\u77e5\u8fd9\u662f\u4e00\u4ef6\u5389\u5bb3\u5175\u5203\uff1f\u5979\u5e76\u4e0d\u62ac\u5934\u884c\u5251\uff0c\u5374\u5411\u84dd\u79e6\u95ee\u9053\uff1a\u201c\u4f60\u8fd9\u638c\u95e8\u4e4b\u4f4d\u5230\u5e95\u8ba9\u662f\u4e0d\u8ba9\uff1f\u201d \u3000\u3000\u76d1\u79e6\u4e0a\u4ef0\u5934\u671b\u7740\u5929\u7a7a\u6025\u843d\u800c\u4e0b\u7684\u957f\u5251\uff0c\u542c\u5979\u8bf4\u8bdd\uff0c\u968f\u53e3\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u8881\u7d2b\u8863\u9053\uff1a\u201c\u6211\u8981\u4f60\u8ba9\u8fd9\u516b\u4ed9\u5251\u638c\u95e8\u4e4b\u4f4d\u3002\u201d\u8fd9\u65f6\u957f\u5251\u5df2\u843d\u5230\u5979\u8ddf\u524d\uff0c\u8881\u7d2b\u8863\u4e00\u9762\u8bf4\u8bdd\uff0c\u4e00\u9762\u542c\u98ce\u8fa8\u5668\uff0c\u4e00\u4f38\u624b\u4fbf\u6293\u6ce8\u5e7f\u5251\u67c4\uff0c\u957f\u5251\u4ece\u6570\u5341\u4e08\u9ad8\u5904\u843d\u5c06\u4e0b\u6765\uff0c\u52bf\u9053\u4f55\u7b49\u51cc\u5389\uff0c\u4f55\u51b5\u8fd9\u5251\u9664\u5382\u5251\u67c4\u4e4b\u5916\uff0c\u901a\u8eab\u662f\u950b\u5229\u7684\u5203\u53e3\uff0c\u5979\u7adf\u773c\u89d2\u4e5f\u6ca1\u659c\u4e00\u4e0b\uff0c\u968f\u968f\u4fbf\u4fbf\u5c31\u62ff\u4f4f\u4e86\u5251\u67c4\u3002 \u3000\u3000\u8fd9\u4e00\u624b\u529f\u592b\u4e0d\u4f46\u84dd\u79e6\u5927\u4e3a\u9707\u60ca\uff0c\u8fde\u65c1\u89c2\u7684\u80e1\u6590\u4e5f\u6697\u81ea\u4f69\u670d\uff0c\u5fc3\u60f3\uff1a\u201c\u5979\u9002\u624d\u593a\u4e86\u5c11\u6797\u97e6\u9640\u95e8\u7684\u638c\u95e8\uff0c\u4f55\u4ee5\u53c8\u8981\u593a\u516b\u4ed9\u5251\u7684\u638c\u95e8\uff1f\u201d\u4f46\u89c1\u5979\u6b63\u5f53\u5999\u9f84\uff0c\u6b66\u529f\u5374\u5982\u6b64\u4e86\u5f97\uff0c\u751f\u5e73\u9664\u8d75\u534a\u5c71\u5916\uff0c\u4ece\u672a\u89c1\u8fc7\u5982\u6b64\u6b66\u5b66\u7684\u9ad8\u4e0b\uff0c\u5fc3\u4e2d\u4e00\u8d77\u8d5e\u4f69\u4e4b\u610f\uff0c\u8138\u4e0a\u7684\u97ad\u4f24\u4f3c\u4e4e\u4e5f\u4e0d\u600e\u4e48\u75bc\u75db\u3002 \u3000\u3000\u84dd\u79e6\u89c1\u5979\u9732\u4e86\u8fd9\u624b\u7edd\u6280\uff0c\u66f4\u4e0d\u6562\u8d38\u7136\u4ece\u4e8b\uff0c\u60f3\u7528\u8a00\u8bed\u5957\u95ee\u51fa\u5979\u7684\u5e95\u7ec6\uff0c\u8bf4\u9053\uff1a\u201c\u59d1\u5a18\u8fd9\u624b\u542c\u98ce\u8fa8\u5668\u7684\u529f\u592b\uff0c\u4f3c\u662f\u5c71\u897f\u4f5f\u5bb6\u7684\u7edd\u827a\u554a\u3002\u201d\u8881\u7d2b\u8863\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u773c\u5149\u5012\u597d\u3002\u90a3\u4e48\u6211\u8fd9\u624b\u63b7\u5251\u4e0a\u5929\u7684\u529f\u592b\u5462\uff1f\u201d\u8bf4\u7740\u53f3\u624b\u4e00\u6325\uff0c\u957f\u5251\u53c8\u98de\u5411\u5929\u7a7a\u3002\u8fd9\u4e00\u6b21\u5374\u4e0d\u662f\u5251\u5c16\u5411\u4e0a\u7684\u76f4\u5347\uff0c\u800c\u662f\u4e00\u8def\u7ffb\u7740\u7b4b\u6597\uff0c\u821e\u6210\u4e2a\u94f6\u8272\u5149\u5708\uff0c\u5189\u5189\u4e0a\u5347\u3002\u867d\u7136\u53bb\u52bf\u4e0d\u6025\uff0c\u4f46\u5f62\u72b6\u7279\u5f02\uff0c\u851a\u4e3a\u5947\u89c2\u3002 \u3000\u3000\u84dd\u79e6\u62ac\u5934\u89c2\u5251\uff0c\u731b\u542c\u5f97\u98ce\u58f0\u5fae\u52a8\uff0c\u8eab\u524d\u6709\u5f02\uff0c\u6025\u5fd9\u4e00\u4e2a\u5012\u7eb5\u6b65\u9000\u5f00\u4e08\u8bb8\uff0c\u53ea\u89c1\u91d1\u5149\u4e00\u95ea\uff0c\u8881\u7d2b\u8863\u94f6\u4e1d\u8f6f\u97ad\u4e0a\u7684\u5c0f\u91d1\u7403\u521a\u4ece\u81ea\u5df1\u8170\u95f4\u63a0\u8fc7\uff0c\u82e5\u4e0d\u662f\u89c1\u673a\u5f97\u5feb\uff0c\u8eab\u4e0a\u4f69\u5251\u53c8\u5df2\u88ab\u5979\u62a2\u53bb\u3002 \u3000\u3000\u539f\u6765\u8881\u7d2b\u8863\u77e5\u4ed6\u6b66\u529f\u9ad8\u51fa\u4e24\u4e2a\u4f8d\u536b\u751a\u591a\uff0c\u662f\u4ee5\u6545\u610f\u63b7\u5251\u6210\u5708\u5f15\u5f00\u4ed6\u7684\u76ee\u5149\uff0c\u518d\u7a81\u7136\u51fa\u624b\u62a2\u5251\uff0c\u54ea\u77e5\u8fd8\u662f\u7ed9\u4ed6\u60ca\u89c9\u907f\u5f00\u3002\u5979\u5fc3\u4e2d\u8fde\u53eb\u53ef\u60dc\uff0c\u84dd\u79e6\u5374\u5df2\u6697\u547c\u60ed\u6127\u3002\u4ed6\u96c4\u9738\u897f\u5357\uff0c\u95e8\u5f92\u904d\u53ca\u4e24\u5e7f\u4e91\u8d35\uff0c\u4e8c\u5341\u5e74\u6765\u4ece\u672a\u9047\u5230\u632b\u6298\uff0c\u60f3\u4e0d\u5230\u8fd9\u6837\u4e00\u4e2a\u9ec4\u6bdb\u4e2b\u5934\u4eca\u65e5\u7adf\u6765\u5982\u6b64\u8f7b\u4fae\u4e8e\u5df1\uff0c\u8fd9\u65f6\u518d\u4e5f\u96be\u4ee5\u5fcd\u8010\uff0c\u5237\u7684\u4e00\u58f0\uff0c\u957f\u5251\u51fa\u624b\uff0c\u53eb\u9053\uff1a\u201c\u597d\uff0c\u6211\u4fbf\u9886\u6559\u59d1\u5a18\u7684\u9ad8\u62db\u3002\u201d \u3000\u3000\u8fd9\u65f6\u7a7a\u4e2d\u957f\u5251\u53bb\u52bf\u5df2\u5c3d\uff0c\u7b14\u76f4\u4e0b\u5815\uff0c\u8881\u7d2b\u8863\u8f6f\u97ad\u7529\u4e0a\uff0c\u97ad\u5934\u5377\u4f4f\u5251\u67c4\uff0c\u500f\u5730\u5411\u524d\u4e00\u9001\uff0c\u957f\u5251\u75be\u5411\u84dd\u79e6\u5f53\u80f8\u523a\u6765\u3002\u4e24\u4eba\u76f8\u9694\u51e0\u53ca\u4e24\u4e08\uff0c\u4f46\u4e00\u970e\u4e4b\u95f4\uff0c\u5251\u5c16\u8ddd\u4ed6\u80f8\u53e3\u5df2\u4e0d\u53ca\u4e00\u5c3a\uff0c\u5c31\u5982\u4e00\u6761\u4e08\u8bb8\u957f\u7684\u957f\u81c2\u6293\u4f4f\u5251\u67c4\uff0c\u7a81\u7136\u5411\u4ed6\u523a\u5230\u4e00\u822c\u3002\u8fd9\u4e00\u62db\u84dd\u79e6\u53c8\u662f\u51fa\u5176\u4e0d\u610f\uff0c\u4e00\u60ca\u4e4b\u4e0b\uff0c\u6025\u5fd9\u6a2a\u5251\u5c01\u6321\u3002 \u3000\u3000\u8881\u7d2b\u8863\u53eb\u9053\uff1a\u201c\u6e58\u5b50\u5439\u7bab\uff01\u201d\u84dd\u79e6\u8fd9\u4e00\u62db\u6b63\u662f\u516b\u4ed9\u5251\u6cd5\u4e2d\u7684\u201c\u6e58\u5b50\u5439\u7bab\u201d\u3002 \u3000\u3000\u516b\u4ed9\u5251\u5728\u897f\u5357\u5404\u7701\u751a\u4e3a\u76db\u884c\uff0c\u4ed6\u60f3\u4f60\u8bc6\u5f97\u6211\u7684\u62db\u6570\u6709\u4f55\u5e0c\u7f55\uff0c\u8981\u77a7\u4f60\u662f\u5426\u6321\u5f97\u4f4f\u4e86\uff0c\u53cc\u7709\u4e00\u626c\uff0c\u559d\u9053\uff1a\u201c\u662f\u2018\u6e58\u5b50\u5439\u7bab\u2019\u4fbf\u600e\u5730\uff1f\u201d\u8881\u7d2b\u8863\u9053\uff1a\u201c\u9634\u9633\u5b9d\u6247\uff01\u201d\u4e00\u8bed\u672a\u6bd5\uff0c\u8f6f\u97ad\u5377\u7740\u957f\u5251\uff0c\u5411\u4ed6\u5de6\u80f8\u53f3\u80f8\u5206\u523a\u4e00\u5251\uff0c\u6b63\u662f\u516b\u4ed9\u5251\u7684\u6b62\u5b97\u5251\u6cd5\u201c\u6c49\u953a\u79bb\u9634\u9633\u5b9d\u6247\u201d\u3002 \u3000\u3000\u84dd\u79e6\u53c8\u662f\u4e00\u60ca\uff0c\u5fc3\u60f3\u5979\u4f1a\u4f7f\u516b\u4ed9\u5251\u6cd5\u5e76\u4e0d\u51fa\u5947\uff0c\u5947\u5728\u4ee5\u8f6f\u97ad\u9001\u5251\uff0c\u5c45\u7136\u529b\u900f\u5251\u5c16\uff0c\u5203\u76f4\u5982\u77e2\uff0c\u5f53\u4e0b\u8e0f\u4e0a\u4e00\u6b65\uff0c\u8981\u5f85\u62a2\u653b\uff0c\u5fc3\u60f3\u5979\u4ee5\u8f6f\u97ad\u4f7f\u5251\uff0c\u5251\u4e0a\u529b\u9053\u865a\u6d6e\uff0c\u53ea\u8981\u53cc\u5251\u4e00\u4ea4\uff0c\u8fd8\u4e0d\u5c06\u5979\u957f\u5251\u51fb\u4e0b\u5730\u6765\u3002\u54ea\u77e5\u4ed6\u957f\u5251\u4e00\u63d0\uff0c\u624b\u52bf\u521a\u8d77\uff0c\u8fd8\u672a\u51fa\u62db\uff0c\u8881\u7d2b\u8863\u53eb\u9053\uff1a\u201c\u91c7\u548c\u732e\u82b1\uff01\u201d\u5ffd\u5730\u6536\u8f6c\u8f6f\u97ad\uff0c\u6b64\u65f6\u97ad\u4e0a\u52bf\u9053\u5df2\u5b8c\uff0c\u957f\u5251\u4e0b\u843d\uff0c\u5979\u5de6\u624b\u63a5\u5251\uff0c\u53f3\u624b\u6301\u97ad\uff0c\u7b11\u541f\u541f\u5730\u671b\u7740\u5bf9\u624b\u3002 \u3000\u3000\u84dd\u79e6\u53c8\u7ed9\u5979\u53eb\u7834\u4e00\u62db\uff0c\u6697\u60f3\u97ad\u957f\u5251\u77ed\uff0c\u9a6c\u9ad8\u6b65\u4f4e\uff0c\u81ea\u5df1\u53cc\u91cd\u4e0d\u5229\uff0c\u4f55\u51b5\u5979\u602a\u62db\u767e\u51fa\uff0c\u4e00\u5473\u620f\u800d\u7ea0\u7f20\uff0c\u8089\u5df1\u53ea\u8981\u7a0d\u6709\u758f\u795e\uff0c\u7740\u4e86\u5979\u7684\u9053\u513f\uff0c\u5c82\u975e\u4e00\u4e16\u5a01\u540d\u4ed8\u4e8e\u6d41\u6c34\uff1f\u5f53\u4e0b\u6309\u5251\u6a2a\u80f8\uff0c\u6b63\u8272\u8bf4\u9053\uff1a\u201c\u5982\u6b64\u513f\u620f\uff0c\u90a3\u7b97\u4ec0\u4e48\uff0c\u59d1\u5a18\u5018\u82e5\u771f\u4ee5\u516b\u4ed9\u5251\u8d50\u62db\uff0c\u5728\u4e0b\u4fbf\u5949\u966a\u8d70\u8d70\u3002\u201d \u3000\u3000\u8881\u7d2b\u8863\u9053\uff1a\u201c\u597d\uff0c\u82e5\u4e0d\u7528\u6b63\u5b97\u516b\u4ed9\u5251\u6cd5\u80dc\u4f60\uff0c\u8c05\u4f60\u4e5f\u4e0d\u7518\u8ba9\u90a3\u638c\u95e8\u4e4b\u4f4d\u3002\u201d \u3000\u3000\u8bf4\u7740\u4e00\u8dc3\u4e0b\u9a6c\uff0c\u4fbf\u5728\u4e0b\u9a6c\u4e4b\u65f6\uff0c\u5df2\u5c06\u8f6f\u97ad\u7f20\u56de\u8170\u95f4\u3002 \u3000\u3000\u84dd\u79e6\u5251\u5c16\u5fae\u659c\uff0c\u5de6\u624b\u634f\u4e2a\u5251\u51b3\uff0c\u4f7f\u7684\u662f\u534a\u62db\u201c\u94c1\u62d0\u674e\u846b\u82a6\u7cfb\u8170\u201d\uff0c\u53ea\u5f85\u5bf9\u624b\u51fa\u5251\uff0c\u4e0b\u534a\u62db\u7acb\u65f6\u53d1\u51fa\u3002 \u3000\u3000\u8881\u7d2b\u8863\u957f\u5251\u4e00\u6296\uff0c\u5f85\u8981\u8fdb\u62db\uff0c\u56de\u7738\u671d\u80e1\u6590\u671b\u4e86\u4e00\u773c\uff0c\u5411\u84dd\u79e6\u9053\uff1a\u201c\u8ddf\u4f60\u6bd4\u8bd5\u4e00\u4e0b\u4e0d\u6253\u7d27\uff0c\u6211\u8fd9\u5b9d\u9a6c\u53ef\u522b\u8ba9\u9a6c\u8d3c\u76d7\u4e86\u53bb\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u5f53\u4f60\u8ddf\u4eba\u52a8\u624b\u4e4b\u65f6\uff0c\u6211\u4e0d\u6253\u4f60\u8fd9\u9a6c\u513f\u7684\u4e3b\u610f\u4fbf\u662f\u3002\u201d\u8881\u7d2b\u8863\u9053\uff1a\u201c\u54fc\uff0c\u5c0f\u80e1\u6590\u8be1\u8ba1\u591a\u7aef\uff0c\u8c01\u4fe1\u4e86\u4ed6\u8c01\u4fbf\u4e0a\u5f53\u3002\u201d\u5de6\u624b\u62c9\u4f4f\u9a6c\u7586\uff0c\u55e4\u7684\u4e00\u5251\uff0c\u91d1\u5203\u5e26\u98ce\uff0c\u4e00\u62db\u201d\u5f20\u679c\u8001\u5012\u9a91\u9a74\u201d\u659c\u659c\u523a\u51fa\u3002 \u3000\u3000\u84dd\u79e6\u89c1\u5979\u5de6\u624b\u7275\u9a6c\uff0c\u53f3\u4e4e\u4f7f\u5251\uff0c\u6697\u60f3\u8fd9\u662f\u4f60\u81ea\u5df1\u627e\u6b7b\uff0c\u53ef\u602a\u4e0d\u5f97\u65c1\u4eba\uff0c\u5f53\u5373\u201c\u62e8\u4e91\u89c1\u65e5\u201d\u3001\u201c\u4ed9\u4eba\u6307\u8def\u201d\u3001\u201c\u9b41\u661f\u70b9\u5143\u201d\uff0c\u62c6\u4e86\u4e00\u62db\u5374\u8fd8\u4e86\u4e24\u5251\u3002 \u3000\u3000\u519c\u7d2b\u8863\u89c1\u4ed6\u5251\u62db\u51cc\u5389\uff0c\u8138\u4e0a\u867d\u662f\u4ecd\u542b\u5fae\u7b11\uff0c\u5fc3\u4e2d\u5374\u767b\u65f6\u6536\u8d77\u8f7b\u89c6\u4e4b\u610f\uff0c\u6697\u60f3\u5e08\u7236\u6240\u8a00\u975e\u865a\uff0c\u516b\u4ed9\u5251\u6cd5\u679c\u662f\u5251\u4e2d\u4e00\u7edd\uff0c\u6b64\u4eba\u4f7f\u5c06\u51fa\u6765\uff0c\u6bd4\u6211\u7684\u529f\u529b\u53ef\u8981\u6df1\u539a\u5f97\u591a\u4e86\uff0c\u4e8e\u662f\u4e5f\u4ee5\u516b\u4ed9\u5251\u6cd5\u89c1\u62db\u62c6\u62db\u3002\u5979\u5de6\u624b\u62c9\u7740\u9a6c\u7f30\uff0c\u65e2\u4e0d\u80fd\u8f6c\u8eab\u62a2\u653b\uff0c\u4e5f\u96be\u4ee5\u5927\u7eb5\u5927\u8dc3\uff0c\u81ea\u662f\u8bf8\u591a\u53d7\u5236\u3002\u4f46\u5979\u95e8\u6237\u5b88\u5f97\u751a\u662f\u4e25\u5bc6\uff0c\u84dd\u79e6\u5374\u4e5f\u627e\u4e0d\u5230\u7834\u7efd\uff0c\u53ea\u89c1\u5979\u6240\u4f7f\u5251\u6cd5\u679c\u662f\u672c\u95e8\u5ae1\u6d3e\uff0c\u4e0d\u7531\u5f97\u6697\u6697\u79f0\u5f02\uff0c\u5fc3\u60f3\u672c\u95e8\u4e4b\u4e2d\uff0c\u600e\u80fd\u51fa\u4e86\u5982\u6b64\u4eba\u7269\uff1f \u3000\u3000\u6597\u5251\u4e4b\u5904\uff0c\u6b63\u5f53\u8861\u9633\u5357\u5317\u6765\u5f80\u7684\u5b98\u9053\u5927\u8def\uff0c\u4e24\u4eba\u53ea\u62c6\u5f97\u5341\u4f59\u62db\uff0c\u5317\u8fb9\u6765\u4e86\u4e00\u961f\u63a8\u7740\u5c0f\u8f66\u7684\u76d0\u8d29\uff0c\u8ddf\u7740\u5357\u8fb9\u5927\u9053\u4e0a\u4e5f\u6765\u4e86\u51e0\u8f86\u9aa1\u8f66\uff0c\u4f17\u5546\u8d29\u670d\u89c1\u8def\u4e0a\u6709\u4eba\u76f8\u6597\uff0c\u4e00\u9f50\u505c\u4e0b\u89c2\u770b\u3002\u4e0d\u591a\u65f6\u5357\u5317\u4e24\u7aef\u53c8\u5230\u5e7f\u4e9b\u884c\u65c5\u5ba2\u5546\u3002\u4f17\u4eba\u4e00\u6765\u89c1\u6597\u5f97\u70ed\u95f9\uff0c\u4e8c\u6765\u754f\u60e7\u4e24\u4e2a\u671d\u5ef7\u6b66\u5b98\uff0c\u90fd\u5019\u5728\u8def\u4e0a\u9759\u9759\u65c1\u89c2\u3002 \u3000\u3000\u53c8\u6597\u4e00\u9635\uff0c\u84dd\u79e6\u5df2\u77a7\u51fa\u5bf9\u65b9\u867d\u7136\u5b66\u8fc7\u516b\u4ed9\u5251\u672f\uff0c\u4f46\u5251\u6cd5\u4e2d\u8bb8\u591a\u7cbe\u5fae\u5965\u5999\u4e4b\u5904\uff0c\u5374\u5e76\u672a\u4f53\u4f1a\u5f97\u5230\uff0c\u53ea\u662f\u5979\u6b66\u529f\u751a\u6742\uff0c\u6bcf\u5230\u5371\u6025\u4e4b\u9645\uff0c\u4fbf\u7a81\u4f7f\u4e00\u62db\u4f3c\u662f\u800c\u975e\u7684\u516b\u4ed9\u5251\u6cd5\uff0c\u5c06\u81ea\u5df1\u7684\u6740\u7740\u5316\u89e3\u4e86\u5f00\u53bb\uff0c\u56e0\u6b64\u4e00\u65f6\u5012\u4e5f\u4e0d\u6613\u53d6\u80dc\u3002\u4ed6\u89c1\u65c1\u89c2\u8005\u4f17\uff0c\u5bf9\u624b\u975e\u4f46\u662f\u4e2a\u5c11\u5973\uff0c\u800c\u4e14\u5de6\u624b\u7275\u9a6c\uff0c\u663e\u662f\u4ee5\u534a\u529b\u4e0e\u81ea\u5df1\u5468\u65cb\uff0c\u7eb5\u4f7f\u548c\u5979\u6253\u6210\u5e73\u624b\uff0c\u4e5f\u5df2\u6ca1\u8138\u9762\u4e0a\u4eac\u53c2\u4e0e\u638c\u95e8\u4eba\u4e4b\u4f1a\u4e86\uff0c\u5f53\u4e0b\u50ac\u52a8\u5251\u529b\uff0c\u5c06\u6570\u5341\u5e74\u6765\u94bb\u7814\u800c\u5f97\u7684\u5fc3\u6cd5\u4e00\u62db\u62db\u4f7f\u5c06\u51fa\u6765\uff0c\u65c1\u89c2\u4f17\u4eba\u89c1\u4ed6\u8d8a\u6597\u8d8a\u52c7\uff0c\u5251\u5149\u970d\u970d\uff0c\u7ed5\u7740\u8881\u7d2b\u8863\u8eab\u5468\u6025\u653b\uff0c\u4e0d\u7531\u5f97\u90fd\u4e3a\u5979\u62c5\u5fc3\u3002\u53ea\u6709\u90a3\u4e24\u540d\u4f8d\u536b\u5374\u76fc\u84dd\u79e6\u5f97\u80dc\uff0c\u597d\u4ee3\u4ed6\u4eec\u4e00\u96ea\u53d7\u8fb1\u4e4b\u803b\u3002 \u3000\u3000\u8881\u7d2b\u8863\u4e45\u6218\u4e0d\u4e0b\uff0c\u5076\u4e00\u8f6c\u8eab\uff0c\u89c1\u5230\u80e1\u6590\u8138\u4e0a\u4f3c\u7b11\u975e\u7b11\uff0c\u4f3c\u6709\u8ba5\u5632\u4e4b\u610f\uff0c\u5fc3\u60f3\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u4f60\u7b11\u6211\u6765\u7740\uff0c\u53eb\u4f60\u77a7\u77a7\u59d1\u5a18\u624b\u6bb5\uff01\u201d\u4f46\u8fd9\u756a\u6597\u5251\u9650\u4e8e\u53ea\u4f7f\u516b\u4ed9\u5251\uff0c\u5176\u4f59\u6b66\u529f\u5c3d\u6570\u4f7f\u4e0d\u51fa\u6765\uff0c\u5de6\u624b\u53c8\u7275\u7740\u767d\u9a6c\uff0c\u82e5\u662f\u6597\u4e86\u4e00\u4f1a\u5c06\u9a6c\u7586\u653e\u5f00\uff0c\u51ed\u8f7b\u529f\u53d6\u80dc\uff0c\u90a3\u8fd8\u662f\u53eb\u80e1\u6590\u5c0f\u770b\u4e86\u3002\u5979\u597d\u80dc\u5fc3\u5207\uff0c\u773c\u89c1\u84dd\u79e6\u62db\u62db\u529b\u4e89\u4e0a\u98ce\uff0c\u81ea\u5df1\u5251\u52bf\u5df2\u88ab\u4ed6\u957f\u5251\u7b3c\u4f4f\uff0c\u500f\u5730\u5de6\u624b\u8f7b\u8f7b\u5411\u524d\u4e00\u5e26\u3002\u90a3\u767d\u9a6c\u6781\u6709\u7075\u6027\uff0c\u53d7\u5230\u4e3b\u4eba\u6307\u5f15\uff0c\u731b\u7136\u4e00\u51b2\uff0c\u76f4\u7acb\u8d77\u6765\uff0c\u4f3c\u8981\u5f80\u84dd\u79e6\u7684\u5934\u4e0a\u8e0f\u843d\u3002 \u3000\u3000\u84dd\u79e6\u4e00\u60ca\uff0c\u4fa7\u8eab\u907f\u8ba9\uff0c\u7a81\u89c9\u624b\u8155\u4e00\u9ebb\uff0c\u624b\u4e2d\u957f\u5251\u5df2\u8131\u624b\u98de\u4e0a\u5929\u7a7a\uff0c\u4ed6\u5168\u795e\u95ea\u907f\u9a6c\u8e44\uff0c\u7adf\u6ca1\u9632\u5230\u624b\u4e2d\u5175\u5203\u906d\u4e86\u5bf9\u65b9\u6697\u7b97\u3002\u4ed6\u5728\u6b66\u6797\u4e2d\u867d\u4e0d\u7b97\u5f97\u662f\u4e00\u6d41\u9ad8\u624b\uff0c\u4f46\u6570\u5341\u5e74\u6765\u4e8b\u4e8b\u5c0f\u5fc3\uff0c\u8fd9\u624d\u957f\u4fdd\u5a01\u540d\uff0c\u60f3\u4e0d\u5230\u4e00\u751f\u8c28\u614e\uff0c\u5230\u5934\u6765\u8fd8\u662f\u767e\u5bc6\u4e00\u758f\uff0c\u8d25\u5728\u4e00\u4e2a\u5c11\u5973\u7684\u624b\u4e0b\u3002\u84dd\u79e6\u5175\u5203\u8131\u624b\uff0c\u7acb\u65f6\u4e00\u4e2a\u7bad\u6b65\uff0c\u62a2\u5230\u81ea\u5df1\u5750\u9a91\u4e4b\u65c1\uff0c\u53c8\u4ece\u978d\u65c1\u53d6\u51fa\u4e00\u67c4\u957f\u5251\uff0c\u539f\u6765\u6b64\u4eba\u505a\u4e8b\u7cbe\u7ec6\u4e4b\u6781\uff0c\u8fde\u957f\u5251\u4e5f\u591a\u5e26\u4e86\u4e00\u628a\u3002 \u3000\u3000\u7a81\u89c1\u767d\u5149\u4e00\u95ea\uff0c\u8881\u7d2b\u8863\u5c06\u624b\u4e2d\u957f\u5251\u4e5f\u63b7\u4e0a\u4e86\u5929\u7a7a\uff0c\u53cc\u5251\u5728\u7a7a\u4e2d\u76f8\u4ea4\uff0c\u5f53\u7684\u4e00\u58f0\u54cd\u84dd\u79e6\u90a3\u67c4\u5251\u7ade\u5728\u7a7a\u4e2d\u65ad\u6210\u4e24\u622a\u3002 \u3000\u3000\u5979\u8fd9\u9707\u5251\u65ad\u5200\u7684\u624b\u6cd5\u5168\u662f\u4e00\u80a1\u5de7\u52b2\uff0c\u5426\u5219\u53cc\u5251\u5728\u7a7a\u4e2d\u5747\u65e0\u7740\u529b\u4e4b\u5904\uff0c\u5982\u4f55\u80fd\u5c06\u7eaf\u94a2\u957f\u5251\u9707\u65ad\uff1f\u5979\u4f7f\u6b64\u624b\u6cd5\uff0c\u610f\u5728\u54d7\u4f17\u53d6\u5ba0\uff0c\u4fbf\u5982\u53d8\u620f\u6cd5\u4e00\u822c\uff0c\u6599\u60f3\u65c1\u4eba\u975e\u559d\u5f69\u4e0d\u53ef\u8fd9\u5f69\u58f0\u4e00\u4f5c\uff0c\u84dd\u79e6\u5fc3\u4e2d\u607c\u6012\uff0c\u518d\u6597\u4fbf\u6613\u80dc\u8fc7\u4ed6\u4e86\u3002 \u3000\u3000\u679c\u7136\u65c1\u89c2\u4f17\u4eba\u9f50\u58f0\u559d\u5f69\u3002\u84dd\u79e6\u4e00\u5446\u4e4b\u4e0b\uff0c\u8138\u8272\u5927\u53d8\uff0c\u8881\u7d2b\u8863\u63a5\u4f4f\u7a7a\u4e2d\u843d\u4e0b\u7684\u957f\u5251\uff0c\u5206\u5fc3\u523a\u5230\uff0c\u53eb\u9053\uff1a\u201c\u66f9\u56fd\u8205\u62cd\u677f\uff01\u201d\u84dd\u79e6\u63d0\u5251\u6321\u683c\uff0c\u5f53\u7684\u4e00\u54cd\uff0c\u957f\u5251\u53c8\u81ea\u65ad\u4e3a\u4e24\u622a\u3002 \u3000\u3000\u8fd9\u4e00\u4e0b\u4ecd\u662f\u8881\u7d2b\u8863\u53d6\u5de7\uff0c\u5979\u51fa\u62db\u867d\u662f\u516b\u4ed9\u5251\u6cd5\uff0c\u4f46\u53cc\u5251\u76f8\u4ea4\u4e4b\u9645\uff0c\u5251\u8eab\u5fae\u5fae\u4e00\u6296\uff0c\u5df2\u7136\u53d8\u62db\u3002\u84dd\u79e6\u4e00\u5251\u843d\u7a7a\uff0c\u88ab\u5979\u6479\u5730\u91cc\u51cc\u7a7a\u62cd\u51fb\uff0c\u6b8a\u65e0\u534a\u70b9\u529b\u9053\u76f8\u6297\uff0c\u5f85\u5f97\u8fd0\u52b2\uff0c\u5251\u8eab\u65e9\u65ad\uff0c\u62c6\u7a7f\u4e86\u8bf4\uff0c\u4e0d\u8fc7\u662f\u4ed6\u6a2a\u7740\u5251\u8eab\uff0c\u4efb\u7531\u5bf9\u65b9\u65a9\u65ad\u800c\u5df2\u3002\u53ea\u662f\u8881\u7d2b\u8863\u5fc3\u5ff5\u5982\u95ea\u7535\uff0c\u51fa\u62db\u4f3c\u5954\u96f7\uff0c\u4e00\u8ba1\u752b\u8fc7\uff0c\u4e8c\u8ba1\u53c8\u751f\uff0c\u5b9e\u662f\u53eb\u4ed6\u9632\u4e0d\u80dc\u9632\u3002 \u3000\u3000\u65c1\u89c2\u4f17\u4eba\u89c1\u90a3\u7f8e\u8c8c\u5c11\u5973\u8fde\u65ad\u4e24\u5251\uff0c\u53c8\u662f\u8f70\u96f7\u4f3c\u7684\u4e00\u58f0\u5927\u5f69\u3002 \u3000\u3000\u84dd\u79e6\u5fc3\u4e0b\u7422\u78e8\uff1a\u201c\u8fd9\u5973\u5b50\u867d\u672a\u80fd\u4ee5\u516b\u4ed9\u5251\u6cd5\u80dc\u6211\uff0c\u4f46\u5979\u6b66\u529f\u751a\u535a\uff0c\u8be1\u5f02\u767e\u7aef\uff0c\u6211\u518d\u8ddf\u5979\u52a8\u624b\u4e5f\u662f\u6789\u7136\u3002\u201d\u773c\u89c1\u5979\u6d0b\u6d0b\u8089\u5f97\uff0c\u7ffb\u8eab\u4e0a\u4e86\u9a6c\u80cc\uff0c\u4fbf\u62f1\u624b\u9053\uff1a\u201c\u4f69\u670d\uff0c\u4f69\u670d\uff01\u201d\u5f2f\u8170\u62fe\u8d77\u4e09\u622a\u65ad\u5251\uff0c\u8bf4\u9053\uff1a\u201c\u5728\u4e0b\u8fd9\u4fbf\u8fd8\u4e61\uff0c\u7ec8\u8eab\u4e0d\u63d0\u5251\u5b57\u3002\u53ea\u662f\u65c1\u4eba\u95ee\u8d77\uff0c\u5728\u4e0b\u8f93\u5728\u54ea\u4e00\u6d3e\u54ea\u4e00\u4f4d\u82f1\u96c4\u8c6a\u6770\u5251\u5e95\uff0c\u5374\u53eb\u5728\u4e0b\u5982\u4f55\u56de\u7b54\uff1f\u201d \u3000\u3000\u8881\u7d2b\u8863\u9053\uff1a\u201c\u6211\u59d3\u8881\u540d\u7d2b\u8863\uff0c\u81f3\u4e8e\u5bb6\u5e08\u7684\u540d\u8bb3\u5417\uff1f\u2026\u2026\u201d\u7eb5\u9a6c\u8d70\u5230\u84dd\u79e6\u8033\u65c1\uff0c\u51d1\u8fd1\u8eab\u53bb\uff0c\u5728\u4ed6\u8033\u8fb9\u8f7b\u8bf4\u4e86\u51e0\u4e2a\u5b57\u3002 \u3000\u3000\u84dd\u79e6\u4e00\u542c\u4e4b\u4e0b\uff0c\u8138\u8272\u53c8\u53d8\uff0c\u8138\u4e0a\u6cae\u4e27\u607c\u6068\u4e4b\u8272\u7acb\u6d88\uff0c\u53d8\u4e3a\u60f6\u6050\u606d\u987a\uff0c\u8bf4\u9053\uff1a\u201c\u65e9\u77e5\u5982\u6b64\uff0c\u5c0f\u4eba\u5982\u4f55\u6562\u4e0e\u59d1\u5a18\u52a8\u624b\uff1f\u59d1\u5a18\u89c1\u5230\u5c0a\u5e08\u4e4b\u65f6\uff0c\u4fbf\u8bf4\u68a7\u5dde\u84dd\u67d0\u5411\u4ed6\u8001\u4eba\u5bb6\u8bf7\u5b89\u3002\u201d\u8bf4\u7740\u7275\u9a6c\u5012\u9000\u4e09\u6b65\uff0c\u5019\u5728\u9053\u65c1\u3002 \u3000\u3000\u8881\u7d2b\u8863\u5728\u767d\u9a6c\u978d\u4e0a\u8f7b\u8f7b\u4e00\u62cd\uff0c\u7b11\u9053\uff1a\u201c\u5f97\u7f6a\u4e86\uff01\u201d\u56de\u5934\u5411\u80e1\u6590\u5ae3\u7136\u4e00\u7b11\uff0c\u4e00\u63d0\u9a6c\u7f30\u3002\u90a3\u767d\u9a6c\u5e76\u672a\u8d77\u6b65\uff0c\u7a81\u7136\u8dc3\u8d77\uff0c\u5728\u7a7a\u4e2d\u8d8a\u8fc7\u5382\u5341\u4f59\u8f86\u76d0\u8f66\uff0c\u5411\u5317\u75be\u9a70\uff0c\u7247\u523b\u95f4\u5df2\u4e0d\u89c1\u4e86\u5f71\u8e2a\u3002 \u3000\u3000\u5927\u9053\u4e0a\u6570\u5341\u5bf9\u773c\u775b\u4e00\u9f50\u671b\u7740\u5979\u7684\u80cc\u5f71\u3002\u4e00\u4eba\u4e00\u9a6c\u65e9\u5df2\u4e0d\u89c1\uff0c\u4f17\u4eba\u4ecd\u662f\u5446\u5446\u5730\u9065\u671b\u3002 \u3000\u3000\u8881\u7d2b\u8863\u4e00\u65e5\u4e4b\u95f4\u8fde\u8d25\u5357\u65b9\u4e24\u5927\u6b66\u5b66\u5b97\u6d3e\u7684\u9ad8\u624b\uff0c\u8fd9\u4efd\u5f97\u610f\u4e4b\u60c5\uff0c\u5b9e\u662f\u96be\u4ee5\u8a00\u5ba3\uff0c\u4f46\u89c1\u9053\u65c1\u6811\u6728\u4e0d\u7edd\u4ece\u8eab\u8fb9\u98de\u5feb\u5012\u9000\uff0c\u60c5\u4e0d\u81ea\u7981\uff0c\u7eb5\u58f0\u5531\u8d77\u6b4c\u6765\u3002 \u3000\u3000\u53ea\u5531\u5f97\u4e24\u53e5\uff0c\u7a81\u89c9\u80cc\u4e0a\u70ed\u70d8\u70d8\u7684\u6709\u4e9b\u5f02\u72b6\uff0c\u5fd9\u4f38\u624b\u53bb\u6478\uff0c\u53ea\u542c\u8f70\u7684\u4e00\u58f0\uff0c\u8eab\u4e0a\u767b\u65f6\u7740\u706b\u3002\u8fd9\u4e00\u6765\u5982\u4f55\u4e0d\u60ca\uff1f\u4e00\u62db\u201c\u4e73\u71d5\u6295\u6797\u201d\uff0c\u4ece\u9a6c\u80cc\u98de\u8eab\u8dc3\u8d77\uff0c\u8df3\u5165\u4e86\u9053\u65c1\u7684\u6cb3\u4e2d\uff0c\u80cc\u4e0a\u706b\u7130\u65b9\u59cb\u7184\u706d\u3002\u5979\u6025\u4ece\u6cb3\u4e2d\u722c\u8d77\uff0c\u4e00\u6478\u80cc\u5fc3\uff0c\u8863\u886b\u4e0a\u5df1\u70e7\u4e86\u4e00\u4e2a\u4eba\u6d1e\uff0c\u867d\u672a\u7740\u8089\uff0c\u4f46\u91cc\u8863\u4e5f\u5df2\u70e7\u7126\u3002 \u3000\u3000\u5979\u6c14\u607c\u5f02\u5e38\uff0c\u4f4e\u58f0\u9a82\u9053\uff1a\u201c\u5c0f\u8d3c\u80e1\u6590\uff0c\u5b9a\u662f\u4f60\u53c8\u4f7f\u9b3c\u8ba1\u3002\u201d\u5f53\u4e0b\u4ece\u8863\u56ca\u4e2d\u53d6\u51fa\u4e00\u4ef6\u5916\u886b\uff0c\u5f85\u8981\u66f4\u6362\uff0c\u4e00\u77a5\u95f4\u53ea\u89c1\u767d\u9a6c\u5de6\u81c0\u4e0a\u53c8\u9ed1\u53c8\u80bf\uff0c\u4e24\u53ea\u5927\u874e\u5b50\u722c\u7740\u6b63\u81ea\u542e\u8840\u3002\u8881\u7d2b\u8863\u5927\u5403\u4e00\u60ca\uff0c\u7528\u9a6c\u97ad\u5c06\u874e\u5b50\u6311\u4e0b\uff0c\u62ac\u8d77\u4e00\u5757\u77f3\u5934\u7838\u5f97\u7a00\u70c2\uff0c\u8fd9\u4e24\u53ea\u5927\u874e\u6bd2\u6027\u5389\u5bb3\uff0c\u9a6c\u81c0\u4e0a\u9ed1\u80bf\u4e4b\u5904\u4e0d\u4f4f\u5730\u6162\u6162\u6269\u5c55\u3002\u767d\u9a6c\u867d\u7136\u4f38\u9a8f\uff0c\u8fd9\u65f6\u4e5f\u5df2\u62b5\u53d7\u4e0d\u4f4f\u75db\u695a\uff0c\u7eb5\u58f0\u54c0\u9e23\uff0c\u524d\u817f\u4e00\u8dea\uff0c\u5367\u5012\u5728\u5730\u3002 \u3000\u3000\u8881\u7d2b\u8863\u699c\u60f6\u65e0\u8ba1\uff0c\u53e3\u4e2d\u53ea\u9a82\uff1a\u201c\u5c0f\u8d3c\u80e1\u6590\uff0c\u80e1\u6590\u5c0f\u8d3c\uff01\u201d\u987e\u4e0d\u5f97\u66f4\u6362\u8eab\u4e0a\u6e7f\u8863\uff0c\u4f38\u624b\u60f3\u53bb\u66ff\u767d\u9a6c\u6324\u51fa\u6bd2\u6db2\u3002\u767d\u9a6c\u6015\u75db\uff0c\u53ea\u662f\u95ea\u907f\u3002\u6b63\u72fc\u72c8\u95f4\uff0c\u5ffd\u542c\u5357\u65b9\u9a6c\u8e44\u58f0\u54cd\uff0c\u4e09\u4e58\u9a6c\u5feb\u6b65\u5954\u6765\uff0c\u5f53\u5148\u4e00\u4eba\u6b63\u662f\u80e1\u6590\u3002 \u3000\u3000\u94f6\u5149\u4e00\u95ea\uff0c\u8881\u7d2b\u8863\u8f6f\u97ad\u5728\u624b\uff0c\u98de\u8eab\u8fce\u4e0a\uff0c\u6325\u97ad\u5411\u80e1\u6590\u5939\u5934\u5939\u8111\u5288\u53bb\uff0c\u9a82\u9053\uff1a\u201c\u5c0f\u8d3c\uff0c\u6697\u7bad\u4f24\u4eba\uff0c\u7b97\u4ec0\u4e48\u597d\u6c49\uff1f\u201d \u3000\u3000\u80e1\u6590\u4e3e\u8d77\u5355\u5200\uff0c\u5f53\u7684\u4e00\u4e0b\u5c06\u5979\u8f6f\u97ad\u683c\u5f00\uff0c\u7b11\u9053\uff1a\u201c\u6211\u600e\u5730\u6697\u7bad\u4f24\u4eba\u4e86\uff1f\u201d \u3000\u3000\u8881\u7d2b\u8863\u53ea\u89c9\u624b\u81c2\u5fae\u5fae\u9178\u9ebb\uff0c\u5fc3\u60f3\u8fd9\u5c0f\u8d3c\u6b66\u529f\u679c\u7136\u4e0d\u5f31\uff0c\u5012\u4e5f\u4e0d\u53ef\u8f7b\u654c\uff0c\u9a82\u9053\uff1a\u201c\u4f60\u7528\u6bd2\u7269\u4f24\u6211\u5750\u9a91\uff0c\u8fd9\u4e0d\u662f\u4e0b\u4e09\u70c2\u7684\u5351\u9119\u884c\u5f84\u5417\uff1f\u201d\u80e1\u6590\u7b11\u9053\uff1a\u201c\u59d1\u5a18\u9a82\u5f97\u5f88\u662f\uff0c\u53ef\u600e\u77e5\u662f\u6211\u80e1\u6590\u4e0b\u7684\u624b\uff1f\u201d \u3000\u3000\u8881\u7d2b\u8863\u4e00\u6014\uff0c\u53ea\u89c1\u4ed6\u8eab\u540e\u4e24\u5339\u9a6c\u4e0a\uff0c\u5750\u7684\u662f\u90a3\u4e24\u4e2a\u672c\u6765\u4f34\u7740\u84dd\u79e6\u7684\u4f8d\u536b\u3002 \u3000\u3000\u4e24\u4eba\u5782\u5934\u4e27\u6c14\uff0c\u53cc\u624b\u5747\u88ab\u7ef3\u5b50\u7f1a\u7740\u3002\u80e1\u6590\u624b\u4e2d\u7275\u7740\u4e24\u6761\u957f\u7ef3\uff0c\u7ef3\u5b50\u53e6\u4e00\u7aef\u5206\u522b\u7cfb\u4f4f\u4e24\u4eba\u7684\u9a6c\u7f30\uff0c\u539f\u6765\u4e24\u540d\u4f8d\u536b\u88ab\u4ed6\u64d2\u7740\u800c\u6765\u3002\u8881\u7d2b\u8863\u5fc3\u5ff5\u4e00\u52a8\uff0c\u5df2\u731c\u5230\u4e86\u4e09\u5206\uff0c\u4fbf\u9053\uff1a\u201c\u96be\u9053\u662f\u8fd9\u4e24\u4e2a\u5bb6\u4f19\uff1f\u201d \u3000\u3000\u80e1\u6590\u7b11\u9053\uff1a\u201c\u4ed6\u4e8c\u4f4d\u7684\u5c0a\u59d3\u5927\u540d\uff0c\u6c5f\u6e56\u4e0a\u7684\u540d\u53f7\uff0c\u59d1\u5a18\u4e0d\u59a8\u5148\u52b3\u795e\u95ee\u95ee\u3002\u201d \u3000\u3000\u8881\u7d2b\u8863\u767d\u4e86\u4ed6\u4e00\u773c\uff0c\u9053\uff1a\u201c\u4f60\u65e2\u77e5\u9053\u4e86\uff0c\u4fbf\u8bf4\u7ed9\u6211\u542c\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u597d\uff0c\u5728\u4e0b\u6765\u7ed9\u8881\u59d1\u5a18\u5f15\u89c1\u4e24\u4f4d\u6b66\u6797\u4e2d\u7684\u6210\u540d\u4eba\u7269\u3002\u8fd9\u4f4d\u662f\u5c0f\u795d\u878d\u66f9\u731b\uff0c\u8fd9\u4f4d\u662f\u94c1\u874e\u5b50\u5d14\u767e\u80dc\u3002\u4f60\u4eec\u4e09\u4f4d\u591a\u4eb2\u8fd1\u4eb2\u8fd1\u3002\u201d \u3000\u3000\u8881\u7d2b\u8863\u4e00\u542c\u4e24\u4eba\u7684\u6d51\u53f7\uff0c\u7acb\u65f6\u604d\u7136\uff0c\u201c\u5c0f\u795d\u878d\u201d\u81ea\u662f\u64c5\u4f7f\u706b\u5668\uff0c\u94c1\u874e\u5b50\u5f53\u7136\u4f1a\u653e\u6bd2\u7269\uff0c\u5b9a\u662f\u8fd9\u4e8c\u4eba\u53d7\u4e86\u6298\u8fb1\uff0c\u5fc3\u4e2d\u4e0d\u5fff\uff0c\u4e58\u7740\u81ea\u5df1\u4e0e\u84dd\u79e6\u6fc0\u6597\u4e4b\u65f6\uff0c\u5077\u5077\u4e0b\u624b\u76f8\u5bb3\u3002\u5f53\u5373\u62cd\u62cd\u62cd\u3001\u62cd\u62cd\u62cd\uff0c\u8fde\u54cd\u516d\u4e0b\uff0c\u5728\u6bcf\u4eba\u5934\u4e0a\u62bd\u4e86\u4e09\u9a6c\u97ad\uff0c\u53ea\u6253\u5f97\u4e24\u4eba\u6ee1\u5934\u6ee1\u8138\u90fd\u662f\u9c9c\u8840\u3002\u5979\u6307\u7740\u94c1\u874e\u5b50\u559d\u9053\uff1a\u201c\u5feb\u53d6\u89e3\u836f\u6cbb\u597d\u6211\u7684\u9a6c\u513f\uff0c\u5426\u5219\u518d\u5403\u6211\u4e09\u97ad\uff0c\u8fd9\u4e00\u6b21\u53ef\u662f\u7528\u8fd9\u6761\u97ad\u5b50\u4e86\uff01\u201d\u8bf4\u7740\u8f6f\u97ad\u4e00\u626c\uff0c\u5580\u5587\u4e00\u58f0\u54cd\uff0c\u5c06\u9053\u65c1\u4e00\u682a\u5927\u67f3\u6811\u7684\u679d\u5e72\u6253\u4e0b\u4e86\u4e00\u622a\u3002 \u3000\u3000\u94c1\u874e\u5b50\u5413\u4e86\u4e00\u8df3\uff0c\u5c06\u7ed1\u7f1a\u7740\u7684\u53cc\u624b\u63d0\u4e86\u4e00\u63d0\uff0c\u9053\uff1a\u201c\u6211\u600e\u80fd\u2026\u2026\u201d\u80e1\u6590\u4e0d\u7b49\u4ed6\u8bf4\u5b8c\uff0c\u5355\u5200\u4e00\u6325\uff0c\u64e6\u7684\u4e00\u58f0\uff0c\u5272\u65ad\u4e86\u4ed6\u624b\u4e0a\u7ef3\u7d22\u3002\u8fd9\u4e00\u5200\u75be\u5288\u800c\u4e0b\uff0c\u7ef3\u7d22\u5e94\u5203\u800c\u65ad\uff0c\u5999\u5728\u51fa\u5200\u6070\u5230\u597d\u5904\uff0c\u6ca1\u4f24\u5230\u4ed6\u534a\u5206\u808c\u80a4\u3002 \u3000\u3000\u8881\u7d2b\u8863\u6a2a\u4e86\u4ed6\u4e00\u773c\uff0c\u9f3b\u4e2d\u5fae\u5fae\u4e00\u54fc\uff0c\u5fc3\u9053\uff1a\u201c\u663e\u672c\u4e8b\u4e48\uff1f\u90a3\u4e5f\u6ca1\u4ec0\u4e48\u4e86\u4e0d\u8d77\u3002\u201d \u3000\u3000\u94c1\u874e\u5b50\u4ece\u6000\u4e2d\u53d6\u51fa\u89e3\u836f\uff0c\u7ed9\u767d\u9a6c\u6577\u4e0a\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u6709\u6211\u7684\u72ec\u95e8\u89e3\u836f\uff0c\u4fbf\u4e0d\u788d\u4e8b\u3002\u201d\u7a0d\u7a0d\u4e00\u987f\uff0c\u53c8\u9053\uff1a\u201c\u53ea\u662f\u8fd9\u7272\u53e3\u4e09\u5929\u4e2d\u4e0d\u80fd\u6025\u8dd1\uff0c\u4ee5\u514d\u4f24\u4e86\u7b4b\u9aa8\u3002\u201d \u3000\u3000\u8881\u7d2b\u8863\u9053\uff1a\u201c\u4f60\u53bb\u7ed9\u5c0f\u795d\u878d\u89e3\u4e86\u7ed1\u7f1a\u3002\u201d\u94c1\u874e\u5b50\u5fc3\u4e2d\u751a\u559c\uff0c\u6697\u60f3\uff1a\u201c\u867d\u7136\u5403\u4e86\u4e09\u9a6c\u97ad\uff0c\u5e78\u559c\u9664\u66f9\u5927\u54e5\u5916\u5e76\u65e0\u719f\u4eba\u77a7\u89c1\u3002\u4ed6\u81ea\u5df1\u4e5f\u5403\u4e09\u97ad\uff0c\u81ea\u7136\u4e0d\u4f1a\u5c06\u6b64\u4e8b\u5f20\u626c\u51fa\u53bb\u3002\u201d\u8981\u77e5\u4ed6\u4eec\u8fd9\u4e9b\u505a\u6b66\u5b98\u7684\uff0c\u8eab\u4e0a\u5403\u4e9b\u82e6\u5934\u5012\u6ca1\u4ec0\u4e48\uff0c\u6700\u6015\u662f\u6298\u4e86\u5a01\u98ce\uff0c\u7ed9\u540c\u4f34\u4eec\u77a7\u4f4e\u4e86\u3002\u4ed6\u8d70\u8fc7\u53bb\u7ed9\u66f9\u731b\u89e3\u4e86\u7ed1\u7f1a\uff0c\u6b63\u5f85\u8981\u8d70\uff0c\u8881\u7d2b\u8863\u9053\uff1a\u201c\u8fd9\u4fbf\u8d70\u4e86\u4e48\uff1f\u4e16\u95f4\u4e0a\u53ef\u6709\u8fd9\u7b49\u4fbf\u5b9c\u4e8b\u60c5\uff1f\u201d \u3000\u3000\u5d14\u66f9\u4e24\u4eba\u5411\u5979\u671b\u4e86\u4e00\u773c\uff0c\u53c8\u4e92\u77a7\u4e00\u773c\u3002\u4ed6\u4e8c\u4eba\u7ed9\u80e1\u6590\u624b\u5230\u64d2\u6765\uff0c\u5355\u662f\u80e1\u6590\u4e00\u4eba\u5df2\u975e\u654c\u624b\uff0c\u4f55\u51b5\u52a0\u4e0a\u8fd9\u4e2a\u6b66\u827a\u9ad8\u5f3a\u7684\u5973\u5b50\uff0c\u53ea\u5f97\u52d2\u9a6c\u4e0d\u52a8\uff0c\u9759\u5019\u53d1\u843d\u3002 \u3000\u3000\u8881\u7d2b\u8863\u9053\uff1a\u201c\u5c0f\u795d\u878d\u628a\u8eab\u8fb9\u7684\u706b\u5668\u90fd\u53d6\u51fa\u6765\uff0c\u94c1\u874e\u5b50\u628a\u6bd2\u7269\u53d6\u51fa\u6765\uff0c\u53ea\u8981\u7559\u4e86\u4e86\u4e00\u4ef6\uff0c\u5c0f\u5fc3\u59d1\u5a18\u7684\u97ad\u5b50\u3002\u201d\u8bf4\u7740\u8f6f\u97ad\u6325\u51fa\uff0c\u4e00\u6296\u4e00\u5377\uff0c\u5728\u7a7a\u4e2d\u62cd\u7684\u4e00\u58f0\u5927\u54cd\u3002 \u3000\u3000\u4e24\u4eba\u65e0\u5948\uff0c\u5fc3\u60f3\uff1a\u201c\u4f60\u8981\u7f34\u4e86\u6211\u4eec\u7684\u6210\u540d\u6697\u5668\uff0c\u4ee5\u89e3\u4f60\u5fc3\u5934\u4e4b\u6068\uff0c\u90a3\u4e5f\u53eb\u505a\u65e0\u6cd5\u53ef\u60f3\u3002\u201d\u53ea\u5f97\u5c06\u6697\u5668\u53d6\u51fa\u3002 \u3000\u3000\u5c0f\u795d\u878d\u7684\u706b\u5668\u662f\u4e00\u4e2a\u88c5\u6709\u5f39\u7c27\u7684\u94c1\u5323\u3002\u94c1\u874e\u5b50\u624b\u91cc\u5374\u62ff\u7740\u4e00\u4e2a\u7af9\u7b52\uff0c\u7b52\u4e2d\u81ea\u7136\u76db\u653e\u7740\u874e\u5b50\u4e86\uff0c\u8fd9\u7af9\u7b52\u7cbe\u5149\u6ed1\u6e9c\uff0c\u8d77\u4e86\u4e00\u5c42\u9ec4\u6cb9\uff0c\u81ea\u5df2\u4f7f\u7528\u591a\u5e74\u3002\u8881\u7d2b\u8863\u4e00\u89c1\uff0c\u60f3\u8d77\u7b52\u4e2d\u6bdb\u8338\u8338\u7684\u6bd2\u7269\uff0c\u4e0d\u7981\u5fc3\u4e2d\u53d1\u6bdb\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4eba\u7adf\u6562\u5bf9\u59d1\u5a18\u6697\u4e0b\u6bd2\u624b\uff0c\u53ef\u7b97\u5f97\u5927\u80c6\u4e4b\u6781\u3002\u4eca\u65e5\u539f\u662f\u975e\u6b7b\u4e0d\u53ef\uff0c\u5e78\u4e8f\u59d1\u5a18\u751f\u5e73\u6709\u4e2a\u60ef\u4f8b\uff0c\u4e00\u5929\u4e4b\u4e2d\u53ea\u6740\u4e00\u4eba\uff0c\u603b\u7b97\u4f60\u4eec\u8fd0\u6c14\u2026\u2026\u201d\u5d14\u66f9\u4e8c\u4eba\u76f8\u671b\u4e00\u773c\uff0c\u5747\u60f3\uff1a \u3000\u3000\u201c\u4e0d\u77e5\u4f60\u4eca\u5929\u5df2\u6740\u8fc7\u4e86\u4eba\u6ca1\u6709\u3002\u201d\u5374\u542c\u8881\u7d2b\u8863\u63a5\u7740\u9053\uff1a\u201c\u2026\u2026\u4e8c\u4eba\u4e4b\u4e2d\u53ea\u987b\u6b7b\u4e00\u4e2a\u4fbf\u591f\u3002\u5230\u5e95\u54ea\u4e00\u4e2a\u6b7b\uff0c\u54ea\u4e00\u4e2a\u6d3b\uff0c\u6211\u4e5f\u96be\u4ee5\u51b3\u5b9a\uff0c\u8fd9\u6837\u5427\uff0c\u4f60\u4eec\u4e92\u76f8\u53d1\u5c04\u6697\u5668\uff0c\u8c01\u8eab\u4e0a\u5148\u4e2d\u4e86\uff0c\u90a3\u4fbf\u8be5\u6b7b\uff1a\u8eb2\u5f97\u8fc7\u7684\uff0c\u5c31\u9976\u4e86\u4ed6\u6027\u547d\u3002\u6211\u7d20\u6765\u8bf4\u4e00\u4e0d\u4e8c\uff0c\u6c42\u4e5f\u65e0\u7528\u3002\u4e00\u3001\u4e8c\u3001\u4e09\uff01\u52a8\u624b\u5427\uff01\u2026\u66f9\u5d14\u4e8c\u4eba\u5fc3\u4e2d\u72b9\u8c6b\uff0c\u4e0d\u77e5\u5979\u8fd9\u756a\u8bdd\u662f\u771f\u662f\u5047\uff0c\u4f46\u968f\u5373\u60f3\u8d77\uff1a\u201c\u82e5\u662f\u7ed9\u4ed6\u5148\u52a8\u4e86\u624b\uff0c\u6211\u5c82\u975e\u5728\u9001\u4e86\u6027\u547d\uff1f\u201d \u3000\u3000\u4e8c\u4eba\u5747\u662f\u5fc3\u72e0\u624b\u8fa3\u4e4b\u8f88\uff0c\u5fc3\u5ff5\u752b\u52a8\uff0c\u7acb\u5373\u51fa\u624b\uff0c\u53ea\u89c1\u706b\u5149\u4e00\u95ea\uff0c\u4e24\u4eba\u9f50\u58f0\u60e8\u547c\u3002\u5c0f\u795d\u878d\u9888\u4e2d\u88ab\u4e00\u53ea\u5927\u874e\u54ac\u4f4f\uff0c\u94c1\u874e\u5b50\u80f8\u524d\u706b\u7403\u4e71\u821e\uff0c\u80e1\u5b50\u7740\u706b\u3002 \u3000\u3000\u8881\u7d2b\u8863\u683c\u683c\u5a07\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u4e0d\u5206\u80dc\u8d25\uff01\u59d1\u5a18\u8fd9\u53e3\u6076\u6c14\u4e5f\u51fa\u4e86\uff0c\u90fd\u7ed9\u6211\u6eda\u5427\uff01\u201d\u66f9\u5d14\u4e8c\u4eba\u8eab\u4e0a\u867d\u7136\u5267\u75db\uff0c\u8fd9\u4e24\u53e5\u8bdd\u5374\u90fd\u542c\u5f97\u6e05\u6d01\u695a\u695a\uff0c\u5f53\u4e0b\u987e\u4e0d\u5f97\u6bd2\u874e\u5728\u9888\uff0c\u987b\u4e0a\u7740\u706b\uff0c\u4e00\u9f50\u7eb5\u9a6c\u4fbf\u5954\uff0c\u76f4\u5230\u9a70\u51fa\u8001\u8fdc\uff0c\u8fd9\u624d\u4e92\u76f8\u6551\u63f4\uff0c\u89e3\u6bd2\u706d\u706b\u3002 \u3000\u3000\u8881\u7d2b\u8863\u7b11\u58f0\u4e0d\u7edd\uff0c\u4e00\u9635\u98ce\u8fc7\u6765\uff0c\u731b\u89c9\u80cc\u4e0a\u51c9\u8258\u98d3\u7684\uff0c\u767b\u65f6\u60f3\u8d77\u8863\u886b\u5df2\u7834\uff0c\u4e00\u8f6c\u773c\uff0c\u53ea\u89c1\u80e1\u6590\u7b11\u563b\u563b\u7684\u671b\u7740\u81ea\u5df1\uff0c\u4e0d\u7531\u5f97\u5927\u7f9e\uff0c\u7ea2\u6655\u53cc\u988a\uff0c\u559d\u9053\uff1a\u201c\u4f60\u77a7\u4ec0\u4e48\uff1f\u201d\u80e1\u6590\u5c06\u5934\u8f6c\u5f00\uff0c\u7b11\u9053\uff1a\u201c\u6211\u5728\u60f3\u5e78\u4e8f\u90a3\u874e\u5b50\u6ca1\u54ac\u5230\u59d1\u5a18\u3002\u201d\u8881\u7d2b\u8863\u4e0d\u7531\u5f97\u6253\u4e2a\u5bd2\u54c4\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u8bdd\u5012\u4e5f\u4e0d\u9519\uff0c\u7ed9\u874e\u5b50\u54ac\u5230\u4e86\uff0c\u90a3\u8fd8\u4e86\u5f97\u4e86\u201d \u3000\u3000\u8bf4\u9053\uff1a\u201c\u6211\u8981\u6362\u8863\u886b\u4e86\uff0c\u4f60\u8d70\u5f00\u4e9b\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u4f60\u4fbf\u5728\u8fd9\u5927\u9053\u4e4b\u4e0a\u6362\u8863\u886b\u4e48\uff1f\u201d\u8881\u7d2b\u8863\u53c8\u751f\u6c14\u53c8\u597d\u7b11\uff0c\u5fc3\u60f3\u81ea\u5df1\u4e00\u7740\u6025\uff0c\u51fa\u8a00\u4e0d\u614e\uff0c\u4e8e\u662f\u53c8\u72e0\u72e0\u77aa\u4e86\u4ed6\u4e00\u773c\uff0c\u8d70\u5230\u9053\u65c1\u6811\u4e1b\u4e4b\u540e\uff0c\u6025\u5fd9\u9664\u4e0b\u5916\u8863\uff0c\u6362\u4e86\u4ef6\u674f\u9ec4\u8272\u7684\u886b\u5b50\uff0c\u5185\u8863\u4ecd\u6e7f\uff0c\u5374\u4e5f\u987e\u4e0d\u5f97\u4e86\u3002\u70e7\u7834\u7684\u8863\u886b\u4e5f\u4e0d\u8981\u4e86\uff0c\u5377\u6210\u4e00\u56e2\uff0c\u629b\u5165\u6cb3\u4e2d\u3002 \u3000\u3000\u80e1\u6590\u773c\u671b\u7740\u7d2b\u8863\u968f\u6ce2\u9010\u6d41\u800c\u53bb\uff0c\u8bf4\u9053\uff1a\u201c\u59d1\u5a18\u9ad8\u59d3\u5927\u540d\uff0c\u53ef\u53eb\u505a\u8881\u9ec4\u886b\uff1f\u201d \u3000\u3000\u8881\u7d2b\u8863\u54fc\u4e86\u4e00\u58f0\uff0c\u77e5\u4ed6\u6599\u5230\u201c\u8881\u7d2b\u8863\u201d\u4e09\u5b57\u5e76\u975e\u81ea\u5df1\u771f\u540d\uff0c\u5ffd\u7136\u5c16\u53eb\u4e00\u58f0\uff1a \u3000\u3000\u201c\u554a\u54df\uff0c\u6709\u4e00\u53ea\u874e\u5b50\u54ac\u6211\u3002\u201d\u4f38\u624b\u6309\u4f4f\u4e86\u80cc\u5fc3\u3002\u80e1\u6590\u4e00\u60ca\uff0c\u53eb\u9053\uff1a\u201c\u5f53\u771f\uff0c\u201d \u3000\u3000\u7eb5\u8eab\u8fc7\u53bb\u60f3\u5e2e\u5979\u6253\u4e0b\u874e\u5b50\u3002\u54ea\u6599\u5230\u8881\u7d2b\u8863\u8fd9\u4e00\u53eb\u5b9e\u662f\u76f8\u6b3a\uff0c\u80e1\u6590\u8eab\u5728\u534a\u7a7a\uff0c\u8881\u7d2b\u8863\u5ffd\u5730\u4f38\u624b\u7528\u529b\u4e00\u63a8\u3002\u8fd9\u4e00\u62db\u6765\u5f97\u65e0\u8e2a\u65e0\u5f71\uff0c\u4ed6\u53c8\u5168\u6ca1\u63d0\u9632\uff0c\u767b\u65f6\u4e00\u4e2a\u7b4b\u6597\u6454\u4e86\u51fa\u53bb\uff0c\u8dcc\u5411\u6cb3\u8fb9\u7684\u4e00\u4e2a\u81ed\u6ce5\u5858\u4e2d\u3002\u4ed6\u5728\u534a\u7a7a\u65f6\u8eab\u5b50\u867d\u5df2\u8f6c\u76f4\uff0c\u4f46\u53cc\u8db3\u4e00\u843d\uff0c\u81ed\u6ce5\u76f4\u6ca1\u81f3\u80f8\u53e3\u3002\u8881\u7d2b\u8863\u62cd\u624b\u563b\u7b11\uff0c\u53eb\u9053\uff1a\u201c\u9601\u4e0b\u9ad8\u59d3\u5927\u540d\uff0c\u53ef\u662f\u53eb\u4f5c\u5c0f\u6ce5\u9cc5\u80e1\u6590\uff1f\u201d \u3000\u3000\u80e1\u6590\u8fd9\u4e00\u4e0b\u771f\u662f\u54ed\u7b11\u4e0d\u5f97\uff0c\u81ea\u5df1\u4e00\u7247\u597d\u5fc3\uff0c\u90a3\u6599\u5230\u5979\u4f1a\u7a81\u7136\u51fa\u624b\uff0c\u8db3\u5e95\u53c8\u662f\u8f6f\u8f6f\u7684\u5168\u4e0d\u53d7\u529b\uff0c\u65e0\u6cd5\u7eb5\u8dc3\uff0c\u53ea\u5f97\u4e00\u6b65\u4e00\u987f\uff0c\u62d6\u6ce5\u5e26\u6c34\u5730\u8d70\u4e86\u4e0a\u6765\u3002\u8fd9\u65f6\u5df2\u4e0d\u7531\u5f97\u4ed6\u4e0d\u6012\uff0c\u4f46\u89c1\u8881\u7d2b\u8863\u7b11\u9765\u5982\u82b1\u76db\u653e\uff0c\u5fc3\u4e2d\u53c8\u5fae\u5fae\u611f\u5230\u4e00\u4e9b\u751c\u610f\uff0c\u5f20\u5f00\u6ee1\u662f\u81ed\u6ce5\u7684\u53cc\u638c\uff0c\u6251\u4e86\u8fc7\u53bb\uff0c\u559d\u9053\uff1a\u201c\u5c0f\u4e2b\u5934\uff0c\u6211\u53eb\u4f60\u6539\u540d\u8881\u6ce5\u886b\uff01\u201d \u3000\u3000\u8881\u7d2b\u8863\u5413\u4e86\u4e00\u8df3\uff0c\u62d4\u811a\u60f3\u9003\u3002\u90a3\u77e5\u80e1\u6590\u7684\u8f7b\u529f\u751a\u662f\u4e86\u5f97\uff0c\u5979\u4e1c\u7a9c\u897f\u8dc3\uff0c\u5374\u59cb\u7ec8\u7ed9\u4ed6\u5f20\u5f00\u53cc\u81c2\u62e6\u4f4f\u53bb\u8def\uff0c\u4f46\u89c1\u4ed6\u4e00\u7eb5\u4e00\u8df3\uff0c\u4e0d\u4f4f\u7684\u4f38\u81c2\u6251\u6765\u3002\u5979\u53c8\u4e0d\u6562\u548c\u4ed6\u52a8\u624b\u62c6\u62db\uff0c\u53ea\u8981\u4e00\u8fd8\u624b\uff0c\u8eab\u4e0a\u975e\u6e85\u6ee1\u81ed\u6ce5\u4e0d\u53ef\u3002\u8fd9\u4e00\u6765\u9003\u65e2\u4e0d\u80fd\uff0c\u6253\u53c8\u4e0d\u5f97\uff0c\u773c\u89c1\u80e1\u6590\u548c\u8eab\u7eb5\u4e0a\uff0c\u81ea\u5df1\u5df2\u65e0\u6cd5\u95ea\u907f\uff0c\u4e00\u4e0b\u4fbf\u8981\u7ed9\u4ed6\u62b1\u4f4f\uff0c\u7d22\u6027\u7ad9\u5b9a\u8eab\u5b50\uff0c\u4fcf\u8138\u4e00\u677f\uff0c\u9053\uff1a\u201c\u4f60\u6562\u78b0\u6211\uff1f\u201d \u3000\u3000\u80e1\u6590\u5f20\u81c2\u7eb5\u8dc3\uff0c\u672c\u6765\u53ea\u662f\u5413\u5979\uff0c\u8fd9\u65f6\u89c1\u5979\u7acb\u5b9a\uff0c\u4e5f\u5373\u505c\u6b65\uff0c\u9f3b\u4e2d\u95fb\u5230\u4e00\u80a1\u6de1\u6de1\u7684\u5e7d\u9999\uff0c\u5fd9\u9000\u51fa\u6570\u6b65\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u597d\u610f\u76f8\u52a9\uff0c\u4f60\u600e\u5730\u72d7\u54ac\u5415\u6d1e\u5bbe\uff1f\u201d \u3000\u3000\u8881\u7d2b\u8863\u7b11\u9053\uff1a\u201c\u8fd9\u662f\u516b\u4ed9\u5251\u4e2d\u7684\u4e00\u62db\uff0c\u53eb\u4f5c\u5415\u6d1e\u5bbe\u63a8\u72d7\u3002\u4f60\u82e5\u4e0d\u4fe1\uff0c\u53ef\u53bb\u95ee\u90a3\u4e2a\u59d3\u84dd\u7684\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u4ee5\u6028\u62a5\u5fb7\uff0c\u6ca1\u826f\u5fc3\u554a\uff0c\u6ca1\u826f\u5fc3\uff01\u201d\u8881\u7d2b\u8863\u9053\uff1a\u201c\u5478\uff01 \u3000\u3000\u8fd8\u8bf4\u624b\u6211\u6709\u5fb7\u5462\uff0c\u8fd9\u53eb\u505a\u5e02\u6069\uff0c\u6700\u574f\u7684\u5bb6\u4f19\u624d\u662f\u5982\u6b64\u3002\u6211\u95ee\u4f60\uff0c\u4f60\u600e\u77e5\u8fd9\u4e24\u4e2a\u5bb6\u4f19\u653e\u706b\u4e0b\u6bd2\uff0c\u64d2\u6765\u7ed9\u6211\uff1f\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u767b\u65f6\u5c06\u80e1\u6590\u95ee\u5f97\u8bed\u585e\u3002\u539f\u6765\u4e24\u540d\u4f8d\u536b\u5728\u5979\u80cc\u4e0a\u6697\u843d\u706b\u79cd\uff0c\u5728\u5979\u9a6c\u81c0\u4e0a\u5077\u653e\u6bd2\u874e\u3002\u80e1\u6590\u786e\u662f\u5728\u65c1\u77a7\u5f97\u6e05\u695a\uff0c\u5f53\u65f6\u5e76\u4e0d\u53eb\u7834\uff0c\u5f85\u8881\u7d2b\u8863\u53bb\u540e\uff0c\u8fd9\u624d\u64d2\u4e86\u4e24\u4eba\u968f\u540e\u8d76\u6765\u3002 \u3000\u3000\u8881\u7d2b\u8863\u9053\uff1a\u201c\u662f\u4e48\uff1f\u6240\u4ee5\u6211\u624d\u4e0d\u9886\u4f60\u8fd9\u4e2a\u60c5\u5462\u3002\u201d\u5979\u53d6\u51fa\u4e00\u5757\u624b\u5e15\uff0c\u63a9\u4f4f\u9f3b\u5b50\uff0c\u76b1\u7709\u9053\uff1a\u201c\u4f60\u8eab\u4e0a\u597d\u81ed\uff0c\u77e5\u4e0d\u77e5\u9053\uff1f\u201d\u80e1\u6590\u9053\uff1a\u201c\u8fd9\u662f\u62dc\u5415\u6d1e\u5bbe\u4e4b\u8d50\u3002\u201d\u8881\u7d2b\u8863\u5fae\u7b11\u9053\uff1a\u201d\u8fd9\u4e48\u8bf4\uff0c\u4f60\u81ea\u5df1\u8ba4\u662f\u5c0f\u72d7\u5566\u3002\u201d\u5979\u5411\u56db\u4e0b\u4e00\u671b\uff0c\u7b11\u9053\uff1a\u201c\u5feb\u4e0b\u6cb3\u53bb\u6d17\u4e2a\u5e72\u51c0\uff0c\u6211\u518d\u8ddf\u4f60\u8bf4\u8d75\u4e09\u2026\u2026\u8d75\u534a\u5c71\u90a3\u5c0f\u5b50\u7684\u4e8b\u3002\u201d\u5979\u672c\u60f3\u8bf4\u201c\u8d75\u4e09\u53d4\u201d\uff0c\u4f46\u6015\u80e1\u6590\u53c8\u81ea\u5c45\u957f\u8f88\uff0c\u7d22\u6027\u6539\u53e3\u53eb\u201c\u8d75\u534a\u5c71\u90a3\u5c0f\u5b50\u201d\u3002 \u3000\u3000\u80e1\u6590\u5927\u559c\uff0c\u9053\uff1a\u201c\u597d\u597d\u3002\u4f60\u8bf7\u5230\u90a3\u8fb9\u6b47\u4e00\u4f1a\u513f\uff0c\u6211\u6d17\u5f97\u5f88\u5feb\u3002\u201d\u8881\u7d2b\u8863\u9053\uff1a\u201c\u6d17\u5f97\u5feb\u4e86\uff0c\u81ed\u6c14\u4e0d\u9664\u3002\u201d\u80e1\u6590\u4e00\u7b11\uff0c\u4e00\u62db\u201c\u4e00\u9e64\u51b2\u5929\u201d\uff0c\u62d4\u8d77\u8eab\u5b50\uff0c\u5411\u6cb3\u4e2d\u843d\u4e0b\u3002 \u3000\u3000\u8881\u7d2b\u8863\u770b\u770b\u767d\u9a6c\u7684\u4f24\u5904\uff0c\u90a3\u94c1\u874e\u5b50\u7684\u89e3\u836f\u679c\u7136\u7075\u9a8c\uff0c\u8fd9\u4e0d\u591a\u65f6\u4e4b\u95f4\uff0c\u80bf\u52bf\u4f3c\u5df2\u7565\u9000\uff0c\u767d\u9a6c\u4e0d\u518d\u5636\u53eb\uff0c\u60f3\u6765\u75db\u695a\u5df2\u51cf\u3002\u5979\u9065\u9065\u5411\u80e1\u6590\u671b\u4e86\u4e00\u773c\uff0c\u53ea\u89c1\u4ed6\u8863\u670d\u978b\u889c\u90fd\u5806\u5728\u5cb8\u8fb9\uff0c\u5374\u6e38\u5230\u8fdc\u8fdc\u5341\u4f59\u4e08\u4e4b\u5916\u53bb\u6d17\u8eab\u4e0a\u6ce5\u6c61\uff0c\u60f3\u662f\u8d64\u8eab\u9732\u4f53\uff0c\u751f\u6015\u7ed9\u81ea\u5df1\u770b\u5230\u3002 \u3000\u3000\u8881\u7d2b\u8863\u5fc3\u5ff5\u4e00\u52a8\uff0c\u4ece\u5305\u88f9\u4e2d\u53d6\u51fa\u4e00\u4ef6\u65e7\u886b\uff0c\u6084\u6084\u8fc7\u53bb\u7f69\u5728\u80e1\u6590\u7684\u8863\u886b\u4e4b\u4e0a\uff0c\u5c06\u4ed6\u6cbe\u6ee1\u4e86\u6ce5\u6d46\u7684\u8863\u670d\u978b\u889c\u4e00\u53e4\u8111\u513f\u5305\u5728\u65e7\u886b\u4e4b\u4e2d\uff0c\u62b1\u5728\u624b\u91cc\uff0c\u8fc7\u53bb\u9a91\u4e0a\u4e86\u9752\u9a6c\uff0c\u7275\u4e86\u767d\u9a6c\uff0c\u5411\u5317\u7f13\u7f13\u800c\u884c\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u4f60\u8fd9\u6837\u6162\uff01\u6211\u8eab\u6709\u8981\u4e8b\uff0c\u53ef\u7b49\u4e0d\u53ca\u4e86\uff01\u201d\u8bf4\u7740\u7b56\u9a6c\u800c\u884c\uff0c\u751f\u6015\u80e1\u6590\u5c31\u6b64\u8d64\u8eab\u722c\u8d77\u6765\u8ffd\u8d76\uff0c\u59cb\u7ec8\u4e0d\u6562\u56de\u5934\u3002\u4f46\u542c\u5f97\u8eab\u540e\u80e1\u6590\u5927\u53eb\uff1a\u201c\u5582\uff0c\u5582\uff01\u8881\u59d1\u5a18\uff01\u6211\u8ba4\u683d\u5566\uff0c\u4f60\u628a\u6211\u8863\u670d\u7559\u4e0b\uff0c\u201d \u3000\u3000\u53eb\u58f0\u8d8a\u6765\u8d8a\u8fdc\uff0c\u663e\u662f\u4ed6\u4e0d\u6562\u51fa\u6cb3\u8ffd\u8d76\u3002 \u3000\u3000\u8881\u7d2b\u8863\u4e00\u8def\u4e0a\u8d8a\u60f3\u8d8a\u662f\u597d\u7b11\uff0c\u63a5\u8fde\u6570\u6b21\uff0c\u5fcd\u4e0d\u4f4f\u7b11\u51fa\u58f0\u6765\uff0c\u53c8\u60f3\u6700\u540e\u4e00\u6b21\u4f5c\u5f04\u80e1\u6590\u4e0d\u514d\u884c\u9669\uff0c\u82e5\u4ed6\u5192\u5192\u5931\u5931\uff0c\u4e0d\u987e\u4e00\u5207\uff0c\u5c31\u6b64\u62a2\u4e0a\u5cb8\u6765\u8ffd\u8d76\uff0c\u53cd\u8981\u4f7f\u81ea\u5df1\u5c34\u5c2c\u4e07\u5206\u3002 \u3000\u3000\u8fd9\u65e5\u53ea\u8d70\u4e86\u4f59\u91cc\uff0c\u5c31\u5728\u9053\u65c1\u627e\u4e2a\u5c0f\u5ba2\u5e97\u6b47\u4e86\u3002\u5979\u8ddf\u81ea\u5df1\u8bf4\uff1a\u201c\u767d\u9a6c\u4e2d\u4e86\u6bd2\uff0c\u94c1\u874e\u5b50\u90a3\u6df7\u86cb\u8bf4\u7684\uff0c\u82e5\u662f\u8dd1\u52a8\uff0c\u4fbf\u8981\u4f24\u4e86\u7b4b\u9aa8\u3002\u201d\u4f46\u5728\u5185\u5fc3\u6df1\u5904\uff0c\u5374\u6781\u76fc\u80e1\u6590\u8d76\u6765\u8ddf\u81ea\u5df1\u7406\u8bba\u4e89\u95f9\u3002 \u3000\u3000\u4e00\u665a\u5e73\u5b89\u8fc7\u53bb\uff0c\u80e1\u6590\u7adf\u6ca1\u8e2a\u5f71\u3002\u6b21\u6668\u7f13\u7f13\u800c\u884c\uff0c\u5fc3\u4e2d\u60f3\u50cf\u80e1\u6590\u4e0d\u77e5\u5982\u4f55\u4e0a\u5cb8\uff0c\u5982\u4f55\u53bb\u5f04\u8863\u886b\u7a7f\uff0c\u60f3\u4e86\u4e00\u4f1a\uff0c\u5fcd\u4e0d\u4f4f\u53c8\u597d\u7b11\u8d77\u6765\u3002\u5979\u6bcf\u5929\u53ea\u884c\u4e94\u516d\u5341\u91cc\u8def\u7a0b\uff0c\u4f46\u80e1\u6590\u59cb\u7ec8\u6ca1\u8ffd\u4e0a\u6765\uff0c\u82b3\u5fc3\u53ef\u53ef\uff0c\u7adf\u662f\u5c3d\u8bb0\u7740\u8fd9\u4e2a\u6d51\u8eab\u81ed\u6ce5\u7684\u5c0f\u6ce5\u9cc5\u80e1\u6590\u3002 \u3000"}),showOnHomepage:!0},{name:"\u7b2c\u4e03\u7ae0\u3000\u98ce\u96e8\u6df1\u5bb5\u53e4\u5e99",content:(0,a.jsx)(a.Fragment,{children:"\u8fd9\u4e00\u65e5\u5230\u4e86\u6e58\u6f6d\u4ee5\u5317\u7684\u6613\u5bb6\u6e7e\uff0c\u79bb\u7701\u57ce\u957f\u6c99\u5df2\u4e0d\u5728\u8fdc\uff0c\u8881\u7d2b\u8863\u6b63\u8981\u627e\u996d\u5e97\u6253\u5c16\uff0c\u53ea\u542c\u5f97\u7801\u5934\u65c1\u4eba\u58f0\u55a7\u54d7\u3002\u4f46\u89c1\u6e58\u6c5f\u4e2d\u505c\u6cca\u7740\u4e00\u8258\u5927\u8239\uff0c\u8239\u5934\u7ad9\u7740\u4e00\u4e2a\u8001\u8005\uff0c\u62f1\u624b\u4e0e\u7801\u5934\u4e0a\u9001\u884c\u7684\u8bf8\u4eba\u4e3a\u793c\u3002\u5979\u4e00\u77a5\u4e4b\u4e0b\uff0c\u89c1\u9001\u884c\u7684\u5927\u90fd\u662f\u6b66\u6797\u4e2d\u4eba\uff0c\u4e2a\u4e2a\u8170\u633a\u80cc\u76f4\uff0c\u7cbe\u795e\u5955\u5955\uff0c\u8001\u8005\u8eab\u540e\u7ad9\u7740\u4e24\u540d\u671d\u5ef7\u7684\u6b66\u5b98\u3002 \u3000\u3000\u5979\u89c1\u4e86\u8fd9\u4e00\u526f\u52bf\u6d3e\uff0c\u5fc3\u4e2d\u4e00\u52a8\uff1a\u201c\u83ab\u975e\u53c8\u662f\u54ea\u4e00\u6d3e\u7684\u638c\u95e8\u4eba\uff0c\u5230\u5317\u4eac\u53bb\u53c2\u4e0e\u798f\u5927\u5e05\u7684\u5927\u4f1a\uff1f\u201d\u51dd\u795e\u77a7\u90a3\u8001\u8005\u65f6\uff0c\u89c1\u4ed6\u4e24\u9b13\u82cd\u82cd\uff0c\u9894\u4e0b\u8001\u5927\u4e00\u90e8\u82b1\u767d\u80e1\u5b50\uff0c\u4f46\u6ee1\u8138\u7ea2\u5149\uff0c\u8863\u9970\u534e\u8d35\uff0c\u5de6\u624b\u624b\u6307\u4e0a\u6234\u7740\u4e00\u53ea\u78a7\u7389\u73ed\u6307\uff0c\u8fdc\u8fdc\u671b\u53bb\uff0c\u5728\u9633\u5149\u4e0b\u53d1\u51fa\u6676\u83b9\u4e4b\u8272\uff0c\u53ea\u542c\u4ed6\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u5404\u4f4d\u8d24\u5f1f\u8bf7\u56de\u5427\uff01\u201d\u62b1\u62f3\u4e00\u62f1\uff0c\u8eab\u5f62\u7aef\u51dd\uff0c\u5f53\u771f\u662f\u7a33\u82e5\u6cf0\u5c71\u3002 \u3000\u3000\u5cb8\u4e0a\u8bf8\u4eba\u9f50\u58f0\u8bf4\u9053\uff1a\u201c\u606d\u795d\u8001\u5e08\u4e00\u8def\u987a\u98ce\uff0c\u4e3a\u6211\u4e5d\u9f99\u6d3e\u626c\u5a01\u4eac\u5e08\u3002\u201d\u90a3\u8001\u8005\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u626c\u5a01\u4eac\u5e08\u662f\u5f53\u4e0d\u8d77\u7684\uff0c\u53ea\u76fc\u4e5d\u9f99\u6d3e\u7684\u540d\u5934\u4e0d\u5728\u6211\u624b\u91cc\u7838\u4e86\uff0c\u4e5f\u5c31\u662f\u5566\u3002\u201d\u8881\u7d2b\u8863\u542c\u4ed6\u58f0\u97f3\u6d2a\u4eae\uff0c\u4e2d\u6c14\u5145\u6c9b\uff0c\u8fd9\u51e0\u53e5\u8bdd\u4f3c\u662f\u8c26\u900a\uff0c\u4f46\u8bed\u6c14\u95f4\u5176\u5b9e\u751a\u662f\u81ea\u8d1f\u3002 \u3000\u3000\u53ea\u542c\u5f97\u5288\u62cd\u58f0\u54cd\uff0c\u9707\u8033\u6b32\u804b\uff0c\u6e58\u6c5f\u4e2d\u7ea2\u8272\u7eb8\u5c51\u98de\u821e\uff0c\u539f\u6765\u5cb8\u4e0a\u8239\u4e2d\u4e00\u9f50\u653e\u8d77\u97ad\u70ae\u3002 \u3000\u3000\u8881\u7d2b\u8863\u77e5\u9053\u97ad\u70ae\u4e00\u5b8c\uff0c\u5927\u8239\u4fbf\u8981\u5f00\u884c\uff0c\u4e8e\u662f\u8f7b\u8f7b\u8dc3\u4e0b\u9a6c\u6765\uff0c\u62ac\u8d77\u4e24\u7247\u77f3\u5b50\uff0c\u5f80\u97ad\u70ae\u4e0a\u63b7\u53bb\u3002\u4e24\u4e32\u97ad\u70ae\u90fd\u662f\u957f\u903e\u4e24\u4e08\uff0c\u77f3\u7247\u63b7\u5230\uff0c\u767b\u65f6\u4ece\u4e2d\u65ad\u7edd\uff0c\u55e4\u55e4\u58f0\u54cd\uff0c\u71c3\u7740\u7684\u97ad\u70ae\u5815\u5165\u6e58\u6c5f\uff0c\u7acb\u65f6\u7184\u706d\u4e86\u3002 \u3000\u3000\u8fd9\u4e00\u6765\uff0c\u5cb8\u4e0a\u8239\u4e2d\uff0c\u4eba\u4eba\u8038\u52a8\u3002\u97ad\u70ae\u65ad\u706d\uff0c\u90a3\u662f\u6700\u5927\u7684\u4e0d\u7965\u4e4b\u5146\u3002\u4f17\u4eba\u77a7\u5f97\u6e05\u695a\uff0c\u97ad\u70ae\u662f\u8fd9\u9ec4\u886b\u5c11\u5973\u7528\u77f3\u7247\u6253\u65ad\u3002\u516d\u4e03\u540d\u5927\u6c49\u7acb\u5373\u5954\u8fd1\u8eab\u53bb\uff0c\u5c06\u5979\u56e2\u56e2\u56f4\u4f4f\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u8c01\u6d3e\u4f60\u6765\u6363\u4e71\u6df7\u95f9\uff1f\u201d\u201c\u6253\u65ad\u97ad\u70ae\uff0c\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d\u201c\u5f53\u771f\u662f\u5403\u4e86\u8c79\u5b50\u80c6\u3001\u8001\u864e\u5fc3\uff0c\u7adf\u6562\u6765\u60f9\u4e5d\u9f99\u6d3e\u7684\u6613\u8001\u5e08\u3002\u201d\u82e5\u975e\u89c1\u5979\u53ea\u662f\u5b64\u8eab\u7684\u7f8e\u8c8c\u5c11\u5973\uff0c\u65e9\u5c31\u8001\u62f3\u9f50\u6325\uff0c\u4e00\u62e5\u800c\u4e0a\u4e86\u3002 \u3000\u3000\u8881\u7d2b\u8863\u6df1\u77e5\u97e6\u9640\u95e8\u4e0e\u516b\u4ed9\u5251\u7684\u6b66\u529f\u5e95\u7ec6\uff0c\u51fa\u624b\u65f6\u6210\u7af9\u5728\u80f8\uff0c\u5e76\u4e0d\u754f\u60e7\uff0c\u8fd9\u4e5d\u9f99\u6d3e\u5374\u4e0d\u77e5\u662f\u4ec0\u4e48\u6765\u5386\uff0c\u773c\u89c1\u4f17\u4eba\u58f0\u52bf\u6c79\u6c79\uff0c\u53ea\u5f97\u5fae\u7b11\u9053\uff1a\u201c\u6211\u7528\u77f3\u5b50\u6253\u6c34\u4e0a\u7684\u96c0\u513f\uff0c\u4e0d\u6599\u5931\u624b\u6253\u65ad\u4e86\u70ae\u4ed7\uff0c\u5b9e\u5728\u8fc7\u610f\u4e0d\u53bb\u3002\u201d \u3000\u3000\u4f17\u4eba\u542c\u5979\u8bed\u58f0\u6e05\u8106\uff0c\u4e00\u53e3\u5916\u8def\u53e3\u97f3\uff0c\u5927\u5bb6\u53c8\u4e03\u5634\u516b\u820c\u5730\u9053\uff1a\u201c\u5931\u624b\u6253\u65ad\u4e00\u4e32\uff0c\u4e5f\u8fd8\u7f62\u4e86\uff0c\u5c82\u6709\u4e24\u4e32\u4e00\u9f50\u6253\u65ad\u4e4b\u7406\uff1f\u201d \u3000\u3000\u201c\u4f60\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u201d\u201c\u5230\u6613\u5bb6\u6e7e\u6765\u5e72\u4e48\uff1f\u201d\u201c\u4eca\u65e5\u662f\u9ec4\u9053\u5409\u65e5\uff0c\u7ed9\u4f60\u8fd9\u4e48\u4e00\u6df7\u95f9\uff0c\u5509\uff0c\u6613\u8001\u5e08\u53ef\u6709\u591a\u4e0d\u75db\u5feb\uff01\u201d \u3000\u3000\u8881\u7d2b\u8863\u7b11\u9053\uff1a\u201c\u4e24\u4e32\u70ae\u4ed7\u6709\u4ec0\u4e48\u7a00\u7f55\uff1f\u518d\u53bb\u4e70\u8fc7\u4e24\u4e32\u6765\u653e\u653e\u4e5f\u5c31\u662f\u4e86\u3002\u201d\u8bf4\u7740\u4ece\u6000\u4e2d\u53d6\u51fa\u4e00\u952d\u9ec4\u91d1\uff0c\u7ea6\u83ab\u6709\u4e8c\u4e24\u6765\u91cd\uff0c\u6258\u5728\u638c\u4e2d\uff0c\u8fd9\u952d\u91d1\u5b50\u4fbf\u662f\u4e70\u4e00\u5343\u4e32\u97ad\u70ae\u4e5f\u5df2\u8db3\u591f\u3002\u4f17\u4eba\u9762\u9762\u76f8\u89d1\uff0c\u5747\u89c9\u8fd9\u5c11\u5973\u5341\u5206\u53e4\u602a\uff0c\u65e0\u4eba\u4f38\u624b\u6765\u63a5\u3002 \u3000\u3000\u8881\u7d2b\u8863\u7b11\u9053\uff1a\u201c\u5404\u4f4d\u90fd\u662f\u4e5d\u9f99\u6d3e\u7684\u5f1f\u5b50\u5417\uff1f\u8fd9\u4f4d\u6613\u8001\u5e08\u662f\u8d35\u6d3e\u7684\u638c\u95e8\u4eba\uff0c\u662f\u4e0d\u662f\uff1f\u4ed6\u8981\u5230\u5317\u4eac\u53bb\u53c2\u4e0e\u798f\u5927\u5e05\u7684\u5929\u4e0b\u638c\u95e8\u4eba\u5927\u4f1a\uff0c\u662f\u4e0d\u662f\uff1f\u201d\u5979\u95ee\u4e00\u53e5\uff0c\u4f17\u4eba\u4fbf\u70b9\u4e00\u70b9\u5934\u3002\u8881\u7d2b\u8863\u6447\u5934\u9053\uff1a\u201c\u70ae\u4ed7\u7184\u706d\uff0c\u90a3\u662f\u5927\u5927\u7684\u4e0d\u7965\u3002\u6613\u8001\u5e08\u8fd8\u662f\u8d81\u65e9\u522b\u53bb\uff0c\u5728\u5bb6\u5b89\u5c45\u7eb3\u798f\u7684\u597d\u3002\u201d \u3000\u3000\u4eba\u7fa4\u4e2d\u4e00\u4e2a\u6c49\u5b50\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u8881\u7d2b\u8863\u795e\u8272\u90d1\u91cd\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u77a7\u6613\u8001\u5e08\u6c14\u8272\u4e0d\u6b63\uff0c\u5370\u5802\u4e0a\u6df1\u900f\u9ed1\u96fe\uff0c\u6740\u7eb9\u76f4\u51b2\u7709\u68a2\u3002\u82e5\u662f\u5230\u4e86\u4eac\u5e08\uff0c\u4e0d\u4f46\u4e5d\u9f99\u6d3e\u5a01\u540d\u5815\u5730\uff0c\u6613\u8001\u5e08\u8fd8\u6709\u6740\u8eab\u4e4b\u7978\u3002\u201d\u4f17\u4eba\u4e00\u542c\uff0c\u4e0d\u7531\u5f97\u76f8\u987e\u53d8\u8272\u3002\u6709\u7684\u5728\u5730\u4e0a\u76f4\u5410\u53e3\u6c34\uff0c\u6709\u7684\u9ad8\u58f0\u6012\u9a82\uff0c\u4e5f\u6709\u7684\u7a83\u7a83\u79c1\u8bae\uff0c\u53ea\u6015\u8fd9\u5973\u5b50\u4f1a\u770b\u76f8\uff0c\u8fd9\u8bdd\u8bf4\u4e0d\u5b9a\u8fd8\u771f\u6709\u51e0\u5206\u9053\u7406\u3002 \u3000\u3000\u4f17\u4eba\u7ad9\u7acb\u4e4b\u5904\u4e0e\u5927\u8239\u8239\u5934\u76f8\u53bb\u4e0d\u8fdc\uff0c\u5979\u53c8\u8bed\u97f3\u6e05\u4eae\uff0c\u6bcf\u4e00\u53e5\u8bdd\u90fd\u4f20\u5165\u4e86\u90a3\u6613\u8001\u5e08\u8033\u4e2d\u3002\u4ed6\u7ec6\u7ec6\u6253\u91cf\u8881\u7d2b\u8863\uff0c\u89c1\u5979\u8eab\u6750\u82d7\u6761\uff0c\u4f53\u6001\u5a40\u5a1c\uff0c\u4f3c\u4e4e\u5e76\u4e0d\u4f1a\u6b66\uff0c\u4f46\u9002\u624d\u7528\u77f3\u7247\u6253\u65ad\u97ad\u70ae\uff0c\u51fa\u624b\u5de7\u5999\uff0c\u52b2\u9053\u4e0d\u5f31\uff0c\u53c8\u89c1\u5979\u6240\u4e58\u767d\u9a6c\u795e\u9a8f\u82f1\u4f1f\uff0c\u5b9e\u975e\u5e38\u7269\uff0c\u6599\u60f3\u6b64\u4eba\u5b9a\u662f\u6709\u6240\u4e3a\u800c\u6765\uff0c\u4e8e\u662f\u62f1\u624b\u8bf4\u9053\uff1a\u201c\u59d1\u5a18\u8d35\u59d3\uff0c\u8bf7\u501f\u4e00\u6b65\u4e0a\u8239\u8bf4\u8bdd\u3002\u201d\u8881\u7d2b\u8863\u9053\uff1a\u201c\u6211\u59d3\u8881\uff0c\u8fd8\u662f\u6613\u8001\u5e08\u4e0a\u5cb8\u6765\u5427\u3002\u201d \u3000\u3000\u5f53\u65f6\u6e58\u4eba\u98ce\u4fd7\uff0c\u4e58\u8239\u8fdc\u884c\uff0c\u767b\u8239\u4e4b\u540e\uff0c\u8239\u672a\u5f00\u884c\u800c\u518d\u56de\u5934\u4e0a\u5cb8\uff0c\u4e8e\u6b64\u884c\u6781\u4e3a\u4e0d\u5229\u3002\u90a3\u6613\u8001\u5e08\u7709\u5934\u5fae\u76b1\uff0c\u6c89\u541f\u4e0d\u8bed\u3002\u4ed6\u867d\u6b66\u529f\u6df1\u539a\uff0c\u505a\u5230\u4e00\u6d3e\u638c\u95e8\uff0c\u4f46\u751f\u5e73\u5bf9\u661f\u76f8\u535c\u5360\u3001\u98ce\u6c34\u582a\u8206\u7b49\u8bf4\u6781\u662f\u5d07\u4fe1\uff0c\u773c\u89c1\u70ae\u4ed7\u4e3a\u8fd9\u5e74\u8f7b\u5973\u5b50\u6253\u706d\uff0c\u53c8\u8bf4\u4ec0\u4e48\u6740\u8eab\u4e4b\u7978\u7b49\u7b49\u4e0d\u7965\u8a00\u8bed\uff0c\u5fc3\u60f3\u5979\u8d8a\u8bf4\u8d8a\u662f\u96be\u542c\uff0c\u8fd8\u4e0d\u5982\u7f6e\u4e4b\u4e0d\u7406\uff0c\u4e8e\u662f\u5bf9\u8239\u5bb6\u8bf4\u9053\uff1a\u201c\u5f00\u8239\u5427\uff01\u201d\u5583\u5583\u81ea\u8bed\uff1a\u201c\u9634\u4eba\u4e0d\u7965\uff0c\u5f85\u5230\u4e86\u7701\u57ce\uff0c\u54b1\u4eec\u518d\u4e70\u798f\u7269\uff0c\u8bf7\u795e\u51b2\u71ac\u3002\u201d\u8239\u5bb6\u9ad8\u58f0\u7b54\u5e94\uff0c\u6709\u7684\u62c9\u8d77\u94c1\u951a\uff0c\u6709\u7684\u4fbf\u62d4\u7bd9\u5b50\u3002 \u3000\u3000\u8881\u7d2b\u8863\u89c1\u4ed6\u4e0d\u7406\u81ea\u5df1\uff0c\u7adf\u8981\u5f00\u8239\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u6162\u6765\u6162\u6765\uff01 \u3000\u3000\u4f60\u82e5\u4e0d\u542c\u6211\u529d\u544a\uff0c\u4e0d\u51fa\u767e\u91cc\u4fbf\u8981\u6845\u65ad\u821f\u8986\uff0c\u5168\u8239\u4eba\u7b49\u5c3d\u6570\u6b7b\u4e8e\u975e\u547d\u3002\u201d\u6613\u8001\u5e08\u8138\u8272\u66f4\u662f\u9634\u6c89\uff0c\u5389\u58f0\u9053\uff1a\u201c\u6211\u77a7\u4f60\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u4e0d\u6765\u8ddf\u4f60\u4e00\u822c\u89c1\u8bc6\u3002\u82e5\u518d\u80e1\u8bf4\u516b\u9053\uff0c\u53ef\u83ab\u602a\u6211\u4e0d\u518d\u5bb9\u60c5\u3002\u201d \u3000\u3000\u8881\u7d2b\u8863\u4e00\u8dc3\u4e0a\u8239\uff0c\u5fae\u7b11\u9053\uff1a\u201c\u6211\u5168\u662f\u4e00\u7247\u597d\u610f\uff0c\u6613\u8001\u5e08\u4f55\u5fc5\u52a8\u6012\uff1f\u8bf7\u95ee\u6613\u8001\u5e08\u5927\u540d\u5982\u4f55\u79f0\u547c\uff0c\u6211\u518d\u8ddf\u4f60\u62c6\u4e00\u4e2a\u5b57\uff0c\u5bf9\u4f60\u5927\u6709\u597d\u5904\u3002\u201d\u6613\u8001\u5e08\u54fc\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4e0d\u987b\u4e86\uff01\u201d\u8881\u7d2b\u8863\u9053\uff1a\u201c\u597d\uff0c\u6613\u8001\u5e08\u65e2\u4e0d\u80af\u4ee5\u5c0a\u53f7\u76f8\u793a\uff0c\u6211\u4fbf\u62c6\u4e00\u62c6\u4f60\u8fd9\u4e2a\u59d3\u3002 \u3000\u3000\u2018\u6613\u2019\u5b57\u4e0a\u9762\u662f\u4e2a\u2018\u65e5\u2019\u5b57\uff0c\u4e0b\u9762\u662f\u4e2a\u2018\u52ff\u2019\u5b57\uff0c\u2018\u52ff\u65e5\u2019\u4fbf\u662f\u2018\u4e0d\u65e5\u2019\uff0c\u610f\u601d\u662f\u547d\u4e0d\u4e45\u77e3\u3002\u6613\u8001\u5e08\u6b64\u884c\u4e58\u8239\uff0c\u8d70\u7684\u662f\u6c34\u8def\uff0c\u2018\u6613\u2019\u5b57\u52a0\u2018\u4e00\u2019\u52a0\u2018\u6c34\u2019\uff0c\u4fbf\u6210\u4e3a\u2018\u6c64\u2019\uff0c\u2018\u8d74\u6c64\u2019\u8e48\u706b\uff0c\u6b64\u884c\u5927\u4e3a\u51f6\u9669\u3002\u821f\u4e3a\u5668\u76bf\u4e4b\u8c61\uff0c\u2018\u6c64\u2019\u4e0b\u52a0\u2018\u76bf\u2019\u4e3a\u2018\u76ea\u2019\uff0c\u6240\u8c13\u2018\u76ea\u7136\u65e0\u5b58\u2019\uff0c\u5168\u8239\u4eba\u7b49\uff0c\u6027\u547d\u96be\u4fdd\u3002\u2018\u6c64\u2019\u5b57\u4e4b\u4e0a\u52a0\u2018\u8349\u2019\u4e3a\u2018\u8361\u2019\uff0c\u53e4\u8bd7\u4e91\uff1a\u2018\u8361\u5b50\u884c\u4e0d\u5f52\u2019\uff0c\u6613\u8001\u5e08\u8fd9\u4e00\u6b21\u53ea\u6015\u8981\u6b7b\u4e8e\u5f02\u4e61\u5ba2\u5730\u4e86\u3002\u201d \u3000\u3000\u6613\u8001\u5e08\u542c\u5230\u6b64\u5904\uff0c\u518d\u4e5f\u5fcd\u8010\u4e0d\u4f4f\uff0c\u4f38\u624b\u5728\u6845\u6746\u4e0a\u7528\u529b\u4e00\u62cd\uff0c\u7830\u7684\u4e00\u58f0\uff0c\u4e00\u6761\u7c97\u5927\u7684\u6845\u6746\u4e0d\u4f4f\u6447\u6643\uff0c\u559d\u9053\uff1a\u201c\u4f60\u6709\u5b8c\u6ca1\u5b8c\uff1f\u201d \u3000\u3000\u8881\u7d2b\u8863\u7b11\u9053\uff1a\u201c\u6613\u8001\u5e08\u6b64\u884c\uff0c\u767e\u4e8b\u987b\u6c42\u5409\u5229\uff0c\u90a3\u4e2a\u2018\u5b8c\u2019\u5b57\uff0c\u662f\u4e07\u4e07\u8bf4\u4e0d\u5f97\u7684\u3002\u6613\u8001\u5e08\uff0c\u4f60\u5230\u5317\u4eac\u662f\u53bb\u4e89\u96c4\u56fe\u9738\uff0c\u4e0d\u662f\u52a8\u62f3\u811a\uff0c\u4fbf\u8981\u52a8\u5200\u67aa\u3002\u2018\u6613\u2019\u5b57\u52a0\u2018\u8db3\u2019\u4e3a\u2018\u8e22\u2019\uff0c\u52a0\u2018\u5200\u2019\u4e3a\u2018\u5254\u2019\uff0c\u56e0\u6b64\u4f60\u4e0d\u4f46\u81ea\u5df1\u7ed9\u4eba\u8e22\u6b7b\uff0c\u4e5d\u9f99\u6d3e\u8fd8\u7ed9\u4eba\u5254\u9664\u3002\u201d \u3000\u3000\u6613\u8001\u5e08\u8d8a\u542c\u8d8a\u6012\uff0c\u4f46\u542c\u5979\u8bf4\u5f97\u5934\u5934\u662f\u9053\uff0c\u4e5f\u4e0d\u7531\u5f97\u6697\u81ea\u5fc3\u60ca\uff0c\u5f3a\u8a00\u9053\uff1a\u201c\u6211\u5355\u540d\u4e00\u4e2a\u2018\u5409\u2019\u5b57\uff0c\u65e9\u4fbf\u5409\u7965\u5409\u5229\u4e86\uff0c\u4f60\u8fd8\u6709\u4f55\u8bdd\u8bf4\uff1f\u201d\u8881\u7d2b\u8863\u6447\u5934\u9053\uff1a\u201c\u5927\u51f6\u5927\u9669\u3002\u8fd9\u4e2a\u2018\u5409\u2019\u5b57\u672c\u6765\u751a\u597d\uff0c\u4f46\u504f\u504f\u5bf9\u6613\u8001\u5e08\u751a\u4e3a\u4e0d\u7965\u3002\u2018\u6613\u2019\u8005\uff0c\u6362\u4e5f\uff0c\u5c06\u5409\u7965\u66f4\u6362\u4e86\u53bb\uff0c\u90a3\u662f\u4ec0\u4e48\uff1f\u81ea\u7136\u662f\u4e0d\u5409\u4e86\u3002\u201d\u6613\u5409\u9ed8\u7136\u3002 \u3000\u3000\u8881\u7d2b\u8863\u53c8\u9053\uff1a\u201c\u8fd9\u2018\u5409\u2019\u5b57\u62c6\u5c06\u5f00\u6765\uff0c\u662f\u2018\u5341\u4e00\u53e3\u2019\u4e09\u5b57\u3002 \u3000\u3000\u6613\u8001\u5e08\u554a\uff0c\u51e1\u4eba\u53ea\u6709\u4e00\u53e3\uff0c\u4f60\u5374\u6709\u5341\u4e00\u53e3\u3002\u591a\u51fa\u6765\u7684\u5341\u53e3\u662f\u4ec0\u4e48\u53e3\uff1f\u90a3\u81ea\u7136\u662f\u4f24\u53e3\uff0c\u662f\u5200\u53e3\u4e86\u3002\u7531\u6b64\u89c2\u4e4b\uff0c\u4f60\u6b64\u756a\u4e0a\u5317\u4eac\u53bb\uff0c\u547d\u4e2d\u6ce8\u5b9a\u8981\u8eab\u4e2d\u5341\u5200\uff0c\u5c38\u9aa8\u4e0d\u5f52\u6545\u4e61\u3002\u201d \u3000\u3000\u8d8a\u662f\u8ff7\u4fe1\u4e4b\u4eba\uff0c\u8d8a\u662f\u542c\u4e0d\u5f97\u4e0d\u7965\u4e4b\u8a00\u3002\u6613\u5409\u672c\u6765\u96cd\u5bb9\u5bbd\u5b8f\uff0c\u9762\u56e2\u56e2\u7684\u4e00\u526f\u5bcc\u5bb6\u7fc1\u6c14\u8c61\uff0c\u6b64\u65f6\u7709\u95f4\u7a81\u73b0\u715e\u6c14\uff0c\u659c\u76ee\u6a2a\u7768\u8881\u7d2b\u8863\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u597d\uff0c\u8881\u59d1\u5a18\uff0c\u591a\u8c22\u91d1\u7389\u826f\u8a00\u3002\u4f60\u662f\u54ea\u4e00\u4f4d\u8001\u5e08\u95e8\u4e0b\uff1f\u4ee4\u5c0a\u662f\u8c01\uff1f\u201d \u3000\u3000\u8881\u7d2b\u8863\u7b11\u9053\uff1a\u201c\u4f60\u4e5f\u8981\u7ed9\u6211\u7b97\u547d\u62c6\u5b57\u4e48\uff1f\u4f55\u5fc5\u8981\u67e5\u6211\u7684\u5e08\u627f\u6765\u5386\uff1f\u201d\u6613\u5409\u51b7\u7b11\u9053\uff1a\u201c\u77a7\u4f60\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u54b1\u4eec\u53c8\u7d20\u4e0d\u76f8\u8bc6\uff0c\u4f60\u5b9a\u662f\u53d7\u4eba\u6307\u4f7f\uff0c\u6765\u8e22\u6613\u67d0\u7684\u76d8\u5b50\u6765\u7740\u3002\u59d3\u6613\u7684\u5927\u4e0d\u4e0e\u5c0f\u6597\uff0c\u7537\u4e0d\u4e0e\u5973\u4e89\uff0c\u4f60\u53eb\u4f60\u80cc\u540e\u90a3\u4eba\u51fa\u6765\uff0c\u77a7\u77a7\u5230\u5e95\u662f\u8c01\u8eab\u4e2d\u5341\u5200\uff0c\u5c38\u9aa8\u4e0d\u5f52\u6545\u4e61\u3002\u201d\u4ed6\u4f38\u624b\u6307\u7740\u5979\u8138\uff0c\u5927\u58f0\u9053\uff1a\u201c\u4f60\u80cc\u540e\u90a3\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u8881\u7d2b\u8863\u7b11\u9053\uff1a\u201c\u6211\u80cc\u540e\u7684\u4eba\u4e48\uff1f\u201d\u5047\u88c5\u56de\u5934\u4e00\u770b\uff0c\u4e0d\u7531\u5f97\u4e00\u60ca\uff0c\u53ea\u89c1\u5cb8\u8fb9\u7ad9\u7740\u4e00\u4eba\uff0c\u7a7f\u4e00\u8eab\u7c97\u5e03\u9752\u8863\uff0c\u6253\u626e\u4f5c\u4e61\u519c\u6a21\u6837\uff0c\u6b63\u662f\u80e1\u6590\uff0c\u5fc3\u60f3\u4e0d\u77e5\u4ed6\u4f55\u65f6\u5230\u4e86\u6b64\u5904\uff0c\u81ea\u5df1\u5168\u795e\u8d2f\u6ce8\u7684\u7ed9\u6613\u5409\u62c6\u5b57\uff0c\u7adf\u6ca1\u5bdf\u89c9\u3002\u5979\u4e0d\u52a8\u58f0\u8272\uff0c\u56de\u8fc7\u5934\u6765\uff0c\u7b11\u9053\uff1a\u201c\u6211\u80cc\u540e\u8fd9\u4eba\u4e48\uff1f\u6211\u77a7\u4ed6\u662f\u4e2a\u770b\u725b\u6311\u7caa\u7684\u4e61\u4e0b\u5c0f\u5b50\u3002\u201d \u3000\u3000\u6613\u5409\u6012\u9053\uff1a\u201c\u4f60\u83ab\u88c5\u80e1\u7f8a\u3002\u6211\u8bf4\u7684\u662f\u5728\u80cc\u540e\u7ed9\u4f60\u6491\u8170\u3001\u53eb\u4f60\u6765\u6363\u9b3c\u7684\u90a3\u4eba\uff0c\u662f\u7537\u5b50\u6c49\u5927\u4e08\u592b\uff0c\u4f55\u5fc5\u85cf\u5934\u9732\u5c3e\uff0c\u9b3c\u9b3c\u795f\u795f\uff1f\u201d\u4ed6\u6599\u5b9a\u662f\u4ec7\u5bb6\u6697\u4e2d\u6307\u4f7f\u8881\u7d2b\u8863\u524d\u6765\u6df7\u95f9\uff0c\u597d\u4f7f\u81ea\u5df1\u51fa\u884c\u4e0d\u5229\uff0c\u6b64\u4eba\u5fc5\u7136\u719f\u77e5\u81ea\u5df1\u7684\u6027\u60c5\u5fcc\u8bb3\uff0c\u5426\u5219\u5979\u4f55\u4ee5\u5c3d\u8bf4\u4e0d\u5409\u4e4b\u8a00\uff1f \u3000\u3000\u5176\u5b9e\u8881\u7d2b\u8863\u5b58\u5fc3\u6363\u4e71\uff0c\u89c1\u4ed6\u8d8a\u662f\u6015\u542c\u4e0d\u5409\u5229\u7684\u8bf4\u8bdd\uff0c\u4fbf\u8d8a\u662f\u5c3d\u62e3\u51f6\u9669\u707e\u7978\u6765\u8bf4\uff0c\u5f53\u4e0b\u6b63\u8272\u9053\uff1a\u201c\u6613\u8001\u5e08\uff0c\u5e38\u8a00\u9053\u826f\u836f\u82e6\u53e3\u5229\u4e8e\u75c5\uff0c\u5fe0\u8a00\u9006\u8033\u5229\u4e8e\u884c\u3002\u6211\u8fd9\u756a\u9006\u8033\u5fe0\u8a00\uff0c\u542c\u4e0d\u542c\u4e5f\u7531\u5f97\u4f60\u3002\u81f3\u4e8e\u4e5d\u9f99\u6d3e\u561b\uff0c\u4f60\u82e5\u4e0d\u53bb\uff0c\u7531\u5c0f\u5973\u5b50\u4ee3\u4f60\u53bb\u4fbf\u4e86\u3002\u201d \u3000\u3000\u5f53\u8881\u7d2b\u8863\u8dc3\u4e0a\u8239\u5934\u4e0d\u4e45\uff0c\u80e1\u6590\u5373\u5df2\u8ddf\u8e2a\u800c\u81f3\u3002\u90a3\u65e5\u4ed6\u5728\u6cb3\u91cc\u6d17\u6fa1\u65f6\u8863\u670d\u88ab\u593a\uff0c\u8d64\u8eab\u9732\u4f53\u7684\u4e0d\u4fbf\u51fa\u6765\uff0c\u597d\u5728\u4e3a\u65f6\u5df2\u665a\uff0c\u4e0d\u4e45\u5929\u4fbf\u9ed1\u4e86\uff0c\u8fd9\u624d\u5230\u4e61\u519c\u5bb6\u53bb\u5077\u4e86\u4e00\u8eab\u8863\u670d\u3002\u4ed6\u6700\u5173\u6000\u7684\u662f\u90a3\u672c\u5bb6\u4f20\u62f3\u7ecf\u5200\u8c31\u3002\u8fd9\u5200\u8c31\u653e\u5728\u8d34\u8089\u8863\u670d\u888b\u4e2d\uff0c\u7adf\u7ed9\u5979\u8fde\u8863\u5e26\u4e66\uff0c\u4e00\u8d77\u53d6\u4e86\u53bb\uff0c\u5fc3\u60f3\u8fd9\u5973\u5b50\u5148\u5077\u6211\u5305\u88b1\uff0c\u53c8\u53d6\u6211\u8863\u670d\uff0c\u5b9a\u662f\u4e3a\u4e86\u8fd9\u672c\u5200\u8c31\uff0c\u5fc3\u4e2d\u5341\u5206\u5fe7\u6025\uff0c\u4e00\u8def\u75be\u8d76\u3002\u5f53\u65e5\u4fbf\u8ffd\u4e0a\u4e86\u5979\uff0c\u4f46\u89c1\u5979\u52d2\u9a6c\u7f13\u7f13\u800c\u884c\uff0c\u5374\u53c8\u4e0d\u662f\u5077\u4e86\u5200\u8c31\u4fbf\u5373\u8fdc\u8d70\u9ad8\u98de\u7684\u6a21\u6837\u3002\u4ed6\u8d8a\u60f3\u8d8a\u7591\uff0c\u65e0\u6cd5\u63a8\u6d4b\u8fd9\u5973\u5b50\u771f\u610f\u4f55\u5728\uff0c\u5fc3\u60f3\u82e5\u662f\u52a8\u624b\u5f3a\u62a2\uff0c\u672a\u5fc5\u80fd\u591f\u5f97\u624b\uff0c\u4e8e\u662f\u6697\u6697\u5728\u540e\u7aa5\u4f3a\uff0c\u8981\u77a7\u5979\u6709\u4f55\u52a8\u9759\uff0c\u53e6\u6709\u4f55\u4eba\u63a5\u5e94\u3002\u4f46\u8ddf\u4e86\u6570\u65e5\uff0c\u59cb\u7ec8\u4e0d\u89c1\u6709\u4f55\u5f02\u72b6\u3002\u8fd9\u65e5\u5728\u6613\u5bb6\u6e7e\u6e58\u6c5f\u4e4b\u7554\uff0c\u5374\u89c1\u5979\u5411\u6613\u5409\u8d77\u8845\uff0c\u7adf\u662f\u53c8\u8981\u62a2\u593a\u638c\u95e8\u4eba\u7684\u6a21\u6837\u3002 \u3000\u3000\u80e1\u6590\u6697\u6697\u79f0\u5947\uff1a\u201c\u8fd9\u4f4d\u59d1\u5a18\u7adf\u662f\u6709\u4e00\u5473\u638c\u95e8\u4eba\u7656\u3002\u5979\u9047\u5230\u4e86\u638c\u95e8\u4eba\u4fbf\u62a2\uff0c\u4e3a\u7684\u662f\u5728\u6c5f\u6e56\u4e0a\u6811\u4fe1\u7acb\u5a01\u5462\uff0c\u8fd8\u662f\u53e6\u6709\u6df1\u610f\uff1f \u3000\u3000\u770b\u6765\u4e24\u4eba\u8bf4\u50f5\u4e86\u4fbf\u8981\u52a8\u624b\uff0c\u4e14\u8ba9\u4ed6\u4eec\u9e6c\u868c\u76f8\u4e89\uff0c\u6211\u4fbf\u6765\u4e2a\u6e14\u7fc1\u5f97\u5229\uff0c\u8bbe\u6cd5\u593a\u56de\u5200\u8c31\u3002\u6b64\u65f6\u7275\u5979\u767d\u9a6c\uff0c\u6613\u5982\u53cd\u638c\uff0c\u4f46\u597d\u66f2\u5b50\u4e0d\u5531\u7b2c\u4e8c\u904d\uff0c\u91cd\u65bd\u6545\u6280\uff0c\u672a\u514d\u663e\u5f97\u6211\u5c0f\u6ce5\u9cc5\u80e1\u6590\u592a\u4e5f\u7b28\u86cb\u3002\u201d \u3000\u3000\u4e8e\u662f\u6162\u6162\u8d70\u8fd1\u8239\u5934\uff0c\u7b49\u5019\u673a\u4f1a\u62a2\u593a\u5979\u80cc\u4e0a\u5305\u88b1\u3002 \u3000\u3000\u53ea\u89c1\u6613\u5409\u4e00\u5f20\u7ea2\u5802\u5802\u7684\u8138\u819b\u7531\u7ea2\u8f6c\u7d2b\uff0c\u5636\u54d1\u7740\u55d3\u5b50\u8bf4\u9053\uff1a\u201c\u59d1\u5a18\u8fd9\u4e48\u8bf4\uff0c\u90a3\u662f\u9a82\u6613\u67d0\u65e0\u80fd\uff0c\u4e0d\u914d\u4f5c\u4e5d\u9f99\u6d3e\u7684\u638c\u95e8\u4eba\uff1f\u201d\u8881\u7d2b\u8863\u5fae\u7b11\u9053\uff1a\u201c\u90a3\u4e5f\u4e0d\u662f\u3002\u6613\u8001\u5e08\u65e2\u7136\u6b64\u884c\u4e0d\u5229\uff0c\u6027\u547d\u53ef\u4e0d\u662f\u95f9\u7740\u73a9\u7684\uff0c\u4e0d\u5982\u628a\u4e5d\u9f99\u6d3e\u7684\u638c\u95e8\u4eba\u8ba9\u4e0e\u6211\u5427\u3002\u5c0f\u5973\u5b50\u4e00\u7247\u597d\u5fc3\uff0c\u7eaf\u7cfb\u4e3a\u4f60\u7740\u60f3\u2026\u2026\u201d \u3000\u3000\u5979\u8bdd\u672a\u8bf4\u5b8c\uff0c\u7a81\u89c1\u8239\u8231\u4e2d\u94bb\u51fa\u4e24\u6761\u6c49\u5b50\uff0c\u624b\u4e2d\u5404\u6301\u4e00\u6761\u4e5d\u8282\u8f6f\u97ad\u3002\u4e00\u4e2a\u4e2d\u5e74\u5927\u6c49\u9053\uff1a\u201c\u8fd9\u5973\u5b50\u75af\u75af\u766b\u766b\uff0c\u5e08\u7236\u4e0d\u5fc5\u7406\u5979\u3002\u5f85\u5f1f\u5b50\u8d76\u5979\u4e0a\u5cb8\uff0c\u83ab\u8bef\u4e86\u5f00\u8239\u7684\u5409\u65f6\u3002\u201d\u8bf4\u7740\u5de6\u624b\u4f38\u51fa\uff0c\u4fbf\u53bb\u63a8\u8881\u7d2b\u8863\u7684\u80a9\u5934\u3002\u8881\u7d2b\u8863\u4f38\u6307\u5728\u4ed6\u624b\u81c2\u4e0a\u8f7b\u8f7b\u4e00\u5f39\uff0c\u8bf4\u9053\uff1a\u201c\u5409\u65f6\u65e9\u5df2\u8bef\u4e86\uff01\u201d\u90a3\u6c49\u5b50\u767b\u89c9\u81c2\u5f2f\u4e2d\u4e00\u9ebb\uff0c\u624b\u638c\u6ca1\u78b0\u5230\u5979\u80a9\u5934\uff0c\u4e0a\u81c2\u4fbf\u5df2\u8f6f\u8f6f\u7684\u5782\u4e86\u4e0b\u6765\u3002\u53e6\u4e00\u4e2a\u6c49\u5b50\u559d\u9053\uff1a\u201c\u5927\u5e08\u54e5\uff0c\u52a8\u5bb6\u4f19\u5427\uff01\u201d \u3000\u3000\u4e24\u4eba\u9f50\u58f0\u547c\u54e8\uff0c\u545b\u5577\u5577\u4e00\u9635\u54cd\u4eae\uff0c\u4e24\u6761\u4e5d\u8282\u8f6f\u97ad\u540c\u65f6\u5411\u8881\u7d2b\u8863\u819d\u5934\u6253\u53bb\u3002\u4ed6\u4eec\u4e0d\u60f3\u4f24\u5979\u6027\u547d\uff0c\u662f\u4ee5\u8f6f\u97ad\u6240\u6307\u4e4b\u5904\u5e76\u975e\u8981\u5bb3\u3002 \u3000\u3000\u8881\u7d2b\u8863\u89c1\u4e24\u4eba\u90fd\u4f7f\u4e5d\u8282\u97ad\uff0c\u5fc3\u5ff5\u4e00\u52a8\uff1a\u201c\u662f\u4e86\uff0c\u4ed6\u4eec\u53eb\u505a\u4e5d\u9f99\u6d3e\uff0c\u5927\u6982\u6700\u64c5\u957f\u7684\u4fbf\u662f\u4e5d\u8282\u97ad\u3002\u201d\u5979\u4e0e\u6613\u5409\u4e1c\u62c9\u897f\u626f\uff0c\u4e00\u6765\u662f\u8981\u4ed6\u5fc3\u70e6\u610f\u4e71\uff0c\u4e8c\u6765\u662f\u60f3\u63a2\u542c\u4ed6\u7684\u6b66\u529f\u5bb6\u6570\uff0c\u8fd9\u65f6\u89c1\u53cc\u97ad\u51fb\u5230\uff0c\u5fc3\u4e2d\u5927\u559c\uff1a\u201c\u597d\u554a\uff0c\u4f60\u4eec\u9047\u4e0a\u4f7f\u8f6f\u97ad\u7684\u8001\u7956\u5b97\u5566\u3002\u201d\u53cc\u624b\u4f38\u51fa\uff0c\u5feb\u901f\u65e0\u4f26\u7684\u6293\u4f4f\u4e24\u6839\u8f6f\u97ad\u97ad\u5934\uff0c\u76f8\u4e92\u4e00\u7f20\uff0c\u6253\u6210\u7ed3\u5f62\uff0c\u8eab\u5b50\u6beb\u4e0d\u79fb\u52a8\uff0c\u5fae\u7b11\u7740\u7ad9\u5728\u5f53\u5730\u3002 \u3000\u3000\u4e24\u540d\u6c49\u5b50\u5c1a\u672a\u5bdf\u89c9\uff0c\u89c1\u97ad\u5934\u5e76\u672a\u6253\u5230\u5979\u8eab\u4e0a\uff0c\u53cd\u800c\u53cc\u97ad\u4e92\u7f20\uff0c\u5404\u81ea\u7528\u529b\u4e00\u626f\uff0c\u8fd9\u4e00\u6765\u6b63\u4e2d\u4e86\u8881\u7d2b\u8863\u4e4b\u8ba1\uff0c\u53cc\u97ad\u97ad\u5934\u672c\u6765\u677e\u677e\u642d\u7740\uff0c\u4e00\u626f\u4e4b\u4e0b\uff0c\u767b\u6210\u6b7b\u7ed3\u3002\u4e24\u4eba\u60ca\u5f97\u5446\u4e86\uff0c\u53c8\u662f\u7528\u529b\u4e00\u626f\u3002\u5e08\u5144\u5f1f\u4fe9\u8182\u529b\u76f8\u5f53\uff0c\u8c01\u4e5f\u626f\u4e0d\u52a8\u8c01\uff0c\u4e24\u6761\u8f6f\u97ad\u5374\u7f20\u5f97\u66f4\u52a0\u7d27\u4e86\u3002 \u3000\u3000\u6613\u5409\u559d\u9053\uff1a\u201c\u83bd\u649e\u4e4b\u5f92\uff0c\u5feb\u9000\u5f00\u4e86\u3002\u201d\u53cc\u624b\u6293\u4f4f\u957f\u888d\u8863\u895f\uff0c\u5411\u5916\u4e00\u6296\uff0c\u5580\u5587\u5587\u4e00\u9635\u54cd\uff0c\u888d\u5b50\u4e0a\u4e03\u4e2a\u8f6f\u548c\u4e00\u9f50\u62c9\u8131\uff0c\u5de6\u624b\u53cd\u5230\u8eab\u540e\u4e00\u626f\uff0c\u957f\u888d\u767b\u65f6\u9664\u4e86\u4e0b\u6765\uff0c\u9732\u51fa\u888d\u5185\u7684\u52b2\u88c5\u7ed3\u675f\u3002\u8fd9\u4e00\u624b\u5e72\u51c0\u5229\u843d\uff0c\u5a01\u98ce\u5341\u8db3\u3002\u5cb8\u4e0a\u7ad9\u7740\u7684\u5927\u90fd\u662f\u4ed6\u7684\u5f1f\u5b50\u4eb2\u53cb\uff0c\u4e5f\u6709\u4e0d\u5c11\u95f2\u4eba\uff0c\u767b\u65f6\u9f50\u58f0\u559d\u4e86\u4e2a\u5927\u5f69\u3002 \u3000\u3000\u8881\u7d2b\u8863\u6447\u5934\u9053\uff1a\u201c\u53e3\u91c7\u4e0d\u597d\u3002\u8fd9\u4e00\u624b\u2018\u8131\u888d\u8ba9\u4f4d\u2019\uff0c\u8131\u888d\u4e0d\u6253\u7d27\uff0c\u8ba9\u4f4d\u561b\uff0c\u5374\u662f\u6ce8\u5b9a\u628a\u638c\u95e8\u4eba\u4e4b\u4f4d\u8ba9\u7ed9\u6211\u5566\u3002\u201d\u6613\u5409\u5fc3\u4e2d\u4e00\u51db\uff0c\u679c\u89c9\u8fd9\u4e00\u624b\u4e5f\u662f\u4e0d\u7965\u4e4b\u5146\uff0c\u53f3\u624b\u4f38\u5230\u8170\u95f4\uff0c\u8f7b\u8f7b\u4e00\u6296\uff0c\u624b\u4e2d\u5df2\u591a\u4e86\u4e00\u6761\u6676\u5149\u95ea\u4eae\u7684\u4e5d\u8282\u97ad\u3002 \u3000\u3000\u8fd9\u4e00\u6296\u5bc2\u7136\u65e0\u58f0\uff0c\u94a2\u97ad\u7684\u4e5d\u8282\u4e92\u76f8\u7adf\u65e0\u534a\u70b9\u78b0\u649e\u3002\u8881\u7d2b\u8863\u6697\u53eb\uff1a\u201c\u554a\u54df\uff0c\u4e0d\u597d\uff01\u8fd9\u624b\u529f\u592b\u6211\u53ef\u4e0d\u4f1a\uff0c\u4eca\u65e5\u53ea\u6015\u8981\u7cdf\uff01\u201d \u3000\u3000\u53ea\u89c1\u4ed6\u8fd9\u6761\u97ad\u5b50\u6bcf\u4e00\u8282\u5747\u6709\u9e21\u86cb\u7c97\u7ec6\uff0c\u4ed6\u8eab\u6750\u53c8\u6781\u9b41\u68a7\uff0c\u4fbf\u5982\u8239\u5934\u4e0a\u7acb\u4e86\u4e00\u5ea7\u94c1\u5854\uff0c\u62ff\u7740\u8fd9\u6761\u5927\u97ad\uff0c\u5f53\u771f\u662f\u5a01\u98ce\u51db\u51db\u3002 \u3000\u3000\u8fd9\u65f6\u8239\u5bb6\u5df2\u6536\u8d77\u4e86\u94c1\u951a\uff0c\u8239\u8eab\u5728\u6c5f\u4e2d\u6447\u6643\u4e0d\u5b9a\u3002\u6613\u5409\u624b\u81c2\u4e00\u6296\uff0c\u4e5d\u8282\u97ad\u98de\u51fa\u53bb\u5377\u4f4f\u4e86\u8239\u5934\u94c1\u951a\uff0c\u8ddf\u7740\u4e00\u6325\uff0c\u6251\u901a\u58f0\u54cd\uff0c\u6c34\u82b1\u56db\u6e85\uff0c\u94c1\u951a\u53c8\u5df2\u843d\u5165\u6c5f\u4e2d\uff0c\u8239\u8eab\u767b\u65f6\u7a33\u4f4f\u3002\u8fd9\u4e00\u624b\u82e5\u975e\u81c2\u4e0a\u6709\u516d\u4e03\u767e\u65a4\u8182\u529b\uff0c\u7109\u80fd\u5982\u6b64\u6325\u6d12\u81ea\u5982\uff1f\u773c\u89c1\u4ed6\u8fd9\u6761\u4e5d\u8282\u97ad\u5e76\u6709\u8f6f\u97ad\u4e0e\u94a2\u97ad\u4e4b\u957f\uff0c\u5185\u5916\u517c\u4fee\uff0c\u975e\u540c\u5c0f\u53ef\u3002 \u3000\u3000\u8881\u7d2b\u8863\u5fc3\u60f3\uff1a\u201c\u4ed6\u8182\u529b\u5f3a\u5927\uff0c\u6325\u97ad\u65e0\u58f0\u3002\u6b64\u4eba\u53ea\u53ef\u667a\u53d6\uff0c\u4e0d\u80fd\u529b\u654c\u3002\u201d\u89c1\u4ed6\u8eab\u6750\u9b41\u68a7\uff0c\u5e74\u7eaa\u53c8\u5927\uff0c\u60f3\u6765\u529f\u529b\u867d\u6df1\uff0c\u624b\u811a\u5c31\u672a\u5fc5\u7075\u4fbf\uff0c\u4e8e\u662f\u5fc3\u751f\u4e00\u8ba1\uff0c\u8bf4\u9053\uff1a\u201c\u6613\u8001\u5e08\uff0c\u6211\u662f\u5973\u5b50\uff0c\u5982\u5728\u8239\u5934\u8ddf\u4f60\u76f8\u6597\uff0c\u4e0d\u8bba\u80dc\u8d1f\uff0c\u90fd\u4e8e\u4f60\u6b64\u884c\u4e0d\u5229\u3002\u54b1\u4eec\u603b\u5f97\u53e6\u89c5\u4e00\u4e2a\u5730\u65b9\u8f83\u91cf\u624d\u662f\u3002\u201d\u6613\u5409\u5fc3\u89c9\u6b64\u8a00\u6709\u7406\uff0c\u53ef\u662f\u53c8\u4e0d\u613f\u4e0a\u5cb8\u3002 \u3000\u3000\u8881\u7d2b\u8863\u53c8\u9053\uff1a\u201c\u6613\u8001\u5e08\uff0c\u54b1\u4eec\u8bdd\u5f97\u8bf4\u5728\u524d\u5934\uff0c\u82e5\u662f\u6211\u80dc\u4e86\u4f60\uff0c\u4f60\u8fd9\u4e5d\u9f99\u6d3e\u638c\u95e8\u4eba\u4e4b\u4f4d\uff0c\u81ea\u5f97\u62f1\u624b\u76f8\u8ba9\uff0c\u4e0d\u77e5\u4f60\u95e8\u4e0b\u7684\u5f1f\u5b50\u4eec\u670d\u662f\u4e0d\u670d\uff1f\u201d\u6613\u5409\u6c14\u5f97\u7d2b\u8138\u6cdb\u767d\uff0c\u559d\u9053\uff1a\u201c\u4e0d\u670d\u4e5f\u5f97\u670d\u3002 \u3000\u3000\u4f46\u82e5\u4f60\u8f93\u4e86\u5462\uff1f\u201d\u8881\u7d2b\u8863\u5a07\u7b11\u9053\uff1a\u201c\u6211\u8ddf\u4f60\u78d5\u5934\uff0c\u53eb\u4f60\u4f5c\u5e72\u7239\uff0c\u8bf7\u4f60\u591a\u75bc\u6211\u8fd9\u5e72\u5973\u513f\u554a\u3002\u201d\u8bf4\u7740\u500f\u5730\u8dc3\u8d77\uff0c\u53f3\u8db3\u5728\u6845\u7d22\u4e0a\u4e00\u6491\uff0c\u5de6\u8db3\u5df2\u8e0f\u4e0a\u4e86\u5e06\u5e95\u7684\u6a2a\u6746\uff0c\u8170\u4e2d\u94f6\u4e1d\u97ad\u6325\u51fa\uff0c\u5411\u4e0a\u4e00\u6296\uff0c\u5377\u4f4f\u4e86\u6845\u6746\uff0c\u624b\u4e0a\u4f7f\u52b2\uff0c\u5e26\u52a8\u8eab\u5b50\u5411\u4e0a\u8dc3\u9ad8\u3002 \u3000\u3000\u5979\u5de6\u81c2\u521a\u62b1\u4f4f\u6845\u6746\uff0c\u53f3\u624b\u53c8\u6325\u51fa\u94f6\u4e1d\u97ad\u518d\u5411\u4e0a\u4e00\u5377\uff0c\u6700\u540e\u4e00\u62db\u201c\u4e00\u9e64\u51b2\u5929\u201d\uff0c\u8eab\u5b50\u5df2\u9ad8\u8fc7\u6845\u6746\uff0c\u8f7b\u8f7b\u5de7\u5de7\u5730\u843d\u5c06\u4e0b\u6765\uff0c\u7ad9\u5728\u5e06\u9876\u3002\u8fd9\u51e0\u4e0b\u8f7b\u7075\u4e4b\u6781\uff0c\u7801\u5934\u4e0a\u65c1\u89c2\u7684\u95f2\u4eba\u65e0\u4e0d\u559d\u5f69\u3002\u4e5d\u9f99\u6d3e\u7684\u5f1f\u5b50\u4e2d\u5374\u6709\u4eba\u53eb\u4e86\u8d77\u6765\uff1a\u201c\u5582\uff0c\u73a9\u8fd9\u624b\u6709\u4ec0\u4e48\u610f\u601d\uff1f\u6709\u79cd\u7684\u4fbf\u4e0b\u6765\uff0c\u9886\u6559\u9886\u6559\u6613\u8001\u5e08\u5a01\u9707\u4e09\u6e58\u7684\u4e5d\u9f99\u97ad\u529f\u592b\u3002\u201d\u8881\u7d2b\u8863\u5927\u58f0\u9053\uff1a\u201c\u5728\u4e0a\u8fb9\u6bd4\u6b66\uff0c\u5927\u4f19\u513f\u90fd\u77a7\u5f97\u6e05\u695a\u4e9b\u3002\u201d \u3000\u3000\u6613\u5409\u54fc\u4e86\u4e00\u58f0\uff0c\u5c06\u4e5d\u9f99\u97ad\u5728\u8170\u95f4\u4e00\u76d8\uff0c\u5de6\u624b\u6293\u4f4f\u6845\u6746\uff0c\u8eab\u5b50\u5df2\u79bb\u5730\u4e8c\u5c3a\uff0c\u8ddf\u7740\u53f3\u624b\u4e00\u642d\uff0c\u8eab\u5b50\u53c8\u4e0a\u5347\u4e8c\u5c3a\u3002\u90a3\u6845\u6746\u6bd4\u5927\u7897\u7684\u7897\u53e3\u8fd8\u7c97\uff0c\u4e00\u624b\u539f\u662f\u65e0\u6cd5\u63e1\u4f4f\uff0c\u4f46\u4ed6\u624b\u6307\u52b2\u529b\u5389\u5bb3\uff0c\u638c\u529b\u53c8\u6781\u6c89\u96c4\uff0c\u53cc\u624b\u4ea4\u4e92\u63e1\u6293\uff0c\u8eab\u5b50\u7adf\u5e73\u5e73\u7a33\u7a33\u5730\u4e0a\u5347\uff0c\u867d\u65e0\u8881\u7d2b\u8863\u7684\u5feb\u6377\u527d\u608d\uff0c\u4f46\u5728\u884c\u5bb6\u770b\u6765\uff0c\u8fd9\u624b\u529f\u592b\u65e2\u7a33\u4e14\u72e0\uff0c\u5b9e\u662f\u975e\u540c\u5c0f\u53ef\u3002 \u3000\u3000\u8881\u7d2b\u8863\u773c\u89c1\u4ed6\u79bb\u6845\u9876\u5c1a\u6709\u4e08\u4f59\uff0c\u5fc3\u60f3\u4e00\u7ed9\u4ed6\u722c\u4e0a\uff0c\u5c31\u4e0d\u597d\u6597\uff0c\u53ea\u6709\u5c45\u9ad8\u4e34\u4e0b\uff0c\u5148\u5236\u6b62\u4ed6\u4e0a\u5347\uff0c\u5f53\u4e0b\u94f6\u4e1d\u97ad\u4e00\u6643\uff0c\u559d\u9053\uff1a\u201c\u6211\u8fd9\u662f\u5341\u516b\u9f99\u97ad\uff0c\u591a\u4e86\u4f60\u4e5d\u9f99\u3002\u201d\u97ad\u68a2\u5728\u7a7a\u4e2d\u6296\u52a8\uff0c\u6402\u5934\u76d6\u5c06\u4e0b\u6765\u3002 \u3000\u3000\u6613\u5409\u53cc\u624b\u4e0d\u7a7a\uff0c\u5982\u4f55\u62b5\u6321\uff1f\u82e5\u8981\u95ea\u907f\uff0c\u53ea\u6709\u6e9c\u4e0b\u6845\u6746\uff0c\u5982\u6b64\u4e00\u62db\u4e0d\u4ea4\uff0c\u5df2\u7136\u8f93\u4e86\uff0c\u7801\u5934\u4e0a\u7684\u4f17\u5f1f\u5b50\u53c8\u9ad8\u58f0\u53eb\u9a82\u8d77\u6765\uff1a \u3000\u3000\u201c\u4e0d\u8981\u8138\uff01\u201d\u201c\u8fd9\u54ea\u662f\u516c\u5e73\u4ea4\u624b\uff1f\u201d\u201c\u5140\u90a3\u5a46\u5a18\uff0c\u4f60\u4e0b\u6765\u52a8\u624b\uff01\u201d\u5374\u89c1\u6613\u5409\u5c06\u5934\u4e00\u504f\uff0c\u5de6\u81c2\u62b1\u4f4f\u6845\u6746\uff0c\u53f3\u624b\u6325\u52a8\u4e5d\u8282\u94a2\u97ad\uff0c\u7adf\u81ea\u4e0b\u8fce\u4e0a\uff0c\u5f80\u94f6\u4e1d\u97ad\u4e0a\u7838\u53bb\u3002 \u3000\u3000\u8881\u7d2b\u8863\u751f\u6015\u53cc\u97ad\u76f8\u4ea4\uff0c\u82e5\u662f\u7ed9\u7f20\u4f4f\u4e86\uff0c\u62c9\u626f\u8d77\u6765\uff0c\u81ea\u5df1\u529b\u5c0f\uff0c\u5fc5\u5b9a\u5403\u4e8f\uff0c\u4e8e\u662f\u6296\u624b\u626c\u97ad\uff0c\u907f\u5f00\u4ed6\u7684\u5175\u5203\uff0c\u5f85\u8981\u56de\u8f6c\u518d\u51fb\uff0c\u54ea\u77e5\u6613\u5409\u4f7f\u4e00\u62db\u201c\u63d2\u82b1\u76d6\u9876\u201d\uff0c\u821e\u52a8\u94a2\u97ad\u62a4\u4f4f\u5934\u8138\uff0c\u5de6\u81c2\u4e00\u677e\u4e00\u7d27\uff0c\u8eab\u5b50\u4e00\u7eb5\u4e00\u63d0\uff0c\u56db\u4e94\u4e2a\u8d77\u843d\uff0c\u5df2\u7a33\u7a33\u5750\u4e0a\u6845\u6746\u4e4b\u9876\uff0c\u4f46\u542c\u5f97\u7801\u5934\u4e0a\u6b22\u58f0\u5927\u8d77\uff0c\u9f13\u638c\u5982\u96f7\u3002 \u3000\u3000\u4ed6\u8fd9\u4e00\u6765\u5360\u5f97\u4e86\u6709\u5229\u5730\u52bf\uff0c\u8881\u7d2b\u8863\u5fc3\u4e2d\u5374\u53cd\u800c\u653e\u5bbd\uff0c\u89c1\u4ed6\u9002\u624d\u51fa\u97ad\uff0c\u529b\u9053\u867d\u731b\uff0c\u62db\u6570\u4e2d\u5374\u65e0\u7279\u5f02\u53d8\u5316\uff0c\u8fdc\u4e0d\u53ca\u81ea\u5df1\u97ad\u6cd5\u7684\u7cbe\u5fae\u5de7\u5999\uff0c\u5f53\u4e0b\u8eab\u5b50\u5411\u5de6\u4e00\u63a2\uff0c\u5237\u7684\u4e00\u58f0\uff0c\u94f6\u4e1d\u97ad\u81ea\u53f3\u73af\u51fb\u800c\u81f3\u3002\u6613\u5409\u7a33\u7a33\u5750\u7740\uff0c\u4e5d\u8282\u97ad\u56de\u8f6c\uff0c\u5c06\u5bf9\u65b9\u8f6f\u97ad\u6321\u5f00\u3002 \u3000\u3000\u8fd9\u65f6\u9633\u5149\u7167\u8000\uff0c\u6e58\u6c5f\u4e2d\u6cdb\u51fa\u4e07\u9053\u91d1\u6ce2\uff0c\u4e24\u4eba\u5728\u4e94\u516d\u4e08\u9ad8\u5904\u76f8\u6597\uff0c\u4e24\u6761\u8f6f\u97ad\u72b9\u4f3c\u7075\u86c7\u76d8\u65cb\uff0c\u7684\u662f\u597d\u770b\u715e\u4eba\u3002\u5cb8\u8fb9\u4eba\u4f17\u8d8a\u805a\u8d8a\u591a\uff0c\u6e58\u6c5f\u4e2d\u4e0a\u4e0a\u4e0b\u4e0b\u7684\u8239\u8236\u4e5f\u591a\u6536\u5e06\u505c\u8235\uff0c\u8239\u4e2d\u6c34\u624b\u4e58\u5ba2\uff0c\u4e00\u9f50\u4ef0\u9996\u89c2\u6597\u3002 \u3000\u3000\u6613\u5409\u81ea\u77e5\u8f7b\u8eab\u529f\u592b\u4e0d\u5982\u5bf9\u65b9\uff0c\u53ea\u662f\u7a33\u5750\u5e06\u9876\uff0c\u53cc\u8db3\u631f\u4f4f\u6845\u6746\uff0c\u5148\u5360\u4e86\u4e2a\u4e0d\u8d25\u4e4b\u5730\u3002\u8881\u7d2b\u8863\u5374\u662f\u4e1c\u7a9c\u897f\u8dc3\uff0c\u5728\u5e06\u9876\u7684\u6a2a\u6841\u4e0a\u5ffd\u8fdb\u5ffd\u9000\u3002\u5979\u94f6\u4e1d\u97ad\u6bd4\u5bf9\u624b\u7684\u4e5d\u9f99\u97ad\u957f\u4e86\u4e00\u500d\u6709\u4f59\uff0c\u53ea\u6709\u5979\u653b\u51fb\u6613\u5409\u7684\u4efd\u513f\uff0c\u6613\u5409\u5374\u65e0\u6cd5\u53cd\u51fb\u3002\u62c6\u5230\u516d\u5341\u4f59\u62db\u540e\uff0c\u5979\u624b\u4e2d\u4e00\u6761\u957f\u97ad\u5982\u94f6\u86c7\u98de\u821e\uff0c\u62db\u6570\u6108\u51fa\u6108\u5947\u3002\u6613\u5409\u6765\u6765\u53bb\u53bb\u5374\u53ea\u662f\u4e03\u516b\u62db\uff0c\u5bc6\u5bc6\u62a4\u4f4f\u4e86\u5168\u8eab\uff0c\u4fdf\u673a\u53bb\u7f20\u5bf9\u65b9\u8f6f\u97ad\u3002 \u3000\u3000\u4e00\u773c\u770b\u6765\uff0c\u8881\u7d2b\u8863\u4f3c\u662f\u5360\u5c3d\u4e86\u4e0a\u98ce\uff0c\u4f46\u5979\u5982\u6b64\u6253\u6cd5\u6781\u662f\u5403\u529b\uff0c\u53ea\u8981\u4e45\u653b\u4e0d\u4e0b\uff0c\u97ad\u6cd5\u4e2d\u7a0d\u6709\u7834\u7efd\uff0c\u6216\u662f\u8db3\u4e0b\u4e00\u6ed1\u4e00\u7eca\uff0c\u90a3\u4fbf\u8f93\u4e86\u3002\u539f\u6765\u6613\u5409\u7684\u7528\u5fc3\uff0c\u6b63\u662f\u5b59\u5b50\u5175\u6cd5\u4e2d\u6240\u8c13\u201c\u5148\u4e3a\u4e0d\u53ef\u80dc\uff0c\u4ee5\u5f85\u654c\u4e4b\u53ef\u80dc\u201d\u3002\u8881\u7d2b\u8863\u65e9\u77e5\u4ed6\u7684\u5fc3\u610f\uff0c\u4f46\u4e0d\u8bba\u5982\u4f55\u53d8\u62db\u8fdb\u653b\uff0c\u4ed6\u8fd9\u4e03\u516b\u62db\u5b88\u62a4\u5168\u8eab\uff0c\u7adf\u662f\u4e25\u5bc6\u5f02\u5e38\uff0c\u65e0\u9699\u53ef\u4e58\u3002\u5982\u5728\u5e73\u5730\uff0c\u5979\u81ea\u53ef\u51cc\u7a7a\u4e0b\u51fb\uff0c\u6216\u662f\u7740\u5730\u6eda\u8fdb\uff0c\u4f46\u81ea\u5df1\u5f15\u4ed6\u9ad8\u7a7a\u76f8\u6597\uff0c\u53cd\u7ed9\u4ed6\u5360\u4e86\u5730\u5229\uff0c\u5374\u975e\u59cb\u6599\u4e4b\u6240\u53ca\u4e86\u3002 \u3000\u3000\u53c8\u6597\u7247\u523b\uff0c\u60c5\u52bf\u4ecd\u65e0\u53d8\u5316\uff0c\u8881\u7d2b\u8863\u5fae\u611f\u6c14\u606f\u7c97\u91cd\uff0c\u7eb5\u8dc3\u4e4b\u9645\uff0c\u5df2\u7a0d\u4e0d\u53ca\u521d\u65f6\u8f7b\u6377\u3002\u6613\u5409\u77a7\u51fa\u8f6c\u673a\u5df2\u81f3\uff0c\u5f85\u5979\u957f\u97ad\u63a0\u5230\u9762\u524d\uff0c\u7a81\u51fa\u5de6\u624b\uff0c\u5f84\u53bb\u6293\u5979\u97ad\u4e0a\u91d1\u7403\u3002\u8881\u7d2b\u8863\u4e00\u60ca\uff0c\u8f6f\u97ad\u4e0b\u6c89\uff0c\u54ea\u77e5\u6613\u5409\u7684\u4e5d\u9f99\u97ad\u53cd\u8fc7\u6765\u4e00\u538b\u4e00\u94a9\uff0c\u82e5\u975e\u5979\u94f6\u4e1d\u97ad\u95ea\u907f\u5f97\u5feb\uff0c\u53cc\u97ad\u5df2\u7f20\u5728\u4e00\u8d77\u3002\u6613\u5409\u5f97\u7406\u4e0d\u8ba9\u4eba\uff0c\u77a7\u51c6\u4e86\u5979\u97ad\u5934\u56de\u8d77\u4e4b\u5904\uff0c\u4e5d\u9f99\u97ad\u4e00\u62db\u201c\u9752\u85e4\u7f20\u846b\u82a6\u201d\uff0c\u5927\u559d\u4e00\u58f0\uff0c\u5df2\u5c06\u94f6\u4e1d\u97ad\u7f20\u4f4f\u3002 \u3000\u3000\u8881\u7d2b\u8863\u53ea\u89c9\u624b\u81c2\u4e00\u9178\uff0c\u624b\u4e2d\u957f\u97ad\u7ed9\u4e00\u80a1\u5f3a\u529b\u5f80\u5916\u6025\u62c9\uff0c\u77e5\u9053\u82e5\u4e0e\u5bf9\u65b9\u86ee\u593a\uff0c\u81ea\u5df1\u5fc5\u8f93\uff0c\u5979\u5fc3\u601d\u8f6c\u5f97\u597d\u5feb\uff0c\u5371\u6025\u4e2d\u500f\u51fa\u9669\u62db\uff0c\u53f3\u624b\u731b\u5730\u4e00\u7529\uff0c\u94f6\u4e1d\u97ad\u7684\u97ad\u67c4\u8131\u624b\u98de\u51fa\uff0c\u7ed5\u7740\u6845\u6746\u610f\u8f6c\u5708\u5b50\uff0c\u4f46\u89c1\u94f6\u5149\u95ea\u52a8\uff0c\u5237\u5587\u5587\u4e00\u9635\u54cd\uff0c\u4e5d\u8282\u94a2\u97ad\u548c\u94f6\u4e1d\u8f6f\u97ad\u4e24\u6761\u8f6f\u97ad\uff0c\u7adf\u5c06\u6613\u5409\u53cc\u817f\u8fde\u540c\u53f3\u81c2\u4e00\u9f50\u7ed5\u5728\u6845\u6746\u4e4b\u4e0a\u3002 \u3000\u3000\u8fd9\u4e00\u4e0b\u53d8\u751f\u4e0d\u6d4b\uff0c\u6613\u5409\u600e\u6599\u60f3\u5f97\u5230\uff1f\u5927\u60ca\u4e4b\u4e0b\uff0c\u5fd9\u4f38\u5de6\u624b\u53bb\u89e3\u97ad\uff0c\u500f\u89c1\u8881\u7d2b\u8863\u6251\u5230\u8eab\u524d\uff0c\u5de6\u624b\u63a2\u51fa\uff0c\u4fbf\u6765\u6316\u4ed6\u773c\u73e0\u3002 \u3000\u3000\u6613\u5409\u5de6\u624b\u6025\u5fd9\u653e\u8131\u8f6f\u97ad\uff0c\u4e3e\u624b\u6321\u67b6\u3002\u54ea\u77e5\u8881\u7d2b\u8863\u8fd9\u4e00\u4e0b\u4e43\u662f\u865a\u62db\uff0c\u5de6\u638c\u5728\u7a7a\u4e2d\u5fae\u4e00\u505c\u987f\uff0c\u7275\u5236\u4ed6\u7684\u5de6\u638c\uff0c\u53f3\u624b\u75be\u51fa\uff0c\u65e9\u5df2\u70b9\u4e2d\u4e86\u4ed6\u5de6\u814b\u4e0b\u7684\u201c\u6e0a\u814b\u7a74\u201d\u3002\u8fd9\u4e00\u62db\u5728\u65c1\u4eba\u770b\u6765\uff0c\u7b80\u76f4\u662f\u6613\u5409\u81ea\u4e3e\u624b\u81c2\uff0c\u9732\u51fa\u814b\u5e95\u4efb\u7531\u5bf9\u65b9\u70b9\u7a74\u4e00\u822c\u3002\u4ed6\u7a74\u9053\u7834\u70b9\uff0c\u5de6\u81c2\u8f6f\u8f6f\u4e0b\u5782\uff0c\u53cc\u817f\u4e0e\u53f3\u81c2\u5374\u53c8\u7ed9\u7f1a\u5728\u6845\u4e0a\uff0c\u53ef\u8bf4\u662f\u4e00\u8d25\u6d82\u5730\uff0c\u518d\u65e0\u56de\u624b\u4e4b\u529b\u3002 \u3000\u3000\u80e1\u6590\u5728\u5730\u4e0b\u89c1\u5979\u8d25\u4e2d\u53d6\u80dc\uff0c\u8fd9\u4e00\u624b\u8d62\u5f97\u5de7\u5999\u65e0\u6bd4\uff0c\u521a\u53eb\u4e86\u58f0\u597d\uff0c\u5ffd\u89c1\u9ec4\u5149\u95ea\u52a8\uff0c\u4e5d\u679a\u91d1\u94b1\u9556\u6025\u5411\u6845\u6746\u4e0a\u98de\u53bb\uff0c\u5c04\u5411\u8881\u7d2b\u8863\u540e\u5fc3\u3002 \u3000\u3000\u8881\u7d2b\u8863\u5c06\u6613\u5409\u6253\u5f97\u5982\u6b64\u72fc\u72c8\uff0c\u5fc3\u4e2d\u5927\u662f\u5f97\u610f\uff0c\u6b63\u8981\u5728\u9ad8\u5904\u5938\u8a00\u51e0\u53e5\uff0c\u903c\u4ed6\u4eb2\u53e3\u8bb8\u8bfa\u8ba9\u4e86\u638c\u95e8\uff0c\u8fd9\u624d\u653e\u4ed6\uff0c\u6ca1\u6599\u5230\u4e0b\u9762\u7adf\u7136\u6709\u4eba\u5077\u88ad\u3002\u8fd9\u4e5d\u679a\u91d1\u94b1\u9556\u6765\u5f97\u65e2\u5feb\uff0c\u90e8\u4f4d\u53c8\u56db\u4e0b\u5206\u6563\uff0c\u5979\u8eab\u5728\u6a2a\u6841\u4e4b\u4e0a\uff0c\u53ea\u8981\u5411\u5de6\u6216\u662f\u5411\u53f3\u8e0f\u51fa\u534a\u6b65\uff0c\u7acb\u65f6\u4ece\u4e94\u516d\u4e08\u9ad8\u5904\u6454\u5c06\u4e0b\u6765\uff0c\u5374\u53c8\u5982\u4f55\u907f\u5f97\uff1f\u60c5\u6025\u667a\u751f\uff0c\u8eab\u5b50\u5411\u540e\u4e00\u4ef0\uff0c\u767b\u65f6\u6454\u4e0b\uff0c\u4e5d\u679a\u94b1\u9556\u4ece\u5e06\u9876\u63a0\u8fc7\u3002\u8239\u5934\u5cb8\u4e0a\u4f17\u4eba\u60ca\u547c\u58f0\u4e2d\uff0c\u53ea\u89c1\u5979\u53cc\u8db3\u94a9\u4f4f\u6a2a\u6841\uff0c\u8eab\u5b50\u6302\u5728\u534a\u7a7a\u3002 \u3000\u3000\u5cb8\u4e0a\u5077\u53d1\u6697\u5668\u4e4b\u4eba\u4e00\u4e0d\u505a\uff0c\u4e8c\u4e0d\u4f11\uff0c\u8ddf\u7740\u53c8\u662f\u4e09\u679a\u94b1\u9556\u5c04\u51fa\uff0c\u8fd9\u4e00\u6b21\u5374\u662f\u4e00\u679a\u88ad\u5979\u8eab\u5b50\uff0c\u4e24\u679a\u5c04\u5411\u6a2a\u6841\uff0c\u53ea\u8981\u5979\u8eab\u5b50\u5411\u4e0a\u7ffb\u8d77\uff0c\u521a\u597d\u662f\u81ea\u884c\u51d1\u5411\u94b1\u9556\u3002\u80e1\u6590\u77e5\u9053\u8fd9\u4e00\u4e0b\u8881\u7d2b\u8863\u518d\u4e5f\u65e0\u6cd5\u907f\u8ba9\uff0c\u7acb\u5373\u4e5f\u662f\u4e09\u679a\u5236\u94b1\u5c04\u51fa\u3002\u4ed6\u51fa\u624b\u867d\u540e\uff0c\u4f46\u624b\u52b2\u51cc\u5389\uff0c\u94b1\u9556\u53bb\u52bf\u5374\u5feb\uff0c\u516d\u679a\u94dc\u94b1\u5728\u7a7a\u4e2d\u4e92\u649e\uff0c\u94ee\u94ee\u94ee\u4e09\u58f0\uff0c\u4e00\u9f50\u659c\u98de\uff0c\u843d\u5165\u4e86\u6c5f\u4e2d\u3002 \u3000\u3000\u8881\u7d2b\u8863\u80cc\u4e0a\u60ca\u51fa\u4e86\u4e00\u8eab\u51b7\u6c57\uff0c\u521a\u6b32\u7ffb\u8eab\u800c\u8d77\uff0c\u80e1\u6590\u5927\u53eb\u4e00\u58f0\uff1a\u201c\u8fd9\u7b97\u4ec0\u4e48\uff1f\u201d\u8dc3\u4e0a\u4e86\u8239\u5934\uff0c\u53ea\u542c\u5580\u5587\u3001\u5580\u5587\u4e24\u58f0\u5de8\u54cd\uff0c\u6a2a\u6841\u65ad\u6298\u3002\u8881\u7d2b\u8863\u8ddf\u7740\u6a2a\u6841\u5411\u6c5f\u4e2d\u8dcc\u843d\uff0c\u800c\u6613\u5409\u5904\u8eab\u6240\u5728\u7684\u6845\u6746\uff0c\u5374\u4e5f\u4ece\u4e2d\u65ad\u7edd\u3002\u8881\u7d2b\u8863\u5f53\u65f6\u5934\u4e0b\u811a\u4e0a\uff0c\u4eb2\u773c\u89c1\u5230\u4f55\u4eba\u53d1\u5c04\u6697\u5668\u5077\u88ad\uff0c\u80e1\u6590\u5982\u4f55\u51fa\u624b\u76f8\u6551\uff0c\u4f46\u6a2a\u6841\u600e\u5730\u65ad\u6298\uff0c\u5374\u672a\u77a7\u89c1\u3002 \u3000\u3000\u539f\u6765\u6613\u5409\u5de6\u80c1\u7a74\u9053\u88ab\u70b9\uff0c\u534a\u8eab\u52a8\u5f39\u4e0d\u5f97\uff0c\u53f3\u624b\u5374\u5c1a\u53ef\u7528\u529b\uff0c\u5fd9\u4ece\u53cc\u97ad\u7f20\u7ed5\u4e4b\u4e2d\u8131\u51fa\u624b\u81c2\uff0c\u773c\u89c1\u8881\u7d2b\u8863\u5012\u6302\u6841\u4e0a\uff0c\u5f53\u5373\u5c06\u5168\u8eab\u52b2\u529b\u8fd0\u4e8e\u638c\u4e0a\uff0c\u53d1\u638c\u51fb\u5411\u6a2a\u6841\u3002\u4ed6\u8182\u529b\u597d\u5927\uff0c\u8fde\u51fb\u4e09\u638c\uff0c\u6841\u65ad\u4eba\u843d\u3002 \u3000\u3000\u5c31\u5728\u6b64\u65f6\uff0c\u80e1\u6590\u4e5f\u5df2\u8dc3\u4e0a\u4e86\u8239\u5934\uff0c\u5fc3\u60f3\u82e5\u662f\u8881\u59d1\u5a18\u843d\u6c34\uff0c\u8fd9\u59d3\u6613\u7684\u53cd\u800c\u5b89\u5750\u6845\u9876\uff0c\u5f85\u4ed6\u6162\u6162\u6e9c\u5c06\u4e0b\u6765\uff0c\u5c82\u975e\u662f\u4ed6\u80dc\u4e86\uff1f \u3000\u3000\u5f53\u5373\u80cc\u9760\u6845\u6746\uff0c\u8fd0\u52b2\u5411\u540e\u529b\u649e\uff0c\u8fd9\u6845\u6746\u53c8\u575a\u53c8\u7c97\uff0c\u4e00\u649e\u4e4b\u4e0b\u53ea\u6643\u4e86\u51e0\u4e0b\u3002\u80e1\u6590\u5fc3\u4e2d\u6025\u4e86\uff0c\u62d4\u51fa\u5355\u5200\uff0c\u5237\u7684\u4e00\u5200\uff0c\u5288\u65ad\u4e86\u6845\u6746\u3002 \u3000\u3000\u773c\u89c1\u8881\u7d2b\u8863\u4e0e\u6613\u5409\u5404\u81ea\u968f\u7740\u4e00\u6bb5\u5de8\u6728\u5f80\u6c5f\u4e2d\u8dcc\u843d\uff0c\u53ea\u662f\u8881\u7d2b\u8863\u7684\u6a2a\u6841\u5148\u65ad\uff0c\u8eab\u5728\u534a\u622a\u6845\u6746\u4e4b\u4e0b\uff0c\u82e5\u662f\u7ed9\u65ad\u6845\u51fb\u4e2d\uff0c\u6027\u547d\u53ef\u5fe7\uff0c\u80e1\u6590\u5f53\u5373\u6293\u8d77\u8239\u5934\u62c9\u7ea4\u7528\u7684\u7af9\u7d22\uff0c\u5bf9\u51c6\u8881\u7d2b\u8863\u8eab\u524d\u6325\u5c06\u8fc7\u53bb\uff0c\u5927\u559d\u9053\uff1a\u201c\u6293\u4f4f\u4e86\uff01\u201d\u7af9\u7d22\u98de\u51fa\uff0c\u6709\u5982\u4e00\u6761\u6781\u957f\u7684\u8f6f\u97ad\u3002 \u3000\u3000\u8881\u7d2b\u8863\u8eab\u5728\u534a\u7a7a\uff0c\u5fc3\u4e2d\u5fd9\u4e71\uff0c\u5979\u867d\u8bc6\u5f97\u6c34\u6027\uff0c\u4f46\u60f3\u5728\u4f17\u76ee\u777d\u777d\u4e4b\u4e0b\u843d\u6c34\uff0c\u5f85\u4f1a\u6e7f\u6dcb\u6dcb\u5730\u722c\u8d77\uff0c\u5c82\u975e\u72fc\u72c8\u4e07\u72b6\uff1f\u7a81\u89c1\u7af9\u7d22\u98de\u5230\uff0c\u6025\u5fd9\u4f38\u624b\u6293\u4f4f\u3002\u80e1\u6590\u4e00\u6325\u4e00\u62c9\uff0c\u8881\u7d2b\u8863\u501f\u52bf\u8dc3\u8d77\uff0c\u8f7b\u8f7b\u5de7\u5de7\u5730\u843d\u5728\u8239\u5934\u3002 \u3000\u3000\u5979\u53cc\u8db3\u521a\u843d\u4e0a\u8239\u677f\uff0c\u53ea\u542c\u5f97\u6251\u901a\u4e00\u58f0\u5de8\u54cd\uff0c\u6c34\u82b1\u56db\u6e85\uff0c\u65e0\u6570\u6c34\u73e0\u98de\u5230\u4e86\u5979\u5934\u4e0a\u8138\u4e0a\uff0c\u6b63\u662f\u6613\u5409\u4e0e\u65ad\u6845\u4e00\u9f50\u843d\u6c34\u3002\u5cb8\u4e0a\u4eba\u4f17\u5927\u58f0\u547c\u53eb\uff0c\u6251\u901a\u6251\u901a\u54cd\u58f0\u4e0d\u7edd\u3002\u539f\u6765\u6613\u5409\u4e0d\u4f1a\u6c34\u6027\uff0c\u4e5d\u9f99\u6d3e\u7684\u5341\u4e03\u516b\u540d\u5f1f\u5b50\u7eb7\u7eb7\u8dc3\u5165\u6e58\u6c5f\uff0c\u4e89\u5148\u6050\u540e\u5730\u53bb\u6551\u5e08\u7236\u3002 \u3000\u3000\u8881\u7d2b\u8863\u5411\u80e1\u6590\u5ae3\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u80e1\u5927\u54e5\uff0c\u8c22\u8c22\u4f60\u5566\uff01\u201d\u80e1\u6590\u7b11\u9053\uff1a\u201c\u6211\u8fd9\u2018\u80e1\u2019\u5b57\u62c6\u5f00\u6765\u662f\u2018\u6708\u5341\u53e3\u201d\u4e09\u5b57\uff0c\u770b\u6765\u6211\u6bcf\u6708\u4e4b\u4e2d\uff0c\u8981\u8eab\u4e2d\u4e5d\u5200\u3002\u201d \u3000\u3000\u8881\u7d2b\u8863\u7b11\u5f97\u66f4\u662f\u6b22\u7545\uff0c\u5fc3\u60f3\u6211\u9002\u624d\u7ed9\u90a3\u6613\u5409\u62c6\u5b57\uff0c\u539f\u6765\u90fd\u53eb\u4ed6\u5077\u542c\u53bb\u5566\uff0c\u7b11\u9053\uff1a\u201c\u5e78\u597d\u4f60\u540d\u5b57\u4e2d\u6709\u4e2a\u2018\u975e\u2019\u5b57\uff0c\u8fd9\u4e00\u2018\u975e\u4e5f\u975e\u4e5f\u2019\uff0c\u90a3\u4e5d\u5200\u4e4b\u5384\u5c31\u9022\u51f6\u5316\u5409\u4e86\u3002\u201d\u80e1\u6590\u7b11\u9053\uff1a\u201c\u591a\u8c22\u59d1\u5a18\u91d1\u53e3\u3002\u201d \u3000\u3000\u8881\u7d2b\u8863\u4e0e\u4ed6\u91cd\u9022\uff0c\u5fc3\u4e2d\u6781\u662f\u9ad8\u5174\uff0c\u53c8\u627f\u4ed6\u51fa\u624b\u76f8\u6551\uff0c\u6709\u610f\u4e0e\u4ed6\u4fee\u597d\uff0c\u53c8\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u2018\u6590\u2019\u5b57\u662f\u6587\u91c7\u6590\u7136\uff0c\u90a3\u4e0d\u5fc5\u8bf4\u4e86\u3002\u2018\u975e\u2019\u5b57\u4e0b\u52a0\u2018\u7fbd\u2019\u5b57\u4e3a\u2018\u7fe1\u2019\uff0c\u4e3b\u5f97\u91d1\u7389\u7fe1\u7fe0\uff1b\u52a0\u2018\u8349\u2019\u5b57\u5934\u4e3a\u2018\u83f2\u2019\uff0c\u4e3b\u82ac\u82b3\u534e\u7f8e\uff1b\u52a0\u7ede\u4e1d\u65c1\u4e3a\u2018\u7eef\u2019\uff0c\u7ea2\u888d\u7389\u5e26\uff0c\u4e3b\u505a\u5927\u5b98\u3002\u201d\u80e1\u6590\u4f38\u4e86\u4f38\u820c\u5934\uff0c\u9053\uff1a\u201c\u5347\u5b98\u53d1\u8d22\uff0c\u53ef\u4e86\u4e0d\u8d77\uff01\u201d \u3000\u3000\u4e24\u4eba\u5728\u8239\u5934\u8bf4\u7b11\uff0c\u65c1\u82e5\u65e0\u4eba\u3002\u5ffd\u542c\u5f97\u7801\u5934\u4e0a\u4e00\u9635\u5927\u4e71\uff0c\u4e5d\u9f99\u6d3e\u4f17\u95e8\u4eba\u5c06\u6613\u5409\u8fde\u7740\u65ad\u6845\uff0c\u4e03\u624b\u516b\u811a\u5730\u62ac\u4e0a\u5cb8\u6765\u3002\u4ed6\u5e74\u8001\u80a5\u80d6\uff0c\u53c8\u4e0d\u901a\u6c34\u6027\uff0c\u5403\u4e86\u51e0\u53e3\u6c34\uff0c\u4e00\u6c14\u4e00\u6012\uff0c\u7adf\u81ea\u6655\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u8881\u7d2b\u8863\u6697\u6697\u5fc3\u60ca\uff1a\u201c\u83ab\u8981\u5f04\u51fa\u4eba\u547d\uff0c\u8fd9\u4e8b\u60c5\u53ef\u95f9\u5927\u4e86\u3002\u201d\u4f4e\u58f0\u9053\uff1a\u201c\u80e1\u5927\u54e5\uff0c\u54b1\u4eec\u5feb\u8d70\u5427\uff01\u201d\u8bf4\u7740\u4e00\u8dc3\u4e0a\u5cb8\uff0c\u4f38\u624b\u53bb\u53d6\u90a3\u7f20\u5728\u65ad\u6845\u4e0a\u7684\u94f6\u4e1d\u8f6f\u97ad\u3002 \u3000\u3000\u4e5d\u9f99\u6d3e\u4f17\u95e8\u4eba\u7eb7\u7eb7\u6012\u559d\uff0c\u516d\u4e03\u6761\u8f6f\u97ad\u9f50\u5f80\u5979\u8eab\u4e0a\u51fb\u4e86\u4e0b\u6765\u3002\u53ea\u542c\u5f97\u545b\u5577\u5577\u54cd\u6210\u4e00\u7247\uff0c\u516d\u4e03\u6761\u8f6f\u97ad\u4e92\u76f8\u649e\u51fb\uff0c\u4fbf\u4f3c\u4e00\u9053\u94c1\u7f51\u822c\u5f53\u5934\u76d6\u5230\u3002\u5979\u94f6\u4e1d\u8f6f\u97ad\u5728\u624b\uff0c\u501f\u529b\u6253\u529b\uff0c\u4e00\u97ad\u4ece\u5934\u9876\u6a2a\u8fc7\uff0c\u8eab\u5b50\u5df2\u659c\u7a9c\u51fa\u53bb\u3002\u5979\u5077\u773c\u518d\u5411\u6613\u5409\u671b\u4e86\u4e00\u773c\uff0c\u53ea\u89c1\u4ed6\u4e00\u4e2a\u80d6\u80d6\u7684\u8eab\u8eaf\u6a2a\u5367\u5730\u4e0b\uff0c\u4e00\u52a8\u4e0d\u52a8\uff0c\u4e5f\u4e0d\u77e5\u662f\u6b7b\u662f\u6d3b\u3002\u80e1\u6590\u7ffb\u8eab\u4e0a\u9a6c\uff0c\u53f3\u624b\u7275\u7740\u767d\u9a6c\uff0c\u53eb\u9053\uff1a\u201c\u4e5d\u9f99\u6d3e\u638c\u95e8\u4eba\u4e0d\u5927\u5409\u5229\uff0c\u4e0d\u5f53\u4e5f\u7f62\u3002\u201d\u8881\u7d2b\u8863\u7b11\u9053\uff1a\u201c\u90a3\u5c31\u542c\u4f60\u5429\u5490\u5566\uff01\u201d\u8dc3\u8d77\u8eab\u6765\uff0c\u4e0a\u4e86\u9a6c\u80cc\u3002 \u3000\u3000\u4e5d\u9f99\u6d3e\u7684\u4f17\u5f1f\u5b50\u5927\u58f0\u53eb\u56b7\uff0c\u7eb7\u7eb7\u8d76\u6765\u963b\u622a\u3002\u4e24\u6761\u8f6f\u97ad\u7740\u5730\u6a2a\u626b\uff0c\u5f80\u9a6c\u8db3\u4e0a\u6253\u53bb\u3002\u8881\u7d2b\u8863\u56de\u8eab\u4e00\u97ad\uff0c\u5df2\u5c06\u4e24\u6761\u8f6f\u97ad\u7684\u97ad\u5934\u7f20\u4f4f\uff0c\u53f3\u624b\u4e00\u63d0\u9a6c\u7f30\uff0c\u767d\u9a6c\u5411\u524d\u75be\u5954\u3002\u8fd9\u9a6c\u795e\u9a8f\u975e\u51e1\uff0c\u811a\u6b65\u56fa\u7136\u8fc5\u6377\u65e0\u6bd4\uff0c\u529b\u6c14\u4e5f\u662f\u5927\u5f97\u5f02\u5e38\uff0c\u53d1\u529b\u51b2\u523a\uff0c\u767b\u65f6\u5c06\u90a3\u4e24\u540d\u624b\u6301\u8f6f\u97ad\u7684\u6c49\u5b50\u62d6\u5012\u3002 \u3000\u3000\u8fd9\u4e00\u4e0b\u53d8\u8d77\u4e0d\u610f\uff0c\u4e24\u540d\u6c49\u5b50\u5927\u60ca\u4e4b\u4e0b\uff0c\u8eab\u5b50\u5df2\u88ab\u767d\u9a6c\u5728\u5730\u4e0b\u62d6\u4e86\u516d\u4e03\u4e08\u8fdc\u3002\u4e24\u4eba\u6025\u6b32\u7ad9\u8d77\uff0c\u4f46\u767d\u9a6c\u53bb\u52bf\u4f55\u7b49\u5feb\u901f\uff0c\u4e24\u4eba\u4e0a\u8eab\u521a\u62ac\u8d77\uff0c\u7acb\u65f6\u53c8\u88ab\u62d6\u5012\uff0c\u60ca\u60f6\u4e4b\u4e2d\u7adf\u81ea\u60f3\u4e0d\u8d77\u629b\u6389\u5175\u5203\uff0c\u4ecd\u662f\u6b7b\u6b7b\u5730\u6293\u4f4f\u97ad\u67c4\u3002 \u3000\u3000\u8881\u7d2b\u8863\u5728\u9a6c\u4e0a\u77a7\u5f97\u597d\u7b11\uff0c\u500f\u5730\u52d2\u9a6c\u505c\u6b65\uff0c\u5f85\u90a3\u4e24\u540d\u6c49\u5b50\u7ad9\u8d77\u8eab\u6765\uff0c\u53ea\u89c1\u4e24\u4eba\u76ee\u9752\u9f3b\u80bf\uff0c\u624b\u8db3\u989c\u9762\u5168\u4e3a\u5730\u4e0b\u6c99\u783e\u64e6\u4f24\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u7684\u8f6f\u97ad\u4e2d\u6709\u5b9d\u4e48\uff1f\u600e\u5730\u4e0d\u820d\u5f97\u653e\u624b\uff1f\u201d\u4e24\u53e5\u8bdd\u521a\u95ee\u5b8c\uff0c\u4e0d\u7b49\u4ed6\u4eec\u56de\u7b54\uff0c\u53f3\u8db3\u8db3\u5c16\u5728\u9a6c\u8179\u4e0a\u8f7b\u8f7b\u4e00\u70b9\u3002\u767d\u9a6c\u5411\u524d\u4e00\u51b2\uff0c\u53c8\u5c06\u4e24\u4eba\u62d6\u5012\u3002\u8fd9\u65f6\u4e24\u4eba\u65b9\u59cb\u7701\u609f\uff0c\u6492\u624b\u5f03\u97ad\uff0c\u8033\u542c\u5f97\u8881\u7d2b\u8863\u683c\u683c\u5a07\u7b11\uff0c\u4e0e\u80e1\u6590\u5e76\u80a9\u9a70\u53bb\u3002 \u3000\u3000\u6613\u5bb6\u6e7e\u4e5d\u9f99\u6d3e\u5f1f\u5b50\u4f17\u591a\uff0c\u58f0\u52bf\u751a\u5927\uff0c\u6b64\u65e5\u4e3a\u8001\u5e08\u9001\u884c\uff0c\u5747\u4f1a\u805a\u5728\u7801\u5934\u4e4b\u4e0a\uff0c\u773c\u89c1\u6613\u5409\u53d7\u632b\uff0c\u539f\u8981\u4e00\u62e5\u800c\u4e0a\u3002\u8881\u7d2b\u8863\u4e0e\u80e1\u6590\u6b66\u529f\u867d\u5f3a\uff0c\u7ec8\u7a76\u662f\u597d\u6c49\u654c\u4e0d\u8fc7\u4eba\u591a\u3002\u5e78\u597d\u8881\u7d2b\u8863\u4e34\u53bb\u65bd\u4e00\u624b\u56de\u97ad\u62c9\u4eba\uff0c\u4e8b\u52bf\u5947\u5e7b\uff0c\u4f17\u5f1f\u5b50\u77a7\u5f97\u76ee\u77aa\u53e3\u5446\uff0c\u4e00\u65f6\u4f1a\u4e0d\u8fc7\u610f\u6765\uff0c\u5f85\u8981\u62a2\u4e0a\u56f4\u653b\uff0c\u4e8c\u4eba\u5df2\u9a70\u9a6c\u8fdc\u53bb\u3002\u8fd9\u65f6\u6613\u5409\u60a0\u60a0\u9192\u8f6c\uff0c\u4f17\u5f1f\u5b50\u4e03\u5634\u516b\u820c\u5730\u4e0a\u524d\u6170\u95ee\uff0c\u75db\u9a82\u8881\u7d2b\u8863\u4f7f\u5978\u884c\u8bc8\uff0c\u7eb7\u7eb7\u8bae\u8bba\uff0c\u5374\u8c01\u4e5f\u4e0d\u77e5\u5979\u7684\u6765\u5386\uff0c\u4e8e\u662f\u4e5d\u9f99\u6d3e\u6240\u6709\u7684\u5bf9\u5934\uff0c\u4e2a\u4e2a\u6210\u4e86\u5979\u80cc\u540e\u6307\u4f7f\u4e4b\u4eba\u3002 \u3000\u3000\u8881\u7d2b\u8863\u9a70\u51fa\u8001\u8fdc\uff0c\u76f4\u81f3\u56de\u5934\u671b\u4e0d\u89c1\u6613\u5bb6\u6e7e\u7684\u623f\u5c4b\uff0c\u624d\u5c06\u593a\u6765\u7684\u4e24\u6839\u4e5d\u8282\u94a2\u97ad\u629b\u5728\u5730\u4e0b\u3002\u5979\u8f6c\u773c\u77a7\u77a7\u80e1\u6590\uff0c\u89c1\u4ed6\u7a7f\u7740\u4e00\u8eab\u4e61\u519c\u7684\u8863\u670d\uff0c\u571f\u5934\u571f\u8111\uff0c\u61a8\u91cc\u61a8\u6c14\uff0c\u5fcd\u4e0d\u4f4f\u597d\u7b11\uff0c\u4f46\u60f3\u9002\u624d\u82e5\u4e0d\u662f\u4ed6\u51fa\u624b\u6551\u63f4\uff0c\u591a\u534a\u81ea\u5df1\u5df2\u5c06\u4e00\u6761\u5c0f\u547d\u9001\u5728\u6613\u5bb6\u6e7e\uff0c\u6b64\u523b\u56de\u601d\uff0c\u4e0d\u7981\u6697\u81ea\u5fc3\u60ca\u3002 \u3000\u3000\u4e24\u4eba\u5e76\u9a91\u8d70\u4e86\u4e00\u9635\uff0c\u80e1\u6590\u9053\uff1a\u201c\u8881\u59d1\u5a18\uff0c\u5929\u4e0b\u6b66\u5b66\uff0c\u5171\u6709\u591a\u5c11\u95e8\u6d3e\uff1f\u201d\u8881\u7d2b\u8863\u7b11\u9053\uff1a\u201c\u4e0d\u77e5\u9053\u554a\uff0c\u4f60\u8bf4\u6709\u591a\u5c11\u95e8\u6d3e\uff1f\u201d\u80e1\u6590\u6447\u5934\u9053\uff1a\u201c\u6211\u8bf4\u4e0d\u4e0a\uff0c\u8fd9\u624d\u8bf7\u6559\u3002\u4f60\u73b0\u4e0b\u5df2\u5f53\u4e86\u97e6\u9640\u95e8\u3001\u516b\u4ed9\u5251\u3001\u4e5d\u9f99\u6d3e\u4e09\u5bb6\u7684\u5927\u638c\u95e8\u5566\u3002\u8fd8\u5f97\u518d\u505a\u51e0\u6d3e\u638c\u95e8\uff0c\u65b9\u624d\u5fc3\u6ee1\u610f\u8db3\uff1f\u201d\u8881\u7d2b\u8863\u7b11\u9053\uff1a\u201c\u867d\u7136\u80dc\u4e86\u6613\u5409\uff0c\u4f46\u4ed6\u95e8\u4e0b\u5f1f\u5b50\u4e0d\u670d\uff0c\u8fd9\u4e5d\u9f99\u6d3e\u7684\u638c\u95e8\u4eba\uff0c\u5b9e\u5728\u662f\u5f53\u5f97\u5341\u5206\u52c9\u5f3a\u7684\u3002\u81f3\u4e8e\u5c11\u6797\u3001\u6b66\u5f53\u3001\u592a\u6781\u8fd9\u4e9b\u5927\u95e8\u6d3e\u7684\u638c\u95e8\u4eba\uff0c\u6211\u662f\u4e0d\u6562\u53bb\u62a2\u7684\u3002\u518d\u6536\u5341\u5bb6\u7834\u94dc\u70c2\u94c1\uff0c\u4e5f\u5c31\u591f\u5566\u3002\u201d\u80e1\u6590\u4f38\u4e86\u4f38\u820c\u5934\uff0c\u9053\uff1a\u201c\u6b66\u6797\u5341\u4e09\u5bb6\u603b\u638c\u95e8\uff0c\u8fd9\u540d\u5934\u53ef\u591f\u5a01\u98ce\u554a\u3002\u201d \u3000\u3000\u8881\u7d2b\u8863\u7b11\u9053\uff1a\u201c\u80e1\u5927\u54e5\uff0c\u4f60\u6b66\u827a\u8fd9\u822c\u5f3a\uff0c\u4f55\u4e0d\u4e5f\u62a2\u51e0\u5bb6\u638c\u95e8\u4eba\u505a\u505a\uff1f\u54b1\u4eec\u4e00\u8def\u6536\u8fc7\u53bb\u3002\u4f60\u6536\u4e00\u5bb6\uff0c\u6211\u6536\u4e00\u5bb6\uff0c\u8f6e\u6d41\u7740\u5f20\u7f57\u3002\u5230\u5f97\u5317\u4eac\uff0c\u6211\u662f\u5341\u4e09\u5bb6\u603b\u638c\u95e8\uff0c\u4f60\u4e5f\u662f\u5341\u4e09\u5bb6\u603b\u638c\u95e8\u3002 \u3000\u3000\u54b1\u54e5\u513f\u4fe9\u4e00\u540c\u53bb\u53c2\u4e0e\u798f\u5927\u5e05\u7684\u4ec0\u4e48\u5929\u4e0b\u638c\u95e8\u4eba\u5927\u4f1a\uff0c\u5c82\u4e0d\u6709\u8da3\uff1f\u201d \u3000\u3000\u80e1\u6590\u8fde\u8fde\u6447\u624b\uff0c\u9053\uff1a\u201c\u6211\u53ef\u6ca1\u8fd9\u4e2a\u80c6\u5b50\uff0c\u66f4\u6ca1\u59d1\u5a18\u7684\u597d\u6b66\u827a\u3002\u591a\u534a\u638c\u95e8\u4eba\u534a\u4e2a\u4e5f\u6ca1\u62a2\u7740\uff0c\u4fbf\u7ed9\u4eba\u5bb6\u4e00\u62db\u2018\u5415\u6d1e\u5bbe\u63a8\u72d7\u2019\uff0c\u6454\u5728\u6cb3\u91cc\uff0c\u53d8\u6210\u4e86\u4e00\u6761\u62d6\u6ce5\u5e26\u6c34\u7684\u843d\u6c34\u72d7\uff01\u82e5\u662f\u5355\u505a\u6ce5\u9cc5\u6d3e\u638c\u95e8\u4eba\u5462\uff0c\u53ef\u53c8\u4e0d\u5927\u5149\u5f69\u3002\u201d\u8881\u7d2b\u8863\u7b11\u5f2f\u4e86\u8170\uff0c\u62b1\u62f3\u9053\uff1a\u201c\u80e1\u5927\u54e5\uff0c\u5c0f\u59b9\u8fd9\u91cc\u8ddf\u4f60\u966a\u4e0d\u662f\u5566\u3002\u201d\u80e1\u6590\u62b1\u62f3\u8fd8\u793c\uff0c\u4e00\u672c\u6b63\u7ecf\u5730\u9053\uff1a\u201c\u4e09\u5bb6\u5927\u638c\u95e8\u8001\u7237\uff0c\u5c0f\u7684\u53ef\u4e0d\u6562\u5f53\u3002\u201d \u3000\u3000\u8881\u7d2b\u8863\u89c1\u4ed6\u6a21\u6837\u8001\u5b9e\uff0c\u8bf4\u8bdd\u5374\u751a\u662f\u98ce\u8da3\uff0c\u5fc3\u4e2d\u66f4\u589e\u4e86\u51e0\u5206\u559c\u6b22\uff0c\u7b11\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u8d75\u534a\u5c71\u90a3\u8001\u5c0f\u5b50\u5938\u4f60\u4e0d\u9519\uff01\u201d\u80e1\u6590\u5fc3\u4e2d\u5bf9\u8d75\u534a\u5c71\u4e00\u76f4\u5ff5\u5ff5\u4e0d\u5fd8\uff0c\u5fd9\u95ee\uff1a\u201c\u8d75\u4e09\u54e5\u600e\u4e48\u5566\uff1f\u4ed6\u8ddf\u4f60\u8bf4\u4ec0\u4e48\u6765\u7740\uff1f\u201d\u8881\u7d2b\u8863\u7b11\u9053\uff1a\u201c\u4f60\u8ffd\u5f97\u4e0a\u6211\uff0c\u4fbf\u8ddf\u4f60\u8bf4\u3002\u201d\u4f38\u8db3\u5c16\u5728\u9a6c\u8179\u4e0a\u8f7b\u8f7b\u4e00\u78b0\u3002 \u3000\u3000\u80e1\u6590\u5fc3\u60f3\u4f60\u8fd9\u767d\u9a6c\u4e00\u8dd1\uff0c\u6211\u54ea\u91cc\u8fd8\u8ffd\u5f97\u4e0a\uff1f\u773c\u89c1\u767d\u9a6c\u540e\u817f\u4e00\u6491\uff0c\u4fbf\u8981\u53d1\u529b\uff0c\u6025\u5fd9\u817e\u8eab\u8dc3\u8d77\uff0c\u5de6\u638c\u5728\u767d\u9a6c\u81c0\u4e0a\u4e00\u6309\uff0c\u8eab\u5b50\u5df2\u843d\u5728\u767d\u9a6c\u7684\u9a6c\u80cc\uff0c\u6b63\u597d\u5750\u5728\u8881\u7d2b\u8863\u8eab\u540e\u3002\u90a3\u767d\u9a6c\u80cc\u4e0a\u591a\u4e86\u4e00\u4eba\uff0c\u7adf\u662f\u6beb\u4e0d\u5728\u610f\uff0c\u4ecd\u662f\u653e\u5f00\u56db\u8e44\uff0c\u8ffd\u98ce\u9010\u7535\u822c\u5411\u524d\u98de\u5954\u3002\u90a3\u5339\u9752\u9a6c\u5728\u540e\u8ddf\u7740\uff0c\u867d\u7136\u7a7a\u978d\uff0c\u4f46\u7247\u523b\u4e4b\u95f4\uff0c\u5df2\u4e0e\u767d\u9a6c\u76f8\u8ddd\u6570\u5341\u4e08\u4e4b\u9065\u3002 \u3000\u3000\u8881\u7d2b\u8863\u5fae\u5fae\u95fb\u5230\u80cc\u540e\u80e1\u6590\u8eab\u4e0a\u7684\u7537\u5b50\u6c14\u606f\uff0c\u8138\u4e0a\u4e00\u70ed\uff0c\u5f85\u8981\u8bf4\u8bdd\uff0c\u5374\u53c8\u4f4f\u53e3\u3002\u5954\u9a70\u4e86\u4e00\u9635\uff0c\u731b\u542c\u5f97\u534a\u7a7a\u4e2d\u4e00\u4e2a\u9739\u96f3\uff0c\u62ac\u5934\u4e00\u671b\uff0c\u4e4c\u4e91\u5df2\u5c06\u534a\u8fb9\u5929\u906e\u6ca1\u3002\u6b64\u65f6\u6b63\u5f53\u76db\u6691\uff0c\u9635\u96e8\u8bf4\u6765\u4fbf\u6765\uff0c\u5979\u4e00\u63d0\u9a6c\u7f30\uff0c\u767d\u9a6c\u5954\u5f97\u66f4\u52a0\u5feb\u4e86\u3002 \u3000\u3000\u4e0d\u5230\u4e00\u76cf\u8336\u65f6\u5206\uff0c\u897f\u98ce\u8f6c\u52b2\uff0c\u9ec4\u8c46\u5927\u7684\u96e8\u70b9\u5df2\u6d12\u5c06\u4e0b\u6765\u3002 \u3000\u3000\u4e00\u773c\u671b\u53bb\uff0c\u5927\u8def\u65c1\u5e76\u65e0\u623f\u5c4b\uff0c\u53ea\u5de6\u8fb9\u5c71\u5773\u4e2d\u9732\u51fa\u4e00\u89d2\u9ec4\u5899\uff0c\u8881\u7d2b\u8863\u7eb5\u9a6c\u9a70\u8fd1\uff0c\u539f\u6765\u662f\u4e00\u5ea7\u53e4\u5e99\uff0c\u7834\u533e\u4e0a\u5199\u7740\u201c\u6e58\u5983\u795e\u7960\u201d\u56db\u4e2a\u5927\u5b57\uff0c\u6ce5\u91d1\u5265\u843d\uff0c\u663e\u5df2\u65e5\u4e45\u5931\u4fee\u3002 \u3000\u3000\u80e1\u6590\u8dc3\u4e0b\u9a6c\u6765\uff0c\u63a8\u5f00\u5e99\u95e8\uff0c\u987e\u4e0d\u5f97\u7ec6\u770b\uff0c\u5148\u5c06\u767d\u9a6c\u62c9\u4e86\u8fdb\u53bb\u3002\u8fd9\u65f6\u7a7a\u4e2d\u7126\u96f7\u4e00\u4e2a\u63a5\u7740\u4e00\u4e2a\uff0c\u95ea\u7535\u8fde\u6643\uff0c\u8881\u7d2b\u8863\u867d\u7136\u6b66\u827a\u9ad8\u5f3a\uff0c\u7981\u4e0d\u4f4f\u8138\u4e0a\u9732\u51fa\u754f\u60e7\u4e4b\u8272\u3002 \u3000\u3000\u80e1\u6590\u5230\u540e\u6bbf\u53bb\u77a7\u4e86\u4e00\u4e0b\uff0c\u5e99\u4e2d\u4eba\u5f71\u4e5f\u65e0\uff0c\u56de\u5230\u524d\u6bbf\uff0c\u8bf4\u9053\uff1a\u201c\u8fd8\u662f\u540e\u6bbf\u5e72\u51c0\u4e9b\u3002\u201d\u627e\u4e86\u4e9b\u7a3b\u8349\uff0c\u6253\u626b\u51fa\u534a\u8fb9\u5730\u65b9\uff0c\u9053\uff1a\u201c\u8fd9\u96e8\u4e0b\u4e0d\u957f\uff0c\u5f85\u4f1a\u96e8\u6536\u4e86\uff0c\u4eca\u5929\u51c6\u80fd\u8d76\u5230\u957f\u6c99\u3002\u201d \u3000\u3000\u8881\u7d2b\u8863\u201c\u55ef\u201d\u4e86\u4e00\u58f0\uff0c\u4e0d\u518d\u8bf4\u8bdd\u3002\u4e24\u4eba\u672c\u6765\u4e00\u76f4\u8bf4\u8bf4\u7b11\u7b11\uff0c\u4f46\u81ea\u540c\u9a91\u5171\u9a70\u4e00\u9635\u4e4b\u540e\uff0c\u8881\u7d2b\u8863\u5fc3\u4e2d\u5fae\u611f\u5f02\u6837\uff0c\u77a7\u7740\u80e1\u6590\uff0c\u4e0d\u81ea\u7981\u5730\u6709\u4e9b\u817c\u8146\uff0c\u6709\u4e9b\u5c34\u5c2c\u3002 \u3000\u3000\u4e24\u4eba\u5e76\u80a9\u5750\u7740\uff0c\u7a81\u7136\u95f4\u540c\u65f6\u8f6c\u8fc7\u5934\u6765\uff0c\u76ee\u5149\u76f8\u89e6\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u5404\u81ea\u628a\u5934\u8f6c\u4e86\u5f00\u53bb\u3002 \u3000\u3000\u9694\u4e86\u4e00\u4f1a\uff0c\u80e1\u6590\u95ee\u9053\uff1a\u201c\u8d75\u4e09\u54e5\u8eab\u5b50\u5b89\u597d\u5427\uff1f\u201d\u8881\u7d2b\u8863\u9053\uff1a\u201c\u597d\u554a\uff01\u4ed6\u4f1a\u6709\u4ec0\u4e48\u4e0d\u597d\uff1f\u201d\u80e1\u6590\u9053\uff1a\u201c\u4ed6\u5728\u54ea\u91cc\uff1f\u6211\u60f3\u5ff5\u4ed6\u5f97\u7d27\uff0c\u771f\u60f3\u89c1\u89c1\u4ed6\u3002\u201d\u8881\u7d2b\u8863\u9053\uff1a\u201c\u90a3\u4f60\u5230\u56de\u7586\u53bb\u554a\u3002\u53ea\u8981\u4f60\u4e0d\u6b7b\uff0c\u4ed6\u4e0d\u6b7b\uff0c\u51c6\u80fd\u89c1\u7740\u3002\u201d \u3000\u3000\u80e1\u6590\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u662f\u521a\u4ece\u56de\u7586\u6765\u5427\uff1f\u201d\u8881\u7d2b\u8863\u56de\u7738\u5fae\u7b11\uff0c\u9053\uff1a\u201c\u662f\u554a\u3002\u4f60\u77a7\u6211\u8fd9\u526f\u6a21\u6837\u50cf\u4e0d\u50cf\uff1f\u201d\u80e1\u6590\u6447\u5934\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\u3002\u6211\u5148\u524d\u53ea\u9053\u56de\u7586\u662f\u6c99\u6f20\u8352\u829c\u4e4b\u5730\uff0c\u54ea\u77e5\u7adf\u6709\u59d1\u5a18\u8fd9\u822c\u7f8e\u5973\u3002\u201d\u8881\u7d2b\u8863\u8138\u4e0a\u4e00\u7ea2\uff0c\u201c\u5478\u201d\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u778e\u8bf4\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u80e1\u6590\u4e00\u8a00\u65e2\u51fa\uff0c\u5fc3\u4e2d\u5fae\u89c9\u540e\u6094\uff0c\u6697\u60f3\u5b64\u7537\u5be1\u5973\u5728\u8fd9\u67af\u5e99\u4e4b\u4e2d\uff0c\u8bf4\u8bdd\u53ef\u5343\u4e07\u8f7b\u6d6e\u4e0d\u5f97\uff0c\u4e8e\u662f\u5c94\u5f00\u8bdd\u9898\uff0c\u95ee\u9053\uff1a\u201c\u798f\u5927\u5e05\u5f00\u8fd9\u4e2a\u5929\u4e0b\u638c\u95e8\u4eba\u5927\u4f1a\uff0c\u5230\u5e95\u662f\u4e3a\u4e86\u4ec0\u4e48\uff0c\u59d1\u5a18\u80fd\u89c1\u544a\u4e48\uff1f\u201d \u3000\u3000\u8881\u7d2b\u8863\u542c\u4ed6\u8bed\u6c14\u7a81\u8f6c\u7aef\u5e84\uff0c\u4e0d\u7981\u5411\u4ed6\u671b\u4e86\u4e00\u773c\uff0c\u8bf4\u9053\uff1a\u201c\u4ed6\u738b\u516c\u8d35\u4eba\uff0c\u5403\u9971\u4e86\u996d\u6ca1\u4e8b\u5e72\uff0c\u627e\u4e9b\u6b66\u6797\u597d\u624b\u6d88\u9063\u6d88\u9063\uff0c\u8fd8\u4e0d\u8ddf\u6597\u9e21\u6597\u87cb\u87c0\u4e00\u822c\u3002\u53ea\u53ef\u53f9\u5929\u4e0b\u65e0\u6570\u6b66\u5b66\u9ad8\u624b\uff0c\u53d7\u4e86\u4ed6\u7684\u611a\u5f04\uff0c\u7adf\u4e0d\u81ea\u77e5\u3002\u201d \u3000\u3000\u80e1\u6590\u4e00\u62cd\u5927\u817f\uff0c\u5927\u58f0\u9053\uff1a\u201c\u59d1\u5a18\u8bf4\u7684\u4e00\u70b9\u4e5f\u4e0d\u9519\u3002\u5982\u6b64\u9ad8\u89c1\uff0c\u4ee4\u6211\u597d\u751f\u4f69\u670d\u3002\u539f\u6765\u59d1\u5a18\u4e00\u8def\u62a2\u90a3\u638c\u95e8\u4eba\u4e4b\u4f4d\uff0c\u662f\u7ed9\u8fd9\u4e2a\u798f\u5927\u5e05\u6363\u4e71\u6765\u7740\u3002\u201d\u8881\u7d2b\u8863\u7b11\u9053\uff1a\u201c\u4e0d\u5982\u54b1\u4e8c\u4eba\u9f50\u5fc3\u5408\u529b\uff0c\u628a\u5929\u4e0b\u638c\u95e8\u4eba\u4e4b\u4f4d\u5148\u62a2\u4ed6\u4e00\u534a\u3002\u8fd9\u4e48\u4e00\u6765\uff0c\u798f\u5927\u5e05\u90a3\u5927\u4f1a\u4fbf\u4e03\u96f6\u516b\u843d\uff0c\u4e0d\u6210\u6c14\u5019\u3002\u54b1\u4eec\u518d\u5230\u4f1a\u4e0a\u7ed9\u4ed6\u4e00\u95f9\uff0c\u53eb\u4ed6\u4ece\u6b64\u4e0d\u6562\u5c0f\u89d1\u5929\u4e0b\u6b66\u5b66\u4e4b\u58eb\u3002\u201d\u80e1\u6590\u8fde\u8fde\u9f13\u638c\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u5c31\u8fd9\u4e48\u529e\u3002 \u3000\u3000\u59d1\u5a18\u9886\u5934\uff0c\u6211\u8ddf\u7740\u4f60\u51fa\u70b9\u5fae\u529b\u3002\u201d\u8881\u7d2b\u8863\u9053\uff1a\u201c\u4f60\u6b66\u529f\u8fdc\u80dc\u4e8e\u6211\uff0c\u4f55\u5fc5\u5ba2\u6c14\u3002\u201d \u3000\u3000\u4e24\u4eba\u8bf4\u5f97\u9ad8\u5174\uff0c\u5374\u89c1\u5927\u96e8\u59cb\u7ec8\u4e0d\u6b62\uff0c\u53cd\u800c\u8d8a\u4e0b\u8d8a\u5927\uff0c\u5e99\u540e\u662f\u4e00\u6761\u5c71\u6da7\uff0c\u5c71\u6c34\u51b2\u5c06\u4e0b\u6765\uff0c\u8f70\u8f70\u9686\u9686\uff0c\u7adf\u4f3c\u6f6e\u6c34\u4e00\u822c\u3002\u90a3\u53e4\u5e99\u5e74\u4e45\u7834\u8d25\uff0c\u5230\u5904\u6f0f\u6c34\u3002\u80e1\u6590\u4e0e\u8881\u7d2b\u8863\u7f29\u5728\u5c4b\u89d2\u4e4b\u4e2d\uff0c\u773c\u89c1\u5929\u8272\u6e10\u9ed1\uff0c\u4e4c\u4e91\u7adf\u8981\u4f3c\u538b\u5230\u5934\u9876\u4e00\u822c\uff0c\u770b\u6765\u5df2\u662f\u65e0\u6cd5\u4e0a\u8def\u3002 \u3000\u3000\u80e1\u6590\u5230\u7076\u95f4\u627e\u4e86\u4e9b\u67f4\u679d\uff0c\u5728\u5730\u4e0b\u70b9\u71c3\u4e86\u4f5c\u706f\uff0c\u7b11\u9053\uff1a\u201c\u5927\u96e8\u4e0d\u6b62\uff0c\u54b1\u4eec\u53ea\u597d\u6328\u4e00\u665a\u997f\u4e86\u3002\u201d \u3000\u3000\u706b\u5149\u6620\u5728\u8881\u7d2b\u8863\u8138\u4e0a\uff0c\u7ea2\u7ea2\u7684\u6108\u589e\u5a07\u8273\u3002\u5979\u81ea\u56de\u7586\u4e07\u91cc\u4e1c\u6765\uff0c\u5728\u8352\u5c71\u91ce\u5730\u6b47\u5bbf\u89c6\u4f5c\u5bfb\u5e38\uff0c\u4f46\u662f\u5b64\u8eab\u4e0e\u4e00\u4e2a\u9752\u5e74\u7537\u5b50\u5171\u5904\u53e4\u5e99\uff0c\u5374\u662f\u4ece\u6240\u672a\u6709\u7684\u7ecf\u5386\uff0c\u5fc3\u5934\u4e0d\u7981\u6709\u4e00\u80a1\u8bf4\u4e0d\u51fa\u7684\u6ecb\u5473\u3002 \u3000\u3000\u80e1\u6590\u627e\u4e9b\u7a3b\u8349\uff0c\u5728\u795e\u575b\u4e0a\u94fa\u597d\uff0c\u53c8\u5728\u8fdc\u79bb\u795e\u575b\u7684\u5730\u4e0b\u5806\u4e86\u4e9b\u7a3b\u8349\uff0c\u7b11\u9053\uff1a\u201c\u5415\u6d1e\u5bbe\u7761\u5929\u4e0a\uff0c\u843d\u6c34\u72d7\u7761\u5730\u4e0b\u3002\u201d\u8bf4\u7740\u5728\u5730\u4e0b\u7a3b\u8349\u5806\u91cc\u4e00\u8eba\uff0c\u7ffb\u8eab\u5411\u58c1\uff0c\u95ed\u4e0a\u4e86\u773c\u775b\u3002\u8881\u7d2b\u8863\u6697\u6697\u70b9\u5934\uff0c\u5fc3\u60f3\u4ed6\u679c\u7136\u662f\u4e2a\u5b88\u793c\u541b\u5b50\uff0c\u7b11\u9053\uff1a\u201c\u843d\u6c34\u72d7\uff0c\u660e\u5929\u89c1\u3002\u201d\u8dc3\u4e0a\u4e86\u795e\u575b\u3002 \u3000\u3000\u5979\u7761\u4e0b\u540e\u5fc3\u795e\u4e0d\u5b9a\uff0c\u8033\u542c\u7740\u6025\u96e8\u6253\u5728\u5c4b\u74e6\u4e4b\u4e0a\uff0c\u54d7\u5566\u5566\u7684\u4e71\u54cd\uff0c\u76f4\u8fc7\u4e86\u534a\u4e2a\u591a\u65f6\u8fb0\uff0c\u624d\u8499\u80e7\u7761\u53bb\u3002 \u3000\u3000\u7761\u5230\u534a\u591c\uff0c\u9690\u9690\u542c\u5f97\u6709\u9a6c\u8e44\u4e4b\u58f0\uff0c\u6e10\u6e10\u5954\u8fd1\uff0c\u8881\u7d2b\u8863\u7ffb\u8eab\u5750\u8d77\uff0c\u80e1\u6590\u4e5f\u5df2\u542c\u5230\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5415\u6d1e\u5bbe\uff0c\u6709\u4eba\u6765\u5566\u3002\u201d \u3000\u3000\u53ea\u542c\u9a6c\u8e44\u58f0\u8d8a\u5954\u8d8a\u8fd1\uff0c\u8fd8\u5939\u6742\u7740\u8f66\u8f6e\u4e4b\u58f0\uff0c\u80e1\u6590\u5fc3\u60f3\uff1a \u3000\u3000\u201c\u8fd9\u573a\u5927\u96e8\u81ea\u4e0b\u5348\u843d\u8d77\uff0c\u4e2d\u95f4\u4e00\u76f4\u4e0d\u505c\uff0c\u600e\u5730\u6709\u4eba\u5192\u7740\u5927\u96e8\uff0c\u8fde\u591c\u8d76\u8def\uff1f\u201d\u53ea\u542c\u5f97\u8f66\u9a6c\u5230\u4e86\u5e99\u5916\uff0c\u4e00\u9f50\u505c\u6b47\u3002\u8881\u7d2b\u8863\u9053\uff1a\u201c\u4ed6\u4eec\u8981\u8fdb\u5e99\u6765\uff01\u201d\u4ece\u795e\u575b\u8dc3\u4e0b\uff0c\u5750\u5728\u80e1\u6590\u8eab\u8fb9\u3002 \u3000\u3000\u679c\u7136\u5e99\u95e8\u5440\u7684\u4e00\u58f0\u63a8\u5f00\u4e86\uff0c\u8f66\u9a6c\u90fd\u7275\u5230\u4e86\u524d\u6bbf\u5eca\u4e0b\u3002\u8ddf\u7740\u4e24\u540d\u8f66\u592b\u624b\u6301\u706b\u628a\uff0c\u8d70\u5230\u540e\u6bbf\uff0c\u89c1\u5230\u80e1\u8881\u4e8c\u4eba\uff0c\u9053\uff1a\u201c\u8fd9\u513f\u6709\u4eba\uff0c\u6211\u4eec\u5728\u524d\u6bbf\u6b47\u3002\u201d\u5f53\u5373\u56de\u4e86\u51fa\u53bb\u3002\u53ea\u542c\u5f97\u524d\u6bbf\u4eba\u58f0\u5608\u6742\uff0c\u7ea6\u6709\u4e8c\u5341\u6765\u4eba\u3002\u6709\u7684\u5288\u67f4\u751f\u706b\uff0c\u6709\u7684\u6d17\u7c73\u716e\u996d\uff0c\u8bf4\u7684\u8bdd\u5927\u90fd\u662f\u5e7f\u4e1c\u53e3\u97f3\u3002\u4e71\u4e86\u4e00\u9635\uff0c\u6e10\u6e10\u5b89\u9759\u4e0b\u6765\u3002 \u3000\u3000\u5ffd\u542c\u4e00\u4eba\u8bf4\u9053\uff1a\u201c\u4e0d\u7528\u94fa\u5e8a\uff0c\u5403\u8fc7\u996d\u540e\uff0c\u4e0d\u7ba1\u96e8\u5927\u96e8\u5c0f\uff0c\u8fd8\u662f\u4e58\u9ed1\u8d76\u8def\u3002\u201d\u80e1\u6590\u542c\u4e86\u8fd9\u53e3\u97f3\uff0c\u5fc3\u4e2d\u4e00\u6123\uff0c\u8fd9\u65f6\u540e\u6bbf\u70b9\u7684\u67f4\u679d\u5c1a\u672a\u7184\u706d\uff0c\u706b\u5149\u4e0b\u53ea\u89c1\u8881\u7d2b\u8863\u4e5f\u662f\u5fae\u5fae\u53d8\u8272\u3002 \u3000\u3000\u53c8\u542c\u524d\u6bbf\u53e6\u4e00\u4eba\u9053\uff1a\u201c\u8001\u7237\u5b50\u4e5f\u592a\u628a\u7ec6\u5566\uff0c\u8fd9\u4e48\u5927\u96e8\u2026\u2026\u201d\u8fd9\u65f6\u96e8\u58f0\u76f4\u54cd\uff0c\u628a\u4ed6\u4e0b\u9762\u7684\u8bdd\u58f0\u6df9\u6ca1\u4e86\u3002\u5148\u524d\u8bf4\u8bdd\u7684\u90a3\u4eba\u5374\u662f\u4e2d\u6c14\u5145\u6c9b\uff0c\u8bed\u97f3\u6d2a\u4eae\uff0c\u58f0\u97f3\u9694\u7740\u9662\u5b50\uff0c\u5728\u5927\u96e8\u4e2d\u4ecd\u662f\u6e05\u6e05\u695a\u695a\u5730\u4f20\u6765\uff1a\u201c\u9ed1\u591c\u4e4b\u4e2d\u53c8\u6709\u5927\u96e8\uff0c\u6b63\u597d\u8d76\u8def\u3002\u83ab\u8981\u8d2a\u5f97\u4e00\u65f6\u5b89\u9038\uff0c\u5374\u628a\u5168\u5bb6\u6027\u547d\u9001\u4e86\uff0c\u6b64\u5904\u79bb\u5927\u8def\u4e0d\u8fdc\uff0c\u522b\u9b3c\u4f7f\u795e\u5dee\u5730\u649e\u5728\u5c0f\u8d3c\u624b\u91cc\u3002\u201d \u3000\u3000\u542c\u5230\u6b64\u5904\uff0c\u80e1\u6590\u518d\u65e0\u6000\u7591\uff0c\u5fc3\u4e0b\u5927\u559c\uff0c\u6697\u9053\uff1a\u201c\u5f53\u771f\u662f\u9b3c\u4f7f\u795e\u5dee\uff0c\u649e\u5728\u6211\u624b\u91cc\u3002\u201d\u4f4e\u58f0\u9053\uff1a\u201c\u5415\u6d1e\u5bbe\uff0c\u5916\u8fb9\u53c8\u662f\u4e00\u4f4d\u638c\u95e8\u4eba\u5230\u4e86\uff0c\u8fd9\u6b21\u5c31\u8ba9\u6211\u6765\u62a2\u3002\u201d\u8881\u7d2b\u8863\u201c\u55ef\u201d\u4e86\u4e00\u58f0\uff0c\u5374\u4e0d\u8bf4\u8bdd\u3002\u80e1\u6590\u89c1\u5979\u5e76\u65e0\u559c\u5bb9\uff0c\u5fc3\u4e2d\u5fae\u611f\u5947\u602a\uff0c\u4e8e\u662f\u7d27\u4e86\u7d27\u8170\u5e26\uff0c\u5c06\u5355\u5200\u63d2\u5728\u8170\u5e26\u91cc\uff0c\u5927\u8e0f\u6b65\u8d70\u5411\u524d\u6bbf\u3002 \u3000\u3000\u53ea\u89c1\u4e1c\u53a2\u8fb9\u4e03\u516b\u4e2a\u4eba\u5e2d\u5730\u800c\u5750\uff0c\u5176\u4e2d\u4e00\u4eba\u8eab\u6750\u9ad8\u5927\uff0c\u5750\u5728\u5730\u4e0b\uff0c\u6bd4\u65c1\u4eba\u9ad8\u51fa\u4e86\u534a\u4e2a\u5934\uff0c\u8eab\u5b50\u5411\u5916\u3002\u80e1\u6590\u4e00\u89c1\u4ed6\u7684\u4fa7\u5f71\uff0c\u8ba4\u5f97\u4ed6\u6b63\u662f\u4f5b\u5c71\u9547\u7684\u5927\u6076\u9738\u51e4\u5929\u5357\u3002\u53ea\u89c1\u4ed6\u5c06\u90a3\u6761\u9ec4\u91d1\u68cd\u501a\u5728\u8eab\u4e0a\uff0c\u62ac\u773c\u671b\u5929\uff0c\u5446\u5446\u51fa\u795e\uff0c\u4e0d\u77e5\u662f\u5728\u6000\u5ff5\u4f5b\u5c71\u9547\u90a3\u4e00\u4efd\u504c\u5927\u7684\u5bb6\u4e1a\uff0c\u8fd8\u662f\u5728\u7b79\u5212\u5bf9\u4ed8\u654c\u4eba\u3001\u91cd\u632f\u96c4\u98ce\u7684\u65b9\u7b56\uff1f\u80e1\u6590\u4ece\u795e\u9f9b\u540e\u7684\u6697\u5f71\u4e2d\u51fa\u6765\uff0c\u524d\u6bbf\u8bf8\u4eba\u5168\u6ca1\u5728\u610f\u3002 \u3000\u3000\u897f\u8fb9\u6bbf\u4e0a\u751f\u7740\u597d\u5927\u4e00\u5806\u67f4\u706b\uff0c\u706b\u4e0a\u540a\u7740\u4e00\u53e3\u5927\u94c1\u9505\uff0c\u6b63\u5728\u716e\u996d\u3002\u80e1\u6590\u8d70\u4e0a\u524d\u53bb\uff0c\u98de\u8d77\u4e00\u817f\uff0c\u545b\u5577\u5577\u4e00\u58f0\u54cd\u4eae\uff0c\u5c06\u90a3\u53e3\u94c1\u9505\u8e22\u5f97\u98de\u5165\u9662\u4e2d\uff0c\u767d\u7c73\u6492\u4e86\u4e00\u5730\u3002 \u3000\u3000\u4f17\u4eba\u4e00\u60ca\uff0c\u4e00\u9f50\u8f6c\u5934\u3002\u51e4\u5929\u5357\u3001\u51e4\u4e00\u9e23\u7236\u5b50\u7b49\u8ba4\u5f97\u4ed6\u7684\uff0c\u65e0\u4e0d\u53d8\u8272\u3002\u7a7a\u624b\u7684\u4eba\u5fd9\u62a2\u7740\u53bb\u6284\u5175\u5203\u3002 \u3000\u3000\u80e1\u6590\u89c1\u4e86\u51e4\u5929\u5357\u90a3\u5f20\u767d\u767d\u80d6\u80d6\u7684\u8138\u819b\uff0c\u60f3\u8d77\u5317\u5e1d\u5e99\u4e2d\u953a\u963f\u56db\u5168\u5bb6\u60e8\u6b7b\u7684\u60c5\u72b6\uff0c\u6c14\u6781\u53cd\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u51e4\u8001\u7237\uff0c\u8fd9\u91cc\u662f\u6e58\u5983\u5e99\uff0c\u98ce\u96c5\u5f97\u884c\u554a\u3002\u201d \u3000\u3000\u51e4\u5929\u5357\u6740\u4e86\u953a\u963f\u56db\u4e00\u5bb6\u4e09\u53e3\uff0c\u7acb\u5373\u6bc1\u5bb6\u51fa\u8d70\uff0c\u4e00\u8def\u4e0a\u663c\u5bbf\u591c\u884c\uff0c\u5c3d\u62e3\u504f\u50fb\u5c0f\u9053\u884c\u8d70\u3002\u4ed6\u505a\u4e8b\u4e5f\u771f\u5e72\u51c0\u5229\u843d\uff0c\u80e1\u6590\u867d\u7136\u673a\u7075\uff0c\u6bd5\u7adf\u6c5f\u6e56\u4e0a\u9605\u5386\u751a\u6d45\uff0c\u6ca1\u80fd\u67e5\u51fa\u4e1d\u6beb\u75d5\u8ff9\u3002\u8fd9\u65e5\u82e5\u975e\u906d\u9047\u5927\u96e8\uff0c\u9634\u5dee\u9633\u9519\uff0c\u51b3\u4e0d\u4f1a\u5728\u8fd9\u53e4\u5e99\u4e2d\u76f8\u9022\u3002 \u3000\u3000\u51e4\u5929\u5357\u773c\u89c1\u5bf9\u5934\u7a81\u7136\u51fa\u73b0\uff0c\u4e0d\u7531\u5f97\u5fc3\u4e2d\u4e00\u5bd2\uff0c\u6697\u9053\uff1a\u201c\u770b\u6765\u8fd9\u6e58\u5983\u5e99\u662f\u51e4\u67d0\u5f52\u5929\u4e4b\u5904\u4e86\u3002\u201d\u4f46\u8138\u4e0a\u4ecd\u662f\u5341\u5206\u9547\u5b9a\uff0c\u7f13\u7f13\u7ad9\u8d77\u8eab\u6765\uff0c\u5411\u513f\u5b50\u62db\u4e86\u62db\u624b\uff0c\u53eb\u4ed6\u8d70\u8fd1\u8eab\u53bb\uff0c\u6709\u8bdd\u5429\u5490\u3002 \u3000\u3000\u80e1\u6590\u6a2a\u5200\u5835\u4f4f\u5e99\u95e8\uff0c\u7b11\u9053\uff1a\u201c\u51e4\u8001\u7237\uff0c\u4e5f\u4e0d\u7528\u5631\u5490\u4ec0\u4e48\u3002 \u3000\u3000\u4f60\u6740\u953a\u963f\u56db\u4e00\u5bb6\uff0c\u6211\u4fbf\u6740\u4f60\u51e4\u8001\u7237\u4e00\u5bb6\u3002\u54b1\u4eec\u4e00\u5200\u4e00\u4e2a\uff0c\u51b3\u4e0d\u542b\u7cca\u3002\u4f60\u51e4\u8001\u7237\u4e0e\u4f17\u4e0d\u540c\uff0c\u7559\u5728\u6700\u540e\uff0c\u514d\u5f97\u4f60\u653e\u5fc3\u4e0d\u4e0b\uff0c\u8fd8\u6015\u4e16\u4e0a\u6709\u4f60\u5bb6\u4eba\u5269\u7740\u3002\u201d \u3000\u3000\u51e4\u5929\u5357\u80cc\u810a\u4e0a\u4e00\u51c9\uff0c\u60f3\u4e0d\u5230\u6b64\u4eba\u5c0f\u5c0f\u5e74\u7eaa\uff0c\u505a\u4e8b\u4e5f\u5c45\u7136\u5982\u6b64\u8fa3\u624b\uff0c\u5c06\u9ec4\u91d1\u68cd\u4e00\u6446\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u6c49\u4e00\u4eba\u505a\u4e8b\u4e00\u8eab\u5f53\uff0c\u591a\u8bf4\u5e9f\u8bdd\u5e72\u4e48\uff1f\u4f60\u8981\u51e4\u67d0\u7684\u6027\u547d\uff0c\u62ff\u53bb\u4fbf\u662f\u3002\u201d\u8bf4\u7740\u62a2\u4e0a\u4e00\u6b65\uff0c\u547c\u7684\u4e00\u58f0\uff0c\u4e00\u62db\u201c\u6402\u5934\u76d6\u9876\u201d\uff0c\u4fbf\u5f80\u80e1\u6590\u8111\u95e8\u51fb\u4e0b\uff0c\u5de6\u624b\u5374\u5411\u540e\u6025\u6325\uff0c\u793a\u610f\u513f\u5b50\u5feb\u8d70\u3002 \u3000\u3000\u51e4\u4e00\u9e23\u77e5\u9053\u7236\u4eb2\u51b3\u4e0d\u662f\u654c\u4eba\u5bf9\u624b\uff0c\u5371\u6025\u4e4b\u9645\u54ea\u80af\u81ea\u5df1\u9003\u547d\uff1f\u5927\u58f0\u53eb\u9053\uff1a\u201c\u5927\u4f19\u513f\u9f50\u4e0a\uff01\u201d\u53ea\u76fc\u501a\u591a\u4e3a\u80dc\uff0c\u8bf4\u7740\u633a\u8d77\u5355\u5200\uff0c\u7eb5\u5230\u4e86\u80e1\u6590\u5de6\u4fa7\u3002\u968f\u7740\u51e4\u5929\u5357\u51fa\u4ea1\u7684\u5bb6\u4eba\u4eb2\u4fe1\u3001\u5f1f\u5b50\u95e8\u4eba\uff0c\u4e00\u5171\u6709\u5341\u516d\u4e03\u4eba\uff0c\u5176\u4e2d\u5927\u534a\u5747\u4f1a\u6b66\u827a\uff0c\u542c\u5f97\u51e4\u4e00\u9e23\u547c\u53eb\uff0c\u6709\u516b\u4e5d\u4eba\u624b\u6267\u5175\u5203\uff0c\u56f4\u5c06\u4e0a\u6765\u3002 \u3000\u3000\u51e4\u5929\u5357\u7709\u5934\u4e00\u76b1\uff0c\u5fc3\u60f3\uff1a\u201c\u54b3\uff01\u5f53\u771f\u662f\u4e0d\u8bc6\u597d\u6b79\u3002\u82e5\u662f\u4eba\u591a\u4fbf\u80fd\u6253\u80dc\uff0c\u6211\u4f5b\u5c71\u9547\u4e0a\u4eba\u8fd8\u4e0d\u591f\u591a\uff1f\u53c8\u4f55\u5fc5\u5343\u91cc\u8fe2\u8fe2\u5730\u80cc\u4e95\u79bb\u4e61\uff0c\u9003\u4ea1\u5728\u5916\uff1f\u201d\u4f46\u4e8b\u5230\u4e34\u5934\uff0c\u4e5f\u5df2\u522b\u65e0\u4ed6\u6cd5\uff0c\u53ea\u6709\u51b3\u4e00\u6b7b\u6218\u3002\u4ed6\u5fc3\u4e2d\u5b58\u4e86\u62da\u4e2a\u540c\u5f52\u4e8e\u5c3d\u7684\u5ff5\u5934\uff0c\u51fa\u624b\u53cd\u800c\u51b7\u9759\uff0c\u4e00\u68cd\u51fb\u51fa\uff0c\u4e0d\u7b49\u62db\u672f\u7528\u8001\uff0c\u91d1\u68cd\u659c\u63a0\uff0c\u62c9\u56de\u6a2a\u626b\u3002 \u3000\u3000\u80e1\u6590\u5fc3\u60f3\u6b64\u4eba\u7f6a\u5927\u6076\u6781\uff0c\u5982\u679c\u4e00\u5200\u9001\u4e86\u4ed6\u6027\u547d\uff0c\u5211\u7f5a\u8fdc\u4e0d\u8db3\u4ee5\u62b5\u507f\u8fc7\u6076\uff0c\u773c\u89c1\u91d1\u68cd\u626b\u5230\uff0c\u5355\u5200\u5f80\u4e0a\u4e00\u629b\uff0c\u4f38\u624b\u4fbf\u53bb\u786c\u6293\u68cd\u5c3e\uff0c\u7adf\u662f\u4e00\u51fa\u624b\u4fbf\u662f\u5c06\u654c\u4eba\u89c6\u82e5\u65e0\u7269\uff0c\u51e4\u5929\u5357\u6697\u60f3\u6211\u4e00\u751f\u95ef\u8361\u6c5f\u6e56\uff0c\u8fd8\u6ca1\u7ed9\u4eba\u5982\u6b64\u8f7b\u89c6\u8fc7\uff0c\u4e0d\u7531\u5f97\u6012\u706b\u76f4\u51b2\u80f8\u81c6\uff0c\u4f46\u4f5b\u5c71\u9547\u4e0a\u4e00\u756a\u4ea4\u624b\uff0c\u77e5\u5bf9\u65b9\u6b66\u529f\u5b9e\u975e\u5df1\u6240\u80fd\u654c\uff0c\u624b\u4e0a\u4e1d\u6beb\u4e0d\u6562\u5927\u610f\uff0c\u6025\u901f\u6536\u68cd\uff0c\u9000\u540e\u4e00\u6b65\u3002\u53ea\u542c\u5f97\u5934\u9876\u79c3\u7684\u4e00\u54cd\uff0c\u4f17\u4eba\u867d\u7136\u5927\u654c\u5f53\u524d\uff0c\u8fd8\u662f\u5fcd\u4e0d\u4f4f\u62ac\u5934\u4e00\u770b\uff0c\u539f\u6765\u80e1\u6590\u90a3\u67c4\u5355\u5200\u629b\u63b7\u4e0a\u53bb\uff0c\u65a9\u4f4f\u4e86\u5c4b\u6881\uff0c\u7559\u5728\u6881\u4e0a\u4e0d\u518d\u6389\u4e0b\u3002 \u3000\u3000\u80e1\u6590\u7eb5\u58f0\u957f\u7b11\uff0c\u7a81\u7136\u63d2\u5165\u4eba\u7fa4\u4e4b\u4e2d\uff0c\u53cc\u624b\u5ffd\u8d77\u5ffd\u843d\uff0c\u5c06\u51e4\u5929\u5357\u516b\u4e5d\u540d\u95e8\u4eba\u5f1f\u5b50\u5c3d\u6570\u70b9\u4e2d\u4e86\u7a74\u9053\uff0c\u6216\u624b\u81c2\u659c\u632f\uff0c\u6216\u63d0\u8db3\u6a2a\u626b\uff0c\u4e00\u4e00\u7529\u5728\u4e24\u65c1\u3002\u970e\u65f6\u4e4b\u95f4\uff0c\u5927\u6bbf\u4e2d\u5fc3\u7a7a\u7a7a\u8361\u8361\uff0c\u53ea\u5269\u4e0b\u51e4\u6c0f\u7236\u5b50\u4e0e\u80e1\u6590\u4e09\u4eba\u3002 \u3000\u3000\u51e4\u5929\u5357\u4e00\u54ac\u7259\uff0c\u4f4e\u58f0\u559d\u9053\uff1a\u201c\u9e23\u513f\u4f60\u8fd8\u4e0d\u8d70\uff0c\u771f\u8981\u51e4\u5bb6\u7edd\u5b50\u7edd\u5b59\u4e48\uff1f\u201d\u51e4\u4e00\u9e23\u5140\u81ea\u8fdf\u7591\uff0c\u63d0\u7740\u5355\u5200\uff0c\u4e0d\u77e5\u8be5\u5f53\u4e0a\u524d\u5939\u51fb\uff0c\u8fd8\u662f\u593a\u8def\u9003\u751f\uff1f \u3000\u3000\u80e1\u6590\u8eab\u5f62\u4e00\u6643\uff0c\u5df2\u62a2\u5230\u4e86\u51e4\u4e00\u9e23\u80cc\u540e\uff0c\u51e4\u5929\u5357\u4e00\u58f0\u5927\u559d\uff0c\u91d1\u68cd\u6325\u51fa\uff0c\u4e0a\u524d\u622a\u62e6\u3002\u80e1\u6590\u5934\u4e00\u4f4e\uff0c\u4ece\u51e4\u4e00\u9e23\u814b\u4e0b\u94bb\u4e86\u8fc7\u53bb\uff0c\u8f7b\u8f7b\u4e00\u638c\uff0c\u5728\u4ed6\u80a9\u5934\u4e00\u63a8\uff0c\u51e4\u4e00\u9e23\u7ad9\u7acb\u4e0d\u7a33\uff0c\u8eab\u5b50\u540e\u4ef0\uff0c\u4fbf\u5411\u68cd\u4e0a\u649e\u53bb\u3002\u51e4\u5929\u5357\u5927\u60ca\uff0c\u6025\u6536\u91d1\u68cd\uff0c\u603b\u7b97\u4ed6\u5728\u8fd9\u68cd\u4e0a\u4e0b\u4e86\u6570\u5341\u5e74\u82e6\u529f\uff0c\u5728\u5343\u94a7\u4e00\u53d1\u4e4b\u9645\u786c\u751f\u751f\u6536\u56de\uff0c\u624d\u6ca1\u5c06\u513f\u5b50\u6253\u5f97\u8111\u6d46\u8ff8\u88c2\u3002 \u3000\u3000\u80e1\u6590\u4e00\u62db\u5f97\u624b\uff0c\u5fc3\u60f3\u7528\u8fd9\u6cd5\u5b50\u6597\u4ed6\uff0c\u5012\u4e5f\u7edd\u5999\uff0c\u4e0d\u5f85\u51e4\u4e00\u9e23\u7ad9\u7a33\uff0c\u53f3\u624b\u6293\u4f4f\u4e86\u4ed6\u540e\u9888\uff0c\u63d0\u8d77\u5de6\u638c\uff0c\u4fbf\u5f80\u4ed6\u8111\u95e8\u62cd\u843d\u3002 \u3000\u3000\u51e4\u5929\u5357\u60f3\u8d77\u4ed6\u5728\u5317\u5e1d\u5e99\u4e2d\u51fb\u65ad\u77f3\u9f9f\u5934\u9888\u7684\u638c\u529b\uff0c\u8fd9\u4e00\u638c\u843d\u5728\u513f\u5b50\u8111\u95e8\u4e4b\u4e0a\uff0c\u600e\u80fd\u8fd8\u6709\u547d\u5728\uff1f\u6025\u5fd9\u91d1\u68cd\u9012\u51fa\uff0c\u731b\u70b9\u80e1\u6590\u5de6\u8170\uff0c\u8feb\u4f7f\u4ed6\u56de\u638c\u81ea\u6551\u3002 \u3000\u3000\u80e1\u6590\u5de6\u638c\u4e3e\u5728\u534a\u7a7a\uff0c\u7a0d\u4e00\u505c\u7559\uff0c\u5f85\u91d1\u68cd\u5c06\u5230\u8170\u95f4\uff0c\u53f3\u624b\u6293\u7740\u51e4\u4e00\u9e23\u8111\u888b\uff0c\u731b\u5730\u5f80\u68cd\u5934\u6025\u9001\u3002\u51e4\u5929\u5357\u7acb\u5373\u53d8\u62db\uff0c\u6539\u4e3a\u201c\u6311\u888d\u64a9\u8863\u201d\uff0c\u81ea\u4e0b\u5411\u4e0a\u6284\u8d77\uff0c\u653b\u654c\u4e0b\u76d8\u3002\u80e1\u6590\u53eb\u9053\uff1a\u201c\u597d\uff01\u201d\u5de6\u638c\u5728\u51e4\u4e00\u9e23\u80cc\u4e0a\u4e00\u63a8\uff0c\u7528\u4ed6\u8eab\u5b50\u53bb\u62b5\u6321\u68cd\u62db\u3002 \u3000\u3000\u5982\u6b64\u6570\u62db\u4e00\u8fc7\uff0c\u51e4\u4e00\u9e23\u53d8\u6210\u4e86\u80e1\u6590\u624b\u4e2d\u7684\u4e00\u4ef6\u5175\u5668\u3002\u80e1\u6590\u4e0d\u662f\u62ff\u4ed6\u8111\u888b\u53bb\u548c\u91d1\u68cd\u78b0\u649e\uff0c\u4fbf\u662f\u7528\u4ed6\u56db\u80a2\u6765\u683c\u67b6\u91d1\u68cd\u3002\u51e4\u5929\u5357\u51fa\u624b\u7a0d\u6162\uff0c\u6b32\u5f85\u7f62\u6597\uff0c\u80e1\u6590\u4fbf\u4e3e\u8d77\u624b\u638c\uff0c\u4f5c\u52bf\u6b32\u51fb\u51e4\u4e00\u9e23\u8981\u5bb3\uff0c\u53eb\u4ed6\u4e0d\u5f97\u4e0d\u6551\uff0c\u4f46\u4e00\u6551\u4e4b\u4e0b\uff0c\u603b\u662f\u5904\u5904\u5371\u673a\uff0c\u6ca1\u4e00\u62db\u4e0d\u662f\u4ee4\u4ed6\u9669\u4e9b\u4eb2\u624b\u51fb\u6bd9\u4e86\u513f\u5b50\u3002\u53c8\u6597\u6570\u62db\uff0c\u51e4\u5929\u5357\u5fc3\u529b\u4ea4\u7601\uff0c\u7a81\u7136\u5411\u540e\u9000\u5f00\u4e09\u6b65\uff0c\u5c06\u91d1\u68cd\u5f80\u5730\u4e0b\u4e00\u63b7\uff0c\u5f53\u7684\u4e00\u58f0\u5de8\u54cd\uff0c\u5730\u4e0b\u9752\u7816\u788e\u4e86\u6570\u5757\uff0c\u60e8\u7136\u4e0d\u8bed\u3002 \u3000\u3000\u80e1\u6590\u5389\u58f0\u559d\u9053\uff1a\u201c\u51e4\u5929\u5357\uff0c\u4f60\u4fbf\u6709\u7231\u5b50\u4e4b\u5fc3\uff0c\u4eba\u5bb6\u513f\u5b50\u5374\u53c8\u600e\u5730\uff1f\u201d \u3000\u3000\u51e4\u5929\u5357\u5fae\u5fae\u4e00\u6014\uff0c\u968f\u5373\u5f3a\u608d\u4e4b\u6c14\u53c8\u76db\uff0c\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u51e4\u67d0\u6a2a\u884c\u5cad\u5357\uff0c\u505a\u5230\u4e94\u864e\u6d3e\u638c\u95e8\uff0c\u751f\u5e73\u6740\u4eba\u65e0\u7b97\u3002\u6211\u8fd9\u513f\u5b50\u624b\u4e0b\u4e5f\u6740\u8fc7\u4e09\u56db\u5341\u6761\u4eba\u547d\uff0c\u4eca\u65e5\u6b7b\u5728\u4f60\u624b\u91cc\uff0c\u53c8\u7b97\u5f97\u4e86\u4ec0\u4e48\uff1f\u4f60\u8fd8\u4e0d\u52a8\u624b\uff0c\u647d\u91cc\u647d\u5506\u7684\u5e72\u4e48\uff1f\u201d\u80e1\u6590\u559d\u9053\uff1a\u201c\u90a3\u4f60\u81ea\u5df1\u4e86\u65ad\u4fbf\u662f\uff0c\u4e0d\u7528\u5c0f\u7237\u591a\u8d39\u624b\u811a\u3002\u201d\u51e4\u5929\u5357\u62fe\u8d77\u91d1\u68cd\uff0c\u54c8\u54c8\u4e00\u7b11\uff0c\u56de\u8f6c\u68cd\u7aef\uff0c\u4fbf\u5f80\u81ea\u5df1\u5934\u9876\u7838\u53bb\u3002 \u3000\u3000\u7a81\u7136\u95f4\u94f6\u5149\u95ea\u52a8\uff0c\u4e00\u6761\u6781\u957f\u7684\u8f6f\u97ad\u81ea\u80e1\u6590\u80cc\u540e\u98de\u51fa\uff0c\u5377\u4f4f\u91d1\u68cd\uff0c\u5f80\u5916\u4e00\u593a\u3002\u51e4\u5929\u5357\u8182\u529b\u751a\u5f3a\uff0c\u786c\u529f\u4e86\u5f97\uff0c\u8fd9\u4e00\u593a\u91d1\u68cd\u7adf\u6ca1\u8131\u624b\uff0c\u4f46\u56de\u8f6c\u4e4b\u52bf\uff0c\u5374\u4e5f\u6b62\u4e86\u3002\u8fd9\u6325\u97ad\u593a\u68cd\u7684\u6b63\u662f\u8881\u7d2b\u8863\uff0c\u5979\u624b\u4e0a\u7528\u529b\uff0c\u5411\u91cc\u4e00\u62c9\uff0c\u51e4\u5929\u5357\u91d1\u68cd\u4ecd\u662f\u51dd\u4f4f\u4e0d\u52a8\uff0c\u5979\u5374\u5df2\u501f\u52bf\u8dc3\u4e86\u51fa\u6765\u3002 \u3000\u3000\u8881\u7d2b\u8863\u7b11\u9053\uff1a\u201c\u80e1\u5927\u54e5\uff0c\u54b1\u4eec\u53ea\u593a\u638c\u95e8\u4e4b\u4f4d\uff0c\u53ef\u4e0d\u80fd\u6740\u4f24\u4eba\u547d\u3002\u201d\u80e1\u6590\u54ac\u7259\u5207\u9f7f\u5730\u9053\uff1a\u201c\u8881\u59d1\u5a18\u4f60\u4e0d\u77e5\u9053\uff0c\u8fd9\u4eba\u7f6a\u6076\u6ed4\u5929\uff0c\u975e\u4e00\u822c\u638c\u95e8\u4eba\u53ef\u6bd4\u3002\u201d\u8881\u7d2b\u8863\u6447\u5934\u9053\uff1a\u201c\u6211\u62a2\u593a\u638c\u95e8\uff0c\u5e08\u7236\u77e5\u9053\u4e86\u4e0d\u8fc7\u4e00\u7b11\u3002\u82e5\u662f\u4f24\u4e86\u4eba\u547d\uff0c\u4ed6\u8001\u4eba\u5bb6\u53ef\u662f\u8981\u5927\u5927\u602a\u7f6a\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u8fd9\u4eba\u662f\u6211\u6740\u7684\uff0c\u8ddf\u59d1\u5a18\u6beb\u65e0\u5e72\u7cfb\u3002\u201d\u8881\u7d2b\u8863\u7b54\u9053\uff1a\u201c\u4e0d\u5bf9\uff0c\u4e0d\u5bf9\uff01\u62a2\u593a\u638c\u95e8\u4e4b\u4e8b\uff0c\u56e0\u6211\u800c\u8d77\u3002\u8fd9\u4eba\u662f\u4e94\u864e\u6d3e\u638c\u95e8\uff0c\u600e\u80fd\u8bf4\u8ddf\u6211\u6ca1\u6709\u5e72\u7cfb\uff1f\u201d\u80e1\u6590\u6025\u9053\uff1a\u201c\u6211\u4ece\u5e7f\u4e1c\u76f4\u8ffd\u5230\u6e56\u5357\uff0c\u4fbf\u662f\u8ffd\u8d76\u8fd9\u6076\u8d3c\u3002\u4ed6\u662f\u638c\u95e8\u4eba\u4e5f\u597d\uff0c\u4e0d\u662f\u638c\u95e8\u4eba\u4e5f\u597d\uff0c\u4eca\u65e5\u975e\u6740\u4e86\u4ed6\u4e0d\u53ef\u3002\u201d \u3000\u3000\u8881\u7d2b\u8863\u6b63\u8272\u9053\uff1a\u201c\u80e1\u5927\u54e5\uff0c\u6211\u8ddf\u4f60\u8bf4\u6b63\u7ecf\u8bdd\uff0c\u4f60\u597d\u597d\u542c\u7740\u4e86\u3002\u201d\u80e1\u6590\u70b9\u4e86\u70b9\u5934\u3002\u8881\u7d2b\u8863\u9053\uff1a\u201c\u4f60\u4e0d\u77e5\u6211\u5e08\u7236\u662f\u8c01\uff0c\u662f\u4e0d\u662f\uff1f\u201d\u80e1\u6590\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\u3002\u59d1\u5a18\u8fd9\u822c\u597d\u8eab\u624b\uff0c\u5c0a\u5e08\u5b9a\u662f\u4e00\u4f4d\u540d\u9707\u6c5f\u6e56\u7684\u5927\u4fa0\uff0c\u8bf7\u95ee\u4ed6\u8001\u4eba\u5bb6\u5927\u540d\u600e\u751f\u79f0\u547c\u3002\u201d \u3000\u3000\u8881\u7d2b\u8863\u9053\uff1a\u201c\u6211\u5e08\u7236\u7684\u540d\u5b57\uff0c\u65e5\u540e\u4f60\u5fc5\u77e5\u9053\u3002\u73b0\u4e0b\u6211\u53ea\u8ddf\u4f60\u8bf4\uff0c\u6211\u79bb\u56de\u7586\u4e4b\u65f6\uff0c\u6211\u5e08\u7236\u5bf9\u6211\u8bf4\u9053\uff1a\u2018\u4f60\u53bb\u4e2d\u539f\uff0c\u4e0d\u7ba1\u600e\u4e48\u80e1\u95f9\uff0c\u6211\u90fd\u4e0d\u7ba1\uff0c\u4f46\u53ea\u8981\u6740\u4e86\u4e00\u4e2a\u4eba\uff0c\u6211\u7acb\u65f6\u53d6\u4f60\u7684\u5c0f\u547d\u3002\u2019\u6211\u5e08\u7236\u5411\u6765\u8bf4\u4e00\u662f\u4e00\uff0c\u8bf4\u4e8c\u662f\u4e8c\uff0c\u51b3\u6ca1\u534a\u5206\u542b\u7cca\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u96be\u9053\u5341\u6076\u4e0d\u8d66\u7684\u574f\u4eba\uff0c\u4e5f\u4e0d\u8bb8\u6740\u4e48\uff1f\u201d\u8881\u7d2b\u8863\u8bf4\u9053\uff1a\u201c\u662f\u554a\uff01 \u3000\u3000\u90a3\u65f6\u6211\u4e5f\u8fd9\u822c\u95ee\u6211\u5e08\u7236\u3002\u4ed6\u8001\u4eba\u5bb6\u9053\uff1a\u2018\u574f\u4eba\u672c\u6765\u8be5\u6740\u3002\u4f46\u4e16\u60c5\u53d8\u5e7b\uff0c\u4e00\u4eba\u5230\u5e95\u662f\u597d\u662f\u574f\uff0c\u4f60\u5c0f\u5c0f\u5e74\u7eaa\u600e\u80fd\u5206\u8fa8\u6e05\u695a\uff1f\u4e16\u4e0a\u6709\u7b11\u9762\u8001\u864e\uff0c\u4e5f\u6709\u864e\u9762\u83e9\u8428\u3002\u4eba\u6b7b\u4e0d\u80fd\u590d\u751f\uff0c\u53ea\u8981\u6740\u9519\u4e00\u4e2a\u4eba\uff0c\u90a3\u4fbf\u7ec8\u8eab\u9057\u6068\u3002\u2019\u201d\u80e1\u6590\u70b9\u5934\u9053\uff1a\u201c\u8bdd\u662f\u4e0d\u9519\u3002\u4f46\u8fd9\u4eba\u4eb2\u53e3\u81ea\u8ba4\u6740\u4eba\u65e0\u7b97\uff0c\u4ed6\u5728\u4f5b\u5c71\u9547\u4e0a\u6740\u5bb3\u826f\u5584\uff0c\u53c8\u662f\u6211\u4eb2\u773c\u89c1\u5230\uff0c\u51b3\u8ba1\u9519\u4e0d\u4e86\u3002\u201d\u8881\u7d2b\u8863\u9053\uff1a\u201c\u6211\u662f\u8feb\u4e8e\u5e08\u547d\uff0c\u4e8b\u51fa\u65e0\u5948\u3002\u80e1\u5927\u54e5\uff0c\u4f60\u77a7\u5728\u6211\u4efd\u4e0a\uff0c\u9ad8\u62ac\u8d35\u624b\uff0c\u5c31\u6b64\u7b97\u4e86\u5427\uff01\u201d \u3000\u3000\u80e1\u6590\u542c\u5979\u8a00\u8f9e\u6073\u5207\uff0c\u786e\u662f\u771f\u5fc3\u76f8\u6c42\uff0c\u81ea\u4e0e\u5979\u76f8\u8bc6\u4ee5\u6765\uff0c\u4ece\u672a\u542c\u8fc7\u5979\u4ee5\u8fd9\u822c\u8bed\u6c14\u8bf4\u8bdd\uff0c\u4e0d\u7531\u5f97\u5fc3\u4e2d\u4e00\u52a8\uff0c\u4f46\u968f\u5373\u60f3\u8d77\u953a\u963f\u56db\u592b\u5987\u7236\u5b50\u6b7b\u4ea1\u6795\u85c9\u7684\u60e8\u72b6\uff0c\u60f3\u8d77\u5317\u5e1d\u795e\u50cf\u5ea7\u524d\u77f3\u4e0a\u5c0f\u513f\u5256\u8179\u7684\u8840\u8ff9\uff0c\u60f3\u8d77\u4f5b\u5c71\u8857\u5934\u6076\u72ac\u6251\u54ac\u953a\u5c0f\u4e8c\u7684\u72e0\u6001\uff0c\u4e00\u80a1\u70ed\u8840\u6d8c\u4e0a\u5fc3\u5934\uff0c\u5927\u58f0\u9053\uff1a\u201c\u8881\u59d1\u5a18\uff0c\u8fd9\u513f\u7684\u4e8b\u4f60\u53ea\u5f53\u6ca1\u78b0\u4e0a\uff0c\u8bf7\u4f60\u5148\u884c\u4e00\u6b65\uff0c\u54b1\u4eec\u5230\u957f\u6c99\u518d\u89c1\u3002\u201d \u3000\u3000\u8881\u7d2b\u8863\u8138\u8272\u4e00\u6c89\uff0c\u6120\u9053\uff1a\u201c\u6211\u751f\u5e73\u4ece\u672a\u5982\u6b64\u4f4e\u58f0\u4e0b\u6c14\u5730\u6c42\u8fc7\u522b\u4eba\uff0c\u4f60\u5374\u5b9a\u662f\u4e0d\u4f9d\u3002\u8fd9\u4eba\u4e0e\u4f60\u53c8\u65e0\u6df1\u4ec7\u5927\u6028\uff0c\u4f60\u4e5f\u4e0d\u8fc7\u662f\u4e3a\u4e86\u65c1\u4eba\u4e4b\u4e8b\uff0c\u8def\u89c1\u4e0d\u5e73\u800c\u5df2\u3002\u4ed6\u6bc1\u5bb6\u9003\u4ea1\uff0c\u663c\u5bbf\u591c\u884c\uff0c\u4e5f\u7b97\u662f\u6015\u5f97\u4f60\u5389\u5bb3\u4e86\u3002\u80e1\u5927\u54e5\uff0c\u4e3a\u4eba\u4e0d\u53ef\u8d76\u5c3d\u6740\u7edd\uff0c\u987b\u7559\u4e09\u5206\u4f59\u5730\u3002\u201d\u80e1\u6590\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u8881\u59d1\u5a18\uff0c\u8fd9\u4eba\u6211\u662f\u975e\u6740\u4e0d\u53ef\u3002\u6211\u5148\u8ddf\u4f60\u8d54\u4e2a\u4e0d\u662f\uff0c\u65e5\u540e\u5c0a\u5e08\u82e5\u662f\u602a\u8d23\uff0c\u6211\u7518\u613f\u72ec\u81ea\u9886\u7f6a\u3002\u201d\u8bf4\u7740\u4e00\u63d6\u5230\u5730\u3002 \u3000\u3000\u53ea\u542c\u5f97\u5237\u7684\u4e00\u54cd\uff0c\u8881\u7d2b\u8863\u94f6\u97ad\u6325\u8d77\uff0c\u5377\u4f4f\u4e86\u5c4b\u6881\u4e0a\u80e1\u6590\u90a3\u67c4\u5355\u5200\uff0c\u4e00\u626f\u843d\u4e0b\uff0c\u8f7b\u8f7b\u4e00\u9001\uff0c\u5377\u5230\u4e86\u4ed6\u9762\u524d\uff0c\u8bf4\u9053\uff1a\u201c\u63a5\u7740\uff01\u201d\u80e1\u6590\u4f38\u624b\u6293\u4f4f\u5200\u67c4\uff0c\u53ea\u542c\u5979\u9053\uff1a\u201c\u80e1\u5927\u54e5\uff0c\u4f60\u5148\u6253\u8d25\u6211\uff0c\u518d\u6740\u4ed6\u5168\u5bb6\uff0c\u90a3\u65f6\u5e08\u7236\u4fbf\u602a\u6211\u4e0d\u5f97\u3002\u201d\u80e1\u6590\u6012\u9053\uff1a\u201c\u4f60\u4e00\u610f\u4ece\u4e2d\u963b\u62e6\uff0c\u5b9a\u6709\u522b\u60c5\u3002\u5c0a\u5e08\u662f\u5802\u5802\u5927\u4fa0\uff0c\u524d\u8f88\u9ad8\u4eba\uff0c\u96be\u9053\u5c31\u4e0d\u8bb2\u60c5\u7406\uff1f\u201d \u3000\u3000\u8881\u7d2b\u8863\u8f7b\u53f9\u4e00\u58f0\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u80e1\u5927\u54e5\uff0c\u4f60\u5f53\u771f\u4e0d\u7ed9\u6211\u4e00\u70b9\u513f\u9762\u5b50\u4e48\uff1f\u201d\u706b\u5149\u6620\u7167\u4e4b\u4e0b\uff0c\u5a07\u8138\u5982\u82b1\uff0c\u4f4e\u8bed\u592e\u6c42\uff0c\u80e1\u6590\u4e0d\u7531\u5f97\u5fc3\u80a0\u4e00\u8f6f\uff0c\u4f46\u8d8a\u662f\u89c1\u5979\u5982\u6b64\u6073\u5207\u76f8\u6c42\uff0c\u8d8a\u662f\u60f3\u5230\u5176\u4e2d\u5fc5\u6709\u8bc8\u8c0b\uff0c\u5fc3\u9053\uff1a\u201c\u80e1\u6590\u554a\u80e1\u6590\uff0c\u4f60\u82e5\u60d1\u4e8e\u7f8e\u8272\uff0c\u4e0d\u987e\u5927\u4e49\uff0c\u6789\u4e3a\u82f1\u96c4\u597d\u6c49\u3002\u4f60\u7239\u7239\u80e1\u4e00\u5200\u4e00\u4e16\u8c6a\u6770\uff0c\u5c82\u80fd\u6709\u4f60\u8fd9\u7b49\u4e0d\u8096\u5b50\u5b59\uff1f\u201d \u3000\u3000\u773c\u89c1\u82e5\u4e0d\u52a8\u6b66\uff0c\u5df2\u96be\u4ee5\u8bdb\u5978\u6740\u6076\uff0c\u53eb\u9053\uff1a\u201c\u5982\u6b64\u4fbf\u5f97\u7f6a\u4e86\u3002\u201d\u5355\u5200\u4e00\u8d77\uff0c\u4e00\u62db\u201c\u5927\u4e09\u62cd\u201d\uff0c\u5200\u5149\u95ea\u95ea\uff0c\u5df2\u5c06\u8881\u7d2b\u8863\u4e0a\u76d8\u7f69\u4f4f\uff0c\u5de6\u624b\u626c\u5904\uff0c\u4e00\u952d\u7eb9\u94f6\u5f80\u51e4\u5929\u5357\u5fc3\u53e3\u6253\u53bb\u3002 \u3000\u3000\u8881\u7d2b\u8863\u89c1\u4ed6\u75f4\u75f4\u671b\u7740\u81ea\u5df1\uff0c\u4f3c\u4e4e\u5df2\u7b54\u5e94\u81ea\u5df1\u8981\u6c42\uff0c\u5fc3\u4e2d\u6b63\u81ea\u559c\u6b22\uff0c\u54ea\u77e5\u9053\u4ed6\u7adf\u4f1a\u7a81\u7136\u51fa\u624b\uff0c\u4e24\u4eba\u76f8\u8ddd\u4e0d\u8fdc\uff0c\u8fd9\u4e00\u62db\u201c\u5927\u4e09\u62cd\u201d\u6765\u5f97\u731b\u6076\uff0c\u94f6\u4e1d\u97ad\u53c8\u957f\u53c8\u8f6f\uff0c\u672c\u5df2\u4e0d\u6613\u62b5\u6321\uff0c\u800c\u4ed6\u5de6\u624b\u53c8\u53d1\u6697\u5668\uff0c\u4f46\u542c\u98ce\u58f0\u52b2\u6025\uff0c\u663e\u662f\u8fd9\u6697\u5668\u51fa\u624b\u6781\u662f\u6c89\u91cd\uff0c\u53ea\u6015\u51e4\u5929\u5357\u672a\u5fc5\u6321\u5f97\u4f4f\u3002\u8881\u7d2b\u8863\u5fc3\u5ff5\u4e00\u95ea\uff1a\u201c\u4ed6\u4e0d\u4f1a\u4f24\u6211\uff01\u201d\u957f\u97ad\u7529\u51fa\uff0c\u6025\u8ffd\u4e0a\u53bb\uff0c\u5f53\u7684\u4e00\u58f0\uff0c\u5c06\u90a3\u952d\u7eb9\u94f6\u6253\u843d\uff0c\u5bf9\u80e1\u6590\u7684\u5200\u62db\u7adf\u662f\u4e0d\u5c01\u4e0d\u67b6\u3002 \u3000\u3000\u539f\u6765\u80e1\u6590\u77e5\u5979\u6b66\u529f\u51b3\u4e0d\u5728\u5df1\u4e4b\u4e0b\uff0c\u53ea\u8981\u4e00\u52a8\u4e0a\u624b\uff0c\u4fbf\u975e\u7247\u65f6\u53ef\u51b3\uff0c\u51e4\u5929\u5357\u7236\u5b50\u4e0d\u514d\u9003\u8d70\uff0c\u662f\u4ee5\u7a81\u7136\u53d1\u96be\uff0c\u4f46\u8eab\u8fb9\u6697\u5668\u53ea\u6709\u94b1\u9556\uff0c\u4fbf\u662f\u6253\u4e2d\u4e5f\u4e0d\u80fd\u81f4\u547d\uff0c\u4e8e\u662f\u5c06\u4e00\u952d\u4e94\u4e24\u91cd\u7684\u7eb9\u94f6\u53d1\u4e86\u51fa\u53bb\uff0c\u8fd9\u4e00\u4e0b\u624b\u52b2\u65e2\u91cd\uff0c\u53bb\u52bf\u53c8\u602a\uff0c\u773c\u89c1\u5b9a\u53ef\u6210\u529f\uff0c\u5c82\u6599\u8881\u7d2b\u8863\u7adf\u7136\u5192\u9669\u4e0d\u62a4\u81ea\u8eab\uff0c\u53cd\u800c\u53bb\u76f8\u6551\u65c1\u4eba\uff0c\u4ed6\u5200\u950b\u79bb\u5979\u5934\u9876\u4e0d\u53ca\u6570\u5bf8\uff0c\u51dd\u81c2\u505c\u4f4f\uff0c\u559d\u9053\uff1a\u201c\u8fd9\u4e3a\u4ec0\u4e48\uff1f\u201d\u8881\u7d2b\u8863\u9053\uff1a\u201c\u8feb\u4e0d\u5f97\u5df2\uff01\u201d\u8eab\u5f62\u84e6\u5730\u5411\u540e\u7eb5\u5f00\u4e08\u4f59\uff0c\u94f6\u97ad\u56de\u7529\uff0c\u53eb\u9053\uff1a\u201c\u770b\u62db\u5427\uff01\u201d \u3000\u3000\u80e1\u6590\u4e3e\u5200\u4e00\u6321\uff0c\u5f85\u8981\u4fdf\u673a\u518d\u5411\u51e4\u5929\u5357\u88ad\u51fb\uff0c\u4f46\u8881\u7d2b\u8863\u7684\u94f6\u4e1d\u8f6f\u97ad\u4e00\u5c55\u5f00\uff0c\u62db\u62db\u6740\u7740\uff0c\u7adf\u662f\u4e0d\u5bb9\u4ed6\u6709\u4e1d\u6beb\u7f13\u624b\u4e4b\u673a\uff0c\u53ea\u5f97\u5168\u795e\u8d2f\u6ce8\uff0c\u89c1\u62db\u62c6\u62db\u3002\u5927\u6bbf\u4e0a\u53ea\u89c1\u8f6f\u97ad\u5316\u6210\u4e00\u4e2a\u94f6\u5149\u5927\u5708\uff0c\u5355\u5200\u821e\u6210\u4e00\u4e2a\u94f6\u5149\u5c0f\u5708\uff0c\u4e24\u4e2a\u94f6\u5708\u76d8\u65cb\u51b2\u51fb\uff0c\u817e\u632a\u95ea\u8dc3\uff0c\u5076\u7136\u53d1\u51fa\u51e0\u4e0b\u5200\u97ad\u649e\u51fb\u4e4b\u58f0\u3002 \u3000\u3000\u6597\u5230\u5206\u9645\uff0c\u8881\u7d2b\u8863\u8f6f\u97ad\u6a2a\u7529\uff0c\u5c06\u795e\u575b\u4e0a\u70b9\u7740\u7684\u8721\u70db\u51fb\u843d\u5730\u4e0b\uff0c\u80e1\u6590\u5fc3\u5ff5\u4e00\u52a8\uff1a\u201c\u5979\u8981\u6253\u706d\u70db\u706b\uff0c\u597d\u8ba9\u90a3\u59d3\u51e4\u7684\u9003\u8d70\u3002\u201d \u3000\u3000\u53ef\u662f\u867d\u77e5\u5979\u7684\u7528\u610f\uff0c\u4e00\u65f6\u5374\u65e0\u5e94\u4ed8\u4e4b\u7b56\uff0c\u53ea\u6709\u5c55\u5f00\u7956\u4f20\u80e1\u5bb6\u5200\u6cd5\u4e2d\u7cbe\u5999\u4e4b\u62db\uff0c\u7740\u7740\u8fdb\u653b\u3002\u8881\u7d2b\u8863\u53eb\u9053\uff1a\u201c\u597d\u5200\u6cd5\uff01\u201d\u97ad\u8eab\u6a2a\u8fc7\uff0c\u67b6\u5f00\u4e86\u4e00\u5200\uff0c\u97ad\u5934\u5df2\u5377\u4f4f\u4e86\u897f\u6bbf\u5730\u4e0b\u70b9\u71c3\u7740\u7684\u4e00\u6839\u67f4\u706b\uff0c\u5411\u4ed6\u63b7\u53bb\u3002 \u3000\u3000\u716e\u996d\u7684\u94c1\u9505\u867d\u88ab\u80e1\u6590\u8e22\u7ffb\uff0c\u70e7\u5f97\u6b63\u65fa\u7684\u4e8c\u4e09\u5341\u6839\u67f4\u706b\u5374\u5140\u81ea\u672a\u7184\u3002\u80e1\u6590\u89c1\u5979\u957f\u97ad\u5377\u8d77\u67f4\u706b\u63b7\u6765\uff0c\u4e0d\u6562\u7528\u529b\u53bb\u7838\uff0c\u53ea\u6015\u706b\u661f\u6e85\u5f00\uff0c\u4f24\u4e86\u5934\u8138\uff0c\u4e8e\u662f\u8dc3\u5f00\u95ea\u907f\uff0c\u8fd9\u4e00\u95ea\u4e00\u907f\uff0c\u4fbf\u4e0d\u80fd\u518d\u5411\u524d\u8fdb\u51fb\u3002\u8881\u7d2b\u8863\u7f13\u51fa\u624b\u6765\uff0c\u5c06\u706b\u5806\u4e2d\u71c3\u7740\u7684\u67f4\u706b\u968f\u5377\u968f\u63b7\uff0c\u4e00\u6839\u752b\u51fa\uff0c\u4e8c\u6839\u7ee7\u81f3\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u9ed1\u6697\u4e2d\u95ea\u8fc7\u4e00\u9053\u9053\u706b\u5149\u3002 \u3000\u3000\u80e1\u6590\u89c1\u67f4\u706b\u4e0d\u65ad\u63b7\u6765\uff0c\u53c8\u591a\u53c8\u5feb\uff0c\u53ea\u5f97\u5c55\u5f00\u8f7b\u529f\uff0c\u5728\u6bbf\u4e2d\u56db\u4e0b\u6e38\u8d70\u3002\u773c\u89c1\u51e4\u5929\u5357\u7684\u5bb6\u4eba\u3001\u5b50\u5f1f\u3001\u8f66\u592b\u4ec6\u4ece\u4e00\u4e2a\u4e2a\u6e9c\u5411\u540e\u6bbf\uff0c\u70b9\u4e2d\u4e86\u7a74\u9053\u7684\u4e5f\u7ed9\u4eba\u62b1\u8d70\uff0c\u51e4\u5929\u5357\u7236\u5b50\u5374\u76ee\u9732\u51f6\u5149\uff0c\u7ad9\u5728\u4e00\u65c1\u3002\u4ed6\u751f\u6015\u51e4\u5929\u5357\u4e58\u673a\u593a\u8def\u8131\u9003\uff0c\u5200\u5149\u970d\u970d\uff0c\u8eab\u5b50\u7adf\u662f\u4e0d\u79bb\u5e99\u95e8\u3002 \u3000\u3000\u6597\u4e86\u4e00\u4f1a\uff0c\u7a7a\u4e2d\u98de\u821e\u7684\u67f4\u706b\u6e10\u5c11\uff0c\u6389\u5728\u5730\u4e0b\u7684\u4e5f\u6e10\u6b21\u7184\u706d\u3002 \u3000\u3000\u8881\u7d2b\u8863\u7b11\u9053\uff1a\u201c\u80e1\u5927\u54e5\uff0c\u4eca\u65e5\u96be\u5f97\u6709\u5174\uff0c\u54b1\u4eec\u4fbf\u5206\u4e2a\u5f3a\u5f31\u5982\u4f55\uff1f\u201d\u8bf4\u7740\u8f6f\u97ad\u6325\u52a8\uff0c\u752b\u70b9\u80e1\u6590\u524d\u80f8\uff0c\u968f\u5373\u8f6c\u800c\u6253\u5411\u53f3\u80c1\u3002 \u3000\u3000\u80e1\u6590\u4e3e\u5200\u67b6\u5f00\u4e86\u524d\u4e00\u62db\uff0c\u7b2c\u4e8c\u62db\u6765\u5f97\u602a\u5f02\uff0c\u6025\u5fd9\u5728\u5730\u4e0b\u4e00\u4e2a\u6253\u6eda\uff0c\u8fd9\u624d\u907f\u5f00\u3002 \u3000\u3000\u8881\u7d2b\u8863\u7b11\u9053\uff1a\u201c\u4e0d\u7528\u5fd9\uff0c\u6211\u4e0d\u4f1a\u4f24\u4f60\u3002\u201d\u8fd9\u53e5\u8bdd\u89e6\u52a8\u4e86\u80e1\u6590\u7684\u50b2\u6c14\uff0c\u5fc3\u60f3\uff1a\u201c\u96be\u9053\u6211\u4fbf\u771f\u7684\u8f93\u4e8e\u4f60\u4e86\uff1f\u201d\u50ac\u52a8\u5200\u6cd5\uff0c\u6b65\u6b65\u8fdb\u903c\u3002\u6b64\u65f6\u5927\u6bbf\u6b63\u4e2d\u53ea\u4f59\u4e00\u6bb5\u67f4\u706b\uff0c\u5140\u81ea\u71c3\u70e7\uff0c\u53ea\u542c\u8881\u7d2b\u8863\u9053\uff1a\u201c\u6211\u8fd9\u8def\u97ad\u6cd5\u62db\u6570\u5947\u5c06\uff0c\u4f60\u53ef\u8981\u5c0f\u5fc3\u4e86\uff01\u201d\u7a81\u7136\u98ce\u96f7\u4e4b\u58f0\u5927\u4f5c\uff0c\u8f70\u8f70\u9686\u9686\uff0c\u4e0d\u77e5\u5979\u8f6f\u97ad\u4e4b\u4e2d\uff0c\u5982\u4f55\u7adf\u80fd\u53d1\u51fa\u5982\u6b64\u602a\u58f0\u3002\u80e1\u6590\u53eb\u4e86\u58f0\uff1a\u201c\u597d\uff01\u201d\u5148\u81ea\u5b88\u7d27\u95e8\u6237\uff0c\u8981\u77a7\u660e\u767d\u5979\u97ad\u6cd5\u7684\u8981\u65e8\uff0c\u518d\u8c0b\u8fdb\u51fb\uff0c\u5ffd\u542c\u5f97\u5fc5\u535c\u4e00\u58f0\uff0c\u6bbf\u4e2d\u7684\u4e00\u6bb5\u67f4\u706b\u7206\u88c2\u5f00\u6765\uff0c\u706b\u82b1\u56db\u6e85\uff0c\u970e\u65f6\u4e4b\u95f4\uff0c\u706b\u82b1\u9690\u706d\uff0c\u6bbf\u4e2d\u9ed1\u6f06\u4e00\u56e2\u3002 \u3000\u3000\u8fd9\u65f6\u96e8\u4e0b\u5f97\u66f4\u52a0\u5927\u4e86\uff0c\u6253\u5728\u5c4b\u74e6\u4e4b\u4e0a\uff0c\u5237\u5237\u4f5c\u58f0\uff0c\u8881\u7d2b\u8863\u7684\u97ad\u58f0\u5939\u5728\u5176\u95f4\uff0c\u66f4\u662f\u9686\u9686\u9707\u8033\u3002\u80e1\u6590\u867d\u7136\u5927\u80c6\uff0c\u5f53\u6b64\u60c5\u666f\uff0c\u5fc3\u4e2d\u4e5f\u4e0d\u7981\u6817\u6817\u81ea\u5371\uff0c\u731b\u5730\u91cc\u4e00\u4e2a\u5ff5\u5934\u5982\u7535\u5149\u77f3\u706b\u822c\u5728\u5fc3\u4e2d\u4e00\u8f6c\uff1a\u201c\u90a3\u65e5\u5728\u4f5b\u5c71\u5317\u5e1d\u5e99\u4e2d\uff0c\u51e4\u5929\u5357\u8981\u4e3e\u5200\u81ea\u6740\uff0c\u6709\u4e00\u5973\u5b50\u7528\u6307\u73af\u6253\u843d\u4ed6\u7684\u5355\u5200\u3002\u77a7\u90a3\u5973\u5b50\u7684\u8eab\u5f62\u624b\u6cd5\uff0c\u5b9a\u662f\u8fd9\u4f4d\u8881\u59d1\u5a18\u4e86\u3002\u201d\u60f3\u5230\u6b64\u5904\uff0c\u80f8\u53e3\u66f4\u662f\u4e00\u51c9\uff1a\u201c\u5979\u4e0e\u6211\u7ed3\u4f34\u540c\u884c\uff0c\u539f\u6765\u662f\u610f\u6b32\u4e0d\u5229\u4e8e\u6211\u3002\u201d\u4e0d\u77e5\u600e\u5730\uff0c\u5fc3\u4e2d\u611f\u5230\u7684\u4e0d\u662f\u60ca\u60e7\uff0c\u800c\u662f\u4e00\u9635\u5931\u671b\u548c\u51c4\u51c9\uff0c\u610f\u5ff5\u7a0d\u5206\uff0c\u624b\u4e0a\u7adf\u4e5f\u7565\u61c8\uff0c\u5200\u5934\u7ed9\u8f6f\u97ad\u4e00\u5377\uff0c\u9669\u4e9b\u8131\u624b\uff0c\u6025\u5fd9\u8fd0\u529b\u5f80\u91cc\u56de\u593a\u3002 \u3000\u3000\u8881\u7d2b\u8863\u7a76\u662f\u5973\u5b50\uff0c\u62db\u6570\u867d\u7cbe\uff0c\u8182\u529b\u5374\u8fdc\u4e0d\u53ca\u80e1\u6590\uff0c\u7ed9\u4ed6\u4e00\u593a\u4e4b\u4e0b\uff0c\u624b\u81c2\u53d1\u9ebb\uff0c\u5f53\u5373\u624b\u8155\u5916\u6296\uff0c\u8f6f\u97ad\u677e\u5f00\u4e86\u5200\u5934\uff0c\u97ad\u68a2\u515c\u8f6c\uff0c\u987a\u52bf\u4fbf\u70b9\u4ed6\u819d\u5f2f\u7684\u201c\u9634\u8c37\u7a74\u201d\u3002\u80e1\u6590\u95ea\u8eab\u907f\u8fc7\uff0c\u8fd8\u4e86\u4e00\u5200\u3002 \u3000\u3000\u8fd9\u65f6\u53e4\u5e99\u4e2d\u9ed1\u6f06\u4e00\u56e2\uff0c\u4e24\u4eba\u53ea\u51ed\u5bf9\u65b9\u5175\u5203\u98ce\u58f0\u62db\u67b6\u3002\u80e1\u6590\u66f4\u662f\u5168\u795e\u6212\u5907\uff0c\u5fc3\u60f3\uff1a\u201c\u5355\u662f\u8fd9\u4f4d\u8881\u59d1\u5a18\uff0c\u6211\u5df2\u96be\u80dc\uff0c\u4f55\u51b5\u8fd8\u6709\u51e4\u5929\u5357\u7236\u5b50\u76f8\u52a9\u3002\u201d\u6b64\u65f6\u4ed6\u6599\u5b9a\u8881\u7d2b\u8863\u4e0e\u51e4\u5929\u5357\u4e43\u662f\u4e00\u515a\u3002\u4eca\u65e5\u663e\u662f\u843d\u5165\u4e86\u654c\u4eba\u7684\u5708\u5957\u4e4b\u4e2d\u3002 \u3000\u3000\u4e24\u4eba\u53c8\u62c6\u6570\u62db\uff0c\u90fd\u662f\u6bcf\u4e00\u8fd1\u8eab\u4fbf\u9047\u51f6\u9669\u3002\u80e1\u6590\u5237\u7684\u4e00\u5200\uff0c\u7ffb\u8155\u6025\u780d\uff0c\u8881\u7d2b\u8863\u8eab\u5b50\u6025\u4ef0\uff0c\u53ea\u89c9\u51b7\u68ee\u68ee\u7684\u5200\u950b\u63a0\u9762\u800c\u8fc7\uff0c\u76f8\u8ddd\u4e0d\u8fc7\u6570\u5bf8\uff0c\u4e0d\u7981\u5413\u4e86\u4e00\u8df3\uff0c\u5bdf\u89c9\u4ed6\u4e0b\u624b\u5df2\u6beb\u4e0d\u5bb9\u60c5\uff0c\u8bf4\u9053\uff1a\u201c\u80e1\u5927\u54e5\uff0c\u4f60\u771f\u751f\u6c14\u4e86\u4e48\uff1f\u201d\u8f6f\u97ad\u8f7b\u6296\uff0c\u5411\u540e\u8dc3\u5f00\u3002 \u3000\u3000\u80e1\u6590\u4e0d\u7b54\uff0c\u51dd\u795e\u503e\u542c\u51e4\u5929\u5357\u7236\u5b50\u7684\u6240\u5728\uff0c\u9632\u4ed6\u4eec\u6697\u4e2d\u5ffd\u65bd\u88ad\u51fb\u3002\u8881\u7d2b\u8863\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u776c\u6211\uff0c\u597d\u5927\u7684\u67b6\u5b50\uff01\u201d\u7a81\u7136\u8f6f\u97ad\u7529\u51fa\uff0c\u52fe\u4ed6\u8db3\u8e1d\u3002\u8fd9\u4e00\u97ad\u6765\u5f97\u65e0\u58f0\u65e0\u606f\uff0c\u80e1\u6590\u731d\u4e0d\u53ca\u9632\uff0c\u8dc3\u8d77\u5df2\u81ea\u4e0d\u53ca\uff0c\u5fd9\u4f38\u5200\u5728\u5730\u4e0b\u4e00\u62c4\uff0c\u6b32\u5f85\u6321\u5f00\u5979\u7684\u8f6f\u97ad\uff0c\u4e0d\u6599\u90a3\u8f6f\u97ad\u4e00\u5377\u4e4b\u540e\u968f\u5373\u5411\u65c1\u6025\u5e26\uff0c\u5378\u5f00\u4e86\u80e1\u6590\u624b\u4e0a\u7684\u6293\u529b\uff0c\u8f7b\u8f7b\u5de7\u5de7\u4fbf\u5c06\u5355\u5200\u593a\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u8fd9\u4e00\u4e0b\u593a\u5200\uff0c\u62db\u6570\u72e1\u733e\uff0c\u52b2\u529b\u5de7\u5999\uff0c\u80e1\u6590\u6697\u53eb\u4e0d\u597d\uff0c\u5175\u5203\u8131\u624b\uff0c\u4eca\u65e5\u83ab\u8981\u4e27\u751f\u5728\u8fd9\u53e4\u5e99\u4e4b\u4e2d\uff0c\u5f53\u4e0b\u4e0d\u5b88\u53cd\u653b\uff0c\u7eb5\u8eab\u524d\u6251\uff0c\u76f4\u6b3a\u8fdb\u8eab\uff0c\u4f38\u638c\u6293\u5979\u5589\u5934\u3002\u8fd9\u4e00\u62db\u201c\u9e70\u722a\u94a9\u624b\u201d\u62db\u6570\u6781\u662f\u72e0\u8fa3\uff0c\u4ed6\u867d\u4f9d\u62f3\u8c31\u6240\u793a\u7ec3\u719f\uff0c\u4f46\u751f\u5e73\u4ece\u672a\u7528\u8fc7\u3002\u8881\u7d2b\u8863\u53ea\u89c9\u5f97\u4e00\u80a1\u70ed\u6c14\u51d1\u8fd1\uff0c\u654c\u4eba\u624b\u6307\u7adf\u5df2\u4f38\u5230\u4e86\u81ea\u5df1\u5589\u5934\uff0c\u6b64\u65f6\u8f6f\u97ad\u5df2\u5728\u5916\u7f18\uff0c\u82e5\u8981\u56de\u8f6c\u6321\u67b6\uff0c\u54ea\u91cc\u8fd8\u6765\u5f97\u53ca\uff1f\u53ea\u5f97\u5c06\u624b\u4e00\u677e\uff0c\u8eab\u5b50\u540e\u4ef0\uff0c\u545b\u5577\u5577\u4e00\u54cd\uff0c\u5200\u97ad\u540c\u65f6\u6454\u5728\u5730\u4e0b\u3002 \u3000\u3000\u80e1\u6590\u4e00\u6293\u5f97\u624b\uff0c\u7b2c\u4e8c\u62db\u201c\u8fdb\u6b65\u8fde\u73af\u201d\uff0c\u8ddf\u7740\u8feb\u51fb\u3002\u8881\u7d2b\u8863\u53cd\u624b\u4e00\u6307\uff0c\u6233\u4e2d\u5728\u80e1\u6590\u53f3\u81c2\u5916\u7f18\uff0c\u9ed1\u6697\u4e4b\u4e2d\u77a7\u4e0d\u6e05\u5bf9\u65b9\u7a74\u9053\uff0c\u8fd9\u4e00\u6307\u6233\u5728\u808c\u8089\u575a\u539a\u4e4b\u5904\uff0c\u624b\u6307\u4e00\u62d7\uff0c\u201c\u554a\u54df\u201d\u4e00\u58f0\u547c\u75db\u3002\u80e1\u6590\u6697\u53eb\uff1a\u201c\u60ed\u6127\uff01\u5e78\u597d\u5979\u77a7\u4e0d\u6e05\u6211\u8eab\u5f62\uff0c\u5426\u5219\u8fd9\u4e00\u6307\u5df2\u88ab\u70b9\u4e2d\u8981\u7a74\u3002\u201d \u3000\u3000\u4e24\u4eba\u5728\u9ed1\u6697\u4e4b\u4e2d\u8d64\u624b\u640f\u51fb\uff0c\u5747\u662f\u5b88\u5fa1\u591a\uff0c\u8fdb\u653b\u5c11\uff0c\u4e00\u9762\u6253\uff0c\u4e00\u9762\u4fbf\u4fdf\u673a\u53bb\u62a2\u5730\u4e0b\u5175\u5203\u3002\u8881\u7d2b\u8863\u4f46\u89c9\u5bf9\u65b9\u8d8a\u6253\u8d8a\u72e0\uff0c\u5168\u4e0d\u662f\u6bd4\u6b66\u8f83\u91cf\u7684\u6a21\u6837\uff0c\u5fc3\u4e0b\u4e5f\u662f\u8d8a\u6765\u8d8a\u60ca\uff0c\u6697\u60f3\uff1a\u201c\u4ed6\u600e\u5730\u5ffd\u7136\u5982\u6b64\u51f6\u72e0\uff1f\u201d\u5979\u81ea\u51fa\u56de\u7586\u4ee5\u6765\uff0c\u4f1a\u8fc7\u4e0d\u5c11\u597d\u624b\uff0c\u5374\u4ee5\u4eca\u665a\u8fd9\u4e00\u5f79\u6700\u79f0\u6076\u6597\uff0c\u7a81\u7136\u95f4\u8eab\u6cd5\u4e00\u53d8\uff0c\u56db\u4e0b\u6e38\u8d70\uff0c\u518d\u4e0d\u8ba9\u80e1\u6590\u8fd1\u8eab\u3002\u80e1\u6590\u89c1\u5bf9\u65b9\u65e2\u4e0d\u7d27\u903c\uff0c\u5f53\u4e0b\u4e5f\u4e0d\u8ffd\u51fb\uff0c\u53ea\u5b88\u4f4f\u4e86\u95e8\u6237\uff0c\u4fa7\u8033\u9759\u542c\uff0c\u8981\u67e5\u77e5\u51e4\u5929\u5357\u7236\u5b50\u8eb2\u5728\u4f55\u5904\uff0c\u7acb\u5373\u53d1\u638c\u5148\u5c06\u4e24\u4eba\u51fb\u6bd9\u3002\u4f46\u8881\u7d2b\u8863\u5954\u8dd1\u8fc5\u901f\uff0c\u8863\u895f\u5e26\u98ce\uff0c\u638c\u529b\u53d1\u51fa\u6765\u4e5f\u662f\u547c\u547c\u6709\u58f0\uff0c\u7adf\u542c\u4e0d\u51fa\u51e4\u5929\u5357\u7236\u5b50\u7684\u547c\u5438\u4e4b\u58f0\u3002 \u3000\u3000\u80e1\u6590\u5fc3\u751f\u4e00\u8ba1\uff1a\u201c\u5979\u65e2\u56db\u4e0b\u6e38\u8d70\uff0c\u6211\u4fbf\u6765\u4e2a\u4f9d\u6837\u846b\u82a6\u3002\u201d\u5f53\u4e0b\u4ece\u4e1c\u81f3\u897f\uff0c\u81ea\u5357\u8d8b\u5317\uff0c\u4f9d\u7740\u201c\u5927\u56db\u8c61\u65b9\u4f4d\u201d\uff0c\u659c\u884c\u76f4\u51b2\uff0c\u968f\u624b\u80e1\u4e71\u53d1\u638c\uff0c\u53ea\u8981\u51e4\u5929\u5357\u7236\u5b50\u649e\u4e0a\u4e86\uff0c\u4e0d\u6b7b\u4e5f\u5f97\u91cd\u4f24\uff0c\u4fbf\u7b97\u4e0d\u649e\u4e0a\uff0c\u53ea\u8981\u4e00\u67b6\u4e00\u95ea\uff0c\u7acb\u65f6\u4fbf\u53ef\u53d1\u89c9\u4ed6\u7236\u5b50\u85cf\u8eab\u4e4b\u6240\u3002 \u3000\u3000\u4e24\u4eba\u672c\u6765\u8fd1\u8eab\u4e92\u640f\uff0c\u6b64\u65f6\u7a81\u7136\u5404\u81ea\u76f2\u6253\u778e\u649e\uff0c\u4f3c\u4e4e\u4e92\u4e0d\u76f8\u5173\uff0c\u4f46\u53ea\u8981\u6709\u8c01\u8dc3\u8fd1\u5175\u5203\u8dcc\u843d\u4e4b\u5904\uff0c\u53e6\u4e00\u4eba\u7acb\u5373\u51b2\u4e0a\u963b\u6321\uff0c\u6570\u62db\u4e00\u8fc7\uff0c\u53c8\u5404\u907f\u5f00\u3002 \u3000\u3000\u80e1\u6590\u5728\u6bbf\u4e0a\u8f6c\u4e86\u4e00\u5708\uff0c\u6ca1\u53d1\u89c9\u51e4\u5929\u5357\u7236\u5b50\u7684\u8e2a\u8ff9\uff0c\u5fc3\u60f3\uff1a \u3000\u3000\u201c\u83ab\u975e\u4ed6\u5df2\u6e9c\u5230\u4e86\u540e\u6bbf\uff1f\u4e0d\u5bf9\u4e0d\u5bf9\uff01\u773c\u4e0b\u5f7c\u5f3a\u6211\u5f31\uff0c\u4ee5\u4ed6\u4f17\u4eba\u4e4b\u529b\uff0c\u4e00\u62e5\u800c\u4e0a\uff0c\u8db3\u53ef\u5236\u6211\u6b7b\u547d\u3002\u5b9a\u662f\u4ed6\u6b63\u5728\u6697\u4e2d\u53e6\u5e03\u9677\u9631\uff0c\u8bf1\u6211\u5165\u5f40\u3002\u5927\u4e08\u592b\u89c1\u673a\u800c\u4f5c\uff0c\u4eca\u65e5\u5148\u884c\u8131\u8eab\uff0c\u518d\u56fe\u540e\u8ba1\u3002\u201d\u4e8e\u662f\u6162\u6162\u8d70\u5411\u6bbf\u95e8\uff0c\u8981\u5f85\u8dc3\u51fa\u3002\u5ffd\u542c\u5f97\u547c\u5587\u4e00\u54cd\uff0c\u4e00\u80a1\u6781\u731b\u70c8\u7684\u52b2\u98ce\u6251\u9762\u800c\u6765\uff0c\u9ed1\u6697\u4e2d\u9690\u7ea6\u77a7\u6765\uff0c\u6b63\u662f\u4e00\u4e2a\u9b41\u68a7\u7684\u4eba\u5f62\u6251\u5230\u3002\u80e1\u6590\u5927\u559c\uff0c\u53eb\u9053\uff1a\u201c\u6765\u5f97\u597d\uff01\u201d\u53cc\u638c\u9f50\u51fa\uff0c\u7830\u7684\u4e00\u58f0\uff0c\u6b63\u51fb\u5728\u90a3\u4eba\u80f8\u524d\u3002\u8fd9\u4e24\u62f3\u4ed6\u7528\u4e0a\u4e86\u5341\u6210\u4e4b\u529b\uff0c\u51e4\u5929\u5357\u5f53\u573a\u4fbf\u5f97\u7b4b\u6298\u9aa8\u65ad\uff0c\u7acb\u65f6\u6bd9\u547d\u3002 \u3000\u3000\u4f46\u624b\u638c\u752b\u4e0e\u90a3\u4eba\u76f8\u89e6\uff0c\u5df2\u77e5\u4e0a\u5f53\uff0c\u53ea\u89c9\u7740\u624b\u5904\u53c8\u786c\u53c8\u51b7\uff0c\u638c\u529b\u65e2\u53d1\uff0c\u4fbf\u6536\u4e0d\u56de\u6765\uff0c\u56db\u4e0b\u91cc\u6ce5\u5c51\u7eb7\u98de\uff0c\u745f\u745f\u4e71\u54cd\uff0c\u539f\u6765\u6251\u8fc7\u6765\u7684\u7adf\u662f\u5e99\u4e2d\u7684\u795e\u50cf\u3002\u53ea\u542c\u5f97\u53c8\u662f\u7830\u562d\u4e00\u58f0\u5de8\u54cd\uff0c\u90a3\u795e\u50cf\u76f4\u8dcc\u51fa\u53bb\uff0c\u649e\u5728\u5899\u4e0a\uff0c\u767b\u65f6\u788e\u6210\u6570\u622a\u3002\u8881\u7d2b\u8863\u7b11\u9053\uff1a\u201c\u597d\u91cd\u7684\u638c\u529b\uff01\u201d\u8fd9\u58f0\u97f3\u53d1\u81ea\u5c71\u95e8\u4e4b\u5916\uff0c\u8ddf\u7740\u545b\u5577\u5577\u4e00\u54cd\uff0c\u5374\u662f\u8f6f\u97ad\u4e0e\u5355\u5200\u90fd\u5df2\u88ab\u5979\u62a2\u5728\u624b\u4e2d\u3002 \u3000\u3000\u80e1\u6590\u5bfb\u601d\uff1a\u201c\u5175\u5203\u5df2\u88ab\u5979\u593a\u53bb\uff0c\u8be5\u5f53\u4e0a\u524d\u7eed\u6218\uff0c\u8fd8\u662f\u5148\u6c42\u8131\u8eab\uff1f\u201d\u5bf9\u65b9\u867d\u662f\u4e2a\u5999\u9f84\u5c11\u5973\uff0c\u4f46\u6b66\u529f\u4e4b\u5f3a\uff0c\u5b9e\u5728\u4e1d\u6beb\u8f7b\u5ffd\u4e0d\u5f97\uff0c\u5404\u6301\u5175\u5203\u76f8\u6597\uff0c\u4e00\u65f6\u96be\u5206\u4e0a\u4e0b\uff0c\u773c\u4e0b\u5979\u6709\u8f6f\u97ad\u5728\u624b\uff0c\u81ea\u5df1\u53ea\u4f59\u7a7a\u624b\uff0c\u90a3\u5c31\u975e\u5979\u4e4b\u654c\uff0c\u4f55\u51b5\u5979\u5c1a\u6709\u5e2e\u624b\uff0c\u8fd9\u5ff5\u5934\u752b\u5728\u5fc3\u4e2d\u4e00\u8f6c\uff0c\u5ffd\u542c\u5f97\u9a6c\u8e44\u58f0\u54cd\uff0c\u8881\u7d2b\u8863\u53eb\u9053\uff1a\u201c\u5582\uff0c\u5357\u9738\u5929\uff0c\u4f60\u600e\u4e48\u5c31\u8d70\u4e86\uff1f\u53ef\u592a\u4e0d\u591f\u670b\u53cb\u4e86\uff01\u201d\u96e8\u58f0\u4e2d\u9a6c\u8e44\u58f0\u53c8\u54cd\uff0c\u542c\u5f97\u5979\u4e0a\u9a6c\u8ffd\u53bb\u3002 \u3000\u3000\u80e1\u6590\u6697\u53eb\uff1a\u201c\u7f62\u4e86\uff0c\u7f62\u4e86\uff01\u201d\u8fd9\u4e00\u4e0b\u53ef\u8bf4\u662f\u4e00\u8d25\u6d82\u5730\u3002\u867d\u60f3\u51e4\u5929\u5357\u7684\u5bb6\u4eba\u5f1f\u5b50\u5c1a\u5728\u5de6\u8fd1\uff0c\u82e5\u8981\u51fa\u6c14\uff0c\u5b9a\u53ef\u8ffd\u4e0a\u6740\u6b7b\u4e00\u6279\uff0c\u4f46\u7f6a\u9b41\u5df2\u53bb\uff0c\u5374\u53bb\u5bfb\u8fd9\u4e9b\u4eba\u7684\u6666\u6c14\uff0c\u4e0d\u662f\u82f1\u96c4\u6240\u4e3a\u3002 \u3000\u3000\u4ed6\u4ece\u6000\u4e2d\u53d6\u51fa\u706b\u6298\uff0c\u70b9\u71c3\u4e86\u9002\u624d\u7184\u706d\u7684\u67f4\u706b\uff0c\u73af\u987e\u6bbf\u4e2d\uff0c\u53ea\u89c1\u90a3\u6e58\u5983\u795e\u50cf\u5934\u65ad\u81c2\u6298\uff0c\u788e\u6210\u6570\u5757\uff0c\u56db\u4e0b\u91cc\u767d\u7c73\u67f4\u8349\u6492\u6ee1\u4e86\u4e00\u5730\u3002\u5e99\u5916\u5927\u96e8\u5140\u81ea\u672a\u6b62\u3002\u4ed6\u77a7\u7740\u8fd9\u756a\u6076\u6597\u7684\u9057\u8ff9\uff0c\u60f3\u8d77\u9002\u624d\u7684\u51f6\u9669\uff0c\u4e0d\u7531\u5f97\u6697\u81ea\u5fc3\u60ca\uff0c\u770b\u4e86\u4e00\u4f1a\uff0c\u5750\u5728\u795e\u575b\u524d\u7684\u6728\u62dc\u57ab\u4e0a\uff0c\u671b\u7740\u4e00\u56e2\u706b\u5149\uff0c\u5446\u5446\u51fa\u795e\u3002 \u3000\u3000\u5fc3\u60f3\uff1a\u201c\u8881\u59d1\u5a18\u4e0e\u51e4\u5929\u5357\u5fc5\u6709\u74dc\u845b\uff0c\u90a3\u662f\u786e\u5b9a\u65e0\u7591\u7684\u4e86\u3002 \u3000\u3000\u8fd9\u5357\u9738\u5929\u65e2\u6709\u5982\u6b64\u5f3a\u63f4\uff0c\u518d\u52a0\u4e0a\u4f5b\u5c71\u9547\u4e0a\u4eba\u591a\u52bf\u4f17\uff0c\u5236\u6211\u8db3\u8db3\u6709\u4f59\uff0c\u5374\u4f55\u4ee5\u8981\u6bc1\u5bb6\u51fa\u8d70\uff1f\u4ed6\u4eec\u4eca\u65e5\u5728\u8fd9\u53e4\u5e99\u4e2d\u8bbe\u4f0f\uff0c\u6211\u5df2\u7136\u4e2d\u8ba1\uff0c\u82e5\u662f\u9f50\u4e0a\u56f4\u653b\uff0c\u6211\u5927\u6709\u6027\u547d\u4e4b\u5fe7\uff0c\u4f55\u4ee5\u65e2\u5360\u4e0a\u98ce\uff0c\u53cd\u800c\u9000\u8d70\uff1f\u77a7\u90a3\u51e4\u5929\u5357\u7684\u795e\u60c5\uff0c\u4e24\u6b21\u81ea\u6215\uff0c\u534a\u70b9\u4e0d\u5047\uff0c\u90a3\u4e48\u8881\u59d1\u5a18\u6697\u4e2d\u76f8\u52a9\uff0c\u4ed6\u4e8b\u5148\u662f\u4e0d\u77e5\u7684\u4e86\u3002\u201d \u3000\u3000\u518d\u60f3\u8d77\u8881\u7d2b\u8863\u6b66\u529f\u6e0a\u535a\uff0c\u667a\u8ba1\u767e\u51fa\uff0c\u6bcf\u6b21\u4e0e\u5979\u8f83\u91cf\uff0c\u603b\u662f\u7ed9\u5979\u62a2\u4e86\u5148\u7740\u3002\u9002\u624d\u9ed1\u6697\u4e2d\u6fc0\u6597\uff0c\u552f\u6050\u60e8\u8d25\uff0c\u5c06\u5979\u89c6\u4f5c\u5927\u654c\uff0c\u6b64\u65f6\u56de\u60f3\uff0c\u5634\u89d2\u8fb9\u5ffd\u9732\u5fae\u7b11\uff0c\u80f8\u4e2d\u67d4\u60c5\u6697\u751f\u3002 \u3000\u3000\u4e0d\u81ea\u7981\u60f3\u5230\uff1a\u201c\u6211\u8ddf\u5979\u72e0\u6597\u4e4b\u65f6\uff0c\u51fa\u624b\u5f53\u771f\u662f\u6beb\u4e0d\u7559\u60c5\uff1f\u201d \u3000\u3000\u8fd9\u4e00\u95ee\u8fde\u81ea\u5df1\u4e5f\u96be\u4ee5\u56de\u7b54\uff0c\u4f3c\u4e4e\u786e\u5df2\u51fa\u4e86\u5168\u529b\uff0c\u4f46\u4f3c\u4e4e\u53c8\u672a\u771f\u4e0b\u6740\u624b\u3002\u201c\u5f53\u5979\u6251\u8fd1\u5288\u638c\u4e4b\u65f6\uff0c\u6211\u90a3\u2018\u7a7f\u5fc3\u9525\u2019\u7684\u5389\u5bb3\u6740\u7740\u4e3a\u4f55\u4e0d\u7528\uff1f\u6211\u4e00\u62db\u2018\u4e0a\u9a6c\u5200\u2019\u780d\u51fa\uff0c\u5979\u4f4e\u5934\u907f\u8fc7\uff0c\u6211\u4e3a\u4ec0\u4e48\u4e0d\u8ddf\u7740\u4f7f\u2018\u9738\u738b\u5378\u7532\u2019\uff1f\u80e1\u6590\u554a\u80e1\u6590\uff0c\u4f60\u662f\u6015\u4f24\u7740\u5979\u554a\u3002\u201d\u7a81\u7136\u95f4\u5fc3\u4e2d\u4e00\u52a8\uff1a\u201c\u5979\u90a3\u4e00\u97ad\u521a\u8981\u6253\u5230\u6211\u80a9\u5934\uff0c\u5ffd\u5730\u6536\u4e86\u56de\u53bb\uff0c\u90a3\u662f\u6709\u610f\u76f8\u8ba9\u5462\uff0c\u8fd8\u662f\u4e0d\u8fc7\u51d1\u5de7\uff1f\u8fd8\u6709\uff0c\u90a3\u4e00\u811a\u8e22\u4e2d\u4e86\u6211\u5de6\u817f\uff0c\u4f55\u4ee5\u7acb\u65f6\u6536\u529b\uff1f\u201d \u3000\u3000\u56de\u5fc6\u9002\u624d\u7684\u62db\u6570\uff0c\u7ec6\u7ec6\u6790\u89e3\uff0c\u5fc3\u4e2d\u767b\u65f6\u611f\u5230\u4e00\u4e1d\u4e1d\u7684\u751c\u610f\uff1a\u201c\u5979\u51b3\u4e0d\u60f3\u4f24\u6211\u6027\u547d\uff01\u5979\u51b3\u4e0d\u60f3\u4f24\u6211\u6027\u547d\u3002\u96be\u9053\u2026\u2026\u96be\u9053\u2026\u2026\u201d\u60f3\u5230\u8fd9\u91cc\uff0c\u4e0d\u6562\u518d\u5f80\u4e0b\u60f3\uff0c\u53ea\u89c9\u5f97\u8179\u4e2d\u9965\u997f\uff0c\u63d0\u8d77\u9002\u624d\u8e22\u7ffb\u4e86\u7684\u94c1\u9505\uff0c\u9505\u4e2d\u8fd8\u5269\u7740\u4e00\u4e9b\u767d\u7c73\uff0c\u4e8e\u662f\u5c06\u5012\u6cfb\u5728\u5730\u7684\u767d\u7c73\u6293\u8d77\u51e0\u628a\uff0c\u5728\u5927\u96e8\u4e2d\u51b2\u53bb\u6ce5\u6c61\uff0c\u653e\u5165\u9505\u4e2d\uff0c\u751f\u706b\u716e\u4e86\u8d77\u6765\u3002 \u3000\u3000\u8fc7\u4e0d\u591a\u65f6\uff0c\u9505\u4e2d\u6e10\u6e10\u900f\u51fa\u996d\u9999\uff0c\u4ed6\u53f9\u4e86\u4e00\u53e3\u957f\u6c14\uff0c\u5fc3\u60f3\uff1a \u3000\u3000\u201c\u82e5\u662f\u6b64\u523b\u6211\u548c\u5979\u5e76\u80a9\u5171\u708a\uff0c\u90a3\u662f\u4f55\u7b49\u98ce\u5149\uff1f\u504f\u751f\u51e4\u5929\u5357\u8fd9\u6076\u8d3c\u95ef\u8fdb\u5e99\u6765\u3002\u201d\u8f6c\u5ff5\u4e00\u60f3\uff1a\u201c\u4e0e\u51e4\u5929\u5357\u72ed\u8def\u76f8\u9022\uff0c\u539f\u662f\u4f73\u4e8b\u3002\u6211\u80e1\u601d\u4e71\u60f3\uff0c\u53ef\u83ab\u8bef\u5165\u4e86\u6b67\u9014\u3002\u201d \u3000\u3000\u5fc3\u4e2d\u6697\u81ea\u8b66\u60d5\uff0c\u4f46\u8881\u7d2b\u8863\u5de7\u7b11\u5ae3\u7136\u7684\u5bb9\u8c8c\uff0c\u603b\u662f\u5728\u8111\u6d77\u4e2d\u76d8\u65cb\u6765\u53bb\uff0c\u7c73\u996d\u6e10\u7126\uff0c\u7adf\u81ea\u4e0d\u89c9\u3002 \u3000\u3000\u5c31\u5728\u6b64\u65f6\uff0c\u5e99\u95e8\u5916\u811a\u6b65\u58f0\u54cd\uff0c\u554a\u7684\u4e00\u58f0\uff0c\u5e99\u95e8\u8f7b\u8f7b\u63a8\u5f00\u3002 \u3000\u3000\u80e1\u6590\u53c8\u60ca\u53c8\u559c\uff0c\u8dc3\u8d77\u8eab\u6765\uff0c\u5fc3\u9053\uff1a\u201c\u5979\u56de\u6765\u4e86\uff01\u201d \u3000\u3000\u706b\u5149\u4e0b\u5374\u89c1\u8fdb\u6765\u4e24\u4eba\uff0c\u4e00\u4e2a\u662f\u4e94\u5341\u5c81\u5de6\u53f3\u7684\u8001\u8005\uff0c\u8138\u8272\u67af\u9ec4\uff0c\u5f62\u5bb9\u7626\u524a\uff0c\u6b63\u662f\u5728\u8861\u9633\u67ab\u53f6\u5e84\u89c1\u8fc7\u7684\u5218\u9e64\u771f\uff0c\u53e6\u4e00\u4eba\u662f\u4e2a\u4e8c\u5341\u4f59\u5c81\u7684\u5c11\u5987\u3002 \u3000\u3000\u90a3\u5218\u9e64\u771f\u4e00\u53ea\u624b\u7528\u9752\u5e03\u7f20\u7740\uff0c\u6302\u5728\u9888\u4e2d\uff0c\u663e\u662f\u53d7\u4e86\u4f24\u3002\u90a3\u5c11\u5987\u8d70\u8def\u4e00\u8df7\u4e00\u62d0\uff0c\u817f\u4e0a\u53d7\u4f24\u4e5f\u81ea\u4e0d\u8f7b\u3002\u4e24\u4eba\u5168\u8eab\u5c3d\u6e7f\uff0c\u6a21\u6837\u751a\u662f\u72fc\u72c8\u3002\u80e1\u6590\u6b63\u5f85\u5f00\u53e3\u62db\u547c\uff0c\u5218\u9e64\u771f\u6f20\u7136\u5411\u4ed6\u671b\u4e86\u4e00\u773c\uff0c\u5411\u90a3\u5c11\u5987\u9053\uff1a\u201c\u4f60\u5230\u91cc\u8fb9\u77a7\u77a7\uff01\u201d\u90a3\u5c11\u5987\u9053\uff1a\u201c\u662f\uff01\u201d\u4ece\u8170\u95f4\u62d4\u51fa\u5355\u5200\uff0c\u8d70\u5411\u540e\u6bbf\u3002\u5218\u9e64\u771f\u9760\u5728\u795e\u575b\u4e0a\u5598\u606f\u51e0\u4e0b\uff0c\u7a81\u7136\u5750\u5012\uff0c\u8138\u4e0a\u795e\u8272\u662f\u5728\u503e\u542c\u5e99\u5916\u58f0\u606f\u3002 \u3000\u3000\u80e1\u6590\u89c1\u4ed6\u5e76\u672a\u8ba4\u51fa\u81ea\u5df1\uff0c\u5fc3\u60f3\uff1a\u201c\u90a3\u65e5\u67ab\u53f6\u5e84\u6bd4\u6b66\uff0c\u4eba\u4eba\u90fd\u8ba4\u5f97\u4ed6\u548c\u8881\u59d1\u5a18\u3002\u6211\u6742\u5728\u4eba\u7fa4\u4e4b\u4e2d\uff0c\u8fd9\u6837\u4e00\u4e2a\u4e61\u4e0b\u5c0f\u5b50\uff0c\u4ed6\u81ea\u662f\u4e0d\u4f1a\u8ba4\u5f97\u4e86\u3002\u201d\u63ed\u5f00\u9505\u76d6\uff0c\u7126\u6c14\u6251\u9f3b\uff0c\u5374\u6709\u534a\u9505\u996d\u716e\u5f97\u7126\u4e86\u3002\u80e1\u6590\u5fae\u5fae\u4e00\u7b11\uff0c\u4f38\u624b\u6293\u4e86\u4e2a\u996d\u56e2\uff0c\u585e\u5728\u53e3\u4e2d\u5927\u56bc\uff0c\u6599\u60f3\u5218\u9e64\u771f\u89c1\u4e86\u81ea\u5df1\u8fd9\u526f\u5403\u996d\u7684\u7c97\u9c81\u6a21\u6837\uff0c\u66f4\u662f\u4e0d\u5728\u610f\u4e0b\u3002 \u3000\u3000\u8fc7\u4e86\u7247\u523b\uff0c\u90a3\u5c11\u5987\u4ece\u540e\u6bbf\u51fa\u6765\uff0c\u624b\u4e2d\u6267\u7740\u4e00\u6839\u70b9\u71c3\u7684\u67f4\u706b\uff0c\u5411\u5218\u9e64\u771f\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\u3002\u201d\u5218\u9e64\u771f\u5401\u4e86\u53e3\u6c14\uff0c\u663e\u662f\u6212\u5907\u4e4b\u5fc3\u7a0d\u61c8\uff0c\u95ed\u76ee\u501a\u7740\u795e\u575b\u517b\u795e\uff0c\u8863\u670d\u4e0a\u7684\u96e8\u6c34\u5728\u5730\u4e0b\u6d41\u6210\u4e86\u4e00\u6761\u5c0f\u6eaa\u6d41\uff0c\u6c34\u4e2d\u6df7\u7740\u9c9c\u8840\u3002\u90a3\u5c11\u5987\u4e5f\u662f\u7b4b\u75b2\u529b\u5c3d\uff0c\u4e0e\u4ed6\u504e\u501a\u5728\u4e00\u8d77\uff0c\u52a8\u4e5f\u4e0d\u52a8\u3002\u77a7\u4e24\u4eba\u795e\u60c5\uff0c\u4f3c\u662f\u4e00\u5bf9\u592b\u5987\uff0c\u53ea\u662f\u8001\u592b\u5c11\u59bb\uff0c\u5e74\u7eaa\u4e0d\u79f0\u3002 \u3000\u3000\u80e1\u6590\u5fc3\u60f3\uff1a\u201c\u51ed\u7740\u5218\u9e64\u771f\u7684\u529f\u592b\uff0c\u6b66\u6797\u4e2d\u8be5\u5f53\u5df2\u5c11\u654c\u624b\uff0c\u600e\u4f1a\u8d25\u5f97\u5982\u6b64\u72fc\u72c8\uff1f\u53ef\u89c1\u6c5f\u6e56\u95f4\u5929\u4e0a\u6709\u5929\uff0c\u4eba\u4e0a\u6709\u4eba\uff0c\u5b9e\u662f\u5927\u610f\u4e0d\u5f97\u3002\u201d\u4fbf\u5728\u6b64\u65f6\uff0c\u9690\u9690\u542c\u5f97\u8fdc\u5904\u53c8\u6709\u9a6c\u8e44\u58f0\u4f20\u6765\u3002 \u3000\u3000\u5218\u9e64\u771f\u970d\u5730\u7ad9\u8d77\uff0c\u4f38\u624b\u5230\u8170\u95f4\u4e00\u62c9\uff0c\u53d6\u51fa\u4e00\u4ef6\u5175\u5203\uff0c\u5374\u662f\u4e00\u6761\u94fe\u5b50\u77ed\u67aa\uff0c\u8bf4\u9053\uff1a\u201c\u4ef2\u840d\uff0c\u4f60\u5feb\u8d70\uff01\u6211\u7559\u5728\u8fd9\u513f\u8ddf\u4ed6\u4eec\u62da\u4e86\u3002\u201d\u53c8\u4ece\u6000\u91cc\u53d6\u51fa\u4e00\u5305\u5c3a\u6765\u957f\u4e4b\u7269\uff0c\u4ea4\u5728\u5979\u7684\u624b\u91cc\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u4f60\u9001\u53bb\u7ed9\u4ed6\u3002\u201d \u3000\u3000\u90a3\u5c11\u5987\u773c\u5708\u513f\u4e00\u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\uff0c\u8981\u6b7b\u4fbf\u5927\u5bb6\u6b7b\u5728\u4e00\u8d77\u3002\u201d \u3000\u3000\u5218\u9e64\u771f\u6012\u9053\uff1a\u201c\u54b1\u4eec\u5343\u8f9b\u4e07\u82e6\uff0c\u8d1f\u4f24\u529b\u6218\uff0c\u4e3a\u7684\u662f\u4f55\u6765\uff1f\u6b64\u4e8b\u82e5\u4e0d\u529e\u5230\uff0c\u6211\u6b7b\u4e0d\u7791\u76ee\uff0c\u4f60\u5feb\u4ece\u540e\u95e8\u9003\u8d70\uff0c\u6211\u7f20\u4f4f\u654c\u4eba\u3002\u201d\u90a3\u5c11\u5987\u5140\u81ea\u604b\u604b\u4e0d\u80af\u4fbf\u884c\uff0c\u54ed\u9053\uff1a\u201c\u8001\u7237\u5b50\uff0c\u4f60\u6211\u592b\u59bb\u4e00\u573a\uff0c\u6211\u6ca1\u597d\u597d\u670d\u4f8d\u4f60\uff0c\u4fbf\u8fd9\u4e48\u2026\u2026\u8fd9\u4e48\u2026\u2026\u201d\u5218\u9e64\u771f\u987f\u8db3\u9053\uff1a\u201c\u4f60\u7ed9\u6211\u529e\u59a5\u8fd9\u4ef6\u5927\u4e8b\uff0c\u6bd4\u4ec0\u4e48\u670d\u4f8d\u90fd\u5f3a\u3002\u201d\u5de6\u624b\u6025\u6325\uff0c\u9053\uff1a\u201c\u5feb\u8d70\uff0c\u5feb\u8d70\uff01\u201d \u3000\u3000\u80e1\u6590\u89c1\u4ed6\u592b\u59bb\u60c5\u91cd\uff0c\u96be\u5206\u96be\u820d\uff0c\u5fc3\u4e2d\u4e0d\u5fcd\uff0c\u6697\u60f3\uff1a\u201c\u8fd9\u5218\u9e64\u771f\u4e3a\u4eba\u6b63\u6d3e\uff0c\u4e0d\u77e5\u662f\u4ec0\u4e48\u4eba\u8ddf\u4ed6\u4e3a\u96be\uff0c\u65e2\u53eb\u6211\u649e\u89c1\u4e86\uff0c\u53ef\u4e0d\u80fd\u4e0d\u7406\u3002\u201d \u3000\u3000\u4fbf\u5728\u6b64\u65f6\uff0c\u9a6c\u8e44\u58f0\u5df2\u5728\u5e99\u95e8\u5916\u505c\u4f4f\uff0c\u542c\u58f0\u97f3\u5171\u662f\u4e09\u5339\u5750\u9a91\uff0c\u4e24\u5339\u505c\u5728\u95e8\u524d\uff0c\u4e00\u5339\u5374\u7ed5\u5230\u4e86\u5e99\u540e\u3002 \u3000\u3000\u5218\u9e64\u771f\u8138\u73b0\u6012\u8272\uff0c\u9053\uff1a\u201c\u7ed9\u4eba\u5bb6\u5835\u4f4f\u4e86\u540e\u95e8\uff0c\u8d70\u4e0d\u4e86\u5566\u3002\u201d \u3000\u3000\u90a3\u5c11\u5987\u56db\u4e0b\u4e00\u671b\uff0c\u6276\u7740\u4e08\u592b\u624b\u81c2\uff0c\u722c\u4e0a\u795e\u575b\uff0c\u8eb2\u5165\u4e86\u795e\u9f9b\u4e4b\u4e2d\uff0c\u5411\u80e1\u6590\u505a\u4e2a\u624b\u52bf\uff0c\u6ee1\u8138\u6c42\u6073\u4e4b\u8272\uff0c\u53eb\u4ed6\u5343\u4e07\u4e0d\u53ef\u6cc4\u6f0f\u3002 \u3000\u3000\u795e\u9f9b\u524d\u7684\u9ec4\u5e54\u5782\u4e0b\u4e86\u4e0d\u4e45\uff0c\u5e99\u95e8\u4e2d\u4fbf\u8d70\u8fdb\u4e24\u4e2a\u4eba\u6765\u3002\u80e1\u6590\u4ecd\u662f\u5750\u5728\u5730\u4e0b\uff0c\u6293\u7740\u996d\u56e2\u6162\u6162\u5480\u56bc\uff0c\u659c\u76ee\u5411\u90a3\u4e24\u4eba\u77a7\u53bb\uff0c\u9976\u662f\u6c5f\u6e56\u4e0a\u7684\u602a\u4eba\u89c1\u8fc7\u4e0d\u5c11\uff0c\u6b64\u523b\u4e5f\u4e0d\u7981\u4e00\u60ca\uff0c\u4f46\u89c1\u8fd9\u4e24\u4eba\u53cc\u76ee\u5411\u4e0b\u659c\u5782\uff0c\u773c\u6210\u4e09\u89d2\uff0c\u4e00\u5927\u4e00\u5c0f\uff0c\u9f3b\u5b50\u5927\u800c\u4e14\u6241\uff0c\u9f3b\u5b54\u671d\u5929\uff0c\u76f8\u8c8c\u5b9e\u662f\u5947\u4e11\u3002 \u3000\u3000\u4e24\u4eba\u5411\u80e1\u6590\u77a7\u4e86\u77a7\uff0c\u5e76\u4e0d\u7406\u4f1a\uff0c\u4e00\u5de6\u4e00\u53f3\uff0c\u8d70\u5230\u4e86\u540e\u6bbf\uff0c\u8fc7\u4e0d\u591a\u65f6\u91cd\u53c8\u51fa\u6765\uff0c\u9662\u5b50\u4e2d\u8f7b\u8f7b\u4e00\u54cd\uff0c\u4e00\u4eba\u4ece\u5c4b\u9876\u8dc3\u4e0b\u3002\u539f\u6765\u5f53\u4e24\u4eba\u524d\u540e\u641c\u67e5\u4e4b\u9645\uff0c\u5835\u4f4f\u540e\u95e8\u90a3\u4eba\u5df2\u8dc3\u5230\u4e86\u5c4b\u9876\u76d1\u89c6\u3002 \u3000\u3000\u80e1\u6590\u5fc3\u9053\uff1a\u201c\u8fd9\u4eba\u7684\u8f7b\u529f\u597d\u751f\u4e86\u5f97\uff01\u201d\u4f46\u89c1\u4eba\u5f71\u4e00\u6643\uff0c\u90a3\u4eba\u4e5f\u8d70\u8fdb\u6bbf\u6765\u3002\u77a7\u4ed6\u5f62\u8c8c\uff0c\u4e0e\u5148\u524d\u4e24\u4eba\u65e0\u5927\u5dee\u522b\uff0c\u4e00\u671b\u800c\u77e5\u4e09\u4eba\u662f\u540c\u80de\u5144\u5f1f\u3002 \u3000\u3000\u4e09\u4eba\u9664\u4e0b\u8eab\u4e0a\u62ab\u7740\u7684\u6cb9\u5e03\u96e8\u8863\uff0c\u80e1\u6590\u53c8\u662f\u4e00\u60ca\uff0c\u539f\u6765\u4e09\u4eba\u62ab\u9ebb\u5e26\u5b5d\uff0c\u7a7f\u7684\u662f\u6bdb\u8fb9\u7c97\u5e03\u5b5d\u8863\uff0c\u8349\u7ef3\u675f\u8170\uff0c\u9ebb\u5e03\u56f4\u9888\uff0c\u4fbf\u4f3c\u521a\u6b7b\u4e86\u7236\u6bcd\u4e00\u822c\u3002\u5927\u6bbf\u4e0a\u5168\u51ed\u4e00\u6839\u67f4\u706b\u7167\u660e\uff0c\u96e8\u58f0\u6dc5\u6ca5\uff0c\u51c9\u98ce\u98d5\u98d5\uff0c\u5439\u5f97\u706b\u5149\u5ffd\u660e\u5ffd\u6697\uff0c\u5c06\u4e09\u4e2a\u4eba\u5f71\u6620\u7167\u5728\u5899\u58c1\u4e4b\u4e0a\uff0c\u500f\u5927\u500f\u5c0f\uff0c\u5b9b\u4f3c\u9b3c\u9b45\u3002 \u3000\u3000\u53ea\u542c\u6700\u540e\u8fdb\u6765\u90a3\u4eba\u9053\uff1a\u201c\u5927\u54e5\uff0c\u7537\u5973\u4e24\u4e2a\u90fd\u53d7\u4e86\u4f24\uff0c\u53c8\u6ca1\u5750\u9a91\uff0c\u7167\u7406\u4e0d\u4f1a\u8d70\u8fdc\uff0c\u5de6\u8fd1\u53c8\u65e0\u4eba\u5bb6\uff0c\u5374\u8eb2\u53bb\u4e86\u54ea\u91cc\uff1f\u201d\u5e74\u7eaa\u6700\u5927\u7684\u4eba\u9053\uff1a\u201c\u591a\u534a\u8eb2\u5728\u4ec0\u4e48\u5c71\u6d1e\u8349\u4e1b\u4e4b\u4e2d\u3002\u54b1\u4eec\u4f11\u5acc\u70e6\u52b3\uff0c\u4fbf\u5230\u5916\u9762\u641c\u53bb\u3002\u4ed6\u4eec\u867d\u7136\u4f24\u4e86\u624b\u8db3\uff0c\u4f46\u4f24\u52bf\u4e0d\u91cd\uff0c\u90a3\u8001\u5934\u624b\u4e0b\u7740\u5b9e\u5389\u5bb3\uff0c\u5927\u5bb6\u987b\u5f97\u5c0f\u5fc3\u3002\u201d\u53e6\u4e00\u4eba\u8f6c\u8eab\u6b63\u8981\u8d70\u51fa\uff0c\u7a81\u7136\u505c\u6b65\uff0c\u95ee\u80e1\u6590\u9053\uff1a\u201c\u5582\uff0c\u5c0f\u5b50\uff0c\u4f60\u6709\u6ca1\u89c1\u5230\u4e00\u4e2a\u8001\u5934\u548c\u4e00\u4e2a\u5e74\u8f7b\u5802\u5ba2\uff1f\u201d\u80e1\u6590\u53e3\u4e2d\u56bc\u996d\uff0c\u60d8\u7136\u6447\u4e86\u6447\u5934\u3002 \u3000\u3000\u90a3\u5927\u54e5\u56db\u4e0b\u77a7\u4e86\u77a7\uff0c\u89c1\u5730\u4e0b\u4e03\u96f6\u516b\u843d\u5730\u6563\u6ee1\u4e86\u7bb1\u7b3c\u8863\u7269\uff0c\u4e00\u5177\u795e\u50cf\u53c8\u5728\u5899\u811a\u4e0b\u788e\u6210\u6570\u5757\uff0c\u5fc3\u4e2d\u8d77\u7591\uff0c\u4ed4\u7ec6\u5bdf\u770b\u5730\u4e0b\u7684\u5e26\u6c34\u8db3\u5370\u3002 \u3000\u3000\u5218\u9e64\u771f\u592b\u5987\u5192\u96e8\u8fdb\u5e99\uff0c\u8db3\u5e95\u4e0b\u81ea\u7136\u62d6\u6ce5\u5e26\u6c34\u3002\u80e1\u6590\u773c\u5149\u5fae\u659c\uff0c\u5df2\u89c1\u5230\u795e\u575b\u4e0a\u7684\u8db3\u8ff9\uff0c\u5fd9\u9053\uff1a\u201c\u521a\u624d\u6709\u597d\u51e0\u4e2a\u4eba\u5728\u8fd9\u91cc\u6253\u67b6\uff0c\u6709\u7537\u6709\u5973\uff0c\u6709\u8001\u6709\u5c11\uff0c\u628a\u6e58\u5983\u5a18\u5a18\u4e5f\u6253\u5728\u5730\u4e0b\u3002\u6709\u7684\u9003\uff0c\u6709\u7684\u8ffd\uff0c\u90fd\u9a91\u9a6c\u8d70\u4e86\u3002\u201d \u3000\u3000\u90a3\u4e09\u5f1f\u8d70\u5230\u5eca\u4e0b\uff0c\u679c\u89c1\u6709\u8bb8\u591a\u9a6c\u8e44\u548c\u8f66\u8f6e\u7684\u6ce5\u5370\uff0c\u5140\u81ea\u672a\u5e72\uff0c\u76f8\u4fe1\u80e1\u6590\u4e4b\u8a00\u4e0d\u5047\uff0c\u56de\u8fdb\u6765\u95ee\u9053\uff1a\u201c\u4ed6\u4eec\u671d\u54ea\u4e00\u8fb9\u53bb\u7684\uff1f\u201d \u3000\u3000\u80e1\u6590\u9053\uff1a\u201c\u597d\u50cf\u662f\u5f80\u5317\u53bb\u7684\u3002\u5c0f\u7684\u8eb2\u5728\u684c\u5b50\u5e95\u4e0b\uff0c\u4e5f\u4e0d\u6562\u591a\u77a7\u2026\u2026\u201d\u90a3\u4e09\u5f1f\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u662f\u4e86\uff01\u201d\u53d6\u51fa\u4e00\u5c0f\u952d\u94f6\u5b50\uff0c\u7ea6\u83ab\u6709\u56db\u4e94\u94b1\u91cd\uff0c\u629b\u5728\u80e1\u6590\u8eab\u524d\uff0c\u9053\uff1a\u201c\u7ed9\u4f60\u5427\uff01\u201d\u80e1\u6590\u8fde\u79f0\uff1a\u201c\u591a\u8c22\u3002\u201d\u62fe\u8d77\u94f6\u5b50\u4e0d\u4f4f\u629a\u6478\uff0c\u8138\u4e0a\u663e\u5f97\u559c\u4e0d\u81ea\u80dc\uff0c\u5fc3\u4e2d\u5374\u60f3\uff1a \u3000\u3000\u201c\u8fd9\u4e09\u4eba\u6076\u9b3c\u4e00\u822c\uff0c\u6b66\u529f\u4e0d\u5f31\uff0c\u82e5\u662f\u8ffd\u4e0a\u4e86\u51e4\u5929\u5357\u4ed6\u4eec\uff0c\u4e71\u6253\u4e00\u6c14\uff0c\u5012\u4e5f\u662f\u4e00\u573a\u597d\u620f\u3002\u201d \u3000\u3000\u90a3\u4e8c\u54e5\u9053\uff1a\u201c\u8001\u5927\uff0c\u8001\u4e09\uff0c\u8d70\u5427\uff01\u201d\u4e09\u4eba\u62ab\u4e0a\u96e8\u8863\uff0c\u8d70\u51fa\u5e99\u95e8\u3002\u80e1\u6590\u4f9d\u7a00\u542c\u5230\u4e00\u4eba\u8bf4\u9053\uff1a\u201c\u8fd9\u4e2d\u95f4\u7684\u8be1\u8ba1\u5b9a\u7136\u5389\u5bb3\uff0c\u65e0\u8bba\u5982\u4f55\u4e0d\u80fd\u8ba9\u4ed6\u62a2\u5728\u524d\u5934\u2026\u2026\u201d\u53c8\u4e00\u4eba\u9053\uff1a\u201c\u82e5\u662f\u622a\u62e6\u4e0d\u4f4f\uff0c\u4e0d\u5982\u8d76\u53bb\u62a5\u4fe1\u3002\u201d\u5148\u524d\u90a3\u4eba\u9053\uff1a\u201c\u5509\uff0c\u54b1\u4eec\u7684\u8bf4\u8bdd\uff0c\u4ed6\u600e\u80af\u76f8\u4fe1\uff1f\u4f55\u51b5\u2026\u2026\u201d\u8fd9\u65f6\u4e09\u4eba\u8d70\u5165\u5927\u96e8\u4e4b\u4e2d\uff0c\u4ee5\u540e\u7684\u8bf4\u8bdd\u7ed9\u96e8\u58f0\u63a9\u6ca1\uff0c\u518d\u4e5f\u542c\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u80e1\u6590\u5fc3\u4e2d\u5947\u602a\uff1a\u201c\u4e0d\u77e5\u662f\u4ec0\u4e48\u5389\u5bb3\u7684\u8be1\u8ba1\uff1f\u53c8\u8981\u53bb\u7ed9\u8c01\u62a5\u4fe1\u4e86\uff1f\u201d\u542c\u5f97\u795e\u9f9b\u4e2d\u5580\u5587\u51e0\u58f0\uff0c\u90a3\u5c11\u5987\u6276\u7740\u5218\u9e64\u771f\u722c\u4e0b\u795e\u575b\u3002 \u3000\u3000\u65e5\u524d\u89c1\u4ed6\u5728\u67ab\u53f6\u5e84\u4e0e\u8881\u7d2b\u8863\u6bd4\u6b66\uff0c\u8eab\u624b\u4f55\u7b49\u77eb\u6377\uff0c\u6b64\u65f6\u4fbf\u722c\u4e0b\u4e00\u5f20\u77ee\u77ee\u7684\u795e\u575b\uff0c\u4e5f\u662f\u98a4\u5dcd\u5dcd\u7684\u552f\u6050\u6454\u8dcc\uff0c\u80e1\u6590\u5fc3\u60f3\uff1a\u201c\u602a\u4e0d\u5f97\u4ed6\u53d7\u4f24\u5982\u6b64\u6c89\u91cd\u3002\u90a3\u4e09\u4e2a\u6076\u9b3c\u8054\u624b\u8fdb\u653b\uff0c\u539f\u4e5f\u96be\u654c\u3002\u201d \u3000\u3000\u5218\u9e64\u771f\u4e0b\u4e86\u795e\u575b\uff0c\u5411\u80e1\u6590\u884c\u4e0b\u793c\u53bb\uff0c\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u5c0f\u54e5\u6551\u547d\u5927\u6069\u3002\u201d\u80e1\u6590\u8fde\u5fd9\u8fd8\u793c\uff0c\u4ed6\u4e0d\u6b32\u900f\u9732\u8eab\u5206\uff0c\u4ecd\u662f\u88c5\u4f5c\u4e61\u519c\u6a21\u6837\uff0c\u7b11\u9053\uff1a\u201c\u90a3\u4e09\u4e2a\u5bb6\u4f19\u5f3a\u6a2a\u9738\u9053\uff0c\u51f6\u795e\u6076\u715e\u4e00\u822c\uff0c\u5f00\u53e3\u4fbf\u662f\u5c0f\u5b50\u957f\u3001\u5c0f\u5b50\u77ed\u7684\uff0c\u6211\u624d\u4e0d\u8ddf\u4ed6\u4eec\u8bf4\u771f\u8bdd\u5462\u3002\u201d\u5218\u9e64\u771f\u9053\uff1a\u201c\u6211\u59d3\u5218\uff0c\u540d\u53eb\u9e64\u771f\uff0c\u5979\u662f\u6211\u8001\u5a46\u3002\u5c0f\u54e5\u4f60\u8d35\u59d3\u554a\uff1f\u201d \u3000\u3000\u80e1\u6590\u5fc3\u60f3\uff1a\u201c\u4f60\u65e2\u8ddf\u6211\u8bf4\u771f\u59d3\u540d\uff0c\u6211\u4e5f\u4e0d\u80fd\u7792\u4f60\u3002\u4f46\u6211\u7684\u540d\u5b57\u4e0d\u50cf\u4e61\u519c\uff0c\u987b\u5f97\u7a0d\u7a0d\u53d8\u4e0a\u4e00\u53d8\u3002\u201d\u4e8e\u662f\u8bf4\u9053\uff1a\u201c\u6211\u59d3\u80e1\uff0c\u53eb\u505a\u80e1\u963f\u5927\u3002\u201d\u4ed6\u60f3\u7239\u5988\u53ea\u751f\u6211\u4e00\u4eba\uff0c\u81ea\u79f0\u963f\u5927\uff0c\u4e5f\u975e\u8bf4\u8c0e\u3002 \u3000\u3000\u5218\u9e64\u771f\u9053\uff1a\u201c\u5c0f\u54e5\u5fc3\u5730\u597d\uff0c\u5c06\u6765\u5b9a\u662f\u540e\u798f\u65e0\u7a77\u2026\u2026\u201d\u8bf4\u5230\u8fd9\u91cc\uff0c\u7709\u5934\u4e00\u76b1\uff0c\u54ac\u7259\u5fcd\u75db\u3002\u90a3\u5c11\u5987\u6025\u9053\uff1a\u201c\u8001\u7237\u5b50\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u201d\u5218\u9e64\u771f\u6447\u4e86\u6447\u5934\uff0c\u501a\u5728\u795e\u575b\u4e0a\u53ea\u662f\u5598\u6c14\u3002\u80e1\u6590\u5fc3\u60f3\u4ed6\u592b\u5987\u4e8c\u4eba\u5fc5\u6709\u8bdd\u8bf4\uff0c\u81ea\u5df1\u5728\u65c1\u4e0d\u4fbf\uff0c\u4e8e\u662f\u9053\uff1a\u201c\u5218\u8001\u7237\u5b50\uff0c\u6211\u5230\u540e\u8fb9\u7761\u53bb\u3002\u201d\u8bf4\u7740\u70b9\u4e86\u4e00\u6839\u67f4\u706b\uff0c\u4fbf\u5230\u540e\u6bbf\u3002 \u3000\u3000\u4ed6\u671b\u7740\u94fa\u5728\u795e\u575b\u4e0a\u7684\u90a3\u5806\u7a3b\u8349\uff0c\u4e0d\u7981\u5446\u5446\u51fa\u795e\uff0c\u6ca1\u591a\u65f6\u4e4b\u524d\uff0c\u8881\u7d2b\u8863\u8fd8\u7761\u5728\u8fd9\u7a3b\u8349\u4e4b\u4e0a\uff0c\u60f3\u4e0d\u5230\u53d8\u6545\u9661\u8d77\uff0c\u7389\u4eba\u8fdc\u53bb\uff0c\u53ea\u5269\u4e0b\u8352\u5c71\u51c4\u51c4\uff0c\u53e4\u5e99\u5bc2\u5bc2\uff0c\u4e0d\u77e5\u65e5\u540e\u662f\u5426\u5c1a\u80fd\u76f8\u89c1\u4e00\u9762\uff1f \u3000\u3000\u8fc7\u4e86\u826f\u4e45\uff0c\u624b\u4e2d\u67f4\u706b\u7206\u4e86\u4e2a\u706b\u82b1\uff0c\u624d\u5c06\u601d\u8def\u6253\u65ad\uff0c\u731b\u7136\u60f3\u8d77\uff1a\u201c\u554a\u54df\u4e0d\u597d\uff0c\u6211\u90a3\u672c\u62f3\u7ecf\u5200\u8c31\u5df2\u7ed9\u5979\u76d7\u4e86\u53bb\uff01\u6b64\u523b\u6211\u5c1a\u80fd\u4e0e\u5979\u6253\u6210\u5e73\u624b\u3002\u7b49\u5979\u77a7\u4e86\u6211\u7684\u62f3\u7ecf\u5200\u8c31\uff0c\u90a3\u65f6\u6211\u6bcf\u4e00\u62db\u6bcf\u4e00\u5f0f\u5979\u5747\u4e86\u7136\u4e8e\u80f8\uff0c\u5c82\u975e\u4e00\u52a8\u624b\u4fbf\u80fd\u5236\u6211\u6b7b\u547d\uff1f\u201d\u6ee1\u80f8\u67d4\u60c5\uff0c\u767b\u65f6\u5316\u4e3a\u60e7\u610f\uff0c\u5c06\u67f4\u706b\u4e00\u629b\uff0c\u9893\u7136\u5012\u5728\u5730\u4e0b\u7a3b\u8349\u4e4b\u4e2d\u3002 \u3000\u3000\u4e00\u8eba\u4e0b\u53bb\uff0c\u521a\u597d\u538b\u5728\u81ea\u5df1\u7684\u5305\u88b1\u4e4b\u4e0a\uff0c\u53ea\u89c9\u5305\u88b1\u6709\u5f02\uff0c\u4f3c\u4e4e\u5927\u4e86\u8bb8\u591a\uff0c\u4ed6\u672c\u6765\u5c06\u5305\u88b1\u5f53\u4f5c\u6795\u5934\uff0c\u540e\u6765\u542c\u5230\u51e4\u5929\u5357\u8bf4\u8bdd\u4e4b\u58f0\uff0c\u51fa\u53bb\u5bfb\u4ec7\uff0c\u90a3\u5305\u88b1\u5e76\u672a\u79fb\u52a8\uff0c\u73b0\u5728\u5374\u79fb\u5230\u4e86\u8170\u4e0b\u3002\u80e1\u6590\u5927\u662f\u5947\u602a\uff0c\u5fc3\u60f3\uff1a\u201c\u5218\u9e64\u771f\u592b\u5987\u4e0e\u90a3\u4e09\u5144\u5f1f\u90fd\u5230\u540e\u6bbf\u6765\u8fc7\uff0c\u96be\u9053\u662f\u4ed6\u4eec\u52a8\u4e86\u6211\u7684\u5305\u88b1\u3002\u201d\u4e8e\u662f\u6643\u706b\u6298\u518d\u70b9\u71c3\u67f4\u706b\uff0c\u6253\u5f00\u5305\u88b1\u4e00\u770b\uff0c\u4e0d\u7531\u5f97\u5446\u4e86\u3002 \u3000\u3000\u53ea\u89c1\u9664\u4e86\u539f\u6765\u7684\u8863\u7269\u4e4b\u5916\uff0c\u591a\u4e86\u4e00\u5957\u5916\u8863\uff0c\u4e00\u5957\u886c\u91cc\u8863\u88e4\uff0c\u4e00\u53cc\u978b\u5b50\uff0c\u4e00\u53cc\u889c\u5b50\u3002\u8fd9\u4e9b\u8863\u88e4\u978b\u889c\u672c\u662f\u4ed6\u7684\uff0c\u90a3\u65e5\u88ab\u8881\u7d2b\u8863\u63a8\u5165\u6ce5\u5858\uff0c\u4e0b\u6cb3\u6d17\u6fa1\u65f6\u9664\u4e86\u4e0b\u6765\uff0c\u4fbf\u90fd\u7ed9\u5979\u53d6\u4e86\u53bb\u3002\u60f3\u4e0d\u5230\u6b64\u65f6\u8863\u88e4\u978b\u889c\u5c3d\u5df2\u6d17\u5f97\u5e72\u5e72\u51c0\u51c0\uff0c\u8863\u895f\u4e0a\u539f\u6709\u7684\u4e24\u4e2a\u7834\u5b54\u4e5f\u5df2\u7f1d\u8865\u6574\u9f50\u3002\u4ed6\u7ffb\u5f00\u8863\u670d\uff0c\u90a3\u672c\u62f3\u7ecf\u5200\u8c31\u6b63\u5728\u5176\u4e0b\uff0c\u5200\u8c31\u65c1\u53e6\u6709\u4e00\u53ea\u4e09\u5bf8\u6765\u957f\u7684\u78a7\u7389\u51e4\u51f0\u3002 \u3000\u3000\u8fd9\u7389\u51e4\u51f0\u96d5\u523b\u5f97\u6781\u662f\u7cbe\u81f4\uff0c\u7eb9\u8def\u7ec6\u5bc6\uff0c\u901a\u4f53\u6676\u83b9\uff0c\u89e6\u624b\u751f\u6e29\u3002 \u3000\u3000\u80e1\u6590\u5446\u4e86\u534a\u664c\uff0c\u5305\u4e0a\u5305\u88b1\uff0c\u90a3\u53ea\u7389\u51e4\u51f0\u5374\u62ff\u5728\u624b\u4e2d\uff0c\u5439\u706d\u67f4\u706b\uff0c\u8eba\u5728\u7a3b\u8349\u5806\u91cc\uff0c\u601d\u6f6e\u8d77\u4f0f\uff1a\u201c\u82e5\u8bf4\u5979\u5bf9\u6211\u597d\uff0c\u4f55\u4ee5\u8981\u6551\u51e4\u5929\u5357\uff0c\u7aed\u529b\u548c\u6211\u4f5c\u5bf9\uff1f\u82e5\u9053\u5bf9\u6211\u4e0d\u597d\uff0c\u8fd9\u7389\u51e4\u51f0\uff0c\u8fd9\u6d17\u5e72\u51c0\u3001\u7f1d\u8865\u597d\u7684\u8863\u670d\u978b\u889c\u53c8\u4e3a\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5728\u9ed1\u6697\u4e2d\u7741\u5927\u4e86\u53cc\u773c\uff0c\u54ea\u91cc\u8fd8\u7761\u5f97\u7740\uff1f"}),showOnHomepage:!0},{name:"\u7b2c\u516b\u7ae0\u3000\u6c5f\u6e56\u98ce\u6ce2\u6076",content:(0,a.jsx)(a.Fragment,{children:"\u7a81\u7136\u6bbf\u95e8\u53e3\u706b\u5149\u95ea\u52a8\uff0c\u5218\u9e64\u771f\u624b\u6267\u67f4\u706b\uff0c\u9760\u5728\u59bb\u5b50\u81c2\u4e0a\uff0c\u7f13\u7f13\u8d70\u8fdb\u540e\u6bbf\uff0c\u8bf4\u9053\uff1a\u201c\u8fd8\u662f\u5728\u8fd9\u513f\u7761\u4e00\u4f1a\u513f\u5427\u3002\u201d\u8bf4\u7740\u5f84\u5f80\u795e\u575b\u8d70\u53bb\uff0c\u77a7\u6a21\u6837\u4fbf\u8981\u7761\u5728\u8881\u7d2b\u8863\u521a\u624d\u7761\u8fc7\u7684\u7a3b\u8349\u4e4b\u4e2d\u3002 \u3000\u3000\u80e1\u6590\u662f\u5c11\u5e74\u4eba\u5fc3\u6027\uff0c\u4e00\u89c1\u5927\u6025\uff0c\u5fd9\u9053\uff1a\u201c\u5218\u8001\u7237\u5b50\uff0c\u4f60\u722c\u4e0a\u722c\u4e0b\u4e0d\u4fbf\uff0c\u5728\u5730\u4e0b\u7761\u65b9\u4fbf\u5f97\u591a\uff0c\u6211\u7684\u94fa\u4f4d\u8ba9\u4f60\u3002\u201d\u8bf4\u7740\u63d0\u8d77\u5305\u88b1\uff0c\u5954\u5230\u795e\u575b\u65c1\u8fb9\uff0c\u4f38\u811a\u8de8\u4e0a\uff0c\u62a2\u5148\u5728\u7a3b\u8349\u5806\u4e2d\u8eba\u4e0b\u4e86\u3002\u5218\u9e64\u771f\u8c22\u9053\uff1a\u201c\u5c0f\u54e5\u771f\u662f\u5fc3\u597d\u3002\u201d \u3000\u3000\u80e1\u6590\u8eba\u5728\u7a3b\u8349\u4e4b\u4e2d\uff0c\u9690\u7ea6\u95fb\u5230\u4e00\u80a1\u6de1\u6de1\u7684\u5e7d\u9999\uff0c\u4e5f\u4e0d\u77e5\u662f\u51fa\u4e8e\u81ea\u5df1\u60f3\u50cf\uff0c\u8fd8\u662f\u8881\u7d2b\u8863\u5f53\u771f\u7559\u4e0b\u4e86\u9999\u6cfd\uff0c\u5fc3\u4e2d\u53c8\u559c\u53c8\u6101\uff0c\u53c8\u4f38\u624b\u53bb\u6478\u6000\u4e2d\u7684\u90a3\u53ea\u7389\u51e4\u51f0\u3002 \u3000\u3000\u7761\u4e86\u4e00\u4f1a\uff0c\u5ffd\u542c\u5f97\u5218\u9e64\u771f\u4f4e\u58f0\u9053\uff1a\u201c\u4ef2\u840d\uff0c\u8fd9\u4f4d\u5c0f\u54e5\u4e3a\u4eba\u771f\u597d\uff0c\u54b1\u592b\u5987\u4fe9\u987b\u5f97\u597d\u597d\u62a5\u7b54\u4ed6\u624d\u662f\u3002\u201d\u90a3\u540d\u53eb\u4ef2\u840d\u7684\u5c11\u5987\u9053\uff1a\u201c\u662f\u554a\uff0c\u82e5\u4e0d\u662f\u4ed6\u4e00\u529b\u906e\u63a9\uff0c\u8fd9\u5e99\u4e2d\u8eba\u7740\u7684\uff0c\u90a3\u5c31\u662f\u54b1\u592b\u59bb\u7684\u4e24\u5177\u5c38\u9996\u5566\u3002\u201d\u5218\u9e64\u771f\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u9002\u624d\u5f53\u771f\u9669\u5230\u4e86\u6781\u5904\uff0c\u953a\u6c0f\u4e09\u5144\u5f1f\u82e5\u8981\u4e3a\u96be\u8fd9\u4f4d\u5c0f\u54e5\uff0c\u6211\u4fbf\u662f\u62da\u4e86\u8001\u547d\u4e0d\u8981\uff0c\u4e5f\u5f97\u6551\u4ed6\u3002\u201d\u4ef2\u840d\u9053\uff1a\u201c\u8fd9\u4e2a\u81ea\u7136\uff0c\u522b\u4eba\u4ee5\u4fa0\u4e49\u5fc3\u80a0\u76f8\u5f85\uff0c\u6211\u4eec\u4fbf\u5f97\u4ee5\u4fa0\u4e49\u5fc3\u80a0\u62a5\u7b54\u3002\u8fd9\u4f4d\u5c0f\u54e5\u867d\u662f\u4e0d\u4f1a\u6b66\u827a\uff0c\u4f46\u4e3a\u4eba\u5374\u80dc\u8fc7\u4e0d\u5c11\u6c5f\u6e56\u8c6a\u6770\u5462\u3002\u201d\u5218\u9e64\u771f\u9053\uff1a\u201c\u4f4e\u58f0\uff01\u83ab\u5435\u9192\u4e86\u4ed6\u3002\u201d\u63a5\u7740\u4f4e\u4f4e\u5524\u4e86\u51e0\u58f0\uff1a\u201c\u5c0f\u54e5\uff01\u5c0f\u54e5\uff01\u201d \u3000\u3000\u80e1\u6590\u5e76\u6ca1\u7761\u7740\uff0c\u4f46\u542c\u4ed6\u4eec\u6781\u529b\u5938\u8d5e\u81ea\u5df1\uff0c\u6599\u77e5\u4ed6\u53c8\u8981\u5f00\u53e3\u79f0\u8c22\uff0c\u672a\u514d\u4e0d\u597d\u610f\u601d\uff0c\u4e8e\u662f\u5047\u88c5\u7761\u719f\uff0c\u5e76\u4e0d\u7b54\u5e94\u3002 \u3000\u3000\u4ef2\u840d\u4f4e\u58f0\u9053\uff1a\u201c\u4ed6\u7761\u7740\u4e86\u3002\u201d\u5218\u9e64\u771f\u9053\uff1a\u201c\u55ef\uff01\u201d\u9694\u4e86\u4e00\u4f1a\uff0c\u53c8\u4f4e\u58f0\u9053\uff1a\u201c\u4ef2\u840d\uff0c\u521a\u624d\u6211\u53eb\u4f60\u72ec\u81ea\u9003\u8d70\uff0c\u4f60\u600e\u4e48\u4e0d\u8d70\uff1f\u201d\u8bed\u6c14\u4e4b\u4e2d\uff0c\u5927\u6709\u8d23\u5907\u4e4b\u610f\u3002\u4ef2\u840d\u9eef\u7136\u9053\uff1a\u201c\u5509\uff01\u4f60\u4f24\u52bf\u8fd9\u4e48\u91cd\uff0c\u6211\u600e\u80fd\u5f03\u4f60\u4e0d\u987e\uff1f\u201d\u5218\u9e64\u771f\u9053\uff1a\u201c\u81ea\u4ece\u6211\u90a3\u8001\u4f34\u6b7b\u540e\uff0c\u6211\u53ea\u9053\u4ece\u6b64\u662f\u4e00\u4e16\u5b64\u82e6\u4f36\u4ec3\u4e86\u3002\u4e0d\u6599\u4f1a\u6709\u4f60\u8ddf\u7740\u6211\uff0c\u5bf9\u6211\u53c8\u662f\u8fd9\u822c\u6069\u7231\u3002\u6211\u53c8\u600e\u820d\u5f97\u8ddf\u4f60\u5206\u5f00\uff1f\u53ef\u662f\u4f60\u77e5\u9053\u8fd9\u5c01\u4e66\u4fe1\u5e72\u7cfb\u4f55\u7b49\u91cd\u5927\uff0c\u82e5\u4e0d\u9001\u5230\u91d1\u9762\u4f5b\u82d7\u5927\u4fa0\u624b\u4e2d\uff0c\u4e0d\u77e5\u6709\u591a\u5c11\u4ec1\u4eba\u4e49\u58eb\u8981\u6b7b\u4e8e\u975e\u547d\u2026\u2026\u201d \u3000\u3000\u80e1\u6590\u542c\u5230\u201c\u91d1\u9762\u4f5b\u82d7\u5927\u4fa0\u201d\u516d\u5b57\uff0c\u5fc3\u4e2d\u4e00\u51db\uff0c\u9669\u4e9b\u513f\u201c\u554a\u201d\u7684\u4e00\u58f0\uff0c\u60ca\u547c\u51fa\u6765\u3002\u4ed6\u77e5\u82d7\u4eba\u51e4\u4e0e\u81ea\u5df1\u7236\u4eb2\u751f\u524d\u6709\u83ab\u5927\u7275\u8fde\uff0c\u636e\u6c5f\u6e56\u4f20\u8a00\uff0c\u81ea\u5df1\u7236\u4eb2\u4fbf\u6b7b\u5728\u4ed6\u624b\u4e2d\uff0c\u4f46\u6bcf\u6b21\u8be2\u95ee\u629a\u517b\u81ea\u5df1\u957f\u5927\u7684\u5e73\u56db\u53d4\uff0c\u4ed6\u603b\u8bf4\u6b64\u4e8b\u622a\u7136\u4e0d\u786e\uff0c\u73b0\u4e0b\u81ea\u5df1\u5e74\u7eaa\u5c1a\u5c0f\uff0c\u5c06\u6765\u5b9a\u4f1a\u539f\u539f\u672c\u672c\u7684\u544a\u77e5\u3002\u80e1\u6590\u5f53\u5e74\u5728\u5546\u5bb6\u5821\u4e2d\uff0c\u66fe\u4e0e\u82d7\u4eba\u51e4\u6709\u8fc7\u4e00\u9762\u4e4b\u7f18\uff0c\u4f46\u89c9\u4ed6\u795e\u5a01\u51db\u51db\uff0c\u5f53\u65f6\u5e7c\u5c0f\u7684\u5fc3\u7075\u4e4b\u4e2d\uff0c\u5bf9\u4ed6\u5927\u4e3a\u94a6\u670d\u3002\u76f4\u5230\u6b64\u65f6\uff0c\u751f\u5e73\u9047\u5230\u7684\u4eba\u7269\u4e4b\u4e2d\uff0c\u771f\u6b63\u4ee4\u4ed6\u5fc3\u6298\u7684\uff0c\u4e5f\u53ea\u8d75\u534a\u5c71\u4e0e\u82d7\u4eba\u51e4\u4e24\u4eba\u800c\u5df2\u3002\u8d75\u534a\u5c71\u548c\u4ed6\u62dc\u4e86\u628a\u5b50\uff0c\u82d7\u4eba\u51e4\u5374\u662f\u6ca1\u8ddf\u4ed6\u8bf4\u8fc7\u4e00\u53e5\u8bdd\uff0c\u751a\u81f3\u8fde\u773c\u89d2\u4e5f\u6ca1\u77a5\u8fc7\u4ed6\u4e00\u4e0b\uff0c\u7136\u800c\u6bcf\u6b21\u60f3\u5230\u6b64\u4eba\uff0c\u603b\u89c9\u4e3a\u4eba\u8be5\u5f53\u5982\u6b64\uff0c\u624d\u7b97\u662f\u82f1\u96c4\u8c6a\u6770\u3002 \u3000\u3000\u53ea\u542c\u4ef2\u840d\u4f4e\u58f0\u9053\uff1a\u201c\u7981\u58f0\uff01\u6b64\u4e8b\u673a\u5bc6\u4e07\u5206\uff0c\u4fbf\u5728\u65e0\u4eba\u4e4b\u5904\uff0c\u4e5f\u4e0d\u53ef\u518d\u8bf4\u3002\u201d\u5218\u9e64\u771f\u9053\uff1a\u201c\u662f\u5566\uff01\u54b1\u4eec\u8fd9\u756a\u5954\u8d70\uff0c\u662f\u4e3a\u4e86\u65e0\u6570\u4ec1\u4eba\u4e49\u58eb\uff0c\u5b9e\u65e0\u534a\u70b9\u79c1\u5fc3\u5728\u5185\u3002\u7687\u5929\u6709\u7075\uff0c\u5b9a\u987b\u4fdd\u4f51\u54b1\u4eec\u6210\u529f\u3002\u201d\u8fd9\u51e0\u53e5\u8bdd\u8bf4\u5f97\u6b63\u6c14\u51db\u7136\u3002\u80e1\u6590\u6697\u6697\u4f69\u670d\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u662f\u4fa0\u4e49\u4e4b\u4e8b\uff0c\u4e0d\u7ba1\u82d7\u4eba\u51e4\u4e8e\u6211\u6709\u6069\u8fd8\u662f\u6709\u4ec7\uff0c\u6211\u5b9a\u5f53\u76f8\u52a9\u5218\u9e64\u771f\u5c06\u4fe1\u9001\u5230\u3002\u201d \u3000\u3000\u4e24\u592b\u59bb\u6b64\u540e\u4e0d\u518d\u5f00\u53e3\u3002\u8fc7\u4e86\u826f\u4e45\uff0c\u80e1\u6590\u6726\u6726\u80e7\u80e7\uff0c\u5fae\u6709\u7761\u610f\uff0c\u5408\u4e0a\u773c\u6b63\u8981\u5165\u7761\uff0c\u5ffd\u542c\u5317\u9762\u53c8\u6709\u9a6c\u8e44\u58f0\u54cd\uff0c\u953a\u6c0f\u5144\u5f1f\u4e09\u4e58\u53bb\u800c\u590d\u56de\u3002\u80e1\u6590\u5fae\u5fae\u4e00\u60ca\uff1a\u201c\u8fd9\u4e09\u4eba\u518d\u56de\u5e99\u6765\uff0c\u6b64\u756a\u5218\u9e64\u771f\u5b9a\u96be\u8eb2\u8fc7\uff0c\u4e0d\u5982\u6211\u5230\u5e99\u5916\u53bb\u6253\u53d1\u4e86\u4ed6\u4eec\u3002\u4fbf\u7b97\u4e0d\u654c\uff0c\u4e5f\u597d\u8ba9\u5218\u6c0f\u592b\u5987\u4e58\u673a\u9003\u8d70\uff0c\u53bb\u9001\u90a3\u5c01\u8981\u51fd\u3002\u201d\u4e8e\u662f\u5c06\u5305\u88b1\u7f1a\u5728\u80cc\u4e0a\uff0c\u8f7b\u8f7b\u6e9c\u4e0b\u795e\u575b\uff0c\u8d70\u51fa\u5e99\u95e8\uff0c\u5411\u953a\u6c0f\u4e09\u5144\u5f1f\u7684\u5750\u9a91\u8fce\u53bb\u3002 \u3000\u3000\u6b64\u65f6\u5927\u96e8\u5df2\u505c\uff0c\u8def\u9762\u79ef\u6c34\u76c8\u5c3a\uff0c\u80e1\u6590\u8df5\u6c34\u5954\u884c\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u9ed1\u6697\u4e2d\u89c1\u4e09\u9a91\u9a6c\u5934\u5c3e\u76f8\u63a5\u5730\u5954\u6765\u3002\u4ed6\u5728\u8def\u4e2d\u4e00\u7ad9\uff0c\u53cc\u624b\u5f20\u5f00\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u6b64\u5c71\u662f\u6211\u5f00\uff0c\u6b64\u6811\u662f\u6211\u683d\uff0c\u82e5\u8981\u4ece\u6b64\u8fc7\uff0c\u7559\u4e0b\u4e70\u8def\u94b1\uff01\u201d \u3000\u3000\u5f53\u5934\u7684\u953a\u8001\u4e09\u54d1\u7136\u5931\u7b11\uff0c\u559d\u9053\uff1a\u201c\u54ea\u91cc\u94bb\u51fa\u6765\u7684\u5c0f\u6bdb\u8d3c\uff01\u201d \u3000\u3000\u4e00\u63d0\u9a6c\u7f30\uff0c\u4fbf\u5f80\u80e1\u6590\u8eab\u4e0a\u51b2\u6765\u3002\u80e1\u6590\u5de6\u624b\u500f\u5730\u4f38\u51fa\uff0c\u6293\u4f4f\u9a6c\u7f30\u4e00\u52d2\uff0c\u90a3\u9a6c\u8fd9\u4e00\u51b2\u4e0d\u4e0b\u6570\u767e\u65a4\u4e4b\u529b\uff0c\u4f46\u88ab\u4ed6\u4e00\u52d2\uff0c\u767b\u65f6\u5012\u9000\u4e86\u51e0\u6b65\u3002\u4ed6\u8ddf\u7740\u4f7f\u51fa\u501f\u529b\u4e4b\u6280\uff0c\u987a\u7740\u90a3\u9a6c\u5012\u9000\u4e4b\u52bf\uff0c\u4e00\u9001\u4e00\u6380\uff0c\u4e00\u5339\u9ad8\u5934\u5927\u9a6c\u7adf\u7136\u7ad9\u7acb\u4e0d\u5b9a\uff0c\u7830\u7684\u4e00\u58f0\uff0c\u7ffb\u5012\u5728\u5730\u3002\u603b\u7b97\u953a\u8001\u4e09\u89c1\u673a\u5f97\u5feb\uff0c\u5148\u81ea\u8dc3\u5728\u8def\u8fb9\u3002 \u3000\u3000\u8fd9\u4e00\u6765\uff0c\u953a\u6c0f\u4e09\u5144\u5f1f\u5c3d\u7686\u9a87\u7136\uff0c\u953a\u8001\u5927\u4e0e\u953a\u8001\u4e8c\u540c\u65f6\u4e0b\u9a6c\uff0c\u4e09\u4eba\u624b\u4e2d\u5df2\u5404\u6301\u4e86\u4e00\u4ef6\u5947\u5f62\u5175\u5203\u3002\u8fd9\u65f6\u5373\u5c06\u9ece\u660e\uff0c\u4f46\u7834\u6653\u4e4b\u524d\uff0c\u6709\u4e00\u6bb5\u77ed\u77ed\u65f6\u5149\u5929\u8272\u66f4\u6697\uff0c\u517c\u4e4b\u5927\u96e8\u867d\u505c\uff0c\u6ee1\u5929\u9ed1\u4e91\u8fc4\u672a\u6d88\u6563\uff0c\u80e1\u6590\u867d\u7741\u5927\u4e86\u773c\u775b\uff0c\u4ecd\u77a7\u4e0d\u6e05\u4e09\u4eba\u624b\u4e2d\u6301\u7684\u662f\u4ec0\u4e48\u5175\u5203\u3002 \u3000\u3000\u53ea\u542c\u5f97\u4e00\u4eba\u7c97\u58f0\u7c97\u6c14\u5730\u8bf4\u9053\uff1a\u201c\u9102\u5317\u953a\u6c0f\u5144\u5f1f\u884c\u7ecf\u8d35\u5730\uff0c\u672a\u66fe\u767b\u95e8\u62dc\u8bbf\uff0c\u6781\u662f\u5931\u793c\u3002\u8bf7\u6559\u9601\u4e0b\u5c0a\u59d3\u5927\u540d\u3002\u201d\u4ed6\u4e09\u4eba\u542c\u80e1\u6590\u53e3\u97f3\u7a1a\u5ae9\uff0c\u77e5\u4ed6\u5e74\u5c81\u4e0d\u5927\uff0c\u672c\u6765\u4e1d\u6beb\u6ca1\u653e\u5728\u5fc3\u4e0a\uff0c\u5f85\u89c1\u4ed6\u4e00\u52d2\u4e00\u63a8\uff0c\u7adf\u5c06\u4e00\u5339\u5065\u9a6c\u6380\u7ffb\u5728\u5730\uff0c\u8fd9\u529f\u592b\u5b9e\u662f\u975e\u540c\u5c0f\u53ef\uff0c\u4e0d\u7531\u5f97\u8038\u7136\u6539\u5bb9\u3002\u8001\u5927\u953a\u5146\u82f1\u51fa\u53e3\u53eb\u5b57\u53f7\uff0c\u8a00\u8bed\u4e4b\u4e2d\u9887\u5177\u793c\u656c\u3002 \u3000\u3000\u80e1\u6590\u867d\u7136\u6ed1\u7a3d\u591a\u667a\uff0c\u751f\u6027\u5374\u975e\u8f7b\u6d6e\uff0c\u542c\u5f97\u5bf9\u65b9\u8bf4\u8bdd\u5ba2\u6c14\uff0c\u4fbf\u9053\uff1a\u201c\u5728\u4e0b\u59d3\u80e1\uff0c\u6ca1\u8bf7\u6559\u4e09\u4f4d\u5927\u53f7\u3002\u201d \u3000\u3000\u953a\u5146\u82f1\u5fc3\u60f3\uff1a\u201c\u6211\u953a\u6c0f\u4e09\u96c4\u540d\u6ee1\u5929\u4e0b\uff0c\u6b66\u6797\u4e2d\u4eba\u8c01\u4e0d\u77e5\u95fb\uff1f \u3000\u3000\u4f60\u542c\u4e86\u2018\u9102\u5317\u953a\u6c0f\u5144\u5f1f\u2019\u516d\u5b57\uff0c\u8fd8\u8981\u8be2\u95ee\u540d\u53f7\uff0c\u89c1\u8bc6\u4e5f\u5fd2\u6d45\u4e86\u3002\u201d\u4e8e\u662f\u7b54\u9053\uff1a\u201c\u5728\u4e0b\u8349\u5b57\u5146\u82f1\uff0c\u8fd9\u662f\u6211\u4e8c\u5f1f\u5146\u6587\uff0c\u4e09\u5f1f\u5146\u80fd\u3002\u6211\u4e09\u5144\u5f1f\u6709\u6025\u4e8b\u5728\u8eab\uff0c\u8bf7\u80e1\u5927\u54e5\u8ba9\u9053\u3002\u80e1\u5927\u54e5\u65e2\u5728\u6b64\u5904\u5f00\u5c71\u7acb\u67dc\uff0c\u6211\u4eec\u5144\u5f1f\u56de\u6765\uff0c\u5b9a\u5f53\u4e13\u8bda\u9053\u8c22\u3002\u201d\u8bf4\u7740\u5c06\u624b\u4e00\u62f1\u3002 \u3000\u3000\u4ee5\u4ed6\u4e00\u4e2a\u6c5f\u6e56\u4e0a\u7684\u6210\u540d\u4eba\u7269\uff0c\u5bf9\u540e\u8f88\u8bf4\u8bdd\u5982\u6b64\u8c26\u606d\uff0c\u4e5f\u7b97\u662f\u96be\u5f97\u4e4b\u6781\uff0c\u53ea\u56e0\u4ed6\u89c1\u80e1\u6590\u4e00\u51fa\u624b\u4fbf\u663e\u9732\u4e86\u6781\u5f3a\u7684\u6b66\u529f\uff0c\u77e5\u9053\u6b64\u4eba\u6781\u662f\u96be\u6597\uff0c\u53c8\u60f3\u4ed6\u672a\u5fc5\u53ea\u662f\u5b64\u8eab\u4e00\u4eba\uff0c\u82e5\u662f\u53e6\u6709\u5e08\u53cb\u5728\u4fa7\uff0c\u90a3\u5c31\u66f4\u52a0\u68d8\u624b\u4e86\u3002 \u3000\u3000\u80e1\u6590\u62b1\u62f3\u8fd8\u793c\uff0c\u8bf4\u9053\uff1a\u201c\u953a\u8001\u5e08\u592a\u8fc7\u591a\u793c\u3002\u4e09\u4f4d\u53ef\u662f\u53bb\u627e\u90a3\u5218\u9e64\u771f\u592b\u5987\u4e48\uff1f\u201d \u3000\u3000\u8fd9\u65f6\u5929\u8272\u6e10\u660e\uff0c\u953a\u6c0f\u4e09\u96c4\u5df2\u8ba4\u51fa\u8fd9\u773c\u524d\u4e4b\u4eba\uff0c\u4fbf\u662f\u9002\u624d\u5728\u6e58\u5983\u5e99\u6240\u89c1\u7684\u4e61\u4e0b\u5c11\u5e74\u3002\u4e09\u5144\u5f1f\u4e92\u77a7\u4e86\u4e00\u773c\uff0c\u5747\u60f3\uff1a\u201c\u8fd9\u6b21\u53ef\u8d70\u4e86\u773c\u5566\uff0c\u539f\u6765\u8fd9\u5c0f\u5b50\u8ddf\u5218\u9e64\u771f\u592b\u5987\u662f\u4e00\u8def\u3002\u201d \u3000\u3000\u6668\u5149\u71b9\u5fae\u4e4b\u4e2d\uff0c\u80e1\u6590\u4e5f\u5df2\u77a7\u660e\u767d\u953a\u6c0f\u4e09\u5144\u5f1f\u624b\u4e2d\u7684\u5947\u5f62\u5175\u5203\uff0c\u4f46\u89c1\u953a\u5146\u82f1\u624b\u6267\u4e00\u5757\u5c3a\u8bb8\u957f\u7684\u94c1\u724c\uff0c\u4e0a\u9762\u9690\u7ea6\u523b\u5f97\u6709\u5b57\uff1b\u953a\u5146\u6587\u62ff\u7684\u662f\u4e00\u6839\u54ed\u4e27\u68d2\uff1b\u953a\u5146\u80fd\u624b\u6301\u4e4b\u7269\u66f4\u662f\u5947\u602a\uff0c\u7adf\u662f\u4e00\u6746\u63d2\u5728\u6b7b\u4eba\u7075\u5ea7\u4e0a\u7684\u62db\u9b42\u5e61\uff0c\u5728\u6668\u98ce\u4e4b\u4e2d\u4e00\u98d8\u4e00\u8361\uff0c\u6a21\u6837\u8be1\u5947\u65e0\u6bd4\u3002\u4e09\u4eba\u76f8\u8c8c\u4e11\u964b\uff0c\u8863\u7740\u602a\u5f02\uff0c\u518d\u7ecf\u8fd9\u4e09\u4ef6\u51f6\u9669\u7684\u5175\u5203\u4e00\u886c\uff0c\u4e0d\u7528\u52a8\u624b\u5df2\u4ee4\u4eba\u6c14\u4e3a\u4e4b\u593a\u3002\u80e1\u6590\u53ea\u6015\u4ed6\u4eec\u7a81\u7136\u53d1\u96be\uff0c\u81ea\u5df1\u53ef\u4e0d\u77e5\u8fd9\u4e09\u4ef6\u5947\u95e8\u5175\u5203\u7684\u5389\u5bb3\u4e4b\u5904\uff0c\u5f53\u4e0b\u5168\u795e\u6212\u5907\uff0c\u4e0d\u6562\u7a0d\u6709\u6020\u5ffd\u3002 \u3000\u3000\u953a\u5146\u82f1\u9053\uff1a\u201c\u9601\u4e0b\u8ddf\u5218\u9e64\u771f\u8001\u5e08\u600e\u751f\u79f0\u547c\uff1f\u201d\u80e1\u6590\u9053\uff1a\u201c\u5728\u4e0b\u548c\u5218\u8001\u5e08\u4eca\u65e5\u662f\u7b2c\u4e8c\u6b21\u89c1\u9762\uff0c\u7d20\u65e0\u6e0a\u6e90\u3002\u53ea\u662f\u89c1\u4e09\u4f4d\u76f8\u903c\u8fc7\u751a\uff0c\u60f3\u4ee3\u4ed6\u8bf4\u4e00\u4e2a\u60c5\u3002\u5e38\u8a00\u9053\u5f97\u597d\uff1a\u80fd\u7f62\u624b\u65f6\u4fbf\u7f62\u624b\uff0c\u5f97\u9976\u4eba\u5904\u4e14\u9976\u4eba\u3002\u5218\u8001\u5e08\u592b\u5987\u65e2\u5df2\u53d7\u4f24\uff0c\u4e09\u4f4d\u4fbf\u5bb9\u8ba9\u51e0\u5206\u5982\u4f55\uff1f\u201d \u3000\u3000\u953a\u5146\u6587\u5fc3\u4e2d\u6025\u8e81\uff0c\u6697\u60f3\u5728\u6b64\u8017\u65f6\u5df2\u4e45\uff0c\u83ab\u8981\u7ed9\u5218\u9e64\u771f\u4e58\u673a\u8d70\u4e86\uff0c\u5f53\u4e0b\u5411\u5927\u54e5\u4f7f\u4e2a\u773c\u8272\uff0c\u6162\u6162\u79fb\u6b65\uff0c\u4fbf\u60f3\u4ece\u80e1\u6590\u8eab\u65c1\u7ed5\u8fc7\u3002 \u3000\u3000\u80e1\u6590\u53cc\u624b\u4e00\u4f38\uff0c\u8bf4\u9053\uff1a\u201c\u4e09\u4f4d\u8ddf\u5218\u8001\u5e08\u6709\u4ec0\u8fc7\u8282\uff0c\u5728\u4e0b\u5168\u4e0d\u77e5\u60c5\u3002\u4f46\u90a3\u5218\u8001\u5e08\u6709\u8981\u4e8b\u5728\u8eab\uff0c\u4e14\u8ba9\u4ed6\u529e\u5b8c\u4e4b\u540e\uff0c\u4e09\u4f4d\u518d\u627e\u4ed6\u6666\u6c14\u5982\u4f55\uff1f\u90a3\u65f6\u5728\u4e0b\u4e8b\u4e0d\u5e72\u5df1\uff0c\u81ea\u7136\u4e0d\u6562\u5192\u6627\u6253\u6270\u3002\u201d\u953a\u5146\u6587\u6012\u9053\uff1a\u201c\u6211\u4eec\u5c31\u662f\u4e0d\u8bb8\u4ed6\u53bb\u529e\u8fd9\u4ef6\u4e8b\u3002\u4f60\u5230\u5e95\u8ba9\u4e0d\u8ba9\u9053\uff1f\u201d \u3000\u3000\u80e1\u6590\u60f3\u8d77\u5218\u9e64\u771f\u592b\u5987\u5bf9\u7b54\u4e4b\u8a00\uff0c\u8bf4\u90a3\u901a\u4e66\u4fe1\u5e72\u8fde\u7740\u65e0\u6570\u4ec1\u4eba\u4e49\u58eb\u7684\u6027\u547d\uff0c\u773c\u89c1\u8fd9\u953a\u6c0f\u4e09\u5144\u5f1f\u5f62\u8c8c\u51f6\u72e0\uff0c\u663e\u7136\u751f\u5e73\u4f5c\u6076\u591a\u7aef\uff0c\u6599\u60f3\u4eca\u65e5\u82e5\u4e0d\u52a8\u624b\uff0c\u6b64\u4e8b\u96be\u4ee5\u5584\u7f62\uff0c\u4e8e\u662f\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8981\u8ba9\u8def\u90a3\u4e5f\u4e0d\u96be\uff0c\u53ea\u987b\u4e70\u8def\u94b1\u4e09\u767e\u4e24\u94f6\u5b50\u3002\u201d \u3000\u3000\u953a\u5146\u6587\u5927\u6012\uff0c\u4e00\u6446\u54ed\u4e27\u68d2\uff0c\u4e0a\u524d\u4fbf\u8981\u52a8\u624b\u3002\u953a\u5146\u82f1\u5de6\u624b\u4e00\u62e6\uff0c\u8bf4\u9053\uff1a\u201c\u4e8c\u5f1f\u4e14\u6162\uff01\u201d\u63a2\u624b\u5165\u6000\uff0c\u53d6\u51fa\u56db\u53ea\u5143\u5b9d\uff0c\u9053\uff1a\u201c\u8fd9\u91cc\u4e09\u767e\u4e24\u94f6\u5b50\u8db3\u8db3\u6709\u4f59\uff0c\u4fbf\u8bf7\u53d6\u53bb\u3002\u201d\u953a\u5146\u6587\u53eb\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u5e72\u4ec0\u4e48\uff1f\u201d\u4ed6\u60f3\u953a\u6c0f\u4e09\u96c4\u7eb5\u6a2a\u8346\u695a\uff0c\u600e\u80fd\u5bf9\u4e00\u4e2a\u540e\u8f88\u5982\u6b64\u793a\u5f31\uff1f\u4f46\u953a\u5146\u82f1\u77e5\u9053\u4e8b\u673a\u6025\u8feb\uff0c\u975e\u5c3d\u5feb\u5c06\u5218\u9e64\u771f\u622a\u4e0b\u4e0d\u53ef\uff0c\u4e8b\u6709\u8f7b\u91cd\u7f13\u6025\uff0c\u80e1\u6590\u8fd9\u6837\u4e00\u4e2a\u65e0\u540d\u5c11\u5e74\uff0c\u5408\u4e09\u5144\u5f1f\u4e4b\u529b\u80dc\u4e4b\u4e0d\u6b66\uff0c\u4f46\u7a0d\u6709\u803d\u6401\uff0c\u90a3\u4fbf\u8bef\u4e86\u5927\u4e8b\uff0c\u56e0\u6b64\u4ed6\u8bf4\u8981\u4e70\u8def\u94b1\uff0c\u4fbf\u53d6\u4e09\u767e\u4e24\u94f6\u5b50\u7ed9\u4ed6\u3002 \u3000\u3000\u8fd9\u4e00\u7740\u5374\u4e5f\u5927\u51fa\u80e1\u6590\u7684\u610f\u6599\u4e4b\u5916\uff0c\u4ed6\u7b11\u563b\u563b\u5730\u6447\u4e86\u6447\u5934\uff0c\u5e76\u4e0d\u4f38\u624b\u53bb\u63a5\uff0c\u8bf4\u9053\uff1a\u201c\u591a\u8c22\uff0c\u591a\u8c22\uff01\u953a\u8001\u5e08\u8bf4\u8fd9\u56db\u53ea\u5143\u5b9d\u4e0d\u6b62\u4e09\u767e\u4e24\uff0c\u53ef\u662f\u665a\u8f88\u7684\u5b9a\u4ef7\u53ea\u662f\u4e00\u767e\u4e24\u94f6\u5b50\u4e00\u4f4d\uff0c\u4e09\u4f4d\u5171\u662f\u4e09\u767e\u4e24\uff0c\u5018\u82e5\u591a\u53d6\uff0c\u672a\u514d\u592a\u4e0d\u516c\u9053\u3002\u8fd9\u6837\u5427\uff0c\u54b1\u4eec\u540c\u5230\u524d\u9762\u5e02\u9547\uff0c\u627e\u4e00\u5bb6\u94f6\u94fa\uff0c\u8bf7\u638c\u67dc\u7684\u4ed4\u7ec6\u79e4\u8fc7\uff0c\u665a\u8f88\u53ea\u8981\u4e09\u767e\u4e24\uff0c\u4e0d\u6562\u591a\u53d6\u4e00\u5206\u4e00\u6beb\u2026\u2026\u201d \u3000\u3000\u953a\u6c0f\u4e09\u96c4\u542c\u5230\u6b64\u5904\uff0c\u5782\u4e0b\u7684\u7709\u6bdb\u90fd\u7ad6\u4e86\u4e0a\u6765\u3002\u953a\u5146\u82f1\u5c06\u94f6\u5b50\u5f80\u6000\u91cc\u4e00\u653e\uff0c\u8bf4\u9053\uff1a\u201c\u4e8c\u5f1f\uff0c\u4e09\u5f1f\uff0c\u4f60\u4eec\u5148\u8d70\u3002\u201d\u5411\u80e1\u6590\u53eb\u9053\uff1a\u201c\u4eae\u5175\u5203\u5427\u3002\u5728\u4e0b\u8ba8\u6559\u8001\u5f1f\u7684\u9ad8\u62db\u3002\u201d \u3000\u3000\u80e1\u6590\u89c1\u4ed6\u795e\u95f2\u6c14\u5b9a\uff0c\u5b9e\u662f\u4e2a\u52b2\u654c\uff0c\u81ea\u5df1\u5355\u5200\u5df2\u7ed9\u8881\u7d2b\u8863\u62a2\u8d70\uff0c\u6b64\u65f6\u8d64\u624b\u7a7a\u62f3\u6597\u4ed6\u4e09\u4eba\uff0c\u53ea\u6015\u96be\u4ee5\u53d6\u80dc\u3002\u4ed6\u4e00\u60f3\u5230\u8881\u7d2b\u8863\uff0c\u5fc3\u4e2d\u5fae\u5fae\u4e00\u751c\uff0c\u4f46\u968f\u5373\u7259\u9f7f\u4e00\u54ac\uff0c\u5fc3\u601d\u82e5\u975e\u4f60\u53d6\u53bb\u6211\u7684\u5175\u5203\uff0c\u6b64\u65f6\u4e5f\u4e0d\u81f4\u5904\u6b64\u9669\u5883\uff0c\u773c\u89c1\u953a\u5146\u6587\u3001\u5146\u80fd\u5144\u5f1f\u8981\u4ece\u81ea\u5df1\u8eab\u4fa7\u7ed5\u8fc7\uff0c\u5374\u5982\u4f55\u963b\u6321\uff1f\u5fc3\u5ff5\u52a8\u5904\uff0c\u500f\u5730\u4fa7\u8eab\u62a2\u4e0a\u4e24\u6b65\uff0c\u53f3\u62f3\u4f38\u51fa\uff0c\u7830\u7684\u4e00\u58f0\uff0c\u51fb\u5728\u953a\u5146\u82f1\u6240\u4e58\u7684\u9ec4\u9a6c\u9f3b\u4e0a\u3002\u8fd9\u4e00\u62f3\u4ed6\u7528\u4e86\u91cd\u624b\u6cd5\uff0c\u6b63\u662f\u80e1\u5bb6\u62f3\u8c31\u4e2d\u6240\u4f20\u6781\u5389\u5bb3\u7684\u6740\u7740\u3002\u90a3\u9ec4\u9a6c\u7acb\u65f6\u8111\u9aa8\u788e\u88c2\uff0c\u59d4\u987f\u5728\u5730\uff0c\u4e00\u52a8\u4e5f\u4e0d\u52a8\u7684\u6b7b\u4e86\u3002 \u3000\u3000\u8fd9\u4e00\u4e0b\u5148\u58f0\u593a\u4eba\uff0c\u953a\u6c0f\u4e09\u96c4\u90fd\u662f\u4e00\u5446\u3002\u80e1\u6590\u987a\u624b\u6293\u8d77\u9ec4\u9a6c\u7684\u9a6c\u978d\uff0c\u5fae\u4e00\u7528\u529b\uff0c\u9a6c\u809a\u5e26\u5df2\u7136\u8ff8\u65ad\uff0c\u4ed6\u5c06\u9a6c\u978d\u6321\u5728\u80f8\u524d\uff0c\u53cc\u624b\u5404\u6301\u4e00\u6839\u956b\u5e26\uff0c\u8bf4\u9053\uff1a\u201c\u5f97\u7f6a\u4e86\uff01\u53ea\u56e0\u5728\u4e0b\u672a\u643a\u5175\u5203\uff0c\u53ea\u597d\u501f\u8fd9\u9a6c\u978d\u4e00\u7528\u3002\u201d\u8bf4\u7740\u5de6\u624b\u7684\u94c1\u956b\u6325\u51fa\uff0c\u88ad\u5411\u953a\u5146\u6587\u7684\u9762\u95e8\uff0c\u53f3\u624b\u94c1\u956b\u6a2a\u51fb\u953a\u5146\u80fd\u53f3\u80c1\uff0c\u53cc\u956b\u9f50\u51fa\uff0c\u5df2\u62e6\u4f4f\u4e24\u4eba\u53bb\u8def\u3002 \u3000\u3000\u953a\u6c0f\u4e09\u96c4\u53c8\u60ca\u53c8\u6012\u3002\u4e09\u5144\u5f1f\u672c\u6765\u90fd\u4f7f\u5224\u5b98\u7b14\uff0c\u4f46\u516b\u5e74\u524d\u8d25\u4e8e\u82d7\u4eba\u51e4\u624b\u4e0b\uff0c\u5f15\u4e3a\u5947\u803b\u5927\u8fb1\uff0c\u4ece\u6b64\u5f03\u7b14\u4e0d\u7528\uff0c\u4e09\u4eba\u5404\u81ea\u7ec3\u4e86\u4e00\u4ef6\u5947\u5f62\u5175\u5203\uff0c\u516b\u5e74\u82e6\u529f\uff0c\u6b66\u529f\u5927\u8fdb\uff0c\u6ee1\u5fc3\u8981\u53bb\u548c\u82d7\u4eba\u51e4\u518d\u51b3\u96cc\u96c4\uff0c\u5c82\u77e5\u5728\u8fd9\u7a77\u4e61\u50fb\u58e4\u4e4b\u95f4\uff0c\u7adf\u53d7\u8fd9\u65e0\u540d\u5c11\u5e74\u7684\u6298\u8fb1\uff1f\u953a\u5146\u82f1\u4e00\u58f0\u547c\u5578\uff0c\u5146\u6587\u3001\u5146\u80fd\u9f50\u5578\u76f8\u5e94\u3001\u5578\u58f0\u4e2d\u9634\u98ce\u607b\u607b\uff0c\u5bd2\u6c14\u68ee\u68ee\uff0c\u80e1\u6590\u542c\u4e86\uff0c\u4e0d\u7531\u5f97\u5fc3\u60ca\uff0c\u53ea\u89c1\u4e09\u4eba\u4e3e\u8d77\u94c1\u7075\u724c\u3001\u54ed\u4e27\u68d2\u3001\u62db\u9b42\u5e61\uff0c\u5206\u81ea\u4e09\u9762\u653b\u4e0a\uff0c\u5f53\u5373\u5c06\u9a6c\u978d\u62a4\u5728\u80f8\u524d\u5f53\u4f5c\u76fe\u724c\uff0c\u53cc\u624b\u821e\u52a8\u94c1\u956b\uff0c\u4fbf\u4f3c\u4f7f\u7740\u4e00\u5bf9\u6d41\u661f\u9524\uff0c\u5c45\u7136\u6709\u653b\u6709\u5b88\u3002 \u3000\u3000\u4ed6\u62f3\u811a\u548c\u5200\u6cd5\u867d\u7cbe\uff0c\u5374\u4e0d\u4f3c\u8881\u7d2b\u8863\u822c\u7cbe\u901a\u591a\u5bb6\u95e8\u6d3e\u6b66\u529f\uff0c\u8fd9\u6d41\u661f\u9524\u7684\u529f\u592b\u4ed6\u4ece\u672a\u7ec3\u8fc7\uff0c\u53ea\u662f\u4ed7\u7740\u5fc3\u7075\u624b\u5feb\uff0c\u6b66\u5b66\u6839\u5e95\u9ad8\u4eba\u4e00\u7b49\uff0c\u8fd9\u624d\u7528\u4ee5\u65bd\u5c55\u62b5\u6321\u3002\u867d\u8bf4\u4e00\u6cd5\u901a\uff0c\u4e07\u6cd5\u901a\uff0c\u6b66\u5b66\u9ad8\u5f3a\u4e4b\u58eb\u5373\u662f\u4e00\u7af9\u4e00\u6728\u5728\u624b\uff0c\u4ea6\u80fd\u7528\u4ee5\u514b\u654c\u62a4\u8eab\uff0c\u4f46\u953a\u6c0f\u4e09\u96c4\u7a76\u662f\u4e00\u6d41\u597d\u624b\uff0c\u4ee5\u672c\u8eab\u529f\u529b\u800c\u8bba\uff0c\u6bcf\u4eba\u5747\u8f83\u4ed6\u6df1\u539a\u3002\u5e78\u597d\u4ed6\u5168\u7136\u4e0d\u4f1a\u6d41\u661f\u9524\u7684\u62db\u672f\uff0c\u8fd9\u624d\u4e0e\u4e09\u4eba\u62c6\u4e86\u4e8c\u4e09\u5341\u62db\uff0c\u5c1a\u672a\u843d\u8d25\u3002 \u3000\u3000\u539f\u6765\u953a\u6c0f\u4e09\u96c4\u89c1\u591a\u8bc6\u5e7f\uff0c\u89c1\u80e1\u6590\u62ff\u4e86\u4e24\u53ea\u9a6c\u956b\u5f53\u4f5c\u6d41\u661f\u9524\u4f7f\uff0c\u5373\u4fbf\u7740\u610f\u8fa8\u8ba4\u4ed6\u7684\u6b66\u529f\u5bb6\u6570\u3002\u53ea\u89c1\u4ed6\u53f3\u624b\u9a6c\u956b\u6a2a\u51fb\u800c\u81f3\uff0c\u5fc3\u60f3\u8fd9\u662f\u5c71\u4e1c\u9752\u5dde\u5f20\u5bb6\u6d41\u661f\u9524\u6cd5\u4e2d\u7684\u4e00\u62db\u201c\u767d\u8679\u8d2f\u65e5\u201d\uff0c\u5de6\u624b\u9a6c\u956b\u4e5f\u5fc5\u987a\u52bf\u6a2a\u51fb\u3002\u54ea\u77e5\u80e1\u6590\u89c1\u953a\u5146\u6587\u7684\u54ed\u4e27\u68d2\u6b63\u81ea\u4e0b\u5411\u4e0a\u6311\u8d77\uff0c\u5934\u9876\u9732\u51fa\u7a7a\u9699\uff0c\u5f53\u5373\u6296\u52a8\u9a6c\u956b\uff0c\u5f53\u5934\u538b\u843d\u3002\u953a\u6c0f\u4e09\u96c4\u5fc3\u4e2d\u5947\u602a\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\u5bb6\u6570\uff1f\u201d \u3000\u3000\u80e1\u6590\u89c1\u953a\u5146\u6587\u4e3e\u68d2\u5c01\u683c\uff0c\u53f3\u624b\u9a6c\u956b\u5f84\u5411\u953a\u5146\u80fd\u626b\u53bb\u3002\u4e09\u5144\u5f1f\u6697\u6697\u70b9\u5934\uff0c\u5fc3\u60f3\uff1a\u201c\u662f\u4e86\uff0c\u539f\u6765\u4ed6\u662f\u9655\u897f\u5ef6\u5dde\u891a\u5341\u9524\u7684\u95e8\u4e0b\uff0c\u8fd9\u4e00\u4e0b\u2018\u626c\u7709\u5410\u6c14\u2019\uff0c\u4e0b\u534a\u62db\u5b9a\u662f\u5c06\u53cc\u956b\u5f53\u80f8\u76f4\u8361\u8fc7\u6765\u4e86\u3002\u201d \u3000\u3000\u4e09\u4eba\u89c1\u8fc7\u4ed6\u63a8\u9a6c\u51fb\u9a6c\uff0c\u8182\u529b\u6781\u5176\u6c89\u96c4\uff0c\u82e5\u662f\u53cc\u9524\u5f53\u80f8\u76f4\u8361\uff0c\u5012\u662f\u5927\u610f\u4e0d\u5f97\uff0c\u5f53\u4e0b\u4e09\u4eba\u5404\u4e3e\u5175\u5203\u633a\u5728\u80f8\u95f4\uff0c\u9f50\u8fd0\u771f\u529b\uff0c\u8981\u786c\u63a5\u786c\u67b6\u4ed6\u8fd9\u4e00\u8361\u3002\u4e0d\u6599\u80e1\u6590\u5168\u4e0d\u77e5\u201c\u626c\u7709\u5410\u6c14\u201d\u662f\u4ec0\u4e48\u62db\u6570\uff0c\u773c\u89c1\u4e09\u4eba\u4e3e\u5175\u5203\u62a4\u80f8\uff0c\u53cc\u956b\u84e6\u5730\u4e0b\u63a0\uff0c\u51fb\u5411\u4e09\u4eba\u4e0b\u76d8\u3002\u4e09\u5144\u5f1f\u5413\u4e86\u4e00\u8df3\uff1a\u201c\u600e\u4e48\u7528\u8d77\u2018\u7ffb\u5929\u8986\u5730\u2019\u7684\u62db\u6570\u6765\uff1f\u201d \u3000\u3000\u953a\u5146\u80fd\u4e00\u9762\u62db\u67b6\uff0c\u4e00\u9762\u53eb\u9053\uff1a\u201c\u5582\uff0c\u592a\u539f\u5e9c\u2018\u6d41\u661f\u8d76\u6708\u2019\u7ae5\u8001\u5e08\u662f\u4f60\u4ec0\u4e48\u4eba\uff1f\u83ab\u975e\u5927\u6c34\u51b2\u5012\u9f99\u738b\u5e99\u4e48\uff1f\u201d\u539f\u6765\u5c71\u897f\u592a\u539f\u5e9c\u7ae5\u8001\u5e08\u7ae5\u6000\u9053\u5584\u4f7f\u6d41\u661f\u53cc\u9524\uff0c\u5916\u53f7\u4eba\u79f0\u201c\u6d41\u661f\u8d76\u6708\u201d\uff0c\u548c\u953a\u6c0f\u4e09\u96c4\u662f\u83ab\u9006\u4e4b\u4ea4\uff0c\u90a3\u201c\u7ffb\u5929\u8986\u5730\u201d\u7684\u62db\u6570\uff0c\u6b63\u662f\u4ed6\u95e8\u4e2d\u7684\u5355\u4f20\u7edd\u6280\uff0c\u522b\u5bb6\u4f7f\u6d41\u661f\u9524\u7684\u51b3\u4e0d\u4f1a\u7528\u3002\u80e1\u6590\u8bef\u6253\u8bef\u649e\uff0c\u8fd9\u4e00\u62db\u4f7f\u5f97\u4f9d\u7a00\u4eff\u4f5b\uff0c\u4ed6\u542c\u953a\u5146\u80fd\u76f8\u8be2\uff0c\u7b11\u9053\uff1a\u201c\u7ae5\u8001\u5e08\u662f\u6211\u5e08\u5f1f\u3002\u201d \u3000\u3000\u8ddf\u7740\u53cc\u956b\u76f4\u6325\u8fc7\u53bb\u3002\u953a\u5146\u80fd\u201c\u5478\u201d\u7684\u4e00\u58f0\uff0c\u9a82\u9053\uff1a\u201c\u6df7\u5c0f\u5b50\u80e1\u8bf4\u516b\u9053\uff01\u201d \u3000\u3000\u4e09\u4eba\u89c1\u4ed6\u9a6c\u956b\u7684\u62db\u6570\u795e\u51fa\u9b3c\u6ca1\uff0c\u6ca1\u6cd5\u6478\u51c6\u4ed6\u6b66\u5b66\u5e08\u627f\uff0c\u5747\u81ea\u5947\u602a\uff1a\u201c\u6211\u4eec\u6570\u5341\u5e74\u6765\u8db3\u8ff9\u904d\u5929\u4e0b\uff0c\u54ea\u4e00\u5bb6\u54ea\u4e00\u6d3e\u7684\u6d41\u661f\u9524\u6ca1\u89c1\u8fc7\uff1f\u8fd9\u5c0f\u5b50\u5374\u771f\u662f\u90aa\u95e8\u3002\u201d \u3000\u3000\u672c\u6765\u52a8\u624b\u6bd4\u6b66\uff0c\u82e5\u80fd\u8bc6\u5f97\u5bf9\u65b9\u7684\u6b66\u529f\u5bb6\u6570\uff0c\u81ea\u80fd\u5360\u654c\u673a\u5148\uff0c\u5904\u5904\u62a2\u5f97\u4e0a\u98ce\uff0c\u4f46\u953a\u6c0f\u4e09\u96c4\u8fde\u731c\u51e0\u6b21\u5168\u90fd\u731c\u9519\uff0c\u5fc3\u795e\u4e00\u4e71\uff0c\u6240\u4f7f\u7684\u62db\u6570\u7adf\u7136\u5927\u4e0d\u7ba1\u7528\u3002\u8fd9\u7686\u56e0\u80e1\u6590\u795e\u62f3\u6bd9\u9a6c\uff0c\u4f7f\u5f97\u4e09\u4eba\u5fc3\u6709\u6240\u5fcc\uff0c\u5426\u5219\u4e5f\u7528\u4e0d\u7740\u8fa8\u8ba4\u4ed6\u5bb6\u6570\u95e8\u6d3e\uff0c\u4e00\u4e0a\u624b\u4fbf\u5404\u5c55\u7edd\u62db\uff0c\u80e1\u6590\u65e9\u5df2\u7cdf\u4e86\u3002 \u3000\u3000\u4e8c\u5341\u4f59\u62db\u4e4b\u540e\uff0c\u953a\u6c0f\u4e09\u96c4\u89c1\u4ed6\u53cc\u956b\u7684\u62db\u6570\u867d\u7136\u5947\u7279\uff0c\u5a01\u529b\u5374\u4e5f\u4e0d\u5f3a\uff0c\u4e8e\u662f\u5404\u5c55\u516b\u5e74\u6765\u82e6\u7ec3\u7684\u7edd\u6280\uff0c\u724c\u3001\u68d2\u3001\u5e61\u4e09\u4ef6\u5947\u5f62\u5175\u5203\u7684\u602a\u62db\u6e90\u6e90\u800c\u81f3\u3002\u953a\u5146\u82f1\u7684\u7075\u724c\u662f\u9554\u94c1\u94f8\u6210\uff0c\u8d70\u7684\u5168\u662f\u521a\u731b\u8def\u5b50\uff0c\u786c\u6253\u786c\u7838\uff0c\u80e1\u6590\u6b64\u65f6\u770b\u5f97\u6e05\u695a\uff0c\u724c\u4e0a\u5199\u7684\u662f\u201c\u4e00\u89c1\u751f\u8d22\u201d\u56db\u5b57\u3002\u953a\u5146\u80fd\u7684\u62db\u9b42\u5e61\u5374\u5168\u662f\u67d4\u529f\uff0c\u90a3\u5e61\u5b50\u5e03\u4e0d\u50cf\u5e03\uff0c\u9769\u4e0d\u50cf\u9769\uff0c\u9a6c\u956b\u6253\u4e0a\u53bb\u5168\u4e0d\u53d7\u529b\uff0c\u4f46\u82e5\u7ed9\u5e61\u5b50\u62c2\u4e2d\u8eab\u4f53\uff0c\u60f3\u6765\u6ecb\u5473\u5b9a\u7136\u6781\u4e0d\u597d\u53d7\u3002\u953a\u5146\u6587\u7684\u54ed\u4e27\u68d2\u5374\u662f\u4ecb\u4e4e\u521a\u67d4\u4e4b\u95f4\uff0c\u5927\u81f4\u662f\u6746\u68d2\u7684\u8def\u5b50\uff0c\u5374\u53c8\u6742\u7740\u97ad\u950f\u7684\u5bb6\u6570\u3002\u4e09\u5144\u5f1f\u5175\u5203\u4e0d\u540c\uff0c\u4f46\u4e09\u4ef6\u5175\u5203\u7684\u6728\u67c4\u4ecd\u662f\u5f53\u5224\u5b98\u7b14\u4f7f\uff0c\u521a\u67d4\u76f8\u6d4e\uff0c\u4e92\u8f85\u4e92\u6210\u3002\u80e1\u6590\u6697\u6697\u53eb\u82e6\uff0c\u77e5\u9053\u518d\u6597\u7247\u523b\uff0c\u975e\u8d25\u4e0d\u53ef\uff0c\u7a81\u7136\u53cc\u638c\u56de\u8f6c\uff0c\u6258\u5728\u9a6c\u978d\u4e4b\u540e\uff0c\u5411\u5916\u6025\u63a8\u3002\u8fd9\u4e00\u63a8\u4e4b\u529b\u52bf\u9053\u4e0d\u5c0f\uff0c\u547c\u7684\u4e00\u58f0\u54cd\uff0c\u9a6c\u978d\u75be\u98de\u800c\u524d\u3002 \u3000\u3000\u953a\u6c0f\u4e09\u96c4\u6025\u8dc3\u95ea\u5f00\uff0c\u4e0d\u77e5\u4ed6\u53c8\u8981\u51fa\u4ec0\u4e48\u602a\u62db\u3002 \u3000\u3000\u80e1\u6590\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u5728\u4e0b\u672c\u662f\u597d\u5fc3\u529d\u67b6\uff0c\u5e76\u6ca1\u8ddf\u4e09\u4f4d\u52a8\u624b\u4e4b\u610f\uff0c\u56e0\u6b64\u8d64\u624b\u7a7a\u62f3\uff0c\u6ca1\u5e26\u5175\u5668\uff0c\u7528\u8fd9\u9a6c\u978d\u5b50\u600e\u80fd\u591f\u6597\u5f97\u8fc7\u4e09\u4f4d\u5f53\u4e16\u82f1\u96c4\uff1f\u4eca\u65e5\u7b97\u6211\u8ba4\u8f93\u4fbf\u662f\u3002\u201d\u8bf4\u7740\u95ea\u8eab\u8ba9\u5728\u9053\u65c1\u3002 \u3000\u3000\u953a\u6c0f\u4e09\u96c4\u660e\u77e5\u4ed6\u51fa\u8a00\u76f8\u6fc0\uff0c\u4f46\u56e0\u6709\u8981\u4e8b\u5728\u8eab\uff0c\u4e0d\u613f\u8ddf\u4ed6\u7ea0\u7f20\u3002\u953a\u5146\u80fd\u4fbf\u9053\uff1a\u201c\u597d\u5427\uff0c\u4e0b\u6b21\u4f60\u53d6\u5f97\u8d81\u624b\u5175\u5203\uff0c\u6211\u4eec\u518d\u9886\u6559\u9ad8\u62db\u3002\u201d\u8bf4\u7740\u62d4\u8db3\u4fbf\u8d70\u3002 \u3000\u3000\u80e1\u6590\u7b11\u9053\uff1a\u201c\u4e0b\u6b21\uff0c\u4e0b\u6b21\uff0c\u597d\u4e00\u4e2a\u4e0b\u6b21\uff01\u539f\u6765\u953a\u6c0f\u4e09\u5144\u5f1f\u662f\u5982\u6b64\u8fd9\u822c\u7684\u4eba\u7269\u3002\u201d\u953a\u5146\u6587\u6012\u9053\uff1a\u201c\u4ec0\u4e48\u5982\u6b64\u8fd9\u822c\uff1f\u4f60\u81ea\u5df1\u6ca1\u5175\u5203\uff0c\u53c8\u602a\u5f97\u8c01\u6765\uff1f\u201d\u80e1\u6590\u9053\uff1a\u201c\u6211\u5012\u6709\u4e2a\u5999\u6cd5\uff0c\u5c31\u53ea\u6050\u4f60\u4eec\u4e0d\u6562\u8ddf\u6211\u6bd4\u8bd5\u3002\u201d\u953a\u6c0f\u4e09\u96c4\u7ecf\u4ed6\u4e00\u6fc0\u518d\u6fc0\uff0c\u518d\u4e5f\u5fcd\u8010\u4e0d\u4f4f\uff0c\u9f50\u58f0\u9053\uff1a\u201c\u4f60\u5212\u4e0b\u9053\u513f\u5427\uff01\u201d\u953a\u5146\u82f1\u8ddf\u7740\u8bf4\u9053\uff1a\u201c\u6211\u4e24\u4f4d\u5144\u5f1f\u5728\u8fd9\u91cc\u9886\u6559\uff0c\u5728\u4e0b\u5374\u8981\u5c11\u966a\u3002\u201d\u8bf4\u7740\u7eb5\u8eab\u8dc3\u8d77\u3002 \u3000\u3000\u80e1\u6590\u8ddf\u7740\u8dc3\u8d77\uff0c\u53cc\u624b\u5728\u7a7a\u4e2d\u4e00\u62e6\u3002\u953a\u5146\u82f1\u6ca1\u60f3\u5230\u4ed6\u8eab\u6cd5\u7adf\u662f\u5982\u6b64\u8fc5\u6377\uff0c\u94c1\u724c\u4e00\u6296\uff0c\u8fce\u9762\u6253\u53bb\u3002\u80e1\u6590\u62f3\u811a\u529f\u592b\u5374\u80dc\u4ed6\u751a\u591a\uff0c\u5f53\u4e0b\u4e0d\u95ea\u4e0d\u907f\uff0c\u8eab\u5b50\u5c1a\u672a\u843d\u5730\uff0c\u53f3\u624b\u5df2\u8ddf\u7740\u56de\u8f6c\uff0c\u6293\u4f4f\u4e86\u4ed6\u53f3\u8155\uff0c\u4e00\u6296\u4e00\u626d\uff0c\u953a\u5146\u82f1\u624b\u4e2d\u7684\u94c1\u724c\u7adf\u9669\u4e9b\u7ed9\u4ed6\u593a\u53bb\u3002 \u3000\u3000\u5146\u6587\u3001\u5146\u80fd\u9f50\u5403\u4e00\u60ca\uff0c\u5206\u81ea\u5de6\u53f3\u653b\u5230\uff0c\u76f8\u52a9\u5144\u957f\u3002\u80e1\u6590\u4e00\u58f0\u957f\u7b11\uff0c\u5411\u540e\u8dc3\u5f00\u4e08\u8bb8\uff0c\u987a\u52bf\u5728\u9053\u65c1\u4e00\u682a\u677e\u6811\u4e0a\u6298\u4e86\u6839\u6811\u679d\uff0c\u8bf4\u9053\uff1a\u201c\u4e09\u4f4d\u6562\u4e0d\u6562\u8bd5\u8bd5\u6211\u7684\u5200\u6cd5\uff1f\u201d \u3000\u3000\u953a\u5146\u82f1\u8fd9\u4e00\u4e0b\u867d\u6ca1\u7ed9\u4ed6\u593a\u53bb\u94c1\u724c\uff0c\u4f46\u624b\u8155\u5df2\u7ed9\u6293\u5f97\u9690\u9690\u751f\u75bc\uff0c\u5fc3\u4e2d\u66f4\u662f\u52a0\u4e86\u4e09\u5206\u7591\u60e7\uff0c\u6697\u60f3\uff1a\u201c\u8fd9\u5c11\u5e74\u5b9e\u975e\u5bfb\u5e38\u4e4b\u8f88\uff0c\u6211\u82e5\u5b64\u8eab\u53bb\u8ffd\u5218\u9e64\u771f\uff0c\u7559\u4e0b\u4e8c\u5f1f\u4e09\u5f1f\u5728\u6b64\uff0c\u5b9e\u662f\u653e\u5fc3\u4e0d\u4e0b\uff0c\u987b\u5f97\u5408\u5144\u5f1f\u4e09\u4eba\u4e4b\u529b\uff0c\u5148\u6599\u7406\u4e86\u4ed6\u3002\u7eb5\u6709\u803d\u6401\uff0c\u4e5f\u8bf4\u4e0d\u5f97\u4e86\u3002\u201d \u3000\u3000\u953a\u5146\u6587\u89c1\u80e1\u6590\u624b\u4e2d\u62ff\u4e86\u4e00\u6839\u56db\u5c3a\u6765\u957f\u7684\u677e\u6280\uff0c\u4e0d\u77e5\u6363\u4ec0\u4e48\u9b3c\uff0c\u773c\u671b\u5927\u54e5\uff0c\u542c\u4ed6\u7684\u4e3b\u610f\u3002 \u3000\u3000\u953a\u5146\u82f1\u6c89\u4f4f\u4e86\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u9601\u4e0b\u8981\u6bd4\u5200\u6cd5\uff0c\u53ef\u60dc\u6211\u4eec\u4e5f\u6ca1\u643a\u5f97\u5355\u5200\uff0c\u5426\u5219\u5012\u4e5f\u53ef\u5949\u501f\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u54b1\u4eec\u7d20\u4e0d\u76f8\u8bc6\uff0c\u81ea\u65e0\u6df1\u4ec7\u5927\u6028\uff0c\u6bd4\u6b66\u53ea\u6c42\u70b9\u5230\u4e3a\u6b62\uff0c\u662f\u4e5f\u4e0d\u662f\uff1f\u201d\u953a\u5146\u82f1\u9053\uff1a\u201c\u4e0d\u9519\uff01\u201d\u80e1\u6590\u7528\u5de6\u624b\u6298\u53bb\u677e\u679d\u4e0a\u7684\u6860\u53c9\u7ec6\u6761\uff0c\u53ea\u5269\u4e0b\u5149\u79c3\u79c3\u7684\u4e00\u6839\u679d\u6761\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u677e\u679d\u4fbf\u7b97\u662f\u4e00\u67c4\u5200\uff0c\u4e09\u4f4d\u8bf7\u4e00\u9f50\u4e0a\u6765\u3002 \u3000\u3000\u54b1\u4eec\u8bdd\u8bf4\u5728\u5148\u5934\uff0c\u8fd9\u677e\u679d\u780d\u5728\u4f55\u5904\uff0c\u4fbf\u7b97\u662f\u94a2\u5200\u780d\u4e2d\u3002\u953a\u6c0f\u4e09\u5144\u5f1f\u8bf4\u8bdd\u7b97\u4e0d\u7b97\u6570\uff1f\u201d \u3000\u3000\u953a\u5146\u82f1\u89c1\u4ed6\u5982\u6b64\u6258\u5927\uff0c\u5fc3\u4e2d\u66f4\u662f\u6709\u6c14\uff0c\u5927\u58f0\u9053\uff1a\u201c\u953a\u6c0f\u4e09\u96c4\u4fe1\u4e49\u4e4b\u540d\u65e9\u904d\u6c5f\u6e56\uff0c\u90a3\u65f6\u4f60\u8fd9\u4f4d\u5c0f\u5144\u5f1f\u53ef\u8fd8\u6ca1\u51fa\u4e16\u5462\u3002\u201d \u3000\u3000\u80e1\u6590\u9053\uff1a\u201c\u5982\u6b64\u6700\u597d\uff0c\u770b\u5200\u5427\uff01\u201d\u4e3e\u8d77\u677e\u679d\uff0c\u5237\u7684\u4e00\u62db\u6a2a\u780d\u3002\u953a\u5146\u6587\u81ea\u540e\u62a2\u4e0a\uff0c\u63d0\u68d2\u4fbf\u6253\u3002\u80e1\u6590\u659c\u8dc3\u907f\u5f00\uff0c\u677e\u679d\u5df2\u65a9\u5411\u953a\u5146\u80fd\u9888\u4e2d\u3002\u953a\u5146\u80fd\u5012\u8f6c\u5e61\u6746\uff0c\u5f80\u4ed6\u677e\u679d\u4e0a\u7838\u53bb\uff0c\u540c\u65f6\u953a\u5146\u82f1\u7684\u94c1\u724c\u4e5f\u5df2\u6253\u5230\u3002 \u3000\u3000\u90a3\u80e1\u5bb6\u5200\u6cd5\u771f\u6709\u9b3c\u795e\u83ab\u6d4b\u4e4b\u53d8\uff0c\u953a\u6c0f\u4e09\u96c4\u6b66\u529f\u867d\u5f3a\uff0c\u4f46\u80e1\u6590\u4e00\u5c06\u90a3\u677e\u679d\u5f53\u4f5c\u5200\u4f7f\uff0c\u7acb\u65f6\u7740\u7740\u62a2\u653b\uff0c\u5728\u4e09\u4eba\u4e4b\u95f4\u7a7f\u63d2\u6765\u53bb\uff0c\u780d\u524a\u65a9\u5288\uff0c\u4e00\u6839\u5c0f\u5c0f\u7684\u677e\u679d\uff0c\u7adf\u7136\u663e\u51fa\u4e86\u65e0\u7a77\u5a01\u529b\u3002\u953a\u6c0f\u4e09\u96c4\u8d8a\u6597\u8d8a\u5947\uff0c\u53ea\u89c1\u4ed6\u8fd9\u677e\u679d\u51b3\u4e0d\u4e0e\u4e09\u822c\u5175\u5203\u78b0\u649e\uff0c\u4f46\u4e58\u6687\u62b5\u9699\uff0c\u62db\u62db\u90fd\u6740\u5411\u81ea\u5df1\u7684\u8981\u5bb3\u3002\u88ab\u677e\u679d\u51fb\u4e2d\u867d\u7136\u65e0\u788d\uff0c\u4f46\u6709\u7ea6\u5728\u5148\uff0c\u51b3\u4e0d\u80fd\u8ba9\u5b83\u78b0\u5230\u8eab\u4f53\u3002\u953a\u5146\u6587\u7126\u8e81\u8d77\u6765\uff0c\u6325\u68d2\u6a2a\u626b\uff0c\u731b\u7838\u80e1\u6590\u80eb\u9aa8\u3002\u4ed6\u4e09\u5144\u5f1f\u6bcf\u4e00\u62db\u90fd\u662f\u4e92\u6709\u547c\u5e94\uff0c\u53ea\u5f85\u80e1\u6590\u8dc3\u8d77\u76f8\u907f\uff0c\u953a\u5146\u80fd\u7684\u62db\u9b42\u5e61\u4fbf\u4ece\u4ed6\u5934\u9876\u76d6\u843d\uff0c\u5146\u82f1\u7684\u94c1\u724c\u5374\u731b\u51fb\u4ed6\u7684\u53f3\u8170\u3002\u54ea\u77e5\u80e1\u6590\u5e76\u4e0d\u8dc3\u8d77\uff0c\u53cd\u800c\u62a2\u524d\u4e00\u6b65\uff0c\u76f4\u6b3a\u5165\u6000\uff0c\u624b\u8d77\u679d\u843d\uff0c\u677e\u679d\u5df2\u51fb\u4e2d\u953a\u5146\u6587\u7684\u5de6\u80a9\u3002 \u3000\u3000\u8fd9\u4e00\u62db\u51cc\u5389\u4e4b\u6781\uff0c\u90a3\u677e\u679d\u5982\u6362\u6210\u4e86\u94a2\u5200\uff0c\u953a\u5146\u6587\u7684\u4e00\u6761\u5de6\u81c2\u5df2\u7acb\u65f6\u88ab\u5378\u4e86\u4e0b\u6765\u3002\u8fd9\u677e\u679d\u7684\u4e00\u51fb\u81ea\u7136\u4f24\u4ed6\u4e0d\u7740\u4ec0\u4e48\uff0c\u4f46\u953a\u5146\u6587\u9762\u8272\u5927\u53d8\uff0c\u53eb\u9053\uff1a\u201c\u7f62\u4e86\uff0c\u7f62\u4e86\uff01\u201d\u5c06\u54ed\u4e27\u68d2\u5f80\u5730\u4e0b\u4e00\u629b\uff0c\u5782\u624b\u9000\u5f00\u3002 \u3000\u3000\u953a\u5146\u82f1\u3001\u953a\u5146\u80fd\u5144\u5f1f\u5fc3\u4e2d\u4e00\u5bd2\uff0c\u724c\u5e61\u5374\u821e\u5f97\u66f4\u52a0\u7d27\u4e86\uff0c\u5404\u65bd\u6740\u7740\uff0c\u53ea\u76fc\u80fd\u5c06\u80e1\u6590\u6253\u4e2d\uff0c\u626f\u4e2a\u5e73\u624b\u3002\u4f46\u8fc7\u4e0d\u6570\u62db\uff0c\u953a\u5146\u82f1\u9888\u4e2d\u7ed9\u677e\u679d\u4e00\u62d6\u800c\u8fc7\uff0c\u953a\u5146\u80fd\u5374\u662f\u53f3\u817f\u4e0a\u88ab\u677e\u679d\u5212\u4e86\u4e00\u4e0b\u3002 \u3000\u3000\u4e24\u4eba\u76f8\u987e\u60e8\u7136\uff0c\u4e00\u9f50\u629b\u4e0b\u5175\u5203\u3002\u7a81\u7136\u95f4\u953a\u5146\u82f1\u201c\u54c7\u201d\u7684\u4e00\u58f0\uff0c\u55b7\u51fa\u4e00\u5927\u53e3\u9c9c\u8840\u3002 \u3000\u3000\u80e1\u6590\u89c1\u4ed6\u4eec\u4fe1\u5b88\u7ea6\u8a00\uff0c\u6697\u60f3\u8fd9\u4e09\u5144\u5f1f\u867d\u7136\u51f6\u6076\uff0c\u8bf4\u8bdd\u5012\u662f\u4f5c\u5f97\u51c6\uff0c\u4ed6\u81ea\u77e5\u5e76\u672a\u4e0b\u624b\u6253\u4f24\u953a\u5146\u82f1\uff0c\u4ed6\u53e3\u5410\u9c9c\u8840\uff0c\u5b9a\u662f\u6025\u6012\u653b\u5fc3\u6240\u81f4\uff0c\u5fc3\u4e0b\u9887\u611f\u6b49\u759a\uff0c\u53cc\u624b\u4e00\u62f1\uff0c\u5f85\u8981\u8bf4\u51e0\u53e5\u6765\u4ea4\u4ee3\u3002\u953a\u5146\u80fd\u54fc\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u9601\u4e0b\u6b66\u6280\u60ca\u4eba\uff0c\u4f69\u670d\u4f69\u670d\uff01\u53ea\u662f\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u4e0d\u8d70\u6b63\u9014\u3002\u53ef\u60dc\u4e86\u4e00\u526f\u597d\u8eab\u624b\u3002\u201d\u80e1\u6590\u6115\u7136\u9053\uff1a\u201c\u6211\u600e\u5730\u4e0d\u8d70\u6b63\u9014\u4e86\uff1f\u201d\u953a\u5146\u6587\u6012\u9053\uff1a\u201c\u4e09\u5f1f\uff0c\u8fd8\u8ddf\u4ed6\u8bf4\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6276\u8d77\u953a\u5146\u82f1\u9a91\u4e0a\u9a6c\u80cc\uff0c\u7275\u7740\u7f30\u7ef3\u4fbf\u8d70\u3002 \u3000\u3000\u4e09\u4ef6\u5947\u95e8\u5175\u5203\u629b\u5728\u6c34\u5751\u4e4b\u4e2d\uff0c\u8c01\u90fd\u6ca1\u518d\u53bb\u62fe\u3002 \u3000\u3000\u80e1\u6590\u773c\u89c1\u4e09\u4eba\u6389\u5934\u4e0d\u987e\u800c\u53bb\uff0c\u5730\u4e0b\u5269\u4e0b\u4e00\u5339\u6b7b\u9a6c\uff0c\u4e09\u4ef6\u5175\u5203\uff0c\u5fc3\u4e2d\u9887\u6709\u611f\u89e6\uff0c\u77a7\u4e86\u597d\u4e00\u9635\u5b50\uff0c\u8fd9\u624d\u56de\u5411\u53e4\u5e99\u3002 \u3000\u3000\u8d70\u8fdb\u5e99\u4e2d\uff0c\u524d\u6bbf\u540e\u6bbf\u90fd\u4e0d\u89c1\u5218\u9e64\u771f\u592b\u5987\u7684\u4eba\u5f71\uff0c\u77e5\u4ed6\u4e8c\u4eba\u5df2\u4e58\u673a\u8fdc\u53bb\uff0c\u60f3\u8d77\u521a\u624d\u505a\u4e86\u4e00\u4ef6\u597d\u4e8b\uff0c\u5fc3\u4e2d\u4e5f\u4e0d\u7981\u6709\u5f97\u610f\u4e4b\u611f\uff0c\u53c8\u60f3\uff1a\u201c\u90a3\u82d7\u4eba\u51e4\u4e0d\u77e5\u4f4f\u5728\u4f55\u5904\uff1f\u6b64\u4eba\u53f7\u79f0\u2018\u6253\u904d\u5929\u4e0b\u65e0\u654c\u624b\u2019\uff0c\u6b66\u529f\u4e0d\u77e5\u5982\u4f55\u4e86\u5f97\uff1f\u201d\u8fd9\u4eba\u4e0e\u81ea\u5df1\u8fc7\u4e16\u4e86\u7684\u7236\u4eb2\u6709\u83ab\u5927\u5173\u8fde\uff0c\u5f53\u65e5\u5546\u5bb6\u5821\u4e00\u89c1\uff0c\u81ea\u5df1\u62f3\u7ecf\u5200\u8c31\u7684\u5934\u4e0a\u4e24\u9875\uff0c\u4e5f\u662f\u51ed\u7740\u4ed6\u7684\u5a01\u98ce\u624d\u4ece\u960e\u57fa\u624b\u4e2d\u53d6\u56de\uff0c\u6b64\u540e\u65f6\u65f6\u5ff5\u53ca\uff0c\u6b64\u523b\u5f88\u60f3\u8ddf\u7740\u5218\u9e64\u771f\u592b\u5987\u53bb\u77a7\u77a7\uff0c\u4f46\u90a3\u51e4\u5929\u5357\u867d\u7136\u9003\u53bb\uff0c\u53bb\u5fc5\u4e0d\u8fdc\uff0c\u6b64\u4ec7\u4e0d\u62a5\uff0c\u975e\u4e08\u592b\u4e5f\uff0c\u5230\u5e95\u662f\u8ffd\u8e2a\u54ea\u4e00\u4e2a\u597d\uff0c\u4e00\u65f6\u7adf\u81ea\u6253\u4e0d\u5b9a\u4e3b\u610f\u3002 \u3000\u3000\u4ed6\u4f4e\u5934\u5bfb\u601d\uff0c\u53c8\u4ece\u6545\u9053\u800c\u56de\uff0c\u8d70\u5230\u9002\u624d\u4e0e\u953a\u6c0f\u4e09\u96c4\u52a8\u624b\u4e4b\u5904\uff0c\u53ea\u89c1\u5730\u4e0b\u7684\u4e09\u4ef6\u5947\u95e8\u5175\u5203\u5df2\u7136\u4e0d\u89c1\uff0c\u90a3\u5339\u6b7b\u9a6c\u5374\u5140\u81ea\u6a2a\u5367\u5728\u5730\u3002\u4ed6\u5927\u662f\u5947\u602a\uff1a\u201c\u6211\u8fd9\u4e00\u6765\u4e00\u53bb\uff0c\u53ea\u662f\u7247\u523b\u95f4\u7684\u4e8b\uff0c\u8fd9\u65f6\u5929\u8272\u5c1a\u65e9\uff0c\u4e0d\u4f1a\u6709\u8fc7\u8def\u4e4b\u4eba\u987a\u624b\u6361\u4e86\u53bb\uff0c\u96be\u9053\u953a\u6c0f\u5144\u5f1f\u53bb\u800c\u590d\u56de\u4e48\uff1f\u201d \u3000\u3000\u4ed6\u5728\u56db\u5904\u5de1\u89c6\uff0c\u4e0d\u89c1\u6709\u5f02\uff0c\u4e00\u8def\u5bdf\u770b\uff0c\u7ec8\u4e8e\u5728\u79bb\u76f8\u6597\u5904\u5341\u4f59\u4e08\u7684\u4e00\u682a\u5927\u6811\u5e72\u4e0a\uff0c\u770b\u5230\u4e00\u4e2a\u6c61\u6ce5\u7684\u8db3\u5370\u3002\u8fd9\u8db3\u5370\u79bb\u5730\u7ea6\u83ab\u4e00\u4e08\u4e09\u5c3a\u9ad8\uff0c\u5370\u5728\u6811\u5e72\u4e0d\u5411\u9053\u8def\u7684\u4e00\u9762\uff0c\u82e5\u975e\u7ec6\u5fc3\u68c0\u89c6\uff0c\u51b3\u4e0d\u4f1a\u770b\u5230\u3002\u8db3\u5370\u7684\u6c61\u6ce5\u751a\u6e7f\uff0c\u5f53\u662f\u7559\u4e0b\u4e0d\u4e45\uff0c\u800c\u8db3\u5370\u7684\u978b\u5e95\u7ea4\u5c0f\uff0c\u53c8\u663e\u662f\u5973\u5b50\u7684\u978b\u5370\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u4e00\u52a8\uff1a\u201c\u96be\u9053\u662f\u5979\uff1f\u6211\u548c\u953a\u6c0f\u4e09\u96c4\u76f8\u6597\u4e4b\u65f6\uff0c\u5979\u4fbf\u8eb2\u5728\u6811\u4e0a\u65c1\u89c2\uff1f\u201d\u60f3\u5230\u8fd9\u91cc\uff0c\u4e00\u9897\u5fc3\u6026\u6026\u4e71\u8df3\uff0c\u7acb\u5373\u7eb5\u8eab\u800c\u8d77\uff0c\u6500\u4f4f\u4e00\u6839\u6811\u5e72\u7ffb\u8eab\u4e0a\u6811\uff0c\u679c\u7136\u5728\u4e00\u6839\u6a2a\u679d\u4e4b\u4e0a\uff0c\u53c8\u89c1\u5230\u4e24\u4e2a\u5e76\u5217\u7684\u5973\u5b50\u6e7f\u6ce5\u8db3\u5370\uff0c\u5728\u6a2a\u679d\u4e4b\u65c1\uff0c\u5374\u6709\u4e00\u6839\u7c97\u5927\u7684\u6811\u679d\u88ab\u8e0f\u65ad\u4e86\uff0c\u65ad\u75d5\u751a\u65b0\u3002\u4ed6\u53cd\u611f\u7591\u60d1\uff1a\u201c\u5018\u82e5\u662f\u8881\u59d1\u5a18\uff0c\u4ee5\u5979\u7684\u8f7b\u8eab\u529f\u592b\uff0c\u51b3\u4e0d\u4f1a\u8e0f\u65ad\u8fd9\u6839\u6811\u679d\u3002\u201d\u518d\u6500\u4e0a\u4e00\u770b\uff0c\u53ea\u89c1\u53e6\u4e00\u6839\u6a2a\u679d\u4e0a\u53c8\u6709\u4e24\u53ea\u5e76\u5217\u7684\u7537\u5b50\u811a\u5370\u3002\u4ed6\u5fc3\u4e2d\u7591\u7aa6\u7acb\u65f6\u5c3d\u53bb\uff0c\u5374\u4e0d\u7531\u5f97\u611f\u5230\u4e00\u9635\u5931\u671b\uff1a\u201c\u539f\u6765\u662f\u5218\u9e64\u771f\u592b\u5987\u5728\u8fd9\u91cc\u5077\u770b\u3002\u201d \u3000\u3000\u7136\u800c\u5fc3\u4e2d\u521a\u660e\u767d\u4e86\u4e00\u4e2a\u7591\u7aa6\uff0c\u7b2c\u4e8c\u4e2a\u3001\u7b2c\u4e09\u4e2a\u7591\u7aa6\u8ddf\u7740\u800c\u6765\uff1a\u201c\u4ed6\u4e8c\u4eba\u8eab\u8d1f\u91cd\u4f24\uff0c\u600e\u80fd\u7a9c\u9ad8\u8eb2\u5728\u6b64\u5904\uff0c\u6211\u7adf\u4e1d\u6beb\u6ca1\u6709\u5bdf\u89c9\uff1f\u953a\u6c0f\u4e09\u96c4\u65e2\u53bb\uff0c\u4ed6\u4eec\u600e\u53c8\u4e0d\u51fa\u58f0\u8ddf\u6211\u62db\u547c\uff1f\u201d\u8f6c\u5ff5\u4e00\u60f3\uff1a \u3000\u3000\u201c\u554a\uff0c\u662f\u4e86\u3002\u4ed6\u4eec\u672c\u6765\u53ea\u9053\u6211\u4e0d\u4f1a\u6b66\u827a\uff0c\u4f46\u7a81\u89c1\u6211\u6253\u8d25\u953a\u6c0f\u4e09\u96c4\uff0c\u5fc3\u4e2d\u8d77\u7591\uff0c\u53ea\u6015\u6211\u4e8e\u4ed6\u4eec\u6709\u6240\u4e0d\u5229\uff0c\u662f\u4ee5\u4e0d\u6562\u9732\u9762\u3002\u6c5f\u6e56\u95f4\u98ce\u6ce2\u9669\u6076\uff0c\u5904\u5904\u5c0f\u5fc3\u5728\u610f\uff0c\u539f\u662f\u524d\u8f88\u7684\u98ce\u8303\u3002\u53c8\u4f55\u51b5\u4ed6\u4eec\u6709\u8981\u4e8b\u5728\u8eab\uff0c\u600e\u80fd\u5927\u610f\uff1f\u201d\u60f3\u5230\u8fd9\u91cc\uff0c\u4fbf\u5373\u91ca\u7136\uff0c\u53ea\u89c1\u4e24\u6392\u5e26\u6ce5\u8db3\u5370\u5728\u8349\u4e1b\u95f4\u5411\u4e1c\u5317\u800c\u53bb\uff0c\u4ed6\u8d77\u4e86\u597d\u5947\u4e4b\u5fc3\uff0c\u4fbf\u987a\u7740\u8db3\u5370\u5411\u524d\u8ffd\u8e2a\u3002 \u3000\u3000\u6574\u591c\u5927\u96e8\u4e4b\u540e\u904d\u5730\u6ce5\u6cde\uff0c\u8fd9\u4e00\u7537\u4e00\u5973\u7684\u8db3\u5370\u751a\u662f\u6e05\u6670\uff0c\u8ddf\u968f\u65f6\u6beb\u4e0d\u8d39\u529b\uff0c\u4f46\u89c1\u4e24\u5bf9\u8db3\u5370\u59cb\u7ec8\u907f\u5f00\u9053\u8def\uff0c\u5728\u8349\u4e1b\u95f4\u66f2\u66f2\u6298\u6298\u5730\u7a7f\u884c\u3002\u8ddf\u4e86\u4e00\u4e2a\u591a\u65f6\u8fb0\uff0c\u5230\u4e86\u4e00\u4e2a\u5c0f\u5e02\u9547\uff0c\u9547\u5916\u8db3\u8ff9\u6742\u6c93\uff0c\u518d\u4e5f\u5206\u4e0d\u6e05\u695a\u4e86\u3002 \u3000\u3000\u80e1\u6590\u5fc3\u60f3\uff1a\u201c\u4ed6\u4e8c\u4eba\u997f\u4e86\u4e00\u665a\uff0c\u6b64\u65f6\u5fc5\u8981\u6253\u5c16\uff0c\u5c31\u53ea\u6015\u4ed6\u4eec\u53ea\u4e70\u4e9b\u9992\u5934\u70b9\u5fc3\uff0c\u4fbf\u7a7f\u9547\u800c\u53bb\uff0c\u90a3\u4fbf\u4e0d\u6613\u8ffd\u5bfb\u3002\u201d\u4e8e\u662f\u5728\u9547\u53e3\u7684\u5c71\u8d27\u5e97\u91cc\u4e70\u4e86\u4e00\u4ef6\u84d1\u8863\u4e00\u9876\u6597\u7b20\uff0c\u7a7f\u6234\u8d77\u6765\uff0c\u5c06\u5927\u534a\u4e2a\u8138\u90fd\u906e\u4f4f\u4e86\uff0c\u8d70\u5230\u9547\u4e0a\u51e0\u5bb6\u996d\u5e97\u548c\u9aa1\u9a6c\u884c\u53bb\u63a2\u89c6\u3002 \u3000\u3000\u77a7\u4e86\u51e0\u5bb6\u90fd\u4e0d\u89c1\u5f71\u8e2a\uff0c\u8fd9\u5e02\u9547\u4e0d\u5927\uff0c\u8f6c\u773c\u4fbf\u5230\u4e86\u9547\u5934\uff0c\u6b63\u8981\u56de\u8fc7\u8eab\u6765\uff0c\u81ea\u884c\u53bb\u4e70\u996d\u5403\uff0c\u5ffd\u542c\u4e00\u4e2a\u5973\u5b50\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u5927\u5ac2\uff0c\u6709\u9488\u7ebf\u8bf7\u76f8\u501f\u4e00\u4f7f\u3002\u201d\u6b63\u662f\u5218\u9e64\u771f\u4e4b\u59bb\u7684\u58f0\u97f3\u3002 \u3000\u3000\u4ed6\u4f4e\u5934\u4ece\u6597\u7b20\u4e0b\u659c\u773c\u770b\u53bb\uff0c\u89c1\u8bdd\u58f0\u662f\u4ece\u4e00\u5bb6\u6c11\u5c45\u4e2d\u53d1\u51fa\uff0c\u5fc3\u60f3\uff1a\u201c\u4ed6\u592b\u5987\u6015\u654c\u4eba\u8ddf\u8e2a\uff0c\u662f\u4ee5\u4e0d\u6562\u4f4f\u5e97\u3002\u201d\u53c8\u60f3\uff1a\u201c\u77a7\u4ed6\u4eec\u8fd9\u7b49\u4e25\u52a0\u9632\u5907\u7684\u6a21\u6837\uff0c\u53ea\u6015\u9664\u4e86\u953a\u6c0f\u5144\u5f1f\uff0c\u5c1a\u6709\u6781\u5389\u5bb3\u7684\u5bf9\u5934\u548c\u4ed6\u4eec\u4e3a\u96be\u3002\u4e00\u4e0d\u505a\uff0c\u4e8c\u4e0d\u4f11\uff0c\u6211\u7d22\u6027\u6697\u4e2d\u4fdd\u62a4\uff0c\u52a1\u5fc5\u8ba9\u4ed6\u4eec\u5c06\u4e66\u4fe1\u9001\u5230\u82d7\u5927\u4fa0\u624b\u4e2d\u3002\u201d\u56de\u5934\u4e0d\u5230\u4e03\u516b\u5bb6\u95e8\u9762\uff0c\u4fbf\u662f\u4e00\u5bb6\u5c0f\u5ba2\u5e97\uff0c\u4e8e\u662f\u627e\u4e00\u4e2a\u623f\u4f4f\u4e86\uff0c\u4e00\u76f4\u6ce8\u89c6\u5218\u9e64\u771f\u501f\u4f4f\u7684\u90a3\u5bb6\u4eba\u5bb6\u3002 \u3000\u3000\u76f4\u5230\u508d\u665a\uff0c\u5218\u9e64\u771f\u592b\u5987\u59cb\u7ec8\u6ca1\u6709\u9732\u9762\u3002\u80e1\u6590\u5fc3\u60f3\uff1a\u201d\u524d\u8f88\u505a\u4e8b\u771f\u662f\u4ed4\u7ec6\uff0c\u4ed6\u4eec\u5b9a\u8981\u5f85\u5929\u9ed1\u900f\u4e86\u65b9\u624d\u542f\u7a0b\u3002\u201d\u679c\u7136\u5f85\u5230\u4e8c\u66f4\u5929\u65f6\uff0c\u671b\u89c1\u5218\u9e64\u771f\u592b\u5987\u4ece\u90a3\u6c11\u5c45\u4e2d\u51fa\u6765\uff0c\u75be\u5954\u51fa\u9547\uff0c\u811a\u6b65\u8fc5\u6377\uff0c\u663e\u7136\u8eab\u4e0a\u5e76\u672a\u53d7\u4f24\u3002 \u3000\u3000\u80e1\u6590\u5fc3\u60f3\uff1a\u201c\u539f\u6765\u4ed6\u4eec\u5148\u524d\u7684\u53d7\u4f24\u5168\u662f\u5047\u88c5\uff0c\u4e0d\u4f46\u7792\u8fc7\u4e86\u953a\u6c0f\u5144\u5f1f\uff0c\u8fde\u6211\u4e5f\u7ed9\u7792\u8fc7\u4e86\u3002\u201d\u4ed6\u4e0d\u6562\u6020\u6162\uff0c\u8dc3\u51fa\u7a97\u6237\uff0c\u8ddf\u968f\u5728\u540e\u3002\u53ea\u89c1\u5218\u9e64\u771f\u814b\u4e0b\u631f\u7740\u4e00\u4e2a\u957f\u957f\u7684\u5305\u88f9\uff0c\u4e0d\u77e5\u5305\u7740\u4ec0\u4e48\u4e1c\u897f\u3002\u4ed6\u7684\u8f7b\u8eab\u529f\u592b\u6bd4\u5218\u9e64\u771f\u9ad8\u660e\u5f97\u591a\uff0c\u6084\u6084\u8ddf\u968f\u5728\u540e\uff0c\u6599\u60f3\u5218\u6c0f\u592b\u5987\u5b9a\u7136\u6beb\u4e0d\u77e5\u89c9\u3002 \u3000\u3000\u8ddf\u7740\u4e8c\u4eba\u8d70\u4e86\u4e94\u516d\u91cc\u8def\uff0c\u6765\u5230\u5b64\u96f6\u96f6\u7684\u4e00\u6240\u5c0f\u5c4b\u4e4b\u524d\uff0c\u53ea\u89c1\u5218\u9e64\u771f\u6253\u4e2a\u624b\u52bf\uff0c\u547d\u59bb\u5b50\u4f0f\u5728\u8349\u4e1b\u4e4b\u4e2d\uff0c\u8d70\u4e0a\u51e0\u6b65\uff0c\u6717\u58f0\u9053\uff1a\u201c\u91d1\u9762\u4f5b\u82d7\u5927\u4fa0\u5728\u5bb6\u4e48\uff1f\u6709\u670b\u53cb\u8fdc\u9053\u6765\u8bbf\u3002\u201d \u3000\u3000\u53ea\u542c\u5c4b\u4e2d\u4e00\u4eba\u8bf4\u9053\uff1a\u201c\u662f\u54ea\u4e00\u4f4d\u670b\u53cb\uff1f\u6055\u82d7\u4eba\u51e4\u773c\u751f\uff0c\u7d20\u4e0d\u76f8\u8bc6\u3002\u201d\u8fd9\u8bdd\u58f0\u5e76\u4e0d\u5341\u5206\u54cd\u4eae\uff0c\u80e1\u6590\u542c\u5728\u8033\u4e2d\u53ea\u89c9\u53c8\u662f\u82cd\u51c9\uff0c\u53c8\u662f\u9187\u539a\u3002 \u3000\u3000\u5218\u9e64\u771f\u9053\uff1a\u201c\u5c0f\u4eba\u59d3\u953a\uff0c\u5949\u9102\u5317\u9b3c\u89c1\u6101\u953a\u6c0f\u5144\u5f1f\u4e4b\u547d\uff0c\u6709\u8981\u51fd\u4e00\u901a\u9001\u4ea4\u82d7\u5927\u4fa0\u3002\u201d\u80e1\u6590\u5927\u662f\u60ca\u5947\uff1a\u201c\u600e\u4e48\u90a3\u4fe1\u662f\u953a\u6c0f\u5144\u5f1f\u7684\uff1f\u4ed6\u4eec\u5374\u4f55\u4ee5\u53c8\u8981\u62e6\u963b\uff1f\u201d\u53ea\u542c\u82d7\u4eba\u51e4\u9053\uff1a\u201c\u8bf7\u8fdb\u5427\uff01\u201d\u5c4b\u4e2d\u70b9\u8d77\u706f\u706b\uff0c\u5440\u7684\u4e00\u58f0\uff0c\u6728\u95e8\u6253\u5f00\u3002\u80e1\u6590\u4f0f\u5728\u4e00\u682a\u6817\u6811\u4e4b\u540e\uff0c\u4f46\u89c1\u4e00\u4e2a\u6781\u9ad8\u6781\u7626\u7684\u4eba\u5f71\u7ad9\u5728\u95e8\u6846\u4e4b\u95f4\uff0c\u5934\u9876\u51e0\u8981\u78b0\u5230\u95e8\u6846\uff0c\u53f3\u624b\u6267\u7740\u4e00\u53ea\u70db\u53f0\u3002 \u3000\u3000\u5218\u9e64\u771f\u62f1\u624b\u884c\u793c\uff0c\u8d70\u8fdb\u5c4b\u4e2d\u3002\u80e1\u6590\u5f85\u4e24\u4eba\u8fdb\u5c4b\uff0c\u4fbf\u6084\u6084\u7ed5\u5230\u5de6\u8fb9\u7a97\u6237\u4e0b\u5077\u77a7\u3002\u82d7\u4eba\u51e4\u9053\uff1a\u201c\u53e6\u5916\u4e24\u4f4d\u4e0d\u8fdb\u6765\u4e48\uff1f\u201d\u5218\u9e64\u771f\u5fc3\u60f3\uff1a\u201c\u54ea\u91cc\u8fd8\u6709\u4e24\u4f4d\uff1f\u201d\u53e3\u4e2d\u542b\u7cca\u7b54\u5e94\u3002 \u3000\u3000\u80e1\u6590\u4e00\u542c\u82d7\u4eba\u51e4\u8bf4\u5230\u201c\u53e6\u5916\u4e24\u4f4d\u201d\uff0c\u5fc3\u4e2d\u4e00\u60ca\uff1a\u201c\u8fd9\u82d7\u4eba\u51e4\u679c\u7136\u5389\u5bb3\u4e4b\u6781\uff0c\u6211\u811a\u6b65\u58f0\u867d\u8f7b\uff0c\u4ed6\u5374\u65e9\u77e5\u5171\u6709\u4e09\u4eba\u540c\u6765\u3002\u201d \u3000\u3000\u5fc3\u60f3\u5728\u6b64\u5077\u770b\uff0c\u4ed6\u4e5f\u5fc5\u5b9a\u77e5\u89c9\uff0c\u6b63\u60f3\u9000\u5f00\uff0c\u5ffd\u542c\u5218\u9e64\u771f\u9053\uff1a\u201c\u953a\u6c0f\u5144\u5f1f\u516b\u5e74\u524d\u9886\u6559\u4e86\u82d7\u5927\u4fa0\u7684\u9ad8\u62db\uff0c\u4f69\u670d\u5f97\u4e94\u4f53\u6295\u5730\uff0c\u73b0\u4e0b\u53e6\u884c\u7ec3\u4e86\u4e09\u4ef6\u5175\u5203\uff0c\u7279\u547d\u5c0f\u4eba\u5148\u9001\u7ed9\u82d7\u5927\u4fa0\u77a7\u77a7\uff0c\u4ee5\u514d\u52a8\u624b\u4e4b\u9645\uff0c\u82d7\u5927\u4fa0\u8bf4\u4ed6\u4eec\u5175\u5203\u602a\u5f02\uff0c\u5360\u4e86\u4fbf\u5b9c\u3002\u201d\u8bf4\u7740\u6253\u5f00\u5305\u88f9\uff0c\u545b\u5577\u5577\u51e0\u58f0\u54cd\uff0c\u5c06\u4e09\u4ef6\u5175\u5668\u6296\u5728\u684c\u4e0a\u3002 \u3000\u3000\u80e1\u6590\u89c9\u5f97\u4ed6\u7684\u4e3e\u52a8\u8d8a\u6765\u8d8a\u662f\u4e0d\u53ef\u601d\u8bae\uff0c\u4fef\u773c\u5230\u7a97\u7f1d\u4e0a\u5411\u5185\u5f20\u671b\uff0c\u4f46\u89c1\u684c\u4e0a\u4e09\u4ef6\u5175\u5668\u6b63\u662f\u90a3\u94c1\u7075\u724c\u3001\u54ed\u4e27\u68d2\u548c\u62db\u9b42\u5e61\uff0c\u5175\u5203\u4e0a\u6ce5\u6c61\u6591\u6591\uff0c\u5140\u81ea\u672a\u64e6\u5e72\u51c0\u3002 \u3000\u3000\u82d7\u4eba\u51e4\u54fc\u4e86\u4e00\u58f0\uff0c\u5411\u4e09\u4ef6\u5175\u5203\u77a7\u4e86\u4e00\u773c\uff0c\u5e76\u4e0d\u7b54\u8bdd\u3002\u5218\u9e64\u771f\u4ece\u6000\u91cc\u6478\u51fa\u4e00\u5c01\u4e66\u4fe1\uff0c\u53cc\u624b\u9012\u4e86\u4e0a\u53bb\uff0c\u8bf4\u9053\uff1a\u201c\u8bf7\u82d7\u5927\u4fa0\u62c6\u770b\uff0c\u5c0f\u4eba\u4fe1\u5df2\u9001\u5230\uff0c\u8fd9\u4fbf\u544a\u8f9e\u3002\u201d\u8bf4\u7740\u53cc\u624b\u4e00\u62f1\uff0c\u5c31\u8981\u9000\u51fa\u3002 \u3000\u3000\u82d7\u4eba\u51e4\u63a5\u8fc7\u4fe1\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u6162\u7740\u3002\u6211\u77a7\u4fe1\u4e4b\u540e\uff0c\u70e6\u4f60\u5e26\u4e00\u53e5\u56de\u8bdd\u3002\u201d\u4ed6\u5fc3\u77e5\u8fd9\u5c01\u5b9a\u662f\u6218\u4e66\uff0c\u5f53\u4e0b\u6495\u5f00\u5c01\u76ae\uff0c\u53d6\u51fa\u4fe1\u6765\u3002 \u3000\u3000\u80e1\u6590\u4e58\u82d7\u4eba\u51e4\u770b\u4fe1\uff0c\u4ed4\u7ec6\u6253\u91cf\u4ed6\u7684\u5f62\u8c8c\uff0c\u4f46\u89c1\u4ed6\u6bd4\u4e4b\u6570\u5e74\u524d\u5728\u5546\u5bb6\u5821\u76f8\u89c1\u4e4b\u65f6\uff0c\u4f3c\u5df2\u8001\u4e86\u8bb8\u591a\uff0c\u8138\u4e0a\u795e\u8272\u4e5f\u5927\u662f\u6194\u60b4\u3002\u82d7\u4eba\u51e4\u770b\u7740\u4e66\u4fe1\uff0c\u53cc\u7709\u767b\u7ad6\uff0c\u773c\u4e2d\u53d1\u51fa\u6124\u6012\u4e4b\u6781\u7684\u5149\u8292\u3002 \u3000\u3000\u80e1\u6590\u77a7\u5f97\u5bb3\u6015\uff0c\u6b63\u60f3\u9000\u5f00\uff0c\u7a81\u89c1\u4ed6\u53cc\u624b\u6293\u4f4f\u4e66\u4fe1\uff0c\u55e4\u7684\u4e00\u4e0b\uff0c\u6495\u6210\u4e24\u534a\u3002 \u3000\u3000\u4e66\u4fe1\u4e00\u7834\uff0c\u5ffd\u7136\u95f4\u4ed6\u9762\u524d\u51fa\u73b0\u4e00\u56e2\u9ec4\u8272\u6d53\u70df\uff0c\u82d7\u4eba\u51e4\u53eb\u58f0\uff1a\u201c\u554a\u54df\uff01\u201d\u53cc\u624b\u63c9\u773c\uff0c\u8138\u73b0\u75db\u82e6\u4e4b\u8272\u3002\u5218\u9e64\u771f\u6025\u7eb5\u5411\u540e\uff0c\u8dc3\u51fa\u4e08\u4f59\u3002 \u3000\u3000\u8fd9\u53d8\u6545\u8d77\u4e8e\u4fc4\u9877\uff0c\u4f46\u4fbf\u5728\u8fd9\u4e00\u970e\u4e4b\u95f4\uff0c\u80e1\u6590\u5fc3\u4e2d\u5df2\u7136\u96ea\u4eae\uff1a\u201c\u539f\u6765\u8fd9\u5218\u9e64\u771f\u5728\u4fe1\u4e2d\u6697\u85cf\u6bd2\u836f\uff0c\u6bd2\u5bb3\u82d7\u5927\u4fa0\u7684\u53cc\u76ee\u3002\u201d\u4ed6\u5927\u53eb\uff1a\u201c\u72d7\u8d3c\u4f11\u8d70\uff01\u201d\u98de\u8eab\u5411\u5218\u9e64\u771f\u6251\u53bb\u3002 \u3000\u3000\u5218\u9e64\u771f\u632b\u819d\u6c89\u8098\uff0c\u4ece\u8170\u95f4\u62d4\u51fa\u94fe\u5b50\u67aa\uff0c\u56de\u624b\u4fbf\u6233\u3002\u80e1\u6590\u5fc3\u4e2d\u6127\u6012\u4ea4\u653b\uff0c\u4fa7\u8eab\u95ea\u907f\uff0c\u4f38\u624b\u53bb\u593a\u4ed6\u94fe\u5b50\u67aa\uff0c\u731b\u89c9\u80cc\u540e\u98ce\u58f0\u52b2\u6025\uff0c\u4e00\u80a1\u521a\u731b\u65e0\u6bd4\u7684\u638c\u529b\u76f4\u6251\u81ea\u5df1\u80cc\u5fc3\uff0c\u53ea\u5f97\u53cc\u638c\u53cd\u51fb\uff0c\u8fd0\u529b\u76f8\u5378\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u82d7\u4eba\u51e4\u6025\u6012\u4e4b\u4e0b\uff0c\u8fd9\u638c\u529b\u5b9a\u7136\u975e\u540c\u5c0f\u53ef\uff0c\u4e0d\u6562\u786c\u63a5\u786c\u67b6\uff0c\u5f53\u4e0b\u4f7f\u51fa\u8d75\u534a\u5c71\u6240\u6388\u7684\u592a\u6781\u62f3\u5999\u672f\u201c\u9634\u9633\u8bc0\u201d\uff0c\u60f3\u5378\u5f00\u5bf9\u65b9\u638c\u529b\uff0c\u5c82\u77e5\u53cc\u624b\u4e0e\u5bf9\u65b9\u624b\u638c\u752b\u63a5\uff0c\u767b\u65f6\u773c\u524d\u4e00\u9ed1\uff0c\u80f8\u53e3\u6c14\u585e\uff0c\u817e\u817e\u817e\u8fde\u9000\u4e09\u6b65\uff0c\u82d7\u4eba\u51e4\u7684\u638c\u529b\u53ea\u5378\u53bb\u4e86\u4e00\u534a\uff0c\u4f59\u4e00\u534a\u8fd8\u662f\u786c\u63a5\u4e86\u8fc7\u6765\u3002\u80e1\u6590\u53eb\u9053\uff1a\u201c\u82d7\u5927\u4fa0\uff0c\u6211\u5e2e\u4f60\u62ff\u8d3c\u2026\u2026\u201d \u3000\u3000\u4e24\u4eba\u8fd9\u4e00\u4ea4\u638c\uff0c\u5218\u9e64\u771f\u5df2\u4e58\u7a7a\u6e9c\u8d70\u3002 \u3000\u3000\u82d7\u4eba\u51e4\u53ea\u89c9\u53cc\u76ee\u5267\u75db\uff0c\u5b9b\u4f3c\u6570\u5341\u679a\u91d1\u9488\u540c\u65f6\u6512\u523a\uff0c\u4ed6\u4e0e\u80e1\u6590\u4ea4\u4e86\u4e00\u62db\uff0c\u89c9\u5f97\u6b64\u4eba\u6b66\u529f\u751a\u5f3a\uff0c\u5b9e\u662f\u4e2a\u52b2\u654c\uff0c\u4e0d\u7531\u5f97\u6697\u81ea\u5fc3\u60ca\uff0c\u80e1\u6590\u90a3\u53e5\u201c\u6211\u5e2e\u4f60\u62ff\u8d3c\u201d\u7684\u8bdd\u7adf\u6ca1\u542c\u89c1\u3002 \u3000\u3000\u80e1\u6590\u773c\u89c1\u5218\u9e64\u771f\u592b\u5987\u5f80\u897f\u9003\u53bb\uff0c\u6b63\u8981\u62d4\u6b65\u8ffd\u8d76\uff0c\u5ffd\u89c1\u5927\u8def\u4e0a\u4e09\u4eba\u5feb\u6b65\u5954\u6765\u3002\u8fd9\u4e09\u4eba\u62ab\u9ebb\u6234\u5b5d\uff0c\u4e0d\u7528\u77a7\u9762\u76ee\uff0c\u4fbf\u77e5\u662f\u953a\u6c0f\u4e09\u96c4\u4e86\u3002 \u3000\u3000\u80e1\u6590\u56de\u8fc7\u5934\u6765\uff0c\u89c1\u82d7\u4eba\u51e4\u53cc\u624b\u6309\u4f4f\u773c\u775b\uff0c\u8138\u4e0a\u795e\u60c5\u75db\u695a\uff0c\u5f85\u8981\u4e0a\u524d\u6551\u52a9\uff0c\u53c8\u6015\u4ed6\u7a81\u7136\u53d1\u638c\uff0c\u4e8e\u662f\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u82d7\u5927\u4fa0\uff0c\u6211\u867d\u4e0d\u662f\u4f60\u670b\u53cb\uff0c\u53ef\u4e5f\u51b3\u8ba1\u4e0d\u4f1a\u52a0\u5bb3\uff0c\u4f60\u4fe1\u4e5f\u4e0d\u4fe1\uff1f\u201d \u3000\u3000\u8fd9\u51e0\u53e5\u8bdd\u8bf4\u5f97\u6781\u662f\u8bda\u6073\u3002\u82d7\u4eba\u51e4\u867d\u672a\u89c1\u5230\u4ed6\u9762\u76ee\uff0c\u81ea\u5df1\u53c8\u521a\u4e2d\u4e86\u5978\u4eba\u6697\u7b97\uff0c\u53cc\u76ee\u75db\u5982\u5200\u525c\uff0c\u4f46\u4e00\u542c\u6b64\u8a00\uff0c\u81ea\u7136\u800c\u7136\u89c9\u5f97\u8fd9\u5c11\u5e74\u7edd\u975e\u574f\u4eba\uff0c\u771f\u6240\u8c13\u82f1\u96c4\u8bc6\u82f1\u96c4\uff0c\u7247\u8a00\u4e4b\u95f4\uff0c\u5df2\u662f\u610f\u6c14\u76f8\u6295\uff0c\u4e8e\u662f\u8bf4\u9053\uff1a\u201c\u4f60\u7ed9\u6211\u6321\u4f4f\u95e8\u5916\u7684\u5978\u4eba\u3002\u201d\u4ed6\u4e0d\u7b54\u80e1\u6590\u201c\u4fe1\u4e5f\u4e0d\u4fe1\uff1f\u201d\u7684\u95ee\u8bdd\uff0c\u4f46\u53eb\u4ed6\u6321\u4f4f\u5916\u654c\uff0c\u90a3\u4fbf\u662f\u5f53\u4ed6\u81f3\u4ea4\u597d\u53cb\u4e00\u822c\u3002 \u3000\u3000\u80e1\u6590\u80f8\u53e3\u4e00\u70ed\uff0c\u4f46\u89c9\u8fd9\u8bdd\u8c6a\u6c14\u5e72\u4e91\uff0c\u82e5\u975e\u80f8\u895f\u5bbd\u535a\u7684\u5927\u82f1\u96c4\u5927\u8c6a\u6770\uff0c\u51b3\u4e0d\u80fd\u8bf4\u5f97\u51fa\u53e3\uff0c\u5f53\u771f\u662f\u6709\u767d\u5934\u5982\u65b0\uff0c\u6709\u503e\u76d6\u5982\u6545\uff0c\u82d7\u4eba\u51e4\u53ea\u4e00\u53e5\u8bdd\uff0c\u80e1\u6590\u7acb\u65f6\u7518\u613f\u4e3a\u4ed6\u8d74\u6c64\u8e48\u706b\uff0c\u773c\u89c1\u953a\u6c0f\u4e09\u5144\u5f1f\u76f8\u8ddd\u5c4b\u95e8\u5c1a\u6709\u4e8c\u5341\u6765\u4e08\uff0c\u5f53\u5373\u62ff\u8d77\u70db\u53f0\uff0c\u5954\u81f3\u540e\u8fdb\u53a8\u623f\u4e2d\uff0c\u62ff\u6c34\u74e2\u5728\u6c34\u7f38\u4e2d\u8200\u4e86\u4e00\u74e2\u6c34\uff0c\u9012\u7ed9\u82d7\u4eba\u51e4\uff0c\u9053\uff1a\u201c\u5feb\u6d17\u6d17\u773c\u775b\u3002\u201d \u3000\u3000\u82d7\u4eba\u51e4\u773c\u775b\u867d\u75db\uff0c\u5fc3\u667a\u4ecd\u6781\u6e05\u660e\uff0c\u542c\u5f97\u6b63\u9762\u5927\u8def\u4e0a\u6709\u4e09\u4eba\u5954\u6765\uff0c\u53e6\u6709\u56db\u4e2a\u4eba\u4ece\u5c4b\u540e\u7a9c\u4e0a\u4e86\u5c4b\u9876\u3002\u4ed6\u63a5\u8fc7\u6c34\u74e2\uff0c\u8d70\u8fdb\u5185\u623f\uff0c\u5148\u5728\u5e8a\u4e0a\u62b1\u8d77\u4e86\u5c0f\u5973\u513f\uff0c\u8fd9\u624d\u4f4e\u5934\u5230\u6c34\u74e2\u4e2d\u6d17\u773c\u3002\u8fd9\u6bd2\u836f\u5b9e\u662f\u731b\u6076\u4e4b\u6781\uff0c\u7ecf\u6c34\u4e00\u6d17\uff0c\u66f4\u662f\u5267\u75db\u900f\u9aa8\u94bb\u5fc3\u3002 \u3000\u3000\u90a3\u5c0f\u5973\u5b69\u7761\u5f97\u8ff7\u8ff7\u7cca\u7cca\uff0c\u8bf4\u9053\uff1a\u201c\u7239\u7239\uff0c\u4f60\u540c\u5170\u513f\u73a9\u4e48\uff1f\u201d \u3000\u3000\u82d7\u4eba\u51e4\u9053\uff1a\u201c\u55ef\uff0c\u4e56\u5170\u513f\uff0c\u7239\u62b1\u7740\u4f60\uff0c\u522b\u7741\u5f00\u773c\u775b\uff0c\u597d\u597d\u7684\u7761\u7740\u3002\u201d\u90a3\u5973\u5b69\u9053\uff1a\u201c\u90a3\u8001\u72fc\u771f\u7684\u6ca1\u5403\u4e86\u5c0f\u767d\u7f8a\u5417\uff1f\u201d\u82d7\u4eba\u51e4\u9053\uff1a\u201c\u81ea\u7136\u6ca1\u6709\uff0c\u730e\u4eba\u6765\u4e86\uff0c\u8001\u72fc\u5c31\u9003\u8d70\u5566\uff01\u201d\u90a3\u5973\u5b69\u5b89\u5fc3\u5730\u53f9\u4e86\u53e3\u6c14\uff0c\u5c06\u8138\u86cb\u513f\u9760\u5728\u7236\u4eb2\u80f8\u53e3\uff0c\u53c8\u7761\u7740\u4e86\u3002 \u3000\u3000\u80e1\u6590\u542c\u4ed6\u7236\u5973\u4fe9\u5bf9\u7b54\uff0c\u5fae\u5fae\u4e00\u6014\uff0c\u968f\u5373\u660e\u767d\uff0c\u5973\u5b69\u5728\u7761\u89c9\u4e4b\u524d\uff0c\u66fe\u542c\u7236\u4eb2\u8bf4\u8fc7\u8001\u72fc\u60f3\u5403\u5c0f\u767d\u7f8a\u7684\u6545\u4e8b\uff0c\u5728\u7761\u68a6\u4e4b\u4e2d\u5140\u81ea\u8bb0\u7740\u3002 \u3000\u3000\u6b64\u65f6\u953a\u6c0f\u5144\u5f1f\u8ddd\u5927\u95e8\u5df2\u4e0d\u5230\u5341\u4e08\uff0c\u53ea\u542c\u5f97\u5657\u5657\u4e24\u58f0\uff0c\u4e24\u4e2a\u4eba\u4ece\u5c4b\u9876\u8dc3\u5165\u4e86\u9662\u5b50\u3002\u80e1\u6590\u5173\u4e0a\u5927\u95e8\uff0c\u62d6\u8fc7\u684c\u5b50\u9876\u4f4f\uff0c\u53eb\u953a\u6c0f\u5144\u5f1f\u4e0d\u80fd\u7acb\u5373\u5165\u5c4b\uff0c\u4ee5\u514d\u524d\u540e\u53d7\u653b\uff0c\u8ddf\u7740\u5de6\u624b\u4e00\u717d\uff0c\u70db\u706b\u7184\u706d\u3002\u8dc3\u5165\u9662\u5b50\u7684\u4e24\u4eba\u89c1\u5c4b\u4e2d\u6ca1\u4e86\u706b\u5149\uff0c\u4e0d\u6562\u7acb\u65f6\u95ef\u8fdb\u3002 \u3000\u3000\u82d7\u4eba\u51e4\u4f4e\u58f0\u9053\uff1a\u201c\u8ba9\u56db\u4e2a\u4eba\u90fd\u8fdb\u6765\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u597d\uff01\u201d\u53d6\u51fa\u706b\u5200\u706b\u77f3\uff0c\u53c8\u70b9\u71c3\u4e86\u8721\u70db\uff0c\u5c06\u70db\u53f0\u653e\u5728\u684c\u4e0a\u3002 \u3000\u3000\u53ea\u542c\u5f97\u5927\u95e8\u5916\u953a\u5146\u82f1\u53eb\u9053\uff1a\u201c\u9102\u5317\u953a\u5146\u82f1\u3001\u5146\u6587\u3001\u5146\u80fd\u4e09\u5144\u5f1f\u62dc\u89c1\u82d7\u5927\u4fa0\uff0c\u6709\u6025\u4e8b\u5949\u544a\u3002\u201d\u82d7\u4eba\u51e4\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u5e76\u4e0d\u7406\u776c\u3002 \u3000\u3000\u9662\u5b50\u4e2d\u7684\u4e24\u4eba\u4e00\u4eba\u6267\u5200\uff0c\u53e6\u4e00\u4eba\u62ff\u7740\u4e00\u6761\u4e09\u8282\u68cd\uff0c\u773c\u89c1\u82d7\u4eba\u51e4\u53cc\u76ee\u7d27\u95ed\uff0c\u7741\u4e0d\u5f00\u6765\uff0c\u4f46\u9707\u4e8e\u201c\u6253\u904d\u5929\u4e0b\u65e0\u654c\u624b\u201d\u7684\u5a01\u540d\uff0c\u54ea\u6562\u8d38\u7136\u8fdb\u5c4b\uff1f\u90a3\u6301\u5200\u7684\u4eba\u5411\u5c4b\u4e0a\u4e00\u62db\u624b\uff0c\u53eb\u9053\uff1a\u201c\u4ed6\u773c\u775b\u778e\u4e86\uff01\u201d\u5c4b\u4e0a\u4e24\u4eba\u5927\u559c\uff0c\u4e00\u9f50\u8dc3\u4e0b\u3002 \u3000\u3000\u80e1\u6590\u77a7\u8fd9\u4e24\u4eba\u8eab\u624b\u77eb\u6377\uff0c\u6bd4\u5148\u524d\u4e24\u4eba\u5f3a\u5f97\u591a\uff0c\u5f53\u4e0b\u8eab\u5f62\u4e00\u95ea\uff0c\u62a2\u5230\u4e86\u4e24\u4eba\u80cc\u540e\uff0c\u53cc\u638c\u5411\u524d\u63a8\u51fa\u3002\u559d\u9053\uff1a\u201c\u8fdb\u53bb\uff01\u201d\u8fd9\u4e00\u63a8\u529b\u9053\u521a\u731b\uff0c\u4e24\u4eba\u4e0d\u6562\u786c\u63a5\uff0c\u5411\u524d\u6025\u51b2\u4e86\u51e0\u6b65\uff0c\u8de8\u8fc7\u95e8\u69db\uff0c\u8fdb\u4e86\u5ba2\u5802\u3002 \u3000\u3000\u80e1\u6590\u5b88\u5728\u8fb9\u95e8\u4e4b\u5916\uff0c\u8f7b\u8f7b\u5438\u4e00\u53e3\u6c14\uff0c\u731b\u529b\u4e00\u5410\uff0c\u6ce2\u7684\u4e00\u58f0\uff0c\u4e00\u4e08\u591a\u5916\u7684\u70db\u706b\u767b\u65f6\u53c8\u706d\u4e86\u3002\u5ba2\u5802\u4e2d\u9ed1\u6f06\u4e00\u56e2\u3002 \u3000\u3000\u6765\u88ad\u7684\u56db\u4eba\u5413\u4e86\u4e00\u8df3\uff0c\u4e00\u6014\u4e4b\u4e0b\uff0c\u5404\u633a\u5175\u5203\u5411\u82d7\u4eba\u51e4\u653b\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u90a3\u5973\u5b69\u7761\u5728\u82d7\u4eba\u51e4\u6000\u4e2d\uff0c\u8f6c\u4e86\u8fc7\u8eab\uff0c\u95ee\u9053\uff1a\u201c\u7239\uff0c\u4ec0\u4e48\u58f0\u97f3\uff1f\u662f\u8001\u72fc\u6765\u4e86\u4e48\uff1f\u201d\u82d7\u4eba\u51e4\u9053\uff1a\u201c\u4e0d\u662f\u8001\u72fc\uff0c\u53ea\u662f\u56db\u53ea\u5c0f\u8017\u5b50\u3002\u201d\u542c\u5230\u5175\u5203\u5288\u98ce\u4e4b\u58f0\u88ad\u5411\u5934\u9876\uff0c\u4e2d\u95f4\u5939\u7740\u9501\u94fe\u626d\u52a8\u7684\u58f0\u97f3\uff0c\u77e5\u662f\u4e09\u8282\u68cd\u3001\u94fe\u5b50\u67aa\u4e00\u7c7b\u6b66\u5668\uff0c\u53f3\u624b\u500f\u5730\u4f38\u51fa\uff0c\u6293\u4f4f\u4e09\u8282\u68cd\u7684\u68cd\u5934\u4e00\u6296\uff0c\u90a3\u4eba\u201c\u554a\u201d\u7684\u4e00\u58f0\uff0c\u624b\u81c2\u9178\u9ebb\uff0c\u4e09\u8282\u68cd\u5df2\u7136\u8131\u624b\u3002\u82d7\u4eba\u51e4\u987a\u624b\u6325\u51fa\uff0c\u62cd\u7684\u4e00\u54cd\uff0c\u51fb\u5728\u4ed6\u8170\u773c\u4e4b\u4e0a\u3002\u90a3\u4eba\u7acb\u65f6\u95ed\u6c14\uff0c\u6655\u4e86\u8fc7\u53bb\u3002\u5176\u4f59\u4e24\u4eba\u4f7f\u5200\uff0c\u4e00\u4eba\u4f7f\u4e00\u6761\u94c1\u97ad\uff0c\u9ed8\u4e0d\u4f5c\u58f0\u7684\u5206\u4ece\u4e09\u9762\u653b\u4e0a\u3002\u4e09\u4eba\u77e5\u9053\u82d7\u4eba\u51e4\u89c6\u529b\u5df2\u5931\uff0c\u5168\u51ed\u542c\u89c9\u8fa8\u654c\uff0c\u662f\u4ee5\u4e0d\u6562\u7a0d\u6709\u58f0\u54cd\u3002 \u3000\u3000\u90a3\u5973\u5b69\u9053\uff1a\u201c\u7239\uff0c\u8017\u5b50\u4f1a\u54ac\u4eba\u4e48\uff1f\u201d\u82d7\u4eba\u51e4\u9053\uff1a\u201c\u8017\u5b50\u60f3\u5077\u5077\u6478\u6478\u7684\u6765\u54ac\u4eba\uff0c\u4e0d\u8fc7\u89c1\u5230\u8001\u732b\uff0c\u8017\u5b50\u4fbf\u53ea\u597d\u9003\u8d70\u4e86\u3002\u201d\u90a3\u5973\u5b69\u9053\uff1a\u201c\u4ec0\u4e48\u58f0\u97f3\u54cd\uff1f\u662f\u522e\u5927\u98ce\u5417\uff1f\u7239\uff0c\u662f\u4e0d\u662f\u8981\u4e0b\u96e8\u4e86\uff1f\u201d\u82d7\u4eba\u51e4\u9053\uff1a\u201c\u662f\u554a\uff01\u5f85\u4f1a\u513f\u8fd8\u8981\u6253\u96f7\u5462\uff01\u201d\u90a3\u5973\u5b69\u9053\uff1a\u201c\u96f7\u516c\u83e9\u8428\u53ea\u6253\u6076\u4eba\uff0c\u4e0d\u6253\u597d\u4eba\u3002\u662f\u4e0d\u662f\uff1f\u201d\u82d7\u4eba\u51e4\u9053\uff1a\u201c\u662f\u554a\uff01\u96f7\u516c\u83e9\u8428\u559c\u6b22\u4e56\u5973\u5b69\u513f\u3002\u201d\u82d7\u4eba\u51e4\u5355\u624b\u62c6\u89e3\u4e09\u822c\u5175\u5203\uff0c\u53e3\u4e2d\u548c\u5973\u513f\u4e00\u95ee\u4e00\u7b54\uff0c\u7adf\u6ca1\u5c06\u8eab\u65c1\u4e09\u4e2a\u654c\u4eba\u653e\u5728\u5fc3\u4e0a\u3002 \u3000\u3000\u90a3\u4e09\u4eba\u8fde\u51fa\u72e0\u62db\uff0c\u90fd\u7ed9\u82d7\u4eba\u51e4\u4f38\u53f3\u624b\u62a2\u653b\u5316\u89e3\u3002\u4e00\u4e2a\u4f7f\u5200\u7684\u5bb3\u6015\u8d77\u6765\uff0c\u53eb\u9053\uff1a\u201c\u98ce\u7d27\uff0c\u626f\u547c\uff01\u201d\u8f6c\u8eab\u51fa\u5916\uff0c\u51b2\u5230\u95e8\u8fb9\u65f6\uff0c\u80e1\u6590\u5de6\u817f\u626b\u51fa\uff0c\u5c06\u4ed6\u8e22\u5012\u5728\u5730\uff0c\u987a\u624b\u5c06\u4ed6\u7684\u5355\u5200\u593a\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u82d7\u4eba\u51e4\u9053\uff1a\u201c\u4e56\u5b9d\u8d1d\uff0c\u4f60\u542c\u3002\u8981\u6253\u96f7\u5566\uff01\u201d\u4e00\u62f3\u51fb\u51fa\uff0c\u6b63\u4e2d\u90a3\u4f7f\u94c1\u97ad\u7684\u4e0b\u989a\uff0c\u7830\u7684\u4e00\u58f0\uff0c\u8fd9\u4eba\u98de\u4e86\u8d77\u6765\uff0c\u8d8a\u8fc7\u80e1\u6590\u5934\u9876\uff0c\u6454\u5728\u9662\u5b50\u4e4b\u4e2d\u3002\u53e6\u4e00\u4e2a\u4f7f\u5200\u7684\u6b66\u529f\u6700\u5f3a\uff0c\u624b\u811a\u6ed1\u6e9c\u3002\u82d7\u4eba\u51e4\u8fde\u53d1\u4e24\u62f3\uff0c\u7adf\u90fd\u7ed9\u4ed6\u907f\u5f00\u3002\u82d7\u4eba\u51e4\u751f\u6015\u60ca\u5413\u4e86\u5973\u513f\uff0c\u53ea\u662f\u5750\u5728\u6905\u4e0a\uff0c\u5e76\u4e0d\u8d77\u8eab\u8ffd\u51fa\u3002 \u3000\u3000\u90a3\u4eba\u8fd9\u65f6\u5df2\u660e\u767d\u82d7\u4eba\u51e4\u773c\u775b\u867d\u778e\uff0c\u81ea\u5df1\u53ef\u5948\u4f55\u4ed6\u4e0d\u5f97\uff0c\u53c8\u77e5\u5b88\u5728\u95e8\u53e3\u90a3\u4eba\u4e5f\u662f\u4e2a\u6781\u5389\u5bb3\u7684\u811a\u8272\uff0c\u81ea\u5df1\u56f0\u5728\u5c0f\u5c4b\u4e4b\u4e2d\uff0c\u53d8\u6210\u4e86\u74ee\u4e2d\u4e4b\u9cd6\uff0c\u96be\u9053\u675f\u624b\u5f85\u6bd9\u4e0d\u6210\uff1f\u7a81\u7136\u5411\u82d7\u4eba\u51e4\u731b\u780d\u4e00\u5200\uff0c\u4e58\u4ed6\u4fa7\u8eab\u907f\u8ba9\uff0c\u4e00\u95ea\u8eab\u8fdb\u4e86\u5367\u5ba4\uff0c\u4ed6\u6643\u4eae\u706b\u6298\uff0c\u70b9\u71c3\u4e86\u5e8a\u4e0a\u7684\u7eb1\u5e10\uff0c\u8ddf\u7740\u4ece\u7a97\u4e2d\u7a9c\u51fa\uff0c\u4e0a\u4e86\u5c4b\u9876\u3002 \u3000\u3000\u7eb1\u5e10\u7740\u706b\u6781\u5feb\uff0c\u8f6c\u77ac\u4e4b\u95f4\uff0c\u5df2\u662f\u6d53\u70df\u6ee1\u5c4b\u3002 \u3000\u3000\u953a\u5146\u82f1\u5728\u95e8\u5916\u53eb\u9053\uff1a\u201c\u82d7\u5927\u4fa0\uff0c\u6211\u4e09\u5144\u5f1f\u662f\u6765\u627e\u4f60\u6bd4\u6b66\u8f83\u91cf\uff0c\u4f46\u6b64\u65f6\u51b3\u4e0d\u4e58\u4eba\u4e4b\u5371\uff0c\u4f60\u653e\u5fc3\u4fbf\u662f\u3002\u201d\u953a\u5146\u6587\u89c1\u7a97\u4e2d\u900f\u51fa\u706b\u5149\uff0c\u53eb\u9053\uff1a\u201c\u8d77\u706b\uff0c\u8d77\u706b\uff01\u201d\u953a\u5146\u80fd\u53eb\u9053\uff1a\u201c\u8d3c\u5b50\u5982\u6b64\u5351\u9119\u3002 \u3000\u3000\u5927\u54e5\uff0c\u54b1\u4eec\u5148\u6551\u706b\u8981\u7d27\u3002\u201d\u4e09\u5144\u5f1f\u8dc3\u4e0a\u5c4b\u9876\u3002 \u3000\u3000\u80e1\u6590\u77e5\u9053\u953a\u6c0f\u5144\u5f1f\u6b66\u529f\u4e86\u5f97\uff0c\u975e\u9002\u624d\u56db\u4eba\u53ef\u6bd4\uff0c\u82d7\u4eba\u51e4\u672c\u4e8b\u518d\u5f3a\uff0c\u603b\u662f\u53cc\u76ee\u4e0d\u80fd\u89c1\u7269\uff0c\u6000\u4e2d\u53c8\u62b1\u7740\u5973\u513f\uff0c\u5b9a\u7136\u96be\u4ee5\u62b5\u654c\uff0c\u987b\u5f97\u81ea\u5df1\u51fa\u624b\u52a9\u4ed6\u6253\u53d1\uff0c\u4e8e\u662f\u5927\u58f0\u559d\u9053\uff1a\u201c\u65e0\u803b\u5978\u5f92\uff0c\u4e0d\u8bb8\u8fdb\u6765\uff01\u201d \u3000\u3000\u90a3\u5973\u5b69\u9053\uff1a\u201c\u7239\uff0c\u597d\u70ed\uff01\u201d\u82d7\u4eba\u51e4\u63a8\u5f00\u684c\u5b50\uff0c\u4e00\u8db3\u8e22\u51fa\uff0c\u95e8\u677f\u5411\u5916\u98de\u51fa\u56db\u4e94\u4e08\u3002\u4ed6\u62b1\u7740\u5973\u5b69\u8e0f\u51fa\u5927\u95e8\uff0c\u5411\u5c4b\u9876\u4e0a\u7684\u953a\u6c0f\u5144\u5f1f\u62db\u62db\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u4e0b\u6765\u52a8\u624b\u4fbf\u662f\u3002\u201d\u4ed6\u6015\u60ca\u5413\u4e86\u5973\u513f\uff0c\u867d\u5bf9\u654c\u4eba\u8bf4\u8bdd\uff0c\u4ecd\u662f\u4f4e\u58f0\u7ec6\u6c14\u3002 \u3000\u3000\u5fc3\u4e2d\u4e0d\u81ea\u7981\u60f3\u5230\uff1a\u516b\u5e74\u4e4b\u524d\uff0c\u4e5f\u662f\u4e0e\u953a\u6c0f\u4e09\u96c4\u5bf9\u654c\uff0c\u4e5f\u662f\u5c4b\u4e2d\u8d77\u706b\uff0c\u4e5f\u662f\u81ea\u5df1\u8eab\u4e0a\u6709\u4f24\uff0c\u53ea\u662f\u966a\u7740\u81ea\u5df1\u7684\u5374\u4e0d\u662f\u5973\u513f\uff0c\u800c\u662f\u540e\u6765\u6210\u4e3a\u81ea\u5df1\u59bb\u5b50\u7684\u59d1\u5a18\u3002\u4e0d\uff0c\u5979\u6ca1\u6709\u966a\uff0c\u662f\u5728\u5371\u6025\u4e4b\u9645\u5148\u9003\u51fa\u53bb\u4e86\u2026\u2026 \u3000\u3000\u80e1\u6590\u773c\u89c1\u706b\u52bf\u731b\u70c8\uff0c\u8f6c\u773c\u4fbf\u8981\u6210\u707e\uff0c\u6599\u60f3\u82d7\u4eba\u51e4\u5fc5\u53ef\u652f\u6301\u5f97\u4e00\u65f6\uff0c\u5012\u662f\u5148\u6551\u706b\u8981\u7d27\uff0c\u629b\u4e0b\u5355\u5200\u5954\u8fdb\u53a8\u623f\uff0c\u89c1\u7076\u65c1\u5e76\u5217\u7740\u4e09\u53ea\u4e03\u77f3\u7f38\uff0c\u7f38\u4e2d\u90fd\u8d2e\u7740\u6e05\u6c34\uff0c\u4e8e\u662f\u4f38\u81c2\u62b1\u4f4f\u4e86\u4e00\u53ea\uff0c\u559d\u4e00\u58f0\uff1a\u201c\u8d77\uff01\u201d\u4e00\u53ea\u88c5\u4e86\u4e94\u516d\u767e\u65a4\u6c34\u7684\u5927\u7f38\u7adf\u7ed9\u4ed6\u62b1\u4e86\u8d77\u6765\u3002\u9976\u662f\u4ed6\u6b64\u65f6\u529f\u529b\u5df2\u81fb\u7b2c\u4e00\u6d41\u597d\u624b\u4e4b\u5883\uff0c\u4e5f\u4e0d\u7981\u811a\u6b65\u8e52\u8dda\u3002\u4ed6\u4e0d\u6562\u900f\u6c14\uff0c\u594b\u529b\u5c06\u6c34\u7f38\u62b1\u5230\u5367\u5ba4\u4e4b\u5916\uff0c\u8fde\u7f38\u5e26\u6c34\uff0c\u4e00\u5e76\u63b7\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u706b\u5934\u7ed9\u8fd9\u7f38\u6c34\u4e00\u6d47\uff0c\u767b\u65f6\u5c0f\u4e86\uff0c\u4f46\u5140\u81ea\u672a\u7184\u3002\u80e1\u6590\u53c8\u53bb\u62b1\u4e86\u4e00\u7f38\u6c34\uff0c\u8d70\u5230\u5367\u5ba4\u95e8\u5916\uff0c\u6b63\u8981\u594b\u529b\u63b7\u51fa\uff0c\u5ffd\u542c\u80cc\u540e\u547c\u7684\u4e00\u54cd\uff0c\u6709\u4eba\u5077\u88ad\u3002\u539f\u6765\u5148\u524d\u88ab\u4ed6\u8e22\u5012\u7684\u90a3\u4eba\u62fe\u8d77\u5730\u4e0b\u5355\u5200\uff0c\u5411\u4ed6\u80cc\u5fc3\u780d\u843d\u3002 \u3000\u3000\u80e1\u6590\u53cc\u624b\u62b1\u7740\u6c34\u7f38\u3002\u65e0\u6cd5\u6321\u683c\u8eb2\u95ea\uff0c\u6025\u5fd9\u53cd\u811a\u5411\u540e\u52fe\u8e22\u3002 \u3000\u3000\u8fd9\u4e00\u8e22\u602a\u5f02\u4e4b\u6781\uff0c\u5f53\u5e74\u960e\u57fa\u5b66\u5f97\u8fd9\u4e00\u62db\uff0c\u8fde\u9a6c\u884c\u7a7a\u8fd9\u7b49\u8457\u540d\u6b66\u5e08\u90fd\u96be\u4ee5\u62c6\u89e3\u3002\u8fd9\u65f6\u80e1\u6590\u53cd\u811a\u8e22\u51fa\uff0c\u6b63\u4e2d\u90a3\u4eba\u5c0f\u8179\u3002\u7830\u7684\u4e00\u54cd\uff0c\u90a3\u4eba\u8fde\u5200\u5e26\u4eba\u98de\u4e86\u8d77\u6765\uff0c\u63a0\u8fc7\u80e1\u6590\u5934\u9876\uff0c\u8dcc\u5728\u4ed6\u62b1\u7740\u7684\u6c34\u7f38\u4e4b\u4e2d\u3002 \u3000\u3000\u4ed6\u62b1\u7740\u90a3\u53e3\u4e03\u77f3\u7f38\u672c\u5df2\u5341\u5206\u5403\u529b\uff0c\u624b\u4e0a\u7a81\u7136\u53c8\u52a0\u4e86\u4e00\u767e\u4e94\u516d\u5341\u65a4\u91cd\u91cf\uff0c\u5982\u4f55\u652f\u6301\u5f97\u4f4f\uff1f\u987a\u624b\u4e00\u63a8\uff0c\u6c34\u7f38\u4e0e\u4eba\u4e00\u9f50\u98de\u5165\u706b\u4e2d\u3002\u6c34\u7f38\u7834\u88c2\uff0c\u53ea\u5272\u5f97\u90a3\u4eba\u6ee1\u8eab\u662f\u4f24\uff0c\u597d\u5728\u706b\u5934\u5df2\u7184\uff0c\u624d\u4e0d\u81f4\u846c\u8eab\u706b\u7a9f\u3002 \u3000\u3000\u80e1\u6590\u5c06\u706b\u6551\u7184\uff0c\u6b63\u8981\u51fa\u53bb\u76f8\u52a9\u82d7\u4eba\u51e4\uff0c\u5ffd\u542c\u5c4b\u540e\u4f20\u6765\u5927\u58f0\u559d\u9a82\uff0c\u53c8\u6709\u62f3\u6253\u8db3\u8e22\u4e4b\u58f0\uff0c\u6709\u4e24\u4eba\u6597\u5f97\u6781\u662f\u6fc0\u70c8\u3002\u542c\u90a3\u559d\u9a82\u7684\u58f0\u97f3\uff0c\u5374\u662f\u5218\u9e64\u771f\u6240\u53d1\uff0c\u53ea\u542c\u4ed6\u559d\u9053\uff1a\u201c\u597d\u5978\u8d3c\uff0c\u7ed9\u6211\u4e0a\u8fd9\u4e2a\u5927\u5f53\uff01\u201d \u3000\u3000\u80e1\u6590\u5fc3\u60f3\uff1a\u201c\u4ed6\u4e0e\u8c01\u52a8\u624b\uff1f\u6b64\u4eba\u662f\u7f6a\u9b41\u7978\u9996\uff0c\u8bf4\u4ec0\u4e48\u4e5f\u5f97\u5c06\u4ed6\u6293\u4f4f\u3002\u201d\u4ece\u540e\u95e8\u5954\u5c06\u51fa\u53bb\uff0c\u53ea\u89c1\u5218\u9e64\u771f\u6b63\u548c\u4e00\u4eba\u8fd1\u8eab\u7ea0\u7f20\uff0c\u8d64\u624b\u53ae\u6253\u3002\u77a7\u8fd9\u4eba\u8eab\u5f62\uff0c\u4fbf\u662f\u7eb5\u706b\u7684\u90a3\u4eba\u3002\u80e1\u6590\u5927\u662f\u5947\u602a\uff0c\u5fc3\u60f3\u4eca\u65e5\u4e4b\u4e8b\u5f53\u771f\u96be\u4ee5\u7d22\u89e3\uff0c\u8fd9\u4e24\u4eba\u660e\u660e\u662f\u4e00\u8def\uff0c\u600e\u4e48\u81ea\u76f8\u706b\u62da\u8d77\u6765\u4e86\uff1f\u53cd\u6b63\u4e24\u4e2a\u90fd\u4e0d\u662f\u597d\u4eba\uff0c\u5f53\u4e0b\u7eb5\u8eab\u800c\u524d\uff0c\u65bd\u5c55\u5927\u64d2\u62ff\u624b\uff0c\u4e00\u6293\u4e0b\u53bb\u4fbf\u64d2\u4f4f\u4e86\u4e24\u4eba\u540e\u5fc3\u8981\u7a74\uff0c\u4e24\u4eba\u6b63\u81ea\u6076\u6597\uff0c\u5206\u4e0d\u51fa\u624b\u76f8\u6297\uff0c\u5426\u5219\u4e8c\u4eba\u6b66\u529f\u90fd\u9887\u4e0d\u5f31\uff0c\u4e5f\u4e0d\u80fd\u7ed9\u4ed6\u4e00\u62ff\u4fbf\u5373\u5f97\u624b\u3002 \u3000\u3000\u80e1\u6590\u4fa7\u8033\u6ca1\u542c\u5230\u5927\u95e8\u5916\u6709\u76f8\u6597\u7684\u58f0\u97f3\uff0c\u751f\u6015\u82d7\u4eba\u51e4\u76ee\u5149\u4e0d\u4fbf\uff0c\u906d\u4e86\u953a\u6c0f\u5144\u5f1f\u7684\u6bd2\u624b\uff0c\u773c\u89c1\u8eab\u5934\u6709\u4e00\u53e3\u4e95\uff0c\u4e8e\u662f\u4e00\u624b\u4e00\u4e2a\uff0c\u5c06\u5218\u9e64\u771f\u548c\u90a3\u4eba\u90fd\u6295\u5165\u4e95\u4e2d\uff0c\u53c8\u5230\u53a8\u623f\u4e2d\u62b1\u51fa\u7b2c\u4e09\u53e3\u5927\u7f38\u538b\u5728\u4e95\u4e0a\uff0c\u8fd9\u624d\u7ed5\u8fc7\u5c4b\u5b50\uff0c\u5954\u5230\u524d\u95e8\u3002 \u3000\u3000\u4f46\u89c1\u953a\u6c0f\u5144\u5f1f\u5df2\u8dc3\u5728\u5730\u4e0b\uff0c\u4e0e\u82d7\u4eba\u51e4\u76f8\u9694\u4e03\u516b\u4e08\uff0c\u624b\u4e2d\u5404\u62ff\u7740\u4e00\u5bf9\u5224\u5b98\u7b14\uff0c\u5374\u4e0d\u6b3a\u8fd1\u52a8\u624b\u3001\u80e1\u6590\u9053\uff1a\u201c\u82d7\u5927\u4fa0\uff0c\u6211\u7ed9\u4f60\u62b1\u5b69\u5b50\u3002\u201d \u3000\u3000\u82d7\u4eba\u51e4\u6b63\u60f3\u81ea\u5df1\u53cc\u76ee\u5df2\u778e\uff0c\u7eb5\u7136\u9000\u5f97\u773c\u524d\u7684\u953a\u6c0f\u4e09\u5144\u5f1f\uff0c\u4f46\u7531\u4e8e\u201c\u6253\u904d\u5929\u4e0b\u65e0\u654c\u624b\u201d\u8fd9\u4e2a\u5916\u53f7\u592a\u6076\uff0c\u751f\u5e73\u7ed3\u4e0b\u4ec7\u5bb6\u65e0\u6570\uff0c\u53ea\u8981\u6c5f\u6e56\u4e0a\u4e00\u4f20\u5f00\u81ea\u5df1\u773c\u775b\u778e\u4e86\uff0c\u5f3a\u4ec7\u7eb7\u81f3\u6c93\u6765\uff0c\u90a3\u65f6\u5982\u4f55\u62b5\u5fa1\uff1f\u770b\u6765\u6027\u547d\u96be\u4ee5\u4fdd\u5168\uff0c\u6700\u653e\u5fc3\u4e0d\u4e0b\u7684\u4fbf\u662f\u8fd9\u4e2a\u5973\u513f\u3002 \u3000\u3000\u4ed6\u4ee5\u8033\u4ee3\u76ee\uff0c\u542c\u5f97\u80e1\u6590\u5374\u654c\u6551\u706b\uff0c\u5e72\u51c0\u5229\u843d\uff0c\u667a\u52c7\u517c\u5168\uff0c\u8fd9\u4eba\u7d20\u4e0d\u76f8\u8bc6\u3002\u5c45\u7136\u5982\u6b64\u4e49\u6c14\uff0c\u5973\u513f\u5b9e\u53ef\u6258\u4ed8\u7ed9\u4ed6\uff0c\u4e8e\u662f\u95ee\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4f60\u5c0a\u59d3\u5927\u540d\uff0c\u4e0e\u6211\u53ef\u6709\u6e0a\u6e90\uff1f\u201d \u3000\u3000\u80e1\u6590\u5fc3\u60f3\u6211\u7239\u7239\u4e0d\u77e5\u5230\u5e95\u662f\u4e0d\u662f\u6b7b\u5728\u4ed6\u7684\u624b\u4e0b\uff0c\u6b64\u523b\u4e0d\u4fbf\u63d0\u8d77\uff0c\u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u4e08\u592b\u7ed3\u4ea4\uff0c\u4f55\u91cd\u4e49\u6c14\uff0c\u53ea\u987b\u809d\u80c6\u76f8\u7167\uff0c\u4f55\u5fc5\u63d0\u540d\u9053\u59d3\uff1f\u82d7\u5927\u4fa0\u82e5\u662f\u4fe1\u6258\u5f97\u8fc7\uff0c\u5728\u4e0b\u4fbf\u662f\u7c89\u8eab\u788e\u9aa8\uff0c\u4e5f\u8981\u4fdd\u62a4\u4ee4\u7231\u5468\u5168\u3002\u201d \u3000\u3000\u82d7\u4eba\u51e4\u9053\uff1a\u201c\u597d\uff0c\u82d7\u4eba\u51e4\u72ec\u6765\u72ec\u5f80\uff0c\u751f\u5e73\u53ea\u6709\u4e24\u4e2a\u77e5\u4ea4\uff0c\u4e00\u4e2a\u662f\u8fbd\u4e1c\u5927\u4fa0\u80e1\u4e00\u5200\uff0c\u53e6\u4e00\u4e2a\u4fbf\u662f\u4f60\u8fd9\u4f4d\u4e0d\u77e5\u59d3\u540d\u3001\u6ca1\u89c1\u8fc7\u9762\u7684\u5c0f\u5144\u5f1f\u3002\u201d\u8bf4\u7740\u62b1\u8d77\u5973\u513f\uff0c\u9012\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u80e1\u6590\u867d\u4e0e\u4ed6\u4e00\u89c1\u5fc3\u6298\uff0c\u4f46\u552f\u6050\u4ed6\u662f\u6740\u7236\u4ec7\u4eba\uff0c\u6069\u4ec7\u4e4b\u9645\uff0c\u5b9e\u6240\u96be\u5904\uff0c\u5f85\u542c\u4ed6\u8bf4\u81ea\u5df1\u7236\u4eb2\u662f\u4ed6\u751f\u5e73\u77e5\u4ea4\uff0c\u5fc3\u5934\u4e00\u559c\uff0c\u53cc\u624b\u63a5\u8fc7\u5973\u5b69\uff0c\u53ea\u89c1\u5979\u7ea6\u83ab\u516d\u4e03\u5c81\u5e74\u7eaa\uff0c\u4f46\u751f\u5f97\u751a\u662f\u5a07\u5c0f\uff0c\u62b1\u5728\u624b\u91cc\uff0c\u53c8\u8f7b\u53c8\u8f6f\uff0c\u6de1\u6de1\u661f\u5149\u4e4b\u4e0b\u89c1\u5979\u5408\u773c\u7761\u7740\uff0c\u547c\u5438\u4f4e\u5fae\uff0c\u5634\u89d2\u8fb9\u9732\u7740\u4e00\u4e1d\u5fae\u7b11\u3002 \u3000\u3000\u953a\u6c0f\u4e09\u96c4\u89c1\u80e1\u6590\u4e5f\u5728\u6b64\u5904\uff0c\u53c8\u4e0e\u82d7\u4eba\u51e4\u5982\u6b64\u5bf9\u7b54\uff0c\u5fc3\u4e2d\u90fd\u611f\u5947\u602a\u3002 \u3000\u3000\u82d7\u4eba\u51e4\u6495\u4e0b\u4e00\u5757\u8863\u895f\uff0c\u5305\u5728\u773c\u4e0a\uff0c\u53cc\u624b\u8d1f\u5728\u80cc\u540e\uff0c\u4f4e\u6c89\u7740\u55d3\u5b50\u9053\uff1a\u201c\u65e0\u803b\u5978\u8d3c\uff0c\u4e00\u9f50\u4e0a\u5427\u3002\u6211\u5973\u513f\u7761\u7740\u4e86\uff0c\u53ef\u83ab\u5927\u58f0\u5435\u9192\u4e86\u5979\u3002\u201d \u3000\u3000\u953a\u5146\u82f1\u8e0f\u4e0a\u4e00\u6b65\uff0c\u6012\u9053\uff1a\u201c\u82d7\u5927\u4fa0\uff0c\u5f53\u5e74\u6211\u5f92\u513f\u6b7b\u5728\u4f60\u624b\u4e0b\uff0c\u6211\u5144\u5f1f\u6765\u8ddf\u4f60\u7b97\u5e10\uff0c\u540e\u6765\u5f97\u77e5\u6211\u5f92\u513f\u89ca\u89ce\u522b\u4eba\u5229\u5668\uff0c\u884c\u6b62\u4e0d\u7aef\uff0c\u6b7b\u6709\u5e94\u5f97\uff0c\u8fd9\u4e8b\u8fd8\u5f97\u591a\u8c22\u4f60\u52a9\u6211\u6e05\u7406\u95e8\u6237\u3002\u201d\u82d7\u4eba\u51e4\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8bf4\u8bdd\u5c0f\u58f0\u4e9b\uff0c\u6211\u542c\u5f97\u89c1\u3002\u201d \u3000\u3000\u953a\u5146\u82f1\u6012\u6c14\u66f4\u589e\uff0c\u5927\u58f0\u9053\uff1a\u201c\u53ea\u662f\u90a3\u65f6\u4f60\u817f\u4e0a\u53d7\u4f24\uff0c\u6211\u4e09\u5144\u5f1f\u4ecd\u975e\u654c\u624b\uff0c\u5fc3\u4e2d\u4e0d\u670d\uff0c\u82e6\u7ec3\u4e86\u516b\u5e74\u6b66\u529f\u4e4b\u540e\uff0c\u4eca\u65e5\u518d\u8981\u6765\u8ba8\u6559\u3002\u5728\u9014\u4e2d\u5f97\u6089\u6709\u5978\u4eba\u8981\u5bf9\u4f60\u6697\u7b97\uff0c\u6211\u5144\u5f1f\u517c\u7a0b\u8d76\u6765\uff0c\u8981\u8bf7\u4f60\u63d0\u9632\u3002\u773c\u4e0b\u5978\u4eba\u5df2\u53bb\uff0c\u4f60\u80af\u4e0d\u80af\u8d50\u6559\uff0c\u4f46\u51ed\u4e8e\u4f60\uff0c\u4f55\u4ee5\u53e3\u51fa\u6076\u8a00\uff1f\u53c8\u4f55\u4ee5\u81ea\u7f1a\u53cc\u773c\uff0c\u96be\u9053\u6211\u953a\u6c0f\u4e09\u96c4\u5982\u6b64\u4e0d\u8096\uff0c\u4f60\u8fde\u4e00\u773c\u90fd\u4e0d\u5c51\u770b\u4e48\uff1f\u8fd8\u662f\u4f60\u81ea\u4ee5\u4e3a\u6b66\u529f\u7cbe\u7edd\uff0c\u95ed\u7740\u773c\u775b\u4e5f\u80fd\u6253\u8d25\u6211\u4e09\u5144\u5f1f\uff1f\u201d \u3000\u3000\u82d7\u4eba\u51e4\u542c\u4ed6\u8bed\u6c14\uff0c\u4f3c\u4e4e\u81ea\u5df1\u53cc\u76ee\u4e2d\u6bd2\u4e4b\u4e8b\uff0c\u4ed6\u5e76\u4e0d\u77e5\u60c5\uff0c\u6c89\u7740\u55d3\u5b50\u9053\uff1a\u201c\u6211\u773c\u775b\u778e\u4e86\uff01\u201d \u3000\u3000\u953a\u5146\u82f1\u5927\u60ca\uff0c\u98a4\u58f0\u9053\uff1a\u201c\u554a\u5537\uff0c\u8fd9\u53ef\u9519\u602a\u4e86\u4f60\u82d7\u5927\u4fa0\uff0c\u6211\u5144\u5f1f\u82e6\u7ec3\u516b\u5e74\uff0c\u6b66\u529f\u4e5f\u6ca1\u4ec0\u4e48\u957f\u8fdb\uff0c\u8ddf\u4f60\u8ba8\u6559\u4e4b\u4e8b\uff0c\u90a3\u4e0d\u7528\u63d0\u4e86\u3002\u4f60\u53ef\u77e5\u97e6\u9640\u95e8\u6709\u4e2a\u540d\u53eb\u5218\u9e64\u771f\u4e4b\u4eba\u5417\uff01\u9002\u624d\u4f60\u6253\u8d70\u7684\u4eba\u4e2d\uff0c\u5e76\u6ca1\u4ed6\u5728\u5185\u3002\u6b64\u4eba\u4e00\u4e24\u65e5\u5185\uff0c\u5b9a\u4f1a\u6765\u8bbf\u3002\u82d7\u5927\u4fa0\u4f60\u773c\u775b\u4e0d\u4fbf\uff0c\u6b64\u4eba\u6765\u65f6\uff0c\u52a1\u987b\u5c0f\u5fc3\u5728\u610f\u3002\u201d \u3000\u3000\u80e1\u6590\u63d2\u53e3\u8bf4\u9053\uff1a\u201c\u953a\u5927\u7237\uff0c\u90a3\u5218\u9e64\u771f\u4e0b\u6bd2\u4e4b\u4e8b\uff0c\u4f60\u5f53\u771f\u4e0d\u77e5\u60c5\u4e48\uff1f\u201d\u953a\u5146\u82f1\u9053\uff1a\u201c\u4f60\u8ddf\u82d7\u5927\u4fa0\u5230\u5e95\u662f\u53cb\u662f\u654c\uff1f\u54b1\u4eec\u8981\u963b\u622a\u90a3\u5218\u9e64\u771f\uff0c\u4f60\u4f55\u4ee5\u53cd\u800c\u6781\u529b\u52a9\u4ed6\uff1f\u201d\u80e1\u6590\u9053\uff1a\u201c\u6b64\u4e8b\u8bf4\u6765\u60ed\u6127\uff0c\u5176\u4e2d\u539f\u59d4\u66f2\u6298\uff0c\u5c0f\u5f1f\u4e5f\u5f04\u4e0d\u660e\u767d\u3002\u597d\u5728\u90a3\u5218\u9e64\u771f\u5df2\u7ed9\u5c0f\u5f1f\u64d2\u4f4f\uff0c\u538b\u5728\u540e\u9762\u4e95\u4e2d\u3002\u54b1\u4eec\u4e00\u95ee\u4fbf\u77e5\u7aef\u7684\u3002\u201d\u8f6c\u5934\u95ee\u82d7\u4eba\u51e4\u9053\uff1a\u201c\u953a\u6c0f\u4e09\u5144\u5f1f\u5230\u5e95\u662f\u597d\u4eba\uff0c\u8fd8\u662f\u574f\u4eba\uff1f\u201d \u3000\u3000\u953a\u5146\u6587\u51b7\u51b7\u5730\u9053\uff1a\u201c\u6211\u4eec\u65e2\u4e0d\u884c\u4fa0\u4ed7\u4e49\uff0c\u53c8\u4e0d\u6d4e\u8d2b\u52a9\u5b64\uff0c\u7b97\u4ec0\u4e48\u597d\u4eba\uff1f\u201d\u82d7\u4eba\u51e4\u9053\uff1a\u201c\u953a\u6c0f\u4e09\u96c4\u5e76\u975e\u5351\u9119\u5c0f\u4eba\u3002\u201d\u4e09\u5144\u5f1f\u542c\u4e86\u82d7\u4eba\u51e4\u8fd9\u53e5\u54c1\u8bc4\uff0c\u5fc3\u4e2d\u5927\u559c\uff0c\u5f53\u771f\u662f\u4e00\u8a00\u4e4b\u8912\uff0c\u8363\u4e8e\u534e\u886e\u3002\u4e09\u5f20\u4e11\u8138\u90fd\u662f\u663e\u5f97\u53c8\u559c\u6b22\u53c8\u611f\u6fc0\u3002 \u3000\u3000\u5146\u6587\u3001\u5146\u80fd\u5144\u5f1f\u4fe9\u7ed5\u5230\u5c4b\u540e\uff0c\u62ac\u5f00\u4e95\u4e0a\u7684\u6c34\u7f38\uff0c\u559d\u9053\uff1a\u201c\u8df3\u4e0a\u6765\u5427\uff01\u201d\u53ea\u542c\u5f97\u4e95\u4e2d\u54fc\u54fc\u5527\u5527\uff0c\u7adf\u6709\u4e24\u4e2a\u4eba\u7684\u58f0\u97f3\uff0c\u7830\u7684\u4e00\u54cd\uff0c\u53c8\u662f\u62cd\u7684\u4e00\u58f0\uff0c\u8fd8\u5939\u7740\u7a00\u91cc\u54d7\u5566\u7684\u6c34\u58f0\uff0c\u90a3\u4e24\u4eba\u4f3c\u4e4e\u6b63\u5728\u62da\u547d\u76f8\u6597\u3002\u5728\u8fd9\u4e95\u4e2d\u4e00\u4e2a\u4eba\u8f6c\u6298\u90fd\u662f\u4e0d\u4fbf\uff0c\u4e24\u4eba\u7adf\u6324\u7740\u4e92\u6bb4\uff0c\u72fc\u72c8\u4e4b\u60c5\uff0c\u53ef\u60f3\u800c\u77e5\u3002\u953a\u5146\u6587\u5c06\u4e95\u8fb9\u7684\u540a\u6876\u5782\u4e86\u4e0b\u53bb\uff0c\u559d\u9053\uff1a\u201c\u6293\u4f4f\u540a\u6876\u3002\u6211\u540a\u4f60\u4eec\u4e0a\u6765\u3002\u201d\u89c9\u5f97\u7ef3\u4e0a\u4e00\u7d27\uff0c\u4e0b\u9762\u5df2\u7ecf\u6293\u4f4f\uff0c\u4e8e\u662f\u4f7f\u52b2\u6536\u7ef3\uff0c\u679c\u7136\u6e7f\u6dcb\u6dcb\u7684\u540a\u8d77\u4e24\u4eba\u3002 \u3000\u3000\u5218\u9e64\u771f\u811a\u672a\u7740\u5730\uff0c\u4e00\u638c\u4fbf\u5411\u53e6\u4e00\u4eba\u62cd\u4e86\u8fc7\u53bb\u3002\u90a3\u4eba\u6b66\u529f\u4e0d\u53ca\u4ed6\uff0c\u5728\u4e95\u4e2d\u5df2\u5403\u4e86\u4e0d\u5c11\u82e6\u5934\uff0c\u7ed9\u4ed6\u6309\u7740\u559d\u9971\u4e86\u6c34\uff0c\u5df2\u7136\u660f\u660f\u6c89\u6c89\u3002\u953a\u5146\u6587\u773c\u89c1\u8fd9\u4e00\u638c\u80fd\u81f4\u4ed6\u6b7b\u547d\uff0c\u5fd9\u4f38\u624b\u683c\u5f00\u3002\u953a\u5146\u80fd\u4e00\u5bf9\u5224\u5b98\u7b14\u5206\u70b9\u4e24\u4eba\u540e\u5fc3\uff0c\u559d\u9053\uff1a\u201c\u8981\u547d\u7684\u4fbf\u4e0d\u8bb8\u52a8\u3002\u201d\u5144\u5f1f\u4fe9\u5c06\u4e24\u4eba\u6293\u5230\u5c4b\u4e2d\u3002 \u3000\u3000\u8fd9\u65f6\u80e1\u6590\u5df2\u5c06\u90a3\u5973\u5b69\u4ea4\u56de\u7ed9\u82d7\u4eba\u51e4\uff0c\u70b9\u4eae\u4e86\u70db\u53f0\u3002\u5367\u5ba4\u4e2d\u70e7\u5f97\u4e00\u584c\u80e1\u6d82\uff0c\u6ee1\u5730\u662f\u6c34\uff0c\u7adf\u65e0\u7acb\u8db3\u4e4b\u5904\u3002\u82d7\u4eba\u51e4\u5c06\u5973\u513f\u653e\u5728\u53a2\u623f\u4e2d\u81ea\u5df1\u5e8a\u4e0a\uff0c\u56de\u8eab\u51fa\u6765\u65f6\uff0c\u953a\u6c0f\u5144\u5f1f\u5df2\u5c06\u5218\u9e64\u771f\u548c\u53e6\u4e00\u4eba\u6293\u5230\u3002 \u3000\u3000\u82d7\u4eba\u51e4\u8f7b\u8f7b\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u2018\u97e6\u9640\u53cc\u9e64\u2019\u7684\u540d\u5934\uff0c\u6211\u4e8c\u5341\u591a\u5e74\u524d\u4fbf\u5df2\u542c\u5230\u8fc7\u3002\u5218\u5e08\u5144\u548c\u4e07\u5e08\u5144\u4e24\u4f4d\uff0c\u6c5f\u6e56\u4e0a\u7684\u58f0\u540d\u5e76\u4e0d\u7b97\u574f\u554a\u3002\u201d\u5218\u9e64\u771f\u9053\uff1a\u201c\u82d7\u5927\u4fa0\uff0c\u6211\u4e0a\u4e86\u5978\u4eba\u7684\u5f53\uff0c\u8ffd\u6094\u83ab\u53ca\u3002\u4f60\u773c\u775b\u7684\u4f24\u91cd\u4e48\uff1f\u201d\u953a\u6c0f\u4e09\u5144\u5f1f\u4e00\u9f50\u201c\u554a\u201d\u7684\u4e00\u58f0\u3002 \u3000\u3000\u4ed6\u4eec\u4e0d\u77e5\u82d7\u4eba\u51e4\u773c\u775b\u53d7\u4f24\uff0c\u539f\u6765\u8fd8\u53ea\u9002\u624d\u4e4b\u4e8b\u3002 \u3000\u3000\u82d7\u4eba\u51e4\u4e0d\u7b54\uff0c\u5411\u90a3\u4f7f\u5200\u4e4b\u4eba\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u7530\u5f52\u519c\u7684\u5f1f\u5b50\u5427\uff1f \u3000\u3000\u5929\u9f99\u95e8\u7684\u6b66\u529f\u4e5f\u5b66\u5230\u4e03\u6210\u706b\u5019\u4e86\u3002\u201d\u90a3\u4eba\u5413\u5f97\u9b42\u4e0d\u9644\u4f53\uff0c\u7a81\u7136\u53cc\u819d\u8dea\u5012\uff0c\u8fde\u8fde\u53e9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u82d7\u5927\u4fa0\uff0c\u5c0f\u4eba\u662f\u53d7\u547d\u5dee\u9063\uff0c\u6982\u4e0d\u7531\u5df1\uff0c\u8bf7\u4f60\u8001\u4eba\u5bb6\u9ad8\u62ac\u8d35\u624b\u3002\u201d\u731b\u5730\u91cc\u201c\u54c7\u3001\u54c7\u201d\u4e24\u58f0\uff0c\u5410\u51fa\u51e0\u53e3\u6c34\u6765\u3002 \u3000\u3000\u5218\u9e64\u771f\u9a82\u9053\uff1a\u201c\u5978\u8d3c\uff0c\u4f60\u9a97\u5f97\u6211\u597d\u82e6\uff01\u201d\u6251\u4e0a\u53bb\u53c8\u8981\u52a8\u624b\u3002 \u3000\u3000\u953a\u5146\u82f1\u4f38\u624b\u4e00\u62e6\uff0c\u9053\uff1a\u201c\u6709\u8bdd\u597d\u597d\u8bf4\uff0c\u5230\u5e95\u662f\u600e\u5730\uff1f\u201d \u3000\u3000\u5218\u9e64\u771f\u4e5f\u662f\u6b66\u6797\u4e2d\u7684\u6210\u540d\u4eba\u7269\uff0c\u53ea\u56e0\u4e0a\u4e86\u522b\u4eba\u7684\u5927\u5f53\uff0c\u8fd9\u624d\u6c14\u6025\u8d25\u574f\uff0c\u96be\u4ee5\u81ea\u5236\uff0c\u7ed9\u953a\u5146\u82f1\u8fd9\u4e48\u4e00\u62e6\uff0c\u60f3\u8d77\u81ea\u5df1\u65e2\u505a\u4e86\u9519\u4e8b\uff0c\u53c8\u7ed9\u4eba\u629b\u5728\u4e95\u91cc\uff0c\u5f04\u5f97\u5982\u6b64\u72fc\u72c8\uff0c\u5b9e\u662f\u751f\u5e73\u7684\u5947\u803b\u5927\u8fb1\uff0c\u773c\u524d\u4e00\u9ed1\uff0c\u9893\u7136\u5750\u5012\u5728\u5730\uff0c\u8bf4\u9053\uff1a\u201c\u7f62\u4e86\uff0c\u7f62\u4e86\uff01\u82d7\u5927\u4fa0\uff0c\u771f\u6b63\u5bf9\u4f60\u4e0d\u4f4f\u3002\u201d \u3000\u3000\u82d7\u4eba\u51e4\u9053\uff1a\u201c\u4e00\u4e2a\u4eba\u4e00\u751f\u4e4b\u4e2d\uff0c\u4e0d\u514d\u8981\u53d7\u5c0f\u4eba\u7684\u6b3a\u9a97\uff0c\u90a3\u53c8\u7b97\u5f97\u4e86\u4ec0\u4e48\uff1f\u5b9a\u662f\u8fd9\u4eba\u9a97\u4f60\u6765\u9001\u4fe1\u7ed9\u6211\u4e86\u3002\u201d\u4ed6\u53cc\u76ee\u4e2d\u6bd2\uff0c\u663e\u5df2\u778e\u4e86\uff0c\u8bf4\u8bdd\u5374\u4ecd\u662f\u5982\u6b64\u8f7b\u63cf\u6de1\u5199\uff0c\u80e1\u6590\u548c\u953a\u6c0f\u5144\u5f1f\u7b49\u90fd\u597d\u751f\u4f69\u670d\uff0c\u5747\u60f3\u5982\u6b64\u5b9a\u529b\uff0c\u4eba\u6240\u96be\u53ca\u3002 \u3000\u3000\u5218\u9e64\u771f\u9053\uff1a\u201c\u8fd9\u4eba\u6211\u662f\u5728\u8861\u9633\u67ab\u53f6\u5e84\u4e0a\u8bc6\u5f97\u7684\u3002\u4ed6\u81ea\u79f0\u540d\u53eb\u5f20\u98de\u96c4\uff0c\u8bf4\u4ee5\u524d\u53d7\u8fc7\u4e07\u5e08\u5f1f\u7684\u6069\u60e0\uff0c\u5f97\u77e5\u4e07\u5e08\u5f1f\u7684\u6b7b\u8baf\u540e\u5341\u5206\u96be\u8fc7\uff0c\u8d76\u6765\u540a\u4e27\u3002\u201d\u82d7\u4eba\u51e4\u9053\uff1a\u201c\u4e07\u9e64\u58f0\u8001\u5e08\u6b7b\u4e86\uff1f\u201d\u5218\u9e64\u771f\u9053\uff1a\u201c\u662f\u554a\u3002\u6211\u89c1\u8fd9\u59d3\u5f20\u7684\u8bf4\u8bdd\u8bda\u6073\uff0c\u4ed6\u53c8\u7740\u610f\u548c\u6211\u7ed3\u7eb3\uff0c\u4e5f\u5c31\u6ca1\u8d77\u7591\u5fc3\uff0c\u4e24\u4eba\u7ed3\u4f34\u5317\u4e0a\u3002\u4ed6\u5728\u9014\u4e2d\u89c1\u5230\u953a\u6c0f\u4e09\u96c4\uff0c\u663e\u5f97\u5f88\u662f\u5bb3\u6015\uff0c\u5f53\u665a\u5728\u5ba2\u5e97\u4e2d\u6211\u548c\u4ed6\u540c\u5ba4\u800c\u7761\uff0c\u542c\u5f97\u4ed6\u8bf4\u8d77\u68a6\u8bdd\u6765\uff0c\u8bf4\u4ec0\u4e48\u8fd9\u5c01\u4fe1\u82e5\u4e0d\u9001\u5230\uff0c\u4fbf\u5bb3\u4e86\u65e0\u6570\u4ec1\u4eba\u4e49\u58eb\u7684\u6027\u547d\u3002 \u3000\u3000\u6211\u60f3\u6b64\u4e8b\u4e0d\u80fd\u8896\u624b\u65c1\u89c2\uff0c\u4fbf\u7528\u8a00\u8bed\u63a2\u95ee\u3002\u4ed6\u8bf4\uff1a\u2018\u5218\u8001\u5e08\uff0c\u6211\u89c1\u4f60\u8ddf\u671d\u5ef7\u7684\u4f8d\u536b\u4e3a\u96be\uff0c\u5927\u662f\u82f1\u96c4\u8c6a\u6770\uff0c\u8fd9\u8bdd\u4e5f\u4e0d\u7528\u7792\u4f60\u3002\u2019\u4e8e\u662f\u53d6\u51fa\u4e00\u5c01\u4fe1\u6765\uff0c\u8bf4\u5fc5\u987b\u9001\u5230\u91d1\u9762\u4f5b\u82d7\u5927\u4fa0\u624b\u4e2d\uff0c\u8bf7\u4ed6\u51fa\u624b\u76f8\u6551\uff0c\u5426\u5219\u6709\u51e0\u5341\u4f4d\u4e49\u58eb\u8981\u7ed9\u671d\u5ef7\u5bb3\u6b7b\u3002\u201d \u3000\u3000\u82d7\u4eba\u51e4\u4e0d\u7f6e\u4e00\u8bcd\u3002\u5218\u9e64\u771f\u7eed\u9053\uff1a\u201c\u8fd9\u59d3\u5f20\u7684\u5978\u8d3c\u53c8\u8bf4\uff0c\u953a\u6c0f\u4e09\u96c4\u4e0e\u82d7\u5927\u4fa0\u6709\u4ec7\uff0c\u5b9a\u8981\u8bbe\u6cd5\u622a\u963b\u3002\u4ed6\u4e0d\u662f\u953a\u6c0f\u4e09\u96c4\u7684\u654c\u624b\uff1a\u8bf7\u6211\u76f8\u52a9\u4e00\u81c2\u4e4b\u529b\u3002\u6211\u60f3\u8fd9\u4ef6\u4e8b\u4e49\u4e0d\u5bb9\u8f9e\uff0c\u5f53\u4e0b\u4e00\u529b\u627f\u5f53\u3002\u4f46\u9014\u4e2d\u548c\u953a\u6c0f\u4e09\u96c4\u4e00\u4ea4\u624b\uff0c\u6211\u8fd9\u8001\u513f\u8fd8\u662f\u683d\u4e86\u7b4b\u6597\u3002\u540e\u6765\u5185\u4eba\u738b\u6c0f\u8d76\u5230\u76f8\u52a9\uff0c\u4ecd\u662f\u4e0d\u654c\u3002\u4e5f\u662f\u4e8b\u5f53\u51d1\u5de7\uff0c\u5728\u6e58\u5983\u5e99\u4e2d\u9047\u4e0a\u4e86\u8fd9\u4f4d\u5c0f\u5144\u5f1f\u3002\u6211\u5728\u67ab\u53f6\u5e84\u4e0a\u66fe\u5f97\u4ed6\u4e4b\u52a9\uff0c\u540e\u6765\u53c8\u89c1\u4ed6\u8fde\u663e\u8eab\u624b\uff0c\u6b66\u529f\u5b9e\u5728\u9ad8\u5f3a\uff0c\u4e8e\u662f\u6211\u592b\u5987\u5047\u88c5\u53d7\u4f24\uff0c\u5b89\u6392\u673a\u5173\uff0c\u8bf7\u4ed6\u963b\u6321\u953a\u6c0f\u4e09\u96c4\uff0c\u8fd9\u4f4d\u5c0f\u5144\u5f1f\u679c\u7136\u4e0a\u4e86\u6211\u7684\u5f53\uff0c\u6211\u5374\u53c8\u4e0a\u4e86\u8fd9\u5978\u8d3c\u7684\u5f53\u3002\u201d\u8bf4\u7740\u5706\u7741\u53cc\u76ee\uff0c\u9aed\u987b\u7fd8\u52a8\uff0c\u6c14\u6124\u96be\u5e73\u3002 \u3000\u3000\u80e1\u6590\u9ed8\u60f3\u7ecf\u8fc7\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u4eba\u7684\u8bdd\u5012\u4f3c\u4e0d\u5047\uff0c\u539f\u6765\u6211\u548c\u8881\u59d1\u5a18\u4e00\u8def\u4e0a\u4e4b\u4e8b\uff0c\u6709\u8bb8\u591a\u90fd\u7ed9\u4ed6\u77a7\u89c1\u4e86\u3002\u201d\u60f3\u5230\u6b64\u5904\uff0c\u8138\u4e0a\u5fae\u5fae\u4e00\u70ed\uff0c\u77a5\u773c\u89c1\u5230\u684c\u4e0a\u653e\u7740\u7684\u4e09\u4ef6\u5175\u5203\uff0c\u95ee\u9053\uff1a\u201c\u90a3\u4f60\u62ff\u4e86\u953a\u6c0f\u4e09\u96c4\u7684\u5175\u5203\uff0c\u53c8\u6765\u5e72\u4e48\uff1f\u201d \u3000\u3000\u5218\u9e64\u771f\u9053\uff1a\u201c\u953a\u6c0f\u4e09\u96c4\u524d\u6765\u5bfb\u4ec7\uff0c\u82d7\u5927\u4fa0\u672a\u5fc5\u77e5\u9053\u3002\u6211\u5148\u884c\u7ed9\u4ed6\u62a5\u4e2a\u8baf\u606f\uff0c\u6559\u4ed6\u597d\u6709\u6240\u9632\u5907\u3002\u9001\u8fd9\u5175\u5203\u524d\u6765\uff0c\u662f\u53d6\u4fe1\u7684\u610f\u601d\u3002\u81f3\u4e8e\u6211\u8bf4\u8fd9\u4fe1\u662f\u953a\u6c0f\u5144\u5f1f\u9001\u6765\uff0c\u90a3\u662f\u8bf4\u7ed9\u4f60\u5c0f\u5144\u5f1f\u542c\u7684\u3002\u6211\u77e5\u4f60\u7d27\u7d27\u8ddf\u968f\u5728\u540e\uff0c\u6015\u4f60\u4e0d\u5229\u4e8e\u6211\uff0c\u8fd9\u4e48\u4e00\u8bf4\uff0c\u76fc\u4f60\u5fc3\u4e2d\u7591\u60d1\u96be\u660e\uff0c\u4fbf\u4e0d\u4f1a\u8d38\u7136\u52a8\u624b\uff0c\u53cd\u6b63\u82d7\u5927\u4fa0\u4e00\u770b\u4fe1\u4fbf\u77e5\u7aef\u7684\uff0c\u5c82\u77e5\uff0c\u5c82\u77e5\u2026\u2026\u201d\u80f8\u53e3\u6c14\u585e\uff0c\u518d\u4e5f\u8bf4\u4e0d\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u953a\u5146\u82f1\u9053\uff1a\u201c\u6211\u5144\u5f1f\u65e0\u610f\u4e4b\u4e2d\uff0c\u542c\u5230\u4e86\u8fd9\u59d3\u5f20\u7684\u5978\u8c0b\uff0c\u53c8\u89c1\u5218\u8001\u5e08\u8ddf\u4ed6\u9b3c\u9b3c\u5d07\u5d07\uff0c\u5b9a\u662f\u8981\u6765\u6697\u7b97\u82d7\u5927\u4fa0\uff0c\u662f\u4ee5\u5168\u529b\u963b\u622a\uff0c\u60f3\u4e0d\u5230\u4e2d\u95f4\u5c1a\u6709\u8fd9\u8bb8\u591a\u8fc7\u8282\u3002\u82d7\u4eba\u4fa0\uff0c\u4f60\u773c\u775b\u600e\u4e48\u53d7\u7684\u4f24\uff1f\u201d \u3000\u3000\u82d7\u4eba\u51e4\u4e0d\u7b54\uff0c\u5c06\u84b2\u6247\u822c\u7684\u5927\u624b\u6325\u4e86\u6325\uff0c\u9053\uff1a\u201c\u8fc7\u53bb\u4e4b\u4e8b\uff0c\u90a3\u4e5f\u4e0d\u7528\u63d0\u4e86\u3002\u201d \u3000\u3000\u80e1\u6590\u773c\u5149\u56db\u4e0b\u626b\u52a8\uff0c\u8981\u627e\u4ed6\u6495\u7834\u7684\u4fe1\u7b3a\uff0c\u679c\u89c1\u4e24\u7247\u7834\u7eb8\u5c1a\u5728\u5c4b\u89d2\u843d\u4e2d\uff0c\u6709\u4e00\u534a\u5df2\u88ab\u6d78\u6e7f\u3002\u4ed6\u6015\u7eb8\u4e0a\u5c1a\u6709\u5267\u6bd2\uff0c\u4e0d\u6562\u8d70\u8fd1\uff0c\u653e\u773c\u671b\u53bb\uff0c\u89c1\u7eb8\u4e0a\u53ea\u6709\u5be5\u5be5\u4e09\u884c\u5b57\uff0c\u6bcf\u4e2a\u5b57\u90fd\u6709\u6838\u6843\u5927\u5c0f\u3002\u4ed6\u773c\u5149\u5728\u4e24\u7247\u7834\u7eb8\u4e0a\u626b\u6765\u626b\u53bb\uff0c\u89c1\u90a3\u4fe1\u5199\u9053\uff1a\u201c\u4eba\u51e4\u6211\u5144\uff1a\u4ee4\u7231\u8d44\u8d28\u5a07\u8d35\u3002\u6211\u5144\u4e00\u4ecb\u6b66\u592b\uff0c\u76f8\u5904\u751a\u4e0d\u5408\u5b9c\uff0c\u6709\u8bef\u4ee4\u7231\u6559\u517b\u3002\u5179\u547d\u4eba\u76f8\u8fce\uff0c\u7531\u5f1f\u629a\u517b\u53ef\u4e5f\u3002\u5f1f\u7530\u5f52\u519c\u987f\u9996\u3002\u201d \u3000\u3000\u60f3\u82d7\u4eba\u51e4\u5bf9\u8fd9\u5973\u513f\u7231\u903e\u6027\u547d\uff0c\u7530\u5f52\u519c\u62d0\u8bf1\u4e86\u4ed6\u59bb\u5b50\u79c1\u5954\uff0c\u8fd9\u65f6\u7adf\u7136\u8fde\u5973\u513f\u4e5f\u60f3\u8981\u4e86\u53bb\uff0c\u53eb\u4ed6\u5982\u4f55\u4e0d\u6012\uff1f\u81ea\u7136\u987a\u624b\u6495\u4fe1\uff0c\u6bd2\u836f\u6697\u85cf\u5728\u4fe1\u7b3a\u7684\u5939\u5c42\u4e4b\u4e2d\uff0c\u4fe1\u7b3a\u4e00\u7834\uff0c\u7acb\u65f6\u98de\u626c\uff0c\u518d\u5feb\u7684\u8eab\u624b\u4e5f\u662f\u8eb2\u95ea\u4e0d\u4e86\u3002\u7530\u5f52\u519c\u8fd9\u4e00\u6761\u8ba1\u7b56\uff0c\u4e5f\u53ef\u7b97\u5f97\u5389\u5bb3\u4e4b\u6781\u4e86\u3002\u80e1\u6590\u56de\u60f3\u6614\u5e74\u5728\u5546\u5bb6\u5821\u4e2d\u6240\u89c1\u82d7\u4eba\u51e4\u3001\u82d7\u592b\u4eba\u3001\u82d7\u5bb6\u5c0f\u5973\u5b69\u4ee5\u53ca\u7530\u5f52\u519c\u56db\u4eba\u4e4b\u95f4\u7684\u60c5\u72b6\uff0c\u6068\u4e0d\u5f97\u7acb\u65f6\u53bb\u627e\u5230\u7530\u5f52\u519c\uff0c\u5c06\u4ed6\u4e00\u5200\u6740\u4e86\u3002 \u3000\u3000\u5218\u9e64\u771f\u8d8a\u60f3\u8d8a\u6c14\uff0c\u559d\u9053\uff1a\u201c\u59d3\u5f20\u7684\uff0c\u4f60\u4fbf\u662f\u5949\u4e86\u5e08\u547d\uff0c\u8981\u6697\u7b97\u82d7\u5927\u4fa0\uff0c\u81ea\u5df1\u9001\u4fe1\u6765\u4fbf\u662f\u4e86\uff0c\u4f55\u4ee5\u504f\u504f\u77a7\u4e0a\u4e86\u6211\u59d3\u5218\u7684\uff1f\u201d \u3000\u3000\u5f20\u98de\u96c4\u55eb\u5685\u9053\uff1a\u201c\u6211\u6015\u2026\u2026\u6015\u82d7\u5927\u4fa0\u77a7\u7834\u6211\u662f\u5929\u9f99\u95e8\u5f1f\u5b50\uff0c\u6709\u4e86\u63d0\u9632\u2026\u2026\u53c8\u5bb3\u6015\u2026\u2026\u5bb3\u6015\u82d7\u5927\u4fa0\u7684\u795e\u5a01\u2026\u2026\u201d\u5218\u9e64\u771f\u6068\u6068\u5730\u9053\uff1a\u201c\u4f60\u6015\u4e07\u4e00\u5978\u8ba1\u8d25\u9732\uff0c\u9003\u8d70\u4e0d\u53ca\u3002\u597d\u5c0f\u5b50\uff0c\u597d\u5c0f\u5b50\uff01\u201d\u4ed6\u8f6c\u5934\u5411\u82d7\u4eba\u51e4\u9053\uff1a\u201c\u82d7\u5927\u4fa0\uff0c\u6211\u5411\u4f60\u8ba8\u4e2a\u60c5\uff0c\u8fd9\u5c0f\u5b50\u4ea4\u7ed9\u6211\uff01\u201d \u3000\u3000\u82d7\u4eba\u51e4\u7f13\u7f13\u5730\u9053\uff1a\u201c\u5218\u8001\u5e08\uff0c\u8fd9\u79cd\u5c0f\u4eba\uff0c\u4e5f\u72af\u4e0d\u7740\u8ddf\u4ed6\u8ba1\u8f83\u3002\u5f20\u98de\u96c4\uff0c\u8fd9\u9662\u5b50\u4e2d\u8fd8\u6709\u4f60\u7684\u4e24\u4e2a\u540c\u4f34\uff0c\u53d7\u4f24\u90fd\u4e0d\u7b97\u8f7b\uff0c\u4f60\u5e26\u4e86\u4ed6\u4eec\u8d70\u5427\uff0c\u4f60\u53bb\u8ddf\u4f60\u5e08\u7236\u8bf4\u2026\u2026\u201d\u4ed6\u5bfb\u601d\u8981\u8bf4\u4ec0\u4e48\u8bdd\uff0c\u6c89\u541f\u534a\u664c\uff0c\u6325\u624b\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\u53ef\u8bf4\u7684\uff0c\u4f60\u8d70\u5427\uff01\u201d \u3000\u3000\u5f20\u98de\u96c4\u53ea\u9053\u8fd9\u6b21\u5f04\u778e\u4e86\u82d7\u4eba\u51e4\u53cc\u773c\uff0c\u5b9a\u662f\u6027\u547d\u96be\u4fdd\uff0c\u5c82\u77e5\u4ed6\u5bbd\u5b8f\u5927\u91cf\uff0c\u7adf\u7136\u5e76\u4e0d\u8ffd\u7a76\uff0c\u5f53\u771f\u662f\u5927\u51fa\u610f\u6599\u4e4b\u5916\uff0c\u5fc3\u4e2d\u611f\u6fc0\uff0c\u5f53\u5373\u8dea\u5012\uff0c\u8fde\u8fde\u78d5\u5934\u3002 \u3000\u3000\u4ed6\u540c\u6765\u4e00\u5171\u56db\u4eba\uff0c\u539f\u60f3\u4e58\u82d7\u4eba\u51e4\u773c\u778e\u540e\u5c06\u4ed6\u5bb3\u6b7b\uff0c\u518d\u5c06\u4ed6\u5973\u513f\u52ab\u8d70\uff0c\u54ea\u6599\u5230\u7adf\u6709\u80e1\u6590\u8fd9\u6837\u4e00\u4e2a\u597d\u624b\u6a2a\u52a0\u5e72\u9884\uff0c\u4f7f\u4ed6\u4eec\u7684\u6bd2\u8ba1\u53ea\u6210\u529f\u4e86\u7b2c\u4e00\u6b65\u3002\u7ed9\u80e1\u6590\u6454\u5165\u5367\u5ba4\u3001\u904d\u8eab\u9cde\u4f24\u90a3\u4eba\u5df2\u4e58\u4e71\u9003\u8d70\uff0c\u53e6\u5916\u7ed9\u82d7\u4eba\u51e4\u7528\u4e09\u8282\u68cd\u53ca\u62f3\u529b\u6253\u4f24\u7684\u4e24\u4eba\u5374\u4f24\u52bf\u6781\u91cd\uff0c\u4e00\u4e2a\u6655\u7740\u5140\u81ea\u672a\u9192\uff0c\u4e00\u4e2a\u4f4e\u58f0\u547b\u541f\uff0c\u6709\u6c14\u65e0\u529b\u3002 \u3000\u3000\u5218\u9e64\u771f\u5bfb\u601d\uff1a\u201c\u82d7\u4eba\u51e4\u5047\u610f\u9976\u8fd9\u4e09\u4eba\uff0c\u5374\u4e0d\u77e5\u8981\u7528\u4ec0\u4e48\u6bd2\u8ba1\u6765\u6298\u78e8\u4ed6\u4eec\uff1f\u201d\u4ed6\u4e45\u5386\u6c5f\u6e56\uff0c\u66fe\u89c1\u8bb8\u591a\u4eba\u64d2\u4f4f\u654c\u4eba\u540e\u4e0d\u5373\u6740\u6b7b\uff0c\u8981\u4f5c\u5f04\u4e2a\u591f\uff0c\u4f7f\u654c\u4eba\u75db\u82e6\u96be\u5f53\uff0c\u6c42\u751f\u4e0d\u5f97\uff0c\u6c42\u6b7b\u4e0d\u80fd\uff0c\u8fd9\u624d\u6162\u6162\u5904\u6b7b\u3002\u53ea\u89c1\u5f20\u98de\u96c4\u6276\u8d77\u53d7\u4f24\u7684\u4e24\u4e2a\u5e08\u5f1f\uff0c\u4e00\u6b65\u6b65\u8d70\u51fa\u95e8\u5916\uff0c\u9010\u6e10\u8fdc\u53bb\uff0c\u82d7\u4eba\u51e4\u59cb\u7ec8\u6ca1\u6709\u51fa\u624b\uff0c\u773c\u89c1\u4e09\u4eba\u5df2\u9690\u6ca1\u5728\u9ed1\u6697\u4e4b\u4e2d\uff0c\u5fcd\u4e0d\u4f4f\u8bf4\u9053\uff1a\u201c\u82d7\u5927\u4fa0\uff0c\u53ef\u4ee5\u6349\u56de\u6765\u5566\uff0c\u90a3\u59d3\u5f20\u7684\u5c0f\u5b50\u624b\u811a\u6ed1\u6e9c\uff0c\u518d\u653e\u5f97\u8fdc\uff0c\u53ea\u6015\u5f53\u771f\u7ed9\u4ed6\u8d70\u4e86\uff01\u201d\u82d7\u4eba\u51e4\u6de1\u6de1\u7684\u9053\uff1a\u201c\u6211\u9976\u4ed6\u4eec\u53bb\u4e86\uff0c\u53c8\u6349\u56de\u6765\u4f5c\u751a\uff1f\u201d\u4ed6\u5fae\u5fae\u4e00\u987f\uff0c\u8bf4\u9053\uff1a\u201c\u4ed6\u4eec\u548c\u6211\u7d20\u4e0d\u76f8\u8bc6\uff0c\u662f\u522b\u4eba\u5dee\u4f7f\u6765\u7684\u3002\u201d \u3000\u3000\u5218\u9e64\u771f\u53c8\u60ca\u53c8\u6127\uff0c\u970d\u5730\u7ad9\u8d77\u8eab\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u82d7\u5927\u4fa0\uff0c\u6211\u5218\u9e64\u771f\u7d20\u4e0d\u8d1f\u4eba\uff0c\u4eca\u65e5\u6ca1\u751f\u773c\u73e0\uff0c\u7d2f\u4f60\u4e0d\u6d45\u3002\u201d\u5de6\u624b\u4e00\u62ac\uff0c\u98df\u6307\u4e2d\u6307\u4f38\u51fa\uff0c\u6233\u5411\u81ea\u5df1\u7684\u773c\u775b\u3002 \u3000\u3000\u80e1\u6590\u5fd9\u62a2\u8fc7\u53bb\uff0c\u4f38\u624b\u60f3\u683c\uff0c\u7ec8\u7a76\u8fdf\u4e86\u4e00\u6b65\uff0c\u53ea\u89c1\u4ed6\u76f4\u633a\u633a\u5730\u7ad9\u7740\uff0c\u8138\u4e0a\u4e24\u884c\u9c9c\u8840\u6d41\u4e0b\uff0c\u5df2\u7136\u81ea\u6bc1\u53cc\u76ee\u3002\u953a\u6c0f\u5144\u5f1f\u5927\u60ca\uff0c\u4e00\u9f50\u7ad9\u8d77\u8eab\u6765\u3002\u82d7\u4eba\u51e4\u9053\uff1a\u201c\u5218\u8001\u5e08\u4f55\u82e6\u5982\u6b64\uff1f\u5728\u4e0b\u6beb\u6ca1\u89c1\u602a\u4e4b\u610f\u3002\u201d\u5218\u9e64\u771f\u54c8\u54c8\u4e00\u7b11\uff0c\u624b\u81c2\u4e00\u6296\uff0c\u5927\u8e0f\u6b65\u8d70\u51fa\u5c4b\u95e8\uff0c\u987a\u624b\u5728\u9053\u65c1\u6298\u4e86\u4e00\u6839\u6811\u679d\uff0c\u70b9\u7740\u9053\u8def\uff0c\u5f84\u81ea\u53bb\u4e86\u3002\u8fc7\u4e0d\u591a\u65f6\uff0c\u53ea\u542c\u4e00\u4e2a\u5973\u5b50\u58f0\u97f3\u60ca\u547c\u8d77\u6765\uff0c\u5374\u662f\u4ed6\u7684\u59bb\u5b50\u738b\u6c0f\u3002 \u3000\u3000\u5c4b\u4e2d\u4e94\u4eba\u5747\u89c9\u60e8\u7136\uff0c\u4e07\u6599\u4e0d\u5230\u6b64\u4eba\u7adf\u7136\u521a\u70c8\u81f3\u6b64\u3002 \u3000\u3000\u82d7\u4eba\u51e4\u53ea\u6015\u80e1\u6590\u4e5f\u6709\u81ea\u759a\u4e4b\u610f\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4f60\u7b54\u5e94\u7167\u987e\u6211\u7684\u5973\u513f\uff0c\u53ef\u522b\u5fd8\u4e86\u3002\u201d\u80e1\u6590\u77e5\u4ed6\u5fc3\u610f\uff0c\u6602\u7136\u9053\uff1a\u201c\u505a\u9519\u4e86\u4e8b\uff0c\u5e94\u5f53\u5c3d\u529b\u8bbe\u6cd5\u8865\u6551\u3002\u5218\u8001\u5e08\u81ea\u6bc1\u80a2\u4f53\uff0c\u5fc3\u4e2d\u867d\u5b89\uff0c\u5374\u4e0d\u514d\u65e0\u76ca\u4e8e\u4e8b\u3002\u201d\u953a\u5146\u82f1\u53f9\u9053\uff1a\u201c\u4e0d\u9519\uff01\u4f46\u8fd9\u4f4d\u5218\u8001\u5e08\u4e5f\u7b97\u5f97\u662f\u4e00\u4f4d\u54cd\u5f53\u5f53\u7684\u597d\u6c49\u5b50\uff01\u201d \u3000\u3000\u4e94\u4eba\u76f8\u5bf9\u800c\u5750\uff0c\u826f\u4e45\u4e0d\u8bed\u3002\u8fc7\u4e86\u597d\u4e00\u4f1a\uff0c\u80e1\u6590\u9053\uff1a\u201c\u82d7\u5927\u4fa0\uff0c\u4f60\u773c\u775b\u600e\u6837\uff1f\u518d\u7528\u6c34\u6d17\u4e00\u6d17\u5427\uff01\u201d\u82d7\u4eba\u51e4\u9053\uff1a\u201c\u4e0d\u7528\u4e86\uff0c\u53ea\u662f\u75db\u5f97\u5389\u5bb3\u3002\u201d\u7ad9\u8d77\u8eab\u6765\uff0c\u5411\u953a\u6c0f\u4e09\u96c4\u9053\uff1a\u201c\u4e09\u4f4d\u8fdc\u6765\uff0c\u65e0\u4ee5\u5f85\u5ba2\uff0c\u5f53\u771f\u7b80\u6162\u5f97\u7d27\u3002\u6211\u8981\u8fdb\u53bb\u8eba\u4e00\u8eba\uff0c\u8bf7\u52ff\u89c1\u602a\u3002\u201d \u3000\u3000\u953a\u5146\u82f1\u9053\uff1a\u201c\u82d7\u5927\u4fa0\u8bf7\u4fbf\uff0c\u4e0d\u7528\u5ba2\u6c14\u3002\u201d\u4e09\u4eba\u6253\u4e2a\u624b\u52bf\uff0c\u5206\u5728\u524d\u95e8\u540e\u95e8\u5b88\u4f4f\uff0c\u53ea\u6015\u7530\u5f52\u519c\u4e0d\u80af\u5c31\u6b64\u7f62\u624b\uff0c\u53c8\u518d\u6d3e\u4eba\u6765\u88ad\u3002 \u3000\u3000\u80e1\u6590\u624b\u6267\u70db\u53f0\uff0c\u8ddf\u7740\u82d7\u4eba\u51e4\u8d70\u8fdb\u53a2\u623f\uff0c\u89c1\u4ed6\u8eba\u4e0a\u4e86\u5e8a\uff0c\u53d6\u88ab\u7ed9\u4ed6\u76d6\u4e0a\u3002\u90a3\u5c0f\u5973\u5b69\u5728\u91cc\u5e8a\u7761\u5f97\u751a\u6c89\uff0c\u8fd9\u4e00\u665a\u5c4b\u4e2d\u5435\u5f97\u5929\u7ffb\u5730\u8986\uff0c\u5979\u7adf\u59cb\u7ec8\u4e0d\u77e5\u3002 \u3000\u3000\u80e1\u6590\u6b63\u8981\u9000\u51fa\uff0c\u5ffd\u542c\u811a\u6b65\u58f0\u54cd\uff0c\u6709\u4eba\u6025\u5954\u800c\u6765\u3002\u953a\u5146\u80fd\u559d\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u4f60\u53c8\u6765\u5566\uff01\u201d\u63a5\u7740\u5f53\u7684\u4e00\u58f0\uff0c\u5175\u5203\u76f8\u4ea4\u3002\u5f20\u98de\u96c4\u7684\u58f0\u97f3\u53eb\u9053\uff1a\u201c\u6211\u6709\u53e5\u8bdd\u8ddf\u82d7\u5927\u4fa0\u8bf4\uff0c\u5b9e\u65e0\u6b79\u610f\u3002\u201d\u953a\u5146\u80fd\u4f4e\u58f0\u9053\uff1a\u201c\u82d7\u5927\u4fa0\u7761\u4e86\uff0c\u6709\u8bdd\u660e\u5929\u518d\u8bf4\u3002\u201d \u3000\u3000\u5f20\u98de\u96c4\u9053\uff1a\u201c\u597d\uff0c\u90a3\u6211\u8ddf\u4f60\u8bf4\u3002\u82d7\u5927\u4fa0\u5927\u4ec1\u5927\u4e49\uff0c\u9976\u6211\u6027\u547d\uff0c\u8fd9\u53e5\u8bdd\u4e0d\u80fd\u4e0d\u8bf4\u3002\u82d7\u5927\u4fa0\u773c\u4e2d\u6240\u67d3\u7684\u6bd2\u836f\uff0c\u4e43\u662f\u65ad\u80a0\u8349\u7684\u7c89\u672b\uff0c\u662f\u6211\u5e08\u7236\u4ece\u6bd2\u624b\u836f\u738b\u90a3\u91cc\u5f97\u6765\u7684\u3002\u5c0f\u4eba\u4e00\u8def\u5bfb\u601d\uff0c\u82e5\u662f\u6c42\u6bd2\u624b\u836f\u738b\u6551\u6cbb\uff0c\u6216\u80fd\u89e3\u5f97\u3002\u6211\u672c\u8be5\u81ea\u5df1\u53bb\u6c42\uff0c\u53ea\u4e0d\u8fc7\u5c0f\u4eba\u662f\u65e0\u540d\u4e4b\u8f88\uff0c\u8fd9\u4e8b\u51b3\u8ba1\u65e0\u529b\u529e\u5230\u3002\u201d\u953a\u5146\u80fd\u201c\u54e6\u201d\u7684\u4e00\u58f0\uff0c\u63a5\u7740\u811a\u6b65\u58f0\u54cd\uff0c\u5f20\u98de\u96c4\u53c8\u8f6c\u8eab\u53bb\u4e86\u3002 \u3000\u3000\u80e1\u6590\u4e00\u542c\u5927\u559c\uff0c\u4ece\u53a2\u623f\u98de\u6b65\u5954\u51fa\uff0c\u9ad8\u58f0\u95ee\u9053\uff1a\u201c\u8fd9\u4f4d\u6bd2\u624b\u836f\u738b\u4f4f\u5728\u54ea\u91cc\uff1f\u201d\u953a\u5146\u82f1\u9053\uff1a\u201c\u4ed6\u5728\u6d1e\u5ead\u6e56\u7554\u9690\u5c45\uff0c\u4e0d\u8fc7\u2026\u2026 \u3000\u3000\u4e0d\u8fc7\u2026\u2026\u201d\u80e1\u6590\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u953a\u5146\u82f1\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u6c42\u8fd9\u602a\u4eba\u6551\u6cbb\uff0c\u53ea\u6015\u4e0d\u6613\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u54b1\u4eec\u597d\u6b79\u4e5f\u5f97\u5c06\u4ed6\u8bf7\u5230\uff0c\u4ed6\u8981\u4ec0\u4e48\u4fbf\u7ed9\u4ed6\u4ec0\u4e48\u3002\u201d\u953a\u5146\u82f1\u6447\u5934\u9053\uff1a\u201c\u4fbf\u96be\u5728\u4ed6\u4ec0\u4e48\u4e5f\u4e0d\u8981\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u8f6f\u6c42\u4e0d\u6210\uff0c\u90a3\u4fbf\u86ee\u6765\u3002\u201d\u953a\u5146\u82f1\u6c89\u541f\u4e0d\u8bed\u3002 \u3000\u3000\u80e1\u6590\u9053\uff1a\u201c\u4e8b\u4e0d\u5b9c\u8fdf\uff0c\u5c0f\u5f1f\u8fd9\u4fbf\u52a8\u8eab\u3002\u4e09\u4f4d\u5728\u8fd9\u91cc\u5b88\u62a4\uff0c\u4ee5\u9632\u518d\u6709\u654c\u4eba\u524d\u6765\u3002\u201d\u4ed6\u5954\u56de\u53a2\u623f\uff0c\u5411\u82d7\u4eba\u51e4\u9053\uff1a\u201c\u82d7\u5927\u4fa0\uff0c\u6211\u7ed9\u4f60\u8bf7\u533b\u751f\u53bb\u3002\u201d\u82d7\u4eba\u51e4\u6447\u5934\u9053\uff1a\u201c\u8bf7\u6bd2\u624b\u836f\u738b\u4e48\uff1f\u90a3\u662f\u5f92\u52b3\u5f80\u8fd4\uff0c\u4e0d\u7528\u53bb\u4e86\u3002\u201d \u3000\u3000\u80e1\u6590\u9053\uff1a\u201c\u4e0d\uff0c\u5929\u4e0b\u65e0\u96be\u4e8b\uff01\u201d\u8bf4\u7740\u8f6c\u8eab\u51fa\u623f\uff0c\u9053\uff1a\u201c\u4e09\u4f4d\u953a\u7237\uff0c\u8fd9\u4f4d\u836f\u738b\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u4ed6\u4f4f\u7684\u5730\u65b9\u600e\u4e48\u53bb\u6cd5\uff1f\u201d \u3000\u3000\u953a\u5146\u6587\u9053\uff1a\u201c\u597d\uff0c\u6211\u966a\u4f60\u8d70\u4e00\u906d\uff01\u4ed6\u7684\u4e8b\u54b1\u4eec\u8def\u4e0a\u6162\u6162\u518d\u8bf4\u3002\u201d\u5bf9\u5146\u82f1\u3001\u5146\u80fd\u4e8c\u4eba\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4e09\u5f1f\uff0c\u4f60\u4eec\u5728\u8fd9\u91cc\u77a7\u7740\u3002\u201d \u3000\u3000\u953a\u5146\u82f1\u3001\u5146\u80fd\u4e24\u4eba\u8138\u4e0a\u5fae\u5fae\u53d8\u8272\uff0c\u5747\u6709\u6050\u60e7\u4e4b\u610f\uff0c\u968f\u5373\u540c\u58f0\u8bf4\u9053\uff1a\u201c\u5343\u4e07\u5c0f\u5fc3\u3002\u201d \u3000\u3000\u4e8b\u5728\u8feb\u5207\uff0c\u80e1\u953a\u4e24\u4eba\u5c55\u5f00\u8f7b\u8eab\u529f\u592b\uff0c\u5411\u5317\u75be\u5954\u3002\u5929\u660e\u540e\u5728\u5e02\u96c6\u4e0a\u5404\u4e70\u4e86\u4e00\u5339\u9a6c\uff0c\u4e0a\u9a6c\u6025\u9a70\u3002 \u3000"}),showOnHomepage:!0},{name:"\u7b2c\u4e5d\u7ae0\u3000\u6bd2\u624b\u836f\u738b",content:(0,a.jsx)(a.Fragment,{children:"\u4e24\u4eba\u90fd\u77e5\u82d7\u4eba\u51e4\u8fd9\u6b21\u53d7\u6bd2\u4e0d\u8f7b\uff0c\u5355\u5355\u542c\u4e86\u90a3\u201c\u65ad\u80a0\u8349\u201d\u4e09\u5b57\uff0c\u4fbf\u77e5\u662f\u5389\u5bb3\u4e4b\u6781\u7684\u6bd2\u836f\uff0c\u773c\u775b\u53c8\u662f\u4eba\u8eab\u6700\u5a07\u5ae9\u67d4\u8f6f\u7684\u5668\u5b98\uff0c\u7eb5\u7136\u8bf7\u5f97\u540d\u533b\uff0c\u65f6\u5019\u4e00\u957f\uff0c\u4e5f\u5fc5\u65e0\u6551\uff0c\u56e0\u6b64\u65e9\u6cbb\u5f97\u4e00\u523b\u4fbf\u597d\u4e00\u523b\u3002\u4e24\u4eba\u9664\u4e86\u8ba9\u5750\u9a91\u559d\u6c34\u5403\u8349\u4e4b\u5916\uff0c\u4e0d\u6562\u6709\u7247\u523b\u803d\u6401\uff0c\u6cbf\u9014\u4e70\u4e9b\u9992\u5934\u70b9\u5fc3\uff0c\u4fbf\u5728\u9a6c\u80cc\u4e0a\u80e1\u4e71\u5403\u4e86\u5145\u9965\u3002 \u3000\u3000\u5982\u6b64\u4e0d\u7720\u4e0d\u4f11\u7684\u8d76\u8def\uff0c\u953a\u80e1\u4e24\u4eba\u6b66\u529f\u7cbe\u6e5b\uff0c\u867d\u7136\u4e24\u65e5\u4e24\u665a\u6ca1\u7761\uff0c\u5c3d\u81ea\u652f\u6301\u5f97\u4f4f\uff0c\u80ef\u4e0b\u7684\u5750\u9a91\u5728\u9014\u4e2d\u5df2\u6362\u8fc7\u4e24\u5339\uff0c\u4f46\u8fd9\u4e00\u65e5\u8d76\u4e0b\u6765\uff0c\u4e5f\u5df2\u811a\u6b65\u8e09\u8dc4\uff0c\u773c\u89c1\u518d\u8dd1\u4e0b\u53bb\uff0c\u975e\u5728\u9053\u4e0a\u5012\u6bd9\u4e0d\u53ef\u3002\u953a\u5146\u6587\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u54b1\u4eec\u53ea\u597d\u8ba9\u7272\u53e3\u6b47\u4e00\u4f1a\u513f\u3002\u201d\u80e1\u6590\u5e94\u9053\uff1a\u201c\u662f\uff01\u201d\u5fc3\u9053\uff1a\u201c\u5018\u82e5\u6211\u9a91\u7684\u662f\u8881\u59d1\u5a18\u90a3\u5339\u767d\u9a6c\uff0c\u6b64\u523b\u65e9\u5df2\u5230\u4e86\u6d1e\u5ead\u6e56\u7554\u4e86\u3002\u201d\u4e00\u60f3\u5230\u8881\u7d2b\u8863\uff0c\u4e0d\u81ea\u7981\u63a2\u624b\u5165\u6000\uff0c\u629a\u6478\u5979\u6240\u7559\u4e0b\u7684\u90a3\u53ea\u7389\u51e4\uff0c\u89e6\u624b\u751f\u6e29\uff0c\u5fc3\u4e2d\u4e5f\u662f\u4e00\u9635\u6e29\u6696\u3002 \u3000\u3000\u4e24\u4eba\u4e0b\u9a6c\uff0c\u5750\u5728\u9053\u65c1\u6811\u4e0b\uff0c\u8ba9\u9a6c\u5339\u5403\u8349\u4f11\u606f\u3002\u953a\u5146\u6587\u9ed8\u4e0d\u4f5c\u58f0\uff0c\u5446\u5446\u51fa\u795e\uff0c\u76b1\u8d77\u4e86\u7709\u5934\u3002\u80e1\u6590\u77e5\u9053\u6b64\u884c\u6b8a\u65e0\u628a\u63e1\uff0c\u95ee\u9053\uff1a\u201c\u953a\u4e8c\u7237\uff0c\u90a3\u6bd2\u624b\u836f\u738b\u5230\u5e95\u662f\u600e\u6837\u4e00\u4e2a\u4eba\u7269\uff1f\u201d\u953a\u5146\u6587\u4e0d\u7b54\uff0c\u4f3c\u4e4e\u6ca1\u542c\u89c1\u4ed6\u7684\u8bf4\u8bdd\uff0c\u8fc7\u4e86\u534a\u664c\uff0c\u7a81\u7136\u60ca\u89c9\uff0c\u9053\uff1a\u201c\u4f60\u521a\u624d\u8bf4\u4ec0\u4e48\uff01\u201d \u3000\u3000\u80e1\u6590\u89c1\u4ed6\u5fc3\u4e0d\u5728\u7109\uff0c\u77e5\u4ed6\u662f\u6302\u5ff5\u82d7\u4eba\u51e4\u7684\u75c5\u51b5\uff0c\u6697\u60f3\u6b64\u4eba\u867d\u7136\u5947\u5f62\u602a\u72b6\uff0c\u96be\u4e3a\u4ed6\u5f88\u591f\u4e49\u6c14\uff0c\u672c\u6765\u4e0e\u82d7\u4eba\u51e4\u7ed3\u4e0b\u4e86\u6881\u5b50\uff0c\u8fd9\u65f6\u7adf\u4e0d\u8f9e\u70e6\u52b3\u7684\u4e3a\u4ed6\u5954\u6ce2\uff0c\u60f3\u5230\u6b64\u5904\uff0c\u4e0d\u7981\u8131\u53e3\u800c\u51fa\uff1a \u3000\u3000\u201c\u953a\u4e8c\u7237\uff0c\u6628\u5929\u591a\u6709\u5f97\u7f6a\uff0c\u771f\u662f\u60ed\u6127\u5f97\u7d27\u3002\u665a\u8f88\u8981\u662f\u65e9\u77e5\u4e09\u4f4d\u5982\u6b64\u4ed7\u4e49\uff0c\u4fbf\u6709\u5929\u5927\u7684\u80c6\u5b50\uff0c\u4e5f\u4e0d\u6562\u5192\u72af\u3002\u201d \u3000\u3000\u953a\u5146\u6587\u54a7\u5f00\u9614\u5634\uff0c\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u90a3\u7b97\u5f97\u4ec0\u4e48\uff1f\u82d7\u5927\u4fa0\u662f\u54cd\u5f53\u5f53\u7684\u597d\u6c49\uff0c\u6211\u4e09\u5144\u5f1f\u5018\u82e5\u89c1\u5371\u4e0d\u6551\uff0c\u90a3\u8fd8\u662f\u4eba\u4e48\uff1f\u5c0f\u5144\u5f1f\u4f60\u81ea\u5df1\u53c8\u4f55\u5c1d\u4e0d\u662f\u5982\u6b64\uff1f\u6211\u5144\u5f1f\u548c\u82d7\u5927\u4fa0\u867d\u6ca1\u4ea4\u60c5\uff0c\u603b\u8fd8\u6709\u8fc7\u4e00\u9762\u4e4b\u7f18\uff0c\u4f60\u53ef\u8ddf\u4ed6\u89c1\u90fd\u6ca1\u89c1\u8fc7\u5462\u3002\u201d \u3000\u3000\u5176\u5b9e\u6570\u5e74\u4e4b\u524d\uff0c\u80e1\u6590\u5728\u5546\u5bb6\u5821\u4e2d\u66fe\u89c1\u8fc7\u82d7\u4eba\u51e4\u4e00\u9762\uff0c\u53ea\u4e0d\u8fc7\u80e1\u6590\u77e5\u9053\u8fd9\u4e8b\uff0c\u82d7\u4eba\u51e4\u5374\u5728\u5f53\u65f6\u5c31\u5bf9\u90a3\u4e2a\u9ec4\u9ec4\u7626\u7626\u7684\u5c0f\u53ae\u89c6\u800c\u4e0d\u89c1\u3002\u66f4\u65e9\u4e9b\u65f6\u5019\uff0c\u5728\u5341\u516b\u5e74\u4e4b\u524d\uff0c\u80e1\u6590\u751f\u4e0b\u8fd8\u53ea\u4e00\u5929\uff0c\u82d7\u4eba\u51e4\u5728\u6cb3\u5317\u6ca7\u5dde\u7684\u5c0f\u5ba2\u5e97\u4e2d\u4e5f\u66fe\u89c1\u8fc7\u4ed6\uff0c\u8fd9\u4ef6\u4e8b\u82d7\u4eba\u51e4\u77e5\u9053\uff0c\u80e1\u6590\u53ef\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u4f46\u82d7\u4eba\u51e4\u54ea\u91cc\u4f1a\u77e5\u9053\uff1a\u5341\u516b\u5e74\u524d\u90a3\u4e2a\u521d\u751f\u5a74\u513f\uff0c\u4fbf\u662f\u4eca\u65e5\u8fd9\u4e2a\u4e0d\u8bc6\u9762\u7684\u5c11\u5e74\u82f1\u96c4\uff1f \u3000\u3000\u953a\u5146\u6587\u53c8\u95ee\uff1a\u201c\u4f60\u521a\u624d\u95ee\u6211\u4ec0\u4e48\uff1f\u201d\u80e1\u6590\u9053\uff1a\u201c\u6211\u95ee\u90a3\u6bd2\u624b\u836f\u738b\u662f\u600e\u4e48\u6837\u7684\u4eba\u7269\uff1f\u201d\u953a\u5146\u6587\u6447\u6447\u5934\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\u3002\u201d\u80e1\u6590\u5947\u9053\uff1a\u201c\u4f60\u4e0d\u77e5\u9053\uff1f\u201d\u953a\u5146\u6587\u9053\uff1a\u201c\u6211\u6c5f\u6e56\u4e0a\u7684\u670b\u53cb\u4e0d\u7b97\u5c11\u4e86\uff0c\u53ef\u662f\u8c01\u4e5f\u4e0d\u77e5\u6bd2\u624b\u836f\u738b\u5230\u5e95\u662f\u600e\u4e48\u6837\u7684\u4eba\u7269\u3002\u201d \u3000\u3000\u80e1\u6590\u597d\u751f\u7eb3\u95f7\uff0c\u5fc3\u60f3\uff1a\u201c\u6211\u53ea\u9053\u4f60\u5fc5\u5b9a\u77e5\u6653\u6b64\u4eba\u7684\u5e95\u7ec6\uff0c\u5426\u5219\u4e5f\u53ef\u5411\u90a3\u5f20\u98de\u96c4\u6253\u542c\u4e2a\u660e\u767d\u3002\u201d\u953a\u5146\u6587\u731c\u5230\u4e86\u4ed6\u5fc3\u610f\uff0c\u8bf4\u9053\uff1a\u201c\u4fbf\u662f\u90a3\u5f20\u98de\u96c4\uff0c\u4e5f\u672a\u5fc5\u4fbf\u77e5\u3002\u4e0d\uff0c\u4ed6\u4e00\u5b9a\u4e0d\u4f1a\u77e5\u9053\u7684\u3002\u201d \u3000\u3000\u80e1\u6590\u201c\u554a\u201d\u4e86\u4e00\u58f0\uff0c\u4e0d\u518d\u63a5\u53e3\u3002 \u3000\u3000\u953a\u5146\u6587\u9053\uff1a\u201c\u5927\u5bb6\u53ea\u77e5\u9053\uff0c\u8fd9\u4eba\u4f4f\u5728\u6d1e\u5ead\u6e56\u7554\u7684\u767d\u9a6c\u5bfa\u3002\u201d \u3000\u3000\u80e1\u6590\u9053\uff1a\u201c\u767d\u9a6c\u5bfa\uff1f\u4ed6\u4f4f\u5728\u5e99\u91cc\u4e48\uff1f\u201d\u953a\u5146\u6587\u9053\uff1a\u201c\u4e0d\uff0c\u767d\u9a6c\u5bfa\u662f\u4e2a\u5e02\u9547\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u60f3\u662f\u4ed6\u9690\u5c45\u4e0d\u89c1\u5916\u4eba\uff0c\u6240\u4ee5\u8c01\u90fd\u6ca1\u89c1\u8fc7\u4ed6\u3002\u201d\u953a\u5146\u6587\u53c8\u6447\u5934\u9053\uff1a\u201c\u4e0d\uff0c\u6709\u5f88\u591a\u4eba\u89c1\u8fc7\u4ed6\u3002\u6b63\u56e0\u4e3a\u6709\u4eba\u89c1\u8fc7\uff0c\u6240\u4ee5\u8c01\u4e5f\u4e0d\u77e5\u4ed6\u662f\u600e\u4e48\u6837\u7684\u4eba\u7269\uff0c\u4e0d\u77e5\u4ed6\u662f\u80d6\u8fd8\u662f\u7626\uff0c\u662f\u4fca\u662f\u4e11\uff0c\u662f\u59d3\u5f20\u8fd8\u662f\u59d3\u674e\u3002\u201d \u3000\u3000\u80e1\u6590\u8d8a\u542c\u8d8a\u662f\u80e1\u6d82\uff0c\u5fc3\u60f3\u65e2\u7136\u6709\u5f88\u591a\u4eba\u89c1\u8fc7\u4ed6\uff0c\u5c31\u7b97\u4e0d\u77e5\u4ed6\u59d3\u540d\uff0c\u600e\u4f1a\u8fde\u80d6\u7626\u4fca\u4e11\u4e5f\u4e0d\u77e5\u9053\uff1f \u3000\u3000\u953a\u5146\u6587\u9053\uff1a\u201c\u6709\u4eba\u8bf4\u6bd2\u624b\u836f\u738b\u662f\u4e2a\u76f8\u8c8c\u6e05\u96c5\u7684\u4e66\u751f\uff0c\u9ad8\u9ad8\u7626\u7626\uff0c\u50cf\u662f\u4e2a\u79c0\u624d\u76f8\u516c\u3002\u6709\u4eba\u5374\u8bf4\u6bd2\u624b\u836f\u738b\u662f\u4e2a\u6ee1\u8138\u6a2a\u8089\u7684\u77ee\u80d6\u5b50\uff0c\u5c31\u50cf\u662f\u4e2a\u6740\u732a\u7684\u5c60\u592b\u3002\u53c8\u6709\u4eba\u8bf4\uff0c\u8fd9\u836f\u738b\u662f\u4e2a\u8001\u548c\u5c1a\uff0c\u8001\u5f97\u5feb\u4e00\u767e\u5c81\u4e86\u3002\u201d\u4ed6\u987f\u4e86\u4e00\u987f\uff0c\u8bf4\u9053\uff1a\u201c\u8fd8\u6709\u4eba\u8bf4\uff0c\u8fd9\u836f\u738b\u7adf\u7136\u662f\u4e2a\u5973\u4eba\uff0c\u662f\u4e2a\u8ddb\u811a\u9a7c\u80cc\u7684\u5973\u4eba\u3002\u201d \u3000\u3000\u80e1\u6590\u6ee1\u8138\u8ff7\u60d8\uff0c\u60f3\u7b11\uff0c\u5374\u53c8\u7b11\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u953a\u5146\u6587\u63a5\u7740\u9053\uff1a\u201c\u8fd9\u4eba\u65e2\u7136\u53f7\u79f0\u836f\u738b\uff0c\u600e\u4e48\u4f1a\u662f\u5973\u4eba\uff1f\u4f46\u8bf4\u8fd9\u8bdd\u7684\u662f\u6c5f\u6e56\u4e0a\u7684\u6210\u540d\u4eba\u7269\uff0c\u5fb7\u9ad8\u671b\u91cd\uff0c\u7d20\u6765\u4e0d\u6253\u8c0e\u8bed\uff0c\u4e0d\u7531\u5f97\u4eba\u4e0d\u4fe1\uff0c\u53ef\u662f\u90a3\u4e9b\u8bf4\u4ed6\u662f\u4e66\u751f\u3001\u662f\u5c60\u592b\u3001\u662f\u548c\u5c1a\u7684\uff0c\u4e5f\u90fd\u4e0d\u662f\u4fe1\u53e3\u96cc\u9ec4\u4e4b\u8f88\uff0c\u4e2a\u4e2a\u8a00\u4e4b\u51ff\u51ff\u3002\u4f60\u8bf4\u5947\u4e0d\u5947\u602a\uff1f\u201d \u3000\u3000\u80e1\u6590\u5f53\u79bb\u5f00\u82d7\u5bb6\u4e4b\u65f6\uff0c\u6ee1\u6000\u4fe1\u5fc3\uff0c\u6599\u60f3\u53ea\u8981\u627e\u5230\u90a3\u4eba\uff0c\u597d\u6b79\u4e5f\u8981\u8bf7\u4e86\u4ed6\u6765\u6cbb\u4f24\uff0c\u81f3\u4e0d\u6d4e\u4e5f\u80fd\u8ba8\u5f97\u89e3\u836f\uff0c\u6b64\u65f6\u542c\u953a\u5146\u6587\u8fd9\u4e48\u4e00\u8bf4\uff0c\u4e00\u9897\u5fc3\u4e0d\u7531\u5f97\u6c89\u4e86\u4e0b\u53bb\uff0c\u662f\u600e\u4e48\u6837\u4e00\u4e2a\u4eba\u4e5f\u65e0\u6cd5\u77e5\u9053\uff0c\u5374\u53c8\u627e\u8c01\u53bb\uff1f\u8f6c\u5ff5\u4e00\u60f3\uff0c\u8bf4\u9053\uff1a\u201c\u662f\u4e86\uff01\u8fd9\u4eba\u4e00\u5b9a\u64c5\u4e8e\u5316\u88c5\u6613\u5bb9\u4e4b\u672f\uff0c\u5ffd\u7537\u5ffd\u5973\uff0c\u5ffd\u4fca\u5ffd\u4e11\uff0c\u53eb\u4eba\u8ba4\u4e0d\u51fa\u4ed6\u7684\u771f\u9762\u76ee\u6765\u3002\u201d \u3000\u3000\u953a\u5146\u6587\u9053\uff1a\u201c\u6c5f\u6e56\u4e0a\u7684\u670b\u53cb\u4e5f\u90fd\u8fd9\u4e48\u8bf4\uff0c\u60f3\u6765\u4ed6\u4f7f\u6bd2\u5929\u4e0b\u65e0\u53cc\uff0c\u5bb3\u5f97\u4eba\u591a\uff0c\u7ed3\u4ec7\u592a\u5e7f\uff0c\u56e0\u6b64\u8eb2\u8eb2\u95ea\u95ea\uff0c\u53eb\u4eba\u6ca1\u6cd5\u627e\u4ed6\u62a5\u4ec7\u3002\u4f46\u5947\u602a\u7684\u662f\uff0c\u4ed6\u4f4f\u5728\u6d1e\u5ead\u6e56\u7554\u7684\u767d\u9a6c\u5bfa\uff0c\u5374\u53c8\u4e0d\u662f\u5341\u5206\u504f\u50fb\u4e4b\u5904\uff0c\u8981\u5bfb\u4e0a\u95e8\u53bb\uff0c\u4e5f\u7b97\u4e0d\u5f97\u600e\u4e48\u4e3a\u96be\u3002\u201d \u3000\u3000\u80e1\u6590\u9053\uff1a\u201c\u8fd9\u4eba\u7528\u6bd2\u836f\u5bb3\u6b7b\u8fc7\u4e0d\u5c11\u4eba\u4e48\uff1f\u201d\u953a\u5146\u6587\u60a0\u7136\u51fa\u795e\uff0c\u9053\uff1a\u201c\u90a3\u662f\u6ca1\u6cd5\u8ba1\u7b97\u7684\u4e86\u3002\u4e0d\u8fc7\u6b7b\u5728\u4ed6\u624b\u4e0b\u7684\u4eba\uff0c\u5927\u90fd\u81ea\u6709\u53d6\u6b7b\u4e4b\u9053\uff0c\u4e0d\u662f\u4f5c\u6076\u591a\u7aef\u7684\u98de\u8d3c\u5927\u76d7\uff0c\u4fbf\u662f\u4ed7\u52bf\u6a2a\u884c\u7684\u571f\u8c6a\u52a3\u7ec5\uff0c\u5012\u6ca1\u542c\u8bf4\u6709\u54ea\u4e00\u4e2a\u4fa0\u4e49\u7684\u6b7b\u5728\u4ed6\u7684\u624b\u4e0b\u3002\u4f46\u56e0\u4ed6\u540d\u58f0\u592a\u54cd\uff0c\u6709\u4eba\u4e2d\u6bd2\u800c\u6b7b\uff0c\u53ea\u8981\u6bd2\u6027\u731b\u70c8\uff0c\u6b7b\u5f97\u5947\u602a\uff0c\u8fd9\u7b14\u5e10\u4fbf\u90fd\u7b97\u5728\u4ed6\u5934\u4e0a\uff0c\u5176\u5b9e\u5927\u534a\u672a\u5fc5\u4fbf\u662f\u4ed6\u5bb3\u7684\u3002\u6709\u65f6\u5019\u4e24\u4e2a\u4eba\u4e00\u5357\u4e00\u5317\uff0c\u76f8\u9694\u51e0\u5343\u91cc\uff0c\u540c\u65f6\u4e2d\u6bd2\u66b4\u6bd9\uff0c\u4e8e\u662f\u4e91\u5357\u7684\u4eba\u8bf4\u6bd2\u624b\u836f\u738b\u5230\u4e86\u4e91\u5357\uff0c\u8fbd\u4e1c\u7684\u4eba\u5374\u8bf4\u836f\u738b\u5728\u8fbd\u4e1c\u51fa\u6ca1\u3002\u8fd9\u4e48\u4e00\u5ba3\u626c\uff0c\u8fd9\u4e2a\u4eba\u66f4\u662f\u5947\u4e0a\u52a0\u5947\u4e86\u3002\u8fd1\u6765\u5df2\u597d\u4e45\u6ca1\u542c\u4eba\u63d0\u5230\u2018\u6bd2\u624b\u836f\u738b\u2019\u56db\u5b57\uff0c\u60f3\u4e0d\u5230\u82d7\u5927\u4fa0\u7684\u4e2d\u6bd2\u7adf\u4f1a\u548c\u4ed6\u6709\u5173\u3002\u5509\uff0c\u65e2\u662f\u6b64\u4eba\u7528\u7684\u836f\uff0c\u53ea\u6015\u2026\u2026\u53ea\u6015\u2026\u2026\u201d\u8bf4\u5230\u8fd9\u91cc\uff0c\u4e0d\u4f4f\u6447\u5934\u3002 \u3000\u3000\u80e1\u6590\u5fc3\u60f3\u6b64\u4e8b\u679c\u7136\u6781\u96be\uff0c\u4e0d\u77e5\u5982\u4f55\u7740\u624b\u662f\u597d\u3002\u953a\u5146\u6587\u7ad9\u8d77\u8eab\u6765\uff0c\u9053\uff1a\u201c\u54b1\u4eec\u8d70\u5427\uff01\u5c0f\u5144\u5f1f\uff0c\u6709\u4e00\u4ef6\u4e8b\u4f60\u5343\u4e07\u8bb0\u4f4f\uff0c\u4e00\u5230\u4e86\u767d\u9a6c\u5bfa\uff0c\u5728\u79bb\u836f\u738b\u5e84\u4e09\u5341\u91cc\u4e4b\u5185\uff0c\u53ef\u5343\u4e07\u4e0d\u80fd\u559d\u4e00\u53e3\u6c34\uff0c\u4e0d\u80fd\u5403\u4e00\u53e3\u4e1c\u897f\uff0c\u4e0d\u7ba1\u9965\u6e34\u5f97\u600e\u4e48\u5389\u5bb3\uff0c\u603b\u4e4b\u4e0d\u80fd\u8ba9\u4e00\u7269\u8fdb\u53e3\u3002\u201d \u3000\u3000\u80e1\u6590\u89c1\u4ed6\u8bf4\u5f97\u90d1\u91cd\uff0c\u5f53\u5373\u7b54\u5e94\uff0c\u731b\u5730\u60f3\u8d77\uff0c\u5f53\u4ed6\u966a\u7740\u81ea\u5df1\u79bb\u5f00\u82d7\u5bb6\u4e4b\u65f6\uff0c\u953a\u5146\u82f1\u548c\u953a\u5146\u80fd\u8138\u4e0a\u90fd\u662f\u4e0d\u4f46\u62c5\u5fe7\uff0c\u7b80\u76f4\u8fd8\u5927\u6709\u60e7\u8272\uff0c\u60f3\u6765\u90a3\u836f\u738b\u7684\u201c\u6bd2\u624b\u201d\u5b9a\u662f\u975e\u540c\u5c0f\u53ef\uff0c\u4ee5\u81f4\u50cf\u953a\u6c0f\u4e09\u96c4\u90a3\u6837\u7684\u4eba\u7269\uff0c\u80c6\u6562\u5411\u201c\u6253\u904d\u5929\u4e0b\u65e0\u654c\u624b\u201d\u82d7\u4eba\u51e4\u6311\u6218\uff0c\u4e00\u542c\u5230\u201c\u6bd2\u624b\u836f\u738b\u201d\u7684\u540d\u5b57\u5374\u662f\u5fc3\u60ca\u80c6\u6218\u3002\u81ea\u5df1\u4e0d\u77e5\u5389\u5bb3\uff0c\u771f\u628a\u5929\u4e0b\u4e8b\u77a7\u5f97\u592a\u8fc7\u8f7b\u6613\u4e86\u3002 \u3000\u3000\u4ed6\u8fc7\u53bb\u7275\u4e86\u9a6c\u5339\uff0c\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u4e0d\u8fc7\u662f\u9080\u4ed6\u6cbb\u75c5\uff0c\u6216\u662f\u8ba8\u4e00\u4efd\u89e3\u836f\uff0c\u5bf9\u4ed6\u5e76\u65e0\u6076\u610f\u3002\u4ed6\u6700\u591a\u4e0d\u80af\uff0c\u90a3\u4e5f\u7f62\u4e86\uff0c\u4f55\u5fc5\u8981\u5bb3\u54b1\u4eec\u6027\u547d\uff1f\u201d\u953a\u5146\u6587\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4f60\u5e74\u7eaa\u8fd8\u8f7b\uff0c\u4e0d\u77e5\u6c5f\u6e56\u4e0a\u4eba\u5fc3\u9669\u8bc8\u3002\u4f60\u5bf9\u4ed6\u867d\u65e0\u6076\u610f\uff0c\u4f46\u4ed6\u8ddf\u4f60\u7d20\u4e0d\u76f8\u8bc6\uff0c\u600e\u4fe1\u5f97\u4f60\u8fc7\uff1f\u773c\u524d\u4fbf\u662f\u4e00\u4e2a\u4f8b\u5b50\uff0c\u5218\u9e64\u771f\u5bf9\u82d7\u5927\u4fa0\u7edd\u65e0\u6b79\u610f\uff0c\u5374\u4f55\u4ee5\u5f04\u778e\u4e86\u4ed6\u7684\u773c\u775b\uff1f\u201d\u80e1\u6590\u9ed8\u7136\u3002\u953a\u5146\u6587\u53c8\u9053\uff1a\u201c\u4f55\u51b5\u8fd9\u6bd2\u624b\u836f\u738b\u4ec7\u5bb6\u904d\u5929\u4e0b\uff0c\u8bb8\u591a\u8ddf\u4ed6\u6beb\u6ca1\u5e72\u7cfb\u7684\u6bd2\u6740\u4e5f\u90fd\u7b97\u5728\u4ed6\u7684\u5e10\u4e0a\u3002\u7109\u77e5\u4f60\u4e0d\u662f\u4ed6\u4ec7\u5bb6\u7684\u5b50\u5f1f\uff1f\u6b64\u4eba\u751f\u6027\u591a\u7591\uff0c\u51fa\u624b\u72e0\u6bd2\uff0c\u5426\u5219\u2018\u836f\u738b\u2019\u4e4b\u4e0a\uff0c\u4f55\u4ee5\u53c8\u52a0\u4e0a\u2018\u6bd2\u624b\u2019\u4e24\u5b57\uff1f\u8fd9\u4e2a\u60ca\u5fc3\u52a8\u9b44\u7684\u5916\u53f7\uff0c\u96be\u9053\u662f\u8f7b\u6613\u5f97\u6765\u7684\u4e48\uff1f\u201d \u3000\u3000\u80e1\u6590\u70b9\u5934\u9053\uff1a\u201c\u953a\u4e8c\u7237\u8bf4\u7684\u662f\u3002\u201d\u953a\u5146\u6587\u9053\uff1a\u201c\u4f60\u82e5\u770b\u5f97\u8d77\u6211\uff0c\u4e0d\u5acc\u6211\u672c\u9886\u4f4e\u5fae\uff0c\u90a3\u4fbf\u5144\u5f1f\u76f8\u79f0\uff0c\u522b\u7237\u4e0d\u7237\u7684\uff0c\u53eb\u5f97\u8fd9\u4e48\u5ba2\u6c14\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u4f60\u662f\u524d\u8f88\u82f1\u96c4\uff0c\u665a\u8f88\u2026\u2026\u201d\u953a\u5146\u6587\u62e6\u7740\u4ed6\u7684\u8bdd\u5934\uff0c\u5927\u58f0\u9053\uff1a\u201c\u5478\uff0c\u5478\uff01\u5c0f\u5144\u5f1f\uff0c\u4e0d\u7792\u4f60\u8bf4\uff0c\u6211\u4e09\u5144\u5f1f\u8ddf\u4f60\u4ea4\u624b\u4e4b\u540e\uff0c\u4f69\u670d\u4f60\u5f97\u7d27\u3002\u82e5\u4f60\u4e0d\u5f53\u6211\u670b\u53cb\uff0c\u90a3\u4fbf\u7b97\u4e86\u3002\u201d \u3000\u3000\u80e1\u6590\u4e5f\u662f\u4e2a\u6027\u5b50\u76f4\u723d\u4e4b\u4eba\uff0c\u4e8e\u662f\u7b11\u7740\u53eb\u4e86\u58f0\uff1a\u201c\u953a\u4e8c\u54e5\u3002\u201d \u3000\u3000\u953a\u5146\u6587\u5f88\u662f\u9ad8\u5174\uff0c\u7ffb\u8eab\u4e0a\u4e86\u9a6c\u80cc\uff0c\u9053\uff1a\u201c\u53ea\u8981\u8fd9\u4e24\u5934\u7272\u53e3\u4e0d\u51fa\u5c94\u5b50\uff0c\u54b1\u4eec\u4e0d\u7528\u5929\u9ed1\u4fbf\u80fd\u8d76\u5230\u767d\u9a6c\u5bfa\u3002\u4f60\u53ef\u5f97\u8bb0\u7740\u6211\u8bdd\uff0c\u522b\u8bf4\u4e0d\u80fd\u5403\u559d\uff0c\u4fbf\u662f\u6478\u4e00\u6478\u7b77\u5b50\uff0c\u4e5f\u5f97\u63d0\u9632\u7b77\u5b50\u4e0a\u4e0b\u4e86\u5267\u6bd2\uff0c\u4f20\u5230\u4f60\u7684\u624b\u4e0a\u3002\u5c0f\u5144\u5f1f\uff0c\u4f60\u8fd9\u4e48\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u4e00\u8eab\u6b66\u529f\uff0c\u82e5\u662f\u5168\u8eab\u53d1\u9ed1\uff0c\u6210\u4e86\u4e00\u5177\u50f5\u5c38\uff0c\u6211\u77a7\u6709\u70b9\u513f\u53ef\u60dc\u5462\uff01\u201d \u3000\u3000\u80e1\u6590\u77e5\u4ed6\u8fd9\u8bdd\u5012\u4e0d\u662f\u5371\u8a00\u8038\u542c\uff0c\u77a7\u82d7\u4eba\u51e4\u53ea\u6495\u7834\u4e00\u5c01\u4fe1\uff0c\u53cc\u773c\u4fbf\u778e\uff0c\u73b0\u4e0b\u8d70\u5165\u6bd2\u624b\u836f\u738b\u7684\u8001\u5de2\uff0c\u4ed6\u54ea\u4e00\u5904\u4e0d\u80fd\u4e0b\u6bd2\uff1f\u5fc3\u60f3\u953a\u5146\u6587\u4e5f\u662f\u6b66\u6797\u4e2d\u7684\u6210\u540d\u4eba\u7269\uff0c\u51b3\u975e\u80c6\u602f\u4e4b\u5f92\uff0c\u4ed6\u8bf4\u5f97\u5982\u6b64\u5389\u5bb3\uff0c\u663e\u89c1\u6b64\u884c\u4e07\u5206\u51f6\u9669\uff0c\u786e\u662f\u5b9e\u60c5\u3002\u4ed6\u660e\u77e5\u9669\u6076\uff0c\u8fd8\u662f\u4e49\u4e0d\u5bb9\u8f9e\u5730\u966a\u81ea\u5df1\u4e0a\u767d\u9a6c\u5bfa\u53bb\uff0c\u6bd4\u4e4b\u81ea\u5df1\u4e0d\u77e5\u5929\u9ad8\u5730\u539a\u7684\u4e71\u95ef\uff0c\u66f4\u662f\u96be\u5f97\u4e86\u3002 \u3000\u3000\u4e24\u5339\u9a6c\u4f11\u606f\u591a\u65f6\uff0c\u7cbe\u529b\u5df2\u590d\uff0c\u7533\u724c\u65f6\u5206\u5230\u4e86\u4e34\u8d44\u53e3\u3002\u4e24\u4eba\u8ba9\u5750\u9a91\u8d70\u4e00\u7a0b\uff0c\u8dd1\u4e00\u7a0b\uff0c\u4e0d\u591a\u65f6\u5df2\u5230\u4e86\u767d\u9a6c\u5bfa\u9547\u4e0a\u3002\u9547\u4e0a\u8857\u9053\u72ed\u7a84\uff0c\u4e24\u4eba\u6df1\u6015\u78b0\u649e\u884c\u4eba\uff0c\u591a\u60f9\u4e8b\u7aef\uff0c\u4e8e\u662f\u7275\u4e86\u9a6c\u5339\u6b65\u884c\u3002 \u3000\u3000\u953a\u5146\u6587\u8138\u8272\u90d1\u91cd\uff0c\u76ee\u4e0d\u659c\u89c6\uff0c\u80e1\u6590\u5374\u653e\u773c\u77a7\u7740\u4e24\u65c1\u7684\u5e97\u94fa\u3002\u5c06\u5230\u5e02\u68a2\u65f6\uff0c\u80e1\u6590\u89c1\u62d0\u5f2f\u89d2\u4e0a\u6311\u51fa\u4e86\u836f\u6750\u94fa\u7684\u818f\u836f\u5e4c\u5b50\uff0c\u62db\u724c\u5199\u7740\u201c\u6d4e\u4e16\u5802\u8001\u5e97\u201d\uff0c\u5fc3\u5ff5\u4e00\u52a8\uff0c\u89e3\u4e0b\u8170\u95f4\u5355\u5200\uff0c\u8fde\u7740\u5200\u9798\u6367\u5728\u624b\u4e2d\uff0c\u8bf4\u9053\uff1a\u201c\u953a\u4e8c\u2026\u2026\u54e5\uff0c\u4f60\u7684\u5224\u5b98\u7b14\u4e5f\u7ed9\u6211\u3002\u201d \u3000\u3000\u953a\u5146\u6587\u4e00\u6014\uff0c\u5fc3\u60f3\u5230\u4e86\u767d\u9a6c\u5bfa\u9547\uff0c\u8be5\u5f53\u5904\u5904\u5c0f\u5fc3\u624d\u662f\uff0c\u600e\u5730\u52a8\u8d77\u5200\u5203\u6765\u5566\uff1f\u4f46\u60f3\u9547\u4e0a\u5fc5\u6709\u836f\u738b\u7684\u8033\u76ee\uff0c\u4e0d\u4fbf\u51fa\u53e3\u8be2\u95ee\uff0c\u4e8e\u662f\u4ece\u8170\u95f4\u62bd\u51fa\u5224\u5b98\u7b14\uff0c\u4ea4\u4e86\u7ed9\u4ed6\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5c0f\u5fc3\u4e86\uff0c\u522b\u60f9\u4e8b\uff01\u201d \u3000\u3000\u80e1\u6590\u70b9\u4e86\u70b9\u5934\uff0c\u8d70\u5230\u836f\u6750\u94fa\u67dc\u53f0\u524d\uff0c\u8bf4\u9053\uff1a\u201c\u52b3\u9a7e\uff01\u6211\u4eec\u4e8c\u4eba\u5230\u836f\u738b\u5e84\u53bb\u62dc\u8bbf\u5e84\u4e3b\uff0c\u4e0d\u4fbf\u643a\u5e26\u5175\u5668\uff0c\u60f3\u5728\u5b9d\u53f7\u5bc4\u653e\u4e00\u4e0b\uff0c\u56de\u5934\u6765\u53d6\u3002\u201d\u5750\u5728\u67dc\u53f0\u540e\u7684\u4e00\u4e2a\u8001\u8005\u542c\u4e86\uff0c\u8138\u9732\u8be7\u5f02\u4e4b\u8272\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u53bb\u836f\u738b\u5e84\uff1f\u201d\u80e1\u6590\u4e0d\u7b49\u4ed6\u518d\u8bf4\u4ec0\u4e48\uff0c\u5c06\u5175\u5668\u5728\u67dc\u53f0\u4e0a\u4e00\u653e\uff0c\u53cc\u624b\u4e00\u62f1\uff0c\u7275\u4e86\u9a6c\u5339\u4fbf\u5927\u8e0f\u6b65\u51fa\u9547\u3002 \u3000\u3000\u4e24\u4eba\u5230\u4e86\u9547\u5916\u65e0\u4eba\u4e4b\u5904\uff0c\u953a\u5146\u6587\u5927\u62c7\u6307\u4e00\u7fd8\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u8fd9\u4e00\u624b\u771f\u6210\u3002\u953a\u8001\u4e8c\u670d\u4e86\u4f60\u5566\uff0c\u771f\u4e8f\u4f60\u60f3\u5f97\u51fa\u3002\u201d\u80e1\u6590\u7b11\u9053\uff1a\u201c\u786c\u7740\u5934\u76ae\u5145\u597d\u6c49\uff0c\u8fd9\u53eb\u505a\u65e0\u53ef\u5948\u4f55\u3002\u201d\u539f\u6765\u4ed6\u60f3\u8fd9\u9547\u4e0a\u7684\u836f\u6750\u94fa\u8ddf\u836f\u738b\u5fc5\u6709\u5e72\u8fde\uff0c\u5c06\u968f\u8eab\u5175\u5668\u653e\u5728\u5e97\u94fa\u4e4b\u4e2d\uff0c\u5e97\u4e2d\u5b9a\u4f1a\u6709\u4eba\u8d76\u53bb\u62a5\u8baf\uff0c\u90a3\u4fbf\u8868\u660e\u81ea\u5df1\u6b64\u6765\u7edd\u65e0\u654c\u610f\u3002\u867d\u7136\u7a7a\u624b\u53bb\u89c1\u8fd9\u4e48\u4e00\u4e2a\u5389\u5bb3\u89d2\u8272\uff0c\u90a3\u662f\u51f6\u9669\u4e4b\u4e0a\u53c8\u52a0\u51f6\u9669\uff0c\u4f46\u6743\u8861\u8f7b\u91cd\uff0c\u8fd9\u4e2a\u9669\u8fd8\u662f\u5927\u53ef\u4e00\u5192\u3002 \u3000\u3000\u89c1\u897f\u9996\u4e00\u5ea7\u5c0f\u5c71\u4e4b\u4e0a\uff0c\u6709\u4e2a\u8001\u8005\u624b\u6301\u836f\u9504\uff0c\u4f3c\u5728\u91c7\u836f\u3002\u80e1\u6590\u89c1\u8fd9\u4eba\u5f62\u8c8c\u4fca\u96c5\uff0c\u9ad8\u9ad8\u7626\u7626\uff0c\u662f\u4e2a\u4e2d\u5e74\u4e66\u751f\uff0c\u5fc3\u5ff5\u4e00\u52a8\uff1a \u3000\u3000\u201c\u96be\u9053\u4ed6\u4fbf\u662f\u6bd2\u624b\u836f\u738b\uff1f\u201d\u4e8e\u662f\u4e0a\u524d\u606d\u606d\u656c\u656c\u7684\u4e00\u63d6\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u8bf7\u95ee\u76f8\u516c\uff0c\u4e0a\u836f\u738b\u5e84\u600e\u751f\u8d70\u6cd5\uff1f\u665a\u8f88\u4e8c\u4eba\u8981\u62dc\u89c1\u5e84\u4e3b\uff0c\u6709\u4e8b\u76f8\u6c42\u3002\u201d \u3000\u3000\u90a3\u4eba\u5bf9\u80e1\u953a\u4e8c\u4eba\u4e00\u773c\u4e5f\u4e0d\u77a7\uff0c\u81ea\u884c\u805a\u7cbe\u4f1a\u795e\u7684\u9504\u571f\u6398\u8349\u3002 \u3000\u3000\u80e1\u6590\u8fde\u95ee\u51e0\u58f0\uff0c\u90a3\u4eba\u59cb\u7ec8\u6beb\u4e0d\u7406\u4f1a\uff0c\u7adf\u4f3c\u804b\u4e86\u4e00\u822c\u3002 \u3000\u3000\u80e1\u6590\u4e0d\u6562\u518d\u95ee\uff0c\u953a\u5146\u6587\u5411\u4ed6\u4f7f\u4e2a\u773c\u8272\uff0c\u4e24\u4eba\u53c8\u5411\u5317\u884c\u3002\u95f7\u58f0\u4e0d\u54cd\u5730\u8d70\u51fa\u4e00\u91cc\u6709\u4f59\uff0c\u80e1\u6590\u6084\u58f0\u9053\uff1a\u201c\u953a\u4e8c\u54e5\uff0c\u53ea\u6015\u8fd9\u4eba\u4fbf\u662f\u836f\u738b\uff0c\u4f60\u77a7\u600e\u4e48\u529e\uff1f\u201d\u953a\u5146\u6587\u9053\uff1a\u201c\u6211\u4e5f\u6709\u51e0\u5206\u7591\u5fc3\uff0c\u53ef\u4e07\u4e07\u70b9\u7834\u4e0d\u5f97\u3002\u4ed6\u81ea\u5df1\u82e5\u4e0d\u627f\u8ba4\uff0c\u800c\u54b1\u4eec\u8ba4\u51fa\u4ed6\u6765\uff0c\u6b63\u662f\u72af\u4e86\u4ed6\u7684\u5927\u5fcc\u3002\u773c\u524d\u53ea\u6709\u5148\u627e\u5230\u836f\u738b\u5e84\uff0c\u54b1\u4eec\u8ba4\u5730\u4e0d\u8ba4\u4eba\uff0c\u90a3\u4fbf\u65e0\u788d\u3002\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u65f6\uff0c\u66f2\u66f2\u6298\u6298\u53c8\u8f6c\u4e86\u51e0\u4e2a\u5f2f\uff0c\u53ea\u89c1\u79bb\u5927\u8def\u6570\u5341\u4e08\u5904\u6709\u4e2a\u5927\u82b1\u5703\uff0c\u4e00\u4e2a\u8eab\u7a7f\u9752\u5e03\u886b\u5b50\u7684\u6751\u5973\u5f2f\u7740\u8170\u5728\u6574\u7406\u82b1\u8349\u3002 \u3000\u3000\u80e1\u6590\u89c1\u82b1\u5703\u4e4b\u540e\u6709\u4e09\u95f4\u8305\u820d\uff0c\u653e\u773c\u8fdc\u671b\uff0c\u56db\u4e0b\u522b\u65e0\u4eba\u70df\uff0c\u4e8e\u662f\u4e0a\u524d\u51e0\u6b65\uff0c\u5411\u90a3\u6751\u5973\u4f5c\u4e86\u4e00\u63d6\uff0c\u95ee\u9053\uff1a\u201c\u8bf7\u95ee\u59d1\u5a18\uff0c\u4e0a\u836f\u738b\u5e84\u8d70\u54ea\u4e00\u6761\u8def\uff1f\u201d \u3000\u3000\u90a3\u6751\u5973\u62ac\u8d77\u5934\u6765\uff0c\u5411\u7740\u80e1\u6590\u4e00\u77a7\uff0c\u4e00\u53cc\u773c\u775b\u660e\u4eae\u4e4b\u6781\uff0c\u773c\u73e0\u9ed1\u5f97\u50cf\u6f06\uff0c\u8fd9\u4e48\u4e00\u62ac\u5934\uff0c\u767b\u65f6\u7cbe\u5149\u56db\u5c04\u3002\u80e1\u6590\u5fc3\u4e2d\u4e00\u6014\uff1a \u3000\u3000\u201c\u8fd9\u4e2a\u4e61\u4e0b\u59d1\u5a18\u7684\u773c\u775b\uff0c\u600e\u4e48\u4eae\u5f97\u5982\u6b64\u5f02\u4e4e\u5bfb\u5e38\uff1f\u201d\u89c1\u5979\u9664\u4e86\u4e00\u53cc\u773c\u775b\u5916\uff0c\u5bb9\u8c8c\u5374\u662f\u5e73\u5e73\uff0c\u808c\u80a4\u67af\u9ec4\uff0c\u8138\u6709\u83dc\u8272\uff0c\u4f3c\u4e4e\u7ec8\u5e74\u5403\u4e0d\u9971\u996d\u4f3c\u7684\uff0c\u5934\u53d1\u4e5f\u662f\u53c8\u9ec4\u53c8\u7a00\uff0c\u53cc\u80a9\u5982\u524a\uff0c\u8eab\u6750\u7626\u5c0f\uff0c\u663e\u662f\u7a77\u6751\u8d2b\u5973\uff0c\u81ea\u5e7c\u4fbf\u5c11\u4e86\u6ecb\u517b\u3002\u5979\u76f8\u8c8c\u4f3c\u4e4e\u5df2\u6709\u5341\u516d\u4e03\u5c81\uff0c\u8eab\u5f62\u5374\u5982\u662f\u4e2a\u5341\u56db\u4e94\u5c81\u7684\u5e7c\u5973\u3002 \u3000\u3000\u80e1\u6590\u53c8\u95ee\u4e00\u53e5\uff1a\u201c\u4e0a\u836f\u738b\u5e84\u4e0d\u77e5\u662f\u5411\u4e1c\u5317\u8fd8\u662f\u5411\u897f\u5317\uff1f\u201d\u90a3\u6751\u5973\u7a81\u7136\u4f4e\u4e0b\u4e86\u5934\uff0c\u51b7\u51b7\u5730\u9053\uff1a\u201c\u4e0d\u77e5\u9053\u3002\u201d\u8bed\u97f3\u5374\u751a\u662f\u6e05\u4eae\u3002 \u3000\u3000\u953a\u5146\u6587\u89c1\u5979\u5982\u6b64\u65e0\u793c\uff0c\u8138\u4e00\u6c89\uff0c\u4fbf\u8981\u53d1\u4f5c\uff0c\u4f46\u968f\u5373\u60f3\u8d77\u6b64\u5904\u8ddd\u836f\u738b\u5e84\u4e0d\u8fdc\uff0c\u4ec0\u4e48\u4eba\u90fd\u5f97\u7f6a\u4e0d\u5f97\uff0c\u54fc\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5144\u5f1f\uff0c\u54b1\u4eec\u53bb\u5427\uff0c\u90a3\u836f\u738b\u5e84\u662f\u767d\u9a6c\u5bfa\u5927\u5927\u6709\u540d\u4e4b\u5904\uff0c\u603b\u4e0d\u80fd\u627e\u4e0d\u5230\u3002\u201d \u3000\u3000\u80e1\u6590\u5fc3\u60f3\u5929\u8272\u5df2\u7ecf\u4e0d\u65e9\uff0c\u82e5\u662f\u8d70\u9519\u4e86\u8def\uff0c\u9ed1\u591c\u4e4b\u4e2d\u5728\u8fd9\u9669\u5730\u5230\u5904\u778e\u95ef\uff0c\u5927\u662f\u4e0d\u5999\uff0c\u5de6\u8fd1\u518d\u65e0\u4eba\u5bb6\u53ef\u4ee5\u95ee\u8def\uff0c\u4e8e\u662f\u53c8\u95ee\u90a3\u6751\u5973\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u7236\u6bcd\u5728\u5bb6\u4e48\uff1f\u4ed6\u4eec\u5b9a\u4f1a\u77e5\u9053\u53bb\u836f\u738b\u5e84\u7684\u8def\u5f84\u3002\u201d\u90a3\u6751\u5973\u4e0d\u518d\u7406\u776c\uff0c\u81ea\u7ba1\u81ea\u7684\u62d4\u8349\u3002 \u3000\u3000\u953a\u5146\u6587\u53cc\u817f\u4e00\u5939\uff0c\u7eb5\u9a6c\u4fbf\u5411\u524d\u5954\uff0c\u9053\u8def\u72ed\u7a84\uff0c\u90a3\u9a6c\u53f3\u8fb9\u524d\u540e\u53cc\u8e44\u8e0f\u5728\u8def\u4e0a\uff0c\u5de6\u4fa7\u7684\u4e24\u8e44\u5374\u8e0f\u5165\u4e86\u82b1\u5703\u3002\u953a\u5146\u6587\u867d\u65e0\u6b79\u610f\uff0c\u4f46\u751f\u6027\u7c97\u8c6a\uff0c\u53c8\u607c\u90a3\u6751\u5973\u65e0\u793c\uff0c\u6025\u4e8e\u8d76\u8def\uff0c\u4e5f\u4e0d\u7406\u4f1a\u3002 \u3000\u3000\u80e1\u6590\u773c\u89c1\u8fd1\u8def\u8fb9\u7684\u4e00\u6392\u82b1\u8349\u4fbf\u8981\u7ed9\u9a6c\u8e0f\u574f\uff0c\u5fd9\u7eb5\u8eab\u4e0a\u524d\uff0c\u62c9\u4f4f\u7f30\u7ef3\u5f80\u53f3\u4e00\u5e26\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u5fc3\u8e0f\u574f\u4e86\u82b1\u8349\u3002\u201d\u90a3\u9a6c\u7ed9\u4ed6\u8fd9\u4e48\u4e00\u5f15\uff0c\u53f3\u8e44\u8e0f\u5230\u4e86\u9053\u8def\u53f3\u4fa7\uff0c\u5de6\u8e44\u56de\u4e0a\u8def\u9762\u3002\u953a\u5146\u6587\u9053\uff1a\u201c\u5feb\u8d70\u5427\uff0c\u5728\u8fd9\u513f\u522b\u803d\u6401\u5566\uff01\u201d\u8bf4\u7740\u4e00\u63d0\u7f30\u7ef3\uff0c\u5411\u524d\u9a70\u53bb\u3002 \u3000\u3000\u80e1\u6590\u81ea\u5e7c\u5b64\u82e6\uff0c\u89c1\u90a3\u6751\u5973\u8d2b\u5f31\uff0c\u5fc3\u4e2d\u5e76\u4e0d\u6c14\u5979\u4e0d\u80af\u6307\u5f15\uff0c\u53cd\u751f\u601c\u60af\u4e4b\u610f\uff0c\u5fc3\u60f3\u5979\u79cd\u8fd9\u4e9b\u82b1\u8349\uff0c\u5b9a\u662f\u5356\u4e86\u8d56\u4ee5\u4e3a\u6d3b\uff0c\u751f\u6015\u7ed9\u81ea\u5df1\u5750\u9a91\u8e0f\u574f\u4e86\uff0c\u4e8e\u662f\u7275\u7740\u9a6c\u6b65\u884c\u8fc7\u4e86\u82b1\u5730\uff0c\u8fd9\u624d\u4e0a\u9a6c\u3002 \u3000\u3000\u90a3\u6751\u5973\u77a7\u5728\u773c\u91cc\uff0c\u7a81\u7136\u62ac\u5934\u95ee\u9053\uff1a\u201c\u4f60\u5230\u836f\u738b\u5e84\u53bb\u5e72\u4e48\uff1f\u201d \u3000\u3000\u80e1\u6590\u52d2\u9a6c\u7b54\u9053\uff1a\u201c\u6709\u4e00\u4f4d\u670b\u53cb\u7ed9\u6bd2\u836f\u4f24\u4e86\u773c\u775b\uff0c\u6211\u4eec\u7279\u5730\u6765\u6c42\u836f\u738b\u8d50\u4e9b\u89e3\u836f\u3002\u201d\u90a3\u6751\u5973\u9053\uff1a\u201c\u4f60\u8ba4\u5f97\u836f\u738b\u4e48\uff1f\u201d\u80e1\u6590\u6447\u5934\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u53ea\u95fb\u5176\u540d\uff0c\u4ece\u6765\u6ca1\u89c1\u8fc7\u4ed6\u8001\u4eba\u5bb6\u3002\u201d\u90a3\u6751\u5973\u6162\u6162\u7ad9\u76f4\u4e86\u8eab\u5b50\uff0c\u5411\u80e1\u6590\u6253\u91cf\u4e86\u51e0\u773c\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u600e\u77e5\u4ed6\u80af\u7ed9\u89e3\u836f\uff1f\u201d \u3000\u3000\u80e1\u6590\u8138\u6709\u4e3a\u96be\u4e4b\u8272\uff0c\u7b54\u9053\uff1a\u201c\u8fd9\u4e8b\u539f\u672c\u96be\u8bf4\u3002\u201d\u5fc3\u4e2d\u5ffd\u7136\u4e00\u52a8\uff1a\u201c\u8fd9\u4f4d\u59d1\u5a18\u4f4f\u5728\u6b64\u5904\uff0c\u6216\u8005\u77e5\u9053\u836f\u738b\u7684\u6027\u60c5\u884c\u4e8b\u3002\u201d\u4e8e\u662f\u7ffb\u8eab\u4e0b\u9a6c\uff0c\u6df1\u6df1\u4e00\u63d6\uff0c\u8bf4\u9053\uff1a\u201c\u4fbf\u662f\u8981\u8bf7\u59d1\u5a18\u6307\u70b9\u9014\u5f84\u3002\u201d\u8fd9\u201c\u6307\u70b9\u9014\u5f84\u201d\u56db\u5b57\uff0c\u5374\u662f\u610f\u5e26\u53cc\u5173\uff0c\u53ef\u4ee5\u8bf4\u662f\u8bf7\u5979\u6307\u70b9\u53bb\u836f\u738b\u5e84\u7684\u9053\u8def\uff0c\u4e5f\u53ef\u8bf4\u662f\u8bf7\u6559\u6c42\u836f\u7684\u65b9\u6cd5\u3002 \u3000\u3000\u90a3\u6751\u5973\u81ea\u5934\u81f3\u811a\u5730\u5411\u4ed6\u6253\u91cf\u4e00\u904d\uff0c\u5e76\u4e0d\u7b54\u8bdd\uff0c\u6307\u7740\u82b1\u5703\u4e2d\u7684\u4e00\u5bf9\u7caa\u6876\uff0c\u9053\uff1a\u201c\u4f60\u5230\u90a3\u8fb9\u7caa\u6c60\u53bb\u88c5\u5c0f\u534a\u6876\u7caa\uff0c\u5230\u6eaa\u91cc\u52a0\u6ee1\u6e05\u6c34\uff0c\u7ed9\u6211\u628a\u8fd9\u5757\u82b1\u6d47\u4e00\u6d47\u3002\u201d \u3000\u3000\u8fd9\u4e09\u53e5\u8bdd\u5927\u51fa\u80e1\u6590\u610f\u6599\u4e4b\u5916\uff0c\u5fc3\u60f3\u6211\u53ea\u662f\u5411\u4f60\u95ee\u8def\uff0c\u600e\u4e48\u7adf\u53eb\u6211\u6d47\u8d77\u82b1\u6765\uff1f\u800c\u4e14\u51fa\u8a00\u9890\u6307\u6c14\u4f7f\uff0c\u7adf\u5c06\u6211\u5f53\u4f5c\u4f60\u5bb6\u96c7\u5de5\u4e00\u822c\uff1f\u4ed6\u867d\u5e7c\u65f6\u8d2b\u82e6\uff0c\u5374\u4e5f\u4ece\u672a\u505a\u8fc7\u6311\u7caa\u6d47\u7caa\u8fd9\u79cd\u79fd\u81ed\u4e4b\u4e8b\uff0c\u53ea\u89c1\u90a3\u6751\u5973\u8bf4\u4e86\u8fd9\u51e0\u53e5\u8bdd\u540e\uff0c\u53c8\u4fef\u8eab\u62d4\u8349\uff0c\u4e00\u773c\u4e5f\u4e0d\u518d\u77a7\u4ed6\u3002\u80e1\u6590\u4e00\u6014\u4e4b\u4e0b\uff0c\u5411\u8305\u820d\u91cc\u4e00\u671b\uff0c\u4e0d\u89c1\u6709\u4eba\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u59d1\u5a18\u751f\u5f97\u7626\u5f31\uff0c\u8981\u6311\u8fd9\u4e24\u5927\u6876\u7caa\u5f53\u771f\u4e0d\u6613\u3002\u6211\u662f\u4e00\u8eab\u529b\u6c14\u7684\u7537\u5b50\u6c49\uff0c\u4fbf\u5e2e\u5979\u6311\u4e00\u62c5\u7caa\u53c8\u6709\u4f55\u59a8\uff1f\u201d\u4e8e\u662f\u5c06\u9a6c\u7cfb\u5728\u4e00\u682a\u67f3\u6811\u4e0a\uff0c\u6311\u8d77\u7caa\u6876\uff0c\u4fbf\u5f80\u7caa\u6c60\u53bb\u62c5\u7caa\u3002 \u3000\u3000\u953a\u5146\u6587\u884c\u4e86\u4e00\u7a0b\uff0c\u4e0d\u89c1\u80e1\u6590\u8ddf\u6765\uff0c\u56de\u5934\u4e00\u770b\uff0c\u8fdc\u8fdc\u671b\u89c1\u4ed6\u80a9\u4e0a\u6311\u4e86\u4e00\u526f\u7caa\u6876\uff0c\u8d70\u5411\u6eaa\u8fb9\uff0c\u4e0d\u7981\u5927\u5947\uff0c\u53eb\u9053\uff1a\u201c\u5582\uff0c\u4f60\u5e72\u4ec0\u4e48\uff1f\u201d\u80e1\u6590\u53eb\u9053\uff1a\u201c\u6211\u5e2e\u8fd9\u4f4d\u59d1\u5a18\u505a\u4e00\u70b9\u5de5\u592b\u3002\u953a\u4e8c\u54e5\u5148\u8d70\u4e00\u6b65\uff0c\u6211\u9a6c\u4e0a\u5c31\u8d76\u6765\u3002\u201d\u953a\u5146\u6587\u6447\u4e86\u6447\u5934\uff0c\u5fc3\u60f3\u5e74\u8f7b\u4eba\u5f53\u771f\u662f\u4e0d\u5206\u8f7b\u91cd\uff0c\u5728\u8fd9\u5f53\u53e3\u5c45\u7136\u8fd8\u8fd9\u822c\u591a\u7ba1\u95f2\u4e8b\uff0c\u4e8e\u662f\u7eb5\u9a6c\u7f13\u7f13\u800c\u884c\u3002 \u3000\u3000\u80e1\u6590\u6311\u4e86\u4e00\u62c5\u7caa\u6c34\uff0c\u56de\u5230\u82b1\u5730\u4e4b\u65c1\uff0c\u7528\u6728\u74e2\u8200\u4e86\uff0c\u4fbf\u8981\u5f80\u82b1\u65c1\u6d47\u53bb\u3002\u90a3\u6751\u5973\u5ffd\u9053\uff1a\u201c\u4e0d\u6210\uff0c\u7caa\u6c34\u592a\u6d53\uff0c\u4e00\u6d47\u4e0b\u53bb\u82b1\u90fd\u67af\u6b7b\u5566\u3002\u201d\u80e1\u6590\u4e00\u5446\uff0c\u4e0d\u77e5\u6240\u63aa\u3002\u90a3\u6751\u5973\u9053\uff1a\u201c\u4f60\u5012\u56de\u7caa\u6c60\u53bb\uff0c\u53ea\u7559\u4e00\u534a\uff0c\u518d\u53bb\u52a0\u534a\u6876\u6c34\uff0c\u90a3\u4fbf\u6210\u4e86\uff1a\u201d\u80e1\u6590\u5fae\u611f\u4e0d\u8010\uff0c\u4f46\u60f3\u597d\u4eba\u505a\u5230\u5e95\uff0c\u4e8e\u662f\u4f9d\u8a00\u5012\u7caa\u52a0\u6c34\uff0c\u56de\u6765\u6d47\u82b1\u3002 \u3000\u3000\u90a3\u6751\u5973\u9053\uff1a\u201c\u5c0f\u5fc3\u4e9b\uff0c\u7caa\u6c34\u4e0d\u53ef\u78b0\u5230\u82b1\u74e3\u53f6\u5b50\u3002\u201d\u80e1\u6590\u5e94\u9053\uff1a\u201c\u662f\uff01\u201d\u89c1\u90a3\u4e9b\u82b1\u6735\u8272\u4f5c\u6df1\u84dd\uff0c\u5f62\u72b6\u5947\u7279\uff0c\u6bcf\u6735\u82b1\u4fbf\u50cf\u662f\u4e00\u53ea\u978b\u5b50\uff0c\u5e7d\u9999\u6de1\u6de1\uff0c\u4e0d\u77e5\u5176\u540d\uff0c\u5f53\u4e0b\u4e00\u74e2\u4e00\u74e2\u7684\u5c0f\u5fc3\u6d47\u4e86\uff0c\u76f4\u628a\u4e24\u6876\u7caa\u6c34\u5c3d\u6570\u6d47\u5b8c\u3002 \u3000\u3000\u90a3\u6751\u5973\u9053\uff1a\u201c\u55ef\uff0c\u518d\u53bb\u6311\u4e86\u6d47\u4e00\u62c5\u3002\u201d\u80e1\u6590\u7ad9\u76f4\u8eab\u5b50\uff0c\u6e29\u8a00\u9053\uff1a\u201c\u6211\u670b\u53cb\u7b49\u5f97\u5fc3\u7126\u4e86\uff0c\u7b49\u6211\u4ece\u836f\u738b\u5e84\u56de\u6765\uff0c\u518d\u5e2e\u4f60\u6d47\u82b1\u5982\u4f55\uff1f\u201d\u90a3\u6751\u5973\u9053\uff1a\u201c\u4f60\u8fd8\u662f\u5728\u8fd9\u513f\u6d47\u82b1\u7684\u597d\u3002\u6211\u89c1\u4f60\u4eba\u4e0d\u9519\uff0c\u624d\u8981\u4f60\u6311\u7caa\u5462\u3002\u201d \u3000\u3000\u80e1\u6590\u542c\u5979\u8a00\u8bed\u5947\u602a\uff0c\u5fc3\u60f3\u53cd\u6b63\u5df2\u7ecf\u803d\u6401\u4e86\uff0c\u4e5f\u4e0d\u4e89\u5728\u8fd9\u4e00\u523b\u65f6\u5149\uff0c\u4e8e\u662f\u52a0\u5feb\u624b\u811a\uff0c\u6025\u6025\u5fd9\u5fd9\u7684\u53c8\u53bb\u6311\u4e86\u4e00\u62c5\u7caa\u6c34\uff0c\u5c06\u5730\u91cc\u7684\u84dd\u82b1\u5c3d\u6570\u6d47\u4e86\u3002\u8fd9\u65f6\u5915\u9633\u5df2\u843d\u5230\u5c71\u5773\uff0c\u91d1\u5149\u53cd\u7167\uff0c\u5c04\u5728\u4e00\u5927\u7247\u84dd\u82b1\u4e4b\u4e0a\uff0c\u8f89\u714c\u707f\u70c2\uff0c\u751a\u662f\u534e\u7f8e\u3002\u80e1\u6590\u5fcd\u4e0d\u4f4f\u8d5e\u9053\uff1a\u201c\u8fd9\u4e9b\u82b1\u771f\u662f\u597d\u770b\uff01\u201d\u4ed6\u6d47\u4e86\u4e24\u62c5\u7caa\uff0c\u5bf9\u8fd9\u4e9b\u82b1\u5df2\u7565\u751f\u611f\u60c5\uff0c\u8d5e\u7f8e\u7684\u8bed\u6c14\u9887\u4e3a\u771f\u8bda\u3002 \u3000\u3000\u90a3\u6751\u5973\u6b63\u5f85\u8bf4\u8bdd\uff0c\u53ea\u89c1\u953a\u5146\u6587\u9a91\u4e86\u9a6c\u5954\u56de\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u5144\u5f1f\uff0c\u8fd9\u65f6\u5019\u8fd8\u4e0d\u8d70\u5417\uff1f\u201d\u80e1\u6590\u9053\uff1a\u201c\u662f\u4e86\uff0c\u6765\u5566\uff0c\u6765\u5566\uff01\u201d\u8f6c\u773c\u671b\u7740\u6751\u5973\uff0c\u76ee\u5149\u4e2d\u542b\u6709\u7948\u6c42\u4e4b\u610f\u3002 \u3000\u3000\u90a3\u6751\u5973\u8138\u4e00\u6c89\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u5e2e\u6211\u6d47\u82b1\uff0c\u539f\u6765\u662f\u4e3a\u4e86\u8981\u6211\u6307\u70b9\u9014\u5f84\uff0c\u662f\u4e0d\u662f\uff1f\u201d\u80e1\u6590\u5fc3\u60f3\uff1a\u201c\u6211\u786e\u662f\u76fc\u4f60\u6307\u70b9\u9053\u8def\uff0c\u4f46\u5e2e\u4f60\u6d47\u82b1\uff0c\u5374\u7eaf\u662f\u4e3a\u4e86\u601c\u4f60\u7626\u5f31\uff0c\u8fd9\u65f6\u518d\u5f00\u53e3\u76f8\u6c42\uff0c\u53cd\u800c\u53d8\u6210\u6709\u610f\u7684\u65bd\u6069\u5e02\u60e0\u4e86\u3002\u201d\u5ffd\u7136\u60f3\u8d77\u90a3\u65e5\u6349\u4e86\u94c1\u874e\u5b50\u548c\u5c0f\u795d\u878d\u4e8c\u4eba\u53bb\u4ea4\u7ed9\u8881\u7d2b\u8863\uff0c\u5979\u66fe\u8bf4\uff1a\u201c\u8fd9\u53eb\u505a\u5e02\u6069\uff0c\u6700\u574f\u7684\u5bb6\u4f19\u624d\u662f\u5982\u6b64\u3002\u201d \u3000\u3000\u5fc3\u4e2d\u7981\u4e0d\u4f4f\u5fae\u611f\u751c\u610f\uff0c\u5f53\u5373\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e9b\u82b1\u771f\u597d\u770b\uff01\u201d\u8d70\u5230\u67f3\u6811\u65c1\u89e3\u7f30\u7275\u9a6c\uff0c\u4e0a\u4e86\u9a6c\u80cc\u3002 \u3000\u3000\u90a3\u6751\u5973\u9053\uff1a\u201c\u4e14\u6162\u3002\u201d\u80e1\u6590\u56de\u8fc7\u5934\u6765\uff0c\u53ea\u6015\u5979\u8fd8\u8981\u647d\u5506\u4ec0\u4e48\uff0c\u5fc3\u4e2d\u5927\u662f\u4e0d\u8010\u3002\u90a3\u6751\u5973\u62d4\u8d77\u4e24\u68f5\u84dd\u82b1\uff0c\u5411\u4ed6\u63b7\u53bb\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8bf4\u8fd9\u82b1\u597d\u770b\uff0c\u5c31\u9001\u4f60\u4e24\u68f5\u3002\u201d\u80e1\u6590\u4f38\u624b\u63a5\u4f4f\uff0c\u8bf4\u9053\uff1a\u201c\u591a\u8c22\uff01\u201d \u3000\u3000\u987a\u624b\u653e\u5728\u6000\u5185\u3002\u90a3\u6751\u5973\u9053\uff1a\u201c\u4ed6\u59d3\u953a\uff0c\u4f60\u59d3\u4ec0\u4e48\uff1f\u201d\u80e1\u6590\u9053\uff1a\u201c\u6211\u59d3\u80e1\u3002\u201d\u90a3\u6751\u5973\u70b9\u5934\u9053\uff1a\u201c\u4f60\u4eec\u8981\u53bb\u836f\u738b\u5e84\uff0c\u8fd8\u662f\u5411\u4e1c\u5317\u65b9\u53bb\u7684\u597d\u3002\u201d \u3000\u3000\u953a\u5146\u6587\u672c\u662f\u5411\u897f\u5317\u800c\u884c\uff0c\u4e45\u7b49\u80e1\u6590\u4e0d\u6765\uff0c\u5fc3\u4e2d\u70e6\u8e81\uff0c\u8fd9\u624d\u56de\u5934\u5bfb\u6765\uff0c\u542c\u90a3\u6751\u5973\u5982\u6b64\u8bf4\uff0c\u4e0d\u8010\u4e4b\u5fc3\u7acb\u65f6\u5c3d\u53bb\uff0c\u4f4e\u58f0\u7b11\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u771f\u6709\u4f60\u7684\uff0c\u53c8\u514d\u5f97\u505a\u54e5\u54e5\u7684\u591a\u8d70\u51a4\u6789\u8def\u3002\u201d\u80e1\u6590\u5374\u9887\u4e3a\u6000\u7591\uff0c\u6697\u60f3\uff1a\u201c\u5018\u82e5\u836f\u738b\u5e84\u662f\u5728\u4e1c\u5317\u65b9\uff0c\u90a3\u4e48\u76f4\u622a\u4e86\u5f53\u7684\u6307\u70b9\u4fbf\u662f\uff0c\u4e3a\u4ec0\u4e48\u8bf4\u2018\u8fd8\u662f\u5411\u4e1c\u5317\u65b9\u53bb\u7684\u597d\u2019\uff1f\u201d\u4f46\u4e0d\u613f\u518d\u5411\u6751\u5973\u8be2\u95ee\uff0c\u4e8e\u662f\u5f15\u9a6c\u5411\u4e1c\u5317\u800c\u53bb\u3002 \u3000\u3000\u4e24\u4eba\u4e00\u9635\u6025\u9a70\uff0c\u5954\u51fa\u516b\u4e5d\u91cc\uff0c\u524d\u9762\u4e00\u7247\u6e56\u6c34\uff0c\u5df2\u65e0\u53bb\u8def\uff0c\u53ea\u6709\u4e00\u6761\u5c0f\u8def\u901a\u5411\u897f\u65b9\u3002 \u3000\u3000\u953a\u5146\u6587\u9a82\u9053\uff1a\u201c\u8fd9\u4e2b\u5934\u5f53\u771f\u53ef\u6076\uff0c\u4e0d\u80af\u6307\u8def\u90a3\u4e5f\u7f62\u4e86\uff0c\u5374\u53eb\u54b1\u4eec\u5927\u8d70\u9519\u8def\u3002\u56de\u53bb\u65f6\u5f97\u597d\u597d\u6559\u8bad\u5979\u4e00\u987f\u3002\u201d\u80e1\u6590\u4e5f\u662f\u597d\u751f\u5947\u602a\uff0c\u81ea\u601d\u5e76\u672a\u5f97\u7f6a\u4e86\u5979\uff0c\u4f55\u4ee5\u8981\u4f5c\u5f04\u81ea\u5df1\uff0c\u8bf4\u9053\uff1a\u201c\u953a\u4e8c\u54e5\uff0c\u8fd9\u4e61\u4e0b\u59d1\u5a18\u5b9a\u548c\u836f\u738b\u5e84\u6709\u4ec0\u4e48\u5e72\u8fde\u3002\u201d\u953a\u5146\u6587\u9053\uff1a\u201c\u55ef\uff0c\u4f60\u77a7\u51fa\u4ec0\u4e48\u7aef\u502a\u6ca1\u6709\uff1f\u201d\u80e1\u6590\u9053\uff1a\u201c\u5979\u4e00\u53cc\u773c\u73e0\u5b50\u70af\u70af\u6709\u795e\uff0c\u8bf4\u8bdd\u7684\u795e\u6001\uff0c\u4e5f\u4e0d\u50cf\u662f\u6ca1\u89c1\u8fc7\u4e16\u9762\u7684\u4e61\u4e0b\u5973\u5b50\u3002\u201d\u953a\u5146\u6587\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff01\u5979\u7ed9\u4f60\u7684\u90a3\u4e24\u68f5\u82b1\uff0c\u8fd8\u662f\u5feb\u4e9b\u629b\u4e86\u3002\u201d \u3000\u3000\u80e1\u6590\u4ece\u6000\u4e2d\u53d6\u51fa\u84dd\u82b1\uff0c\u53ea\u89c1\u82b1\u5149\u5a07\u8273\uff0c\u5012\u662f\u4e0d\u5fcd\u4fbf\u6b64\u4e22\u5f03\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u5c0f\u4e24\u68f5\u82b1\u513f\uff0c\u60f3\u6765\u4e5f\u65e0\u5927\u788d\uff01\u201d\u4e8e\u662f\u4ecd\u65e7\u653e\u56de\u6000\u4e2d\uff0c\u7eb5\u9a6c\u5411\u897f\u9a70\u53bb\u3002\u953a\u5146\u6587\u5728\u540e\u53eb\u9053\uff1a\u201c\u5582\uff0c\u8fd8\u662f\u5c0f\u5fc3\u4e9b\u597d\u3002\u201d \u3000\u3000\u80e1\u6590\u542b\u7cca\u7b54\u5e94\uff0c\u4e00\u97ad\u5411\u9a6c\u81c0\u62bd\u53bb\uff0c\u5411\u897f\u98de\u5954\u3002\u66ae\u972d\u82cd\u832b\u4e2d\uff0c\u9635\u9635\u5f52\u9e26\u4ece\u5934\u9876\u8d8a\u8fc7\u3002 \u3000\u3000\u7a81\u7136\u4e4b\u95f4\uff0c\u53ea\u89c1\u53f3\u624b\u4fa7\u4e24\u4e2a\u4eba\u4fef\u8eab\u6e56\u8fb9\uff0c\u4f3c\u5728\u559d\u6c34\u3002\u80e1\u6590\u4e00\u52d2\u9a6c\uff0c\u5f85\u8981\u8be2\u95ee\uff0c\u5374\u89c1\u4e24\u4eba\u59cb\u7ec8\u4e0d\u52a8\uff0c\u5fc3\u77e5\u6709\u5f02\uff0c\u8df3\u4e0b\u9a6c\u53bb\uff0c\u53eb\u9053\uff1a\u201c\u52b3\u9a7e\uff01\u201d\u4e24\u4eba\u4ecd\u662f\u4e0d\u52a8\u3002\u953a\u5146\u6587\u4f38\u624b\u4e00\u6273\u4e00\u4eba\u80a9\u5934\uff0c\u90a3\u4eba\u4ef0\u5929\u7ffb\u5012\uff0c\u4f46\u89c1\u4ed6\u53cc\u773c\u7ffb\u767d\uff0c\u65e9\u5df2\u6b7b\u53bb\u591a\u65f6\uff0c\u8138\u4e0a\u6ee1\u662f\u9ed1\u70b9\uff0c\u808c\u8089\u626d\u66f2\u3002\u751a\u662f\u53ef\u6016\uff0c\u518d\u77a7\u53e6\u4e00\u4eba\u65f6\u4e5f\u662f\u5982\u6b64\u3002 \u3000\u3000\u953a\u5146\u6587\u9053\uff1a\u201c\u4e2d\u6bd2\u6b7b\u7684\u3002\u201d\u80e1\u6590\u70b9\u70b9\u5934\uff0c\u89c1\u4e24\u540d\u6b7b\u8005\u8eab\u4e0a\u90fd\u5e26\u7740\u5175\u5200\uff0c\u8bf4\u9053\uff1a\u201c\u6bd2\u624b\u836f\u738b\u7684\u5bf9\u5934\uff1f\u201d\u953a\u5146\u6587\u4e5f\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u4e24\u4eba\u4e0a\u9a6c\u53c8\u884c\uff0c\u8fd9\u65f6\u5929\u8272\u6e10\u9ed1\uff0c\u66f4\u89c9\u524d\u9014\u51f6\u9669\u91cd\u91cd\u3002\u53c8\u884c\u4e00\u7a0b\u3002\u53ea\u89c1\u8def\u65c1\u8349\u6728\u7a00\u758f\uff0c\u8d8a\u662f\u524d\u884c\uff0c\u8349\u6728\u8d8a\u5c11\uff0c\u5230\u540e\u6765\u5730\u4e0b\u5149\u6e9c\u6e9c\u7684\u4e00\u7247\uff0c\u7adf\u662f\u5bf8\u8349\u4e0d\u751f\uff0c\u5927\u6811\u5c0f\u6811\u66f4\u6ca1\u4e00\u68f5\u3002\u80e1\u6590\u5fc3\u4e2d\u8d77\u7591\uff0c\u52d2\u9a6c\u8bf4\u9053\uff1a\u201c\u953a\u4e8c\u54e5\uff0c\u4f60\u77a7\u8fd9\u91cc\u5927\u662f\u53e4\u602a\u3002\u201d\u953a\u5146\u6587\u4e5f\u5df2\u77a7\u51fa\u4e0d\u5bf9\uff0c\u9053\uff1a\u201c\u82e5\u662f\u6709\u4eba\u94f2\u51c0\u5228\u7edd\uff0c\u4e5f\u5fc5\u7559\u4e0b\u8349\u6839\u75d5\u8ff9\uff0c\u6211\u770b\u2026\u2026\u201d\u4ed6\u6c89\u541f\u7247\u523b\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u90a3\u836f\u738b\u5e84\u5b9a\u5728\u5de6\u8fd1\uff0c\u60f3\u662f\u4ed6\u5728\u571f\u4e2d\u4e0b\u4e86\u5267\u6bd2\uff0c\u4ee5\u81f4\u8fde\u8349\u4e5f\u6ca1\u4e00\u6839\u3002\u201d \u3000\u3000\u80e1\u6590\u70b9\u4e86\u70b9\u5934\uff0c\u5fc3\u4e2d\u60ca\u60e7\uff0c\u4ece\u5305\u88b1\u4e0a\u6495\u4e0b\u51e0\u6839\u5e03\u6761\uff0c\u5c06\u953a\u5146\u6587\u6240\u4e58\u5750\u9a91\u7684\u9a6c\u53e3\u7f1a\u4f4f\uff0c\u7136\u540e\u7f1a\u4e0a\u81ea\u5df1\u5750\u9a91\u7684\u9a6c\u53e3\u3002\u953a\u5146\u6587\u77e5\u4ed6\u751f\u6015\u518d\u5411\u524d\u884c\u65f6\u9047\u5230\u6709\u6bd2\u8349\u6728\uff0c\u7272\u53e3\u56bc\u5230\u4fbf\u4e0d\u514d\u9047\u5bb3\uff0c\u70b9\u4e86\u70b9\u5934\uff0c\u6697\u8d5e\u4ed6\u5fc3\u601d\u7ec6\u5bc6\u3002 \u3000\u3000\u884c\u4e0d\u591a\u65f6\uff0c\u8fdc\u8fdc\u671b\u89c1\u4e00\u5ea7\u623f\u5c4b\u3002\u8d70\u5230\u8fd1\u5904\uff0c\u53ea\u89c1\u5c4b\u5b50\u7684\u6a21\u6837\u6781\u662f\u53e4\u602a\uff0c\u4fbf\u4f3c\u662f\u4e00\u5ea7\u5927\u575f\u6a21\u6837\uff0c\u65e0\u95e8\u65e0\u7a97\uff0c\u9ed1\u9edd\u9edd\u7684\u751a\u662f\u9634\u68ee\u53ef\u6016\u3002\u4e24\u4eba\u5747\u60f3\uff1a\u201c\u77a7\u8fd9\u5c4b\u5b50\u7684\u6a21\u6837\uff0c\u90a3\u81ea\u7136\u662f\u836f\u738b\u5e84\u4e86\u3002\u201d\u79bb\u5c4b\u6570\u4e08\uff0c\u6709\u4e00\u6392\u77ee\u77ee\u7684\u5c0f\u6811\u73af\u5c4b\u800c\u751f\uff0c\u6811\u53f6\u4fbf\u4f3c\u79cb\u65e5\u67ab\u53f6\u4e00\u822c\uff0c\u6bb7\u7ea2\u5982\u8840\uff0c\u5728\u66ae\u8272\u4e4b\u4e2d\uff0c\u4ee4\u4eba\u77a7\u7740\u4e0d\u5bd2\u800c\u6817\u3002 \u3000\u3000\u953a\u5146\u6587\u5e73\u751f\u6d6a\u8361\u6c5f\u6e56\uff0c\u4ec0\u4e48\u51f6\u9669\u4e4b\u4e8b\u6ca1\u6709\u89c1\u8fc7\uff1f\u4ed6\u81ea\u5df1\u4e09\u5144\u5f1f\u4fbf\u6253\u626e\u6210\u51f6\u95e8\u4e27\u4e3b\u4e00\u822c\uff0c\u4ee4\u4eba\u89c1\u4e4b\u751f\u754f\uff0c\u4f46\u8fd9\u65f6\u770b\u5230\u8fd9\u822c\u60c5\u666f\uff0c\u5fc3\u4e2d\u4e5f\u4e0d\u7981\u7a81\u7a81\u4e71\u8df3\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u600e\u4e48\u529e\uff1f\u201d\u80e1\u6590\u9053\uff1a\u201c\u54b1\u4eec\u4ee5\u793c\u76f8\u6c42\uff0c\u968f\u673a\u5e94\u53d8\u3002\u201d\u4e8e\u662f\u7eb5\u9a6c\u5411\u524d\uff0c\u884c\u5230\u79bb\u77ee\u6811\u4e1b\u6570\u4e08\u4e4b\u5904\uff0c\u4e0b\u9a6c\u7275\u4e86\u7f30\u7ef3\uff0c\u6717\u58f0\u9053\uff1a\u201c\u9102\u5317\u953a\u5146\u6587\uff0c\u665a\u8f88\u8fbd\u4e1c\u80e1\u6590\uff0c\u7279\u6765\u5411\u836f\u738b\u524d\u8f88\u8bf7\u5b89\u3002\u201d\u8fd9\u4e09\u53e5\u8bdd\u6bcf\u4e00\u5b57\u90fd\u4ece\u4e39\u7530\u9001\u51fa\uff0c\u867d\u7136\u5e76\u4e0d\u5982\u4f55\u54cd\u4eae\uff0c\u4f46\u58f0\u95fb\u91cc\u8bb8\uff0c\u5c4b\u4e2d\u4eba\u5fc5\u81ea\u542c\u5f97\u6e05\u6e05\u695a\u695a\u3002 \u3000\u3000\u8fc7\u4e86\u534a\u664c\uff0c\u5c4b\u4e2d\u7adf\u65e0\u534a\u70b9\u52a8\u9759\u3002\u80e1\u6590\u53c8\u8bf4\u4e86\u4e00\u904d\uff0c\u5706\u5c4b\u4e4b\u4e2d\u4ecd\u662f\u6beb\u65e0\u5e94\u58f0\uff0c\u4fbf\u4f3c\u65e0\u4eba\u5c45\u4f4f\u4e00\u822c\u3002\u80e1\u6590\u53c8\u6717\u58f0\u9053\uff1a\u201c\u91d1\u9762\u4f5b\u82d7\u5927\u4fa0\u4e2d\u6bd2\u53d7\u4f24\uff0c\u6240\u7528\u6bd2\u836f\uff0c\u662f\u5978\u4eba\u81ea\u524d\u8f88\u5904\u76d7\u6765\u3002\u656c\u8bf7\u524d\u8f88\u6148\u60b2\uff0c\u8d50\u4ee5\u89e3\u836f\u3002\u201d \u3000\u3000\u4f46\u4e0d\u8bba\u4ed6\u8bf4\u4ec0\u4e48\uff0c\u5706\u5c4b\u4e4b\u4e2d\u59cb\u7ec8\u5bc2\u65e0\u58f0\u606f\u3002 \u3000\u3000\u8fc7\u4e86\u826f\u4e45\uff0c\u5929\u8272\u66f4\u52a0\u9ed1\u4e86\u3002\u80e1\u6590\u4f4e\u58f0\u9053\uff1a\u201c\u953a\u4e8c\u54e5\uff0c\u600e\u4e48\u529e\uff1f\u201d\u953a\u5146\u6587\u9053\uff1a\u201c\u603b\u4e0d\u6210\u773c\u770b\u82d7\u5927\u4fa0\u778e\u4e86\u53cc\u76ee\uff0c\u54b1\u4eec\u4fbf\u6b64\u7a7a\u624b\u800c\u8fd4\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4fbf\u662f\u9f99\u6f6d\u864e\u7a74\uff0c\u4e5f\u5f97\u95ef\u4e0a\u4e00\u95ef\u3002\u201d \u3000\u3000\u4e24\u4eba\u8fd9\u65f6\u5747\u5df2\u8d77\u4e86\u52a8\u6b66\u7528\u5f3a\u4e4b\u610f\uff0c\u5fc3\u60f3\u90a3\u6bd2\u624b\u836f\u738b\u867d\u7136\u64c5\u4e8e\u4f7f\u6bd2\uff0c\u6b66\u529f\u5374\u672a\u5fc5\u4e86\u5f97\uff0c\u8f6f\u786c\u517c\u65bd\uff0c\u975e\u5f97\u5c06\u89e3\u836f\u53d6\u4e86\u5230\u624b\u4e0d\u53ef\u3002\u4e24\u4eba\u653e\u4e0b\u9a6c\u5339\uff0c\u8d70\u5411\u77ee\u6811\u3002\u53ea\u89c1\u90a3\u4e00\u4e1b\u6811\u751f\u5f97\u679d\u53f6\u7d27\u5bc6\uff0c\u4e0d\u80fd\u7a7f\u8fc7\uff0c\u953a\u5146\u6587\u7eb5\u8eab\u4e00\u8dc3\uff0c\u4fbf\u4ece\u6811\u4e1b\u4e0a\u98de\u8d8a\u8fc7\u53bb\u3002 \u3000\u3000\u4ed6\u8eab\u5728\u534a\u7a7a\uff0c\u9f3b\u4e2d\u731b\u7136\u95fb\u5230\u4e00\u9635\u6d53\u9999\uff0c\u773c\u524d\u4e00\u9ed1\uff0c\u767b\u65f6\u6655\u7729\uff0c\u6454\u8dcc\u5728\u6811\u4e1b\u4e4b\u5185\u3002\u80e1\u6590\u4e00\u89c1\u5927\u60ca\uff0c\u8ddf\u7740\u8dc3\u8fdb\uff0c\u8d8a\u8fc7\u6811\u4e1b\u9876\u4e0a\u65f6\uff0c\u4f46\u89c9\u5947\u9999\u523a\u9f3b\uff0c\u4e2d\u4eba\u6b32\u5455\uff0c\u80f8\u53e3\u751a\u662f\u70e6\u6076\u3002\u4ed6\u4e00\u843d\u5730\uff0c\u5fd9\u4f38\u624b\u6276\u8d77\u953a\u5146\u6587\uff0c\u63a2\u4ed6\u9f3b\u95f4\u5c1a\u6709\u547c\u5438\uff0c\u53ea\u662f\u53cc\u76ee\u7d27\u95ed\uff0c\u624b\u6307\u548c\u989c\u9762\u5374\u662f\u51b0\u51b7\u3002 \u3000\u3000\u80e1\u6590\u6697\u6697\u53eb\u82e6\uff1a\u201c\u82d7\u5927\u4fa0\u7684\u89e3\u836f\u5c1a\u672a\u6c42\u5f97\uff0c\u953a\u4e8c\u54e5\u5374\u53c8\u4e2d\u6bd2\uff0c\u77a7\u6765\u6211\u81ea\u5df1\u4e5f\u5df2\u6cbe\u4e0a\u6bd2\u6c14\uff0c\u53ea\u662f\u8fd8\u6ca1\u53d1\u4f5c\u800c\u5df2\u3002\u201d\u5f53\u4e0b\u8eab\u5f62\u4e00\u77ee\uff0c\u76f4\u7eb5\u5411\u5706\u5c4b\u4e4b\u524d\uff0c\u53eb\u9053\uff1a\u201c\u836f\u738b\u524d\u8f88\uff0c\u665a\u8f88\u7a7a\u624b\u524d\u6765\u62dc\u5e84\uff0c\u5b9e\u65e0\u6b79\u610f\uff0c\u518d\u4e0d\u8d50\u89c1\uff0c\u665a\u8f88\u8feb\u5f97\u65e0\u793c\u4e86\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u4e86\u8fd9\u8bdd\u540e\uff0c\u6253\u91cf\u90a3\u5706\u5c4b\u7684\u5899\u57a3\uff0c\u53ea\u89c1\u81ea\u5c4b\u9876\u4ee5\u81f3\u5899\u811a\u901a\u4f53\u9ed1\u8272\uff0c\u663e\u7136\u5e76\u975e\u4e0a\u6728\u6240\u6784\u3002\u4ed6\u4e0d\u6562\u4f38\u624b\u53bb\u63a8\uff0c\u4f46\u56db\u4e0b\u5730\u91cc\u6253\u626b\u5f97\u5e72\u51c0\u65e0\u6bd4\uff0c\u8fde\u4e00\u5757\u6781\u7ec6\u5c0f\u7684\u7816\u77f3\u4e5f\u65e0\u6cd5\u627e\u5230\uff0c\u4e8e\u662f\u4ece\u6000\u4e2d\u6478\u51fa\u4e00\u952d\u94f6\u4e24\uff0c\u5728\u5899\u4e0a\u8f7b\u6572\u4e09\u4e0b\uff0c\u679c\u7136\u94ee\u94ee\u94ee\u7684\u53d1\u51fa\u91d1\u5c5e\u4e4b\u58f0\u3002 \u3000\u3000\u4ed6\u5c06\u94f6\u4e24\u653e\u56de\u6000\u4e2d\uff0c\u4e00\u4f4e\u5934\uff0c\u9f3b\u4e2d\u5ffd\u7136\u95fb\u5230\u4e00\u9635\u6de1\u6de1\u6e05\u9999\uff0c\u7cbe\u795e\u4e3a\u4e4b\u4e00\u632f\uff0c\u5934\u8111\u672c\u6765\u660f\u660f\u6c89\u6c89\uff0c\u4e00\u95fb\u5230\u8fd9\u9999\u6c14\uff0c\u7acb\u65f6\u6e05\u660e\u3002\u4ed6\u7565\u7565\u5f2f\u8170\uff0c\u9999\u6c14\u66f4\u6d53\uff0c\u539f\u6765\u8fd9\u9999\u6c14\u662f\u4ece\u90a3\u6751\u5973\u6240\u8d60\u7684\u84dd\u82b1\u4e0a\u53d1\u51fa\u3002\u80e1\u6590\u5fc3\u4e2d\u4e00\u52a8\uff1a\u201c\u770b\u6765\u8fd9\u9999\u6c14\u6709\u89e3\u6bd2\u4e4b\u529f\uff0c\u5979\u679c\u7136\u662f\u4e00\u756a\u597d\u610f\u3002\u201d \u3000\u3000\u4ed6\u52a0\u5feb\u811a\u6b65\uff0c\u73af\u7ed5\u5706\u5c4b\u5954\u4e86\u4e00\u5468\uff0c\u975e\u4f46\u627e\u4e0d\u5230\u95e8\u7a97\uff0c\u8fde\u5c0f\u5b54\u548c\u7ec6\u7f1d\u4e5f\u6ca1\u53d1\u89c1\uff0c\u5fc3\u60f3\u96be\u9053\u5c4b\u4e2d\u5f53\u771f\u5e76\u65e0\u4eba\u5c45\uff1f\u5426\u5219\u6beb\u65e0\u901a\u98ce\u4e4b\u5904\uff0c\u600e\u80fd\u4e0d\u7ed9\u95f7\u6b7b\uff1f\u4ed6\u624b\u4e2d\u6ca1\u6709\u5175\u5203\uff0c\u5bf9\u8fd9\u901a\u4f53\u94c1\u94f8\u7684\u5706\u5c4b\u5b9e\u5728\u65e0\u6cd5\u53ef\u65bd\u3002\u51dd\u601d\u7247\u523b\uff0c\u4ece\u6000\u4e2d\u53d6\u51fa\u84dd\u82b1\uff0c\u653e\u5728\u953a\u5146\u6587\u9f3b\u4e0b\uff0c\u8fc7\u4e0d\u591a\u65f6\uff0c\u679c\u7136\u4ed6\u6253\u4e86\u4e2a\u55b7\u568f\uff0c\u60a0\u60a0\u9192\u8f6c\u3002 \u3000\u3000\u80e1\u6590\u5927\u559c\uff0c\u5fc3\u9053\uff1a\u201c\u90a3\u59d1\u5a18\u65e2\u6709\u89e3\u6bd2\u4e4b\u6cd5\uff0c\u4e0d\u5982\u56de\u53bb\u6c42\u5979\u6307\u70b9\u3002\u201d\u4e8e\u662f\u5c06\u4e00\u679d\u84dd\u82b1\u63d2\u5728\u953a\u5146\u6587\u895f\u4e0a\uff0c\u81ea\u5df1\u624b\u4e2d\u62ff\u4e86\u4e00\u679d\uff0c\u6276\u7740\u953a\u5146\u6587\u8dc3\u8fc7\u77ee\u6811\u3002\u4ed6\u53cc\u8db3\u843d\u5730\uff0c\u5ffd\u542c\u5f97\u5706\u5c4b\u4e2d\u6709\u4eba\u5927\u58f0\u201c\u54a6\uff01\u201d\u7684\u4e00\u4e0b\u60ca\u547c\u3002\u58f0\u97f3\u9694\u7740\u94c1\u58c1\u4f20\u6765\uff0c\u9887\u4e3a\u90c1\u95f7\uff0c\u4f46\u4ecd\u53ef\u542c\u5f97\u51fa\u53c8\u662f\u60ca\u5947\u53c8\u662f\u6124\u6012\u4e4b\u610f\u3002 \u3000\u3000\u80e1\u6590\u56de\u5934\u53eb\u9053\uff1a\u201c\u836f\u738b\u524d\u8f88\uff0c\u53ef\u80af\u8d50\u89c1\u4e00\u9762\u4e48\uff1f\u201d\u5706\u5c4b\u4e2d\u5bc2\u7136\u65e0\u58f0\u3002\u4ed6\u63a5\u8fde\u95ee\u4e86\u4e24\u58f0\uff0c\u5bf9\u65b9\u518d\u65e0\u58f0\u606f\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u7830\u7830\u4e24\u54cd\uff0c\u91cd\u7269\u5012\u5730\u3002\u80e1\u6590\u56de\u8fc7\u5934\u6765\uff0c\u53ea\u89c1\u4e24\u5339\u5750\u9a91\u540c\u65f6\u6454\u5012\uff0c\u7eb5\u8eab\u8fc7\u53bb\u4e00\u77a7\uff0c\u4e24\u5339\u9a6c\u773c\u76ee\u7d27\u95ed\uff0c\u53e3\u5410\u9ed1\u6cab\uff0c\u5df2\u7136\u4e2d\u6bd2\u65ad\u6c14\uff0c\u8eab\u4e0a\u5374\u6ca1\u534a\u70b9\u4f24\u75d5\u3002 \u3000\u3000\u5230\u6b64\u5730\u6b65\uff0c\u4e24\u4eba\u4e0d\u6562\u518d\u5728\u8fd9\u9669\u5730\u591a\u9017\u7559\uff0c\u4f4e\u58f0\u5546\u91cf\u4e86\u51e0\u53e5\uff0c\u51b3\u610f\u56de\u53bb\u5411\u6751\u5973\u6c42\u6559\uff0c\u4e8e\u662f\u4ece\u539f\u8def\u8d76\u56de\u3002 \u3000\u3000\u953a\u5146\u6587\u4e2d\u6bd2\u540e\u811a\u529b\u75b2\u60eb\uff0c\u884c\u4e00\u7a0b\u6b47\u4e00\u7a0b\uff0c\u76f4\u5230\u4e8c\u66f4\u65f6\u5206\uff0c\u624d\u56de\u5230\u90a3\u6751\u5973\u7684\u8305\u5c4b\u4e4b\u524d\u3002\u9ed1\u591c\u4e4b\u4e2d\uff0c\u82b1\u5703\u4e2d\u7684\u84dd\u82b1\u9999\u6c14\u99a5\u90c1\uff0c\u953a\u80e1\u4e8c\u4eba\u4e00\u95fb\u4e4b\u4e0b\uff0c\u56f0\u7d2f\u5c3d\u53bb\uff0c\u5927\u611f\u6109\u9002\u3002 \u3000\u3000\u53ea\u89c1\u8305\u820d\u7684\u7a97\u4e2d\u7a81\u7136\u900f\u51fa\u706f\u5149\uff0c\u5440\u7684\u4e00\u58f0\uff0c\u67f4\u6249\u6253\u5f00\uff0c\u90a3\u6751\u5973\u5f00\u95e8\u51fa\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u8bf7\u8fdb\u6765\u5427\uff01\u53ea\u662f\u4e61\u4e0b\u6ca1\u4ec0\u4e48\u6b3e\u5f85\uff0c\u7c97\u8336\u6de1\u996d\uff0c\u6020\u6162\u4e86\u8d35\u5ba2\u3002\u201d\u80e1\u6590\u542c\u5979\u51fa\u8a00\u4e0d\u4fd7\uff0c\u5fd9\u62b1\u62f3\u9053\uff1a\u201c\u6df1\u591c\u53e8\u6270\uff0c\u5f88\u662f\u8fc7\u610f\u4e0d\u53bb\u3002\u201d\u90a3\u6751\u5973\u5fae\u5fae\u4e00\u7b11\uff0c\u95ea\u8eab\u95e8\u65c1\uff0c\u8ba9\u4e24\u4eba\u8fdb\u5c4b\u3002 \u3000\u3000\u80e1\u6590\u8e0f\u8fdb\u8305\u5c4b\uff0c\u89c1\u5c4b\u4e2d\u6728\u684c\u6728\u51f3\uff0c\u9648\u8bbe\u4e5f\u8ddf\u5bfb\u5e38\u519c\u5bb6\u65e0\u5f02\uff0c\u53ea\u662f\u7ea4\u5c18\u4e0d\u67d3\uff0c\u5e72\u51c0\u5f97\u8fc7\u4e86\u4efd\uff0c\u751a\u81f3\u8fde\u5899\u811a\u4e4b\u4e0b\uff0c\u677f\u58c1\u7f1d\u4e2d\uff0c\u4e5f\u51b2\u6d17\u5f97\u6ca1\u7559\u4e0b\u534a\u70b9\u7070\u571f\u3002\u8fd9\u822c\u6e05\u6d01\u7684\u6a21\u6837\uff0c\u4fbf\u4f3c\u5706\u5c4b\u5468\u906d\u4e00\u822c\uff0c\u4ee4\u4eba\u5fc3\u4e2d\u9690\u9690\u4e0d\u5b89\u3002 \u3000\u3000\u90a3\u6751\u5973\u9053\uff1a\u201c\u953a\u7237\u3001\u80e1\u7237\u8bf7\u5750\u3002\u201d\u8bf4\u7740\u5230\u53a8\u4e0b\u62ff\u51fa\u4e24\u526f\u7897\u7b77\uff0c\u8ddf\u7740\u6258\u51fa\u4e09\u83dc\u4e00\u6c64\uff0c\u4e24\u5927\u7897\u70ed\u6c14\u817e\u817e\u7684\u767d\u7c73\u996d\u3002\u4e09\u7897\u83dc\u662f\u714e\u8c46\u8150\u3001\u9c9c\u7b0b\u7092\u8c46\u82bd\u3001\u8349\u83c7\u716e\u767d\u83dc\uff0c\u90a3\u6c64\u5219\u662f\u54b8\u83dc\u8c46\u74e3\u6c64\u3002 \u3000\u3000\u867d\u662f\u7d20\u83dc\uff0c\u5374\u4e5f\u9999\u6c14\u6251\u9f3b\u3002 \u3000\u3000\u4e24\u4eba\u5954\u9a70\u4e86\u5927\u534a\u65e5\uff0c\u65e9\u5c31\u997f\u4e86\u3002\u80e1\u6590\u7b11\u9053\uff1a\u201c\u591a\u8c22\uff01\u201d\u7aef\u8d77\u996d\u7897\uff0c\u63d0\u7b77\u4fbf\u5403\u3002\u953a\u5146\u6587\u5fc3\u4e0b\u5927\u7591\uff0c\u5bfb\u601d\uff1a\u201c\u8fd9\u996d\u83dc\u5979\u65e9\u5c31\u9884\u5907\u597d\u4e86\uff0c\u663e\u662f\u6599\u5230\u6211\u4eec\u53bb\u540e\u5fc5\u56de\u3002\u5b81\u53ef\u997f\u6b7b\u4e86\uff0c\u8fd9\u996d\u5374\u5343\u4e07\u5403\u4e0d\u5f97\u3002\u201d\u89c1\u90a3\u6751\u5973\u8f6c\u8eab\u56de\u5165\u53a8\u4e0b\uff0c\u5411\u80e1\u6590\u4f7f\u4e2a\u773c\u8272\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5144\u5f1f\uff0c\u6211\u8ddf\u4f60\u8bf4\u8fc7\uff0c\u5728\u836f\u738b\u5e84\u4e09\u5341\u91cc\u5730\u4e4b\u5185\uff0c\u51b3\u4e0d\u80fd\u996e\u98df\u3002\u4f60\u600e\u5730\u5fd8\u4e86\uff1f\u201d \u3000\u3000\u80e1\u6590\u5374\u60f3\uff1a\u201c\u8fd9\u4f4d\u59d1\u5a18\u5bf9\u6211\u82e5\u6709\u6b79\u5fc3\uff0c\u51b3\u4e0d\u80fd\u9001\u82b1\u7ed9\u6211\u3002 \u3000\u3000\u867d\u7136\u9632\u4eba\u4e4b\u5fc3\u4e0d\u53ef\u65e0\uff0c\u4f46\u82e5\u662f\u4e0d\u5403\u6b64\u9910\uff0c\u90a3\u5b9a\u662f\u5c06\u5979\u5f97\u7f6a\u4e86\u3002\u201d \u3000\u3000\u4ed6\u6b63\u8981\u56de\u7b54\uff0c\u90a3\u6751\u5973\u53c8\u4ece\u53a8\u4e0b\u6258\u51fa\u4e00\u53ea\u6728\u76d8\uff0c\u76d8\u4e2d\u4e00\u53ea\u5c0f\u5c0f\u6728\u6876\uff0c\u88c5\u6ee1\u4e86\u767d\u996d\u3002 \u3000\u3000\u80e1\u6590\u7ad9\u8d77\u8eab\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u59d1\u5a18\u539a\u5f85\uff0c\u6211\u4eec\u8981\u8bf7\u62dc\u89c1\u4ee4\u5c0a\u4ee4\u5802\u3002\u201d\u90a3\u6751\u5973\u9053\uff1a\u201c\u6211\u7239\u5988\u90fd\u8fc7\u4e16\u4e86\uff0c\u8fd9\u91cc\u4fbf\u53ea\u6211\u4e00\u4eba\u3002\u201d \u3000\u3000\u80e1\u6590\u201c\u554a\u201d\u4e86\u4e00\u58f0\uff0c\u5750\u4e0b\u6765\u4e3e\u7b77\u4fbf\u5403\uff0c\u4e09\u7897\u83dc\u80b4\u505a\u5f97\u672c\u81ea\u9c9c\u7f8e\uff0c\u80e1\u6590\u4e3a\u8ba8\u5979\u559c\u6b22\uff0c\u66f4\u662f\u8d5e\u4e0d\u7edd\u53e3\u3002 \u3000\u3000\u953a\u5146\u6587\u5fc3\u60f3\uff1a\u201c\u4f60\u65e2\u4e0d\u542c\u6211\u529d\uff0c\u90a3\u4e5f\u65e0\u6cd5\uff0c\u603b\u4e0d\u6210\u4e24\u4e2a\u4e00\u9f50\u7740\u4e86\u4eba\u5bb6\u9053\u513f\u3002\u201d\u5411\u90a3\u6751\u5973\u9053\uff1a\u201c\u6211\u9002\u624d\u6655\u53bb\u591a\u65f6\uff0c\u809a\u5b50\u91cc\u5f88\u4e0d\u8212\u670d\uff0c\u4e0d\u60f3\u5403\u996d\u3002\u201d\u90a3\u6751\u5973\u659f\u4e86\u4e00\u676f\u8336\u6765\uff0c\u9053\uff1a\u201c\u90a3\u4e48\u8bf7\u7528\u4e00\u676f\u6e05\u8336\u3002\u201d\u953a\u5146\u6587\u89c1\u8336\u6c34\u78a7\u7eff\uff0c\u6e05\u6f88\u53ef\u7231\uff0c\u867d\u7136\u53e3\u4e2d\u5927\u611f\u5e72\u6e34\uff0c\u4ecd\u7136\u8c22\u4e86\u4e00\u58f0\uff0c\u63a5\u8fc7\u8336\u676f\u653e\u5728\u684c\u4e0a\uff0c\u5374\u4e0d\u996e\u7528\u3002 \u3000\u3000\u6751\u5973\u4e5f\u4e0d\u4e3a\u610f\uff0c\u89c1\u80e1\u6590\u72fc\u541e\u864e\u54bd\uff0c\u5403\u4e86\u4e00\u7897\u53c8\u4e00\u7897\uff0c\u4e0d\u7531\u5f97\u7709\u68a2\u773c\u89d2\u4e4b\u95f4\u9887\u9732\u559c\u8272\u3002\u80e1\u6590\u77a7\u5728\u773c\u91cc\uff0c\u5fc3\u60f3\u6211\u53cd\u6b63\u5403\u4e86\uff0c\u5c11\u5403\u82e5\u662f\u4e2d\u6bd2\uff0c\u591a\u5403\u4e5f\u662f\u4e2d\u6bd2\uff0c\u7d22\u6027\u653e\u5f00\u809a\u5b50\uff0c\u5403\u4e86\u56db\u5927\u7897\u767d\u7c73\u996d\uff0c\u5c06\u4e09\u83dc\u4e00\u6c64\u5403\u5f97\u5c3d\u662f\u7897\u5e95\u671d\u5929\u3002\u6751\u5973\u8fc7\u6765\u6536\u62fe\uff0c\u80e1\u6590\u62a2\u7740\u628a\u7897\u7b77\u653e\u5728\u76d8\u4e2d\uff0c\u6258\u5230\u53a8\u4e0b\uff0c\u968f\u624b\u4fbf\u5728\u6c34\u7f38\u4e2d\u8200\u4e86\u6c34\uff0c\u5c06\u7897\u7b77\u6d17\u5e72\u51c0\u4e86\uff0c\u62b9\u5e72\u653e\u5165\u6a71\u4e2d\u3002 \u3000\u3000\u90a3\u6751\u5973\u6d17\u956c\u626b\u5730\uff0c\u4e24\u4eba\u4e00\u9f50\u52a8\u624b\u6536\u62fe\u3002\u80e1\u6590\u4e5f\u4e0d\u63d0\u8d77\u9002\u624d\u4e4b\u4e8b\uff0c\u89c1\u6c34\u7f38\u4e2d\u53ea\u5269\u4e0b\u4e86\u5c0f\u534a\u7f38\u6c34\uff0c\u62ff\u4e86\u6c34\u6876\uff0c\u5230\u95e8\u5916\u5c0f\u6eaa\u4e2d\u6311\u4e86\u4e24\u62c5\uff0c\u5c06\u6c34\u7f38\u88c5\u5f97\u6ee1\u6ee1\u3002 \u3000\u3000\u6311\u5b8c\u4e86\u6c34\u56de\u5230\u5802\u4e0a\uff0c\u89c1\u953a\u5146\u6587\u5df2\u4f0f\u5728\u684c\u4e0a\u7761\u4e86\u3002\u90a3\u6751\u5973\u9053\uff1a\u201c\u4e61\u4e0b\u4eba\u5bb6\uff0c\u6ca1\u5f85\u5ba2\u7684\u5730\u65b9\uff0c\u53ea\u597d\u59d4\u5c48\u80e1\u7237\uff0c\u80e1\u4e71\u5728\u957f\u51f3\u4e0a\u7761\u4e00\u665a\u5427\uff01\u201d\u80e1\u6590\u9053\uff1a\u201c\u59d1\u5a18\u4e0d\u7528\u5ba2\u6c14\uff01\u201d\u53ea\u89c1\u5979\u8d70\u8fdb\u5185\u5ba4\uff0c\u8f7b\u8f7b\u5c06\u623f\u95e8\u5173\u4e0a\uff0c\u5374\u6ca1\u542c\u89c1\u843d\u95e9\u4e4b\u58f0\uff0c\u5fc3\u60f3\u8fd9\u4e2a\u59d1\u5a18\u5b64\u96f6\u96f6\u7684\u72ec\u5c45\u4e8e\u6b64\uff0c\u7adf\u6562\u8ba9\u4e24\u4e2a\u7537\u5b50\u6c49\u5728\u5c4b\u4e2d\u7559\u5bbf\uff0c\u80c6\u5b50\u5374\u662f\u4e0d\u5c0f\uff0c\u4f38\u624b\u8f7b\u63a8\u953a\u5146\u6587\u7684\u80a9\u8180\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u953a\u4e8c\u54e5\uff0c\u5728\u957f\u51f3\u4e0a\u7761\u5f97\u8212\u670d\u4e9b\uff01\u201d \u3000\u3000\u54ea\u77e5\u8fd9\u4e48\u8f7b\u8f7b\u4e00\u63a8\uff0c\u953a\u5146\u6587\u7adf\u5e94\u624b\u800c\u5012\uff0c\u7830\u7684\u4e00\u58f0\uff0c\u8dcc\u5728\u5730\u4e0b\u3002\u80e1\u6590\u5927\u5403\u4e00\u60ca\uff0c\u6025\u5fd9\u62b1\u7740\u4ed6\u8170\u6276\u8d77\uff0c\u5728\u4ed6\u8138\u4e0a\u4e00\u6478\uff0c\u7740\u624b\u706b\u6eda\uff0c\u7adf\u662f\u53d1\u7740\u9ad8\u70e7\u3002\u80e1\u6590\u5fd9\u9053\uff1a\u201c\u953a\u4e8c\u54e5\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u201d \u3000\u3000\u4e3e\u6cb9\u706f\u51d1\u8fd1\u77a7\u65f6\uff0c\u53ea\u89c1\u4ed6\u6ee1\u8138\u901a\u7ea2\uff0c\u5b9b\u4f3c\u9152\u9189\uff0c\u53e3\u4e2d\u9f3b\u4e2d\u66f4\u55b7\u51fa\u9635\u9635\u6781\u6d53\u7684\u9152\u6c14\u3002\u80e1\u6590\u5927\u5947\uff1a\u201c\u4ed6\u8fde\u8336\u4e5f\u4e0d\u6562\u559d\u4e00\u53e3\uff0c\u600e\u4e48\u8fd9\u4e00\u970e\u65f6\u4e4b\u95f4\uff0c\u7adf\u4f1a\u9189\u5012\uff1f\u201d\u53c8\u542c\u4ed6\u8ff7\u8ff7\u7cca\u7cca\u9053\uff1a\u201c\u6211\u6ca1\u9189\uff0c\u6ca1\u6709\u9189\uff01\u6765\u6765\u6765\uff0c\u8ddf\u4f60\u518d\u559d\u4e09\u5927\u7897\uff01\u201d\u8ddf\u7740\u201c\u4e94\u7ecf\u9b41\u9996\uff01\u201d\u201c\u56db\u5b63\u53d1\u8d22\uff01\u201d\u7684\u8c41\u8d77\u62f3\u6765\u3002 \u3000\u3000\u80e1\u6590\u4e00\u8f6c\u5ff5\uff0c\u77e5\u4ed6\u5b9a\u662f\u7740\u4e86\u90a3\u6751\u5973\u7684\u624b\u811a\uff0c\u4ed6\u4e0d\u80af\u5403\u996d\u996e\u8336\uff0c\u90a3\u6751\u5973\u5374\u7528\u4ec0\u4e48\u5947\u5999\u6cd5\u95e8\uff0c\u5f04\u5f97\u4ed6\u4fbf\u4f3c\u5927\u9189\u4e00\u822c\uff0c\u5fc3\u4e2d\u60ca\u5947\u4ea4\u96c6\uff0c\u4e0d\u77e5\u662f\u53bb\u6c42\u90a3\u6751\u5973\u6551\u6cbb\u5462\uff0c\u8fd8\u662f\u8ba9\u4ed6\u987a\u5176\u81ea\u7136\uff0c\u6162\u6162\u9192\u8f6c\uff0c\u8f6c\u5ff5\u53c8\u60f3\uff1a\u201c\u8fd9\u662f\u4e2d\u6bd2\uff0c\u5e76\u975e\u771f\u7684\u9152\u9189\uff0c\u672a\u5fc5\u4fbf\u80fd\u81ea\u884c\u6e05\u9192\u3002\u201d \u3000\u3000\u6b63\u5728\u6b64\u65f6\uff0c\u5ffd\u542c\u8fdc\u5904\u4f20\u6765\u4e00\u9635\u9635\u60e8\u5389\u7684\u91ce\u517d\u55e5\u53eb\u4e4b\u58f0\uff0c\u6df1\u591c\u542c\u6765\uff0c\u4e0d\u7531\u5f97\u4ee4\u4eba\u5bd2\u6bdb\u76f4\u7ad6\uff0c\u542c\u58f0\u97f3\u4f3c\u662f\u72fc\u55e5\uff0c\u4f46\u6d1e\u5ead\u6e56\u7554\u591a\u662f\u5e73\u539f\uff0c\u7eb5\u6709\u4e00\u4e8c\u91ce\u72fc\uff0c\u4e5f\u4e0d\u81f4\u5982\u8fd9\u822c\u6210\u7fa4\u7ed3\u961f\u3002 \u3000\u3000\u90a3\u58f0\u97f3\u6e10\u53eb\u6e10\u8fd1\uff0c\u80e1\u6590\u7ad9\u8d77\u8eab\u6765\uff0c\u4fa7\u8033\u51dd\u542c\uff0c\u53ea\u542c\u5f97\u72fc\u55e5\u4e4b\u4e2d\uff0c\u8fd8\u5939\u7740\u4e00\u4e8c\u58f0\u5c71\u7f8a\u7684\u54a9\u54a9\u4e4b\u58f0\uff0c\u663e\u7136\u662f\u72fc\u7fa4\u8ffd\u7f8a\u800c\u566c\u3002\u5f53\u4e0b\u4e5f\u4e0d\u4ee5\u4e3a\u610f\uff0c\u6b63\u60f3\u518d\u53bb\u5bdf\u770b\u953a\u5146\u6587\u7684\u60c5\u72b6\uff0c\u5440\u7684\u4e00\u58f0\uff0c\u623f\u95e8\u63a8\u5f00\uff0c\u90a3\u6751\u5973\u624b\u6301\u70db\u53f0\uff0c\u8d70\u4e86\u51fa\u6765\uff0c\u8138\u4e0a\u7565\u73b0\u60ca\u60f6\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u72fc\u53eb\u554a\u3002\u201d\u80e1\u6590\u70b9\u4e86\u70b9\u5934\uff0c\u9053\uff1a\u201c\u59d1\u5a18\u2026\u2026\u201d\u5411\u953a\u5146\u6587\u4e00\u6307\u3002 \u3000\u3000\u53ea\u542c\u5f97\u9a6c\u8e44\u58f0\u3001\u7f8a\u54a9\u58f0\u3001\u72fc\u55e5\u58f0\u5435\u6210\u4e00\u7247\uff0c\u7adf\u662f\u76f4\u5954\u8fd9\u8305\u5c4b\u800c\u6765\u3002\u80e1\u6590\u8138\u4e0a\u53d8\u8272\uff0c\u5fc3\u60f3\u82e5\u662f\u654c\u4eba\u5927\u4e3e\u6765\u88ad\uff0c\u8fd9\u8305\u5c4b\u4e0d\u7ecf\u4e00\u51b2\uff0c\u4f55\u51b5\u953a\u4e8c\u54e5\u4e2d\u6bd2\u540e\u4eba\u4e8b\u4e0d\u77e5\uff0c\u8fd9\u6751\u5973\u5904\u5728\u8098\u814b\u4e4b\u65c1\uff0c\u662f\u654c\u662f\u53cb\uff0c\u8eab\u5206\u4e0d\u660e\uff0c\u8fd9\u4fbf\u5982\u4f55\u662f\u597d\uff1f\u8f6c\u5ff5\u672a\u6bd5\uff0c\u53ea\u542c\u5f97\u4e00\u9a91\u5feb\u9a6c\u6025\u9a70\u800c\u81f3\u3002\u80e1\u6590\u624b\u65e0\u5bf8\u94c1\uff0c\u5f2f\u8170\u62b1\u8d77\u953a\u5146\u6587\uff0c\u51b2\u8fdb\u53a8\u623f\uff0c\u60f3\u8981\u627e\u67c4\u83dc\u5200\uff0c\u9ed1\u6697\u4e2d\u5374\u53c8\u6478\u7d22\u4e0d\u5230\uff0c\u53ea\u542c\u90a3\u6751\u5973\u5927\u58f0\u53eb\u9053\uff1a\u201c\u662f\u5b5f\u5bb6\u7684\u4eba\u4e48\uff1f\u534a\u591c\u4e09\u66f4\u5230\u8fd9\u91cc\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u80e1\u6590\u542c\u5979\u53e3\u6c14\u4e25\u5389\uff0c\u4e0d\u4f3c\u4f5c\u4f2a\uff0c\u770b\u6765\u5979\u4e0e\u6765\u88ad\u4e4b\u4eba\u5e76\u975e\u4e00\u8def\uff0c\u5fc3\u4e2d\u7a0d\u6170\uff0c\u5f53\u4e0b\u62a2\u51fa\u540e\u9662\uff0c\u5728\u5730\u4e0a\u6293\u8d77\u4e00\u628a\u7816\u77f3\uff0c\u7eb5\u8eab\u4e0a\u4e86\u4e00\u682a\u67f3\u6811\uff0c\u5c06\u953a\u5146\u6587\u6401\u5728\u4e24\u4e2a\u5927\u6860\u679d\u4e4b\u95f4\uff0c\u51dd\u76ee\u671b\u53bb\u3002 \u3000\u3000\u661f\u5149\u4e0b\u53ea\u89c1\u4e00\u4e2a\u7070\u8863\u6c49\u5b50\u9a91\u5728\u9a6c\u4e0a\uff0c\u5df2\u51b2\u5230\u4e86\u8305\u5c4b\u4e4b\u524d\uff0c\u9a6c\u540e\u5c18\u571f\u98de\u626c\uff0c\u53eb\u58f0\u5927\u4f5c\uff0c\u8ddf\u7740\u5341\u51e0\u5934\u997f\u72fc\u3002\u77a7\u8fd9\u60c5\u52bf\uff0c\u4f3c\u4e4e\u90a3\u4eba\u9014\u4e2d\u9047\u5230\u997f\u72fc\u88ad\u51fb\uff0c\u7eb5\u9a6c\u5954\u9003\uff0c\u4f46\u518d\u4e00\u770b\uff0c\u53ea\u89c1\u9a6c\u540e\u62d6\u7740\u767d\u767d\u7684\u4e00\u56e2\u4e1c\u897f\uff0c\u539f\u6765\u662f\u53ea\u6d3b\u7f8a\uff0c\u80e1\u6590\u5fc3\u60f3\uff0c\u8fd9\u591a\u534a\u662f\u4e2a\u730e\u4eba\uff0c\u4ee5\u7f8a\u4e3a\u9975\uff0c\u8bbe\u8ba1\u8bf1\u6355\u72fc\u7fa4\u3002\u5374\u89c1\u90a3\u4eba\u7eb5\u9a6c\u9a70\u5165\u82b1\u5703\uff0c\u76f4\u5954\u5230\u4e1c\u9996\uff0c\u5708\u8f6c\u9a6c\u5934\uff0c\u53c8\u5411\u897f\u9a70\u6765\uff0c\u4e00\u7fa4\u997f\u72fc\u5728\u540e\u8ffd\u53eb\uff0c\u8fd9\u4e48\u4e00\u6765\u4e00\u53bb\uff0c\u767b\u65f6\u5c06\u82b1\u5703\u8df5\u8e0f\u5f97\u4e0d\u6210\u6a21\u6837\u3002\u8fd9\u6c49\u5b50\u7684\u5750\u9a91\u751a\u662f\u9a8f\u826f\uff0c\u4ed6\u9a91\u672f\u53c8\u7cbe\uff0c\u6765\u56de\u51b2\u4e86\u51e0\u6b21\uff0c\u997f\u72fc\u59cb\u7ec8\u54ac\u4e0d\u5230\u6d3b\u7f8a\u3002 \u3000\u3000\u80e1\u6590\u4e00\u8f6c\u5ff5\u95f4\uff0c\u5df2\u7136\u7701\u609f\uff1a\u201c\u554a\uff0c\u8fd9\u5bb6\u4f19\u662f\u6765\u8e29\u574f\u84dd\u82b1\uff01 \u3000\u3000\u6211\u5982\u4f55\u80fd\u8896\u624b\u4e0d\u7406\uff1f\u201d\u5f53\u4e0b\u53cc\u8db3\u4e00\u70b9\uff0c\u8dc3\u5230\u4e86\u8305\u5c4b\u9876\u4e0a\uff0c\u5ffd\u542c\u90a3\u4eba\u201c\u54ce\u54df\uff01\u201d\u4e00\u58f0\u53eb\uff0c\u7eb5\u9a6c\u5411\u5317\u75be\u9a70\u800c\u53bb\uff0c\u90a3\u6d3b\u7f8a\u5374\u7559\u5728\u82b1\u5703\u4e4b\u4e2d\u3002\u7fa4\u72fc\u6251\u4e0a\u53bb\u62a2\u54ac\u6495\u593a\uff0c\u66f4\u5c06\u82b1\u5703\u8e42\u8e8f\u5f97\u72fc\u85c9\u4e0d\u582a\u3002 \u3000\u3000\u80e1\u6590\u5fc3\u9053\uff1a\u201c\u90a3\u4eba\u7528\u5fc3\u597d\u4e0d\u6b79\u6bd2\uff01\u201d\u4e24\u5757\u77f3\u5b50\u98de\u51fa\uff0c\u5657\u5657\u4e24\u58f0\uff0c\u6253\u5728\u4e24\u5934\u6076\u72fc\u8111\u95e8\u6b63\u4e2d\uff0c\u767b\u65f6\u8111\u6d46\u8ff8\u88c2\uff0c\u5c38\u6a2a\u5c31\u5730\u3002\u4ed6\u8ddf\u7740\u53c8\u6253\u51fa\u4e24\u5757\u77f3\u5b50\uff0c\u8fd9\u4e00\u6b21\u77f3\u5b50\u8f83\u5c0f\uff0c\u51c6\u5934\u4e5f\u7565\u504f\u4e86\u4e9b\uff0c\u4e00\u4e2d\u72fc\u8179\uff0c\u4e00\u4e2d\u72fc\u80a9\uff0c\u4f46\u5c3d\u7ba1\u5982\u6b64\uff0c\u4e24\u5934\u6076\u72fc\u4e5f\u5df2\u75db\u5f97\u55f7\u55f7\u5927\u53eb\u3002\u7fa4\u72fc\u8fde\u5403\u82e6\u5934\uff0c\u77e5\u9053\u5c4b\u9876\u6709\u4eba\uff0c\u4ef0\u8d77\u4e86\u5934\u671b\u7740\u80e1\u6590\uff0c\u5f20\u7259\u821e\u722a\uff0c\u58f0\u52bf\u6c79\u6c79\u3002\u80e1\u6590\u89c1\u4e86\u7fa4\u72fc\u8fd9\u526f\u51f6\u6076\u795e\u60c5\uff0c\u5fc3\u4e2d\u5927\u662f\u53d1\u6bdb\uff0c\u81ea\u5df1\u8d64\u624b\u7a7a\u62f3\uff0c\u5b9e\u4e0d\u6613\u548c\u8fd9\u5341\u51e0\u5934\u6076\u72fc\u7684\u6bd2\u7259\u5229\u722a\u76f8\u6297\uff0c\u5f53\u4e0b\u77a7\u51c6\u4e86\u4e00\u5934\u6700\u5927\u7684\u96c4\u72fc\uff0c\u4e00\u5757\u74e6\u7247\u659c\u524a\u800c\u4e0b\uff0c\u6b63\u4e2d\u54bd\u5589\u3002\u90a3\u72fc\u5728\u5730\u4e0b\u4e00\u4e2a\u6253\u6eda\uff0c\u5403\u75db\u4e0d\u8fc7\uff0c\u8f6c\u8eab\u4fbf\u9003\uff0c\u53e6\u6709\u4e00\u5934\u5927\u72fc\u54ac\u4e86\u767d\u7f8a\uff0c\u8ddf\u7740\u9003\u8d70\u3002\u7247\u523b\u4e4b\u95f4\uff0c\u53eb\u58f0\u8d8a\u53bb\u8d8a\u8fdc\uff0c\u82b1\u5703\u4e2d\u7684\u84dd\u82b1\u5374\u5df2\u88ab\u8df5\u8e0f\u5f97\u4e03\u96f6\u516b\u843d\u3002 \u3000\u3000\u80e1\u6590\u8dc3\u4e0b\u5c4b\u6765\uff0c\u8fde\u79f0\uff1a\u201c\u53ef\u60dc\uff0c\u53ef\u60dc\uff01\u201d\u5fc3\u60f3\u90a3\u6751\u5973\u8f9b\u52e4\u9504\u82b1\u62d4\u8349\uff0c\u5c06\u8fd9\u7247\u84dd\u82b1\u57f9\u690d\u5f97\u5927\u662f\u53ef\u89c2\uff0c\u73b0\u4e0b\u9877\u523b\u4e4b\u95f4\u5c3d\u5f52\u6bc1\u8d25\uff0c\u4e00\u5b9a\u607c\u6012\u5f02\u5e38\u3002\u54ea\u77e5\u6751\u5973\u5bf9\u84dd\u82b1\u88ab\u6bc1\u4e4b\u4e8b\u4e00\u53e5\u4e0d\u63d0\uff0c\u53ea\u7b11\u541f\u541f\u5730\u9053\uff1a\u201c\u591a\u8c22\u80e1\u7237\u63f4\u624b\u4e86\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u8bf4\u6765\u60ed\u6127\uff01\u90fd\u602a\u6211\u89c1\u673a\u4e0d\u65e9\uff0c\u51fa\u624b\u592a\u8fdf\uff0c\u5018\u82e5\u65e9\u5c06\u90a3\u6076\u6c49\u5728\u82b1\u5703\u5916\u6253\u4e0b\u9a6c\u6765\uff0c\u8fd9\u7247\u82b1\u5349\u8fd8\u80fd\u4fdd\u5168\u3002\u201d \u3000\u3000\u90a3\u6751\u5973\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u84dd\u82b1\u5c31\u7b97\u4e0d\u7ed9\u6076\u72fc\u8e0f\u574f\uff0c\u8fc7\u51e0\u5929\u4e5f\u4f1a\u81ea\u884c\u840e\u8c22\u3002\u53ea\u4e0d\u8fc7\u8fdf\u65e9\u4e4b\u95f4\uff0c\u90a3\u4e5f\u7b97\u4e0d\u4e86\u4ec0\u4e48\u3002\u201d\u80e1\u6590\u4e00\u6014\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u59d1\u5a18\u5410\u5c5e\u4e0d\u51e1\uff0c\u8a00\u8bed\u4e4b\u95f4\u4f3c\u542b\u7384\u673a\u3002\u201d\u8bf4\u9053\uff1a\u201c\u5728\u5e9c\u4e0a\u5435\u6270\uff0c\u5374\u8fd8\u6ca1\u8bf7\u6559\u59d1\u5a18\u5c0a\u59d3\u3002\u201d\u90a3\u6751\u5973\u5fae\u4e00\u6c89\u541f\uff0c\u9053\uff1a\u201c\u6211\u59d3\u7a0b\uff0c\u4f46\u5728\u65c1\u4eba\u8ddf\u524d\uff0c\u4f60\u522b\u63d0\u8d77\u6211\u7684\u59d3\u6c0f\u3002\u201d\u8fd9\u4e09\u53e5\u8bdd\u8bf4\u5f97\u751a\u662f\u4eb2\u5207\uff0c\u4f3c\u4e4e\u5df2\u5c06\u80e1\u6590\u5f53\u4f5c\u662f\u81ea\u5df1\u4eba\u770b\u5f85\u3002\u80e1\u6590\u5f88\u662f\u9ad8\u5174\uff0c\u9053\uff1a\u201c\u90a3\u6211\u53eb\u4f60\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u90a3\u6751\u5973\u9053\uff1a\u201c\u4f60\u8fd9\u4eba\u5f88\u597d\uff0c\u6211\u4fbf\u7d22\u6027\u8fde\u540d\u5b57\u4e5f\u90fd\u8ddf\u4f60\u8bf4\u4e86\u3002 \u3000\u3000\u6211\u53eb\u7a0b\u7075\u7d20\uff0c\u2018\u7075\u67a2\u2019\u7684\u2018\u7075\u2019\uff0c\u2018\u7d20\u95ee\u2019\u7684\u2018\u7d20\u2019\u3002\u201d\u80e1\u6590\u4e0d\u77e5\u201c\u7075\u67a2\u201d\u548c\u201c\u7d20\u95ee\u201d\u4e43\u662f\u4e2d\u56fd\u4e24\u5927\u533b\u7ecf\uff0c\u53ea\u89c9\u5f97\u8fd9\u4e24\u4e2a\u5b57\u5f88\u662f\u96c5\u81f4\uff0c\u4e0d\u50cf\u519c\u6751\u5973\u5b50\u7684\u540d\u5b57\uff0c\u8fd9\u65f6\u5df2\u77e5\u5979\u51b3\u4e0d\u662f\u5bfb\u5e38\u4e61\u4e0b\u59d1\u5a18\uff0c\u4e5f\u4e0d\u4ee5\u4e3a\u5f02\uff0c\u7b11\u9053\uff1a\u201c\u90a3\u6211\u4fbf\u53eb\u4f60\u2018\u7075\u59d1\u5a18\u2019\uff0c\u522b\u4eba\u542c\u6765\uff0c\u53ea\u5f53\u6211\u53eb\u4f60\u2018\u6797\u59d1\u5a18\u2019\u5462\u3002\u201d\u7a0b\u7075\u7d20\u5ae3\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u603b\u6709\u6cd5\u513f\u8ba8\u6211\u6b22\u559c\u3002\u201d\u80e1\u6590\u5fc3\u4e2d\u5fae\u5fae\u4e00\u52a8\uff0c\u89c9\u5f97\u5979\u76f8\u8c8c\u867d\u7136\u5e76\u4e0d\u751a\u7f8e\uff0c\u4f46\u8fd9\u4e48\u4e00\u8a00\u4e00\u7b11\uff0c\u5374\u81ea\u6709\u4e00\u80a1\u59a9\u5a9a\u7684\u98ce\u81f4\u3002 \u3000\u3000\u4ed6\u6b63\u60f3\u8be2\u95ee\u953a\u5146\u6587\u9152\u9189\u4e4b\u4e8b\uff0c\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u4f60\u7684\u953a\u4e8c\u54e5\u559d\u9189\u4e86\u9152\uff0c\u4e0d\u788d\u4e8b\uff0c\u5230\u5929\u660e\u4fbf\u9192\u4e86\u3002\u73b0\u4e0b\u6211\u8981\u53bb\u77a7\u51e0\u4e2a\u4eba\uff0c\u4f60\u540c\u4e0d\u540c\u6211\u53bb\uff1f\u201d \u3000\u3000\u80e1\u6590\u89c9\u5f97\u8fd9\u4e2a\u5c0f\u59d1\u5a18\u884c\u4e8b\u5904\u5904\u5341\u5206\u5947\u602a\uff0c\u8fd9\u534a\u591c\u4e09\u66f4\u53bb\u63a2\u8bbf\u522b\u4eba\uff0c\u5fc5\u6709\u6df1\u610f\uff0c\u4fbf\u9053\uff1a\u201c\u6211\u81ea\u7136\u53bb\u3002\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u4f60\u966a\u6211\u53bb\uff0c\u54b1\u4eec\u53ef\u5f97\u7ea6\u6cd5\u4e09\u7ae0\u3002\u7b2c\u4e00\uff0c\u4f60\u4eca\u665a\u4e0d\u8bb8\u8ddf\u4eba\u8bf4\u8bdd\u2026\u2026\u201d\u80e1\u6590\u9053\uff1a\u201c\u597d\uff0c\u6211\u626e\u54d1\u5b50\u4fbf\u662f\u3002\u201d\u7a0b\u7075\u7d20\u7b11\u9053\uff1a\u201c\u90a3\u5012\u4e0d\u7528\uff0c\u8ddf\u6211\u8bf4\u8bdd\u5f53\u7136\u53ef\u4ee5\u3002\u7b2c\u4e8c\uff0c\u4e0d\u80fd\u8ddf\u4eba\u52a8\u6b66\uff0c\u653e\u6697\u5668\u70b9\u7a74\uff0c\u4e00\u6982\u7981\u6b62\u3002\u7b2c\u4e09\uff0c\u4e0d\u80fd\u79bb\u5f00\u6211\u4e09\u6b65\u4e4b\u5916\u3002\u201d \u3000\u3000\u80e1\u6590\u70b9\u5934\u7b54\u5e94\uff0c\u5fc3\u60f3\uff1a\u201c\u539f\u6765\u5979\u5e26\u6211\u53bb\u89c1\u6bd2\u624b\u836f\u738b\u3002\u5979\u53eb\u6211\u4e0d\u80fd\u79bb\u5f00\u5979\u8eab\u8fb9\u4e09\u6b65\uff0c\u81ea\u662f\u6015\u6211\u4e2d\u6bd2\u53d7\u5bb3\u4e86\u3002\u201d\u5f53\u4e0b\u751a\u662f\u632f\u594b\uff0c\u9053\uff1a\u201c\u54b1\u4eec\u8fd9\u4fbf\u53bb\u4e48\uff1f\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u5f97\u5e26\u4e9b\u4e1c\u897f\u3002\u201d\u8d70\u8fdb\u81ea\u5df1\u623f\u5185\uff0c\u7ea6\u8fc7\u4e86\u4e00\u76cf\u8336\u65f6\u5206\uff0c\u6311\u4e86\u4e24\u53ea\u7af9\u7ba9\u51fa\u6765\uff0c\u7ba9\u4e0a\u7528\u76d6\u76d6\u7740\uff0c\u4e0d\u77e5\u91cc\u9762\u653e\u7740\u4e9b\u4ec0\u4e48\uff0c\u770b\u5979\u7684\u6a21\u6837\uff0c\u6311\u5f97\u9887\u4e3a\u5403\u529b\u3002 \u3000\u3000\u80e1\u6590\u9053\uff1a\u201c\u6211\u6765\u6311\uff01\u201d\u5c06\u6241\u62c5\u63a5\u4e86\u8fc7\u6765\uff0c\u4e00\u653e\u4e0a\u80a9\u5934\uff0c\u51e0\u6709\u4e00\u767e\u4e8c\u4e09\u5341\u65a4\u3002\u4e24\u53ea\u7af9\u7ba9\u8f7b\u91cd\u60ac\u6b8a\uff0c\u4e00\u53ea\u751a\u91cd\uff0c\u4e00\u53ea\u5374\u662f\u6781\u8f7b\uff0c\u6311\u6765\u9887\u4e0d\u65b9\u4fbf\uff0c\u53ea\u89c1\u953a\u5146\u6587\u5140\u81ea\u4f0f\u5728\u684c\u4e0a\uff0c\u547c\u547c\u5927\u7761\uff0c\u7ecf\u8fc7\u4ed6\u8eab\u65c1\u4fbf\u95fb\u5230\u4e00\u80a1\u6d53\u70c8\u7684\u9152\u6c14\u3002 \u3000\u3000\u4e24\u4eba\u51fa\u4e86\u8305\u820d\uff0c\u7a0b\u7075\u7d20\u5c06\u95e8\u5e26\u4e0a\uff0c\u5728\u524d\u5f15\u8def\u3002\u80e1\u6590\u9053\uff1a\u201c\u7075\u59d1\u5a18\uff0c\u6211\u95ee\u4f60\u4e00\u4ef6\u4e8b\uff0c\u6210\u4e0d\u6210\uff1f\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u6210\u554a\uff0c\u5c31\u6015\u6211\u7b54\u4e0d\u4e0a\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u4f60\u82e5\u7b54\u4e0d\u51fa\uff0c\u5929\u4e0b\u5c31\u6ca1\u7b2c\u4e8c\u4e2a\u4eba\u7b54\u5f97\u51fa\u4e86\u3002\u6211\u90a3\u953a\u4e8c\u54e5\u6ef4\u6c34\u6ca1\u6709\u5165\u53e3\uff0c\u600e\u5730\u4f1a\u9189\u6210\u8fd9\u4e2a\u6a21\u6837\uff1f\u201d\u7a0b\u7075\u7d20\u8f7b\u8f7b\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5c31\u56e0\u4ed6\u6ef4\u6c34\u4e0d\u80af\u5165\u53e3\uff0c\u8fd9\u624d\u5403\u4e86\u4e8f\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u8fd9\u4e2a\u6211\u5c31\u4e0d\u61c2\u4e86\u3002\u953a\u4e8c\u54e5\u662f\u8001\u6c5f\u6e56\uff0c\u9102\u5317\u9b3c\u89c1\u6101\u953a\u6c0f\u4e09\u96c4\uff0c\u5728\u6b66\u6797\u4e2d\u4e5f\u7b97\u9887\u6709\u540d\u58f0\u3002\u6211\u5374\u662f\u4e2a\u89c1\u8bc6\u6d45\u964b\u4e4b\u4eba\uff0c\u54ea\u77e5\u9053\u4ed6\u5904\u5904\u5c0f\u5fc3\uff0c\u53cd\u800c\u2026\u2026\u201d\u8bf4\u5230\u8fd9\u91cc\uff0c\u4f4f\u53e3\u4e0d\u8bf4\u4e86\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u4f60\u8bf4\u597d\u4e86\uff01\u4ed6\u5904\u5904\u5c0f\u5fc3\uff0c\u53cd\u800c\u7740\u4e86\u6211\u7684\u9053\u513f\uff0c\u662f\u4e0d\u662f\uff1f\u5904\u5904\u5c0f\u5fc3\u63d0\u9632\u4fbf\u6709\u7528\u4e86\u5417\uff1f\u53ea\u6709\u50cf\u4f60\u8fd9\u822c\uff0c\u624d\u4f1a\u592a\u5e73\u65e0\u4e8b\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u6211\u600e\u4e48\u554a\uff01\u201d\u7a0b\u7075\u7d20\u7b11\u9053\uff1a\u201c\u53eb\u4f60\u6311\u7caa\u4fbf\u6311\u7caa\uff0c\u53eb\u4f60\u5403\u996d\u4fbf\u5403\u996d\u3002\u8fd9\u822c\u542c\u8bdd\uff0c\u4eba\u5bb6\u600e\u80fd\u5fcd\u5fc3\u5bb3\u4f60\uff1f\u201d\u80e1\u6590\u7b11\u9053\uff1a\u201c\u539f\u6765\u505a\u4eba\u8981\u542c\u8bdd\u3002\u53ef\u662f\u4f60\u6574\u4eba\u7684\u6cd5\u513f\u4e5f\u592a\u5de7\u5999\u4e86\u4e9b\uff0c\u6211\u5230\u73b0\u5728\u8fd8\u662f\u6478\u4e0d\u7740\u5934\u8111\u3002\u201d \u3000\u3000\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u597d\uff0c\u6211\u6559\u4f60\u4e00\u4e2a\u4e56\u3002\u5385\u4e0a\u6709\u4e00\u76c6\u5c0f\u5c0f\u7684\u767d\u82b1\uff0c\u4f60\u77a7\u89c1\u4e86\u4e48\uff1f\u201d\u80e1\u6590\u5f53\u65f6\u6ca1\u7559\u610f\uff0c\u8fd9\u65f6\u4e00\u52a0\u56de\u60f3\uff0c\u679c\u7136\u8bb0\u5f97\u7a97\u53e3\u4e00\u5f20\u534a\u684c\u4e0a\u653e\u7740\u4e00\u76c6\u5c0f\u6735\u513f\u7684\u767d\u82b1\u3002\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u8fd9\u76c6\u82b1\u53eb\u505a\u918d\u9190\u9999\uff0c\u82b1\u9999\u9189\u4eba\uff0c\u6781\u662f\u5389\u5bb3\uff0c\u95fb\u5f97\u7a0d\u4e45\uff0c\u4fbf\u548c\u996e\u4e86\u70c8\u9152\u4e00\u822c\u65e0\u5f02\u3002\u6211\u5728\u6c64\u91cc\u3001\u8336\u91cc\u90fd\u653e\u4e86\u89e3\u836f\u3002\u8c01\u6559\u4ed6\u4e0d\u559d\u554a\uff1f\u201d \u3000\u3000\u80e1\u6590\u604d\u7136\u5927\u609f\uff0c\u4e0d\u7981\u5bf9\u8fd9\u4f4d\u59d1\u5a18\u5927\u8d77\u656c\u754f\u4e4b\u5fc3\uff0c\u6697\u9053\u81ea\u6765\u53ea\u542c\u8bf4\u6709\u4eba\u5728\u996e\u98df\u4e4b\u4e2d\u4e0b\u6bd2\uff0c\u54ea\u77e5\u5979\u4e0b\u6bd2\u7684\u65b9\u6cd5\u5374\u9ad8\u660e\u5f97\u591a\uff0c\u5bf9\u65b9\u4e0d\u5403\u4e0d\u559d\u53cd\u800c\u4f1a\u4e2d\u6bd2\u3002\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u5f85\u4f1a\u56de\u53bb\u6211\u4fbf\u7ed9\u4ed6\u89e3\u836f\uff0c\u4f60\u4e0d\u7528\u62c5\u5fc3\u3002\u201d\u80e1\u6590\u5fc3\u4e2d\u4e00\u52a8\uff1a\u201c\u8fd9\u4f4d\u59d1\u5a18\u65e2\u7136\u64c5\u7528\u836f\u7269\uff0c\u8bf4\u4e0d\u5b9a\u80fd\u6cbb\u82d7\u5927\u4fa0\u7684\u4f24\u76ee\uff0c\u90a3\u4fbf\u4e0d\u987b\u53bb\u6c42\u4ec0\u4e48\u6bd2\u624b\u836f\u738b\u4e86\u3002\u201d\u4e8e\u662f\u95ee\u9053\uff1a\u201c\u7075\u59d1\u5a18\uff0c\u4f60\u77e5\u9053\u89e3\u6cbb\u65ad\u80a0\u8349\u6bd2\u6027\u7684\u6cd5\u5b50\u5417\uff1f\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u96be\u8bf4\u3002\u201d \u3000\u3000\u80e1\u6590\u542c\u5979\u8bf4\u4e86\u8fd9\u4e24\u4e2a\u5b57\uff0c\u4fbf\u6ca1\u4e0b\u6587\uff0c\u4e0d\u4fbf\u5c31\u63d0\u533b\u6cbb\u4e4b\u8bf7\uff0c\u53ea\u89c1\u5979\u811a\u6b65\u8f7b\u76c8\uff0c\u5728\u524d\u4e0d\u75be\u4e0d\u5f90\u5730\u8d70\u7740\uff0c\u867d\u4e0d\u662f\u65bd\u5c55\u8f7b\u529f\uff0c\u4f46\u6ca1\u8fc7\u591a\u5c11\u65f6\u5149\u5df2\u8d70\u4e86\u516d\u4e03\u91cc\u8def\uff0c\u77a7\u65b9\u5411\u662f\u8d70\u5411\u6b63\u4e1c\uff0c\u4e0d\u662f\u53bb\u836f\u738b\u5e84\u7684\u9053\u8def\uff0c\u5ffd\u7136\u53c8\u60f3\u5230\u4e00\u4e8b\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u8fd8\u60f3\u95ee\u4f60\u4e00\u4ef6\u4e8b\uff0c\u9002\u624d\u6211\u548c\u953a\u4e8c\u54e5\u53bb\u836f\u738b\u5e84\uff0c\u4f60\u8bf4\u8fd8\u662f\u5411\u4e1c\u5317\u65b9\u53bb\u7684\u597d\uff0c\u6545\u610f\u53eb\u6211\u4eec\u7ed5\u9053\u591a\u8d70\u4e86\u4e8c\u5341\u51e0\u91cc\u8def\u3002\u8fd9\u5176\u4e2d\u7684\u7528\u610f\uff0c\u6211\u4e00\u76f4\u6ca1\u80fd\u660e\u767d\u3002\u201d \u3000\u3000\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u4f60\u771f\u6b63\u60f3\u95ee\u6211\u7684\uff0c\u8fd8\u4e0d\u662f\u8fd9\u4ef6\u4e8b\u3002\u6211\u731c\u4f60\u662f\u60f3\u95ee\uff1a\u836f\u738b\u5e84\u660e\u660e\u662f\u5728\u897f\u5317\uff0c\u54b1\u4eec\u600e\u4e48\u5411\u4e1c\u8d70\uff1f\u201d\u80e1\u6590\u7b11\u9053\uff1a\u201c\u4f60\u65e2\u731c\u5230\u4e86\uff0c\u90a3\u6211\u4e00\u5e76\u8bf7\u95ee\u4fbf\u662f\u3002\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u54b1\u4eec\u6240\u4ee5\u4e0d\u671d\u836f\u738b\u5e84\u8d70\uff0c\u56e0\u4e3a\u5e76\u4e0d\u662f\u53bb\u836f\u738b\u5e84\u3002\u201d\u8fd9\u4e00\u4e0b\uff0c\u80e1\u6590\u53c8\u662f\u51fa\u4e8e\u610f\u6599\u4e4b\u5916\uff0c\u201c\u554a\u201d\u4e86\u4e00\u58f0\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u53c8\u9053\uff1a\u201c\u767d\u5929\u6211\u8981\u4f60\u6d47\u82b1\uff0c\u4e00\u6765\u662f\u8bd5\u8bd5\u4f60\uff0c\u4e8c\u6765\u662f\u8981\u4f60\u803d\u6401\u4e9b\u65f6\u5149\uff0c\u540e\u6765\u518d\u53eb\u4f60\u7ed5\u9053\u591a\u8d70\u4e8c\u5341\u51e0\u91cc\uff0c\u4e5f\u662f\u4e3a\u4e86\u8981\u4f60\u591a\u8017\u65f6\u523b\uff0c\u8fd9\u6837\u4fbf\u80fd\u5728\u5929\u9ed1\u4e4b\u540e\u518d\u5230\u836f\u738b\u5e84\u5916\u3002\u53ea\u56e0\u836f\u738b\u5e84\u5916\u6240\u79cd\u7684\u8840\u77ee\u6817\uff0c\u4e00\u5230\u5929\u9ed1\uff0c\u6bd2\u6027\u4fbf\u5c0f\uff0c\u6211\u7ed9\u4f60\u7684\u84dd\u82b1\u624d\u514b\u5f97\u5b83\u4f4f\u3002\u201d \u3000\u3000\u80e1\u6590\u542c\u4e86\uff0c\u5fc3\u4e2d\u94a6\u670d\u65e0\u5df2\uff0c\u4e07\u60f3\u4e0d\u5230\u7528\u6bd2\u4f7f\u836f\uff0c\u7adf\u6709\u8fd9\u8bb8\u591a\u5b66\u95ee\uff0c\u8fd9\u4e2a\u8c8c\u4e0d\u60ca\u4eba\u7684\u5c0f\u59d1\u5a18\u7528\u5fc3\u6df1\u81f3\uff0c\u66f4\u975e\u5e38\u4eba\u6240\u53ca\uff0c\u5f53\u4e0b\u8bf4\u5230\u5728\u6d1e\u5ead\u6e56\u89c1\u5230\u7684\u4e24\u540d\u6b7b\u8005\u3002\u7a0b\u7075\u7d20\u542c\u8bf4\u4e24\u540d\u6b7b\u8005\u8138\u4e0a\u6ee1\u662f\u9ed1\u70b9\uff0c\u808c\u8089\u626d\u66f2\uff0c\u54fc\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8fd9\u79cd\u9b3c\u8759\u8760\u7684\u6bd2\u65e0\u836f\u53ef\u6cbb\u3002\u4ed6\u4eec\u4ec0\u4e48\u4e5f\u4e0d\u987e\u4e86\u3002\u201d\u80e1\u6590\u5fc3\u9053\uff1a\u201c\u2018\u9b3c\u8759\u8760\u2019\u662f\u4ec0\u4e48\u6bd2\uff0c\u5979\u8bf4\u4e86\u6211\u4e5f\u4e0d\u61c2\u3002\u53cd\u6b63\u4e00\u610f\u542c\u5979\u5429\u5490\u884c\u4e8b\u4fbf\u4e86\uff0c\u591a\u8bf4\u591a\u95ee\uff0c\u5f92\u7136\u663e\u5f97\u81ea\u5df1\u4e00\u65e0\u662f\u5904\u3002\u201d\u4e8e\u662f\u4e0d\u518d\u8be2\u95ee\uff0c\u8ddf\u5728\u5979\u8eab\u540e\u4e00\u8def\u5411\u4e1c\u3002 \u3000\u3000\u53c8\u8d70\u4e86\u4e94\u516d\u91cc\u8def\uff0c\u8fdb\u4e86\u4e00\u5ea7\u9ed1\u9edd\u9edd\u7684\u6811\u6797\u3002\u7a0b\u7075\u7d20\u4f4e\u58f0\u9053\uff1a\u201c\u5230\u4e86\u3002\u4ed6\u4eec\u8fd8\u6ca1\u6765\uff0c\u54b1\u4eec\u5728\u8fd9\u6811\u6797\u5b50\u4e2d\u7b49\u5019\uff0c\u4f60\u628a\u8fd9\u53ea\u7af9\u7ba9\u653e\u5728\u90a3\u682a\u6811\u4e0b\u3002\u201d\u8bf4\u7740\u5411\u4e00\u682a\u5927\u6811\u4e00\u6307\u3002\u80e1\u6590\u4f9d\u8a00\u63d0\u4e86\u90a3\u53ea\u4efd\u91cf\u751a\u91cd\u7684\u7af9\u7ba9\u8fc7\u53bb\u653e\u597d\u3002\u7a0b\u7075\u7d20\u8d70\u5230\u79bb\u5927\u6811\u516b\u4e5d\u4e08\u5904\u7684\u4e00\u4e1b\u957f\u8349\u4e4b\u65c1\uff0c\u9053\uff1a\u201c\u8fd9\u4e00\u53ea\u7af9\u7ba9\u7ed9\u6211\u63d0\u8fc7\u6765\u3002\u201d\u968f\u5373\u62e8\u5f00\u957f\u8349\uff0c\u94bb\u8fdb\u4e86\u8349\u4e1b\u4e4b\u4e2d\u3002 \u3000\u3000\u80e1\u6590\u4e5f\u4e0d\u95ee\u8c01\u8fd8\u6ca1\u6765\uff0c\u7b49\u5019\u4ec0\u4e48\uff0c\u8bb0\u7740\u4e0d\u79bb\u5f00\u5979\u4e09\u6b65\u7684\u7ea6\u8a00\uff0c\u4fbf\u63d0\u4e86\u53e6\u4e00\u53ea\u7af9\u7ba9\uff0c\u4e5f\u94bb\u8fdb\u8349\u4e1b\uff0c\u6328\u5728\u5979\u7684\u8eab\u65c1\u3002\u4ef0\u5934\u5411\u5929\uff0c\u53ea\u89c1\u6708\u8f6e\u897f\u659c\uff0c\u5df2\u8fc7\u591c\u534a\u3002\u6811\u6797\u4e2d\u866b\u58f0\u6b64\u8d77\u5f7c\u4f0f\uff0c\u5076\u7136\u4e5f\u542c\u5230\u4e00\u4e8c\u58f0\u67ad\u9e23\u3002\u7a0b\u7075\u7d20\u9012\u7ed9\u4ed6\u4e00\u7c92\u836f\u4e38\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u542b\u5728\u53e3\u91cc\uff0c\u522b\u541e\u4e0b\uff01\u201d\u80e1\u6590\u770b\u4e5f\u4e0d\u770b\u4fbf\u653e\u5165\u5634\u4e2d\uff0c\u4f46\u89c9\u5473\u9053\u6781\u82e6\u3002 \u3000\u3000\u4e24\u4eba\u9759\u9759\u7684\u5750\u7740\uff0c\u8fc7\u4e86\u5c0f\u534a\u4e2a\u65f6\u8fb0\uff0c\u80e1\u6590\u4e1c\u60f3\u897f\u60f3\uff0c\u53ea\u89c9\u8fd9\u4e00\u65e5\u4e00\u665a\u7684\u7ecf\u5386\uff0c\u5b9e\u5728\u5927\u662f\u8be1\u5f02\uff0c\u53ef\u8bf4\u662f\u751f\u5e73\u4ece\u6240\u672a\u9047\u4e4b\u5947\u3002\u7a81\u7136\u4e4b\u95f4\uff0c\u60f3\u5230\u4e86\u8881\u7d2b\u8863\uff1a\u201c\u4e0d\u77e5\u5979\u8fd9\u65f6\u8eab\u5728\u4f55\u5904\uff1f\u5982\u679c\u8fd9\u65f6\u5728\u6211\u8eab\u7554\u7684\uff0c\u4e0d\u662f\u8fd9\u4e2a\u7626\u7626\u5c0f\u5c0f\u7684\u59d1\u5a18\u800c\u662f\u8881\u59d1\u5a18\uff0c\u4e0d\u77e5\u5979\u8981\u8ddf\u6211\u8bf4\u4ec0\u4e48\uff1f\u201d\u4e00\u60f3\u5230\u5979\uff0c\u4fbf\u4f38\u624b\u5165\u6000\uff0c\u53bb\u6478\u7389\u51e4\u3002 \u3000\u3000\u5ffd\u7136\u7a0b\u7075\u7d20\u4f38\u624b\u62c9\u4e86\u4ed6\u7684\u8863\u89d2\uff0c\u5411\u524d\u4e00\u6307\u3002\u80e1\u6590\u987a\u7740\u5979\u624b\u6307\u77a7\u53bb\uff0c\u53ea\u89c1\u8fdc\u5904\u4e00\u76cf\u706f\u7b3c\uff0c\u6b63\u5728\u6e10\u6e10\u79fb\u8fd1\u3002\u672c\u6765\u706f\u7b3c\u7684\u706b\u5149\u5fc5\u662f\u6697\u7ea2\u4e4b\u8272\uff0c\u4f46\u8fd9\u76cf\u706f\u7b3c\u53d1\u51fa\u7684\u5374\u662f\u78a7\u6cb9\u6cb9\u7684\u7eff\u5149\u3002 \u3000\u3000\u706f\u7b3c\u6765\u5f97\u751a\u5feb\uff0c\u4e0d\u591a\u65f6\u5df2\u5230\u8eab\u524d\u5341\u4f59\u4e08\u5916\uff0c\u706f\u4e0b\u77a7\u5f97\u660e\u767d\uff0c\u63d0\u706f\u7684\u662f\u4e2a\u9a7c\u80cc\u5973\u5b50\uff0c\u8d70\u8d77\u8def\u6765\u5de6\u9ad8\u53f3\u4f4e\uff0c\u770b\u6765\u53f3\u811a\u662f\u8ddb\u7684\u3002\u5979\u8eab\u540e\u7d27\u968f\u7740\u4e00\u4e2a\u6c49\u5b50\uff0c\u8eab\u6750\u9b41\u68a7\uff0c\u8170\u95f4\u63d2\u7740\u660e\u6643\u6643\u7684\u4e00\u628a\u5c16\u5200\u3002 \u3000\u3000\u80e1\u6590\u60f3\u8d77\u953a\u5146\u6587\u7684\u8bf4\u8bdd\uff0c\u8eab\u5b50\u4e0d\u7531\u5f97\u5fae\u5fae\u4e00\u9707\uff1a\u201c\u953a\u4e8c\u54e5\u8bf4\uff0c\u6709\u4eba\u8bf4\u6bd2\u624b\u836f\u738b\u662f\u4e2a\u5c60\u592b\u6a21\u6837\u7684\u5927\u6c49\uff0c\u53c8\u6709\u4eba\u8bf4\u836f\u738b\u662f\u4e2a\u53c8\u9a7c\u53c8\u8ddb\u7684\u5973\u5b50\u3002\u90a3\u4e48\u8fd9\u4e24\u4eba\u4e4b\u4e2d\uff0c\u5fc5\u6709\u4e00\u4e2a\u662f\u836f\u738b\u3002\u201d\u659c\u773c\u5411\u7a0b\u7075\u7d20\u4e00\u770b\uff0c\u9ed1\u6697\u4e4b\u4e2d\uff0c\u77a7\u4e0d\u89c1\u5979\u7684\u8138\u8272\uff0c\u4f46\u89c1\u5979\u4e00\u5bf9\u6e05\u6f88\u6676\u83b9\u7684\u5927\u773c\uff0c\u76ee\u4e0d\u8f6c\u775b\u5730\u671b\u7740\u4e24\u4eba\uff0c\u795e\u60c5\u663e\u751a\u7d27\u5f20\u3002\u80e1\u6590\u767b\u65f6\u8d77\u4e86\u4fa0\u4e49\u4e4b\u5fc3\uff1a\u201c\u8fd9\u6bd2\u624b\u836f\u738b\u5982\u8981\u4e0d\u5229\u4e8e\u5979\uff0c\u6211\u4fbf\u662f\u62da\u7740\u6027\u547d\uff0c\u4e5f\u8981\u62a4\u5979\u5468\u5168\u3002\u201d \u3000\u3000\u90a3\u4e00\u7537\u4e00\u5973\u8d8a\u8d70\u8d8a\u8fd1\u3002\u53ea\u89c1\u90a3\u5973\u5b50\u5bb9\u8c8c\u751a\u662f\u6587\u79c0\uff0c\u867d\u7136\u8eab\u6709\u6b8b\u75be\uff0c\u4ecd\u53ef\u8bf4\u5f97\u4e0a\u662f\u4e2a\u7f8e\u5973\uff0c\u90a3\u5927\u6c49\u5374\u662f\u6ee1\u8138\u6a2a\u8089\uff0c\u5f62\u76f8\u51f6\u72e0\u3002\u4e24\u4eba\u90fd\u662f\u56db\u5341\u6765\u5c81\u5e74\u7eaa\u3002\u80e1\u6590\u4e00\u8eab\u6b66\u529f\uff0c\u4fbf\u662f\u9047\u5230\u6c5f\u6e56\u4e0a\u6700\u5389\u5bb3\u7684\u5de8\u5bc7\u5927\u8d3c\u73af\u653b\uff0c\u4e5f\u662f\u65e0\u6240\u754f\u60e7\uff0c\u4f46\u8fd9\u65f6\u5374\u4e0d\u7531\u81ea\u4e3b\u7684\u5fc3\u4e2d\u6026\u6026\u4e71\u8df3\uff0c\u81ea\u89c9\u6b66\u529f\u6709\u65f6\u800c\u7a77\uff0c\u5bf9\u4ed8\u8fd9\u79cd\u4eba\uff0c\u6b66\u529f\u672a\u5fc5\u4fbf\u80fd\u7ba1\u7528\u3002 \u3000\u3000\u90a3\u4e24\u4eba\u8d70\u5230\u80e1\u6590\u8eab\u524d\u4e03\u516b\u4e08\u5904\uff0c\u5ffd\u7136\u6298\u800c\u5411\u5de6\uff0c\u53c8\u8d70\u4e86\u5341\u4f59\u4e08\uff0c\u7ad9\u5b9a\u8eab\u5b50\u3002\u90a3\u5927\u6c49\u6717\u58f0\u53eb\u9053\uff1a\u201c\u6155\u5bb9\u5e08\u5144\uff0c\u6211\u592b\u5987\u4f9d\u7ea6\u524d\u6765\uff0c\u4fbf\u8bf7\u9732\u9762\u76f8\u89c1\u5427\uff01\u201d \u3000\u3000\u4ed6\u7ad9\u7acb\u4e4b\u5904\u8ddd\u80e1\u6590\u5e76\u4e0d\u751a\u8fdc\uff0c\u7a81\u7136\u5f00\u53e3\u8bf4\u8bdd\uff0c\u58f0\u97f3\u53c8\u5927\uff0c\u53ea\u628a\u4ed6\u5413\u4e86\u4e00\u8df3\u3002\u90a3\u5927\u6c49\u8bf4\u4e86\u4e24\u904d\uff0c\u65e0\u4eba\u7b54\u8bdd\uff0c\u80e1\u6590\u5fc3\u60f3\uff1a \u3000\u3000\u201c\u8fd9\u91cc\u9664\u4e86\u54b1\u4eec\u56db\u4eba\uff0c\u518d\u6ca1\u65c1\u4eba\uff0c\u54ea\u91cc\u8fd8\u6709\u4ec0\u4e48\u6155\u5bb9\u5e08\u5144\uff1f\u8fd9\u4e24\u4eba\u539f\u6765\u662f\u4e00\u5bf9\u592b\u59bb\u3002\u201d \u3000\u3000\u90a3\u9a7c\u80cc\u5973\u5b50\u7ec6\u58f0\u7ec6\u6c14\u5730\u9053\uff1a\u201c\u6155\u5bb9\u5e08\u5144\u65e2\u7136\u4e0d\u80af\u73b0\u8eab\uff0c\u6211\u592b\u5987\u8feb\u5f97\u65e0\u793c\u4e86\u3002\u201d \u3000\u3000\u80e1\u6590\u6697\u6697\u597d\u7b11\uff1a\u201c\u8fd9\u53eb\u505a\u4e00\u62a5\u8fd8\u4e00\u62a5\u3002\u9002\u624d\u6211\u5230\u836f\u738b\u5e84\u6765\u62dc\u8bbf\uff0c\u8bf4\u4ec0\u4e48\u4f60\u4eec\u4e5f\u4e0d\u7406\u776c\u3002\u8fd9\u65f6\u5019\u522b\u4eba\u4e5f\u7ed9\u4e00\u4e2a\u8f6f\u9489\u5b50\u4f60\u4eec\u78b0\u78b0\u3002\u201d\u53ea\u89c1\u90a3\u5973\u5b50\u4ece\u6000\u4e2d\u53d6\u51fa\u4e00\u675f\u8349\u6765\uff0c\u4f38\u5230\u706f\u7b3c\u4e2d\u53bb\u70b9\u71c3\u4e86\uff0c\u7acb\u65f6\u53d1\u51fa\u4e00\u80a1\u6d53\u70df\u3002\u8fc7\u4e0d\u591a\u65f6\uff0c\u6797\u4e2d\u4fbf\u767d\u96fe\u7030\u6f2b\uff0c\u70df\u96fe\u4e4b\u4e2d\u5fae\u6709\u6a80\u9999\u6c14\u606f\uff0c\u5012\u4e5f\u5e76\u4e0d\u96be\u95fb\u3002 \u3000\u3000\u80e1\u6590\u542c\u5979\u8bf4\u201c\u8feb\u5f97\u65e0\u793c\u201d\u56db\u5b57\u3002\u77e5\u9053\u8fd9\u80a1\u70df\u96fe\u5b9a\u7136\u5389\u5bb3\uff0c\u4f46\u81ea\u5df1\u5374\u4e5f\u4e0d\u611f\u5230\u6709\u4f55\u4e0d\u9002\uff0c\u60f3\u5fc5\u662f\u53e3\u4e2d\u542b\u4e86\u836f\u4e38\u4e4b\u529f\uff0c\u8f6c\u5934\u5411\u7a0b\u7075\u7d20\u671b\u4e86\u4e00\u773c\u3002\u8fd9\u65f6\u5979\u4e5f\u6b63\u56de\u7738\u77a7\u4ed6\uff0c\u76ee\u5149\u4e2d\u5145\u6ee1\u4e86\u5173\u6ce8\u4e4b\u8272\u3002\u80e1\u6590\u5fc3\u4e2d\u611f\u6fc0\uff0c\u5fae\u5fae\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u90a3\u70df\u96fe\u8d8a\u6765\u8d8a\u6d53\uff0c\u7a81\u7136\u5927\u6811\u4e0b\u7684\u7af9\u7ba9\u4e2d\u6709\u4eba\u5927\u58f0\u6253\u4e86\u4e2a\u55b7\u568f\u3002 \u3000\u3000\u80e1\u6590\u5927\u5403\u4e00\u60ca\uff1a\u201c\u600e\u4e48\u7af9\u7ba9\u4e2d\u6709\u4eba\uff1f\u6211\u6311\u4e86\u534a\u5929\u4e00\u70b9\u4e5f\u6ca1\u77e5\u60c5\u3002\u90a3\u4e48\u6211\u8ddf\u7a0b\u59d1\u5a18\u7684\u8bf4\u8bdd\uff0c\u90fd\u8ba9\u4ed6\u542c\u53bb\u4e86\uff1f\u201d\u81ea\u5fd6\u5bf9\u6bd2\u7269\u533b\u836f\u4e4b\u9053\u867d\u7136\u4e00\u7a8d\u4e0d\u901a\uff0c\u4f46\u7ec3\u4e86\u8fd9\u8bb8\u591a\u5e74\u6b66\u529f\uff0c\u51b3\u4e0d\u80fd\u6311\u7740\u4e00\u4e2a\u4eba\u8d70\u8fd9\u8bb8\u591a\u8def\u800c\u832b\u7136\u4e0d\u89c9\uff0c\u9664\u975e\u8fd9\u662f\u4e2a\u6b7b\u4eba\uff0c\u90a3\u53c8\u4f5c\u522b\u8bba\u3002\u4ed6\u5fc3\u4e2d\u5927\u662f\u60ca\u5947\uff0c\u53ea\u542c\u7af9\u7ba9\u4e2d\u90a3\u4eba\u53c8\u8fde\u6253\u51e0\u4e2a\u55b7\u568f\uff0c\u7ba9\u76d6\u6380\u5f00\uff0c\u8dc3\u4e86\u51fa\u6765\u3002\u4f46\u89c1\u4ed6\u957f\u888d\u5112\u5dfe\uff0c\u6b63\u662f\u65e5\u95f4\u6240\u89c1\u5728\u5c0f\u5c71\u4e0a\u91c7\u836f\u7684\u90a3\u4e2a\u8001\u8005\u3002 \u3000\u3000\u8fd9\u65f6\u4ed6\u8863\u886b\u51cc\u4e71\uff0c\u5934\u5dfe\u6b6a\u659c\uff0c\u795e\u60c5\u751a\u662f\u72fc\u72c8\uff0c\u5df2\u6ca1\u534a\u70b9\u65e5\u95f4\u6240\u89c1\u7684\u5112\u96c5\u795e\u6001\uff0c\u4e00\u89c1\u5230\u90a3\u7537\u5973\u4e8c\u4eba\uff0c\u6012\u58f0\u559d\u9053\uff1a\u201c\u597d\u554a\uff0c\u59dc\u5e08\u5f1f\u3001\u859b\u5e08\u59b9\u3002\u4f60\u4eec\u4e0b\u624b\u8d8a\u6765\u8d8a\u9634\u6bd2\u4e86\u3002\u201d \u3000\u3000\u90a3\u592b\u5987\u4fe9\u89c1\u4ed6\u8fd9\u822c\u6a21\u6837\uff0c\u4e5f\u4f3c\u9887\u51fa\u610f\u6599\u4e4b\u5916\u3002\u90a3\u5927\u6c49\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u8fd8\u8bf4\u6211\u4eec\u4e0b\u4e86\u9634\u6bd2\uff1f\u4f60\u8eb2\u5728\u7af9\u7ba9\u4e4b\u4e2d\uff0c\u8c01\u53c8\u6599\u5f97\u5230\u4e86\uff1f\u6155\u5bb9\u5e08\u5144\u2026\u2026\u201d\u4ed6\u8bdd\u672a\u8bf4\u5b8c\uff0c\u90a3\u8001\u8005\u55c5\u4e86\u51e0\u4e0b\uff0c\u795e\u8272\u5927\u53d8\uff0c\u6025\u4ece\u6000\u4e2d\u6478\u51fa\u4e00\u679a\u836f\u4e38\uff0c\u653e\u5165\u53e3\u4e2d\u3002 \u3000\u3000\u90a3\u9a7c\u80cc\u5973\u5b50\u5c06\u6563\u53d1\u6d53\u70df\u7684\u8349\u836f\u4e00\u8db3\u8e0f\u706d\uff0c\u653e\u56de\u6000\u4e2d\uff0c\u8bf4\u9053\uff1a\u201c\u5927\u5e08\u5144\uff0c\u6765\u4e0d\u53ca\u5566\uff0c\u6765\u4e0d\u53ca\u5566\uff01\u201d \u3000\u3000\u90a3\u8001\u8005\u8138\u5982\u571f\u8272\uff0c\u9893\u7136\u5750\u5728\u5730\u4e0b\uff0c\u8fc7\u4e86\u534a\u664c\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u7b97\u6211\u683d\u4e86\u3002\u201d \u3000\u3000\u90a3\u5927\u6c49\u4ece\u6000\u4e2d\u6478\u51fa\u4e00\u4e2a\u9752\u8272\u74f7\u74f6\uff0c\u4e3e\u5728\u624b\u91cc\uff0c\u9053\uff1a\u201c\u89e3\u836f\u4fbf\u5728\u8fd9\u91cc\u3002\u4f60\u5e08\u4f84\u4e2d\u4e86\u4f60\u7684\u6bd2\u624b\uff0c\u5f97\u62ff\u89e3\u836f\u6765\u6362\u554a\u3002\u201d\u90a3\u8001\u8005\u9053\uff1a\u201c\u80e1\u8bf4\u516b\u9053\uff01\u4f60\u4eec\u8bf4\u662f\u5c0f\u94c1\u54e5\u4e48\uff1f\u6211\u51e0\u5e74\u6ca1\u89c1\u4ed6\u4e86\uff0c\u4e0b\u4ec0\u4e48\u6bd2\u624b\uff1f\u201d\u90a3\u9a7c\u80cc\u5973\u5b50\u9053\uff1a\u201c\u4f60\u7ea6\u6211\u4eec\u5230\u8fd9\u91cc\uff0c\u53ea\u662f\u8981\u8bf4\u8fd9\u53e5\u8bdd\u4e48\uff1f\u201d\u8f6c\u5934\u5411\u90a3\u5927\u6c49\u8bf4\u9053\uff1a\u201c\u94c1\u5c71\uff0c\u54b1\u4eec\u8d70\u5427\u3002\u201c\u8bf4\u7740\u6389\u5934\u4fbf\u8d70\u3002\u90a3\u5927\u6c49\u5c1a\u6709\u72b9\u8c6b\uff0c\u9053\uff1a\u201c\u5c0f\u94c1\u2026\u2026\u201d\u90a3\u5973\u5b50\u9053\uff1a\u201c\u4ed6\u6068\u54b1\u4eec\u5165\u9aa8\uff0c\u5b81\u53ef\u81ea\u5df1\u9001\u4e86\u6027\u547d\uff0c\u4e5f\u51b3\u4e0d\u80af\u9976\u8fc7\u5c0f\u94c1\u3002\u8fd9\u4e9b\u5e74\u6765\uff0c\u96be\u9053\u4f60\u8fd8\u60f3\u4e0d\u901a\uff1f\u201d\u90a3\u5927\u6c49\u60f3\u8d70\u53c8\u4e0d\u80af\u8d70\uff0c\u8bf4\u9053\uff1a\u201c\u5927\u5e08\u5144\uff0c\u54b1\u4eec\u591a\u5e74\u4ee5\u524d\u7684\u6028\u6068\uff0c\u5230\u8fd9\u65f6\u4f55\u5fc5\u518d\u653e\u5728\u5fc3\u4e0a\uff1f\u5c0f\u5f1f\u5949\u529d\u4e00\u53e5\uff0c\u8fd8\u662f\u4ea4\u6362\u89e3\u836f\uff0c\u628a\u8fd9\u4e2a\u7ed3\u5b50\u4e5f\u540c\u65f6\u89e3\u5f00\u4e86\u5427\uff01\u201d\u8fd9\u51e0\u53e5\u8bdd\u8bf4\u5f97\u751a\u662f\u8bda\u6073\u3002 \u3000\u3000\u90a3\u8001\u8005\u95ee\u9053\uff1a\u201c\u859b\u5e08\u59b9\uff0c\u5c0f\u94c1\u4e2d\u4e86\u4ec0\u4e48\u6bd2\uff1f\u201d\u90a3\u5973\u5b50\u51b7\u7b11\u4e00\u58f0\uff0c\u5e76\u4e0d\u56de\u7b54\u3002\u90a3\u5927\u6c49\u9053\uff1a\u201c\u5927\u5e08\u5144\uff0c\u5230\u8fd9\u5730\u6b65\uff0c\u4e5f\u4e0d\u7528\u5047\u60fa\u60fa\u4e86\u3002\u5c0f\u5f1f\u606d\u8d3a\u4f60\u79cd\u6210\u4e86\u4e03\u5fc3\u6d77\u68e0\u2026\u2026\u201d\u90a3\u8001\u8005\u5927\u58f0\u9053\uff1a\u201c\u8c01\u79cd\u6210\u4e86\u4e03\u5fc3\u6d77\u68e0\uff1f\u96be\u9053\u5c0f\u94c1\u4e2d\u7684\u662f\u4e03\u5fc3\u6d77\u68e0\u4e4b\u6bd2\uff1f\u6211\u6ca1\u6709\u554a\uff0c\u6211\u6ca1\u6709\u554a\u3002\u201d\u4ed6\u8bf4\u8fd9\u51e0\u53e5\u8bdd\u65f6\u795e\u60c5\u60f6\u6025\uff0c\u6050\u60e7\u4e4b\u610f\u89c1\u4e8e\u989c\u8272\u3002 \u3000\u3000\u4e24\u592b\u5987\u5bf9\u671b\u4e86\u4e00\u773c\uff0c\u5fc3\u4e2d\u5747\u60f3\uff1a\u201c\u96be\u9053\u4ed6\u5047\u88c5\u5f97\u8fd9\u822c\u50cf\uff1f\u201d \u3000\u3000\u90a3\u5973\u5b50\u9053\uff1a\u201c\u597d\uff0c\u6155\u5bb9\u5e08\u5144\uff0c\u5e9f\u8bdd\u5c11\u8bf4\u3002\u4f60\u7ea6\u6211\u4eec\u5230\u8fd9\u91cc\u6765\u76f8\u4f1a\uff0c\u6709\u4ec0\u4e48\u5429\u5490\uff1f\u201d\u90a3\u8001\u8005\u6414\u5934\u9053\uff1a\u201c\u6211\u6ca1\u6709\u7ea6\u554a\u3002\u662f\u4f60\u4eec\u628a\u6211\u642c\u5230\u8fd9\u91cc\u6765\uff0c\u600e\u4e48\u53cd\u8bf4\u662f\u6211\u76f8\u7ea6\uff1f\u201d\u8bf4\u5230\u8fd9\u91cc\uff0c\u53c8\u6c14\u53c8\u6127\uff0c\u7a81\u7136\u98de\u8d77\u4e00\u817f\uff0c\u5c06\u7af9\u7ba9\u8e22\u51fa\u4e86\u516d\u4e03\u4e08\u5916\u3002 \u3000\u3000\u90a3\u5973\u5b50\u51b7\u51b7\u5730\u9053\uff1a\u201c\u96be\u9053\u8fd9\u5c01\u4fe1\u4e5f\u4e0d\u662f\u4f60\u5199\u7684\uff1f\u5e08\u5144\u7684\u5b57\u8ff9\uff0c\u6211\u751f\u5e73\u77a7\u5f97\u4e5f\u4e0d\u7b97\u5c11\u4e86\u3002\u201d\u8bf4\u7740\u4ece\u6000\u4e2d\u53d6\u51fa\u4e00\u5f20\u7eb8\u7b3a\uff0c\u5de6\u624b\u4e00\u626c\uff0c\u90a3\u7eb8\u7b3a\u4fbf\u5411\u8001\u8005\u98de\u4e86\u8fc7\u53bb\u3002\u90a3\u8001\u8005\u4f38\u624b\u6b32\u63a5\uff0c\u7a81\u7136\u7f29\u624b\uff0c\u8ddf\u7740\u4e00\u638c\u53d1\u51fa\u3002\u638c\u98ce\u5c06\u90a3\u7eb8\u7b3a\u5728\u7a7a\u4e2d\u6321\u4e86\u4e00\u6321\uff0c\u5de6\u624b\u4e2d\u6307\u4e00\u5f39\uff0c\u53d1\u51fa\u4e86\u4e00\u679a\u6697\u5668\u3002\u8fd9\u6697\u5668\u662f\u4e00\u679a\u957f\u7ea6\u4e09\u5bf8\u7684\u900f\u9aa8\u9489\uff0c\u5c04\u5411\u7eb8\u7b3a\uff0c\u62cd\u7684\u4e00\u58f0\uff0c\u5c06\u7eb8\u7b3a\u9489\u5728\u6811\u4e0a\u3002 \u3000\u3000\u80e1\u6590\u6697\u81ea\u5bd2\u5fc3\uff1a\u201c\u8ddf\u8fd9\u4e9b\u4eba\u6253\u4ea4\u9053\uff0c\u5bf9\u65b9\u8bf4\u4e00\u53e5\u8bdd\uff0c\u55b7\u4e00\u53e3\u6c14\uff0c\u90fd\u8981\u63d0\u9632\u4ed6\u4e0b\u6bd2\u3002\u8fd9\u8001\u8005\u4e0d\u6562\u7528\u624b\u53bb\u63a5\u7b3a\uff0c\u81ea\u662f\u6015\u7b3a\u4e0a\u6709\u6bd2\u4e86\u3002\u201d\u53ea\u89c1\u9a7c\u80cc\u5973\u5b50\u63d0\u9ad8\u706f\u7b3c\u3002\u706b\u5149\u7167\u8000\u7eb8\u7b3a\uff0c\u767d\u7eb8\u4e0a\u4e24\u884c\u5927\u5b57\uff0c\u80e1\u6590\u867d\u5728\u8fdc\u5904\uff0c\u4e5f\u770b\u5f97\u6e05\u695a\uff0c\u89c1\u7eb8\u4e0a\u5199\u7740\u9053\uff1a\u201c\u59dc\u859b\u4e24\u4f4d\uff1a\u4e09\u66f4\u540e\u8bf7\u8d74\u9ed1\u864e\u6797\uff0c\u6709\u4e8b\u76f8\u5546\uff0c\u77e5\u540d\u4e0d\u5177\u3002\u201d \u3000\u3000\u90a3\u4e24\u884c\u5b57\u7b14\u81f4\u67af\u7626\uff0c\u5374\u9887\u633a\u62d4\uff0c\u5b57\u5982\u5176\u4eba\uff0c\u548c\u90a3\u8001\u8005\u7684\u8eab\u5f62\u9690\u9690\u7136\u6709\u76f8\u7c7b\u4e4b\u5904\u3002 \u3000\u3000\u90a3\u8001\u8005\u201c\u54a6\u201d\u7684\u4e00\u58f0\uff0c\u4f3c\u4e4e\u751a\u662f\u8be7\u5f02\u3002 \u3000\u3000\u90a3\u5927\u6c49\u95ee\u9053\uff1a\u201c\u5927\u5e08\u5144\uff0c\u6709\u4ec0\u4e48\u4e0d\u5bf9\u4e86\uff1f\u201d\u90a3\u8001\u8005\u51b7\u51b7\u5730\u9053\uff1a\u201c\u8fd9\u4fe1\u4e0d\u662f\u6211\u5199\u7684\u3002\u201d\u6b64\u8a00\u4e00\u51fa\uff0c\u592b\u5987\u4e24\u4eba\u5bf9\u671b\u4e86\u4e00\u773c\u3002\u90a3\u9a7c\u80cc\u5973\u5b50\u51b7\u7b11\u4e86\u4e00\u58f0\uff0c\u663e\u662f\u4e0d\u76f8\u4fe1\u4ed6\u7684\u8bf4\u8bdd\u3002\u90a3\u8001\u8005\u9053\uff1a\u201c\u4fe1\u4e0a\u7684\u7b14\u8ff9\uff0c\u5012\u771f\u548c\u6211\u7684\u4e66\u6cd5\u751a\u662f\u76f8\u50cf\uff0c\u8fd9\u53ef\u5947\u4e86\u3002\u201d\u4ed6\u4f38\u5de6\u624b\u6478\u4e86\u6478\u988f\u4e0b\u80e1\u987b\uff0c\u52c3\u7136\u6012\u9053\uff1a\u201c\u4f60\u4eec\u628a\u6211\u88c5\u5728\u7af9\u7ba9\u4e4b\u4e2d\uff0c\u62ac\u5230\u8fd9\u91cc\uff0c\u5230\u5e95\u5e72\u4ec0\u4e48\u6765\u5566\uff1f\u201d\u90a3\u5973\u5b50\u9053\uff1a\u201c\u5c0f\u94c1\u4e2d\u4e86\u4e03\u5fc3\u6d77\u68e0\u4e4b\u6bd2\uff0c\u4f60\u5230\u5e95\u7ed9\u6cbb\u5462\uff0c\u8fd8\u662f\u4e0d\u7ed9\u6cbb\uff1f\u201d \u3000\u3000\u90a3\u8001\u8005\u9053\uff1a\u201c\u4f60\u62ff\u5f97\u7a33\u4e48\uff1f\u5f53\u771f\u662f\u4e03\u5fc3\u2026\u2026\u4e03\u5fc3\u6d77\u68e0\u4e48\uff1f\u201d\u8bf4\u5230\u201c\u4e03\u5fc3\u6d77\u68e0\u201d\u56db\u5b57\u65f6\u58f0\u97f3\u5fae\u98a4\uff0c\u8bed\u97f3\u4e2d\u6d41\u9732\u4e86\u5f3a\u70c8\u7684\u6050\u60e7\u4e4b\u610f\u3002 \u3000\u3000\u80e1\u6590\u542c\u5230\u8fd9\u91cc\uff0c\u5fc3\u4e2d\u6e10\u6e10\u660e\u767d\uff0c\u5b9a\u662f\u53e6\u5916\u6709\u4e00\u4e2a\u9ad8\u624b\u4ece\u4e2d\u62e8\u5f04\uff0c\u4ee5\u81f4\u8fd9\u4e09\u4eba\u8bf4\u6765\u8bf4\u53bb\uff0c\u8a00\u8bed\u603b\u662f\u4e0d\u80fd\u63a5\u69ab\u3002\u90a3\u4e48\u8fd9\u9ad8\u624b\u662f\u8c01\u5462\uff1f \u3000\u3000\u4ed6\u4e0d\u81ea\u7981\u5730\u8f6c\u5934\u5411\u8eab\u65c1\u7a0b\u7075\u7d20\u671b\u4e86\u4e00\u773c\uff0c\u4f46\u89c1\u5979\u4e00\u53cc\u6717\u82e5\u660e\u661f\u7684\u5927\u773c\u5728\u9ed1\u6697\u4e2d\u70af\u70af\u53d1\u5149\u3002\u96be\u9053\u8fd9\u4e2a\u9762\u9ec4\u808c\u7626\u7684\u5c0f\u59d1\u5a18\u7adf\u6709\u8fd9\u822c\u80fd\u8010\uff1f\u8fd9\u53ef\u592a\u4e5f\u4ee4\u4eba\u96be\u4ee5\u76f8\u4fe1\uff01 \u3000\u3000\u4ed6\u6b63\u81ea\u51dd\u601d\uff0c\u731b\u542c\u5f97\u4e00\u58f0\u5927\u559d\uff0c\u58f0\u97f3\u545c\u545c\uff0c\u6781\u662f\u602a\u5f02\uff0c\u5fd9\u56de\u8fc7\u5934\u6765\uff0c\u53ea\u89c1\u90a3\u8001\u8005\u548c\u90a3\u5bf9\u592b\u5987\u5df2\u6b3a\u8fd1\u5728\u4e00\u8d77\uff0c\u5404\u81ea\u8e72\u7740\u8eab\u5b50\uff0c\u53cc\u624b\u5411\u524d\u5e73\u63a8\uff0c\u516d\u638c\u76f8\u63a5\uff0c\u53e3\u4e2d\u9f50\u58f0\u201c\u545c\u545c\u201d\u800c\u547c\u3002\u8001\u8005\u559d\u58f0\u5cfb\u5389\uff0c\u5927\u6c49\u559d\u58f0\u7c97\u731b\uff0c\u90a3\u9a7c\u80cc\u5973\u5b50\u7684\u559d\u58f0\u5374\u9ad8\u800c\u5c16\u9510\u3002 \u3000\u3000\u4e09\u4eba\u7684\u559d\u58f0\u90fd\u662f\u4e00\u822c\u6f2b\u957f\uff0c\u8fde\u7eed\u4e0d\u65ad\u3002\u7a81\u7136\u4e4b\u95f4\uff0c\u559d\u58f0\u9f50\u6b62\uff0c\u53ea\u89c1\u90a3\u8001\u8005\u7eb5\u8eab\u540e\u8dc3\uff0c\u5bd2\u5149\u4e00\u95ea\uff0c\u53d1\u51fa\u4e00\u679a\u900f\u9aa8\u9489\uff0c\u5c06\u706f\u7b3c\u6253\u706d\uff0c\u8ddf\u7740\u90a3\u5927\u6c49\u5927\u53eb\u4e00\u58f0\uff1a\u201c\u554a\u54df\uff01\u201d\u663e\u662f\u4e2d\u4e86\u8001\u8005\u7684\u6697\u7b97\uff0c\u8eab\u4e0a\u53d7\u4f24\u3002 \u3000\u3000\u8fd9\u65f6\u6797\u4e2d\u9ed1\u6f06\u4e00\u56e2\uff0c\u53ea\u89c9\u56db\u4e0b\u91cc\u5904\u5904\u90fd\u662f\u5371\u673a\uff0c\u80e1\u6590\u987a\u624b\u62c9\u7740\u7a0b\u7075\u7d20\u7684\u624b\u5411\u540e\u4e00\u626f\uff0c\u81ea\u5df1\u5df2\u6321\u5728\u5979\u7684\u8eab\u524d\u3002\u8fd9\u4e00\u6321\u4ed6\u5b9e\u662f\u672a\u7ecf\u601d\u7d22\uff0c\u53ea\u89c9\u51f6\u9669\u8feb\u8fd1\uff0c\u975e\u5c3d\u529b\u4fdd\u62a4\u8fd9\u4e2a\u5f31\u5973\u5b50\u4e0d\u53ef\uff0c\u81f3\u4e8e\u51ed\u4ed6\u4e4b\u529b\u662f\u5426\u4fdd\u62a4\u5f97\u4e86\uff0c\u5374\u7edd\u672a\u60f3\u5230\u3002 \u3000\u3000\u90a3\u5927\u6c49\u53eb\u4e86\u8fd9\u4e00\u4e0b\u4e4b\u540e\uff0c\u7acb\u5373\u5bc2\u7136\u65e0\u58f0\uff0c\u6811\u6797\u4e2d\u867d\u7136\u5171\u6709\u4e94\u4eba\uff0c\u7adf\u662f\u6ca1\u534a\u70b9\u58f0\u606f\u3002 \u3000\u3000\u80e1\u6590\u53c8\u542c\u5230\u4e86\u8349\u95f4\u7684\u866b\u58f0\uff0c\u542c\u5230\u8fdc\u5904\u732b\u5934\u9e70\u7684\u5495\u5495\u800c\u9e23\u3002 \u3000\u3000\u5ffd\u7136\u4e4b\u95f4\uff0c\u4e00\u53ea\u8f6f\u8f6f\u7684\u5c0f\u624b\u4f38\u4e86\u8fc7\u6765\uff0c\u63e1\u4f4f\u4e86\u4ed6\u7c97\u5927\u7684\u624b\u638c\u3002 \u3000\u3000\u80e1\u6590\u8eab\u5b50\u4e00\u98a4\uff0c\u968f\u5373\u77e5\u9053\u8fd9\u662f\u7a0b\u7075\u7d20\u7684\u624b\uff0c\u53ea\u89c9\u67d4\u5ae9\u7ea4\u7ec6\uff0c\u5012\u50cf\u5341\u4e00\u4e8c\u5c81\u5973\u7ae5\u7684\u624b\u638c\u4e00\u822c\u3002 \u3000\u3000\u5728\u4e00\u7247\u5bc2\u9759\u4e4b\u4e2d\uff0c\u773c\u524d\u5ffd\u5730\u5347\u8d77\u4e24\u80a1\u8885\u8885\u7684\u70df\u96fe\uff0c\u4e00\u767d\u4e00\u7070\uff0c\u4e24\u80a1\u70df\u50cf\u4e24\u6761\u6d3b\u86c7\u4e00\u822c\uff0c\u81ea\u4e24\u65c1\u5411\u4e2d\u592e\u6e38\u53bb\uff0c\u4e92\u76f8\u649e\u51fb\u3002\u540c\u65f6\u55e4\u55e4\u7684\u8f7b\u54cd\u4e0d\u7edd\uff0c\u80e1\u6590\u5728\u9ed1\u6697\u4e2d\u7741\u5927\u4e86\u773c\u775b\u89c2\u770b\uff0c\u9690\u7ea6\u89c1\u5230\u5de6\u53f3\u5404\u6709\u4e00\u70b9\u706b\u661f\u3002\u4e00\u70b9\u706b\u661f\u4e4b\u540e\u662f\u90a3\u4e2a\u8001\u8005\uff0c\u53e6\u4e00\u70b9\u706b\u661f\u4e4b\u540e\u662f\u90a3\u9a7c\u80cc\u5973\u5b50\u3002\u4e24\u4eba\u5404\u81ea\u8e72\u7740\u8eab\u5b50\uff0c\u7528\u529b\u9f13\u6c14\u5c06\u70df\u96fe\u5411\u5bf9\u65b9\u5439\u53bb\uff0c\u81ea\u662f\u70b9\u71c3\u4e86\u8349\u836f\uff0c\u53d1\u51fa\u6bd2\u70df\uff0c\u8981\u4ee4\u5bf9\u65b9\u4e2d\u6bd2\u3002 \u3000\u3000\u4e24\u4eba\u5439\u4e86\u597d\u4e00\u4f1a\uff0c\u6797\u4e2d\u70df\u96fe\u7030\u6f2b\uff0c\u8d8a\u6765\u8d8a\u6d53\u3002\u7a81\u7136\u4e4b\u95f4\uff0c\u90a3\u8001\u8005\u201c\u54a6\u201d\u7684\u4e00\u58f0\uff0c\u62ac\u5934\u77a7\u7740\u5148\u524d\u9489\u5728\u5927\u6811\u4e0a\u7684\u90a3\u5f20\u7eb8\u7b3a\u3002 \u3000\u3000\u80e1\u6590\u89c1\u90a3\u7eb8\u7b3a\u5fae\u5fae\u6447\u6643\uff0c\u4e0a\u9762\u53d1\u51fa\u95ea\u95ea\u5149\u8292\uff0c\u7adf\u662f\u5199\u7740\u53d1\u5149\u7684\u51e0\u884c\u5b57\u3002\u90a3\u592b\u5987\u4e8c\u4eba\u4e5f\u5927\u662f\u60ca\u5947\uff0c\u8f6c\u5934\u77a7\u53bb\uff0c\u53ea\u89c1\u90a3\u51e0\u884c\u5b57\u5199\u9053\uff1a\u201c\u5b57\u8c15\u6155\u5bb9\u666f\u5cb3\u3001\u59dc\u94c1\u5c71\u3001\u859b\u9e4a\u4e09\u5f92\u77e5\u6089\uff1a\u5c14\u7b49\u4e92\u76f8\u6b8b\u5bb3\uff0c\u4e0d\u5ff5\u5e08\u95e8\u4e4b\u8c0a\uff0c\u4f59\u751a\u538c\u4e4b\uff0c\u5b9c\u5373\u5c3d\u91ca\u524d\u6106\uff0c\u7ee7\u4f59\u9057\u5fd7\uff0c\u662f\u6240\u81f3\u5631\u3002\u4f59\u4e34\u7ec8\u4e4b\u60c5\uff0c\u7d20\u5f92\u5f53\u4e3a\u8be6\u544a\u4e5f\u3002\u50e7\u65e0\u55d4\u7edd\u7b14\u3002\u201d \u3000\u3000\u90a3\u8001\u8005\u548c\u5973\u5b50\u9f50\u58f0\u60ca\u547c\uff1a\u201c\u5e08\u7236\u6b7b\u4e86\u4e48\uff1f\u7a0b\u5e08\u59b9\uff0c\u4f60\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u7a0b\u7075\u7d20\u8f7b\u8f7b\u6323\u8131\u4e86\u80e1\u6590\u7684\u624b\uff0c\u4ece\u6000\u91cc\u53d6\u51fa\u4e00\u6839\u8721\u70db\uff0c\u6643\u706b\u6298\u70b9\u71c3\u4e86\uff0c\u7f13\u6b65\u8d70\u51fa\u3002 \u3000\u3000\u8001\u8005\u6155\u5bb9\u666f\u5cb3\u3001\u9a7c\u80cc\u5973\u5b50\u859b\u9e4a\u90fd\u662f\u8138\u8272\u5927\u53d8\uff0c\u5389\u58f0\u9053\uff1a\u201c\u5e08\u7236\u7684\u2018\u836f\u738b\u795e\u7bc7\u2019\u5462\uff1f\u662f\u4f60\u6536\u7740\u4e48\uff1f\u201d\u7a0b\u7075\u7d20\u51b7\u7b11\u9053\uff1a\u201c\u6155\u5bb9\u5e08\u5144\uff0c\u859b\u5e08\u59ca\uff0c\u5e08\u7236\u6559\u517b\u4f60\u4eec\u4e00\u751f\uff0c\u6069\u5fb7\u5982\u5c71\uff0c\u4f60\u4eec\u4e0d\u5173\u6000\u4ed6\u8001\u4eba\u5bb6\u751f\u6b7b\uff0c\u5374\u53ea\u95ee\u4ed6\u7684\u9057\u7269\uff0c\u672a\u514d\u592a\u8fc7\u65e0\u60c5\u3002\u59dc\u5e08\u5144\uff0c\u4f60\u600e\u4e48\u8bf4\uff1f\u201d \u3000\u3000\u90a3\u5927\u6c49\u59dc\u94c1\u5c71\u53d7\u4f24\u540e\u5012\u5728\u5730\u4e0b\uff0c\u542c\u7a0b\u7075\u7d20\u95ee\u53ca\uff0c\u62ac\u8d77\u5934\u6765\uff0c\u6012\u9053\uff1a\u201c\u5c0f\u94c1\u4e4b\u4f24\uff0c\u5b9a\u662f\u4f60\u4e0b\u7684\u6bd2\u624b\uff0c\u8fd9\u91cc\u4e00\u5207\uff0c\u4e5f\u5fc5\u662f\u4f60\u8fd9\u4e2b\u5934\u4ece\u4e2d\u6363\u9b3c\uff01\u5feb\u5c06\u2018\u836f\u738b\u795e\u7bc7\u2019\u4ea4\u51fa\u6765\uff01\u201d\u7a0b\u7075\u7d20\u51dd\u76ee\u4e0d\u8bed\u3002\u6155\u5bb9\u666f\u5cb3\u559d\u9053\uff1a\u201c\u5e08\u7236\u504f\u5fc3\uff0c\u5b9a\u662f\u4ea4\u4e86\u7ed9\u4f60\uff01\u201d\u859b\u9e4a\u9053\uff1a\u201c\u5c0f\u5e08\u59b9\uff0c\u4f60\u5c06\u795e\u7bc7\u53d6\u51fa\u6765\uff0c\u5927\u4f19\u513f\u4e00\u540c\u89c2\u770b\u5427\u3002\u201d\u53e3\u543b\u4e2d\u8bf1\u9a97\u4e4b\u610f\u518d\u4e5f\u660e\u767d\u4e0d\u8fc7\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5e08\u7236\u7684\u2018\u836f\u738b\u795e\u7bc7\u2019\u786e\u662f\u4f20\u4e86\u7ed9\u6211\u3002\u201d \u3000\u3000\u5979\u987f\u4e86\u4e00\u987f\uff0c\u4ece\u6000\u4e2d\u53c8\u53d6\u51fa\u4e00\u5f20\u7eb8\u7b3a\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u5e08\u7236\u5199\u7ed9\u6211\u7684\u8c15\u5b57\uff0c\u4e09\u4f4d\u8bf7\u770b\u3002\u201d\u8bf4\u7740\u4ea4\u7ed9\u859b\u9e4a\u3002\u859b\u9e4a\u4f38\u624b\u5f85\u63a5\uff0c\u59dc\u94c1\u5c71\u559d\u9053\uff1a\u201c\u5e08\u59b9\uff0c\u5c0f\u5fc3\uff01\u201d\u859b\u9e4a\u731b\u5730\u7701\u609f\uff0c\u9000\u540e\u4e86\u4e00\u6b65\uff0c\u5411\u8eab\u524d\u7684\u4e00\u68f5\u5927\u6811\u4e00\u6307\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u53f9\u4e86\u53e3\u6c14\uff0c\u5728\u5934\u53d1\u4e0a\u62d4\u4e0b\u4e00\u679a\u94f6\u7c2a\uff0c\u63d2\u5728\u7b3a\u4e0a\uff0c\u624b\u4e00\u626c\uff0c\u8fde\u7c2a\u5e26\u7b3a\u98de\u5c04\u51fa\u53bb\uff0c\u9489\u5728\u6811\u4e0a\u3002 \u3000\u3000\u80e1\u6590\u89c1\u5979\u8fd9\u4e00\u4e0b\u51fa\u624b\uff0c\u529f\u592b\u751a\u662f\u4e0d\u5f31\uff0c\u5fc3\u60f3\uff1a\u201c\u771f\u60f3\u4e0d\u5230\u8fd9\u4e48\u4e00\u4e2a\u7626\u5f31\u5e7c\u5973\uff0c\u7adf\u4f1a\u8ddf\u8fd9\u4e09\u4eba\u662f\u540c\u95e8\u7684\u5e08\u5144\u59b9\u3002\u201d\u773c\u671b\u7eb8\u7b3a\uff0c\u501f\u7740\u5979\u624b\u4e2d\u8721\u70db\u7684\u4eae\u5149\uff0c\u89c1\u7b3a\u4e0a\u5199\u9053\uff1a\u201c\u5b57\u8c15\u7075\u7d20\u77e5\u6089\uff1a\u4f59\u6b7b\u4e4b\u540e\uff0c\u5c14\u5373\u4f20\u544a\u5e08\u5144\u5e08\u59ca\u3002\u4e09\u4eba\u4e2d\u82e5\u6709\u5ff5\u53ca\u8001\u50e7\u8005\uff0c\u5c14\u4ee5\u836f\u738b\u795e\u7bc7\u793a\u4e4b\u3002\u65e0\u60b2\u6078\u601d\u5ff5\u4e4b\u60c5\u8005\uff0c\u6069\u4e49\u5df2\u7edd\uff0c\u975e\u6211\u5f92\u77e3\u3002\u5207\u5207\u6b64\u5631\u3002\u50e7\u65e0\u55d4\u7edd\u7b14\u3002\u201d \u3000\u3000\u6155\u5bb9\u666f\u5cb3\u3001\u59dc\u94c1\u5c71\u3001\u859b\u9e4a\u4e09\u4eba\u770b\u4e86\u8fd9\u5f20\u8c15\u5b57\uff0c\u9762\u9762\u76f8\u89d1\uff0c\u5747\u601d\u81ea\u5df1\u53ea\u5173\u5ff5\u7740\u5e08\u7236\u7684\u9057\u7269\uff0c\u5bf9\u5e08\u7236\u56e0\u4f55\u53bb\u4e16\u56fa\u7136\u4e0d\u95ee\u4e00\u53e5\uff0c\u66f4\u65e0\u534a\u5206\u54c0\u75db\u60b2\u4f24\u4e4b\u610f\u3002\u4e09\u4eba\u53ea\u5446\u4e86\u4e00\u77ac\u4e4b\u95f4\uff0c\u7a81\u7136\u5927\u53eb\u4e00\u58f0\uff0c\u540c\u65f6\u53d1\u96be\uff0c\u9f50\u5411\u7a0b\u7075\u7d20\u6251\u6765\u3002 \u3000\u3000\u80e1\u6590\u53eb\u9053\uff1a\u201c\u7075\u59d1\u5a18\u5c0f\u5fc3\uff01\u201d\u98de\u7eb5\u800c\u51fa\uff0c\u773c\u89c1\u859b\u9e4a\u7684\u53cc\u638c\u5df2\u62cd\u5230\u7a0b\u7075\u7d20\u9762\u524d\uff0c\u5fd9\u8fd0\u638c\u529b\u5411\u524d\u51fb\u51fa\uff0c\u5355\u638c\u5bf9\u53cc\u638c\uff0c\u817e\u7684\u4e00\u58f0\uff0c\u5c06\u859b\u9e4a\u9707\u51fa\u4e8c\u4e08\u4ee5\u5916\uff0c\u53f3\u638c\u968f\u5373\u56de\u8f6c\uff0c\u4e00\u52fe\u4e00\u5e26\uff0c\u5201\u4f4f\u59dc\u94c1\u5c71\u7684\u624b\u8155\uff0c\u8fd0\u8d77\u592a\u6781\u62f3\u7684\u201c\u4e71\u73af\u8bc0\u201d\uff0c\u501f\u52bf\u4e00\u629b\uff0c\u59dc\u94c1\u5c71\u4e00\u4e2a\u80a5\u5927\u7684\u8eab\u8eaf\u76f4\u98de\u4e86\u51fa\u53bb\uff0c\u63b7\u5f97\u6bd4\u859b\u9e4a\u66f4\u8fdc\uff0c\u7ed3\u7ed3\u5b9e\u5b9e\u5730\u6454\u5728\u5730\u4e0b\u3002 \u3000\u3000\u539f\u6765\u8fd9\u4e24\u4eba\u867d\u7136\u64c5\u4e8e\u4e0b\u6bd2\uff0c\u6b66\u529f\u5374\u975e\u4e00\u6d41\u9ad8\u624b\uff01 \u3000\u3000\u4ed6\u56de\u8fc7\u8eab\u6765\uff0c\u5f85\u8981\u5bf9\u4ed8\u6155\u5bb9\u666f\u5cb3\uff0c\u53ea\u89c1\u4ed6\u6643\u4e86\u4e24\u6643\uff0c\u5ffd\u5730\u4e00\u4ea4\u8dcc\u5012\uff0c\u4fef\u5728\u5730\u4e0b\uff0c\u518d\u4e5f\u7ad9\u4e0d\u8d77\u6765\u3002 \u3000\u3000\u859b\u9e4a\u6c14\u5598\u5401\u5401\u5730\u9053\uff1a\u201c\u5c0f\u5e08\u59b9\uff0c\u4f60\u4f0f\u4e0b\u597d\u5389\u5bb3\u7684\u5e2e\u624b\u554a\uff0c\u8fd9\u5c0f\u4f19\u5b50\u662f\u8c01\uff1f\u201d \u3000\u3000\u80e1\u6590\u63a5\u53e3\u9053\uff1a\u201c\u6211\u59d3\u80e1\u540d\u6590\uff0c\u8d24\u592b\u5987\u6709\u4e8b\u5c3d\u7ba1\u627e\u6211\u4fbf\u662f\u2026\u2026\u201d\u7a0b\u7075\u7d20\u987f\u8db3\u9053\uff1a\u201c\u4f60\u8fd8\u8bf4\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u80e1\u6590\u4e00\u6014\uff0c\u53ea\u89c1\u59dc\u94c1\u5c71\u6162\u6162\u7ad9\u8d77\u8eab\u6765\uff0c\u592b\u5987\u4fe9\u5411\u80e1\u6590\u72e0\u72e0\u671b\u4e86\u4e00\u773c\uff0c\u76f8\u4e92\u6301\u6276\uff0c\u8dcc\u8dcc\u649e\u649e\u5730\u51fa\u4e86\u6811\u6797\u3002 \u3000"}),showOnHomepage:!0},{name:"\u7b2c\u5341\u7ae0\u3000\u4e03\u5fc3\u6d77\u68e0",content:(0,a.jsx)(a.Fragment,{children:"\u7a0b\u7075\u7d20\u5439\u706d\u4e86\u8721\u70db\uff0c\u653e\u5165\u6000\u4e2d\uff0c\u4e00\u58f0\u4e0d\u54cd\u3002\u80e1\u6590\u9053\uff1a\u201c\u7075\u59d1\u5a18\uff0c\u4f60\u8fd9\u6155\u5bb9\u5e08\u5144\u600e\u4e48\u4e86\uff1f\u201d\u7a0b\u7075\u7d20\u201c\u563f\u201d\u7684\u4e00\u58f0\uff0c\u5e76\u4e0d\u56de\u7b54\u3002\u8fc7\u4e86\u534a\u664c\uff0c\u80e1\u6590\u53c8\u95ee\u4e00\u53e5\uff0c\u7a0b\u7075\u7d20\u53c8\u662f\u201c\u54fc\u201d\u7684\u4e00\u4e0b\u3002\u80e1\u6590\u4f4e\u58f0\u9053\uff1a\u201c\u600e\u4e48\uff1f\u4f60\u5fc3\u91cc\u4e0d\u75db\u5feb\u4e48\uff1f\u201d\u7a0b\u7075\u7d20\u5e7d\u5e7d\u5730\u9053\uff1a\u201c\u6211\u8bf4\u7684\u8bdd\uff0c\u4f60\u6ca1\u4e00\u53e5\u653e\u5728\u5fc3\u4e0a\uff1f\u201d \u3000\u3000\u80e1\u6590\u4e00\u6014\uff0c\u8fd9\u624d\u60f3\u8d77\uff0c\u5979\u548c\u81ea\u5df1\u7ea6\u6cd5\u4e09\u7ae0\uff0c\u81ea\u5df1\u53ef\u4e00\u6761\u4e5f\u6ca1\u9075\u5b88\uff1a\u201c\u5979\u8981\u6211\u4e0d\u8ddf\u65c1\u4eba\u8bf4\u8bdd\uff0c\u6211\u4e0d\u4f46\u8bf4\u8bdd\uff0c\u8fd8\u81ea\u62a5\u59d3\u540d\u3002 \u3000\u3000\u5979\u8981\u6211\u4e0d\u8bb8\u52a8\u6b66\uff0c\u6211\u5374\u8fde\u6253\u4e24\u4eba\u3002\u5979\u53eb\u6211\u4e0d\u5f97\u79bb\u5f00\u5979\u8eab\u5b50\u4e09\u6b65\uff0c\u54b3\uff0c\u6211\u79bb\u5f00\u5979\u5341\u6b65\u4e5f\u4e0d\u6b62\u4e86\u2026\u2026\u201d\u8d8a\u60f3\u8d8a\u662f\u6b49\u7136\uff0c\u9053\uff1a\u201c\u771f\u5bf9\u4e0d\u8d77\uff0c\u53ea\u56e0\u4e3a\u6211\u89c1\u8fd9\u4e09\u4eba\u5f88\u662f\u51f6\u72e0\uff0c\u53ea\u6015\u4f24\u5230\u4e86\u4f60\uff0c\u5fc3\u4e2d\u7740\u6025\uff0c\u6240\u4ee5\u4ec0\u4e48\u90fd\u5fd8\u4e86\u3002\u201d \u3000\u3000\u7a0b\u7075\u7d20\u201c\u55e4\u201d\u7684\u4e00\u7b11\uff0c\u8bed\u97f3\u7a81\u8f6c\u67d4\u548c\uff0c\u9053\uff1a\u201c\u90a3\u4f60\u5168\u662f\u4e3a\u4e86\u6211\u5566\uff01\u81ea\u5df1\u5fd8\u5f97\u5e72\u5e72\u51c0\u51c0\uff0c\u5374\u628a\u9519\u5904\u90fd\u63a8\u5728\u65c1\u4eba\u8eab\u4e0a\uff0c\u597d\u4e0d\u5bb3\u81ca\uff01\u80e1\u5927\u54e5\uff0c\u4f60\u4e3a\u4ec0\u4e48\u8981\u81ea\u62a5\u59d3\u540d\uff1f\u8fd9\u5bf9\u592b\u59bb\u6700\u4f1a\u8bb0\u6068\uff0c\u4e00\u627e\u4e0a\u4e86\u4f60\uff0c\u9634\u9b42\u4e0d\u6563\uff0c\u96be\u7f20\u5f97\u7d27\u3002\u4ed6\u4eec\u660e\u6253\u4e0d\u8fc7\u4f60\uff0c\u6697\u4e2d\u4e0b\u8d77\u6bd2\u6765\uff0c\u5343\u65b9\u767e\u8ba1\uff0c\u795e\u51fa\u9b3c\u6ca1\uff0c\u4f60\u8fd9\u53ef\u662f\u9632\u4e0d\u80dc\u9632\u3002\u201d \u3000\u3000\u80e1\u6590\u53ea\u542c\u5f97\u5fc3\u4e2d\u53d1\u6bdb\uff0c\u5fc3\u60f3\u5979\u7684\u8bdd\u5012\u975e\u5f20\u5927\u5176\u8f9e\uff0c\u4f46\u4e8b\u5df2\u5982\u6b64\uff0c\u6015\u4e5f\u6789\u7136\u3002\u7a0b\u7075\u7d20\u53c8\u95ee\uff1a\u201c\u4f60\u5e72\u4e48\u628a\u59d3\u540d\u8bf4\u7ed9\u4ed6\u592b\u5987\u77e5\u9053\uff1f\u201d\u80e1\u6590\u8f7b\u8f7b\u4e00\u7b11\uff0c\u5e76\u4e0d\u56de\u7b54\u3002\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u4f60\u6253\u4e86\u4ed6\u4eec\u4e8c\u4eba\uff0c\u53ea\u6015\u4ed6\u4eec\u627e\u4e0a\u6211\uff0c\u662f\u4e0d\u662f\uff1f\u4f60\u8981\u628a\u4e00\u5207\u90fd\u63fd\u5728\u81ea\u5df1\u8eab\u4e0a\u3002\u80e1\u5927\u54e5\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4e00\u76f4\u5f85\u6211\u8fd9\u6837\u597d\uff1f\u201d\u6700\u540e\u8fd9\u4e24\u53e5\u8bdd\u8bf4\u5f97\u751a\u662f\u6e29\u67d4\uff0c\u80e1\u6590\u5728\u9ed1\u6697\u4e2d\u867d\u77a7\u4e0d\u89c1\u5979\u7684\u9762\u5bb9\uff0c\u4f46\u60f3\u6765\u4e5f\u5fc5\u662f\u795e\u8272\u67d4\u548c\uff0c\u5f53\u4e0b\u4e5f\u5f88\u8bda\u6073\u7684\u9053\uff1a\u201c\u4f60\u4e00\u76f4\u7167\u987e\u6211\uff0c\u4f7f\u6211\u907f\u5374\u5371\u96be\u3002\u5c06\u5fc3\u6bd4\u5fc3\uff0c\u6211\u81ea\u7136\u5f53\u4f60\u662f\u597d\u670b\u53cb\u5566\u3002\u201d \u3000\u3000\u7a0b\u7075\u7d20\u5f88\u662f\u9ad8\u5174\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u771f\u7684\u628a\u6211\u5f53\u4f5c\u597d\u670b\u53cb\u4e48\uff1f\u90a3\u4e48\u6211\u5148\u6551\u4f60\u4e00\u547d\u518d\u8bf4\u3002\u201d\u80e1\u6590\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u5f97\u70b9\u4e2a\u706b\uff0c\u90a3\u706f\u7b3c\u5462\uff1f\u201d\u4fef\u8eab\u53bb\u6478\u859b\u9e4a\u4e22\u4e0b\u7684\u90a3\u53ea\u706f\u7b3c\uff0c\u4f46\u5728\u9ed1\u6697\u4e4b\u4e2d\u4e00\u65f6\u6478\u4e0d\u5230\uff0c\u4e0d\u77e5\u5979\u662f\u4e22\u5728\u54ea\u4e00\u5904\u8349\u4e1b\u4e4b\u4e2d\u3002\u80e1\u6590\u9053\uff1a\u201c\u4f60\u6000\u91cc\u4e0d\u662f\u8fd8\u6709\u534a\u622a\u8721\u70db\u4e48\uff1f\u201d\u7a0b\u7075\u7d20\u7b11\u9053\uff1a\u201c\u4f60\u8981\u5c0f\u547d\u513f\u4e0d\u8981\uff1f\u8fd9\u662f\u7528\u4e03\u5fc3\u6d77\u68e0\u505a\u7684\u8721\u70db\u554a\u2026\u2026\u55ef\uff0c\u55ef\uff0c\u5728\u8fd9\u513f\u4e86\u3002\u201d\u5979\u5728\u8349\u4e1b\u4e2d\u6478\u5230\u4e86\u706f\u7b3c\uff0c\u6643\u706b\u6298\u70b9\u71c3\u4e86\uff0c\u9ed1\u9edd\u9edd\u7684\u68ee\u6797\u4e4b\u4e2d\uff0c\u767b\u65f6\u751f\u8d77\u4e00\u56e2\u6de1\u9ec4\u7684\u5149\u4eae\uff0c\u5c06\u4e24\u4eba\u7f69\u5728\u706f\u7b3c\u5149\u4e0b\u3002 \u3000\u3000\u80e1\u6590\u542c\u5230\u59dc\u94c1\u5c71\u592b\u5987\u548c\u6155\u5bb9\u666f\u5cb3\u63a5\u8fde\u51e0\u6b21\u8bf4\u8d77\u201c\u4e03\u5fc3\u6d77\u68e0\u201d\u56db\u5b57\uff0c\u4f3c\u4e4e\u90a3\u662f\u4e00\u4ef6\u6781\u5389\u5bb3\u7684\u6bd2\u7269\uff0c\u706f\u7b3c\u5149\u4e0b\u89c1\u6155\u5bb9\u666f\u5cb3\u4fef\u4f0f\u5728\u5730\uff0c\u4e00\u52a8\u4e5f\u4e0d\u52a8\uff0c\u4f3c\u4e4e\u5df2\u7136\u50f5\u6bd9\uff0c\u5fc3\u4e0b\u767b\u65f6\u7701\u609f\uff0c\u201c\u554a\u201d\u7684\u4e00\u58f0\u53eb\u4e86\u51fa\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u82e5\u975e\u6211\u9c81\u83bd\u51fa\u624b\uff0c\u90a3\u59dc\u94c1\u5c71\u592b\u5987\u4e5f\u7ed9\u4f60\u5236\u670d\u4e86\u3002\u201d\u7a0b\u7075\u7d20\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u662f\u4e3a\u6211\u7684\u4e00\u4efd\u597d\u5fc3\uff0c\u80e1\u5927\u54e5\uff0c\u6211\u8fd8\u662f\u9886\u4f60\u7684\u60c5\u3002\u201d \u3000\u3000\u80e1\u6590\u671b\u7740\u5979\u4f3c\u4e4e\u5f31\u4e0d\u7981\u98ce\u7684\u8eab\u5b50\uff0c\u5fc3\u4e0b\u597d\u751f\u60ed\u6127\uff1a\u201c\u5979\u5e74\u7eaa\u8fd8\u5c0f\u6211\u51e0\u5c81\uff0c\u4f46\u8fd9\u822c\u667a\u8ba1\u767e\u51fa\uff0c\u6211\u6789\u7136\u81ea\u8d1f\u806a\u660e\uff0c\u54ea\u91cc\u53ca\u5f97\u4e0a\u5979\u534a\u5206\u3002\u201d\u8fd9\u65f6\u5df2\u660e\u767d\u5176\u4e2d\u9053\u7406\uff0c\u7a0b\u7075\u7d20\u7684\u8721\u70db\u4e43\u662f\u7528\u5267\u6bd2\u7684\u836f\u7269\u5236\u6210\uff0c\u70b9\u71c3\u4e4b\u540e\uff0c\u53d1\u51fa\u7684\u6bd2\u6c14\u65e2\u65e0\u81ed\u5473\uff0c\u53c8\u65e0\u70df\u96fe\uff0c\u56e0\u6b64\u8fde\u6155\u5bb9\u666f\u5cb3\u7b49\u4e09\u4e2a\u4f7f\u6bd2\u7684\u5927\u884c\u5bb6\u4e5f\u5815\u5176\u672f\u4e2d\u800c\u4e0d\u81ea\u89c9\u3002 \u3000\u3000\u81ea\u5df1\u82e5\u4e0d\u8d38\u7136\u51fa\u624b\uff0c\u90a3\u4e48\u59dc\u94c1\u5c71\u592b\u5987\u591a\u95fb\u4e86\u4e00\u4f1a\u8721\u70db\u7684\u6bd2\u6c14\uff0c\u5fc5\u5b9a\u6655\u5012\u3002\u4f46\u90a3\u65f6\u4e24\u4eba\u6b63\u5939\u653b\u7a0b\u7075\u7d20\uff0c\u51fa\u624b\u51cc\u5389\uff0c\u53ea\u6015\u5c1a\u672a\u6655\u5012\uff0c\u5979\u5df2\u5148\u53d7\u5176\u5bb3\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u731c\u5230\u4ed6\u7684\u5fc3\u601d\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u7528\u624b\u6307\u78b0\u4e00\u4e0b\u6211\u80a9\u5934\u7684\u8863\u670d\u3002\u201d\u80e1\u6590\u4e0d\u660e\u5979\u7684\u7528\u610f\uff0c\u4f46\u4f9d\u8a00\u4f38\u51fa\u98df\u6307\uff0c\u8f7b\u8f7b\u5728\u5979\u80a9\u4e0a\u629a\u4e86\u4e00\u4e0b\uff0c\u7a81\u7136\u98df\u6307\u6709\u5982\u706b\u7099\uff0c\u4e0d\u7981\u5168\u8eab\u90fd\u8df3\u4e86\u8d77\u6765\u3002\u7a0b\u7075\u7d20\u89c1\u4ed6\u8fd9\u4e00\u8df3\u60c5\u5f62\u6781\u662f\u72fc\u72c8\uff0c\u683c\u683c\u4e00\u9635\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4ed6\u592b\u5987\u82e5\u662f\u6293\u4f4f\u6211\u7684\u8863\u670d\uff0c\u90a3\u6ecb\u5473\u4fbf\u662f\u8fd9\u822c\u4e86\u3002\u201d \u3000\u3000\u80e1\u6590\u5c06\u98df\u6307\u5728\u7a7a\u4e2d\u6447\u4e86\u51e0\u6447\uff0c\u53ea\u89c9\u7099\u75db\u672a\u5df2\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u5bb6\u4f19\uff01\u4f60\u8863\u886b\u4e0a\u653e\u4e86\u4ec0\u4e48\u6bd2\u836f\uff1f\u8fd9\u4e48\u5389\u5bb3\uff1f\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u8fd9\u662f\u8d64\u874e\u7c89\uff0c\u4e5f\u6ca1\u4ec0\u4e48\u4e86\u4e0d\u8d77\u3002\u201d\u80e1\u6590\u4f38\u98df\u6307\u5728\u706f\u7b3c\u7684\u706b\u5149\u4e0b\u4e00\u770b\uff0c\u53ea\u89c1\u624b\u6307\u4e0a\u5df2\u8d77\u4e86\u4e00\u4e2a\u4e2a\u7ec6\u6ce1\uff0c\u5fc3\u60f3\uff1a\u201c\u9ed1\u6697\u4e4b\u4e2d\uff0c\u5e78\u4e8f\u6211\u6ca1\u78b0\u5230\u5979\u7684\u8863\u886b\uff0c\u5426\u5219\u90a3\u8fd8\u4e86\u5f97\u3002\u201d \u3000\u3000\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u80e1\u5927\u54e5\uff0c\u4f60\u522b\u602a\u6211\u53eb\u4f60\u4e0a\u5f53\u3002\u6211\u662f\u8981\u4f60\u77e5\u9053\uff0c\u4e0b\u6b21\u78b0\u5230\u6211\u8fd9\u4e09\u4e2a\u5e08\u5144\u5e08\u59ca\uff0c\u5f53\u771f\u8981\u5904\u5904\u63d0\u9632\u3002\u4f60\u6b66\u529f\u81ea\u7136\u6bd4\u4ed6\u4eec\u9ad8\u660e\u5f97\u592a\u591a\uff0c\u4f46\u4f60\u77a7\u77a7\u4f60\u7684\u624b\u638c\u3002\u201d \u3000\u3000\u80e1\u6590\u4f38\u638c\u4e00\u770b\uff0c\u4e0d\u89c1\u6709\u4f55\u5f02\u72b6\u3002\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u4f60\u5728\u706f\u7b3c\u524d\u7167\u7167\u3002\u201d\u80e1\u6590\u4f38\u638c\u5230\u706f\u7b3c\u4e4b\u524d\uff0c\u53ea\u89c1\u638c\u5fc3\u9690\u9690\u4f3c\u6709\u4e00\u5c42\u9ed1\u6c14\uff0c\u5fc3\u4e2d\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u4ed6\u2026\u2026\u4ed6\u4eec\u4e24\u4eba\u7ec3\u8fc7\u6bd2\u7802\u638c\u4e48\uff1f\u201d\u7a0b\u7075\u7d20\u6de1\u6de1\u5730\u9053\uff1a\u201c\u6bd2\u624b\u836f\u738b\u7684\u5f1f\u5b50\uff0c\u5c82\u6709\u4e0d\u7ec3\u6bd2\u7802\u638c\u4e4b\u7406\uff1f\u201d \u3000\u3000\u80e1\u6590\u201c\u554a\u201d\u7684\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u539f\u6765\u5c0a\u5e08\u65e0\u55d4\u5927\u5e08\uff0c\u624d\u662f\u771f\u6b63\u7684\u6bd2\u624b\u836f\u738b\u3002\u4ed6\u8001\u4eba\u5bb6\u53bb\u4e16\u4e86\u4e48\uff1f\u600e\u4e48\u4f60\u8fd9\u51e0\u4f4d\u5e08\u5144\u5e08\u59ca\u5982\u6b64\u65e0\u60c5\u65e0\u4e49\uff1f\u201d \u3000\u3000\u7a0b\u7075\u7d20\u8f7b\u8f7b\u53f9\u4e86\u53e3\u6c14\uff0c\u5230\u5927\u6811\u4e0a\u62d4\u4e0b\u94f6\u7c2a\u548c\u900f\u9aa8\u9489\uff0c\u5c06\u5e08\u7236\u7684\u4e24\u5f20\u5b57\u8c15\u6298\u597d\uff0c\u653e\u56de\u6000\u4e2d\u3002\u8fd9\u65f6\u7b2c\u4e00\u5f20\u5b57\u8c15\u4e0a\u53d1\u5149\u7684\u5b57\u8ff9\u5df2\u9690\u6ca1\u4e0d\u89c1\uff0c\u53ea\u9732\u51fa\u201c\u77e5\u540d\u4e0d\u5177\u201d\u6240\u5199\u7684\u90a3\u4e24\u884c\u9ed1\u5b57\u3002 \u3000\u3000\u80e1\u6590\u9053\uff1a\u201c\u8fd9\u5b57\u6761\u662f\u4f60\u5199\u7684\uff1f\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u662f\u554a\uff0c\u5e08\u7236\u90a3\u91cc\u6709\u6211\u5927\u5e08\u5144\u624b\u6284\u7684\u836f\u7ecf\u3002\u4ed6\u7684\u5b57\u6211\u770b\u5f97\u719f\u4e86\u3002\u53ea\u662f\u8fd9\u51e0\u884c\u5b57\u5b66\u5f97\u4e0d\u597d\uff0c\u5f97\u5176\u5f62\u800c\u4e0d\u80fd\u5f97\u5176\u795e\u3002\u4ed6\u7684\u4e66\u6cd5\u8fd8\u8981\u5cfb\u5ced\u5f97\u591a\u3002\u201d \u3000\u3000\u80e1\u6590\u6b66\u529f\u867d\u5f3a\uff0c\u4f46\u81ea\u5e7c\u65e0\u4eba\u6559\u4ed6\u8bfb\u4e66\uff0c\u56e0\u6b64\u8bf4\u5230\u4e66\u6cd5\u4ec0\u4e48\uff0c\u90a3\u662f\u4e00\u7a8d\u4e0d\u901a\uff0c\u542c\u5979\u8fd9\u4e48\u8bf4\uff0c\u4e00\u53e5\u8bdd\u4e5f\u63a5\u4e0d\u4e0a\u53bb\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u5e08\u7236\u7684\u624b\u8c15\u5411\u6765\u662f\u7528\u4e09\u70bc\u77fe\u6c34\u6240\u5199\uff0c\u8981\u5728\u706b\u4e0a\u4e00\u70d8\uff0c\u65b9\u59cb\u663e\u73b0\uff0c\u6211\u53c8\u7528\u864e\u9aa8\u7684\u9aa8\u9ad3\u63cf\u4e86\u4e00\u904d\uff0c\u9ed1\u6697\u4e4b\u4e2d\u4fbf\u53d1\u95ea\u5149\u4e86\u3002\u4f60\u77a7\uff01\u201d\u8bf4\u7740\u7184\u4e86\u706f\u706b\uff0c\u7eb8\u7b3a\u4e0a\u679c\u7136\u73b0\u51fa\u5979\u5e08\u7236\u624b\u8c15\u95ea\u5149\u5b57\u8ff9\uff0c\u5f85\u5f97\u70b9\u4eae\u706f\u7b3c\uff0c\u95ea\u5149\u4e4b\u5b57\u9690\u6ca1\uff0c\u770b\u5230\u7684\u53ea\u662f\u7a0b\u7075\u7d20\u6240\u5199\u7684\u77ed\u7b80\u3002\u8fd9\u77ed\u7b80\u81ea\u662f\u5199\u5728\u624b\u8c15\u7684\u4e24\u884c\u4e4b\u95f4\u3002\u56e0\u6b64\u540c\u662f\u4e00\u5f20\u7eb8\u7b3a\uff0c\u5149\u4eae\u65f6\u73b0\u77ed\u7b80\uff0c\u9ed1\u6697\u4e2d\u89c1\u624b\u8c15\uff0c\u8bf4\u7a7f\u4e86\u6beb\u4e0d\u5e0c\u5947\u3002\u4f46\u6155\u5bb9\u666f\u5cb3\u7b49\u6b63\u81ea\u5168\u795e\u8d2f\u6ce8\uff0c\u4e92\u76f8\u6fc0\u6597\uff0c\u7a81\u89c1\u5e08\u7236\u7684\u624b\u8c15\u5728\u6811\u4e0a\u663e\u73b0\uff0c\u81ea\u4e0d\u514d\u8981\u5927\u5403\u4e00\u60ca\uff0c\u800c\u7a0b\u7075\u7d20\u518d\u624b\u6301\u8721\u70db\u8d70\u51fa\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u4ed6\u4eec\u53ea\u60f3\u7740\u5e08\u7236\u6240\u9057\u7684\u90a3\u90e8\u201c\u836f\u738b\u795e\u7bc7\u201d\uff0c\u7eb5\u7136\u7ec6\u5fc3\uff0c\u4e5f\u4e0d\u4f1a\u518d\u9632\u5230\u5979\u624b\u4e2d\u8721\u70db\u4f1a\u6563\u53d1\u6bd2\u6c14\u4e86\u3002 \u3000\u3000\u8fd9\u4e9b\u8be1\u5f02\u4e4b\u4e8b\u4e00\u4ef6\u4ef6\u7684\u63ed\u5f00\uff0c\u80e1\u6590\u604d\u7136\u5927\u609f\uff0c\u8138\u4e0a\u6d41\u9732\u51fa\u53c8\u660e\u767d\u4e86\u4e00\u4ef6\u4e8b\u7684\u559c\u8272\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u7b11\u9053\uff1a\u201c\u4f60\u4e2d\u4e86\u6bd2\u7802\u638c\uff0c\u600e\u4e48\u53cd\u800c\u9ad8\u5174\u4e86\uff1f\u201d\u80e1\u6590\u7b11\u9053\uff1a\u201c\u4f60\u7b54\u5141\u6551\u6211\u4e00\u547d\u7684\uff0c\u6709\u836f\u738b\u7684\u9ad8\u8db3\u5728\u6b64\uff0c\u6211\u8fd8\u62c5\u5fc3\u4e9b\u4ec0\u4e48\uff1f\u201d\u7a0b\u7075\u7d20\u5ae3\u7136\u4e00\u7b11\uff0c\u5ffd\u7136\u9f13\u6c14\u4e00\u5439\uff0c\u53c8\u5c06\u706f\u7b3c\u5439\u706d\u4e86\uff0c\u53ea\u542c\u5979\u8d70\u5230\u7af9\u7ba9\u4e4b\u65c1\uff0c\u745f\u745f\u7d22\u7d22\u5730\u53d1\u51fa\u4e00\u4e9b\u8f7b\u5fae\u7684\u54cd\u58f0\uff0c\u4e0d\u77e5\u5979\u5728\u7af9\u7ba9\u4e2d\u62ff\u4e9b\u4ec0\u4e48\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u56de\u6765\u70b9\u71c3\u4e86\u706f\u7b3c\u3002 \u3000\u3000\u80e1\u6590\u773c\u524d\u7a81\u7136\u4e00\u4eae\uff0c\u89c1\u5979\u5df2\u6362\u4e0a\u4e86\u4e00\u5957\u767d\u886b\u84dd\u88e4\u3002\u7a0b\u7075\u7d20\u7b11\u9053\uff1a\u201c\u8fd9\u8863\u886b\u4e0a\u6ca1\u6709\u6bd2\u7c89\u4e86\uff0c\u514d\u5f97\u4f60\u63d0\u5fc3\u540a\u80c6\uff0c\u552f\u6050\u4e00\u4e2a\u4e0d\u5c0f\u5fc3\uff0c\u78b0\u5230\u4e86\u6211\u7684\u8863\u670d\u3002\u201d\u80e1\u6590\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u4ec0\u4e48\u90fd\u60f3\u5230\u4e86\u3002\u6211\u5e74\u7eaa\u662f\u6d3b\u5728\u72d7\u8eab\u4e0a\u7684\uff0c\u6709\u4f60\u5341\u6210\u4e2d\u4e00\u6210\u806a\u660e\uff0c\u90a3\u4fbf\u597d\u4e86\u3002\u201d \u3000\u3000\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u6211\u5b66\u4e86\u4f7f\u7528\u6bd2\u836f\uff0c\u6574\u65e5\u4fbf\u5728\u601d\u91cf\u6253\u7b97\uff0c\u8981\u600e\u4e48\u4e0b\u6bd2\uff0c\u65c1\u4eba\u624d\u4e0d\u77e5\u89c9\uff0c\u53c8\u8981\u9632\u4eba\u53cd\u6765\u4e0b\u6bd2\uff0c\u6316\u7a7a\u5fc3\u601d\uff0c\u4fbf\u60f3\u8fd9\u79cd\u4e8b\u513f\u3002\u54b3\uff0c\u54ea\u53ca\u5f97\u4e0a\u4f60\u5fc3\u4e2d\u6d77\u9614\u5929\u7a7a\uff0c\u81ea\u7531\u81ea\u5728\uff1f\u201d\u8bf4\u7740\u8f7b\u8f7b\u53f9\u4e86\u53e3\u6c14\uff0c\u62c9\u8fc7\u80e1\u6590\u7684\u53f3\u624b\uff0c\u7528\u94f6\u7c2a\u5728\u4ed6\u6bcf\u6839\u624b\u6307\u4e0a\u523a\u4e86\u4e00\u4e2a\u5c0f\u5b54\uff0c\u7136\u540e\u53cc\u624b\u4e24\u6839\u5927\u62c7\u6307\u81ea\u4ed6\u638c\u5fc3\u5411\u624b\u6307\u6324\u8feb\uff0c\u5c0f\u5b54\u4e2d\u6d41\u51fa\u7684\u8840\u6db2\uff0c\u5e26\u6709\u7d2b\u9ed1\u4e4b\u8272\u3002\u5979\u9488\u523a\u7684\u90e8\u4f4d\u6070\u5230\u597d\u5904\uff0c\u7adf\u662f\u4e0d\u611f\u75db\u695a\uff0c\u63a8\u6324\u9ed1\u8840\uff0c\u624b\u52bf\u53c8\u6781\u662f\u7075\u5de7\uff0c\u8fc7\u4e0d\u591a\u65f6\uff0c\u51fa\u6765\u7684\u8840\u6db2\u6e10\u53d8\u9c9c\u7ea2\u3002 \u3000\u3000\u8fd9\u65f6\u4f0f\u5728\u5730\u4e0b\u7684\u6155\u5bb9\u666f\u5cb3\u7a81\u7136\u8eab\u5b50\u4e00\u52a8\u3002\u80e1\u6590\u9053\uff1a\u201c\u9192\u5566\uff01\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u4e0d\u4f1a\u9192\u7684\uff0c\u81f3\u5c11\u8fd8\u6709\u4e09\u4e2a\u65f6\u8fb0\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u521a\u624d\u6211\u628a\u4ed6\u6311\u4e86\u6765\uff0c\u8fd9\u4eba\u5c31\u50cf\u6b7b\u4e86\u4e00\u822c\uff0c\u6211\u4e00\u70b9\u4e5f\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u4ed6\u50f5\u662f\u50f5\u5f97\u5230\u4e86\u5bb6\uff0c\u6211\u7684\u50bb\u53ef\u4e5f\u50bb\u5f97\u5230\u4e86\u5bb6\u3002\u201d\u7a0b\u7075\u7d20\u5fae\u7b11\u9053\uff1a\u201c\u4f60\u53e3\u53e3\u58f0\u58f0\u8bf4\u81ea\u5df1\u50bb\uff0c\u90a3\u624d\u53eb\u4e0d\u50bb\u5462\u3002\u201d \u3000\u3000\u9694\u4e86\u4e00\u4f1a\uff0c\u80e1\u6590\u9053\uff1a\u201c\u4ed6\u4eec\u8001\u662f\u95ee\u4ec0\u4e48\u2018\u836f\u738b\u795e\u7bc7\u2019\uff0c\u90a3\u662f\u4e00\u90e8\u836f\u4e66\uff0c\u662f\u4e0d\u662f\uff1f\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u662f\u554a\uff0c\u8fd9\u662f\u6211\u5e08\u7236\u82b1\u4e86\u6bd5\u751f\u5fc3\u8840\u6240\u8457\u7684\u4e00\u90e8\u4e66\u3002\u7ed9\u4f60\u77a7\u77a7\u5427\uff01\u201d\u4f38\u624b\u5165\u6000\uff0c\u53d6\u51fa\u4e00\u4e2a\u5c0f\u5c0f\u5305\u88b1\uff0c\u6253\u5f00\u5916\u9762\u7684\u5e03\u5305\uff0c\u91cc\u9762\u662f\u4e00\u5c42\u6cb9\u7eb8\uff0c\u6cb9\u7eb8\u4e4b\u5185\uff0c\u624d\u662f\u4e00\u90e8\u516d\u5bf8\u957f\u3001\u56db\u5bf8\u5bbd\u7684\u9ec4\u7eb8\u4e66\u3002\u7a0b\u7075\u7d20\u7528\u94f6\u7c2a\u6311\u5f00\u4e66\u9875\uff0c\u53ea\u89c1\u6bcf\u4e00\u9875\u4e0a\u90fd\u5bc6\u5bc6\u9ebb\u9ebb\u5730\u5199\u6ee1\u4e86\u8747\u5934\u5c0f\u6977\uff0c\u4e0d\u8a00\u53ef\u77e5\uff0c\u8fd9\u4e66\u6bcf\u4e00\u9875\u4e0a\u90fd\u67d3\u6ee1\u5267\u6bd2\uff0c\u65e0\u77e5\u4e4b\u4eba\u968f\u624b\u4e00\u7ffb\uff0c\u975e\u5012\u5927\u9709\u4e0d\u53ef\u3002 \u3000\u3000\u80e1\u6590\u89c1\u5979\u5bf9\u81ea\u5df1\u63a8\u5fc3\u7f6e\u8179\uff0c\u4ec0\u4e48\u91cd\u5927\u7684\u79d8\u5bc6\u4e5f\u4e0d\u9690\u7792\uff0c\u5fc3\u4e2d\u81ea\u662f\u559c\u6b22\uff0c\u53ea\u662f\u89c1\u4e86\u8fd9\u90e8\u6bd2\u7ecf\u5fc3\u4e2d\u53d1\u6bdb\uff0c\u4f3c\u89c9\u591a\u77a7\u5f97\u51e0\u773c\uff0c\u8fde\u773c\u775b\u4e5f\u4f1a\u4e2d\u6bd2\uff0c\u4e0d\u81ea\u7981\u5730\u9732\u51fa\u754f\u7f29\u4e4b\u610f\u3002\u7a0b\u7075\u7d20\u5c06\u836f\u4e66\u5305\u597d\uff0c\u653e\u56de\u6000\u4e2d\uff0c\u7136\u540e\u53d6\u51fa\u4e00\u4e2a\u9ec4\u8272\u5c0f\u74f6\uff0c\u5012\u51fa\u4e00\u4e9b\u7d2b\u8272\u7c89\u672b\uff0c\u6577\u5728\u80e1\u6590\u624b\u6307\u7684\u9488\u5b54\u4e0a\uff0c\u5728\u4ed6\u624b\u81c2\u5173\u8282\u4e0a\u63a8\u62ff\u51e0\u4e0b\uff0c\u90a3\u4e9b\u7c89\u672b\u7adf\u4ece\u9488\u5b54\u4e2d\u5438\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u80e1\u6590\u559c\u9053\uff1a\u201c\u5927\u56fd\u624b\uff0c\u8fd9\u822c\u7684\u795e\u4e4e\u5176\u6280\uff0c\u6211\u4ece\u672a\u89c1\u8fc7\u3002\u201d\u7a0b\u7075\u7d20\u7b11\u9053\uff1a\u201c\u90a3\u7b97\u4ec0\u4e48\uff1f\u4f60\u82e5\u89c1\u6211\u5e08\u7236\u7ed9\u4eba\u5f00\u819b\u5256\u8179\u3001\u63a5\u9aa8\u7eed\u80a2\u7684\u672c\u4e8b\uff0c\u90a3\u624d\u53eb\u795e\u6280\u5462\u3002\u201d\u80e1\u6590\u60a0\u7136\u795e\u5f80\uff0c\u9053\uff1a\u201c\u662f\u554a\uff0c\u5c0a\u5e08\u867d\u7136\u64c5\u4e8e\u4f7f\u6bd2\uff0c\u4f46\u60f3\u6765\u4e5f\u5fc5\u64c5\u4e8e\u6cbb\u75c5\u6551\u4eba\uff0c\u5426\u5219\u600e\u80fd\u79f0\u5f97\u2018\u836f\u738b\u2019\u4e8c\u5b57\uff1f\u201d \u3000\u3000\u7a0b\u7075\u7d20\u8138\u4e0a\u73b0\u51fa\u559c\u5bb9\uff0c\u9053\uff1a\u201c\u6211\u5e08\u7236\u82e5\u662f\u542c\u5230\u4f60\u8fd9\u51e0\u53e5\u8bdd\uff0c\u4ed6\u4e00\u5b9a\u4f1a\u559c\u6b22\u4f60\u5f97\u7d27\uff0c\u8981\u8bf4\u4f60\u662f\u4ed6\u7684\u5c11\u5e74\u77e5\u5df1\u5462\u3002\u54b3\uff0c\u53ea\u53ef\u60dc\u4ed6\u8001\u4eba\u5bb6\u5df2\u4e0d\u5728\u4e86\u3002\u201d\u8bf4\u7740\u773c\u7736\u4e0d\u81ea\u7981\u7684\u7ea2\u4e86\u3002 \u3000\u3000\u80e1\u6590\u9053\uff1a\u201c\u4f60\u90a3\u9a7c\u80cc\u5e08\u59ca\u8bf4\u4f60\u5e08\u7236\u504f\u5fc3\uff0c\u53ea\u7ba1\u75bc\u7231\u5c0f\u5f92\u5f1f\uff0c\u8fd9\u8bdd\u591a\u534a\u4e0d\u5047\uff0c\u6211\u770b\u4e5f\u53ea\u4f60\u4e00\u4eba\uff0c\u624d\u8bb0\u7740\u5e08\u7236\u3002\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u6211\u5e08\u7236\u751f\u5e73\u6536\u4e86\u56db\u4e2a\u5f92\u513f\uff0c\u8fd9\u56db\u4eba\u7ed9\u4f60\u4e00\u665a\u4e0a\u90fd\u89c1\u5230\u4e86\u3002\u6155\u5bb9\u666f\u5cb3\u662f\u6211\u5927\u5e08\u5144\uff0c\u59dc\u94c1\u5c71\u662f\u4e8c\u5e08\u5144\uff0c\u859b\u9e4a\u662f\u4e09\u5e08\u59ca\u3002\u5e08\u7236\u672c\u6765\u4e0d\u60f3\u518d\u6536\u5f92\u513f\u4e86\uff0c\u4f46\u89c1\u6211\u4e09\u4f4d\u5e08\u5144\u5e08\u59ca\u95f9\u5f97\u592a\u4e0d\u50cf\u8bdd\uff0c\u53ea\u6015\u4ed6\u767e\u5e74\u4e4b\u540e\u65e0\u4eba\u5236\u5f97\u4ed6\u4eec\uff0c\u4e09\u4eba\u4e3a\u975e\u4f5c\u6b79\uff0c\u66f4\u8981\u8086\u65e0\u5fcc\u60ee\uff0c\u5bb3\u4eba\u4e0d\u6d45\uff0c\u56e0\u6b64\u5230\u5f97\u665a\u5e74\uff0c\u53c8\u6536\u4e86\u6211\u8fd9\u4e2a\u5e7c\u5f92\u3002\u201d\u5979\u987f\u4e86\u4e00\u987f\uff0c\u53c8\u9053\uff1a\u201c\u6211\u8fd9\u4e09\u4e2a\u5e08\u5144\u5e08\u59ca\u672c\u6027\u539f\u6765\u4e5f\u4e0d\u574f\uff0c\u53ea\u4e3a\u4e09\u5e08\u59ca\u5ac1\u4e86\u4e8c\u5e08\u5144\uff0c\u5927\u5e08\u5144\u548c\u4ed6\u4fe9\u7ed3\u4e0b\u6df1\u4ec7\uff0c\u4e09\u4e2a\u4eba\u8c01\u4e5f\u4e0d\u80af\u5e72\u4f11\uff0c\u5f04\u5230\u540e\u6765\u7adf\u7136\u96be\u4ee5\u6536\u62fe\u3002\u201d \u3000\u3000\u80e1\u6590\u70b9\u5934\u9053\uff1a\u201c\u4f60\u5927\u5e08\u5144\u4e5f\u60f3\u8981\u5a36\u4f60\u4e09\u5e08\u59ca\uff0c\u662f\u4e0d\u662f\uff1f\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u8fd9\u4e9b\u4e8b\u8fc7\u53bb\u5f88\u4e45\u4e86\uff0c\u6211\u4e5f\u4e0d\u5927\u660e\u767d\u3002\u53ea\u77e5\u9053\u5927\u5e08\u54e5\u672c\u6765\u662f\u6709\u5e08\u5ac2\u7684\uff0c\u4e09\u5e08\u59ca\u559c\u6b22\u5927\u5e08\u54e5\uff0c\u4fbf\u628a\u5e08\u5ac2\u6bd2\u6b7b\u4e86\u3002\u201d\u80e1\u6590\u201c\u554a\u201d\u7684\u4e00\u58f0\uff0c\u53ea\u89c9\u5b66\u4f1a\u4e86\u4e0b\u6bd2\u7684\u529f\u592b\uff0c\u5b9e\u662f\u5bb3\u591a\u5229\u5c11\uff0c\u81ea\u7136\u800c\u7136\u7684\u4f1a\u6b8b\u5fcd\u8d77\u6765\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u53c8\u9053\uff1a\u201c\u5927\u5e08\u54e5\u4e00\u6c14\u4e4b\u4e0b\uff0c\u7ed9\u4e09\u5e08\u59ca\u670d\u4e86\u4e00\u79cd\u6bd2\u836f\uff0c\u5bb3\u5f97\u5979\u9a7c\u4e86\u80cc\uff0c\u8ddb\u4e86\u811a\u3002\u4e8c\u5e08\u54e5\u6697\u4e2d\u4e00\u76f4\u559c\u6b22\u7740\u4e09\u5e08\u59ca\uff0c\u5979\u867d\u7136\u6b8b\u5e9f\uff0c\u5374\u5e76\u4e0d\u5acc\u5f03\uff0c\u4fbf\u548c\u5979\u6210\u4e86\u5a5a\u3002\u4e5f\u4e0d\u77e5\u600e\u4e48\uff0c\u4ed6\u4eec\u6210\u5a5a\u4e4b\u540e\uff0c\u5927\u5e08\u54e5\u5374\u53c8\u60f3\u5ff5\u8d77\u4e09\u5e08\u59ca\u7684\u8bf8\u822c\u597d\u5904\u6765\uff0c\u7adf\u7136\u53c8\u53bb\u7f20\u7740\u5979\u3002\u6211\u5e08\u7236\u7ed9\u4ed6\u4eec\u4e09\u4eba\u5f04\u5f97\u5341\u5206\u5fc3\u70e6\uff0c\u4e0d\u7ba1\u600e\u4e48\u5f00\u5bfc\u6559\u8bad\uff0c\u8fd9\u4e09\u4eba\u53cd\u53cd\u590d\u590d\uff0c\u603b\u662f\u7ea0\u7f20\u4e0d\u6e05\u3002\u5012\u662f\u6211\u4e8c\u5e08\u54e5\u4e3a\u4eba\u6bd4\u8f83\u6b63\u6d3e\uff0c\u5bf9\u59bb\u5b50\u59cb\u7ec8\u6ca1\u6709\u4e8c\u5fc3\u3002\u4ed6\u4eec\u5728\u8fd9\u6d1e\u5ead\u6e56\u8fb9\u7528\u751f\u94c1\u94f8\u4e86\u8fd9\u5ea7\u836f\u738b\u5e84\uff0c\u5e84\u5916\u53c8\u79cd\u4e86\u8840\u77ee\u6817\uff0c\u539f\u5148\u672c\u662f\u4e3a\u4e86\u9632\u5907\u5927\u5e08\u54e5\u7ea0\u7f20\uff0c\u540e\u6765\u4ed6\u592b\u5987\u4fe9\u5728\u6c5f\u6e56\u4e0a\u591a\u7ed3\u4ec7\u5bb6\uff0c\u8fd9\u836f\u738b\u5e84\u53c8\u6210\u4e86\u4ed6\u4eec\u907f\u4ec7\u4e4b\u5904\u4e86\u3002\u201d \u3000\u3000\u80e1\u6590\u70b9\u5934\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\u3002\u602a\u4e0d\u5f97\u6c5f\u6e56\u4e0a\u8bf4\u5230\u6bd2\u624b\u836f\u738b\u65f6\u8bf4\u6cd5\u4e0d\u540c\uff0c\u6709\u7684\u8bf4\u662f\u4e2a\u79c0\u624d\u76f8\u516c\uff0c\u6709\u7684\u8bf4\u662f\u4e2a\u7c97\u8c6a\u5927\u6c49\uff0c\u6709\u7684\u8bf4\u662f\u4e2a\u9a7c\u80cc\u5973\u5b50\uff0c\u66f4\u6709\u4eba\u8bf4\u662f\u4e2a\u8001\u548c\u5c1a\u3002\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u771f\u6b63\u7684\u6bd2\u624b\u836f\u738b\uff0c\u5176\u5b9e\u4e5f\u8bf4\u4e0d\u4e0a\u662f\u8c01\u3002\u6211\u5e08\u7236\u633a\u4e0d\u559c\u6b22\u8fd9\u4e2a\u540d\u5934\u3002\u4ed6\u8bf4\uff1a\u2018\u6211\u4f7f\u7528\u6bd2\u7269\uff0c\u662f\u4e3a\u4e86\u6cbb\u75c5\u6551\u4eba\uff0c\u79f0\u6211\u201c\u836f\u738b\u201d\uff0c\u90a3\u662f\u6127\u4e0d\u6562\u5f53\uff0c\u4e0a\u9762\u518d\u52a0\u201c\u6bd2\u624b\u201d\u4e8c\u5b57\uff0c\u96be\u9053\u65e0\u55d4\u8001\u548c\u5c1a\u662f\u968f\u4fbf\u6740\u4eba\u7684\u4e48\uff1f\u2019\u53ea\u56e0\u6211\u5e08\u7236\u4f7f\u7528\u6bd2\u7269\u51fa\u4e86\u540d\uff0c\u6211\u4e09\u4f4d\u5e08\u5144\u5e08\u59ca\u53c8\u4f7f\u5f97\u592a\u6ee5\uff0c\u6709\u65f6\u4e0d\u514d\u8bef\u4f24\u597d\u4eba\uff0c\u56e0\u6b64\u2018\u6bd2\u624b\u836f\u738b\u2019\u8fd9\u56db\u4e2a\u5b57\uff0c\u5728\u6c5f\u6e56\u4e0a\u540d\u5934\u5f04\u5f97\u5341\u5206\u54cd\u4eae\u3002\u5e08\u7236\u4e0d\u8bb8\u5e08\u5144\u5e08\u59ca\u6cc4\u9732\u5404\u4eba\u8eab\u5206\u59d3\u540d\uff0c\u8fd9\u4e48\u4e00\u6765\uff0c\u53ea\u8981\u4ec0\u4e48\u5730\u65b9\u6709\u4e86\u79bb\u5947\u7684\u4e0b\u6bd2\u6848\u4ef6\uff0c\u4e00\u5207\u5e10\u4fbf\u90fd\u7b97\u5728\u2018\u6bd2\u624b\u836f\u738b\u2019\u56db\u5b57\u5934\u4e0a\uff0c\u4f60\u77a7\u51a4\u662f\u4e0d\u51a4\uff1f\u201d \u3000\u3000\u80e1\u6590\u9053\uff1a\u201c\u90a3\u4f60\u5e08\u7236\u8be5\u5f53\u51fa\u5934\u8fa9\u4e2a\u660e\u767d\u554a\u3002\u201d\u7a0b\u7075\u7d20\u53f9\u9053\uff1a\u201c\u8fd9\u79cd\u4e8b\u4e5f\u662f\u8fa9\u4e0d\u80dc\u8fa9\u2026\u2026\u201d\u8bf4\u5230\u8fd9\u91cc\uff0c\u5df2\u5c06\u80e1\u6590\u7684\u4e94\u53ea\u624b\u6307\u63a8\u62ff\u6577\u836f\u5b8c\u6bd5\uff0c\u7ad9\u8d77\u8eab\u6765\uff0c\u9053\uff1a\u201c\u54b1\u4eec\u4eca\u665a\u8fd8\u6709\u4e24\u4ef6\u4e8b\u8981\u529e\uff0c\u82e5\u4e0d\u662f\u2026\u2026\u201d\u8bf4\u5230\u8fd9\u91cc\u7a81\u7136\u4f4f\u53e3\uff0c\u5fae\u5fae\u4e00\u7b11\u3002 \u3000\u3000\u80e1\u6590\u63a5\u53e3\u9053\uff1a\u201c\u82e5\u4e0d\u662f\u6211\u4e0d\u542c\u8bdd\uff0c\u8fd9\u4e24\u4ef6\u4e8b\u5c31\u6613\u529e\u5f97\u5f88\uff0c\u73b0\u4e0b\u4e0d\u514d\u8981\u5927\u8d39\u624b\u811a\u3002\u201d \u3000\u3000\u7a0b\u7075\u7d20\u7b11\u9053\uff1a\u201c\u4f60\u77e5\u9053\u5c31\u597d\u5566\uff0c\u8d70\u5427\uff01\u201d\u80e1\u6590\u6307\u7740\u8eba\u5728\u5730\u4e0b\u7684\u6155\u5bb9\u666f\u5cb3\u9053\uff1a\u201c\u53c8\u8981\u8bf7\u541b\u5165\u7ba9\uff1f\u201d\u7a0b\u7075\u7d20\u7b11\u9053\uff1a\u201c\u52b3\u60a8\u7684\u5927\u9a7e\u3002\u201d\u80e1\u6590\u6293\u8d77\u6155\u5bb9\u666f\u5cb3\u80cc\u4e0a\u8863\u670d\uff0c\u5c06\u4ed6\u653e\u5165\u7af9\u7ba9\uff0c\u653e\u5728\u80a9\u4e0a\u6311\u8d77\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u5728\u524d\u9886\u8def\uff0c\u5374\u662f\u5411\u897f\u5357\u65b9\u800c\u884c\uff0c\u8d70\u4e86\u4e09\u91cc\u6a21\u6837\uff0c\u6765\u5230\u4e00\u5ea7\u5c0f\u5c4b\u4e4b\u524d\uff0c\u53eb\u9053\uff1a\u201c\u738b\u5927\u53d4\uff0c\u53bb\u5427\uff01\u201d\u5c4b\u95e8\u6253\u5f00\uff0c\u51fa\u6765\u4e00\u4e2a\u6c49\u5b50\uff0c\u5168\u8eab\u9ed1\u6f06\u6f06\u7684\uff0c\u6311\u7740\u4e00\u526f\u62c5\u5b50\u3002\u80e1\u6590\u5fc3\u60f3\uff1a\u201c\u53c8\u6709\u5947\u4e8b\u51fa\u6765\u5566\uff01\u201d\u6709\u4e86\u524d\u8f66\u4e4b\u9274\uff0c\u54ea\u91cc\u8fd8\u6562\u591a\u95ee\uff0c\u5f53\u4e0b\u7d27\u7d27\u8ddf\u5728\u7a0b\u7075\u7d20\u8eab\u540e\uff0c\u5f53\u771f\u4e0d\u79bb\u5f00\u5979\u8eab\u8fb9\u4e09\u6b65\u3002\u7a0b\u7075\u7d20\u56de\u7738\u4e00\u7b11\uff0c\u610f\u793a\u5609\u8bb8\u3002 \u3000\u3000\u90a3\u6c49\u5b50\u8ddf\u968f\u5728\u4e8c\u4eba\u4e4b\u540e\uff0c\u4e00\u8a00\u4e0d\u53d1\u3002\u7a0b\u7075\u7d20\u6298\u800c\u5411\u5317\uff0c\u56db\u66f4\u8fc7\u540e\uff0c\u5230\u4e86\u836f\u738b\u5e84\u5916\u3002 \u3000\u3000\u5979\u4ece\u7af9\u7ba9\u4e2d\u53d6\u51fa\u4e09\u5927\u4e1b\u84dd\u82b1\uff0c\u5206\u7ed9\u80e1\u6590\u548c\u90a3\u6c49\u5b50\u6bcf\u4eba\u4e00\u4e1b\uff0c\u4e8e\u662f\u5f84\u8d8a\u8840\u77ee\u6817\u800c\u8fc7\uff0c\u5230\u4e86\u94c1\u94f8\u7684\u5706\u5c4b\u5916\u9762\uff0c\u53eb\u9053\uff1a\u201c\u4e8c\u5e08\u54e5\uff0c\u4e09\u5e08\u59ca\uff0c\u5f00\u4e0d\u5f00\u95e8\uff1f\u201d\u8fde\u95ee\u4e09\u58f0\uff0c\u5706\u5c4b\u4e2d\u5bc2\u65e0\u58f0\u606f\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u5411\u90a3\u6c49\u5b50\u70b9\u70b9\u5934\u3002\u90a3\u6c49\u5b50\u653e\u4e0b\u62c5\u5b50\uff0c\u62c5\u5b50\u7684\u4e00\u7aef\u662f\u4e2a\u98ce\u7bb1\u3002\u4ed6\u62c9\u52a8\u98ce\u7bb1\uff0c\u70e7\u7ea2\u70ad\u706b\uff0c\u7194\u8d77\u94c1\u6765\uff0c\u6562\u60c5\u662f\u4e2a\u94c1\u5320\u3002\u80e1\u6590\u770b\u5f97\u5927\u5947\u3002\u53c8\u8fc7\u7247\u523b\uff0c\u53ea\u89c1\u90a3\u6c49\u5b50\u5c06\u70e7\u7ea2\u7684\u94c1\u6c41\u6d47\u5728\u5706\u5c4b\u4e4b\u4e0a\uff0c\u6478\u7740\u5c4b\u4e0a\u7684\u7f1d\u9699\uff0c\u4e00\u6761\u6761\u7684\u6d47\u53bb\uff0c\u539f\u6765\u7adf\u662f\u5c06\u94c1\u5c4b\u4e0a\u542f\u95ed\u95e8\u7a97\u7684\u901a\u8def\u4e00\u4e00\u5c01\u4f4f\u3002\u59dc\u94c1\u5c71\u548c\u859b\u9e4a\u867d\u5728\u5c4b\u4e2d\uff0c\u60f3\u662f\u5fcc\u60ee\u7a0b\u7075\u7d20\u5389\u5bb3\uff0c\u7adf\u7136\u4e0d\u6562\u51fa\u6765\u963b\u6321\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u89c1\u94c1\u5c4b\u7684\u7f1d\u9699\u5df2\u5c01\u4e86\u5341\u4e4b\u516b\u4e5d\uff0c\u8fd9\u65f6\u5c4b\u4e2d\u4eba\u5df2\u65e0\u6cd5\u7a81\u56f4\u800c\u51fa\uff0c\u4e8e\u662f\u5411\u80e1\u6590\u62db\u62db\u624b\u3002\u4e24\u4eba\u5411\u4e1c\u8d8a\u8fc7\u8840\u77ee\u6817\uff0c\u5411\u897f\u5317\u8d70\u4e86\u6570\u5341\u4e08\uff0c\u53ea\u89c1\u904d\u5730\u90fd\u662f\u5927\u5ca9\u77f3\u3002\u7a0b\u7075\u7d20\u53e3\u4e2d\u6570\u7740\u811a\u6b65\uff0c\u5317\u884c\u51e0\u6b65\uff0c\u53c8\u5411\u897f\u51e0\u6b65\uff0c\u8f7b\u58f0\u9053\uff1a\u201c\u662f\u4e86\uff01\u201d\u70b9\u4e86\u706f\u7b3c\u4e00\u7167\uff0c\u53ea\u89c1\u4e24\u5757\u5927\u5ca9\u77f3\u4e4b\u95f4\u6709\u4e2a\u7897\u53e3\u5927\u5c0f\u7684\u6d1e\u7a74\uff0c\u6d1e\u4e0a\u53c8\u7528\u4e00\u5757\u5ca9\u77f3\u51cc\u7a7a\u6401\u7740\u3002\u7a0b\u7075\u7d20\u4f4e\u58f0\u9053\uff1a\u201c\u8fd9\u662f\u4ed6\u4eec\u7684\u901a\u6c14\u5b54\u3002\u201d\u53d6\u51fa\u90a3\u534a\u622a\u8721\u70db\u70b9\u71c3\u4e86\uff0c\u653e\u5728\u6d1e\u53e3\uff0c\u4e0e\u80e1\u6590\u7ad9\u5f97\u8fdc\u8fdc\u5730\u77a7\u7740\u3002 \u3000\u3000\u8721\u70db\u70b9\u7740\u540e\uff0c\u6563\u51fa\u6781\u6de1\u7684\u8f7b\u70df\uff0c\u968f\u7740\u5fae\u98ce\uff0c\u8885\u8885\u4ece\u6d1e\u4e2d\u94bb\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u77a7\u4e86\u8fd9\u822c\u60c5\u666f\uff0c\u80e1\u6590\u5bf9\u7a0b\u7075\u7d20\u7684\u624b\u6bb5\u66f4\u662f\u656c\u754f\uff0c\u4f46\u60f3\u5230\u94c1\u5c4b\u4e2d\u4eba\u7ed9\u6bd2\u70df\u8fd9\u4e48\u4e00\u85b0\uff0c\u54ea\u91cc\u8fd8\u6709\u751f\u8def\uff1f\u4e0d\u81ea\u7981\u5730\u8d77\u4e86\u601c\u60af\u4e4b\u5ff5\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u6de1\u6de1\u8f7b\u70df\uff0c\u672c\u5df2\u6781\u96be\u77e5\u89c9\uff0c\u4fbf\u7b97\u53ca\u65f6\u53d1\u89c1\uff0c\u5835\u4e0a\u6c14\u5b54\uff0c\u6700\u540e\u8fd8\u662f\u8981\u7a92\u606f\u800c\u6b7b\uff0c\u53ea\u5dee\u5728\u6b7b\u5f97\u8fdf\u65e9\u800c\u5df2\u3002\u96be\u9053\u6211\u773c\u770b\u7740\u5979\u5e72\u8fd9\u79cd\u7edd\u6237\u706d\u95e8\u7684\u6bd2\u8fa3\u884c\u5f84\uff0c\u7adf\u4e0d\u52a0\u963b\u6b62\u4e48\uff1f\u201d \u3000\u3000\u53ea\u89c1\u7a0b\u7075\u7d20\u53d6\u51fa\u4e00\u628a\u5c0f\u5c0f\u56e2\u6247\uff0c\u8f7b\u717d\u70db\u706b\uff0c\u8721\u70db\u4e0a\u5192\u51fa\u7684\u8f7b\u70df\u5c3d\u6570\u4ece\u5ca9\u5b54\u4e2d\u94bb\u4e86\u8fdb\u53bb\uff0c\u80e1\u6590\u518d\u4e5f\u5fcd\u8010\u4e0d\u4f4f\uff0c\u970d\u5730\u7ad9\u8d77\uff0c\u8bf4\u9053\uff1a\u201c\u7075\u59d1\u5a18\uff0c\u4f60\u90a3\u5e08\u5144\u5e08\u59ca\uff0c\u4e0e\u4f60\u5f53\u771f\u6709\u4e0d\u53ef\u89e3\u7684\u6028\u4ec7\u4e48\uff1f\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u6ca1\u6709\u5440\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u4f60\u5e08\u7236\u4f20\u4e0b\u9057\u547d\uff0c\u8981\u4f60\u6e05\u7406\u95e8\u6237\uff0c\u662f\u4e0d\u662f\uff1f\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u773c\u4e0b\u8fd8\u6ca1\u5230\u8fd9\u4e2a\u5730\u6b65\u3002\u201d \u3000\u3000\u80e1\u6590\u9053\uff1a\u201c\u90a3\u2026\u2026\u90a3\u2026\u2026\u201d\u5fc3\u4e2d\u6fc0\u52a8\uff0c\u4e0d\u77e5\u5982\u4f55\u63aa\u8f9e\uff0c\u4e00\u65f6\u8bf4\u4e0d\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u62ac\u8d77\u5934\u6765\uff0c\u6de1\u6de1\u5730\u9053\uff1a\u201c\u4ec0\u4e48\u554a\uff1f\u77a7\u4f60\u6025\u6210\u8fd9\u526f\u6837\u5b50\uff01\u201d\u80e1\u6590\u5b9a\u4e86\u5b9a\u795e\u9053\uff1a\u201c\u5018\u82e5\u4f60\u5e08\u54e5\u5e08\u59ca\u2026\u2026\u5e76\u65e0\u975e\u6740\u4e0d\u53ef\u7684\u8fc7\u6076\uff0c\u8fd8\u662f\u7ed9\u4ed6\u4eec\u7559\u4e00\u6761\u6539\u8fc7\u81ea\u65b0\u7684\u9053\u8def\u3002\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u662f\u554a\uff0c\u6211\u5e08\u7236\u4e5f\u8fd9\u4e48\u8bf4\u3002\u201d\u987f\u4e86\u4e00\u987f\uff0c\u8bf4\u9053\uff1a\u201c\u53ef\u60dc\u4f60\u6ca1\u89c1\u5230\u6211\u5e08\u7236\uff0c\u5426\u5219\u4f60\u4eec\u4e00\u8001\u4e00\u5c11\uff0c\u4e00\u5b9a\u633a\u8bf4\u5f97\u6765\u3002\u201d\u53e3\u4e2d\u8bf4\u8bdd\uff0c\u624b\u4e0a\u56e2\u6247\u4ecd\u662f\u4e0d\u4f4f\u62e8\u52a8\u3002 \u3000\u3000\u80e1\u6590\u6414\u4e86\u6414\u5934\uff0c\u6307\u7740\u8721\u70db\u9053\uff1a\u201c\u8fd9\u6bd2\u70df\u2026\u2026\u8fd9\u6bd2\u70df\u4e0d\u4f1a\u81f4\u4eba\u6b7b\u547d\u4e48\uff1f\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u554a\uff0c\u539f\u6765\u54b1\u4eec\u80e1\u5927\u54e5\u5728\u5927\u53d1\u6148\u60b2\u5566\u3002 \u3000\u3000\u6211\u662f\u8981\u6551\u4eba\u6027\u547d\uff0c\u4e0d\u662f\u5728\u4f24\u5929\u5bb3\u7406\u3002\u201d\u8bf4\u7740\u8f6c\u8fc7\u5934\u6765\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u795e\u8272\u9887\u662f\u59a9\u5a9a\u3002\u80e1\u6590\u6ee1\u8138\u901a\u7ea2\uff0c\u5fc3\u60f3\u81ea\u5df1\u53c8\u505a\u4e86\u4e00\u6b21\u50bb\u74dc\uff0c\u867d\u4e0d\u61c2\u55b7\u653e\u6bd2\u70df\u4e3a\u4f55\u53cd\u662f\u6551\u4eba\uff0c\u5fc3\u4e2d\u5374\u751a\u611f\u8212\u7545\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u4f38\u51fa\u5de6\u624b\u5c0f\u6307\uff0c\u7528\u6307\u7532\u5728\u8721\u70db\u4e0a\u523b\u4e86\u4e00\u6761\u6d45\u5370\uff0c\u9053\uff1a\u201c\u8bf7\u4f60\u7ed9\u6211\u77a7\u7740\uff0c\u522b\u8ba9\u98ce\u5439\u7184\u4e86\uff0c\u70b9\u5230\u8fd9\u6761\u7ebf\u4e0a\u5c31\u7184\u4e86\u8721\u70db\u3002\u201d\u5c06\u56e2\u6247\u53d8\u7ed9\u80e1\u6590\uff0c\u7ad9\u76f4\u8eab\u5b50\uff0c\u56db\u4e0b\u5bdf\u770b\uff0c\u503e\u542c\u58f0\u606f\u3002\u80e1\u6590\u5b66\u7740\u5979\u6837\uff0c\u5c06\u8f7b\u70df\u717d\u5165\u5ca9\u5b54\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u5728\u5341\u4f59\u4e08\u5916\u515c\u4e86\u4e2a\u5708\u5b50\uff0c\u6ca1\u89c1\u4ec0\u4e48\u5f02\u72b6\uff0c\u5750\u5728\u4e00\u5757\u5706\u5ca9\u4e4b\u4e0a\uff0c\u8bf4\u9053\uff1a\u201c\u4eca\u665a\u5f15\u72fc\u6765\u8e0f\u6211\u82b1\u5703\u7684\uff0c\u662f\u4e8c\u5e08\u54e5\u7684\u513f\u5b50\uff0c\u53eb\u505a\u5c0f\u94c1\u3002\u201d\u80e1\u6590\u201c\u554a\u201d\u4e86\u4e00\u58f0\u3002\u9053\uff1a\u201c\u4ed6\u4e5f\u5728\u8fd9\u4e0b\u9762\u4e48\uff1f\u201d \u3000\u3000\u8bf4\u7740\u5411\u5ca9\u5b54\u4e2d\u6307\u4e86\u6307\u3002\u7a0b\u7075\u7d20\u7b11\u9053\uff1a\u201c\u662f\u554a\uff01\u54b1\u4eec\u8d39\u8fd9\u4e48\u5927\u52b2\uff0c\u4fbf\u662f\u53bb\u6551\u4ed6\u3002\u5148\u85b0\u6655\u4e86\u5e08\u54e5\u5e08\u59ca\uff0c\u505a\u8d77\u4e8b\u6765\u4e0d\u4f1a\u788d\u624b\u788d\u811a\u3002\u201d \u3000\u3000\u80e1\u6590\u5fc3\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\u3002\u201d \u3000\u3000\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u4e8c\u5e08\u54e5\u548c\u4e09\u5e08\u59ca\u6709\u4e00\u5bb6\u59d3\u5b5f\u7684\u5bf9\u5934\uff0c\u5230\u4e86\u6d1e\u5ead\u6e56\u8fb9\u5df2\u6709\u534a\u5e74\uff0c\u4f7f\u5c3d\u5fc3\u673a\uff0c\u603b\u662f\u89e3\u4e0d\u4e86\u94c1\u5c4b\u5916\u7684\u8840\u77ee\u6817\u4e4b\u6bd2\uff0c\u653b\u4e0d\u8fdb\u53bb\u3002\u6b7b\u5728\u6d1e\u5ead\u6e56\u7554\u7684\u90a3\u4e24\u4e2a\u4eba\uff0c\u5341\u4e5d\u4fbf\u662f\u5b5f\u5bb6\u7684\u3002 \u3000\u3000\u6211\u79cd\u7684\u84dd\u82b1\uff0c\u5374\u662f\u8840\u77ee\u6817\u7684\u514b\u661f\uff0c\u4e8c\u5e08\u54e5\u4ed6\u4eec\u4e00\u76f4\u4e0d\u77e5\uff0c\u76f4\u5230\u4f60\u548c\u953a\u7237\u8eab\u4e0a\u5e26\u4e86\u84dd\u82b1\uff0c\u4e0d\u6015\u6bd2\u4fb5\uff0c\u4ed6\u4eec\u8fd9\u624d\u60ca\u89c9\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u662f\u4e86\uff0c\u6211\u548c\u953a\u4e8c\u54e5\u6765\u7684\u65f6\u5019\uff0c\u542c\u5230\u94c1\u5c4b\u4e2d\u6709\u4eba\u60ca\u53eb\uff0c\u5fc5\u662f\u4e3a\u6b64\u3002\u201d\u7a0b\u7075\u7d20\u70b9\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u8840\u77ee\u6817\u7684\u6bd2\u6027\uff0c\u672c\u662f\u65e0\u836f\u53ef\u89e3\uff0c\u987b\u5f97\u7ecf\u5e38\u670d\u98df\u6811\u4e0a\u6240\u7ed3\u7684\u6817\u5b50\uff0c\u624d\u4e0d\u53d7\u90a3\u6811\u6c14\u606f\u7684\u4fb5\u5bb3\u3002\u5e78\u597d\u8840\u77ee\u6817\u6bd2\u6027\u867d\u7136\u5389\u5bb3\uff0c\u5012\u4e5f\u4e0d\u6613\u4e3a\u5bb3\u4eba\u755c\uff0c\u56e0\u4e3a\u53ea\u8981\u6709\u8fd9\u4e48\u4e00\u68f5\u6811\u957f\u7740\uff0c\u5468\u56f4\u6570\u5341\u6b65\u5185\u5bf8\u8349\u4e0d\u751f\uff0c\u866b\u8681\u7edd\u8ff9\uff0c\u4e00\u770b\u4fbf\u77e5\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u8fd9\u94c1\u5c4b\u5468\u56f4\u8fde\u8349\u6839\u4e5f\u6ca1\u534a\u6761\u3002\u6211\u628a\u4e24\u5339\u9a6c\u7684\u53e3\u90fd\u624e\u4f4f\u4e86\uff0c\u8fd8\u662f\u907f\u4e0d\u4e86\u6bd2\u8d28\uff0c\u82e5\u4e0d\u662f\u4f60\u76f8\u8d60\u84dd\u82b1\u2026\u2026\u201d\u8bf4\u5230\u8fd9\u91cc\uff0c\u60f3\u8d77\u4eca\u665a\u7684\u83bd\u649e\uff0c\u4e0d\u81ea\u7981\u6697\u6697\u60ca\u5fc3\uff0c\u5fc3\u9053\uff1a\u201c\u65e0\u602a\u6c5f\u6e56\u4e0a\u4e00\u63d0\u5230\u2018\u6bd2\u624b\u836f\u738b\u2019\u4fbf\u8c08\u864e\u8272\u53d8\uff0c\u953a\u4e8c\u54e5\u6781\u529b\u6212\u5907\uff0c\u786e\u975e\u65e0\u56e0\u3002\u201d \u3000\u3000\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u6211\u8fd9\u84dd\u82b1\u662f\u65b0\u8bd5\u51fa\u6765\u7684\u54c1\u79cd\uff0c\u603b\u7b97\u627f\u8499\u4e0d\u5f03\uff0c\u6ca1\u5728\u534a\u8def\u4e0a\u4e22\u6389\u3002\u201d\u80e1\u6590\u5fae\u7b11\u9053\uff1a\u201c\u8fd9\u82b1\u989c\u8272\u5a07\u8273\uff0c\u5f88\u662f\u597d\u770b\u3002\u201d \u3000\u3000\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u5e78\u4e8f\u8fd9\u84dd\u82b1\u597d\u770b\uff0c\u5018\u82e5\u4e0d\u7f8e\uff0c\u4f60\u4fbf\u628a\u5b83\u629b\u4e86\uff0c\u662f\u4e0d\u662f\uff1f\u201d\u80e1\u6590\u4e00\u65f6\u4e0d\u77e5\u6240\u5bf9\uff0c\u53ea\u8bf4\uff1a\u201c\u5514\u2026\u2026\u5514\u2026\u2026\u201d\u5fc3\u4e2d\u5728\u60f3\uff1a\u201c\u5018\u82e5\u8fd9\u84dd\u82b1\u679c\u771f\u5341\u5206\u4e11\u964b\uff0c\u6211\u4f1a\u4e0d\u4f1a\u4ecd\u7136\u85cf\u5728\u8eab\u8fb9\uff1f\u662f\u5426\u5e78\u4e8f\u82b1\u7f8e\uff0c\u8fd9\u624d\u6551\u4e86\u6211\u548c\u953a\u4e8c\u54e5\u7684\u6027\u547d\uff1f\u201d \u3000\u3000\u6b63\u5728\u6b64\u65f6\uff0c\u4e00\u9635\u98ce\u5439\u4e86\u8fc7\u6765\uff0c\u80e1\u6590\u6b63\u81ea\u5bfb\u601d\uff0c\u6ca1\u4e3e\u6247\u6321\u4f4f\u8721\u70db\uff0c\u70db\u706b\u4e00\u95ea\uff0c\u767b\u65f6\u7184\u4e86\u3002\u80e1\u6590\u8f7b\u8f7b\u53eb\u58f0\uff1a\u201c\u554a\u54df\uff01\u201d\u5fd9\u53d6\u51fa\u706b\u6298\uff0c\u5f85\u8981\u518d\u70b9\u8721\u70db\uff0c\u53ea\u542c\u7a0b\u7075\u7d20\u5728\u9ed1\u6697\u4e2d\u9053\uff1a\u201c\u7b97\u5566\uff0c\u4e5f\u5dee\u4e0d\u591a\u591f\u4e86\u3002\u201d\u80e1\u6590\u542c\u5979\u8bed\u6c14\u4e2d\u9887\u6709\u4e0d\u60a6\u4e4b\u610f\uff0c\u5fc3\u60f3\u5979\u53eb\u6211\u505a\u4ec0\u4e48\u4e8b\uff0c\u6211\u603b\u662f\u6ca1\u505a\u5f97\u59a5\u8d34\uff0c\u4f3c\u4e4e\u4e00\u5207\u5168\u90fd\u6f2b\u4e0d\u7ecf\u5fc3\uff0c\u6b49\u7136\u9053\uff1a\u201c\u771f\u5bf9\u4e0d\u8d77\uff0c\u4eca\u665a\u4e0d\u77e5\u600e\u7684\uff0c\u6211\u603b\u662f\u5931\u9b42\u843d\u9b44\u7684\u3002\u201d\u7a0b\u7075\u7d20\u9ed8\u7136\u4e0d\u8bed\u3002 \u3000\u3000\u80e1\u6590\u9053\uff1a\u201c\u6211\u6b63\u5728\u60f3\u4f60\u8fd9\u53e5\u8bdd\uff0c\u6ca1\u6599\u5230\u521a\u597d\u6709\u4e00\u9635\u98ce\u6765\u3002 \u3000\u3000\u7075\u59d1\u5a18\uff0c\u6211\u60f3\u8fc7\u4e86\uff0c\u4f60\u9001\u6211\u8fd9\u84dd\u82b1\u4e4b\u65f6\uff0c\u6211\u5168\u6ca1\u77e5\u8fd9\u662f\u6551\u547d\u4e4b\u7269\uff0c\u4f46\u65e2\u662f\u4eba\u5bb6\u4e00\u756a\u597d\u610f\u7ed9\u7684\u4e1c\u897f\uff0c\u6211\u81ea\u4f1a\u597d\u597d\u6536\u7740\u3002\u201d\u7a0b\u7075\u7d20\u542c\u4ed6\u8fd9\u51e0\u53e5\u8bdd\u8bf4\u5f97\u6073\u5207\uff0c\u201c\u55ef\u201d\u4e86\u4e00\u58f0\u3002 \u3000\u3000\u5728\u9ed1\u6697\u4e4b\u4e2d\uff0c\u4e24\u4eba\u76f8\u5bf9\u5750\u7740\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u80e1\u6590\u9053\uff1a\u201c\u6211\u4ece\u5c0f\u6ca1\u7239\u6ca1\u5a18\uff0c\u96be\u5f97\u6709\u8c01\u7ed9\u6211\u4ec0\u4e48\u4e1c\u897f\u3002\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u662f\u5566\uff0c\u6211\u4e5f\u4ece\u5c0f\u6ca1\u7239\u6ca1\u5a18\uff0c\u8fd8\u4e0d\u662f\u6d3b\u5f97\u8fd9\u4e48\u5927\u4e86\uff1f\u201d\u8bf4\u7740\u70b9\u71c3\u4e86\u706f\u7b3c\uff0c\u8bf4\u9053\uff1a\u201c\u8d70\u5427\uff01\u201d \u3000\u3000\u80e1\u6590\u5077\u773c\u77a7\u5979\u8138\u8272\uff0c\u4f3c\u4e4e\u5e76\u6ca1\u751f\u6c14\uff0c\u5f53\u4e0b\u4e0d\u6562\u591a\u95ee\uff0c\u8ddf\u968f\u5728\u540e\u3002 \u3000\u3000\u4e24\u4eba\u56de\u5230\u94c1\u5c4b\u4e4b\u524d\uff0c\u89c1\u90a3\u94c1\u5320\u5750\u5728\u5730\u4e0b\u5438\u70df\u3002\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u738b\u5927\u53d4\uff0c\u52b3\u60a8\u9a7e\u51ff\u5f00\u8fd9\u6761\u7f1d\uff01\u201d\u6240\u6307\u4e4b\u5904\uff0c\u6b63\u662f\u9002\u624d\u5979\u8981\u94c1\u5320\u710a\u4e0a\u4e86\u7684\u3002\u90a3\u94c1\u5320\u4e5f\u6ca1\u95ee\u4ec0\u4e48\u539f\u7531\uff0c\u62ff\u51fa\u94c1\u9524\u94c1\u51ff\uff0c\u53ee\u53ee\u5f53\u5f53\u5730\u51ff\u4e86\u8d77\u6765\uff0c\u4e0d\u5230\u4e00\u987f\u996d\u65f6\u5206\uff0c\u5df2\u5c06\u710a\u4e0a\u7684\u7f1d\u51ff\u5f00\u3002\u7a0b\u7075\u7d20\u8bf4\u9053\uff1a\u201c\u5f00\u95e8\u5427\uff01\u201d \u3000\u3000\u90a3\u94c1\u5320\u7528\u94c1\u9524\u4e1c\u6253\u6253\uff0c\u897f\u6572\u6572\uff0c\u5012\u8f6c\u94c1\u9524\uff0c\u7528\u9524\u67c4\u4e00\u64ac\uff0c\u5f53\u7684\u4e00\u58f0\uff0c\u4e00\u5757\u5927\u94c1\u677f\u843d\u4e86\u4e0b\u6765\uff0c\u9732\u51fa\u4e00\u4e2a\u516d\u5c3a\u9ad8\u3001\u4e09\u5c3a\u5bbd\u7684\u95e8\u6765\u3002\u8fd9\u94c1\u5320\u5bf9\u94c1\u5c4b\u7684\u6784\u9020\u4f3c\u4e4e\u4e86\u5982\u6307\u638c\uff0c\u4f38\u624b\u5728\u95e8\u8fb9\u4e00\u62c9\uff0c\u4fbf\u6709\u4e00\u5ea7\u5c0f\u5c0f\u7684\u94c1\u68af\u4f38\u51fa\uff0c\u4ece\u95e8\u4e0a\u901a\u5411\u5185\u8fdb\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u54b1\u4eec\u628a\u84dd\u82b1\u7559\u5728\u5916\u9762\u3002\u201d\u4e09\u4eba\u5c06\u8eab\u4e0a\u63d2\u7684\u4e00\u675f\u84dd\u82b1\u90fd\u629b\u5728\u5730\u4e0b\u3002\u7a0b\u7075\u7d20\u6b63\u8981\u8de8\u6b65\u4ece\u5c0f\u94c1\u68af\u8d70\u8fdb\u5c4b\u53bb\uff0c\u8f7b\u8f7b\u55c5\u4e86\u4e00\u4e0b\uff0c\u9053\uff1a\u201c\u80e1\u5927\u54e5\uff0c\u600e\u4e48\u4f60\u8eab\u4e0a\u8fd8\u6709\u84dd\u82b1\uff1f\u522b\u5e26\u8fdb\u53bb\u3002\u201d \u3000\u3000\u80e1\u6590\u5e94\u9053\uff1a\u201c\u5662\uff01\u201d\u4ece\u6000\u4e2d\u6478\u51fa\u4e00\u4e2a\u5e03\u5305\uff0c\u6253\u4e86\u5f00\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u9f3b\u5b50\u771f\u7075\uff0c\u6211\u5305\u5728\u5305\u91cc\u4f60\u4e5f\u77e5\u9053\u3002\u201d \u3000\u3000\u90a3\u5e03\u5305\u4e2d\u5305\u7740\u4ed6\u7684\u5bb6\u4f20\u62f3\u7ecf\u5200\u8c31\uff0c\u8fd8\u6709\u4e00\u4e9b\u6742\u7269\uff0c\u65e5\u95f4\u7a0b\u7075\u7d20\u7ed9\u4ed6\u7684\u90a3\u68f5\u84dd\u82b1\u4e5f\u5728\u5176\u5185\uff0c\u53ea\u662f\u5305\u4e86\u5927\u534a\u65e5\uff0c\u65e9\u5df2\u67af\u840e\u4e86\u3002\u80e1\u6590\u6361\u4e86\u51fa\u6765\uff0c\u653e\u5728\u94c1\u95e8\u677f\u4e0a\u3002\u7a0b\u7075\u7d20\u89c1\u4ed6\u73cd\u800c\u91cd\u4e4b\u7684\u6536\u85cf\u7740\u8fd9\u68f5\u84dd\u82b1\uff0c\u77e5\u4ed6\u521a\u624d\u679c\u7136\u6ca1\u8bf4\u5047\u8bdd\uff0c\u5f88\u662f\u559c\u6b22\uff0c\u5411\u4ed6\u5ae3\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u6ca1\u9a97\u4eba\uff01\u201d\u80e1\u6590\u4e00\u695e\uff0c\u5fc3\u9053\uff1a\u201c\u6211\u4f55\u5fc5\u9a97\u4f60\uff1f\u201d\u7a0b\u7075\u7d20\u6307\u7740\u94c1\u5c4b\u7684\u95e8\u9053\uff1a\u201c\u91cc\u9762\u7684\u4eba\u5e73\u65f6\u670d\u98df\u8840\u6817\u60ef\u4e86\uff0c\u8fd9\u84dd\u82b1\u6b63\u662f\u514b\u661f\uff0c\u4ed6\u4eec\u62b5\u53d7\u4e0d\u4f4f\u3002\u201d\u63d0\u8d77\u706f\u7b3c\uff0c\u8e0f\u6b65\u8fdb\u5185\u3002\u80e1\u6590\u548c\u738b\u94c1\u5320\u8ddf\u7740\u8fdb\u53bb\u3002 \u3000\u3000\u8d70\u5b8c\u94c1\u68af\uff0c\u662f\u4e00\u6761\u72ed\u7a84\u7684\u752c\u9053\uff0c\u8f6c\u4e86\u4e24\u4e2a\u5f2f\uff0c\u6765\u5230\u4e00\u4e2a\u5c0f\u5c0f\u5385\u5802\u3002\u53ea\u89c1\u5899\u4e0a\u6302\u7740\u4e66\u753b\u5bf9\u8054\uff0c\u6e58\u5983\u7af9\u7684\u684c\u6905\uff0c\u9648\u8bbe\u751a\u662f\u96c5\u81f4\u3002\u80e1\u6590\u6697\u6697\u7eb3\u7f55\uff1a\u201c\u90a3\u59dc\u94c1\u5c71\u5f62\u8c8c\u7c97\u9c81\uff0c\u5c45\u5904\u5374\u662f\u8fd9\u7b49\u7684\u6240\u5728\uff0c\u5012\u50cf\u662f\u5230\u4e86\u79c0\u624d\u4e66\u751f\u7684\u5bb6\u91cc\u3002\u201d\u7a0b\u7075\u7d20\u6beb\u4e0d\u505c\u7559\uff0c\u4e00\u76f4\u8d70\u5411\u540e\u8fdb\u3002\u80e1\u6590\u8ddf\u7740\u5979\u8d70\u8fdb\u4e00\u95f4\u53a8\u623f\u6a21\u6837\u7684\u5c4b\u5b50\uff0c\u773c\u524d\u6240\u89c1\uff0c\u4e0d\u7531\u5f97\u5927\u5403\u4e00\u60ca\u3002 \u3000\u3000\u53ea\u89c1\u59dc\u94c1\u5c71\u548c\u859b\u9e4a\u5012\u5728\u5730\u4e0b\uff0c\u4e0d\u77e5\u662f\u6b7b\u662f\u6d3b\u3002\u5f53\u4e03\u5fc3\u6d77\u68e0\u6240\u5236\u8721\u70db\u7684\u8f7b\u70df\u4ece\u5ca9\u5b54\u4e2d\u900f\u5165\u4e4b\u65f6\uff0c\u80e1\u6590\u5df2\u6599\u5230\u5b9a\u7136\u6709\u6b64\u60c5\u666f\uff0c\u5012\u4e5f\u4e0d\u4ee5\u4e3a\u5f02\uff0c\u5947\u602a\u7684\u662f\u4e00\u53ea\u5927\u94c1\u956c\u76db\u6ee1\u4e86\u70ed\u6c34\uff0c\u956c\u4e2d\u7adf\u5750\u7740\u4e00\u4e2a\u9752\u5e74\u7537\u5b50\u3002\u8fd9\u4eba\u8d64\u88f8\u7740\u4e0a\u8eab\uff0c\u956c\u4e2d\u6c34\u6c14\u4e0d\u65ad\u55b7\u5192\uff0c\u770b\u6765\u8fd9\u6c34\u867d\u975e\u6cb8\u817e\uff0c\u5374\u5df2\u751a\u70ed\uff0c\u8bf4\u4e0d\u5b9a\u8fd9\u4eba\u5df2\u6d3b\u6d3b\u716e\u6b7b\u3002 \u3000\u3000\u80e1\u6590\u4e00\u4e2a\u7bad\u6b65\u62a2\u4e0a\u524d\u53bb\uff0c\u5f85\u8981\u5c06\u90a3\u4eba\u4ece\u956c\u4e2d\u62c9\u8d77\uff0c\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u522b\u52a8\uff01\u4f60\u77a7\u4ed6\u2026\u2026\u77a7\u4ed6\u8eab\u4e0a\u8fd8\u6709\u6ca1\u6709\u8863\u670d\u3002\u201d\u80e1\u6590\u63a2\u9996\u5230\u956c\u4e2d\u4e00\u770b\uff0c\u9053\uff1a\u201c\u4ed6\u7a7f\u7740\u88e4\u5b50\u3002\u201d\u7a0b\u7075\u7d20\u8138\u4e0a\u5fae\u5fae\u4e00\u7ea2\uff0c\u70b9\u4e86\u70b9\u5934\uff0c\u8d70\u8fd1\u956c\u8fb9\uff0c\u63a2\u4e86\u63a2\u90a3\u4eba\u9f3b\u606f\uff0c\u9053\uff1a\u201c\u4f60\u5230\u7076\u4e0b\u52a0\u4e9b\u67f4\u706b\uff01\u201d \u3000\u3000\u80e1\u6590\u5413\u4e86\u4e00\u8df3\uff0c\u5411\u90a3\u4eba\u518d\u671b\u4e00\u773c\uff0c\u8ba4\u51fa\u4ed6\u4fbf\u662f\u5f15\u4e86\u72fc\u7fa4\u6765\u8df5\u8e0f\u82b1\u5703\u4e4b\u4eba\uff0c\u53ea\u89c1\u4ed6\u53cc\u76ee\u7d27\u95ed\uff0c\u5f20\u5927\u4e86\u53e3\uff0c\u58ee\u5065\u7684\u80f8\u812f\u5fae\u5fae\u8d77\u4f0f\uff0c\u679c\u7136\u672a\u6b7b\uff0c\u4f46\u663e\u5df2\u6655\u53bb\uff0c\u5931\u4e86\u77e5\u89c9\uff0c\u95ee\u9053\uff1a\u201c\u4ed6\u662f\u5c0f\u94c1\uff1f\u4ed6\u4eec\u7684\u513f\u5b50\uff1f\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u5e08\u54e5\u5e08\u59ca\u60f3\u71ac\u51fa\u4ed6\u8eab\u4e0a\u7684\u6bd2\u8d28\uff0c\u4f46\u6ca1\u6709\u4e03\u5fc3\u6d77\u68e0\u7684\u82b1\u7c89\uff0c\u603b\u662f\u6cbb\u4e0d\u597d\u3002\u201d\u80e1\u6590\u8fd9\u624d\u653e\u5fc3\uff0c\u89c1\u7076\u4e2d\u706b\u52bf\u5fae\u5f31\uff0c\u4e8e\u662f\u52a0\u4e86\u4e00\u6839\u786c\u67f4\uff0c\u751f\u6015\u6c34\u716e\u5f97\u592a\u70ed\uff0c\u5c0f\u94c1\u62b5\u53d7\u4e0d\u4f4f\uff0c\u4e0d\u6562\u591a\u52a0\u3002\u7a0b\u7075\u7d20\u7b11\u9053\uff1a\u201c\u591a\u52a0\u51e0\u6839\uff0c\u716e\u4e0d\u719f\uff0c\u7168\u4e0d\u70c2\u7684\u3002\u201d\u80e1\u6590\u4f9d\u8a00\uff0c\u53c8\u62ff\u4e24\u6761\u786c\u67f4\u585e\u5165\u7076\u4e2d\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u4f38\u624b\u5165\u956c\uff0c\u63a2\u4e86\u63a2\u6c34\u7684\u51b7\u70ed\uff0c\u4ece\u6000\u4e2d\u6478\u51fa\u4e00\u4e2a\u5c0f\u5c0f\u836f\u74f6\uff0c\u5012\u51fa\u4e9b\u9ec4\u8272\u7c89\u672b\uff0c\u585e\u5728\u59dc\u94c1\u5c71\u548c\u859b\u9e4a\u9f3b\u4e2d\u3002 \u3000\u3000\u7a0d\u5f85\u7247\u523b\uff0c\u4e24\u4eba\u5148\u540e\u6253\u4e86\u51e0\u4e2a\u55b7\u568f\uff0c\u7741\u773c\u9192\u8f6c\uff0c\u53ea\u89c1\u7a0b\u7075\u7d20\u624b\u4e2d\u62ff\u7740\u4e00\u53ea\u6c34\u74e2\uff0c\u4ece\u956c\u4e2d\u6339\u4e86\u4e00\u74e2\u70ed\u6c34\u5012\u53bb\uff0c\u518d\u4ece\u6c34\u7f38\u4e2d\u6339\u4e86\u4e00\u74e2\u51b7\u6c34\u52a0\u5728\u956c\u4e2d\u3002\u592b\u5987\u4fe9\u5bf9\u671b\u4e86\u4e00\u773c\uff0c\u521d\u9192\u65f6\u90a3\u53c8\u60ca\u53c8\u6012\u7684\u795e\u8272\u7acb\u65f6\u8f6c\u4e3a\u559c\u8272\uff0c\u77e5\u9053\u5979\u65e2\u80af\u51fa\u624b\u76f8\u6551\uff0c\u72ec\u751f\u7231\u5b50\u4fbf\u662f\u6b7b\u91cc\u9003\u751f\u3002\u4e24\u4eba\u7ad9\u8d77\u8eab\u6765\uff0c\u9ed8\u7136\u4e0d\u8bed\uff0c\u5fc3\u4e2d\u5404\u662f\u4e00\u80a1\u8bf4\u4e0d\u51fa\u7684\u6ecb\u5473\uff1a\u7231\u5b50\u660e\u660e\u662f\u4e2d\u4e86\u5979\u7684\u6bd2\u624b\uff0c\u6b64\u523b\u5979\u5374\u53c8\u6765\u76f8\u6551\uff0c\u5411\u5979\u9053\u8c22\u662f\u72af\u4e0d\u7740\uff0c\u4f46\u662f\u5979\u5982\u4e0d\u6551\uff0c\u513f\u5b50\u53c8\u6d3b\u4e0d\u6210\uff1b\u518d\u8bf4\uff0c\u5979\u4e0d\u8fc7\u662f\u5c0f\u5e08\u59b9\uff0c\u81ea\u5df1\u513f\u5b50\u7684\u5e74\u7eaa\u8fd8\u5927\u8fc7\u5979\uff0c\u54ea\u77e5\u5e08\u7236\u504f\u5fc3\uff0c\u4f20\u7ed9\u5979\u7684\u672c\u9886\u8fdc\u80dc\u8fc7\u81ea\u5df1\u592b\u5987\uff0c\u63a5\u8fde\u53d7\u5979\u514b\u5236\uff0c\u7adf\u662f\u7f1a\u624b\u7f1a\u811a\uff0c\u6ca1\u534a\u70b9\u8fd8\u624b\u7684\u4f59\u5730\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u4e00\u89c1\u6c34\u6c7d\u7565\u76db\uff0c\u4fbf\u6339\u53bb\u4e00\u74e2\u70ed\u6c34\uff0c\u52a0\u6dfb\u4e00\u74e2\u51b7\u6c34\uff0c\u4f7f\u59dc\u5c0f\u94c1\u8eab\u4e0a\u7684\u6bd2\u8d28\u9010\u6b65\u71ac\u51fa\u3002\u71ac\u4e86\u4e00\u4f1a\uff0c\u5979\u5ffd\u5411\u738b\u94c1\u5320\u9053\uff1a\u201c\u518d\u4e0d\u52a8\u624b\uff0c\u4fbf\u62a5\u4e0d\u4e86\u4ec7\u5566\uff01\u201d\u738b\u94c1\u5320\u9053\uff1a\u201c\u662f\uff01\u201d\u5728\u7076\u8fb9\u62fe\u8d77\u4e00\u6bb5\u786c\u67f4\uff0c\u5939\u5934\u5939\u8111\u4fbf\u5411\u59dc\u94c1\u5c71\u6253\u53bb\u3002 \u3000\u3000\u59dc\u94c1\u5c71\u5927\u6012\uff0c\u559d\u9053\uff1a\u201c\u4f60\u5e72\u4ec0\u4e48\uff1f\u201d\u4e00\u628a\u6293\u4f4f\u786c\u67f4\uff0c\u5f85\u8981\u8fd8\u624b\u3002\u859b\u9e4a\u9053\uff1a\u201c\u94c1\u5c71\uff0c\u54b1\u4eec\u4eca\u65e5\u6709\u6c42\u4e8e\u5e08\u59b9\uff0c\u8fd9\u51e0\u4e0b\u4e5f\u6328\u4e0d\u8d77\u4e48\uff1f\u201d\u59dc\u94c1\u5c71\u4e00\u5446\uff0c\u6012\u9053\uff1a\u201c\u597d\uff01\u201d\u677e\u624b\u653e\u5f00\u4e86\u786c\u67f4\u3002\u738b\u94c1\u5320\u4e00\u67f4\u6253\u4e86\u4e0b\u53bb\uff0c\u59dc\u94c1\u5c71\u65e2\u4e0d\u95ea\u907f\uff0c\u4e5f\u4e0d\u62db\u67b6\uff0c\u633a\u7740\u5934\u8ba9\u4ed6\u731b\u51fb\u4e00\u8bb0\u3002\u738b\u94c1\u5320\u9a82\u9053\uff1a\u201c\u4f60\u62a2\u8001\u5b50\u7530\u5730\uff0c\u903c\u8001\u5b50\u7ed9\u4f60\u94f8\u9020\u94c1\u5c4b\uff0c\u8fd8\u6253\u5f97\u8001\u5b50\u65ad\u4e86\u4e09\u6839\u808b\u9aa8\uff0c\u5728\u5e8a\u4e0a\u8eba\u4e86\u534a\u5e74\uff0c\u72d7\u5a18\u517b\u7684\uff0c\u60f3\u4e0d\u5230\u4e5f\u6709\u4eca\u65e5\u3002\u201d\u9a82\u4e00\u53e5\uff0c\u4fbf\u7528\u786c\u67f4\u731b\u51fb\u4e00\u4e0b\uff0c\u4ed6\u6253\u4e86\u51e0\u5341\u5e74\u94c1\uff0c\u867d\u7136\u4e0d\u4f1a\u6b66\u529f\uff0c\u4f46\u53f3\u81c2\u7684\u6253\u51fb\u4e4b\u529b\u4f55\u7b49\u521a\u731b\uff0c\u6253\u5f97\u51e0\u4e0b\uff0c\u786c\u67f4\u4fbf\u65ad\u4e86\u3002 \u3000\u3000\u59dc\u94c1\u5c71\u59cb\u7ec8\u4e0d\u8fd8\u624b\uff0c\u54ac\u7740\u7259\u4efb\u4ed6\u6bb4\u51fb\u3002 \u3000\u3000\u80e1\u6590\u4ece\u90a3\u738b\u94c1\u5320\u7684\u9a82\u58f0\u542c\u6765\uff0c\u77e5\u4ed6\u66fe\u53d7\u59dc\u94c1\u5c71\u592b\u5987\u6781\u5927\u7684\u6b3a\u538b\uff0c\u4eca\u65e5\u7a0b\u7075\u7d20\u4f38\u5f20\u516c\u9053\uff0c\u8ba9\u4ed6\u51fa\u4e86\u8fd9\u53e3\u6076\u6c14\uff0c\u5012\u4e5f\u662f\u5927\u5feb\u4eba\u5fc3\u4e4b\u4e3e\u3002\u738b\u94c1\u5320\u6253\u65ad\u4e86\u4e09\u6839\u786c\u67f4\uff0c\u89c1\u59dc\u94c1\u5c71\u6ee1\u8138\u662f\u8840\uff0c\u5374\u54ac\u7740\u7259\u9f7f\u4e00\u58f0\u4e0d\u54fc\uff0c\u4ed6\u662f\u4e2a\u826f\u5584\u4e4b\u4eba\uff0c\u89c9\u5f97\u6c14\u4e5f\u51fa\u4e86\uff0c\u867d\u7136\u5f53\u5e74\u81ea\u5df1\u53d7\u4ed6\u7236\u5b50\u6bb4\u6253\u8fdc\u60e8\u4e8e\u6b64\uff0c\u4f46\u4e5f\u4e0d\u4e3a\u5df1\u751a\uff0c\u5c06\u786c\u67f4\u5f80\u5730\u4e0b\u4e00\u629b\uff0c\u5411\u7a0b\u7075\u7d20\u62b1\u62f3\u9053\uff1a\u201c\u7a0b\u59d1\u5a18\uff0c\u4eca\u65e5\u4f60\u66ff\u6211\u51fa\u4e86\u8fd9\u53e3\u6c14\uff0c\u5c0f\u4eba\u96be\u4ee5\u62a5\u7b54\u3002\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u738b\u5927\u53d4\u4e0d\u5fc5\u591a\u793c\u3002\u201d\u8f6c\u5934\u5411\u859b\u9e4a\u9053\uff1a\u201c\u4e09\u5e08\u59ca\uff0c\u4f60\u4eec\u628a\u7530\u5730\u8fd8\u4e86\u738b\u5927\u53d4\uff0c\u51b2\u7740\u5c0f\u59b9\u7684\u9762\u5b50\uff0c\u4ee5\u540e\u4e5f\u522b\u627e\u4ed6\u62a5\u4ec7\uff0c\u597d\u4e0d\u597d\uff1f\u201d\u859b\u9e4a\u4f4e\u6c89\u7740\u55d3\u5b50\u9053\uff1a\u201c\u6211\u4eec\u8fd9\u8f88\u5b50\u6c38\u4e0d\u8e0f\u8fdb\u6e56\u5357\u7701\u5883\u4e86\u3002\u518d\u8bf4\uff0c\u8fd9\u79cd\u4eba\u4e5f\u4e0d\u4f1a\u53eb\u6211\u4eec\u5ff5\u5ff5\u4e0d\u5fd8\u3002\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u597d\uff0c\u5c31\u662f\u8fd9\u6837\u3002\u738b\u5927\u53d4\uff0c\u4f60\u5148\u56de\u53bb\u5427\uff0c\u8fd9\u91cc\u6ca1\u4f60\u7684\u4e8b\u4e86\u3002\u201d \u3000\u3000\u738b\u94c1\u5320\u6ee1\u8138\u559c\u8272\uff0c\u62fe\u8d77\u6298\u5728\u5730\u4e0b\u7684\u534a\u622a\u786c\u67f4\uff0c\u5fc3\u9053\uff1a\u201c\u4f60\u8fd9\u6076\u9738\u5f53\u5e74\u6253\u5f97\u8001\u5b50\u591a\u60e8\uff01\u8fd9\u534a\u622a\u5e26\u8840\u786c\u67f4\uff0c\u8001\u5b50\u662f\u8981\u5f53\u5b9d\u8d1d\u4e00\u822c\u5730\u85cf\u8d77\u6765\u4e86\u3002\u201d\u53c8\u5411\u7a0b\u7075\u7d20\u548c\u80e1\u6590\u884c\u4e86\u4e00\u793c\uff0c\u8f6c\u8eab\u51fa\u53bb\u3002 \u3000\u3000\u80e1\u6590\u89c1\u5230\u8fd9\u5f20\u6734\u5b9e\u6df3\u539a\u7684\u8138\u4e0a\u5145\u6ee1\u7740\u5c0f\u5b69\u5b50\u4e00\u822c\u7684\u559c\u8272\uff0c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u5ffd\u5730\u8bb0\u8d77\u4f5b\u5c71\u9547\u5317\u5e1d\u5e99\u4e2d\u7684\u60e8\u5267\u3002\u90a3\u65e5\u6076\u9738\u51e4\u5929\u5357\u88ab\u81ea\u5df1\u5236\u4f4f\uff0c\u5bf9\u953a\u963f\u56db\u7684\u8d23\u9a82\u65e0\u8f9e\u53ef\u5bf9\uff0c\u4f46\u81ea\u5df1\u53ea\u79bb\u5f00\u7247\u523b\uff0c\u953a\u963f\u56db\u5168\u5bb6\u767b\u65f6\u5c38\u6a2a\u6bbf\u5802\u3002\u8fd9\u59dc\u94c1\u5c71\u592b\u5987\u7684\u5978\u8bc8\u51f6\u6b8b\u4e0d\u5728\u51e4\u5929\u5357\u4e4b\u4e0b\uff0c\u672a\u5fc5\u4f1a\u4fe1\u5b88\u8bfa\u8a00\uff0c\u53ea\u6015\u7a0b\u7075\u7d20\u4e00\u53bb\uff0c\u7acb\u65f6\u4f1a\u5bf9\u738b\u94c1\u5320\u75db\u4e0b\u6bd2\u624b\u3002\u4ed6\u60f3\u5230\u6b64\u5904\uff0c\u8ffd\u5230\u95e8\u53e3\uff0c\u53eb\u9053\uff1a\u201c\u738b\u5927\u53d4\uff0c\u6211\u6709\u53e5\u8bdd\u8ddf\u4f60\u8bf4\u3002\u201d\u738b\u94c1\u5320\u7ad9\u5b9a\u811a\u6b65\uff0c\u56de\u5934\u77a7\u7740\u4ed6\u3002\u80e1\u6590\u9053\uff1a\u201c\u738b\u5927\u53d4\uff0c\u8fd9\u59d3\u59dc\u7684\u592b\u59bb\u4e0d\u662f\u597d\u4eba\u3002\u4f60\u8d76\u7d27\u5356\u4e86\u7530\u5730\uff0c\u8d70\u5f97\u8fdc\u8fdc\u7684\uff0c\u522b\u5728\u8fd9\u91cc\u591a\u803d\u3002\u4ed6\u4eec\u7684\u624b\u6bb5\u6bd2\u8fa3\u5f97\u7d27\u3002\u201d \u3000\u3000\u738b\u94c1\u5320\u4e00\u6014\uff0c\u5f88\u820d\u4e0d\u5f97\u8fd9\u4f4f\u4e86\u51e0\u5341\u5e74\u7684\u5bb6\u4e61\uff0c\u9053\uff1a\u201c\u4ed6\u4eec\u7b54\u5e94\u4e86\u6c38\u4e0d\u8e0f\u8fdb\u6e56\u5357\u7701\u5883\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u8fd9\u79cd\u4eba\u7684\u8bf4\u8bdd\uff0c\u4e5f\u4fe1\u5f97\u8fc7\u4e48\uff1f\u201d\u738b\u94c1\u5320\u604d\u7136\u5927\u609f\uff0c\u8fde\u8bf4\uff1a\u201c\u5bf9\uff0c\u5bf9\uff01\u6211\u660e\u513f\u4fbf\u8d70\uff01\u201d \u3000\u3000\u4ed6\u8de8\u51fa\u94c1\u95e8\uff0c\u8f6c\u5934\u53c8\u95ee\uff1a\u201c\u4f60\u8d35\u59d3\uff1f\u201d\u80e1\u6590\u9053\uff1a\u201c\u6211\u59d3\u80e1\u3002\u201d\u738b\u94c1\u5320\u9053\uff1a\u201c\u597d\uff0c\u80e1\u7237\uff0c\u54b1\u4eec\u518d\u89c1\u4e86\uff0c\u4f60\u8fd9\u4e00\u8f88\u5b50\u53ef\u5f97\u597d\u597d\u5f85\u7a0b\u59d1\u5a18\u554a\u3002\u201d \u3000\u3000\u8fd9\u6b21\u8f6e\u5230\u80e1\u6590\u4e00\u6014\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u201d\u738b\u94c1\u5320\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u80e1\u7237\uff0c\u738b\u94c1\u5320\u53c8\u4e0d\u662f\u50bb\u5b50\uff0c\u96be\u9053\u6211\u8fd8\u77a7\u4e0d\u51fa\u4e48\uff1f\u7a0b\u59d1\u5a18\u4eba\u65e2\u806a\u660e\uff0c\u5fc3\u773c\u513f\u53c8\u597d\uff0c\u8fd9\u4efd\u672c\u4e8b\u66f4\u52a0\u4e0d\u7528\u63d0\u5566\u3002\u4eba\u5bb6\u5bf9\u4f60\u4e00\u7247\u771f\u5fc3\uff0c\u8fd9\u4e00\u8f88\u5b50\u4f60\u53ef\u5f97\u591a\u542c\u5979\u8bdd\u3002\u201d\u8bf4\u7740\u54c8\u54c8\u5927\u7b11\u3002 \u3000\u3000\u80e1\u6590\u542c\u4ed6\u8bdd\u4e2d\u6709\u56e0\uff0c\u5374\u4e0d\u4fbf\u591a\u8bf4\uff0c\u53ea\u5f97\u542b\u7cca\u7b54\u5e94\uff0c\u8bf4\u9053\uff1a\u201c\u518d\u89c1\u5566\u3002\u201d\u738b\u94c1\u5320\u9053\uff1a\u201c\u80e1\u7237\uff0c\u518d\u89c1\uff0c\u518d\u89c1\uff01\u201d\u6536\u62fe\u4e86\u98ce\u7bb1\u5bb6\u751f\uff0c\u6311\u5728\u80a9\u5934\u4fbf\u8d70\u3002\u4ed6\u8d70\u51fa\u51e0\u6b65\uff0c\u7a81\u7136\u653e\u5f00\u55d3\u5b50\uff0c\u5531\u8d77\u6d1e\u5ead\u6e56\u8fb9\u7684\u60c5\u6b4c\u6765\u3002 \u3000\u3000\u53ea\u542c\u4ed6\u5531\u9053\uff1a\u201c\u5c0f\u59b9\u5b50\u5f85\u60c5\u90ce\u2014\u2014\u6069\u60c5\u6df1\uff0c \u3000\u3000\u4f60\u83ab\u8d1f\u4e86\u59b9\u5b50\u2014\u2014\u4e00\u6bb5\u60c5\uff0c \u3000\u3000\u4f60\u89c1\u4e86\u5979\u9762\u65f6\u2014\u2014\u8981\u5f85\u5979\u597d\uff0c \u3000\u3000\u4f60\u4e0d\u89c1\u5979\u9762\u65f6\u2014\u2014\u5929\u5929\u8981\u5341\u4e03\u516b\u904d\u6302\u5728\u5fc3\uff01\u201d \u3000\u3000\u4ed6\u7684\u55d3\u5b50\u6709\u4e9b\u5636\u54d1\uff0c\u4f46\u9759\u591c\u4e2d\u542c\u7740\u8fd9\u66f2\u60c5\u6b4c\uff0c\u81ea\u6709\u4e00\u80a1\u8361\u4eba\u5fc3\u9b44\u7684\u7f20\u7ef5\u5473\u9053\u3002\u80e1\u6590\u7ad9\u5728\u95e8\u53e3\uff0c\u542c\u5f97\u6b4c\u58f0\u6e10\u6e10\u8fdc\u53bb\uff0c\u9690\u6ca1\u4e0d\u95fb\uff0c\u8fd9\u624d\u56de\u5230\u53a8\u623f\u3002 \u3000\u3000\u53ea\u89c1\u59dc\u5c0f\u94c1\u5df2\u7136\u9192\u8f6c\uff0c\u7ad9\u5728\u5730\u4e0b\uff0c\u5168\u8eab\u6e7f\u6dcb\u6dcb\u7684\uff0c\u4e0a\u8eab\u5df2\u62ab\u4e86\u8863\u886b\uff0c\u59dc\u5bb6\u4e09\u4eba\u5bf9\u7a0b\u7075\u7d20\u53c8\u662f\u5fcc\u60ee\uff0c\u53c8\u662f\u6000\u6068\uff0c\u4f46\u5bf9\u5979\u7528\u836f\u4f7f\u836f\u7684\u795e\u6280\uff0c\u4e0d\u81ea\u7981\u7684\u4e5f\u6709\u4e00\u80a1\u8273\u7fa1\u4e4b\u610f\u3002\u4e09\u4eba\u51b7\u51b7\u7684\u7ad9\u7740\uff0c\u5e76\u4e0d\u9053\u8c22\uff0c\u5374\u4e5f\u4e0d\u793a\u654c\u610f\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u4ece\u6000\u4e2d\u53d6\u51fa\u4e09\u675f\u767d\u8272\u7684\u5e72\u8349\u836f\uff0c\u653e\u5728\u684c\u4e0a\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u79bb\u5f00\u6b64\u95f4\u4e4b\u65f6\uff0c\u90a3\u5b5f\u5bb6\u4e00\u5e72\u4eba\u5b9a\u4f1a\u8ffd\u8e2a\u62e6\u622a\u3002\u8fd9\u4e09\u675f\u918d\u9190\u9999\u7528\u4e03\u5fc3\u6d77\u68e0\u70bc\u5236\u8fc7\uff0c\u8db3\u4ee5\u9000\u654c\uff0c\u4f46\u4e0d\u81f4\u6740\u4eba\u518d\u589e\u65b0\u4ec7\u3002\u201d \u3000\u3000\u59dc\u94c1\u5c71\u542c\u5230\u8fd9\u91cc\uff0c\u8138\u73b0\u559c\u8272\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u5e08\u59b9\uff0c\u591a\u8c22\u4f60\u5e2e\u6211\u60f3\u5f97\u5468\u5230\u3002\u201d\u80e1\u6590\u5fc3\u60f3\uff1a\u201c\u5979\u6551\u6d3b\u4f60\u513f\u5b50\u6027\u547d\uff0c\u4f60\u4e0d\u8bf4\u4e00\u4e2a\u8c22\u5b57\uff0c\u76f4\u5230\u52a9\u4f60\u9000\u654c\uff0c\u8fd9\u624d\u79f0\u8c22\uff0c\u60f3\u6765\u8fd9\u654c\u4eba\u5b9a\u7136\u751a\u5f3a\u3002\u5374\u4e0d\u77e5\u5b5f\u5bb6\u7684\u4eba\u662f\u54ea\u4e00\u8def\u82f1\u96c4\u597d\u6c49\uff0c\u8fde\u8fd9\u5bf9\u7528\u6bd2\u7684\u9ad8\u624b\u4e5f\u4e00\u7b79\u83ab\u5c55\uff0c\u53ea\u6709\u56f0\u5b88\u5728\u94c1\u5c4b\u4e4b\u4e2d\u3002\u201d \u3000\u3000\u7a0b\u7075\u7d20\u8bf4\u9053\uff1a\u201c\u5c0f\u94c1\uff0c\u4e2d\u4e86\u9b3c\u8759\u8760\u5267\u6bd2\u90a3\u4e24\u4eba\uff0c\u90fd\u662f\u5b5f\u5bb6\u7684\u5427\uff1f\u4f60\u4e0b\u624b\u597d\u72e0\u554a\uff01\u201d\u5979\u8bf4\u8fd9\u8bdd\u4e4b\u65f6\uff0c\u5411\u5c0f\u94c1\u4e00\u773c\u4e5f\u6ca1\u77a7\u3002 \u3000\u3000\u59dc\u5c0f\u94c1\u5413\u4e86\u4e00\u8df3\uff0c\u5fc3\u60f3\uff1a\u201c\u4f60\u600e\u77e5\u9053\uff1f\u201d\u55eb\u5685\u7740\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u2026\u2026\u201d\u59dc\u94c1\u5c71\u9053\uff1a\u201c\u5c0f\u5e08\u59b9\uff0c\u5c0f\u94c1\u6b64\u4e8b\u5927\u9519\uff0c\u611a\u5144\u5df2\u8d23\u6253\u4ed6\u8fc7\u4e86\u3002\u201d\u8bf4\u7740\u8d70\u8fc7\u53bb\u62c9\u8d77\u5c0f\u94c1\u7684\u8863\u886b\uff0c\u63a8\u7740\u4ed6\u8eab\u5b50\u8f6c\u8fc7\u80cc\u540e\u6765\uff0c\u9732\u51fa\u6ee1\u80cc\u97ad\u75d5\uff0c\u8840\u8272\u6bb7\u7136\uff0c\u90fd\u662f\u65b0\u7ed3\u7684\u75a4\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u7ed9\u4ed6\u7597\u6bd2\u4e4b\u65f6\uff0c\u65e9\u5df2\u77a7\u89c1\uff0c\u4f46\u60f3\u5230\u4f7f\u7528\u65e0\u836f\u53ef\u89e3\u7684\u5267\u6bd2\uff0c\u5b9e\u662f\u672c\u95e8\u5927\u5fcc\uff0c\u4e0d\u5f97\u4e0d\u518d\u63d0\u53ca\u3002\u5979\u6240\u4ee5\u77e5\u9053\u90a3\u4e24\u4eba\u662f\u5c0f\u94c1\u6240\u6bd2\u6b7b\uff0c\u4e5f\u662f\u56e0\u89c1\u5230\u4ed6\u80cc\u4e0a\u97ad\u75d5\uff0c\u8fd9\u624d\u63a8\u60f3\u800c\u77e5\u3002\u5979\u60f3\u8d77\u5148\u5e08\u65e0\u55d4\u5927\u5e08\u7684\u8c06\u8c06\u544a\u8beb\uff1a\u201c\u672c\u95e8\u64c5\u4e8e\u4f7f\u6bd2\uff0c\u65c1\u4eba\u6df1\u6076\u75db\u7edd\uff0c\u5176\u5b9e\u4e0b\u6bd2\u4f24\u4eba\uff0c\u6bd4\u4e4b\u5175\u5203\u62f3\u811a\u5374\u591a\u4e86\u4e00\u5c42\u6148\u60b2\u5fc3\u80a0\u3002\u4e0b\u6bd2\u4e4b\u540e\uff0c\u5982\u679c\u5bf9\u65b9\u6094\u609f\u6c42\u9976\uff0c\u7acb\u8a93\u6539\u8fc7\uff0c\u53c8\u6216\u662f\u53d1\u89c9\u4f24\u9519\u4e86\u4eba\uff0c\u90fd\u53ef\u89e3\u6551\u3002\u4f46\u82e5\u4e00\u5200\u5c06\u4eba\u6740\u4e86\uff0c\u5374\u662f\u4eba\u6b7b\u4e0d\u80fd\u590d\u751f\u3002\u56e0\u6b64\u51e1\u662f\u65e0\u836f\u53ef\u89e3\u7684\u5267\u6bd2\uff0c\u672c\u95e8\u5f1f\u5b50\u51b3\u8ba1\u4e0d\u53ef\u7528\u4ee5\u4f24\u4eba\uff0c\u5bf9\u65b9\u5c31\u662f\u5927\u5978\u5927\u6076\uff0c\u603b\u4e5f\u8981\u7ed9\u4ed6\u7559\u4e00\u6761\u56de\u5934\u81ea\u65b0\u4e4b\u8def\u3002\u201d\u5fc3\u60f3\u8fd9\u6761\u672c\u95e8\u7684\u5927\u6212\uff0c\u4e8c\u5e08\u54e5\u4e09\u5e08\u59ca\u5bf9\u5c0f\u94c1\u4e5f\u4e00\u5b9a\u5e38\u81ea\u8a00\u53ca\uff0c\u4e0d\u77e5\u4ed6\u4f55\u4ee5\u7adf\u6562\u5927\u80c6\u72af\u89c4\uff1f\u89c1\u4ed6\u80cc\u4e0a\u97ad\u75d5\u7d2f\u7d2f\uff0c\u7eb5\u6a2a\u4ea4\u53c9\uff0c\u60f3\u6765\u7236\u6bcd\u8d23\u6253\u4e0d\u8f7b\uff0c\u8fd9\u6b21\u53c8\u53d7\u6cb8\u6c34\u71ac\u8eab\u4e4b\u82e6\uff0c\u4e5f\u662f\u4e00\u756a\u91cd\u60e9\uff0c\u4e8e\u662f\u8eac\u8eab\u65bd\u793c\uff0c\u8bf4\u9053\uff1a\u201c\u5e08\u54e5\u5e08\u59ca\uff0c\u5c0f\u59b9\u591a\u6709\u5f97\u7f6a\uff0c\u54b1\u4eec\u540e\u4f1a\u6709\u671f\u3002\u201d \u3000\u3000\u59dc\u94c1\u5c71\u8fd8\u4e86\u4e00\u63d6\uff0c\u859b\u9e4a\u53ea\u54fc\u4e86\u4e00\u58f0\uff0c\u5374\u4e0d\u7406\u4f1a\u3002\u7a0b\u7075\u7d20\u4e5f\u4e0d\u4ee5\u4e3a\u610f\uff0c\u5411\u80e1\u6590\u4f5c\u4e2a\u773c\u8272\uff0c\u76f8\u5055\u51fa\u95e8\u3002 \u3000\u3000\u4e24\u4eba\u8de8\u51fa\u5927\u95e8\uff0c\u59dc\u94c1\u5c71\u81ea\u540e\u8d76\u4e0a\uff0c\u53eb\u9053\uff1a\u201c\u5c0f\u5e08\u59b9\uff01\u201d\u7a0b\u7075\u7d20\u56de\u8fc7\u5934\u6765\uff0c\u89c1\u4ed6\u8138\u4e0a\u6709\u4e3a\u96be\u4e4b\u8272\uff0c\u6b32\u8a00\u53c8\u6b62\uff0c\u5df2\u77e5\u5176\u610f\uff0c\u95ee\u9053\uff1a\u201c\u4e8c\u5e08\u54e5\u6709\u4f55\u5429\u5490\uff1f\u201d\u59dc\u94c1\u5c71\u9053\uff1a\u201c\u90a3\u4e09\u675f\u918d\u9190\u9999\uff0c\u987b\u5f97\u6709\u4e09\u4e2a\u529f\u529b\u76f8\u82e5\u4e4b\u4eba\u8fd0\u6c14\u65bd\u4e3a\uff0c\u65b9\u80fd\u62d2\u654c\u3002\u5c0f\u94c1\u529f\u529b\u5c1a\u6d45\uff0c\u611a\u5144\u60f3\u8bf7\u5e08\u59b9\u2026\u2026\u201d\u8bf4\u5230\u8fd9\u91cc\uff0c\u867d\u6781\u76fc\u5979\u7559\u4e0b\u76f8\u52a9\uff0c\u603b\u89c9\u8bf4\u4e0d\u51fa\u53e3\uff0c\u201c\u60f3\u8bf7\u5e08\u59b9\u2026\u2026\u201d\u51e0\u4e2a\u5b57\u8fde\u8bf4\u4e86\u51e0\u904d\uff0c\u63a5\u4e0d\u4e0b\u8bdd\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u6307\u7740\u95e8\u5916\u7684\u7af9\u7ba9\u9053\uff1a\u201c\u5927\u5e08\u54e5\u4fbf\u5728\u8fd9\u7af9\u7ba9\u4e4b\u4e2d\u3002\u5c0f\u59b9\u7559\u4e0b\u7684\u6d77\u68e0\u82b1\u7c89\uff0c\u8db3\u591f\u66ff\u4ed6\u89e3\u6bd2\u3002\u4e8c\u5e08\u54e5\u4f55\u4e0d\u4e58\u673a\u8ddf\u4ed6\u4fee\u597d\u8a00\u548c\uff0c\u4e5f\u53ef\u5f97\u4e00\u5f3a\u52a9\uff1f\u201d\u59dc\u94c1\u5c71\u5927\u559c\uff0c\u4ed6\u4e00\u76f4\u4e3a\u5927\u5e08\u54e5\u7684\u7ea0\u7f20\u4e0d\u4f11\u800c\u70e6\u607c\uff0c\u60f3\u4e0d\u5230\u5c0f\u5e08\u59b9\u7adf\u5df2\u5b89\u6392\u4e86\u8fd9\u4e2a\u4e00\u4e3e\u4e24\u5f97\u7684\u5999\u8ba1\uff0c\u65e2\u9000\u5f3a\u654c\uff0c\u53c8\u89e3\u4e86\u5e08\u5144\u5f1f\u95f4\u591a\u5e74\u7684\u5acc\u9699\uff0c\u5fd9\u8fde\u58f0\u9053\u8c22\uff0c\u5c06\u7af9\u7ba9\u63d0\u8fdb\u95e8\u53bb\u3002 \u3000\u3000\u80e1\u6590\u4ece\u94c1\u95e8\u677f\u4e0a\u62fe\u8d77\u90a3\u675f\u67af\u4e86\u7684\u84dd\u82b1\uff0c\u653e\u5165\u6000\u4e2d\u3002\u7a0b\u7075\u7d20\u6643\u4e86\u4ed6\u4e00\u773c\uff0c\u5411\u59dc\u94c1\u5c71\u6325\u624b\u9053\u522b\uff0c\u8bf4\u9053\uff1a\u201c\u4e8c\u5e08\u54e5\uff0c\u4f60\u5934\u8138\u51fa\u8840\uff0c\u8eab\u4e0a\u6bd2\u6c14\u5df2\u7136\u6563\u53bb\uff0c\u53ef\u522b\u602a\u5c0f\u59b9\u65e0\u793c\u554a\u3002\u201d\u59dc\u94c1\u5c71\u4e00\u695e\uff0c\u767b\u65f6\u9192\u609f\uff0c\u5fc3\u9053\uff1a\u201c\u5979\u53eb\u738b\u94c1\u5320\u6253\u6211\uff0c\u56fa\u662f\u60e9\u6211\u6614\u65e5\u7684\u51f6\u6a2a\uff0c\u4f46\u4e5f\u672a\u59cb\u4e0d\u65e0\u5584\u610f\u3002\u9e4a\u59b9\u6bd2\u6c14\u672a\u6563\uff0c\u8fd8\u5f97\u7ed9\u5979\u653e\u8840\u5462\uff01\u201d\u60f3\u8d77\u4e8b\u4e8b\u65e9\u5728\u8fd9\u4e2a\u5c0f\u5e08\u59b9\u7684\u7b97\u4e2d\uff0c\u81ea\u5df1\u8fdc\u975e\u5176\u654c\uff0c\u7ec8\u4e8e\u6b7b\u5fc3\u584c\u5730\uff0c\u606f\u4e86\u62a2\u593a\u5e08\u7236\u9057\u8457\u201c\u836f\u738b\u795e\u7bc7\u201d\u7684\u5ff5\u5934\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u548c\u80e1\u6590\u56de\u5230\u8305\u820d\uff0c\u953a\u5146\u6587\u5140\u81ea\u6c89\u9189\u672a\u9192\u3002\u8fd9\u4e00\u665a\u6574\u6574\u5fd9\u4e86\u4e00\u591c\uff0c\u6b64\u65f6\u5929\u5df2\u5927\u660e\uff0c\u7a0b\u7075\u7d20\u53d6\u51fa\u89e3\u836f\uff0c\u8981\u80e1\u6590\u5582\u7ed9\u953a\u5146\u6587\u670d\u4e0b\uff0c\u7136\u540e\u4e24\u4eba\u5404\u62ff\u4e86\u4e00\u628a\u9504\u5934\uff0c\u5c06\u82b1\u5703\u4e2d\u8df5\u8e0f\u672a\u5c3d\u7684\u84dd\u82b1\u7ec6\u7ec6\u8fde\u6839\u9504\u53bb\uff0c\u4e0d\u7559\u534a\u68f5\uff0c\u5c3d\u6570\u6df1\u57cb\u5165\u571f\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u6211\u5148\u89c1\u72fc\u7fa4\u6765\u88ad\uff0c\u8fd8\u9053\u662f\u5b5f\u5bb6\u7684\u4eba\u6765\u62a2\u84dd\u82b1\uff0c\u540e\u6765\u89c1\u5c0f\u94c1\u9879\u9888\u4e2d\u6302\u4e86\u4e00\u5927\u675f\u836f\u8349\uff0c\u624d\u731c\u5230\u4ed6\u7684\u7528\u610f\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u4ed6\u600e\u4e48\u4e2d\u4e86\u4f60\u4e03\u5fc3\u6d77\u68e0\u4e4b\u6bd2\uff1f\u9ed1\u6697\u4e2d\u6211\u6ca1\u77a7\u5f97\u6e05\u695a\u3002\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u6211\u7528\u900f\u9aa8\u9489\u6253\u4e86\u4ed6\u4e00\u9489\uff0c\u9489\u4e0a\u6709\u4e03\u5fc3\u6d77\u68e0\u7684\u6bd2\u8d28\uff0c\u8fd8\u5e26\u7740\u90a3\u5c01\u5047\u5192\u5927\u5e08\u54e5\u7684\u4fe1\uff0c\u7ea6\u4ed6\u4eec\u5728\u6811\u6797\u4e2d\u76f8\u4f1a\u3002\u90a3\u900f\u9aa8\u9489\u662f\u5927\u5e08\u54e5\u81ea\u94f8\u7684\u72ec\u95e8\u6697\u5668\uff0c\u4e8c\u5e08\u54e5\u4e09\u5e08\u59ca\u5411\u6765\u8ba4\u5f97\uff0c\u81ea\u662f\u6ca1\u6709\u6000\u7591\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u4f60\u5927\u5e08\u54e5\u7684\u6697\u5668\uff0c\u4f60\u5374\u4ece\u4f55\u5904\u5f97\u6765\uff1f\u201d \u3000\u3000\u7a0b\u7075\u7d20\u7b11\u9053\uff1a\u201c\u4f60\u5012\u731c\u731c\u3002\u201d\u80e1\u6590\u5fae\u4e00\u6c89\u541f\uff0c\u9053\uff1a\u201c\u554a\uff01\u662f\u4e86\uff0c\u90a3\u65f6\u4f60\u5927\u5e08\u54e5\u5df2\u7ed9\u4f60\u64d2\u4f4f\uff0c\u660f\u6655\u5728\u7af9\u7ba9\u4e4b\u4e2d\uff0c\u6697\u5668\u662f\u4ece\u4ed6\u8eab\u4e0a\u641c\u51fa\u6765\u7684\u3002\u201d\u7a0b\u7075\u7d20\u7b11\u9053\uff1a\u201c\u4e0d\u9519\u3002\u5927\u5e08\u54e5\u89c1\u4e86\u6211\u7684\u84dd\u82b1\u540e\u65e9\u5df2\u8d77\u7591\uff0c\u4f60\u4eec\u5411\u4ed6\u95ee\u8def\uff0c\u4ed6\u4fbf\u8ddf\u8e2a\u800c\u6765\uff0c\u6b63\u597d\u81ea\u6295\u7af9\u7ba9\u3002\u201d \u3000\u3000\u4e24\u4eba\u8bf4\u5f97\u9ad8\u5174\uff0c\u4e00\u9f50\u501a\u9504\u5927\u7b11\uff0c\u5ffd\u542c\u5f97\u8eab\u540e\u4e00\u4e2a\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u4ec0\u4e48\u597d\u7b11\u554a\uff1f\u201d\u4e24\u4eba\u56de\u8fc7\u5934\u6765\uff0c\u53ea\u89c1\u953a\u5146\u6587\u8ff7\u8ff7\u7cca\u7cca\u5730\u7ad9\u5728\u5c4b\u6a90\u4e0b\uff0c\u8138\u4e0a\u7ea2\u7ea2\u7684\u5c1a\u5e26\u9152\u610f\u3002\u80e1\u6590\u4e00\u6123\uff0c\u9053\uff1a\u201c\u7075\u59d1\u5a18\uff0c\u82d7\u5927\u4fa0\u4f24\u52bf\u4e0d\u8f7b\uff0c\u6211\u4eec\u987b\u5f97\u4fbf\u53bb\u3002\u8fd9\u89e3\u836f\u5982\u4f55\u7528\u6cd5\uff0c\u8bf7\u4f60\u6307\u70b9\u3002\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u82d7\u5927\u4fa0\u4f24\u5728\u773c\u76ee\uff0c\u90a3\u662f\u4eba\u8eab\u6700\u67d4\u5ae9\u4e4b\u5904\uff0c\u7528\u836f\u8f7b\u91cd\uff0c\u5927\u6709\u659f\u914c\u3002\u4e0d\u77e5\u4ed6\u4f24\u5f97\u600e\u6837\uff1f\u201d\u8fd9\u4e00\u53e5\u8bdd\u53ef\u95ee\u5012\u4e86\u80e1\u6590\u3002\u4ed6\u4e00\u610f\u60f3\u8bf7\u5979\u53bb\u65bd\u6551\uff0c\u53ea\u662f\u7d20\u65e0\u6e0a\u6e90\uff0c\u4eba\u5bb6\u53c8\u662f\u4e2a\u5e74\u8f7b\u5973\u5b50\uff0c\u4fbf\u50cf\u59dc\u94c1\u5c71\u90a3\u6837\uff0c\u90a3\u4e00\u53e5\u76f8\u6c42\u7684\u8bdd\u7adf\u7136\u8bf4\u4e0d\u51fa\u53e3\u6765\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u5fae\u7b11\u9053\uff1a\u201c\u4f60\u82e5\u6c42\u6211\uff0c\u6211\u4fbf\u53bb\u3002\u53ea\u662f\u4f60\u4e5f\u987b\u7b54\u5e94\u6211\u4e00\u4ef6\u4e8b\u3002\u201d\u80e1\u6590\u5927\u559c\uff0c\u5fd9\u9053\uff1a\u201c\u7b54\u5e94\u5f97\uff0c\u7b54\u5e94\u5f97\uff0c\u4ec0\u4e48\u4e8b\u554a\uff1f\u201d \u3000\u3000\u7a0b\u7075\u7d20\u7b11\u9053\uff1a\u201c\u8fd9\u65f6\u8fd8\u4e0d\u77e5\u9053\uff0c\u5c06\u6765\u6211\u60f3\u5230\u4e86\u4fbf\u8ddf\u4f60\u8bf4\uff0c\u5c31\u6015\u4f60\u65e5\u540e\u8981\u8d56\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u6211\u8d56\u4e86\u4fbf\u662f\u4e2a\u8d3c\u738b\u516b\uff01\u201d\u7a0b\u7075\u7d20\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u6536\u62fe\u4e9b\u66ff\u6362\u8863\u670d\uff0c\u54b1\u4eec\u4fbf\u8d70\u3002\u201d\u80e1\u6590\u89c1\u5979\u8eab\u5b50\u7626\u7626\u602f\u602f\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u4f60\u4e00\u591c\u6ca1\u7761\uff0c\u53ea\u6015\u592a\u7d2f\u4e86\u3002\u201d\u7a0b\u7075\u7d20\u8f7b\u8f7b\u6447\u5934\uff0c\u7fe9\u7136\u8fdb\u623f\u3002 \u3000\u3000\u953a\u5146\u6587\u54ea\u77e5\u81ea\u5df1\u6c89\u7761\u534a\u591c\uff0c\u5df2\u8d77\u4e86\u4e0d\u5c11\u53d8\u6545\uff0c\u4e00\u65f6\u4e4b\u95f4\u80e1\u6590\u4e5f\u6765\u4e0d\u53ca\u5411\u4ed6\u7ec6\u8bf4\uff0c\u53ea\u8bf4\u89e3\u836f\u5df2\u7ecf\u6c42\u5230\uff0c\u8fd9\u4f4d\u7a0b\u59d1\u5a18\u662f\u6cbb\u4f24\u7597\u6bd2\u7684\u597d\u624b\uff0c\u7b54\u5e94\u540c\u53bb\u7ed9\u82d7\u4eba\u51e4\u533b\u773c\u3002\u953a\u5146\u6587\u8fd8\u5f85\u8981\u95ee\uff0c\u7a0b\u7075\u7d20\u5df2\u4ece\u623f\u4e2d\u51fa\u6765\uff0c\u80cc\u4e0a\u8d1f\u4e86\u4e00\u4e2a\u5c0f\u5305\uff0c\u624b\u4e2d\u6367\u7740\u4e00\u5c0f\u76c6\u82b1\u3002 \u3000\u3000\u8fd9\u76c6\u82b1\u7684\u53f6\u5b50\u4e5f\u548c\u5bfb\u5e38\u6d77\u68e0\u65e0\u5f02\uff0c\u82b1\u74e3\u7d27\u8d34\u679d\u5e72\u800c\u751f\uff0c\u82b1\u679d\u5982\u94c1\uff0c\u82b1\u74e3\u4e0a\u6709\u4e03\u4e2a\u5c0f\u5c0f\u7684\u9ec4\u70b9\u3002\u80e1\u6590\u9053\uff1a\u201c\u8fd9\u4fbf\u662f\u5927\u540d\u9f0e\u9f0e\u7684\u4e03\u5fc3\u6d77\u68e0\u4e86\uff1f\u201d\u7a0b\u7075\u7d20\u6367\u7740\u9001\u5230\u4ed6\u9762\u524d\uff0c\u80e1\u6590\u5413\u4e86\u4e00\u8df3\uff0c\u4e0d\u81ea\u7981\u5730\u5411\u540e\u9000\u4e86\u4e00\u6b65\u3002\u7a0b\u7075\u7d20\u5657\u54e7\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u82b1\u7684\u6839\u830e\u82b1\u53f6\uff0c\u5747\u662f\u5947\u6bd2\u65e0\u6bd4\uff0c\u4f46\u4e0d\u52a0\u5236\u70bc\uff0c\u4e0d\u4f1a\u4f24\u4eba\u3002\u4f60\u53ea\u8981\u4e0d\u53bb\u5403\u5b83\uff0c\u4fbf\u6b7b\u4e0d\u4e86\u3002\u201d\u80e1\u6590\u7b11\u9053\uff1a\u201c\u4f60\u5f53\u6211\u662f\u725b\u7f8a\u4e48\uff0c\u5403\u751f\u8349\u751f\u82b1\uff1f\u201d\u5c06\u90a3\u76c6\u82b1\u63a5\u4e86\u8fc7\u6765\u3002\u7a0b\u7075\u7d20\u6263\u4e0a\u677f\u95e8\u3002 \u3000\u3000\u4e09\u4eba\u6765\u5230\u767d\u9a6c\u5bfa\u9547\u4e0a\uff0c\u5411\u836f\u6750\u94fa\u53d6\u56de\u5bc4\u5b58\u7684\u5175\u5203\u3002\u953a\u5146\u6587\u53d6\u51fa\u94f6\u4e24\uff0c\u4e70\u4e86\u4e09\u5339\u5750\u9a91\uff0c\u4e0d\u6562\u803d\u6401\uff0c\u5c31\u539f\u8def\u8d76\u56de\u3002 \u3000\u3000\u90a3\u767d\u9a6c\u5bfa\u662f\u4e2a\u5c0f\u9547\uff0c\u4e70\u5230\u4e09\u5339\u5750\u9a91\u5df2\u7ecf\u5f88\u4e0d\u5bb9\u6613\uff0c\u81ea\u4e0d\u662f\u4ec0\u4e48\u9a8f\u9a6c\u826f\u9a79\uff0c\u884c\u5230\u5929\u9ed1\u4e5f\u4e0d\u8fc7\u8d76\u4e86\u4e24\u767e\u6765\u91cc\u3002\u4e09\u4eba\u8d2a\u8d76\u8def\u7a0b\uff0c\u9519\u8fc7\u4e86\u5bbf\u5934\uff0c\u773c\u89c1\u4e09\u5339\u9a6c\u56f0\u4e4f\u4e0d\u582a\uff0c\u5df2\u7ecf\u4e0d\u80fd\u518d\u8d70\uff0c\u53ea\u5f97\u5728\u4e00\u5ea7\u5c0f\u6811\u6797\u4e2d\u5c31\u5730\u91ce\u5bbf\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u5b9e\u5728\u652f\u6301\u4e0d\u4f4f\u4e86\uff0c\u5012\u5728\u80e1\u6590\u627e\u6765\u7684\u4e00\u5806\u67af\u8349\u4e0a\uff0c\u4e0d\u4e45\u4fbf\u5373\u7761\u53bb\u3002\u953a\u5146\u6587\u53eb\u80e1\u6590\u4e5f\u7761\uff0c\u8bf4\u81ea\u5df1\u6628\u665a\u5df2\u7ecf\u7761\u8fc7\u3002\u4eca\u665a\u53ef\u4ee5\u5b88\u591c\u3002 \u3000\u3000\u80e1\u6590\u7761\u5230\u534a\u591c\uff0c\u5ffd\u542c\u5f97\u4e1c\u8fb9\u9690\u9690\u6709\u864e\u5578\u4e4b\u58f0\uff0c\u4e00\u60ca\u800c\u9192\u3002 \u3000\u3000\u90a3\u864e\u5578\u58f0\u4e0d\u4e45\u4fbf\u5373\u8fdc\u53bb\uff0c\u80e1\u6590\u5374\u518d\u4e5f\u96be\u4ee5\u5165\u7761\uff0c\u8bf4\u9053\uff1a\u201c\u953a\u4e8c\u54e5\u4f60\u7761\u5427\uff0c\u53cd\u6b63\u6211\u7761\u4e0d\u7740\uff0c\u540e\u534a\u591c\u6211\u6765\u5b88\u3002\u201d \u3000\u3000\u4ed6\u6253\u5750\u7247\u523b\uff0c\u542c\u7a0b\u7075\u7d20\u548c\u953a\u5146\u6587\u547c\u5438\u6c89\u7a33\uff0c\u7761\u5f97\u751a\u9163\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u4e00\u6b21\u591a\u7ba1\u95f2\u4e8b\uff0c\u803d\u6401\u4e86\u597d\u51e0\u5929\uff0c\u8ffd\u5bfb\u51e4\u5929\u5357\u4fbf\u66f4\u4e3a\u4e0d\u6613\u4e86\uff0c\u5374\u4e0d\u77e5\u4ed6\u53bb\u4e0d\u53bb\u5317\u4eac\u53c2\u4e0e\u638c\u95e8\u4eba\u5927\u4f1a\uff1f\u201d\u4e1c\u601d\u897f\u60f3\uff0c\u4e0d\u80fd\u5b81\u5b9a\uff0c\u4ece\u6000\u4e2d\u53d6\u51fa\u5e03\u5305\uff0c\u6253\u4e86\u5f00\u6765\uff0c\u53c8\u5c06\u90a3\u675f\u84dd\u82b1\u5305\u5728\u5305\u91cc\uff0c\u5ffd\u7136\u60f3\u8d77\u738b\u94c1\u5320\u6240\u5531\u7684\u90a3\u9996\u60c5\u6b4c\uff0c\u5fc3\u4e2d\u4e00\u52a8\uff1a\u201c\u96be\u9053\u5979\u5f53\u771f\u5bf9\u6211\u5f88\u597d\uff0c\u6211\u5374\u6ca1\u77a7\u51fa\u6765\u4e48\uff1f\u201d \u3000\u3000\u6b63\u81ea\u51fa\u795e\uff0c\u5ffd\u542c\u5f97\u7a0b\u7075\u7d20\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u5305\u513f\u4e2d\u85cf\u7740\u4e9b\u4ec0\u4e48\u5b9d\u8d1d\uff1f\u7ed9\u6211\u77a7\u77a7\u6210\u4e0d\u6210\uff1f\u201d\u80e1\u6590\u56de\u8fc7\u5934\u6765\uff0c\u6de1\u6de1\u6708\u5149\u4e4b\u4e0b\uff0c\u53ea\u89c1\u5979\u4e0d\u77e5\u4f55\u65f6\u5df2\u7136\u9192\u6765\uff0c\u5750\u5728\u67af\u8349\u4e4b\u4e0a\u3002 \u3000\u3000\u80e1\u6590\u9053\uff1a\u201c\u6211\u5f53\u662f\u5b9d\u8d1d\uff0c\u4f60\u77a7\u6765\u6216\u8bb8\u4e0d\u503c\u4e00\u7b11\u3002\u201d\u5c06\u5e03\u5305\u644a\u5f00\u4e86\u9001\u5230\u5979\u9762\u524d\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u6211\u5c0f\u65f6\u5019\u5e73\u56db\u53d4\u7ed9\u6211\u524a\u7684\u4e00\u67c4\u5c0f\u7af9\u5200\uff0c\u8fd9\u662f\u6211\u7ed3\u4e49\u5144\u957f\u8d75\u4e09\u54e5\u7ed9\u7684\u4e00\u6735\u7ea2\u7ed2\u82b1\uff1b\u8fd9\u662f\u6211\u7956\u4f20\u7684\u62f3\u7ecf\u5200\u8c31\u2026\u2026\u201d\u6307\u5230\u8881\u7d2b\u8863\u6240\u8d60\u7684\u90a3\u53ea\u7389\u51e4\uff0c\u987f\u4e86\u4e00\u987f\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u670b\u53cb\u9001\u7684\u4e00\u4ef6\u73a9\u610f\u513f\u3002\u201d \u3000\u3000\u90a3\u7389\u51e4\u5728\u6708\u4e0b\u53d1\u51fa\u67d4\u548c\u7684\u83b9\u5149\uff0c\u7a0b\u7075\u7d20\u542c\u4ed6\u8bed\u97f3\u6709\u5f02\uff0c\u62ac\u8d77\u5934\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u662f\u4e00\u4e2a\u59d1\u5a18\u670b\u53cb\u5427\uff1f\u201d\u80e1\u6590\u8138\u4e0a\u4e00\u7ea2\uff0c\u9053\uff1a\u201c\u662f\uff01\u201d\u7a0b\u7075\u7d20\u7b11\u9053\uff1a\u201c\u8fd9\u8fd8\u4e0d\u662f\u4ef7\u503c\u8fde\u57ce\u7684\u5b9d\u8d1d\u5417\uff1f\u201d\u8bf4\u7740\u5fae\u5fae\u4e00\u7b11\uff0c\u5c06\u5e03\u5305\u8fd8\u7ed9\u80e1\u6590\uff0c\u5f84\u81ea\u7761\u4e86\u3002 \u3000\u3000\u80e1\u6590\u5446\u4e86\u534a\u664c\uff0c\u4e5f\u4e0d\u77e5\u662f\u559c\u662f\u6101\uff0c\u8033\u8fb9\u4f3c\u4e4e\u9690\u9690\u54cd\u8d77\u4e86\u738b\u94c1\u5320\u7684\u6b4c\u58f0\uff1a \u3000\u3000\u4f60\u4e0d\u89c1\u5979\u9762\u65f6\u2014\u2014\u5929\u5929\u8981\u5341\u4e03\u516b\u904d\u6302\u5728\u5fc3\uff01"}),showOnHomepage:!0},{name:"\u7b2c\u5341\u4e00\u7ae0\u3000\u6069\u4ec7\u4e4b\u9645",content:(0,a.jsx)(a.Fragment,{children:"\u6b21\u65e5\u4e00\u65e9\uff0c\u4e09\u4eba\u4e0a\u9a6c\u53c8\u884c\uff0c\u6765\u65f6\u4e24\u4eba\u5feb\u9a6c\uff0c\u53ea\u5954\u9a70\u4e86\u4e00\u65e5\uff0c\u56de\u53bb\u65f6\u5374\u5230\u6b21\u65e5\u5929\u9ed1\uff0c\u65b9\u5230\u82d7\u4eba\u51e4\u6240\u4f4f\u7684\u5c0f\u5c4b\u4e4b\u5916\u3002 \u3000\u3000\u953a\u5146\u6587\u89c1\u5c4b\u5916\u7684\u6811\u4e0a\u7cfb\u7740\u4e03\u5339\u9ad8\u5934\u5927\u9a6c\uff0c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u4f60\u4eec\u5728\u8fd9\u91cc\u7a0d\u7b49\uff0c\u6211\u5148\u53bb\u77a7\u77a7\u3002\u201d\u7ed5\u5230\u5c4b\u540e\uff0c\u542c\u5f97\u5c4b\u4e2d\u6709\u597d\u51e0\u4eba\u5728\u5927\u58f0\u8bf4\u8bdd\uff0c\u6084\u6084\u5230\u7a97\u4e0b\u5411\u5185\u4e00\u5f20\uff0c\u53ea\u89c1\u82d7\u4eba\u51e4\u7528\u5e03\u8499\u4f4f\u4e86\u773c\uff0c\u6602\u7136\u800c\u7acb\uff0c\u5385\u95e8\u53e3\u7ad9\u7740\u51e0\u6761\u6c49\u5b50\uff0c\u624b\u4e2d\u5404\u6267\u5175\u5203\uff0c\u795e\u8272\u751a\u662f\u51f6\u731b\u3002\u953a\u5146\u6587\u73af\u987e\u5ba4\u5185\uff0c\u4e0d\u89c1\u5144\u957f\u5146\u82f1\u3001\u5144\u5f1f\u5146\u80fd\u7684\u5f71\u8e2a\uff0c\u5fc3\u60f3\u4ed6\u4e8c\u4eba\u8d23\u5728\u4fdd\u62a4\u82d7\u5927\u4fa0\uff0c\u5374\u4e0d\u77e5\u4f55\u4ee5\u7adf\u4f1a\u79bb\u53bb\uff0c\u5fc3\u4e2d\u4e0d\u7981\u5fe7\u7591\u3002 \u3000\u3000\u53ea\u542c\u5f97\u90a3\u4e94\u4e2a\u6c49\u5b50\u4e2d\u4e00\u4eba\u8bf4\u9053\uff1a\u201c\u82d7\u4eba\u51e4\uff0c\u4f60\u773c\u775b\u4e5f\u778e\u4e86\uff0c\u6d3b\u5728\u4e16\u4e0a\u53ea\u4e0d\u8fc7\u662f\u591a\u53d7\u4e9b\u513f\u6d3b\u7f6a\u3002\u4f9d\u6211\u8bf4\u554a\uff0c\u8fd8\u4e0d\u5982\u65e9\u70b9\u81ea\u5df1\u5bfb\u4e2a\u4e86\u65ad\uff0c\u4e5f\u514d\u5f97\u5927\u7237\u4eec\u591a\u8d39\u624b\u811a\u3002\u201d\u82d7\u4eba\u51e4\u54fc\u4e86\u4e00\u58f0\uff0c\u5e76\u4e0d\u8bf4\u8bdd\u3002\u53c8\u6709\u4e00\u540d\u6c49\u5b50\u8bf4\u9053\uff1a\u201c\u4f60\u53f7\u79f0\u6253\u904d\u5929\u4e0b\u65e0\u654c\u624b\uff0c\u5728\u6c5f\u6e56\u4e0a\u4e5f\u72c2\u4e86\u51e0\u5341\u5e74\u5566\u3002\u4eca\u65e5\u4e56\u4e56\u513f\u722c\u5728\u5730\u4e0b\u7ed9\u5927\u7237\u4eec\u78d5\u51e0\u4e2a\u54cd\u5934\uff0c\u7237\u4eec\u4e00\u53d1\u5584\u5fc3\uff0c\u8bf4\u4e0d\u5b9a\u8fd8\u80fd\u8ba9\u4f60\u591a\u5403\u51e0\u5e74\u7a9d\u56ca\u996d\u3002\u201d \u3000\u3000\u82d7\u4eba\u51e4\u4f4e\u54d1\u7740\u55d3\u5b50\u9053\uff1a\u201c\u7530\u5f52\u519c\u5462\uff1f\u4ed6\u600e\u4e48\u6ca1\u80c6\u5b50\u4eb2\u81ea\u6765\u8ddf\u6211\u8bf4\u8bdd\uff1f\u201d\u9996\u5148\u8bf4\u8bdd\u7684\u6c49\u5b50\u7b11\u9053\uff1a\u201c\u6599\u7406\u4f60\u8fd9\u778e\u5b50\uff0c\u8fd8\u7528\u5f97\u7740\u7530\u5927\u7237\u81ea\u5df1\u51fa\u9a6c\u4e48\uff1f\u201d\u82d7\u4eba\u51e4\u6da9\u7136\u8bf4\u9053\uff1a\u201c\u7530\u5f52\u519c\u6ca1\u6765\uff1f\u4ed6\u8fde\u6740\u6211\u4e5f\u6ca1\u80c6\u4e48\uff1f\u201d \u3000\u3000\u4fbf\u5728\u6b64\u65f6\uff0c\u953a\u5146\u6587\u5ffd\u89c9\u5f97\u80a9\u5934\u6709\u4eba\u8f7b\u8f7b\u4e00\u62cd\uff0c\u4ed6\u5403\u4e86\u4e00\u60ca\uff0c\u5411\u524d\u7eb5\u51fa\u534a\u4e08\uff0c\u56de\u8fc7\u5934\u6765\uff0c\u89c1\u662f\u80e1\u6590\u548c\u7a0b\u7075\u7d20\u4e24\u4eba\uff0c\u8fd9\u624d\u653e\u5fc3\u3002\u80e1\u6590\u8d70\u5230\u4ed6\u8eab\u524d\uff0c\u5411\u897f\u9996\u4e00\u6307\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u953a\u5927\u54e5\u548c\u4e09\u54e5\u5728\u90a3\u8fb9\u7ed9\u8d3c\u5b50\u56f4\u4e0a\u5566\uff0c\u4f60\u5feb\u53bb\u76f8\u5e2e\u3002\u6211\u5728\u8fd9\u513f\u7167\u6599\u82d7\u5927\u4fa0\u3002\u201d\u953a\u5146\u6587\u77e5\u4ed6\u6b66\u529f\u4e86\u5f97\uff0c\u53c8\u6302\u5ff5\u7740\u5144\u5f1f\uff0c\u5f53\u4e0b\u4ece\u8170\u95f4\u62bd\u51fa\u5224\u5b98\u7b14\uff0c\u5411\u897f\u75be\u9a70\u800c\u53bb\u3002 \u3000\u3000\u4ed6\u8fd9\u4e48\u4e00\u7eb5\u4e00\u5954\uff0c\u5c4b\u4e2d\u5df2\u7136\u77e5\u89c9\u3002\u4e00\u4eba\u559d\u9053\uff1a\u201c\u5916\u8fb9\u662f\u8c01\uff1f\u201d \u3000\u3000\u80e1\u6590\u7b11\u9053\uff1a\u201c\u4e00\u4f4d\u662f\u533b\u751f\uff0c\u4e00\u4e2a\u662f\u5c60\u592b\u3002\u201d\u90a3\u4eba\u6012\u559d\uff1a\u201c\u4ec0\u4e48\u533b\u751f\u5c60\u592b\uff1f\u201d\u80e1\u6590\u7b11\u9053\uff1a\u201c\u533b\u751f\u7ed9\u82d7\u5927\u4fa0\u6cbb\u773c\uff0c\u5c60\u592b\u6740\u732a\u5bb0\u72d7\uff01\u201d \u3000\u3000\u90a3\u4eba\u6012\u9a82\u4e00\u58f0\uff0c\u4fbf\u8981\u62a2\u51fa\u3002\u53e6\u4e00\u540d\u6c49\u5b50\u4e00\u628a\u62c9\u4f4f\u4ed6\u81c2\u8180\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u522b\u4e2d\u4e86\u8c03\u864e\u79bb\u5c71\u4e4b\u8ba1\u3002\u7530\u5927\u7237\u53ea\u53eb\u54b1\u4eec\u6740\u8fd9\u59d3\u82d7\u7684\uff0c\u65c1\u7684\u4e8b\u4e0d\u7528\u591a\u7ba1\u3002\u201d\u90a3\u4eba\u5589\u5934\u5495\u565c\u51e0\u58f0\uff0c\u7ad9\u5b9a\u811a\u4e0d\u52a8\u4e86\u3002\u80e1\u6590\u539f\u6015\u82d7\u4eba\u51e4\u773c\u775b\u4e0d\u4fbf\u5403\u4e8f\uff0c\u8981\u60f3\u8bf1\u654c\u51fa\u5c4b\uff0c\u9010\u4e00\u5bf9\u4ed8\uff0c\u54ea\u77e5\u4ed6\u4eec\u5374\u4e0d\u4e0a\u8fd9\u5f53\u3002 \u3000\u3000\u82d7\u4eba\u51e4\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4f60\u56de\u6765\u4e86\uff1f\u201d\u80e1\u6590\u6717\u58f0\u9053\uff1a\u201c\u5728\u4e0b\u5df2\u8bf7\u5230\u4e86\u6bd2\u624b\u836f\u738b\u4ed6\u8001\u4eba\u5bb6\u6765\uff0c\u82d7\u5927\u4fa0\u7684\u773c\u51c6\u80fd\u6cbb\u597d\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u201c\u6bd2\u624b\u836f\u738b\u201d\uff0c\u539f\u662f\u865a\u5f20\u58f0\u52bf\uff0c\u606b\u5413\u654c\u4eba\uff0c\u679c\u7136\u5c4b\u4e2d\u4e94\u4eba\u5c3d\u7686\u53d8\u8272\uff0c\u4e00\u9f50\u56de\u5934\uff0c\u5374\u89c1\u95e8\u53e3\u7ad9\u7740\u4e00\u4e2a\u7c97\u58ee\u5c11\u5e74\uff0c\u53e6\u6709\u4e00\u4e2a\u7626\u602f\u602f\u7684\u59d1\u5a18\uff0c\u54ea\u91cc\u6709\u4ec0\u4e48\u201c\u6bd2\u624b\u836f\u738b\u201d\uff1f \u3000\u3000\u82d7\u4eba\u51e4\u9053\uff1a\u201c\u8fd9\u91cc\u4e94\u4e2a\u72d7\u5d3d\u5b50\u4e0d\u7528\u5c0f\u5144\u5f1f\u64cd\u5fc3\uff0c\u4f60\u5feb\u53bb\u76f8\u52a9\u953a\u6c0f\u4e09\u96c4\u3002\u8d3c\u5b50\u6765\u7684\u4eba\u4e0d\u5c11\uff0c\u4ed6\u4eec\u8981\u501a\u591a\u4e3a\u80dc\u3002\u201d \u3000\u3000\u80e1\u6590\u8fd8\u672a\u56de\u7b54\uff0c\u53ea\u542c\u5f97\u80cc\u540e\u811a\u6b65\u58f0\u54cd\uff0c\u4e00\u4e2a\u6e05\u6717\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u82d7\u5144\u6599\u4e8b\u5982\u795e\uff0c\u6211\u4eec\u679c\u7136\u662f\u501a\u591a\u4e3a\u80dc\u5566\uff01\u201d \u3000\u3000\u80e1\u6590\u56de\u5934\u4e00\u671b\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u53ea\u89c1\u9ad8\u9ad8\u77ee\u77ee\u5341\u51e0\u6761\u6c49\u5b50\uff0c\u624b\u4e2d\u5404\u6301\u5175\u5203\uff0c\u6162\u6162\u8d70\u8fd1\u3002\u6b64\u5916\u5c1a\u6709\u5341\u4f59\u540d\u5e84\u5ba2\u50ee\u4ec6\uff0c\u9ad8\u4e3e\u706b\u628a\u3002\u953a\u6c0f\u4e09\u96c4\u53cc\u624b\u53cd\u7f1a\uff0c\u5df2\u88ab\u64d2\u4f4f\u3002\u4e00\u4e2a\u4e2d\u5e74\u76f8\u516c\u8170\u60ac\u957f\u5251\uff0c\u8d70\u5728\u5404\u4eba\u524d\u5934\u3002\u80e1\u6590\u89c1\u8fd9\u4eba\u957f\u7709\u4fca\u76ee\uff0c\u6c14\u5b87\u8f69\u6602\uff0c\u6b63\u662f\u6570\u5e74\u524d\u5728\u5546\u5bb6\u5821\u4e2d\u89c1\u8fc7\u7684\u7530\u5f52\u519c\u3002\u5f53\u5e74\u80e1\u6590\u53ea\u662f\u4e2a\u9ec4\u76ae\u7cbe\u7626\u7684\u7ae5\u5b50\uff0c\u773c\u4e0b\u8eab\u5f62\u76f8\u8c8c\u4ff1\u5df2\u5927\u53d8\uff0c\u7530\u5f52\u519c\u81ea\u7136\u4e0d\u8ba4\u5f97\u4ed6\u3002 \u3000\u3000\u82d7\u4eba\u51e4\u4ef0\u5934\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u7530\u5f52\u519c\uff0c\u4f60\u4e0d\u6740\u4e86\u6211\uff0c\u603b\u662f\u7761\u4e0d\u5b89\u7a33\u3002\u4eca\u5929\u5e26\u6765\u7684\u4eba\u53ef\u4e0d\u5c11\u554a\uff01\u201d\u7530\u5f52\u519c\u9053\uff1a\u201c\u6211\u4eec\u662f\u5b89\u4efd\u5b88\u5df1\u7684\u826f\u6c11\uff0c\u600e\u6562\u8bf4\u8981\u4eba\u6027\u547d\uff1f\u53ea\u4e0d\u8fc7\u524d\u6765\u606d\u8bf7\u82d7\u5927\u4fa0\u5230\u820d\u4e0b\u76d8\u6853\u51e0\u65e5\u3002\u8c01\u53eb\u54b1\u4eec\u6709\u6545\u4eba\u4e4b\u60c5\u5462\u3002\u201d\u8fd9\u51e0\u53e5\u8bdd\u8bf4\u5f97\u8f7b\u63cf\u6de1\u5199\uff0c\u53ef\u662f\u6d0b\u6d0b\u81ea\u5f97\u4e4b\u60c5\u6ea2\u4e8e\u8a00\u8868\uff0c\u4eca\u65e5\u8fde\u5a01\u9707\u6e58\u9102\u7684\u953a\u6c0f\u4e09\u96c4\u90fd\u5df2\u88ab\u64d2\uff0c\u82d7\u4eba\u51e4\u53cc\u76ee\u5df2\u778e\uff0c\u6b64\u5916\u66f4\u65e0\u5f3a\u63f4\uff0c\u54ea\u91cc\u66f4\u6709\u9003\u751f\u7684\u673a\u4f1a\uff1f\u81f3\u4e8e\u7ad9\u5728\u95e8\u53e3\u7684\u80e1\u6590\u548c\u7a0b\u7075\u7d20\uff0c\u4ed6\u81ea\u7136\u6ca1\u653e\u5728\u773c\u89d2\u4e4b\u4e0b\uff0c\u4fbf\u4f3c\u6ca1\u8fd9\u4e24\u4e2a\u4eba\u4e00\u822c\u3002 \u3000\u3000\u80e1\u6590\u89c1\u654c\u4f17\u6211\u5be1\uff0c\u953a\u6c0f\u4e09\u96c4\u4e00\u9f50\u5931\u624b\uff0c\u770b\u6765\u5bf9\u65b9\u597d\u624b\u4e0d\u5c11\uff0c\u5982\u4f55\u9000\u654c\u6551\u4eba\uff0c\u5b9e\u662f\u4e0d\u6613\u3002\u4ed6\u6e38\u76ee\u5bdf\u770b\u654c\u60c5\uff0c\u7530\u5f52\u519c\u8eab\u540e\u7ad9\u7740\u4e24\u4e2a\u5973\u5b50\u3002\u6b64\u5916\u6709\u4e00\u4e2a\u67af\u7626\u8001\u8005\u624b\u6301\u70b9\u7a74\u6a5b\uff0c\u53e6\u4e00\u4e2a\u4e2d\u5e74\u6c49\u5b50\u62ff\u7740\u4e00\u5bf9\u94c1\u724c\uff0c\u53cc\u76ee\u7cbe\u5149\u56db\u5c04\uff0c\u770b\u6765\u8fd9\u4e24\u4eba\u90fd\u662f\u52b2\u654c\u3002\u6b64\u5916\u6709\u4e03\u516b\u540d\u6c49\u5b50\u62c9\u7740\u4e24\u6761\u6781\u957f\u6781\u7ec6\u7684\u94c1\u94fe\uff0c\u4e0d\u77e5\u6709\u4ec0\u4e48\u7528\u9014\u3002 \u3000\u3000\u80e1\u6590\u5fae\u4e00\u6c89\u541f\uff0c\u4fbf\u5373\u7701\u609f\uff1a\u201c\u662f\u4e86\uff0c\u4ed6\u4eec\u6015\u82d7\u5927\u4fa0\u773c\u778e\u540e\u4ecd\u662f\u5341\u5206\u5389\u5bb3\uff0c\u8fd9\u4e24\u6761\u94c1\u94fe\u660e\u660e\u662f\u7eca\u811a\u4e4b\u7528\uff0c\u6b3a\u4ed6\u773c\u775b\u4e0d\u4fbf\uff0c\u4e03\u516b\u4eba\u62c9\u7740\u94c1\u94fe\u8fdc\u8fdc\u4e00\u7eca\u4e00\u56f4\uff0c\u4ed6\u6b66\u529f\u518d\u5f3a\uff0c\u4e5f\u975e\u6454\u5012\u4e0d\u53ef\u3002\u201d \u3000\u3000\u4ed6\u5411\u7530\u5f52\u519c\u671b\u4e86\u4e00\u773c\uff0c\u80f8\u53e3\u5fcd\u4e0d\u4f4f\u6012\u706b\u4e0a\u5347\uff0c\u5fc3\u60f3\uff1a\u201c\u4f60\u8bf1\u62d0\u4eba\u5bb6\u59bb\u5b50\uff0c\u82d7\u5927\u4fa0\u5df2\u9976\u4e86\u4f60\uff0c\u7adf\u8981\u4e00\u4e2a\u6bd2\u8ba1\u63a5\u7740\u4e00\u4e2a\uff0c\u975e\u5c06\u4eba\u7f6e\u4e4b\u6b7b\u5730\u4e0d\u53ef\u3002\u5982\u6b64\u51f6\u72e0\uff0c\u5f53\u771f\u79bd\u517d\u4e0d\u5982\u3002\u201d \u3000\u3000\u5176\u5b9e\u7530\u5f52\u519c\u56fa\u7136\u9634\u6bd2\uff0c\u5374\u4e5f\u6709\u4e0d\u5f97\u5df2\u7684\u82e6\u8877\uff0c\u81ea\u4ece\u4e0e\u82d7\u4eba\u51e4\u7684\u59bb\u5b50\u5357\u5170\u79c1\u5954\u4e4b\u540e\uff0c\u60f3\u8d77\u5979\u662f\u5f53\u4e16\u7b2c\u4e00\u9ad8\u624b\u7684\u59bb\u5b50\uff0c\u6bcf\u65e5\u91cc\u98df\u4e0d\u7518\u5473\uff0c\u5bdd\u4e0d\u5b89\u6795\uff0c\u4e00\u6709\u4ec0\u4e48\u98ce\u5439\u8349\u52a8\uff0c\u4fbf\u7591\u5fc3\u662f\u82d7\u4eba\u51e4\u524d\u6765\u5bfb\u4ec7\u3002 \u3000\u3000\u5357\u5170\u521d\u65f6\u5bf9\u4ed6\u662f\u6b7b\u5fc3\u584c\u5730\u7684\u70ed\u60c5\u75f4\u604b\uff0c\u4f46\u89c1\u4ed6\u6574\u65e5\u63d0\u5fc3\u540a\u80c6\uff0c\u65e5\u65e5\u591c\u591c\u5bb3\u6015\u81ea\u5df1\u7684\u4e08\u592b\uff0c\u4e0d\u514d\u751f\u4e86\u9119\u8584\u4e4b\u610f\u3002\u56e0\u4e3a\u8fd9\u4e2a\u4e08\u592b\u82d7\u4eba\u51e4\uff0c\u5979\u5b9e\u5728\u4e0d\u89c9\u5f97\u6709\u4ec0\u4e48\u53ef\u6015\u3002\u5728\u5979\u5fc3\u4e2d\uff0c\u53ea\u8981\u4e24\u5fc3\u771f\u8bda\u7684\u76f8\u7231\uff0c\u4fbf\u662f\u7ed9\u82d7\u4eba\u51e4\u4e00\u5251\u6740\u4e86\uff0c\u90a3\u53c8\u6709\u4ec0\u4e48\uff1f\u5979\u770b\u5230\u7530\u5f52\u519c\u5bf9\u4ed6\u81ea\u5df1\u6027\u547d\u7684\u987e\u5ff5\uff0c\u8fdc\u80dc\u4e8e\u73cd\u91cd\u5979\u7684\u60c5\u7231\u3002\u5979\u662f\u629b\u5f03\u4e86\u4e08\u592b\uff0c\u629b\u5f03\u4e86\u5973\u513f\uff0c\u629b\u5f03\u4e86\u540d\u8282\u6765\u8ddf\u968f\u4ed6\u7684\uff0c\u800c\u4ed6\u5374\u5e76\u4e0d\u4ee5\u4e3a\u8fd9\u662f\u4e16\u754c\u4e0a\u6700\u5b9d\u8d35\u7684\u3002 \u3000\u3000\u56e0\u4e3a\u5bb3\u6015\uff0c\u4e8e\u662f\u7530\u5f52\u519c\u7684\u98ce\u6d41\u6f47\u6d12\u4fbf\u51cf\u8272\u4e86\uff0c\u4e8e\u662f\u5bf9\u7434\u68cb\u4e66\u753b\u4fbf\u4e0d\u5927\u6709\u5174\u81f4\u4e86\uff0c\u4fbf\u5f88\u5c11\u6709\u65f6\u5019\u4f34\u7740\u5979\u5728\u5986\u53f0\u524d\u8c03\u8102\u5f04\u7c89\u4e86\u3002\u4ed6\u5927\u90e8\u5206\u65f6\u5019\u5728\u7ec3\u5251\u6253\u5750\u3002 \u3000\u3000\u8fd9\u4f4d\u5b98\u5bb6\u5c0f\u59d0\uff0c\u5374\u4e00\u76f4\u662f\u8ba8\u538c\u4eba\u5bb6\u6253\u62f3\u52a8\u5200\u7684\u3002\u5c31\u7b97\u6b66\u529f\u7ec3\u5f97\u8ddf\u82d7\u4eba\u51e4\u4e00\u822c\u9ad8\u5f3a\uff0c\u53c8\u503c\u5f97\u4ec0\u4e48\uff1f\u4f55\u51b5\uff0c\u5979\u867d\u7136\u4e0d\u4f1a\u6b66\u529f\uff0c\u5374\u4e5f\u77e5\u9053\u7530\u5f52\u519c\u6c38\u8fdc\u7ec3\u4e0d\u5230\u82d7\u4eba\u51e4\u7684\u5730\u6b65\u3002 \u3000\u3000\u7530\u5f52\u519c\u5374\u77e5\u9053\uff0c\u53ea\u8981\u82d7\u4eba\u51e4\u4e0d\u6b7b\uff0c\u81ea\u5df1\u4e00\u5207\u56fe\u8c0b\u7ec8\u5f52\u662f\u4e00\u573a\u6625\u68a6\uff0c\u4ec0\u4e48\u5bcc\u53ef\u654c\u56fd\u7684\u8d22\u5b9d\uff0c\u4ec0\u4e48\u6c14\u76d6\u6c5f\u6e56\u7684\u6743\u52bf\uff0c\u7ec8\u7a76\u4e0d\u8fc7\u662f\u955c\u4e2d\u82b1\u3001\u6c34\u4e2d\u6708\u7f62\u4e86\uff01 \u3000\u3000\u56e0\u6b64\u867d\u7136\u662f\u81ea\u5df1\u5bf9\u4e0d\u8d77\u82d7\u4eba\u51e4\uff0c\u4f46\u4ed6\u975e\u6740\u4e86\u8fd9\u4eba\u4e0d\u53ef\u3002\u73b0\u5728\uff0c\u82d7\u4eba\u51e4\u7684\u773c\u775b\u5df2\u5f04\u778e\u4e86\uff0c\u4ed6\u6b66\u529f\u9ad8\u5f3a\u7684\u4e09\u4e2a\u52a9\u624b\u90fd\u5df2\u64d2\u4f4f\u4e86\uff0c\u5ba4\u5185\u6709\u4e94\u540d\u597d\u624b\u5728\u7b49\u5f85\u81ea\u5df1\u4e0b\u624b\u7684\u53f7\u4ee4\uff0c\u5c4b\u5916\u6709\u5341\u591a\u540d\u597d\u624b\u9884\u5907\u622a\u62e6\uff0c\u6b64\u5916\uff0c\u8fd8\u6709\u4e24\u6761\u82d7\u4eba\u51e4\u770b\u4e0d\u89c1\u7684\u957f\u957f\u7684\u94c1\u94fe\u2026\u2026 \u3000\u3000\u7a0b\u7075\u7d20\u9760\u5728\u80e1\u6590\u7684\u8eab\u8fb9\uff0c\u4e00\u76f4\u9ed8\u4e0d\u4f5c\u58f0\uff0c\u4f46\u4e00\u5207\u60c5\u52bf\u5168\u77a7\u5728\u773c\u91cc\u3002\u5979\u7f13\u7f13\u4f38\u624b\u5165\u6000\uff0c\u6478\u51fa\u4e86\u534a\u622a\u8721\u70db\uff0c\u53c8\u53d6\u51fa\u706b\u647a\u3002 \u3000\u3000\u53ea\u8981\u8721\u70db\u4e00\u70b9\u7740\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u5468\u56f4\u7684\u4eba\u5168\u975e\u4e2d\u6bd2\u6655\u5012\u4e0d\u53ef\u3002\u5979\u5411\u8eab\u540e\u4f17\u4eba\u4e00\u773c\u4e5f\u4e0d\u671b\uff0c\u6643\u4eae\u4e86\u706b\u647a\uff0c\u4fbf\u5f80\u70db\u82af\u4e0a\u51d1\u53bb\uff0c\u5728\u591c\u665a\u70b9\u4e00\u679d\u8721\u70db\uff0c\u90a3\u662f\u8c01\u4e5f\u4e0d\u4f1a\u5728\u610f\u7684\u4e8b\u3002 \u3000\u3000\u90a3\u77e5\u80cc\u540e\u7a81\u7136\u98d5\u5730\u4e00\u58f0\uff0c\u6253\u6765\u4e86\u4e00\u679a\u6697\u5668\u3002\u8fd9\u6697\u5668\u81ea\u8fd1\u5904\u53d1\u6765\uff0c\u65e2\u5feb\u4e14\u51c6\uff0c\u7a0b\u7075\u7d20\u731d\u4e0d\u53ca\u9632\uff0c\u8721\u70db\u7adf\u88ab\u6697\u5668\u6253\u6210\u4e24\u622a\uff0c\u8dcc\u5728\u5730\u4e0b\u3002\u5979\u5403\u4e86\u4e00\u60ca\uff0c\u56de\u8fc7\u5934\u6765\uff0c\u53ea\u89c1\u4e00\u4e2a\u5341\u516d\u5c81\u5de6\u53f3\u7684\u5c0f\u59d1\u5a18\u5389\u58f0\u9053\uff1a\u201c\u4f60\u7ed9\u6211\u89c4\u89c4\u77e9\u77e9\u7684\u7ad9\u7740\uff0c\u522b\u6363\u9b3c\uff01\u201d \u3000\u3000\u4f17\u4eba\u76ee\u5149\u4e00\u65f6\u90fd\u5c04\u5230\u4e86\u7a0b\u7075\u7d20\u8eab\u4e0a\uff0c\u5747\u6709\u8bb6\u5f02\u4e4b\u8272\u3002\u7a0b\u7075\u7d20\u89c1\u90a3\u6697\u5668\u662f\u4e00\u679a\u94c1\u9525\uff0c\u6de1\u6de1\u7684\u9053\uff1a\u201c\u6363\u4ec0\u4e48\u9b3c\u554a\uff1f\u201d\u5fc3\u4e2d\u5374\u6697\u81ea\u7740\u6025\uff1a\u201c\u600e\u4e48\u8fd9\u4e2a\u5c0f\u59d1\u5a18\u5c45\u7136\u8bc6\u7834\u4e86\u6211\u7684\u673a\u5173\uff1f\u8fd9\u53ef\u6709\u70b9\u96be\u529e\u4e86\u3002\u201d \u3000\u3000\u7530\u5f52\u519c\u53ea\u659c\u6643\u4e00\u773c\uff0c\u5e76\u4e0d\u5728\u610f\uff0c\u8bf4\u9053\uff1a\u201c\u82d7\u5144\uff0c\u8ddf\u6211\u4eec\u8d70\u5427\uff01\u201d \u3000\u3000\u4ed6\u624b\u4e0b\u4e00\u540d\u6c49\u5b50\u4f38\u624b\u5728\u80e1\u6590\u80a9\u5934\u731b\u529b\u4e00\u63a8\uff0c\u559d\u9053\uff1a\u201c\u4f60\u662f\u4ec0\u4e48\u4eba\uff1f\u7ad9\u5f00\u4e9b\u3002\u8fd9\u91cc\u6ca1\u70ed\u95f9\u77a7\u3002\u201d\u4ed6\u89c1\u80e1\u7a0b\u4e8c\u4eba\u8c8c\u4e0d\u60ca\u4eba\uff0c\u8fd8\u9053\u662f\u82d7\u4eba\u51e4\u7684\u90bb\u5c45\u3002\u80e1\u6590\u4e5f\u4e0d\u8fd8\u624b\uff0c\u7d22\u6027\u88c5\u50bb\uff0c\u4fbf\u7ad9\u5f00\u4e00\u6b65\u3002 \u3000\u3000\u82d7\u4eba\u51e4\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4f60\u5feb\u8d70\uff0c\u522b\u518d\u987e\u6211\uff01\u53ea\u8981\u8bbe\u6cd5\u6551\u51fa\u953a\u6c0f\u4e09\u96c4\uff0c\u82d7\u67d0\u6c38\u611f\u5927\u5fb7\u3002\u201d\u80e1\u6590\u548c\u953a\u6c0f\u4e09\u96c4\u5747\u662f\u5927\u4e3a\u611f\u52a8\uff1a \u3000\u3000\u201c\u82d7\u5927\u4fa0\u4ec1\u4e49\u8fc7\u4eba\uff0c\u867d\u7136\u8eab\u5904\u7edd\u5883\uff0c\u4ecd\u662f\u53ea\u987e\u65c1\u4eba\uff0c\u4e0d\u987e\u81ea\u5df1\u3002\u201d \u3000\u3000\u7530\u5f52\u519c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u5411\u80e1\u6590\u6a2a\u4e86\u4e00\u773c\uff0c\u5fc3\u60f3\uff1a\u201c\u96be\u9053\u8fd9\u5c0f\u5b50\u8fd8\u4f1a\u6709\u4ec0\u4e48\u95e8\u9053\uff1f\u201d\u559d\u9053\uff1a\u201c\u8bf7\u82d7\u5927\u4fa0\u4e0a\u8def\u3002\u201d \u3000\u3000\u8fd9\u516d\u4e2a\u5b57\u4e00\u51fa\u53e3\uff0c\u5c4b\u4e2d\u4e94\u4eba\u5200\u67aa\u5e76\u4e3e\uff0c\u540c\u65f6\u5411\u82d7\u4eba\u51e4\u8eab\u4e0a\u4e94\u5904\u8981\u5bb3\u6740\u53bb\u3002 \u3000\u3000\u5c0f\u5c4b\u7684\u5385\u5802\u672c\u5c31\u4e0d\u5927\uff0c\u516d\u4e2a\u4eba\u6324\u5728\u91cc\u9762\uff0c\u773c\u89c1\u82d7\u4eba\u51e4\u65e0\u53ef\u95ea\u907f\uff0c\u5c82\u77e5\u4ed6\u53cc\u638c\u4e00\u9519\uff0c\u7adf\u662f\u786c\u751f\u751f\u4ece\u4e24\u4eba\u4e4b\u95f4\u6324\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u4e94\u4eba\u5175\u5203\u5c3d\u6570\u843d\u7a7a\uff0c\u5580\u5587\u5587\u51e0\u58f0\u54cd\uff0c\u4e00\u5f20\u6905\u5b50\u88ab\u4e24\u67c4\u5200\u540c\u65f6\u5288\u6210\u6570\u5757\u3002 \u3000\u3000\u82d7\u4eba\u51e4\u56de\u8f6c\u8eab\u6765\uff0c\u795e\u5a01\u51db\u51db\u7684\u7ad9\u5728\u95e8\u53e3\uff0c\u4ed6\u8d64\u624b\u7a7a\u62f3\uff0c\u773c\u4e0a\u5305\u5e03\uff0c\u5374\u5835\u4f4f\u95e8\u4e0d\u8ba9\u4e94\u4e2a\u654c\u4eba\u9003\u8d70\u3002\u80e1\u6590\u672c\u5f85\u51b2\u5165\u76f8\u63f4\uff0c\u4f46\u89c1\u4ed6\u56de\u8eab\u8fd9\u4e48\u4e00\u7ad9\uff0c\u5df2\u77e5\u4ed6\u6709\u6043\u65e0\u6050\uff0c\u7eb5\u65e0\u4e0d\u80dc\uff0c\u4e00\u65f6\u4e5f\u4e0d\u81f4\u843d\u8d25\u3002 \u3000\u3000\u90a3\u4e94\u540d\u6c49\u5b50\u5fc3\u4e2d\u5747\u9053\uff1a\u201c\u6211\u4eec\u4e94\u4e2a\u4eba\u8054\u624b\uff0c\u4eca\u65e5\u82e5\u8fd8\u5bf9\u4ed8\u4e0d\u4e86\u4e00\u4e2a\u778e\u5b50\uff0c\u6b64\u540e\u8fd8\u6709\u4ec0\u4e48\u8138\u9762\u518d\u5728\u6c5f\u6e56\u884c\u8d70\uff1f\u201d \u3000\u3000\u82d7\u4eba\u51e4\u53eb\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4f60\u518d\u4e0d\u8d70\uff0c\u66f4\u5f85\u4f55\u65f6\uff1f\u201d\u80e1\u6590\u9053\uff1a\u201c\u82d7\u5927\u4fa0\u653e\u5fc3\uff0c\u51ed\u8fd9\u4e9b\u72d7\u5d3d\u5b50\uff0c\u8fd8\u6321\u4e0d\u4e86\u6211\u7684\u8def\uff01\u201d\u82d7\u4eba\u51e4\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u82f1\u96c4\u5e74\u5c11\uff0c\u540e\u751f\u53ef\u754f\uff01\u201d\u8bf4\u4e86\u8fd9\u51e0\u4e2a\u5b57\uff0c\u7a81\u7136\u62a2\u5165\u4eba\u4e1b\uff0c\u94c1\u638c\u98de\u821e\uff0c\u8098\u649e\u8db3\u8e22\uff0c\u5a01\u4e0d\u53ef\u5f53\u3002 \u3000\u3000\u5ba4\u4e2d\u8fd9\u4e94\u4eba\u5747\u975e\u5bfb\u5e38\u4e4b\u8f88\uff0c\u4e00\u89c1\u82d7\u4eba\u51e4\u638c\u529b\u6c89\u96c4\uff0c\u4fbf\u5404\u9000\u5f00\uff0c\u9760\u7740\u5899\u58c1\uff0c\u4fdf\u9699\u8fdb\u51fb\u3002\u6df7\u4e71\u4e2d\u684c\u5b50\u503e\u5012\uff0c\u5ba4\u4e2d\u706f\u706b\u7184\u706d\u3002\u5c4b\u5916\u4e24\u4eba\u9ad8\u4e3e\u706b\u628a\uff0c\u8d70\u5230\u95e8\u53e3\uff0c\u56e0\u82d7\u4eba\u51e4\u53cc\u76ee\u65e2\u778e\uff0c\u6709\u65e0\u706b\u5149\u5168\u662f\u4e00\u6837\uff0c\u90a3\u4e94\u4eba\u5374\u53ef\u5927\u5360\u4fbf\u5b9c\u3002 \u3000\u3000\u7a81\u542c\u4e00\u4eba\u5927\u543c\u4e00\u58f0\uff0c\u633a\u67aa\u5411\u82d7\u4eba\u51e4\u523a\u53bb\uff0c\u8fd9\u4e00\u67aa\u5bf9\u51c6\u4ed6\u7684\u5c0f\u8179\uff0c\u53bb\u52bf\u6781\u662f\u72e0\u8fa3\u3002\u82d7\u4eba\u51e4\u53f3\u817f\u6a2a\u8de8\uff0c\u4f38\u638c\u6b32\u6293\u67aa\u5934\uff0c\u90a3\u77e5\u897f\u5357\u89d2\u4e0a\u4e00\u4eba\u6084\u6ca1\u58f0\u7684\u4f0f\u7740\uff0c\u500f\u5730\u6325\u5200\u780d\u51fa\uff0c\u5657\u7684\u4e00\u58f0\uff0c\u6b63\u4e2d\u4ed6\u53f3\u817f\u3002\u539f\u6765\u8fd9\u4eba\u9887\u6709\u667a\u8ba1\uff0c\u77e5\u9053\u82d7\u4eba\u51e4\u5168\u4ed7\u8033\u6735\u542c\u654c\uff0c\u95fb\u98ce\u8fa8\u5668\u3002\u4ed6\u5c4f\u4f4f\u547c\u5438\uff0c\u4e00\u52a8\u4e0d\u52a8\u7684\u8e72\u7740\uff0c\u82d7\u4eba\u51e4\u6fc0\u6597\u65b9\u9163\uff0c\u81ea\u4e0d\u77e5\u4ed6\u7684\u6240\u5728\uff0c\u76f4\u5019\u5230\u82d7\u4eba\u51e4\u7684\u53f3\u817f\u4f38\u5230\u81ea\u5df1\u8ddf\u524d\uff0c\u8fd9\u624d\u4e00\u5200\u780d\u843d\u3002 \u3000\u3000\u5c4b\u5185\u5c4b\u5916\u4f17\u4eba\u89c1\u82d7\u4eba\u51e4\u53d7\u4f24\uff0c\u4e00\u9f50\u6b22\u547c\u3002 \u3000\u3000\u953a\u5146\u82f1\u559d\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u5feb\u53bb\u6551\u82d7\u5927\u4fa0\uff0c\u518d\u5f85\u4e00\u4f1a\u53ef\u6765\u4e0d\u53ca\u4e86\u3002\u201d \u3000\u3000\u4fbf\u5728\u6b64\u65f6\uff0c\u82d7\u4eba\u51e4\u5de6\u80a9\u53c8\u4e2d\u4e86\u4e00\u97ad\u3002\u4ed6\u5fc3\u4e2d\u60f3\uff1a\u201c\u4eca\u65e5\u4e4b\u52bf\uff0c\u82e5\u65e0\u5175\u5203\uff0c\u7a7a\u624b\u6740\u4e0d\u51fa\u91cd\u56f4\u3002\u201d \u3000\u3000\u80e1\u6590\u4e5f\u65e9\u5df2\u770b\u6e05\u695a\u5c40\u9762\uff0c\u987b\u5f97\u5c06\u624b\u4e2d\u5355\u5200\u629b\u7ed9\u82d7\u4eba\u51e4\uff0c\u4ed6\u65b9\u80fd\u5236\u80dc\uff0c\u4f46\u95e8\u5916\u52b2\u654c\u4e0d\u5c11\uff0c\u81ea\u5df1\u6ca1\u4e86\u5175\u5203\uff0c\u5374\u4e5f\u96be\u4ee5\u62b5\u6321\uff0c\u5982\u4f55\u4e24\u5168\uff0c\u4e00\u65f6\u5f77\u5fa8\u65e0\u8ba1\uff0c\u773c\u89c1\u60c5\u52bf\u7d27\u6025\uff0c\u4e0d\u6687\u7ec6\u601d\uff0c\u53eb\u9053\uff1a\u201c\u82d7\u5927\u4fa0\u63a5\u5200\uff01\u201d\u6325\u8d77\u5185\u529b\uff0c\u547c\u7684\u4e00\u58f0\uff0c\u5c06\u5355\u5200\u63b7\u4e86\u8fdb\u53bb\u3002\u8fd9\u4e00\u63b7\u529b\u9053\u5947\u731b\uff0c\u5ba4\u4e2d\u4e94\u4e2a\u654c\u4eba\u4fbf\u8981\u4f38\u624b\u6765\u63a5\uff0c\u624b\u8155\u975e\u6298\u65ad\u4e0d\u53ef\uff0c\u53ea\u6709\u82d7\u4eba\u51e4\u4e00\u4eba\uff0c\u624d\u63a5\u5f97\u4e86\u8fd9\u4e00\u63b7\u3002 \u3000\u3000\u54ea\u77e5\u6b64\u65f6\u82d7\u4eba\u51e4\u7684\u5de6\u8180\u6b63\u4f38\u5230\u897f\u5357\u89d2\u5904\u8bf1\u654c\uff0c\u5f85\u90a3\u4eba\u53c8\u662f\u4e00\u5200\u780d\u51fa\uff0c\u624b\u8155\u4e00\u7ffb\uff0c\u5939\u624b\u5df2\u5c06\u5355\u5200\u62a2\u8fc7\uff0c\u542c\u7740\u80e1\u6590\u5355\u5200\u63b7\u6765\u7684\u98ce\u52bf\uff0c\u5200\u80cc\u5bf9\u5200\u80cc\u4e00\u78b0\uff0c\u5f53\u7684\u4e00\u58f0\uff0c\u706b\u82b1\u56db\u6e85\uff0c\u7adf\u5c06\u63b7\u8fdb\u6765\u7684\u5355\u5200\u7838\u51fa\u95e8\u53bb\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u81ea\u5df1\u7559\u7740\uff0c\u4e14\u77a7\u6211\u778e\u5b50\u6740\u8d3c\u3002\u201d \u3000\u3000\u4ed6\u8eab\u4e0a\u867d\u53d7\u4e86\u4e24\u5904\u4f24\uff0c\u4f46\u624b\u4e2d\u6709\u4e86\u5175\u5203\uff0c\u60c5\u52bf\u767b\u65f6\u5927\u4e0d\u540c\uff0c\u547c\u547c\u4e24\u5200\uff0c\u5c06\u4e94\u540d\u654c\u4eba\u903c\u5f97\u53c8\u8d34\u4f4f\u4e86\u5899\u58c1\u3002 \u3000\u3000\u5c4b\u4e2d\u4e94\u4eba\u7d20\u77e5\u201c\u82d7\u5bb6\u5251\u201d\u7684\u5a01\u540d\uff0c\u4f46\u7cbe\u4e8e\u5251\u672f\u4e4b\u4eba\u6781\u5c11\u4f1a\u4f7f\u5355\u5200\uff0c\u5747\u60f3\u4f60\u7eb5\u7136\u593a\u5f97\u4e00\u628a\u5200\uff0c\u672a\u5fc5\u6bd4\u7a7a\u624b\u66f4\u5f3a\uff0c\u5404\u4eba\u5406\u559d\u4e00\u58f0\uff0c\u633a\u7740\u5175\u5203\u53c8\u4e0a\u3002\u53ea\u89c1\u95e8\u5916\u4eae\u5149\u4e00\u95ea\uff0c\u53c8\u63b7\u8fdb\u4e00\u628a\u5200\u6765\uff0c\u8fd9\u4e00\u6b21\u5374\u662f\u63b7\u7ed9\u90a3\u5355\u5200\u88ab\u593a\u7684\u6c49\u5b50\u3002\u90a3\u4eba\u4f38\u624b\u63a5\u4f4f\uff0c\u4ed6\u9002\u624d\u5175\u5203\u8131\u624b\uff0c\u9887\u89c9\u8138\u4e0a\u65e0\u5149\uff0c\u975e\u7acb\u529f\u96be\u4ee5\u633d\u56de\u989c\u9762\uff0c\u5f53\u4e0b\u821e\u5200\u62a2\u653b\uff0c\u5411\u82d7\u4eba\u51e4\u8fce\u9762\u780d\u53bb\u3002 \u3000\u3000\u82d7\u4eba\u51e4\u51dd\u7acb\u4e0d\u52a8\uff0c\u542c\u5f97\u6b63\u9762\u5200\u6765\uff0c\u5de6\u4fa7\u97ad\u81f3\uff0c\u4ecd\u662f\u4e0d\u95ea\u4e0d\u67b6\uff0c\u5f85\u5f97\u5200\u97ad\u79bb\u8eab\u4e0d\u8fc7\u534a\u5c3a\uff0c\u731b\u5730\u8f6c\u8eab\uff0c\u5237\u7684\u4e00\u5200\uff0c\u6b63\u4e2d\u6301\u97ad\u8005\u53f3\u81c2\uff0c\u624b\u81c2\u7acb\u65ad\uff0c\u94a2\u97ad\u843d\u5730\u3002\u90a3\u4eba\u957f\u58f0\u60e8\u547c\u3002\u6301\u5200\u8005\u5413\u4e86\u4e00\u8df3\uff0c\u4f0f\u8eab\u5411\u65c1\u6eda\u5f00\u3002 \u3000\u3000\u80e1\u6590\u5fc3\u4e2d\u4e00\u52a8\uff1a\u201c\u8fd9\u4e00\u62db\u2018\u9e5e\u5b50\u7ffb\u8eab\u5200\u2019\u660e\u660e\u662f\u6211\u80e1\u5bb6\u5200\u6cd5\uff0c\u82d7\u5927\u4fa0\u5982\u4f55\u4f1a\u4f7f\uff1f\u800c\u4ed6\u4f7f\u5f97\u5c45\u7136\u6bd4\u6211\u66f4\u662f\u7cbe\u5999\uff01\u201d \u3000\u3000\u5c4b\u4e2d\u5176\u4f59\u56db\u4eba\u4e00\u6123\u4e4b\u4e0b\uff0c\u6709\u4eba\u5f00\u53e3\u53eb\u4e86\u8d77\u6765\uff1a\u201c\u82d7\u778e\u5b50\u4e5f\u4f1a\u4f7f\u5200\uff01\u201d \u3000\u3000\u7530\u5f52\u519c\u731b\u5730\u8bb0\u8d77\uff1a\u5f53\u5e74\u80e1\u4e00\u5200\u548c\u82d7\u4eba\u51e4\u66fe\u4e92\u4f20\u5200\u6cd5\u5251\u6cd5\uff0c\u53c8\u66fe\u4ea4\u6362\u5200\u5251\u6bd4\u6b66\uff0c\u5fc3\u4e2d\u4e00\u51db\uff0c\u53eb\u9053\uff1a\u201c\u4ed6\u4f7f\u7684\u662f\u80e1\u5bb6\u5200\u6cd5\uff0c\u4e0e\u82d7\u5bb6\u5251\u5168\u7136\u4e0d\u540c\u3002\u5927\u4f19\u513f\u5c0f\u5fc3\u4e9b\uff01\u201d \u3000\u3000\u82d7\u4eba\u51e4\u54fc\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4eca\u65e5\u53eb\u9f20\u8f88\u89c1\u8bc6\u80e1\u5bb6\u5200\u6cd5\u7684\u5389\u5bb3\uff01\u201d\u8e0f\u4e0a\u4e24\u6b65\uff0c\u4e00\u62db\u201c\u6000\u4e2d\u62b1\u6708\u201d\uff0c\u56de\u5200\u4e00\u524a\uff0c\u4e43\u662f\u865a\u62db\uff0c\u8ddf\u7740\u201c\u95ed\u95e8\u94c1\u6247\u201d\uff0c\u5355\u5200\u4e00\u63a8\u4e00\u6a2a\uff0c\u53c8\u6709\u4e00\u4eba\u8170\u95f4\u4e2d\u5200\uff0c\u5012\u5728\u5730\u4e0b\u3002 \u3000\u3000\u80e1\u6590\u53c8\u60ca\u53c8\u559c\uff1a\u201c\u4ed6\u4f7f\u7684\u679c\u7136\u662f\u6211\u80e1\u5bb6\u5200\u6cd5\uff01\u539f\u6765\u8fd9\u4e24\u62db\u865a\u865a\u5b9e\u5b9e\uff0c\u7adf\u53ef\u4ee5\u5982\u6b64\u53d8\u5316\uff01\u201d\u8981\u77e5\u82d7\u4eba\u51e4\u5f97\u80e1\u4e00\u5200\u4eb2\u53e3\u6307\u70b9\u5200\u6cd5\u7684\u5999\u8be3\u8981\u65e8\uff0c\u4ed6\u6b66\u529f\u6839\u5e95\u53c8\u597d\uff0c\u6bd4\u4e4b\u80e1\u6590\u5355\u4ece\u5200\u8c31\u4e0a\u81ea\u884c\u7422\u78e8\uff0c\u6240\u77e5\u81ea\u7136\u66f4\u4e3a\u7cbe\u6df1\u3002 \u3000\u3000\u4f46\u89c1\u82d7\u4eba\u51e4\u5355\u5200\u5c55\u5f00\uff0c\u5bd2\u5149\u95ea\u95ea\uff0c\u5982\u98ce\u4f3c\u7535\uff0c\u5406\u559d\u58f0\u4e2d\uff0c\u4e00\u62db\u201c\u6c99\u50e7\u62dc\u4f5b\u201d\uff0c\u4e00\u4eba\u82b1\u67aa\u6298\u65ad\uff0c\u659c\u80a9\u88ab\u5288\uff0c\u8ddf\u7740\u201c\u4e0a\u6b65\u6458\u661f\u5200\u201d\uff0c\u53c8\u6709\u4e00\u4eba\u65ad\u817f\u8dcc\u5012\u3002 \u3000\u3000\u7530\u5f52\u519c\u53eb\u9053\uff1a\u201c\u94b1\u56db\u5f1f\uff0c\u51fa\u6765\uff0c\u51fa\u6765\uff01\u201d\u4ed6\u89c1\u82d7\u4eba\u51e4\u5927\u5c55\u795e\u5a01\uff0c\u8fd9\u65f6\u5c4b\u4e2d\u53ea\u5269\u4e0b\u4e86\u4e00\u4e2a\u4f7f\u5355\u5200\u7684\u201c\u94b1\u56db\u5f1f\u201d\uff0c\u5373\u4ee4\u6709\u4eba\u51b2\u5165\u76f8\u63f4\uff0c\u4e5f\u672a\u5fc5\u80fd\u64cd\u80dc\u7b97\uff0c\u51b3\u610f\u8bf1\u4ed6\u51fa\u5c4b\u7528\u94c1\u94fe\u64d2\u62ff\u3002\u4f46\u82d7\u4eba\u51e4\u62e6\u4f4f\u5c4b\u95e8\uff0c\u90a3\u59d3\u94b1\u7684\u5982\u4f55\u80fd\u591f\u51fa\u6765\uff1f \u3000\u3000\u82d7\u4eba\u51e4\u77e5\u9053\u6b64\u4eba\u4fbf\u662f\u9634\u6bd2\u624b\u6cd5\u780d\u4f24\u81ea\u5df1\u53f3\u817f\u4e4b\u4eba\uff0c\u51b3\u4e0d\u5bb9\u4ed6\u5982\u6b64\u8f7b\u6613\u8131\u9003\uff0c\u94a2\u5200\u6643\u52a8\uff0c\u5c06\u4ed6\u903c\u5728\u5c4b\u89d2\u4e4b\u4e2d\uff0c\u731b\u7684\u4e00\u5200\u201c\u7a7f\u624b\u85cf\u5200\u201d\u780d\u5c06\u51fa\u53bb\uff0c\u545b\u5577\u4e00\u54cd\uff0c\u90a3\u4eba\u5355\u5200\u8131\u624b\u3002\u8fd9\u4eba\u6781\u662f\u72e1\u733e\uff0c\u4e58\u52bf\u5728\u5730\u4e0b\u4e00\u6eda\uff0c\u7a7f\u8fc7\u684c\u5e95\uff0c\u60f3\u6b3a\u82d7\u4eba\u51e4\u773c\u4e0d\u89c1\u7269\uff0c\u4fbf\u6b64\u9003\u51fa\u5c4b\u53bb\u3002\u82d7\u4eba\u51e4\u987a\u624b\u6293\u8d77\u4e00\u5f20\u677f\u51f3\uff0c\u7528\u529b\u63b7\u51fa\u3002\u90a3\u4eba\u6b63\u597d\u4ece\u684c\u5e95\u6eda\u51fa\uff0c\u7830\u7684\u4e00\u58f0\uff0c\u677f\u51f3\u649e\u6b63\u4ed6\u7684\u80f8\u53e3\u3002\u8fd9\u4e00\u63b7\u529b\u9053\u4f55\u7b49\u521a\u731b\uff0c\u767b\u65f6\u808b\u9aa8\u4e0e\u51f3\u811a\u9f50\u65ad\uff0c\u90a3\u4eba\u7acb\u65f6\u660f\u6b7b\u8fc7\u53bb\u3002 \u3000\u3000\u82d7\u4eba\u51e4\u7247\u523b\u95f4\u8fde\u4f24\u4e94\u4eba\uff0c\u603b\u7b97\u4ed6\u77e5\u8fd9\u4e9b\u4eba\u5168\u662f\u53d7\u7530\u5f52\u519c\u6307\u4f7f\uff0c\u4e0e\u81ea\u5df1\u65e0\u51a4\u65e0\u4ec7\uff0c\u56e0\u6b64\u672a\u4e0b\u6740\u624b\uff0c\u6bcf\u4eba\u5747\u4f7f\u5176\u8eab\u53d7\u91cd\u4f24\u800c\u6b62\u3002\u4f46\u96f6\u65f6\u4e4b\u95f4\u4e94\u540d\u597d\u624b\u4e00\u9f50\u5012\u5730\uff0c\u5c4b\u5916\u4f17\u4eba\u65e0\u4e0d\u9a87\u7136\uff0c\u5747\u60f3\uff1a\u201c\u8fd9\u4eba\u53f7\u79f0\u6253\u904d\u5929\u4e0b\u65e0\u654c\u624b\uff0c\u679c\u7136\u4e86\u5f97\uff01\u82e5\u4ed6\u773c\u775b\u4e0d\u778e\uff0c\u6211\u8f88\u4eca\u65e5\u90fd\u6b7b\u65e0\u846c\u8eab\u4e4b\u5730\u4e86\u3002\u201d \u3000\u3000\u7530\u5f52\u519c\u6717\u58f0\u7b11\u9053\uff1a\u201c\u82d7\u5144\uff0c\u4f60\u6b66\u529f\u8d8a\u6765\u8d8a\u9ad8\uff0c\u5c0f\u5f1f\u4f69\u670d\u5f97\u5f88\u3002\u6765\u6765\u6765\uff0c\u5c0f\u5f1f\u7528\u5929\u9f99\u5251\u9886\u6559\u9886\u6559\u4f60\u7684\u80e1\u5bb6\u5200\u6cd5\uff01\u201d\u63a5\u7740\u4f7f\u4e2a\u773c\u8272\uff0c\u90a3\u4e9b\u624b\u63e1\u94c1\u94fe\u7684\u6c49\u5b50\u4e0a\u524d\u51e0\u6b65\uff0c\u4f59\u4eba\u5374\u9000\u4e86\u5f00\u53bb\u3002 \u3000\u3000\u82d7\u4eba\u51e4\u9053\uff1a\u201c\u597d\uff01\u201d\u4ed6\u4e5f\u6599\u5230\u7530\u5f52\u519c\u5fc5\u6709\u9634\u9669\u7684\u540e\u7740\uff0c\u4f46\u5f62\u683c\u52bf\u7981\uff0c\u975e\u5f97\u51fa\u5c4b\u52a8\u624b\u4e0d\u53ef\u3002 \u3000\u3000\u80e1\u6590\u7a81\u7136\u8bf4\u9053\uff1a\u201c\u4e14\u6162\uff01\u59d3\u7530\u7684\uff0c\u4f60\u8981\u9886\u6559\u80e1\u5bb6\u5200\u6cd5\uff0c\u4f55\u5fc5\u82d7\u5927\u4fa0\u4eb2\u81ea\u52a8\u624b\uff0c\u5728\u4e0b\u6307\u70b9\u4f60\u51e0\u8def\uff0c\u4e5f\u5c31\u662f\u4e86\uff01\u201d \u3000\u3000\u7530\u5f52\u519c\u89c1\u4ed6\u9002\u624d\u63b7\u5200\u63a5\u5200\u7684\u624b\u6cd5\u52b2\u529b\uff0c\u5df2\u77e5\u4ed6\u4e0d\u662f\u5e73\u5e38\u5c11\u5e74\uff0c\u4f46\u7a76\u4e5f\u6ca1\u600e\u4e48\u653e\u5728\u5fc3\u4e0a\uff0c\u5411\u4ed6\u6a2a\u4e86\u4e00\u773c\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u662f\u4f55\u4eba\uff1f\u80c6\u6562\u5728\u7530\u5927\u7237\u9762\u524d\u53e3\u51fa\u72c2\u8a00\uff1f\u201d \u3000\u3000\u80e1\u6590\u9053\uff1a\u201c\u6211\u662f\u82d7\u5927\u4fa0\u7684\u670b\u53cb\uff0c\u9002\u624d\u89c1\u82d7\u5927\u4fa0\u65bd\u5c55\u80e1\u5bb6\u5200\u6cd5\uff0c\u5fc3\u4e0b\u597d\u751f\u656c\u4f69\uff0c\u8bb0\u4f4f\u4e86\u4ed6\u51e0\u4e0b\u62db\u6570\uff0c\u5c31\u60f3\u8bd5\u6f14\u4e00\u756a\u3002\u9601\u4e0b\u624b\u4e2d\u65e2\u7136\u6709\u5251\uff0c\u53ea\u597d\u52b3\u4f60\u5927\u9a7e\uff0c\u7ed9\u6211\u5582\u5582\u62db\u4e86\uff01\u201d \u3000\u3000\u7530\u5f52\u519c\u6c14\u5f97\u8138\u76ae\u7126\u9ec4\uff0c\u8fd8\u6ca1\u5f00\u53e3\uff0c\u80e1\u6590\u559d\u9053\uff1a\u201c\u770b\u5200\uff01\u201d\u4e00\u62db\u201c\u7a7f\u624b\u85cf\u5200\u201d\uff0c\u5f53\u80f8\u731b\u5288\u8fc7\u53bb\uff0c\u6b63\u662f\u9002\u624d\u82d7\u4eba\u51e4\u7528\u4ee5\u6253\u843d\u59d3\u94b1\u7684\u624b\u4e2d\u5175\u5203\u8fd9\u4e00\u62db\u3002\u7530\u5f52\u519c\u4e3e\u5251\u5c01\u67b6\uff0c\u5f53\u7684\u4e00\u54cd\uff0c\u5200\u5251\u76f8\u4ea4\uff0c\u7530\u5f52\u519c\u8eab\u5b50\u4e00\u6643\uff0c\u80e1\u6590\u5374\u9000\u4e86\u4e00\u6b65\u3002 \u3000\u3000\u8981\u77e5\u7530\u5f52\u519c\u662f\u5929\u9f99\u95e8\u5317\u5b97\u7684\u638c\u95e8\u4eba\uff0c\u4e00\u624b\u5929\u9f99\u5251\u6cd5\u81ea\u5e7c\u7ec3\u8d77\uff0c\u5df2\u6709\u56db\u5341\u5e74\u7684\u9020\u8be3\uff0c\u529f\u529b\u81ea\u6bd4\u80e1\u6590\u6df1\u539a\u5f97\u591a\u3002\u4e24\u4eba\u8fd9\u4e00\u8f83\u5185\u529b\uff0c\u80e1\u6590\u7adf\u81ea\u8f93\u4e86\u4e00\u7b79\u3002\u4f46\u7530\u5f52\u519c\u89c1\u5bf9\u65b9\u5c0f\u5c0f\u5e74\u7eaa\uff0c\u8182\u529b\u7adf\u5982\u6b64\u6c89\u96c4\uff0c\u6ee1\u4ee5\u4e3a\u8fd9\u4e00\u5251\u8981\u5c06\u4ed6\u5355\u5200\u9707\u98de\uff0c\u5185\u4f24\u5455\u8840\uff0c\u90a3\u77e5\u4ed6\u53ea\u9000\u4e86\u4e00\u6b65\uff0c\u8138\u4e0a\u82e5\u65e0\u5176\u4e8b\uff0c\u5012\u4e5f\u4e0d\u7981\u6697\u81ea\u60ca\u8be7\u3002 \u3000\u3000\u82d7\u4eba\u51e4\u7ad9\u5728\u95e8\u53e3\uff0c\u542c\u5f97\u80e1\u6590\u4e0a\u524d\uff0c\u542c\u5f97\u5200\u524a\u7684\u98ce\u52bf\uff0c\u53c8\u542c\u5f97\u4e24\u4eba\u5200\u5251\u76f8\u4ea4\uff0c\u80e1\u6590\u5012\u9000\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4f60\u8fd9\u62db\u2018\u7a7f\u624b\u85cf\u5200\u2019\u4f7f\u5f97\u4e00\u70b9\u4e0d\u9519\u3002\u53ef\u662f\u80e1\u5bb6\u5200\u6cd5\u7684\u8981\u65e8\u7aef\u5728\u62db\u6570\u7cbe\u5947\uff0c\u4e0d\u5728\u4ee5\u529b\u78b0\u529b\u3002\u8bf7\u4f60\u9000\u5f00\uff0c\u8ba9\u6211\u778e\u5b50\u6765\u6536\u62fe\u4ed6\u3002\u201d \u3000\u3000\u80e1\u6590\u542c\u5230\u201c\u80e1\u5bb6\u5200\u6cd5\u7684\u8981\u65e8\u7aef\u5728\u62db\u6570\u7cbe\u5947\uff0c\u4e0d\u5728\u4ee5\u529b\u78b0\u529b\u201d\u8fd9\u4e24\u53e5\u8bdd\uff0c\u5fc3\u5ff5\u4e00\u52a8\uff0c\u6697\u9053\uff1a\u201c\u82d7\u5927\u4fa0\u8fd9\u4e24\u53e5\u8bdd\u4ee4\u6211\u8305\u585e\u987f\u5f00\uff0c\u8ddf\u654c\u4eba\u786c\u62da\uff0c\u90a3\u662f\u4ee5\u5df1\u4e4b\u77ed\uff0c\u653b\u654c\u4e4b\u957f\u3002\u201d\u53c8\u60f3\u8d77\u5f53\u5e74\u8d75\u534a\u5c71\u5728\u5546\u5bb6\u5821\u8bb2\u89e3\u6b66\u5b66\u7cbe\u4e49\uff0c\u6b63\u4e0e\u82d7\u4eba\u51e4\u7684\u8bf4\u6cd5\u4e0d\u8c0b\u800c\u5408\uff0c\u5fc3\u4e2d\u4e00\u559c\u4e4b\u4e0b\uff0c\u5927\u58f0\u9053\uff1a\u201c\u4e14\u6162\uff01\u82d7\u5927\u4fa0\u9002\u624d\u6240\u4f7f\u5200\u6cd5\u6211\u53ea\u8bd5\u4e86\u4e00\u62db\uff0c\u8fd8\u6709\u5341\u51e0\u62db\u672a\u8bd5\u3002\u201d\u8f6c\u8fc7\u5934\u6765\uff0c\u5411\u7530\u5f52\u519c\u9053\uff1a\u201c\u8fd9\u4e00\u62db\u2018\u7a7f\u624b\u85cf\u5200\u2019\uff0c\u4f60\u77e5\u9053\u5389\u5bb3\u4e86\u4e48\uff1f\u201d \u3000\u3000\u7530\u5f52\u519c\u559d\u9053\uff1a\u201c\u6d51\u5c0f\u5b50\uff0c\u8fd8\u4e0d\u7ed9\u6211\u6eda\u5f00\uff01\u201d \u3000\u3000\u80e1\u6590\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u4f60\u4e0d\u670d\u6c14\uff0c\u5f85\u6211\u628a\u80e1\u5bb6\u5200\u6cd5\u4e00\u4e00\u65bd\u5c55\uff0c\u82e5\u662f\u6211\u4f7f\u5f97\u4e0d\u5bf9\uff0c\u6253\u4f60\u4e0d\u8fc7\uff0c\u6211\u8ddf\u4f60\u78d5\u5934\u3002\u5018\u82e5\u4f60\u8f93\u4e86\u5462\uff1f\u201d \u3000\u3000\u7530\u5f52\u519c\u6ee1\u809a\u5b50\u6ca1\u597d\u6c14\uff0c\u559d\u9053\uff1a\u201c\u6211\u4e5f\u8ddf\u4f60\u78d5\u5934\uff01\u201d \u3000\u3000\u80e1\u6590\u7b11\u9053\uff1a\u201c\u90a3\u5012\u4e0d\u7528\uff01\u4f60\u82e5\u4e0d\u654c\u80e1\u5bb6\u5200\u6cd5\uff0c\u90a3\u5c31\u987b\u7acb\u65f6\u5c06\u953a\u6c0f\u4e09\u96c4\u653e\u4e86\u3002\u8fd9\u4e09\u4f4d\u6b66\u529f\u4fee\u4e3a\uff0c\u53ef\u6bd4\u4f60\u9ad8\u660e\u5f97\u592a\u591a\u3002\u82e5\u8bf4\u5355\u6253\u72ec\u6597\uff0c\u4f60\u51b3\u975e\u4e09\u4f4d\u953a\u5144\u654c\u624b\u3002\u5355\u51ed\u4eba\u591a\uff0c\u90a3\u7b97\u751a\u4e48\u82f1\u96c4\uff1f\u201d\u4ed6\u8fd9\u756a\u8bdd\u4e00\u5219\u6fc0\u6012\u5bf9\u65b9\uff0c\u4e8c\u5219\u4e5f\u662f\u66ff\u953a\u6c0f\u4e09\u96c4\u51fa\u6c14\u3002 \u3000\u3000\u4e09\u953a\u53cc\u624b\u88ab\u7f1a\uff0c\u542c\u4e86\u8fd9\u51e0\u53e5\u8bdd\uff0c\u5fc3\u4e2d\u751a\u662f\u611f\u6fc0\u3002 \u3000\u3000\u7530\u5f52\u519c\u884c\u4e8b\u672c\u6765\u6f47\u6d12\uff0c\u4f46\u7ed9\u80e1\u6590\u8fd9\u4e48\u4e00\u6fc0\uff0c\u7adf\u662f\u5927\u5927\u7684\u6c89\u4e0d\u4f4f\u6c14\uff0c\u5fc3\u60f3\uff1a\u201c\u4f60\u60f3\u8f93\u4e86\u7ed9\u6211\u78d5\u5934\uff1f\u6709\u8fd9\u4e48\u4fbf\u5b9c\u4e8b\uff01\u4eca\u65e5\u53eb\u4f60\u7684\u5c0f\u547d\u96be\u9003\u6211\u7684\u5251\u5e95\u3002\u201d\u5f53\u4e0b\u5de6\u8896\u4e00\u62c2\uff0c\u5de6\u624b\u634f\u4e2a\u5251\u8bc0\uff0c\u659c\u8d70\u4e09\u6b65\uff0c\u4ed6\u5fc3\u4e2d\u867d\u6012\uff0c\u5374\u4e0d\u83bd\u8fdb\uff0c\u4f7f\u7684\u7adf\u662f\u6b63\u89c4\u7684\u5929\u9f99\u95e8\u4e00\u5b57\u5251\u6cd5\u3002 \u3000\u3000\u4f17\u4eba\u89c1\u9996\u9886\u51fa\u624b\uff0c\u4e00\u9f50\u9000\u5f00\uff0c\u624b\u6267\u706b\u628a\u7684\u9ad8\u9ad8\u4e3e\u8d77\uff0c\u56f4\u6210\u4e00\u4e2a\u660e\u6643\u6643\u7684\u706b\u5708\u3002 \u3000\u3000\u80e1\u6590\u53eb\u9053\uff1a\u201c\u2018\u6000\u4e2d\u62b1\u6708\u2019\uff0c\u672c\u662f\u865a\u62db\uff0c\u4e0b\u4e00\u62db\u2018\u95ed\u95e8\u94c1\u6247\u2019\uff01\u201d\u53e3\u4e2d\u5406\u559d\uff0c\u5355\u5200\u4e00\u63a8\u4e00\u6a2a\uff0c\u6b63\u4e0e\u82d7\u4eba\u51e4\u9002\u624d\u6240\u4f7f\u7684\u4e00\u6a21\u4e00\u6837\u3002\u7530\u5f52\u519c\u8eab\u5b50\u4e00\u95ea\uff0c\u6a2a\u5251\u6025\u523a\u3002\u80e1\u6590\u53eb\u9053\uff1a\u201c\u82d7\u5927\u4fa0\uff0c\u4e0b\u4e00\u62db\u600e\u4e48\uff1f\u6211\u5bf9\u4ed8\u4e0d\u4e86\u5566\uff01\u201d \u3000\u3000\u82d7\u4eba\u51e4\u542c\u4ed6\u53eb\u51fa\u201c\u6000\u4e2d\u62b1\u6708\u201d\u4e0e\u201c\u95ed\u95e8\u94c1\u6247\u201d\u4e24\u62db\u7684\u540d\u5b57\uff0c\u4e5f\u4e0d\u600e\u4e48\u60ca\u5f02\uff0c\u56e0\u80e1\u5bb6\u5200\u6cd5\u7684\u62db\u6570\u5916\u8868\u4e0a\u770b\u53bb\uff0c\u548c\u6b66\u6797\u4e2d\u4e00\u822c\u5927\u8def\u5200\u6cd5\u5e76\u65e0\u591a\u5927\u4e0d\u540c\uff0c\u53ea\u662f\u53d8\u5316\u5947\u5999\uff0c\u653b\u5219\u53bb\u52bf\u51cc\u5389\uff0c\u5b88\u5219\u95e8\u6237\u4e25\u8c28\uff0c\u653b\u4e2d\u6709\u5b88\uff0c\u5b88\u4e2d\u6709\u653b\uff0c\u4ee4\u4eba\u83ab\u6d4b\u9ad8\u6df1\uff0c\u8fd9\u65f6\u542c\u80e1\u6590\u6025\u53eb\uff0c\u7709\u5934\u4e00\u76b1\uff0c\u53eb\u9053\uff1a\u201c\u6c99\u50e7\u62dc\u4f5b\u3002\u201d \u3000\u3000\u80e1\u6590\u4f9d\u8a00\u4e00\u5200\u5288\u53bb\u3002\u7530\u5f52\u519c\u957f\u5251\u659c\u523a\uff0c\u6765\u70b9\u80e1\u6590\u624b\u8155\u3002 \u3000\u3000\u82d7\u4eba\u51e4\u53eb\u9053\uff1a\u201c\u9e5e\u5b50\u7ffb\u8eab\uff01\u201d\u4ed6\u8bdd\u672a\u8bf4\u5b8c\uff0c\u80e1\u6590\u5df2\u4f7f\u201c\u9e5e\u5b50\u7ffb\u8eab\u201d\u780d\u53bb\u3002\u7530\u5f52\u519c\u5403\u4e86\u4e00\u60ca\uff0c\u6025\u5fd9\u9000\u5f00\u4e00\u6b65\uff0c\u55e4\u7684\u4e00\u58f0\uff0c\u957f\u888d\u888d\u89d2\u5df2\u88ab\u5200\u950b\u5272\u53bb\u4e00\u5757\u3002\u4ed6\u8138\u4e0a\u5fae\u5fae\u4e00\u7ea2\uff0c\u5237\u5237\u5237\u8fde\u523a\u4e09\u5251\uff0c\u8fc5\u6377\u65e0\u4f26\uff0c\u5fc3\u60f3\uff1a\u201c\u96be\u9053\u4f60\u82d7\u4eba\u51e4\u8fd8\u6765\u5f97\u53ca\u6307\u70b9\uff1f\u201d \u3000\u3000\u82d7\u4eba\u51e4\u4e00\u60ca\uff0c\u6697\u53eb\u8981\u7cdf\u3002\u5374\u542c\u80e1\u6590\u7b11\u9053\uff1a\u201c\u82d7\u5927\u4fa0\u6211\u5df2\u907f\u4e86\u4ed6\u4e09\u5251\uff0c\u600e\u5730\u53cd\u51fb\uff1f\u201d\u82d7\u4eba\u51e4\u987a\u53e3\u9053\uff1a\u201c\u5173\u5e73\u732e\u5370\uff01\u201d\u80e1\u6590\u9053\uff1a\u201c\u597d\uff01\u201d\u679c\u7136\u662f\u4e00\u5200\u201c\u5173\u5e73\u732e\u5370\u201d\uff01 \u3000\u3000\u8fd9\u4e00\u5200\u5288\u53bb\uff0c\u52bf\u631f\u52b2\u98ce\uff0c\u5a01\u529b\u4e0d\u5c0f\uff0c\u4f46\u82d7\u4eba\u51e4\u5148\u5df2\u53eb\u51fa\uff0c\u7530\u5f52\u519c\u662f\u6b66\u6797\u4e00\u5927\u5b97\u6d3e\u7684\u638c\u95e8\uff0c\u6240\u5b66\u65e2\u7cbe\uff0c\u4eba\u53c8\u673a\u7075\uff0c\u65e9\u5df2\u62a2\u5148\u907f\u5f00\u3002\u80e1\u6590\u8ddf\u7740\u4e00\u5200\u524a\u53bb\uff0c\u8fd9\u4e00\u62db\u662f\u201c\u591c\u53c9\u63a2\u6d77\u201d\u3002\u4ed6\u5200\u5230\u4e2d\u9014\uff0c\u82d7\u4eba\u51e4\u4e5f\u5df2\u53eb\u4e86\u51fa\u6765\uff1a\u201c\u591c\u53c9\u63a2\u6d77\uff01\u201d \u3000\u3000\u5341\u4f59\u62db\u4e00\u8fc7\uff0c\u7530\u5f52\u519c\u7adf\u88ab\u8feb\u5f97\u624b\u5fd9\u811a\u4e71\uff0c\u5168\u5904\u4e0b\u98ce\uff0c\u4e00\u77a5\u773c\u89c1\u65c1\u89c2\u4f17\u4eba\u5747\u6709\u60ca\u5f02\u4e4b\u8272\uff0c\u5f53\u4e0b\u5251\u6cd5\u4e00\u53d8\uff0c\u5feb\u51fb\u5feb\u523a\u3002\u80e1\u6590\u5c55\u5f00\u751f\u5e73\u6240\u5b66\uff0c\u4ee5\u5feb\u6253\u5feb\u3002\u82d7\u4eba\u51e4\u53e3\u4e2d\u8fd8\u5728\u547c\u559d\uff1a\u201c\u4e0a\u6b65\u62a2\u5200\uff0c\u4eae\u5200\u52bf\uff0c\u89c2\u97f3\u5750\u83b2\uff0c\u6d6a\u5b50\u56de\u5934\u2026\u2026\u201d\u4f17\u4eba\u53ea\u89c1\u80e1\u6590\u5200\u950b\u6240\u5411\uff0c\u7adf\u4e0e\u82d7\u4eba\u51e4\u53eb\u7684\u82e5\u5408\u7b26\u8282\uff0c\u65e0\u4e0d\u9a87\u7136\u3002 \u3000\u3000\u5176\u5b9e\u8fd9\u4e8b\u4e5f\u4e0d\u5e0c\u5947\u3002\u660e\u672b\u6e05\u521d\u4e4b\u65f6\uff0c\u80e1\u82d7\u8303\u7530\u56db\u5bb6\u6b66\u529f\u5747\u6709\u58f0\u4e8e\u4e16\u3002\u82d7\u4eba\u51e4\u4e3a\u4e00\u4ee3\u5927\u4fa0\uff0c\u4e13\u7cbe\u5251\u672f\uff0c\u5bf9\u5929\u9f99\u95e8\u5251\u672f\u719f\u77e5\u4e8e\u80f8\uff0c\u8fd9\u65f6\u7530\u80e1\u4e24\u4eba\u76f8\u6597\uff0c\u4ed6\u773c\u775b\u867d\u7136\u4e0d\u89c1\uff0c\u4e00\u542c\u98ce\u58f0\u5373\u80fd\u8fa8\u77e5\u4e8c\u4eba\u6240\u4f7f\u7684\u5927\u81f4\u662f\u4f55\u62db\u672f\u3002\u80e1\u6590\u51fa\u62db\u8fdb\u5200\uff0c\u5176\u5b9e\u662f\u4f9d\u636e\u81ea\u5df1\u751f\u5e73\u6240\u5b66\u5168\u529b\u65bd\u4e3a\uff0c\u82e5\u662f\u542c\u5230\u82d7\u4eba\u51e4\u6307\u70b9\u518d\u884c\u51fa\u529b\uff0c\u5728\u8fd9\u751f\u6b7b\u7cfb\u4e8e\u4e00\u53d1\u7684\u62da\u6597\u4e4b\u9645\uff0c\u54ea\u91cc\u8fd8\u6765\u5f97\u53ca\uff1f\u53ea\u662f\u4ed6\u548c\u82d7\u4eba\u51e4\u6240\u5b66\u7684\u80e1\u5bb6\u5200\u6cd5\u7cfb\u51fa\u540c\u6e90\uff0c\u5168\u65e0\u4e8c\u81f4\u3002\u82d7\u4eba\u51e4\u53e3\u4e2d\u547c\u559d\u548c\u4ed6\u624b\u4e0a\u65bd\u4e3a\uff0c\u521a\u597d\u914d\u5f97\u5929\u8863\u65e0\u7f1d\uff0c\u5012\u4f3c\u662f\u9884\u5148\u6392\u6f14\u7eaf\u719f\u3001\u5728\u4f17\u4eba\u4e4b\u524d\u8bd5\u6f14\u4e00\u822c\u3002 \u3000\u3000\u7530\u5f52\u519c\u6697\u60f3\uff1a\u201c\u83ab\u975e\u8fd9\u4eba\u662f\u82d7\u4eba\u51e4\u7684\u5f1f\u5b50\uff1f\u8981\u4e0d\u7136\u82d7\u4eba\u51e4\u773c\u775b\u672a\u778e\uff0c\u88c5\u6a21\u4f5c\u6837\u7684\u5305\u4e0a\u4e00\u5757\u767d\u5e03\uff0c\u5b9e\u5219\u77a7\u5f97\u6e05\u6e05\u695a\u695a\uff1f\u201d \u3000\u3000\u60f3\u5230\u6b64\u5904\uff0c\u4e0d\u7981\u751f\u4e86\u602f\u610f\u3002\u80e1\u6590\u7684\u5355\u5200\u5374\u8d8a\u4f7f\u8d8a\u5feb\u3002 \u3000\u3000\u8fd9\u65f6\u82d7\u4eba\u51e4\u518d\u4e5f\u65e0\u6cd5\u542c\u51fa\u4e24\u4eba\u7684\u62db\u6570\uff0c\u5df2\u7136\u4f4f\u53e3\u4e0d\u53eb\uff0c\u5fc3\u4e2d\u5374\u5728\u7422\u78e8\uff1a\u201c\u8fd9\u5c11\u5e74\u5200\u6cd5\u5982\u6b64\u7cbe\u5947\uff0c\u4e0d\u77e5\u662f\u54ea\u4e00\u4f4d\u9ad8\u624b\u7684\u95e8\u4e0b\uff1f\u201d \u3000\u3000\u82e5\u662f\u4ed6\u53cc\u76ee\u5f97\u89c1\uff0c\u770b\u5230\u80e1\u6590\u7684\u80e1\u5bb6\u5200\u6cd5\u4f7f\u5f97\u5982\u6b64\u7cbe\u7eaf\uff0c\u81ea\u65e9\u6599\u5230\u4ed6\u662f\u80e1\u4e00\u5200\u7684\u4f20\u4eba\u4e86\uff01 \u3000\u3000\u4f17\u4eba\u56f4\u7740\u7684\u5708\u5b50\u8d8a\u79bb\u8d8a\u5f00\uff0c\u90fd\u6015\u88ab\u5200\u950b\u5251\u5203\u78b0\u53ca\u3002 \u3000\u3000\u80e1\u6590\u4e00\u4e2a\u8f6c\u8eab\uff0c\u5374\u89c1\u7a0b\u7075\u7d20\u7ad9\u5728\u5708\u5b50\u4e4b\u5185\uff0c\u6ee1\u8138\u90fd\u662f\u5173\u6ce8\u4e4b\u60c5\uff0c\u4e0d\u77e5\u600e\u7684\uff0c\u7adf\u5728\u8fd9\u9163\u6597\u4e4b\u9645\uff0c\u8111\u6d77\u4e2d\u98d8\u8fc7\u4e86\u738b\u94c1\u5320\u5411\u4ed6\u6240\u5531\u7684\u56db\u53e5\u60c5\u6b4c\uff0c\u4e0d\u7981\u5411\u5979\u5fae\u5fae\u4e00\u7b11\uff0c\u7a81\u7136\u8f6c\u5934\u559d\u9053\uff1a\u201c\u2018\u6000\u4e2d\u62b1\u6708\u2019\uff0c\u672c\u662f\u865a\u62db\uff01\u201d \u3000\u3000\u8bdd\u58f0\u672a\u6bd5\uff0c\u5f53\u7684\u4e00\u58f0\uff0c\u7530\u5f52\u519c\u957f\u5251\u843d\u5730\uff0c\u624b\u81c2\u4e0a\u6ee1\u662f\u9c9c\u8840\uff0c\u8e09\u8dc4\u5012\u9000\uff0c\u8eab\u5b50\u6643\u4e86\u4e24\u6643\uff0c\u55b7\u51fa\u4e00\u53e3\u8840\u6765\u3002 \u3000\u3000\u539f\u6765\u201c\u6000\u4e2d\u62b1\u6708\u201d\uff0c\u672c\u662f\u865a\u62db\uff0c\u4e0b\u4e00\u62db\u662f\u201c\u95ed\u95e8\u94c1\u6247\u201d\u3002\u8fd9\u4e24\u62db\u4e00\u865a\u4e00\u5b9e\uff0c\u5f53\u665a\u82d7\u4eba\u51e4\u548c\u80e1\u6590\u5404\u5df2\u4f7f\u4e86\u4e00\u6b21\uff0c\u7530\u5f52\u519c\u81ea\u662f\u77a7\u5f97\u660e\u767d\uff0c\u6fc0\u6597\u4e2d\u731b\u542c\u5f97\u201c\u6000\u4e2d\u62b1\u6708\uff0c\u672c\u662f\u865a\u62db\u201d\u8fd9\u516b\u5b57\uff0c\u81ea\u7136\u800c\u7136\u7684\u9632\u4ed6\u4e0b\u4e00\u62db\u201c\u95ed\u95e8\u94c1\u6247\u201d\u3002\u54ea\u77e5\u9053\u80e1\u5bb6\u5200\u6cd5\u5999\u5728\u865a\u5b9e\u4e92\u7528\uff0c\u5ffd\u865a\u5ffd\u5b9e\uff0c\u8fd9\u4e00\u62db\u201c\u6000\u4e2d\u62b1\u6708\u201d\u5374\u7a81\u7136\u53d8\u4e3a\u5b9e\u62db\uff0c\u80e1\u6590\u5355\u5200\u56de\u62b1\uff0c\u4e00\u5200\u780d\u5728\u4ed6\u7684\u8155\u4e0a\uff0c\u8ddf\u7740\u5200\u4e2d\u5939\u638c\uff0c\u5728\u4ed6\u80f8\u53e3\u7ed3\u7ed3\u5b9e\u5b9e\u7684\u731b\u51fb\u4e00\u638c\u3002 \u3000\u3000\u80e1\u6590\u7b11\u9053\uff1a\u201c\u4f60\u600e\u5730\u5982\u6b64\u6027\u6025\uff0c\u4e0d\u542c\u6211\u8bf4\u5b8c\uff1f\u6211\u8bf4\u2018\u6000\u4e2d\u62b1\u6708\uff0c\u672c\u662f\u865a\u62db\uff0c\u53d8\u4e3a\u5b9e\u62db\uff0c\u53c8\u6709\u4f55\u59a8\uff1f\u2019\u4f60\u542c\u4e86\u4e0a\u534a\u622a\uff0c\u6ca1\u542c\u4e0b\u534a\u622a\uff01\u201d \u3000\u3000\u7530\u5f52\u519c\u80f8\u53e3\u7ffb\u817e\uff0c\u4f3c\u4e4e\u53c8\u8981\u6709\u5927\u53e3\u9c9c\u8840\u55b7\u51fa\uff0c\u77e5\u9053\u4eca\u65e5\u5df2\u4e00\u8d25\u6d82\u5730\uff0c\u53c8\u6015\u82d7\u4eba\u51e4\u773c\u775b\u5176\u5b9e\u672a\u778e\uff0c\u5f3a\u884c\u8fd0\u6c14\u5fcd\u4f4f\uff0c\u4e00\u6307\u953a\u6c0f\u4e09\u96c4\uff0c\u547d\u624b\u4e0b\u4eba\u89e3\u7f1a\uff0c\u968f\u5373\u5c06\u624b\u4e00\u6325\uff0c\u8f6c\u8fc7\u8eab\u53bb\uff0c\u5fcd\u4e0d\u4f4f\u53c8\u662f\u4e00\u53e3\u8840\u5410\u51fa\u3002 \u3000\u3000\u90a3\u653e\u9525\u7684\u5c0f\u59d1\u5a18\u7530\u9752\u6587\u662f\u7530\u5f52\u519c\u4e4b\u5973\uff0c\u662f\u4ed6\u524d\u59bb\u6240\u751f\uff0c\u5979\u89c1\u7236\u4eb2\u8eab\u53d7\u91cd\u4f24\uff0c\u6025\u5fd9\u62a2\u4e0a\u6276\u4f4f\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u7239\uff0c\u54b1\u4eec\u8d70\u5427\uff1f\u201d \u3000\u3000\u7530\u5f52\u519c\u70b9\u70b9\u5934\u3002 \u3000\u3000\u4f17\u4eba\u7fa4\u9f99\u65e0\u9996\uff0c\u4eba\u6570\u867d\u4f17\uff0c\u5374\u5df2\u5168\u65e0\u6597\u5fd7\u3002\u82d7\u4eba\u51e4\u6293\u8d77\u5c4b\u4e2d\u53d7\u4f24\u4e94\u4eba\uff0c\u4e00\u4e00\u63b7\u51fa\u3002\u4f17\u4eba\u4f38\u624b\u63a5\u4f4f\uff0c\u8f6c\u8eab\u4fbf\u8d70\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u53eb\u9053\uff1a\u201c\u5c0f\u59d1\u5a18\uff0c\u6697\u5668\u5e26\u56de\u5bb6\u53bb\uff01\u201d\u53f3\u624b\u4e00\u626c\uff0c\u94c1\u9525\u5411\u7530\u9752\u6587\u98de\u53bb\u3002 \u3000\u3000\u7530\u9752\u6587\u7adf\u4e0d\u56de\u5934\uff0c\u5de6\u624b\u5411\u540e\u4e00\u6284\u63a5\u4f4f\uff0c\u624b\u6cd5\u6781\u662f\u4f36\u4fd0\u3002\u54ea\u77e5\u9525\u752b\u5165\u624b\uff0c\u5979\u5168\u8eab\u4e00\u8df3\uff0c\u7acb\u5373\u5c06\u94c1\u9525\u629b\u5728\u5730\u4e0b\uff0c\u5de6\u624b\u8fde\u8fde\u6325\u52a8\uff0c\u4f3c\u4e4e\u90a3\u94c1\u9525\u6781\u5176\u70eb\u624b\u4e00\u822c\u3002 \u3000\u3000\u80e1\u6590\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8d64\u874e\u7c89\uff01\u201d\u7a0b\u7075\u7d20\u56de\u4ee5\u4e00\u7b11\uff0c\u5979\u679c\u7136\u662f\u5728\u94c1\u9525\u4e0a\u653e\u4e86\u8d64\u874e\u7c89\u3002 \u3000\u3000\u7247\u523b\u4e4b\u95f4\uff0c\u7530\u5f52\u519c\u4e00\u884c\u4eba\u53bb\u5f97\u5e72\u5e72\u51c0\u51c0\uff0c\u5c0f\u5c4b\u4e4b\u524d\u53c8\u662f\u6f06\u9ed1\u4e00\u56e2\u3002 \u3000\u3000\u953a\u5146\u82f1\u6717\u58f0\u9053\uff1a\u201c\u82d7\u5927\u4fa0\uff0c\u8d3c\u5b50\u4eca\u65e5\u8d25\u53bb\uff0c\u4e0d\u4f1a\u518d\u6765\u3002\u6211\u4e09\u5144\u5f1f\u7ef4\u62a4\u65e0\u529b\uff0c\u5927\u662f\u60ed\u6127\uff0c\u671b\u4f60\u53cc\u76ee\u65e9\u65e5\u75ca\u53ef\u3002\u201d\u53c8\u5411\u80e1\u6590\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u6211\u4e09\u953a\u4ea4\u4e86\u4f60\u8fd9\u4f4d\u670b\u53cb\uff0c\u4ed6\u65e5\u82e5\u6709\u5dee\u9063\uff0c\u613f\u5c3d\u6b7b\u529b\uff01\u201d\u4e09\u4eba\u4e00\u62b1\u62f3\uff0c\u5f84\u81ea\u5feb\u6b65\u53bb\u4e86\u3002 \u3000\u3000\u80e1\u6590\u77e5\u4ed6\u4e09\u4eba\u5931\u624b\u88ab\u64d2\uff0c\u8138\u4e0a\u65e0\u5149\uff0c\u5f53\u4e0b\u4e0d\u4fbf\u518d\u8bf4\u4ec0\u4e48\u3002 \u3000\u3000\u82d7\u4eba\u51e4\u5fc3\u4e2d\u6069\u6028\u5206\u660e\uff0c\u53e3\u5934\u5374\u4e0d\u559c\u591a\u8a00\uff0c\u53ea\u662f\u62f1\u624b\u8fd8\u793c\uff0c\u8033\u542c\u5f97\u7530\u5f52\u519c\u4e00\u884c\u4eba\u5317\u53bb\uff0c\u953a\u6c0f\u4e09\u96c4\u5374\u662f\u5357\u884c\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u4f60\u4e24\u4f4d\u6b66\u529f\u60ca\u4eba\uff0c\u53ef\u8ba9\u6211\u5927\u5f00\u773c\u754c\u4e86\u3002\u82d7\u5927\u4fa0\uff0c\u8bf7\u4f60\u56de\u8fdb\u5c4b\u53bb\uff0c\u6211\u77a7\u77a7\u4f60\u7684\u773c\u775b\u3002\u201d \u3000\u3000\u5f53\u4e0b\u4e09\u4eba\u56de\u8fdb\u5c4b\u4e2d\u3002\u80e1\u6590\u642c\u8d77\u5012\u7ffb\u4e86\u7684\u684c\u6905\uff0c\u70b9\u4eae\u6cb9\u706f\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u8f7b\u8f7b\u89e3\u5f00\u82d7\u4eba\u51e4\u773c\u4e0a\u7684\u5305\u5e03\uff0c\u624b\u6301\u70db\u53f0\uff0c\u7ec6\u7ec6\u5bdf\u770b\u3002 \u3000\u3000\u80e1\u6590\u4e0d\u53bb\u770b\u82d7\u4eba\u51e4\u7684\u4f24\u76ee\uff0c\u53ea\u662f\u671b\u7740\u7a0b\u7075\u7d20\u7684\u795e\u8272\uff0c\u8981\u4ece\u5979\u8138\u8272\u4e4b\u4e2d\uff0c\u770b\u51fa\u82d7\u4eba\u51e4\u7684\u4f24\u76ee\u662f\u5426\u6709\u6551\u3002\u4f46\u89c1\u7a0b\u7075\u7d20\u7684\u773c\u73e0\u6676\u83b9\u6e05\u6f88\uff0c\u72b9\u4f3c\u4e00\u6cd3\u6e05\u6c34\uff0c\u8138\u4e0a\u53ea\u9732\u51fa\u51dd\u601d\u4e4b\u610f\uff0c\u65e2\u65e0\u96be\u8272\uff0c\u4ea6\u65e0\u559c\u5bb9\uff0c\u76f4\u662f\u6559\u4eba\u731c\u5ea6\u4e0d\u900f\u3002 \u3000\u3000\u82d7\u4eba\u51e4\u548c\u80e1\u6590\u90fd\u662f\u6781\u6709\u80c6\u8bc6\u4e4b\u4eba\uff0c\u4f46\u5728\u8fd9\u4e00\u523b\u95f4\uff0c\u5fc3\u4e2d\u7684\u60f4\u60f4\u4e0d\u5b89\uff0c\u5c24\u751a\u4e8e\u8eab\u5904\u5f3a\u654c\u73af\u4f3a\u4e4b\u4e2d\u3002 \u3000\u3000\u8fc7\u4e86\u534a\u664c\uff0c\u7a0b\u7075\u7d20\u4ecd\u662f\u51dd\u89c6\u4e0d\u8bed\u3002\u82d7\u4eba\u51e4\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u6bd2\u836f\u836f\u6027\u5389\u5bb3\uff0c\u53c8\u9694\u4e86\u8fd9\u8bb8\u591a\u65f6\u523b\uff0c\u82e5\u662f\u96be\u6cbb\uff0c\u59d1\u5a18\u4f46\u8bf4\u4e0d\u59a8\u3002\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u8981\u6cbb\u5230\u4e0e\u5e38\u4eba\u4e00\u822c\uff0c\u5e76\u4e0d\u4e3a\u96be\uff0c\u53ea\u662f\u82d7\u5927\u4fa0\u5e76\u975e\u5e38\u4eba\u3002\u201d\u80e1\u6590\u5947\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u82d7\u5927\u4fa0\u4eba\u79f0\u2018\u6253\u904d\u5929\u4e0b\u65e0\u654c\u624b\u2019\uff0c\u6b66\u529f\u5982\u6b64\u7cbe\u5f3a\uff0c\u76ee\u529b\u81ea\u4ea6\u5f02\u4e4e\u5bfb\u5e38\uff0c\u518d\u8005\u5185\u529b\u65e2\u6df1\uff0c\u53cc\u76ee\u5fc5\u5f53\u70af\u70af\u6709\u795e\uff0c\u51db\u7136\u751f\u5a01\u3002\u5018\u82e5\u7ed9\u6211\u8fd9\u5eb8\u533b\u6cbb\u5f97\u5931\u4e86\u795e\u91c7\uff0c\u5c82\u4e0d\u53ef\u60dc\uff1f\u201d \u3000\u3000\u82d7\u4eba\u51e4\u54c8\u54c8\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u59d1\u5a18\u5410\u5c5e\u4e0d\u51e1\uff0c\u624b\u6bb5\u81ea\u662f\u6781\u9ad8\u7684\u4e86\u3002\u4f46\u4e0d\u77e5\u8ddf\u4e00\u55d4\u5927\u5e08\u600e\u751f\u79f0\u547c\uff1f\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u539f\u6765\u82d7\u5927\u4fa0\u8fd8\u662f\u5148\u5e08\u7684\u6545\u4eba\u2026\u2026\u201d\u82d7\u4eba\u51e4\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4e00\u55d4\u5927\u5e08\u4ea1\u6545\u4e86\u4e48\uff1f\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u82d7\u4eba\u51e4\u970d\u5730\u7ad9\u8d77\uff0c\u8bf4\u9053\uff1a\u201c\u5728\u4e0b\u6709\u8a00\u8981\u8ddf\u59d1\u5a18\u8bf4\u77e5\u3002\u201d \u3000\u3000\u80e1\u6590\u89c1\u4ed6\u795e\u8272\u6709\u5f02\uff0c\u5fc3\u4e2d\u5947\u602a\uff0c\u53c8\u60f3\uff1a\u201c\u7a0b\u59d1\u5a18\u7684\u5e08\u7236\u6bd2\u624b\u836f\u738b\u6cd5\u540d\u53eb\u505a\u201c\u65e0\u55d4\u2019\uff0c\u600e\u4e48\u82d7\u5927\u4fa0\u79f0\u4ed6\u4e3a\u2018\u4e00\u55d4\u2019\uff1f\u201d \u3000\u3000\u53ea\u542c\u82d7\u4eba\u51e4\u9053\uff1a\u201c\u5f53\u5e74\u5c0a\u5e08\u4e0e\u5728\u4e0b\u66fe\u6709\u5c0f\u5c0f\u8fc7\u8282\uff0c\u5728\u4e0b\u65e0\u793c\uff0c\u66fe\u635f\u4f24\u8fc7\u5c0a\u5e08\u3002\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u554a\uff0c\u5148\u5e08\u5de6\u624b\u5c11\u4e86\u4e24\u6839\u624b\u6307\uff0c\u90a3\u662f\u7ed9\u82d7\u5927\u4fa0\u7528\u5251\u524a\u53bb\u7684\uff1f\u201d\u82d7\u4eba\u51e4\u9053\uff1a\u201c\u4e0d\u9519\u3002\u867d\u7136\u8fd9\u756a\u8fc7\u8282\u5c0a\u5e08\u540e\u6765\u7acb\u5373\u4fbf\u62a5\u590d\u4e86\uff0c\u7b97\u662f\u626f\u4e86\u4e2a\u76f4\uff0c\u4e24\u4e0d\u5403\u4e8f\uff0c\u4f46\u524d\u665a\u8fd9\u4f4d\u5144\u5f1f\u8981\u53bb\u5411\u5c0a\u5e08\u6c42\u6551\u4e4b\u65f6\uff0c\u5728\u4e0b\u5374\u77e5\u662f\u81ea\u8ba8\u6ca1\u8da3\uff0c\u6789\u8d39\u5fc3\u673a\u3002\u4eca\u65e5\u59d1\u5a18\u6765\u6b64\uff0c\u5728\u4e0b\u8fd8\u9053\u662f\u5949\u4e86\u5c0a\u5e08\u4e4b\u547d\uff0c\u4ee5\u5fb7\u62a5\u6028\uff0c\u5b9e\u6240\u611f\u6fc0\u3002\u53ef\u662f\u5c0a\u5e08\u65e2\u5df2\u901d\u4e16\uff0c\u59d1\u5a18\u662f\u4e0d\u77e5\u8fd9\u6bb5\u65e7\u4e8b\u7684\u4e86\uff1f\u201d\u7a0b\u7075\u7d20\u6447\u5934\u9053\uff1a\u201c\u4e0d\u77e5\u3002\u201d \u3000\u3000\u82d7\u4eba\u51e4\u8f6c\u8eab\u8d70\u8fdb\u5185\u5ba4\uff0c\u6367\u51fa\u4e00\u53ea\u94c1\u76d2\uff0c\u4ea4\u7ed9\u7a0b\u7075\u7d20\uff0c\u9053\uff1a\u201c\u8fd9\u662f\u5c0a\u5e08\u9057\u7269\uff0c\u59d1\u5a18\u4e00\u770b\u4fbf\u77e5\u3002\u201d \u3000\u3000\u90a3\u94c1\u76d2\u7ea6\u83ab\u516b\u5bf8\u89c1\u65b9\uff0c\u751f\u6ee1\u94c1\u9508\uff0c\u5df2\u662f\u591a\u5e74\u65e7\u7269\u3002\u7a0b\u7075\u7d20\u6253\u5f00\u76d2\u76d6\uff0c\u53ea\u89c1\u76d2\u4e2d\u6709\u4e00\u6761\u5c0f\u86c7\u7684\u9aa8\u9abc\uff0c\u53e6\u6709\u4e00\u4e2a\u5c0f\u5c0f\u78c1\u74f6\uff0c\u74f6\u4e0a\u523b\u7740\u201c\u86c7\u836f\u201d\u4e24\u5b57\uff0c\u5979\u8ba4\u5f97\u8fd9\u79cd\u836f\u74f6\u662f\u5e08\u7236\u5e38\u7528\u4e4b\u7269\uff0c\u4f46\u4e0d\u77e5\u90a3\u5c0f\u86c7\u7684\u9aa8\u9abc\u662f\u4f55\u7528\u610f\u3002 \u3000\u3000\u82d7\u4eba\u51e4\u6de1\u6de1\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5c0a\u5e08\u548c\u6211\u8a00\u8bed\u5931\u548c\uff0c\u4e24\u4eba\u52a8\u8d77\u624b\u6765\u3002\u7b2c\u4e8c\u5929\u5c0a\u5e08\u547d\u4eba\u9001\u4e86\u8fd9\u53ea\u94c1\u76d2\u7ed9\u6211\uff0c\u4f20\u8a00\u9053\uff1a\u2018\u82e5\u6709\u80c6\u5b50\uff0c\u4fbf\u6253\u5f00\u76d2\u5b50\u77a7\u77a7\uff0c\u5426\u5219\u6295\u5165\u6c5f\u6cb3\u4e4b\u4e2d\u7b97\u4e86\u3002\u2019\u6211\u81ea\u662f\u4e0d\u53d7\u4ed6\u6fc0\uff0c\u4e00\u5f00\u76d2\u76d6\uff0c\u91cc\u9762\u8dc3\u51fa\u8fd9\u6761\u5c0f\u86c7\uff0c\u5728\u6211\u624b\u80cc\u4e0a\u54ac\u4e86\u4e00\u53e3\uff0c\u8fd9\u6761\u5c0f\u86c7\u5267\u6bd2\u65e0\u6bd4\uff0c\u6211\u534a\u6761\u624b\u81c2\u767b\u65f6\u53d1\u9ed1\u3002\u4f46\u5c0a\u5e08\u5728\u94c1\u76d2\u4e2d\u9644\u6709\u86c7\u836f\uff0c\u6211\u670d\u7528\u4e4b\u540e\uff0c\u6027\u547d\u662f\u65e0\u788d\u7684\uff0c\u8fd9\u4e00\u756a\u75db\u82e6\u5374\u4e5f\u96be\u5f53\u4e4b\u81f3\u3002\u201d\u8bf4\u7740\u54c8\u54c8\u5927\u7b11\u3002 \u3000\u3000\u80e1\u6590\u548c\u7a0b\u7075\u7d20\u76f8\u5bf9\u800c\u563b\uff0c\u5747\u60f3\u8fd9\u756a\u4e3e\u52a8\u539f\u662f\u6bd2\u624b\u836f\u738b\u7684\u62ff\u624b\u597d\u620f\u3002 \u3000\u3000\u82d7\u4eba\u51e4\u9053\uff1a\u201c\u54b1\u4eec\u8bdd\u5df2\u8bf4\u660e\uff0c\u59d3\u82d7\u7684\u4e0d\u80fd\u6697\u4e2d\u5360\u4eba\u4fbf\u5b9c\u3002 \u3000\u3000\u59d1\u5a18\u597d\u5fc3\u533b\u6211\uff0c\u6599\u60f3\u8d77\u6765\u51b3\u975e\u4e00\u55d4\u5927\u5e08\u672c\u610f\uff0c\u70e6\u52b3\u59d1\u5a18\u4e00\u756a\u8dcb\u6d89\uff0c\u5728\u4e0b\u5c31\u6b64\u8c22\u8fc7\u3002\u201d\u8bf4\u7740\u4e00\u63d6\uff0c\u7ad9\u8d77\u8eab\u6765\u8d70\u5230\u95e8\u8fb9\uff0c\u4fbf\u662f\u9001\u5ba2\u4e4b\u610f\u3002 \u3000\u3000\u80e1\u6590\u6697\u6697\u4f69\u670d\uff0c\u5fc3\u60f3\u82d7\u4eba\u51e4\u884c\u4e8b\u5927\u6709\u53e4\u4eba\u9057\u98ce\uff0c\u8c6a\u8fc8\u6177\u6168\uff0c\u4e0d\u6127\u201c\u5927\u4fa0\u201d\u4e24\u5b57\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u5374\u4e0d\u7ad9\u8d77\uff0c\u8bf4\u9053\uff1a\u201c\u82d7\u5927\u4fa0\uff0c\u6211\u5e08\u7236\u65e9\u5c31\u4e0d\u53eb\u2018\u4e00\u55d4\u2019\u4e86\u554a\u3002\u201d\u82d7\u4eba\u51e4\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u6211\u5e08\u7236\u51fa\u5bb6\u4e4b\u524d\uff0c\u813e\u6c14\u5f88\u662f\u66b4\u8e81\u3002\u4ed6\u51fa\u5bb6\u540e\u6cd5\u540d\u2018\u5927\u55d4\u2019\uff0c\u540e\u6765\u4fee\u6027\u517b\u5fc3\uff0c\u9887\u6709\u8fdb\u76ca\uff0c\u4e8e\u662f\u66f4\u540d\u2018\u4e00\u55d4\u2019\u3002 \u3000\u3000\u5018\u82e5\u82d7\u5927\u4fa0\u4e0e\u5148\u5e08\u52a8\u624b\u4e4b\u65f6\uff0c\u5148\u5e08\u4e0d\u53eb\u4e00\u55d4\uff0c\u4ecd\u662f\u53eb\u4f5c\u5927\u55d4\uff0c\u8fd9\u94c1\u76d2\u4e2d\u4fbf\u53ea\u6709\u6bd2\u86c7\u800c\u65e0\u89e3\u836f\u4e86\u3002\u201d\u82d7\u4eba\u51e4\u201c\u554a\u201d\u7684\u4e00\u58f0\uff0c\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u4ed6\u8001\u4eba\u5bb6\u6536\u6211\u505a\u5f92\u513f\u7684\u65f6\u5019\uff0c\u6cd5\u540d\u53eb\u4f5c\u2018\u5fae\u55d4\u2019\u3002\u4e09\u5e74\u4e4b\u524d\uff0c\u4ed6\u8001\u4eba\u5bb6\u6539\u4f5c\u4e86\u2018\u65e0\u55d4\u2019\u3002\u82d7\u5927\u4fa0\uff0c\u4f60\u53ef\u628a\u6211\u5e08\u7236\u592a\u5c0f\u770b\u4e86\u3002\u201d\u82d7\u4eba\u51e4\u53c8\u662f\u201c\u554a\u201d\u7684\u4e00\u58f0\u3002\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u4ed6\u8001\u4eba\u5bb6\u6492\u624b\u897f\u5f52\u4e4b\u65f6\uff0c\u65e9\u5df2\u5927\u5f7b\u5927\u609f\uff0c\u65e0\u55d4\u65e0\u559c\uff0c\u54ea\u91cc\u8fd8\u4f1a\u628a\u4f60\u8fd9\u756a\u5c0f\u5c0f\u65e7\u6028\u8bb0\u5728\u5fc3\u4e0a\uff1f\u201d \u3000\u3000\u82d7\u4eba\u51e4\u4f38\u624b\u5728\u5927\u817f\u4e0a\u4e00\u62cd\uff0c\u8bf4\u9053\uff1a\u201c\u554a\u5440\uff01\u6211\u786e\u662f\u628a\u8fd9\u4f4d\u6545\u4eba\u77a7\u5f97\u5c0f\u4e86\u3002\u4e00\u522b\u5341\u4f59\u5e74\uff0c\u4eba\u5bb6\u5c82\u80fd\u5982\u4f60\u82d7\u4eba\u51e4\u4e00\u822c\u4e1d\u6beb\u6ca1\u6709\u957f\u8fdb\uff1f\u59d1\u5a18\u4f60\u8d35\u59d3\uff1f\u201d \u3000\u3000\u7a0b\u7075\u7d20\u62bf\u5634\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u59d3\u7a0b\u3002\u201d\u4ece\u5305\u88b1\u4e2d\u53d6\u51fa\u4e00\u53ea\u6728\u76d2\uff0c\u6253\u5f00\u76d2\u76d6\uff0c\u62ff\u51fa\u4e00\u67c4\u5c0f\u5200\uff0c\u4e00\u679a\u91d1\u9488\uff0c\u8bf4\u9053\uff1a\u201c\u82d7\u5927\u4fa0\uff0c\u8bf7\u4f60\u653e\u677e\u5168\u8eab\u7a74\u9053\u3002\u201d\u82d7\u4eba\u51e4\u9053\uff1a\u201c\u662f\u4e86\uff01\u201d \u3000\u3000\u80e1\u6590\u89c1\u7a0b\u7075\u7d20\u62ff\u4e86\u5200\u9488\u8d70\u5230\u82d7\u4eba\u51e4\u8eab\u524d\uff0c\u5fc3\u4e2d\u7a81\u8d77\u4e00\u5ff5\uff1a \u3000\u3000\u201c\u82d7\u5927\u4fa0\u548c\u90a3\u6bd2\u624b\u836f\u738b\u6709\u4ec7\u3002\u6c5f\u6e56\u4e0a\u4eba\u5fc3\u96be\u6d4b\uff0c\u5018\u82e5\u4ed6\u4eec\u6b63\u662f\u5b89\u6392\u6076\u8ba1\uff0c\u7531\u7a0b\u59d1\u5a18\u501f\u6cbb\u4f24\u4e3a\u540d\uff0c\u5374\u4e0b\u6bd2\u624b\uff0c\u5c82\u4e0d\u662f\u6211\u80e1\u6590\u7b2c\u4e8c\u6b21\u53c8\u7ed9\u4eba\u501f\u4f5c\u4e86\u6740\u4eba\u4e4b\u5200\uff1f\u8fd9\u65f6\u82d7\u5927\u4fa0\u5168\u8eab\u7a74\u9053\u653e\u677e\uff0c\u53ea\u987b\u5728\u8981\u7a74\u4e2d\u8f7b\u8f7b\u4e00\u9488\uff0c\u5373\u80fd\u5236\u4ed6\u6b7b\u547d\u3002\u201d\u6b63\u81ea\u8e0c\u8e87\uff0c\u7a0b\u7075\u7d20\u56de\u8fc7\u5934\u6765\uff0c\u5c06\u5c0f\u5200\u4ea4\u4e86\u7ed9\u4ed6\uff0c\u9053\uff1a\u201c\u4f60\u7ed9\u6211\u62ff\u7740\u3002\u201d\u5ffd\u89c1\u4ed6\u8138\u8272\u6709\u5f02\uff0c\u5f53\u5373\u4f1a\u610f\uff0c\u7b11\u9053\uff1a\u201c\u82d7\u5927\u4fa0\u653e\u5fc3\uff0c\u4f60\u5374\u4e0d\u653e\u5fc3\u5417\uff1f\u201d\u80e1\u6590\u9053\uff1a\u201c\u5018\u82e5\u662f\u7ed9\u6211\u6cbb\u4f24\uff0c\u6211\u653e\u4e00\u767e\u4e8c\u5341\u4e2a\u5fc3\u3002\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u4f60\u8bf4\u6211\u662f\u597d\u4eba\u5462\uff0c\u8fd8\u662f\u574f\u4eba\uff1f\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u5355\u5200\u76f4\u5165\u7684\u95ee\u4e86\u51fa\u6765\uff0c\u80e1\u6590\u7edd\u65e0\u601d\u7d22\uff0c\u968f\u53e3\u7b54\u9053\uff1a\u201c\u4f60\u81ea\u7136\u662f\u597d\u4eba\u3002\u201d\u7a0b\u7075\u7d20\u5f88\u662f\u559c\u6b22\uff0c\u5411\u4ed6\u4e00\u7b11\u3002\u5979\u808c\u80a4\u9ec4\u7626\uff0c\u672c\u6765\u7b97\u4e0d\u5f97\u7f8e\u4e3d\uff0c\u4f46\u4e00\u7b11\u4e4b\u4e0b\uff0c\u795e\u91c7\u7115\u53d1\uff0c\u72b9\u5982\u6625\u82b1\u521d\u7efd\u3002\u80e1\u6590\u5fc3\u4e2d\u66f4\u65e0\u534a\u70b9\u7591\u8651\uff0c\u62a5\u4ee5\u4e00\u7b11\u3002\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u4f60\u771f\u7684\u76f8\u4fe1\u6211\u4e86\u5427\uff1f\u201d\u8bf4\u7740\u8138\u4e0a\u5fae\u5fae\u4e00\u7ea2\uff0c\u8f6c\u8fc7\u8138\u53bb\uff0c\u4e0d\u6562\u518d\u548c\u4ed6\u773c\u5149\u76f8\u5bf9\u3002 \u3000\u3000\u80e1\u6590\u66f2\u8d77\u624b\u6307\uff0c\u5728\u81ea\u5df1\u989d\u89d2\u4e0a\u8f7b\u8f7b\u6253\u4e86\u4e2a\u7206\u6817\uff0c\u7b11\u9053\uff1a\u201c\u6253\u4f60\u8fd9\u80e1\u6d82\u5c0f\u5b50\uff01\u201d\u5fc3\u4e2d\u5ffd\u7136\u4e00\u52a8\u3002\u201c\u5979\u95ee\uff1a\u2018\u4f60\u771f\u7684\u76f8\u4fe1\u6211\u4e86\u5427\uff1f\u2019\u4e3a\u4ec0\u4e48\u8981\u8138\u7ea2\uff1f\u201d\u738b\u94c1\u5320\u6240\u5531\u7684\u90a3\u51e0\u53e5\u60c5\u6b4c\uff0c\u7a81\u7136\u95f4\u5728\u5fc3\u5e95\u54cd\u8d77\uff1a\u201c\u5c0f\u59b9\u5b50\u5f85\u60c5\u90ce\u2014\u2014\u6069\u60c5\u6df1\uff0c\u4f60\u83ab\u8d1f\u4e86\u5c0f\u59b9\u5b50\u2014\u2014\u4e00\u6bb5\u60c5\u2026\u2026\u201d \u3000\u3000\u7a0b\u7075\u7d20\u63d0\u8d77\u91d1\u9488\uff0c\u5728\u82d7\u4eba\u51e4\u773c\u4e0a\u201c\u9633\u767d\u7a74\u201d\u3001\u773c\u65c1\u201c\u775b\u660e\u7a74\u201d\u3001\u773c\u4e0b\u201c\u627f\u6ce3\u7a74\u201d\u4e09\u5904\u7a74\u9053\u9010\u4e00\u523a\u8fc7\uff0c\u7528\u5c0f\u5200\u5728\u201c\u627f\u6ce3\u7a74\u201d\u4e0b\u5272\u5f00\u5c11\u4e9b\u76ae\u8089\uff0c\u53c8\u6362\u8fc7\u4e00\u679a\u91d1\u9488\uff0c\u523a\u5728\u7834\u5b54\u4e4b\u4e2d\uff0c\u5979\u5927\u62c7\u6307\u5728\u9488\u5c3e\u4e00\u63a7\u4e00\u653e\uff0c\u9488\u5c3e\u4e2d\u4fbf\u6d41\u51fa\u9ed1\u8840\u6765\u3002\u539f\u6765\u8fd9\u4e00\u679a\u91d1\u9488\u4e2d\u95f4\u662f\u7a7a\u7684\u3002\u773c\u89c1\u8840\u6d41\u4e0d\u6b62\uff0c\u9ed1\u8840\u53d8\u7d2b\uff0c\u7d2b\u8840\u53d8\u7ea2\u3002\u80e1\u6590\u867d\u662f\u5916\u884c\uff0c\u4e5f\u77e5\u6bd2\u6db2\u5df2\u7136\u53bb\u5c3d\uff0c\u6b22\u547c\u9053\uff1a\u201c\u597d\u5566\uff01\u201d \u3000\u3000\u7a0b\u7075\u7d20\u5728\u4e03\u5fc3\u6d77\u68e0\u4e0a\u91c7\u4e0b\u56db\u7247\u53f6\u5b50\uff0c\u6363\u5f97\u70c2\u4e86\uff0c\u6577\u5728\u82d7\u4eba\u51e4\u773c\u4e0a\u3002\u82d7\u4eba\u51e4\u8138\u4e0a\u808c\u8089\u5fae\u5fae\u4e00\u52a8\uff0c\u63a5\u7740\u8eab\u4e0b\u6905\u5b50\u683c\u7684\u4e00\u54cd\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u82d7\u5927\u4fa0\uff0c\u6211\u542c\u80e1\u5927\u54e5\u8bf4\uff0c\u4f60\u6709\u4e00\u4f4d\u5343\u91d1\uff0c\u957f\u5f97\u633a\u662f\u53ef\u7231\uff0c\u5979\u5728\u54ea\u91cc\u554a\uff1f\u201d\u82d7\u4eba\u51e4\u9053\uff1a\u201c\u8fd9\u91cc\u4e0d\u592a\u5e73\uff0c\u9001\u5230\u90bb\u820d\u5bb6\u53bb\u4e86\u3002\u201d\u7a0b\u7075\u7d20\u7528\u5e03\u6761\u7ed9\u4ed6\u7f1a\u5728\u773c\u4e0a\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u5566\uff01\u4e09\u5929\u4e4b\u540e\uff0c\u5f85\u5f97\u75bc\u75db\u8fc7\u53bb\uff0c\u9ebb\u75d2\u96be\u5f53\u4e4b\u65f6\uff0c\u63ed\u5f00\u5e03\u5e26\uff0c\u90a3\u4fbf\u6ca1\u4e8b\u4e86\u3002\u73b0\u4e0b\u8bf7\u8fdb\u53bb\u8eba\u7740\u6b47\u6b47\u3002\u80e1\u5927\u54e5\uff0c\u54b1\u4eec\u505a\u996d\u53bb\u3002\u201d \u3000\u3000\u82d7\u4eba\u51e4\u7ad9\u8d77\u8eab\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u6211\u95ee\u4f60\u4e00\u53e5\u8bdd\u3002\u8fbd\u4e1c\u5927\u4fa0\u80e1\u4e00\u5200\uff0c\u662f\u4f60\u7684\u4f2f\u7236\u5462\u8fd8\u662f\u53d4\u7236\uff1f\u201d\u8981\u77e5\u80e1\u6590\u4ee5\u80e1\u5bb6\u5200\u6cd5\u51fb\u8d25\u7530\u5f52\u519c\uff0c\u82d7\u4eba\u51e4\u867d\u672a\u4eb2\u7779\uff0c\u4f46\u542c\u5f97\u51fa\u4ed6\u5200\u6cd5\u4e0a\u7684\u9020\u8be3\u5927\u975e\u5bfb\u5e38\uff0c\u82e5\u4e0d\u662f\u80e1\u4e00\u5200\u7684\u5ae1\u4f20\uff0c\u51b3\u4e0d\u80fd\u6709\u6b64\u529f\u592b\u3002\u4ed6\u77e5\u80e1\u4e00\u5200\u53ea\u751f\u4e00\u5b50\uff0c\u800c\u90a3\u513f\u5b50\u65e9\u5df2\u7ed9\u4eba\u6740\u6b7b\uff0c\u629b\u5165\u6cb3\u4e2d\uff0c\u56e0\u6b64\u731c\u60f3\u80e1\u6590\u5fc5\u662f\u80e1\u4e00\u5200\u7684\u4f84\u5b50\u3002 \u3000\u3000\u80e1\u6590\u6da9\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u4f4d\u8fbd\u4e1c\u5927\u4fa0\u4e0d\u662f\u6211\u7684\u4f2f\u7236\uff0c\u4e5f\u4e0d\u662f\u6211\u53d4\u7236\u3002\u201d\u82d7\u4eba\u51e4\u751a\u662f\u5947\u602a\uff0c\u5fc3\u60f3\u80e1\u5bb6\u5200\u6cd5\u7d20\u6765\u4e0d\u4f20\u5916\u4eba\uff0c\u4f55\u51b5\u8fd9\u5c11\u5e74\u786e\u53c8\u59d3\u80e1\uff0c\u53c8\u95ee\u9053\uff1a\u201c\u90a3\u4f4d\u80e1\u4e00\u5200\u80e1\u5927\u4fa0\uff0c\u4f60\u53eb\u4ed6\u4f5c\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u80e1\u6590\u5fc3\u4e2d\u96be\u8fc7\uff0c\u53ea\u56e0\u4e0d\u77e5\u82d7\u4eba\u51e4\u548c\u81ea\u5df1\u7236\u4eb2\u7a76\u7adf\u6709\u751a\u5173\u8054\uff0c\u4e0d\u613f\u4fbf\u6b64\u81ea\u627f\u8eab\u5206\uff0c\u9053\uff1a\u201c\u80e1\u5927\u4fa0\uff1f\u4ed6\u65e9\u901d\u4e16\u591a\u5e74\u4e86\uff0c\u6211\u90a3\u6709\u798f\u4efd\u6765\u53eb\u4ed6\u4ec0\u4e48\uff1f\u201d\u5fc3\u4e2d\u5728\u60f3\uff1a\u201c\u6211\u8fd9\u4e00\u751f\u82e5\u6709\u798f\u4efd\u53eb\u4e00\u58f0\u7239\u7239\u5988\u5988\uff0c\u80fd\u5f97\u4ed6\u4eec\u4eb2\u53e3\u7b54\u5e94\u4e00\u58f0\uff0c\u8fd9\u4e16\u4e0a\u6211\u8fd8\u5e0c\u6c42\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u82d7\u4eba\u51e4\u5fc3\u4e2d\u7eb3\u7f55\uff0c\u5446\u7acb\u7247\u523b\uff0c\u5fae\u5fae\u6447\u5934\uff0c\u56de\u8fdb\u5367\u5ba4\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u89c1\u80e1\u6590\u8138\u6709\u9eef\u7136\u4e4b\u8272\uff0c\u8981\u9017\u4ed6\u9ad8\u5174\uff0c\u8bf4\u9053\uff1a\u201c\u80e1\u5927\u54e5\uff0c\u4f60\u7d2f\u4e86\u534a\u5929\uff0c\u5750\u4e00\u5ffd\u513f\u5427\uff01\u201d\u80e1\u6590\u6447\u5934\u9053\uff1a\u201c\u6211\u4e0d\u7d2f\u3002\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u4f60\u5750\u4e0b\uff0c\u6211\u6709\u8bdd\u8ddf\u4f60\u8bf4\u3002\u201d\u80e1\u6590\u4f9d\u8a00\u5750\u4e0b\uff0c\u7a81\u89c9\u81c0\u4e0b\u4e00\u865a\uff0c\u5580\u7684\u4e00\u54cd\uff0c\u6905\u5b50\u788e\u5f97\u56db\u5206\u4e94\u88c2\u3002\u7a0b\u7075\u7d20\u62cd\u624b\u7b11\u9053\uff1a\u201c\u4e94\u767e\u65a4\u7684\u5927\u726f\u725b\u4e5f\u6ca1\u4f60\u91cd\u3002\u201d \u3000\u3000\u80e1\u6590\u4e0b\u76d8\u529f\u592b\u6781\u7a33\uff0c\u867d\u7136\u5750\u4e86\u4e2a\u7a7a\uff0c\u4f46\u53cc\u817f\u7acb\u65f6\u62ff\u6869\uff0c\u5e76\u6ca1\u6454\u5012\uff0c\u5fc3\u4e2d\u89c9\u5f97\u5947\u602a\u3002\u7a0b\u7075\u7d20\u7b11\u9053\uff1a\u201c\u90a3\u4e03\u5fc3\u6d77\u68e0\u7684\u53f6\u5b50\u6577\u5728\u8089\u4e0a\uff0c\u75db\u4e8e\u5200\u5272\u5341\u500d\uff0c\u82e5\u662f\u4f60\u554a\uff0c\u53ea\u6015\u53eb\u51fa\u6211\u7684\u5988\u6765\u5566\u3002\u201d \u3000\u3000\u80e1\u6590\u4e00\u7b11\uff0c\u8fd9\u624d\u4f1a\u610f\uff0c\u539f\u6765\u9002\u624d\u82d7\u4eba\u51e4\u5fcd\u75db\uff0c\u867d\u662f\u4e0d\u52a8\u58f0\u8272\uff0c\u4f46\u4e00\u80a1\u5185\u52b2\uff0c\u65e9\u628a\u6905\u5b50\u5750\u5f97\u8106\u70c2\u4e86\u3002 \u3000\u3000\u4e24\u4eba\u716e\u4e86\u4e00\u5927\u956c\u996d\uff0c\u7092\u4e86\u4e09\u76d8\u83dc\uff0c\u8bf7\u82d7\u4eba\u51e4\u51fa\u6765\u540c\u5403\u3002\u82d7\u4eba\u51e4\u9053\uff1a\u201c\u80fd\u559d\u9152\u5417\uff1f\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u80fd\u559d\uff0c\u4ec0\u4e48\u90fd\u4e0d\u7528\u5fcc\u3002\u201d\u82d7\u4eba\u51e4\u62ff\u51fa\u4e09\u74f6\u767d\u5e72\u6765\uff0c\u6bcf\u4eba\u9762\u524d\u653e\u4e86\u4e00\u74f6\uff0c\u9053\uff1a\u201c\u5927\u5bb6\u81ea\u5df1\u5012\u9152\u559d\uff0c\u4e0d\u7528\u5ba2\u6c14\u3002\u201d\u8bf4\u7740\u5728\u7897\u4e2d\u5012\u4e86\u534a\u7897\uff0c\u4ef0\u8116\u5b50\u4e00\u996e\u800c\u5c3d\u3002 \u3000\u3000\u80e1\u6590\u662f\u4e2a\u597d\u9152\u4e4b\u4eba\uff0c\u966a\u4ed6\u559d\u4e86\u534a\u7897\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u4e0d\u559d\uff0c\u5374\u628a\u534a\u74f6\u767d\u5e72\u5012\u5728\u79cd\u4e03\u5fc3\u6d77\u68e0\u7684\u9676\u76c6\u4e2d\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u82b1\u5f97\u7528\u9152\u6d47\uff0c\u4e00\u6d47\u6c34\u4fbf\u6b7b\u3002\u6211\u5728\u79cd\u918d\u9190\u9999\u65f6\u609f\u5230\u4e86\u8fd9\u4e2a\u9053\u7406\u3002\u5e08\u5144\u5e08\u59ca\u4ed6\u4eec\u4e0d\u61c2\uff0c\u4e00\u76f4\u5fd9\u4e86\u5341\u591a\u5e74\uff0c\u59cb\u7ec8\u79cd\u4e0d\u6d3b\u3002\u201d \u3000\u3000\u5269\u4e0b\u7684\u534a\u74f6\u5206\u7ed9\u82d7\u80e1\u4e8c\u4eba\u5012\u5728\u7897\u4e2d\uff0c\u81ea\u5df1\u5403\u996d\u76f8\u966a\u3002 \u3000\u3000\u82d7\u4eba\u51e4\u53c8\u559d\u4e86\u534a\u7897\u9152\uff0c\u610f\u5174\u751a\u8c6a\uff0c\u95ee\u9053\uff1a\u201c\u80e1\u5144\u5f1f\uff0c\u4f60\u7684\u5200\u6cd5\u662f\u8c01\u6559\u7684\uff1f\u201d\u80e1\u6590\u7b54\u9053\uff1a\u201c\u6ca1\u4eba\u6559\uff0c\u662f\u7167\u7740\u4e00\u672c\u5200\u8c31\u4e0a\u7684\u56fe\u6837\u548c\u89e3\u8bf4\u5b66\u7684\u3002\u201d\u82d7\u4eba\u51e4\u201c\u55ef\u201d\u4e86\u4e00\u58f0\u3002\u80e1\u6590\u9053\uff1a\u201c\u540e\u6765\u9047\u5230\u7ea2\u82b1\u4f1a\u7684\u8d75\u4e09\u5f53\u5bb6\uff0c\u4f20\u4e86\u6211\u51e0\u6761\u592a\u6781\u62f3\u7684\u8981\u8bc0\u3002\u201d\u82d7\u4eba\u51e4\u4e00\u62cd\u5927\u817f\uff0c\u53eb\u9053\uff1a\u201c\u662f\u5343\u81c2\u5982\u6765\u8d75\u534a\u5c71\u8d75\u4e09\u5f53\u5bb6\u4e86\uff1f\u201d\u80e1\u6590\u9053\uff1a\u201c\u6b63\u662f\u3002\u201d\u82d7\u4eba\u51e4\u9053\uff1a\u201c\u602a\u4e0d\u5f97\uff0c\u602a\u4e0d\u5f97\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u82d7\u4eba\u51e4\u9053\uff1a\u201c\u4e45\u6155\u7ea2\u82b1\u4f1a\u9648\u603b\u8235\u4e3b\u8c6a\u6770\u4ed7\u4e49\uff0c\u8bf8\u4f4d\u5f53\u5bb6\u82f1\u96c4\u4e86\u5f97\uff0c\u53ea\u53ef\u60dc\u8c79\u9690\u56de\u7586\uff0c\u82d7\u67d0\u65e0\u7f18\u89c1\u5f97\uff0c\u5b9e\u662f\u751f\u5e73\u61be\u4e8b\u3002\u201d\u80e1\u6590\u542c\u4ed6\u8bed\u610f\u4e4b\u4e2d\u5bf9\u8d75\u534a\u5c71\u6781\u662f\u63a8\u91cd\uff0c\u5fc3\u4e0b\u4e5f\u611f\u559c\u6b22\u3002 \u3000\u3000\u82d7\u4eba\u51e4\u5c06\u4e00\u74f6\u9152\u5012\u5e72\uff0c\u4e3e\u7897\u996e\u4e86\uff0c\u970d\u5730\u7ad9\u8d77\uff0c\u6478\u5230\u653e\u5728\u8336\u51e0\u4e0a\u7684\u5355\u5200\uff0c\u8bf4\u9053\uff1a\u201c\u80e1\u5144\u5f1f\uff0c\u6614\u5e74\u6211\u9047\u5230\u80e1\u4e00\u5200\u5927\u4fa0\uff0c\u4ed6\u4f20\u4e86\u6211\u4e00\u624b\u80e1\u5bb6\u5200\u6cd5\u3002\u4eca\u65e5\u6211\u7528\u4ee5\u6740\u9000\u5f3a\u654c\uff0c\u4f60\u7528\u4ee5\u6253\u8d25\u7530\u5f52\u519c\uff0c\u4fbf\u662f\u8fd9\u8def\u5200\u6cd5\u4e86\u3002\u563f\u563f\uff0c\u771f\u662f\u597d\u5200\u6cd5\u554a\uff0c\u597d\u5200\u6cd5\uff01\u201d\u84e6\u5730\u91cc\u4ef0\u5929\u957f\u5578\uff0c\u8dc3\u51fa\u6237\u5916\uff0c\u63d0\u5200\u4e00\u7acb\uff0c\u5c06\u90a3\u4e00\u8def\u80e1\u5bb6\u5200\u6cd5\u65bd\u5c55\u5f00\u6765\u3002 \u3000\u3000\u53ea\u89c1\u4ed6\u6b65\u6cd5\u51dd\u7a33\uff0c\u5200\u950b\u56de\u8f6c\uff0c\u6216\u95f2\u96c5\u8212\u5f90\uff0c\u6216\u521a\u731b\u8fc5\u6377\uff0c\u4e00\u62db\u4e00\u5f0f\uff0c\u4ff1\u662f\u52bf\u631f\u52b2\u98ce\u3002\u80e1\u6590\u51dd\u795e\u89c2\u770b\uff0c\u89c1\u4ed6\u6240\u4f7f\u62db\u6570\uff0c\u679c\u4e0e\u5200\u8c31\u4e0a\u6240\u8bb0\u4e00\u822c\u65e0\u5f02\uff0c\u53ea\u662f\u5200\u52bf\u8f83\u4e3a\u6536\u655b\uff0c\u800c\u6bd4\u81ea\u5df1\u6240\u4f7f\uff0c\u4e5f\u7f13\u6162\u5f97\u591a\u3002\u80e1\u6590\u53ea\u9053\u4ed6\u662f\u4e3a\u4e86\u8ba9\u81ea\u5df1\u770b\u5f97\u6e05\u695a\uff0c\u6545\u610f\u653e\u6162\u3002 \u3000\u3000\u82d7\u4eba\u51e4\u4e00\u8def\u5200\u6cd5\u4f7f\u5b8c\uff0c\u6a2a\u5200\u800c\u7acb\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4ee5\u4f60\u5200\u6cd5\u4e0a\u7684\u9020\u8be3\uff0c\u80dc\u90a3\u7530\u5f52\u519c\u662f\u7ef0\u7ef0\u6709\u4f59\uff0c\u4f46\u7b49\u6211\u773c\u775b\u597d\u4e86\uff0c\u4f60\u8981\u548c\u6211\u6253\u6210\u5e73\u624b\uff0c\u5374\u5c1a\u6709\u4e0d\u53ca\u3002\u201d \u3000\u3000\u80e1\u6590\u9053\uff1a\u201c\u8fd9\u4e2a\u81ea\u7136\u3002\u665a\u8f88\u600e\u662f\u82d7\u5927\u4fa0\u7684\u654c\u624b\uff1f\u201d\u82d7\u4eba\u51e4\u6447\u5934\u9053\uff1a\u201c\u8fd9\u8bdd\u9519\u4e86\u3002\u5f53\u5e74\u80e1\u5927\u4fa0\u4ee5\u8fd9\u8def\u5200\u6cd5\uff0c\u548c\u6211\u6574\u6574\u6597\u4e86\u4e94\u5929\uff0c\u59cb\u7ec8\u4e0d\u5206\u4e0a\u4e0b\u3002\u4ed6\u4f7f\u5200\u4e4b\u65f6\uff0c\u53ef\u6bd4\u4f60\u7f13\u6162\u5f97\u591a\uff0c\u6536\u655b\u5f97\u591a\u3002\u201d\u80e1\u6590\u4e00\u6014\uff0c\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\uff1f\u201d\u82d7\u4eba\u51e4\u9053\uff1a\u201c\u662f\u554a\uff0c\u4e0e\u5176\u4ee5\u4e3b\u6b3a\u5ba2\uff0c\u4e0d\u5982\u4ee5\u5ba2\u72af\u4e3b\u3002\u5ae9\u80dc\u4e8e\u8001\uff0c\u8fdf\u80dc\u4e8e\u6025\u3002\u7f20\u3001\u6ed1\u3001\u7ede\u3001\u64e6\u3001\u62bd\u3001\u622a\uff0c\u5f3a\u4e8e\u5c55\u3001\u62b9\u3001\u94a9\u3001\u5241\u3001\u780d\u3001\u5288\u3002\u201d \u3000\u3000\u539f\u6765\u4ee5\u4e3b\u6b3a\u5ba2\uff0c\u4ee5\u5ba2\u72af\u4e3b\uff0c\u5747\u662f\u4f7f\u5200\u4e4b\u52bf\uff0c\u4ee5\u5200\u5c16\u5f00\u7838\u654c\u5668\u4e3a\u201c\u5ae9\u201d\uff0c\u4ee5\u8fd1\u67c4\u5904\u5200\u5203\u5f00\u7838\u654c\u5668\u4e3a\u201c\u8001\u201d\uff1b\u78d5\u6258\u7a0d\u6162\u4e3a\u201c\u8fdf\u201d\uff0c\u4ee5\u5200\u5148\u8fce\u4e3a\u201c\u6025\u201d\uff0c\u81f3\u4e8e\u7f20\u3001\u6ed1\u3001\u7ede\u3001\u64e6\u7b49\u7b49\uff0c\u4e5f\u90fd\u662f\u4f7f\u5200\u7684\u8bf8\u822c\u6cd5\u95e8\u3002 \u3000\u3000\u82d7\u4eba\u51e4\u6536\u5200\u8fd8\u5165\uff0c\u62ff\u8d77\u7b77\u5b50\uff0c\u6252\u4e86\u4e24\u53e3\u996d\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u6162\u6162\u609f\u5230\u6b64\u7406\uff0c\u4ed6\u65e5\u5fc5\u53ef\u79f0\u96c4\u6b66\u6797\uff0c\u7eb5\u6a2a\u6c5f\u6e56\u3002\u201d \u3000\u3000\u80e1\u6590\u201c\u55ef\u201d\u4e86\u4e00\u58f0\uff0c\u4e3e\u7740\u7b77\u5b50\u6b32\u631f\u4e0d\u631f\uff0c\u5fc3\u4e2d\u601d\u91cf\u7740\u4ed6\u90a3\u51e0\u53e5\u8bdd\uff0c\u7b77\u5b50\u505c\u5728\u534a\u7a7a\u3002\u7a0b\u7075\u7d20\u7528\u7b77\u5b50\u5728\u4ed6\u7b77\u5b50\u4e0a\u8f7b\u8f7b\u4e00\u6572\uff0c\u7b11\u9053\uff1a\u201c\u996d\u4e5f\u4e0d\u5403\u4e86\u5417\uff1f\u201d\u80e1\u6590\u6b63\u81ea\u7422\u78e8\u5200\u8bc0\uff0c\u5168\u8eab\u7684\u52b2\u529b\u4e0d\u77e5\u4e0d\u89c9\u90fd\u8d2f\u6ce8\u53f3\u81c2\u4e4b\u4e0a\u3002\u7a0b\u7075\u7d20\u7684\u7b77\u5b50\u6572\u4e86\u8fc7\u6765\uff0c\u4ed6\u7b77\u5b50\u4e0a\u81ea\u7136\u800c\u7136\u7684\u751f\u51fa\u4e00\u80a1\u53cd\u9707\u4e4b\u529b\uff0c\u55d2\u7684\u4e00\u58f0\u8f7b\u54cd\uff0c\u7a0b\u7075\u7d20\u7684\u4e00\u53cc\u7b77\u5b50\u7adf\u5c14\u9707\u4e3a\u56db\u622a\u3002\u5979\u201c\u554a\u201d\u7684\u4e00\u58f0\u8f7b\u547c\uff0c\u7b11\u9053\uff1a\u201c\u663e\u672c\u4e8b\u4e48\uff1f\u201d \u3000\u3000\u80e1\u6590\u5fd9\u8d54\u7b11\u9053\uff1a\u201c\u5bf9\u4e0d\u8d77\uff0c\u6211\u60f3\u7740\u82d7\u5927\u4fa0\u90a3\u756a\u8bdd\uff0c\u4e0d\u7981\u51fa\u4e86\u795e\u3002\u201d\u968f\u624b\u5c06\u624b\u4e2d\u7b77\u5b50\u9012\u4e86\u7ed9\u5979\u3002\u7a0b\u7075\u7d20\u63a5\u8fc7\u6765\u4fbf\u5403\uff0c\u80e1\u6590\u5374\u5583\u5583\u5ff5\u7740\uff1a\u201c\u5ae9\u80dc\u4e8e\u8001\uff0c\u8fdf\u80dc\u4e8e\u6025\uff0c\u4e0e\u5176\u4ee5\u4e3b\u6b3a\u5ba2\u2026\u2026\u201d\u4e00\u62ac\u5934\uff0c\u89c1\u5979\u6b63\u7528\u81ea\u5df1\u4f7f\u8fc7\u7684\u7b77\u5b50\u5403\u996d\uff0c\u7adf\u662f\u4e1d\u6beb\u4e0d\u4ee5\u4e3a\u8bb8\uff0c\u4e0d\u7531\u5f97\u8138\u4e0a\u4e00\u7ea2\uff0c\u6b32\u5f85\u62ff\u6765\u4ee3\u5979\u62ed\u62b9\u5e72\u51c0\uff0c\u4e3a\u65f6\u5df2\u8fdf\uff0c\u8981\u9053\u6b49\u51e0\u53e5\u5427\uff0c\u5374\u53c8\u592a\u7740\u5f62\u8ff9\uff0c\u4e8e\u662f\u5230\u53a8\u623f\u53bb\u53e6\u884c\u53d6\u4e86\u4e00\u53cc\u7b77\u5b50\u3002 \u3000\u3000\u4ed6\u6252\u4e86\u51e0\u53e3\u996d\uff0c\u4f38\u7b77\u5230\u90a3\u76d8\u7092\u767d\u83dc\u4e2d\u53bb\u631f\u83dc\uff0c\u82d7\u4eba\u51e4\u7684\u7b77\u5b50\u4e5f\u521a\u597d\u4f38\u51fa\uff0c\u8f7b\u8f7b\u4e00\u62e8\uff0c\u5c06\u4ed6\u7684\u7b77\u5b50\u6321\u4e86\u5f00\u53bb\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u2018\u622a\u2019\u5b57\u8bc0\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u4e0d\u9519\uff01\u201d\u4e3e\u7b77\u53c8\u4e0a\uff0c\u4f46\u82d7\u4eba\u51e4\u7684\u4e00\u53cc\u7b77\u5b50\u5b88\u5f97\u4e25\u5bc6\u5f02\u5e38\uff0c\u4e0d\u8bba\u4ed6\u5982\u4f55\u9ad8\u62a2\u4f4e\u62e8\uff0c\u59cb\u7ec8\u4f38\u4e0d\u8fdb\u76d8\u5b50\u4e4b\u4e2d\u3002 \u3000\u3000\u80e1\u6590\u5fc3\u60f3\uff1a\u201c\u52a8\u5200\u5b50\u62da\u6597\u4e4b\u65f6\uff0c\u4ed6\u773c\u775b\u867d\u7136\u4e0d\u80fd\u89c6\u7269\uff0c\u4f46\u53ef\u542c\u98ce\u8fa8\u5668\uff0c\u4ece\u5175\u5203\u5288\u98ce\u7684\u58f0\u97f3\u4e4b\u4e2d\uff0c\u8fa8\u660e\u4e86\u654c\u62db\u7684\u6765\u8def\u3002\u8fd9\u65f6\u6211\u4e00\u53cc\u5c0f\u5c0f\u7684\u7b77\u5b50\uff0c\u4f38\u51fa\u53bb\u53c8\u65e0\u98ce\u58f0\uff0c\u4ed6\u5982\u4f55\u80fd\u591f\u5bdf\u89c9\uff1f\u201d \u3000\u3000\u4e24\u4eba\u8fdb\u9000\u9080\u51fb\uff0c\u53c8\u62c6\u4e86\u6570\u62db\uff0c\u80e1\u6590\u7a81\u7136\u9886\u609f\uff0c\u539f\u6765\u82d7\u4eba\u51e4\u8fd9\u65f6\u6240\u4f7f\u62db\u6570\uff0c\u5168\u662f\u7528\u7684\u201c\u540e\u53d1\u5236\u4eba\u201d\u4e4b\u672f\uff0c\u8981\u5f85\u53cc\u65b9\u7b77\u5b50\u76f8\u4ea4\uff0c\u4ed6\u624d\u968f\u673a\u5e94\u53d8\uff0c\u8fd9\u6b63\u662f\u6240\u8c13\u201c\u4ee5\u5ba2\u72af\u4e3b\u201d\u3001\u201c\u8fdf\u80dc\u4e8e\u6025\u201d\u7b49\u7b49\u7684\u9053\u7406\u3002 \u3000\u3000\u80e1\u6590\u4e00\u660e\u6b64\u7406\uff0c\u4e0d\u518d\u4f38\u7b77\u62a2\u83dc\uff0c\u5374\u5c06\u7b77\u5b50\u9ad8\u4e3e\u534a\u7a7a\uff0c\u8fdf\u8fdf\u4e0d\u843d\uff0c\u53cc\u773c\u51dd\u89c6\u7740\u82d7\u4eba\u51e4\u7684\u7b77\u5b50\uff0c\u81ea\u5df1\u7684\u7b77\u5b50\u4e00\u5bf8\u4e00\u5bf8\u7684\u6162\u6162\u79fb\u843d\uff0c\u7ec8\u4e8e\u78b0\u5230\u4e86\u767d\u83dc\u3002\u90a3\u65f6\u7684\u624b\u6cd5\u53ef\u5c31\u5feb\u6377\u65e0\u4f26\uff0c\u4e00\u631f\u7f29\u56de\uff0c\u9001\u5230\u4e86\u5634\u91cc\u3002\u82d7\u4eba\u51e4\u77a7\u4e0d\u89c1\u4ed6\u7b77\u5b50\u7684\u8d77\u843d\uff0c\u81ea\u662f\u4e0d\u80fd\u62e6\u622a\uff0c\u5c06\u53cc\u7b77\u5f80\u684c\u4e0a\u4e00\u63b7\uff0c\u54c8\u54c8\u5927\u7b11\u3002 \u3000\u3000\u80e1\u6590\u81ea\u8fd9\u53e3\u767d\u83dc\u4e00\u5403\uff0c\u624d\u771f\u6b63\u8e0f\u5165\u4e86\u7b2c\u4e00\u6d41\u9ad8\u624b\u7684\u5883\u754c\uff0c\u56de\u60f3\u9002\u624d\u82b1\u4e86\u8fd9\u8bb8\u591a\u529b\u6c14\u624d\u80dc\u5f97\u7530\u5f52\u519c\uff0c\u970e\u65f6\u4e4b\u95f4\u53c8\u662f\u559c\u6b22\uff0c\u53c8\u662f\u60ed\u6127\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u89c1\u4ed6\u7ec8\u4e8e\u62a2\u5230\u767d\u83dc\uff0c\u7b11\u541f\u541f\u7684\u671b\u7740\u4ed6\uff0c\u5fc3\u4e0b\u4e5f\u5341\u5206\u4ee3\u4ed6\u9ad8\u5174\u3002 \u3000\u3000\u82d7\u4eba\u51e4\u9053\uff1a\u201c\u80e1\u5bb6\u5200\u6cd5\u4eca\u65e5\u7ec8\u4e8e\u6709\u4e86\u4f20\u4eba\uff0c\u5509\uff0c\u80e1\u5927\u54e5\u554a\u80e1\u5927\u54e5\uff01\u201d\u8bf4\u5230\u8fd9\u91cc\uff0c\u8bed\u97f3\u751a\u662f\u82cd\u51c9\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u77a7\u51fa\u4ed6\u4e0e\u80e1\u6590\u4e4b\u95f4\uff0c\u4f3c\u6709\u4ec0\u4e48\u96be\u89e3\u7684\u7ea0\u845b\uff0c\u4e0d\u613f\u4ed6\u591a\u63d0\u6b64\u4e8b\uff0c\u4e8e\u662f\u95ee\u9053\uff1a\u201c\u82d7\u5927\u4fa0\uff0c\u4f60\u548c\u5148\u5e08\u5f53\u5e74\u4e3a\u4e86\u4ec0\u4e48\u4e8b\u60c5\u7ed3\u4ec7\uff0c\u80fd\u8bf4\u7ed9\u6211\u4eec\u542c\u542c\u5417\uff1f\u201d \u3000\u3000\u82d7\u4eba\u51e4\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u8fd9\u4e00\u4ef6\u4e8b\u6211\u5230\u4eca\u65e5\u8fd8\u662f\u4e0d\u80fd\u660e\u767d\u3002\u5341\u516b\u5e74\u524d\uff0c\u6211\u8bef\u4f24\u4e86\u4e00\u4f4d\u597d\u670b\u53cb\uff0c\u53ea\u56e0\u5175\u5203\u4e0a\u5582\u6709\u5267\u6bd2\uff0c\u89c1\u8840\u5c01\u5589\uff0c\u7adf\u5c14\u65e0\u6cd5\u633d\u6551\u3002\u6211\u60f3\u8fd9\u6bd2\u836f\u5982\u6b64\u5389\u5bb3\uff0c\u591a\u534a\u4e0e\u5c0a\u5e08\u6709\u5173\uff0c\u56e0\u6b64\u53bb\u5411\u5c0a\u5e08\u8be2\u95ee\u3002\u5c0a\u5e08\u4e00\u53e3\u5426\u8ba4\uff0c\u8bf4\u9053\u6beb\u4e0d\u77e5\u60c5\uff0c\u60f3\u662f\u6211\u4e00\u6765\u4e0d\u4f1a\u8bf4\u8bdd\uff0c\u4e8c\u6765\u5fc3\u60c5\u751a\u6076\uff0c\u4e0d\u514d\u5f97\u7f6a\u4e86\u5c0a\u5e08\uff0c\u4e24\u4eba\u8fd9\u624d\u52a8\u624b\u3002\u201d \u3000\u3000\u80e1\u6590\u4e00\u8a00\u4e0d\u53d1\uff0c\u542c\u4ed6\u8bf4\u5b8c\uff0c\u9694\u4e86\u534a\u664c\uff0c\u624d\u95ee\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u8fd9\u4f4d\u597d\u670b\u53cb\u662f\u4f60\u4eb2\u624b\u6740\u6b7b\u7684\u4e86\uff1f\u201d\u82d7\u4eba\u51e4\u9053\uff1a\u201c\u6b63\u662f\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u90a3\u4eba\u7684\u592b\u4eba\u5462\uff1f\u4f60\u65a9\u8349\u9664\u6839\uff0c\u4e00\u8d77\u6740\u4e86\uff1f\u201d \u3000\u3000\u7a0b\u7075\u7d20\u89c1\u4ed6\u624b\u6309\u5200\u67c4\uff0c\u8138\u8272\u94c1\u9752\uff0c\u773c\u89c1\u4e00\u4e2a\u676f\u9152\u8a00\u6b22\u7684\u5c40\u9762\uff0c\u8f6c\u773c\u95f4\u4fbf\u8981\u8f6c\u4e3a\u4e00\u573a\u8165\u98ce\u8840\u96e8\u3002\u5979\u5168\u4e0d\u77e5\u8c01\u662f\u8c01\u975e\uff0c\u4f46\u5fc3\u4e2d\u7edd\u65e0\u534a\u70b9\u7591\u95ee\uff1a\u201c\u5982\u679c\u4ed6\u4e8c\u4eba\u52a8\u624b\u780d\u6740\uff0c\u6211\u5f97\u7acb\u65f6\u52a9\u4ed6\u3002\u201d \u3000\u3000\u8fd9\u4e2a\u201c\u4ed6\u201d\u5230\u5e95\u662f\u8c01\uff0c\u5979\u5fc3\u4e2d\u81ea\u662f\u6e05\u6e05\u695a\u695a\u7684\u3002 \u3000\u3000\u82d7\u4eba\u51e4\u8bed\u97f3\u751a\u662f\u82e6\u6da9\uff0c\u7f13\u7f13\u7684\u9053\uff1a\u201c\u4ed6\u592b\u4eba\u5f53\u573a\u81ea\u520e\u6b89\u592b\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u90a3\u6761\u547d\u4e5f\u662f\u4f60\u5bb3\u7684\u4e86\uff1f\u201d\u82d7\u4eba\u51e4\u51c4\u7136\u9053\uff1a\u201c\u6b63\u662f\uff01\u201d \u3000\u3000\u80e1\u6590\u7ad9\u8d77\u8eab\u6765\uff0c\u68ee\u7136\u9053\uff1a\u201c\u8fd9\u4f4d\u597d\u670b\u53cb\u59d3\u751a\u540d\u8c01\uff1f\u201d\u82d7\u4eba\u51e4\u9053\uff1a\u201c\u4f60\u771f\u8981\u77e5\u9053\uff1f\u201d\u80e1\u6590\u9053\uff1a\u201c\u6211\u8981\u77e5\u9053\u3002\u201d\u82d7\u4eba\u51e4\u9053\uff1a\u201c\u597d\uff0c\u4f60\u8ddf\u6211\u6765\uff01\u201d\u5927\u8e0f\u6b65\u8d70\u8fdb\u540e\u5802\u3002\u80e1\u6590\u968f\u540e\u8ddf\u53bb\u3002\u7a0b\u7075\u7d20\u7d27\u8ddf\u5728\u80e1\u6590\u4e4b\u540e\u3002 \u3000\u3000\u53ea\u89c1\u82d7\u4eba\u51e4\u63a8\u5f00\u53a2\u623f\u623f\u95e8\uff0c\u623f\u5185\u5c45\u4e2d\u4e00\u5f20\u767d\u6728\u684c\u5b50\uff0c\u684c\u4e0a\u653e\u7740\u4e24\u5757\u7075\u724c\uff0c\u4e00\u5757\u5199\u7740\u201c\u4e49\u5144\u8fbd\u4e1c\u5927\u4fa0\u80e1\u516c\u4e00\u5200\u4e4b\u7075\u4f4d\u201d\uff0c\u53e6\u4e00\u5757\u5199\u7740\u201c\u4e49\u5ac2\u80e1\u592b\u4eba\u4e4b\u7075\u4f4d\u201d\u3002 \u3000\u3000\u80e1\u6590\u671b\u7740\u8fd9\u4e24\u4f4d\u7075\u724c\uff0c\u624b\u8db3\u51b0\u51b7\uff0c\u5168\u8eab\u53d1\u98a4\u3002\u4ed6\u65e9\u5c31\u7591\u5fc3\u7236\u6bcd\u4e4b\u4e27\uff0c\u5fc5\u4e0e\u82d7\u4eba\u51e4\u6709\u91cd\u5927\u5173\u8054\uff0c\u4f46\u89c1\u4ed6\u4e3a\u4eba\u6177\u6168\u8c6a\u4fa0\uff0c\u4e00\u76f4\u76fc\u671b\u81ea\u5df1\u662f\u7591\u5fc3\u9519\u4e86\u3002\u4f46\u6b64\u523b\u4ed6\u76f4\u8ba4\u4e0d\u8bb3\uff0c\u53ef\u662f\u4ed6\u65e2\u8bf4\u201c\u6211\u8bef\u4f24\u4e86\u4e00\u4f4d\u597d\u670b\u53cb\u201d\uff0c\u795e\u8272\u8bed\u6c14\u4e4b\u95f4\uff0c\u53c8\u662f\u542b\u7740\u65e0\u9650\u9690\u75db\uff0c\u4e00\u970e\u65f6\u95f4\uff0c\u4e0d\u77e5\u8be5\u5f53\u5982\u4f55\u624d\u597d\u3002 \u3000\u3000\u82d7\u4eba\u51e4\u8f6c\u8fc7\u8eab\u6765\uff0c\u53cc\u624b\u8d1f\u5728\u80cc\u540e\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u65e2\u4e0d\u80af\u8bf4\u548c\u80e1\u5927\u4fa0\u6709\u4f55\u5e72\u8fde\uff0c\u6211\u4e5f\u4e0d\u5fc5\u8ffd\u95ee\u3002\u5c0f\u5144\u5f1f\uff0c\u4f60\u7b54\u5e94\u8fc7\u7167\u987e\u6211\u5973\u513f\u7684\uff0c\u8fd9\u8bdd\u53ef\u8981\u8bb0\u5f97\u3002\u597d\u5427\uff0c\u4f60\u8981\u66ff\u80e1\u5927\u4fa0\u62a5\u4ec7\uff0c\u4fbf\u53ef\u52a8\u624b\uff01\u201d \u3000\u3000\u80e1\u6590\u4e3e\u8d77\u5355\u5200\uff0c\u505c\u5728\u534a\u7a7a\uff0c\u5fc3\u60f3\uff1a\u201c\u6211\u53ea\u8981\u7528\u4ed6\u9002\u624d\u6559\u6211\u2018\u4ee5\u5ba2\u72af\u4e3b\u2019\u4e4b\u8bc0\uff0c\u7f13\u7f13\u843d\u5200\uff0c\u4ed6\u51b3\u8ba1\u8eb2\u95ea\u4e0d\u4e86\uff0c\u90a3\u4fbf\u62a5\u4e86\u6740\u7236\u6740\u6bcd\u7684\u5927\u4ec7\uff01\u201d \u3000\u3000\u7136\u89c1\u4ed6\u8138\u8272\u5e73\u548c\uff0c\u65e2\u65e0\u4f24\u5fc3\u4e4b\u8272\uff0c\u4ea6\u65e0\u60e7\u6015\u4e4b\u610f\uff0c\u8fd9\u4e00\u5200\u5982\u4f55\u780d\u5f97\u4e0b\u53bb\uff1f\u7a81\u7136\u95f4\u5927\u53eb\u4e00\u58f0\uff0c\u8f6c\u8eab\u4fbf\u8d70\u3002\u7a0b\u7075\u7d20\u8ffd\u4e86\u51fa\u6765\uff0c\u6367\u8d77\u90a3\u76c6\u4e03\u5fc3\u6d77\u68e0\uff0c\u53d6\u4e86\u968f\u8eab\u5305\u88b1\uff0c\u968f\u540e\u8d76\u53bb\u3002 \u3000\u3000\u80e1\u6590\u4e00\u53e3\u6c14\u72c2\u5954\u4e86\u5341\u6765\u91cc\u8def\uff0c\u7a81\u7136\u6251\u7ffb\u5728\u5730\uff0c\u75db\u54ed\u8d77\u6765\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u843d\u540e\u751a\u8fdc\uff0c\u9694\u4e86\u826f\u4e45\uff0c\u8fd9\u624d\u5954\u5230\uff0c\u89c1\u5230\u4ed6\u60b2\u4f24\u4e4b\u60c5\uff0c\u77e5\u9053\u6b64\u65f6\u65e0\u53ef\u529d\u6170\uff0c\u4e8e\u662f\u9ed8\u9ed8\u5750\u5728\u4ed6\u7684\u8eab\u65c1\uff0c\u4e14\u8ba9\u4ed6\u7eb5\u58f0\u4e00\u54ed\uff0c\u53d1\u6cc4\u5fc3\u5934\u7684\u60b2\u4f24\u3002 \u3000\u3000\u80e1\u6590\u76f4\u54ed\u5230\u773c\u6cea\u5e72\u4e86\uff0c\u8fd9\u624d\u6b62\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u7075\u59d1\u5a18\uff0c\u4ed6\u6740\u6b7b\u7684\u4fbf\u662f\u6211\u7684\u7239\u7239\u5988\u5988\uff0c\u6b64\u4ec7\u4e0d\u5171\u6234\u5929\u3002\u201d \u3000\u3000\u7a0b\u7075\u7d20\u5446\u4e86\u534a\u664c\uff0c\u9053\uff1a\u201c\u90a3\u54b1\u4eec\u7ed9\u4ed6\u6cbb\u773c\uff0c\u8fd9\u4e8b\u53ef\u9519\u4e86\u3002\u201d \u3000\u3000\u80e1\u6590\u9053\uff1a\u201c\u6cbb\u4ed6\u773c\u775b\uff0c\u4e00\u70b9\u4e5f\u4e0d\u9519\u3002\u5f85\u4ed6\u53cc\u773c\u597d\u4e86\uff0c\u6211\u518d\u53bb\u627e\u4ed6\u62a5\u4ec7\u3002\u201d\u4ed6\u987f\u4e86\u4e00\u987f\uff0c\u9053\uff1a\u201c\u53ea\u662f\u4ed6\u6b66\u529f\u8fdc\u80dc\u4e8e\u6211\uff0c\u975e\u5f97\u5148\u628a\u6b66\u827a\u7ec3\u597d\u4e86\u4e0d\u53ef\u3002\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u4ed6\u65e2\u7528\u5582\u6bd2\u7684\u5175\u5203\u4f24\u4f60\u7239\u7239\uff0c\u54b1\u4eec\u4e5f\u53ef\u4e00\u62a5\u8fd8\u4e00\u62a5\u3002\u201d \u3000\u3000\u80e1\u6590\u89c9\u5f97\u5979\u5168\u5fc3\u5168\u610f\u7684\u62a4\u7740\u81ea\u5df1\uff0c\u5fc3\u4e2d\u597d\u751f\u611f\u6fc0\uff0c\u4f46\u60f3\u5230\u5979\u8981\u4ee5\u5389\u5bb3\u6bd2\u836f\u53bb\u5bf9\u4ed8\u82d7\u4eba\u51e4\uff0c\u8bf4\u4e5f\u5947\u602a\uff0c\u53cd\u800c\u4e0d\u81ea\u7981\u7684\u51db\u7136\u611f\u5230\u60e7\u610f\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u53c8\u60f3\uff1a\u201c\u8fd9\u4f4d\u7075\u59d1\u5a18\u806a\u660e\u624d\u667a\uff0c\u80dc\u6211\u5341\u500d\uff0c\u6b66\u529f\u4e5f\u81ea\u4e0d\u5f31\uff0c\u4f46\u6574\u65e5\u548c\u6bd2\u7269\u4e3a\u4f0d\uff0c\u603b\u662f\u2026\u2026\u201d\u4ed6\u81ea\u5df1\u4e5f\u4e0d\u77e5\u201c\u603b\u662f\u2026\u2026\u201d\u751a\u4e48\uff0c\u5fc3\u5e95\u53ea\u9690\u9690\u7684\u89c9\u5f97\u4e0d\u59a5\u3002 \u3000"}),showOnHomepage:!0},{name:"\u7b2c\u5341\u4e8c\u7ae0\u3000\u53e4\u602a\u7684\u76d7\u515a",content:(0,a.jsx)(a.Fragment,{children:"\u4ed6\u5927\u54ed\u4e00\u573a\u4e4b\u540e\uff0c\u80f8\u95f4\u90c1\u95f7\u53d1\u6cc4\u4e86\u4e0d\u5c11\uff0c\u773c\u89c1\u5929\u5df2\u9ece\u660e\uff0c\u6b63\u53ef\u8d76\u8def\uff0c\u521a\u8981\u7ad9\u8d77\u8eab\u6765\uff0c\u7a81\u7136\u53eb\u4e86\u58f0\u201c\u554a\u54df\uff01\u201d \u3000\u3000\u539f\u6765\u4ed6\u5fc3\u795e\u6fc0\u8361\uff0c\u4ece\u82d7\u4eba\u51e4\u5bb6\u4e2d\u6025\u51b2\u800c\u51fa\uff0c\u7adf\u5c06\u968f\u8eab\u7684\u5305\u88b1\u7559\u4e0b\u4e86\uff0c\u5018\u518d\u56de\u5934\u53bb\u53d6\uff0c\u6b64\u65f6\u5b9e\u4e0d\u613f\u548c\u82d7\u4eba\u51e4\u4f1a\u9762\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u5e7d\u5e7d\u7684\u9053\uff1a\u201c\u522b\u7684\u90fd\u6ca1\u4ec0\u4e48\uff0c\u5c31\u662f\u90a3\u53ea\u7389\u51e4\u51f0\u4e22\u4e0d\u5f97\u3002\u201d\u80e1\u6590\u7ed9\u5979\u8bf4\u4e2d\u5fc3\u4e8b\uff0c\u8138\u4e0a\u4e00\u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u5728\u8fd9\u513f\u7a0d\u7b49\uff0c\u6211\u8d76\u56de\u53bb\u62ff\u5305\u88b1\uff0c\u5426\u5219\u8fde\u4eca\u665a\u5403\u996d\u4f4f\u5e97\u7684\u94f6\u5b50\u4e5f\u6ca1\u6709\u4e86\u3002\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u6211\u6709\u94f6\u5b50\uff0c\u8fde\u91d1\u5b50\u4e5f\u6709\u3002\u201d\u8bf4\u7740\u4ece\u6000\u4e2d\u53d6\u51fa\u4e24\u5c0f\u952d\u9ec4\u91d1\u6765\u3002\u80e1\u6590\u9053\uff1a\u201c\u6700\u8981\u7d27\u7684\u662f\u6211\u5bb6\u4f20\u7684\u62f3\u7ecf\u5200\u8c31\uff0c\u51b3\u8ba1\u4e22\u4e0d\u5f97\u3002\u201d\u7a0b\u7075\u7d20\u4f38\u624b\u5165\u6000\uff0c\u53d6\u51fa\u4ed6\u90a3\u672c\u62f3\u7ecf\u5200\u8c31\u6765\uff0c\u6de1\u6de1\u7684\u9053\uff1a\u201c\u53ef\u662f\u8fd9\u672c\uff1f\u201d \u3000\u3000\u80e1\u6590\u53c8\u60ca\u53c8\u559c\uff0c\u9053\uff1a\u201c\u4f60\u771f\u7ec6\u5fc3\uff0c\u4ec0\u4e48\u90fd\u5e2e\u6211\u7167\u6599\u7740\u4e86\u3002\u201d \u3000\u3000\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u5c31\u53ef\u60dc\u90a3\u53ea\u7389\u51e4\u7ed9\u6211\u5728\u8def\u4e0a\u4e22\u4e86\uff0c\u5f53\u771f\u8fc7\u610f\u4e0d\u53bb\u3002\u201d\u80e1\u6590\u89c1\u5979\u8138\u8272\u90d1\u91cd\uff0c\u4e0d\u50cf\u662f\u8bf4\u7b11\uff0c\u5fc3\u4e2d\u4e00\u6025\uff0c\u9053\uff1a\u201c\u6211\u56de\u5934\u627e\u627e\u53bb\uff0c\u8bf4\u4e0d\u5b9a\u8fd8\u80fd\u627e\u5230\u3002\u201d\u8bf4\u7740\u8f6c\u5934\u4fbf\u8d70\u3002\u7a0b\u7075\u7d20\u5ffd\u9053\uff1a\u201c\u54a6\uff0c\u8fd9\u91cc\u4eae\u6643\u6643\u7684\u662f\u4ec0\u4e48\u4e1c\u897f\uff1f\u201d\u4f38\u624b\u5230\u9752\u8349\u4e4b\u4e2d\uff0c\u62fe\u8d77\u4e00\u4ef6\u9970\u7269\uff0c\u83b9\u7136\u751f\u5149\uff0c\u6b63\u662f\u90a3\u53ea\u7389\u51e4\u3002 \u3000\u3000\u80e1\u6590\u5927\u559c\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u662f\u5973\u8bf8\u845b\uff0c\u5c0f\u5f20\u826f\uff0c\u5c0f\u53ef\u7518\u62dc\u4e0b\u51e4\u3002\u201d \u3000\u3000\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u89c1\u4e86\u8fd9\u7389\u51e4\uff0c\u77a7\u4f60\u559c\u6b22\u5f97\u4ec0\u4e48\u4f3c\u7684\u3002\u8fd8\u7ed9\u4f60\u5427\uff01\u201d \u3000\u3000\u4e8e\u662f\u5c06\u5200\u8c31\u548c\u7389\u51e4\u90fd\u8fd8\u4e86\u7ed9\u4ed6\uff0c\u8bf4\u9053\uff1a\u201c\u80e1\u5927\u54e5\uff0c\u54b1\u4eec\u540e\u4f1a\u6709\u671f\u3002\u201d \u3000\u3000\u80e1\u6590\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4f60\u751f\u6c14\u4e86\u4e48\uff1f\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u6211\u751f\u4ec0\u4e48\u6c14\uff1f\u201d \u3000\u3000\u4f46\u773c\u7736\u4e00\u7ea2\uff0c\u73e0\u6cea\u6b32\u6ef4\uff0c\u8f6c\u8fc7\u4e86\u5934\u53bb\u3002\u80e1\u6590\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u8981\u5230\u54ea\u91cc\u53bb\uff1f\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u600e\u4e48\u4e0d\u77e5\u9053\uff1f\u201d \u3000\u3000\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u6211\u6ca1\u7239\u6ca1\u5a18\uff0c\u5e08\u7236\u53c8\u6b7b\u4e86\uff0c\u53c8\u6ca1\u4eba\u9001\u4ec0\u4e48\u7389\u51e4\u51f0\u3001\u7389\u9e92\u9e9f\u7ed9\u6211\uff0c\u6211\u2026\u2026\u6211\u600e\u4e48\u77e5\u9053\u5230\u54ea\u91cc\u53bb\u3002\u201d\u8bf4\u5230\u8fd9\u91cc\uff0c\u6cea\u6c34\u7ec8\u4e8e\u6d41\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u80e1\u6590\u81ea\u548c\u5979\u76f8\u8bc6\u4ee5\u6765\uff0c\u89c1\u5979\u5fc3\u601d\u7ec6\u5bc6\uff0c\u5904\u5904\u5360\u4eba\u4e0a\u98ce\uff0c\u4efb\u4f55\u96be\u4e8b\u5230\u4e86\u624b\u4e0a\uff0c\u65e0\u4e0d\u8fce\u5203\u800c\u89e3\uff0c\u4f46\u8fd9\u65f6\u89c1\u5979\u6084\u7acb\u6653\u98ce\u4e4b\u4e2d\uff0c\u6b8b\u6708\u659c\u7167\uff0c\u602f\u751f\u751f\u7684\u80cc\u5f71\u5fae\u5fae\u8038\u52a8\uff0c\u5fc3\u4e2d\u4e0d\u7531\u5f97\u5927\u751f\u601c\u60dc\u4e4b\u5fc3\uff0c\u8bf4\u9053\uff1a\u201c\u7075\u59d1\u5a18\uff0c\u6211\u9001\u4f60\u4e00\u7a0b\u3002\u201d \u3000\u3000\u7a0b\u7075\u7d20\u80cc\u7740\u8eab\u5b50\uff0c\u62c9\u8863\u89d2\u62ed\u4e86\u62ed\u773c\u6cea\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u53c8\u4e0d\u5230\u54ea\u91cc\u53bb\uff0c\u4f60\u9001\u6211\u505a\u4ec0\u4e48\uff1f\u4f60\u8981\u6211\u533b\u6cbb\u82d7\u4eba\u51e4\u7684\u773c\u775b\uff0c\u6211\u5df2\u7ecf\u7ed9\u6cbb\u597d\u5566\u3002\u201d \u3000\u3000\u80e1\u6590\u8981\u9017\u5979\u9ad8\u5174\uff0c\u8bf4\u9053\uff1a\u201c\u53ef\u662f\u8fd8\u6709\u4e00\u4ef6\u4e8b\u6ca1\u505a\u3002\u201d\u7a0b\u7075\u7d20\u8f6c\u8fc7\u8eab\u6765\uff0c\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u201d\u80e1\u6590\u9053\uff1a\u201c\u6211\u6c42\u4f60\u533b\u6cbb\u82d7\u4eba\u51e4\uff0c\u4f60\u8bf4\u4e5f\u8981\u6c42\u6211\u4e00\u4ef6\u4e8b\u7684\u3002\u4ec0\u4e48\u4e8b\u554a\uff0c\u4f60\u8fd8\u6ca1\u8bf4\u5462\u3002\u201d \u3000\u3000\u7a0b\u7075\u7d20\u7a76\u662f\u4e2a\u5e74\u8f7b\u59d1\u5a18\uff0c\u7a81\u7136\u7834\u6d95\u4e3a\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u4e0d\u63d0\u8d77\uff0c\u6211\u5012\u5fd8\u4e86\uff0c\u8fd9\u53eb\u505a\u81ea\u4f5c\u5b7d\uff0c\u4e0d\u53ef\u6d3b\u3002\u597d\uff0c\u6211\u8981\u4f60\u5e72\u4ec0\u4e48\uff0c\u4f60\u90fd\u5f97\u7b54\u5e94\uff0c\u662f\u4e0d\u662f\uff1f\u201d\u80e1\u6590\u786e\u662f\u5fc3\u7518\u60c5\u613f\u7684\u4e3a\u5979\u65e0\u8bba\u505a\u4ec0\u4e48\u4e8b\uff0c\u6602\u7136\u9053\uff1a\u201c\u53ea\u8981\u6211\u529b\u6240\u80fd\u53ca\uff0c\u65e0\u4e0d\u4ece\u547d\u3002\u201d \u3000\u3000\u7a0b\u7075\u7d20\u4f38\u51fa\u624b\u6765\uff0c\u9053\uff1a\u201c\u597d\uff0c\u90a3\u53ea\u7389\u51e4\u51f0\u7ed9\u4e86\u6211\u3002\u201d\u80e1\u6590\u4e00\u5446\uff0c\u5fc3\u4e2d\u5927\u662f\u4e3a\u96be\uff0c\u4f46\u4ed6\u7ec8\u7a76\u662f\u4e2a\u8a00\u51fa\u5fc5\u8df5\u4e4b\u4eba\uff0c\u5f53\u5373\u5c06\u7389\u51e4\u9012\u4e86\u8fc7\u53bb\u3002\u7a0b\u7075\u7d20\u4e0d\u63a5\uff0c\u9053\uff1a\u201c\u6211\u8981\u6765\u5e72\u4ec0\u4e48\uff1f\u6211\u8981\u4f60\u628a\u5b83\u7838\u5f97\u7a00\u70c2\u3002\u201d \u3000\u3000\u8fd9\u4e00\u4ef6\u4e8b\u80e1\u6590\u53ef\u4e07\u4e07\u4e0b\u4e0d\u4e86\u624b\uff0c\u5446\u5446\u7684\u6014\u5728\u5f53\u5730\uff0c\u77a7\u77a7\u7a0b\u7075\u7d20\uff0c\u53c8\u77a7\u77a7\u624b\u4e2d\u7389\u51e4\uff0c\u4e0d\u77e5\u5982\u4f55\u662f\u597d\uff0c\u8881\u7d2b\u8863\u90a3\u4fcf\u4e3d\u5a07\u7f8e\u7684\u8eab\u5f62\u9762\u5e9e\uff0c\u5239\u90a3\u95f4\u5728\u5fc3\u5934\u8fde\u8f6c\u4e86\u51e0\u8f6c\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u7f13\u6b65\u8d70\u8fd1\uff0c\u4ece\u4ed6\u624b\u91cc\u63a5\u8fc7\u7389\u51e4\uff0c\u7ed9\u4ed6\u653e\u5165\u6000\u4e2d\uff0c\u5fae\u7b11\u9053\uff1a\u201c\u4ece\u4eca\u4ee5\u540e\uff0c\u53ef\u522b\u592a\u8f7b\u6613\u7b54\u5e94\u4eba\u5bb6\u3002\u4e16\u4e0a\u6709\u8bb8\u591a\u4e8b\u60c5\uff0c\u53e3\u4e2d\u867d\u7136\u7b54\u5e94\u4e86\uff0c\u5374\u662f\u65e0\u6cd5\u529e\u5230\u7684\u5462\u3002\u597d\u5427\uff0c\u54b1\u4eec\u53ef\u4ee5\u8d70\u5566\uff01\u201d \u3000\u3000\u80e1\u6590\u5fc3\u5934\u6005\u60d8\uff0c\u611f\u5230\u4e00\u80a1\u8bf4\u4e0d\u51fa\u7684\u6ecb\u5473\uff0c\u7ed9\u5979\u6367\u7740\u90a3\u76c6\u4e03\u5fc3\u6d77\u68e0\uff0c\u8ddf\u5728\u540e\u9762\u3002 \u3000\u3000\u884c\u5230\u5348\u95f4\uff0c\u6765\u5230\u4e00\u5ea7\u5927\u9547\u3002\u80e1\u6590\u9053\uff1a\u201c\u54b1\u4eec\u627e\u5bb6\u996d\u5e97\u5403\u996d\uff0c\u7136\u540e\u53bb\u4e70\u4e24\u5934\u7272\u53e3\u3002\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u53ea\u89c1\u4e00\u4e2a\u8eab\u7a7f\u7f0e\u5b50\u957f\u888d\u3001\u5546\u4eba\u6a21\u6837\u7684\u4e2d\u5e74\u6c49\u5b50\u8d70\u4e0a\u524d\u6765\uff0c\u62b1\u62f3\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u80e1\u7237\u4e48\uff1f\u201d\u80e1\u6590\u4ece\u672a\u89c1\u8fc7\u6b64\u4eba\uff0c\u8fd8\u793c\u9053\uff1a\u201c\u4e0d\u6562\uff0c\u6b63\u662f\u5c0f\u53ef\u3002\u8bf7\u95ee\u8d35\u59d3\uff0c\u4e0d\u77e5\u5982\u4f55\u8bc6\u5f97\u5c0f\u53ef\uff1f\u201d\u90a3\u4eba\u5fae\u7b11\u9053\uff1a\u201c\u5c0f\u4eba\u5949\u4e3b\u4eba\u4e4b\u547d\uff0c\u5728\u6b64\u606d\u5019\u591a\u65f6\uff0c\u8bf7\u5f80\u8fd9\u8fb9\u7528\u4e9b\u7c97\u70b9\u3002\u201d\u8bf4\u7740\u606d\u606d\u656c\u656c\u7684\u5f15\u7740\u4e8c\u4eba\u5230\u4e86\u4e00\u5ea7\u9152\u697c\u4e4b\u4e2d\u3002 \u3000\u3000\u9152\u697c\u4e2d\u5e97\u4f34\u4e5f\u4e0d\u5f85\u90a3\u4eba\u5429\u5490\uff0c\u7acb\u5373\u6446\u4e0a\u9152\u9994\u3002\u8bf4\u662f\u7c97\u70b9\uff0c\u5374\u662f\u5341\u5206\u4e30\u76db\u7cbe\u81f4\u7684\u9152\u5e2d\u3002\u80e1\u6590\u548c\u7a0b\u7075\u7d20\u90fd\u611f\u5947\u602a\u3002\u4f46\u89c1\u90a3\u5546\u4eba\u5750\u5728\u4e0b\u9996\u76f8\u966a\uff0c\u4e00\u53e5\u4e0d\u63d0\u4f55\u4eba\u76f8\u8bf7\uff0c\u4e8c\u4eba\u4e5f\u5c31\u4e0d\u95ee\uff0c\u968f\u610f\u5403\u4e86\u4e9b\u3002 \u3000\u3000\u9152\u996d\u5df2\u7f62\uff0c\u90a3\u5546\u4eba\u9053\uff1a\u201c\u8bf7\u4e24\u4f4d\u5230\u8fd9\u8fb9\u4f11\u606f\u3002\u201d\u4e0b\u4e86\u9152\u697c\uff0c\u65e9\u6709\u4ece\u4eba\u7275\u4e86\u4e09\u5339\u5927\u9a6c\u8fc7\u6765\u3002\u4e09\u4eba\u4e0a\u4e86\u9a6c\uff0c\u90a3\u5546\u4eba\u5728\u524d\u5f15\u8def\uff0c\u9a70\u51fa\u5e02\u9547\uff0c\u884c\u4e86\u4e94\u516d\u91cc\uff0c\u5230\u4e86\u4e00\u5ea7\u5927\u5e84\u9662\u524d\u3002\u4f46\u89c1\u5782\u6768\u7ed5\u5b85\uff0c\u767d\u5899\u4e4c\u95e8\uff0c\u6c14\u6d3e\u751a\u662f\u4e0d\u5c0f\u3002 \u3000\u3000\u5e84\u9662\u95e8\u524d\u7ad9\u7740\u516d\u4e03\u540d\u5bb6\u4e01\uff0c\u89c1\u90a3\u5546\u4eba\u5230\u6765\uff0c\u4e00\u9f50\u5782\u624b\u8083\u7acb\u3002\u90a3\u5546\u4eba\u8bf7\u80e1\u6590\u548c\u7a0b\u7075\u7d20\u5230\u5927\u5385\u7528\u8336\uff0c\u684c\u4e0a\u6446\u6ee1\u4e86\u679c\u54c1\u7ec6\u70b9\u3002\u80e1\u6590\u5fc3\u60f3\uff1a\u201c\u6211\u82e5\u95ee\u4ed6\u4f55\u4ee5\u5982\u6b64\u63a5\u5f85\uff0c\u4ed6\u4e0d\u5230\u65f6\u5019\uff0c\u5b9a\u4e0d\u80af\u8bf4\uff0c\u4e14\u8ba9\u4ed6\u5f04\u8db3\u7384\u865a\uff0c\u6211\u53ea\u968f\u673a\u5e94\u53d8\u4fbf\u4e86\u3002\u201d\u5f53\u4e0b\u548c\u7a0b\u7075\u7d20\u968f\u610f\u8c08\u8bba\u6cbf\u9014\u98ce\u7269\u666f\u8272\uff0c\u6ca1\u53bb\u7406\u776c\u90a3\u4eba\u3002\u90a3\u5546\u4eba\u53ea\u662f\u606d\u656c\u76f8\u966a\uff0c\u5bf9\u4e24\u4eba\u7684\u8c08\u8bba\u7adf\u4e0d\u63d2\u53e3\u534a\u53e5\u3002 \u3000\u3000\u7528\u7f62\u70b9\u5fc3\uff0c\u90a3\u5546\u4eba\u8bf4\u9053\uff1a\u201c\u80e1\u7237\u548c\u8fd9\u4f4d\u59d1\u5a18\u65c5\u9014\u52b3\u987f\uff0c\u8bf7\u5185\u5ba4\u6d17\u6fa1\u66f4\u8863\u3002\u201d\u80e1\u6590\u5fc3\u60f3\uff1a\u201c\u542c\u4ed6\u53e3\u6c14\uff0c\u4f3c\u4e0d\u77e5\u7a0b\u59d1\u5a18\u7684\u6765\u5386\uff0c\u5982\u6b64\u66f4\u5999\u3002\u4ed6\u5982\u679c\u6562\u5411\u6bd2\u624b\u836f\u738b\u7684\u5f1f\u5b50\u4e0b\u6bd2\uff0c\u6b63\u597d\u81ea\u8ba8\u82e6\u5403\u3002\u201d\u5f53\u4e0b\u968f\u7740\u5bb6\u4e01\u8d70\u8fdb\u5185\u5802\u3002\u53e6\u6709\u4ec6\u5987\u524d\u6765\u4f8d\u5019\u7a0b\u7075\u7d20\u5f80\u540e\u697c\u6d17\u6c90\u3002 \u3000\u3000\u4e24\u4eba\u7a0d\u52a0\u4f11\u606f\uff0c\u53c8\u5230\u5927\u5385\uff0c\u4f60\u770b\u6211\uff0c\u6211\u770b\u4f60\uff0c\u4f46\u89c1\u5bf9\u65b9\u8eab\u4e0a\u8863\u5c65\u90fd\u662f\u7115\u7136\u4e00\u65b0\u3002\u7a0b\u7075\u7d20\u4f4e\u58f0\u7b11\u9053\uff1a\u201c\u80e1\u5927\u54e5\uff0c\u8fc7\u65b0\u5e74\u5417\uff1f\u6253\u626e\u5f97\u8fd9\u4e48\u9f50\u6574\u3002\u201d\u80e1\u6590\u89c1\u5979\u8138\u4e0a\u8584\u65bd\u8102\u7c89\uff0c\u6e05\u79c0\u4e4b\u4e2d\u5fae\u589e\u5a07\u8273\u4e4b\u8272\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u5374\u50cf\u65b0\u5a18\u5b50\u4e00\u822c\u5462\u3002\u201d\u7a0b\u7075\u7d20\u8138\u4e0a\u4e00\u7ea2\uff0c\u8f6c\u8fc7\u4e86\u5934\u4e0d\u7406\u3002\u80e1\u6590\u6697\u6094\u5931\u8a00\uff0c\u4f46\u5077\u773c\u76f8\u77a7\uff0c\u5979\u8138\u4e0a\u5374\u4e0d\u89c1\u6709\u4f55\u6012\u8272\uff0c\u76ee\u5149\u4e2d\u53ea\u662f\u9732\u51fa\u53c8\u987d\u76ae\u53c8\u7f9e\u602f\u7684\u5149\u8292\u3002 \u3000\u3000\u8fd9\u65f6\u5385\u4e0a\u53c8\u5df2\u4e30\u9648\u9152\u9994\uff0c\u90a3\u5546\u4eba\u5411\u80e1\u6590\u656c\u4e86\u4e09\u676f\u9152\uff0c\u8f6c\u8eab\u5165\u5185\uff0c\u56de\u51fa\u65f6\u624b\u6367\u6258\u76d8\uff0c\u76d8\u4e2d\u653e\u7740\u4e00\u4e2a\u7ea2\u5e03\u5305\u88b1\uff0c\u6253\u5f00\u5305\u88b1\uff0c\u91cc\u9762\u662f\u4e00\u672c\u6ce5\u91d1\u7b3a\u8ba2\u6210\u7684\u7c3f\u5b50\uff0c\u5c01\u76ae\u4e0a\u5199\u7740\u201c\u606d\u5448\u80e1\u5927\u7237\u5370\u6590\u54c2\u7eb3\u201d\u4e5d\u4e2a\u5b57\u3002\u4ed6\u53cc\u624b\u6367\u7740\u7c3f\u5b50\uff0c\u5448\u5230\u80e1\u6590\u9762\u524d\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u4eba\u5949\u4e3b\u4eba\u4e4b\u547d\uff0c\u5c06\u8fd9\u4efd\u8584\u793c\u5448\u4ea4\u80e1\u5927\u7237\u3002\u201d \u3000\u3000\u80e1\u6590\u5e76\u4e0d\u63a5\u7c3f\uff0c\u95ee\u9053\uff1a\u201c\u8d35\u4e3b\u4eba\u662f\u8c01\uff1f\u4f55\u4ee5\u8d60\u793c\u5c0f\u53ef\uff1f\u201d\u90a3\u5546\u4eba\u9053\uff1a\u201c\u655d\u4e0a\u5429\u5490\uff0c\u4e0d\u5f97\u63d0\u4ed6\u540d\u5b57\uff0c\u5c06\u6765\u80e1\u5927\u7237\u81ea\u7136\u77e5\u6653\u3002\u201d \u3000\u3000\u80e1\u6590\u597d\u751f\u5947\u602a\uff0c\u63a5\u8fc7\u9526\u7c3f\uff0c\u7ffb\u5f00\u4e00\u770b\uff0c\u53ea\u89c1\u7b2c\u4e00\u9875\u5199\u9053\uff1a\u201c\u4e0a\u7b49\u6c34\u7530\u56db\u767e\u4e00\u5341\u4e94\u4ea9\u4e03\u5206\u201d\uff0c\u4e0b\u9762\u8be6\u7ec6\u6ce8\u660e\u7530\u4ea9\u7684\u56db\u81f3\u548c\u5ea7\u843d\uff0c\u53c8\u6ce8\u660e\u4f43\u6237\u4e3a\u8c01\uff0c\u6bcf\u5e74\u7f34\u79df\u8c37\u82e5\u5e72\u7b49\u7b49\u3002 \u3000\u3000\u80e1\u6590\u5927\u5947\uff0c\u5fc3\u60f3\uff1a\u201c\u6211\u8981\u8fd9\u56db\u767e\u591a\u4ea9\u6c34\u7530\u5e72\u4ec0\u4e48\uff1f\u201d\u518d\u7ffb\u8fc7\u7b2c\u4e8c\u9875\uff0c\u89c1\u5199\u9053\uff1a\u201c\u5e84\u5b50\u4e00\u5ea7\uff0c\u4e94\u8fdb\uff0c\u8ba1\u697c\u623f\u5341\u4e8c\u95f4\uff0c\u5e73\u623f\u4e03\u5341\u4e09\u95f4\u3002\u201d\u4e0b\u9762\u4e5f\u4ee5\u5c0f\u5b57\u8be6\u6ce8\u5e84\u5b50\u4e1c\u5357\u897f\u5317\u7684\u56db\u81f3\uff0c\u4ee5\u53ca\u6bcf\u95f4\u623f\u5b50\u7684\u540d\u79f0\uff0c\u82b1\u56ed\u3001\u5385\u5802\u3001\u53a2\u623f\uff0c\u4ee5\u81f3\u7076\u62ab\u3001\u67f4\u623f\u3001\u9a6c\u53a9\u7b49\u7b49\uff0c\u65e0\u4e0d\u4e66\u5199\u660e\u767d\u3002\u518d\u7ffb\u4e0b\u53bb\uff0c\u5219\u662f\u5e84\u5b50\u4e2d\u5a62\u4ec6\u7684\u540d\u5b57\uff0c\u65e5\u7528\u91d1\u94f6\u3001\u7cae\u98df\u3001\u7272\u53e3\u3001\u8f66\u8f7f\u3001\u5bb6\u5177\u3001\u8863\u7740\u7b49\u7b49\uff0c\u65e0\u4e0d\u5177\u5907\u3002 \u3000\u3000\u80e1\u6590\u7ffb\u9605\u4e00\u8fc7\uff0c\u5927\u662f\u8ff7\u60d8\uff0c\u5c06\u7c3f\u5b50\u4ea4\u7ed9\u7a0b\u7075\u7d20\uff0c\u9053\uff1a\u201c\u4f60\u770b\u3002\u201d\u7a0b\u7075\u7d20\u770b\u4e86\u4e00\u904d\uff0c\u4e5f\u731c\u4e0d\u900f\u662f\u4ec0\u4e48\u7528\u610f\uff0c\u7b11\u9053\uff1a\u201c\u606d\u559c\u53d1\u8d22\uff0c\u606d\u559c\u53d1\u8d22\uff01\u201d \u3000\u3000\u90a3\u5546\u4eba\u9053\uff1a\u201c\u655d\u4e0a\u8bf4\u4ed3\u5352\u4e4b\u95f4\uff0c\u63aa\u5907\u4e0d\u5468\uff0c\u5b9e\u662f\u4e0d\u6210\u656c\u610f\u3002\u201d \u3000\u3000\u987f\u4e86\u4e00\u987f\uff0c\u8bf4\u9053\uff1a\u201c\u5f85\u4f1a\u5c0f\u4eba\u966a\u80e1\u5927\u7237\uff0c\u5230\u623f\u820d\u5404\u5904\u53bb\u77a7\u77a7\u3002\u201d \u3000\u3000\u80e1\u6590\u95ee\u9053\uff1a\u201c\u4f60\u8d35\u59d3\uff1f\u201d\u90a3\u5546\u4eba\u9053\uff1a\u201c\u5c0f\u4eba\u59d3\u5f20\u3002\u8fd9\u91cc\u7684\u7530\u5730\u623f\u4ea7\uff0c\u6682\u65f6\u7531\u5c0f\u4eba\u66ff\u80e1\u5927\u7237\u7ecf\u7ba1\u3002\u80e1\u5927\u7237\u77a7\u7740\u6709\u4ec0\u4e48\u4e0d\u59a5\uff0c\u53ea\u987b\u5429\u5490\u4fbf\u662f\u3002\u7530\u5730\u623f\u5c4b\u7684\u5951\u636e\uff0c\u90fd\u5728\u8fd9\u91cc\uff0c\u8bf7\u80e1\u5927\u7237\u6536\u7ba1\u3002\u201d \u3000\u3000\u8bf4\u7740\u53c8\u5448\u4e0a\u8bb8\u591a\u6587\u636e\u3002\u80e1\u6590\u9053\uff1a\u201c\u4f60\u4e14\u6536\u7740\u3002\u5e38\u8a00\u9053\uff1a\u65e0\u529f\u4e0d\u53d7\u7984\u3002\u5982\u6b64\u539a\u793c\uff0c\u6211\u672a\u5fc5\u80fd\u53d7\u5462\u3002\u201d\u90a3\u5546\u4eba\u9053\uff1a\u201c\u80e1\u5927\u7237\u592a\u8c26\u4e86\u3002\u655d\u4e0a\u53ea\u8bf4\u793c\u6570\u592a\u8584\uff0c\u5fc3\u4e2d\u7740\u5b9e\u8fc7\u610f\u4e0d\u53bb\u3002\u201d \u3000\u3000\u80e1\u6590\u81ea\u5e7c\u95ef\u8361\u6c5f\u6e56\uff0c\u5947\u8be1\u602a\u5f02\u4e4b\u4e8b\uff0c\u89c1\u95fb\u9887\u4e0d\u5728\u5c11\uff0c\u4f46\u7a81\u7136\u6536\u5230\u8fd9\u6837\u4e00\u4efd\u539a\u793c\uff0c\u800c\u9001\u793c\u4e4b\u4eba\u53c8\u907f\u4e0d\u89c1\u9762\uff0c\u8fd9\u79cd\u4e8b\u5374\u4ece\u6ca1\u542c\u89c1\u8fc7\u3002\u770b\u8fd9\u59d3\u5f20\u7684\u6b65\u5c65\u4e3e\u6b62\uff0c\u51b3\u8ba1\u4e0d\u4f1a\u6b66\u529f\uff0c\u8c08\u5410\u4e2d\u4e5f\u6beb\u65e0\u6b66\u6797\u4eba\u7269\u7684\u6c14\u606f\uff0c\u77a7\u6765\u4ed6\u53ea\u662f\u5949\u4eba\u4e4b\u5631\uff0c\u4e0d\u89c1\u5f97\u4fbf\u77e5\u5185\u60c5\u3002 \u3000\u3000\u9152\u996d\u5df2\u7f62\uff0c\u80e1\u6590\u548c\u7a0b\u7075\u7d20\u5230\u4e66\u623f\u4f11\u606f\u3002\u4f46\u89c1\u4e66\u623f\u4e2d\u56db\u58c1\u56fe\u4e66\uff0c\u51e0\u5217\u6978\u67b0\uff0c\u67b6\u9648\u7476\u7434\uff0c\u751a\u662f\u96c5\u81f4\u3002\u4e00\u540d\u4e66\u50ee\u9001\u4e0a\u6e05\u8336\u540e\u9000\u4e86\u51fa\u53bb\uff0c\u623f\u4e2d\u53ea\u7559\u4e0b\u80e1\u7a0b\u4e8c\u4eba\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u7b11\u9053\uff1a\u201c\u80e1\u5458\u5916\uff0c\u60f3\u4e0d\u5230\u4f60\u5728\u8fd9\u513f\u505a\u8d77\u8001\u7237\u6765\u5566\u3002\u201d \u3000\u3000\u80e1\u6590\u60f3\u60f3\uff0c\u4e5f\u662f\u4e0d\u7981\u5931\u7b11\uff0c\u4f46\u968f\u5373\u76b1\u7709\u8bf4\u9053\uff1a\u201c\u6211\u77a7\u9001\u793c\u4e4b\u4eba\u5b9a\u6709\u6b79\u610f\uff0c\u53ea\u662f\u5b9e\u5728\u731c\u4e0d\u51fa\u8fd9\u4eba\u662f\u8c01\uff1f\u5982\u6b64\u4f5c\u6cd5\u6709\u4ec0\u4e48\u7528\u610f\uff1f\u201d \u3000\u3000\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u4f1a\u4e0d\u4f1a\u662f\u82d7\u4eba\u51e4\uff1f\u201d\u80e1\u6590\u6447\u5934\u9053\uff1a\u201c\u8fd9\u4eba\u867d\u548c\u6211\u6709\u4e0d\u5171\u6234\u5929\u7684\u6df1\u4ec7\uff0c\u4f46\u6211\u77a7\u4ed6\u5149\u660e\u78ca\u843d\uff0c\u5b9e\u662f\u4e00\u6761\u597d\u6c49\uff0c\u4e0d\u81f4\u5e72\u8fd9\u7b49\u9b3c\u9b3c\u795f\u795f\u7684\u52fe\u5f53\u3002\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u4f60\u52a9\u4ed6\u9000\u654c\uff0c\u4ed6\u4fbf\u9001\u4f60\u4e00\u4efd\u539a\u793c\uff0c\u4e00\u6765\u9053\u8c22\uff0c\u4e8c\u6765\u76fc\u671b\u5316\u89e3\u6028\u4ec7\uff0c\u6050\u6015\u5012\u662f\u4e00\u756a\u7f8e\u610f\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u59d3\u80e1\u7684\u5c82\u80fd\u77a7\u5728\u8fd9\u91d1\u94f6\u7530\u4ea7\u4efd\u4e0a\uff0c\u5fd8\u4e86\u7236\u6bcd\u5927\u4ec7\uff1f\u4e0d\uff0c\u4e0d\uff01\u82d7\u4eba\u51e4\u4e0d\u4f1a\u5982\u6b64\u5c0f\u89d1\u4e86\u6211\u3002\u201d\u7a0b\u7075\u7d20\u4f38\u4e86\u4f38\u820c\u5934\uff0c\u9053\uff1a\u201c\u90a3\u5012\u662f\u6211\u5c0f\u89d1\u4e86\u4f60\u5566\u3002\u201d \u3000\u3000\u4e24\u4eba\u5546\u91cf\u4e86\u534a\u65e5\uff0c\u77a7\u4e0d\u51fa\u7aef\u502a\uff0c\u51b3\u610f\u4fbf\u5728\u6b64\u4f4f\u5bbf\u4e00\u5bb5\uff0c\u597d\u6b79\u4e5f\u8981\u63a2\u5bfb\u51fa\u4e00\u70b9\u7ebf\u7d22\u3002\u5230\u4e86\u665a\u95f4\uff0c\u80e1\u6590\u5728\u540e\u5802\u5927\u623f\u4e2d\u5b89\u7761\uff0c\u7a0b\u7075\u7d20\u7684\u95fa\u623f\u5374\u8bbe\u5728\u82b1\u56ed\u65c1\u7684\u697c\u4e0a\u3002\u80e1\u6590\u4e00\u751f\u4e4b\u4e2d\u4ece\u672a\u4f4f\u8fc7\u5982\u6b64\u5bcc\u4e3d\u5802\u7687\u7684\u5c4b\u5b87\uff0c\u800c\u8fd9\u5c4b\u5b87\u5c45\u7136\u5c5e\u4e8e\u81ea\u5df1\uff0c\u66f4\u662f\u532a\u5937\u6240\u601d\u3002 \u3000\u3000\u4ed6\u7761\u5230\u4e8c\u66f4\u65f6\u5206\uff0c\u8f7b\u8f7b\u63a8\u7a97\u8dc3\u51fa\uff0c\u7a9c\u5230\u5c4b\u9762\uff0c\u4f0f\u4f4e\u8eab\u5b50\u4e00\u671b\uff0c\u89c1\u897f\u9762\u540e\u9662\u4e2d\u706f\u706b\u672a\u7184\uff0c\u4e8e\u662f\u5c55\u5f00\u8f7b\u8eab\u529f\u592b\uff0c\u5954\u4e86\u8fc7\u53bb\u3002\u8db3\u94a9\u5c4b\u6a90\uff0c\u4e00\u4e2a\u201c\u5012\u5377\u73e0\u5e18\u201d\uff0c\u4ece\u7a97\u7f1d\u4e2d\u5411\u5185\u5f20\u671b\uff0c\u53ea\u89c1\u90a3\u59d3\u5f20\u7684\u6ef4\u6ef4\u7b03\u7b03\u7684\u6253\u7740\u7b97\u76d8\uff0c\u6b63\u81ea\u7b97\u5e10\uff0c\u53e6\u4e00\u4e2a\u8001\u5bb6\u4eba\u5728\u65c1\u76f8\u966a\u3002\u90a3\u59d3\u5f20\u7684\u5199\u51e0\u7b14\u5e10\uff0c\u4fbf\u8ddf\u90a3\u5bb6\u4eba\u8bf4\u51e0\u53e5\u8bdd\uff0c\u8bf4\u7684\u90fd\u662f\u5de5\u85aa\u67f4\u7c73\u7b49\u7b49\u7410\u4e8b\u3002 \u3000\u3000\u80e1\u6590\u542c\u4e86\u534a\u5929\uff0c\u5168\u65e0\u5934\u7eea\uff0c\u6b63\u8981\u56de\u8eab\uff0c\u5ffd\u542c\u5f97\u4e1c\u8fb9\u5c4b\u9762\u4e0a\u4e00\u58f0\u8f7b\u54cd\u3002\u4ed6\u7ffb\u8eab\u7ad9\u76f4\uff0c\u624b\u63e1\u5200\u67c4\uff0c\u53ea\u89c1\u6765\u7684\u5374\u662f\u7a0b\u7075\u7d20\u3002 \u3000\u3000\u5979\u505a\u4e2a\u624b\u52bf\uff0c\u80e1\u6590\u7eb5\u8eab\u8fc7\u53bb\u3002\u7a0b\u7075\u7d20\u6084\u58f0\u9053\uff1a\u201c\u6211\u524d\u524d\u540e\u540e\u90fd\u77a7\u8fc7\u4e86\uff0c\u6ca1\u534a\u70b9\u8e4a\u8df7\u3002\u4f60\u770b\u5230\u4ec0\u4e48\u6ca1\u6709\uff1f\u201d\u80e1\u6590\u6447\u4e86\u6447\u5934\u3002\u4e24\u4eba\u5206\u522b\u56de\u623f\uff0c\u8fd9\u4e00\u665a\u5404\u81ea\u63d0\u9632\uff0c\u53cd\u590d\u601d\u91cf\uff0c\u90fd\u6ca1\u7761\u5f97\u5b89\u7a33\u3002 \u3000\u3000\u6b21\u6668\u8d77\u8eab\uff0c\u65e9\u6709\u50ee\u4ec6\u9001\u4e0a\u53c2\u6c64\u71d5\u7a9d\uff0c\u8ddf\u7740\u4fbf\u662f\u9762\u997a\u70b9\u5fc3\uff0c\u80e1\u6590\u5374\u53e6\u6709\u4e00\u58f6\u72b6\u5143\u7ea2\u7f8e\u9152\u3002\u80e1\u6590\u5fc3\u60f3\uff1a\u201c\u6709\u7075\u59d1\u5a18\u4e3a\u4f34\uff0c\u8c08\u8c08\u8bb2\u8bb2\uff0c\u5012\u4e5f\u9887\u4e0d\u5bc2\u5bde\u3002\u5728\u8fd9\u91cc\u4f4f\u7740\uff0c\u8bf4\u5f97\u4e0a\u65e0\u5fe7\u65e0\u8651\uff0c\u5feb\u4e50\u900d\u9065\u3002\u201d \u3000\u3000\u84e6\u5730\u8f6c\u5ff5\uff1a\u201c\u90a3\u59d3\u51e4\u7684\u6076\u9738\u6740\u4e86\u953a\u963f\u56db\u5168\u5bb6\uff0c\u6211\u4e0d\u4f38\u6b64\u51a4\uff0c\u6709\u4f55\u9762\u76ee\u7acb\u4e8e\u5929\u5730\u4e4b\u95f4\uff1f\u201d\u60f3\u5230\u6b64\u5904\uff0c\u80f8\u95f4\u70ed\u8840\u6cb8\u817e\uff0c\u4fbf\u5411\u7a0b\u7075\u7d20\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u8fd9\u5c31\u52a8\u8eab\u4e86\u5427\uff1f\u201d\u7a0b\u7075\u7d20\u4e5f\u4e0d\u95ee\u4ed6\u8981\u5230\u4f55\u5904\uff0c\u7b54\u9053\uff1a\u201c\u597d\uff0c\u662f\u8be5\u52a8\u8eab\u4e86\u3002\u201d \u3000\u3000\u4e24\u4eba\u56de\u8fdb\u5367\u5ba4\uff0c\u6362\u4e86\u65e7\u65f6\u8863\u670d\u3002\u80e1\u6590\u5bf9\u90a3\u59d3\u5f20\u7684\u5546\u4eba\u9053\uff1a\u201c\u6211\u4eec\u8d70\u4e86\uff01\u201d\u8bf4\u4e86\u8fd9\u4e00\u53e5\uff0c\u62d4\u6b65\u4fbf\u8d70\u3002\u90a3\u59d3\u5f20\u7684\u5927\u662f\u9519\u6115\uff0c\u9053\uff1a\u201c\u8fd9\u2026\u2026\u8fd9\u2026\u2026\u600e\u4e48\u8d70\u5f97\u8fd9\u822c\u5feb\uff1f\u80e1\u5927\u2026\u2026\u80e1\u5927\u7237\uff0c\u5c0f\u4eba\u53bb\u5907\u8def\u4e0a\u4f7f\u8d39\uff0c\u60a8\u8bf7\u7b49\u4e00\u4f1a\u3002\u201d\u5f85\u4ed6\u8fdb\u53bb\u7aef\u4e86\u4e00\u5927\u76d8\u91d1\u952d\u94f6\u952d\u51fa\u6765\uff0c\u80e1\u7a0b\u4e8c\u4eba\u65e9\u5df2\u8fdc\u53bb\u3002 \u3000\u3000\u4e8c\u4eba\u8de8\u5f00\u5927\u6b65\uff0c\u5411\u5317\u800c\u884c\uff0c\u4e2d\u5348\u65f6\u5206\u5230\u4e86\u4e00\u5904\u5e02\u96c6\uff0c\u4e00\u6253\u542c\uff0c\u624d\u77e5\u6628\u665a\u4f4f\u5bbf\u4e4b\u5904\u53eb\u4f5c\u4e49\u5802\u9547\u3002\u80e1\u6590\u53d6\u51fa\u94f6\u5b50\u4e70\u4e86\u4e24\u5339\u9a6c\uff0c\u4e24\u4eba\u5e76\u9a91\uff0c\u8c08\u8bba\u6628\u65e5\u7684\u5947\u4e8b\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u54b1\u4eec\u767d\u5403\u767d\u559d\uff0c\u767d\u4f4f\u767d\u5bbf\uff0c\u534a\u70b9\u4e5f\u6ca1\u6709\u635f\u5230\u4ec0\u4e48\u3002\u8fd9\u6837\u8bf4\u6765\uff0c\u90a3\u4e3b\u4eba\u4f3c\u4e4e\u5e76\u6ca1\u5b89\u7740\u6b79\u5fc3\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u6211\u603b\u89c9\u8fd9\u4ef6\u4e8b\u9634\u9633\u602a\u6c14\uff0c\u5f88\u6709\u70b9\u513f\u90aa\u95e8\u3002\u201d\u7a0b\u7075\u7d20\u7b11\u9053\uff1a\u201c\u6211\u5012\u76fc\u8fd9\u79cd\u90aa\u95e8\u7684\u4e8b\u513f\u591a\u9047\u4e0a\u4e9b\uff0c\u4e00\u8def\u4e0a\u9634\u9633\u602a\u6c14\u4e2a\u4e0d\u505c\u3002\u5582\uff0c\u80e1\u5927\u7237\uff0c\u4f60\u5230\u5e95\u662f\u53bb\u54ea\u91cc\u554a\uff1f\u201d\u80e1\u6590\u9053\uff1a\u201c\u6211\u8981\u4e0a\u5317\u4eac\u3002\u4f60\u4e5f\u540c\u53bb\u73a9\u73a9\uff0c\u597d\u4e0d\u597d\uff1f\u201d\u7a0b\u7075\u7d20\u7b11\u9053\uff1a\u201c\u597d\u662f\u6ca1\u4ec0\u4e48\u4e0d\u597d\uff0c\u5c31\u53ea\u6015\u6709\u4e9b\u513f\u4e0d\u4fbf\u3002\u201d\u80e1\u6590\u5947\u9053\uff1a\u201c\u4ec0\u4e48\u4e0d\u4fbf\uff1f\u201d\u7a0b\u7075\u7d20\u7b11\u9053\uff1a\u201c\u80e1\u5927\u7237\u53bb\u63a2\u8bbf\u90a3\u4f4d\u8d60\u7389\u51e4\u7684\u59d1\u5a18\uff0c\u8fd8\u5f97\u968f\u8eab\u5e26\u4e2a\u4f7f\u5524\u7684\u4e2b\u73af\u4e48\uff1f\u201d \u3000\u3000\u80e1\u6590\u6b63\u8272\u8bf4\u9053\uff1a\u201c\u4e0d\uff0c\u6211\u662f\u53bb\u8ffd\u6740\u4e00\u4e2a\u4ec7\u4eba\u3002\u6b64\u4eba\u6b66\u529f\u867d\u4e0d\u751a\u9ad8\uff0c\u53ef\u662f\u8033\u76ee\u4f17\u591a\uff0c\u72e1\u72ef\u591a\u667a\uff0c\u76fc\u671b\u7075\u59d1\u5a18\u52a9\u6211\u4e00\u81c2\u4e4b\u529b\u3002\u201d\u4e8e\u662f\u5c06\u4f5b\u51fa\u9547\u4e0a\u51e4\u5929\u5357\u5982\u4f55\u6740\u5bb3\u953a\u963f\u56db\u5168\u5bb6\uff0c\u5982\u4f55\u5e99\u4e2d\u907f\u96e8\u76f8\u9047\uff0c\u5982\u4f55\u7ed9\u4ed6\u518d\u5ea6\u9003\u8d70\u7b49\u60c5\u4e00\u4e00\u8bf4\u4e86\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u542c\u4ed6\u8bf4\u5230\u53e4\u5e99\u9082\u9005\u3001\u51e4\u5929\u5357\u9ed1\u591c\u5154\u8131\u7684\u7ecf\u8fc7\u65f6\uff0c\u8a00\u8bed\u4e2d\u6709\u4e9b\u4e0d\u5c3d\u4e0d\u5b9e\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u4f4d\u8d60\u7389\u51e4\u7684\u59d1\u5a18\u4e5f\u5728\u53e4\u5e99\u4e4b\u4e2d\uff0c\u662f\u4e0d\u662f\u554a\uff1f\u201d\u80e1\u6590\u4e00\u6014\uff0c\u5fc3\u60f3\u5979\u806a\u660e\u4e4b\u6781\uff0c\u53cd\u6b63\u6211\u4e5f\u6ca1\u505a\u4e8f\u5fc3\u4e4b\u4e8b\uff0c\u4e0d\u7528\u7792\u5979\uff0c\u4e8e\u662f\u7d22\u6027\u8fde\u5982\u4f55\u8bc6\u5f97\u8881\u7d2b\u8863\u3001\u5979\u5982\u4f55\u8fde\u593a\u4e09\u6d3e\u638c\u95e8\u4eba\u4e4b\u4f4d\u3001\u5979\u5982\u4f55\u6551\u52a9\u51e4\u5929\u5357\u7b49\u60c5\uff0c\u4e5f\u4ece\u5934\u81f3\u5c3e\u8bf4\u4e86\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u95ee\u9053\uff1a\u201c\u8fd9\u4f4d\u8881\u59d1\u5a18\u662f\u4e2a\u7f8e\u4eba\u513f\uff0c\u662f\u4e0d\u662f\uff1f\u201d\u80e1\u6590\u5fae\u5fae\u4e00\u6014\uff0c\u8138\u90fd\u7ea2\u4e86\uff0c\u8bf4\u9053\uff1a\u201c\u7b97\u662f\u5f88\u7f8e\u5427\u3002\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u6bd4\u6211\u8fd9\u4e11\u4e2b\u5934\u597d\u770b\u5f97\u591a\uff0c\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u80e1\u6590\u6ca1\u9632\u5230\u5979\u7adf\u4f1a\u5982\u6b64\u5355\u5200\u76f4\u5165\u7684\u8be2\u95ee\uff0c\u4e0d\u7531\u5f97\u9887\u662f\u5c34\u5c2c\uff0c\u9053\uff1a\u201c\u8c01\u8bf4\u4f60\u662f\u4e11\u4e2b\u5934\u4e86\uff1f\u8881\u59d1\u5a18\u6bd4\u4f60\u5927\u4e86\u51e0\u5c81\uff0c\u81ea\u7136\u751f\u5f97\u9ad8\u5927\u4e9b\u3002\u201d\u7a0b\u7075\u7d20\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u516b\u5c81\u7684\u65f6\u5019\uff0c\u62ff\u5988\u5988\u7684\u955c\u5b50\u6765\u73a9\u3002\u6211\u59ca\u59ca\u8bf4\uff1a\u2018\u4e11\u516b\u602a\uff0c\u4e0d\u7528\u7167\u5566\uff01\u7167\u6765\u7167\u53bb\u8fd8\u662f\u4e2a\u4e11\u516b\u602a\u3002\u2019\u54fc\uff01\u6211\u4e5f\u4e0d\u7406\u5979\uff0c\u4f60\u731c\u540e\u6765\u600e\u6837\uff1f\u201d \u3000\u3000\u80e1\u6590\u5fc3\u4e2d\u4e00\u5bd2\uff0c\u6697\u60f3\uff1a\u201c\u4f60\u522b\u628a\u59ca\u59ca\u6bd2\u6b7b\u4e86\u624d\u597d\u3002\u201d\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u7a0b\u7075\u7d20\u542c\u4ed6\u8bed\u97f3\u5fae\u98a4\uff0c\u8138\u6709\u5f02\u8272\uff0c\u731c\u4e2d\u4e86\u4ed6\u7684\u5fc3\u601d\uff0c\u9053\uff1a\u201c\u4f60\u6015\u6211\u6bd2\u6b7b\u59ca\u59ca\u5417\uff1f\u90a3\u65f6\u6211\u8fd8\u53ea\u516b\u5c81\u5462\u3002\u55ef\uff0c\u7b2c\u4e8c\u5929\uff0c\u5bb6\u4e2d\u7684\u955c\u5b50\u901a\u7edf\u4e0d\u89c1\u5566\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u8fd9\u5012\u5947\u4e86\u3002\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u4e00\u70b9\u4e5f\u4e0d\u5947\uff0c\u90fd\u7ed9\u6211\u4e22\u5230\u4e86\u4e95\u91cc\u3002\u201d\u5979\u987f\u4e86\u4e00\u987f\uff0c\u8bf4\u9053\uff1a\u201c\u4f46\u6211\u4e22\u5b8c\u4e86\u955c\u5b50\uff0c\u968f\u5373\u5c31\u61c2\u4e86\u3002\u751f\u6765\u662f\u4e2a\u4e11\u4e2b\u5934\uff0c\u5c31\u7b97\u6ca1\u4e86\u955c\u5b50\uff0c\u8fd8\u662f\u4e11\u7684\u3002\u90a3\u4e95\u91cc\u7684\u6c34\u9762\uff0c\u4fbf\u662f\u4e00\u9762\u5706\u5706\u7684\u955c\u5b50\uff0c\u628a\u6211\u7684\u6a21\u6837\u7ed9\u7167\u5f97\u6e05\u6e05\u695a\u695a\u3002\u90a3\u65f6\u5019\u554a\uff0c\u6211\u771f\u60f3\u8df3\u5230\u4e95\u91cc\u53bb\u6b7b\u4e86\u3002\u201d\u5979\u8bf4\u5230\u8fd9\u91cc\uff0c\u7a81\u7136\u4e3e\u8d77\u97ad\u5b50\u72c2\u62bd\u9a6c\u81c0\uff0c\u5411\u524d\u6025\u5954\u3002 \u3000\u3000\u80e1\u6590\u7eb5\u9a6c\u8ddf\u968f\uff0c\u4e24\u4eba\u4e00\u53e3\u6c14\u9a70\u51fa\u5341\u4f59\u91cc\u8def\uff0c\u7a0b\u7075\u7d20\u624d\u52d2\u4f4f\u9a6c\u5934\u3002\u80e1\u6590\u89c1\u5979\u773c\u5708\u7ea2\u7ea2\u7684\uff0c\u663e\u662f\u9002\u624d\u54ed\u8fc7\u6765\u7740\uff0c\u4e0d\u6562\u671d\u5979\u591a\u770b\uff0c\u5fc3\u60f3\uff1a\u201c\u4f60\u867d\u6ca1\u8881\u59d1\u5a18\u7f8e\u8c8c\uff0c\u4f46\u51b3\u4e0d\u662f\u4e11\u4e2b\u5934\u3002\u4f55\u51b5\u4e00\u4e2a\u4eba\u54c1\u5fb7\u7b2c\u4e00\uff0c\u624d\u667a\u65b9\u662f\u7b2c\u4e8c\uff0c\u76f8\u8c8c\u597d\u4e0d\u597d\u4e43\u662f\u5929\u751f\uff0c\u4f55\u5fc5\u56e0\u800c\u4f24\u5fc3\uff1f\u4f60\u4e8b\u4e8b\u806a\u660e\uff0c\u600e\u4e48\u5bf9\u6b64\u4fbf\u8fd9\u5730\u770b\u4e0d\u5f00\uff1f\u201d\u77a7\u7740\u5979\u7626\u524a\u7684\u4fa7\u5f71\uff0c\u5fc3\u4e2d\u5927\u8d77\u601c\u610f\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u6709\u4e00\u4e8b\u76f8\u6c42\uff0c\u4e0d\u77e5\u4f60\u80af\u4e0d\u80af\u7b54\u5141\uff0c\u4e0d\u77e5\u6211\u662f\u5426\u9ad8\u6500\u5f97\u4e0a\uff1f\u201d \u3000\u3000\u7a0b\u7075\u7d20\u8eab\u5b50\u4e00\u9707\uff0c\u98a4\u58f0\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u8bf4\u4ec0\u4e48\uff1f\u201d\u80e1\u6590\u4ece\u5979\u4fa7\u540e\u671b\u53bb\uff0c\u89c1\u5979\u8033\u6839\u5b50\u548c\u534a\u8fb9\u8138\u988a\u5168\u90fd\u7ea2\u4e86\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u6211\u90fd\u65e0\u7236\u6bcd\u4eb2\u4eba\uff0c\u6211\u60f3\u548c\u4f60\u7ed3\u62dc\u4e3a\u5144\u59b9\uff0c\u4f60\u8bf4\u597d\u4e48\uff1f\u201d \u3000\u3000\u7a0b\u7075\u7d20\u7684\u8138\u988a\u5239\u65f6\u95f4\u53d8\u4e3a\u82cd\u767d\uff0c\u5927\u58f0\u7b11\u9053\uff1a\u201c\u597d\u554a\uff0c\u90a3\u6709\u4ec0\u4e48\u4e0d\u597d\uff1f\u6211\u6709\u8fd9\u4e48\u4e00\u4f4d\u5144\u957f\uff0c\u5f53\u771f\u662f\u6c42\u4e4b\u4e0d\u5f97\u5462\uff1f\u201d \u3000\u3000\u80e1\u6590\u542c\u5979\u8bed\u6c14\u4e2d\u542b\u6709\u8ba5\u8bbd\u4e4b\u610f\uff0c\u4e0d\u7981\u9887\u4e3a\u72fc\u72c8\uff0c\u9053\uff1a\u201c\u6211\u662f\u4e00\u7247\u771f\u5fc3\u3002\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u6211\u96be\u9053\u662f\u5047\u610f\uff1f\u201d\u8bf4\u7740\u8df3\u4e0b\u9a6c\u6765\uff0c\u5728\u8def\u65c1\u64ae\u571f\u4e3a\u9999\uff0c\u53cc\u819d\u4e00\u5c48\uff0c\u4fbf\u8dea\u5728\u5730\u4e0a\u3002\u80e1\u6590\u89c1\u5979\u5982\u6b64\u723d\u5feb\uff0c\u4e5f\u8dea\u5728\u5730\u4e0a\uff0c\u5411\u5929\u62dc\u4e86\u51e0\u62dc\uff0c\u76f8\u5bf9\u78d5\u5934\u884c\u793c\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u4eba\u4eba\u90fd\u8bf4\u516b\u62dc\u4e4b\u4ea4\uff0c\u54b1\u4eec\u5f97\u78d5\u8db3\u516b\u4e2a\u5934\u2026\u2026 \u3000\u3000\u4e00\u3001\u4e8c\u3001\u4e09\u3001\u56db\u3001\u2026\u2026\u4e03\u3001\u516b\u2026\u2026\u55ef\uff0c\u6211\u505a\u59b9\u59b9\uff0c\u591a\u78d5\u4e24\u4e2a\u3002\u201d \u3000\u3000\u679c\u7136\u591a\u78d5\u4e86\u4e24\u4e2a\u5934\uff0c\u8fd9\u624d\u7ad9\u8d77\u3002 \u3000\u3000\u80e1\u6590\u89c1\u5979\u8a00\u8bed\u884c\u52a8\u4e4b\u4e2d\uff0c\u7a81\u7136\u95f4\u5fae\u5e26\u72c2\u6001\uff0c\u81ea\u5df1\u4e5f\u6709\u4e9b\u4e0d\u81ea\u7136\u8d77\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u4ece\u4eca\u800c\u540e\uff0c\u6211\u53eb\u4f60\u4e8c\u59b9\u4e86\u3002\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u5bf9\uff0c\u4f60\u662f\u5927\u54e5\u3002\u54b1\u4eec\u600e\u4e48\u4e0d\u7acb\u4e0b\u76df\u8a93\uff0c\u8bf4\u4ec0\u4e48\u6709\u798f\u5171\u4eab\u3001\u6709\u96be\u540c\u5f53\uff1f\u201d\u80e1\u6590\u9053\uff1a\u201c\u7ed3\u4e49\u8d35\u5728\u5fc3\u76df\uff0c\u8bf4\u4e0d\u8bf4\u90fd\u662f\u4e00\u6837\u3002\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u554a\uff0c\u539f\u6765\u5982\u6b64\u3002\u201d\u8bf4\u7740\u8dc3\u4e0a\u4e86\u9a6c\u80cc\uff0c\u8fd9\u65e5\u76f4\u5230\u9ec4\u660f\uff0c\u59cb\u7ec8\u6ca1\u518d\u8ddf\u80e1\u6590\u8bf4\u8bdd\u3002 \u3000\u3000\u508d\u665a\u4e8c\u4eba\u5230\u4e86\u5b89\u9646\uff0c\u521a\u9a70\u9a6c\u8fdb\u5165\u5e02\u53e3\uff0c\u4fbf\u6709\u4e00\u540d\u5e97\u5c0f\u4e8c\u8d70\u4e0a\u6765\u7275\u4f4f\u9a6c\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u80e1\u5927\u7237\u5427\uff1f\u8bf7\u6765\u5c0f\u5e97\u6b47\u9a6c\u3002\u201d \u3000\u3000\u80e1\u6590\u5947\u9053\uff1a\u201c\u4f60\u600e\u77e5\u9053\uff1f\u201d\u5e97\u5c0f\u4e8c\u7b11\u9053\uff1a\u201c\u5c0f\u4eba\u5728\u8fd9\u513f\u7b49\u4e86\u534a\u5929\u5566\u3002\u201d\u4e8e\u662f\u5728\u524d\u5f15\u8def\uff0c\u8ba9\u7740\u4e8c\u4eba\u8fdb\u4e86\u4e00\u5bb6\u623f\u820d\u9ad8\u655e\u7684\u5ba2\u5e97\u3002\u4e0a\u623f\u5374\u53ea\u7559\u4e86\u4e00\u95f4\uff0c\u4e8e\u662f\u53c8\u5f00\u4e86\u4e00\u95f4\uff0c\u8336\u6c34\u9152\u996d\u4e5f\u4e0d\u7528\u5429\u5490\uff0c\u4fbf\u6d41\u6c34\u4ef7\u9001\u5c06\u4e0a\u6765\u3002\u80e1\u6590\u95ee\u90a3\u5e97\u5c0f\u4e8c\uff0c\u662f\u8c01\u53eb\u4ed6\u8fd9\u822c\u4f8d\u5019\u3002\u90a3\u5e97\u5c0f\u4e8c\u7b11\u9053\uff1a\u201c\u4e49\u5802\u9547\u7684\u80e1\u5927\u7237\uff0c\u8c01\u8fd8\u80fd\u4e0d\u77e5\u9053\u4e48\uff1f\u201d\u6b21\u6668\u7ed3\u5e10\uff0c\u638c\u67dc\u7684\u8fde\u8fde\u6253\u8eac\uff0c\u8bf4\u9053\u65e9\u5df2\u4ed8\u8fc7\u4e86\uff0c\u53ea\u80af\u6536\u80e1\u6590\u7ed9\u5e97\u4f34\u7684\u51e0\u94b1\u94f6\u5b50\u8d4f\u94b1\u3002 \u3000\u3000\u4e00\u8fde\u51e0\u65e5\uff0c\u90fd\u662f\u5982\u6b64\u3002\u80e1\u6590\u548c\u7a0b\u7075\u7d20\u867d\u90fd\u662f\u6781\u6709\u667a\u8ba1\u4e4b\u4eba\uff0c\u4f46\u9650\u4e8e\u5e74\u7eaa\u9605\u5386\uff0c\u7adf\u662f\u77a7\u4e0d\u900f\u8fd9\u4e00\u95e8\u6c5f\u6e56\u4f0e\u4fe9\u3002 \u3000\u3000\u5230\u7b2c\u56db\u65e5\u52a8\u8eab\u540e\uff0c\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u5927\u54e5\uff0c\u6211\u8fde\u65e5\u7559\u5fc3\uff0c\u54b1\u4eec\u524d\u540e\u65e0\u4eba\u8ddf\u968f\uff0c\u90a3\u5fc5\u662f\u6709\u4eba\u5728\u524d\u9014\u8bf4\u4e86\u4f60\u7684\u5bb9\u8c8c\u670d\u8272\uff0c\u547d\u4eba\u5b88\u5019\u3002\u54b1\u4eec\u6765\u4e2a\u4e54\u88c5\u6539\u626e\uff0c\u7136\u540e\u4ece\u65c1\u5bdf\u770b\uff0c\u8bf4\u4e0d\u5b9a\u4fbf\u80fd\u5f97\u6089\u771f\u76f8\u3002\u201d\u80e1\u6590\u559c\u9053\uff1a\u201c\u6b64\u8ba1\u5927\u5999\u3002\u201d \u3000\u3000\u4e24\u4eba\u5728\u5e02\u4e0a\u4e70\u4e86\u4e24\u5957\u8863\u886b\u978b\u5e3d\uff0c\u884c\u5230\u90ca\u5916\uff0c\u5728\u4e00\u5904\u65e0\u4eba\u8352\u6797\u4e4b\u4e2d\u6539\u626e\u3002\u7a0b\u7075\u7d20\u7528\u5934\u53d1\u526a\u6210\u5047\u987b\uff0c\u7c98\u5728\u80e1\u6590\u5507\u4e0a\uff0c\u5c06\u4ed6\u626e\u6210\u4e2a\u56db\u5341\u6765\u5c81\u7684\u4e2d\u5e74\u6c49\u5b50\uff0c\u81ea\u5df1\u5374\u7a7f\u4e0a\u957f\u886b\uff0c\u5934\u6234\u5c0f\u5e3d\uff0c\u53d8\u6210\u4e2a\u7626\u7626\u5c0f\u5c0f\u7684\u5c11\u5e74\u7537\u5b50\u3002\u4e24\u4eba\u4e00\u770b\uff0c\u76f8\u5bf9\u5927\u7b11\u3002\u5230\u4e86\u524d\u9762\u5e02\u96c6\uff0c\u4e24\u4eba\u66f4\u5c06\u5750\u9a91\u6362\u4e86\u9a74\u5b50\u3002\u80e1\u6590\u5c06\u5355\u5200\u5305\u5165\u5305\u88b1\uff0c\u518d\u4e70\u4e86\u4e00\u6839\u65f1\u70df\u7ba1\uff0c\u5438\u4e86\u51e0\u53e3\uff0c\u541e\u70df\u5410\u96fe\uff0c\u8fd9\u4e00\u526f\u795e\u8272\uff0c\u65c1\u4eba\u4fbf\u773c\u529b\u518d\u597d\uff0c\u4e5f\u51b3\u8ba1\u8ba4\u4ed6\u4e0d\u51fa\u3002 \u3000\u3000\u8fd9\u65e5\u508d\u665a\u5230\u4e86\u5e7f\u6c34\uff0c\u53ea\u89c1\u5927\u9053\u65c1\u7ad9\u7740\u4e24\u540d\u5e97\u4f34\uff0c\u4f38\u957f\u4e86\u8116\u5b50\u4e1c\u5f20\u897f\u671b\uff0c\u80e1\u6590\u77e5\u4ed6\u4eec\u6b63\u5728\u7b49\u5019\u81ea\u5df1\uff0c\u4e0d\u7981\u6697\u7b11\uff0c\u5f84\u53bb\u6295\u5e97\uff0c\u638c\u67dc\u7684\u89c1\u8fd9\u4e8c\u4eba\u6a21\u6837\u5bd2\u9178\uff0c\u62db\u547c\u4fbf\u61d2\u6d0b\u6d0b\u5730\uff0c\u7ed9\u4e86\u4ed6\u4eec\u4e24\u95f4\u504f\u9662\u3002\u90a3\u4e24\u540d\u5e97\u4f34\u76f4\u7b49\u5230\u5929\u9ed1\uff0c\u8fd9\u624d\u6ca1\u7cbe\u6253\u91c7\u7684\u56de\u5e97\u3002 \u3000\u3000\u80e1\u6590\u53eb\u4e86\u4e00\u4eba\u8fdb\u6765\uff0c\u8ddf\u4ed6\u6709\u4e00\u642d\u6ca1\u4e00\u642d\u7684\u778e\u626f\uff0c\u60f3\u4ece\u4ed6\u53e3\u4e2d\u63a2\u542c\u4e9b\u6d88\u606f\u3002\u521a\u8bf4\u5f97\u51e0\u53e5\u95f2\u8bdd\uff0c\u5ffd\u7136\u5927\u9053\u4e0a\u9a6c\u8e44\u58f0\u54cd\uff0c\u542c\u58f0\u97f3\u4e0d\u6b62\u4e00\u4e58\u3002\u90a3\u5e97\u4f34\u559c\u9053\uff1a\u201c\u80e1\u5927\u7237\u6765\u5566\u3002\u201d\u98de\u5954\u51fa\u5e97\u3002 \u3000\u3000\u80e1\u6590\u5fc3\u9053\uff1a\u201c\u80e1\u5927\u7237\u65e9\u5230\u5566\uff0c\u8ddf\u4f60\u8bf4\u4e86\u8fd9\u4f1a\u5b50\u8bdd\uff0c\u4f60\u8fd8\u4e0d\u77e5\u9053\u3002\u201d\u5f53\u4e0b\u8d70\u5230\u5927\u5802\u4e0a\u53bb\u77a7\u70ed\u95f9\u3002\u53ea\u542c\u5f97\u4eba\u58f0\u55a7\u54d7\uff0c\u90a3\u5e97\u4f34\u5927\u58f0\u9053\uff1a\u201c\u4e0d\u662f\u80e1\u5927\u7237\uff0c\u662f\u9556\u5c40\u5b50\u7684\u8fbe\u5b98\u7237\u3002\u201d\u8ddf\u7740\u8d70\u8fdb\u4e00\u4e2a\u8d9f\u5b50\u624b\u6765\uff0c\u624b\u6367\u9556\u65d7\uff0c\u5728\u5ba2\u5e97\u5916\u7684\u7af9\u7b52\u4e2d\u4e00\u63d2\u3002 \u3000\u3000\u80e1\u6590\u770b\u90a3\u9556\u65d7\u65f6\uff0c\u5fc3\u4e2d\u4e00\u6115\uff0c\u53ea\u89c1\u90a3\u9556\u65d7\u9ec4\u5e95\u9ed1\u7ebf\uff0c\u7ee3\u7740\u4e00\u5339\u80cc\u751f\u53cc\u7ffc\u7684\u9a8f\u9a6c\uff0c\u5f53\u5e74\u5728\u5546\u5bb6\u5821\u4e2d\uff0c\u66fe\u89c1\u8fc7\u8fd9\u9556\u65d7\u4e00\u9762\uff0c\u8ba4\u5f97\u662f\u98de\u9a6c\u9556\u5c40\u7684\u65d7\u53f7\uff0c\u5fc3\u60f3\u8fd9\u9556\u5c40\u4e3b\u4eba\u767e\u80dc\u795e\u62f3\u9a6c\u884c\u7a7a\u5df2\u5728\u5546\u5bb6\u5821\u70e7\u6b7b\uff0c\u4e0d\u77e5\u773c\u4e0b\u4f55\u4eba\u5145\u4efb\u9556\u5934\u3002\u770b\u90a3\u9556\u65d7\u6b8b\u7834\u892a\u8272\uff0c\u5df2\u662f\u591a\u5e74\u672a\u6362\uff0c\u90a3\u8d9f\u5b50\u624b\u4e5f\u662f\u5e74\u8001\u8870\u8fc8\uff0c\u6ca1\u4ec0\u4e48\u7cbe\u795e\uff0c\u4f3c\u4e4e\u98de\u9a6c\u9556\u5c40\u7684\u8fd1\u51b5\u672a\u89c1\u5f97\u600e\u751f\u5174\u65fa\u3002 \u3000\u3000\u8ddf\u7740\u9556\u5934\u8fdb\u6765\uff0c\u5374\u662f\u96c4\u8d73\u8d73\u6c14\u6602\u6602\u7684\u4e00\u6761\u6c49\u5b50\uff0c\u4f46\u89c1\u4ed6\u8138\u4e0a\u65e0\u6570\u5c0f\u75a4\uff0c\u80e1\u6590\u8ba4\u5f97\u4ed6\u662f\u9a6c\u884c\u7a7a\u7684\u5f1f\u5b50\u5f90\u94ee\u3002\u5728\u4ed6\u4e4b\u540e\u662f\u4e00\u4e2a\u7a7f\u7740\u52b2\u88c5\u7684\u5c11\u5987\uff0c\u53cc\u624b\u5404\u643a\u4e00\u4e2a\u7537\u5b69\uff0c\u6b63\u662f\u9a6c\u884c\u7a7a\u7684\u5973\u513f\u9a6c\u6625\u82b1\u3002 \u3000\u3000\u80e1\u6590\u548c\u5979\u76f8\u522b\u6570\u5e74\uff0c\u8fd9\u65f6\u89c1\u5979\u867d\u7136\u4ecd\u662f\u5bb9\u8272\u79c0\u4e3d\uff0c\u4f46\u5df2\u63a9\u4e0d\u4f4f\u8138\u4e0a\u7684\u98ce\u971c\u6194\u60b4\u3002\u4e24\u4e2a\u7537\u5b69\u4e0d\u8fc7\u56db\u5c81\u5de6\u53f3\uff0c\u5374\u662f\u96ea\u767d\u53ef\u7231\uff0c\u5c24\u5176\u4e24\u4eba\u76f8\u8c8c\u4e00\u6a21\u4e00\u6837\uff0c\u663e\u662f\u4e00\u5bf9\u5b6a\u751f\u5144\u5f1f\u3002\u53ea\u542c\u4e00\u4e2a\u7537\u5b69\u5b50\u9053\uff1a\u201c\u5988\uff0c\u6211\u997f\u5566\uff0c\u8981\u5403\u9762\u9762\u3002\u201d\u9a6c\u6625\u82b1\u4f4e\u5934\u9053\uff1a\u201c\u597d\uff0c\u7b49\u7239\u6d17\u4e86\u8138\uff0c\u5927\u4f19\u513f\u4e00\u8d77\u5403\u3002\u201d \u3000\u3000\u80e1\u6590\u5fc3\u9053\uff1a\u201c\u539f\u6765\u4ed6\u5e08\u5144\u59b9\u5df2\u6210\u4e86\u4eb2\uff0c\u8fd8\u751f\u4e0b\u4e24\u4e2a\u5b69\u5b50\u3002\u201d \u3000\u3000\u90a3\u5e74\u4ed6\u5728\u5546\u5bb6\u5821\u4e3a\u5546\u8001\u592a\u6240\u64d2\uff0c\u88ab\u5546\u5b9d\u9707\u7528\u97ad\u5b50\u62bd\u6253\uff0c\u9a6c\u6625\u82b1\u66fe\u51fa\u529b\u6c42\u60c5\uff0c\u6b64\u4e8b\u5e38\u5728\u5fc3\u5934\u3002\u4eca\u65e5\u4ed6\u4e61\u9082\u9005\uff0c\u82e5\u4e0d\u662f\u4ed6\u4e0d\u613f\u7ed9\u4eba\u8ba4\u51fa\u771f\u9762\u76ee\uff0c\u65e9\u5df2\u4e0a\u53bb\u76f8\u8ba4\u9053\u6545\u4e86\u3002 \u3000\u3000\u5f00\u5ba2\u5e97\u7684\u5bf9\u4e8e\u9556\u5c40\u5b50\u5411\u6765\u4e0d\u6562\u5f97\u7f6a\uff0c\u867d\u89c1\u98de\u9a6c\u9556\u5c40\u8fd9\u5355\u9556\u53ea\u662f\u4e00\u8f86\u9556\u8f66\uff0c\u5404\u4eba\u8863\u9970\u655d\u65e7\uff0c\u6599\u60f3\u6ca1\u591a\u5927\u6cb9\u6c34\uff0c\u4f46\u638c\u67dc\u7684\u8fd8\u662f\u4e0a\u524d\u6bb7\u52e4\u63a5\u5f85\u3002 \u3000\u3000\u5f90\u94ee\u542c\u8bf4\u6ca1\u4e86\u4e0a\u623f\uff0c\u7709\u5934\u4e00\u76b1\uff0c\u6b63\u8981\u53d1\u8bdd\uff0c\u8d9f\u5b50\u624b\u5df2\u4ece\u91cc\u9762\u6253\u4e86\u4e2a\u8f6c\u51fa\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u671d\u5357\u90a3\u4e24\u95f4\u4e0a\u623f\u4e0d\u660e\u660e\u7a7a\u7740\u5417\uff1f\u600e\u5730\u6ca1\u4e86\uff1f\u201d \u3000\u3000\u638c\u67dc\u7684\u8d54\u7b11\u8bf4\u9053\uff1a\u201c\u8fbe\u5b98\u7237\u89c1\u8c05\u3002\u8fd9\u4e24\u95f4\u623f\u524d\u5929\u5c31\u6709\u4eba\u5b9a\u4e0b\u4e86\uff0c\u5df2\u4ed8\u4e86\u94f6\u5b50\uff0c\u8bf4\u597d\u4eca\u665a\u8981\u7528\u3002\u201d\u5f90\u94ee\u8fd1\u5e74\u6765\u65f6\u8fd0\u4e0d\u6d4e\uff0c\u8d70\u9556\u5e38\u6709\u5931\u95ea\uff0c\u56e0\u6b64\u4e00\u809a\u76ae\u7684\u59d4\u5c48\uff0c\u542c\u4e86\u6b64\u8a00\uff0c\u4f38\u624b\u5728\u5e10\u53f0\u4e0a\u7528\u529b\u4e00\u62cd\uff0c\u4fbf\u8981\u53d1\u4f5c\u3002\u9a6c\u6625\u82b1\u5fd9\u62c9\u62c9\u4ed6\u8863\u8896\uff0c\u8bf4\u9053\uff1a\u201c\u7b97\u5566\uff0c\u80e1\u4e71\u4f4f\u8fd9\u4e48\u4e00\u5bb5\uff0c\u4e5f\u5c31\u662f\u4e86\u3002\u201d \u3000\u3000\u5f90\u94ee\u8fd8\u771f\u542c\u59bb\u5b50\u7684\u8bdd\uff0c\u5411\u638c\u67dc\u7684\u72e0\u72e0\u77aa\u4e86\u4e00\u773c\uff0c\u8d70\u8fdb\u4e86\u671d\u897f\u7684\u5c0f\u623f\u3002\u9a6c\u6625\u82b1\u62c9\u7740\u4e24\u4e2a\u5b69\u5b50\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u8fd9\u5355\u9556\u916c\u91d1\u8fd9\u4e48\u5fae\u8584\uff0c\u82e5\u4e0d\u5bf9\u4ed8\u7740\u4f7f\uff0c\u8fd8\u5f97\u4e8f\u672c\u3002\u4e0d\u4f4f\u4e0a\u623f\uff0c\u7701\u51e0\u94b1\u94f6\u5b50\u4e5f\u662f\u597d\u7684\u3002\u201d\u5f90\u94ee\u9053\uff1a\u201c\u8bdd\u662f\u4e0d\u9519\uff0c\u4f46\u6211\u5c31\u77a7\u7740\u8fd9\u4e9b\u72d7\u773c\u770b\u4eba\u4f4e\u7684\u5bb6\u4f19\u751f\u6c14\u3002\u201d \u3000\u3000\u539f\u6765\u9a6c\u884c\u7a7a\u6b7b\u540e\uff0c\u5f90\u94ee\u548c\u4e0e\u6625\u82b1\u4e0d\u4e45\u6210\u5a5a\uff0c\u4e24\u4eba\u63a5\u638c\u4e86\u98de\u9a6c\u9556\u5c40\u3002\u5f90\u94ee\u7684\u6b66\u529f\u5a01\u540d\u56fa\u7136\u4e0d\u53ca\u5e08\u7236\uff0c\u800c\u4ed6\u751f\u5c31\u4e00\u526f\u76f4\u809a\u76f4\u80a0\uff0c\u6c5f\u6e56\u4e0a\u7684\u573a\u9762\u7ed3\u4ea4\u66f4\u662f\u65bd\u5c55\u4e0d\u5f00\uff0c\u4e09\u56db\u5e74\u4e2d\u8fde\u78b0\u4e86\u51e0\u6b21\u9489\u5b50\uff0c\u6bcf\u6b21\u5747\u4e8f\u9a6c\u6625\u82b1\u591a\u65b9\u8bbe\u6cd5\uff0c\u624d\u8d54\u8865\u5f25\u7f1d\u4e86\u8fc7\u53bb\u3002\u4f46\u8fd9\u4e48\u4e00\u6765\uff0c\u98de\u9a6c\u9556\u5c40\u7684\u751f\u610f\u4fbf\u4e00\u843d\u5343\u4e08\uff0c\u5927\u4e70\u5356\u662f\u6c38\u4e0d\u4e0a\u95e8\u7684\u4e86\u3002\u8fd9\u4e00\u6b21\u6709\u4e2a\u76d0\u5546\u8981\u9001\u4e00\u7b14\u94f6\u5b50\u4e0a\u5317\u76f4\u96b6\u4fdd\u5b9a\u5e9c\u53bb\uff0c\u4e3a\u6570\u53ea\u6709\u4e5d\u5343\u4e24\uff0c\u6258\u5927\u9556\u5c40\u5e26\u5acc\u916c\u91d1\u8d35\uff0c\u8fd9\u624d\u4ea4\u4e86\u7ed9\u98de\u9a6c\u9556\u5c40\u3002 \u3000\u3000\u5f90\u94ee\u592b\u5987\u5411\u6765\u4e00\u540c\u8d70\u9556\uff0c\u9a6c\u6625\u82b1\u4ee5\u5bb6\u4e2d\u6ca1\u53ef\u9760\u7684\u4eb2\u4eba\uff0c\u653e\u5fc3\u4e0d\u4e0b\u5b69\u5b50\uff0c\u4fbf\u5e26\u540c\u4e86\u51fa\u95e8\uff0c\u8c05\u6765\u8fd9\u533a\u533a\u4e5d\u5343\u4e24\u94f6\u5b50\uff0c\u5728\u8def\u4e0a\u4e5f\u4e0d\u4f1a\u6709\u4ec0\u4e48\u98ce\u9669\u3002 \u3000\u3000\u80e1\u6590\u5411\u9556\u8f66\u671b\u4e86\u4e00\u773c\uff0c\u8d70\u5230\u7a0b\u7075\u7d20\u623f\u4e2d\uff0c\u8bf4\u9053\uff1a\u201c\u4e8c\u59b9\uff0c\u8fd9\u5bf9\u9556\u5934\u592b\u5987\u662f\u6211\u7684\u8001\u76f8\u8bc6\u3002\u201d\u4e8e\u662f\u5c06\u5546\u5bb6\u5821\u4e2d\u5982\u4f55\u8ddf\u4ed6\u4eec\u76f8\u9047\u7684\u4e8b\u7b80\u7565\u8bf4\u4e86\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u4f60\u8ba4\u4e0d\u8ba4\u4ed6\u4eec\uff1f\u201d\u80e1\u6590\u9053\uff1a\u201c\u5f85\u660e\u513f\u4e0a\u4e86\u9053\uff0c\u5230\u8352\u50fb\u65e0\u4eba\u4e4b\u5904\uff0c\u8fd9\u624d\u4e0a\u524d\u76f8\u8ba4\u3002\u201d\u7a0b\u7075\u7d20\u7b11\u9053\uff1a\u201c\u8352\u50fb\u65e0\u4eba\u4e4b\u5904\uff1f\u554a\uff0c\u90a3\u53ef\u4e86\u4e0d\u5f97\uff01\u4ed6\u4eec\u4e0d\u5f53\u4f60\u8fd9\u5c0f\u80e1\u5b50\u662f\u52ab\u9556\u7684\u5f3a\u4eba\u624d\u602a\u3002\u201d\u80e1\u6590\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u679d\u9556\u4e0d\u503c\u5f97\u80e1\u5927\u5be8\u4e3b\u52a8\u624b\u3002\u7a0b\u4e8c\u5be8\u4e3b\uff0c\u4f60\u77a7\u5982\u4f55\uff1f\u201d\u7a0b\u7075\u7d20\u7b11\u9053\uff1a\u201c\u77a7\u90a3\u9556\u5ba2\u8eab\u4e0a\u65e0\u94b1\uff0c\u751a\u662f\u5bd2\u4f27\u3002\u4f60\u6211\u5144\u5f1f\u76d7\u4ea6\u6709\u9053\uff0c\u4e0d\u514d\u62cd\u9a6c\u4e0a\u524d\uff0c\u9001\u4ed6\u51e0\u952d\u91d1\u5b50\u4fbf\u4e86\u3002\u201d\u80e1\u6590\u54c8\u54c8\u4e00\u7b11\u3002\u4ed6\u786e\u662f\u6709\u8d60\u91d1\u4e4b\u5fc3\uff0c\u53ea\u662f\u8981\u76d8\u7b97\u4e2a\u59a5\u5584\u6cd5\u513f\uff0c\u8d60\u91d1\u4e4b\u65f6\u987b\u5f97\u4e0d\u5931\u4e86\u656c\u610f\u3002 \u3000\u3000\u4e24\u4eba\u7528\u8fc7\u665a\u81b3\uff0c\u80e1\u6590\u56de\u623f\u5c31\u5bdd\uff0c\u7761\u5230\u4e2d\u591c\uff0c\u5ffd\u542c\u5f97\u5c4b\u9762\u4e0a\u5580\u7684\u4e00\u58f0\u8f7b\u54cd\u3002\u4ed6\u867d\u5728\u7761\u68a6\u4e4b\u4e2d\uff0c\u4ecd\u662f\u7acb\u5373\u60ca\u89c9\uff0c\u7ffb\u8eab\u5750\u8d77\uff0c\u8de8\u6b65\u4e0b\u7095\uff0c\u542c\u5f97\u5c4b\u4e0a\u5171\u6709\u4e8c\u4eba\u3002\u90a3\u4e8c\u4eba\u8f7b\u8f7b\u4e00\u51fb\u638c\uff0c\u5f84\u4ece\u5c4b\u9762\u8dc3\u843d\u3002\u80e1\u6590\u7ad9\u5230\u7a97\u53e3\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u4e24\u4e2a\u4eba\u662f\u4ec0\u4e48\u6765\u5934\uff0c\u7adf\u662f\u5982\u6b64\u5927\u80c6\uff0c\u65c1\u82e5\u65e0\u4eba\uff1f\u201d\u4f38\u624b\u6307\u6233\u7834\u7a97\u7eb8\uff0c\u5f80\u5916\u5f20\u671b\uff0c\u89c1\u4e24\u4eba\u90fd\u662f\u8eab\u7a7f\u957f\u886b\uff0c\u624b\u4e2d\u4e0d\u6267\u5175\u5203\uff0c\u63a8\u5f00\u671d\u5357\u4e00\u95f4\u4e0a\u623f\u7684\u95e8\uff0c\u4fbf\u8d70\u4e86\u8fdb\u53bb\uff0c\u8ddf\u7740\u706b\u5149\u4e00\u95ea\uff0c\u70b9\u8d77\u706f\u6765\u3002 \u3000\u3000\u80e1\u6590\u5fc3\u60f3\uff1a\u201c\u539f\u6765\u8fd9\u4e24\u4eba\u8bc6\u5f97\u5e97\u4e3b\u4e1c\uff0c\u4e0d\u662f\u6b79\u4eba\u3002\u201d\u56de\u5230\u7095\u4e0a\uff0c\u5ffd\u542c\u5f97\u8e22\u8df6\u8e22\u8df6\u62d6\u978b\u76ae\u54cd\uff0c\u5e97\u5c0f\u4e8c\u8d70\u5230\u4e0a\u623f\u95e8\u53e3\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u662f\u8c01\u554a\uff1f\u600e\u5730\u4e09\u66f4\u534a\u591c\u7684\uff0c\u4e5f\u4e0d\u8d70\u5927\u95e8\uff0c\u5c31\u8fd9\u4e48\u7a9c\u4e86\u4e0b\u6765\uff1f\u201d\u4ed6\u53e3\u4e2d\u547c\u559d\uff0c\u8d70\u8fdb\u4e0a\u623f\uff0c\u4e00\u811a\u521a\u8e0f\u8fdb\uff0c\u4fbf\u201c\u554a\u54df\u201d \u3000\u3000\u4e00\u58f0\u5927\u53eb\uff0c\u8ddf\u7740\u7830\u7684\u4e00\u54cd\uff0c\u53c8\u662f\u201c\u6211\u7684\u5988\u554a\uff0c\u6253\u6b7b\u4eba\u5566\u201d\u53eb\u4e86\u8d77\u6765\uff0c\u539f\u6765\u7ed9\u4eba\u6454\u4e86\u51fa\u6765\uff0c\u7ed3\u7ed3\u5b9e\u5b9e\u7684\u8dcc\u5728\u9662\u5b50\u4e4b\u4e2d\u3002 \u3000\u3000\u8fd9\u4e48\u4e00\u5435\u95f9\uff0c\u6ee1\u5e97\u7684\u4eba\u5168\u9192\u4e86\u3002\u4e24\u4e2a\u957f\u886b\u5ba2\u4e2d\u4e00\u4eba\u7ad9\u5728\u4e0a\u623f\u95e8\u53e3\uff0c\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u5949\u9e21\u516c\u5c71\u738b\u5927\u5be8\u4e3b\u4e4b\u547d\uff0c\u4eca\u665a\u8e29\u76d8\u5b50\u3001\u52ab\u9556\u94f6\u6765\u7740\uff0c\u627e\u7684\u662f\u98de\u9a6c\u9556\u5c40\u5f90\u9556\u5934\u3002\u95f2\u6742\u4eba\u7b49\uff0c\u4e8b\u4e0d\u5e72\u5df1\uff0c\u5feb\u5feb\u56de\u623f\u5b89\u7761\uff0c\u514d\u5f97\u8bef\u4f24\u4eba\u547d\u3002\u201d \u3000\u3000\u5f90\u94ee\u548c\u9a6c\u6625\u82b1\u65e9\u5c31\u9192\u4e86\uff0c\u542c\u4ed6\u5982\u6b64\u53eb\u9635\uff0c\u4e0d\u7531\u5f97\u53c8\u60ca\u53c8\u6012\uff0c\u5fc3\u60f3\u6041\u4ed6\u591a\u5389\u5bb3\u7684\u5927\u76d7\uff0c\u4e5f\u51b3\u4e0d\u80fd\u6b3a\u5230\u5ba2\u5e97\u4e2d\u6765\uff0c\u8fd9\u5e7f\u6c34\u53c8\u4e0d\u662f\u5c0f\u5730\u65b9\uff0c\u8fd9\u7b49\u65e0\u6cd5\u65e0\u5929\uff0c\u53ef\u5c31\u4ece\u672a\u89c1\u8fc7\u3002\u5f90\u94ee\u63a5\u53e3\u5927\u58f0\u9053\uff1a\u201c\u59d3\u5f90\u7684\u4fbf\u5728\u8fd9\u91cc\uff0c\u4e24\u4f4d\u76f8\u597d\u7684\u7559\u4e0b\u4e07\u513f\u3002\u201d\u90a3\u4eba\u5927\u7b11\u9053\uff1a\u201c\u4f60\u628a\u4e5d\u5343\u4e24\u7eb9\u94f6\uff0c\u4e00\u6746\u9556\u65d7\uff0c\u53cc\u624b\u5949\u9001\u7ed9\u5927\u7237\uff0c\u4e5f\u5c31\u662f\u4e86\uff0c\u95ee\u5927\u7237\u4ec0\u4e48\u4e07\u513f\uff1f\u54b1\u4eec\u524d\u5934\u89c1\u3002\u201d\u8bf4\u7740\u62cd\u62cd\u4e24\u58f0\u51fb\u638c\uff0c\u4e24\u4eba\u98de\u8eab\u4e0a\u5c4b\u3002 \u3000\u3000\u5f90\u94ee\u53f3\u624b\u4e00\u626c\uff0c\u4e24\u679d\u94a2\u9556\u6fc0\u5c04\u800c\u4e0a\u3002\u540e\u9762\u90a3\u4eba\u56de\u624b\u4e00\u6284\uff0c\u4e00\u624b\u63a5\u4f4f\uff0c\u8ddf\u7740\u5411\u4e0b\u63b7\u51fa\uff0c\u5f53\u7684\u4e00\u58f0\u54cd\uff0c\u706b\u661f\u56db\u6e85\uff0c\u4e00\u9f50\u843d\u5728\u5f90\u94ee\u8eab\u524d\u4e00\u5c3a\u4e4b\u5904\uff0c\u4e24\u679d\u9556\u90fd\u9489\u5165\u4e86\u9662\u5b50\u4e2d\u7684\u9752\u77f3\u677f\u91cc\uff0c\u8fd9\u4e00\u624b\u52b2\u529e\uff0c\u5f90\u94ee\u5c31\u4e07\u4e07\u4e0d\u80fd\u3002\u53ea\u542c\u4e24\u4eba\u5728\u5c4b\u4e0a\u54c8\u54c8\u5927\u7b11\uff0c\u8ddf\u7740\u9a6c\u8e44\u58f0\u54cd\uff0c\u5411\u5317\u800c\u53bb\u3002 \u3000\u3000\u5e97\u4e2d\u5e97\u4f19\u548c\u4f4f\u5ba2\u5f85\u90a3\u4e24\u4e2a\u66b4\u5ba2\u8fdc\u53bb\uff0c\u8fd9\u624d\u4e03\u5f20\u516b\u5634\u7684\u7eb7\u7eb7\u8bae\u8bba\uff0c\u6709\u7684\u8bf4\u5feb\u4e9b\u62a5\u5b98\uff0c\u6709\u7684\u529d\u5f90\u94ee\u4e0d\u5982\u7ed5\u9053\u800c\u884c\u3002 \u3000\u3000\u5f90\u94ee\u9ed8\u4e0d\u4f5c\u58f0\uff0c\u62d4\u8d77\u4e24\u679a\u94a2\u9556\uff0c\u56de\u5230\u623f\u4e2d\u3002\u592b\u59bb\u4fe9\u4f4e\u58f0\u5546\u91cf\uff0c\u77a7\u8fd9\u4e24\u4eba\u6b66\u529f\u9887\u4e3a\u4e0d\u51e1\uff0c\u8be5\u662f\u6b66\u6797\u4e2d\u7684\u6210\u540d\u4eba\u7269\uff0c\u600e\u4f1a\u77a7\u4e2d\u8fd9\u4e00\u679d\u5c0f\u9556\uff1f\u867d\u7136\u660e\u77e5\u524d\u9014\u4e0d\u5409\uff0c\u4f46\u4e00\u679d\u9556\u51fa\u4e86\u95e8\uff0c\u89c4\u77e9\u662f\u6709\u8fdb\u65e0\u9000\uff0c\u51b3\u4e0d\u80fd\u6253\u56de\u5934\uff0c\u5426\u5219\u9556\u5c40\u5b50\u5c31\u7b97\u662f\u81ea\u5df1\u7838\u4e86\u62db\u724c\u3002\u5f90\u94ee\u6c14\u6124\u6124\u7684\u9053\uff1a\u201c\u9ed1\u9053\u4e0a\u670b\u53cb\u8d8a\u6765\u8d8a\u662f\u6b3a\u4eba\u5566\uff0c\u5f80\u540e\u53bb\u54b1\u4eec\u8fd9\u53e3\u996d\u8fd8\u80fd\u5403\u4e48\uff1f\u6211\u62da\u7740\u6027\u547d\u4e0d\u8981\uff0c\u4e5f\u5f97\u7ed9\u4ed6\u4eec\u5e72\u4e0a\u4e86\u3002\u8fd9\u4e24\u4e2a\u5b69\u5b50\u2026\u2026\u201d\u9a6c\u6625\u82b1\u9053\uff1a\u201c\u54b1\u4eec\u8ddf\u9ed1\u9053\u4e0a\u7684\u65e0\u51a4\u65e0\u4ec7\uff0c\u6700\u591a\u4e0d\u8fc7\u662f\u94f6\u5b50\u7684\u4e8b\uff0c\u8fd8\u4e0d\u81f4\u6709\u4eba\u547d\u5e72\u7cfb\uff0c\u5e26\u7740\u5b69\u5b50\u4e5f\u4e0d\u59a8\u3002\u201d \u3000\u3000\u4f46\u5728\u5979\u5fc3\u5e95\uff0c\u65e9\u5df2\u5728\u6df1\u6df1\u540e\u6094\uff0c\u5b9e\u4e0d\u8be5\u8ba9\u8fd9\u4e24\u4e2a\u5e7c\u513f\u966a\u7740\u7236\u6bcd\u5e72\u5192\u6c5f\u6e56\u4e0a\u7684\u98ce\u9669\u3002 \u3000\u3000\u80e1\u6590\u548c\u7a0b\u7075\u7d20\u9694\u7740\u7a97\u5b50\uff0c\u4e00\u5207\u77a7\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u5fc3\u4e0b\u4e5f\u662f\u6697\u6697\u5947\u602a\uff0c\u89c9\u5f97\u8fd9\u4e00\u8def\u800c\u6765\uff0c\u4e0d\u53ef\u89e3\u4e4b\u4e8b\u751a\u591a\uff0c\u6ee1\u4ee5\u4e3a\u4e54\u88c5\u6539\u626e\u4e4b\u540e\uff0c\u4fbf\u53ef\u907f\u8fc7\u8ffd\u8e2a\uff0c\u5c82\u77e5\u7b2c\u4e00\u5929\u4fbf\u9047\u5230\u98de\u9a6c\u9556\u5c40\u8fd9\u4ef6\u5947\u4e8b\u3002 \u3000\u3000\u6b21\u65e5\u6e05\u6668\uff0c\u98de\u9a6c\u9556\u5c40\u7684\u9556\u8f66\u4e00\u8d77\u884c\uff0c\u80e1\u6590\u548c\u7a0b\u7075\u7d20\u4fbf\u4e0d\u5373\u4e0d\u79bb\u7684\u8ddf\u968f\u5728\u540e\u3002\u5f90\u94ee\u89c1\u4ed6\u4e8c\u4eba\u8ddf\u8e2a\u4e0d\u820d\uff0c\u8d8a\u770b\u8def\u9053\u8d8a\u662f\u4e0d\u5bf9\uff0c\u6599\u4ed6\u4e8c\u4eba\u5b9a\u662f\u8d3c\u515a\uff0c\u4e0d\u65f6\u56de\u5934\u6012\u76ee\u800c\u89c6\u3002\u80e1\u7a0b\u4e8c\u4eba\u5374\u88c5\u4f5c\u4e0d\u89c1\u3002 \u3000\u3000\u4e2d\u5348\u6253\u5c16\uff0c\u80e1\u7a0b\u4e8c\u4eba\u4e5f\u548c\u98de\u9a6c\u9556\u5c40\u4e00\u5904\u5403\u725b\u8089\u9762\u997c\u3002\u884c\u5230\u508d\u665a\uff0c\u79bb\u6b66\u80dc\u5173\u7ea6\u6709\u56db\u5341\u6765\u91cc\uff0c\u53ea\u542c\u5f97\u9a6c\u8e44\u58f0\u54cd\uff0c\u4e24\u9a91\u9a6c\u8fce\u9762\u98de\u9a70\u800c\u6765\u3002\u9a6c\u4e0a\u4e58\u5ba2\u8eab\u7a7f\u7070\u5e03\u957f\u888d\uff0c\u4ece\u9556\u8f66\u65c1\u4e00\u63a0\u800c\u8fc7\uff0c\u76f4\u5954\u8fc7\u80e1\u7a0b\u4e8c\u4eba\u8eab\u65c1\uff0c\u8fd9\u624d\u9760\u62e2\u5e76\u9a70\uff0c\u7eb5\u58f0\u957f\u7b11\uff0c\u542c\u58f0\u97f3\u6b63\u662f\u6628\u665a\u7684\u4e24\u4e2a\u66b4\u5ba2\u3002 \u3000\u3000\u80e1\u6590\u9053\uff1a\u201c\u5f85\u5f97\u4ed6\u4eec\u518d\u4ece\u540e\u9762\u8ffd\u4e0a\uff0c\u4e0d\u51fa\u51e0\u91cc\u8def\uff0c\u4fbf\u8981\u52a8\u624b\u4e86\u3002\u201d\u8bdd\u72b9\u672a\u6bd5\uff0c\u5ffd\u542c\u524d\u9762\u9a6c\u8e44\u58f0\u54cd\uff0c\u53c8\u6709\u4e24\u4e58\u9a6c\u4ece\u8eab\u65c1\u63a0\u8fc7\uff0c\u9a6c\u4e0a\u4e58\u5ba2\u8eab\u624b\u77eb\u5065\uff0c\u663e\u662f\u6c5f\u6e56\u4eba\u7269\u3002\u80e1\u6590\u9053\uff1a\u201c\u5947\u602a\uff0c\u5947\u602a\uff01\u201d\u884c\u4e0d\u5230\u4e00\u91cc\u8def\uff0c\u53c8\u6709\u4e24\u4e58\u9a6c\u8fce\u9762\u5954\u6765\uff0c\u8ddf\u7740\u53c8\u6709\u4e24\u4e58\u9a6c\u3002 \u3000\u3000\u5f90\u94ee\u89c1\u4e86\u8fd9\u7b49\u5927\u52bf\u6d3e\uff0c\u65e9\u5df2\u628a\u5fc3\u6a2a\u4e86\uff0c\u4e0d\u6012\u53cd\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5e08\u59b9\uff0c\u5e08\u7236\u66fe\u8bf4\uff0c\u7eff\u6797\u4e2d\u4e00\u7b49\u4e00\u7684\u5927\u5be8\uff0c\u5174\u5e08\u52a8\u4f17\u52ab\u90a3\u4e00\u7b49\u4e00\u7684\u5927\u9556\uff0c\u90a3\u624d\u6d3e\u5230\u516d\u4e2a\u597d\u624b\u63a2\u76d8\u5b50\uff0c\u4eca\u65e5\u5c45\u7136\u8fde\u6d3e\u5230\u516b\u4f4d\u9ad8\u4eba\uff0c\u540e\u9762\u53c8\u6709\u4e24\u4f4d\u9634\u9b42\u4e0d\u6563\u7684\u8ddf\u7740\uff0c\u53ea\u6015\u54b1\u4eec\u8fd9\u8def\u9556\u4fdd\u7684\u4e0d\u662f\u7eb9\u94f6\u4e5d\u5343\u4e24\uff0c\u800c\u662f\u4e5d\u767e\u4e07\u3001\u4e5d\u5343\u4e07\u4e24\uff01\u201d \u3000\u3000\u9a6c\u6625\u82b1\u731c\u4e0d\u900f\u654c\u4eba\u4f55\u4ee5\u5982\u6b64\u5927\u5f20\u65d7\u9f13\uff0c\u6765\u5bf9\u4ed8\u8fd9\u679d\u5fae\u4e0d\u8db3\u9053\u7684\u5c0f\u9556\uff0c\u4f46\u8d8a\u662f\u4e0d\u61c2\uff0c\u8d8a\u662f\u621a\u7136\u6709\u5fe7\uff0c\u5bf9\u5f90\u94ee\u548c\u8d9f\u5b50\u624b\u9053\uff1a\u201c\u5f85\u4f1a\u60c5\u52bf\u4e0d\u5bf9\uff0c\u54b1\u4eec\u5e26\u4e86\u5b69\u5b50\u9003\u547d\u8981\u7d27\u3002\u8fd9\u4e5d\u5343\u4e24\u94f6\u5b50\u561b\uff0c\u6570\u76ee\u4e0d\u5927\uff0c\u603b\u8fd8\u80fd\u5f20\u7f57\u7740\u8d54\u5f97\u8d77\u3002\u201d\u5f90\u94ee\u6602\u7136\u9053\uff1a\u201c\u5e08\u7236\u4e00\u4e16\u82f1\u540d\uff0c\u4fbf\u8fd9\u4e48\u9001\u5728\u54b1\u8fd9\u4e2a\u4e0d\u6210\u6750\u7684\u5f1f\u5b50\u624b\u4e2d\u5417\uff1f\u201d\u9a6c\u6625\u82b1\u51c4\u7136\u9053\uff1a\u201c\u603b\u5f97\u77a7\u5b69\u5b50\u4efd\u4e0a\u3002\u4eca\u540e\u6211\u4e24\u53e3\u5b50\u8015\u7530\u52a1\u519c\uff0c\u5403\u4e00\u53e3\u82e6\u996d\uff0c\u4e5f\u4e0d\u505a\u8fd9\u52a8\u5200\u5b50\u62da\u547d\u7684\u52fe\u5f53\u5566\u3002\u201d \u3000\u3000\u8bf4\u5230\u8fd9\u91cc\uff0c\u5ffd\u542c\u5f97\u8eab\u540e\u8e44\u58f0\u5954\u817e\uff0c\u56de\u5934\u4e00\u671b\uff0c\u5c18\u571f\u98de\u626c\uff0c\u90a3\u516b\u4e58\u9a6c\u4e00\u9f50\u81ea\u540e\u8d76\u4e86\u4e0a\u6765\u3002\u545c\u7684\u4e00\u58f0\u957f\u9e23\uff0c\u4e00\u679d\u54cd\u7bad\u4ece\u5934\u9876\u98de\u8fc7\uff0c\u8ddf\u7740\u8fce\u9762\u4e5f\u6709\u516b\u4e58\u9a6c\u5954\u6765\u3002 \u3000\u3000\u80e1\u6590\u9053\uff1a\u201c\u77a7\u8fd9\u58f0\u52bf\uff0c\u8fd9\u5e2e\u5b50\u4eba\u53ea\u6015\u662f\u51b2\u7740\u54b1\u4eec\u800c\u6765\u3002\u201d\u7a0b\u7075\u7d20\u70b9\u5934\u9053\uff1a\u201c\u7530\u5f52\u519c\uff01\u201d\u80e1\u6590\u9053\uff1a\u201c\u54b1\u4eec\u7684\u6539\u626e\u7ec8\u7a76\u4e0d\u6210\uff0c\u8fd8\u662f\u7ed9\u8ba4\u51fa\u4e86\u3002\u201d \u3000\u3000\u8fd9\u65f6\u524d\u9762\u516b\u4e58\u9a6c\uff0c\u540e\u9762\u516b\u4e58\u9a6c\u4e00\u9f50\u52d2\u7f30\u4e0d\u52a8\uff0c\u5df2\u5c06\u9556\u5c40\u5b50\u4e00\u884c\u4eba\u548c\u80e1\u7a0b\u4e8c\u4eba\u5939\u4f4f\u5728\u4e2d\u95f4\u3002 \u3000\u3000\u5f90\u94ee\u7ffb\u8eab\u4e0b\u9a6c\uff0c\u4eae\u51fa\u5355\u5200\uff0c\u62b1\u62f3\u9053\uff1a\u201c\u5728\u4e0b\u5f90\u2026\u2026\u201d\u53ea\u8bf4\u4e86\u4e09\u5b57\uff0c\u524d\u9762\u516b\u4e58\u9a6c\u4e2d\u4e00\u4e2a\u8001\u8005\u7a81\u7136\u98de\u8dc3\u4e0b\u9a6c\uff0c\u7eb5\u8eab\u800c\u524d\uff0c\u624b\u4e2d\u6301\u7740\u4e00\u4ef6\u5947\u5f62\u5175\u5203\uff0c\u4e00\u8bed\u4e0d\u53d1\uff0c\u4fbf\u5411\u5f90\u94ee\u8138\u4e0a\u7838\u53bb\u3002 \u3000\u3000\u80e1\u6590\u548c\u7a0b\u7075\u7d20\u52d2\u9a6c\u5728\u65c1\uff0c\u89c1\u90a3\u8001\u8005\u624b\u4e2d\u5175\u5203\u751a\u662f\u5947\u602a\uff0c\u524d\u9762\u4e00\u4e2a\u6a2a\u6761\uff0c\u5f2f\u66f2\u5982\u86c7\uff0c\u6a2a\u6761\u540e\u751f\u7740\u4e01\u5b57\u5f62\u7684\u63e1\u624b\uff0c\u90a3\u6a2a\u6761\u4e24\u7aef\u5c16\u5229\uff0c\u4fbf\u4f3c\u4e00\u67c4\u53d8\u5f62\u7684\u9e64\u5634\u9504\u6a21\u6837\u3002\u80e1\u6590\u4e0d\u8bc6\u6b64\u7269\uff0c\u95ee\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u90a3\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u7a0b\u7075\u7d20\u8fd8\u672a\u56de\u7b54\uff0c\u8eab\u540e\u4e00\u540d\u5927\u76d7\u7b11\u9053\uff1a\u201c\u8001\u5c0f\u5b50\uff0c\u6559\u4f60\u4e00\u4e2a\u4e56\uff0c\u8fd9\u53eb\u505a\u96f7\u9707\u6321\u3002\u201d\u7a0b\u7075\u7d20\u63a5\u53e3\u9053\uff1a\u201c\u96f7\u9707\u6321\u4e0d\u548c\u95ea\u7535\u9525\u540c\u4f7f\uff0c\u6b66\u529f\u4e5f\u662f\u5e73\u5e38\u3002\u201d \u3000\u3000\u90a3\u5927\u76d7\u4e00\u5446\uff0c\u4e0d\u518d\u4f5c\u58f0\uff0c\u659c\u773c\u6253\u91cf\u7a0b\u7075\u7d20\uff0c\u5fc3\u60f3\u8fd9\u7626\u5c0f\u5b50\u5c45\u7136\u4e5f\u77e5\u9053\u95ea\u7535\u9525\u3002\u539f\u6765\u8001\u8005\u662f\u4ed6\u5e08\u5144\uff0c\u8fd9\u5927\u76d7\u81ea\u5df1\u6240\u4f7f\u7684\u4fbf\u662f\u95ea\u7535\u9525\u3002\u4ed6\u4e8c\u4eba\u7684\u5e08\u7236\u53f3\u624b\u4f7f\u95ea\u7535\u9525\uff0c\u5de6\u624b\u4f7f\u96f7\u9707\u6321\uff0c\u4e00\u653b\u4e00\u5b88\uff0c\u53d8\u5316\u6781\u5c3d\u5947\u5999\u3002\u4f46\u8fd9\u4e24\u4ef6\u5175\u5203\u4e00\u957f\u4e00\u77ed\uff0c\u53cc\u624b\u5171\u4f7f\u65f6\u76f8\u8f85\u76f8\u6210\uff0c\u5a01\u529b\u56fa\u7136\u751a\u5927\uff0c\u4f46\u4e5f\u5341\u5206\u8270\u96be\uff0c\u4ed6\u5e08\u5144\u5f1f\u4e8c\u4eba\u5404\u5f97\u5e08\u7236\u4e00\u53ea\u624b\u7684\u6280\u827a\uff0c\u59cb\u7ec8\u5b66\u4e0d\u4f1a\u4e24\u4ef6\u5175\u5203\u540c\u4f7f\u3002\u4ed6\u4e8c\u4eba\u81ea\u5e7c\u4fbf\u5728\u585e\u5916\uff0c\u521d\u6765\u4e2d\u539f\u672a\u4e45\uff0c\u800c\u4ed6\u7684\u95ea\u7535\u9525\u53c8\u662f\u85cf\u5728\u8896\u4e2d\uff0c\u5e76\u672a\u53d6\u51fa\uff0c\u4e0d\u610f\u7ed9\u7a0b\u7075\u7d20\u4e00\u8bed\u9053\u7834\u6765\u5386\uff0c\u4e0d\u7981\u60ca\u8be7\u65e0\u5df2\u3002 \u3000\u3000\u4ed6\u90a3\u77e5\u7a0b\u7075\u7d20\u7684\u5e08\u7236\u6bd2\u624b\u836f\u738b\u65e0\u55d4\u5927\u5e08\u89c1\u95fb\u5e7f\u535a\uff0c\u5e73\u65f6\u5e38\u548c\u8fd9\u4e2a\u6700\u953a\u7231\u7684\u5c0f\u5f1f\u5b50\u8bb2\u8ff0\u5404\u5bb6\u5404\u6d3e\u6b66\u529f\uff0c\u56e0\u6b64\u5979\u867d\u7136\u4ece\u672a\u89c1\u8fc7\u96f7\u9707\u6321\uff0c\u4f46\u4e00\u542c\u5176\u540d\uff0c\u4fbf\u77e5\u5c1a\u6709\u4e00\u628a\u95ea\u7535\u9525\u3002 \u3000\u3000\u4f46\u89c1\u90a3\u8001\u8005\u5c06\u5175\u5203\u4f7f\u5f97\u8f70\u8f70\u53d1\u53d1\uff0c\u679c\u7136\u6709\u96f7\u9707\u4e4b\u5a01\u3002\u5f90\u94ee\u5355\u5200\u4e0a\u7684\u529f\u592b\u867d\u4e5f\u4e0d\u5f31\uff0c\u4f46\u88ab\u90a3\u96f7\u9707\u6321\u88f9\u4f4f\u4e86\uff0c\u6e10\u6e10\u65bd\u5c55\u4e0d\u5f00\u3002 \u3000\u3000\u53ea\u542c\u5f97\u524d\u540e\u5341\u4e94\u540d\u5927\u76d7\u4f60\u4e00\u8a00\uff0c\u6211\u4e00\u8bed\uff0c\u51fa\u8a00\u8ba5\u5632\uff1a\u201c\u4ec0\u4e48\u98de\u9a6c\u9556\u5c40\uff1f\u5f53\u5e74\u9a6c\u8001\u9556\u5934\u8d70\u9556\uff0c\u624d\u79f0\u5f97\u4e0a\u2018\u98de\u9a6c\u2019\u4e8c\u5b57\uff0c\u5230\u4e86\u59d3\u5f90\u7684\u624b\u91cc\uff0c\u65e9\u8be5\u6539\u79f0\u72d7\u722c\u9556\u5c40\u5566\uff01\u201d\u201c\u8fd9\u5c0f\u5b50\u5b66\u4e86\u4e24\u624b\u4e09\u811a\u6bdb\uff0c\u4e0d\u5728\u5bb6\u91cc\u62b1\u5a03\u5a03\uff0c\u5374\u5230\u5916\u9762\u6765\u4e22\u4eba\u73b0\u4e16\u3002\u201d\u201c\u5582\uff0c\u59d3\u5f90\u7684\uff0c\u5feb\u8dea\u4e0b\u6765\u78d5\u4e09\u4e2a\u54cd\u5934\uff0c\u6211\u4eec\u5927\u54e5\u4fbf\u9976\u4e86\u4f60\u7684\u72d7\u547d\u3002\u201d\u201c\u8d70\u9556\u8d70\u5f97\u8fd9\u4e48\u5bd2\u8822\uff0c\u8fde\u4e5d\u5343\u4e24\u94f6\u5b50\u4e5f\u4fdd\uff0c\u4e0d\u5982\u4e70\u5757\u8c46\u8150\u6765\u81ea\u5df1\u649e\u6b7b\u4e86\u7f62\uff01\u201d\u201c\u795e\u62f3\u65e0\u654c\u9a6c\u8001\u9556\u5934\u5f53\u5e74\u8d6b\u8d6b\u5a01\u540d\uff0c\u6b66\u6797\u4e2d\u65e0\u4eba\u4e0d\u670d\uff0c\u8fd9\u8113\u5305\u5c0f\u5b50\u771f\u662f\u5bf9\u4e0d\u4f4f\u5e08\u7236\u3002\u201d\u201c\u6211\u77a7\u4ed6\u592b\u4eba\u6bd4\u4ed6\u5f3a\u4e0a\u5341\u500d\uff0c\u5f53\u771f\u662f\u4e00\u679d\u9c9c\u82b1\u63d2\u5728\u725b\u7caa\u91cc\uff01\u597d\u6559\u4eba\u77a7\u7740\u751f\u6c14\u3002\u201d \u3000\u3000\u80e1\u6590\u542c\u4e86\u5404\u4eba\u8a00\u8bed\uff0c\u5fc3\u60f3\u8fd9\u7fa4\u5927\u76d7\u5bf9\u5f90\u94ee\u7684\u5e95\u7ec6\u6478\u5f97\u751a\u662f\u6e05\u695a\uff0c\u4e0d\u4f46\u77e5\u9053\u4ed6\u7684\u5e08\u627f\u6765\u5386\uff0c\u8fd8\u77e5\u4ed6\u4e00\u5171\u4fdd\u4e86\u591a\u5c11\u9556\u94f6\uff0c\u8bf4\u8bdd\u4e4b\u4e2d\u5bf9\u4ed6\u56fa\u662f\u6781\u5c3d\u5c16\u9178\u523b\u8584\uff0c\u4f46\u5bf9\u9a6c\u6625\u82b1\u548c\u5979\u8fc7\u4e16\u7684\u7236\u4eb2\u5374\u6beb\u65e0\u5f97\u7f6a\u4e4b\u5904\uff0c\u751a\u81f3\u8fd8\u663e\u5f97\u9887\u4e3a\u5c0a\u656c\u3002\u80e1\u6590\u867d\u7136\u4e0d\u8bc6\u96f7\u9707\u6321\uff0c\u4f46\u90a3\u8001\u8005\u529f\u529b\u4e0d\u5f31\uff0c\u51fa\u624b\u65e2\u72e0\u4e14\u51c6\uff0c\u5374\u662f\u4e00\u773c\u4fbf\u77e5\uff0c\u4e0d\u7531\u5f97\u6697\u81ea\u5947\u602a\uff1a\u201c\u8fd9\u8001\u5934\u513f\u867d\u4e0d\u80fd\u8bf4\u662f\u6c5f\u6e56\u4e0a\u7684\u7b2c\u4e00\u6d41\u597d\u624b\uff0c\u4f46\u5982\u6b64\u6b66\u529f\uff0c\u5fc5\u662f\u4e2a\u9887\u6709\u8eab\u5206\u7684\u6210\u540d\u4eba\u7269\u3002\u77a7\u5404\u4eba\u7684\u4f5c\u4e3a\uff0c\u51b3\u975e\u51b2\u7740\u8fd9\u533a\u533a\u4e5d\u5343\u4e24\u94f6\u5b50\u800c\u6765\u3002\u4f46\u82e5\u662f\u7530\u5f52\u519c\u6d3e\u6765\u8ddf\u6211\u4e3a\u96be\uff0c\u5374\u53c8\u4f55\u5fc5\u8d39\u8fd9\u4e48\u5927\u7684\u52b2\u513f\u53bb\u5bf9\u4ed8\u5f90\u94ee\uff1f\u201d \u3000\u3000\u9a6c\u6625\u82b1\u5728\u65c1\u77a7\u5f97\u7126\u6025\u4e07\u5206\uff0c\u5979\u65e9\u77e5\u4e08\u592b\u4e0d\u662f\u4eba\u5bb6\u5bf9\u624b\uff0c\u7136\u800c\u81ea\u5df1\u4e0a\u524d\u76f8\u52a9\uff0c\u53ea\u4e0d\u8fc7\u591a\u5f15\u4e00\u4e2a\u654c\u4eba\u4e0b\u573a\uff0c\u4e8e\u4e8b\u4e1d\u6beb\u65e0\u8865\uff0c\u4e24\u4e2a\u513f\u5b50\u65e0\u4eba\u7167\u6599\uff0c\u5374\u52bf\u5fc5\u843d\u5165\u76d7\u4f17\u624b\u4e2d\u3002\u773c\u7741\u7741\u7684\u77a7\u7740\u4e08\u592b\u8d8a\u6765\u8d8a\u662f\u4e0d\u6d4e\uff0c\u7a81\u89c1\u90a3\u8001\u8005\u5c06\u86c7\u5f62\u5175\u5668\u5f80\u524d\u75be\u9001\uff0c\u5708\u8f6c\u56de\u62c9\uff0c\u5f90\u94ee\u5355\u5200\u8131\u624b\uff0c\u98de\u4e0a\u534a\u5929\uff0c\u5979\u201c\u554a\u201d\u7684\u4e00\u58f0\u53eb\u4e86\u51fa\u6765\u3002 \u3000\u3000\u90a3\u8001\u8005\u5de6\u8db3\u6a2a\u626b\uff0c\u5f90\u94ee\u6025\u8dc3\u907f\u8fc7\u3002\u90a3\u5355\u5200\u4ece\u534a\u7a7a\u843d\u5c06\u4e0b\u6765\uff0c\u76d7\u4f17\u4e2d\u4e00\u4eba\u4e3e\u8d77\u957f\u5251\uff0c\u5f80\u4e0a\u4e00\u64a9\uff0c\u4e00\u67c4\u94a2\u5200\u767b\u65f6\u65ad\u4e3a\u4e24\u622a\u3002\u90a3\u76d7\u4f19\u8eab\u624b\u597d\u5feb\uff0c\u957f\u5251\u8ddf\u7740\u4e00\u5288\u4e00\u524a\uff0c\u53c8\u5c06\u5c1a\u672a\u843d\u5730\u7684\u4e24\u622a\u65ad\u5200\u65a9\u6210\u56db\u622a\u3002\u4ed6\u624b\u4e2d\u6240\u6301\u7684\u56fa\u662f\u6781\u950b\u5229\u7684\u5b9d\u5251\uff0c\u800c\u51fa\u624b\u4e4b\u8fc5\u6377\uff0c\u66f4\u662f\u4f7f\u4eba\u76ee\u4e3a\u4e4b\u7729\u3002\u7fa4\u76d7\u9f50\u58f0\u559d\u5f69\u3002 \u3000\u3000\u77a7\u8fd9\u60c5\u52bf\uff0c\u54ea\u91cc\u662f\u62e6\u8def\u52ab\u9556\uff0c\u5b9e\u662f\u5bf9\u5f90\u94ee\u5b58\u5fc3\u620f\u5f04\uff01\u5355\u662f\u8fd9\u624b\u6301\u957f\u5251\u7684\u5927\u76d7\u4e00\u4eba\uff0c\u6253\u8d25\u5f90\u94ee\u592b\u5987\u4fbf\u5df2\u7ef0\u7ef0\u6709\u4f59\uff0c\u4f55\u51b5\u540c\u4f19\u5171\u6709\u4e00\u5341\u516d\u4eba\uff0c\u770b\u6765\u4e2a\u4e2a\u90fd\u662f\u597d\u624b\uff0c\u4e2a\u4e2a\u7b11\u50b2\u81ea\u82e5\uff0c\u4fbf\u5982\u5341\u516d\u5934\u7075\u732b\u56f4\u4f4f\u4e86\u4e00\u53ea\u5c0f\u9f20\uff0c\u8981\u620f\u800d\u4e2a\u591f\uff0c\u624d\u5206\u800c\u541e\u566c\u3002 \u3000\u3000\u5f90\u94ee\u7ea2\u4e86\u53cc\u773c\uff0c\u53cc\u81c2\u6325\u821e\uff0c\u62db\u62db\u90fd\u662f\u62da\u547d\u7684\u62f3\u5f0f\uff0c\u4f46\u90a3\u8001\u8005\u96f7\u9707\u6321\u7684\u94c1\u67c4\u957f\u903e\u56db\u5c3a\uff0c\u5f90\u94ee\u5982\u4f55\u6b3a\u5f97\u8fd1\u8eab\u53bb\uff1f\u6570\u62db\u4e4b\u95f4\uff0c\u53ea\u542c\u5f97\u55e4\u7684\u4e00\u58f0\u54cd\uff0c\u96f7\u9707\u6321\u7684\u5c16\u7aef\u5212\u7834\u4e86\u5f90\u94ee\u88e4\u811a\uff0c\u5927\u817f\u4e0a\u9c9c\u8840\u957f\u6d41\uff0c\u63a5\u7740\u53c8\u662f\u4e00\u54cd\uff0c\u5f90\u94ee\u5de6\u81c0\u4e2d\u6321\u3002\u90a3\u8001\u8005\u62ac\u8d77\u4e00\u817f\uff0c\u5c06\u4ed6\u8e22\u7ffb\u5728\u5730\uff0c\u4e00\u811a\u8e0f\u4f4f\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u8981\u4f60\u6027\u547d\uff0c\u53ea\u8981\u5e9f\u4e86\u4f60\u7684\u4e00\u5bf9\u62db\u5b50\uff0c\u7f5a\u4f60\u4e0d\u751f\u773c\u775b\uff0c\u592a\u4e5f\u80e1\u6d82\u3002\u201d\u5f90\u94ee\u53c8\u662f\u5bb3\u6015\uff0c\u53c8\u662f\u6124\u6012\uff0c\u80f8\u53e3\u6c14\u4e3a\u4e4b\u585e\uff0c\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u9a6c\u6625\u82b1\u53eb\u9053\uff1a\u201c\u4f17\u4f4d\u670b\u53cb\uff0c\u4f60\u4eec\u8981\u9556\u94f6\uff0c\u62ff\u53bb\u4fbf\u662f\u3002\u6211\u4eec\u8ddf\u5404\u4f4d\u5f80\u65e5\u65e0\u51a4\uff0c\u8fd1\u65e5\u65e0\u4ec7\uff0c\u4f55\u5fc5\u8d76\u5c3d\u6740\u7edd\uff1f\u201d\u90a3\u4f7f\u5251\u7684\u5927\u76d7\u7b11\u9053\uff1a\u201c\u9a6c\u59d1\u5a18\uff0c\u4f60\u662f\u597d\u4eba\uff0c\u4e0d\u7528\u591a\u7ba1\u95f2\u4e8b\u3002\u201d \u3000\u3000\u9a6c\u6625\u82b1\u9053\uff1a\u201c\u4ec0\u4e48\u591a\u7ba1\u95f2\u4e8b\uff1f\u4ed6\u662f\u6211\u4e08\u592b\u554a\u3002\u201d\u4f7f\u96f7\u9707\u6321\u7684\u8001\u8005\u9053\uff1a\u201c\u6211\u4eec\u5c31\u662f\u77a7\u7740\u4ed6\u592a\u4e5f\u4e0d\u914d\uff0c\u59d4\u66f2\u4e86\u624d\u8c8c\u53cc\u5168\u7684\u9a6c\u59d1\u5a18\uff0c\u8fd9\u624d\u5343\u91cc\u8fe2\u8fe2\u7684\u8d76\u6765\u3002\u8fd9\u4e2a\u62b1\u4e0d\u5e73\u975e\u6253\u4e0d\u53ef\uff01\u201d \u3000\u3000\u80e1\u6590\u548c\u7a0b\u7075\u7d20\u8d8a\u542c\u8d8a\u662f\u5947\u602a\uff0c\u5747\u60f3\uff1a\u201c\u8fd9\u6279\u5927\u76d7\u5c45\u7136\u6765\u7ba1\u4eba\u5bb6\u592b\u59bb\u7684\u5bb6\u52a1\u4e8b\uff0c\u8fd8\u8bf4\u4ec0\u4e48\u6253\u62b1\u4e0d\u5e73\uff0c\u5f53\u771f\u597d\u7b11\u3002\u201d\u4e24\u4eba\u5bf9\u671b\u4e00\u773c\uff0c\u76ee\u5149\u4e2d\u5747\u542b\u7b11\u610f\u3002 \u3000\u3000\u4fbf\u5728\u6b64\u65f6\uff0c\u90a3\u8001\u8005\u4e3e\u8d77\u96f7\u9707\u6321\uff0c\u6321\u5c16\u5bf9\u51c6\u5f90\u94ee\u53f3\u773c\uff0c\u6233\u4e86\u4e0b\u53bb\u3002\u9a6c\u6625\u82b1\u5927\u53eb\u4e00\u58f0\uff0c\u62a2\u4e0a\u76f8\u6551\uff0c\u547c\u7684\u4e00\u54cd\uff0c\u9a6c\u4e0a\u4e00\u4e2a\u76d7\u4f19\u624b\u4e2d\u82b1\u67aa\u4ece\u7a7a\u523a\u4e0b\uff0c\u5c06\u5979\u62e6\u4f4f\u3002\u4e24\u4e2a\u5c0f\u5b69\u9f50\u53eb\uff1a\u201c\u7238\u7238\uff01\u201d \u3000\u3000\u5411\u5f90\u94ee\u8eab\u8fb9\u5954\u53bb\u3002 \u3000\u3000\u7a81\u7136\u95f4\u4e00\u4e2a\u7070\u5f71\u4e00\u6643\uff0c\u90a3\u8001\u8005\u624b\u8155\u4e0a\u4e00\u9ebb\uff0c\u6025\u5fd9\u7ffb\u6321\u8fce\u654c\uff0c\u624b\u91cc\u84e6\u7136\u95f4\u8f7b\u4e86\uff0c\u539f\u6765\u624b\u4e2d\u5175\u5203\u7adf\u5df2\u4e0d\u77e5\u53bb\u5411\uff0c\u60ca\u6012\u4e2d\u62ac\u8d77\u5934\u6765\uff0c\u53ea\u89c1\u90a3\u7070\u5f71\u8dc3\u4e0a\u9a6c\u80cc\uff0c\u81ea\u5df1\u7684\u72ec\u95e8\u5175\u5203\u96f7\u9707\u6321\u5374\u5df2\u7ed9\u4ed6\u62ff\u5728\u624b\u4e2d\u821e\u5f04\uff0c\u767d\u5149\u95ea\u95ea\uff0c\u8f6c\u6210\u4e00\u4e2a\u5706\u5708\u3002 \u3000\u3000\u5982\u6b64\u500f\u6765\u500f\u53bb\uff0c\u4e00\u77ac\u4e4b\u95f4\u4e0b\u9a6c\u4e0a\u9a6c\uff0c\u7a7a\u624b\u593a\u4e86\u4ed6\u96f7\u9707\u6321\u7684\uff0c\u6b63\u662f\u80e1\u6590\uff01 \u3000\u3000\u4f17\u76d7\u76f8\u987e\u9a87\u7136\uff0c\u9877\u523b\u95f4\u5bc2\u9759\u65e0\u58f0\uff0c\u7adf\u65e0\u4e00\u4eba\u8bf4\u8bdd\uff0c\u4eba\u4eba\u5747\u4e3a\u773c\u524d\u4e4b\u4e8b\u60ca\u5f97\u5446\u4e86\u3002\u8fc7\u4e86\u534a\u664c\uff0c\u5404\u4eba\u624d\u7eb7\u7eb7\u547c\u559d\uff0c\u4e3e\u5200\u633a\u6756\uff0c\u5954\u5411\u80e1\u6590\u3002 \u3000\u3000\u80e1\u6590\u5927\u53eb\u9053\uff1a\u201c\u662f\u7ebf\u4e0a\u7684\u5408\u5b57\u513f\u5417\uff1f\u98ce\u7d27\uff0c\u626f\u547c\uff0c\u8001\u7a91\u91cc\u6765\u4e86\u82b1\u95e8\u7684\uff0c\u4e09\u5200\u5154\u513f\u7237\u6362\u7740\u8d70\uff0c\u54b1\u4eec\u80e1\u5b50\u4e0a\u5f00\u6d1e\uff0c\u8d22\u795e\u83e9\u8428\u4e0a\u5c71\uff01\u201d\u7fa4\u76d7\u53c8\u662f\u4e00\u6014\uff0c\u542c\u4ed6\u8bf4\u7684\u9ed1\u8bdd\u4e0d\u50cf\u9ed1\u8bdd\uff0c\u4e0d\u77e5\u778e\u626f\u4e9b\u4ec0\u4e48\u3002 \u3000\u3000\u90a3\u96f7\u9707\u6321\u88ab\u593a\u7684\u8001\u8005\u6012\u9053\uff1a\u201c\u670b\u53cb\uff0c\u4f60\u662f\u54ea\u4e00\u8def\u7684\uff0c\u6765\u6405\u8fd9\u6dcc\u6d51\u6c34\u5e72\u4e48\uff1f\u201d \u3000\u3000\u80e1\u6590\u9053\uff1a\u201c\u5144\u5f1f\u4e13\u505a\u6ca1\u672c\u94b1\u4e70\u5356\uff0c\u597d\u5bb9\u6613\u8ddf\u4e0a\u4e86\u98de\u9a6c\u9556\u5c40\u7684\u4e5d\u5343\u4e24\u94f6\u5b50\uff0c\u6ca1\u60f3\u5230\u534a\u8def\u91cc\u6740\u51fa\u6765\u5341\u516d\u4e2a\u7a0b\u54ac\u91d1\u3002\u5404\u4f4d\u8981\u5206\u4e00\u4efd\uff0c\u8fd9\u4e0d\u53eb\u4eba\u5fc3\u75bc\u4e48\uff1f\u201d\u90a3\u8001\u8005\u51b7\u7b11\u9053\uff1a\u201c\u54fc\uff0c\u670b\u53cb\u522b\u88c5\u849c\u5566\uff0c\u8d81\u65e9\u7559\u4e0b\u4e2a\u4e07\u513f\u6765\u662f\u6b63\u7ecf\u3002\u201d \u3000\u3000\u5f90\u94ee\u4e8e\u5343\u94a7\u4e00\u53d1\u4e4b\u9645\u9003\u5f97\u4e86\u6027\u547d\uff0c\u6402\u4f4f\u4e86\u4e24\u4e2a\u513f\u5b50\u3002\u9a6c\u6625\u82b1\u7ad9\u5728\u4ed6\u7684\u8eab\u65c1\uff0c\u7741\u7740\u4e00\u53cc\u5927\u773c\u671b\u4f4f\u80e1\u6590\uff0c\u4e00\u65f6\u4e4b\u95f4\u8fd8\u4e0d\u660e\u767d\u773c\u524d\u5230\u5e95\u53d1\u751f\u4e86\u4f55\u4e8b\u3002\u5979\u53ea\u9053\u80e1\u6590\u548c\u7a0b\u7075\u7d20\u4e5f\u5fc5\u90fd\u662f\u76d7\u4f19\u4e00\u8def\uff0c\u90a3\u77e5\u4ed6\u5374\u548c\u90a3\u8001\u8005\u4e89\u4e86\u8d77\u6765\u3002 \u3000\u3000\u53ea\u89c1\u80e1\u6590\u4f38\u624b\u4e00\u62b9\u4e0a\u5507\u7684\u5c0f\u80e1\u5b50\uff0c\u54ac\u7740\u70df\u888b\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u6211\u8ddf\u4f60\u5b9e\u8bf4\u4e86\u7f62\u3002\u795e\u62f3\u65e0\u654c\u9a6c\u884c\u7a7a\u662f\u6211\u5e08\u5f1f\uff0c\u5e08\u4f84\u7684\u4e8b\u513f\uff0c\u8001\u4eba\u5bb6\u4e0d\u80fd\u4e0d\u7ba1\u3002\u201d \u3000\u3000\u80e1\u6590\u6b64\u8bed\u4e00\u51fa\uff0c\u9a6c\u6625\u82b1\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u60f3\uff1a\u201c\u54ea\u91cc\u51fa\u6765\u4e86\u8fd9\u6837\u4e00\u4e2a\u5e08\u4f2f\uff1f\u6211\u4ece\u6ca1\u542c\u7239\u7239\u8bf4\u8fc7\uff0c\u800c\u4e14\u8fd9\u4eba\u5e74\u7eaa\u6bd4\u7239\u7239\u8f7b\u5f97\u591a\uff0c\u54ea\u80fd\u662f\u5e08\u4f2f\uff1f\u201d \u3000\u3000\u7a0b\u7075\u7d20\u5728\u4e00\u65c1\u89c1\u4ed6\u88c5\u8154\u4f5c\u52bf\uff0c\u5fcd\u4e0d\u4f4f\u8981\u7b11\u51fa\u58f0\u6765\uff0c\u4f46\u89c1\u4ed6\u5927\u654c\u5f53\u524d\uff0c\u8eab\u5728\u91cd\u56f4\uff0c\u4ecd\u80fd\u6f2b\u4e0d\u5728\u610f\u7684\u8a00\u7b11\u81ea\u82e5\uff0c\u5374\u4e5f\u4e0d\u7981\u4f69\u670d\u4ed6\u7684\u80c6\u8272\u3002 \u3000\u3000\u90a3\u8001\u8005\u5c06\u4fe1\u5c06\u7591\uff0c\u54fc\u7684\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u5c0a\u9a7e\u662f\u9a6c\u8001\u9556\u5934\u7684\u5e08\u5144\uff1f\u5e74\u5c81\u4e0d\u50cf\u554a\uff0c\u6211\u4eec\u4e5f\u6ca1\u542c\u8bf4\u9a6c\u8001\u9556\u5934\u6709\u4ec0\u4e48\u5e08\u5144\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u6211\u95e8\u4e2d\u53ea\u7ba1\u5165\u95e8\u5148\u540e\uff0c\u4e0d\u7ba1\u5e74\u7eaa\u5927\u5c0f\u3002\u9a6c\u884c\u7a7a\u662f\u4ec0\u4e48\u5927\u4eba\u7269\u4e86\uff0c\u8fd8\u7528\u5f97\u7740\u5192\u5145\u4ed6\u5e08\u5144\u4e48\uff1f\u201d \u3000\u3000\u5148\u5165\u5e08\u95e8\u4e3a\u5c0a\u7684\u89c4\u77e9\uff0c\u6b66\u6797\u4e2d\u8bb8\u591a\u95e8\u6d3e\u539f\u90fd\u662f\u6709\u7684\u3002\u90a3\u8001\u8005\u5411\u9a6c\u6625\u82b1\u671b\u4e86\u4e00\u773c\uff0c\u5bdf\u770b\u5979\u7684\u8138\u8272\uff0c\u8f6c\u5934\u53c8\u95ee\u80e1\u6590\u9053\uff1a\u201c\u6ca1\u8bf7\u6559\u5c0a\u9a7e\u7684\u4e07\u513f\u3002\u201d\u80e1\u6590\u62ac\u5934\u5411\u5929\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5e08\u5f1f\u53eb\u795e\u62f3\u65e0\u654c\u9a6c\u884c\u7a7a\uff0c\u533a\u533a\u5728\u4e0b\u4fbf\u53eb\u6b6a\u62f3\u6709\u654c\u725b\u8015\u7530\u3002\u201d\u7fa4\u76d7\u4e00\u542c\uff0c\u5c3d\u7686\u5927\u7b11\u3002 \u3000\u3000\u8fd9\u4e00\u53e5\u8bdd\u660e\u663e\u662f\u6b3a\u4eba\u7684\u5047\u8bdd\uff0c\u90a3\u8001\u8005\u53ea\u56e0\u4ed6\u7a7a\u624b\u593a\u4e86\u81ea\u5df1\u7684\u5175\u5203\uff0c\u624d\u8ddf\u4ed6\u5bf9\u7b54\u4e86\u8fd9\u4e00\u9635\u5b50\u8bdd\uff0c\u5426\u5219\u65e9\u5c31\u51fa\u624b\u4e86\u3002\u4ed6\u6027\u5b50\u672c\u4fbf\u8e81\u6025\uff0c\u542c\u5230\u201c\u725b\u8015\u7530\u201d\u8fd9\u4e09\u5b57\uff0c\u518d\u4e5f\u5fcd\u8010\u4e0d\u4f4f\uff0c\u864e\u543c\u4e00\u58f0\uff0c\u4fbf\u5411\u80e1\u6590\u6251\u6765\u3002 \u3000\u3000\u80e1\u6590\u52d2\u9a6c\u4e00\u95ea\uff0c\u96f7\u9707\u6321\u4e00\u6643\uff0c\u90a3\u8001\u8005\u624b\u4e2d\u500f\u5730\u591a\u4e86\u4e00\u7269\uff0c\u4e3e\u624b\u4e00\u770b\uff0c\u5374\u4e0d\u662f\u96f7\u9707\u6321\u662f\u4ec0\u4e48\uff1f\u7269\u5f52\u539f\u4e3b\uff0c\u4ed6\u672c\u8be5\u559c\u6b22\uff0c\u7136\u800c\u8fd9\u5175\u5203\u5e76\u975e\u81ea\u5df1\u593a\u56de\uff0c\u5374\u662f\u5bf9\u65b9\u585e\u5165\u81ea\u5df1\u624b\u4e2d\uff0c\u77a7\u4e5f\u6ca1\u77a7\u6e05\uff0c\u83ab\u540d\u5176\u5999\u7684\u4fbf\u5f97\u56de\u4e86\u5175\u5203\u3002 \u3000\u3000\u4f17\u76d7\u9f50\u58f0\u559d\u5f69\uff0c\u53eb\u9053\uff1a\u201c\u891a\u5927\u54e5\u597d\u672c\u4e8b\uff01\u201d\u90fd\u9053\u662f\u4ed6\u4ee5\u7a7a\u624b\u5165\u767d\u5203\u7684\u529f\u592b\u62a2\u56de\u3002\u8fd9\u59d3\u891a\u7684\u8001\u8005\u5374\u81ea\u77e5\u6ee1\u4e0d\u662f\u90a3\u56de\u4e8b\uff0c\u5f53\u771f\u662f\u54d1\u5b50\u5403\u9ec4\u8fde\uff0c\u8bf4\u4e0d\u51fa\u7684\u82e6\u3002\u4ed6\u5fae\u5fae\u4e00\u6014\uff0c\u8bf4\u9053\uff1a\u201c\u5c0a\u9a7e\u63d2\u624b\u7ba1\u8fd9\u6863\u5b50\u4e8b\uff0c\u5230\u5e95\u4e3a\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u80e1\u6590\u9053\uff1a\u201c\u8001\u5144\u5012\u8bf7\u5148\u8bf4\u8bf4\uff0c\u6211\u8fd9\u4e24\u4e2a\u5e08\u4f84\u597d\u597d\u4e00\u5bf9\u592b\u59bb\uff0c\u4f55\u4ee5\u8981\u5404\u4f4d\u6765\u6253\u62b1\u4e0d\u5e73\uff1f\u201d\u90a3\u8001\u8005\u8bf4\u9053\uff1a\u201c\u591a\u7ba1\u95f2\u4e8b\uff0c\u4e8e\u5c0a\u9a7e\u65e0\u76ca\u3002\u6211\u597d\u8a00\u76f8\u529d\uff0c\u8fd8\u662f\u5404\u884c\u5404\u8def\u7f62\uff01\u201d\u4f17\u76d7\u5747\u611f\u8be7\u5f02\uff1a\u201c\u891a\u5927\u54e5\u5e73\u65e5\u591a\u4e48\u9739\u96f3\u706b\u7206\u7684\u6027\u513f\uff0c\u4eca\u65e5\u5c45\u7136\u8fd9\u822c\u6c89\u5f97\u4f4f\u6c14\u3002\u201d \u3000\u3000\u80e1\u6590\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u8bdd\u518d\u5bf9\u4e5f\u6ca1\u6709\u4e86\uff0c\u591a\u7ba1\u95f2\u4e8b\u65e0\u76ca\u3002\u54b1\u4eec\u5927\u4f19\u513f\u5404\u884c\u5404\u8def\u3002\u8bf7\u554a\uff0c\u8bf7\u554a\uff01\u201d\u90a3\u8001\u8005\u9000\u540e\u4e09\u6b65\uff0c\u559d\u9053\uff1a\u201c\u4f60\u65e2\u4e0d\u542c\u826f\u8a00\uff0c\u5728\u4e0b\u8feb\u5f97\u8981\u9886\u6559\u9ad8\u62db\u3002\u201d\u8bf4\u7740\u96f7\u9707\u6321\u4e00\u4e3e\uff0c\u62a4\u4f4f\u4e86\u80f8\u53e3\u3002 \u3000\u3000\u80e1\u6590\u9053\uff1a\u201c\u5355\u6253\u72ec\u6597\uff0c\u6709\u4ec0\u4e48\u5473\u9053\uff1f\u53ef\u662f\u4eba\u592a\u591a\u4e86\uff0c\u4e71\u7cdf\u7cdf\u7684\u4e5f\u4e0d\u5927\u65b9\u4fbf\u3002\u8fd9\u6837\u5427\uff0c\u6211\u725b\u8015\u7530\u4e00\u4eba\uff0c\u6597\u6597\u4f60\u4eec\u4e09\u4f4d\u3002\u201d \u3000\u3000\u8bf4\u7740\u63d0\u65f1\u70df\u7ba1\u5411\u90a3\u4f7f\u957f\u5251\u7684\u4e00\u6307\uff0c\u53c8\u5411\u90a3\u8001\u8005\u7684\u5e08\u5f1f\u4e00\u6307\u3002 \u3000\u3000\u90a3\u4f7f\u5251\u7684\u76f8\u8c8c\u82f1\u633a\uff0c\u795e\u60c5\u50b2\u6162\uff0c\u4ef0\u5929\u7b11\u9053\uff1a\u201c\u597d\u72c2\u5984\u7684\u8001\u5c0f\u5b50\uff01\u201d\u90a3\u59d3\u891a\u7684\u8001\u8005\u5374\u65e9\u77e5\u80e1\u6590\u51b3\u975e\u6613\u4e0e\u4e4b\u8f88\uff0c\u4e00\u5bf9\u4e00\u7684\u8ddf\u4ed6\u52a8\u624b\uff0c\u4e5f\u771f\u6ca1\u628a\u63e1\uff0c\u4ed6\u65e2\u81ea\u613f\u5411\u4e09\u4eba\u6311\u6218\uff0c\u6b63\u662f\u6c42\u4e4b\u4e0d\u5f97\uff0c\u8bf4\u9053\uff1a\u201c\u8042\u8d24\u5f1f\uff0c\u4e0a\u5b98\u5e08\u5f1f\uff0c\u4ed6\u662f\u81ea\u53d6\u5176\u6b7b\uff0c\u6028\u4e0d\u5f97\u65c1\u4eba\uff0c\u54b1\u4e09\u4e2a\u4fbf\u4e00\u9f50\u966a\u4ed6\u73a9\u73a9\u3002\u201d \u3000\u3000\u90a3\u59d3\u8042\u7684\u5140\u81ea\u4e0d\u613f\uff0c\u8bf4\u9053\uff1a\u201c\u8c05\u8fd9\u8001\u5c0f\u5b50\u600e\u662f\u891a\u5927\u54e5\u7684\u5bf9\u624b\uff1f\u8981\u4e0d\uff0c\u4f60\u5e08\u5144\u5f1f\u4e00\u9f50\u51fa\u9a6c\uff0c\u8ba9\u5927\u4f19\u513f\u77bb\u4ef0\u77bb\u4ef0\u585e\u5916\u2018\u96f7\u7535\u4ea4\u4f5c\u2019\u7684\u7edd\u6280\uff01\u201d\u7fa4\u76d7\u8f70\u7136\u53eb\u597d\u3002 \u3000\u3000\u80e1\u6590\u6447\u5934\u9053\uff1a\u201c\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u4fbf\u8fd9\u822c\u80c6\u5c0f\uff0c\u89c1\u4e0d\u5f97\u5927\u9635\u4ed7\uff0c\u53ef\u60dc\u554a\u53ef\u60dc\u3002\u201d \u3000\u3000\u90a3\u59d3\u8042\u7684\u957f\u7709\u4e00\u6311\uff0c\u8dc3\u4e0b\u9a6c\u6765\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u891a\u5927\u54e5\u8bf7\u8ba9\u4e00\u6b65\uff0c\u5c0f\u5f1f\u72ec\u81ea\u6765\u6559\u8bad\u6559\u8bad\u8fd9\u72c2\u5f92\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u4f60\u8981\u6559\u8bad\u6211\u6b6a\u62f3\u6709\u654c\u725b\u8015\u7530\uff0c\u90a3\u4e5f\u6210\u3002\u53ef\u662f\u54b1\u54e5\u513f\u4e24\u8bdd\u8bf4\u5728\u5148\uff0c\u5018\u82e5\u6211\u725b\u8015\u7530\u8f93\u4e86\uff0c\u4f60\u8981\u5bb0\u8981\u6740\uff0c\u4efb\u51ed\u5904\u7f6e\u3002\u4e0d\u8fc7\u8981\u662f\u5c0f\u5144\u5f1f\u4f60\u6709\u4e00\u4e2a\u5931\u95ea\uff0c\u90a3\u4fbf\u5982\u4f55\uff1f\u201d\u90a3\u59d3\u8042\u7684\u51b7\u7b11\u9053\uff1a\u201c\u90a3\u662f\u4f60\u75f4\u5fc3\u5984\u60f3\u3002\u201d \u3000\u3000\u80e1\u6590\u7b11\u9053\uff1a\u201c\u8bf4\u4e0d\u5b9a\u8001\u5929\u7237\u4fdd\u4f51\uff0c\u5c0f\u5144\u5f1f\u4f60\u7adf\u6709\u4e2a\u4e09\u957f\u4e24\u77ed\uff0c\u4e03\u8364\u516b\u7d20\uff0c\u90a3\u4fbf\u5982\u4f55\uff1f\u201d\u90a3\u59d3\u8042\u7684\u559d\u9053\uff1a\u201c\u8c01\u8ddf\u4f60\u80e1\u8bf4\u516b\u9053\uff1f\u82e5\u6211\u8f93\u4e86\uff0c\u4e5f\u4efb\u51ed\u4f60\u8001\u5c0f\u5b50\u5904\u7f6e\u4fbf\u662f\u3002\u201d \u3000\u3000\u80e1\u6590\u9053\uff1a\u201c\u4efb\u51ed\u6211\u8001\u5c0f\u5b50\u5904\u7f6e\uff0c\u90a3\u53ef\u4e0d\u6562\u5f53\uff0c\u53ea\u662f\u8bf7\u5404\u4f4d\u5bbd\u5b8f\u5927\u91cf\uff0c\u522b\u518d\u6765\u7ba1\u6211\u5e08\u4f84\u5c0f\u592b\u59bb\u4fe9\u7684\u5bb6\u52a1\uff0c\u8fd9\u4e2a\u62b1\u4e0d\u5e73\uff0c\u54b1\u4eec\u5c31\u522b\u6253\u4e86\u5427\uff01\u201d\u90a3\u59d3\u8042\u7684\u597d\u4e0d\u8010\u70e6\uff0c\u957f\u5251\u4e00\u6446\uff0c\u95ea\u8d77\u4e00\u9053\u5bd2\u5149\uff0c\u559d\u9053\uff1a\u201c\u4fbf\u662f\u8fd9\u6837\uff01\u201d \u3000\u3000\u80e1\u6590\u76ee\u5149\u6a2a\u626b\u4f17\u76d7\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u8042\u5bb6\u5c0f\u5144\u5f1f\u7684\u8bdd\uff0c\u4f5c\u4e0d\u4f5c\u51c6\uff1f\u5018\u82e5\u4ed6\u8f93\u4e86\uff0c\u4f60\u4eec\u5404\u4f4d\u5927\u7237\u8fd8\u6253\u4e0d\u6253\u62b1\u4e0d\u5e73\uff1f\u201d \u3000\u3000\u7a0b\u7075\u7d20\u542c\u5230\u8fd9\u91cc\uff0c\u518d\u4e5f\u5fcd\u8010\u4e0d\u4f4f\uff0c\u7ec8\u4e8e\u55e4\u7684\u4e00\u58f0\uff0c\u7b11\u4e86\u51fa\u6765\uff0c\u5fc3\u60f3\u4ed6\u81ea\u5df1\u5c0f\u5c0f\u5e74\u7eaa\uff0c\u5c45\u7136\u53e3\u53e3\u58f0\u58f0\u53eb\u4eba\u5bb6\u201c\u5c0f\u5144\u5f1f\u201d\uff0c\u522b\u4eba\u4e3a\u4e86\u201c\u9c9c\u82b1\u63d2\u5728\u725b\u7caa\u4e0a\u201d\uff0c\u56e0\u800c\u5174\u5e08\u52a8\u4f17\u7684\u6765\u6253\u62b1\u4e0d\u5e73\uff0c\u6b64\u4e8b\u5df2\u5341\u5206\u597d\u7b11\uff0c\u800c\u4ed6\u6a2a\u52a0\u63d2\u624b\uff0c\u53c8\u4e0d\u8bb8\u4eba\u5bb6\u6253\u62b1\u4e0d\u5e73\uff0c\u66f4\u662f\u532a\u5937\u6240\u601d\u3002 \u3000\u3000\u76d7\u4f17\u7d20\u77e5\u90a3\u59d3\u8042\u7684\u5251\u672f\u7cbe\u5947\uff0c\u624b\u4e2d\u90a3\u53e3\u5b9d\u5251\u66f4\u662f\u524a\u94c1\u5982\u6ce5\u7684\u5229\u5203\uff0c\u51fa\u624b\u6597\u8fd9\u4e61\u4e0b\u571f\u8001\u513f\u5c0f\u80e1\u5b50\uff0c\u5b9a\u662f\u6709\u80dc\u65e0\u8d25\u3002\u4f17\u4eba\u6b64\u884c\u539f\u672c\u563b\u563b\u54c8\u54c8\uff0c\u5f53\u4f5c\u4e00\u4ef6\u6781\u6709\u8da3\u7684\u73a9\u95f9\uff0c\u9014\u4e2d\u591a\u751f\u4e8b\u7aef\uff0c\u6b63\u662f\u6c42\u4e4b\u4e0d\u5f97\uff0c\u4e8e\u662f\u7eb7\u7eb7\u8bf4\u9053\uff1a\u201c\u4f60\u5c0f\u80e1\u5b50\u82e5\u662f\u8d62\u4e86\u4e00\u62db\u534a\u5f0f\uff0c\u54b1\u4eec\u5927\u4f19\u513f\u62cd\u5c41\u80a1\u4fbf\u8d70\uff0c\u8fd9\u4e2a\u62b1\u4e0d\u5e73\u662f\u51c6\u5b9a\u4e0d\u6253\u7684\u4e86\uff01\u201d \u3000\u3000\u80e1\u6590\u9053\uff1a\u201c\u8bf8\u4f4d\u8bf4\u7684\u662f\u4eba\u8bdd\uff0c\u5c31\u662f\u8fd9\u4e48\u529e\uff0c\u8fd9\u62b1\u4e0d\u5e73\u6253\u4e0d\u6253\u5f97\u6210\uff0c\u5f97\u77a7\u6211\u5c0f\u80e1\u5b50\u7684\u73a9\u827a\u513f\u884c\u4e0d\u884c\u3002\u770b\u62db\uff01\u201d\u731b\u5730\u4e3e\u8d77\u65f1\u70df\u7ba1\uff0c\u5f80\u81ea\u5df1\u8863\u9886\u4e2d\u4e00\u63d2\uff0c\u8dc3\u4e0b\u9a6c\u6765\uff0c\u4e00\u4e2a\u8e09\u8dc4\uff0c\u9669\u4e9b\u6454\u5012\u3002 \u3000\u3000\u4f17\u4eba\u542c\u4ed6\u4e00\u58f0\u559d\uff1a\u201c\u770b\u62db\uff01\u201d\u53c8\u89c1\u4ed6\u4e3e\u8d77\u70df\u7ba1\uff0c\u90fd\u9053\u4ed6\u8981\u4ee5\u70df\u7ba1\u5f53\u4f5c\u5175\u5668\uff0c\u90a3\u77e5\u4ed6\u7adf\u5c06\u70df\u7ba1\u63d2\u5728\u8863\u9886\u4e4b\u4e2d\uff0c\u53c8\u89c1\u4ed6\u4e0b\u9a6c\u7684\u8eab\u6cd5\u5982\u6b64\u7b28\u62d9\u72fc\u72c8\uff0c\u65c1\u89c2\u7684\u5341\u4e94\u4e2a\u5927\u76d7\u4e4b\u4e2d\uff0c\u5012\u6709\u5341\u4e8c\u4e09\u4eba\u7b11\u4e86\u51fa\u6765\u3002 \u3000\u3000\u90a3\u59d3\u8042\u7684\u559d\u9053\uff1a\u201c\u4f60\u7528\u4ec0\u4e48\u5175\u5203\uff0c\u4eae\u51fa\u6765\u5427\uff01\u201d\u80e1\u6590\u9053\uff1a\u201c\u9ec4\u725b\u8015\u7530\uff0c\u5f97\u7528\u7281\u8019\uff01\u891a\u5927\u5be8\u4e3b\uff0c\u4f60\u624b\u91cc\u8fd9\u4ef6\u5bb6\u4f19\u5012\u50cf\u4e2a\u7281\u8019\uff0c\u501f\u6765\u4f7f\u4f7f\uff01\u201d\u8bf4\u7740\u4f38\u624b\u51fa\u53bb\uff0c\u5411\u90a3\u59d3\u891a\u7684\u8001\u8005\u501f\u90a3\u96f7\u9707\u6321\u3002 \u3000\u3000\u90a3\u8001\u8005\u89c1\u4e86\u4ed6\u4e5f\u771f\u6709\u4e9b\u5fcc\u60ee\uff0c\u5012\u9000\u4e24\u6b65\uff0c\u6012\u9053\uff1a\u201c\u4e0d\u501f\uff01 \u3000\u3000\u8c05\u4f60\u4e5f\u4e0d\u4f1a\u4f7f\uff01\u201d\u80e1\u6590\u53f3\u624b\u624b\u638c\u671d\u5929\uff0c\u59cb\u7ec8\u6446\u7740\u4e2a\u4e5e\u8ba8\u7684\u59ff\u52bf\uff0c\u53c8\u9053\uff1a\u201c\u501f\u4e00\u501f\u4f55\u59a8\uff1f\u201d\u7a81\u7136\u624b\u81c2\u4e00\u957f\u4e00\u642d\uff0c\u90a3\u8001\u8005\u4e3e\u6321\u6b32\u67b6\uff0c\u4e0d\u77e5\u600e\u7684\uff0c\u624b\u4e2d\u5ffd\u7a7a\uff0c\u90a3\u96f7\u9707\u6321\u7adf\u53c8\u5df2\u5230\u4e86\u80e1\u6590\u624b\u4e2d\u3002 \u3000\u3000\u90a3\u8001\u8005\u4e00\u60ca\u975e\u5c0f\uff0c\u5012\u7a9c\u51fa\u4e00\u4e08\u5f00\u5916\uff0c\u8138\u4e0a\u808c\u8089\u62bd\u6410\uff0c\u5982\u89c1\u9b3c\u9b45\u3002 \u3000\u3000\u8981\u77e5\u80e1\u6590\u8fd9\u8def\u7a7a\u624b\u593a\u4eba\u5175\u5203\u7684\u529f\u592b\uff0c\u4e43\u662f\u4ed6\u8fdc\u7956\u98de\u5929\u72d0\u72f8\u6f5c\u5fc3\u94bb\u7814\u51fa\u6765\u7684\u7edd\u6280\u3002\u5f53\u5e74\u98de\u5929\u72d0\u72f8\u8f85\u4f50\u95ef\u738b\u674e\u81ea\u6210\u8d77\u5175\u6253\u5929\u4e0b\uff0c\u51ed\u7740\u8fd9\u624b\u672c\u9886\uff0c\u4e0d\u77e5\u593a\u8fc7\u591a\u5c11\u82f1\u96c4\u597d\u6c49\u624b\u4e2d\u7684\u5175\u5668\uff0c\u5f53\u771f\u662f\u6765\u65e0\u5f71\uff0c\u53bb\u65e0\u8e2a\uff0c\u795e\u51fa\u9b3c\u6ca1\uff0c\u8be1\u79d8\u65e0\u6bd4\uff0c\u201c\u98de\u5929\u72d0\u72f8\u201d\u90a3\u56db\u5b57\u5916\u53f7\uff0c\u4e00\u534a\u4e5f\u662f\u7531\u6b64\u800c\u6765\u3002 \u3000\u3000\u90a3\u59d3\u8042\u58ee\u6c49\u89c1\u80e1\u6590\u624b\u4e2d\u6709\u4e86\u5175\u5668\uff0c\u63d0\u5251\u4fbf\u5f80\u4ed6\u540e\u5fc3\u523a\u6765\u3002 \u3000\u3000\u80e1\u6590\u659c\u8eab\u95ea\u5f00\uff0c\u56de\u4e86\u4e00\u6321\uff0c\u8ddf\u7740\u81ea\u5de6\u4fa7\u62a2\u4e0a\uff0c\u96f7\u9707\u6321\u56de\u63a0\u6a2a\u523a\u3002 \u3000\u3000\u59d3\u891a\u7684\u8001\u8005\u53ea\u77a7\u5f97\u5f20\u5927\u4e86\u53e3\uff0c\u5408\u4e0d\u62e2\u6765\uff0c\u539f\u6765\u80e1\u6590\u6240\u4f7f\u7684\u62db\u6570\uff0c\u7adf\u662f\u4ed6\u5e08\u7236\u4eb2\u6388\u7684\u201c\u516d\u5341\u56db\u8def\u8f70\u5929\u96f7\u9707\u6321\u6cd5\u201d\uff0c\u4e00\u6a21\u4e00\u6837\uff0c\u5168\u65e0\u4e8c\u81f4\u3002\u4ed6\u90a3\u59d3\u4e0a\u5b98\u7684\u5e08\u5f1f\u66f4\u662f\u8be7\u5f02\uff0c\u660e\u660e\u542c\u5f97\u80e1\u6590\u8fde\u96f7\u9707\u6321\u7684\u540d\u5b57\u4e5f\u4e0d\u8bc6\uff0c\u4f7f\u51fa\u6765\u7684\u6321\u6cd5\uff0c\u5374\u548c\u5e08\u54e5\u5168\u7136\u76f8\u540c\u3002\u4ed6\u4e8c\u4eba\u90a3\u60f3\u5f97\u5230\u80e1\u6590\u6b66\u529f\u6839\u5e95\u65e2\u597d\uff0c\u4eba\u53c8\u806a\u660e\u65e0\u6bd4\uff0c\u77a7\u4e86\u90a3\u59d3\u891a\u8001\u8005\u4e0e\u5f90\u94ee\u6253\u6597\uff0c\u65e9\u5c06\u62db\u6570\u8bb0\u5728\u5fc3\u4e2d\u3002\u4f55\u51b5\u4ed6\u6240\u4f7f\u62db\u6570\u867d\u7136\u5f62\u4f3c\uff0c\u5176\u4e2d\u7528\u52b2\u548c\u53d8\u5316\u7684\u8bf8\u822c\u6cd5\u95e8\uff0c\u5374\u7edd\u4e0d\u76f8\u5e72\u3002 \u3000\u3000\u90a3\u59d3\u8042\u7684\u8fd9\u65f6\u518d\u4e5f\u4e0d\u6562\u8f7b\u6162\uff0c\u5251\u8d70\u8f7b\u7075\uff0c\u8eab\u624b\u751a\u662f\u4fbf\u6377\u3002 \u3000\u3000\u80e1\u6590\u6240\u7528\u5175\u5203\u5168\u4e0d\u987a\u624b\uff0c\u517c\u4e4b\u6709\u610f\u7729\u4eba\u8033\u76ee\uff0c\u62db\u62db\u4f9d\u7740\u90a3\u59d3\u891a\u8001\u8005\u7684\u6b66\u529f\u6cd5\u95e8\u800c\u4f7f\uff0c\u66f4\u52a0\u591a\u4e86\u4e00\u5c42\u62d8\u675f\uff0c\u4f46\u89c1\u654c\u4eba\u957f\u5251\u65bd\u5c55\u5f00\u6765\uff0c\u5bd2\u5149\u95ea\u95ea\uff0c\u5251\u6cd5\u5b9e\u975e\u51e1\u4fd7\u3002\u4ed6\u4e00\u9762\u62db\u67b6\uff0c\u5fc3\u4e0b\u5bfb\u601d\uff1a\u201c\u8fd9\u5341\u516d\u4eba\u770b\u6765\u90fd\u662f\u786c\u624b\uff0c\u5018\u82e5\u4e00\u62e5\u800c\u4e0a\uff0c\u6211\u548c\u4e8c\u59b9\u7eb5\u80fd\u8131\u8eab\uff0c\u5f90\u94ee\u4e00\u5bb6\u56db\u53e3\u4e00\u5b9a\u7cdf\u7cd5\uff0c\u53ea\u6709\u6253\u8d25\u4e86\u8fd9\u4eba\uff0c\u6324\u5151\u5f97\u4ed6\u4eec\u4e0d\u80fd\u52a8\u624b\uff0c\u65b9\u662f\u4e0a\u7b56\u3002\u201d\u7a81\u89c1\u5bf9\u624b\u957f\u5251\u4e00\u6c89\uff0c\u77e5\u9053\u4e0d\u5999\uff0c\u5f85\u60f3\u5982\u4f55\u53d8\u62db\uff0c\u5f53\u7684\u4e00\u58f0\uff0c\u96f7\u9707\u6321\u7684\u4e00\u7aef\u5df2\u88ab\u5229\u5251\u524a\u53bb\u3002 \u3000\u3000\u76d7\u4f17\u773c\u89c1\u80e1\u6590\u4e3e\u6b62\u90aa\u95e8\uff0c\u672c\u6765\u5fc3\u4e2d\u5747\u81ea\u5600\u5495\uff0c\u5ffd\u89c1\u90a3\u59d3\u8042\u7684\u5f97\u5229\uff0c\u9f50\u58f0\u6b22\u547c\u3002\u59d3\u8042\u7684\u7cbe\u795e\u4e00\u632f\uff0c\u6b65\u6b65\u8fdb\u903c\u3002\u80e1\u6590\u4ece\u891a\u59d3\u8001\u8005\u90a3\u91cc\u5b66\u5f97\u7684\u51e0\u62db\u6321\u6cd5\uff0c\u582a\u582a\u5df2\u7ecf\u7528\u5b8c\uff0c\u5fc3\u60f3\u518d\u6253\u4e0b\u53bb\u9a6c\u811a\u4fbf\u9732\uff0c\u773c\u89c1\u96f7\u9707\u6321\u88ab\u524a\u53bb\u4e00\u7aef\uff0c\u5fc3\u5ff5\u4e00\u52a8\uff0c\u56de\u6321\u659c\u7838\uff0c\u654c\u4eba\u957f\u5251\u5708\u8f6c\uff0c\u5f53\u7684\u4e00\u58f0\u54cd\uff0c\u53e6\u4e00\u7aef\u4e5f\u524a\u53bb\u4e86\u3002 \u3000\u3000\u80e1\u6590\u53eb\u9053\uff1a\u201c\u597d\uff0c\u4f60\u8fd9\u822c\u4e0d\u7ed9\u891a\u5927\u7237\u9762\u5b50\uff0c\u6bc1\u4e86\u4ed6\u6210\u540d\u7684\u5175\u5203\uff0c\u672a\u514d\u592a\u4e5f\u4e0d\u591f\u670b\u53cb\uff01\u201d \u3000\u3000\u59d3\u8042\u7684\u4e00\u6014\uff0c\u5fc3\u60f3\u8fd9\u8bdd\u5012\u4e5f\u6709\u7406\u3002\u7a81\u7136\u5f53\u7684\u53c8\u662f\u4e00\u54cd\uff0c\u80e1\u6590\u7adf\u5c06\u534a\u622a\u6321\u67c4\u7838\u5230\u4ed6\u5251\u950b\u4e0a\u53bb\uff0c\u624b\u4e2d\u53ea\u4f59\u4e0b\u5c3a\u6765\u957f\u7684\u4e00\u5c0f\u622a\uff0c\u53c8\u542c\u4ed6\u53eb\u9053\uff1a\u201c\u4f1a\u4f7f\u96f7\u9707\u6321\uff0c\u4e0d\u4f7f\u95ea\u7535\u9525\uff0c\u6b66\u529f\u4e5f\u662f\u7a00\u677e\u5e73\u5e38\u3002\u201d\u8bf4\u7740\u5c06\u4e00\u5c0f\u622a\u6321\u67c4\u9012\u51fa\uff0c\u4fbf\u5982\u7834\u7532\u9525\u822c\u4f7f\u4e86\u51fa\u6765\u3002 \u3000\u3000\u59d3\u4e0a\u5b98\u7684\u5927\u76d7\u5148\u542c\u4ed6\u8bf4\u95ea\u7535\u9525\uff0c\u4e0d\u7531\u5f97\u4e00\u60ca\uff0c\u4f46\u77a7\u4e86\u4ed6\u51e0\u8def\u9525\u6cd5\uff0c\u6a2a\u6233\u76f4\u523a\uff0c\u5168\u4e0d\u662f\u90a3\u4e00\u4f1a\u4e8b\uff0c\u8fd9\u624d\u653e\u5fc3\uff0c\u5927\u58f0\u7b11\u9053\uff1a\u201c\u8fd9\u7b97\u90a3\u4e00\u95e8\u5b50\u7684\u95ea\u7535\u9525\uff1f\u201d\u80e1\u6590\u9053\uff1a\u201c\u4f60\u5b66\u7684\u4e0d\u5bf9\uff0c\u6211\u7684\u624d\u5bf9\u3002\u201d\u8bf4\u7740\u8fde\u523a\u6025\u6233\u3002\u5176\u5b9e\u4ed6\u9664\u5355\u5200\u4e4b\u5916\uff0c\u4ec0\u4e48\u5175\u5668\u90fd\u4e0d\u4f1a\u4f7f\uff0c\u8fd9\u95ea\u7535\u9525\u53ea\u662f\u88c5\u6a21\u4f5c\u6837\uff0c\u6240\u5389\u5bb3\u8005\u5168\u5728\u4e00\u53ea\u5de6\u624b\uff0c\u8fd1\u8eab\u800c\u640f\uff0c\u5de6\u624b\u52fe\u6253\u9501\u62ff\uff0c\u5f53\u771f\u662f\u201c\u4e00\u5bf8\u77ed\uff0c\u4e00\u5bf8\u9669\u201d\u3002 \u3000\u3000\u90a3\u59d3\u8042\u7684\u624b\u4e2d\u867d\u6709\u5229\u5251\uff0c\u7adf\u662f\u963b\u6321\u4e0d\u4f4f\uff0c\u88ab\u4ed6\u653b\u5f97\u8fde\u8fde\u5012\u9000\uff0c\u731b\u5730\u91cc\u201c\u554a\u201d\u7684\u4e00\u58f0\u5927\u53eb\uff0c\u4e24\u4eba\u540c\u65f6\u5411\u540e\u8dc3\u5f00\u3002\u53ea\u89c1\u80e1\u6590\u8eab\u524d\u6676\u5149\u95ea\u8000\uff0c\u90a3\u53e3\u5b9d\u5251\u5df2\u5230\u4e86\u4ed6\u7684\u624b\u91cc\u3002 \u3000\u3000\u80e1\u6590\u5de6\u819d\u4e00\u8dea\uff0c\u4ece\u5927\u9053\u65c1\u6293\u8d77\u4e00\u5757\u4e8c\u5341\u6765\u65a4\u7684\u5927\u77f3\uff0c\u53f3\u624b\u6301\u5251\uff0c\u5251\u5c16\u62b5\u5730\uff0c\u5251\u8eab\u6a2a\u659c\uff0c\u5de6\u624b\u9ad8\u4e3e\u5927\u77f3\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u53e3\u5b9d\u5251\u950b\u5229\u5f97\u7d27\uff0c\u6211\u6765\u7838\u5b83\u51e0\u4e0b\uff0c\u77a7\u662f\u7838\u5f97\u65ad\uff0c\u7838\u4e0d\u65ad\uff1f\u201d\u8bf4\u7740\u4f5c\u52bf\u4fbf\u8981\u5c06\u5927\u77f3\u5f80\u5251\u8eab\u4e0a\u7838\u53bb\u3002 \u3000\u3000\u7eb5\u662f\u5929\u4e0b\u6700\u950b\u5229\u7684\u5229\u5251\uff0c\u7528\u5927\u77f3\u7838\u5728\u5b83\u5e73\u677f\u7684\u5251\u8eab\u4e0a\uff0c\u4e5f\u975e\u4e00\u7838\u5373\u65ad\u4e0d\u53ef\u3002\u90a3\u59d3\u8042\u7684\u5bf9\u8fd9\u53e3\u5b9d\u5251\u7231\u5982\u6027\u547d\uff0c\u89c1\u4e86\u8fd9\u822c\u60e8\u72b6\uff0c\u767b\u65f6\u5413\u5f97\u8138\u8272\u82cd\u767d\uff0c\u53eb\u9053\uff1a\u201c\u5728\u4e0b\u8ba4\u8f93\u4fbf\u662f\u3002\u201d \u3000\u3000\u80e1\u6590\u9053\uff1a\u201c\u6211\u77a7\u8fd9\u53e3\u597d\u5251\uff0c\u672a\u5fc5\u4e00\u7838\u4fbf\u65ad\u3002\u201d\u8bf4\u7740\u53c8\u5c06\u5927\u77f3\u4e00\u4e3e\u3002 \u3000\u3000\u90a3\u59d3\u8042\u7684\u53eb\u9053\uff1a\u201c\u5c0a\u9a7e\u82e5\u662f\u559c\u6b22\uff0c\u62ff\u53bb\u4fbf\u662f\uff0c\u522b\u635f\u4f24\u4e86\u5b9d\u7269\u3002\u201d \u3000\u3000\u80e1\u6590\u5fc3\u60f3\u6b64\u4eba\u5012\u662f\u4e2a\u60c5\u79cd\uff0c\u5b81\u53ef\u5251\u5165\u654c\u624b\u4e5f\u4e0d\u613f\u5251\u6bc1\uff0c\u4e8e\u662f\u4e0d\u518d\u5b09\u7b11\uff0c\u53cc\u624b\u6a2a\u6367\u5b9d\u5251\uff0c\u9001\u5230\u4ed6\u8eab\u524d\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u5f1f\u65e0\u793c\uff0c\u591a\u6709\u5f97\u7f6a\u3002\u201d \u3000\u3000\u90a3\u4eba\u5927\u51fa\u610f\u5916\uff0c\u53ea\u9053\u80e1\u6590\u7eb5\u4e0d\u6bc1\u5251\uff0c\u4e5f\u5fc5\u53d6\u53bb\uff0c\u8981\u77e5\u5982\u6b64\u5229\u5203\uff0c\u5f53\u4e16\u7f55\u89c1\uff0c\u6709\u6b64\u4e00\u5251\uff0c\u5e73\u6dfb\u4e86\u4e00\u500d\u529f\u592b\uff0c\u6b66\u6797\u4e2d\u4eba\u6709\u8c01\u4e0d\u7231\uff1f\u5f53\u4e0b\u4e5f\u4f38\u53cc\u624b\u63a5\u8fc7\uff0c\u8bf4\u9053\uff1a\u201c\u591a\u8c22\uff0c\u591a\u8c22\uff01\u201d\u60f6\u6050\u4e4b\u4e2d\uff0c\u63a9\u4e0d\u4f4f\u6ee1\u8138\u7684\u559c\u51fa\u671b\u5916\u4e4b\u60c5\u3002 \u3000\u3000\u80e1\u6590\u77e5\u9053\u591c\u957f\u68a6\u591a\uff0c\u4e0d\u80fd\u518d\u803d\uff0c\u7ffb\u8eab\u4e0a\u9a6c\uff0c\u5411\u7fa4\u76d7\u62f1\u624b\u9053\uff1a\u201c\u627f\u8499\u9ad8\u62ac\u8d35\u624b\uff0c\u5144\u5f1f\u8fd9\u91cc\u8c22\u8fc7\u3002\u201d\u8fd9\u53e5\u8bdd\u5374\u8bf4\u5f97\u751a\u662f\u8bda\u6073\u3002\u5411\u5f90\u94ee\u548c\u9a6c\u6625\u82b1\u53eb\u9053\uff1a\u201c\u8d70\u5427\uff01\u201d\u5f90\u94ee\u592b\u5987\u60ca\u9b42\u672a\u5b9a\uff0c\u8d76\u7740\u9556\u8f66\uff0c\u7eb5\u9a6c\u4fbf\u8d70\u3002\u80e1\u6590\u548c\u7a0b\u7075\u7d20\u5728\u540e\u62bc\u961f\uff0c\u6ca1\u518d\u5411\u540e\u591a\u671b\u4e00\u773c\uff0c\u4ee5\u514d\u53c8\u751f\u4e8b\u7aef\uff0c\u8033\u542c\u5f97\u7fa4\u76d7\u4f4e\u58f0\u8bae\u8bba\uff0c\u5374\u4e0d\u7eb5\u9a6c\u6765\u8ffd\u3002 \u3000\u3000\u56db\u4eba\u4e00\u53e3\u6c14\u9a70\u51fa\u5341\u4f59\u91cc\uff0c\u59cb\u7ec8\u4e0d\u89c1\u6709\u76d7\u4f19\u8ffd\u6765\u3002 \u3000\u3000\u5f90\u94ee\u52d2\u4f4f\u9a6c\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u5c0a\u9a7e\u51fa\u624b\u76f8\u6551\uff0c\u5728\u4e0b\u751a\u662f\u611f\u6fc0\uff0c\u5374\u4f55\u4ee5\u8981\u5192\u5145\u5728\u4e0b\u7684\u5e08\u4f2f\uff1f\u201d\u80e1\u6590\u542c\u4ed6\u8bed\u6c14\u4e2d\u751a\u6709\u602a\u8d23\u4e4b\u610f\uff0c\u5fae\u7b11\u9053\uff1a\u201c\u987a\u53e3\u8bf4\u8bf4\u800c\u5df2\uff0c\u5144\u5f1f\u4e0d\u8981\u89c1\u602a\u3002\u201d\u5f90\u94ee\u9053\uff1a\u201c\u5c0a\u9a7e\u8d34\u4e0a\u8fd9\u4e24\u6487\u80e1\u5b50\uff0c\u9022\u4eba\u4fbf\u53eb\u5144\u5f1f\uff0c\u4e5f\u672a\u514d\u628a\u5929\u4e0b\u4eba\u90fd\u77a7\u5c0f\u4e86\u3002\u201d \u3000\u3000\u80e1\u6590\u4e00\u6115\uff0c\u6ca1\u60f3\u5230\u8fd9\u4e2a\u83bd\u649e\u4e4b\u4eba\uff0c\u7adf\u4f1a\u77a7\u5f97\u51fa\u6765\u3002\u7a0b\u7075\u7d20\u4f4e\u58f0\u9053\uff1a\u201c\u5b9a\u662f\u4ed6\u59bb\u5b50\u77a7\u51fa\u4e86\u7834\u7efd\u3002\u201d \u3000\u3000\u80e1\u6590\u7565\u4e00\u70b9\u5934\uff0c\u51dd\u89c6\u9a6c\u6625\u82b1\uff0c\u5fc3\u60f3\u5979\u77a7\u51fa\u6211\u80e1\u5b50\u662f\u5047\u88c5\uff0c\u5374\u4e0d\u77e5\u662f\u5426\u8ba4\u51fa\u4e86\u6211\u662f\u8c01\u3002 \u3000\u3000\u5f90\u94ee\u89c1\u4e86\u4ed6\u8fd9\u526f\u795e\u60c5\uff0c\u53ea\u9053\u81ea\u5df1\u59bb\u5b50\u751f\u5f97\u7f8e\u4e3d\uff0c\u80e1\u6590\u9014\u4e2d\u7d27\u7d27\u8ddf\u968f\uff0c\u65e9\u4fbf\u4e0d\u6000\u597d\u610f\u3002\u4ed6\u88ab\u76d7\u515a\u620f\u5f04\u4fae\u8fb1\u4e86\u4e2a\u591f\uff0c\u5df2\u5b58\u5fc5\u6b7b\u4e4b\u610f\uff0c\u5fc3\u795e\u5931\u5e38\uff0c\u653e\u773c\u4f46\u89c9\u4eba\u4eba\u662f\u654c\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u9601\u4e0b\u6b66\u827a\u9ad8\u5f3a\uff0c\u4f60\u8981\u6740\u6211\uff0c\u8fd9\u4fbf\u4e0a\u5427\uff01\u201d\u8bf4\u7740\u4e00\u5f2f\u8170\uff0c\u5c31\u4ece\u8d9f\u5b50\u624b\u7684\u8170\u95f4\u62d4\u51fa\u5355\u5200\uff0c\u7acb\u9a6c\u6a2a\u5200\uff0c\u5411\u7740\u80e1\u6590\u51db\u7136\u50b2\u89c6\u3002 \u3000\u3000\u80e1\u6590\u4e0d\u660e\u4ed6\u7684\u5fc3\u610f\uff0c\u6b32\u5f85\u89e3\u91ca\uff0c\u5ffd\u89c9\u80cc\u540e\u9a6c\u8e44\u58f0\u6025\uff0c\u4e00\u9a91\u5feb\u9a6c\u72c2\u5954\u800c\u81f3\u3002\u8fd9\u5339\u9a6c\u867d\u65e0\u8881\u7d2b\u8863\u90a3\u767d\u9a6c\u7684\u795e\u9a8f\uff0c\u5374\u4e5f\u662f\u5c11\u6709\u7684\u540d\u9a79\uff0c\u7247\u523b\u95f4\u4fbf\u4ece\u9556\u961f\u65c1\u63a0\u8fc7\u3002\u80e1\u6590\u4e00\u77a5\u4e4b\u4e0b\uff0c\u8ba4\u5f97\u9a6c\u4e0a\u4e58\u5ba2\u4fbf\u662f\u5341\u516d\u76d7\u4f19\u4e4b\u4e00\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u54b1\u4eec\u8d70\u5427\uff0c\u72af\u4e0d\u7740\u591a\u7ba1\u95f2\u4e8b\uff0c\u6253\u62b1\u4e0d\u5e73\u3002\u201d\u5c82\u77e5\u201c\u591a\u7ba1\u95f2\u4e8b\uff0c\u6253\u62b1\u4e0d\u5e73\u201d\u8fd9\u516b\u4e2a\u5b57\uff0c\u6b63\u89e6\u52a8\u5f90\u94ee\u7684\u5fcc\u8bb3\uff0c\u4ed6\u773c\u4e2d\u5982\u8981\u55b7\u51fa\u706b\u6765\uff0c\u4fbf\u8981\u7eb5\u9a6c\u4e0a\u524d\u76f8\u62da\u3002\u9a6c\u6625\u82b1\u6025\u53eb\uff1a\u201c\u5e08\u54e5\uff0c\u4f60\u53c8\u72af\u80e1\u6d82\u5566\uff01\u201d\u5f90\u94ee\u4e00\u5446\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u4e00\u63d0\u9a6c\u7f30\uff0c\u8ddf\u7740\u4f38\u9a6c\u97ad\u5728\u80e1\u6590\u7684\u5750\u9a91\u81c0\u4e0a\u62bd\u4e86\u4e00\u97ad\uff0c\u4e24\u5339\u9a6c\u5411\u5317\u6025\u9a70\u800c\u53bb\u3002\u80e1\u6590\u56de\u5934\u53eb\u9053\uff1a\u201c\u9a6c\u59d1\u5a18\uff0c\u53ef\u8bb0\u5f97\u5546\u5bb6\u5821\u4e48\uff1f\u201d \u3000\u3000\u9a6c\u6625\u82b1\u6597\u7136\u95f4\u6ee1\u8138\u901a\u7ea2\uff0c\u5583\u5583\u9053\uff1a\u201c\u5546\u5bb6\u5821\uff0c\u5546\u5bb6\u5821\uff01\u6211\u600e\u80fd\u4e0d\u8bb0\u5f97\uff1f\u201d\u5979\u5fc3\u6447\u795e\u9a70\uff0c\u601d\u5ff5\u5f80\u4e8b\uff0c\u4f46\u8111\u6d77\u4e2d\u534a\u5206\u4e5f\u6ca1\u51fa\u73b0\u80e1\u6590\u7684\u5f71\u5b50\u3002\u5979\u662f\u5728\u60f3\u7740\u53e6\u5916\u4e00\u4e2a\u4eba\uff0c\u90a3\u4e2a\u534e\u8d35\u6e29\u96c5\u7684\u516c\u5b50\u7237\u2026\u2026 \u3000\u3000\u80e1\u7a0b\u4e8c\u4eba\u7eb5\u9a6c\u5954\u51fa\u4e09\u56db\u91cc\uff0c\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u5927\u54e5\uff0c\u6253\u62b1\u4e0d\u5e73\u7684\u53c8\u8ffd\u4e0a\u6765\u5566\u3002\u201d\u80e1\u6590\u4e5f\u65e9\u5df2\u542c\u5230\u6765\u8def\u4e0a\u9a6c\u8e44\u6742\u6c93\uff0c\u5171\u6709\u5341\u4f59\u9a91\u4e4b\u591a\uff0c\u8bf4\u9053\uff1a\u201c\u5f53\u771f\u52a8\u624b\uff0c\u54b1\u4eec\u5be1\u4e0d\u654c\u4f17\uff0c\u53c8\u4e0d\u77e5\u8fd9\u6279\u4eba\u662f\u4ec0\u4e48\u6765\u5934\u3002\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u6211\u77a7\u8fd9\u4e9b\u4eba\u672a\u5fc5\u4fbf\u771f\u662f\u5f3a\u76d7\u3002\u201d\u80e1\u6590\u70b9\u5934\u9053\uff1a\u201c\u8fd9\u4e2d\u95f4\u53e4\u602a\u5f88\u591a\uff0c\u4e00\u65f6\u53ef\u60f3\u4e0d\u660e\u767d\u3002\u201d \u3000\u3000\u8fd9\u65f6\u4e00\u9635\u897f\u98ce\u5439\u6765\uff0c\u6765\u8def\u4e0a\u4f20\u6765\u4e00\u9635\u91d1\u5203\u76f8\u4ea4\u4e4b\u58f0\u3002\u80e1\u6590\u60ca\u9053\uff1a\u201c\u7ed9\u8ffd\u4e0a\u4e86\u3002\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u6211\u77a7\u90a3\u4e9b\u4eba\u7684\u5fc3\u610f\uff0c\u90a3\u4f4d\u9a6c\u59d1\u5a18\u51b3\u8ba1\u65e0\u788d\uff0c\u4ed6\u4eec\u4e5f\u4e0d\u4f1a\u4f24\u90a3\u5f90\u7237\u7684\u6027\u547d\uff0c\u4e0d\u8fc7\u82e6\u5934\u662f\u514d\u4e0d\u4e86\u8981\u5403\u7684\u4e86\u3002\u201d\u80e1\u6590\u7aed\u529b\u601d\u7d22\uff0c\u76b1\u7709\u9053\uff1a\u201c\u6211\u53ef\u771f\u662f\u4e0d\u660e\u767d\u3002\u201d \u3000\u3000\u5ffd\u542c\u5f97\u9a6c\u8e44\u58f0\u54cd\uff0c\u659c\u523a\u5f80\u897f\u5317\u89d2\u9a70\u53bb\uff0c\u8d70\u7684\u5374\u4e0d\u662f\u5927\u9053\uff0c\u540c\u65f6\u9690\u9690\u53c8\u4f20\u6765\u4e00\u4e2a\u5973\u5b50\u7684\u547c\u559d\u4e4b\u58f0\u3002 \u3000\u3000\u80e1\u6590\u9a70\u9a6c\u4e0a\u4e86\u9053\u65c1\u4e00\u5ea7\u5c0f\u4e18\uff0c\u7eb5\u76ee\u9065\u671b\uff0c\u53ea\u89c1\u4e24\u540d\u76d7\u4f19\u5404\u4e58\u5feb\u9a6c\uff0c\u624b\u81c2\u4e2d\u90fd\u62b1\u7740\u4e00\u4e2a\u5b69\u5b50\u3002\u9a6c\u6625\u82b1\u5f92\u6b65\u8ffd\u8d76\uff0c\u5934\u53d1\u6563\u4e71\uff0c\u4f3c\u4e4e\u5728\u558a\uff1a\u201c\u8fd8\u6211\u5b69\u5b50\uff0c\u8fd8\u6211\u5b69\u5b50\uff01\u201d\u9694\u5f97\u8fdc\u4e86\uff0c\u542c\u4e0d\u6e05\u695a\u3002\u90a3\u4e24\u4e2a\u76d7\u515a\u5175\u5203\u4e00\u4e3e\uff0c\u5ffd\u5730\u5206\u5411\u5de6\u53f3\u9a70\u5f00\u3002\u9a6c\u6625\u82b1\u4e00\u5446\uff0c\u4e24\u4e2a\u5b69\u5b50\u90fd\u662f\u4e00\u822c\u7684\u5fc3\u5934\u4e4b\u8089\uff0c\u4e0d\u77e5\u8be5\u5411\u54ea\u4e00\u4e2a\u8ffd\u8d76\u624d\u662f\u3002 \u3000\u3000\u80e1\u6590\u77a7\u5f97\u5927\u6012\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u4e9b\u76d7\u8d3c\u771f\u662f\u65e0\u6076\u4e0d\u4f5c\u3002\u201d\u53eb\u9053\uff1a\u201c\u4e8c\u59b9\uff0c\u5feb\u6765\uff01\u201d\u660e\u77e5\u5be1\u4e0d\u654c\u4f17\uff0c\u82e5\u662f\u63d2\u624b\uff0c\u6b64\u4e8b\u5b9e\u6781\u51f6\u9669\uff0c\u4f46\u773c\u89c1\u8fd9\u79cd\u4e0d\u5e73\u4e4b\u4e8b\uff0c\u603b\u4e0d\u80fd\u7f6e\u4e4b\u4e0d\u7406\uff0c\u4e8e\u662f\u7eb5\u9a6c\u8ffd\u4e86\u4e0a\u53bb\u3002\u4f46\u76f8\u9694\u65e2\u8fdc\uff0c\u5750\u9a91\u53c8\u6ca1\u76d7\u4f19\u7684\u9a6c\u5feb\uff0c\u5f85\u8ffd\u5230\u9a6c\u6625\u82b1\u8eab\u8fb9\uff0c\u4e24\u4e2a\u5927\u76d7\u65e9\u5df2\u62b1\u7740\u5b69\u5b50\u4e0d\u77e5\u53bb\u5411\u3002\u53ea\u89c1\u9a6c\u6625\u82b1\u5446\u5446\u7ad9\u7740\uff0c\u5374\u4e0d\u54ed\u6ce3\u3002 \u3000\u3000\u80e1\u6590\u53eb\u9053\uff1a\u201c\u9a6c\u59d1\u5a18\u522b\u7740\u6025\uff0c\u6211\u5b9a\u5f53\u52a9\u4f60\u593a\u56de\u5b69\u5b50\u3002\u201d\u5176\u5b9e\u8fd9\u65f6\u201c\u9a6c\u59d1\u5a18\u201d\u65e9\u5df2\u6210\u4e86\u201c\u5f90\u592b\u4eba\u201d\uff0c\u4f46\u5728\u80e1\u6590\u5fc3\u4e2d\uff0c\u4e00\u76f4\u4fbf\u662f\u201c\u9a6c\u59d1\u5a18\u201d\uff0c\u8131\u53e3\u800c\u51fa\uff0c\u5168\u6ca1\u60f3\u5230\u6539\u53e3\u3002 \u3000\u3000\u9a6c\u6625\u82b1\u542c\u4e86\u6b64\u8a00\uff0c\u7cbe\u795e\u4e00\u632f\uff0c\u4fbf\u8981\u8dea\u5c06\u4e0b\u53bb\u3002\u80e1\u6590\u5fd9\u9053\uff1a\u201c\u8bf7\u52ff\u591a\u793c\uff0c\u5f90\u5144\u5462\uff1f\u201d\u9a6c\u6625\u82b1\u9053\uff1a\u201c\u6211\u8ffd\u8d76\u5b69\u5b50\uff0c\u4ed6\u5374\u7ed9\u4eba\u7f20\u4f4f\u4e86\u3002\u201d \u3000\u3000\u7a0b\u7075\u7d20\u9a70\u9a6c\u5954\u5230\u80e1\u6590\u8eab\u8fb9\uff0c\u8bf4\u9053\uff1a\u201c\u5317\u9762\u53c8\u6709\u654c\u4eba\u3002\u201d\u80e1\u6590\u5411\u5317\u671b\u53bb\uff0c\u679c\u89c1\u5c18\u571f\u98de\u626c\uff0c\u53c8\u6709\u516b\u4e5d\u9a91\u5954\u6765\u3002\u80e1\u6590\u9053\uff1a\u201c\u654c\u4eba\u9a91\u7684\u90fd\u662f\u597d\u9a6c\uff0c\u54b1\u4eec\u9003\u4e0d\u8fdc\uff0c\u5f97\u627e\u4e2a\u5730\u65b9\u8eb2\u4e00\u8eb2\u3002\u201d\u6e38\u76ee\u56db\u987e\uff0c\u4e00\u7247\u7a7a\u65f7\uff0c\u5e76\u65e0\u85cf\u8eab\u4e4b\u5904\uff0c\u53ea\u897f\u5317\u89d2\u4e0a\u6709\u4e00\u4e1b\u5c0f\u6811\u6797\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u9a6c\u97ad\u4e00\u6307\uff0c\u9053\uff1a\u201c\u53bb\u90a3\u8fb9\u3002\u201d\u5411\u9a6c\u6625\u82b1\u9053\uff1a\u201c\u4e0a\u9a6c\u5440\uff01\u201d \u3000\u3000\u9a6c\u6625\u82b1\u9053\uff1a\u201c\u591a\u8c22\u59d1\u5a18\uff01\u201d\u8dc3\u4e0a\u9a6c\u80cc\uff0c\u5750\u5728\u5979\u7684\u8eab\u540e\u3002\u7a0b\u7075\u7d20\u7b11\u9053\uff1a\u201c\u4f60\u773c\u5149\u771f\u597d\uff0c\u5371\u6025\u4e2d\u8fd8\u80fd\u77a7\u51fa\u6211\u662f\u5973\u626e\u7537\u88c5\u3002\u201d\u4e09\u4eba\u4e24\u9a91\uff0c\u5411\u6811\u6797\u5954\u53bb\u3002 \u3000\u3000\u53ea\u5954\u51fa\u91cc\u8bb8\uff0c\u76d7\u515a\u4fbf\u5df2\u53d1\u89c9\uff0c\u53ea\u542c\u5f97\u58f0\u58f0\u553f\u54e8\uff0c\u5357\u8fb9\u5341\u4f59\u9a91\uff0c\u5317\u8fb9\u516b\u4e5d\u9a91\uff0c\u4e24\u5934\u56f4\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u80e1\u6590\u4e00\u9a6c\u5f53\u5148\uff0c\u62a2\u5165\u6811\u6797\uff0c\u89c1\u6797\u540e\u5171\u6709\u516d\u4e03\u95f4\u5c0f\u5c4b\uff0c\u5fc3\u60f3\u518d\u5411\u524d\u9003\uff0c\u975e\u7ed9\u8ffd\u4e0a\u4e0d\u53ef\uff0c\u53ea\u6709\u5728\u5c4b\u4e2d\u6682\u907f\u3002\u5954\u5230\u5c4b\u524d\uff0c\u89c1\u4e2d\u95f4\u662f\u5ea7\u8f83\u5927\u7684\u77f3\u5c4b\uff0c\u4e24\u4fa7\u7684\u90fd\u662f\u8305\u820d\u3002\u4ed6\u4f38\u624b\u63a8\u5f00\u77f3\u5c4b\u7684\u677f\u95e8\uff0c\u91cc\u9762\u4e00\u4e2a\u8001\u5987\u4eba\u5367\u75c5\u5728\u5e8a\uff0c\u89c1\u5230\u80e1\u6590\u65f6\u60ca\u5f97\u8bf4\u4e0d\u51fa\u8bdd\u6765\uff0c\u53ea\u662f\u201c\u554a\uff0c\u554a\u201d\u7684\u4f4e\u53eb\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u89c1\u90a3\u4e9b\u8305\u820d\u4e00\u95f4\u95f4\u90fd\u662f\u67f4\u6249\u7d27\u95ed\uff0c\u56db\u58c1\u53c8\u65e0\u7a97\u5b54\uff0c\u770b\u6765\u4e0d\u662f\u4eba\u5c45\u4e4b\u6240\uff0c\u8e22\u5f00\u677f\u95e8\u4e00\u671b\uff0c\u89c1\u5c4b\u4e2d\u5806\u6ee1\u4e86\u67f4\u8349\uff0c\u53e6\u4e00\u95f4\u5374\u5806\u4e86\u8bb8\u591a\u77f3\u5934\u3002\u539f\u6765\u8fd9\u4e9b\u5c4b\u5b50\u662f\u77f3\u7070\u7a91\u8d2e\u79ef\u77f3\u7070\u77f3\u548c\u67f4\u8349\u4e4b\u5904\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u53d6\u51fa\u706b\u647a\uff0c\u6253\u7740\u4e86\u706b\uff0c\u5f80\u4e24\u4fa7\u8305\u820d\u4e0a\u4e00\u70b9\uff0c\u62c9\u7740\u9a6c\u6625\u82b1\u8fdb\u4e86\u77f3\u5c4b\uff0c\u5173\u4e0a\u4e86\u95e8\uff0c\u53c8\u4e0a\u4e86\u95e8\u95e9\u3002 \u3000\u3000\u8fd9\u51e0\u95f4\u8305\u820d\u79bb\u77f3\u5c4b\u7ea6\u6709\u4e09\u56db\u4e08\u8fdc\uff0c\u67f4\u8349\u7740\u706b\u4e4b\u540e\uff0c\u4eba\u5728\u77f3\u5c4b\u4e2d\u867d\u7136\u70bd\u70ed\uff0c\u4f46\u53ef\u5c06\u654c\u4eba\u6321\u5f97\u4e00\u65f6\uff0c\u540c\u65f6\u77f3\u5c4b\u65c1\u7684\u8305\u820d\u5c3d\u6570\u70e7\u5149\uff0c\u654c\u4eba\u65e0\u85cf\u8eab\u4e4b\u5904\uff0c\u8981\u8fdb\u653b\u4fbf\u8f83\u4e0d\u6613\u3002 \u3000\u3000\u9a6c\u6625\u82b1\u89c1\u5979\u5c0f\u5c0f\u5e74\u7eaa\uff0c\u5374\u662f\u5f53\u673a\u7acb\u65ad\uff0c\u4e00\u89c1\u8305\u820d\uff0c\u6beb\u4e0d\u601d\u7d22\u7684\u4fbf\u653e\u4e0a\u4e86\u706b\uff0c\u81ea\u5df1\u5374\u8981\u5f85\u8fdb\u4e86\u77f3\u5c4b\u4e4b\u540e\uff0c\u60f3\u4e86\u4e00\u4f1a\uff0c\u65b9\u59cb\u660e\u767d\u5979\u7684\u7528\u610f\uff0c\u8d5e\u9053\uff1a\u201c\u59d1\u5a18\uff01\u4f60\u597d\u806a\u660e\uff01\u201d \u3000\u3000\u8305\u820d\u706b\u5934\u65b9\u8d77\uff0c\u76d7\u4f17\u5df2\u7eb7\u7eb7\u9a70\u5165\u6811\u6797\uff0c\u9a6c\u5339\u89c1\u4e86\u706b\u5149\uff0c\u4e0d\u6562\u5954\u8fd1\uff0c\u56db\u5468\u56e2\u56e2\u7ad9\u5b9a\u3002 \u3000\u3000\u9a6c\u6625\u82b1\u8fdb\u4e86\u77f3\u5c4b\uff0c\u60ca\u9b42\u7565\u5b9a\uff0c\u5374\u60ac\u5ff5\u513f\u5b50\u843d\u5165\u76d7\u624b\uff0c\u4e0d\u77e5\u6b64\u523b\u662f\u6b7b\u662f\u6d3b\u3002\u5979\u867d\u662f\u8457\u540d\u62f3\u5e08\u4e4b\u5973\uff0c\u81ea\u5e7c\u4fbf\u968f\u7236\u95ef\u8361\u6c5f\u6e56\uff0c\u4e0d\u77e5\u7ecf\u5386\u8fc7\u591a\u5c11\u98ce\u9669\uff0c\u4f46\u7231\u513f\u906d\u63b3\uff0c\u4e0d\u7531\u5f97\u73e0\u6cea\u76c8\u7736\u3002\u5979\u4f38\u8896\u62ed\u4e86\u62ed\u773c\u6cea\uff0c\u5411\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u59b9\u5b50\uff0c\u4f60\u548c\u6211\u7d20\u4e0d\u76f8\u8bc6\uff0c\u4f55\u4ee5\u72af\u9669\u76f8\u6551\uff1f\u201d \u3000\u3000\u8fd9\u4e00\u53e5\u4e5f\u771f\u8be5\u95ee\uff0c\u8981\u77e5\u8fd9\u6279\u5927\u76d7\u4e2a\u4e2a\u6b66\u827a\u9ad8\u5f3a\uff0c\u4eba\u6570\u53c8\u4f17\uff0c\u4fbf\u662f\u5979\u7236\u4eb2\u795e\u62f3\u65e0\u654c\u9a6c\u884c\u7a7a\u4eb2\u81ea\u9047\u4e0a\u4e86\uff0c\u4e5f\u51b3\u8ba1\u62b5\u654c\u4e0d\u4f4f\u3002\u8fd9\u4e24\u4eba\u65e0\u4eb2\u65e0\u6545\uff0c\u7adf\u7136\u5c06\u8fd9\u6869\u4e8b\u62c9\u5728\u81ea\u5df1\u8eab\u4e0a\uff0c\u5c82\u4e0d\u662f\u767d\u767d\u8d54\u4e86\u6027\u547d\uff1f\u81f3\u4e8e\u80e1\u6590\u81ea\u79f0\u201c\u6b6a\u62f3\u6709\u654c\u725b\u8015\u7530\u201d\uff0c\u5979\u81ea\u7136\u77e5\u9053\u662f\u620f\u5f04\u7fa4\u76d7\u4e4b\u8a00\u3002\u5979\u7236\u4eb2\u7684\u6b66\u529f\u662f\u7956\u7236\u6240\u4f20\uff0c\u5e76\u65e0\u540c\u95e8\u5144\u5f1f\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u5fae\u5fae\u4e00\u7b11\uff0c\u6307\u7740\u80e1\u6590\u7684\u80cc\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u8ba4\u5f97\u4ed6\u4e48\uff1f \u3000\u3000\u4ed6\u5374\u8ba4\u5f97\u4f60\u5462\u3002\u201d \u3000\u3000\u80e1\u6590\u6b63\u4ece\u77f3\u5c4b\u7a97\u5b54\u4e2d\u5411\u5916\u5f20\u671b\uff0c\u542c\u5f97\u7a0b\u7075\u7d20\u7684\u8bdd\uff0c\u56de\u5934\u4e00\u7b11\uff0c\u968f\u5373\u8f6c\u8eab\u4f38\u624b\uff0c\u4ece\u7a97\u5b54\u4e2d\u63a5\u4e86\u4e00\u679d\u94a2\u9556\u3001\u4e00\u679d\u7529\u624b\u7bad\u8fdb\u6765\uff0c\u629b\u5728\u5730\u4e0b\uff0c\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u6ca1\u5e26\u6697\u5668\uff0c\u53ea\u597d\u501f\u7528\u4eba\u5bb6\u7684\u4e86\u3002 \u3000\u3000\u4e00\u3001\u4e8c\u3001\u4e09\u3001\u56db\u2026\u2026\u4e94\u3001\u516d\u2026\u2026\u8fd9\u91cc\u5357\u8fb9\u5171\u662f\u516d\u4eba\u3002\u201d\u8f6c\u5230\u53e6\u4e00\u8fb9\u7a97\u5b54\u4e2d\u5f20\u671b\uff0c\u8bf4\u9053\uff1a\u201c\u4e00\u3001\u4e8c\u3001\u4e09\u2026\u2026\u5317\u8fb9\u4e03\u4eba\uff0c\u53ef\u60dc\u4e1c\u897f\u4e24\u9762\u77a7\u4e0d\u89c1\u3002\u201d \u3000\u3000\u56de\u5934\u5411\u5c4b\u4e2d\u4e00\u671b\uff0c\u89c1\u5c4b\u89d2\u780c\u7740\u4e00\u53ea\u77f3\u7076\uff0c\u5fc3\u5ff5\u4e00\u52a8\uff0c\u62ff\u8d77\u7076\u4e0a\u94c1\u9505\uff0c\u53f3\u624b\u63e1\u4f4f\u9505\u8033\uff0c\u5de6\u624b\u62ff\u4e86\u9505\u76d6\uff0c\u7a81\u7136\u4ece\u7a97\u5b54\u4e2d\u63a2\u8eab\u51fa\u53bb\uff0c\u5411\u4e1c\u77a7\u4e86\u4e00\u4f1a\uff0c\u53c8\u5411\u897f\u77a7\u4e86\u4e00\u4f1a\u3002\u8fd9\u4e48\u4e00\u6765\uff0c\u4ed6\u4e0a\u534a\u8eab\u5c3d\u5df2\u9732\u5728\u654c\u4eba\u6697\u5668\u7684\u88ad\u51fb\u4e4b\u4e0b\uff0c\u4f46\u90a3\u94c1\u9505\u548c\u9505\u76d6\u4fbf\u4f3c\u4e24\u9762\u76fe\u724c\uff0c\u62a4\u4f4f\u4e86\u5de6\u53f3\u3002\u53ea\u542c\u5f97\u53ee\u53ee\u5f53\u5f53\u3001\u7684\u7684\u7b03\u7b03\u4e00\u9635\u54cd\u4eae\uff0c\u4ed6\u7f29\u8eab\u8fdb\u7a97\uff0c\u54c8\u54c8\u5927\u7b11\u3002\u53ea\u89c1\u9505\u76d6\u4e0a\u9489\u7740\u56db\u4e94\u4ef6\u6697\u5668\uff0c\u94c1\u9505\u4e2d\u5374\u53c8\u6284\u7740\u4e94\u516d\u4ef6\uff0c\u4ec0\u4e48\u94c1\u83b2\u5b50\u3001\u8896\u7bad\u3001\u98de\u9525\u3001\u4e27\u95e8\u9489\u7b49\u90fd\u6709\u3002\u90a3\u9505\u53e3\u5df2\u7f3a\u4e86\u4e00\u5927\u5757\uff0c\u5374\u662f\u7ed9\u4e00\u5757\u98de\u8757\u77f3\u6253\u7f3a\u4e86\u7684\u3002 \u3000\u3000\u80e1\u6590\u8bf4\u9053\uff1a\u201c\u524d\u540e\u5de6\u53f3\uff0c\u4e00\u5171\u662f\u4e8c\u5341\u4e00\u4eba\u3002\u6211\u6ca1\u77a7\u89c1\u5f90\u5144\u548c\u4e24\u4e2a\u5b69\u5b50\uff0c\u63a8\u60f3\u8d77\u6765\uff0c\u5c1a\u6709\u4e8c\u4eba\u5206\u8eab\u5bf9\u4ed8\u5f90\u5144\uff0c\u6709\u4e24\u4eba\u62b1\u7740\u5b69\u5b50\uff0c\u5bf9\u65b9\u5171\u662f\u4e8c\u5341\u4e94\u4eba\u4e86\u3002\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u4e8c\u5341\u4e94\u4eba\u82e5\u662f\u5e73\u5eb8\u4e4b\u8f88\uff0c\u81ea\u7136\u4e0d\u8db3\u4e3a\u60a3\uff0c\u53ef\u662f\u8fd9\u4e00\u6279\u2026\u2026\u201d\u80e1\u6590\u9053\uff1a\u201c\u4e8c\u59b9\uff0c\u4f60\u53ef\u77e5\u90a3\u4f7f\u96f7\u9707\u6321\u7684\u662f\u4ec0\u4e48\u6765\u5934\uff1f\u201d \u3000\u3000\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u6211\u542c\u5e08\u7236\u8bf4\u8d77\u8fc7\u6709\u8fd9\u4e48\u4e00\u8def\u5916\u95e8\u5175\u5668\uff0c\u8bf4\u9053\u64c5\u4f7f\u96f7\u9707\u6321\u3001\u95ea\u7535\u9525\u7684\uff0c\u90fd\u662f\u585e\u5317\u767d\u5bb6\u5821\u4e00\u6d3e\u3002\u53ef\u662f\u90a3\u4f7f\u5b9d\u5251\u7684\u8fd9\u4eba\uff0c\u5251\u672f\u660e\u660e\u662f\u6d59\u4e1c\u7684\u7941\u5bb6\u5251\u3002\u4e00\u4e2a\u662f\u585e\u5317\uff0c\u4e00\u4e2a\u662f\u6d59\u4e1c\uff0c\u55ef\uff0c\u5927\u54e5\uff0c\u4f60\u542c\u51fa\u4e86\u4ed6\u4eec\u7684\u53e3\u97f3\u4e48\uff1f\u201d \u3000\u3000\u9a6c\u6625\u82b1\u63a5\u53e3\u9053\uff1a\u201c\u662f\u554a\uff0c\u6709\u7684\u662f\u5e7f\u4e1c\u53e3\u97f3\uff0c\u8fd8\u6709\u6e56\u5357\u6e56\u5317\u7684\uff0c\u4e5f\u6709\u5c71\u4e1c\u5c71\u897f\u7684\u3002\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u5929\u4e0b\u51b3\u6ca1\u8fd9\u4e48\u4e00\u7fa4\u76d7\u4f19\uff0c\u4f1a\u5408\u4e86\u56db\u9762\u516b\u65b9\u7684\u8fd9\u8bb8\u591a\u597d\u624b\uff0c\u5374\u6765\u62a2\u52ab\u533a\u533a\u4e5d\u5343\u4e24\u94f6\u5b50\u3002\u201d \u3000\u3000\u9a6c\u6625\u82b1\u542c\u5230\u201c\u533a\u533a\u4e5d\u5343\u4e24\u94f6\u5b50\u201d\u4e00\u53e5\u8bdd\uff0c\u8138\u4e0a\u5fae\u5fae\u4e00\u7ea2\u3002 \u3000\u3000\u98de\u9a6c\u9556\u5c40\u5f00\u8bbe\u4ee5\u6765\uff0c\u7684\u786e\u4ece\u6ca1\u627f\u4fdd\u8fc7\u8fd9\u6837\u4e00\u679d\u5c0f\u9556\u3002 \u3000\u3000\u80e1\u6590\u9053\uff1a\u201c\u4e3a\u4eca\u4e4b\u8ba1\uff0c\u987b\u5f97\u5148\u67e5\u660e\u654c\u4eba\u7684\u6765\u610f\uff0c\u5230\u5e95\u662f\u51b2\u7740\u54b1\u5144\u59b9\u800c\u6765\u5462\uff0c\u8fd8\u662f\u51b2\u7740\u9a6c\u59d1\u5a18\u800c\u6765\u3002\u201d\u4ed6\u521d\u65f6\u89c1\u4e86\u654c\u4eba\u8fd9\u822c\u58f0\u52bf\uff0c\u53ea\u9053\u5b9a\u662f\u7530\u5f52\u519c\u4e00\u8def\uff0c\u4f46\u76d7\u4f19\u7684\u6240\u4f5c\u6240\u4e3a\uff0c\u5374\u5904\u5904\u9488\u5bf9\u7740\u5f90\u94ee\u3001\u9a6c\u6625\u82b1\u592b\u5987\uff0c\u663e\u7136\u53c8\u4e0e\u82d7\u4eba\u51e4\u3001\u7530\u5f52\u519c\u4e00\u4e8b\u65e0\u5173\u3002 \u3000\u3000\u9a6c\u6625\u82b1\u9053\uff1a\u201c\u90a3\u81ea\u7136\u662f\u51b2\u7740\u98de\u9a6c\u9556\u5c40\u3002\u8fd9\u4f4d\u5927\u54e5\u8d35\u59d3\uff1f\u8bf7\u6055\u5c0f\u59b9\u773c\u62d9\u3002\u201d\u80e1\u6590\u4f38\u624b\u6495\u4e0b\u5507\u4e0a\u7c98\u7740\u7684\u80e1\u5b50\uff0c\u7b11\u9053\uff1a\u201c\u9a6c\u59d1\u5a18\uff0c\u4f60\u4e0d\u8ba4\u5f97\u6211\u4e86\u4e48\uff1f\u201d \u3000\u3000\u9a6c\u6625\u82b1\u671b\u7740\u4ed6\u90a3\u5f20\u58ee\u5065\u4e4b\u4e2d\u5fae\u5e26\u7a1a\u6c14\u7684\u8138\uff0c\u770b\u6765\u5e74\u7eaa\u751a\u8f7b\uff0c\u5374\u60f3\u4e0d\u8d77\u66fe\u5728\u90a3\u91cc\u89c1\u8fc7\u3002 \u3000\u3000\u80e1\u6590\u7b11\u9053\uff1a\u201c\u5546\u5c11\u7237\uff0c\u8bf7\u4f60\u53bb\u653e\u4e86\u963f\u6590\uff0c\u522b\u518d\u96be\u4e3a\u4ed6\u4e86\u3002\u201d \u3000\u3000\u9a6c\u6625\u82b1\u4e00\u6014\uff0c\u6a31\u53e3\u5fae\u5f20\uff0c\u5374\u65e0\u8bdd\u8bf4\u3002\u80e1\u6590\u53c8\u9053\uff1a\u201c\u963f\u6590\u7ed9\u4f60\u540a\u7740\uff0c\u591a\u53ef\u601c\u7684\uff0c\u4f60\u5148\u53bb\u653e\u4e86\u4ed6\uff0c\u6211\u518d\u7ed9\u4f60\u63e1\u4e00\u56de\uff0c\u597d\u4e0d\u597d\uff1f\u201d \u3000\u3000\u5f53\u5e74\u80e1\u6590\u5728\u5546\u5bb6\u5821\u7ed9\u5546\u5b9d\u9707\u540a\u6253\uff0c\u6781\u662f\u60e8\u9177\uff0c\u9a6c\u6625\u82b1\u77a7\u5f97\u4e0d\u5fcd\uff0c\u6073\u6c42\u91ca\u653e\u3002\u5546\u5b9d\u9707\u5bf9\u5979\u953a\u60c5\uff0c\u867d\u7136\u607c\u6068\u80e1\u6590\uff0c\u5374\u4e5f\u5141\u5176\u6240\u8bf7\uff0c\u4f46\u8981\u63e1\u4e00\u63e1\u5979\u7684\u624b\u4e3a\u916c\uff0c\u9a6c\u6625\u82b1\u4e5f\u5c31\u7b54\u5e94\u3002\u867d\u7136\u5176\u65f6\u80e1\u6590\u5df2\u7ecf\u81ea\u8131\u6346\u7f1a\uff0c\u4f46\u9a6c\u6625\u82b1\u4e3a\u4ed6\u6c42\u60c5\u4e4b\u8a00\u5374\u53e5\u53e5\u542c\u5f97\u660e\u767d\uff0c\u5f53\u65f6\u5c0f\u5c0f\u7684\u5fc3\u7075\u4e4b\u4e2d\uff0c\u4fbf\u5b58\u7740\u4e00\u4efd\u6df1\u6df1\u7684\u611f\u6fc0\uff0c\u76f4\u5230\u6b64\u523b\uff0c\u8fd9\u4efd\u611f\u6fc0\u4ecd\u662f\u6ca1\u6d88\u51cf\u534a\u5206\u3002 \u3000\u3000\u4e3a\u4e86\u62a5\u7b54\u5f53\u5e74\u90a3\u4e24\u53e5\u6c42\u60c5\u4e4b\u8a00\uff0c\u4ed6\u4fbf\u662f\u8981\u9001\u4e86\u81ea\u5df1\u6027\u547d\uff0c\u4e5f\u6240\u7518\u613f\u3002\u4eca\u65e5\u8eab\u5904\u9669\u5730\uff0c\u5fc3\u4e2d\u53cd\u800c\u9ad8\u5174\uff0c\u56e0\u4e3a\u5f53\u5e74\u53d7\u82e6\u6700\u6df1\u4e4b\u65f6\uff0c\u66fe\u6709\u4e00\u4f4d\u59d1\u5a18\u51fa\u8a00\u4e3a\u4ed6\u6c42\u60c5\uff0c\u5230\u8fd9\u65f6\u5019\uff0c\u81ea\u5df1\u7adf\u80fd\u5728\u8fd9\u4f4d\u59d1\u5a18\u5371\u96be\u4e4b\u9645\u6765\u5c3d\u5fc3\u62a5\u7b54\u3002 \u3000\u3000\u9a6c\u6625\u82b1\u542c\u4e86\u90a3\u4e24\u53e5\u8bdd\uff0c\u98de\u971e\u6251\u9762\uff0c\u53eb\u9053\uff1a\u201c\u554a\uff0c\u4f60\u662f\u963f\u6590\uff0c\u5546\u5bb6\u5821\u4e2d\u7684\u963f\u6590\uff01\u201d\u987f\u4e86\u4e00\u987f\uff0c\u53c8\u9053\uff1a\u201c\u4f60\u662f\u80e1\u5927\u4fa0\u80e1\u4e00\u5200\u4e4b\u5b50\uff0c\u80e1\u6590\u80e1\u5144\u5f1f\u3002\u201d \u3000\u3000\u80e1\u6590\u5fae\u7b11\u7740\u70b9\u4e86\u70b9\u5934\uff0c\u4f46\u542c\u5979\u63d0\u5230\u81ea\u5df1\u7236\u4eb2\u7684\u540d\u5b57\uff0c\u53c8\u60f3\u8d77\u4e86\u5e7c\u5e74\u4e4b\u4e8b\uff0c\u5fc3\u4e2d\u4e0d\u7981\u4e00\u9178\u3002 \u3000\u3000\u9a6c\u6625\u82b1\u9053\uff1a\u201c\u80e1\u5144\u5f1f\u4f60\u2026\u2026\u4f60\u2026\u2026\u987b\u5f97\u6551\u6211\u90a3\u4e24\u4e2a\u5b69\u5b50\u3002\u201d \u3000\u3000\u80e1\u6590\u9053\uff1a\u201c\u5c0f\u5f1f\u81ea\u5f53\u7aed\u529b\u3002\u201d\u7565\u4e00\u4fa7\u8eab\uff0c\u9053\uff1a\u201c\u8fd9\u662f\u5c0f\u5f1f\u7684\u7ed3\u4e49\u59b9\u5b50\uff0c\u7a0b\u7075\u7d20\u59d1\u5a18\u3002\u201d \u3000\u3000\u9a6c\u6625\u82b1\u521a\u53eb\u4e86\u4e00\u58f0\u201c\u7a0b\u59d1\u5a18\u201d\uff0c\u7a81\u7136\u7830\u7684\u4e00\u58f0\u5927\u54cd\uff0c\u77f3\u5c4b\u7684\u677f\u95e8\u88ab\u4ec0\u4e48\u5de8\u7269\u4e00\u649e\uff0c\u5c4b\u9876\u6ce5\u7070\u6251\u7c0c\u7c0c\u76f4\u843d\u3002\u597d\u5728\u677f\u95e8\u575a\u539a\uff0c\u95e8\u95e9\u7c97\u5927\uff0c\u6ca1\u7ed9\u649e\u5f00\u3002 \u3000\u3000\u80e1\u6590\u5728\u7a97\u5b54\u4e2d\u5411\u5916\u5f20\u53bb\uff0c\u89c1\u56db\u4e2a\u5927\u76d7\u9a91\u5728\u9a6c\u4e0a\uff0c\u7528\u7ef3\u7d22\u62d6\u4e86\u4e00\u6bb5\u6811\u5e72\uff0c\u8fdc\u8fdc\u9a70\u6765\uff0c\u5954\u5230\u79bb\u95e8\u4e08\u8bb8\u4e4b\u5904\uff0c\u56db\u4eba\u540c\u65f6\u653e\u624b\u4e00\u9001\uff0c\u6811\u5e72\u4fbf\u7830\u7684\u4e00\u58f0\uff0c\u53c8\u649e\u5728\u95e8\u4e0a\u3002 \u3000\u3000\u80e1\u6590\u5fc3\u60f3\uff1a\u201c\u5927\u95e8\u82e5\u662f\u7ed9\u649e\u5f00\u4e86\uff0c\u76d7\u4f17\u4e00\u62e5\u800c\u5165\uff0c\u90a3\u53ef\u62b5\u6321\u4e0d\u4f4f\u3002\u201d\u5f53\u4e0b\u624b\u4e2d\u6697\u6263\u4e00\u679a\u4e27\u95e8\u9489\uff0c\u4e00\u679d\u7529\u624b\u7bad\uff0c\u5f85\u90a3\u56db\u540d\u5927\u76d7\u7eb5\u9a6c\u8fdc\u53bb\u540e\u56de\u5934\u53c8\u6765\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u8001\u5c0f\u5b50\u624b\u4e0b\u7559\u60c5\uff0c\u5c04\u9a6c\u4e0d\u5c04\u4eba\u3002\u201d \u3000\u3000\u773c\u770b\u56db\u9a91\u9a6c\u5954\u5230\u4e09\u56db\u4e08\u5f00\u5916\uff0c\u4ed6\u53f3\u624b\u8fde\u626c\uff0c\u4e24\u679a\u6697\u5668\u7535\u5c04\u800c\u51fa\uff0c\u547c\u547c\u4e24\u54cd\uff0c\u5206\u522b\u9489\u5165\u5f53\u5148\u4e24\u5339\u9a6c\u7684\u9876\u95e8\u6b63\u4e2d\u3002\u4e24\u5339\u9a6c\u53eb\u4e5f\u6ca1\u53eb\u4e00\u58f0\uff0c\u7acb\u65f6\u5012\u6bd9\u3002\u9a6c\u80cc\u4e0a\u7684\u4e24\u540d\u5927\u76d7\u7ffb\u6eda\u4e0b\u978d\u3002\u540e\u9762\u4e24\u4e58\u9a6c\u7ed9\u6811\u5e72\u4e00\u7eca\uff0c\u8ddf\u7740\u6454\u5012\u3002\u9a6c\u4e0a\u4e58\u5ba2\u7eb5\u8eab\u8dc3\u8d77\uff0c\u6ca1\u7ed9\u538b\u7740\u3002 \u3000\u3000\u65c1\u89c2\u7684\u76d7\u4f17\u9f50\u58f0\u60ca\u547c\uff0c\u5954\u4e0a\u5bdf\u770b\uff0c\u53ea\u89c1\u4e24\u679a\u6697\u5668\u6df1\u5165\u9a6c\u8111\uff0c\u5c04\u5165\u5904\u53ea\u4f59\u4e00\u5b54\uff0c\u8fde\u7bad\u5c3e\u4e5f\u6ca1\u7559\u5728\u5916\u9762\uff0c\u8fd9\u4e00\u4e0b\u624b\u52b2\uff0c\u5f53\u771f\u662f\u7f55\u89c1\u7f55\u95fb\u3002\u7fa4\u76d7\u4e2a\u4e2a\u90fd\u662f\u597d\u624b\uff0c\u5982\u4f55\u4e0d\u77e5\u90a3\u5c0f\u80e1\u5b50\u786e\u662f\u624b\u4e0b\u7559\u60c5\uff0c\u8fd9\u4e24\u4ef6\u6697\u5668\u53ea\u8981\u6253\u4e2d\u5934\u80f8\u8179\u4efb\u4f55\u4e00\u5904\uff0c\u54ea\u91cc\u8fd8\u6709\u547d\u5728\uff1f\u7fa4\u76d7\u4e00\u6115\u4e4b\u4e0b\uff0c\u553f\u54e8\u8fde\u8fde\uff0c\u9000\u5230\u4e86\u5341\u4f59\u4e08\u5916\uff0c\u76f4\u81f3\u5bf9\u65b9\u6697\u5668\u51b3\u8ba1\u6253\u4e0d\u5230\u7684\u5904\u6240\uff0c\u8fd9\u624d\u805a\u5728\u4e00\u8d77\uff0c\u4f4e\u58f0\u5546\u8bae\u3002 \u3000\u3000\u80e1\u6590\u9002\u624d\u51fa\u5176\u4e0d\u610f\u7684\u5ffd\u53d1\u6697\u5668\uff0c\u5982\u679c\u5bf9\u51c6\u4e86\u4eba\u8eab\uff0c\u7fa4\u76d7\u4e2d\u81f3\u5c11\u4e5f\u5f97\u6b7b\u4f24\u4e09\u56db\u4eba\uff0c\u5c40\u52bf\u81ea\u53ef\u548c\u7f13\uff0c\u4f46\u80e1\u6590\u4e0d\u660e\u5bf9\u65b9\u6765\u5386\uff0c\u4e0d\u613f\u8d38\u7136\u6740\u4f24\u4eba\u547d\uff0c\u4ee5\u81f3\u7ed3\u4e0b\u4e86\u4e0d\u53ef\u89e3\u7684\u6df1\u4ec7\uff0c\u4f55\u51b5\u9a6c\u6625\u82b1\u4e8c\u5b50\u843d\u5165\u654c\u624b\uff0c\u5f90\u94ee\u4e0b\u843d\u4e0d\u660e\uff0c\u53cc\u65b9\u82e5\u80fd\u5584\u7f62\uff0c\u81ea\u662f\u4e0a\u7b56\u3002 \u3000\u3000\u7fa4\u76d7\u4e00\u9000\uff0c\u80e1\u6590\u56de\u8fc7\u8eab\u6765\uff0c\u89c1\u677f\u95e8\u5df2\u7ed9\u649e\u51fa\u4e86\u4e00\u6761\u5927\u88c2\u7f1d\uff0c\u5fc3\u60f3\u518d\u649e\u5f97\u4e24\u4e0b\uff0c\u4fbf\u65e0\u6cd5\u963b\u654c\u653b\u5165\u4e86\u3002 \u3000\u3000\u9a6c\u6625\u82b1\u9053\uff1a\u201c\u80e1\u5144\u5f1f\uff0c\u7a0b\u5bb6\u59b9\u5b50\uff0c\u4f60\u4eec\u8bf4\u600e\u4e48\u529e\uff1f\u201d\u80e1\u6590\u76b1\u7709\u9053\uff1a\u201c\u8fd9\u4e9b\u76d7\u4f19\u4f60\u4e00\u4e2a\u4e5f\u4e0d\u8ba4\u8bc6\u4e48\uff1f\u201d\u9a6c\u6625\u82b1\u6447\u5934\u9053\uff1a\u201c\u4e0d\u8bc6\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u82e5\u8bf4\u662f\u4ee4\u5c0a\u5f53\u5e74\u7ed3\u4e0b\u7684\u4ec7\u5bb6\uff0c\u4ed6\u4eec\u8a00\u8bed\u4e4b\u4e2d\uff0c\u5bf9\u4ee4\u5c0a\u5374\u751a\u662f\u656c\u91cd\u3002\u5982\u679c\u6709\u610f\u548c\u4f60\u4e3a\u96be\uff0c\u56e0\u800c\u63b3\u53bb\u4e24\u4e2a\u5b69\u5b50\uff0c\u4e00\u6765\u4f60\u4e00\u4e2a\u4eba\u4e5f\u4e0d\u8bc6\uff0c\u4e8c\u6765\u4ed6\u4eec\u5bf9\u4f60\u5e76\u65e0\u534a\u53e5\u4e0d\u656c\u7684\u8a00\u8bed\u3002\u5bf9\u5f90\u5927\u54e5\u561b\uff0c\u4ed6\u4eec\u786e\u662f\u5341\u5206\u65e0\u793c\uff0c\u4f46\u8981\u548c\u5f90\u5927\u54e5\u8fc7\u4e0d\u53bb\uff0c\u53ef\u4e0d\u7528\u8fd9\u822c\u5174\u5e08\u52a8\u4f17\u554a\u3002\u201d \u3000\u3000\u9a6c\u6625\u82b1\u9053\uff1a\u201c\u4e0d\u9519\u3002\u76d7\u4f17\u4e4b\u4e2d\uff0c\u4e0d\u8bba\u54ea\u4e00\u4e2a\uff0c\u6b66\u529f\u90fd\u80dc\u8fc7\u6211\u5e08\u54e5\u3002\u53ea\u8981\u6709\u4e00\u4e24\u4eba\u51fa\u9a6c\uff0c\u4fbf\u5df2\u8db3\u591f\u4e86\u3002\u201d\u80e1\u6590\u70b9\u5934\u9053\uff1a\u201c\u4e8b\u60c5\u7684\u786e\u53e4\u602a\uff0c\u4f46\u9a6c\u59d1\u5a18\u4e5f\u4e0d\u7528\u592a\u8fc7\u62c5\u5fc3\uff0c\u77a7\u4ed6\u4eec\u7684\u4f5c\u4e3a\uff0c\u5e76\u65e0\u4f24\u4eba\u4e4b\u610f\uff0c\u5012\u4f3c\u5728\u8ddf\u5f90\u5927\u54e5\u5f00\u73a9\u7b11\u4f3c\u7684\u3002\u201d\u9a6c\u6625\u82b1\u60f3\u5230\u201c\u4e00\u6735\u9c9c\u82b1\u63d2\u5728\u725b\u7caa\u4e0a\u201d\u8fd9\u4e9b\u8bdd\uff0c\u8138\u4e0a\u53c8\u662f\u4e00\u7ea2\u3002 \u3000\u3000\u4e24\u4eba\u5728\u8fd9\u8fb9\u5546\u8bae\uff0c\u7a0b\u7075\u7d20\u5df2\u6170\u629a\u4e86\u77f3\u5c4b\u4e2d\u7684\u8001\u5987\uff0c\u5728\u94c1\u9505\u4e2d\u716e\u8d77\u996d\u6765\u3002 \u3000\u3000\u4e09\u4eba\u9971\u9910\u4e86\u4e00\u987f\uff0c\u4ece\u7a97\u5b54\u4e2d\u671b\u5c06\u51fa\u53bb\uff0c\u4f46\u89c1\u7fa4\u76d7\u6765\u53bb\u5fd9\u788c\uff0c\u4e0d\u77e5\u5728\u5e72\u4e9b\u4ec0\u4e48\uff0c\u56e0\u88ab\u6811\u6728\u6321\u4f4f\u4e86\uff0c\u77a7\u4e0d\u6e05\u884c\u52a8\u3002 \u3000\u3000\u80e1\u6590\u548c\u7a0b\u7075\u7d20\u4f4e\u58f0\u8c08\u8bba\u4e86\u4e00\u9635\uff0c\u90fd\u89c9\u96be\u4ee5\u7d22\u89e3\u3002\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u8fd9\u4e8b\u8ddf\u4e49\u5802\u9547\u4e0a\u7684\u80e1\u5927\u8d22\u4e3b\u53ef\u6709\u5e72\u8fde\u4e48\uff1f\u201d\u80e1\u6590\u9053\uff1a\u201c\u6211\u662f\u4e00\u70b9\u4e5f\u4e0d\u77e5\u9053\u3002\u201d\u4ed6\u987f\u4e86\u4e00\u987f\uff0c\u8bf4\u9053\uff1a\u201c\u4e0e\u5176\u8001\u662f\u95f7\u5728\u846b\u82a6\u91cc\uff0c\u6211\u4eec\u8fd8\u4e0d\u5982\u73b0\u51fa\u771f\u9762\u76ee\u6765\uff0c\u5018\u82e5\u4e24\u4e8b\u6709\u751a\u5e72\u8fde\uff0c\u6211\u4eec\u4e5f\u597d\u6253\u5b9a\u4e3b\u610f\u5e94\u4ed8\uff0c\u514d\u5f97\u9a6c\u59d1\u5a18\u7684\u4e08\u592b\u513f\u5b50\u53d7\u8fd9\u65e0\u5984\u4e4b\u707e\u3002\u201d\u7a0b\u7075\u7d20\u70b9\u4e86\u70b9\u5934\u3002\u80e1\u6590\u7c98\u4e0a\u4e86\u5c0f\u80e1\u5b50\uff0c\u4e0e\u7a0b\u7075\u7d20\u4e24\u4eba\u8d70\u5230\u95e8\u8fb9\uff0c\u6253\u5f00\u4e86\u5927\u95e8\u3002 \u3000\u3000\u7fa4\u76d7\u89c1\u6709\u4eba\u51fa\u6765\uff0c\u6015\u4ed6\u4eec\u7a81\u56f4\uff0c\u5341\u4f59\u4e58\u9a6c\u56db\u4e0b\u6563\u5f00\uff0c\u903c\u8fd1\u5c4b\u524d\u3002 \u3000\u3000\u80e1\u6590\u53eb\u9053\uff1a\u201c\u5404\u4f4d\u5018\u662f\u51b2\u7740\u6211\u59d3\u80e1\u7684\u800c\u6765\uff0c\u6211\u80e1\u6590\u548c\u4e49\u59b9\u7a0b\u7075\u7d20\u4fbf\u5728\u6b64\u5904\uff0c\u4e0d\u987b\u7275\u8fde\u65c1\u4eba\uff01\u201d\u8bf4\u7740\u62cd\u7684\u4e00\u58f0\uff0c\u628a\u70df\u7ba1\u4e00\u6298\u4e24\u6bb5\uff0c\u626f\u4e0b\u5507\u4e0a\u7684\u5c0f\u80e1\u5b50\uff0c\u5c06\u8138\u4e0a\u5316\u88c5\u5c3d\u6570\u62b9\u53bb\u3002\u7a0b\u7075\u7d20\u4e5f\u6458\u4e0b\u4e86\u5c0f\u5e3d\uff0c\u6563\u5f00\u9752\u4e1d\uff0c\u9732\u51fa\u5973\u5b69\u513f\u5bb6\u7684\u9762\u76ee\u3002 \u3000\u3000\u7fa4\u76d7\u8138\u4e0a\u5747\u73b0\u60ca\u5f02\u4e4b\u8272\uff0c\u4e07\u6ca1\u60f3\u5230\u6b64\u4eba\u6b66\u529f\u5982\u6b64\u4e86\u5f97\uff0c\u7adf\u662f\u4e2a\u4e8c\u5341\u5c81\u672a\u6ee1\u7684\u5c11\u5e74\u3002\u7fa4\u76d7\u4f60\u671b\u6211\uff0c\u6211\u671b\u4f60\uff0c\u4e00\u65f6\u6253\u4e0d\u5b9a\u4e3b\u610f\u3002 \u3000\u3000\u7a81\u6709\u4e00\u4eba\u8d8a\u4f17\u800c\u51fa\uff0c\u9762\u767d\u8eab\u9ad8\uff0c\u6b63\u662f\u90a3\u4f7f\u5251\u7684\u59d3\u8042\u5927\u76d7\u3002 \u3000\u3000\u4ed6\u5411\u80e1\u6590\u4e00\u62b1\u62f3\uff0c\u8bf4\u9053\uff1a\u201c\u5c0a\u9a7e\u8fd8\u5251\u4e4b\u5fb7\uff0c\u5728\u4e0b\u6ca1\u9f7f\u4e0d\u5fd8\u3002\u6211\u4eec\u7684\u4e8b\u8ddf\u4e24\u4f4d\u7edd\u65e0\u5173\u8054\uff0c\u4e24\u4f4d\u5c3d\u7ba1\u8bf7\u4fbf\uff0c\u5728\u4e0b\u5728\u8fd9\u513f\u606d\u9001\u3002\u201d \u3000\u3000\u8bf4\u7740\u7ffb\u8eab\u4e0b\u9a6c\uff0c\u5728\u9a6c\u81c0\u4e0a\u8f7b\u8f7b\u4e00\u62cd\uff0c\u90a3\u9a6c\u8d70\u5230\u80e1\u6590\u8ddf\u524d\u505c\u4f4f\uff0c\u770b\u6765\u8fd9\u5927\u76d7\u662f\u8fde\u5750\u9a91\u4e5f\u5949\u9001\u4e86\u3002 \u3000\u3000\u80e1\u6590\u62b1\u62f3\u8fd8\u793c\uff0c\u8bf4\u9053\uff1a\u201c\u9a6c\u59d1\u5a18\u5462\uff1f\u4f60\u4eec\u7b54\u5e94\u4e86\u4e0d\u6253\u8fd9\u62b1\u4e0d\u5e73\u7684\u3002\u201d\u90a3\u59d3\u8042\u7684\u7b54\u9053\uff1a\u201c\u62b1\u4e0d\u5e73\u662f\u4e0d\u6562\u6253\u4e86\u3002\u6211\u5144\u5f1f\u4eec\u53ea\u9080\u8bf7\u9a6c\u59d1\u5a18\u5317\u4e0a\u4e00\u884c\uff0c\u51b3\u4e0d\u6562\u635f\u4f24\u9a6c\u59d1\u5a18\u5206\u6beb\u3002\u201d \u3000\u3000\u80e1\u6590\u7b11\u9053\uff1a\u201c\u82e5\u662f\u597d\u610f\u9080\u5ba2\uff0c\u4f55\u5fc5\u5982\u6b64\u5927\u60ca\u5c0f\u602a\u3002\u201d\u8f6c\u5934\u53eb\u9053\uff1a\u201c\u9a6c\u59d1\u5a18\uff0c\u4eba\u5bb6\u9080\u4f60\u53bb\u4f5c\u5ba2\uff0c\u4f60\u53bb\u662f\u4e0d\u53bb\uff1f\u201d\u9a6c\u6625\u82b1\u8d70\u51fa\u95e8\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u548c\u5404\u4f4d\u7d20\u4e0d\u76f8\u8bc6\uff0c\u9080\u6211\u4f5c\u751a\uff1f\u201d \u3000\u3000\u76d7\u4f17\u4e2d\u6709\u4eba\u7b11\u9053\uff1a\u201c\u6211\u5144\u5f1f\u4eec\u81ea\u7136\u4e0d\u8bc6\u9a6c\u59d1\u5a18\uff0c\u53ef\u662f\u6709\u4eba\u8bc6\u5f97\u4f60\u554a\u3002\u201d\u9a6c\u6625\u82b1\u5927\u58f0\u9053\uff1a\u201c\u6211\u7684\u5b69\u5b50\u5462\uff1f\u5feb\u8fd8\u6211\u5b69\u5b50\u6765\u3002\u201d \u3000\u3000\u90a3\u59d3\u8042\u7684\u9053\uff1a\u201c\u4e24\u4f4d\u4ee4\u90ce\u5b89\u597d\u65e0\u6059\uff0c\u9a6c\u59d1\u5a18\u5c3d\u53ef\u653e\u5fc3\u3002\u6211\u4eec\u51fa\u5168\u529b\u4fdd\u62a4\uff0c\u5c1a\u6050\u6709\u751a\u5931\u95ea\uff0c\u600e\u6562\u60ca\u5413\u4e86\u4e24\u4f4d\u4e07\u91d1\u4e4b\u4f53\u7684\u5c0f\u516c\u5b50\uff1f\u201d \u3000\u3000\u7a0b\u7075\u7d20\u5411\u80e1\u6590\u77a7\u4e86\u4e00\u773c\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u5f3a\u76d7\u8bf4\u8bdd\u8d8a\u6765\u8d8a\u5ba2\u6c14\u4e86\u3002\u8fd9\u5f90\u94ee\u5de6\u53f3\u4e0d\u8fc7\u662f\u4e2a\u9556\u5934\uff0c\u4ed6\u751f\u7684\u513f\u5b50\u662f\u4ec0\u4e48\u4e07\u91d1\u4e4b\u4f53\u4e86\uff1f\u201d\u53ea\u89c1\u9a6c\u6625\u82b1\u7a81\u7136\u7ea2\u6655\u6ee1\u8138\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u53bb\uff01\u5feb\u8fd8\u6211\u5b69\u5b50\u6765\uff01\u201d\u4e5f\u4e0d\u7b49\u7fa4\u76d7\u56de\u7b54\uff0c\u5f84\u81ea\u56de\u8fdb\u4e86\u77f3\u5c4b\u3002 \u3000\u3000\u80e1\u6590\u89c1\u9a6c\u6625\u82b1\u884c\u52a8\u5947\u7279\uff0c\u7591\u7aa6\u66f4\u589e\uff0c\u8bf4\u9053\uff1a\u201c\u9a6c\u59d1\u5a18\u548c\u5728\u4e0b\u4ea4\u60c5\u975e\u6d45\uff0c\u4e0d\u8bba\u4e3a\u4e86\u4f55\u4e8b\uff0c\u5728\u4e0b\u51b3\u4e0d\u80fd\u8896\u624b\u65c1\u89c2\u3002\u201d \u3000\u3000\u90a3\u59d3\u8042\u7684\u9053\uff1a\u201c\u5c0a\u9a7e\u6b66\u529f\u867d\u5f3a\uff0c\u4f46\u53cc\u62f3\u96be\u654c\u56db\u624b\u3002\u6211\u4eec\u5f1f\u5144\u4e00\u5171\u6709\u4e8c\u5341\u4e94\u4eba\uff0c\u5f85\u5230\u665a\u95f4\uff0c\u53e6\u6709\u5f3a\u63f4\u5230\u6765\u3002\u201d \u3000\u3000\u80e1\u6590\u5fc3\u60f3\uff1a\u201c\u8fd9\u4eba\u6240\u8bf4\u7684\u4eba\u6570\uff0c\u548c\u6211\u6240\u731c\u7684\u4e00\u70b9\u4e0d\u9519\uff0c\u603b\u7b97\u6ca1\u6709\u9a97\u6211\u3002\u7ba1\u4ed6\u5f3a\u63f4\u662f\u8c01\uff0c\u6211\u5c82\u80fd\u820d\u9a6c\u59d1\u5a18\u800c\u53bb\uff1f\u4f46\u4e8c\u59b9\u5374\u4e0d\u80fd\u5e73\u767d\u65e0\u7aef\u7684\u8ba9\u5979\u5728\u6b64\u9001\u4e86\u547d\u3002\u201d\u4e8e\u662f\u4f4e\u58f0\u9053\uff1a\u201c\u4e8c\u59b9\uff0c\u4f60\u5148\u9a91\u8fd9\u9a6c\uff0c\u7a81\u56f4\u51fa\u53bb\uff0c\u6211\u4e00\u4eba\u7167\u6599\u9a6c\u59d1\u5a18\uff0c\u90a3\u4fbf\u5bb9\u6613\u5f97\u591a\u3002\u201d \u3000\u3000\u7a0b\u7075\u7d20\u77e5\u4ed6\u987e\u5ff5\u81ea\u5df1\uff0c\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u7ed3\u62dc\u4e4b\u65f6\uff0c\u8bf4\u7684\u662f\u2018\u6709\u96be\u5171\u5f53\u2019\u5462\uff0c\u8fd8\u662f\u2018\u6709\u96be\u5148\u9003\u2019\uff1f\u201d\u80e1\u6590\u9053\uff1a\u201c\u4f60\u548c\u9a6c\u59d1\u5a18\u4ece\u4e0d\u76f8\u8bc6\uff0c\u4f55\u5fc5\u4e3a\u5979\u72af\u9669\uff1f\u81f3\u4e8e\u6211\uff0c\u90a3\u53ef\u4e0d\u540c\u3002\u201d\u7a0b\u7075\u7d20\u7684\u773c\u5149\u59cb\u7ec8\u6ca1\u671b\u4ed6\u4e00\u773c\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u4f55\u5fc5\u4e3a\u5979\u72af\u9669\uff1f\u53ef\u662f\u6211\u548c\u4f60\u96be\u9053\u4e5f\u662f\u4ece\u4e0d\u76f8\u8bc6\u4e48\uff1f\u201d \u3000\u3000\u80e1\u6590\u5fc3\u4e2d\u5927\u662f\u611f\u6fc0\uff0c\u81ea\u5fd6\u4e00\u751f\u4e4b\u4e2d\uff0c\u7518\u613f\u548c\u81ea\u5df1\u540c\u6b7b\u7684\uff0c\u5e73\u56db\u53d4\u662f\u4f1a\u7684\uff0c\u8d75\u534a\u5c71\u4e5f\u4f1a\u7684\uff0c\uff08\u5947\u602a\u5f97\u5f88\uff0c\u4e00\u77ac\u4e4b\u95f4\uff0c\u5fc3\u4e2d\u5ffd\u5730\u63a0\u8fc7\u4e00\u4e2a\u53e4\u602a\u7684\u5ff5\u5934\uff1a\u82d7\u4eba\u51e4\u4e5f\u4f1a\u7684\uff09\uff0c\u4eca\u65e5\u53c8\u6709\u4e00\u4f4d\u5e74\u8f7b\u59d1\u5a18\u5b89\u5b89\u9759\u9759\u5730\u7ad9\u5728\u81ea\u5df1\u8eab\u65c1\uff0c\u4e00\u70b9\u4e5f\u4e0d\u8e0c\u8e87\uff0c\u53ea\u662f\u8fd9\u4e48\u8bf4\uff1a\u201c\u6d3b\u7740\uff0c\u54b1\u4eec\u4e00\u8d77\u6d3b\uff0c\u8981\u6b7b\uff0c\u4fbf\u4e00\u8d77\u6b7b\uff01\u201d \u3000\u3000\u90a3\u59d3\u8042\u7684\u5927\u76d7\u7b49\u4e86\u7247\u523b\uff0c\u53c8\u8bf4\u9053\uff1a\u201c\u5f1f\u5144\u4eec\u51b3\u4e0d\u6562\u6709\u4f24\u9a6c\u59d1\u5a18\u534a\u5206\uff0c\u5bf9\u4e24\u4f4d\u5374\u4e0d\u5b58\u987e\u5fcc\u3002\u4e24\u4f4d\u53c8\u4f55\u5fc5\u6ca1\u6765\u7531\u7684\u81ea\u5904\u9669\u5730\uff1f\u5c0a\u9a7e\u884c\u4e8b\u5149\u660e\u78ca\u843d\uff0c\u5728\u4e0b\u4f69\u670d\u5f97\u7d27\u3002\u54b1\u4eec\u540e\u4f1a\u6709\u671f\uff0c\u4eca\u65e5\u4fbf\u6b64\u522b\u8fc7\u5982\u4f55\uff1f\u201d\u80e1\u6590\u9053\uff1a\u201c\u4f60\u4eec\u653e\u4e0d\u653e\u9a6c\u59d1\u5a18\u8d70\uff1f\u201d \u3000\u3000\u90a3\u59d3\u8042\u7684\u6447\u4e86\u6447\u5934\uff0c\u8fd8\u5f85\u76f8\u529d\uff0c\u7fa4\u76d7\u4e2d\u5df2\u6709\u8bb8\u591a\u4eba\u547c\u559d\u8d77\u6765\uff1a\u201c\u8fd9\u5c0f\u5b50\u4e0d\u8bc6\u597d\u6b79\uff0c\u8042\u5927\u54e5\u4e0d\u5fc5\u518d\u8ddf\u4ed6\u591a\u8d39\u5507\u820c\uff01\u201d\u201c\u8fd9\u53eb\u505a\u5929\u5802\u6709\u8def\u4f60\u4e0d\u8d70\uff0c\u5730\u72f1\u65e0\u95e8\u81ea\u8fdb\u6765\u3002\u201d\u201c\u50bb\u5c0f\u5b50\uff0c\u51ed\u4f60\u4e00\u4eba\uff0c\u5f53\u771f\u6709\u5929\u5927\u7684\u672c\u4e8b\u4e48\uff1f\u201d \u3000\u3000\u7a81\u89c1\u767d\u5149\u4e00\u95ea\uff0c\u4e00\u4ef6\u6697\u5668\u5411\u80e1\u6590\u75be\u5c04\u8fc7\u6765\u3002\u90a3\u59d3\u8042\u7684\u5927\u76d7\u8dc3\u8d77\u8eab\u6765\u4e00\u628a\u6293\u4f4f\uff0c\u5374\u662f\u4e00\u67c4\u98de\u5200\u3002 \u3000\u3000\u80e1\u6590\u9053\uff1a\u201c\u5c0a\u9a7e\u597d\u610f\uff0c\u5144\u5f1f\u5fc3\u9886\uff0c\u4ece\u6b64\u523b\u8d77\uff0c\u54b1\u4eec\u8c01\u4e5f\u4e0d\u6b20\u8c01\u7684\u60c5\u3002\u201d\u8bf4\u7740\u62c9\u7740\u7a0b\u7075\u7d20\u7684\u624b\uff0c\u7ffb\u8eab\u8fdb\u4e86\u77f3\u5c4b\u3002 \u3000\u3000\u4f46\u542c\u5f97\u80cc\u540e\u98ce\u58f0\u547c\u547c\uff0c\u597d\u51e0\u4ef6\u6697\u5668\u5c04\u6765\uff0c\u4ed6\u7528\u529b\u4e00\u63a8\u5927\u95e8\uff0c\u6258\u6258\u6258\u51e0\u58f0\uff0c\u51e0\u4ef6\u6697\u5668\u90fd\u9489\u4e0a\u4e86\u95e8\u677f\u3002\u7fa4\u76d7\u5927\u58f0\u553f\u54e8\uff0c\u51b2\u8fd1\u95e8\u524d\u3002 \u3000\u3000\u80e1\u6590\u62a2\u5230\u7a97\u5b54\uff0c\u62fe\u8d77\u684c\u4e0a\u7684\u94a2\u9556\uff0c\u5bf9\u51c6\u653b\u5f97\u6700\u8fd1\u7684\u5927\u76d7\u63b7\u4e86\u51fa\u53bb\u3002\u4ed6\u4ecd\u4e0d\u613f\u5c31\u6b64\u800c\u4e0b\u6740\u624b\uff0c\u8fd9\u4e00\u9556\u5bf9\u51c6\u4e86\u90a3\u5927\u76d7\u80a9\u5934\u3002 \u3000\u3000\u90a3\u5927\u76d7\u201c\u554a\u201d\u7684\u4e00\u58f0\uff0c\u80a9\u5934\u4e2d\u9556\uff0c\u8fd9\u4eba\u6781\u662f\u51f6\u608d\uff0c\u7adf\u81ea\u4e0d\u9000\uff0c\u53eb\u9053\uff1a\u201c\u4f17\u5144\u5f1f\uff0c\u4eca\u65e5\u8fde\u8fd9\u4e00\u4e2a\u5c0f\u5b50\u4e5f\u6536\u62fe\u4e0d\u4e0b\uff0c\u54b1\u4eec\u8fd8\u6709\u8138\u56de\u53bb\u5417\uff1f\u201d\u7fa4\u76d7\u8fde\u58f0\u5406\u559d\uff0c\u56db\u9762\u51b2\u4e0a\u3002\u53ea\u542c\u5f97\u4e1c\u8fb9\u548c\u897f\u8fb9\u7684\u77f3\u5899\u4e0a\u540c\u65f6\u53d1\u51fa\u649e\u51fb\u4e4b\u58f0\uff0c\u663e\u7136\u8fd9\u4e24\u9762\u56e0\u65e0\u7a97\u5b54\uff0c\u76d7\u4f17\u4e0d\u6015\u80e1\u6590\u53d1\u5c04\u6697\u5668\uff0c\u6b63\u7528\u91cd\u7269\u649e\u51fb\uff0c\u8981\u7834\u58c1\u800c\u5165\u3002 \u3000\u3000\u80e1\u6590\u8fde\u53d1\u6697\u5668\uff0c\u5357\u5317\u4e24\u9762\u7684\u76d7\u4f19\u5411\u540e\u9000\u5374\uff0c\u4e1c\u897f\u9762\u7684\u649e\u51fb\u58f0\u5374\u4e1d\u6beb\u4e0d\u505c\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u53d6\u51fa\u4e03\u5fc3\u6d77\u68e0\u6240\u5236\u8721\u70db\uff0c\u53c8\u5c06\u89e3\u836f\u5206\u7ed9\u80e1\u6590\u3001\u9a6c\u6625\u82b1\u548c\u75c5\u5012\u5728\u5e8a\u7684\u5987\u4eba\uff0c\u53eb\u4ed6\u4eec\u542b\u5728\u5634\u91cc\uff0c\u4e00\u5f85\u654c\u4eba\u653b\u5165\uff0c\u4fbf\u70b9\u8d77\u8721\u70db\uff0c\u85b0\u5012\u654c\u4eba\u3002 \u3000\u3000\u4f46\u7a0b\u7075\u7d20\u7684\u6bd2\u836f\u5bf9\u4ed8\u5c11\u6570\u654c\u4eba\u56fa\u7136\u5e94\u9a8c\u5982\u795e\uff0c\u654c\u4eba\u5927\u4e3e\u6765\u653b\uff0c\u5bf9\u4e4b\u4e0d\u514d\u65e0\u6d4e\u4e8e\u4e8b\u3002\u9884\u5907\u8fd9\u679d\u8721\u70db\uff0c\u4e5f\u53ea\u662f\u5c3d\u529b\u800c\u4e3a\uff0c\u80fd\u591a\u4f24\u5f97\u4e00\u4eba\u4fbf\u51cf\u5f31\u4e00\u5206\u654c\u52bf\uff0c\u81f3\u4e8e\u662f\u5426\u80fd\u51b2\u51fa\u91cd\u56f4\uff0c\u5b9e\u5728\u6beb\u65e0\u628a\u63e1\u3002 \u3000\u3000\u4fbf\u5728\u6b64\u65f6\uff0c\u79c3\u7684\u4e00\u54cd\uff0c\u897f\u9996\u7684\u77f3\u58c1\u5df2\u88ab\u653b\u7834\u4e00\u6d1e\uff0c\u53ea\u89c1\u7fa4\u76d7\u5bb3\u6015\u80e1\u6590\u5389\u5bb3\uff0c\u5374\u65e0\u4eba\u80c6\u6562\u5b64\u8eab\u94bb\u8fdb\uff0c\u4f46\u7834\u6d1e\u52bf\u5c06\u8d8a\u51ff\u8d8a\u5927\uff0c\u603b\u80fd\u4e00\u62e5\u800c\u5165\u3002\u80e1\u6590\u89c1\u60c5\u52bf\u7d27\u8feb\uff0c\u6697\u5668\u53c8\u5df2\u4f7f\u5b8c\uff0c\u5728\u77f3\u5c4b\u4e2d\u56db\u4e0b\u6253\u91cf\uff0c\u8981\u627e\u4e9b\u4ec0\u4e48\u91cd\u7269\u6765\u6295\u63b7\u4f24\u654c\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u53eb\u9053\uff1a\u201c\u5927\u54e5\uff0c\u8fd9\u4e1c\u897f\u518d\u5999\u4e0d\u8fc7\u3002\u201d\u8bf4\u7740\u4fef\u8eab\u5230\u90a3\u75c5\u5987\u7684\u5e8a\u8fb9\uff0c\u4f38\u624b\u5728\u5730\u4e0b\u4e00\u6309\uff0c\u53cc\u624b\u4e3e\u8d77\uff0c\u4e24\u624b\u638c\u4e0a\u767d\u767d\u7684\u90fd\u662f\u77f3\u7070\u3002\u539f\u6765\u4e61\u4eba\u5728\u6b64\u70e7\u77f3\u7070\uff0c\u77f3\u5c4b\u4e2d\u79ef\u6709\u4e0d\u5c11\u3002 \u3000\u3000\u80e1\u6590\u53eb\u9053\uff1a\u201c\u5999\u6781\uff01\u201d\u55e4\u7684\u4e00\u58f0\uff0c\u626f\u4e0b\u957f\u888d\u7684\u4e00\u5757\u8863\u895f\uff0c\u5305\u4e86\u4e00\u5927\u5305\u77f3\u7070\uff0c\u731b\u5730\u7f29\u8eab\u4e00\u51b2\uff0c\u7adf\u4ece\u7834\u5b54\u4e2d\u94bb\u4e86\u51fa\u53bb\uff0c\u95ed\u4f4f\u773c\u775b\uff0c\u53f3\u624b\u4e00\u626c\uff0c\u4e00\u5305\u77f3\u7070\u6492\u51fa\uff0c\u7acb\u5373\u94bb\u56de\u77f3\u5c4b\u3002 \u3000\u3000\u7fa4\u76d7\u6b63\u81ea\u8ba1\u8bae\u5982\u4f55\u653b\u5165\u77f3\u5c4b\uff0c\u5982\u4f55\u4ece\u7834\u5b54\u4e2d\u51b2\u8fdb\u800c\u4e0d\u81f4\u4e3a\u80e1\u6590\u6240\u4f24\uff0c\u90a3\u6599\u5f97\u5230\u4ed6\u53cd\u5ba2\u4e3a\u4e3b\uff0c\u7adf\u4ece\u7834\u6d1e\u4e2d\u653b\u5c06\u51fa\u6765\uff1f\u8fd9\u4e00\u5927\u5305\u77f3\u7070\u56db\u6563\u98de\u626c\uff0c\u767d\u96fe\u832b\u832b\uff0c\u7ad9\u5f97\u6700\u8fd1\u7684\u4e09\u540d\u5927\u76d7\u773c\u4e2d\u767b\u65f6\u6cbe\u4e0a\uff0c\u5267\u75db\u96be\u5f53\uff0c\u4e00\u9f50\u5931\u58f0\u5927\u53eb\u3002 \u3000\u3000\u80e1\u6590\u7a81\u51fb\u6210\u529f\uff0c\u4e00\u8f6c\u8eab\uff0c\u7a0b\u7075\u7d20\u53c8\u9012\u4e86\u4e24\u4e2a\u77f3\u7070\u5305\u7ed9\u4ed6\u3002 \u3000\u3000\u80e1\u6590\u9053\uff1a\u201c\u597d\uff01\u201d\u4ece\u77f3\u7076\u4e0a\u6273\u4e0b\u4e00\u5757\u5927\u77f3\uff0c\u4f38\u5de6\u624b\u9ad8\u9ad8\u4e3e\u8d77\uff0c\u98de\u8eab\u4e00\u8dc3\uff0c\u5ffd\u5587\u5587\u4e00\u58f0\u54cd\uff0c\u5c4b\u9876\u649e\u7834\u4e86\u4e00\u4e2a\u5927\u6d1e\u3002 \u3000\u3000\u4ed6\u4e8c\u6b21\u8dc3\u8d77\u65f6\u4ece\u5c4b\u9876\u4e2d\u94bb\u51fa\uff0c\u4e24\u4e2a\u77f3\u7070\u5305\u626c\u5904\uff0c\u7fa4\u76d7\u4e2d\u53c8\u6709\u4eba\u5931\u58f0\u60ca\u547c\u3002\u7a0b\u7075\u7d20\u8fde\u5305\u51e0\u4e2a\u77f3\u7070\u5305\uff0c\u653e\u5728\u94c1\u9505\u4e2d\u9012\u4e0a\u5c4b\u9876\uff0c\u80e1\u6590\u4e1c\u5357\u897f\u5317\u4e00\u9635\u629b\u6253\uff0c\u7fa4\u76d7\u53c8\u53eb\u53c8\u9a82\uff0c\u9000\u5165\u4e86\u6797\u4e2d\u3002 \u3000\u3000\u8fd9\u4e00\u80a1\u7fa4\u76d7\u4e03\u516b\u4eba\u773c\u76ee\u53d7\u4f24\uff0c\u4e00\u65f6\u4e0d\u6562\u518d\u903c\u8fd1\u77f3\u5c4b\u3002 \u3000\u3000\u5982\u6b64\u76f8\u6301\u4e86\u4e00\u4e2a\u591a\u65f6\u8fb0\uff0c\u7fa4\u76d7\u4e0d\u6562\u8fc7\u6765\uff0c\u80e1\u6590\u7b49\u5374\u4e5f\u4e0d\u6562\u51b2\u6740\u51fa\u53bb\uff0c\u4e00\u5931\u77f3\u5c4b\u7684\u51ed\u85c9\uff0c\u90a3\u4fbf\u65e0\u6cd5\u4ee5\u5c11\u6297\u4f17\u3002 \u3000\u3000\u80e1\u6590\u548c\u7a0b\u7075\u7d20\u6709\u8bf4\u6709\u7b11\uff0c\u4e24\u4eba\u540c\u5904\u60a3\u96be\uff0c\u6bd4\u5f80\u65e5\u66f4\u589e\u4eb2\u5bc6\u3002\u9a6c\u6625\u82b1\u5374\u6709\u70b9\u513f\u795e\u4e0d\u5b88\u820d\uff0c\u53ea\u662f\u4f4e\u5934\u9ed8\u9ed8\u6c89\u601d\uff0c\u65e2\u4e0d\u5916\u671b\u654c\u4eba\uff0c\u5bf9\u80e1\u7a0b\u4e24\u4eba\u7684\u8bf4\u8bdd\u4e5f\u4f3c\u542c\u800c\u4e0d\u95fb\u3002 \u3000\u3000\u80e1\u6590\u9053\uff1a\u201c\u54b1\u4eec\u5b88\u5230\u665a\u95f4\uff0c\u6216\u80fd\u4e58\u9ed1\u9003\u8d70\u3002\u4eca\u591c\u5018\u82e5\u8d70\u4e0d\u8131\uff0c\u4e8c\u59b9\uff0c\u90a3\u8981\u7d2f\u5f97\u4f60\u9001\u4e00\u6761\u5c0f\u547d\u4e86\uff0c\u81f3\u4e8e\u6211\u6b6a\u62f3\u6709\u654c\u725b\u8015\u7530\u8fd9\u8001\u5c0f\u5b50\u7684\u8001\u547d\uff0c\u563f\uff0c\u563f\uff01\u201d\u8bf4\u7740\u4f38\u624b\u6307\u5728\u4e0a\u5507\u4e00\u6478\uff0c\u7b11\u9053\uff1a\u201c\u65e9\u77e5\u8ddf\u59d3\u725b\u7684\u65e0\u5173\uff0c\u8fd9\u6487\u80e1\u5b50\u5012\u6709\u70b9\u820d\u4e0d\u5f97\u4e86\u3002\u201d \u3000\u3000\u7a0b\u7075\u7d20\u5fae\u5fae\u4e00\u7b11\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5927\u54e5\uff0c\u5f85\u4f1a\u5982\u679c\u8d70\u4e0d\u8131\uff0c\u4f60\u6551\u6211\u5462\uff0c\u8fd8\u662f\u6551\u9a6c\u59d1\u5a18\uff1f\u201d \u3000\u3000\u80e1\u6590\u9053\uff1a\u201c\u4e24\u4e2a\u90fd\u6551\u3002\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u6211\u662f\u95ee\u4f60\uff0c\u5018\u82e5\u53ea\u80fd\u6551\u51fa\u4e00\u4e2a\uff0c\u53e6\u4e00\u4e2a\u975e\u6b7b\u4e0d\u53ef\uff0c\u4f60\u4fbf\u6551\u8c01\uff1f\u201d \u3000\u3000\u80e1\u6590\u5fae\u4e00\u6c89\u541f\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u6551\u9a6c\u59d1\u5a18\uff01\u6211\u8ddf\u4f60\u540c\u6b7b\u3002\u201d \u3000\u3000\u7a0b\u7075\u7d20\u8f6c\u8fc7\u5934\u6765\uff0c\u4f4e\u4f4e\u53eb\u4e86\u58f0\uff1a\u201c\u5927\u54e5\uff01\u201d\u4f38\u624b\u63e1\u4f4f\u4e86\u4ed6\u624b\u3002 \u3000\u3000\u80e1\u6590\u5fc3\u4e2d\u4e00\u9707\uff0c\u5ffd\u542c\u5f97\u5c4b\u5916\u811a\u6b65\u58f0\u54cd\uff0c\u5f80\u7a97\u5b54\u4e2d\u4e00\u671b\uff0c\u53eb\u9053\uff1a\u201c\u554a\u54df\uff0c\u4e0d\u597d\uff01\u201d \u3000\u3000\u53ea\u89c1\u7fa4\u76d7\u7eb7\u7eb7\u4ece\u6797\u4e2d\u8dc3\u51fa\uff0c\u624b\u4e0a\u90fd\u62d6\u7740\u6811\u679d\u67f4\u8349\uff0c\u4e0d\u4f4f\u5f80\u77f3\u5c4b\u5468\u56f4\u63b7\u6765\uff0c\u77a7\u8fd9\u60c5\u52bf\uff0c\u663e\u662f\u8981\u884c\u706b\u653b\u3002\u80e1\u6590\u548c\u7a0b\u7075\u7d20\u624b\u63e1\u7740\u624b\uff0c\u76f8\u4e92\u770b\u4e86\u4e00\u773c\uff0c\u4ece\u5bf9\u65b9\u7684\u773c\u8272\u4e4b\u4e2d\uff0c\u4e24\u4eba\u90fd\u77a7\u51fa\u5904\u5883\u5df2\u662f\u65e0\u671b\u3002 \u3000\u3000\u9a6c\u6625\u82b1\u5ffd\u7136\u7ad9\u5230\u7a97\u53e3\uff0c\u53eb\u9053\uff1a\u201c\u5582\uff0c\u4f60\u4eec\u9886\u5934\u7684\u4eba\u662f\u8c01\uff1f \u3000\u3000\u6211\u6709\u8bdd\u8ddf\u4ed6\u8bf4\u3002\u201d \u3000\u3000\u7fa4\u76d7\u4e2d\u7ad9\u51fa\u4e00\u4e2a\u7626\u7626\u5c0f\u5c0f\u7684\u8001\u8005\uff0c\u8bf4\u9053\uff1a\u201c\u9a6c\u59d1\u5a18\u6709\u8bdd\uff0c\u8bf7\u5429\u5490\u5c0f\u4eba\u5427\uff01\u201d\u9a6c\u6625\u82b1\u9053\uff1a\u201c\u6211\u8fc7\u6765\u8ddf\u4f60\u8bf4\uff0c\u4f60\u53ef\u4e0d\u5f97\u62e6\u7740\u6211\u4e0d\u653e\u3002\u201d\u90a3\u8001\u8005\u9053\uff1a\u201c\u8c01\u6709\u8fd9\u4e48\u5927\u80c6\uff0c\u6562\u62e6\u4f4f\u9a6c\u59d1\u5a18\u4e86\uff1f\u201d \u3000\u3000\u9a6c\u6625\u82b1\u8138\u4e0a\u4e00\u7ea2\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u80e1\u5144\u5f1f\uff0c\u7a0b\u5bb6\u59b9\u5b50\uff0c\u6211\u51fa\u53bb\u8ddf\u4ed6\u4eec\u8bf4\u51e0\u53e5\u8bdd\u518d\u56de\u6765\u3002\u201d\u80e1\u6590\u5fd9\u9053\uff1a\u201c\u554a\uff0c\u4f7f\u4e0d\u5f97\uff0c\u5f3a\u76d7\u8d3c\u9aa8\u5934\uff0c\u600e\u8bb2\u4fe1\u4e49\uff1f\u9a6c\u59d1\u5a18\u4f60\u8fd9\u53ef\u4e0d\u662f\u81ea\u6295\u864e\u53e3\uff1f\u201d \u3000\u3000\u9a6c\u6625\u82b1\u9053\uff1a\u201c\u56f0\u5728\u6b64\u5904\uff0c\u4e8b\u60c5\u603b\u662f\u4e0d\u4e86\u3002\u4e24\u4f4d\u9ad8\u4e49\uff0c\u6211\u7ec8\u751f\u4e0d\u5fd8\u3002\u201d \u3000\u3000\u80e1\u6590\u5fc3\u60f3\uff1a\u201c\u5979\u662f\u8981\u5c06\u4e8b\u60c5\u4e00\u4e2a\u513f\u627f\u5f53\uff0c\u597d\u8ba9\u6211\u4e24\u4eba\u4e0d\u53d7\u7275\u7d2f\u3002\u5979\u5b64\u8eab\u524d\u5f80\uff0c\u81ea\u662f\u51f6\u591a\u5409\u5c11\uff0c\u6551\u4eba\u4e0d\u6551\u5f7b\uff0c\u5c82\u662f\u5927\u4e08\u592b\u6240\u4e3a\uff1f\u201d\u773c\u770b\u9a6c\u6625\u82b1\u751a\u662f\u575a\u51b3\uff0c\u5df2\u4f38\u624b\u53bb\u62d4\u95e8\u95e9\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u4e48\u6211\u966a\u4f60\u53bb\u3002\u201d\u9a6c\u6625\u82b1\u8138\u4e0a\u53c8\u662f\u5fae\u5fae\u4e00\u7ea2\uff0c\u9053\uff1a\u201c\u4e0d\u7528\u4e86\u3002\u201d \u3000\u3000\u7a0b\u7075\u7d20\u5b9e\u5728\u731c\u6d4b\u4e0d\u900f\uff0c\u9a6c\u6625\u82b1\u4f55\u4ee5\u4f1a\u51e0\u6b21\u4e09\u756a\u7684\u8138\u7ea2\uff1f\u96be\u9053\u5979\u5bf9\u80e1\u5927\u54e5\u7adf\u4e5f\u6709\u60c5\uff1f\u60f3\u5230\u6b64\u5904\uff0c\u4e0d\u7531\u5f97\u81ea\u5df1\u4e5f\u8138\u7ea2\u4e86\u3002 \u3000\u3000\u80e1\u6590\u9053\uff1a\u201c\u597d\uff0c\u65e2\u662f\u5982\u6b64\uff0c\u6211\u53bb\u64d2\u4e00\u4e2a\u4eba\u6765\uff0c\u4f5c\u4e3a\u4eba\u8d28\u3002\u201d \u3000\u3000\u9a6c\u6625\u82b1\u9053\uff1a\u201c\u80e1\u5144\u5f1f\uff0c\u4e0d\u5fc5\u2026\u2026\u201d\u8bdd\u672a\u8bf4\u5b8c\uff0c\u80e1\u6590\u5df2\u53f3\u624b\u63d0\u8d77\u5355\u5200\uff0c\u5de6\u624b\u4e00\u63a8\u5927\u95e8\uff0c\u731b\u5730\u51b2\u4e86\u51fa\u53bb\u3002\u7fa4\u76d7\u9f50\u58f0\u5927\u547c\u3002 \u3000\u3000\u80e1\u6590\u5c55\u5f00\u8f7b\u529f\uff0c\u5f80\u659c\u523a\u91cc\u75be\u5954\u3002\u7fa4\u76d7\u9f50\u58f0\u547c\u53eb\uff1a\u201c\u5c0f\u5b50\u8981\u9003\u547d\u5566\uff01\u201d\u201c\u77f3\u5c4b\u91cc\u8fd8\u6709\u4eba\uff0c\u56db\u4e0b\u91cc\u515c\u4f4f\u3002\u201d\u201c\u5c0f\u5fc3\uff0c\u63d0\u9632\u90a3\u5c0f\u5b50\u4f7f\u8be1\u3002\u201d\u547c\u559d\u58f0\u4e2d\uff0c\u80e1\u6590\u7684\u4eba\u5f71\u4fbf\u5982\u4e00\u6e9c\u7070\u70df\u822c\u6251\u5230\u4e86\u7fa4\u76d7\u4e4b\u4e2d\u3002 \u3000\u3000\u4e24\u540d\u76d7\u4f19\u63e1\u5200\u6765\u62e6\uff0c\u80e1\u6590\u5934\u4e00\u4f4e\uff0c\u4ece\u4e24\u67c4\u5927\u5200\u4e0b\u94bb\u4e86\u8fc7\u53bb\uff0c\u5de6\u624b\u4e00\u52fe\uff0c\u60f3\u62ff\u5de6\u9996\u90a3\u4eba\u624b\u8155\u3002\u5c82\u77e5\u90a3\u4eba\u624b\u811a\u751a\u662f\u6ed1\u6e9c\uff0c\u5355\u5200\u6a2a\u626b\uff0c\u80e1\u6590\u8feb\u5f97\u4e3e\u5200\u4e00\u5c01\uff0c\u7adf\u6ca1\u62ff\u5230\u3002\u8fd9\u4e48\u7a0d\u4e00\u803d\u6401\uff0c\u53c8\u6709\u4e09\u540d\u5927\u76d7\u6251\u4e86\u4e0a\u6765\uff0c\u4e24\u6761\u94a2\u97ad\uff0c\u4e00\u6761\u94fe\u5b50\u67aa\uff0c\u767b\u65f6\u5c06\u80e1\u6590\u56f4\u5728\u5793\u5fc3\u3002 \u3000\u3000\u80e1\u6590\u5927\u58f0\u4e00\u559d\uff0c\u63d0\u5200\u731b\u5288\uff0c\u5f53\u5f53\u5f53\u4e09\u54cd\u8fc7\u53bb\uff0c\u4e24\u6761\u94a2\u97ad\u843d\u5730\uff0c\u94fe\u5b50\u67aa\u65ad\u4e3a\u4e24\u622a\uff0c\u8fd9\u4e09\u5200\u4f7f\u7684\u662f\u6781\u521a\u6781\u731b\u4e4b\u529b\uff0c\u867d\u6253\u843d\u4e86\u654c\u4eba\u4e09\u822c\u5175\u5203\uff0c\u4f46\u4ed6\u7684\u5355\u5200\u4e5f\u662f\u5203\u53e3\u5377\u8fb9\uff0c\u96be\u4ee5\u518d\u7528\u3002 \u3000\u3000\u76d7\u4f17\u89c1\u4ed6\u5982\u6b64\u795e\u52c7\uff0c\u4e0d\u81ea\u7981\u7684\u5411\u4e24\u65c1\u8ba9\u5f00\u3002 \u3000\u3000\u90a3\u8001\u8005\u559d\u9053\uff1a\u201c\u8ba9\u6211\u6765\u4f1a\u4f1a\u82f1\u96c4\u597d\u6c49\uff01\u201d\u8d64\u624b\u7a7a\u62f3\uff0c\u7331\u8eab\u4fbf\u4e0a\u3002\u80e1\u6590\u4e00\u60ca\uff1a\u201c\u6b64\u4eba\u8eab\u624b\u6c89\u7a33\uff0c\u5927\u662f\u52b2\u654c\u3002\u201d\u5de6\u624b\u4e00\u626c\uff0c\u53eb\u9053\uff1a\u201c\u7167\u9556\uff01\u201d \u3000\u3000\u90a3\u8001\u8005\u4f4f\u8db3\u51dd\u795e\uff0c\u5f85\u4ed6\u94a2\u9556\u63b7\u6765\u3002\u90a3\u77e5\u80e1\u6590\u8fd9\u4e00\u4e0b\u5374\u662f\u865a\u62db\uff0c\u5de6\u8db3\u4e00\u70b9\uff0c\u8eab\u5b50\u5ffd\u5730\u98de\u8d77\uff0c\u8d8a\u8fc7\u4e24\u540d\u5927\u76d7\u7684\u5934\u9876\uff0c\u53f3\u81c2\u4e00\u957f\uff0c\u5df2\u5c06\u4e00\u540d\u5927\u76d7\u63ea\u4e0b\u9a6c\u6765\u3002\u4ed6\u6293\u4f4f\u4e86\u8fd9\u5927\u76d7\u7684\u8109\u95e8\uff0c\u8ddf\u7740\u7ffb\u8eab\u4e0a\u9a6c\uff0c\u4ece\u4eba\u4e1b\u4e2d\u786c\u95ef\u51fa\u6765\u3002 \u3000\u3000\u90a3\u9a6c\u88ab\u80e1\u6590\u4e00\u811a\u8e22\u5728\u809a\u8179\uff0c\u5403\u75db\u4e0d\u8fc7\uff0c\u5411\u524d\u6025\u7a9c\u3002\u76d7\u4f17\u547c\u559d\u53eb\u9a82\uff0c\u6709\u7684\u4e58\u9a6c\uff0c\u6709\u7684\u6b65\u884c\uff0c\u968f\u540e\u8ffd\u8d76\u3002\u90a3\u9a6c\u5954\u51fa\u6570\u4e08\uff0c\u80e1\u6590\u53ea\u542c\u5f97\u8111\u540e\u98ce\u751f\uff0c\u4e00\u4f4e\u5934\uff0c\u4e24\u679a\u94c1\u9525\u4ece\u5934\u9876\u98de\u8fc7\uff0c\u53bb\u52bf\u5947\u52b2\uff0c\u53d1\u9525\u7684\u5b9e\u662f\u9ad8\u624b\u3002 \u3000\u3000\u80e1\u6590\u5728\u9a6c\u4e0a\u8f6c\u8fc7\u8eab\u6765\uff0c\u5012\u9a91\u978d\u4e0a\uff0c\u5c06\u90a3\u5927\u76d7\u4e3e\u5728\u80f8\u524d\uff0c\u53eb\u9053\uff1a\u201c\u53d1\u6697\u5668\u554a\uff0c\u8d8a\u591a\u8d8a\u597d\uff01\u201d\u90a3\u5927\u76d7\u7ed9\u6263\u4f4f\u8109\u95e8\uff0c\u5168\u8eab\u9178\u8f6f\uff0c\u52a8\u5f39\u4e0d\u5f97\u3002\u80e1\u6590\u54c8\u54c8\u5927\u7b11\uff0c\u4f38\u811a\u53cd\u8e22\u9a6c\u8179\uff0c\u53ea\u8e22\u4e86\u4e00\u811a\uff0c\u90a3\u9a6c\u6251\u5730\u5012\u4e86\uff0c\u539f\u6765\u5f53\u4ed6\u8f6c\u8eab\u4e4b\u524d\uff0c\u9a6c\u81c0\u4e0a\u5148\u5df2\u4e2d\u4e86\u4e00\u679a\u94c1\u9525\uff0c\u7a7f\u8179\u800c\u5165\u3002\u80e1\u6590\u4e00\u7eb5\u843d\u5730\uff0c\u6a2a\u6301\u5927\u76d7\uff0c\u4e00\u6b65\u6b65\u7684\u9000\u5165\u77f3\u5c4b\u3002 \u3000\u3000\u7fa4\u76d7\u6015\u4ed6\u52a0\u5bb3\u540c\u4f34\uff0c\u7adf\u662f\u4e0d\u6562\u4e00\u62e5\u800c\u4e0a\u3002\u7fa4\u76d7\u6789\u81ea\u6709\u4e8c\u5341\u4f59\u540d\u597d\u624b\uff0c\u5374\u7ed9\u4ed6\u4e00\u4eba\u500f\u6765\u500f\u53bb\uff0c\u6a2a\u51b2\u76f4\u649e\uff0c\u4e0d\u4f46\u6ca1\u4f24\u5230\u4ed6\u4e1d\u6beb\uff0c\u53cd\u7ed9\u4ed6\u64d2\u53bb\u4e00\u4eba\u3002\u7fa4\u76d7\u76f8\u987e\u6c14\u6cae\uff0c\u5fc3\u4e0b\u56fa\u81ea\u607c\u6012\uff0c\u5374\u4e5f\u4e0d\u7981\u6697\u6697\u4f69\u670d\u3002 \u3000\u3000\u9a6c\u6625\u82b1\u559d\u5f69\u9053\uff1a\u201c\u597d\u8eab\u624b\uff0c\u597d\u672c\u4e8b\uff01\u201d\u7f13\u6b65\u51fa\u5c4b\uff0c\u5411\u7fa4\u76d7\u4e2d\u8d70\u53bb\uff0c\u7adf\u662f\u7a7a\u624b\u4e0d\u6301\u5175\u5203\u3002 \u3000\u3000\u7fa4\u76d7\u89c1\u5979\u8d70\u8fd1\uff0c\u7eb7\u7eb7\u4e0b\u9a6c\uff0c\u8ba9\u51fa\u4e00\u6761\u8def\u6765\u3002\u9a6c\u6625\u82b1\u4e0d\u505c\u6b65\u7684\u5411\u524d\uff0c\u76f4\u5230\u79bb\u77f3\u5c4b\u4e8c\u5341\u4f59\u4e08\u4e4b\u5904\u7684\u6811\u6797\u8fb9\uff0c\u8fd9\u624d\u7acb\u5b9a\u3002 \u3000\u3000\u80e1\u6590\u548c\u7a0b\u7075\u7d20\u5728\u7a97\u4e2d\u9065\u9065\u76f8\u671b\uff0c\u89c1\u9a6c\u6625\u82b1\u80cc\u5411\u77f3\u5c4b\uff0c\u90a3\u8001\u8005\u7ad9\u5728\u5979\u9762\u524d\u8bf4\u8bdd\u3002\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u8bf4\u5979\u4e3a\u4ec0\u4e48\u8d70\u5f97\u8fd9\u4e48\u8fdc\uff1f\u82e5\u6709\u4e0d\u6d4b\uff0c\u5c82\u4e0d\u662f\u76f8\u6551\u4e0d\u53ca\uff1f\u201d\u80e1\u6590\u201c\u55ef\u201d\u4e86\u4e00\u58f0\uff0c\u4ed6\u77e5\u7a0b\u7075\u7d20\u5982\u6b64\u76f8\u95ee\uff0c\u5176\u5b9e\u5fc3\u4e2d\u65e9\u5df2\u6709\u4e86\u7b54\u6848\u3002 \u3000\u3000\u679c\u7136\uff0c\u7a0b\u7075\u7d20\u63a5\u7740\u5c31\u628a\u7b54\u6848\u8bf4\u4e86\u51fa\u6765\uff1a\u201c\u56e0\u4e3a\u5979\u548c\u7fa4\u76d7\u8bf4\u8bdd\uff0c\u4e0d\u613f\u7ed9\u54b1\u4e24\u4e2a\u542c\u89c1\uff01\u201d\u80e1\u6590\u53c8\u662f\u201c\u55ef\u201d\u7684\u4e00\u58f0\u3002\u4ed6\u77e5\u9053\u7a0b\u7075\u7d20\u7684\u731c\u6d4b\u4e0d\u9519\uff0c\u53ef\u662f\uff0c\u90a3\u53c8\u4e3a\u4ec0\u4e48\uff1f \u3000\u3000\u80e1\u6590\u548c\u7a0b\u7075\u7d20\u542c\u4e0d\u5230\u9a6c\u6625\u82b1\u548c\u7fa4\u76d7\u7684\u8bf4\u8bdd\uff0c\u4f46\u81ea\u7a97\u9065\u671b\uff0c\u5404\u4eba\u7684\u795e\u60c5\u9690\u7ea6\u53ef\u89c1\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u5927\u54e5\uff0c\u8fd9\u76d7\u9b41\u5bf9\u9a6c\u59d1\u5a18\u8bf4\u8bdd\u7684\u6a21\u6837\uff0c\u53ef\u606d\u656c\u5f97\u5f88\u54ea\uff0c\u7adf\u6ca1\u534a\u70b9\u98de\u626c\u56a3\u5f20\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u76d7\u9b41\u5f88\u6709\u6db5\u517b\uff0c\u786e\u662f\u4e2a\u52b2\u654c\u3002\u201d\u7a0b\u7075\u7d20\u8bf4\u9053\uff1a\u201c\u6211\u77a7\u4e0d\u662f\u6709\u6db5\u517b\uff0c\u5012\u50cf\u662f\u4ec6\u4eba\u8ddf\u4e3b\u5987\u7980\u62a5\u4ec0\u4e48\u4f3c\u7684\u3002\u201d\u80e1\u6590\u4e5f\u5df2\u770b\u51fa\u4e86\u8fd9\u4e00\u8282\uff0c\u5fc3\u4e2d\u9690\u9690\u89c9\u5f97\u4e0d\u5bf9\uff0c\u4f46\u60f3\u8fd9\u4e8b\u751a\u4e3a\u5c34\u5c2c\uff0c\u4e0d\u613f\u4eb2\u53e3\u8bf4\u51fa\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u77a7\u4e86\u4e00\u4f1a\uff0c\u53c8\u9053\uff1a\u201c\u9a6c\u59d1\u5a18\u5728\u6447\u5934\uff0c\u5979\u5b9a\u662f\u4e0d\u80af\u8ddf\u90a3\u76d7\u9b41\u53bb\u3002\u53ef\u662f\u5979\u4e3a\u4ec0\u4e48\u2026\u2026\u201d\u7a81\u7136\u4fa7\u8fc7\u5934\u6765\uff0c\u77a7\u7740\u80e1\u6590\u7684\u8138\uff0c\u5fc3\u4e2d\u82e5\u6709\u6240\u611f\uff0c\u53c8\u56de\u5934\u671b\u5411\u7a97\u5916\u3002 \u3000\u3000\u80e1\u6590\u9053\uff1a\u201c\u4f60\u8981\u8bf4\u4ec0\u4e48\uff1f\u4f60\u8bf4\u5979\u4e3a\u4ec0\u4e48\u2026\u2026\u600e\u5730\u4e0d\u8bf4\u4e86\uff1f\u201d \u3000\u3000\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\u8be5\u4e0d\u8be5\u95ee\u4f60\u3002\u95ee\u4e86\u51fa\u6765\uff0c\u6015\u4f60\u751f\u6c14\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u4e8c\u59b9\uff0c\u4f60\u8ddf\u6211\u5728\u8fd9\u513f\u540c\u751f\u5171\u6b7b\uff0c\u54b1\u4eec\u4e4b\u95f4\u8fd8\u6709\u4ec0\u4e48\u4e0d\u80fd\u8bf4\u7684\uff1f\u6211\u4ec0\u4e48\u90fd\u4e0d\u4f1a\u7792\u4f60\u3002\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u597d\uff01\u9a6c\u59d1\u5a18\u8ddf\u90a3\u76d7\u9b41\u8bf4\u8bdd\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u662f\u53d1\u607c\uff0c\u5374\u8981\u8138\u7ea2\uff1f\u8fd9\u8fd8\u4e0d\u5947\uff0c\u4e3a\u4ec0\u4e48\u8fde\u4f60\u4e5f\u8981\u8138\u7ea2\uff1f\u201d \u3000\u3000\u80e1\u6590\u9053\uff1a\u201c\u6211\u5728\u7591\u5fc3\u4e00\u4ef6\u4e8b\uff0c\u53ea\u662f\u5c1a\u65e0\u4f50\u8bc1\uff0c\u73b0\u4e0b\u8fd8\u4e0d\u4fbf\u660e\u8a00\u3002\u4e8c\u59b9\uff0c\u4f60\u5927\u54e5\u5149\u660e\u78ca\u843d\uff0c\u51b3\u65e0\u4e0d\u53ef\u5bf9\u4eba\u8a00\u4e4b\u4e8b\u3002\u4f60\u4fe1\u5f97\u8fc7\u6211\u4e48\uff1f\u201d\u7a0b\u7075\u7d20\u89c1\u4ed6\u795e\u8272\u6073\u5207\uff0c\u5fc3\u4e2d\u5f88\u662f\u9ad8\u5174\uff0c\u5fae\u7b11\u9053\uff1a\u201c\u90a3\u4f60\u662f\u5728\u4ee3\u5979\u8138\u7ea2\u4e86\u3002\u65c1\u4eba\u7684\u4e8b\uff0c\u6211\u7ba1\u4e0d\u7740\u3002\u53ea\u8981\u4f60\u5f88\u597d\uff0c\u90a3\u5c31\u597d\u4e86\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u6211\u521d\u8bc6\u9a6c\u59d1\u5a18\u4e4b\u65f6\uff0c\u662f\u4e2a\u5341\u4e09\u56db\u5c81\u7684\u62d6\u9f3b\u6d95\u5c0f\u53ae\u3002\u5979\u89c1\u6211\u53ef\u601c\uff0c\u8fd9\u624d\u7ed9\u6211\u6c42\u60c5\u2026\u2026\u201d\u8bf4\u5230\u8fd9\u91cc\uff0c\u62ac\u5934\u51fa\u4e86\u4f1a\u795e\uff0c\u53ea\u89c1\u5929\u8fb9\u665a\u971e\u5982\u706b\u70e7\u822c\u7ea2\uff0c\u8f7b\u8f7b\u8bf4\u9053\uff1a\u201c\u8be5\u4e0d\u8be5\u8fd9\u6837\uff0c\u6211\u4e0d\u77e5\u9053\u3002\u4f46\u6211\u76f8\u4fe1\u5979\u662f\u597d\u4eba\u2026\u2026\u5979\u826f\u5fc3\u662f\u633a\u597d\u7684\u3002\u201d \u3000\u3000\u8fd9\u65f6\u4ed6\u8eab\u540e\u90a3\u5927\u76d7\u7a81\u7136\u4e00\u58f0\u4f4e\u54fc\uff0c\u663e\u662f\u7a74\u9053\u88ab\u70b9\u540e\u9178\u75db\u96be\u5f53\u3002\u80e1\u6590\u8f6c\u8eab\u5728\u4ed6\u201c\u7ae0\u95e8\u7a74\u201d\u4e0a\u4e00\u62cd\uff0c\u53c8\u5728\u4ed6\u201c\u5929\u6c60\u7a74\u201d\u4e0a\u63a8\u62ff\u4e86\u51e0\u4e0b\uff0c\u89e3\u5f00\u4e86\u4ed6\u7684\u7a74\u9053\uff0c\u8bf4\u9053\uff1a\u201c\u4e8b\u51fa\u65e0\u5948\uff0c\u591a\u6709\u5f97\u7f6a\uff0c\u8bf7\u52ff\u89c1\u602a\u3002\u5c0a\u9a7e\u9ad8\u59d3\u5927\u540d\u3002\u201d \u3000\u3000\u90a3\u5927\u76d7\u6d53\u7709\u5de8\u773c\uff0c\u8eab\u6750\u9b41\u68a7\uff0c\u5bf9\u80e1\u6590\u6012\u76ee\u800c\u89c6\uff0c\u5927\u58f0\u9053\uff1a\u201c\u6211\u5b66\u827a\u4e0d\u7cbe\uff0c\u7ed9\u4f60\u64d2\u6765\uff0c\u8981\u6740\u8981\u5250\uff0c\u4fbf\u53ef\u52a8\u624b\uff0c\u591a\u8bf4\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u80e1\u6590\u89c1\u4ed6\u786c\u6c14\uff0c\u5012\u94a6\u670d\u4ed6\u662f\u6761\u6c49\u5b50\uff0c\u7b11\u9053\uff1a\u201c\u6211\u8ddf\u5c0a\u9a7e\u4ece\u6ca1\u4f1a\u8fc7\uff0c\u65e0\u51a4\u65e0\u4ec7\uff0c\u5c82\u6709\u76f8\u5bb3\u4e4b\u610f\uff1f\u53ea\u662f\u4eca\u65e5\u4e4b\u4e8b\u5904\u5904\u900f\u7740\u5947\u602a\uff0c\u5728\u4e0b\u5fc3\u4e2d\u4e0d\u660e\uff0c\u8001\u5144\u80fd\u4e0d\u80fd\u7565\u52a0\u70b9\u660e\uff1f\u201d\u90a3\u5927\u76d7\u5389\u58f0\u9053\uff1a\u201c\u4f60\u5f53\u6211\u6c6a\u94c1\u9e57\u662f\u5351\u9119\u5c0f\u4eba\u4e48\uff1f\u51ed\u4f60\u82b1\u8a00\u5de7\u8bed\uff0c\u4f11\u60f3\u5957\u95ee\u5f97\u51fa\u6211\u534a\u53e5\u53e3\u4f9b\u3002\u201d \u3000\u3000\u7a0b\u7075\u7d20\u4f38\u4e86\u4f38\u820c\u5934\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u80af\u8bf4\u59d3\u540d\uff0c\u8fd9\u4e0d\u662f\u8bf4\u4e86\u4e48\uff1f\u539f\u6765\u662f\u6c6a\u94c1\u9e57\u6c6a\u7237\uff0c\u4e45\u4ef0\u4e45\u4ef0\u3002\u201d\u6c6a\u94c1\u9e57\u5478\u7684\u4e00\u58f0\uff0c\u9a82\u9053\uff1a\u201c\u9ec4\u6bdb\u5c0f\u4e2b\u5934\uff0c\u4f60\u61c2\u5f97\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u7a0b\u7075\u7d20\u4e0d\u53bb\u7406\u4ed6\uff0c\u5411\u80e1\u6590\u9053\uff1a\u201c\u5927\u54e5\uff0c\u8fd9\u662f\u4e2a\u6d51\u4eba\u3002\u4e0d\u8fc7\u4ed6\u9e70\u722a\u96c1\u884c\u95e8\u7684\u524d\u8f88\u6b66\u5e08\uff0c\u8ddf\u5c0f\u59b9\u9887\u6709\u70b9\u4ea4\u60c5\u3002\u5468\u94c1\u9e6a\u3001\u66fe\u94c1\u9e25\u4ed6\u4eec\u89c1\u4e86\u6211\u90fd\u5f88\u606d\u656c\u3002\u4f60\u5c31\u4e0d\u7528\u96be\u4e3a\u4ed6\u3002\u201d\u8bf4\u7740\u5411\u80e1\u6590\u7728\u4e86\u7728\u773c\u775b\u3002 \u3000\u3000\u6c6a\u94c1\u9e57\u5927\u662f\u5947\u602a\uff0c\u9053\uff1a\u201c\u4f60\u8bc6\u5f97\u6211\u5927\u5e08\u5144\u3001\u4e8c\u5e08\u5144\u4e48\uff1f\u201d\u8bed\u6c14\u767b\u65f6\u53d8\u4e86\u3002\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u600e\u4e48\u4e0d\u8bc6\uff1f\u6211\u77a7\u4f60\u7684\u9e70\u722a\u529f\u548c\u96c1\u884c\u5200\u90fd\u6ca1\u5b66\u5f97\u5230\u5bb6\u3002\u201d\u6c6a\u94c1\u9e57\u9053\uff1a\u201c\u662f\uff01\u201d\u4f4e\u4e86\u5934\u9887\u4e3a\u60ed\u6127\u3002 \u3000\u3000\u539f\u6765\u9e70\u722a\u96c1\u884c\u95e8\u662f\u5317\u65b9\u6b66\u5b66\u4e2d\u7684\u4e00\u4e2a\u5927\u95e8\u6d3e\u3002\u95e8\u4e2d\u5927\u5f1f\u5b50\u5468\u94c1\u9e6a\u3001\u4e8c\u5f1f\u5b50\u66fe\u94c1\u9e25\u5728\u6c5f\u6e56\u4e0a\u6210\u540d\u5df2\u4e45\u3002\u7a0b\u7075\u7d20\u66fe\u542c\u5e08\u7236\u8bf4\u8d77\u8fc7\uff0c\u77e5\u9053\u4ed6\u95e8\u4e2d\u8fd9\u4e00\u4ee3\u7684\u5f1f\u5b50\uff0c\u53d6\u540d\u7b2c\u4e09\u5b57\u591a\u7528\u201c\u9e1f\u201d\u65c1\uff0c\u8fd9\u65f6\u542c\u6c6a\u94c1\u9e57\u4e00\u62a5\u540d\uff0c\u53c8\u89c1\u4ed6\u4f7f\u7684\u662f\u96c1\u7fce\u5200\uff0c\u81ea\u7136\u4e00\u731c\u4fbf\u4e2d\u3002\u81f3\u4e8e\u6c6a\u94c1\u9e57\u7684\u6b66\u529f\u6ca1\u5b66\u5230\u5bb6\uff0c\u66f4\u662f\u4e0d\u7528\u591a\u8bf4\uff0c\u4ed6\u6b66\u529f\u5018\u82e5\u5b66\u5f97\u597d\u4e86\uff0c\u53c8\u600e\u4f1a\u7ed9\u80e1\u6590\u64d2\u6765\uff1f\u4f46\u6c6a\u94c1\u9e57\u8111\u7b4b\u4e0d\u600e\u4e48\u7075\uff0c\u542c\u7a0b\u7075\u7d20\u8bf4\u5f97\u5934\u5934\u662f\u9053\uff0c\u5c45\u7136\u4fbf\u6df1\u4fe1\u4e0d\u7591\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u4f60\u4e24\u4f4d\u5e08\u54e5\u600e\u4e48\u6ca1\u8ddf\u4f60\u4e00\u8d77\u6765\uff1f\u6211\u6ca1\u89c1\u4ed6\u4eec\u554a\u3002\u201d\u5176\u5b9e\u5979\u5e76\u4e0d\u8bc6\u5f97\u5468\u94c1\u9e6a\u3001\u66fe\u94c1\u9e25\uff0c\u4f46\u60f3\u8fd9\u4e24\u4eba\u5a01\u540d\u4e0d\u5c0f\uff0c\u82e5\u5728\u76d7\u7fa4\u4e4b\u4e2d\uff0c\u5fc5\u662f\u9886\u5934\u5c45\u9996\u7684\u4eba\u7269\uff0c\u4f46\u90a3\u7626\u8001\u4eba\u548c\u5176\u4f59\u51e0\u4e2a\u76d7\u9996\u90fd\u4e0d\u4f7f\u5200\uff0c\u60f3\u6765\u5468\u66fe\u4e8c\u4eba\u5fc5\u4e0d\u5728\u5185\u3002\u8fd9\u4e00\u4e0b\u679c\u7136\u53c8\u731c\u4e2d\u4e86\u3002\u6c6a\u94c1\u9e57\u9053\uff1a\u201c\u5468\u5e08\u54e5\u548c\u66fe\u5e08\u54e5\u90fd\u7559\u5728\u5317\u4eac\u3002\u5e72\u8fd9\u4e9b\u5c0f\u4e8b\uff0c\u600e\u80fd\u52b3\u52a8\u4ed6\u4e24\u4f4d\u7684\u5927\u9a7e\uff1f\u201d\u8a00\u4e0b\u751a\u6709\u5f97\u610f\u4e4b\u8272\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u5fc3\u9053\uff1a\u201c\u4ed6\u4e8c\u4eba\u7559\u5728\u5317\u4eac\uff0c\u96be\u9053\u8fd9\u4f19\u76d7\u515a\u7adf\u662f\u4ece\u5317\u4eac\u6765\u7684\uff1f\u6211\u518d\u8bd3\u4ed6\u4e00\u8bd3\u3002\u201d\u4e8e\u662f\u8f7b\u63cf\u6de1\u5199\u7684\u9053\uff1a\u201c\u5929\u4e0b\u638c\u95e8\u4eba\u5927\u4f1a\u4e0d\u4e45\u4fbf\u8981\u5f00\u5566\u3002\u4f60\u4eec\u9e70\u722a\u96c1\u884c\u95e8\u5b9a\u8981\u5728\u4f1a\u91cc\u5927\u5927\u9732\u4e00\u9732\u8138\u3002\u4f60\u603b\u8981\u56de\u5317\u4eac\u8d76\u8fd9\u4e2a\u70ed\u95f9\u5427\uff1f\u201d\u6c5f\u94c1\u9e57\u9053\uff1a\u201c\u90a3\u8fd8\u7528\u8bf4\uff1f\u5dee\u4f7f\u4e00\u529e\u59a5\uff0c\u5927\u4f19\u5168\u5f97\u56de\u53bb\u3002\u201d \u3000\u3000\u80e1\u6590\u548c\u7a0b\u7075\u7d20\u5fc3\u4e2d\u90fd\u662f\u4e00\u6014\uff1a\u201c\u4ec0\u4e48\u5dee\u4f7f\uff1f\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u8d35\u5be8\u4f17\u4f4d\u5f53\u5bb6\u7684\u53d7\u4e86\u62db\u5b89\uff0c\u7ed9\u7687\u4e0a\u51fa\u529b\uff0c\u90a3\u662f\u5149\u7956\u8000\u5b97\u7684\u4e8b\u54ea\u3002\u201d\u4e0d\u6599\u8fd9\u4e00\u731c\u6d4b\u53ef\u51fa\u4e86\u5c94\u513f\uff0c\u7a0b\u7075\u7d20\u53ea\u9053\u4ed6\u4eec\u90fd\u662f\u76d7\u4f19\uff0c\u5374\u5728\u529e\u5dee\uff0c\u90a3\u4e0d\u662f\u53d7\u4e86\u62db\u5b89\u662f\u4ec0\u4e48\uff1f\u90a3\u77e5\u6c6a\u94c1\u9e57\u4e00\u5bf9\u7ec6\u7ec6\u7684\u773c\u775b\u4e00\u7ffb\uff0c\u8bf4\u9053\uff1a\u201c\u4ec0\u4e48\u62db\u5b89\uff1f\u4f60\u5f53\u6211\u4eec\u771f\u662f\u76d7\u8d3c\u4e48\uff1f\u201d\u7a0b\u7075\u7d20\u6697\u53eb\uff1a\u201c\u4e0d\u597d\uff01\u201d\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u88c5\u4f5c\u662f\u9ed1\u9053\u4e0a\u7684\u670b\u53cb\uff0c\u5927\u5bb6\u5fc3\u7167\u4e0d\u5ba3\uff0c\u53c8\u4f55\u5fc5\u70b9\u7a7f\uff1f\u201d \u3000\u3000\u5979\u867d\u7136\u63a9\u9970\u5f97\u4f3c\u4e4e\u4e1d\u6beb\u6ca1\u9732\u75d5\u8ff9\uff0c\u4f46\u6c6a\u94c1\u9e57\u7ec8\u4e8e\u8d77\u4e86\u7591\u5fc3\uff0c\u7a0b\u7075\u7d20\u518d\u7528\u8a00\u8bed\u76f8\u9017\uff0c\u4ed6\u53ea\u662f\u77aa\u7740\u773c\u775b\uff0c\u4e00\u8a00\u4e0d\u53d1\u3002 \u3000\u3000\u80e1\u6590\u5ffd\u9053\uff1a\u201c\u4e8c\u59b9\uff0c\u4f60\u65e2\u8bc6\u5f97\u8fd9\u4f4d\u6c6a\u5144\u7684\u5e08\u54e5\uff0c\u54b1\u4eec\u4e0d\u4fbf\u518d\u884c\u7559\u96be\u3002\u6c6a\u5144\uff0c\u4f60\u8bf7\u56de\u5427\uff01\u201d\u6c6a\u94c1\u9e57\u6115\u7136\u7ad9\u8d77\u3002 \u3000\u3000\u80e1\u6590\u6253\u5f00\u77f3\u5ba4\u7684\u6728\u95e8\uff0c\u8bf4\u9053\uff1a\u201c\u5f97\u7f6a\u83ab\u602a\uff0c\u540e\u4f1a\u6709\u671f\u3002\u201d\u6c6a\u94c1\u9e57\u4e0d\u77e5\u4ed6\u8981\u4f7f\u4ec0\u4e48\u8be1\u8ba1\uff0c\u4e0d\u6562\u8de8\u6b65\u3002\u7a0b\u7075\u7d20\u62c9\u62c9\u80e1\u6590\u7684\u8863\u89d2\uff0c\u8fde\u4f7f\u773c\u8272\u3002\u80e1\u6590\u4e00\u7b11\u9053\uff1a\u201c\u5c0f\u5f1f\u80e1\u6590\uff0c\u6211\u4e49\u59b9\u7a0b\u7075\u7d20\uff0c\u591a\u591a\u62dc\u4e0a\u5468\u66fe\u4e24\u4f4d\u6b66\u5e08\u3002\u201d\u8bf4\u7740\u8f7b\u8f7b\u5f80\u6c6a\u94c1\u9e57\u8eab\u540e\u4e00\u63a8\uff0c\u5c06\u4ed6\u63a8\u51fa\u95e8\u5916\u3002\u6c6a\u94c1\u9e57\u5927\u60d1\u4e0d\u89e3\uff0c\u4ecd\u662f\u8fdf\u7591\u7740\u5e76\u4e0d\u4e3e\u6b65\uff0c\u56de\u5934\u4e00\u671b\uff0c\u5374\u89c1\u6728\u95e8\u5df2\u7136\u5173\u4e0a\uff0c\u8fd9\u624d\u5411\u524d\u8d70\u4e86\u51e0\u6b65\uff0c\u8ddf\u7740\u53c8\u5012\u9000\u51e0\u6b65\uff0c\u751f\u6015\u80e1\u6590\u5728\u81ea\u5df1\u80cc\u540e\u53d1\u5c04\u6697\u5668\uff0c\u5f85\u9000\u5230\u4e94\u516d\u4e08\u5916\uff0c\u89c1\u77f3\u5ba4\u4e2d\u59cb\u7ec8\u6ca1\u6709\u52a8\u9759\uff0c\u8fd9\u624d\u8f6c\u8eab\uff0c\u98de\u4e5f\u4f3c\u7684\u5954\u5165\u6811\u6797\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u5927\u54e5\uff0c\u6211\u662f\u4fe1\u53e3\u5f00\u6cb3\u554a\uff0c\u8c01\u8bc6\u5f97\u4ed6\u7684\u5468\u94c1\u9e21\u3001\u66fe\u94c1\u9e2d\u4e86\uff0c\u4f60\u600e\u5730\u4fe1\u4ee5\u4e3a\u771f\uff0c\u653e\u4e86\u4ed6\u53bb\uff1f\u201d\u80e1\u6590\u9053\uff1a\u201c\u6211\u77a7\u8fd9\u4e9b\u4eba\u51b3\u4e0d\u6562\u4f24\u5bb3\u9a6c\u59d1\u5a18\u3002\u518d\u8bf4\uff0c\u6c6a\u94c1\u9e57\u662f\u4e2a\u6d51\u4eba\uff0c\u8fd9\u4e9b\u76d7\u4f19\u672a\u5fc5\u770b\u91cd\u4ed6\u3002\u4ed6\u4eec\u771f\u8981\u5bf9\u9a6c\u59d1\u5a18\u6709\u4ec0\u4e48\u7559\u96be\uff0c\u4e5f\u4e0d\u4f1a\u987e\u60dc\u8fd9\u4e2a\u6d51\u4eba\u3002\u201d\u7a0b\u7075\u7d20\u8d5e\u9053\uff1a\u201c\u4f60\u60f3\u5f97\u6781\u662f\u2026\u2026\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u7a97\u5b54\u4e2d\u671b\u89c1\u9a6c\u6625\u82b1\u7f13\u6b65\u800c\u56de\uff0c\u7fa4\u76d7\u606d\u606d\u656c\u656c\u7684\u9001\u5230\u6797\u8fb9\uff0c\u4e0d\u518d\u524d\u884c\uff0c\u4efb\u5979\u72ec\u81ea\u56de\u8fdb\u77f3\u5c4b\u3002 \u3000\u3000\u80e1\u7a0b\u4e8c\u4eba\u773c\u4e2d\u9732\u51fa\u8be2\u95ee\u4e4b\u8272\uff0c\u4f46\u5747\u4e0d\u5f00\u53e3\u3002\u9a6c\u6625\u82b1\u9053\uff1a\u201c\u4ed6\u4eec\u90fd\u79f0\u8d5e\u80e1\u5144\u5f1f\u6b66\u529f\u65e2\u9ad8\uff0c\u4eba\u53c8\u4ec1\u4e49\uff0c\u5b9e\u662f\u4f4d\u5c11\u5e74\u82f1\u96c4\u3002\u201d \u3000\u3000\u80e1\u6590\u8c26\u900a\u4e86\u51e0\u53e5\uff0c\u89c1\u5979\u5446\u5446\u51fa\u795e\uff0c\u6ca1\u518d\u63a5\u8bf4\u4e0b\u6587\uff0c\u4e5f\u4e0d\u4fbf\u518d\u95ee\u3002 \u3000\u3000\u9694\u4e86\u534a\u664c\uff0c\u9a6c\u6625\u82b1\u9053\uff1a\u201c\u80e1\u5144\u5f1f\uff0c\u7a0b\u5bb6\u59b9\u5b50\uff0c\u4f60\u4eec\u8d70\u5427\u3002 \u3000\u3000\u6211\u7684\u4e8b\u2026\u2026\u4f60\u4eec\u4e24\u4f4d\u5e2e\u4e0d\u4e86\u5fd9\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u4f60\u672a\u8131\u9669\u5883\uff0c\u6211\u600e\u80fd\u820d\u4f60\u800c\u53bb\uff1f\u201d\u9a6c\u6625\u82b1\u9053\uff1a\u201c\u6211\u5728\u8fd9\u91cc\u6ca1\u6709\u5371\u9669\uff0c\u4ed6\u4eec\u4e0d\u6562\u5bf9\u6211\u600e\u6837\u3002\u201d\u80e1\u6590\u5fc3\u60f3\uff1a\u201c\u8fd9\u4e24\u53e5\u8bdd\u591a\u6015\u786e\u662f\u5b9e\u60c5\uff0c\u4f46\u8ba9\u5979\u5b64\u8eab\u7559\u5728\u8fd9\u91cc\uff0c\u600e\u80fd\u5b89\u5fc3\uff1f\u201d \u3000\u3000\u4f46\u89c1\u5979\u8138\u4e0a\u4e00\u9635\u7ea2\uff0c\u4e00\u9635\u767d\uff0c\u5ffd\u7136\u6ceb\u7136\u6b32\u6ce3\uff0c\u5ffd\u800c\u5634\u89d2\u8fb9\u9732\u51fa\u5fae\u7b11\uff0c\u80e1\u6590\u548c\u7a0b\u7075\u7d20\u76f8\u987e\u53d1\u6014\u3002\u77f3\u5ba4\u5185\u5916\uff0c\u4e00\u7247\u5bc2\u9759\u3002 \u3000\u3000\u80e1\u6590\u62c9\u62c9\u7a0b\u7075\u7d20\u7684\u8863\u89d2\uff0c\u4e24\u4eba\u8d70\u5230\u7a97\u8fb9\uff0c\u5411\u5916\u89c2\u671b\u3002\u80e1\u6590\u4f4e\u58f0\u9053\uff1a\u201c\u4e8c\u59b9\uff0c\u4f60\u8bf4\u600e\u4e48\u529e\uff1f\u201d\u7a0b\u7075\u7d20\u4f4e\u58f0\u9053\uff1a\u201c\u5927\u4ec1\u5927\u4e49\u7684\u5c11\u5e74\u82f1\u96c4\u8bf4\u600e\u4e48\u529e\uff0c\u9ec4\u6bdb\u4e2b\u5934\u4fbf\u4e5f\u600e\u4e48\u529e\u3002\u201d\u80e1\u6590\u6084\u58f0\u9053\uff1a\u201c\u6211\u7591\u5fc3\u7740\u4e00\u4ef6\u4e8b\uff0c\u53ef\u662f\u65e0\u8bba\u5982\u4f55\u4e0d\u4fbf\u4eb2\u53e3\u95ee\u5979\uff0c\u8fd9\u822c\u50f5\u6301\u4e0b\u53bb\uff0c\u7ec8\u4e5f\u4e0d\u662f\u4e86\u5c40\u3002\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u6211\u731c\u4e0a\u4e00\u731c\u3002\u4f60\u8bf4\u6709\u4e2a\u59d3\u5546\u7684\uff0c\u5f53\u5e74\u5bf9\u5979\u9887\u6709\u60c5\u610f\uff0c\u662f\u4e0d\u662f\uff1f\u201d\u80e1\u6590\u9053\uff1a\u201c\u662f\u554a\uff0c\u4f60\u771f\u806a\u660e\u3002\u6211\u7591\u5fc3\u8fd9\u4f19\u4eba\u90fd\u662f\u53d7\u5546\u5b9d\u9707\u4e4b\u6258\u800c\u6765\uff0c\u56e0\u6b64\u5bf9\u9a6c\u59d1\u5a18\u751a\u662f\u5ba2\u6c14\uff0c\u5bf9\u5979\u4e08\u592b\u5374\u4e0d\u65ad\u7684\u8baa\u7b11\u7f9e\u8fb1\u3002\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u770b\u6765\u9a6c\u59d1\u5a18\u5bf9\u90a3\u59d3\u5546\u7684\u8fd8\u662f\u672a\u514d\u6709\u60c5\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u56e0\u6b64\u6211\u5c31\u4e0d\u77e5\u9053\u600e\u4e48\u529e\u4e86\u3002\u201d \u3000\u3000\u4e24\u4eba\u8bf4\u8bdd\u4e4b\u65f6\uff0c\u6ca1\u77a7\u7740\u5bf9\u65b9\uff0c\u53ea\u662f\u53e3\u5507\u8f7b\u8f7b\u800c\u52a8\uff0c\u9a6c\u6625\u82b1\u5750\u5728\u5c4b\u89d2\uff0c\u4e0d\u4f1a\u542c\u5230\u3002 \u3000\u3000\u773c\u89c1\u5f97\u665a\u971e\u6e10\u6de1\uff0c\u5929\u8272\u6162\u6162\u9ed1\u4e86\u4e0b\u6765\uff0c\u7a81\u7136\u95f4\u897f\u9996\u8fde\u58f0\u553f\u54e8\uff0c\u6709\u51e0\u4e58\u9a6c\u5954\u6765\u3002\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u53c8\u6765\u4e86\u5e2e\u624b\u3002\u201d\u80e1\u6590\u4fa7\u8033\u4e00\u542c\uff0c\u9053\uff1a\u201c\u600e\u5730\u6709\u4e00\u4eba\u6b65\u884c\uff1f\u201d\u679c\u7136\u8fc7\u4e0d\u591a\u65f6\uff0c\u4e00\u4e2a\u4eba\u98de\u6b65\u5954\u8fd1\uff0c\u540e\u9762\u56db\u9a91\u9a6c\u6210\u6247\u5f62\u6563\u5f00\u7740\u8ffd\u8d76\u3002\u4f46\u9a6c\u4e0a\u56db\u4eba\u4f3c\u4e4e\u5b58\u5fc3\u620f\u5f04\uff0c\u5e76\u6ca1\u50ac\u9a6c\uff0c\u53e3\u4e2d\u5406\u559d\u553f\u54e8\uff0c\u59cb\u7ec8\u79bb\u524d\u9762\u5954\u9003\u4e4b\u4eba\u4e24\u4e09\u4e08\u8fdc\u3002\u90a3\u4eba\u5934\u53d1\u6563\u4e71\uff0c\u811a\u6b65\u8e09\u8dc4\uff0c\u663e\u5df2\u7b4b\u75b2\u529b\u5c3d\u3002 \u3000\u3000\u80e1\u6590\u770b\u6e05\u4e86\u90a3\u4eba\u9762\u76ee\uff0c\u53eb\u9053\uff1a\u201c\u5f90\u5927\u54e5\uff0c\u5230\u8fd9\u91cc\u6765\uff01\u201d\u8bf4\u7740\u6253\u5f00\u6728\u95e8\uff0c\u5f85\u8981\u8d76\u51fa\u53bb\u63a5\u5e94\uff0c\u4f46\u4e3a\u65f6\u5df2\u7136\u4e0d\u53ca\uff0c\u56db\u9a91\u9a6c\u4ece\u65c1\u7ed5\u4e86\u4e0a\u6765\uff0c\u62e6\u4f4f\u5f90\u94ee\u7684\u53bb\u8def\u3002\u6797\u4e2d\u76d7\u4f17\u4e5f\u4e00\u62e5\u800c\u51fa\u3002 \u3000\u3000\u80e1\u6590\u82e5\u662f\u51b2\u51fa\uff0c\u53ea\u6015\u7fa4\u76d7\u4e58\u673a\u62a2\u5165\u5c4b\u6765\uff0c\u7a0b\u7075\u7d20\u548c\u9a6c\u6625\u82b1\u4fbf\u8981\u5403\u4e8f\uff0c\u53ea\u597d\u773c\u7741\u7741\u77a7\u7740\u5f90\u94ee\u7ed9\u7fa4\u76d7\u56f4\u4f4f\u3002\u80e1\u6590\u7eb5\u58f0\u53eb\u9053\uff1a\u201c\u501a\u591a\u4e3a\u80dc\uff0c\u7b97\u4ec0\u4e48\u82f1\u96c4\u597d\u6c49\uff1f\u201d\u7eb5\u9a6c\u8ffd\u6765\u7684\u56db\u4e2a\u6c49\u5b50\u4e2d\u4e00\u4eba\u53eb\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u6b63\u8981\u5355\u6253\u72ec\u6597\uff0c\u4f1a\u4e00\u4f1a\u795e\u62f3\u65e0\u6545\u7684\u9ad8\u5f92\uff0c\u6597\u4e00\u6597\u98de\u9a6c\u9556\u5c40\u7684\u5f90\u5927\u9556\u5934\u3002\u201d\u80e1\u6590\u542c\u8fd9\u58f0\u97f3\u597d\u719f\uff0c\u51dd\u76ee\u4e00\u671b\uff0c\u5931\u58f0\u53eb\u9053\uff1a\u201c\u662f\u5546\u5b9d\u9707\uff01\u201d \u3000\u3000\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u8fd9\u59d3\u5546\u7684\u679c\u771f\u6765\u4e86\uff01\u201d\u4f46\u89c1\u4ed6\u8eab\u5f62\u633a\u62d4\uff0c\u767d\u51c0\u9762\u76ae\uff0c\u786e\u662f\u6bd4\u6ee1\u8138\u75a4\u75d5\u7684\u5f90\u94ee\u4fca\u96c5\u5341\u500d\uff0c\u53c8\u89c1\u4ed6\u4ece\u9a6c\u80cc\u4e0a\u7ffb\u978d\u800c\u4e0b\uff0c\u8eab\u6cd5\u6f47\u6d12\u5229\u843d\uff0c\u5fc3\u60f3\uff1a\u201c\u4ed6\u548c\u9a6c\u59d1\u5a18\u624d\u7b97\u662f\u4e00\u5bf9\u513f\uff0c\u65e0\u602a\u90a3\u4e9b\u4eba\u8981\u6253\u4ec0\u4e48\u62b1\u4e0d\u5e73\uff0c\u8bf4\u751a\u4e48\u9c9c\u82b1\u63d2\u5728\u725b\u7caa\u4e0a\u3002\u201d\u5979\u7a76\u7adf\u662f\u5e74\u8f7b\u59d1\u5a18\uff0c\u5fcd\u4e0d\u4f4f\u53eb\u9053\uff1a\u201c\u9a6c\u5bb6\u59ca\u59ca\uff0c\u90a3\u59d3\u5546\u7684\u6765\u5566\uff01\u201d\u9a6c\u6625\u82b1\u201c\u55ef\u201d\u7684\u4e00\u58f0\uff0c\u4f3c\u4e4e\u6ca1\u61c2\u5f97\u7a0b\u7075\u7d20\u5728\u8bf4\u4e9b\u4ec0\u4e48\u3002 \u3000\u3000\u8fd9\u65f6\u7fa4\u76d7\u5df2\u56f4\u6210\u4e86\u8001\u5927\u4e00\u4e2a\u5708\u5b50\uff0c\u906e\u4f4f\u4e86\u4ece\u77f3\u5ba4\u7a97\u4e2d\u671b\u51fa\u53bb\u7684\u76ee\u5149\u3002\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u5927\u54e5\uff0c\u8fd9\u91cc\u77a7\u4e0d\u89c1\uff0c\u54b1\u4eec\u4e0a\u5c4b\u9876\u53bb\u3002\u201d \u3000\u3000\u80e1\u6590\u9053\uff1a\u201c\u597d\uff01\u201d \u3000\u3000\u4e24\u4eba\u8dc3\u4e0a\u5c4b\u9876\uff0c\u671b\u89c1\u5f90\u94ee\u548c\u5546\u5b9d\u9707\u6012\u76ee\u76f8\u5411\u3002\u5546\u5b9d\u9707\u624b\u63d0\u4e00\u67c4\u539a\u80cc\u8584\u5203\u7684\u5355\u5200\uff0c\u5f90\u94ee\u5374\u662f\u7a7a\u624b\u3002\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u8fd9\u53ef\u4e0d\u516c\u5e73\u3002\u201d\u80e1\u6590\u5c1a\u672a\u7b54\u8bdd\uff0c\u53ea\u542c\u5f97\u5546\u5b9d\u9707\u5927\u58f0\u9053\uff1a\u201c\u5f90\u7237\uff0c\u5546\u67d0\u8ddf\u4f60\u52a8\u624b\uff0c\u7528\u4e0d\u7740\u501a\u591a\u4e3a\u80dc\uff0c\u4e5f\u4e0d\u80fd\u6b3a\u4f60\u7a7a\u624b\u3002\u4f60\u7528\u5200\uff0c\u6211\u7a7a\u624b\uff0c\u8fd9\u4e48\u7740\u4f60\u603b\u4e0d\u5403\u4e8f\u4e86\u5427\uff1f\u201d\u8bf4\u7740\u63d0\u5200\u4e00\u63b7\uff0c\u7adf\u628a\u624b\u4e2d\u5355\u5200\u67c4\u524d\u5203\u540e\u7684\u5411\u5f90\u94ee\u63b7\u53bb\u3002 \u3000\u3000\u5f90\u94ee\u4f38\u624b\u63a5\u4f4f\uff0c\u547c\u547c\u5598\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u5728\u5546\u5bb6\u5821\u4e2d\uff0c\u4f60\u5bf9\u6211\u5e08\u59b9\u8fd9\u822c\u6a21\u6837\uff0c\u4f60\u5f53\u6211\u6ca1\u751f\u773c\u775b\u4e48\uff1f\u4f60\u4eca\u65e5\u7a7a\u7fa4\u800c\u6765\uff0c\u4e3a\u7684\u662f\u4ec0\u4e48\uff0c\u59d3\u5f90\u7684\u4e0d\u5fc5\u591a\u8bf4\u3002\u5546\u5b9d\u9707\uff0c\u4f60\u62ff\u5200\u5b50\u5427\uff01\u201d\u5546\u5b9d\u9707\u9ad8\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u4fbf\u51ed\u4e00\u53cc\u8089\u638c\uff0c\u6597\u4f60\u7684\u5355\u5200\u3002\u4f17\u4f4d\u5927\u54e5\uff0c\u5982\u6211\u4f24\u5728\u4ed6\u7684\u5200\u4e0b\uff0c\u53ea\u6028\u6211\u72c2\u5984\u81ea\u5927\uff0c\u4efb\u8c01\u4e0d\u5f97\u76f8\u52a9\u3002\u201d \u3000\u3000\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u4ed6\u4e3a\u4ec0\u4e48\u8fd9\u822c\u5927\u58f0\uff1f\u663e\u662f\u8981\u8bf4\u7ed9\u9a6c\u59d1\u5a18\u542c\u4e86\u3002 \u3000\u3000\u4ed6\u7a7a\u624b\u6597\u4eba\u5bb6\u5355\u5200\uff0c\u4e0d\u4f46\u662f\u5728\u5fc3\u4e0a\u4eba\u9762\u524d\u901e\u80fd\uff0c\u8fd8\u8981\u6253\u52a8\u5979\u7684\u5fc3\u3002\u201d\u80e1\u6590\u53f9\u4e86\u4e00\u53e3\u6c14\u3002\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u8bf4\u9a6c\u59d1\u5a18\u76fc\u671b\u8c01\u80dc\uff1f\u201d\u80e1\u6590\u6447\u5934\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\u3002\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u4e00\u4e2a\u662f\u4e08\u592b\uff0c\u4e00\u4e2a\u662f\u5916\u4eba\uff0c\u773c\u4e0b\u6b63\u5728\u4e3a\u4e86\u5979\u62da\u547d\uff0c\u5979\u5374\u8eb2\u5728\u5c4b\u91cc\u7406\u4e5f\u4e0d\u7406\u3002\u6211\u8bf4\u9a6c\u59d1\u5a18\u79c1\u5fc3\u4e4b\u4e2d\uff0c\u53ea\u6015\u8fd8\u5728\u76fc\u671b\u8fd9\u4f4d\u5546\u5c11\u7237\u5f97\u80dc\u5462\u3002\u201d\u80e1\u6590\u5fc3\u4e2d\u7684\u60f3\u6cd5\u4e5f\u662f\u5982\u6b64\uff0c\u4f46\u4ecd\u662f\u6447\u5934\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u5f90\u94ee\u89c1\u5546\u5b9d\u9707\u5b9a\u7136\u4e0d\u80af\u7528\u5175\u5203\uff0c\u5355\u5200\u4e00\u6a2a\uff0c\u8bf4\u9053\uff1a\u201c\u53cd\u6b63\u59d3\u5f90\u7684\u9677\u5165\u91cd\u56f4\uff0c\u4eca\u65e5\u4e5f\u4e0d\u60f3\u6d3b\u7740\u56de\u53bb\u4e86\u3002\u201d\u5237\u7684\u4e00\u5200\uff0c\u5f80\u5546\u5b9d\u9707\u5934\u9876\u780d\u843d\u3002\u5546\u5b9d\u9707\u6b66\u529f\u672c\u5c31\u9ad8\u51fa\u4ed6\u751a\u591a\uff0c\u5f53\u5e74\u5728\u5546\u5bb6\u5821\u5411\u4ed6\u8ba8\u6559\u62f3\u811a\uff0c\u53ea\u662f\u88c5\u8154\u4f5c\u52bf\uff0c\u8fd9\u6570\u5e74\u4e2d\u8ddf\u7740\u516b\u5366\u95e8\u4e2d\u7684\u5e08\u4f2f\u5e08\u53d4\u738b\u6c0f\u5144\u5f1f\u75db\u4e0b\u82e6\u529f\uff0c\u516b\u5366\u5200\u548c\u516b\u5366\u638c\u7684\u529f\u592b\u66f4\u662f\u7cbe\u8fdb\u3002 \u3000\u3000\u5f90\u94ee\u5954\u9003\u4e86\u534a\u65e5\uff0c\u6c14\u529b\u8870\u7aed\uff0c\u624b\u4e2d\u867d\u7136\u591a\u4e86\u4e00\u53e3\u5200\uff0c\u4f46\u5728\u5546\u5b9d\u9707\u516b\u5366\u638c\u51fb\u3001\u6253\u3001\u5288\u3001\u62ff\u4e4b\u4e0b\uff0c\u4e0d\u6570\u62db\u4fbf\u843d\u4e0b\u98ce\u3002 \u3000\u3000\u80e1\u6590\u76b1\u7709\u9053\uff1a\u201c\u8fd9\u59d3\u5546\u7684\u751a\u662f\u72e1\u6ed1\u2026\u2026\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u4f60\u8981\u4e0d\u8981\u51fa\u624b\uff1f\u201d\u80e1\u6590\u9053\uff1a\u201c\u6211\u662f\u4e3a\u52a9\u9a6c\u59d1\u5a18\u800c\u6765\uff0c\u4f46\u662f\u2026\u2026\u4f46\u662f\u2026\u2026\uff0c\u6211\u53ef\u771f\u4e0d\u77e5\u5979\u5fc3\u610f\u5982\u4f55\uff1f\u201d\u7a0b\u7075\u7d20\u5bf9\u9a6c\u6625\u82b1\u751a\u662f\u4e0d\u6ee1\uff0c\u8bf4\u9053\uff1a\u201c\u9a6c\u59d1\u5a18\u51b3\u65e0\u5371\u9669\uff0c\u4f60\u597d\u5fc3\u76f8\u52a9\uff0c\u5979\u53ef\u672a\u5fc5\u9886\u4f60\u8fd9\u4e2a\u60c5\u3002 \u3000\u3000\u54b1\u4eec\u4e0d\u5982\u8d70\u5427\uff01\u201d\u80e1\u6590\u89c1\u5f90\u94ee\u7684\u5355\u5200\u7ed9\u5546\u5b9d\u9707\u638c\u529b\u903c\u4f4f\u4e86\uff0c\u780d\u51fa\u53bb\u65f6\u4e1c\u5012\u897f\u6b6a\uff0c\u5df2\u662f\u5168\u7136\u4e0d\u6210\u7ae0\u6cd5\uff0c\u77a7\u7740\u751a\u662f\u51c4\u60e8\uff0c\u8bf4\u9053\uff1a\u201c\u4e8c\u59b9\uff0c\u4f60\u8bf4\u7684\u662f\uff0c\u8fd9\u4ef6\u4e8b\u54b1\u4eec\u7ba1\u4e0d\u4e86\u3002\u201d \u3000\u3000\u4ed6\u8dc3\u4e0b\u5c4b\u9876\uff0c\u56de\u5165\u77f3\u5ba4\uff0c\u8bf4\u9053\uff1a\u201c\u9a6c\u59d1\u5a18\uff0c\u5f90\u5927\u54e5\u5feb\u652f\u6301\u4e0d\u4f4f\u4e86\uff0c\u90a3\u59d3\u5546\u7684\u53ea\u6015\u8981\u4e0b\u6bd2\u624b\u3002\u201d\u9a6c\u6625\u82b1\u5446\u5446\u51fa\u795e\uff0c\u201c\u55ef\u201d\u4e86\u4e00\u58f0\u3002\u80e1\u6590\u6012\u706b\u4e0a\u51b2\uff0c\u4fbf\u4e0d\u518d\u8bf4\uff0c\u5411\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u4e8c\u59b9\uff0c\u54b1\u4eec\u8d70\u5427\uff01\u201d\u9a6c\u6625\u82b1\u4f3c\u4e4e\u7a81\u7136\u4ece\u68a6\u4e2d\u9192\u89c9\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u8981\u8d70\uff1f\u4e0a\u54ea\u91cc\u53bb\uff1f\u201d\u80e1\u6590\u6602\u7136\u9053\uff1a\u201c\u9a6c\u59d1\u5a18\uff0c\u4f60\u4ece\u524d\u4e3a\u6211\u6c42\u60c5\uff0c\u6211\u4e00\u76f4\u611f\u6fc0\uff0c\u4f46\u4f60\u5bf9\u5f90\u5927\u54e5\u8fd9\u822c\u2026\u2026\u201d \u3000\u3000\u4ed6\u8bdd\u672a\u8bf4\u5b8c\uff0c\u731b\u542c\u5f97\u8fdc\u5904\u4e00\u58f0\u60e8\u53eb\uff0c\u6b63\u662f\u5f90\u94ee\u7684\u58f0\u97f3\uff0c\u8ddf\u7740\u5546\u5b9d\u9707\u7eb5\u58f0\u957f\u7b11\uff0c\u7b11\u58f0\u4e2d\u5145\u6ee1\u4e86\u5f97\u610f\u4e4b\u60c5\u3002\u7fa4\u76d7\u8f70\u7136\u559d\u5f69\uff1a \u3000\u3000\u201c\u597d\u516b\u5366\u638c\uff01\u201d \u3000\u3000\u9a6c\u6625\u82b1\u4e00\u60ca\uff0c\u53eb\u9053\uff1a\u201c\u5e08\u54e5\uff01\u201d\u5411\u5916\u51b2\u51fa\u3002\u80e1\u6590\u6068\u6068\u7684\u9053\uff1a\u201c\u60c5\u4eba\u6253\u6b7b\u4e86\u4e08\u592b\uff0c\u6b63\u5408\u5fc3\u610f\uff01\u201d\u7a0b\u7075\u7d20\u89c1\u4ed6\u6124\u6068\u96be\u5f53\uff0c\u67d4\u58f0\u5b89\u6170\u9053\uff1a\u201c\u8fd9\u79cd\u4e8b\u4f60\u4fbf\u6709\u5929\u5927\u7684\u672c\u4e8b\uff0c\u4e5f\u6ca1\u6cd5\u5b50\u7ba1\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u5979\u82e5\u662f\u4e0d\u7231\u5979\u5e08\u54e5\uff0c\u53c8\u4f55\u5fc5\u548c\u4ed6\u6210\u4eb2\uff1f\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u90a3\u5b9a\u662f\u8feb\u4e8e\u7236\u4eb2\u4e4b\u547d\u4e86\u3002\u201d\u80e1\u6590\u6447\u5934\u9053\uff1a\u201c\u4e0d\uff0c\u5979\u7236\u4eb2\u65e9\u70e7\u6b7b\u5728\u5546\u5bb6\u5821\u4e2d\u4e86\u3002\u4fbf\u7b97\u66fe\u6709\u5a5a\u7ea6\uff0c\u4e5f\u53ef\u6bc1\u4e86\uff0c\u603b\u80dc\u8fc7\u843d\u5f97\u8fd9\u822c\u4e0b\u573a\u3002\u201d \u3000\u3000\u5ffd\u542c\u5f97\u4eba\u4e1b\u4e2d\u53c8\u4f20\u51fa\u5f90\u94ee\u7684\u4e00\u58f0\u547b\u541f\uff0c\u80e1\u6590\u559c\u9053\uff1a\u201c\u5f90\u5927\u54e5\u6ca1\u6b7b\uff0c\u77a7\u77a7\u53bb\u3002\u201d\u8bf4\u7740\u62c9\u7740\u7a0b\u7075\u7d20\u7684\u624b\u8d70\u51fa\u77f3\u5c4b\uff0c\u6025\u6b65\u6324\u5165\u76d7\u7fa4\u4e4b\u4e2d\u3002 \u3000\u3000\u8bf4\u4e5f\u5947\u602a\uff0c\u6ca1\u591a\u4e45\u4e4b\u524d\uff0c\u7fa4\u76d7\u548c\u80e1\u6590\u4e00\u653b\u4e00\u5b88\uff0c\u5217\u9635\u5bf9\u5792\uff0c\u4f46\u8fd9\u65f6\u7fa4\u76d7\u53ea\u6ce8\u89c6\u9a6c\u6625\u82b1\u3001\u5546\u5b9d\u9707\u3001\u5f90\u94ee\u4e09\u4eba\uff0c\u5bf9\u80e1\u7a0b\u4e8c\u4eba\u5954\u8fd1\u7adf\u90fd\u4e0d\u4ee5\u4e3a\u610f\u3002 \u3000\u3000\u80e1\u6590\u4f4e\u5934\u770b\u5f90\u94ee\u65f6\uff0c\u53ea\u89c1\u4ed6\u80f8\u53e3\u4e00\u5927\u6ee9\u9c9c\u8840\uff0c\u6c14\u606f\u5fae\u5f31\uff0c\u663e\u662f\u7ed9\u5546\u5b9d\u9707\u638c\u529b\u9707\u4f24\u4e86\u5185\u810f\uff0c\u8f6c\u773c\u4fbf\u8981\u65ad\u6c14\u3002\u9a6c\u6625\u82b1\u5446\u5446\u7ad9\u5728\u4ed6\u7684\u8eab\u524d\uff0c\u9ed8\u4e0d\u4f5c\u58f0\u3002 \u3000\u3000\u80e1\u6590\u5f2f\u4e0b\u8170\u53bb\uff0c\u4fef\u8eab\u5728\u5f90\u94ee\u8033\u8fb9\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5f90\u5927\u54e5\uff0c\u4f60\u6709\u4ec0\u4e48\u672a\u4e86\u4e4b\u4e8b\uff0c\u5144\u5f1f\u7ed9\u4f60\u529e\u53bb\u3002\u201d\u5f90\u94ee\u671b\u671b\u59bb\u5b50\uff0c\u671b\u671b\u5546\u5b9d\u9707\uff0c\u82e6\u7b11\u4e86\u4e00\u4e0b\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u6ca1\u6709\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u6211\u53bb\u627e\u5230\u4f60\u7684\u4e24\u4e2a\u5b69\u5b50\uff0c\u629a\u517b\u4ed6\u4eec\u6210\u4eba\u3002\u201d\u4ed6\u548c\u5f90\u94ee\u5168\u65e0\u4ea4\u60c5\uff0c\u53ea\u662f\u773c\u89c1\u4ed6\u843d\u5f97\u8fd9\u822c\u4e0b\u573a\uff0c\u6fc0\u4e8e\u4e49\u6124\uff0c\u5fcd\u4e0d\u4f4f\u8981\u633a\u8eab\u800c\u51fa\u3002 \u3000\u3000\u5f90\u94ee\u53c8\u82e6\u7b11\u4e86\u4e00\u4e0b\uff0c\u4f4e\u58f0\u8bf4\u4e86\u4e00\u53e5\u8bdd\uff0c\u53ea\u56e0\u6c14\u606f\u592a\u5fae\uff0c\u80e1\u6590\u542c\u4e0d\u660e\u767d\uff0c\u4e8e\u662f\u628a\u53f3\u8033\u51d1\u5230\u4ed6\u7684\u53e3\u8fb9\uff0c\u53ea\u542c\u4ed6\u4f4e\u58f0\u9053\uff1a\u201c\u5b69\u5b50\u2026\u2026\u5b69\u5b50\u2026\u2026\u5ac1\u8fc7\u6765\u4e4b\u524d\u2026\u2026\u65e9\u5c31\u6709\u4e86\u2026\u2026\u4e0d\u662f\u6211\u7684\u2026\u2026\u201d\u4e00\u53e3\u6c14\u547c\u51fa\uff0c\u4e0d\u518d\u5438\u8fdb\uff0c\u4fbf\u6b64\u6c14\u7edd\u3002 \u3000\u3000\u80e1\u6590\u604d\u7136\u5927\u609f\uff1a\u201c\u602a\u4e0d\u5f97\u9a6c\u59d1\u5a18\u8981\u548c\u4ed6\u6210\u4eb2\uff0c\u539f\u6765\u706b\u70e7\u5546\u5bb6\u5821\u540e\uff0c\u8fd9\u59d3\u5546\u7684\u4e0d\u77e5\u53bb\u5411\uff0c\u800c\u5979\u6709\u4e86\u8eab\u5b55\uff0c\u5374\u4e0d\u80fd\u4e0d\u5ac1\u3002\u602a\u4e0d\u5f97\u4e24\u4e2a\u5b69\u5b50\u7389\u96ea\u53ef\u7231\uff0c\u4e0e\u5f90\u5927\u54e5\u7684\u76f8\u8c8c\u534a\u5206\u4e5f\u4e0d\u50cf\u3002\u201d\u4ed6\u4f38\u8170\u7ad9\u8d77\uff0c\u65e0\u8bdd\u53ef\u8bf4\uff0c\u8033\u542c\u5f97\u9a6c\u8e44\u58f0\u54cd\uff0c\u53c8\u6709\u4e24\u4e58\u9a6c\u9a70\u8fd1\u3002\u6bcf\u5339\u9a6c\u4e0a\u5750\u7740\u4e00\u4e2a\u6c49\u5b50\uff0c\u6bcf\u4eba\u6000\u91cc\u5b89\u5b89\u7a33\u7a33\u7684\u5404\u62b1\u4e00\u4e2a\u9a6c\u6625\u82b1\u7684\u5b69\u5b50\u3002 \u3000\u3000\u9a6c\u6625\u82b1\u77a7\u77a7\u5f90\u94ee\uff0c\u53c8\u77a7\u77a7\u5546\u5b9d\u9707\uff0c\u8bf4\u9053\uff1a\u201c\u5546\u5c11\u7237\uff0c\u6211\u5f53\u5bb6\u7684\u662f\u4f60\u6253\u6b7b\u7684\uff1f\u201d\u5546\u5b9d\u9707\u9053\uff1a\u201c\u5200\u5b50\u8fd8\u5728\u4ed6\u624b\u91cc\uff0c\u6211\u53ef\u6ca1\u5360\u4ed6\u7684\u4fbf\u5b9c\u3002\u201d\u9a6c\u6625\u82b1\u70b9\u70b9\u5934\uff0c\u4ece\u5f90\u94ee\u53f3\u624b\u4e2d\u53d6\u4e0b\u5355\u5200\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u4f60\u5bb6\u4f20\u7684\u516b\u5366\u5200\uff0c\u6211\u5728\u5546\u5bb6\u5821\u4e2d\u89c1\u8fc7\u7684\u3002\u201d\u5546\u5b9d\u9707\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u4f60\u597d\u8bb0\u6027\uff0c\u591a\u4e8f\u4f60\u8fd8\u8bb0\u5f97\u3002\u201d\u9a6c\u6625\u82b1\u9053\uff1a\u201c\u6211\u600e\u4e48\u4e0d\u8bb0\u5f97\uff1f\u5546\u5bb6\u5821\u7684\u4e8b\uff0c\u597d\u50cf\u4fbf\u90fd\u5728\u773c\u524d\u4e00\u822c\u3002\u201d \u3000\u3000\u7a0b\u7075\u7d20\u4fa7\u76ee\u77a7\u7740\u80e1\u6590\uff0c\u53ea\u89c1\u4ed6\u6ee1\u8138\u901a\u7ea2\uff0c\u80f8\u53e3\u4e0d\u4f4f\u8d77\u4f0f\uff0c\u5f3a\u5fcd\u6012\u6c14\uff0c\u5374\u4e0d\u53d1\u4f5c\u3002 \u3000\u3000\u9a6c\u6625\u82b1\u63d0\u7740\u516b\u5366\u5200\uff0c\u8d5e\u9053\uff1a\u201c\u597d\u5200\uff01\u201d\u6162\u6162\u8d70\u5230\u5546\u5b9d\u9707\u8eab\u524d\u3002\u5546\u5b9d\u9707\u5634\u8fb9\u542b\u7b11\uff0c\u76ee\u5149\u4e2d\u8574\u7740\u60c5\u610f\uff0c\u4f38\u624b\u6765\u63a5\u3002\u9a6c\u6625\u82b1\u5012\u8fc7\u5200\u950b\uff0c\u4fbf\u4f3c\u8981\u5c06\u5200\u67c4\u9012\u7ed9\u4ed6\uff0c\u7a81\u7136\u95f4\u767d\u5149\u4e00\u95ea\uff0c\u5200\u5934\u731b\u5730\u8f6c\u8fc7\uff0c\u6ce2\u7684\u4e00\u58f0\u8f7b\u54cd\uff0c\u523a\u5165\u4e86\u5546\u5b9d\u9707\u8170\u95f4\u3002 \u3000\u3000\u5546\u5b9d\u9707\u4e00\u58f0\u5927\u53eb\uff0c\u4e00\u638c\u62cd\u51fa\uff0c\u5c06\u9a6c\u6625\u82b1\u51fb\u5f97\u5012\u9000\u6570\u6b65\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u2026\u2026\u4f60\u2026\u2026\u4e3a\u4ec0\u4e48\u2026\u2026\u201d\u4e00\u53e5\u8bdd\u6ca1\u8bf4\u5b8c\uff0c\u5411\u524d\u4e00\u6251\uff0c\u4fbf\u5df2\u6bd9\u547d\u3002 \u3000\u3000\u8fd9\u4e00\u4e0b\u4eba\u4eba\u51fa\u5176\u4e0d\u610f\uff0c\u672c\u6765\u5546\u5b9d\u9707\u51fb\u6b7b\u5f90\u94ee\uff0c\u9a6c\u6625\u82b1\u4e3a\u592b\u62a5\u4ec7\uff0c\u8c01\u90fd\u5e94\u8be5\u6599\u60f3\u5f97\u5230\uff0c\u4f46\u9a6c\u6625\u82b1\u5bf9\u5f90\u94ee\u4e4b\u6b7b\u6ca1\u663e\u793a\u534a\u5206\u4f24\u5fc3\uff0c\u548c\u5546\u5b9d\u9707\u4e00\u95ee\u4e00\u7b54\uff0c\u53c8\u4f3c\u662f\u6b22\u7136\u53d9\u65e7\uff0c\u7a81\u7136\u95f4\u5200\u5149\u4e00\u95ea\uff0c\u5df2\u662f\u767d\u5203\u523a\u654c\u3002 \u3000\u3000\u7fa4\u76d7\u4e00\u6115\u4e4b\u95f4\uff0c\u5c1a\u672a\u53eb\u51fa\u58f0\u6765\uff0c\u80e1\u6590\u5728\u7a0b\u7075\u7d20\u80cc\u540e\u8f7b\u8f7b\u4e00\u63a8\uff0c\u62c9\u7740\u9a6c\u6625\u82b1\u7684\u624b\u81c2\uff0c\u6025\u901f\u9000\u5165\u4e86\u77f3\u5c4b\u3002\u7fa4\u76d7\u4e00\u9635\u55a7\u54d7\uff0c\u5f85\u6b32\u62e6\u963b\uff0c\u5df2\u7136\u6162\u4e86\u4e00\u6b65\u3002\u9002\u624d\u4e4b\u4e8b\u5b9e\u5728\u592a\u8fc7\u7a81\u5140\uff0c\u7fa4\u76d7\u663e\u7136\u8981\u8ba1\u8bae\u4e00\u756a\uff0c\u5e76\u4e0d\u7acb\u65f6\u4fbf\u5411\u77f3\u5c4b\u8fdb\u653b\uff0c\u53cd\u800c\u9000\u4e86\u5f00\u53bb\u3002 \u3000\u3000\u80e1\u6590\u5411\u9a6c\u6625\u82b1\u53f9\u9053\uff1a\u201c\u5148\u524d\u6211\u9519\u602a\u4f60\u4e86\uff0c\u4f60\u539f\u4e0d\u662f\u8fd9\u6837\u7684\u4eba\u3002\u201d\u9a6c\u6625\u82b1\u4e0d\u7b54\uff0c\u72ec\u81ea\u5446\u5750\u5728\u5c4b\u89d2\u4e4b\u4e2d\u3002\u7a0b\u7075\u7d20\u5bf9\u5979\u81ea\u4e5f\u5168\u7136\u6539\u89c2\uff0c\u67d4\u58f0\u5b89\u6170\u5979\u51e0\u53e5\u3002\u9a6c\u6625\u82b1\u53cc\u76ee\u5411\u524d\u76f4\u89c6\uff0c\u55ef\u4e5f\u4e0d\u55ef\u4e00\u58f0\u3002 \u3000\u3000\u80e1\u6590\u5411\u7a0b\u7075\u7d20\u4f7f\u4e2a\u773c\u8272\uff0c\u4e24\u4eba\u53c8\u5e76\u80a9\u7ad9\u5728\u7a97\u524d\u3002\u80e1\u6590\u9053\uff1a\u201c\u9a6c\u59d1\u5a18\u4e3a\u592b\u62a5\u4ec7\uff0c\u6740\u4e86\u654c\u4eba\u4e2a\u63aa\u624b\u4e0d\u53ca\uff0c\u53ef\u662f\u8fd9\u4e48\u4e00\u6765\uff0c\u6211\u66f4\u52a0\u4e0d\u61c2\u4e86\u3002\u201d\u7a0b\u7075\u7d20\u4e5f\u662f\u5927\u60d1\u4e0d\u89e3\uff0c\u672c\u6765\u5546\u5b9d\u9707\u4e00\u5230\uff0c\u4e00\u5207\u90fd\u5df2\u771f\u76f8\u5927\u767d\uff0c\u4f46\u73b0\u4e0b\u8bb8\u591a\u4e8b\u60c5\u7acb\u65f6\u53c8\u53d8\u5f97\u5341\u5206\u53e4\u602a\u3002\u9a6c\u6625\u82b1\u7adf\u4f1a\u4eb2\u624b\u5c06\u5546\u5b9d\u9707\u6740\u6b7b\uff0c\u662f\u4e0d\u662f\u5979\u773c\u89c1\u4e08\u592b\u60e8\u6b7b\uff0c\u7a81\u7136\u5929\u826f\u53d1\u73b0\uff1f\u5982\u679c\u7fa4\u76d7\u786e\u662f\u5546\u5b9d\u9707\u9080\u6765\uff0c\u90a3\u4e48\u4ed6\u4e00\u6b7b\u4e4b\u540e\uff0c\u76d7\u4f17\u5b9a\u8981\u7fa4\u76f8\u6124\u6fc0\uff0c\u53eb\u56a3\u653b\u6765\uff0c\u4f46\u7fa4\u76d7\u9664\u4e86\u60ca\u5947\u4e4b\u5916\uff0c\u4f55\u4ee5\u5e76\u65e0\u5f02\u4e3e\uff1f \u3000\u3000\u80e1\u6590\u51dd\u795e\u601d\u7d22\u4e86\u4e00\u4f1a\uff0c\u8bf4\u9053\uff1a\u201c\u4e8c\u59b9\uff0c\u8fd9\u4e2d\u95f4\u6709\u5f88\u591a\u96be\u89e3\u4e4b\u5904\uff0c\u54b1\u4e24\u4eba\u8d38\u7136\u63d2\u624b\uff0c\u8bf4\u4e0d\u5b9a\u53cd\u800c\u5bb3\u4e86\u597d\u4eba\u3002\u9a6c\u59d1\u5a18\u662f\u4e00\u5b9a\u4e0d\u80af\u8bf4\u7684\u4e86\uff0c\u6211\u53bb\u95ee\u90a3\u76d7\u9b41\u53bb\u3002\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u4ed6\u600e\u80af\u8bf4\uff1f\u201d \u3000\u3000\u80e1\u6590\u9053\uff1a\u201c\u6211\u53bb\u8bd5\u8bd5\uff01\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u5343\u4e07\u5f97\u5c0f\u5fc3\u4e86\uff01\u201d\u80e1\u6590\u9053\uff1a\u201c\u7406\u4f1a\u5f97\u3002\u201d\u5f00\u4e86\u5c4b\u95e8\uff0c\u7f13\u6b65\u800c\u51fa\uff0c\u5411\u76d7\u4f17\u8d70\u53bb\u3002 \u3000\u3000\u7fa4\u76d7\u89c1\u4ed6\u5b64\u8eab\u51fa\u6765\uff0c\u624b\u4e2d\u4e0d\u643a\u5175\u5203\uff0c\u8138\u4e0a\u5747\u6709\u60ca\u5f02\u4e4b\u8272\u3002 \u3000\u3000\u80e1\u6590\u8d70\u5230\u79bb\u7fa4\u76d7\u516d\u4e03\u4e08\u8fdc\u5904\uff0c\u7ad9\u5b9a\u8bf4\u9053\uff1a\u201c\u5728\u4e0b\u6709\u4e00\u53e5\u673a\u5bc6\u4e4b\u8a00\uff0c\u8981\u548c\u8d35\u9996\u9886\u8bf4\u3002\u201d\u8bf4\u7740\u5728\u8eab\u4e0a\u62cd\u4e86\u62cd\uff0c\u793a\u610f\u4e0d\u5e26\u5229\u5668\u3002 \u3000\u3000\u7fa4\u76d7\u4e2d\u4e00\u6761\u7c97\u58ee\u6c49\u5b50\u559d\u9053\uff1a\u201c\u5927\u4f19\u513f\u90fd\u662f\u597d\u5144\u5f1f\uff0c\u6709\u8bdd\u5c3d\u8bf4\u4e0d\u59a8\uff0c\u4f55\u5fc5\u9b3c\u9b3c\u795f\u795f\uff1f\u201d\u80e1\u6590\u7b11\u9053\uff1a\u201c\u5404\u4f4d\u90fd\u662f\u82f1\u96c4\u597d\u6c49\uff0c\u9886\u5934\u7684\u81ea\u7136\u66f4\u662f\u4e00\u4f4d\u4e86\u4e0d\u8d77\u7684\u4eba\u7269\uff0c\u96be\u9053\u8ddf\u6211\u8bf4\u53e5\u8bdd\u90fd\u4e0d\u6562\u4e48\uff1f\u201d \u3000\u3000\u90a3\u7626\u524a\u8001\u4eba\u53f3\u624b\u6446\u4e86\u6446\uff0c\u8bf4\u9053\uff1a\u201c\u2018\u4e86\u4e0d\u8d77\u7684\u4eba\u7269\u2019\u8fd9\u516d\u4e2a\u5b57\uff0c\u90a3\u53ef\u4e0d\u6562\u5f53\u3002\u6211\u77a7\u4f60\u5c0f\u5144\u5f1f\u5012\u662f\u4f4d\u5c11\u5e74\u82f1\u96c4\uff0c\u540e\u751f\u53ef\u754f\uff0c\u540e\u751f\u53ef\u754f\uff01\u201d\u4ed6\u8bdd\u4e2d\u79f0\u8d5e\u80e1\u6590\uff0c\u4f46\u6ee1\u8138\u662f\u8001\u6c14\u6a2a\u79cb\u4e4b\u8272\u3002 \u3000\u3000\u80e1\u6590\u62f1\u624b\u9053\uff1a\u201c\u8001\u7237\u5b50\uff0c\u8bf7\u501f\u4e00\u6b65\u8bf4\u8bdd\u3002\u201d\u8bf4\u7740\u5411\u6797\u4e2d\u7a7a\u65f7\u4e4b\u5904\u8d70\u53bb\u3002 \u3000\u3000\u90a3\u7626\u8001\u4eba\u659c\u773c\u5fae\u7768\uff0c\u9002\u624d\u9a6c\u6625\u82b1\u624b\u5203\u5546\u5b9d\u9707\u4e4b\u4e8b\uff0c\u4e5f\u592a\u4ee4\u4eba\u9707\u60ca\uff0c\u4ed6\u5fc3\u795e\u5140\u81ea\u672a\u5b81\uff0c\u751f\u6015\u80e1\u6590\u4e5f\u6697\u85cf\u6bd2\u8ba1\uff0c\u4e0d\u6562\u4fbf\u6b64\u8ddf\u968f\u8fc7\u53bb\uff0c\u4f46\u82e5\u4e0d\u53bb\uff0c\u53c8\u672a\u514d\u8fc7\u4e8e\u793a\u5f31\uff0c\u5f53\u4e0b\u5168\u795e\u6212\u5907\uff0c\u4e00\u6b65\u6b65\u7684\u8d70\u8fd1\u3002 \u3000\u3000\u80e1\u6590\u62b1\u62f3\u9053\uff1a\u201c\u665a\u8f88\u59d3\u80e1\u540d\u6590\uff0c\u8001\u7237\u5b50\u4f60\u5c0a\u59d3\u5927\u540d\u3002\u201d\u90a3\u8001\u8005\u4e0d\u7b54\uff0c\u9053\uff1a\u201c\u5c0a\u9a7e\u6709\u4f55\u8bf4\u8bdd\uff1f\u201d\u80e1\u6590\u7b11\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\u3002\u6211\u8981\u8ddf\u8001\u7237\u5b50\u8ba8\u6559\u51e0\u8def\u62f3\u811a\u3002\u201d \u3000\u3000\u90a3\u8001\u8005\u6ca1\u60f3\u5230\u4ed6\u7adf\u4f1a\u8bf4\u51fa\u8fd9\u53e5\u8bdd\u6765\uff0c\u52c3\u7136\u53d8\u8272\uff0c\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u4f60\u9a97\u6211\u8fc7\u6765\uff0c\u4fbf\u8981\u8bf4\u8fd9\u4e00\u53e5\u8bdd\u5417\uff1f\u201d\u80e1\u6590\u7b11\u9053\uff1a\u201c\u8001\u7237\u5b50\u4e14\u52ff\u52a8\u6012\uff0c\u6211\u662f\u60f3\u8ddf\u4f60\u8d4c\u4e00\u4e2a\u73a9\u610f\u513f\u3002\u201d \u3000\u3000\u90a3\u8001\u8005\u54fc\u7684\u4e00\u58f0\uff0c\u8f6c\u8eab\u4fbf\u8d70\u3002\u80e1\u6590\u9053\uff1a\u201c\u6211\u65e9\u6599\u4f60\u4e0d\u6562\uff01 \u3000\u3000\u6211\u4fbf\u662f\u7ad9\u5728\u539f\u5730\u4e0d\u52a8\uff0c\u4f60\u4e5f\u6253\u6211\u4e0d\u8fc7\u3002\u201d\u90a3\u8001\u8005\u6012\u9053\uff1a\u201c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u201d\u80e1\u6590\u9053\uff1a\u201c\u6211\u53cc\u811a\u9489\u5728\u5730\u4e0b\uff0c\u534a\u5bf8\u4e0d\u5f97\u79fb\u52a8\uff0c\u4f60\u5374\u53ef\u4efb\u610f\u8d70\u52a8\uff0c\u54b1\u4eec\u8fd9\u822c\u6bd4\u6bd4\u62f3\u811a\uff0c\u4f60\u8bf4\u8c01\u8d62\u8c01\u8f93\uff1f\u201d \u3000\u3000\u90a3\u8001\u8005\u89c1\u4ed6\u8fed\u732e\u8eab\u624b\uff0c\u593a\u96f7\u9707\u6321\uff0c\u64d2\u6c6a\u94c1\u9e57\uff0c\u62a2\u5251\u8fd8\u5251\uff0c\u63a5\u53d1\u6697\u5668\uff0c\u4e8b\u4e8b\u7729\u4eba\u8033\u76ee\uff0c\u82e5\u8bf4\u5355\u6253\u72ec\u6597\uff0c\u8fd8\u5f53\u771f\u6709\u70b9\u80c6\u602f\uff0c\u4f46\u542c\u4ed6\u7adf\u6562\u5927\u8a00\u4e0d\u60ed\uff0c\u8bf4\u53cc\u8db3\u4e0d\u52a8\u800c\u548c\u81ea\u5df1\u76f8\u6597\uff0c\u8fd9\u6837\u7684\u4e8b\u6c5f\u6e56\u4e0a\u53ef\u4ece\u672a\u542c\u89c1\u8fc7\u3002\u4ed6\u662f\u6cb3\u5357\u5f00\u5c01\u5e9c\u516b\u6781\u62f3\u7684\u638c\u95e8\u4eba\uff0c\u4eba\u65e2\u7a33\u7ec3\uff0c\u6b66\u529f\u53c8\u9ad8\uff0c\u56e0\u6b64\u8fd9\u6b21\u540c\u6765\u7684\u4e09\u5341\u4f59\u4eba\u4e4b\u4e2d\u4ee5\u4ed6\u4e3a\u9996\uff0c\u5fc3\u60f3\u5bf9\u65b9\u7b54\u5141\u53cc\u8db3\u4e0d\u52a8\uff0c\u81ea\u5df1\u5df2\u7acb\u4e8e\u4e0d\u8d25\u4e4b\u5730\uff0c\u8fd9\u4efd\u4fbf\u5b9c\u662f\u7a33\u7a33\u5360\u4e86\uff0c\u5f53\u4e0b\u5e76\u4e0d\u607c\u6012\uff0c\u53cd\u800c\u9ad8\u5174\uff0c\u7b11\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\u51fa\u4e86\u8fd9\u4e2a\u65b0\u82b1\u6837\u6765\u8003\u8f83\u8001\u5934\u5b50\uff0c\u597d\uff0c\u8fd9\u51e0\u6839\u8001\u9aa8\u5934\u4fbf\u8ddf\u7740\u4f60\u71ac\u71ac\u3002\u54b1\u4eec\u8bb8\u4e0d\u8bb8\u7528\u6697\u5668\u54ea\uff1f\u201d\u80e1\u6590\u5fae\u7b11\u9053\uff1a\u201c\u4ee5\u6b66\u4f1a\u53cb\uff0c\u7528\u4ec0\u4e48\u6697\u5668\uff1f\u201d \u3000\u3000\u90a3\u8001\u8005\u5fc3\u60f3\uff1a\u201c\u6211\u4fbf\u6253\u4ed6\u4e0d\u8fc7\uff0c\u53ea\u987b\u9000\u5f00\u4e09\u6b65\uff0c\u4ed6\u811a\u6b65\u4e0d\u80fd\u79fb\u52a8\uff0c\u8c05\u4ed6\u624b\u81c2\u80fd\u6709\u591a\u957f\uff1f\u6700\u4e0d\u6d4e\u4e5f\u662f\u4e2a\u5e73\u624b\u3002\u201d\u8bf4\u4e86\u58f0\uff1a\u201c\u597d\uff01\u201d \u3000\u3000\u80e1\u6590\u9053\uff1a\u201c\u665a\u8f88\u4e0e\u8001\u7237\u5b50\u7d20\u4e0d\u76f8\u8bc6\uff0c\u8fd9\u6b21\u591a\u7ba1\u95f2\u4e8b\uff0c\u5b9e\u662f\u80e1\u95f9\u3002\u665a\u8f88\u53ea\u8981\u8f93\u4e86\u4e00\u62db\u534a\u5f0f\uff0c\u6211\u548c\u4e49\u59b9\u4e24\u4eba\u7acb\u523b\u4fbf\u8d70\u3002\u201d\u90a3\u8001\u8005\u5fc3\u60f3\uff1a\u201c\u4ed6\u82e5\u4e00\u5473\u62a4\u7740\u9a6c\u59d1\u5a18\uff0c\u6b64\u4e8b\u7ec8\u662f\u4e0d\u4e86\u3002\u6211\u4eec\u5018\u82e5\u6043\u4f17\u5f3a\u653b\uff0c\u52bf\u5fc5\u591a\u4f24\u4eba\u547d\uff0c\u5982\u4f24\u7740\u9a6c\u59d1\u5a18\uff0c\u66f4\u662f\u5927\u5927\u4e0d\u59a5\uff0c\u8fd8\u662f\u5584\u7f62\u4e3a\u5999\u3002\u201d\u4e8e\u662f\u8bf4\u9053\uff1a\u201c\u662f\u554a\uff01\u8fd9\u4e8b\u539f\u672c\u8ddf\u65c1\u4eba\u7edd\u4e0d\u76f8\u5e72\u3002 \u3000\u3000\u9a6c\u59d1\u5a18\u6b64\u540e\u5bcc\u8d35\u8363\u534e\uff0c\u76f4\u4e0a\u9752\u4e91\uff0c\u4f60\u65e2\u8ddf\u5979\u6709\u4ea4\u60c5\uff0c\u53ea\u6709\u4ee3\u5979\u559c\u6b22\u3002\u201d \u3000\u3000\u80e1\u6590\u6414\u4e86\u6414\u540e\u8111\uff0c\u9053\uff1a\u201c\u6211\u4fbf\u662f\u4e0d\u660e\u767d\u3002\u8001\u7237\u5b50\u5018\u82e5\u4efb\u8ba9\u4e00\u62db\uff0c\u665a\u8f88\u8981\u8bf7\u8001\u7237\u5b50\u8bf4\u660e\u5176\u4e2d\u7684\u539f\u59d4\u3002\u201d \u3000\u3000\u90a3\u8001\u8005\u5fae\u4e00\u6c89\u541f\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u4fbf\u662f\u8fd9\u6837\u3002\u201d\u89c1\u80e1\u6590\u53cc\u8db3\u4e00\u7ad9\uff0c\u76f8\u8ddd\u4e00\u5c3a\u516b\u5bf8\uff0c\u5dbd\u5cd9\u6e0a\u6e1f\uff0c\u6c89\u7a33\u65e0\u6bd4\uff0c\u4e0d\u7981\u5fc3\u4e2d\u4e00\u52a8\uff1a \u3000\u3000\u201c\u8bf4\u4e0d\u5b9a\u8fd8\u771f\u8f93\u4e0e\u4ed6\u4e86\u3002\u201d\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u8bdd\u8bf4\u660e\u5728\u5148\uff0c\u6211\u82e5\u8f93\u4e86\uff0c\u53ea\u597d\u5bf9\u4f60\u8bf4\uff0c\u4f46\u4f60\u51b3\u4e0d\u80fd\u8ddf\u7b2c\u4e8c\u4eba\u8bf4\u8d77\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u6211\u4e49\u59b9\u53ef\u987b\u8ddf\u5979\u660e\u8a00\u3002\u201d\u90a3\u8001\u8005\u5fc3\u60f3\uff1a\u201c\u5e72\u67f4\u70c8\u706b\u597d\u716e\u996d\uff0c\u5e72\u5144\u5e72\u59b9\u597d\u505a\u4eb2\u3002\u4f60\u4eec\u5e72\u5144\u5e72\u59b9\uff0c\u4f55\u7b49\u4eb2\u5bc6\uff1f\u5c31\u7b97\u53e3\u4e2d\u7b54\u5e94\u4e86\u4e0d\u8bf4\uff0c\u4e5f\u5c82\u6709\u4e0d\u8bf4\u4e4b\u7406\uff1f\u201d\u4fbf\u9053\uff1a\u201c\u7b2c\u4e09\u4eba\u53ef\u51b3\u8ba1\u4e0d\u80fd\u8bf4\u4e86\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u597d\uff01\u4fbf\u662f\u8fd9\u6837\u3002\u6211\u53c8\u600e\u77e5\u51c6\u80fd\u8d62\u5f97\u4f60\u8001\u4eba\u5bb6\uff1f\u201d \u3000\u3000\u90a3\u8001\u8005\u8eab\u5f62\u4e00\u8d77\uff0c\u5fae\u7b11\u9053\uff1a\u201c\u6709\u50ed\u4e86\uff01\u201d\u5de6\u624b\u6325\u638c\u5288\u51fa\uff0c\u53f3\u62f3\u6210\u94a9\uff0c\u6b63\u662f\u516b\u6781\u62f3\u4e2d\u7684\u201c\u63a8\u5c71\u5f0f\u201d\u3002\u80e1\u6590\u987a\u624b\u4e00\u5e26\uff0c\u89c9\u4ed6\u8fd9\u4e00\u638c\u529b\u9053\u751a\u539a\uff0c\u8bf4\u9053\uff1a\u201c\u8001\u7237\u5b50\u597d\u638c\u529b\uff01\u201d \u3000\u3000\u7fa4\u76d7\u89c1\u4e24\u4eba\u62c9\u5f00\u67b6\u5b50\u52a8\u624b\uff0c\u7eb7\u7eb7\u8d76\u4e86\u8fc7\u6765\uff0c\u4f46\u89c1\u4e24\u4eba\u8138\u4e0a\u5404\u5e26\u5fae\u7b11\uff0c\u5f53\u4e0b\u7ad9\u5b9a\u4e86\u89c2\u6597\u3002\u90a3\u516b\u6781\u62f3\u7684\u516b\u6781\u4e43\u662f\u201c\u7ffb\u624b\u3001\u63f2\u8155\u3001\u5bf8\u6073\u3001\u6296\u5c55\u201d\uff0c\u5171\u5206\u201c\u6402\u3001\u6253\u3001\u817e\u3001\u5c01\u3001\u8e22\u3001\u8e6c\u3001\u626b\u3001\u6302\u201d\u516b\u5f0f\uff0c\u8bb2\u7a76\u7684\u662f\u72e0\u6377\u654f\u6d3b\u3002\u90a3\u8001\u8005\u65bd\u5c55\u5f00\u6765\uff0c\u4f46\u89c1\u4ed6\u7ffb\u624b\u4e4b\u7075\u3001\u63f2\u8155\u4e4b\u5de7\u3001\u5bf8\u6073\u4e4b\u7cbe\u3001\u6296\u5c55\u4e4b\u901f\uff0c\u7684\u662f\u540d\u5bb6\u9ad8\u624b\u7684\u98ce\u8303\u3002\u7fa4\u76d7\u770b\u5f97\u6697\u6697\u4f69\u670d\uff0c\u5fc3\u60f3\u4ed6\u4ee5\u516b\u6781\u62f3\u626c\u5a01\u5927\u6cb3\u5357\u5317\uff0c\u6210\u540d\u4e09\u5341\u4f59\u8f7d\uff0c\u679c\u6709\u771f\u624d\u5b9e\u5b66\uff0c\u7edd\u975e\u6d6a\u5f97\u865a\u58f0\u3002 \u3000\u3000\u53ea\u89c1\u90a3\u8001\u8005\u4e00\u6b65\u4e09\u73af\u3001\u4e09\u6b65\u4e5d\u8f6c\u3001\u5341\u4e8c\u8fde\u73af\u3001\u5927\u5f0f\u53d8\u5c0f\u5f0f\uff0c\u5c0f\u5f0f\u53d8\u4e2d\u76d8\uff0c\u201c\u9a91\u9a6c\u5f0f\u201d\u3001\u201c\u9c7c\u9cde\u5f0f\u201d\u3001\u201c\u5f13\u6b65\u5f0f\u201d\u3001\u201c\u78e8\u819d\u5f0f\u201d\uff0c\u5728\u80e1\u6590\u8eab\u65c1\u817e\u632a\u8df3\u8dc3\uff0c\u62f3\u811a\u8d8a\u6765\u8d8a\u5feb\u3002 \u3000\u3000\u80e1\u6590\u5374\u53ea\u662f\u4e00\u5473\u7a33\u5b88\uff0c\u89c1\u5f0f\u5316\u5f0f\uff0c\u679c\u7136\u53cc\u8db3\u6ca1\u79fb\u52a8\u5206\u6beb\u3002 \u3000\u3000\u6597\u5230\u5206\u9645\uff0c\u90a3\u8001\u8005\u53ea\u611f\u62f3\u638c\u51fa\u53bb\u4e4b\u65f6\u6e10\u8d8b\u6ede\u6da9\uff0c\u4f3c\u6709\u4e00\u80a1\u7c98\u529b\u963b\u5728\u4ed6\u62f3\u638c\u4e4b\u95f4\uff0c\u5fc3\u4e2d\u6697\u53eb\uff1a\u201c\u4e0d\u597d\uff01\u201d\u5f85\u8981\u540e\u8dc3\u9000\u5f00\uff0c\u5bf9\u65b9\u4e0d\u80fd\u8ffd\u51fb\uff0c\u4fbf\u7b97\u662f\u6ca1\u6709\u8f93\u8d62\uff0c\u90a3\u77e5\u4ed6\u5de6\u638c\u56de\u62bd\uff0c\u80e1\u6590\u53f3\u624b\u5df2\u6293\u4f4f\u4ed6\u7684\u53f3\u638c\uff0c\u540c\u65f6\u5de6\u624b\u6210\u62f3\uff0c\u5728\u4ed6\u53f3\u8098\u5e95\u4e00\u4e0b\u8f7b\u63c9\u3002 \u3000\u3000\u90a3\u8001\u8005\u5927\u60ca\uff0c\u8fd0\u52b2\u4e00\u6323\u6ca1\u80fd\u6323\u8131\uff0c\u4fbf\u77e5\u81ea\u5df1\u53f3\u81c2\u975e\u65ad\u4e0d\u53ef\uff0c\u5fc3\u4e2d\u6b63\u81ea\u51b0\u51c9\uff0c\u80e1\u6590\u7a81\u7136\u677e\u624b\u8dc3\u5f00\uff0c\u811a\u6b65\u4e00\u4e2a\u8e09\u8dc4\uff0c\u8bf4\u9053\uff1a\u201c\u8001\u7237\u5b50\u638c\u529b\u6c89\u96c4\uff0c\u4f69\u670d\uff0c\u4f69\u670d\u3002\u201d \u3000\u3000\u90a3\u8001\u8005\u5fc3\u4e2d\u96ea\u4eae\uff0c\u597d\u751f\u611f\u6fc0\uff0c\u5bf9\u65b9\u975e\u4f46\u9976\u4ed6\u4e00\u81c2\u4e0d\u65ad\uff0c\u8fd8\u6545\u610f\u811a\u6b65\u8e09\u8dc4\uff0c\u88c5\u5f97\u6253\u6210\u5e73\u624b\uff0c\u4f7f\u81ea\u5df1\u4e0d\u81f4\u5728\u4f17\u5144\u5f1f\u524d\u5931\u4e86\u9762\u5b50\uff0c\u4fdd\u5168\u81ea\u5df1\u4e00\u751f\u4ee4\u540d\uff0c\u5b9e\u662f\u6069\u5fb7\u975e\u6d45\uff0c\u4e8e\u662f\u8fc7\u53bb\u643a\u4e86\u80e1\u6590\u4e4b\u624b\uff0c\u7b11\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\u82f1\u96c4\u4e86\u5f97\uff0c\u54b1\u4eec\u5230\u8fd9\u8fb9\u8bf4\u8bdd\u3002\u201d \u3000"}),showOnHomepage:!0},{name:"\u7b2c\u5341\u4e09\u7ae0\u3000\u5317\u4eac\u4f17\u6b66\u5b98",content:(0,a.jsx)(a.Fragment,{children:"\u4e24\u4eba\u8d70\u5230\u6811\u6797\u6df1\u5904\uff0c\u80e1\u6590\u773c\u89c1\u56db\u4e0b\u65e0\u4eba\uff0c\u53ea\u9053\u4ed6\u8981\u8bf4\u4e86\uff0c\u90a3\u77e5\u90a3\u8001\u8005\u4e00\u8dc3\u4e0a\u6811\uff0c\u5411\u4ed6\u62db\u624b\u3002\u80e1\u6590\u8ddf\u7740\u4e0a\u53bb\uff0c\u5750\u5728\u679d\u5e72\u4e4b\u4e0a\u3002\u90a3\u8001\u8005\u9053\uff1a\u201c\u5728\u8fd9\u91cc\u8bf4\u6e05\u9759\u4e9b\u3002\u201d\u80e1\u6590\u5e94\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u90a3\u8001\u8005\u8138\u9732\u5fae\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5148\u524d\u542c\u5f97\u9601\u4e0b\u81ea\u62a5\u5c0a\u59d3\u5927\u540d\uff0c\u59d3\u80e1\u540d\u6590\u3002\u4e0d\u77e5\u8fd9\u4e2a\u6590\u5b57\uff0c\u662f\u6590\u7136\u6210\u7ae0\u4e4b\u2018\u6590\u2019\u5462\uff0c\u662f\u4e00\u98de\u51b2\u5929\u4e4b\u2018\u98de\u2019\u5462\uff0c\u8fd8\u662f\u662f\u975e\u5206\u660e\u4e4b\u2018\u975e\u2019\uff1f\u201d\u80e1\u6590\u542c\u4ed6\u5410\u5c5e\u65af\u6587\uff0c\u9053\uff1a\u201c\u8349\u5b57\u4e4b\u6590\uff0c\u662f\u4e00\u4e2a\u2018\u6587\u2019\u5b57\u4e0a\u9762\u52a0\u4e00\u4e2a\u2018\u975e\u2019\u5b57\u3002\u201d \u3000\u3000\u90a3\u8001\u8005\u9053\uff1a\u201c\u5728\u4e0b\u59d3\u79e6\uff0c\u8349\u5b57\u8010\u4e4b\uff0c\u4e00\u751f\u5bc4\u8ff9\u6c5f\u6e56\uff0c\u5927\u82f1\u96c4\u5927\u8c6a\u6770\u4f1a\u8fc7\u4e0d\u5c11\uff0c\u4f46\u5982\u9601\u4e0b\u8fd9\u822c\u5e74\u7eaa\uff0c\u6b66\u529f\u9020\u8be3\u7adf\u5df2\u5230\u4e86\u8fd9\u7b49\u5730\u6b65\uff0c\u5b9e\u662f\u751f\u5e73\u672a\u89c1\u3002\u201d\u4ed6\u987f\u4e86\u4e00\u987f\uff0c\u53c8\u9053\uff1a\u201c\u9601\u4e0b\u5b85\u5fc3\u5fe0\u539a\uff0c\u8bc6\u89c1\u4e0d\u51e1\uff0c\u66f4\u662f\u6b66\u6797\u4e2d\u6781\u4e3a\u5e0c\u6709\u3002\u5c0f\u5144\u5f1f\uff0c\u8001\u6c49\u7b97\u662f\u670d\u4e86\u4f60\u5566\uff01\u201d \u3000\u3000\u80e1\u6590\u9053\uff1a\u201c\u79e6\u7237\uff0c\u665a\u8f88\u6709\u4e00\u4e8b\u8bf7\u6559\u3002\u201d\u79e6\u8010\u4e4b\u9053\uff1a\u201c\u4f60\u4e0d\u7528\u592a\u8c26\u5566\uff0c\u8fd9\u4e48\u7740\uff0c\u6211\u53e8\u957f\u4f60\u51e0\u5c81\uff0c\u79f0\u4f60\u4e00\u58f0\u5144\u5f1f\uff0c\u4f60\u4fbf\u53eb\u6211\u4e00\u58f0\u79e6\u5927\u54e5\u3002\u4f60\u65e2\u624b\u4e0b\u5bb9\u60c5\uff0c\u987e\u5168\u4e86\u6211\u8fd9\u8001\u9762\u5b50\uff0c\u90a3\u4f60\u95ee\u4ec0\u4e48\uff0c\u6211\u7b54\u4ec0\u4e48\u4fbf\u662f\u3002\u201d \u3000\u3000\u80e1\u6590\u5fd9\u9053\uff1a\u201c\u4e0d\u6562\u4e0d\u6562\uff0c\u5144\u5f1f\u89c1\u79e6\u5927\u54e5\u6709\u4e00\u62db\u662f\u8eab\u5b50\u5411\u540e\u5fae\u4ef0\uff0c\u4e0a\u76d8\u6545\u793a\u4e0d\u7a33\uff0c\u5de6\u81c2\u7f6e\u4e8e\u53f3\u81c2\u4e0a\u4ea4\u53c9\u8f6e\u6253\uff0c\u7ffb\u6210\u9633\u638c\uff0c\u7136\u540e\u4e24\u624b\u6210\u9634\u62f3\u6253\u51fa\u3002\u8fd9\u4e00\u62db\u53d8\u5316\u6781\u662f\u7cbe\u5999\uff0c\u505a\u5144\u5f1f\u7684\u9669\u4e9b\u4fbf\u62db\u67b6\u4e0d\u4f4f\uff0c\u5fc3\u4e2d\u751a\u662f\u4ef0\u6155\u3002\u201d \u3000\u3000\u79e6\u8010\u4e4b\u5fc3\u4e2d\u4e00\u559c\uff0c\u4ed6\u62f3\u811a\u4e0a\u8f93\u4e86\uff0c\u4f9d\u7ea6\u4fbf\u5f97\u5c06\u6b64\u884c\u771f\u60c5\u548c\u76d8\u6258\u51fa\uff0c\u53ea\u9053\u80e1\u6590\u4fbf\u8981\u8bd8\u95ee\u6b64\u4e8b\uff0c\u90a3\u77e5\u4ed6\u7adf\u662f\u8bf7\u6559\u81ea\u5df1\u7684\u5f97\u610f\u6b66\u529f\uff0c\u5bf9\u65b9\u6240\u95ee\uff0c\u6b63\u662f\u4ed6\u8d56\u4ee5\u6210\u540d\u7684\u516b\u6781\u62f3\u4e2d\u516b\u5927\u7edd\u62db\u4e4b\u4e00\uff0c\u4e8e\u662f\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u662f\u655d\u6d3e\u6b66\u529f\u4e2d\u6bd4\u8f83\u6709\u7528\u7684\u4e00\u62db\uff0c\u53eb\u4f5c\u2018\u53cc\u6253\u5947\u95e8\u2019\u3002\u201d\u4e8e\u662f\u8ddf\u7740\u89e3\u91ca\u8fd9\u4e00\u62db\u4e2d\u7684\u7cbe\u5fae\u5965\u5999\u3002 \u3000\u3000\u80e1\u6590\u672c\u6027\u597d\u6b66\uff0c\u542c\u5f97\u6d25\u6d25\u6709\u5473\uff0c\u63a5\u7740\u53c8\u8bf7\u6559\u4e86\u51e0\u4e2a\u4e0d\u660e\u7684\u7591\u70b9\u3002 \u3000\u3000\u6b66\u6797\u4e2d\u4e0d\u8bba\u90a3\u4e00\u95e8\u90a3\u4e00\u6d3e\uff0c\u65e2\u80fd\u6388\u5f92\u4f20\u6280\uff0c\u5353\u7136\u6210\u5bb6\uff0c\u603b\u6709\u5176\u72ec\u5230\u6210\u5c31\uff0c\u90a3\u516b\u6781\u62f3\u5f53\u6709\u6e05\u96cd\u4e7e\u5e74\u95f4\uff0c\u6b66\u6797\u4e2d\u540d\u5934\u751a\u54cd\uff0c\u58f0\u52bf\u4e5f\u53ea\u7a0d\u900a\u4e8e\u592a\u6781\u3001\u516b\u5366\u8bf8\u95e8\u3002\u80e1\u6590\u548c\u79e6\u8010\u4e4b\u8fc7\u62db\u4e4b\u65f6\uff0c\u7559\u5fc3\u4ed6\u7684\u62f3\u62db\u638c\u6cd5\uff0c\u8fd9\u65f6\u6240\u95ee\u7684\u5168\u662f\u516b\u6781\u62f3\u4e2d\u7684\u9ad8\u5999\u4e4b\u4f5c\u3002\u79e6\u8010\u4e4b\u8d77\u521d\u8fd8\u6050\u672c\u95e8\u79d8\u5965\u6cc4\u9732\u4e8e\u4eba\uff0c\u89e3\u91ca\u65f6\u5341\u5206\u4e2d\u53ea\u8bf4\u4e03\u5206\uff0c\u7136\u542c\u5bf9\u65b9\u6240\u95ee\uff0c\u6bcf\u4e00\u53e5\u90fd\u6414\u7740\u75d2\u5904\uff0c\u795e\u6001\u53c8\u6781\u606d\u8c28\uff0c\u6559\u4ed6\u5fcd\u4e0d\u4f4f\u8981\u503e\u56ca\u5410\u9732\uff0c\u53c8\u60f3\uff0c\u53cd\u6b63\u4ed6\u6b66\u529f\u5f3a\u80dc\u4e8e\u6211\uff0c\u5b66\u4e86\u6211\u7684\u62f3\u6cd5\uff0c\u4e5f\u4ecd\u4e0d\u8fc7\u662f\u5f3a\u80dc\u4e8e\u6211\uff0c\u53c8\u6709\u4ec0\u4e48\u5927\u4e0d\u4e86\uff1f\u800c\u80e1\u6590\u6709\u65f6\u7a0d\u6292\u5df1\u89c1\uff0c\u53c8\u5bf9\u516b\u6781\u62f3\u7684\u957f\u5904\u66f4\u6709\u9526\u4e0a\u6dfb\u82b1\u4e4b\u5999\u3002 \u3000\u3000\u4e24\u4eba\u8fd9\u4e48\u4e00\u8c08\u8bba\uff0c\u7adf\u8bf4\u4e86\u8db3\u8db3\u534a\u4e2a\u65f6\u8fb0\uff0c\u7fa4\u76d7\u8fdc\u8fdc\u671b\u7740\uff0c\u4f46\u89c1\u79e6\u8010\u4e4b\u53cc\u624b\u6bd4\u5212\uff0c\u4f7f\u7740\u4ed6\u5f97\u610f\u7684\u62f3\u62db\uff0c\u80e1\u6590\u6709\u65f6\u4e5f\u51fa\u624b\u8fdb\u62db\uff0c\u4e24\u4eba\u6709\u8bf4\u6709\u7b11\uff0c\u751a\u662f\u4eb2\u70ed\uff0c\u663e\u662f\u5728\u94bb\u7814\u62f3\u672f\u6b66\u529f\u3002\u4f17\u4eba\u77a7\u4e86\u534a\u5929\uff0c\u542c\u4e0d\u89c1\u4e24\u4eba\u7684\u8bf4\u8bdd\uff0c\u867d\u89c9\u8be7\u5f02\uff0c\u5374\u4e5f\u5c31\u4e0d\u518d\u77a7\u4e86\u3002 \u3000\u3000\u53c8\u8bf4\u4e86\u4e00\u9635\uff0c\u79e6\u8010\u4e4b\u9053\uff1a\u201c\u80e1\u5144\u5f1f\uff0c\u516b\u6781\u62f3\u7684\u62f3\u62db\u662f\u5f88\u4e86\u4e0d\u8d77\u7684\uff0c\u53ea\u53ef\u60dc\u6211\u6ca1\u5b66\u5f97\u5230\u5bb6\uff0c\u6298\u5728\u4f60\u7684\u624b\u4e0b\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u79e6\u5927\u54e5\u8bf4\u54ea\u91cc\u8bdd\u6765\uff1f\u54b1\u4eec\u5f53\u771f\u518d\u6597\u4e0b\u53bb\uff0c\u4e5f\u4e0d\u77e5\u8c01\u80dc\u8c01\u8d25\u3002 \u3000\u3000\u5144\u5f1f\u5bf9\u8d35\u6d3e\u6b66\u529f\u4f69\u670d\u5f97\u7d27\u3002\u4eca\u65e5\u5929\u8272\u5df2\u665a\uff0c\u4e00\u65f6\u4e4b\u95f4\u4e5f\u8bf7\u6559\u4e0d\u4e86\u8bb8\u591a\uff0c\u65e5\u540e\u5144\u5f1f\u5230\u5317\u4eac\u6765\uff0c\u5b9a\u5f53\u4e13\u8bda\u62dc\u8bbf\uff0c\u957f\u8c08\u51e0\u65e5\u3002\u6b64\u523b\u6682\u4e14\u522b\u8fc7\u3002\u201d\u8bf4\u7740\u53cc\u624b\u4e00\u62f1\uff0c\u4fbf\u8981\u4e0b\u6811\u3002 \u3000\u3000\u79e6\u8010\u4e4b\u4e00\u6014\uff0c\u5fc3\u9053\uff1a\u201c\u54b1\u4eec\u6709\u7ea6\u5728\u5148\uff0c\u6211\u987b\u5f97\u8bf4\u660e\u6b64\u884c\u7684\u539f\u59d4\uff0c\u4f46\u4ed6\u53ea\u548c\u6211\u8bb2\u8bba\u4e00\u756a\u6b66\u529f\uff0c\u5373\u4fbf\u544a\u8f9e\uff0c\u5929\u4e0b\u5b81\u6709\u662f\u7406\uff1f \u3000\u3000\u662f\u4e86\uff0c\u8fd9\u5c11\u5e74\u662f\u7ed9\u6211\u9762\u5b50\uff0c\u4ed6\u65e2\u8bb2\u4ea4\u60c5\uff0c\u6211\u5c82\u53ef\u8bf4\u8fc7\u7684\u8bdd\u4e0d\u7b97\uff1f\u201d\u5f53\u5373\u8bf4\u9053\uff1a\u201c\u5144\u5f1f\u4e14\u6162\u3002\u54b1\u54e5\u513f\u4fe9\u4e0d\u6253\u4e0d\u6210\u76f8\u8bc6\uff0c\u8fd9\u4f1a\u5b50\u7684\u4e8b\uff0c\u4e58\u8fd9\u65f6\u8bf4\u4e2a\u660e\u767d\uff0c\u4e5f\u597d\u6709\u4e2a\u4e86\u65ad\u554a\u3002\u201d \u3000\u3000\u80e1\u6590\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5144\u5f1f\u548c\u90a3\u5546\u5b9d\u9707\u5546\u5927\u54e5\u539f\u4e5f\u76f8\u8bc6\u7684\uff0c\u60f3\u4e0d\u5230\u9a6c\u59d1\u5a18\u7adf\u4f1a\u7a81\u7136\u51fa\u624b\uff0c\u7ed9\u4e08\u592b\u62a5\u4ec7\u3002\u201d\u4e8e\u662f\u628a\u5728\u5546\u5bb6\u5821\u4e2d\u5982\u4f55\u7ed3\u8bc6\u9a6c\u6625\u82b1\u548c\u5546\u5b9d\u9707\u4e4b\u4e8b\uff0c\u8be6\u8be6\u7ec6\u7ec6\u7684\u8bf4\u4e86\u4e00\u904d\u3002 \u3000\u3000\u79e6\u8010\u4e4b\u5fc3\u9053\uff1a\u201c\u597d\u554a\uff0c\u6211\u8fd8\u6ca1\u8bf4\uff0c\u4f60\u5012\u5148\u8bf4\u4e86\u3002\u8fd9\u5c11\u5e74\u884c\u4e8b\uff0c\u5904\u5904\u6559\u4eba\u5fc3\u670d\u3002\u201d\u8bf4\u9053\uff1a\u201c\u53e4\u4eba\u4e00\u996d\u4e4b\u6069\uff0c\u5343\u91d1\u4ee5\u62a5\u3002\u9a6c\u59d1\u5a18\u4e8e\u80e1\u5144\u5f1f\u6709\u4ee3\u4e3a\u6c42\u60c5\u4e4b\u5fb7\uff0c\u4f60\u4e0d\u5fd8\u65e7\u6069\uff0c\u6b63\u662f\u5927\u4e08\u592b\u672c\u8272\u3002\u4f60\u4e0d\u660e\u9a6c\u59d1\u5a18\u4f55\u4ee5\u6beb\u4e0d\u7559\u60c5\u7684\u6740\u4e86\u5546\u5b9d\u9707\uff0c\u96be\u9053\u90a3\u4e24\u4e2a\u5b69\u5b50\uff0c\u662f\u5546\u5b9d\u9707\u751f\u7684\u4e48\uff1f\u201d\u80e1\u6590\u6414\u5934\u9053\uff1a\u201c\u6211\u542c\u5f90\u94ee\u4e34\u6b7b\u4e4b\u65f6\uff0c\u8bf4\u8fd9\u4e24\u4e2a\u5b69\u513f\u4e0d\u662f\u4ed6\u7684\u4eb2\u751f\u513f\u5b50\u3002\u201d\u79e6\u8010\u4e4b\u4e00\u62cd\u819d\u5934\uff0c\u9053\uff1a\u201c\u539f\u6765\u4ed6\u5012\u4e5f\u4e0d\u662f\u50bb\u5b50\u3002\u201d \u3000\u3000\u80e1\u6590\u4e00\u65f6\u4fbf\u5982\u5815\u5165\u4e94\u91cc\u96fe\u4e2d\u3002\u79e6\u8010\u4e4b\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4f60\u5728\u5546\u5bb6\u5821\u4e4b\u65f6\uff0c\u53ef\u66fe\u89c1\u5230\u6709\u4e00\u4f4d\u8d35\u516c\u5b50\u4e48\uff1f\u201d \u3000\u3000\u80e1\u6590\u4e00\u542c\uff0c\u767b\u65f6\u5982\u68a6\u521d\u9192\u3002\u53ea\u56e0\u90a3\u65e5\u665a\u95f4\uff0c\u4ed6\u4eb2\u773c\u89c1\u5230\u5546\u5b9d\u9707\u548c\u9a6c\u6625\u82b1\u5728\u6811\u4e0b\u624b\u62c9\u624b\u7684\u8bf4\u8bdd\uff0c\u4e00\u5fc3\u4ee5\u4e3a\u4e24\u4eba\u4e92\u6709\u60c5\u610f\uff0c\u800c\u9a6c\u6625\u82b1\u548c\u90a3\u8d35\u516c\u5b50\u4e00\u89c1\u953a\u60c5\u3001\u4e92\u7f20\u75f4\u604b\u8fd9\u4e00\u573a\u5b7d\u7f18\uff0c\u4ed6\u5374\u5168\u7136\u4e0d\u77e5\u3002\u90a3\u65e5\u706b\u70e7\u5546\u5bb6\u5821\u540e\uff0c\u4ed6\u89c1\u5230\u9a6c\u6625\u82b1\u548c\u90a3\u8d35\u516c\u5b50\u5728\u90ca\u5916\u504e\u501a\u8bf4\u8bdd\uff0c\u7709\u68a2\u773c\u89d2\u4e4b\u95f4\u4e92\u8574\u6df1\u60c5\uff0c\u4ed6\u867d\u77a7\u5728\u773c\u91cc\uff0c\u5374\u662f\u4e1d\u6beb\u4e0d\u660e\u5176\u4e2d\u542b\u4e49\uff0c\u56e0\u6b64\u59cb\u7ec8\u6ca1\u60f3\u5230\u90a3\u8d35\u516c\u5b50\u8eab\u4e0a\uff0c\u8fd9\u65f6\u7ecf\u79e6\u8010\u4e4b\u4e00\u70b9\u660e\uff0c\u624d\u604d\u7136\u5927\u609f\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u516b\u5366\u95e8\u7684\u738b\u6c0f\u5144\u5f1f\u2026\u2026\u201d\u79e6\u8010\u4e4b\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u90a3\u6b21\u662f\u516b\u5366\u95e8\u738b\u6c0f\u5144\u5f1f\u8ddf\u968f\u798f\u516c\u5b50\u53bb\u5546\u5bb6\u5821\u7684\u3002\u201d \u3000\u3000\u5728\u80e1\u6590\u5fc3\u574e\u513f\u4e2d\uff0c\u798f\u516c\u5b50\u662f\u4f55\u7b49\u6837\u4eba\uff0c\u65e9\u5df2\u751a\u4e3a\u6de1\u6f20\uff0c\u4f46\u738b\u6c0f\u5144\u5f1f\u7684\u516b\u5366\u5200\u548c\u516b\u5366\u638c\uff0c\u4e00\u62db\u4e00\u5f0f\uff0c\u5374\u8bb0\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u8bf4\u9053\uff1a\u201c\u798f\u516c\u5b50\uff0c\u798f\u516c\u5b50\u2026\u2026\u55ef\uff0c\u8fd9\u4f4d\u798f\u516c\u5b50\u76f8\u8c8c\u6e05\u96c5\uff0c\u5012\u548c\u90a3\u4e24\u4e2a\u5c0f\u5b69\u513f\u6709\u70b9\u76f8\u50cf\u3002\u201d \u3000\u3000\u79e6\u8010\u4e4b\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u798f\u516c\u5b50\u8363\u534e\u5bcc\u8d35\uff0c\u8bf4\u6743\u52bf\uff0c\u9664\u4e86\u7687\u4e0a\u4fbf\u662f\u4ed6\uff1b\u8bf4\u8c6a\u5bcc\uff0c\u4ed6\u8981\u591a\u5c11\u7687\u4e0a\u4fbf\u7ed9\u591a\u5c11\u3002\u53ef\u662f\u4ed6\u4eba\u5230\u4e2d\u5e74\uff0c\u5374\u6709\u4e00\u4ef6\u4e8b\u5927\u5927\u4e0d\u8db3\uff0c\u90a3\u4fbf\u662f\u819d\u4e0b\u65e0\u513f\u3002\u201d \u3000\u3000\u80e1\u6590\u542c\u4ed6\u8bf4\u5f97\u90a3\u798f\u516c\u5b50\u5982\u6b64\u5a01\u52bf\uff0c\u5fc3\u4e2d\u4e00\u9707\uff0c\u9053\uff1a\u201c\u90a3\u798f\u516c\u5b50\uff0c\u4fbf\u662f\u798f\u5eb7\u5b89\u4e48\uff1f\u201d\u79e6\u8010\u4e4b\u9053\uff1a\u201c\u4e0d\u662f\u4ed6\u662f\u8c01\uff1f\u90a3\u6b63\u662f\u5e73\u91d1\u5ddd\u5927\u5e05\uff0c\u505a\u8fc7\u6b63\u767d\u65d7\u6ee1\u6d32\u90fd\u7edf\uff0c\u76db\u4eac\u5c06\u519b\uff0c\u4e91\u8d35\u603b\u7763\uff0c\u56db\u5ddd\u603b\u7763\uff0c\u73b0\u4efb\u592a\u5b50\u592a\u4fdd\uff0c\u5175\u90e8\u5c1a\u4e66\uff0c\u603b\u7ba1\u5185\u52a1\u5e9c\u5927\u81e3\u7684\u798f\u516c\u5b50\uff0c\u798f\u5927\u5e05\uff01\u201d \u3000\u3000\u80e1\u6590\u9053\uff1a\u201c\u55ef\uff0c\u90a3\u4e24\u4e2a\u5c0f\u5b69\u513f\uff0c\u4fbf\u662f\u8fd9\u4f4d\u798f\u516c\u5b50\u7684\u4eb2\u751f\u9aa8\u8089\u3002\u4ed6\u662f\u5dee\u4f60\u4eec\u6765\u63a5\u56de\u53bb\u7684\u4e86\uff1f\u201d\u79e6\u8010\u4e4b\u9053\uff1a\u201c\u798f\u5927\u5e05\u6b64\u65f6\u8fd8\u4e0d\u77e5\u4ed6\u6709\u4e86\u8fd9\u4e24\u4e2a\u5b69\u5b50\u3002\u4fbf\u662f\u6211\u4eec\uff0c\u4e5f\u662f\u9002\u624d\u542c\u9a6c\u59d1\u5a18\u8bf4\u4e86\u624d\u77e5\u3002\u201d \u3000\u3000\u80e1\u6590\u70b9\u4e86\u70b9\u5934\uff0c\u5fc3\u60f3\uff1a\u201c\u539f\u6765\u9a6c\u59d1\u5a18\u8ddf\u4ed6\u8bf4\u8bdd\u4e4b\u65f6\u8138\u7ea2\uff0c\u4fbf\u662f\u4e3a\u6b64\uff0c\u5979\u6240\u4ee5\u5410\u9732\u771f\u60c5\uff0c\u662f\u8981\u4ed6\u4eec\u4e0d\u5f97\u4f24\u4e86\u5b69\u5b50\u3002\u5979\u4e3a\u4e86\u7231\u60dc\u513f\u5b50\uff0c\u8fd9\u4ef6\u4e8b\u867d\u4e0d\u5149\u91c7\uff0c\u5374\u4e5f\u4e0d\u5f97\u4e0d\u8bf4\u3002\u201d\u53ea\u542c\u79e6\u8010\u4e4b\u53c8\u9053\uff1a\u201c\u798f\u5927\u5e05\u53ea\u662f\u5dee\u6211\u4eec\u6765\u77a7\u77a7\u9a6c\u59d1\u5a18\u7684\u60c5\u5f62\uff0c\u4f46\u6211\u4eec\u63e3\u6469\u5927\u5e05\u4e4b\u610f\uff0c\u6700\u597d\u662f\u8fce\u63a5\u9a6c\u59d1\u5a18\u8d74\u4eac\u3002\u9a6c\u59d1\u5a18\u8fd9\u65f6\u4e08\u592b\u5df2\u7ecf\u6545\u4e16\uff0c\u65e0\u4f9d\u65e0\u9760\uff0c\u4f55\u4e0d\u5c31\u8d74\u4eac\u53bb\u548c\u798f\u5927\u5e05\u76f8\u805a\uff1f\u5979\u4e24\u4e2a\u513f\u5b50\u7236\u5b50\u76f8\u9022\uff0c\u4ece\u6b64\u9752\u4e91\u76f4\u4e0a\uff0c\u5927\u5bcc\u5927\u8d35\uff0c\u5c82\u4e0d\u5f3a\u4e8e\u5728\u9556\u5c40\u5b50\u4e2d\u4f4e\u4e09\u4e0b\u56db\u7684\u53ae\u6df7\uff1f\u80e1\u5144\u5f1f\uff0c\u4f60\u4fbf\u529d\u529d\u9a6c\u59d1\u5a18\uff1f\u201d \u3000\u3000\u80e1\u6590\u5fc3\u4e2d\u6df7\u4e71\uff0c\u542c\u4ed6\u4e4b\u8a00\uff0c\u5012\u4e5f\u6709\u7406\uff0c\u53ea\u662f\u5176\u4e2d\u603b\u89c9\u6709\u751a\u4e0d\u59a5\uff0c\u81f3\u4e8e\u4ec0\u4e48\u4e0d\u59a5\uff0c\u4e00\u65f6\u5374\u53c8\u8bf4\u4e0d\u4e0a\u6765\u3002 \u3000\u3000\u4ed6\u6c89\u541f\u534a\u664c\uff0c\u95ee\u9053\uff1a\u201c\u90a3\u5546\u5b9d\u9707\u5462\uff1f\u600e\u4e48\u8ddf\u4f60\u4eec\u5728\u4e00\u8d77\u4e86\uff1f\u201d \u3000\u3000\u79e6\u8010\u4e4b\u9053\uff1a\u201c\u5546\u5b9d\u9707\u5f97\u738b\u6c0f\u5144\u5f1f\u7684\u4e3e\u8350\uff0c\u4e5f\u5728\u798f\u5927\u5e05\u5e9c\u4e2d\u5f53\u5dee\u3002 \u3000\u3000\u56e0\u4ed6\u8bc6\u5f97\u9a6c\u59d1\u5a18\uff0c\u662f\u4ee5\u4e00\u540c\u5357\u4e0b\u3002\u201d\u80e1\u6590\u8138\u8272\u4e00\u6c89\uff0c\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u4ed6\u6253\u6b7b\u5f90\u94ee\u5f90\u5927\u54e5\uff0c\u662f\u51fa\u4e8e\u798f\u5927\u5e05\u7684\u6388\u610f\uff1f\u201d\u79e6\u8010\u4e4b\u5fd9\u9053\uff1a\u201c\u90a3\u5012\u4e0d\u662f\uff0c\u798f\u5927\u5e05\u8d35\u4eba\u4e8b\u5fd9\uff0c\u600e\u77e5\u9a6c\u59d1\u5a18\u5df2\u548c\u90a3\u59d3\u5f90\u7684\u6210\u5a5a\uff1f\u4ed6\u53ea\u662f\u5fc3\u8840\u6765\u6f6e\uff0c\u60f3\u8d77\u4e86\u65e7\u60c5\uff0c\u6d3e\u51e0\u4e2a\u5f53\u5dee\u7684\u5357\u6765\u6253\u63a2\u4e00\u4e0b\u6d88\u606f\u3002\u6b64\u523b\u5df2\u6709\u4e24\u4e2a\u5144\u5f1f\u98de\u9a6c\u8d74\u4eac\u8d76\u62a5\u559c\u8baf\uff0c\u798f\u5927\u5e05\u4e00\u77e5\u4ed6\u7adf\u6709\u4e24\u4f4d\u516c\u5b50\uff0c\u8fd9\u756a\u9ad8\u5174\u81ea\u662f\u4e0d\u7528\u8bf4\u7684\u4e86\u3002\u201d \u3000\u3000\u8fd9\u4e48\u4e00\u8bf4\uff0c\u80e1\u6590\u5fc3\u5934\u8bb8\u591a\u7591\u56e2\uff0c\u4e00\u65f6\u5c3d\u89e3\u3002\u53ea\u89c9\u6b64\u4e8b\u6028\u4e0d\u5f97\u9a6c\u6625\u82b1\uff0c\u4e5f\u6028\u4e0d\u5f97\u798f\u5eb7\u5b89\uff0c\u5546\u5b9d\u9707\u6740\u5f90\u94ee\u56fa\u7136\u4e0d\u8be5\uff0c\u53ef\u662f\u4ed6\u5df2\u4e00\u547d\u76f8\u507f\uff0c\u81ea\u4e5f\u5df2\u65e0\u8bdd\u53ef\u8bf4\uff0c\u53ea\u662f\u60f3\u5230\u5f90\u94ee\u4e00\u751f\u5fe0\u539a\u8001\u5b9e\uff0c\u660e\u77e5\u4e8c\u5b50\u975e\u5df1\u4eb2\u751f\uff0c\u59cb\u7ec8\u9690\u5fcd\u4e0d\u8a00\uff0c\u5230\u6700\u540e\u5374\u53c8\u843d\u5f97\u5982\u6b64\u4e0b\u573a\uff0c\u6df1\u4e3a\u607b\u7136\uff0c\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u79e6\u5927\u54e5\uff0c\u6b64\u4e8b\u5df2\u5206\u5256\u660e\u767d\uff0c\u7b97\u662f\u5c0f\u5f1f\u591a\u7ba1\u95f2\u4e8b\u3002\u201d\u8f7b\u8f7b\u4e00\u7eb5\uff0c\u843d\u5728\u5730\u4e0b\u3002 \u3000\u3000\u79e6\u8010\u4e4b\u89c1\u4ed6\u843d\u6811\u4e4b\u65f6\uff0c\u81ea\u5df1\u4e1d\u6beb\u4e0d\u89c9\u6811\u5e72\u6447\u52a8\uff0c\u7adf\u662f\u5168\u6ca1\u5728\u6811\u4e0a\u501f\u529b\uff0c\u82e5\u4e0d\u7ec6\u60f3\uff0c\u90a3\u4e5f\u7f62\u4e86\uff0c\u7565\u4e00\u5bfb\u601d\uff0c\u53ea\u89c9\u5f97\u8fd9\u95e8\u8f7b\u529f\u5b9e\u662f\u6df1\u9083\u96be\u6d4b\uff0c\u81ea\u5df1\u518d\u7ec3\u5341\u5e74\uff0c\u4e5f\u662f\u51b3\u8ba1\u4e0d\u80fd\u8fbe\u6b64\u5883\u754c\uff0c\u4e0d\u77e5\u4ed6\u5c0f\u5c0f\u5e74\u7eaa\uff0c\u4f55\u4ee5\u7adf\u80fd\u5230\u6b64\u5730\u6b65\uff1f\u4ed6\u53c8\u662f\u60ca\u5f02\uff0c\u53c8\u611f\u6cae\u4e27\uff0c\u5f85\u5f97\u8dc3\u843d\u5730\u4e0b\uff0c\u89c1\u80e1\u6590\u65e9\u5df2\u56de\u8fdb\u77f3\u5c4b\u53bb\u4e86\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u5728\u7a97\u524d\u4e45\u5f85\u80e1\u6590\u4e0d\u5f52\uff0c\u65e9\u5df2\u5fc3\u7126\u4e07\u5206\uff0c\u597d\u5bb9\u6613\u76fc\u5f97\u4ed6\u5f52\u6765\uff0c\u89c1\u4ed6\u795e\u8272\u9eef\u7136\uff0c\u4f3c\u4e4e\u5341\u5206\u96be\u8fc7\uff0c\u5f53\u4e0b\u4e5f\u4e0d\u76f8\u8be2\uff0c\u53ea\u662f\u548c\u4ed6\u8bf4\u4e9b\u95f2\u8bdd\u3002 \u3000\u3000\u8fc7\u4e0d\u591a\u65f6\uff0c\u6c6a\u94c1\u9e57\u63d0\u4e86\u4e00\u5927\u9505\u996d\u3001\u4e00\u5927\u9505\u7ea2\u70e7\u8089\u9001\u6765\u77f3\u5c4b\uff0c\u8fd8\u6709\u4e09\u74f6\u70e7\u9152\u3002\u80e1\u6590\u5c06\u9152\u5012\u5728\u7897\u91cc\u4fbf\u559d\u3002\u7a0b\u7075\u7d20\u53d6\u51fa\u94f6\u9488\uff0c\u8981\u8bd5\u9152\u83dc\u4e2d\u662f\u5426\u6709\u6bd2\u3002\u80e1\u6590\u9053\uff1a\u201c\u6709\u9a6c\u59d1\u5a18\u5728\u6b64\uff0c\u4ed6\u4eec\u600e\u6562\u4e0b\u6bd2\uff1f\u201d\u9a6c\u6625\u82b1\u8138\u4e0a\u4e00\u7ea2\uff0c\u7adf\u4e0d\u8fc7\u6765\u5403\u996d\u3002\u80e1\u6590\u4e5f\u4e0d\u76f8\u529d\uff0c\u95f7\u58f0\u4e0d\u54cd\u7684\u5c06\u4e09\u74f6\u70e7\u9152\u559d\u4e86\u4e2a\u70b9\u6ef4\u4e0d\u5269\uff0c\u5403\u4e86\u4e00\u5927\u7897\u8089\uff0c\u5374\u4e0d\u5403\u996d\uff0c\u9189\u91ba\u91ba\u9760\u5728\u684c\u4e0a\uff0c\u7eb3\u5934\u4fbf\u7761\u3002 \u3000\u3000\u80e1\u6590\u6b21\u6668\u8f6c\u9192\uff0c\u89c1\u81ea\u5df1\u80cc\u4e0a\u62ab\u4e86\u4e00\u4ef6\u957f\u888d\uff0c\u60f3\u662f\u7a0b\u7075\u7d20\u5728\u665a\u95f4\u6240\u76d6\u3002\u5979\u7ad9\u5728\u7a97\u53e3\uff0c\u79c0\u53d1\u88ab\u6668\u98ce\u4e00\u5439\uff0c\u5fae\u5fae\u98de\u626c\u3002 \u3000\u3000\u80e1\u6590\u671b\u7740\u5979\u82d7\u6761\u80cc\u5f71\uff0c\u5fc3\u4e2d\u6df7\u548c\u7740\u611f\u6fc0\u548c\u601c\u60dc\u4e4b\u610f\uff0c\u53eb\u4e86\u58f0\uff1a\u201c\u4e8c\u59b9\uff01\u201d\u7a0b\u7075\u7d20\u201c\u55ef\u201d\u7684\u4e00\u58f0\uff0c\u8f6c\u8fc7\u8eab\u6765\u3002\u80e1\u6590\u89c1\u5979\u7761\u773c\u60fa\u5fea\uff0c\u5927\u6709\u5026\u8272\uff0c\u9053\uff1a\u201c\u4f60\u4e00\u665a\u6ca1\u7761\u5417\uff1f\u554a\uff0c\u6211\u5fd8\u4e86\u8ddf\u4f60\u8bf4\uff0c\u6709\u9a6c\u59d1\u5a18\u5728\u6b64\uff0c\u4ed6\u4eec\u4e0d\u6562\u5bf9\u54b1\u4eec\u600e\u6837\u3002\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u9a6c\u59d1\u5a18\u534a\u591c\u91cc\u6084\u6084\u51fa\u5c4b\uff0c\u81f3\u4eca\u672a\u56de\u3002\u5979\u51fa\u53bb\u65f6\u8f7b\u624b\u8f7b\u811a\uff0c\u6015\u60ca\u9192\u4e86\u4f60\uff0c\u6211\u4e5f\u4fbf\u5047\u88c5\u7761\u7740\u3002\u201d\u80e1\u6590\u5fae\u5fae\u4e00\u60ca\uff0c\u8f6c\u8fc7\u8eab\u6765\uff0c\u679c\u89c1\u9a6c\u6625\u82b1\u6240\u5750\u4e4b\u5904\u53ea\u5269\u4e0b\u4e00\u5f20\u7a7a\u51f3\u3002 \u3000\u3000\u4e24\u4eba\u6253\u5f00\u5c4b\u95e8\uff0c\u8d70\u4e86\u51fa\u53bb\uff0c\u6811\u6797\u4e2d\u7adf\u662f\u5bc2\u7136\u65e0\u4eba\uff0c\u6570\u5341\u4e58\u4eba\u9a6c\uff0c\u5728\u9ed1\u591c\u4e2d\u9000\u5f97\u5e72\u5e72\u51c0\u51c0\u3002\u6811\u4e0a\u7f1a\u7740\u4e24\u5339\u5750\u9a91\uff0c\u81ea\u662f\u7559\u7ed9\u80e1\u7a0b\u4e8c\u4eba\u7684\u3002 \u3000\u3000\u518d\u8d70\u51fa\u6570\u4e08\uff0c\u53ea\u89c1\u6797\u4e2d\u5806\u7740\u4e24\u4e2a\u65b0\u575f\uff0c\u575f\u524d\u5e76\u65e0\u6807\u5fd7\uff0c\u4e5f\u4e0d\u77e5\u54ea\u4e00\u4e2a\u662f\u5f90\u94ee\u7684\uff0c\u54ea\u4e00\u4e2a\u662f\u5546\u5b9d\u9707\u7684\u3002\u80e1\u6590\u5fc3\u60f3\uff1a\u201c\u867d\u7136\u4e00\u4e2a\u662f\u4e08\u592b\uff0c\u4e00\u4e2a\u662f\u6740\u4e08\u592b\u7684\u4ec7\u4eba\uff0c\u4f46\u5728\u9a6c\u59d1\u5a18\u5fc3\u4e2d\uff0c\u6050\u6015\u4e24\u4eba\u4e5f\u65e0\u591a\u5927\u5dee\u522b\uff0c\u90fd\u662f\u7231\u7740\u5979\u800c\u5979\u5e76\u4e0d\u7231\u7684\u4eba\uff0c\u90fd\u662f\u4e3a\u4e86\u5979\u800c\u9001\u547d\u7684\u4e0d\u5e78\u4e4b\u4eba\u3002\u201d\u60f3\u5230\u6b64\u5904\uff0c\u4e0d\u7531\u5f97\u559f\u7136\u957f\u53f9\uff0c\u4e8e\u662f\u5c06\u79e6\u8010\u4e4b\u7684\u8bf4\u8bdd\u90fd\u8f6c\u8ff0\u7ed9\u7a0b\u7075\u7d20\u542c\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u542c\u4e86\uff0c\u4e5f\u662f\u9eef\u7136\u53f9\u606f\uff0c\u8bf4\u9053\uff1a\u201c\u539f\u6765\u90a3\u7626\u8001\u5934\u513f\u662f\u516b\u6781\u62f3\u7684\u638c\u95e8\u4eba\u79e6\u8010\u4e4b\u3002\u4ed6\u6709\u4e2a\u5916\u53f7\uff0c\u53eb\u4f5c\u516b\u81c2\u54ea\u5412\u3002\u8fd9\u79cd\u4eba\u5728\u6743\u8d35\u95e8\u4e0b\u4f5c\u8d70\u72d7\uff0c\u54c1\u683c\u513f\u5f88\u4f4e\uff0c\u54b1\u4eec\u4eca\u540e\u4e0d\u7528\u7406\u4ed6\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u662f\u554a\u3002\u201d \u3000\u3000\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u9a6c\u59d1\u5a18\u5fc3\u4e2d\u559c\u6b22\u798f\u516c\u5b50\uff0c\u5f90\u94ee\u4fbf\u662f\u6d3b\u7740\uff0c\u4e5f\u53ea\u6709\u5f92\u589e\u82e6\u607c\u3002\u4ed6\u5c0f\u5c0f\u4e00\u4e2a\u5012\u9709\u7684\u9556\u5e08\uff0c\u600e\u80fd\u8ddf\u4eba\u5bb6\u5175\u90e8\u5c1a\u4e66\u3001\u7edf\u5175\u5927\u5143\u5e05\u76f8\u4e89\uff1f\u201d\u80e1\u6590\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5012\u8fd8\u662f\u6b7b\u4e86\u5e72\u51c0\u3002\u201d \u3000\u3000\u4e8e\u662f\u5728\u4e24\u5ea7\u575f\u524d\u62dc\u4e86\u51e0\u62dc\uff0c\u8bf4\u9053\uff1a\u201c\u5f90\u5927\u54e5\u3001\u5546\u516c\u5b50\uff0c\u4f60\u4eec\u751f\u524d\u4e0d\u8bba\u548c\u6211\u6709\u6069\u6709\u6028\uff0c\u6b7b\u540e\u4e00\u7b14\u52fe\u9500\u3002\u9a6c\u59d1\u5a18\u4ece\u6b64\u5bcc\u8d35\u4e0d\u5c3d\uff0c\u4f60\u4eec\u4e24\u4f4d\u6b7b\u800c\u6709\u77e5\uff0c\u4e5f\u4e0d\u7528\u518d\u8bb0\u7740\u5979\u4e86\u3002\u201d \u3000\u3000\u4e8c\u4eba\u7275\u4e86\u9a6c\u5339\uff0c\u7f13\u6b65\u51fa\u6797\u3002\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u5927\u54e5\uff0c\u54b1\u4eec\u5230\u54ea\u513f\u53bb\uff1f\u201d\u80e1\u6590\u9053\uff1a\u201c\u5148\u627e\u5230\u5ba2\u5e97\uff0c\u8ba9\u4f60\u5b89\u7761\u534a\u65e5\uff0c\u518d\u8bf4\u522b\u7684\uff0c\u53ef\u522b\u7d2f\u574f\u4e86\u6211\u7684\u59b9\u5b50\uff01\u201d\u7a0b\u7075\u7d20\u542c\u4ed6\u8bf4\u201c\u6211\u7684\u59b9\u5b50\u201d\uff0c\u5fc3\u4e2d\u8bf4\u4e0d\u51fa\u7684\u559c\u6b22\uff0c\u8f6c\u5934\u5411\u4ed6\u751c\u751c\u4e00\u7b11\u3002 \u3000\u3000\u5728\u524d\u9014\u9547\u4e0a\u5ba2\u5e97\u4e4b\u4e2d\uff0c\u7a0b\u7075\u7d20\u5927\u7761\u534a\u65e5\uff0c\u9192\u8f6c\u65f6\u5df2\u662f\u5348\u540e\u672a\u523b\u3002\u5979\u72ec\u81ea\u51fa\u5e97\uff0c\u8bf4\u8981\u53bb\u4e70\u4e9b\u7269\u4e8b\uff0c\u56de\u6765\u65f6\u624b\u4e0a\u6367\u4e86\u4e24\u4e2a\u5927\u7eb8\u5305\uff0c\u7b11\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u731c\u6211\u4e70\u4e86\u4e9b\u4ec0\u4e48\uff1f\u201d\u80e1\u6590\u89c1\u7eb8\u4e0a\u5370\u7740\u201c\u8001\u4e5d\u798f\u8863\u5e84\u201d\u7684\u5e97\u53f7\uff0c\u9053\uff1a\u201c\u54b1\u4eec\u53c8\u6765\u7c98\u80e1\u5b50\u4e54\u88c5\u6539\u626e\u4e48\uff1f\u201d \u3000\u3000\u7a0b\u7075\u7d20\u6253\u5f00\u7eb8\u5305\uff0c\u6bcf\u4e00\u5305\u4e2d\u90fd\u662f\u4e00\u4ef6\u5d2d\u65b0\u7684\u8863\u886b\uff0c\u4e00\u7537\u4e00\u5973\uff0c\u7537\u88c5\u6de1\u9752\uff0c\u5973\u88c5\u5ae9\u9ec4\uff0c\u5747\u751a\u96c5\u81f4\u3002\u665a\u996d\u540e\u7a0b\u7075\u7d20\u53eb\u80e1\u6590\u8bd5\u7a7f\uff0c\u8863\u8896\u957f\u4e86\u4e24\u5bf8\uff0c\u814b\u5e95\u4e5f\u663e\u5f97\u592a\u80a5\uff0c\u4e8e\u662f\u53d6\u51fa\u526a\u5200\u9488\u7ebf\uff0c\u4fbf\u5728\u706f\u4e0b\u7ed9\u4ed6\u4fee\u526a\u3002 \u3000\u3000\u80e1\u6590\u9053\uff1a\u201c\u4e8c\u59b9\uff0c\u6211\u8bf4\u54b1\u4eec\u5f97\u4e0a\u5317\u4eac\u77a7\u77a7\u3002\u201d\u7a0b\u7075\u7d20\u62bf\u5634\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u65e9\u77e5\u9053\u4f60\u8981\u4e0a\u5317\u4eac\u554a\uff0c\u6240\u4ee5\u4e70\u4e24\u4ef6\u597d\u4e00\u70b9\u513f\u7684\u8863\u886b\uff0c\u5426\u5219\u4e61\u4e0b\u5927\u59d1\u5a18\u8fdb\u4eac\uff0c\u4e0d\u7ed9\u4eba\u7b11\u8bdd\u4e48\uff1f\u201d\u80e1\u6590\u7b11\u9053\uff1a\u201c\u4f60\u771f\u60f3\u5f97\u5468\u5230\u3002\u54b1\u4e24\u4e2a\u4e61\u4e0b\u4eba\u4fbf\u8981\u8fdb\u4eac\u53bb\u4f1a\u4f1a\u5929\u5b50\u811a\u5e95\u4e0b\u7684\u4eba\u7269\uff0c\u77a7\u77a7\u798f\u5927\u5e05\u7684\u638c\u95e8\u4eba\u5927\u4f1a\u4e4b\u4e2d\uff0c\u5230\u5e95\u6709\u4e9b\u4ec0\u4e48\u82f1\u96c4\u8c6a\u6770\u3002\u201d\u8fd9\u4e24\u53e5\u8bdd\u8bf4\u5f97\u8f7b\u63cf\u6de1\u5199\uff0c\u8bed\u610f\u4e4b\u4e2d\uff0c\u5374\u81ea\u6709\u4e00\u80a1\u8c6a\u6c14\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u624b\u4e2d\u505a\u7740\u9488\u7ebf\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u60f3\u798f\u5927\u5e05\u5f00\u8fd9\u4e2a\u5929\u4e0b\u638c\u95e8\u4eba\u5927\u4f1a\uff0c\u5b89\u7740\u4ec0\u4e48\u5fc3\u773c\u513f\uff1f\u201d\u80e1\u6590\u9053\uff1a\u201c\u90a3\u81ea\u662f\u7f51\u7f57\u4eba\u624d\u4e4b\u610f\u4e86\uff0c\u4ed6\u8981\u5929\u4e0b\u82f1\u96c4\uff0c\u90fd\u6295\u5230\u4ed6\u7684\u9b54\u4e0b\u3002\u53ef\u662f\u771f\u6b63\u7684\u5927\u82f1\u96c4\u5927\u8c6a\u6770\uff0c\u5374\u672a\u5fc5\u4f1a\u53bb\u3002\u201d\u7a0b\u7075\u7d20\u5fae\u7b11\u9053\uff1a\u201c\u50cf\u4f60\u8fd9\u7b49\u5c11\u5e74\u82f1\u96c4\uff0c\u4fbf\u4e0d\u4f1a\u53bb\u4e86\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u6211\u7b97\u662f\u90a3\u4e00\u95e8\u5b50\u7684\u82f1\u96c4\uff1f\u6211\u8bf4\u7684\u662f\u82d7\u4eba\u51e4\u8fd9\u4e00\u6d41\u7684\u6210\u540d\u4eba\u7269\u3002\u201d\u4ed6\u5ffd\u5730\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5018\u82e5\u6211\u7239\u7239\u5728\u4e16\uff0c\u5230\u8fd9\u638c\u95e8\u4eba\u5927\u4f1a\u4e2d\u53bb\u6405\u4ed6\u4e2a\u5929\u7ffb\u5730\u8986\uff0c\u90a3\u624d\u53eb\u4eba\u75db\u5feb\u5462\u3002\u201d \u3000\u3000\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u4f60\u53bb\u8ddf\u8fd9\u798f\u5927\u5e05\u6363\u6363\u86cb\uff0c\u4e0d\u4e5f\u597d\u5417\uff1f\u6211\u77a7\u8fd8\u6709\u4e00\u4e2a\u4eba\u662f\u5fc5\u5b9a\u8981\u53bb\u7684\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u8c01\u554a\uff1f\u201d\u7a0b\u7075\u7d20\u5fae\u7b11\u9053\uff1a\u201c\u8fd9\u53eb\u4f5c\u660e\u77e5\u6545\u95ee\u4e86\u3002\u4f60\u8fd8\u662f\u7ed9\u6211\u723d\u723d\u5feb\u5feb\u5730\u8bf4\u51fa\u6765\u7684\u597d\u3002\u201d \u3000\u3000\u80e1\u6590\u65e9\u5df2\u660e\u767d\u5979\u7684\u5fc3\u610f\uff0c\u4e5f\u4e0d\u518d\u5047\u88c5\uff0c\u8bf4\u9053\uff1a\u201c\u5979\u4e5f\u672a\u5fc5\u4e00\u5b9a\u53bb\u3002\u201d\u987f\u4e86\u4e00\u987f\uff0c\u53c8\u9053\uff1a\u201c\u8fd9\u4f4d\u8881\u59d1\u5a18\u662f\u53cb\u662f\u654c\uff0c\u6211\u8fd8\u5f04\u4e0d\u660e\u767d\u5462\u3002\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u5982\u679c\u6bcf\u4e2a\u654c\u4eba\u90fd\u9001\u6211\u4e00\u53ea\u7389\u51e4\u513f\uff0c\u6211\u5012\u76fc\u671b\u904d\u5929\u4e0b\u90fd\u662f\u654c\u4eba\u624d\u597d\u2026\u2026\u201d \u3000\u3000\u5ffd\u542c\u5f97\u7a97\u5916\u4e00\u4e2a\u5973\u5b50\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u6211\u4e5f\u9001\u4f60\u4e00\u53ea\uff01\u201d\u58f0\u97f3\u752b\u6bd5\uff0c\u55e4\u7684\u4e00\u54cd\uff0c\u4e00\u7269\u5c04\u7a7f\u7a97\u7eb8\uff0c\u5411\u7a0b\u7075\u7d20\u98de\u6765\u3002 \u3000\u3000\u80e1\u6590\u62ff\u8d77\u684c\u4e0a\u7a0b\u7075\u7d20\u88c1\u8863\u7684\u7af9\u5c3a\uff0c\u5411\u90a3\u7269\u4e00\u6572\uff0c\u51fb\u843d\u5728\u684c\uff0c\u968f\u624b\u4e00\u638c\u62e8\u53bb\uff0c\u70db\u5149\u5e94\u98ce\u800c\u706d\u3002\u63a5\u7740\u542c\u5f97\u7a97\u5916\u90a3\u4eba\u8bf4\u9053\uff1a\u201c\u6311\u706f\u591c\u8c08\uff0c\u7f8e\u5f97\u7d27\u54ea\uff01\u201d \u3000\u3000\u80e1\u6590\u542c\u8bdd\u58f0\u4f9d\u7a00\u4fbf\u662f\u8881\u7d2b\u8863\u7684\u53e3\u97f3\uff0c\u80f8\u53e3\u4e00\u70ed\uff0c\u51b2\u53e3\u800c\u51fa\uff1a\u201c\u662f\u8881\u59d1\u5a18\u4e48\uff1f\u201d\u5374\u542c\u6b65\u58f0\u7ec6\u788e\uff0c\u9877\u523b\u95f4\u5df2\u7136\u8fdc\u53bb\u3002 \u3000\u3000\u80e1\u6590\u6253\u706b\u91cd\u70b9\u8721\u70db\uff0c\u53ea\u89c1\u7a0b\u7075\u7d20\u8138\u8272\u82cd\u767d\uff0c\u9ed8\u4e0d\u4f5c\u58f0\u3002\u80e1\u6590\u9053\uff1a\u201c\u54b1\u4eec\u51fa\u53bb\u77a7\u77a7\u3002\u201d \u3000\u3000\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u4f60\u53bb\u77a7\u5427\uff01\u201d\u80e1\u6590\u201c\u55ef\u201d\u4e86\u4e00\u58f0\uff0c\u5374\u4e0d\u51fa\u53bb\uff0c\u62ff\u8d77\u684c\u4e0a\u90a3\u7269\u770b\u65f6\uff0c\u5374\u662f\u4e00\u7c92\u5c0f\u5c0f\u77f3\u5b50\uff0c\u5fc3\u60f3\uff1a\u201c\u6b64\u4eba\u884c\u4e8b\u795e\u51fa\u9b3c\u6ca1\uff0c\u4e0d\u77e5\u4f55\u65f6\u8e51\u4e0a\u4e86\u6211\u4eec\uff0c\u6211\u7adf\u662f\u6beb\u4e0d\u77e5\u89c9\u3002\u201d\u660e\u77e5\u7a0b\u7075\u7d20\u8981\u5fc3\u4e2d\u4e0d\u5feb\uff0c\u4f46\u5fcd\u4e0d\u4f4f\u63a8\u5f00\u7a97\u5b50\uff0c\u8dc3\u51fa\u7a97\u5916\u4e00\u770b\uff0c\u56db\u4e0b\u91cc\u81ea\u662f\u65e9\u65e0\u4eba\u5f71\u3002 \u3000\u3000\u4ed6\u56de\u8fdb\u623f\u6765\uff0c\u642d\u8baa\u7740\u60f3\u8bf4\u4ec0\u4e48\u8bdd\u3002\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u5929\u8272\u4e0d\u65e9\uff0c\u5927\u54e5\u4f60\u56de\u623f\u5b89\u7761\u53bb\u5427\uff01\u201d\u80e1\u6590\u9053\uff1a\u201c\u6211\u5012\u8fd8\u4e0d\u5026\u3002\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u6211\u5374\u5026\u4e86\uff0c\u660e\u65e5\u4e00\u65e9\u4fbf\u5f97\u8d76\u8def\u5462\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u662f\u3002\u201d\u81ea\u884c\u56de\u623f\u3002 \u3000\u3000\u8fd9\u4e00\u665a\u4ed6\u7ffb\u6765\u8986\u53bb\uff0c\u603b\u662f\u7761\u4e0d\u5b89\u6795\uff0c\u4e00\u65f6\u60f3\u5230\u8881\u7d2b\u8863\uff0c\u4e00\u65f6\u60f3\u5230\u7a0b\u7075\u7d20\uff0c\u4e00\u65f6\u5374\u53c8\u60f3\u5230\u9a6c\u6625\u82b1\u3001\u5f90\u94ee\u548c\u5546\u5b9d\u9707\u3002\u76f4\u5230\u56db\u66f4\u65f6\u5206\uff0c\u8fd9\u624d\u6726\u6726\u80e7\u80e7\u7684\u7761\u53bb\u3002 \u3000\u3000\u7b2c\u4e8c\u5929\u8fd8\u672a\u8d77\u5e8a\uff0c\u7a0b\u7075\u7d20\u6572\u95e8\u8fdb\u6765\uff0c\u624b\u4e2d\u62ff\u7740\u90a3\u4ef6\u65b0\u888d\u5b50\uff0c\u7b11\u563b\u563b\u7684\u9053\uff1a\u201c\u5feb\u8d77\u6765\uff0c\u5916\u9762\u6709\u597d\u4e1c\u897f\u7b49\u7740\u4f60\u3002\u201d\u5c06\u888d\u5b50\u653e\u5728\u684c\u4e0a\uff0c\u7fe9\u7136\u51fa\u623f\u3002 \u3000\u3000\u80e1\u6590\u7ffb\u8eab\u5750\u8d77\uff0c\u62ab\u4e0a\u8eab\u5b50\u4e00\u8bd5\uff0c\u5927\u5c0f\u957f\u77ed\uff0c\u65e0\u4e0d\u5408\u5f0f\uff0c\u5fc3\u60f3\u6628\u665a\u6211\u56de\u623f\u5b89\u7761\u4e4b\u65f6\uff0c\u5979\u4e00\u53ea\u8896\u5b50\u4e5f\u6ca1\u7f1d\u597d\uff0c\u770b\u6765\u7b49\u6211\u8d70\u540e\uff0c\u5979\u53c8\u7f1d\u4e86\u591a\u65f6\uff0c\u4e8e\u662f\u7a7f\u4e86\u65b0\u886b\uff0c\u8d70\u51fa\u623f\u6765\uff0c\u5411\u7a0b\u7075\u7d20\u4e00\u63d6\uff0c\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u4e8c\u59b9\u3002\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u591a\u8c22\u4ec0\u4e48\uff1f\u4eba\u5bb6\u8fd8\u7ed9\u4f60\u9001\u4e86\u9a8f\u9a6c\u6765\u5462\u3002\u201d \u3000\u3000\u80e1\u6590\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\u9a8f\u9a6c\uff1f\u201d\u8d70\u5230\u9662\u5b50\u4e2d\u4e00\u770b\uff0c\u53ea\u89c1\u4e00\u5339\u904d\u8eab\u5149\u6d01\u5982\u96ea\u7684\u767d\u9a6c\u7cfb\u5728\u9a6c\u6869\u4e4b\u4e0a\uff0c\u6b63\u662f\u6614\u5e74\u5728\u5546\u5bb6\u5821\u89c1\u5230\u8d75\u534a\u5c71\u6240\u9a91\u3001\u540e\u6765\u8881\u7d2b\u8863\u4e58\u5750\u7684\u90a3\u5339\u767d\u9a6c\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u4eca\u513f\u4e00\u65e9\u6211\u521a\u8d77\u8eab\uff0c\u5e97\u5c0f\u4e8c\u4fbf\u5927\u547c\u5c0f\u53eb\uff0c\u8bf4\u5927\u95e8\u7ed9\u5c0f\u5077\u513f\u534a\u591c\u91cc\u6253\u5f00\u4e86\uff0c\u4e0d\u77e5\u7ed9\u5077\u4e86\u4ec0\u4e48\u4e1c\u897f\u3002\u4f46\u524d\u540e\u4e00\u67e5\uff0c\u975e\u4f46\u4e00\u7269\u4e0d\u5c11\uff0c\u9662\u5b50\u91cc\u53cd\u800c\u591a\u4e86\u4e00\u5339\u9a6c\u3002\u8fd9\u662f\u7f1a\u5728\u9a6c\u978d\u5b50\u4e0a\u7684\u3002\u201d\u8bf4\u7740\u9012\u8fc7\u4e00\u4e2a\u5c0f\u5c0f\u7ee2\u5305\uff0c\u4e0a\u9762\u5199\u7740\uff1a\u201c\u80e1\u76f8\u516c\u7a0b\u59d1\u5a18\u540c\u62c6\u3002\u201d\u5b57\u8ff9\u751a\u662f\u5a1f\u79c0\u3002 \u3000\u3000\u80e1\u6590\u6253\u5f00\u7ee2\u5305\uff0c\u4e0d\u7531\u5f97\u5446\u4e86\uff0c\u539f\u6765\u5305\u91cc\u53c8\u662f\u4e00\u53ea\u7389\u51e4\uff0c\u7adf\u548c\u5148\u524d\u7559\u8d60\u81ea\u5df1\u7684\u4e00\u6a21\u4e00\u6837\uff0c\u5fc3\u4e2d\u7acb\u60f3\uff1a\u201c\u96be\u9053\u6211\u90a3\u53ea\u7adf\u662f\u5931\u843d\u4e86\uff0c\u8fd8\u662f\u7ed9\u5979\u76d7\u4e86\u53bb\uff1f\u201d\u4f38\u624b\u5230\u6000\u4e2d\u4e00\u6478\uff0c\u89e6\u624b\u751f\u6e29\uff0c\u90a3\u7389\u51e4\u597d\u7aef\u7aef\u7684\u4fbf\u5728\u6000\u4e2d\uff0c\u53d6\u51fa\u6765\u4e00\u770b\uff0c\u4e24\u53ea\u7389\u51e4\u679c\u7136\u96d5\u7422\u5f97\u5168\u7136\u76f8\u540c\uff0c\u53ea\u662f\u4e00\u53ea\u51e4\u5934\u5411\u5de6\uff0c\u4e00\u53ea\u5411\u53f3\u3002 \u3000\u3000\u7ee2\u5305\u4e2d\u53e6\u6709\u4e00\u5f20\u5c0f\u5c0f\u767d\u7eb8\uff0c\u7eb8\u4e0a\u5199\u9053\uff1a\u201c\u9a6c\u5f52\u539f\u4e3b\uff0c\u51e4\u8d60\u4fa0\u5973\u3002\u201d\u80e1\u6590\u53c8\u662f\u4e00\u5446\uff1a\u201c\u8fd9\u9a6c\u53c8\u4e0d\u662f\u6211\u7684\uff0c\u600e\u8bf4\u5f97\u4e0a\u2018\u9a6c\u5f52\u539f\u4e3b\u2019\uff1f\u96be\u9053\u8981\u6211\u8f6c\u8fd8\u7ed9\u8d75\u4e09\u54e5\u4e48\uff1f\u201d\u4e8e\u662f\u5c06\u7b80\u5e16\u548c\u7389\u51e4\u9012\u7ed9\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u8881\u59d1\u5a18\u4e5f\u9001\u4e86\u4e00\u53ea\u7389\u51e4\u7ed9\u4f60\u3002\u201d \u3000\u3000\u7a0b\u7075\u7d20\u4e00\u770b\u7b80\u5e16\u4e0a\u7684\u516b\u5b57\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u53c8\u662f\u4ec0\u4e48\u4fa0\u5973\u4e86\uff1f \u3000\u3000\u4e0d\u662f\u7ed9\u6211\u7684\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u5305\u4e0a\u4e0d\u662f\u660e\u660e\u5199\u7740\u2018\u7a0b\u59d1\u5a18\u2019\uff1f\u5979\u6628\u665a\u53c8\u8bf4\uff1a\u2018\u597d\uff0c\u6211\u4e5f\u9001\u4f60\u4e00\u53ea\uff01\u2019\u201d\u7a0b\u7075\u7d20\u6de1\u7136\u9053\uff1a\u201c\u65e2\u662f\u5982\u6b64\uff0c\u6211\u4fbf\u6536\u4e0b\u3002\u8fd9\u4f4d\u8881\u59d1\u5a18\u5982\u6b64\u539a\u7231\uff0c\u6211\u53ef\u65e0\u4ee5\u4e3a\u62a5\u4e86\u3002\u201d \u3000\u3000\u4e24\u4eba\u4e00\u8def\u5317\u884c\uff0c\u9014\u4e2d\u518d\u6ca1\u9047\u4e0a\u4f55\u7b49\u5f02\u4e8b\uff0c\u8881\u7d2b\u8863\u4e5f\u6ca1\u518d\u73b0\u8eab\uff0c\u4f46\u5728\u80e1\u6590\u548c\u7a0b\u7075\u7d20\u5fc3\u4e2d\uff0c\u4f55\u65f6\u4f55\u523b\u5747\u6709\u4e2a\u8881\u7d2b\u8863\u5728\u3002\u7a97\u4e0b\u95f2\u8c08\uff0c\u7a97\u5916\u4fbf\u4f3c\u6709\u8881\u7d2b\u8863\u5728\u7a83\u542c\uff1b\u5c71\u9053\u9a70\u9a91\uff0c\u5c71\u80cc\u540e\u4fbf\u4f3c\u6709\u8881\u7d2b\u8863\u8eb2\u7740\u3002\u4e24\u4eba\u90fd\u7edd\u53e3\u4e0d\u63d0\u5979\u7684\u540d\u5b57\uff0c\u4f46\u53e3\u91cc\u8d8a\u662f\u56de\u907f\uff0c\u5fc3\u4e2d\u8d8a\u662f\u4e0d\u81ea\u7981\u7684\u8981\u60f3\u5230\u5979\u3002 \u3000\u3000\u4e24\u4eba\u5747\u60f3\uff1a\u201c\u5230\u4e86\u5317\u4eac\uff0c\u603b\u8981\u9047\u89c1\u5979\u4e86\u3002\u201d\u6709\u65f6\uff0c\u76fc\u671b\u5feb\u4e9b\u548c\u5979\u76f8\u89c1\uff1b\u6709\u65f6\uff0c\u5374\u53c8\u76fc\u671b\u8ddf\u5979\u8d8a\u8fdf\u76f8\u89c1\u8d8a\u597d\u3002 \u3000\u3000\u5230\u5317\u4eac\u7684\u8def\u7a0b\u672c\u6765\u5f88\u8fdc\uff0c\u4e24\u4eba\u53c8\u662f\u8fdf\u8fdf\u800c\u884c\uff0c\u957f\u9014\u8dcb\u6d89\uff0c\u98ce\u971c\u4ea4\u4fb5\uff0c\u7a0b\u7075\u7d20\u663e\u5f97\u66f4\u52a0\u6194\u60b4\u4e86\u3002 \u3000\u3000\u4f46\u662f\uff0c\u5317\u4eac\u7ec8\u4e8e\u5230\u4e86\uff0c\u80e1\u6590\u548c\u7a0b\u7075\u7d20\u5e76\u9a91\u8fdb\u4e86\u90fd\u95e8\u3002 \u3000\u3000\u8fdb\u57ce\u95e8\u65f6\u80e1\u6590\u5411\u7a0b\u7075\u7d20\u671b\u4e86\u4e00\u773c\uff0c\u9690\u9690\u7ea6\u7ea6\u95f4\u4f3c\u4e4e\u770b\u5230\u4e00\u6ef4\u6cea\u73e0\u843d\u5728\u5730\u4e0a\u7684\u5c18\u571f\u4e4b\u4e2d\uff0c\u53ea\u662f\u5979\u5c06\u5934\u504f\u7740\uff0c\u6ca1\u80fd\u89c1\u5230\u5979\u7684\u5bb9\u8272\u3002 \u3000\u3000\u80e1\u6590\u5fc3\u5934\u4e00\u9707\uff1a\u201c\u8fd9\u6b21\u5230\u5317\u4eac\u6765\uff0c\u53ef\u6765\u5bf9\u4e86\u5417\uff1f\u201d \u3000\u3000\u5176\u65f6\u6b63\u5f53\u4e7e\u9686\u4e2d\u53f6\uff0c\u56db\u6d77\u5347\u5e73\u3002\u4eac\u90fd\u79ef\u50a8\u6bb7\u5bcc\uff0c\u5929\u4e0b\u7cbe\u534e\uff0c\u5c3d\u6c47\u4e8e\u65af\u3002 \u3000\u3000\u80e1\u6590\u548c\u7a0b\u7075\u7d20\u81ea\u6b63\u9633\u95e8\u5165\u57ce\uff0c\u5728\u5357\u57ce\u4e00\u5bb6\u5ba2\u5e97\u4e4b\u4e2d\u8981\u4e86\u4e24\u95f4\u5ba2\u623f\uff0c\u5348\u95f4\u7528\u8fc7\u9762\u70b9\uff0c\u76f8\u5055\u5230\u8857\u9053\u5404\u5904\u95f2\u901b\uff0c\u4f46\u89c1\u7199\u7199\u6518\u6518\uff0c\u77a7\u4e0d\u5c3d\u7684\u6ee1\u773c\u7e41\u534e\u3002\u4e24\u4eba\u4e0d\u8ba4\u5f97\u9053\u8def\uff0c\u53ea\u5728\u8857\u4e0a\u968f\u610f\u4e71\u8d70\u3002 \u3000\u3000\u901b\u4e86\u4e2a\u628a\u65f6\u8fb0\uff0c\u80e1\u6590\u4e70\u4e86\u51e0\u4e32\u51b0\u7cd6\u846b\u82a6\uff0c\u4e0e\u7a0b\u7075\u7d20\u5404\u81ea\u62ff\u5728\u624b\u4e2d\uff0c\u8fb9\u8d70\u8fb9\u5403\u3002\u5ffd\u542c\u5f97\u8def\u8fb9\u5c0f\u9523\u5f53\u5f53\u58f0\u54cd\uff0c\u6709\u4eba\u5927\u58f0\u5406\u559d\uff0c\u5374\u662f\u7a7a\u5730\u4e0a\u6709\u4e00\u4f19\u4eba\u5728\u6f14\u6b66\u5356\u827a\u3002\u80e1\u6590\u559c\u9053\uff1a\u201c\u4e8c\u59b9\uff0c\u77a7\u77a7\u53bb\u3002\u201d \u3000\u3000\u4e24\u4eba\u6324\u5165\u4eba\u4e1b\uff0c\u53ea\u89c1\u4e00\u540d\u7c97\u58ee\u6c49\u5b50\u624b\u6301\u4e00\u67c4\u5355\u5200\uff0c\u62b1\u62f3\u8bf4\u9053\uff1a\u201c\u5144\u5f1f\u4f7f\u4e00\u8def\u56db\u95e8\u5200\u6cd5\uff0c\u8981\u8bf7\u5404\u4f4d\u5927\u7237\u6307\u6559\u3002\u6709\u4e00\u9996\u2018\u5200\u8bc0\u2019\u8a00\u9053\uff1a\u2018\u5fa1\u4fae\u6467\u950b\u51b3\u80dc\u5f3a\uff0c\u6d45\u5f00\u6df1\u5165\u654c\u4eba\u4f24\u3002\u80c6\u6b32\u5927\u516e\u5fc3\u6b32\u7ec6\uff0c\u7b4b\u987b\u8212\u516e\u81c2\u987b\u957f\u3002\u5f7c\u9ad8\u6211\u77ee\u582a\u5e38\u7528\uff0c\u654c\u5076\u4f4e\u65f6\u6211\u5373\u626c\u3002\u654c\u950b\u672a\u89c1\u4f11\u5148\u8fdb\uff0c\u865a\u523a\u4f2a\u624e\u5f15\u8bf1\u8bd3\u3002\u5f15\u5f7c\u4e0d\u6765\u987b\u5356\u7834\uff0c\u773c\u660e\u624b\u5feb\u59cb\u4e3a\u826f\u3002\u6d45\u6df1\u8001\u5ae9\u7686\u78d5\u6253\uff0c\u8fdb\u9000\u98de\u817e\u5373\u8eb2\u85cf\u3002\u529f\u592b\u4e45\u7ec3\u65b9\u4e91\u719f\uff0c\u719f\u80fd\u751f\u5de7\u5927\u540d\u626c\u3002\u2019\u201d \u3000\u3000\u80e1\u6590\u542c\u4e86\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u51e0\u53e5\u5200\u8bc0\u5012\u662f\u4e0d\u9519\uff0c\u60f3\u6765\u529f\u592b\u4e5f\u5fc5\u662f\u5f3a\u7684\u3002\u201d\u53ea\u89c1\u90a3\u4e2a\u6c49\u5b50\u6446\u4e2a\u95e8\u6237\uff0c\u5355\u5200\u4e00\u8d77\uff0c\u5c55\u62b9\u94a9\u5241\uff0c\u5288\u6253\u78d5\u624e\uff0c\u4f7f\u4e86\u8d77\u6765\uff0c\u81ea\u201c\u5927\u9e4f\u5c55\u7fc5\u201d\u3001\u201c\u91d1\u9e21\u72ec\u7acb\u201d\uff0c\u4ee5\u81f3\u201c\u72ec\u5288\u534e\u5c71\u201d\u3001\u201c\u5206\u82b1\u62c2\u67f3\u201d\uff0c\u4e00\u62db\u4e00\u5f0f\uff0c\u4f7f\u5f97\u5012\u662f\u6709\u6761\u4e0d\u7d0a\uff0c\u4f46\u811a\u6b65\u865a\u6d6e\uff0c\u5200\u52bf\u659c\u6643\uff0c\u529f\u592b\u5b9e\u662f\u4e0d\u8db3\u4e00\u54c2\u3002 \u3000\u3000\u80e1\u6590\u6697\u6697\u597d\u7b11\uff0c\u5fc3\u9053\uff1a\u201c\u65e9\u4fbf\u542c\u4eba\u8bf4\uff0c\u4eac\u5e08\u4e4b\u4eba\u5927\u8a00\u6d6e\u5938\u7684\u5c45\u591a\uff0c\u8fd9\u6c49\u5b50\u5439\u5f97\u5634\u54cd\uff0c\u4f7f\u51fa\u6765\u53ef\u5168\u4e0d\u662f\u90a3\u4f1a\u5b50\u4e8b\u3002\u201d\u6b63\u8981\u548c\u7a0b\u7075\u7d20\u79bb\u53bb\u3002\u4eba\u7fa4\u4e2d\u7a81\u7136\u4e00\u4eba\u54c8\u54c8\u5927\u7b11\uff0c\u559d\u9053\uff1a\u201c\u5140\u90a3\u6c49\u5b50\uff0c\u4f60\u4f7f\u7684\u662f\u4ec0\u4e48\u72d7\u5c41\u5200\u6cd5\uff1f\u201d \u3000\u3000\u4f7f\u5200\u7684\u6c49\u5b50\u5927\u6012\uff0c\u6536\u5200\u56de\u89c6\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u8fd9\u8def\u662f\u6b63\u5b97\u56db\u95e8\u5200\uff0c\u96be\u9053\u4e0d\u5bf9\u4e86\u4e48\uff1f\u5012\u8981\u8bf7\u6559\u3002\u201d \u3000\u3000\u4eba\u7fa4\u4e2d\u8d70\u51fa\u4e00\u6761\u5927\u6c49\uff0c\u7b11\u9053\uff1a\u201c\u597d\uff0c\u6211\u6765\u6559\u4f60\u3002\u201d\u8fd9\u4eba\u8eab\u7a7f\u6b66\u5b98\u670d\u8272\uff0c\u8eaf\u9ad8\u58f0\u96c4\uff0c\u751a\u662f\u5a01\u6b66\u3002\u4ed6\u8d70\u4e0a\u524d\u53bb\uff0c\u63a5\u8fc7\u90a3\u5356\u6b66\u6c49\u5b50\u624b\u4e2d\u5355\u5200\uff0c\u4e00\u77a5\u773c\u7a81\u7136\u89c1\u5230\u80e1\u6590\uff0c\u5446\u4e86\u4e00\u5446\uff0c\u559c\u9053\uff1a\u201c\u80e1\u5927\u54e5\uff0c\u4f60\u4e5f\u5230\u4e86\u5317\u4eac\uff1f\u54c8\u54c8\uff0c\u4f60\u662f\u5f53\u4eca\u4f7f\u5200\u7684\u597d\u624b\uff0c\u5c31\u8bf7\u4f60\u6765\u9732\u4e00\u9732\uff0c\u8ba9\u8fd9\u5c0f\u5b50\u5f00\u5f00\u773c\u754c\uff0c\u6559\u4ed6\u77e5\u9053\u4ec0\u4e48\u624d\u662f\u5200\u6cd5\u3002\u201d \u3000\u3000\u5f53\u4ed6\u4ece\u4eba\u5708\u4e2d\u51fa\u6765\u4e4b\u65f6\uff0c\u80e1\u6590\u548c\u7a0b\u7075\u7d20\u65e9\u5df2\u8ba4\u51fa\uff0c\u6b64\u4eba\u6b63\u662f\u9e70\u722a\u96c1\u884c\u95e8\u7684\u6c6a\u94c1\u9e57\u3002\u4ed6\u5728\u56f4\u56f0\u9a6c\u6625\u82b1\u65f6\u5047\u626e\u76d7\u4f19\uff0c\u539f\u6765\u5374\u662f\u73b0\u4efb\u6709\u529f\u540d\u7684\u6b66\u5b98\u3002 \u3000\u3000\u80e1\u6590\u77e5\u4ed6\u5fc3\u76f4\u53e3\u5feb\uff0c\u5012\u975e\u5978\u6ed1\u4e4b\u8f88\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5c0f\u5f1f\u7684\u73a9\u610f\u513f\u7b97\u5f97\u4ec0\u4e48\uff1f\u6c6a\u5927\u54e5\uff0c\u8fd8\u662f\u4f60\u663e\u4e00\u624b\u3002\u201d \u3000\u3000\u6c6a\u94c1\u9e57\u77e5\u9053\u81ea\u5df1\u7684\u6b66\u529f\u548c\u80e1\u6590\u53ef\u5dee\u5f97\u592a\u8fdc\uff0c\u6709\u4ed6\u5728\u8fd9\u91cc\uff0c\u90a3\u91cc\u8fd8\u6709\u81ea\u5df1\u5356\u5f04\u7684\u4efd\u513f\uff1f\u5c06\u5355\u5200\u5f80\u5730\u4e0b\u4e00\u63b7\uff0c\u7b11\u9053\uff1a\u201c\u6765\u6765\u6765\uff0c\u80e1\u5927\u54e5\uff0c\u8fd9\u4f4d\u59d1\u5a18\u662f\u59d3\u2026\u2026\u59d3\u2026\u2026\u59d3\u7a0b\uff0c\u5bf9\u4e86\uff0c\u7a0b\u59d1\u5a18\uff0c\u54b1\u4eec\u540c\u53bb\u75db\u996e\u4e09\u676f\u3002\u4e24\u4f4d\u5230\u4eac\u5e08\u6765\uff0c\u5728\u4e0b\u8fd9\u4e2a\u4e1c\u9053\u662f\u975e\u505a\u4e0d\u53ef\u7684\u4e86\u3002\u201d\u8bf4\u7740\u62c9\u4e86\u80e1\u6590\u7684\u624b\uff0c\u4fbf\u95ef\u51fa\u4eba\u4e1b\u3002 \u3000\u3000\u90a3\u5356\u6b66\u7684\u6c49\u5b50\u600e\u6562\u548c\u505a\u5b98\u7684\u9876\u649e\uff1f\u8baa\u8baa\u7684\u62fe\u8d77\u5355\u5200\uff0c\u5f85\u4e09\u4eba\u8d70\u8fdc\uff0c\u53c8\u5439\u4e86\u8d77\u6765\u3002 \u3000\u3000\u6c6a\u94c1\u9e57\u4e00\u9762\u8d70\uff0c\u4e00\u9762\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u80e1\u5927\u54e5\uff0c\u54b1\u4eec\u8fd9\u53eb\u505a\u4e0d\u6253\u4e0d\u6210\u76f8\u8bc6\uff0c\u4f60\u8001\u54e5\u7684\u6b66\u827a\uff0c\u5728\u4e0b\u5b9e\u5728\u662f\u4f69\u670d\u5f97\u7d27\u3002\u8d76\u660e\u513f\u6211\u7ed9\u4f60\u53bb\u8ddf\u798f\u5927\u5e05\u8bf4\u8bf4\uff0c\u4ed6\u8001\u4eba\u5bb6\u4e00\u89c1\u4e86\u4f60\u8fd9\u7b49\u4eba\u624d\uff0c\u5fc5\u5b9a\u6b22\u559c\u91cd\u7528\uff0c\u90a3\u65f6\u5019\u554a\uff0c\u5144\u5f1f\u8fd8\u5f97\u4ef0\u4ed7\u4f60\u7167\u987e\u5462\u2026\u2026\u201d\u8bf4\u5230\u8fd9\u91cc\uff0c\u5ffd\u7136\u653e\u4f4e\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u90a3\u4f4d\u9a6c\u59d1\u5a18\u554a\uff0c\u6211\u4eec\u63a5\u4e86\u5979\u6bcd\u5b50\u4e09\u4eba\u8fdb\u4eac\u4e4b\u540e\uff0c\u73b0\u4e0b\u4f4f\u5728\u798f\u5927\u5e05\u5e9c\u4e2d\uff0c\u5f53\u771f\u662f\u4eab\u4e0d\u5c3d\u7684\u8363\u534e\u5bcc\u8d35\u3002\u798f\u5927\u5e05\u4ec0\u4e48\u90fd\u6709\u4e86\uff0c\u5c31\u662f\u6ca1\u6709\u513f\u5b50\uff0c\u8fd9\u4e00\u4e0b\uff0c\u90a3\u9a6c\u59d1\u5a18\u8bf4\u4e0d\u5b9a\u4fbf\u6276\u6b63\u505a\u4e86\u5927\u5e05\u592b\u4eba\uff0c\u54c8\u54c8\uff0c\u54c8\u54c8\uff01\u4f60\u8001\u54e5\u65e9\u77e5\u4eca\u65e5\uff0c\u8ddf\u6211\u4eec\u90a3\u4e00\u573a\u67b6\u4e5f\u4e0d\u4f1a\u6253\u7684\u4e86\u5427\uff1f\u201d\u4ed6\u8d8a\u8bf4\u8d8a\u54cd\uff0c\u5728\u5927\u8857\u4e0a\u65c1\u82e5\u65e0\u4eba\u7684\u54c8\u54c8\u5927\u7b11\u3002 \u3000\u3000\u80e1\u6590\u542c\u7740\u5fc3\u4e2d\u5374\u6ee1\u4e0d\u662f\u5473\u513f\uff0c\u6697\u60f3\u9a6c\u6625\u82b1\u5728\u5a5a\u524d\u548c\u798f\u5eb7\u5b89\u65e9\u6709\u79c1\u60c5\uff0c\u90a3\u4e24\u4e2a\u5b69\u5b50\u4e5f\u786e\u662f\u798f\u5eb7\u5b89\u7684\u4eb2\u9aa8\u8089\uff0c\u773c\u4e0b\u5979\u4e08\u592b\u5df2\u6545\uff0c\u518d\u53bb\u548c\u798f\u5eb7\u5b89\u76f8\u805a\uff0c\u4e5f\u6ca1\u4ec0\u4e48\u4e0d\u5bf9\uff0c\u4f46\u4e00\u60f3\u5230\u5f90\u94ee\u5728\u6811\u6797\u4e2d\u60e8\u6b7b\u7684\u60c5\u72b6\uff0c\u603b\u662f\u4e0d\u514d\u9eef\u7136\u3002 \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u4e09\u4eba\u6765\u5230\u4e00\u5ea7\u5927\u9152\u697c\u524d\u3002\u9152\u697c\u4e0a\u60ac\u7740\u4e00\u5757\u91d1\u5b57\u62db\u724c\uff0c\u5199\u7740\u201c\u805a\u82f1\u697c\u201d\u4e09\u4e2a\u5927\u5b57\u3002 \u3000\u3000\u9152\u4fdd\u4e00\u89c1\u6c6a\u94c1\u9e57\uff0c\u5fd9\u542b\u7b11\u4e0a\u6765\u62db\u547c\uff0c\u8bf4\u9053\uff1a\u201c\u6c6a\u5927\u4eba\uff0c\u4eca\u513f\u6765\u5f97\u65e9\uff0c\u5148\u5728\u96c5\u5ea7\u559d\u51e0\u676f\u5427\uff1f\u201d\u6c6a\u94c1\u9e57\u9053\uff1a\u201c\u597d\uff01\u4eca\u513f\u6211\u8bf7\u4e24\u4f4d\u4f53\u9762\u670b\u53cb\uff0c\u9152\u83dc\u53ef\u5f97\u7279\u522b\u4e30\u76db\u3002\u201d\u9152\u4fdd\u7b11\u9053\uff1a\u201c\u90a3\u8fd8\u7528\u5429\u5490\uff1f\u201d\u5f15\u7740\u4e09\u4eba\u5728\u96c5\u5ea7\u4e2d\u5b89\u4e86\u4e2a\u5ea7\u513f\uff0c\u659f\u9152\u9001\u83dc\uff0c\u5341\u5206\u6bb7\u52e4\uff0c\u663e\u7136\u6c6a\u94c1\u9e57\u662f\u8fd9\u91cc\u5e38\u5ba2\u3002 \u3000\u3000\u80e1\u6590\u77a7\u9152\u697c\u4e2d\u7684\u5ba2\u4eba\uff0c\u5341\u4e4b\u516d\u4e03\u90fd\u662f\u7a7f\u6b66\u5b98\u670d\u8272\uff0c\u4fbf\u4e0d\u662f\u519b\u5b98\u6253\u626e\uff0c\u4e5f\u5927\u90fd\u662f\u96c4\u8d73\u8d73\u7684\u6b66\u6797\u8c6a\u5ba2\u6a21\u6837\uff0c\u770b\u6765\u8fd9\u9152\u697c\u662f\u4ee5\u505a\u6b66\u4eba\u751f\u610f\u4e3a\u5927\u5b97\u7684\u4e86\u3002 \u3000\u3000\u4eac\u5e08\u70f9\u8c03\uff0c\u679c\u7136\u5927\u80dc\u522b\u5904\uff0c\u6b64\u65f6\u6b63\u5f53\u708e\u6691\uff0c\u9152\u4fdd\u9001\u4e0a\u6765\u7684\u9152\u83dc\u7cbe\u7f8e\u53ef\u53e3\uff0c\u5374\u4e0d\u80a5\u817b\u3002\u80e1\u6590\u8fde\u58f0\u79f0\u597d\u3002\u6c5f\u94c1\u9e57\u8981\u6323\u9762\u5b50\uff0c\u7adf\u662f\u53eb\u4e86\u6ee1\u684c\u7684\u83dc\u80b4\u3002 \u3000\u3000\u4e24\u4eba\u5bf9\u996e\u4e86\u5341\u51e0\u676f\uff0c\u5ffd\u542c\u5f97\u9694\u623f\u62e5\u8fdb\u4e00\u6279\u4eba\u6765\uff0c\u8fc7\u4e0d\u591a\u65f6\uff0c\u4fbf\u547c\u5362\u559d\u96c9\uff0c\u5927\u8d4c\u8d77\u6765\u3002\u4e00\u4eba\u5927\u58f0\u559d\u9053\uff1a\u201c\u4e5d\u70b9\u5929\u6760\uff01\u901a\u5403\uff01\u201d\u80e1\u6590\u542c\u90a3\u53e3\u97f3\u751a\u719f\uff0c\u5fae\u5fae\u4e00\u6014\uff0c\u6c6a\u94c1\u9e57\u7b11\u9053\uff1a\u201c\u662f\u719f\u670b\u53cb\uff01\u201d\u5927\u58f0\u9053\uff1a\u201c\u79e6\u5927\u54e5\uff0c\u4f60\u731c\u662f\u8c01\u6765\u4e86\uff1f\u201d\u80e1\u6590\u7acb\u65f6\u60f3\u8d77\uff0c\u90a3\u4eba\u6b63\u662f\u516b\u6781\u62f3\u7684\u638c\u95e8\u4eba\u79e6\u8010\u4e4b\uff0c\u53ea\u542c\u4ed6\u9694\u7740\u677f\u58c1\u53eb\u9053\uff1a\u201c\u8c01\u77e5\u4f60\u5e26\u7684\u662f\u4ec0\u4e48\u732a\u670b\u72d7\u53cb\uff1f\u4e00\u5757\u513f\u6eda\u8fc7\u6765\u8d4c\u51e0\u624b\u5427\uff1f\u201d\u6c6a\u94c1\u9e57\u7b11\u9053\uff1a\u201c\u4f60\u9a82\u6211\u4e0d\u6253\u7d27\uff0c\u5f97\u7f6a\u4e86\u597d\u670b\u53cb\uff0c\u53ef\u53eb\u4f60\u5403\u4e0d\u4f4f\u515c\u7740\u8d70\u5462\uff01\u201d \u3000\u3000\u7ad9\u8d77\u8eab\u6765\uff0c\u62c9\u7740\u80e1\u6590\u7684\u624b\u8bf4\u9053\uff1a\u201c\u80e1\u5927\u54e5\uff0c\u54b1\u4eec\u8fc7\u53bb\u77a7\u77a7\u3002\u201d \u3000\u3000\u4e24\u4eba\u8d70\u5230\u9694\u623f\uff0c\u4e00\u6380\u95e8\u5e18\uff0c\u53ea\u542c\u79e6\u8010\u4e4b\u5406\u559d\u9053\uff1a\u201c\u4e09\u70b9\uff0c\u6885\u82b1\u4e00\u5bf9\uff0c\u5403\u5929\uff0c\u8d54\u4e0a\u95e8\uff01\u201d\u4ed6\u4e00\u62ac\u5934\uff0c\u731b\u7136\u89c1\u5230\u80e1\u6590\uff0c\u5446\u4e86\u4e00\u5446\uff0c\u559c\u9053\uff1a\u201c\u554a\uff0c\u662f\u4f60\uff0c\u60f3\u4e0d\u5230\uff0c\u60f3\u4e0d\u5230\uff01\u201d\u5c06\u724c\u4e00\u63a8\uff0c\u7ad9\u8d77\u8eab\u6765\uff0c\u4f38\u624b\u5728\u81ea\u5df1\u989d\u89d2\u4e0a\u6253\u4e86\u51e0\u4e2a\u7206\u6817\uff0c\u7b11\u9053\uff1a\u201c\u8be5\u6b7b\uff0c\u8be5\u6b7b\uff01\u6211\u80e1\u8bf4\u516b\u9053\uff0c\u600e\u77e5\u662f\u80e1\u5927\u54e5\u9a7e\u5230\uff0c\u6765\u6765\u6765\uff0c\u4f60\u6765\u63a8\u5e84\u3002\u201d \u3000\u3000\u80e1\u6590\u773c\u5149\u4e00\u626b\uff0c\u53ea\u89c1\u623f\u4e2d\u805a\u7740\u5341\u6765\u4e2a\u6b66\u5b98\uff0c\u56f4\u4e86\u4e00\u684c\u5728\u8d4c\u724c\u4e5d\uff0c\u79e6\u8010\u4e4b\u6b63\u5728\u505a\u5e84\u3002\u8fd9\u5341\u6765\u4e2a\u4eba\uff0c\u5012\u6709\u4e00\u5927\u534a\u662f\u626e\u8fc7\u62e6\u52ab\u98de\u9a6c\u9556\u5c40\u7684\u5927\u76d7\u800c\u548c\u81ea\u5df1\u4ea4\u8fc7\u624b\u7684\uff0c\u4f7f\u96f7\u9707\u6321\u59d3\u891a\u7684\uff0c\u4f7f\u95ea\u7535\u9525\u59d3\u4e0a\u5b98\u7684\uff0c\u4f7f\u5251\u59d3\u8042\u7684\uff0c\u90fd\u5728\u5176\u5185\u3002 \u3000\u3000\u4f17\u4eba\u89c1\u4ed6\u7a81\u7136\u5230\u6765\uff0c\u5608\u6210\u4e00\u7247\u7684\u623f\u4e2d\u5239\u65f6\u95f4\u5bc2\u9759\u65e0\u58f0\u3002 \u3000\u3000\u80e1\u6590\u62b1\u62f3\u4f5c\u4e2a\u56db\u65b9\u63d6\uff0c\u7b11\u9053\uff1a\u201c\u591a\u8c22\u5404\u4f4d\u76f8\u8d60\u5750\u9a91\u3002\u201d\u4f17\u4eba\u8c26\u900a\u51e0\u53e5\u3002\u90a3\u59d3\u8042\u7684\u4fbf\u9053\uff1a\u201c\u80e1\u5927\u54e5\uff0c\u4f60\u6765\u63a8\u5e84\uff0c\u4f60\u6709\u6ca1\u5e26\u94f6\u5b50\u6765\uff1f\u5c0f\u5f1f\u4eca\u513f\u624b\u6c14\u597d\uff0c\u4f60\u5148\u4f7f\u7740\u3002\u201d\u8bf4\u7740\u5c06\u4e09\u5c01\u94f6\u5b50\u63a8\u5230\u4ed6\u9762\u524d\u3002 \u3000\u3000\u80e1\u6590\u751f\u6027\u6781\u7231\u7ed3\u4ea4\u670b\u53cb\uff0c\u5bf9\u505a\u5b98\u7684\u867d\u65e0\u597d\u611f\uff0c\u4f46\u89c1\u8fd9\u4e00\u5e72\u4eba\u5bf9\u81ea\u5df1\u6781\u662f\u5c0a\u91cd\uff0c\u800c\u4ed6\u672c\u6765\u53c8\u559c\u6b22\u8d4c\u94b1\uff0c\u7b11\u9053\uff1a\u201c\u8fd8\u662f\u79e6\u5927\u54e5\u63a8\u5e84\uff0c\u5c0f\u5f1f\u6765\u4e0b\u6ce8\u78b0\u78b0\u8fd0\u6c14\u3002\u8042\u5927\u54e5\uff0c\u4f60\u5148\u6536\u7740\uff0c\u5f85\u4f1a\u8f93\u5e72\u4e86\u518d\u95ee\u4f60\u501f\u3002\u201d\u8f6c\u5934\u95ee\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u4e8c\u59b9\uff0c\u4f60\u8d4c\u4e0d\u8d4c\uff1f\u201d\u7a0b\u7075\u7d20\u62bf\u5634\u7b11\u9053\uff1a\u201c\u6211\u4e0d\u8d4c\uff0c\u6211\u5e2e\u4f60\u6367\u94f6\u5b50\u56de\u5bb6\u3002\u201d \u3000\u3000\u79e6\u8010\u4e4b\u5750\u56de\u5e84\u5bb6\uff0c\u6d17\u724c\u63b7\u9ab0\u3002\u80e1\u6590\u548c\u6c6a\u94c1\u9e57\u4fbf\u8ddf\u7740\u4e0b\u6ce8\u3002 \u3000\u3000\u4f17\u6b66\u5b98\u521d\u65f6\u89c1\u5230\u80e1\u6590\uff0c\u5747\u4e0d\u514d\u9887\u4e3a\u5c34\u5c2c\uff0c\u4f46\u51e0\u526f\u724c\u4e5d\u4e00\u63a8\uff0c\u89c1\u4ed6\u8c08\u7b11\u98ce\u751f\uff0c\u7edd\u53e3\u4e0d\u63d0\u65e7\u4e8b\uff0c\u5927\u4f19\u4e5f\u4fbf\u5404\u81ea\u51dd\u795e\u8d4c\u535a\uff0c\u4e0d\u518d\u4ecb\u610f\u3002 \u3000\u3000\u80e1\u6590\u6709\u8f93\u6709\u8d62\uff0c\u8fdb\u51fa\u4e0d\u5927\uff0c\u5fc3\u4e0b\u76d8\u7b97\uff1a\u201c\u4eca\u65e5\u662f\u516b\u6708\u521d\u4e5d\uff0c\u518d\u8fc7\u516d\u5929\u5c31\u662f\u4e2d\u79cb\uff0c\u90a3\u5929\u4e0b\u638c\u95e8\u4eba\u5927\u4f1a\u662f\u798f\u5927\u5e05\u6240\u53ec\uff0c\u5b9a\u4e8e\u4e2d\u79cb\u8282\u5927\u5bb4\u3002\u51e4\u5929\u5357\u8fd9\u5978\u8d3c\u8eab\u4e3a\u4e94\u864e\u95e8\u638c\u95e8\u4eba\uff0c\u4ed6\u4fbf\u662f\u4e0d\u6765\uff0c\u5728\u4f1a\u4e2d\u603b\u4e5f\u53ef\u63a2\u542c\u5230\u4e9b\u8fd9\u5978\u8d3c\u7684\u8baf\u606f\u7aef\u502a\u3002\u773c\u524d\u8fd9\u73ed\u4eba\u90fd\u662f\u798f\u5927\u5e05\u7684\u5f97\u529b\u4e0b\u5c5e\uff0c\u4e0d\u59a8\u8ddf\u4ed6\u4eec\u7ed3\u7eb3\u7ed3\u7eb3\u3002\u6211\u4e0d\u662f\u4ec0\u4e48\u638c\u95e8\u4eba\uff0c\u4f46\u53ea\u8981\u4ed6\u4eec\u5e26\u643a\uff0c\u5728\u4f1a\u4e0a\u966a\u90a3\u4e9b\u638c\u95e8\u4eba\u559d\u4e00\u676f\u603b\u662f\u884c\u7684\u3002\u201d \u3000\u3000\u5f53\u4e0b\u4e0d\u8ba1\u8f93\u8d62\uff0c\u968f\u610f\u4e0b\u6ce8\uff0c\u724c\u98ce\u7adf\u662f\u751a\u987a\uff0c\u6ca1\u591a\u4e45\u5df2\u8d62\u4e86\u4e09\u56db\u767e\u4e24\u94f6\u5b50\u3002 \u3000\u3000\u8d4c\u4e86\u4e00\u4e2a\u591a\u65f6\u8fb0\uff0c\u5929\u8272\u5df2\u665a\uff0c\u5404\u4eba\u4e0b\u6ce8\u4e5f\u6e10\u6e10\u5927\u4e86\u8d77\u6765\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u9774\u58f0\u6a50\u6a50\uff0c\u95e8\u5e18\u6380\u5f00\uff0c\u8d70\u8fdb\u4e09\u4e2a\u4eba\u6765\u3002\u6c6a\u94c1\u9e57\u4e00\u89c1\uff0c\u7acb\u65f6\u7ad9\u76f4\u8eab\u5b50\uff0c\u606d\u606d\u656c\u656c\u7684\u53eb\u9053\uff1a\u201c\u5927\u5e08\u54e5\uff0c\u4e8c\u5e08\u54e5\uff0c\u4f60\u4e24\u4f4d\u90fd\u6765\u5566\u3002\u201d\u56f4\u5728\u684c\u524d\u8d4c\u535a\u7684\u4eba\u4e5f\u90fd\u7eb7\u7eb7\u62db\u547c\uff0c\u6709\u7684\u53eb\u201c\u5468\u5927\u7237\uff0c\u66fe\u4e8c\u7237\u201d\uff0c\u6709\u7684\u53eb\u201c\u5468\u5927\u4eba\uff0c\u66fe\u5927\u4eba\u201d\uff0c\u795e\u8272\u4e4b\u95f4\u90fd\u9887\u4e3a\u606d\u8c28\u3002 \u3000\u3000\u80e1\u6590\u548c\u7a0b\u7075\u7d20\u4e00\u542c\uff0c\u5fc3\u9053\uff1a\u201c\u539f\u6765\u662f\u9e70\u722a\u96c1\u884c\u95e8\u7684\u5468\u94c1\u9e6a\u3001\u66fe\u94c1\u9e25\u5230\u4e86\uff0c\u8fd9\u4e24\u4eba\u5a01\u98ce\u4e0d\u5c0f\u554a\u3002\u201d\u6253\u91cf\u4e8c\u4eba\u65f6\uff0c\u89c1\u90a3\u5468\u94c1\u9e6a\u77ed\u5c0f\u7cbe\u608d\uff0c\u8eab\u957f\u4e0d\u8fc7\u4e94\u5c3a\uff0c\u4e94\u5341\u6765\u5c81\u5e74\u7eaa\uff0c\u5374\u5df2\u6ee1\u5934\u767d\u53d1\u3002\u66fe\u94c1\u9e25\u5e74\u8fd1\u4e94\u5341\uff0c\u8eab\u5b50\u9ad8\u7626\uff0c\u624b\u4e2d\u62ff\u7740\u4e00\u4e2a\u9f3b\u70df\u58f6\uff0c\u9a6c\u8902\u4e0a\u60ac\u7740\u4e00\u6761\u91d1\u94fe\uff0c\u9887\u6709\u4e9b\u65d7\u4eba\u8d35\u65cf\u7684\u6c14\u6d3e\u3002\u80e1\u6590\u4e00\u770b\u90a3\u7b2c\u4e09\u4e2a\u4eba\uff0c\u5fc3\u4e2d\u5fae\u5fae\u4e00\u6014\uff0c\u539f\u6765\u662f\u5f53\u5e74\u5728\u5546\u5bb6\u5821\u4e2d\u4f1a\u8fc7\u9762\u7684\u5929\u9f99\u95e8\u6bb7\u4ef2\u7fd4\uff0c\u53ea\u89c1\u4ed6\u4e24\u9b13\u6591\u767d\uff0c\u5df2\u8001\u4e86\u4e0d\u5c11\u3002\u6bb7\u4ef2\u7fd4\u7684\u773c\u5149\u5728\u80e1\u6590\u8138\u4e0a\u63a0\u8fc7\uff0c\u89c1\u4ed6\u53ea\u662f\u4e2a\u4e61\u4e0b\u4eba\uff0c\u6beb\u6ca1\u5728\u610f\u3002\u8981\u77e5\u5f53\u5e74\u4e24\u4eba\u76f8\u89c1\u4e4b\u65f6\uff0c\u80e1\u6590\u53ea\u662f\u4e2a\u5341\u4e09\u56db\u5c81\u7684\u5b69\u5b50\uff0c\u8fd9\u65f6\u8eab\u91cf\u4e00\u9ad8\uff0c\u8138\u5bb9\u4e5f\u53d8\u4e86\uff0c\u54ea\u91cc\u8fd8\u8ba4\u5f97\u51fa\u6765\uff1f \u3000\u3000\u79e6\u8010\u4e4b\u7ad9\u8d77\u8eab\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u5468\u5927\u54e5\uff0c\u66fe\u4e8c\u54e5\uff0c\u6211\u7ed9\u4f60\u5f15\u89c1\u4e00\u4f4d\u670b\u53cb\uff0c\u8fd9\u4f4d\u662f\u80e1\u5927\u54e5\uff0c\u633a\u4fca\u7684\u8eab\u624b\u3002\u4e3a\u4eba\u53c8\u6781\u591f\u670b\u53cb\uff0c\u4eca\u513f\u521a\u4e0a\u5317\u4eac\u6765\u3002\u4f60\u4eec\u4e09\u4f4d\u591a\u4eb2\u8fd1\u4eb2\u8fd1\u3002\u201d\u5468\u94c1\u9e6a\u5411\u80e1\u6590\u70b9\u4e86\u70b9\u5934\uff0c\u66fe\u94c1\u9e25\u7b11\u4e86\u7b11\uff0c\u8bf4\u58f0\uff1a\u201c\u4e45\u4ef0\uff01\u201d\u4e24\u4eba\u6b66\u529f\u5353\u7edd\uff0c\u5728\u4eac\u5e08\u4eab\u76db\u540d\u5df2\u4e45\uff0c\u81ea\u4e0d\u5c06\u8fd9\u6837\u4e00\u4e2a\u4e61\u4e0b\u5c11\u5e74\u77a7\u5728\u773c\u91cc\u3002 \u3000\u3000\u6c6a\u94c1\u9e57\u77a7\u7740\u7a0b\u7075\u7d20\uff0c\u5fc3\u4e2d\u5927\u662f\u5947\u602a\uff1a\u201c\u4f60\u8bf4\u8ddf\u6211\u5927\u5e08\u54e5\u3001\u4e8c\u5e08\u54e5\u76f8\u8bc6\uff0c\u600e\u5730\u4e0d\u62db\u547c\u554a\uff1f\u201d\u4ed6\u90a3\u60f3\u5230\u7a0b\u7075\u7d20\u5f53\u65e5\u4e43\u662f\u4fe1\u53e3\u80e1\u5439\u3002\u7a0b\u7075\u7d20\u731c\u5230\u4ed6\u7684\u5fc3\u601d\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u70b9\u4e86\u70b9\u5934\uff0c\u7728\u7728\u773c\u775b\u3002\u6c6a\u94c1\u9e57\u53ea\u9053\u5176\u4e2d\u5fc5\u6709\u7f18\u6545\uff0c\u5f53\u4e0b\u4e5f\u4e0d\u6562\u591a\u95ee\u3002 \u3000\u3000\u79e6\u8010\u4e4b\u53c8\u63a8\u4e86\u4e24\u526f\u5e84\uff0c\u4fbf\u5c06\u5e84\u8ba9\u7ed9\u4e86\u5468\u94c1\u9e6a\u3002\u8fd9\u65f6\u66fe\u94c1\u9e25\u3001\u6bb7\u4ef2\u7fd4\u7b49\u4e00\u4e0b\u573a\uff0c\u843d\u6ce8\u66f4\u52a0\u5927\u4e86\u3002\u80e1\u6590\u624b\u6c14\u6781\u65fa\uff0c\u8fde\u843d\u8fde\u4e2d\uff0c\u4e0d\u5230\u534a\u4e2a\u65f6\u8fb0\uff0c\u5df2\u8d62\u4e86\u8fd1\u5343\u4e24\u94f6\u5b50\u3002\u5468\u94c1\u9e6a\u8fd9\u4e2a\u5e84\u5374\u662f\u6781\u9709\uff0c\u5c06\u5e26\u6765\u7684\u94f6\u5b50\u548c\u5e84\u7968\u8f93\u4e86\u5341\u4e4b\u4e03\u516b\uff0c\u8fd9\u65f6\u4e00\u628a\u9ab0\u5b50\u63b7\u4e0b\u6765\uff0c\u62ff\u5230\u56db\u5f20\u724c\u7adf\u662f\u4e8c\u4e09\u5173\uff0c\u8d54\u4e86\u4e00\u526f\u901a\u5e84\uff0c\u5c06\u724c\u4e00\u63a8\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u6210\uff0c\u4e8c\u5f1f\uff0c\u4f60\u6765\u63a8\u3002\u201d \u3000\u3000\u66fe\u94c1\u9e25\u7684\u5e84\u8f93\u8f93\u8d62\u8d62\uff0c\u4e0d\u65fa\u4e5f\u4e0d\u9709\uff0c\u80e1\u6590\u5374\u53c8\u591a\u8d62\u4e86\u4e03\u516b\u767e\u4e24\uff0c\u53ea\u89c1\u4ed6\u9762\u524d\u5806\u4e86\u597d\u5927\u4e00\u5806\u94f6\u5b50\u3002\u66fe\u94c1\u9e25\u7b11\u9053\uff1a\u201c\u4e61\u4e0b\u8001\u5f1f\uff0c\u8d4c\u795e\u83e9\u8428\u8ddf\u4f60\u63a5\u98ce\uff0c\u4f60\u6765\u505a\u5e84\u3002\u201d \u3000\u3000\u80e1\u6590\u9053\uff1a\u201c\u597d\uff01\u201d\u6d17\u4e86\u6d17\u724c\uff0c\u63b7\u8fc7\u9ab0\u5b50\uff0c\u62ff\u8d77\u724c\u6765\u4e00\u914d\uff0c\u5934\u9053\u516b\u70b9\uff0c\u4e8c\u9053\u4e00\u5bf9\u677f\u51f3\uff0c\u7adf\u5403\u4e86\u4e24\u5bb6\u3002 \u3000\u3000\u5468\u94c1\u9e6a\u8f93\u5f97\u4e0d\u52a8\u58f0\u8272\uff0c\u66fe\u94c1\u9e25\u66f4\u662f\u6f47\u6d12\u81ea\u82e5\uff0c\u62bd\u7a7a\u4fbf\u8bf4\u51e0\u53e5\u4fcf\u76ae\u8bdd\u3002\u6bb7\u4ef2\u7fd4\u53d1\u8d77\u6bdb\u6765\uff0c\u4e0d\u4f4f\u7684\u5583\u5583\u5492\u9a82\uff0c\u540e\u6765\u8f93\u5f97\u6025\u4e86\uff0c\u5c06\u5269\u4e0b\u7684\u4e8c\u767e\u6765\u4e24\u94f6\u5b50\u5b64\u6ce8\u4e00\u63b7\uff0c\u62bc\u5728\u4e0b\u95e8\uff0c\u4e00\u5f00\u724c\u51fa\u6765\uff0c\u4e09\u70b9\u5403\u4e09\u70b9\uff0c\u4e5d\u70b9\u5403\u4e5d\u70b9\uff0c\u7adf\u53c8\u8f93\u4e86\u3002\u6bb7\u4ef2\u7fd4\u8138\u8272\u94c1\u9752\uff0c\u4f38\u638c\u5728\u684c\u4e0a\u4e00\u62cd\uff0c\u7830\u7684\u4e00\u58f0\uff0c\u6ee1\u684c\u7684\u9aa8\u724c\u3001\u94f6\u4e24\u3001\u9ab0\u5b50\u90fd\u8df3\u4e86\u8d77\u6765\uff0c\u7834\u53e3\u9a82\u9053\uff1a\u201c\u8fd9\u4e61\u4e0b\u5c0f\u5b50\u9ab0\u5b50\u91cc\u6709\u9b3c\uff0c\u54ea\u91cc\u4fbf\u6709\u8fd9\u7b49\u5de7\u6cd5\uff0c\u4e09\u70b9\u5403\u4e09\u70b9\uff0c\u4e5d\u70b9\u5403\u4e5d\u70b9\uff1f\u4fbf\u662f\u724c\u65fa\uff0c\u4e5f\u4e0d\u80fd\u65fa\u5f97\u8fd9\u6837\uff01\u201d \u3000\u3000\u79e6\u8010\u4e4b\u5fd9\u9053\uff1a\u201c\u6bb7\u5927\u54e5\uff0c\u4f60\u53ef\u522b\u80e1\u8a00\u4e71\u8bed\uff0c\u8fd9\u4f4d\u80e1\u5927\u54e5\u662f\u597d\u670b\u53cb\uff01\u201d \u3000\u3000\u4f17\u4eba\u671b\u671b\u6bb7\u4ef2\u7fd4\uff0c\u671b\u671b\u80e1\u6590\uff0c\u89c1\u8fc7\u80e1\u6590\u8eab\u624b\u4e4b\u4eba\u5fc3\u4e2d\u90fd\u60f3\uff1a\u6bb7\u4ef2\u7fd4\u8bf4\u4ed6\u8d4c\u724c\u6b3a\u8bc8\uff0c\u4ed6\u51b3\u8ba1\u4e0d\u80af\u5e72\u4f11\uff0c\u8fd9\u573a\u67b6\u4e00\u6253\uff0c\u6bb7\u4ef2\u7fd4\u51c6\u8981\u5012\u5927\u9709\u3002 \u3000\u3000\u4e0d\u6599\u80e1\u6590\u53ea\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u8d4c\u94b1\u603b\u6709\u8f93\u8d62\uff0c\u6bb7\u5927\u54e5\u63a8\u5e84\u7f62\u3002\u201d \u3000\u3000\u6bb7\u4ef2\u7fd4\u970d\u5730\u7ad9\u8d77\uff0c\u4ece\u8170\u95f4\u89e3\u4e0b\u4f69\u5251\uff0c\u4f17\u4eba\u53ea\u9053\u4ed6\u8981\u52a8\u624b\uff0c\u5374\u4e0d\u529d\u963b\u3002 \u3000\u3000\u8981\u77e5\u6b66\u5b98\u4eec\u8d4c\u94b1\u6253\u67b6\uff0c\u5b9e\u662f\u7a00\u677e\u5e73\u5e38\u3002\u90a3\u77e5\u6bb7\u4ef2\u7fd4\u5c06\u4f69\u5251\u5f80\u684c\u4e0a\u4e00\u653e\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u8fd9\u53e3\u5251\u5c11\u8bf4\u4e5f\u503c\u4e03\u516b\u767e\u4e24\u94f6\u5b50\uff0c\u4fbf\u8ddf\u4f60\u8d4c\u4e94\u767e\u4e24\uff01\u201d\u90a3\u4f69\u5251\u7684\u5251\u9798\u91d1\u9576\u7389\u5d4c\uff0c\u751a\u662f\u534e\u4e3d\uff0c\u5355\u662f\u77a7\u8fd9\u5251\u9798\uff0c\u4fbf\u5df2\u4ef7\u503c\u4e0d\u83f2\u3002 \u3000\u3000\u80e1\u6590\u7b11\u9053\uff1a\u201c\u597d\uff01\u8be5\u8d4c\u516b\u767e\u4e24\u624d\u516c\u5e73\u3002\u201d\u6bb7\u4ef2\u7fd4\u62ff\u8fc7\u9aa8\u724c\u9ab0\u5b50\uff0c\u9053\uff1a\u201c\u6211\u53ea\u8ddf\u4f60\u8fd9\u4e61\u4e0b\u5c0f\u5b50\u8d4c\uff0c\u4e0d\u53d7\u65c1\u4eba\u843d\u6ce8\uff0c\u54b1\u4eec\u4e00\u526f\u724c\u51b3\u8f93\u8d62\uff01\u201d\u80e1\u6590\u4ece\u8eab\u524d\u7684\u94f6\u5b50\u5806\u4e2d\u53d6\u8fc7\u516b\u767e\u4e24\uff0c\u63a8\u4e86\u51fa\u53bb\uff0c\u9053\uff1a\u201c\u4f60\u63b7\u9ab0\u5427\uff01\u201d \u3000\u3000\u6bb7\u4ef2\u7fd4\u53cc\u638c\u5408\u4f4f\u4e24\u7c92\u9ab0\u5b50\uff0c\u6447\u4e86\u51e0\u6447\uff0c\u5439\u4e00\u53e3\u6c14\uff0c\u63b7\u4e86\u51fa\u6765\uff0c\u4e00\u7c92\u4e94\uff0c\u4e00\u7c92\u56db\uff0c\u5171\u662f\u4e5d\u70b9\u3002\u4ed6\u62ff\u8d77\u7b2c\u4e00\u624b\u7684\u56db\u5f20\u724c\uff0c\u4e00\u770b\u4e4b\u4e0b\uff0c\u8138\u6709\u559c\u8272\uff0c\u559d\u9053\uff1a\u201c\u4e61\u4e0b\u5c0f\u5b50\uff0c\u8fd9\u4e00\u6b21\u4f60\u5f04\u4e0d\u4e86\u9b3c\u5427\uff01\u201d\u5de6\u624b\u4e00\u7ffb\uff0c\u662f\u526f\u4e5d\u70b9\uff0c\u53f3\u624b\u7830\u7684\u4e00\u7ffb\uff0c\u7adf\u662f\u4e00\u5bf9\u5929\u724c\u3002 \u3000\u3000\u80e1\u6590\u5374\u4e0d\u7ffb\u724c\uff0c\u7528\u624b\u6307\u6478\u4e86\u6478\u724c\u5e95\uff0c\u914d\u597d\u4e86\u524d\u540e\u9053\uff0c\u5408\u6251\u7740\u6392\u5728\u684c\u4e0a\u3002\u6bb7\u4ef2\u7fd4\u559d\u9053\uff1a\u201c\u4e61\u4e0b\u5c0f\u5b50\uff0c\u7ffb\u724c\uff01\u201d\u4ed6\u53ea\u9053\u5df2\u7ecf\u8d62\u5b9a\uff0c\u4e00\u4f38\u81c2\u4fbf\u5c06\u516b\u767e\u94f6\u5b50\u63b3\u5230\u4e86\u8eab\u524d\u3002\u6c6a\u94c1\u9e57\u53eb\u9053\uff1a\u201c\u522b\u6027\u6025\uff0c\u77a7\u8fc7\u724c\u518d\u8bf4\u3002\u201d\u80e1\u6590\u4f38\u51fa\u4e09\u6839\u624b\u6307\uff0c\u5728\u81ea\u5df1\u524d\u4e24\u5f20\u724c\u4e0a\u8f7b\u8f7b\u4e00\u62cd\uff0c\u53c8\u5728\u540e\u4e24\u5f20\u724c\u4e0a\u4e00\u62cd\uff0c\u624b\u638c\u4e00\u626b\uff0c\u4fbf\u5c06\u56db\u5f20\u5408\u7740\u7684\u724c\u63a8\u5165\u4e86\u4e71\u724c\u4e4b\u4e2d\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u8d62\u5566\uff01\u201d\u6bb7\u4ef2\u7fd4\u5927\u662f\u5f97\u610f\uff0c\u6b63\u8981\u5938\u53e3\uff0c\u7a81\u7136\u201c\u54a6\u201d\u7684\u4e00\u58f0\u60ca\u53eb\uff0c\u671b\u7740\u684c\u5b50\uff0c\u767b\u65f6\u5446\u4f4f\u4e86\u3002 \u3000\u3000\u4f17\u4eba\u987a\u7740\u4ed6\u76ee\u5149\u77a7\u53bb\uff0c\u53ea\u89c1\u6731\u7ea2\u6f06\u7684\u684c\u9762\u4e4b\u4e0a\uff0c\u6e05\u6e05\u695a\u695a\u7684\u5370\u7740\u56db\u5f20\u724c\u7684\u9633\u7eb9\uff0c\u524d\u4e24\u5f20\u662f\u4e00\u5bf9\u957f\u4e09\uff0c\u540e\u4e24\u5f20\u4e00\u5f20\u4e09\u70b9\uff0c\u4e00\u5f20\u516d\u70b9\uff0c\u5408\u8d77\u6765\u7adf\u662f\u4e00\u5bf9\u201c\u81f3\u5c0a\u5b9d\u201d\uff0c\u56db\u5f20\u724c\u7eb9\u8def\u5206\u660e\uff0c\u96d5\u5728\u684c\u4e0a\u70b9\u5b50\u4e00\u7c92\u7c92\u7684\u51f8\u8d77\uff0c\u663e\u662f\u80e1\u6590\u4e09\u6839\u6307\u5934\u8fd9\u4e48\u4e00\u62cd\uff0c\u4fbf\u4ee5\u5185\u529b\u5728\u7ea2\u6728\u684c\u4e0a\u5370\u4e86\u4e0b\u6765\u3002\u805a\u8d4c\u4e4b\u4eba\u4e2a\u4e2a\u90fd\u662f\u4f1a\u5bb6\uff0c\u4e00\u89c1\u5982\u6b64\u5185\u529b\uff0c\u4e0d\u7ea6\u800c\u540c\u7684\u9f50\u58f0\u559d\u5f69\u3002 \u3000\u3000\u6bb7\u4ef2\u7fd4\u6ee1\u8138\u901a\u7ea2\uff0c\u8fde\u94f6\u5b50\u5e26\u5251\uff0c\u4e00\u9f50\u63a8\u5230\u80e1\u6590\u8eab\u524d\uff0c\u7ad9\u8d77\u8eab\u6765\uff0c\u8f6c\u5934\u4fbf\u8d70\u3002\u80e1\u6590\u62ff\u8d77\u4f69\u5251\uff0c\u8bf4\u9053\uff1a\u201c\u6bb7\u5927\u54e5\uff0c\u6211\u53c8\u4e0d\u4f1a\u4f7f\u5251\uff0c\u8981\u4f60\u7684\u5251\u4f55\u7528\uff1f\u201d\u53cc\u624b\u9012\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u6bb7\u4ef2\u7fd4\u5374\u4e0d\u63a5\u5251\uff0c\u8bf4\u9053\uff1a\u201c\u8bf7\u6559\u5c0a\u9a7e\u7684\u4e07\u513f\u3002\u201d\u80e1\u6590\u8fd8\u672a\u56de\u7b54\uff0c\u6c6a\u94c1\u9e57\u62a2\u7740\u9053\uff1a\u201c\u8fd9\u4f4d\u670b\u53cb\u59d3\u80e1\u540d\u6590\u3002\u201d\u6bb7\u4ef2\u7fd4\u5583\u5583\u7684\u9053\uff1a\u201c\u80e1\u6590\uff0c\u80e1\u6590\uff1f\u201d\u7a81\u7136\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u554a\uff0c\u5728\u5c71\u4e1c\u5546\u5bb6\u5821\u4e2d\u2026\u2026\u201d\u80e1\u6590\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5728\u4e0b\u66fe\u548c\u6bb7\u7237\u6709\u8fc7\u4e00\u9762\u4e4b\u7f18\uff0c\u6bb7\u7237\u5374\u4e0d\u8bb0\u5f97\u4e86\u3002\u201d\u6bb7\u4ef2\u7fd4\u8138\u5982\u6b7b\u7070\uff0c\u63a5\u8fc7\u4f69\u5251\u5f80\u684c\u4e0a\u4e00\u63b7\uff0c\u8bf4\u9053\uff1a\u201c\u602a\u4e0d\u5f97\uff0c\u602a\u4e0d\u5f97\uff01\u201d\u6380\u5f00\u95e8\u5e18\uff0c\u5927\u8e0f\u6b65\u8d70\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u4e00\u65f6\u623f\u4e2d\u4f17\u6b66\u5b98\u7eb7\u7eb7\u8bae\u8bba\uff0c\u79f0\u8d5e\u80e1\u6590\u7684\u5185\u529b\u4e86\u5f97\uff0c\u53c8\u8bf4\u6bb7\u4ef2\u7fd4\u8f93\u94b1\u8f93\u5f97\u5bd2\u8822\uff0c\u592a\u6ca1\u98ce\u5ea6\u3002 \u3000\u3000\u5468\u94c1\u9e6a\u7f13\u7f13\u7ad9\u8d77\u8eab\u6765\uff0c\u6307\u7740\u80e1\u6590\u8eab\u524d\u90a3\u4e00\u5927\u5806\u94f6\u5b50\u9053\uff1a\u201c\u80e1\u5144\u5f1f\uff0c\u4f60\u8fd9\u91cc\u4e00\u5171\u6709\u591a\u5c11\u94f6\u5b50\uff1f\u201d\u80e1\u6590\u9053\uff1a\u201c\u56db\u4e94\u5343\u4e24\u5427\uff01\u201d \u3000\u3000\u5468\u94c1\u9e6a\u6413\u7740\u9aa8\u724c\uff0c\u5728\u684c\u4e0a\u6162\u6162\u63a8\u52a8\uff0c\u6162\u6162\u780c\u6210\u56db\u6761\uff0c\u7136\u540e\u4ece\u6000\u4e2d\u6478\u51fa\u4e00\u4e2a\u5927\u5c01\u888b\u6765\uff0c\u653e\u5728\u8eab\u524d\uff0c\u9053\uff1a\u201c\u6765\uff0c\u6211\u8ddf\u4f60\u8d4c\u4e00\u526f\u724c\u3002\u82e5\u662f\u6211\u8d62\uff0c\u8d62\u4e86\u4f60\u8fd9\u56db\u4e94\u5343\u4e24\u94f6\u5b50\u548c\u4f69\u5251\u3002\u82e5\u662f\u4f60\u724c\u597d\uff0c\u628a\u8fd9\u4e2a\u62ff\u53bb\u3002\u201d \u3000\u3000\u4f17\u4eba\u89c1\u90a3\u5c01\u888b\u4e0a\u4ec0\u4e48\u5b57\u4e5f\u6ca1\u5199\uff0c\u4e0d\u77e5\u91cc\u9762\u653e\u7740\u4e9b\u4ec0\u4e48\uff0c\u90fd\u60f3\uff0c\u4ed6\u597d\u5bb9\u6613\u8d62\u4e86\u8fd9\u8bb8\u591a\u94f6\u5b50\uff0c\u600e\u80af\u4e00\u526f\u724c\u4fbf\u8f93\u7ed9\u4f60\uff1f\u53c8\u4e0d\u77e5\u4f60\u8fd9\u5c01\u888b\u91cc\u662f\u4ec0\u4e48\u4e1c\u897f\uff0c\u8981\u662f\u53ea\u6709\u4e00\u5f20\u767d\u7eb8\uff0c\u5c82\u4e0d\u662f\u505a\u4e86\u51a4\u5927\u5934\uff1f\u90a3\u77e5\u80e1\u6590\u60f3\u4e5f\u4e0d\u60f3\uff0c\u5c06\u9762\u524d\u5927\u5806\u94f6\u5b50\u5c3d\u6570\u63a8\u4e86\u51fa\u53bb\uff0c\u4e5f\u4e0d\u95ee\u4ed6\u5c01\u888b\u4e2d\u653e\u7740\u4ec0\u4e48\uff0c\u8bf4\u9053\uff1a\u201c\u8d4c\u4e86\uff01\u201d \u3000\u3000\u5468\u94c1\u9e6a\u548c\u66fe\u94c1\u9e25\u5bf9\u671b\u4e00\u773c\uff0c\u5404\u6709\u5609\u8bb8\u4e4b\u8272\uff0c\u4f3c\u4e4e\u8bf4\u8fd9\u5c11\u5e74\u6f47\u6d12\u8c6a\u723d\uff0c\u6c14\u6d3e\u4e0d\u51e1\u3002 \u3000\u3000\u5468\u94c1\u9e6a\u62ff\u8d77\u9ab0\u5b50\uff0c\u968f\u624b\u4e00\u63b7\uff0c\u63b7\u4e86\u4e2a\u4e03\u70b9\uff0c\u8ba9\u80e1\u6590\u62ff\u7b2c\u4e00\u624b\u724c\uff0c\u81ea\u5df1\u62ff\u4e86\u7b2c\u4e09\u624b\uff0c\u8f7b\u63cf\u6de1\u5199\u7684\u4e00\u770b\uff0c\u7ffb\u8fc7\u9aa8\u724c\uff0c\u62cd\u62cd\u4e24\u58f0\uff0c\u5728\u684c\u4e0a\u8fde\u51fb\u4e24\u4e0b\u3002\u4f17\u4eba\u5446\u4e86\u4e00\u5446\uff0c\u8ddf\u7740\u6b22\u547c\u53eb\u597d\uff0c\u539f\u6765\u56db\u5f20\u724c\u5206\u6210\u4e00\u524d\u4e00\u540e\u7684\u4e24\u9053\uff0c\u5e73\u5e73\u6574\u6574\u7684\u5d4c\u5728\u684c\u4e2d\uff0c\u724c\u9762\u4e0e\u684c\u9762\u76f8\u9f50\uff0c\u4fbf\u662f\u8bf7\u6728\u5320\u6765\u5728\u684c\u9762\u4e0a\u6316\u4e86\u6d1e\uff0c\u5c06\u9aa8\u724c\u9576\u5d4c\u8fdb\u53bb\uff0c\u4e5f\u672a\u5fc5\u6709\u8fd9\u822c\u5e73\u6ed1\u3002\u4f46\u8fd9\u4e00\u624b\u724c\u70b9\u5b50\u5374\u662f\u5e73\u5e73\uff0c\u524d\u4e94\u540e\u516d\u3002 \u3000\u3000\u80e1\u6590\u7ad9\u8d77\u8eab\u6765\uff0c\u7b11\u9053\uff1a\u201c\u5468\u5927\u7237\uff0c\u5bf9\u4e0d\u8d77\uff0c\u6211\u53ef\u8d62\u4e86\u4f60\u5566\uff01\u201d \u3000\u3000\u53f3\u624b\u4e00\u6325\uff0c\u62cd\u7684\u4e00\u58f0\u54cd\uff0c\u56db\u5f20\u724c\u540c\u65f6\u4ece\u7a7a\u4e2d\u63b7\u4e86\u4e0b\u6765\uff0c\u8fd9\u56db\u5f20\u724c\u7adf\u7136\u4e5f\u662f\u5206\u6210\u524d\u540e\u4e24\u9053\uff0c\u5e73\u5e73\u6574\u6574\u7684\u5d4c\u5165\u684c\u4e2d\uff0c\u724c\u9762\u4e0e\u684c\u9762\u76f8\u9f50\u3002\u5468\u94c1\u9e6a\u4ee5\u624b\u52b2\u76f4\u51fb\uff0c\u4f7f\u7684\u662f\u4ed6\u672c\u95e8\u7edd\u6280\u9e70\u722a\u529b\uff0c\u90a3\u662f\u4ed6\u6570\u5341\u5e74\u82e6\u7ec3\u7684\u5916\u95e8\u786c\u529f\uff0c\u539f\u5df2\u975e\u540c\u5c0f\u53ef\uff0c\u5c82\u77e5\u80e1\u6590\u4e3e\u724c\u51cc\u7a7a\u4e00\u63b7\uff0c\u4e5f\u80fd\u5d4c\u724c\u5165\u684c\uff0c\u8fd9\u4e00\u624b\u529f\u592b\u66f4\u662f\u8fdc\u80dc\u4e86\uff0c\u4f55\u51b5\u5468\u94c1\u9e6a\u8fde\u51fb\u4e24\u4e0b\uff0c\u80e1\u6590\u5374\u53ea\u51ed\u4e00\u63b7\u3002 \u3000\u3000\u4f17\u4eba\u60ca\u5f97\u5446\u4e86\uff0c\u8fde\u559d\u5f69\u4e5f\u90fd\u5fd8\u8bb0\u3002\u5468\u94c1\u9e6a\u795e\u8272\u81ea\u82e5\uff0c\u5c06\u5c01\u888b\u63a8\u5230\u80e1\u6590\u9762\u524d\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4eca\u513f\u724c\u98ce\u771f\u65fa\u3002\u201d\u4f17\u4eba\u8fd9\u65f6\u624d\u77a7\u6e05\u695a\u4e86\u80e1\u6590\u8fd9\u4e00\u624b\u724c\uff0c\u539f\u6765\u662f\u516b\u516b\u5173\uff0c\u524d\u4e00\u9053\u516b\u70b9\uff0c\u540e\u4e00\u9053\u4e5f\u662f\u516b\u70b9\u3002 \u3000\u3000\u80e1\u6590\u7b11\u9053\uff1a\u201c\u4e00\u65f6\u95f9\u73a9\uff0c\u5c82\u80fd\u4f5c\u771f\uff01\u201d\u968f\u624b\u5c06\u5c01\u888b\u63a8\u4e86\u56de\u53bb\u3002\u5468\u94c1\u9e6a\u76b1\u7709\u9053\uff1a\u201c\u80e1\u5144\u5f1f\uff0c\u4f60\u5018\u82e5\u4e0d\u6536\uff0c\u90a3\u662f\u635f\u6211\u59d3\u5468\u7684\u8d4c\u94b1\u6ca1\u54c1\u5566\uff01\u8fd9\u4e00\u624b\u724c\u5982\u662f\u6211\u8d62\uff0c\u6211\u5c82\u80fd\u8ddf\u4f60\u5ba2\u6c14\uff1f\u8fd9\u662f\u6211\u4eca\u513f\u5728\u5ba3\u6b66\u95e8\u5185\u4e70\u7684\u4e00\u6240\u5b85\u5b50\uff0c\u4e5f\u4e0d\u7b97\u5927\uff0c\u4e0d\u8fc7\u56db\u4ea9\u6765\u5730\u3002\u201d \u3000\u3000\u8bf4\u7740\u4ece\u5c01\u888b\u4e2d\u62bd\u51fa\u4e00\u5f20\u9ec4\u6f84\u6f84\u7684\u7eb8\u6765\uff0c\u539f\u6765\u662f\u4e00\u5f20\u5c4b\u5951\u3002\u65c1\u89c2\u4f17\u4eba\u90fd\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u60f3\u8fd9\u4e00\u573a\u8d4c\u535a\u5f53\u771f\u8c6a\u9614\u5f97\u53ef\u4ee5\uff0c\u5ba3\u6b66\u95e8\u5185\u4e00\u6240\u5927\u5b85\u5b50\uff0c\u5c11\u8bf4\u4e5f\u503c\u5f97\u516d\u4e03\u5343\u4e24\u94f6\u5b50\u3002 \u3000\u3000\u5468\u94c1\u9e6a\u5c06\u5c4b\u5951\u63a8\u5230\u80e1\u6590\u8eab\u524d\uff0c\u8bf4\u9053\uff1a\u201c\u4eca\u513f\u8d4c\u795e\u83e9\u8428\u8ddf\u5b9a\u4e86\u4f60\uff0c\u6ca1\u5f97\u8bf4\u7684\u3002\u724c\u5c40\u4e0d\u5982\u6563\u4e86\u5427\u3002\u8fd9\u5ea7\u5b85\u5b50\u4f60\u8981\u63a8\u8f9e\uff0c\u4fbf\u662f\u77a7\u6211\u59d3\u5468\u7684\u4e0d\u8d77\uff01\u201d\u80e1\u6590\u7b11\u9053\uff1a\u201c\u65e2\u662f\u5982\u6b64\uff0c\u505a\u5144\u5f1f\u7684\u5374\u4e4b\u4e0d\u606d\u3002\u5f85\u6536\u62fe\u597d\u4e86\uff0c\u8bf7\u5404\u4f4d\u5927\u54e5\u8fc7\u53bb\u5927\u8d4c\u4e00\u573a\u3002\u201d\u4f17\u4eba\u8f70\u7136\u7b54\u5e94\u3002\u5468\u94c1\u9e6a\u62f1\u4e86\u62f1\u624b\uff0c\u5f84\u81ea\u4e0e\u66fe\u94c1\u9e25\u8d70\u4e86\u3002\u6c6a\u94c1\u9e57\u89c1\u5927\u5e08\u54e5\u7247\u523b\u4e4b\u95f4\u5c06\u4e00\u5ea7\u5927\u5b85\u8f93\u53bb\uff0c\u7adf\u662f\u9762\u4e0d\u6539\u8272\uff0c\u4ed6\u4e00\u9897\u5fc3\u53cd\u800c\u6251\u901a\u6251\u901a\u7684\u8df3\u4e2a\u4e0d\u5b9a\u3002 \u3000\u3000\u5f53\u4e0b\u80e1\u6590\u5411\u79e6\u8010\u4e4b\u3001\u6c6a\u94c1\u9e57\u7b49\u4eba\u4f5c\u522b\uff0c\u548c\u7a0b\u7075\u7d20\u56de\u5230\u5ba2\u5e97\u3002\u7a0b\u7075\u7d20\u7b11\u9053\uff1a\u201c\u4f60\u547d\u4e2d\u6ce8\u5b9a\u8981\u4f5c\u5927\u8d22\u4e3b\uff0c\u4fbf\u63a8\u4e5f\u63a8\u4e0d\u6389\uff0c\u5728\u4e49\u5802\u9547\u7f6e\u4e0b\u4e86\u826f\u7530\u7f8e\u5730\uff0c\u54ea\u77e5\u9053\u7b2c\u4e00\u5929\u5230\u5317\u4eac\uff0c\u53c8\u8d62\u4e86\u4e00\u6240\u5927\u5b85\u5b50\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u8fd9\u59d3\u5468\u7684\u5012\u4e5f\u8c6a\u6c14\uff0c\u77a7\u4ed6\u7626\u7626\u5c0f\u5c0f\uff0c\u8c8c\u4e0d\u60ca\u4eba\uff0c\u90a3\u4e00\u624b\u9e70\u722a\u529b\u53ef\u7740\u5b9e\u4e0d\u542b\u7cca\uff0c\u60f3\u4e0d\u5230\u5b98\u573a\u4e4b\u4e2d\u8fd8\u6709\u8fd9\u7b49\u4eba\u7269\u3002\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u4f60\u8d62\u7684\u8fd9\u6240\u5b85\u5b50\u62ff\u6765\u5e72\u4e48\u5440\uff1f\u81ea\u5df1\u4f4f\u5462\uff0c\u8fd8\u662f\u5356\u4e86\u5b83\uff1f\u201d\u80e1\u6590\u9053\uff1a\u201c\u8bf4\u4e0d\u5b9a\u660e\u5929\u4e00\u573a\u5927\u8d4c\uff0c\u53c8\u8f93\u4e86\u51fa\u53bb\uff0c\u96be\u9053\u8d4c\u795e\u83e9\u8428\u5f53\u771f\u662f\u968f\u8eab\u5e26\u5417\uff1f\u201d \u3000\u3000\u6b21\u6668\u4e24\u4eba\u8d77\u8eab\uff0c\u521a\u7528\u5b8c\u65e9\u70b9\uff0c\u5e97\u4f19\u5e26\u4e86\u4e00\u4e2a\u4e2d\u5e74\u6c49\u5b50\u8fc7\u6765\uff0c\u9053\uff1a\u201c\u80e1\u5927\u7237\uff0c\u8fd9\u4f4d\u5927\u7237\u6709\u4e8b\u627e\u4f60\u3002\u201d\u80e1\u6590\u89c1\u8fd9\u4eba\u6234\u4e86\u4e00\u526f\u58a8\u955c\uff0c\u957f\u888d\u9a6c\u8902\uff0c\u8863\u670d\u5149\u9c9c\uff0c\u6307\u7532\u7559\u5f97\u957f\u957f\u7684\uff0c\u5374\u4e0d\u76f8\u8bc6\u3002 \u3000\u3000\u8fd9\u4eba\u53f3\u817f\u534a\u66f2\uff0c\u8bf7\u4e86\u4e2a\u5b89\uff0c\u9053\uff1a\u201c\u80e1\u5927\u7237\uff0c\u5468\u5927\u4eba\u5429\u5490\uff0c\u95ee\u80e1\u5927\u7237\u4ec0\u4e48\u65f6\u5019\u6709\u7a7a\uff0c\u8bf7\u8fc7\u5ba3\u6b66\u95e8\u5185\u77a7\u77a7\u90a3\u5ea7\u5b85\u5b50\u3002\u5c0f\u4eba\u59d3\u5168\uff0c\u662f\u90a3\u5b85\u5b50\u7684\u7ba1\u5bb6\u3002\u201d\u80e1\u6590\u597d\u5947\u5fc3\u8d77\uff0c\u5411\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u4e8c\u59b9\uff0c\u54b1\u4eec\u8fd9\u4fbf\u77a7\u77a7\u53bb\u3002\u201d \u3000\u3000\u90a3\u59d3\u5168\u7684\u606d\u606d\u656c\u656c\u5f15\u7740\u4e8c\u4eba\u6765\u5230\u5ba3\u6b66\u95e8\u5185\u3002\u80e1\u6590\u548c\u7a0b\u7075\u7d20\u89c1\u90a3\u5b85\u5b50\u6731\u6f06\u5927\u95e8\uff0c\u9ec4\u94dc\u5927\u95e8\u9489\uff0c\u77f3\u5e93\u95e8\u5899\uff0c\u9752\u77f3\u8e0f\u9636\uff0c\u7740\u5b9e\u9f50\u6574\u3002\u4e00\u8fdb\u5927\u95e8\uff0c\u81ea\u524d\u5385\u3001\u540e\u5385\u3001\u504f\u5385\uff0c\u4ee5\u81f3\u53a2\u623f\u3001\u82b1\u56ed\uff0c\u65e0\u4e0d\u9648\u8bbe\u8003\u7a76\uff0c\u7528\u5177\u6bd5\u5907\u3002\u90a3\u59d3\u5168\u7684\u9053\uff1a\u201c\u80e1\u5927\u7237\u5018\u82e5\u5408\u610f\uff0c\u4fbf\u8bf7\u642c\u8fc7\u6765\u3002\u66fe\u5927\u4eba\u53eb\u4e86\u4e00\u684c\u7b75\u5e2d\uff0c\u8bf4\u4eca\u665a\u6765\u5411\u80e1\u5927\u7237\u606d\u8d3a\u4e54\u8fc1\u3002\u5468\u5927\u4eba\u3001\u6c6a\u5927\u4eba\u4ed6\u4eec\u90fd\u8981\u6765\u8ba8\u4e00\u676f\u9152\u559d\u3002\u201d \u3000\u3000\u80e1\u6590\u54c8\u54c8\u5927\u7b11\uff0c\u9053\uff1a\u201c\u4ed6\u4eec\u5012\u60f3\u5f97\u5468\u5230\uff0c\u90a3\u4fbf\u4e00\u9f50\u8bf7\u5427\uff01\u201d \u3000\u3000\u5168\u7ba1\u5bb6\u9053\uff1a\u201c\u5c0f\u4eba\u7406\u4f1a\u5f97\u3002\u201d\u8eac\u8eab\u9000\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u5f85\u4ed6\u8d70\u8fdc\uff0c\u9053\uff1a\u201c\u5927\u54e5\uff0c\u8fd9\u5ea7\u5b85\u5b50\u53ea\u6015\u4e8c\u4e07\u4e24\u94f6\u5b50\u4e5f\u4e0d\u6b62\u3002\u8fd9\u4ef6\u4e8b\u5927\u4e0d\u5bfb\u5e38\u3002\u201d\u80e1\u6590\u70b9\u5934\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u77a7\u8fd9\u4e2d\u95f4\u6709\u4ec0\u4e48\u8e4a\u8df7\uff1f\u201d\u7a0b\u7075\u7d20\u5fae\u7b11\u9053\uff1a\u201c\u6211\u60f3\u603b\u662f\u6709\u4e2a\u4eba\u5728\u6697\u6697\u559c\u6b22\u4f60\uff0c\u6240\u4ee5\u6545\u610f\u63a5\u4e8c\u8fde\u4e09\uff0c\u4e00\u4efd\u4e00\u4efd\u7684\u9001\u4f60\u5927\u793c\u3002\u201d \u3000\u3000\u80e1\u6590\u77e5\u5979\u5728\u8bf4\u8881\u7d2b\u8863\uff0c\u8138\u4e0a\u4e00\u7ea2\uff0c\u6447\u4e86\u6447\u5934\u3002\u7a0b\u7075\u7d20\u7b11\u9053\uff1a\u201c\u6211\u662f\u8ddf\u4f60\u8bf4\u7b11\u5462\u3002\u6211\u5927\u54e5\u6177\u6168\u8c6a\u4fa0\uff0c\u4e5f\u4e0d\u4f1a\u628a\u8fd9\u4e9b\u7530\u5730\u623f\u4ea7\u653e\u5728\u5fc3\u4e0a\u3002\u8fd9\u9001\u793c\u4e4b\u4eba\uff0c\u51b3\u4e0d\u662f\u4f60\u7684\u77e5\u5df2\uff0c\u5426\u5219\u7684\u8bdd\uff0c\u8fd8\u4e0d\u5982\u9001\u4e00\u53ea\u7389\u51e4\u51f0\u3002\u8fd9\u9001\u793c\u7684\u82e5\u4e0d\u662f\u6015\u4f60\uff0c\u4fbf\u5728\u60f3\u7b3c\u7edc\u4f60\u3002\u55ef\uff0c\u8c01\u80fd\u6709\u8fd9\u4e48\u5927\u624b\u7b14\u554a\uff1f\u201d\u80e1\u6590\u51db\u7136\u9053\uff1a\u201c\u662f\u798f\u5927\u5e05\uff1f\u201d \u3000\u3000\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u6211\u77a7\u662f\u6709\u70b9\u513f\u50cf\u3002\u4ed6\u624b\u4e0b\u7528\u4e86\u8fd9\u8bb8\u591a\u4eba\u7269\uff0c\u6709\u54ea\u4e00\u4e2a\u53ca\u5f97\u4e0a\u4f60\uff1f\u518d\u8bf4\uff0c\u9a6c\u59d1\u5a18\u65e2\u7136\u5f97\u4ed6\u5ba0\u5e78\uff0c\u4e5f\u603b\u5f97\u9001\u4f60\u4e00\u4efd\u539a\u793c\u3002\u4ed6\u4eec\u77e5\u4f60\u6027\u60c5\u803f\u76f4\uff0c\u4e0d\u80fd\u8f7b\u6613\u6536\u53d7\u8c6a\u95e8\u7684\u8d22\u7269\uff0c\u4e8e\u662f\u6d3e\u4eba\u5728\u8d4c\u53f0\u4e0a\u9001\u7ed9\u4f60\u3002\u201d \u3000\u3000\u80e1\u6590\u9053\uff1a\u201c\u55ef\u3002\u4ed6\u4eec\u6d88\u606f\u4e5f\u771f\u7075\u3002\u6211\u4eec\u7b2c\u4e00\u5929\u5230\u5317\u4eac\uff0c\u5c31\u7acb\u523b\u8ba9\u6211\u5927\u8d62\u4e00\u573a\u3002\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u6211\u4eec\u53c8\u6ca1\u4e54\u88c5\u6539\u626e\uff0c\u591a\u534a\u4e00\u5207\u65e9\u5c31\u5b89\u6392\u597d\u4e86\uff0c\u53ea\u7b49\u6211\u4eec\u5230\u6765\u3002\u8ddf\u6c6a\u94c1\u9e57\u76f8\u9047\u662f\u78b0\u5de7\uff0c\u5728\u805a\u82f1\u697c\u4e2d\u4e00\u8d4c\uff0c\u8baf\u606f\u62a5\u4e86\u51fa\u53bb\uff0c\u5468\u94c1\u9e6a\u62ff\u4e86\u5c4b\u5951\u5c31\u6765\u4e86\u3002\u201d\u80e1\u6590\u70b9\u5934\u9053\uff1a\u201c\u4f60\u731c\u5f97\u6709\u7406\u3002\u6628\u665a\u5468\u94c1\u9e6a\u53ea\u8981\u6709\u610f\u8f93\u7ed9\u6211\uff0c\u90a3\u4e00\u6ce8\u4fbf\u7b97\u662f\u6211\u8f93\u4e86\uff0c\u4ed6\u518d\u8d4c\u4e0b\u53bb\uff0c\u603b\u6709\u6cd5\u5b50\u6559\u6211\u8d62\u4e86\u8fd9\u5ea7\u5b85\u5b50\u3002\u201d \u3000\u3000\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u90a3\u4f60\u600e\u751f\u5904\u7f6e\uff1f\u201d\u80e1\u6590\u9053\uff1a\u201c\u4eca\u665a\u6211\u518d\u8ddf\u4ed6\u4eec\u8d4c\u4e00\u573a\uff0c\u60f3\u6cd5\u5b50\u628a\u5b85\u5b50\u8f93\u51fa\u53bb\uff0c\u77a7\u6211\u6709\u6ca1\u6709\u8fd9\u4e2a\u624b\u6bb5\u3002\u201d\u7a0b\u7075\u7d20\u7b11\u9053\uff1a\u201c\u4e24\u5bb6\u90fd\u8981\u6545\u610f\u8d4c\u8f93\uff0c\u8fd9\u4e00\u573a\u4ea4\u624b\uff0c\u5374\u4e5f\u70ed\u95f9\u5f97\u7d27\u5462\u3002\u201d \u3000\u3000\u5f53\u65e5\u5348\u540e\u7533\u724c\u65f6\u5206\uff0c\u66fe\u94c1\u9e25\u7740\u4eba\u9001\u4e86\u4e00\u5e2d\u6781\u4e30\u76db\u7684\u9c7c\u7fc5\u71d5\u7a9d\u5e2d\u6765\u3002\u90a3\u59d3\u5168\u7684\u7ba1\u5bb6\u7387\u9886\u4ec6\u5f79\uff0c\u5728\u5927\u5385\u4e0a\u5e03\u7f6e\u5f97\u706f\u70db\u8f89\u714c\uff0c\u559c\u6c14\u6d0b\u6d0b\u3002 \u3000\u3000\u6c6a\u94c1\u9e57\u7b2c\u4e00\u4e2a\u5230\u6765\u3002\u4ed6\u5728\u5b85\u5b50\u524d\u540e\u5de6\u53f3\u8d70\u4e86\u4e00\u906d\uff0c\u4e0d\u4f4f\u53e3\u7684\u79f0\u8d5e\u8fd9\u5b85\u5b50\u5802\u7687\u534e\u7f8e\uff0c\u53c8\u5927\u8d5e\u80e1\u6590\u6628\u665a\u8d4c\u8fd0\u4ea8\u901a\uff0c\u624b\u6c14\u5947\u4f73\u3002\u80e1\u6590\u5fc3\u9053\uff1a\u201c\u8fd9\u6c6a\u94c1\u9e57\u6027\u76f4\uff0c\u77a7\u6765\u4e0d\u660e\u5176\u4e2d\u7684\u8fc7\u8282\uff0c\u5f85\u4f1a\u6211\u5c06\u8fd9\u5b85\u5b50\u8f93\u4e86\u7ed9\u4ed6\uff0c\u77a7\u4ed6\u7684\u4e24\u4e2a\u5e08\u5144\u5982\u4f55\u5904\u7f6e\uff0c\u90a3\u5012\u6709\u4e00\u573a\u597d\u620f\u77a7\u5462\u3002\u201d \u3000\u3000\u4e0d\u4e45\u5468\u94c1\u9e6a\u3001\u66fe\u94c1\u9e25\u5e08\u5144\u5f1f\u4fe9\u5230\u4e86\uff0c\u59d3\u891a\u3001\u59d3\u4e0a\u5b98\u3001\u59d3\u8042\u7684\u4e09\u4eba\u5230\u4e86\u3002\u8fc7\u4e0d\u591a\u65f6\uff0c\u79e6\u8010\u4e4b\u54c8\u54c8\u5927\u7b11\u7684\u8fdb\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u80e1\u5144\u5f1f\uff0c\u6211\u7ed9\u4f60\u5e26\u4e86\u4e24\u4f4d\u8001\u670b\u53cb\u6765\uff0c\u4f60\u731c\u731c\u662f\u8c01\uff1f\u201d \u3000\u3000\u53ea\u89c1\u4ed6\u8eab\u540e\u8d70\u8fdb\u4e09\u4e2a\u4eba\u6765\u3002\u6700\u540e\u4e00\u4eba\u662f\u6628\u5929\u89c1\u8fc7\u7684\u6bb7\u4ef2\u7fd4\uff0c\u7ecf\u4e86\u6628\u665a\u4e4b\u4e8b\uff0c\u4ed6\u5c45\u7136\u4ecd\u6765\uff0c\u5012\u662f\u9887\u51fa\u80e1\u6590\u610f\u6599\u4e4b\u5916\u3002\u5176\u4f59\u4e24\u4eba\u5bb9\u8c8c\u76f8\u4f3c\uff0c\u90fd\u662f\u7cbe\u795e\u77cd\u94c4\u7684\u8001\u8005\uff0c\u770b\u6765\u751a\u662f\u9762\u5584\uff0c\u80e1\u6590\u5fae\u5fae\u4e00\u6014\uff0c\u5f85\u770b\u5230\u4e24\u4eba\u811a\u6b65\u843d\u5730\u65f6\u811a\u5c16\u7a0d\u659c\u5411\u91cc\uff0c\u6b63\u662f\u516b\u5366\u95e8\u529f\u592b\u6781\u5176\u6df1\u539a\u4e4b\u8c61\uff0c\u5f53\u5373\u7701\u609f\uff0c\u62a2\u4e0a\u884c\u793c\uff0c\u8bf4\u9053\uff1a\u201c\u738b\u5927\u7237\u3001\u738b\u4e8c\u7237\u4e24\u4f4d\u524d\u8f88\u9a7e\u5230\uff0c\u771f\u662f\u60f3\u4e0d\u5230\u3002\u5546\u5bb6\u5821\u4e00\u522b\uff0c\u4e24\u4f4d\u7cbe\u795e\u66f4\u52a0\u5065\u65fa\u4e86\u3002\u201d\u539f\u6765\u8fd9\u4e24\u4eba\u6b63\u662f\u516b\u5366\u95e8\u738b\u5251\u82f1\u3001\u738b\u5251\u6770\u5144\u5f1f\u3002 \u3000\u3000\u5341\u4e8c\u4eba\u6b22\u547c\u7545\u996e\uff0c\u5e2d\u4e0a\u8bf4\u7684\u90fd\u662f\u6c5f\u6e56\u4e0a\u82f1\u96c4\u8c6a\u6770\u4e4b\u4e8b\u3002\u6bb7\u4ef2\u7fd4\u63d0\u5230\u5f53\u5e74\u5728\u5546\u5bb6\u5821\u4e2d\uff0c\u4f17\u4eba\u5982\u4f55\u88ab\u56f0\u94c1\u5385\uff0c\u8eab\u906d\u706b\u707c\u4e4b\u5371\uff0c\u5982\u4f55\u4e8f\u5f97\u80e1\u6590\u667a\u52c7\u53cc\u5168\uff0c\u594b\u8eab\u89e3\u56f4\u3002\u79e6\u8010\u4e4b\u3001\u5468\u94c1\u9e6a\u7b49\u542c\u4e86\uff0c\u66f4\u662f\u5927\u8d5e\u4e0d\u5df2\u3002\u7a0b\u7075\u7d20\u76ee\u6f84\u5982\u6c34\uff0c\u8109\u8109\u7684\u671b\u7740\u80e1\u6590\uff0c\u5fc3\u60f3\u8fd9\u4e9b\u82f1\u96c4\u4e8b\u8ff9\uff0c\u4f60\u81ea\u5df1\u4ece\u6765\u4e0d\u8bf4\u3002 \u3000\u3000\u7b75\u5e2d\u6563\u540e\uff0c\u773c\u89c1\u4e00\u8f6e\u660e\u6708\u6d8c\u5c06\u4e0a\u6765\uff0c\u8fd9\u5929\u662f\u516b\u6708\u521d\u5341\uff0c\u867d\u5df2\u7acb\u79cb\uff0c\u4ecd\u9887\u708e\u70ed\uff0c\u90a3\u662f\u53eb\u4f5c\u201c\u6842\u82b1\u84b8\u201d\u3002\u5168\u7ba1\u5bb6\u5728\u82b1\u56ed\u4ead\u4e2d\u6446\u8bbe\u4e86\u74dc\u679c\uff0c\u8bf7\u4f17\u4eba\u4e58\u51c9\u6d88\u6691\u3002\u80e1\u6590\u9053\uff1a\u201c\u5404\u4f4d\u5148\u559d\u676f\u6e05\u8336\uff0c\u54b1\u4eec\u518d\u6765\u5927\u8d4c\u4e00\u573a\u3002\u201d\u4f17\u4eba\u8f70\u7136\u53eb\u597d\uff0c\u6765\u5230\u82b1\u56ed\u7684\u51c9\u4ead\u4e2d\u5750\u4e0b\u3002 \u3000\u3000\u6ca1\u8bb2\u8bba\u5f97\u51e0\u53e5\uff0c\u5ffd\u542c\u5f97\u5eca\u4e0a\u4f20\u6765\u4e00\u9635\u55a7\u54d7\uff0c\u5374\u662f\u6709\u4eba\u5728\u4e0e\u5168\u7ba1\u5bb6\u5927\u58f0\u5435\u56b7\uff0c\u63a5\u7740\u5168\u7ba1\u5bb6\u201c\u554a\u54df\u201d\u4e00\u58f0\u5927\u53eb\uff0c\u7830\u7684\u4e00\u54cd\uff0c\u4f3c\u4e4e\u88ab\u4eba\u8e22\u4e86\u4e2a\u7b4b\u6597\u3002 \u3000\u3000\u53ea\u89c1\u4e00\u6761\u94c1\u5854\u4f3c\u7684\u5927\u6c49\u98de\u6b65\u95ef\u8fdb\u4ead\u6765\uff0c\u4f38\u624b\u5728\u684c\u4e0a\u4e00\u62cd\uff0c\u545b\u5577\u5577\u4e00\u9635\u54cd\u4eae\uff0c\u8336\u676f\u679c\u76d8\u7b49\u7269\uff0c\u6454\u5f97\u4e00\u5730\u3002\u90a3\u5927\u6c49\u6307\u7740\u5468\u94c1\u9e6a\uff0c\u7c97\u58f0\u9053\uff1a\u201c\u5468\u5927\u54e5\uff0c\u8fd9\u5374\u662f\u4f60\u7684\u4e0d\u662f\u4e86\u3002\u8fd9\u5ea7\u5b85\u5b50\u6211\u5356\u7ed9\u4f60\u4e00\u4e07\u4e8c\u5343\u4e24\u94f6\u5b50\uff0c\u90a3\u53ef\u662f\u534a\u5356\u534a\u9001\uff0c\u51b2\u7740\u4f60\u5468\u5927\u54e5\u7684\u9762\u5b50\uff0c\u505a\u5144\u5f1f\u7684\u8fd8\u80fd\u8ba1\u8f83\u4e48\uff1f\u4e0d\u6599\u4e00\u8f6c\u773c\u95f4\uff0c\u4f60\u5374\u62ff\u53bb\u8f6c\u9001\u4e86\u522b\u4eba\uff0c\u6211\u8fd9\u4e2a\u4e8f\u53ef\u5403\u4e0d\u8d77\uff01\u5927\u5bb6\u6765\u8bc4\u8bc4\u8fd9\u4e2a\u7406\uff0c\u6211\u59d3\u5fb7\u7684\u80fd\u505a\u8fd9\u51a4\u5927\u5934\u4e48\uff1f\u201d \u3000\u3000\u5468\u94c1\u9e6a\u51b7\u51b7\u5730\u9053\uff1a\u201c\u4f60\u94b1\u4e0d\u591f\u4f7f\uff0c\u597d\u597d\u7684\u8bf4\u4fbf\u4e86\u3002\u8fd9\u91cc\u662f\u597d\u670b\u53cb\u5bb6\u91cc\uff0c\u4f60\u6765\u80e1\u95f9\u4ec0\u4e48\uff1f\u201d\u90a3\u9ed1\u5927\u6c49\u4e00\u5f20\u8138\u80c0\u5f97\u9ed1\u4e2d\u6cdb\u7ea2\uff0c\u4f38\u624b\u53c8\u5f80\u684c\u4e0a\u62cd\u53bb\u3002\u5468\u94c1\u9e6a\u5de6\u624b\u4e00\u52fe\u4e00\u5e26\uff0c\u5c06\u4ed6\u4e24\u53ea\u624b\u8155\u90fd\u7262\u7262\u6293\u4f4f\u4e86\uff0c\u522b\u77a7\u5468\u94c1\u9e6a\u8eab\u6750\u77ee\u5c0f\uff0c\u7ad9\u8d77\u6765\u4e0d\u8fc7\u521a\u53ca\u90a3\u5927\u6c49\u7684\u80a9\u8180\uff0c\u4f46\u90a3\u5927\u6c49\u53cc\u624b\u88ab\u4ed6\u4e00\u6293\uff0c\u72b9\u4f3c\u7ed9\u4e00\u4e2a\u94c1\u7b8d\u7b8d\u4f4f\u4e86\uff0c\u7adf\u662f\u6323\u624e\u4e0d\u8131\u3002 \u3000\u3000\u5468\u94c1\u9e6a\u62c9\u7740\u4ed6\u8d70\u5230\u4ead\u5916\uff0c\u4f4e\u58f0\u8ddf\u4ed6\u8bf4\u4e86\u51e0\u53e5\u8bdd\u3002\u90a3\u5927\u6c49\u5140\u81ea\u4e0d\u80af\u4f9d\u4ece\uff0c\u5476\u5476\u4e0d\u4f11\u3002\u5468\u94c1\u9e6a\u607c\u4e86\u8d77\u6765\uff0c\u53cc\u81c2\u8fd0\u529b\u5f80\u524d\u4e00\u63a8\u3002\u90a3\u5927\u6c49\u7ad9\u7acb\u4e0d\u5b9a\uff0c\u5411\u540e\u8dcc\u51fa\u51e0\u6b65\uff0c\u649e\u5728\u4e00\u682a\u6885\u6811\u4e4b\u4e0a\uff0c\u5580\u5587\u4e00\u58f0\uff0c\u649e\u65ad\u4e86\u8001\u5927\u4e24\u6839\u6860\u679d\u3002\u5468\u94c1\u9e6a\u559d\u9053\uff1a\u201c\u59d3\u5fb7\u7684\u83bd\u592b\uff0c\u7ed9\u6211\u5728\u5916\u8fb9\u4f8d\u5019\u7740\uff0c\u4e0d\u6015\u6b7b\u7684\u4fbf\u6765\u7f57\u56cc\uff01\u201d\u90a3\u5927\u6c49\u629a\u7740\u80cc\u4e0a\u7684\u75db\u5904\uff0c\u4f4e\u5934\u8d8b\u51fa\u3002 \u3000\u3000\u66fe\u94c1\u9e25\u54c8\u54c8\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u83bd\u592b\u60ef\u5e38\u626b\u4eba\u6e05\u5174\uff0c\u5927\u5e08\u54e5\u65e9\u5c31\u8be5\u597d\u597d\u63cd\u4ed6\u4e00\u987f\u3002\u201d\u5468\u94c1\u9e6a\u5fae\u7b11\u9053\uff1a\u201c\u6211\u5c31\u77a7\u7740\u4ed6\u5fc3\u773c\u513f\u8fd8\u597d\uff0c\u4e5f\u4e0d\u8ddf\u4ed6\u4e00\u822c\u89c1\u8bc6\u3002\u80e1\u5927\u54e5\uff0c\u5012\u6559\u4f60\u89c1\u7b11\u4e86\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u597d\u8bf4\uff0c\u597d\u8bf4\u3002\u65e2\u662f\u8fd9\u5b85\u5b50\u4ed6\u5356\u4fbf\u5b9c\u4e86\uff0c\u5144\u5f1f\u518d\u8865\u4ed6\u4e9b\u94f6\u5b50\u4fbf\u662f\u3002\u201d\u5468\u94c1\u9e6a\u5fd9\u9053\uff1a\u201c\u80e1\u5927\u54e5\u8bf4\u54ea\u91cc\u8bdd\u6765\uff1f\u8fd9\u4ef6\u4e8b\u5144\u5f1f\u81ea\u4f1a\u6599\u7406\uff0c\u4e0d\u7528\u4f60\u64cd\u5fc3\u3002\u5012\u662f\u90a3\u4e2a\u83bd\u649e\u4e4b\u5f92\uff0c\u65e0\u610f\u4e2d\u5f97\u7f6a\u4e86\u80e1\u5927\u54e5\uff0c\u4ed6\u539f\u4e0d\u77e5\u80e1\u5927\u54e5\u5982\u6b64\u82f1\u96c4\u4e86\u5f97\uff0c\u65e2\u505a\u4e0b\u4e86\u4e8b\u6765\uff0c\u6b64\u523b\u5b9e\u662f\u540e\u6094\u83ab\u53ca\u3002\u5144\u5f1f\u4fbf\u53eb\u4ed6\u6765\u5411\u80e1\u5927\u54e5\u656c\u9152\u8d54\u793c\uff0c\u51b2\u7740\u5144\u5f1f\u548c\u8fd9\u91cc\u5404\u4f4d\u7684\u9762\u5b50\uff0c\u80e1\u5927\u54e5\u4fbf\u4e0d\u8ba1\u8f83\u8fd9\u4e00\u906d\u5982\u4f55\uff1f\u201d \u3000\u3000\u80e1\u6590\u7b11\u9053\uff1a\u201c\u8d54\u793c\u4e24\u5b57\uff0c\u4f11\u8981\u63d0\u8d77\u3002\u65e2\u662f\u5468\u5927\u54e5\u7684\u670b\u53cb\uff0c\u8bf7\u4ed6\u4e00\u540c\u6765\u559d\u4e00\u676f\u5427\uff01\u201d\u5468\u94c1\u9e6a\u7ad9\u8d77\u8eab\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u80e1\u5927\u54e5\u662f\u5c11\u5e74\u82f1\u96c4\uff0c\u6211\u4eec\u5168\u90fd\u8bda\u5fc3\u7ed3\u4ea4\u4f60\u8fd9\u4f4d\u670b\u53cb\u3002\u90a3\u83bd\u592b\u505a\u9519\u4e86\u4e8b\uff0c\u6211\u4eec\u5927\u4f19\u513f\u5168\u6d3e\u4ed6\u7684\u4e0d\u662f\u3002\u80e1\u5927\u54e5\u5927\u4eba\u5927\u91cf\uff0c\u52a1\u8bf7\u4e0d\u8981\u4ecb\u6000\u3002\u201d \u3000\u3000\u80e1\u6590\u9053\uff1a\u201c\u4e9b\u4e9b\u5c0f\u4e8b\u4f55\u5fc5\u6302\u9f7f\uff1f\u5468\u5927\u54e5\u8bf4\u5f97\u592a\u5ba2\u6c14\u4e86\u3002\u201d\u5468\u94c1\u9e6a\u4e00\u8eac\u5230\u5730\uff0c\u8bf4\u9053\uff1a\u201c\u5144\u5f1f\u5148\u884c\u8c22\u8fc7\u3002\u201d\u66fe\u94c1\u9e25\u548c\u79e6\u8010\u4e4b\u4e5f\u540c\u65f6\u8d77\u8eab\u4f5c\u63d6\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u4e00\u9f50\u591a\u8c22\u4e86\u3002\u201d\u80e1\u6590\u5fd9\u7ad9\u8d77\u8fd8\u793c\u3002\u5468\u94c1\u9e6a\u9053\uff1a\u201c\u6211\u53bb\u53eb\u90a3\u83bd\u592b\u6765\uff0c\u8ddf\u80e1\u5927\u54e5\u8d54\u7f6a\u3002\u201d\u8bf4\u7740\u8f6c\u8eab\u51fa\u5916\u3002 \u3000\u3000\u80e1\u6590\u548c\u7a0b\u7075\u7d20\u5bf9\u671b\u4e86\u4e00\u773c\uff0c\u5747\u60f3\uff1a\u201c\u8fd9\u83bd\u592b\u867d\u7136\u884c\u4e3a\u7c97\u9c81\u4e86\u4e9b\uff0c\u4f46\u5468\u94c1\u9e6a\u8fd9\u756a\u8d54\u793c\u7684\u8a00\u8bed\uff0c\u5374\u672a\u514d\u8fc7\u4e8e\u90d1\u91cd\u3002\u4e0d\u77e5\u8fd9\u9ed1\u5927\u6c49\u662f\u4f55\u95e8\u9053\uff1f\u201d \u3000\u3000\u8fc7\u4e86\u7247\u523b\uff0c\u53ea\u542c\u5f97\u811a\u6b65\u58f0\u54cd\uff0c\u56ed\u4e2d\u8d70\u8fdb\u4e24\u4e2a\u4eba\u6765\u3002\u5468\u94c1\u9e6a\u643a\u7740\u4e00\u4eba\u4e4b\u624b\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u83bd\u592b\u554a\u83bd\u592b\uff0c\u5feb\u656c\u80e1\u5927\u54e5\u4e09\u676f\u9152\uff01\u4f60\u4eec\u8fd9\u53eb\u4e0d\u6253\u4e0d\u6210\u76f8\u8bc6\uff0c\u80e1\u5927\u54e5\u7b54\u5e94\u539f\u8c05\u4f60\u5566\u3002\u4ed6\u5927\u4e08\u592b\u4e00\u8a00\u65e2\u51fa\uff0c\u9a77\u9a6c\u96be\u8ffd\u3002\u4eca\u65e5\u4fbf\u5b9c\u4e86\u4f60\u8fd9\u83bd\u592b\uff01\u201d \u3000\u3000\u80e1\u6590\u970d\u5730\u7ad9\u8d77\uff0c\u98d8\u8eab\u51fa\u4ead\uff0c\u5de6\u8db3\u4e00\u70b9\uff0c\u5148\u62a2\u8fc7\u53bb\u6321\u4f4f\u4e86\u90a3\u4eba\u7684\u9000\u8def\uff0c\u94c1\u9752\u7740\u8138\uff0c\u5389\u58f0\u8bf4\u9053\uff1a\u201c\u59d3\u5468\u7684\uff0c\u4f60\u95f9\u4ec0\u4e48\u7384\u865a\uff1f \u3000\u3000\u6211\u82e5\u4e0d\u624b\u5203\u6b64\u4eba\uff0c\u6211\u80e1\u6590\u6789\u79f0\u9876\u5929\u7acb\u5730\u7684\u7537\u5b50\u6c49\uff01\u201d \u3000\u3000\u8fdb\u56ed\u6765\u8fd9\u4eba\uff0c\u6b63\u662f\u5e7f\u4e1c\u4f5b\u5c71\u9547\u4e0a\u6740\u5bb3\u953a\u963f\u56db\u5168\u5bb6\u7684\u4e94\u864e\u95e8\u638c\u95e8\u4eba\u51e4\u5929\u5357\uff01 \u3000\u3000\u80e1\u6590\u6b64\u65f6\u5df2\u7136\u5fc3\u4e2d\u96ea\u4eae\uff0c\u539f\u6765\u5468\u94c1\u9e6a\u5b89\u6392\u4e0b\u5708\u5957\uff0c\u547d\u4e00\u4e2a\u83bd\u592b\u6765\u80e1\u95f9\u4e00\u756a\uff0c\u7136\u540e\u5957\u5f97\u4ed6\u7684\u8a00\u8bed\uff0c\u8981\u81ea\u5df1\u7b54\u5e94\u539f\u8c05\u4e00\u4e2a\u83bd\u592b\u3002\u4ed6\u60f3\u8d77\u953a\u963f\u56db\u5168\u5bb6\u60e8\u6b7b\u7684\u60c5\u72b6\uff0c\u70ed\u8840\u4e0a\u6d8c\uff0c\u76ee\u5149\u4e2d\u4f3c\u8981\u8ff8\u51fa\u706b\u6765\u3002 \u3000\u3000\u5468\u94c1\u9e6a\u9053\uff1a\u201c\u80e1\u5927\u54e5\uff0c\u6211\u8ddf\u4f60\u76f4\u8bf4\u4e86\u7f62\u3002\u4e49\u5802\u9547\u4e0a\u7684\u7530\u5730\u623f\u4ea7\uff0c\u5168\u662f\u8fd9\u83bd\u592b\u9001\u7684\u3002\u8fd9\u4e00\u5ea7\u5b85\u5b50\u548c\u5bb6\u5177\uff0c\u4e5f\u5168\u662f\u8fd9\u83bd\u592b\u4e70\u7684\u3002\u4ed6\u8ddf\u4f60\u8d54\u4e0d\u662f\u4e4b\u5fc3\uff0c\u8bf4\u5f97\u4e0a\u662f\u8bda\u6073\u4e4b\u6781\u4e86\u3002\u5927\u4e08\u592b\u62ff\u5f97\u8d77\u653e\u5f97\u4e0b\uff0c\u8fc7\u53bb\u7684\u5c0f\u5c0f\u6028\u4ec7\uff0c\u4f55\u5fc5\u653e\u5728\u5fc3\u4e0a\uff1f\u51e4\u8001\u5927\uff0c\u5feb\u7ed9\u80e1\u5927\u54e5\u8d54\u793c\u5427\uff01\u201d \u3000\u3000\u80e1\u6590\u89c1\u51e4\u5929\u5357\u53cc\u624b\u62b1\u62f3\uff0c\u610f\u6b32\u884c\u793c\uff0c\u53cc\u81c2\u4e00\u5f20\uff0c\u8bf4\u9053\uff1a\u201c\u4e14\u6162\uff01\u201d\u5411\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u4e8c\u59b9\uff0c\u4f60\u8fc7\u6765\uff01\u201d\u7a0b\u7075\u7d20\u5feb\u6b65\u8d70\u5230\u4ed6\u7684\u8eab\u8fb9\uff0c\u5e76\u80a9\u7ad9\u7740\u3002\u80e1\u6590\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u5404\u4f4d\u8bf7\u4e86\uff01\u59d3\u80e1\u7684\u7ed3\u4ea4\u670b\u53cb\uff0c\u51ed\u7684\u662f\u610f\u6c14\u76f8\u6295\uff0c\u662f\u975e\u5206\u660e\u3002\u54b1\u4eec\u5403\u559d\u8d4c\u535a\uff0c\u90a3\u7b97\u4e0d\u4e86\u751a\u4e48\uff0c\u4fbf\u662f\u5e02\u4e95\u5c0f\u4eba\uff0c\u4e5f\u5c82\u4e0d\u76f8\u805a\u559d\u9152\u8d4c\u94b1\uff1f\u5927\u4e08\u592b\u4e49\u6c14\u4e3a\u5148\uff0c\u4ee5\u91d1\u94f6\u6765\u8ba8\u597d\u80e1\u67d0\uff0c\u53ef\u628a\u80e1\u67d0\u4eba\u7684\u4eba\u54c1\u77a7\u5f97\u4e00\u94b1\u4e0d\u503c\u4e86\uff01\u201d \u3000\u3000\u66fe\u94c1\u9e25\u7b11\u9053\uff1a\u201c\u80e1\u5927\u54e5\u53ef\u8bef\u4f1a\u4e86\u3002\u51e4\u8001\u5927\u8d60\u9001\u4e00\u70b9\u8584\u793c\uff0c\u4e5f\u53ea\u662f\u7565\u8868\u656c\u610f\uff0c\u54ea\u91cc\u6562\u770b\u8f7b\u8001\u5144\u4e86\uff1f\u201d \u3000\u3000\u80e1\u6590\u53f3\u624b\u4e00\u6446\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u59d3\u51e4\u7684\u5728\u5e7f\u4e1c\u4f5c\u5a01\u4f5c\u798f\uff0c\u4e3a\u4e86\u8c0b\u53d6\u90bb\u820d\u4e00\u5757\u5730\u76ae\uff0c\u5c06\u4eba\u5bb6\u4e00\u5bb6\u8001\u5c0f\u5bb3\u5f97\u4e2a\u4e2a\u6b7b\u4e8e\u975e\u547d\u3002\u6211\u80e1\u6590\u548c\u953a\u5bb6\u975e\u4eb2\u975e\u6545\uff0c\u4f46\u65e2\u4f38\u624b\u7ba1\u4e0a\u4e86\u8fd9\u4ef6\u4e8b\uff0c\u4fbf\u8ddf\u8fd9\u59d3\u51e4\u7684\u6076\u68cd\u8a93\u4e0d\u5e76\u5b58\u4e8e\u5929\u5730\u4e4b\u95f4\u3002\u5018\u82e5\u8981\u5f97\u7f6a\u670b\u53cb\uff0c\u90a3\u4e5f\u662f\u52bf\u975e\u5f97\u5df2\uff0c\u8981\u8bf7\u5404\u4f4d\u89c1\u8c05\u3002\u5468\u5927\u54e5\uff0c\u8fd9\u5f20\u5c4b\u5951\u8bf7\u6536\u4e0b\u4e86\u3002\u201d\u4ece\u6000\u4e2d\u6478\u51fa\u5957\u7740\u5c4b\u5951\u7684\u4fe1\u5c01\uff0c\u8f7b\u8f7b\u4e00\u6325\uff0c\u90a3\u4fe1\u5c01\u76f4\u98d8\u5230\u5468\u94c1\u9e6a\u9762\u524d\u3002 \u3000\u3000\u5468\u94c1\u9e6a\u53ea\u5f97\u63a5\u4f4f\uff0c\u5f85\u8981\u4ea4\u8fd8\u7ed9\u4ed6\uff0c\u5374\u60f3\u51ed\u7740\u81ea\u5df1\u624b\u6307\u4e0a\u7684\u529f\u592b\uff0c\u96be\u4ee5\u8fd9\u822c\u5e73\u5e73\u7a33\u7a33\u7684\u5c06\u4fe1\u5c01\u9001\u5230\u4ed6\u9762\u524d\u3002 \u3000\u3000\u53ea\u542c\u80e1\u6590\u6717\u58f0\u9053\uff1a\u201c\u8fd9\u91cc\u662f\u4eac\u5e08\u91cd\u5730\uff0c\u5929\u5b50\u811a\u5e95\u4e0b\u7684\u5730\u65b9\uff0c\u8fd9\u59d3\u51e4\u7684\u53c8\u4e0d\u77e5\u6709\u591a\u5c11\u597d\u670b\u597d\u53cb\uff0c\u4f46\u6211\u80e1\u6590\u4eca\u665a\u8c41\u51fa\u4e86\u6027\u547d\uff0c\u5b9a\u8981\u52a8\u4e00\u52a8\u4ed6\u3002\u662f\u59d3\u80e1\u7684\u597d\u670b\u53cb\u4fbf\u4e0d\u8981\u62e6\u963b\uff0c\u662f\u59d3\u51e4\u7684\u597d\u670b\u53cb\uff0c\u5927\u4f19\u513f\u4e00\u9f50\u4e0a\u5427\uff01\u201d\u8bf4\u7f62\u53cc\u624b\u53c9\u8170\u4e00\u7ad9\u3002\u4ed6\u660e\u77e5\u5317\u4eac\u57ce\u4e2d\u9ad8\u624b\u5982\u4e91\uff0c\u8fd9\u51e4\u5929\u5357\u65e2\u6562\u9732\u9762\uff0c\u81ea\u7136\u662f\u6709\u5907\u800c\u6765\uff0c\u522b\u8bf4\u53e6\u6709\u5e2e\u624b\uff0c\u5355\u662f\u738b\u6c0f\u5144\u5f1f\u3001\u5468\u66fe\u4e8c\u4eba\uff0c\u90a3\u4fbf\u6781\u4e0d\u597d\u6597\uff0c\u4f46\u4ed6\u5fc3\u4e2d\u6124\u6168\u5df2\u6781\uff0c\u65e9\u5c06\u751f\u6b7b\u7f6e\u4e4b\u5ea6\u5916\u3002 \u3000\u3000\u5468\u94c1\u9e6a\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u80e1\u5927\u54e5\u65e2\u7136\u4e0d\u7ed9\u9762\u5b50\uff0c\u6211\u4eec\u8fd9\u548c\u4e8b\u4f6c\u662f\u505a\u4e0d\u6210\u5566\u3002\u51e4\u8001\u5927\u4f60\u8fd9\u4fbf\u8bf7\u7f62\uff0c\u54b1\u4eec\u8fd8\u8981\u559d\u9152\u8d4c\u94b1\u5462\u3002\u201d\u80e1\u6590\u597d\u5bb9\u6613\u89c1\u5230\u51e4\u5929\u5357\uff0c\u90a3\u91cc\u8fd8\u5bb9\u4ed6\u8131\u8eab\uff1f\u53cc\u638c\u4e00\u9519\uff0c\u4fbf\u5411\u51e4\u5929\u5357\u6251\u53bb\u3002 \u3000\u3000\u5468\u94c1\u9e6a\u7709\u5934\u4e00\u76b1\uff0c\u9053\uff1a\u201c\u8fd9\u4e5f\u672a\u514d\u592a\u8fc7\u4efd\u4e86\u5427\uff01\u201d\u5de6\u81c2\u6a2a\u4f38\u62e6\u963b\uff0c\u53f3\u624b\u5374\u7ffb\u6210\u9634\u638c\uff0c\u6697\u4f0f\u4e86\u4e00\u62db\u201c\u5012\u66f3\u4e5d\u725b\u5c3e\u201d\u7684\u64d2\u62ff\u624b\uff0c\u610f\u6b32\u6293\u4f4f\u80e1\u6590\u624b\u8155\uff0c\u5c31\u52bf\u56de\u62d6\u3002 \u3000\u3000\u80e1\u6590\u65e2\u7136\u51fa\u624b\uff0c\u65e9\u628a\u65c1\u4eba\u7684\u52a9\u62f3\u6253\u7b97\u5728\u5185\uff0c\u4f46\u5fc3\u60f3\uff1a\u201c\u4f60\u4eec\u9762\u5b50\u4e0a\u5bf9\u6211\u793c\u8c8c\u5468\u5230\uff0c\u6211\u5bf9\u4f60\u4eec\u4e5f\u5c31\u51b3\u4e0d\u5148\u884c\u51fa\u624b\u3002\u201d\u773c\u89c1\u5468\u94c1\u9e6a\u4f38\u624b\u6293\u6765\uff0c\u66f4\u4e0d\u8fd8\u624b\uff0c\u8ba9\u4ed6\u4e00\u628a\u6293\u4f4f\u8155\u9aa8\uff0c\u6263\u4f4f\u4e86\u81ea\u5df1\u7684\u8109\u95e8\u3002 \u3000\u3000\u5468\u94c1\u9e6a\u5927\u559c\uff0c\u6697\u60f3\uff1a\u201c\u79e6\u8010\u4e4b\u3001\u51e4\u8001\u5927\u4ed6\u4eec\u628a\u8fd9\u5c0f\u5b50\u7684\u672c\u4e8b\u5938\u4e0a\u4e86\u5929\u53bb\uff0c\u65e9\u77e5\u4e0d\u8fc7\u5982\u6b64\uff0c\u4f55\u5fc5\u8ddf\u4ed6\u8fd9\u822c\u4f4e\u58f0\u4e0b\u6c14\uff1f\u201d\u53e3\u4e2d\u4ecd\u662f\u8bf4\u9053\uff1a\u201c\u4e0d\u8981\u52a8\u624b\uff01\u201d\u8fd0\u52b2\u6025\u7a81\uff0c\u7a81\u7136\u95f4\u53ea\u89c9\u80e1\u6590\u7684\u8155\u9aa8\u575a\u786c\u5982\u94c1\uff0c\u731b\u5730\u91cc\u6d8c\u5230\u4e00\u80a1\u53cd\u62d6\u4e4b\u529b\uff0c\u4ee5\u786c\u5bf9\u786c\uff0c\u5468\u94c1\u9e6a\u7acb\u8db3\u4e0d\u5b9a\uff0c\u7acb\u5373\u677e\u624b\uff0c\u4e00\u4e2a\u8e09\u8dc4\uff0c\u5411\u524d\u8dcc\u51fa\u4e09\u6b65\u3002 \u3000\u3000\u8fd9\u64d2\u62ff\u624b\u62d6\u6253\uff0c\u662f\u9e70\u722a\u96c1\u884c\u95e8\u4e2d\u6700\u62ff\u624b\u5f97\u610f\u7684\u529f\u592b\uff0c\u80e1\u6590\u504f\u504f\u5c31\u5728\u8fd9\u529f\u592b\u4e0a\uff0c\u632b\u8d25\u4e86\u8fd9\u4e00\u95e8\u7684\u638c\u95e8\u5927\u5e08\u5144\u3002 \u3000\u3000\u4e24\u4eba\u4ea4\u6362\u8fd9\u4e00\u62db\uff0c\u53ea\u662f\u77ac\u606f\u95f4\u7684\u4e8b\u3002\u51e4\u5929\u5357\u5df2\u626d\u8fc7\u8eab\u8eaf\uff0c\u5411\u5916\u4fbf\u5954\u3002\u80e1\u6590\u6251\u8fc7\u53bb\u75be\u5288\u4e00\u638c\uff0c\u51e4\u5929\u5357\u56de\u624b\u62b5\u4f4f\u3002 \u3000\u3000\u66fe\u94c1\u9e25\u9053\uff1a\u201c\u597d\u597d\u513f\u7684\u559d\u9152\u8d4c\u94b1\uff0c\u4f55\u5fc5\u4f24\u4e86\u548c\u6c14\uff1f\u201d\u53f3\u624b\u4e94\u6839\u624b\u6307\u6210\u9e70\u722a\u4e4b\u52bf\uff0c\u6293\u5411\u80e1\u6590\u80cc\u5fc3\u3002\u4ed6\u4f3c\u4e4e\u662f\u597d\u610f\u529d\u67b6\uff0c\u5176\u5b9e\u5374\u662f\u65bd\u4e86\u6740\u624b\u3002\u4f46\u89c1\u80e1\u6590\u4e00\u610f\u5411\u51e4\u5929\u5357\u8fdb\u653b\uff0c\u5bf9\u8eab\u540e\u7684\u88ad\u51fb\u7adf\u4f3c\u4e0d\u77e5\uff0c\u90a3\u59d3\u8042\u7684\u5fcd\u4e0d\u4f4f\u53eb\u9053\uff1a\u201c\u80e1\u5927\u54e5\uff0c\u5c0f\u5fc3\uff01\u201d\u5693\u7684\u4e00\u54cd\uff0c\u66fe\u94c1\u9e25\u4e94\u6307\u5df2\u843d\u5728\u80e1\u6590\u80cc\u4e0a\uff0c\u4f46\u7740\u6307\u4e4b\u5904\uff0c\u4f3c\u662f\u6293\u5230\u4e86\u4e00\u5757\u53c8\u97e7\u53c8\u539a\u7684\u725b\u7b4b\u3002\u80e1\u6590\u80cc\u4e0a\u808c\u8089\u4e00\u5f39\uff0c\u4fbf\u5c06\u4ed6\u4e94\u6839\u624b\u6307\u5f39\u5f00\u3002 \u3000\u3000\u773c\u89c1\u5468\u66fe\u4e24\u4eba\u62e6\u963b\u4e0d\u4f4f\uff0c\u6bb7\u4ef2\u7fd4\u4ece\u659c\u523a\u91cc\u7a9c\u5230\uff0c\u66f4\u4e0d\u5047\u4f5c\u529d\u67b6\uff0c\u6325\u62f3\u5411\u80e1\u6590\u9762\u95e8\u6253\u53bb\u3002\u80e1\u6590\u5934\u4e00\u4f4e\uff0c\u5de6\u638c\u642d\u4e0a\u4e86\u4ed6\u7684\u80cc\u5fc3\uff0c\u5410\u6c14\u626c\u58f0\uff0c\u201c\u563f\u201d\u7684\u4e00\u58f0\uff0c\u6bb7\u4ef2\u7fd4\u7684\u8eab\u5b50\u76f4\u98de\u51fa\u53bb\uff0c\u649e\u5411\u51e4\u5929\u5357\u80cc\u5fc3\u3002\u8fd9\u4e00\u4e0b\u80e1\u6590\u539f\u6ca1\u60f3\u80fd\u649e\u5230\u51e4\u5929\u5357\uff0c\u4f46\u4ed6\u53ea\u8981\u95ea\u8eab\u907f\u5f00\uff0c\u6bb7\u4ef2\u7fd4\u7684\u8111\u888b\u4fbf\u649e\u4e0a\u4e00\u5ea7\u5047\u5c71\uff0c\u52bf\u5728\u975e\u4f38\u624b\u76f8\u6551\u4e0d\u53ef\uff0c\u8fd9\u4e48\u7f13\u5f97\u4e00\u7f13\uff0c\u4fbf\u9003\u4e0d\u8131\u4e86\u3002\u5c82\u77e5\u8fd9\u51e4\u5929\u5357\u5b9e\u5728\u8001\u5978\u5de8\u733e\uff0c\u773c\u89c1\u6bb7\u4ef2\u7fd4\u51fa\u529b\u6551\u63f4\u81ea\u5df1\uff0c\u5374\u4e0d\u987e\u4ed6\u7684\u6b7b\u6d3b\uff0c\u53cd\u800c\u5de6\u8db3\u5728\u4ed6\u80a9\u5934\u4e00\u501f\u529b\uff0c\u8dc3\u5411\u56f4\u5899\u3002\u53ea\u542c\u5f97\u7830\u7684\u4e00\u54cd\uff0c\u6bb7\u4ef2\u7fd4\u649e\u4e0a\u5047\u5c71\uff0c\u6ee1\u5934\u9c9c\u8840\uff0c\u7acb\u65f6\u6655\u6b7b\u8fc7\u53bb\u3002 \u3000\u3000\u65c1\u89c2\u4f17\u4eba\u4e2a\u4e2a\u90fd\u662f\u597d\u624b\uff0c\u51e4\u5929\u5357\u8fd9\u4e00\u4e0b\u592a\u8fc7\u5351\u9119\uff0c\u5982\u4f55\u77a7\u4e0d\u51fa\u6765\uff1f\u738b\u6c0f\u5144\u5f1f\u672c\u6b32\u51fa\u624b\uff0c\u53ea\u662f\u5fcc\u60ee\u80e1\u6590\u4e86\u5f97\uff0c\u672a\u5fc5\u8ba8\u5f97\u4e86\u597d\uff0c\u6b63\u81ea\u8fdf\u7591\uff0c\u773c\u89c1\u51e4\u5929\u5357\u53ea\u987e\u9003\u547d\uff0c\u53cd\u5bb3\u670b\u53cb\uff0c\u5144\u5f1f\u4fe9\u5bf9\u671b\u4e00\u773c\uff0c\u8138\u4e0a\u5404\u73b0\u9119\u5937\u4e4b\u8272\uff0c\u4fbf\u4e0d\u80af\u518d\u51fa\u624b\u4e86\u3002 \u3000\u3000\u80e1\u6590\u5fc3\u60f3\uff1a\u201c\u8ba9\u8fd9\u5978\u8d3c\u9003\u51fa\u4e86\u56f4\u5899\u4e4b\u5916\uff0c\u90a3\u4fbf\u591a\u4e86\u4e00\u756a\u624b\u811a\u3002\u4f55\u51b5\u56f4\u5899\u5916\u4ed6\u5b9a\u6709\u63f4\u5175\u3002\u201d\u89c1\u4ed6\u53cc\u8db3\u521a\u8981\u7ad9\u4e0a\u5899\u5934\uff0c\u7acb\u5373\u7eb5\u8eab\u8dc3\u8d77\uff0c\u62a2\u4e0a\u62e6\u622a\u3002 \u3000\u3000\u51e4\u5929\u5357\u521a\u5728\u5899\u5934\u7acb\u5b9a\uff0c\u7a81\u89c1\u8eab\u524d\u591a\u4e86\u4e00\u4eba\uff0c\u6708\u5149\u4e0b\u770b\u5f97\u660e\u767d\uff0c\u6b63\u662f\u6b7b\u5bf9\u5934\u80e1\u6590\uff0c\u8fd9\u4e00\u60ca\u5f53\u771f\u662f\u975e\u540c\u5c0f\u53ef\uff0c\u53f3\u8155\u7ffb\u5904\uff0c\u4e00\u67c4\u660e\u6643\u6643\u7684\u5315\u9996\u81ea\u4e0b\u64a9\u4e0a\uff0c\u5411\u4ed6\u5c0f\u8179\u75be\u523a\u8fc7\u53bb\u3002 \u3000\u3000\u80e1\u6590\u6025\u8d77\u5de6\u817f\uff0c\u8db3\u5c16\u8e22\u4e2d\u4ed6\u7684\u624b\u8155\uff0c\u90a3\u5315\u9996\u76f4\u98de\u8d77\u6765\uff0c\u843d\u5230\u4e86\u5899\u5916\u3002\u51e4\u5929\u5357\u51fa\u624b\u4e5f\u662f\u72e0\u8fa3\u5f02\u5e38\uff0c\u5728\u8fd9\u56f4\u5899\u9876\u4e0a\u5c3a\u8bb8\u4e4b\u5730\u8fd1\u8eab\u8089\u640f\uff0c\u62db\u6570\u66f4\u662f\u51cc\u5389\uff0c\u4e00\u5315\u9996\u6ca1\u523a\u4e2d\uff0c\u5de6\u62f3\u8ddf\u7740\u51fb\u51fa\u3002 \u3000\u3000\u80e1\u6590\u66f4\u4e0d\u56de\u624b\uff0c\u524d\u80f8\u4e00\u633a\uff0c\u8fd0\u8d77\u5185\u52b2\uff0c\u786c\u6321\u4e86\u4ed6\u8fd9\u4e00\u62f3\uff0c\u7830\u7684\u4e00\u58f0\uff0c\u51e4\u5929\u5357\u88ab\u81ea\u5df1\u7684\u62f3\u529b\u9707\u4e86\u56de\u6765\uff0c\u7acb\u8db3\u4e0d\u5b9a\uff0c\u6454\u4e0b\u56f4\u5899\u3002 \u3000\u3000\u80e1\u6590\u8ddf\u7740\u8dc3\u4e0b\uff0c\u4e3e\u8db3\u8e0f\u843d\u3002\u51e4\u5929\u5357\u4e00\u4e2a\u6253\u6eda\u907f\u8fc7\uff0c\u53cc\u8db3\u4f7f\u52b2\uff0c\u518d\u5ea6\u8dc3\u5411\u5899\u5934\u3002\u80e1\u6590\u8fd9\u4e00\u6b21\u4e0d\u5bb9\u4ed6\u518d\u5728\u5899\u5934\u7acb\u8db3\uff0c\u53cc\u624b\u4e00\u6325\uff0c\u201c\u4e00\u9e64\u51b2\u5929\u201d\uff0c\u8ddf\u7740\u7a9c\u9ad8\uff0c\u5374\u6bd4\u51e4\u5929\u5357\u9ad8\u4e86\u6570\u5c3a\uff0c\u843d\u4e0b\u65f6\u6b63\u597d\u9a91\u5728\u4ed6\u7684\u80a9\u5934\uff0c\u53cc\u817f\u631f\u4f4f\u4e86\u4ed6\u7684\u5934\u9888\u3002\u51e4\u5929\u5357\u547c\u5438\u95ed\u585e\uff0c\u81ea\u77e5\u65e0\u5e78\uff0c\u95ed\u76ee\u5f85\u6b7b\u3002 \u3000\u3000\u80e1\u6590\u53eb\u9053\uff1a\u201c\u5978\u8d3c\uff01\u4eca\u65e5\u6559\u4f60\u6076\u8d2f\u6ee1\u76c8\uff01\u201d\u63d0\u8d77\u624b\u638c\uff0c\u4fbf\u5f80\u4ed6\u5929\u7075\u76d6\u62cd\u843d\u3002 \u3000"}),showOnHomepage:!0},{name:"\u7b2c\u5341\u56db\u7ae0\u3000\u7d2b\u7f57\u886b\u52a8\u7ea2\u70db\u79fb",content:(0,a.jsx)(a.Fragment,{children:"\u7a81\u89c9\u80cc\u540e\u91d1\u5203\u63a0\u98ce\uff0c\u4e00\u4eba\u5a07\u58f0\u559d\u9053\uff1a\u201c\u624b\u4e0b\u7559\u4eba\uff01\u201d\u559d\u58f0\u672a\u6b47\uff0c\u5200\u950b\u5df2\u53ca\u540e\u9888\u3002\u8fd9\u4e00\u4e0b\u6765\u5f97\u597d\u5feb\uff0c\u80e1\u6590\u624b\u638c\u4e0d\u53ca\u62cd\u4e0b\uff0c\u6025\u5fd9\u4fa7\u5934\uff0c\u907f\u5f00\u4e86\u80cc\u540e\u523a\u6765\u7684\u4e00\u5200\uff0c\u56de\u81c2\u53cd\u624b\uff0c\u53bb\u52fe\u80cc\u540e\u654c\u4eba\u7684\u624b\u8155\u3002\u90a3\u4eba\u8eab\u624b\u77eb\u6377\uff0c\u4e00\u523a\u4e0d\u4e2d\uff0c\u7acb\u65f6\u53d8\u62db\uff0c\u5237\u5237\u4e24\u5315\u9996\uff0c\u5206\u523a\u80e1\u6590\u53cc\u80c1\u3002\u80e1\u6590\u8f6c\u4e0d\u8fc7\u8eab\u6765\uff0c\u53ea\u5f97\u7eb5\u8eab\u79bb\u4e86\u51e4\u5929\u5357\u80a9\u5934\uff0c\u5411\u524d\u4e00\u6251\u3002\u90a3\u4eba\u5982\u5f71\u968f\u5f62\uff0c\u7740\u7740\u8fdb\u903c\u3002 \u3000\u3000\u80e1\u6590\u6012\u9053\uff1a\u201c\u8881\u59d1\u5a18\uff0c\u5e72\u5417\u603b\u662f\u8ddf\u6211\u4e3a\u96be\uff1f\u201d\u56de\u8fc7\u5934\u6765\uff0c\u53ea\u89c1\u624b\u6301\u5315\u9996\u90a3\u4eba\u7d2b\u886b\u96ea\u80a4\uff0c\u5934\u5305\u9752\u5dfe\uff0c\u6b63\u662f\u8881\u7d2b\u8863\u3002 \u3000\u3000\u6708\u5149\u4e0b\u4f46\u89c1\u5979\u4f3c\u55d4\u4f3c\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u8981\u9886\u6559\u80e1\u5927\u54e5\u7a7a\u624b\u5165\u767d\u5203\u7684\u529f\u592b\uff01\u201d\u80e1\u6590\u9053\uff1a\u201c\u6765\u65e5\u65b9\u957f\uff0c\u4e0d\u5fd9\u5728\u6b64\u523b\u3002\u201d\u7eb5\u8eab\u6251\u5411\u51e4\u5929\u5357\u65f6\uff0c\u8881\u7d2b\u8863\u7331\u8eab\u800c\u4e0a\uff0c\u5315\u9996\u76f4\u6307\u4ed6\u54bd\u5589\u3002 \u3000\u3000\u8fd9\u4e00\u62db\u653b\u5176\u4e0d\u5f97\u4e0d\u6551\uff0c\u80e1\u6590\u53ea\u5f97\u6c89\u8098\u53cd\u6253\uff0c\u659c\u638c\u5288\u5979\u80a9\u5934\u3002\u970e\u65f6\u4e4b\u95f4\uff0c\u4e24\u4eba\u4ee5\u5feb\u6253\u5feb\uff0c\u4ea4\u6362\u4e86\u5341\u6765\u62db\uff0c\u4f46\u89c1\u5200\u5149\u95ea\u52a8\uff0c\u638c\u5f71\u98de\u821e\uff0c\u62db\u62db\u90fd\u77a7\u5f97\u4eba\u60ca\u5fc3\u52a8\u9b44\u3002 \u3000\u3000\u5468\u94c1\u9e6a\u3001\u66fe\u94c1\u9e25\u3001\u738b\u6c0f\u5144\u5f1f\u7b49\u90fd\u4e0d\u8bc6\u5f97\u8881\u7d2b\u8863\uff0c\u7a81\u7136\u89c1\u5979\u5728\u51e4\u5929\u5357\u547d\u5728\u9877\u523b\u4e4b\u9645\u73b0\u8eab\u76f8\u6551\uff0c\u6b66\u529f\u53c8\u5982\u6b64\u9ad8\u5f3a\uff0c\u65e0\u4e0d\u60ca\u8be7\u3002 \u3000\u3000\u4f46\u89c1\u8fd9\u4e24\u4eba\u51fa\u624b\u5947\u5feb\uff0c\u4f17\u4eba\u77a7\u5f97\u773c\u90fd\u82b1\u4e86\uff0c\u731b\u542c\u5f97\u80e1\u6590\u4e00\u58f0\u547c\u53f1\uff0c\u4e24\u4eba\u540c\u65f6\u7ffb\u4e0a\u56f4\u5899\uff0c\u8ddf\u7740\u53c8\u540c\u65f6\u8dc3\u5230\u4e86\u5899\u5916\u3002 \u3000\u3000\u8881\u7d2b\u8863\u7684\u5315\u9996\u7ffb\u98de\u51fb\u523a\uff0c\u62db\u62db\u4e0d\u79bb\u80e1\u6590\u7684\u8981\u5bb3\uff0c\u51fa\u624b\u4e4b\u72e0\u8fa3\u51cc\u5389\uff0c\u76f4\u5982\u6027\u547d\u76f8\u640f\u4e00\u822c\u3002\u80e1\u6590\u90a3\u6562\u6020\u6162\uff0c\u51dd\u795e\u63a5\u6218\uff0c\u8033\u542c\u5f97\u51e4\u5929\u5357\u7eb5\u58f0\u957f\u7b11\uff0c\u53eb\u9053\uff1a\u201c\u80e1\u5bb6\u5c0f\u5144\u5f1f\uff0c\u8001\u54e5\u54e5\u5931\u966a\u4e86\uff0c\u54b1\u4eec\u540e\u4f1a\u6709\u671f\u3002\u201d\u7b11\u58f0\u6108\u53bb\u6108\u8fdc\uff0c\u9ed1\u591c\u4e2d\u9065\u9065\u542c\u6765\uff0c\u4fbf\u4f3c\u67ad\u9e23\u3002 \u3000\u3000\u80e1\u6590\u5927\u6012\uff0c\u6025\u6b32\u62a2\u6b65\u53bb\u8ffd\uff0c\u5374\u7ed9\u8881\u7d2b\u8863\u7f20\u4f4f\u4e86\uff0c\u8131\u8eab\u4e0d\u5f97\u3002\u4ed6\u5fc3\u4e2d\u8d8a\u53d1\u605a\u6012\uff0c\u559d\u9053\uff1a\u201c\u8881\u59d1\u5a18\uff0c\u5728\u4e0b\u8ddf\u4f60\u65e0\u6028\u65e0\u4ec7\u2026\u2026\u201d\u4e00\u8a00\u672a\u6bd5\uff0c\u767d\u5149\u95ea\u52a8\uff0c\u5315\u9996\u5df2\u7136\u53ca\u8eab\u3002 \u3000\u3000\u9ad8\u624b\u8fc7\u62db\uff0c\u751f\u6b7b\u51b3\u4e8e\u4fc4\u9877\uff0c\u4e07\u4e07\u6025\u8e81\u4e0d\u5f97\uff0c\u80e1\u6590\u7684\u6b66\u529f\u53ea\u6bd4\u8881\u7d2b\u8863\u7a0d\u80dc\u534a\u7b79\uff0c\u4f46\u4e00\u4e2a\u7a7a\u624b\uff0c\u4e00\u4e2a\u6709\u5200\uff0c\u5f62\u52bf\u5df2\u7136\u626f\u5e73\uff0c\u4ed6\u773c\u7741\u7741\u7684\u89c1\u4ec7\u4eba\u518d\u6b21\u9003\u8d70\uff0c\u4e00\u5206\u5fc3\uff0c\u7adf\u7ed9\u523a\u4e2d\u4e86\u5de6\u80a9\u3002 \u3000\u3000\u54e7\u7684\u4e00\u58f0\uff0c\u5315\u9996\u5212\u7834\u80a9\u8863\uff0c\u8fd9\u65f6\u8881\u7d2b\u8863\u53f3\u624b\u53ea\u987b\u4e58\u52bf\u4e00\u6c89\uff0c\u80e1\u6590\u80a9\u5934\u52bf\u987b\u91cd\u4f24\u7b4b\u9aa8\uff0c\u90a3\u77e5\u5979\u624b\u8155\u659c\u7ffb\uff0c\u53cd\u5411\u4e0a\u6311\u3002\u80e1\u6590\u80a9\u4e0a\u53ea\u611f\u5fae\u5fae\u4e00\u51c9\uff0c\u4e1d\u6beb\u672a\u635f\uff0c\u5fc3\u4e2d\u4e00\u6014\uff1a\u201c\u4f60\u53c8\u4f55\u5fc5\u624b\u4e0b\u5bb9\u60c5\uff1f\u201d \u3000\u3000\u8881\u7d2b\u8863\u683c\u683c\u5a07\u7b11\uff0c\u5012\u8f6c\u5315\u9996\uff0c\u5411\u4ed6\u63b7\u4e86\u8fc7\u53bb\uff0c\u8ddf\u7740\u81ea\u8170\u95f4\u64a4\u51fa\u8f6f\u97ad\uff0c\u7b11\u9053\uff1a\u201c\u80e1\u5927\u54e5\uff0c\u54b1\u4eec\u771f\u5200\u771f\u67aa\u7684\u8f83\u91cf\u4e00\u573a\u3002\u201d \u3000\u3000\u80e1\u6590\u6b63\u8981\u4f38\u624b\u53bb\u63a5\u5315\u9996\uff0c\u5ffd\u542c\u5899\u5934\u7a0b\u7075\u7d20\u53eb\u9053\uff1a\u201c\u7528\u5355\u5200\u5427\uff01\u201d\u5c06\u4ed6\u5355\u5200\u63b7\u4e0b\u3002\u539f\u6765\u7a0b\u7075\u7d20\u89c1\u4ed6\u8d64\u624b\u7a7a\u62f3\uff0c\u751f\u6015\u5931\u5229\uff0c\u5df2\u5954\u8fdb\u623f\u53bb\u5c06\u4ed6\u7684\u5175\u5203\u62ff\u4e86\u51fa\u6765\u3002 \u3000\u3000\u8881\u7d2b\u8863\u53eb\u9053\uff1a\u201c\u597d\u4f53\u8d34\u7684\u59b9\u5b50\uff01\u201d\u7a81\u7136\u8f6f\u97ad\u6325\u8d77\uff0c\u63a0\u5411\u9ad8\u5899\u3002\u7a0b\u7075\u7d20\u7eb5\u8eab\u8dc3\u5165\uff0c\u8881\u7d2b\u8863\u7684\u8f6f\u97ad\u5728\u5899\u5934\u642d\u4f4f\uff0c\u4e00\u501f\u529b\uff0c\u4fbf\u5982\u4e00\u53ea\u5927\u9e1f\u822c\u98de\u4e86\u8fdb\u53bb\uff0c\u6708\u5149\u4e0b\u8863\u8882\u98d8\u98d8\u3002\u5b9b\u82e5\u4ed9\u5b50\u51cc\u7a7a\u3002\u5979\u8eab\u5b50\u5c1a\u672a\u843d\u5730\uff0c\u547c\u7684\u4e00\u97ad\uff0c\u5411\u7a0b\u7075\u7d20\u80cc\u5fc3\u51fb\u4e86\u8fc7\u53bb\uff0c\u53eb\u9053\uff1a\u201c\u7a0b\u5bb6\u59b9\u5b50\uff0c\u63a5\u6211\u4e09\u62db\u3002\u201d \u3000\u3000\u7a0b\u7075\u7d20\u4fa7\u8eab\u4f4e\u5934\uff0c\u8ba9\u8fc7\u4e86\u4e00\u97ad\u3002\u4f46\u8881\u7d2b\u8863\u53d8\u62db\u5947\u5feb\uff0c\u5de6\u56de\u53f3\u65cb\uff0c\u767b\u65f6\u5c06\u5979\u88f9\u5728\u97ad\u5f71\u4e4b\u4e2d\u3002 \u3000\u3000\u80e1\u6590\u77e5\u9053\u7a0b\u7075\u7d20\u51b3\u4e0d\u662f\u5979\u654c\u624b\uff0c\u6b64\u523b\u82e5\u53bb\u8ffd\u6740\u51e4\u5929\u5357\uff0c\u751f\u6015\u8881\u7d2b\u8863\u7adf\u4e0b\u6740\u624b\uff0c\u7eb5\u7136\u5931\u53bb\u673a\u7f18\uff0c\u4e5f\u53ea\u5f97\u7f62\u4e86\uff0c\u5f53\u4e0b\u8dc3\u8fdb\u56ed\u4e2d\uff0c\u633a\u5200\u53eb\u9053\uff1a\u201c\u4f60\u8981\u8f83\u91cf\uff0c\u4fbf\u8f83\u91cf\uff01\u201d\u8881\u7d2b\u8863\u9053\uff1a\u201c\u597d\u4f53\u8d34\u7684\u5927\u54e5\uff01\u201d\u56de\u8fc7\u8f6f\u97ad\uff0c\u6765\u5377\u80e1\u6590\u7684\u5200\u5934\u3002 \u3000\u3000\u4e24\u4eba\u5404\u4f7f\u79f0\u624b\u7684\u5175\u5203\uff0c\u8fd9\u4e00\u642d\u4e0a\u624b\uff0c\u60c5\u52bf\u4e0e\u9002\u624d\u53c8\u81ea\u4e0d\u540c\u3002\u80e1\u6590\u4f7f\u7684\u662f\u5bb6\u4f20\u80e1\u5bb6\u5200\u6cd5\uff0c\u521a\u4e2d\u6709\u67d4\uff0c\u67d4\u4e2d\u6709\u521a\uff0c\u8fc5\u6377\u65f6\u4f3c\u95ea\u7535\u5954\u96f7\uff0c\u6c89\u7a33\u5904\u5982\u6e0a\u6e1f\u5cb3\u5cd9\u3002\u8881\u7d2b\u8863\u7684\u97ad\u6cd5\u4e5f\u662f\u7eb5\u6a2a\u7075\u52a8\uff0c\u5927\u662f\u540d\u624b\u98ce\u8303\u3002\u9877\u523b\u4e4b\u95f4\uff0c\u4e24\u4eba\u5df2\u62c6\u4e86\u4e09\u5341\u4f59\u62db\uff0c\u5f53\u771f\u662f\u97ad\u6325\u53bb\u5982\u7075\u86c7\u77eb\u592d\uff0c\u5200\u780d\u6765\u82e5\u731b\u864e\u7ffb\u6251\u3002 \u3000\u3000\u79e6\u8010\u4e4b\u3001\u5468\u94c1\u9e6a\u3001\u738b\u6c0f\u5144\u5f1f\u7b49\u77a7\u7740\u65e0\u4e0d\u9a87\u7136\uff1a\u201c\u8fd9\u4e24\u4eba\u5c0f\u5c0f\u5e74\u7eaa\uff0c\u6b66\u529f\u4e0a\u7adf\u6709\u8fd9\u7b49\u9020\u8be3\uff01\u201d\u5176\u5b9e\u4e24\u4eba\u8fd9\u65f6\u6bd4\u62da\u5175\u5203\uff0c\u90fd\u8fd8\u53ea\u4f7f\u51fa\u516d\u4e03\u6210\u529f\u592b\uff0c\u80e1\u6590\u89c1\u8881\u7d2b\u8863\u6bcf\u6bcf\u5728\u8981\u7d27\u5173\u5934\u6545\u610f\u4e0d\u4e0b\u6740\u7740\uff0c\u81ea\u5df1\u5200\u4e0b\u4e5f\u5c31\u5bb9\u8ba9\u51e0\u5206\uff0c\u4e00\u9762\u6253\uff0c\u4e00\u9762\u601d\u91cf\uff1a\u201c\u5979\u5982\u6b64\u5bf9\u6211\uff0c\u5230\u5e95\u662f\u4f55\u7528\u610f\uff1f\u201d \u3000\u3000\u9002\u624d\u5468\u94c1\u9e6a\u3001\u66fe\u94c1\u9e25\u3001\u6bb7\u4ef2\u7fd4\u4e09\u4eba\u51fa\u624b\u5bf9\u4ed8\u80e1\u6590\uff0c\u5747\u6ca1\u8ba8\u5f97\u4e86\u597d\u53bb\uff0c\u4f17\u6b66\u5b98\u5fc3\u77e5\u5355\u6253\u72ec\u6597\uff0c\u4e0d\u662f\u4ed6\u5bf9\u624b\uff0c\u773c\u89c1\u8881\u7d2b\u8863\u7f20\u4f4f\u4e86\u4ed6\uff0c\u6b63\u662f\u4e0b\u624b\u7684\u826f\u673a\uff0c\u5404\u4eba\u4f7f\u4e2a\u773c\u8272\uff0c\u88c5\u4f5c\u51dd\u76ee\u89c2\u6218\uff0c\u5374\u6563\u5728\u4e24\u4eba\u8eab\u5468\uff0c\u6162\u6162\u903c\u8fd1\uff0c\u4fbf\u8981\u5408\u51fb\u80e1\u6590\u3002 \u3000\u3000\u51e1\u662f\u6b66\u5b66\u9ad8\u624b\uff0c\u51fa\u624b\u65f6\u65e0\u4e0d\u773c\u89c2\u516d\u8def\uff0c\u8033\u542c\u516b\u65b9\uff0c\u5468\u94c1\u9e6a\u7b49\u8fd9\u822c\u795e\u6001\uff0c\u80e1\u6590\u81ea\u90fd\u77a7\u5728\u773c\u91cc\uff0c\u4e0d\u7981\u6697\u6697\u7126\u6025\uff1a\u201c\u8fd9\u6279\u4eba\u4fbf\u8981\u4e00\u62e5\u800c\u4e0a\uff0c\u6211\u8131\u8eab\u867d\u7136\u4e0d\u96be\uff0c\u5374\u5206\u4e0d\u51fa\u624b\u6765\u7167\u987e\u4e8c\u59b9\u4e86\u3002\u201d \u3000\u3000\u4e00\u77a5\u4e4b\u95f4\uff0c\u89c1\u7a0b\u7075\u7d20\u7ad9\u5728\u4e00\u65c1\uff0c\u5012\u662f\u795e\u8272\u81ea\u82e5\uff0c\u5fc3\u60f3\uff1a\u201c\u53ea\u6709\u5148\u5c06\u8881\u59d1\u5a18\u6253\u9000\uff0c\u518d\u6765\u5bf9\u4ed8\u65c1\u4eba\u3002\u201d\u8a00\u5ff5\u53ca\u6b64\uff0c\u5237\u5237\u8fde\u780d\u4e09\u5200\uff0c\u5747\u662f\u80e1\u5bb6\u5200\u6cd5\u4e2d\u7684\u5389\u5bb3\u5bb6\u6570\u3002 \u3000\u3000\u8881\u7d2b\u8863\u4e00\u907f\u4e8c\u6321\uff0c\u559d\u5f69\u9053\uff1a\u201c\u597d\u5200\u6cd5\uff01\u201d\u7a81\u7136\u56de\u8fc7\u957f\u97ad\uff0c\u7adf\u4e0d\u62b5\u6321\u80e1\u6590\u523a\u5411\u81ea\u5df1\u8170\u95f4\u7684\u5200\u5c16\uff0c\u4e00\u62db\u201c\u51e4\u51f0\u4e09\u70b9\u5934\u201d\uff0c\u5411\u66fe\u94c1\u9e25\u3001\u5468\u94c1\u9e6a\u3001\u79e6\u8010\u4e4b\u4e09\u4eba\u7684\u9762\u95e8\u5404\u70b9\u4e00\u70b9\u3002 \u3000\u3000\u8fd9\u4e00\u62db\u6765\u5f97\u597d\u4e0d\u7a81\u5140\uff0c\u4e09\u4eba\u6025\u5fd9\u540e\u8dc3\uff0c\u66fe\u94c1\u9e25\u7ec8\u4e8e\u6162\u4e86\u4e00\u6b65\uff0c\u97ad\u7aef\u5728\u989d\u5934\u64e6\u8fc7\uff0c\u5e26\u51fa\u4e86\u4e00\u6761\u8840\u75d5\u3002\u4fbf\u5728\u6b64\u65f6\uff0c\u80e1\u6590\u7684\u5200\u5c16\u8ddd\u5979\u8170\u95f4\u4e5f\u5df2\u4e0d\u8fc7\u5c3a\u8bb8\uff0c\u773c\u89c1\u5979\u5ffd\u7136\u51fa\u97ad\u4e3a\u81ea\u5df1\u9000\u654c\uff0c\u5f53\u5373\u53f3\u81c2\u4e00\u7a33\uff0c\u5355\u5200\u4e0d\u8fdb\u4e0d\u9000\uff0c\u505c\u4f4f\u4e0d\u52a8\u3002\u5728\u5982\u6b64\u6025\u907d\u4e4b\u95f4\uff0c\u5c06\u5175\u5203\u7a33\u5f97\u72b9\u4f3c\u5728\u534a\u7a7a\u4e2d\u9489\u4f4f\u4e86\u4e00\u822c\uff0c\u53ef\u6bd4\u5f84\u523a\u654c\u4eba\u96be\u4e0a\u5341\u500d\u3002 \u3000\u3000\u8881\u7d2b\u8863\u4e00\u53cc\u5999\u76ee\u671b\u5b9a\u80e1\u6590\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u600e\u4e48\u4e0d\u523a\uff1f\u201d\u5ffd\u542c\u5f97\u66fe\u94c1\u9e25\u53eb\u9053\uff1a\u201c\u597d\u4f53\u8d34\u7684\u54e5\u54e5\u59b9\u59b9\u554a\uff01\u201d\u5b66\u7684\u662f\u65d7\u4eba\u6076\u5c11\u7684\u8d2b\u5634\u58f0\u8c03\u3002 \u3000\u3000\u8881\u7d2b\u8863\u4fcf\u8138\u4e00\u6c89\uff0c\u6536\u97ad\u56f4\u8170\uff0c\u5411\u80e1\u6590\u9053\uff1a\u201c\u80e1\u5927\u54e5\uff0c\u8fd9\u51e0\u4f4d\u82f1\u96c4\u597d\u6c49\uff0c\u4f60\u7ed9\u6211\u5f15\u89c1\u5f15\u89c1\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u597d\uff01\u8fd9\u4f4d\u662f\u516b\u6781\u62f3\u7684\u638c\u95e8\u4eba\u79e6\u8010\u4e4b\u79e6\u5927\u7237\uff0c\u8fd9\u4f4d\u662f\u9e70\u722a\u96c1\u884c\u95e8\u7684\u638c\u95e8\u4eba\u5468\u94c1\u9e6a\u5468\u5927\u7237\u2026\u2026\u201d\u8ddf\u7740\u5c06\u738b\u5251\u82f1\u3001\u738b\u5251\u6770\u5144\u5f1f\u3001\u66fe\u94c1\u9e25\u3001\u6c6a\u94c1\u9e57\u7b49\u4e00\u4e00\u5f15\u89c1\u4e86\u3002\u8fd9\u65f6\u738b\u5251\u6770\u5df2\u5c06\u6bb7\u4ef2\u7fd4\u6551\u9192\uff0c\u53ea\u542c\u4ed6\u4e0d\u4f4f\u53e3\u7684\u5492\u9a82\u51e4\u5929\u5357\uff0c\u8bf4\u4ec0\u4e48\u201c\u5982\u6b64\u65e0\u803b\u5351\u9119\u4e4b\u5f92\uff0c\u54b1\u54e5\u513f\u4fe9\u4e0d\u80fd\u7b97\u5b8c\u3002\u201d\u80e1\u6590\u6700\u540e\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u8881\u59d1\u5a18\u3002\u201d\u5fc3\u5ff5\u4e00\u52a8\uff0c\u53c8\u9053\uff1a\u201c\u8881\u59d1\u5a18\u662f\u5c11\u6797\u97e6\u9640\u95e8\u3001\u5e7f\u897f\u516b\u4ed9\u5251\u3001\u6e56\u5357\u6613\u5bb6\u6e7e\u4e5d\u9f99\u97ad\u4e09\u6d3e\u7684\u603b\u638c\u95e8\u3002\u201d \u3000\u3000\u4f17\u4eba\u4e00\u542c\uff0c\u90fd\u662f\u8038\u7136\u52a8\u5bb9\uff0c\u867d\u60f3\u80e1\u6590\u4e0d\u4f1a\u6253\u8bf3\uff0c\u4f46\u8138\u4e0a\u5747\u6709\u4e0d\u4fe1\u4e4b\u8272\u3002 \u3000\u3000\u8881\u7d2b\u8863\u5fae\u7b11\u9053\uff1a\u201c\u4f60\u6ca1\u8bf4\u5f97\u660e\u767d\u3002\u90af\u90f8\u5e9c\u6606\u4ed1\u5200\u3001\u5f70\u5fb7\u5e9c\u5929\u7f61\u5251\u3001\u4fdd\u5b9a\u5e9c\u54ea\u5412\u62f3\u8fd9\u4e09\u95e8\uff0c\u4e5f\u8bf7\u533a\u533a\u505a\u4e86\u638c\u95e8\u4eba\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u59d1\u5a18\u53c8\u8363\u4efb\u4e86\u4e09\u5bb6\u638c\u95e8\uff0c\u606d\u559c\u606d\u559c\u3002\u201d \u3000\u3000\u8881\u7d2b\u8863\u7b11\u9053\uff1a\u201c\u591a\u8c22\uff01\u8fd9\u4e00\u6b21\u6211\u4e0a\u5317\u4eac\u6765\uff0c\u539f\u662f\u60f3\u505a\u5341\u5bb6\u603b\u638c\u95e8\uff0c\u4f46\u6e56\u5317\u6b66\u5f53\u5c71\u7684\u65e0\u9752\u5b50\u9053\u957f\u6211\u6253\u4ed6\u4e0d\u8fc7\uff0c\u6cb3\u5357\u5c11\u6797\u5bfa\u7684\u5927\u667a\u7985\u5e08\u6211\u4e0d\u6562\u53bb\u62db\u60f9\u3002\u521a\u597d\u8fd9\u91cc\u6709\u4e09\u4f4d\u638c\u95e8\u4eba\u5728\u6b64\u3002 \u3000\u3000\u5582\uff0c\u891a\u8001\u5e08\uff0c\u4f60\u585e\u5317\u96f7\u7535\u95e8\u7684\u638c\u95e8\u8001\u5e08\u9ebb\u8001\u592b\u5b50\u5230\u4e86\u5317\u4eac\u4e48\uff1f\u201d \u3000\u3000\u90a3\u4f7f\u96f7\u9707\u6321\u7684\u59d3\u891a\u6b66\u5e08\u5355\u540d\u4e00\u4e2a\u8f70\u5b57\uff0c\u542c\u5979\u95ee\u5230\u5e08\u7236\uff0c\u8bf4\u9053\uff1a\u201c\u5bb6\u5e08\u5411\u6765\u4e0d\u6765\u5185\u5730\u8d70\u52a8\uff0c\u6709\u4ec0\u4e48\u4e8b\uff0c\u90fd\u4ea4\u7ed9\u5f1f\u5b50\u4eec\u529e\u3002\u201d \u3000\u3000\u8881\u7d2b\u8863\u9053\uff1a\u201c\u597d\uff0c\u4f60\u662f\u5927\u5e08\u5144\uff0c\u53ef\u7b97\u5f97\u4e0a\u662f\u534a\u4e2a\u638c\u95e8\u4eba\u3002\u8fd9\u4e48\u7740\uff0c\u4eca\u665a\u6211\u5c31\u593a\u4e09\u4e2a\u534a\u638c\u95e8\u4eba\u3002\u5341\u5bb6\u603b\u638c\u95e8\u505a\u4e0d\u6210\uff0c\u4e5d\u5bb6\u534a\u4e5f\u5c06\u5c31\u7740\u5bf9\u4ed8\u4e86\u3002\u201d \u3000\u3000\u6b64\u8a00\u4e00\u51fa\uff0c\u5468\u94c1\u9e6a\u7b49\u65e0\u4e0d\u53d8\u8272\u3002\u79e6\u8010\u4e4b\u62b1\u62f3\u4e00\u62f1\uff0c\u54c8\u54c8\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5c11\u6797\u97e6\u9640\u95e8\u7684\u638c\u95e8\u4e07\u9e64\u58f0\u4e07\u5927\u54e5\uff0c\u8ddf\u5728\u4e0b\u6709\u6570\u5341\u5e74\u7684\u4ea4\u60c5\uff0c\u5374\u4e0d\u77e5\u5982\u4f55\u5c06\u638c\u95e8\u4e4b\u4f4d\u4f20\u7ed9\u59d1\u5a18\u4e86\uff1f\u201d\u8881\u7d2b\u8863\u9053\uff1a\u201c\u4e07\u5927\u7237\u6b7b\u5566\uff0c\u4ed6\u5e08\u5f1f\u5218\u9e64\u771f\u6253\u4e0d\u8fc7\u6211\uff0c\u4e09\u4e2a\u5f92\u5f1f\u66f4\u662f\u8113\u5305\u3002 \u3000\u3000\u54b1\u4eec\u62f3\u811a\u5200\u67aa\u4e0a\u5206\u9ad8\u4e0b\uff0c\u8fd9\u638c\u95e8\u4e4b\u4f4d\u4e0d\u8ba9\u4e5f\u5f97\u8ba9\u3002\u79e6\u8001\u5e08\uff0c\u6211\u5148\u9886\u6559\u4f60\u7684\u516b\u6781\u62f3\u529f\u592b\uff0c\u518d\u8ddf\u5468\u8001\u5e08\u3001\u738b\u8001\u5e08\u3001\u891a\u8001\u5e08\u4ed6\u4eec\u4e09\u4f4d\u8fc7\u8fc7\u62db\u3002\u6211\u5f53\u4e0a\u4e86\u4e5d\u5bb6\u534a\u603b\u638c\u95e8\uff0c\u4e5f\u597d\u5230\u90a3\u5929\u4e0b\u638c\u95e8\u4eba\u5927\u4f1a\u4e2d\u53bb\u98ce\u5149\u98ce\u5149\u3002\u201d \u3000\u3000\u8fd9\u51e0\u53e5\u8bdd\uff0c\u7adf\u662f\u6beb\u6ca1\u5c06\u5468\u3001\u79e6\u3001\u738b\u3001\u891a\u4f17\u9ad8\u624b\u77a7\u5728\u773c\u91cc\u3002 \u3000\u3000\u5979\u8fd9\u4e48\u4e00\u53eb\u9635\uff0c\u5468\u94c1\u9e6a\u3001\u738b\u5251\u82f1\u7b49\u90fd\u662f\u5929\u4e0b\u95fb\u540d\u7684\u6b66\u5b66\u597d\u624b\uff0c\u7eb5\u7136\u547d\u4e27\u5f53\u573a\uff0c\u4e5f\u51b3\u4e0d\u80fd\u9000\u7f29\u3002 \u3000\u3000\u5468\u94c1\u9e6a\u9053\uff1a\u201c\u6211\u4eec\u9b54\u722a\u96c1\u884c\u95e8\u81ea\u5148\u5e08\u8c22\u4e16\uff0c\u5f92\u5f1f\u4eec\u4e2a\u4e2a\u4e0d\u6210\u5668\uff0c\u5148\u5e08\u7684\u529f\u592b\u5341\u6210\u4e2d\u5b66\u4e0d\u5230\u4e00\u6210\u3002\u59d1\u5a18\u80af\u8d50\u6559\u8bf2\uff0c\u655d\u6d3e\u4e0a\u4e0b\u54ea\u4e00\u4e2a\u4e0d\u611f\u5149\u5ba0\uff1f\u53ea\u662f\u5e08\u5144\u5f1f\u4eec\u90fd\u662f\u8822\u6750\uff0c\u53ea\u7ec3\u4e86\u4e9b\u5148\u5e08\u4f20\u4e0b\u7684\u529f\u592b\uff0c\u522b\u6d3e\u7684\u529f\u592b\u5374\u4e0d\u4f1a\u7ec3\u3002\u201d\u8881\u7d2b\u8863\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u81ea\u7136\u3002\u6211\u82e5\u4e0d\u4f1a\u9e70\u722a\u96c1\u884c\u95e8\u7684\u529f\u592b\uff0c\u600e\u80fd\u5f53\u5f97\u9e70\u722a\u96c1\u884c\u95e8\u7684\u638c\u95e8\uff1f\u5468\u8001\u5e08\u5927\u53ef\u653e\u5fc3\u3002\u201d \u3000\u3000\u5468\u94c1\u9e6a\u548c\u66fe\u94c1\u9e25\u90fd\u662f\u6c14\u9ec4\u4e86\u8138\uff0c\u5e08\u5144\u5f1f\u5bf9\u671b\u4e00\u773c\uff0c\u5747\u60f3\uff1a \u3000\u3000\u201c\u4fbf\u662f\u518d\u5f3a\u7684\u9ad8\u624b\uff0c\u4e5f\u4ece\u6ca1\u6562\u8f7b\u89c6\u9e70\u722a\u96c1\u884c\u95e8\u4e86\u3002\u4f60\u4ed7\u7740\u8c01\u7684\u52bf\u5934\uff0c\u5230\u5317\u4eac\u57ce\u6765\u6492\u91ce\uff1f\u201d \u3000\u3000\u4ed6\u4eec\u6536\u4e86\u51e4\u5929\u5357\u7684\u91cd\u793c\uff0c\u4e3a\u4ed6\u51fa\u5934\u6392\u89e3\uff0c\u6ca1\u80fd\u529e\u6210\uff0c\u4e5f\u4e0d\u8fc7\u626b\u5174\u800c\u5df2\uff0c\u6bd5\u7adf\u4e8b\u4e0d\u5e72\u5df1\uff0c\u5e76\u4e0d\u600e\u4e48\u653e\u5728\u5fc3\u4e0a\u3002\u53ef\u662f\u8fd9\u59d1\u5a18\u7adf\u6562\u6765\u786c\u62a2\u638c\u95e8\u4e4b\u4f4d\uff0c\u5982\u6b64\u6b3a\u4e0a\u5934\u6765\uff0c\u5c82\u53ef\u4e0d\u8ba4\u771f\u5bf9\u4ed8\uff1f \u3000\u3000\u79e6\u8010\u4e4b\u77e5\u9053\u4eca\u665a\u5df2\u975e\u52a8\u624b\u4e0d\u53ef\uff0c\u9002\u624d\u89c1\u8881\u7d2b\u8863\u7684\u529f\u592b\u548c\u80e1\u6590\u662f\u5728\u4f2f\u4ef2\u4e4b\u95f4\uff0c\u81ea\u5df1\u5374\u66fe\u8d25\u5728\u80e1\u6590\u624b\u4e0b\uff0c\u8981\u60f3\u8ba8\u4e00\u4e2a\u5de7\uff0c\u8ba9\u5979\u5148\u6597\u5468\u738b\u8bf8\u4eba\uff0c\u8017\u5c3d\u4e86\u529b\u6c14\uff0c\u81ea\u5df1\u518d\u6765\u6361\u4fbf\u5b9c\uff0c\u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u5468\u8001\u5e08\u3001\u738b\u8001\u5e08\u7684\u529f\u592b\u6bd4\u5144\u5f1f\u6df1\u5f97\u591a\uff0c\u5144\u5f1f\u8eb2\u5728\u540e\u9762\u5427\uff01\u201d \u3000\u3000\u8881\u7d2b\u8863\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u8bf4\u6211\u4e5f\u77e5\u9053\uff0c\u4f60\u7684\u529f\u592b\u4e0d\u5982\u4ed6\u4eec\uff0c\u6211\u8981\u6311\u5f31\u7684\u5148\u6253\uff0c\u597d\u7559\u4e0b\u529b\u6c14\uff0c\u5bf9\u4ed8\u5f3a\u7684\u3002\u5916\u8fb9\u8349\u5730\u4e0a\u6ed1\u811a\uff0c\u54b1\u4eec\u5230\u4ead\u4e2d\u8fc7\u62db\u3002\u4e0a\u6765\u5427\uff01\u201d\u8eab\u5f62\u4e00\u6643\uff0c\u8fdb\u4e86\u4ead\u5b50\uff0c\u53cc\u8db3\u5e76\u7acb\uff0c\u6c89\u80a9\u584c\u80ef\uff0c\u4e94\u6307\u5e76\u62e2\uff0c\u624b\u5fc3\u5411\u4e0a\uff0c\u5728\u5c0f\u8179\u524d\u865a\u865a\u6258\u4f4f\uff0c\u6b63\u662f\u201c\u516b\u6781\u62f3\u201d\u7684\u8d77\u624b\u5f0f\u201c\u6000\u4e2d\u62b1\u6708\u201d\u3002 \u3000\u3000\u79e6\u8010\u4e4b\u5403\u4e86\u4e00\u60ca\uff1a\u201c\u672c\u6d3e\u6b66\u529f\u5411\u6765\u6d41\u4f20\u4e0d\u5e7f\uff0c\u4f46\u8fd9\u4e00\u62db\u2018\u6000\u4e2d\u62b1\u6708\u2019\uff0c\u5de6\u80a9\u4f4e\uff0c\u53f3\u80a9\u9ad8\uff0c\u5de6\u624b\u659c\uff0c\u53f3\u624b\u6b63\uff0c\u663e\u662f\u5df2\u5f97\u672c\u6d3e\u7684\u5fc3\u4f20\uff0c\u5979\u5374\u4ece\u4f55\u5b66\u6765\uff1f\u201d\u5411\u80e1\u6590\u659c\u7768\u4e00\u773c\uff0c\u53c8\u60f3\uff1a\u201c\u90a3\u65e5\u6211\u8ddf\u4ed6\u52a8\u624b\uff0c\u5f53\u7136\u4e0d\u4f7f\u8d77\u624b\u5f0f\uff0c\u540e\u6765\u548c\u4ed6\u8bb2\u8bba\u672c\u95e8\u62f3\u6cd5\uff0c\u8fd9\u4e00\u62db\u4e5f\u672a\u63d0\u5230\u3002\u81ea\u4e0d\u662f\u4ed6\u4f20\u7ed9\u8fd9\u5973\u5b50\u4e86\u3002\u201d\u5fc3\u4e2d\u60ca\u7591\uff0c\u8138\u4e0a\u5374\u4e0d\u52a8\u58f0\u8272\uff0c\u8bf4\u9053\uff1a\u201c\u65e2\u662f\u5982\u6b64\uff0c\u5f85\u5c0f\u8001\u513f\u642c\u5f00\u684c\u5b50\u51f3\u5b50\uff0c\u514d\u5f97\u788d\u624b\u788d\u811a\u3002\u201d \u3000\u3000\u8881\u7d2b\u8863\u9053\uff1a\u201c\u79e6\u8001\u5e08\u8fd9\u8bdd\u5dee\u4e86\u3002\u672c\u95e8\u62f3\u6cd5\u2018\u7ffb\u624b\u3001\u63c9\u8155\u3001\u5bf8\u6073\u3001\u6296\u5c55\u2019\u516b\u6781\uff0c\u2018\u6402\u3001\u6253\u3001\u817e\u3001\u5c01\u3001\u8e22\u3001\u8e6c\u3001\u626b\u3001\u5366\u2019\u516b\u5f0f\uff0c\u53d8\u5316\u4e3a\u2018\u95ea\u3001\u957f\u3001\u8dc3\u3001\u8eb2\u3001\u62d7\u3001\u5207\u3001\u95ed\u3001\u62e8\u2019\u516b\u6cd5\uff0c\u56db\u5341\u4e5d\u8def\u516b\u6781\u62f3\uff0c\u8bb2\u7a76\u7684\u662f\u5c0f\u5de7\u817e\u632a\uff0c\u82e5\u662f\u5acc\u8fd9\u684c\u5b50\u51f3\u5b50\u788d\u4e8b\uff0c\u5f53\u771f\u4e0e\u654c\u4eba\u6027\u547d\u76f8\u640f\u4e4b\u65f6\uff0c\u96be\u9053\u4e5f\u53eb\u654c\u4eba\u5148\u642c\u5f00\u684c\u6905\u5417\uff1f\u201d\u5979\u8fd9\u756a\u8bdd\u5b9b\u7136\u662f\u638c\u95e8\u4eba\u6559\u8bad\u672c\u95e8\u5c0f\u8f88\u7684\u53e3\u543b\uff0c\u800c\u516b\u6781\u62f3\u7684\u8bf8\u79cd\u6cd5\u8bc0\uff0c\u5374\u53c8\u8bf4\u5f97\u4e00\u5b57\u4e0d\u9519\u3002 \u3000\u3000\u79e6\u8010\u4e4b\u8138\u4e0a\u4e00\u7ea2\uff0c\u66f4\u4e0d\u7b54\u8bdd\uff0c\u5f2f\u8170\u8dc3\u8fdb\u4ead\u4e2d\uff0c\u4e00\u62db\u201c\u63a8\u5c71\u5f0f\u201d\uff0c\u5de6\u638c\u63a8\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u8881\u7d2b\u8863\u6447\u4e86\u6447\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u62db\u4e0d\u597d\uff01\u201d\u66f4\u4e0d\u62db\u67b6\uff0c\u53ea\u662f\u5411\u5de6\u8e0f\u4e86\u4e00\u6b65\uff0c\u79e6\u8010\u4e4b\u8eab\u524d\u4fbf\u662f\u684c\u5b50\u6321\u4f4f\uff0c\u8fd9\u4e00\u638c\u63a8\u4e0d\u5230\u5979\u8eab\u4e0a\u3002\u4ed6\u53d8\u62db\u5374\u4e5f\u8fc5\u901f\uff0c\u201c\u62bd\u6b65\u7ffb\u9762\u9524\u201d\u3001\u201c\u9e5e\u5b50\u7ffb\u8eab\u201d\u3001\u201c\u5288\u5366\u638c\u201d\uff0c\u8fde\u4f7f\u4e09\u8bb0\u7edd\u62db\u3002\u8881\u7d2b\u8863\u53f3\u8db3\u5fae\u63d0\uff0c\u5de6\u81c2\u7f6e\u4e8e\u53f3\u81c2\u4e0a\u4ea4\u53c9\u8f6e\u6253\uff0c\u7ffb\u6210\u9633\u62f3\uff0c\u8ddf\u7740\u4fbf\u5feb\u5982\u7535\u95ea\u822c\u4ee5\u9634\u62f3\u6253\u51fa\uff0c\u6b63\u662f\u516b\u6781\u62f3\u4e2d\u7684\u7b2c\u56db\u5341\u56db\u5f0f\u201c\u53cc\u6253\u5947\u95e8\u201d\uff0c\u8fd9\u539f\u662f\u79e6\u8010\u4e4b\u7684\u5f97\u610f\u62db\u6570\uff0c\u53ef\u662f\u8881\u7d2b\u8863\u8fd9\u4e00\u62db\u51fa\u5f97\u5feb\u6781\uff0c\u79e6\u8010\u4e4b\u731d\u4e0d\u53ca\u9632\uff0c\u6025\u5fd9\u659c\u8eab\u95ea\u907f\uff0c\u7830\u7684\u4e00\u4e0b\uff0c\u649e\u5230\u4e86\u684c\u4e0a\uff0c\u684c\u4e0a\u8336\u7897\u767b\u65f6\u6253\u7ffb\u4e86\u4e09\u53ea\u3002\u8881\u7d2b\u8863\u7b11\u9053\uff1a\u201c\u5c0f\u5fc3\uff01\u201d\u5de6\u7f20\u8eab\u3001\u53f3\u7f20\u8eab\u3001\u5de6\u53cc\u649e\u3001\u53f3\u53cc\u649e\u3001\u4e00\u6b65\u4e09\u73af\u3001\u4e09\u6b65\u4e5d\u8f6c\uff0c\u90a3\u516b\u6781\u62f3\u7684\u62db\u6570\u4fbf\u5982\u96e8\u70b9\u822c\u6253\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u79e6\u8010\u4e4b\u594b\u529b\u62db\u67b6\uff0c\u773c\u770b\u5979\u4f7f\u7684\u62db\u6570\u56fa\u662f\u672c\u95e8\u62f3\u6cd5\uff0c\u4f46\u5ffd\u5feb\u5ffd\u6162\u3001\u504f\u5de6\u504f\u53f3\uff0c\u5374\u53c8\u4e0e\u672c\u95e8\u529f\u592b\u5927\u4e0d\u76f8\u540c\u3002\u8881\u7d2b\u8863\u9053\uff1a\u201c\u4f60\u600e\u5730\u53ea\u62db\u67b6\uff0c\u4e0d\u8fd8\u624b\uff1f\u4f60\u4f7f\u7684\u662f\u516b\u6781\u62f3\uff0c\u53ef\u4e0d\u662f\u6328\u63cd\u62f3\uff01\u201d \u3000\u3000\u79e6\u8010\u4e4b\u9a82\u9053\uff1a\u201c\u5c0f\u8d31\u4eba\uff01\u201d\u4e00\u62db\u201c\u9752\u9f99\u51fa\u6c34\u201d\uff0c\u5de6\u62f3\u6210\u94a9\uff0c\u53f3\u62f3\u547c\u7684\u4e00\u58f0\u6253\u4e86\u51fa\u53bb\u3002\u8881\u7d2b\u8863\u5e94\u4ee5\u4e00\u62db\u201c\u9501\u624b\u6512\u62f3\u201d\uff0c\u7a81\u7136\u53f3\u8098\u4e00\u6446\uff0c\u7ffb\u624b\u6293\u4f4f\u4e86\u4ed6\u7684\u53f3\u8155\uff0c\u5411\u4ed6\u80cc\u4e0a\u626d\u8f6c\uff0c\u5de6\u624b\u540c\u65f6\u4e0a\u524d\uff0c\u56db\u6307\u524d\u3001\u62c7\u6307\u540e\uff0c\u5df2\u62ff\u4f4f\u4e86\u4ed6\u7684\u201c\u80a9\u8d1e\u7a74\u201d\uff0c\u987a\u52bf\u5411\u524d\u4e00\u9001\uff0c\u5c06\u4ed6\u6309\u5230\u4e86\u684c\u4e0a\uff0c\u6b63\u597d\u5c06\u4ed6\u5634\u5df4\u6309\u5230\u4e86\u8336\u7897\u4e0a\uff0c\u559d\u9053\uff1a\u201c\u5403\u8336\uff01\u201d \u3000\u3000\u5979\u4f7f\u8fd9\u4e00\u624b\u201c\u5206\u7b4b\u9519\u9aa8\u624b\u201d\u672c\u6765\u5e73\u5e73\u65e0\u5947\uff0c\u51e0\u4e4e\u4e0d\u8bba\u90a3\u4e00\u95e8\u90a3\u4e00\u6d3e\u90fd\u4f1a\u7ec3\u5230\uff0c\u53ea\u662f\u51fa\u624b\u5947\u901f\uff0c\u79e6\u8010\u4e4b\u624b\u8155\u521a\u4e00\u78b0\u5230\u5979\u7684\u624b\u6307\uff0c\u5168\u8eab\u5df2\u88ab\u5236\u4f4f\uff0c\u4e0d\u7981\u53c8\u60ca\u53c8\u6012\uff0c\u53c8\u9a82\u9053\uff1a\u201c\u5c0f\u8d31\u4eba\uff01\u201d \u3000\u3000\u8881\u7d2b\u8863\u53cc\u624b\u4f7f\u4e2a\u51b7\u52b2\uff0c\u5580\u5587\u4e00\u58f0\uff0c\u79e6\u8010\u4e4b\u53f3\u80a9\u5173\u8282\u7acb\u65f6\u8131\u81fc\u3002\u8881\u7d2b\u8863\u653e\u5f00\u4ed6\u624b\u8155\uff0c\u5750\u5728\u5706\u51f3\u4e0a\u5fae\u5fae\u51b7\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u638c\u95e8\u4eba\u4e4b\u4f4d\u4f60\u8ba9\u662f\u4e0d\u8ba9\uff1f\u201d\u79e6\u8010\u4e4b\u53ea\u75bc\u5f97\u6ee1\u989d\u90fd\u662f\u51b7\u6c57\uff0c\u4e00\u8a00\u4e0d\u53d1\uff0c\u5feb\u6b65\u51fa\u4ead\u3002 \u3000\u3000\u738b\u5251\u82f1\u4e0a\u524d\u5de6\u624b\u6258\u4f4f\u4ed6\u53f3\u81c2\uff0c\u53f3\u624b\u6293\u4f4f\u4ed6\u5934\u9888\uff0c\u4e00\u63a8\u4e00\u9001\uff0c\u5c06\u4ed6\u80a9\u5934\u5173\u8282\u8fd8\u5165\u81fc\u7a9d\uff0c\u8f6c\u5934\u8bf4\u9053\uff1a\u201c\u8881\u59d1\u5a18\u7684\u516b\u6781\u62f3\u529f\u592b\u679c\u7136\u795e\u5999\uff0c\u6211\u9886\u6559\u9886\u6559\u4f60\u7684\u516b\u5c01\u638c\u3002\u201d\u8bf4\u7740\u8e0f\u6b65\u8fdb\u4ead\u3002 \u3000\u3000\u8881\u7d2b\u8863\u89c1\u4ed6\u6b65\u5c65\u51dd\u7a33\uff0c\u5fc3\u77e5\u662f\u4e2a\u52b2\u654c\u3002\u672c\u6765\u51e1\u662f\u7ec3\u201c\u6e38\u8eab\u516b\u5366\u638c\u201d\u4e4b\u4eba\uff0c\u5fc5\u5b9a\u6b65\u6cd5\u98d8\u9038\uff0c\u884c\u8def\u72b9\u5982\u8db3\u4e0d\u70b9\u5730\u4e00\u822c\uff0c\u4f46\u4ed6\u811a\u6b65\u843d\u5730\u6781\u91cd\uff0c\u5c18\u571f\u98de\u626c\uff0c\u90a3\u662f\u201c\u81ea\u91cd\u81f3\u8f7b\u3001\u81f3\u8f7b\u8fd4\u91cd\u201d\uff0c\u6839\u57fa\u575a\u5b9e\u65e0\u6bd4\uff0c\u4ed6\u6570\u5341\u5e74\u7684\u529f\u529b\uff0c\u51b3\u975e\u81ea\u5df1\u6240\u80fd\u671b\u5176\u9879\u80cc\u3002 \u3000\u3000\u80e1\u6590\u5feb\u6b65\u8d70\u5230\u4ead\u4e2d\uff0c\u62ff\u8d77\u8336\u676f\u559d\u4e86\u4e00\u53e3\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u6b64\u4eba\u5389\u5bb3\uff0c\u4e0d\u53ef\u8f7b\u654c\u3002\u201d\u8881\u7d2b\u8863\u773c\u76ae\u4f4e\u5782\uff0c\u7ec6\u58f0\u9053\uff1a\u201c\u6211\u591a\u6b21\u574f\u4f60\u5927\u4e8b\uff0c\u4f60\u4e0d\u602a\u6211\u5417\uff1f\u201d\u8fb9\u4e00\u53e5\u8bdd\u80e1\u6590\u5374\u7b54\u4e0d\u4e0a\u6765\uff0c\u8bf4\u662f\u4e0d\u602a\uff0c\u662f\u5979\u63a5\u8fde\u4e09\u6b21\u5c06\u51e4\u5929\u5357\u4ece\u81ea\u5df1\u624b\u5e95\u4e0b\u6551\u51fa\uff1b\u8bf4\u662f\u602a\u5979\u7f62\uff0c\u77a7\u7740\u5979\u82e5\u6709\u60c5\u3001\u82e5\u65e0\u60c5\u7684\u773c\u6ce2\uff0c\u5374\u53c8\u600e\u80fd\u602a\u5f97\uff1f \u3000\u3000\u8881\u7d2b\u8863\u89c1\u80e1\u6590\u8d70\u5165\u4ead\u6765\u6559\u81ea\u5df1\u63d0\u9632\uff0c\u65e9\u662f\u82b3\u5fc3\u5927\u6170\uff0c\u5979\u672c\u5fc3\u5b58\u60ca\u7591\uff0c\u751f\u6015\u6597\u4e0d\u8fc7\u8fd9\u4f4d\u516b\u5366\u95e8\u7684\u9ad8\u624b\uff0c\u8fd9\u65f6\u7cbe\u795e\u4e00\u632f\uff0c\u52c7\u6c14\u500d\u589e\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u4f60\u653e\u5fc3\uff01\u201d\u8db3\u5c16\u4e00\u767b\uff0c\u8dc3\u4e0a\u4e00\u5f20\u5706\u51f3\uff0c\u8bf4\u9053\uff1a\u201c\u738b\u8001\u5e08\uff0c\u516b\u5366\u95e8\u7684\u529f\u592b\uff0c\u8bb2\u7a76\u8db3\u8e0f\u516b\u5366\u65b9\u4f4d\uff0c\u4e7e\u3001\u5764\u3001\u5dfd\u3001\u574e\u3001\u9707\u3001\u5151\u3001\u79bb\u3001\u826e\uff0c\u54b1\u4eec\u4fbf\u5728\u8fd9\u4e9b\u51f3\u4e0a\u8fc7\u8fc7\u62db\u3002\u201d\u738b\u5251\u82f1\u9053\uff1a\u201c\u597d\uff01\u201d\u6162\u6162\u8e0f\u4e0a\u5706\u51f3\uff0c\u53cc\u624b\u4e92\u5708\uff0c\u4e00\u638c\u9886\u524d\uff0c\u4e00\u638c\u5c45\u540e\u3002\u80e1\u6590\u53c8\u5411\u8881\u7d2b\u8863\u77a7\u4e86\u4e00\u773c\uff0c\u9000\u51fa\u4ead\u5b50\u3002 \u3000\u3000\u8881\u7d2b\u8863\u9053\uff1a\u201c\u7d20\u95fb\u516b\u5366\u95e8\u4e2d\u738b\u6c0f\u5144\u5f1f\u82f1\u6770\u9f50\u540d\uff0c\u5f85\u4f1a\u738b\u8001\u5e08\u8d25\u4e86\u4e4b\u540e\uff0c\u4ee4\u5f1f\u8fd8\u6253\u4e0d\u6253\u5462\uff1f\u201d \u3000\u3000\u738b\u5251\u82f1\u751f\u6027\u51dd\u91cd\uff0c\u542c\u4e86\u8fd9\u8bdd\u5374\u4e5f\u5fcd\u4e0d\u4f4f\u6c14\u5f80\u4e0a\u51b2\uff0c\u4f9d\u5979\u8bf4\u6765\uff0c\u4f3c\u4e4e\u8fd8\u6ca1\u52a8\u624b\uff0c\u81ea\u5df1\u5df2\u7136\u8d25\u5b9a\u3002\u4ed6\u672c\u5c31\u4e0d\u5584\u8a00\u8f9e\uff0c\u76db\u6012\u4e4b\u4e0b\uff0c\u66f4\u662f\u7ed3\u7ed3\u5df4\u5df4\u7684\u8bf4\u4e0d\u51fa\u8bdd\u3002\u738b\u5251\u6770\u6012\u9053\uff1a\u201c\u5c0f\u4e2b\u5934\u80e1\u8bf4\u516b\u9053\uff0c\u4f60\u53ea\u987b\u5728\u6211\u5927\u54e5\u624b\u4e0b\u63a5\u5f97\u4e00\u767e\u62db\uff0c\u54b1\u5144\u5f1f\u4fe9\u4ece\u6b64\u4e0d\u4f7f\u516b\u5366\u638c\u3002\u201d\u987b\u77e5\u738b\u6c0f\u5144\u5f1f\u671b\u91cd\u6b66\u6797\uff0c\u5bfb\u5e38\u6b66\u5e08\u8fde\u4ed6\u4eec\u7684\u5341\u62db\u516b\u62db\u4e5f\u63a5\u4e0d\u4f4f\u3002\u738b\u5251\u6770\u4e00\u51fa\u53e3\u7adf\u8bf4\u5230\u4e00\u767e\u62db\uff0c\u5374\u4e5f\u662f\u4e1d\u6beb\u6ca1\u5c0f\u89d1\u4e86\u5979\u3002 \u3000\u3000\u8881\u7d2b\u8863\u659c\u773c\u76f8\u7768\uff0c\u51b7\u51b7\u5730\u9053\uff1a\u201c\u6211\u51fb\u8d25\u4ee4\u5144\u4e4b\u540e\uff0c\u7b97\u4e0d\u7b97\u516b\u5366\u95e8\u7684\u638c\u95e8\uff1f\u4f60\u8fd8\u6253\u4e0d\u6253\uff1f\u201d\u738b\u5251\u6770\u9053\uff1a\u201c\u4f60\u5148\u5439\u4ec0\u4e48\uff1f\u6253\u5f97\u8d62\u6211\u54e5\u54e5\u518d\u8bf4\u4e0d\u8fdf\u3002\u201d\u8881\u7d2b\u8863\u9053\uff1a\u201c\u6211\u4fbf\u662f\u8981\u95ee\u4e00\u4e2a\u660e\u767d\u3002\u201d \u3000\u3000\u738b\u5251\u6770\u5c1a\u672a\u7b54\u8bdd\uff0c\u738b\u5251\u82f1\u95ee\u9053\uff1a\u201c\u5c0a\u5e08\u662f\u8c01\uff1f\u201d\u8881\u7d2b\u8863\u9053\uff1a\u201c\u4f60\u95ee\u6211\u5e08\u627f\u5e72\u5417\uff1f\u201d\u5979\u4e4c\u6e9c\u6e9c\u7684\u773c\u73e0\u9aa8\u788c\u4e00\u8f6c\uff0c\u5df2\u660e\u5176\u610f\uff0c\u8bf4\u9053\uff1a\u201c\u55ef\uff0c\u738b\u8001\u5e08\u662f\u52a8\u4e86\u771f\u6012\uff0c\u8981\u4e0b\u6740\u624b\uff0c\u6240\u4ee5\u5148\u95ee\u4e00\u95ee\u6211\u5e08\u7236\u3002\u6211\u5e08\u7236\u540d\u5934\u592a\u54cd\uff0c\u8bf4\u51fa\u6765\u5413\u574f\u4e86\u4f60\u3002\u6211\u4e0d\u62ac\u5e08\u7236\u51fa\u6765\u3002\u4f60\u5c3d\u7ba1\u4f7f\u4f60\u516b\u5366\u95e8\u7684\u7edd\u62db\u3002\u5e38\u8a00\u9053\u4e0d\u77e5\u8005\u4e0d\u7f6a\uff0c\u4f60\u4fbf\u6253\u6b7b\u4e86\u6211\uff0c\u6211\u5e08\u7236\u4e5f\u4e0d\u602a\u4f60\u3002\u201d \u3000\u3000\u8fd9\u51e0\u53e5\u8bdd\u6b63\u8bf4\u4e2d\u4e86\u738b\u5251\u82f1\u7684\u5fc3\u4e8b\uff0c\u4ed6\u89c1\u8881\u7d2b\u8863\u5148\u548c\u80e1\u6590\u76f8\u6597\uff0c\u8ddf\u7740\u5236\u4f4f\u79e6\u8010\u4e4b\uff0c\u51fa\u624b\u7740\u5b9e\u4e0d\u4fd7\uff0c\u5b9a\u662f\u5927\u6709\u6765\u5934\uff0c\u82e5\u662f\u4e0b\u91cd\u624b\u4f24\u4e86\u5979\uff0c\u5979\u5e08\u7236\u65e5\u540e\u627e\u573a\uff0c\u591a\u534a\u6781\u96be\u5e94\u4ed8\uff0c\u542c\u5979\u8fd9\u822c\u8bf4\uff0c\u4fbf\u9053\uff1a\u201c\u8fd9\u91cc\u5404\u4f4d\u90fd\u662f\u89c1\u8bc1\u3002\u201d\u547c\u7684\u4e00\u638c\uff0c\u8fce\u9762\u51fb\u51fa\uff0c\u638c\u529b\u672a\u65bd\uff0c\u8eab\u968f\u638c\u8d77\uff0c\u8e0f\u5764\u5954\u79bb\uff0c\u8db3\u4e0b\u5df2\u79fb\u52a8\u4e86\u65b9\u4f4d\u3002\u522b\u77a7\u4ed6\u8eab\u8eaf\u80a5\u5927\uff0c\u516b\u5366\u95e8\u8f7b\u529f\u4e00\u4f7f\u51fa\uff0c\u7adf\u5982\u98de\u71d5\u63a0\u6ce2\u4e00\u822c\u3002 \u3000\u3000\u8881\u7d2b\u8863\u659c\u638c\u5378\u529b\uff0c\u81ea\u826e\u8ffd\u9707\uff0c\u624b\u4e0a\u4f7f\u7684\u56fa\u662f\u516b\u5366\u638c\uff0c\u811a\u4e0b\u8e0f\u7684\u4e5f\u662f\u516b\u5366\u65b9\u4f4d\u3002\u738b\u5251\u82f1\u8fde\u5288\u6570\u638c\uff0c\u90fd\u7ed9\u5979\u4e00\u4e00\u5378\u5f00\u3002\u4e24\u4eba\u7ed5\u7740\u5706\u684c\uff0c\u5728\u5341\u4e8c\u53ea\u77f3\u51f3\u4e0a\u5954\u9a70\u65cb\u8f6c\uff0c\u5012\u4f3c\u5c0f\u513f\u6349\u8ff7\u85cf\u4e00\u822c\uff0c\u4f46\u8d8a\u8f6c\u8d8a\u5feb\uff0c\u8863\u895f\u751f\u98ce\u3002 \u3000\u3000\u738b\u5251\u82f1\u5fc3\u60f3\uff1a\u201c\u8fd9\u4e2b\u5934\u5fc3\u601d\u7075\u5de7\uff0c\u8bf1\u5f97\u6211\u5728\u77f3\u51f3\u4e0a\u8ddf\u5979\u9694\u684c\u6362\u638c\u3002\u5979\u638c\u529b\u539f\u672c\u4e0d\u80fd\u8ddf\u6211\u76f8\u6bd4\uff0c\u4f46\u4e2d\u95f4\u6321\u7740\u4e00\u5f20\u5706\u684c\uff0c\u4fbf\u4e0d\u6015\u6211\u6c89\u731b\u7684\u638c\u529b\u3002\u201d\u53c8\u60f3\uff1a\u201c\u8fd9\u4e2b\u5934\u6b66\u529f\u751a\u6742\uff0c\u5c45\u7136\u5c06\u6211\u95e8\u4e2d\u7684\u516b\u5366\u638c\u4f7f\u5f97\u5934\u5934\u662f\u9053\uff0c\u6211\u4f55\u5fc5\u7528\u5bfb\u5e38\u638c\u6cd5\u8ddf\u5979\u7ea0\u7f20\uff1f\u201d\u731b\u5730\u91cc\u4e00\u58f0\u957f\u5578\uff0c\u811a\u6b65\u9519\u4e71\uff0c\u624b\u638c\u6b6a\u659c\uff0c\u7adf\u4f7f\u51fa\u4e86\u4ed6\u7236\u4eb2\u5a01\u9707\u6cb3\u6714\u738b\u7ef4\u626c\u7684\u5bb6\u4f20\u7edd\u6280\u201c\u516b\u9635\u516b\u5366\u638c\u201d\u6765\u3002 \u3000\u3000\u8fd9\u4e00\u8def\u638c\u6cd5\u738b\u7ef4\u626c\u53ea\u4f20\u4e24\u4e2a\u513f\u5b50\uff0c\u8fde\u5916\u59d3\u7684\u5f1f\u5b50\u5982\u5546\u5251\u9e23\u7b49\u4e5f\u5747\u4e0d\u4f20\uff0c\u90a3\u662f\u5728\u516b\u5366\u638c\u4e2d\u5939\u4e86\u516b\u9635\u56fe\u4e4b\u6cd5\uff1a\u5929\u9635\u5c45\u4e7e\u4e3a\u5929\u95e8\uff0c\u5730\u9635\u5c45\u5764\u4e3a\u5730\u95e8\uff0c\u98ce\u9635\u5c45\u5dfd\u4e3a\u98ce\u95e8\uff0c\u4e91\u9635\u5c45\u9707\u4e3a\u4e91\u95e8\uff0c\u98de\u9f99\u5c45\u574e\u4e3a\u98de\u9f99\u95e8\uff0c\u6b66\u7ffc\u5c45\u5151\u4e3a\u6b66\u7ffc\u95e8\uff0c\u9e1f\u7fd4\u5c45\u79bb\u4e3a\u9e1f\u7fd4\u95e8\uff0c\u873f\u76d8\u5c45\u826e\u4e3a\u873f\u76d8\u95e8\uff1b\u5929\u5730\u98ce\u4e91\u4e3a\u56db\u6b63\u95e8\uff0c\u9f99\u864e\u9e1f\u873f\u4e3a\u56db\u5947\u95e8\uff1b\u4e7e\u5764\u826e\u5dfd\u4e3a\u9616\u95e8\uff0c\u574e\u79bb\u9707\u5151\u4e3a\u5f00\u95e8\u3002\u8fd9\u56db\u6b63\u56db\u5947\uff0c\u56db\u5f00\u56db\u9616\uff0c\u7528\u5230\u6b66\u5b66\u4e4b\u4e0a\uff0c\u970e\u65f6\u4e4b\u95f4\u53d8\u5316\u5947\u5e7b\uff0c\u867d\u662f\u5728\u5c0f\u5c0f\u4e00\u4e2a\u51c9\u4ead\u4e4b\u4e2d\uff0c\u9690\u9690\u6709\u5e03\u9635\u800c\u6218\u4e4b\u610f\u3002 \u3000\u3000\u8fd9\u516b\u9635\u516b\u5366\u638c\u8881\u7d2b\u8863\u522b\u8bf4\u6ca1\u6709\u5b66\u8fc7\uff0c\u8fde\u542c\u4e5f\u6ca1\u6709\u542c\u8fc7\uff0c\u53ea\u56e0\u8fd9\u662f\u738b\u7ef4\u626c\u7684\u4e0d\u4f20\u4e4b\u79d8\uff0c\u4ee5\u5979\u5e08\u7236\u6b66\u5b66\u4e4b\u6e0a\u535a\u5f53\u4e16\u65e0\u53cc\uff0c\u5374\u4e5f\u662f\u6709\u6240\u672a\u77e5\u3002\u8881\u7d2b\u8863\u53ea\u63a5\u5f97\u6570\u638c\uff0c\u767b\u65f6\u773c\u82b1\u7f2d\u4e71\uff0c\u6697\u6697\u53eb\u82e6\u3002\u80e1\u6590\u7ad9\u5728\u4ead\u5916\u63a0\u9635\uff0c\u4e5f\u77e5\u60c5\u52bf\u4e0d\u5999\uff0c\u53ea\u662f\u8881\u7d2b\u8863\u5927\u8a00\u5728\u5148\uff0c\u8bf4\u8981\u593a\u516b\u5366\u95e8\u638c\u95e8\uff0c\u81ea\u5df1\u51b3\u4e0d\u80fd\u63d2\u624b\u76f8\u52a9\uff0c\u773c\u89c1\u738b\u5251\u82f1\u8d8a\u6253\u8d8a\u5360\u4e0a\u98ce\uff0c\u6b63\u6ca1\u505a\u7406\u4f1a\u5904\uff0c\u5ffd\u89c1\u8881\u7d2b\u8863\u5de6\u8db3\u4e00\u767b\uff0c\u8dc3\u4e0a\u684c\u9762\uff0c\u8bf4\u9053\uff1a\u201c\u51f3\u5b50\u4e0a\u65bd\u5c55\u4e0d\u5f00\uff0c\u54b1\u4eec\u5728\u684c\u4e0a\u6597\u6597\u3002\u738b\u8001\u5e08\uff0c\u53ef\u4e0d\u8bb8\u8e0f\u788e\u4e86\u8336\u7897\u679c\u789f\u3002\u201d \u3000\u3000\u738b\u5251\u82f1\u4e00\u8a00\u4e0d\u53d1\uff0c\u8ddf\u7740\u4e0a\u4e86\u684c\u9762\uff0c\u8fd9\u65f6\u4e24\u4eba\u76f8\u8ddd\u8fd1\u4e86\uff0c\u8881\u7d2b\u8863\u65e0\u53ef\u53d6\u5de7\uff0c\u5bf9\u65b9\u62cd\u51fb\u8fc7\u6765\u7684\u638c\u62f3\uff0c\u52bf\u987b\u786c\u63a5\u786c\u67b6\uff0c\u4f46\u811a\u4e0b\u5374\u5360\u4e86\u4fbf\u5b9c\u3002\u539f\u6765\u684c\u4e0a\u653e\u7740\u5341\u4e8c\u53ea\u8336\u7897\uff0c\u56db\u76d8\u679c\u5b50\uff0c\u5168\u662f\u6563\u843d\u4e71\u7f6e\uff0c\u8fd9\u53ef\u4e0d\u540c\u6885\u82b1\u6869\u3001\u9752\u7af9\u9635\u6bcf\u4e00\u5904\u843d\u8db3\u70b9\u5747\u6709\u89c4\u5f8b\uff0c\u738b\u5251\u82f1\u7684\u516b\u9635\u516b\u5366\u638c\u5728\u5e73\u5730\u4e0a\u65bd\u5c55\u5a01\u529b\u6700\u5f3a\uff0c\u4e00\u4e0a\u6885\u82b1\u6869\uff0c\u53d8\u5316\u65e2\u53d7\u9650\u5236\uff0c\u5a01\u529b\u4fbf\u5df2\u76f8\u5e94\u51cf\u5f31\u3002\u8fd9\u65f6\u5728\u8fd9\u684c\u9762\u4e4b\u4e0a\uff0c\u66f4\u751f\u6015\u4e0d\u5c0f\u5fc3\u8e0f\u788e\u4e86\u8336\u7897\u679c\u76d8\uff0c\u4e3a\u8fd9\u5201\u94bb\u7684\u4e2b\u5934\u6240\u7b11\uff0c\u5f53\u4e0b\u5c3d\u91cf\u4e0d\u79fb\u811a\u6b65\uff0c\u4e00\u5473\u50ac\u52a8\u638c\u529b\uff0c\u81ea\u5fd6\u4e0d\u51ed\u811a\u6b65\u638c\u6cd5\u4e4b\u5999\uff0c\u5355\u9760\u6df1\u539a\u7684\u5185\u529f\uff0c\u5c31\u80fd\u5c06\u5979\u6bc1\u5728\u4e00\u53cc\u8089\u638c\u4e4b\u4e0b\u3002 \u3000\u3000\u4f46\u542c\u5f97\u638c\u98ce\u547c\u547c\uff0c\u4ead\u7554\u7684\u82b1\u6735\u4e3a\u4ed6\u638c\u529b\u6240\u6fc0\uff0c\u7247\u7247\u843d\u82f1\uff0c\u98de\u821e\u800c\u4e0b\u3002 \u3000\u3000\u5f53\u8881\u7d2b\u8863\u8dc3\u4e0a\u684c\u9762\u4e4b\u65f6\uff0c\u65e9\u5df2\u8ba1\u53ca\u5229\u5bb3\uff0c\u773c\u89c1\u5bf9\u65b9\u4e00\u638c\u638c\u5982\u75be\u98ce\u9aa4\u96e8\u822c\u51fb\u5230\uff0c\u5979\u53ea\u662f\u8db3\u4e0d\u505c\u6b65\u7684\u524d\u7a9c\u540e\u8dc3\uff0c\u5e76\u4e0d\u548c\u4ed6\u5bf9\u638c\u62c6\u89e3\uff0c\u77e5\u9053\u53ea\u8981\u548c\u5bf9\u65b9\u96c4\u6d51\u7684\u638c\u529b\u4e00\u7c98\u4f4f\uff0c\u90a3\u4fbf\u8131\u4e0d\u4e86\u8eab\uff0c\u53ea\u89c1\u738b\u5251\u82f1\u53f3\u638c\u865a\u6643\uff0c\u5de6\u638c\u659c\u5f15\uff0c\u53f3\u638c\u6b63\u8981\u5288\u51fa\uff0c\u5979\u5de6\u8db3\u5c16\u8f7b\u8f7b\u4e00\u6311\uff0c\u4e00\u53ea\u8336\u7897\u5411\u4ed6\u6251\u9762\u98de\u53bb\u3002\u738b\u5251\u82f1\u5403\u4e86\u4e00\u60ca\uff0c\u95ea\u8eab\u907f\u5f00\uff0c\u8881\u7d2b\u8863\u6599\u5230\u4ed6\u8d8b\u907f\u7684\u65b9\u4f4d\uff0c\u53cc\u8db3\u8fde\u6311\uff0c\u4e03\u516b\u53ea\u8336\u7897\u63a5\u4e8c\u8fde\u4e09\u7684\u98de\u5c06\u8fc7\u53bb\u3002\u738b\u5251\u82f1\u907f\u5f00\u4e86\u4e09\u53ea\uff0c\u7ec8\u4e8e\u907f\u4e0d\u5f00\u7b2c\u56db\u3001\u4e94\u53ea\uff0c\u556a\u556a\u4e24\u58f0\uff0c\u6253\u4e2d\u4e86\u4ed6\u80a9\u5934\u3002\u4ed6\u51fa\u638c\u5288\u5f00\u7b2c\u4e03\u3001\u516b\u53ea\uff0c\u7897\u4e2d\u7684\u8336\u6c34\u8336\u53f6\u5374\u6dcb\u4e86\u4ed6\u6ee1\u5934\u6ee1\u8138\uff0c\u8ddf\u7740\u7b2c\u4e5d\u3001\u5341\u53ea\u8336\u7897\u53c8\u51fb\u4e2d\u80f8\u53e3\u3002 \u3000\u3000\u738b\u5251\u82f1\u3001\u738b\u5251\u6770\u9f50\u58f0\u6012\u543c\uff0c\u65c1\u89c2\u7684\u6c6a\u94c1\u9e57\u3001\u891a\u8f70\u3001\u6bb7\u4ef2\u7fd4\u7b49\u4e5f\u5fcd\u4e0d\u4f4f\u60ca\u547c\uff0c\u53ea\u89c1\u6700\u540e\u4e24\u53ea\u8336\u7897\u76f4\u5954\u738b\u5251\u82f1\u53cc\u773c\u3002\u4ed6\u6124\u6012\u5df2\u6781\uff0c\u731b\u529b\u4e00\u638c\u51fb\u51fa\u3002\u8881\u7d2b\u8863\u8e22\u8336\u7897\u6270\u654c\uff0c\u539f\u672c\u662f\u7b49\u4ed6\u8fd9\u4e00\u638c\uff0c\u8fd9\u826f\u673a\u5982\u4f55\u80af\u4e88\u9519\u8fc7\uff1f\u5f53\u4e0b\u8eab\u8eaf\u4e00\u95ea\uff0c\u5df2\u4f38\u624b\u6293\u4f4f\u4ed6\u7684\u53f3\u8155\uff0c\u5de6\u624b\u5728\u4ed6\u7684\u81c2\u5f2f\u91cc\u201c\u66f2\u6c60\u7a74\u201d\u4e00\u62ff\uff0c\u4e00\u626d\u4e00\u63a8\uff0c\u5580\u7684\u4e00\u54cd\uff0c\u738b\u5251\u6770\u5927\u53eb\u201c\u554a\u54df\u201d\u58f0\u4e2d\uff0c\u738b\u5251\u82f1\u81c2\u9ab1\u5df2\u8131\u3002 \u3000\u3000\u8fd9\u4e00\u624b\u4ecd\u53ea\u662f\u5bfb\u5e38\u201c\u5206\u7b4b\u9519\u9aa8\u624b\u201d\uff0c\u8bf4\u4e0d\u4e0a\u4ec0\u4e48\u5947\u5999\u7684\u5bb6\u6570\uff0c\u53ea\u662f\u5979\u51fa\u624b\u5982\u7535\uff0c\u738b\u5251\u82f1\u7adf\u662f\u95ea\u907f\u4e0d\u4e86\uff0c\u81f4\u8d3b\u7ec8\u8eab\u4e4b\u7f9e\u3002 \u3000\u3000\u738b\u5251\u6770\u53cc\u624b\u4e00\u62cd\uff0c\u548c\u8eab\u5411\u8881\u7d2b\u8863\u80cc\u540e\u6251\u53bb\u3002\u80e1\u6590\u63a8\u51fa\u4e00\u638c\uff0c\u5c06\u4ed6\u9707\u9000\u4e09\u6b65\uff0c\u8bf4\u9053\uff1a\u201c\u738b\u5144\u4e14\u6162\uff01\u8bf4\u597d\u662f\u4e00\u4e2a\u6597\u4e00\u4e2a\u3002\u201d \u3000\u3000\u738b\u5251\u82f1\u9762\u8272\u60e8\u767d\uff0c\u50f5\u5728\u684c\u4e0a\u3002\u8881\u7d2b\u8863\u5fc3\u60f3\uff1a\u201c\u82e5\u662f\u8f7b\u6613\u653e\u4e86\u4ed6\uff0c\u4ed6\u5144\u5f1f\u56de\u5934\u627e\u573a\uff0c\u6211\u53ef\u6597\u4ed6\u4eec\u4e0d\u8fc7\uff01\u201d\u7adf\u662f\u4e0b\u624b\u4e0d\u5bb9\u60c5\uff0c\u4e58\u7740\u4ed6\u65e0\u529b\u6297\u5fa1\u4e4b\u65f6\uff0c\u5580\u5587\u4e00\u58f0\uff0c\u5c06\u4ed6\u5de6\u81c2\u7684\u5173\u8282\u4e5f\u5378\u8131\u4e86\uff0c\u4e00\u6307\u70b9\u5728\u4ed6\u592a\u9633\u7a74\u4e0a\uff0c\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u516b\u5366\u95e8\u7684\u638c\u95e8\u8ba9\u662f\u4e0d\u8ba9\uff1f\u201d \u3000\u3000\u738b\u5251\u82f1\u95ed\u76ee\u5f85\u6b7b\uff0c\u66f4\u4e0d\u8bf4\u8bdd\u3002\u738b\u5251\u6770\u559d\u9053\uff1a\u201c\u5feb\u653e\u6211\u5144\u957f\uff0c\u4f60\u8981\u505a\u638c\u95e8\uff0c\u505a\u4f60\u7684\u4fbf\u662f\u3002\u201d\u8881\u7d2b\u8863\u9053\uff1a\u201c\u8bf4\u8bdd\u53ef\u8981\u7b97\u6570\uff1f\u201d\u738b\u5251\u6770\u9053\uff1a\u201c\u7b97\u6570\uff0c\u7b97\u6570\u3002\u201d\u8881\u7d2b\u8863\u8fd9\u624d\u5fae\u5fae\u4e00\u7b11\uff0c\u8dc3\u4e0b\u684c\u5b50\u3002\u738b\u5251\u6770\u8d1f\u8d77\u5144\u957f\uff0c\u5934\u4e5f\u4e0d\u56de\u5730\u5feb\u6b65\u8d70\u51fa\u3002 \u3000\u3000\u5468\u94c1\u9e6a\u9053\uff1a\u201c\u59d1\u5a18\u8fde\u593a\u4e24\u5bb6\u638c\u95e8\uff0c\u679c\u7136\u662f\u806a\u660e\u4f36\u4fd0\uff0c\u5374\u4e0d\u77e5\u7559\u4e0b\u4ec0\u4e48\u5999\u8ba1\uff0c\u8981\u65bd\u5728\u6211\u59d3\u5468\u7684\u8eab\u4e0a\uff1f\u201d\u8fd9\u8bdd\u660e\u660e\u8bf4\u5979\u4e0d\u8fc7\u662f\u4f7f\u8be1\u8ba1\u53d6\u80dc\uff0c\u8bf4\u4e0d\u4e0a\u662f\u771f\u5b9e\u672c\u9886\u3002\u8881\u7d2b\u8863\u9053\uff1a\u201c\u5bf9\u4ed8\u4f60\u9b54\u722a\u96c1\u884c\u95e8\uff0c\u8fd8\u7528\u5f97\u7740\u667a\u8ba1\uff1f\u4f60\u5e08\u5144\u5f1f\u4e09\u4e2a\u4eba\u662f\u4e00\u9f50\u4e0a\u5462\uff0c\u8fd8\u662f\u5468\u8001\u5e08\u4e00\u4e2a\u4eba\u8ddf\u6211\u8fc7\u62db\uff1f\u201d\u5468\u94c1\u9e6a\u6de1\u6de1\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8881\u59d1\u5a18\u6b64\u8a00\uff0c\u771f\u662f\u95e8\u7f1d\u91cc\u770b\u4eba\uff0c\u628a\u5317\u4eac\u57ce\u91cc\u7684\u6b66\u5e08\u4eec\u5168\u90fd\u77a7\u5f97\u6241\u4e86\u3002 \u3000\u3000\u5468\u67d0\u6253\u4ece\u5341\u4e09\u5c81\u4e0a\u8d77\uff0c\u4ece\u6765\u4fbf\u662f\u5355\u6253\u72ec\u6597\u3002\u201d\u8881\u7d2b\u8863\u9053\uff1a\u201c\u55ef\uff0c\u90a3\u4f60\u5341\u4e09\u5c81\u524d\uff0c\u4fbf\u4e0d\u662f\u82f1\u96c4\u597d\u6c49\uff0c\u4e13\u7231\u4e24\u4e2a\u6253\u4e00\u4e2a\u3002\u201d\u5468\u94c1\u9e6a\u9053\uff1a\u201c\u563f\uff0c\u6211\u81ea\u5341\u4e09\u5c81\u8d77\u59cb\u5b66\u827a\u3002\u201d\u8881\u7d2b\u8863\u9053\uff1a\u201c\u662f\u82f1\u96c4\u597d\u6c49\uff0c\u751f\u6765\u4fbf\u662f\u82f1\u96c4\u597d\u6c49\uff0c\u6709\u7684\u4eba\u6b66\u827a\u518d\u9ad8\uff0c\u59cb\u7ec8\u4e0d\u8fc7\u662f\u7a9d\u56ca\u5e9f\u3002\u5468\u8001\u5e08\uff0c\u6211\u53ef\u4e0d\u662f\u8bf4\u4f60\u3002\u201d\u4e0d\u77e5\u600e\u7684\uff0c\u5979\u5bf9\u4e8e\u738b\u5251\u82f1\u3001\u738b\u5251\u6770\u5144\u5f1f\uff0c\u5fc3\u4e2d\u8fd8\u5b58\u7740\u4e09\u5206\u4f69\u670d\uff0c\u89c1\u4e86\u5468\u94c1\u9e6a\u5927\u523a\u523a\u5730\u81ea\u89c6\u6781\u9ad8\u7684\u795e\u6c14\uff0c\u5374\u662f\u8bf4\u4e0d\u51fa\u7684\u8ba8\u538c\u3002 \u3000\u3000\u5468\u94c1\u9e6a\u51e0\u65f6\u53d7\u8fc7\u65c1\u4eba\u8fd9\u7b49\u7f9e\u8fb1\uff1f\u5fc3\u4e2d\u72c2\u6012\uff0c\u5634\u91cc\u5374\u53ea\u54fc\u4e86\u4e00\u58f0\u3002\u6c6a\u94c1\u9e57\u53eb\u4e86\u8d77\u6765\uff1a\u201c\u5c0f\u4e2b\u5934\uff0c\u8ddf\u6211\u5927\u5e08\u54e5\u8bf4\u8bdd\uff0c\u53ef\u5f97\u5ba2\u6c14\u4e9b\u3002\u201d \u3000\u3000\u8881\u7d2b\u8863\u77e5\u4ed6\u662f\u4e2a\u6d51\u4eba\uff0c\u4e5f\u4e0d\u7406\u776c\uff0c\u5bf9\u5468\u94c1\u9e6a\u9053\uff1a\u201c\u62ff\u51fa\u6765\uff0c\u653e\u5728\u684c\u4e0a\u3002\u201d\u5468\u94c1\u9e6a\u6115\u7136\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u201d\u8881\u7d2b\u8863\u9053\uff1a\u201c\u94dc\u9e70\u94c1\u96c1\u724c\u3002\u201d \u3000\u3000\u4e00\u542c\u5230\u201c\u94dc\u9e70\u94c1\u96c1\u724c\u201d\u4e94\u5b57\uff0c\u5468\u94c1\u9e6a\u6db5\u517b\u529f\u592b\u518d\u9ad8\uff0c\u4e5f\u5df2\u4e0d\u80fd\u88c5\u4f5c\u795e\u8272\u81ea\u82e5\uff0c\u5927\u58f0\u9053\uff1a\u201c\u554a\u54c8\uff01\u6211\u95e8\u4e2d\u7684\u4e8b\uff0c\u4f60\u5012\u771f\u77e5\u9053\u5f97\u4e0d\u5c11\u3002\u201d\u4f38\u624b\u4ece\u8170\u5e26\u4e0a\u89e3\u4e0b\u4e00\u4e2a\u9526\u56ca\uff0c\u653e\u5728\u684c\u4e0a\uff0c\u559d\u9053\uff1a\u201c\u94dc\u9e70\u94c1\u96c1\u724c\u4fbf\u5728\u8fd9\u91cc\uff0c\u4f60\u4eca\u65e5\u5148\u53d6\u6211\u59d3\u5468\u7684\u6027\u547d\uff0c\u518d\u53d6\u6b64\u724c\u3002\u201d\u8881\u7d2b\u8863\u9053\uff1a\u201c\u62ff\u51fa\u6765\u77a7\u77a7\uff0c\u8c01\u77e5\u9053\u662f\u771f\u662f\u5047\u3002\u201d \u3000\u3000\u5468\u94c1\u9e6a\u53cc\u624b\u5fae\u5fae\u53d1\u98a4\uff0c\u89e3\u5f00\u9526\u56ca\uff0c\u53d6\u51fa\u4e00\u5757\u56db\u5bf8\u957f\u3001\u4e24\u5bf8\u5bbd\u7684\u91d1\u724c\u6765\uff0c\u724c\u4e0a\u9576\u7740\u4e00\u53ea\u63a2\u722a\u94dc\u9e70\uff0c\u4e00\u53ea\u659c\u98de\u94c1\u96c1\uff0c\u6b63\u662f\u9b54\u722a\u96c1\u884c\u95e8\u4e2d\u4e16\u4ee3\u76f8\u4f20\u7684\u638c\u95e8\u4fe1\u724c\uff0c\u51e1\u662f\u672c\u95e8\u5f1f\u5b50\uff0c\u89c1\u6b64\u724c\u5982\u89c1\u638c\u95e8\u4eba\u3002 \u3000\u3000\u539f\u6765\u9e70\u722a\u96c1\u884c\u95e8\u5728\u660e\u672b\u5929\u542f\uff0c\u5d07\u796f\u5e74\u95f4\uff0c\u539f\u662f\u6b66\u6797\u4e2d\u4e00\u5927\u95e8\u6d3e\uff0c\u51e0\u4ee3\u638c\u95e8\u4eba\u90fd\u662f\u6b66\u529f\u5353\u7edd\uff0c\u95e8\u89c4\u4e5f\u6781\u4e25\u8c28\u3002\u4f46\u4f20\u5230\u5468\u94c1\u9e6a\u3001\u66fe\u94c1\u9e25\u7b49\u4eba\u624b\u91cc\u65f6\uff0c\u8bf8\u5f1f\u5b50\u4e3a\u6ee1\u6e05\u6743\u8d35\u6240\u7528\uff0c\u67d3\u4e0a\u4e86\u4eac\u4e2d\u8c6a\u5962\u7684\u4e60\u6c14\uff0c\u6b66\u529f\u5df2\u8fdc\u4e0d\u5982\u524d\u4eba\u3002\u540e\u6765\u76f4\u5230\u5609\u5e86\u5e74\u95f4\uff0c\u9e70\u722a\u96c1\u884c\u95e8\u4e2d\u51fa\u4e86\u51e0\u4e2a\u4e86\u4e0d\u8d77\u7684\u4eba\u7269\uff0c\u8be5\u95e8\u65b9\u59cb\u4e2d\u5174\u3002 \u3000\u3000\u8881\u7d2b\u8863\u9053\uff1a\u201c\u770b\u6765\u50cf\u662f\u771f\u7684\uff0c\u4e0d\u8fc7\u4e5f\u8bf4\u4e0d\u5b9a\u3002\u201d\u539f\u6765\u5979\u9002\u624d\u548c\u738b\u5251\u82f1\u4e00\u756a\u5267\u6597\uff0c\u867d\u7136\u4fa5\u5e78\u53cd\u8d25\u4e3a\u80dc\uff0c\u5185\u529b\u5374\u5df2\u5927\u8017\uff0c\u8fd9\u65f6\u6545\u610f\u626f\u6de1\uff0c\u4e00\u6765\u8981\u6fc0\u6012\u5bf9\u624b\uff0c\u4e8c\u6765\u4e5f\u662f\u6b47\u529b\u517b\u6c14\u3002 \u3000\u3000\u5468\u94c1\u9e6a\u89c1\u591a\u8bc6\u5e7f\uff0c\u5982\u4f55\u4e0d\u77e5\u5979\u7684\u5fc3\u610f\uff1f\u5f53\u4e0b\u66f4\u4e0d\u591a\u8a00\uff0c\u53cc\u624b\u4e00\u632f\u4e00\u538b\uff0c\u7a81\u7136\u8dc3\u4e0a\u51c9\u4ead\u4e4b\u9876\uff0c\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u8d8a\u6253\u8d8a\u9ad8\uff0c\u6211\u4fbf\u5728\u8fd9\u4ead\u5b50\u9876\u4e0a\u9886\u6559\u9ad8\u62db\u3002\u201d\u987b\u77e5\u4ed6\u7684\u95e8\u6d3e\u4ee5\u9b54\u722a\u96c1\u884c\u4e3a\u540d\uff0c\u81ea\u662f\u4e00\u64c5\u9e70\u722a\u64d2\u62ff\uff0c\u4e8c\u64c5\u96c1\u884c\u8f7b\u529f\u3002\u4ed6\u8dc3\u4e0a\u4ead\u9876\uff0c\u5b58\u5fc3\u6545\u5c45\u9669\u5730\uff0c\u4fbf\u4e8e\u65bd\u5c55\u8f7b\u529f\uff0c\u4e0e\u5bf9\u624b\u4f5c\u4e00\u756a\u751f\u6b7b\u640f\u51fb\uff0c\u540c\u65f6\u4ee4\u5979\u65e0\u6cd5\u53d6\u5de7\u884c\u8be1\uff0c\u66f4\u6709\u4e00\u7740\u662f\u8981\u80e1\u6590\u4e0d\u80fd\u5728\u5371\u6025\u4e2d\u51fa\u624b\u76f8\u52a9\u3002\u5728\u5468\u94c1\u9e6a\u5fc3\u4e2d\uff0c\u8881\u7d2b\u8863\u6b66\u529f\u867d\u9ad8\uff0c\u7ec8\u4e0d\u8fc7\u662f\u5973\u6d41\u4e4b\u8f88\uff0c\u771f\u6b63\u7684\u52b2\u654c\u5374\u662f\u80e1\u6590\u3002 \u3000\u3000\u4ed6\u90a3\u77e5\u64d2\u62ff\u548c\u8f7b\u529f\u8fd9\u4e24\u95e8\uff0c\u4e5f\u6b63\u662f\u8881\u7d2b\u8863\u7684\u4e13\u957f\u7edd\u6280\uff0c\u4ed6\u82e5\u662f\u89c1\u8fc7\u5979\u548c\u6613\u5409\u5728\u9ad8\u6845\u9876\u4e0a\u6597\u97ad\u65f6\u90a3\u4e00\u8def\u60ca\u4e16\u9a87\u4fd7\u7684\u8f7b\u529f\uff0c\u4e5f\u4e0d\u4f1a\u8dc3\u4e0a\u8fd9\u51c9\u4ead\u4e4b\u9876\u4e86\u3002 \u3000\u3000\u80e1\u6590\u89c1\u4e86\u4ed6\u8fd9\u4e00\u7eb5\u4e00\u8dc3\uff0c\u867d\u7136\u8f7b\u6377\uff0c\u5374\u51b3\u4e0d\u80fd\u548c\u8881\u7d2b\u8863\u7684\u8eab\u624b\u76f8\u6bd4\uff0c\u767b\u65f6\u4fbf\u5bbd\u4e86\u5fc3\uff0c\u8f6c\u8fc7\u5934\u6765\uff0c\u4e24\u4eba\u76f8\u89c6\u4e00\u7b11\u3002 \u3000\u3000\u8881\u7d2b\u8863\u6545\u610f\u5e76\u4e0d\u70ab\u793a\uff0c\u8001\u8001\u5b9e\u5b9e\u7684\u8dc3\u4e0a\u4ead\u9876\uff0c\u8bf4\u9053\uff1a\u201c\u770b\u62db\uff01\u201d\u53cc\u624b\u5341\u6307\u62ff\u6210\u9e70\u722a\u4e4b\u5f0f\uff0c\u659c\u8eab\u6251\u51fb\u3002 \u3000\u3000\u62f3\u672f\u7684\u722a\u6cd5\uff0c\u5927\u8def\u5206\u4e3a\u9f99\u722a\u3001\u864e\u722a\u3001\u9e70\u722a\u4e09\u79cd\u3002\u9f99\u722a\u662f\u56db\u6307\u5e76\u62e2\uff0c\u62c7\u6307\u4f38\u5c55\uff0c\u8155\u8282\u5c48\u5411\u624b\u5fc3\uff1b\u864e\u722a\u662f\u4e94\u6307\u5404\u81ea\u5206\u5f00\uff0c\u7b2c\u4e8c\u3001\u7b2c\u4e09\u6307\u9aa8\u5411\u624b\u5fc3\u5f2f\u66f2\uff1b\u9e70\u722a\u662f\u56db\u6307\u5e76\u62e2\uff0c\u62c7\u6307\u5f20\u5f00\uff0c\u4e94\u6307\u7684\u7b2c\u4e8c\u3001\u7b2c\u4e09\u6307\u9aa8\u5411\u624b\u5fc3\u5f2f\u66f2\u3002\u4e09\u79cd\u722a\u6cd5\u5404\u6709\u6240\u957f\uff0c\u4ee5\u9f99\u722a\u529f\u6700\u4e3a\u6df1\u5965\u96be\u7ec3\u3002 \u3000\u3000\u5468\u94c1\u9e6a\u89c1\u5979\u6240\u4f7f\u679c\u7136\u662f\u672c\u95e8\u5bb6\u6570\uff0c\u5fc3\u60f3\uff1a\u201c\u4f60\u82e5\u7528\u53e4\u602a\u6b66\u529f\uff0c\u6211\u5c1a\u6709\u6240\u5fcc\uff0c\u4f60\u771f\u7684\u4f7f\u9e70\u722a\u96c1\u884c\u529f\uff0c\u90a3\u53ef\u662f\u81ea\u5bfb\u6b7b\u8def\u4e86\u3002\u201d \u3000\u3000\u5f53\u4e0b\u53cc\u624b\u4e5f\u6210\u9e70\u722a\uff0c\u53cd\u624b\u94a9\u6253\u3002 \u3000\u3000\u4f17\u4eba\u4ef0\u9996\u800c\u89c2\uff0c\u53ea\u89c1\u4e24\u4eba\u8f7b\u8eab\u7eb5\u8dc3\uff0c\u63a5\u8fd1\u65f6\u64d2\u62ff\u62c6\u6253\u6570\u62db\uff0c\u7acb\u5373\u9000\u5f00\u3002\u8fd9\u4e00\u665a\u56db\u573a\u6fc0\u6597\uff0c\u4ee5\u8fd9\u4e00\u573a\u6700\u4e3a\u597d\u770b\uff0c\u4f46\u4e5f\u4ee5\u8fd9\u4e00\u573a\u6700\u4e3a\u51f6\u9669\u3002\u6708\u5149\u4e4b\u4e0b\uff0c\u4ead\u6a90\u4ead\u89d2\uff0c\u4e24\u4eba\u771f\u5982\u4e00\u53cc\u5927\u9e1f\u4e00\u822c\uff0c\u7ffb\u98de\u640f\u51fb\u3002 \u3000\u3000\u84e6\u5730\u91cc\u4e24\u4eba\u6b3a\u8fd1\u8eab\u5904\uff0c\u5580\u5580\u6570\u54cd\uff0c\u8881\u7d2b\u8863\u4e00\u58f0\u547c\u53f1\uff0c\u5468\u94c1\u9e6a\u957f\u58f0\u5927\u53eb\uff0c\u8dcc\u4e0b\u4ead\u6765\u3002 \u3000\u3000\u5468\u94c1\u9e6a\u5982\u4f55\u8dcc\u4e0b\uff0c\u53ea\u56e0\u4e24\u4eba\u624b\u811a\u592a\u5feb\uff0c\u65c1\u89c2\u4f17\u4eba\u4e4b\u4e2d\uff0c\u53ea\u6709\u80e1\u6590\u548c\u66fe\u94c1\u9e25\u770b\u6e05\u695a\u4e86\u3002\u5468\u94c1\u9e6a\u6fc0\u6597\u4e2d\u4f7f\u51fa\u7edd\u62db\u201c\u56db\u96c1\u5357\u98de\u201d\uff0c\u4ee5\u8fde\u73af\u817f\u8fde\u8e22\u5bf9\u624b\u56db\u811a\uff0c\u8e22\u5230\u7b2c\u4e8c\u817f\u65f6\u88ab\u8881\u7d2b\u8863\u4ee5\u201c\u5206\u7b4b\u9519\u9aa8\u624b\u201d\u62a2\u8fc7\u53bb\u5378\u8131\u4e86\u5de6\u817f\u5173\u8282\u3002\u4ed6\u8fd9\u4e00\u62db\u53cc\u817f\u6b64\u8d77\u5f7c\u843d\uff0c\u4e2d\u9014\u65e0\u6cd5\u6536\u52bf\uff0c\u5de6\u817f\u867d\u5df2\u53d7\u4f24\uff0c\u53f3\u817f\u4ecd\u7136\u8e22\u51fa\uff0c\u8881\u7d2b\u8863\u5bf9\u51c6\u4ed6\u819d\u76d6\u8e39\u4e86\u4e00\u811a\uff0c\u53f3\u817f\u53d7\u4f24\u66f4\u91cd\u3002\u65c1\u4eba\u5374\u53ea\u89c1\u4ed6\u6454\u4e0b\u65f6\u80a9\u80cc\u7740\u5730\uff0c\u843d\u4e0b\u540e\u7adf\u4e0d\u518d\u7ad9\u8d77\u3002\u8fd9\u51c9\u4ead\u5e76\u4e0d\u751a\u9ad8\uff0c\u4ee5\u5468\u94c1\u9e6a\u7684\u8f7b\u8eab\u529f\u592b\uff0c\u7eb5\u7136\u5931\u624b\uff0c\u8dc3\u4e0b\u540e\u51b3\u4e0d\u81f4\u4fbf\u4e0d\u80fd\u8d77\u8eab\uff0c\u96be\u9053\u7adf\u662f\u5df2\u53d7\u81f4\u547d\u91cd\u4f24\uff1f \u3000\u3000\u6c6a\u94c1\u9e57\u7d20\u6765\u656c\u7231\u5927\u5e08\u5144\uff0c\u5927\u53eb\uff1a\u201c\u5e08\u54e5\uff01\u201d\u5954\u8fd1\u524d\u53bb\uff0c\u8bed\u58f0\u4e2d\u5df2\u5e26\u7740\u54ed\u97f3\u3002\u4ed6\u4fef\u8eab\u6276\u8d77\u5468\u94c1\u9e6a\uff0c\u8ba9\u4ed6\u7ad9\u7a33\u3002\u4f46\u5468\u94c1\u9e6a\u4e24\u817f\u8131\u81fc\uff0c\u54ea\u91cc\u8fd8\u80fd\u7ad9\u7acb\uff1f\u6c6a\u94c1\u9e57\u6276\u8d77\u4ed6\u540e\u53cc\u624b\u653e\u5f00\u3002\u5468\u94c1\u9e6a\u547b\u541f\u4e00\u58f0\uff0c\u53c8\u8981\u6454\u5012\u3002\u66fe\u94c1\u9e25\u4f4e\u58f0\u9a82\u9053\uff1a\u201c\u8822\u6750\uff01\u201d\u62a2\u524d\u6276\u8d77\u3002\u4ed6\u6b66\u529f\u5728\u9e70\u722a\u96c1\u884c\u95e8\u4e2d\u4e5f\u7b97\u662f\u9876\u5c16\u513f\u7684\u597d\u624b\uff0c\u53ea\u662f\u4e0d\u4f1a\u63a8\u62ff\u63a5\u9aa8\u4e4b\u672f\uff0c\u62b1\u8d77\u5468\u94c1\u9e6a\uff0c\u4fbf\u8981\u5954\u51fa\u3002 \u3000\u3000\u5468\u94c1\u9e6a\u559d\u9053\uff1a\u201c\u53d6\u4e86\u9e70\u96c1\u724c\u3002\u201d\u66fe\u94c1\u9e25\u767b\u65f6\u7701\u609f\uff0c\u62a2\u8fdb\u51c9\u4ead\uff0c\u4f38\u624b\u5f80\u5706\u684c\u4e0a\u53bb\u53d6\u91d1\u724c\uff0c\u7a81\u7136\u5934\u9876\u98ce\u58f0\u98d2\u7136\uff0c\u638c\u529b\u5df2\u7136\u53ca\u9996\u3002\u66fe\u94c1\u9e25\u53f3\u624b\u62b1\u7740\u5e08\u5144\uff0c\u5de6\u624b\u4e0d\u53ca\u53d6\u724c\uff0c\u53ea\u5f97\u53cd\u638c\u4e0a\u8fce\uff0c\u54ea\u77e5\u8fd9\u4e00\u67b6\u5374\u67b6\u4e86\u4e2a\u7a7a\u3002\u773c\u524d\u9ed1\u5f71\u4e00\u6643\uff0c\u4e00\u4eba\u4ece\u51c9\u4ead\u9876\u4e0a\u7ffb\u8eab\u800c\u4e0b\uff0c\u5df2\u5c06\u684c\u4e0a\u91d1\u724c\u6293\u5728\u624b\u4e2d\uff0c\u559d\u9053\uff1a\u201c\u6253\u8f93\u4e86\u60f3\u8d56\u4e48\uff1f\u201d\u6b63\u662f\u8881\u7d2b\u8863\u3002 \u3000\u3000\u66fe\u94c1\u9e25\u53c8\u60ca\u53c8\u6012\uff0c\u62b1\u7740\u5468\u94c1\u9e6a\uff0c\u50f5\u5728\u4ead\u4e2d\uff0c\u4e0d\u77e5\u8be5\u5f53\u548c\u8881\u7d2b\u8863\u62da\u547d\uff0c\u8fd8\u662f\u5148\u8bf7\u4eba\u53bb\u6cbb\u5927\u5e08\u5144\u518d\u8bf4\uff1f \u3000\u3000\u80e1\u6590\u4e0a\u524d\u4e00\u6b65\uff0c\u8bf4\u9053\uff1a\u201c\u5468\u5144\u53cc\u817f\u8131\u4e86\u81fc\uff0c\u82e5\u4e0d\u7acb\u523b\u63a8\u4e0a\uff0c\u53ea\u6015\u4f24\u4e86\u7b4b\u9aa8\u3002\u201d\u4e5f\u4e0d\u7b49\u5468\u66fe\u4e24\u4eba\u7b54\u8bdd\uff0c\u4f38\u624b\u62c9\u4f4f\u5468\u94c1\u9e6a\u7684\u5de6\u817f\uff0c\u4e00\u63a8\u4e00\u9001\uff0c\u5580\u7684\u4e00\u58f0\uff0c\u63a5\u4e0a\u4e86\u81fc\uff0c\u8ddf\u7740\u53c8\u63a5\u4e0a\u4e86\u53f3\u817f\u5173\u8282\uff0c\u518d\u5728\u4ed6\u8170\u4fa7\u7a74\u9053\u4e2d\u63a8\u62ff\u6570\u4e0b\u3002\u5468\u94c1\u9e6a\u767b\u65f6\u75bc\u75db\u5927\u51cf\u3002 \u3000\u3000\u80e1\u6590\u5411\u8881\u7d2b\u8863\u4f38\u51fa\u624b\u638c\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u94dc\u9e70\u94c1\u96c1\u724c\u4e5f\u6ca1\u4ec0\u4e48\u597d\u73a9\uff0c\u4f60\u8fd8\u4e86\u5468\u5927\u54e5\u5427\uff01\u201d\u8881\u7d2b\u8863\u542c\u4ed6\u8bf4\u5230\u201c\u4e5f\u6ca1\u4ec0\u4e48\u597d\u73a9\u201d\u516d\u5b57\uff0c\u5ae3\u7136\u4e00\u7b11\uff0c\u5c06\u91d1\u724c\u653e\u5728\u4ed6\u638c\u5fc3\u3002 \u3000\u3000\u80e1\u6590\u53cc\u624b\u6367\u724c\uff0c\u606d\u606d\u656c\u656c\u7684\u9012\u5230\u5468\u94c1\u9e6a\u9762\u524d\u3002\u5468\u94c1\u9e6a\u4f38\u624b\u6293\u8d77\uff0c\u8bf4\u9053\uff1a\u201c\u4e24\u4f4d\u7684\u597d\u5904\uff0c\u59d3\u5468\u7684\u4f46\u6559\u6709\u4e00\u53e3\u6c14\u5728\uff0c\u7ec8\u6709\u62a5\u7b54\u4e4b\u65f6\u3002\u201d\u8bf4\u7740\u5411\u8881\u7d2b\u8863\u548c\u80e1\u6590\u5404\u671b\u4e00\u773c\uff0c\u6276\u7740\u66fe\u94c1\u9e25\u8f6c\u8eab\u4fbf\u8d70\u3002\u5411\u8881\u7d2b\u8863\u6240\u671b\u7684\u90a3\u4e00\u773c\uff0c\u76ee\u5149\u4e2d\u5145\u6ee1\u4e86\u6028\u6bd2\uff0c\u77a7\u5411\u80e1\u6590\u7684\u90a3\u4e00\u773c\uff0c\u5374\u663e\u793a\u4e86\u611f\u6fc0\u4e4b\u60c5\u3002 \u3000\u3000\u8881\u7d2b\u8863\u6beb\u6ca1\u5728\u610f\uff0c\u5c0f\u5634\u4e00\u6241\uff0c\u79c0\u7709\u5fae\u626c\uff0c\u5411\u7740\u4f7f\u96f7\u9707\u6321\u7684\u891a\u8f70\u8bf4\u9053\uff1a\u201c\u891a\u5927\u7237\uff0c\u4f60\u8fd9\u534a\u4e2a\u638c\u95e8\u4eba\uff0c\u54b1\u4eec\u8fd8\u6bd4\u4e0d\u6bd4\u5212\uff1f\u201d \u3000\u3000\u5230\u4e86\u6b64\u65f6\uff0c\u891a\u8f70\u518d\u7b28\u4e5f\u8be5\u6709\u4e09\u5206\u81ea\u77e5\u4e4b\u660e\uff0c\u9886\u4f1a\u5f97\u51ed\u7740\u81ea\u5df1\u8fd9\u51e0\u624b\u529f\u592b\uff0c\u51b3\u4e0d\u80fd\u662f\u5979\u654c\u624b\uff0c\u62b1\u62f3\u8bf4\u9053\uff1a\u201c\u655d\u6d3e\u96f7\u7535\u95e8\u7531\u5bb6\u5e08\u6267\u638c\uff0c\u533a\u533a\u4f55\u6562\u81ea\u5c45\u638c\u95e8\uff1f\u59d1\u5a18\u4f46\u80af\u8d50\u6559\uff0c\u4fbf\u8bf7\u9a7e\u4e34\u585e\u5317\uff0c\u5bb6\u5e08\u5b9a\u662f\u6b22\u8fce\u5f97\u7d27\u3002\u201d\u4ed6\u8fd9\u51e0\u53e5\u8bdd\u4e0d\u4ea2\u4e0d\u5351\uff0c\u5374\u628a\u62c5\u5b50\u90fd\u63a8\u5230\u4e86\u5e08\u7236\u80a9\u4e0a\u3002 \u3000\u3000\u8881\u7d2b\u8863\u201c\u563f\u563f\u201d\u4e00\u7b11\uff0c\u5de6\u624b\u6446\u4e86\u51e0\u6446\uff0c\u9053\uff1a\u201c\u8fd8\u6709\u90a3\u4e00\u4f4d\u8981\u8d50\u6559\uff1f\u201d \u3000\u3000\u6bb7\u4ef2\u7fd4\u7b49\u4e00\u9f50\u62b1\u62f3\uff0c\u8bf4\u9053\uff1a\u201c\u80e1\u5927\u7237\uff0c\u518d\u89c1\u4e86\u3002\u201d\u8f6c\u8eab\u51fa\u5916\uff0c\u5404\u5b58\u6ee1\u8179\u7591\u56e2\uff0c\u4e0d\u77e5\u8fd9\u6b66\u529f\u5982\u6b64\u9ad8\u5f3a\u7684\u5c11\u5973\u5230\u5e95\u662f\u751a\u4e48\u8def\u9053\u3002 \u3000\u3000\u80e1\u6590\u4eb2\u81ea\u9001\u5230\u5927\u95e8\u53e3\uff0c\u56de\u5230\u82b1\u56ed\u6765\u65f6\uff0c\u5ffd\u542c\u5f97\u534a\u7a7a\u4e2d\u6253\u4e86\u4e2a\u9739\u96f3\uff0c\u62ac\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u4e4c\u4e91\u6ee1\u5929\uff0c\u65e9\u5c06\u660e\u6708\u63a9\u6ca1\u3002 \u3000\u3000\u8881\u7d2b\u8863\u9053\uff1a\u201c\u5f53\u771f\u662f\u5929\u6709\u4e0d\u6d4b\u98ce\u4e91\uff0c\u4eba\u6709\u65e6\u5915\u7978\u798f\u3002\u60f3\u4e0d\u5230\u80e1\u5927\u54e5\u6e38\u4fa0\u98ce\u5c18\uff0c\u4e00\u5230\u4eac\u5e08\uff0c\u5374\u9762\u56e2\u56e2\u505a\u8d77\u5bcc\u5bb6\u7fc1\u6765\u3002\u201d \u3000\u3000\u542c\u5979\u4e00\u63d0\u8d77\u6b64\u4e8b\uff0c\u4e0d\u7531\u5f97\u80e1\u6590\u6c14\u5f80\u4e0a\u51b2\uff0c\u8bf4\u9053\uff1a\u201c\u8881\u59d1\u5a18\uff0c\u8fd9\u5b85\u7b2c\u662f\u90a3\u59d3\u51e4\u5978\u4eba\u7684\u4ea7\u4e1a\uff0c\u6211\u4fbf\u662f\u5728\u8fd9\u5c4b\u4e2d\u591a\u5f85\u4e00\u523b\uff0c\u4e5f\u662f\u73b7\u8fb1\u4e86\uff0c\u544a\u8f9e\uff01\u201d\u56de\u5934\u5411\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u4e8c\u59b9\uff0c\u54b1\u4eec\u8d70\uff01\u201d \u3000\u3000\u8881\u7d2b\u8863\u9053\uff1a\u201c\u8fd9\u4e09\u66f4\u534a\u591c\uff0c\u4f60\u4eec\u5374\u5230\u54ea\u91cc\u53bb\uff1f\u4f60\u4e0d\u89c1\u53d8\u4e86\u5929\uff0c\u8f6c\u773c\u4fbf\u662f\u4e00\u573a\u5927\u96e8\u4e48\uff1f\u201d\u5979\u521a\u8bf4\u4e86\u8fd9\u53e5\u8bdd\uff0c\u9ec4\u8c46\u822c\u7684\u96e8\u70b9\u4fbf\u5df2\u6d12\u5c06\u4e0b\u6765\u3002 \u3000\u3000\u80e1\u6590\u6012\u9053\uff1a\u201c\u4fbf\u662f\u9732\u5bbf\u8857\u5934\uff0c\u4e5f\u80dc\u4e8e\u5728\u5978\u8d3c\u7684\u5c4b\u6a90\u4e0b\u8eb2\u96e8\u3002\u201d\u8bf4\u7740\u5934\u4e5f\u4e0d\u56de\u7684\u5f80\u5916\u4fbf\u8d70\u3002\u7a0b\u7075\u7d20\u8ddf\u7740\u8d70\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u5ffd\u542c\u8881\u7d2b\u8863\u5728\u80cc\u540e\u6068\u6068\u7684\u9053\uff1a\u201c\u51e4\u5929\u5357\u8fd9\u5978\u4eba\uff0c\u539f\u672c\u662f\u6b7b\u6709\u4f59\u8f9c\u3002\u6211\u6068\u4e0d\u5f97\u4eb2\u624b\u5272\u4ed6\u51e0\u5200\uff01\u201d \u3000\u3000\u80e1\u6590\u7ad9\u5b9a\u8eab\u5b50\uff0c\u56de\u5934\u6012\u9053\uff1a\u201c\u4f60\u8fd9\u65f6\u5374\u53c8\u6765\u8bf4\u98ce\u51c9\u8bdd\uff1f\u201d\u8881\u7d2b\u8863\u9053\uff1a\u201c\u6211\u5fc3\u4e2d\u5bf9\u8fd9\u51e4\u5929\u5357\u7684\u6028\u6bd2\uff0c\u80dc\u4f60\u767e\u500d\uff01\u201d\u987f\u4e86\u4e00\u987f\uff0c\u54ac\u7259\u5207\u9f7f\u5730\u9053\uff1a\u201c\u4f60\u53ea\u4e0d\u8fc7\u6068\u4e86\u4ed6\u51e0\u4e2a\u6708\uff0c\u6211\u5374\u5df2\u6068\u4e86\u4ed6\u4e00\u8f88\u5b50\uff01\u201d\u8bf4\u5230\u6700\u540e\u8fd9\u51e0\u4e2a\u5b57\u65f6\uff0c\u8bed\u97f3\u7adf\u662f\u6709\u4e9b\u54fd\u54bd\u3002 \u3000\u3000\u80e1\u6590\u542c\u5979\u8bf4\u5f97\u60b2\u5207\uff0c\u4e1d\u6beb\u4e0d\u4f3c\u4f5c\u4f2a\uff0c\u4e0d\u7981\u5927\u5947\uff0c\u95ee\u9053\uff1a\u201c\u65e2\u662f\u5982\u6b64\uff0c\u6211\u51e0\u56de\u8981\u6740\u4ed6\uff0c\u4f55\u4ee5\u4f60\u53c8\u4e09\u756a\u56db\u6b21\u7684\u76f8\u6551\uff1f\u201d\u8881\u7d2b\u8863\u9053\uff1a\u201c\u662f\u4e09\u6b21\uff01\u51b3\u4e0d\u80fd\u6709\u7b2c\u56db\u6b21\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u662f\u4e09\u6b21\uff0c\u90a3\u53c8\u600e\u5730\uff1f\u201d \u3000\u3000\u4e24\u4eba\u8bf4\u8bdd\u4e4b\u9645\uff0c\u5927\u96e8\u5df2\u662f\u503e\u76c6\u800c\u4e0b\uff0c\u5c06\u4e09\u4eba\u8eab\u4e0a\u8863\u670d\u90fd\u6dcb\u5f97\u6e7f\u4e86\u3002 \u3000\u3000\u8881\u7d2b\u8863\u9053\uff1a\u201c\u4f60\u96be\u9053\u8981\u6211\u5728\u5927\u96e8\u4e2d\u7ec6\u7ec6\u89e3\u91ca\uff1f\u4f60\u4fbf\u662f\u4e0d\u6015\u96e8\uff0c\u4f60\u59b9\u5b50\u5a07\u602f\u602f\u7684\u8eab\u5b50\uff0c\u96be\u9053\u4e5f\u4e0d\u6015\u4e48\uff1f\u201d\u80e1\u6590\u9053\uff1a\u201c\u597d\uff0c\u4e8c\u59b9\uff0c\u54b1\u4eec\u8fdb\u53bb\u8bf4\u8bdd\u3002\u201d \u3000\u3000\u5f53\u4e0b\u4e09\u4eba\u8d70\u5230\u4e66\u623f\u4e4b\u4e2d\uff0c\u4e66\u7ae5\u70b9\u4e86\u8721\u70db\uff0c\u9001\u4e0a\u9999\u8317\u7ec6\u70b9\uff0c\u9000\u4e86\u51fa\u53bb\u3002\u8fd9\u4e66\u623f\u9648\u8bbe\u751a\u662f\u7cbe\u96c5\u3002\u4e1c\u58c1\u4e24\u5217\u4e66\u67b6\uff0c\u653e\u6ee1\u4e86\u56fe\u4e66\u3002\u897f\u8fb9\u4e00\u6392\u957f\u7a97\uff0c\u831c\u7eb1\u7a97\u95f4\u7eff\u7af9\u63a9\u6620\uff0c\u9690\u9690\u9001\u6765\u6842\u82b1\u9999\u6c14\u3002 \u3000\u3000\u5357\u8fb9\u5899\u4e0a\u6302\u7740\u4e00\u5e45\u8463\u5176\u660c\u7684\u4ed5\u5973\u56fe\uff1b\u4e00\u5e45\u5bf9\u8054\uff0c\u662f\u795d\u679d\u5c71\u7684\u884c\u4e66\uff0c\u5199\u7740\u767d\u4e50\u5929\u7684\u4e24\u53e5\u8bd7\uff1a\u201c\u7ea2\u8721\u70db\u79fb\u6843\u53f6\u8d77\uff0c\u7d2b\u7f57\u886b\u52a8\u67d8\u679d\u6765\u3002\u201d \u3000\u3000\u80e1\u6590\u5fc3\u4e2d\u7422\u78e8\u7740\u8881\u7d2b\u8863\u90a3\u51e0\u53e5\u5947\u602a\u7684\u8a00\u8bed\uff0c\u54ea\u91cc\u53bb\u7559\u5fc3\u4ec0\u4e48\u4e66\u753b\uff1f\u4f55\u51b5\u4ed6\u8bfb\u4e66\u751a\u5c11\uff0c\u5c31\u7b97\u770b\u4e86\u4e5f\u662f\u4e0d\u61c2\u3002\u7a0b\u7075\u7d20\u5374\u5728\u5fc3\u4e2d\u9ed8\u9ed8\u5ff5\u4e86\u4e24\u904d\uff0c\u77a7\u4e86\u4e00\u773c\u684c\u4e0a\u7684\u7ea2\u70db\uff0c\u53c8\u671b\u4e86\u4e00\u773c\u8881\u7d2b\u8863\u8eab\u4e0a\u7684\u7d2b\u7f57\u886b\uff0c\u6697\u60f3\uff1a\u201c\u5bf9\u8054\u4e0a\u8fd9\u4e24\u53e5\u8bdd\uff0c\u5012\u4f3c\u4e3a\u6b64\u60c5\u6b64\u666f\u800c\u8bbe\u3002\u53ef\u662f\u6211\u6df7\u5728\u8fd9\u4e2d\u95f4\uff0c\u5374\u53c8\u7b97\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e09\u4eba\u9ed8\u9ed8\u65e0\u8a00\uff0c\u5404\u6000\u5fc3\u4e8b\uff0c\u4f46\u542c\u5f97\u7a97\u5916\u96e8\u70b9\u6253\u5728\u6b8b\u8377\u7af9\u53f6\u4e4b\u4e0a\uff0c\u6dc5\u6ca5\u6709\u58f0\uff0c\u70db\u6cea\u7f13\u7f13\u5782\u4e0b\u3002\u7a0b\u7075\u7d20\u62ff\u8d77\u70db\u53f0\u65c1\u7684\u5c0f\u94f6\u7b77\uff0c\u631f\u4e0b\u70db\u5fc3\uff0c\u5ba4\u4e2d\u4e00\u7247\u5bc2\u9759\u3002 \u3000\u3000\u80e1\u6590\u81ea\u5e7c\u98d8\u6cca\u6c5f\u6e56\uff0c\u5982\u6b64\u4f34\u7740\u4e24\u4e2a\u7ea2\u5986\u5a07\u5973\uff0c\u9759\u5750\u4e66\u658b\uff0c\u5374\u662f\u751f\u5e73\u7b2c\u4e00\u6b21\u3002 \u3000\u3000\u8fc7\u4e86\u826f\u4e45\uff0c\u8881\u7d2b\u8863\u671b\u7740\u7a97\u5916\u96e8\u70b9\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u5341\u4e5d\u5e74\u524d\uff0c\u4e5f\u662f\u8fd9\u4e48\u4e00\u4e2a\u4e0b\u96e8\u5929\u7684\u665a\u4e0a\uff0c\u5728\u5e7f\u4e1c\u7701\u4f5b\u5c71\u9547\uff0c\u4e00\u4e2a\u5c11\u5987\u62b1\u7740\u4e00\u4e2a\u5973\u5a03\u5a03\uff0c\u5192\u96e8\u5728\u8def\u4e0a\u5954\u8dd1\u3002\u5979\u4e0d\u77e5\u9053\u5230\u4ec0\u4e48\u5730\u65b9\u53bb\u597d\uff0c\u56e0\u4e3a\u5979\u5df2\u7ed9\u4eba\u903c\u5f97\u8d70\u6295\u65e0\u8def\u3002\u5979\u7684\u4eb2\u4eba\uff0c\u90fd\u7ed9\u4eba\u5bb3\u6b7b\u4e86\uff0c\u5979\u81ea\u5df1\u53c8\u53d7\u4e86\u96be\u5f53\u7684\u7f9e\u8fb1\u3002\u5982\u679c\u4e0d\u662f\u4e3a\u4e86\u6000\u4e2d\u8fd9\u4e2a\u5c0f\u5973\u513f\uff0c\u5979\u65e9\u5c31\u8df3\u5728\u6cb3\u91cc\u81ea\u5c3d\u4e86\u3002 \u3000\u3000\u201c\u8fd9\u5c11\u5987\u59d3\u8881\uff0c\u540d\u53eb\u94f6\u59d1\u3002\u8fd9\u540d\u5b57\u5f88\u4e61\u4e0b\u6c14\uff0c\u56e0\u4e3a\u5979\u672c\u6765\u662f\u4e2a\u4e61\u4e0b\u59d1\u5a18\u3002\u5979\u957f\u5f97\u5f88\u7f8e\uff0c\u867d\u7136\u6709\u70b9\u9ed1\uff0c\u7136\u800c\u7709\u6e05\u76ee\u79c0\uff0c\u53c8\u4fcf\u53c8\u4e3d\uff0c\u4f5b\u5c71\u9547\u4e0a\u7684\u9752\u5e74\u5b50\u5f1f\u7ed9\u5979\u53d6\u4e86\u4e2a\u5916\u53f7\uff0c\u53eb\u4f5c\u2018\u9ed1\u7261\u4e39\u2019\u3002\u5979\u5bb6\u91cc\u662f\u6253\u6e14\u4eba\u5bb6\uff0c\u6bcf\u5929\u6e05\u65e9\uff0c\u5979\u4fbf\u6311\u4e86\u9c7c\u4ece\u4e61\u4e0b\u9001\u5230\u4f5b\u5c71\u7684\u9c7c\u884c\u91cc\u6765\u3002\u6709\u4e00\u5929\uff0c\u4f5b\u5c71\u9547\u7684\u51e4\u5927\u8d22\u4e3b\u51e4\u5929\u5357\u6446\u9152\u8bf7\u5ba2\uff0c\u94f6\u59d1\u6311\u4e86\u4e00\u62c5\u9c7c\u9001\u5230\u51e4\u5e9c\u91cc\u53bb\u3002\u8fd9\u771f\u53eb\u4f5c\u5929\u6709\u4e0d\u6d4b\u98ce\u4e91\uff0c\u4eba\u6709\u65e6\u5915\u7978\u798f\uff0c\u8fd9\u4e2a\u9c9c\u82b1\u4e00\u822c\u7684\u5927\u59d1\u5a18\u504f\u751f\u7ed9\u51e4\u5929\u5357\u77a7\u89c1\u4e86\u3002 \u3000\u3000\u201c\u59d3\u51e4\u7684\u59bb\u59be\u6ee1\u5802\uff0c\u4f46\u5fc3\u72b9\u672a\u8db3\uff0c\u5f3a\u903c\u7740\u73b7\u6c61\u4e86\u5979\u3002\u94f6\u59d1\u5fc3\u614c\u610f\u4e71\uff0c\u9c7c\u94b1\u4e5f\u6ca1\u6536\uff0c\u4fbf\u9003\u56de\u4e86\u5bb6\u91cc\u3002\u8c01\u77e5\u4fbf\u662f\u8fd9\u4e48\u4e00\u56de\u5b7d\u7f18\uff0c\u5979\u5c31\u6b64\u6000\u4e86\u5b55\uff0c\u5979\u7236\u4eb2\u95ee\u660e\u60c5\u7531\uff0c\u8d76\u5230\u51e4\u5e9c\u53bb\u7406\u8bba\u3002\u51e4\u8001\u7237\u53cd\u800c\u5927\u53d1\u813e\u6c14\uff0c\u53eb\u4eba\u6253\u4e86\u4ed6\u4e00\u987f\uff0c\u8bf4\u4ed6\u80e1\u8a00\u4e71\u8bed\uff0c\u6492\u8d56\u8bb9\u8bc8\u3002\u94f6\u59d1\u7684\u7239\u618b\u4e86\u4e00\u809a\u6c14\u56de\u5f97\u5bb6\u6765\uff0c\u5c31\u6b64\u4e00\u75c5\u4e0d\u8d77\uff0c\u62d6\u4e86\u51e0\u4e2a\u6708\uff0c\u7ec8\u4e8e\u6b7b\u4e86\u3002\u94f6\u59d1\u7684\u4f2f\u4f2f\u53d4\u53d4\u8bf4\u5979\u5bb3\u6b7b\u4e86\u4eb2\u751f\u7236\u4eb2\uff0c\u4e0d\u8bb8\u5979\u6234\u5b5d\uff0c\u4e0d\u8bb8\u5979\u5411\u68fa\u6750\u78d5\u5934\uff0c\u8fd8\u8bf4\u8981\u5c06\u5979\u88c5\u5728\u732a\u7b3c\u91cc\uff0c\u6d78\u5728\u6cb3\u91cc\u6df9\u6b7b\u3002 \u3000\u3000\u201c\u94f6\u59d1\u8fde\u591c\u9003\u5230\u4e86\u4f5b\u5c71\u9547\u4e0a\uff0c\u6328\u4e86\u51e0\u4e2a\u6708\uff0c\u751f\u4e0b\u4e86\u4e00\u4e2a\u5c0f\u5973\u5b69\u3002\u6bcd\u5973\u4fe9\u8fc7\u4e0d\u4e86\u65e5\u5b50\uff0c\u53ea\u597d\u5728\u9547\u4e0a\u4e5e\u8ba8\u3002\u9547\u4e0a\u7684\u4eba\u53ef\u601c\u5979\uff0c\u6709\u7684\u5c31\u65bd\u820d\u4e9b\u94f6\u7c73\u5468\u6d4e\uff0c\u80cc\u540e\u81ea\u4e0d\u514d\u8bf4\u51e4\u8001\u7237\u7684\u95f2\u8bdd\uff0c\u8bf4\u4ed6\u4f5c\u5b7d\u5bb3\u4eba\u3002\u53ea\u662f\u4ed6\u52bf\u529b\u5927\uff0c\u8c01\u4e5f\u4e0d\u6562\u5f53\u7740\u4ed6\u9762\u63d0\u8d77\u6b64\u4e8b\u3002 \u3000\u3000\u201c\u9547\u4e0a\u9c7c\u884c\u4e2d\u6709\u4e00\u4e2a\u4f19\u8ba1\u5411\u6765\u548c\u94f6\u59d1\u5f88\u8bf4\u5f97\u6765\uff0c\u5fc3\u4e2d\u4e00\u76f4\u5728\u5077\u5077\u7684\u559c\u6b22\u5979\uff0c\u4e8e\u662f\u4ed6\u6258\u4eba\u53bb\u8ddf\u94f6\u59d1\u8bf4\u8981\u5a36\u5979\u4e3a\u59bb\uff0c\u8fd8\u613f\u610f\u8ba4\u5979\u5973\u513f\u5f53\u4f5c\u81ea\u5df1\u5973\u513f\u3002\u94f6\u59d1\u81ea\u7136\u5f88\u9ad8\u5174\uff0c\u4e24\u4eba\u4fbf\u62dc\u5802\u6210\u4eb2\u3002\u90a3\u77e5\u6709\u4eba\u8ba8\u597d\u51e4\u8001\u7237\uff0c\u53bb\u7980\u544a\u4e86\u4ed6\u3002 \u3000\u3000\u201c\u51e4\u8001\u7237\u5927\u6012\uff0c\u8bf4\u9053\uff1a\u2018\u751a\u4e48\u9c7c\u884c\u7684\u4f19\u8ba1\u90a3\u4e48\u5927\u80c6\uff0c\u8fde\u6211\u8981\u8fc7\u7684\u5973\u4eba\u4ed6\u4e5f\u6562\u8981\uff1f\u2019\u5f53\u4e0b\u6d3e\u4e86\u5341\u591a\u4e2a\u5f92\u5f1f\u5230\u90a3\u9c7c\u884c\u4f19\u8ba1\u5bb6\u91cc\uff0c\u5c06\u6b63\u5728\u559d\u559c\u9152\u7684\u5ba2\u4eba\u8d76\u4e2a\u7cbe\u5149\uff0c\u628a\u53f0\u6905\u5e8a\u7076\u6363\u5f97\u7a00\u70c2\uff0c\u8fd8\u628a\u90a3\u9c7c\u884c\u4f19\u8ba1\u8d76\u51fa\u4f5b\u5c71\u9547\uff0c\u8bf4\u4ece\u6b64\u4e0d\u8bb8\u4ed6\u56de\u6765\u3002\u201d \u3000\u3000\u7830\u7684\u4e00\u54cd\uff0c\u80e1\u6590\u4f38\u624b\u5728\u684c\u4e0a\u7528\u529b\u4e00\u62cd\uff0c\u53ea\u9707\u5f97\u70db\u706b\u4e71\u6643\uff0c\u559d\u9053\uff1a\u201c\u8fd9\u5978\u8d3c\u6041\u5730\u4f5c\u6076\u591a\u7aef\uff01\u201d \u3000\u3000\u8881\u7d2b\u8863\u4e00\u773c\u4e5f\u6ca1\u671b\u4ed6\uff0c\u6cea\u5149\u83b9\u83b9\uff0c\u5411\u7740\u7a97\u5916\uff0c\u6c89\u6d78\u5728\u81ea\u5df1\u6240\u8bf4\u7684\u6545\u4e8b\u4e4b\u4e2d\uff0c\u8f7b\u8f7b\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u94f6\u59d1\u6362\u4e0b\u4e86\u65b0\u5a18\u8863\u670d\uff0c\u62b1\u4e86\u5973\u513f\uff0c\u5f53\u5373\u8ffd\u51fa\u4f5b\u5c71\u9547\u53bb\u3002 \u3000\u3000\u90a3\u665a\u5929\u4e0b\u5927\u96e8\uff0c\u628a\u6bcd\u5973\u4fe9\u5168\u8eab\u90fd\u6253\u6e7f\u4e86\u3002\u5979\u5728\u96e8\u4e2d\u53c8\u8dcc\u53c8\u5954\u7684\u8d70\u51fa\u5341\u6765\u91cc\u5730\uff0c\u5ffd\u89c1\u5927\u8def\u4e0a\u6709\u4e00\u4e2a\u4eba\u4fef\u4f0f\u5728\u5730\u3002\u5979\u53ea\u9053\u662f\u4e2a\u9189\u6c49\uff0c\u597d\u5fc3\u8981\u6276\u4ed6\u8d77\u6765\uff0c\u90a3\u77e5\u4f4e\u5934\u4e00\u770b\uff0c\u8fd9\u4eba\u6ee1\u8138\u8840\u6c61\uff0c\u65e9\u5df2\u6b7b\u4e86\uff0c\u7adf\u4fbf\u662f\u90a3\u4e2a\u8ddf\u5979\u62dc\u4e86\u5802\u7684\u9c7c\u884c\u4f19\u8ba1\u3002\u539f\u6765\u51e4\u8001\u7237\u547d\u4eba\u5019\u5728\u9547\u5916\uff0c\u4e0b\u624b\u5bb3\u6b7b\u4e86\u4ed6\u3002 \u3000\u3000\u201c\u94f6\u59d1\u4f24\u5fc3\u82e6\u695a\uff0c\u771f\u7684\u4e0d\u60f3\u518d\u6d3b\u4e86\u3002\u5979\u7528\u624b\u6316\u4e86\u4e2a\u5751\uff0c\u57cb\u4e86\u4e08\u592b\uff0c\u5f53\u65f6\u4fbf\u60f3\u5f80\u6cb3\u91cc\u8df3\u53bb\uff0c\u4f46\u6000\u4e2d\u7684\u5973\u5a03\u5b50\u5374\u4e00\u58f0\u58f0\u54ed\u5f97\u53ef\u601c\u3002\u5e26\u7740\u5979\u4e00\u8d77\u8df3\u5427\uff0c\u600e\u5fcd\u5fc3\u5bb3\u6b7b\u4eb2\u751f\u5973\u513f\uff1f\u6487\u4e0b\u5979\u5427\uff0c\u8fd9\u6837\u4e00\u4e2a\u5a74\u513f\u7559\u5728\u5927\u96e8\u4e4b\u4e2d\uff0c\u4e5f\u662f\u6b7b\u8def\u4e00\u6761\u3002\u5979\u601d\u524d\u60f3\u540e\uff0c\u54ac\u4e86\u54ac\u7259\uff0c\u7ec8\u4e8e\u62b1\u4e86\u5973\u513f\u5411\u524d\u8d70\u53bb\uff0c\u8bf4\u4ec0\u4e48\u4e5f\u5f97\u628a\u5973\u513f\u517b\u5927\u3002\u201d \u3000\u3000\u7a0b\u7075\u7d20\u542c\u5230\u8fd9\u91cc\uff0c\u6cea\u6c34\u4e00\u6ef4\u6ef4\u7684\u6d41\u4e86\u4e0b\u6765\uff0c\u542c\u8881\u7d2b\u8863\u4f4f\u53e3\u4e0d\u8bf4\u4e86\uff0c\u95ee\u9053\uff1a\u201c\u8881\u59ca\u59ca\uff0c\u540e\u6765\u600e\u6837\u4e86\uff1f\u201d \u3000\u3000\u8881\u7d2b\u8863\u53d6\u624b\u5e15\u62b9\u4e86\u62b9\u773c\u89d2\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u53eb\u6211\u59ca\u59ca\uff0c\u8be5\u5f53\u628a\u89e3\u836f\u7ed9\u6211\u670d\u4e86\u5427\uff1f\u201d\u7a0b\u7075\u7d20\u82cd\u767d\u7684\u8138\u4e00\u7ea2\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u539f\u6765\u4f60\u65e9\u77e5\u9053\u4e86\u3002\u201d\u659f\u8fc7\u4e00\u676f\u6e05\u8336\uff0c\u968f\u624b\u4ece\u6307\u7532\u4e2d\u5f39\u4e86\u4e00\u4e9b\u6de1\u9ec4\u8272\u7684\u7c89\u672b\u5728\u8336\u91cc\u3002 \u3000\u3000\u8881\u7d2b\u8863\u9053\uff1a\u201c\u59b9\u5b50\u7684\u5fc3\u5730\u5012\u597d\uff0c\u65e9\u4fbf\u5728\u6307\u7532\u4e2d\u9884\u5907\u4e86\u89e3\u836f\uff0c\u60f3\u795e\u4e0d\u77e5\u9b3c\u4e0d\u89c9\u7684\u4fbf\u7ed9\u6211\u670d\u4e0b\u3002\u201d\u8bf4\u7740\u7aef\u8fc7\u8336\u6765\uff0c\u4e00\u996e\u800c\u5c3d\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u4f60\u4e2d\u7684\u4e5f\u4e0d\u662f\u4ec0\u4e48\u81f4\u547d\u7684\u6bd2\u836f\uff0c\u53ea\u662f\u8981\u5927\u75c5\u4e00\u573a\uff0c\u59d4\u987f\u51e0\u4e2a\u6708\uff0c\u4f7f\u5f97\u80e1\u5927\u54e5\u53bb\u6740\u90a3\u51e4\u5929\u5357\u65f6\uff0c\u4f60\u4e0d\u80fd\u518d\u51fa\u624b\u76f8\u6551\u3002\u201d\u8881\u7d2b\u8863\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u65e9\u77e5\u4e2d\u4e86\u4f60\u7684\u6bd2\u624b\uff0c\u53ea\u662f\u4f60\u5982\u4f55\u4e0b\u7684\u6bd2\uff0c\u6211\u59cb\u7ec8\u60f3\u4e0d\u8d77\u6765\u3002\u8fdb\u8fd9\u5c4b\u5b50\u4e4b\u540e\uff0c\u6211\u53ef\u6ca1\u559d\u8fc7\u4e00\u53e3\u8336\uff0c\u5403\u8fc7\u534a\u7247\u70b9\u5fc3\u3002\u201d \u3000\u3000\u80e1\u6590\u5fc3\u5934\u6697\u60ca\uff1a\u201c\u539f\u6765\u8881\u59d1\u5a18\u867d\u7136\u6781\u610f\u63d0\u9632\uff0c\u7ec8\u7a76\u8fd8\u662f\u7740\u4e86\u4e8c\u59b9\u7684\u9053\u513f\u3002\u201d \u3000\u3000\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u4f60\u548c\u80e1\u5927\u54e5\u5728\u5899\u5916\u76f8\u6597\uff0c\u6211\u63b7\u5200\u7ed9\u5927\u54e5\u3002\u90a3\u53e3\u5200\u7684\u5200\u5203\u4e0a\u6709\u4e00\u5c42\u8584\u8584\u6bd2\u7c89\uff0c\u4f60\u7684\u8f6f\u97ad\u4e0a\u4fbf\u6cbe\u7740\u4e86\uff0c\u4f60\u624b\u4e0a\u4e5f\u6cbe\u7740\u4e86\u3002\u5f85\u4f1a\u5f97\u628a\u5355\u5200\u8f6f\u97ad\u90fd\u5728\u6e05\u6c34\u4e2d\u51b2\u6d17\u5e72\u51c0\u3002\u201d\u8881\u7d2b\u8863\u548c\u80e1\u6590\u5bf9\u671b\u4e00\u773c\uff0c\u5747\u60f3\uff1a\u201c\u5982\u6b64\u4e0b\u6bd2\uff0c\u771f\u662f\u6559\u4eba\u9632\u4e0d\u80dc\u9632\u3002\u201d \u3000\u3000\u7a0b\u7075\u7d20\u7ad9\u8d77\u8eab\u6765\uff0c\u655b\u887d\u884c\u793c\uff0c\u8bf4\u9053\uff1a\u201c\u8881\u59ca\u59ca\uff0c\u59b9\u5b50\u8ddf\u4f60\u8d54\u4e0d\u662f\u5566\u3002\u6211\u5b9e\u4e0d\u77e5\u4e2d\u95f4\u6709\u8fd9\u8bb8\u591a\u539f\u59d4\u66f2\u6298\u3002\u201d\u8881\u7d2b\u8863\u8d77\u8eab\u8fd8\u793c\uff0c\u9053\uff1a\u201c\u4e0d\u7528\u5ba2\u6c14\uff0c\u591a\u8499\u4f60\u624b\u4e0b\u7559\u60c5\uff0c\u4e0b\u7684\u4e0d\u662f\u81f4\u547d\u6bd2\u836f\u3002\u201d \u3000\u3000\u4e24\u4eba\u76f8\u5bf9\u4e00\u7b11\uff0c\u5404\u81ea\u5c31\u5750\u3002 \u3000\u3000\u80e1\u6590\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u90a3\u51e4\u5929\u5357\u4fbf\u662f\u4f60\u2026\u2026\u4f60\u7684\u2026\u2026\u201d \u3000\u3000\u8881\u7d2b\u8863\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u90a3\u94f6\u59d1\u662f\u6211\u5988\u5988\uff0c\u51e4\u5929\u5357\u4fbf\u662f\u6211\u7684\u4eb2\u751f\u4e4b\u7236\u3002\u4ed6\u867d\u5bb3\u5f97\u6211\u5a18\u513f\u4fe9\u5982\u6b64\u60e8\u6cd5\uff0c\u4f46\u6211\u5e08\u7236\u8a00\u9053\uff1a\u2018\u4eba\u65e0\u7236\u6bcd\uff0c\u4f55\u6709\u6b64\u8eab\uff1f\u2019\u6211\u62dc\u522b\u5e08\u7236\u3001\u4e1c\u6765\u4e2d\u539f\u4e4b\u65f6\uff0c\u5e08\u7236\u5429\u5490\u6211\u8bf4\uff1a\u2018\u4f60\u7236\u4eb2\u4f5c\u6076\u591a\u7aef\uff0c\u6b64\u751f\u5fc5\u906d\u6a2a\u7978\u3002\u4f60\u53ef\u6551\u4ed6\u4e09\u6b21\u6027\u547d\uff0c\u4ee5\u4e86\u7236\u5973\u4e4b\u60c5\u3002\u81ea\u6b64\u4f60\u662f\u4f60\uff0c\u4ed6\u662f\u4ed6\uff0c\u4e0d\u518d\u76f8\u5e72\u3002\u2019\u80e1\u5927\u54e5\uff0c\u5728\u4f5b\u5c71\u9547\u5317\u5e1d\u5e99\u4e2d\u6211\u6551\u4e86\u4ed6\u4e00\u6b21\uff0c\u90a3\u665a\u6e58\u5983\u5e99\u4e2d\u6551\u4e86\u4ed6\u4e00\u6b21\uff0c\u4eca\u665a\u53c8\u6551\u4e86\u4ed6\u4e00\u6b21\u3002\u4e0b\u56de\u82e5\u518d\u649e\u5728\u6211\u624b\u91cc\uff0c\u6211\u5148\u8981\u6740\u4e86\u4ed6\uff0c\u7ed9\u6211\u6b7b\u4e86\u7684\u82e6\u547d\u5988\u5988\u62a5\u4ec7\u96ea\u6068\u3002\u201d\u8bf4\u7740\u795e\u8272\u51db\u7136\uff0c\u773c\u5149\u4e2d\u6ee1\u662f\u6068\u610f\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u4ee4\u5802\u8fc7\u4e16\u4e86\u4e48\uff1f\u201d\u8881\u7d2b\u8863\u9053\uff1a\u201c\u6211\u5988\u5988\u9003\u51fa\u4f5b\u5c71\u9547\u540e\uff0c\u4e00\u8def\u4e5e\u98df\u5411\u5317\u3002\u5979\u53ea\u60f3\u79bb\u5f00\u4f5b\u5c71\u8d8a\u8fdc\u8d8a\u597d\uff0c\u6c38\u4e0d\u8981\u518d\u89c1\u51e4\u8001\u7237\u7684\u9762\uff0c\u6c38\u4e0d\u518d\u542c\u5230\u4ed6\u7684\u540d\u5b57\u3002\u5728\u9053\u4e0a\u6d41\u843d\u4e86\u51e0\u4e2a\u6708\uff0c\u540e\u6765\u5230\u4e86\u6c5f\u897f\u7701\u5357\u660c\u5e9c\uff0c\u6295\u5165\u4e86\u4e00\u5bb6\u59d3\u6c64\u7684\u5e9c\u4e2d\u53bb\u505a\u5973\u4f63\u2026\u2026\u201d\u80e1\u6590\u201c\u54e6\u201d\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6c5f\u897f\u5357\u660c\u5e9c\u6c64\u5bb6\uff0c\u4e0d\u77e5\u548c\u90a3\u7518\u9716\u60e0\u4e03\u7701\u6c64\u5927\u4fa0\u6709\u5e72\u7cfb\u6ca1\u6709\uff1f\u201d \u3000\u3000\u8881\u7d2b\u8863\u542c\u5230\u201c\u7518\u9716\u60e0\u4e03\u7701\u6c64\u5927\u4fa0\u201d\u516b\u5b57\uff0c\u5634\u8fb9\u808c\u8089\u5fae\u5fae\u4e00\u52a8\uff0c\u9053\uff1a\u201c\u6211\u5988\u4fbf\u662f\u6b7b\u5728\u6c64\u2026\u2026\u6c64\u5927\u4fa0\u5e9c\u4e0a\u7684\u3002\u6211\u5988\u6b7b\u540e\u7b2c\u4e09\u5929\uff0c\u6211\u5e08\u7236\u4fbf\u63a5\u4e86\u6211\u53bb\uff0c\u5e26\u6211\u5230\u56de\u7586\uff0c\u9694\u4e86\u4e00\u5341\u516b\u5e74\uff0c\u8fd9\u624d\u56de\u6765\u4e2d\u539f\u3002\u201d \u3000\u3000\u80e1\u6590\u9053\uff1a\u201c\u4e0d\u77e5\u5c0a\u5e08\u7684\u4e0a\u4e0b\u600e\u751f\u79f0\u547c\uff1f\u8881\u59d1\u5a18\u5404\u5bb6\u5404\u6d3e\u7684\u6b66\u529f\u65e0\u6240\u4e0d\u4f1a\uff0c\u65e0\u6240\u4e0d\u7cbe\uff0c\u5c0a\u5e08\u5fc5\u662f\u4e00\u4f4d\u65f7\u4e16\u96be\u9022\u7684\u5947\u4eba\u3002\u90a3\u82d7\u5927\u4fa0\u53f7\u79f0\u2018\u6253\u904d\u5929\u4e0b\u65e0\u654c\u624b\u2019\uff0c\u4e5f\u4e0d\u89c1\u5f97\u6709\u8fd9\u7b49\u672c\u4e8b\uff01\u201d \u3000\u3000\u8881\u7d2b\u8863\u9053\uff1a\u201c\u5bb6\u5e08\u7684\u540d\u8bb3\u56e0\u672a\u5f97\u5979\u8001\u4eba\u5bb6\u5141\u53ef\uff0c\u6682\u4e14\u4e0d\u80fd\u544a\u77e5\uff0c\u8fd8\u8bf7\u539f\u8c05\u3002\u518d\u8bf4\uff0c\u6211\u81ea\u5df1\u7684\u540d\u5b57\u4e5f\u4e0d\u662f\u771f\u7684\uff0c\u4e0d\u4e45\u80e1\u5927\u54e5\u548c\u7a0b\u5bb6\u59b9\u5b50\u81ea\u4f1a\u77e5\u9053\u3002\u81f3\u4e8e\u90a3\u4f4d\u82d7\u5927\u4fa0\uff0c\u6211\u4eec\u5728\u56de\u7586\u4e5f\u66fe\u542c\u5230\u8fc7\u4ed6\u7684\u540d\u5934\u3002\u5f53\u65f6\u7ea2\u82b1\u4f1a\u7684\u65e0\u5c18\u9053\u957f\u5f88\u4e0d\u670d\u6c14\uff0c\u5b9a\u8981\u5230\u4e2d\u539f\u6765\u8ddf\u4ed6\u8f83\u91cf\u8f83\u91cf\uff0c\u4f46\u8d75\u534a\u5c71\u8d75\u4e09\u53d4\u2026\u2026\u201d\u5979\u8bf4\u5230\u201c\u8d75\u4e09\u53d4\u201d\u4e09\u5b57\u65f6\uff0c\u5411\u80e1\u6590\u62bf\u5634\u4e00\u7b11\uff0c\u610f\u601d\u8bf4\uff1a\u201c\u53c8\u7ed9\u4f60\u8ba8\u4e86\u4fbf\u5b9c\u53bb\u5566\uff01\u201d\u7eed\u9053\uff1a\u201c\u8d75\u534a\u5c71\u77e5\u9053\u5176\u4e2d\u539f\u59d4\uff0c\u8bf4\u82d7\u5927\u4fa0\u6240\u4ee5\u7528\u8fd9\u5916\u53f7\uff0c\u5e76\u975e\u72c2\u5984\u81ea\u5927\uff0c\u5374\u662f\u53e6\u6709\u82e6\u8877\uff0c\u542c\u8bf4\u4ed6\u662f\u4e3a\u62a5\u7236\u4ec7\uff0c\u6545\u610f\u6fc0\u4f7f\u8fbd\u4e1c\u7684\u4e00\u4f4d\u9ad8\u624b\u524d\u6765\u627e\u4ed6\u3002\u540e\u6765\u6c5f\u6e56\u4e0a\u7eb7\u7eb7\u4f20\u8a00\uff0c\u4ed6\u7236\u4ec7\u5df2\u62a5\uff0c\u66fe\u6570\u6b21\u5f53\u4f17\u5ba3\u79f0\uff0c\u51b3\u4e0d\u6562\u7528\u8fd9\u4e2a\u540d\u53f7\uff0c\u8bf4\u9053\uff1a\u2018\u4ec0\u4e48\u6253\u904d\u5929\u4e0b\u65e0\u654c\u624b\uff0c\u8fd9\u5916\u53f7\u513f\u72d7\u5c41\u4e0d\u901a\u3002\u5927\u4fa0\u80e1\u4e00\u5200\u7684\u6b66\u529f\uff0c\u5c31\u6bd4\u6211\u9ad8\u5f3a\u5f97\u591a\u4e86\uff01\u2019\u201d \u3000\u3000\u80e1\u6590\u5fc3\u5934\u4e00\u51db\uff0c\u95ee\u9053\uff1a\u201c\u82d7\u4eba\u51e4\u5f53\u771f\u8bf4\u8fc7\u8fd9\u53e5\u8bdd\uff1f\u201d \u3000\u3000\u8881\u7d2b\u8863\u9053\uff1a\u201c\u6211\u81ea\u7136\u6ca1\u4eb2\u8033\u542c\u5230\uff0c\u90a3\u662f\u8d75\u2026\u2026\u8d75\u534a\u5c71\u8bf4\u7684\u3002 \u3000\u3000\u65e0\u5c18\u9053\u957f\u542c\u4e86\u8fd9\u8bdd\uff0c\u96c4\u5fc3\u5927\u8d77\uff0c\u5374\u53c8\u8981\u6765\u8ddf\u90a3\u4f4d\u80e1\u4e00\u5200\u6bd4\u5212\u6bd4\u5212\u3002\u540e\u6765\u6253\u542c\u4e0d\u5230\u8fd9\u4f4d\u80e1\u5927\u4fa0\u8eab\u5728\u4f55\u65b9\uff0c\u4e5f\u53ea\u5f97\u7f62\u4e86\u3002\u90a3\u4e00\u5e74\u8d75\u534a\u5c71\u6765\u5230\u4e2d\u539f\uff0c\u9047\u89c1\u4e86\u4f60\uff0c\u56de\u53bb\u56de\u7586\u540e\uff0c\u597d\u751f\u79f0\u8d5e\u4f60\u82f1\u96c4\u4e86\u5f97\u3002\u53ea\u662f\u90a3\u65f6\u6211\u5e74\u7eaa\u8fd8\u5c0f\uff0c\u4ed6\u4eec\u8bf4\u4ec0\u4e48\u6211\u4e5f\u4e0d\u61c2\u3002\u8fd9\u6b21\u5c0f\u59b9\u4e1c\u6765\uff0c\u6587\u56db\u5a76\u4fbf\u8981\u6211\u9a91\u4e86\u5979\u7684\u767d\u9a6c\u6765\uff0c\u5979\u8bf4\u5018\u82e5\u9047\u5230\u2018\u90a3\u4f4d\u59d3\u80e1\u7684\u5c11\u5e74\u8c6a\u6770\uff0c\u4fbf\u628a\u6211\u8fd9\u5339\u5750\u9a91\u8d60\u4e86\u4e0e\u4ed6\u3002\u2019\u201d \u3000\u3000\u80e1\u6590\u5947\u9053\uff1a\u201c\u8fd9\u4f4d\u6587\u56db\u5a76\u662f\u8c01\uff1f\u5979\u8ddf\u6211\u7d20\u4e0d\u76f8\u8bc6\uff0c\u4f55\u4ee5\u8d60\u6211\u8fd9\u7b49\u91cd\u793c\uff1f\u201d \u3000\u3000\u8881\u7d2b\u8863\u9053\uff1a\u201c\u8bf4\u8d77\u6587\u56db\u5a76\u6765\uff0c\u5f53\u5e74\u6c5f\u6e56\u4e0a\u5927\u5927\u6709\u540d\u3002\u5979\u4fbf\u662f\u5954\u96f7\u624b\u6587\u6cf0\u6765\u6587\u56db\u53d4\u7684\u5a18\u5b50\uff0c\u59d3\u9a86\u540d\u51b0\uff0c\u4eba\u79f0\u2018\u9e33\u9e2f\u5200\u2019\u7684\u4fbf\u662f\u3002\u5979\u542c\u8d75\u534a\u5c71\u8bf4\u53ca\u4f60\u5728\u5546\u5bb6\u5821\u5927\u7834\u94c1\u5385\u4e4b\u4e8b\uff0c\u53c8\u542c\u8bf4\u4f60\u5f88\u559c\u6b22\u8fd9\u5339\u767d\u9a6c\uff0c\u5f53\u65f6\u4fbf\u57cb\u6028\u4ed6\u9053\uff1a\u2018\u4e09\u54e5\uff0c\u65e2\u6709\u8fd9\u7b49\u4eba\u7269\uff0c\u4f60\u4f55\u4e0d\u4fbf\u5c06\u8fd9\u5339\u9a6c\u8d60\u4e86\u4e0e\u4ed6\uff1f\u96be\u9053\u4f60\u8d75\u4e09\u7237\u7ed3\u4ea4\u5f97\u5c11\u5e74\u82f1\u96c4\uff0c\u6211\u6587\u56db\u5a18\u5b50\u7ed3\u4ea4\u4e0d\u5f97\uff1f\u2019\u201d \u3000\u3000\u80e1\u6590\u542c\u4e86\uff0c\u8fd9\u624d\u660e\u767d\u8881\u7d2b\u8863\u90a3\u65e5\u5728\u5ba2\u5e97\u4e2d\u7559\u4e0b\u67ec\u5e16\uff0c\u8bf4\u4ec0\u4e48\u201c\u9a6c\u5f52\u539f\u4e3b\u201d\uff0c\u539f\u6765\u4e43\u662f\u4e3a\u6b64\uff0c\u5fc3\u4e2d\u5bf9\u9a86\u51b0\u597d\u751f\u611f\u6fc0\uff0c\u6697\u60f3\uff1a\u201c\u5982\u6b64\u5b9d\u9a6c\uff0c\u4e07\u91d1\u96be\u6c42\u3002\u8fd9\u4f4d\u6587\u56db\u5a18\u5b50\u548c\u6211\u76f8\u9694\u4e07\u91cc\uff0c\u53ea\u51ed\u4ed6\u4eba\u7247\u8a00\u79f0\u8bb8\uff0c\u4fbf\u5373\u5272\u7231\u76f8\u8d60\uff0c\u8fd9\u756a\u9686\u60c5\u9ad8\u4e49\uff0c\u6211\u80e1\u6590\u5f53\u771f\u662f\u96be\u4ee5\u4e3a\u62a5\u4e86\u3002\u201d\u53c8\u95ee\uff1a\u201c\u8d75\u4e09\u54e5\u60f3\u5fc5\u5b89\u597d\u3002\u6b64\u95f4\u4e8b\u4e86\u4e4b\u540e\uff0c\u6211\u4fbf\u60f3\u8d74\u56de\u7586\u4e00\u884c\uff0c\u4e00\u6765\u63a2\u8bbf\u8d75\u4e09\u54e5\uff0c\u4e8c\u6765\u524d\u53bb\u62dc\u89c1\u4f17\u4f4d\u524d\u8f88\u82f1\u96c4\u3002\u201d \u3000\u3000\u8881\u7d2b\u8863\u9053\uff1a\u201c\u90a3\u5012\u4e0d\u7528\u3002\u4ed6\u4eec\u90fd\u8981\u6765\u5566\u3002\u201d \u3000\u3000\u80e1\u6590\u4e00\u542c\u5927\u559c\uff0c\u4f38\u624b\u5728\u684c\u4e0a\u4e00\u62cd\uff0c\u7ad9\u8d77\u8eab\u6765\uff0c\u8bf4\u4e0d\u51fa\u7684\u5fc3\u75d2\u96be\u6414\u3002\u7a0b\u7075\u7d20\u77e5\u4ed6\u5fc3\u610f\uff0c\u9053\uff1a\u201c\u6211\u7ed9\u4f60\u53d6\u9152\u53bb\u3002\u201d\u51fa\u623f\u5429\u5490\u4e66\u7ae5\uff0c\u9001\u4e86\u4e03\u516b\u74f6\u9152\u6765\u3002\u80e1\u6590\u8fde\u5c3d\u4e24\u74f6\uff0c\u60f3\u5230\u4e0d\u4e45\u4fbf\u53ef\u548c\u4f17\u4f4d\u82f1\u96c4\u76f8\u89c1\uff0c\u8c6a\u6c14\u6a2a\u751f\uff0c\u8fde\u95ee\uff1a\u201c\u8d75\u4e09\u54e5\u4ed6\u4eec\u4f55\u65f6\u5230\u6765\uff1f\u201d \u3000\u3000\u8881\u7d2b\u8863\u8138\u8272\u90d1\u91cd\uff0c\u8bf4\u9053\uff1a\u201c\u518d\u9694\u56db\u5929\uff0c\u4fbf\u662f\u4e2d\u79cb\uff0c\u90a3\u662f\u5929\u4e0b\u638c\u95e8\u4eba\u5927\u4f1a\u7684\u6b63\u65e5\u3002\u8fd9\u4e2a\u5927\u4f1a\u662f\u798f\u5eb7\u5b89\u53ec\u96c6\u7684\u3002\u4ed6\u5b98\u5c45\u5175\u90e8\u5c1a\u4e66\u3001\u603b\u7ba1\u5185\u52a1\u5e9c\u5927\u81e3\uff0c\u6267\u638c\u5929\u4e0b\u5175\u9a6c\u5927\u6743\uff0c\u7687\u4eb2\u56fd\u621a\u4e2a\u4e2a\u8be5\u5c5e\u4ed6\u7ba1\uff0c\u5374\u4f55\u4ee5\u8981\u6765\u548c\u6c5f\u6e56\u4e0a\u7684\u8c6a\u5ba2\u6253\u4ea4\u9053\uff1f\u201d \u3000\u3000\u80e1\u6590\u9053\uff1a\u201c\u6211\u4e5f\u4e00\u76f4\u5728\u7422\u78e8\u6b64\u4e8b\uff0c\u60f3\u6765\u4ed6\u662f\u8981\u7f51\u7f57\u666e\u5929\u4e0b\u82f1\u96c4\u597d\u6c49\uff0c\u4f9b\u671d\u5ef7\u9a71\u4f7f\uff0c\u4fbf\u50cf\u662f\u7687\u5e1d\u7528\u8003\u72b6\u5143\u3001\u8003\u8fdb\u58eb\u7684\u6cd5\u5b50\u6765\u7b3c\u7edc\u8bfb\u4e66\u4eba\u4e00\u822c\u3002\u201d\u8881\u7d2b\u8863\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5f53\u5e74\u5510\u592a\u5b97\u89c1\u5e94\u8bd5\u4e3e\u5b50\u4ece\u8003\u573a\u4e2d\u9c7c\u8d2f\u800c\u51fa\uff0c\u559c\u9053\uff1a\u2018\u5929\u4e0b\u82f1\u96c4\uff0c\u5165\u6211\u5f40\u4e2d\u77e3\u3002\u2019\u798f\u5eb7\u5b89\u5f00\u8fd9\u4e2a\u5927\u4f1a\uff0c\u81ea\u4e5f\u60f3\u4ee5\u529f\u540d\u5229\u7984\u6765\u5f15\u8bf1\u5929\u4e0b\u82f1\u96c4\u3002\u53ef\u662f\u4ed6\u53e6\u6709\u4e00\u4ef6\u5207\u80a4\u4e4b\u75db\uff0c\u5374\u662f\u5916\u4eba\u6240\u4e0d\u77e5\u7684\u3002\u798f\u5eb7\u5b89\u66fe\u7ecf\u7ed9\u8d75\u534a\u5c71\u3001\u6587\u56db\u53d4\u3001\u65e0\u5c18\u9053\u957f\u4ed6\u4eec\u902e\u53bb\u8fc7\uff0c\u8fd9\u4ef6\u4e8b\u4f60\u53ef\u77e5\u9053\u4e48\uff1f\u201d \u3000\u3000\u80e1\u6590\u53c8\u60ca\u53c8\u559c\uff0c\u4ef0\u8116\u5b50\u559d\u4e86\u4e00\u5927\u7897\u9152\uff0c\u8bf4\u9053\uff1a\u201c\u75db\u5feb\uff0c\u75db\u5feb\uff01\u6211\u5374\u6ca1\u542c\u8bf4\u8fc7\uff0c\u65e0\u5c18\u9053\u957f\u3001\u6587\u56db\u7237\u4ed6\u4eec\u5982\u6b64\u82f1\u96c4\u4e86\u5f97\uff0c\u5f53\u771f\u4ee4\u4eba\u503e\u5012\u3002\u201d \u3000\u3000\u8881\u7d2b\u8863\u62bf\u5634\u7b11\u9053\uff1a\u201c\u53e4\u4eba\u4ee5\u6c49\u4e66\u4e0b\u9152\uff0c\u4f60\u5374\u4ee5\u82f1\u96c4\u8c6a\u6770\u5927\u5feb\u4eba\u5fc3\u4e4b\u4e8b\u4e0b\u9152\u3002\u82e5\u662f\u8bf4\u8d77\u6587\u56db\u53d4\u4ed6\u4eec\u7684\u4f5c\u4e3a\uff0c\u4f60\u4fbf\u662f\u5343\u676f\u4e0d\u9189\uff0c\u4e5f\u8981\u53eb\u4f60\u9189\u5367\u4e09\u65e5\u3002\u201d\u80e1\u6590\u5012\u4e86\u4e00\u7897\u9152\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u4fbf\u8bf7\u8bf4\u3002\u201d \u3000\u3000\u8881\u7d2b\u8863\u9053\uff1a\u201c\u8fd9\u4e9b\u4e8b\u513f\u8bf4\u6765\u8bdd\u957f\uff0c\u4e00\u65f6\u4e4b\u95f4\u4e5f\u8bf4\u4e0d\u4e86\u3002\u5927\u7565\u800c\u8a00\uff0c\u6587\u56db\u53d4\u4ed6\u4eec\u77e5\u9053\u798f\u5eb7\u5b89\u5f88\u5f97\u5f53\u4eca\u7687\u5e1d\u4e7e\u9686\u7684\u5ba0\u7231\uff0c\u56e0\u6b64\u4e0a\u5c06\u4ed6\u6349\u4e86\u53bb\uff0c\u80c1\u8feb\u7687\u5e1d\u91cd\u5efa\u798f\u5efa\u5c11\u6797\u5bfa\uff0c\u53c8\u7b54\u5e94\u4e0d\u5bb3\u7ea2\u82b1\u4f1a\u6563\u5728\u5404\u7701\u7684\u597d\u6c49\u670b\u53cb\uff0c\u8fd9\u624d\u653e\u4e86\u4ed6\u51fa\u6765\u3002\u201d \u3000\u3000\u80e1\u6590\u4e00\u62cd\u5927\u817f\uff0c\u8bf4\u9053\uff1a\u201c\u798f\u5eb7\u5b89\u81ea\u7136\u4ee5\u4e3a\u662f\u5947\u803b\u5927\u8fb1\u3002\u4ed6\u62db\u96c6\u5929\u4e0b\u6b66\u6797\u5404\u5bb6\u5404\u6d3e\u7684\u638c\u95e8\u4eba\uff0c\u60f3\u662f\u8981\u548c\u6587\u56db\u7237\u4ed6\u4eec\u518d\u51b3\u96cc\u96c4\u4e86\uff1f\u201d\u8881\u7d2b\u8863\u9053\uff1a\u201c\u5bf9\u4e86\uff01\u6b64\u4e8b\u4f60\u731c\u4e2d\u4e86\u4e00\u5927\u534a\u3002\u4eca\u5e74\u79cb\u51ac\u4e4b\u4ea4\uff0c\u798f\u5eb7\u5b89\u6599\u5f97\u6587\u56db\u53d4\u4ed6\u4eec\u8981\u4e0a\u5317\u4eac\u6765\uff0c\u662f\u4ee5\u5148\u884c\u62db\u96c6\u5404\u7701\u6b66\u6797\u597d\u624b\u3002\u4ed6\u81ea\u5728\u5341\u5e74\u524d\u5403\u4e86\u90a3\u4e2a\u5927\u82e6\u5934\u4e4b\u540e\uff0c\u624d\u77e5\u4ed6\u624b\u4e0b\u5175\u9a6c\u867d\u591a\uff0c\u5374\u4e0d\u8db3\u4ee5\u4e0e\u6b66\u6797\u8c6a\u6770\u4e3a\u654c\u3002\u201d\u80e1\u6590\u9f13\u638c\u7b11\u9053\uff1a\u201c\u4f60\u593a\u4e86\u8fd9\u4e5d\u5bb6\u534a\u638c\u95e8\uff0c\u539f\u6765\u662f\u8981\u5148\u6740\u4ed6\u4e00\u4e2a\u4e0b\u9a6c\u5a01\u3002\u201d \u3000\u3000\u8881\u7d2b\u8863\u9053\uff1a\u201c\u6211\u5e08\u7236\u548c\u6587\u56db\u53d4\u4ed6\u4eec\u4ea4\u60c5\u5f88\u6df1\u3002\u4f46\u5c0f\u59b9\u8fd9\u6b21\u56de\u5230\u4e2d\u539f\uff0c\u5374\u662f\u4e3a\u4e86\u81ea\u5df1\u7684\u79c1\u4e8b\u3002\u6211\u5148\u5230\u5e7f\u4e1c\u4f5b\u5c71\uff0c\u8981\u77a7\u77a7\u51e4\u8001\u7237\u5230\u5e95\u662f\u600e\u6837\u4e00\u4e2a\u4eba\u7269\uff0c\u4e5f\u662f\u673a\u7f18\u5de7\u5408\uff0c\u4e0d\u4f46\u6551\u4e86\u4ed6\u7684\u6027\u547d\uff0c\u8fd8\u63a2\u542c\u5230\u4e86\u5929\u4e0b\u638c\u95e8\u4eba\u5927\u4f1a\u7684\u8baf\u606f\u3002\u6211\u6709\u4e8b\u672a\u4e86\uff0c\u4e0d\u80fd\u8d76\u53bb\u56de\u7586\u62a5\u8baf\uff0c\u4e8e\u662f\u4e5f\u4e0d\u6015\u80e1\u5927\u54e5\u89c1\u7b11\uff0c\u4e00\u8def\u4ece\u5357\u5230\u5317\uff0c\u80e1\u95f9\u5230\u4e86\u5317\u4eac\uff0c\u4e5f\u597d\u8ba9\u798f\u5eb7\u5b89\u77e5\u6653\uff0c\u4ed6\u7684\u4ec0\u4e48\u52b3\u4ec0\u5b50\u638c\u95e8\u4eba\u5927\u4f1a\uff0c\u672a\u5fc5\u80fd\u7ba1\u4ec0\u4e48\u4e8b\u3002\u201d \u3000\u3000\u80e1\u6590\u5fc3\u5ff5\u4e00\u52a8\uff1a\u201c\u60f3\u662f\u8d75\u4e09\u54e5\u5728\u4eba\u524d\u628a\u6211\u5938\u5f97\u592a\u8fc7\u4e86\uff0c\u8fd9\u4f4d\u59d1\u5a18\u4e0d\u670d\u6c14\uff0c\u4ee5\u81f4\u4e00\u8def\u4e0a\u5c3d\u662f\u8ddf\u6211\u8f83\u91cf\u3002\u201d\u5411\u8881\u7d2b\u8863\u77aa\u4e86\u4e00\u773c\uff0c\u8bf4\u9053\uff1a\u201c\u8fd8\u6709\uff0c\u4e5f\u597d\u8ba9\u8d75\u534a\u5c71\u4ed6\u4eec\u77e5\u9053\uff0c\u90a3\u4e2a\u59d3\u80e1\u7684\u5c11\u5e74\uff0c\u672a\u5fc5\u771f\u6709\u4ec0\u4e48\u672c\u4e8b\u3002\u201d \u3000\u3000\u8881\u7d2b\u8863\u683c\u683c\u800c\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u4ece\u5e7f\u4e1c\u8f83\u91cf\u5230\u5317\u4eac\uff0c\u6211\u4e5f\u6ca1\u80fd\u5360\u4e86\u4f60\u7684\u4e0a\u98ce\u3002\u80e1\u5927\u54e5\uff0c\u65e5\u540e\u6211\u89c1\u5230\u8d75\u534a\u5c71\u65f6\uff0c\u4f60\u731c\u6211\u8981\u8ddf\u4ed6\u8bf4\u4ec0\u4e48\u8bdd\uff1f\u201d\u80e1\u6590\u6447\u5934\uff1a\u201c\u6211\u4e0d\u77e5\u9053\u3002\u201d\u8881\u7d2b\u8863\u6b63\u8272\u9053\uff1a\u201c\u6211\u8bf4\uff1a\u2018\u8d75\u4e09\u53d4\uff0c\u4f60\u7684\u5c0f\u4e49\u5f1f\u540d\u4e0d\u865a\u4f20\uff0c\u679c\u7136\u662f\u4e00\u4f4d\u82f1\u96c4\u597d\u6c49\uff01\u2019\u201d \u3000\u3000\u80e1\u6590\u4e07\u4e07\u6599\u60f3\u4e0d\u5230\uff0c\u8fd9\u4e2a\u4e00\u76f4\u8ddf\u81ea\u5df1\u4f5c\u5bf9\u4e3a\u96be\u7684\u59d1\u5a18\uff0c\u7adf\u4f1a\u5f53\u9762\u79f0\u8d5e\u8d77\u81ea\u5df1\u6765\uff0c\u4e0d\u7531\u5f97\u6ee1\u8138\u901a\u7ea2\uff0c\u5927\u662f\u53d1\u7a98\uff0c\u5fc3\u4e2d\u5374\u751a\u611f\u751c\u7f8e\u8212\u7545\u3002\u4ece\u5e7f\u4e1c\u76f4\u5230\u5317\u4eac\uff0c\u98ce\u5c18\u884c\u65c5\uff0c\u95f4\u5173\u5343\u91cc\uff0c\u4ed6\u8111\u6d77\u4e4b\u4e2d\u65e0\u65e5\u4e0d\u6709\u8881\u7d2b\u8863\u7684\u5f71\u5b50\u5728\uff0c\u53ea\u662f\u6bcf\u60f3\u5230\u8fd9\u4f4d\u53c8\u7f8e\u4e3d\u52a8\u4eba\u53c8\u5201\u94bb\u53e4\u602a\u7684\u59d1\u5a18\uff0c\u4e03\u5206\u6b22\u559c\u4e4b\u4e2d\uff0c\u4e0d\u514d\u5e26\u7740\u4e24\u5206\u56f0\u60d1\uff0c\u4e00\u5206\u7740\u607c\u3002\u4eca\u591c\u4e00\u5915\u957f\u8c08\uff0c\u5acc\u9699\u5c3d\u53bb\uff0c\u539f\u6765\u4e2d\u95f4\u7adf\u6709\u8fd9\u8bb8\u591a\u539f\u59d4\uff0c\u600e\u4e0d\u4ee4\u4ed6\u5728\u4e09\u5206\u9152\u9189\u4e4b\u4e2d\uff0c\u518d\u52a0\u4e0a\u4e86\u4e09\u5206\u5fc3\u9189\uff1f \u3000\u3000\u8fd9\u65f6\u7a97\u5916\u96e8\u58f0\u5df2\u7ec6\uff0c\u4e00\u679d\u8721\u70db\u4e5f\u6e10\u6e10\u70b9\u5230\u4e86\u5c3d\u5934\u3002\u80e1\u6590\u53c8\u559d\u4e86\u4e00\u5927\u7897\u9152\uff0c\u8bf4\u9053\uff1a\u201c\u8881\u59d1\u5a18\uff0c\u4f60\u8bf4\u6709\u4e8b\u672a\u4e86\uff0c\u4e0d\u77e5\u6709\u7528\u5f97\u7740\u6211\u7684\u5730\u65b9\u5417\uff1f\u201d\u8881\u7d2b\u8863\u6447\u5934\u9053\uff1a\u201c\u591a\u8c22\u4e86\uff0c\u6211\u60f3\u4e0d\u7528\u8bf7\u4f60\u5e2e\u5fd9\u3002\u201d\u5979\u89c1\u80e1\u6590\u8138\u4e0a\u5fae\u6709\u5931\u671b\u4e4b\u8272\uff0c\u53c8\u9053\uff1a\u201c\u82e5\u662f\u6211\u6599\u7406\u4e0d\u4e86\uff0c\u81ea\u5f53\u518d\u5411\u4f60\u548c\u7a0b\u5bb6\u59b9\u5b50\u6c42\u6551\u3002\u80e1\u5927\u54e5\uff0c\u518d\u8fc7\u56db\u5929\uff0c\u4fbf\u662f\u638c\u95e8\u4eba\u5927\u4f1a\u4e4b\u671f\uff0c\u54b1\u4e09\u4e2a\u5230\u4f1a\u4e2d\u53bb\u6270\u4ed6\u4e00\u4e2a\u843d\u82b1\u6d41\u6c34\uff0c\u6f14\u4e00\u51fa\u2018\u4e09\u82f1\u5927\u95f9\u5317\u4eac\u57ce\u2019\uff0c\u4f60\u8bf4\u597d\u662f\u4e0d\u597d\uff1f\u201d \u3000\u3000\u80e1\u6590\u8c6a\u6c14\u52c3\u53d1\uff0c\u53eb\u9053\uff1a\u201c\u5999\u6781\uff0c\u5999\u6781\uff01\u82e5\u4e0d\u6311\u4e86\u8fd9\u638c\u95e8\u4eba\u5927\u4f1a\uff0c\u8d75\u4e09\u54e5\u3001\u6587\u56db\u7237\u3001\u6587\u56db\u5976\u5976\u4ed6\u4eec\u7ed3\u4ea4\u6211\u8fd9\u5c0f\u5b50\u53c8\u6709\u4ec0\u4e48\u7528\uff1f\u201d \u3000\u3000\u7a0b\u7075\u7d20\u4e00\u76f4\u5728\u65c1\u542c\u7740\uff0c\u9ed8\u4e0d\u4f5c\u58f0\uff0c\u8fd9\u65f6\u7ec8\u4e8e\u63d2\u53e3\u9053\uff1a\u201c\u2018\u53cc\u82f1\u95f9\u5317\u4eac\u2019\uff0c\u4e5f\u5df2\u591f\u4e86\uff0c\u600e\u5730\u62c9\u626f\u4e0a\u6211\u8fd9\u4e2a\u4e0d\u4e2d\u7528\u7684\u5bb6\u4f19\uff1f\u201d \u3000\u3000\u8881\u7d2b\u8863\u6402\u7740\u5979\u5a07\u602f\u602f\u7684\u80a9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u7a0b\u5bb6\u59b9\u5b50\uff0c\u5feb\u522b\u8fd9\u4e48\u8bf4\u3002\u4f60\u7684\u672c\u4e8b\u80dc\u6211\u5341\u500d\u3002\u6211\u53ea\u6562\u8ba8\u597d\u4f60\uff0c\u4e0d\u6562\u5f97\u7f6a\u4f60\u3002\u201d \u3000\u3000\u7a0b\u7075\u7d20\u4ece\u6000\u4e2d\u53d6\u51fa\u90a3\u53ea\u7389\u51e4\uff0c\u8bf4\u9053\uff1a\u201c\u8881\u59ca\u59ca\uff0c\u4f60\u548c\u6211\u5927\u54e5\u4e4b\u95f4\u7684\u8bef\u4f1a\u4e5f\u8bf4\u660e\u767d\u5566\uff0c\u8fd9\u53ea\u7389\u51e4\u8fd8\u662f\u4f60\u62ff\u7740\u3002\u8981\u4e0d\u7136\uff0c\u4e24\u53ea\u51e4\u51f0\u90fd\u7ed9\u4e86\u6211\u5927\u54e5\u3002\u201d \u3000\u3000\u8881\u7d2b\u8863\u4e00\u6014\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u8981\u4e0d\u7136\uff0c\u4e24\u53ea\u51e4\u51f0\u90fd\u7ed9\u4e86\u6211\u5927\u54e5\uff01\u201d \u3000\u3000\u7a0b\u7075\u7d20\u8bf4\u8fd9\u4e24\u53e5\u8bdd\u65f6\u539f\u65e0\u522b\u610f\uff0c\u4f46\u89c9\u8881\u7d2b\u8863\u54c1\u8c8c\u6b66\u529f\uff0c\u90fd\u662f\u5934\u6311\u4eba\u624d\uff0c\u4e00\u8def\u4e0a\u542c\u80e1\u6590\u8a00\u4e0b\u4e4b\u610f\uff0c\u65e9\u5df2\u60c5\u4e0d\u81ea\u7981\u5730\u5bf9\u5979\u5341\u5206\u503e\u5fc3\uff0c\u53ea\u662f\u4e3a\u4e86\u5979\u6570\u5ea6\u76f8\u6551\u51e4\u5929\u5357\uff0c\u8fd9\u624d\u5fc3\u5b58\u82a5\u8482\uff0c\u4eca\u65e5\u4e0d\u4f46\u524d\u5acc\u5c3d\u91ca\uff0c\u800c\u4e14\u53cc\u65b9\u8bf4\u6765\u66f4\u662f\u5927\u6709\u6e0a\u6e90\uff0c\u90a3\u8fd8\u6709\u4ec0\u4e48\u963b\u788d\uff1f\u4f46\u542c\u8881\u7d2b\u8863\u5c06\u81ea\u5df1\u8fd9\u53e5\u8bdd\u91cd\u8bf4\u4e86\u4e00\u904d\uff0c\u5012\u4f3c\u662f\u81ea\u5df1\u8bed\u5e26\u53cc\u5173\uff0c\u6709\u201c\u4e8c\u5973\u5171\u4e8b\u4e00\u592b\u201d\u4e4b\u610f\uff0c\u4e0d\u7531\u5f97\u7ea2\u6655\u53cc\u988a\uff0c\u5fd9\u9053\uff1a\u201c\u4e0d\uff0c\u4e0d\uff0c\u6211\u4e0d\u662f\u8fd9\u4e2a\u610f\u601d\u3002\u201d\u8881\u7d2b\u8863\u9053\uff1a\u201c\u4e0d\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d\u7a0b\u7075\u7d20\u5982\u4f55\u80fd\u591f\u89e3\u91ca\uff0c\u7a98\u5f97\u51e0\u4e4e\u8981\u6389\u4e0b\u6cea\u6765\u3002 \u3000\u3000\u8881\u7d2b\u8863\u9053\uff1a\u201c\u7a0b\u5bb6\u59b9\u5b50\uff0c\u4f60\u5728\u90a3\u5355\u5200\u4e4b\u4e0a\uff0c\u4e3a\u4f55\u4e0d\u4e0b\u81f4\u547d\u7684\u6bd2\u836f\uff1f\u201d\u7a0b\u7075\u7d20\u76ee\u4e2d\u542b\u6cea\uff0c\u6124\u7136\u9053\uff1a\u201c\u6211\u867d\u662f\u6bd2\u624b\u836f\u738b\u7684\u5f1f\u5b50\uff0c\u4f46\u751f\u5e73\u4ece\u672a\u6740\u8fc7\u4e00\u4e2a\u4eba\u3002\u96be\u9053\u6211\u5c31\u80fd\u968f\u968f\u4fbf\u4fbf\u7684\u5bb3\u4f60\u4e48\uff1f\u4f55\u51b5\u2026\u2026\u4f55\u51b5\u4f60\u662f\u4ed6\u7684\u5fc3\u4e0a\u4eba\uff0c\u4ed6\u6574\u5929\u9664\u4e86\u5403\u996d\u7761\u89c9\uff0c\u5ff5\u5ff5\u4e0d\u5fd8\uff0c\u4fbf\u662f\u5728\u60f3\u7740\u4f60\u3002\u6211\u600e\u4f1a\u5f53\u771f\u5bb3\u4f60\uff1f\u201d\u8bf4\u5230\u8fd9\u91cc\uff0c\u6cea\u73e0\u513f\u7ec8\u4e8e\u593a\u7736\u800c\u51fa\u3002 \u3000\u3000\u8881\u7d2b\u8863\u4e00\u6115\uff0c\u7ad9\u8d77\u8eab\u6765\uff0c\u98de\u5feb\u7684\u5411\u80e1\u6590\u63a0\u4e86\u4e00\u773c\uff0c\u53ea\u89c1\u4ed6\u8138\u4e0a\u663e\u5f97\u751a\u662f\u5ff8\u6029\u5c34\u5c2c\u3002\u7a0b\u7075\u7d20\u8fd9\u4e00\u756a\u8bdd\uff0c\u7a81\u7136\u5410\u9732\u4e86\u4ed6\u7684\u5fc3\u4e8b\uff0c\u5b9e\u662f\u5927\u51fa\u4ed6\u610f\u6599\u4e4b\u5916\uff0c\u4e0d\u514d\u751a\u662f\u72fc\u72c8\uff0c\u4f46\u76ee\u5149\u4e4b\u4e2d\uff0c\u5374\u662f\u6ee1\u542b\u6b3e\u6b3e\u67d4\u60c5\u3002 \u3000\u3000\u8881\u7d2b\u8863\u4e0a\u6392\u7259\u9f7f\u4e00\u54ac\u4e0b\u5507\uff0c\u5411\u7a0b\u7075\u7d20\u67d4\u58f0\u9053\uff1a\u201c\u4f60\u653e\u5fc3\uff01 \u3000\u3000\u7ec8\u4e0d\u80fd\u4e24\u53ea\u51e4\u51f0\u90fd\u7ed9\u4e86\u4ed6\uff01\u201d\u84e6\u5730\u91cc\u7ea4\u624b\u4e00\u626c\uff0c\u5657\u7684\u4e00\u58f0\uff0c\u6247\u706d\u4e86\u70db\u706b\uff0c\u7a7f\u7a97\u800c\u51fa\uff0c\u767b\u9ad8\u8d8a\u623f\u800c\u53bb\u3002 \u3000\u3000\u80e1\u6590\u548c\u7a0b\u7075\u7d20\u90fd\u662f\u4e00\u60ca\uff0c\u5954\u5230\u7a97\u8fb9\u53bb\u770b\u65f6\uff0c\u4f46\u89c1\u5bbf\u96e8\u521d\u6674\uff0c\u94f6\u5149\u6cfb\u5730\uff0c\u65e9\u5df2\u4e0d\u89c1\u8881\u7d2b\u8863\u7684\u4eba\u5f71\u3002 \u3000\u3000\u4e24\u4eba\u5fc3\u5934\uff0c\u90fd\u5728\u5480\u56bc\u5979\u4e34\u53bb\u65f6\u90a3\u4e00\u53e5\u8bdd\uff1a\u201c\u4f60\u653e\u5fc3\uff0c\u7ec8\u4e0d\u80fd\u4e24\u53ea\u51e4\u51f0\u90fd\u7ed9\u4e86\u4ed6\uff01\u201d \u3000"}),showOnHomepage:!0},{name:"\u7b2c\u5341\u4e94\u7ae0\u3000\u534e\u62f3\u56db\u5341\u516b",content:(0,a.jsx)(a.Fragment,{children:"\u4e24\u4eba\u5e76\u80a9\u7ad9\u5728\u9ed1\u6697\u4e4b\u4e2d\uff0c\u9ed8\u7136\u826f\u4e45\uff0c\u5ffd\u542c\u5f97\u5c4b\u74e6\u4e0a\u5580\u7684\u4e00\u58f0\u54cd\u3002\u80e1\u6590\u5927\u559c\uff0c\u53ea\u9053\u8881\u7d2b\u8863\u53bb\u800c\u590d\u56de\uff0c\u60c5\u4e0d\u81ea\u7981\u7684\u53eb\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u56de\u6765\u4e86\uff01\u201d\u5ffd\u542c\u5f97\u5c4b\u4e0a\u4e00\u4e2a\u7537\u5b50\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u80e1\u5927\u7237\uff0c\u8bf7\u4f60\u501f\u4e00\u6b65\u8bf4\u8bdd\u3002\u201d\u542c\u58f0\u97f3\u5374\u662f\u90a3\u4e2a\u7231\u5251\u5982\u547d\u7684\u8042\u59d3\u6b66\u5b98\u3002 \u3000\u3000\u80e1\u6590\u9053\uff1a\u201c\u6b64\u95f4\u9664\u6211\u4e49\u59b9\u5916\u5e76\u65e0\u65c1\u4eba\uff0c\u8042\u5144\u8bf7\u8fdb\u6765\u559d\u4e00\u676f\u9152\u3002\u201d \u3000\u3000\u8fd9\u59d3\u8042\u7684\u6b66\u5b98\u5355\u540d\u4e00\u4e2a\u94ba\u5b57\uff0c\u90a3\u65e5\u80e1\u6590\u4e0d\u6bc1\u4ed6\u7684\u5b9d\u5251\uff0c\u4e00\u76f4\u5fc3\u4e2d\u597d\u751f\u611f\u6fc0\uff0c\u5f53\u8881\u7d2b\u8863\u548c\u79e6\u8010\u4e4b\u3001\u738b\u5251\u82f1\u3001\u5468\u94c1\u9e6a\u4e09\u4eba\u76f8\u6597\u4e4b\u65f6\uff0c\u4ed6\u89c1\u80e1\u6590\u6697\u4e2d\u9887\u6709\u504f\u8892\u8881\u7d2b\u8863\u4e4b\u610f\uff0c\u662f\u4ee5\u59cb\u7ec8\u9ed8\u4e0d\u4f5c\u58f0\uff0c\u8fd9\u65f6\u542c\u80e1\u6590\u8fd9\u822c\u8bf4\uff0c\u4fbf\u4ece\u5c4b\u9876\u8dc3\u4e0b\uff0c\u8bf4\u9053\uff1a\u201c\u80e1\u5927\u54e5\uff0c\u4f60\u7684\u4e00\u4f4d\u65e7\u53cb\u547d\u5c0f\u5f1f\u524d\u6765\uff0c\u8bf7\u80e1\u5927\u54e5\u5927\u9a7e\u8fc7\u53bb\u4e00\u8c08\u3002\u201d \u3000\u3000\u80e1\u6590\u5947\u9053\uff1a\u201c\u6211\u7684\u65e7\u53cb\uff1f\u90a3\u662f\u8c01\u554a\uff1f\u201d\u8042\u94ba\u9053\uff1a\u201c\u5c0f\u5f1f\u5949\u547d\u4e0d\u5f97\u6cc4\u9732\uff0c\u8fd8\u8bf7\u539f\u8c05\u3002\u80e1\u5927\u54e5\u89c1\u9762\u81ea\u77e5\u3002\u201d\u80e1\u6590\u5411\u7a0b\u7075\u7d20\u671b\u4e86\u4e00\u773c\uff0c\u9053\uff1a\u201c\u4e8c\u59b9\uff0c\u4f60\u5728\u6b64\u7a0d\u5f85\uff0c\u6211\u5929\u660e\u4e4b\u524d\u5fc5\u56de\u3002\u201d\u7a0b\u7075\u7d20\u8f6c\u8eab\u53d6\u8fc7\u4ed6\u7684\u5355\u5200\uff0c\u9053\uff1a\u201c\u5e26\u5175\u5203\u4e48\uff1f\u201d\u80e1\u6590\u89c1\u8042\u94ba\u8170\u95f4\u672a\u7cfb\u5b9d\u5251\uff0c\u9053\uff1a\u201c\u65e2\u662f\u65e7\u53cb\u89c1\u62db\uff0c\u4e0d\u7528\u5e26\u4e86\u3002\u201d \u3000\u3000\u5f53\u4e0b\u4e24\u4eba\u4ece\u5927\u95e8\u51fa\u53bb\uff0c\u95e8\u5916\u505c\u7740\u4e00\u8f86\u4e24\u5339\u9a6c\u62c9\u7684\u9a6c\u8f66\uff0c\u8f66\u8eab\u91d1\u6f06\u7eb1\u56f4\uff0c\u751a\u662f\u534e\u8d35\u3002\u80e1\u6590\u5bfb\u601d\uff1a\u201c\u96be\u9053\u53c8\u662f\u51e4\u5929\u5357\u8fd9\u53ae\u65bd\u4ec0\u4e48\u9b3c\u8ba1\uff1f\u8fd9\u6b21\u518d\u6559\u6211\u649e\u4e0a\uff0c\u7eb5\u662f\u7a7a\u624b\uff0c\u4e5f\u4e00\u638c\u5c06\u4ed6\u6bd9\u4e86\u3002\u201d \u3000\u3000\u4e24\u4eba\u8fdb\u8f66\u5750\u597d\uff0c\u8f66\u592b\u97ad\u5b50\u4e00\u626c\uff0c\u4e24\u5339\u9a8f\u9a6c\u53d1\u8db3\u4fbf\u884c\u3002\u9a6c\u8e44\u51fb\u5728\u5317\u4eac\u57ce\u5927\u8857\u7684\u9752\u77f3\u677f\u4e0a\uff0c\u54cd\u58f0\u5f97\u5f97\uff0c\u9759\u591c\u542c\u6765\uff0c\u5206\u5916\u6e05\u6670\u3002\u4eac\u57ce\u4e4b\u4e2d\uff0c\u5bb5\u95f4\u672c\u6765\u4e0d\u8bb8\u884c\u8f66\u9a70\u9a6c\uff0c\u4f46\u5de1\u591c\u5175\u4e01\u89c1\u5230\u9a6c\u8f66\u524d\u7684\u7ea2\u8272\u65e0\u5b57\u706f\u7b3c\uff0c\u4fa7\u8eab\u8ba9\u5728\u8857\u8fb9\uff0c\u4fbf\u8ba9\u8f66\u5b50\u8fc7\u53bb\u4e86\u3002 \u3000\u3000\u7ea6\u83ab\u884c\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u9a6c\u8f66\u5728\u4e00\u5835\u5927\u767d\u7c89\u5899\u524d\u505c\u4f4f\u3002\u8042\u94ba\u5148\u8df3\u4e0b\u8f66\uff0c\u5f15\u7740\u80e1\u6590\u8d70\u8fdb\u4e00\u9053\u5c0f\u95e8\uff0c\u6cbf\u7740\u4e00\u6392\u9e45\u5375\u77f3\u94fa\u7684\u82b1\u5f84\uff0c\u8d70\u8fdb\u4e00\u5ea7\u82b1\u56ed\u3002\u8fd9\u56ed\u5b50\u89c4\u6a21\u597d\u5927\uff0c\u82b1\u6728\u7e41\u8302\uff0c\u4ead\u9601\u3001\u56de\u5eca\u3001\u5047\u5c71\u3001\u6c60\u6cbc\uff0c\u4e00\u5904\u5904\u89c2\u4e4b\u4e0d\u5c3d\uff0c\u4ead\u9601\u4e4b\u95f4\u5f80\u5f80\u70b9\u7740\u7eb1\u706f\u3002 \u3000\u3000\u80e1\u6590\u6697\u6697\u79f0\u5947\uff1a\u201c\u51e4\u5929\u5357\u8fd9\u53ae\u4e5f\u771f\u795e\u901a\u5e7f\u5927\uff0c\u8fd9\u56ed\u5b50\u4e0d\u662f\u4e00\u4e8c\u767e\u4e07\u4e24\u94f6\u5b50\uff0c\u4f11\u60f3\u4e70\u5f97\u5230\u624b\u3002\u4ed6\u5728\u4f5b\u5c71\u79ef\u805a\u7684\u9020\u5b7d\u94b1\uff0c\u5f53\u771f\u4e0d\u5c11\u3002\u201d\u4f46\u8f6c\u5ff5\u53c8\u60f3\uff1a\u201c\u53ea\u6015\u672a\u5fc5\u4fbf\u662f\u59d3\u51e4\u7684\u5978\u8d3c\u3002\u4ed6\u518d\u5f3a\u4e5f\u4e0d\u8fc7\u662f\u5e7f\u4e1c\u4e00\u4e2a\u571f\u8c6a\u6076\u9738\uff0c\u600e\u80fd\u5dee\u9063\u5f97\u52a8\u8042\u94ba\u8fd9\u822c\u6709\u529f\u540d\u7684\u6b66\u5b98\uff1f\u201d \u3000\u3000\u5bfb\u601d\u4e4b\u9645\uff0c\u8042\u94ba\u5f15\u7740\u4ed6\u8f6c\u8fc7\u4e00\u5ea7\u5047\u5c71\u5806\u6210\u7684\u77f3\u969c\uff0c\u8fc7\u4e86\u4e00\u9053\u6728\u6865\uff0c\u8d70\u8fdb\u4e00\u5ea7\u6c34\u9601\uff0c\u9601\u4e2d\u70b9\u7740\u4e24\u679d\u7ea2\u70db\uff0c\u684c\u4e0a\u6446\u5217\u7740\u8336\u7897\u7ec6\u70b9\u3002\u8042\u94ba\u9053\uff1a\u201c\u8d35\u53cb\u8fd9\u4fbf\u5c31\u6765\uff0c\u5c0f\u5f1f\u5728\u95e8\u5916\u76f8\u5019\u3002\u201d\u8bf4\u65f6\u8f6c\u8eab\u51fa\u95e8\u3002 \u3000\u3000\u80e1\u6590\u770b\u8fd9\u9601\u4e2d\u9648\u8bbe\u65f6\uff0c\u4f46\u89c1\u7cbe\u81f4\u96c5\u6d01\uff0c\u6ee1\u773c\u5bcc\u8d35\u4e4b\u6c14\uff0c\u5ba3\u6b66\u95e8\u5916\u7684\u90a3\u6240\u5b85\u7b2c\u672c\u4e5f\u7b97\u5f97\u4e0a\u534e\u4e3d\uff0c\u4f46\u79ef\u8fd9\u5c0f\u9601\u76f8\u6bd4\uff0c\u5374\u53c8\u662f\u76f8\u5dee\u4e0d\u53ef\u4ee5\u9053\u91cc\u8ba1\u4e86\u3002\u897f\u9996\u5899\u4e0a\u60ac\u4e86\u4e00\u4e2a\u6761\u5e45\uff0c\u6b63\u6977\u4e66\u7740\u4e00\u7bc7\u5e84\u5b50\u7684\u300a\u8bf4\u5251\u300b\uff0c\u4e0b\u9762\u7f72\u540d\u7684\u7adf\u662f\u5f53\u4eca\u4e7e\u9686\u7687\u5e1d\u4e4b\u5b50\u6210\u4eb2\u738b\u3002\u8fd9\u7bc7\u6587\u5b57\u662f\u540e\u4eba\u4f2a\u4f5c\uff0c\u5e76\u975e\u5e84\u5b50\u6240\u64b0\uff0c\u80e1\u6590\u81ea\u4e5f\u4e0d\u77e5\uff0c\u5750\u4e86\u4e00\u4f1a\u89c9\u5f97\u65e0\u804a\uff0c\u4fbf\u4ece\u5934\u9ed8\u9ed8\u8bf5\u8bfb\uff0c\u597d\u5728\u6587\u53e5\u6d45\u663e\uff0c\u5012\u80fd\u660e\u767d\uff1a\u201c\u6614\u8d75\u6587\u738b\u559c\u5251\uff0c\u5251\u58eb\u5939\u95e8\u800c\u5ba2\u4e09\u5343\u4f59\u4eba\uff0c\u65e5\u591c\u76f8\u51fb\u4e8e\u524d\uff0c\u6b7b\u4f24\u8005\u5c81\u767e\u4f59\u4eba\uff0c\u597d\u4e4b\u4e0d\u538c\u2026\u2026\u201d\u5fc3\u60f3\uff1a\u201c\u798f\u5927\u5e05\u53ec\u96c6\u5929\u4e0b\u638c\u95e8\u4eba\u5927\u4f1a\uff0c\u4e0d\u77e5\u662f\u5426\u5728\u5b66\u8fd9\u8d75\u6587\u738b\u7684\u699c\u6837\uff1f\u201d\u5f85\u8bfb\u5230\uff1a\u201c\u2026\u2026 \u3000\u3000\u81e3\u4e4b\u5251\uff0c\u5341\u6b65\u6740\u4e00\u4eba\uff0c\u5343\u91cc\u4e0d\u7559\u884c\u3002\u738b\u5927\u8bf4\u4e4b\u66f0\uff1a\u5929\u4e0b\u65e0\u654c\u77e3\u3002\u5e84\u5b50\u66f0\uff1a\u592b\u4e3a\u5251\u8005\u793a\u4e4b\u4ee5\u865a\uff0c\u5f00\u4e4b\u4ee5\u5229\uff0c\u540e\u4e4b\u4ee5\u53d1\uff0c\u5148\u4e4b\u4ee5\u81f3\u2026\u2026\u201d\u4ed6\u5fc3\u9053\uff1a\u201c\u5e84\u5b50\u81ea\u79f0\u80fd\u5341\u6b65\u6740\u4e00\u4eba\uff0c\u5343\u91cc\u4e0d\u7559\u884c\uff0c\u90a3\u81ea\u662f\u5929\u4e0b\u65e0\u654c\u4e86\uff0c\u770b\u6765\u8fd9\u5e84\u5b50\u662f\u5728\u5439\u725b\u3002\u81f3\u4e8e\u2018\u793a\u865a\u5f00\u5229\uff0c\u540e\u53d1\u5148\u81f3\u2019\u90a3\u51e0\u53e5\u8bdd\uff0c\u786e\u662f\u6b66\u5b66\u4e2d\u7684\u7cbe\u4e49\uff0c\u4e0d\u4f46\u5251\u672f\u662f\u8fd9\u6837\uff0c\u5200\u6cd5\u62f3\u6cd5\u53c8\u4f55\u5c1d\u4e0d\u662f\uff1f\u201d \u3000\u3000\u5ffd\u542c\u5f97\u80cc\u540e\u811a\u6b65\u4e4b\u58f0\u7ec6\u788e\uff0c\u9690\u9690\u9999\u98ce\u6251\u9f3b\uff0c\u4ed6\u56de\u8fc7\u8eab\u6765\uff0c\u89c1\u662f\u4e00\u4e2a\u7f8e\u8c8c\u5c11\u5987\uff0c\u8eab\u7a7f\u6de1\u7eff\u7eb1\u886b\uff0c\u542b\u7b11\u800c\u7acb\uff0c\u6b63\u662f\u9a6c\u6625\u82b1\u3002 \u3000\u3000\u80e1\u6590\u604d\u7136\u5927\u609f\uff1a\u201c\u539f\u6765\u8fd9\u91cc\u662f\u798f\u5eb7\u5b89\u7684\u5e9c\u7b2c\uff0c\u6211\u600e\u4f1a\u60f3\u4e0d\u5230\uff1f\u201d\u53ea\u89c1\u9a6c\u6625\u82b1\u4e0a\u524d\u9053\u4e2a\u4e07\u798f\uff0c\u7b11\u9053\uff1a\u201c\u80e1\u5144\u5f1f\uff0c\u60f3\u4e0d\u5230\u54b1\u4eec\u53c8\u5728\u4eac\u4e2d\u76f8\u89c1\uff0c\u8bf7\u5750\u8bf7\u5750\u3002\u201d\u8bf4\u7740\u4eb2\u624b\u6367\u8336\uff0c\u4ece\u679c\u76d2\u4e2d\u62ff\u4e86\u51e0\u4ef6\u7ec6\u70b9\uff0c\u653e\u5728\u4ed6\u7684\u8eab\u524d\uff0c\u53c8\u9053\uff1a\u201c\u6211\u542c\u8bf4\u80e1\u5144\u5f1f\u5230\u4e86\u5317\u4eac\uff0c\u597d\u751f\u60f3\u5ff5\uff0c\u6025\u7740\u8981\u89c1\u89c1\u4f60\uff0c\u8981\u591a\u8c22\u4f60\u90a3\u4e00\u756a\u76f8\u62a4\u7684\u6069\u5fb7\u3002\u201d \u3000\u3000\u80e1\u6590\u89c1\u5979\u53d1\u8fb9\u63d2\u7740\u4e00\u6735\u5c0f\u5c0f\u767d\u7ed2\u82b1\uff0c\u7b97\u662f\u7ed9\u5f90\u94ee\u6234\u5b5d\uff0c\u4f46\u8863\u9970\u534e\u8d35\uff0c\u795e\u8272\u95f4\u559c\u6ea2\u7709\u68a2\uff0c\u54ea\u91cc\u662f\u65b0\u4e27\u4e08\u592b\u7684\u5be1\u5987\u6a21\u6837\uff1f\u4e8e\u662f\u6de1\u6de1\u5730\u9053\uff1a\u201c\u5176\u5b9e\u90fd\u662f\u5c0f\u5f1f\u591a\u4e8b\uff0c\u65e9\u77e5\u662f\u798f\u5927\u5e05\u6d3e\u4eba\u6765\u76f8\u8fce\u5f90\u5927\u5ac2\uff0c\u4e5f\u7528\u4e0d\u7740\u5728\u77f3\u5c4b\u4e2d\u8fd9\u4e48\u4e00\u756a\u62c5\u60ca\u4e86\u3002\u201d \u3000\u3000\u9a6c\u6625\u82b1\u542c\u4ed6\u53e3\u79f0\u201c\u5f90\u5927\u5ac2\u201d\uff0c\u8138\u4e0a\u5fae\u5fae\u4e00\u7ea2\uff0c\u9053\uff1a\u201c\u4e0d\u7ba1\u600e\u4e48\uff0c\u80e1\u5144\u5f1f\u4e49\u6c14\u6df1\u91cd\uff0c\u6211\u603b\u662f\u5341\u5206\u611f\u6fc0\u7684\u3002\u5976\u5988\uff0c\u5976\u5988\uff0c\u5e26\u516c\u5b50\u7237\u51fa\u6765\u3002\u201d \u3000\u3000\u4e1c\u9996\u95e8\u4e2d\u5e94\u58f0\u8fdb\u6765\u4e24\u4e2a\u4ec6\u5987\uff0c\u643a\u7740\u4e24\u4e2a\u5b69\u513f\u3002\u4e24\u5b69\u5411\u9a6c\u6625\u82b1\u53eb\u4e86\u58f0\u201c\u5988\uff01\u201d\u9760\u5728\u5979\u7684\u8eab\u65c1\u3002\u4e24\u4e2a\u5b69\u513f\u9762\u8c8c\u4e00\u6a21\u4e00\u6837\uff0c\u672c\u5c31\u7389\u96ea\u53ef\u7231\uff0c\u8fd9\u4e00\u8863\u9526\u7740\u7f0e\uff0c\u6302\u73e0\u6234\u7389\uff0c\u66f4\u52a0\u663e\u5f97\u5a07\u8d35\u4e86\u3002 \u3000\u3000\u9a6c\u6625\u82b1\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u8fd8\u8ba4\u5f97\u80e1\u53d4\u53d4\u4e48\uff1f\u80e1\u53d4\u53d4\u5728\u9053\u4e0a\u4e00\u76f4\u5e2e\u7740\u54b1\u4eec\uff0c\u5feb\u5411\u80e1\u53d4\u53d4\u78d5\u5934\u554a\u3002\u201d\u4e8c\u5b69\u4e0a\u524d\u62dc\u5012\uff0c\u53eb\u4e86\u58f0\uff1a\u201c\u80e1\u53d4\u53d4\uff01\u201d \u3000\u3000\u80e1\u6590\u4f38\u624b\u6276\u8d77\uff0c\u5fc3\u60f3\uff1a\u201c\u4eca\u65e5\u4f60\u4eec\u8fd8\u53eb\u6211\u4e00\u58f0\u53d4\u53d4\uff0c\u8fc7\u4e0d\u591a\u65f6\uff0c\u4f60\u4eec\u4fbf\u662f\u5a01\u98ce\u8d6b\u8d6b\u7684\u7687\u4eb2\u56fd\u621a\uff0c\u90a3\u91cc\u8fd8\u8ba4\u5f97\u6211\u8fd9\u8349\u83bd\u4e4b\u58eb\uff1f\u201d \u3000\u3000\u9a6c\u6625\u82b1\u9053\uff1a\u201c\u80e1\u5144\u5f1f\uff0c\u6211\u6709\u4e00\u4e8b\u76f8\u6c42\uff0c\u4e0d\u77e5\u4f60\u80fd\u7b54\u5141\u4e48\uff1f\u201d \u3000\u3000\u80e1\u6590\u9053\uff1a\u201c\u5927\u5ac2\uff0c\u5f53\u65e5\u5728\u5546\u5bb6\u5821\u4e2d\uff0c\u5c0f\u5f1f\u88ab\u5546\u5b9d\u9707\u540a\u6253\uff0c\u8499\u4f60\u51fa\u529b\u76f8\u6551\uff0c\u6b64\u6069\u5c0f\u5f1f\u6df1\u8bb0\u5fc3\u4e2d\uff0c\u7ec8\u4e0d\u6562\u5fd8\u3002\u65e5\u524d\u5728\u77f3\u5c4b\u4e2d\u5c0f\u5f1f\u66ff\u4f60\u6297\u62d2\u7fa4\u76d7\uff0c\u867d\u5219\u662f\u591a\u7ba1\u95f2\u4e8b\uff0c\u778e\u8d77\u5fd9\u5934\uff0c\u4e0d\u514d\u6559\u4eba\u597d\u7b11\uff0c\u4f46\u5728\u5c0f\u5f1f\u5fc3\u4e2d\uff0c\u603b\u7b97\u662f\u62a5\u7b54\u4e86\u4f60\u6614\u65e5\u7684\u4e00\u756a\u6069\u5fb7\u3002\u4eca\u65e5\u82e5\u77e5\u662f\u4f60\u89c1\u62db\uff0c\u5c0f\u5f1f\u539f\u4e5f\u4e0d\u4f1a\u5230\u6765\u3002\u4ece\u4eca\u800c\u540e\uff0c\u54b1\u4eec\u8d35\u8d31\u6709\u522b\uff0c\u518d\u4e5f\u6ca1\u4ec0\u4e48\u76f8\u5e72\u4e86\u3002\u201d\u8fd9\u4e00\u756a\u8bdd\u4f83\u4f83\u800c\u8a00\uff0c\u663e\u662f\u5bf9\u5979\u9887\u4e3a\u4e0d\u6ee1\u3002 \u3000\u3000\u9a6c\u6625\u82b1\u53f9\u9053\uff1a\u201c\u80e1\u5144\u5f1f\uff0c\u6211\u867d\u7136\u4e0d\u597d\uff0c\u5374\u4e5f\u4e0d\u662f\u8d8b\u708e\u9644\u52bf\u4e4b\u4eba\u3002\u6240\u8c13\u2018\u4e00\u89c1\u953a\u60c5\u2019\uff0c\u603b\u662f\u524d\u751f\u7684\u5b7d\u7f18\u2026\u2026\u201d\u5979\u8d8a\u8bf4\u58f0\u97f3\u8d8a\u4f4e\uff0c\u6162\u6162\u4f4e\u4e0b\u4e86\u5934\u53bb\u3002 \u3000\u3000\u80e1\u6590\u542c\u5979\u8bf4\u5230\u201c\u4e00\u89c1\u953a\u60c5\u201d\u56db\u5b57\uff0c\u89e6\u52a8\u4e86\u81ea\u5df1\u7684\u5fc3\u4e8b\uff0c\u767b\u65f6\u5bf9\u5979\u4e0d\u6ee1\u4e4b\u60c5\u5927\u51cf\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8981\u6211\u505a\u4ec0\u4e48\u4e8b\uff1f\u5176\u5b9e\uff0c\u798f\u5927\u5e05\u8fd8\u6709\u4ec0\u4e48\u4e8b\u4e0d\u80fd\u529e\u5230\uff0c\u4f60\u5374\u6765\u6c42\u6211\uff1f\u201d\u9a6c\u6625\u82b1\u9053\uff1a\u201c\u6211\u662f\u4e3a\u8fd9\u4e24\u4e2a\u5b69\u513f\u6c42\u4f60\uff0c\u8bf7\u4f60\u6536\u4e86\u4ed6\u4eec\u4e3a\u5f92\uff0c\u4f20\u4ed6\u4eec\u4e00\u70b9\u6b66\u827a\u3002\u201d\u80e1\u6590\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e24\u4f4d\u516c\u5b50\u7237\u5c0a\u8363\u5bcc\u8d35\uff0c\u53c8\u4f55\u5fc5\u5b66\u4ec0\u4e48\u6b66\u827a\uff1f\u201d \u3000\u3000\u9a6c\u6625\u82b1\u9053\uff1a\u201c\u5f3a\u8eab\u5065\u4f53\uff0c\u90a3\u4e5f\u662f\u597d\u7684\u3002\u201d \u3000\u3000\u6b63\u8bf4\u5230\u6b64\u5904\uff0c\u5ffd\u542c\u5f97\u9601\u5916\u4e00\u4e2a\u7537\u4eba\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u6625\u59b9\uff0c\u8fd9\u5f53\u513f\u8fd8\u6ca1\u7761\u4e48\uff1f\u201d\u9a6c\u6625\u82b1\u8138\u8272\u5fae\u53d8\uff0c\u5411\u95e8\u8fb9\u7684\u4e00\u5ea7\u5c4f\u98ce\u6307\u4e86\u6307\uff0c\u80e1\u6590\u5f53\u5373\u9690\u8eab\u5728\u5c4f\u98ce\u4e4b\u540e\u3002\u53ea\u542c\u5f97\u9774\u58f0\u6a50\u6a50\uff0c\u4e00\u4eba\u8d70\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u9a6c\u6625\u82b1\u9053\uff1a\u201c\u600e\u4e48\u4f60\u81ea\u5df1\u8fd8\u4e0d\u7761\uff1f\u4e0d\u53bb\u966a\u4f34\u592b\u4eba\uff0c\u5374\u5230\u8fd9\u91cc\u4f5c\u4ec0\u4e48\uff1f\u201d\u90a3\u4eba\u4f38\u624b\u63e1\u4f4f\u4e86\u5979\u624b\uff0c\u7b11\u9053\uff1a\u201c\u7687\u4e0a\u53ec\u89c1\u5546\u8bae\u519b\u52a1\uff0c\u5230\u8fd9\u65f6\u65b9\u9000\u3002\u4f60\u602a\u6211\u4eca\u665a\u6765\u5f97\u592a\u8fdf\u4e86\u4e48\uff1f\u201d \u3000\u3000\u80e1\u6590\u4e00\u542c\uff0c\u4fbf\u77e5\u8fd9\u662f\u798f\u5eb7\u5b89\u4e86\uff0c\u5fc3\u60f3\u81ea\u5df1\u8eb2\u5728\u8fd9\u91cc\uff0c\u597d\u4e0d\u5c34\u5c2c\uff0c\u4ed6\u4e8c\u4eba\u7684\u60c5\u8bdd\u52bf\u5fc5\u4f20\u8fdb\u8033\u4e2d\uff0c\u6b32\u4e0d\u542c\u800c\u4e0d\u53ef\u5f97\uff0c\u4f55\u51b5\u773c\u524d\u60c5\u52bf\u4f3c\u662f\u6765\u548c\u9a6c\u6625\u82b1\u79c1\u76f8\u5e7d\u4f1a\uff0c\u82e5\u662f\u7ed9\u4ed6\u53d1\u89c9\uff0c\u4e8e\u9a6c\u6625\u82b1\u548c\u81ea\u5df1\u90fd\u5927\u5927\u4e0d\u59a5\uff0c\u5bdf\u770b\u5468\u56f4\u60c5\u52bf\uff0c\u6b32\u8c0b\u8131\u8eab\u4e4b\u8ba1\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u9a6c\u6625\u82b1\u9053\uff1a\u201c\u5eb7\u54e5\uff0c\u6211\u7ed9\u4f60\u5f15\u89c1\u4e00\u4e2a\u4eba\u3002\u8fd9\u4eba\u4f60\u4e5f\u66fe\u89c1\u8fc7\uff0c\u53ea\u662f\u60f3\u5fc5\u65e9\u5df2\u5fd8\u4e86\u3002\u201d\u8ddf\u7740\u63d0\u9ad8\u58f0\u97f3\u53eb\u9053\uff1a\u201c\u80e1\u5144\u5f1f\uff0c\u4f60\u6765\u89c1\u8fc7\u798f\u5927\u5e05\u3002\u201d \u3000\u3000\u80e1\u6590\u53ea\u5f97\u8f6c\u4e86\u51fa\u6765\uff0c\u5411\u798f\u5eb7\u5b89\u4e00\u63d6\u3002\u798f\u5eb7\u5b89\u4e07\u6599\u4e0d\u5230\u5c4f\u98ce\u4e4b\u540e\u7adf\u85cf\u5f97\u6709\u4e2a\u7537\u4eba\uff0c\u5927\u5403\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u8fd9\u2026\u2026\u8fd9\u2026\u2026\u201d \u3000\u3000\u9a6c\u6625\u82b1\u7b11\u9053\uff1a\u201c\u8fd9\u4f4d\u5144\u5f1f\u59d3\u80e1\uff0c\u5355\u540d\u4e00\u4e2a\u6590\u5b57\uff0c\u4ed6\u5e74\u7eaa\u867d\u8f7b\uff0c\u5374\u662f\u6b66\u529f\u5353\u7edd\uff0c\u4f60\u624b\u4e0b\u90a3\u4e9b\u6b66\u58eb\uff0c\u6ca1\u4e00\u4e2a\u53ca\u5f97\u4e0a\u4ed6\u3002\u8fd9\u6b21\u4f60\u6d3e\u4eba\u63a5\u6211\u6765\u4eac\u65f6\uff0c\u8fd9\u4f4d\u80e1\u5144\u5f1f\u5e2e\u4e86\u6211\u4e0d\u5c11\u5fd9\uff0c\u56e0\u6b64\u6211\u8bf7\u4e86\u4ed6\u6765\u3002\u4f60\u600e\u751f\u91cd\u91cd\u916c\u8c22\u4ed6\u554a\uff1f\u201d \u3000\u3000\u798f\u5eb7\u5b89\u8138\u4e0a\u53d8\u8272\uff0c\u542c\u5979\u8bf4\u5b8c\uff0c\u8fd9\u624d\u5b81\u5b9a\uff0c\u9053\uff1a\u201c\u55ef\uff0c\u90a3\u662f\u8be5\u8c22\u7684\uff0c\u90a3\u662f\u8be5\u8c22\u7684\u3002\u201d\u5de6\u624b\u5411\u80e1\u6590\u4e00\u6325\u9053\uff1a\u201c\u4f60\u5148\u51fa\u53bb\u5427\uff0c\u8fc7\u51e0\u65e5\u6211\u81ea\u4f1a\u4f20\u89c1\u3002\u201d\u8bed\u6c14\u4e4b\u95f4\uff0c\u5fae\u73b0\u4e0d\u60a6\uff0c\u82e5\u4e0d\u662f\u788d\u7740\u9a6c\u6625\u82b1\u7684\u9762\u5b50\uff0c\u65e9\u5df2\u76f4\u65a5\u4ed6\u64c5\u95ef\u5e9c\u7b2c\u3001\u89c1\u9762\u4e0d\u8dea\u7684\u65e0\u793c\u4e86\u3002\u9a6c\u6625\u82b1\u9053\uff1a\u201c\u80e1\u5144\u5f1f\u2026\u2026\u201d \u3000\u3000\u80e1\u6590\u618b\u4e86\u4e00\u809a\u5b50\u6c14\uff0c\u8f6c\u8eab\u4fbf\u51fa\uff0c\u5fc3\u60f3\uff1a\u201c\u597d\u6ca1\u6765\u7531\uff0c\u534a\u591c\u4e09\u66f4\u7684\u6765\u53d7\u8fd9\u756a\u7f9e\u8fb1\u3002\u201d\u8042\u94ba\u5728\u9601\u95e8\u5916\u76f8\u5019\uff0c\u4f38\u4e86\u4f38\u820c\u5934\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u798f\u5927\u5e05\u521a\u624d\u8fdb\u53bb\uff0c\u89c1\u7740\u4e86\u4e48\uff1f\u201d\u80e1\u6590\u9053\uff1a\u201c\u9a6c\u59d1\u5a18\u7ed9\u6211\u5f15\u89c1\u4e86\uff0c\u8bf4\u8981\u798f\u5927\u5e05\u916c\u8c22\u6211\u4ec0\u4e48\u3002\u201d\u8042\u94ba\u559c\u9053\uff1a\u201c\u53ea\u987b\u5f97\u9a6c\u59d1\u5a18\u4e00\u8a00\uff0c\u798f\u5927\u5e05\u5c82\u6709\u4e0d\u53e6\u773c\u76f8\u770b\u7684\uff1f\u65e5\u540e\u5c0f\u5f1f\u8ffd\u968f\u80e1\u5927\u54e5\u4e4b\u540e\uff0c\u90a3\u771f\u662f\u518d\u597d\u4e0d\u8fc7\u3002\u201d\u4ed6\u4f69\u670d\u80e1\u6590\u6b66\u529f\u548c\u4e3a\u4eba\uff0c\u8fd9\u51e0\u53e5\u8bdd\u5012\u662f\u8877\u5fc3\u4e4b\u8a00\u3002 \u3000\u3000\u5f53\u4e0b\u4e24\u4eba\u4ece\u539f\u8def\u51fa\u53bb\uff0c\u6765\u5230\u4e00\u5ea7\u8377\u82b1\u6c60\u4e4b\u65c1\uff0c\u79bb\u5927\u95e8\u5df2\u8fd1\uff0c\u5ffd\u542c\u5f97\u811a\u6b65\u58f0\u54cd\uff0c\u6709\u51e0\u4eba\u5feb\u6b65\u8ffd\u4e86\u4e0a\u6765\uff0c\u53eb\u9053\uff1a\u201c\u80e1\u5927\u7237\u8bf7\u7559\u6b65\u3002\u201d \u3000\u3000\u80e1\u6590\u6115\u7136\u505c\u6b65\uff0c\u89c1\u662f\u56db\u540d\u6b66\u5b98\uff0c\u5f53\u5148\u4e00\u4eba\u624b\u4e2d\u6367\u7740\u4e00\u53ea\u9526\u76d2\u3002\u90a3\u4eba\u9053\uff1a\u201c\u9a6c\u59d1\u5a18\u6709\u51e0\u4ef6\u793c\u7269\u8d60\u7ed9\u80e1\u5927\u7237\uff0c\u8bf7\u4f60\u8d50\u6536\u3002\u201d \u3000\u3000\u80e1\u6590\u6b63\u6ca1\u597d\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u4eba\u65e0\u529f\u4e0d\u53d7\u7984\uff0c\u4e0d\u6562\u62dc\u9886\u3002\u201d\u90a3\u4eba\u9053\uff1a\u201c\u9a6c\u59d1\u5a18\u4e00\u756a\u76db\u610f\uff0c\u80e1\u5927\u7237\u4e0d\u5fc5\u5ba2\u6c14\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u8bf7\u4f60\u8f6c\u544a\u9a6c\u59d1\u5a18\uff0c\u4fbf\u8bf4\u5979\u7684\u9686\u60c5\u539a\u610f\uff0c\u59d3\u80e1\u7684\u5fc3\u9886\u4e86\u3002\u201d\u8bf4\u7740\u8f6c\u8eab\u4fbf\u8d70\u3002 \u3000\u3000\u90a3\u6b66\u5b98\u8d76\u4e0a\u524d\u6765\uff0c\u795e\u8272\u751a\u662f\u7126\u6025\uff0c\u9053\uff1a\u201c\u80e1\u5927\u7237\uff0c\u4f60\u82e5\u5fc5\u4e0d\u80af\u53d7\uff0c\u9a6c\u59d1\u5a18\u5b9a\u8981\u602a\u7f6a\u5c0f\u4eba\u3002\u8042\u5927\u54e5\uff0c\u4f60\u2026\u2026\u4f60\u4fbf\u529d\u529d\u80e1\u5927\u7237\u3002\u6211\u5b9e\u5728\u662f\u5949\u547d\u5dee\u9063\u2026\u2026\u201d\u80e1\u6590\u5fc3\u9053\uff1a\u201c\u77a7\u4f60\u6b65\u5c65\u77eb\u6377\uff0c\u8eab\u6cd5\u7a33\u51dd\uff0c\u4e5f\u662f\u4e00\u628a\u597d\u624b\uff0c\u4f55\u82e6\u4e3a\u4e86\u529f\u540d\u5229\u7984\uff0c\u5374\u53bb\u505a\u4eba\u5bb6\u4f4e\u4e09\u4e0b\u56db\u7684\u5974\u624d\u3002\u201d \u3000\u3000\u8042\u94ba\u63a5\u8fc7\u9526\u76d2\uff0c\u53ea\u89c9\u76d2\u5b50\u751a\u662f\u6c89\u91cd\uff0c\u60f3\u6765\u6240\u76db\u793c\u54c1\u5fc5\u662f\u8d35\u91cd\u4e4b\u7269\u3002\u90a3\u6b66\u5b98\u966a\u7b11\u9053\uff1a\u201c\u8bf7\u80e1\u5927\u7237\u6253\u5f00\u77a7\u77a7\uff0c\u5c31\u662f\u53ea\u6536\u4e00\u4ef6\uff0c\u5c0f\u4eba\u4e5f\u611f\u6069\u4e0d\u6d45\u3002\u201d\u8042\u94ba\u9053\uff1a\u201c\u80e1\u5927\u54e5\uff0c\u8fd9\u4f4d\u5144\u5f1f\u6240\u8a00\u4e5f\u662f\u5b9e\u60c5\uff0c\u5018\u82e5\u9a6c\u59d1\u5a18\u56e0\u6b64\u602a\u8d23\uff0c\u8fd9\u4f4d\u5144\u5f1f\u7684\u524d\u7a0b\u5c31\u6b64\u6bc1\u4e86\u3002\u4f60\u5c31\u80e1\u4e71\u6536\u53d7\u4e00\u4ef6\uff0c\u4e5f\u597d\u8ba9\u4ed6\u6709\u4e2a\u4ea4\u4ee3\u3002\u201d \u3000\u3000\u80e1\u6590\u5fc3\u9053\uff1a\u201c\u51b2\u7740\u4f60\u7684\u9762\u5b50\uff0c\u6211\u4fbf\u6536\u4e00\u4ef6\u62ff\u53bb\u5468\u6d4e\u7a77\u4eba\u4e5f\u662f\u597d\u7684\u3002\u201d\u4e8e\u662f\u4f38\u624b\u63ed\u5f00\u9526\u76d2\u4e4b\u76d6\uff0c\u53ea\u89c1\u76d2\u91cc\u4e00\u5f20\u7ea2\u7f0e\u5305\u7740\u56db\u56db\u65b9\u65b9\u7684\u4e00\u5757\u4e1c\u897f\uff0c\u7f0e\u5b50\u7684\u56db\u89d2\u6298\u62e2\u6765\u6253\u4e86\u4e24\u4e2a\u7ed3\u3002\u80e1\u6590\u76b1\u7740\u7709\u5934\uff0c\u9053\uff1a\u201c\u90a3\u662f\u4ec0\u4e48\uff1f\u201d\u90a3\u6b66\u5b98\u9053\uff1a\u201c\u5c0f\u4eba\u4e0d\u77e5\u3002\u201d\u80e1\u6590\u5fc3\u60f3\uff1a\u201c\u8fd9\u793c\u7269\u4e0d\u77e5\u662f\u5426\u6574\u5757\u7684\uff1f\u201d\u4f38\u624b\u4fbf\u53bb\u89e3\u90a3\u7f0e\u5b50\u7684\u7ed3\u3002 \u3000\u3000\u521a\u89e3\u5f00\u4e86\u4e00\u4e2a\u7ed3\uff0c\u7a81\u7136\u95f4\u76d2\u76d6\u4e00\u5f39\uff0c\u62cd\u7684\u4e00\u54cd\uff0c\u76d2\u76d6\u731b\u5730\u5408\u62e2\uff0c\u5c06\u4ed6\u53cc\u624b\u7262\u7262\u631f\u4f4f\uff0c\u970e\u65f6\u95f4\u4f46\u89c9\u5267\u75db\u5f7b\u9aa8\uff0c\u8155\u9aa8\u51e0\u4e4e\u6298\u65ad\uff0c\u539f\u6765\u8fd9\u76d2\u5b50\u7adf\u662f\u7cbe\u94a2\u6240\u94f8\uff0c\u4e2d\u95f4\u85cf\u7740\u6781\u7cbe\u5de7\u6781\u5f3a\u529b\u7684\u673a\u62ec\uff0c\u76d2\u5916\u5305\u4ee5\u9526\u7f0e\uff0c\u662f\u4ee5\u77a7\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u76d2\u76d6\u4e00\u5408\u4e0a\uff0c\u767b\u65f6\u8d8a\u6536\u8d8a\u7d27\uff0c\u80e1\u6590\u6025\u5fd9\u6c14\u8fd0\u53cc\u8155\u4e0e\u6297\uff0c\u82e5\u662f\u4ed6\u5185\u529b\u7a0d\u5dee\uff0c\u53ea\u6015\u53cc\u8155\u5df2\u65ad\uff0c\u9976\u662f\u5982\u6b64\uff0c\u4e00\u53e3\u6c14\u4e5f\u662f\u4e1d\u6beb\u677e\u61c8\u4e0d\u5f97\u3002\u56db\u4e2a\u6b66\u5b98\u89c1\u4ed6\u4e2d\u8ba1\uff0c\u7acb\u65f6\u62d4\u51fa\u5315\u9996\uff0c\u4e8c\u524d\u4e8c\u540e\uff0c\u62b5\u5728\u4ed6\u7684\u524d\u80f8\u540e\u80cc\u3002 \u3000\u3000\u8042\u94ba\u60ca\u5f97\u5446\u4e86\uff0c\u5fd9\u9053\uff1a\u201c\u5e72\u2026\u2026\u5e72\u4ec0\u4e48\uff1f\u201d\u90a3\u9886\u5934\u7684\u6b66\u5b98\u9053\uff1a\u201c\u798f\u5927\u5e05\u6709\u4ee4\uff0c\u6355\u62ff\u5201\u5f92\u80e1\u6590\u3002\u201d\u8042\u94ba\u9053\uff1a\u201c\u80e1\u5927\u7237\u662f\u9a6c\u59d1\u5a18\u8bf7\u6765\u7684\u5ba2\u4eba\uff0c\u600e\u80fd\u5982\u6b64\u76f8\u5f85\uff1f\u201d\u90a3\u6b66\u5b98\u51b7\u7b11\u9053\uff1a\u201c\u8042\u5927\u54e5\uff0c\u4f60\u4fbf\u95ee\u798f\u5927\u5e05\u53bb\u3002\u54b1\u4eec\u5f53\u5dee\u7684\u600e\u77e5\u9053\u8fd9\u8bb8\u591a\uff1f\u201d \u3000\u3000\u8042\u94ba\u4e00\u6014\uff0c\u9053\uff1a\u201c\u80e1\u5927\u54e5\u4f60\u653e\u5fc3\uff0c\u5176\u4e2d\u5fc5\u6709\u8bef\u4f1a\u3002\u6211\u4fbf\u53bb\u62a5\u77e5\u9a6c\u59d1\u5a18\uff0c\u5979\u5b9a\u80fd\u8bbe\u6cd5\u6551\u4f60\u3002\u201d\u90a3\u6b66\u5b98\u559d\u9053\uff1a\u201c\u7ad9\u4f4f\uff01\u798f\u5927\u5e05\u5bc6\u4ee4\uff0c\u51b3\u4e0d\u80fd\u6cc4\u6f0f\u98ce\u58f0\uff0c\u8ba9\u9a6c\u59d1\u5a18\u77e5\u9053\u3002\u4f60\u6709\u51e0\u9897\u8111\u888b\uff1f\u201d \u3000\u3000\u8042\u94ba\u6ee1\u5934\u90fd\u662f\u9ec4\u8c46\u5927\u7684\u6c57\u73e0\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u76d2\u5b50\u662f\u6211\u4eb2\u624b\u9012\u7ed9\u80e1\u5927\u54e5\u7684\uff0c\u6211\u5c82\u4e0d\u662f\u6210\u4e86\u5978\u8bc8\u5c0f\u4eba\uff1f\u4f46\u798f\u5927\u5e05\u65e2\u6709\u5bc6\u4ee4\uff0c\u53c8\u600e\u80fd\u6297\u547d\uff1f\u201d \u3000\u3000\u90a3\u6b66\u5b98\u5c06\u5315\u9996\u8f7b\u8f7b\u5f80\u524d\u4e00\u9001\uff0c\u5200\u5c16\u5272\u7834\u80e1\u6590\u8863\u670d\uff0c\u523a\u5230\u808c\u80a4\uff0c\u559d\u9053\uff1a\u201c\u5feb\u8d70\u5427\uff01\u201d \u3000\u3000\u90a3\u94a2\u76d2\u662f\u897f\u6d0b\u5de7\u624b\u5320\u4eba\u6240\u5236\uff0c\u5f39\u7c27\u673a\u62ec\u6781\u662f\u9738\u9053\uff0c\u4e0a\u4e0b\u76d2\u8fb9\u7684\u9526\u7f0e\u4e00\u7834\uff0c\u4fbf\u9732\u51fa\u950b\u5229\u7684\u5203\u53e3\uff0c\u539f\u6765\u76d2\u76d6\u7684\u4e24\u8fb9\uff0c\u7adf\u662f\u4e24\u628a\u5229\u5203\u3002 \u3000\u3000\u8042\u94ba\u89c1\u80e1\u6590\u624b\u8155\u4e0a\u9c9c\u8840\u8ff8\u6d41\uff0c\u5373\u5c06\u4f24\u5230\u7b4b\u9aa8\uff0c\u5fc3\u60f3\uff1a\u201c\u80e1\u5927\u54e5\u4fbf\u662f\u72af\u4e86\u5f25\u5929\u5927\u7f6a\uff0c\u4e5f\u4e0d\u80fd\u4ee5\u6b64\u5351\u9119\u624b\u6bb5\u5bf9\u4ed8\u3002\u201d\u4ed6\u5bf9\u80e1\u6590\u4e00\u76f4\u656c\u4ef0\uff0c\u8fd9\u65f6\u89c1\u6b64\u60e8\u72b6\uff0c\u53c8\u81ea\u6127\u7978\u51fa\u4e8e\u5df1\uff0c\u7a81\u7136\u4f38\u624b\u6293\u4f4f\u94a2\u76d2\uff0c\u624b\u6307\u63d2\u5165\u76d2\u7f1d\uff0c\u7528\u529b\u4e00\u6273\uff0c\u76d2\u76d6\u5f20\u5f00\uff0c\u80e1\u6590\u53cc\u624b\u767b\u5f97\u81ea\u7531\u3002 \u3000\u3000\u4fbf\u5728\u6b64\u65f6\uff0c\u90a3\u4e3a\u9996\u6b66\u5b98\u4e00\u5315\u9996\u523a\u4e86\u8fc7\u53bb\u3002\u8042\u94ba\u7684\u6b66\u529f\u672c\u5728\u6b64\u4eba\u4e4b\u4e0a\uff0c\u53ea\u662f\u53cc\u624b\u5c1a\u5728\u94a2\u76d2\u4e4b\u4e2d\uff0c\u7adf\u7136\u65e0\u6cd5\u95ea\u907f\uff0c\u201c\u554a\u201d \u3000\u3000\u7684\u4e00\u58f0\u60e8\u547c\uff0c\u5315\u9996\u5165\u80f8\uff0c\u7acb\u65f6\u6bd9\u547d\u3002 \u3000\u3000\u5728\u8fd9\u7535\u5149\u77f3\u706b\u822c\u7684\u4e00\u77ac\u4e4b\u95f4\uff0c\u80e1\u6590\u5410\u4e00\u53e3\u6c14\uff0c\u80f8\u80cc\u95f4\u767b\u65f6\u7f29\u5165\u6570\u5bf8\uff0c\u7acb\u5373\u7eb5\u8eab\u800c\u8d77\uff0c\u4e09\u67c4\u5315\u9996\u76f4\u5212\u4e0b\u6765\uff0c\u4e24\u67c4\u843d\u7a7a\uff0c\u53e6\u4e00\u67c4\u5374\u5728\u4ed6\u53f3\u817f\u4e0a\u5212\u4e86\u4e00\u9053\u8840\u75d5\u3002\u80e1\u6590\u53cc\u8db3\u9f50\u98de\uff0c\u6b64\u65f6\u6027\u547d\u5728\u547c\u5438\u4e4b\u95f4\uff0c\u54ea\u91cc\u8fd8\u80fd\u5bb9\u60c5\uff1f\u53f3\u8db3\u8db3\u5c16\u524d\u8e22\uff0c\u5de6\u8db3\u8db3\u8ddf\u540e\u649e\uff0c\u4eba\u5728\u534a\u7a7a\u4e4b\u4e2d\uff0c\u5df2\u5c06\u4e24\u540d\u6b66\u5b98\u8e22\u6bd9\u3002 \u3000\u3000\u523a\u6b7b\u8042\u94ba\u7684\u90a3\u6b66\u5b98\u4e0d\u7b49\u80e1\u6590\u843d\u5730\uff0c\u4e00\u62db\u201c\u8346\u8f72\u732e\u56fe\u201d\uff0c\u5f84\u5411\u80e1\u6590\u5c0f\u8179\u4e0a\u523a\u6765\uff0c\u8fd9\u4e00\u4e0b\u52bf\u631f\u52b2\u98ce\uff0c\u751a\u662f\u51cc\u5389\u3002\u80e1\u6590\u5de6\u8db3\u81ea\u540e\u7ffb\u4e0a\uff0c\u817e\u7684\u4e00\u4e0b\uff0c\u8e39\u5728\u4ed6\u7684\u80f8\u53e3\u3002\u90a3\u6b66\u5b98\u6251\u901a\u4e00\u58f0\uff0c\u8dcc\u5165\u4e86\u8377\u6c60\uff0c\u5341\u4f59\u6839\u808b\u9aa8\u9f50\u65ad\uff0c\u773c\u89c1\u662f\u4e0d\u6d3b\u7684\u4e86\u3002 \u3000\u3000\u53e6\u4e00\u540d\u6b66\u5b98\u89c1\u52bf\u5934\u4e0d\u597d\uff0c\u201c\u554a\u54df\u201d\u4e00\u58f0\uff0c\u8f6c\u5934\u4fbf\u8d70\u3002\u80e1\u6590\u7eb5\u8eab\u8fc7\u53bb\uff0c\u5939\u9888\u63d0\u5c06\u8d77\u6765\uff0c\u4e00\u638c\u4fbf\u8981\u5f80\u4ed6\u5929\u7075\u76d6\u51fb\u843d\uff0c\u6708\u5149\u4e0b\u53ea\u89c1\u4ed6\u773c\u4e2d\u6ee1\u662f\u54c0\u6c42\u4e4b\u8272\uff0c\u5fc3\u80a0\u4e00\u8f6f\uff1a\u201c\u4ed6\u548c\u6211\u65e0\u51a4\u65e0\u4ec7\uff0c\u4e0d\u8fc7\u662f\u53d7\u798f\u5eb7\u5b89\u7684\u5dee\u9063\uff0c\u4f55\u5fc5\u4f24\u4ed6\u6027\u547d\u3002\u201d \u3000\u3000\u5f53\u4e0b\u63d0\u7740\u4ed6\u8d70\u5230\u5047\u5c71\u4e4b\u540e\uff0c\u4f4e\u58f0\u559d\u95ee\uff1a\u201c\u798f\u5eb7\u5b89\u4f55\u4ee5\u8981\u62ff\u6211\uff1f\u201d\u90a3\u6b66\u5b98\u9053\uff1a\u201c\u5b9e\u2026\u2026\u5b9e\u5728\u4e0d\u77e5\u9053\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u8fd9\u65f6\u4ed6\u5728\u54ea\u91cc\uff1f\u201d\u90a3\u6b66\u5b98\u9053\uff1a\u201c\u798f\u5927\u5e05\u2026\u2026\u798f\u5927\u5e05\u4ece\u9a6c\u59d1\u5a18\u7684\u9601\u5b50\u4e2d\u51fa\u6765\uff0c\u5631\u5490\u4e86\u6211\u4eec\uff0c\u53c8\u2026\u2026\u53c8\u56de\u8fdb\u53bb\u4e86\u3002\u201d\u80e1\u6590\u4f38\u624b\u70b9\u4e86\u4ed6\u7684\u54d1\u7a74\uff0c\u8bf4\u9053\uff1a\u201c\u547d\u4fbf\u9976\u4f60\uff0c\u660e\u65e5\u6709\u4eba\u95ee\u8d77\uff0c\u4f60\u4fbf\u8bf4\u8fd9\u59d3\u8042\u7684\u4e5f\u662f\u6211\u6740\u7684\u3002\u5018\u82e5\u4f60\u8d70\u6f0f\u6d88\u606f\uff0c\u4ed6\u5bb6\u5c0f\u6709\u751a\u98ce\u5439\u8349\u52a8\uff0c\u6211\u5c06\u4f60\u5168\u5bb6\u6740\u5f97\u5e72\u5e72\u51c0\u51c0\u3002\u201d\u90a3\u6b66\u5b98\u8bf4\u4e0d\u51fa\u8bdd\uff0c\u53ea\u662f\u70b9\u5934\u3002 \u3000\u3000\u80e1\u6590\u62b1\u8fc7\u8042\u94ba\u7684\u5c38\u8eab\uff0c\u85cf\u5728\u5047\u5c71\u7a9f\u91cc\uff0c\u8dea\u4e0b\u62dc\u4e86\u56db\u62dc\uff0c\u518d\u5c06\u5176\u4f59\u4e24\u5177\u5c38\u8eab\u8e22\u5728\u8349\u4e1b\u4e4b\u4e2d\uff0c\u7136\u540e\u6495\u4e0b\u8863\u895f\uff0c\u88f9\u4e86\u4e24\u8155\u7684\u4f24\u53e3\uff0c\u817f\u4e0a\u7684\u5200\u4f24\u867d\u4e0d\u5389\u5bb3\uff0c\u53e3\u5b50\u5374\u957f\uff0c\u8fd9\u65f6\u5fcd\u4e0d\u4f4f\u6012\u706b\u586b\u81ba\uff0c\u62fe\u8d77\u4e00\u628a\u5315\u9996\uff0c\u4fbf\u5f80\u6c34\u9601\u800c\u6765\u3002 \u3000\u3000\u80e1\u6590\u77e5\u9053\u798f\u5eb7\u5b89\u5e9c\u4e2d\u536b\u58eb\u5fc5\u4f17\uff0c\u4e0d\u6562\u7a0d\u6709\u8f7b\u5ffd\uff0c\u5728\u5927\u6811\u3001\u5047\u5c71\u3001\u82b1\u4e1b\u4e4b\u540e\u77a7\u6e05\u695a\u524d\u9762\u65e0\u4eba\uff0c\u8fd9\u624d\u95ea\u8eab\u800c\u524d\u3002\u5c06\u8fd1\u6c34\u9601\u7684\u6865\u8fb9\uff0c\u53ea\u89c1\u4e24\u5784\u706f\u7b3c\u524d\u5bfc\uff0c\u516b\u540d\u536b\u58eb\u5f15\u7740\u798f\u5eb7\u5b89\u8fc7\u6765\u3002\u5e78\u597d\u82b1\u56ed\u4e2d\u6781\u5bcc\u4e18\u58d1\u4e4b\u80dc\uff0c\u5230\u5904\u90fd\u53ef\u85cf\u8eab\uff0c\u80e1\u6590\u8eab\u5b50\u4e00\u7f29\uff0c\u9690\u5728\u4e00\u682a\u77f3\u7b0b\u4e4b\u540e\uff0c\u53ea\u542c\u798f\u5eb7\u5b89\u9053\uff1a\u201c\u4f60\u53bb\u5ba1\u95ee\u90a3\u59d3\u80e1\u7684\u5201\u5f92\uff0c\u7ec6\u7ec6\u95ee\u4ed6\u8ddf\u9a6c\u59d1\u5a18\u600e\u751f\u76f8\u8bc6\uff0c\u662f\u4ec0\u4e48\u4ea4\u60c5\uff0c\u534a\u591c\u91cc\u5230\u6211\u5e9c\u4e2d\uff0c\u662f\u4e3a\u4e86\u751a\u4e48\u3002\u8fd9\u4ef6\u4e8b\u4e0d\u8bb8\u6cc4\u6f0f\u534a\u70b9\u98ce\u58f0\u3002\u5ba1\u95ee\u660e\u767d\u4e4b\u540e\uff0c\u901f\u6765\u56de\u62a5\u3002\u81f3\u4e8e\u90a3\u5201\u5f92\u5462\uff0c\u55ef\uff0c\u4e58\u7740\u4eca\u665a\u4fbf\u6bd9\u4e86\u4ed6\uff0c\u6b64\u4e8b\u4ee5\u540e\u4e0d\u53ef\u518d\u63d0\u3002\u201d \u3000\u3000\u4ed6\u8eab\u540e\u4e00\u4eba\u8fde\u58f0\u7b54\u5e94\uff0c\u9053\uff1a\u201c\u5c0f\u4eba\u7406\u4f1a\u5f97\u3002\u201d\u798f\u5eb7\u5b89\u53c8\u9053\uff1a\u201c\u82e5\u662f\u9a6c\u59d1\u5a18\u95ee\u8d77\uff0c\u4fbf\u8bf4\u6211\u9001\u4e86\u4ed6\u4e09\u5343\u4e24\u94f6\u5b50\uff0c\u9063\u4ed6\u56de\u5bb6\u91cc\u53bb\u4e86\u3002\u201d\u90a3\u4eba\u53c8\u9053\uff1a\u201c\u662f\uff0c\u662f\uff01\u201d\u80e1\u6590\u8d8a\u542c\u8d8a\u6012\uff0c\u5fc3\u60f3\u539f\u6765\u798f\u5eb7\u5b89\u53ea\u4e0d\u8fc7\u7591\u5fc3\u6211\u548c\u9a6c\u59d1\u5a18\u6709\u751a\u79c1\u60c5\uff0c\u7adf\u7136\u4fbf\u4e0b\u6bd2\u624b\uff0c\u7ec8\u4e8e\u5bb3\u4e86\u8042\u94ba\u7684\u6027\u547d\u3002 \u3000\u3000\u8fd9\u65f6\u5019\u80e1\u6590\u82e5\u662f\u7eb5\u5c06\u51fa\u53bb\uff0c\u7acb\u65f6\u4fbf\u53ef\u5c06\u798f\u5eb7\u5b89\u6bd9\u4e8e\u5315\u9996\u4e4b\u4e0b\uff0c\u4f46\u4ed6\u5fc3\u4e2d\u867d\u6012\uff0c\u884c\u4e8b\u5374\u4e0d\u83bd\u649e\uff0c\u81ea\u5fd6\u521d\u5230\u4eac\u5e08\uff0c\u8bf8\u4e8b\u672a\u660e\uff0c\u800c\u798f\u5eb7\u5b89\u624b\u638c\u5929\u4e0b\u5175\u9a6c\u5927\u6743\uff0c\u58f0\u5a01\u8d6b\u8d6b\uff0c\u7a76\u662f\u4e0d\u6562\u8d38\u7136\u4fbf\u51fa\u624b\u884c\u523a\uff0c\u4e8e\u662f\u4f0f\u5728\u77f3\u7b0b\u4e4b\u540e\uff0c\u5f85\u798f\u5eb7\u5b89\u4e00\u884c\u53bb\u8fdc\u3002 \u3000\u3000\u90a3\u53d7\u547d\u53bb\u62f7\u95ee\u80e1\u6590\u4e4b\u4eba\u53e3\u4e2d\u8f7b\u8f7b\u54fc\u7740\u5c0f\u66f2\uff0c\u65bd\u65bd\u7136\u7684\u8fc7\u6765\u3002\u80e1\u6590\u63a2\u8eab\u957f\u81c2\uff0c\u9661\u5730\u5728\u4ed6\u80c1\u4e0b\u4e00\u70b9\u3002\u90a3\u4eba\u4e5f\u6ca1\u77a7\u6e05\u654c\u4eba\u662f\u8c01\uff0c\u8eab\u5b50\u4e00\u8f6f\uff0c\u6251\u5730\u5012\u4e86\u3002\u80e1\u6590\u518d\u5728\u4ed6\u4e24\u5904\u819d\u5f2f\u91cc\u70b9\u4e86\u7a74\u9053\uff0c\u7136\u540e\u5feb\u6b65\u5411\u798f\u5eb7\u5b89\u8ddf\u53bb\uff0c\u8fdc\u8fdc\u542c\u5f97\u4ed6\u8bf4\u9053\uff1a\u201c\u8fd9\u6df1\u66f4\u534a\u591c\u7684\uff0c\u8001\u592a\u592a\u53eb\u6211\u6709\u4ec0\u4e48\u4e8b\uff1f\u662f\u8c01\u8ddf\u5979\u8001\u4eba\u5bb6\u5728\u4e00\u8d77\uff1f\u201d\u4e00\u540d\u4f8d\u4ece\u9053\uff1a\u201c\u516c\u4e3b\u4eca\u65e5\u8fdb\u5bab\uff0c\u56de\u5e9c\u540e\u4e00\u76f4\u548c\u8001\u592a\u592a\u5728\u4e00\u8d77\u3002\u201d\u798f\u5eb7\u5b89\u201c\u55ef\u201d\u4e86\u4e00\u58f0\uff0c\u4e0d\u518d\u8a00\u8bed\u3002 \u3000\u3000\u80e1\u6590\u8ddf\u7740\u4ed6\u7a7f\u5ead\u7ed5\u5eca\uff0c\u89c1\u4ed6\u8fdb\u4e86\u4e00\u95f4\u9752\u677e\u73af\u7ed5\u7684\u5c4b\u5b50\u3002\u4f17\u4f8d\u4ece\u8fdc\u8fdc\u7684\u5b88\u5728\u5c4b\u5916\u3002\u80e1\u6590\u7ed5\u5230\u5c4b\u540e\uff0c\u94bb\u8fc7\u6811\u4e1b\uff0c\u53ea\u89c1\u5317\u8fb9\u7a97\u4e2d\u900f\u51fa\u706f\u5149\u3002\u4ed6\u6084\u6084\u8d70\u5230\u7a97\u4e0b\uff0c\u89c1\u7a97\u5b50\u662f\u7eff\u8272\u7ec6\u7eb1\u6240\u7cca\uff0c\u5fc3\u5ff5\u4e00\u52a8\uff0c\u6084\u6ca1\u58f0\u7684\u6298\u4e86\u4e00\u6761\u677e\u679d\uff0c\u6321\u5728\u9762\u524d\uff0c\u7136\u540e\u9694\u7740\u677e\u9488\u4ece\u7a97\u7eb1\u4e2d\u5411\u5c4b\u5185\u671b\u53bb\u3002 \u3000\u3000\u53ea\u89c1\u5c4b\u5185\u5c45\u4e2d\u5750\u7740\u4e24\u4e2a\u4e09\u5341\u6765\u5c81\u7684\u8d35\u5987\uff0c\u4e0b\u9996\u5750\u7740\u4e00\u4e2a\u516d\u5341\u6765\u5c81\u7684\u8001\u5987\uff0c\u90a3\u8001\u5987\u7684\u5de6\u4fa7\uff0c\u53c8\u5750\u7740\u4e24\u4e2a\u5987\u4eba\u3002\u4e94\u4e2a\u5973\u5b50\u90fd\u662f\u6ee1\u8eab\u7eb1\u7f57\u7ef8\u7f0e\uff0c\u73e0\u5149\u5b9d\u6c14\u3002\u798f\u5eb7\u5b89\u5148\u5c48\u819d\u5411\u4e2d\u95f4\u4e24\u4e2a\u8d35\u5987\u8bf7\u5b89\uff0c\u518d\u5411\u8001\u5987\u8bf7\u5b89\uff0c\u53eb\u4e86\u58f0\uff1a\u201c\u5a18\uff01\u201d\u53e6\u5916\u4e24\u4e2a\u5987\u4eba\u89c1\u4ed6\u8fdb\u6765\uff0c\u65e9\u4fbf\u7ad9\u8d77\u3002 \u3000\u3000\u539f\u6765\u798f\u5eb7\u5b89\u7684\u7236\u4eb2\u5085\u6052\uff0c\u662f\u5f53\u4eca\u4e7e\u9686\u4e4b\u540e\u5b5d\u8d24\u7687\u540e\u7684\u4eb2\u5f1f\u3002\u5085\u6052\u7684\u59bb\u5b50\u662f\u6ee1\u6d32\u51fa\u540d\u7684\u7f8e\u4eba\uff0c\u5165\u5bab\u671d\u89c1\u4e4b\u65f6\u7ed9\u4e7e\u9686\u770b\u4e2d\u4e86\uff0c\u4e24\u4eba\u6709\u4e86\u79c1\u60c5\uff0c\u751f\u4e0b\u7684\u5b69\u5b50\u4fbf\u662f\u798f\u5eb7\u5b89\u3002\u5085\u6052\u7531\u4e8e\u59ca\u59ca\u3001\u59bb\u5b50\u3001\u513f\u5b50\u4e09\u91cd\u5173\u7cfb\uff0c\u6df1\u5f97\u4e7e\u9686\u7684\u5ba0\u5e78\uff0c\u51fa\u5c06\u5165\u76f8\uff0c\u4e00\u5171\u505a\u4e86\u4e8c\u5341\u4e09\u5e74\u7684\u592a\u5e73\u5bb0\u76f8\uff0c\u6b64\u65f6\u5df2\u7ecf\u901d\u4e16\u3002 \u3000\u3000\u5085\u6052\u5171\u6709\u56db\u5b50\u3002\u957f\u5b50\u798f\u7075\u5b89\uff0c\u5c01\u591a\u7f57\u989d\u9a78\uff0c\u66fe\u968f\u5146\u60e0\u51fa\u5f81\u56de\u7586\u6709\u529f\uff0c\u5347\u4e3a\u6b63\u767d\u65d7\u6ee1\u6d32\u526f\u90fd\u7edf\uff0c\u5df2\u6b7b\u3002\u6b21\u5b50\u798f\u9686\u5b89\uff0c\u5c01\u548c\u7855\u989d\u9a78\uff0c\u505a\u8fc7\u5175\u90e8\u5c1a\u4e66\u548c\u5de5\u90e8\u5c1a\u4e66\uff0c\u5c01\u516c\u7235\u3002\u7b2c\u4e09\u5b50\u4fbf\u662f\u798f\u5eb7\u5b89\u3002\u4ed6\u4e24\u4e2a\u54e5\u54e5\u90fd\u505a\u9a78\u9a6c\uff0c\u4ed6\u6700\u5f97\u4e7e\u9686\u6069\u9047\uff0c\u53cd\u800c\u4e0d\u5c1a\u516c\u4e3b\uff0c\u4e0d\u77e5\u5185\u60c5\u7684\u4eba\u4fbf\u5f15\u4ee5\u4e3a\u5947\uff0c\u5176\u5b9e\u4ed6\u662f\u4e7e\u9686\u7684\u4eb2\u751f\u9aa8\u8089\uff0c\u600e\u80fd\u518d\u505a\u7687\u5e1d\u7684\u5973\u5a7f\uff1f\u8fd9\u65f6\u4ed6\u8eab\u4efb\u5175\u90e8\u5c1a\u4e66\uff0c\u603b\u7ba1\u5185\u52a1\u5e9c\u5927\u81e3\uff0c\u52a0\u592a\u5b50\u592a\u4fdd\u8854\u3002\u5085\u6052\u7b2c\u56db\u5b50\u798f\u957f\u5b89\u4efb\u6237\u90e8\u5c1a\u4e66\uff0c\u540e\u6765\u5c01\u5230\u4faf\u7235\u3002\u5f53\u65f6\u6ee1\u95e8\u5bcc\u8d35\u6781\u54c1\uff0c\u4e3e\u671d\u83ab\u53ca\u3002 \u3000\u3000\u5c4b\u5185\u5c45\u4e2d\u800c\u5750\u7684\u8d35\u5987\u4fbf\u662f\u798f\u5eb7\u5b89\u7684\u4e24\u4e2a\u516c\u4e3b\u5ac2\u5ac2\u3002\u4e8c\u5ac2\u548c\u5609\u516c\u4e3b\u80fd\u8bf4\u4f1a\u9053\uff0c\u5584\u4f3a\u4eba\u610f\uff0c\u662f\u4e7e\u9686\u7684\u7b2c\u56db\u5973\uff0c\u81ea\u5e7c\u4fbf\u6781\u5f97\u4e7e\u9686\u7684\u5ba0\u7231\uff0c\u6ca1\u9694\u6570\u65e5\uff0c\u4e7e\u9686\u4fbf\u8981\u62db\u5979\u8fdb\u5bab\uff0c\u8bf4\u8bdd\u89e3\u95f7\u3002\u5979\u548c\u798f\u5eb7\u5b89\u5b9e\u867d\u5144\u59b9\uff0c\u540d\u5c5e\u541b\u81e3\uff0c\u56e0\u6b64\u798f\u5eb7\u5b89\u89c1\u4e86\u5979\u4e5f\u987b\u8bf7\u5b89\u884c\u793c\u3002\u5176\u4f59\u4e24\u4e2a\u5987\u4eba\u4e00\u4e2a\u662f\u798f\u5eb7\u5b89\u7684\u59bb\u5b50\u6d77\u5170\u6c0f\uff0c\u4e00\u4e2a\u662f\u798f\u957f\u5b89\u7684\u59bb\u5b50\u3002 \u3000\u3000\u798f\u5eb7\u5b89\u5728\u897f\u9996\u7684\u6905\u4e0a\u5750\u4e0b\uff0c\u8bf4\u9053\uff1a\u201c\u4e24\u4f4d\u516c\u4e3b\u548c\u5a18\u8fd9\u4e48\u591c\u6df1\u4e86\uff0c\u600e\u5730\u8fd8\u4e0d\u5b89\u606f\uff1f\u201d\u8001\u592b\u4eba\u9053\uff1a\u201c\u4e24\u4f4d\u516c\u4e3b\u542c\u8bf4\u4f60\u6709\u4e86\u5b69\u513f\uff0c\u559c\u6b22\u5f97\u4e86\u4e0d\u5f97\uff0c\u6025\u7740\u8981\u89c1\u89c1\u3002\u201d\u798f\u5eb7\u5b89\u5411\u6d77\u5170\u6c0f\u671b\u4e86\u4e00\u773c\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u5973\u5b50\u662f\u6c49\u4eba\uff0c\u8fd8\u6ca1\u5b66\u4f1a\u793c\u4eea\uff0c\u56e0\u6b64\u6ca1\u6562\u8ba9\u5979\u6765\u53e9\u89c1\u516c\u4e3b\u548c\u5a18\u3002\u201d \u3000\u3000\u548c\u5609\u516c\u4e3b\u7b11\u9053\uff1a\u201c\u5eb7\u8001\u4e09\u770b\u4e2d\u7684\uff0c\u90a3\u8fd8\u5dee\u5f97\u4e86\u4e48\uff1f\u6211\u4eec\u4e5f\u4e0d\u8981\u89c1\u90a3\u5973\u5b50\uff0c\u4f60\u5feb\u53eb\u4eba\u9886\u90a3\u4e24\u4e2a\u5b69\u513f\u6765\u77a7\u77a7\u3002\u7236\u7687\u8bf4\uff0c\u8fc7\u51e0\u65e5\u53eb\u5ac2\u5b50\u5e26\u4e86\u8fdb\u5bab\u671d\u89c1\u5462\u3002\u201d \u3000\u3000\u798f\u5eb7\u5b89\u6697\u81ea\u5f97\u610f\uff0c\u5fc3\u60f3\u8fd9\u4e24\u4e2a\u7c89\u5986\u7389\u7422\u7684\u5b69\u513f\uff0c\u7687\u4e0a\u89c1\u4e86\u5b9a\u7136\u559c\u7231\uff0c\u4e8e\u662f\u547d\u4e2b\u9b1f\u51fa\u53bb\u5429\u5490\u4f8d\u4ece\uff0c\u7acb\u5373\u62b1\u4e24\u4f4d\u5c0f\u516c\u5b50\u6765\u89c1\u3002 \u3000\u3000\u548c\u5609\u516c\u4e3b\u53c8\u9053\uff1a\u201c\u4eca\u513f\u6211\u8fdb\u5bab\u53bb\uff0c\u6bcd\u540e\u8bf4\u5eb7\u8001\u4e09\u505a\u4e8b\u9b3c\u9b3c\u795f\u795f\uff0c\u5728\u5916\u8fb9\u751f\u4e0b\u4e86\u5b69\u513f\uff0c\u51e0\u5e74\u4e5f\u4e0d\u53bb\u627e\u56de\u6765\uff0c\u628a\u5927\u5bb6\u7792\u5f97\u597d\u7d27\uff0c\u5c0f\u5fc3\u7236\u7687\u5265\u4f60\u7684\u76ae\u3002\u201d\u798f\u5eb7\u5b89\u7b11\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u5b69\u513f\u7684\u4e8b\uff0c\u4e5f\u662f\u76f4\u5230\u4e0a\u4e2a\u6708\u624d\u77e5\u9053\u7684\u3002\u201d \u3000\u3000\u8bf4\u4e86\u4e00\u4f1a\u5b50\u8bdd\uff0c\u4e24\u540d\u5976\u5988\u62b1\u4e86\u90a3\u5bf9\u53cc\u751f\u5b69\u513f\u8fdb\u6765\u3002\u798f\u5eb7\u5b89\u547d\u5144\u5f1f\u4fe9\u5411\u516c\u4e3b\u3001\u8001\u592a\u592a\u3001\u592a\u592a\u3001\u5a76\u5a76\u78d5\u5934\u3002\u4e24\u4e2a\u5b69\u513f\u5f88\u662f\u542c\u8bdd\uff0c\u867d\u7136\u7761\u773c\u60fa\u5fea\uff0c\u8fd8\u662f\u4f9d\u8a00\u884c\u793c\u3002 \u3000\u3000\u4f17\u4eba\u89c1\u8fd9\u5bf9\u5b69\u5b50\u7684\u6a21\u6837\u513f\u957f\u5f97\u7adf\u65e0\u534a\u70b9\u5206\u522b\uff0c\u4e00\u822c\u7684\u5706\u5706\u8138\u86cb\uff0c\u7709\u76ee\u6e05\u79c0\uff0c\u548c\u5609\u516c\u4e3b\u62cd\u624b\u7b11\u9053\uff1a\u201c\u5eb7\u8001\u4e09\uff0c\u8fd9\u5bf9\u5b69\u513f\u8ddf\u4f60\u662f\u4e00\u4e2a\u5370\u6a21\u5b50\u91cc\u51fa\u6765\u7684\u3002\u4f60\u4fbf\u662f\u60f3\u8d56\u4e86\u4e0d\u8ba4\u5e10\uff0c\u53ef\u4e5f\u8d56\u4e0d\u6389\u3002\u201d\u6d77\u5170\u6c0f\u5bf9\u8fd9\u4ef6\u4e8b\u672c\u6765\u5fc3\u4e2d\u4e0d\u60a6\uff0c\u4f46\u89c1\u8fd9\u5bf9\u53cc\u751f\u5b69\u513f\u5b9e\u5728\u53ef\u7231\uff0c\u5fcd\u4e0d\u4f4f\u6402\u5728\u6000\u91cc\uff0c\u7740\u5b9e\u4eb2\u70ed\u3002\u8001\u592b\u4eba\u548c\u516c\u4e3b\u4eec\u5404\u6709\u89c1\u9762\u793c\u54c1\u3002\u4e24\u4e2a\u5976\u5988\u6276\u7740\u5b69\u513f\uff0c\u4e0d\u4f4f\u7684\u78d5\u5934\u8c22\u8d4f\u3002 \u3000\u3000\u4e24\u4f4d\u516c\u4e3b\u548c\u6d77\u5170\u6c0f\u7b49\u8bf4\u4e86\u4e00\u4f1a\u5b50\u8bdd\uff0c\u4e00\u9f50\u9000\u51fa\u3002\u8001\u592b\u4eba\u548c\u798f\u5eb7\u5b89\u5e26\u9886\u53cc\u751f\u5b69\u513f\u9001\u516c\u4e3b\u51fa\u95e8\uff0c\u56de\u6765\u53c8\u81ea\u5750\u4e0b\u3002 \u3000\u3000\u8001\u592b\u4eba\u53eb\u8fc7\u8eab\u540e\u7684\u4e2b\u9b1f\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u53bb\u8ddf\u90a3\u9a6c\u59d1\u5a18\u8bf4\uff0c\u8001\u592a\u592a\u5f88\u559c\u6b22\u8fd9\u5bf9\u5b69\u513f\uff0c\u4eca\u665a\u4fbf\u7559\u4ed6\u4eec\u4f34\u8001\u592a\u592a\u7761\uff0c\u53eb\u9a6c\u59d1\u5a18\u4e0d\u7528\u7b49\u4ed6\u4e24\u5144\u5f1f\u5566\u3002\u201d\u90a3\u4e2b\u9b1f\u7b54\u5e94\u4e86\u3002\u8001\u592b\u4eba\u62c9\u5f00\u684c\u8fb9\u7684\u62bd\u5c5c\uff0c\u53d6\u51fa\u4e00\u628a\u9576\u6ee1\u4e86\u5b9d\u77f3\u7684\u91d1\u58f6\uff0c\u653e\u5728\u684c\u4e0a\uff0c\u8bf4\u9053\uff1a\u201c\u62ff\u8fd9\u58f6\u53c2\u6c64\u53bb\u8d4f\u7ed9\u9a6c\u59d1\u5a18\uff0c\u8bf4\u8001\u592a\u592a\u4e00\u5b9a\u597d\u597d\u7167\u770b\u5979\u7684\u5b69\u5b50\uff0c\u53eb\u5979\u653e\u5fc3\uff01\u201d \u3000\u3000\u798f\u5eb7\u5b89\u624b\u4e2d\u6b63\u6367\u4e86\u4e00\u7897\u8336\uff0c\u4e00\u542c\u6b64\u8a00\uff0c\u8138\u8272\u5927\u53d8\uff0c\u53cc\u624b\u4e00\u98a4\uff0c\u4e00\u5927\u7247\u8336\u6c34\u6cfc\u4e86\u51fa\u6765\uff0c\u6e85\u5728\u888d\u4e0a\uff0c\u6014\u6014\u7684\u62ff\u7740\u8336\u7897\u826f\u4e45\u4e0d\u8bed\u3002 \u3000\u3000\u53ea\u89c1\u90a3\u4e2b\u9b1f\u6367\u4e86\u91d1\u58f6\uff0c\u653e\u5728\u4e00\u53ea\u91d1\u6f06\u63d0\u76d2\u4e4b\u4e2d\uff0c\u63d0\u7740\u53bb\u4e86\u3002 \u3000\u3000\u8fd9\u65f6\u4e24\u4e2a\u5b69\u513f\u5026\u5f97\u8981\u7761\uff0c\u4e0d\u4f4f\u53e3\u7684\u53eb\uff1a\u201c\u5988\u5988\uff0c\u5988\u5988\uff0c\u8981\u5988\u5988\u3002\u201d\u8001\u592b\u4eba\u9053\uff1a\u201c\u597d\u5b69\u5b50\u522b\u5435\uff0c\u4e56\u4e56\u7684\u8ddf\u7740\u5976\u5976\u3002\u5976\u5976\u7ed9\u7cd6\u7cd6\u7cd5\u7cd5\u5403\u3002\u201d\u4e24\u4e2a\u5b69\u513f\u54ed\u53eb\uff1a\u201c\u4e0d\u8981\u7cd6\u7cd6\u7cd5\u7cd5\uff01\u4e0d\u8981\u5976\u5976\uff01\u8981\u5988\u5988\uff01\u201d\u8001\u592b\u4eba\u8138\u4e00\u6c89\uff0c\u6325\u624b\u547d\u5976\u5988\u5c06\u5b69\u5b50\u5e26\u4e86\u4e0b\u53bb\uff0c\u53c8\u4f7f\u4e2a\u773c\u8272\uff0c\u4f17\u4e2b\u9b1f\u4e5f\u90fd\u9000\u51fa\uff0c\u5c4b\u5185\u53ea\u5269\u4e0b\u798f\u5eb7\u5b89\u6bcd\u5b50\u4e8c\u4eba\u3002 \u3000\u3000\u9694\u4e86\u597d\u4e00\u4f1a\uff0c\u6bcd\u5b50\u4fe9\u59cb\u7ec8\u6ca1\u4ea4\u8c08\u534a\u53e5\uff0c\u8001\u592b\u4eba\u51dd\u671b\u513f\u5b50\u3002 \u3000\u3000\u798f\u5eb7\u5b89\u5374\u671b\u7740\u522b\u5904\uff0c\u4e0d\u6562\u548c\u6bcd\u4eb2\u7684\u76ee\u5149\u76f8\u63a5\u3002 \u3000\u3000\u8fc7\u4e86\u826f\u4e45\uff0c\u798f\u5eb7\u5b89\u53f9\u4e86\u53e3\u957f\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u5a18\uff0c\u4f60\u4e3a\u4ec0\u4e48\u5bb9\u4e0d\u5f97\u5979\uff1f\u201d\u8001\u592b\u4eba\u9053\uff1a\u201c\u90a3\u8fd8\u7528\u95ee\u4e48\uff0c\u8fd9\u5973\u5b50\u662f\u6c49\u4eba\uff0c\u5c45\u5fc3\u4fbf\u5c31\u53f5\u6d4b\u3002\u4f55\u51b5\u53c8\u662f\u9556\u5c40\u5b50\u51fa\u8eab\uff0c\u4f7f\u5200\u62a1\u67aa\uff0c\u4e00\u8eab\u7684\u6b66\u529f\u3002\u54b1\u4eec\u5e9c\u4e2d\u6709\u4e24\u4f4d\u516c\u4e3b\uff0c\u600e\u80fd\u548c\u8fd9\u6837\u7684\u4eba\u5171\u5c45\uff1f\u5341\u5e74\u524d\u7687\u4e0a\u8eab\u5386\u5927\u9669\uff0c\u4e5f\u4fbf\u662f\u4e3a\u4e86\u4e00\u4e2a\u5f02\u65cf\u7684\u7f8e\u5973\uff0c\u96be\u9053\u4f60\u4fbf\u5fd8\u4e86\uff1f\u8ba9\u8fd9\u79cd\u6bd2\u86c7\u4e00\u822c\u7684\u5973\u5b50\u5904\u5728\u8098\u814b\u4e4b\u95f4\uff0c\u54b1\u4eec\u90fd\u8981\u5bdd\u98df\u4e0d\u5b89\u3002\u201d \u3000\u3000\u798f\u5eb7\u5b89\u9053\uff1a\u201c\u5a18\u7684\u8bdd\u81ea\u7136\u4e0d\u9519\uff0c\u5b69\u513f\u521d\u65f6\u4e5f\u6ca1\u60f3\u8981\u63a5\u5979\u8fdb\u5e9c\uff0c\u53ea\u662f\u6d3e\u4eba\u53bb\u77a7\u77a7\uff0c\u9001\u5979\u4e9b\u94f6\u4e24\u3002\u90a3\u77e5\u5979\u7adf\u751f\u4e0b\u4e86\u4e24\u4e2a\u513f\u5b50\uff0c\u8fd9\u662f\u5b69\u513f\u7684\u4eb2\u9aa8\u8840\uff0c\u90a3\u4fbf\u53c8\u4e0d\u540c\u4e86\u3002\u201d \u3000\u3000\u8001\u592b\u4eba\u70b9\u5934\u9053\uff1a\u201c\u4f60\u5e74\u8fd1\u56db\u65ec\uff0c\u5c1a\u65e0\u6240\u51fa\uff0c\u6709\u8fd9\u4e24\u4e2a\u5b69\u5b50\u81ea\u7136\u5f88\u597d\u3002\u54b1\u4eec\u597d\u597d\u629a\u517b\u4e24\u4e2a\u5b69\u513f\u957f\u5927\uff0c\u65e5\u540e\u4ed6\u4eec\u5c01\u4faf\u88ad\u7235\uff0c\u4e00\u751f\u8363\u534e\u5bcc\u8d35\uff0c\u4ed6\u4eec\u7684\u6bcd\u4eb2\u4e5f\u53ef\u5b89\u5fc3\u4e86\u3002\u201d \u3000\u3000\u798f\u5eb7\u5b89\u6c89\u541f\u534a\u664c\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5b69\u513f\u4e4b\u610f\uff0c\u5c06\u90a3\u5973\u5b50\u9001\u5f80\u8fb9\u90e1\u8fdc\u5730\uff0c\u4ece\u6b64\u4e0d\u518d\u89c1\u9762\uff0c\u90a3\u4e5f\u662f\u4e86\uff0c\u60f3\u4e0d\u5230\u6bcd\u4eb2\u2026\u2026\u201d\u8001\u592b\u4eba\u8138\u8272\u4e00\u6c89\uff0c\u8bf4\u9053\uff1a\u201c\u6789\u4e3a\u4f60\u8eab\u5c45\u9ad8\u5b98\uff0c\u8fde\u8fd9\u4e2d\u95f4\u7684\u5229\u5bb3\u4e5f\u6ca1\u60f3\u5230\uff1f\u5979\u7684\u4eb2\u751f\u5b69\u513f\u5728\u54b1\u4eec\u5e9c\u4e2d\uff0c\u5979\u5c82\u6709\u4e0d\u751f\u4e8b\u7aef\u7684\uff1f\u8fd9\u79cd\u6c5f\u6e56\u5973\u5b50\u628a\u5fc3\u4e00\u6a2a\uff0c\u4ec0\u4e48\u4e8b\u4e5f\u505a\u5f97\u51fa\u6765\u3002\u201d\u798f\u5eb7\u5b89\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u8001\u592b\u4eba\u9053\uff1a\u201c\u4f60\u547d\u4eba\u5c06\u5979\u539a\u4e8e\u846c\u6b93\uff0c\u4e5f\u7b97\u662f\u5c3d\u4e86\u4e00\u756a\u5fc3\u610f\u2026\u2026\u201d\u798f\u5eb7\u5b89\u53c8\u70b9\u4e86\u70b9\u5934\uff0c\u5e94\u9053\uff1a\u201c\u662f\uff01\u201d \u3000\u3000\u80e1\u6590\u5728\u7a97\u5916\u8d8a\u542c\u8d8a\u662f\u5fc3\u60ca\uff0c\u521d\u65f6\u5c1a\u4e0d\u660e\u4ed6\u6bcd\u5b50\u4e8c\u4eba\u8bdd\u4e2d\u4e4b\u610f\uff0c\u5f85\u542c\u5230\u201c\u539a\u4e8e\u846c\u6b93\u201d\u56db\u5b57\uff0c\u8fd9\u4e00\u60ca\u5f53\u771f\u662f\u975e\u540c\u5c0f\u53ef\uff0c\u5fc3\u9053\uff1a\u201c\u539f\u6765\u4ed6\u4e8c\u4eba\u6041\u5730\u6b79\u6bd2\uff0c\u5b9a\u4e0b\u9634\u8c0b\u6bd2\u8ba1\uff0c\u593a\u4e86\u5b69\u5b50\uff0c\u7adf\u7136\u8fd8\u8981\u8c0b\u6b7b\u9a6c\u59d1\u5a18\u3002\u6b64\u4e8b\u5341\u5206\u7d27\u6025\uff0c\u7247\u523b\u5ef6\u6328\u4e0d\u5f97\uff0c\u4e58\u7740\u4ed6\u4e8c\u4eba\u6bd2\u8ba1\u5c1a\u672a\u53d1\u52a8\uff0c\u987b\u5f97\u7acb\u5373\u53bb\u544a\u77e5\u9a6c\u59d1\u5a18\uff0c\u8fde\u591c\u6551\u5979\u51fa\u5e9c\u3002\u201d \u3000\u3000\u5f53\u4e0b\u6084\u6084\u8d70\u51fa\uff0c\u5faa\u539f\u8def\u56de\u5411\u6c34\u9601\uff0c\u5e78\u559c\u591c\u9759\u4eba\u5b9a\uff0c\u56ed\u4e2d\u65e0\u4eba\u884c\u8d70\uff0c\u6740\u6b7b\u70b9\u5012\u7684\u536b\u58eb\u4e5f\u5c1a\u672a\u7ed9\u4eba\u53d1\u89c9\u3002\u80e1\u6590\u5fc3\u4e2d\u7126\u6025\uff0c\u8d70\u5f97\u6781\u5feb\uff0c\u5fc3\u4e2d\u5374\u81ea\u8e0c\u8e87\uff1a\u201c\u9a6c\u59d1\u5a18\u5bf9\u8fd9\u798f\u5eb7\u5b89\u4e00\u89c1\u953a\u60c5\uff0c\u4ed6\u4e8c\u4eba\u4e45\u522b\u91cd\u9022\uff0c\u6b63\u81ea\u60c5\u70ed\uff0c\u600e\u80af\u542c\u4e86\u6211\u8fd9\u4e00\u756a\u8bdd\uff0c\u4fbf\u6b64\u9003\u51fa\u5e9c\u53bb\uff1f\u8981\u600e\u751f\u8bf4\u5f97\u5979\u76f8\u4fe1\u624d\u597d\uff1f\u201d \u3000\u3000\u5fc3\u4e2d\u8ba1\u8f83\u672a\u5b9a\uff0c\u5df2\u5230\u6c34\u9601\u4e4b\u524d\uff0c\u4f46\u89c1\u95e8\u5916\u5df2\u591a\u4e86\u56db\u540d\u536b\u58eb\uff0c\u5fc3\u60f3\uff1a\u201c\u54fc\uff0c\u4ed6\u4eec\u5df2\u5148\u4f0f\u4e0b\u4e86\u4eba\uff0c\u6015\u5979\u9003\u8d70\uff01\u201d\u5f53\u4e0b\u4e0d\u6562\u60ca\u52a8\uff0c\u7ed5\u5230\u9601\u540e\uff0c\u8f7b\u8eab\u4e00\u7eb5\uff0c\u8dc3\u8fc7\u6c34\u9601\u5916\u7684\u4e00\u7247\u6c60\u6c34\uff0c\u53ea\u89c1\u9601\u4e2d\u706f\u706b\u5140\u81ea\u672a\u7184\uff0c\u51d1\u773c\u8fc7\u53bb\u5f80\u7f1d\u4e2d\u4e00\u671b\uff0c\u4e0d\u7531\u5f97\u5446\u4e86\u3002 \u3000\u3000\u53ea\u89c1\u9a6c\u6625\u82b1\u5012\u5728\u5730\u4e0b\uff0c\u62b1\u7740\u809a\u5b50\u4e0d\u4f4f\u547b\u541f\uff0c\u5934\u53d1\u6563\u4e71\uff0c\u8138\u4e0a\u5df2\u5168\u65e0\u8840\u8272\uff0c\u670d\u4f8d\u5979\u7684\u4e2b\u9b1f\u4ec6\u5987\u5374\u4e00\u4e2a\u4e5f\u4e0d\u5728\u8eab\u8fb9\u3002 \u3000\u3000\u80e1\u6590\u89c1\u4e86\u8fd9\u60c5\u666f\uff0c\u767b\u65f6\u9192\u609f\uff1a\u201c\u554a\u54df\uff0c\u4e0d\u597d\uff01\u7ec8\u7a76\u8fd8\u662f\u6765\u8fdf\u4e86\u4e00\u6b65\u3002\u201d\u6025\u5fd9\u63a8\u7a97\u800c\u5165\uff0c\u4fef\u8eab\u770b\u65f6\uff0c\u53ea\u89c1\u5979\u6c14\u5598\u751a\u6025\uff0c\u8138\u8272\u94c1\u9752\uff0c\u773c\u775b\u901a\u7ea2\uff0c\u5982\u8981\u6ef4\u51fa\u8840\u6765\u3002 \u3000\u3000\u9a6c\u6625\u82b1\u89c1\u80e1\u6590\u8fc7\u6765\uff0c\u65ad\u65ad\u7eed\u7eed\u7684\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u2026\u2026\u809a\u5b50\u75db\u2026\u2026\u80e1\u5144\u5f1f\u2026\u2026\u4f60\u2026\u2026\u201d\u8bf4\u5230\u4e00\u4e2a\u201c\u4f60\u201d\u5b57\uff0c\u518d\u4e5f\u65e0\u529b\u8bf4\u4e0b\u53bb\u3002\u80e1\u6590\u5728\u5979\u8033\u8fb9\u4f4e\u58f0\u9053\uff1a\u201c\u521a\u624d\u4f60\u5403\u4e86\u4ec0\u4e48\u4e1c\u897f\uff1f\u201d\u9a6c\u6625\u82b1\u773c\u671b\u8336\u51e0\u4e0a\u7684\u4e00\u628a\u9576\u6ee1\u4e86\u7ea2\u84dd\u5b9d\u77f3\u7684\u91d1\u58f6\uff0c\u5374\u8bf4\u4e0d\u51fa\u8bdd\u3002 \u3000\u3000\u80e1\u6590\u8ba4\u5f97\u8fd9\u628a\u91d1\u58f6\uff0c\u6b63\u662f\u798f\u5eb7\u5b89\u7684\u6bcd\u4eb2\u88c5\u4e86\u53c2\u6c64\uff0c\u547d\u4e2b\u9b1f\u9001\u7ed9\u5979\u559d\u7684\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u8001\u5987\u4eba\u5fc3\u8ba1\u597d\u6bd2\uff0c\u5979\u8981\u5bb3\u6b7b\u9a6c\u59d1\u5a18\uff0c\u5374\u8981\u7559\u4e0b\u90a3\u4e24\u4e2a\u5b69\u5b50\uff0c\u662f\u4ee5\u5148\u5c06\u5b69\u5b50\u53eb\u53bb\uff0c\u8fd9\u624d\u9001\u53c2\u6c64\u6765\u3002\u5426\u5219\u9a6c\u59d1\u5a18\u62ff\u5230\u53c2\u6c64\uff0c\u77e5\u662f\u6781\u6ecb\u8865\u7684\u7269\u54c1\uff0c\u5b9a\u4f1a\u7ed9\u513f\u5b50\u559d\u4e0a\u51e0\u53e3\u3002\u201d\u53c8\u60f3\uff1a\u201c\u55ef\uff0c\u798f\u5eb7\u5b89\u4e00\u89c1\u9001\u51fa\u53c2\u6c64\uff0c\u8138\u8272\u7acb\u53d8\uff0c\u8336\u6c34\u6cfc\u5728\u8863\u895f\u4e4b\u4e0a\uff0c\u4ed6\u5f53\u65f6\u663e\u7136\u5df2\u77e5\u53c2\u6c64\u4e4b\u4e2d\u4e0b\u4e86\u6bd2\uff0c\u5c45\u7136\u5e76\u4e0d\u8bbe\u6cd5\u963b\u6b62\uff0c\u4e8b\u540e\u53c8\u4e0d\u6765\u6551\u3002\u4ed6\u867d\u975e\u4eb2\u624b\u4e0b\u6bd2\uff0c\u5374\u4e5f\u548c\u4eb2\u624b\u4e0b\u6bd2\u4e00\u822c\u65e0\u5f02\u3002\u201d\u4e0d\u7981\u5583\u5583\u7684\u9053\uff1a\u201c\u597d\u6bd2\u8fa3\u7684\u5fc3\u80a0\uff01\u201d \u3000\u3000\u9a6c\u6625\u82b1\u6323\u624e\u7740\u9053\uff1a\u201c\u4f60\u4f60\u2026\u2026\u5feb\u53bb\u62a5\u77e5\u2026\u2026\u798f\u5927\u5e05\uff0c\u8bf7\u5927\u592b\uff0c\u8bf7\u5927\u592b\u77a7\u77a7\u2026\u2026\u201d\u80e1\u6590\u5fc3\u9053\uff1a\u201c\u8981\u798f\u5927\u5e05\u8bf7\u5927\u592b\uff0c\u53ea\u6709\u518d\u8bf7\u4f60\u591a\u5403\u4e9b\u6bd2\u836f\u3002\u773c\u4e0b\u53ea\u6709\u8981\u4e8c\u59b9\u8bbe\u6cd5\u89e3\u6551\u3002\u201d\u4e8e\u662f\u63ed\u8d77\u4e00\u5757\u6905\u62ab\uff0c\u5c06\u90a3\u76db\u8fc7\u53c2\u6c64\u7684\u91d1\u58f6\u5305\u4e86\uff0c\u63e3\u5728\u6000\u4e2d\uff0c\u542c\u6c34\u9601\u5916\u5e76\u65e0\u52a8\u9759\uff0c\u62b1\u8d77\u9a6c\u6625\u82b1\uff0c\u8f7b\u8f7b\u4ece\u7a97\u4e2d\u8df3\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u9a6c\u6625\u82b1\u5403\u4e86\u4e00\u60ca\uff0c\u53eb\u9053\uff1a\u201c\u80e1\u2026\u2026\u201d\u80e1\u6590\u5fd9\u4f38\u624b\u6309\u4f4f\u5979\u5634\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u522b\u4f5c\u58f0\uff0c\u6211\u5e26\u4f60\u53bb\u770b\u533b\u751f\u3002\u201d\u9a6c\u6625\u82b1\u9053\uff1a\u201c\u6211\u7684\u5b69\u5b50\u2026\u2026\u201d \u3000\u3000\u80e1\u6590\u4e0d\u53ca\u7ec6\u8bf4\uff0c\u62b1\u7740\u5979\u8dc3\u8fc7\u6c60\u5858\uff0c\u6b63\u8981\u89c5\u8def\u5954\u51fa\uff0c\u5ffd\u542c\u5f97\u8eab\u540e\u8863\u895f\u5e26\u98ce\uff0c\u4e24\u4e2a\u4eba\u5954\u4e86\u8fc7\u6765\uff0c\u559d\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\uff1f\u201d\u80e1\u6590\u5411\u524d\u75be\u5954\uff0c\u90a3\u4e24\u4eba\u4e5f\u63d0\u6c14\u6025\u8ffd\u3002 \u3000\u3000\u80e1\u6590\u8dd1\u5f97\u751a\u5feb\uff0c\u7a81\u7136\u95f4\u6536\u4f4f\u811a\u6b65\u3002\u90a3\u4e24\u4eba\u6ca1\u6599\u5230\u4ed6\u4f1a\u5ffd\u5730\u505c\u6b65\uff0c\u4e00\u51b2\u4fbf\u8fc7\u4e86\u4ed6\u7684\u8eab\u524d\u3002\u80e1\u6590\u7a9c\u8d77\u534a\u7a7a\uff0c\u53cc\u817f\u9f50\u98de\uff0c\u4e24\u53ea\u811a\u8db3\u5c16\u540c\u65f6\u5206\u522b\u8e22\u4e2d\u4e24\u4eba\u80cc\u5fc3\u201c\u795e\u5802\u7a74\u201d\u3002\u4e24\u4eba\u54fc\u90fd\u6ca1\u54fc\u4e00\u58f0\uff0c\u6251\u5730\u4fbf\u5012\u3002\u770b\u8fd9\u4e24\u4eba\u8eab\u4e0a\u7684\u670d\u8272\uff0c\u6b63\u662f\u5b88\u5728\u6c34\u9601\u5916\u7684\u5e9c\u4e2d\u536b\u58eb\u3002 \u3000\u3000\u80e1\u6590\u5fc3\u60f3\u8fd9\u4e48\u4e00\u6765\uff0c\u5f62\u8ff9\u5df2\u9732\uff0c\u987e\u4e0d\u5230\u518d\u884c\u63a9\u9970\u884c\u85cf\uff0c\u5411\u5e9c\u95e8\u5916\u76f4\u51b2\u51fa\u53bb\u3002\u4f46\u542c\u5f97\u5e9c\u4e2d\u4f20\u547c\u4e4b\u58f0\u6b64\u4f0f\u5f7c\u8d77\uff0c\u4f17\u536b\u58eb\u5927\u53eb\uff1a\u201c\u6709\u523a\u5ba2\uff0c\u6709\u523a\u5ba2\uff01\u201d \u3000\u3000\u4ed6\u8fdb\u6765\u4e4b\u65f6\u6cbf\u8def\u7559\u5fc3\uff0c\u8ba4\u660e\u9014\u5f84\uff0c\u5f53\u4e0b\u4ecd\u4ece\u9e45\u5375\u77f3\u7684\u82b1\u5f84\u5954\u5411\u5c0f\u95e8\uff0c\u7ffb\u8fc7\u7c89\u5899\uff0c\u90a3\u8f86\u9a6c\u8f66\u5012\u4ecd\u662f\u5019\u5728\u95e8\u5916\u3002\u4ed6\u5c06\u9a6c\u6625\u82b1\u653e\u5165\u8f66\u4e2d\uff0c\u559d\u9053\uff1a\u201c\u56de\u53bb\u3002\u201d\u90a3\u8f66\u592b\u5df2\u542c\u5230\u5e9c\u4e2d\u5435\u56b7\uff0c\u89c1\u80e1\u6590\u795e\u8272\u6709\u5f02\uff0c\u5f85\u8981\u95ee\u4e2a\u660e\u767d\uff0c\u80e1\u6590\u7830\u7684\u4e00\u638c\uff0c\u5c06\u4ed6\u4ece\u5ea7\u4f4d\u4e0a\u51fb\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u4fbf\u5728\u6b64\u65f6\uff0c\u5e9c\u4e2d\u5df2\u6709\u56db\u4e94\u540d\u536b\u58eb\u8ffd\u5230\uff0c\u80e1\u6590\u63d0\u8d77\u7f30\u7ef3\uff0c\u5f97\u513f\u4e00\u58f0\uff0c\u8d76\u8f66\u4fbf\u8dd1\uff0c\u51e0\u540d\u536b\u58eb\u8ffd\u4e86\u5341\u4f59\u4e08\u6ca1\u8ffd\u4e0a\uff0c\u7eb7\u7eb7\u53eb\u9053\uff1a\u201c\u5e26\u9a6c\uff0c\u5e26\u9a6c\u3002\u201d \u3000\u3000\u80e1\u6590\u50ac\u9a6c\u75be\u9a70\uff0c\u5954\u51fa\u91cc\u8bb8\uff0c\u4f46\u542c\u5f97\u8e44\u58f0\u6025\u4fc3\uff0c\u4e8c\u5341\u4f59\u9a91\u9a6c\u5148\u540e\u8ffd\u6765\u3002\u8ffd\u5175\u9a91\u7684\u90fd\u662f\u597d\u9a6c\uff0c\u8d8a\u8ffd\u8d8a\u8fd1\u3002\u80e1\u6590\u6697\u6697\u7126\u6025\uff1a \u3000\u3000\u201c\u8fd9\u662f\u5929\u5b50\u811a\u5e95\u4e0b\u7684\u4eac\u57ce\uff0c\u53ef\u4e0d\u6bd4\u5bfb\u5e38\uff0c\u518d\u4e00\u95f9\u4fbf\u6709\u5de1\u57ce\u5175\u9a6c\u51fa\u52a8\u56f4\u6355\uff0c\u5c31\u7b97\u6211\u80fd\u8131\u8eab\uff0c\u9a6c\u59d1\u5a18\u5374\u53c8\u5982\u4f55\u80fd\u6551\uff1f\u201d \u3000\u3000\u9ed1\u6697\u4e4b\u4e2d\uff0c\u89c1\u8ffd\u6765\u7684\u4eba\u624b\u4e2d\u90fd\u62ff\u7740\u706b\u628a\uff0c\u8f66\u4e2d\u9a6c\u6625\u82b1\u521d\u65f6\u5c1a\u6709\u547b\u541f\u4e4b\u58f0\uff0c\u8fd9\u65f6\u5374\u5df2\u6ca1\u4e86\u58f0\u606f\uff0c\u80e1\u6590\u597d\u751f\u8bb0\u6302\uff0c\u95ee\u9053\uff1a\u201c\u9a6c\u59d1\u5a18\uff0c\u809a\u75db\u597d\u4e9b\u4e86\u4e48\uff1f\u201d\u8fde\u95ee\u6570\u58f0\uff0c\u9a6c\u6625\u82b1\u90fd\u6ca1\u56de\u7b54\u3002\u4e00\u56de\u5934\uff0c\u53ea\u89c1\u706b\u70ac\u7167\u8000\uff0c\u8ffd\u5175\u53c8\u8fd1\u4e86\u4e9b\u3002\u5ffd\u542c\u5f97\u55d6\u7684\u4e00\u58f0\u54cd\uff0c\u6709\u4eba\u63b7\u4e86\u4e00\u679a\u98de\u8757\u77f3\u8fc7\u6765\uff0c\u8981\u6253\u4ed6\u540e\u5fc3\u3002\u80e1\u6590\u5de6\u624b\u4e00\u6284\u63a5\u4f4f\uff0c\u56de\u624b\u63b7\u53bb\uff0c\u4f46\u542c\u5f97\u4e00\u4eba\u201c\u554a\u54df\u201d\u4e00\u58f0\u547c\u53eb\uff0c\u6454\u4e0b\u9a6c\u6765\u3002 \u3000\u3000\u8fd9\u4e00\u4e0b\u5012\u5c06\u80e1\u6590\u63d0\u9192\u4e86\uff0c\u6700\u597d\u662f\u53d1\u6697\u5668\u4ee5\u9000\u8ffd\u5175\uff0c\u53ef\u662f\u8eab\u8fb9\u6ca1\u643a\u5e26\u6697\u5668\uff0c\u8ffd\u6765\u7684\u798f\u5e9c\u536b\u58eb\u53c8\u5b66\u4e86\u4e56\uff0c\u4e0d\u518d\u53d1\u5c04\u6697\u5668\u3002 \u3000\u3000\u4ed6\u597d\u751f\u7126\u6025\uff1a\u201c\u56de\u5230\u5ba3\u6b66\u95e8\u5916\u8def\u7a0b\u5c1a\u8fdc\uff0c\u534a\u591c\u91cc\u4e00\u5e72\u4eba\u5982\u6b64\u5927\u547c\u5c0f\u53eb\uff0c\u5982\u4f55\u4e0d\u60ca\u52a8\u5b98\u5175\uff1f\u201d\u60c5\u6025\u667a\u751f\uff0c\u5ffd\u7136\u60f3\u8d77\u6000\u4e2d\u7684\u91d1\u58f6\uff0c\u4f38\u624b\u9694\u7740\u6905\u62ab\u4f7f\u52b2\u8fde\u634f\u6570\u4e0b\uff0c\u91d1\u58f6\u4e0a\u9576\u5d4c\u7684\u5b9d\u77f3\u767b\u65f6\u8dcc\u843d\u4e86\u516b\u4e5d\u5757\uff0c\u4ed6\u5c06\u5b9d\u77f3\u53d6\u5728\u624b\u4e2d\uff0c\u706b\u628a\u7167\u8000\u4e0b\u77a7\u5f97\u5206\u660e\uff0c\u53f3\u624b\u8fde\u626c\uff0c\u5b9d\u77f3\u4e00\u9897\u9897\u98de\u51fa\uff0c\u516b\u9897\u5b9d\u77f3\u6253\u4e2d\u4e86\u4e94\u540d\u536b\u58eb\uff0c\u5b9d\u77f3\u867d\u5c0f\uff0c\u80e1\u6590\u7684\u624b\u52b2\u5374\u5927\uff0c\u6253\u4e2d\u5934\u8138\u773c\u76ee\uff0c\u75bc\u75db\u975e\u5e38\u3002\u8fd9\u4e48\u4e00\u6765\uff0c\u4f17\u536b\u58eb\u4fbf\u4e0d\u6562\u592a\u8fc7\u903c\u8fd1\u3002 \u3000\u3000\u80e1\u6590\u900f\u4e86\u4e00\u53e3\u957f\u6c14\uff0c\u4f38\u624b\u5230\u8f66\u4e2d\u4e00\u63a2\u9a6c\u6625\u82b1\u7684\u9f3b\u606f\uff0c\u5e78\u559c\u5c1a\u6709\u547c\u5438\uff0c\u53ea\u542c\u5f97\u5979\u4f4e\u58f0\u547b\u541f\u4e00\u58f0\uff0c\u8138\u988a\u4e0a\u5374\u662f\u751a\u4e3a\u51b0\u51b7\uff0c\u773c\u89c1\u79bb\u4f4f\u6240\u5df2\u4e0d\u5728\u8fdc\uff0c\u5f53\u4e0b\u6325\u97ad\u8fde\u50ac\uff0c\u9a70\u5230\u4e00\u6761\u5c94\u8def\u4e4b\u4e0a\u3002\u4f4f\u6240\u5728\u4e1c\uff0c\u4ed6\u5374\u5c06\u9a6c\u8f66\u8d76\u7740\u5411\u897f\uff0c\u8f6c\u8fc7\u4e00\u4e2a\u5f2f\uff0c\u7acb\u65f6\u56de\u8eab\u62b1\u8d77\u9a6c\u6625\u82b1\uff0c\u6325\u9a6c\u97ad\u8fde\u62bd\u6570\u97ad\uff0c\u8eab\u5b50\u79bb\u8f66\u7eb5\u8d77\uff0c\u4f0f\u5728\u4e00\u95f4\u5c4b\u5b50\u9876\u4e0a\u3002\u53ea\u89c1\u9a6c\u8f66\u5411\u897f\u76f4\u9a70\uff0c\u4f17\u536b\u58eb\u8ffd\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u80e1\u6590\u5f85\u4f17\u4eba\u8d70\u8fdc\uff0c\u8fd9\u624d\u4ece\u5c4b\u9876\u56de\u5165\u5b85\u4e2d\uff0c\u521a\u8d8a\u8fc7\u56f4\u5899\uff0c\u53ea\u542c\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u56de\u6765\u4e86\uff01\u6709\u4eba\u8ffd\u4f60\u4e48\uff1f\u201d\u80e1\u6590\u9053\uff1a\u201c\u9a6c\u59d1\u5a18\u4e2d\u4e86\u5267\u6bd2\uff0c\u5feb\u7ed9\u77a7\u77a7\u3002\u201d\u4ed6\u62b1\u7740\u9a6c\u6625\u82b1\uff0c\u62a2\u5148\u8fdb\u4e86\u5385\u4e2d\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u70b9\u8d77\u8721\u70db\uff0c\u89c1\u9a6c\u6625\u82b1\u8138\u4e0a\u7070\u6251\u6251\u7684\u5168\u65e0\u8840\u8272\uff0c\u518d\u634f\u4e86\u634f\u5979\u7684\u624b\u6307\uff0c\u89c1\u9677\u4e0b\u4e4b\u540e\u4e0d\u518d\u5f39\u8d77\uff0c\u8f7b\u8f7b\u6447\u4e86\u6447\u5934\uff0c\u95ee\u9053\uff1a\u201c\u4e2d\u7684\u4ec0\u4e48\u6bd2\uff1f\u201d\u80e1\u6590\u4ece\u6000\u4e2d\u53d6\u51fa\u91d1\u58f6\uff0c\u9053\uff1a\u201c\u5728\u53c2\u6c64\u91cc\u4e0b\u7684\u6bd2\u3002\u8fd9\u662f\u76db\u53c2\u6c64\u7684\u58f6\u3002\u201d\u7a0b\u7075\u7d20\u63ed\u5f00\u58f6\u76d6\uff0c\u55c5\u4e86\u51e0\u4e0b\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u5389\u5bb3\uff0c\u662f\u9e64\u9876\u7ea2\u3002\u201d \u3000\u3000\u80e1\u6590\u9053\uff1a\u201c\u80fd\u6551\u4e0d\u80fd\uff1f\u201d\u7a0b\u7075\u7d20\u4e0d\u7b54\uff0c\u63a2\u4e86\u63a2\u9a6c\u6625\u82b1\u7684\u5fc3\u8df3\uff0c\u8bf4\u9053\uff1a\u201c\u82e5\u4e0d\u662f\u5927\u5bcc\u5927\u8d35\u4e4b\u5bb6\uff0c\u4e5f\u4e0d\u80fd\u6709\u8fd9\u822c\u73cd\u8d35\u7684\u91d1\u58f6\u3002\u201d \u3000\u3000\u80e1\u6590\u6068\u6068\u7684\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4e0b\u6bd2\u7684\u662f\u5bb0\u76f8\u592b\u4eba\uff0c\u5175\u90e8\u5c1a\u4e66\u7684\u6bcd\u4eb2\u3002\u201d \u3000\u3000\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u554a\uff0c\u6211\u4eec\u8fd9\u4e00\u884c\u4eba\u4e2d\uff0c\u7adf\u51fa\u4e86\u5982\u6b64\u5bcc\u8d35\u7684\u4eba\u7269\u3002\u201d \u3000\u3000\u80e1\u6590\u89c1\u5979\u4e0d\u52a8\u58f0\u8272\uff0c\u4f3c\u4e4e\u9a6c\u6625\u82b1\u4e2d\u6bd2\u867d\u6df1\uff0c\u5c1a\u6709\u53ef\u6551\uff0c\u5fc3\u4e0b\u7a0d\u5bbd\u3002\u7a0b\u7075\u7d20\u7ffb\u5f00\u9a6c\u6625\u82b1\u7684\u773c\u76ae\u77a7\u4e86\u77a7\uff0c\u7a81\u7136\u4f4e\u58f0\u201c\u554a\u201d\u7684\u4e00\u58f0\u3002\u80e1\u6590\u5fd9\u95ee\uff1a\u201c\u600e\u4e48\uff1f\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u53c2\u6c64\u4e2d\u9664\u4e86\u9e64\u9876\u7ea2\uff0c\u8fd8\u6709\u756a\u6728\u9cd6\u3002\u201d\u80e1\u6590\u4e0d\u6562\u95ee\u201c\u8fd8\u6709\u6551\u6ca1\u6709\uff1f\u201d\u5374\u95ee\uff1a\u201c\u600e\u751f\u6551\u6cd5\uff1f\u201d \u3000\u3000\u7a0b\u7075\u7d20\u76b1\u7709\u9053\uff1a\u201c\u4e24\u6837\u6bd2\u836f\u5939\u653b\uff0c\u8fd9\u4e00\u6765\u4fbf\u5927\u8d39\u624b\u811a\u3002\u201d\u8fd4\u8eab\u5165\u5ba4\uff0c\u4ece\u836f\u7bb1\u4e2d\u53d6\u51fa\u4e24\u9897\u767d\u8272\u836f\u4e38\uff0c\u7ed9\u9a6c\u6625\u82b1\u670d\u4e0b\uff0c\u8bf4\u9053\uff1a\u201c\u987b\u5f97\u627e\u4e2a\u6e05\u9759\u7684\u5bc6\u5ba4\uff0c\u7528\u91d1\u9488\u523a\u5979\u5341\u4e09\u5904\u7a74\u9053\uff0c\u89e3\u836f\u4ece\u7a74\u9053\u4e2d\u9001\u5165\u4f53\u5185\uff0c\u82e5\u80fd\u9a6c\u4e0a\u65bd\u9488\uff0c\u5b9a\u53ef\u89e3\u6551\u3002\u53ea\u662f\u5341\u4e8c\u4e2a\u65f6\u8fb0\u4e4b\u5185\uff0c\u4e0d\u5f97\u79fb\u52a8\u5979\u8eab\u5b50\u3002\u201d \u3000\u3000\u80e1\u6590\u9053\uff1a\u201c\u798f\u5eb7\u5b89\u7684\u536b\u58eb\u8f6c\u773c\u4fbf\u4f1a\u5bfb\u6765\uff0c\u4e0d\u80fd\u5728\u8fd9\u91cc\u7528\u9488\u3002 \u3000\u3000\u54b1\u4eec\u5f97\u53bb\u4e61\u4e0b\u627e\u4e2a\u8352\u50fb\u6240\u5728\u3002\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u90a3\u4fbf\u5f97\u8d76\u5feb\u52a8\u8eab\uff0c\u90a3\u4e24\u7c92\u836f\u4e38\u53ea\u80fd\u5ef6\u5f97\u5979\u4e00\u4e2a\u65f6\u8fb0\u7684\u6027\u547d\u3002\u201d\u8bf4\u7740\u53f9\u4e86\u53e3\u6c14\uff0c\u53c8\u9053\uff1a\u201c\u6211\u8fd9\u4f4d\u540c\u884c\u5bb0\u76f8\u592b\u4eba\u7684\u5fc3\u80a0\u867d\u6bd2\uff0c\u4e0b\u6bd2\u7684\u624b\u6bb5\u5374\u4f4e\u3002\u8fd9\u4e24\u6837\u6bd2\u836f\u6df7\u7528\uff0c\u53c8\u548c\u5728\u53c2\u6c64\u4e4b\u4e2d\uff0c\u6bd2\u6027\u53d1\u4f5c\u4fbf\u6162\u4e86\uff0c\u82e5\u662f\u5355\u7528\u4e00\u6837\uff0c\u9a6c\u59d1\u5a18\u8fd9\u65f6\u54ea\u91cc\u8fd8\u6709\u547d\u5728\uff1f\u201d\u80e1\u6590\u5306\u5306\u5fd9\u5fd9\u7684\u6536\u62fe\u7269\u4ef6\uff0c\u8bf4\u9053\uff1a\u201c\u5f53\u4eca\u4e4b\u4e16\uff0c\u8fd8\u6709\u8c01\u80fd\u80dc\u5f97\u8fc7\u54b1\u4eec\u836f\u738b\u59d1\u5a18\u7684\u795e\u6280\uff1f\u201d \u3000\u3000\u7a0b\u7075\u7d20\u5fae\u5fae\u4e00\u7b11\uff0c\u6b63\u8981\u56de\u7b54\uff0c\u5ffd\u542c\u5f97\u9a6c\u8e44\u58f0\u81ea\u8fdc\u800c\u8fd1\uff0c\u5954\u5230\u4e86\u5b85\u5916\u3002\u80e1\u6590\u62bd\u51fa\u5355\u5200\uff0c\u8bf4\u9053\uff1a\u201c\u8bf4\u4e0d\u5f97\uff0c\u53ea\u597d\u53ae\u6740\u4e00\u573a\u3002\u201d \u3000\u3000\u5fc3\u4e2d\u6697\u81ea\u7126\u6025\uff1a\u201c\u654c\u4eba\u5b9a\u7136\u6108\u6740\u6108\u591a\uff0c\u5371\u6025\u4e2d\u6211\u53ea\u80fd\u987e\u4e86\u4e8c\u59b9\uff0c\u53ef\u6551\u4e0d\u5f97\u9a6c\u59d1\u5a18\u3002\u201d \u3000\u3000\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u4eac\u5e08\u4e4b\u4e2d\uff0c\u53ea\u6015\u52a8\u4e0d\u5f97\u86ee\u3002\u5927\u54e5\uff0c\u4f60\u628a\u684c\u5b50\u6905\u5b50\u5806\u5f97\u9ad8\u9ad8\u7684\u642d\u4e00\u4e2a\u9ad8\u53f0\u3002\u201d\u80e1\u6590\u4e0d\u660e\u5176\u610f\uff0c\u4f46\u60f3\u5979\u667a\u8ba1\u591a\u7aef\uff0c\u8fd9\u65f6\u60c5\u52bf\u6025\u8feb\uff0c\u4e0d\u53ca\u7ec6\u95ee\uff0c\u4e8e\u662f\u4f9d\u8a00\u5c06\u684c\u5b50\u6905\u5b50\u90fd\u53e0\u4e86\u8d77\u6765\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u6307\u7740\u7a97\u5916\u90a3\u682a\u5927\u6811\u9053\uff1a\u201c\u4f60\u5e26\u9a6c\u59d1\u5a18\u4e0a\u6811\u53bb\u3002\u201d\u80e1\u6590\u8fd8\u5200\u5165\u9798\uff0c\u62b1\u7740\u9a6c\u6625\u82b1\uff0c\u8d70\u5230\u7a97\u6811\u4e0b\uff0c\u7eb5\u8eab\u8dc3\u4e0a\u6811\u5e72\uff0c\u5c06\u9a6c\u6625\u82b1\u85cf\u5728\u679d\u53f6\u63a9\u6620\u7684\u6697\u5904\u3002 \u3000\u3000\u4f46\u542c\u5f97\u811a\u6b65\u58f0\u54cd\uff0c\u6570\u540d\u536b\u58eb\u8d8a\u5899\u800c\u5165\uff0c\u6e10\u6e10\u8d70\u8fd1\uff0c\u53c8\u542c\u5f97\u90a3\u59d3\u5168\u7684\u7ba1\u5bb6\u51fa\u53bb\u67e5\u95ee\uff0c\u4f17\u536b\u58eb\u5389\u58f0\u547c\u53f1\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u5439\u7184\u70db\u706b\uff0c\u53e6\u884c\u53d6\u51fa\u4e00\u679a\u8721\u70db\uff0c\u70b9\u71c3\u4e86\u63d2\u5728\u70db\u53f0\u4e4b\u4e0a\uff0c\u5173\u4e0a\u4e86\u7a97\u5b50\uff0c\u8fd9\u624d\u5e26\u4e0a\u95e8\u8d70\u51fa\uff0c\u5728\u5730\u4e0b\u62fe\u4e86\u4e00\u5757\u77f3\u5757\uff0c\u8dc3\u4e0a\u6811\u5e72\uff0c\u5750\u5728\u80e1\u6590\u8eab\u65c1\u3002\u80e1\u6590\u4f4e\u58f0\u9053\uff1a\u201c\u5171\u6709\u5341\u4e03\u4e2a\uff01\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u836f\u529b\u591f\u7528\uff01\u201d \u3000\u3000\u53ea\u542c\u5f97\u4f17\u536b\u58eb\u56db\u4e0b\u641c\u67e5\uff0c\u5176\u4e2d\u6709\u4e00\u4eba\u7684\u53e3\u97f3\u6b63\u662f\u6bb7\u4ef2\u7fd4\u3002 \u3000\u3000\u4f17\u536b\u58eb\u5fcc\u60ee\u80e1\u6590\u4e86\u5f97\uff0c\u53c8\u9053\u8881\u7d2b\u8863\u4ecd\u5728\u5b85\u4e2d\uff0c\u4e0d\u6562\u5230\u5904\u4e71\u95ef\uff0c\u4e5f\u4e0d\u6562\u843d\u5355\uff0c\u4e09\u4e2a\u4e00\u7fa4\u3001\u56db\u4e2a\u4e00\u961f\u7684\u641c\u6765\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u5c06\u77f3\u5757\u9012\u7ed9\u80e1\u6590\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5c06\u684c\u6905\u6253\u4e0b\u6765\uff01\u201d\u80e1\u6590\u7b11\u9053\uff1a\u201c\u5999\u8ba1\uff01\u201d\u77f3\u5757\u98de\u5165\uff0c\u51fb\u5728\u4e2d\u95f4\u7684\u4e00\u5f20\u684c\u5b50\u4e0a\u3002\u90a3\u684c\u6905\u5806\u6210\u7684\u9ad8\u53f0\u767b\u65f6\u5012\u584c\uff0c\u7830\u562d\u4e4b\u58f0\uff0c\u54cd\u6210\u4e00\u7247\u3002 \u3000\u3000\u4f17\u536b\u58eb\u53eb\u9053\uff1a\u201c\u5728\u8fd9\u91cc\uff0c\u5728\u8fd9\u91cc\uff01\u201d\u5927\u4f19\u501a\u4ed7\u4eba\u591a\uff0c\u4e89\u5148\u6050\u540e\u7684\u4e00\u62e5\u5165\u5385\uff0c\u53ea\u89c1\u5385\u4e0a\u684c\u6905\u4e71\u6210\u4e00\u56e2\uff0c\u4fbf\u4f3c\u6709\u4eba\u66fe\u7ecf\u5728\u6b64\u6fc0\u70c8\u6597\u6bb4\uff0c\u4f46\u4e0d\u89c1\u534a\u4e2a\u4eba\u5f71\u3002\u4f17\u4eba\u6b63\u9519\u6115\u95f4\uff0c\u7a81\u7136\u5934\u8111\u6655\u7729\uff0c\u7acb\u8db3\u4e0d\u5b9a\uff0c\u4e00\u9f50\u6454\u5012\u3002\u80e1\u6590\u9053\uff1a\u201c\u4e03\u5fc3\u6d77\u68e0\uff0c\u53c8\u594f\u5947\u529f\uff01\u201d \u3000\u3000\u7a0b\u7075\u7d20\u6084\u6b65\u5165\u5385\uff0c\u5439\u706d\u70db\u706b\uff0c\u5c06\u8721\u70db\u6536\u5165\u6000\u4e2d\uff0c\u5411\u80e1\u6590\u62db\u624b\u9053\uff1a\u201c\u5feb\u8d70\u5427\uff01\u201d\u80e1\u6590\u8d1f\u8d77\u9a6c\u6625\u82b1\uff0c\u8d8a\u5899\u800c\u51fa\uff0c\u53ea\u8f6c\u51fa\u4e00\u4e2a\u80e1\u540c\uff0c\u4e0d\u7531\u5f97\u53eb\u4e00\u58f0\u82e6\uff0c\u4f46\u89c1\u524d\u9762\u8857\u5934\u706f\u7b3c\u706b\u628a\u7167\u8000\u5982\u540c\u767d\u663c\uff0c\u4e00\u961f\u5b98\u5175\u6b63\u5728\u5de1\u67e5\u3002 \u3000\u3000\u80e1\u6590\u5fd9\u6298\u5411\u5357\u884c\uff0c\u8d70\u4e0d\u5230\u534a\u91cc\uff0c\u53c8\u89c1\u4e00\u961f\u5b98\u5175\u8fce\u9762\u5de1\u6765\u3002 \u3000\u3000\u4ed6\u5fc3\u60f3\uff1a\u201c\u798f\u5927\u5e05\u5e9c\u6709\u523a\u5ba2\u4e4b\u4e8b\uff0c\u60f3\u5df2\u4f20\u904d\u4e5d\u57ce\uff0c\u8fd9\u65f6\u5230\u5904\u5de1\u67e5\u4e25\u5bc6\uff0c\u8981\u6df7\u5230\u90ca\u5916\u8352\u50fb\u7684\u5904\u6240\uff0c\u5012\u662f\u7740\u5b9e\u4e0d\u6613\u3002\u201d\u4f46\u542c\u5f97\u80cc\u540e\u4eba\u58f0\u55a7\u54d7\uff0c\u53c8\u662f\u4e00\u961f\u5b98\u5175\u5de1\u6765\u3002 \u3000\u3000\u80e1\u6590\u89c1\u524d\u540e\u6709\u654c\uff0c\u65e0\u5730\u53ef\u9000\uff0c\u5411\u7a0b\u7075\u7d20\u6253\u4e2a\u624b\u52bf\uff0c\u7eb5\u8eab\u8d8a\u5899\uff0c\u7ffb\u8fdb\u8eab\u65c1\u7684\u4e00\u6240\u5927\u5b85\u5b50\u3002\u7a0b\u7075\u7d20\u8ddf\u7740\u8df3\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u843d\u811a\u5904\u751a\u662f\u67d4\u8f6f\uff0c\u5374\u662f\u4e00\u7247\u8349\u5730\uff0c\u773c\u524d\u706f\u706b\u660e\u4eae\uff0c\u4eba\u5934\u6c79\u6d8c\u3002\u4e24\u4eba\u90fd\u5403\u4e86\u4e00\u60ca\uff1a\u201c\u6599\u4e0d\u5230\u8fd9\u91cc\u4e5f\u6709\u5b98\u5175\u3002\u201d\u542c\u5f97\u5899\u5916\u811a\u6b65\u58f0\u54cd\uff0c\u4e24\u961f\u5b98\u5175\u805a\u5728\u4e00\u8d77\uff0c\u5728\u52bf\u5df2\u4e0d\u80fd\u518d\u8dc3\u51fa\u5899\u53bb\uff0c\u53ea\u89c1\u5de6\u9996\u6709\u5ea7\u5047\u5c71\uff0c\u5047\u5c71\u524d\u82b1\u4e1b\u906e\u63a9\uff0c\u80e1\u6590\u8d1f\u7740\u9a6c\u6625\u82b1\u62a2\u4e86\u8fc7\u53bb\uff0c\u5f80\u5047\u5c71\u540e\u4e00\u8eb2\u3002 \u3000\u3000\u7a81\u7136\u95f4\u5047\u5c71\u540e\u4e00\u4eba\u957f\u8eab\u7ad9\u8d77\uff0c\u767d\u5149\u95ea\u52a8\uff0c\u4e00\u67c4\u5315\u9996\u5f53\u80f8\u624e\u5230\u3002 \u3000\u3000\u80e1\u6590\u4e07\u6599\u4e0d\u5230\u8fd9\u5047\u5c71\u540e\u9762\u7adf\u6709\u654c\u4eba\u57cb\u4f0f\uff0c\u5982\u6b64\u6084\u6ca1\u58f0\u7684\u731b\u65bd\u88ad\u51fb\uff0c\u4ed3\u5352\u4e4b\u95f4\u53ea\u5f97\u6454\u4e0b\u80cc\u4e0a\u7684\u9a6c\u6625\u82b1\uff0c\u4f38\u5de6\u624b\u5f80\u654c\u4eba\u8098\u5e95\u4e00\u6258\uff0c\u53f3\u624b\u4fbf\u5373\u9012\u62f3\u3002\u8fd9\u4eba\u624b\u811a\u7adf\u662f\u5341\u5206\u4e86\u5f97\uff0c\u56de\u8098\u659c\u907f\uff0c\u5315\u9996\u6a2a\u624e\uff0c\u5de6\u624b\u65bd\u51fa\u64d2\u62ff\u624b\u6cd5\uff0c\u53cd\u52fe\u80e1\u6590\u7684\u624b\u8155\uff0c\u5316\u89e3\u4e86\u4ed6\u8fd9\u4e00\u62f3\u3002\u6700\u5947\u7684\u662f\u4ed6\u8138\u4e0a\u8499\u4e86\u4e00\u5757\u9ec4\u5dfe\uff0c\u59cb\u7ec8\u4e00\u8a00\u4e0d\u53d1\u3002 \u3000\u3000\u80e1\u6590\u5fc3\u60f3\uff1a\u201c\u4f60\u4e0d\u51fa\u58f0\uff0c\u90a3\u662f\u6700\u5999\u4e0d\u8fc7\u3002\u201d\u8033\u542c\u5f97\u5b98\u5175\u4fbf\u5728\u5899\u5916\uff0c\u4ed6\u53ea\u987b\u5f20\u53e3\u4e00\u547c\uff0c\u90a3\u4fbf\u5927\u4e8b\u4e0d\u5999\u3002 \u3000\u3000\u4e24\u4e2a\u4eba\u8fd1\u8eab\u8089\u640f\uff0c\u5404\u65bd\u6740\u624b\u3002\u80e1\u6590\u77a7\u51fa\u4ed6\u7684\u6b66\u529f\u662f\u957f\u62f3\u4e00\u8def\uff0c\u51fa\u62db\u65e2\u72e0\u4e14\u731b\uff0c\u6b66\u529f\u9020\u8be3\u7adf\u4e0d\u5728\u79e6\u8010\u4e4b\u3001\u5468\u94c1\u9e6a\u4e00\u6d41\u4e4b\u4e0b\uff0c\u4f55\u51b5\u624b\u4e2d\u591a\u4e86\u5175\u5203\uff0c\u66f4\u5360\u4fbf\u5b9c\u3002\u76f4\u62c6\u5230\u7b2c\u4e5d\u62db\u4e0a\uff0c\u80e1\u6590\u624d\u6b3a\u8fdb\u4ed6\u6000\u4e2d\uff0c\u4f38\u6307\u70b9\u4e86\u4ed6\u80f8\u53e3\u7684\u201c\u9e20\u5c3e\u7a74\u201d\u3002\u90a3\u4eba\u6781\u662f\u608d\u52c7\uff0c\u867d\u7136\u7a74\u9053\u88ab\u70b9\uff0c\u4ecd\u98de\u53f3\u8db3\u6765\u8e22\uff0c\u80e1\u6590\u53c8\u4f38\u6307\u70b9\u4e86\u4ed6\u8db3\u80eb\u7684\u201c\u4e2d\u90fd\u7a74\u201d\uff0c\u8fd9\u624d\u6454\u5012\u5728\u5730\uff0c\u52a8\u5f39\u4e0d\u5f97\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u78b0\u4e86\u78b0\u80e1\u6590\u7684\u80a9\u5934\uff0c\u5411\u706f\u5149\u5904\u4e00\u6307\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u50cf\u662f\u5728\u505a\u620f\u3002\u201d\u80e1\u6590\u62ac\u5934\u770b\u53bb\uff0c\u4f46\u89c1\u7a7a\u65f7\u5904\u642d\u4e86\u8001\u5927\u4e00\u4e2a\u620f\u53f0\uff0c\u53f0\u4e0b\u4e00\u6392\u6392\u7684\u5750\u6ee1\u4e86\u4eba\uff0c\u706f\u5149\u8f89\u714c\uff0c\u53f0\u4e0a\u7684\u620f\u5b50\u5374\u5c1a\u672a\u51fa\u573a\u3002 \u3000\u3000\u5176\u65f6\u6b63\u5f53\u4e7e\u9686\u9f0e\u76db\u4e4b\u4e16\uff0c\u5317\u4eac\u57ce\u4e2d\u5b98\u5ba6\u4eba\u5bb6\u6709\u751a\u4e48\u559c\u5e86\u5bb4\u4f1a\uff0c\u5f80\u5f80\u63a5\u8fde\u5531\u620f\u6570\u65e5\uff0c\u901a\u5bb5\u8fbe\u65e6\uff0c\u4ea6\u975e\u5f02\u4e8b\u3002 \u3000\u3000\u80e1\u6590\u5401\u4e86\u53e3\u6c14\uff0c\u62c9\u4e0b\u90a3\u6c49\u5b50\u8138\u4e0a\u8499\u7740\u7684\u9ec4\u5dfe\uff0c\u9690\u7ea6\u53ef\u89c1\u4ed6\u9762\u76ee\u7c97\u8c6a\uff0c\u56db\u5341\u6765\u5c81\u5e74\u7eaa\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u8fd9\u6c49\u5b50\u60f3\u662f\u4e58\u7740\u4eba\u5bb6\u6709\u559c\u4e8b\uff0c\u62bd\u7a7a\u5b50\u5077\u9e21\u6478\u72d7\u6765\u7740\uff0c\u6240\u4ee5\u4e00\u58f0\u4e5f\u4e0d\u6562\u51fa\u3002\u201d\u7a0b\u7075\u7d20\u70b9\u4e86\u70b9\u5934\uff0c\u6084\u58f0\u9053\uff1a\u201c\u53ea\u6015\u4e0d\u662f\u5c0f\u8d3c\u3002\u201d\u80e1\u6590\u5fae\u7b11\u9053\uff1a\u201c\u4eac\u5e08\u4e4b\u4e2d\uff0c\u8fde\u5c0f\u8d3c\u4e5f\u8fd9\u822c\u4e86\u5f97\u3002\u201d\u5fc3\u4e2d\u6697\u81ea\u5600\u5495\uff1a\u201c\u77a7\u8fd9\u4eba\u8eab\u624b\uff0c\u51b3\u975e\u5bfb\u5e38\u7684\u9f20\u7a83\u72d7\u76d7\uff0c\u82e5\u4e0d\u662f\u5b58\u5fc3\u505a\u4e00\u4ef6\u5927\u6848\uff0c\u4fbf\u662f\u6765\u5bfb\u4ec7\u6740\u4eba\uff0c\u4e5f\u662f\u4ed6\u5408\u8be5\u5012\u9709\uff0c\u5374\u7ed9\u6211\u65e0\u610f\u4e4b\u95f4\u64d2\u4f4f\u4e86\u3002\u201d\u7a0b\u7075\u7d20\u4f4e\u58f0\u9053\uff1a\u201c\u54b1\u4eec\u4e0d\u5982\u4fbf\u5728\u8fd9\u5927\u6237\u4eba\u5bb6\u5bfb\u4e00\u5904\u7a7a\u50fb\u67f4\u623f\u6216\u662f\u9601\u697c\uff0c\u8eb2\u4ed6\u5341\u4e8c\u4e2a\u65f6\u8fb0\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u6211\u770b\u4e5f\u53ea\u6709\u5982\u6b64\u3002\u5916\u8fb9\u67e5\u5f97\u8fd9\u822c\u7d27\uff0c\u5982\u4f55\u80fd\u591f\u51fa\u53bb\uff1f\u201d \u3000\u3000\u4fbf\u5728\u6b64\u65f6\uff0c\u620f\u53f0\u4e0a\u95e8\u5e18\u4e00\u6380\uff0c\u8d70\u51fa\u4e00\u4e2a\u4eba\u6765\u3002\u90a3\u4eba\u7a7f\u7740\u5bfb\u5e38\u7684\u845b\u7eb1\u5927\u8902\uff0c\u4e5f\u6ca1\u52fe\u8138\uff0c\u8d70\u5230\u53f0\u53e3\u4e00\u7ad9\uff0c\u62b1\u62f3\u65bd\u793c\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u5404\u4f4d\u5e08\u4f2f\u5e08\u53d4\u3001\u5e08\u5144\u5f1f\u59ca\u59b9\u8bf7\u4e86\uff01\u201d\u80e1\u6590\u542c\u4ed6\u8bf4\u8bdd\u58f0\u97f3\u6d2a\u4eae\uff0c\u77a7\u8fd9\u795e\u60c5\uff0c\u4f3c\u4e4e\u4e0d\u662f\u5531\u620f\u3002\u53c8\u542c\u4ed6\u9053\uff1a\u201c\u6b64\u523b\u5929\u5c06\u9ece\u660e\uff0c\u8f6c\u773c\u53c8\u662f\u4e00\u65e5\uff0c\u518d\u8fc7\u4e09\u5929\uff0c\u4fbf\u662f\u5929\u4e0b\u638c\u95e8\u4eba\u5927\u4f1a\u7684\u4f1a\u671f\u3002\u53ef\u662f\u54b1\u4eec\u897f\u5cb3\u534e\u62f3\u95e8\uff0c\u76f4\u5230\u6b64\u523b\uff0c\u8fd8\u662f\u6ca1\u63a8\u51fa\u638c\u95e8\u4eba\u6765\u3002 \u3000\u3000\u8fd9\u4e00\u4ef6\u4e8b\u53ef\u5b9e\u5728\u4e0d\u80fd\u518d\u62d6\u3002\u5982\u4f55\u529e\u7406\uff0c\u8bf7\u5404\u652f\u6d3e\u7684\u524d\u8f88\u4eec\u793a\u4e0b\u3002\u201d \u3000\u3000\u53f0\u4e0b\u4eba\u4e1b\u4e2d\u7ad9\u8d77\u4e00\u4e2a\u8eab\u7a7f\u9ed1\u8272\u9a6c\u8902\u7684\u8001\u8005\uff0c\u54b3\u55fd\u4e86\u51e0\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u534e\u62f3\u56db\u5341\u516b\uff0c\u827a\u6210\u884c\u5929\u6daf\u3002\u54b1\u4eec\u897f\u5cb3\u534e\u62f3\u95e8\u4e09\u767e\u5e74\u6765\uff0c\u4e00\u76f4\u5206\u4e3a\u827a\u5b57\u3001\u6210\u5b57\u3001\u884c\u5b57\u3001\u5929\u5b57\u3001\u6daf\u5b57\u4e94\u4e2a\u652f\u6d3e\uff0c\u5df2\u6709\u4e09\u767e\u5e74\u6ca1\u603b\u638c\u95e8\u4e86\u3002\u867d\u8bf4\u4e94\u6d3e\u90fd\u662f\u597d\u751f\u5174\u65fa\uff0c\u4f46\u5e08\u5144\u5f1f\u4eec\u603b\u662f\u5404\u5b58\u95e8\u6237\u4e4b\u89c1\uff0c\u4eba\u4eba\u90fd\u8bf4\uff1a\u2018\u6211\u662f\u827a\u5b57\u6d3e\u7684\uff0c\u6211\u662f\u6210\u5b57\u6d3e\u7684\u3002\u2019\u4ece\u4e0d\u8bf4\u6211\u662f\u897f\u5cb3\u534e\u62f3\u95e8\u7684\u3002\u6ca1\u60f3\u5230\u522b\u6d3e\u7684\u6b66\u5e08\u4eec\uff0c\u5374\u4ece\u4e0d\u7406\u4f1a\u4f60\u662f\u827a\u5b57\u6d3e\u8fd8\u662f\u6210\u5b57\u6d3e\uff0c\u603b\u5f53\u54b1\u4eec\u662f\u897f\u5cb3\u534e\u62f3\u95e8\u7684\u95e8\u4e0b\u3002\u54b1\u4eec\u8fd9\u4e00\u95e8\u4eba\u6570\u4f17\u591a\uff0c\u6253\u4ece\u8001\u7956\u5b97\u624b\u4e0a\u4f20\u4e0b\u6765\u7684\u73a9\u827a\u513f\u4e5f\u771f\u4e0d\u542b\u7cca\uff0c\u53ef\u662f\u5e72\u4e48\u8fdc\u8fdc\u4e0d\u53ca\u5c11\u6797\u3001\u6b66\u5f53\u3001\u592a\u6781\u3001\u516b\u5366\u8fd9\u4e9b\u95e8\u6d3e\u540d\u58f0\u54cd\u4eae\u5462\uff1f\u8fd8\u4e0d\u662f\u56e0\u4e3a\u54b1\u4eec\u5206\u6210\u4e86\u4e94\u4e2a\u652f\u6d3e\uff0c\u529b\u5206\u5219\u5f31\uff0c\u90a3\u6709\u4ec0\u4e48\u8bf4\u7684\u3002\u201d \u3000\u3000\u90a3\u8001\u8005\u6ee1\u53e3\u90fd\u662f\u9655\u5317\u7684\u571f\u8154\uff0c\u8bf4\u5230\u8fd9\u91cc\uff0c\u54b3\u55fd\u51e0\u58f0\uff0c\u53f9\u4e86\u4e00\u53e3\u957f\u6c14\uff0c\u53c8\u9053\uff1a\u201c\u82e5\u4e0d\u662f\u798f\u5927\u5e05\u53ec\u5f00\u8fd9\u4e2a\u5929\u4e0b\u638c\u95e8\u4eba\u5927\u4f1a\uff0c\u54b1\u4eec\u897f\u5cb3\u534e\u62f3\u95e8\u4e0d\u77e5\u8981\u5230\u54ea\u4e00\u5e74\u54ea\u4e00\u6708\uff0c\u624d\u6709\u638c\u95e8\u4eba\u51fa\u6765\u5462\u3002 \u3000\u3000\u5e78\u597d\u6709\u8fd9\u4ef6\u76db\u4e3e\uff0c\u603b\u7b97\u628a\u8fd9\u4f4d\u638c\u95e8\u4eba\u7ed9\u903c\u51fa\u6765\u4e86\u3002\u6211\u8001\u673d\u4eca\u65e5\u8981\u8bf4\u4e00\u53e5\u8bdd\uff1a\u54b1\u4eec\u63a8\u4e3e\u8fd9\u4f4d\u638c\u95e8\u4eba\uff0c\u4e0d\u5355\u662f\u8981\u4ed6\u5230\u5927\u4f1a\u4e4b\u4e2d\u7ed9\u897f\u5cb3\u534e\u62f3\u95e8\u4e89\u5149\uff0c\u8fd8\u8981\u4ed6\u5c06\u672c\u95e8\u597d\u597d\u6574\u987f\u4e00\u756a\u3002\u4ece\u6b64\u4e94\u652f\u5f52\u5b97\uff0c\u5927\u4f19\u513f\u9f50\u5fc3\u5408\u529b\uff0c\u4f7f\u5f97\u534e\u62f3\u95e8\u5728\u6b66\u6797\u4e2d\u6296\u4e00\u6296\u5a01\u98ce\uff0c\u5410\u4e00\u5410\u8c6a\u6c14\u3002\u201d\u53f0\u4e0b\u4f17\u4eba\u9f50\u58f0\u559d\u5f69\uff0c\u66f4\u6709\u8bb8\u591a\u4eba\u5288\u5288\u62cd\u62cd\u7684\u9f13\u8d77\u638c\u6765\u3002 \u3000\u3000\u80e1\u6590\u5fc3\u60f3\uff1a\u201c\u539f\u6765\u662f\u897f\u5cb3\u534e\u62f3\u95e8\u5728\u8fd9\u91cc\u805a\u4f1a\u3002\u201d\u4ed6\u5f20\u76ee\u56db\u671b\uff0c\u60f3\u8981\u627e\u4e2a\u9690\u50fb\u7684\u6240\u5728\uff0c\u4f46\u5404\u5904\u901a\u9053\u5747\u5728\u706f\u706b\u7167\u8000\u4e4b\u4e0b\uff0c\u56ed\u4e2d\u805a\u7740\u7684\u603b\u6709\u4e8c\u767e\u6765\u4eba\uff0c\u53ea\u8981\u4e00\u51fa\u53bb\uff0c\u5b9a\u4f1a\u7ed9\u4eba\u53d1\u89c1\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u53ea\u76fc\u4ed6\u4eec\u5feb\u4e9b\u4e3e\u4e86\u638c\u95e8\u4eba\u51fa\u6765\uff0c\u897f\u5cb3\u534e\u62f3\u4e5f\u597d\uff0c\u4e1c\u5cb3\u6cf0\u62f3\u4e5f\u597d\uff0c\u8d8a\u65e9\u6563\u573a\u8d8a\u597d\u3002\u201d \u3000\u3000\u53ea\u542c\u5f97\u53f0\u4e0a\u90a3\u4eba\u8bf4\u9053\uff1a\u201c\u8521\u5e08\u4f2f\u7684\u8bdd\uff0c\u53e5\u53e5\u662f\u91d1\u77f3\u826f\u8a00\u3002 \u3000\u3000\u665a\u8f88\u5fdd\u4e3a\u827a\u5b57\u6d3e\u4e4b\u957f\uff0c\u80c6\u6562\u4ee3\u672c\u6d3e\u7684\u5168\u4f53\u5e08\u5144\u5f1f\u4eec\u8bf4\u4e00\u53e5\uff0c\u5f85\u4f1a\u63a8\u4e3e\u4e86\u638c\u95e8\u4eba\u51fa\u6765\uff0c\u6211\u4eec\u827a\u5b57\u6d3e\u5168\u5fc3\u5168\u610f\u542c\u4ece\u638c\u95e8\u4eba\u7684\u8a00\u8bed\u3002\u4ed6\u8001\u4eba\u5bb6\u8bf4\u4ec0\u4e48\u4fbf\u662f\u4ec0\u4e48\uff0c\u827a\u5b57\u6d3e\u51b3\u65e0\u4e00\u53e5\u5f02\u8a00\u3002\u201d\u53f0\u4e0b\u4e00\u4eba\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u597d\uff01\u201d\u58f0\u97f3\u62d6\u5f97\u957f\u957f\u7684\uff0c\u4fbf\u5982\u53f0\u4e0a\u7684\u4eba\u5531\u4e86\u4e00\u53e5\u597d\u620f\uff0c\u53f0\u4e0b\u770b\u5ba2\u53eb\u597d\u4e00\u822c\uff0c\u5176\u4e2d\u8ba5\u5632\u4e4b\u610f\uff0c\u5374\u4e5f\u751a\u662f\u660e\u663e\u3002 \u3000\u3000\u53f0\u4e0a\u90a3\u4eba\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5176\u4f59\u5404\u6d3e\u600e\u4e48\u8bf4\uff1f\u201d\u53ea\u89c1\u53f0\u4e0b\u4e00\u4e2a\u4e2a\u4eba\u7ad9\u8d77\uff0c\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u6210\u5b57\u6d3e\u51b3\u4e0d\u6562\u8fdd\u80cc\u638c\u95e8\u4eba\u7684\u8bdd\u3002\u201d\u201c\u4ed6\u8001\u4eba\u5bb6\u5429\u5490\u4ec0\u4e48\uff0c\u54b1\u4eec\u884c\u5b57\u6d3e\u4e00\u5b9a\u7167\u529e\u3002\u201d\u201c\u5929\u5b57\u6d3e\u9075\u4ece\u53f7\u4ee4\uff0c\u4e0d\u6562\u6709\u8fdd\u3002\u201d\u201c\u6daf\u5b57\u6d3e\u662f\u5c0f\u5f1f\u5f1f\uff0c\u5927\u54e5\u54e5\u4eec\u5e26\u5934\u5e72\uff0c\u5c0f\u5f1f\u5f1f\u51b3\u4e0d\u80fd\u6709\u7b2c\u4e8c\u53e5\u8bdd\u3002\u201d \u3000\u3000\u53f0\u4e0a\u90a3\u4eba\u9053\uff1a\u201c\u597d\uff01\u5404\u652f\u6d3e\u9f50\u5fc3\u4e00\u81f4\uff0c\u90a3\u771f\u662f\u518d\u597d\u4e5f\u6ca1\u6709\u4e86\u3002\u773c\u4e0b\u5404\u652f\u6d3e\u7684\u652f\u957f\uff0c\u5404\u4f4d\u524d\u8f88\u5e08\u4f2f\u5e08\u53d4\uff0c\u90fd\u5df2\u5230\u9f50\uff0c\u53ea\u6709\u5929\u5b57\u6d3e\u59ec\u5e08\u4f2f\u6ca1\u6765\u3002\u4ed6\u8001\u4eba\u5bb6\u634e\u4e86\u4fe1\u6765\uff0c\u8bf4\u6d3e\u4ed6\u4ee4\u90ce\u59ec\u5e08\u5144\u8d74\u4f1a\u3002\u4f46\u7b49\u5230\u6b64\u523b\uff0c\u59ec\u5e08\u5144\u8fd8\u662f\u6ca1\u5230\u3002\u8fd9\u4f4d\u5e08\u5144\u884c\u4e8b\u7d20\u6765\u795e\u51fa\u9b3c\u6ca1\uff0c\u8bf4\u4e0d\u5b9a\u8fd9\u5f53\u513f\u65e9\u5df2\u5230\u4e86\uff0c\u4e5f\u4e0d\u77e5\u8eb2\u5728\u4ec0\u4e48\u5730\u65b9\u2026\u2026\u201d\u8bf4\u5230\u8fd9\u91cc\uff0c\u53f0\u4e0a\u53f0\u4e0b\u4e00\u9f50\u7b11\u4e86\u8d77\u6765\u3002 \u3000\u3000\u80e1\u6590\u4fef\u5230\u90a3\u6c49\u5b50\u8033\u8fb9\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u4f60\u59d3\u59ec\uff0c\u662f\u4e0d\u662f\uff1f\u201d\u90a3\u6c49\u5b50\u70b9\u4e86\u70b9\u5934\uff0c\u773c\u4e2d\u5145\u6ee1\u4e86\u8ff7\u60d8\u4e4b\u8272\uff0c\u5b9e\u4e0d\u77e5\u8fd9\u4e00\u7537\u4e8c\u5973\u662f\u4ec0\u4e48\u8def\u9053\u3002 \u3000\u3000\u53f0\u4e0a\u90a3\u4eba\u8bf4\u9053\uff1a\u201c\u59ec\u5e08\u5144\u4e00\u4eba\u6ca1\u5230\uff0c\u54b1\u4eec\u8db3\u8db3\u7b49\u4e86\u4ed6\u4e00\u5929\u534a\u591c\uff0c\u603b\u4e5f\u5bf9\u5f97\u4f4f\u4e86\uff0c\u65e5\u540e\u59ec\u5e08\u4f2f\u4e5f\u4e0d\u80fd\u602a\u8d23\u54b1\u4eec\u3002\u73b0\u4e0b\u8981\u8bf7\u5404\u4f4d\u524d\u8f88\u5e08\u4f2f\u5e08\u53d4\u4eec\u6307\u70b9\uff0c\u672c\u95e8\u8fd9\u4f4d\u638c\u95e8\u4eba\u662f\u5982\u4f55\u63a8\u6cd5\u3002\u201d \u3000\u3000\u4f17\u4eba\u7b49\u4e86\u4e00\u665a\uff0c\u4e3a\u7684\u4fbf\u662f\u8981\u77a7\u8fd9\u4e00\u51fa\u63a8\u4e3e\u638c\u95e8\u4eba\u7684\u597d\u620f\uff0c\u542c\u5230\u8fd9\u91cc\uff0c\u90fd\u662f\u5174\u9ad8\u91c7\u70c8\uff0c\u53f0\u4e0b\u5404\u4eba\u4e5f\u4e0d\u4f9d\u6b21\u5e8f\uff0c\u7eb7\u7eb7\u53eb\u56b7\uff1a \u3000\u3000\u201c\u51ed\u529f\u592b\u6bd4\u8bd5\u554a\uff01\u201d\u201c\u8c01\u4e5f\u4e0d\u670d\u8c01\uff0c\u4e0d\u51ed\u62f3\u811a\u5668\u68b0\uff0c\u90a3\u51ed\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u771f\u5200\u771f\u811a\uff0c\u6253\u5f97\u4eba\u4eba\u5fc3\u670d\uff0c\u81ea\u7136\u662f\u638c\u95e8\u4eba\u4e86\u3002\u201d \u3000\u3000\u90a3\u59d3\u8521\u7684\u8001\u8005\u7ad9\u8d77\u8eab\u6765\uff0c\u54b3\u55fd\u4e00\u58f0\uff0c\u6717\u58f0\u9053\uff1a\u201c\u672c\u6765\u561b\uff0c\u638c\u95e8\u4eba\u51ed\u5fb7\u4e0d\u51ed\u529b\uff0c\u540e\u751f\u5c0f\u5b50\u73a9\u827a\u513f\u518d\u9ad8\u660e\uff0c\u4e5f\u4e0d\u80fd\u8d8a\u8fc7\u5fb7\u9ad8\u671b\u91cd\u7684\u524d\u8f88\u53bb\u3002\u201d\u4ed6\u987f\u4e86\u4e00\u987f\uff0c\u773c\u5149\u5411\u4f17\u4eba\u4e00\u626b\uff0c\u53c8\u9053\uff1a\u201c\u53ef\u662f\u8fd9\u4e00\u6b21\u60c5\u5f62\u4e0d\u540c\u5566\u3002\u5728\u5929\u4e0b\u638c\u95e8\u4eba\u5927\u4f1a\u4e4b\u4e2d\uff0c\u65e2\u662f\u82f1\u96c4\u805a\u4f1a\uff0c\u81ea\u7136\u8981\u5404\u663e\u795e\u901a\u3002\u54b1\u4eec\u897f\u5cb3\u534e\u62f3\u95e8\u5018\u662f\u4e3e\u4e86\u4e2a\u7cdf\u8001\u5934\u513f\u51fa\u53bb\uff0c\u4eba\u5bb6\u80fd\u4e0d\u80fd\u559d\u4e00\u53e5\u5f69\uff0c\u8d5e\u4e00\u53e5\uff1a\u2018\u597d\uff0c\u534e\u62f3\u95e8\u7684\u7cdf\u8001\u5934\u513f\u5fb7\u9ad8\u671b\u91cd\uff0c\u8001\u800c\u4e0d\u6b7b\u2019\uff1f\u201d\u4f17\u4eba\u542c\u5f97\u54c8\u54c8\u5927\u7b11\u3002\u7a0b\u7075\u7d20\u4e5f\u7981\u4e0d\u4f4f\u62bf\u4f4f\u4e86\u5634\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u7cdf\u8001\u5934\u513f\u5012\u4f1a\u8bf4\u7b11\u8bdd\u3002\u201d \u3000\u3000\u90a3\u59d3\u8521\u7684\u8001\u8005\u5927\u58f0\u9053\uff1a\u201c\u534e\u62f3\u56db\u5341\u516b\uff0c\u827a\u6210\u884c\u5929\u6daf\u3002\u53ef\u662f\u51e0\u767e\u5e74\u6765\uff0c\u534e\u62f3\u95e8\u8fd9\u56db\u5341\u516b\u8def\u62f3\u811a\u5668\u68b0\uff0c\u6ca1\u4e00\u4e2a\u4eba\u80fd\u8bf4\u5f97\u4e0a\u8def\u8def\u7cbe\u901a\u3002\u4eca\u65e5\u4e4b\u4e8b\uff0c\u54ea\u4e00\u4f4d\u73a9\u827a\u513f\u6700\u9ad8\uff0c\u90a3\u4e00\u4f4d\u4fbf\u6267\u638c\u672c\u95e8\u3002\u201d\u4f17\u4eba\u521a\u559d\u5f97\u4e00\u58f0\u5f69\uff0c\u5ffd\u7136\u540e\u95e8\u4e0a\u64c2\u9f13\u822c\u7684\u6572\u8d77\u95e8\u6765\u3002 \u3000\u3000\u4f17\u4eba\u4e00\u6115\uff0c\u6709\u4eba\u8bf4\u9053\uff1a\u201c\u662f\u59ec\u5e08\u5144\u5230\u4e86\uff01\u201d\u6709\u4eba\u4fbf\u53bb\u5f00\u95e8\u3002 \u3000\u3000\u706f\u7b3c\u706b\u628a\u7167\u8000\uff0c\u62e5\u8fdb\u6765\u4e00\u961f\u5b98\u5175\u3002 \u3000\u3000\u80e1\u6590\u53f3\u624b\u6309\u5b9a\u5200\u67c4\uff0c\u5de6\u624b\u63e1\u4f4f\u4e86\u7a0b\u7075\u7d20\u7684\u624b\uff0c\u4e24\u4eba\u76f8\u89c6\u4e00\u7b11\uff0c\u867d\u662f\u5371\u673a\u5f53\u524d\uff0c\u4e24\u4eba\u53cd\u800c\u66f4\u52a0\u5fc3\u610f\u76f8\u901a\u3002 \u3000\u3000\u4f46\u5f53\u76f8\u4e92\u518d\u671b\u4e00\u773c\u65f6\uff0c\u7a0b\u7075\u7d20\u5374\u9eef\u7136\u4f4e\u4e0b\u4e86\u5934\u53bb\uff0c\u539f\u6765\u5979\u8fd9\u65f6\u5ffd\u7136\u60f3\u5230\u4e86\u8881\u7d2b\u8863\uff1a\u201c\u6211\u548c\u5927\u54e5\u4e00\u540c\u6b7b\u5728\u8fd9\u91cc\uff0c\u4e0d\u77e5\u8881\u59d1\u5a18\u4fbf\u4f1a\u600e\u6837\uff1f\u201d\u5979\u5fc3\u77e5\u80e1\u6590\u8fd9\u65f6\u4e5f\u4e00\u5b9a\u60f3\u5230\u4e86\u8881\u7d2b\u8863\uff1a\u201c\u6211\u548c\u4e8c\u59b9\u4e00\u540c\u6b7b\u5728\u8fd9\u91cc\uff0c\u4e0d\u77e5\u8881\u59d1\u5a18\u4fbf\u4f1a\u600e\u6837\uff1f\u201d \u3000\u3000\u9886\u961f\u7684\u6b66\u5b98\u8d70\u5230\u4eba\u4e1b\u4e4b\u4e2d\uff0c\u67e5\u95ee\u4e86\u51e0\u53e5\uff0c\u542c\u8bf4\u662f\u897f\u5cb3\u534e\u62f3\u95e8\u5728\u6b64\u63a8\u4e3e\u638c\u95e8\u4eba\uff0c\u90a3\u6b66\u5b98\u7684\u795e\u6001\u767b\u65f6\u53d8\u5f97\u5341\u5206\u5ba2\u6c14\uff0c\u4f46\u8fd8\u662f\u63d0\u7740\u706f\u7b3c\uff0c\u5230\u5404\u4eba\u8138\u4e0a\u7167\u770b\u4e00\u904d\uff0c\u53c8\u5728\u56ed\u5b50\u524d\u540e\u5de6\u53f3\u5de1\u67e5\u3002 \u3000\u3000\u80e1\u6590\u548c\u7a0b\u7075\u7d20\u7f29\u5728\u5047\u5c71\u4e4b\u4e2d\uff0c\u773c\u89c1\u90a3\u706f\u7b3c\u6e10\u6e10\u7167\u8fd1\uff0c\u5fc3\u60f3\uff1a\u201c\u4e0d\u77e5\u8fd9\u6b66\u5b98\u7684\u8fd0\u6c14\u5982\u4f55\uff1f\u82e5\u662f\u4ed6\u5c06\u706f\u7b3c\u5230\u5047\u5c71\u4e2d\u6765\u4e00\u7167\uff0c\u8bf4\u4e0d\u5f97\uff0c\u53ea\u597d\u8bf7\u4ed6\u5f53\u5934\u5403\u4e0a\u4e00\u5200\u3002\u201d \u3000\u3000\u5ffd\u542c\u5f97\u53f0\u4e0a\u90a3\u4eba\u8bf4\u9053\uff1a\u201c\u54ea\u4e00\u4f4d\u6b66\u529f\u6700\u9ad8\uff0c\u54ea\u4e00\u4f4d\u4fbf\u6267\u638c\u672c\u95e8\u3002\u8fd9\u53e5\u8bdd\u8c01\u90fd\u542c\u89c1\u4e86\u3002\u4f17\u4f4d\u5e08\u4f2f\u5e08\u53d4\u3001\u5e08\u5144\u59ca\u59b9\uff0c\u4fbf\u8bf7\u4e00\u4e00\u4e0a\u53f0\u6765\u663e\u663e\u7edd\u827a\u3002\u201d\u4ed6\u8fd9\u53e5\u8bdd\u521a\u8bf4\u5b8c\uff0c\u4f17\u4eba\u773c\u524d\u4e00\u4eae\uff0c\u4fbf\u6709\u4e00\u4e2a\u8eab\u7a7f\u6de1\u7ea2\u886b\u5b50\u7684\u5c11\u5987\u8df3\u5230\u53f0\u4e0a\uff0c\u8bf4\u9053\uff1a\u201c\u884c\u5b57\u6d3e\u5f1f\u5b50\u9ad8\u4e91\uff0c\u5411\u5404\u4f4d\u524d\u8f88\u5e08\u4f2f\u5e08\u5144\u4eec\u8ba8\u6559\u3002\u201d\u4f17\u4eba\u89c1\u5979\u9732\u7684\u8fd9\u4e00\u624b\u8f7b\u529f\u59ff\u5f0f\u7f8e\u5999\uff0c\u517c\u4e4b\u8863\u886b\u7fe9\u7fe9\uff0c\u76f8\u8c8c\u53c8\u597d\uff0c\u4e0d\u7981\u90fd\u559d\u4e86\u4e00\u58f0\u5f69\u3002\u90a3\u6b66\u5b98\u77a7\u5f97\u5446\u4e86\uff0c\u54ea\u91cc\u8fd8\u60f3\u5230\u53bb\u641c\u67e5\u523a\u5ba2\uff1f \u3000\u3000\u53f0\u4e0b\u8ddf\u7740\u4fbf\u6709\u4e00\u4e2a\u5c11\u5e74\u8df3\u4e0a\uff0c\u8bf4\u9053\uff1a\u201c\u827a\u5b57\u6d3e\u5f1f\u5b50\u5f20\u590d\u9f99\uff0c\u8bf7\u9ad8\u5e08\u59ca\u6307\u6559\u3002\u201d\u9ad8\u4e91\u9053\uff1a\u201c\u5f20\u5e08\u5144\u4e0d\u5fc5\u5ba2\u6c14\u3002\u201d\u53f3\u817f\u534a\u8e72\uff0c\u5de6\u817f\u524d\u4f38\uff0c\u53f3\u624b\u6a2a\u638c\uff0c\u5de6\u624b\u53cd\u94a9\uff0c\u6b63\u662f\u534e\u62f3\u4e2d\u51fa\u624b\u7b2c\u4e00\u62db\u201c\u51fa\u52bf\u8de8\u864e\u897f\u5cb3\u4f20\u201d\u3002\u5f20\u590d\u9f99\u63d0\u819d\u56de\u73af\u4eae\u638c\uff0c\u5e94\u4ee5\u4e00\u62db\u201c\u5546\u7f8a\u767b\u679d\u811a\u72ec\u60ac\u201d\u3002\u4e24\u4eba\u5404\u51fa\u672c\u95e8\u62f3\u62db\uff0c\u6597\u4e86\u8d77\u6765\u3002\u4e8c\u5341\u4f59\u5408\u540e\uff0c\u9ad8\u4e91\u4f7f\u62db\u201c\u56de\u5934\u671b\u6708\u51e4\u5c55\u7fc5\u201d\uff0c\u6251\u6b65\u4eae\u638c\uff0c\u4e00\u638c\u5c06\u5f20\u590d\u9f99\u51fb\u4e0b\u53f0\u53bb\u3002 \u3000\u3000\u90a3\u6b66\u5b98\u5927\u58f0\u53eb\u597d\uff0c\u8fde\u8bf4\uff1a\u201c\u4e86\u4e0d\u8d77\uff0c\u4e86\u4e0d\u8d77\uff01\u201d\u53ea\u89c1\u53f0\u4e0b\u53c8\u6709\u4e00\u540d\u58ee\u6c49\u8dc3\u4e0a\uff0c\u8bf4\u4e86\u51e0\u53e5\u5ba2\u6c14\u8bdd\uff0c\u4fbf\u548c\u9ad8\u4e91\u52a8\u624b\u3002\u8fd9\u4e00\u6b21\u5374\u662f\u9ad8\u4e91\u4e00\u4e2a\u5931\u8db3\uff0c\u7ed9\u90a3\u58ee\u6c49\u63a8\u5f97\u6454\u4e2a\u7b4b\u6597\u3002\u90a3\u6b66\u5b98\u8bf4\u9053\uff1a\u201c\u53ef\u60dc\uff0c\u53ef\u60dc\uff01\u201d\u6ca1\u5174\u81f4\u518d\u77a7\uff0c\u7387\u9886\u4f17\u5b98\u5175\u51fa\u95e8\u53c8\u641c\u67e5\u53bb\u4e86\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u89c1\u5b98\u5175\u51fa\u95e8\uff0c\u677e\u4e86\u53e3\u6c14\uff0c\u4f46\u89c1\u620f\u53f0\u4e0a\u4e00\u4e2a\u4e0a\uff0c\u4e00\u4e2a\u4e0b\uff0c\u6597\u4e4b\u4e0d\u5df2\uff0c\u4e0d\u77e5\u95f9\u5230\u4ec0\u4e48\u65f6\u5019\uff0c\u624d\u9009\u5f97\u638c\u95e8\u4eba\u51fa\u6765\u3002\u770b\u80e1\u6590\u65f6\uff0c\u5374\u89c1\u4ed6\u5168\u795e\u8d2f\u6ce8\u7684\u51dd\u671b\u53f0\u4e0a\u4e24\u4eba\u76f8\u6597\uff0c\u7a0b\u7075\u7d20\u5fc3\u60f3\uff1a \u3000\u3000\u201c\u8fd9\u4e24\u4eba\u7684\u62f3\u811a\u6253\u5f97\u867d\u72e0\uff0c\u4e5f\u4e0d\u89c1\u5f97\u6709\u591a\u9ad8\u660e\uff0c\u5927\u54e5\u4e3a\u4ec0\u4e48\u77a7\u5f97\u8fd9\u4e48\u51fa\u795e\uff1f\u201d\u4f4e\u58f0\u9053\uff1a\u201c\u5927\u54e5\uff0c\u8fc7\u4e86\u5927\u534a\u4e2a\u65f6\u8fb0\u5566\uff0c\u5f97\u8d76\u5feb\u60f3\u4e2a\u6cd5\u513f\u624d\u597d\u3002\u518d\u4e0d\u65bd\u9488\u7528\u836f\uff0c\u4fbf\u8981\u803d\u8bef\u4e86\u3002\u201d\u80e1\u6590\u201c\u55ef\u201d\u4e86\u4e00\u58f0\uff0c\u4ecd\u662f\u76ee\u4e0d\u8f6c\u77ac\u7684\u671b\u7740\u53f0\u4e0a\u3002 \u3000\u3000\u4e0d\u4e45\u4e00\u4eba\u8d25\u9000\u4e0b\u53f0\uff0c\u53e6\u4e00\u4eba\u4e0a\u53bb\u548c\u80dc\u8005\u6bd4\u8bd5\u3002\u8bf4\u662f\u540c\u95e8\u8f83\u827a\uff0c\u7136\u800c\u76f8\u6597\u7684\u4e24\u4eba\u5b9a\u662f\u4e0d\u540c\u652f\u6d3e\u7684\u95e8\u5f92\uff0c\u867d\u975e\u6027\u547d\u76f8\u640f\uff0c\u4f46\u80dc\u8d1f\u5173\u7cfb\u652f\u6d3e\u7684\u8363\u8fb1\uff0c\u5404\u4eba\u90fd\u662f\u5168\u529b\u4ee5\u8d74\u3002\u8fd9\u65f6\u95e8\u4e2d\u9ad8\u624b\u5c1a\u672a\u4e0a\u573a\uff0c\u773c\u524d\u8fd9\u4e9b\u4eba\u4e5f\u4e0d\u662f\u771f\u7684\u60f3\u80fd\u5f53\u4e0a\u638c\u95e8\u4eba\uff0c\u53ea\u662f\u534e\u62f3\u95e8\u4e94\u4e2a\u652f\u6d3e\u5411\u6765\u660e\u4e89\u6697\u6597\uff0c\u4e58\u6b64\u673a\u4f1a\uff0c\u4ee5\u5f80\u76f8\u4e92\u6709\u8fc7\u8282\u7684\u4fbf\u5728\u53f0\u4e0a\u597d\u597d\u6253\u4e0a\u4e00\u67b6\uff0c\u56e0\u6b64\u62f3\u6765\u811a\u53bb\uff0c\u5012\u662f\u7740\u5b9e\u70ed\u95f9\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u89c1\u80e1\u6590\u4f3c\u4e4e\u770b\u5f97\u5446\u4e86\uff0c\u5fc3\u60f3\uff1a\u201c\u5927\u54e5\u5929\u6027\u7231\u6b66\uff0c\u4e00\u89c1\u522b\u4eba\u6bd4\u8bd5\u4fbf\u4ec0\u4e48\u90fd\u5fd8\u4e86\u3002\u201d\u4f38\u624b\u5728\u4ed6\u80cc\u4e0a\u8f7b\u8f7b\u4e00\u63a8\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u773c\u4e0b\u60c5\u52bf\u7d27\u8feb\uff0c\u54b1\u4eec\u95ef\u51fa\u53bb\u518d\u8bf4\u3002\u8fd9\u4e9b\u4eba\u90fd\u662f\u6b66\u6797\u4e2d\u7684\u597d\u6c49\uff0c\u52a8\u4ee5\u6c5f\u6e56\u4e49\u6c14\uff0c\u4ed6\u4eec\u672a\u5fc5\u4fbf\u4f1a\u53bb\u7980\u62a5\u5b98\u5e9c\u3002\u201d\u80e1\u6590\u6447\u4e86\u6447\u5934\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u522b\u7684\u4e8b\u4e5f\u8fd8\u7f62\u4e86\uff0c\u798f\u5927\u5e05\u7684\u4e8b\uff0c\u4ed6\u4eec\u600e\u80fd\u4e0d\u8bf4\uff1f\u90a3\u6b63\u662f\u7acb\u529f\u7684\u826f\u673a\u3002\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u8981\u4e0d\uff0c\u54b1\u4eec\u5192\u4e0a\u4e00\u4e2a\u9669\uff0c\u4fbf\u5728\u8fd9\u513f\u7ed9\u9a6c\u59d1\u5a18\u7528\u836f\uff0c\u53ea\u662f\u5929\u5149\u767d\u65e5\u7684\u803d\u5728\u8fd9\u513f\uff0c\u975e\u7ed9\u4eba\u77a7\u89c1\u4e0d\u53ef\u3002\u201d\u8bf4\u5230\u540e\u6765\uff0c\u8bed\u97f3\u4e2d\u5df2\u662f\u5341\u5206\u7126\u6025\u3002\u5979\u5e73\u7d20\u751a\u662f\u5b89\u8be6\uff0c\u8fd9\u65f6\u82e5\u975e\u5f53\u771f\u7d27\u8feb\uff0c\u51b3\u4e0d\u81f4\u8fd9\u822c\u4e0d\u4f4f\u53e3\u7684\u50ac\u4fc3\u3002 \u3000\u3000\u80e1\u6590\u201c\u55ef\u201d\u4e86\u4e00\u58f0\uff0c\u4ecd\u662f\u76ee\u4e0d\u8f6c\u775b\u7684\u77a7\u7740\u53f0\u4e0a\u4e24\u4eba\u6bd4\u6b66\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u8f7b\u8f7b\u53f9\u4e86\u53e3\u6c14\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5f85\u4f1a\u6551\u4e0d\u4e86\u9a6c\u59d1\u5a18\uff0c\u53ef\u522b\u602a\u6211\u3002\u201d\u80e1\u6590\u5ffd\u9053\uff1a\u201c\u597d\uff0c\u867d\u7136\u77a7\u4e0d\u5168\uff0c\u4e5f\u53ea\u5f97\u5192\u9669\u8bd5\u4e0a\u4e00\u8bd5\u3002\u201d \u3000\u3000\u7a0b\u7075\u7d20\u4e00\u6014\uff0c\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u201d\u80e1\u6590\u9053\uff1a\u201c\u6211\u53bb\u593a\u90a3\u897f\u5cb3\u534e\u62f3\u7684\u638c\u95e8\u4eba\u3002\u8001\u5929\u7237\u4fdd\u4f51\uff0c\u82e5\u80fd\u6210\u529f\uff0c\u4ed6\u4eec\u4fbf\u4f1a\u542c\u6211\u53f7\u4ee4\u3002\u201d \u3000\u3000\u7a0b\u7075\u7d20\u5927\u559c\uff0c\u8fde\u8fde\u6447\u6643\u4ed6\u7684\u624b\u81c2\uff0c\u8bf4\u9053\uff1a\u201c\u5927\u54e5\uff0c\u8fd9\u4e9b\u4eba\u5982\u4f55\u80fd\u662f\u4f60\u5bf9\u624b\uff1f\u4e00\u5b9a\u6210\u529f\uff0c\u4e00\u5b9a\u6210\u529f\uff01\u201d\u80e1\u6590\u9053\uff1a\u201c\u53ea\u662f\u82e6\u5728\u6211\u987b\u5f97\u4f7f\u4ed6\u4eec\u7684\u62f3\u6cd5\uff0c\u4e00\u65f6\u4e09\u523b\u4e4b\u95f4\uff0c\u54ea\u91cc\u8bb0\u5f97\u4e86\u8fd9\u8bb8\u591a\uff1f \u3000\u3000\u5bf9\u4ed8\u5eb8\u624b\u4e5f\u8fd8\u7f62\u4e86\uff0c\u5c11\u65f6\u9ad8\u624b\u4e0a\u53f0\uff0c\u8fd9\u51e0\u4e0b\u62f3\u6cd5\u5b9a\u4e0d\u7ba1\u4f7f\uff0c\u975e\u9732\u51fa\u9a6c\u811a\u4e0d\u53ef\u3002\u4ed6\u4eec\u82e5\u77e5\u6211\u4e0d\u662f\u672c\u95e8\u5f1f\u5b50\uff0c\u7eb5\u7136\u5f97\u80dc\uff0c\u4e5f\u4e0d\u80af\u63a8\u6211\u505a\u638c\u95e8\u4eba\u3002\u201d\u8bf4\u5230\u8fd9\u91cc\uff0c\u4e0d\u7981\u53c8\u60f3\u8d77\u4e86\u8881\u7d2b\u8863\u3002\u5979\u5404\u5bb6\u5404\u6d3e\u7684\u6b66\u529f\u4f3c\u4e4e\u65e0\u4e00\u4e0d\u7cbe\uff0c\u5018\u82e5\u5979\u5728\u6b64\u5904\uff0c\u7531\u5979\u51fa\u9a6c\uff0c\u5b9a\u6bd4\u81ea\u5df1\u6709\u628a\u63e1\u5f97\u591a\u3002\u5176\u5b9e\uff0c\u4ed6\u5fc3\u4e2d\u82e5\u4e0d\u662f\u5ff5\u5179\u5728\u5179\u7684\u6709\u4e2a\u8881\u7d2b\u8863\uff0c\u53c8\u600e\u60f3\u5f97\u5230\u8981\u53bb\u593a\u534e\u62f3\u95e8\u7684\u638c\u95e8\uff1f \u3000\u3000\u4f46\u542c\u5f97\u201c\u554a\u54df\u201d\u4e00\u58f0\u5927\u53eb\uff0c\u4e00\u4eba\u6454\u4e0b\u53f0\u6765\u3002\u53f0\u4e0b\u6709\u4eba\u9a82\u9053\uff1a\u201c\u4ed6\u5988\u7684\uff0c\u4e0b\u624b\u8fd9\u4e48\u91cd\uff01\u201d\u53e6\u4e00\u4eba\u53cd\u5507\u76f8\u8ba5\uff1a\u201c\u52a8\u4e0a\u4e86\u624b\uff0c\u8fd8\u7ba1\u4ec0\u4e48\u8f7b\u91cd\uff1f\u4f60\u6709\u672c\u4e8b\uff0c\u4e0a\u53bb\u627e\u573a\u5b50\u554a\u3002\u201d\u90a3\u4eba\u7c97\u58f0\u9053\uff1a\u201c\u597d\uff0c\u54b1\u54e5\u513f\u4fe9\u4fbf\u6bd4\u5212\u6bd4\u5212\u3002\u201d\u53e6\u4e00\u4eba\u5374\u53ea\u7ba1\u51fa\u8a00\u9634\u635f\uff1a\u201c\u6211\u4e0d\u662f\u4f60\u5341\u516b\u4ee3\u5019\u8865\u638c\u95e8\u4eba\u7684\u5bf9\u624b\uff0c\u4e0d\u6562\u8ddf\u60a8\u8001\u4eba\u5bb6\u8fc7\u62db\u3002\u201d \u3000\u3000\u80e1\u6590\u7ad9\u8d77\u8eab\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u5018\u82e5\u5230\u4e86\u65f6\u8fb0\uff0c\u6211\u8fd8\u6ca1\u80fd\u593a\u5f97\u638c\u95e8\u4eba\uff0c\u4f60\u4fbf\u5728\u8fd9\u513f\u7ed9\u9a6c\u59d1\u5a18\u65bd\u9488\u7528\u836f\uff0c\u54b1\u4eec\u8d70\u4e00\u6b65\u77a7\u4e00\u6b65\u3002\u201d \u3000\u3000\u62ff\u8d77\u90a3\u59d3\u59ec\u6c49\u5b50\u8499\u8138\u7684\u9ec4\u5dfe\uff0c\u8499\u5728\u81ea\u5df1\u8138\u4e0a\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u201c\u55ef\u201d\u4e86\u4e00\u58f0\uff0c\u5fae\u7b11\u9053\uff1a\u201c\u4eba\u5bb6\u662f\u4e5d\u5bb6\u534a\u603b\u638c\u95e8\uff0c\u96be\u9053\u4f60\u4fbf\u8fde\u4e00\u5bb6\u4e5f\u5f53\u4e0d\u4e0a\uff1f\u201d\u5979\u8fd9\u53e5\u8bdd\u4e00\u51fa\u53e3\uff0c\u7acb\u5373\u597d\u751f\u540e\u6094\uff1a \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u603b\u662f\u5ff5\u5ff5\u4e0d\u5fd8\u5730\u60f3\u7740\u8881\u59d1\u5a18\uff0c\u53c8\u4e0d\u65ad\u63d0\u9192\u5927\u54e5\uff0c\u53eb\u4ed6\u4e5f\u662f\u5ff5\u5ff5\u4e0d\u5fd8\uff1f\u201d\u53ea\u89c1\u80e1\u6590\u6602\u7136\u8d70\u51fa\u5047\u5c71\uff0c\u77a7\u7740\u4ed6\u7684\u80cc\u5f71\uff0c\u53c8\u60f3\uff1a\u201c\u6211\u4fbf\u662f\u4e0d\u63d0\u9192\uff0c\u4ed6\u96be\u9053\u4fbf\u6709\u4e00\u523b\u5fd8\u4e86\uff1f\u201d\u4f46\u89c1\u4ed6\u5927\u8e0f\u6b65\u8d70\u5411\u620f\u53f0\uff0c\u4e0d\u7981\u53c8\u662f\u751c\u871c\uff0c\u53c8\u662f\u5fc3\u9178\u3002 \u3000\u3000\u80e1\u6590\u521a\u8d70\u5230\u53f0\u8fb9\uff0c\u5374\u89c1\u4e00\u4eba\u62a2\u5148\u8df3\u4e86\u4e0a\u53bb\uff0c\u6b63\u662f\u521a\u624d\u8ddf\u4eba\u5435\u5634\u7684\u90a3\u4e2a\u5927\u6c49\u3002\u80e1\u6590\u5fc3\u60f3\uff1a\u201c\u5f85\u8fd9\u4e24\u4eba\u5206\u51fa\u80dc\u8d25\uff0c\u53c8\u5f97\u8017\u4e0a\u8bb8\u591a\u529f\u592b\uff0c\u591a\u803d\u6401\u4e00\u523b\uff0c\u9a6c\u59d1\u5a18\u4fbf\u591a\u4e00\u523b\u5371\u9669\u3002\u201d\u5f53\u4e0b\u8ddf\u7740\u7eb5\u8d77\uff0c\u534a\u7a7a\u4e2d\u6293\u4f4f\u90a3\u6c49\u5b50\u7684\u80cc\u5fc3\uff0c\u8bf4\u9053\uff1a\u201c\u5e08\u5144\u4e14\u6162\uff0c\u8ba9\u6211\u5148\u6765\u3002\u201d \u3000\u3000\u80e1\u6590\u8fd9\u4e00\u6293\u65bd\u5c55\u4e86\u5bb6\u4f20\u5927\u64d2\u62ff\u624b\uff0c\u5927\u62c7\u6307\u6263\u4f4f\u90a3\u5927\u6c49\u80cc\u5fc3\u7b2c\u4e5d\u690e\u8282\u4e0b\u7684\u201c\u7b4b\u7f29\u7a74\u201d\uff0c\u5c0f\u6307\u6263\u4f4f\u4e86\u4ed6\u7b2c\u4e94\u690e\u8282\u4e0b\u7684\u201c\u795e\u9053\u7a74\u201d\u3002\u8fd9\u5927\u6c49\u867d\u7136\u8eab\u8eaf\u7c97\u58ee\uff0c\u5374\u54ea\u91cc\u8fd8\u80fd\u52a8\u5f39\uff1f\u80e1\u6590\u4e58\u7740\u90a3\u4e00\u7eb5\u4e4b\u52bf\uff0c\u7ad9\u5230\u4e86\u53f0\u53e3\uff0c\u987a\u624b\u4e00\u6325\uff0c\u5c06\u90a3\u5927\u6c49\u63b7\u4e86\u4e0b\u53bb\uff0c\u521a\u597d\u4ee4\u4ed6\u5b89\u5b89\u7a33\u7a33\u7684\u5750\u5165\u4e00\u5f20\u7a7a\u6905\u4e4b\u4e2d\u3002 \u3000\u3000\u4ed6\u8fd9\u4e00\u4e0b\u7a81\u5982\u5176\u6765\u7684\u663e\u793a\u4e86\u4e00\u624b\u4e0a\u4e58\u6b66\u529f\uff0c\u53f0\u4e0b\u4f17\u4eba\u65e0\u4e0d\u60ca\u5947\uff0c\u5012\u6709\u4e00\u534a\u4eba\u7ad9\u8d77\u8eab\u6765\u3002\u4f46\u89c1\u4ed6\u8138\u4e0a\u8499\u4e86\u4e00\u5757\u9ec4\u5dfe\uff0c\u9762\u76ee\u770b\u4e0d\u6e05\u695a\uff0c\u4e5f\u4e0d\u77e5\u662f\u8001\u662f\u5c11\uff0c\u53ea\u662f\u80cc\u540e\u62d6\u7740\u4e00\u6761\u6cb9\u5149\u4e4c\u4eae\u7684\u5927\u8fab\uff0c\u663e\u662f\u5e74\u7eaa\u4e0d\u5927\u3002\u8fd9\u822c\u5e74\u7eaa\u800c\u6709\u5982\u6b64\u529f\u529b\uff0c\u53f0\u4e0b\u6108\u662f\u89c1\u591a\u8bc6\u5e7f\u7684\u9ad8\u624b\uff0c\u6108\u662f\u8be7\u5f02\u3002 \u3000\u3000\u80e1\u6590\u5411\u53f0\u4e0a\u90a3\u4eba\u4e00\u62b1\u62f3\uff0c\u8bf4\u9053\uff1a\u201c\u5929\u5b57\u6d3e\u5f1f\u5b50\u7a0b\u7075\u80e1\uff0c\u8bf7\u5e08\u5144\u6307\u6559\u3002\u201d \u3000\u3000\u7a0b\u7075\u7d20\u5728\u5047\u5c71\u80cc\u540e\u542c\u5f97\u6e05\u695a\uff0c\u542c\u4ed6\u81ea\u79f0\u201c\u7a0b\u7075\u80e1\u201d\uff0c\u4e0d\u7981\u5fae\u7b11\uff0c\u4f46\u5fc3\u4e2d\u968f\u5373\u4e00\u9178\uff1a\u201c\u5018\u82e5\u4ed6\u771f\u5f53\u662f\u6211\u7684\u4eb2\u5144\u957f\uff0c\u5012\u662f\u514d\u5374\u4e86\u4e0d\u5c11\u70e6\u607c\u3002\u201d \u3000\u3000\u53f0\u4e0a\u90a3\u4eba\u89c1\u80e1\u6590\u8fd9\u7b49\u58f0\u52bf\uff0c\u5fc3\u4e0b\u5148\u81ea\u602f\u4e86\uff0c\u606d\u606d\u656c\u656c\u7684\u8fd8\u793c\u9053\uff1a\u201c\u5c0f\u5f1f\u5b66\u827a\u4e0d\u7cbe\uff0c\u8fd8\u8bf7\u7a0b\u5e08\u5144\u624b\u4e0b\u7559\u60c5\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u597d\u8bf4\uff0c\u597d\u8bf4\uff01\u201d\u5f53\u4e0b\u66f4\u4e0d\u5ba2\u5957\uff0c\u53f3\u817f\u534a\u8e72\uff0c\u5de6\u817f\u524d\u4f38\uff0c\u53f3\u624b\u6a2a\u638c\uff0c\u5de6\u624b\u53cd\u94a9\uff0c\u6b63\u662f\u534e\u62f3\u4e2d\u51fa\u624b\u7b2c\u4e00\u62db\u201c\u51fa\u52bf\u8de8\u864e\u897f\u5cb3\u4f20\u201d\u3002\u90a3\u4eba\u8f6c\u8eab\u63d0\u819d\u4f38\u638c\uff0c\u5e94\u4ee5\u4e00\u62db\u201c\u767d\u733f\u5077\u6843\u62dc\u5929\u5ead\u201d\uff0c\u8fd9\u4e00\u62db\u5b88\u591a\u4e8e\u653b\uff0c\u5168\u662f\u81ea\u4fdd\u4e4b\u610f\u3002\u80e1\u6590\u6251\u6b65\u5288\u638c\uff0c\u51fa\u4e00\u62db\u201c\u5434\u738b\u8bd5\u5251\u5288\u7389\u7816\u201d\u3002\u90a3\u4eba\u4ecd\u662f\u4e0d\u6562\u786c\u63a5\uff0c\u4f7f\u4e00\u62db\u201c\u64a4\u8eab\u5012\u6b65\u4e00\u6e9c\u70df\u201d\u3002\u80e1\u6590\u4e0d\u613f\u8ddf\u4ed6\u591a\u8017\uff0c\u4fbf\u4f7f\u201c\u659c\u8eab\u62e6\u95e8\u63d2\u94c1\u95e9\u201d\uff0c\u8fd9\u662f\u4e00\u62db\u62d7\u52bf\u5f13\u6b65\u51b2\u62f3\uff0c\u5de6\u638c\u53d8\u62f3\uff0c\u4f38\u76f4\u4e86\u731b\u51fb\u4e0b\u53bb\uff0c\u53f3\u62f3\u8ddf\u7740\u51b2\u51fb\u800c\u51fa\u3002\u90a3\u4eba\u89c1\u4ed6\u62f3\u52bf\u6c89\u731b\uff0c\u968f\u624b\u4e00\u67b6\u3002\u80e1\u6590\u624b\u81c2\u4e0a\u5185\u529b\u4e00\u6536\u4e00\u653e\uff0c\u5c06\u4ed6\u8f7b\u8f7b\u63a8\u4e0b\u53f0\u53bb\u3002 \u3000\u3000\u53ea\u542c\u5f97\u53f0\u4e0b\u4e00\u58f0\u5927\u543c\uff0c\u5148\u524d\u88ab\u80e1\u6590\u63b7\u4e0b\u7684\u90a3\u540d\u5927\u6c49\u53c8\u8df3\u4e86\u4e0a\u6765\uff0c\u559d\u9053\uff1a\u201c\u5976\u5976\u7684\uff0c\u4f60\u7b97\u662f\u4ec0\u4e48\u4e1c\u897f\u2026\u2026\u201d\u80e1\u6590\u62a2\u4e0a\u4e00\u6b65\uff0c\u4f7f\u62db\u201c\u91d1\u9e4f\u5c55\u7fc5\u5ead\u4e2d\u7ad9\u201d\u53cc\u81c2\u6a2a\u5f00\u4f38\u5c55\u3002\u90a3\u5927\u6c49\u7adf\u662f\u65e0\u6cd5\u5728\u53f0\u53e3\u7ad9\u7acb\uff0c\u88ab\u80e1\u6590\u7684\u81c2\u529b\u4e00\u903c\uff0c\u53c8\u6454\u4e86\u4e0b\u53bb\u3002\u8fd9\u4e00\u6b21\u80e1\u6590\u607c\u4ed6\u51fa\u8a00\u65e0\u793c\uff0c\u4f7f\u4e86\u4e09\u5206\u52b2\u529b\uff0c\u4f46\u542c\u5f97\u5580\u5587\u4e00\u54cd\uff0c\u90a3\u5927\u6c49\u538b\u70c2\u4e86\u53f0\u524d\u7684\u4e24\u5f20\u6905\u5b50\u3002 \u3000\u3000\u4ed6\u8fde\u8d25\u4e8c\u4eba\u4e4b\u540e\uff0c\u53f0\u4e0b\u4f17\u4eba\u7eb7\u7eb7\u4ea4\u5934\u63a5\u8033\uff0c\u90fd\u5411\u5929\u5b57\u6d3e\u7684\u5f1f\u5b50\u63a2\u8be2\u8fd9\u4eba\u662f\u8c01\u7684\u95e8\u4e0b\uff0c\u4f46\u5929\u5b57\u6d3e\u7684\u4f17\u5f1f\u5b50\u5374\u65e0\u4e00\u4eba\u5f97\u77e5\u3002\u827a\u5b57\u6d3e\u7684\u4e00\u4e2a\u524d\u8f88\u9053\uff1a\u201c\u8fd9\u4eba\u672c\u95e8\u7684\u6b66\u529f\u4e0d\u7eaf\uff0c\u663e\u662f\u5e26\u827a\u6295\u5e08\u7684\uff0c\u5341\u4e4b\u516b\u4e5d\uff0c\u662f\u59ec\u8001\u4e09\u65b0\u6536\u7684\u95e8\u5f92\u3002\u201d\u6210\u5b57\u6d3e\u7684\u4e00\u4e2a\u8001\u8005\u9053\uff1a\u201c\u90a3\u4fbf\u662f\u59ec\u8001\u4e09\u7684\u4e0d\u662f\u4e86\uff0c\u4ed6\u6d3e\u5e26\u827a\u6295\u5e08\u7684\u95e8\u5f92\u6765\u4e89\u593a\u638c\u95e8\u4eba\u4e4b\u4f4d\uff0c\u5c82\u4e0d\u662f\u53cd\u628a\u672c\u95e8\u6b66\u529f\u6bd4\u4e86\u4e0b\u53bb\uff1f\u201d \u3000\u3000\u539f\u6765\u6240\u8c13\u201c\u59ec\u8001\u4e09\u201d\uff0c\u4fbf\u662f\u5929\u5b57\u6d3e\u7684\u652f\u957f\u3002\u4ed6\u6b66\u529f\u5728\u897f\u5cb3\u534e\u62f3\u95e8\u4e2d\u7b97\u5f97\u7b2c\u4e00\uff0c\u53ea\u662f\u5341\u5e74\u524d\u4e24\u817f\u762b\u4e86\uff0c\u73b0\u4e0b\u867d\u7136\u4e0d\u826f\u4e8e\u884c\uff0c\u4f46\u5a01\u540d\u4ecd\u662f\u6781\u5927\uff0c\u540c\u95e8\u5e08\u5144\u5f1f\u5bf9\u4ed6\u90fd\u662f\u5fcc\u60ee\u4e09\u5206\u3002\u4f17\u4eba\u89c1\u8fd9\u4e2a\u201c\u5929\u5b57\u6d3e\u7684\u7a0b\u7075\u80e1\u201d\u6b66\u529f\u4e86\u5f97\uff0c\u800c\u59ec\u8001\u4e09\u6d3e\u6765\u7684\u513f\u5b50\u59ec\u6653\u5cf0\u59cb\u7ec8\u672a\u9732\u9762\uff0c\u90fd\u9053\u4ed6\u4fbf\u662f\u59ec\u8001\u4e09\u7684\u95e8\u5f92\uff0c\u5374\u90a3\u77e5\u59ec\u6653\u5cf0\u65e9\u7ed9\u80e1\u6590\u70b9\u4e2d\u4e86\u7a74\u9053\uff0c\u8eba\u5728\u5047\u5c71\u540e\u9762\u52a8\u5f39\u4e0d\u5f97\u3002\u90a3\u59ec\u8001\u4e09\u6b66\u529f\u4e00\u5f3a\uff0c\u4e3a\u4eba\u4e0d\u514d\u9a84\u50b2\uff0c\u5bf9\u540c\u95e8\u8c01\u4e5f\u6ca1\u77a7\u5728\u773c\u4e2d\uff0c\u53cc\u817f\u762b\u75ea\u540e\u95ed\u95e8\u8c22\u5ba2\uff0c\u5c06\u4e00\u8eab\u6b66\u529f\u90fd\u4f20\u7ed9\u4e86\u513f\u5b50\u3002\u8fd9\u4e00\u6b21\u534e\u62f3\u95e8\u4e94\u4e2a\u652f\u6d3e\u7684\u597d\u624b\u7fa4\u805a\u5317\u4eac\uff0c\u51ed\u6b66\u529f\u4ee5\u5b9a\u638c\u95e8\uff0c\u59ec\u6653\u5cf0\u5bf9\u8fd9\u638c\u95e8\u4e4b\u4f4d\u5fd7\u5728\u5fc5\u5f97\u3002\u4ed6\u6b66\u529f\u5df2\u8d76\u5f97\u4e0a\u7236\u4eb2\u7684\u4e5d\u6210\uff0c\u4f46\u6027\u683c\u5374\u8fdc\u4e0d\u53ca\u7236\u4eb2\u7684\u5149\u660e\u78ca\u843d\u3002\u4ed6\u6084\u6084\u5730\u8eb2\u5728\u5047\u5c71\u4e4b\u540e\uff0c\u8981\u77a7\u660e\u767d\u4e86\u5bf9\u624b\u5404\u4eba\u7684\u865a\u5b9e\uff0c\u7136\u540e\u51fa\u6765\u4e00\u51fb\u800c\u4e2d\uff0c\u4e0d\u6599\u9634\u9519\u9633\u5dee\uff0c\u5374\u7ed9\u80e1\u6590\u5236\u4f4f\uff0c\u4ed6\u53ea\u9053\u662f\u522b\u4e2a\u652f\u6d3e\u7684\u9634\u8c0b\uff0c\u6697\u4e2d\u4f0f\u4e0b\u9ad8\u624b\u6765\u5bf9\u4ed8\u81ea\u5df1\u3002\u9002\u624d\u4ed6\u548c\u5bf9\u624b\u53ea\u62c6\u5f97\u6570\u62db\uff0c\u5373\u88ab\u70b9\u4e2d\u7a74\u9053\uff0c\u4e00\u8eab\u6b66\u529f\u5168\u6ca1\u673a\u4f1a\u65bd\u5c55\uff0c\u7236\u4eb2\u548c\u81ea\u5df1\u7684\u5168\u76d8\u8ba1\u8f83\uff0c\u970e\u65f6\u95f4\u4ed8\u4e8e\u6d41\u6c34\uff0c\u5fc3\u4e0b\u605a\u6012\u4e4b\u6781\uff0c\u53ea\u76fc\u80fd\u4e0a\u53f0\u53bb\u518d\u548c\u80e1\u6590\u62da\u4e2a\u4f60\u6b7b\u6211\u6d3b\u3002\u4f46\u542c\u5f97\u80e1\u6590\u5728\u53f0\u4e0a\u5c06\u5404\u652f\u6d3e\u597d\u624b\u4e00\u4e2a\u4e2a\u6253\u4e86\u4e0b\u6765\uff0c\u770b\u6765\u518d\u4e5f\u65e0\u4eba\u80fd\u5c06\u4ed6\u5236\u670d\uff0c\u4e8e\u662f\u52a0\u7d27\u8fd0\u6c14\u6025\u51b2\u7a74\u9053\uff0c\u8981\u624b\u8db3\u901f\u5f97\u81ea\u7531\u3002 \u3000\u3000\u4f46\u80e1\u6590\u7684\u70b9\u7a74\u529f\u592b\u662f\u7956\u4f20\u7edd\u6280\uff0c\u59ec\u6653\u5cf0\u6240\u5b66\u4e0e\u4e4b\u622a\u7136\u4e0d\u540c\u3002\u4ed6\u5e73\u5fc3\u9759\u6c14\u7684\u6f5c\u8fd0\u5185\u529b\uff0c\u4e5f\u51b3\u4e0d\u80fd\u81ea\u89e3\u88ab\u95ed\u4f4f\u7684\u7a74\u9053\uff0c\u4f55\u51b5\u8fd9\u822c\u72c2\u6012\u5fe7\u6025\uff0c\u86ee\u51b2\u6025\u653b\uff1f\u4e00\u8f6e\u5f3a\u8fd0\u5185\u529b\u4e4b\u540e\uff0c\u7a81\u7136\u95f4\u6c14\u5165\u5c94\u9053\uff0c\u767b\u65f6\u6655\u4e86\u8fc7\u53bb\u3002\u8981\u77e5\u59ec\u8001\u4e09\u6240\u7ec3\u7684\u529f\u592b\u8fc7\u4e8e\u521a\u72e0\uff0c\u517c\u4e4b\u8e90\u7b49\u6c42\u8fdb\uff0c\u7ec8\u4e8e\u5728\u5750\u529f\u65f6\u8d70\u706b\u5165\u9b54\uff0c\u4ee5\u81f4\u53cc\u8db3\u762b\u75ea\u3002\u59ec\u6653\u5cf0\u8fd9\u65f6\u91cd\u8e48\u4e43\u7236\u8986\u8f99\uff0c\u51f6\u9669\u72b9\u6709\u8fc7\u4e4b\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u5168\u795e\u8d2f\u6ce8\u7684\u77a7\u7740\u80e1\u6590\u5728\u620f\u53f0\u4e0a\u4e0e\u4eba\u6bd4\u62f3\uff0c\u4f46\u89c1\u4ed6\u4e00\u62db\u4e00\u5f0f\uff0c\u679c\u7136\u5168\u662f\u65b0\u5b66\u6765\u7684\u201c\u897f\u5cb3\u534e\u62f3\u201d\uff0c\u5fc3\u9053\uff1a\u201c\u5927\u54e5\u4e8e\u6b66\u5b66\u4e00\u95e8\uff0c\u4f3c\u4e4e\u5929\u751f\u4fbf\u4f1a\u7684\u3002\u8fd9\u897f\u5cb3\u534e\u62f3\u62db\u5f0f\u7e41\u590d\uff0c\u4ed6\u53ea\u5728\u7247\u523b\u4e4b\u95f4\u77a7\u4eba\u62c6\u89e3\u8fc7\u62db\uff0c\u4fbf\u90fd\u5b66\u4f1a\u4e86\u3002\u201d \u3000\u3000\u4fbf\u5728\u6b64\u65f6\uff0c\u5ffd\u542c\u5f97\u8eab\u65c1\u90a3\u5927\u6c49\u4f4e\u54fc\u4e00\u58f0\uff0c\u58f0\u97f3\u751a\u662f\u5f02\u6837\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u8f6c\u5934\u770b\u65f6\uff0c\u53ea\u89c1\u4ed6\u53cc\u76ee\u7d27\u95ed\uff0c\u820c\u5934\u4f38\u5728\u5634\u5916\uff0c\u5df2\u88ab\u7259\u9f7f\u54ac\u5f97\u9c9c\u8840\u76f4\u6d41\uff0c\u5168\u8eab\u4e0d\u4f4f\u98a4\u6296\uff0c\u72b9\u4f3c\u53d1\u759f\u4e00\u822c\u3002\u7a0b\u7075\u7d20\u77e5\u4ed6\u662f\u6025\u5f15\u5185\u529b\u5f3a\u51b2\u7a74\u9053\uff0c\u4ee5\u81f4\u8d70\u706b\u5c94\u6c14\uff0c\u6b64\u65f6\u82e5\u4e0d\u6551\u6cbb\uff0c\u91cd\u5219\u5fc3\u795e\u9519\u4e71\uff0c\u75af\u766b\u53d1\u72c2\uff0c\u8f7b\u5219\u80a2\u4f53\u6b8b\u5e9f\uff0c\u6b66\u529f\u5168\u5931\u3002\u5979\u5fc3\u60f3\uff1a \u3000\u3000\u201c\u6211\u4eec\u548c\u4ed6\u65e0\u51a4\u65e0\u4ec7\uff0c\u4f55\u5fc5\u4e3a\u4e86\u6551\u4e00\u4eba\u800c\u53cd\u5bb3\u4e00\u4eba\uff1f\u201d\u4e8e\u662f\u53d6\u51fa\u91d1\u9488\uff0c\u5728\u4ed6\u9634\u7ef4\u8109\u7684\u5ec9\u6cc9\u3001\u5929\u7a81\u3001\u671f\u95e8\u3001\u5927\u6a2a\u56db\u5904\u7a74\u9053\u4e2d\u5404\u65bd\u9488\u523a\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u59ec\u6653\u5cf0\u60a0\u60a0\u9192\u8f6c\uff0c\u89c1\u7a0b\u7075\u7d20\u6b63\u5728\u66ff\u81ea\u5df1\u65bd\u9488\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u591a\u8c22\u59d1\u5a18\u3002\u201d\u7a0b\u7075\u7d20\u505a\u4e2a\u624b\u52bf\uff0c\u53eb\u4ed6\u4e0d\u53ef\u4f5c\u58f0\u3002 \u3000\u3000\u53ea\u542c\u5f97\u80e1\u6590\u5728\u53f0\u4e0a\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u638c\u95e8\u4e4b\u4f4d\uff0c\u52a1\u987b\u65e9\u5b9a\uff0c\u8fd9\u822c\u6597\u5c06\u4e0b\u53bb\uff0c\u4f55\u65f6\u65b9\u662f\u4e86\u5c40\uff1f\u5404\u4f4d\u5e08\u4f2f\u5e08\u53d4\u3001\u5e08\u5144\u5e08\u5f1f\uff0c\u613f\u610f\u6307\u6559\u7684\u53ef\u8bf7\u4e09\u56db\u4f4d\u540c\u65f6\u4e0a\u53f0\u3002\u5f1f\u5b50\u82e5\u662f\u8f93\u4e86\uff0c\u51b3\u65e0\u6028\u8a00\u3002\u201d \u3000\u3000\u4f17\u4eba\u4e00\u542c\uff0c\u90fd\u60f3\u8fd9\u5c0f\u5b50\u597d\u72c2\uff0c\u672c\u6765\u4e00\u4e2a\u4eba\u4e0d\u6562\u4e0a\u53f0\u7684\uff0c\u8fd9\u65f6\u7eb7\u7eb7\u8fde\u624b\u4e0a\u53f0\u9080\u6597\u3002\u5176\u5b9e\u80e1\u6590\u65b0\u5b66\u7684\u62db\u6570\u7a76\u5c5e\u6709\u9650\uff0c\u518d\u6597\u4e0b\u53bb\u52bf\u5fc5\u9732\u51fa\u7834\u7efd\uff0c\u7fa4\u6bb4\u5408\u6597\u5374\u53ef\u53d6\u5de7\uff0c\u6df7\u4e71\u4e2d\u65c1\u4eba\u4e0d\u6613\u770b\u51fa\uff0c\u518d\u5219\u5982\u6b64\u8f66\u8f6e\u6218\u7684\u6597\u5c06\u4e0b\u53bb\uff0c\u81ea\u5df1\u7eb5\u7136\u5185\u529b\u5145\u6c9b\uff0c\u7ec8\u987b\u529b\u5c3d\uff0c\u800c\u65bd\u6551\u9a6c\u6625\u82b1\u5374\u662f\u523b\u4e0d\u5bb9\u7f13\uff0c\u662f\u4ee5\u975e\u901f\u6218\u901f\u51b3\u4e0d\u53ef\u3002 \u3000\u3000\u4ed6\u50ac\u52a8\u638c\u529b\uff0c\u8f6c\u773c\u53c8\u51fb\u4e86\u51e0\u4eba\u4e0b\u53f0\u3002\u897f\u5cb3\u534e\u62f3\u95e8\u7684\u4e94\u6d3e\u5f1f\u5b50\u4e4b\u4e2d\uff0c\u5929\u5b57\u6d3e\u5f1f\u5b50\u90fd\u9053\u4ed6\u662f\u5949\u4e86\u59ec\u652f\u957f\u4e4b\u547d\u800c\u6765\uff0c\u56e0\u6b64\u65e0\u4eba\u4e0a\u53f0\u4e0e\u4ed6\u4ea4\u624b\uff0c\u5176\u4f59\u56db\u4e2a\u652f\u6d3e\u4e2d\u7684\u5c11\u58ee\u5f3a\u624b\uff0c\u5c3d\u5df2\u8d25\u5728\u4ed6\u7684\u62f3\u811a\u4e4b\u4e0b\u3002\u81f3\u4e8e\u4e00\u822c\u540d\u5bbf\u9ad8\u624b\uff0c\u81ea\u5fd6\u5b9e\u65e0\u53d6\u80dc\u628a\u63e1\uff0c\u4e3a\u4e86\u987e\u5168\u6570\u5341\u5e74\u7684\u4ee4\u540d\uff0c\u8c01\u4e5f\u4e0d\u80af\u4e0a\u53bb\u6311\u6218\u3002\u540e\u6765\u827a\u5b57\u6d3e\u3001\u6210\u5b57\u6d3e\u3001\u884c\u5b57\u6d3e\u4e09\u6d3e\u4e2d\u5404\u51fa\u4e00\u540d\u62f3\u672f\u6700\u7cbe\u7684\u58ee\u5e74\u597d\u624b\uff0c\u8054\u624b\u4e0a\u53f0\uff0c\u4f46\u5341\u4f59\u5408\u540e\u8fd8\u662f\u5c3d\u6570\u8d25\u4e86\u4e0b\u6765\u3002\u8fd9\u4e00\u6765\uff0c\u56db\u6d3e\u524d\u8f88\u540d\u5bbf\uff0c\u9752\u5e74\u5f1f\u5b50\uff0c\u5c3d\u7686\u9762\u9762\u76f8\u89d1\uff0c\u8c01\u4e5f\u4e0d\u6562\u633a\u8eab\u4e0a\u53f0\u3002 \u3000\u3000\u5374\u89c1\u90a3\u8eab\u7a7f\u9ed1\u9a6c\u8902\u7684\u59d3\u8521\u8001\u8005\u7ad9\u4e86\u8d77\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u7a0b\u5e08\u5144\uff0c\u4f60\u6b66\u529f\u9ad8\u5f3a\uff0c\u679c\u7136\u4ee4\u4eba\u4f69\u670d\u3002\u4f46\u8001\u673d\u77a7\u4f60\u7684\u62f3\u62db\uff0c\u4e0e\u672c\u95e8\u6240\u4f20\u5374\u6709\u70b9\u513f\u4f3c\u662f\u800c\u975e\uff0c\u55ef\u55ef\uff0c\u53ef\u8bf4\u662f\u5f62\u4f3c\u800c\u795e\u975e\uff0c\u8fd9\u4e2a\u2026\u2026\u8fd9\u4e2a\u5473\u9053\u5927\u5927\u4e0d\u540c\u3002\u201d \u3000\u3000\u80e1\u6590\u5fc3\u4e2d\u4e00\u51db\uff0c\u6697\u60f3\uff1a\u201c\u8fd9\u8001\u513f\u7684\u773c\u5149\u679c\u7136\u5389\u5bb3\uff0c\u6211\u6240\u7528\u62f3\u62db\u867d\u662f\u897f\u5cb3\u534e\u62f3\uff0c\u4f46\u9707\u4eba\u4e0b\u53f0\u3001\u6454\u4eba\u5012\u5730\u7684\u5185\u52b2\uff0c\u81ea\u7136\u8ddf\u4ed6\u4eec\u534e\u62f3\u5168\u4e0d\u76f8\u5e72\u3002\u201d\u8981\u77e5\u897f\u5cb3\u534e\u62f3\u662f\u5929\u4e0b\u8457\u540d\u7684\u5916\u95e8\u6b66\u529f\uff0c\u5176\u4e2d\u7cbe\u5fae\u5965\u5999\u4e4b\u5904\uff0c\u5c82\u662f\u80e1\u6590\u77a7\u51e0\u4e2a\u4eba\u5bf9\u62c6\u8fc7\u62db\u4fbf\u80fd\u9886\u4f1a\uff1f\u4f55\u51b5\u4ed6\u6240\u89c1\u5230\u7684\u53c8\u4e0d\u662f\u8be5\u95e8\u9ad8\u624b\uff0c\u81ea\u4e0d\u514d\u5b66\u5f97\u5f62\u4f3c\u800c\u795e\u975e\u3002\u8fd9\u65f6\u5b9e\u903c\u5904\u6b64\uff0c\u53ea\u5f97\u786c\u4e86\u5934\u76ae\u8bf4\u9053\uff1a\u201c\u534e\u62f3\u56db\u5341\u516b\uff0c\u827a\u884c\u6210\u5929\u6daf\u3002 \u3000\u3000\u82e5\u4e0d\u662f\u5404\u4eba\u6240\u609f\u4e0d\u540c\uff0c\u672c\u95e8\u4f55\u4ee5\u4f1a\u5206\u6210\u4e94\u4e2a\u652f\u6d3e\uff1f\u6b66\u5b66\u4e4b\u9053\uff0c\u539f\u65e0\u5b9a\u6cd5\u3002\u6211\u5929\u5b57\u6d3e\u609f\u5230\u7684\u62f3\u7406\u7565\u7565\u4e0e\u4f17\u4e0d\u540c\uff0c\u4e5f\u662f\u6709\u7684\u3002\u201d \u3000\u3000\u4ed6\u60f3\u5018\u80fd\u5c06\u5929\u5b57\u6d3e\u62c9\u5f97\u6765\u652f\u6301\u81ea\u5df1\uff0c\u4fbf\u4e0d\u81f3\u5b64\u7acb\u65e0\u63f4\u3002\u679c\u7136\u5929\u5b57\u6d3e\u7684\u4f17\u5f1f\u5b50\u542c\u4ed6\u8a00\u8bed\u4e2d\u62ac\u9ad8\u672c\u6d3e\uff0c\u5fc3\u4e2d\u90fd\u5f88\u8212\u670d\uff0c\u4fbf\u6709\u4eba\u5728\u53f0\u4e0b\u5927\u58f0\u9644\u548c\u3002 \u3000\u3000\u90a3\u59d3\u8521\u8001\u8005\u6447\u5934\u9053\uff1a\u201c\u7a0b\u5e08\u5144\uff0c\u4f60\u662f\u59ec\u8001\u4e09\u95e8\u4e0b\u4e0d\u662f\uff1f\u662f\u5e26\u827a\u6295\u5e08\u7684\u4e0d\u662f\uff1f\u8001\u673d\u773c\u775b\u6ca1\u6709\u82b1\uff0c\u77a7\u4f60\u7684\u529f\u592b\uff0c\u5341\u6210\u4e4b\u4e2d\u5012\u6709\u4e5d\u6210\u4e0d\u662f\u672c\u95e8\u7684\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u8521\u5e08\u4f2f\uff0c\u4f60\u8fd9\u8bdd\u5f1f\u5b50\u53ef\u4e0d\u6562\u82df\u540c\u3002\u672c\u95e8\u82e5\u8981\u5728\u5929\u4e0b\u638c\u95e8\u4eba\u5927\u4f1a\u4e4b\u4e2d\uff0c\u4e0e\u5c11\u6797\u3001\u6b66\u5f53\u3001\u592a\u6781\u3001\u516b\u5366\u90a3\u4e9b\u5927\u6d3e\u4e89\u96c4\uff0c\u4e00\u663e\u897f\u5cb3\u534e\u62f3\u95e8\u7684\u5a01\u98ce\uff0c\u4fbf\u987b\u878d\u4f1a\u8d2f\u901a\uff0c\u63a8\u9648\u51fa\u65b0\uff0c\u5f1f\u5b50\u6240\u5b66\u7684\u5185\u52b2\uff0c\u4e00\u5927\u534a\u662f\u6211\u5e08\u7236\u8fd9\u5341\u51e0\u5e74\u6765\u95ed\u95e8\u82e6\u601d\u3001\u522b\u51fa\u5fc3\u88c1\u6240\u521b\uff0c\u7684\u786e\u9887\u6709\u72ec\u5230\u4e4b\u5904\u3002\u8521\u5e08\u4f2f\u82e5\u662f\u8ba4\u4e3a\u5f1f\u5b50\u4e0d\u6210\uff0c\u4fbf\u8bf7\u4e0a\u53f0\u6765\u6307\u70b9\u4e00\u62db\u3002\u201d \u3000\u3000\u90a3\u59d3\u8521\u7684\u8001\u8005\u6709\u4e9b\u72b9\u8c6b\uff0c\u8bf4\u9053\uff1a\u201c\u672c\u95e8\u6709\u4f60\u8001\u5f1f\u8fd9\u822c\u6770\u51fa\u7684\u4eba\u6750\uff0c\u539f\u662f\u5927\u4f19\u7684\u5149\u5f69\uff0c\u8001\u673d\u6b22\u559c\u4e5f\u8fd8\u6765\u4e0d\u53ca\uff0c\u8fd8\u80fd\u6709\u751a\u4e48\u8bdd\u8bf4\uff1f\u53ea\u662f\u8001\u673d\u5fc3\u4e2d\u5b58\u7740\u4e00\u4e2a\u7591\u56e2\uff0c\u4e0d\u80fd\u4e0d\u8bf4\u3002\u8fd9\u6837\u7f62\uff0c\u8bf7\u7a0b\u8001\u5f1f\u5728\u53f0\u4e0a\u7ec3\u4e00\u5957\u4e00\u8def\u534e\u62f3\uff0c\u8fd9\u662f\u672c\u95e8\u7684\u57fa\u672c\u529f\u592b\uff0c\u8fd9\u91cc\u5341\u51e0\u4f4d\u8001\u5144\u5f1f\u4e2a\u4e2a\u76ee\u5149\u5982\u70ac\uff0c\u662f\u4fbf\u662f\uff0c\u4e0d\u662f\u4fbf\u4e0d\u662f\uff0c\u8c01\u4e5f\u4e0d\u80fd\u80e1\u8bf4\u3002\u4f60\u8001\u5f1f\u53ea\u8981\u771f\u7684\u7cbe\u719f\u672c\u95e8\u6b66\u529f\uff0c\u8001\u673d\u7b2c\u4e00\u4e2a\u4fbf\u6b22\u5929\u559c\u5730\u7684\u62e5\u4f60\u4e3a\u638c\u95e8\u3002\u201d \u3000\u3000\u679c\u7136\u59dc\u662f\u8001\u7684\u8fa3\uff0c\u80e1\u6590\u548c\u4eba\u52a8\u624b\u8fc7\u62db\uff0c\u5c1a\u80fd\u501f\u7740\u4f3c\u662f\u800c\u975e\u7684\u534e\u62f3\u65bd\u5c55\u672c\u8eab\u6b66\u529f\uff0c\u4f46\u8981\u4ed6\u7a7a\u624b\u7ec3\u4e00\u8def\u62f3\u6cd5\uff0c\u62ac\u624b\u8e22\u817f\u4e4b\u9645\uff0c\u771f\u4f2a\u7acb\u5224\uff0c\u518d\u4e5f\u65e0\u6240\u5047\u501f\u3002\u4f55\u51b5\u4ed6\u5077\u5b66\u6765\u7684\u62f3\u62db\u53ea\u662f\u4e00\u9cde\u534a\u722a\uff0c\u5e76\u975e\u6210\u5957\uff0c\u5982\u4f55\u80fd\u4ece\u5934\u81f3\u5c3e\u7684\u4f7f\u4e00\u8def\u62f3\u6cd5\uff1f \u3000\u3000\u80e1\u6590\u867d\u662f\u9976\u6709\u667a\u8ba1\uff0c\u542c\u4e86\u4ed6\u8fd9\u756a\u8bdd\u7adf\u662f\u505a\u58f0\u4e0d\u5f97\uff0c\u6b63\u60f3\u51fa\u8a00\u63a8\u8f9e\uff0c\u5ffd\u542c\u5047\u5c71\u540e\u4e00\u4eba\u53eb\u9053\uff1a\u201c\u8521\u5e08\u4f2f\uff0c\u4f60\u4f55\u4ee5\u603b\u662f\u8ddf\u6211\u4eec\u5929\u5b57\u6d3e\u4e3a\u96be\uff1f\u8fd9\u4f4d\u7a0b\u5e08\u5144\u662f\u6211\u7239\u7239\u7684\u5f97\u610f\u5f1f\u5b50\uff0c\u4ed6\u8fdb\u6211\u95e8\u5df2\u6709\u4e00\u5341\u4e8c\u5e74\uff0c\u96be\u9053\u8fde\u8fd9\u5957\u4e00\u8def\u534e\u62f3\u4e5f\u4e0d\u4f1a\u7ec3\uff1f\u201d\u53ea\u89c1\u4e00\u4eba\u8fc8\u6b65\u8d70\u5230\u53f0\u524d\uff0c\u6b63\u662f\u5929\u5b57\u6d3e\u4e2d\u7684\u5934\u6311\u811a\u8272\u59ec\u6653\u5cf0\u3002\u51e1\u662f\u5929\u5b57\u6d3e\u6709\u4e8b\uff0c\u4ed6\u603b\u4ee3\u7236\u4eb2\u51fa\u9762\u5904\u7406\u63a5\u5934\uff0c\u9690\u7136\u5df2\u662f\u8be5\u6d3e\u7684\u652f\u957f\uff0c\u56e0\u6b64\u6ca1\u4e00\u4e2a\u4e0d\u8ba4\u5f97\u3002 \u3000\u3000\u59ec\u6653\u5cf0\u8dc3\u4e0a\u53f0\u53bb\uff0c\u62b1\u62f3\u8bf4\u9053\uff1a\u201c\u5bb6\u7236\u95ed\u95e8\u9690\u5c45\uff0c\u5c06\u4e00\u8eab\u672c\u4e8b\u90fd\u4f20\u7ed9\u4e86\u8fd9\u4f4d\u7a0b\u5e08\u5144\uff0c\u4e00\u5341\u4e8c\u5e74\u6765\u4e3a\u7684\u4fbf\u662f\u4eca\u65e5\u3002\u8fd9\u4f4d\u7a0b\u5e08\u54e5\u6b66\u529f\u80dc\u6211\u5341\u500d\uff0c\u5404\u4f4d\u6709\u76ee\u5171\u7779\uff0c\u8fd8\u6709\u4ec0\u4e48\u8bdd\u8bf4\uff1f\u201d\u4f17\u4eba\u4e00\u542c\uff0c\u518d\u65e0\u6000\u7591\uff0c\u4eba\u4eba\u5747\u77e5\u59ec\u8001\u4e09\u602a\u50fb\u597d\u80dc\uff0c\u6084\u6084\u8c03\u6559\u4e86\u4e00\u4e2a\u597d\u5f92\u5f1f\uff0c\u5f85\u5f97\u827a\u6210\u4e4b\u540e\uff0c\u7a81\u7136\u663e\u793a\u4e8e\u4f17\u4eba\u4e4b\u524d\uff0c\u539f\u548c\u4ed6\u7684\u813e\u6c14\u76f8\u5408\u3002\u518d\u8bf4\u59ec\u6653\u5cf0\u7d20\u6765\u527d\u608d\u96c4\u5f3a\uff0c\u8fde\u4ed6\u4e5f\u5bf9\u80e1\u6590\u5fc3\u670d\uff0c\u54ea\u91cc\u8fd8\u6709\u4ec0\u4e48\u5047\u7684\uff1f \u3000\u3000\u90a3\u59d3\u8521\u7684\u8001\u8005\u8fd8\u5f85\u518d\u95ee\uff0c\u59ec\u6653\u5cf0\u6717\u58f0\u9053\uff1a\u201c\u8521\u5e08\u4f2f\u65e2\u8981\u8003\u8f83\u6211\u5929\u5b57\u6d3e\u7684\u529f\u592b\uff0c\u5f1f\u5b50\u4fbf\u4ee3\u7a0b\u5e08\u54e5\u7ec3\u4e00\u5957\uff0c\u8bf7\u8521\u5e08\u4f2f\u6307\u70b9\u3002\u201d \u3000\u3000\u4e5f\u4e0d\u5f85\u8521\u8001\u8005\u56de\u7b54\uff0c\u53cc\u817f\u4e00\u5e76\uff0c\u4f7f\u51fa\u201c\u6653\u661f\u5f53\u5934\u5373\u8d70\u62f3\u201d\uff0c\u8ddf\u7740\u201c\u51fa\u52bf\u8de8\u864e\u897f\u5cb3\u4f20\u201d\u3001\u201c\u91d1\u9e4f\u5c55\u7fc5\u5ead\u4e2d\u7ad9\u201d\u3001\u201c\u97e6\u9640\u732e\u62b1\u5728\u80f8\u524d\u201d\u3001\u201c\u628a\u81c2\u62e6\u95e8\u6a2a\u94c1\u95e9\u201d\u3001\u201c\u9b41\u9b3c\u4ef0\u6597\u64a9\u7eff\u680f\u201d\uff0c\u4e00\u62db\u62db\u7684\u7ec3\u4e86\u8d77\u6765\u3002\u4f46\u89c1\u4ed6\u4e0a\u80a2\u662f\u62f3\u3001\u638c\u3001\u94a9\u3001\u722a\u56de\u65cb\u53d8\u5316\uff0c\u51b2\u3001\u63a8\u3001\u683d\u3001\u5207\u3001\u5288\u3001\u6311\u3001\u9876\u3001\u67b6\u3001\u6491\u3001\u64a9\u3001\u7a7f\u3001\u6447\u5341\u4e8c\u822c\u624b\u6cd5\u4f38\u5c48\u56de\u73af\uff0c\u4e0b\u80a2\u81ea\u5f13\u7bad\u6b65\u3001\u9a6c\u6b65\u3001\u4ec6\u6b65\u3001\u865a\u6b65\u3001\u4e01\u6b65\u4e94\u9879\u6b65\u6839\u53d8\u51fa\u884c\u6b65\u3001\u5012\u6b65\u3001\u8fc8\u6b65\u3001\u5077\u6b65\u3001\u8e0f\u6b65\u3001\u51fb\u6b65\u3001\u8dc3\u6b65\u4e03\u822c\u6b65\u6cd5\uff0c\u6c89\u7a33\u5904\u4f3c\u8c61\u6b62\u864e\u8e1e\uff0c\u8fc5\u6377\u65f6\u5982\u9e70\u640f\u5154\u8131\u3002\u53f0\u4e0b\u4eba\u4eba\u662f\u672c\u95e8\u5f1f\u5b50\uff0c\u65e0\u4e0d\u719f\u4e60\u8fd9\u8def\u62f3\u6cd5\uff0c\u4f46\u89c1\u4ed6\u9020\u8be3\u5982\u6b64\u6df1\u539a\uff0c\u5c3d\u7686\u53f9\u670d\u3002\u8fde\u5404\u652f\u6d3e\u7684\u540d\u5bbf\u524d\u8f88\uff0c\u4e5f\u662f\u4e0d\u4f4f\u4ef7\u7684\u70b9\u5934\u3002\u53ea\u89c1\u4ed6\u4e00\u76f4\u7ec3\u5230\u201c\u51e4\u51f0\u65cb\u7a9d\u56de\u8eab\u8f6c\u201d\u3001\u201c\u817f\u767b\u4e5d\u5929\u51b2\u94c1\u62f3\u201d\u3001\u201c\u82f1\u96c4\u6253\u864e\u6536\u62db\u52bf\u201d\uff0c\u6700\u540e\u662f\u201c\u62f3\u7f62\u5ead\u524d\u4e94\u66f4\u5929\u201d\uff0c\u62db\u62db\u6cd5\u5ea6\u4e25\u5bc6\uff0c\u7684\u662f\u597d\u62f3\uff01 \u3000\u3000\u4ed6\u53cc\u624b\u4e00\u6536\uff0c\u53f0\u4e0b\u9707\u5929\u4ef7\u559d\u8d77\u4e00\u58f0\u5f69\u6765\u3002 \u3000\u3000\u81ea\u59ec\u6653\u5cf0\u4e00\u4e0a\u53f0\uff0c\u80e1\u6590\u5fc3\u4e2d\u4fbf\u81ea\u5947\u602a\uff0c\u4e0d\u77e5\u7a0b\u7075\u7d20\u7528\u751a\u4e48\u6cd5\u5b50\uff0c\u903c\u5f97\u4ed6\u6765\u8ddf\u81ea\u5df1\u89e3\u56f4\uff0c\u5f85\u89c1\u4ed6\u7ec3\u4e86\u8fd9\u8def\u62f3\u6cd5\uff0c\u5fc3\u4e2d\u4e5f\u8d5e\uff1a\u201c\u897f\u5cb3\u534e\u62f3\u975e\u540c\u5c0f\u53ef\uff0c\u6b64\u4eba\u53ea\u8981\u80fd\u8f85\u4ee5\u5185\u52b2\uff0c\u4fbf\u6210\u540d\u5bb6\u3002\u201d \u3000\u3000\u53ef\u662f\u89c1\u4ed6\u62f3\u6cd5\u4e00\u7ec3\u5b8c\uff0c\u767b\u65f6\u6c14\u606f\u7c97\u91cd\uff0c\u5168\u8eab\u5fae\u5fae\u53d1\u98a4\uff0c\u7adf\u4f3c\u5927\u75c5\u672a\u6108\uff0c\u6216\u662f\u8eab\u53d7\u91cd\u4f24\u4e00\u822c\u3002\u53f0\u4e0b\u4f17\u4eba\u672a\u66fe\u53d1\u89c9\uff0c\u80e1\u6590\u4fbf\u7ad9\u5728\u4ed6\u7684\u8eab\u540e\uff0c\u5374\u770b\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u53c8\u89c1\u4ed6\u80cc\u4e0a\u6c57\u900f\u8863\u886b\uff0c\u5b9e\u975e\u6b66\u529f\u9ad8\u5f3a\u4e4b\u4eba\u6240\u5e94\u4e3a\uff0c\u5fc3\u4e2d\u66f4\u589e\u4e86\u4e00\u5c42\u5947\u602a\u3002 \u3000\u3000\u59ec\u6653\u5cf0\u5b9a\u4e86\u5b9a\u795e\uff0c\u8bf4\u9053\uff1a\u201c\u8fd8\u6709\u54ea\u4e00\u4f4d\u5e08\u4f2f\u5e08\u53d4\u3001\u5e08\u5144\u5e08\u5f1f\uff0c\u613f\u548c\u7a0b\u5e08\u54e5\u6bd4\u8bd5\u7684\uff0c\u4fbf\u8bf7\u4e0a\u53f0\u3002\u201d\u4ed6\u8fde\u95ee\u4e09\u58f0\uff0c\u65e0\u4eba\u5e94\u58f0\u3002 \u3000\u3000\u5929\u5b57\u6d3e\u7684\u4e00\u7fa4\u5f1f\u5b50\u90fd\u5927\u58f0\u53eb\u4e86\u8d77\u6765\uff1a\u201c\u606d\u559c\u7a0b\u5e08\u54e5\u8363\u4efb\u897f\u5cb3\u534e\u62f3\u95e8\u7684\u638c\u95e8\u4eba\uff01\u201d\u4f17\u4eba\u8ddf\u7740\u6b22\u547c\u3002\u80e1\u6590\u6267\u638c\u534e\u62f3\u95e8\u4e00\u4e8b\u4fbf\u6210\u5b9a\u5c40\u3002 \u3000\u3000\u59ec\u6653\u5cf0\u5411\u80e1\u6590\u4e00\u62b1\u62f3\uff0c\u8bf4\u9053\uff1a\u201c\u606d\u559c\uff0c\u606d\u559c\uff01\u201d\u80e1\u6590\u62b1\u62f3\u8fd8\u793c\uff0c\u53ea\u89c1\u4ed6\u773c\u4e2d\u5145\u6ee1\u4e86\u6028\u6bd2\u4e4b\u60c5\uff0c\u4f46\u8bb0\u6302\u7740\u9a6c\u6625\u82b1\u7684\u75c5\u60c5\uff0c\u4e5f\u6ca1\u5fc3\u7eea\u53bb\u7406\u4f1a\uff0c\u8bf4\u9053\uff1a\u201c\u59ec\u5e08\u5f1f\uff0c\u4f60\u5feb\u627e\u95f4\u9759\u5ba4\uff0c\u9886\u54b1\u4eec\u4e24\u4f4d\u5e08\u59b9\u53bb\u4f11\u606f\u3002\u201d\u59ec\u6653\u5cf0\u70b9\u70b9\u5934\uff0c\u8dc3\u4e0b\u53f0\u6765\uff0c\u4f46\u53cc\u8db3\u7740\u5730\u65f6\uff0c\u4e00\u4e2a\u8e09\u8dc4\uff0c\u9669\u9669\u6454\u5012\u3002 \u3000\u3000\u80e1\u6590\u8d70\u5230\u53f0\u53e3\uff0c\u8bf4\u9053\uff1a\u201c\u5404\u4f4d\u8f9b\u82e6\u4e86\u4e00\u665a\uff0c\u8bf7\u5404\u81ea\u56de\u53bb\u4f11\u606f\u3002\u660e\u65e5\u665a\u95f4\uff0c\u54b1\u4eec\u518d\u5546\u5927\u8ba1\uff0c\u603b\u987b\u5728\u5929\u4e0b\u638c\u95e8\u4eba\u5927\u4f1a\u4e4b\u4e2d\uff0c\u8ba9\u534e\u62f3\u95e8\u626c\u7709\u5410\u6c14\u3002\u201d\u4ed6\u8fd9\u53e5\u8bdd\u5012\u975e\u865a\u8a00\uff0c\u5fc3\u4e2d\u5bf9\u534e\u62f3\u95e8\u5b9e\u662f\u5b58\u4e86\u51e0\u5206\u611f\u6fc0\u3002\u5728\u4f17\u5b98\u5175\u56f4\u6355\u4e4b\u4e0b\uff0c\u82e5\u4e0d\u662f\u673a\u7f18\u51d1\u5de7\uff0c\u8d8a\u5899\u800c\u5165\u65f6\u4ed6\u4eec\u6b63\u5728\u63a8\u4e3e\u638c\u95e8\uff0c\u591a\u534a\u9a6c\u6625\u82b1\u4fbf\u514d\u4e0d\u4e86\u6bd2\u53d1\u8eab\u6b7b\uff0c\u5012\u6bd9\u957f\u8857\u4e4b\u4e0a\u3002\u5982\u6709\u673a\u7f18\u80fd\u66ff\u534e\u62f3\u95e8\u4e89\u4e9b\u5149\u5f69\uff0c\u4ed6\u4e5f\u771f\u613f\u610f\u51fa\u529b\u3002 \u3000\u3000\u4f17\u4eba\u95fb\u8a00\uff0c\u7eb7\u7eb7\u7ad9\u8d77\u8eab\u6765\uff0c\u53e3\u4e2d\u90fd\u5728\u8bae\u8bba\u80e1\u6590\u7684\u529f\u592b\u3002\u6709\u7684\u66f4\u8bf4\u59ec\u8001\u4e09\u6df1\u8c0b\u8fdc\u8651\uff0c\u4e00\u9e23\u60ca\u4eba\uff1b\u6709\u7684\u8d5e\u626c\u59ec\u6653\u5cf0\u8fd9\u4e00\u8def\u62f3\u4f7f\u5f97\u5b9e\u662f\u9ad8\u660e\u3002\u5929\u5b57\u6d3e\u7684\u4f17\u5f1f\u5b50\u66f4\u662f\u5174\u9ad8\u91c7\u70c8\uff0c\u5f97\u610f\u975e\u51e1\u3002 \u3000\u3000\u6709\u51e0\u4e2a\u524d\u8f88\u540d\u5bbf\u60f3\u8fc7\u6765\u8ddf\u80e1\u6590\u6500\u8c08\uff0c\u80e1\u6590\u5374\u53cc\u624b\u4e00\u62f1\uff0c\u8ddf\u7740\u59ec\u6653\u5cf0\u76f4\u5165\u5185\u5802\u3002\u7a0b\u7075\u7d20\u6276\u4e86\u9a6c\u6625\u82b1\u6df7\u5728\u4eba\u4e1b\u4e4b\u4e2d\uff0c\u8ddf\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u8fd9\u5ea7\u5927\u5b85\u5b50\u662f\u534e\u62f3\u95e8\u4e2d\u4e00\u4f4d\u5c45\u5b98\u7684\u65d7\u4eba\u6240\u6709\u3002\u80e1\u6590\u65e2\u4e3a\u638c\u95e8\uff0c\u672c\u5b85\u4e3b\u4eba\u81ea\u662f\u5bf9\u4ed6\u62db\u5f85\u5f97\u5341\u5206\u6bb7\u52e4\u3002\u80e1\u6590\u59cb\u7ec8\u4e0d\u63ed\u5f00\u8499\u5728\u8138\u4e0a\u7684\u9ec4\u5dfe\uff0c\u76f4\u5230\u4e0e\u7a0b\u7075\u7d20\u3001\u9a6c\u6625\u82b1\u3001\u59ec\u6653\u5cf0\u4e09\u4eba\u8fdb\u4e86\u5185\u5ba4\uff0c\u624d\u9664\u4e0b\u9ec4\u5dfe\uff0c\u8bf4\u9053\uff1a\u201c\u59ec\u5927\u54e5\uff0c\u591a\u8c22\u4f60\u5566\uff01\u8fd9\u638c\u95e8\u4eba\u4e4b\u4f4d\uff0c\u6211\u5b9a\u4f1a\u8ba9\u7ed9\u4f60\u3002\u201d\u59ec\u6653\u5cf0\u54fc\u4e86\u4e00\u58f0\uff0c\u5374\u4e0d\u7b54\u8bdd\u3002\u80e1\u6590\u53bb\u770b\u9a6c\u6625\u82b1\u65f6\uff0c\u53ea\u89c1\u5979\u9ed1\u6c14\u6ee1\u8138\uff0c\u65e9\u5df2\u4eba\u4e8b\u4e0d\u77e5\uff0c\u9f3b\u5b54\u4e2d\u51fa\u6c14\u591a\u8fdb\u6c14\u5c11\uff0c\u5f53\u771f\u662f\u547d\u82e5\u6e38\u4e1d\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u62b1\u7740\u9a6c\u6625\u82b1\u5e73\u5367\u5e8a\u4e0a\uff0c\u53d6\u51fa\u91d1\u9488\uff0c\u9694\u7740\u8863\u670d\u66ff\u5979\u5728\u5341\u4e09\u5904\u7a74\u9053\u4e2d\u90fd\u6253\u4e0a\u4e86\uff0c\u6bcf\u679d\u91d1\u9488\u5c3e\u4e0a\u90fd\u56f4\u4e0a\u4e86\u4e00\u56e2\u68c9\u82b1\u3002 \u3000\u3000\u5979\u624b\u811a\u6781\u5feb\uff0c\u5374\u6beb\u4e0d\u5fd9\u4e71\u3002\u80e1\u6590\u89c1\u5979\u795e\u8272\u6c89\u9759\u5e73\u548c\uff0c\u8fd9\u624d\u653e\u4e86\u4e00\u534a\u5fc3\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u76cf\u8336\u529f\u592b\uff0c\u91d1\u9488\u5c3e\u4e0a\u7f13\u7f13\u6d41\u51fa\u9ed1\u8840\uff0c\u6cbe\u5728\u68c9\u82b1\u4e4b\u4e0a\uff0c\u539f\u6765\u91d1\u9488\u4e2d\u7a7a\uff0c\u4ee5\u6b64\u62d4\u51fa\u6bd2\u8d28\u3002\u7a0b\u7075\u7d20\u8212\u4e86\u4e00\u53e3\u6c14\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u4ece\u836f\u74f6\u4e2d\u53d6\u51fa\u4e00\u7c92\u78a7\u7eff\u7684\u4e38\u836f\u9012\u7ed9\u59ec\u6653\u5cf0\uff0c\u8bf4\u9053\uff1a\u201c\u59ec\u5927\u54e5\uff0c\u4f60\u5230\u81ea\u5df1\u623f\u91cc\u4f11\u606f\u5427\u3002\u8fd9\u836f\u4e38\u8fde\u670d\u5341\u7c92\uff0c\u4f60\u8eab\u4f53\u5185\u7684\u6bd2\u8d28\u4fbf\u4f1a\u53bb\u5c3d\u3002\u201d\u59ec\u6653\u5cf0\u63a5\u8fc7\u4e86\u836f\u4e38\uff0c\u4e00\u58f0\u4e0d\u54cd\u7684\u51fa\u623f\u800c\u53bb\u3002 \u3000\u3000\u80e1\u6590\u8fd9\u624d\u660e\u767d\uff0c\u539f\u6765\u7a0b\u7075\u7d20\u662f\u4ee5\u5979\u770b\u5bb6\u672c\u9886\uff0c\u903c\u5f97\u59ec\u6653\u5cf0\u4e0d\u5f97\u4e0d\u542c\u53f7\u4ee4\uff0c\u7b11\u9053\uff1a\u201c\u836f\u738b\u59d1\u5a18\u65e0\u5f80\u800c\u4e0d\u5229\u3002\u4f60\u7528\u6bd2\u836f\u505a\u597d\u4e8b\uff0c\u5c0a\u5e08\u5f53\u5e74\u53ea\u6015\u4e5f\u6709\u6240\u4e0d\u53ca\u3002\u201d \u3000\u3000\u7a0b\u7075\u7d20\u5fae\u7b11\u4e0d\u7b54\uff0c\u5176\u5b9e\u8fd9\u4e00\u6b21\u5979\u5012\u4e0d\u662f\u7528\u836f\u786c\u903c\uff0c\u90a3\u662f\u5148\u52a9\u59ec\u6653\u5cf0\u901a\u89e3\u7a74\u9053\uff0c\u53bb\u4e86\u8d70\u706b\u5165\u9b54\u7684\u5371\u96be\uff0c\u518d\u5728\u4ed6\u8eab\u4e0a\u65bd\u4e00\u70b9\u836f\u7269\u3002\u8fd9\u836f\u7269\u4e00\u4e0a\u8eab\u540e\u9ebb\u75d2\u96be\u5f53\uff0c\u4e8e\u8eab\u5b50\u5374\u65e0\u591a\u5927\u635f\u5bb3\uff0c\u6240\u8c13\u8fde\u670d\u5341\u7c92\u7684\u89e3\u836f\uff0c\u4e5f\u53ea\u662f\u6cbb\u91d1\u521b\u5916\u4f24\u7684\u6b62\u8840\u751f\u808c\u4e38\uff0c\u59ec\u6653\u5cf0\u5e76\u65e0\u5916\u4f24\uff0c\u670d\u4e86\u7b49\u5982\u4e0d\u670d\u3002\u4f46\u59ec\u6653\u5cf0\u54ea\u91cc\u77e5\u9053\uff1f\u542c\u5979\u8bf4\u5f97\u6bd2\u6027\u5389\u5bb3\u65e0\u6bd4\uff0c\u81ea\u4e0d\u6562\u4e0d\u4fef\u9996\u542c\u4ee4\uff0c\u5373\u4ee4\u6709\u6240\u7591\u5fc3\uff0c\u4e5f\u4e0d\u80fd\u4ee5\u81ea\u5df1\u7684\u6027\u547d\u6765\u8bd5\u4e00\u8bd5\u771f\u5047\u3002\u7a0b\u7075\u7d20\u5fc3\u4e2d\u5728\u8bf4\uff1a\u201c\u6211\u5411\u5e08\u7236\u53d1\u8fc7\u8a93\uff0c\u8fd9\u4e00\u751f\u4e4b\u4e2d\uff0c\u51b3\u4e0d\u7528\u6bd2\u836f\u5bb3\u4e00\u4e2a\u65e0\u8f9c\u4e4b\u4eba\uff0c\u597d\u6559\u4eba\u77e5\u9053\u6bd2\u624b\u836f\u738b\u624b\u6bb5\u867d\u8fa3\uff0c\u5374\u4e0d\u505a\u534a\u4ef6\u574f\u4e8b\u3002\u201d \u3000\u3000\u5979\u62ff\u4e86\u4e00\u67c4\u954a\u5b50\uff0c\u6362\u8fc7\u6cbe\u4e86\u6bd2\u8840\u7684\u68c9\u82b1\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u7d2f\u4e86\u4e00\u591c\uff0c\u4fbf\u5728\u8fd9\u69bb\u4e0a\u6b47\u6b47\uff0c\u517b\u4e00\u4f1a\u513f\u795e\u3002\u6709\u6211\u7167\u6599\u7740\u9a6c\u59d1\u5a18\uff0c\u4f60\u653e\u5fc3\u4fbf\u662f\u3002\u201d\u80e1\u6590\u4e5f\u771f\u5026\u4e86\uff0c\u659c\u8eab\u501a\u5728\u69bb\u4e0a\u3002\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u4f60\u8fd9\u4f4d\u638c\u95e8\u8001\u5e08\u5085\u6709\u4ef6\u4e8b\u53ef\u5f97\u5c0f\u5fc3\u5728\u610f\u3002\u8fd9\u5341\u4e8c\u4e2a\u65f6\u8fb0\u4e4b\u4e2d\uff0c\u4e0d\u80fd\u6709\u4eba\u8fdb\u6765\u6ecb\u6270\u9a6c\u59d1\u5a18\uff0c\u4e5f\u4e0d\u8bb8\u5979\u5f00\u53e3\u8bf4\u8bdd\uff0c\u5426\u5219\u5979\u5185\u6c14\u4e00\u5c94\uff0c\u6bd2\u8d28\u4e0d\u80fd\u62d4\u51c0\uff0c\u53ea\u8981\u7559\u4e0b\u5c11\u8bb8\uff0c\u90a3\u4fbf\u662f\u524d\u529f\u5c3d\u5f03\u3002\u201d \u3000\u3000\u80e1\u6590\u7b11\u9053\uff1a\u201c\u897f\u5cb3\u534e\u62f3\u638c\u95e8\u4eba\u7a0b\u7075\u80e1\uff0c\u8c28\u5949\u592a\u4e0a\u638c\u95e8\u4eba\u7a0b\u7075\u7d20\u53f7\u4ee4\uff0c\u4e00\u5207\u51db\u9075\uff0c\u4e0d\u6562\u6709\u8fdd\u3002\u201d\u7a0b\u7075\u7d20\u7b11\u9053\uff1a\u201c\u6211\u80fd\u662f\u4f60\u7684\u592a\u4e0a\u638c\u95e8\u4eba\u5417\uff1f\u90a3\u4f4d\u2026\u2026\u201d\u8bf4\u5230\u8fd9\u91cc\uff0c\u7a81\u7136\u4f4f\u53e3\uff0c\u4fef\u8eab\u53bb\u770b\u9a6c\u6625\u82b1\u7684\u4f24\u52bf\u3002 \u3000\u3000\u8fc7\u4e86\u534a\u664c\uff0c\u5979\u56de\u8fc7\u5934\u6765\uff0c\u89c1\u80e1\u6590\u5e76\u672a\u95ed\u76ee\u5165\u7761\uff0c\u5446\u5446\u7684\u671b\u7740\u7a97\u5916\u51fa\u795e\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u5728\u60f3\u4ec0\u4e48\uff1f\u201d\u80e1\u6590\u9053\uff1a\u201c\u6211\u60f3\u4ed6\u4eec\u660e\u65e5\u89c1\u4e86\u6211\u7684\u771f\u9762\u76ee\uff0c\u4e00\u770b\u5e74\u7eaa\u4e0d\u5bf9\uff0c\u4e0d\u77e5\u6709\u4ec0\u4e48\u8bdd\u8bf4\uff1f\u597d\u5728\u53ea\u987b\u6328\u8fc7\u5341\u4e8c\u4e2a\u65f6\u8fb0\uff0c\u54b1\u4eec\u62cd\u624b\u4fbf\u53bb\uff0c\u867d\u7136\u5bf9\u4e0d\u8d77\u4ed6\u4eec\uff0c\u5fc3\u4e2d\u4e0d\u5b89\uff0c\u4f46\u4e8b\u51fa\u65e0\u5948\uff0c\u90a3\u4e5f\u53ea\u597d\u2026\u2026\u53ea\u597d\u2026\u2026\u201d\u7a0b\u7075\u7d20\u7b11\u9053\uff1a\u201c\u4e5f\u53ea\u597d\u72d7\u6025\u8df3\u5899\u4e86\u3002\u201d\u80e1\u6590\u7b11\u9053\uff1a\u201c\u662f\u554a\uff01\u8df3\u5899\u800c\u5165\uff0c\u60f3\u4e0d\u5230\u7adf\u78b0\u4e0a\u4e86\u8fd9\u4e48\u4e00\u56de\u5947\u4e8b\u3002\u201d \u3000\u3000\u7a0b\u7075\u7d20\u51dd\u76ee\u5411\u80e1\u6590\u671b\u4e86\u4e00\u4f1a\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff01\u4fbf\u662f\u8fd9\u6837\u3002\u201d\u80e1\u6590\u5947\u9053\uff1a\u201c\u4ec0\u4e48\u4fbf\u662f\u8fd9\u6837\uff1f\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u54b1\u4eec\u5728\u8def\u4e0a\u626e\u8fc7\u5c0f\u80e1\u5b50\uff0c\u8fd9\u4e00\u6b21\u4f60\u4fbf\u626e\u4e2a\u5927\u80e1\u5b50\u3002\u518d\u7ed9\u4f60\u80e1\u5b50\u4e0a\u67d3\u4e0a\u4e00\u70b9\u989c\u8272\uff0c\u5305\u7ba1\u4f60\u5927\u4e0a\u4e8c\u5341\u5c81\u5e74\u7eaa\u3002\u4f60\u8981\u5f53\u59ec\u6653\u5cf0\u7684\u5e08\u5144\uff0c\u603b\u5f97\u5e74\u8fd1\u56db\u5341\u624d\u884c\u554a\u3002\u201d \u3000\u3000\u80e1\u6590\u62cd\u638c\u5927\u559c\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u6b63\u53d1\u6101\uff0c\u548c\u798f\u5eb7\u5b89\u8fd9\u4e48\u6b63\u9762\u4e00\u95f9\uff0c\u518d\u4e5f\u4e0d\u80fd\u53bb\u77a7\u77a7\u90a3\u4e2a\u5929\u4e0b\u638c\u95e8\u4eba\u5927\u4f1a\u3002\u4f60\u82e5\u80fd\u7ed9\u6211\u88c5\u4e0a\u4e00\u90e8\u5929\u8863\u65e0\u7f1d\u7684\u5927\u80e1\u5b50\uff0c\u6211\u7a0b\u7075\u80e1\u4fbf\u5802\u5802\u6b63\u6b63\uff0c\u4ee5\u897f\u5cb3\u534e\u62f3\u62f3\u95e8\u4eba\u7684\u8eab\u5206\uff0c\u5230\u4f1a\u4e2d\u53bb\u89c1\u8bc6\u89c1\u8bc6\u3002\u201d\u7a0b\u7075\u7d20\u53f9\u9053\uff1a\u201c\u638c\u95e8\u4eba\u5927\u4f1a\u662f\u4e0d\u7528\u53bb\u4e86\uff0c\u6df7\u5f97\u8fc7\u660e\u5929\uff0c\u8ba9\u9a6c\u59d1\u5a18\u592a\u5e73\u65e0\u4e8b\uff0c\u4e5f\u5c31\u662f\u5566\u3002\u5230\u4f1a\u4e2d\u6d89\u9669\uff0c\u90a3\u53ef\u72af\u4e0d\u7740\u3002\u201d \u3000\u3000\u80e1\u6590\u8c6a\u6c14\u52c3\u53d1\uff0c\u8bf4\u9053\uff1a\u201c\u4e8c\u59b9\uff0c\u6211\u53ea\u95ee\u4f60\uff1a\u8fd9\u90e8\u80e1\u5b50\u80fd\u4e0d\u80fd\u88c5\u5f97\u50cf\uff1f\u201d \u3000\u3000\u7a0b\u7075\u7d20\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8981\u626e\u5e74\u8001\u4e4b\u4eba\uff0c\u88c5\u90e8\u80e1\u5b50\u6709\u4f55\u96be\u5904\uff1f\u96be\u662f\u96be\u5728\u4e3e\u624b\u6295\u8db3\uff0c\u8bf4\u8bdd\u795e\u60c5\uff0c\u65e0\u4e00\u4e0d\u662f\u8001\u5e74\u800c\u975e\u5c11\u5e74\u3002 \u3000\u3000\u7eb5\u662f\u7cbe\u795e\u77cd\u94c4\u3001\u8eab\u8d1f\u6b66\u529f\u7684\u8001\u82f1\u96c4\uff0c\u5374\u4e5f\u548c\u5e74\u8f7b\u529b\u58ee\u4e4b\u4eba\u4e0d\u540c\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u4f60\u5927\u54e5\u5c3d\u529b\u800c\u4e3a\u3002\u53ea\u987b\u7792\u5f97\u8fc7\u4e00\u65f6\uff0c\u4e5f\u5c31\u662f\u4e86\u3002\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u597d\uff0c\u54b1\u4eec\u4fbf\u8bd5\u4e00\u8bd5\u3002\u8fd9\u4e00\u6b21\u6211\u5374\u626e\u4e2a\u8001\u5a46\u5a46\uff0c\u8ddf\u7740\u4f60\u5230\u638c\u95e8\u4eba\u5927\u4f1a\u4e4b\u4e2d\u77a7\u77a7\u70ed\u95f9\u3002\u201d \u3000\u3000\u80e1\u6590\u54c8\u54c8\u5927\u7b11\uff0c\u9038\u5174\u6a2a\u98de\uff0c\u8bf4\u9053\uff1a\u201c\u4e8c\u59b9\uff0c\u54b1\u8001\u5144\u59b9\u4fe9\u6d3b\u4e86\u8fd9\u4e00\u5927\u628a\u5e74\u7eaa\uff0c\u884c\u5c06\u5c31\u6728\uff0c\u8fd9\u573a\u70ed\u95f9\u53ef\u4e0d\u80fd\u4e0d\u8d76\u3002\u201d\u7a0b\u7075\u7d20\u4f4e\u58f0\u559d\u9053\uff1a\u201c\u58f0\u97f3\u8f7b\u4e9b\uff01\u201d\u4f46\u89c1\u9a6c\u6625\u82b1\u5728\u5e8a\u4e0a\u52a8\u4e86\u4e00\u4e0b\uff0c\u5e78\u597d\u6ca1\u6709\u60ca\u9192\u3002\u80e1\u6590\u4f38\u4e86\u4f38\u820c\u5934\uff0c\u5f2f\u8d77\u98df\u6307\uff0c\u5728\u81ea\u5df1\u989d\u4e0a\u8f7b\u51fb\u4e00\u4e0b\uff0c\u8bf4\u9053\uff1a\u201c\u8be5\u6b7b\uff01\u201d \u3000\u3000\u7a0b\u7075\u7d20\u53d6\u51fa\u9488\u7ebf\u5305\u6765\uff0c\u62ff\u51fa\u4e00\u628a\u5c0f\u526a\u5200\uff0c\u526a\u4e0b\u81ea\u5df1\u9b13\u8fb9\u51e0\u7f15\u79c0\u53d1\uff0c\u518d\u4ece\u836f\u7bb1\u4e2d\u53d6\u51fa\u4e9b\u836f\u6599\uff0c\u5728\u8336\u7897\u4e2d\u7528\u6e05\u6c34\u8c03\u5300\uff0c\u5c06\u5934\u53d1\u6d78\u5728\u836f\u91cc\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u6b47\u4e00\u4f1a\u513f\uff0c\u5f85\u8f6f\u5934\u53d1\u53d8\u6210\u786c\u80e1\u5b50\uff0c\u6211\u4fbf\u53eb\u4f60\u3002\u201d \u3000\u3000\u80e1\u6590\u4fbf\u5728\u69bb\u4e0a\u5408\u773c\uff0c\u5fc3\u4e2d\u5bf9\u8fd9\u4f4d\u4e49\u59b9\u7684\u806a\u660e\u673a\u667a\uff0c\u8bf4\u4e0d\u51fa\u7684\u6b22\u559c\u8d5e\u53f9\u3002\u7761\u68a6\u4e4b\u4e2d\uff0c\u4e00\u4f1a\u513f\u89c1\u9a6c\u6625\u82b1\u6bd2\u53d1\u8eab\u6b7b\uff0c\u5f62\u72b6\u53ef\u6016\uff1b\u4e00\u4f1a\u513f\u81ea\u5df1\u6293\u4f4f\u798f\u5eb7\u5b89\uff0c\u72e0\u72e0\u7684\u8d23\u5907\u4ed6\u5fc3\u80a0\u6bd2\u8fa3\uff1b\u53c8\u4e00\u4f1a\u513f\u81ea\u5df1\u7ed9\u4f17\u536b\u58eb\u64d2\u4f4f\u4e86\uff0c\u62da\u547d\u6323\u624e\uff0c\u5374\u4e0d\u80fd\u8131\u8eab\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u4e00\u4e2a\u58f0\u97f3\u5728\u8033\u8fb9\u67d4\u58f0\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u5728\u4f5c\u4ec0\u4e48\u68a6\uff1f\u201d \u3000\u3000\u80e1\u6590\u4e00\u8dc3\u800c\u8d77\uff0c\u63c9\u4e86\u63c9\u773c\u775b\uff0c\u5fae\u4e00\u51dd\u795e\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u6765\u7167\u6599\u9a6c\u59d1\u5a18\uff0c\u8be5\u5f53\u7531\u4f60\u7761\u4e00\u5ffd\u513f\u4e86\u3002\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u5148\u7ed9\u4f60\u88c5\u4e0a\u80e1\u5b50\uff0c\u8fd9\u624d\u653e\u5fc3\u3002\u201d\u62ff\u8d77\u6d46\u786c\u4e86\u7684\u4e00\u6761\u6761\u5934\u53d1\uff0c\u7528\u80f6\u6c34\u7ed9\u4ed6\u7c98\u5728\u988f\u4e0b\u548c\u816e\u8fb9\u3002\u8fd9\u4e00\u756a\u529f\u592b\u597d\u4e0d\u8d39\u65f6\uff0c\u76f4\u7c98\u4e86\u5c06\u8fd1\u4e00\u4e2a\u65f6\u8fb0\uff0c\u773c\u89c1\u7ea2\u65e5\u5f53\u7a97\uff0c\u65b9\u624d\u7c98\u5b8c\u3002 \u3000\u3000\u80e1\u6590\u63fd\u955c\u4e00\u7167\uff0c\u4e0d\u7531\u5f97\u54d1\u7136\u5931\u7b11\uff0c\u53ea\u89c1\u81ea\u5df1\u8138\u4e0a\u4e00\u90e8\u7edc\u816e\u80e1\u5b50\uff0c\u866c\u9aef\u621f\u5f20\uff0c\u4e0d\u4f46\u9762\u76ee\u5168\u975e\uff0c\u800c\u4e14\u5927\u589e\u5a01\u6b66\uff0c\u5fc3\u4e2d\u5f88\u662f\u9ad8\u5174\uff0c\u7b11\u9053\uff1a\u201c\u4e8c\u59b9\uff0c\u6211\u8fd9\u6a21\u6837\u513f\u633a\u7f8e\u554a\uff0c\u65e5\u540e\u6211\u771f\u7684\u4fbf\u7559\u4e0a\u8fd9\u4e48\u4e00\u90e8\u5927\u80e1\u5b50\u3002\u201d \u3000\u3000\u7a0b\u7075\u7d20\u60f3\u8bf4\uff1a\u201c\u53ea\u6015\u4f60\u5fc3\u4e0a\u4eba\u672a\u5fc5\u7b54\u5e94\u3002\u201d\u4f46\u8bdd\u5230\u53e3\u8fb9\uff0c\u7ec8\u4e8e\u5fcd\u4f4f\u4e86\u3002\u5979\u5fd9\u4e86\u4e00\u665a\uff0c\u5230\u8fd9\u65f6\u5fc3\u529b\u4ea4\u56f0\uff0c\u773c\u89c1\u9a6c\u6625\u82b1\u7761\u5f97\u5b89\u7a33\uff0c\u518d\u4e5f\u652f\u6301\u4e0d\u4f4f\uff0c\u4f0f\u5728\u684c\u4e0a\u4fbf\u7761\u7740\u4e86\u3002 \u3000\u3000\u5341\u5e74\u4e4b\u540e\uff0c\u80e1\u6590\u5ff5\u7740\u6b64\u65e5\u4e4b\u60c5\uff0c\u679c\u771f\u7559\u4e86\u4e00\u90e8\u7edc\u816e\u5927\u80e1\u5b50\uff0c\u90a3\u81ea\u4e0d\u662f\u7a0b\u7075\u7d20\u8fd9\u65f6\u6240\u80fd\u6599\u5230\u4e86\u3002 \u3000\u3000\u80e1\u6590\u4ece\u69bb\u4e0a\u53d6\u8fc7\u4e00\u5f20\u8584\u88ab\uff0c\u88f9\u4f4f\u4e86\u5979\u8eab\u5b50\uff0c\u8f7b\u8f7b\u62b1\u7740\u5979\u6a2a\u5367\u69bb\u4e0a\uff0c\u62c9\u8584\u88ab\u66ff\u5979\u76d6\u597d\uff0c\u518d\u5c06\u9ec4\u5dfe\u8499\u4f4f\u4e86\u8138\uff0c\u8d70\u5230\u59ec\u6653\u5cf0\u623f\u5916\uff0c\u53eb\u9053\uff1a\u201c\u59ec\u5144\uff0c\u5728\u5c4b\u91cc\u4e48\uff1f\u201d \u3000\u3000\u59ec\u6653\u5cf0\u54fc\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u662f\u54ea\u4e00\u4f4d\uff1f\u6709\u4ec0\u4e48\u4e8b\uff1f\u201d\u80e1\u6590\u63a8\u95e8\u8fdb\u53bb\u3002\u59ec\u6653\u5cf0\u4e00\u89c1\u662f\u4ed6\uff0c\u201c\u554a\u201d\u7684\u4e00\u58f0\u4f4e\u547c\uff0c\u4ece\u6905\u4e2d\u8dc3\u8d77\u8eab\u6765\u3002 \u3000\u3000\u80e1\u6590\u9053\uff1a\u201c\u59ec\u5144\uff0c\u6211\u8fd9\u662f\u8ddf\u4f60\u8d54\u4e0d\u662f\u6765\u5566\u3002\u201d\u59ec\u6653\u5cf0\u6728\u7136\u4e0d\u7b54\uff0c\u773c\u5149\u4e2d\u663e\u662f\u654c\u610f\u6781\u6df1\u3002\u80e1\u6590\u9053\uff1a\u201c\u6709\u4e00\u4ef6\u4e8b\u6211\u5f97\u8ddf\u59ec\u5144\u8bf4\u4e2a\u660e\u767d\uff0c\u5c0f\u5f1f\u51b3\u8ba1\u65e0\u610f\u505a\u8d35\u6d3e\u7684\u638c\u95e8\u4eba\uff0c\u53ea\u662f\u673a\u7f18\u51d1\u5408\uff0c\u5c0f\u5f1f\u53c8\u8feb\u4e8e\u65e0\u5948\uff0c\u8fd9\u624d\u574f\u4e86\u59ec\u5144\u7684\u5927\u4e8b\u3002\u201d\u4e8e\u662f\u5c06\u9a6c\u6625\u82b1\u5982\u4f55\u4e2d\u6bd2\u3001\u5982\u4f55\u53d7\u5b98\u5175\u56f4\u6355\u3001\u5982\u4f55\u8d8a\u5899\u5165\u6765\u8eb2\u907f\u3001\u5982\u4f55\u4e3a\u4e86\u6551\u6cbb\u4eba\u547d\u8fd9\u624d\u4e0a\u53f0\u51fa\u624b\u7b49\u60c5\u4e00\u4e00\u8bf4\u4e86\uff0c\u53ea\u662f\u9a6c\u6625\u82b1\u4e3a\u4f55\u4eba\u6240\u5bb3\u3001\u8ffd\u6355\u4ed6\u7684\u4e43\u662f\u798f\u5eb7\u5b89\u4e00\u8282\uff0c\u5374\u7565\u8fc7\u4e86\u4e0d\u8bf4\u3002\u59ec\u6653\u5cf0\u9759\u9759\u542c\u7740\uff0c\u8138\u8272\u7a0d\u89c1\u548c\u7f13\uff0c\u7b49\u80e1\u6590\u8bf4\u5b8c\uff0c\u4ecd\u53ea\u201c\u55ef\u201d\u7684\u4e00\u58f0\uff0c\u5e76\u4e0d\u63a5\u53e3\u8bf4\u8bdd\u3002 \u3000\u3000\u80e1\u6590\u53c8\u9053\uff1a\u201c\u5927\u4e08\u592b\u8a00\u51fa\u5982\u5c71\uff0c\u82e5\u662f\u5341\u5929\u4e4b\u5185\uff0c\u6211\u4e0d\u5c06\u638c\u95e8\u4eba\u4e4b\u4f4d\u8ba9\u4f60\uff0c\u6559\u6211\u4e27\u751f\u5200\u5251\u4e4b\u4e0b\uff0c\u5343\u8f7d\u4e4b\u540e\u4ecd\u53d7\u6c5f\u6e56\u597d\u6c49\u553e\u9a82\u3002\u201d\u6b66\u6797\u4e2d\u4eba\u6b7b\u4e8e\u5200\u5251\u4e4b\u4e0b\uff0c\u539f\u5c5e\u5bfb\u5e38\uff0c\u4f46\u82e5\u4e3a\u5929\u4e0b\u82f1\u96c4\u6240\u4e0d\u9f7f\uff0c\u5374\u662f\u6700\u611f\u7f9e\u803b\u4e4b\u4e8b\u3002 \u3000\u3000\u59ec\u6653\u5cf0\u542c\u4ed6\u53d1\u4e0b\u8fd9\u4e2a\u91cd\u8a93\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u638c\u95e8\u4eba\u4e4b\u4f4d\uff0c\u6211\u4e5f\u4e0d\u7528\u4f60\u8ba9\u3002\u4f60\u6b66\u529f\u80dc\u6211\u5341\u500d\uff0c\u8fd9\u662f\u6211\u77e5\u9053\u7684\u3002\u4f46\u4f60\u5b9e\u975e\u672c\u95e8\u4e2d\u4eba\uff0c\u5374\u6765\u6267\u638c\u95e8\u6237\uff0c\u81ea\u662f\u4ee4\u4eba\u5fc3\u4e2d\u4e0d\u670d\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u662f\u4e86\u3002 \u3000\u3000\u5f85\u8fd9\u6b21\u638c\u95e8\u4eba\u5927\u4f1a\u4e00\u8fc7\uff0c\u6211\u5c06\u524d\u540e\u771f\u76f8\u90d1\u91cd\u5ba3\u5e03\uff0c\u5728\u8d35\u95e8\u5404\u4f4d\u524d\u8f88\u9762\u524d\u8c22\u7f6a\u3002\u7136\u540e\u8ba9\u8d35\u95e8\u5404\u4f4d\u5f1f\u5b50\u518d\u51ed\u6b66\u529f\u4ee5\u5b9a\u638c\u95e8\uff0c\u8fd9\u4e48\u529e\u597d\u4e0d\u597d\uff1f\u201d\u59ec\u6653\u5cf0\u5fc3\u60f3\uff1a\u201c\u672c\u95e8\u4e4b\u4e2d\uff0c\u65e0\u4eba\u80fd\u80dc\u5f97\u4e86\u6211\u3002\u8fd9\u822c\u81ea\u884c\u4e89\u6765\uff0c\u81ea\u6bd4\u4ed6\u62f1\u624b\u76f8\u8ba9\u5149\u5f69\u5f97\u591a\u3002\u201d\u4e8e\u662f\u70b9\u5934\u9053\uff1a\u201c\u8fd9\u5012\u662f\u53ef\u884c\u3002\u53ef\u662f\u7a0b\u5927\u54e5\u2026\u2026\u201d \u3000\u3000\u80e1\u6590\u7b11\u9053\uff1a\u201c\u6211\u59d3\u80e1\uff0c\u6211\u4e49\u59b9\u624d\u59d3\u7a0b\u3002\u201d\u8bf4\u7740\u63ed\u53bb\u8499\u5728\u8138\u4e0a\u7684\u9ec4\u5dfe\u3002\u59ec\u6653\u5cf0\u89c1\u4ed6\u6ee1\u988a\u866c\u9aef\uff0c\u6839\u6839\u89c1\u8089\uff0c\u8c8c\u76f8\u751a\u662f\u5a01\u6b66\uff0c\u4e0d\u7981\u6697\u81ea\u8d5e\u53f9\uff0c\u8bf4\u9053\uff1a\u201c\u80e1\u5927\u54e5\uff0c\u672c\u95e8\u7684\u51e0\u4f4d\u524d\u8f88\u5f88\u96be\u8bf4\u8bdd\uff0c\u65e5\u540e\u4f60\u63ed\u793a\u771f\u76f8\uff0c\u53ea\u6015\u5b9a\u6709\u4e00\u573a\u98ce\u6ce2\u3002\u867d\u7136\u4f60\u6b66\u529f\u9ad8\u5f3a\uff0c\u539f\u4e5f\u4e0d\u6015\uff0c\u4f46\u597d\u6c49\u654c\u4e0d\u8fc7\u4eba\u591a\u3002\u54b1\u4eec\u897f\u5cb3\u534e\u62f3\u95e8\u9047\u4e0a\u4e86\u95e8\u6237\u5927\u4e8b\uff0c\u90a3\u662f\u6709\u540d\u7684\u9634\u9b42\u4e0d\u6563\uff0c\u6b7b\u7f20\u70c2\u6253\u3002\u201d\u80e1\u6590\u7b11\u9053\uff1a\u201c\u8fd9\u4e8b\u6211\u4e5f\u60f3\u5230\u4e86\u3002\u540e\u65e5\u638c\u95e8\u4eba\u5927\u4f1a\u4e4b\u4e2d\uff0c\u6211\u5f53\u5c3d\u529b\u4e3a\u897f\u5cb3\u534e\u62f3\u95e8\u6323\u4e00\u4e2a\u5927\u5927\u7684\u5f69\u5934\uff0c\u5c06\u529f\u8d4e\u7f6a\uff0c\u60f3\u6765\u5404\u4f4d\u524d\u8f88\u4e5f\u53ef\u89c1\u8c05\u4e86\u3002\u201d \u3000\u3000\u59ec\u6653\u5cf0\u70b9\u70b9\u5934\uff0c\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u53ef\u60dc\u6211\u8eab\u4e2d\u5267\u6bd2\uff0c\u4e0d\u6562\u591a\u8017\u529b\u6c14\uff0c\u5426\u5219\u5012\u53ef\u628a\u672c\u95e8\u62f3\u6cd5\uff0c\u6f14\u51e0\u5957\u7ed9\u80e1\u5144\u77a7\u77a7\u3002\u80e1\u5144\u8bb0\u5728\u5fc3\u91cc\uff0c\u4e8b\u5230\u4e34\u5934\uff0c\u4fbf\u4e0d\u6613\u9732\u51fa\u9a6c\u811a\u3002\u201d \u3000\u3000\u80e1\u6590\u5475\u5475\u800c\u7b11\uff0c\u7ad9\u8d77\u6765\u5411\u59ec\u6653\u5cf0\u6df1\u6df1\u4e00\u63d6\uff0c\u8bf4\u9053\uff1a\u201c\u59ec\u5144\uff0c\u6211\u4ee3\u4e49\u59b9\u5411\u4f60\u8d54\u7f6a\u4e86\u3002\u201d\u59ec\u6653\u5cf0\u8fd8\u4e86\u4e00\u793c\uff0c\u5fc3\u4e2d\u5374\u5927\u4e3a\u4e0d\u603f\uff1a \u3000\u3000\u201c\u6211\u88ab\u5979\u4e0b\u4e86\u6bd2\uff0c\u5374\u6709\u4ec0\u4e48\u53ef\u7b11\u7684\uff1f\u201d\u5fc3\u4e0b\u8fd9\u822c\u60f3\uff0c\u8138\u4e0a\u4fbf\u9887\u6709\u60bb\u60bb\u4e4b\u8272\u3002\u80e1\u6590\u9053\uff1a\u201c\u59ec\u5144\uff0c\u6211\u4e49\u59b9\u5728\u4f60\u8eab\u4e0a\u4e0b\u6bd2\uff0c\u4f24\u53e3\u5728\u54ea\u91cc\uff1f\u201d\u59ec\u6653\u5cf0\u5377\u8d77\u5de6\u624b\u8896\u5b50\uff0c\u53ea\u89c1\u4ed6\u4e0a\u81c2\u80bf\u8d77\u4e86\u9e21\u86cb\u5927\u7684\u4e00\u5757\uff0c\u808c\u8089\u53d1\u9ed1\uff0c\u4f24\u53e3\u6709\u5c0f\u6307\u5934\u5927\u5c0f\uff0c\u9690\u9690\u6e17\u51fa\u9ed1\u8840\uff0c\u679c\u5982\u662f\u4e2d\u4e86\u5267\u6bd2\u4e00\u822c\u3002 \u3000\u3000\u80e1\u6590\u5fc3\u60f3\uff1a\u201c\u4e8c\u59b9\u7528\u836f\uff0c\u5f53\u771f\u662f\u795e\u4e4e\u5176\u6280\u3002\u4e0d\u77e5\u7528\u4e86\u4ec0\u4e48\u836f\u7269\uff0c\u5f04\u5f97\u4ed6\u624b\u81c2\u53d8\u6210\u8fd9\u822c\u6a21\u6837\u3002\u5018\u82e5\u6211\u8eab\u4e0a\u6709\u4e86\u8fd9\u6837\u4e00\u4e2a\u4f24\u53e3\uff0c\u81ea\u4e5f\u4f1a\u5bdd\u98df\u4e0d\u5b89\u3002\u201d\u95ee\u9053\uff1a\u201c\u59ec\u5144\u89c9\u5f97\u600e\u6837\uff1f\u201d\u59ec\u6653\u5cf0\u9053\uff1a\u201c\u8fd9\u4e00\u5757\u8089\u9ebb\u6728\u4e0d\u4ec1\uff0c\u5168\u65e0\u77e5\u89c9\u3002\u201d\u80e1\u6590\u5fc3\u9053\uff1a\u201c\u539f\u6765\u662f\u4e0b\u4e86\u6781\u91cd\u7684\u9ebb\u836f\u3002\u201d\u4e00\u4f38\u624b\u6293\u4f4f\u4ed6\u624b\u81c2\uff0c\u4fef\u53e3\u4fbf\u5f80\u4ed6\u521b\u53e3\u4e0a\u542e\u5438\u3002\u59ec\u6653\u5cf0\u5927\u60ca\uff0c\u53eb\u9053\uff1a\u201c\u4f7f\u4e0d\u5f97\uff0c\u4f7f\u4e0d\u5f97\uff01\u4f60\u4e0d\u8981\u547d\u4e86\u5417\uff1f\u201d\u53ea\u662f\u7ed9\u4ed6\u53cc\u624b\u6293\u4f4f\u4e86\uff0c\u7adf\u81ea\u52a8\u5f39\u4e0d\u5f97\uff0c\u5fc3\u4e2d\u60ca\u7591\u4e0d\u5b9a\uff1a\u201c\u5982\u6b64\u5267\u6bd2\uff0c\u4e2d\u5728\u624b\u81c2\u5df2\u662f\u8fd9\u6837\u5389\u5bb3\uff0c\u4ed6\u4e00\u542e\u5165\u53e3\uff0c\u5c82\u4e0d\u7acb\u6bd9\uff1f\u6211\u548c\u4ed6\u65e0\u4eb2\u65e0\u6545\uff0c\u4ed6\u4f55\u5fc5\u820d\u547d\u76f8\u6551\uff1f\u201d \u3000\u3000\u80e1\u6590\u542e\u4e86\u51e0\u53e3\uff0c\u5c06\u9ed1\u8840\u5410\u5728\u5730\u4e0b\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u59ec\u5144\u4e0d\u5fc5\u60ca\u7591\uff0c\u8fd9\u6bd2\u836f\u662f\u5047\u7684\u3002\u201d\u59ec\u6653\u5cf0\u4e0d\u660e\u5176\u610f\uff0c\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u201d\u80e1\u6590\u9053\uff1a\u201c\u6211\u4e49\u59b9\u548c\u4f60\u7d20\u4e0d\u76f8\u8bc6\uff0c\u5c82\u80fd\u968f\u4fbf\u4e0b\u6bd2\u624b\u5bb3\u4f60\uff1f\u5979\u53ea\u662f\u8ddf\u4f60\u5f00\u4e2a\u73a9\u7b11\uff0c\u7ed9\u4f60\u653e\u4e0a\u4e9b\u65e0\u5bb3\u7684\u9ebb\u836f\u800c\u5df2\u3002\u4f60\u77a7\u6211\u542e\u5728\u53e3\u4e2d\uff0c\u603b\u53ef\u653e\u5fc3\u4e86\u5427\u3002\u201d \u3000\u3000\u59ec\u6653\u5cf0\u867d\u7136\u670d\u4e86\u7a0b\u7075\u7d20\u6240\u7ed9\u7684\u89e3\u836f\uff0c\u5fc3\u4e0b\u4e00\u76f4\u60f4\u60f4\uff0c\u4e0d\u77e5\u8fd9\u89e3\u836f\u662f\u5426\u5f53\u771f\u6709\u6548\uff0c\u6bd2\u6027\u5373\u4f7f\u80fd\u89e3\uff0c\u662f\u5426\u4f1a\u7559\u4e0b\u540e\u60a3\uff0c\u4f24\u53ca\u7b4b\u9aa8\uff0c\u8fd9\u65f6\u542c\u80e1\u6590\u4e00\u8bf4\uff0c\u4e0d\u7531\u5f97\u60ca\u559c\u4ea4\u96c6\uff0c\u9053\uff1a\u201c\u80e1\u5144\uff0c\u4f60\u2026\u2026\u4f60\u5bf9\u6211\u660e\u8a00\uff0c\u96be\u9053\u4fbf\u4e0d\u6015\u6211\u4e0d\u542c\u6307\u4f7f\u4e48\uff1f\u201d\u80e1\u6590\u9053\uff1a\u201c\u4e08\u592b\u76f8\u4ea4\uff0c\u8d35\u5728\u8bda\u4fe1\u3002\u6211\u89c1\u59ec\u5144\u5927\u6709\u4e49\u6c14\uff0c\u4f55\u5fc5\u4ee4\u4f60\u591a\u803d\u51e0\u65e5\u5fc3\u4e8b\uff1f\u201d\u59ec\u6653\u5cf0\u5927\u559c\uff0c\u62cd\u6848\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u6211\u4ea4\u4e86\u4f60\u8fd9\u4f4d\u670b\u53cb\u3002\u80e1\u5144\u4fbf\u662f\u5f97\u7f6a\u4e86\u5f53\u4eca\u5929\u5b50\uff0c\u72af\u4e0b\u5f25\u5929\u5927\u7f6a\uff0c\u5c0f\u5f1f\u4e5f\u8981\u8ddf\u4f60\u51fa\u529b\uff0c\u51b3\u4e0d\u6562\u76b1\u4e00\u76b1\u7709\u5934\u3002\u201d \u3000\u3000\u80e1\u6590\u9053\uff1a\u201c\u591a\u8c22\u59ec\u5144\u539a\u610f\uff0c\u6211\u6240\u5f97\u7f6a\u7684\u90a3\u4eba\uff0c\u867d\u7136\u4e0d\u662f\u5f53\u4eca\u5929\u5b50\uff0c\u4f46\u548c\u5929\u5b50\u7684\u6743\u52bf\u4e5f\u5dee\u4e0d\u4e86\u591a\u5c11\u3002\u59ec\u5144\uff0c\u6628\u665a\u6211\u89c1\u4f60\u6240\u7ec3\u7684\u4e00\u8def\u534e\u62f3\uff0c\u5176\u4e2d\u4e00\u62db\u8fd4\u8eab\u63d0\u819d\u7a7f\u638c\uff0c\u8d76\u6b65\u3001\u51fb\u6b65\u4e4b\u540e\uff0c\u90a3\u4e00\u4e0b\u8dc3\u6b65\uff0c\u4f55\u4ee5\u5728\u534a\u7a7a\u4e2d\u65b9\u5411\u7565\u53d8\uff1f\u201d\u80e1\u6590\u6240\u8bf4\u7684\u90a3\u4e00\u62db\uff0c\u540d\u53eb\u201c\u91ce\u9a6c\u56de\u4e61\u6512\u8e44\u884c\u201d\uff0c\u4e00\u62db\u4e4b\u4e2d\u52a8\u4f5c\u751a\u662f\u7e41\u590d\u3002 \u3000\u3000\u59ec\u6653\u5cf0\u542c\u4ed6\u4e00\u8bf4\uff0c\u6697\u9053\uff1a\u201c\u597d\u5389\u5bb3\u7684\u773c\u5149\uff01\u6628\u665a\u6211\u7ec3\u8fd9\u4e00\u8def\u534e\u62f3\uff0c\u4ece\u5934\u81f3\u5c3e\u7cbe\u795e\u8d2f\u6ce8\uff0c\u53ea\u6709\u5728\u8fd9\u4e00\u62db\u2018\u91ce\u9a6c\u56de\u4e61\u6512\u8e44\u884c\u2019\u4e0a\uff0c\u8dc3\u8d77\u65f6\u5ffd\u7136\u60f3\u5230\u81c2\u4e0a\u6240\u4e2d\u5267\u6bd2\uff0c\u4e0d\u514d\u5fc3\u795e\u6da3\u6563\u3002\u82e5\u662f\u548c\u4ed6\u5bf9\u654c\u52a8\u624b\uff0c\u8fd9\u7834\u7efd\u7acb\u65f6\u4fbf\u7ed9\u4ed6\u6293\u4f4f\u4e86\u3002\u201d\u8bf4\u9053\uff1a\u201c\u80e1\u5144\u773c\u5149\u5f53\u771f\u9ad8\u660e\uff0c\u5c0f\u5f1f\u4f69\u670d\u5f97\u7d27\uff0c\u90a3\u4e00\u62db\u786e\u662f\u7ec3\u5f97\u4e0d\u5927\u59a5\u5f53\u3002\u201d \u3000\u3000\u4e8e\u662f\u91cd\u884c\u4f7f\u4e86\u4e00\u904d\u3002\u80e1\u6590\u70b9\u5934\u9053\uff1a\u201c\u8fd9\u624d\u5bf9\u4e86\u3002\u5426\u5219\u7167\u6628\u665a\u59ec\u5144\u6240\u4f7f\uff0c\u53ea\u6015\u654c\u4eba\u53ef\u4ee5\u4e58\u865a\u800c\u5165\u3002\u201d \u3000\u3000\u59ec\u6653\u5cf0\u65e2\u77e5\u5e76\u672a\u4e2d\u6bd2\uff0c\u7cbe\u795e\u4e00\u632f\uff0c\u4e8e\u662f\u5c06\u4e00\u5341\u4e8c\u8def\u897f\u5cb3\u534e\u62f3\uff0c\u4ece\u5934\u81f3\u5c3e\u7684\u6f14\u4e86\u51fa\u6765\u3002\u80e1\u6590\u4f9d\u62db\u5b66\u5f0f\uff0c\u867d\u4e0d\u80fd\u5728\u4e00\u65f6\u4e4b\u95f4\u5c3d\u6570\u8bb0\u5168\uff0c\u4f46\u4e5f\u5373\u9886\u4f1a\u5230\u4e86\u6bcf\u4e00\u8def\u62f3\u6cd5\u7684\u7cbe\u4e49\u6240\u5728\uff0c\u8bf4\u9053\uff1a\u201c\u8d35\u6d3e\u7684\u62f3\u6cd5\u535a\u5927\u7cbe\u6df1\uff0c\u597d\u597d\u94bb\u7814\u4e0b\u53bb\uff0c\u786e\u662f\u5a01\u529b\u65e0\u7a77\u3002 \u3000\u3000\u6211\u77a7\u8fd9\u4e00\u5341\u4e8c\u8def\u534e\u62f3\uff0c\u53ea\u987b\u7cbe\u901a\u4e00\u8def\uff0c\u4fbf\u8db3\u4ee5\u626c\u540d\u7acb\u4e07\u3002\u201d \u3000\u3000\u59ec\u6653\u5cf0\u542c\u4ed6\u79f0\u8d5e\u672c\u6d3e\u6b66\u529f\uff0c\u5f88\u662f\u9ad8\u5174\uff0c\u8bf4\u9053\uff1a\u201c\u662f\u554a\u3002\u672c\u95e8\u4e2d\u76f8\u4f20\u6709\u4e24\u53e5\u8bdd\uff0c\u8bf4\u9053\uff1a\u2018\u534e\u62f3\u56db\u5341\u516b\uff0c\u827a\u6210\u884c\u5929\u6daf\u2019\u3002\u56db\u5341\u516b\u8def\u529f\u592b\uff0c\u5206\u4e3a\u4e00\u5341\u516b\u8def\u767b\u5802\u62f3\uff0c\u4e00\u5341\u4e8c\u8def\u5165\u5ba4\u62f3\uff0c\u8fd8\u6709\u4e00\u5341\u516b\u8def\u5200\u67aa\u5251\u68cd\u7684\u5668\u68b0\u529f\u592b\u3002\u672c\u95e8\u5f1f\u5b50\u522b\u8bf4\u2018\u827a\u6210\u2019\u4e24\u5b57\uff0c\u4fbf\u662f\u80fd\u5c06\u56db\u5341\u516b\u8def\u529f\u592b\u5c3d\u6570\u5b66\u5168\u4e86\u7684\uff0c\u4e5f\u662f\u5be5\u5be5\u65e0\u51e0\u3002\u201d \u3000\u3000\u4e24\u4eba\u8bf4\u5230\u6b66\u827a\uff0c\u8c08\u8bba\u6781\u662f\u6295\u5951\uff0c\u6f14\u62db\u8bd5\u5f0f\uff0c\u4e0d\u77e5\u4e0d\u89c9\u95f4\u5df2\u5230\u5348\u540e\u3002\u4e3b\u4eba\u6d3e\u6765\u670d\u4f8d\u80e1\u6590\u7684\u4f8d\u4ec6\u6570\u6b21\u8981\u8bf7\u4ed6\u5403\u996d\uff0c\u4f46\u89c1\u4e8c\u4eba\u7ec3\u5f97\u8d77\u52b2\uff0c\u7ad9\u5728\u4e00\u65c1\uff0c\u4e0d\u6562\u5f00\u53e3\u3002\u5f85\u5f97\u59ec\u6653\u5cf0\u4f7f\u4e00\u62db\u65cb\u98ce\u811a\uff0c\u8dc3\u8d77\u534a\u7a7a\u6a2a\u8e22\u800c\u51fa\uff0c\u95e8\u5916\u7a81\u7136\u6709\u4eba\u559d\u5f69\u9053\uff1a\u201c\u597d\u4e00\u62db\u2018\u98ce\u5377\u9739\u96f3\u4e0a\u4e5d\u5929\u2019\uff01\u201d\u80e1\u6590\u4e00\u770b\uff0c\u5374\u662f\u90a3\u59d3\u8521\u7684\u8001\u8005\uff0c\u5f53\u4e0b\u542b\u7b11\u62b1\u62f3\uff0c\u4e0a\u524d\u62db\u547c\u3002 \u3000\u3000\u6ce8\uff1a\u4e00\u3001\u6e05\u671d\u76f8\u56fd\u592b\u4eba\u4e0b\u6bd2\uff0c\u786e\u6709\u5176\u4e8b\u3002\u8881\u679a\u300a\u968f\u56ed\u8bd7\u8bdd\u300b\u5377\u4e00\u6709\u8bb0\uff1a\u201c\u4f59\u957f\u59d1\u5ac1\u6148\u6eaa\u59da\u6c0f\u3002\u59da\u6bcd\u80fd\u8bd7\uff0c\u51fa\u5916\u4e3a\u5973\u5085\u3002\u5eb7\u7199\u95f4\uff0c\u67d0\u76f8\u56fd\u4ee5\u5343\u91d1\u8058\u5f80\u6559\u5973\u516c\u5b50\u3002\u5230\u5e9c\u4f4f\u82b1\u56ed\u4e2d\uff0c\u6781\u73e0\u5e18\u7389\u5c4f\u4e4b\u4e3d\u3002\u51fa\u62dc\u4e24\u59dd\u5bb9\u6001\u7edd\u4e16\uff0c\u4e0e\u4e4b\u8bed\uff0c\u7686\u5434\u97f3\uff0c\u5e74\u5341\u516d\u4e03\uff0c\u5b66\u7434\u5b66\u8bd7\u9887\u806a\u9896\u3002 \u3000\u3000\u591c\u4f34\u5973\u5085\u7720\uff0c\u65b9\u77e5\u5f85\u5e74\u4e4b\u5973\uff0c\u5c1a\u672a\u4f8d\u5bdd\u4e8e\u76f8\u516c\u4e5f\u3002\u5ffd\u4e00\u5915\u4e8c\u5973\u4ece\u5185\u51fa\uff0c\u9762\u5fae\u7ea2\u3002\u95ee\u4e4b\uff0c\u66f0\uff1a\u5802\u4e0a\u592b\u4eba\u8d50\u996e\u3002\u968f\u89e3\u8863\u5bdd\u3002\u672a\u4e8c\u9f13\uff0c\u4ece\u5e10\u5185\u8dc3\u51fa\uff0c\u62a2\u5730\u547c\u5929\uff0c\u8bed\u5476\u5476\u4e0d\u53ef\u8fa8\u3002\u98a0\u4ec6\u7247\u65f6\uff0c\u4e03\u7a8d\u6d41\u8840\u800c\u6b7b\u3002 \u3000\u3000\u76d6\u592b\u4eba\u559d\u9152\u65f6\uff0c\u4e1a\u5df2\u9e29\u4e4b\u77e3\u3002\u59da\u6bcd\u8e09\u8dc4\u5f03\u8d44\u88c5\u5373\u591c\u9003\u5f52\u3002\u5e38\u544a\u4eba\u4e91\uff0c\u4e8c\u5973\u5e74\u957f\u8005\u5c24\u53ef\u60dc\uff0c\u6709\u81ea\u5632\u4e00\u8054\u4e91\uff1a\u91cf\u6d45\u9152\u75d5\u5148\u4e0a\u9762\uff0c\u5174\u9ad8\u7434\u66f2\u4e0d\u548c\u5f26\u3002\u201d\u6279\u672c\u4e91\uff1a\u201c\u67d0\u76f8\u56fd\u8005\uff0c\u660e\u73e0\u4e5f\u3002\u201d \u3000\u3000\u4e8c\u3001\u798f\u5eb7\u5b89\u4e3a\u4eba\u6deb\u6076\u3002\u4f0d\u62c9\u7eb3\uff08\u4e7e\u9686\u65f6\u4efb\u95fd\u6d59\u603b\u7763\uff09\u4e4b\u5b50\u6279\u6ce8\u300a\u968f\u56ed\u8bd7\u8bdd\u300b\uff0c\u6709\u4e91\uff1a\u201c\u798f\u5eb7\u5b89\u81f3\u6deb\u6781\u6076\uff0c\u4f5c\u5b7d\u592a\u91cd\uff0c\u6d41\u6bd2\u5b50\u5b59\uff0c\u53ef\u4ee5\u6212\u77e3\u3002\u201d\u6309\u8be5\u6279\u6ce8\u5f53\u4f5c\u4e8e\u5609\u5e86\u5e74\u95f4\u3002 \u3000"}),showOnHomepage:!0},{name:"\u7b2c\u5341\u516d\u7ae0\u3000\u9f99\u6f6d\u864e\u7a74",content:(0,a.jsx)(a.Fragment,{children:"\u8fd9\u59d3\u8521\u7684\u8001\u8005\u5355\u540d\u4e00\u4e2a\u5a01\u5b57\uff0c\u5728\u534e\u62f3\u95e8\u4e2d\u8f88\u4efd\u751a\u9ad8\u3002\u4ed6\u89c1\u80e1\u6590\u53bb\u4e86\u8138\u4e0a\u6240\u8499\u9ec4\u5e03\u540e\uff0c\u539f\u6765\u662f\u8fd9\u7b49\u6a21\u6837\u7684\u4e00\u4e2a\u5927\u80e1\u5b50\uff0c\u7ec6\u7ec6\u5411\u4ed6\u6253\u91cf\u4e86\u51e0\u773c\uff0c\u62b1\u62f3\u9053\uff1a\u201c\u542f\u7980\u638c\u95e8\uff0c\u798f\u5927\u5e05\u6709\u6587\u4e66\u5230\u6765\u3002\u201d \u3000\u3000\u80e1\u6590\u5fc3\u4e2d\u4e00\u51db\uff1a\u201c\u8fd9\u4ef6\u4e8b\u7ec8\u4e8e\u7792\u4e0d\u8fc7\u4e86\uff0c\u4e14\u77a7\u4ed6\u600e\u4e48\u8bf4\uff1f\u201d \u3000\u3000\u8138\u4e0a\u4e0d\u52a8\u58f0\u8272\uff0c\u53ea\u201c\u55ef\u201d\u4e86\u4e00\u58f0\u3002\u5374\u542c\u8521\u5a01\u9053\uff1a\u201c\u8fd9\u6587\u4e66\u662f\u7ed9\u5c0f\u8001\u513f\u7684\uff0c\u67e5\u95ee\u672c\u95e8\u7684\u638c\u95e8\u4eba\u63a8\u4e3e\u51fa\u4e86\u6ca1\u6709\uff1f\u5176\u4e2d\u9644\u4e86\u56db\u4efd\u8bf7\u5e16\uff0c\u8bf7\u638c\u95e8\u4eba\u4e8e\u4e2d\u79cb\u6b63\u65e5\uff0c\u5e26\u540c\u672c\u95e8\u4e09\u540d\u5f1f\u5b50\uff0c\u524d\u8d74\u5929\u4e0b\u638c\u95e8\u4eba\u5927\u4f1a\u2026\u2026\u201d \u3000\u3000\u80e1\u6590\u542c\u5230\u8fd9\u91cc\uff0c\u677e\u4e86\u4e00\u53e3\u6c14\uff0c\u5fc3\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\uff0c\u5012\u5413\u4e86\u6211\u4e00\u8df3\u3002\u522b\u7684\u4e5f\u6ca1\u4ec0\u4e48\uff0c\u53ea\u662f\u8fd9\u4e00\u65e5\u4e00\u665a\u4e4b\u4e2d\uff0c\u9a6c\u59d1\u5a18\u4e0d\u80fd\u79fb\u52a8\uff0c\u798f\u5eb7\u5b89\u8fd9\u6587\u4e66\u82e5\u662f\u4e0b\u4ee4\u6293\u4eba\u6765\u7740\uff0c\u9a6c\u59d1\u5a18\u7684\u6027\u547d\u7ec8\u4e8e\u8fd8\u662f\u9001\u5728\u4ed6\u624b\u4e0a\u4e86\u3002\u201d \u3000\u3000\u4ed6\u751f\u6015\u798f\u5eb7\u5b89\u73a9\u751a\u82b1\u6837\uff0c\u8fd8\u662f\u5c06\u90a3\u6587\u4e66\u63a5\u4e86\u8fc7\u6765\uff0c\u7ec6\u7ec6\u77a7\u4e86\u4e00\u904d\uff0c\u8bf4\u9053\uff1a\u201c\u8521\u5e08\u4f2f\uff0c\u59ec\u5e08\u5f1f\uff0c\u4fbf\u8bf7\u4f60\u4eec\u4e24\u4f4d\u76f8\u966a\uff0c\u518d\u52a0\u4e0a\u6211\u5e08\u59b9\uff0c\u54b1\u4eec\u56db\u4e2a\u8d74\u638c\u95e8\u4eba\u5927\u4f1a\u53bb\u3002\u201d\u8521\u5a01\u548c\u59ec\u6653\u5cf0\u5927\u559c\uff0c\u8fde\u8fde\u79f0\u8c22\u3002\u4f8d\u4ec6\u4e0a\u524d\u7980\u9053\uff1a\u201c\u8bf7\u7a0b\u7237\u3001\u8521\u7237\u3001\u59ec\u7237\u4e09\u4f4d\u51fa\u53bb\u7528\u996d\u3002\u201d \u3000\u3000\u80e1\u6590\u70b9\u70b9\u5934\uff0c\u6b63\u8981\u53bb\u53eb\u9192\u7a0b\u7075\u7d20\uff0c\u5ffd\u542c\u5f97\u5979\u5728\u623f\u4e2d\u53eb\u9053\uff1a\u201c\u5927\u54e5\uff0c\u8bf7\u8fc7\u6765\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u4e24\u4f4d\u5148\u8bf7\uff0c\u6211\u968f\u540e\u4fbf\u6765\u3002\u201d\u542c\u5979\u53eb\u58f0\u9887\u4e3a\u7126\u6025\uff0c\u5f53\u4e0b\u5feb\u6b65\u8d70\u5230\u623f\u4e2d\uff0c\u4e00\u6380\u95e8\u5e18\uff0c\u4fbf\u542c\u5f97\u9a6c\u6625\u82b1\u4f4e\u58f0\u53eb\u5524\uff1a\u201c\u6211\u5b69\u5b50\u5462\uff1f\u53eb\u4ed6\u54e5\u513f\u4fe9\u8fc7\u6765\u554a\u2026\u2026\u6211\u8981\u77a7\u77a7\u5b69\u5b50\u2026\u2026\u4ed6\u54e5\u513f\u4fe9\u5462\uff1f\u201d \u3000\u3000\u7a0b\u7075\u7d20\u79c0\u7709\u7d27\u8e59\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5979\u4e00\u5b9a\u8981\u77a7\u5b69\u5b50\uff0c\u8fd9\u4ef6\u4e8b\u4e0d\u5999\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u90a3\u4e24\u4e2a\u5b69\u5b50\u843d\u5728\u90a3\u5fc3\u80a0\u5982\u6b64\u72e0\u6bd2\u7684\u8001\u5987\u624b\u4e2d\uff0c\u54b1\u4eec\u7ec8\u987b\u8bbe\u6cd5\u6551\u4e86\u51fa\u6765\u3002\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u9a6c\u59d1\u5a18\u5f88\u662f\u7126\u8e81\uff0c\u7acb\u65f6\u8981\u89c1\uff0c\u89c1\u4e0d\u7740\u5b69\u5b50\uff0c\u4fbf\u54ed\u558a\u53eb\u5524\u3002\u8fd9\u4e8e\u5979\u75c5\u52bf\u5927\u5927\u4e0d\u59a5\u3002\u201d \u3000\u3000\u80e1\u6590\u6c89\u541f\u9053\uff1a\u201c\u5f85\u6211\u53bb\u529d\u529d\u3002\u201d\u7a0b\u7075\u7d20\u6447\u5934\u9053\uff1a\u201c\u5979\u795e\u667a\u4e0d\u6e05\uff0c\u529d\u4e0d\u4e86\u7684\u3002\u9664\u975e\u9a6c\u4e0a\u5c06\u5b69\u5b50\u62b1\u6765\uff0c\u5426\u5219\u5979\u5fc3\u5934\u90c1\u79ef\uff0c\u6bd2\u8840\u56fa\u7136\u4e0d\u80fd\u5c3d\u9664\uff0c\u836f\u529b\u4e5f\u65e0\u6cd5\u8fbe\u4e8e\u810f\u8151\u3002\u201d \u3000\u3000\u80e1\u6590\u7ed5\u5ba4\u5f77\u5fa8\uff0c\u4e00\u65f6\u82e6\u65e0\u5999\u7b56\uff0c\u8bf4\u9053\uff1a\u201c\u4fbf\u662f\u5192\u9669\u518d\u5165\u798f\u5927\u5e05\u5e9c\u53bb\u62a2\u5b69\u5b50\uff0c\u6700\u5feb\u4e5f\u5f97\u7b49\u5230\u4eca\u665a\u3002\u201d\u7a0b\u7075\u7d20\u5413\u4e86\u4e00\u8df3\uff0c\u9053\uff1a\u201c\u518d\u8fdb\u798f\u5e9c\u53bb\uff0c\u90a3\u4e0d\u662f\u9001\u6b7b\u4e48\uff1f\u201d\u80e1\u6590\u82e6\u7b11\u4e86\u4e00\u4e0b\uff0c\u4ed6\u4f55\u5c1d\u4e0d\u77e5\u6628\u665a\u95f9\u51fa\u4e86\u8fd9\u4e48\u60ca\u5929\u52a8\u5730\u7684\u4e00\u4ef6\u4e8b\uff0c\u4eca\u65e5\u798f\u5eb7\u5b89\u5e9c\u4e2d\u81ea\u662f\u6212\u5907\u68ee\u4e25\uff0c\u4fbf\u8981\u8e0f\u8fdb\u4e00\u6b65\u4e5f\u662f\u5343\u96be\u4e07\u96be\uff0c\u5982\u4f55\u80fd\u518d\u62a2\u5f97\u8fd9\u4e24\u4e2a\u5b69\u5b50\u51fa\u6765\uff1f\u82e5\u6709\u6570\u5341\u4e2a\u6b66\u827a\u9ad8\u5f3a\u4e4b\u4eba\u540c\u65f6\u4e0b\u624b\uff0c\u6216\u8005\u5c1a\u80fd\u6210\u4e8b\uff0c\u53ea\u51ed\u4ed6\u5355\u67aa\u5339\u9a6c\uff0c\u518d\u52a0\u4e0a\u7a0b\u7075\u7d20\uff0c\u6700\u591a\u52a0\u4e0a\u59ec\u6653\u5cf0\uff0c\u4e09\u4e2a\u4eba\u96be\u9053\u771f\u6709\u901a\u5929\u7684\u672c\u4e8b\uff1f \u3000\u3000\u8fc7\u4e86\u826f\u4e45\uff0c\u53ea\u542c\u5f97\u9a6c\u6625\u82b1\u4e0d\u4f4f\u53eb\u5524\uff1a\u201c\u5b69\u5b50\uff0c\u5feb\u8fc7\u6765\uff0c\u5988\u5fc3\u91cc\u4e0d\u8212\u670d\u3002\u4f60\u4eec\u5230\u54ea\u513f\u53bb\u4e86\uff1f\u5230\u54ea\u513f\u53bb\u4e86\uff1f\u201d\u80e1\u6590\u76b1\u7709\u9053\uff1a\u201c\u4e8c\u59b9\uff0c\u4f60\u8bf4\u600e\u4e48\u529e\uff1f\u201d\u7a0b\u7075\u7d20\u6447\u5934\u9053\uff1a\u201c\u5979\u8fd9\u822c\u7275\u809a\u6302\u80a0\uff0c\u4e0d\u4f4f\u53e3\u7684\u53eb\u5524\uff0c\u4e0d\u5230\u4e09\u65e5\uff0c\u4e0d\u514d\u6bd2\u6c14\u653b\u5fc3\u3002\u54b1\u4eec\u53ea\u6709\u5c3d\u529b\u800c\u4e3a\uff0c\u5f53\u771f\u6551\u4e0d\u4e86\uff0c\u90a3\u4e5f\u662f\u5929\u6570\u4f7f\u7136\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u5148\u5403\u996d\u53bb\uff0c\u4e00\u4f1a\u518d\u6765\u5546\u91cf\u3002\u201d \u3000\u3000\u996d\u540e\u7a0b\u7075\u7d20\u53c8\u66ff\u9a6c\u6625\u82b1\u7528\u4e86\u4e00\u6b21\u836f\uff0c\u53ea\u542c\u5979\u5374\u53eb\u8d77\u798f\u5eb7\u5b89\u6765\uff1a\u201c\u5eb7\u54e5\uff0c\u5eb7\u54e5\uff0c\u600e\u5730\u4f60\u4e0d\u776c\u6211\u554a\uff1f\u4f60\u628a\u54b1\u4eec\u7684\u4e24\u4e2a\u4e56\u513f\u5b50\u62b1\u8fc7\u6765\uff0c\u6211\u8981\u4eb2\u4eb2\u4ed6\u54e5\u513f\u4fe9\u3002\u201d\u53ea\u628a\u80e1\u6590\u542c\u5f97\u53c8\u662f\u6124\u6012\uff0c\u53c8\u662f\u7126\u6025\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u62c9\u4e86\u62c9\u4ed6\u8863\u8896\uff0c\u8d70\u5230\u623f\u5916\u7684\u5c0f\u5ba4\u4e4b\u4e2d\uff0c\u8138\u8272\u90d1\u91cd\uff0c\u8bf4\u9053\uff1a\u201c\u5927\u54e5\uff0c\u6211\u8ddf\u4f60\u8bf4\u8fc7\u7684\u8bdd\uff0c\u6709\u4e0d\u7b97\u7684\u6ca1\u6709\uff1f\u201d\u80e1\u6590\u597d\u751f\u5947\u602a\uff1a\u201c\u5e72\u4e48\u95ee\u8d77\u8fd9\u53e5\u8bdd\u6765\uff1f\u201d\u6447\u5934\u9053\uff1a\u201c\u6ca1\u6709\u554a\u3002\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u597d\u3002\u6211\u6709\u4e00\u53e5\u8bdd\uff0c\u4f60\u597d\u597d\u542c\u7740\u3002\u5018\u82e5\u4f60\u518d\u8fdb\u798f\u5eb7\u5b89\u5e9c\u4e2d\u53bb\u62a2\u9a6c\u59d1\u5a18\u7684\u513f\u5b50\uff0c\u4f60\u53e6\u8bf7\u540d\u533b\u6765\u6cbb\u5979\u7684\u6bd2\u7f62\u3002\u6211\u9a6c\u4e0a\u4fbf\u56de\u5357\u65b9\u53bb\u3002\u201d \u3000\u3000\u80e1\u6590\u4e00\u6115\uff0c\u5c1a\u672a\u7b54\u8bdd\uff0c\u7a0b\u7075\u7d20\u5df2\u7fe9\u7136\u8fdb\u623f\u3002\u80e1\u6590\u77e5\u5979\u8fd9\u756a\u8bdd\u5168\u662f\u4e3a\u4e86\u987e\u5ff5\u7740\u4ed6\uff0c\u6599\u4ed6\u773c\u770b\u5982\u6b64\u60c5\u52bf\uff0c\u5b9a\u4f1a\u5192\u9669\u518d\u5165\u798f\u5e9c\uff0c\u6b64\u4e3e\u9664\u4e86\u8d54\u4e0a\u4e00\u6761\u6027\u547d\u4e4b\u5916\uff0c\u51b3\u65e0\u597d\u5904\u3002\u4ed6\u81ea\u5df1\u539f\u4e5f\u60f3\u5230\uff0c\u53ef\u662f\u6b64\u4e8b\u89e6\u52a8\u4e86\u4ed6\u7684\u4fa0\u4e49\u5fc3\u80a0\uff0c\u5fc6\u8d77\u6614\u5e74\u5728\u5546\u5bb6\u5821\u88ab\u64d2\u540a\u6253\uff0c\u9a6c\u6625\u82b1\u4e0d\u4f4f\u51fa\u8a00\u6c42\u60c5\u3002\u6709\u6069\u4e0d\u62a5\uff0c\u975e\u4e08\u592b\u4e5f\uff0c\u4ed6\u5df2\u7136\u51b3\u610f\u4e00\u8bd5\uff0c\u4f46\u7a0b\u7075\u7d20\u5ffd\u51fa\u6b64\u8a00\uff0c\u5018\u82e5\u81ea\u5df1\u62da\u6b7b\u6551\u4e86\u4e24\u4e2a\u5b69\u5b50\u51fa\u6765\uff0c\u7a0b\u7075\u7d20\u5374\u4e00\u6012\u800c\u53bb\uff0c\u90a3\u53ef\u53c8\u7cdf\u4e86\u3002 \u3000\u3000\u4e00\u65f6\u4e4b\u95f4\u8e0c\u8e87\u65e0\u8ba1\uff0c\u4fe1\u6b65\u8d70\u4e0a\u5927\u8857\uff0c\u4e0d\u77e5\u4e0d\u89c9\u95f4\u4fbf\u6765\u5230\u798f\u5eb7\u5b89\u5e9c\u9644\u8fd1\uff0c\u4f46\u89c1\u6bcf\u9694\u4e94\u6b65\u5341\u6b65\uff0c\u4fbf\u662f\u4e24\u4e2a\u536b\u58eb\uff0c\u4eba\u4eba\u63d0\u7740\u5175\u5203\uff0c\u5b88\u536b\u4e25\u5bc6\u4e4b\u6781\uff0c\u522b\u8bf4\u95ef\u8fdb\u5e9c\u53bb\uff0c\u53ea\u8981\u518d\u8d70\u8fd1\u51e0\u6b65\uff0c\u536b\u58eb\u4fbf\u8981\u8fc7\u6765\u76d8\u67e5\u3002 \u3000\u3000\u80e1\u6590\u4e0d\u6562\u591a\u803d\uff0c\u5fc3\u4e2d\u95f7\u95f7\u4e0d\u4e50\uff0c\u8f6c\u8fc7\u4e24\u6761\u6a2a\u8857\uff0c\u89c1\u6709\u4e00\u5ea7\u9152\u697c\uff0c\u4fbf\u4e0a\u697c\u53bb\u72ec\u81ea\u5c0f\u914c\u3002\u521a\u559d\u5f97\u4e24\u676f\uff0c\u5ffd\u542c\u9694\u623f\u4e2d\u4e00\u4eba\u9053\uff1a\u201c\u6c6a\u5927\u54e5\uff0c\u4eca\u513f\u54b1\u4eec\u559d\u5230\u8fd9\u513f\u4e3a\u6b62\uff0c\u5f85\u4f1a\u5c31\u8981\u5f53\u503c\uff0c\u559d\u5f97\u8138\u4e0a\u9152\u7cdf\u4e00\u822c\u7684\uff0c\u53ef\u4e0d\u5927\u7f8e\u3002\u201d\u53e6\u4eba\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u597d\uff0c\u54b1\u4eec\u518d\u5e72\u4e09\u676f\u4fbf\u5403\u996d\u3002\u201d \u3000\u3000\u80e1\u6590\u4e00\u542c\u6b64\u4eba\u58f0\u97f3\uff0c\u6b63\u662f\u6c6a\u94c1\u9e57\uff0c\u5fc3\u60f3\uff1a\u201c\u5929\u4e0b\u4e8b\u771f\u6709\u8fd9\u822c\u5de7\uff0c\u5c45\u7136\u53c8\u5728\u8fd9\u91cc\u649e\u4e0a\u4ed6\u3002\u201d\u8f6c\u5ff5\u4e00\u60f3\uff0c\u5374\u4e5f\u4e0d\u8db3\u4e3a\u5947\uff0c\u4ed6\u4eec\u8bf4\u5f85\u4f1a\u4fbf\u8981\u5f53\u503c\uff0c\u81ea\u662f\u53bb\u798f\u5eb7\u5b89\u5e9c\u8f6e\u73ed\u5b88\u536b\u3002\u8fd9\u662f\u798f\u5e9c\u9644\u8fd1\u6700\u8003\u7a76\u7684\u4e00\u5bb6\u9152\u697c\uff0c\u4ed6\u4eec\u5728\u5b88\u536b\u4e4b\u524d\uff0c\u5148\u6765\u559d\u4e0a\u4e09\u676f\uff0c\u90a3\u4e5f\u5e73\u5e38\u5f97\u7d27\u3002\u5018\u82e5\u6c6a\u94c1\u9e57\u8fd9\u79cd\u4eba\u5f53\u503c\u4e4b\u524d\u4e0d\u5148\u8212\u8212\u670d\u670d\u7684\u559d\u4e0a\u4e00\u573a\uff0c\u90a3\u624d\u53eb\u5947\u5462\u3002 \u3000\u3000\u53ea\u542c\u53e6\u4e00\u4eba\u9053\uff1a\u201c\u6c6a\u5927\u54e5\uff0c\u4f60\u8bf4\u4f60\u8bc6\u5f97\u80e1\u6590\u3002\u4ed6\u5230\u5e95\u662f\u600e\u4e48\u6837\u4e00\u4e2a\u4eba\uff1f\u201d\u80e1\u6590\u542c\u4ed6\u63d0\u5230\u81ea\u5df1\u540d\u5b57\uff0c\u4e0d\u7981\u4e00\u51db\uff0c\u66f4\u662f\u51dd\u795e\u9759\u542c\u3002 \u3000\u3000\u53ea\u542c\u6c6a\u94c1\u9e57\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8bf4\u5230\u80e1\u6590\u6b64\u4eba\uff0c\u5c0f\u5c0f\u5e74\u7eaa\uff0c\u4e0d\u4f46\u6b66\u827a\u9ad8\u5f3a\uff0c\u800c\u4e14\u7231\u4ea4\u670b\u53cb\uff0c\u771f\u662f\u4e00\u6761\u597d\u6c49\u5b50\u3002\u53ef\u501f\u4ed6\u603b\u662f\u8981\u548c\u5927\u5e05\u4f5c\u5bf9\uff0c\u6628\u665a\u66f4\u95ef\u5230\u5e9c\u91cc\u53bb\u884c\u523a\u5927\u5e05\uff0c\u771f\u4e0d\u77e5\u4ece\u4f55\u8bf4\u8d77\uff1f\u201d\u90a3\u4eba\u7b11\u9053\uff1a\u201c\u6c6a\u5927\u54e5\uff0c\u4f60\u867d\u8bc6\u5f97\u80e1\u6590\uff0c\u53ef\u662f\u504f\u6ca1\u751f\u5c31\u4e00\u4e2a\u5347\u5b98\u53d1\u8d22\u7684\u547d\u513f\uff0c\u5426\u5219\u7684\u8bdd\uff0c\u54b1\u4eec\u559d\u5b8c\u4e86\u9152\uff0c\u51fa\u5f97\u8857\u53bb\uff0c\u504f\u5de7\u649e\u89c1\u4e86\u4ed6\uff0c\u54b1\u54e5\u513f\u4fe9\u5c06\u4ed6\u624b\u5230\u64d2\u6765\uff0c\u5c82\u4e0d\u662f\u5927\u5927\u7684\u4e00\u4ef6\u529f\u52b3\uff1f\u201d\u6c6a\u94c1\u9e57\u7b11\u9053\uff1a\u201c\u54c8\u54c8\uff0c\u4f60\u5012\u8bf4\u5f97\u8f7b\u677e\u60ec\u610f\uff01\u51ed\u4f60\u5f20\u4e5d\u7684\u672c\u9886\u54ea\uff0c\u4fbf\u662f\u6709\u4e8c\u5341\u4e2a\uff0c\u4e5f\u672a\u5fc5\u80fd\u62ff\u5f97\u4f4f\u4ed6\u3002\u201d\u90a3\u5f20\u4e5d\u4e00\u542c\u6b64\u8a00\uff0c\u5fc3\u4e2d\u607c\u4e86\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u4f60\u5462\uff0c\u8981\u51e0\u4e2a\u6c6a\u94c1\u9e57\u624d\u62ff\u5f97\u4f4f\u4ed6\uff1f\u201d\u6c6a\u94c1\u9e57\u9053\uff1a\u201c\u6211\u662f\u66f4\u52a0\u4e0d\u6210\u5566\uff0c\u4fbf\u6709\u56db\u5341\u4e2a\u6211\u8fd9\u79cd\u8113\u5305\uff0c\u4e5f\u4e0d\u7ba1\u7528\u3002\u201d\u5f20\u4e5d\u51b7\u7b11\u9053\uff1a\u201c\u4ed6\u5f53\u771f\u4fbf\u6709\u4e09\u5934\u516d\u81c2\uff0c\u8bf4\u5f97\u8fd9\u822c\u5389\u5bb3\u3002\u201d \u3000\u3000\u80e1\u6590\u542c\u4ed6\u4e8c\u4eba\u8bdd\u4e0d\u6295\u673a\uff0c\u5fc3\u5ff5\u4e00\u52a8\uff0c\u773c\u89c1\u65f6\u673a\u7a0d\u7eb5\u5373\u901d\uff0c\u5f53\u4e0b\u66f4\u4e0d\u518d\u601d\uff0c\u63ed\u8fc7\u95e8\u5e18\uff0c\u8e0f\u6b65\u8d70\u8fdb\u90bb\u623f\uff0c\u8bf4\u9053\uff1a\u201c\u6c6a\u5927\u54e5\uff0c\u4f60\u5728\u8fd9\u513f\u559d\u9152\u554a\uff01\u5582\uff0c\u8fd9\u4f4d\u662f\u5f20\u5927\u54e5\u3002\u5c0f\u4e8c\uff0c\u5c0f\u4e8c\uff0c\u628a\u6211\u7684\u5ea7\u513f\u642c\u5230\u8fd9\u91cc\u6765\u3002\u201d \u3000\u3000\u6c6a\u94c1\u9e57\u548c\u5f20\u4e5d\u4e00\u89c1\u80e1\u6590\uff0c\u90fd\u662f\u4e00\u6014\uff0c\u5fc3\u60f3\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u54b1\u4eec\u53ef\u4e0d\u76f8\u8bc6\u554a\uff1f\u201d\u6c6a\u94c1\u9e57\u867d\u542c\u7740\u4ed6\u8bdd\u58f0\u6709\u4e9b\u719f\u7a14\uff0c\u4f46\u89c1\u4ed6\u866c\u9aef\u6ee1\u8138\uff0c\u90a3\u60f3\u5f97\u5230\u662f\u4ed6\uff1f\u80e1\u6590\u53c8\u9053\uff1a\u201c\u521a\u624d\u6211\u9047\u89c1\u5468\u94c1\u9e6a\u5468\u5927\u54e5\uff0c\u66fe\u94c1\u9e25\u66fe\u4e8c\u54e5\uff0c\u5728\u805a\u82f1\u697c\u559d\u4e86\u51e0\u676f\uff0c\u8fd8\u8bf4\u8d77\u4f60\u6c6a\u5927\u54e5\u5462\u3002\u201d\u6c6a\u94c1\u9e57\u542b\u7cca\u7b54\u5e94\uff0c\u7aed\u529b\u601d\u7d22\u6b64\u4eba\u662f\u8c01\uff0c\u542c\u4ed6\u8bf4\u6765\uff0c\u548c\u5468\u5e08\u54e5\u3001\u66fe\u5e08\u54e5\u4ed6\u4eec\u90fd\u662f\u719f\u8bc6\uff0c\u5e94\u8be5\u4e0d\u662f\u5916\u4eba\uff0c\u600e\u5730\u4e00\u65f6\u7adf\u60f3\u4e0d\u8d77\u6765\uff1f\u4e0d\u4f4f\u5728\u5fc3\u4e2d\u6697\u9a82\u81ea\u5df1\u80e1\u6d82\u3002 \u3000\u3000\u5e97\u4f34\u6446\u597d\u5ea7\u5934\u3002\u80e1\u6590\u9053\uff1a\u201c\u4eca\u513f\u5c0f\u5f1f\u4f5c\u4e1c\uff0c\u5f88\u4e45\u6ca1\u8ddf\u6c6a\u5927\u54e5\u3001\u5f20\u5927\u54e5\u559d\u4e00\u676f\u4e86\u3002\u201d\u638f\u51fa\u5341\u4e24\u94f6\u5b50\u5411\u5e97\u4f34\u4e00\u629b\uff0c\u9053\uff1a\u201c\u7ed9\u5b58\u5728\u67dc\u4e0a\uff0c\u6709\u62ff\u624b\u7cbe\u81f4\u7684\u9152\u83dc\uff0c\u53ea\u7ba1\u4f5c\u6765\u3002\u201d\u90a3\u5e97\u4f34\u89c1\u4ed6\u624b\u9762\u8c6a\u9614\uff0c\u767b\u65f6\u5341\u5206\u606d\u8c28\uff0c\u4e00\u53e0\u8fde\u58f0\u7684\u5429\u5490\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u4e0d\u4e45\u9152\u83dc\u9646\u7eed\u9001\u4e0a\uff0c\u80e1\u6590\u8c08\u7b11\u98ce\u751f\uff0c\u8bf4\u8d77\u6765\u79e6\u8010\u4e4b\u3001\u6bb7\u4ef2\u7fd4\u3001\u738b\u5251\u82f1\u3001\u738b\u5251\u6770\u5144\u5f1f\u8fd9\u5e72\u4eba\u90fd\u5f88\u719f\u7edc\uff0c\u4e00\u4f1a\u513f\u8bf4\u6b66\u827a\uff0c\u4e00\u4f1a\u513f\u8bf4\u8d4c\u535a\uff0c\u4f3c\u4e4e\u4e2a\u4e2a\u90fd\u662f\u4ed6\u7684\u77e5\u4ea4\u670b\u53cb\u3002\u6c6a\u94c1\u9e57\u8001\u5927\u7eb3\u95f7\uff0c\u4eba\u5bb6\u8fd9\u822c\u4eb2\u70ed\uff0c\u5018\u82e5\u5f00\u53e3\u95ee\u4ed6\u59d3\u540d\uff0c\u90a3\u53ef\u662f\u5927\u5927\u5931\u793c\uff0c\u4f46\u6b64\u4eba\u5230\u5e95\u662f\u8c01\uff0c\u4fbf\u662f\u60f3\u7834\u4e86\u8111\u888b\uff0c\u4e5f\u60f3\u4e0d\u5230\u534a\u70b9\u56e0\u5934\u3002\u5f20\u4e5d\u53ea\u9053\u80e1\u6590\u662f\u6c6a\u94c1\u9e57\u7684\u8001\u53cb\uff0c\u89c1\u4ed6\u51fa\u624b\u723d\u5feb\uff0c\u6765\u5934\u663e\u53c8\u4e0d\u5c0f\uff0c\u81ea\u4e5f\u4e50\u5f97\u53e8\u6270\u4ed6\u4e00\u987f\u3002 \u3000\u3000\u559d\u4e86\u4e00\u4f1a\u9152\uff0c\u83dc\u80b4\u90fd\u5df2\u4e0a\u9f50\uff0c\u6c6a\u94c1\u9e57\u5b9e\u5728\u5fcd\u8010\u4e0d\u4f4f\u4e86\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8fd9\u4f4d\u5927\u54e5\u6028\u6211\u65e0\u793c\uff0c\u6211\u8d8a\u6d3b\u8d8a\u662f\u80e1\u6d82\u5566\u3002\u201d\u8bf4\u7740\u4f38\u624b\u5728\u81ea\u5df1\u7684\u989d\u5934\u4e0a\u91cd\u91cd\u4e00\u51fb\uff0c\u53c8\u9053\uff1a\u201c\u4e00\u65f6\u4e4b\u95f4\u6211\u7adf\u60f3\u4e0d\u8d77\u4f60\u8001\u54e5\u7684\u540d\u5b57\uff0c\u771f\u662f\u8be5\u6b7b\u4e4b\u6781\u4e86\u3002\u201d \u3000\u3000\u80e1\u6590\u7b11\u9053\uff1a\u201c\u6c6a\u5927\u54e5\u771f\u662f\u8d35\u4eba\u591a\u5fd8\u4e8b\u3002\u6628\u513f\u665a\u4e0a\uff0c\u4f60\u4e0d\u662f\u8fd8\u5728\u820d\u4e0b\u5403\u996d\u5417\uff1f\u53ea\u53ef\u60dc\u4e00\u573a\u724c\u4e5d\u6ca1\u63a8\u6210\uff0c\u5012\u5f04\u5f97\u5468\u5927\u54e5\u8ddf\u4eba\u5bb6\u52a8\u624b\u8fc7\u62db\uff0c\u4f24\u4e86\u548c\u6c14\u3002\u201d\u6c6a\u94c1\u9e57\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u2026\u2026\u201d\u80e1\u6590\u7b11\u9053\uff1a\u201c\u5c0f\u5f1f\u4fbf\u662f\u80e1\u6590\uff01\u201d \u3000\u3000\u6b64\u8a00\u4e00\u51fa\uff0c\u6c6a\u94c1\u9e57\u548c\u5f20\u4e5d\u731b\u5730\u4e00\u9f50\u7ad9\u8d77\uff0c\u60ca\u5f97\u8bdd\u4e5f\u8bf4\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u80e1\u6590\u7b11\u9053\uff1a\u201c\u600e\u4e48\uff1f\u5c0f\u5f1f\u88c5\u4e86\u4e00\u90e8\u80e1\u5b50\uff0c\u6c6a\u5927\u54e5\u4fbf\u4e0d\u8ba4\u5f97\u4e86\u4e48\uff1f\u201d\u6c6a\u94c1\u9e57\u4f4e\u58f0\u9053\uff1a\u201c\u6084\u58f0\uff01\u80e1\u5927\u54e5\uff0c\u57ce\u4e2d\u5230\u5904\u90fd\u5728\u627e\u4f60\uff0c\u4f60\u6562\u5982\u6b64\u5927\u80c6\uff0c\u5c45\u7136\u8fd8\u5230\u8fd9\u91cc\u6765\u559d\u9152\uff1f\u201d\u80e1\u6590\u7b11\u9053\uff1a\u201c\u6015\u4ec0\u4e48\uff1f \u3000\u3000\u8fde\u4f60\u6c6a\u5927\u54e5\u4e5f\u4e0d\u8ba4\u5f97\u6211\uff0c\u65c1\u4eba\u600e\u8ba4\u5f97\u51fa\u6765\uff1f\u201d\u6c6a\u94c1\u9e57\u9053\uff1a\u201c\u5317\u4eac\u57ce\u91cc\u662f\u4e0d\u80fd\u518d\u803d\u4e86\uff0c\u4f60\u5feb\u5feb\u51fa\u57ce\u53bb\u5427\uff1f\u76d8\u7f20\u591f\u4e0d\u591f\uff1f\u201d \u3000\u3000\u80e1\u6590\u9053\uff1a\u201c\u591a\u8c22\u6c6a\u5927\u54e5\u53e4\u9053\u70ed\u80a0\uff0c\u5c0f\u5f1f\u94f6\u5b50\u8db3\u7528\u4e86\u3002\u201d\u5fc3\u60f3\uff1a\u201c\u6b64\u4eba\u6027\u5b50\u7c97\u9c81\uff0c\u5012\u662f\u4e2a\u539a\u9053\u4e4b\u4eba\u3002\u201d\u90a3\u5f20\u4e5d\u5374\u8138\u4e0a\u53d8\u8272\uff0c\u4f4e\u4e0b\u4e86\u5934\u4e00\u8a00\u4e0d\u53d1\u3002 \u3000\u3000\u6c6a\u94c1\u9e57\u53c8\u9053\uff1a\u201c\u4eca\u65e5\u57ce\u95e8\u53e3\u76d8\u67e5\u5f97\u7d27\uff0c\u4f60\u51fa\u57ce\u65f6\u522b\u8981\u9732\u51fa\u7834\u7efd\uff0c\u8fd8\u662f\u6211\u548c\u5f20\u5927\u54e5\u9001\u4f60\u51fa\u57ce\u4e3a\u5999\u3002\u90a3\u4f4d\u7a0b\u59d1\u5a18\u5462\uff1f\u201d\u80e1\u6590\u6447\u5934\u9053\uff1a\u201c\u6211\u6682\u4e14\u4e0d\u51fa\u57ce\u3002\u6211\u8fd8\u6709\u4e00\u7b14\u5e10\u8981\u8ddf\u798f\u5927\u5e05\u7b97\u4e00\u7b97\u3002\u201d \u3000\u3000\u5f20\u4e5d\u542c\u5230\u8fd9\u91cc\uff0c\u8138\u4e0a\u795e\u8272\u66f4\u662f\u663e\u5f97\u5f02\u6837\u3002 \u3000\u3000\u6c6a\u94c1\u9e57\u9053\uff1a\u201c\u80e1\u5927\u54e5\uff0c\u6211\u672c\u9886\u662f\u8fdc\u8fdc\u7684\u4e0d\u53ca\u4f60\uff0c\u53ef\u662f\u6709\u4e00\u53e5\u826f\u8a00\u76f8\u529d\u3002\u798f\u5927\u5e05\u6743\u52bf\u718f\u5929\uff0c\u4f60\u4fbf\u5f53\u771f\u8ddf\u4ed6\u6709\u4ec7\uff0c\u53c8\u600e\u6597\u4ed6\u5f97\u8fc7\uff1f\u6211\u5403\u4ed6\u7684\u996d\uff0c\u5728\u4ed6\u95e8\u4e0b\u529e\u4e8b\uff0c\u4e5f\u4e0d\u80fd\u4e00\u5473\u62a4\u7740\u4f60\u3002\u4eca\u65e5\u5192\u4e2a\u9669\u9001\u4f60\u51fa\u57ce\u3002\u4f60\u5feb\u5feb\u8d70\u5427\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u4e0d\u6210\uff0c\u6c6a\u5927\u54e5\uff0c\u4f60\u53ef\u77e5\u6211\u4e3a\u4ec0\u4e48\u5f97\u7f6a\u4e86\u798f\u5927\u5e05\uff1f\u201d\u6c6a\u94c1\u9e57\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\uff0c\u6b63\u60f3\u95ee\u4f60\u3002\u201d \u3000\u3000\u80e1\u6590\u5f53\u4e0b\u5c06\u798f\u5eb7\u5b89\u5982\u4f55\u5728\u5546\u5bb6\u5821\u7ed3\u8bc6\u9a6c\u6625\u82b1\uff0c\u5982\u4f55\u548c\u5979\u751f\u4e0b\u4e24\u4e2a\u5b69\u5b50\uff0c\u6628\u665a\u9a6c\u6625\u82b1\u5982\u4f55\u4e2d\u6bd2\u7b49\u60c5\u4e00\u4e00\u4f4e\u58f0\u8bf4\u4e86\uff0c\u53c8\u8bf4\u5230\u81ea\u5df1\u5982\u4f55\u76f8\u6551\uff0c\u9a6c\u6625\u82b1\u5982\u4f55\u601d\u5ff5\u513f\u5b50\uff0c\u547d\u5728\u5782\u5371\uff0c\u81ea\u5df1\u867d\u7136\u5e72\u5192\u4e07\u9669\uff0c\u4e5f\u8981\u5c06\u90a3\u4e24\u4e2a\u5b69\u5b50\u6551\u4e86\u51fa\u6765\u53bb\u4ea4\u7ed9\u5979\u3002 \u3000\u3000\u6c6a\u94c1\u9e57\u8d8a\u542c\u8d8a\u6012\uff0c\u62cd\u684c\u8bf4\u9053\uff1a\u201c\u539f\u6765\u8fd9\u4eba\u5fc3\u80a0\u5982\u6b64\u72e0\u6bd2\uff01 \u3000\u3000\u80e1\u5927\u54e5\uff0c\u4f60\u82f1\u96c4\u4fa0\u4e49\uff0c\u5f53\u771f\u4ee4\u4eba\u597d\u751f\u94a6\u4f69\u3002\u53ef\u662f\u798f\u5927\u5e05\u5e9c\u4e2d\u6212\u5907\u4e25\u5bc6\uff0c\u4e0d\u77e5\u6709\u591a\u5c11\u9ad8\u624b\u56db\u4e0b\u5b88\u536b\uff0c\u8981\u6551\u90a3\u4e24\u5b69\u5b50\uff0c\u8fd9\u4f1a\u513f\u662f\u60f3\u4e5f\u4f11\u60f3\u3002\u53ea\u597d\u5f85\u8fd9\u4ef6\u4e8b\u677e\u4e86\u4e0b\u6765\uff0c\u6162\u6162\u518d\u60f3\u6cd5\u5b50\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u6211\u5374\u6709\u4e2a\u8ba1\u8f83\u5728\u6b64\uff0c\u54b1\u4eec\u501f\u7528\u4e86\u5f20\u5927\u54e5\u7684\u670d\u8272\uff0c\u8ba9\u6211\u626e\u6210\u536b\u58eb\uff0c\u9ed1\u591c\u4e4b\u4e2d\uff0c\u7531\u4f60\u9886\u7740\u5230\u5e9c\u91cc\u53bb\u52a8\u624b\u3002\u201d \u3000\u3000\u5f20\u4e5d\u8138\u8272\u5927\u53d8\uff0c\u970d\u5730\u7ad9\u8d77\uff0c\u624b\u6309\u5200\u67c4\u3002\u80e1\u6590\u5de6\u624b\u6301\u7740\u9152\u676f\u559d\u4e86\u53e3\u9152\uff0c\u53f3\u624b\u6b63\u4f38\u51fa\u7b77\u5b50\u53bb\u631f\u83dc\uff0c\u7a81\u7136\u95f4\u5de6\u624b\u4e00\u626c\uff0c\u534a\u676f\u9152\u6cfc\u5411\u5f20\u4e5d\u773c\u4e2d\u3002\u5f20\u4e5d\u201c\u554a\u201d\u7684\u4e00\u58f0\u60ca\u547c\uff0c\u4f38\u624b\u53bb\u63c9\u3002\u80e1\u6590\u7b77\u5b50\u63a2\u51fa\uff0c\u5728\u4ed6\u80f8\u53e3\u201c\u795e\u85cf\u201d\u548c\u201c\u4e2d\u5ead\u201d\u4e24\u7a74\u4e0a\u5404\u6233\u4e86\u4e00\u4e0b\u3002\u5f20\u4e5d\u8eab\u5b50\u4e00\u8f6f\uff0c\u767b\u65f6\u5012\u5728\u6905\u4e0a\u3002 \u3000\u3000\u5e97\u5c0f\u4e8c\u542c\u5f97\u58f0\u97f3\uff0c\u8fc7\u6765\u5bdf\u770b\u3002\u80e1\u6590\u9053\uff1a\u201c\u8fd9\u4f4d\u603b\u7237\u559d\u9189\u4e86\uff0c\u5f97\u627e\u4e2a\u5e97\u623f\u6b47\u6b47\u3002\u201d\u5e97\u5c0f\u4e8c\u9053\uff1a\u201c\u8fc7\u53bb\u4e94\u5bb6\u95e8\u9762\uff0c\u4fbf\u662f\u5b89\u8fdc\u8001\u5e97\u3002\u5c0f\u4eba\u6276\u8fd9\u4f4d\u603b\u7237\u8fc7\u53bb\u5427\uff01\u201d\u80e1\u6590\u9053\uff1a\u201c\u597d\uff01\u201d\u53c8\u8d4f\u4e86\u4ed6\u4e94\u94b1\u94f6\u5b50\u3002\u90a3\u5e97\u5c0f\u4e8c\u6b22\u5929\u559c\u5730\uff0c\u6276\u7740\u5f20\u4e5d\u5230\u90a3\u5ba2\u5e97\u4e4b\u4e2d\u3002\u80e1\u6590\u8981\u4e86\u4e00\u95f4\u4e0a\u623f\uff0c\u95e9\u4e0a\u4e86\u95e8\uff0c\u4f38\u6307\u53c8\u70b9\u4e86\u5f20\u4e5d\u8eab\u4e0a\u4e09\u5904\u7a74\u9053\uff0c\u4ee4\u4ed6\u5341\u4e8c\u4e2a\u65f6\u8fb0\u4e4b\u4e2d\uff0c\u52a8\u5f39\u4e0d\u5f97\u3002 \u3000\u3000\u6c6a\u94c1\u9e57\u5fc3\u4e2d\u72b9\u4f3c\u5341\u4e94\u4e2a\u540a\u6876\u6253\u6c34\uff0c\u4e03\u4e0a\u516b\u843d\uff0c\u773c\u89c1\u80e1\u6590\u884c\u4fa0\u4ed7\u4e49\uff0c\u505a\u4e8b\u723d\u5feb\u660e\u51b3\uff0c\u4e0d\u7981\u751a\u662f\u4f69\u670d\uff0c\u4f46\u60f3\u5230\u5e72\u7684\u662f\u5982\u6b64\u4e00\u6869\u5947\u9669\u4e4b\u4e8b\uff0c\u5fc3\u4e2d\u53c8\u662f\u60f4\u60f4\u4e0d\u5b89\u3002\u80e1\u6590\u9664\u4e0b\u8eab\u4e0a\u8863\u670d\uff0c\u7ed9\u5f20\u4e5d\u6362\u4e0a\uff0c\u81ea\u5df1\u5374\u7a7f\u4e0a\u4e86\u4ed6\u7684\u4e00\u8eab\u6b66\u5b98\u670d\u8272\uff0c\u597d\u5728\u4e24\u4eba\u90fd\u662f\u4e2d\u7b49\u8eab\u6750\uff0c\u7a7f\u7740\u5012\u4e5f\u5408\u8eab\u3002 \u3000\u3000\u6c6a\u94c1\u9e57\u9053\uff1a\u201c\u6211\u662f\u7533\u6b63\u5f53\u503c\uff0c\u8fc7\u4e00\u4f1a\u513f\u65f6\u5019\u4fbf\u5230\u4e86\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u4f60\u7ed9\u5f20\u4e5d\u544a\u4e2a\u5047\uff0c\u8bf4\u4ed6\u751f\u4e86\u75c5\uff0c\u4e0d\u80fd\u5f53\u5dee\u3002\u6211\u5728\u8fd9\u513f\u7b49\u4f60\uff0c\u5230\u665a\u95f4\u4e8c\u66f4\u5929\u65f6\uff0c\u4f60\u6765\u63a5\u6211\u3002\u201d\u6c6a\u94c1\u9e57\u5446\u4e86\u534a\u664c\uff0c\u5fc3\u60f3\u53ea\u8981\u8fd9\u4e00\u53e5\u8bdd\u513f\u7b54\u5e94\u4e0b\u6765\uff0c\u4e00\u751f\u4fbf\u53d8\u4e86\u6a21\u6837\uff0c\u8981\u505a\u4e2a\u94c1\u94ee\u94ee\u7684\u6c49\u5b50\uff0c\u751a\u4e48\u8363\u534e\u5bcc\u8d35\uff0c\u5c31\u662f\u4e00\u7b14\u52fe\u9500\uff1b\u4f46\u82e5\u4e00\u5fc3\u4e00\u610f\u4e3a\u798f\u5927\u5e05\u51fa\u529b\uff0c\u4e0d\u514d\u662f\u975e\u4e0d\u5206\uff0c\u4e8e\u5fc3\u4e0d\u5b89\u3002 \u3000\u3000\u80e1\u6590\u89c1\u4ed6\u8fdf\u7591\uff0c\u8bf4\u9053\uff1a\u201c\u6c6a\u5927\u54e5\uff0c\u8fd9\u4ef6\u4e8b\u4e0d\u662f\u4e00\u65f6\u53ef\u51b3\uff0c\u4f60\u4e5f\u4e0d\u7528\u6b64\u523b\u4fbf\u56de\u6211\u8bdd\u3002\u201d\u6c6a\u94c1\u9e57\u70b9\u4e86\u70b9\u5934\uff0c\u5f84\u81ea\u51fa\u5e97\u53bb\u4e86\u3002 \u3000\u3000\u80e1\u6590\u8eba\u5728\u7095\u4e0a\uff0c\u653e\u5934\u4fbf\u7761\uff0c\u4ed6\u77e5\u9053\u773c\u524d\u5b9e\u662f\u4e00\u573a\u8c6a\u8d4c\uff0c\u4e0d\u8fc7\u4e0b\u7684\u8d4c\u6ce8\u5374\u662f\u81ea\u5df1\u7684\u6027\u547d\u3002 \u3000\u3000\u5230\u4e8c\u66f4\u5929\u65f6\uff0c\u6c6a\u94c1\u9e57\u6216\u8005\u679c\u771f\u72ec\u4e2a\u513f\u6084\u6084\u6765\u9886\u4e86\u81ea\u5df1\uff0c\u6df7\u8fdb\u798f\u5eb7\u5b89\u5e9c\u4e2d\u3002\u4f46\u8fd9\u4e48\u4e00\u6765\uff0c\u6c6a\u94c1\u9e57\u7684\u6027\u547d\u4fbf\u662f\u5341\u6210\u4e2d\u53bb\u4e86\u4e5d\u6210\u3002\u4ed6\u8ddf\u81ea\u5df1\u8bf4\u4e0d\u4e0a\u6709\u4ec0\u4e48\u4ea4\u60c5\uff0c\u8ddf\u9a6c\u6625\u82b1\u66f4\u662f\u5168\u65e0\u6e0a\u6e90\uff0c\u4e3a\u4e86\u4e24\u4e2a\u4e0d\u76f8\u5e72\u4e4b\u4eba\u800c\u7518\u5192\u751f\u6b7b\u5927\u9669\uff0c\u4f9d\u7740\u6c6a\u94c1\u9e57\u7684\u6027\u513f\uff0c\u4ed6\u80af\u5e72\uff1f\u4ed6\u81ea\u5e7c\u4fbf\u542c\u4ece\u5468\u94c1\u9e6a\u7684\u5429\u5490\uff0c\u5bf9\u8fd9\u4f4d\u5927\u5e08\u5144\u5949\u82e5\u795e\u660e\uff0c\u4f55\u51b5\u53c8\u5728\u798f\u5eb7\u5b89\u624b\u4e0b\u5c45\u5b98\u591a\u5e74\uff0c\u8fd9\u201c\u529f\u540d\u5229\u7984\u201d\u56db\u5b57\uff0c\u4e8e\u4ed6\u53ef\u4e0d\u662f\u5c0f\u4e8b\u3002 \u3000\u3000\u82e5\u662f\u4e00\u4f4d\u610f\u6c14\u76f8\u6295\u7684\u6c5f\u6e56\u597d\u6c49\uff0c\u80e1\u6590\u51b3\u65e0\u6000\u7591\u3002\u4f46\u6c6a\u94c1\u9e57\u5374\u662f\u4e2a\u672c\u4e8b\u5e73\u5eb8\u3001\u6d51\u6d51\u5669\u5669\u7684\u6b66\u5b98\u3002 \u3000\u3000\u5982\u679c\u4ed6\u51b3\u5b9a\u5347\u5b98\u53d1\u8d22\uff0c\u90a3\u4e48\u4e8c\u66f4\u4e0d\u5230\uff0c\u8fd9\u5ba2\u5e97\u524d\u540e\u5de6\u53f3\uff0c\u4fbf\u4f1a\u6709\u4e0a\u767e\u540d\u597d\u624b\u5305\u56f4\u4e0a\u6765\uff0c\u81ea\u5df1\u7eb5\u7136\u594b\u529b\u6b7b\u6218\uff0c\u4e5f\u5b9a\u7136\u4e0d\u514d\u3002 \u3000\u3000\u8fd9\u5176\u95f4\u6ca1\u6709\u6298\u8877\u7684\u8def\u53ef\u8d70\u3002\u6c6a\u94c1\u9e57\u4e0d\u80fd\u4e24\u4e0d\u76f8\u5e2e\uff0c\u6b64\u4e8b\u4ed6\u82e5\u4e0d\u544a\u53d1\uff0c\u5f20\u4e5d\u65e5\u540e\u600e\u4f1a\u4e0d\u53bb\u544a\u4ed6\uff1f \u3000\u3000\u80e1\u6590\u624b\u4e2d\u5df2\u62ff\u4e86\u4e00\u526f\u724c\u4e5d\uff0c\u8fd9\u65f6\u5019\u8fd8\u6ca1\u7ffb\u51fa\u6765\u3002\u8981\u662f\u8f93\u4e86\uff0c\u90a3\u4fbf\u8f93\u4e86\u81ea\u5df1\u7684\u6027\u547d\u3002\u8fd9\u526f\u724c\u662f\u597d\u662f\u574f\uff0c\u5168\u51ed\u6c6a\u94c1\u9e57\u4e00\u5ff5\u4e4b\u5dee\u3002\u4ed6\u77e5\u9053\u6c6a\u94c1\u9e57\u4e0d\u662f\u574f\u4eba\uff0c\u4f46\u8981\u4ed6\u5192\u9669\u5b9e\u5728\u592a\u5927\uff0c\u6c42\u4ed6\u7684\u5b9e\u5728\u592a\u591a\uff0c\u800c\u81ea\u5df1\u53ef\u6ca1\u534a\u70b9\u597d\u5904\u80fd\u62a5\u7b54\u4e8e\u4ed6\u2026\u2026\u6c6a\u94c1\u9e57\u8fd9\u6837\u7684\u4eba\u53ef\u5584\u53ef\u6076\uff0c\u8c01\u4e5f\u4e0d\u80fd\u9006\u6599\u3002\u5c06\u6027\u547d\u62bc\u5728\u4ed6\u7684\u8eab\u4e0a\uff0c\u539f\u662f\u9669\u7740\uff0c\u4f46\u9664\u6b64\u4e4b\u5916\uff0c\u5b9e\u65e0\u522b\u6cd5\u3002\u798f\u5eb7\u5b89\u5e9c\u4e2d\u5982\u6b64\u6212\u5907\uff0c\u82e5\u662f\u65e0\u4eba\u6307\u5f15\u76f8\u52a9\uff0c\u51b3\u8ba1\u6df7\u4e0d\u8fdb\u53bb\u3002 \u3000\u3000\u4ed6\u4e00\u7740\u6795\u4fbf\u547c\u547c\u5927\u7761\uff0c\u8fd9\u4e00\u6b21\u7adf\u8fde\u68a6\u4e5f\u6ca1\u6709\u505a\u3002\u4ed6\u6839\u672c\u4e0d\u53bb\u731c\u6d4b\u8fd9\u573a\u8c6a\u8d4c\u7ed3\u679c\u4f1a\u5982\u4f55\u3002 \u3000\u3000\u724c\u8fd8\u6ca1\u7ffb\uff0c\u8c01\u4e5f\u4e0d\u77e5\u9053\u662f\u4ec0\u4e48\u724c\u3002\u778e\u731c\u6709\u4ec0\u4e48\u7528\uff1f \u3000\u3000\u4ed6\u7761\u4e86\u4e00\u4e2a\u591a\u65f6\u8fb0\uff0c\u6726\u80e7\u4e2d\u542c\u5f97\u5e97\u5802\u6709\u4eba\u5927\u58f0\u8bf4\u8bdd\uff0c\u7acb\u65f6\u9192\u89c9\uff0c\u5750\u4e86\u8d77\u6765\u3002\u53ea\u542c\u90a3\u4eba\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u6b63\u8981\u89c1\u2018\u7384\u2019\u5b57\u53f7\u7684\u90a3\u4f4d\u603b\u7237\u3002\u559d\u9189\u4e86\u4e48\uff1f\u6709\u516c\u4e8b\u627e\u4ed6\u3002\u4f60\u53bb\u7ed9\u6211\u77a7\u77a7\u3002\u201d \u3000\u3000\u80e1\u6590\u4e00\u542c\u4e0d\u662f\u6c6a\u94c1\u9e57\u7684\u58f0\u97f3\uff0c\u5fc3\u4e0b\u51c9\u4e86\u534a\u622a\uff0c\u6697\u9053\uff1a\u201c\u563f\u563f\uff0c\u8fd9\u4e00\u573a\u5927\u8d4c\u7ec8\u7a76\u662f\u8f93\u4e86\u3002\u201d\u63d0\u8d77\u5355\u5200\uff0c\u8f7b\u8f7b\u63a8\u7a97\u5411\u5916\u4e00\u671b\uff0c\u53ea\u89c1\u56db\u4e0b\u91cc\u9ed1\u6c89\u6c89\u7684\u5e76\u65e0\u52a8\u9759\uff0c\u5f53\u4e0b\u7ffb\u8eab\u4e0a\u5c4b\uff0c\u4f0f\u5728\u74e6\u9762\uff0c\u51dd\u795e\u503e\u542c\u3002 \u3000\u3000\u6c6a\u94c1\u9e57\u4e00\u53bb\uff0c\u80e1\u6590\u77e5\u4ed6\u53ea\u6709\u4e24\u6761\u8def\u53ef\u8d70\uff1b\u82e5\u4ee5\u4fa0\u4e49\u4e3a\u91cd\uff0c\u8fd9\u65f6\u4fbf\u4f1a\u5355\u8eab\u6765\u5f15\u81ea\u5df1\u5077\u5165\u798f\u5e9c\uff1b\u5018\u82e5\u60dc\u8eab\u6c42\u7984\uff0c\u5fc5\u5b9a\u662f\u5f15\u4e86\u798f\u5e9c\u7684\u6b66\u58eb\u524d\u6765\u56f4\u6355\u3002\u4ed6\u65e2\u7136\u4e0d\u6765\uff0c\u6b64\u4e8b\u81ea\u662f\u7cdf\u4e86\u3002\u4f46\u5ba2\u5e97\u56db\u5468\uff0c\u7adf\u7136\u65e0\u4eba\u57cb\u4f0f\uff0c\u5012\u4e5f\u9887\u51fa\u80e1\u6590\u610f\u6599\u4e4b\u5916\u3002\u8981\u77e5\u524d\u6765\u56f4\u6355\u7684\u6b66\u58eb\u4e0d\u6765\u5219\u5df2\uff0c\u6765\u5219\u5fc5\u5b9a\u4eba\u6570\u4f17\u591a\uff0c\u4e00\u4e8c\u4e2a\u9ad8\u624b\u5c1a\u53ef\u9690\u8eab\u6f5c\u4f0f\uff0c\u4e0d\u4ee4\u81ea\u5df1\u53d1\u73b0\u8e2a\u8ff9\uff0c\u4eba\u6570\u4e00\u591a\uff0c\u4fbf\u662f\u900f\u6c14\u4e4b\u58f0\u4e5f\u80fd\u542c\u89c1\u4e86\u3002 \u3000\u3000\u4ed6\u89c1\u654c\u4eba\u975e\u4f17\uff0c\u7a0d\u89c9\u5bbd\u5fc3\u3002\u4f46\u89c1\u7a97\u5916\u70db\u5149\u6643\u52a8\uff0c\u5e97\u5c0f\u4e8c\u624b\u91cc\u62ff\u7740\u4e00\u53ea\u70db\u53f0\uff0c\u5728\u95e8\u5916\u8bf4\u9053\uff1a\u201c\u603b\u7237\uff0c\u8fd9\u91cc\u6709\u4e00\u4f4d\u603b\u7237\u8981\u89c1\u60a8\u8001\u4eba\u5bb6\u3002\u201d\u80e1\u6590\u7ffb\u8eab\u4ece\u7a97\u4e2d\u8fdb\u623f\uff0c\u843d\u5730\u65e0\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u8bf7\u8fdb\u6765\u5427\uff01\u201d\u5e97\u5c0f\u4e8c\u63a8\u5f00\u623f\u95e8\uff0c\u5c06\u70db\u53f0\u653e\u5728\u684c\u4e0a\uff0c\u966a\u7b11\u9053\uff1a\u201c\u90a3\u4e00\u4f4d\u603b\u7237\u9152\u9192\u4e86\u5427\uff1f\u82e5\u662f\u8fd8\u6ca1\u59a5\u8d34\uff0c\u8981\u4e0d\u7ed9\u505a\u4e00\u7897\u9192\u9152\u6c64\u559d\uff1f\u201d \u3000\u3000\u80e1\u6590\u968f\u53e3\u9053\uff1a\u201c\u4e0d\u7528\uff01\u201d\u773c\u5149\u76ef\u5728\u5e97\u5c0f\u4e8c\u8eab\u540e\u90a3\u540d\u536b\u58eb\u8138\u4e0a\u3002 \u3000\u3000\u53ea\u89c1\u4ed6\u7ea6\u83ab\u56db\u5341\u6765\u5c81\u5e74\u7eaa\uff0c\u7070\u6251\u6251\u4e00\u5f20\u8138\u86cb\uff0c\u4e1d\u6beb\u4e0d\u52a8\u58f0\u8272\uff0c\u80e1\u6590\u5fc3\u9053\uff1a\u201c\u597d\u5389\u5bb3\u7684\u811a\u8272\uff01\u5b64\u8eab\u8fdb\u6211\u623f\u6765\uff0c\u5c45\u7136\u4e0d\u9732\u534a\u70b9\u6212\u60e7\u4e4b\u610f\u3002\u96be\u9053\u4f60\u5f53\u771f\u6709\u8fc7\u4eba\u7684\u672c\u9886\uff0c\u7edd\u6ca1\u5c06\u6211\u80e1\u6590\u653e\u5728\u5fc3\u4e0a\u5417\uff1f\u201d\u53ea\u542c\u90a3\u536b\u58eb\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u5f20\u5927\u54e5\u5417\uff1f\u54b1\u4eec\u6ca1\u89c1\u8fc7\u9762\uff0c\u5c0f\u5f1f\u59d3\u4efb\uff0c\u4efb\u901a\u6b66\uff0c\u5728\u5de6\u8425\u5f53\u5dee\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u539f\u6765\u662f\u4efb\u5927\u54e5\uff0c\u5e78\u4f1a\u5e78\u4f1a\u3002\u5927\u4f19\u513f\u4eba\u591a\uff0c\u5e73\u65e5\u5c11\u8ddf\u4efb\u5927\u54e5\u4eb2\u8fd1\u3002\u201d\u4efb\u901a\u6b66\u9053\uff1a\u201c\u662f\u554a\u3002\u4e0a\u5934\u8f6c\u4e0b\u6765\u4e00\u4ef6\u516c\u4e8b\uff0c\u53eb\u5c0f\u5f1f\u9001\u7ed9\u5f20\u5927\u54e5\u3002\u201d\u8bf4\u7740\u4ece\u8eab\u8fb9\u62bd\u51fa\u4e00\u4ef6\u516c\u6587\u6765\u3002 \u3000\u3000\u80e1\u6590\u63a5\u8fc7\u4e00\u770b\uff0c\u89c1\u516c\u6587\u5de6\u89d2\u4e0a\u8d6b\u7136\u5370\u7740\u201c\u5175\u90e8\u6b63\u5802\u201d\u56db\u4e2a\u7ea2\u5b57\uff0c\u5c01\u76ae\u4e0a\u5199\u9053\uff1a\u201c\u5373\u4ea4\u5b89\u8fdc\u5ba2\u5e97\uff0c\u5de1\u6355\u53f3\u8425\u5f20\u4e5d\u6536\u62c6\uff0c\u901f\u901f\u4e0d\u8bef\u3002\u201d\u80e1\u6590\u4e0a\u6b21\u5728\u798f\u5e9c\u4e2d\u4e0a\u4e86\u4e2a\u5927\u5f53\uff0c\u53cc\u624b\u4e3a\u94a2\u76d2\u6240\u4f24\uff0c\u8fd9\u4e00\u56de\u5b66\u4e86\u4e56\uff0c\u4e0d\u5373\u5f00\u62c6\u516c\u6587\uff0c\u5148\u5c0f\u5fc3\u634f\u4e86\u634f\u5c01\u5957\uff0c\u89c1\u5176\u4e2d\u5e76\u65e0\u53e4\u602a\uff0c\u53c8\u60f3\u5230\u82d7\u4eba\u51e4\u4e3a\u62c6\u4fe1\u800c\u6bd2\u836f\u4f24\u76ee\uff0c\u5f53\u4e0b\u5c06\u516c\u6587\u5782\u5230\u5c0f\u8179\u4e4b\u524d\uff0c\u8fd9\u624d\u62c6\u5f00\u5c01\u5957\uff0c\u62bd\u51fa\u4e00\u5f20\u767d\u7eb8\uff0c\u5c31\u70db\u5149\u4e00\u770b\uff0c\u4e0d\u7531\u5f97\u60ca\u7591\u4ea4\u96c6\u3002 \u3000\u3000\u539f\u6765\u7eb8\u4e0a\u5e76\u65e0\u4e00\u5b57\uff0c\u5374\u753b\u4e86\u4e00\u5e45\u7b14\u81f4\u7c97\u964b\u7684\u56fe\u753b\u3002\u56fe\u4e2d\u4e00\u4e2a\u540a\u6b7b\u9b3c\u6253\u7740\u624b\u52bf\uff0c\u6b63\u5728\u7aed\u529b\u529d\u4e00\u4eba\u60ac\u6881\u4e0a\u540a\u3002\u5f53\u65f6\u8ff7\u4fe1\uff0c\u6709\u4eba\u60ac\u6881\u81ea\u5c3d\uff0c\u6b7b\u540e\u53d8\u9b3c\uff0c\u5fc5\u987b\u5343\u65b9\u767e\u8ba1\u5f15\u8bf1\u53e6\u4e00\u4eba\u53d8\u9b3c\uff0c\u4ed6\u81ea\u5df1\u65b9\u5f97\u8f6c\u4e16\u6295\u80ce\uff0c\u540e\u6765\u7684\u6b7b\u8005\u4fbf\u662f\u6240\u8c13\u66ff\u6b7b\u9b3c\u4e86\u3002\u8fd9\u8bf4\u6cd5\u867d\u7136\u8352\u8bde\u4e0d\u7ecf\uff0c\u4f46\u5f53\u65f6\u5374\u662f\u4eba\u4eba\u7686\u77e5\u3002 \u3000\u3000\u80e1\u6590\u51dd\u795e\u4e00\u60f3\uff0c\u5fc3\u5ff5\u4e00\u52a8\uff0c\u95ee\u9053\uff1a\u201c\u4efb\u5927\u54e5\u4eca\u665a\u5728\u5927\u5e05\u5e9c\u4e2d\u8f6e\u503c\uff1f\u201d\u4efb\u901a\u6b66\u9053\uff1a\u201c\u6b63\u662f\uff01\u5c0f\u5f1f\u8fd9\u4fbf\u8981\u53bb\u3002\u201d\u8bf4\u7740\u8f6c\u8eab\u6b32\u884c\u3002 \u3000\u3000\u80e1\u6590\u9053\uff1a\u201c\u4e14\u6162\uff01\u8bf7\u95ee\u8fd9\u516c\u4e8b\u662f\u8c01\u5dee\u4efb\u5927\u54e5\u9001\u6765\uff1f\u201d\u4efb\u901a\u6b66\u9053\uff1a\u201c\u662f\u6211\u4eec\u6797\u53c2\u5c06\u5dee\u5c0f\u5f1f\u9001\u6765\u3002\u201d \u3000\u3000\u80e1\u6590\u5230\u8fd9\u65f6\u5df2\u662f\u5fc3\u4e2d\u96ea\u4eae\uff1a\u539f\u6765\u6c6a\u94c1\u9e57\u81ea\u5df1\u62ff\u4e0d\u5b9a\u4e3b\u610f\uff0c\u7ec8\u7a76\u8fd8\u662f\u53bb\u548c\u5927\u5e08\u54e5\u5468\u94c1\u9e6a\u5546\u91cf\u3002\u5468\u94c1\u9e6a\u5ff5\u7740\u80e1\u6590\u6628\u665a\u7eed\u817f\u8fd8\u724c\u4e4b\u5fb7\uff0c\u60f3\u51fa\u4e86\u8fd9\u4e2a\u8ba1\u8f83\uff0c\u4ed6\u4e0d\u8ba9\u6c6a\u94c1\u9e57\u72af\u9669\uff0c\u5374\u8f97\u8f6c\u7684\u5dee\u4e86\u4e2a\u66ff\u6b7b\u9b3c\u6765\u3002\u7531\u8fd9\u4eba\u9886\u80e1\u6590\u8fdb\u798f\u5e9c\uff0c\u4e0d\u8bba\u6210\u8d25\uff0c\u5747\u4e0e\u4ed6\u5e08\u5144\u5f1f\u65e0\u6d89\uff0c\u56e0\u6b64\u4fe1\u4e0a\u975e\u4f46\u4e0d\u7f72\u59d3\u540d\uff0c\u8fde\u5b57\u8ff9\u4e5f\u4e0d\u7559\u4e00\u4e2a\uff0c\u4ee5\u9632\u4e07\u4e00\u4e8b\u673a\u4e0d\u5bc6\uff0c\u7275\u8fde\u4e8e\u4ed6\u3002\u8fd9\u4e00\u4ef6\u516c\u6587\u4ed6\u5939\u5728\u4ea4\u7ed9\u5de6\u8425\u6797\u53c2\u5c06\u7684\u4e00\u53e0\u6587\u4ef6\u4e4b\u4e2d\uff0c\u8f6c\u4e86\u51e0\u4e2a\u624b\uff0c\u8c01\u4e5f\u4e0d\u77e5\u8fd9\u516c\u6587\u81ea\u4f55\u800c\u6765\u3002\u6797\u53c2\u5c06\u4e00\u89c1\u662f\u201c\u5175\u90e8\u6b63\u5802\u201d\u7684\u516c\u4e8b\uff0c\u4e0d\u6562\u5ef6\u6401\uff0c\u7acb\u5373\u5dee\u4eba\u9001\u6765\u3002\u5468\u94c1\u9e6a\u65e9\u77e5\u5de6\u8425\u7684\u536b\u58eb\u4eca\u665a\u5168\u4f53\u5728\u798f\u5e9c\u4e2d\u5f53\u503c\u5b88\u536b\uff0c\u90a3\u6797\u53c2\u5c06\u4e0d\u7ba1\u6d3e\u8c01\u9001\u4fe1\uff0c\u80e1\u6590\u5747\u53ef\u968f\u4ed6\u8fdb\u5e9c\u3002 \u3000\u3000\u8fd9\u4e2d\u95f4\u7684\u539f\u59d4\u66f2\u6298\u80e1\u6590\u867d\u7136\u4e0d\u80fd\u5c3d\u77e5\uff0c\u5374\u4e5f\u731c\u4e86\u4e2a\u516b\u4e0d\u79bb\u4e5d\uff0c\u5fc3\u4e0b\u6697\u7b11\u5468\u94c1\u9e6a\u8001\u5978\u5de8\u733e\uff0c\u5728\u4eac\u5e08\u6df7\u4e86\u6570\u5341\u5e74\u7684\u4eba\uff0c\u884c\u4e8b\u679c\u7136\u4e0e\u4f17\u4e0d\u540c\uff0c\u4f46\u5bf9\u4ed6\u76f8\u52a9\u7684\u4e00\u756a\u597d\u610f\uff0c\u5374\u4e5f\u6697\u6697\u611f\u6fc0\uff0c\u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u4e0a\u5934\u6709\u4ee4\uff0c\u547d\u5144\u5f1f\u968f\u4efb\u5927\u54e5\u8fdb\u5e9c\u5b88\u536b\u3002\u201d\u8ddf\u7740\u53c8\u9053\uff1a\u201c\u4ed6\u5988\u7684\uff0c\u4eca\u513f\u672c\u662f\u8f6e\u5230\u6211\u4f11\u5047\uff0c\u534a\u591c\u4e09\u66f4\u7684\uff0c\u53c8\u628a\u4eba\u53eb\u4e86\u53bb\u3002\u201d \u3000\u3000\u4efb\u901a\u6b66\u7b11\u9053\uff1a\u201c\u5927\u5e05\u5e9c\u4e2d\u95f9\u523a\u5ba2\uff0c\u5927\u4f19\u513f\u8c01\u90fd\u5f97\u8f9b\u82e6\u4e9b\u3002 \u3000\u3000\u597d\u5728\u90a3\u4e00\u4efd\u4f18\u8d4f\u603b\u662f\u77ed\u4e0d\u4e86\u3002\u201d\u80e1\u6590\u7b11\u9053\uff1a\u201c\u56de\u5934\u9886\u5230\u4e86\u94b1\uff0c\u5c0f\u5f1f\u4f5c\u4e1c\uff0c\u54b1\u54e5\u513f\u4fe9\u5230\u805a\u82f1\u697c\u53bb\u597d\u597d\u4e50\u4ed6\u4e00\u573a\u3002\u4efb\u5927\u54e5\uff0c\u4f60\u662f\u597d\u9152\u597d\u8d4c\u3001\u8fd8\u662f\u597d\u8272\uff1f\u201d\u4efb\u901a\u6b66\u54c8\u54c8\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u9152\u8272\u8d22\u6c14\u56db\u95e8\uff0c\u505a\u5144\u5f1f\u7684\u5168\u90fd\u6253\u4ece\u5fc3\u773c\u513f\u91cc\u6b22\u559c\u51fa\u6765\u3002\u201d\u80e1\u6590\u5728\u4ed6\u80a9\u4e0a\u4e00\u62cd\uff0c\u663e\u5f97\u6781\u662f\u4eb2\u70ed\uff0c\u7b11\u9053\uff1a\u201c\u54b1\u4fe9\u610f\u6c14\u76f8\u6295\uff0c\u5f53\u771f\u662f\u76f8\u89c1\u6068\u665a\u4e86\u3002\u5c0f\u4e8c\uff0c\u5c0f\u4e8c\uff0c\u5feb\u53d6\u9152\u6765\uff01\u201d \u3000\u3000\u4efb\u901a\u6b66\u8e0c\u8e87\u9053\uff1a\u201c\u4eca\u665a\u8981\u5f53\u5dee\uff0c\u82e5\u662f\u53c2\u5c06\u77e5\u9053\u54b1\u4eec\u559d\u9152\uff0c\u53ea\u6015\u4e0d\u4fbf\u3002\u201d\u80e1\u6590\u4f4e\u58f0\u9053\uff1a\u201c\u559d\u4e09\u676f\uff0c\u53c2\u5c06\u77e5\u9053\u4e2a\u5c41\uff01\u201d\u8bf4\u8bdd\u95f4\uff0c\u5e97\u5c0f\u4e8c\u5df2\u53d6\u8fc7\u9152\u6765\uff0c\u591c\u91cc\u6ca1\u751a\u4e48\u4e0b\u9152\u4e4b\u7269\uff0c\u53ea\u5207\u4e86\u4e00\u76c6\u5364\u725b\u8089\u3002 \u3000\u3000\u80e1\u6590\u548c\u4efb\u901a\u6b66\u8fde\u5e72\u4e09\u676f\uff0c\u63b7\u4e86\u4e00\u4e24\u94f6\u5b50\u5728\u684c\u4e0a\uff0c\u8bf4\u9053\uff1a\u201c\u4f59\u4e0b\u7684\u662f\u8d4f\u94b1\uff01\u201d\u5e97\u5c0f\u4e8c\u5927\u559c\uff0c\u6b63\u8981\u9053\u8c22\u3002\u4efb\u901a\u6b66\u4e00\u628a\u5c06\u94f6\u5b50\u62a2\u8fc7\uff0c\u7b11\u9053\uff1a\u201c\u5f20\u5927\u54e5\u8fd9\u624b\u9762\u4e5f\u672a\u514d\u9614\u5f97\u8fc7\u4efd\uff0c\u54b1\u4eec\u5728\u798f\u5927\u5e05\u5e9c\u4e2d\u5f53\u5dee\u7684\uff0c\u559d\u51e0\u676f\u9152\u8fd8\u7528\u7ed9\u94b1\uff1f\u8d70\u5427\uff01\u65f6\u5019\u5dee\u4e0d\u591a\u5566\u3002\u201d \u3000\u3000\u5de6\u624b\u62c9\u7740\u80e1\u6590\uff0c\u5411\u5916\u62a2\u51fa\uff0c\u53f3\u624b\u5c06\u94f6\u5b50\u585e\u5165\u6000\u91cc\u3002\u5e97\u5c0f\u4e8c\u77a7\u5728\u773c\u91cc\uff0c\u5374\u662f\u6562\u6012\u800c\u4e0d\u6562\u8a00\u3002\u8981\u77e5\u798f\u5eb7\u5b89\u5e9c\u91cc\u7684\u536b\u58eb\u5728\u5317\u4eac\u57ce\u91cc\u6a2a\u884c\u60ef\u4e86\uff0c\u770b\u767d\u620f\u3001\u5403\u767d\u98df\uff0c\u6d51\u662f\u95f2\u4e8b\uff0c\u4fbf\u662f\u987a\u624b\u7275\u7f8a\u62ff\u4e9b\u5e97\u94fa\u91cc\u7684\u7269\u4e8b\uff0c\u5c0f\u767e\u59d3\u53c8\u600e\u6562\u4f5c\u58f0\uff1f \u3000\u3000\u80e1\u6590\u4e00\u7b11\uff0c\u5fc3\u60f3\u6b64\u4eba\u8d2a\u8d22\u597d\u9152\uff0c\u5012\u662f\u5bb9\u6613\u5bf9\u4ed8\uff0c\u5f53\u4e0b\u4e0e\u4ed6\u643a\u624b\u51fa\u5e97\u3002\u5c06\u51fa\u5e97\u95e8\u65f6\uff0c\u5ffd\u542c\u5f97\u5c4b\u9876\u4e0a\u5580\u7684\u4e00\u58f0\u8f7b\u54cd\uff0c\u58f0\u97f3\u867d\u6781\u7ec6\u5fae\uff0c\u4f46\u80e1\u6590\u542c\u5728\u8033\u91cc\uff0c\u4fbf\u77e5\u6709\u5f02\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u4efb\u5927\u54e5\uff0c\u6211\u5fd8\u4e86\u4e00\u4ef6\u7269\u4e8b\uff0c\u8bf7\u4f60\u7a0d\u5f85\u3002\u201d\u4e00\u8f6c\u8eab\uff0c\u4fbf\u56de\u8fdb\u81ea\u5df1\u623f\u4e2d\uff0c\u9ed1\u6697\u4e2d\u53ea\u89c1\u4e00\u4e2a\u7626\u524a\u7684\u8eab\u5f62\u8d8a\u7a97\u800c\u51fa\uff0c\u8eab\u6cd5\u751a\u662f\u5feb\u6377\uff0c\u4f9d\u7a00\u4fbf\u662f\u5468\u94c1\u9e6a\u3002 \u3000\u3000\u80e1\u6590\u5927\u5947\uff1a\u201c\u4ed6\u53c8\u5230\u6211\u623f\u4e2d\u6765\u5e72\u4e48\uff1f\u201d\u5fae\u4e00\u6c89\u541f\uff0c\u63ed\u5f00\u5e8a\u5e10\uff0c\u63a2\u624b\u5230\u5f20\u4e5d\u9f3b\u5b54\u8fb9\u4e00\u8bd5\uff0c\u679c\u7136\u547c\u5438\u5df2\u6b62\uff0c\u7adf\u662f\u88ab\u5468\u94c1\u9e6a\u4f7f\u91cd\u624b\u70b9\u6b7b\u4e86\u3002\u80e1\u6590\u5fc3\u4e2d\u4e00\u5bd2\uff1a\u201c\u6b64\u4eba\u5f53\u771f\u662f\u5fc3\u601d\u5468\u5bc6\uff0c\u4e0b\u624b\u6bd2\u8fa3\u3002\u672c\u6765\u82e5\u4e0d\u9664\u53bb\u5f20\u4e5d\uff0c\u5b9a\u4f1a\u6cc4\u6f0f\u4ed6\u5e08\u5144\u5f1f\u4fe9\u7684\u673a\u5173\uff0c\u53ea\u662f\u6ca1\u6599\u5230\u6211\u524d\u811a\u624d\u51fa\u95e8\uff0c\u4ed6\u540e\u811a\u4fbf\u8fdb\u6765\u4e0b\u624b\uff0c\u8fde\u7247\u523b\u5598\u606f\u7684\u4f59\u88d5\u4e5f\u6ca1\u6709\u3002\u201d\u65e2\u662f\u5982\u6b64\uff0c\u4ed6\u53cd\u800c\u653e\u5fc3\uff0c\u77e5\u9053\u5468\u94c1\u9e6a\u5bf9\u5df1\u786e\u662f\u4e00\u7247\u771f\u5fc3\uff0c\u4e0d\u81f4\u4e8e\u8bf1\u5f15\u81ea\u5df1\u8fdb\u4e86\u798f\u5e9c\uff0c\u518d\u4ee4\u4eba\u56f4\u4e0a\u52a8\u624b\u3002 \u3000\u3000\u4e8e\u662f\u5c06\u5f20\u4e5d\u8eab\u5b50\u4e00\u7ffb\uff0c\u8ba9\u4ed6\u8138\u5b54\u671d\u91cc\uff0c\u62c9\u8fc7\u88ab\u5b50\u7a9d\u597d\u4e86\uff0c\u8f6c\u8eab\u51fa\u623f\uff0c\u8bf4\u9053\uff1a\u201c\u4efb\u5927\u54e5\uff0c\u52b3\u4f60\u7b49\u5019\uff0c\u54b1\u4eec\u8d70\u5427\u3002\u201d\u4efb\u901a\u6b66\u9053\uff1a\u201c\u81ea\u5df1\u5f1f\u5144\uff0c\u5ba2\u6c14\u4ec0\u4e48\uff1f\u201d\u4e24\u4eba\u5e76\u80a9\u800c\u884c\uff0c\u5927\u6447\u5927\u6446\u7684\u8d70\u5411\u798f\u5eb7\u5b89\u5e9c\u3002 \u3000\u3000\u53ea\u89c1\u798f\u5e9c\u95e8\u524d\u7ad9\u7740\u4e8c\u5341\u6765\u540d\u536b\u58eb\uff0c\u679c\u662f\u6212\u5907\u4e0d\u540c\u5f80\u65e5\u3002\u80e1\u6590\u8ddf\u7740\u4efb\u901a\u6b66\u8d70\u5230\u95e8\u53e3\uff0c\u4e00\u540d\u5343\u603b\u4f4e\u58f0\u559d\u9053\uff1a\u201c\u5a01\u9707\u2014\u2014\u201d\u4efb\u901a\u6b66\u63a5\u53e3\u9053\uff1a\u201c\u2014\u2014\u56db\u6d77\uff01\u201d\u90a3\u5343\u603b\u70b9\u4e86\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u4eca\u513f\u5927\u4f19\u5f97\u591a\u52a0\u70b9\u52b2\u3002\u201d\u4efb\u901a\u6b66\u9053\uff1a\u201c\u90a3\u8fd8\u4f1a\u9519\u4e48\uff1f\u201d\u80e1\u6590\u9053\uff1a\u201c\u8001\u603b\uff0c\u4f60\u8bf4\u4eca\u665a\u4f1a\u4e0d\u4f1a\u6709\u523a\u5ba2\u518d\u8fdb\u5e9c\u6765\uff1f\u201d\u90a3\u5343\u603b\u7b11\u9053\uff1a\u201c\u9664\u975e\u4ed6\u5403\u4e86\u8c79\u5b50\u80c6\uff0c\u8001\u864e\u5fc3\u3002\u201d\u80e1\u6590\u54c8\u54c8\u4e00\u7b11\uff0c\u8fdb\u4e86\u5927\u95e8\u3002 \u3000\u3000\u5230\u8fbe\u4e2d\u95e8\u65f6\uff0c\u53c8\u662f\u4e00\u5c0f\u961f\u536b\u58eb\u5b88\u7740\u3002\u4e00\u540d\u5343\u603b\u4f4e\u559d\u53e3\u4ee4\uff1a \u3000\u3000\u201c\u5a01\u9707\u2014\u2014\u201d\u4efb\u901a\u6b66\u7b54\u9053\uff1a\u201c\u2014\u2014\u7edd\u57df\uff01\u201d\u90a3\u5343\u603b\u9053\uff1a\u201c\u4efb\u901a\u6b66\uff0c\u8fd9\u4eba\u9762\u751f\u5f97\u5f88\uff0c\u662f\u8c01\u554a\uff1f\u201d\u4efb\u901a\u6b66\u9053\uff1a\u201c\u662f\u53f3\u8425\u7684\u5f20\u5927\u54e5\uff0c\u4f60\u6ca1\u89c1\u8fc7\u4e48\uff1f\u201d\u90a3\u5343\u603b\u201c\u55ef\u201d\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u8fd9\u90e8\u80e1\u5b50\u957f\u5f97\u5012\u662f\u633a\u5a01\u98ce\u7684\u3002\u201d \u3000\u3000\u4e24\u4eba\u6298\u800c\u5411\u5de6\uff0c\u7a7f\u8fc7\u4e24\u9053\u8fb9\u95e8\uff0c\u5230\u4e86\u82b1\u56ed\u4e4b\u4e2d\u3002\u56ed\u95e8\u53e3\u53c8\u662f\u4e00\u5c0f\u961f\u536b\u58eb\uff0c\u90a3\u53e3\u4ee4\u5374\u53d8\u6210\u4e86\u201c\u5a01\u9707\u2014\u2014\u5343\u79cb\u201d\u3002\u80e1\u6590\u5fc3\u60f3\uff1a\u201c\u5018\u82e5\u6211\u4e0d\u968f\u4efb\u901a\u6b66\u8fdb\u6765\uff0c\u4fbf\u7b97\u8fc7\u4e86\u5927\u95e8\uff0c\u4e5f\u4e0d\u80fd\u8fc7\u4e8c\u95e8\u3002 \u3000\u3000\u5373\u4f7f\u6211\u63a2\u542c\u5230\u4e86\u2018\u5a01\u9707\u56db\u6d77\u2019\u7684\u53e3\u4ee4\uff0c\u4e5f\u60f3\u4e0d\u5230\u6bcf\u4e00\u9053\u95e8\u7684\u53e3\u4ee4\u5404\u6709\u53d8\u5316\u3002\u201d \u3000\u3000\u8fdb\u4e86\u82b1\u56ed\uff0c\u80e1\u6590\u5df2\u8bc6\u5f97\u8def\u5f84\uff0c\u5fc3\u60f3\u591c\u957f\u68a6\u591a\uff0c\u65e9\u4e9b\u4e0b\u624b\uff0c\u4e5f\u597d\u8ba9\u9a6c\u6625\u82b1\u65e9\u4e00\u523b\u5b89\u5fc3\uff0c\u53c8\u60f3\uff1a\u201c\u4e8c\u59b9\u89c1\u6211\u8fd9\u4e48\u4e45\u4e0d\u56de\u53bb\uff0c\u5fc5\u5df2\u6599\u5230\u6211\u8fdb\u4e86\u798f\u5e9c\uff0c\u5b9a\u4e5f\u5fe7\u5fc3\u3002\u201d\u5f53\u4e0b\u52a0\u5feb\u811a\u6b65\uff0c\u5411\u798f\u5eb7\u5b89\u4e4b\u6bcd\u7684\u4f4f\u6240\u8d70\u53bb\u3002\u4efb\u901a\u6b66\u5f88\u662f\u8be7\u5f02\uff0c\u9053\uff1a\u201c\u5f20\u5927\u54e5\uff0c\u4f60\u5230\u90a3\u91cc\u53bb\uff1f\u201d\u80e1\u6590\u9053\uff1a\u201c\u4e0a\u5934\u6d3e\u6211\u4fdd\u62a4\u592a\u592b\u4eba\uff0c\u8bf4\u9053\u51b3\u8ba1\u4e0d\u53ef\u4ee4\u592a\u592b\u4eba\u53d7\u5230\u60ca\u5413\u3002\u4f60\u4e0d\u77e5\u9053\u4e48\uff1f\u201d\u4efb\u901a\u6b66\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\uff01\u201d \u3000\u3000\u4fbf\u5728\u6b64\u65f6\uff0c\u524d\u9762\u4e24\u540d\u536b\u58eb\u6084\u6ca1\u58f0\u7684\u5de1\u4e86\u8fc7\u6765\u3002\u5de6\u9996\u4e00\u4eba\u4f4e\u559d\u9053\uff1a\u201c\u62a5\u540d\uff01\u201d\u4efb\u901a\u6b66\u9053\uff1a\u201c\u5de6\u8425\u4efb\u901a\u6b66\uff01\u201d\u80e1\u6590\u9053\uff1a\u201c\u53f3\u8425\u5f20\u4e5d\uff01\u201d\u90a3\u4eba\u201c\u554a\u201d\u7684\u4e00\u58f0\uff0c\u624b\u6309\u5200\u67c4\uff0c\u559d\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u4f60\u662f\u8c01\uff1f\u201d \u3000\u3000\u80e1\u6590\u5fc3\u4e2d\u4e00\u51db\uff0c\u77e5\u9053\u6b64\u4eba\u548c\u5f20\u4e5d\u719f\u8bc6\uff0c\u4e8b\u5df2\u8d25\u9732\uff0c\u51d1\u5230\u4ed6\u8033\u8fb9\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u6211\u662f\u80e1\u6590\uff01\u201d\u90a3\u4eba\u60ca\u5f97\u5446\u4e86\uff0c\u4e00\u65f6\u624b\u8db3\u65e0\u63aa\u3002\u80e1\u6590\u4f38\u6307\u4e00\u6233\uff0c\u70b9\u4e2d\u4e86\u4ed6\u7684\u7a74\u9053\uff0c\u5de6\u624b\u624b\u8098\u987a\u52bf\u4e00\u649e\uff0c\u53c8\u6253\u4e2d\u4e86\u53e6\u4e00\u540d\u536b\u58eb\u7684\u7a74\u9053\u3002\u4efb\u901a\u6b66\u60ca\u60f6\u5931\u63aa\uff0c\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u2026\u2026\u5e72\u4ec0\u4e48\uff1f\u201d\u80e1\u6590\u51b7\u51b7\u7684\u9053\uff1a\u201c\u5927\u4e08\u592b\u884c\u4e0d\u6539\u59d3\uff0c\u5750\u4e0d\u6539\u540d\uff0c\u6211\u59d3\u80e1\u540d\u6590\u7684\u4fbf\u662f\u3002\u201d\u4e00\u9762\u8bf4\uff0c\u4e00\u9762\u5c06\u4e24\u540d\u7a74\u9053\u88ab\u70b9\u7684\u536b\u58eb\u63b7\u5165\u4e86\u82b1\u4e1b\u3002 \u3000\u3000\u4efb\u901a\u6b66\u5438\u4e00\u53e3\u6c14\uff0c\u5237\u7684\u4e00\u58f0\uff0c\u62d4\u51fa\u4e86\u8170\u5200\u3002\u80e1\u6590\u7b11\u9053\uff1a\u201c\u4eba\u4eba\u90fd\u5df2\u77a7\u89c1\uff0c\u662f\u4f60\u5f15\u6211\u8fdb\u5e9c\u6765\u7684\u3002\u4f60\u53eb\u56b7\u8d77\u6765\uff0c\u6709\u4f55\u597d\u5904\uff1f \u3000\u3000\u8fd8\u4e0d\u5982\u4e56\u4e56\u7684\u522b\u4f5c\u58f0\u3002\u201d\u4efb\u901a\u6b66\u53c8\u60ca\u53c8\u6015\uff0c\u54ea\u91cc\u8fd8\u8bf4\u5f97\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u80e1\u6590\u9053\uff1a\u201c\u4f60\u8981\u547d\u7684\uff0c\u4fbf\u8ddf\u7740\u6211\u6765\u3002\u201d\u4efb\u901a\u6b66\u8fd9\u65f6\u516d\u795e\u65e0\u4e3b\uff0c\u53ea\u5f97\u8ddf\u5728\u4ed6\u8eab\u540e\uff0c\u773c\u89c1\u4ed6\u4e00\u4f38\u624b\u4e00\u56de\u8098\uff0c\u4fbf\u6253\u5012\u4e86\u4e24\u540d\u6b66\u529f\u6bd4\u81ea\u5df1\u9ad8\u5f97\u591a\u7684\u536b\u58eb\uff0c\u82e5\u662f\u4e0e\u4ed6\u52a8\u624b\uff0c\u5f92\u7136\u9001\u4e86\u6027\u547d\uff0c\u53ea\u76fc\u4ed6\u522b\u95f9\u51fa\u4ec0\u4e48\u4e8b\u6765\uff0c\u8fde\u7d2f\u4e86\u81ea\u5df1\u3002\u4f46\u80e1\u6590\u65e2\u7136\u8fdb\u5f97\u5e9c\u6765\uff0c\u5c82\u6709\u4e0d\u95f9\u4e8b\u4e4b\u7406\uff1f\u4efb\u901a\u6b66\u8fd9\u822c\u75f4\u60f3\uff0c\u4e5f\u4e0d\u8fc7\u5728\u65e0\u6cd5\u4e4b\u4e2d\u81ea\u884c\u5bbd\u6170\u800c\u5df2\u3002 \u3000\u3000\u80e1\u6590\u5feb\u6b65\u5230\u76f8\u56fd\u592b\u4eba\u7684\u5c4b\u5916\uff0c\u53ea\u89c1\u4e03\u516b\u540d\u536b\u58eb\u7ad9\u5728\u95e8\u53e3\uff0c\u82e5\u662f\u5411\u524d\u786c\u95ef\uff0c\u672a\u5fc5\u80fd\u8fc5\u901f\u8fc7\u5f97\u8fd9\u4e00\u5173\uff0c\u5fc3\u5ff5\u4e00\u52a8\uff0c\u7ed5\u7740\u8d70\u5230\u5c4b\u4fa7\uff0c\u63d0\u58f0\u559d\u9053\uff1a\u201c\u4efb\u901a\u6b66\uff0c\u4f60\u5e72\u4ec0\u4e48\uff1f\u95ef\u5230\u592a\u592b\u4eba\u5c4b\u91cc\u6765\uff0c\u60f3\u9020\u53cd\u4e48\uff1f\u201d\u8fd9\u4e00\u559d\u66f4\u4ee4\u4efb\u901a\u6b66\u6478\u4e0d\u7740\u534a\u70b9\u5934\u8111\uff0c\u7ed3\u7ed3\u5df4\u5df4\u7684\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u2026\u2026\u201d \u3000\u3000\u80e1\u6590\u559d\u9053\uff1a\u201c\u5feb\u505c\u6b65\uff0c\u4f60\u56fe\u8c0b\u4e0d\u8f68\u4e48\uff1f\u201d\u4f17\u536b\u58eb\u542c\u4ed6\u5406\u559d\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u4e00\u9f50\u5954\u4e86\u8fc7\u6765\u3002\u80e1\u6590\u4f38\u638c\u6258\u5728\u4efb\u901a\u6b66\u7684\u80cc\u4e0a\uff0c\u638c\u529b\u4e00\u9001\uff0c\u4ed6\u90a3\u5e9e\u5927\u7684\u8eab\u8eaf\u98de\u4e86\u51fa\u53bb\uff0c\u7830\u7684\u4e00\u58f0\uff0c\u649e\u5728\u7a97\u683c\u4e4b\u4e0a\uff0c\u767b\u65f6\u6728\u5c51\u7eb7\u98de\u3002\u80e1\u6590\u53eb\u9053\uff1a\u201c\u62ff\u4f4f\u4ed6\uff0c\u62ff\u4f4f\u4ed6\uff01\u5feb\u5feb\uff01\u201d \u3000\u3000\u4f17\u536b\u58eb\u4e00\u62e5\u800c\u4e0a\uff0c\u90fd\u53bb\u6349\u62ff\u4efb\u901a\u6b66\u3002\u80e1\u6590\u5927\u53eb\uff1a\u201c\u83ab\u60ca\u5413\u4e86\u592a\u592b\u4eba\uff01\u8fd9\u53cd\u8d3c\u80c6\u5b50\u5012\u662f\u4e0d\u5c0f\u3002\u201d\u4e00\u9762\u53eb\u56b7\uff0c\u4e00\u9762\u51b2\u8fdb\u623f\u53bb\u3002 \u3000\u3000\u53ea\u89c1\u592a\u592b\u4eba\u53cc\u624b\u5404\u62c9\u7740\u4e00\u4e2a\u5b69\u5b50\uff0c\u60ca\u95ee\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d\u90a3\u4e24\u5b69\u5b50\u5140\u5728\u557c\u54ed\uff0c\u53eb\u7740\uff1a\u201c\u6211\u8981\u5988\u5988\uff0c\u6211\u8981\u5988\u5988\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u6709\u523a\u5ba2\uff01\u5c0f\u4eba\u4fdd\u62a4\u592a\u592b\u4eba\u548c\u4e24\u4f4d\u516c\u5b50\u7237\u51fa\u53bb\u3002\u201d\u592a\u592b\u4eba\u591a\u89c1\u4e8b\u6545\uff0c\u4e00\u51db\u4e4b\u4e0b\uff0c\u5fc3\u4e2d\u8d77\u7591\uff0c\u559d\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u523a\u5ba2\u5728\u54ea\u91cc\uff1f\u201d\u80e1\u6590\u4e0d\u6562\u591a\u803d\uff0c\u53c8\u607c\u6068\u5979\u5fc3\u80a0\u6bd2\u8fa3\uff0c\u4e0b\u624b\u6bd2\u5bb3\u9a6c\u6625\u82b1\uff0c\u5f53\u5373\u62a2\u4e0a\u4e00\u6b65\uff0c\u53cd\u624b\u4fbf\u662f\u4e00\u638c\u3002 \u3000\u3000\u8fd9\u592a\u592b\u4eba\u8d35\u4e3a\u76f8\u56fd\u592b\u4eba\uff0c\u5f53\u4eca\u7687\u5e1d\u662f\u5979\u60c5\u90ce\uff0c\u4e09\u4e2a\u513f\u5b50\u90fd\u505a\u5c1a\u4e66\uff0c\u4e24\u4e2a\u5ab3\u5987\u662f\u91d1\u679d\u7389\u53f6\u7684\u516c\u4e3b\uff0c\u51fa\u4e16\u4ee5\u6765\uff0c\u54ea\u91cc\u53d7\u8fc7\u8fd9\u822c\u6bb4\u8fb1\uff1f\u80e1\u6590\u867d\u77e5\u5979\u5fc3\u80a0\u4e4b\u6bd2\uff0c\u4e0d\u4e0b\u4e8e\u5927\u5978\u5de8\u6076\uff0c\u4f46\u7ec8\u7a76\u5ff5\u5979\u662f\u4e2a\u5e74\u8001\u5987\u4eba\uff0c\u4e0d\u6b32\u4fbf\u6b64\u4f24\u5979\u6027\u547d\uff0c\u8fd9\u4e00\u638c\u53ea\u4f7f\u4e86\u4e00\u5206\u529b\u6c14\u3002\u9976\u662f\u5982\u6b64\uff0c\u5979\u53f3\u988a\u5df2\u9ad8\u9ad8\u80bf\u8d77\uff0c\u6ee1\u53e3\u9c9c\u8840\uff0c\u8dcc\u843d\u4e86\u4e24\u679a\u7259\u9f7f\uff0c\u60ca\u6012\u4e4b\u4e0b\uff0c\u51e0\u4e4e\u6655\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u80e1\u6590\u4fef\u8eab\u5bf9\u4e24\u4e2a\u5b69\u5b50\u9053\uff1a\u201c\u6211\u5e26\u4f60\u4eec\u53bb\u89c1\u5988\u5988\u3002\u5988\u5988\u60f3\u5ff5\u4f60\u4eec\u5f97\u7d27\u3002\u201d\u4e24\u4e2a\u5b69\u513f\u767b\u65f6\u7b11\u9010\u989c\u5f00\uff0c\u4f38\u51fa\u56db\u6761\u5c0f\u624b\u81c2\uff0c\u8981\u80e1\u6590\u62b1\u4e86\u53bb\u89c1\u6bcd\u4eb2\u3002\u80e1\u6590\u5de6\u81c2\u4e00\u957f\uff0c\u4e00\u81c2\u62b1\u8d77\u4e24\u4e2a\u5b69\u5b50\uff0c\u4fbf\u5728\u6b64\u65f6\uff0c\u5df2\u6709\u4e24\u540d\u536b\u58eb\u5954\u8fdb\u5c4b\u6765\u3002 \u3000\u3000\u80e1\u6590\u5fc3\u60f3\uff0c\u82e5\u4e0d\u501f\u91cd\u592a\u592b\u4eba\uff0c\u5b9e\u96be\u8131\u8eab\uff0c\u4f38\u53f3\u624b\u6293\u4f4f\u592a\u592b\u4eba\u8863\u9886\uff0c\u559d\u9053\uff1a\u201c\u592a\u592b\u4eba\u5728\u6211\u638c\u63e1\u4e4b\u4e2d\uff0c\u4f60\u4eec\u4e0a\u6765\uff0c\u5927\u5bb6\u4e00\u9f50\u90fd\u6b7b\uff01\u201d\u8bf4\u7740\u62a2\u6b65\u4fbf\u5f80\u5916\u95ef\u3002 \u3000\u3000\u8fd9\u65f6\u51e0\u540d\u536b\u58eb\u5df2\u5c06\u4efb\u901a\u6b66\u64d2\u4f4f\uff0c\u773c\u7741\u7741\u7684\u89c1\u80e1\u6590\u4e00\u624b\u62b1\u4e86\u4e24\u4e2a\u5b69\u5b50\uff0c\u4e00\u624b\u62c9\u7740\u592a\u592b\u4eba\u76f4\u5f80\u5916\u5954\u3002\u4f17\u536b\u58eb\u6295\u9f20\u5fcc\u5668\uff0c\u90a3\u6562\u4e0a\u524d\u52a8\u624b\uff1f\u53ea\u662f\u8fde\u58f0\u553f\u54e8\uff0c\u7d27\u8ddf\u5728\u4ed6\u8eab\u540e\u56db\u4e94\u6b65\u4e4b\u5904\uff0c\u624b\u4e2d\u5200\u5251\u8ddd\u4ed6\u80cc\u5fc3\u4e0d\u8fc7\u6570\u5c3a\uff0c\u867d\u89c1\u4ed6\u65e0\u6cd5\u5206\u624b\u62b5\u5fa1\uff0c\u4f46\u7ec8\u7a76\u4e0d\u6562\u9012\u4e0a\u524d\u53bb\u3002\u80e1\u6590\u5fc3\u4e2d\u4e5f\u662f\u6697\u6697\u53eb\u82e6\uff0c\u773c\u89c1\u56ed\u4e2d\u4f17\u536b\u58eb\u56db\u9762\u516b\u65b9\u7684\u805a\u96c6\uff0c\u81ea\u5df1\u5e26\u7740\u4e00\u8001\u4e8c\u5c11\uff0c\u62d6\u62d6\u62c9\u62c9\uff0c\u54ea\u91cc\u80fd\u51fa\u5e9c\u95e8\uff1f \u3000\u3000\u654c\u4eba\u7eb5\u7136\u5fc3\u5b58\u987e\u5fcc\uff0c\u4f46\u53ea\u8981\u6709\u4eba\u5927\u80c6\u4e0a\u524d\uff0c\u81ea\u5df1\u603b\u4e0d\u80fd\u5f53\u771f\u4fbf\u5c06\u592a\u592b\u4eba\u6253\u6b7b\u3002 \u3000\u3000\u65e0\u6cd5\u53ef\u65bd\u4e4b\u4e0b\uff0c\u53ea\u6709\u6025\u6b65\u5411\u524d\u3002\u8fd9\u4e00\u6765\u53cc\u65b9\u6210\u4e86\u50f5\u6301\u4e4b\u5c40\uff0c\u4f17\u536b\u58eb\u56fa\u7136\u4e0d\u6562\u4e0a\u524d\u52a8\u624b\uff0c\u80e1\u6590\u5374\u4e5f\u4e0d\u80fd\u8131\u51fa\u9669\u5730\uff0c\u65f6\u5019\u4e00\u957f\uff0c\u536b\u58eb\u8d8a\u96c6\u8d8a\u591a\uff0c\u5904\u5883\u4fbf\u8d8a\u662f\u5371\u9669\u3002\u4e00\u65f6\u82e6\u65e0\u5584\u7b56\uff0c\u53ea\u6709\u8c41\u51fa\u4e86\u6027\u547d\u4e0d\u8981\uff0c\u8d70\u4e00\u6b65\u4fbf\u7b97\u4e00\u6b65\uff0c\u4f46\u542c\u5f97\u53eb\u56b7\u4f20\u4ee4\u4e4b\u58f0\uff0c\u56db\u4e0b\u547c\u5e94\u3002\u4ed6\u4e00\u624b\u62b1\u7740\u5b69\u5b50\uff0c\u4e00\u624b\u62d6\u7740\u592a\u592b\u4eba\uff0c\u884c\u8d70\u4e0d\u5feb\uff0c\u53ea\u662f\u5f80\u9ed1\u6697\u5904\u95ef\u53bb\u3002 \u3000\u3000\u4fbf\u5728\u6b64\u65f6\uff0c\u5ffd\u89c1\u5de6\u9996\u706b\u5149\u4e00\u95ea\uff0c\u6709\u4eba\u5927\u58f0\u53eb\u9053\uff1a\u201c\u523a\u5ba2\u884c\u523a\u516c\u4e3b\uff01\u8981\u70e7\u6b7b\u516c\u4e3b\u5566\uff0c\u8981\u70e7\u6b7b\u516c\u4e3b\u5566\uff01\u201d\u80e1\u6590\u4e00\u6014\uff0c\u542c\u53eb\u56b7\u4e4b\u58f0\u6b63\u662f\u5468\u94c1\u9e6a\u3002\u4f46\u89c1\u6d53\u70df\u706b\u7130\uff0c\u4ece\u5de6\u8fb9\u7684\u4e00\u6392\u5c4b\u4e2d\u51b2\u5929\u800c\u8d77\u3002\u90a3\u548c\u5609\u516c\u4e3b\u662f\u5f53\u4eca\u7687\u5e1d\u7684\u4eb2\u751f\u7231\u5973\u3002\u82e5\u6709\u5931\u95ea\uff0c\u798f\u5eb7\u5b89\u5e9c\u4e2d\u5408\u5e9c\u536b\u58eb\u90fd\u6709\u91cd\u7f6a\u3002\u53ea\u6240\u5468\u94c1\u9e6a\u53c8\u53eb\u9053\uff1a\u201c\u5927\u5bb6\u5feb\u53bb\u6551\u706b\uff0c\u83ab\u4f24\u4e86\u516c\u4e3b\uff0c\u6211\u6765\u6551\u592a\u592b\u4eba\u3002\u201d\u5468\u94c1\u9e6a\u5728\u798f\u5eb7\u5b89\u624b\u4e0b\u7d20\u6709\u5a01\u4fe1\uff0c\u4f17\u536b\u58eb\u53c8\u5728\u60ca\u60f6\u5931\u63aa\u4e4b\u4e0b\uff0c\u542c\u4ed6\u53eb\u58f0\u5a01\u4e25\uff0c\u81ea\u6709\u4e00\u80a1\u6151\u4eba\u4e4b\u52bf\uff0c\u4e8e\u662f\u4e00\u7a9d\u8702\u7684\u5411\u516c\u4e3b\u7684\u4f4f\u6240\u5954\u53bb\u3002 \u3000\u3000\u80e1\u6590\u5df2\u77e5\u8fd9\u662f\u4ed6\u8c03\u864e\u79bb\u5c71\u4e4b\u8ba1\uff0c\u597d\u66ff\u81ea\u5df1\u8131\u56f0\uff0c\u5fc3\u4e0b\u597d\u751f\u611f\u6fc0\u3002\u53ea\u89c1\u5468\u94c1\u9e6a\u75be\u5954\u800c\u81f3\uff0c\u4e00\u5200\u6402\u5934\u780d\u5230\u3002\u80e1\u6590\u5411\u65c1\u4e00\u95ea\uff0c\u559d\u9053\uff1a\u201c\u597d\u5389\u5bb3\uff01\u201d\u5c06\u592a\u592b\u4eba\u5411\u4ed6\u4e00\u63a8\u3002\u5468\u94c1\u9e6a\u6276\u4f4f\u592a\u592b\u4eba\uff0c\u8d1f\u5728\u80cc\u4e0a\u3002\u80e1\u6590\u4e00\u624b\u62b1\u4e86\u4e00\u4e2a\u5b69\u5b50\u3002\u811a\u4e0b\u767b\u65f6\u5feb\u4e86\uff0c\u53ea\u542c\u5468\u94c1\u9e6a\u53c8\u63d0\u6c14\u53eb\u9053\uff1a\u201c\u523a\u5ba2\u6765\u5f97\u4e0d\u5c11\uff0c\u5404\u4eba\u7d27\u5b88\u539f\u5730\uff0c\u4fdd\u62a4\u5927\u5e05\u548c\u4e24\u4f4d\u516c\u4e3b\uff0c\u5343\u4e07\u4e0d\u53ef\u4e2d\u4e86\u523a\u5ba2\u7684\u8c03\u864e\u79bb\u5c71\u4e4b\u8ba1\u3002\u201d\u4f17\u536b\u58eb\u4e00\u542c\u201c\u8c03\u864e\u79bb\u5c71\u201d\u56db\u5b57\uff0c\u5fc3\u4e0b\u5747\u5404\u51db\u7136\uff0c\u4e0d\u6562\u518d\u8ffd\u3002 \u3000\u3000\u80e1\u6590\u75be\u8d8b\u82b1\u56ed\u540e\u95e8\uff0c\u7ffb\u5899\u800c\u51fa\uff0c\u5374\u53ea\u53eb\u5f97\u4e00\u58f0\u82e6\uff0c\u4f46\u89c1\u4e1c\u9762\u897f\u9762\uff0c\u90fd\u662f\u9ed1\u538b\u538b\u7684\u4e00\u7247\uff0c\u7ad9\u6ee1\u4e86\u536b\u58eb\u3002\u4ed6\u62b1\u4e86\u4e24\u4e2a\u5b69\u5b50\uff0c\u8d8a\u8fc7\u4e00\u5927\u7247\u7a7a\u5730\uff0c\u62a2\u8fdb\u4e86\u4e00\u6761\u80e1\u540c\u3002\u4f17\u536b\u58eb\u5927\u547c\uff1a\u201c\u62ff\u523a\u5ba2\uff0c\u62ff\u523a\u5ba2\uff01\u201d\u81ea\u540e\u8ffd\u6765\u3002 \u3000\u3000\u80e1\u6590\u5954\u5b8c\u80e1\u540c\uff0c\u8f6c\u5230\u4e00\u6761\u6a2a\u8857\uff0c\u53ea\u89c1\u524d\u9762\u4e00\u8f86\u9aa1\u8f66\u505c\u5728\u8857\u5fc3\u3002\u80e1\u6590\u4e00\u8dc3\u4e0a\u8f66\uff0c\u53eb\u9053\uff1a\u201c\u5feb\u8d76\uff0c\u5feb\u8d76\uff01\u91cd\u91cd\u8d4f\u4f60\u94f6\u5b50\uff01\u201d \u3000\u3000\u8f66\u592b\u4f4d\u4e0a\u5e76\u80a9\u5750\u7740\u4e24\u4eba\u3002\u53f3\u8fb9\u4e00\u4e2a\u8eab\u6750\u7626\u524a\u7684\u6c49\u5b50\u4e00\u63d0\u7f30\u7ef3\uff0c\u97ad\u5b50\u62cd\u7684\u4e00\u54cd\uff0c\u9aa1\u5b50\u62c9\u7740\u8f66\u5b50\u4fbf\u8dd1\u3002 \u3000\u3000\u80e1\u6590\u5598\u606f\u7a0d\u5b9a\uff0c\u53ea\u89c9\u5947\u81ed\u51b2\u9f3b\uff0c\u5b9a\u775b\u4e00\u770b\uff0c\u89c1\u8f66\u4e0a\u88c5\u6ee1\u4e86\u7caa\u6876\uff0c\u539f\u6765\u90a3\u662f\u6328\u95e8\u6cbf\u6237\u66ff\u4eba\u5012\u7caa\u6876\u7684\u4e00\u8f86\u7caa\u8f66\uff0c\u5fc3\u60f3\uff1a \u3000\u3000\u201c\u602a\u4e0d\u5f97\u534a\u591c\u4e09\u66f4\u7684\uff0c\u7adf\u6709\u4e00\u8f86\u9aa1\u8f66\u5728\u8fd9\u513f\uff1f\u201d\u56de\u5934\u671b\u65f6\uff0c\u89c1\u4f17\u536b\u58eb\u5927\u58f0\u5450\u558a\uff0c\u968f\u540e\u8d76\u6765\u3002 \u3000\u3000\u4ed6\u5fc3\u5ff5\u4e00\u52a8\uff0c\u63d0\u8d77\u4e00\u53ea\u7caa\u6876\uff0c\u5411\u540e\u63b7\u4e86\u8fc7\u53bb\u3002\u8fd9\u4e00\u63b7\u529b\u9053\u6781\u731b\uff0c\u4e24\u540d\u5954\u5728\u6700\u5148\u7684\u536b\u58eb\u767b\u65f6\u7ed9\u7caa\u6876\u649e\u5012\uff0c\u6dcb\u6f13\u6ee1\u8eab\uff0c\u4e00\u65f6\u7adf\u7136\u722c\u4e0d\u8d77\u6765\u3002\u5176\u4f59\u4f17\u536b\u58eb\u89c1\u72b6\uff0c\u4e00\u9f50\u9a7b\u8db3\u3002\u8fd9\u4e9b\u4eba\u90fd\u662f\u7cbe\u9009\u7684\u608d\u52c7\u6b66\u58eb\uff0c\u5200\u5c71\u67aa\u6797\u5413\u4ed6\u4eec\u4e0d\u5012\uff0c\u4f46\u5927\u7caa\u6876\u5f53\u5934\u63b7\u6765\uff0c\u5374\u662f\u8c01\u4e5f\u4e0d\u6562\u5c1d\u4e00\u5c1d\u8fd9\u822c\u6ecb\u5473\u3002 \u3000\u3000\u90a3\u9aa1\u5b50\u8db3\u4e0d\u505c\u6b65\u7684\u5411\u524d\u76f4\u8dd1\uff0c\u4f46\u8fc7\u4e0d\u591a\u65f6\uff0c\u540e\u9762\u4eba\u58f0\u9690\u9690\uff0c\u4f17\u536b\u58eb\u53c8\u8d76\u4e86\u4e0a\u6765\u3002\u987b\u77e5\u798f\u5eb7\u5b89\u662f\u5f53\u671d\u5175\u90e8\u5c1a\u4e66\uff0c\u6267\u638c\u5929\u4e0b\u5175\u9a6c\u5927\u6743\uff0c\u5e9c\u4e2d\u536b\u58eb\u4e2a\u4e2a\u5747\u975e\u5eb8\u624b\uff0c\u7ed9\u80e1\u6590\u63a5\u8fde\u4e24\u665a\u95f9\u4e86\u4e2a\u5929\u7ffb\u5730\u8986\uff0c\u4f17\u536b\u58eb\u7684\u8138\u76ae\u5f80\u54ea\u91cc\u6401\u53bb\uff1f\u56e0\u6b64\u4e00\u89c1\u7caa\u8f66\u8dd1\u8fdc\uff0c\u7caa\u6876\u5df2\u63b7\u6295\u4e0d\u5230\uff0c\u5404\u4eba\u8e0f\u8fc7\u6ee1\u5730\u7caa\u6c34\uff0c\u9532\u800c\u4e0d\u820d\u7684\u7ee7\u7eed\u8ffd\u8d76\u3002 \u3000\u3000\u80e1\u6590\u5fc3\u4e0b\u70e6\u607c\uff1a\u201c\u5018\u82e5\u6211\u4fbf\u8fd9\u4e48\u56de\u53bb\uff0c\u5c82\u4e0d\u662f\u81ea\u884c\u6cc4\u9732\u4e86\u4f4f\u5904\uff1f\u9a6c\u59d1\u5a18\u672a\u8131\u9669\u5883\uff0c\u600e\u80fd\u5f15\u9b3c\u4e0a\u95e8\uff1f\u4f46\u82e5\u4e0d\u56de\u4f4f\u5904\uff0c\u5374\u53c8\u8eb2\u5230\u54ea\u91cc\u53bb\uff1f\u201d\u4fbf\u8fd9\u4e48\u5bfb\u601d\u4e4b\u9645\uff0c\u4f17\u536b\u58eb\u53c8\u8feb\u5f97\u8fd1\u4e86\u4e9b\uff0c\u53ea\u662f\u5bb3\u6015\u7caa\u6876\uff0c\u4e0d\u6562\u5341\u5206\u903c\u8fd1\uff0c\u5404\u4eba\u5747\u60f3\uff1a\u201c\u54b1\u4eec\u4fbf\u662f\u8fd9\u4e48\u8fdc\u8fdc\u8ddf\u7740\uff0c\u96be\u9053\u5728\u8fd9\u5317\u4eac\u57ce\u4e2d\uff0c\u4f60\u8fd8\u80fd\u63d2\u7fc5\u98de\u53bb\uff1f\u201d \u3000\u3000\u8f6c\u773c\u4e4b\u95f4\uff0c\u9aa1\u8f66\u9a70\u5230\u4e00\u4e2a\u5341\u5b57\u8def\u53e3\uff0c\u53ea\u89c1\u8857\u5fc3\u53c8\u505c\u7740\u4e00\u8f86\u7caa\u8f66\u3002\u80e1\u6590\u6240\u4e58\u7684\u8f66\u5b50\u9a70\u7740\u9760\u8fd1\uff0c\u8d76\u9aa1\u5b50\u7684\u8f66\u592b\u4f38\u81c2\u5411\u80e1\u6590\u4e00\u62db\uff0c\u559d\u9053\uff1a\u201c\u8fc7\u53bb\uff01\u201d\u7eb5\u8eab\u4e00\u8dc3\uff0c\u5750\u4e0a\u4e86\u53e6\u4e00\u8f86\u7caa\u8f66\u3002\u80e1\u6590\u62b1\u7740\u4e24\u4e2a\u5b69\u5b50\u8ddf\u7740\u8dc3\u8fc7\u3002\u5148\u524d\u8f66\u4e0a\u7684\u53e6\u4e00\u4e2a\u6c49\u5b50\u63a5\u8fc7\u7f30\u7ef3\uff0c\u7adf\u662f\u6beb\u4e0d\u505c\u7559\uff0c\u5411\u897f\u8fb9\u5c94\u9053\u4e0a\u5954\u4e86\u4e0b\u53bb\u3002\u80e1\u6590\u6240\u4e58\u7684\u9aa1\u8f66\u5374\u5411\u4e1c\u884c\u3002 \u3000\u3000\u5f85\u5f97\u4f17\u536b\u58eb\u8ffd\u5230\uff0c\u53ea\u89c1\u4e24\u8f86\u4e00\u6a21\u4e00\u6837\u7684\u7caa\u8f66\uff0c\u4e00\u8f86\u5411\u4e1c\uff0c\u4e00\u8f86\u5411\u897f\uff0c\u5374\u4e0d\u77e5\u523a\u5ba2\u662f\u5728\u90a3\u4e00\u8f86\u8f66\u4e2d\u3002\u4f17\u4eba\u7565\u4e00\u5546\u8bae\uff0c\u5f53\u4e0b\u5175\u5206\u4e24\u8def\uff0c\u5206\u5934\u8ffd\u8d76\u3002 \u3000\u3000\u80e1\u6590\u542c\u4e86\u90a3\u8eab\u6750\u7626\u524a\u7684\u6c49\u5b50\u90a3\u4e00\u58f0\u547c\u559d\uff0c\u53c8\u89c1\u4e86\u8fd9\u4e00\u8dc3\u7684\u8eab\u6cd5\uff0c\u5df2\u77e5\u662f\u7a0b\u7075\u7d20\u524d\u6765\u63a5\u5e94\uff0c\u559c\u9053\uff1a\u201c\u4e8c\u59b9\uff0c\u539f\u6765\u662f\u4f60\uff01\u201d \u3000\u3000\u7a0b\u7075\u7d20\u201c\u54fc\u201d\u7684\u4e00\u58f0\uff0c\u5e76\u4e0d\u7b54\u8bdd\u3002\u80e1\u6590\u53c8\u95ee\uff1a\u201c\u9a6c\u59d1\u5a18\u600e\u6837\uff1f \u3000\u3000\u75c5\u52bf\u6ca1\u8f6c\u5427\uff1f\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u4e0d\u77e5\u9053\u3002\u201d\u80e1\u6590\u77e5\u5979\u751f\u6c14\u4e86\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u4e8c\u59b9\uff0c\u6211\u6ca1\u542c\u4f60\u8bdd\uff0c\u539f\u662f\u6211\u7684\u4e0d\u662f\uff0c\u8bf7\u4f60\u539f\u8c05\u8fd9\u4e00\u6b21\u3002\u201d \u3000\u3000\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u6211\u8bf4\u8fc7\u4e0d\u7ed9\u5979\u6cbb\u75c5\uff0c\u4fbf\u4e0d\u6cbb\u75c5\u3002\u96be\u9053\u6211\u8bf4\u7684\u4e0d\u662f\u4eba\u8bdd\u4e48\uff1f\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u53c8\u5230\u4e86\u4e00\u5904\u5c94\u9053\uff0c\u4f46\u89c1\u8857\u4e2d\u5fc3\u4ecd\u662f\u505c\u7740\u4e00\u8f86\u7caa\u8f66\u3002\u8fd9\u4e00\u6b21\u7a0b\u7075\u7d20\u5374\u4e0d\u6362\u8f66\uff0c\u53ea\u662f\u553f\u54e8\u4e00\u58f0\uff0c\u505a\u4e2a\u624b\u52bf\uff0c\u4e24\u8f86\u7caa\u8f66\u5206\u5411\u5357\u5317\uff0c\u540c\u65f6\u5954\u884c\u3002\u4f17\u536b\u58eb\u8ffd\u5230\u65f6\u9762\u9762\u76f8\u89d1\uff0c\u5927\u547c\uff1a \u3000\u3000\u201c\u90aa\u95e8\uff01\u90aa\u95e8\uff01\u201d\u53ea\u5f97\u53c8\u5206\u4e00\u534a\u4eba\u5317\u8d76\uff0c\u4e00\u534a\u4eba\u5357\u8ffd\u3002 \u3000\u3000\u5317\u4eac\u57ce\u4e2d\u8857\u9053\u6709\u5982\u68cb\u76d8\uff0c\u4e00\u9053\u9053\u7eb5\u901a\u5357\u5317\uff0c\u6a2a\u8d2f\u4e1c\u897f\uff0c\u56e0\u6b64\u884c\u4e0d\u5230\u6570\u7bad\u4e4b\u5730\uff0c\u4fbf\u51fa\u73b0\u4e00\u6761\u5c94\u9053\uff0c\u6bcf\u5904\u5341\u5b57\u8def\u53e3\uff0c\u5fc5\u6709\u4e00\u8f86\u7caa\u8f66\u505c\u7740\u3002\u7a0b\u7075\u7d20\u89c1\u4f17\u536b\u58eb\u8ffd\u5f97\u8fd1\u4e86\uff0c\u4fbf\u4e0d\u6362\u8f66\uff0c\u4ee5\u514d\u7eb5\u8d77\u8dc3\u843d\u65f6\u7ed9\u4ed6\u4eec\u53d1\u89c9\uff0c\u82e5\u662f\u76f8\u8ddd\u751a\u8fdc\uff0c\u4fbf\u548c\u80e1\u6590\u643a\u540c\u4e24\u5b69\u6362\u4e00\u8f86\u8f66\uff0c\u4f7f\u9aa1\u5b50\u529b\u65b0\uff0c\u5954\u9a70\u66f4\u5feb\u3002\u8fd9\u6837\u6bcf\u5230\u4e00\u5904\u5c94\u9053\uff0c\u4f17\u536b\u58eb\u7684\u4eba\u6570\u4fbf\u51cf\u5c11\u4e86\u4e00\u534a\uff0c\u5230\u5f97\u540e\u6765\uff0c\u7a00\u7a00\u843d\u843d\u7684\u53ea\u6709\u4e94\u516d\u4eba\u8ffd\u5728\u540e\u9762\u3002\u8fd9\u4e94\u516d\u4eba\u4e5f\u5df2\u5954\u5f97\u6c14\u5598\u5401\u5401\uff0c\u811a\u6b65\u6162\u4e86\u5f88\u591a\u3002 \u3000\u3000\u80e1\u6590\u53c8\u9053\uff1a\u201c\u4e8c\u59b9\uff0c\u4f60\u8fd9\u6761\u8ba1\u7b56\u771f\u662f\u518d\u5999\u4e0d\u8fc7\uff0c\u5018\u82e5\u4e0d\u662f\u96c7\u7528\u6df1\u591c\u5012\u7caa\u7684\u7caa\u8f66\uff0c\u5bfb\u5e38\u7684\u5927\u8f66\u4e00\u8f86\u8f86\u505c\u5728\u8857\u5fc3\uff0c\u7ed9\u5de1\u591c\u5b98\u5175\u77a7\u89c1\u4e86\uff0c\u5b9a\u4f1a\u8d77\u7591\u3002\u201d\u7a0b\u7075\u7d20\u51b7\u7b11\u9053\uff1a\u201c\u8d77\u7591\u53c8\u600e\u4e48\u6837\uff1f\u53cd\u6b63\u4f60\u4e0d\u7231\u60dc\u81ea\u5df1\uff0c\u4fbf\u662f\u6b7b\u5728\u5b98\u5175\u624b\u4e2d\uff0c\u4e5f\u662f\u6d3b\u8be5\u3002\u201d\u80e1\u6590\u7b11\u9053\uff1a\u201c\u6211\u6b7b\u662f\u6d3b\u8be5\uff0c\u53ea\u662f\u7d2f\u5f97\u59d1\u5a18\u4f24\u5fc3\uff0c\u90a3\u4fbf\u8fc7\u610f\u4e0d\u53bb\u3002\u201d\u7a0b\u7075\u7d20\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u542c\u6211\u8bdd\uff0c\u81ea\u5df1\u7231\u9001\u547d\uff0c\u624d\u6ca1\u4eba\u4e3a\u4f60\u4f24\u5fc3\u5462\u3002\u9664\u975e\u662f\u4f60\u90a3\u4e2a\u591a\u60c5\u591a\u4e49\u7684\u8881\u59d1\u5a18\u2026\u2026\u5979\u53c8\u600e\u4e48\u4e0d\u6765\u52a9\u4f60\u4e00\u81c2\u4e4b\u529b\uff1f\u201d\u80e1\u6590\u9053\uff1a\u201c\u5979\u6ca1\u77e5\u9053\u6211\u4f1a\u8fd9\u6837\u50bb\uff0c\u7adf\u4f1a\u95ef\u8fdb\u798f\u5927\u5e05\u5e9c\u4e2d\u53bb\u3002\u5929\u4e0b\u53ea\u6709\u4e00\u4f4d\u59d1\u5a18\uff0c\u624d\u77e5\u9053\u6211\u4f1a\u8fd9\u822c\u86ee\u5e72\u80e1\u6765\uff0c\u4e5f\u53ea\u6709\u5979\uff0c\u624d\u80fd\u5728\u7d27\u6025\u5173\u5934\u6551\u6211\u6027\u547d\u3002\u201d \u3000\u3000\u8fd9\u51e0\u53e5\u8bdd\u8bf4\u5f97\u7a0b\u7075\u7d20\u5fc3\u4e2d\u8212\u670d\u6170\u8d34\u65e0\u6bd4\uff0c\u54fc\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5f53\u5e74\u6551\u4f60\u6027\u547d\u7684\u662f\u9a6c\u59d1\u5a18\uff0c\u6240\u4ee5\u4f60\u8fd9\u822c\u5ff5\u5ff5\u4e0d\u5fd8\uff0c\u8981\u62a5\u5979\u5927\u6069\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u5728\u6211\u5fc3\u4e2d\uff0c\u9a6c\u59d1\u5a18\u600e\u80fd\u8ddf\u6211\u7684\u4e8c\u59b9\u76f8\u6bd4\uff1f\u201d \u3000\u3000\u7a0b\u7075\u7d20\u5728\u9ed1\u6697\u4e2d\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u6c42\u6211\u6551\u6cbb\u9a6c\u59d1\u5a18\uff0c\u4ec0\u4e48\u597d\u542c\u7684\u8bdd\u90fd\u4f1a\u8bf4\u3002\u5f85\u5f97\u4e0d\u6c42\u4eba\u5bb6\u4e86\uff0c\u4fbf\u53c8\u628a\u6211\u7684\u8bf4\u8bdd\u5f53\u4f5c\u8033\u8fb9\u98ce\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u5018\u82e5\u6211\u8bf4\u7684\u662f\u5047\u8bdd\uff0c\u6559\u6211\u4e0d\u5f97\u597d\u6b7b\u3002\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u771f\u4fbf\u771f\uff0c\u5047\u4fbf\u5047\uff0c\u8c01\u8981\u4f60\u8d4c\u5492\u53d1\u8a93\u4e86\uff1f\u201d\u5979\u8fd9\u53e5\u8bdd\u53e3\u6c14\u677e\u52a8\u4e0d\u5c11\uff0c\u663e\u662f\u80f8\u4e2d\u7684\u6c14\u607c\u5df2\u6d88\u4e86\u5927\u534a\u3002 \u3000\u3000\u518d\u8fc7\u4e00\u4e2a\u5341\u5b57\u8def\u53e3\uff0c\u53ea\u89c1\u8ddf\u5728\u8f66\u540e\u7684\u536b\u58eb\u53ea\u5269\u4e0b\u4e24\u4eba\u3002\u80e1\u6590\u7b11\u9053\uff1a\u201c\u4e8c\u59b9\uff0c\u4f60\u62c9\u4e00\u62c9\u7f30\uff0c\u6211\u53d8\u4e2a\u620f\u6cd5\u4f60\u77a7\u3002\u201d\u7a0b\u7075\u7d20\u5de6\u624b\u4e00\u52d2\uff0c\u90a3\u9aa1\u5b50\u500f\u5730\u505c\u6b65\u3002\u5728\u540e\u8ffd\u8d76\u7684\u4e24\u540d\u536b\u58eb\u5954\u5f97\u51e0\u6b65\uff0c\u4e0e\u9aa1\u8f66\u5df2\u76f8\u8ddd\u4e0d\u8fdc\u3002\u80e1\u6590\u63d0\u8d77\u4e00\u53ea\u7a7a\u7caa\u6876\uff0c\u731b\u5730\u63b7\u51fa\uff0c\u5657\u7684\u4e00\u54cd\uff0c\u6b63\u597d\u5957\u5728\u4e00\u540d\u536b\u58eb\u7684\u5934\u4e0a\u3002\u53e6\u4e00\u540d\u536b\u58eb\u5403\u4e86\u4e00\u60ca\uff0c\u201c\u554a\u201d\u7684\u4e00\u58f0\u5927\u53eb\uff0c\u8f6c\u8eab\u4fbf\u9003\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u89c1\u4e86\u8fd9\u6ed1\u7a3d\u60c5\u72b6\uff0c\u5fcd\u4e0d\u4f4f\u5657\u54e7\u4e00\u58f0\uff0c\u7b11\u4e86\u51fa\u6765\u3002\u4fbf\u5728\u8fd9\u4e00\u7b11\u4e4b\u4e2d\uff0c\u6ee1\u8154\u6012\u706b\u7ec8\u4e8e\u5316\u4e3a\u4e4c\u6709\u3002 \u3000\u3000\u80e1\u6590\u548c\u5979\u5e76\u80a9\u5750\u5728\u8f66\u4e0a\uff0c\u63a5\u8fc7\u7f30\u7ef3\uff0c\u8fd9\u65f6\u8ddd\u6628\u665a\u5c45\u4f4f\u4e4b\u5904\u5df2\u7ecf\u4e0d\u8fdc\uff0c\u540e\u9762\u4e5f\u518d\u65e0\u536b\u58eb\u8ffd\u6765\u3002\u4e24\u4eba\u518d\u9a70\u4e00\u7a0b\uff0c\u4fbf\u5373\u4e0b\u8f66\uff0c\u5c06\u8f66\u5b50\u4ea4\u7ed9\u539f\u6765\u7684\u8f66\u592b\uff0c\u53c8\u52a0\u8d4f\u4e86\u4ed6\u4e00\u4e24\u94f6\u5b50\uff0c\u547d\u4ed6\u56de\u53bb\u3002\u5404\u4eba\u62b1\u4e86\u4e00\u4e2a\u5c0f\u5b69\uff0c\u6b65\u884c\u800c\u5f52\uff0c\u8d8a\u5899\u56de\u8fdb\u5c45\u5904\uff0c\u5f53\u771f\u662f\u795e\u4e0d\u77e5\uff0c\u9b3c\u4e0d\u89c9\uff0c\u5374\u6709\u8c01\u77e5\u9053\u8fd9\u4e24\u4eba\u9002\u624d\u6b63\u662f\u4ece\u798f\u5927\u5e05\u5e9c\u4e2d\u5927\u95f9\u800c\u56de\uff1f \u3000\u3000\u9a6c\u6625\u82b1\u89c1\u5230\u4e24\u4e2a\u5b69\u5b50\uff0c\u7cbe\u795e\u5927\u632f\uff0c\u7d27\u7d27\u6402\u4f4f\u4e86\uff0c\u773c\u6cea\u4fbf\u5982\u73cd\u73e0\u65ad\u7ebf\u822c\u6d41\u4e0b\u3002\u4e24\u4e2a\u5b69\u5b50\u4e5f\u662f\u5927\u4e3a\u9ad8\u5174\uff0c\u76f4\u53eb\u201c\u5988\u5988\uff01\u201d \u3000\u3000\u7a0b\u7075\u7d20\u77a7\u7740\u8fd9\u822c\u60c5\u666f\uff0c\u773c\u7736\u5fae\u6e7f\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5927\u54e5\uff0c\u6211\u4e0d\u602a\u4f60\u5566\u3002\u54b1\u4eec\u539f\u8be5\u628a\u5b69\u5b50\u593a\u6765\uff0c\u8ba9\u4ed6\u4eec\u6bcd\u5b50\u56e2\u805a\u3002\u201d\u80e1\u6590\u6b49\u7136\u9053\uff1a\u201c\u6211\u6ca1\u542c\u4f60\u7684\u5429\u5490\uff0c\u5fc3\u4e2d\u603b\u662f\u62b1\u61be\u3002\u201d\u7a0b\u7075\u7d20\u5ae3\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u54b1\u4eec\u7b2c\u4e00\u5929\u89c1\u9762\uff0c\u4f60\u4fbf\u6ca1\u542c\u6211\u5429\u5490\u3002\u6211\u53eb\u4f60\u4e0d\u53ef\u79bb\u6211\u8eab\u8fb9\uff0c\u53eb\u4f60\u4e0d\u53ef\u51fa\u624b\uff0c\u4f60\u542c\u8bdd\u4e86\u4e48\uff1f\u201d \u3000\u3000\u9a6c\u6625\u82b1\u89c1\u5230\u5b69\u5b50\u540e\uff0c\u5fc3\u4e0b\u4e00\u5bbd\uff0c\u75ca\u53ef\u5f97\u4fbf\u5feb\u4e86\uff0c\u518d\u52a0\u7a0b\u7075\u7d20\u7ec6\u5fc3\u65bd\u9488\u4e0b\u836f\uff0c\u4f53\u5185\u6bd2\u6c14\u6e10\u9664\u3002\u53ea\u662f\u5979\u95ee\u8d77\u5982\u4f55\u5230\u4e86\u8fd9\u91cc\uff0c\u798f\u5eb7\u5b89\u4f55\u4ee5\u4e0d\u89c1\uff1f\u80e1\u6590\u548c\u7a0b\u7075\u7d20\u5374\u4e0d\u660e\u8a00\u3002\u4e24\u4e2a\u5b69\u5b50\u5e74\u7eaa\u5c1a\u5c0f\uff0c\u4e5f\u8bf4\u4e0d\u51fa\u4e00\u4e2a\u6240\u4ee5\u7136\u6765\u3002 \u3000"}),showOnHomepage:!0},{name:"\u7b2c\u5341\u4e03\u7ae0\u3000\u5929\u4e0b\u638c\u95e8\u4eba\u5927\u4f1a",content:(0,a.jsx)(a.Fragment,{children:"\u8f6c\u773c\u8fc7\u4e86\u6570\u65e5\uff0c\u5df2\u662f\u4e2d\u79cb\u3002\u8fd9\u65e5\u5348\u540e\uff0c\u80e1\u6590\u5e26\u540c\u7a0b\u7075\u7d20\u3001\u8521\u5a01\u3001\u59ec\u6653\u5cf0\u4e09\u4eba\uff0c\u5f84\u53bb\u798f\u5eb7\u5b89\u5e9c\u4e2d\uff0c\u8d74\u90a3\u5929\u4e0b\u6b66\u6797\u638c\u95e8\u4eba\u5927\u4f1a\u3002 \u3000\u3000\u80e1\u6590\u8fd9\u4e00\u6b21\u7684\u5316\u88c5\uff0c\u4e0e\u65e5\u524d\u866c\u9aef\u6ee1\u816e\uff0c\u53c8\u81ea\u4e0d\u540c\u3002\u4ed6\u526a\u77ed\u4e86\u80e1\u5b50\uff0c\u53c8\u7528\u836f\u67d3\u6210\u9ec4\u8272\uff0c\u8138\u76ae\u4e5f\u6d82\u6210\u4e86\u6de1\u9ec4\uff0c\u5012\u4f3c\u751f\u4e86\u9ec4\u75b8\u75c5\u4e00\u822c\uff0c\u6ee1\u8eab\u9526\u8863\u707f\u70c2\uff0c\u7fe1\u7fe0\u9f3b\u70df\u58f6\u3001\u78a7\u7389\u642c\u6307\u3001\u6ce5\u91d1\u5927\u82b1\u6298\u6247\uff0c\u6253\u626e\u5f97\u53c8\u8c6a\u9614\u53c8\u4fd7\u6c14\uff0c\u7a0b\u7075\u7d20\u5374\u626e\u6210\u4e2a\u4e2d\u5e74\u5987\u4eba\uff0c\u5f13\u80cc\u5f2f\u8170\uff0c\u6ee1\u8138\u76b1\u7eb9\uff0c\u8c01\u53c8\u77a7\u5f97\u51fa\u5979\u662f\u4e2a\u5341\u4e03\u516b\u5c81\u7684\u5927\u59d1\u5a18\uff1f \u3000\u3000\u80e1\u6590\u5bf9\u8521\u5a01\u8bf4\u662f\u5949\u4e86\u5e08\u7236\u4e4b\u547d\uff0c\u4e0d\u5f97\u5728\u638c\u95e8\u4eba\u5927\u4f1a\u4e2d\u9732\u4e86\u771f\u9762\u76ee\u3002\u8521\u5a01\u552f\u552f\u800c\u5e94\uff0c\u4e5f\u4e0d\u591a\u95ee\u3002 \u3000\u3000\u5230\u5f97\u798f\u5eb7\u5b89\u5e9c\u5927\u95e8\u53e3\uff0c\u53ea\u89c1\u536b\u58eb\u5c3d\u64a4\uff0c\u53ea\u6709\u516b\u540d\u77e5\u5ba2\u7ad9\u5728\u95e8\u8fb9\u8fce\u5bbe\u3002\u80e1\u6590\u9012\u4e0a\u6587\u4e66\u3002\u90a3\u77e5\u5ba2\u606d\u800c\u656c\u4e4b\u7684\u8fce\u4e86\u8fdb\u53bb\uff0c\u8bf7\u4ed6\u56db\u4eba\u5728\u4e1c\u9996\u4e00\u5e2d\u4e0a\u5750\u4e0b\u3002 \u3000\u3000\u540c\u5e2d\u7684\u5c1a\u6709\u56db\u4eba\uff0c\u4e92\u76f8\u4e00\u8bf7\u95ee\uff0c\u5374\u539f\u6765\u662f\u7334\u62f3\u5927\u5723\u95e8\u7684\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u89c1\u90a3\u638c\u95e8\u8001\u8005\u9ad8\u9876\u5c16\u5634\uff0c\u7ea2\u816e\u957f\u81c2\uff0c\u786e\u662f\u5e26\u7740\u4e09\u5206\u7334\u513f\u76f8\uff0c\u4e0d\u7531\u5f97\u6697\u6697\u597d\u7b11\u3002 \u3000\u3000\u8fd9\u65f6\u5385\u4e2d\u5bbe\u5ba2\u5df2\u5230\u4e86\u4e00\u5927\u534a\uff0c\u95e8\u5916\u5c1a\u9646\u7eed\u8fdb\u6765\u3002\u5385\u4e2d\u8fce\u5bbe\u7684\u77e5\u5ba2\u90fd\u662f\u798f\u5eb7\u5b89\u624b\u4e0b\u6b66\u5b98\uff0c\u6709\u7684\u7adf\u662f\u4e09\u56db\u54c1\u7684\u5927\u5458\uff0c\u82e5\u662f\u51fa\u4e86\u798f\u5e9c\uff0c\u54ea\u4e00\u4e2a\u4e0d\u662f\u58f0\u5a01\u714a\u8d6b\u7684\u9ad8\u5b98\u5927\u5c06\uff0c\u4f46\u5728\u5927\u5e05\u5e9c\u4e2d\uff0c\u5374\u4e0d\u8fc7\u662f\u8bf7\u5ba2\u968f\u5458\u4e00\u822c\uff0c\u6bd4\u4e4b\u7ae5\u4ec6\u53ae\u517b\u4e5f\u9ad8\u4e0d\u4e86\u591a\u5c11\u3002 \u3000\u3000\u80e1\u6590\u4e00\u77a5\u4e4b\u95f4\uff0c\u53ea\u89c1\u5468\u94c1\u9e6a\u548c\u6c6a\u94c1\u9e57\u5e76\u80a9\u8d70\u6765\u3002\u4e24\u4eba\u559c\u6c14\u6d0b\u6d0b\uff0c\u670d\u8272\u9876\u6234\u90fd\u5df2\u6362\u8fc7\uff0c\u663e\u5df2\u5347\u4e86\u5b98\u3002\u5468\u6c6a\u4e8c\u4eba\u8d70\u8fc7\u80e1\u6590\u548c\u7a0b\u7075\u7d20\u8eab\u524d\uff0c\u81ea\u6ca1\u8ba4\u51fa\u4ed6\u4eec\u3002 \u3000\u3000\u53ea\u542c\u53e6\u5916\u4e24\u4e2a\u6b66\u5b98\u5411\u5468\u6c6a\u4e8c\u4eba\u7b11\u563b\u563b\u7684\u9053\uff1a\u201c\u606d\u559c\u5468\u5927\u54e5\u3001\u6c6a\u5927\u54e5\uff0c\u90a3\u665a\u8fd9\u573a\u529f\u52b3\u5b9e\u5728\u4e0d\u5c0f\u3002\u201d\u6c6a\u94c1\u9e57\u9ad8\u5174\u5f97\u54a7\u5f00\u4e86\u5927\u5634\uff0c\u7b11\u9053\uff1a\u201c\u90a3\u4e5f\u53ea\u662f\u78b0\u5de7\u7f62\u5566\uff0c\u7b97\u5f97\u4ec0\u4e48\u672c\u9886\uff1f\u201d\u53c8\u6709\u4e00\u4e2a\u6b66\u5b98\u8d70\u4e86\u8fc7\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u4e00\u4f4d\u662f\u8bb0\u540d\u603b\u5175\uff0c\u4e00\u4f4d\u662f\u5b9e\u6388\u526f\u5c06\uff0c\u563f\u563f\uff0c\u4e86\u4e0d\u8d77\uff0c\u4e86\u4e0d\u8d77\u3002\u798f\u5927\u5e05\u624b\u4e0b\u7684\u7ea2\u4eba\uff0c\u8981\u7b97\u4f60\u4e24\u4f4d\u5347\u5b98\u6700\u5feb\u4e86\u3002\u201d\u5468\u94c1\u9e6a\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5e73\u5927\u54e5\u53d6\u7b11\u4e86\u3002\u54b1\u5144\u5f1f\u4fe9\u65e0\u529f\u53d7\u7984\uff0c\u600e\u6bd4\u5f97\u4e0a\u5e73\u5927\u54e5\u5728\u6218\u573a\u4e0a\u6323\u6765\u7684\u529f\u540d\uff1f\u201d\u90a3\u6b66\u5b98\u6b63\u8272\u9053\uff1a\u201c\u5468\u5927\u54e5\u52c7\u6551\u76f8\u56fd\u592b\u4eba\uff0c\u6c6a\u5927\u54e5\u529b\u62a4\u516c\u4e3b\u3002\u4e07\u5c81\u7237\u4eb2\u53e3\u5fa1\u5c01\uff0c\u5c0f\u5f1f\u5982\u4f55\u6bd4\u5f97\uff1f\u201d \u3000\u3000\u4f46\u89c1\u5468\u6c6a\u4e8c\u4eba\u6240\u5230\u4e4b\u5904\uff0c\u4f17\u6b66\u5b98\u90fd\u8981\u606d\u8d3a\u5949\u627f\u51e0\u53e5\u3002\u5404\u5bb6\u638c\u95e8\u4eba\u542c\u5230\u4e86\uff0c\u6709\u7684\u597d\u5947\u5fc3\u8d77\uff0c\u95ee\u8d77\u4e8c\u4eba\u5982\u4f55\u7acb\u529f\u62a4\u4e3b\u3002\u4f17\u6b66\u5b98\u4fbf\u52a0\u6cb9\u6dfb\u9171\u3001\u6709\u58f0\u6709\u8272\u7684\u8bf4\u4e86\u8d77\u6765\u3002\u80e1\u6590\u9694\u5f97\u8fdc\u4e86\uff0c\u53ea\u9690\u7ea6\u542c\u5230\u4e2a\u5927\u6982\uff1a\u539f\u6765\u90a3\u4e00\u665a\u80e1\u6590\u591c\u95ef\u798f\u5e9c\uff0c\u52c7\u52ab\u53cc\u7ae5\u3002\u5468\u94c1\u9e6a\u8001\u8c0b\u6df1\u7b97\uff0c\u4e0d\u4f46\u5c06\u4e00\u573a\u7978\u4e8b\u6d88\u5f2d\u4e8e\u65e0\u5f62\uff0c\u53cd\u800c\u56e0\u4e3a\u5148\u5f97\u8baf\u606f\uff0c\u88c5\u8154\u4f5c\u52bf\uff0c\u4ece\u80e1\u6590\u624b\u4e2d\u593a\u56de\u76f8\u56fd\u592b\u4eba\uff0c\u53c8\u53eb\u6c6a\u94c1\u9e57\u62a2\u5148\u53bb\u4fdd\u62a4\u516c\u4e3b\u3002\u90a3\u76f8\u56fd\u592b\u4eba\u662f\u4e7e\u9686\u7687\u5e1d\u7684\u60c5\u4eba\uff0c\u516c\u4e3b\u662f\u7687\u5e1d\u7684\u7231\u5973\uff0c\u8fd9\u4e00\u573a\u529f\u52b3\u7acb\u5f97\u8f7b\u6613\u4e4b\u6781\u3002 \u3000\u3000\u4f46\u5728\u7687\u5e1d\u773c\u4e2d\uff0c\u5374\u6bd4\u6218\u9635\u4e2d\u7684\u51b2\u950b\u9677\u9635\u80dc\u8fc7\u767e\u500d\uff0c\u56e0\u6b64\u91d1\u6bbf\u53ec\u89c1\uff0c\u6e29\u52c9\u6709\u52a0\uff0c\u5c06\u4ed6\u4e8c\u4eba\u8fde\u5347\u6570\u7ea7\u3002\u76f8\u56fd\u592b\u4eba\u3001\u548c\u5609\u516c\u4e3b\u3001\u798f\u5eb7\u5b89\u53c8\u8d4f\u4e86\u4e0d\u5c11\u73e0\u5b9d\u91d1\u94f6\u3002\u4e00\u665a\u4e4b\u95f4\uff0c\u5468\u6c6a\u4e8c\u4eba\u5927\u7ea2\u800c\u7279\u7ea2\u3002\u4eba\u4eba\u90fd\u8bf4\u6570\u767e\u540d\u523a\u5ba2\u591c\u88ad\u798f\u5927\u5e05\u5e9c\uff0c\u82e5\u4e0d\u662f\u5468\u6c6a\u4e8c\u4eba\u529b\u6218\uff0c\u76f8\u56fd\u592b\u4eba\u548c\u516c\u4e3b\u6027\u547d\u4e0d\u4fdd\u3002\u4f17\u536b\u58eb\u4e3a\u4e86\u63a9\u9970\u81ea\u5df1\u65e0\u80fd\uff0c\u5c06\u523a\u5ba2\u7684\u4eba\u6570\u8d8a\u8bf4\u8d8a\u591a\uff0c\u5230\u4f3c\u662f\u4f17\u536b\u58eb\u4ee5\u5be1\u654c\u4f17\uff0c\u820d\u547d\u62b5\u6321\uff0c\u624d\u4fdd\u5f97\u798f\u5eb7\u5b89\u65e0\u6059\u3002\u7ed3\u679c\u4eba\u4eba\u65e0\u8fc7\u6709\u529f\u3002\u798f\u5eb7\u5b89\u867d\u7136\u5931\u4e86\u4e24\u4e2a\u513f\u5b50\uff0c\u5927\u4e3a\u70e6\u607c\uff0c\u4f46\u60f3\u8d77\u5341\u5e74\u524d\u81ea\u5df1\u843d\u5165\u7ea2\u82b1\u4f1a\u624b\u4e2d\u7684\u5371\u96be\uff0c\u8fd9\u4e00\u665a\u6709\u60ca\u65e0\u9669\uff0c\u523a\u5ba2\u5168\u6570\u6740\u9000\uff0c\u53cd\u800c\u5927\u8d4f\u536b\u58eb\u3002\u5b98\u573a\u60ef\u4f8b\u539f\u662f\u5982\u6b64\uff0c\u7792\u4e0a\u4e0d\u7792\u4e0b\uff0c\u7686\u5927\u6b22\u559c\u3002 \u3000\u3000\u80e1\u6590\u548c\u7a0b\u7075\u7d20\u5bf9\u671b\u51e0\u773c\uff0c\u90fd\u4e0d\u7981\u6697\u6697\u597d\u7b11\u3002\u4ed6\u4e8c\u4eba\u90fd\u7b97\u9976\u6709\u667a\u8ba1\uff0c\u4f46\u51b3\u8ba1\u60f3\u4e0d\u5230\u5468\u94c1\u9e6a\u7adf\u4f1a\u51fa\u6b64\u4e00\u7740\uff0c\u5e73\u767d\u65e0\u7aef\u5f97\u4e86\u4e00\u573a\u5bcc\u8d35\u3002\u80e1\u6590\u5fc3\u60f3\uff1a\u201c\u6b64\u4eba\u8ba1\u8c0b\u6df1\u8fdc\uff0c\u624b\u6bb5\u6bd2\u8fa3\uff0c\u5c06\u6765\u98de\u9ec4\u817e\u8fbe\uff0c\u5728\u5b98\u573a\u4e2d\u524d\u7a0b\u65e0\u9650\u3002\u201d \u3000\u3000\u7eb7\u6270\u95f4\uff0c\u6570\u5341\u5e2d\u5df2\u6e10\u6e10\u5750\u6ee1\u3002\u80e1\u6590\u6697\u4e2d\u4e00\u70b9\u6570\uff0c\u4e00\u5171\u662f\u516d\u5341\u4e8c\u684c\uff0c\u6bcf\u684c\u516b\u4eba\uff0c\u5206\u4e3a\u4e24\u6d3e\uff0c\u5219\u6765\u4e0e\u4f1a\u7684\u5171\u662f\u4e00\u767e\u4e8c\u5341\u56db\u5bb6\u638c\u95e8\u4eba\uff0c\u5bfb\u601d\uff1a\u201c\u5929\u4e0b\u6b66\u529f\u95e8\u6d3e\uff0c\u7adf\u662f\u5982\u6b64\u7e41\u591a\uff0c\u800c\u62d2\u9080\u4e0d\u6765\u4e0e\u4f1a\u7684\uff0c\u6050\u6015\u4e5f\u662f\u4e0d\u5c11\u3002\u201d\u53c8\u89c1\u6709\u6570\u5e2d\u53ea\u5750\u7740\u56db\u4eba\uff0c\u53c8\u6709\u6570\u5e2d\u4e00\u4eba\u4e5f\u65e0\uff0c\u4e0d\u81ea\u7981\u7684\u60f3\u5230\u4e86\u8881\u7d2b\u8863\uff1a\u201c\u4e0d\u77e5\u5979\u4eca\u65e5\u6765\u662f\u4e0d\u6765\uff1f\u201d \u3000\u3000\u7a0b\u7075\u7d20\u89c1\u4ed6\u82e5\u6709\u6240\u601d\uff0c\u76ee\u5149\u4e2d\u9732\u51fa\u6e29\u67d4\u7684\u795e\u8272\uff0c\u65e9\u731c\u5230\u4ed6\u662f\u5728\u60f3\u8d77\u4e86\u8881\u7d2b\u8863\uff0c\u5fc3\u4e2d\u5fae\u5fae\u4e00\u9178\uff0c\u5ffd\u89c1\u4ed6\u988a\u8fb9\u808c\u8089\u4e00\u52a8\uff0c\u8138\u8272\u5927\u53d8\uff0c\u53cc\u773c\u4e2d\u5145\u6ee1\u4e86\u6012\u706b\uff0c\u987a\u7740\u4ed6\u76ee\u5149\u77a7\u53bb\u65f6\uff0c\u53ea\u89c1\u897f\u9996\u7b2c\u56db\u5e2d\u4e0a\u5750\u7740\u4e00\u4e2a\u8eab\u6750\u9b41\u68a7\u7684\u8001\u8005\uff0c\u624b\u4e2d\u63e1\u7740\u4e24\u679a\u94c1\u80c6\uff0c\u6676\u5149\u95ea\u4eae\uff0c\u6ef4\u6e9c\u6e9c\u5730\u8f6c\u52a8\uff0c\u6b63\u662f\u4e94\u864e\u95e8\u7684\u638c\u95e8\u4eba\u51e4\u5929\u5357\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u5fd9\u4f38\u624b\u62c9\u4e86\u62c9\u4ed6\u8863\u62bd\u3002\u80e1\u6590\u767b\u65f6\u7701\u609f\uff0c\u56de\u8fc7\u5934\u6765\uff0c\u5fc3\u9053\uff1a\u201c\u4f60\u65e2\u6765\u6b64\u5904\uff0c\u7ec8\u987b\u9003\u4e0d\u51fa\u6211\u624b\u5fc3\u3002\u563f\uff0c\u51e4\u5929\u5357\u4f60\u8fd9\u6076\u8d3c\uff0c\u4f60\u9053\u6211\u5927\u95f9\u5927\u5e05\u5e9c\u540e\uff0c\u51b3\u8ba1\u4e0d\u6562\u5230\u8fd9\u638c\u95e8\u4eba\u5927\u4f1a\u4e2d\u6765\uff0c\u5c82\u77e5\u6211\u504f\u504f\u6765\u4e86\u3002\u201d \u3000\u3000\u5348\u65f6\u5df2\u5c4a\uff0c\u5404\u5e2d\u4e0a\u5747\u5df2\u5750\u9f50\u3002\u80e1\u6590\u6e38\u76ee\u56db\u987e\uff0c\u4f46\u89c1\u5927\u5385\u6b63\u4e2d\u60ac\u7740\u4e00\u4e2a\u9526\u969c\uff0c\u9489\u7740\u516b\u4e2a\u5927\u91d1\u5b57\uff1a\u201c\u4ee5\u6b66\u4f1a\u53cb\uff0c\u7fa4\u82f1\u6bd5\u81f3\u3002\u201d \u3000\u3000\u9526\u969c\u4e0b\u5e76\u5217\u56db\u5e2d\uff0c\u6bcf\u5e2d\u90fd\u662f\u53ea\u8bbe\u4e00\u5f20\u684c\u6905\uff0c\u4e0a\u94fa\u864e\u76ae\uff0c\u5374\u5c1a\u65e0\u4eba\u5165\u5ea7\uff0c\u60f3\u6765\u662f\u4e3a\u738b\u516c\u8d35\u4eba\u6240\u8bbe\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u5979\u8fd8\u6ca1\u6765\u3002\u201d\u80e1\u6590\u660e\u77e5\u5979\u8bf4\u7684\u662f\u8881\u7d2b\u8863\uff0c\u5374\u987a\u53e3\u9053\uff1a\u201c\u8c01\u6ca1\u6765\uff1f\u201d\u7a0b\u7075\u7d20\u4e0d\u7b54\uff0c\u53ea\u662f\u81ea\u8a00\u81ea\u8bed\uff1a\u201c\u5979\u65e2\u5f53\u4e86\u4e5d\u5bb6\u534a\u603b\u638c\u95e8\uff0c\u603b\u4e0d\u80fd\u4e0d\u6765\u3002\u201d \u3000\u3000\u53c8\u8fc7\u7247\u65f6\uff0c\u53ea\u89c1\u4e00\u4f4d\u4e8c\u54c1\u9876\u6234\u7684\u5c06\u519b\u7ad9\u8d77\u8eab\u6765\uff0c\u58f0\u82e5\u6d2a\u949f\u7684\u8bf4\u9053\uff1a\u201c\u8bf7\u56db\u5927\u638c\u95e8\u4eba\u5165\u5e2d\u3002\u201d\u4f17\u536b\u58eb\u4e00\u8def\u4f20\u547c\u51fa\u53bb\uff1a\u201c\u8bf7\u56db\u5927\u638c\u95e8\u4eba\u5165\u5e2d\uff01\u201d\u201c\u8bf7\u56db\u5927\u638c\u95e8\u4eba\u5165\u5e2d\uff01\u201d\u201c\u8bf7\u56db\u5927\u638c\u95e8\u4eba\u5165\u5e2d\uff01\u201d \u3000\u3000\u5385\u4e2d\u7fa4\u8c6a\u5fc3\u4e2d\u5747\u5404\u4e0d\u89e3\uff1a\u201c\u8fd9\u91cc\u4e0e\u4f1a\u7684\uff0c\u9664\u4e86\u968f\u4f34\u5f1f\u5b50\uff0c\u4e3b\u65b9\u8fce\u5bbe\u77e5\u5ba2\u7684\u4eba\u5458\u4e4b\u5916\uff0c\u4e2a\u4e2a\u90fd\u662f\u638c\u95e8\u4eba\uff0c\u600e\u5730\u8fd8\u5206\u4ec0\u4e48\u56db\u5927\u56db\u5c0f\uff1f\u201d \u3000\u3000\u8fd9\u65f6\u5927\u5385\u4e2d\u4e00\u7247\u8083\u9759\uff0c\u53ea\u89c1\u4e24\u540d\u4e09\u54c1\u6b66\u5b98\u5f15\u7740\u56db\u4e2a\u4eba\u8d70\u8fdb\u5385\u6765\uff0c\u4e00\u76f4\u8d70\u5230\u9526\u969c\u4e0b\u7684\u864e\u76ae\u6905\u65c1\uff0c\u5206\u8bf7\u56db\u4eba\u5165\u5ea7\u3002 \u3000\u3000\u770b\u8fd9\u56db\u4eba\u65f6\uff0c\u89c1\u5f53\u5148\u4e00\u4eba\u662f\u4e2a\u767d\u7709\u8001\u50e7\uff0c\u624b\u4e2d\u6491\u7740\u4e00\u6839\u9ec4\u6768\u6728\u7684\u7985\u6756\uff0c\u9762\u76ee\u6148\u7965\uff0c\u770b\u6765\u6ca1\u4e00\u767e\u5c81\uff0c\u4e5f\u6709\u4e5d\u5341\u5c81\u3002\u7b2c\u4e8c\u4eba\u662f\u4e2a\u4e03\u5341\u6765\u5c81\u7684\u9053\u4eba\uff0c\u8138\u4e0a\u9ed1\u9edd\u9edd\u5730\uff0c\u53cc\u76ee\u4f3c\u5f00\u4f3c\u95ed\uff0c\u5f62\u5bb9\u9887\u4e3a\u59d4\u7410\u3002\u8fd9\u4e00\u50e7\u4e00\u9053\uff0c\u8c8c\u76f8\u5224\u82e5\u4e91\u6ce5\uff0c\u8001\u548c\u5c1a\u9ad8\u5927\u5a01\u4e25\uff0c\u4e00\u671b\u800c\u77e5\u662f\u4e2a\u6709\u9053\u9ad8\u50e7\u3002\u90a3\u9053\u4eba\u5374\u4f3c\u4e2a\u5bfb\u5e38\u65bd\u6cd5\u5316\u7f18\u3001\u753b\u7b26\u9a97\u4eba\u7684\u8305\u5c71\u9053\u58eb\uff0c\u4e0d\u77e5\u4f55\u4ee5\u7adf\u4e5f\u7b97\u662f\u201c\u56db\u5927\u638c\u95e8\u4eba\u201d\u4e4b\u4e00\uff1f \u3000\u3000\u7b2c\u4e09\u4eba\u662f\u4e2a\u7cbe\u795e\u77cd\u94c4\u7684\u8001\u8005\uff0c\u516d\u5341\u4f59\u5c81\u5e74\u7eaa\uff0c\u53cc\u76ee\u70af\u70af\u95ea\u5149\uff0c\u4e24\u8fb9\u592a\u9633\u7a74\u9ad8\u9ad8\u9f13\u8d77\uff0c\u663e\u662f\u5185\u529f\u6df1\u539a\u3002\u4ed6\u4e00\u8fdb\u5385\u6765\uff0c\u4fbf\u542b\u7b11\u62b1\u62f3\uff0c\u548c\u8fd9\u4e00\u4e2a\u90a3\u4e00\u4e2a\u70b9\u5934\u62db\u547c\uff0c\u4e00\u767e\u591a\u4e2a\u638c\u95e8\u4eba\u4e2d\uff0c\u770b\u6765\u5012\u6709\u516b\u4e5d\u5341\u4eba\u8ddf\u4ed6\u76f8\u8bc6\uff0c\u5f53\u771f\u662f\u4ea4\u6e38\u904d\u5929\u4e0b\u3002\u5404\u4eba\u4e0d\u662f\u53eb\u201c\u6c64\u5927\u7237\u201d\uff0c\u4fbf\u662f\u79f0\u201c\u6c64\u5927\u4fa0\u201d\uff0c\u53ea\u6709\u51e0\u4f4d\u5e74\u5c81\u751a\u9ad8\u7684\u6b66\u6797\u540d\u5bbf\uff0c\u624d\u53eb\u4ed6\u4e00\u58f0\u201c\u7518\u9716\u5144\uff01\u201d \u3000\u3000\u80e1\u6590\u5fc3\u60f3\uff1a\u201c\u8fd9\u4e00\u4f4d\u4fbf\u662f\u53f7\u79f0\u2018\u7518\u9716\u60e0\u4e03\u7701\u2019\u7684\u6c64\u6c9b\u6c64\u5927\u4fa0\u4e86\u3002\u8881\u59d1\u5a18\u7684\u5988\u5988\u4fbf\u66fe\u8499\u4ed6\u6536\u5bb9\u8fc7\u3002\u6b64\u4eba\u4fa0\u540d\u56db\u64ad\uff0c\u6b66\u6797\u4e2d\u90fd\u8bf4\u4ed6\u4ec1\u4e49\u8fc7\u4eba\uff0c\u60f3\u4e0d\u5230\u4eca\u65e5\u4e5f\u53d7\u4e86\u798f\u5eb7\u5b89\u7684\u7b3c\u7edc\u3002\u201d \u3000\u3000\u4f46\u89c1\u4ed6\u4e0d\u5373\u5c31\u5750\uff0c\u8d70\u5230\u6bcf\u4e00\u5e2d\u4e0a\uff0c\u4e0e\u76f8\u8bc6\u4e4b\u4eba\u5bd2\u6684\u51e0\u53e5\uff0c\u62c9\u624b\u62cd\u80a9\uff0c\u900f\u7740\u6781\u662f\u4eb2\u70ed\u3002\u5f85\u8d70\u5230\u80e1\u6590\u8fd9\u4e00\u684c\u65f6\uff0c\u4e00\u628a\u62c9\u4f4f\u7334\u62f3\u5927\u5723\u95e8\u7684\u638c\u95e8\u4eba\uff0c\u7b11\u9053\uff1a\u201c\u8001\u7334\u513f\uff0c\u4f60\u4e5f\u6765\u5566\uff1f\u563f\u563f\uff0c\u600e\u4e48\u5e2d\u4e0a\u4e0d\u7ed9\u9884\u5907\u4e00\u76c6\u87e0\u6843\u513f\uff1f\u201d \u3000\u3000\u90a3\u638c\u95e8\u4eba\u5374\u5bf9\u4ed6\u751a\u662f\u606d\u656c\uff0c\u7b11\u9053\uff1a\u201c\u6c64\u5927\u4fa0\uff0c\u6709\u4e03\u516b\u5e74\u6ca1\u89c1\u60a8\u8001\u4eba\u5bb6\u5566\u3002\u4e00\u76f4\u6ca1\u6765\u8ddf\u60a8\u8001\u4eba\u5bb6\u8bf7\u5b89\u95ee\u597d\uff0c\u5b9e\u5728\u8be5\u6253\u3002\u60a8\u8d8a\u8001\u8d8a\u5065\u65fa\uff0c\u771f\u662f\u96be\u5f97\u3002\u201d\u6c64\u6c9b\u4f38\u624b\u5728\u4ed6\u80a9\u5934\u4e00\u62cd\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u82b1\u679c\u5c71\u6c34\u5e18\u6d1e\u7684\u7334\u5b50\u7334\u5b59\u3001\u7334\u5a46\u7334\u5973\uff0c\u5927\u5c0f\u90fd\u5e73\u5b89\u5427\uff1f\u201d\u90a3\u638c\u95e8\u4eba\u9053\uff1a\u201c\u6258\u6c64\u5927\u4fa0\u7684\u798f\uff0c\u5927\u4f19\u513f\u90fd\u5b89\u5065\u3002\u201d \u3000\u3000\u6c64\u6c9b\u54c8\u54c8\u4e00\u7b11\uff0c\u5411\u59ec\u6653\u5cf0\u9053\uff1a\u201c\u59ec\u8001\u4e09\u6ca1\u6765\u5417\uff1f\u201d\u59ec\u6653\u5cf0\u4fef\u8eab\u8bf7\u4e86\u4e2a\u5b89\uff0c\u8bf4\u9053\uff1a\u201c\u5bb6\u4e25\u6ca1\u6765\u3002\u5bb6\u4e25\u6bcf\u65e5\u91cc\u8bb0\u6302\u6c64\u5927\u4fa0\uff0c\u5e38\u8bf4\u670d\u4e86\u6c64\u5927\u4fa0\u8d4f\u8d50\u7684\u4eba\u53c2\u517b\u8363\u4e38\u540e\uff0c\u7cbe\u795e\u597d\u5f97\u591a\u4e86\u3002\u201d\u6c64\u6c9b\u9053\uff1a\u201c\u4f60\u662f\u4f4f\u5728\u4e91\u4f8d\u90ce\u5e9c\u4e0a\u5417\uff1f\u660e\u513f\u6211\u518d\u7ed9\u4f60\u9001\u4e9b\u6765\u3002\u201d\u59ec\u6653\u5cf0\u54c8\u8170\u76f8\u8c22\u3002\u6c64\u6c9b\u5411\u80e1\u6590\u3001\u7a0b\u7075\u7d20\u3001\u8521\u5a01\u4e09\u4eba\u70b9\u70b9\u5934\uff0c\u8d70\u5230\u522b\u684c\u53bb\u4e86\u3002 \u3000\u3000\u90a3\u7334\u62f3\u5927\u5723\u95e8\u7684\u638c\u95e8\u4eba\u9053\uff1a\u201c\u6c64\u5927\u4fa0\u7684\u5916\u53f7\u53eb\u505a\u2018\u7518\u9716\u60e0\u4e03\u7701\u2019\uff0c\u5176\u5b9e\u5462\uff0c\u5c82\u6b62\u662f\u4e03\u7701\u800c\u5df2\uff1f\u90a3\u4e00\u5e74\u4ffa\u4fdd\u7684\u4e00\u679d\u5341\u516b\u4e07\u4e24\u94f6\u5b50\u7684\u4e1d\u7ef8\u9556\u5728\u7518\u51c9\u9053\u4e0a\u5931\u843d\u4e86\uff0c\u4e00\u5bb6\u5b50\u6025\u5f97\u5168\u8981\u8df3\u4e95\uff0c\u82e5\u4e0d\u662f\u6c64\u5927\u4fa0\u633a\u8eab\u800c\u51fa\uff0c\u53c8\u8f6f\u53c8\u786c\uff0c\u65e2\u6328\u9762\u5b50\uff0c\u53c8\u52a8\u5200\u5b50\uff0c\u2018\u9152\u6cc9\u4e09\u864e\u2019\u600e\u80af\u4ea4\u8fd8\u8fd9\u4e00\u679d\u9556\u5462\uff1f\u201d\u8ddf\u7740\u4fbf\u53e3\u6cab\u6a2a\u98de\uff0c\u8bf4\u8d77\u4e86\u5f53\u5e74\u4e4b\u4e8b\u3002\u539f\u6765\u4ed6\u53d7\u4e86\u6c64\u6c9b\u7684\u5927\u6069\uff0c\u6ca1\u9f7f\u4e0d\u5fd8\uff0c\u4e00\u6709\u673a\u4f1a\uff0c\u4fbf\u8981\u5ba3\u626c\u4ed6\u7684\u597d\u5904\u3002 \u3000\u3000\u8fd9\u6c64\u6c9b\u4e00\u8d70\u8fdb\u5927\u5385\uff0c\u771f\u4fbf\u4f3c\u201c\u5927\u5c06\u519b\u516b\u9762\u5a01\u98ce\u201d\uff0c\u4eba\u4eba\u7684\u773c\u5149\u90fd\u671b\u7740\u4ed6\u3002\u90a3\u201c\u56db\u5927\u638c\u95e8\u4eba\u201d\u7684\u5176\u4f59\u4e09\u4eba\u767b\u65f6\u9eef\u7136\u65e0\u5149\u3002 \u3000\u3000\u7b2c\u56db\u4eba\u4f5c\u6b66\u5b98\u6253\u626e\uff0c\u7a7f\u7740\u56db\u54c1\u9876\u6234\uff0c\u5728\u8fd9\u5927\u5385\u4e4b\u4e2d\uff0c\u5b98\u7235\u9ad8\u4e8e\u4ed6\u7684\u6b66\u5b98\u6709\u7684\u662f\uff0c\u4f46\u4ed6\u6b65\u5c65\u6c89\u7a33\uff0c\u6c14\u5ea6\u5a01\u4e25\uff0c\u9690\u7136\u662f\u4e00\u6d3e\u5927\u5b97\u5e08\u7684\u8eab\u5206\u3002\u53ea\u89c1\u4ed6\u7ea6\u83ab\u4e94\u5341\u5c81\u5e74\u7eaa\uff0c\u65b9\u9762\u5927\u8033\uff0c\u53cc\u7709\u98de\u626c\u6709\u68f1\uff0c\u4e0d\u58f0\u4e0d\u54cd\u7684\u8d70\u5230\u7b2c\u56db\u5e2d\u4e0a\u4e00\u5750\uff0c\u5982\u6e0a\u4e4b\u6e1f\uff0c\u5982\u5cb3\u4e4b\u5cd9\uff0c\u51dd\u795e\u5b88\u4e2d\uff0c\u5bf9\u8eab\u5468\u7684\u6270\u6518\u5b9b\u4f3c\u4e0d\u95fb\u4e0d\u89c1\u3002\u80e1\u6590\u5fc3\u9053\uff1a\u201c\u8fd9\u4e5f\u662f\u4e00\u4f4d\u975e\u540c\u5c0f\u53ef\u7684\u4eba\u7269\u3002\u201d \u3000\u3000\u4ed6\u521d\u6765\u638c\u95e8\u4eba\u5927\u4f1a\u4e4b\u65f6\uff0c\u6ee1\u8154\u96c4\u5fc3\uff0c\u6ca1\u5c06\u8c01\u653e\u5728\u773c\u4e2d\uff0c\u5f85\u5f97\u4e00\u89c1\u8fd9\u56db\u5927\u638c\u95e8\u4eba\uff0c\u767b\u65f6\u5927\u589e\u6212\u60e7\uff0c\u5bfb\u601d\uff1a\u201c\u6c64\u5927\u4fa0\u548c\u90a3\u6b66\u5b98\u4efb\u8c01\u4e00\u4eba\uff0c\u6211\u90fd\u672a\u5fc5\u62b5\u654c\u5f97\u8fc7\u3002\u90a3\u548c\u5c1a\u548c\u9053\u4eba\u6392\u540d\u5c1a\u5728\u4ed6\u4e8c\u4eba\u4e4b\u4e0a\uff0c\u81ea\u7136\u4e5f\u975e\u5eb8\u624b\u3002\u4eca\u65e5\u6211\u7684\u8eab\u5206\u4e07\u4e07\u6cc4\u6f0f\u4e0d\u5f97\uff0c\u522b\u8bf4\u4e00\u767e\u591a\u4e2a\u638c\u95e8\u4eba\u4e2a\u4e2a\u90fd\u662f\u9876\u513f\u5c16\u513f\u7684\u9ad8\u624b\uff0c\u53ea\u6d88\u8fd9\u2018\u50e7\u3001\u9053\u3001\u4fa0\u3001\u5b98\u201d\u56db\u4eba\u9f50\u4e0a\uff0c\u5236\u670d\u6211\u4fbf\u7ef0\u7ef0\u6709\u4f59\u3002\u201d\u4ed6\u60e7\u610f\u4e00\u751f\uff0c\u5f53\u4e0b\u53ea\u662f\u6293\u7740\u74dc\u5b50\u6162\u6162\u55d1\u7740\uff0c\u4e0d\u6562\u518d\u4e1c\u5f20\u897f\u671b\uff0c\u751f\u6015\u7ed9\u798f\u5eb7\u5b89\u624b\u4e0b\u7684\u536b\u58eb\u4eec\u5bdf\u89c9\u4e86\u3002 \u3000\u3000\u8fc7\u4e86\u597d\u4e00\u4f1a\uff0c\u6c64\u6c9b\u624d\u548c\u4f17\u4eba\u62db\u547c\u5b8c\u6bd5\uff0c\u56de\u5230\u81ea\u5df1\u5ea7\u4e0a\u3002\u5374\u53c8\u6709\u8bb8\u591a\u540e\u751f\u665a\u8f88\uff0c\u4e00\u4e2a\u4e2a\u8d76\u7740\u8fc7\u53bb\u8ddf\u4ed6\u78d5\u5934\u8bf7\u5b89\u3002\u6c64\u6c9b\u5bb6\u8d44\u8c6a\u5bcc\uff0c\u4ed7\u4e49\u758f\u8d22\uff0c\u968f\u5728\u4ed6\u8eab\u540e\u7684\u95e8\u4eba\u5f1f\u5b50\u5e26\u7740\u5927\u6279\u7ea2\u5c01\u5305\uff0c\u51e1\u662f\u4ece\u672a\u89c1\u8fc7\u9762\u7684\u665a\u8f88\u5411\u4ed6\u78d5\u4e00\u4e2a\u5934\uff0c\u4fbf\u7ed9\u56db\u4e24\u94f6\u5b50\u4f5c\u89c1\u9762\u793c\u3002\u53c8\u4e71\u4e86\u4e00\u9635\uff0c\u65b9\u624d\u89c1\u793c\u5df2\u7f62\u3002 \u3000\u3000\u53ea\u542c\u5f97\u4e00\u4f4d\u4e8c\u54c1\u6b66\u5b98\u559d\u9053\uff1a\u201c\u659f\u9152\uff01\u201d\u5728\u5404\u5e2d\u4f3a\u5019\u7684\u4ec6\u5f79\u63d0\u58f6\u7ed9\u5404\u4eba\u659f\u6ee1\u4e86\u9152\u3002\u90a3\u6b66\u5b98\u4e3e\u8d77\u676f\u6765\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u5404\u6d3e\u638c\u95e8\u7684\u524d\u8f88\u6b66\u5e08\uff0c\u8fdc\u9053\u6765\u5230\u4eac\u57ce\uff0c\u798f\u5927\u5e05\u6781\u662f\u6b22\u8fce\u3002\u73b0\u4e0b\u5144\u5f1f\u5148\u656c\u5404\u4f4d\u4e00\u676f\uff0c\u5f85\u4f1a\u798f\u5927\u5e05\u4eb2\u81ea\u6765\u5411\u5404\u4f4d\u656c\u9152\u3002\u201d\u8bf4\u7740\u4e3e\u676f\u4e00\u996e\u800c\u5c3d\u3002\u4f17\u4eba\u4e5f\u5747\u5e72\u676f\u3002 \u3000\u3000\u90a3\u6b66\u5b98\u53c8\u9053\uff1a\u201c\u4eca\u65e5\u5230\u6765\u7684\uff0c\u5168\u662f\u6b66\u6797\u4e2d\u7684\u82f1\u96c4\u8c6a\u6770\u3002\u81ea\u53e4\u4ee5\u6765\uff0c\u4ece\u672a\u6709\u8fc7\u5982\u6b64\u76db\u4e8b\u3002\u798f\u5927\u5e05\u6700\u9ad8\u5174\u7684\uff0c\u662f\u5c45\u7136\u8bf7\u5230\u4e86\u56db\u5927\u638c\u95e8\u4eba\u4e00\u9f50\u5149\u4e34\uff0c\u73b0\u4e0b\u7ed9\u5404\u4f4d\u5f15\u89c1\u3002\u201d\u4ed6\u6307\u7740\u7b2c\u4e00\u5e2d\u7684\u767d\u7709\u8001\u50e7\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u6cb3\u5357\u5d69\u5c71\u5c11\u6797\u5bfa\u65b9\u4e08\u5927\u667a\u7985\u5e08\u3002\u5343\u4f59\u5e74\u6765\uff0c\u5c11\u6797\u6d3e\u4e00\u76f4\u662f\u5929\u4e0b\u6b66\u5b66\u4e4b\u6e90\u3002\u4eca\u65e5\u7684\u5929\u4e0b\u638c\u95e8\u4eba\u5927\u4f1a\uff0c\u81ea\u5f53\u63a8\u5927\u667a\u7985\u5e08\u5750\u4e2a\u9996\u5e2d\u3002\u201d\u7fa4\u8c6a\u4e00\u9f50\u9f13\u638c\u3002\u5c11\u6797\u6d3e\u5206\u652f\u5e9e\u5927\uff0c\u6b64\u65e5\u4e0e\u4f1a\u7684\u5404\u95e8\u6d3e\u4e2d\uff0c\u51e0\u6709\u4e09\u5206\u4e4b\u4e00\u662f\u6e90\u51fa\u5c11\u6797\uff0c\u4f17\u4eba\u89c1\u90a3\u6b66\u5b98\u5c0a\u5d07\u5c11\u6797\u5bfa\u7684\u9ad8\u50e7\uff0c\u5c3d\u7686\u559c\u6b22\u3002 \u3000\u3000\u90a3\u6b66\u5b98\u6307\u7740\u7b2c\u4e8c\u5e2d\u7684\u9053\u4eba\u8bf4\u9053\uff1a\u201c\u9664\u4e86\u5c11\u6797\u6d3e\uff0c\u81ea\u8be5\u63a8\u6b66\u5f53\u4e3a\u5c0a\u4e86\u3002\u8fd9\u4e00\u4f4d\u662f\u6b66\u5f53\u5c71\u592a\u548c\u5bab\u89c2\u4e3b\u65e0\u9752\u5b50\u9053\u957f\u3002\u201d\u6b66\u5f53\u6d3e\u5a01\u540d\u751a\u76db\uff0c\u4e3a\u5185\u5bb6\u62f3\u5251\u4e4b\u7956\u3002\u7fa4\u8c6a\u89c1\u8fd9\u9053\u4eba\u59d4\u9761\u4e0d\u632f\uff0c\u5f62\u8c8c\u5eb8\u4fd7\uff0c\u90fd\u662f\u6697\u6697\u5947\u602a\u3002\u6709\u4e9b\u89c1\u95fb\u5e7f\u535a\u7684\u540d\u5bbf\u66f4\u60f3\uff1a\u201c\u81ea\u4ece\u5341\u5e74\u524d\u6b66\u5f53\u6d3e\u638c\u95e8\u4eba\u9a6c\u94b0\u901d\u4e16\uff0c\u6b66\u5f53\u9ad8\u624b\u706b\u624b\u5224\u5b98\u5f20\u53ec\u91cd\u53c8\u6b7b\u5728\u56de\u7586\uff0c\u6ca1\u542c\u8bf4\u6b66\u5f53\u6d3e\u7acb\u4e86\u8c01\u505a\u638c\u95e8\u4eba\u554a\u3002\u8fd9\u592a\u548c\u5bab\u89c2\u4e3b\u65e0\u9752\u5b50\u7684\u540d\u5934\uff0c\u53ef\u6ca1\u542c\u89c1\u8fc7\u3002\u201d \u3000\u3000\u7b2c\u4e09\u4f4d\u6c64\u6c9b\u6c64\u5927\u4fa0\u7684\u540d\u5934\u4eba\u4eba\u7686\u77e5\uff0c\u7528\u4e0d\u7740\u4ed6\u6765\u4ecb\u7ecd\uff0c\u4f46\u90a3\u6b66\u5b98\u8fd8\u662f\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u7518\u9716\u60e0\u4e03\u7701\u6c64\u5927\u4fa0\uff0c\u662f\u2018\u4e09\u624d\u5251\u2019\u7684\u638c\u95e8\u4eba\u3002\u6c64\u5927\u4fa0\u4fa0\u540d\u9707\u52a8\u5929\u4e0b\uff0c\u4ec1\u4e49\u76d6\u4e16\uff0c\u65e0\u4eba\u4e0d\u77e5\uff0c\u4e0d\u7528\u5c0f\u5f1f\u591a\u9976\u820c\u4e86\u3002\u201d\u4ed6\u8bf4\u4e86\u8fd9\u51e0\u53e5\u8bdd\uff0c\u4f17\u4eba\u9f50\u58f0\u8d77\u54c4\uff0c\u90fd\u7ed9\u6c64\u6c9b\u6367\u573a\u3002\u8fd9\u60c5\u666f\u6bd4\u4e4b\u5f15\u89c1\u65e0\u9752\u5b50\u65f6\u56fa\u662f\u5927\u5927\u4e0d\u540c\uff0c\u4fbf\u662f\u5c11\u6797\u5bfa\u65b9\u4e08\u5927\u667a\u7985\u5e08\uff0c\u4e5f\u662f\u6709\u6240\u4e0d\u53ca\u3002 \u3000\u3000\u80e1\u6590\u542c\u5f97\u90bb\u684c\u4e0a\u7684\u4e00\u4e2a\u8001\u8005\u8bf4\u9053\uff1a\u201c\u6b66\u6797\u4e4b\u4e2d\uff0c\u6709\u7684\u662f\u95e8\u6d3e\u62ac\u9ad8\u4e86\u4eba\uff0c\u6709\u7684\u662f\u4eba\u62ac\u9ad8\u4e86\u95e8\u6d3e\u3002\u90a3\u4f4d\u9752\u4ec0\u4e48\u9053\u957f\uff0c\u53ea\u56e0\u662f\u6b66\u5f53\u5c71\u592a\u548c\u5bab\u7684\u89c2\u4e3b\uff0c\u4fbf\u7b97\u662f\u5929\u4e0b\u56db\u5927\u638c\u95e8\u4eba\u4e4b\u4e00\uff0c\u6211\u770b\u672a\u5fc5\u4fbf\u6709\u4ec0\u4e48\u771f\u624d\u5b9e\u5b66\u5427\uff1f\u81f3\u4e8e\u2018\u4e09\u624d\u5251\u2019\u4e00\u95e8\u5462\uff0c\u82e5\u4e0d\u662f\u51fa\u4e86\u6c64\u5927\u4fa0\u8fd9\u6837\u4e00\u4f4d\u767e\u4e16\u96be\u9022\u7684\u4eba\u7269\uff0c\u5728\u6b66\u6797\u4e2d\u53c8\u80fd\u5360\u5230\u4ec0\u4e48\u5e2d\u4f4d\u5462\uff1f\u201d\u4e00\u4e2a\u58ee\u6c49\u63a5\u53e3\u9053\uff1a\u201c\u5e08\u53d4\u8bf4\u5f97\u662f\u3002\u201d\u80e1\u6590\u542c\u4e86\u4e5f\u6697\u6697\u70b9\u5934\u3002 \u3000\u3000\u4f17\u4eba\u4e71\u4e86\u4e00\u9635\uff0c\u76ee\u5149\u90fd\u79fb\u5230\u4e86\u90a3\u7aef\u5750\u7b2c\u56db\u5e2d\u7684\u6b66\u5b98\u8eab\u4e0a\u3002 \u3000\u3000\u5531\u540d\u5f15\u89c1\u7684\u90a3\u6b66\u5b98\u8bf4\u9053\uff1a\u201c\u8fd9\u4e00\u4f4d\u662f\u6211\u4eec\u6ee1\u6d32\u7684\u82f1\u96c4\u3002\u8fd9\u4f4d\u6d77\u5170\u5f3c\u6d77\u5927\u4eba\uff0c\u662f\u9576\u9ec4\u65d7\u9a81\u9a91\u8425\u7684\u4f50\u9886\uff0c\u8fbd\u4e1c\u9ed1\u9f99\u95e8\u7684\u638c\u95e8\u4eba\u3002\u201d \u3000\u3000\u6d77\u5170\u5f3c\u7684\u5b98\u804c\u6bd4\u4ed6\u4f4e\uff0c\u5f53\u90a3\u4e8c\u54c1\u6b66\u5b98\u8bf4\u8fd9\u756a\u8bdd\u65f6\uff0c\u4ed6\u907f\u5e2d\u8083\u7acb\uff0c\u72b6\u751a\u606d\u8c28\u3002 \u3000\u3000\u80e1\u6590\u90bb\u684c\u90a3\u8001\u8005\u53c8\u548c\u540c\u684c\u7684\u4eba\u7a83\u7a83\u79c1\u8bae\u8d77\u6765\uff1a\u201c\u8fd9\u4e00\u4f4d\u54ea\uff0c\u5374\u662f\u5b98\u804c\u62ac\u9ad8\u95e8\u6d3e\u4e86\u3002\u8fbd\u4e1c\u9ed1\u9f99\u95e8\uff0c\u563f\u563f\uff0c\u5728\u6b66\u6797\u4e2d\u540d\u4e0d\u89c1\u7ecf\u4f20\uff0c\u7b97\u90a3\u4e00\u4f1a\u5b50\u7684\u56db\u5927\u638c\u95e8\uff1f\u53ea\u4e0d\u8fc7\u56db\u5927\u638c\u95e8\u4eba\u5018\u82e5\u4e2a\u4e2a\u90fd\u662f\u6c49\u4eba\uff0c\u6ca1\u5b89\u63d2\u4e00\u4e2a\u6ee1\u6d32\u4eba\uff0c\u798f\u5927\u5e05\u7684\u8138\u4e0a\u987b\u4e0d\u597d\u770b\u3002 \u3000\u3000\u8fd9\u4e00\u4f4d\u6d77\u5927\u4eba\u6700\u591a\u53ea\u662f\u6709\u51e0\u767e\u65a4\u86ee\u529b\uff0c\u600e\u80fd\u548c\u4e2d\u539f\u5404\u5927\u95e8\u6d3e\u7684\u540d\u5bb6\u9ad8\u624b\u8f83\u91cf\uff1f\u201d\u90a3\u58ee\u6c49\u53c8\u9053\uff1a\u201c\u5e08\u53d4\u8bf4\u5f97\u662f\u3002\u201d\u8fd9\u4e00\u6b21\u80e1\u6590\u5fc3\u4e2d\u5374\u9887\u4e0d\u4ee5\u4e3a\u7136\uff0c\u6697\u60f3\uff1a\u201c\u4f60\u83ab\u5c0f\u89d1\u4e86\u8fd9\u4e00\u4f4d\u6ee1\u6d32\u597d\u6c49\uff0c\u6b64\u4eba\u82f1\u534e\u5185\u655b\uff0c\u7a33\u51dd\u7aef\u91cd\uff0c\u6bd4\u4f60\u8fd9\u7cdf\u8001\u5934\u513f\u53ea\u6015\u5f3a\u5f97\u591a\u5462\u3002\u201d \u3000\u3000\u90a3\u56db\u5927\u638c\u95e8\u4eba\u9010\u4e00\u7ad9\u8d77\u6765\u5411\u7fa4\u8c6a\u656c\u9152\uff0c\u5404\u81ea\u8bf4\u4e86\u51e0\u53e5\u8c26\u900a\u7684\u8bdd\u3002\u5927\u667a\u7985\u5e08\u6c14\u5ea6\u96cd\u7136\uff0c\u786e\u6709\u9886\u8896\u7fa4\u4f26\u4e4b\u98ce\u3002\u6c64\u6c9b\u5999\u8bed\u5982\u73e0\uff0c\u53ea\u8bf4\u4e86\u4e03\u516b\u53e5\u8bdd\uff0c\u5374\u5f15\u8d77\u4e09\u6b21\u54c4\u5802\u5927\u7b11\u3002\u65e0\u9752\u5b50\u548c\u6d77\u5170\u5f3c\u90fd\u4e0d\u5584\u8f9e\u4ee4\u3002\u65e0\u9752\u5b50\u4e00\u53e3\u6e56\u5317\u4e61\u4e0b\u571f\u8bdd\uff0c\u5c16\u58f0\u5c16\u6c14\uff0c\u5012\u6709\u4e00\u5927\u534a\u4eba\u4e0d\u61c2\u4ed6\u8bf4\u4e9b\u4ec0\u4e48\u3002\u80e1\u6590\u6697\u81ea\u5947\u602a\uff1a\u201c\u8fd9\u4f4d\u9053\u957f\u8bf4\u8bdd\u4e2d\u6c14\u4e0d\u8db3\uff0c\u600e\u80fd\u4e3a\u6b66\u5f53\u6d3e\u8fd9\u7b49\u5927\u6d3e\u7684\u638c\u95e8\uff0c\u591a\u534a\u4ed6\u6b66\u827a\u867d\u4f4e\uff0c\u8f88\u4efd\u5374\u9ad8\uff0c\u53c8\u6709\u4eba\u671b\uff0c\u4e3a\u95e8\u4e0b\u4f17\u5f1f\u5b50\u6240\u63a8\u91cd\u3002\u201d \u3000\u3000\u5f53\u4e0b\u53a8\u5f79\u9001\u83dc\u4e0a\u6765\uff0c\u798f\u5927\u5e05\u5e9c\u5bb4\u5ba2\uff0c\u7aef\u7684\u662f\u975e\u6bd4\u5bfb\u5e38\uff0c\u5355\u662f\u90a3\u4e00\u575b\u575b\u4e8c\u5341\u5e74\u7684\u72b6\u5143\u7ea2\u9648\u7ecd\uff0c\u4fbf\u662f\u6781\u96be\u5c1d\u5230\u7684\u7f8e\u9152\u3002\u80e1\u6590\u9152\u5230\u676f\u5e72\uff0c\u4e00\u53e3\u6c14\u559d\u4e86\u4e8c\u5341\u4f59\u676f\u3002\u7a0b\u7075\u7d20\u89c1\u4ed6\u9152\u5174\u751a\u8c6a\uff0c\u53ea\u662f\u62bf\u5634\u5fae\u7b11\uff0c\u5076\u5c14\u56de\u5934\uff0c\u4fbf\u671b\u51e4\u5929\u5357\u4e00\u773c\uff0c\u751f\u6015\u4ed6\u8d70\u5f97\u6ca1\u4e86\u5f71\u8e2a\u3002 \u3000\u3000\u5403\u4e86\u4e03\u516b\u9053\u83dc\uff0c\u5ffd\u542c\u5f97\u4f17\u4f8d\u536b\u9ad8\u58f0\u4f20\u547c\uff1a\u201c\u798f\u5927\u5e05\u5230\uff01\u201d\u731b\u542c\u5f97\u547c\u547c\u6570\u58f0\uff0c\u5927\u5385\u4e0a\u4f17\u6b66\u5b98\u4e00\u9f50\u79bb\u5e2d\u8083\u7acb\uff0c\u970e\u65f6\u4e4b\u95f4\uff0c\u4eba\u4eba\u90fd\u4f3c\u53d8\u6210\u4e86\u4e00\u5c0a\u5c0a\u77f3\u50cf\uff0c\u4e00\u52a8\u4e5f\u4e0d\u52a8\u4e86\u3002\u5404\u95e8\u6d3e\u7684\u638c\u95e8\u4eba\u90fd\u662f\u6b66\u6797\u8c6a\u5ba2\uff0c\u6ca1\u89c1\u8fc7\u8fd9\u7b49\u519b\u7eaa\u8083\u7a46\u7684\u795e\u6001\uff0c\u90fd\u4e0d\u7531\u5f97\u5403\u4e86\u4e00\u60ca\uff0c\u4e09\u4e09\u4e24\u4e24\u7684\u7ad9\u8d77\u8eab\u6765\u3002 \u3000\u3000\u53ea\u542c\u5f97\u9774\u58f0\u6a50\u6a50\uff0c\u51e0\u4e2a\u4eba\u8d70\u8fdb\u5385\u6765\u3002\u4f17\u6b66\u5b98\u9f50\u58f0\u559d\u9053\uff1a\u201c\u53c2\u89c1\u5927\u5e05\uff01\u201d\u4e00\u9f50\u4fef\u8eab\uff0c\u534a\u819d\u8dea\u4e86\u4e0b\u53bb\u3002\u798f\u5eb7\u5b89\u5c06\u624b\u4e00\u6446\uff0c\u8bf4\u9053\uff1a\u201c\u7f62\u4e86\uff01\u8bf7\u8d77\uff01\u201d\u4f17\u6b66\u5b98\u9053\uff1a\u201c\u8c22\u5927\u5e05\uff01\u201d\u556a\u556a\u6570\u58f0\uff0c\u5404\u81ea\u7ad9\u8d77\u3002 \u3000\u3000\u80e1\u6590\u5fc3\u9053\uff1a\u201c\u798f\u5eb7\u5b89\u6cbb\u519b\u4e25\u6574\uff0c\u5927\u975e\u5e73\u5eb8\u4e4b\u8f88\u3002\u65e0\u602a\u4ed6\u6570\u6b21\u51fa\u5f81\uff0c\u6bcf\u4e00\u6b21\u90fd\u6253\u80dc\u4ed7\u3002\u201d\u53ea\u89c1\u4ed6\u6ee1\u8138\u6625\u98ce\uff0c\u795e\u8272\u751a\u559c\uff0c\u53c8\u60f3\uff1a\u201c\u8fd9\u4eba\u5168\u65e0\u5fc3\u809d\uff0c\u4e24\u4e2a\u513f\u5b50\u7ed9\u4eba\u62a2\u4e86\u53bb\uff0c\u7adf\u662f\u6f2b\u4e0d\u5728\u4e4e\u3002\u201d \u3000\u3000\u798f\u5eb7\u5b89\u547d\u4eba\u659f\u4e86\u4e00\u676f\u9152\uff0c\u8bf4\u9053\uff1a\u201c\u5404\u4f4d\u6b66\u5e08\u6765\u4eac\uff0c\u672c\u90e8\u7ed9\u5404\u4f4d\u63a5\u98ce\uff0c\u5e72\u676f\uff01\u201d\u8bf4\u7740\u4e3e\u676f\u800c\u5c3d\u3002\u7fa4\u8c6a\u4e00\u9f50\u5e72\u676f\u3002 \u3000\u3000\u8fd9\u4e00\u6b21\u80e1\u6590\u53ea\u5c06\u9152\u676f\u5728\u5507\u8fb9\u78b0\u4e86\u4e00\u78b0\uff0c\u5e76\u4e0d\u996e\u9152\u3002\u4ed6\u5fc3\u4e2d\u607c\u6068\u798f\u5eb7\u5b89\u5fc3\u80a0\u6bd2\u8fa3\uff0c\u660e\u77e5\u6bcd\u4eb2\u5bf9\u9a6c\u6625\u82b1\u4e0b\u6bd2\uff0c\u5374\u4e0d\u76f8\u6551\uff0c\u56e0\u6b64\u4e0d\u613f\u8ddf\u4ed6\u5e72\u676f\u3002 \u3000\u3000\u798f\u5eb7\u5b89\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u8fd9\u4e2a\u5929\u4e0b\u638c\u95e8\u4eba\u5927\u4f1a\uff0c\u4e07\u5c81\u7237\u4e5f\u77e5\u9053\u4e86\u3002\u521a\u624d\u7687\u4e0a\u53ec\u89c1\uff0c\u8d50\u4e86\u4e8c\u5341\u56db\u53ea\u676f\u5b50\uff0c\u547d\u672c\u90e8\u8f6c\u8d50\u7ed9\u4e8c\u5341\u56db\u4f4d\u638c\u95e8\u4eba\u3002\u201d\u4ed6\u624b\u4e00\u6325\uff0c\u4f17\u4eba\u6367\u4e0a\u4e09\u53ea\u9526\u76d2\uff0c\u5728\u684c\u4e0a\u94fa\u4e86\u9526\u7f0e\uff0c\u4ece\u76d2\u4e2d\u53d6\u51fa\u676f\u6765\u3002 \u3000\u3000\u53ea\u89c1\u7b2c\u4e00\u53ea\u76d2\u4e2d\u76db\u7684\u662f\u516b\u53ea\u7389\u676f\uff0c\u7b2c\u4e8c\u53ea\u76d2\u4e2d\u662f\u516b\u53ea\u91d1\u676f\uff0c\u7b2c\u4e09\u53ea\u76d2\u4e2d\u53d6\u51fa\u7684\u662f\u516b\u53ea\u94f6\u676f\uff0c\u5206\u6210\u4e09\u5217\u653e\u5728\u684c\u4e0a\u3002\u7389\u6c14\u6676\u83b9\uff0c\u91d1\u8272\u707f\u70c2\uff0c\u94f6\u5149\u8f89\u714c\u3002\u676f\u4e0a\u51f9\u51f9\u51f8\u51f8\u7684\u523b\u6ee1\u4e86\u82b1\u7eb9\uff0c\u8fdc\u8fdc\u77a7\u53bb\uff0c\u53ea\u89c9\u751a\u662f\u8003\u7a76\u7cbe\u7ec6\uff0c\u5927\u5185\u9ad8\u624b\u5320\u4eba\u7684\u624b\u827a\uff0c\u679c\u662f\u4e0d\u540c\u3002 \u3000\u3000\u798f\u5eb7\u5b89\u9053\uff1a\u201c\u8fd9\u7389\u676f\u4e0a\u523b\u7684\u662f\u87e0\u9f99\u4e4b\u5f62\uff0c\u53eb\u505a\u7389\u9f99\u676f\uff0c\u6700\u662f\u73cd\u8d35\u3002\u91d1\u676f\u4e0a\u523b\u7684\u662f\u98de\u51e4\u4e4b\u5f62\uff0c\u53eb\u4f5c\u91d1\u98ce\u676f\u3002\u94f6\u676f\u4e0a\u523b\u7684\u662f\u8dc3\u9ca4\u4e4b\u5f62\uff0c\u53eb\u4f5c\u94f6\u9ca4\u676f\u3002\u201d \u3000\u3000\u4f17\u4eba\u671b\u7740\u4e8c\u5341\u56db\u53ea\u5fa1\u676f\uff0c\u5747\u60f3\uff1a\u201c\u8fd9\u91cc\u4e0e\u4f1a\u7684\u638c\u95e8\u4eba\u5171\u6709\u4e00\u767e\u4f59\u4eba\uff0c\u5fa1\u676f\u5374\u53ea\u6709\u4e8c\u5341\u56db\u53ea\uff0c\u5374\u8d50\u7ed9\u8c01\u597d\uff1f\u96be\u9053\u662f\u62c8\u9604\u62bd\u7b7e\u4e0d\u6210\uff1f\u518d\u8bf4\uff0c\u90a3\u7389\u9f99\u676f\u81ea\u6bd4\u94f6\u9ca4\u8d35\u91cd\u5f97\u591a\uff0c\u5374\u53c8\u662f\u8c01\u5f97\u7389\u7684\uff0c\u8c01\u5f97\u94f6\u7684\uff1f\u201d \u3000\u3000\u53ea\u89c1\u798f\u5eb7\u5b89\u53d6\u8fc7\u56db\u53ea\u7389\u676f\uff0c\u4eb2\u624b\u9001\u5230\u56db\u5927\u638c\u95e8\u4eba\u7684\u5e2d\u4e0a\uff0c\u6bcf\u4eba\u4e00\u53ea\uff0c\u8bf4\u9053\uff1a\u201c\u56db\u4f4d\u638c\u95e8\u662f\u6b66\u6797\u9996\u9886\uff0c\u6bcf\u4eba\u9886\u7389\u9f99\u676f\u4e00\u53ea\u3002\u201d \u3000\u3000\u5927\u667a\u7985\u5e08\u7b49\u4e00\u9f50\u8eac\u8eab\u9053\u8c22\u3002 \u3000\u3000\u798f\u5eb7\u5b89\u53c8\u9053\uff1a\u201c\u8fd9\u91cc\u5c1a\u4f59\u4e0b\u4e8c\u5341\u53ea\u5fa1\u676f\uff0c\u672c\u90e8\u60f3\u8bf7\u8bf8\u4f4d\u5404\u732e\u7edd\u827a\uff0c\u6b66\u529f\u6700\u5f3a\u7684\u56db\u4f4d\u5206\u5f97\u56db\u53ea\u7389\u676f\uff0c\u53ef\u4e0e\u5c11\u6797\u3001\u6b66\u5f53\u3001\u4e09\u624d\u5251\u3001\u9ed1\u9f99\u95e8\u56db\u95e8\u5408\u79f0\u2018\u7389\u9f99\u516b\u95e8\u2019\uff0c\u662f\u5929\u4e0b\u7b2c\u4e00\u7b49\u7684\u5927\u95e8\u6d3e\u3002 \u3000\u3000\u5176\u6b21\u516b\u4f4d\u638c\u95e8\u4eba\u5206\u5f97\u516b\u53ea\u91d1\u676f\uff0c\u90a3\u662f\u2018\u91d1\u51e4\u516b\u95e8\u2019\u3002\u518d\u5176\u6b21\u516b\u4f4d\u5206\u5f97\u516b\u53ea\u94f6\u676f\uff0c\u90a3\u662f\u2018\u94f6\u9ca4\u516b\u95e8\u2019\u3002\u4ece\u6b64\u5404\u95e8\u5404\u6d3e\u5206\u4e86\u7b49\u7ea7\u6b21\u7b2c\uff0c\u6b66\u6797\u4e2d\u4fbf\u53ef\u5c11\u4e86\u8bb8\u591a\u7eb7\u4e89\u3002\u81f3\u4e8e\u5927\u667a\u7985\u5e08\u3001\u65e0\u9752\u5b50\u9053\u957f\u3001\u6c64\u5927\u4fa0\u3001\u6d77\u4f50\u9886\u56db\u4f4d\uff0c\u5219\u662f\u54c1\u5b9a\u6b66\u529f\u9ad8\u4e0b\u7684\u516c\u8bc1\uff0c\u5404\u4f4d\u53ef\u6709\u5f02\u8bae\u6ca1\u6709\uff1f\u201d \u3000\u3000\u8bb8\u591a\u6709\u89c1\u8bc6\u7684\u638c\u95e8\u4eba\u5747\u60f3\uff1a\u201c\u8fd9\u54ea\u91cc\u662f\u5c11\u4e86\u8bb8\u591a\u7eb7\u4e89\uff1f\u5404\u95e8\u5404\u6d3e\u4e00\u5206\u7b49\u7ea7\u6b21\u7b2c\uff0c\u6b66\u6797\u4e2d\u7acb\u65f6\u4fbf\u60f9\u51fa\u65e0\u7a77\u7684\u7978\u60a3\u3002\u8fd9\u4e8c\u5341\u56db\u53ea\u5fa1\u676f\u52bf\u5fc5\u4f60\u4e89\u6211\u593a\u3002\u5929\u4e0b\u6b66\u4eba\u4ece\u6b64\u4e89\u540d\u4ee5\u6597\uff0c\u81ea\u76f8\u6b8b\u6740\uff0c\u5200\u5149\u8840\u5f71\uff0c\u518d\u4e5f\u6ca1\u6709\u5b81\u65e5\u4e86\u3002\u201d \u3000\u3000\u53ef\u662f\u798f\u5927\u5e05\u65e2\u5982\u6b64\u8bf4\uff0c\u53c8\u6709\u8c01\u6562\u5f02\u8bae\uff1f\u65e9\u6709\u4eba\u968f\u58f0\u9644\u548c\uff0c\u7eb7\u7eb7\u559d\u5f69\u3002 \u3000\u3000\u798f\u5eb7\u5b89\u53c8\u9053\uff1a\u201c\u5f97\u4e86\u8fd9\u4e8c\u5341\u56db\u53ea\u5fa1\u676f\u7684\uff0c\u81ea\u7136\u8981\u597d\u597d\u7684\u770b\u7ba1\u7740\u3002\u82e5\u662f\u7ed9\u522b\u95e8\u522b\u6d3e\u62a2\u4e86\u53bb\u3001\u5077\u4e86\u53bb\uff0c\u90a3\u7389\u9f99\u516b\u95e8\u3001\u91d1\u51e4\u516b\u95e8\u3001\u94f6\u9ca4\u516b\u95e8\uff0c\u8ddf\u4eca\u65e5\u4f1a\u4e2d\u6240\u5b9a\uff0c\u5374\u53c8\u4e0d\u540c\u4e86\u54c7\uff01\u201d\u8fd9\u756a\u8bdd\u8bf4\u5f97\u53c8\u660e\u767d\u4e86\u4e00\u5c42\uff0c\u5374\u4ecd\u6709\u4e0d\u5c11\u6b66\u4eba\u9644\u548c\u54c4\u7b11\u3002 \u3000\u3000\u80e1\u6590\u542c\u4e86\u798f\u5eb7\u5b89\u7684\u4e00\u756a\u8bf4\u8bdd\uff0c\u53c8\u60f3\u8d77\u8881\u7d2b\u8863\u65e5\u524d\u6240\u8ff0\u4ed6\u53ec\u5f00\u8fd9\u5929\u4e0b\u638c\u95e8\u4eba\u5927\u4f1a\u7684\u7528\u610f\uff0c\u5fc3\u9053\uff1a\u201c\u521d\u65f6\u6211\u8fd8\u9053\u4ed6\u53ea\u662f\u5ef6\u63fd\u5929\u4e0b\u82f1\u96c4\u8c6a\u6770\uff0c\u6536\u4e3a\u5df1\u7528\uff0c\u90a3\u77e5\u4ed6\u7684\u7528\u610f\u66f4\u8981\u6bd2\u8fa3\u5f97\u591a\u3002\u4ed6\u662f\u5b58\u5fc3\u6311\u8d77\u6b66\u6797\u4e2d\u5404\u95e8\u6d3e\u7684\u7eb7\u4e89\uff0c\u8981\u5929\u4e0b\u6b66\u5b66\u4e4b\u58eb\uff0c\u53ea\u4e3a\u4e86\u4e00\u70b9\u513f\u865a\u540d\uff0c\u4fbf\u81ea\u76f8\u6b8b\u6740\uff0c\u518d\u4e5f\u6ca1\u4f59\u529b\u6765\u53cd\u6297\u6ee1\u6e05\u3002\u201d\u6b63\u60f3\u5230\u8fd9\u91cc\uff0c\u53ea\u89c1\u7a0b\u7075\u7d20\u4f38\u51fa\u98df\u6307\uff0c\u6cbe\u4e86\u4e00\u70b9\u8336\u6c34\uff0c\u5728\u684c\u4e0a\u5199\u4e86\u4e2a\u201c\u4e8c\u201d\uff0c\u53c8\u5199\u4e86\u4e2a\u201c\u6843\u201d\u5b57\uff0c\u5199\u540e\u968f\u5373\u7528\u624b\u6307\u62b9\u53bb\u3002 \u3000\u3000\u80e1\u6590\u70b9\u4e86\u70b9\u5934\uff0c\u8fd9\u201c\u4e8c\u6843\u6740\u4e09\u58eb\u201d\u7684\u6545\u4e8b\uff0c\u4ed6\u662f\u66fe\u542c\u4eba\u8bf4\u8fc7\u7684\uff0c\u5fc3\u9053\uff1a\u201c\u53e4\u65f6\u664f\u5a74\u4f7f\u2018\u4e8c\u6843\u6740\u4e09\u58eb\u2019\u7684\u5947\u8ba1\uff0c\u53ea\u7528\u4e24\u679a\u6843\u5b50\uff0c\u4fbf\u4f7f\u4e09\u4e2a\u6840\u9a9c\u4e0d\u9a6f\u7684\u52c7\u58eb\u81ea\u6740\u800c\u6b7b\u3002\u4eca\u65e5\u798f\u5eb7\u5b89\u8981\u5b66\u77ee\u5b50\u664f\u5a74\u3002\u53ea\u4e0d\u8fc7\u4ed6\u6c14\u9b44\u5927\u5f97\u591a\uff0c\u8981\u4ee5\u4e8c\u5341\u56db\u53ea\u676f\u5b50\uff0c\u5bb3\u5c3d\u4e86\u5929\u4e0b\u6b66\u4eba\u3002\u201d\u4ed6\u73af\u987e\u56db\u5468\uff0c\u53ea\u89c1\u5c11\u58ee\u7684\u6b66\u4eba\u5927\u90fd\u5174\u9ad8\u91c7\u70c8\uff0c\u6025\u6b32\u4e00\u663e\u8eab\u624b\uff0c\u4f46\u4e5f\u6709\u5c11\u6570\u4e2d\u5e74\u548c\u8001\u5e74\u7684\u638c\u95e8\u4eba\u9732\u51fa\u4e0d\u4ee5\u4e3a\u7136\u7684\u795e\u8272\uff0c\u663e\u662f\u4e5f\u60f3\u5230\u4e86\u4e89\u676f\u4e4b\u4e8b\uff0c\u540e\u60a3\u5927\u662f\u4e0d\u5c0f\u3002 \u3000\u3000\u4f46\u89c1\u5927\u5385\u4e0a\u5404\u4eba\u7eb7\u7eb7\u8bae\u8bba\uff0c\u4e00\u65f6\u58f0\u97f3\u6781\u662f\u5608\u6742\uff0c\u53ea\u542c\u90bb\u684c\u6709\u4eba\u8bf4\u9053\uff1a\u201c\u738b\u8001\u7237\u5b50\uff0c\u4f60\u795e\u62f3\u95e8\u7684\u6b66\u529f\u51fa\u7c7b\u62d4\u8403\uff0c\u5929\u4e0b\u5c11\u6709\u4eba\u654c\uff0c\u5b9a\u53ef\u593a\u5f97\u4e00\u53ea\u7389\u9f99\u676f\u4e86\u3002\u201d\u90a3\u4eba\u8c26\u9053\uff1a\u201c\u7389\u9f99\u676f\u662f\u4e0d\u6562\u60f3\u7684\uff0c\u5018\u82e5\u80fd\u6367\u5f97\u4e00\u53ea\u91d1\u51e4\u676f\u56de\u5bb6\uff0c\u4e5f\u53ef\u4ee5\u5411\u5b69\u5b50\u4eec\u4ea4\u5dee\u5566\uff01\u201d\u53c8\u6709\u4eba\u4f4e\u58f0\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u5c31\u6015\u8fde\u94f6\u9ca4\u676f\u4e5f\u6cbe\u4e0d\u5230\u4e00\u70b9\u8fb9\u513f\uff0c\u90a3\u53ef\u5c31\u4e22\u4eba\u5566\u3002\u201d\u90a3\u59d3\u738b\u7684\u8001\u8005\u6012\u76ee\u800c\u89c6\uff0c\u8bf4\u98ce\u51c9\u8bdd\u7684\u4eba\u5374\u6cf0\u7136\u81ea\u82e5\uff0c\u4e0d\u4e88\u7406\u4f1a\u3002\u4e00\u65f6\u4e4b\u95f4\uff0c\u6570\u767e\u4eba\u4ea4\u5934\u63a5\u8033\uff0c\u8c08\u7684\u90fd\u662f\u90a3\u4e8c\u5341\u56db\u53ea\u5fa1\u676f\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u798f\u5eb7\u5b89\u8eab\u65c1\u968f\u4ece\u51fb\u4e86\u4e09\u4e0b\u638c\uff0c\u8bf4\u9053\uff1a\u201c\u5404\u4f4d\u8bf7\u9759\u4e00\u9759\uff0c\u798f\u5927\u5e05\u5c1a\u6709\u8bdd\u8bf4\u3002\u201d\u5927\u5385\u4e0a\u5608\u6742\u4e4b\u58f0\uff0c\u6e10\u6e10\u6b62\u6b47\uff0c\u53ea\u56e0\u7fa4\u8c6a\u7d20\u6765\u4e0d\u53d7\u7ea6\u675f\uff0c\u4e0d\u4f3c\u519b\u4f0d\u4e4b\u4e2d\u4ee4\u51fa\u5373\u4ece\uff0c\u9694\u4e86\u597d\u4e00\u9635\uff0c\u65b9\u624d\u5bc2\u9759\u65e0\u58f0\u3002 \u3000\u3000\u798f\u5eb7\u5b89\u9053\uff1a\u201c\u5404\u4f4d\u518d\u559d\u51e0\u676f\uff0c\u5f85\u4f1a\u9152\u9189\u996d\u9971\uff0c\u5404\u732e\u7edd\u827a\u3002 \u3000\u3000\u81f3\u4e8e\u6bd4\u8bd5\u6b66\u827a\u7684\u65b9\u6cd5\uff0c\u5927\u5bb6\u542c\u5b89\u63d0\u7763\u8bf4\u4e00\u8bf4\u3002\u201d \u3000\u3000\u7ad9\u5728\u4ed6\u8eab\u65c1\u7684\u5b89\u63d0\u7763\u8170\u7c97\u8180\u5bbd\uff0c\u8c8c\u76f8\u5a01\u6b66\uff0c\u8bf4\u9053\uff1a\u201c\u8bf7\u5404\u4f4d\u5bbd\u91cf\u591a\u7528\u9152\u996d\uff0c\u7b75\u5e2d\u8fc7\u540e\uff0c\u5144\u5f1f\u518d\u5411\u5404\u4f4d\u89e3\u8bf4\u3002\u8bf7\uff0c\u8bf7\uff0c\u5144\u5f1f\u656c\u5404\u4f4d\u4e00\u676f\u3002\u201d\u8bf4\u7740\u5728\u5927\u676f\u4e2d\u659f\u4e86\u4e00\u6ee1\u676f\uff0c\u4e00\u996e\u800c\u5c3d\u3002 \u3000\u3000\u4e0e\u4f1a\u7684\u7fa4\u96c4\u672c\u6765\u5927\u90fd\u8c6a\u4e8e\u9152\u91cf\uff0c\u4f46\u8fd9\u65f6\u60f3\u5230\u996d\u540e\u4fbf\u6709\u4e00\u573a\u5267\u6597\uff0c\u4eba\u4eba\u90fd\u4e0d\u6562\u591a\u559d\uff0c\u9664\u4e86\u4e00\u4e9b\u51b3\u610f\u4e0d\u51fa\u624b\u593a\u676f\u7684\u9ad8\u624b\u8006\u5bbf\u4e4b\u5916\uff0c\u90fd\u662f\u4e3e\u676f\u6cbe\u5507\uff0c\u4f5c\u4e2a\u610f\u601d\uff0c\u4fbf\u653e\u4e0b\u4e86\u9152\u676f\u3002 \u3000\u3000\u9152\u7b75\u4e30\u76db\u65e0\u6bd4\uff0c\u53ef\u662f\u4eba\u4eba\u5fc3\u6709\u6302\u6000\uff0c\u8c01\u4e5f\u6ca1\u5fc3\u7eea\u6765\u7ec6\u5c1d\u6ee1\u684c\u5c71\u73cd\u6d77\u5473\uff0c\u53ea\u662f\u60f3\u5230\u5f85\u4f1a\u4fbf\u8981\u52a8\u624b\uff0c\u996d\u5374\u975e\u5403\u9971\u4e0d\u53ef\uff0c\u56e0\u6b64\u4e00\u5e72\u6b66\u5e08\uff0c\u5341\u4e4b\u516b\u4e5d\u90fd\u662f\u9152\u4e0d\u9189\u800c\u996d\u9971\u3002 \u3000\u3000\u5f85\u5f97\u7b75\u5e2d\u64a4\u53bb\uff0c\u5b89\u63d0\u7763\u51fb\u638c\u4e09\u4e0b\u3002\u5e9c\u4e2d\u4ec6\u5f79\u5728\u5927\u5385\u6b63\u4e2d\u5e76\u6392\u653e\u4e86\u516b\u5f20\u592a\u5e08\u6905\uff0c\u4e1c\u5385\u548c\u897f\u5385\u4e5f\u5404\u6446\u516b\u5f20\u3002\u5927\u5385\u7684\u516b\u5f20\u592a\u5e08\u6905\u4e0a\u94fa\u4e86\u91d1\u4e1d\u7ee3\u7684\u7ea2\u8272\u7f0e\u57ab\uff0c\u4e1c\u5385\u6905\u4e0a\u94fa\u4e86\u7eff\u8272\u7f0e\u57ab\uff0c\u897f\u5385\u6905\u4e0a\u94fa\u4e86\u767d\u8272\u7f0e\u57ab\u3002\u4e09\u540d\u536b\u58eb\u6367\u4e86\u7389\u9f99\u676f\u3001\u91d1\u51e4\u676f\u3001\u94f6\u9ca4\u676f\uff0c\u5206\u522b\u653e\u5728\u5927\u5385\u3001\u4e1c\u5385\u548c\u897f\u5385\u7684\u4e09\u5f20\u8336\u51e0\u4e0a\u3002 \u3000\u3000\u5b89\u63d0\u7763\u89c1\u5b89\u6392\u5df2\u6bd5\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u4eca\u65e5\u4ee5\u6b66\u4f1a\u53cb\uff0c\u8bb2\u7a76\u70b9\u5230\u4e3a\u6b62\uff0c\u8c01\u8ddf\u8c01\u90fd\u6ca1\u51a4\u4ec7\uff0c\u6700\u597d\u662f\u522b\u4f24\u4eba\u6d41\u8840\u3002\u4e0d\u8fc7\u52a8\u624b\u8fc7\u62db\u7684\u5f53\u4e2d\uff0c\u5200\u67aa\u6ca1\u773c\uff0c\u4e5f\u4fdd\u4e0d\u5b9a\u6709\u4ec0\u4e48\u5931\u624b\u3002\u798f\u5927\u5e05\u5429\u5490\u4e86\uff0c\u54ea\u4e00\u4f4d\u53d7\u8f7b\u4f24\u7684\uff0c\u9001\u4e94\u5341\u4e24\u6c64\u836f\u8d39\uff0c\u91cd\u4f24\u7684\u9001\u4e09\u767e\u4e24\uff0c\u4e0d\u5e78\u4e27\u547d\u7684\uff0c\u798f\u5927\u5e05\u6069\u5178\uff0c\u629a\u6064\u5bb6\u5c5e\u7eb9\u94f6\u4e00\u5343\u4e24\u3002\u5728\u4f1a\u4e0a\u5931\u624b\u4f24\u4eba\u7684\uff0c\u4e0d\u8d1f\u7f6a\u8d23\u3002\u201d\u4f17\u4eba\u4e00\u542c\uff0c\u5fc3\u4e0b\u90fd\u662f\u4e00\u51c9\uff1a\u201c\u8fd9\u4e0d\u662f\u660e\u7740\u8ba9\u54b1\u4eec\u62da\u547d\u4e48\uff1f\u201d \u3000\u3000\u5b89\u63d0\u7763\u987f\u4e86\u4e00\u987f\uff0c\u53c8\u9053\uff1a\u201c\u73b0\u4e0b\u6bd4\u6b66\u5f00\u59cb\uff0c\u8bf7\u56db\u5927\u638c\u95e8\u4eba\u5165\u5ea7\u3002\u201d \u3000\u3000\u56db\u540d\u536b\u58eb\u8d70\u5230\u5927\u667a\u7985\u5e08\u3001\u65e0\u9752\u5b50\u3001\u6c64\u6c9b\u3001\u6d77\u5170\u5f3c\u8ddf\u524d\uff0c\u5f15\u7740\u56db\u4eba\u5728\u5927\u5385\u7684\u592a\u5e08\u6905\u4e0a\u5c45\u4e2d\u5750\u4e0b\u3002\u516b\u5f20\u6905\u4e0a\u5750\u4e86\u56db\u4eba\uff0c\u6bcf\u4e00\u8fb9\u90fd\u8fd8\u7a7a\u51fa\u4e24\u4e2a\u5ea7\u4f4d\u3002 \u3000\u3000\u5b89\u63d0\u7763\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u73b0\u4e0b\u8bf7\u5929\u4e0b\u5404\u5bb6\u5404\u6d3e\u7684\u638c\u95e8\u9ad8\u624b\uff0c\u5728\u798f\u5927\u5e05\u9762\u524d\u5404\u663e\u7edd\u827a\u3002\u54ea\u4e00\u4f4d\u81ea\u5fd6\u6709\u80fd\u8010\u9886\u5f97\u94f6\u9ca4\u676f\u7684\uff0c\u8bf7\u5230\u897f\u5385\u5c31\u5750\uff1b\u80fd\u9886\u5f97\u91d1\u51e4\u676f\u7684\uff0c\u8bf7\u5230\u4e1c\u5385\u5c31\u5750\u3002\u82e5\u662f\u81ea\u4fe1\u786e\u80fd\u827a\u538b\u5f53\u573a\uff0c\u53ef\u548c\u56db\u5927\u638c\u95e8\u4eba\u5e76\u5217\u7684\uff0c\u8bf7\u5230\u5927\u5385\u6b63\u4e2d\u5c31\u5750\u3002\u4e8c\u5341\u4f4d\u638c\u95e8\u4eba\u5165\u5750\u4e4b\u540e\uff0c\u4f59\u4e0b\u7684\u638c\u95e8\u4eba\u54ea\u4e00\u4f4d\u4e0d\u670d\uff0c\u53ef\u5411\u5c31\u5ea7\u7684\u6311\u6218\uff0c\u8d25\u8005\u544a\u9000\uff0c\u80dc\u8005\u5c31\u4f4d\uff0c\u76f4\u5230\u65e0\u4eba\u51fa\u6765\u6311\u6218\u4e3a\u6b62\u3002\u5404\u4f4d\u770b\u8fd9\u6cd5\u513f\u5408\u9002\u4e48\uff1f\u201d \u3000\u3000\u4f17\u4eba\u5fc3\u60f3\uff1a\u201c\u8fd9\u4e0d\u662f\u6446\u4e0b\u4e86\u4e8c\u5341\u5ea7\u64c2\u53f0\u5417\uff1f\u201d\u867d\u89c9\u5927\u6df7\u6218\u4e4b\u4e0b\u6b7b\u4f24\u5fc5\u591a\uff0c\u4f46\u529b\u5f3a\u8005\u80dc\uff0c\u5012\u4e5f\u516c\u5e73\u5408\u7406\u3002\u8bb8\u591a\u6b66\u5e08\u4fbf\u5927\u58f0\u8bf4\u597d\uff0c\u65e0\u4eba\u5f02\u8bae\u3002 \u3000\u3000\u8fd9\u65f6\u798f\u5eb7\u5b89\u5750\u5728\u5de6\u4e0a\u9996\u4e00\u5f20\u5927\u6905\u4e2d\u3002\u4e24\u8fb9\u5206\u7ad9\u7740\u5341\u516d\u540d\u9ad8\u624b\u536b\u58eb\uff0c\u5468\u94c1\u9e6a\u548c\u738b\u5251\u82f1\u90fd\u5728\u5176\u5185\uff0c\u4e25\u5bc6\u536b\u62a4\uff0c\u751f\u6015\u4f17\u6b66\u5e08\u9f99\u86c7\u6df7\u6742\uff0c\u5176\u4e2d\u9690\u85cf\u4e86\u523a\u5ba2\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u4f38\u624b\u8098\u5728\u80e1\u6590\u81c2\u4e0a\u8f7b\u8f7b\u4e00\u6572\uff0c\u5634\u89d2\u5411\u4e0a\u4e00\u52aa\uff0c\u80e1\u6590\u987a\u7740\u5979\u773c\u5149\u5411\u4e0a\u770b\u53bb\uff0c\u53ea\u89c1\u5c4b\u89d2\u4e00\u6392\u6392\u7684\u7ad9\u6ee1\u4e86\u536b\u58eb\uff0c\u90fd\u662f\u624b\u63e1\u5175\u5203\u3002\u770b\u6765\u4eca\u65e5\u798f\u5eb7\u5b89\u5e9c\u4e2d\u6212\u5907\u4e4b\u4e25\uff0c\u53ea\u6015\u8fd8\u80dc\u8fc7\u4e86\u7687\u5bab\u5185\u9662\uff0c\u5e9c\u7b2c\u5468\u56f4\uff0c\u81ea\u4e5f\u662f\u5e03\u6ee1\u4e86\u7cbe\u5175\u9510\u58eb\u3002\u80e1\u6590\u5fc3\u60f3\uff1a \u3000\u3000\u201c\u4eca\u65e5\u80fd\u627e\u5230\u51e4\u5929\u5357\u90a3\u6076\u8d3c\u7684\u8e2a\u8ff9\uff0c\u5fc3\u613f\u5df2\u4e86\uff0c\u65e0\u8bba\u5982\u4f55\u4e0d\u53ef\u6cc4\u6f0f\u4e86\u5f62\u8ff9\uff0c\u5426\u5219\u53ea\u6015\u6027\u547d\u96be\u4fdd\u3002\u5f85\u4f1a\u82e5\u80fd\u66ff\u534e\u62f3\u95e8\u593a\u5230\u4e00\u53ea\u94f6\u9ca4\u676f\uff0c\u4e5f\u7b97\u662f\u5bf9\u5f97\u8d77\u8fd9\u4f4d\u59ec\u5144\u4e86\u3002\u53ea\u662f\u6211\u8d8a\u8fdf\u51fa\u624b\u8d8a\u597d\uff0c\u514d\u5f97\u591a\u5f15\u4eba\u6ce8\u76ee\u3002\u201d \u3000\u3000\u90a3\u77e5\u4ed6\u5fc3\u4e2d\u8fd9\u4e48\u6253\u7b97\uff0c\u65c1\u4eba\u7adf\u4e5f\u90fd\u662f\u8fd9\u4e2a\u4e3b\u610f\u3002\u53ea\u4e0d\u8fc7\u80e1\u6590\u6015\u7684\u662f\u88ab\u4eba\u8bc6\u7834\u4e54\u88c5\uff0c\u5176\u4f59\u6b66\u5e08\u5374\u5747\u76fc\u65c1\u4eba\u6597\u4e86\u4e2a\u7b4b\u75b2\u529b\u5c3d\uff0c\u81ea\u5df1\u6700\u540e\u51fa\u624b\uff0c\u5750\u6536\u6e14\u4eba\u4e4b\u5229\uff0c\u662f\u4ee5\u5b89\u63d0\u7763\u8fde\u8bf4\u51e0\u904d\uff1a \u3000\u3000\u201c\u8bf7\u5404\u4f4d\u5c31\u5ea7\uff01\u201d\u90a3\u4e8c\u5341\u5f20\u7a7a\u6905\u59cb\u7ec8\u7a7a\u8361\u8361\u5730\uff0c\u7adf\u65e0\u4e00\u4e2a\u6b66\u5e08\u51fa\u6765\u5750\u5165\u3002 \u3000\u3000\u4fd7\u8bed\u8bf4\u5f97\u597d\uff1a\u201c\u6587\u65e0\u7b2c\u4e00\uff0c\u6b66\u65e0\u7b2c\u4e8c\u201d\u3002\u51e1\u662f\u6587\u4eba\uff0c\u4ece\u65e0\u4e00\u4e2a\u81ea\u4ee5\u4e3a\u6587\u7ae0\u5b66\u95ee\u5929\u4e0b\u7b2c\u4e00\uff0c\u4f46\u5b66\u6b66\u4e4b\u58eb\uff0c\u9664\u4e86\u4fee\u517b\u7279\u6df1\u7684\u9ad8\u624b\u4e4b\u5916\uff0c\u51b3\u8ba1\u4e0d\u80af\u7518\u5c45\u4eba\u540e\u3002\u4f55\u51b5\u6b64\u65e5\u4e0e\u4f1a\u4e4b\u4eba\u90fd\u662f\u4e00\u6d3e\u4e4b\u957f\uff0c\u5e73\u7d20\u5747\u662f\u81ea\u5c0a\u81ea\u5927\u60ef\u4e86\u7684\uff0c\u5c31\u8bf4\u81ea\u5df1\u540d\u5fc3\u6de1\u6cca\uff0c\u4e0d\u559c\u548c\u4eba\u4e89\u7ade\uff0c\u4f46\u6240\u6267\u638c\u7684\u8fd9\u95e8\u6d3e\u7684\u5a01\u671b\u5374\u51b3\u4e0d\u80fd\u5815\u4e86\u3002\u53ea\u8981\u8fd9\u665a\u5728\u4f1a\u4e2d\u5931\u624b\uff0c\u672c\u95e8\u4e2d\u6210\u5343\u6210\u767e\u7684\u5f1f\u5b50\u4eca\u540e\u5728\u6c5f\u6e56\u4e0a\u90fd\u8981\u62ac\u4e0d\u8d77\u5934\u6765\uff0c\u81ea\u5df1\u56de\u5230\u672c\u95e8\u4e4b\u4e2d\uff0c\u53c8\u600e\u6709\u9762\u76ee\u89c1\u4eba\uff1f\u53ea\u6015\u8fd9\u638c\u95e8\u4eba\u4e5f\u5f53\u4e0d\u4e0b\u53bb\u4e86\u3002\u5f53\u771f\u662f\u4eba\u540c\u6b64\u5fc3\uff0c\u5fc3\u540c\u6b64\u610f\uff1a\u201c\u6211\u82e5\u4e0d\u51fa\u624b\uff0c\u5c06\u6765\u5c1a\u53ef\u63a8\u6258\u4ea4\u4ee3\u3002\u82e5\u662f\u51fa\u624b\uff0c\u975e\u593a\u5f97\u7389\u9f99\u676f\u4e0d\u53ef\u3002\u8981\u4e00\u53ea\u91d1\u51e4\u676f\u3001\u94f6\u9ca4\u676f\uff0c\u53c8\u6709\u4f55\u7528\uff1f\u201d\u56e0\u6b64\u4f17\u6b66\u5e08\u7684\u773c\u5149\uff0c\u4e2a\u4e2a\u90fd\u6ce8\u89c6\u7740\u5927\u5385\u4e0a\u90a3\u56db\u5f20\u7a7a\u7740\u7684\u592a\u5e08\u6905\uff0c\u81f3\u4e8e\u4e1c\u5385\u548c\u897f\u5385\u7684\u91d1\u51e4\u676f\u548c\u94f6\u9ca4\u676f\uff0c\u7adf\u662f\u8c01\u90fd\u4e0d\u5728\u610f\u4e0b\u3002 \u3000\u3000\u50f5\u6301\u4e86\u7247\u523b\uff0c\u5b89\u63d0\u7763\u5e72\u7b11\u9053\uff1a\u201c\u5404\u4f4d\u7adf\u90fd\u8fd9\u4e48\u8c26\u865a\uff1f\u8fd8\u662f\u60f3\u8ba9\u522b\u4e2a\u513f\u7d2f\u57ae\u4e86\uff0c\u518d\u6765\u6361\u4e2a\u73b0\u6210\u4fbf\u5b9c\uff1f\u90a3\u53ef\u4e0d\u5408\u6b66\u5b66\u5927\u5e08\u7684\u8eab\u5206\u554a\u3002\u201d\u8fd9\u51e0\u53e5\u8bdd\u4f3c\u662f\u8bf4\u7b11\uff0c\u5176\u5b9e\u5374\u662f\u9053\u7834\u4e86\u5404\u4eba\u5fc3\u4e8b\uff0c\u4ee5\u8a00\u76f8\u6fc0\u3002 \u3000\u3000\u679c\u7136\u4ed6\u8fd9\u53e5\u8bdd\u521a\u8bf4\u5b8c\uff0c\u4eba\u4e1b\u4e2d\u540c\u65f6\u8d70\u51fa\u4e24\u4e2a\u4eba\u6765\uff0c\u5728\u4e24\u5f20\u6905\u4e2d\u4e00\u5750\u3002\u4e00\u4e2a\u5927\u6c49\u8eab\u5982\u94c1\u5854\uff0c\u4e00\u8a00\u4e0d\u53d1\uff0c\u5374\u628a\u4e00\u5f20\u7d2b\u6a80\u6728\u7684\u592a\u5e08\u6905\u5750\u5f97\u683c\u683c\u76f4\u54cd\u3002\u53e6\u4e00\u4e2a\u4e2d\u7b49\u8eab\u6750\uff0c\u988f\u4e0b\u957f\u7740\u4e00\u90e8\u9ec4\u80e1\u5b50\uff0c\u7b11\u9053\uff1a\u201c\u8001\u5144\uff0c\u54b1\u54e5\u513f\u4fe9\u90fd\u662f\u629b\u7816\u5f15\u7389\u3002\u51b2\u7740\u773c\u524d\u8fd9\u8bb8\u591a\u8001\u5e08\u7236\u3001\u5927\u9ad8\u624b\uff0c\u54b1\u54e5\u513f\u96be\u9053\u8fd8\u80fd\u628a\u4e24\u53ea\u7389\u9f99\u676f\u6367\u56de\u5bb6\u53bb\u5417\uff1f\u4f60\u53ef\u522b\u628a\u6905\u5b50\u5750\u70c2\u4e86\uff0c\u987b\u5f97\u7559\u7ed9\u65c1\u4eba\u6765\u5750\u5462\u3002\u201d\u90a3\u9ed1\u5927\u6c49\u201c\u563f\u201d\u7684\u4e00\u58f0\uff0c\u8138\u8272\u96be\u770b\uff0c\u663e\u7136\u5bf9\u4ed6\u7684\u73a9\u7b11\u9887\u4e0d\u4ee5\u4e3a\u7136\u3002 \u3000\u3000\u4e00\u4e2a\u7a7f\u7740\u56db\u54c1\u9876\u6234\u7684\u6b66\u5b98\u8d70\u4e0a\u524d\u6765\uff0c\u6307\u7740\u90a3\u5927\u6c49\u6717\u58f0\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u2018\u4e8c\u90ce\u62f3\u2019\u7684\u638c\u95e8\u4eba\u9ec4\u5e0c\u8282\u9ec4\u8001\u5e08\u3002\u201d\u6307\u7740\u9ec4\u80e1\u5b50\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u2018\u71d5\u9752\u62f3\u2019\u7684\u638c\u95e8\u4eba\u6b27\u9633\u516c\u653f\u6b27\u9633\u8001\u5e08\u3002\u201d \u3000\u3000\u80e1\u6590\u542c\u5f97\u90bb\u684c\u90a3\u8001\u8005\u4f4e\u58f0\u9053\uff1a\u201c\u597d\u54c7\uff0c\u8fde\u2018\u5343\u91cc\u72ec\u884c\u4fa0\u2019\u6b27\u9633\u516c\u653f\uff0c\u5c45\u7136\u4e5f\u60f3\u53d6\u7389\u9f99\u676f\u3002\u201d\u80e1\u6590\u5fc3\u4e2d\u5fae\u5fae\u4e00\u9707\uff0c\u539f\u6765\u90a3\u6b27\u9633\u516c\u653f\u81ea\u5df1\u5b89\u4e0a\u4e2a\u5916\u53f7\u53eb\u4f5c\u201c\u5343\u91cc\u72ec\u884c\u4fa0\u201d\uff0c\u5176\u5b9e\u662f\u4e2a\u72ec\u811a\u5927\u76d7\uff0c\u7a7a\u6709\u4fa0\u76d7\u4e4b\u540d\uff0c\u5e76\u65e0\u5176\u5b9e\uff0c\u5728\u6b66\u6797\u4e2d\u540d\u5934\u867d\u54cd\uff0c\u58f0\u8a89\u5374\u662f\u6781\u4e3a\u4e0d\u4f73\uff0c\u80e1\u6590\u4e5f\u66fe\u542c\u5230\u8fc7\u4ed6\u7684\u540d\u5b57\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u4e00\u5750\u4e0a\uff0c\u8ddf\u7740\u4e00\u4e2a\u9053\u4eba\u4e0a\u53bb\uff0c\u90a3\u662f\u201c\u6606\u4ed1\u5200\u201d\u7684\u638c\u95e8\u4eba\u897f\u7075\u9053\u4eba\u3002\u53ea\u89c1\u4ed6\u8138\u542b\u5fae\u7b11\uff0c\u8eab\u4e0a\u4e0d\u5e26\u5175\u5203\uff0c\u4f3c\u4e4e\u6210\u7af9\u5728\u80f8\uff0c\u6781\u6709\u628a\u63e1\uff0c\u4f17\u4eba\u90fd\u6709\u4e9b\u5947\u602a\uff1a\u201c\u8fd9\u9053\u58eb\u662f\u2018\u6606\u4ed1\u5200\u2019\u7684\u638c\u95e8\u4eba\uff0c\u600e\u5730\u4e0d\u5e26\u5355\u5200\uff1f\u201d \u3000\u3000\u5385\u4e0a\u5404\u4eba\u6b63\u773c\u7741\u7741\u7684\u671b\u7740\u90a3\u4f59\u4e0b\u7684\u4e00\u5f20\u7a7a\u6905\uff0c\u4e0d\u77e5\u8fd8\u6709\u8c01\u633a\u8eab\u800c\u51fa\u3002\u5b89\u63d0\u7763\u8bf4\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u53ea\u7389\u676f\uff0c\u6ca1\u8c01\u8981\u4e86\u4e48\uff1f\u201d \u3000\u3000\u53ea\u542c\u5f97\u4eba\u4e1b\u4e2d\u4e00\u4eba\u53eb\u9053\uff1a\u201c\u597d\u5427\uff01\u7559\u4e0b\u7ed9\u6211\u9152\u9b3c\u88c5\u9152\u559d\uff01\u201d \u3000\u3000\u4e00\u4e2a\u8eab\u6750\u9ad8\u7626\u7684\u6c49\u5b50\u8e09\u8e09\u8dc4\u8dc4\u800c\u51fa\uff0c\u4e00\u624b\u62ff\u9152\u58f6\uff0c\u4e00\u624b\u62ff\u9152\u676f\uff0c\u8d70\u5230\u5385\u5fc3\uff0c\u6655\u5934\u8f6c\u5411\u7684\u7ed5\u4e86\u4e24\u4e2a\u5708\u5b50\uff0c\u7a81\u7136\u5012\u8f6c\u8eab\u5b50\uff0c\u5411\u540e\u4e00\u8dcc\uff0c\u6454\u5165\u4e86\u90a3\u53ea\u7a7a\u6905\u4e4b\u4e2d\u3002\u8fd9\u4e00\u4e0b\u8eab\u6cd5\u8f7b\u7075\uff0c\u663e\u662f\u5f88\u9ad8\u660e\u7684\u6b66\u529f\u3002\u5927\u5385\u4e2d\u4e0d\u4e4f\u8bc6\u8d27\u4e4b\u4eba\uff0c\u65e9\u6709\u4eba\u53eb\u4e86\u8d77\u6765\uff1a\u201c\u597d\u4e00\u62db\u2018\u5f20\u679c\u8001\u5012\u9a91\u9a74\uff0c\u6454\u5728\u9ad8\u6865\u4e0a\u2019\uff01\u201d\u539f\u6765\u8fd9\u4eba\u662f\u201c\u9189\u516b\u4ed9\u201d\u7684\u638c\u95e8\u4eba\u5343\u676f\u5c45\u58eb\u6587\u9189\u7fc1\uff0c\u4f46\u89c1\u4ed6\u8863\u886b\u8934\u891b\uff0c\u6ee1\u8138\u9152\u6c14\uff0c\u4e00\u526f\u4ee4\u4eba\u83ab\u6d4b\u9ad8\u6df1\u7684\u6a21\u6837\u3002 \u3000\u3000\u5b89\u63d0\u7763\u9053\uff1a\u201c\u56db\u4f4d\u8001\u5e08\u80c6\u8bc6\u8fc7\u4eba\uff0c\u53ef\u656c\u53ef\u4f69\u3002\u8fd8\u6709\u54ea\u4e00\u4f4d\u8001\u5e08\uff0c\u81ea\u4fe1\u6b66\u529f\u80dc\u5f97\u8fc7\u8fd9\u56db\u4f4d\u4e2d\u4efb\u4f55\u4e00\u4f4d\u7684\uff0c\u4fbf\u8bf7\u51fa\u6765\u6311\u6218\u3002 \u3000\u3000\u82e5\u662f\u65e0\u4eba\u6311\u6218\uff0c\u90a3\u4e48\u4e8c\u90ce\u62f3\u3001\u71d5\u9752\u62f3\u3001\u6606\u4ed1\u5200\u3001\u9189\u516b\u4ed9\u56db\u95e8\uff0c\u4fbf\u5f97\u5f52\u4e8e\u2018\u7389\u9f99\u516b\u95e8\u2019\u4e4b\u5217\u4e86\u3002\u201d \u3000\u3000\u53ea\u89c1\u4e1c\u9996\u4e00\u4eba\u62a2\u6b65\u800c\u4e0a\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u4eba\u5468\u9686\uff0c\u613f\u610f\u4f1a\u4e00\u4f1a\u2018\u5343\u91cc\u72ec\u884c\u4fa0\u2019\u6b27\u9633\u8001\u5e08\u3002\u201d\u8fd9\u4eba\u6ee1\u8138\u808c\u8089\u866c\u8d77\uff0c\u8eab\u6750\u77ee\u58ee\uff0c\u4fbf\u5982\u4e00\u53ea\u726f\u725b\u76f8\u4f3c\u3002 \u3000\u3000\u80e1\u6590\u5bf9\u4e00\u5e72\u6b66\u6797\u4eba\u7269\u90fd\u4e0d\u76f8\u8bc6\uff0c\u5168\u4ed7\u65c1\u542c\u90bb\u5ea7\u7684\u8001\u8005\u5bf9\u4eba\u89e3\u8bf4\u3002\u597d\u5728\u90a3\u8001\u8005\u9887\u4ee5\u89c1\u591a\u8bc6\u5e7f\u81ea\u559c\uff0c\u51e1\u662f\u77e5\u9053\u7684\uff0c\u65e0\u4e0d\u62a2\u5148\u800c\u8bf4\u3002\u53ea\u542c\u4ed6\u9053\uff1a\u201c\u8fd9\u4f4d\u5468\u8001\u5e08\u662f\u2018\u91d1\u521a\u62f3\u2019\u7684\u638c\u95e8\u4eba\uff0c\u53c8\u662f\u5c71\u897f\u5927\u540c\u5e9c\u5174\u9686\u9556\u5c40\u7684\u603b\u9556\u5934\u3002\u542c\u8bf4\u6b27\u9633\u516c\u653f\u52ab\u8fc7\u4ed6\u7684\u9556\uff0c\u4ed6\u4e8c\u4eba\u5f88\u6709\u8fc7\u8282\u3002\u6211\u770b\u8fd9\u4f4d\u5468\u8001\u5e08\u4e0b\u573a\u5b50\uff0c\u5176\u610f\u5012\u4e0d\u4e00\u5b9a\u662f\u5728\u7389\u9f99\u676f\u3002\u201d \u3000\u3000\u80e1\u6590\u5fc3\u60f3\uff1a\u201c\u6b66\u6797\u4e2d\u6069\u6069\u6028\u6028\uff0c\u7275\u7f20\u7ea0\u845b\uff0c\u5c31\u50cf\u6211\u81ea\u5df1\uff0c\u8fd9\u4e00\u8d9f\u5168\u662f\u4e3a\u51e4\u5929\u5357\u90a3\u6076\u8d3c\u800c\u6765\u3002\u5404\u95e8\u5404\u6d3e\u4e4b\u95f4\uff0c\u53ea\u6015\u7d2f\u4e16\u6210\u4ec7\u5df2\u8fbe\u6570\u767e\u5e74\u7684\u4e5f\u6709\u4e0d\u5c11\u3002\u96be\u9053\u90fd\u60f3\u5728\u4eca\u65e5\u4f1a\u4e2d\u4e86\u65ad\u4e48\uff1f\u201d \u3000\u3000\u60f3\u5230\u8fd9\u91cc\uff0c\u60c5\u4e0d\u81ea\u7981\u7684\u671b\u4e86\u51e4\u5929\u5357\u4e00\u773c\uff0c\u53ea\u89c1\u4ed6\u4e0d\u4f4f\u624b\u7684\u8f6c\u52a8\u4e24\u679a\u94c1\u80c6\uff0c\u5374\u4e0d\u53d1\u51fa\u534a\u70b9\u58f0\u606f\uff0c\u795e\u8272\u751a\u662f\u5b81\u5b9a\u3002\u80e1\u6590\u5728\u798f\u5eb7\u5b89\u5e9c\u4e2d\u95f9\u4e86\u4e24\u665a\uff0c\u4e5d\u57ce\u5927\u7d22\uff0c\u51e4\u5929\u5357\u6599\u60f3\u4ed6\u65e9\u5df2\u9003\u51fa\u5317\u4eac\uff0c\u9ad8\u98de\u8fdc\u8d70\uff0c\u90a3\u60f3\u5f97\u5230\u4ed6\u82f1\u96c4\u4fa0\u80c6\uff0c\u7adf\u53c8\u4f1a\u6df7\u8fdb\u8fd9\u9f99\u6f6d\u864e\u7a74\u7684\u638c\u95e8\u4eba\u5927\u4f1a\u4e2d\u6765\uff1f \u3000\u3000\u5468\u9686\u8fd9\u4e48\u4e00\u6311\u6218\uff0c\u6b27\u9633\u516c\u653f\u7b11\u563b\u563b\u7684\u8d70\u4e0b\u5ea7\u4f4d\uff0c\u7b11\u9053\uff1a\u201c\u5468\u603b\u9556\u5934\uff0c\u8fd1\u6765\u53d1\u8d22\uff1f\u751f\u610f\u5174\u9686\uff1f\u201d \u3000\u3000\u5468\u9686\u5e74\u524d\u6240\u4fdd\u7684\u516b\u4e07\u4e24\u94f6\u5b50\u4e00\u679d\u9556\u7ed9\u4ed6\u52ab\u4e86\uff0c\u59cb\u7ec8\u8ffd\u4e0d\u56de\u6765\uff0c\u8d54\u5f97\u503e\u5bb6\u8361\u4ea7\uff0c\u6570\u5341\u5e74\u7684\u79ef\u84c4\u4e00\u65e6\u800c\u5c3d\uff0c\u5982\u4f55\u4e0d\u6068\u5f97\u7259\u75d2\u75d2\u7684\uff1f\u5f53\u4e0b\u66f4\u4e0d\u6253\u8bdd\uff0c\u4e00\u62db\u201c\u53cc\u5288\u53cc\u649e\u201d\u76f4\u51fb\u51fa\u53bb\u3002\u6b27\u9633\u516c\u653f\u8fd8\u4e86\u4e00\u62db\u71d5\u9752\u62f3\u4e2d\u7684\u201c\u8131\u9774\u8f6c\u8eab\u201d\uff0c\u4e24\u4eba\u767b\u65f6\u6fc0\u6597\u8d77\u6765\u3002 \u3000\u3000\u5468\u9686\u80dc\u5728\u529b\u5927\u62db\u6c89\uff0c\u4e0b\u76d8\u7a33\u56fa\uff0c\u6b27\u9633\u516c\u653f\u5374\u4ee5\u62f3\u62db\u7075\u52a8\u3001\u8eab\u6cd5\u8f7b\u6377\u89c1\u957f\u3002\u5468\u9686\u4e00\u8eab\u6a2a\u7ec3\u529f\u592b\uff0c\u5bf9\u654c\u4eba\u6765\u62db\u7adf\u4e0d\u5927\u95ea\u907f\uff0c\u80a9\u5934\u80f8\u53e3\u63a5\u8fde\u4e2d\u4e86\u4e09\u62f3\uff0c\u7adf\u662f\u54fc\u4e5f\u6ca1\u54fc\u4e00\u58f0\uff0c\u7a81\u7136\u95f4\u547c\u7684\u4e00\u62f3\u6253\u51fa\uff0c\u5374\u662f\u201c\u91d1\u521a\u62f3\u201d\u4e2d\u7684\u201c\u8fce\u98ce\u6253\u201d\u3002\u6b27\u9633\u516c\u653f\u4e00\u7b11\u95ea\u5f00\uff0c\u98de\u811a\u8e39\u51fa\uff0c\u8e22\u5728\u4ed6\u7684\u817f\u4e0a\u3002\u5468\u9686\u201c\u62a2\u80cc\u5927\u4e09\u62cd\u201d\u5c31\u5730\u7ffb\u6eda\uff0c\u6454\u4e86\u4e00\u4ea4\uff0c\u5374\u53c8\u7ad9\u8d77\u3002 \u3000\u3000\u4e24\u4eba\u62c6\u5230\u56db\u4e94\u5341\u62db\uff0c\u5468\u9686\u8eab\u4e0a\u5df2\u4e2d\u4e86\u5341\u4f59\u4e0b\u62f3\u811a\uff0c\u51b7\u4e0d\u9632\u9f3b\u4e0a\u53c8\u4e2d\u4e86\u4e00\u62f3\uff0c\u767b\u65f6\u9f3b\u8840\u957f\u6d41\uff0c\u8863\u895f\u4e0a\u5168\u662f\u9c9c\u8840\u3002\u6b27\u9633\u516c\u653f\u7b11\u9053\uff1a\u201c\u5468\u8001\u5e08\uff0c\u6211\u53ea\u4e0d\u8fc7\u62a2\u4e86\u4f60\u9556\u94f6\uff0c\u53c8\u6ca1\u62a2\u4f60\u8001\u5a46\uff0c\u8bf4\u4e0d\u4e0a\u6740\u7236\u4e4b\u4ec7\u3001\u593a\u59bb\u4e4b\u6068\u3002\u8fd9\u5c31\u7b97\u4e86\u5427\uff01\u201d\u5468\u9686\u4e00\u8a00\u4e0d\u53d1\uff0c\u6251\u4e0a\u53d1\u62db\u3002\u6b27\u9633\u516c\u653f\u4ed7\u7740\u8f7b\u529f\u4e86\u5f97\uff0c\u4fa7\u8eab\u907f\u5f00\uff0c\u53e3\u4e2d\u4e0d\u65ad\u8bf4\u8f7b\u8584\u8a00\u8bed\uff0c\u610f\u56fe\u6fc0\u6012\u5bf9\u65b9\u3002 \u3000\u3000\u9163\u6218\u4e2d\u5468\u9686\u5c0f\u8179\u4e0a\u53c8\u88ab\u8d50\u4e2d\u4e86\u4e00\u811a\uff0c\u4ed6\u5de6\u624b\u6309\u8179\uff0c\u6ee1\u8138\u75db\u82e6\u4e4b\u8272\uff0c\u7a81\u7136\u4e4b\u95f4\uff0c\u53f3\u624b\u201c\u91d1\u94a9\u6302\u7389\u201d\uff0c\u62a2\u8fdb\u4e00\u6b65\uff0c\u4e00\u62db\u201c\u6ca1\u906e\u62e6\u201d\uff0c\u7ed3\u7ed3\u5b9e\u5b9e\u7684\u6376\u4e2d\u5728\u654c\u4eba\u80f8\u53e3\u3002\u4f46\u542c\u5f97\u5580\u5587\u4e00\u54cd\uff0c\u6b27\u9633\u516c\u653f\u65ad\u4e86\u51e0\u6839\u808b\u9aa8\uff0c\u6447\u6447\u6643\u6643\uff0c\u4e00\u53e3\u9c9c\u8840\u55b7\u4e86\u51fa\u6765\u3002 \u3000\u3000\u4ed6\u77e5\u5468\u9686\u6068\u5df1\u5165\u9aa8\uff0c\u4e00\u62db\u5f97\u80dc\uff0c\u8ddf\u7740\u4fbf\u518d\u4e0b\u6bd2\u624b\uff0c\u8fd9\u65f6\u81ea\u5df1\u5df2\u65e0\u529b\u62b5\u5fa1\uff0c\u5f53\u4e0b\u5f3a\u5fcd\u75bc\u75db\uff0c\u95ea\u8eab\u9000\u4e0b\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u662f\u4f60\u80dc\u4e86\u2026\u2026\u201d\u5468\u9686\u5f85\u8981\u8ffd\u51fb\uff0c\u6c64\u6c9b\u8bf4\u9053\uff1a\u201c\u5468\u8001\u5e08\uff0c\u80dc\u8d1f\u5df2\u5206\uff0c\u4e0d\u80fd\u518d\u52a8\u624b\u4e86\u3002\u4f60\u8bf7\u5750\u5427\u3002\u201d\u5468\u9686\u542c\u5f97\u662f\u6c64\u6c9b\u51fa\u8a00\uff0c\u4e0d\u6562\u8fdd\u9006\uff0c\u62b1\u62f3\u9053\uff1a\u201c\u5c0f\u4eba\u4e0d\u6562\u4e89\u8fd9\u7389\u9f99\u676f\uff01\u201d\u62bd\u8eab\u5f52\u5ea7\u3002 \u3000\u3000\u4f17\u6b66\u5e08\u5927\u90fd\u77a7\u4e0d\u8d77\u6b27\u9633\u516c\u653f\u7684\u4e3a\u4eba\uff0c\u89c1\u5468\u9686\u82e6\u6218\u83b7\u80dc\uff0c\u7eb7\u7eb7\u8fc7\u6765\u6170\u95ee\u3002\u6b27\u9633\u516c\u653f\u6ee1\u8138\u60ed\u8272\uff0c\u5374\u4e0d\u6562\u79bb\u5ea7\u51fa\u5e9c\uff0c\u4ed6\u81ea\u77e5\u51a4\u5bb6\u592a\u591a\uff0c\u8fd9\u65f6\u8eab\u53d7\u91cd\u4f24\uff0c\u53ea\u8981\u4e00\u51fa\u798f\u5927\u5e05\u5e9c\uff0c\u7acb\u65f6\u4fbf\u6709\u4eba\u8ddf\u51fa\u6765\u4e0b\u624b\uff0c\u5468\u9686\u7b2c\u4e00\u4e2a\u4fbf\u8981\u51fa\u6765\uff0c\u53ea\u5f97\u53d6\u51fa\u4f24\u836f\u548c\u9152\u541e\u670d\uff0c\u5f3a\u5fcd\u75bc\u75db\uff0c\u5750\u7740\u4e0d\u52a8\uff0c\u5bf9\u65c1\u4eba\u7684\u51b7\u5632\u70ed\u8bbd\uff0c\u53ea\u4f5c\u4e0d\u95fb\u3002 \u3000\u3000\u80e1\u6590\u5fc3\u9053\uff1a\u201c\u8fd9\u5468\u9686\u770b\u4f3c\u6206\u76f4\uff0c\u5176\u5b9e\u751a\u662f\u806a\u660e\uff0c\u51ed\u4ed6\u7684\u529f\u592b\uff0c\u90a3\u7389\u9f99\u676f\u662f\u51b3\u8ba1\u593a\u4e0d\u5230\u7684\uff0c\u4e00\u6218\u5f97\u80dc\uff0c\u5168\u540d\u800c\u9000\u3002\u2018\u91d1\u521a\u62f3\u2019\u867d\u4e0d\u80fd\u5217\u540d\u4e3a\u2018\u7389\u9f99\u516b\u95e8\u2019\uff0c\u4f46\u5728\u6c5f\u6e56\u4e0a\u5374\u8c01\u4e5f\u4e0d\u80fd\u5c0f\u770b\u4e86\u3002\u201d \u3000\u3000\u53ea\u542c\u6c64\u6c9b\u8bf4\u9053\uff1a\u201c\u5468\u8001\u5e08\u65e2\u7136\u5fd7\u4e0d\u5728\u676f\uff0c\u6709\u54ea\u4e00\u4f4d\u8001\u5e08\u4e0a\u6765\u5750\u8fd9\u6905\u5b50\uff1f\u201d \u3000\u3000\u8fd9\u4e00\u53ea\u7a7a\u6905\u662f\u4e0d\u6218\u800c\u5f97\uff0c\u5012\u662f\u7701\u4e86\u4e00\u756a\u529b\u6c14\uff0c\u65e9\u6709\u4eba\u77a7\u51fa\u4fbf\u5b9c\uff0c\u4e24\u6761\u6c49\u5b50\u5206\u4ece\u5de6\u53f3\u62a2\u4e86\u8fc7\u53bb\u3002\u773c\u770b\u4e24\u4eba\u548c\u592a\u5e08\u6905\u76f8\u8ddd\u7684\u8fdc\u8fd1\u90fd\u662f\u4e00\u822c\uff0c\u8c01\u7684\u811a\u4e0b\u5feb\u4e00\u6b65\uff0c\u8c01\u4fbf\u53ef\u4ee5\u62a2\u5230\u3002\u54ea\u77e5\u4e24\u4eba\u6765\u52bf\u90fd\u6025\uff0c\u5954\u5230\u6905\u524d\uff0c\u53cc\u80a9\u4e00\u649e\uff0c\u5404\u81ea\u9000\u4e86\u4e24\u6b65\u3002\u4fbf\u5728\u6b64\u65f6\uff0c\u547c\u7684\u4e00\u58f0\uff0c\u4e00\u4eba\u4ece\u4eba\u4e1b\u4e2d\u7a9c\u4e86\u51fa\u6765\uff0c\u53cc\u81c2\u4e00\u632f\uff0c\u5982\u5927\u9e1f\u822c\u98de\u8d77\uff0c\u8f7b\u8f7b\u5de7\u5de7\u7684\u843d\u5728\u6905\u4e2d\u3002\u4ed6\u540e\u53d1\u800c\u5148\u81f3\uff0c\u7adf\u62a2\u5728\u90a3\u4e24\u6761\u6c49\u5b50\u7684\u524d\u9762\uff0c\u8fd9\u4e00\u4efd\u8f7b\u529f\u53ef\u5b9e\u5728\u800d\u5f97\u6f02\u4eae\u3002\u4eba\u4e1b\u4e2d\u8f70\u96f7\u4ef7\u559d\u4e86\u58f0\u5f69\u3002 \u3000\u3000\u90a3\u4e92\u76f8\u78b0\u649e\u7684\u4e24\u4e2a\u6c49\u5b50\u89c1\u6709\u4eba\u62a2\u5148\u5750\u5165\u6905\u4e2d\uff0c\u5411\u4ed6\u4e00\u770b\uff0c\u9f50\u58f0\u53eb\u9053\uff1a\u201c\u554a\uff0c\u662f\u4f60\uff01\u201d\u4e0d\u7ea6\u800c\u540c\u5730\u5411\u4ed6\u653b\u4e86\u8fc7\u53bb\u3002\u90a3\u4eba\u5750\u5728\u6905\u4e2d\uff0c\u5374\u4e0d\u8d77\u8eab\uff0c\u5de6\u8db3\u7830\u7684\u4e00\u4e0b\u8e22\u51fa\uff0c\u5c06\u5de6\u8fb9\u90a3\u6c49\u5b50\u8e22\u4e86\u4e2a\u7b4b\u6597\uff0c\u53f3\u624b\u4e00\u957f\uff0c\u626d\u4f4f\u53f3\u8fb9\u6c49\u5b50\u7684\u540e\u9886\uff0c\u4e00\u8f6c\u4e00\u7529\uff0c\u5c06\u4ed6\u6454\u4e86\u4e00\u4ea4\u3002\u4ed6\u8eab\u4e0d\u79bb\u6905\uff0c\u968f\u624b\u6253\u5012\u4e24\u4eba\u3002\u4f17\u4eba\u90fd\u662f\u4e00\u60ca\uff1a\u201c\u8fd9\u4eba\u6b66\u529f\u6041\u5730\u4e86\u5f97\uff01\u201d \u3000\u3000\u5b89\u63d0\u7763\u4e0d\u8bc6\u6b64\u4eba\uff0c\u8d70\u4e0a\u4e24\u6b65\uff0c\u95ee\u9053\uff1a\u201c\u9601\u4e0b\u5c0a\u59d3\u5927\u540d\uff1f\u662f\u4f55\u95e8\u4f55\u6d3e\u7684\u638c\u95e8\u4eba\uff1f\u201d \u3000\u3000\u90a3\u4eba\u5c1a\u672a\u56de\u7b54\uff0c\u5730\u4e0b\u6454\u5012\u7684\u4e24\u4e2a\u6c49\u5b50\u5df2\u722c\u8d77\u8eab\u6765\uff0c\u4e00\u4e2a\u54c7\u54c7\u5927\u53eb\uff0c\u4e00\u4e2a\u7834\u53e3\u4e71\u9a82\uff0c\u62a1\u62f3\u53c8\u5411\u4ed6\u6253\u53bb\u3002\u4ece\u4e8c\u4eba\u5927\u53eb\u5927\u56b7\u7684\u8a00\u8bed\u4e2d\u542c\u6765\uff0c\u4f3c\u4e4e\u8fd9\u4eba\u4e00\u8def\u4e0a\u4fae\u5f04\u620f\u800d\uff0c\u4e8c\u4eba\u65e9\u5df2\u5f88\u5403\u4e86\u4ed6\u7684\u82e6\u5934\u3002\u90a3\u4eba\u501f\u529b\u5f15\u529b\uff0c\u5de6\u638c\u5728\u5de6\u8fb9\u6c49\u5b50\u7684\u80cc\u5fc3\u4e0a\u4e00\u63a8\uff0c\u53f3\u8db3\u5f2f\u8f6c\uff0c\u556a\u7684\u4e00\u58f0\uff0c\u5728\u53f3\u8fb9\u6c49\u5b50\u7684\u5c41\u80a1\u4e0a\u8e22\u4e86\u4e00\u811a\u3002\u4e24\u4eba\u8eab\u4e0d\u7531\u4e3b\u7684\u5411\u524d\u4e00\u51b2\u3002\u5e78\u597d\u4e24\u4eba\u53d8\u52bf\u4e5f\u5feb\uff0c\u4e0d\u7b49\u76f8\u4e92\u649e\u5934\uff0c\u56db\u53ea\u624b\u5df2\u4f38\u624b\u626d\u4f4f\uff0c\u53ea\u662f\u53bb\u52bf\u6025\u4e86\uff0c\u7ec8\u4e8e\u7ad9\u4e0d\u4f4f\u811a\uff0c\u4e00\u9f50\u6454\u5012\u3002 \u3000\u3000\u5de6\u8fb9\u90a3\u6c49\u5b50\u53eb\u9053\uff1a\u201c\u9f50\u8001\u4e8c\uff0c\u54b1\u4eec\u81ea\u5df1\u7684\u5e10\u65e5\u540e\u518d\u7b97\uff0c\u4eca\u65e5\u5e76\u80a9\u5b50\u4e0a\uff0c\u5148\u6599\u7406\u4e86\u8fd9\u53ae\u518d\u8bf4\u3002\u201d\u53f3\u8fb9\u7684\u6c49\u5b50\u9053\uff1a\u201c\u4e0d\u9519\uff01\u201d \u3000\u3000\u4e00\u8dc3\u800c\u8d77\uff0c\u4fbf\u4ece\u8170\u95f4\u62bd\u51fa\u4e86\u4e00\u67c4\u5315\u9996\u3002 \u3000\u3000\u80e1\u6590\u542c\u5f97\u90bb\u5ea7\u90a3\u8001\u8005\u81ea\u8a00\u81ea\u8bed\uff1a\u201c\u2018\u9e2d\u5f62\u95e8\u2019\u7684\u7ffb\u6c5f\u51eb\u4e00\u6b7b\uff0c\u4f20\u4e0b\u7684\u4e24\u4e2a\u5f1f\u5b50\u5b9e\u5728\u592a\u4e0d\u6210\u5668\u3002\u201d\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u4e0d\u518d\u5f80\u4e0b\u89e3\u91ca\u3002 \u3000\u3000\u80e1\u6590\u89c1\u4e24\u4e2a\u6c49\u5b50\u8eab\u6cd5\u751a\u662f\u53e4\u602a\uff0c\u597d\u5947\u5fc3\u8d77\uff0c\u8d70\u8fc7\u53bb\u62f1\u4e00\u62f1\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u8bf7\u95ee\u524d\u8f88\uff0c\u8fd9\u4e24\u4f4d\u662f\u2018\u9e2d\u5f62\u95e8\u2019\u7684\u4e48\uff1f\u201d\u90a3\u8001\u8005\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u9601\u4e0b\u9762\u751f\u5f97\u7d27\u554a\u3002\u8bf7\u6559\u5c0a\u59d3\u5927\u540d\uff1f\u201d\u80e1\u6590\u8fd8\u672a\u56de\u7b54\uff0c\u8521\u5a01\u5df2\u7ad9\u8d77\u8eab\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u7ed9\u4e24\u4f4d\u5f15\u89c1\u3002\u8fd9\u662f\u655d\u95e8\u65b0\u4efb\u638c\u95e8\u4eba\u7a0b\u7075\u80e1\u7a0b\u8001\u5e08\uff0c\u8fd9\u4f4d\u662f\u2018\u5148\u5929\u62f3\u2019\u638c\u95e8\u4eba\u90ed\u7389\u5802\u90ed\u8001\u5e08\u3002\u4f60\u4eec\u4e24\u4f4d\u591a\u4eb2\u8fd1\u4eb2\u8fd1\u3002\u201d \u3000\u3000\u90ed\u7389\u5802\u8bc6\u5f97\u8521\u5a01\uff0c\u77e5\u9053\u534e\u62f3\u95e8\u4eba\u624d\u8f88\u51fa\uff0c\u662f\u5317\u65b9\u62f3\u5bb6\u7684\u4e00\u5927\u95e8\u6d3e\uff0c\u4e0d\u7531\u5f97\u5bf9\u80e1\u6590\u8083\u7136\u8d77\u656c\uff0c\u5fd9\u8d77\u7acb\u8ba9\u5ea7\uff0c\u8bf4\u9053\uff1a\u201c\u7a0b\u8001\u5e08\uff0c\u6211\u8fd9\u5e2d\u4e0a\u53ea\u6709\u56db\u4eba\uff0c\u8981\u4e0d\u8981\u5230\u8fd9\u8fb9\u5750\uff1f\u201d\u80e1\u6590\u9053\uff1a\u201c\u751a\u597d\uff01\u201d\u5411\u5927\u5723\u95e8\u7684\u7334\u5f62\u8001\u513f\u544a\u4e86\u7f6a\uff0c\u548c\u7a0b\u7075\u7d20\u3001\u59ec\u6653\u5cf0\u3001\u8521\u5a01\u4e09\u4eba\u5c06\u676f\u7b77\u632a\u5230\u90ed\u7389\u5802\u5e2d\u4e0a\uff0c\u5750\u4e86\u4e0b\u6765\u3002\u201c\u5148\u5929\u62f3\u201d\u4e00\u6d3e\u6765\u5386\u751a\u53e4\uff0c\u521b\u4e8e\u5510\u4ee3\uff0c\u4f46\u5386\u4ee3\u62f3\u5e08\u4f20\u6280\u65f6\u5404\u81ea\u7559\u62db\uff0c\u5343\u4f59\u5e74\u6765\u53c8\u6ca1\u51fa\u4ec0\u4e48\u51fa\u7c7b\u62d4\u8403\u7684\u82f1\u6770\uff0c\u56e0\u4e4b\u5230\u5f97\u6e05\u4ee3\uff0c\u5df2\u8d8b\u5f0f\u5fae\u3002\u90ed\u7389\u5802\u81ea\u77e5\u6b66\u529f\u4e0d\u8db3\u4ee5\u4e0e\u522b\u6d3e\u7684\u540d\u5bb6\u9ad8\u624b\u4e89\u80dc\uff0c\u4e5f\u6ca1\u8d77\u4e89\u593a\u5fa1\u676f\u4e4b\u610f\uff0c\u5fc3\u5b89\u7406\u5f97\u7684\u5750\u5728\u4e00\u65c1\uff0c\u996e\u9152\u89c2\u6597\uff0c\u8fd9\u65f6\u542c\u80e1\u6590\u95ee\u8d77\uff0c\u8bf4\u9053\uff1a\u201c\u2018\u9e2d\u5f62\u62f3\u2019\u7684\u6a21\u6837\u5f88\u4e0d\u4e2d\u77a7\uff0c\u4f46\u9a6c\u6b65\u4f4e\uff0c\u4e0b\u76d8\u7a33\uff0c\u6c34\u9762\u4e0a\u7684\u529f\u592b\u5c24\u5176\u4e86\u5f97\u3002\u5f53\u5e74\u7ffb\u6c5f\u51eb\u5728\u4e16\u4e4b\u65e5\uff0c\u6cb3\u5957\u4e00\u5e26\u662f\u7531\u4ed6\u79f0\u9738\u4e86\u3002\u7ffb\u6c5f\u51eb\u4e00\u6b7b\uff0c\u4f20\u4e0b\u4e86\u4e24\u4e2a\u5f1f\u5b50\uff0c\u8fd9\u62ff\u5315\u9996\u7684\u53eb\u505a\u9f50\u4f2f\u6d9b\uff0c\u90a3\u62ff\u7834\u7532\u9525\u7684\u53eb\u505a\u9648\u9ad8\u6ce2\u3002\u4e24\u4eba\u4e89\u505a\u638c\u95e8\u4eba\u5df2\u4e89\u4e86\u5341\u5e74\uff0c\u8c01\u4e5f\u4e0d\u670d\u8c01\u3002\u8fd9\u6b21\u798f\u5927\u5e05\u8bf7\u5404\u5bb6\u5404\u6d3e\u7684\u638c\u95e8\u4eba\u8d74\u4f1a\uff0c\u563f\uff0c\u597d\u5bb6\u4f19\uff0c\u5e08\u5144\u5f1f\u4fe9\u8001\u4e86\u8138\u76ae\uff0c\u53ef\u4e00\u8d77\u6765\u5566\uff01\u201d \u3000\u3000\u53ea\u89c1\u9f50\u4f2f\u6d9b\u548c\u9648\u9ad8\u6ce2\u5404\u6301\u4e00\u67c4\u77ed\u5175\u5203\uff0c\u5de6\u53f3\u5206\u8fdb\uff0c\u5750\u5728\u6905\u4e2d\u90a3\u4eba\u5374\u4ecd\u4e0d\u7ad9\u8d77\uff0c\u9a82\u9053\uff1a\u201c\u6ca1\u51fa\u606f\u7684\u4e1c\u897f\uff0c\u6211\u5728\u5170\u5dde\u8ddf\u4f60\u4eec\u600e\u4e48\u8bf4\u4e86\uff1f\u53eb\u4f60\u4eec\u522b\u4e0a\u5317\u4eac\uff0c\u5374\u504f\u504f\u8981\u6765\u3002\u201d\u8fd9\u4eba\u5934\u5c16\u8138\u5c0f\uff0c\u62ff\u7740\u4e00\u6839\u5c0f\u5c0f\u65f1\u70df\u7ba1\uff0c\u547c\u565c\u547c\u565c\u7684\u5438\u7740\uff0c\u7559\u7740\u4e24\u6487\u9ec4\u9ec4\u7684\u9f20\u987b\uff0c\u7ea6\u83ab\u4e94\u5341\u6765\u5c81\u5e74\u7eaa\u3002 \u3000\u3000\u5b89\u63d0\u7763\u8fde\u95ee\u4ed6\u59d3\u540d\u95e8\u6d3e\uff0c\u4ed6\u5374\u59cb\u7ec8\u4e0d\u7406\u3002\u80e1\u6590\u89c1\u4ed6\u624b\u811a\u751a\u957f\uff0c\u968f\u968f\u4fbf\u4fbf\u7684\u4e1c\u5288\u4e00\u638c\uff0c\u897f\u8e22\u4e00\u817f\uff0c\u4fbf\u5c06\u9f50\u9648\u4e8c\u4eba\u7684\u62db\u6570\u5316\u89e3\u4e86\u53bb\uff0c\u6b66\u529f\u4f3c\u4e4e\u5e76\u4e0d\u751a\u9ad8\uff0c\u4f46\u62db\u6570\u5374\u6781\u602a\u5f02\uff0c\u95ee\u90ed\u7389\u5802\u9053\uff1a\u201c\u90ed\u8001\u5e08\uff0c\u8fd9\u4f4d\u524d\u8f88\u662f\u8c01\u554a\uff1f\u201d\u90ed\u7389\u5802\u76b1\u7709\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026 \u3000\u3000\u8fd9\u4e2a\u2026\u2026\u201d\u4ed6\u53ef\u4e5f\u4e0d\u8ba4\u8bc6\uff0c\u4e0d\u7531\u5f97\u8138\u4e0a\u6709\u4e9b\u8baa\u8baa\u7684\uff0c\u65c1\u4eba\u4ee5\u6b66\u529f\u89c1\u8d1f\u81ea\u60ed\uff0c\u4ed6\u5374\u4ee5\u8bc6\u4e0d\u51fa\u65c1\u4eba\u7684\u6765\u5386\u4e3a\u7f9e\u3002 \u3000\u3000\u53ea\u542c\u90a3\u5438\u65f1\u70df\u7684\u8001\u8005\u9a82\u9053\uff1a\u201c\u4e0b\u6d41\u80da\u5b50\uff0c\u82e5\u4e0d\u662f\u77a7\u5728\u6211\u90a3\u8fc7\u4e16\u7684\u5144\u5f1f\u7ffb\u6c5f\u51eb\u8138\u4e0a\uff0c\u6211\u624d\u4e0d\u7406\u4f60\u4eec\u7684\u4e8b\u5462\u3002\u7ffb\u6c5f\u51eb\u4e00\u4e16\u82f1\u96c4\uff0c\u6536\u7684\u5f92\u5f1f\u5374\u8d2a\u56fe\u529f\u540d\u5229\u7984\uff0c\u6765\u8d76\u8fd9\u8d9f\u6df7\u6c34\u3002\u4f60\u4eec\u5230\u5e95\u56de\u4e0d\u56de\u53bb\uff1f\u201d\u9648\u9ad8\u6ce2\u633a\u9525\u76f4\u6233\uff0c\u559d\u9053\uff1a\u201c\u6211\u5e08\u7236\u51e0\u65f6\u6709\u4f60\u8fd9\u4e2a\u81ed\u670b\u53cb\u4e86\uff1f\u6211\u5728\u5e08\u7236\u95e8\u4e0b\u4e03\u516b\u5e74\uff0c\u4ece\u6765\u6ca1\u89c1\u8fc7\u4f60\u8fd9\u7cdf\u8001\u5934\u5b50\uff01\u201d \u3000\u3000\u90a3\u8001\u8005\u9a82\u9053\uff1a\u201c\u7ffb\u6c5f\u51eb\u662f\u6211\u5c0f\u65f6\u73a9\u6ce5\u6c99\u3001\u6349\u866b\u8681\u7684\u670b\u53cb\uff0c\u4f60\u8fd9\u5a03\u5a03\u77e5\u9053\u4ec0\u4e48\uff1f\u201d\u7a81\u7136\u5de6\u624b\u4e00\u4f38\uff0c\u556a\u7684\u4e00\u4e0b\uff0c\u6253\u4e86\u4ed6\u4e00\u4e2a\u8033\u62ec\u5b50\u3002\u8fd9\u65f6\u9f50\u4f2f\u6d9b\u5df2\u653b\u5230\u4ed6\u7684\u53f3\u4fa7\uff0c\u90a3\u8001\u8005\u62ac\u817f\u4e00\u8e39\uff0c\u6b63\u597d\u8e39\u4e2d\u4ed6\u7684\u9762\u95e8\uff0c\u559d\u9053\uff1a\u201c\u4f60\u5e08\u7236\u6b7b\u4e86\uff0c\u6211\u6765\u4ee3\u4ed6\u6559\u8bad\u3002\u201d \u3000\u3000\u5927\u5385\u4e0a\u7fa4\u96c4\u89c1\u4e09\u4eba\u6597\u5f97\u6ed1\u7a3d\uff0c\u65e0\u4e0d\u5931\u7b11\u3002\u4f46\u9f50\u4f2f\u6d9b\u548c\u9648\u9ad8\u6ce2\u5f53\u771f\u662f\u5927\u6d51\u4eba\u4e24\u4e2a\uff0c\u8c01\u90fd\u65e9\u77a7\u51fa\u6765\u4ed6\u4eec\u51b3\u4e0d\u662f\u8001\u8005\u7684\u5bf9\u624b\uff0c\u4e8c\u4eba\u5374\u8fd8\u662f\u82e6\u82e6\u7ea0\u7f20\u3002\u90a3\u8001\u8005\u8bf4\u9053\uff1a\u201c\u798f\u5927\u5e05\u53eb\u4f60\u4eec\u6765\uff0c\u96be\u9053\u5f53\u771f\u662f\u5b89\u7740\u597d\u5fc3\u4e48\uff1f\u4ed6\u662f\u8981\u6311\u5f97\u4f60\u4eec\u81ea\u76f8\u6b8b\u6740\uff0c\u4e3a\u4e86\u51e0\u53ea\u559d\u9152\u5acc\u5c0f\u3001\u88c5\u5c3f\u4e0d\u591f\u7684\u676f\u5b50\uff0c\u5927\u5bb6\u62da\u4e2a\u4f60\u6b7b\u6211\u6d3b\uff01\u201d\u8fd9\u53e5\u8bdd\u660e\u7740\u662f\u6559\u8bad\u9f50\u9648\u4e8c\u4eba\uff0c\u4f46\u58f0\u97f3\u54cd\u6717\uff0c\u5927\u5385\u4e0a\u4eba\u4eba\u90fd\u542c\u89c1\u4e86\u3002 \u3000\u3000\u80e1\u6590\u6697\u6697\u70b9\u5934\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u4f4d\u524d\u8f88\u5012\u662f\u9887\u6709\u89c1\u8bc6\uff0c\u4e5f\u4e8f\u5f97\u4ed6\u6709\u8fd9\u526f\u80c6\u5b50\uff0c\u8bf4\u51fa\u8fd9\u51e0\u53e5\u8bdd\u6765\u3002\u201d \u3000\u3000\u679c\u7136\u5b89\u63d0\u7763\u542c\u4e86\u4ed6\u8fd9\u8bdd\uff0c\u518d\u4e5f\u5fcd\u8010\u4e0d\u4f4f\uff0c\u559d\u9053\uff1a\u201c\u4f60\u5230\u5e95\u662f\u8c01\uff1f\u5728\u8fd9\u91cc\u80e1\u8bf4\u516b\u9053\u7684\u6363\u4e71\uff1f\u201d\u603b\u7b97\u4ed6\u8fd8\u788d\u7740\u7fa4\u96c4\u7684\u9762\u5b50\uff0c\u5f53\u4ed6\u662f\u9080\u6765\u7684\u5bbe\u5ba2\uff0c\u5426\u5219\u65e9\u5c31\u4e00\u5df4\u638c\u6253\u8fc7\u53bb\u4e86\u3002 \u3000\u3000\u90a3\u8001\u8005\u54a7\u5634\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u81ea\u7ba1\u6559\u6211\u7684\u4e24\u4e2a\u540e\u8f88\uff0c\u53c8\u788d\u7740\u4f60\u4ec0\u4e48\u4e86\uff1f\u201d\u65f1\u70df\u7ba1\u4f38\u51fa\uff0c\u53ee\u53ee\u4e24\u54cd\uff0c\u5c06\u9f50\u9648\u624b\u4e2d\u7684\u5315\u9996\u548c\u7834\u7532\u9525\u6253\u843d\uff0c\u5c06\u65f1\u70df\u7ba1\u5f80\u8170\u5e26\u4e2d\u4e00\u63d2\uff0c\u53f3\u624b\u626d\u4f4f\u9f50\u4f2f\u6d9b\u7684\u5de6\u8033\uff0c\u5de6\u624b\u626d\u4f4f\u9648\u9ad8\u6ce2\u7684\u53f3\u8033\uff0c\u626c\u957f\u800c\u51fa\u3002\u8bf4\u4e5f\u5947\u602a\uff0c\u4e24\u4eba\u7adf\u662f\u670d\u670d\u5e16\u5e16\u7684\u4e00\u58f0\u4e0d\u4f5c\uff0c\u53ea\u662f\u6b6a\u5634\u95ed\u773c\uff0c\u5fcd\u7740\u75bc\u75db\uff0c\u795e\u60c5\u6781\u662f\u53ef\u7b11\u3002\u539f\u6765\u90a3\u8001\u8005\u4e24\u53ea\u624b\u5927\u62c7\u6307\u548c\u98df\u6307\u626d\u4f4f\u8033\u6735\uff0c\u53e6\u5916\u4e09\u6307\u5374\u5206\u6263\u4e24\u4eba\u8111\u540e\u7684\u201c\u5f3a\u95f4\u201d\u201c\u98ce\u5e9c\u201d\u4e24\u7a74\uff0c\u4ee4\u4ed6\u4eec\u624b\u8db3\u4ff1\u8f6f\uff0c\u53cd\u6297\u4e0d\u5f97\u3002 \u3000\u3000\u80e1\u6590\u5fc3\u9053\uff1a\u201c\u8fd9\u4f4d\u524d\u8f88\u89c1\u4e8b\u660e\u767d\uff0c\u6b66\u529f\u9ad8\u5f3a\uff0c\u4ed6\u65e5\u6c5f\u6e56\u4e0a\u76f8\u9022\uff0c\u5012\u53ef\u548c\u4ed6\u76f8\u4ea4\u3002\u9f50\u9648\u4e8c\u4eba\u82e5\u80fd\u5f97\u4ed6\u8c03\u6559\uff0c\u5c06\u6765\u4e5f\u4e0d\u4f1a\u5982\u6b64\u6ca1\u51fa\u606f\u4e86\u3002\u201d \u3000\u3000\u5b89\u63d0\u7763\u9a82\u9053\uff1a\u201c\u6df7\u5e10\u738b\u516b\u7f94\u5b50\uff0c\u5230\u5927\u5e05\u5e9c\u6765\u80e1\u95f9\uff0c\u5f53\u771f\u662f\u6d3b\u5f97\u4e0d\u8010\u70e6\u4e86\u2026\u2026\u201d\u5ffd\u7136\u6ce2\u7684\u4e00\u58f0\uff0c\u4eba\u4e1b\u4e2d\u98de\u51fa\u4e00\u4e2a\u8089\u4e38\uff0c\u6b63\u597d\u9001\u5728\u4ed6\u7684\u5634\u91cc\u3002\u5b89\u63d0\u7763\u4e00\u60ca\u4e4b\u4e0b\uff0c\u9aa8\u788c\u4e00\u4e0b\uff0c\u541e\u5165\u4e86\u809a\u4e2d\uff0c\u767b\u65f6\u76ee\u77aa\u53e3\u5446\uff0c\u8bf4\u4e0d\u51fa\u8bdd\u6765\uff0c\u867d\u7136\u7259\u9f7f\u95f4\u6cbe\u5230\u4e00\u4e9b\u8089\u5473\uff0c\u5374\u4e0d\u6e05\u695a\u5230\u5e95\u541e\u4e86\u4ec0\u4e48\u602a\u4e1c\u897f\u4e0b\u809a\uff0c\u53c8\u4e0d\u77e5\u8fd9\u7269\u4e8b\u4e4b\u4e2d\u662f\u5426\u6709\u6bd2\uff0c\u81ea\u662f\u66f4\u4e0d\u77e5\u8fd9\u8089\u4e38\u662f\u4f55\u4eba\u6240\u63b7\u4e86\u3002\u8fd9\u4e00\u4e0b\u8c01\u4e5f\u6ca1\u77a7\u660e\u767d\uff0c\u53ea\u89c1\u4ed6\u5f20\u5927\u4e86\u53e3\uff0c\u6ee1\u8138\u60ca\u60f6\u4e4b\u8272\uff0c\u4e00\u53e5\u8bdd\u6ca1\u9a82\u5b8c\uff0c\u5374\u6ca1\u518d\u9a82\u4e0b\u53bb\u3002 \u3000\u3000\u6c64\u6c9b\u5411\u7740\u5b89\u63d0\u7763\u7684\u80cc\u5fc3\uff0c\u6ca1\u89c1\u5230\u4ed6\u53e3\u541e\u8089\u4e38\uff0c\u8bf4\u9053\uff1a\u201c\u6c5f\u6e56\u4e0a\u5c71\u6797\u9690\u9038\u4e4b\u58eb\uff0c\u6240\u5728\u591a\u6709\uff0c\u539f\u4e5f\u4e0d\u8db3\u4e3a\u5947\u3002\u8fd9\u4f4d\u524d\u8f88\u5f88\u6e05\u9ad8\uff0c\u4e0d\u613f\u8ddf\u54b1\u4eec\u4fd7\u4eba\u4e3a\u4f0d\uff0c\u90a3\u4e5f\u7f62\u4e86\u3002\u8fd9\u91cc\u6709\u4e00\u5f20\u6905\u5b50\u7a7a\u7740\uff0c\u5374\u6709\u54ea\u4e00\u4f4d\u8001\u5e08\u4e0a\u6765\u5750\u4e00\u5750\uff1f\u201d \u3000\u3000\u4eba\u4e1b\u4e2d\u4e00\u4eba\u53eb\u9053\uff1a\u201c\u6211\u6765\uff01\u201d\u4f17\u4eba\u53ea\u95fb\u5176\u58f0\uff0c\u4e0d\u89c1\u5176\u4eba\uff0c\u8fc7\u4e86\u597d\u4e00\u4f1a\uff0c\u624d\u89c1\u4eba\u4e1b\u4e2d\u6324\u51fa\u4e00\u4e2a\u77ee\u5b50\u6765\u3002\u53ea\u89c1\u8fd9\u4eba\u4e0d\u8fc7\u4e09\u5c3a\u516d\u4e03\u5bf8\u9ad8\uff0c\u6ee1\u8138\u866c\u9aef\uff0c\u6a21\u6837\u751a\u662f\u51f6\u6a2a\u3002\u6709\u4e9b\u5e74\u8f7b\u6b66\u5e08\u89c1\u4ed6\u77ee\u5f97\u53e4\u602a\uff0c\u4e0d\u7981\u7b11\u51fa\u58f0\u6765\u3002\u90a3\u77ee\u5b50\u56de\u8fc7\u5934\u6765\uff0c\u6012\u76ee\u800c\u89c6\uff0c\u773c\u5149\u70af\u70af\uff0c\u81ea\u6709\u4e00\u80a1\u5a01\u4e25\uff0c\u4f17\u4eba\u7adf\u81ea\u4e0d\u6562\u7b11\u4e86\u3002 \u3000\u3000\u90a3\u77ee\u5b50\u8d70\u5230\u4e8c\u90ce\u62f3\u638c\u95e8\u4eba\u9ec4\u5e0c\u8282\u8eab\u524d\uff0c\u5411\u7740\u4ed6\u4ece\u5934\u81f3\u811a\u7684\u6253\u91cf\u3002\u9ec4\u5e0c\u8282\u5750\u5728\u6905\u4e0a\uff0c\u72b9\u4f3c\u4e00\u5ea7\u94c1\u5854\uff0c\u6bd4\u90a3\u77ee\u5b50\u7ad9\u7740\u8fd8\u9ad8\u51fa\u534a\u4e2a\u5934\u3002\u90a3\u77ee\u5b50\u5bf9\u4ed6\u81ea\u4e0a\u770b\u5230\u4e0b\uff0c\u53c8\u81ea\u4e0b\u770b\u5230\u4e0a\uff0c\u5374\u4e0d\u8bf4\u8bdd\u3002\u9ec4\u5e0c\u8282\u9053\uff1a\u201c\u770b\u4ec0\u4e48\uff1f\u8981\u8ddf\u6211\u8f83\u91cf\u4e00\u4e0b\u4e48\uff01\u201d\u90a3\u77ee\u5b50\u54fc\u4e86\u4e00\u58f0\uff0c\u7ed5\u5230\u6905\u5b50\u80cc\u540e\uff0c\u53c8\u53bb\u6253\u91cf\u4ed6\u7684\u540e\u8111\u3002\u9ec4\u5e0c\u8282\u6050\u4ed6\u5728\u8eab\u540e\u7a81\u65bd\u6697\u7b97\uff0c\u8ddf\u7740\u8f6c\u8fc7\u5934\u53bb\uff0c\u90a3\u77ee\u5b50\u5374\u53c8\u7ed5\u5230\u4ed6\u6b63\u9762\uff0c\u4ecd\u662f\u4fa7\u4e86\u5934\uff0c\u77aa\u773c\u800c\u89c6\u3002\u90a3\u56db\u54c1\u6b66\u5b98\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u8001\u5e08\u662f\u9655\u897f\u5730\u5802\u62f3\u638c\u95e8\u4eba\uff0c\u5b97\u96c4\u5b97\u8001\u5e08\uff01\u201d \u3000\u3000\u9ec4\u5e0c\u8282\u7ed9\u4ed6\u77a7\u5f97\u53d1\u6bdb\uff0c\u970d\u5730\u7ad9\u8d77\u8eab\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u5b97\u8001\u5e08\uff0c\u5728\u4e0b\u9886\u6559\u9886\u6559\u4f60\u7684\u5730\u5802\u62f3\u7edd\u62db\u3002\u201d\u90a3\u77e5\u5b97\u96c4\u53cc\u8db3\u4e00\u767b\uff0c\u5750\u8fdb\u4e86\u4ed6\u8eab\u65c1\u7a7a\u7740\u7684\u6905\u4e2d\u3002\u9ec4\u5e0c\u8282\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u613f\u8ddf\u6211\u8fc7\u62db\uff0c\u90a3\u4e5f\u597d\uff01\u201d\u5750\u56de\u539f\u5ea7\u3002\u5b97\u96c4\u5374\u53c8\u7eb5\u8eab\u79bb\u5ea7\uff0c\u8d70\u5230\u4ed6\u8ddf\u524d\uff0c\u5c06\u4e00\u9897\u51ac\u74dc\u822c\u7684\u8111\u888b\uff0c\u8f6c\u5230\u5de6\u8fb9\uff0c\u53c8\u8f6c\u5230\u53f3\u8fb9\uff0c\u53ea\u662f\u77a7\u4ed6\u3002 \u3000\u3000\u9ec4\u5e0c\u8282\u6012\u559d\u9053\uff1a\u201c\u4f60\u77a7\u4ec0\u4e48\uff1f\u201d\u5b97\u96c4\u9053\uff1a\u201c\u9002\u624d\u996e\u9152\u4e4b\u65f6\uff0c\u4f60\u5e72\u4e48\u77a7\u4e86\u6211\u4e00\u773c\uff0c\u53c8\u7b11\u4e86\u8d77\u6765\uff1f\u4f60\u7b11\u6211\u8eab\u6750\u77ee\u5c0f\uff0c\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u9ec4\u5e0c\u8282\u7b11\u9053\uff1a\u201c\u4f60\u8eab\u6750\u77ee\u5c0f\uff0c\u8ddf\u6211\u6709\u4ec0\u4e48\u76f8\u5e72\uff1f\u201d\u5b97\u96c4\u5927\u6012\uff0c\u559d\u9053\uff1a\u201c\u4f60\u8fd8\u8ba8\u6211\u4fbf\u5b9c\uff01\u201d\u9ec4\u5e0c\u8282\u5947\u9053\uff1a\u201c\u54a6\uff0c\u6211\u600e\u5730\u8ba8\u4f60\u4fbf\u5b9c\u4e86\uff1f\u201d \u3000\u3000\u5b97\u96c4\u9053\uff1a\u201c\u4f60\u8bf4\u6211\u8eab\u6750\u77ee\u5c0f\uff0c\u8ddf\u4f60\u6709\u4ec0\u4e48\u76f8\u5e72\uff1f\u563f\u563f\uff0c\u6211\u751f\u5f97\u77ee\uff0c\u90a3\u53ea\u8ddf\u6211\u8001\u5b50\u76f8\u5e72\uff0c\u4f60\u4e0d\u662f\u6765\u6df7\u5145\u6211\u8001\u5b50\u5417\uff1f\u201d\u6b64\u8a00\u4e00\u51fa\uff0c\u5927\u5385\u4e2d\u767b\u65f6\u54c4\u5802\u5927\u7b11\u3002 \u3000\u3000\u798f\u5eb7\u5b89\u6b63\u559d\u4e86\u4e00\u53e3\u8336\uff0c\u5fcd\u4e0d\u4f4f\u55b7\u4e86\u51fa\u6765\u3002\u7a0b\u7075\u7d20\u4f0f\u5728\u684c\u4e0a\uff0c\u7b11\u5f97\u63c9\u7740\u809a\u5b50\u3002\u80e1\u6590\u5374\u6015\u5927\u7b11\u4e4b\u4e0b\uff0c\u7c98\u7740\u7684\u80e1\u5b50\u843d\u4e86\u4e0b\u6765\uff0c\u53ea\u5f97\u5f3a\u81ea\u5fcd\u4f4f\u3002 \u3000\u3000\u9ec4\u5e0c\u8282\u7b11\u9053\uff1a\u201c\u4e0d\u6562\uff0c\u4e0d\u6562\uff01\u6211\u513f\u5b50\u6bd4\u5b97\u8001\u5e08\u7684\u6a21\u6837\u513f\u4fca\u5f97\u591a\u4e86\u3002\u201d\u5b97\u96c4\u4e00\u8a00\u4e0d\u53d1\uff0c\u547c\u7684\u4e00\u62f3\u4fbf\u5f80\u4ed6\u5c0f\u809a\u4e0a\u51fb\u53bb\u3002\u9ec4\u5e0c\u8282\u65e9\u6709\u63d0\u9632\uff0c\u4ed6\u8eab\u6750\u867d\u5927\uff0c\u884c\u52a8\u5374\u751a\u662f\u654f\u6377\uff0c\u4e00\u8dc3\u800c\u8d77\uff0c\u8df3\u5728\u4e00\u65c1\u3002\u53ea\u542c\u5580\u5587\u4e00\u54cd\uff0c\u5b97\u96c4\u4e00\u62f3\u5df2\u5c06\u4e00\u5f20\u7d2b\u6a80\u6728\u7684\u6905\u5b50\u6253\u5f97\u788e\u88c2\u3002\u8fd9\u4e00\u62f3\u6253\u51fa\uff0c\u5927\u5385\u4e0a\u7b11\u58f0\u7acb\u6b62\uff0c\u4f17\u4eba\u89c1\u4ed6\u867d\u7136\u6a21\u6837\u4e11\u964b\uff0c\u8a00\u8bed\u53ef\u7b11\uff0c\u4f46\u795e\u529b\u60ca\u4eba\uff0c\u5012\u662f\u4e0d\u53ef\u5c0f\u89d1\u4e86\u3002 \u3000\u3000\u5b97\u96c4\u4e00\u62f3\u4e0d\u4e2d\uff0c\u8eab\u5b50\u540e\u4ef0\uff0c\u53cd\u811a\u4fbf\u5411\u9ec4\u5e0c\u8282\u8e22\u53bb\u3002\u9ec4\u5e0c\u8282\u5de6\u811a\u7f29\u8d77\uff0c\u201c\u82f1\u96c4\u72ec\u7acb\u201d\uff0c\u8ddf\u7740\u8fd8\u4e86\u4e00\u62db\u201c\u6253\u516b\u5f0f\u8dfa\u5b50\u811a\u201d\u3002 \u3000\u3000\u5b97\u96c4\u5c31\u5730\u6eda\u5012\uff0c\u4f7f\u4e86\u5730\u5802\u62f3\u51fa\u6765\uff0c\u624b\u8db3\u9f50\u65bd\uff0c\u4e13\u653b\u5bf9\u65b9\u7684\u4e0b\u4e09\u8def\u3002\u9ec4\u5e0c\u8282\u8fde\u4f7f\u201c\u626b\u5802\u817f\u201d\u3001\u201c\u9000\u6b65\u8de8\u864e\u52bf\u201d\u3001\u201c\u8df3\u7bad\u6b65\u201d\u6570\u62db\uff0c\u653b\u5b88\u517c\u5907\u3002\u4f46\u4ed6\u7684\u201c\u4e8c\u90ce\u62f3\u201d\u7684\u957f\u5904\u662f\u5728\u62f3\u638c\u800c\u975e\u817f\u6cd5\uff0c\u82e5\u4e0e\u5e38\u4eba\u640f\u51fb\uff0c\u7ed9\u4ed6\u4f7f\u51fa\u201c\u4e8c\u90ce\u62c5\u5c71\u638c\u201d\u3001\u201c\u76d6\u9a6c\u4e09\u62f3\u201d\u7b49\u7edd\u62db\u6765\uff0c\u51ed\u7740\u4ed6\u62f3\u5feb\u529b\u6c89\uff0c\u539f\u662f\u4e0d\u6613\u62b5\u6321\uff0c\u800c\u4ed6\u6240\u7ec3\u817f\u6cd5\uff0c\u4e5f\u662f\u7a9d\u5fc3\u817f\uff0c\u64a9\u9634\u817f\u7b49\u7528\u4ee5\u8e22\u4eba\u4e0a\u76d8\u4e2d\u76d8\uff0c\u8fd9\u65f6\u9047\u5230\u5b97\u96c4\u5728\u5730\u4e0b\u6eda\u6765\u6eda\u53bb\uff0c\u751f\u5e73\u6240\u7ec3\u7684\u529f\u592b\u5c3d\u6570\u53d8\u4e86\u65e0\u7528\u6b66\u4e4b\u5730\uff0c\u4e0d\u4f46\u62f3\u5934\u6253\u4eba\u4e0d\u7740\uff0c\u8e22\u817f\u4e5f\u65e0\u7528\u6b66\u5904\uff0c\u53ea\u662f\u8df3\u8dc3\u800c\u907f\u3002\u8fc7\u4e0d\u591a\u65f6\uff0c\u819d\u5f2f\u91cc\u5df2\u88ab\u5b97\u96c4\u63a5\u8fde\u8e22\u4e2d\u6570\u817f\uff0c\u53c8\u75db\u53c8\u9178\u4e4b\u9645\uff0c\u5b97\u96c4\u53cc\u817f\u4e00\u7ede\uff0c\u9ec4\u5e0c\u8282\u7ad9\u7acb\u4e0d\u4f4f\uff0c\u6454\u5012\u5728\u5730\u3002 \u3000\u3000\u5b97\u96c4\u7eb5\u8eab\u6251\u4e0a\uff0c\u90a3\u77e5\u9ec4\u5e0c\u8282\u8eab\u5b50\u8dcc\u5012\uff0c\u53cd\u800c\u6709\u65bd\u5c55\u4f59\u5730\uff0c\u4e00\u62f3\u51fb\u51fa\uff0c\u6b63\u4e2d\u5bf9\u65b9\u80a9\u5934\uff0c\u5c06\u5b97\u96c4\u51fb\u51fa\u4e08\u4f59\u3002\u5b97\u96c4\u4e00\u4e2a\u6253\u6eda\uff0c\u53c8\u653b\u4e86\u56de\u6765\u3002\u9ec4\u5e0c\u8282\u8dea\u5728\u5730\u4e0b\uff0c\u77a7\u51c6\u6765\u52bf\uff0c\u5de6\u638c\u53f3\u62f3\uff0c\u540c\u65f6\u51fb\u51fa\uff0c\u5b97\u96c4\u659c\u8eab\u6eda\u5f00\u3002\u4e24\u4eba\u7740\u5730\u800c\u6597\uff0c\u53ea\u542c\u5f97\u7830\u7830\u4e4b\u58f0\u4e0d\u7edd\uff0c\u8eab\u4e0a\u5404\u81ea\u4e0d\u65ad\u4e2d\u62db\u3002\u4f46\u4e24\u4eba\u90fd\u662f\u76ae\u7c97\u8089\u539a\u4e4b\u8f88\uff0c\u5f88\u6328\u5f97\u8d77\u6253\u51fb\uff0c\u4f60\u6253\u6211\u4e00\u62f3\uff0c\u6211\u8fd8\u4f60\u4e00\u811a\uff0c\u4e00\u65f6\u7adf\u5206\u4e0d\u51fa\u80dc\u8d1f\uff0c\u8fd9\u822c\u640f\u51fb\uff0c\u5b97\u96c4\u5df2\u5360\u4e0d\u5230\u4fbf\u5b9c\uff0c\u84e6\u5730\u91cc\u9ec4\u5e0c\u8282\u5356\u4e2a\u7834\u7efd\uff0c\u8ba9\u5b97\u96c4\u6eda\u8fc7\u8eab\u6765\uff0c\u62da\u7740\u80f8\u53e3\u91cd\u91cd\u6328\u4e0a\u4e00\u62f3\uff0c\u53cc\u624b\u9f50\u51fa\uff0c\u6293\u4f4f\u4ed6\u7684\u8116\u5b50\uff0c\u4e00\u7ffb\u8eab\uff0c\u5c06\u4ed6\u538b\u5728\u8eab\u4e0b\uff0c\u53cc\u624b\u4f7f\u529b\u6536\u7d27\u3002\u5b97\u96c4\u4f38\u62f3\u731b\u51fb\u9ec4\u5e0c\u8282\u80c1\u4e0b\uff0c\u4f46\u9ec4\u5e0c\u8282\u597d\u5bb9\u6613\u6293\u4f4f\u654c\u4eba\u8981\u5bb3\uff0c\u5982\u4f55\u80af\u653e\uff1f\u5b97\u96c4\u900f\u4e0d\u8fc7\u6c14\u6765\uff0c\u6ee1\u8138\u80c0\u6210\u7d2b\u9171\uff0c\u51fb\u51fa\u53bb\u7684\u62f3\u5934\u4e5f\u6e10\u6e10\u65e0\u529b\u4e86\u3002 \u3000\u3000\u7fa4\u96c4\u89c1\u4e8c\u4eba\u86ee\u6253\u70c2\u62da\uff0c\u5b9b\u5982\u5e02\u4e95\u4e4b\u5f92\u6253\u67b6\u4e00\u822c\uff0c\u90a3\u8fd8\u6709\u4e1d\u6beb\u638c\u95e8\u4eba\u7684\u8eab\u5206\uff0c\u90fd\u662f\u6447\u5934\u7a83\u7b11\u3002 \u3000\u3000\u773c\u89c1\u5b97\u96c4\u6e10\u6e10\u4e0d\u652f\uff0c\u4eba\u4e1b\u4e2d\u5ffd\u7136\u8df3\u51fa\u4e00\u4e2a\u6c49\u5b50\uff0c\u64c2\u62f3\u5f80\u9ec4\u5e0c\u8282\u80cc\u4e0a\u51fb\u53bb\u3002\u5b89\u63d0\u7763\u559d\u9053\uff1a\u201c\u9000\u4e0b\uff0c\u4e0d\u5f97\u4e24\u4e2a\u6253\u4e00\u4e2a\u3002\u201d\u4f46\u90a3\u4eba\u62f3\u5934\u5df2\u6253\u5230\u4e86\u9ec4\u5e0c\u8282\u80cc\u5fc3\u3002\u9ec4\u5e0c\u8282\u5403\u75db\uff0c\u624b\u4e00\u677e\uff0c\u5b97\u96c4\u7ffb\u8eab\u8df3\u8d77\uff0c\u4eba\u4e1b\u4e2d\u53c8\u6709\u4e00\u4eba\u8df3\u51fa\uff0c\u957f\u81c2\u62a1\u62f3\uff0c\u6ca1\u5934\u6ca1\u8111\u7684\u5411\u90a3\u6c49\u5b50\u6253\u53bb\u3002\u539f\u6765\u8fd9\u4e24\u4eba\u4e00\u4e2a\u662f\u5b97\u96c4\u7684\u5927\u5f1f\u5b50\uff0c\u4e00\u4e2a\u662f\u9ec4\u5e0c\u8282\u7684\u513f\u5b50\uff0c\u5404\u81ea\u51fa\u6765\u52a9\u62f3\uff0c\u5927\u5385\u4e0a\u767b\u65f6\u53d8\u6210\u4e24\u5bf9\u513f\u76f8\u6bb4\u3002 \u3000\u3000\u65c1\u89c2\u4f17\u4eba\u5450\u558a\u52a9\u5a01\uff0c\u62cd\u624b\u53eb\u597d\u3002\u4e00\u573a\u6b66\u6797\u4e2d\u638c\u95e8\u4eba\u7684\u6bd4\u6b66\u8f83\u827a\uff0c\u7adf\u53d8\u6210\u4e86\u800d\u628a\u620f\u4e00\u822c\uff0c\u5e84\u4e25\u4e4b\u610f\uff0c\u8361\u7136\u65e0\u5b58\u3002 \u3000\u3000\u5b97\u96c4\u5403\u4e86\u4e00\u6b21\u4e8f\uff0c\u4e0d\u6562\u518d\u4fa5\u5e78\u6c42\u80dc\uff0c\u5f53\u4e0b\u4e25\u5b88\u95e8\u6237\uff0c\u548c\u9ec4\u5e0c\u8282\u6597\u4e86\u4e2a\u65d7\u9f13\u76f8\u5f53\u3002\u9ec4\u5e0c\u8282\u7684\u513f\u5b50\u4e34\u654c\u7ecf\u9a8c\u4e0d\u8db3\uff0c\u63a5\u8fde\u7ed9\u5bf9\u65b9\u8e22\u4e86\u51e0\u4e2a\u7b4b\u6597\u3002\u4ed6\u4e00\u6012\u4e4b\u4e0b\uff0c\u4ece\u9774\u7b52\u4e2d\u62d4\u51fa\u4e00\u67c4\u77ed\u5200\uff0c\u4fbf\u5411\u654c\u4eba\u5241\u53bb\u3002\u5b97\u96c4\u7684\u5f1f\u5b50\u5403\u4e86\u4e00\u60ca\uff0c\u4ed6\u8eab\u4e0a\u6ca1\u643a\u5175\u5203\uff0c\u62a2\u8fc7\u6c64\u6c9b\u8eab\u65c1\u90a3\u5f20\u7a7a\u7740\u7684\u592a\u5e08\u6905\uff0c\u821e\u52a8\u62db\u67b6\u3002 \u3000\u3000\u8fd9\u573a\u6bd4\u6b66\u8d8a\u6765\u8d8a\u4e0d\u6210\u6a21\u6837\u3002\u5b89\u63d0\u7763\u559d\u9053\uff1a\u201c\u8fd9\u6210\u4ec0\u4e48\u6837\u5b50\uff1f \u3000\u3000\u56db\u4e2a\u4eba\u901a\u7edf\u7ed9\u6211\u9000\u4e0b\u3002\u201d\u4f46\u5b97\u96c4\u7b49\u56db\u4eba\u6253\u5f97\u5174\u8d77\uff0c\u5168\u6ca1\u542c\u89c1\u4ed6\u7684\u8bf4\u8bdd\u3002 \u3000\u3000\u6d77\u5170\u5f3c\u7ad9\u8d77\u8eab\u6765\uff0c\u9053\uff1a\u201c\u63d0\u7763\u5927\u4eba\u7684\u8bdd\uff0c\u4f60\u4eec\u6ca1\u542c\u89c1\u4e48\uff1f\u201d \u3000\u3000\u9ec4\u5e0c\u8282\u7684\u513f\u5b50\u4e00\u5200\u5411\u5bf9\u624b\u5241\u53bb\uff0c\u5374\u5241\u4e86\u4e2a\u7a7a\u3002\u6d77\u5170\u5f3c\u4e00\u4f38\u624b\uff0c\u6293\u4f4f\u4ed6\u7684\u80f8\u53e3\uff0c\u987a\u624b\u5411\u5916\u63b7\u51fa\uff0c\u8ddf\u7740\u56de\u624b\u6293\u4f4f\u5b97\u96c4\u7684\u5f1f\u5b50\uff0c\u4e5f\u63b7\u5230\u4e86\u5929\u4e95\u4e4b\u4e2d\u3002\u4f17\u4eba\u4e00\u5446\u4e4b\u4e0b\uff0c\u4f46\u89c1\u6d77\u5170\u5f3c\u4e00\u624b\u4e00\u4e2a\uff0c\u53c8\u5df2\u6293\u4f4f\u5b97\u96c4\u548c\u9ec4\u5e0c\u8282\uff0c\u540c\u65f6\u63b7\u4e86\u51fa\u53bb\u3002\u56db\u4eba\u8dcc\u6210\u4e00\u56e2\uff0c\u5934\u6655\u8111\u80c0\u4e4b\u4e0b\uff0c\u4e71\u626d\u4e71\u6253\uff0c\u76f4\u5230\u51e0\u540d\u536b\u58eb\u5954\u8fc7\u53bb\u62c6\u5f00\uff0c\u65b9\u624d\u7f62\u624b\uff0c\u4f46\u4eba\u4eba\u5747\u5df2\u76ee\u80bf\u9f3b\u9752\uff0c\u5140\u81ea\u4e92\u76f8\u53eb\u9a82\u4e0d\u4f11\u3002 \u3000\u3000\u6d77\u5170\u5f3c\u8fd9\u4e00\u663e\u8eab\u624b\uff0c\u65c1\u89c2\u7fa4\u96c4\u65e0\u4e0d\u60d5\u7136\u5fc3\u60ca\uff0c\u5747\u60f3\uff1a\u201c\u8fd9\u4eba\u8eab\u5217\u56db\u5927\u638c\u95e8\uff0c\u679c\u7136\u6709\u6781\u9ad8\u7684\u6b66\u529f\uff0c\u8fd9\u4e48\u968f\u624b\u4e00\u6293\u4e00\u63b7\uff0c\u5c31\u5c06\u5b97\u9ec4\u4e8c\u4eba\u5982\u7a3b\u8349\u822c\u629b\u4e86\u51fa\u53bb\u3002\u201d\u8981\u77e5\u5b97\u96c4\u548c\u9ec4\u5e0c\u8282\u867d\u7136\u6597\u5f97\u72fc\u72c8\uff0c\u4f46\u4e24\u4eba\u786e\u6709\u771f\u5b9e\u672c\u9886\uff0c\u5728\u6c5f\u6e56\u4e0a\u4e5f\u90fd\u9887\u6709\u58f0\u671b\uff0c\u5b9e\u975e\u7b49\u95f2\u4e4b\u8f88\u3002 \u3000\u3000\u6d77\u5170\u5f3c\u63b7\u51fa\u56db\u4eba\u540e\uff0c\u56de\u5f52\u5ea7\u4f4d\u3002\u6c64\u6c9b\u8d5e\u9053\uff1a\u201c\u6d77\u5927\u4eba\u597d\u8eab\u624b\uff0c\u4ee4\u4eba\u597d\u751f\u4f69\u670d\u3002\u201d\u6d77\u5170\u5f3c\u7b11\u9053\uff1a\u201c\u53ef\u53eb\u6c64\u5927\u4fa0\u89c1\u7b11\u4e86\uff0c\u8fd9\u51e0\u4e2a\u5bb6\u4f19\u53ef\u5b9e\u5728\u95f9\u5f97\u592a\u4e0d\u6210\u8bdd\u3002\u201d \u3000\u3000\u8fd9\u65f6\u4f8d\u4ec6\u642c\u5f00\u7834\u6905\uff0c\u6362\u4e86\u4e00\u5f20\u592a\u5e08\u6905\u4e0a\u6765\u3002\u201c\u6606\u4ed1\u5200\u201d\u638c\u95e8\u4eba\u897f\u7075\u9053\u4eba\u672c\u6765\u4e00\u76f4\u8138\u542b\u5fae\u7b11\uff0c\u5f85\u89c1\u6d77\u5170\u5f3c\u9732\u4e86\u8fd9\u624b\u529f\u592b\uff0c\u81ea\u89c9\u96be\u4ee5\u548c\u4ed6\u5e76\u5217\uff0c\u5fdd\u5c45\u201c\u7389\u9f99\u516b\u95e8\u201d\u7684\u638c\u95e8\u4eba\u4e4b\u4e00\uff0c\u4e0d\u7531\u5f97\u6709\u4e9b\u5c40\u4fc3\u4e0d\u5b89\u8d77\u6765\u3002\u90a3\u4e00\u65c1\u201c\u9189\u516b\u4ed9\u201d\u638c\u95e8\u4eba\u5343\u676f\u5c45\u58eb\u6587\u9189\u7fc1\uff0c\u5374\u4ecd\u662f\u81ea\u659f\u81ea\u996e\uff0c\u9189\u773c\u6a21\u7cca\uff0c\u5bf9\u773c\u524d\u4e4b\u4e8b\u604d\u82e5\u4e0d\u95fb\u4e0d\u89c1\u3002 \u3000\u3000\u5b89\u63d0\u7763\u8bf4\u9053\uff1a\u201c\u798f\u5927\u5e05\u8bf7\u5404\u4f4d\u6765\u6b64\uff0c\u4e43\u662f\u8f83\u91cf\u6b66\u529f\uff0c\u4ee5\u5b9a\u6280\u827a\u9ad8\u4e0b\uff0c\u53ef\u5343\u4e07\u522b\u50cf\u9002\u624d\u8fd9\u51e0\u4f4d\u8fd9\u822c\u4e71\u6253\u4e00\u6c14\uff0c\u4e0d\u514d\u8d3b\u7b11\u5927\u65b9\u3002\u201d\u53ea\u542c\u5b97\u96c4\u5728\u5eca\u4e0b\u559d\u9053\uff1a\u201c\u4ec0\u4e48\u8d3b\u7b11\u5927\u65b9\uff1f\u8d3b\u54ed\u5c0f\u65b9\uff1f\u4f60\u61c2\u6b66\u529f\u4e0d\u61c2\uff1f\u54b1\u4eec\u6765\u8f83\u91cf\u8f83\u91cf\u3002\u201d\u5b89\u63d0\u7763\u53ea\u4f5c\u6ca1\u542c\u89c1\uff0c\u4e0d\u53bb\u776c\u4ed6\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u91cc\u8fd8\u6709\u4e24\u4e2a\u5ea7\u4f4d\uff0c\u54ea\u4e00\u4f4d\u771f\u82f1\u96c4\u3001\u771f\u597d\u6c49\u4e0a\u6765\u4e58\u5750\uff1f\u201d \u3000\u3000\u5b97\u96c4\u5927\u6012\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u8fd9\u4e48\u8bf4\uff0c\u662f\u9a82\u6211\u4e0d\u662f\u771f\u82f1\u96c4\u4e86\uff1f\u96be\u9053\u6211\u662f\u72d7\u718a\uff1f\u201d\u4ed6\u4e0d\u7406\u4f1a\u9002\u624d\u66fe\u88ab\u6d77\u5170\u5f3c\u63b7\u8dcc\uff0c\u5f53\u5373\u4ece\u5eca\u4e0b\u7eb5\u4e86\u51fa\u6765\uff0c\u5411\u5b89\u63d0\u7763\u5954\u53bb\uff0c\u7a81\u7136\u95f4\u811a\u6b65\u8e09\u8dc4\uff0c\u8dcc\u4e86\u4e2a\u7b4b\u6597\u3002\u539f\u6765\u4e00\u540d\u536b\u58eb\u4f38\u8db3\u4e00\u7eca\uff0c\u6454\u4e86\u4ed6\u4e00\u4ea4\u3002\u5b97\u96c4\u5927\u6012\uff0c\u8f6c\u8fc7\u8eab\u6765\u627e\u5bfb\u6697\u7b97\u4e4b\u4eba\u65f6\uff0c\u90a3\u536b\u58eb\u65e9\u5df2\u8eb2\u5f00\u3002\u5b97\u96c4\u5583\u5583\u5492\u9a82\uff0c\u4e0d\u77e5\u662f\u8c01\u6697\u4e2d\u7eca\u4ed6\u3002 \u3000\u3000\u8fd9\u65f6\u4f17\u4eba\u90fd\u671b\u7740\u4e2d\u95f4\u7684\u4e24\u5f20\u592a\u5e08\u6905\uff0c\u6ca1\u8c01\u518d\u53bb\u7406\u4f1a\u5b97\u96c4\u3002 \u3000\u3000\u539f\u6765\u4e00\u5f20\u7a7a\u6905\u4e0a\u5750\u7740\u4e00\u4e2a\u7a7f\u6708\u767d\u50e7\u888d\u7684\u548c\u5c1a\uff0c\u5531\u540d\u6b66\u5b98\u62a5\u79f0\u662f\u8499\u53e4\u54c8\u8d64\u5927\u5e08\uff0c\u53e6\u4e00\u5f20\u7a7a\u6905\u4e0a\u5374\u6324\u7740\u5750\u4e86\u4e24\u4eba\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u76f8\u8c8c\u4e00\u6a21\u4e00\u6837\uff0c\u5012\u6302\u7709\uff0c\u6597\u9e21\u773c\uff0c\u4e00\u5bf9\u773c\u73e0\u7d27\u9760\u5728\u9f3b\u6881\u4e4b\u65c1\uff0c\u7ea6\u83ab\u56db\u5341\u6765\u5c81\u5e74\u7eaa\uff0c\u670d\u9970\u6253\u626e\u6ca1\u534a\u4e1d\u5206\u522b\uff0c\u663e\u7136\u662f\u4e00\u5bf9\u5b6a\u751f\u5144\u5f1f\u3002\u8fd9\u4e24\u4eba\u5bb9\u8c8c\u4e5f\u6ca1\u4ec0\u4e48\u7279\u5f02\uff0c\u4f46\u8fd9\u53cc\u6597\u9e21\u773c\u5374\u886c\u5f97\u5f62\u76f8\u751a\u662f\u8be1\u5947\u3002\u5531\u540d\u6b66\u5b98\u8bf4\u9053\uff1a\u201c\u8fd9\u4e24\u4f4d\u662f\u8d35\u5dde\u2018\u53cc\u5b50\u95e8\u2019\u7684\u638c\u95e8\u4eba\u502a\u4e0d\u5927\u3001\u502a\u4e0d\u5c0f\u502a\u6c0f\u53cc\u96c4\u3002\u201d \u3000\u3000\u4f17\u4eba\u4e00\u542c\u4ed6\u4fe9\u7684\u540d\u5b57\uff0c\u767b\u65f6\u90fd\u4e50\u4e86\uff0c\u518d\u77a7\u4e8c\u4eba\u7684\u5bb9\u8c8c\u8eab\u5f62\uff0c\u771f\u7684\u518d\u4e5f\u6ca1\u534a\u5206\u5dee\u5f02\uff0c\u4e5f\u4e0d\u77e5\u502a\u4e0d\u5927\u662f\u54e5\u54e5\u5462\uff0c\u8fd8\u662f\u502a\u4e0d\u5c0f\u662f\u54e5\u54e5\u3002\u5982\u679c\u4e00\u4e2a\u53eb\u502a\u5927\uff0c\u4e00\u4e2a\u502a\u5c0f\uff0c\u90a3\u81ea\u662f\u5206\u4e86\u957f\u5e7c\uff0c\u4f46\u201c\u4e0d\u5927\u201d\u4f3c\u4e4e\u662f\u5c0f\uff0c\u201c\u4e0d\u5c0f\u201d\u4f3c\u4e4e\u662f\u5927\uff0c\u5374\u53c8\u672a\u5fc5\u5c3d\u7136\u3002\u53ea\u89c1\u4e24\u4eba\u53cc\u624b\u90fd\u62e2\u5728\u8863\u8896\u4e4b\u4e2d\uff0c\u597d\u50cf\u5929\u6c14\u6781\u51b7\u4e00\u822c\u3002\u4f17\u4eba\u6307\u6307\u70b9\u70b9\u7684\u8bae\u8bba\uff0c\u6709\u7684\u66f4\u6253\u8d77\u8d4c\u6765\uff0c\u6709\u7684\u8bf4\u502a\u4e0d\u5927\u5c45\u957f\uff0c\u6709\u7684\u8bf4\u502a\u4e0d\u5c0f\u4e3a\u5927\uff0c\u4f46\u5230\u5e95\u54ea\u4e00\u4e2a\u662f\u502a\u4e0d\u5927\uff0c\u54ea\u4e00\u4e2a\u662f\u502a\u4e0d\u5c0f\uff0c\u5374\u53c8\u662f\u8c01\u4e5f\u5f04\u4e0d\u6e05\u695a\u3002\u4e24\u5144\u5f1f\u795e\u8272\u6728\u7136\uff0c\u56db\u76ee\u5411\u524d\u76f4\u89c6\uff0c\u4e8c\u4eba\u90fd\u975e\u7626\u524a\uff0c\u4f46\u5e76\u6392\u5750\u5728\u4e00\u5f20\u6905\u4e2d\uff0c\u4e1d\u6beb\u4e0d\u89c1\u6324\u8feb\uff0c\u60f3\u6765\u81ea\u5e7c\u4fbf\u8fd9\u4e48\u5750\u60ef\u4e86\u7684\u3002\u798f\u5eb7\u5b89\u51dd\u76ee\u77a7\u7740\u4e8c\u4eba\uff0c\u8138\u542b\u5fae\u7b11\uff0c\u4e5f\u662f\u5927\u611f\u5174\u5473\u3002 \u3000\u3000\u4f17\u4eba\u6b63\u8bae\u8bba\u95f4\uff0c\u5ffd\u5730\u773c\u524d\u4e00\u4eae\uff0c\u53ea\u89c1\u4eba\u4e1b\u4e2d\u8d70\u51fa\u4e00\u4e2a\u5973\u5b50\u6765\u3002\u8fd9\u5973\u5b50\u8eab\u7a7f\u6de1\u9ec4\u7f57\u886b\uff0c\u4e0b\u8eab\u7cfb\u7740\u8471\u7eff\u88d9\u5b50\uff0c\u4e8c\u5341\u4e00\u4e8c\u5c81\u5e74\u7eaa\uff0c\u80a4\u8272\u767d\u5ae9\uff0c\u9887\u6709\u98ce\u97f5\u3002\u5531\u540d\u6b66\u5b98\u62a5\u9053\uff1a\u201c\u51e4\u9633\u5e9c\u2018\u4e94\u6e56\u95e8\u2019\u7684\u638c\u95e8\u4eba\u6851\u98de\u8679\u59d1\u5a18\u3002\u201d\u4f17\u6b66\u5e08\u7a81\u7136\u89c1\u5230\u4e00\u4e2a\u7f8e\u8c8c\u59d1\u5a18\u51fa\u573a\uff0c\u90fd\u662f\u7cbe\u795e\u4e00\u632f\u3002 \u3000\u3000\u90ed\u7389\u5802\u5bf9\u80e1\u6590\u9053\uff1a\u201c\u4e94\u6e56\u95e8\u7684\u5f1f\u5b50\u90fd\u662f\u505a\u6c5f\u6e56\u5356\u89e3\u7684\u8425\u751f\uff0c\u4e16\u4ee3\u76f8\u4f20\uff0c\u638c\u95e8\u4eba\u4e00\u5b9a\u662f\u5973\u5b50\u3002\u4fbf\u662f\u6709\u6b66\u827a\u6781\u9ad8\u3001\u672c\u9886\u6781\u5927\u7684\u7537\u5f1f\u5b50\uff0c\u4e5f\u4e0d\u80fd\u5f53\u638c\u95e8\u4eba\u3002\u53ea\u662f\u8fd9\u4f4d\u6851\u59d1\u5a18\u5e74\u7eaa\u8fd9\u6837\u8f7b\uff0c\u6050\u6015\u4e0d\u89c1\u5f97\u6709\u4ec0\u4e48\u771f\u5b9e\u529f\u592b\u5427\uff1f\u201d \u3000\u3000\u53ea\u89c1\u6851\u98de\u8679\u8d70\u5230\u502a\u6c0f\u6606\u4ef2\u9762\u524d\uff0c\u53cc\u624b\u53c9\u8170\uff0c\u7b11\u9053\uff1a\u201c\u8bf7\u95ee\u4e24\u4f4d\u502a\u7237\uff0c\u54ea\u4e00\u4f4d\u662f\u8001\u5927\uff1f\u201d\u4e24\u4eba\u6447\u4e86\u6447\u5934\uff0c\u5e76\u4e0d\u56de\u7b54\uff0c\u6851\u98de\u8679\u7b11\u9053\uff1a\u201c\u4fbf\u662f\u53cc\u751f\u5144\u5f1f\uff0c\u4e5f\u6709\u4e2a\u65e9\u751f\u8fdf\u751f\uff0c\u8001\u5927\u8001\u4e8c\u3002\u201d\u502a\u6c0f\u6606\u4ef2\u4ecd\u65e7\u6447\u4e86\u6447\u5934\u3002\u6851\u98de\u8679\u9053\uff1a\u201c\u54a6\uff0c\u8fd9\u53ef\u5947\u5566\uff01\u201d\u6307\u7740\u5de6\u9996\u90a3\u4eba\u9053\uff1a\u201c\u4f60\u662f\u8001\u5927\uff1f\u201d\u90a3\u4eba\u6447\u4e86\u6447\u5934\u3002\u5979\u53c8\u6307\u7740\u53f3\u9996\u90a3\u4eba\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u662f\u8001\u5927\u4e86\uff1f\u201d\u90a3\u4eba\u53c8\u6447\u4e86\u6447\u5934\u3002\u6851\u98de\u8679\u76b1\u7709\u9053\uff1a\u201c\u54b1\u4eec\u6b66\u6797\u4e2d\u4eba\uff0c\u8bb2\u7a76\u8bf4\u8bdd\u4e0d\u6253\u8bf3\u8bed\u3002\u201d\u53f3\u9996\u90a3\u4eba\u9053\uff1a\u201c\u8c01\u6253\u8bf3\u4e86\uff1f\u6211\u4e0d\u662f\u4ed6\u54e5\u54e5\uff0c\u4ed6\u4e5f\u4e0d\u662f\u6211\u54e5\u54e5\u3002\u201d\u6851\u98de\u8679\u9053\uff1a\u201c\u4f60\u4e8c\u4f4d\u53ef\u603b\u662f\u53cc\u751f\u5144\u5f1f\u5427\uff1f\u201d\u4e24\u4eba\u540c\u65f6\u6447\u4e86\u6447\u5934\u3002 \u3000\u3000\u8fd9\u51e0\u4e0b\u6447\u5934\uff0c\u5927\u5385\u4e0a\u767b\u65f6\u7fa4\u60c5\u8038\u52a8\uff0c\u4ed6\u4e8c\u4eba\u76f8\u8c8c\u5982\u6b64\u4f3c\u6cd5\uff0c\u51b3\u4e0d\u80fd\u4e0d\u662f\u53cc\u751f\u5144\u5f1f\u3002 \u3000\u3000\u6851\u98de\u8679\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u8fd9\u8fd8\u4e0d\u662f\u6253\u8bf3\uff1f\u4f60\u4eec\u82e5\u4e0d\u662f\u53cc\u751f\u5144\u5f1f\uff0c\u6740\u4e86\u6211\u5934\u4e5f\u4e0d\u4fe1\u3002\u90a3\u4e48\u8c01\u662f\u502a\u4e0d\u5927\uff1f\u201d\u5de6\u9996\u90a3\u4eba\u9053\uff1a\u201c\u6211\u662f\u502a\u4e0d\u5927\u3002\u201d\u6851\u98de\u8679\u9053\uff1a\u201c\u597d\uff0c\u662f\u4f60\u5148\u51fa\u4e16\u5462\u8fd8\u662f\u4ed6\u5148\u51fa\u4e16\uff1f\u201d \u3000\u3000\u502a\u4e0d\u5927\u76b1\u7709\u9053\uff1a\u201c\u4f60\u8fd9\u4f4d\u59d1\u5a18\u7f20\u5939\u4e0d\u6e05\uff0c\u4f60\u53c8\u4e0d\u662f\u8ddf\u54b1\u5144\u5f1f\u6500\u4eb2\uff0c\u95ee\u8fd9\u4e2a\u5e72\u4e48\uff01\u201d\u6851\u98de\u8679\u8d70\u60ef\u6c5f\u6e56\uff0c\u5bf9\u4ed6\u8fd9\u53e5\u610f\u542b\u8f7b\u8584\u4e4b\u8a00\u4e5f\u4e0d\u5728\u610f\uff0c\u62cd\u624b\u7b11\u9053\uff1a\u201c\u597d\u5566\uff0c\u4f60\u81ea\u5df1\u62db\u8ba4\u662f\u5144\u5f1f\u5566\uff01\u201d\u502a\u4e0d\u5927\u9053\uff1a\u201c\u54b1\u4eec\u662f\u5144\u5f1f\uff0c\u53ef\u4e0d\u662f\u53cc\u751f\u5144\u5f1f\u3002\u201d\u6851\u98de\u8679\u4f38\u98df\u6307\u70b9\u4f4f\u816e\u8fb9\uff0c\u6447\u5934\uff1a\u201c\u6211\u4e0d\u4fe1\u3002\u201d\u502a\u4e0d\u5927\u9053\uff1a\u201c\u4f60\u4e0d\u4fe1\u5c31\u7b97\u4e86\u3002\u8c01\u8981\u4f60\u76f8\u4fe1\uff1f\u201d \u3000\u3000\u6851\u98de\u8679\u751a\u662f\u56fa\u6267\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u662f\u53cc\u751f\u5144\u5f1f\uff0c\u6709\u4ec0\u4e48\u4e0d\u597d\uff1f \u3000\u3000\u4e3a\u4ec0\u4e48\u4e0d\u80af\u76f8\u8ba4\uff1f\u201d\u502a\u4e0d\u5c0f\u9053\uff1a\u201c\u4f60\u4e00\u5b9a\u8981\u77e5\u9053\u5176\u4e2d\u7f18\u7531\uff0c\u8ddf\u4f60\u8bf4\u4e86\uff0c\u90a3\u4e5f\u4e0d\u59a8\u3002\u4f46\u54b1\u5144\u5f1f\u6709\u4e2a\u89c4\u77e9\uff0c\u77e5\u9053\u4e86\u6211\u4eec\u51fa\u8eab\u7684\u79d8\u5bc6\u4e4b\u540e\uff0c\u987b\u5f97\u6328\u54b1\u5144\u5f1f\u4e09\u638c\uff0c\u5018\u82e5\u81ea\u77e5\u6328\u4e0d\u8d77\u7684\uff0c\u4fbf\u5f97\u5411\u54b1\u5144\u5f1f\u78d5\u4e09\u4e2a\u54cd\u5934\u3002\u201d \u3000\u3000\u6851\u98de\u8679\u5b9e\u5728\u597d\u5947\u5fc3\u8d77\uff0c\u6697\u60f3\uff1a\u201c\u4ed6\u4eec\u8981\u6253\u6211\u4e09\u638c\uff0c\u672a\u5fc5\u4fbf\u6253\u5f97\u5230\u4e86\uff0c\u6211\u5148\u542c\u542c\u8fd9\u79d8\u5bc6\u518d\u8bf4\u3002\u201d\u4e8e\u662f\u70b9\u5934\u9053\uff1a\u201c\u597d\uff0c\u4f60\u4eec\u8bf4\u7f62\uff01\u201d \u3000\u3000\u502a\u6c0f\u5144\u5f1f\u5ffd\u5730\u7ad9\u8d77\uff0c\u4e24\u4eba\u8fd9\u4e00\u7ad9\uff0c\u7adf\u65e0\u5206\u6beb\u5148\u540e\u8fdf\u901f\u4e4b\u5dee\uff0c\u771f\u5982\u662f\u4e00\u4e2a\u4eba\u4e00\u822c\u3002\u6851\u98de\u8679\u5f97\u610f\u6d0b\u6d0b\u7684\u9053\uff1a\u201c\u8fd9\u8fd8\u4e0d\u662f\u53cc\u751f\u5144\u5f1f\uff1f\u5f53\u771f\u9a97\u9b3c\u4e5f\u4e0d\u76f8\u4fe1\uff01\u201d\u53ea\u89c1\u4ed6\u4e8c\u4eba\u53cc\u624b\u4f38\u51fa\u8896\u7b52\uff0c\u773c\u524d\u91d1\u5149\u95ea\u4e86\u51e0\u95ea\uff0c\u539f\u6765\u4e8c\u4eba\u5341\u6839\u624b\u6307\u4e0a\u90fd\u5957\u7740\u53c8\u5c16\u53c8\u957f\u7684\u91d1\u5957\uff0c\u82e5\u662f\u5411\u4eba\u6293\u6765\uff0c\u5012\u662f\u4e0d\u6613\u62b5\u6321\u7684\u5229\u5668\u3002\u502a\u6c0f\u5144\u5f1f\u8eab\u5f62\u6643\u52a8\uff0c\u4f38\u51fa\u624b\u6307\uff0c\u4fbf\u5411\u6851\u98de\u8679\u6293\u5230\u3002 \u3000\u3000\u6851\u98de\u8679\u5403\u4e86\u4e00\u60ca\uff0c\u6025\u5fd9\u7eb5\u8eab\u8dc3\u5f00\uff0c\u559d\u9053\uff1a\u201c\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u502a\u4e0d\u5927\u7ad9\u5728\u4e1c\u5357\u89d2\uff0c\u502a\u4e0d\u5c0f\u7ad9\u897f\u5317\u89d2\u4e0a\uff0c\u4e24\u4e2a\u4eba\u624b\u81c2\u4f38\u5f00\uff0c\u6bcf\u6839\u624b\u6307\u4e0a\u52a0\u4e86\u5c16\u5229\u7684\u91d1\u5957\uff0c\u90fd\u6709\u4e03\u516b\u5bf8\u957f\uff0c\u767b\u65f6\u5c06\u6851\u98de\u8679\u56f4\u5728\u4e2d\u95f4\u3002 \u3000\u3000\u5b89\u63d0\u7763\u5fd9\u9053\uff1a\u201c\u4eca\u65e5\u4f1a\u4e2d\u89c4\u77e9\uff0c\u53ea\u80fd\u5355\u6253\u72ec\u6597\uff0c\u4e0d\u8bb8\u501a\u591a\u4e3a\u80dc\u3002\u201d \u3000\u3000\u502a\u4e0d\u5c0f\u90a3\u53cc\u6597\u9e21\u773c\u7684\u4e24\u9897\u773c\u73e0\u672c\u6765\u805a\u5728\u9f3b\u6881\u4e4b\u65c1\uff0c\u5ffd\u7136\u6a2a\u5411\u5de6\u53f3\u4e00\u5206\uff0c\u671d\u5b89\u63d0\u7763\u767d\u4e86\u4e00\u773c\uff0c\u51b7\u51b7\u5730\u9053\uff1a\u201c\u5b89\u5927\u4eba\uff0c\u4f60\u53ef\u77e5\u54b1\u54e5\u513f\u4fe9\u662f\u54ea\u4e00\u95e8\u54ea\u4e00\u6d3e\u554a\uff1f\u201d\u5b89\u63d0\u7763\u9053\uff1a\u201c\u4f60\u4e24\u4f4d\u662f\u8d35\u5dde\u2018\u53cc\u5b50\u95e8\u2019\u5427\uff1f\u201d\u502a\u4e0d\u5927\u7684\u773c\u73e0\u4e5f\u500f\u5730\u5206\u5f00\uff0c\u8bf4\u9053\uff1a\u201c\u54b1\u2018\u53cc\u5b50\u95e8\u2019\u81ea\u6765\u76f8\u4f20\uff0c\u6240\u6536\u7684\u5f1f\u5b50\u4e0d\u662f\u53cc\u751f\u5144\u5f1f\uff0c\u4fbf\u662f\u53cc\u751f\u59ca\u59b9\uff0c\u548c\u4eba\u52a8\u624b\uff0c\u4ece\u6765\u5c31\u6ca1\u5355\u6253\u72ec\u6597\u7684\u3002\u201d \u3000\u3000\u5b89\u63d0\u7763\u5c1a\u672a\u7b54\u8bdd\uff0c\u6851\u98de\u8679\u62a2\u7740\u9053\uff1a\u201c\u7167\u554a\uff0c\u4f60\u4eec\u521a\u624d\u8bf4\u4e0d\u662f\u53cc\u751f\u5144\u5f1f\uff0c\u8fd9\u4f1a\u513f\u81ea\u5df1\u53c8\u627f\u8ba4\u4e86\u3002\u201d\u502a\u4e0d\u5c0f\u9053\uff1a\u201c\u6211\u4eec\u4e0d\u662f\u53cc\u751f\u5144\u5f1f\uff01\u201d \u3000\u3000\u4f17\u4eba\u542c\u4e86\u4ed6\u4e8c\u4eba\u53cd\u53cd\u590d\u590d\u7684\u8bf4\u8bdd\uff0c\u90fd\u89c9\u5f97\u8fd9\u5bf9\u5b9d\u8d1d\u513f\u5144\u5f1f\u6709\u4e9b\u513f\u75f4\u5446\u3002\u6851\u98de\u8679\u683c\u683c\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u548c\u4f60\u4eec\u6b6a\u7f20\u5566\uff0c\u53cd\u6b63\u6211\u53c8\u4e0d\u60f3\u8981\u8fd9\u7389\u9f99\u676f\uff01\u201d\u8bf4\u7740\u4fbf\u8981\u9000\u5f00\u3002\u502a\u4e0d\u5c0f\u53cc\u624b\u4e00\u62e6\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u5df2\u95ee\u8fc7\u6211\u4eec\u7684\u8eab\u4e16\uff0c\u662f\u53d7\u6211\u4eec\u4e09\u638c\u5462\uff0c\u8fd8\u662f\u5411\u54b1\u5144\u5f1f\u78d5\u4e09\u4e2a\u5934\uff1f\u201d\u6851\u98de\u8679\u79c0\u7709\u5fae\u8e59\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u59cb\u7ec8\u8bf4\u4e0d\u660e\u767d\uff0c\u53c8\u8bf4\u662f\u5144\u5f1f\uff0c\u53c8\u8bf4\u4e0d\u662f\u53cc\u751f\u5144\u5f1f\u3002\u5929\u4e0b\u82f1\u96c4\u90fd\u5728\u6b64\uff0c\u5012\u8bf7\u5927\u5bb6\u8bc4\u8bc4\u8fd9\u4e2a\u7406\u770b\u3002\u201d \u3000\u3000\u502a\u4e0d\u5927\u9053\uff1a\u201c\u597d\uff0c\u4f60\u4e00\u5b9a\u8981\u542c\uff0c\u4fbf\u8ddf\u4f60\u8bf4\u4e86\u3002\u201d\u502a\u4e0d\u5c0f\u9053\uff1a\u201c\u6211\u4eec\u4e24\u4e2a\u4e00\u6bcd\u540c\u80de\u3002\u201d\u502a\u4e0d\u5927\u9053\uff1a\u201c\u4e00\u6bcd\u540c\u80de\u5171\u6709\u4e09\u4eba\u3002\u201d\u502a\u4e0d\u5c0f\u9053\uff1a\u201c\u6211\u4e24\u4eba\u662f\u4e09\u80de\u80ce\u4e2d\u7684\u4e24\u4e2a\u3002\u201d\u502a\u4e0d\u5927\u9053\uff1a\u201c\u6240\u4ee5\u8bf4\u867d\u662f\u5144\u5f1f\uff0c\u5374\u4e0d\u662f\u53cc\u751f\u5144\u5f1f\u3002\u201d\u502a\u4e0d\u5c0f\u9053\uff1a\u201c\u5927\u54e5\u54e5\u751f\u4e0b\u5a18\u80ce\u5c31\u4e00\u547d\u545c\u547c\u3002\u201d\u502a\u4e0d\u5927\u9053\uff1a\u201c\u6211\u4eec\u4e8c\u4eba\u540c\u65f6\u751f\u4e0b\uff0c\u4e0d\u5206\u5148\u540e\u3002\u201d\u502a\u4e0d\u5c0f\u9053\uff1a\u201c\u53cc\u5934\u5e76\u80a9\uff0c\u8eab\u5b50\u76f8\u8fde\u3002\u201d\u502a\u4e0d\u5927\u9053\uff1a\u201c\u4e00\u4f4d\u540d\u533b\u5de7\u65bd\u795e\u672f\uff0c\u5c06\u6211\u5144\u5f1f\u4e8c\u4eba\u7528\u5200\u5256\u5f00\u3002\u201d\u502a\u4e0d\u5c0f\u9053\uff1a\u201c\u56e0\u6b64\u4e0a\u6211\u4e8c\u4eba\u5206\u4e0d\u51fa\u8c01\u662f\u54e5\u54e5\uff0c\u8c01\u662f\u5f1f\u5f1f\u3002\u201d\u502a\u4e0d\u5927\u9053\uff1a\u201c\u6211\u65e2\u4e0d\u5927\uff0c\u4ed6\u4e5f\u4e0d\u5c0f\u3002\u201d \u3000\u3000\u4ed6\u4e8c\u4eba\u4f60\u4e00\u53e5\uff0c\u6211\u4e00\u53e5\uff0c\u4e00\u53e3\u6c14\u7684\u8bf4\u5c06\u4e0b\u6765\uff0c\u4e2d\u95f4\u6ca1\u5206\u6beb\u505c\u987f\uff0c\u8bed\u6c14\u8fde\u8d2f\uff0c\u97f3\u8c03\u76f8\u540c\uff0c\u82e5\u6709\u4eba\u5728\u9694\u58c1\u542c\u6765\uff0c\u51b3\u8ba1\u4e0d\u4fe1\u8fd9\u662f\u51fa\u4e8e\u4e8c\u4eba\u4e4b\u53e3\u3002\u5927\u5385\u4e0a\u4f17\u4eba\u53ea\u542c\u5f97\u53c8\u662f\u8be7\u5f02\uff0c\u53c8\u662f\u597d\u7b11\uff0c\u4eba\u4eba\u5747\u60f3\u8fd9\u4e8b\u867d\u7136\u5947\u5999\uff0c\u5374\u4e5f\u5e76\u975e\u4e8b\u7406\u6240\u65e0\uff0c\u4e0d\u7531\u5f97\u5c3d\u7686\u60ca\u53f9\u3002 \u3000\u3000\u6851\u98de\u8679\u7b11\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\uff0c\u8fd9\u79cd\u5929\u4e0b\u5947\u95fb\uff0c\u6211\u4eca\u65e5\u8fd8\u662f\u7b2c\u4e00\u6b21\u542c\u5230\u3002\u201d\u502a\u4e0d\u5c0f\u9053\uff1a\u201c\u4f60\u78d5\u4e0d\u78d5\u5934\uff1f\u201d\u6851\u98de\u8679\u9053\uff1a\u201c\u5934\u662f\u4e0d\u78d5\u7684\u3002\u4f60\u8981\u6253\uff0c\u4fbf\u52a8\u624b\u5427\uff0c\u6211\u53ef\u6ca1\u7b54\u5e94\u4f60\u4e0d\u8fd8\u624b\u3002\u201d \u3000\u3000\u502a\u4e0d\u5927\u3001\u502a\u4e0d\u5c0f\u4e24\u5144\u5f1f\u4e92\u76f8\u5e76\u4e0d\u62db\u547c\uff0c\u7a81\u7136\u95f4\u91d1\u5149\u6643\u52a8\uff0c\u4e8c\u5341\u6839\u5957\u7740\u5c16\u5229\u91d1\u5957\u7684\u624b\u6307\u75be\u6293\u800c\u81f3\u3002\u6851\u98de\u8679\u8eab\u6cd5\u7075\u4fbf\uff0c\u7adf\u4ece\u4e8c\u5341\u6839\u957f\u957f\u7684\u624b\u722a\u4e4b\u95f4\u95ea\u907f\u4e86\u5f00\u53bb\u3002\u502a\u6c0f\u5144\u5f1f\u81ea\u51fa\u5a18\u80ce\u4ee5\u6765\uff0c\u4ece\u672a\u5206\u5f00\u8fc7\u4e00\u4e2a\u65f6\u8fb0\uff0c\u6240\u5b66\u6b66\u529f\u4e5f\u7eaf\u662f\u5206\u8fdb\u5408\u51fb\u4e4b\u672f\uff0c\u4e24\u4e2a\u4eba\u548c\u4e00\u4e2a\u4eba\u7edd\u65e0\u5206\u522b\uff0c\u4fbf\u5982\u662f\u4e00\u4e2a\u56db\u624b\u56db\u8db3\u4e8c\u5341\u6839\u624b\u6307\u7684\u5355\u4eba\u4e00\u822c\uff0c\u4e24\u4eba\u51fa\u624b\u914d\u5408\u5f97\u4e1d\u4e1d\u5165\u6263\uff0c\u502a\u4e0d\u5927\u5de6\u624b\u752b\u4f38\uff0c\u502a\u4e0d\u5c0f\u7684\u53f3\u624b\u5df2\u81ea\u4fa7\u65b9\u5305\u6284\u4e86\u8fc7\u6765\u3002\u6851\u98de\u8679\u8eab\u6cd5\u867d\u662f\u6ed1\u6e9c\u4e4b\u6781\uff0c\u4f46\u5341\u4f59\u62db\u5185\uff0c\u7adf\u662f\u8fd8\u4e0d\u5f97\u4e00\u62db\uff0c\u773c\u89c1\u60c5\u52bf\u751a\u662f\u5371\u6025\uff0c\u8fd9\u5c40\u9762\u65e0\u6cd5\u957f\u4e45\u6491\u6301\uff0c\u53ea\u8981\u7a0d\u6709\u758f\u795e\uff0c\u7ec8\u987b\u4f24\u5728\u4ed6\u4e24\u5144\u5f1f\u7684\u722a\u4e0b\u3002 \u3000\u3000\u5385\u4e0a\u65c1\u89c2\u7684\u7fa4\u96c4\u4e4b\u4e2d\uff0c\u8bb8\u591a\u4eba\u5fcd\u4e0d\u4f4f\u547c\u559d\u8d77\u6765\uff1a\u201c\u4e24\u4e2a\u6253\u4e00\u4e2a\uff0c\u7b97\u662f\u82f1\u96c4\u5462\u8fd8\u662f\u72d7\u718a\uff1f\u201d\u201c\u4e24\u4e2a\u5927\u7537\u4eba\u5408\u6597\u4e00\u4e2a\u5e74\u8f7b\u59d1\u5a18\uff0c\u53ef\u771f\u662f\u8981\u8138\u5f97\u7d27\uff01\u201d\u201c\u4eba\u5bb6\u59d1\u5a18\u662f\u7a7a\u624b\uff0c\u8fd9\u4e24\u4f4d\u7237\u4eec\u624b\u6307\u4e0a\u53ef\u5e26\u7740\u5175\u5203\u5440\uff01\u201d\u201c\u5c0f\u5144\u5f1f\uff0c\u4f60\u4e0a\u53bb\u76f8\u52a9\u4e00\u81c2\u4e4b\u529b\uff0c\u8bf4\u4e0d\u5b9a\u4eba\u5bb6\u5927\u59d1\u5a18\u5bf9\u4f60\u7531\u611f\u751f\u60c5\u5462\uff0c\u54c8\u54c8\uff01\u201d \u3000\u3000\u6b63\u5608\u95f9\u95f4\uff0c\u502a\u4e0d\u5927\u548c\u502a\u4e0d\u5c0f\u7a81\u7136\u540c\u65f6\u201c\u54a6\u201d\u7684\u4e00\u58f0\u547c\u53eb\uff0c\u5e76\u80a9\u8dc3\u5728\u5de6\u9996\uff0c\u51dd\u76ee\u671b\u5411\u798f\u5eb7\u5b89\uff0c\u8138\u4e0a\u5145\u6ee1\u60ca\u559c\u7684\u795e\u8272\u3002\u4f17\u4eba\u4e00\u9f50\u987a\u7740\u4ed6\u4e8c\u4eba\u76ee\u5149\u77a7\u53bb\uff0c\u4f46\u89c1\u798f\u5eb7\u5b89\u7b11\u541f\u541f\u7684\u5750\u5728\u6905\u4e2d\uff0c\u4e00\u624b\u62c9\u7740\u4e00\u4e2a\u5b69\u513f\uff0c\u4f4e\u58f0\u8ddf\u4e24\u4eba\u8bf4\u8bdd\u3002\u8fd9\u4e24\u4e2a\u5b69\u513f\u751f\u5f97\u7389\u96ea\u53ef\u7231\uff0c\u76f8\u8c8c\u5168\u7136\u76f8\u540c\uff0c\u663e\u7136\u4e5f\u662f\u4e00\u5bf9\u53cc\u751f\u5144\u5f1f\uff0c\u4f46\u4e0e\u502a\u4e0d\u5927\u3001\u502a\u4e0d\u5c0f\u5144\u5f1f\u76f8\u6bd4\uff0c\u4e8c\u4fca\u4e8c\u4e11\uff0c\u886c\u6258\u5f97\u52a0\u500d\u5206\u660e\u3002\u4f17\u4eba\u770b\u4e86\uff0c\u53c8\u5747\u662f\u4e00\u4e50\u3002 \u3000\u3000\u80e1\u6590\u548c\u7a0b\u7075\u7d20\u5374\u540c\u65f6\u5fc3\u5934\u5927\u9707\uff0c\u539f\u6765\u8fd9\u4e24\u4e2a\u5b69\u513f\u6b63\u662f\u9a6c\u6625\u82b1\u7684\u513f\u5b50\uff0c\u4e0d\u77e5\u53c8\u5982\u4f55\u7ed9\u798f\u5eb7\u5b89\u593a\u4e86\u56de\u6765\uff1f\u80e1\u7a0b\u4e8c\u4eba\u8ddf\u7740\u4fbf\u60f3\uff1a\u201c\u5b69\u513f\u65e2\u7ed9\u4ed6\u593a\u56de\uff0c\u90a3\u4e48\u6211\u4eec\u7684\u884c\u85cf\u4e5f\u65e9\u4fbf\u7ed9\u4ed6\u8bc6\u7834\u4e86\u3002\u201d\u7a0b\u7075\u7d20\u5411\u80e1\u6590\u4f7f\u4e2a\u773c\u8272\uff0c\u793a\u610f\u987b\u5f53\u53ca\u65e9\u6e9c\u8d70\u3002\u80e1\u6590\u70b9\u4e86\u70b9\u5934\uff0c\u5fc3\u60f3\uff1a\u201c\u5bf9\u65b9\u82e5\u5df2\u8bc6\u7834\uff0c\u81ea\u7136\u6697\u4e2d\u65e9\u6709\u5e03\u7f6e\uff0c\u6b64\u65f6\u5df2\u8d70\u4e0d\u8131\u4e86\u3002\u53ea\u80fd\u968f\u673a\u5e94\u53d8\uff0c\u518d\u4f5c\u9053\u7406\u3002\u201d \u3000\u3000\u502a\u4e0d\u5927\u3001\u502a\u4e0d\u5c0f\u5144\u5f1f\u4ed4\u7ec6\u6253\u91cf\u90a3\u4e24\u4e2a\u5b69\u513f\uff0c\u5982\u75f4\u5982\u72c2\uff0c\u76f4\u662f\u795e\u4e0d\u5b88\u820d\u7684\u6a21\u6837\u3002\u6851\u98de\u8679\u7b11\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u5b69\u513f\u5f88\u597d\uff0c\u4f60\u4eec\u53ef\u8981\u6536\u4ed6\u4eec\u505a\u5f1f\u5b50\u4e48\uff1f\u201d\u8fd9\u4e24\u53e5\u8bdd\uff0c\u6070\u6b63\u8bf4\u4e2d\u4e86\u502a\u6c0f\u5144\u5f1f\u7684\u5fc3\u4e8b\u3002 \u3000\u3000\u8981\u77e5\u6b66\u6797\u4e4b\u4e2d\uff0c\u5f92\u56fa\u62e9\u5e08\uff0c\u5e08\u4ea6\u62e9\u5f92\u3002\u8981\u9047\u4e0a\u4e00\u4f4d\u6b66\u5b66\u6df1\u6e5b\u7684\u660e\u5e08\u56fa\u662f\u4e0d\u6613\uff0c\u4f46\u8981\u6536\u4e00\u4e2a\u806a\u660e\u9896\u609f\u3001\u52e4\u52c9\u597d\u5b66\u7684\u5f92\u5f1f\uff0c\u4e5f\u975e\u6709\u6781\u597d\u7684\u673a\u7f18\u4e0d\u53ef\u3002\u201c\u53cc\u5b50\u95e8\u201d\u7684\u6280\u827a\u6b66\u529f\u5fc5\u987b\u4e24\u4eba\u540c\u7ec3\u540c\u4f7f\uff0c\u867d\u7136\u53ef\u6536\u4e24\u4e2a\u5e74\u9f84\u8eab\u6750\u3001\u6027\u60c5\u8d44\u8d28\u90fd\u5dee\u4e0d\u591a\u7684\u5f92\u513f\u5171\u5b66\uff0c\u4f46\u603b\u662f\u4ee5\u53cc\u751f\u5144\u5f1f\u6700\u4e3a\u4f73\u5999\u3002\u56e0\u53cc\u751f\u5144\u5f1f\u4eba\u4e0d\u4f46\u795e\u667a\u8eab\u4f53\u90fd\u4e00\u6a21\u4e00\u6837\uff0c\u540c\u65f6\u5f80\u5f80\u5fc3\u610f\u9690\u9690\u76f8\u901a\uff0c\u4e34\u654c\u4e4b\u65f6\uff0c\u81ea\u7136\u800c\u7136\u80fd\u53d1\u51fa\u4ee4\u4eba\u51fa\u4e4e\u610f\u6599\u4e4b\u5916\u7684\u5a01\u529b\u3002\u56e0\u6b64\u201c\u53cc\u5b50\u95e8\u201d\u7684\u6b66\u5e08\u8981\u6536\u4e00\u5bf9\u5f97\u610f\u5f1f\u5b50\uff0c\u53ef\u6bd4\u5e38\u4eba\u8981\u96be\u4e0a\u767e\u500d\u3002\u8fd9\u65f6\u502a\u6c0f\u5144\u5f1f\u89c1\u5230\u798f\u5eb7\u5b89\u8fd9\u5bf9\u53cc\u751f\u513f\u5b50\uff0c\u770b\u6765\u8d44\u8d28\u6839\u9aa8\uff0c\u65e0\u4e00\u4e0d\u662f\u4e0a\u4e0a\u4e4b\u9009\uff0c\u5f53\u771f\u662f\u5fc3\u75d2\u96be\u6414\uff0c\u8bf4\u4e0d\u51fa\u7684\u53c8\u662f\u6b22\u559c\uff0c\u53c8\u662f\u96be\u8fc7\u3002 \u3000\u3000\u798f\u5eb7\u5b89\u7b11\u563b\u563b\u7684\u4f4e\u58f0\u9053\uff1a\u201c\u770b\u8fd9\u4e24\u4f4d\u5e08\u7236\uff0c\u4ed6\u4eec\u4e5f\u662f\u53cc\u751f\u7684\u540c\u80de\u5144\u5f1f\u3002\u4ed6\u4e24\u4f4d\u7684\u76f8\u8c8c\uff0c\u4e0d\u662f\u5b8c\u5168\u76f8\u540c\u4e48\uff1f\u4f60\u4eec\u731c\uff0c\u8fd9\u4e8c\u4eba\u4e4b\u4e2d\uff0c\u90a3\u4e00\u4f4d\u662f\u54e5\u54e5\uff1f\u201d\u539f\u6765\u798f\u5eb7\u5b89\u593a\u56de\u8fd9\u5bf9\u5b69\u5b50\u540e\uff0c\u5fc3\u4e0b\u751a\u559c\uff0c\u5ffd\u7136\u89c1\u5230\u502a\u6c0f\u5144\u5f1f\u7684\u6a21\u6837\uff0c\u4e8e\u662f\u53eb\u4e86\u5b69\u5b50\u4fe9\u51fa\u6765\u77a7\u77a7\u3002 \u3000\u3000\u4e24\u4e2a\u5b69\u513f\u51dd\u89c6\u7740\u502a\u6c0f\u5144\u5f1f\uff0c\u4ed6\u4e8c\u4eba\u672c\u8eab\u662f\u53cc\u751f\u5144\u5f1f\uff0c\u53e6\u5177\u4e00\u79cd\u65c1\u4eba\u6240\u65e0\u7684\u7279\u5f02\u611f\u89c9\uff0c\u672c\u6765\u6781\u6613\u5206\u8fa8\u502a\u6c0f\u5144\u5f1f\u8c01\u5927\u8c01\u5c0f\uff0c\u4f46\u8fd9\u4e8c\u4eba\u540c\u65f6\u51fa\u4e16\uff0c\u8fde\u4f53\u800c\u5206\uff0c\u4e24\u4e2a\u5b69\u513f\u5374\u4e5f\u65e0\u6cd5\u8fa8\u522b\u3002 \u3000\u3000\u7fa4\u96c4\u77a7\u77a7\u5927\u7684\u4e00\u5bf9\uff0c\u53c8\u77a7\u77a7\u5c0f\u7684\u4e00\u5bf9\uff0c\u90fd\u662f\u7b11\u563b\u563b\u7684\u4f4e\u58f0\u8c08\u8bba\u3002 \u3000\u3000\u7a81\u7136\u4e4b\u95f4\uff0c\u502a\u6c0f\u5144\u5f1f\u5927\u559d\u4e00\u58f0\uff0c\u731b\u5730\u91cc\u5206\u4ece\u5de6\u53f3\u5411\u798f\u5eb7\u5b89\u8fce\u9762\u6293\u6765\u3002\u798f\u5eb7\u5b89\u5927\u5403\u4e00\u60ca\uff0c\u5c1a\u672a\u60f3\u5230\u95ea\u907f\uff0c\u7ad9\u5728\u8eab\u65c1\u7684\u4e24\u540d\u536b\u58eb\u65e9\u6251\u4e86\u4e0a\u53bb\u8fce\u654c\u3002\u90a3\u77e5\u502a\u6c0f\u5144\u5f1f\u7684\u8eab\u6cd5\u6781\u4e3a\u602a\u5f02\uff0c\u5954\u5230\u4e2d\u9014\uff0c\u539f\u6765\u7ad9\u5728\u5de6\u9996\u7684\u502a\u4e0d\u5927\u8f6c\u800c\u5411\u53f3\uff0c\u53f3\u9996\u7684\u502a\u4e0d\u5c0f\u8f6c\u800c\u5411\u5de6\uff0c\u4ea4\u53c9\u6613\u4f4d\uff0c\u970e\u773c\u95f4\u4fbf\u5c06\u4e24\u540d\u536b\u58eb\u629b\u5728\u8eab\u540e\u3002\u4ed6\u4e8c\u4eba\u88ad\u51fb\u798f\u5eb7\u5b89\u53ea\u662f\u865a\u62db\uff0c\u4e00\u4eba\u4f38\u51fa\u5de6\u811a\uff0c\u4e00\u4eba\u4f38\u51fa\u53f3\u811a\uff0c\u53cc\u8db3\u9f50\u98de\uff0c\u7830\u7684\u4e00\u54cd\uff0c\u8e22\u5728\u798f\u5eb7\u5b89\u5ea7\u6905\u7684\u6905\u811a\u4e0a\uff0c\u5ea7\u6905\u5411\u540e\u4ef0\u8dcc\uff0c\u798f\u5eb7\u5b89\u7684\u8eab\u5b50\u4fbf\u6454\u4e86\u51fa\u53bb\u3002\u4f17\u536b\u58eb\u60ca\u53f1\u4e4b\u4e0b\uff0c\u6709\u7684\u62a2\u4e0a\u62e6\u622a\uff0c\u6709\u7684\u5954\u8fc7\u6765\u6321\u5728\u798f\u5eb7\u5b89\u8eab\u524d\uff0c\u66f4\u6709\u7684\u4f38\u624b\u8fc7\u53bb\u76f8\u6276\u3002\u502a\u6c0f\u5144\u5f1f\u5374\u4e00\u624b\u4e00\u4e2a\uff0c\u5df2\u5c06\u4e24\u4e2a\u5b69\u5b50\u631f\u5728\u80c1\u4e0b\uff0c\u8fd4\u8eab\u8dc3\u51fa\u3002 \u3000\u3000\u5927\u5385\u4e0a\u767b\u65f6\u5927\u4e71\uff0c\u53ea\u542c\u5f97\u7830\u7830\u7830\u7830\uff0c\u554a\u54df\u554a\u54df\u7684\u6570\u58f0\uff0c\u56db\u540d\u62a2\u8fc7\u6765\u62e6\u622a\u7684\u536b\u58eb\u5df2\u88ab\u502a\u6c0f\u5144\u5f1f\u8e22\u7ffb\u3002\u773c\u89c1\u4ed6\u4e8c\u4eba\u631f\u7740\u4e00\u5bf9\u5b69\u513f\u6b63\u8981\u5954\u5230\u5385\u53e3\uff0c\u5ffd\u7136\u95f4\u4eba\u5f71\u4e00\u6643\uff0c\u4e24\u4e2a\u4eba\u5feb\u6b65\u62a2\u5230\uff0c\u4f38\u624b\u88ad\u5411\u4e8c\u4eba\u7684\u540e\u5fc3\u3002 \u3000\u3000\u8fd9\u4e8c\u4eba\u6240\u51fa\u62db\u6570\u8fe5\u4e0d\u76f8\u540c\u3002\u6d77\u5170\u5f3c\u4e00\u624b\u6293\u5411\u502a\u4e0d\u5c0f\u7684\u540e\u9888\uff0c\u53c8\u5feb\u53c8\u51c6\uff0c\u6c64\u6c9b\u5374\u662f\u5411\u502a\u4e0d\u5927\u7684\u540e\u8170\u62cd\u51fa\u4e00\u638c\u7ef5\u638c\u3002\u8fd9\u4e24\u62db\u521a\u67d4\u6709\u522b\uff0c\u5374\u5747\u662f\u5341\u5206\u5389\u5bb3\u7684\u62db\u6570\uff0c\u6b63\u662f\u653b\u654c\u4e4b\u4e0d\u5f97\u4e0d\u6551\u3002\u502a\u6c0f\u5144\u5f1f\u542c\u5f97\u80cc\u540e\u98ce\u58f0\u52b2\u6025\uff0c\u6025\u5fd9\u56de\u638c\u62db\u67b6\uff0c\u556a\u556a\u4e24\u58f0\uff0c\u502a\u4e0d\u5c0f\u8eab\u5b50\u4e00\u6643\uff0c\u502a\u4e0d\u5927\u811a\u4e0b\u4e00\u4e2a\u8e09\u8dc4\uff0c\u5634\u91cc\u55b7\u51fa\u4e00\u53e3\u9c9c\u8840\uff0c\u4e24\u4eba\u540c\u65f6\u653e\u4e0b\u4e86\u624b\u4e2d\u5b69\u513f\u3002 \u3000\u3000\u4fbf\u8fd9\u4e48\u7f13\u5f97\u4e00\u7f13\uff0c\u738b\u5251\u82f1\u548c\u5468\u94c1\u9e6a\u53cc\u53cc\u62a2\u5230\uff0c\u62b1\u8d77\u4e86\u5b69\u513f\u3002\u738b\u5468\u4e8c\u4eba\u7684\u6b66\u529f\u8fdc\u5728\u502a\u6c0f\u5144\u5f1f\u4e4b\u4e0a\uff0c\u8fd9\u5bf9\u5b69\u513f\u4e00\u5165\u4e8c\u4eba\u4e4b\u624b\uff0c\u502a\u6c0f\u5144\u5f1f\u518d\u4e5f\u65e0\u6cd5\u62a2\u5230\u4e86\u3002 \u3000\u3000\u798f\u5eb7\u5b89\u60ca\u9b42\u7565\u5b9a\uff0c\u6012\u559d\uff1a\u201c\u5927\u80c6\u72c2\u5f92\uff0c\u6293\u4e0b\u4e86\u3002\u201d\u6d77\u5170\u5f3c\u548c\u6c64\u6c9b\u62a2\u4e0a\u4e24\u6b65\uff0c\u4e00\u51fa\u64d2\u62ff\u624b\uff0c\u4e00\u4f7f\u9501\u9aa8\u6cd5\uff0c\u5206\u522b\u5c06\u502a\u6c0f\u5144\u5f1f\u6263\u4f4f\u3002\u502a\u6c0f\u5144\u5f1f\u9002\u624d\u8ddf\u4ed6\u4eec\u4e00\u4ea4\u62f3\u638c\uff0c\u5747\u5df2\u53d7\u4e86\u5185\u4f24\uff0c\u6b64\u65f6\u7adf\u662f\u65e0\u6cd5\u6297\u62d2\u3002 \u3000\u3000\u6d77\u6c64\u4e8c\u4eba\u62ff\u4f4f\u502a\u6c0f\u5144\u5f1f\uff0c\u6b63\u8981\u8f6c\u8eab\uff0c\u5ffd\u89c1\u6a90\u5934\u4eba\u5f71\u4e00\u6643\uff0c\u98d8\u4e0b\u4e24\u4e2a\u4eba\u6765\u3002\u5927\u5385\u4e2d\u8721\u70db\u70b9\u5f97\u660e\u6643\u6643\u5730\uff0c\u65e0\u5f02\u767d\u663c\uff0c\u4f46\u4f17\u4eba\u4e00\u89c1\u8fd9\u4e24\u4eba\uff0c\u65e0\u4e0d\u80cc\u4e0a\u611f\u5230\u4e00\u9635\u5bd2\u610f\uff0c\u5b9b\u4f3c\u9ed1\u591c\u72ec\u884c\uff0c\u5728\u6df1\u5c71\u591c\u5893\u4e4b\u4e2d\u649e\u5230\u4e86\u6d3b\u9b3c\u4e00\u822c\u3002 \u3000\u3000\u8fd9\u4e8c\u4eba\u8eab\u6750\u6781\u7626\u6781\u9ad8\uff0c\u53cc\u7709\u659c\u659c\u5782\u4e0b\uff0c\u8138\u988a\u53c8\u7626\u53c8\u957f\uff0c\u6b63\u4f3c\u4f20\u8bf4\u4e2d\u52fe\u9b42\u62d8\u9b44\u7684\u65e0\u5e38\u9b3c\u4e00\u822c\uff0c\u8bf4\u4e5f\u5947\u602a\uff0c\u4e8c\u4eba\u76f8\u8c8c\u4e5f\u662f\u4e00\u6a21\u4e00\u6837\uff0c\u7adf\u7136\u53c8\u51fa\u73b0\u4e86\u4e00\u5bf9\u53cc\u751f\u5144\u5f1f\u3002 \u3000\u3000\u4ed6\u4e8c\u4eba\u8eab\u6cd5\u5982\u7535\uff0c\u4e00\u4e2a\u51fa\u638c\u51fb\u5411\u6d77\u5170\u5f3c\uff0c\u53e6\u4e00\u4e2a\u51fb\u5411\u6c64\u6c9b\u3002\u6d77\u6c64\u4e8c\u4eba\u5404\u81ea\u51fa\u638c\u76f8\u8fce\u3002\u4f46\u542c\u5f97\u6ce2\u6ce2\u4e24\u58f0\u8f7b\u54cd\u8fc7\u53bb\uff0c\u6d77\u5170\u5f3c\u5168\u8eab\u9aa8\u8282\u683c\u683c\u4e71\u54cd\uff0c\u6c64\u6c9b\u5374\u6643\u4e86\u51e0\u6643\u3002 \u3000\u3000\u7fa4\u96c4\u6b63\u81ea\u4e07\u5206\u9519\u6115\uff0c\u4e00\u76f4\u7a33\u5750\u592a\u5e08\u6905\u4e2d\u7684\u201c\u9189\u516b\u4ed9\u201d\u638c\u95e8\u4eba\u6587\u9189\u7fc1\u731b\u5730\u4e00\u8dc3\u800c\u8d77\uff0c\u5c16\u58f0\u60ca\u53eb\uff1a\u201c\u9ed1\u65e0\u5e38\uff0c\u767d\u65e0\u5e38\uff01\u201d \u3000\u3000\u90a3\u53cc\u7626\u5b50\u624b\u638c\u548c\u6d77\u6c64\u4e8c\u4eba\u76f8\u63a5\uff0c\u76ee\u5149\u5982\u7535\uff0c\u5c04\u5230\u6587\u9189\u7fc1\u8138\u4e0a\uff0c\u5de6\u9996\u4e00\u4eba\u51b7\u51b7\u5730\u9053\uff1a\u201c\u4f60\u4f5c\u6076\u591a\u7aef\uff0c\u4eca\u65e5\u8fd8\u60f3\u9003\u547d\u4e48\uff1f\u201d \u3000\u3000\u731b\u5730\u91cc\u4e24\u4eba\u638c\u529b\u5411\u5916\u4e00\u5410\uff0c\u6d77\u6c64\u4e8c\u4eba\u5404\u9000\u4e00\u6b65\uff0c\u8fd9\u5bf9\u7626\u5b50\u5df2\u62a2\u8d77\u502a\u6c0f\u5144\u5f1f\u3002\u53f3\u9996\u90a3\u4eba\u8bf4\u9053\uff1a\u201c\u8fd9\u4e8c\u4eba\u8ddf\u54b1\u5144\u5f1f\u65e0\u4eb2\u65e0\u6545\uff0c\u77a7\u5728\u5927\u5bb6\u90fd\u662f\u53cc\u751f\u5144\u5f1f\u4efd\u4e0a\uff0c\u6551\u4e86\u4ed6\u4eec\u6027\u547d\u3002\u201d\u5de6\u9996\u90a3\u4eba\u62b1\u62f3\u56e2\u56e2\u4e00\u62f1\u624b\uff0c\u6717\u58f0\u9053\uff1a\u201c\u7ea2\u82b1\u4f1a\u5e38\u8d6b\u5fd7\u3001\u5e38\u4f2f\u5fd7\u5144\u5f1f\uff0c\u5411\u5929\u4e0b\u82f1\u96c4\u95ee\u597d\uff01\u201d \u3000\u3000\u6d77\u5170\u5f3c\u548c\u6c64\u6c9b\u8ddf\u4e8c\u4eba\u5bf9\u4e86\u4e00\u638c\uff0c\u5747\u611f\u80f8\u53e3\u6c14\u8840\u7ffb\u6d8c\uff0c\u5fc3\u4e0b\u6697\u6697\u9a87\u5f02\uff0c\u5fae\u4e00\u8c03\u606f\uff0c\u6b63\u6b32\u4e0a\u524d\u518d\u6218\uff0c\u5ffd\u542c\u5230\u201c\u5e38\u8d6b\u5fd7\u3001\u5e38\u4f2f\u5fd7\u201d\u4e24\u4eba\u7684\u59d3\u540d\uff0c\u90fd\u4e0d\u7981\u201c\u54a6\u201d\u7684\u4e00\u58f0\uff0c\u505c\u4e86\u811a\u6b65\u3002 \u3000\u3000\u5e38\u6c0f\u5144\u5f1f\u5934\u4e00\u70b9\uff0c\u6293\u8d77\u502a\u6c0f\u5144\u5f1f\uff0c\u4e0a\u4e86\u5c4b\u6a90\uff0c\u4f46\u542c\u5f97\u201c\u554a\u54df\uff01\u201d\u201c\u54fc\uff01\u201d\u201c\u54ce\uff01\u201d\u4e4b\u58f0\uff0c\u4e00\u8def\u54cd\u5c06\u8fc7\u53bb\uff0c\u7ec8\u4e8e\u6e10\u53bb\u6e10\u8fdc\uff0c\u9690\u6ca1\u65e0\u58f0\uff0c\u90a3\u81ea\u662f\u5b88\u5728\u5c4b\u9876\u7684\u4f17\u536b\u58eb\u4e00\u8def\u4e0a\u7ed9\u4ed6\u5144\u5f1f\u9a71\u9000\uff0c\u6216\u662f\u6454\u4e0b\u5c4b\u6765\u3002 \u3000\u3000\u6d77\u5170\u5f3c\u548c\u6c64\u6c9b\u90fd\u89c9\u624b\u638c\u4e0a\u6709\u9ebb\u8fa3\u8fa3\u4e4b\u611f\uff0c\u63d0\u8d77\u4e00\u770b\uff0c\u5fcd\u4e0d\u4f4f\u53c8\u90fd\u201c\u554a\u201d\u7684\u4e00\u58f0\uff0c\u4f4e\u4f4e\u60ca\u547c\u3002\u539f\u6765\u4e24\u4eba\u624b\u638c\u5747\u5df2\u7d2b\u9ed1\uff0c\u8fd9\u624d\u60f3\u8d77\u897f\u5ddd\u53cc\u4fa0\u201c\u9ed1\u65e0\u5e38\u3001\u767d\u65e0\u5e38\u201d\u5e38\u6c0f\u5144\u5f1f\u7684\u9ed1\u6c99\u638c\u5929\u4e0b\u9a70\u540d\uff0c\u95fb\u540d\u5df2\u4e45\uff0c\u4eca\u65e5\u4e00\u4f1a\uff0c\u679c\u7136\u662f\u975e\u540c\u5c0f\u53ef\u3002 \u3000\u3000\u798f\u5eb7\u5b89\u53ec\u5f00\u8fd9\u6b21\u5929\u4e0b\u638c\u95e8\u4eba\u5927\u4f1a\uff0c\u7528\u610f\u4e4b\u4e00\uff0c\u672c\u662f\u5728\u5bf9\u4ed8\u7ea2\u82b1\u4f1a\u7fa4\u96c4\uff0c\u5c82\u77e5\u4f17\u76ee\u777d\u777d\u4e4b\u4e0b\uff0c\u5e38\u6c0f\u5144\u5f1f\u500f\u6765\u500f\u53bb\uff0c\u7adf\u662f\u5982\u5165\u65e0\u4eba\u4e4b\u5883\u3002\u4ed6\u5fc3\u4e0b\u6781\u662f\u607c\u6012\uff0c\u6c89\u7740\u8138\u4e00\u8a00\u4e0d\u53d1\uff0c\u76ee\u5149\u5411\u5c45\u4e2d\u7684\u51e0\u53ea\u592a\u5e08\u6905\u4e00\u77a5\uff0c\u53ea\u89c1\u5c11\u6797\u5bfa\u7684\u5927\u667a\u7985\u5e08\u5782\u7709\u4f4e\u76ee\uff0c\u4e0d\u6539\u5e73\u65f6\u795e\u6001\uff1b\u6b66\u5f53\u6d3e\u7684\u65e0\u9752\u5b50\u8138\u5e26\u60f6\u60d1\uff0c\u4f3c\u6709\u60e7\u8272\u3002\u90a3\u6587\u9189\u7fc1\u76f4\u633a\u633a\u7684\u7ad9\u7740\uff0c\u4e00\u52a8\u4e5f\u4e0d\u52a8\uff0c\u53cc\u76ee\u5411\u524d\u77aa\u89c6\uff0c\u5e38\u6c0f\u5144\u5f1f\u65e9\u5df2\u53bb\u8fdc\uff0c\u4ed6\u5140\u81ea\u5413\u5f97\u9b42\u4e0d\u9644\u4f53\u3002 \u3000\u3000\u8fd9\u4e00\u5e55\u80e1\u6590\u77a7\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u4ed6\u542c\u5230\u201c\u7ea2\u82b1\u4f1a\u201d\u4e09\u5b57\uff0c\u5df2\u662f\u5fc3\u4e2d\u6026\u6026\u800c\u8df3\uff0c\u5f85\u89c1\u5e38\u6c0f\u5144\u5f1f\u8bf4\u6765\u4fbf\u6765\uff0c\u8bf4\u53bb\u4fbf\u53bb\uff0c\u5c06\u6ee1\u5385\u6b66\u5e08\u89c6\u5982\u65e0\u7269\uff0c\u66f4\u662f\u5fc3\u795e\u4ff1\u9189\uff0c\u5fc3\u4e2d\u53ea\u662f\u60f3\u7740\u4e00\u4e2a\u5ff5\u5934\uff1a \u3000\u3000\u201c\u8fd9\u624d\u662f\u82f1\u96c4\u8c6a\u6770\uff01\u201d \u3000\u3000\u6851\u98de\u8679\u4e00\u76f4\u5728\u65c1\u77a7\u7740\u70ed\u95f9\uff0c\u89c1\u4e86\u8fd9\u5f53\u65e5\u6587\u9189\u7fc1\u8fd8\u662f\u5413\u6210\u8fd9\u4e2a\u6a21\u6837\uff0c\u5979\u5c11\u5e74\u597d\u4e8b\uff0c\u4f38\u624b\u5728\u4ed6\u81c2\u4e0a\u8f7b\u8f7b\u4e00\u63a8\uff0c\u7b11\u9053\uff1a\u201c\u5750\u4e0b\u5427\uff0c\u4e00\u5bf9\u65e0\u5e38\u9b3c\u65e9\u53bb\u5566\uff01\u201d\u90a3\u77e5\u5979\u8fd9\u4e48\u4e00\u63a8\uff0c\u6587\u9189\u7fc1\u5e94\u624b\u800c\u5012\uff0c\u518d\u4e0d\u8d77\u6765\u3002\u6851\u98de\u8679\u5927\u5403\u4e00\u60ca\uff0c\u4fef\u8eab\u4e00\u770b\uff0c\u4f46\u89c1\u4ed6\u6ee1\u8138\u9752\u7d2b\u4e4b\u8272\uff0c\u65e9\u5df2\u80c6\u88c2\u800c\u6b7b\uff0c\u5fd9\u53eb\u9053\uff1a\u201c\u6b7b\u5566\uff0c\u6b7b\u5566\uff0c\u8fd9\u4eba\u5413\u6b7b\u5566\uff01\u201d \u3000\u3000\u5927\u5385\u4e0a\u7fa4\u96c4\u4e00\u9635\u9a9a\u52a8\uff0c\u8fd9\u6587\u9189\u7fc1\u5148\u524d\u5750\u5728\u592a\u5e08\u6905\u4e2d\u81ea\u659f\u81ea\u996e\uff0c\u5c06\u8c01\u90fd\u4e0d\u77a7\u5728\u773c\u91cc\uff0c\u5927\u6709\u201c\u8001\u5b50\u5929\u4e0b\u7b2c\u4e00\u201d\u4e4b\u6982\uff0c\u60f3\u4e0d\u5230\u5e38\u6c0f\u5144\u5f1f\u4e00\u5230\uff0c\u53ea\u4e00\u53e5\u8bdd\uff0c\u7adf\u5c14\u6d3b\u751f\u751f\u7684\u5c06\u4ed6\u5413\u6b7b\u3002 \u3000\u3000\u90ed\u7389\u5802\u53f9\u9053\uff1a\u201c\u6b7b\u6709\u4f59\u8f9c\uff0c\u6b7b\u6709\u4f59\u8f9c\uff01\u201d\u80e1\u6590\u9053\uff1a\u201c\u90ed\u524d\u8f88\uff0c\u8fd9\u59d3\u6587\u7684\u751f\u5e73\u54c1\u884c\u4e0d\u4f73\u4e48\uff1f\u201d\u90ed\u7389\u5802\u6447\u5934\u9053\uff1a\u201c\u5c82\u5355\u662f\u54c1\u884c\u4e0d\u4f73\u800c\u5df2\uff0c\u5978\u6deb\u63b3\u63a0\uff0c\u65e0\u6240\u4e0d\u4e3a\u3002\u6211\u672c\u4e0d\u8be5\u8bf4\u6b7b\u4eba\u7684\u574f\u8bdd\uff0c\u4f46\u4e8b\u5b9e\u4ff1\u5728\uff0c\u4e5f\u4e0d\u5fc5\u8bb3\u8a00\u3002\u6211\u65e9\u6599\u5230\u4ed6\u51b3\u8ba1\u4e0d\u5f97\u5584\u7ec8\uff0c\u53ea\u662f\u7adf\u4f1a\u7ed9\u9ed1\u767d\u65e0\u5e38\u4e00\u4e0b\u5b50\u5413\u6b7b\uff0c\u53ef\u8c01\u4e5f\u610f\u60f3\u4e0d\u5230\u3002\u201d\u53e6\u4e00\u4eba\u63d2\u53e3\u9053\uff1a\u201c\u60f3\u662f\u5e38\u6c0f\u5144\u5f1f\u66fe\u5bfb\u4ed6\u591a\u65f6\uff0c\u4eca\u65e5\u51a4\u5bb6\u72ed\u8def\uff0c\u91cd\u53c8\u649e\u89c1\u3002\u201d\u90ed\u7389\u5802\u9053\uff1a\u201c\u4ee5\u524d\u8fd9\u59d3\u6587\u7684\u4e00\u5b9a\u66fe\u7ed9\u5e38\u6c0f\u5144\u5f1f\u902e\u4f4f\u8fc7\uff0c\u8bf4\u4e0d\u5b9a\u8fd8\u53d1\u4e0b\u8fc7\u4ec0\u4e48\u91cd\u8a93\u3002\u201d\u90a3\u4eba\u6447\u5934\u9053\uff1a\u201c\u81ea\u4f5c\u5b7d\uff0c\u4e0d\u53ef\u6d3b\u3002\u201d\u90ed\u7389\u5802\u9053\uff1a\u201c\u8fd9\u53eb\u4f5c\u662f\u975e\u53ea\u4e3a\u591a\u5f00\u53e3\uff0c\u70e6\u607c\u7686\u56e0\u5f3a\u51fa\u5934\u3002\u4ed6\u82e5\u662f\u7a0d\u6709\u81ea\u77e5\u4e4b\u660e\uff0c\u4e0d\u53bb\u60f3\u5f97\u4ec0\u4e48\u7389\u9f99\u5fa1\u676f\uff0c\u8eb2\u5728\u4eba\u7fa4\u4e4b\u4e2d\uff0c\u897f\u5ddd\u53cc\u4fa0\u4e5f\u4e0d\u4f1a\u89c1\u5230\u4ed6\u554a\u3002\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u9645\uff0c\u4eba\u4e1b\u4e2d\u8d70\u51fa\u4e00\u4e2a\u8001\u8005\u6765\uff0c\u8170\u95f4\u63d2\u7740\u4e00\u6839\u9ed1\u9edd\u9edd\u7684\u5927\u70df\u888b\uff0c\u8d70\u5230\u6587\u9189\u7fc1\u5c38\u8eab\u4e4b\u65c1\uff0c\u54ed\u9053\uff1a\u201c\u6587\u4e8c\u5f1f\uff0c\u60f3\u4e0d\u5230\u4f60\u4eca\u65e5\u547d\u4e27\u9f20\u8f88\u4e4b\u624b\u3002\u201d \u3000\u3000\u80e1\u6590\u542c\u5f97\u4ed6\u9a82\u201c\u897f\u5ddd\u53cc\u4fa0\u201d\u4e3a\u9f20\u8f88\uff0c\u5fc3\u4e0b\u5927\u6012\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u90ed\u524d\u8f88\uff0c\u8fd9\u8001\u513f\u662f\u8c01\uff1f\u201d\u90ed\u7389\u5802\u9053\uff1a\u201c\u8fd9\u662f\u5f00\u5c01\u5e9c\u2018\u7384\u6307\u95e8\u2019\u7684\u638c\u95e8\u4eba\uff0c\u590d\u59d3\u4e0a\u5b98\uff0c\u53eb\u4f5c\u4e0a\u5b98\u94c1\u751f\uff0c\u81ea\u5df1\u5c01\u4e86\u4e2a\u5916\u53f7\uff0c\u53eb\u4ec0\u4e48\u2018\u70df\u971e\u6563\u4eba\u2019\u3002\u4ed6\u548c\u6587\u9189\u7fc1\u4e00\u9f3b\u5b54\u51fa\u6c14\uff0c\u81ea\u79f0\u2018\u70df\u9152\u4e8c\u4ed9\u2019\uff01\u201d\u80e1\u6590\u89c1\u4ed6\u4e00\u4ef6\u5927\u8902\u4e0a\u5149\u6ed1\u6676\u4eae\uff0c\u6ee1\u662f\u70df\u6cb9\uff0c\u8170\u95f4\u7684\u70df\u7b52\u751a\u662f\u5947\u7279\uff0c\u88c5\u70df\u7684\u7a9d\u513f\u51e0\u4e4e\u6709\u62f3\u5934\u5927\u5c0f\uff0c\u60f3\u662f\u4ed6\u70df\u763e\u5947\u91cd\uff0c\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u8fd9\u79cd\u70df\u9b3c\uff0c\u8fd8\u79f0\u5f97\u4e0a\u662f\u4e2a\u2018\u4ed9\u2019\u5b57\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u94c1\u751f\u62b1\u7740\u6587\u9189\u7fc1\u7684\u5c38\u8eab\u5e72\u53f7\u4e86\u51e0\u58f0\uff0c\u7ad9\u8d77\u8eab\u6765\uff0c\u77aa\u7740\u6851\u98de\u8679\u6012\u9053\uff1a\u201c\u4f60\u5e72\u4e48\u6bdb\u624b\u6bdb\u811a\uff0c\u5c06\u6211\u6587\u4e8c\u5f1f\u63a8\u6b7b\u4e86\uff1f\u201d\u6851\u98de\u8679\u5927\u51fa\u610f\u5916\uff0c\u9053\uff1a\u201c\u4ed6\u660e\u660e\u662f\u5413\u6b7b\u7684\uff0c\u600e\u5730\u662f\u6211\u63a8\u6b7b\u7684\uff1f\u201d\u4e0a\u5b98\u94c1\u751f\u9053\uff1a\u201c\u563f\u563f\uff0c\u597d\u7aef\u7aef\u4e00\u4e2a\u4eba\uff0c\u600e\u4e48\u4f1a\u5413\u6b7b\uff1f\u5b9a\u662f\u4f60\u6697\u4e0b\u9634\u6bd2\u624b\u6bb5\uff0c\u5bb3\u4e86\u6211\u6587\u4e8c\u5f1f\u6027\u547d\u3002\u201d \u3000\u3000\u539f\u6765\u4ed6\u89c1\u6587\u9189\u7fc1\u4e00\u5413\u800c\u6b7b\uff0c\u6c5f\u6e56\u4e0a\u4f20\u626c\u5f00\u6765\uff0c\u58f0\u540d\u5927\u662f\u4e0d\u597d\uff0c\u201c\u9189\u516b\u4ed9\u201d\u8fd9\u4e00\u95e8\uff0c\u53ea\u6015\u4ece\u6b64\u518d\u65e0\u62ac\u5934\u4e4b\u65e5\uff0c\u56e0\u6b64\u786c\u6d3e\u662f\u6851\u98de\u8679\u6697\u4e0b\u6bd2\u624b\u3002\u987b\u77e5\u6b66\u6797\u4eba\u7269\u88ab\u4eba\u5bb3\u6b7b\uff0c\u90a3\u662f\u5bfb\u5e38\u4e4b\u4e8b\uff0c\u4e0d\u81f4\u4e8e\u58f0\u540d\u6709\u7d2f\u3002\u6851\u98de\u8679\u5e74\u5c81\u5c1a\u8f7b\uff0c\u4e0d\u61c2\u5bf9\u65b9\u5ac1\u7978\u4e8e\u5df1\u7684\u7528\u610f\uff0c\u60ca\u6012\u4e4b\u4e0b\uff0c\u8fa9\u9053\uff1a\u201c\u6211\u8ddf\u4ed6\u7d20\u4e0d\u76f8\u8bc6\uff0c\u4f55\u5fc5\u5bb3\u4ed6\uff1f\u8fd9\u91cc\u5343\u767e\u5bf9\u773c\u775b\u90fd\u77a7\u89c1\u4e86\uff0c\u4ed6\u660e\u660e\u662f\u5413\u6b7b\u7684\u3002\u201d \u3000\u3000\u5750\u5728\u592a\u5e08\u6905\u4e2d\u7684\u8499\u53e4\u54c8\u8d64\u5927\u5e08\u4e00\u76f4\u695e\u5934\u695e\u8111\u7684\u9ed8\u4e0d\u4f5c\u58f0\uff0c\u8fd9\u65f6\u7a81\u7136\u63d2\u53e3\u9053\uff1a\u201c\u8fd9\u4f4d\u59d1\u5a18\u6ca1\u4e0b\u6bd2\u624b\uff0c\u6211\u662f\u77a7\u5f97\u6e05\u6e05\u695a\u695a\u7684\u3002\u90a3\u4e24\u4e2a\u6076\u9b3c\u4e00\u6765\uff0c\u8fd9\u4f4d\u6587\u7237\u4fbf\u5413\u6b7b\u4e86\u3002\u6211\u542c\u5f97\u4ed6\u53eb\u9053\uff1a\u2018\u9ed1\u65e0\u5e38\u3001\u767d\u65e0\u5e38\uff01\u2019\u201d\u4ed6\u58f0\u97f3\u5b8f\u5927\uff0c\u8bf4\u5230\u201c\u9ed1\u65e0\u5e38\u3001\u767d\u65e0\u5e38\u201d \u3000\u3000\u8fd9\u516d\u4e2a\u5b57\u65f6\uff0c\u5b66\u7740\u6587\u9189\u7fc1\u7684\u8bed\u8c03\uff0c\u66f4\u662f\u5341\u5206\u53e4\u602a\u3002\u4f17\u4eba\u4e00\u6123\u4e4b\u4e0b\uff0c\u54c4\u5802\u5927\u7b11\u8d77\u6765\u3002 \u3000\u3000\u54c8\u8d64\u5374\u4e0d\u77e5\u4f17\u4eba\u56e0\u4f55\u800c\u7b11\uff0c\u5927\u58f0\u9053\uff1a\u201c\u96be\u9053\u6211\u8bf4\u9519\u4e86\u4e48\uff1f \u3000\u3000\u8fd9\u4e24\u4e2a\u65e0\u5e38\u9b3c\u751f\u5f97\u8fd9\u822c\u4e11\u6076\uff0c\u602a\u6a21\u602a\u6837\u7684\uff0c\u5413\u6b7b\u4eba\u4e5f\u4e0d\u7a00\u5947\u3002 \u3000\u3000\u4f60\u53ef\u522b\u9519\u602a\u4e86\u8fd9\u4f4d\u59d1\u5a18\u3002\u201d \u3000\u3000\u6851\u98de\u8679\u9053\uff1a\u201c\u662f\u4e48\uff1f\u8fd9\u4f4d\u5927\u5e08\u4e5f\u8fd9\u4e48\u8bf4\u3002\u4ed6\u81ea\u662f\u5413\u6b7b\u7684\uff0c\u5173\u6211\u4ec0\u4e48\u4e8b\u4e86\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u94c1\u751f\u4ece\u8170\u95f4\u62d4\u51fa\u65f1\u70df\u7b52\uff0c\u88c5\u4e0a\u4e00\u5927\u888b\u70df\u4e1d\uff0c\u6253\u706b\u70b9\u7740\u4e86\uff0c\u5438\u4e86\u4e24\u53e3\uff0c\u6597\u7136\u95f4\u4e00\u80a1\u767d\u70df\u8fce\u9762\u5411\u5979\u55b7\u53bb\uff0c\u559d\u9053\uff1a\u201c\u8d31\u5a62\uff0c\u4f60\u660e\u660e\u662f\u6740\u4eba\u51f6\u624b\uff0c\u5374\u8fd8\u8981\u8d56\uff1f\u201d \u3000\u3000\u6851\u98de\u8679\u89c1\u767d\u70df\u55b7\u5230\uff0c\u6025\u5fd9\u95ea\u907f\uff0c\u4f46\u4e3a\u65f6\u4e0d\u53ca\uff0c\u9f3b\u4e2d\u5df2\u5438\u4e86\u4e00\u4e9b\u767d\u70df\u8fdb\u53bb\uff0c\u5934\u8111\u4e2d\u5fae\u5fae\u53d1\u6655\uff0c\u542c\u4ed6\u51fa\u53e3\u4f24\u4eba\uff0c\u518d\u4e5f\u5fcd\u8010\u4e0d\u4f4f\uff0c\u56de\u9a82\u9053\uff1a\u201c\u7f20\u5939\u4e0d\u6e05\u7684\u8001\u9b3c\uff0c\u96be\u9053\u6211\u6015\u4e86\u4f60\u5417\uff1f\u4f60\u8bf4\u662f\u6211\u6740\u7684\uff0c\u8fde\u4f60\u4e00\u8d77\u6740\u4e86\uff0c\u4fbf\u53c8\u600e\u4e48\u6837\uff1f\u201d\u5de6\u638c\u865a\u62cd\uff0c\u53f3\u8db3\u4fbf\u5f80\u4ed6\u8170\u95f4\u91cc\u8e22\u53bb\u3002 \u3000\u3000\u90a3\u54c8\u8d64\u548c\u5c1a\u5927\u58f0\u9053\uff1a\u201c\u8001\u5934\u513f\uff0c\u4f60\u522b\u51a4\u6789\u597d\u4eba\uff0c\u6211\u4eb2\u773c\u76ee\u7779\uff0c\u8fd9\u6587\u7237\u660e\u660e\u662f\u7ed9\u90a3\u4e24\u4e2a\u6076\u9b3c\u5413\u6b7b\u7684\u2026\u2026\u201d \u3000\u3000\u80e1\u6590\u89c1\u8fd9\u548c\u5c1a\u50bb\u91cc\u50bb\u6c14\uff0c\u6027\u5b50\u5012\u662f\u6b63\u76f4\uff0c\u53ea\u662f\u4ed6\u5f00\u53e3\u201c\u6076\u9b3c\u201d\uff0c\u95ed\u53e3\u201c\u6076\u9b3c\u201d\uff0c\u542c\u6765\u6781\u4e0d\u987a\u8033\uff0c\u4e0d\u7531\u5f97\u5fc3\u4e2d\u6709\u6c14\uff0c\u8981\u5f85\u60f3\u4e2a\u6cd5\u513f\uff0c\u7ed9\u4ed6\u4e00\u70b9\u5c0f\u5c0f\u82e6\u5934\u5403\u5403\uff0c\u5ffd\u89c1\u897f\u9996\u5385\u4e2d\u8d70\u51fa\u4e00\u4e2a\u9752\u5e74\u4e66\u751f\u6765\uff0c\u7b14\u76f4\u5411\u54c8\u8d64\u548c\u5c1a\u8d70\u53bb\u3002\u8fd9\u4eba\u7ea6\u83ab\u4e8c\u5341\u4e94\u516d\u5c81\u5e74\u7eaa\uff0c\u8eab\u6750\u7626\u5c0f\uff0c\u6253\u626e\u5f97\u9887\u4e3a\u4fca\u96c5\uff0c\u53f3\u624b\u6447\u7740\u4e00\u67c4\u6298\u6247\uff0c\u8d70\u5230\u54c8\u8d64\u8ddf\u524d\uff0c\u8bf4\u9053\uff1a\u201c\u5927\u548c\u5c1a\uff0c\u4f60\u6709\u4e00\u53e5\u8bdd\u8bf4\u9519\u4e86\uff0c\u5f97\u6539\u4e00\u6539\u53e3\u3002\u201d\u54c8\u8d64\u77aa\u76ee\u9053\uff1a\u201c\u4ec0\u4e48\u8bdd\u8bf4\u9519\u4e86\uff1f\u201d \u3000\u3000\u90a3\u4e66\u751f\u9053\uff1a\u201c\u90a3\u4e24\u4f4d\u4e0d\u662f\u2018\u6076\u9b3c\u2019\uff0c\u4e43\u662f\u8d6b\u8d6b\u6709\u540d\u7684\u2018\u897f\u5ddd\u53cc\u4fa0\u2019\u5e38\u6c0f\u6606\u4ef2\uff0c\u76f8\u8c8c\u867d\u751f\u5f97\u7279\u5f02\uff0c\u4f46\u6b66\u529f\u9ad8\u5f3a\uff0c\u884c\u4fa0\u4ed7\u4e49\uff0c\u6c5f\u6e56\u4e4b\u4e0a\uff0c\u4eba\u4eba\u94a6\u4ef0\u3002\u201d\u8fd9\u51e0\u53e5\u8bdd\u53ea\u628a\u80e1\u6590\u542c\u5f97\u5fc3\u4e2d\u5927\u60a6\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u4f4d\u4e66\u751f\u76f8\u516c\u80fd\u8bf4\u5f97\u51fa\u8fd9\u6837\u51e0\u53e5\u6765\uff0c\u4eba\u54c1\u5927\u662f\u4e0d\u51e1\uff0c\u5012\u8981\u8ddf\u4ed6\u7ed3\u4ea4\u7ed3\u4ea4\u3002\u201d \u3000\u3000\u54c8\u8d64\u9053\uff1a\u201c\u90a3\u6587\u7237\u4e0d\u662f\u53eb\u4ed6\u4eec\u2018\u9ed1\u65e0\u5e38\u3001\u767d\u65e0\u5e38\u2019\u5417\uff1f\u9ed1\u65e0\u5e38\u3001\u767d\u65e0\u5e38\u600e\u4e48\u4e0d\u662f\u6076\u9b3c\uff1f\u201d\u90a3\u4e66\u751f\u9053\uff1a\u201c\u4ed6\u4e8c\u4f4d\u59d3\u5e38\uff0c\u540d\u5b57\u4e4b\u4e2d\uff0c\u53c8\u662f\u4e00\u4f4d\u6709\u4e2a\u2018\u8d6b\u2019\u5b57\uff0c\u4e00\u4f4d\u6709\u4e2a\u2018\u4f2f\u2019\u5b57\uff0c\u56e0\u6b64\u524d\u8f88\u7684\u670b\u53cb\u4eec\uff0c\u5f00\u73a9\u7b11\u53eb\u4ed6\u4e8c\u4f4d\u4e3a\u9ed1\u65e0\u5e38\u3001\u767d\u65e0\u5e38\u3002\u8fd9\u5916\u53f7\u513f\u82e5\u975e\u6709\u8eab\u5206\u7684\u524d\u8f88\u540d\u5bbf\uff0c\u5374\u4e5f\u4e0d\u662f\u968f\u4fbf\u79f0\u547c\u5f97\u7684\u3002\u201d \u3000\u3000\u4ed6\u4e8c\u4eba\u4e00\u4e2a\u77aa\u7740\u773c\u775b\u5927\u547c\u5c0f\u53eb\uff0c\u4e00\u4e2a\u65af\u65af\u6587\u6587\u7684\u7ed9\u4ed6\u89e3\u8bf4\uff0c\u90a3\u4e00\u8fb9\u4e0a\u5b98\u94c1\u751f\u548c\u6851\u98de\u8679\u5374\u5df2\u52a8\u4e0a\u4e86\u624b\u3002\u83ab\u770b\u6851\u98de\u8679\u9002\u624d\u7ed9\u502a\u6c0f\u5144\u5f1f\u903c\u5f97\u53ea\u6709\u62db\u67b6\u95ea\u907f\uff0c\u5168\u65e0\u8fd8\u624b\u4e4b\u529b\uff0c\u5b9e\u5728\u201c\u53cc\u5b50\u95e8\u201d\u7684\u6b66\u529f\u4e24\u4eba\u5408\u4f7f\uff0c\u592a\u8fc7\u602a\u5f02\uff0c\u8fd9\u65f6\u5979\u4e00\u5bf9\u4e00\u7684\u548c\u4e0a\u5b98\u94c1\u751f\u8fc7\u62db\uff0c\u7adf\u662f\u4e1d\u6beb\u4e0d\u843d\u4e0b\u98ce\u3002\u90a3\u4e0a\u5b98\u94c1\u751f\u770b\u4f3c\u7a7a\u624b\uff0c\u5176\u5b9e\u624b\u4e2d\u90a3\u652f\u65f1\u70df\u7ba1\u4e43\u9554\u94c1\u6253\u5c31\uff0c\u7adf\u5f53\u4f5c\u4e86\u70b9\u7a74\u6a5b\u4f7f\u3002\u4ed6\u201c\u7384\u6307\u95e8\u201d\u539f\u64c5\u6253\u4eba\u8eab\u4e09\u5341\u516d\u5927\u7a74\uff0c\u53ea\u662f\u6851\u98de\u8679\u8eab\u6cd5\u8fc7\u4e8e\u6ed1\u6e9c\uff0c\u59cb\u7ec8\u6253\u4e0d\u5230\u5979\u7684\u7a74\u9053\uff0c\u6709\u51e0\u6b21\u8fc7\u4e8e\u6258\u5927\uff0c\u9669\u4e9b\u8fd8\u88ab\u5979\u98de\u8db3\u8e22\u4e2d\u3002 \u3000\u3000\u4f46\u542c\u5f97\u4ed6\u55e4\u6e9c\u6e9c\u7684\u4e0d\u505c\u5438\u70df\uff0c\u541e\u70df\u5410\u96fe\uff0c\u90a3\u6839\u70df\u7ba1\u7adf\u88ab\u4ed6\u5438\u5f97\u6e10\u6e10\u7684\u7531\u9ed1\u8f6c\u7ea2\uff0c\u539f\u6765\u90a3\u5927\u70df\u6597\u4e4b\u4e2d\u85cf\u7740\u8bb8\u591a\u7cbe\u70ad\uff0c\u4ed6\u4e00\u5438\u4e00\u5439\uff0c\u5c06\u9554\u94c1\u70df\u6597\u6e10\u6e10\u70e7\u7ea2\u3002\u8fd9\u4e48\u4e00\u6765\uff0c\u4e00\u6839\u5bfb\u5e38\u70df\u7ba1\u53d8\u6210\u4e86\u4e00\u4ef6\u6781\u5389\u5bb3\u7684\u5229\u5668\uff0c\u6253\u5f97\u7a0d\u8fd1\uff0c\u6851\u98de\u8679\u4fbf\u611f\u624b\u70eb\u9762\u70ed\uff0c\u8863\u5e26\u88d9\u89d2\u66f4\u7ed9\u70df\u6597\u7099\u7126\u4e86\u3002\u5979\u5fc3\u4e2d\u4e00\u614c\uff0c\u624b\u811a\u7a0d\u6162\uff0c\u84e6\u5730\u91cc\u4e0a\u5b98\u94c1\u751f\u4e00\u53e3\u767d\u70df\u76f4\u55b7\u5230\u5979\u8138\u4e0a\uff0c\u6851\u98de\u8679\u53ea\u611f\u5934\u8111\u4e00\u9635\u6655\u7729\uff0c\u767b\u65f6\u5929\u65cb\u5730\u8f6c\uff0c\u7ad9\u7acb\u4e0d\u5b9a\uff0c\u8eab\u5b50\u4e00\u6643\uff0c\u6454\u5012\u5728\u5730\u3002\u539f\u6765\u4e0a\u5b98\u94c1\u751f\u6240\u5438\u7684\u70df\u8349\u4e4b\u4e2d\uff0c\u6df7\u6709\u6781\u731b\u70c8\u7684\u8ff7\u836f\uff0c\u4ed6\u4e00\u6765\u5e73\u65f6\u5438\u60ef\uff0c\u4e8c\u6765\u53e3\u9f3b\u4e4b\u4e2d\u53e6\u6709\u89e3\u836f\u3002 \u3000\u3000\u90a3\u4e66\u751f\u7ad9\u5728\u4e00\u65c1\u8ddf\u54c8\u8d64\u548c\u5c1a\u8bf4\u8bdd\uff0c\u6ca1\u7406\u4f1a\u8eab\u65c1\u7684\u6253\u6597\uff0c\u5ffd\u7136\u95f4\u9f3b\u4e2d\u95fb\u5230\u4e00\u80a1\u5f02\u9999\uff0c\u5176\u4e2d\u7adf\u6df7\u6709\u9ed1\u9053\u4e2d\u6240\u4f7f\u7684\u8ff7\u9999\u5728\u5185\uff0c\u4e0d\u7531\u5f97\u5927\u6012\u3002\u4e00\u77a5\u773c\u95f4\uff0c\u53ea\u89c1\u4e0a\u5b98\u94c1\u751f\u7684\u70df\u7ba1\u5df2\u70b9\u5411\u6851\u98de\u8679\u819d\u5f2f\u7a74\u9053\uff0c\u55e4\u7684\u4e00\u58f0\u54cd\uff0c\u70df\u7130\u98de\u626c\uff0c\u7126\u6c14\u89e6\u9f3b\uff0c\u5979\u88d9\u5b50\u5df2\u70e7\u7a7f\u4e86\u4e00\u4e2a\u6d1e\uff0c\u6851\u98de\u8679\u53d7\u4f24\uff0c\u5927\u53eb\u4e00\u58f0\uff0c\u4e0a\u5b98\u94c1\u751f\u7b2c\u4e8c\u4e0b\u53c8\u6253\u5411\u5979\u7684\u8170\u95f4\u3002 \u3000\u3000\u90a3\u4e66\u751f\u6012\u559d\uff1a\u201c\u4f4f\u624b\uff01\u201d\u4e0a\u5b98\u94c1\u751f\u4e00\u6014\u4e4b\u95f4\uff0c\u90a3\u4e66\u751f\u4e00\u5f2f\u8170\uff0c\u5df2\u9664\u4e0b\u54c8\u8d64\u548c\u5c1a\u7684\u4e00\u5bf9\u978b\u5b50\uff0c\u8fd4\u8eab\u5411\u4e0a\u5b98\u94c1\u751f\u70e7\u7ea2\u4e86\u7684\u70df\u6597\u4e0a\u631f\u53bb\u3002 \u3000\u3000\u90a3\u4e66\u751f\u8fd9\u51e0\u4e0b\u624b\u811a\u5f53\u771f\u662f\u5982\u98ce\u4f3c\u7535\uff0c\u54c8\u8d64\u548c\u5c1a\u4e00\u6014\u4e4b\u4e0b\uff0c\u5927\u53eb\uff1a\u201c\u4f60\u2026\u2026\u4f60\u8131\u4e86\u6211\u978b\u5b50\u5e72\u4e48\uff1f\u201d\u4ed6\u558a\u53eb\u58f0\u4e2d\uff0c\u90a3\u4e66\u751f\u5df2\u7528\u4e24\u53ea\u978b\u5b50\u7684\u978b\u5e95\u631f\u4f4f\u4e86\u90a3\u70e7\u5f97\u901a\u7ea2\u7684\u9554\u94c1\u70df\u6597\uff0c\u4e00\u6323\u4e00\u626d\uff0c\u7ed5\u5230\u4e0a\u5b98\u94c1\u751f\u8eab\u540e\u3002\u55e4\u55e4\u51e0\u58f0\u54cd\uff0c\u4e0a\u5b98\u94c1\u751f\u8863\u8896\u70e7\u7126\uff0c\u4ed6\u53f3\u81c2\u5403\u75db\uff0c\u53ea\u5f97\u6492\u624b\u3002\u90a3\u4e66\u751f\u8fde\u978b\u5e26\u70df\u7ba1\u5f80\u5916\u4e00\u6296\uff0c\u6454\u4e86\u51fa\u53bb\uff0c\u62a2\u6b65\u53bb\u770b\u6851\u98de\u8679\uff0c\u53ea\u89c1\u5979\u53cc\u76ee\u7d27\u95ed\uff0c\u660f\u8ff7\u4e0d\u9192\u3002 \u3000\u3000\u556a\u556a\u4e24\u54cd\uff0c\u54c8\u8d64\u7684\u4e00\u5bf9\u978b\u5b50\u8dcc\u5728\u9152\u5e2d\u4e4b\u4e0a\uff0c\u6c64\u6c34\u56db\u6e85\uff0c\u90a3\u70df\u7ba1\u5374\u5bf9\u51c6\u4e86\u90ed\u7389\u5802\u98de\u53bb\uff0c\u529b\u52b2\u52bf\u6025\u3002\u90ed\u7389\u5802\u53eb\u58f0\uff1a\u201c\u554a\u54df\uff01\u201d \u3000\u3000\u6025\u6b32\u95ea\u907f\uff0c\u53ea\u662f\u90a3\u70df\u7ba1\u6765\u5f97\u592a\u5feb\uff0c\u53c8\u662f\u51fa\u5176\u4e0d\u610f\uff0c\u4e00\u65f6\u4e0d\u53ca\u8eb2\u8ba9\uff0c\u773c\u89c1\u90a3\u901a\u7ea2\u7099\u70ed\u7684\u94c1\u70df\u6597\u4fbf\u8981\u649e\u5230\u4ed6\u7684\u9762\u95e8\u3002\u80e1\u6590\u4f38\u624b\u6293\u8d77\u4e00\u53cc\u7b77\u5b50\uff0c\u529b\u900f\u7b77\u7aef\uff0c\u534a\u7a7a\u4e2d\u5c06\u70df\u7ba1\u631f\u4f4f\u4e86\u3002 \u3000\u3000\u8fd9\u51e0\u4e0b\u5154\u8d77\u9e58\u843d\uff0c\u53d8\u5316\u83ab\u6d4b\uff0c\u5927\u5385\u4e0a\u7fa4\u8c6a\u5446\u4e86\u4e00\u5446\uff0c\u8fd9\u624d\u9f50\u58f0\u559d\u5f69\u3002\u90a3\u4e66\u751f\u5411\u80e1\u6590\u70b9\u5934\u4e00\u7b11\uff0c\u8c22\u4ed6\u76f8\u52a9\uff0c\u514d\u81f4\u65e0\u610f\u4f24\u4eba\uff0c\u8f6c\u8fc7\u5934\u6765\uff0c\u76b1\u4e86\u7709\u671b\u7740\u6851\u98de\u8679\uff0c\u4e0d\u77e5\u5982\u4f55\u89e3\u6551\uff0c\u4e00\u987f\u4e4b\u4e0b\uff0c\u5411\u4e0a\u5b98\u94c1\u751f\u559d\u9053\uff1a\u201c\u8fd9\u91cc\u5927\u4f19\u513f\u6bd4\u6b66\u8f83\u827a\uff0c\u4f60\u600e\u5730\u7528\u8d77\u8ff7\u836f\u6765\u5566\uff1f\u5feb\u53d6\u89e3\u836f\u51fa\u6765\uff01\u201d \u3000\u3000\u4e0a\u5b98\u94c1\u751f\u88ab\u4ed6\u593a\u53bb\u70df\u7ba1\uff0c\u77e5\u9053\u8fd9\u4e66\u751f\u51fa\u624b\u654f\u6377\uff0c\u81ea\u5df1\u53c8\u6ca1\u4e86\u5175\u5203\uff0c\u4e0d\u6562\u518d\u786c\uff0c\u53ea\u9634\u9634\u5730\u9053\uff1a\u201c\u8c01\u7528\u8ff7\u836f\u5566\uff1f\u8fd9\u4e2b\u5934\u5b9a\u529b\u592a\u5dee\uff0c\u8f6c\u4e86\u51e0\u4e2a\u5708\u5b50\u4fbf\u6655\u5012\u4e86\uff0c\u53c8\u602a\u5f97\u8c01\u6765\uff1f\u201d\u65c1\u89c2\u4f17\u4eba\u4e0d\u660e\u771f\u76f8\uff0c\u5012\u4e5f\u4e0d\u4fbf\u7f16\u6d3e\u8c01\u7684\u4e0d\u662f\u3002 \u3000\u3000\u5374\u89c1\u897f\u5385\u5e2d\u4e0a\u8d70\u51fa\u4e00\u4e2a\u8170\u5f2f\u5f13\u80cc\u7684\u4e2d\u5e74\u5987\u4eba\uff0c\u624b\u4e2d\u62ff\u7740\u4e00\u53ea\u9152\u676f\uff0c\u542b\u4e86\u4e00\u53e3\u9152\uff0c\u4fbf\u5f80\u6851\u98de\u8679\u8138\u4e0a\u55b7\u53bb\u3002\u90a3\u4e66\u751f\u9053\uff1a\u201c\u554a\uff0c\u8fd9\u2026\u2026\u8fd9\u662f\u89e3\u836f\u4e48\uff1f\u201d\u90a3\u5987\u4eba\u4e0d\u7b54\uff0c\u53c8\u55b7\u4e86\u4e00\u53e3\u9152\uff0c\u55b7\u5230\u7b2c\u4e09\u53e3\u65f6\uff0c\u6851\u98de\u8679\u7741\u5f00\u773c\u6765\uff0c\u4e00\u65f6\u4e0d\u660e\u6240\u4ee5\u3002 \u3000\u3000\u4e0a\u5b98\u94c1\u751f\u9053\uff1a\u201c\u54c8\uff0c\u8fd9\u4e2b\u5934\u53ef\u4e0d\u662f\u81ea\u5df1\u9192\u4e86\uff1f\u600e\u5730\u80e1\u8bf4\u516b\u9053\uff0c\u8bf4\u6211\u4f7f\u8ff7\u836f\uff1f\u5802\u5802\u798f\u5927\u5e05\u5e9c\u4e2d\uff0c\u8bf4\u8bdd\u53ef\u5f97\u68c0\u70b9\u4e9b\u3002\u201d\u90a3\u4e66\u751f\u53cd\u624b\u4e00\u8bb0\u8033\u5149\uff0c\u559d\u9053\uff1a\u201c\u5148\u6253\u4f60\u8fd9\u4e0b\u4e09\u70c2\u7684\u5978\u5f92\u3002\u201d\u4e0a\u5b98\u94c1\u751f\u4e00\u4f4e\u5934\uff0c\u8fd9\u4e00\u638c\u5c45\u7136\u5e76\u6ca1\u6253\u4e2d\u3002\u90a3\u4e66\u751f\u6253\u5f97\u5de7\u5999\uff0c\u8fd9\u201c\u70df\u971e\u6563\u4eba\u201d\u5374\u4e5f\u8eb2\u5f97\u7075\u52a8\u3002 \u3000\u3000\u6851\u98de\u8679\u4f38\u624b\u63c9\u4e86\u63c9\u773c\u775b\uff0c\u5df2\u7136\u9192\u609f\uff0c\u4e00\u8dc3\u800c\u8d77\uff0c\u5de6\u638c\u63a2\u51fa\uff0c\u62cd\u5411\u4e0a\u5b98\u94c1\u751f\u80f8\u53e3\uff0c\u9a82\u9053\uff1a\u201c\u4f60\u7528\u6bd2\u70df\u55b7\u4eba\uff01\u201d \u3000\u3000\u4e0a\u5b98\u94c1\u751f\u659c\u8eab\u95ea\u5f00\uff0c\u5411\u90a3\u4e2d\u5e74\u5987\u4eba\u77aa\u4e86\u4e00\u773c\uff0c\u5fc3\u4e2d\u53c8\u60ca\u53c8\u6012\uff1a\u201c\u6b64\u4eba\u600e\u80fd\u89e3\u6211\u7684\u72ec\u95e8\u8ff7\u836f\uff1f\u6211\u8ddf\u4f60\u65e0\u51a4\u65e0\u4ec7\uff0c\u4f55\u4ee5\u6765\u591a\u7ba1\u95f2\u4e8b\uff1f\u201d \u3000\u3000\u6851\u98de\u8679\u5411\u90a3\u4e66\u751f\u70b9\u4e86\u70b9\u5934\uff0c\u9053\uff1a\u201c\u591a\u8c22\u76f8\u516c\u63f4\u624b\u3002\u201d\u90a3\u4e66\u751f\u6307\u7740\u90a3\u5987\u4eba\u9053\uff1a\u201c\u662f\u8fd9\u4f4d\u5973\u4fa0\u6551\u9192\u4f60\u7684\u3002\u201d \u3000\u3000\u90a3\u5987\u4eba\u51b7\u51b7\u7684\u9053\uff1a\u201c\u6211\u4e0d\u4f1a\u6551\u4eba\u3002\u201d\u8f6c\u8eab\u63a5\u8fc7\u80e1\u6590\u624b\u4e2d\u7684\u7b77\u5b50\uff0c\u631f\u7740\u90a3\u6839\u94c1\u70df\u7ba1\uff0c\u4ea4\u5728\u4e0a\u5b98\u94c1\u751f\u624b\u91cc\uff0c\u4ecd\u662f\u5636\u54d1\u7740\u55d3\u5b50\u9053\uff1a\u201c\u8fd9\u6b21\u53ef\u5f97\u62ff\u7a33\u4e86\u3002\u201d \u3000\u3000\u8fd9\u4e00\u6765\uff0c\u90a3\u4e66\u751f\u3001\u6851\u98de\u8679\u3001\u4e0a\u5b98\u94c1\u751f\u5168\u90fd\u80e1\u6d82\u4e86\uff0c\u4e0d\u77e5\u8fd9\u5987\u4eba\u662f\u4f55\u8def\u9053\uff0c\u5979\u6551\u9192\u4e86\u6851\u98de\u8679\uff0c\u5374\u53c8\u5c06\u70df\u7ba1\u8fd8\u7ed9\u4e0a\u5b98\u94c1\u751f\uff0c\u96be\u9053\u5979\u662f\u4e2a\u6ee5\u597d\u4eba\uff0c\u4e0d\u5206\u662f\u975e\u7684\u4e13\u505a\u597d\u4e8b\u4e48\uff1f\u53ea\u89c1\u5979\u5934\u53d1\u82b1\u767d\uff0c\u8138\u8272\u8721\u9ec4\uff0c\u4f53\u8d28\u6781\u662f\u8870\u5f31\uff0c\u4e0d\u4f3c\u8eab\u6709\u6b66\u529f\uff0c\u5f85\u8981\u4ed4\u7ec6\u6253\u91cf\u65f6\uff0c\u90a3\u5987\u4eba\u5df2\u8f6c\u8fc7\u8eab\u5b50\uff0c\u56de\u5f52\u5e2d\u4e0a\u3002\u8fd9\u5987\u4eba\u6b63\u662f\u7a0b\u7075\u7d20\u6240\u4e54\u88c5\u6539\u626e\u3002\u8981\u77e5\u82e5\u4e0d\u662f\u6bd2\u624b\u836f\u738b\u7684\u9ad8\u5f92\uff0c\u4e5f\u51b3\u4e0d\u80fd\u5728\u9877\u523b\u4e4b\u95f4\uff0c\u4fbf\u89e3\u4e86\u4e0a\u5b98\u94c1\u751f\u6240\u4f7f\u7684\u72ec\u95e8\u8ff7\u836f\u3002 \u3000\u3000\u54c8\u8d64\u4e00\u76f4\u4e0d\u505c\u53e3\u7684\u5927\u53eb\uff1a\u201c\u8fd8\u6211\u978b\u5b50\u6765\uff0c\u8fd8\u6211\u978b\u5b50\u6765\uff01\u201d\u4f46\u5404\u4eba\u5fc3\u6709\u65c1\u9a9b\uff0c\u8c01\u4e5f\u6ca1\u6709\u7406\u4ed6\u3002\u54c8\u8d64\u5927\u607c\uff0c\u4f38\u624b\u5f80\u90a3\u4e66\u751f\u80cc\u5fc3\u626d\u53bb\uff0c\u559d\u9053\uff1a\u201c\u8fd8\u6211\u978b\u5b50\u4e0d\u8fd8\uff1f\u201d\u90a3\u4e66\u751f\u8eab\u5b50\u4e00\u4fa7\uff0c\u8ba9\u4e86\u5f00\u53bb\uff0c\u7b11\u9053\uff1a\u201c\u5927\u548c\u5c1a\uff0c\u978b\u5b50\u70e7\u7126\u5566\uff1f\u201d\u54c8\u8d64\u8db3\u4e0b\u65e0\u978b\uff0c\u751a\u662f\u72fc\u72c8\uff0c\u5954\u5230\u9152\u5e2d\u4e0a\u53bb\u6361\u8d77\uff0c\u53ea\u662f\u4e00\u5bf9\u978b\u5b50\u9152\u6c34\u6dcb\u6f13\uff0c\u91cc\u91cc\u5916\u5916\u90fd\u662f\u6cb9\u817b\uff0c\u600e\u80fd\u518d\u7a7f\uff1f\u53ef\u662f\u4e0d\u7a7f\u53c8\u4e0d\u6210\uff0c\u53ea\u5f97\u52c9\u5f3a\u5957\u5728\u811a\u4e0a\uff0c\u8f6c\u5934\u53bb\u627e\u90a3\u4e66\u751f\u7684\u6666\u6c14\u65f6\uff0c\u5374\u5df2\u5bfb\u4e0d\u5230\u4ed6\u7684\u8e2a\u5f71\u3002 \u3000\u3000\u4f46\u89c1\u4e0a\u5b98\u94c1\u751f\u548c\u6851\u98de\u8679\u53c8\u5df2\u6597\u5728\u4e00\u8d77\u3002\u54c8\u8d64\u8f6c\u4e86\u51e0\u4e2a\u5708\u5b50\uff0c\u4e0d\u89c1\u4e66\u751f\uff0c\u53ea\u5f97\u56de\u53bb\u5750\u5728\u592a\u5e08\u6905\u4e2d\uff0c\u5583\u5583\u9053\uff1a\u201c\u76f4\u5a18\u8d3c\uff0c\u4eca\u65e5\u4e5f\u771f\u6666\u6c14\uff0c\u649e\u89c1\u4e86\u4e00\u5bf9\u65e0\u5e38\u9b3c\uff0c\u53c8\u9047\u4e0a\u4e86\u4e00\u4e2a\u79c0\u624d\u9b3c\u3002\u201d \u3000\u3000\u53e3\u4e2d\u5343\u8d3c\u4e07\u8d3c\u5730\u9a82\u4e2a\u4e0d\u505c\u3002 \u3000\u3000\u4ed6\u9a82\u4e86\u4e00\u9635\uff0c\u89c1\u4e0a\u5b98\u94c1\u751f\u548c\u6851\u98de\u8679\u8d8a\u6597\u8d8a\u5feb\uff0c\u4e00\u65f6\u4e5f\u5206\u4e0d\u51fa\u9ad8\u4e0b\uff0c\u65e0\u804a\u8d77\u6765\uff0c\u66f4\u4f4f\u53e3\u4e0d\u9a82\u4e86\uff0c\u5374\u89c9\u811a\u4e0a\u6cb9\u817b\u817b\u7684\u5341\u5206\u96be\u53d7\uff0c\u5fcd\u4e0d\u4f4f\u53c8\u7834\u53e3\u9a82\u4e86\u51fa\u6765\u3002 \u3000\u3000\u7a81\u7136\u95f4\u53ea\u542c\u5f97\u4f17\u4eba\u54c8\u54c8\u5927\u7b11\uff0c\u54c8\u8d64\u77aa\u76ee\u800c\u89c6\uff0c\u4e0d\u89c1\u6709\u4f55\u53ef\u7b11\u4e4b\u5904\uff0c\u5374\u89c1\u4f17\u4eba\u7684\u76ee\u5149\u4e00\u9f50\u671b\u7740\u81ea\u5df1\uff0c\u54c8\u8d64\u6478\u4e86\u6478\u8138\uff0c\u4f4e\u5934\u77a7\u77a7\u8eab\u4e0a\u8863\u670d\uff0c\u9664\u4e86\u4e00\u53cc\u978b\u5b50\u4e4b\u5916\uff0c\u5e76\u65e0\u4ec0\u4e48\u7279\u5f02\uff0c\u6012\u9053\uff1a\u201c\u7b11\u4ec0\u4e48\uff1f\u6709\u4ec0\u4e48\u597d\u7b11\uff1f\u201d\u4f17\u4eba\u5374\u7b11\u5f97\u66f4\u52a0\u5389\u5bb3\u4e86\u3002\u54c8\u8d64\u5fc3\u9053\uff1a\u201c\u597d\u5427\uff0c\u9f9f\u513f\u5b50\uff0c\u4f60\u4eec\u7b11\u4f60\u4eec\u7684\uff0c\u8001\u5b50\u53ef\u4e0d\u6765\u7406\u4f1a\u3002\u201d\u4e00\u672c\u6b63\u7ecf\u7684\u5750\u5728\u6905\u4e2d\uff0c\u53ea\u9053\u81ea\u5df1\u89c1\u602a\u4e0d\u602a\uff0c\u5176\u602a\u81ea\u8d25\uff0c\u4f17\u4eba\u778e\u7b11\u4e00\u9635\uff0c\u81ea\u4f1a\u6b62\u6b47\uff0c\u5c82\u77e5\u5927\u5385\u4e2d\u7b11\u58f0\u8d8a\u6765\u8d8a\u54cd\u3002\u6851\u98de\u8679\u867d\u5728\u6076\u6597\uff0c\u4f46\u5076\u4e00\u56de\u5934\u4e4b\u9645\uff0c\u5374\u4e5f\u5fcd\u4e0d\u4f4f\u62bf\u5634\u5ae3\u7136\u3002 \u3000\u3000\u54c8\u8d64\u76ee\u77aa\u53e3\u5446\uff0c\u5fc3\u614c\u610f\u4e71\uff0c\u5b9e\u4e0d\u77e5\u4f17\u4eba\u7b11\u4e9b\u4ec0\u4e48\uff0c\u4e1c\u5f20\u897f\u671b\uff0c\u60c5\u72b6\u66f4\u662f\u6ed1\u7a3d\u3002\u6851\u98de\u8679\u7ec8\u4e8e\u8010\u4e0d\u5f97\u4e86\uff0c\u7b11\u9053\uff1a\u201c\u5927\u548c\u5c1a\uff0c\u4f60\u80cc\u540e\u662f\u4ec0\u4e48\u554a\uff1f\u201d\u54c8\u8d64\u4e00\u8dc3\u79bb\u6905\uff0c\u56de\u8fc7\u5934\u6765\uff0c\u53ea\u89c1\u90a3\u4e66\u751f\u7a33\u7a33\u7684\u5750\u5728\u4ed6\u6905\u80cc\u4e4b\u4e0a\uff0c\u6307\u624b\u5212\u811a\uff0c\u505a\u7740\u54d1\u5267\uff0c\u9017\u5f15\u4f17\u4eba\u53d1\u7b11\u3002 \u3000\u3000\u539f\u6765\u4ed6\u5728\u6905\u80cc\u4e0a\u5df2\u5750\u4e86\u751a\u4e45\uff0c\u9ed8\u4e0d\u4f5c\u58f0\u7684\u505a\u51fa\u5404\u79cd\u602a\u6a21\u602a\u6837\u3002 \u3000\u3000\u54c8\u8d64\u5927\u6012\uff0c\u559d\u9053\uff1a\u201c\u79c0\u624d\u9b3c\uff0c\u4f60\u5e72\u4e48\u4f5c\u5f04\u6211\uff1f\u201d\u90a3\u4e66\u751f\u8038\u8038\u80a9\u5934\uff0c\u505a\u4e2a\u624b\u52bf\uff0c\u610f\u8c13\uff1a\u201c\u6211\u6ca1\u4f5c\u5f04\u4f60\u554a\u3002\u201d\u54c8\u8d64\u559d\u9053\uff1a\u201c\u90a3\u4f60\u5e72\u4e48\u5750\u5728\u8fd9\u91cc\uff1f\u201d\u90a3\u4e66\u751f\u6307\u6307\u8336\u51e0\u4e0a\u7684\u516b\u53ea\u7389\u9f99\u676f\uff0c\u505a\u4e2a\u53d6\u800c\u85cf\u4e4b\u6000\u5185\u7684\u624b\u52bf\uff0c\u610f\u601d\u8bf4\uff1a\u201c\u6211\u60f3\u53d6\u8fd9\u7389\u9f99\u676f\u3002\u201d\u54c8\u8d64\u53c8\u9053\uff1a\u201c\u4f60\u8981\u4e89\u593a\u5fa1\u676f\uff1f\u201d\u90a3\u4e66\u751f\u70b9\u4e86\u70b9\u5934\u3002\u54c8\u8d64\u9053\uff1a\u201c\u8fd9\u91cc\u8fd8\u6709\u7a7a\u7740\u7684\u5ea7\u4f4d\uff0c\u5e72\u4e48\u4e0d\u5750\uff1f\u201d\u90a3\u4e66\u751f\u6307\u6307\u5385\u4e0a\u7684\u7fa4\u8c6a\uff0c\u5de6\u624b\u8fde\u626c\uff0c\u53f3\u624b\u63e1\u62f3\u865a\u51fb\u5df1\u5934\uff0c\u8ddf\u7740\u7f29\u80a9\u62b1\u5934\uff0c\u4f5c\u6781\u5ea6\u5bb3\u6015\u72b6\u3002\u4f17\u4eba\u8f70\u7b11\u58f0\u4e2d\uff0c\u54c8\u8d64\u9053\uff1a\u201c\u4f60\u6015\u4eba\u6253\uff0c\u4e0d\u6562\u5750\uff0c\u53c8\u4e3a\u4ec0\u4e48\u5750\u5728\u6211\u7684\u6905\u80cc\u4e0a\uff1f\u201d\u90a3\u4e66\u751f\u865a\u8e22\u4e00\u811a\uff0c\u53cc\u624b\u865a\u51fb\u62cd\u638c\uff0c\u8eab\u5b50\u6ed1\u4e0b\uff0c\u5750\u5728\u6905\u4e2d\uff0c\u8fd9\u610f\u601d\u5341\u5206\u660e\u663e\uff1a\u201c\u6211\u5c06\u4f60\u4e00\u811a\u8e22\u5f00\uff0c\u5360\u4e86\u4f60\u7684\u6905\u5b50\u3002\u201d\u4ed6\u8eab\u5b50\u4e00\u6ed1\u4e0b\uff0c\u767b\u65f6\u7b11\u58f0\u54c4\u5802\u3002 \u3000\u3000\u798f\u5eb7\u5b89\u3001\u5b89\u63d0\u7763\u7b49\u89c1\u8fd9\u573a\u6bd4\u6b66\u95f9\u5f97\u602a\u6001\u767e\u51fa\uff0c\u4e0e\u539f\u610f\u5927\u76f8\u5f84\u5ead\uff0c\u5fc3\u4e2d\u90fd\u611f\u4e0d\u5feb\uff0c\u4f46\u89c1\u8fd9\u4e66\u751f\u5201\u94bb\u53e4\u602a\uff0c\u54c8\u8d64\u548c\u5c1a\u504f\u53c8\u5fe0\u539a\u8001\u5b9e\uff0c\u4e24\u4eba\u7adf\u4f3c\u4e8b\u5148\u4e32\u901a\u4e86\u6765\u6f14\u4e00\u51fa\u53cc\u7c27\u620f\u4e00\u822c\uff0c\u4e5f\u7981\u4e0d\u4f4f\u5fae\u7b11\u3002\u8fd9\u65f6\u90a3\u5bf9\u53cc\u751f\u5b69\u513f\u5df2\u7531\u738b\u5251\u82f1\u3001\u738b\u5251\u6770\u5144\u5f1f\u62a4\u9001\u5230\u4e86\u540e\u9662\uff0c\u82e5\u662f\u5c1a\u5728\u5927\u5385\uff0c\u5b69\u5b50\u4eec\u559c\u6b22\u70ed\u95f9\uff0c\u66f4\u8981\u54c8\u54c8\u5927\u7b11\u4e86\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u4f4e\u58f0\u5bf9\u80e1\u6590\u9053\uff1a\u201c\u8fd9\u4eba\u7684\u8f7b\u529f\u5de7\u5999\u4e4b\u6781\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u662f\u554a\uff0c\u4ed6\u8eab\u6cd5\u5947\u7075\uff0c\u53e6\u6210\u4e00\u6d3e\uff0c\u6211\u751f\u5e73\u8fd8\u6ca1\u89c1\u8fc7\u3002\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u4f3c\u4e4e\u5b58\u5fc3\u6363\u86cb\u6765\u7740\u3002\u201d\u80e1\u6590\u7f13\u7f13\u70b9\u5934\uff0c\u4e0d\u518d\u8bf4\u8bdd\u3002 \u3000\u3000\u8fd9\u65f6\u4f1a\u4e2d\u6709\u8bc6\u4e4b\u58eb\u4e5f\u90fd\u5df2\u770b\u51fa\uff0c\u8fd9\u4e66\u751f\u660e\u7740\u662f\u8ddf\u54c8\u8d64\u73a9\u95f9\uff0c\u5b9e\u5219\u662f\u5728\u6405\u6270\u798f\u5eb7\u5b89\u8fd9\u5929\u4e0b\u638c\u95e8\u4eba\u5927\u4f1a\uff0c\u8981\u4ee4\u4ed6\u4e00\u4e2a\u5e84\u4e25\u8083\u7a46\u7684\u82f1\u8c6a\u805a\u4f1a\uff0c\u53d8\u6210\u767e\u620f\u6742\u9648\u7684\u80e1\u95f9\u4e4b\u573a\u3002 \u3000\u3000\u53ea\u89c1\u90a3\u4e66\u751f\u4ece\u6000\u4e2d\u53d6\u51fa\u4e00\u67c4\u6298\u6247\u6307\u7740\u54c8\u8d64\uff0c\u8bf4\u9053\uff1a\u201c\u54c8\u8d64\u548c\u5c1a\uff0c\u4f60\u4e0d\u53ef\u5bf9\u6211\u65e0\u793c\u3002\u6b64\u6247\u4e4b\u4e2d\uff0c\u85cf\u7740\u4f60\u7684\u8001\u7956\u5b97\u3002\u201d\u54c8\u8d64\u4fa7\u8fc7\u4e86\u5934\uff0c\u77a7\u77a7\u6298\u6247\uff0c\u4e0d\u89c1\u5176\u4e2d\u6709\u4f55\u5f02\u72b6\uff0c\u6447\u5934\u9053\uff1a\u201c\u4e0d\u4fe1\u4f60\u7684\u778e\u8bf4\uff01\u201d\u90a3\u4e66\u751f\u7a81\u7136\u6253\u5f00\u6298\u6247\uff0c\u5411\u7740\u4ed6\u4e00\u626c\uff0c\u4e00\u672c\u6b63\u7ecf\u7684\u9053\uff1a\u201c\u4f60\u4e0d\u4fe1\uff1f\u90a3\u5c31\u6e05\u6e05\u695a\u695a\u7684\u77a7\u4e00\u77a7\u3002\u201d \u3000\u3000\u4f17\u4eba\u4e00\u770b\u4ed6\u7684\u6298\u6247\uff0c\u65e0\u4e0d\u7b11\u5f97\u6253\u8dcc\uff0c\u539f\u6765\u767d\u7eb8\u6247\u9762\u4e0a\u753b\u7740\u4e00\u53ea\u6781\u5927\u7684\u4e4c\u9f9f\u3002\u8fd9\u53ea\u4e4c\u9f9f\u809a\u76ae\u671d\u5929\uff0c\u4f38\u51fa\u957f\u957f\u7684\u5934\u9888\uff0c\u52aa\u529b\u8981\u7ffb\u8f6c\u8eab\u6765\uff0c\u4f46\u770b\u6837\u5b50\u504f\u53c8\u7ffb\u4e0d\u8f6c\uff0c\u795e\u60c5\u6781\u662f\u6ed1\u7a3d\u3002 \u3000\u3000\u80e1\u6590\u5fcd\u4f4f\u7b11\u671b\u7a0b\u7075\u7d20\u4e00\u773c\uff0c\u4e24\u4eba\u66f4\u52a0\u786e\u5b9a\u65e0\u7591\uff0c\u8fd9\u4e66\u751f\u4e43\u662f\u6709\u5907\u800c\u6765\uff0c\u5b58\u5fc3\u6363\u4e71\u3002\u4e0d\u7531\u5f97\u5bf9\u4ed6\u90fd\u6697\u81ea\u4f69\u670d\uff0c\u987b\u77e5\u5728\u8fd9\u9f99\u6f6d\u864e\u7a74\u4e4b\u4e2d\uff0c\u5929\u4e0b\u82f1\u8c6a\u4e4b\u524d\uff0c\u8fd9\u822c\u6405\u5c40\uff0c\u5b9e\u5177\u8fc7\u4eba\u80c6\u8bc6\u3002 \u3000\u3000\u54c8\u8d64\u5927\u6012\uff0c\u543c\u58f0\u5982\u96f7\uff0c\u559d\u9053\uff1a\u201c\u4f60\u9a82\u6211\u662f\u4e4c\u9f9f\uff1f\u81ed\u79c0\u624d\u5f53\u771f\u6d3b\u5f97\u4e0d\u8010\u70e6\u4e86\uff01\u201d\u90a3\u4e66\u751f\u4e0d\u52a8\u58f0\u8272\uff0c\u8bf4\u9053\uff1a\u201c\u505a\u4e4c\u9f9f\u6709\u4ec0\u4e48\u4e0d\u597d\uff1f\u9f9f\u9e64\u5ef6\u9f84\uff0c\u6211\u8bf4\u4f60\u957f\u547d\u767e\u5c81\u554a\u3002\u201d\u54c8\u8d64\u9053\uff1a\u201c\u5478\uff0c\u4e4c\u9f9f\u662f\u9a82\u4eba\u7684\u8bdd\u3002\u8001\u5a46\u5077\u6c49\u5b50\uff0c\u90a3\u4fbf\u662f\u505a\u4e4c\u9f9f\u4e86\u3002\u201d\u90a3\u4e66\u751f\u9053\uff1a\u201c\u5931\u656c\uff0c\u5931\u656c\uff01\u539f\u6765\u5927\u548c\u5c1a\u8fd8\u5a36\u5f97\u6709\u8001\u5a46\uff01\u4e0d\u77e5\u5a36\u4e86\u51e0\u4e2a\uff1f\u201d \u3000\u3000\u6c64\u6c9b\u89c1\u798f\u5eb7\u5b89\u7684\u8138\u8272\u8d8a\u6765\u8d8a\u662f\u4e0d\u5584\uff0c\u6b63\u8981\u51fa\u6765\u5e72\u9884\uff0c\u7a81\u89c1\u54c8\u8d64\u6012\u543c\u4e00\u58f0\uff0c\u4f38\u624b\u4fbf\u5f80\u90a3\u4e66\u751f\u80cc\u5fc3\u6293\u53bb\u3002\u8fd9\u4e00\u6b21\u90a3\u4e66\u751f\u7adf\u662f\u6ca1\u80fd\u907f\u5f00\uff0c\u88ab\u4ed6\u63d0\u8d77\u8eab\u5b50\uff0c\u91cd\u91cd\u7684\u5f80\u5730\u4e0b\u4e00\u6454\u3002\u539f\u6765\u54c8\u8d64\u662f\u8499\u53e4\u7684\u6454\u4ea4\u9ad8\u624b\uff0c\u8499\u53e4\u6454\u4ea4\u4e4b\u6280\uff0c\u5171\u5206\u5927\u6293\u3001\u4e2d\u6293\u3001\u5c0f\u6293\u4e09\u95e8\uff0c\u5404\u6709\u5389\u5bb3\u7edd\u6280\u3002\u54c8\u8d64\u662f\u4e2d\u6293\u95e8\u7684\u638c\u95e8\u4eba\uff0c\u6700\u64c5\u957f\u8170\u817f\u4e4b\u52b2\uff0c\u6293\u4eba\u80f8\u80cc\uff0c\u767e\u53d1\u767e\u4e2d\u3002 \u3000\u3000\u90a3\u4e66\u751f\u88ab\u4ed6\u4e00\u6293\u4e00\u6454\uff0c\u773c\u770b\u8981\u5403\u4e2a\u5c0f\u4e8f\uff0c\u90a3\u77e5\u660e\u660e\u89c1\u5230\u4ed6\u662f\u80cc\u810a\u5411\u4e0b\uff0c\u843d\u5730\u65f6\u5374\u662f\u53cc\u811a\u5148\u7740\u3002\u4ed6\u817f\u4e0a\u5982\u540c\u88c5\u4e0a\u673a\u62ec\uff0c\u4e00\u7740\u5730\u7acb\u523b\u5f39\u8d77\uff0c\u7b11\u563b\u563b\u7684\u7ad9\u7740\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u6454\u6211\u4e0d\u5012\u3002\u201d\u54c8\u8d64\u9053\uff1a\u201c\u518d\u6765\uff01\u201d\u90a3\u4e66\u751f\u9053\uff1a\u201c\u597d\uff0c\u518d\u6765\uff01\u201d\u8d70\u8fd1\u8eab\u53bb\uff0c\u7a81\u7136\u4f38\u51fa\u53cc\u624b\uff0c\u626d\u4f4f\u4ed6\u7684\u80f8\u53e3\u3002\u4f17\u4eba\u90fd\u662f\u5927\u4e3a\u5947\u602a\uff0c\u54c8\u8d64\u9b41\u68a7\u5947\u4f1f\uff0c\u90a3\u4e66\u751f\u5374\u7626\u7626\u5c0f\u5c0f\uff0c\u4f55\u51b5\u54c8\u8d64\u64c5\u4e8e\u6454\u4ea4\uff0c\u4eba\u4eba\u4eb2\u89c1\uff0c\u90a3\u4e66\u751f\u548c\u4ed6\u76f8\u6597\uff0c\u82e5\u4e0d\u65bd\u5c55\u8f7b\u529f\uff0c\u4fbf\u5f53\u4ee5\u5de7\u5999\u62f3\u62db\u53d6\u80dc\uff0c\u600e\u5730\u7adf\u662f\u4ee5\u5df1\u4e4b\u77ed\uff0c\u653b\u654c\u4e4b\u957f\uff1f \u3000\u3000\u54c8\u8d64\u5f53\u5373\u4f38\u624b\u6293\u4e66\u751f\u80a9\u5934\uff0c\u51fa\u811a\u6a2a\u626b\u3002\u90a3\u4e66\u751f\u5411\u524d\u4e00\u8dcc\uff0c\u6402\u4f4f\u4e86\u54c8\u8d64\u7c97\u5927\u7684\u8116\u5b50\uff0c\u53cc\u8db3\u8db3\u5c16\u540c\u65f6\u5f80\u54c8\u8d64\u819d\u76d6\u91cc\u8e22\u53bb\u3002\u54c8\u8d64\u53cc\u817f\u4e00\u8f6f\uff0c\u5411\u524d\u8dea\u5012\u3002\u4f46\u4ed6\u867d\u8d25\u4e0d\u4e71\uff0c\u53cd\u624b\u6293\u4f4f\u90a3\u4e66\u751f\u7684\u80cc\u5fc3\uff0c\u5c06\u4ed6\u626d\u8fc7\u6765\u538b\u5728\u8eab\u4e0b\u3002\u90a3\u4e66\u751f\u5927\u53eb\uff1a\u201c\u4e0d\u5f97\u4e86\uff0c\u4e0d\u5f97\u4e86\uff01\u201d \u3000\u3000\u4ece\u4ed6\u814b\u7a9d\u5e95\u4e0b\u63a2\u5934\u51fa\u6765\uff0c\u4f38\u4f38\u820c\u5934\uff0c\u88c5\u4e2a\u9b3c\u8138\u3002 \u3000\u3000\u6b64\u65f6\u80e1\u6590\u3001\u6c64\u6c9b\u3001\u6d77\u5170\u5f3c\u7b49\u9ad8\u624b\u5fc3\u4e0b\u90fd\u5df2\u96ea\u4eae\uff0c\u8fd9\u4e66\u751f\u7cbe\u4e8e\u70b9\u7a74\u6253\u7a74\uff0c\u54c8\u8d64\u7edd\u4e0d\u662f\u4ed6\u7684\u5bf9\u624b\uff0c\u800c\u4e14\u8fd9\u4e66\u751f\u4e8e\u6454\u4ea4\u76f8\u6251\u4e4b\u672f\u4e5f\u751a\u5a34\u719f\uff0c\u867d\u7136\u8182\u529b\u4e0d\u53ca\u54c8\u8d64\uff0c\u53ef\u662f\u624b\u811a\u6ed1\u6e9c\uff0c\u626d\u6597\u65f6\u6bcf\u6bcf\u4ece\u7edd\u5883\u4e2d\u8131\u56f0\u800c\u51fa\u3002\u4ed6\u6240\u4ee5\u4e0d\u5c06\u54c8\u8d64\u6253\u5012\uff0c\u663e\u662f\u5bf9\u4ed6\u4e0d\u5b58\u654c\u610f\uff0c\u53ea\u662f\u501f\u7740\u4ed6\u73a9\u95f9\u7b11\u4e50\uff0c\u8981\u4ee4\u798f\u5eb7\u5b89\u548c\u56db\u5927\u638c\u95e8\u4eba\u8138\u4e0a\u65e0\u5149\u3002 \u3000\u3000\u53e6\u4e00\u8fb9\u6851\u98de\u8679\u5c55\u5f00\u5c0f\u5de7\u529f\u592b\uff0c\u548c\u4e0a\u5b98\u94c1\u751f\u6e38\u6597\u4e0d\u4f11\u3002\u5979\u51e4\u9633\u5e9c\u4e94\u6e56\u95e8\u6700\u64c5\u957f\u7684\u6b66\u529f\u4e43\u662f\u201c\u94c1\u83b2\u529f\u201d\uff0c\u978b\u5c16\u4e0a\u5305\u4ee5\u5c16\u94c1\uff0c\u82e5\u662f\u8e22\u4e2d\u8981\u5bb3\uff0c\u7acb\u53ef\u53d6\u4eba\u6027\u547d\u3002\u4e0a\u5b98\u94c1\u751f\u6d6a\u8361\u6c5f\u6e56\u6570\u5341\u5e74\uff0c\u5982\u4f55\u4e0d\u7701\u5f97\u5979\u7684\u5389\u5bb3\uff1f\u6bcf\u89c1\u5979\u978b\u5c16\u8e22\u6765\uff0c\u6025\u5fd9\u5f15\u8eab\u95ea\u907f\u3002\u4ed6\u662f\u6c5f\u6e56\u4e0a\u7684\u6210\u540d\u4eba\u7269\uff0c\u548c\u8fd9\u5e74\u8f7b\u59d1\u5a18\u6597\u4e86\u8fd1\u767e\u62db\uff0c\u7adf\u7136\u4e1d\u6beb\u4e0d\u5360\u4e0a\u98ce\uff0c\u773c\u89c1\u5979\u9e33\u9e2f\u817f\u3001\u62d0\u5b50\u817f\u3001\u5708\u5f39\u817f\u3001\u94a9\u626b\u817f\u3001\u7a7f\u5fc3\u817f\u3001\u649e\u5fc3\u817f\u3001\u5355\u98de\u817f\u3001\u53cc\u98de\u817f\uff0c\u5c42\u51fa\u4e0d\u7a77\uff0c\u8d8a\u6765\u8d8a\u5feb\uff0c\u5fc3\u4e0b\u7126\u8e81\u8d77\u6765\uff0c\u770b\u6765\u82e5\u8981\u53d6\u80dc\uff0c\u987b\u5f97\u91cd\u65bd\u6545\u6280\uff0c\u4e8e\u662f\u8001\u6c14\u6a2a\u79cb\u5730\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6a2a\u8e22\u7ad6\u8e22\uff0c\u6709\u4ec0\u4e48\u7528\uff1f\u201d\u88c5\u4f5c\u6f2b\u4e0d\u5728\u4e4e\uff0c\u51d1\u53e3\u5230\u70df\u7ba1\u4e0a\u53bb\u6df1\u6df1\u5438\u4e86\u4e00\u4e0b\u3002 \u3000\u3000\u6851\u98de\u8679\u89c1\u4ed6\u5438\u70df\uff0c\u5df2\u81ea\u63d0\u9632\uff0c\u6025\u5fd9\u62a2\u5230\u4e0a\u98ce\uff0c\u9632\u4ed6\u55b7\u70df\u3002 \u3000\u3000\u4e0a\u5b98\u94c1\u751f\u5438\u4e86\u8fd9\u53e3\u70df\u540e\uff0c\u53c8\u62c6\u5f97\u6570\u62db\uff0c\u6e10\u6e10\u53cc\u76ee\u5706\u77aa\uff0c\u5411\u524d\u76f4\u89c6\uff0c\u773c\u4e2d\u9732\u51fa\u75af\u72d7\u822c\u7684\u51f6\u5149\uff0c\u7a81\u7136\u201c\u80e1\u80e1\u201d\u5927\u53eb\uff0c\u5411\u6851\u98de\u8679\u6251\u4e86\u8fc7\u53bb\u3002\u6851\u98de\u8679\u89c1\u4e86\u8fd9\u795e\u60c5\uff0c\u5fc3\u4e2d\u5bb3\u6015\uff0c\u4e0d\u6562\u6b63\u9762\u4e0e\u6597\uff0c\u95ea\u8eab\u907f\u5728\u4e00\u65c1\u3002\u4e0a\u5b98\u94c1\u751f\u8db3\u4e0d\u505c\u6b65\u7684\u5411\u524d\u76f4\u51b2\uff0c\u201c\u80e1\u201d\u7684\u4e00\u58f0\u5927\u53eb\uff0c\u5374\u5411\u798f\u5eb7\u5b89\u6251\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u7ad9\u5728\u798f\u5eb7\u5b89\u8eab\u8fb9\u6700\u8fd1\u7684\u536b\u58eb\u662f\u9b54\u722a\u96c1\u884c\u95e8\u7684\u66fe\u94c1\u9e25\uff0c\u5ffd\u89c1\u4e0a\u5b98\u94c1\u751f\u72af\u4e0a\u4f5c\u4e71\uff0c\u6025\u5fd9\u62a2\u4e0a\u52fe\u4f4f\u4ed6\u624b\u8155\uff0c\u5411\u5916\u4e00\u7529\u3002\u4e0a\u5b98\u94c1\u751f\u4e00\u4e2a\u8e09\u8dc4\uff0c\u8dcc\u4e86\u51fa\u53bb\uff0c\u773c\u775b\u53d1\u76f4\uff0c\u5411\u4e1c\u9996\u5e2d\u4e0a\u51b2\u4e86\u8fc7\u53bb\uff0c\u4e71\u6293\u4e71\u6253\uff0c\u7adf\u662f\u75af\u4e86\u3002 \u3000\u3000\u80e1\u6590\u659c\u773c\u77a7\u7740\u7a0b\u7075\u7d20\uff0c\u89c1\u5979\u4f3c\u7b11\u975e\u7b11\uff0c\u65b9\u59cb\u660e\u767d\u5979\u9002\u624d\u5c06\u70df\u7ba1\u8fd8\u7ed9\u4e0a\u5b98\u94c1\u751f\u7684\u7528\u610f\uff0c\u539f\u6765\u5979\u4e8e\u9877\u523b\u4e4b\u95f4\uff0c\u5728\u70df\u6597\u4e4b\u4e2d\u88c5\u4e0a\u4e86\u53e6\u4e00\u79cd\u5389\u5bb3\u8ff7\u836f\uff0c\u5373\u4ee5\u5176\u4eba\u4e4b\u9053\uff0c\u8fd8\u6cbb\u5176\u4eba\u4e4b\u8eab\uff0c\u4ee4\u8fd9\u4e00\u751f\u4ee5\u8ff7\u836f\u5bb3\u4eba\u7684\u4e0a\u5b98\u94c1\u751f\uff0c\u5728\u81ea\u5df1\u7684\u70df\u7ba1\u4e2d\u5438\u8fdb\u8ff7\u836f\u3002\u8fd9\u8ff7\u836f\u5165\u8111\uff0c\u767b\u65f6\u795e\u667a\u8ff7\u4e71\uff0c\u5982\u766b\u5982\u72c2\uff0c\u4ed6\u539f\u6765\u53e3\u4e2d\u6240\u542b\u7684\u89e3\u836f\u5168\u4e0d\u7ba1\u7528\u3002 \u3000\u3000\u4e1c\u9996\u5e2d\u4e0a\u7684\u597d\u624b\u89c1\u4ed6\u51b2\u5230\uff0c\u81ea\u5373\u51fa\u624b\u5c06\u4ed6\u8d76\u5f00\u3002\u4e0a\u5b98\u94c1\u751f\u5728\u5730\u4e0b\u6253\u4e86\u4e2a\u6eda\uff0c\u5ffd\u7136\u62b1\u4f4f\u4e00\u5f20\u684c\u5b50\u7684\u684c\u817f\uff0c\u5f20\u53e3\u4e71\u5543\u4e71\u54ac\u3002\u4f17\u4eba\u89c1\u4e86\u8fd9\u7b49\u60c5\u666f\uff0c\u90fd\u662f\u6697\u6697\u60ca\u6016\uff0c\u8c01\u4e5f\u7b11\u4e0d\u51fa\u6765\uff0c\u4e0d\u77e5\u4ed6\u4f55\u4ee5\u4f1a\u7a81\u7136\u5982\u6b64\u3002 \u3000\u3000\u4f17\u4eba\u4e00\u65f6\u9ed8\u4e0d\u4f5c\u58f0\uff0c\u5927\u5385\u4e4b\u4e0a\uff0c\u53ea\u542c\u5f97\u54c8\u8d64\u5728\u201c\u5c0f\u755c\u751f\u3001\u8d3c\u79c0\u624d\u201d\u7684\u9a82\u4e0d\u7edd\u53e3\u3002\u90a3\u4e66\u751f\u9053\uff1a\u201c\u6211\u529d\u4f60\u522b\u9a82\u4e86\u5427\u3002\u201d\u54c8\u8d64\u6012\u9053\uff1a\u201c\u6211\u9a82\u4f60\u4fbf\u600e\u6837\uff1f\u8d3c\u79c0\u624d\uff01\u201d\u90a3\u4e66\u751f\u9053\uff1a\u201c\u8c05\u4f60\u4e5f\u4e0d\u6562\u9a82\u798f\u5927\u5e05\uff0c\u4f60\u6709\u79cd\u7684\uff0c\u4fbf\u9a82\u4e00\u58f0\u8d3c\u5927\u5e05\u3002\u201d \u3000\u3000\u54c8\u8d64\u6c14\u607c\u5934\u4e0a\uff0c\u4e0d\u52a0\u8003\u8651\uff0c\u968f\u53e3\u4fbf\u5927\u58f0\u9a82\u9053\uff1a\u201c\u8d3c\u5927\u5e05\uff01\u201d \u3000\u3000\u8bdd\u4e00\u51fa\u53e3\uff0c\u624d\u77e5\u4e0d\u5999\uff0c\u4f46\u5df2\u7ecf\u6536\u4e0d\u56de\u8f6c\uff0c\u6025\u5f97\u53ea\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u4e0d\u662f\u9a82\u4ed6\uff0c\u662f\u2026\u2026\u662f\u2026\u2026\u9a82\u4f60\uff01\u201d\u90a3\u4e66\u751f\u7b11\u9053\uff1a\u201c\u6211\u53c8\u4e0d\u505a\u5927\u5e05\uff0c\u4f60\u9a82\u6211\u8d3c\u5927\u5e05\u5e72\u4e48\uff1f\u201d \u3000\u3000\u54c8\u8d64\u4e0a\u4e86\u8fd9\u4e2a\u5f53\uff0c\u751f\u6015\u798f\u5eb7\u5b89\u89c1\u8d23\uff0c\u53ea\u6025\u5f97\u989d\u5934\u9752\u7b4b\u66b4\u73b0\uff0c\u6ee1\u8138\u901a\u7ea2\uff0c\u548c\u8eab\u6251\u4e86\u4e0b\u6765\uff0c\u90a3\u4e66\u751f\u4e58\u4ed6\u5fc3\u795e\u604d\u60da\uff0c\u4fa7\u8eab\u4e00\u8ba9\uff0c\u63ea\u7740\u4ed6\u53f3\u81c2\u501f\u529b\u4e00\u9001\uff0c\u54c8\u8d64\u4e00\u4e2a\u80a5\u5927\u7684\u8eab\u8eaf\u98de\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u4e0a\u5b98\u94c1\u751f\u6b63\u62b1\u4f4f\u684c\u817f\u72c2\u54ac\uff0c\u54c8\u8d64\u6454\u5c06\u4e0b\u6765\uff0c\u817e\u7684\u4e00\u54cd\uff0c\u6070\u597d\u538b\u5728\u4ed6\u80cc\u4e0a\u3002 \u3000\u3000\u4e0a\u5b98\u94c1\u751f\u201c\u80e1\u80e1\u201d\u5927\u53eb\uff0c\u62b1\u7262\u4ed6\u53cc\u81c2\uff0c\u4e00\u53e3\u5f80\u4ed6\u7684\u5149\u5934\u5927\u8111\u888b\u4e0a\u54ac\u843d\u3002\u54c8\u8d64\u5403\u75db\uff0c\u632f\u81c2\u6b32\u5c06\u4ed6\u6454\u5f00\u3002\u90a3\u77e5\u4e00\u4e2a\u4eba\u795e\u667a\u80e1\u6d82\u4e4b\u540e\uff0c\u7adf\u4f1a\u751f\u51fa\u5e73\u7d20\u6240\u65e0\u7684\u5de8\u529b\u51fa\u6765\uff0c\u54c8\u8d64\u7684\u8182\u529b\u672c\u6765\u6bd4\u4ed6\u5f3a\u5f97\u591a\uff0c\u8fd9\u65f6\u5374\u8131\u4e0d\u51fa\u4ed6\u7684\u6402\u62b1\uff0c\u53ea\u7ed9\u4ed6\u54ac\u5f97\u6ee1\u5934\u9c9c\u8840\u6dcb\u6f13\uff0c\u76f4\u75db\u5f97\u54c7\u54c7\u6025\u53eb\u3002 \u3000\u3000\u90a3\u4e66\u751f\u54c8\u54c8\u5927\u7b11\uff0c\u53eb\u9053\uff1a\u201c\u5999\u6781\uff0c\u5999\u6781\uff01\u201d\u4ed6\u4e00\u9762\u9f13\u638c\uff0c\u4e00\u9762\u6162\u6162\u9000\u5411\u653e\u7740\u516b\u53ea\u7389\u9f99\u676f\u7684\u8336\u51e0\uff0c\u7a81\u7136\u95f4\u8863\u8896\u4e00\u62c2\uff0c\u6293\u8d77\u4e24\u53ea\u7389\u9f99\u676f\uff0c\u5bf9\u6851\u98de\u8679\u9053\uff1a\u201c\u5fa1\u676f\u5df2\u5f97\uff0c\u54b1\u4eec\u8d70\u5427\uff01\u201d \u3000\u3000\u6851\u98de\u8679\u4e00\u6014\uff0c\u5979\u548c\u8fd9\u4e66\u751f\u7d20\u4e0d\u76f8\u8bc6\uff0c\u4f46\u89c1\u4ed6\u5bf9\u81ea\u5df1\u4e00\u76f4\u751a\u662f\u4eb2\u5207\uff0c\u4e0d\u81ea\u7981\u7684\u70b9\u4e86\u70b9\u5934\uff0c\u968f\u7740\u4ed6\u98de\u5954\u51fa\u5916\u3002 \u3000\u3000\u798f\u5eb7\u5b89\u8eab\u65c1\u7684\u516d\u4e03\u540d\u536b\u58eb\u5927\u547c\uff1a\u201c\u6349\u5978\u7ec6\uff01\u6349\u5978\u7ec6\uff01\u201d\u201c\u62ff\u4f4f\u4e86\uff01\u201d\u201c\u62ff\u4f4f\u5077\u5fa1\u676f\u7684\u8d3c\uff01\u201d\u4e00\u9f50\u8702\u62e5\u7740\u8ffd\u4e86\u51fa\u6765\u3002 \u3000\u3000\u7fa4\u8c6a\u89c1\u8fd9\u5c11\u5e74\u4e66\u751f\u5728\u4f17\u76ee\u777d\u777d\u4e4b\u4e0b\uff0c\u7adf\u5c14\u5927\u80c6\u53d6\u676f\u6b32\u884c\uff0c\u65e0\u4e0d\u60ca\u9a87\uff0c\u65e9\u6709\u4eba\u8ddf\u7740\u4f17\u536b\u58eb\u559d\u4e86\u8d77\u6765\uff1a\u201c\u653e\u4e0b\u7389\u676f\uff01\u201d\u201c\u4ec0\u4e48\u4eba\uff0c\u8fd9\u822c\u80e1\u95f9\uff1f\u201d\u201c\u662f\u54ea\u4e00\u5bb6\u54ea\u4e00\u6d3e\u7684\u6df7\u5e10\u4e1c\u897f\uff1f\u201d \u3000\u3000\u9002\u624d\u5e38\u8d6b\u5fd7\u3001\u5e38\u4f2f\u5fd7\u5144\u5f1f\u4ece\u5c4b\u9876\u4e0a\u51b2\u5165\uff0c\u6551\u53bb\u4e86\u8d35\u5dde\u53cc\u5b50\u95e8\u502a\u6c0f\u5144\u5f1f\uff0c\u798f\u5eb7\u5b89\u5e9c\u4e2d\u536b\u58eb\u5728\u5927\u95e8\u5916\u53c8\u589e\u6dfb\u4eba\u5458\uff0c\u8fd9\u65f6\u542c\u89c1\u5927\u5385\u4e2d\u4e00\u7247\u5406\u559d\u4e4b\u58f0\uff0c\u95e8\u5916\u7684\u536b\u58eb\u7acb\u65f6\u5c06\u95e8\u5835\u4f4f\u3002\u5b89\u63d0\u7763\u4e00\u58f0\u4ee4\u4e0b\uff0c\u6570\u5341\u540d\u536b\u58eb\u5c06\u90a3\u5c11\u5e74\u4e66\u751f\u548c\u6851\u98de\u8679\u524d\u540e\u56f4\u4f4f\u3002 \u3000\u3000\u90a3\u4e66\u751f\u7b11\u9053\uff1a\u201c\u8c01\u6562\u4e0a\u6765\uff0c\u6211\u5c31\u5c06\u7389\u676f\u4e00\u6454\uff0c\u77a7\u5b83\u788e\u662f\u4e0d\u788e\u3002\u201d\u4f17\u536b\u58eb\u5012\u4e5f\u4e0d\u6562\u8d38\u7136\u4e0a\u524d\uff0c\u751f\u6015\u4ed6\u5f53\u771f\u8c41\u51fa\u4e86\u6027\u547d\u80e1\u6765\uff0c\u5c06\u5fa1\u8d50\u7684\u7389\u676f\u6454\u7834\u4e86\u3002\u5404\u4eba\u624b\u6267\u5175\u5203\uff0c\u5c06\u4e8c\u4eba\u5305\u56f4\u4e86\u4e2a\u5bc6\u4e0d\u901a\u98ce\u3002 \u3000\u3000\u6851\u98de\u8679\u53d7\u9080\u6765\u53c2\u4e0e\u8fd9\u638c\u95e8\u4eba\u5927\u4f1a\uff0c\u53ea\u662f\u6765\u8d76\u4e00\u4e2a\u70ed\u95f9\uff0c\u5e76\u65e0\u522b\u610f\uff0c\u7a81\u7136\u95f4\u95ef\u51fa\u8fd9\u4e2a\u5927\u7978\u6765\uff0c\u53ea\u5413\u5f97\u8138\u8272\u60e8\u767d\uff0c\u4e00\u9897\u5fc3\u51e0\u4e4e\u8981\u8df3\u51fa\u4e86\u8154\u5b50\u3002 \u3000\u3000\u80e1\u6590\u5bf9\u7a0b\u7075\u7d20\u5bf9\u671b\u4e00\u773c\uff0c\u7a0b\u7075\u7d20\u7f13\u7f13\u7684\u6447\u4e86\u6447\u5934\u3002\u4e24\u4eba\u867d\u5bf9\u90a3\u5c11\u5e74\u4e66\u751f\u751a\u6709\u597d\u611f\uff0c\u4f46\u8fd9\u65f6\u8eab\u9677\u91cd\u56f4\u4e4b\u4e2d\uff0c\u5982\u679c\u51fa\u624b\u76f8\u6551\uff0c\u53ea\u4e0d\u8fc7\u767d\u9976\u4e0a\u4e24\u6761\u6027\u547d\uff0c\u4e8e\u4e8b\u65e0\u8865\u3002\u773c\u770b\u8fd9\u5c40\u52bf\u65e0\u6cd5\u957f\u4e45\u50f5\u6301\uff0c\u6d77\u5170\u5f3c\u6b63\u5927\u8e0f\u6b65\u8d70\u5c06\u8fc7\u53bb\uff0c\u4ed6\u4e00\u51fa\u624b\uff0c\u90a3\u4e66\u751f\u548c\u6851\u98de\u8679\u5b9a\u7136\u62b5\u6321\u4e0d\u4f4f\u3002 \u3000\u3000\u90a3\u4e66\u751f\u9ad8\u4e3e\u7389\u676f\uff0c\u7b11\u541f\u541f\u7684\u9053\uff1a\u201c\u6851\u59d1\u5a18\uff0c\u8fd9\u4e00\u6b21\u54b1\u4eec\u53ef\u5f97\u6539\u4e2a\u4e3b\u610f\u5566\uff0c\u4f60\u82e5\u662f\u5c06\u7389\u676f\u5f80\u5730\u4e0b\u6454\u53bb\uff0c\u8bf4\u4e0d\u5b9a\u8fd8\u6ca1\u78b0\u5230\u5730\u4e0a\uff0c\u5df2\u6709\u5feb\u624b\u5feb\u811a\u7684\u5bb6\u4f19\u62a2\u7740\u63a5\u4e86\u53bb\u3002\u54b1\u4eec\u4e0d\u5982\u8fd9\u6837\u5427\uff0c\u4f60\u542c\u6211\u53eb\u4e00\u4e8c\u4e09\uff0c\u53eb\u5230\u2018\u4e09\u2019\u5b57\uff0c\u5580\u5587\u4e00\u54cd\uff0c\u5c31\u5728\u624b\u4e2d\u634f\u788e\u4e86\u3002\u201d \u3000\u3000\u6851\u98de\u8679\u4e0d\u7531\u81ea\u4e3b\u7684\u70b9\u4e86\u70b9\u5934\uff0c\u5fc3\u4e2d\u5374\u5728\u6697\u9a82\u81ea\u5df1\uff0c\u4e3a\u4ec0\u4e48\u8ddf\u4ed6\u7d20\u4e0d\u76f8\u8bc6\uff0c\u5374\u4e8b\u4e8b\u542c\u4ed6\u6307\u4f7f\u3002 \u3000\u3000\u6d77\u5170\u5f3c\u8d70\u4e0a\u524d\u53bb\uff0c\u539f\u662f\u6253\u7b97\u5728\u4ed6\u6454\u51fa\u7389\u676f\u65f6\u5feb\u624b\u63a5\u8fc7\uff0c\u542c\u4ed6\u8fd9\u51e0\u53e5\u8bdd\u4e00\u8bf4\uff0c\u767b\u65f6\u505c\u4f4f\u4e86\u811a\u6b65\u3002 \u3000\u3000\u6c64\u6c9b\u54c8\u54c8\u4e00\u7b11\uff0c\u8d70\u5230\u4e66\u751f\u8ddf\u524d\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4f60\u8d35\u59d3\u5927\u540d\u554a\uff1f\u4eca\u65e5\u5728\u5929\u4e0b\u82f1\u96c4\u4e4b\u524d\u5927\u5927\u7684\u9732\u4e86\u4e00\u4e0b\u8138\uff0c\u5f53\u771f\u662f\u8038\u52a8\u6b66\u6797\u3002\u4f60\u4e0d\u7559\u4e0b\u4e2a\u540d\u513f\uff0c\u90a3\u600e\u4e48\u6210\uff1f\u201d\u90a3\u4e66\u751f\u7b11\u9053\uff1a\u201c\u5728\u4e0b\u4e00\u4e0d\u4e3a\u540d\uff0c\u4e8c\u4e0d\u4e3a\u5229\uff0c\u53ea\u89c9\u8fd9\u7389\u676f\u513f\u597d\u73a9\uff0c\u60f3\u62ff\u56de\u5bb6\u53bb\u73a9\u73a9\uff0c\u73a9\u5f97\u538c\u4e86\uff0c\u4fbf\u5373\u5949\u8fd8\u3002\u201d \u3000\u3000\u6c64\u6c9b\u7b11\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4f60\u7684\u6b66\u529f\u5f88\u7279\u5f02\uff0c\u8001\u54e5\u54e5\u7528\u5fc3\u77a7\u4e86\u534a\u5929\uff0c\u4e5f\u77a7\u4e0d\u51fa\u4e00\u4e2a\u95e8\u9053\u6765\u3002\u5c0a\u5e08\u662f\u54ea\u4e00\u4f4d\u554a\uff1f\u8bf4\u8d77\u6765\u6216\u8bb8\u5927\u5bb6\u90fd\u6709\u4ea4\u60c5\u3002\u5e74\u8f7b\u4eba\u5f00\u4e2a\u5c0f\u73a9\u7b11\uff0c\u4e5f\u6ca1\u4ec0\u4e48\u5927\u4e0d\u4e86\uff0c\u51b2\u7740\u8001\u54e5\u54e5\u8fd9\u70b9\u5c0f\u9762\u5b50\uff0c\u798f\u5927\u5e05\u4e5f\u4e0d\u80fd\u602a\u7f6a\uff0c\u8fd8\u662f\u5165\u5e2d\u518d\u559d\u9152\u5427\u3002\u201d \u3000\u3000\u8bf4\u7740\u4fa7\u5934\u5411\u4f17\u536b\u58eb\u9053\uff1a\u201c\u5927\u4f19\u513f\u9000\u5f00\u4e9b\uff01\u8fd9\u4f4d\u5144\u5f1f\u662f\u597d\u670b\u53cb\uff0c\u4ed6\u5f00\u4e2a\u73a9\u7b11\uff0c\u5374\u6765\u8fd9\u4e48\u5174\u5e08\u52a8\u4f17\u7684\uff0c\u4e0d\u8ba9\u4eba\u5bb6\u7b11\u8bdd\u54b1\u4eec\u592a\u8fc7\u5c0f\u6c14\u4e48\uff1f\u201d\u4f17\u536b\u58eb\u542c\u4ed6\u8fd9\u4e48\u8bf4\uff0c\u90fd\u9000\u5f00\u4e86\u4e24\u6b65\u3002 \u3000\u3000\u90a3\u4e66\u751f\u7b11\u9053\uff1a\u201c\u59d3\u6c64\u7684\uff0c\u6211\u53ef\u4e0d\u5165\u4f60\u8fd9\u7b11\u9762\u8001\u864e\u7684\u5708\u5957\u3002 \u3000\u3000\u4f60\u518d\u8d70\u8fd1\u4e00\u6b65\uff0c\u6211\u4fbf\u628a\u7389\u676f\u634f\u788e\u4e86\u3002\u4f60\u82e5\u662f\u771f\u6709\u62c5\u5f53\uff0c\u4fbf\u8ba9\u6211\u628a\u7389\u676f\u501f\u56de\u5bb6\u53bb\uff0c\u628a\u73a9\u4e09\u5929\u3002\u4e09\u65e5\u4e4b\u540e\uff0c\u4e00\u51c6\u5949\u8fd8\u3002\u201d \u3000\u3000\u4f17\u4eba\u5fc3\u60f3\uff1a\u201c\u4f60\u62ff\u4e86\u7389\u676f\u4e00\u51fa\u5927\u95e8\uff0c\u5374\u5230\u54ea\u91cc\u518d\u53bb\u627e\u4f60\uff1f \u3000\u3000\u4ec0\u4e48\u4e09\u65e5\u4e4b\u540e\u4e00\u5b9a\u5949\u8fd8\uff0c\u8c01\u6765\u4fe1\u4f60\uff1f\u201d\u5404\u4eba\u7684\u76ee\u5149\u4e00\u9f50\u671b\u7740\u6c64\u6c9b\uff0c\u77a7\u4ed6\u5982\u4f55\u56de\u7b54\u3002 \u3000\u3000\u53ea\u89c1\u4ed6\u53c8\u662f\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u53c8\u6709\u4ec0\u4e48\u6253\u7d27\uff1f\u5c0f\u5144\u5f1f\uff0c\u4f60\u624b\u91cc\u8fd9\u53ea\u7389\u676f\u561b\uff0c\u4e3b\u513f\u7684\u540d\u4efd\u8fd8\u6ca1\u5b9a\u3002\u8001\u54e5\u54e5\u5374\u8499\u798f\u5927\u5e05\u7684\u6069\u5178\u5148\u8d4f\u4e86\u4e00\u53ea\u3002\u8fd9\u6837\u5427\uff0c\u6211\u81ea\u5df1\u7684\u90a3\u53ea\u501f\u7ed9\u4f60\uff0c\u4f60\u7231\u73a9\u5230\u51e0\u65f6\u4fbf\u51e0\u65f6\uff0c\u4ec0\u4e48\u65f6\u5019\u73a9\u5f97\u538c\u4e86\uff0c\u5e26\u4e2a\u4fe1\u6765\uff0c\u6211\u518d\u6765\u53d6\u56de\u5c31\u662f\u4e86\u3002\u201d\u8bf4\u7740\u8d70\u5230\u653e\u7389\u676f\u7684\u51e0\u524d\uff0c\u5148\u53d6\u8fc7\u4e00\u5757\u94fa\u5728\u684c\u4e0a\u7684\u5927\u9526\u7f0e\uff0c\u515c\u5728\u5de6\u624b\u4e4b\u4e0a\uff0c\u7136\u540e\u53d6\u8fc7\u4e00\u53ea\u7389\u9f99\u676f\uff0c\u653e\u5728\u9526\u7f0e\u4e0a\uff0c\u90d1\u800c\u91cd\u4e4b\u7684\u8d70\u5230\u90a3\u4e66\u751f\u8ddf\u524d\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u62ff\u53bb\u5427\uff01\u201d \u3000\u3000\u8fd9\u4e00\u7740\u5927\u51fa\u4eba\u4eba\u7684\u610f\u6599\u4e4b\u5916\u3002\u4f17\u4eba\u53ea\u9053\u4ed6\u5634\u91cc\u8bf4\u5f97\u6f02\u4eae\uff0c\u5b9e\u5219\u662f\u5728\u60f3\u4e58\u673a\u593a\u56de\u4e66\u751f\u624b\u4e2d\u7684\u7389\u676f\uff0c\u54ea\u77e5\u4ed6\u501f\u676f\u4e4b\u8a00\u5e76\u975e\u865a\u8bdd\uff0c\u53cd\u800c\u53c8\u9001\u4e00\u53ea\u7389\u676f\u8fc7\u53bb\u3002 \u3000\u3000\u90a3\u4e66\u751f\u4e5f\u662f\u9887\u4e3a\u8be7\u5f02\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u5916\u53f7\u513f\u53eb\u505a\u2018\u7518\u9716\u60e0\u4e03\u7701\u2019\uff0c\u679c\u7136\u662f\u6177\u6168\u5f97\u7d27\u3002\u4e24\u53ea\u7389\u676f\u4e00\u6a21\u4e00\u6837\uff0c\u4e5f\u4e0d\u7528\u6389\u4e86\u3002\u6851\u59d1\u5a18\u7684\u7389\u676f\uff0c\u5c31\u7b97\u662f\u5411\u8fd9\u4f4d\u6d77\u5927\u4eba\u501f\u7684\u3002\u6c64\u5927\u4fa0\uff0c\u70e6\u4f60\u4f5c\u4e2a\u4e2d\u4fdd\u3002\u6d77\u5927\u4eba\uff0c\u8bf7\u4f60\u653e\u5fc3\uff0c\u4e09\u65e5\u4e4b\u540e\u6851\u59d1\u5a18\u82e5\u662f\u4e0d\u4ea4\u8fd8\u7389\u676f\uff0c\u4f60\u552f\u6c64\u5927\u4fa0\u662f\u95ee\u3002\u201d\u6c64\u6c9b\u7b11\u9053\uff1a\u201c\u597d\u5427\uff01\u628a\u4e8b\u513f\u90fd\u63fd\u5728\u6211\u8eab\u4e0a\uff0c\u59d3\u6c64\u7684\u4e00\u529b\u627f\u5f53\u3002\u6851\u59d1\u5a18\uff0c\u4f60\u603b\u4e0d\u8be5\u53eb\u6211\u4e3a\u96be\u7f62\uff1f\u201d\u8bf4\u7740\u5411\u6851\u98de\u8679\u8d70\u8fd1\u4e86\u4e00\u6b65\u3002 \u3000\u3000\u6851\u98de\u8679\u55eb\u5685\u7740\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u2026\u2026\u201d\u773c\u671b\u90a3\u5c11\u5e74\u4e66\u751f\uff0c\u4e0d\u77e5\u5982\u4f55\u56de\u7b54\u624d\u662f\u3002 \u3000\u3000\u6c64\u6c9b\u5de6\u8098\u7a81\u7136\u4e00\u6296\uff0c\u4e00\u4e2a\u8098\u9525\uff0c\u649e\u5728\u5979\u53f3\u8155\u8155\u5e95\u3002\u6851\u98de\u8679\u201c\u554a\u201d\u7684\u4e00\u58f0\u60ca\u547c\uff0c\u7389\u676f\u8131\u624b\u5411\u4e0a\u98de\u51fa\uff0c\u4fbf\u5728\u6b64\u65f6\uff0c\u6c64\u6c9b\u53f3\u624b\u6293\u8d77\u9526\u7f0e\u4e0a\u7389\u676f\uff0c\u5de6\u624b\u9526\u7f0e\u6325\u51fa\uff0c\u5df2\u5c06\u90a3\u5c11\u5e74\u4e0a\u8eab\u88f9\u4f4f\u3002 \u3000\u3000\u53f3\u624b\u98df\u6307\u8fde\u52a8\uff0c\u9694\u7740\u9526\u7f0e\u70b9\u4e2d\u4e86\u4ed6\u201c\u4e91\u95e8\u201d\u3001\u201c\u66f2\u6c60\u201d\u3001\u201c\u5408\u8c37\u201d\u4e09\u5904\u7a74\u9053\uff0c\u8ddf\u7740\u4f38\u624b\u63a5\u4f4f\u7a7a\u4e2d\u843d\u4e0b\u7684\u7389\u676f\uff0c\u5de6\u8db3\u98de\u51fa\uff0c\u8e22\u5012\u4e86\u6851\u98de\u8679\uff0c\u8db3\u5c16\u987a\u52bf\u5728\u5979\u819d\u5f2f\u91cc\u4e00\u70b9\u3002\u90a3\u201c\u4e91\u95e8\u7a74\u201d\u662f\u5728\u80a9\u5934\uff0c\u201c\u66f2\u6c60\u7a74\u201d\u5728\u8098\u5f2f\uff0c\u201c\u5408\u8c37\u7a74\u201d\u5728\u5927\u62c7\u6307\u4e0e\u98df\u6307\u4e4b\u95f4\uff0c\u4e09\u7a74\u88ab\u70b9\uff0c\u90a3\u4e66\u751f\u81ea\u80a9\u81f3\u6307\uff0c\u4e00\u6761\u80a9\u8180\u8f6f\u762b\u65e0\u529b\uff0c\u518d\u4e5f\u4e0d\u80fd\u634f\u788e\u7389\u676f\u4e86\u3002 \u3000\u3000\u8fd9\u51e0\u4e0b\u5154\u8d77\u9e58\u843d\uff0c\u76f4\u5982\u53d8\u620f\u6cd5\u4e00\u822c\uff0c\u4f17\u4eba\u8fd8\u6ca1\u6709\u770b\u6e05\u695a\u600e\u5730\uff0c\u6c64\u6c9b\u5df2\u6253\u5012\u4e8c\u4eba\uff0c\u624b\u6367\u4e09\u53ea\u7389\u9f99\u676f\uff0c\u653e\u56de\u51e0\u4e0a\u3002\u5f85\u4ed6\u7b11\u541f\u541f\u7684\uff0c\u5750\u56de\u592a\u5e08\u6905\u4e2d\uff0c\u5927\u5385\u4e0a\u8fd9\u624d\u5f69\u58f0\u96f7\u52a8\u3002 \u3000\u3000\u90ed\u7389\u5802\u6478\u7740\u80e1\u987b\uff0c\u4e0d\u4f4f\u4ef7\u8fde\u58f0\u8d5e\u53f9\uff1a\u201c\u8fd9\u4e00\u77ac\u4e4b\u95f4\u6253\u5012\u4e24\u4eba\uff0c\u5df2\u662f\u6781\u4e3a\u4e0d\u6613\uff0c\u66f4\u96be\u7684\u662f\u4e09\u4e2a\u4eba\u624b\u91cc\u90fd\u6709\u4e00\u53ea\u7389\u676f\uff0c\u53ea\u8981\u5206\u5bf8\u62ff\u634f\u5dee\u4e86\u5398\u6beb\uff0c\u4efb\u8c01\u4e00\u53ea\u7389\u676f\u90fd\u4f1a\u635f\u4f24\uff0c\u90a3\u4e48\u8fd9\u4e00\u6b21\u5927\u4f1a\u4fbf\u4e0d\u514d\u7f8e\u4e2d\u4e0d\u8db3\uff0c\u66f4\u96be\u5f97\u7684\u662f\u8fd9\u4e00\u526f\u80c6\u8bc6\u3002\u7a0b\u8001\u5f1f\uff0c\u4f60\u8bf4\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u80e1\u6590\u70b9\u5934\u9053\uff1a\u201c\u96be\u5f97\uff0c\u96be\u5f97\u3002\u201d\u4ed6\u89c1\u4e86\u9002\u624d\u72b9\u5982\u96f7\u8f70\u7535\u95ea\u822c\u7684\u4e00\u5e55\uff0c\u4e0d\u7531\u5f97\u96c4\u5fc3\u987f\u8d77\uff0c\u6697\u60f3\uff1a\u201c\u8fd9\u59d3\u6c64\u7684\u679c\u662f\u827a\u4e1a\u4e0d\u51e1\uff0c\u82e5\u6709\u673a\u7f18\uff0c\u5012\u8981\u8ddf\u4ed6\u8f83\u91cf\u8f83\u91cf\u3002\u201d\u53c8\u60f3\uff1a\u201c\u90a3\u5c11\u5e74\u4e66\u751f\u548c\u6851\u59d1\u5a18\u5931\u624b\u88ab\u64d2\uff0c\u5c31\u7b97\u4fdd\u5f97\u6027\u547d\uff0c\u4e5f\u8981\u53d7\u5c3d\u6298\u78e8\uff0c\u600e\u751f\u60f3\u4e2a\u6cd5\u513f\u76f8\u6551\u624d\u597d\u3002\u201d \u3000\u3000\u8fd9\u65f6\u4f17\u536b\u58eb\u5df2\u53d6\u8fc7\u7ef3\u7d22\uff0c\u5c06\u90a3\u4e66\u751f\u548c\u6851\u98de\u8679\u7ed1\u4e86\uff0c\u63a8\u5230\u798f\u5eb7\u5b89\u8ddf\u524d\uff0c\u542c\u7531\u53d1\u843d\u3002\u798f\u5eb7\u5b89\u5c06\u624b\u4e00\u6325\uff0c\u8bf4\u9053\uff1a\u201c\u62bc\u5728\u4e00\u65c1\uff0c\u6162\u6162\u518d\u95ee\uff0c\u4f11\u5f97\u963b\u4e86\u5404\u4f4d\u82f1\u96c4\u7684\u5174\u5934\u3002\u5b89\u63d0\u7763\uff0c\u4f60\u8ba9\u5927\u5bb6\u6bd4\u4e0b\u53bb\u5427\uff01\u201d\u5b89\u63d0\u7763\u9053\uff1a\u201c\u662f\uff01\u201d\u5f53\u5373\u4f20\u4e0b\u53f7\u4ee4\uff0c\u547d\u7fa4\u8c6a\u7ee7\u7eed\u6bd4\u8bd5\u3002 \u3000\u3000\u80e1\u6590\u89c1\u8fd9\u4e9b\u4eba\u6597\u6765\u6597\u53bb\uff0c\u5e76\u65e0\u6770\u51fa\u7684\u672c\u9886\uff0c\u5ff5\u7740\u9a6c\u6625\u82b1\u7684\u4e24\u4e2a\u513f\u5b50\u4e0d\u77e5\u5982\u4f55\u91cd\u88ab\u593a\u56de\uff0c\u9a6c\u6625\u82b1\u4e0d\u77e5\u662f\u5426\u53c8\u906d\u5371\u96be\uff0c\u4e5f\u65e0\u5fc3\u7eea\u53bb\u770b\u5404\u4eba\u4e89\u6597\u3002 \u3000\u3000\u6765\u6765\u53bb\u53bb\u6bd4\u8bd5\u4e86\u5341\u591a\u4eba\uff0c\u5ffd\u542c\u5f97\u95e8\u5916\u536b\u58eb\u5927\u58f0\u53eb\u9053\uff1a\u201c\u5723\u65e8\u5230\uff01\u201d \u3000"}),showOnHomepage:!0},{name:"\u7b2c\u5341\u516b\u7ae0\u3000\u5b9d\u5200\u94f6\u9488",content:(0,a.jsx)(a.Fragment,{children:"\u7fa4\u8c6a\u542c\u4e86\uff0c\u5747\u662f\u4e00\u6115\u3002\u798f\u5eb7\u5b89\u5e9c\u4e2d\u4e0a\u4e0b\u4eba\u7b49\u5374\u90fd\u662f\u53f8\u7a7a\u89c1\u60ef\uff0c\u77e5\u9053\u7687\u4e0a\u5fc3\u8840\u6765\u6f6e\uff0c\u4fbf\u662f\u534a\u591c\u4e09\u66f4\u4e5f\u6709\u5723\u65e8\uff0c\u56e0\u6b64\u4e0d\u4ee5\u4e3a\u5947\uff0c\u5f53\u5373\u6446\u4e0b\u9999\u6848\u3002\u798f\u5eb7\u5b89\u7ad9\u8d77\u8eab\u6765\uff0c\u8dea\u5728\u6ef4\u6c34\u6a90\u524d\u63a5\u65e8\u3002\u81ea\u5b89\u63d0\u7763\u4ee5\u4e0b\uff0c\u4eba\u4eba\u4e00\u9f50\u8dea\u5012\u3002\u80e1\u6590\u5f53\u6b64\u60c5\u666f\uff0c\u53ea\u5f97\u8ddf\u7740\u8dea\u4e0b\uff0c\u5fc3\u4e2d\u6697\u6697\u5492\u9a82\u3002 \u3000\u3000\u53ea\u542c\u5f97\u9774\u58f0\u6a50\u6a50\uff0c\u9662\u5b50\u4e2d\u8d70\u8fdb\u4e94\u4e2a\u4eba\u6765\uff0c\u5f53\u5148\u4e00\u4eba\u662f\u4e2a\u8001\u592a\u76d1\u3002\u798f\u5eb7\u5b89\u8bc6\u5f97\u4ed6\u662f\u4e7e\u6e05\u5bab\u7684\u592a\u76d1\u5218\u4e4b\u4f59\uff0c\u8eab\u540e\u8ddf\u7740\u56db\u540d\u5185\u73ed\u5bbf\u536b\u3002\u90a3\u5218\u4e4b\u4f59\u8d70\u5230\u5385\u95e8\u53e3\uff0c\u5374\u4e0d\u8fdb\u5385\uff0c\u4fbf\u5728\u95e8\u524d\u7ad9\u5b9a\uff0c\u5c55\u5f00\u5723\u65e8\uff0c\u5ba3\u8bfb\u9053\uff1a\u201c\u5175\u90e8\u5c1a\u4e66\u798f\u5eb7\u5b89\u542c\u65e8\uff1a\u9002\u624d\u64d2\u5230\u7537\u5973\u8d3c\u4eba\u5404\u4e00\uff0c\u7740\u5373\u5e26\u6765\u5bab\u4e2d\uff0c\u94a6\u6b64\uff01\u201d \u3000\u3000\u798f\u5eb7\u5b89\u767b\u65f6\u5446\u4e86\uff0c\u5fc3\u60f3\uff1a\u201c\u7687\u4e0a\u7684\u4fe1\u606f\u7adf\u5982\u6b64\u4e4b\u5feb\u3002\u4ed6\u8981\u5e26\u4e24\u540d\u8d3c\u4eba\u53bb\u5e72\u4ec0\u4e48\uff1f\u201d\u4e00\u62ac\u5934\uff0c\u53ea\u89c1\u5218\u4e4b\u4f59\u6324\u7709\u5f04\u773c\uff0c\u795e\u6c14\u5f88\u662f\u53e4\u602a\uff0c\u53c8\u60f3\u5e73\u7d20\u592a\u76d1\u4f20\u65e8\uff0c\u5b9a\u662f\u5f80\u5927\u5385\u6b63\u4e2d\u5411\u5916\u4e00\u7ad9\uff0c\u671d\u5357\u5ba3\u8bfb\uff0c\u8fd9\u4e00\u6b21\u5374\u662f\u671d\u91cc\u5ba3\u65e8\u3002\u8fd9\u5218\u4e4b\u4f59\u662f\u5bab\u4e2d\u8001\u5e74\u592a\u76d1\uff0c\u51b3\u4e0d\u80fd\u9519\u4e86\u89c4\u77e9\uff0c\u5176\u4e2d\u5fc5\u6709\u7f18\u6545\uff0c\u4e8e\u662f\u7ad9\u8d77\u8eab\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u5218\u516c\u516c\uff0c\u8bf7\u5750\u4e0b\u559d\u8336\uff0c\u77a7\u4e00\u77a7\u8fd9\u91cc\u82f1\u96c4\u597d\u6c49\u4eec\u732e\u6f14\u8eab\u624b\u3002\u201d\u5218\u4e4b\u4f59\u6b23\u7136\u9053\uff1a\u201c\u597d\u6781\uff0c\u597d\u6781\uff01\u201d\u7a81\u7136\u95f4\u7709\u5934\u4e00\u76b1\uff0c\u9053\uff1a\u201c\u591a\u8c22\u798f\u5927\u5e05\u5566\uff0c\u8336\u662f\u4e0d\u559d\u4e86\uff0c\u7687\u4e0a\u7b49\u7740\u56de\u590d\u3002\u201d \u3000\u3000\u798f\u5eb7\u5b89\u4e00\u77a7\u8fd9\u60c5\u666f\uff0c\u604d\u7136\u800c\u609f\uff0c\u77e5\u4ed6\u53d7\u4e86\u8eab\u540e\u90a3\u51e0\u540d\u536b\u58eb\u7684\u631f\u5236\uff0c\u5047\u4f20\u5723\u65e8\uff0c\u8fd9\u56db\u540d\u536b\u58eb\u4e0d\u662f\u53cd\u53db\uff0c\u4fbf\u662f\u65c1\u4eba\u5047\u626e\u7684\uff0c\u5f53\u4e0b\u4e0d\u52a8\u58f0\u8272\uff0c\u7b11\u9053\uff1a\u201c\u966a\u7740\u4f60\u7684\u51e0\u4f4d\u5927\u54e5\u662f\u8c01\u554a\uff1f\u600e\u5730\u9762\u751f\u5f97\u7d27\u3002\u201d\u5218\u4e4b\u4f59\u82e6\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u90a3\u4e2a\u2026\u2026\u563f\u563f\uff0c\u4ed6\u4eec\u662f\u5916\u7701\u65b0\u6765\u7684\u3002\u201d \u3000\u3000\u798f\u5eb7\u5b89\u66f4\u662f\u5fc3\u4e2d\u96ea\u4eae\uff0c\u987b\u77e5\u5185\u73ed\u5bbf\u536b\u65e5\u591c\u5728\u7687\u5e1d\u4e4b\u4fa7\uff0c\u82e5\u975e\u4eb2\u8d35\uff0c\u4fbf\u662f\u6709\u529f\u52cb\u7684\u4e16\u81e3\u5b50\u5f1f\uff0c\u5916\u7701\u6765\u7684\u6b66\u4eba\u90a3\u91cc\u80fd\u5f53\uff1f\u5fc3\u60f3\uff1a\u201c\u53ea\u6709\u8c03\u5f00\u8fd9\u56db\u4eba\uff0c\u5218\u592a\u76d1\u65b9\u4e0d\u53d7\u4ed6\u4eec\u631f\u6301\u3002\u201d\u8bf4\u9053\uff1a\u201c\u65e2\u662f\u5982\u6b64\uff0c\u56db\u4f4d\u4f8d\u536b\u5927\u54e5\u4fbf\u628a\u8d3c\u4eba\u5e26\u8d70\u5427\uff01\u201d\u8bf4\u7740\u5411\u7ed1\u5728\u4e00\u65c1\u7684\u5c11\u5e74\u4e66\u751f\u548c\u6851\u98de\u8679\u4e00\u6307\u3002 \u3000\u3000\u56db\u540d\u4f8d\u536b\u4e2d\u4fbf\u6709\u4e00\u4eba\u8d70\u4e0a\u524d\u6765\uff0c\u53bb\u7275\u90a3\u4e66\u751f\u3002\u798f\u5eb7\u5b89\u9053\uff1a\u201c\u4e14\u6162\uff01\u8fd9\u4f4d\u4f8d\u536b\u5927\u54e5\u8d35\u59d3\uff1f\u201d\u6309\u7167\u5e38\u60c5\uff0c\u798f\u5eb7\u5b89\u5bf9\u5bab\u4e2d\u4f8d\u536b\u5ba2\u6c14\uff0c\u79f0\u4e00\u58f0\u201c\u4f8d\u536b\u5927\u54e5\u201d\uff0c\u4f46\u5f53\u4f8d\u536b\u7684\u5b98\u9636\u6bd4\u4ed6\u4f4e\u5f97\u591a\uff0c\u5fc5\u5b9a\u4e0a\u524d\u8bf7\u5b89\u3002\u8fd9\u4f8d\u536b\u5374\u5927\u524c\u524c\u7684\u4e0d\u7406\uff0c\u53ea\u8bf4\uff1a\u201c\u4ffa\u59d3\u5f20\uff01\u201d\u798f\u5eb7\u5b89\u9053\uff1a\u201c\u5f20\u5927\u54e5\u5230\u5bab\u4e2d\u51e0\u65f6\u4e86\uff1f\u600e\u5730\u6ca1\u4f1a\u8fc7\uff1f\u201d \u3000\u3000\u90a3\u4f8d\u536b\u5c1a\u672a\u56de\u7b54\uff0c\u5218\u4e4b\u4f59\u8eab\u540e\u4e00\u4e2a\u8eab\u6750\u80a5\u80d6\u7684\u4f8d\u536b\u7a81\u7136\u53f3\u624b\u4e00\u626c\uff0c\u94f6\u5149\u95ea\u95ea\uff0c\u4e00\u4ef6\u68ad\u5b50\u822c\u7684\u6697\u5668\u5c04\u4e86\u51fa\u6765\uff0c\u98de\u5411\u653e\u7f6e\u7389\u9f99\u676f\u7684\u8336\u51e0\u3002\u8fd9\u6697\u5668\u53bb\u52bf\u5cfb\u6025\uff0c\u773c\u89c1\u516b\u53ea\u7389\u676f\u8981\u4e00\u9f50\u6253\u788e\u3002\u4f17\u536b\u58eb\u7eb7\u7eb7\u547c\u559d\uff0c\u5584\u4e8e\u53d1\u5c04\u6697\u5668\u7684\u4fbf\u5404\u81ea\u51fa\u624b\uff0c\u53ea\u89c1\u8896\u7bad\u3001\u98de\u9556\u3001\u94c1\u83b2\u5b50\u3001\u94c1\u84ba\u85dc\uff0c\u4e03\u516b\u4ef6\u6697\u5668\u9f50\u5411\u94f6\u68ad\u5c04\u53bb\u3002\u90a3\u80a5\u80d6\u7684\u4f8d\u536b\u53cc\u624b\u8fde\u626c\uff0c\u4e5f\u662f\u4e03\u516b\u4ef6\u6697\u5668\u4e00\u9f50\u5c04\u51fa\u3002 \u3000\u3000\u53ea\u542c\u5f97\u53ee\u53ee\u4e4b\u58f0\u4e0d\u7edd\uff0c\u4f17\u536b\u58eb\u7684\u6697\u5668\u4e00\u9f50\u78b0\u843d\u3002\u90a3\u94f6\u68ad\u98de\u5230\u8336\u51e0\uff0c\u94a9\u4f4f\u4e86\u4e00\u53ea\u7389\u9f99\u676f\u3002\u8bf4\u4e5f\u5947\u602a\uff0c\u8fd9\u68ad\u5b50\u5728\u534a\u7a7a\u4e2d\u7adf\u4f1a\u81ea\u884c\u8f6c\u5f2f\uff0c\u94a9\u4f4f\u7389\u9f99\u676f\u540e\u659c\u659c\u98de\u56de\uff0c\u53c8\u56de\u5230\u90a3\u4f8d\u536b\u624b\u4e2d\u3002 \u3000\u3000\u4f17\u4eba\u773c\u89c1\u8fd9\u822c\u602a\u5f02\u60c5\u666f\uff0c\u65e0\u4e0d\u6115\u7136\u3002\u80e1\u6590\u89c1\u4e86\u90a3\u80d6\u4f8d\u536b\u8fd9\u7b49\u53d1\u5c04\u6697\u5668\u7684\u795e\u6280\uff0c\u5fcd\u4e0d\u4f4f\u53eb\u9053\uff1a\u201c\u8d75\u4e09\u54e5\uff01\u201d \u3000\u3000\u539f\u6765\u90a3\u80d6\u4f8d\u536b\u6b63\u662f\u5343\u81c2\u5982\u6765\u8d75\u534a\u5c71\u6240\u4e54\u88c5\u6539\u626e\u3002\u90a3\u4e2a\u53bb\u6551\u4e66\u751f\u7684\u4f8d\u536b\uff0c\u5374\u662f\u7ea2\u82b1\u4f1a\u4e2d\u7684\u9b3c\u89c1\u6101\u77f3\u53cc\u82f1\u3002\u8fd9\u4e00\u5e72\u4eba\u65e9\u4fbf\u5728\u798f\u5eb7\u5b89\u5e9c\u5916\u63a5\u5e94\uff0c\u89c1\u90a3\u5c11\u5e74\u4e66\u751f\u5931\u624b\u88ab\u64d2\uff0c\u6b63\u597d\u592a\u76d1\u5218\u4e4b\u4f59\u5728\u5e9c\u95e8\u5916\u7ecf\u8fc7\uff0c\u4fbf\u64d2\u4e86\u6765\u5047\u4f20\u5723\u65e8\u3002\u4f46\u8fd9\u4e9b\u6c5f\u6e56\u4e0a\u7684\u8c6a\u6770\u4e4b\u58eb\u7ec8\u7a76\u4e0d\u61c2\u5bab\u5ef7\u548c\u5b98\u573a\u89c4\u77e9\uff0c\u4e00\u8fdb\u798f\u5eb7\u5b89\u5e9c\u4fbf\u9732\u51fa\u9a6c\u811a\u3002 \u3000\u3000\u8d75\u534a\u5c71\u89c1\u798f\u5eb7\u5b89\u795e\u8272\u548c\u8a00\u8bed\u95f4\u5df2\u7136\u8d77\u7591\uff0c\u4e0d\u5f85\u4ed6\u4e0b\u4ee4\u62ff\u4eba\uff0c\u5148\u4e0b\u624b\u4e3a\u5f3a\uff0c\u5f53\u5373\u53d1\u51fa\u4e00\u679a\u98de\u71d5\u94f6\u68ad\uff0c\u62a2\u4e86\u4e00\u53ea\u7389\u676f\u3002\u8fd9\u98de\u71d5\u94f6\u68ad\u662f\u4ed6\u522b\u51fa\u5fc3\u88c1\u7684\u4e00\u79cd\u6697\u5668\uff0c\u68ad\u4f5c\u5f27\u5f62\uff0c\u63b7\u51fa\u540e\u80fd\u98de\u56de\u624b\u6765\u3002 \u3000\u3000\u4ed6\u4e00\u62a2\u5230\u7389\u676f\uff0c\u731b\u542c\u5f97\u6709\u4eba\u53eb\u4e86\u58f0\uff1a\u201c\u8d75\u4e09\u54e5\uff01\u201d\u8fd9\u53eb\u58f0\u4e2d\u771f\u60c5\u6d41\u9732\uff0c\u4f3c\u4e4e\u4e4d\u9022\u4eb2\u4eba\u4e00\u822c\uff0c\u4e3e\u76ee\u5411\u53eb\u58f0\u6765\u5904\u77a7\u53bb\uff0c\u5374\u4e0d\u89c1\u6709\u719f\u8bc6\u4e4b\u4eba\u3002\u8981\u77e5\u80e1\u6590\u548c\u4ed6\u668c\u522b\u591a\u5e74\uff0c\u8eab\u5f62\u5bb9\u8c8c\u5747\u5df2\u5927\u53d8\uff0c\u522b\u8bf4\u4ed6\u5df2\u4e54\u88c5\u6539\u626e\uff0c\u5c31\u662f\u6ca1\u6709\u6539\u88c5\uff0c\u4e4d\u7136\u76f8\u9022\uff0c\u4e5f\u672a\u5fc5\u8ba4\u5f97\u51fa\u6765\u3002 \u3000\u3000\u5904\u8eab\u5728\u8fd9\u9f99\u6f6d\u864e\u7a74\u4e4b\u4e2d\uff0c\u4e00\u77a5\u95f4\u6ca1\u77a7\u89c1\u719f\u4eba\uff0c\u51b3\u65e0\u4f59\u88d5\u518d\u77a7\u7b2c\u4e8c\u773c\uff0c\u4ed6\u53cc\u81c2\u8fde\u626c\uff0c\u4f46\u542c\u5f97\u55e4\u55e4\u4e4b\u58f0\u4e0d\u7edd\uff0c\u6bcf\u54cd\u4e00\u4e0b\uff0c\u4fbf\u6709\u4e00\u679d\u7ea2\u70db\u88ab\u6697\u5668\u6253\u7184\uff0c\u9877\u523b\u95f4\u5927\u5385\u4e2d\u9ed1\u6f06\u4e00\u56e2\u3002\u53ea\u542c\u5f97\u4ed6\u5927\u58f0\u53eb\u9053\uff1a\u201c\u798f\u5eb7\u5b89\u770b\u9556\uff01\u201d\u8ddf\u7740\u6709\u4e24\u4eba\u5927\u58f0\u60e8\u53eb\uff0c\u663e\u5df2\u4e2d\u4e86\u4ed6\u7684\u6697\u5668\u3002\u4f46\u542c\u5f97\u4e52\u4e52\u4e53\u4e53\uff0c\u54cd\u8d77\u4e00\u7247\u5175\u5203\u4e4b\u58f0\uff0c\u539f\u6765\u5df2\u6709\u4e24\u540d\u536b\u58eb\u62a2\u4e0a\u5c06\u77f3\u53cc\u82f1\u622a\u4f4f\u3002 \u3000\u3000\u8d75\u534a\u5c71\u53eb\u9053\uff1a\u201c\u8d70\u5427\uff0c\u4e0d\u53ef\u604b\u6218\uff01\u201d\u4ed6\u77e5\u8eab\u5904\u9669\u5730\uff0c\u5927\u5385\u4e4b\u4e0a\u9ad8\u624b\u5982\u4e91\uff0c\u4e00\u51fb\u4e0d\u4e2d\u4fbf\u5f53\u98d8\u7136\u8fdc\u5f15\uff0c\u6551\u4eba\u4e4b\u4e8b\uff0c\u53ea\u5f97\u5f90\u56fe\u540e\u8ba1\uff0c\u773c\u4e0b\u501f\u7740\u9ed1\u6697\u4e2d\u4e00\u7247\u6df7\u4e71\uff0c\u5c1a\u53ef\u8131\u8eab\uff0c\u82e5\u662f\u65f6\u673a\u4e00\u8fc7\uff0c\u8fde\u81ea\u5df1\u4e5f\u4f1a\u9677\u8eab\u5176\u4e2d\u3002\u4f46\u8fd9\u65f6\u77f3\u53cc\u82f1\u5df2\u88ab\u7eca\u4f4f\uff0c\u8ddf\u7740\u53c8\u6709\u4e24\u4eba\u653b\u5230\uff0c\u522b\u8bf4\u6551\u4eba\uff0c\u8fde\u4ed6\u81ea\u5df1\u4e5f\u8d70\u4e0d\u8131\u4e86\u3002 \u3000\u3000\u80e1\u6590\u5f53\u90a3\u5c11\u5e74\u4e66\u751f\u4e3a\u6c64\u6c9b\u64d2\u83b7\u4e4b\u65f6\uff0c\u5373\u62df\u51fa\u624b\u76f8\u6551\uff0c\u53ea\u662f\u5385\u4e0a\u5f3a\u654c\u73af\u4f3a\uff0c\u5355\u662f\u6b63\u4e2d\u592a\u5e08\u6905\u4e0a\u6240\u5750\u7684\u90a3\u56db\u5927\u638c\u95e8\uff0c\u81ea\u5df1\u5bf9\u6bcf\u4e00\u4e2a\u90fd\u65e0\u5236\u80dc\u628a\u63e1\uff0c\u7a81\u89c1\u8d75\u534a\u5c71\u6253\u706d\u6ee1\u5385\u706f\u706b\uff0c\u5f53\u4e0b\u66f4\u4e0d\u72b9\u8c6b\uff0c\u7acb\u5373\u7eb5\u8eab\u62a2\u5230\u90a3\u5c11\u5e74\u4e66\u751f\u8eab\u65c1\u3002\u6c64\u6c9b\u51fa\u624b\u70b9\u7a74\uff0c\u80e1\u6590\u770b\u5f97\u5206\u660e\uff0c\u6240\u70b9\u7684\u662f\u201c\u4e91\u95e8\u201d\u3001\u201c\u66f2\u6c60\u201d\u3001\u201c\u5408\u8c37\u201d\u4e09\u7a74\uff0c\u8fd9\u65f6\u4e00\u4fef\u8eab\u95f4\uff0c\u4fbf\u5f80\u90a3\u4e66\u751f\u80a9\u540e\u201c\u5929\u5b97\u7a74\u201d\u4e0a\u4e00\u62cd\uff0c\u767b\u65f6\u89e3\u4e86\u4ed6\u7684\u201c\u4e91\u95e8\u7a74\u201d\uff0c\u5f85\u8981\u518d\u53bb\u63a8\u62ff\u4ed6\u201c\u5929\u6c60\u7a74\u201d\u65f6\uff0c\u5934\u9876\u7a81\u7136\u88ad\u6765\u4e00\u9635\u8f7b\u5fae\u638c\u98ce\u3002 \u3000\u3000\u80e1\u6590\u5de6\u624b\u4e00\u7ffb\uff0c\u8fce\u7740\u638c\u98ce\u6765\u5904\u8fd8\u4e86\u4e00\u638c\uff0c\u53ea\u89c9\u654c\u4eba\u638c\u52bf\u6765\u5f97\u5feb\u6781\uff0c\u62cd\u7684\u4e00\u58f0\u8f7b\u54cd\uff0c\u53cc\u638c\u76f8\u4ea4\u3002\u80e1\u6590\u8eab\u5b50\u4e00\u9707\uff0c\u4e0d\u7531\u81ea\u4e3b\u7684\u5012\u9000\u534a\u6b65\uff0c\u5fc3\u4e2d\u5927\u5403\u4e00\u60ca\uff1a\u201c\u6b64\u4eba\u638c\u529b\u6041\u5730\u6d51\u539a\uff01\u201d\u53ea\u5f97\u62da\u5168\u529b\u76f8\u6297\uff0c\u4f46\u89c9\u5bf9\u65b9\u5185\u529b\u65e0\u7a77\u65e0\u5c3d\u7684\u6e90\u6e90\u800c\u6765\u3002\u80e1\u6590\u6697\u6697\u53eb\u82e6\uff0c\u5fc3\u60f3\uff1a\u201c\u6bd4\u62da\u638c\u529b\uff0c\u975e\u7247\u523b\u95f4\u53ef\u51b3\u80dc\u8d25\uff0c\u706f\u70db\u5c11\u65f6\u4fbf\u4f1a\u70b9\u8d77\uff0c\u770b\u6765\u6211\u8131\u8eab\u4e0d\u6613\u4e86\u3002\u201d\u5bf9\u638c\u6bd4\u62da\uff0c\u5fc3\u4e2d\u52a8\u5ff5\uff0c\u90fd\u53ea\u662f\u7535\u5149\u706b\u77f3\u822c\u7684\u4e00\u970e\u95f4\u4e4b\u4e8b\uff0c\u5ffd\u542c\u5f97\u90a3\u5c11\u5e74\u4e66\u751f\u4f4e\u58f0\u9053\uff1a\u201c\u591a\u8c22\u63f4\u624b\uff01\u201d\u7adf\u5df2\u8dc3\u8d77\u8eab\u6765\u3002 \u3000\u3000\u4ed6\u8fd9\u4e00\u8dc3\u8d77\uff0c\u80e1\u6590\u7acb\u65f6\u9192\u609f\uff1a\u201c\u6211\u53ea\u89e3\u4e86\u4ed6\u7684\u4e91\u95e8\u7a74\uff0c\u4ed6\u7684\u66f2\u6c60\u3001\u5408\u8c37\u4e24\u7a74\uff0c\u539f\u6765\u662f\u8ddf\u6211\u5bf9\u638c\u4e4b\u4eba\u89e3\u4e86\u3002\u90a3\u4e48\u6b64\u4eba\u662f\u53cb\u975e\u654c\u3002\u201d\u4ed6\u4e00\u60f3\u5230\u6b64\u8282\uff0c\u5bf9\u65b9\u4e5f\u540c\u65f6\u60f3\u5230\uff1a\u201c\u6211\u53ea\u89e3\u4e86\u4ed6\u66f2\u6c60\u3001\u5408\u8c37\u4e24\u7a74\uff0c\u5c1a\u6709\u4e91\u95e8\u7a74\u672a\u89e3\uff0c\u539f\u6765\u662f\u8ddf\u6211\u5bf9\u638c\u4e4b\u4eba\u89e3\u4e86\u3002 \u3000\u3000\u90a3\u4e48\u6b64\u4eba\u662f\u53cb\u975e\u654c\u3002\u201d\u4e24\u4eba\u5fc3\u5ff5\u76f8\u540c\uff0c\u5f53\u5373\u5404\u64a4\u638c\u529b\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u4e66\u751f\u6293\u8d77\u8eba\u5728\u8eab\u65c1\u7684\u6851\u98de\u8679\uff0c\u6025\u6b65\u5954\u51fa\uff0c\u53eb\u9053\uff1a\u201c\u798f\u5eb7\u5b89\u5df2\u88ab\u6211\u5bb0\u4e86\uff01\u5c11\u6797\u6d3e\u4f17\u4f4d\u597d\u6c49\u653b\u4e1c\u8fb9\uff0c\u6b66\u5f53\u6d3e\u4f17\u4f4d\u597d\u6c49\u653b\u897f\u8fb9\uff01\u5927\u4f19\u513f\u6740\u554a\uff01\u6740\u554a\uff01\u201d\u9ed1\u6697\u4e2d\u4f46\u542c\u5f97\u5175\u5203\u4e71\u54cd\uff0c\u5385\u4e0a\u56fa\u662f\u4e71\u6210\u4e00\u56e2\uff0c\u4eba\u4eba\u5fc3\u4e2d\u4e5f\u662f\u4e71\u6210\u4e00\u56e2\u3002 \u3000\u3000\u4f17\u536b\u58eb\u542c\u5230\u798f\u5927\u5e05\u88ab\u5bb3\uff0c\u65e0\u4e0d\u5413\u51fa\u4e00\u8eab\u51b7\u6c57\uff0c\u53c8\u542c\u5f97\u201c\u5c11\u6797\u6d3e\u4f17\u4f4d\u597d\u6c49\u653b\u4e1c\u8fb9\uff0c\u6b66\u5f53\u6d3e\u4f17\u4f4d\u597d\u6c49\u653b\u897f\u8fb9\u201d\u7684\u558a\u58f0\uff0c\u8fd9\u4e24\u5927\u95e8\u6d3e\u95e8\u4eba\u4f17\u591a\uff0c\u96be\u9053\u5f53\u771f\u53cd\u53db\u4e86\uff1f \u3000\u3000\u5ffd\u542c\u5f97\u5468\u94c1\u9e6a\u7684\u58f0\u97f3\u53eb\u9053\uff1a\u201c\u798f\u5927\u5e05\u5e73\u5b89\u65e0\u6059\uff0c\u522b\u4e0a\u4e86\u8d3c\u5b50\u7684\u5f53\u3002\u201d\u5f85\u5f97\u4f17\u536b\u58eb\u70b9\u4eae\u706f\u70db\uff0c\u8d75\u534a\u5c71\u3001\u77f3\u53cc\u82f1\uff0c\u4ee5\u53ca\u5c11\u5e74\u4e66\u751f\u548c\u6851\u98de\u8679\u90fd\u5df2\u4e0d\u77e5\u53bb\u5411\u3002 \u3000\u3000\u53ea\u89c1\u798f\u5eb7\u5b89\u7aef\u5750\u6905\u4e2d\uff0c\u6c64\u6c9b\u548c\u6d77\u5170\u5f3c\u6321\u5728\u8eab\u524d\uff0c\u524d\u540e\u5de6\u53f3\uff0c\u516d\u5341\u591a\u540d\u536b\u58eb\u5982\u8089\u5c4f\u98ce\u822c\u56e2\u56e2\u4fdd\u62a4\u3002\u5728\u8fd9\u7b49\u4e25\u5bc6\u9632\u5b88\u4e4b\u4e0b\uff0c\u4fbf\u662f\u6709\u5343\u767e\u540d\u9ad8\u624b\u540c\u65f6\u653b\u5230\uff0c\u4e00\u65f6\u4e09\u523b\u4e4b\u95f4\u4e5f\u4f24\u4ed6\u4e0d\u5230\u534a\u6839\u6beb\u6bdb\uff0c\u4f55\u51b5\u53ea\u662f\u4e09\u6570\u4e2a\u523a\u5ba2\uff1f\u4f46\u4e5f\u56e0\u4ed6\u624b\u4e0b\u536b\u58eb\u4eba\u4eba\u53ea\u60f3\u5230\u4fdd\u62a4\u5927\u5e05\uff0c\u8d75\u534a\u5c71\u548c\u90a3\u5c11\u5e74\u4e66\u751f\u7b49\u624d\u5f97\u4e58\u9ed1\u9003\u8d70\u3002\u5426\u5219\u4ed6\u6570\u4eba\u6b66\u529f\u518d\u5f3a\uff0c\u4e5f\u51b3\u4e0d\u80fd\u8fd9\u822c\u8f7b\u6613\u7684\u5168\u8eab\u800c\u9000\u3002 \u3000\u3000\u4f17\u4eba\u89c1\u798f\u5eb7\u5b89\u8138\u5e26\u5fae\u7b11\uff0c\u795e\u8272\u9547\u5b9a\uff0c\u5927\u5385\u4e0a\u767b\u65f6\u9759\u4e86\u4e0b\u6765\uff1b\u53c8\u89c1\u5c11\u6797\u6d3e\u638c\u95e8\u4eba\u5927\u667a\u7985\u5e08\u548c\u6b66\u5f53\u6d3e\u638c\u95e8\u4eba\u65e0\u9752\u5b50\u5b89\u5750\u6905\u4e2d\uff0c\u90fd\u77e5\u90a3\u4e66\u751f\u8fd9\u4e00\u756a\u558a\u53eb\uff0c\u53ea\u4e0d\u8fc7\u662f\u6270\u4e71\u4eba\u5fc3\u3002 \u3000\u3000\u798f\u5eb7\u5b89\u7b11\u9053\uff1a\u201c\u8d3c\u5b50\u80e1\u8a00\u4e71\u8bed\uff0c\u7985\u5e08\u548c\u9053\u957f\u4e0d\u5fc5\u4ecb\u610f\u3002\u201d\u5b89\u63d0\u7763\u8d70\u5230\u798f\u5eb7\u5b89\u9762\u524d\u8bf7\u5b89\uff0c\u8bf4\u9053\uff1a\u201c\u5351\u804c\u65e0\u80fd\uff0c\u7adf\u8ba9\u8d3c\u5b50\u9003\u8d70\uff0c\u8bf7\u5927\u5e05\u964d\u7f6a\u3002\u201d\u798f\u5eb7\u5b89\u5c06\u624b\u4e00\u6446\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u90fd\u662f\u6211\u7d2f\u4e8b\uff0c\u7b97\u4e0d\u5f97\u662f\u4f60\u4eec\u6ca1\u672c\u4e8b\u3002\u5927\u5bb6\u987e\u7740\u4fdd\u62a4\u6211\uff0c\u4e5f\u4e0d\u53bb\u7406\u4f1a\u6bdb\u8d3c\u4e86\u3002\u201d \u3000\u3000\u4ed6\u5fc3\u4e2d\u751a\u662f\u6ee1\u610f\uff0c\u89c9\u5f97\u4f17\u536b\u58eb\u4eba\u4eba\u5c3d\u8d23\uff0c\u4ee5\u4ed6\u4e3a\u91cd\uff0c\u7aed\u529b\u4fdd\u62a4\uff0c\u53c8\u9053\uff1a\u201c\u51e0\u4e2a\u5c0f\u6bdb\u8d3c\u6765\u6363\u4e71\u4e00\u756a\uff0c\u7b97\u5f97\u4ec0\u4e48\u5927\u4e8b\uff1f\u4e22\u4e86\u4e00\u53ea\u7389\u9f99\u676f\uff0c\u55ef\uff0c\u90a3\u4e5f\u597d\uff0c\u77a7\u662f\u54ea\u4e00\u6d3e\u7684\u638c\u95e8\u4eba\u65e5\u540e\u53bb\u593a\u5c06\u6765\uff0c\u518d\u64d2\u83b7\u4e86\u8fd9\u52ab\u676f\u6bdb\u8d3c\uff0c\u8fd9\u53ea\u7389\u9f99\u676f\u4fbf\u5f52\u4ed6\u6240\u6709\u3002\u8fd9\u4e00\u4ef6\u4e8b\u53c8\u6597\u667a\u53c8\u6597\u529b\uff0c\u6bd4\u4e4b\u5728\u8fd9\u91cc\u5355\u662f\u8f83\u91cf\u6b66\u529f\uff0c\u4e0d\u662f\u66f4\u6709\u610f\u601d\u4e48\uff1f\u201d \u3000\u3000\u7fa4\u8c6a\u5927\u58f0\u6b22\u547c\uff0c\u90fd\u8d5e\u798f\u5927\u5e05\u5b89\u6392\u5de7\u5999\u3002\u80e1\u6590\u548c\u7a0b\u7075\u7d20\u5bf9\u671b\u4e00\u773c\uff0c\u5fc3\u4e0b\u4e5f\u4e0d\u7981\u4f69\u670d\u798f\u5eb7\u5b89\u5927\u6709\u5e94\u53d8\u4e4b\u624d\uff0c\u5931\u676f\u7684\u4e11\u4e8b\u8f7b\u8f7b\u63a9\u8fc7\uff0c\u800c\u4e14\u4e00\u7ffb\u624b\u95f4\uff0c\u7ed9\u7ea2\u82b1\u4f1a\u4f0f\u4e0b\u4e86\u4e00\u4e2a\u5fc3\u8179\u5927\u60a3\u3002\u6b66\u6797\u4e2d\u81ea\u6709\u4e0d\u5c11\u4eba\u8d2a\u56fe\u51fa\u540d\uff0c\u4f1a\u5343\u65b9\u767e\u8ba1\u5730\u53bb\u8bbe\u6cd5\u593a\u56de\u7389\u9f99\u676f\uff0c\u4e0d\u8bba\u6210\u529f\u4e0e\u5426\uff0c\u90fd\u662f\u4f7f\u7ea2\u82b1\u4f1a\u6811\u4e0b\u4e0d\u5c11\u5f3a\u654c\u3002 \u3000\u3000\u798f\u5eb7\u5b89\u5411\u5b89\u63d0\u7763\u9053\uff1a\u201c\u8ba9\u4ed6\u4eec\u63a5\u4e0b\u53bb\u6bd4\u8bd5\u5427\uff01\u201d\u5b89\u63d0\u7763\u8eac\u8eab\u9053\uff1a\u201c\u662f\uff01\u201d\u8f6c\u8fc7\u8eab\u6765\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u798f\u5927\u5e05\u6709\u4ee4\uff0c\u8bf7\u5929\u4e0b\u82f1\u96c4\u7ee7\u7eed\u6bd4\u8bd5\u6b66\u827a\uff0c\u4e14\u77a7\u4f59\u4e0b\u7684\u4e09\u53ea\u5fa1\u8d50\u7389\u676f\uff0c\u5f52\u5c5e\u8c01\u624b\u3002\u201d\u4ed6\u867d\u662f\u8bf4\u201c\u798f\u5927\u5e05\u6709\u4ee4\u201d\uff0c\u4f46\u8fd8\u662f\u7528\u4e86\u4e00\u4e2a\u201c\u8bf7\u201d\u5b57\uff0c\u90a3\u662f\u5bf9\u7fa4\u8c6a\u751a\u8868\u5c0a\u91cd\uff0c\u4ee5\u5ba2\u793c\u76f8\u5f85\u4e4b\u610f\u3002 \u3000\u3000\u798f\u5eb7\u5b89\u5429\u5490\u9053\uff1a\u201c\u642c\u5f00\u4e00\u5f20\u6905\u5b50\uff01\u201d\u4fbf\u6709\u4e00\u540d\u536b\u58eb\u4e0a\u524d\uff0c\u5c06\u7a7a\u7740\u7684\u592a\u5e08\u6905\u642c\u5f00\u4e86\u4e00\u5f20\uff0c\u5385\u5fc3\u7559\u4e0b\u4e09\u5f20\u7a7a\u6905\u3002\u4f17\u4eba\u8fd9\u65f6\u65b9\u59cb\u53d1\u89c9\uff0c\u201c\u6606\u4ed1\u5200\u201d\u638c\u95e8\u4eba\u897f\u7075\u9053\u4eba\u5df2\u4e0d\u77e5\u4f55\u65f6\u79bb\u6905\uff0c\u60f3\u662f\u4ed6\u773c\u89c1\u5404\u5bb6\u5404\u6d3e\u6b66\u529f\u9ad8\u51fa\u81ea\u5df1\u4e4b\u4eba\u751a\u591a\uff0c\u4e0e\u5176\u88ab\u4eba\u8d76\u4e0b\u5ea7\u4f4d\uff0c\u8fd8\u4e0d\u5982\u81ea\u884c\u9000\u4f4d\uff0c\u514d\u5f97\u51fa\u4e11\u9732\u4e56\u3002 \u3000\u3000\u8fd9\u65f6\u80e1\u6590\u601d\u6f6e\u8d77\u4f0f\uff0c\u5fc3\u4e2d\u5b58\u7740\u8bb8\u591a\u7591\u56e2\uff1a\u201c\u798f\u5eb7\u5b89\u7684\u4e00\u5bf9\u53cc\u751f\u513f\u5b50\u5982\u4f55\u53c8\u88ab\u4ed6\u593a\u56de\uff1f\u6211\u5192\u5145\u534e\u62f3\u95e8\u638c\u95e8\u4eba\uff0c\u662f\u4e0d\u662f\u5df2\u88ab\u53d1\u89c9\uff1f\u5bf9\u65b9\u8fdf\u8fdf\u4e0d\u4e88\u63ed\u7834\uff0c\u662f\u4e0d\u662f\u6697\u4e2d\u5df2\u5e03\u7f6e\u4e0b\u6781\u5389\u5bb3\u7684\u9677\u9631\uff1f\u6211\u9002\u624d\u66ff\u90a3\u5c11\u5e74\u4e66\u751f\u89e3\u7a74\uff0c\u9ed1\u6697\u4e2d\u4e0e\u4eba\u5bf9\u638c\uff0c\u6b64\u4eba\u5185\u529b\u6d51\u539a\uff0c\u975e\u540c\u5c0f\u53ef\uff0c\u4ed6\u4e5f\u51fa\u624b\u52a9\u90a3\u4e66\u751f\uff0c\u81ea\u662f\u5927\u5385\u4e0a\u7fa4\u8c6a\u4e4b\u4e00\uff0c\u5374\u4e0d\u77e5\u662f\u8c01\uff1f\u201d \u3000\u3000\u4ed6\u660e\u77e5\u5728\u6b64\u5904\u591a\u803d\u5f97\u4e00\u523b\uff0c\u4fbf\u591a\u589e\u4e00\u5206\u51f6\u9669\uff0c\u4f46\u4e00\u6765\u5fc3\u4e2d\u5b58\u7740\u8fd9\u8bb8\u591a\u7591\u56e2\u672a\u89e3\uff1b\u4e8c\u6765\u773c\u89c1\u51e4\u5929\u5357\u4fbf\u5728\u8eab\u65c1\uff0c\u597d\u5bb9\u6613\u77e5\u9053\u4e86\u4ed6\u7684\u4e0b\u843d\uff0c\u5c82\u80af\u53c8\u8ba9\u4ed6\u8d70\u4e86\uff1f\u4e09\u6765\u4e5f\u8981\u77a7\u4e00\u77a7\u4f59\u4e0b\u7684\u4e09\u53ea\u7389\u9f99\u676f\u7531\u90a3\u6d3e\u7684\u638c\u95e8\u4eba\u6240\u5f97\u3002 \u3000\u3000\u5176\u5b9e\uff0c\u8fd9\u4e9b\u90fd\u53ea\u662f\u4ed6\u8111\u5b50\u91cc\u6240\u60f3\u5230\u7684\u539f\u56e0\uff0c\u771f\u6b63\u7684\u539f\u56e0\uff0c\u5374\u662f\u5728\u5fc3\u4e2d\u9690\u9690\u7ea6\u7ea6\u89c9\u5f97\u7684\uff1a\u8881\u7d2b\u8863\u4e00\u5b9a\u4f1a\u6765\u3002\u65e2\u77e5\u5979\u8981\u6765\uff0c\u4ed6\u5c31\u51b3\u8ba1\u4e0d\u8d70\u3002\u4fbf\u6709\u5929\u5927\u7684\u5371\u9669\uff0c\u4e5f\u5413\u4ed6\u4e0d\u8d70\u3002 \u3000\u3000\u8fd9\u65f6\u5385\u4e0a\u53c8\u6709\u4e24\u5bf9\u4eba\u5728\u6bd4\u62da\u6b66\u529f\u3002\u56db\u4e2a\u4eba\u90fd\u4f7f\u5175\u5203\u3002\u80e1\u6590\u4e00\u770b\uff0c\u89c1\u56db\u4eba\u7684\u6b66\u529f\u6bd4\u4e4b\u4ee5\u524d\u51fa\u624b\u7684\u90fd\u9ad8\u3002\u4e0d\u4e45\u4e00\u4e2a\u4f7f\u4e09\u8282\u68cd\u7684\u8d25\u4e86\u4e0b\u53bb\uff0c\u53e6\u4e00\u4e2a\u4f7f\u6d41\u661f\u9524\u7684\u4e0a\u6765\u3002\u542c\u90a3\u5531\u540d\u6b66\u5b98\u62a5\u540d\uff0c\u662f\u592a\u539f\u5e9c\u7684\u201c\u6d41\u661f\u8d76\u6708\u201d\u7ae5\u6000\u9053\u3002\u80e1\u6590\u60f3\u8d77\u6570\u6708\u524d\u4e0e\u953a\u6c0f\u4e09\u96c4\u4ea4\u624b\uff0c\u66fe\u542c\u4ed6\u4eec\u63d0\u8fc7\u201c\u6d41\u661f\u8d76\u6708\u7ae5\u8001\u5e08\u201d\u7684\u540d\u5934\u3002\u8fd9\u7ae5\u6000\u9053\u5728\u53cc\u9524\u4e0a\u7684\u9020\u8be3\u679c\u7136\u751a\u662f\u6df1\u539a\uff0c\u53ea\u5341\u4f59\u5408\u4fbf\u5c06\u5bf9\u624b\u6253\u8d25\u4e86\uff0c\u63a5\u7740\u4e0a\u6765\u7684\u4e24\u4eba\u4e5f\u90fd\u4e0d\u662f\u4ed6\u654c\u624b\u3002 \u3000\u3000\u9ad8\u624b\u6bd4\u6b66\uff0c\u82e5\u975e\u6bd4\u62da\u5185\u529b\uff0c\u5f80\u5f80\u51e0\u4e2a\u7167\u9762\u4fbf\u5206\u80dc\u8d25\uff0c\u800c\u52a8\u5230\u5175\u5203\uff0c\u751f\u6b7b\u51b3\u4e8e\u4fc4\u9877\uff0c\u6bd4\u4e4b\u8f83\u91cf\u62f3\u811a\u66f4\u662f\u51f6\u9669\u5f97\u591a\u3002\u53cc\u65b9\u6bd4\u8bd5\u8005\u5e76\u65e0\u6df1\u4ec7\u5927\u6028\uff0c\u5927\u90fd\u662f\u95fb\u540d\u4e0d\u76f8\u8bc6\uff0c\u529f\u592b\u4e0a\u4e00\u5206\u9ad8\u4f4e\uff0c\u7a0d\u900a\u4e00\u7b79\u8005\u4fbf\u5373\u77e5\u96be\u800c\u9000\uff0c\u8c01\u90fd\u4e0d\u613f\u5e72\u5192\u6027\u547d\u4e4b\u9669\u800c\u6b7b\u62da\u5230\u5e95\u3002\u56e0\u4e4b\u5728\u798f\u5eb7\u5b89\u8fd9\u4e9b\u53ea\u8bc6\u6b66\u5b66\u76ae\u6bdb\u7684\u4eba\u773c\u4e2d\uff0c\u6bd4\u8bd5\u7684\u53cc\u65b9\u90fd\u662f\u81ea\u60dc\u7fbd\u6bdb\uff0c\u6570\u5408\u95f4\u4fbf\u6709\u4eba\u9000\u4e0b\uff0c\u53cd\u4e0d\u53ca\u9ec4\u5e0c\u8282\u3001\u6851\u98de\u8679\u3001\u6b27\u9633\u516c\u653f\u3001\u54c8\u8d64\u548c\u5c1a\u7b49\u4e00\u5e72\u4eba\u731b\u6253\u72e0\u6bb4\u7684\u597d\u770b\u3002\u4f46\u6b66\u529f\u9ad8\u660e\u4e4b\u4eba\u5374\u770b\u5f97\u660e\u767d\uff0c\u51fa\u8d5b\u8005\u7684\u6b66\u529f\u8d8a\u6765\u8d8a\u9ad8\uff0c\u8981\u53d6\u80dc\u662f\u8d8a\u6765\u8d8a\u4e0d\u5bb9\u6613\uff0c\u8bb8\u591a\u638c\u95e8\u4eba\u539f\u672c\u8dc3\u8dc3\u6b32\u8bd5\u7684\uff0c\u8fd9\u65f6\u90fd\u6539\u53d8\u4e86\u4e3b\u610f\uff0c\u51b3\u5b9a\u8896\u624b\u65c1\u89c2\u3002\u6709\u65f6\u4e24\u4e2a\u4eba\u6597\u5f97\u4f3c\u4e4e\u6ca1\u7cbe\u6253\u5f69\u3001\u5e73\u6de1\u65e0\u5947\uff0c\u800c\u6c64\u6c9b\u3001\u6d77\u5170\u5f3c\u8fd9\u4e9b\u9ad8\u624b\u5374\u559d\u8d77\u5f69\u6765\u3002\u4e00\u822c\u4e0d\u660e\u5176\u7406\u7684\u540e\u8f88\uff0c\u4e0d\u662f\u77a0\u76ee\u7ed3\u820c\uff0c\u5446\u82e5\u6728\u9e21\uff0c\u4fbf\u662f\u968f\u58f0\u9644\u548c\uff0c\u5047\u5145\u5185\u884c\u3002 \u3000\u3000\u9976\u662f\u51fa\u8d5b\u8005\u4e2a\u4e2a\u5c0f\u5fc3\u7ffc\u7ffc\uff0c\u4f46\u4e00\u5165\u573a\u5b50\uff0c\u603b\u662f\u529b\u6c42\u53d6\u80dc\uff0c\u5175\u5203\u65e0\u773c\uff0c\u8fd8\u662f\u6709\u4e09\u4e2a\u638c\u95e8\u4eba\u6bd9\u4e8e\u5f53\u573a\uff0c\u4e03\u4e2a\u4eba\u8eab\u53d7\u91cd\u4f24\u3002\u603b\u7b97\u798f\u5eb7\u5b89\u5a01\u52bf\u6151\u4eba\uff0c\u6b7b\u4f24\u8005\u95e8\u4e0b\u7684\u5f1f\u5b50\u5373\u65f6\u4e0d\u6562\u53d1\u4f5c\uff0c\u4f46\u6b66\u6797\u4e2d\u51a4\u51a4\u76f8\u62a5\u7684\u65e0\u6570\u8165\u98ce\u8840\u96e8\uff0c\u90fd\u5df2\u5728\u8fd9\u4e00\u65e5\u4e2d\u4f0f\u4e0b\u4e86\u56e0\u5b50\u3002 \u3000\u3000\u6e05\u671d\u987a\u6cbb\u3001\u5eb7\u7199\u3001\u96cd\u6b63\u4e09\u671d\uff0c\u6b66\u6797\u4e2d\u53cd\u6e05\u4e49\u4e3e\u6b64\u8d77\u5f7c\u4f0f\uff0c\u767e\u4f59\u5e74\u6765\u59cb\u7ec8\u4e0d\u80fd\u5e73\u670d\uff0c\u4f46\u81ea\u4e7e\u9686\u4e2d\u53f6\u4ee5\u540e\uff0c\u6b66\u6797\u4eba\u58eb\u81ea\u76f8\u6b8b\u6740\u4e4b\u98ce\u5927\u76db\uff0c\u987e\u4e0d\u5230\u518d\u6765\u53cd\u6e05\uff0c\u4f7f\u6e05\u5ef7\u53bb\u4e86\u4e00\u5927\u9690\u5fe7\u3002\u867d\u7136\u539f\u56e0\u591a\u822c\uff0c\u4f46\u8fd9\u6b21\u5929\u4e0b\u638c\u95e8\u4eba\u5927\u4f1a\u5b9e\u662f\u4e00\u5927\u4e3b\u56e0\u3002\u540e\u6765\u6b66\u6797\u4e2d\u6709\u8bc6\u4e4b\u58eb\u51fa\u529b\u8c03\u89e3\u5f25\u7f1d\uff0c\u4ecd\u662f\u96be\u4f7f\u5404\u5bb6\u5404\u6d3e\u6cef\u5374\u4ec7\u6028\u3002\u4e0d\u660e\u767d\u798f\u5eb7\u5b89\u8fd9\u4e2a\u5927\u9634\u8c0b\u4e4b\u4eba\uff0c\u8fd8\u9053\u6ee1\u6e05\u6c14\u8fd0\u65b9\u76db\uff0c\u8349\u83bd\u82f1\u96c4\u81ea\u76f8\u653b\u6740\uff0c\u4e43\u5929\u6570\u4f7f\u7136\u3002 \u3000\u3000\u6d41\u661f\u8d76\u6708\u7ae5\u6000\u9053\u4ee5\u4e00\u5bf9\u6d41\u661f\u53cc\u9524\uff0c\u5728\u4e0d\u5230\u534a\u4e2a\u65f6\u8fb0\u4e4b\u5185\u8fde\u8d25\u4e94\u6d3e\u638c\u95e8\u9ad8\u624b\uff0c\u5176\u4f59\u7684\u638c\u95e8\u4eba\u60ee\u4e8e\u4ed6\u53cc\u9524\u6b64\u6765\u5f7c\u5f80\u3001\u8fc5\u6377\u5faa\u73af\u7684\u653b\u52bf\uff0c\u4e00\u65f6\u65e0\u4eba\u518d\u4e0a\u524d\u6311\u6218\u3002 \u3000\u3000\u4fbf\u5728\u6b64\u65f6\uff0c\u5385\u5916\u5306\u5306\u8d70\u8fdb\u4e00\u540d\u6b66\u5b98\uff0c\u5230\u798f\u5eb7\u5b89\u9762\u524d\u4f4e\u58f0\u7980\u544a\u4e86\u51e0\u53e5\u3002\u798f\u5eb7\u5b89\u70b9\u4e86\u70b9\u5934\uff0c\u90a3\u6b66\u5b98\u8d70\u5230\u5385\u53e3\uff0c\u5927\u58f0\u9053\uff1a\u201c\u798f\u5927\u5e05\u6709\u8bf7\u5929\u9f99\u95e8\u5317\u5b97\u638c\u95e8\u4eba\u7530\u8001\u5e08\u8fdb\u89c1\u3002\u201d\u5385\u5916\u53c8\u6709\u6b66\u5b98\u4f20\u547c\u51fa\u53bb\uff1a\u201c\u798f\u5927\u5e05\u6709\u8bf7\u5929\u9f99\u95e8\u5317\u5b97\u638c\u95e8\u4eba\u7530\u8001\u5e08\u8fdb\u89c1\u3002\u201d \u3000\u3000\u80e1\u6590\u548c\u7a0b\u7075\u7d20\u5bf9\u671b\u4e00\u773c\uff0c\u5fc3\u5934\u90fd\u662f\u5fae\u5fae\u4e00\u9707\uff1a\u201c\u4ed6\u4e5f\u6765\u4e86\uff01\u201d \u3000\u3000\u8fc7\u4e0d\u591a\u65f6\uff0c\u53ea\u89c1\u7530\u5f52\u519c\u8eab\u7a7f\u957f\u888d\u9a6c\u8902\uff0c\u5fae\u7b11\u7740\u7f13\u6b65\u8fdb\u6765\uff0c\u8eab\u540e\u8ddf\u968f\u7740\u9ad8\u9ad8\u77ee\u77ee\u7684\u516b\u4eba\u3002\u4ed6\u8d70\u5230\u798f\u5eb7\u5b89\u8eab\u524d\uff0c\u8eac\u8eab\u8bf7\u5b89\u3002 \u3000\u3000\u798f\u5eb7\u5b89\u6b20\u4e86\u6b20\u8eab\uff0c\u62f1\u624b\u8fd8\u793c\uff0c\u5fae\u7b11\u7740\u9053\uff1a\u201c\u7530\u8001\u5e08\u597d\uff0c\u8bf7\u5750\u5427\uff01\u201d \u3000\u3000\u7fa4\u8c6a\u4e00\u89c1\uff0c\u90fd\u60f3\uff1a\u201c\u5929\u9f99\u95e8\u6b66\u529f\u540d\u9707\u5929\u4e0b\uff0c\u5df2\u5386\u767e\u5e74\uff0c\u81ea\u660e\u672b\u4ee5\u6765\uff0c\u80e1\u82d7\u8303\u7530\u56db\u5bb6\u9f50\u540d\uff0c\u4ee3\u4ee3\u5747\u6709\u597d\u624b\u3002\u8fd9\u59d3\u7530\u7684\u6c14\u6d3e\u4e0d\u51e1\uff0c\u798f\u5927\u5e05\u5bf9\u4ed6\u4e5f\u662f\u4f18\u793c\u6709\u52a0\uff0c\u4e0e\u5bf9\u522b\u6d3e\u7684\u638c\u95e8\u4eba\u4e0d\u540c\u3002 \u3000\u3000\u5374\u4e0d\u77e5\u4ed6\u662f\u5426\u771f\u6709\u60ca\u4eba\u827a\u4e1a\uff1f\u201d\u6bcf\u4e00\u6d3e\u4e0e\u4f1a\u7684\u5747\u9650\u56db\u4eba\uff0c\u4ed6\u5374\u5e26\u4e86\u516b\u540d\u968f\u4ece\uff0c\u4f55\u51b5\u8fd9\u822c\u5927\u6a21\u5927\u6837\u7684\u8fdf\u8fdf\u800c\u81f3\uff0c\u7fa4\u8c6a\u867d\u7136\u9707\u4e8e\u4ed6\u7684\u5a01\u540d\uff0c\u5fc3\u4e2d\u5374\u5747\u6709\u4e0d\u5e73\u4e4b\u610f\u3002 \u3000\u3000\u7530\u5f52\u519c\u548c\u5c11\u6797\u3001\u6b66\u5f53\u4e24\u6d3e\u638c\u95e8\u4eba\u70b9\u5934\u4e3a\u793c\uff0c\u770b\u6765\u76f8\u4e92\u95f4\u5747\u4e0d\u719f\u8bc6\uff0c\u4f46\u4ed6\u548c\u7518\u9716\u60e0\u4e03\u7701\u6c64\u6c9b\u5374\u6781\u662f\u719f\u7edc\u3002\u6c64\u6c9b\u62cd\u7740\u4ed6\u80a9\u8180\u7b11\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u505a\u54e5\u54e5\u7684\u4e00\u76f4\u7275\u8bb0\u7740\u4f60\uff0c\u5fc3\u60f3\u600e\u4e48\u5230\u8fd9\u5f53\u513f\u8fd8\u4e0d\u5230\u6765\uff1f\u5018\u82e5\u4f60\u7adf\u662f\u5230\u5f97\u8fdf\u4e86\uff0c\u62ff\u4e0d\u5230\u4e00\u53ea\u7389\u9f99\u676f\uff0c\u505a\u54e5\u54e5\u7684\u8fd9\u4e00\u53ea\u5982\u4f55\u597d\u610f\u601d\u6367\u56de\u5bb6\u53bb\uff1f\u4f60\u5929\u9f99\u95e8\u82e5\u662f\u4e0d\u5f97\u7389\u676f\uff0c\u90a3\u4e00\u5929\u4f60\u9ad8\u5174\u8d77\u6765\uff0c\u627e\u8001\u54e5\u54e5\u6765\u6bd4\u5212\u6bd4\u5212\uff0c\u6211\u9664\u4e86\u53cc\u624b\u5949\u4e0a\u7389\u676f\uff0c\u518d\u6ca1\u7b2c\u4e8c\u53e5\u8bdd\u597d\u8bf4\uff0c\u5c82\u4e0d\u7cdf\u7cd5\uff1f\u201d\u8ddf\u7740\u5c06\u798f\u5927\u5e05\u5631\u4ee4\u5404\u6d3e\u6bd4\u8bd5\u6b66\u529f\u4ee5\u53d6\u5fa1\u676f\u7684\u4e8b\uff0c\u5411\u4ed6\u8bf4\u4e86\u4e00\u904d\u3002 \u3000\u3000\u7530\u5f52\u519c\u7b11\u9053\uff1a\u201c\u5144\u5f1f\u5982\u4f55\u6562\u548c\u5927\u54e5\u76f8\u6bd4\uff1f\u6211\u5929\u9f99\u95e8\u5018\u5f97\u798f\u5927\u5e05\u6069\u5178\uff0c\u8499\u5927\u54e5\u7167\u62c2\uff0c\u80fd\u5728\u5929\u4e0b\u82f1\u96c4\u4e4b\u524d\u4e0d\u592a\u51fa\u4e11\u4e22\u8138\uff0c\u4e5f\u5df2\u559c\u51fa\u671b\u5916\u4e86\u3002\u201d\u8bf4\u7740\u4e24\u4eba\u4e00\u9f50\u5927\u7b11\u3002\u4ed6\u8bdd\u662f\u8bf4\u5f97\u8c26\u865a\uff0c\u4f46\u795e\u8272\u4e4b\u95f4\uff0c\u663e\u662f\u5c06\u7389\u9f99\u676f\u770b\u4f5c\u4e86\u56ca\u4e2d\u4e4b\u7269\u3002\u6c64\u6c9b\u548c\u4eba\u4eba\u90fd\u5f88\u4eb2\u70ed\uff0c\u4f46\u5bf9\u5f85\u7530\u5f52\u519c\u7684\u795e\u60c5\u5374\u53c8\u4e0e\u4f17\u4e0d\u540c\u3002\u542c\u4ed6\u4e8c\u4eba\u79f0\u547c\u8bed\u6c14\uff0c\u4f3c\u4e4e\u8fd8\u662f\u62dc\u628a\u5b50\u7684\u5144\u5f1f\u3002 \u3000\u3000\u80e1\u6590\u5fc3\u60f3\uff1a\u201c\u8fd9\u59d3\u7530\u7684\u548c\u6211\u4ea4\u8fc7\u624b\uff0c\u6b66\u529f\u867d\u6bd4\u8fd9\u4e9b\u4eba\u90fd\u9ad8\uff0c\u5374\u672a\u5fc5\u80fd\u53ca\u5f97\u4e0a\u6c64\u6c9b\u548c\u6d77\u5170\u5f3c\uff0c\u8981\u8bf4\u4e00\u5b9a\u593a\u5230\u7389\u9f99\u676f\uff0c\u672a\u514d\u662f\u5c06\u5929\u4e0b\u82f1\u96c4\u90fd\u77a7\u5f97\u5c0f\u4e86\u3002\u201d\u60f3\u8d77\u4ed6\u6697\u7b97\u82d7\u4eba\u51e4\u7684\u65e0\u803b\u5351\u9119\u884c\u5f84\uff0c\u5df2\u81ea\u6253\u5b9a\u4e86\u4e3b\u610f\uff1a\u201c\u4ed6\u4e0d\u5f97\u7389\u9f99\u676f\u4fbf\u7f62\uff0c\u82e5\u662f\u4fa5\u5e78\u593a\u5f97\uff0c\u597d\u6b79\u8981\u4ed6\u5728\u5929\u4e0b\u7fa4\u96c4\u4e4b\u524d\uff0c\u5927\u5927\u7684\u51fa\u4e00\u4e2a\u4e11\u3002\u201d\u4ed6\u548c\u7530\u5f52\u519c\u5728\u82d7\u4eba\u51e4\u5bb6\u4e2d\u4ea4\u8fc7\u624b\uff0c\u4ee5\u7956\u4f20\u5200\u6cd5\uff0c\u6253\u5f97\u4ed6\u53e3\u5410\u9c9c\u8840\uff0c\u5927\u8d25\u800c\u8d70\uff0c\u4f55\u51b5\u5176\u65f6\u80e1\u6590\u672a\u5f97\u82d7\u4eba\u51e4\u7684\u6307\u70b9\uff0c\u672a\u609f\u80e1\u5bb6\u5200\u6cd5\u4e2d\u7684\u7cbe\u4e49\u8981\u8bc0\u3002\u6b64\u523b\u4ed6\u5355\u4ee5\u5200\u6cd5\u800c\u8bba\uff0c\u5929\u4e0b\u51e0\u4e4e\u65e0\u4eba\u80dc\u5f97\u8fc7\u4ed6\uff0c\u5373\u662f\u4e0e\u82d7\u4eba\u51e4\u3001\u8d75\u534a\u5c71\u8fd9\u7b49\u7b2c\u4e00\u6d41\u7684\u9ad8\u624b\u76f8\u6bd4\uff0c\u4e5f\u5df2\u4e0d\u9051\u591a\u8ba9\uff0c\u7530\u5f52\u519c\u81ea\u7136\u8fdc\u975e\u5176\u654c\u3002 \u3000\u3000\u5f53\u7530\u5f52\u519c\u8fdb\u6765\u4e4b\u65f6\uff0c\u5927\u5385\u7684\u6bd4\u8bd5\u7a0d\u505c\u7247\u523b\uff0c\u8fd9\u65f6\u5175\u5203\u76f8\u51fb\u4e4b\u58f0\u53c8\u4f5c\u3002\u7530\u5f52\u519c\u5750\u5728\u6905\u4e2d\uff0c\u624b\u6301\u9152\u676f\u89c2\u6597\u3002\u795e\u8272\u6781\u662f\u95f2\u96c5\uff0c\u773c\u770b\u6709\u4eba\u80dc\uff0c\u6709\u4eba\u8d25\uff0c\u4ed6\u53ea\u662f\u8138\u5e26\u5fae\u7b11\uff0c\u65e0\u52a8\u4e8e\u8877\uff0c\u6709\u65f6\u4fbf\u8ddf\u6c64\u6c9b\u8bf4\u51e0\u53e5\u95f2\u8bdd\u3002\u4f17\u4eba\u90fd\u5df2\u770b\u51fa\uff0c\u4ed6\u9762\u5b50\u4e0a\u4f3c\u662f\u88c5\u4f5c\u9ad8\u4eba\u4e00\u7b49\uff0c\u4e0d\u5c51\u548c\u4eba\u4e89\u80dc\uff0c\u5b9e\u5219\u662f\u4ee5\u9038\u5f85\u52b3\uff0c\u8981\u5230\u6700\u540e\u7684\u5f53\u53e3\u65b9\u624d\u51fa\u624b\uff0c\u5728\u65c1\u4eba\u7cbe\u75b2\u529b\u7aed\u4e4b\u4f59\uff0c\u518d\u884c\u65bd\u5c55\u5168\u529b\u4e00\u51fb\u3002 \u3000\u3000\u6d41\u661f\u8d76\u6708\u7ae5\u6000\u9053\u5750\u5728\u592a\u5e08\u6905\u4e2d\uff0c\u89c1\u826f\u4e45\u65e0\u4eba\u4e0a\u6765\u6311\u6218\uff0c\u7a81\u7136\u4e00\u8dc3\u800c\u8d77\uff0c\u8d70\u5230\u7530\u5f52\u519c\u8eab\u524d\uff0c\u8bf4\u9053\uff1a\u201c\u7530\u8001\u5e08\uff0c\u59d3\u7ae5\u7684\u9886\u6559\u4f60\u7684\u9ad8\u62db\u3002\u201d\u4f17\u4eba\u90fd\u662f\u4e00\u6123\u3002\u81ea\u6bd4\u8bd5\u5f00\u59cb\u4ee5\u6765\uff0c\u603b\u662f\u5f97\u80dc\u8005\u5750\u5728\u592a\u5e08\u6905\u4e2d\uff0c\u7531\u4eba\u4e0a\u524d\u6311\u6218\uff0c\u5c82\u77e5\u7ae5\u6000\u9053\u5374\u662f\u8d70\u4e0b\u5ea7\u6765\uff0c\u53cd\u53bb\u5411\u7530\u5f52\u519c\u6c42\u6597\u3002 \u3000\u3000\u7530\u5f52\u519c\u7b11\u9053\uff1a\u201c\u4e0d\u5fd9\u5427\uff1f\u201d\u624b\u4e2d\u4ecd\u662f\u6301\u7740\u9152\u676f\u3002\u7ae5\u6000\u9053\u8bf4\u9053\uff1a\u201c\u53cd\u6b63\u8fdf\u65e9\u90fd\u662f\u4e00\u6597\uff0c\u4e58\u7740\u6211\u8fd9\u65f6\u8fd8\u6709\u529b\u6c14\uff0c\u5411\u7530\u8001\u5e08\u9886\u6559\u9886\u6559\u3002\u4e5f\u514d\u5f97\u4f60\u517b\u7cbe\u84c4\u9510\uff0c\u5230\u6700\u540e\u6765\u6361\u73b0\u6210\u4fbf\u5b9c\u3002\u201d\u4ed6\u5fc3\u76f4\u53e3\u5feb\uff0c\u60f3\u5230\u4ec0\u4e48\uff0c\u4fbf\u8bf4\u4e86\u51fa\u53e3\uff0c\u518d\u65e0\u987e\u5fcc\u3002\u7fa4\u8c6a\u4e2d\u4fbf\u6709\u4e8c\u5341\u4f59\u4eba\u559d\u8d77\u5f69\u6765\u3002\u8fd9\u4e9b\u4eba\u89c1\u7740\u7530\u5f52\u519c\u8fd9\u7b49\u5927\u523a\u523a\u7684\u6a21\u6837\uff0c\u65e9\u611f\u4e0d\u5fff\u3002 \u3000\u3000\u7530\u5f52\u519c\u54c8\u54c8\u4e00\u7b11\uff0c\u773c\u89c1\u65e0\u6cd5\u63a8\u6258\uff0c\u5411\u6c64\u6c9b\u7b11\u9053\uff1a\u201c\u5927\u54e5\uff0c\u5144\u5f1f\u8981\u732e\u4e11\u4e86\u3002\u201d\u6c64\u6c9b\u9053\uff1a\u201c\u606d\u795d\u8d24\u5f1f\u9a6c\u5230\u6210\u529f\uff01\u201d \u3000\u3000\u7ae5\u6000\u9053\u8f6c\u8fc7\u5934\u6765\uff0c\u76f4\u77aa\u7740\u6c64\u6c9b\uff0c\u7c97\u58f0\u9053\uff1a\u201c\u6c64\u8001\u5e08\uff0c\u798f\u5927\u5e05\u7b97\u4f60\u662f\u56db\u5927\u638c\u95e8\u4e4b\u4e00\uff0c\u8bf7\u4f60\u4f5c\u516c\u8bc1\u6765\u7740\uff0c\u8fd9\u4e00\u4e2a\u2018\u516c\u2019\u5b57\uff0c\u672a\u514d\u6709\u70b9\u513f\u4e0d\u5bf9\u5934\u5427\uff1f\u201d\u6c64\u6c9b\u88ab\u4ed6\u76f4\u8a00\u9876\u649e\uff0c\u4e0d\u514d\u6709\u4e9b\u5c34\u5c2c\uff0c\u5f3a\u7b11\u9053\uff1a\u201c\u5728\u4e0b\u54ea\u91cc\u4e0d\u516c\u4e86\uff1f\u8bf7\u7ae5\u8001\u5e08\u6307\u6559\u3002\u201d\u7ae5\u6000\u9053\u8bf4\u9053\uff1a\u201c\u6211\u8ddf\u7530\u8001\u5e08\u8fd8\u6ca1\u6bd4\u8bd5\uff0c\u4f60\u5c31\u5148\u504f\u4e86\u5fc3\u5566\uff0c\u8bf4\u4ec0\u4e48\u2018\u606d\u795d\u8d24\u5f1f\u9a6c\u5230\u6210\u529f\u3002\u201d\u5929\u4e0b\u82f1\u96c4\u5728\u6b64\uff0c\u8fd9\u53ef\u662f\u4eba\u4eba\u542c\u89c1\u7684\u3002\u201d \u3000\u3000\u6c64\u6c9b\u5fc3\u4e2d\u5927\u6012\uff0c\u8fd1\u4e8c\u4e09\u5341\u5e74\u6765\uff0c\u4eba\u4eba\u89c1\u4e86\u4ed6\u90fd\u662f\u6c64\u5927\u4fa0\u524d\u3001\u6c64\u5927\u4fa0\u540e\uff0c\u4ece\u65e0\u4e00\u4eba\u6562\u5bf9\u4ed6\u5982\u6b64\u9876\u649e\uff0c\u66f4\u4f55\u51b5\u662f\u5728\u5927\u5ead\u5e7f\u4f17\u4e4b\u95f4\u8fd9\u822c\u7684\u76f4\u65a5\u5176\u975e\uff0c\u4f46\u4ed6\u57ce\u5e9c\u751a\u6df1\uff0c\u4ecd\u662f\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e5f\u606d\u795d\u7ae5\u8001\u5e08\u65d7\u5f00\u5f97\u80dc\u3002\u201d \u3000\u3000\u7ae5\u6000\u9053\u4e00\u6014\uff0c\u5fc3\u60f3\u4e24\u4eba\u6bd4\u8bd5\uff0c\u4e00\u4e2a\u65d7\u5f00\u5f97\u80dc\uff0c\u4e00\u4e2a\u9a6c\u5230\u6210\u529f\uff0c\u5929\u4e0b\u51b3\u65e0\u662f\u7406\uff0c\u4f46\u4ed6\u65e2\u8fd9\u822c\u8bf4\uff0c\u5374\u4e5f\u65e0\u4ece\u8fa9\u9a73\uff0c\u4fbf\u5927\u58f0\u9053\uff1a\u201c\u6c64\u8001\u5e08\uff0c\u795d\u4f60\u4e5f\u662f\u65d7\u5f00\u5f97\u80dc\uff0c\u9a6c\u5230\u6210\u529f\uff01\u201d\u7fa4\u8c6a\u4e00\u542c\uff0c\u4e00\u9f50\u8f70\u7b11\u8d77\u6765\u3002 \u3000\u3000\u7530\u5f52\u519c\u5411\u6c64\u6c9b\u4f7f\u4e2a\u773c\u8272\uff0c\u610f\u601d\u8bf4\uff1a\u201c\u5927\u54e5\u653e\u5fc3\uff0c\u8fd9\u65e0\u793c\u83bd\u649e\u4e4b\u5f92\uff0c\u5144\u5f1f\u4e00\u5b9a\u597d\u597d\u7684\u6559\u8bad\u6559\u8bad\u4ed6\u3002\u201d\u5f53\u4e0b\u7f13\u6b65\u8d70\u5230\u5385\u5fc3\uff0c\u9053\uff1a\u201c\u7ae5\u8001\u5e08\u8bf7\u4e0a\u5427\uff01\u201d \u3000\u3000\u7ae5\u6000\u9053\u89c1\u4ed6\u4e0d\u5378\u957f\u888d\uff0c\u624b\u4e2d\u53c8\u65e0\u5175\u5203\uff0c\u6108\u52a0\u6124\u6012\uff0c\u8bf4\u9053\uff1a\u201c\u7530\u8001\u5e08\u8981\u4ee5\u7a7a\u624b\u63a5\u5728\u4e0b\u8fd9\u5bf9\u6d41\u661f\u9524\u4e48\uff1f\u201d \u3000\u3000\u7530\u5f52\u519c\u6781\u5de5\u5fc3\u8ba1\uff0c\u884c\u4e8b\u81ea\u4fbf\u6301\u91cd\uff0c\u81ea\u5fd6\u5982\u80fd\u5728\u4e09\u62db\u4e24\u5f0f\u4e4b\u5185\u5c06\u4ed6\u6253\u5012\uff0c\u5728\u5929\u4e0b\u7fa4\u96c4\u4e4b\u524d\u5927\u663e\u5a01\u98ce\uff0c\u81ea\u662f\u518d\u5999\u4e0d\u8fc7\uff0c\u4f46\u770b\u5bf9\u65b9\u8eab\u8eaf\u96c4\u4f1f\uff0c\u808c\u8089\u4f3c\u94c1\uff0c\u5b9e\u975e\u6613\u4e0e\u4e4b\u8f88\u3002\u7b11\u9053\uff1a\u201c\u7ae5\u8001\u5e08\u540d\u6ee1\u664b\u9655\uff0c\u6c5f\u6e56\u4e0a\u597d\u6c49\u90a3\u4e00\u4e2a\u4e0d\u77e5\u6d41\u661f\u8d76\u6708\u7684\u7edd\u6280\uff0c\u5728\u4e0b\u4fbf\u4f7f\u5175\u5203\uff0c\u4e5f\u672a\u5fc5\u662f\u7ae5\u8001\u5e08\u7684\u5bf9\u624b\u3002\u201d\u53f3\u624b\u4e00\u62db\uff0c\u4ed6\u5927\u5f1f\u5b50\u66f9\u4e91\u5947\u53cc\u624b\u6367\u7740\u4e00\u67c4\u957f\u5251\uff0c\u5448\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u7530\u5f52\u519c\u63a5\u8fc7\u4e86\u5251\uff0c\u5de6\u624b\u4e00\u6446\uff0c\u7b11\u9053\uff1a\u201c\u8bf7\u5427\uff01\u201d\u7ae5\u6000\u9053\u89c1\u4ed6\u5251\u672a\u51fa\u9798\uff0c\u5fc3\u60f3\u4f60\u5df2\u5175\u5203\u5728\u624b\uff0c\u4f60\u7231\u4ec0\u4e48\u65f6\u5019\u62d4\u5251\uff0c\u90a3\u662f\u4f60\u81ea\u5df1\u7684\u4e8b\uff0c\u5f53\u4e0b\u624b\u6307\u642d\u4f4f\u9524\u94fe\u4e2d\u5fc3\u5411\u4e0b\u4e00\u8f6c\uff0c\u4e00\u5bf9\u6d41\u661f\u9524\u76f4\u7ad6\u4e0a\u6765\uff0c\u90a3\u9524\u94fe\u7adf\u5982\u662f\u4e24\u6839\u94c1\u68d2\u4e00\u822c\u3002\u7fa4\u8c6a\u9f50\u58f0\u79f0\u8d5e\uff1a\u201c\u597d\u529f\u592b\uff01\u201d \u3000\u3000\u559d\u5f69\u58f0\u4e2d\uff0c\u4ed6\u5de6\u9524\u4ecd\u662f\u7ad6\u5728\u534a\u7a7a\uff0c\u53f3\u9524\u5e73\u80f8\u5df2\u7136\u76f4\u51fb\u51fa\u53bb\uff0c\u4f46\u8fd9\u4e00\u9524\u98de\u5230\u79bb\u7530\u5f52\u519c\u80f8\u53e3\u7ea6\u6709\u5c3a\u534a\u4e4b\u5904\uff0c\u500f\u5730\u505c\u7559\u4e0d\u8fdb\uff0c\u5de6\u9524\u8fc5\u6377\u5f02\u5e38\u7684\u81ea\u540e\u8d76\u4e86\u4e0a\u6765\uff0c\u76f4\u51fb\u7530\u5f52\u519c\u7684\u5c0f\u8179\u3002\u524d\u9524\u865a\u62db\u8bf1\u654c\uff0c\u540e\u4e00\u9524\u624d\u662f\u5168\u529b\u51fa\u51fb\uff0c\u4ed6\u4e00\u4e0a\u6765\u4fbf\u4f7f\u51fa\u201c\u6d41\u661f\u8d76\u6708\u201d\u7684\u6210\u540d\u7edd\u6280\u3002 \u3000\u3000\u7530\u5f52\u519c\u5fae\u5fae\u4e00\u60ca\uff0c\u659c\u9000\u4e00\u6b65\uff0c\u957f\u5251\u6307\u51fa\uff0c\u7adf\u662f\u8fde\u7740\u5251\u9798\u523a\u4e86\u8fc7\u53bb\u3002\u7ae5\u6000\u9053\u5927\u6012\uff0c\u5fc3\u9053\uff1a\u201c\u4f60\u4e0d\u9664\u5251\u9798\uff0c\u5206\u660e\u662f\u77a7\u6211\u4e0d\u8d77\u3002\u201d\u5f53\u4e0b\u624b\u4e0a\u52a0\u52b2\uff0c\u5c06\u4e00\u5bf9\u94c1\u9524\u821e\u6210\u4e00\u56e2\u9ed1\u5149\u3002\u4ed6\u8fd9\u5bf9\u53cc\u9524\u4e00\u5feb\u4e00\u6162\uff0c\u4e00\u865a\u4e00\u5b9e\uff0c\u800c\u5feb\u8005\u672a\u5fc5\u771f\u5feb\uff0c\u6162\u8005\u4e5f\u672a\u5fc5\u771f\u6162\uff0c\u865a\u865a\u5b9e\u5b9e\uff0c\u53d8\u5316\u591a\u7aef\u3002\u7530\u5f52\u519c\u957f\u5251\u59cb\u7ec8\u4e0d\u51fa\u9798\uff0c\u4f46\u4e00\u62db\u4e00\u5f0f\uff0c\u4ecd\u662f\u4f9d\u7740\u201c\u5929\u9f99\u5251\u201d\u7684\u5251\u6cd5\u3002 \u3000\u3000\u62c6\u5f97\u4e09\u5341\u4f59\u62db\uff0c\u7530\u5f52\u519c\u5df2\u6478\u6e05\u695a\u5bf9\u65b9\u9524\u6cd5\u7684\u8def\u5b50\uff0c\u9661\u7136\u95f4\u957f\u5251\u4e00\u63a2\uff0c\u75be\u70b9\u7ae5\u6000\u9053\u5de6\u817f\u819d\u5f2f\u201c\u66f2\u6cc9\u7a74\u201d\u3002\u8fd9\u4e00\u62db\u5e76\u975e\u5251\u6cd5\uff0c\u957f\u5251\u8fde\u9798\uff0c\u7adf\u662f\u53d8\u4f5c\u5224\u5b98\u7b14\u7528\u3002\u7ae5\u6000\u9053\u5403\u4e86\u4e00\u60ca\uff0c\u9000\u540e\u4e24\u6b65\u3002\u7530\u5f52\u519c\u957f\u5251\u6a2a\u7838\uff0c\u51fb\u4ed6\u5927\u817f\uff0c\u8fd9\u4e00\u4e0b\u5374\u662f\u5c06\u5251\u9798\u5f53\u94c1\u950f\u4f7f\uff0c\u8fd9\u4e00\u62db\u201c\u67f3\u6797\u6362\u950f\u201d\uff0c\u539f\u662f\u950f\u6cd5\u3002\u4ed6\u5728\u4e24\u62db\u4e4b\u95f4\uff0c\u81ea\u5251\u6cd5\u53d8\u4e3a\u7b14\u6cd5\uff0c\u53c8\u81ea\u7b14\u6cd5\u53d8\u4e3a\u950f\u6cd5\u3002 \u3000\u3000\u7ae5\u6000\u9053\u5fc3\u4e2d\u4e00\u614c\uff0c\u5de6\u624b\u6d41\u661f\u9524\u5012\u5377\u4e0a\u6765\uff0c\u53f3\u624b\u5728\u9524\u94fe\u4e0a\u4e00\u63a8\uff0c\u94c1\u9524\u5411\u7530\u5f52\u519c\u7709\u5fc3\u76f4\u649e\u8fc7\u53bb\u3002\u8fd9\u662f\u4e00\u62db\u4e24\u8d25\u4ff1\u4f24\u7684\u6253\u6cd5\uff0c\u62da\u7740\u5927\u817f\u53d7\u5251\u9798\u4e00\u7838\uff0c\u94c1\u9524\u5374\u4e5f\u8981\u51fb\u4e2d\u4e86\u4ed6\u3002 \u3000\u3000\u7530\u5f52\u519c\u6ca1\u6599\u5230\u5bf9\u65b9\u7adf\u4e0d\u95ea\u907f\u653b\u7740\uff0c\u5251\u9798\u8ddd\u4ed6\u5927\u817f\u4e0d\u8fc7\u6570\u5bf8\uff0c\u5374\u89c9\u52b2\u98ce\u6251\u9762\uff0c\u94c1\u9524\u5df2\u98de\u4e86\u8fc7\u6765\uff0c\u82e5\u662f\u4e24\u4e0b\u9f50\u4e2d\uff0c\u5bf9\u65b9\u6700\u591a\u5e9f\u4e86\u4e00\u6761\u817f\uff0c\u81ea\u5df1\u5374\u662f\u8111\u6d46\u8ff8\u88c2\u4e4b\u7978\uff0c\u767e\u5fd9\u4e2d\u5012\u8f6c\u957f\u5251\uff0c\u5f80\u4ed6\u9524\u94fe\u4e2d\u642d\u53bb\u3002\u8fd9\u4e00\u4e0b\u8f6c\u653b\u4e3a\u5b88\uff0c\u767b\u5c45\u52a3\u52bf\u3002\u7ae5\u6000\u9053\u6d41\u661f\u9524\u4e00\u6536\uff0c\u9524\u94fe\u5df2\u5377\u4f4f\u957f\u5251\uff0c\u5f80\u91cc\u4e00\u593a\uff0c\u8ddf\u7740\u53f3\u9524\u6a2a\u51fb\u8fc7\u53bb\u3002 \u3000\u3000\u773c\u89c1\u7530\u5f52\u519c\u5175\u5203\u88ab\u5236\uff0c\u82e5\u8981\u9003\u5f97\u6027\u547d\uff0c\u957f\u5251\u975e\u6492\u624b\u4e0d\u53ef\uff0c\u53ea\u542c\u5f97\u5237\u7684\u4e00\u58f0\uff0c\u9752\u5149\u4e00\u95ea\uff0c\u957f\u5251\u7adf\u5df2\u51fa\u9798\uff0c\u5251\u5c16\u98a4\u5904\uff0c\u7ae5\u6000\u9053\u53f3\u8155\u4e2d\u5251\u3002\u539f\u6765\u4ed6\u4ee5\u9524\u94fe\u5377\u4f4f\u957f\u5251\uff0c\u4e00\u62c9\u4e00\u593a\u4e4b\u4e0b\uff0c\u6070\u597d\u5c06\u5251\u9798\u62d4\u8131\u3002\u7530\u5f52\u519c\u4e58\u673a\u6325\u5251\u4f24\u654c\uff0c\u8ddf\u7740\u62a2\u4e0a\u4e24\u6b65\uff0c\u5de6\u624b\u98df\u6307\u8fde\u52a8\uff0c\u70b9\u4e2d\u4e86\u4ed6\u80f8\u53e3\u4e09\u5904\u8981\u7a74\u3002 \u3000\u3000\u7ae5\u6000\u9053\u5168\u8eab\u9178\u9ebb\uff0c\u4e24\u679a\u6d41\u661f\u9524\u7838\u5c06\u4e0b\u6765\uff0c\u6253\u5f97\u5730\u4e0b\u7816\u5c51\u7eb7\u98de\u3002\u7530\u5f52\u519c\u8fd8\u5251\u5165\u9798\uff0c\u7b11\u541f\u541f\u5730\u9053\uff1a\u201c\u627f\u8ba9\uff01\u627f\u8ba9\uff01\u201d\u5750\u5165\u4e86\u7ae5\u6000\u9053\u5148\u524d\u5750\u8fc7\u7684\u592a\u5e08\u6905\u4e2d\u3002 \u3000\u3000\u4ed6\u867d\u5f97\u80dc\uff0c\u4f46\u5385\u4e0a\u7fa4\u8c6a\u90fd\u89c9\u8fd9\u4e00\u4ed7\u8d62\u5f97\u4fa5\u5e78\uff0c\u9887\u6709\u72e1\u8bc8\u4e4b\u610f\uff0c\u5e76\u975e\u4ee5\u771f\u5b9e\u672c\u9886\u53d6\u80dc\uff0c\u56e0\u6b64\u9664\u4e86\u6c64\u6c9b\u7b49\u4eba\u5be5\u5be5\u51e0\u4e0b\u5f69\u58f0\uff0c\u8c01\u90fd\u6ca1\u559d\u5f69\u53eb\u597d\u3002 \u3000\u3000\u7ae5\u6000\u9053\u7a74\u9053\u88ab\u70b9\u540e\u7ad9\u7740\u4e0d\u52a8\uff0c\u6446\u7740\u4e2a\u6325\u9524\u51fb\u4eba\u7684\u59ff\u5f0f\uff0c\u6a2a\u7709\u6012\u76ee\uff0c\u6a21\u6837\u6781\u662f\u53ef\u7b11\u3002\u7530\u5f52\u519c\u5374\u4e0d\u7ed9\u4ed6\u89e3\u7a74\uff0c\u5750\u5728\u6905\u4e2d\u81ea\u884c\u8ddf\u6c64\u6c9b\u8bf4\u7b11\uff0c\u4efb\u7531\u7ae5\u6000\u9053\u51fa\u4e11\u9732\u4e56\uff0c\u7adf\u662f\u89c6\u82e5\u65e0\u7779\u3002\u5385\u4e0a\u81ea\u6709\u4e0d\u5c11\u70b9\u7a74\u6253\u7a74\u540d\u5bb6\uff0c\u5fc3\u4e2d\u5747\u611f\u4e0d\u5fff\uff0c\u4f46\u8c01\u90fd\u77e5\u9053\uff0c\u53ea\u8981\u4e00\u51fa\u53bb\u7ed9\u7ae5\u6000\u9053\u89e3\u4e86\u7a74\uff0c\u4fbf\u662f\u8ddf\u7530\u5f52\u519c\u548c\u6c64\u6c9b\u8fc7\u4e0d\u53bb\u3002\u7530\u5f52\u519c\u8fd8\u4e0d\u600e\u6837\uff0c\u90a3\u7518\u9716\u60e0\u4e03\u7701\u6c64\u6c9b\u5374\u662f\u540d\u5934\u592a\u5927\uff0c\u90a3\u4e9b\u70b9\u7a74\u6253\u7a74\u540d\u5bb6\u5341\u4e5d\u662f\u8001\u6210\u6301\u91cd\u4e4b\u8f88\uff0c\u90fd\u4e0d\u613f\u4e3a\u8fd9\u4e8b\u800c\u5f97\u7f6a\u6c64\u6c9b\u3002\u4f46\u773c\u89c1\u7ae5\u6000\u9053\u50bb\u4e0d\u695e\u767b\u7684\u7ad9\u5728\u90a3\u91cc\uff0c\u8bb8\u591a\u4eba\u90fd\u4e0d\u7981\u4e3a\u4ed6\u96be\u53d7\u3002 \u3000\u3000\u897f\u9996\u5e2d\u4e0a\u4e00\u6761\u5927\u6c49\u970d\u5730\u7ad9\u8d77\uff0c\u624b\u4e2d\u62d6\u4e86\u4e00\u6839\u53c8\u7c97\u53c8\u957f\u7684\u9554\u94c1\u68cd\uff0c\u8fc8\u6b65\u51fa\u6765\uff0c\u90a3\u94c1\u68cd\u62d6\u8fc7\u7816\u5730\uff0c\u545b\u5577\u5577\u76f4\u54cd\u3002\u4ed6\u8d70\u5230\u7530\u5f52\u519c\u9762\u524d\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u59d3\u7530\u7684\uff0c\u4f60\u7ed9\u4eba\u5bb6\u89e3\u7a74\u9053\u554a\uff0c\u8ba9\u4ed6\u50f5\u5728\u8fd9\u91cc\u5e72\u4ec0\u4e48\uff1f\u201d\u7530\u5f52\u519c\u5fae\u7b11\u9053\uff1a\u201c\u9601\u4e0b\u662f\u8c01\uff1f\u201d\u90a3\u5927\u6c49\u9053\uff1a\u201c\u6211\u53eb\u674e\u5ef7\u8c79\uff0c\u4f60\u542c\u89c1\u8fc7\u6ca1\u6709\uff1f\u201d \u3000\u3000\u4ed6\u8fd9\u4e00\u4e0b\u81ea\u62a5\u59d3\u540d\uff0c\u58f0\u5982\u9739\u96f3\uff0c\u9707\u5f97\u4f17\u4eba\u8033\u4e2d\u90fd\u662f\u55e1\u55e1\u4f5c\u54cd\u3002\u7fa4\u8c6a\u4e00\u542c\u6b64\u4eba\u4fbf\u662f\u674e\u5ef7\u8c79\uff0c\u90fd\u662f\u5fae\u611f\u8be7\u5f02\u3002\u539f\u6765\u674e\u5ef7\u8c79\u662f\u4e94\u53f0\u6d3e\u7684\u638c\u95e8\u5927\u5f1f\u5b50\uff0c\u5728\u9655\u897f\u5ef6\u5b89\u5e9c\u5f00\u8bbe\u9556\u5c40\uff0c\u4ee5\u4e94\u90ce\u68cd\u6cd5\u9a70\u540d\u5929\u4e0b\uff0c\u4ed6\u7684\u201c\u4e94\u90ce\u9556\u5c40\u201d\u5728\u5317\u4e03\u7701\u4e5f\u662f\u9887\u6709\u58f0\u540d\u3002\u4f17\u4eba\u5fc3\u60f3\u4ed6\u65e2\u662f\u51fa\u540d\u7684\u9556\u5934\uff0c\u81ea\u662f\u7cbe\u660e\u5f3a\u5e72\uff0c\u8001\u4e8e\u4e16\u6545\uff0c\u4e0d\u6599\u7adf\u662f\u8fd9\u6837\u7684\u4e00\u4e2a\u83bd\u592b\u3002 \u3000\u3000\u7530\u5f52\u519c\u5750\u5728\u6905\u4e2d\uff0c\u5e76\u4e0d\u62ac\u8eab\uff0c\u4e94\u53f0\u6d3e\u674e\u5ef7\u8c79\u7684\u540d\u5b57\uff0c\u4ed6\u81ea\u662f\u542c\u89c1\u8fc7\u7684\uff0c\u4f46\u4ed6\u5047\u4f5c\u8bb6\u8272\uff0c\u6447\u5934\u9053\uff1a\u201c\u6ca1\u542c\u89c1\u8fc7\u3002\u9601\u4e0b\u662f\u54ea\u4e00\u5bb6\u54ea\u4e00\u6d3e\u7684\u554a\uff1f\u201d\u674e\u5ef7\u8c79\u5927\u6012\uff0c\u559d\u9053\uff1a\u201c\u4e94\u53f0\u6d3e\u4f60\u542c\u89c1\u8fc7\u6ca1\u6709\uff1f\u201d\u7530\u5f52\u519c\u4ecd\u662f\u6447\u5934\uff0c\u8138\u4e0a\u5374\u663e\u5f97\u53c8\u662f\u62b1\u6b49\uff0c\u53c8\u662f\u60f6\u6050\uff0c\u8bf4\u9053\uff1a\u201c\u662f\u4e94\u53f0\uff1f\u4e0d\u662f\u4e03\u53f0\u3001\u516b\u53f0\u4e48\uff1f\u201d\u4ed6\u5c06\u201c\u516b\u53f0\u201d\u4e24\u5b57\uff0c\u6545\u610f\u5ff5\u5f97\u8ddf\u201c\u738b\u516b\u86cb\u201d\u7684\u201c\u516b\u86cb\u201d\u76f8\u4f3c\uff0c\u5385\u4e0a\u4e00\u4e9b\u5e74\u8f7b\u4eba\u5fcd\u4e0d\u4f4f\u4fbf\u7b11\u5c06\u8d77\u6765\u3002 \u3000\u3000\u597d\u5728\u674e\u5ef7\u8c79\u5012\u6ca1\u89c9\u5bdf\uff0c\u8bf4\u9053\uff1a\u201c\u662f\u4e94\u53f0\u6d3e\uff01\u5927\u5bb6\u662f\u6b66\u6797\u4e00\u8109\uff0c\u4f60\u5feb\u89e3\u7ae5\u8001\u5e08\u7684\u7a74\u9053\u3002\u201d\u7530\u5f52\u519c\u9053\uff1a\u201c\u4f60\u8ddf\u7ae5\u8001\u5e08\u662f\u597d\u670b\u53cb\u4e48\uff1f\u201d\u674e\u5ef7\u8c79\u9053\uff1a\u201c\u4e0d\u662f\uff01\u6211\u8ddf\u4ed6\u7d20\u4e0d\u76f8\u8bc6\u3002\u4f46\u4f60\u8fd9\u822c\u4f5c\u5f04\u4eba\uff0c\u592a\u4e0d\u6210\u8bdd\u3002\u6211\u77a7\u4e0d\u8fc7\u773c\u3002\u201d\u7530\u5f52\u519c\u76b1\u7709\u9053\uff1a\u201c\u6211\u53ea\u4f1a\u70b9\u7a74\uff0c\u5f53\u5e74\u5e08\u7236\u6ca1\u6559\u6211\u89e3\u7a74\u3002\u201d\u674e\u5ef7\u8c79\u9053\uff1a\u201c\u6211\u4e0d\u4fe1\uff01\u201d \u3000\u3000\u798f\u5eb7\u5b89\u3001\u5b89\u63d0\u7763\u7b49\u4e00\u5e72\u4eba\u542c\u7740\u4ed6\u4e8c\u4eba\u5bf9\u7b54\uff0c\u5f88\u89c9\u6709\u8da3\uff0c\u5747\u77e5\u7530\u5f52\u519c\u662f\u5728\u4f5c\u5f04\u8fd9\u4e2a\u6d51\u4eba\u3002\u8fd9\u4e9b\u4eb2\u8d35\u5927\u5b98\u770b\u7740\u4f17\u6b66\u5e08\u6bd4\u6b66\uff0c\u539f\u662f\u5f53\u4f5c\u4e00\u6869\u8d4f\u5fc3\u4e50\u4e8b\uff0c\u4fbf\u5982\u770b\u620f\u542c\u66f2\u3001\u77a7\u53d8\u620f\u6cd5\u4e00\u822c\uff0c\u4e00\u8fde\u4e32\u4e0d\u505c\u624b\u7684\u6fc0\u70c8\u6253\u6597\u4e4b\u540e\uff0c\u6709\u4e2a\u5c0f\u4e11\u6765\u63d2\u79d1\u6253\u6d51\uff0c\u5012\u4e5f\u5174\u5473\u76ce\u7136\u3002 \u3000\u3000\u7530\u5f52\u519c\u4e00\u773c\u77a5\u89c1\u798f\u5eb7\u5b89\u7b11\u563b\u563b\u7684\u795e\u6c14\uff0c\u66f4\u6b32\u51d1\u8da3\uff0c\u4fbf\u9053\uff1a\u201c\u8fd9\u6837\u5427\uff01\u4f60\u5728\u4ed6\u819d\u5f2f\u91cc\u7528\u529b\u8e22\u4e00\u811a\uff0c\u4fbf\u89e3\u5f00\u4e86\u4ed6\u7a74\u9053\u3002\u201d\u674e\u5ef7\u8c79\u9053\uff1a\u201c\u5f53\u771f\uff1f\u201d\u7530\u5f52\u519c\u9053\uff1a\u201c\u5e08\u7236\u4ee5\u524d\u8fd9\u6837\u6559\u6211\uff0c\u4e0d\u8fc7\u6211\u81ea\u5df1\u4e5f\u6ca1\u8bd5\u8fc7\u3002\u201d \u3000\u3000\u674e\u5ef7\u8c79\u63d0\u8d77\u53f3\u8db3\uff0c\u5728\u7ae5\u6000\u9053\u819d\u5f2f\u91cc\u4e00\u8e22\u3002\u4ed6\u8fd9\u4e00\u811a\u529b\u9053\u7528\u5f97\u4e0d\u5927\uff0c\u4f46\u7ae5\u6000\u9053\u8fd8\u662f\u5e94\u811a\u800c\u5012\uff0c\u6eda\u5728\u5730\u4e0b\uff0c\u7ffb\u4e86\u51e0\u4e2a\u8f6c\u8eab\uff0c\u624b\u8db3\u59ff\u5f0f\u4e1d\u6beb\u4e0d\u53d8\uff0c\u53ea\u662f\u4ee5\u76f4\u7acb\u53d8\u4e3a\u6a2a\u8eba\u3002\u539f\u6765\u674e\u5ef7\u8c79\u662f\u4e0a\u4e86\u5f53\uff0c\u8981\u6551\u4eba\u53cd\u800c\u5c06\u4eba\u8e22\u5012\u3002 \u3000\u3000\u798f\u5eb7\u5b89\u54c8\u54c8\u5927\u7b11\uff0c\u4f17\u8d35\u5b98\u8ddf\u7740\u7b11\u4e86\u8d77\u6765\u3002\u7fa4\u8c6a\u672c\u6765\u6709\u4eba\u60f3\u65a5\u8d23\u7530\u5f52\u519c\u7684\uff0c\u4f46\u89c1\u798f\u5eb7\u5b89\u4e00\u7b11\uff0c\u90fd\u4e0d\u6562\u51fa\u58f0\u4e86\u3002 \u3000\u3000\u7b11\u58f0\u672a\u7edd\uff0c\u5ffd\u542c\u5f97\u547c\u547c\u547c\u4e09\u54cd\uff0c\u4e09\u53ea\u9152\u676f\u98de\u5230\u534a\u7a7a\uff0c\u4f17\u4eba\u4e00\u9f50\u62ac\u5934\u77a7\u53bb\uff0c\u53ea\u89c1\u4e09\u676f\u4e92\u76f8\u78b0\u649e\uff0c\u4e52\u4e53\u4e24\u58f0\uff0c\u649e\u5f97\u7c89\u788e\u3002 \u3000\u3000\u4f17\u4eba\u76ee\u5149\u987a\u7740\u9152\u676f\u7684\u788e\u7247\u671b\u4e0b\u5730\u6765\uff0c\u53ea\u89c1\u7ae5\u6000\u9053\u5df2\u7136\u7ad9\u8d77\uff0c\u624b\u4e2d\u63e1\u7740\u4e00\u53ea\u9152\u676f\uff0c\u8bf4\u9053\uff1a\u201c\u54ea\u4e00\u4f4d\u82f1\u96c4\u6697\u4e2d\u76f8\u52a9\uff0c\u7ae5\u6000\u9053\u7ec8\u8eab\u4e0d\u5fd8\u5927\u5fb7\u3002\u201d\u8bf4\u7740\u5c06\u9152\u676f\u63e3\u5728\u6000\u4e2d\uff0c\u72e0\u72e0\u77a7\u4e86\u7530\u5f52\u519c\u4e00\u773c\uff0c\u6025\u5954\u51fa\u5385\u3002 \u3000\u3000\u539f\u6765\u6709\u4eba\u63b7\u676f\u98de\u7a7a\u4e92\u649e\uff0c\u4e43\u662f\u8981\u5f15\u5f00\u5404\u4eba\u7684\u76ee\u5149\uff0c\u5f53\u4f17\u4eba\u4e00\u9f50\u77a7\u7740\u7a7a\u4e2d\u7684\u4e09\u53ea\u9152\u676f\u4e4b\u65f6\uff0c\u4ed6\u5374\u53c8\u4ee5\u4e00\u53ea\u9152\u676f\u63b7\u53bb\uff0c\u6253\u5728\u7ae5\u6000\u9053\u80cc\u5fc3\u7684\u201c\u7b4b\u7f29\u7a74\u201d\u4e0a\uff0c\u89e3\u5f00\u4e86\u4ed6\u88ab\u70b9\u7684\u7a74\u9053\u3002 \u3000\u3000\u8fd9\u4e00\u4e0b\u5385\u4e0a\u8bb8\u591a\u9ad8\u624b\u90fd\u88ab\u7792\u8fc7\uff0c\u5927\u5bb6\u5747\u77e5\u8fd9\u4e00\u4e0b\u529f\u592b\u751a\u662f\u9ad8\u660e\uff0c\u5374\u8c01\u4e5f\u4e0d\u77e5\u662f\u4f55\u4eba\u51fa\u624b\u3002 \u3000\u3000\u6c64\u6c9b\u62ff\u8fc7\u4e24\u53ea\u9152\u676f\uff0c\u659f\u6ee1\u4e86\u9152\uff0c\u8d70\u5230\u80e1\u6590\u5e2d\u524d\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u5144\u53f0\u9762\u751f\u5f97\u5f88\u54ea\uff01\u8bf7\u6559\u5c0a\u59d3\u5927\u540d\uff0c\u9601\u4e0b\u98de\u676f\u89e3\u7a74\u7684\u529f\u592b\uff0c\u5728\u4e0b\u94a6\u4f69\u5f97\u7d27\u3002\u201d \u3000\u3000\u80e1\u6590\u9002\u624d\u5ff5\u7740\u7ae5\u6000\u9053\u662f\u953a\u6c0f\u4e09\u96c4\u7684\u670b\u53cb\uff0c\u53c8\u89c1\u7530\u5f52\u519c\u8fb1\u4eba\u592a\u751a\uff0c\u52a8\u4e86\u4fa0\u4e49\u5fc3\u80a0\uff0c\u867d\u77e5\u8eab\u5728\u9669\u5730\uff0c\u5374\u5fcd\u4e0d\u4f4f\u51fa\u624b\u66ff\u4ed6\u89e3\u5f00\u7a74\u9053\uff0c\u90a3\u77e5\u6c64\u6c9b\u76ee\u5149\u9510\u5229\uff0c\u7adf\u7136\u77a7\u7834\u3002\u80e1\u6590\u8bf4\u9053\uff1a\u201c\u5728\u4e0b\u662f\u534e\u62f3\u95e8\u7684\uff0c\u655d\u59d3\u7a0b\uff0c\u8349\u5b57\u7075\u80e1\u3002\u6c64\u5927\u4fa0\u8bf4\u4ec0\u4e48\u98de\u676f\u89e3\u7a74\uff0c\u5728\u4e0b\u53ef\u4e0d\u61c2\u4e86\u3002\u201d\u6c64\u6c9b\u5475\u5475\u7b11\u9053\uff1a\u201c\u9601\u4e0b\u4f55\u5fc5\u9690\u7792\uff1f\u8fd9\u4e00\u5e2d\u4e0a\u4e0d\u662f\u5c11\u4e86\u56db\u53ea\u9152\u676f\u4e48\uff1f\u201d\u80e1\u6590\u5fc3\u60f3\uff1a\u201c\u770b\u6765\u4ed6\u4e5f\u4e0d\u662f\u77a7\u89c1\u6211\u98de\u63b7\u9152\u676f\uff0c\u53ea\u4e0d\u8fc7\u67e5\u5230\u6211\u5e2d\u4e0a\u5c11\u4e86\u56db\u53ea\u9152\u676f\u800c\u5df2\u3002\u201d\u4e8e\u662f\u8f6c\u5934\u5411\u90ed\u7389\u5802\u9053\uff1a\u201c\u90ed\u8001\u5e08\uff0c\u539f\u6765\u4f60\u8eab\u6000\u7edd\u6280\uff0c\u98de\u63b7\u9152\u676f\uff0c\u89e3\u4e86\u90a3\u59d3\u7ae5\u7684\u7a74\u9053\u3002\u4f69\u670d\u4f69\u670d\uff01\u201d \u3000\u3000\u90ed\u7389\u5802\u6700\u662f\u80c6\u5c0f\u6015\u4e8b\uff0c\u552f\u6050\u60f9\u7978\uff0c\u5fd9\u9053\uff1a\u201c\u6211\u6ca1\u63b7\u676f\uff0c\u6211\u6ca1\u63b7\u676f\u3002\u201d \u3000\u3000\u6c64\u6c9b\u8bc6\u5f97\u4ed6\u5df2\u4e45\uff0c\u77e5\u4ed6\u6ca1\u8fd9\u4e2a\u80fd\u8010\uff0c\u4e00\u770b\u4ed6\u540c\u5e2d\u8bf8\u4eba\uff0c\u53ea\u534e\u62f3\u95e8\u7684\u8521\u5a01\u6210\u540d\u5df2\u4e45\uff0c\u4f46\u7d20\u77e5\u4ed6\u6697\u5668\u529f\u592b\u751a\u662f\u5e73\u5e38\uff0c\u4e8e\u662f\u5c06\u53f3\u624b\u7684\u4e00\u676f\u9152\u9012\u7ed9\u80e1\u6590\uff0c\u7b11\u9053\uff1a\u201c\u7a0b\u5144\uff0c\u4eca\u65e5\u5e78\u4f1a\uff01\u5144\u5f1f\u656c\u4f60\u4e00\u676f\u3002\u201d\u8bf4\u7740\u4e3e\u676f\u548c\u4ed6\u7684\u9152\u676f\u8f7b\u8f7b\u4e00\u78b0\u3002 \u3000\u3000\u53ea\u542c\u5f97\u4e52\u7684\u4e00\u54cd\uff0c\u80e1\u6590\u624b\u4e2d\u7684\u9152\u676f\u5ffd\u5730\u788e\u88c2\uff0c\u70ed\u9152\u548c\u74f7\u7247\u9f50\u98de\uff0c\u90fd\u6253\u5728\u80e1\u6590\u80f8\u53e3\u3002\u539f\u6765\u6c64\u6c9b\u5728\u8fd9\u4e00\u78b0\u4e4b\u4e2d\uff0c\u6697\u8fd0\u6f5c\u529b\uff0c\u80e1\u6590\u7684\u6b66\u529f\u5982\u4f55\uff0c\u8fd9\u53ea\u4e00\u78b0\u4fbf\u53ef\u8bd5\u4e86\u51fa\u6765\u3002\u4e0d\u6599\u4e24\u676f\u76f8\u78b0\uff0c\u534e\u62f3\u95e8\u638c\u95e8\u4eba\u7a0b\u7075\u80e1\u4f3c\u4e4e\u534a\u70b9\u5185\u529f\u4e5f\u6ca1\u6709\uff0c\u9152\u676f\u7c89\u788e\u4e4b\u4e0b\uff0c\u9152\u6d46\u74f7\u7247\u90fd\u6e85\u5411\u4ed6\u4e00\u8fb9\u3002\u6c64\u6c9b\u624b\u4e2d\u9152\u676f\u56fa\u7136\u5b8c\u597d\u65e0\u635f\uff0c\u8863\u4e0a\u4e5f\u4e0d\u6e85\u5230\u534a\u70b9\u9152\u6c34\u3002\u6c64\u6c9b\u5fae\u7b11\u9053\uff1a\u201c\u5bf9\u4e0d\u8d77\uff01\u201d\u81ea\u884c\u56de\u5f52\u5165\u5ea7\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u5c0f\u8001\u513f\u7a00\u677e\u5e73\u5e38\uff0c\u90a3\u4e48\u98de\u676f\u89e3\u7a74\u7684\u5374\u53c8\u662f\u8c01\uff1f\u201d \u3000\u3000\u53ea\u89c1\u7530\u5f52\u519c\u548c\u674e\u5ef7\u8c79\u5df2\u5728\u5385\u5fc3\u4ea4\u8d77\u624b\u6765\u3002\u7530\u5f52\u519c\u624b\u6301\u957f\u5251\uff0c\u9752\u5149\u95ea\u95ea\uff0c\u8fd9\u6b21\u5251\u5df2\u51fa\u9798\uff0c\u4e0d\u6562\u518d\u884c\u6258\u5927\u3002\u674e\u5ef7\u8c79\u4f7f\u5f00\u4e94\u90ce\u68cd\u6cd5\uff0c\u4e00\u62db\u62db\u201c\u63a8\u7a97\u671b\u6708\u201d\u3001\u201c\u80cc\u68cd\u649e\u949f\u201d\u3001\u201c\u767d\u733f\u95ee\u8def\u201d\u3001\u201c\u6a2a\u62e6\u5929\u95e8\u201d\uff0c\u53ea\u89c1\u4ed6\u5708\u3001\u70b9\u3001\u5288\u3001\u8f67\u3001\u6311\u3001\u649e\u3001\u6492\u3001\u6740\uff0c\u62db\u719f\u529b\u731b\uff0c\u4f7f\u5c06\u51fa\u6765\u6781\u6709\u5a01\u52bf\u3002\u7fa4\u8c6a\u77a7\u5f97\u6697\u6697\u5fc3\u670d\uff0c\u8fd9\u624d\u77e5\u4e94\u90ce\u9556\u5c40\u8fd1\u5341\u591a\u5e74\u6765\u58f0\u540d\u6781\u54cd\uff0c\u674e\u603b\u9556\u5934\u679c\u662f\u6709\u8fc7\u4eba\u7684\u6280\u827a\u3002\u7530\u5f52\u519c\u7684\u5929\u9f99\u5251\u81ea\u4e5f\u662f\u6b66\u6797\u4e2d\u7684\u4e00\u7edd\uff0c\u6fc0\u6597\u4e2d\u6e10\u6e10\u5360\u5230\u4e86\u4e0a\u98ce\uff0c\u4f46\u8981\u5728\u77ed\u65f6\u5185\u53d6\u80dc\uff0c\u770b\u6765\u7740\u5b9e\u4e0d\u6613\u3002 \u3000\u3000\u9163\u6597\u4e4b\u4e2d\uff0c\u7530\u5f52\u519c\u5ffd\u5730\u8863\u895f\u4e00\u7ffb\uff0c\u545b\u5577\u4e00\u58f0\uff0c\u4ece\u957f\u8863\u4e0b\u62d4\u51fa\u4e00\u67c4\u77ed\u5200\u3002\u70db\u706b\u4e4b\u4e0b\uff0c\u8fd9\u5200\u5149\u8292\u95ea\u70c1\u4e0d\u5b9a\uff0c\u8fdc\u8fdc\u77a7\u53bb\uff0c\u5982\u5b9d\u77f3\uff0c\u5982\u7409\u7483\uff0c\u5982\u6e05\u6c34\uff0c\u5982\u5bd2\u51b0\u3002 \u3000\u3000\u53ea\u89c1\u674e\u5ef7\u8c79\u4f7f\u4e00\u62db\u201c\u5012\u53cd\u4e7e\u5764\u201d\uff0c\u53cd\u68cd\u5288\u843d\uff0c\u7530\u5f52\u519c\u4ee5\u53f3\u624b\u957f\u5251\u4e00\u62e8\u3002\u674e\u5ef6\u8c79\u94c1\u68cd\u5411\u524d\u76f4\u9001\uff0c\u6b63\u662f\u4e00\u62db\u201c\u9752\u9f99\u51fa\u6d1e\u201d\uff0c\u8fd9\u4e00\u62db\u4ece\u9501\u5589\u67aa\u6cd5\u4e2d\u53d8\u6765\uff0c\u4e43\u662f\u5947\u9669\u4e4b\u7740\u3002\u4f46\u4ed6\u4f7f\u5f97\u7eaf\u719f\uff0c\u65f6\u523b\u5206\u5bf8\uff0c\u65e0\u4e0d\u62ff\u634f\u6070\u5230\u597d\u5904\uff0c\u6b63\u662f\u4ece\u5947\u9669\u4e2d\u89c1\u529f\u529b\u3002\u7530\u5f52\u519c\u5374\u4e0d\u9000\u95ea\uff0c\u5de6\u624b\u5355\u5200\u4e0a\u64a9\uff0c\u5f53\u7684\u4e00\u54cd\uff0c\u9554\u94c1\u68cd\u65ad\u4e3a\u4e24\u622a\u3002\u7530\u5f52\u519c\u4e58\u4ed6\u5fc3\u4e2d\u614c\u4e71\uff0c\u53f3\u624b\u5251\u6025\u523a\u800c\u81f3\uff0c\u5728\u4ed6\u624b\u8155\u4e0a\u4e00\u5212\uff0c\u7b4b\u8109\u5df2\u65ad\u3002 \u3000\u3000\u674e\u5ef7\u8c79\u5927\u53eb\u4e00\u58f0\uff0c\u629b\u4e0b\u94c1\u68cd\u3002\u4ed6\u8155\u7b4b\u65e2\u65ad\uff0c\u4e00\u53ea\u53f3\u624b\u4ece\u6b64\u4fbf\u5e9f\u4e86\u3002\u4ed6\u4e00\u751f\u5355\u7ec3\u4e94\u90ce\u68cd\uff0c\u68cd\u68d2\u529f\u592b\u5fc5\u987b\u53cc\u624b\u9f50\u4f7f\uff0c\u53f3\u624b\u4e00\u5e9f\uff0c\u7b49\u4e8e\u6b66\u529f\u5168\u5931\u3002\u970e\u65f6\u4e4b\u95f4\uff0c\u60f3\u8d77\u534a\u751f\u82e6\u82e6\u6323\u6765\u7684\u5a01\u540d\u4e00\u8d25\u6d82\u5730\uff0c\u9556\u5c40\u5b50\u53ea\u597d\u5173\u95e8\uff0c\u81ea\u5df1\u94b1\u8d22\u6765\u5f97\u5bb9\u6613\uff0c\u7d20\u65e0\u79ef\u84c4\uff0c\u4e00\u5bb6\u8001\u5c0f\u7acb\u65f6\u4fbf\u9677\u5165\u51bb\u9981\u4e4b\u5883\uff1b\u53c8\u60f3\u8d77\u81ea\u5df1\u751f\u6027\u66b4\u8e81\uff0c\u751f\u5e73\u7ed3\u4e0b\u51a4\u5bb6\u5bf9\u5934\u4e0d\u5c11\uff0c\u522b\u8bf4\u4ec7\u4eba\u5bfb\u4e0a\u95e8\u6765\u65e0\u6cd5\u5bf9\u4ed8\uff0c\u4fbf\u662f\u5e73\u65e5\u53d7\u8fc7\u81ea\u5df1\u6c14\u7684\u540c\u884c\u540e\u8f88\u3001\u5e02\u4e95\u5c0f\u4eba\uff0c\u51b7\u5632\u70ed\u8bbd\u8d77\u6765\u53c8\u600e\u80fd\u53d7\u5f97\u4e86\uff1f\u4ed6\u662f\u4e2a\u76f4\u809a\u76f4\u80a0\u4e4b\u4eba\uff0c\u53ea\u89c9\u518d\u591a\u6d3b\u4e00\u523b\uff0c\u8fd9\u53e3\u6c14\u4e5f\u662f\u54bd\u4e0d\u4e0b\u53bb\uff0c\u5de6\u624b\u62fe\u8d77\u534a\u622a\u94c1\u68cd\uff0c\u549a\u7684\u4e00\u58f0\uff0c\u51fb\u5728\u81ea\u5df1\u8111\u76d6\u4e4b\u4e0a\uff0c\u767b\u65f6\u6bd9\u547d\u3002 \u3000\u3000\u5927\u5385\u4e0a\u4f17\u4eba\u9f50\u58f0\u60ca\u547c\uff0c\u7ad9\u7acb\u8d77\u6765\uff0c\u5927\u5bb6\u89c1\u4ed6\u63d0\u8d77\u534a\u622a\u94c1\u68cd\uff0c\u90fd\u9053\u5fc5\u662f\u8ddf\u7530\u5f52\u519c\u62da\u547d\uff0c\u90a3\u6599\u5230\u7adf\u4f1a\u81ea\u6215\u800c\u6b7b\u3002\u8fd9\u4e00\u4e2a\u53d8\u6545\uff0c\u60ca\u5f97\u4eba\u4eba\u90fd\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002\u5b89\u63d0\u7763\u9053\uff1a\u201c\u626b\u5174\uff0c\u626b\u5174\uff01\u201d\u547d\u4eba\u5c06\u5c38\u8eab\u62ac\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u674e\u5ef7\u8c79\u5982\u662f\u5728\u6fc0\u6597\u4e2d\u88ab\u7530\u5f52\u519c\u4e00\u5251\u523a\u6b7b\uff0c\u90a3\u4e5f\u7f62\u4e86\uff0c\u5982\u6b64\u8fd9\u822c\u903c\u5f97\u4ed6\u81ea\u6740\uff0c\u4f17\u4eba\u5747\u611f\u6c14\u6124\u3002 \u3000\u3000\u897f\u5357\u89d2\u4e0a\u4e00\u4eba\u7ad9\u4e86\u8d77\u6765\uff0c\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u7530\u8001\u5e08\uff0c\u4f60\u7528\u5b9d\u5200\u524a\u65ad\u94c1\u68cd\uff0c\u80dc\u5c40\u5df2\u5b9a\uff0c\u4f55\u5fc5\u518d\u65ad\u4ed6\u624b\u7b4b\uff1f\u201d\u7530\u5f52\u519c\u9053\uff1a\u201c\u5175\u5668\u65e0\u773c\uff0c\u5018\u82e5\u5728\u4e0b\u5b66\u827a\u4e0d\u7cbe\uff0c\u7ed9\u4ed6\u626b\u4e0a\u4e00\u68cd\uff0c\u90a3\u4e5f\u662f\u6ca1\u547d\u7684\u4e86\u3002\u201d \u3000\u3000\u90a3\u4eba\u51b7\u7b11\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u4f60\u662f\u5b66\u827a\u5f88\u7cbe\u7684\u4e86\uff1f\u201d\u7530\u5f52\u519c\u9053\uff1a\u201c\u4e0d\u6562\uff01\u8001\u5144\u5982\u662f\u4e0d\u670d\uff0c\u5c3d\u53ef\u4e0b\u573a\u6307\u6559\u3002\u201d\u90a3\u4eba\u9053\uff1a\u201c\u5f88\u597d\uff01\u201d \u3000\u3000\u8fd9\u4eba\u4f7f\u7684\u4e5f\u662f\u957f\u5251\uff0c\u4e0b\u573a\u540e\u7adf\u662f\u4e0d\u901a\u59d3\u540d\uff0c\u5237\u5237\u4e24\u5251\uff0c\u5411\u7530\u5f52\u519c\u5f53\u80f8\u76f4\u523a\u3002\u7530\u5f52\u519c\u4ecd\u662f\u53f3\u5251\u5de6\u5200\uff0c\u62c6\u4e0d\u4e03\u516b\u5408\uff0c\u5f53\u7684\u4e00\u58f0\uff0c\u5b9d\u5200\u53c8\u524a\u65ad\u4e86\u4ed6\u7684\u957f\u5251\uff0c\u8ddf\u7740\u4e00\u5251\u523a\u4f24\u4e86\u4ed6\u5de6\u80f8\u3002 \u3000\u3000\u7fa4\u8c6a\u89c1\u4ed6\u51fa\u624b\u72e0\u8fa3\uff0c\u63a5\u4e8c\u8fde\u4e09\u7684\u6709\u4eba\u4e0a\u6765\u6311\u6218\uff0c\u8fd9\u4e9b\u4eba\u5927\u534a\u4e0d\u662f\u4e3a\u4e86\u4e89\u593a\u7389\u9f99\u676f\uff0c\u53ea\u89c9\u674e\u5ef7\u8c79\u6b7b\u5f97\u751a\u60e8\uff0c\u8981\u632b\u6298\u4e00\u4e0b\u7530\u5f52\u519c\u7684\u5a01\u98ce\u3002\u53ef\u662f\u4ed6\u5de6\u624b\u5b9d\u5200\u5b9e\u5728\u592a\u8fc7\u5389\u5bb3\uff0c\u4e0d\u8bba\u4ec0\u4e48\u5175\u5203\uff0c\u78b0\u4e0a\u4e86\u4fbf\u5373\u65ad\u6298\uff0c\u5230\u540e\u6765\u8fde\u4e94\u884c\u8f6e\u3001\u72ec\u80e1\u94dc\u4eba\u8fd9\u4e9b\u602a\u5f02\u5175\u5203\u4e5f\u90fd\u51fa\u573a\uff0c\u4f46\u65e0\u4e00\u80fd\u5f53\u4ed6\u5b9d\u5200\u7684\u950b\u9510\u3002 \u3000\u3000\u6709\u4eba\u51fa\u8a00\u76f8\u6fc0\uff0c\u8bf4\u9053\uff1a\u201c\u7530\u8001\u5e08\uff0c\u4f60\u6b66\u529f\u4e5f\u53ea\u5e73\u5e73\uff0c\u5355\u9760\u4e00\u67c4\u5b9d\u5200\uff0c\u90a3\u7b97\u7684\u662f\u4ec0\u4e48\u82f1\u96c4\uff1f\u4f60\u6709\u79cd\u7684\uff0c\u4fbf\u8ddf\u6211\u62f3\u811a\u4e0a\u89c1\u9ad8\u4e0b\u3002\u201d\u7530\u5f52\u519c\u7b11\u9053\uff1a\u201c\u8fd9\u5b9d\u5200\u662f\u6211\u5929\u9f99\u95e8\u4e16\u4ee3\u76f8\u4f20\u7684\u9547\u95e8\u4e4b\u5b9d\u3002\u4eca\u65e5\u798f\u5927\u5e05\u8981\u5404\u5bb6\u5404\u6d3e\u8f83\u91cf\u9ad8\u4e0b\u3002\u6211\u662f\u5929\u9f99\u95e8\u7684\u638c\u95e8\u4eba\uff0c\u4e0d\u7528\u672c\u95e8\u4e4b\u5b9d\uff0c\u5374\u7528\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4ed6\u51fa\u624b\u4e4b\u9645\uff0c\u4e5f\u771f\u662f\u4e0d\u7559\u60c5\u9762\uff0c\u5b9d\u5200\u4e00\u65ad\u4eba\u5175\u5203\uff0c\u53f3\u624b\u957f\u5251\u4fbf\u6bc1\u4eba\u624b\u8db3\uff0c\u8fde\u8d25\u5341\u4f59\u4eba\u540e\uff0c\u65c1\u4eba\u89c1\u4e0a\u53bb\u4e0d\u662f\u65ad\u624b\uff0c\u4fbf\u662f\u6298\u8db3\uff0c\u65e0\u4e0d\u8eab\u53d7\u91cd\u4f24\uff0c\u867d\u6709\u81ea\u6043\u6b66\u529f\u80fd\u80dc\u4e8e\u4ed6\u7684\uff0c\u4f46\u60f3\u4e0d\u51fa\u62b5\u6321\u4ed6\u5b9d\u5200\u7684\u6cd5\u95e8\uff0c\u4e2a\u4e2a\u754f\u60e7\u675f\u624b\u3002 \u3000\u3000\u6c64\u6c9b\u89c1\u65e0\u4eba\u518d\u4e0a\u6765\u6311\u6218\uff0c\u5475\u5475\u7b11\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u4eca\u65e5\u4e00\u6218\uff0c\u4f60\u5929\u9f99\u95e8\u5a01\u9707\u5929\u4e0b\uff0c\u6211\u505a\u54e5\u54e5\u7684\u8138\u4e0a\u4e5f\u6709\u5149\u5f69\u3002\u6765\u6765\u6765\uff0c\u6211\u656c\u4f60\u4e00\u676f\u5e86\u529f\u9152\uff01\u201d \u3000\u3000\u80e1\u6590\u5411\u7a0b\u7075\u7d20\u77a7\u4e86\u4e00\u773c\uff0c\u7a0b\u7075\u7d20\u7f13\u7f13\u6447\u5934\u3002\u80e1\u6590\u81ea\u4e5f\u5341\u5206\u607c\u6068\u7530\u5f52\u519c\u7684\u5f3a\u6a2a\uff0c\u4f46\u4e00\u6765\u4e0d\u6562\u6cc4\u9732\u8eab\u5206\uff0c\u9002\u624d\u98de\u676f\u63b7\u89e3\u7ae5\u6000\u9053\u7684\u7a74\u9053\uff0c\u51e0\u4e4e\u5df2\u88ab\u6c64\u6c9b\u770b\u7834\uff1b\u4e8c\u6765\u8fd9\u67c4\u5b9d\u5200\u5982\u6b64\u5389\u5bb3\uff0c\u5b9e\u662f\u751f\u5e73\u4ece\u6240\u672a\u89c1\u7684\u5229\u5668\uff0c\u82e5\u662f\u4e0a\u53bb\u76f8\u6597\uff0c\u5148\u5df2\u8f93\u4e86\u4e03\u6210\u3002\u53c8\u60f3\uff1a\u201c\u5f53\u65e5\u4ed6\u7387\u4f17\u53bb\u82d7\u4eba\u51e4\u5bb6\u4e2d\u4e4b\u65f6\uff0c\u4f55\u4ee5\u4e0d\u643a\u8fd9\u67c4\u5b9d\u5200\uff1f\u90a3\u65f6\u5982\u679c\u4ed6\u5b9d\u5200\u5728\u624b\uff0c\u8bf4\u4e0d\u5b9a\u6211\u5df2\u6d3b\u4e0d\u5230\u4eca\u65e5\u4e86\u3002\u201d\u4ed6\u4e0d\u77e5\u5929\u9f99\u95e8\u8fd9\u628a\u5b9d\u5200\u7531\u5357\u5317\u4e8c\u5b97\u8f6e\u503c\u6267\u638c\uff0c\u5f53\u65f6\u5374\u5c1a\u5728\u5357\u5b97\u7684\u638c\u95e8\u4eba\u624b\u4e2d\u3002 \u3000\u3000\u53ea\u89c1\u7530\u5f52\u519c\u5f97\u610f\u626c\u626c\u7684\u4e3e\u8d77\u9152\u676f\uff0c\u6b63\u8981\u51d1\u5230\u5507\u8fb9\uff0c\u5ffd\u542c\u5f97\u55e4\u7684\u4e00\u58f0\uff0c\u4e00\u7c92\u94c1\u83e9\u63d0\u5411\u4ed6\u9152\u676f\u98de\u4e86\u8fc7\u53bb\uff0c\u60f3\u662f\u6709\u4eba\u53d1\u6697\u5668\u8981\u6253\u7834\u4ed6\u7684\u9152\u676f\u3002 \u3000\u3000\u7530\u5f52\u519c\u89c6\u82e5\u4e0d\u89c1\uff0c\u4ecd\u662f\u4e3e\u676f\u559d\u9152\u3002\u66f9\u96ea\u5947\u53eb\u9053\uff1a\u201c\u5e08\u7236\uff0c\u5c0f\u5fc3\uff01\u201d\u7530\u5f52\u519c\u5f85\u90a3\u94c1\u83e9\u63d0\u98de\u5230\u8eab\u524d\uff0c\u4f38\u51fa\u624b\u6307\uff0c\u55d2\u7684\u4e00\u58f0\u8f7b\u54cd\uff0c\u5c06\u94c1\u83e9\u63d0\u5f39\u51fa\u5385\u95e8\u3002\u4f17\u4eba\u89c1\u4ed6\u9732\u4e86\u8fd9\u624b\uff0c\u867d\u7136\u4e0d\u76f4\u4ed6\u7684\u4e3a\u4eba\uff0c\u5374\u4e5f\u6709\u4eba\u7981\u4e0d\u4f4f\u53eb\u4e86\u58f0\uff1a\u201c\u597d\uff01\u201d \u3000\u3000\u90a3\u7c92\u94c1\u83e9\u63d0\u75be\u98de\u800c\u51fa\uff0c\u5385\u95e8\u4e2d\u6b63\u597d\u8d70\u8fdb\u4e00\u4e2a\u4eba\u6765\u3002\u90a3\u4eba\u89c1\u6697\u5668\u98de\u5411\u81ea\u5df1\u80f8\u53e3\uff0c\u4e5f\u662f\u4f38\u6307\u4e00\u5f39\uff0c\u8bf4\u9053\uff1a\u201c\u4fbf\u8fd9\u822c\u8fce\u63a5\u5ba2\u4eba\u4e48\uff1f\u201d\u90a3\u94c1\u83e9\u63d0\u7ecf\u4ed6\u4e00\u5f39\uff0c\u7acb\u65f6\u53d1\u51fa\u5c16\u9510\u7684\u7834\u7a7a\u4e4b\u58f0\uff0c\u5411\u7530\u5f52\u519c\u98de\u56de\u3002\u4ece\u58f0\u97f3\u542c\u6765\uff0c\u8fd9\u4e00\u5f39\u4e4b\u529b\u5b9e\u662f\u60ca\u4eba\uff0c\u6bd4\u7530\u5f52\u519c\u5389\u5bb3\u591a\u4e86\u3002 \u3000\u3000\u7530\u5f52\u519c\u4e00\u60ca\u4e4b\u4e0b\uff0c\u4e0d\u6562\u4f38\u624b\u53bb\u63a5\uff0c\u8eab\u5b50\u5411\u53f3\u4e00\u95ea\u3002\u4ed6\u8eab\u540e\u7ad9\u7740\u4e00\u540d\u798f\u5eb7\u5b89\u7684\u536b\u58eb\uff0c\u542c\u5f97\u98ce\u58f0\uff0c\u94c1\u83e9\u63d0\u5df2\u5230\u8eab\u524d\uff0c\u4e0d\u53ca\u95ea\u8ba9\uff0c\u5fd9\u4f38\u624b\u6284\u4f4f\uff0c\u4f46\u542c\u5580\u7684\u4e00\u54cd\uff0c\u4e2d\u6307\u9aa8\u5df2\u7136\u6298\u65ad\uff0c\u75bc\u5f97\u201c\u554a\u201d\u7684\u4e00\u58f0\u5927\u53eb\u3002 \u3000\u3000\u4f17\u4eba\u89c1\u5c0f\u5c0f\u4e00\u679a\u94c1\u83e9\u63d0\uff0c\u7adf\u80fd\u5728\u4e00\u5f39\u4e4b\u4e0b\u5c06\u4eba\u6307\u9aa8\u6298\u65ad\uff0c\u6b64\u4eba\u6307\u529b\u7684\u51cc\u5389\uff0c\u5b9e\u662f\u7f55\u89c1\u7f55\u95fb\uff0c\u4e00\u9f50\u6ce8\u76ee\u5411\u4ed6\u77a7\u53bb\u3002 \u3000\u3000\u53ea\u89c1\u6b64\u4eba\u6781\u7626\u6781\u9ad8\uff0c\u5de6\u624b\u62ff\u7740\u53ea\u864e\u6491\uff0c\u80a9\u5934\u659c\u6302\u836f\u56ca\uff0c\u4e00\u4ef6\u9752\u5e03\u957f\u888d\u6d17\u5f97\u892a\u5c3d\u4e86\u989c\u8272\uff0c\u62d6\u7740\u53cc\u7834\u70c2\u6ce5\u6cde\u7684\u5e03\u978b\uff0c\u88c5\u675f\u6253\u626e\uff0c\u4fbf\u662f\u4e61\u9547\u95f4\u5e38\u89c1\u7684\u8d70\u65b9\u90ce\u4e2d\uff0c\u53ea\u662f\u76ee\u5149\u70af\u70af\uff0c\u987e\u76fc\u4f3c\u7535\uff0c\u4e94\u5b98\u5947\u5927\uff0c\u7c97\u7709\u3001\u5927\u773c\u3001\u5927\u9f3b\u3001\u5927\u53e3\u3001\u53cc\u8033\u62db\u98ce\uff0c\u98a7\u9aa8\u9ad8\u8038\uff0c\u8fd9\u526f\u76f8\u8c8c\u4efb\u8c01\u4e00\u89c1\u4e4b\u540e\u90fd\u6c38\u8fdc\u4e0d\u4f1a\u5fd8\u8bb0\uff0c\u5934\u53d1\u5df2\u7136\u82b1\u767d\uff0c\u81f3\u5c11\u5df2\u6709\u4e94\u5341\u6765\u5c81\uff0c\u8138\u4e0a\u751f\u6ee1\u4e86\u9ed1\u6591\u3002\u4ed6\u8eab\u540e\u8ddf\u7740\u4e8c\u4eba\uff0c\u4f3c\u662f\u4ed6\u5f1f\u5b50\u6216\u662f\u53ae\u4ec6\uff0c\u795e\u6001\u6781\u662f\u606d\u8c28\u3002 \u3000\u3000\u80e1\u6590\u548c\u7a0b\u7075\u7d20\u89c1\u4e86\u5f53\u5148\u90a3\u4eba\u8fd8\u4e0d\u600e\u6837\uff0c\u4e00\u770b\u5230\u4ed6\u8eab\u540e\u4e8c\u4eba\uff0c\u5374\u662f\u5403\u4e86\u4e00\u60ca\uff0c\u539f\u6765\u4e00\u4e2a\u8001\u4e66\u751f\uff0c\u6b63\u662f\u7a0b\u7075\u7d20\u7684\u5927\u5e08\u5144\u6155\u5bb9\u666f\u5cb3\uff1b\u53e6\u4e00\u4e2a\u9a7c\u80cc\u8ddb\u8db3\u7684\u5973\u5b50\uff0c\u5374\u662f\u5979\u4e09\u5e08\u59ca\u859b\u9e4a\u3002\u80e1\u6590\u548c\u7a0b\u7075\u7d20\u5bf9\u77a7\u4e00\u773c\uff0c\u90fd\u662f\u5927\u5947\uff1a\u201c\u600e\u4e48\u4ed6\u4e24\u4e2a\u6b7b\u5bf9\u5934\u8d70\u5230\u4e86\u4e00\u8d77\uff1f\u859b\u9e4a\u7684\u4e08\u592b\u59dc\u94c1\u5c71\u5374\u53c8\u4e0d\u5728\uff1f\u201d\u7a0b\u7075\u7d20\u89c1\u80e1\u6590\u773c\u5149\u4e2d\u9732\u51fa\u7591\u95ee\u4e4b\u8272\uff0c\u77e5\u4ed6\u662f\u95ee\u90a3\u4e2a\u8d70\u65b9\u90ce\u4e2d\u662f\u8c01\uff0c\u4fbf\u7f13\u7f13\u7684\u6447\u4e86\u6447\u5934\uff0c\u5979\u53ef\u4e5f\u4e0d\u8ba4\u8bc6\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u201c\u554a\u54df\u201d\u4e00\u58f0\u60e8\u53eb\uff0c\u90a3\u6307\u5934\u6298\u65ad\u7684\u536b\u58eb\u8dcc\u5012\u5728\u5730\uff0c\u4e0d\u4f4f\u6253\u6eda\uff0c\u5c06\u4e00\u53ea\u624b\u638c\u9ad8\u9ad8\u4e3e\u8d77\u3002\u4f17\u4eba\u521d\u65f6\u5747\u611f\u5947\u602a\uff1a\u201c\u65e2\u7136\u8eab\u4e3a\u798f\u5927\u5e05\u7684\u536b\u58eb\uff0c\u81ea\u6709\u76f8\u5f53\u6b66\u529f\uff0c\u600e\u5730\u65ad\u4e86\u4e00\u6839\u6307\u5934\u4e5f\u62b5\u53d7\u4e0d\u8d77\uff1f\u201d\u5f85\u89c1\u5230\u4ed6\u90a3\u53ea\u624b\u638c\u5176\u9ed1\u5982\u58a8\uff0c\u624d\u77e5\u539f\u6765\u662f\u4e2d\u4e86\u5267\u6bd2\u3002 \u3000\u3000\u8fd9\u6b21\u5929\u4e0b\u5404\u5bb6\u5404\u6d3e\u638c\u95e8\u4eba\u5927\u805a\u4f1a\uff0c\u798f\u5e9c\u4f17\u536b\u58eb\u96c4\u5fc3\u52c3\u52c3\uff0c\u9887\u6709\u548c\u5404\u6d3e\u597d\u624b\u4e00\u4e89\u96c4\u957f\u4e4b\u610f\uff0c\u8981\u663e\u5f97\u5728\u4eac\u4e2d\u5c45\u5b98\u7684\u82f1\u96c4\u786e\u6709\u771f\u624d\u5b9e\u5b66\uff0c\u51b3\u4e0d\u8f93\u4e8e\u5404\u5730\u7684\u8349\u83bd\u8c6a\u6770\u3002\u8fd9\u624b\u6307\u6298\u65ad\u7684\u536b\u58eb\u5f52\u5468\u94c1\u9e6a\u6240\u7ba1\uff0c\u4ed6\u89c1\u6b64\u4eba\u5982\u6b64\u51fa\u4e11\uff0c\u7709\u5934\u4e00\u76b1\uff0c\u4e0a\u524d\u559d\u9053\uff1a\u201c\u8d77\u6765\uff0c\u8d77\u6765\uff01\u8fd9\u4e00\u70b9\u513f\u82e6\u5934\u4e5f\u6328\u4e0d\u8d77\uff0c\u592a\u4e0d\u6210\u8bdd\u5566\uff01\u201d\u90a3\u4eba\u5bf9\u5468\u94c1\u9e6a\u5f88\u662f\u60e7\u6015\uff0c\u5fd9\u9053\uff1a\u201c\u662f\uff0c\u662f\uff01\u201d\u6323\u624e\u7740\u5f85\u8981\u7ad9\u8d77\uff0c\u7a81\u7136\u8eab\u5b50\u4e00\u6643\uff0c\u6655\u4e86\u8fc7\u53bb\u3002\u5468\u94c1\u9e6a\u4ece\u9152\u5e2d\u4e0a\u53d6\u8fc7\u4e00\u53cc\u7b77\u5b50\uff0c\u631f\u8d77\u90a3\u9897\u94c1\u83e9\u63d0\u4e00\u770b\uff0c\u89c1\u4e0a\u9762\u523b\u7740\u4e00\u4e2a\u201c\u67ef\u201d\u5b57\uff0c\u8138\u8272\u5fae\u53d8\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u5170\u5dde\u67ef\u5b50\u5bb9\u67ef\u4e09\u7237\uff0c\u4f60\u8d8a\u6765\u8d8a\u957f\u8fdb\u5566\u3002\u8fd9\u94c1\u83e9\u63d0\u4e0a\u5582\u7684\u6bd2\u836f\u53ef\u5389\u5bb3\u5f97\u7d27\u54ea\uff01\u201d \u3000\u3000\u53ea\u89c1\u4eba\u4e1b\u4e2d\u7ad9\u8d77\u4e00\u4e2a\u6ee1\u8138\u9ebb\u5b50\u7684\u5927\u6c49\uff0c\u8bf4\u9053\uff1a\u201c\u5468\u8001\u7237\u4f60\u53ef\u522b\u8840\u53e3\u55b7\u4eba\u3002\u8fd9\u679a\u94c1\u83e9\u63d0\u662f\u6211\u6240\u53d1\uff0c\u90a3\u662f\u4e0d\u9519\uff0c\u6211\u53ea\u662f\u77a7\u4e0d\u8fc7\u4eba\u5bb6\u72c2\u5984\u81ea\u5927\uff0c\u8981\u6253\u788e\u4eba\u5bb6\u624b\u4e2d\u9152\u676f\u3002\u6211\u67ef\u5bb6\u6697\u5668\u4e0a\u51b3\u8ba1\u4e0d\u8bb8\u5582\u6bd2\uff0c\u4e16\u4ee3\u76f8\u4f20\uff0c\u5411\u4e3a\u7981\u4f8b\uff0c\u67ef\u5b50\u5bb9\u518d\u4e0d\u8096\uff0c\u4e5f\u4e0d\u6562\u574f\u4e86\u7956\u5b97\u7684\u5bb6\u89c4\u3002\u201d\u5468\u94c1\u9e6a\u89c1\u95fb\u5e7f\u535a\uff0c\u4e5f\u77e5\u67ef\u5bb6\u64c5\u4f7f\u4e03\u822c\u6697\u5668\uff0c\u4f46\u5411\u6765\u4e25\u7981\u5582\u6bd2\uff0c\u5f53\u4e0b\u6c89\u541f\u4e0d\u8bed\uff0c\u53ea\u9053\uff1a\u201c\u8fd9\u53ef\u5947\u4e86\uff01\u201d \u3000\u3000\u67ef\u5b50\u5bb9\u9053\uff1a\u201c\u8ba9\u6211\u77a7\u77a7\uff01\u201d\u8d70\u8fc7\u6765\u62ff\u8d77\u90a3\u679a\u94c1\u83e9\u63d0\u4e00\u770b\uff0c\u9053\uff1a\u201c\u8fd9\u662f\u6211\u7684\u94c1\u83e9\u63d0\u554a\uff0c\u8fd9\u4e0a\u9762\u600e\u4f1a\u6709\u6bd2\u2026\u2026\u554a\u54df\uff01\u201d\u7a81\u7136\u95f4\u5927\u53eb\u4e00\u58f0\uff0c\u5c06\u94c1\u83e9\u63d0\u6295\u5728\u5730\u4e0b\uff0c\u53f3\u624b\u8fde\u6325\uff0c\u4f3c\u4e4e\u53d7\u5230\u70c8\u706b\u70e7\u7099\u4e00\u822c\u3002\u53ea\u89c1\u4ed6\u8138\u8272\u60e8\u767d\uff0c\u8981\u5c06\u53d7\u4f24\u7684\u624b\u6307\u9001\u5230\u53e3\u4e2d\u542e\u5438\uff0c\u5468\u94c1\u9e6a\u75be\u51fa\u4e00\u638c\uff0c\u65ab\u4e2d\u4ed6\u7684\u5c0f\u81c2\uff0c\u53eb\u9053\uff1a\u201c\u5438\u4e0d\u5f97\uff01\u201d\u6321\u4f4f\u4ed6\u624b\u6307\u5165\u53e3\uff0c\u770b\u4ed6\u5927\u62c7\u6307\u548c\u98df\u6307\u4e24\u6839\u624b\u6307\u65f6\uff0c\u90fd\u5df2\u80bf\u4e86\u8d77\u6765\uff0c\u8272\u5982\u6de1\u58a8\u3002\u67ef\u5b50\u5bb9\u5168\u8eab\u53d1\u98a4\uff0c\u989d\u89d2\u4e0a\u9ec4\u8c46\u5927\u7684\u6c57\u73e0\u4e00\u6ef4\u6ef4\u7684\u6e17\u4e86\u51fa\u6765\u3002 \u3000\u3000\u90a3\u8d70\u65b9\u90ce\u4e2d\u5411\u7740\u6155\u5bb9\u666f\u5cb3\u9053\uff1a\u201c\u7ed9\u8fd9\u4e24\u4eba\u6cbb\u4e00\u6cbb\u3002\u201d\u6155\u5bb9\u666f\u5cb3\u9053\uff1a\u201c\u662f\uff01\u201d\u4ece\u6000\u4e2d\u53d6\u51fa\u4e00\u76d2\u836f\u818f\uff0c\u5728\u67ef\u5b50\u5bb9\u548c\u90a3\u536b\u58eb\u624b\u4e0a\u6d82\u4e86\u4e00\u4e9b\u3002\u67ef\u5b50\u5bb9\u98a4\u6296\u6e10\u6b62\uff0c\u90a3\u536b\u58eb\u4e5f\u9192\u4e86\u8f6c\u6765\u3002 \u3000\u3000\u7fa4\u8c6a\u8fd9\u624d\u9192\u609f\uff0c\u67ef\u5b50\u5bb9\u53d1\u94c1\u83e9\u63d0\u6253\u7530\u5f52\u519c\u7684\u9152\u676f\uff0c\u7530\u5f52\u519c\u968f\u624b\u5f39\u51fa\uff0c\u53c8\u7ed9\u90a3\u8d70\u65b9\u90ce\u4e2d\u5f39\u56de\u3002\u4f46\u8d70\u65b9\u90ce\u4e2d\u5c31\u8fd9\u4e48\u4e00\u5f39\uff0c\u5df2\u5728\u94c1\u83e9\u63d0\u4e0a\u5582\u4e86\u6781\u5389\u5bb3\u7684\u6bd2\u836f\u3002\u8fd9\u7b49\u4e0b\u6bd2\u7684\u672c\u9886\uff0c\u6c5f\u6e56\u4e0a\u6050\u6015\u53ea\u6709\u4e00\u4eba\u3002\u5385\u4e0a\u4e0d\u5c11\u4eba\u5df2\u5728\u7a83\u7a83\u79c1\u8bed\uff1a\u201c\u6bd2\u624b\u836f\u738b\uff0c\u6bd2\u624b\u836f\u738b\uff01\u83ab\u975e\u662f\u6bd2\u624b\u836f\u738b\uff1f\u201d \u3000\u3000\u5468\u94c1\u9e6a\u8d70\u8fd1\u524d\u53bb\uff0c\u5411\u90a3\u8d70\u65b9\u90ce\u4e2d\u4e00\u62b1\u62f3\uff0c\u8bf4\u9053\uff1a\u201c\u9601\u4e0b\u5c0a\u59d3\u5927\u540d\uff1f\u201d\u90a3\u4eba\u5fae\u5fae\u4e00\u7b11\uff0c\u5e76\u4e0d\u56de\u7b54\u3002\u6155\u5bb9\u666f\u5cb3\u9053\uff1a\u201c\u5728\u4e0b\u6155\u5bb9\u666f\u5cb3\uff0c\u8fd9\u662f\u62d9\u8346\u859b\u9e4a\u3002\u201d\u4ed6\u987f\u4e86\u4e00\u987f\uff0c\u624d\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u54b1\u592b\u5987\u7684\u5e08\u7236\uff0c\u77f3\u5148\u751f\uff0c\u6c5f\u6e56\u4e0a\u9001\u4ed6\u8001\u4eba\u5bb6\u4e00\u4e2a\u5916\u53f7\uff0c\u53eb\u4f5c\u2018\u6bd2\u624b\u836f\u738b\u2019\uff01\u201d \u3000\u3000\u8fd9\u201c\u6bd2\u624b\u836f\u738b\u201d\u56db\u5b57\u4e00\u51fa\u53e3\uff0c\u65c1\u4eba\u8fd8\u90fd\u7f62\u4e86\uff0c\u8981\u77e5\u4e0e\u4f1a\u7684\u4e0d\u662f\u4e00\u6d3e\u638c\u95e8\uff0c\u591a\u534a\u4fbf\u662f\u5404\u6d3e\u7684\u8006\u5bbf\u957f\u8001\uff0c\u5927\u90fd\u77e5\u9053\u201c\u6bd2\u624b\u836f\u738b\u201d\u4e43\u662f\u5f53\u4e16\u4f7f\u6bd2\u7684\u7b2c\u4e00\u9ad8\u624b\uff0c\u6155\u5bb9\u666f\u5cb3\u5c31\u7b97\u4e0d\u8bf4\uff0c\u4e5f\u65e9\u731c\u60f3\u662f\u4ed6\u3002\u4f46\u8fd9\u56db\u4e2a\u5b57\u542c\u5728\u7a0b\u7075\u7d20\u548c\u80e1\u6590\u8033\u4e2d\uff0c\u5b9e\u662f\u8be7\u5f02\u65e0\u6bd4\u3002\u7a0b\u7075\u7d20\u66f4\u4e3a\u6c14\u607c\uff0c\u5fc3\u60f3\u8fd9\u4eba\u4e0d\u4f46\u5047\u5192\u5148\u5e08\u540d\u5934\uff0c\u800c\u8fd9\u53e5\u8bdd\u51fa\u8bf8\u5927\u5e08\u5144\u4e4b\u53e3\uff0c\u5c24\u5176\u4ee4\u5979\u60b2\u6124\u96be\u5e73\u3002\u53e6\u4e00\u4ef6\u4e8b\u4e5f\u4f7f\u5979\u751a\u662f\u5947\u602a\uff1a\u4e09\u5e08\u59ca\u859b\u9e4a\u539f\u662f\u4e8c\u5e08\u5144\u59dc\u94c1\u5c71\u4e4b\u59bb\uff0c\u4e24\u4eba\u6240\u751f\u7684\u513f\u5b50\u4e5f\u5df2\u957f\u5927\u6210\u4eba\uff0c\u4f55\u4ee5\u8fd9\u65f6\u5927\u5e08\u5144\u5374\u516c\u7136\u79f0\u5979\u4e3a\u201c\u62d9\u8346\u201d\uff1f \u3000\u3000\u5979\u6599\u77e5\u8fd9\u4e2d\u95f4\u5fc5\u5df2\u53d1\u751f\u6781\u91cd\u5927\u7684\u53d8\u6545\uff0c\u773c\u4e0b\u96be\u4ee5\u67e5\u7a76\uff0c\u552f\u6709\u9759\u89c2\u5176\u53d8\u3002 \u3000\u3000\u5468\u94c1\u9e6a\u867d\u7136\u52c7\u608d\uff0c\u4f46\u542c\u5230\u201c\u6bd2\u624b\u836f\u738b\u201d\u7684\u540d\u5934\uff0c\u8fd8\u662f\u4e0d\u7981\u53d8\u8272\uff0c\u62b1\u62f3\u8bf4\u4e86\u53e5\uff1a\u201c\u4e45\u4ef0\uff01\u4e45\u4ef0\uff01\u201d\u77f3\u5148\u751f\u4f38\u51fa\u624b\u53bb\uff0c\u7b11\u9053\uff1a\u201c\u9601\u4e0b\u5c0a\u59d3\u5927\u540d\uff0c\u54b1\u4fe9\u4eb2\u8fd1\u4eb2\u8fd1\u3002\u201d\u5468\u94c1\u9e6a\u970d\u5730\u9000\u5f00\u4e00\u6b65\uff0c\u62b1\u62f3\u9053\uff1a\u201c\u5728\u4e0b\u5468\u94c1\u9e6a\uff0c\u77f3\u524d\u8f88\u597d\uff01\u201d\u4ed6\u80c6\u5b50\u518d\u5927\uff0c\u4e5f\u51b3\u4e0d\u6562\u53bb\u548c\u6bd2\u624b\u836f\u738b\u62c9\u624b\u3002 \u3000\u3000\u77f3\u5148\u751f\u5475\u5475\u5927\u7b11\uff0c\u8d70\u5230\u798f\u5eb7\u5b89\u9762\u524d\uff0c\u8eac\u8eab\u4e00\u63d6\uff0c\u8bf4\u9053\uff1a\u201c\u5c71\u91ce\u95f2\u4eba\uff0c\u53c2\u89c1\u5927\u5e05\uff01\u201d\u8fd9\u65f6\u798f\u5eb7\u5b89\u8eab\u65c1\u7684\u536b\u58eb\u5df2\u5c06\u6bd2\u624b\u836f\u738b\u7684\u6765\u5386\u7980\u544a\u4e86\u4ed6\uff0c\u798f\u5eb7\u5b89\u773c\u89c1\u4ed6\u53ea\u662f\u624b\u6307\u8f7b\u5f39\u94c1\u83e9\u63d0\uff0c\u4fbf\u5373\u4f24\u4e86\u4e24\u4eba\uff0c\u77e5\u9053\u6b64\u4eba\u6781\u662f\u4e86\u5f97\uff0c\u5f53\u4e0b\u5fae\u5fae\u6b20\u8eab\uff0c\u8bf4\u9053\uff1a\u201c\u5148\u751f\u8bf7\u5750\uff01\u201d \u3000\u3000\u77f3\u5148\u751f\u5e26\u540c\u6155\u5bb9\u666f\u5cb3\u3001\u859b\u9e4a\u592b\u5987\u5728\u4e00\u65c1\u5750\u4e86\u3002\u9644\u8fd1\u7fa4\u8c6a\u7eb7\u7eb7\u907f\u8ba9\uff0c\u8c01\u4e5f\u4e0d\u6562\u8ddf\u4ed6\u4e09\u4eba\u6328\u8fd1\uff0c\u970e\u65f6\u4e4b\u95f4\uff0c\u4ed6\u5e08\u5f92\u4e09\u4eba\u8eab\u65c1\u7a7a\u8361\u8361\u5730\u6e05\u51fa\u4e86\u4e00\u5927\u7247\u5730\u65b9\u3002 \u3000\u3000\u4e00\u540d\u6b66\u5b98\u8d70\u4e86\u8fc7\u53bb\uff0c\u79bb\u77f3\u5148\u751f\u4e94\u5c3a\u4fbf\u5373\u7ad9\u5b9a\uff0c\u5c06\u4e89\u593a\u5fa1\u676f\u4ee5\u5b9a\u95e8\u6d3e\u9ad8\u4e0b\u7684\u89c4\u77e9\u8bf4\u4e86\uff0c\u8bdd\u4e00\u8bf4\u5b8c\uff0c\u7acb\u5373\u9000\u5f00\uff0c\u552f\u6050\u6cbe\u67d3\u5230\u4ed6\u8eab\u4e0a\u7684\u4e00\u4e1d\u6bd2\u6c14\u3002 \u3000\u3000\u77f3\u5148\u751f\u5fae\u7b11\u9053\uff1a\u201c\u5c0a\u9a7e\u8d35\u59d3\uff1f\u201d\u90a3\u6b66\u5b98\u9053\uff1a\u201c\u655d\u59d3\u5df4\u3002\u201d\u77f3\u5148\u751f\u9053\uff1a\u201c\u5df4\u8001\u7237\uff0c\u4f60\u4f55\u5fc5\u89c1\u6211\u7b49\u5bb3\u6015\uff1f\u8001\u592b\u7684\u5916\u53f7\u53eb\u4f5c\u2018\u6bd2\u624b\u836f\u738b\u2019\uff0c\u867d\u4f1a\u4e0b\u6bd2\uff0c\u4e5f\u4f1a\u7528\u836f\u6cbb\u75c5\u554a\u3002\u5df4\u8001\u7237\u8138\u4e0a\u9690\u5e03\u9752\u6c14\uff0c\u8179\u4e2d\u4f3c\u6709\u8708\u86a3\u86f0\u4f0f\uff0c\u82e5\u4e0d\u901f\u6cbb\uff0c\u5341\u5929\u540e\u53ea\u6015\u6027\u547d\u96be\u4fdd\u3002\u201d\u90a3\u6b66\u5b98\u5927\u5403\u4e00\u60ca\uff0c\u5c06\u4fe1\u5c06\u7591\uff0c\u9053\uff1a\u201c\u809a\u5b50\u91cc\u600e\u4f1a\u6709\u8708\u86a3\uff1f\u201d\u77f3\u5148\u751f\u9053\uff1a\u201c\u5df4\u8001\u7237\u6700\u8fd1\u53ef\u66fe\u548c\u4eba\u4e89\u5435\uff1f\u201d \u3000\u3000\u5317\u4eac\u57ce\u91cc\u505a\u6b66\u5b98\u7684\uff0c\u548c\u4eba\u4e89\u5435\u4e43\u662f\u5bb6\u5e38\u4fbf\u996d\uff0c\u90a3\u81ea\u7136\u662f\u6709\u7684\uff0c\u90a3\u59d3\u5df4\u7684\u6b66\u5b98\u60ca\u9053\uff1a\u201c\u6709\u554a\uff01\u96be\u9053\u2026\u2026\u96be\u9053\u90a3\u72d7\u8d3c\u5411\u6211\u4e0b\u4e86\u6bd2\u624b\uff1f\u201d\u77f3\u5148\u751f\u4ece\u836f\u56ca\u4e2d\u53d6\u51fa\u4e24\u7c92\u9752\u8272\u836f\u4e38\uff0c\u8bf4\u9053\uff1a\u201c\u5df4\u8001\u7237\u82e5\u662f\u4fe1\u5f97\u8fc7\uff0c\u4e0d\u59a8\u7528\u9152\u541e\u670d\u4e86\u8fd9\u4e24\u7c92\u836f\u3002\u201d \u3000\u3000\u90a3\u6b66\u5b98\u7ed9\u4ed6\u8bf4\u5f97\u5fc3\u4e2d\u53d1\u6bdb\uff0c\u9690\u9690\u4fbf\u89c9\u809a\u4e2d\u4f3c\u6709\u8708\u86a3\u722c\u52a8\uff0c\u5f53\u4e0b\u66f4\u4e0d\u591a\u60f3\uff0c\u63a5\u8fc7\u836f\u4e38\u4e22\u5728\u5634\u91cc\uff0c\u62ff\u8d77\u4e00\u7897\u9152\uff0c\u9aa8\u561f\u561f\u7684\u559d\u4e0b\u53bb\u3002\u8fc7\u4e0d\u591a\u65f6\uff0c\u4fbf\u89c9\u809a\u75db\uff0c\u80f8\u53e3\u70e6\u6076\u6b32\u5455\uff0c\u201c\u54c7\u201d\u7684\u4e00\u58f0\uff0c\u5455\u4e86\u8bb8\u591a\u98df\u7269\u51fa\u6765\u3002 \u3000\u3000\u77f3\u5148\u751f\u62a2\u4e0a\u4e09\u6b65\uff0c\u4f38\u624b\u5728\u4ed6\u80f8\u53e3\u6309\u6469\uff0c\u559d\u9053\uff1a\u201c\u5410\u5e72\u51c0\u4e86\uff01 \u3000\u3000\u522b\u7559\u4e0b\u4e86\u6bd2\u7269\uff01\u201d\u90a3\u6b66\u5b98\u62da\u547d\u5455\u5410\uff0c\u4e00\u4f4e\u5934\uff0c\u53ea\u89c1\u5455\u51fa\u6765\u7684\u79fd\u7269\u4e4b\u4e2d\u6709\u4e09\u6761\u4e24\u5bf8\u957f\u7684\u866b\u5b50\u8815\u8815\u800c\u52a8\uff0c\u7ea2\u5934\u9ed1\u8eab\uff0c\u6b63\u662f\u8708\u86a3\u3002 \u3000\u3000\u90a3\u6b66\u5b98\u5927\u53eb\uff1a\u201c\u4e09\u6761\u2026\u2026\u4e09\u6761\u8708\u86a3\uff01\u201d\u4e00\u60ca\u4e4b\u4e0b\uff0c\u9669\u4e9b\u6655\u53bb\uff0c\u5fd9\u5411\u77f3\u5148\u751f\u62dc\u5012\uff0c\u8c22\u4ed6\u6551\u547d\u4e4b\u6069\u3002\u5eca\u4e0b\u4ec6\u5f79\u4e0a\u6765\u6e05\u626b\u79fd\u7269\u3002\u7fa4\u8c6a\u65e0\u4e0d\u53f9\u670d\u3002 \u3000\u3000\u80e1\u6590\u4e0d\u4fe1\u4eba\u8179\u4e2d\u4f1a\u6709\u8708\u86a3\uff0c\u4f46\u4eb2\u773c\u76ee\u7779\uff0c\u5374\u53c8\u4e0d\u7531\u5f97\u4e0d\u4fe1\u3002\u7a0b\u7075\u7d20\u5728\u4ed6\u8033\u8fb9\u4f4e\u58f0\u9053\uff1a\u201c\u522b\u8bf4\u4e09\u6761\u5c0f\u8708\u86a3\uff0c\u6211\u53eb\u4f60\u809a\u91cc\u5455\u51fa\u4e09\u6761\u9752\u86c7\u51fa\u6765\u4e5f\u6210\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u7ed9\u4f60\u670d\u4e24\u7c92\u5455\u5410\u836f\u4e38\uff0c\u6211\u8896\u4e2d\u65e9\u5c31\u6697\u85cf\u6bd2\u866b\u3002\u201d\u80e1\u6590\u4f4e\u58f0\u9053\uff1a\u201c\u662f\u4e86\uff0c\u4e58\u6211\u5455\u5410\u5927\u4f5c\u3001\u809a\u75db\u96be\u5f53\u4e4b\u9645\uff0c\u5c06\u6bd2\u866b\u4e22\u5728\u79fd\u7269\u4e4b\u4e2d\uff0c\u6709\u8c01\u77e5\u9053\uff1f\u201d\u7a0b\u7075\u7d20\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4ed6\u62a2\u8fc7\u53bb\u7ed9\u90a3\u6b66\u5b98\u6309\u6469\u80f8\u53e3\uff0c\u5018\u82e5\u6ca1\u8fd9\u4e00\u7740\uff0c\u620f\u6cd5\u5c31\u4e0d\u7075\u3002\u201d\u80e1\u6590\u4f4e\u58f0\u9053\uff1a\u201c\u5176\u5b9e\u8fd9\u4eba\u6b66\u529f\u5f88\u662f\u4e86\u5f97\uff0c\u5927\u53ef\u4e0d\u5fc5\u73a9\u8fd9\u79cd\u7384\u865a\u3002\u201d\u7a0b\u7075\u7d20\u8bed\u58f0\u653e\u5230\u6781\u4f4e\uff0c\u8bf4\u9053\uff1a\u201c\u5927\u54e5\uff0c\u8fd9\u5927\u5385\u4e0a\u6240\u6709\u8bf8\u4eba\u4e4b\u4e2d\uff0c\u6211\u6700\u60e7\u6015\u6b64\u4eba\u3002 \u3000\u3000\u4f60\u5343\u4e07\u5f97\u5c0f\u5fc3\u5728\u610f\u3002\u201d\u80e1\u6590\u81ea\u8ddf\u5979\u76f8\u8bc6\u4ee5\u6765\uff0c\u89c1\u5979\u4e8b\u4e8b\u80f8\u6709\u6210\u7af9\uff0c\u4ece\u672a\u8bf4\u8fc7\u201c\u60e7\u6015\u201d\u4e24\u5b57\uff0c\u6b64\u523b\u7adf\u662f\u8bf4\u5f97\u8fd9\u822c\u90d1\u91cd\uff0c\u53ef\u89c1\u8fd9\u77f3\u5148\u751f\u5b9e\u5728\u975e\u540c\u5c0f\u53ef\uff0c\u53c8\u60f3\u6b64\u4eba\u5192\u4e86\u5979\u5148\u5e08\u4e4b\u540d\u51fa\u6765\u62db\u6447\uff0c\u8d25\u574f\u5979\u5148\u5e08\u7684\u540d\u5934\uff0c\u5979\u7ec8\u7a76\u4e0d\u80fd\u8896\u624b\u4e0d\u7406\u3002 \u3000\u3000\u53ea\u542c\u5f97\u77f3\u5148\u751f\u7b11\u9053\uff1a\u201c\u6211\u867d\u6536\u4e86\u51e0\u4e2a\u5f1f\u5b50\uff0c\u53ef\u662f\u5411\u6765\u4e0d\u7acb\u4ec0\u4e48\u95e8\u6d3e\u3002\u4eca\u65e5\u5c31\u8ddf\u5404\u4f4d\u524d\u8f88\u5b66\u5b66\uff0c\u4e5f\u6765\u5f00\u5b97\u7acb\u6d3e\uff0c\u4fa5\u5e78\u6367\u5f97\u4e00\u53ea\u94f6\u9ca4\u676f\u56de\u5bb6\uff0c\u4e5f\u597d\u8ba9\u5f1f\u5b50\u4eec\u98ce\u5149\u98ce\u5149\u3002\u201d\u7f13\u6b65\u8d70\u5c06\u8fc7\u53bb\uff0c\u5927\u6a21\u5927\u6837\u7684\u5728\u7530\u5f52\u519c\u8eab\u65c1\u592a\u5e08\u6905\u4e2d\u4e00\u5750\uff0c\u5374\u54ea\u91cc\u662f\u5f97\u4e00\u53ea\u94f6\u9ca4\u676f\u4e3a\u5df2\u8db3\uff0c\u663e\u662f\u8981\u5728\u516b\u5927\u95e8\u6d3e\u4e2d\u5360\u4e00\u5e2d\u5730\u3002 \u3000\u3000\u4ed6\u8fd9\u4e48\u4e00\u5750\uff0c\u51ed\u4e86\u201c\u6bd2\u624b\u836f\u738b\u201d\u6570\u5341\u5e74\u6765\u7684\u540d\u58f0\uff0c\u624b\u5f39\u94c1\u83e9\u63d0\u7684\u529f\u529b\uff0c\u4f24\u4eba\u4e8e\u6307\u987e\u95f4\u7684\u4e0b\u6bd2\u624b\u6cd5\uff0c\u8fd9\u4e00\u53ea\u7389\u9f99\u676f\u5c31\u7b97\u662f\u62ff\u5b9a\u4e86\uff0c\u8c01\u4e5f\u4e0d\u4f1a\u52a8\u5ff5\u53bb\u8ddf\u4ed6\u6311\u6218\uff0c\u53ef\u4e5f\u6ca1\u8c01\u52a8\u5ff5\u53bb\u8ddf\u4ed6\u8bf4\u8bdd\u3002 \u3000\u3000\u4e00\u65f6\u4e4b\u95f4\uff0c\u5927\u5385\u9759\u4e86\u4e00\u7247\u3002\u5c11\u6797\u6d3e\u7684\u638c\u95e8\u65b9\u4e08\u5927\u667a\u7985\u5e08\u5ffd\u9053\uff1a\u201c\u77f3\u5148\u751f\uff0c\u65e0\u55d4\u548c\u5c1a\u8ddf\u4f60\u600e\u4e48\u79f0\u547c\uff1f\u201d\u77f3\u5148\u751f\u9053\uff1a\u201c\u65e0\u55d4\uff1f \u3000\u3000\u4e0d\u77e5\u9053\uff0c\u6211\u4e0d\u8ba4\u5f97\u3002\u201d\u8138\u4e0a\u4e1d\u6beb\u4e0d\u52a8\u58f0\u8272\u3002\u5927\u667a\u7985\u5e08\u53cc\u624b\u5408\u5341\uff0c\u8bf4\u9053\uff1a\u201c\u963f\u5f25\u9640\u4f5b\uff01\u201d\u77f3\u5148\u751f\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u5927\u667a\u7985\u5e08\u53c8\u5ba3\u4e86\u4e00\u58f0\u4f5b\u53f7\uff1a\u201c\u963f\u5f25\u9640\u4f5b\uff01\u201d\u77f3\u5148\u751f\u4fbf\u4e0d\u518d\u95ee\u3002 \u3000\u3000\u81ea\u4ed6\u5e08\u5f92\u4e09\u4eba\u8fdb\u4e86\u5927\u5385\uff0c\u7a0b\u7075\u7d20\u7684\u76ee\u5149\u4ece\u6ca1\u79bb\u5f00\u8fc7\u4ed6\u4e09\u4eba\uff0c\u53ea\u89c1\u77f3\u5148\u751f\u6162\u6162\u8f6c\u8fc7\u5934\u53bb\uff0c\u548c\u7530\u5f52\u519c\u5bf9\u671b\u4e86\u4e00\u773c\u3002\u4e24\u4eba\u795e\u8272\u6728\u7136\uff0c\u76ee\u5149\u4e2d\u5168\u65e0\u793a\u610f\uff0c\u4f46\u7a0b\u7075\u7d20\u5fc3\u5ff5\u4e00\u52a8\uff0c\u5df2\u7136\u660e\u767d\uff1a \u3000\u3000\u201c\u4ed6\u4e24\u4eba\u65e9\u5df2\u76f8\u8bc6\u3002\u7530\u5f52\u519c\u77e5\u9053\u6211\u5e08\u7236\u7684\u540d\u5b57\uff0c\u77e5\u9053\u2018\u65e0\u55d4\u5927\u5e08\u2019\u624d\u662f\u771f\u6b63\u7684\u2018\u6bd2\u624b\u836f\u738b\u2019\u3002\u8fd9\u4f4d\u5c11\u6797\u9ad8\u50e7\u5374\u4e5f\u77e5\u9053\u3002\u201d\u5ffd\u53c8\u60f3\u5230\uff1a\u201c\u7530\u5f52\u519c\u7528\u6765\u6bd2\u778e\u82d7\u4eba\u51e4\u7684\u65ad\u80a0\u8349\uff0c\u539f\u6765\u5c31\u662f\u8fd9\u4eba\u7ed9\u7684\u3002\u201d \u3000\u3000\u7530\u5f52\u519c\u5b9d\u5200\u950b\u5229\uff0c\u77f3\u5148\u751f\u6bd2\u836f\u5389\u5bb3\uff0c\u5750\u7a33\u4e86\u4e24\u5f20\u592a\u5e08\u6905\uff0c\u516b\u53ea\u7389\u9f99\u676f\u4e4b\u4e2d\uff0c\u53ea\u6709\u4e00\u53ea\u8fd8\u6ca1\u4e3b\u4eba\u3002\u7fa4\u8c6a\u5747\u60f3\uff1a\u201c\u662f\u5426\u80fd\u5217\u5165\u516b\u5927\u95e8\u6d3e\uff0c\u5168\u77a7\u8fd9\u6700\u540e\u4e00\u53ea\u7389\u9f99\u676f\u7531\u8c01\u62a2\u5f97\u3002\u201d\u771f\u6240\u8c13\u4eba\u540c\u6b64\u5fc3\uff0c\u9877\u523b\u4e4b\u95f4\uff0c\u4eba\u4e1b\u4e2d\u8dc3\u51fa\u4e03\u516b\u4eba\u6765\uff0c\u4e00\u9f50\u60f3\u53bb\u5750\u90a3\u5f20\u7a7a\u6905\uff0c\u4e09\u8a00\u4e24\u8bed\uff0c\u4fbf\u5206\u6210\u56db\u5bf9\u6597\u4e86\u8d77\u6765\u3002\u9877\u8d25\u8005\u9000\u4e0b\uff0c\u80dc\u8005\u6216\u63a5\u7eed\u4e92\u6597\uff0c\u6216\u548c\u65b0\u6765\u8005\u5e94\u6218\uff0c\u6b64\u6765\u5f7c\u5f80\u7684\u6fc0\u6597\u826f\u4e45\uff0c\u53ea\u542c\u5f97\u95e8\u5916\u66f4\u9f13\u6253\u4e86\u56db\u66f4\uff0c\u76f8\u6597\u7684\u56db\u4eba\u8d25\u4e0b\u4e86\u4e24\u4eba\uff0c\u53ea\u5269\u4e0b\u4e24\u4e2a\u80dc\u8005\u4e92\u6597\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u6b64\u65f6\u5747\u4ee5\u6d51\u539a\u638c\u529b\u6bd4\u62da\u5185\u529b\uff0c\u4e45\u4e45\u76f8\u6301\u4e0d\u51b3\uff0c\u6bd4\u7684\u662f\u9ad8\u6df1\u6b66\u529f\uff0c\u5916\u5f62\u770b\u6765\u5374\u662f\u5e73\u6de1\u65e0\u5947\u3002\u798f\u5eb7\u5b89\u5f88\u4e0d\u8010\u70e6\uff0c\u63a5\u8fde\u6253\u4e86\u51e0\u4e2a\u5475\u6b20\uff0c\u8bf4\u9053\uff1a\u201c\u77a7\u5f97\u95f7\u6b7b\u4eba\u4e86\uff01\u201d\u8fd9\u53e5\u8bdd\u58f0\u97f3\u751a\u8f7b\uff0c\u4f46\u6b63\u5728\u6bd4\u62da\u5185\u529f\u7684\u4e24\u4eba\u5374\u90fd\u6e05\u6e05\u695a\u695a\u7684\u542c\u5165\u8033\u4e2d\u3002\u4e24\u4eba\u8138\u8272\u9f50\u53d8\uff0c\u5404\u81ea\u64a4\u638c\uff0c\u9000\u540e\u4e09\u6b65\u3002\u4e00\u4e2a\u9053\uff1a\u201c\u54b1\u4eec\u53c8\u4e0d\u662f\u800d\u7334\u513f\u620f\u7684\uff0c\u5230\u8fd9\u91cc\u5356\u5f04\u82b1\u62f3\u7ee3\u817f\uff0c\u53eb\u5b98\u8001\u7237\u4eec\u559d\u5f69\uff01\u201d\u53e6\u4e00\u4e2a\u9053\uff1a\u201c\u4e0d\u9519\uff01\u56de\u5bb6\u62b1\u5a03\u5a03\u53bb\u5427\uff01\u201d\u4e24\u4eba\u8bf4\u7740\u5475\u5475\u800c\u7b11\uff0c\u643a\u624b\u51fa\u4e86\u5927\u5385\u3002 \u3000\u3000\u80e1\u6590\u6697\u6697\u70b9\u5934\uff1a\u201c\u8fd9\u4e8c\u4eba\u6b66\u529f\u751a\u9ad8\uff0c\u8bc6\u89c1\u679c\u7136\u4e5f\u9ad8\u4eba\u4e00\u7b49\u3002 \u3000\u3000\u53ea\u53ef\u60dc\u4e71\u54c4\u54c4\u4e4b\u4e2d\u6ca1\u542c\u5230\u4ed6\u4eec\u7684\u540d\u5b57\u3002\u201d\u8f6c\u5934\u95ee\u90ed\u7389\u5802\u65f6\uff0c\u4ed6\u4e5f\u4e0d\u8bc6\u8fd9\u4e24\u4e2a\u4e61\u4e0b\u571f\u8001\u513f\u4e00\u822c\u7684\u4eba\u7269\u3002 \u3000\u3000\u90ed\u7389\u5802\u8bf4\u9053\uff1a\u201c\u4ed6\u4eec\u4e0a\u6765\u4e4b\u65f6\uff0c\u5b89\u63d0\u7763\u95ee\u4ed6\u4eec\u59d3\u540d\u95e8\u6d3e\uff0c\u4e24\u4eba\u90fd\u662f\u7b11\u4e86\u7b11\u6ca1\u8bf4\u3002\u201d\u80e1\u6590\u5fc3\u60f3\uff1a\u201c\u8fd9\u4e24\u4f4d\u9ad8\u624b\u72b9\u5982\u795e\u9f99\u89c1\u9996\u4e0d\u89c1\u5c3e\uff0c\u8fde\u59d3\u540d\u4e5f\u6ca1\u7559\u4e0b\u3002\u201d \u3000\u3000\u4ed6\u6b63\u4f4e\u4e86\u5934\u548c\u90ed\u7389\u5802\u6084\u58f0\u8bf4\u8bdd\uff0c\u7a0b\u7075\u7d20\u5ffd\u7136\u8f7b\u8f7b\u78b0\u4e86\u78b0\u4ed6\u624b\u8098\uff0c\u80e1\u6590\u62ac\u8d77\u5934\u6765\uff0c\u53ea\u542c\u5f97\u4e00\u540d\u6b66\u5b98\u5531\u540d\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u4e94\u864e\u95e8\u638c\u95e8\u4eba\u51e4\u5929\u5357\u51e4\u8001\u7237\uff01\u201d\u4f46\u89c1\u51e4\u5929\u5357\u624b\u6301\u719f\u94dc\u68cd\uff0c\u8d70\u4e0a\u53bb\u5728\u7a7a\u7740\u7684\u592a\u5e08\u6905\u4e2d\u4e00\u5750\uff0c\u8bf4\u9053\uff1a\u201c\u54ea\u4e00\u4f4d\u524d\u6765\u6307\u6559\u3002\u201d\u80e1\u6590\u5927\u559c\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u53ae\u7684\u6b66\u529f\u672a\u8fbe\u4e00\u6d41\u9ad8\u624b\u4e4b\u5883\uff0c\u5c45\u7136\u4e5f\u60f3\u6765\u593a\u7389\u9f99\u676f\uff0c\u5148\u8ba9\u4ed6\u51fa\u4e00\u756a\u4e11\uff0c\u518d\u6765\u6536\u62fe\u4ed6\uff0c\u90a3\u66f4\u5999\u4e86\u3002\u201d \u3000\u3000\u53ea\u89c1\u51e4\u5929\u5357\u63a5\u8fde\u6253\u8d25\u4e86\u4e24\u4eba\uff0c\u6b63\u81ea\u5f97\u610f\u6d0b\u6d0b\uff0c\u4e00\u4e2a\u624b\u6301\u5355\u5200\u7684\u4eba\u4e0a\u53bb\u6311\u6218\u3002\u8fd9\u4e2a\u4eba\u7684\u6b66\u827a\u53ef\u5c31\u9ad8\u4e86\uff0c\u53ea\u4e09\u62db\u4e00\u8fc7\uff0c\u80e1\u6590\u5fc3\u9053\uff1a\u201c\u8fd9\u6076\u8d3c\u51b3\u4e0d\u662f\u5bf9\u624b\uff01\u201d \u3000\u3000\u679c\u7136\u51e4\u5929\u5357\u543c\u53eb\u8fde\u8fde\uff0c\u8fed\u9047\u9669\u62db\u3002\u90a3\u4f7f\u5355\u5200\u7684\u4f3c\u4e4e\u4e0d\u4e3a\u5df2\u751a\uff0c\u53ea\u76fc\u4ed6\u77e5\u96be\u800c\u9000\uff0c\u5e76\u4e0d\u65bd\u5c55\u6740\u624b\uff0c\u56e0\u6b64\u867d\u6709\u51e0\u6b21\u53ef\u4e58\u4e4b\u673a\uff0c\u5374\u90fd\u4f7f\u4e86\u7f13\u62db\u3002\u4f46\u51e4\u5929\u5357\u53ea\u662f\u4e0d\u4f4f\u5012\u9000\uff0c\u5e76\u4e0d\u8ba4\u8f93\uff0c\u7a81\u7136\u95f4\u6a2a\u626b\u4e00\u68cd\uff0c\u90a3\u4f7f\u5355\u5200\u7684\u8eab\u5f62\u4e00\u77ee\uff0c\u94dc\u68cd\u4ece\u4ed6\u5934\u9876\u63a0\u8fc7\u3002\u4ed6\u6b63\u6b32\u4e58\u52bf\u8fdb\u62db\uff0c\u5ffd\u5730\u53eb\u58f0\uff1a\u201c\u554a\u54df\uff01\u201d\u5c31\u5730\u4e00\u6eda\uff0c\u8ddf\u7740\u8dc3\u4e86\u8d77\u6765\uff0c\u4f46\u843d\u4e0b\u65f6\u53f3\u8db3\u4e00\u4e2a\u8e09\u8dc4\uff0c\u7ad9\u7acb\u4e0d\u5b9a\uff0c\u53c8\u6454\u5012\u5728\u5730\uff0c\u6012\u559d\uff1a \u3000\u3000\u201c\u4f60\u4f7f\u6697\u5668\uff0c\u4e0d\u8981\u8138\uff01\u201d \u3000\u3000\u51e4\u5929\u5357\u62c4\u68cd\u5fae\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u798f\u5927\u5e05\u53c8\u6ca1\u89c4\u5b9a\u4e0d\u5f97\u4f7f\u6697\u5668\u3002 \u3000\u3000\u4e0a\u5f97\u573a\u6765\uff0c\u5175\u5203\u62f3\u811a\uff0c\u6bd2\u836f\u6697\u5668\uff0c\u6089\u542c\u5c0a\u4fbf\u3002\u201d \u3000\u3000\u90a3\u4f7f\u5355\u5200\u7684\u5377\u8d77\u88e4\u811a\uff0c\u53ea\u89c1\u819d\u5934\u4e0b\u201c\u728a\u9f3b\u7a74\u201d\u4e2d\u8d6b\u7136\u63d2\u7740\u4e00\u679a\u4e24\u5bf8\u6765\u957f\u7684\u94f6\u9488\u3002\u8fd9\u201c\u728a\u9f3b\u7a74\u201d\u6b63\u5f53\u819d\u5934\u4e4b\u4e0b\uff0c\u4fd7\u540d\u819d\u773c\uff0c\u4e24\u65c1\u7a7a\u9677\uff0c\u72b6\u4f3c\u725b\u9f3b\uff0c\u56e0\u4ee5\u4e3a\u540d\uff0c\u6b63\u662f\u5927\u817f\u548c\u5c0f\u817f\u4e4b\u4ea4\u7684\u8981\u7d27\u7a74\u9053\uff0c\u6b64\u7a74\u4e2d\u8ba1\uff0c\u8fd9\u6761\u817f\u4fbf\u4e0d\u7ba1\u7528\u4e86\u3002 \u3000\u3000\u7fa4\u8c6a\u90fd\u662f\u597d\u751f\u5947\u602a\uff0c\u773c\u89c1\u9002\u624d\u4e24\u4eba\u6597\u5f97\u751a\u7d27\uff0c\u51e4\u5929\u5357\u7edd\u65e0\u4f59\u6687\u53d1\u5c04\u6697\u5668\uff0c\u53c8\u6ca1\u89c1\u4ed6\u62ac\u81c2\u626c\u624b\uff0c\u8fd9\u679a\u94f6\u9488\u4e0d\u77e5\u5982\u4f55\u53d1\u51fa\uff1f \u3000\u3000\u90a3\u4f7f\u5355\u5200\u7684\u62d4\u4e0b\u94f6\u9488\uff0c\u6068\u6068\u9000\u4e0b\u3002\u53c8\u6709\u4e00\u4e2a\u4f7f\u97ad\u7684\u4e0a\u6765\uff0c\u8fd9\u4eba\u7684\u94c1\u97ad\u4f7f\u5f97\u72b9\u5982\u66b4\u98ce\u9aa4\u96e8\u4e00\u822c\uff0c\u4e8c\u5341\u4f59\u62db\u4e4b\u5185\uff0c\u4e00\u62db\u7d27\u4f3c\u4e00\u62db\uff0c\u7adf\u4e0d\u8ba9\u51e4\u5929\u5357\u6709\u4e1d\u6beb\u5598\u606f\u4e4b\u673a\u3002\u4ed6\u773c\u89c1\u51e4\u5929\u5357\u68cd\u6cd5\u5e76\u4e0d\u5982\u4f55\u4e86\u5f97\uff0c\u5012\u662f\u90a3\u65e0\u5f71\u65e0\u8e2a\u7684\u94f6\u9488\u751a\u662f\u96be\u5f53\uff0c\u56e0\u6b64\u4e0a\u6740\u62db\u4e0d\u7edd\uff0c\u51b3\u4e0d\u8ba9\u4ed6\u7f13\u624b\u6765\u53d1\u5c04\u6697\u5668\uff0c\u90a3\u77e5\u6597\u5230\u5c06\u8fd1\u4e09\u5341\u62db\u65f6\uff0c\u51e4\u5929\u5357\u68cd\u6cd5\u6e10\u4e71\uff0c\u90a3\u4f7f\u97ad\u7684\u5374\u53c8\u662f\u201c\u554a\u54df\u201d\u4e00\u58f0\u5927\u53eb\uff0c\u5012\u9000\u5f00\u53bb\uff0c\u4ece\u81ea\u5df1\u5c0f\u8179\u4e0a\u62d4\u51fa\u4e00\u679a\u94f6\u9488\uff0c\u4f24\u53e3\u8840\u6d41\u5982\u6ce8\uff0c\u4f24\u5f97\u7adf\u662f\u6781\u91cd\u3002 \u3000\u3000\u5385\u4e0a\u7fa4\u8c6a\u65e0\u4e0d\u60ca\u8be7\uff0c\u4f3c\u51e4\u5929\u5357\u8fd9\u7b49\u53d1\u5c04\u6697\u5668\uff0c\u5b9e\u662f\u751f\u5e73\u6240\u672a\u95fb\u3002\u82e5\u8bf4\u662f\u65c1\u4eba\u6697\u4e2d\u76f8\u52a9\uff0c\u4f17\u76ee\u777d\u777d\u4e4b\u4e0b\uff0c\u603b\u4f1a\u6709\u4eba\u53d1\u89c1\u3002\u773c\u4e0b\u8fd9\u4e24\u573a\u76f8\u6597\uff0c\u90fd\u662f\u51e4\u5929\u5357\u52bf\u5c06\u4e0d\u652f\u4e4b\u65f6\uff0c\u7a81\u7136\u4e4b\u95f4\u5bf9\u624b\u4e2d\u4e86\u6697\u5668\u3002\u96be\u9053\u51e4\u5929\u5357\u7adf\u4f1a\u884c\u4f7f\u90aa\u6cd5\uff0c\u5fc3\u5ff5\u4e00\u52a8\uff0c\u94f6\u9488\u4fbf\u4f1a\u4ece\u5929\u98de\u5230\uff1f \u3000\u3000\u504f\u6709\u51e0\u4e2a\u4e0d\u670d\u6c14\u7684\uff0c\u63a5\u8fde\u4e0a\u53bb\u8ddf\u4ed6\u76f8\u6597\u3002\u4e00\u4eba\u5168\u795e\u8d2f\u6ce8\u7684\u9632\u5907\u94f6\u9488\uff0c\u4e0d\u63d0\u9632\u7ed9\u4ed6\u94dc\u68cd\u51fb\u4e2d\u80a9\u5934\uff0c\u8eab\u8d1f\u91cd\u4f24\uff0c\u53e6\u5916\u4e09\u4eba\u5374\u4e5f\u90fd\u7ed9\u4ed6\u201c\u65e0\u5f71\u94f6\u9488\u201d\u6240\u4f24\u3002\u4e00\u65f6\u5927\u5385\u4e4b\u4e0a\u7fa4\u60c5\u8038\u52a8\u3002 \u3000\u3000\u80e1\u6590\u548c\u7a0b\u7075\u7d20\u773c\u89c1\u51e4\u5929\u5357\u63a5\u4e8c\u8fde\u4e09\u4ee5\u65e0\u5f71\u94f6\u9488\u4f24\u4eba\uff0c\u51dd\u795e\u89c2\u770b\uff0c\u7adf\u662f\u77a7\u4e0d\u51fa\u4e1d\u6beb\u7834\u7efd\u3002\u80e1\u6590\u672c\u60f3\u5f53\u51e4\u5929\u5357\u5174\u9ad8\u91c7\u70c8\u4e4b\u65f6\uff0c\u7a81\u7136\u4e0a\u524d\u5c06\u4ed6\u6740\u6b7b\uff0c\u4e00\u6765\u4e3a\u4f5b\u5c71\u9547\u4e0a\u953a\u963f\u56db\u5168\u5bb6\u62a5\u4ec7\uff0c\u4e8c\u6765\u597d\u663e\u626c\u534e\u62f3\u95e8\u7684\u540d\u5934\uff0c\u4f46\u77a7\u4e0d\u900f\u8fd9\u94f6\u9488\u6697\u5668\u7684\u6765\u8def\uff0c\u53ea\u6709\u6682\u4e14\u8896\u624b\uff0c\u82e5\u662f\u8d38\u7136\u4e0a\u524d\u4e89\u950b\uff0c\u53ea\u8981\u4e00\u4e2a\u63aa\u624b\u4e0d\u53ca\uff0c\u975e\u4f46\u81ea\u53d6\u5176\u8fb1\uff0c\u6291\u4e14\u6709\u6027\u547d\u4e4b\u5fe7\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u731c\u5230\u4ed6\u7684\u5fc3\u610f\uff0c\u7f13\u7f13\u6447\u4e86\u6447\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u53ea\u7389\u9f99\u676f\uff0c\u54b1\u4eec\u4e0d\u8981\u4e86\u5427\uff1f\u201d\u80e1\u6590\u5411\u8521\u5a01\u548c\u59ec\u6653\u5cf0\u9053\uff1a\u201c\u8fd9\u4f4d\u51e4\u8001\u5e08\u7684\u6b66\u529f\uff0c\u8fd8\u4e0d\u600e\u6837\uff0c\u53ea\u662f\u2026\u2026\u201d\u59ec\u6653\u5cf0\u70b9\u5934\u9053\uff1a\u201c\u662f\u554a\uff0c\u4ed6\u653e\u5c04\u7684\u94f6\u9488\u53ef\u5b9e\u5728\u90aa\u95e8\uff0c\u65e0\u58f0\u65e0\u606f\uff0c\u65e0\u5f71\u65e0\u8e2a\uff0c\u7adf\u662f\u6ca1\u534a\u70b9\u5148\u5146\uff0c\u76f4\u81f3\u5bf9\u65b9\u4e00\u58f0\u60e8\u53eb\uff0c\u624d\u77e5\u662f\u4e2d\u4e86\u4ed6\u7684\u6697\u5668\u3002\u201d\u8521\u5a01\u9053\uff1a\u201c\u9664\u975e\u662f\u5934\u6234\u94a2\u76d4\uff0c\u8eab\u7a7f\u94c1\u7532\uff0c\u624d\u80fd\u8ddf\u4ed6\u6597\u4e0a\u4e00\u6597\u3002\u201d \u3000\u3000\u8521\u5a01\u8fd9\u53e5\u8bdd\u4e0d\u8fc7\u662f\u8bb2\u7b11\uff0c\u90a3\u77e5\u5385\u4e0a\u4f17\u6b66\u5b98\u4e4b\u4e2d\uff0c\u5f53\u771f\u6709\u4eba\u5fc3\u6000\u4e0d\u670d\uff0c\u547d\u4eba\u53bb\u53d6\u4e86\u4e0a\u9635\u7528\u7684\u94c1\u7532\uff0c\u5168\u8eab\u62ab\u6302\uff0c\u624b\u6267\u5f00\u5c71\u5927\u65a7\uff0c\u4e0a\u524d\u6311\u6218\u3002 \u3000\u3000\u8fd9\u540d\u6b66\u5b98\u540d\u53eb\u6728\u6587\u5bdf\uff0c\u5f53\u5e74\u968f\u798f\u5eb7\u5b89\u8fdc\u5f81\u9752\u6d77\uff0c\u5bd2\u65d7\u65a9\u5c06\uff0c\u7acb\u8fc7\u4e0d\u5c11\u6c57\u9a6c\u529f\u52b3\uff0c\u4e43\u662f\u6e05\u519b\u4e2d\u7684\u4e00\u5458\u51fa\u540d\u7684\u6ee1\u6d32\u731b\u5c06\uff0c\u8fd9\u65f6\u624b\u6267\u5927\u65a7\u8d70\u5230\u5385\u4e2d\uff0c\u5a01\u98ce\u51db\u51db\uff0c\u6740\u6c14\u817e\u817e\uff0c\u540c\u50da\u888d\u6cfd\u9f50\u58f0\u559d\u5f69\u3002\u798f\u5eb7\u5b89\u4e5f\u8d50\u9152\u4e00\u676f\uff0c\u5148\u884c\u6170\u52b3\u3002 \u3000\u3000\u4e24\u4eba\u4e00\u63a5\u4e0a\u624b\uff0c\u68cd\u65a7\u76f8\u4ea4\uff0c\u5f53\u5f53\u4e4b\u58f0\uff0c\u9707\u8033\u6b32\u804b\uff0c\u4e24\u822c\u6c89\u91cd\u7684\u957f\u5175\u5668\u653b\u5b88\u62b5\u62d2\uff0c\u5377\u8d77\u9635\u9635\u75be\u98ce\uff0c\u70db\u5149\u4e5f\u7ed9\u5439\u5f97\u5ffd\u660e\u5ffd\u6697\u3002\u6728\u6587\u5bdf\u8eab\u7a7f\u94c1\u7532\uff0c\u8f6c\u52a8\u7a76\u5c5e\u6781\u4e0d\u7075\u4fbf\uff0c\u4f46\u4ed7\u7740\u8182\u529b\u6781\u5927\uff0c\u5f00\u5c71\u5de8\u65a7\u821e\u5c06\u5f00\u6765\uff0c\u5b9e\u662f\u5a01\u4e0d\u53ef\u5f53\u3002 \u3000\u3000\u5468\u94c1\u9e6a\u3001\u66fe\u94c1\u9e25\u548c\u738b\u5251\u82f1\u3001\u738b\u5251\u6770\u56db\u4eba\u7ad9\u5728\u798f\u5eb7\u5b89\u8eab\u524d\uff0c\u624b\u4e2d\u5404\u6267\u5175\u5203\uff0c\u751f\u6015\u5de8\u65a7\u6216\u662f\u94dc\u68cd\u8131\u624b\u7529\u51fa\uff0c\u4f24\u53ca\u5927\u5e05\u3002 \u3000\u3000\u6597\u5230\u4e8c\u5341\u4f59\u5408\uff0c\u51e4\u5929\u5357\u62e6\u5934\u4e00\u68cd\u626b\u53bb\uff0c\u6728\u6587\u5bdf\u5934\u4e00\u4f4e\uff0c\u987a\u52bf\u6325\u65a7\u53bb\u780d\u5bf9\u65b9\u53f3\u817f\uff0c\u5ffd\u542c\u5f97\u62cd\u7684\u4e00\u58f0\u8f7b\u54cd\uff0c\u65c1\u89c2\u7fa4\u8c6a\u201c\u54e6\u201d\u7684\u4e00\u4e0b\uff0c\u9f50\u58f0\u547c\u53eb\u3002\u4e24\u4eba\u5404\u81ea\u8dc3\u5f00\u51e0\u6b65\uff0c\u4f46\u89c1\u5730\u4e0b\u5815\u7740\u4e00\u4e2a\u7ea2\u8272\u7ed2\u7403\uff0c\u6b63\u662f\u4ece\u6728\u6587\u5bdf\u5934\u76d4\u4e0a\u843d\u4e0b\uff0c\u7ed2\u7403\u4e0a\u63d2\u7740\u4e00\u679a\u94f6\u9488\uff0c\u95ea\u95ea\u53d1\u4eae\u3002 \u3000\u3000\u60f3\u662f\u6728\u6587\u5bdf\u4f4e\u5934\u6325\u65a7\u4e4b\u65f6\uff0c\u51e4\u5929\u5357\u53d1\u51fa\u65e0\u5f71\u94f6\u9488\uff0c\u53ea\u56e0\u987e\u5ff5\u4ed6\u662f\u798f\u5927\u5e05\u7231\u5c06\uff0c\u4e0d\u6562\u4f24\u4ed6\u8eab\u5b50\u3002\u90a3\u7ed2\u7403\u4ee5\u94c5\u4e1d\u7cfb\u5728\u5934\u76d4\u4e4b\u4e0a\uff0c\u987b\u5f97\u5c04\u65ad\u94c5\u4e1d\uff0c\u7ed2\u7403\u65b9\u80fd\u843d\u4e0b\uff0c\u867d\u7136\u4e24\u4eba\u76f8\u8ddd\u751a\u8fd1\uff0c\u4f46\u4ed3\u5352\u95f4\u7adf\u80fd\u5c04\u5f97\u5982\u6b64\u4e4b\u51c6\uff0c\u4e0d\u5dee\u6beb\u5398\uff0c\u5b9e\u662f\u4e86\u4e0d\u8d77\u7684\u6697\u5668\u529f\u592b\u3002 \u3000\u3000\u6728\u6587\u5bdf\u4e00\u5446\u4e4b\u4e0b\uff0c\u5df2\u77e5\u662f\u5bf9\u65b9\u624b\u4e0b\u5bb9\u60c5\uff0c\u8fd9\u4e00\u9488\u5018\u662f\u504f\u4f4e\u6570\u5bf8\uff0c\u4ece\u7709\u5fc3\u95f4\u8d2f\u8111\u800c\u5165\uff0c\u8fd9\u65f6\u7109\u6709\u547d\u5728\uff1f\u4fbf\u662f\u5168\u8eab\u94c1\u7532\uff0c\u53c8\u6709\u4f55\u7528\uff1f\u4ed6\u5fc3\u60a6\u8bda\u670d\uff0c\u53cc\u624b\u62b1\u62f3\uff0c\u8bf4\u9053\uff1a\u201c\u591a\u627f\u51e4\u8001\u5e08\u624b\u4e0b\u7559\u60c5\u3002\u201d\u51e4\u5929\u5357\u606d\u606d\u656c\u656c\u7684\u8bf7\u4e86\u4e2a\u5b89\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u4eba\u6b66\u827a\u8ddf\u6728\u5927\u4eba\u76f8\u5dee\u751a\u8fdc\uff0c\u8fd9\u4e9b\u53d1\u5c04\u6697\u5668\u7684\u5fae\u672b\u529f\u592b\uff0c\u5728\u7586\u573a\u4e4b\u4e0a\u90a3\u662f\u7edd\u65e0\u7528\u5904\u3002\u5018\u82e5\u54b1\u4fe9\u9a91\u9a6c\u6bd4\u8bd5\uff0c\u5c0f\u4eba\u65e9\u7ed9\u5927\u4eba\u4e00\u65a7\u5288\u4e0b\u9a6c\u6765\u4e86\u3002\u201d\u6728\u6587\u5bdf\u7b11\u9053\uff1a\u201c\u597d\u8bf4\uff0c\u597d\u8bf4\u3002\u201d \u3000\u3000\u798f\u5eb7\u5b89\u542c\u51e4\u5929\u5357\u8bf4\u8bdd\u5f97\u4f53\uff0c\u4e0d\u6562\u6043\u827a\u9a84\u5176\u90e8\u5c5e\uff0c\u5fc3\u4e0b\u751a\u559c\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u51e4\u8001\u5e08\u7684\u73a9\u827a\u513f\u5f88\u4e0d\u9519\u3002\u201d\u5c06\u624b\u4e2d\u7684\u78a7\u7389\u9f3b\u70df\u58f6\u9012\u7ed9\u5468\u94c1\u9e6a\uff0c\u9053\uff1a\u201c\u8d4f\u4e86\u4ed6\u5427\uff01\u201d\u51e4\u5929\u5357\u5fd9\u4e0a\u524d\u8c22\u8d4f\u3002 \u3000\u3000\u6728\u6587\u5bdf\u8d2f\u7532\u8d1f\u65a7\uff0c\u53ee\u53ee\u5f53\u5f53\u7684\u9000\u4e86\u4e0b\u53bb\u3002\u7fa4\u8c6a\u7eb7\u7eb7\u8bae\u8bba\u3002 \u3000\u3000\u4eba\u4e1b\u4e2d\u5ffd\u7136\u7ad9\u8d77\u4e00\u4eba\uff0c\u6717\u58f0\u9053\uff1a\u201c\u51e4\u8001\u5e08\u7684\u6697\u5668\u529f\u592b\u679c\u7136\u4e86\u5f97\uff0c\u5728\u4e0b\u6765\u9886\u6559\u9886\u6559\u3002\u201d\u4f17\u4eba\u56de\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u4ed6\u6ee1\u8138\u9ebb\u76ae\uff0c\u6b63\u662f\u9002\u624d\u53d1\u5c04\u94c1\u83e9\u63d0\u800c\u4e2d\u6bd2\u7684\u67ef\u5b50\u5bb9\u3002\u4ed6\u624b\u4e0a\u6d82\u4e86\u836f\u818f\u540e\uff0c\u8fd9\u65f6\u6bd2\u6027\u5df2\u89e3\u3002 \u3000\u3000\u4ed6\u5170\u5dde\u67ef\u5bb6\u4ee5\u4e03\u822c\u6697\u5668\u5f00\u6d3e\uff0c\u53eb\u505a\u201c\u67ef\u6c0f\u4e03\u9752\u95e8\u201d\u3002\u90a3\u4e03\u79cd\u6697\u9752\u5b50\uff1f\u4fbf\u662f\u8896\u7bad\u3001\u98de\u8757\u77f3\u3001\u94c1\u83e9\u63d0\u3001\u94c1\u84ba\u85dc\u3001\u98de\u5200\u3001\u94a2\u9556\u3001\u4e27\u95e8\u9489\uff0c\u53f7\u79f0\u201c\u7bad\u3001\u8757\u3001\u83e9\u3001\u85dc\u3001\u5200\u3001\u9556\u3001\u9489\u201d\u4e03\u7edd\u3002\u867d\u7136\u8fd9\u4e03\u79cd\u6697\u5668\u90fd\u662f\u6781\u5e38\u89c1\u4e4b\u7269\uff0c\u4f46\u4ed6\u5bb6\u4f20\u7684\u53d1\u5c04\u624b\u6cd5\u4e0e\u4f17\u4e0d\u540c\uff0c\u5200\u4e2d\u5939\u77f3\uff0c\u9489\u4e2d\u5939\u9556\uff0c\u800c\u4e14\u6570\u79cd\u6697\u5668\u80fd\u5728\u7a7a\u4e2d\u81ea\u884c\u78b0\u649e\uff0c\u5c04\u51fa\u65f6\u6216\u6b63\u6216\u659c\uff0c\u4ee4\u4eba\u6781\u96be\u6321\u907f\u3002\u82e5\u5728\u7a7a\u65f7\u4e4b\u5904\u76f8\u6597\uff0c\u8fd8\u80fd\u7a9c\u5f00\u6570\u4e08\uff0c\u7136\u540e\u770b\u51c6\u6697\u5668\u6765\u8def\uff0c\u6216\u52a0\u683c\u51fb\uff0c\u6216\u884c\u8eb2\u95ea\uff0c\u4f46\u5728\u8fd9\u5927\u5385\u4e4b\u4e0a\uff0c\u5730\u4f4d\u7a84\u5c0f\uff0c\u5374\u662f\u6781\u96be\u5bf9\u4ed8\u4e86\u3002 \u3000\u3000\u51e4\u5929\u5357\u5c06\u9f3b\u70df\u58f6\u90d1\u800c\u91cd\u4e4b\u7684\u7528\u624b\u5e15\u5305\u597d\uff0c\u653e\u5165\u6000\u4e2d\uff0c\u663e\u5f97\u5bf9\u798f\u5eb7\u5b89\u5c0a\u656c\u4e4b\u6781\uff0c\u8fd9\u624d\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u67ef\u8001\u5e08\u8981\u8ddf\u5728\u4e0b\u6bd4\u8bd5\u6697\u5668\uff0c\u5927\u5385\u4e4b\u4e0a\uff0c\u6697\u5668\u98de\u63b7\u6765\u53bb\uff0c\u82e5\u662f\u8bef\u4f24\u4e86\u5404\u4f4d\u5927\u4eba\uff0c\u90a3\u53ef\u5403\u7f6a\u4e0d\u8d77\u3002\u201d \u3000\u3000\u5468\u94c1\u9e6a\u7b11\u9053\uff1a\u201c\u51e4\u8001\u5e08\u4e0d\u5fc5\u591a\u8651\uff0c\u5c3d\u7ba1\u65bd\u5c55\u4fbf\u662f\u3002\u54b1\u4eec\u505a\u536b\u58eb\u7684\uff0c\u96be\u9053\u5c3d\u5403\u996d\u4e0d\u7ba1\u4e8b\u4e48\uff1f\u201d\u51e4\u5929\u5357\u542b\u7b11\u62b1\u62f3\uff0c\u8bf4\u9053\uff1a\u201c\u5f97\u7f6a\uff0c\u5f97\u7f6a\uff01\u201d\u80e1\u6590\u5fc3\u60f3\uff1a\u201c\u65e0\u602a\u8fd9\u6076\u8d3c\u72ec\u9738\u4e00\u65b9\uff0c\u5386\u4e45\u4e0d\u8d25\u3002 \u3000\u3000\u4ed6\u4ea4\u7ed3\u5b98\u5e9c\uff0c\u786e\u662f\u5fc3\u601d\u5468\u5bc6\uff0c\u624b\u6bb5\u5341\u5206\u9ad8\u660e\u3002\u201d \u3000\u3000\u53ea\u89c1\u67ef\u5b50\u5bb9\u9664\u4e86\u957f\u888d\uff0c\u9732\u51fa\u5168\u8eab\u9ed1\u8272\u7d27\u8eab\u8863\u9760\u3002\u4ed6\u8fd9\u5957\u8863\u88e4\u751a\u662f\u5947\u7279\uff0c\u5230\u5904\u90fd\u662f\u53e3\u888b\u548c\u5e26\u5b50\uff0c\u8fd9\u91cc\u76db\u4e00\u888b\u94a2\u9556\uff0c\u90a3\u91cc\u63d2\u4e09\u628a\u98de\u5200\uff0c\u81ea\u5934\u9888\u4ee5\u81f3\u5c0f\u817f\uff0c\u6ca1\u4e00\u5904\u4e0d\u88c5\u6697\u5668\uff0c\u80f8\u524d\u56fa\u7136\u6709\u888b\uff0c\u80cc\u4e0a\u4e5f\u6709\u8bb8\u591a\u5c0f\u888b\u3002\u798f\u5eb7\u5b89\u54c8\u54c8\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4e8f\u4ed6\u60f3\u5f97\u51fa\u8fd9\u5957\u53e4\u602a\u88c5\u675f\uff0c\u5468\u8eab\u5012\u5982\u523a\u732c\u4e00\u822c\u3002\u201d \u3000\u3000\u53ea\u89c1\u67ef\u5b50\u5bb9\u5de6\u624b\u4e00\u7ffb\uff0c\u4ece\u8170\u95f4\u53d6\u51fa\u4e00\u53ea\u5f62\u4f3c\u6c34\u6753\u7684\u5175\u5668\u6765\uff0c\u53ea\u662f\u6753\u53e3\u950b\u5229\uff0c\u6709\u5982\u5229\u5203\u3002\u539f\u6765\u90a3\u662f\u4ed6\u5bb6\u4f20\u7684\u72ec\u95e8\u5175\u5668\uff0c\u6709\u4e00\u4e2a\u7279\u522b\u540d\u79f0\uff0c\u53eb\u505a\u201c\u77f3\u6c89\u5927\u6d77\u201d\u3002\u8fd9\u201c\u77f3\u6c89\u5927\u6d77\u201d\u4e00\u7269\u4e8c\u7528\uff0c\u672c\u8eab\u6709\u4e09\u5341\u516d\u8def\u62db\u6570\uff0c\u7528\u6cd5\u4ecb\u4e4e\u5355\u5200\u548c\u677f\u65a7\u4e4b\u95f4\uff0c\u4f46\u53e6\u6709\u4e00\u822c\u5999\u7528\uff0c\u53ef\u4ee5\u6284\u63a5\u6697\u5668\uff0c\u654c\u4eba\u4e0d\u8bba\u4f55\u79cd\u6697\u5668\u53d1\u5c04\u8fc7\u6765\uff0c\u4ed6\u8fd9\u94c1\u6753\u4e00\u515c\u4e00\u6284\uff0c\u4fbf\u63a5\u4e86\u8fc7\u53bb\uff0c\u5b9b\u4f3c\u77f3\u6c89\u5927\u6d77\u822c\u65e0\u5f71\u65e0\u8e2a\uff0c\u4ed6\u53cd\u53ef\u4ece\u6753\u4e2d\u53d6\u8fc7\u654c\u4eba\u6697\u5668\uff0c\u968f\u5373\u8fd8\u51fb\u3002\u8fd9\u201c\u77f3\u6c89\u5927\u6d77\u201d\u4e0d\u5c5e\u4e8e\u5341\u516b\u822c\u5175\u5668\u4e4b\u5217\uff0c\u4e43\u662f\u65c1\u95e8\u7684\u5175\u5203\uff0c\u6c5f\u6e56\u4e0a\u4e5f\u6709\u79f0\u4e4b\u4e3a\u201c\u501f\u7bad\u6753\u201d\u7684\uff0c\u610f\u8c13\u53ef\u501f\u654c\u4eba\u4e4b\u7bad\u800c\u7528\u3002 \u3000\u3000\u4ed6\u8fd9\u5175\u5668\u4e00\u53d6\u51fa\uff0c\u5385\u4e0a\u7fa4\u8c6a\u5012\u6709\u4e00\u5927\u534a\u4e0d\u8bc6\u5f97\u3002\u51e4\u5929\u5357\u7b11\u9053\uff1a\u201c\u67ef\u8001\u5e08\u4eca\u65e5\u8ba9\u6211\u4eec\u5927\u5f00\u773c\u754c\u3002\u201d\u80e1\u6590\u5374\u60f3\uff1a\u201c\u540c\u662f\u6697\u5668\u540d\u5bb6\uff0c\u8d75\u4e09\u54e5\u6f47\u6d12\u5927\u65b9\uff0c\u8eab\u4e0a\u4e0d\u89c1\u4e00\u679a\u6697\u5668\uff0c\u5374\u662f\u53d6\u4e4b\u4e0d\u7edd\uff0c\u7528\u4e4b\u4e0d\u5c3d\uff0c\u8fd9\u59d3\u67ef\u7684\u672a\u514d\u663e\u5f97\u5c0f\u5bb6\u6c14\u4e86\u3002\u201d \u3000\u3000\u53ea\u89c1\u67ef\u5b50\u5bb9\u94c1\u6753\u4e00\u7ffb\uff0c\u659c\u5288\u51e4\u5929\u5357\u80a9\u5934\u3002\u51e4\u5929\u5357\u4fa7\u8eab\u8ba9\u5f00\uff0c\u8fd8\u4e86\u4e00\u68cd\uff0c\u4e24\u4eba\u4fbf\u6597\u5c06\u8d77\u6765\u3002\u90a3\u67ef\u5b50\u5bb9\u53e3\u8bf4\u662f\u8ddf\u4ed6\u6bd4\u8bd5\u6697\u5668\uff0c\u4f46\u6753\u6cd5\u7cbe\u5999\uff0c\u6b65\u6b65\u8fdb\u903c\uff0c\u7adf\u662f\u4e0d\u653e\u6697\u5668\u3002 \u3000\u3000\u6597\u4e86\u4e00\u9635\uff0c\u67ef\u5b50\u5bb9\u53eb\u9053\uff1a\u201c\u770b\u9556\uff01\u201d\u98d5\u7684\u4e00\u54cd\uff0c\u4e00\u679a\u94a2\u9556\u98de\u63b7\u800c\u51fa\u3002\u51e4\u5929\u5357\u5e74\u7eaa\u5df2\u7136\u4e0d\u8f7b\uff0c\u591a\u5e74\u6765\u517b\u5c0a\u5904\u4f18\uff0c\u8eab\u6750\u4e5f\u6781\u80a5\u80d6\uff0c\u4f46\u5c11\u5e74\u65f6\u7684\u529f\u592b\u7adf\u6ca1\u4e1d\u6beb\u6401\u4e0b\uff0c\u7eb5\u8dc3\u7075\u6d3b\uff0c\u8f7b\u8f7b\u4e00\u95ea\uff0c\u4fbf\u628a\u94a2\u9556\u8ba9\u4e86\u5f00\u53bb\u3002\u67ef\u5b50\u5bb9\u53c8\u53eb\u9053\uff1a\u201c\u98de\u8757\u77f3\uff0c\u8896\u7bad\uff01\u201d\u8fd9\u4e00\u6b21\u662f\u4e24\u679a\u6697\u5668\u540c\u65f6\u5c04\u4e86\u51fa\u6765\u3002\u51e4\u5929\u5357\u4f4e\u5934\u907f\u5f00\u4e00\u679a\uff0c\u4ee5\u94dc\u68cd\u683c\u5f00\u4e00\u679a\u3002\u53ea\u542c\u67ef\u5b50\u5bb9\u53c8\u53eb\u9053\uff1a\u201c\u94c1\u84ba\u85dc\uff0c\u6253\u4f60\u5de6\u80a9\uff01\u98de\u5200\uff0c\u524a\u4f60\u53f3\u817f\uff01\u201d\u679c\u7136\u4e00\u679a\u94c1\u84ba\u85dc\u63b7\u5411\u4ed6\u5de6\u80a9\uff0c\u4e00\u67c4\u98de\u5200\u524a\u5411\u4ed6\u7684\u53f3\u817f\u3002\u51e4\u5929\u5357\u5148\u884c\u5f97\u4ed6\u63d0\u793a\uff0c\u8f7b\u8f7b\u5de7\u5de7\u7684\u4fbf\u907f\u8fc7\u4e86\u3002 \u3000\u3000\u4f17\u4eba\u5fc3\u60f3\uff0c\u8fd9\u67ef\u5b50\u5bb9\u5fd2\u4e5f\u8001\u5b9e\uff0c\u600e\u5730\u5c06\u6697\u5668\u7684\u79cd\u7c7b\u53bb\u8def\uff0c\u4e00\u4e00\u5148\u8ddf\u4ed6\u8bf4\u4e86\uff1f\u90a3\u77e5\u4ed6\u63b7\u51fa\u516b\u4e5d\u679a\u6697\u5668\u540e\uff0c\u53e3\u4e2d\u547c\u559d\u8d8a\u6765\u8d8a\u5feb\uff0c\u6697\u5668\u4e5f\u8d8a\u653e\u8d8a\u591a\uff0c\u547c\u559d\u5374\u975e\u6bcf\u6b21\u90fd\u5bf9\u4e86\u3002\u6709\u65f6\u53e3\u4e2d\u547c\u559d\u7528\u8896\u7bad\u5c04\u5de6\u773c\uff0c\u5176\u5b9e\u5374\u662f\u53d1\u98de\u8757\u77f3\u6253\u53f3\u80f8\u3002\u4f17\u4eba\u8fd9\u624d\u660e\u767d\uff0c\u539f\u6765\u4ed6\u53e3\u4e2d\u547c\u559d\u4e43\u662f\u6270\u654c\u5fc3\u795e\uff0c\u63a5\u8fde\u591a\u6b21\u547c\u559d\u4e0d\u9519\uff0c\u7a81\u7136\u5939\u4e00\u6b21\u9a97\u4eba\u7684\u53eb\u5524\uff0c\u53ea\u8981\u7a0d\u6709\u758f\u795e\uff0c\u7acb\u65f6\u4fbf\u4f1a\u4e0a\u5f53\u3002\u5018\u82e5\u6697\u5668\u53bb\u8def\u548c\u547c\u559d\u5168\u7136\u4e0d\u540c\uff0c\u5bf9\u65b9\u4fbf\u53ef\u6839\u672c\u7f6e\u4e4b\u4e0d\u7406\uff0c\u6076\u5728\u5bf9\u7684\u591a\u800c\u9519\u7684\u5c11\uff0c\u53ea\u5076\u5c14\u5728\u516d\u4e03\u6b21\u6b63\u786e\u7684\u547c\u559d\u4e4b\u4e2d\uff0c\u5939\u4e0a\u4e00\u6b21\u4f7f\u8bc8\uff0c\u90a3\u5c31\u6781\u4e3a\u96be\u9632\u3002 \u3000\u3000\u90ed\u7389\u5802\u9053\uff1a\u201c\u67ef\u5bb6\u4e03\u9752\u95e8\u7684\u6697\u5668\u529f\u592b\uff0c\u679c\u662f\u53e6\u6709\u4e00\u529f\uff0c\u770b\u6765\u4ed6\u53e3\u4e2d\u7684\u547c\u559d\uff0c\u4e5f\u662f\u4ece\u5c0f\u7ec3\u8d77\uff0c\u5176\u5389\u5bb3\u4e4b\u5904\uff0c\u5b9e\u4e0d\u8f93\u4e8e\u94a2\u9556\u98de\u5200\u3002\u4ed6\u8fd9\u2018\u4e03\u9752\u95e8\u2019\u4e4b\u540d\uff0c\u8981\u6539\u4e3a\u2018\u516b\u9752\u95e8\u2019\u624d\u5408\u3002\u201d\u59ec\u6653\u5cf0\u9053\uff1a\u201c\u4f46\u8fd9\u822c\u8be1\u8ba1\u591a\u7aef\uff0c\u4e0d\u662f\u540d\u95e8\u5927\u6d3e\u7684\u624b\u6bb5\u3002\u201d \u3000\u3000\u7a0b\u7075\u7d20\u624b\u4e2d\u73a9\u5f04\u7740\u4ece\u70df\u971e\u6563\u4eba\u5904\u593a\u6765\u7684\u5927\u70df\u888b\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u51e4\u8001\u5e08\u600e\u5730\u8fd8\u4e0d\u53d1\u5c04\u94f6\u9488\uff1f\u8fd9\u822c\u641e\u4e0b\u53bb\uff0c\u7ec8\u4e8e\u8981\u4e0a\u4e86\u8fd9\u59d3\u67ef\u7684\u5927\u5f53\u4e3a\u6b62\u3002\u201d\u59ec\u6653\u5cf0\u9053\uff1a\u201c\u6211\u77a7\u8fd9\u59d3\u51e4\u7684\u4f3c\u4e4e\u662f\u6210\u7af9\u5728\u80f8\uff0c\u4ed6\u53d1\u5c04\u6697\u5668\u662f\u8d35\u7cbe\u4e0d\u8d35\u591a\uff0c\u4e00\u51fb\u800c\u4e2d\uff0c\u4fbf\u8db3\u5236\u80dc\u3002\u201d\u7a0b\u7075\u7d20\u201c\u55ef\u201d\u7684\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6bd4\u6697\u5668\u4fbf\u6bd4\u6697\u5668\uff0c\u8fd9\u67ef\u5b50\u5bb9\u647d\u91cc\u647d\u5506\u7684\u7f20\u5939\u4e0d\u6e05\u3002\u201d \u3000\u3000\u8fd9\u65f6\u5927\u5385\u4e0a\u7a7a\uff0c\u5341\u4f59\u679a\u6697\u5668\u98de\u821e\u6765\u53bb\uff0c\u597d\u770b\u715e\u4eba\u3002\u5468\u94c1\u9e6a\u7b49\u4e25\u52a0\u6212\u5907\uff0c\u4fdd\u62a4\u5927\u5e05\u3002\u5b89\u63d0\u7763\u7b49\u5927\u5b98\u8eab\u4fa7\uff0c\u4e5f\u5404\u6709\u9ad8\u624b\u536b\u58eb\u9632\u536b\u3002\u4f17\u536b\u58eb\u4e0d\u4f46\u9632\u67ef\u5b50\u5bb9\u53d1\u5c04\u7684\u9556\u7bad\u98de\u6765\u8bef\u4f24\uff0c\u8fd8\u6050\u7fa4\u8c6a\u4e4b\u4e2d\u6df7\u6709\u523a\u5ba2\uff0c\u4e58\u4e71\u53d1\u5c04\u6697\u5668\uff0c\u7adf\u5411\u5927\u5e05\u4e0b\u624b\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u5ffd\u9053\uff1a\u201c\u8fd9\u59d3\u67ef\u7684\u592a\u8fc7\u8ba8\u538c\uff0c\u6211\u6765\u5f00\u4ed6\u4e2a\u73a9\u7b11\u3002\u201d\u53ea\u542c\u5f97\u67ef\u5b50\u5bb9\u53eb\u9053\uff1a\u201c\u94c1\u84ba\u85dc\uff0c\u6253\u4f60\u5de6\u81c2\uff01\u201d\u7a0b\u7075\u7d20\u5b66\u7740\u4ed6\u7684\u58f0\u8c03\u8bed\u6c14\uff0c\u4e5f\u53eb\u9053\uff1a\u201c\u8089\u9992\u5934\uff0c\u6253\u4f60\u7684\u5634\u5df4\uff01\u201d\u53f3\u624b\u5728\u70df\u6597\u4e0a\u51d1\u4e86\u4e00\u4e0b\uff0c\u968f\u624b\u4e00\u626c\uff0c\u4e00\u679a\u5c0f\u5c0f\u7684\u6697\u5668\u679c\u7136\u5c04\u5411\u4ed6\u7684\u5634\u5df4\u3002\u8fd9\u6697\u5668\u98de\u53bb\u65f6\u5e76\u65e0\u7834\u7a7a\u4e4b\u58f0\uff0c\u770b\u6765\u4efd\u91cf\u751a\u8f7b\uff0c\u53ea\u662f\u4e0a\u9762\u5e26\u6709\u4e00\u4e1d\u706b\u661f\u3002\u4fd7\u8bed\u9053\uff1a\u201c\u8089\u9992\u5934\u6253\u72d7\uff0c\u6709\u53bb\u65e0\u56de\u3002\u201d\u4f17\u4eba\u542c\u5230\u201c\u8089\u9992\u5934\uff0c\u6253\u4f60\u7684\u5634\u5df4\u201d\u516b\u5b57\uff0c\u5df2\u662f\u5341\u5206\u597d\u7b11\uff0c\u4f55\u51b5\u5979\u5b66\u7684\u58f0\u8c03\u8bed\u6c14\uff0c\u8ddf\u67ef\u5b50\u5bb9\u7684\u547c\u559d\u4e00\u822c\u65e0\u4e8c\uff0c\u65e9\u6709\u6570\u5341\u4eba\u7b11\u4e86\u8d77\u6765\u3002 \u3000\u3000\u67ef\u5b50\u5bb9\u89c1\u6697\u5668\u6765\u5f97\u5947\u7279\uff0c\u63d0\u8d77\u201c\u501f\u7bad\u6753\u201d\u4e00\u6284\uff0c\u515c\u5728\u6753\u4e2d\uff0c\u5de6\u624b\u4fbf\u4f38\u5165\u6753\u4e2d\u6361\u8d77\uff0c\u6b32\u5f85\u8fd8\u656c\uff0c\u7a81\u7136\u95f4\u201c\u562d\u201d\u7684\u4e00\u58f0\u5de8\u54cd\uff0c\u90a3\u6697\u5668\u70b8\u4e86\u5f00\u6765\u3002\u4f17\u4eba\u5927\u5403\u4e00\u60ca\uff0c\u67ef\u5b50\u5bb9\u66f4\u662f\u5168\u8eab\u8df3\u8d77\u3002\u4f46\u89c1\u7eb8\u5c51\u7eb7\u98de\uff0c\u9f3b\u4e2d\u95fb\u5230\u4e00\u9635\u785d\u78fa\u6c14\u606f\uff0c\u5374\u90a3\u91cc\u662f\u6697\u5668\uff0c\u7adf\u662f\u4e00\u679a\u5b69\u7ae5\u9022\u5e74\u8fc7\u8282\u73a9\u800d\u7684\u5c0f\u7206\u7af9\u3002\u4f17\u4eba\u4e00\u5446\u4e4b\u4e0b\uff0c\u968f\u5373\u5168\u5802\u54c4\u7b11\u3002 \u3000\u3000\u67ef\u5b50\u5bb9\u5168\u795e\u8d2f\u6ce8\u5728\u51e4\u5929\u5357\u8eab\u4e0a\uff0c\u751f\u6050\u4ed6\u5077\u53d1\u65e0\u5f71\u94f6\u9488\uff0c\u867d\u7136\u906d\u6b64\u4fae\u5f04\uff0c\u5374\u662f\u76ee\u4e0d\u659c\u89c6\uff0c\u4e0d\u6562\u641c\u5bfb\u6295\u63b7\u8fd9\u679a\u7206\u7af9\u4e4b\u4eba\uff0c\u53ea\u662f\u9a82\u9053\uff1a\u201c\u6709\u79cd\u7684\u4fbf\u6765\u6bd4\u5212\u6bd4\u5212\uff0c\u8c01\u8ddf\u4f60\u95f9\u8fd9\u4e9b\u987d\u7ae5\u884c\u5f84\uff1f\u201d \u3000\u3000\u7a0b\u7075\u7d20\u7ad9\u8d77\u8eab\u6765\uff0c\u7b11\u563b\u563b\u7684\u8d70\u5230\u4e1c\u9996\uff0c\u53c8\u53d6\u51fa\u4e00\u679a\u7206\u7af9\uff0c\u5728\u70df\u888b\u4e2d\u70b9\u71c3\u4e86\uff0c\u53eb\u9053\uff1a\u201c\u5927\u77f3\u5934\uff0c\u6253\u4f60\u7684\u4e03\u5bf8\u3002\u201d\u5e38\u8a00\u9053\uff1a\u201c\u6253\u86c7\u6253\u4e03\u5bf8\u201d\uff0c\u86c7\u9888\u79bb\u9996\u4e03\u5bf8\uff0c\u4e43\u662f\u6bd2\u86c7\u81f4\u547d\u4e4b\u5904\uff0c\u8fd9\u4e00\u6b21\u7adf\u662f\u5c06\u4ed6\u6bd4\u4f5c\u4e86\u6bd2\u86c7\u3002\u4f17\u4eba\u54c4\u7b11\u58f0\u4e2d\uff0c\u90a3\u7206\u7af9\u98de\u63b7\u8fc7\u53bb\u3002\u8fd9\u4e00\u56de\u4ed6\u518d\u4e0d\u4e0a\u5f53\u3002\u7a0b\u7075\u7d20\u8fd9\u7206\u7af9\u53c8\u63b7\u5f97\u4f3c\u4e4e\u592a\u65e9\uff0c\u67ef\u5b50\u5bb9\u624b\u6307\u5f39\u51fa\u4e00\u679a\u4e27\u95e8\u9489\uff0c\u5c06\u7206\u7af9\u6253\u56de\uff0c\u562d\u7684\u4e00\u54cd\uff0c\u7206\u7af9\u5728\u7a7a\u4e2d\u70b8\u4e86\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u53c8\u63b7\u4e00\u679a\uff0c\u53eb\u9053\uff1a\u201c\u9752\u77f3\u677f\uff0c\u6253\u4f60\u7684\u786c\u58f3\u3002\u201d\u90a3\u662f\u5c06\u4ed6\u6bd4\u4f5c\u4e4c\u9f9f\u4e86\u3002\u67ef\u5b50\u5bb9\u5fc3\u60f3\uff1a\u201c\u4f60\u662f\u8981\u6fc0\u6012\u6211\uff0c\u597d\u8ba9\u90a3\u59d3\u51e4\u7684\u4e58\u673a\u4e0b\u624b\uff0c\u6211\u504f\u4e0d\u4e0a\u4f60\u7684\u5f53\u3002\u201d\u5f53\u4e0b\u53c8\u5f39\u51fa\u4e00\u679a\u4e27\u95e8\u9489\uff0c\u5c06\u7206\u7af9\u5f39\u5f00\uff0c\u4ecd\u662f\u5728\u534a\u7a7a\u70b8\u4e86\u3002 \u3000\u3000\u5b89\u63d0\u7763\u7b11\u7740\u53eb\u9053\uff1a\u201c\u4e24\u4eba\u6bd4\u8bd5\uff0c\u65c1\u4eba\u4e0d\u5f97\u6ecb\u6270\u3002\u201d\u53c8\u89c1\u67ef\u5b50\u5bb9\u8fd9\u4e24\u679a\u4e27\u95e8\u9489\u8dcc\u843d\u65f6\u548c\u5b89\u653e\u7389\u9f99\u676f\u7684\u957f\u51e0\u76f8\u8ddd\u592a\u8fd1\uff0c\u5bf9\u8eab\u65c1\u7684\u4e24\u540d\u536b\u58eb\u9053\uff1a\u201c\u8fc7\u53bb\u62a4\u7740\u5fa1\u676f\uff0c\u522b\u8ba9\u6697\u5668\u6253\u788e\u4e86\u3002\u201d\u4e24\u540d\u536b\u58eb\u5e94\u9053\uff1a\u201c\u662f\uff01\u201d\u8d70\u5230\u957f\u51e0\u4e4b\u524d\uff0c\u6321\u5728\u5fa1\u676f\u4e4b\u524d\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u7b11\u563b\u563b\u7684\u56de\u5f52\u5ea7\u4f4d\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u5bb6\u4f19\u673a\u4f36\u5f97\u7d27\uff0c\u4e0a\u4e86\u4e00\u56de\u5f53\uff0c\u7b2c\u4e8c\u6b21\u4e0d\u80af\u4f38\u624b\u53bb\u63a5\u7206\u7af9\u3002\u201d\u80e1\u6590\u6697\u81ea\u5947\u602a\uff1a\u201c\u4e8c\u59b9\u660e\u77e5\u51e4\u5929\u5357\u662f\u6211\u5bf9\u5934\uff0c\u5374\u504f\u53bb\u4f5c\u5f04\u90a3\u59d3\u67ef\u7684\uff0c\u4e0d\u77e5\u662f\u4f55\u7528\u610f\uff1f\u201d \u3000\u3000\u67ef\u5b50\u5bb9\u89c1\u4eba\u4eba\u8138\u4e0a\u5747\u542b\u7b11\u610f\uff0c\u6025\u6b32\u633d\u56de\u989c\u9762\uff0c\u6697\u5668\u8d8a\u5c04\u8d8a\u591a\u3002\u51e4\u5929\u5357\u624b\u5fd9\u811a\u4e71\uff0c\u5df2\u81ea\u96be\u4ee5\u652f\u6301\uff0c\u7a81\u7136\u95f4\u4f38\u624b\u5728\u94dc\u68cd\u5934\u4e0a\u4e00\u62bd\u3002\u67ef\u5b50\u5bb9\u53ea\u9053\u4ed6\u8981\u53d1\u5c04\u94f6\u9488\uff0c\u6025\u5fd9\u7eb5\u8eab\u8dc3\u5f00\uff0c\u5374\u89c1\u4ed6\u4ece\u94dc\u68cd\u4e2d\u62bd\u51fa\u4e00\u6761\u4e1c\u897f\uff0c\u987a\u52bf\u4e00\u6325\uff0c\u90a3\u7269\u5982\u96e8\u4f1e\u822c\u5f20\u4e86\u5f00\u6765\uff0c\u6210\u4e3a\u4e00\u9762\u8f7b\u76fe\u3002\u8fd9\u8f7b\u76fe\u6781\u8f6f\u6781\u8584\uff0c\u4f3c\u662f\u4e00\u53ea\u7eb8\u9e5e\uff0c\u76fe\u9762\u9ed1\u9edd\u9edd\u5730\uff0c\u4e0d\u77e5\u662f\u7528\u4eba\u53d1\u8fd8\u662f\u7528\u4ec0\u4e48\u7279\u5f02\u8d28\u6599\u7f16\u7ec7\u800c\u6210\uff0c\u76fe\u4e0a\u7ed8\u7740\u4e94\u4e2a\u864e\u5934\uff0c\u5f20\u53e3\u9732\u7259\uff0c\u795e\u6001\u5a01\u731b\u3002\u4f17\u4eba\u4e00\u89c1\uff0c\u5fc3\u4e2d\u90fd\u9053\uff1a\u201c\u4ed6\u662f\u4e94\u864e\u95e8\u7684\u638c\u95e8\u4eba\uff0c\u2018\u4e94\u864e\u95e8\u2019\u8fd9\u540d\u79f0\uff0c\u539f\u6765\u8fd8\u662f\u4ece\u8fd9\u76fe\u724c\u800c\u6765\u3002\u201d \u3000\u3000\u53ea\u89c1\u4ed6\u4e00\u624b\u6325\u68cd\uff0c\u4e00\u624b\u6301\u76fe\uff0c\u5c06\u67ef\u5b50\u5bb9\u6e90\u6e90\u5c04\u6765\u7684\u6697\u5668\u5c3d\u6570\u6321\u5f00\u3002\u90a3\u4e9b\u9556\u7bad\u5200\u77f3\u867d\u7136\u6765\u52bf\u5f3a\u52b2\uff0c\u4f46\u7adf\u662f\u6253\u4e0d\u7a7f\u8fd9\u9762\u8f7b\u8f6f\u76fe\u724c\uff0c\u770b\u6765\u8fd9\u8f7b\u76fe\u7684\u8d28\u5730\u5b9e\u662f\u575a\u97e7\u4e4b\u6781\u3002 \u3000\u3000\u80e1\u6590\u4e00\u89c1\u5230\u4ed6\u4ece\u68cd\u4e2d\u62bd\u51fa\u8f7b\u76fe\uff0c\u767b\u65f6\u9192\u609f\uff0c\u81ea\u9a82\u611a\u4e0d\u53ef\u53ca\uff1a\u201c\u4ed6\u5728\u94dc\u68cd\u4e2d\u6697\u85cf\u673a\u5173\uff0c\u8fd9\u7b49\u660e\u767d\u7684\u4e8b\uff0c\u5148\u524d\u5982\u4f55\u731c\u60f3\u4e0d\u900f\uff1f\u4ed6\u8fd9\u94f6\u9488\u81ea\u7136\u4e5f\u662f\u88c5\u5728\u94dc\u68cd\u4e4b\u4e2d\uff0c\u6fc0\u6597\u65f6\u53ea\u987b\u4e00\u6309\u68cd\u4e0a\u673a\u62ec\uff0c\u94f6\u9488\u6fc0\u5c04\u800c\u51fa\uff0c\u8c01\u80fd\u8eb2\u95ea\u5f97\u4e86\uff1f\u4eba\u4eba\u53ea\u9053\u53d1\u5c04\u6697\u5668\u5b9a\u987b\u4f38\u81c2\u626c\u624b\uff0c\u4ed6\u5374\u53ea\u987b\u5728\u94dc\u68cd\u7684\u4e00\u5b9a\u90e8\u4f4d\u4e00\u634f\uff0c\u94f6\u9488\u5c04\u51fa\uff0c\u81ea\u662f\u795e\u4e0d\u77e5\u9b3c\u4e0d\u89c9\u4e86\u3002\u201d \u3000\u3000\u60f3\u660e\u6b64\u8282\uff0c\u7cbe\u795e\u4e3a\u4e4b\u4e00\u632f\uff0c\u5fcc\u654c\u4e4b\u5fc3\u5c3d\u53bb\uff0c\u4f46\u89c1\u51e4\u5929\u5357\u8fb9\u6253\u8fb9\u9000\uff0c\u6e10\u6e10\u9000\u5411\u4e00\u5217\u516b\u5f20\u592a\u5e08\u6905\u4e4b\u524d\uff0c\u731b\u542c\u5f97\u67ef\u5b50\u5bb9\u4e00\u58f0\u60e8\u53eb\uff0c\u51e4\u5929\u5357\u7eb5\u58f0\u957f\u7b11\u3002\u67ef\u5b50\u5bb9\u5012\u9000\u6570\u6b65\uff0c\u624b\u6309\u80ef\u4e0b\uff0c\u6162\u6162\u8e72\u4e0b\u8eab\u53bb\uff0c\u518d\u4e5f\u7ad9\u4e0d\u8d77\u6765\u3002\u51e4\u5929\u5357\u5374\u7b11\u541f\u541f\u7684\u5750\u5165\u592a\u5e08\u6905\u4e2d\u3002 \u3000\u3000\u4e24\u540d\u536b\u58eb\u4e0a\u524d\u53bb\uff0c\u6276\u8d77\u67ef\u5b50\u5bb9\uff0c\u53ea\u89c1\u4ed6\u54ac\u7d27\u7259\u5173\uff0c\u4f38\u624b\u4ece\u80ef\u4e0b\u62d4\u51fa\u4e00\u679a\u94f6\u9488\uff0c\u9488\u4e0a\u67d3\u6ee1\u9c9c\u8840\u3002\u94f6\u9488\u867d\u7ec6\uff0c\u56e0\u662f\u6253\u4e2d\u4e0b\u9634\u8981\u7a74\uff0c\u53d7\u4f24\u5927\u662f\u4e0d\u8f7b\u3002\u4ed6\u5df2\u4e0d\u80fd\u884c\u8d70\uff0c\u5728\u4e24\u540d\u536b\u58eb\u6400\u6276\u4e0b\u8e09\u8dc4\u800c\u9000\u3002 \u3000\u3000\u6c64\u6c9b\u5ffd\u7136\u9f3b\u4e2d\u4e00\u54fc\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u6697\u7bad\u4f24\u4eba\uff0c\u975e\u4e3a\u597d\u6c49\uff01\u201d\u51e4\u5929\u5357\u8f6c\u8fc7\u5934\u53bb\uff0c\u8bf4\u9053\uff1a\u201c\u6c64\u5927\u4fa0\u53ef\u662f\u8bf4\u6211\u4e48\uff1f\u201d\u6c64\u6c9b\u9053\uff1a\u201c\u6211\u8bf4\u7684\u662f\u6697\u7bad\u4f24\u4eba\uff0c\u975e\u4e3a\u597d\u6c49\u3002\u5927\u4e08\u592b\u5149\u660e\u78ca\u843d\uff0c\u4f55\u4ee5\u8981\u5e72\u8fd9\u7b49\u52fe\u5f53\uff1f\u201d\u51e4\u5929\u5357\u970d\u5730\u7ad9\u8d77\u559d\u9053\uff1a\u201c\u54b1\u4eec\u8bb2\u660e\u4e86\u662f\u6bd4\u5212\u6697\u5668\uff0c\u6697\u5668\u6697\u5668\uff0c\u96be\u9053\u8fd8\u6709\u660e\u7684\u5417\uff1f\u201d \u3000\u3000\u6c64\u6c9b\u9053\uff1a\u201c\u51e4\u8001\u5e08\u8981\u8ddf\u6211\u6bd4\u5212\u6bd4\u5212\uff0c\u662f\u4e0d\u662f\uff1f\u201d\u51e4\u5929\u5357\u9053\uff1a\u201c\u6c64\u5927\u4fa0\u540d\u9707\u5929\u4e0b\uff0c\u5c0f\u4eba\u5c82\u6562\u5192\u72af\uff1f\u8fd9\u59d3\u67ef\u7684\u60f3\u662f\u6c64\u5927\u4fa0\u7684\u81f3\u4ea4\u597d\u53cb\u4e86\uff1f\u201d\u6c64\u6c9b\u6c89\u7740\u8138\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5170\u5dde\u67ef\u5bb6\u8ddf\u5728\u4e0b\u6709\u70b9\u513f\u4ea4\u60c5\u3002\u201d\u51e4\u5929\u5357\u9053\uff1a\u201c\u65e2\u662f\u5982\u6b64\uff0c\u5c0f\u4eba\u820d\u547d\u966a\u541b\u5b50\uff0c\u6c64\u5927\u4fa0\u5212\u4e0b\u9053\u513f\u6765\u5427\uff01\u201d \u3000\u3000\u4e24\u4eba\u8d8a\u8bf4\u8d8a\u50f5\uff0c\u773c\u89c1\u4fbf\u8981\u52a8\u624b\u3002\u80e1\u6590\u5fc3\u9053\uff1a\u201c\u8fd9\u6c64\u6c9b\u867d\u7136\u4ea4\u7ed3\u5b98\u5e9c\uff0c\u5374\u8fd8\u6709\u662f\u975e\u5584\u6076\u4e4b\u5206\u3002\u201d \u3000\u3000\u5b89\u63d0\u7763\u8d70\u4e86\u8fc7\u6765\uff0c\u7b11\u9053\uff1a\u201c\u6c64\u5927\u4fa0\u662f\u6bd4\u8bd5\u7684\u516c\u8bc1\uff0c\u4eca\u65e5\u662f\u4e0d\u80fd\u5927\u663e\u8eab\u624b\u7684\u3002\u8fc7\u51e0\u65e5\u5c0f\u5f1f\u4f5c\u4e1c\uff0c\u90a3\u65f6\u8bf7\u6c64\u5927\u4fa0\u9732\u4e00\u624b\uff0c\u8ba9\u5927\u4f19\u513f\u5f00\u5f00\u773c\u754c\u3002\u201d\u6c64\u6c9b\u7b11\u9053\uff1a\u201c\u90a3\u5148\u591a\u8c22\u63d0\u7763\u5927\u4eba\u8d4f\u9152\u4e86\u3002\u201d \u3000\u3000\u8f6c\u5934\u5411\u51e4\u5929\u5357\u6a2a\u4e86\u4e00\u773c\uff0c\u63d0\u8d77\u81ea\u5df1\u7684\u592a\u5e08\u6905\u5f80\u5730\u4e0b\u4e00\u8e6c\uff0c\u518d\u63d0\u8d77\u6765\u79fb\u5728\u4e00\u65c1\uff0c\u548c\u51e4\u5929\u5357\u8fdc\u79bb\u6570\u5c3a\uff0c\u8fd9\u624d\u5750\u4e0b\uff0c\u4f3c\u4e4e\u4e0d\u5c51\u4e0e\u4ed6\u9760\u8fd1\u3002 \u3000\u3000\u8fd9\u4e00\u79fb\u6905\uff0c\u53ea\u89c1\u9752\u7816\u4e0a\u9732\u51fa\u4e86\u56db\u4e2a\u6df1\u6df1\u7684\u6905\u811a\u811a\u5370\uff0c\u5385\u4e0a\u70db\u5149\u660e\u4eae\u5982\u540c\u767d\u663c\uff0c\u7ad9\u5f97\u8f83\u8fd1\u7684\u90fd\u77a7\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u8fd9\u4e00\u624b\u529f\u592b\u770b\u4f3c\u4e0d\u96be\uff0c\u5176\u5b9e\u662f\u8574\u84c4\u7740\u6570\u5341\u5e74\u4fee\u4e3a\u7684\u5185\u529b\u3002\u970e\u65f6\u4e4b\u95f4\uff0c\u5385\u4e0a\u5f69\u58f0\u96f7\u52a8\u3002\u7ad9\u5728\u540e\u9762\u7684\u4eba\u6ca1\u77a7\u89c1\uff0c\u6025\u5fd9\u67e5\u95ee\uff0c\u7b49\u5f97\u95ee\u660e\u767d\u4e86\uff0c\u53c8\u6324\u4e0a\u524d\u6765\u89c2\u770b\u3002 \u3000\u3000\u51e4\u5929\u5357\u51b7\u7b11\u9053\uff1a\u201c\u6c64\u5927\u4fa0\u8fd9\u624b\u529f\u592b\u5e05\u6781\u4e86\uff01\u5728\u4e0b\u518d\u7ec3\u4e8c\u5341\u5e74\u4e5f\u7ec3\u4e0d\u6210\u3002\u53ef\u662f\u5929\u5916\u6709\u5929\uff0c\u4eba\u4e0a\u6709\u4eba\uff0c\u5728\u771f\u6b63\u6b66\u5b66\u9ad8\u624b\u770b\u6765\uff0c\u90a3\u4e5f\u5e73\u5e73\u65e0\u5947\u3002\u201d\u6c64\u6c9b\u9053\uff1a\u201c\u51e4\u8001\u5e08\u8bf4\u5f97\u534a\u70b9\u4e5f\u4e0d\u9519\uff0c\u5728\u6b66\u5b66\u9ad8\u624b\u77a7\u6765\uff0c\u771f\u662f\u4e00\u6587\u94b1\u4e5f\u4e0d\u503c\u3002\u4e0d\u8fc7\u53ea\u8981\u80fd\u80dc\u5f97\u8fc7\u51e4\u8001\u5e08\uff0c\u6211\u4e5f\u5fc3\u6ee1\u610f\u8db3\u4e86\u3002\u201d \u3000\u3000\u5b89\u63d0\u7763\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4f4d\u5c3d\u6597\u4ec0\u4e48\u53e3\uff1f\u5929\u4e5f\u5feb\u4eae\u5566\uff0c\u4e03\u53ea\u7389\u9f99\u676f\uff0c\u516d\u53ea\u5df2\u6709\u4e86\u4e3b\u513f\u3002\u54b1\u4eec\u4eca\u665a\u5b9a\u4e86\u7389\u9f99\u676f\u7684\u540d\u5206\uff0c\u660e\u665a\u518d\u6765\u4e89\u91d1\u51e4\u676f\u548c\u94f6\u9ca4\u676f\u3002\u8fd8\u6709\u54ea\u4e00\u4f4d\u82f1\u96c4\uff0c\u8981\u4e0a\u6765\u8ddf\u51e4\u8001\u5e08\u6bd4\u5212\uff1f\u201d\u4ed6\u63d0\u8d77\u55d3\u5b50\u8fde\u53eb\u4e09\u904d\uff0c\u5927\u5385\u4e0a\u9759\u6084\u6084\u5730\u6ca1\u4eba\u7b54\u8154\u3002 \u3000\u3000\u5b89\u63d0\u7763\u5411\u51e4\u5929\u5357\u9053\uff1a\u201c\u606d\u559c\u51e4\u8001\u5e08\uff0c\u8fd9\u53ea\u7389\u9f99\u676f\u5f52\u4e86\u4f60\u5566\uff01\u201d"}),showOnHomepage:!0},{name:"\u7b2c\u5341\u4e5d\u7ae0\u3000\u76f8\u89c1\u6b22",content:(0,a.jsx)(a.Fragment,{children:"\u5ffd\u542c\u5f97\u4e00\u4eba\u53eb\u9053\uff1a\u201c\u4e14\u6162\uff0c\u6211\u6765\u6597\u4e00\u6597\u51e4\u5929\u5357\u3002\u201d\u53ea\u89c1\u4e00\u4e2a\u5f62\u8c8c\u59d4\u7410\u7684\u9ec4\u80e1\u5b50\u4e2d\u5e74\u4eba\u7a7a\u624b\u8dc3\u51fa\uff0c\u5531\u540d\u7684\u6b66\u5b98\u5531\u9053\uff1a\u201c\u897f\u5cb3\u534e\u62f3\u95e8\u638c\u95e8\u4eba\u7a0b\u7075\u80e1\u7a0b\u8001\u5e08\uff01\u201d \u3000\u3000\u51e4\u5929\u5357\u7ad9\u8d77\u8eab\u6765\uff0c\u53cc\u624b\u6a2a\u6301\u94dc\u68cd\uff0c\u8bf4\u9053\uff1a\u201c\u7a0b\u8001\u5e08\u7528\u4ec0\u4e48\u5175\u5203\uff1f\u201d \u3000\u3000\u80e1\u6590\u68ee\u7136\u9053\uff1a\u201c\u90a3\u96be\u8bf4\u5f97\u5f88\u3002\u201d\u7a81\u7136\u7331\u8eab\u76f4\u4e0a\uff0c\u6b3a\u5230\u7aef\u5750\u5728\u592a\u5e08\u6905\u4e2d\u7684\u7530\u5f52\u519c\u8eab\u524d\uff0c\u5de6\u624b\u98df\u4e2d\u4e24\u6839\u624b\u6307\u201c\u53cc\u9f99\u62a2\u73e0\u201d\uff0c\u6233\u5411\u7530\u5f52\u519c\u53cc\u76ee\u3002 \u3000\u3000\u8fd9\u4e00\u7740\u4eba\u4eba\u90fd\u662f\u5927\u51fa\u610f\u6599\u4e4b\u5916\u3002\u7530\u5f52\u519c\u867d\u7136\u5927\u5403\u4e00\u60ca\uff0c\u5e94\u53d8\u4ecd\u662f\u5947\u901f\uff0c\u53cc\u624b\u6325\u51fa\uff0c\u5c01\u4f4f\u6765\u62db\u3002\u90a3\u77e5\u4ed6\u5feb\uff0c\u80e1\u6590\u66f4\u5feb\uff0c\u53cc\u624b\u4e00\u5708\uff0c\u5df2\u53d8\u201c\u6000\u4e2d\u62b1\u6708\u201d\uff0c\u5206\u51fb\u4ed6\u4e24\u4fa7\u592a\u9633\u7a74\u3002\u7530\u5f52\u519c\u4e0d\u53ca\u8d77\u8eab\u8fce\u654c\uff0c\u53cc\u624b\u5916\u683c\uff0c\u4ee5\u6321\u4fa7\u51fb\u3002 \u3000\u3000\u80e1\u6590\u4e58\u4ed6\u53cc\u624b\u63d0\u8d77\u6321\u67b6\uff0c\u814b\u4e0b\u7a7a\u865a\uff0c\u4e00\u7ffb\u624b\uff0c\u5df2\u6293\u4f4f\u4ed6\u8170\u95f4\u5b9d\u5200\u7684\u5200\u67c4\uff0c\u5237\u7684\u4e00\u54cd\uff0c\u9752\u5149\u95ea\u5904\uff0c\u5b9d\u5200\u5df2\u5165\u624b\u4e2d\uff0c\u4e58\u52bf\u8f6c\u8eab\uff0c\u780d\u5411\u51e4\u5929\u5357\u624b\u4e2d\u7684\u94dc\u68cd\u3002 \u3000\u3000\u5200\u662f\u5b9d\u5200\uff0c\u62db\u662f\u5feb\u62db\uff0c\u53ea\u542c\u5f97\u5bdf\u5bdf\u5bdf\u4e09\u58f0\u8f7b\u54cd\uff0c\u8ddf\u7740\u5f53\u5577\u5577\u4e24\u58f0\uff0c\u51e4\u5929\u5357\u7684\u719f\u94dc\u68cd\u4e2d\u95f4\u65ad\u4e0b\u4e24\u622a\uff0c\u6389\u5728\u5730\u4e0b\u3002\u539f\u6765\u80e1\u6590\u5728\u77ac\u606f\u4e4b\u95f4\u8fde\u780d\u4e09\u5200\uff0c\u51e4\u5929\u5357\u672a\u53ca\u53d8\u62db\uff0c\u624b\u4e2d\u5175\u5203\u5df2\u53d8\u6210\u56db\u6bb5\uff0c\u53cc\u624b\u5404\u63e1\u7740\u77ed\u77ed\u7684\u4e00\u622a\u94dc\u68cd\uff0c\u97ad\u4e0d\u50cf\u97ad\uff0c\u5c3a\u4e0d\u50cf\u5c3a\uff0c\u5b9e\u662f\u5c34\u5c2c\u5f02\u5e38\u3002 \u3000\u3000\u51e4\u5929\u5357\u60ca\u60f6\u4e4b\u4e0b\uff0c\u6025\u5fd9\u5411\u65c1\u8dc3\u5f00\u4e09\u6b65\u3002\u4fbf\u5728\u6b64\u65f6\uff0c\u7ad9\u5728\u5385\u95e8\u53e3\u7684\u6c6a\u94c1\u9e57\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u4e5d\u5bb6\u534a\u603b\u638c\u95e8\u5230\u3002\u201d \u3000\u3000\u80e1\u6590\u5fc3\u5934\u4e00\u51db\uff0c\u62ac\u5934\u5411\u5385\u95e8\u770b\u53bb\uff0c\u767b\u65f6\u60ca\u5f97\u5446\u4e86\u3002 \u3000\u3000\u53ea\u89c1\u95e8\u4e2d\u8fdb\u6765\u4e00\u4e2a\u5999\u9f84\u5c3c\u59d1\uff0c\u7f01\u8863\u8292\u978b\uff0c\u624b\u6267\u4e91\u5e1a\uff0c\u6b63\u662f\u8881\u7d2b\u8863\u3002\u53ea\u662f\u5979\u5934\u4e0a\u5df2\u65e0\u4e00\u6839\u9752\u4e1d\uff0c\u8111\u95e8\u5904\u5e76\u6709\u6212\u5370\u3002 \u3000\u3000\u80e1\u6590\u53cc\u773c\u4e00\u82b1\uff0c\u8fd8\u6015\u662f\u770b\u9519\u4e86\u4eba\uff0c\u8fce\u4e0a\u4e00\u6b65\uff0c\u770b\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u5374\u4e0d\u662f\u8881\u7d2b\u8863\u662f\u8c01\uff1f \u3000\u3000\u970e\u65f6\u95f4\u80e1\u6590\u53ea\u89c9\u5929\u65cb\u5730\u8f6c\uff0c\u5fc3\u4e2d\u4e71\u6210\u4e00\u7247\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u662f\u8881\u2026\u2026\u201d \u3000\u3000\u8881\u7d2b\u8863\u53cc\u624b\u5408\u5341\uff0c\u9eef\u7136\u9053\uff1a\u201c\u5c0f\u5c3c\u5706\u6027\u3002\u201d \u3000\u3000\u80e1\u6590\u5140\u81ea\u6ca1\u4f1a\u8fc7\u610f\u6765\uff0c\u7a81\u7136\u95f4\u80cc\u5fc3\u201c\u60ac\u67a2\u7a74\u201d\u201c\u547d\u95e8\u7a74\u201d \u3000\u3000\u4e24\u5904\u7a74\u9053\u75bc\u75db\u5165\u9aa8\uff0c\u811a\u6b65\u4e00\u6643\uff0c\u6454\u5012\u5728\u5730\uff0c\u624b\u4e2d\u5b9d\u5200\u4e5f\u6492\u624b\u629b\u51fa\u3002 \u3000\u3000\u8881\u7d2b\u8863\u6012\u559d\uff1a\u201c\u4f4f\u624b\uff01\u201d\u6025\u5fd9\u62a2\u4e0a\uff0c\u62e6\u5728\u80e1\u6590\u8eab\u540e\u3002 \u3000\u3000\u81ea\u80e1\u6590\u593a\u5200\u65ad\u68cd\u3001\u4e5d\u5bb6\u534a\u603b\u638c\u95e8\u73b0\u8eab\uff0c\u4ee5\u81f3\u80e1\u6590\u53d7\u4f24\u5012\u5730\uff0c\u53ea\u9877\u523b\u4e4b\u95f4\u7684\u4e8b\u3002\u5385\u4e0a\u4f17\u4eba\u5c3d\u7686\u9519\u6115\u4e4b\u9645\uff0c\u5df2\u662f\u5947\u53d8\u6a2a\u751f\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u89c1\u80e1\u6590\u53d7\u4f24\uff0c\u5fc3\u4e0b\u5927\u6025\uff0c\u6025\u5fd9\u62a2\u51fa\u3002\u8881\u7d2b\u8863\u4fef\u8eab\u6b63\u8981\u6276\u8d77\u80e1\u6590\uff0c\u89c1\u7a0b\u7075\u7d20\u7eb5\u5230\uff0c\u5f53\u5373\u7f29\u624b\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5feb\u6276\u4ed6\u5230\u65c1\u8fb9\uff01\u201d\u53f3\u624b\u4e91\u5e1a\u5728\u8eab\u540e\u4e00\u6325\uff0c\u4f3c\u662f\u6321\u67b6\u4ec0\u4e48\u6697\u5668\uff0c\u62a4\u5728\u80e1\u7a0b\u4e8c\u4eba\u8eab\u540e\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u534a\u6276\u534a\u62b1\u7684\u643a\u7740\u80e1\u6590\uff0c\u5feb\u6b65\u8d70\u56de\u5e2d\u4f4d\uff0c\u6cea\u773c\u76c8\u76c8\uff0c\u8bf4\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u600e\u6837\u4e86\uff1f\u201d\u80e1\u6590\u82e6\u7b11\u9053\uff1a\u201c\u80cc\u4e0a\u4e2d\u4e86\u6697\u5668\uff0c\u662f\u60ac\u67a2\u548c\u547d\u95e8\u3002\u201d\u7a0b\u7075\u7d20\u8fd9\u65f6\u4e5f\u987e\u4e0d\u5f97\u7537\u5973\u4e4b\u5acc\uff0c\u5fd9\u634b\u8d77\u4ed6\u957f\u888d\u548c\u91cc\u8863\uff0c\u89c1\u4ed6\u60ac\u67a2\u548c\u547d\u95e8\u4e24\u7a74\u4e0a\u679c\u7136\u5404\u6709\u4e00\u4e2a\u5c0f\u5b54\uff0c\u9c9c\u8840\u6e17\u51fa\uff0c\u6697\u5668\u5df2\u6df1\u5165\u808c\u9aa8\u3002 \u3000\u3000\u8881\u7d2b\u8863\u9053\uff1a\u201c\u90a3\u662f\u9540\u94f6\u7684\u94c1\u9488\uff0c\u6ca1\u6709\u6bd2\uff0c\u4f60\u653e\u5fc3\u3002\u201d\u4e3e\u8d77\u4e91\u5e1a\uff0c\u5148\u4ece\u5e1a\u4e1d\u4e1b\u4e2d\u62d4\u51fa\u4e00\u679a\u94f6\u9488\uff0c\u7136\u540e\u5c06\u4e91\u5e1a\u4e4b\u7aef\u62b5\u5728\u80e1\u6590\u60ac\u67a2\u7a74\u4e0a\uff0c\u8f7b\u8f7b\u5411\u5916\u4e00\u62c9\uff0c\u8d77\u4e86\u4e00\u679a\u94f6\u9488\u51fa\u6765\uff0c\u8ddf\u7740\u53c8\u8d77\u51fa\u4e86\u4ed6\u547d\u95e8\u7a74\u4e2d\u7684\u94f6\u9488\u3002\u539f\u6765\u4e91\u5e1a\u4e1d\u4e1b\u4e4b\u4e2d\u88c5\u7740\u4e00\u5757\u6781\u5927\u7684\u78c1\u94c1\u3002 \u3000\u3000\u80e1\u6590\u9053\uff1a\u201c\u8881\u59d1\u5a18\u2026\u2026\u4f60\u2026\u2026\u4f60\u2026\u2026\u201d\u8881\u7d2b\u8863\u4f4e\u58f0\u9053\uff1a\u201c\u6211\u4e00\u76f4\u7792\u7740\u4f60\uff0c\u662f\u6211\u4e0d\u597d\u3002\u201d\u987f\u4e86\u4e00\u987f\uff0c\u53c8\u9053\uff1a\u201c\u6211\u81ea\u5e7c\u51fa\u5bb6\uff0c\u6cd5\u540d\u53eb\u505a\u2018\u5706\u6027\u2019\u3002\u6211\u8bf4\u2018\u59d3\u8881\u2019\uff0c\u4e00\u5219\u662f\u6211\u5a18\u7684\u59d3\uff0c\u4e8c\u5219\u4fbf\u662f\u5c06\u2018\u5706\u6027\u2019\u4e24\u5b57\u98a0\u5012\u8fc7\u6765\u3002\u2018\u7d2b\u8863\u2019\uff0c\u90a3\u4fbf\u662f\u7f01\u8863\u8292\u978b\u7684\u2018\u7f01\u8863\u2019\uff01\u201d \u3000\u3000\u80e1\u6590\u6014\u6014\u7684\u671b\u7740\u5979\uff0c\u6b32\u5f85\u4e0d\u4fe1\u6b64\u4e8b\uff0c\u4f46\u773c\u524d\u7684\u8881\u7d2b\u8863\u660e\u660e\u662f\u4e2a\u5999\u5c3c\uff0c\u9694\u4e86\u534a\u664c\uff0c\u624d\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u4e3a\u4ec0\u4e48\u8981\u9a97\u6211\uff1f\u201d \u3000\u3000\u5706\u6027\u4f4e\u5782\u4e86\u5934\uff0c\u53cc\u773c\u77a7\u7740\u5730\u4e0b\uff0c\u8f7b\u8f7b\u5730\u9053\uff1a\u201c\u6211\u5949\u5e08\u7236\u4e4b\u547d\uff0c\u4ece\u56de\u7586\u5230\u4e2d\u539f\u6765\uff0c\u5355\u8eab\u4e00\u4eba\uff0c\u82e5\u4f5c\u50e7\u5c3c\u4e4b\u88c5\uff0c\u957f\u9014\u6295\u5bbf\u6253\u5c16\u751a\u662f\u4e0d\u4fbf\uff0c\u56e0\u6b64\u6539\u4f5c\u4fd7\u5bb6\u6253\u626e\u3002\u6211\u5934\u4e0a\u88c5\u7684\u662f\u5047\u53d1\uff0c\u996e\u98df\u4e0d\u6cbe\u8364\u8165\uff0c\u60f3\u662f\u4f60\u6ca1\u77a7\u51fa\u6765\u3002\u201d \u3000\u3000\u80e1\u6590\u4e0d\u77e5\u8bf4\u4ec0\u4e48\u597d\uff0c\u7ec8\u4e8e\u8f7b\u8f7b\u53f9\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u5b89\u63d0\u7763\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u8fd8\u6709\u54ea\u4e00\u4f4d\u6765\u8ddf\u4e94\u864e\u95e8\u51e4\u8001\u5e08\u6bd4\u8bd5\uff1f\u201d \u3000\u3000\u80e1\u6590\u8fd9\u65f6\u5fc3\u795e\u604d\u60da\uff0c\u9eef\u7136\u9b42\u9500\uff0c\u5bf9\u5b89\u63d0\u7763\u7684\u8bdd\u7adf\u662f\u542c\u800c\u4e0d\u95fb\u3002 \u3000\u3000\u5b89\u63d0\u7763\u8fde\u95ee\u4e86\u4e09\u904d\uff0c\u89c1\u65e0\u4eba\u4e0a\u524d\u8ddf\u51e4\u5929\u5357\u6311\u6218\uff0c\u5411\u798f\u5eb7\u5b89\u9053\uff1a\u201c\u56de\u5927\u5e05\uff1a\u8fd9\u4e03\u53ea\u7389\u9f99\u5fa1\u676f\uff0c\u4fbf\u8d4f\u7ed9\u8fd9\u4e03\u4f4d\u8001\u5e08\uff1f\u201d\u798f\u5eb7\u5b89\u9053\uff1a\u201c\u5f88\u597d\uff0c\u5f88\u597d\uff01\u201d \u3000\u3000\u5176\u65f6\u5929\u5df2\u9ece\u660e\uff0c\u7a97\u683c\u4e2d\u5c04\u8fdb\u6726\u80e7\u5fae\u5149\uff0c\u7ecf\u8fc7\u4e00\u591c\u5267\u4e89\uff0c\u4e03\u53ea\u7389\u9f99\u676f\u7684\u5f52\u5c5e\u624d\u7b97\u5b9a\u5c40\u3002\u5385\u4e0a\u7fa4\u8c6a\u7eb7\u7eb7\u8bae\u8bba\uff1a\u201c\u7ea2\u82b1\u4f1a\u62a2\u53bb\u7684\u90a3\u53ea\u7389\u9f99\u676f\uff0c\u4e0d\u77e5\u54ea\u4e00\u6d3e\u638c\u95e8\u6709\u672c\u4e8b\u593a\u5f97\u56de\u6765\uff1f\u201d\u201c\u563f\uff0c\u4efb\u4ed6\u672c\u9886\u518d\u5f3a\uff0c\u4e5f\u4e0d\u80fd\u8ddf\u7ea2\u82b1\u4f1a\u6597\u554a\u3002\u201d\u201c\u7ea2\u82b1\u4f1a\u9648\u603b\u8235\u4e3b\u6b66\u529f\u7edd\u9876\uff0c\u8fd8\u6709\u65e0\u5c18\u9053\u4eba\u3001\u8d75\u534a\u5c71\u3001\u6587\u6cf0\u6765\u3001\u5e38\u6c0f\u5144\u5f1f\uff0c\u54ea\u4e00\u4e2a\u4e0d\u662f\u54cd\u5f53\u5f53\u7684\u811a\u8272\uff1f\u8c01\u60f3\u53bb\u593a\u676f\uff0c\u90a3\u4e0d\u662f\u8001\u5bff\u661f\u4e0a\u540a\uff0c\u5acc\u547d\u957f\u4e48\uff1f\u201d \u3000\u3000\u53c8\u6709\u4eba\u77a7\u7740\u5706\u6027\u7a83\u7a83\u79c1\u8bae\uff1a\u201c\u600e\u4e48\u8fd9\u4e2a\u4fcf\u5c3c\u59d1\u7adf\u662f\u4e5d\u5bb6\u534a\u603b\u638c\u95e8\uff1f\u771f\u662f\u90aa\u95e8\u3002\u201d\u201c\u662f\u90a3\u4e5d\u5bb6\u534a\uff1f\u600e\u4e48\u8fd8\u6709\u534a\u4e2a\u638c\u95e8\u4eba\u7684\uff1f\u201d \u3000\u3000\u201c\u5979\u8981\u662f\u771f\u7684\u6b66\u529f\u9ad8\u5f3a\uff0c\u600e\u5730\u53c8\u4e0d\u53bb\u593a\u4e00\u53ea\u7389\u9f99\u676f\uff1f\u201d\u201c\u563f\uff0c\u4eba\u5bb6\u51e4\u8001\u5e08\u7684\u94f6\u9488\uff0c\u5979\u60f9\u5f97\u8d77\u4e48\uff1f\u4ed6\u624b\u4e2d\u94dc\u68cd\u7ed9\u780d\u6210\u4e86\u56db\u6bb5\uff0c\u8fd8\u80fd\u65bd\u653e\u94f6\u9488\uff0c\u8d25\u4e2d\u53d6\u80dc\uff0c\u4e86\u4e0d\u8d77\u3002\u201d\u53e6\u4e00\u4e2a\u4e0d\u670d\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u4e5f\u4e0d\u89c1\u5f97\uff01\u534e\u62f3\u95e8\u90a3\u9ec4\u80e1\u5b50\u542c\u5230\u4e5d\u5bb6\u534a\u603b\u638c\u95e8\u8fdb\u6765\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u8fd9\u624d\u7740\u4e86\u90a3\u59d3\u51e4\u7684\u9053\u513f\u3002\u5426\u5219\u7684\u8bdd\uff0c\u4e5f\u4e0d\u77e5\u8c01\u80dc\u8c01\u8d25\u3002\u201d \u3000\u3000\u53c8\u4e00\u4e2a\u9053\uff1a\u201c\u770b\u6765\u8fd8\u662f\u90a3\u7530\u5f52\u519c\u5dee\u52b2\uff0c\u4ed6\u5929\u9f99\u95e8\u7684\u9547\u95e8\u4e4b\u5b9d\u7ed9\u4eba\u7a7a\u624b\u593a\u4e86\u53bb\uff0c\u8fd9\u4f1a\u513f\u5c45\u7136\u539a\u7740\u8138\u76ae\uff0c\u53c8\u5c06\u5b9d\u5200\u6361\u4e86\u56de\u53bb\u3002\u201d \u3000\u3000\u53e6\u4e00\u4eba\u9053\uff1a\u201c\u4e0d\u9519\uff01\u534e\u62f3\u95e8\u5f53\u7136\u80dc\u8fc7\u4e86\u5929\u9f99\u95e8\u3002\u201d \u3000\u3000\u5b89\u63d0\u7763\u8d70\u5230\u957f\u51e0\u4e4b\u65c1\uff0c\u6367\u8d77\u4e86\u6258\u76d8\uff0c\u5f80\u4e2d\u95f4\u4e00\u7ad9\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u4e07\u5c81\u7237\u6069\u5178\uff0c\u94a6\u8d50\u7389\u9f99\u5fa1\u676f\uff0c\u7740\u5c11\u6797\u6d3e\u638c\u95e8\u4eba\u5927\u667a\u7985\u5e08\u3001\u6b66\u5f53\u6d3e\u638c\u95e8\u4eba\u65e0\u9752\u5b50\u9053\u4eba\u3001\u4e09\u624d\u5251\u638c\u95e8\u4eba\u6c64\u6c9b\u3001\u9ed1\u9f99\u95e8\u638c\u95e8\u4eba\u6d77\u5170\u5f3c\u3001\u5929\u9f99\u95e8\u638c\u95e8\u4eba\u7530\u5f52\u519c\u2026\u2026\u201d\u8bf4\u5230\u8fd9\u91cc\uff0c\u987f\u4e86\u4e00\u987f\uff0c\u4f4e\u58f0\u5411\u77f3\u5148\u751f\u9053\uff1a\u201c\u77f3\u8001\u5e08\uff0c\u8d35\u95e8\u6d3e\u548c\u5927\u540d\u600e\u4e48\u79f0\u547c\uff1f\u201d \u3000\u3000\u77f3\u5148\u751f\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u8349\u5b57\u4e07\u55d4\uff0c\u81f3\u4e8e\u95e8\u6d3e\u561b\uff0c\u5c31\u53eb\u4f5c\u836f\u738b\u95e8\u5427\u3002\u201d\u5b89\u63d0\u7763\u7eed\u9053\uff1a\u201c\u2026\u2026\u836f\u738b\u95e8\u638c\u95e8\u4eba\u77f3\u4e07\u55d4\uff0c\u4e94\u864e\u95e8\u638c\u95e8\u4eba\u51e4\u5929\u5357\u6536\u6267\u3002\u8c22\u6069\uff01\u201d \u3000\u3000\u542c\u5230\u201c\u8c22\u6069\u201d\u4e24\u5b57\uff0c\u798f\u5eb7\u5b89\u7b49\u5b98\u5458\u4e00\u9f50\u7ad9\u8d77\u3002\u6b66\u6797\u7fa4\u8c6a\u4e2d\u6709\u4e9b\u61c2\u793c\u6570\u7684\u4fbf\u7ad9\u4e86\u8d77\u6765\uff0c\u6709\u4e9b\u5374\u5750\u7740\u4e0d\u52a8\uff0c\u76f4\u5230\u4f17\u536b\u58eb\u559d\u9053\uff1a\u201c\u90fd\u7ad9\u8d77\u6765\uff01\u201d\u8fd9\u624d\u7eb7\u7eb7\u8d77\u7acb\u3002\u5927\u667a\u7985\u5e08\u548c\u65e0\u9752\u5b50\u5404\u4ee5\u50e7\u9053\u95e8\u4e2d\u89c4\u77e9\u884c\u793c\u3002\u6c64\u6c9b\u3001\u6d77\u5170\u5f3c\u7b49\u8dea\u4e0b\u78d5\u5934\u3002 \u3000\u3000\u5b89\u63d0\u7763\u5f85\u5404\u4eba\u8dea\u62dc\u5df2\u6bd5\uff0c\u7b11\u9053\uff1a\u201c\u606d\u559c\uff0c\u606d\u559c\uff01\u201d\u5c06\u6258\u76d8\u9012\u4e86\u8fc7\u53bb\u3002\u5927\u667a\u7985\u5e08\u7b49\u4e03\u4eba\u6bcf\u4eba\u4f38\u624b\u53d6\u4e86\u4e00\u53ea\u7389\u9f99\u676f\u3002 \u3000\u3000\u7a81\u7136\u4e4b\u95f4\uff0c\u4e03\u4e2a\u4eba\u624b\u4e0a\u72b9\u4f3c\u78b0\u5230\u4e86\u70e7\u5f97\u901a\u7ea2\u7684\u70d9\u94c1\uff0c\u5b9e\u5728\u62ff\u634f\u4e0d\u4f4f\uff0c\u4e00\u9f50\u677e\u624b\u3002\u4e52\u4e52\u4e53\u4e53\u4e00\u9635\u6e05\u8106\u7684\u54cd\u58f0\u8fc7\u53bb\uff0c\u4e03\u53ea\u7389\u676f\u540c\u65f6\u5728\u9752\u7816\u5730\u4e0a\u7838\u5f97\u7c89\u788e\u3002 \u3000\u3000\u8fd9\u4e00\u4e0b\u53d8\u6545\uff0c\u4e0d\u4f46\u4e03\u4eba\u5927\u60ca\u5931\u8272\uff0c\u81ea\u798f\u5eb7\u5b89\u4ee5\u4e0b\uff0c\u65e0\u4e0d\u7fa4\u60c5\u8038\u52a8\uff0c\u9f50\u95ee\uff1a\u201c\u600e\u6837\uff1f\u600e\u6837\uff1f\u201d\u9877\u523b\u4e4b\u95f4\uff0c\u4e03\u4eba\u63e1\u8fc7\u7389\u676f\u7684\u624b\u638c\u90fd\u662f\u53c8\u7126\u53c8\u80bf\uff0c\u7099\u75db\u96be\u5f53\uff0c\u4e0d\u4f4f\u7684\u5728\u8863\u670d\u4e0a\u62c2\u64e6\u3002\u6d77\u5170\u5f3c\u4f38\u6307\u5230\u53e3\u4e2d\u542e\u5438\u6b62\u75db\uff0c\u7a81\u7136\u95f4\u5927\u58f0\u602a\u53eb\uff0c\u539f\u6765\u820c\u5934\u4e0a\u4e5f\u5267\u75db\u8d77\u6765\u3002 \u3000\u3000\u80e1\u6590\u5411\u7a0b\u7075\u7d20\u671b\u4e86\u4e00\u773c\uff0c\u5fae\u5fae\u70b9\u5934\u3002\u4ed6\u6b64\u65f6\u65b9\u624d\u660e\u767d\uff0c\u539f\u6765\u7a0b\u7075\u7d20\u5728\u63b7\u6253\u67ef\u5b50\u5bb9\u7684\u7b2c\u4e8c\u679a\u548c\u7b2c\u4e09\u679a\u7206\u7af9\u4e4b\u4e2d\uff0c\u88c5\u4e0a\u4e86\u8d64\u874e\u7c89\u4e4b\u7c7b\u7684\u6bd2\u836f\uff0c\u7206\u7af9\u5728\u4e03\u53ea\u7389\u9f99\u676f\u4e0a\u7a7a\u70b8\u5f00\uff0c\u6bd2\u7c89\u4fbf\u6563\u5728\u676f\u4e0a\u3002\u8fd9\u4e00\u4e2a\u5e03\u7f6e\u610f\u8c0b\u6df1\u8fdc\uff0c\u4e1d\u6beb\u4e0d\u9732\u75d5\u8ff9\uff0c\u6b64\u523b\u624d\u89c1\u529f\u6548\u3002 \u3000\u3000\u53ea\u89c1\u7a0b\u7075\u7d20\u541e\u70df\u5410\u96fe\uff0c\u4e0d\u4f4f\u7684\u5438\u7740\u65f1\u70df\u7ba1\uff0c\u5438\u4e86\u4e00\u7b52\uff0c\u53c8\u88c5\u4e00\u7b52\uff0c\u534a\u70b9\u4e5f\u65e0\u5f97\u610f\u4e4b\u8272\u3002\u5979\u5de6\u638c\u4e2d\u6697\u85cf\u836f\u4e38\uff0c\u9012\u4e86\u4e24\u9897\u7ed9\u80e1\u6590\uff0c\u4e24\u9897\u7ed9\u5706\u6027\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u541e\u4e0b\uff01\u201d\u4e24\u4eba\u77e5\u5979\u5fc5\u6709\u6df1\u610f\uff0c\u4f9d\u8a00\u670d\u4e86\u3002 \u3000\u3000\u8fd9\u65f6\u4eba\u4eba\u7684\u76ee\u5149\u90fd\u77a7\u7740\u90a3\u4e03\u4eba\u548c\u5730\u4e0b\u7389\u676f\u7684\u788e\u7247\uff0c\u60ca\u6115\u4e4b\u4e0b\uff0c\u5927\u5385\u4e0a\u5bc2\u9759\u65e0\u58f0\u3002 \u3000\u3000\u5706\u6027\u5ffd\u5730\u8d70\u5230\u5385\u5fc3\uff0c\u4e91\u5e1a\u6307\u7740\u6c64\u6c9b\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u6c64\u6c9b\uff0c\u8fd9\u662f\u7687\u4e0a\u5fa1\u8d50\u7684\u7389\u676f\uff0c\u4f60\u5982\u6b64\u80c6\u5927\u5984\u4e3a\uff0c\u7adf\u6562\u6697\u65bd\u8be1\u8ba1\uff0c\u5c3d\u6570\u7838\u788e\u3002\u4f60\u5fc3\u5b58\u4e0d\u8f68\uff0c\u548c\u7ea2\u82b1\u4f1a\u6697\u4e2d\u52fe\u7ed3\uff0c\u8981\u62c6\u6563\u798f\u5927\u5e05\u7684\u5929\u4e0b\u638c\u95e8\u4eba\u5927\u4f1a\u3002\u4f60\u8fd9\u822c\u5927\u9006\u4e0d\u9053\uff0c\u76ee\u65e0\u957f\u4e0a\uff0c\u5929\u4e0b\u82f1\u96c4\u90fd\u5bb9\u4f60\u4e0d\u5f97\uff01\u201d \u3000\u3000\u5979\u4e00\u5b57\u4e00\u53e5\uff0c\u8bf4\u5f97\u6e05\u8106\u54cd\u6717\u3002\u8fd9\u756a\u8bdd\u8f9e\u610f\u4e25\u5cfb\uff0c\u5934\u5934\u662f\u9053\uff0c\u53c8\u8bf4\u4ed6\u8ddf\u7ea2\u82b1\u4f1a\u6697\u4e2d\u52fe\u7ed3\u3002\u4f17\u4eba\u6b63\u5728\u832b\u65e0\u5934\u7eea\u4e4b\u9645\uff0c\u5ffd\u542c\u5f97\u5979\u65a9\u9489\u622a\u94c1\u7684\u8bf4\u4e86\u51fa\u6765\uff0c\u771f\u6240\u8c13\u5148\u5165\u4e3a\u4e3b\uff0c\u65e0\u4e0d\u4ee5\u4e3a\u5b9e\u662f\u6c64\u6c9b\u6240\u4e3a\u3002 \u3000\u3000\u798f\u5eb7\u5b89\u5fc3\u4e2d\u6012\u6781\uff0c\u624b\u4e00\u6325\uff0c\u738b\u5251\u82f1\u3001\u5468\u94c1\u9e6a\u7b49\u9ad8\u624b\u536b\u58eb\u90fd\u56f4\u5230\u4e86\u6c64\u6c9b\u8eab\u65c1\u3002 \u3000\u3000\u9976\u662f\u6c64\u6c9b\u4e00\u751f\u7ecf\u5386\u8fc7\u4e0d\u5c11\u5927\u98ce\u5927\u6d6a\uff0c\u6b64\u523b\u4e5f\u662f\u8138\u8272\u60e8\u767d\uff0c\u65e2\u60ca\u4e14\u6012\uff0c\u8eab\u5b50\u53d1\u98a4\uff0c\u559d\u9053\uff1a\u201c\u5c0f\u5996\u5c3c\uff0c\u8fd9\u79cd\u4e8b\u4e5f\u80fd\u7a7a\u53e3\u767d\u8d56\u3001\u80e1\u8bf4\u516b\u9053\u4e48\uff1f\u201d \u3000\u3000\u5706\u6027\u51b7\u7b11\u9053\uff1a\u201c\u6211\u662f\u80e1\u8bf4\u516b\u9053\u4e4b\u4eba\u4e48\uff1f\u201d\u5979\u5411\u7740\u738b\u5251\u82f1\u9053\uff1a\u201c\u516b\u5366\u95e8\u7684\u638c\u95e8\u4eba\u738b\u8001\u5e08\u3002\u201d\u8f6c\u5934\u5411\u5468\u94c1\u9e6a\u9053\uff1a\u201c\u9e70\u722a\u96c1\u884c\u95e8\u7684\u638c\u95e8\u4eba\u5468\u8001\u5e08\uff0c\u4f60\u4eec\u90fd\u8ba4\u5f97\u6211\u662f\u8c01\u3002\u8fd9\u4e5d\u5bb6\u534a\u7684\u603b\u638c\u95e8\u6211\u662f\u4e0d\u5f53\u7684\u4e86\u3002\u53ef\u662f\u6211\u662f\u80e1\u8bf4\u516b\u9053\u4e4b\u4eba\u5462\uff0c\u8fd8\u662f\u6709\u62c5\u5f53\u3001\u6709\u8eab\u5206\u4e4b\u4eba\uff1f\u4f60\u4eec\u4e24\u4f4d\u4e14\u8bf4\u4e00\u53e5\u3002\u201d \u3000\u3000\u738b\u5251\u82f1\u548c\u5468\u94c1\u9e6a\u81ea\u5706\u6027\u4e00\u8fdb\u5927\u5385\uff0c\u5fc3\u4e2d\u4fbf\u60f4\u60f4\u4e0d\u5b89\uff0c\u6df1\u6050\u5979\u5c06\u593a\u5f97\u81ea\u5df1\u638c\u95e8\u4e4b\u4f4d\u7684\u771f\u60c5\u6296\u9732\u51fa\u6765\u3002\u4ed6\u4e8c\u4eba\u662f\u798f\u5eb7\u5b89\u8eab\u524d\u6700\u6709\u8138\u9762\u7684\u536b\u58eb\u9996\u9886\uff0c\u53c8\u662f\u5317\u4eac\u57ce\u4e2d\u6b66\u5e08\u7684\u9876\u513f\u5c16\u513f\u4eba\u7269\uff0c\u5018\u82e5\u4f17\u4eba\u77e5\u6089\u4ed6\u4e8c\u4eba\u8fde\u638c\u95e8\u4e4b\u4f4d\u4e5f\u8ba9\u4eba\u593a\u4e86\u53bb\uff0c\u4eca\u540e\u600e\u751f\u505a\u4eba\uff1f\u8fd9\u65f6\u542c\u5f97\u5706\u6027\u79f0\u547c\u81ea\u5df1\u4e3a\u672c\u95e8\u638c\u95e8\u4eba\uff0c\u53c8\u8bf4\uff1a\u201c\u8fd9\u4e5d\u5bb6\u534a\u7684\u603b\u638c\u95e8\u6211\u662f\u4e0d\u5f53\u7684\u4e86\u201d\u3002\u90a3\u663e\u662f\u70b9\u660e\u3002\u7ed9\u5979\u593a\u53bb\u7684\u638c\u95e8\u4e4b\u4f4d\u91cd\u884c\u5f52\u8fd8\u539f\u4e3b\uff0c\u5f53\u771f\u662f\u5982\u540c\u4e34\u5211\u7684\u65a9\u72af\u9047\u5230\u7687\u6069\u5927\u8d66\u4e00\u822c\uff0c\u5fc3\u4e2d\u5982\u4f55\u4e0d\u559c\uff1f\u5706\u6027\u8fd9\u4e48\u76f8\u8be2\uff0c\u53c8\u600e\u6562\u4e0d\u987a\u7740\u5979\u610f\u601d\u56de\u7b54\uff1f\u4f55\u51b5\u4ed6\u4e8c\u4eba\u542c\u4e86\u5979\u8fd9\u756a\u65a5\u8d23\u6c64\u6c9b\u7684\u8a00\u8bed\u4e4b\u540e\uff0c\u539f\u4e5f\u7591\u5fc3\u516b\u6210\u662f\u6c64\u6c9b\u6697\u4e2d\u6363\u9b3c\uff0c\u5426\u5219\u597d\u7aef\u7aef\u7684\u4e03\u53ea\u7389\u676f\uff0c\u600e\u4f1a\u9661\u7136\u95f4\u4e00\u9f50\u6454\u4e0b\u8dcc\u788e\u3002 \u3000\u3000\u738b\u5251\u82f1\u5f53\u5373\u606d\u606d\u656c\u656c\u5730\u8bf4\u9053\uff1a\u201c\u60a8\u8001\u4eba\u5bb6\u6b66\u827a\u8d85\u7fa4\uff0c\u5728\u4e0b\u751a\u662f\u656c\u670d\uff0c\u4e3a\u4eba\u53c8\u5bbd\u5b8f\u5927\u91cf\uff0c\u5b9e\u662f\u5f53\u4e16\u6b66\u6797\u4e2d\u7684\u6770\u51fa\u4eba\u6750\u3002\u201d \u3000\u3000\u5468\u94c1\u9e6a\u65e5\u524d\u7ed9\u5979\u6253\u8d25\uff0c\u5fc3\u4e0b\u867d\u7136\u5341\u5206\u8bb0\u6068\uff0c\u4f46\u5b9e\u5728\u6015\u5979\u5f53\u4f17\u6296\u9732\u4e11\u4e8b\uff0c\u4e5f\u9053\uff1a\u201c\u5728\u4e0b\u76f8\u4fe1\u60a8\u8001\u4eba\u5bb6\u8a00\u800c\u6709\u4fe1\uff0c\u987e\u5168\u5927\u4f53\uff0c\u5c0a\u91cd\u6b66\u6797\u540c\u9053\u7684\u989c\u9762\uff0c\u82e5\u975e\u4e07\u4e0d\u5f97\u5df2\uff0c\u51b3\u4e0d\u63ed\u9732\u6210\u540d\u4eba\u7269\u7684\u9690\u79c1\u3002\u201d\u4ed6\u8fd9\u51e0\u53e5\u8bdd\u5176\u5b9e\u8bf4\u7684\u90fd\u662f\u81ea\u5df1\u4e4b\u4e8b\uff0c\u6c42\u5979\u987e\u4f4f\u81ea\u5df1\u9762\u5b50\uff0c\u4f46\u5728\u65c1\u4eba\u542c\u6765\uff0c\u81ea\u7136\u90fd\u4ee5\u4e3a\u53e5\u53e5\u8bf4\u7684\u662f\u6c64\u6c9b\u3002 \u3000\u3000\u4f17\u4eba\u542c\u5f97\u798f\u5eb7\u5b89\u6700\u4eb2\u4fe1\u7684\u4e24\u4e2a\u536b\u58eb\u9996\u9886\u8fd9\u822c\u8bf4\uff0c\u4ed6\u4e8c\u4eba\u53c8\u90fd\u5bf9\u8fd9\u5c11\u5e74\u5c3c\u59d1\u8fd9\u822c\u606d\u8c28\uff0c\u53e3\u53e3\u58f0\u58f0\u7684\u201c\u60a8\u8001\u4eba\u5bb6\u201d\uff0c\u54ea\u91cc\u8fd8\u6709\u6000\u7591\uff1f \u3000\u3000\u798f\u5eb7\u5b89\u559d\u9053\uff1a\u201c\u62ff\u4e0b\u4e86\uff01\u201d\u738b\u5251\u82f1\u3001\u5468\u94c1\u9e6a\u548c\u6d77\u5170\u5f3c\u4e00\u9f50\u4f38\u624b\uff0c\u4fbf\u8981\u64d2\u62ff\u6c64\u6c9b\u3002 \u3000\u3000\u6c64\u6c9b\u4f7f\u62db\u201c\u5927\u5708\u624b\u201d\uff0c\u5185\u52b2\u541e\u5410\uff0c\u903c\u5f00\u4e86\u4e09\u4eba\uff0c\u53eb\u9053\uff1a\u201c\u4e14\u6162\uff01\u201d\u5411\u798f\u5eb7\u5b89\u9053\uff1a\u201c\u798f\u5927\u5e05\uff0c\u5c0f\u4eba\u8981\u548c\u5979\u5bf9\u8d28\u51e0\u53e5\uff0c\u82e5\u662f\u5979\u80fd\u8bf4\u5f97\u51fa\u771f\u51ed\u5b9e\u636e\uff0c\u5c0f\u4eba\u7518\u9886\u5927\u5e05\u7f6a\u8d23\uff0c\u6b7b\u800c\u65e0\u6028\u3002\u5426\u5219\u8fd9\u7b49\u8840\u53e3\u55b7\u4eba\uff0c\u5c0f\u4eba\u5b9e\u662f\u4e0d\u670d\u3002\u201d \u3000\u3000\u798f\u5eb7\u5b89\u7d20\u77e5\u6c64\u6c9b\u7684\u540d\u671b\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u4f60\u4fbf\u548c\u5979\u5bf9\u8d28\u3002\u201d \u3000\u3000\u6c64\u6c9b\u77aa\u89c6\u5706\u6027\uff0c\u6012\u9053\uff1a\u201c\u6211\u548c\u4f60\u7d20\u4e0d\u76f8\u8bc6\uff0c\u4f55\u6545\u8fd9\u7b49\u5984\u8d56\u4e8e\u6211\uff1f\u4f60\u7a76\u662f\u4f55\u4eba\uff1f\u201d \u3000\u3000\u5706\u6027\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u548c\u4f60\u7d20\u4e0d\u76f8\u8bc6\uff0c\u65e0\u6028\u65e0\u4ec7\uff0c\u4f55\u5fc5\u5e73\u767d\u7684\u51a4\u6789\u4f60\uff1f\u53ea\u662f\u6211\u8ddf\u7ea2\u82b1\u4f1a\u6709\u6df1\u4ec7\u5927\u6068\u3002\u4f60\u65e2\u52a0\u76df\u5165\u4e86\u7ea2\u82b1\u4f1a\uff0c\u6df7\u8fdb\u638c\u95e8\u4eba\u5927\u4f1a\u4e2d\u6765\u6363\u9b3c\uff0c\u6211\u4fbf\u975e\u63ed\u7a7f\u4f60\u7684\u9634\u8c0b\u8be1\u8ba1\u4e0d\u53ef\u3002\u4f60\u4ea4\u53cb\u5e7f\u9614\uff0c\u76f8\u8bc6\u904d\u5929\u4e0b\uff0c\u4ea4\u7ed3\u65c1\u7684\u670b\u53cb\uff0c\u4e5f\u4e0d\u5173\u6211\u4e8b\uff0c\u4f60\u4ea4\u7ed3\u7ea2\u82b1\u4f1a\u532a\u5f92\uff0c\u6211\u5374\u5bb9\u4f60\u4e0d\u5f97\u3002\u201d \u3000\u3000\u80e1\u6590\u5728\u4e00\u65c1\u542c\u7740\uff0c\u5fc3\u4e0b\u5b58\u7740\u8001\u5927\u7591\u56e2\uff0c\u4ed6\u660e\u77e5\u5706\u6027\u548c\u7ea2\u82b1\u4f1a\u4f17\u82f1\u96c4\u6e0a\u6e90\u751a\u6df1\uff0c\u8fd9\u7838\u788e\u7389\u676f\u4e4b\u4e8b\uff0c\u53c8\u660e\u660e\u662f\u7a0b\u7075\u7d20\u505a\u4e0b\u7684\u624b\u811a\uff0c\u5374\u4e0d\u77e5\u5979\u4f55\u4ee5\u8981\u8fd9\u822c\u8bec\u9677\u6c64\u6c9b\uff1f\u4ed6\u5fc3\u4e2d\u8f6c\u4e86\u51e0\u4e2a\u5ff5\u5934\uff0c\u731b\u7136\u60f3\u8d77\uff0c\u5706\u6027\u66fe\u8bf4\u5979\u6bcd\u4eb2\u88ab\u51e4\u5929\u5357\u903c\u8feb\u79bb\u5f00\u5e7f\u4e1c\u4e4b\u540e\uff0c\u66fe\u5f97\u6c64\u6c9b\u6536\u7559\uff0c\u96be\u9053\u5979\u6bcd\u4eb2\u4e4b\u6b7b\uff0c\u7adf\u548c\u6c64\u6c9b\u6709\u5173\uff1f \u3000\u3000\u4ed6\u81ea\u4ece\u84e6\u5730\u91cc\u89c1\u5230\u90a3\u5ff5\u5ff5\u4e0d\u5fd8\u7684\u4fca\u4fcf\u59d1\u5a18\u7adf\u662f\u4e00\u4e2a\u5c3c\u59d1\uff0c\u4fbf\u5373\u795e\u9b42\u4e0d\u5b9a\uff0c\u59cb\u7ec8\u65e0\u6cd5\u9759\u4e0b\u6765\u601d\u7d22\uff0c\u8111\u6d77\u4e2d\u8bf8\u822c\u5ff5\u5934\u6b64\u53bb\u5f7c\u6765\uff0c\u72b9\u4f3c\u4e71\u6f6e\u6012\u6d8c\uff0c\u8fde\u80cc\u4e0a\u7684\u4f24\u75db\u4e5f\u5fd8\u8bb0\u4e86\u3002 \u3000\u3000\u798f\u5eb7\u5b89\u5341\u5e74\u524d\u66fe\u4e3a\u7ea2\u82b1\u4f1a\u7fa4\u96c4\u6240\u64d2\uff0c\u5927\u53d7\u6298\u8fb1\uff0c\u5fc3\u4e2d\u6068\u6781\u4e86\u7ea2\u82b1\u4f1a\u4eba\u7269\uff0c\u8fd9\u4e00\u6b21\u62db\u96c6\u5404\u6d3e\u638c\u95e8\u4eba\u805a\u4f1a\uff0c\u4e3b\u65e8\u4e4b\u4e00\u4fbf\u662f\u4e3a\u4e86\u5bf9\u4ed8\u7ea2\u82b1\u4f1a\uff0c\u8fd9\u65f6\u542c\u4e86\u5706\u6027\u4e00\u756a\u8a00\u8bed\uff0c\u5fc3\u60f3\u8fd9\u59d3\u6c64\u7684\u7231\u4ea4\u6c5f\u6e56\u8c6a\u5ba2\uff0c\u7ea2\u82b1\u4f1a\u7684\u532a\u9996\u4e2a\u4e2a\u662f\u6b66\u6797\u4e2d\u7684\u5389\u5bb3\u811a\u8272\uff0c\u82e5\u662f\u8ddf\u4ed6\u79c1\u901a\u6b3e\u66f2\uff0c\u7ed3\u4ea4\u6765\u5f80\uff0c\u90a3\u662f\u534a\u70b9\u4e0d\u5947\uff0c\u82e5\u65e0\u4ea4\u5f80\uff0c\u53cd\u5012\u7a00\u5947\u4e86\u3002 \u3000\u3000\u53ea\u542c\u6c64\u6c9b\u8bf4\u9053\uff1a\u201c\u4f60\u8bf4\u6211\u7ed3\u4ea4\u7ea2\u82b1\u4f1a\u532a\u9996\uff0c\u662f\u8c01\u89c1\u6765\uff1f\u6709\u4f55\u51ed\u8bc1\uff1f\u201d \u3000\u3000\u5706\u6027\u5411\u5b89\u63d0\u7763\u9053\uff1a\u201c\u63d0\u7763\u5927\u4eba\uff0c\u8fd9\u5978\u4eba\u6c64\u6c9b\uff0c\u6709\u8ddf\u7ea2\u82b1\u4f1a\u532a\u9996\u6765\u5f80\u7684\u4e66\u4fe1\u3002\u4f60\u80fd\u8bbe\u6cd5\u67e5\u5bf9\u7b14\u8ff9\u771f\u5047\u4e48\uff1f\u201d\u5b89\u63d0\u7763\u9053\uff1a\u201c\u53ef\u4ee5\uff01\u201d\u8f6c\u5934\u5411\u8eab\u65c1\u7684\u6b66\u5b98\u5429\u5490\u4e86\u51e0\u53e5\u3002\u90a3\u6b66\u5b98\u8d70\u5411\u4e00\u65c1\u65b9\u684c\uff0c\u7ffb\u5f00\u5377\u5b97\uff0c\u53d6\u51fa\u51e0\u5c01\u4fe1\u6765\uff0c\u4e43\u662f\u6c64\u6c9b\u5199\u7ed9\u5b89\u63d0\u7763\u7684\u4e66\u4fe1\uff0c\u4fe1\u4e2d\u7b54\u5e94\u6765\u4eac\u8d74\u4f1a\uff0c\u5e76\u4f5c\u4f1a\u4e2d\u6bd4\u6b66\u516c\u8bc1\u3002 \u3000\u3000\u6c64\u6c9b\u6709\u6043\u65e0\u6050\uff0c\u6697\u5fd6\u81ea\u5df1\u7ed3\u4ea4\u867d\u5e7f\uff0c\u4f46\u884c\u4e8b\u5411\u6765\u8c28\u7ec6\uff0c\u5e76\u4e0d\u8bc6\u5f97\u7ea2\u82b1\u4f1a\u4eba\u7269\uff0c\u8fd9\u5c3c\u59d1\u4fbf\u662f\u634f\u9020\u4e66\u4fe1\uff0c\u7b14\u8ff9\u4e00\u5bf9\u4fbf\u77e5\u771f\u4f2a\uff0c\u5f53\u4e0b\u53ea\u662f\u5fae\u5fae\u51b7\u7b11\u3002 \u3000\u3000\u5706\u6027\u51b7\u51b7\u7684\u9053\uff1a\u201c\u7518\u9716\u60e0\u4e03\u7701\u6c64\u6c9b\u6c64\u5927\u4fa0\uff0c\u4f60\u5e3d\u5b50\u4e4b\u4e2d\uff0c\u85cf\u7684\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6c64\u6c9b\u4e00\u6115\uff0c\u8bf4\u9053\uff1a\u201c\u6709\u4ec0\u4e48\uff1f\u5e3d\u5b50\u4fbf\u662f\u5e3d\u5b50\u3002\u201d\u4ed6\u53d6\u4e0b\u5e3d\u5b50\uff0c\u91cc\u91cc\u5916\u5916\u4e00\u770b\uff0c\u7edd\u65e0\u5f02\u72b6\uff0c\u4e3a\u793a\u6e05\u767d\uff0c\u4fbf\u4ea4\u7ed9\u4e86\u6d77\u5170\u5f3c\u3002 \u3000\u3000\u6d77\u5170\u5f3c\u770b\u4e86\u770b\uff0c\u4ea4\u7ed9\u5b89\u63d0\u7763\u3002\u5b89\u63d0\u7763\u4e5f\u4ed4\u7ec6\u770b\u4e86\u770b\uff0c\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\u554a\u3002\u201d\u5706\u6027\u9053\uff1a\u201c\u8bf7\u63d0\u7763\u5927\u4eba\u5272\u5f00\u6765\u77a7\u77a7\u3002\u201d \u3000\u3000\u6ee1\u6d32\u98ce\u4fd7\uff0c\u9047\u6709\u76db\u5bb4\uff0c\u4f8b\u6709\u5927\u5757\u767d\u716e\u732a\u8089\uff0c\u5404\u4eba\u4ee5\u81ea\u5907\u89e3\u624b\u5200\u7247\u5272\u800c\u98df\uff0c\u56e0\u6b64\u5b89\u63d0\u7763\u8eab\u8fb9\u4ea6\u643a\u6709\u89e3\u624b\u5200\u3002\u4ed6\u542c\u5706\u6027\u8fd9\u822c\u8bf4\uff0c\u4fbf\u53d6\u51fa\u5200\u5b50\uff0c\u5272\u5f00\u6c64\u6c9b\u5c0f\u5e3d\u7684\u7ebf\u7f1d\uff0c\u53ea\u89c1\u5e3d\u5185\u6240\u886c\u68c9\u7d6e\u4e4b\u4e2d\uff0c\u679c\u7136\u85cf\u6709\u4e00\u4fe1\u3002\u5b89\u63d0\u7763\u201c\u54e6\u201d\u7684\u4e00\u58f0\uff0c\u62bd\u4e86\u51fa\u6765\u3002 \u3000\u3000\u6c64\u6c9b\u8138\u5982\u571f\u8272\uff0c\u9053\uff1a\u201c\u8fd9\u2026\u2026\u8fd9\u2026\u2026\u201d\u5fcd\u4e0d\u4f4f\u60f3\u8fc7\u53bb\u77a7\u77a7\uff0c\u53ea\u542c\u5237\u5237\u4e24\u58f0\uff0c\u738b\u5251\u82f1\u548c\u5468\u94c1\u9e6a\u62bd\u5200\u62e6\u4f4f\u3002 \u3000\u3000\u5b89\u63d0\u7763\u5c55\u5f00\u4fe1\u7b3a\uff0c\u6717\u58f0\u8bfb\u9053\uff1a\u201c\u4e0b\u8d70\u6c64\u6c9b\uff0c\u8c28\u62dc\u4e0a\u9648\u603b\u8235\u4e3b\u9ebe\u4e0b\uff1a\u6240\u5631\u4e4b\u4e8b\uff0c\u81ea\u5f53\u5c3d\u5fc3\u7aed\u529b\uff0c\u6b7b\u800c\u540e\u5df2\uff0c\u76d6\u975e\u6b64\u4e0d\u8db3\u4ee5\u62a5\u77e5\u9047\u4e4b\u5927\u6069\u4e5f\u3002\u552f\u5f7c\u4f27\u65e2\u5927\u4e3e\u96c6\u4f17\uff0c\u4f1a\u5929\u4e0b\u8bf8\u95e8\u6d3e\u638c\u95e8\u4eba\u4e8e\u4e00\u5802\uff0c\u81ea\u5fc5\u6212\u5907\u68ee\u4e25\u3002\u4e0b\u8d70\u82e5\u4e0d\u5e78\u6709\u8d1f\u6240\u6258\uff0c\u4fbf\u5f53\u8840\u6e85\u4eac\u534e\uff0c\u4ee5\u6b64\u4e66\u6b64\u5e3d\u62dc\u89c1\u660e\u516c\u8033\u3002\u4e0b\u8d70\u5728\u4eac\uff0c\u63a2\u5f97\u2026\u2026\u201d\u4ed6\u8bfb\u5230\u8fd9\u91cc\uff0c\u8138\u8272\u5fae\u53d8\uff0c\u4fbf\u4e0d\u518d\u8bfb\u4e0b\u53bb\uff0c\u5c06\u4e66\u4fe1\u5448\u7ed9\u4e86\u798f\u5eb7\u5b89\u3002 \u3000\u3000\u798f\u5eb7\u5b89\u63a5\u8fc7\u6765\u770b\u4e0b\u53bb\uff0c\u53ea\u89c1\u4fe1\u4e2d\u7eed\u9053\uff1a\u201c\u2026\u2026\u63a2\u5f97\u5f7c\u4f27\u8eab\u4e16\u9690\u4e8b\u751a\u5925\uff0c\u5982\u80fd\u76f8\u89c1\uff0c\u4e00\u4e00\u9762\u9648\u3002\u4e3e\u9996\u897f\u773a\uff0c\u60f3\u671b\u98ce\u91c7\u3002\u4f55\u65e5\u91cd\u56da\u5f7c\u914b\u4e8e\u516d\u548c\u5854\u9876\uff0c\u518d\u63b3\u5f7c\u4f27\u4e8e\u7d2b\u7981\u57ce\u4e2d\uff0c\u4e0d\u4ea6\u5feb\u54c9\uff01\u201d \u3000\u3000\u798f\u5eb7\u5b89\u6108\u8bfb\u6108\u6012\uff0c\u51e0\u6b32\u6c14\u7834\u80f8\u819b\u3002 \u3000\u3000\u539f\u6765\u5341\u5e74\u524d\u4e7e\u9686\u7687\u5e1d\u5728\u676d\u5dde\u5fae\u670d\u51fa\u6e38\uff0c\u66fe\u4e3a\u7ea2\u82b1\u4f1a\u7fa4\u96c4\u8bbe\u8ba1\u64d2\u83b7\uff0c\u56da\u4e8e\u516d\u548c\u5854\u9876\uff0c\u540e\u6765\u798f\u5eb7\u5b89\u53c8\u5728\u5317\u4eac\u7981\u57ce\u4e2d\u4e3a\u7ea2\u82b1\u4f1a\u6240\u4fd8\u3002\u8fd9\u4e24\u4ef6\u4e8b\u4e7e\u9686\u548c\u798f\u5eb7\u5b89\u90fd\u5f15\u4e3a\u6bd5\u751f\u5947\u803b\u5927\u8fb1\uff0c\u51e1\u662f\u5f53\u5e74\u9884\u95fb\u6b64\u4e8b\u7684\u5b98\u5458\u4f8d\u536b\uff0c\u90fd\u5df2\u88ab\u4e7e\u9686\u9010\u5e74\u6765\u501f\u6545\u8bdb\u622e\u706d\u53e3\u3002\u6b64\u4e24\u4e8b\u53c8\u56e0\u5173\u6d89\u5230\u7ea2\u82b1\u4f1a\u603b\u8235\u4e3b\u9648\u5bb6\u6d1b\u7684\u8eab\u4e16\u9690\u4e8b\uff0c\u662f\u4ee5\u7ea2\u82b1\u4f1a\u4ea6\u79d8\u800c\u4e0d\u5ba3\uff0c\u6c5f\u6e56\u4e0a\u77e5\u8005\u6781\u5c11\u3002\u4e8b\u9694\u5341\u5e74\uff0c\u798f\u5eb7\u5b89\u521b\u75db\u6e10\u6de1\u3002\u5c82\u77e5\u6c64\u6c9b\u7adf\u5728\u4fe1\u4e2d\u53c8\u63ed\u5f00\u4e86\u8fd9\u4e2a\u5927\u75ae\u75a4\u3002\u798f\u5eb7\u5b89\u53c8\u60f3\uff1a\u4fe1\u5185\u201c\u63a2\u5f97\u5f7c\u4f27\u8eab\u4e16\u9690\u4e8b\u751a\u5925\u201d\u4e91\u4e91\uff0c\u53c8\u4e0d\u77e5\u5305\u542b\u7740\u591a\u5c11\u4e11\u95fb\u9690\u79c1\uff1f\u798f\u5eb7\u5b89\u662f\u4e7e\u9686\u7684\u79c1\u751f\u5b50\uff0c\u5355\u662f\u8fd9\u4e00\u4ef6\u4e8b\uff0c\u80c6\u6562\u63d0\u5230\u4e00\u53e5\u7684\u4eba\u4fbf\u8db3\u4ee5\u706d\u95e8\u6740\u8eab\u3002 \u3000\u3000\u798f\u5eb7\u5b89\u867d\u7136\u5411\u6765\u9547\u9759\uff0c\u8fd9\u65f6\u4e5f\u5df2\u6c14\u5f97\u8138\u8272\u7126\u9ec4\uff0c\u53cc\u624b\u98a4\u6296\uff0c\u968f\u624b\u63a5\u8fc7\u5b89\u63d0\u7763\u9012\u4e0a\u6765\u6c64\u6c9b\u7684\u53e6\u4e00\u5c01\u4e66\u4fe1\uff0c\u4e00\u770b\u4e4b\u4e0b\uff0c\u4e24\u5c01\u4fe1\u4e0a\u7684\u5b57\u8ff9\u5374\u5e76\u4e0d\u751a\u4f3c\uff0c\u4f46\u76db\u6012\u4e4b\u9645\uff0c\u5df2\u65e0\u5fc3\u7eea\u53bb\u7ec6\u52a0\u6838\u5bf9\u3002 \u3000\u3000\u6c64\u6c9b\u89c1\u81ea\u5df1\u5c0f\u5e3d\u4e4b\u4e2d\u7adf\u4f1a\u85cf\u7740\u4e00\u5c01\u4e66\u4fe1\uff0c\u60ca\u60f6\u4e4b\u540e\u5fae\u4e00\u51dd\u601d\uff0c\u5df2\u662f\u604d\u7136\uff0c\u77e5\u662f\u5706\u6027\u6697\u4e2d\u505a\u4e0b\u7684\u624b\u811a\uff1b\u81ea\u662f\u5979\u5904\u5fc3\u79ef\u8651\uff0c\u4e70\u4e86\u4e00\u9876\u4e00\u6a21\u4e00\u6837\u7684\u5c0f\u5e3d\uff0c\u4f2a\u9020\u4e66\u4fe1\uff0c\u7f1d\u5728\u5e3d\u4e2d\uff0c\u7136\u540e\u5728\u81ea\u5df1\u7761\u89c9\u6216\u662f\u6d17\u6fa1\u4e4b\u9645\u6362\u4e86\u4e00\u9876\u3002 \u3000\u3000\u4ed6\u542c\u5b89\u63d0\u7763\u8bfb\u4fe1\u8bfb\u4e86\u4e00\u534a\uff0c\u4e0d\u7981\u6ee1\u80cc\u51b7\u6c57\uff0c\u5fc3\u60f3\u4eca\u65e5\u5927\u7978\u4e34\u5934\uff0c\u518d\u89c1\u4ed6\u7adf\u5c14\u4e0d\u6562\u518d\u8bfb\u4e66\u4fe1\u7684\u540e\u534a\uff0c\u5374\u5448\u7ed9\u4e86\u798f\u5eb7\u5b89\u4eb2\u9605\uff0c\u53ef\u60f3\u800c\u77e5\uff0c\u4fe1\u4e2d\u66f4\u662f\u5199\u6ee1\u4e86\u5927\u9006\u4e0d\u9053\u7684\u8a00\u8bed\u3002\u4ed6\u5fc3\u60f3\uff1a \u3000\u3000\u201c\u4eca\u65e5\u8981\u8fa9\u660e\u8fd9\u4e0d\u767d\u4e4b\u51a4\uff0c\u60df\u6709\u67e5\u660e\u8fd9\u5c0f\u5c3c\u59d1\u7684\u6765\u5386\u3002\u201d\u4fa7\u5934\u7ec6\u770b\u5706\u6027\uff0c\u84e6\u5730\u4e00\u60ca\uff1a\u201c\u8fd9\u5c3c\u59d1\u597d\u751f\u9762\u719f\uff0c\u4ece\u524d\u89c1\u8fc7\u7684\u3002\u201d\u9661\u7136\u60f3\u8d77\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u662f\u94f6\u59d1\uff0c\u94f6\u59d1\u7684\u5973\u513f\uff01\u201d\u5706\u6027\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u7ec8\u4e8e\u8ba4\u51fa\u6765\u4e86\u3002\u201d \u3000\u3000\u6c64\u6c9b\u5927\u53eb\uff1a\u201c\u798f\u5927\u5e05\uff0c\u8fd9\u5c3c\u59d1\u662f\u5c0f\u4eba\u7684\u4ec7\u5bb6\u3002\u5979\u8bbe\u4e0b\u5708\u5957\uff0c\u9677\u5bb3\u4e8e\u6211\u3002\u5927\u5e05\uff0c\u4f60\u5343\u4e07\u4fe1\u5979\u4e0d\u5f97\u3002\u201d \u3000\u3000\u5706\u6027\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u662f\u4f60\u7684\u4ec7\u5bb6\u3002\u6211\u6bcd\u4eb2\u8d70\u6295\u65e0\u8def\uff0c\u6765\u5230\u4f60\u5bb6\u3002\u4f60\u8fd9\u4eba\u9762\u517d\u5fc3\u7684\u6c64\u5927\u4fa0\uff0c\u89c1\u6211\u6bcd\u4eb2\u7f8e\u8c8c\uff0c\u7adf\u4f7f\u66b4\u529b\u4fb5\u72af\u4e8e\u5979\uff0c\u5bb3\u5f97\u6211\u6bcd\u4eb2\u60ac\u6881\u81ea\u5c3d\u3002\u8fd9\u4e8b\u53ef\u662f\u6709\u7684\uff1f\u201d \u3000\u3000\u6c64\u6c9b\u5fc3\u77e5\u82e5\u662f\u5728\u5929\u4e0b\u82f1\u96c4\u4e4b\u524d\u627f\u8ba4\u4e86\u8fd9\u4ef6\u4e11\u884c\uff0c\u81ea\u7136\u4ece\u6b64\u58f0\u540d\u626b\u5730\uff0c\u518d\u4e5f\u65e0\u989c\u89c1\u4eba\uff0c\u4f46\u6743\u8861\u8f7b\u91cd\uff0c\u5b81\u53ef\u76f4\u8ba4\u6b64\u4e8b\uff0c\u597d\u4ee4\u798f\u5eb7\u5b89\u76f8\u4fe1\u8fd9\u5c0f\u5c3c\u59d1\u662f\u631f\u4ec7\u8bec\u9677\uff0c\u4e8e\u662f\u70b9\u5934\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u786e\u6709\u6b64\u4e8b\u3002\u201d \u3000\u3000\u7fa4\u8c6a\u5bf9\u6c64\u6c9b\u672c\u6765\u751a\u662f\u656c\u91cd\uff0c\u90fd\u5f53\u4ed6\u662f\u4e2a\u6276\u5371\u89e3\u56f0\u3001\u6025\u4eba\u4e4b\u96be\u7684\u5927\u4fa0\uff0c\u867d\u542c\u4ed6\u548c\u7ea2\u82b1\u4f1a\u52fe\u7ed3\uff0c\u4f46\u7ea2\u82b1\u4f1a\u7fa4\u96c4\u58f0\u540d\u6781\u597d\uff0c\u6b66\u6797\u4e2d\u4f17\u6240\u4ef0\u6155\uff0c\u6c64\u6c9b\u5373\u4f7f\u5165\u4e86\u7ea2\u82b1\u4f1a\uff0c\u4e5f\u4e1d\u6beb\u65e0\u635f\u4e8e\u5176\u201c\u5927\u4fa0\u201d\u4e24\u5b57\u7684\u4ee4\u8a89\uff0c\u8fd9\u65f6\u5374\u542c\u5f97\u4ed6\u4eb2\u53e3\u76f4\u8ba4\u903c\u5978\u96be\u5973\uff0c\u5bb3\u4eba\u81ea\u5c3d\uff0c\u4e0d\u7531\u5f97\u5927\u54d7\u3002\u8bb8\u591a\u76f4\u6027\u5b50\u7684\u767b\u65f6\u4fbf\u5927\u58f0\u65a5\u8d23\uff0c\u6709\u7684\u9a82\u4ed6\u201c\u4f2a\u541b\u5b50\u201d\uff0c\u6709\u7684\u9a82\u4ed6\u201c\u8863\u51a0\u79bd\u517d\u201d\uff0c\u6709\u7684\u8bf4\u4ed6\u81ea\u5c45\u201c\u5927\u4fa0\u201d\uff0c\u5b9e\u662f\u4e0d\u8bc6\u7f9e\u803b\u3002 \u3000\u3000\u5706\u6027\u5f85\u4eba\u58f0\u7a0d\u9759\uff0c\u51b7\u51b7\u5730\u9053\uff1a\u201c\u6211\u4e00\u76f4\u60f3\u6740\u4e86\u4f60\u8fd9\u79bd\u517d\uff0c\u66ff\u4ea1\u6bcd\u62a5\u4ec7\uff0c\u53ef\u662f\u4f60\u6b66\u529f\u592a\u5f3a\uff0c\u6211\u6597\u4f60\u4e0d\u8fc7\uff0c\u53ea\u6709\u65e5\u591c\u5728\u4f60\u5c4b\u9876\u7a97\u4e0b\u7aa5\u4f3a\u3002\u563f\u563f\uff0c\u5929\u5047\u5176\u4fbf\uff0c\u7ed9\u6211\u542c\u5230\u4f60\u8ddf\u7ea2\u82b1\u4f1a\u8d75\u534a\u5c71\u3001\u5e38\u6c0f\u5144\u5f1f\u3001\u77f3\u53cc\u82f1\u8fd9\u4e9b\u532a\u9996\u9634\u8c0b\u79c1\u8bae\u3002\u9002\u624d\u62a2\u593a\u7389\u9f99\u676f\u7684\u90a3\u4e2a\u5c11\u5e74\u4e66\u751f\uff0c\u4fbf\u662f\u7ea2\u82b1\u4f1a\u603b\u8235\u4e3b\u9648\u5bb6\u6d1b\u7684\u4e66\u50ee\u5fc3\u781a\uff0c\u662f\u4e5f\u4e0d\u662f\uff1f\u201d\u4f17\u4eba\u4e00\u542c\uff0c\u53c8\u662f\u4e00\u9635\u5608\u4e71\u3002 \u3000\u3000\u798f\u5eb7\u5b89\u4e5f\u5373\u60f3\u8d77\uff1a\u201c\u6b64\u4eba\u6b63\u662f\u5fc3\u781a\u3002\u4ed6\u597d\u5927\u7684\u80c6\u5b50\uff0c\u7adf\u4e0d\u6015\u6211\u8ba4\u4ed6\u51fa\u6765\uff01\u201d \u3000\u3000\u6c64\u6c9b\u9053\uff1a\u201c\u6211\u600e\u8ba4\u5f97\u4ed6\uff1f\u5018\u82e5\u6211\u8ddf\u7ea2\u82b1\u4f1a\u52fe\u7ed3\uff0c\u4f55\u4ee5\u53c8\u51fa\u624b\u64d2\u4f4f\u4ed6\uff1f\u201d \u3000\u3000\u5706\u6027\u563f\u563f\u51b7\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u624b\u811a\u505a\u5f97\u5982\u6b64\u5e72\u51c0\u5229\u843d\uff0c\u8981\u662f\u6211\u4e8b\u5148\u6ca1\u542c\u5230\u4f60\u4eec\u6697\u4e2d\u7684\u5bc6\u8bae\uff0c\u4e5f\u51b3\u8ba1\u60f3\u4e0d\u5230\u8fd9\u9634\u8c0b\u3002\u6211\u95ee\u4f60\uff0c\u4f60\u6c64\u5927\u4fa0\u7684\u70b9\u7a74\u624b\u6cd5\u53e6\u5177\u4e00\u529f\uff0c\u4f60\u4e0b\u624b\u70b9\u4e86\u4eba\u5bb6\u7a74\u9053\u4e4b\u540e\uff0c\u672c\u6765\u65c1\u4eba\u518d\u4e5f\u65e0\u6cd5\u89e3\u5f97\u5f00\u3002\u53ef\u662f\u9002\u624d\u4f60\u70b9\u4e86\u90a3\u7ea2\u82b1\u4f1a\u532a\u5f92\u7684\u7a74\u9053\uff0c\u4f55\u4ee5\u5927\u5385\u4e0a\u706f\u706b\u9f50\u7184\uff1f\u90a3\u532a\u5f92\u8eab\u4e0a\u7684\u7a74\u9053\u53c8\u4f55\u4ee5\u5ffd\u7136\u89e3\u4e86\uff0c\u5f97\u4ee5\u9003\u53bb\uff1f\u201d\u6c64\u6c9b\u5f20\u53e3\u7ed3\u820c\uff0c\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\u8fd9\u4e2a\u2026\u2026\u60f3\u662f\u6697\u4e2d\u6709\u4eba\u89e3\u6551\u3002\u201d \u3000\u3000\u5706\u6027\u5389\u58f0\u9053\uff1a\u201c\u6697\u4e2d\u89e3\u6551\u4e4b\u4eba\uff0c\u9664\u4e86\u6c64\u6c9b\u6c64\u5927\u4fa0\uff0c\u5929\u4e0b\u518d\u65e0\u7b2c\u4e8c\u4e2a\u3002\u5f53\u65f6\u9664\u4f60\u4e4b\u5916\uff0c\u8fd8\u6709\u8c01\u7ad9\u5728\u90a3\u4eba\u7684\u8eab\u8fb9\uff1f\u201d \u3000\u3000\u80e1\u6590\u5fc3\u60f3\uff1a\u201c\u5979\u8a00\u8f9e\u950b\u5229\uff0c\u6c64\u6c9b\u5b9e\u662f\u767e\u53e3\u96be\u8fa9\u3002\u90a3\u5c11\u5e74\u4e66\u751f\u7684\u7a74\u9053\uff0c\u660e\u660e\u662f\u6211\u89e3\u7684\u3002\u4f46\u6211\u53ea\u89e3\u4e86\u4e00\u534a\uff0c\u53e6\u4e00\u534a\u4e0d\u77e5\u662f\u4f55\u4eba\u6240\u89e3\uff0c\u4f46\u60f3\u6765\u51b3\u4e0d\u4f1a\u662f\u6c64\u6c9b\u3002\u201d \u3000\u3000\u53ea\u542c\u5f97\u5706\u6027\u53c8\u9053\uff1a\u201c\u798f\u5927\u5e05\uff0c\u8fd9\u6c64\u6c9b\u548c\u7ea2\u82b1\u4f1a\u532a\u5f92\u8ba1\u8bae\u5b9a\u5f53\uff0c\u5047\u88c5\u5c06\u90a3\u532a\u5f92\u5fc3\u781a\u64d2\u83b7\uff0c\u653e\u5728\u4f60\u8eab\u65c1\uff0c\u518d\u7531\u53e6\u4e00\u6279\u532a\u5f92\u6253\u706d\u70db\u706b\uff0c\u90a3\u5fc3\u781a\u4fbf\u4e58\u4e71\u5c31\u8fd1\u5411\u4f60\u884c\u523a\u3002\u8fd9\u6279\u532a\u5f92\u610f\u6599\u4e4b\u4e2d\uff0c\u4f17\u536b\u58eb\u89c1\u90a3\u4e66\u751f\u5df2\u88ab\u70b9\u4e86\u7a74\u9053\uff0c\u52a8\u5f39\u4e0d\u5f97\uff0c\u81ea\u4e0d\u4f1a\u9632\u4ed6\u884c\u523a\u3002 \u3000\u3000\u5929\u5e78\u798f\u5927\u5e05\u6d2a\u798f\u9f50\u5929\uff0c\u9022\u51f6\u5316\u5409\u3002\u4f17\u536b\u58eb\u53c8\u5fe0\u5fc3\u803f\u803f\uff0c\u9632\u536b\u5468\u5bc6\uff0c\u70db\u706b\u706d\u7184\u4e4b\u540e\uff0c\u7acb\u5373\u4e00\u9f50\u6321\u5728\u5927\u5e05\u8eab\u524d\u4fdd\u62a4\uff0c\u8d3c\u4eba\u7684\u5978\u8ba1\u624d\u4e0d\u5f97\u901e\u3002\u201d\u6c64\u6c9b\u5927\u53eb\uff1a\u201c\u4f60\u80e1\u8bf4\u516b\u9053\uff0c\u54ea\u6709\u6b64\u4e8b\uff1f\u201d \u3000\u3000\u798f\u5eb7\u5b89\u56de\u60f3\u9002\u624d\u7684\u60c5\u666f\uff0c\u5bf9\u5706\u6027\u4e4b\u8a00\u4e0d\u7531\u5f97\u4fe1\u4e86\u4e2a\u5341\u8db3\u5341\uff0c\u6697\u53eb\uff1a\u201c\u597d\u9669\uff01\u201d\u5411\u738b\u5251\u82f1\u548c\u5468\u94c1\u9e6a\u9053\uff1a\u201c\u4f60\u4eec\u5f88\u597d\uff0c\u56de\u5934\u5347\u4f60\u4eec\u7684\u5b98\u3002\u201d \u3000\u3000\u5706\u6027\u4e58\u673a\u53c8\u9053\uff1a\u201c\u738b\u5927\u4eba\uff0c\u5468\u5927\u4eba\uff0c\u9002\u624d\u8d3c\u4eba\u7684\u5978\u8ba1\u662f\u5426\u5982\u6b64\uff1f\u201d\u738b\u5251\u82f1\u548c\u5468\u94c1\u9e6a\u5747\u60f3\uff1a\u201c\u8fd9\u5c0f\u5c3c\u59d1\u662f\u5f97\u7f6a\u4e0d\u5f97\u7684\u3002\u4f55\u51b5\u6211\u4eec\u8d8a\u662f\u8bf4\u5f97\u51f6\u9669\uff0c\u4fdd\u62a4\u5927\u5e05\u4e4b\u529f\u8d8a\u9ad8\uff0c\u56de\u5934\u5c01\u8d4f\u8d8a\u5927\u3002\u201d \u3000\u3000\u4e8e\u662f\u4e00\u4e2a\u8bf4\uff1a\u201c\u90a3\u4e66\u751f\u786e\u662f\u66fe\u6251\u5230\u5927\u5e05\u8eab\u524d\u6765\uff0c\u5e78\u597d\u672a\u80fd\u6210\u529f\u3002\u201d\u53e6\u4e00\u4e2a\u8bf4\uff1a\u201c\u9ed1\u6697\u4e4b\u4e2d\uff0c\u7684\u786e\u6709\u4eba\u8fc7\u6765\uff0c\u529f\u592b\u5389\u5bb3\u5f97\u5f88\uff0c\u6211\u4eec\u53ea\u597d\u62da\u4e86\u547d\u62b5\u6321\u2026\u2026\u5374\u6ca1\u60f3\u5230\u7adf\u662f\u6c64\u6c9b\uff0c\u5f53\u771f\u51f6\u9669\u5f97\u7d27\u3002\u201d \u3000\u3000\u6c64\u6c9b\u96be\u4ee5\u8fa9\u89e3\uff0c\u53ea\u5f97\u5bf9\u5706\u6027\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u6ee1\u53e3\u80e1\u8a00\uff01\u9002\u624d\u4f60\u53c8\u4e0d\u5728\u5385\u4e0a\uff0c\u5982\u4f55\u5f97\u77e5\uff1f\u201d\u5706\u6027\u5e76\u4e0d\u56de\u7b54\uff0c\u56de\u5934\u5411\u7740\u51e4\u5929\u5357\u4e0a\u4e0a\u4e0b\u4e0b\u7684\u6253\u91cf\u3002 \u3000\u3000\u51e4\u5929\u5357\u662f\u5979\u4eb2\u751f\u4e4b\u7236\uff0c\u53ef\u662f\u66fe\u903c\u5f97\u5979\u6bcd\u4eb2\u98a0\u6c9b\u6d41\u79bb\uff0c\u53d7\u5c3d\u4e86\u82e6\u695a\uff0c\u6700\u540e\u4e0d\u5f97\u5584\u7ec8\u3002\u5979\u66fe\u53d1\u4e0b\u8a93\u613f\uff0c\u8981\u6551\u4ed6\u4e09\u6b21\uff0c\u4ee5\u5c3d\u7236\u5973\u4e4b\u60c5\uff0c\u7136\u540e\u518d\u53d6\u4ed6\u6027\u547d\uff0c\u66ff\u82e6\u547d\u7684\u4ea1\u6bcd\u62a5\u4ec7\u3002\u5979\u65e2\u8bec\u9677\u4e86\u6c64\u6c9b\uff0c\u539f\u53ef\u518d\u5c06\u51e4\u5929\u5357\u6273\u9677\u5728\u5185\uff0c\u4f46\u5411\u4ed6\u77a7\u4e86\u4e24\u773c\uff0c\u5fc3\u4e2d\u7ec8\u662f\u4e0d\u5fcd\uff0c\u4e00\u65f6\u62ff\u4e0d\u5b9a\u4e3b\u610f\u3002 \u3000\u3000\u5706\u6027\u8fd9\u4e48\u4e00\u72b9\u8c6b\uff0c\u6c64\u6c9b\u8001\u5978\u5de8\u733e\uff0c\u767b\u65f6\u77a7\u51fa\u5979\u8138\u8272\u8fdf\u7591\u4e0d\u5b9a\uff0c\u53c8\u89c1\u5979\u773c\u5149\u4e0d\u4f4f\u7684\u6e9c\u5411\u51e4\u5929\u5357\uff0c\u5fc3\u5ff5\u4e00\u52a8\uff0c\u4e24\u4e0b\u91cc\u4e00\u51d1\u5408\uff0c\u767b\u5373\u6599\u5b9a\u8fd9\u4e8b\u5168\u662f\u51e4\u5929\u5357\u6697\u4e2d\u5e03\u4e0b\u7684\u8ba1\u8c0b\uff0c\u53eb\u9053\uff1a\u201c\u51e4\u5929\u5357\uff0c\u539f\u6765\u662f\u4f60\u4ece\u4e2d\u6363\u9b3c\uff01\u4f60\u8981\u6211\u6697\u4e2d\u52a9\u4f60\uff0c\u4ee4\u4f60\u4e94\u864e\u95e8\u5728\u638c\u95e8\u4eba\u5927\u4f1a\u4e2d\u538b\u5012\u7fa4\u96c4\uff0c\u8fd9\u65f6\u5374\u53c8\u53eb\u4f60\u5973\u513f\u6765\u9677\u5bb3\u4e8e\u6211\u3002\u201d\u51e4\u5929\u5357\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u6211\u5973\u513f\uff1f\u5979\u2026\u2026\u5979\u662f\u6211\u5973\u513f\uff1f\u201d\u7fa4\u8c6a\u542c\u4e86\u4e24\u4eba\u4e4b\u8a00\uff0c\u65e0\u4e0d\u60ca\u5947\u3002 \u3000\u3000\u6c64\u6c9b\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u8fd8\u5728\u8fd9\u91cc\u5047\u75f4\u5047\u5446\uff0c\u88c5\u4f5c\u4e0d\u77e5\u3002\u4f60\u77a7\u77a7\u8fd9\u5c0f\u5c3c\u59d1\uff0c\u8ddf\u5f53\u5e74\u7684\u94f6\u59d1\u6709\u4ec0\u4e48\u5206\u522b\uff1f\u201d \u3000\u3000\u51e4\u5929\u5357\u53cc\u773c\u77aa\u7740\u5706\u6027\uff0c\u6014\u6014\u7684\u8bf4\u4e0d\u51fa\u8bdd\u6765\uff0c\u4f46\u89c1\u5979\u867d\u4f5c\u5c3c\u59d1\u88c5\u675f\uff0c\u4f46\u79c0\u7709\u7f8e\u76ee\uff0c\u5b9b\u7136\u4fbf\u662f\u6614\u65e5\u7684\u6e14\u5bb6\u5973\u94f6\u59d1\u3002 \u3000\u3000\u539f\u6765\u5f53\u5e74\u94f6\u59d1\u5e26\u4e86\u5973\u513f\u4ece\u5e7f\u4e1c\u4f5b\u5c71\u9003\u5230\u6e56\u5317\uff0c\u6295\u8eab\u6c64\u6c9b\u5e9c\u4e2d\u4e3a\u4f63\u3002\u6c64\u6c9b\u8fd9\u4eba\u5916\u8868\u9053\u8c8c\u5cb8\u7136\uff0c\u4e00\u526f\u4ec1\u4eba\u4e49\u58eb\u7684\u6a21\u6837\uff0c\u5b9e\u5219\u884c\u6b62\u751a\u662f\u4e0d\u7aef\uff0c\u89c1\u94f6\u59d1\u7f8e\u8c8c\uff0c\u4fbf\u5f3a\u903c\u5979\u76f8\u4ece\u3002\u94f6\u59d1\u7f9e\u6124\u4e4b\u4e0b\uff0c\u60ac\u6881\u800c\u6b7b\u3002 \u3000\u3000\u5706\u6027\u5374\u8499\u5ce8\u7709\u6d3e\u4e2d\u4e00\u4f4d\u8f88\u4efd\u6781\u9ad8\u7684\u5c3c\u59d1\u6551\u53bb\uff0c\u5e26\u5230\u5929\u5c71\uff0c\u81ea\u5e7c\u4fbf\u7ed9\u5979\u843d\u53d1\uff0c\u6388\u4ee5\u6b66\u827a\u3002\u90a3\u4f4d\u5c3c\u59d1\u7684\u4f4f\u5904\u548c\u5929\u6c60\u602a\u4fa0\u8881\u58eb\u9704\u53ca\u7ea2\u82b1\u4f1a\u7fa4\u96c4\u4e0d\u8fdc\uff0c\u5e73\u65e5\u5207\u78cb\u6b66\u5b66\uff0c\u65f6\u76f8\u8fc7\u4ece\u3002\u5706\u6027\u5929\u8d44\u6781\u4f73\uff0c\u5979\u5e08\u7236\u7684\u6b66\u529f\u539f\u5df2\u6781\u4e3a\u9ad8\u6df1\u7e41\u590d\uff0c\u4f46\u5979\u8d2a\u591a\u4e0d\u538c\uff0c\u6bcf\u6b21\u89c1\u5230\u8881\u58eb\u9704\uff0c\u603b\u662f\u7f20\u7740\u4ed6\u8981\u4f20\u6388\u51e0\u62db\uff0c\u800c\u4ece\u9648\u5bb6\u6d1b\u3001\u970d\u9752\u6850\u76f4\u81f3\u5fc3\u781a\uff0c\u7ea2\u82b1\u4f1a\u7fa4\u96c4\u65e0\u4eba\u4e0d\u662f\u591a\u591a\u5c11\u5c11\u7684\u4f20\u8fc7\u5979\u4e00\u4e9b\u529f\u592b\u3002\u5929\u6c60\u602a\u4fa0\u8881\u58eb\u9704\u8001\u6765\u5bc2\u5bde\uff0c\u5bf9\u5979\u4f20\u6388\u5c24\u591a\u3002\u8881\u58eb\u9704\u4e8e\u5929\u4e0b\u6b66\u5b66\uff0c\u51e0\u4e4e\u8bf4\u5f97\u4e0a\u65e0\u6240\u4e0d\u77e5\uff0c\u4f55\u51b5\u518d\u52a0\u4e0a\u5341\u51e0\u4f4d\u660e\u5e08\uff0c\u662f\u4ee5\u5706\u6027\u827a\u517c\u5404\u6d3e\u4e4b\u6240\u957f\uff0c\u5979\u4eba\u53c8\u806a\u660e\u673a\u8b66\uff0c\u4ee5\u667a\u5de7\u8865\u529f\u529b\u4e0d\u8db3\uff0c\u82e5\u4e0d\u662f\u5e74\u7eaa\u592a\u8f7b\uff0c\u5185\u529f\u4fee\u4e3a\u5c1a\u6d45\uff0c\u76f4\u5df2\u53ef\u8dfb\u4e00\u6d41\u9ad8\u624b\u4e4b\u5883\u3002 \u3000\u3000\u8fd9\u4e00\u5e74\u5706\u6027\u7980\u660e\u5e08\u7236\uff0c\u56de\u4e2d\u571f\u4e3a\u6bcd\u62a5\u4ec7\uff0c\u9e33\u9e2f\u5200\u9a86\u51b0\u4fbf\u6258\u5979\u5e26\u6765\u767d\u9a6c\uff0c\u9047\u5230\u80e1\u6590\u65f6\u8d60\u9001\u4e8e\u4ed6\u3002\u53ea\u662f\u8d75\u534a\u5c71\u5c06\u80e1\u6590\u5938\u5f97\u592a\u597d\uff0c\u5706\u6027\u5c11\u5e74\u6027\u60c5\uff0c\u5fc3\u4e0b\u4e0d\u670d\uff0c\u8fd9\u624d\u6709\u9014\u4e2d\u548c\u80e1\u6590\u6570\u5ea6\u8f83\u91cf\u4e4b\u4e8b\u3002\u4e0d\u6599\u4e24\u4eba\u89c1\u9762\u540e\u60fa\u60fa\u76f8\u60dc\uff0c\u5fc3\u4e2d\u60c5\u82d7\u6697\u8301\u3002\u5706\u6027\u5f85\u5f97\u60ca\u89c9\uff0c\u5df2\u662f\u67d4\u80a0\u767e\u8f6c\uff0c\u96be\u4ee5\u81ea\u9063\u4e86\u3002\u5979\u81ea\u884c\u7ea6\u5236\uff0c\u4e0d\u6562\u591a\u548c\u80e1\u6590\u89c1\u9762\uff0c\u53ea\u662f\u6697\u4e2d\u8ddf\u968f\u3002\u540e\u6765\u89c1\u4ed6\u7ed3\u8bc6\u4e86\u7a0b\u7075\u7d20\uff0c\u5979\u65e2\u611f\u81ea\u4f24\uff0c\u4ea6\u590d\u81ea\u6170\uff0c\u81ea\u5df1\u662f\u65b9\u5916\u4e4b\u4eba\uff0c\u7ec8\u8eab\u6ce8\u5b9a\u4ee5\u9752\u706f\u53e4\u4f5b\u4e3a\u4f34\uff0c\u5f53\u5e74\u62dc\u5e08\u4e4b\u65f6\uff0c\u66fe\u7acb\u4e0b\u91cd\u8a93\uff0c\u4e3a\u5e08\u7236\u7684\u8863\u94b5\u4f20\u4eba\uff0c\u5e08\u6069\u6df1\u91cd\uff0c\u51b3\u8ba1\u4e0d\u6562\u6709\u80cc\u3002\u7a0b\u7075\u7d20\u806a\u660e\u667a\u6167\uff0c\u72b9\u80dc\u4e8e\u5df1\uff0c\u5bf9\u80e1\u6590\u66f4\u662f\u4e00\u5f80\u60c5\u6df1\uff0c\u80e1\u6590\u5f97\u4ee5\u4e3a\u4fa3\uff0c\u539f\u4ea6\u5927\u4f73\u3002\u56e0\u6b64\u4e0a\u7559\u8d60\u7389\u51e4\uff0c\u5fae\u901a\u6d88\u606f\uff0c\u4f46\u6697\u5730\u91cc\u5374\u5df2\u4e0d\u77e5\u5077\u5f39\u4e86\u591a\u5c11\u73e0\u6cea\u3002 \u3000\u3000\u5979\u6b64\u756a\u4e1c\u6765\u62a5\u4ec7\uff0c\u5927\u4ec7\u4eba\u662f\u7518\u9716\u60e0\u4e03\u7701\u6c64\u6c9b\uff0c\u5fc3\u60f3\u82e5\u662f\u6697\u4e2d\u884c\u523a\u4e0b\u6bd2\uff0c\u539f\u4ea6\u4e0d\u96be\uff0c\u4f46\u6b64\u4eba\u4e00\u751f\u5047\u4ec1\u5047\u4e49\uff0c\u6cbd\u540d\u9493\u8a89\uff0c\u987b\u5f97\u5728\u5929\u4e0b\u597d\u6c49\u4e4b\u524d\u63ed\u7834\u4ed6\u7684\u5047\u9762\u5177\uff0c\u90a3\u6bd4\u5c06\u4ed6\u4e00\u5251\u7a7f\u5fc3\u66f4\u662f\u75db\u5feb\u3002 \u3000\u3000\u9002\u9022\u798f\u5eb7\u5b89\u6b63\u8981\u53ec\u5f00\u5929\u4e0b\u638c\u95e8\u4eba\u5927\u4f1a\uff0c\u5206\u9063\u4eba\u624b\u524d\u5f80\u5404\u5730\uff0c\u9080\u8bf7\u5404\u5bb6\u5404\u6d3e\u7684\u638c\u95e8\u4eba\u8d74\u4eac\u4e0e\u4f1a\u3002\u5706\u6027\u67e5\u77e5\u798f\u5eb7\u5b89\u6b64\u4e3e\u7684\u7528\u610f\uff0c\u4e00\u6765\u662f\u6536\u7f57\u6c5f\u6e56\u8c6a\u6770\uff0c\u4ee5\u529f\u540d\u8d22\u5e1b\u76f8\u7f81\u7e3b\uff0c\u7528\u4ee5\u5bf9\u4ed8\u7ea2\u82b1\u4f1a\u7fa4\u96c4\uff1b\u4e8c\u6765\u662f\u6311\u62e8\u79bb\u95f4\uff0c\u4f7f\u5404\u6d3e\u6b66\u5e08\u76f8\u4e92\u4e89\u6597\uff0c\u4e0d\u81f4\u5171\u540c\u53cd\u6297\u6e05\u653f\u5e9c\u3002\u5979\u7ec6\u7ec6\u7b79\u5212\uff0c\u8981\u5728\u638c\u95e8\u4eba\u5927\u4f1a\u4e4b\u4e2d\u5148\u63ed\u9732\u6c64\u6c9b\u7684\u771f\u76f8\uff0c\u518d\u6740\u4ed6\u4e3a\u6bcd\u62a5\u4ec7\uff0c\u5982\u80fd\u5728\u4f1a\u4e2d\u5927\u95f9\u4e00\u573a\uff0c\u4f7f\u798f\u5eb7\u5b89\u5978\u8ba1\u4e0d\u901e\uff0c\u90a3\u4e0d\u4f46\u5e2e\u4e86\u7ea2\u82b1\u4f1a\u8bf8\u4f2f\u53d4\u4e00\u4e2a\u5927\u5fd9\uff0c\u4e0d\u6789\u4ed6\u4eec\u5e73\u65e5\u7684\u8f9b\u82e6\u6559\u5bfc\uff0c\u6291\u4e14\u662f\u9020\u798f\u5929\u4e0b\u6b66\u6797\u4e86\u3002 \u3000\u3000\u5728\u6e56\u5317\u6c64\u6c9b\u8001\u5bb6\uff0c\u4ed6\u95e8\u4eba\u5b50\u4f84\u56fa\u7136\u4e0d\u5c11\uff0c\u4fbf\u662f\u517b\u5728\u5bb6\u4e2d\u7684\u95f2\u6c49\u95e8\u5ba2\u4e5f\u6709\u6570\u5341\u4eba\u4e4b\u591a\uff0c\u8981\u6df7\u8fdb\u4ed6\u5e9c\u4e2d\u6781\u662f\u4e0d\u6613\uff0c\u4f46\u5230\u4e86\u5317\u4eac\uff0c\u6c64\u6c9b\u4f4f\u7684\u4e0d\u8fc7\u662f\u4e00\u5bb6\u4e0a\u7b49\u5ba2\u5e97\uff0c\u5706\u6027\u6539\u4f5c\u7537\u88c5\uff0c\u8fdb\u51fa\u5ba2\u5e97\uff0c\u8c01\u4e5f\u4e0d\u5728\u610f\u4e0b\u3002\u5979\u5077\u542c\u4e86\u6c64\u6c9b\u51e0\u6b21\u8c08\u8bdd\uff0c\u77e5\u4ed6\u70ed\u4e2d\u529f\u540d\uff0c\u4e9f\u76fc\u4e58\u673a\u5df4\u7ed3\u4e0a\u798f\u5eb7\u5b89\uff0c\u5c31\u6b64\u5e73\u6b65\u9752\u4e91\uff0c\u4e8e\u662f\u8bbe\u4e0b\u8ba1\u8c0b\uff0c\u4f2a\u9020\u4e66\u4fe1\uff0c\u5077\u6362\u5c0f\u5e3d\u3002\u518d\u52a0\u4e0a\u7a0b\u7075\u7d20\u788e\u7389\u9f99\u676f\u3001\u80e1\u6590\u6551\u5fc3\u781a\u7b49\u51e0\u4ef6\u4e8b\u4e00\u51d1\u5408\uff0c\u6c64\u6c9b\u4fbf\u6709\u82cf\u5f20\u4e4b\u820c\u4e5f\u5df2\u8fa9\u89e3\u4e0d\u6765\u3002 \u3000\u3000\u5979\u539f\u6765\u6253\u7b97\u5c06\u51e4\u5929\u5357\u4e5f\u9677\u5bb3\u5728\u5185\uff0c\u4f46\u7236\u5973\u5929\u6027\uff0c\u867d\u8bf4\u4ed6\u65e0\u6076\u4e0d\u4f5c\uff0c\u5bf9\u5df1\u5b9e\u65e0\u534a\u5206\u7236\u5973\u4e4b\u60c5\uff0c\u53ef\u662f\u8bdd\u5230\u5634\u8fb9\u7ec8\u662f\u8bf4\u4e0d\u51fa\u53e3\u3002 \u3000\u3000\u6c64\u6c9b\u6b64\u523b\u75c5\u6025\u4e71\u6295\u533b\uff0c\u4fbf\u5982\u884c\u5c06\u6eba\u6b7b\u4e4b\u4eba\uff0c\u5c31\u662f\u78b0\u5230\u4e00\u6839\u7a3b\u8349\uff0c\u4e5f\u662f\u7d27\u6293\u4e0d\u653e\uff0c\u53eb\u9053\uff1a\u201c\u51e4\u5929\u5357\uff0c\u4f60\u8bf4\uff0c\u5979\u662f\u4e0d\u662f\u4f60\u7684\u5973\u513f\uff1f\u201d\u51e4\u5929\u5357\u7f13\u7f13\u70b9\u4e86\u70b9\u5934\u3002\u6c64\u6c9b\u5927\u58f0\u9053\uff1a\u201c\u798f\u5927\u5e05\uff0c\u4ed6\u7236\u5973\u4fe9\u8bbe\u4e0b\u5708\u5957\uff0c\u9677\u5bb3\u4e8e\u6211\u3002\u201d\u51e4\u5929\u5357\u6012\u9053\uff1a\u201c\u6211\u4e3a\u4ec0\u4e48\u8981\u5bb3\u4f60\uff1f\u201d\u6c64\u6c9b\u9053\uff1a\u201c\u53ea\u56e0\u6211\u903c\u6b7b\u4e86\u4f60\u7684\u59bb\u5b50\u3002\u201d\u51e4\u5929\u5357\u51b7\u7b11\u9053\uff1a\u201c\u563f\u563f\uff0c\u4f60\u903c\u6b7b\u7684\u90a3\u4e2a\u5973\u5b50\uff0c\u8c01\u8bf4\u662f\u6211\u59bb\u5b50\uff1f\u51e4\u67d0\u5230\u4e86\u624b\u4fbf\u4e22\uff0c\u8fd9\u79cd\u5973\u5b50\u2026\u2026\u201d\u4ed6\u8bf4\u5230\u8fd9\u91cc\uff0c\u5ffd\u7136\u89c1\u5230\u5706\u6027\u51b7\u68ee\u68ee\u7684\u76ee\u5149\u51dd\u89c6\u7740\u81ea\u5df1\uff0c\u4e0d\u7981\u6253\u4e2a\u5bd2\u6218\uff0c\u4e0d\u6562\u518d\u8bf4\u3002 \u3000\u3000\u6c64\u6c9b\u9053\uff1a\u201c\u597d\uff0c\u4e8b\u5df2\u5982\u6b64\uff0c\u6211\u4e5f\u4e0d\u5fc5\u9690\u7792\u3002\u90a3\u65e0\u5f71\u94f6\u9488\uff0c\u662f\u4f60\u653e\u7684\u8fd8\u662f\u6211\u653e\u7684\uff1f\u4f60\u82e5\u80fd\u653e\uff0c\u90a3\u4fbf\u5c04\u6211\u4e00\u679a\u8bd5\u8bd5\u3002\u201d \u3000\u3000\u4ed6\u6b64\u8a00\u4e00\u51fa\uff0c\u7fa4\u8c6a\u53c8\u5927\u54d7\u8d77\u6765\u3002 \u3000\u3000\u80e1\u6590\u80cc\u4e0a\u4e2d\u9488\uff0c\u7565\u4e00\u5b9a\u795e\u4e4b\u540e\uff0c\u5df2\u77e5\u90a3\u94f6\u9488\u51b3\u975e\u51e4\u5929\u5357\u6240\u53d1\uff0c\u5f53\u65f6\u4ed6\u5200\u65ad\u94dc\u68cd\uff0c\u6b63\u9762\u5bf9\u7740\u51e4\u5929\u5357\uff0c\u5706\u6027\u8fdb\u6765\u65f6\u4ed6\u5fc3\u795e\u604d\u60da\uff0c\u80cc\u5fc3\u4fbf\u4e2d\u94f6\u9488\uff0c\u90a3\u5b9a\u662f\u5728\u4ed6\u8eab\u540e\u4e4b\u4eba\u5077\u88ad\u3002\u4ed6\u89c1\u6c64\u6c9b\u521d\u65f6\u548c\u51e4\u5929\u5357\u4e89\u5435\uff0c\u8bf4\u4ed6\u201c\u6697\u7bad\u4f24\u4eba\uff0c\u4e0d\u662f\u597d\u6c49\u201d\uff0c\u59cb\u7ec8\u6ca1\u7591\u5fc3\u5230\u6c64\u6c9b\u8eab\u4e0a\uff0c\u6599\u60f3\u82e5\u4e0d\u662f\u6d77\u5170\u5f3c\u6240\u4e3a\uff0c\u4fbf\u662f\u90a3\u4e2a\u59d4\u59d4\u7410\u7410\u7684\u6b66\u5f53\u638c\u95e8\u65e0\u9752\u5b50\u505a\u4e86\u624b\u811a\uff0c\u90a3\u6599\u5230\u7adf\u662f\u6c64\u51e4\u4e8c\u4eba\u6545\u610f\u5e03\u4e0b\u7591\u9635\uff0c\u63a9\u4eba\u8033\u76ee\u3002 \u3000\u3000\u539f\u6765\u51e4\u5929\u5357\u4ece\u4f5b\u5c71\u9547\u5317\u9003\uff0c\u7ecf\u8fc7\u6e56\u5317\u65f6\u66fe\u5728\u6c64\u6c9b\u5bb6\u4e2d\u4f4f\u8fc7\u51e0\u5929\uff0c\u65e0\u610f\u4e2d\u542c\u5230\u4e24\u4e2a\u4ec6\u4eba\u8c08\u5230\u5e7f\u4e1c\u4f5b\u5c71\u7684\u98ce\u571f\u4eba\u60c5\uff0c\u4e0d\u7531\u5f97\u5173\u5fc3\uff0c\u8d4f\u4e86\u90a3\u4e24\u4ec6\u5341\u51e0\u4e24\u94f6\u5b50\uff0c\u7ec6\u95ee\u60c5\u7531\uff0c\u7adf\u63a2\u542c\u5230\u4e86\u94f6\u59d1\u4e4b\u4e8b\u3002\u51e4\u5929\u5357\u5bf9\u94f6\u59d1\u72b9\u5982\u8fc7\u773c\u4e91\u70df\uff0c\u81ea\u4e0d\u5c06\u8fd9\u4ef6\u4e8b\u653e\u5728\u5fc3\u4e0a\uff0c\u4e00\u7b11\u4e86\u4e4b\uff0c\u4e5f\u4e0d\u8ddf\u6c64\u6c9b\u63d0\u8d77\u3002\u6765\u5317\u4eac\u65f6\uff0c\u4e00\u8def\u4e4b\u4e0a\u66fe\u8bbe\u6cd5\u8ba8\u597d\u80e1\u6590\uff0c\u4e49\u5802\u9547\u7684\u5927\u5b85\u7530\u5730\uff0c\u4fbf\u662f\u4ed6\u6240\u9001\u7684\u4e86\uff0c\u5230\u4e86\u5317\u4eac\u540e\u53c8\u4f7f\u4e86\u4e0d\u5c11\u94f6\u5b50\uff0c\u8bf7\u4e86\u5468\u94c1\u9e6a\u51fa\u9762\u5316\u89e3\u3002 \u3000\u3000\u4f46\u80e1\u6590\u4fa0\u4e49\u5fc3\u80a0\uff0c\u867d\u7136\u953a\u963f\u56db\u8ddf\u4ed6\u65e0\u4eb2\u65e0\u6545\uff0c\u5374\u662f\u6b7b\u7f20\u5230\u5e95\uff0c\u4e0d\u80af\u7f62\u4f11\u3002\u51e4\u5929\u5357\u5fc3\u60f3\uff0c\u6b64\u4eba\u4e0d\u9664\uff0c\u81ea\u5df1\u8fd9\u4e00\u751f\u7ec8\u662f\u5bdd\u98df\u96be\u5b89\uff0c\u5f53\u4e0b\u53bb\u8ddf\u6c64\u6c9b\u5546\u91cf\uff0c\u6015\u4ed6\u4e0d\u80af\u76f8\u52a9\uff0c\u6545\u610f\u5371\u8a00\u8038\u542c\uff0c\u8bf4\u80e1\u6590\u5b9a\u8981\u5230\u638c\u95e8\u4eba\u5927\u4f1a\u4e2d\u6765\u6363\u4e71\u3002\u6c64\u6c9b\u521d\u65f6\u8fd8\u4e0d\u80af\u63d2\u624b\uff0c\u51e4\u5929\u5357\u4fbf\u63d0\u5230\u94f6\u59d1\u4e4b\u4e8b\uff0c\u6697\u793a\u6c64\u6c9b\u82e5\u4e0d\u76f8\u52a9\uff0c\u8bf4\u4e0d\u5f97\u8981\u5c06\u8fd9\u4ef6\u4e8b\u6296\u9732\u51fa\u6765\uff0c\u4f46\u82e5\u6c64\u6c9b\u80fd\u8bbe\u6cd5\u9664\u4e86\u80e1\u6590\uff0c\u4ed6\u56de\u5230\u4f5b\u5c71\u91cd\u6574\u57fa\u4e1a\uff0c\u6bcf\u5e74\u9001\u4ed6\u4e00\u4e07\u4e24\u94f6\u5b50\u3002 \u3000\u3000\u6c64\u6c9b\u4ea4\u7ed3\u670b\u53cb\uff0c\u82b1\u8d39\u6781\u5927\u3002\u4ed6\u4e3a\u4e86\u535a\u4ec1\u4e49\u4e4b\u540d\uff0c\u53c8\u4e0d\u80fd\u50cf\u51e4\u5929\u5357\u8fd9\u822c\u5f00\u8d4c\u573a\u3001\u9738\u7801\u5934\uff0c\u516c\u7136\u7684\u5de7\u53d6\u8c6a\u593a\uff0c\u542c\u51e4\u5929\u5357\u7b54\u5e94\u6bcf\u5e74\u76f8\u9001\u4e00\u4e07\u4e24\u94f6\u5b50\uff0c\u81ea\u4e0d\u514d\u5fc3\u52a8\uff0c\u518d\u52a0\u4e0a\u987e\u5fcc\u94f6\u59d1\u4e4b\u4e8b\u8d25\u9732\uff0c\u4e8e\u662f\u7b54\u5e94\u76f8\u52a9\u3002 \u3000\u3000\u6c64\u6c9b\u751a\u5de5\u5fc3\u8ba1\uff0c\u9774\u5e95\u4e4b\u4e2d\uff0c\u88c5\u8bbe\u6709\u6781\u4e3a\u7cbe\u5de7\u7684\u94f6\u9488\u6697\u5668\uff0c\u4ed6\u884c\u8def\u8db3\u8ddf\u5e76\u4e0d\u7740\u5730\uff0c\u8db3\u8ddf\u82e5\u5728\u5730\u4e0b\u4e00\u78b0\uff0c\u8db3\u5c16\u4e0a\u4fbf\u6709\u94f6\u9488\u5c04\u51fa\uff0c\u5f53\u771f\u662f\u65e0\u5f71\u65e0\u8e2a\uff0c\u4eba\u6240\u96be\u6d4b\u3002\u4ed6\u60f3\u65e2\u7136\u76f8\u52a9\u51e4\u5929\u5357\uff0c\u7d22\u6027\u5927\u52a9\u4e00\u756a\uff0c\u8ba9\u4ed6\u6367\u4e00\u53ea\u7389\u9f99\u676f\u56de\u5230\u4f5b\u5c71\uff0c\u58f0\u5a01\u5927\u632f\u4e4b\u4e0b\uff0c\u6bcf\u5e74\u76f8\u8d60\u7684\u916c\u91d1\u81ea\u4e5f\u4e0d\u6b62\u662f\u4e00\u4e07\u4e24\u94f6\u5b50\u4e86\u3002\u51e4\u5929\u5357\u5728\u4f1a\u4e2d\u8fde\u8d25\u9ad8\u624b\uff0c\u5168\u662f\u6c64\u6c9b\u6697\u653e\u94f6\u9488\u3002\u94f6\u9488\u65e2\u7ec6\uff0c\u4ed6\u8e0f\u8db3\u53d1\u9488\u4e4b\u6280\u53c8\u662f\u5de7\u5999\u5f02\u5e38\uff0c\u867d\u5728\u4f17\u76ee\u777d\u777d\u4e4b\u4e0b\uff0c\u7adf\u65e0\u4e00\u4eba\u53d1\u89c9\uff0c\u4fbf\u8fde\u7a0b\u7075\u7d20\u8fd9\u7b49\u5fc3\u601d\u5468\u5bc6\u4e4b\u4eba\uff0c\u4e5f\u6ca1\u770b\u51fa\u7aef\u502a\u3002 \u3000\u3000\u4e0d\u6599\u53d8\u751f\u4e0d\u6d4b\uff0c\u51ed\u7a7a\u95ef\u4e86\u4e00\u4e2a\u5c0f\u5c3c\u59d1\u8fdb\u6765\uff0c\u4e00\u756a\u8a00\u8bed\uff0c\u5c06\u6c64\u6c9b\u7d27\u7d27\u5730\u7f20\u5728\u7f51\u91cc\uff0c\u7adf\u662f\u4e1d\u6beb\u6297\u8fa9\u4e0d\u5f97\u3002\u4ed6\u5371\u6025\u4e4b\u4e2d\uff0c\u7a81\u7136\u53d1\u89c9\u8fd9\u5c3c\u59d1\u662f\u51e4\u5929\u5357\u7684\u5973\u513f\uff0c\u4e0d\u7ba1\u4e09\u4e03\u4e8c\u5341\u4e00\uff0c\u4fbf\u5c06\u8fd9\u4e8b\u8bf4\u51fa\u6765\u3002\u4ed6\u60f3\u903c\u6b7b\u5f31\u5973\u3001\u6bd4\u6b66\u4f5c\u5f0a\u4e8b\u5c0f\uff0c\u52fe\u7ed3\u7ea2\u82b1\u4f1a\u3001\u56fe\u8c0b\u53db\u4e71\u7684\u7f6a\u540d\u5374\u662f\u6781\u5927\uff0c\u4e24\u5bb3\u76f8\u6743\u53d6\u5176\u8f7b\uff0c\u5f53\u4e0b\u4fbf\u5411\u51e4\u5929\u5357\u7236\u5973\u53cd\u51fb\u3002 \u3000\u3000\u51e4\u5929\u5357\u4e00\u542c\u6c64\u6c9b\u4e4b\u8a00\uff0c\u4fbf\u77e5\u4ed6\u7684\u7528\u610f\uff0c\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u77e5\u9053\u4e86\u4f60\u52fe\u7ed3\u7ea2\u82b1\u4f1a\u3001\u610f\u56fe\u4e0d\u8f68\u7684\u5978\u8c0b\uff0c\u4f60\u4fbf\u60f3\u5077\u653e\u94f6\u9488\uff0c\u6697\u4e2d\u52a9\u6211\uff0c\u5356\u4e00\u4e2a\u597d\uff0c\u76fc\u671b\u6211\u4e0d\u5411\u798f\u5927\u5e05\u63ed\u9732\u3002\u563f\u563f\uff0c\u53ef\u662f\u6211\u51e4\u5929\u5357\u8d64\u80c6\u5fe0\u5fc3\uff0c\u4e00\u5fc3\u62a5\u56fd\uff0c\u5c82\u80af\u53d7\u4f60\u8fd9\u79cd\u5978\u8d3c\u6536\u4e70\u2026\u2026\u201d \u3000\u3000\u6c64\u6c9b\u542c\u4ed6\u7adf\u7136\u53cd\u54ac\u4e00\u53e3\uff0c\u6599\u4ed6\u5fc5\u5b9a\u8d8a\u8bf4\u8d8a\u662f\u4e0d\u582a\uff0c\u66b4\u6012\u4e4b\u4e0b\uff0c\u53cc\u8db3\u4e00\u767b\uff0c\u56db\u679a\u94f6\u9488\u6fc0\u5c04\u800c\u51fa\uff0c\u4e00\u9f50\u5c04\u8fdb\u4e86\u4ed6\u5c0f\u8179\u3002 \u3000\u3000\u51e4\u5929\u5357\u5927\u53eb\u4e00\u58f0\uff0c\u62b1\u4f4f\u809a\u5b50\uff0c\u5f2f\u4e0b\u8170\u6765\uff0c\u5495\u549a\u4e00\u58f0\uff0c\u6454\u5012\u5728\u5730\u3002\u5706\u6027\u6025\u5fd9\u62a2\u4e0a\u6276\u4f4f\uff0c\u53eb\u9053\uff1a\u201c\u7239\uff0c\u7239\u2026\u2026\u4f60\u2026\u2026\u600e\u4e48\u5566\uff1f\u201d \u3000\u3000\u738b\u5251\u82f1\u3001\u5468\u94c1\u9e6a\u7b49\u89c1\u6c64\u6c9b\u6b64\u65f6\u5c1a\u8981\u884c\u51f6\uff0c\u4e00\u9f50\u62e5\u4e0a\uff0c\u5c06\u4ed6\u6293\u4f4f\u3002\u6c64\u6c9b\u4e5f\u4e0d\u53cd\u6297\uff0c\u53ea\u53eb\uff1a\u201c\u51a4\u6789\uff0c\u51a4\u6789\uff01\u51a4\u5b7d\uff0c\u51a4\u5b7d\uff01\u201d \u3000\u3000\u4ed6\u5fc3\u77e5\u798f\u5eb7\u5b89\u751a\u662f\u591a\u7591\uff0c\u6b64\u4e8b\u7eb5\u7136\u8fa9\u660e\uff0c\u4e5f\u51b3\u8ba1\u653e\u4e0d\u8fc7\u81ea\u5df1\uff0c\u4f55\u51b5\u94c1\u6848\u4f3c\u5c71\uff0c\u65e0\u8bba\u5982\u4f55\u8fa9\u660e\u4e0d\u4e86\uff0c\u603b\u662f\u81ea\u5df1\u751f\u5e73\u4f5c\u7684\u6076\u4e8b\u592a\u591a\uff0c\u5230\u5934\u6765\u906d\u6b64\u62a5\u5e94\u3002 \u3000\u3000\u5706\u6027\u5c06\u51e4\u5929\u5357\u6276\u8d77\uff0c\u53ea\u89c1\u4ed6\u53cc\u773c\u4e00\u7ffb\uff0c\u5df2\u7136\u6c14\u7edd\u800c\u6b7b\u3002 \u3000\u3000\u5385\u4e0a\u65e9\u5df2\u4e71\u6210\u4e00\u56e2\uff0c\u8c01\u4e5f\u542c\u4e0d\u89c1\u8c01\u7684\u8bf4\u8bdd\u3002 \u3000\u3000\u798f\u5eb7\u5b89\u5fc3\u60f3\uff1a\u201c\u8fd9\u6c64\u6c9b\u5b9a\u7136\u53e6\u6709\u540c\u8c0b\u4e4b\u4eba\uff0c\u90a3\u5c0f\u5c3c\u59d1\u591a\u534a\u4e5f\u77e5\u4ed6\u4fe1\u5185\u4e4b\u8a00\uff0c\u867d\u8bf4\u5978\u8c0b\u7531\u5979\u63ed\u9732\uff0c\u5374\u4e5f\u4e0d\u80fd\u7559\u4e0b\u6d3b\u53e3\uff0c\u4efb\u5979\u5ba3\u6cc4\u4e8e\u5916\u3002\u201d\u4e8e\u662f\u4f4e\u58f0\u5411\u5b89\u63d0\u7763\u9053\uff1a\u201c\u5173\u4e0a\u4e86\u5927\u95e8\uff0c\u8c01\u90fd\u4e0d\u8bb8\u51fa\u53bb\uff0c\u62ff\u4e0b\u4e86\u9010\u4e2a\u513f\u5ba1\u95ee\u3002\u201d \u3000\u3000\u80e1\u6590\u89c1\u52bf\u4e0d\u5bf9\uff0c\u7eb5\u8eab\u62a2\u5230\u5706\u6027\u8eab\u8fb9\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5feb\u8d70\uff01\u8fdf\u4e86\u4fbf\u8131\u4e0d\u4e86\u8eab\u5566\u3002\u201d\u5706\u6027\u70b9\u4e86\u70b9\u5934\uff0c\u4e24\u4eba\u8d70\u5230\u7a0b\u7075\u7d20\u8eab\u65c1\u3002\u5706\u6027\u7a81\u7136\u4f38\u51fa\u4e00\u6307\uff0c\u70b9\u5728\u8521\u5a01\u80c1\u4e0b\uff0c\u8ddf\u7740\u53c8\u5728\u4ed6\u80a9\u5934\u548c\u80cc\u5fc3\u7684\u91cd\u7a74\u4e0a\u8fde\u70b9\u4e24\u6307\u3002\u8521\u5a01\u767b\u65f6\u8dcc\u5012\u3002 \u3000\u3000\u59ec\u6653\u5cf0\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4f60\u2026\u2026\u201d\u5706\u6027\u9053\uff1a\u201c\u80e1\u5927\u54e5\uff0c\u662f\u6b64\u4eba\u6cc4\u9732\u673a\u5bc6\uff0c\u6697\u4e2d\u5c06\u798f\u5eb7\u5b89\u7684\u4e24\u4e2a\u513f\u5b50\u9001\u4e86\u56de\u53bb\u3002\u201d\u80e1\u6590\u201c\u554a\u201d \u3000\u3000\u7684\u4e00\u58f0\uff0c\u6012\u9053\uff1a\u201c\u6b64\u4eba\u5982\u6b64\u53ef\u6076\uff01\u201d\u4f38\u8db3\u5728\u8521\u5a01\u80cc\u5fc3\u4e0a\u91cd\u91cd\u8e22\u4e86\u4e00\u811a\uff0c\u8fd9\u4e00\u811a\u867d\u4e0d\u53d6\u4e86\u4ed6\u6027\u547d\uff0c\u4f46\u8521\u5a01\u81ea\u6b64\u7b4b\u8109\u5927\u635f\uff0c\u5df2\u4e0e\u5e9f\u4eba\u65e0\u5f02\u3002\u6df7\u4e71\u4e4b\u4e2d\uff0c\u4ed6\u4e8c\u4eba\u5bf9\u4ed8\u8521\u5a01\uff0c\u65c1\u4eba\u4e5f\u672a\u77e5\u89c9\u3002\u80e1\u6590\u5bf9\u59ec\u6653\u5cf0\u9053\uff1a\u201c\u59ec\u5144\u5feb\u8d70\u3002\u4e00\u5207\u591a\u8c22\u3002\u54b1\u4eec\u540e\u4f1a\u6709\u671f\u3002\u201d\u59ec\u6653\u5cf0\u89c1\u60c5\u52bf\u4e0d\u5bf9\uff0c\u62f1\u4e86\u62f1\u624b\uff0c\u62a2\u6b65\u51fa\u95e8\u3002 \u3000\u3000\u53ea\u542c\u5b89\u63d0\u7763\u53eb\u9053\uff1a\u201c\u5927\u5bb6\u5404\u5f52\u539f\u5ea7\uff0c\u4e0d\u53ef\u5608\u5435\uff01\u201d \u3000\u3000\u7a0b\u7075\u7d20\u88c5\u4e86\u4e00\u7b52\u70df\uff0c\u72c2\u55b7\u4e86\u51e0\u53e3\uff0c\u8ddf\u7740\u53c8\u8d70\u5230\u5385\u5de6\u5385\u53f3\uff0c\u4e00\u9762\u55b7\u70df\uff0c\u4e00\u9762\u6382\u8d77\u4e86\u811a\u5728\u4eba\u4e1b\u4e2d\u77a7\u70ed\u95f9\u3002\u5ffd\u7136\u6709\u4eba\u53eb\u9053\uff1a\u201c\u554a\u54df\uff0c\u809a\u5b50\u597d\u75db\uff01\u201d\u4ed6\u53eb\u58f0\u752b\u6b47\uff0c\u56db\u5468\u90fd\u6709\u4eba\u53eb\u4e86\u8d77\u6765\uff1a\u201c\u554a\u54df\uff0c\u554a\u54df\uff01\u809a\u75db\uff0c\u809a\u75db\u3002\u201d\u7a0b\u7075\u7d20\u56de\u5230\u80e1\u6590\u548c\u5706\u6027\u8eab\u8fb9\uff0c\u4f7f\u4e2a\u773c\u8272\uff0c\u62b1\u4f4f\u809a\u5b50\u53eb\u9053\uff1a\u201c\u554a\u5537\uff0c\u597d\u75db\uff0c\u597d\u75db\uff0c\u4e2d\u4e86\u6bd2\u5566\uff01\u201d \u3000\u3000\u90a3\u81ea\u79f0\u201c\u6bd2\u624b\u836f\u738b\u201d\u7684\u77f3\u4e07\u55d4\u809a\u4e2d\u4e5f\u5267\u70c8\u75bc\u75db\uff0c\u6025\u5fd9\u53d6\u51fa\u4e00\u675f\u836f\u8349\uff0c\u6253\u706b\u70b9\u71c3\u4e86\u3002\u4ed6\u70b9\u71c3\u836f\u8349\uff0c\u539f\u662f\u610f\u6b32\u89e3\u6bd2\uff0c\u7a0b\u7075\u7d20\u65e9\u6599\u5230\u4e86\u6b64\u7740\uff0c\u8eb2\u5728\u4eba\u4e1b\u4e2d\u53eb\u9053\uff1a\u201c\u6bd2\u624b\u836f\u738b\u653e\u6bd2\uff0c\u6bd2\u624b\u836f\u738b\u653e\u6bd2\uff01\u201d\u80e1\u6590\u8ddf\u7740\u53eb\u9053\uff1a\u201c\u5feb\uff0c\u5feb\u5236\u4f4f\u4ed6\uff0c\u6bd2\u624b\u836f\u738b\u8981\u6bd2\u6b7b\u798f\u5927\u5e05\u3002\u201d \u3000\u3000\u4e00\u7247\u6df7\u4e71\u4e4b\u4e2d\uff0c\u4f17\u4eba\u90a3\u91cc\u8fd8\u80fd\u5206\u8fa8\u5230\u5e95\u6bd2\u4ece\u4f55\u6765\uff0c\u5fc3\u4e2d\u9707\u4e8e\u201c\u6bd2\u624b\u836f\u738b\u201d\u7684\u5a01\u540d\uff0c\u8ba4\u5b9a\u4ed6\u4e00\u51fa\u624b\u4fbf\u662f\u4e0b\u6bd2\uff0c\u4f55\u51b5\u81ea\u5df1\u809a\u4e2d\u6b63\u5728\u75db\u4e0d\u53ef\u5f53\uff0c\u773c\u89c1\u4ed6\u624b\u4e2d\u836f\u8349\u5df2\u7ecf\u70b9\u71c3\uff0c\u70e7\u51fa\u767d\u70df\uff0c\u6599\u60f3\u8fd9\u70df\u81ea\u7136\u5267\u6bd2\u65e0\u6bd4\uff0c\u4e2d\u8005\u7acb\u6bd9\uff0c\u8c01\u4e5f\u4e0d\u6562\u8d70\u8fd1\u5236\u6b62\u3002\u53ea\u542c\u98d5\u98d5\u98d5\u54cd\u58f0\u4e0d\u7edd\uff0c\u56db\u9762\u516b\u65b9\u7684\u6697\u5668\u90fd\u5411\u77f3\u4e07\u55d4\u5c04\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u90a3\u77f3\u4e07\u55d4\u7684\u6b66\u529f\u4e5f\u771f\u4e86\u5f97\uff0c\u867d\u7136\u5728\u970e\u65f6\u4e4b\u95f4\u6210\u4e3a\u4f17\u77e2\u4e4b\u7684\uff0c\u7adf\u662f\u4e34\u5371\u4e0d\u4e71\uff0c\u4e00\u77ee\u8eab\uff0c\u6380\u7ffb\u4e00\u5f20\u65b9\u684c\uff0c\u6a2a\u8fc7\u6765\u6321\u5728\u8eab\u524d\uff0c\u53ea\u542c\u5f97\u567c\u567c\u556a\u556a\uff0c\u72b9\u4f3c\u4e0b\u4e86\u4e00\u5c42\u5bc6\u5bc6\u7684\u51b0\u96f9\uff0c\u6570\u5341\u679a\u6697\u5668\u5c3d\u6570\u6253\u5728\u684c\u9762\u4e4b\u4e0a\u3002\u4ed6\u5927\u58f0\u53eb\u9053\uff1a\u201c\u6709\u4eba\u5728\u8336\u9152\u4e4b\u4e2d\u4e0b\u4e86\u6bd2\u836f\uff0c\u548c\u6211\u4f55\u5e72\uff1f\u201d \u3000\u3000\u6b64\u756a\u524d\u6765\u8d74\u4f1a\u7684\u6c5f\u6e56\u8c6a\u5ba2\u4e4b\u4e2d\uff0c\u539f\u6709\u8bb8\u591a\u4eba\u60f3\u5230\u798f\u5eb7\u5b89\u62db\u96c6\u5929\u4e0b\u638c\u95e8\u4eba\u805a\u4f1a\uff0c\u53ea\u6015\u6697\u4e2d\u5b89\u6392\u4e0b\u9634\u8c0b\u6bd2\u8ba1\uff0c\u8981\u5c06\u6b66\u6797\u4e2d\u7684\u597d\u624b\u4e00\u7f51\u6253\u5c3d\u3002\u987b\u77e5\u201c\u5112\u4ee5\u6587\u4e71\u6cd5\uff0c\u4fa0\u4ee5\u6b66\u72af\u7981\u201d\uff0c\u5386\u6765\u4eba\u4e3b\u5927\u81e3\uff0c\u82e5\u4e0d\u80fd\u7f51\u7f57\u6587\u6b66\u624d\u58eb\u4ee5\u7528\uff0c\u4fbf\u6b32\u52a0\u4e4b\u65a7\u94ba\u800c\u706d\uff0c\u4ee5\u514d\u4e3a\u60a3\u6c11\u95f4\uff0c\u717d\u52a8\u5929\u4e0b\u3002\u8fd9\u65f6\u542c\u5230\u77f3\u4e07\u55d4\u5927\u53eb\uff1a\u201c\u6709\u4eba\u5728\u8336\u9152\u4e4b\u4e2d\u4e0b\u4e86\u6bd2\u836f\u201d\uff0c\u4e2a\u4e2a\u5fc3\u60ca\u8089\u8df3\uff0c\u81f3\u4e8e\u798f\u5eb7\u5b89\u81ea\u5df1\u548c\u4f17\u536b\u58eb\u5176\u5b9e\u4e5f\u662f\u809a\u4e2d\u75bc\u75db\uff0c\u65c1\u4eba\u81ea\u7136\u4e0d\u77e5\u3002 \u3000\u3000\u5f53\u4e0b\u5385\u4e0a\u66f4\u52a0\u5927\u4e71\u8d77\u6765\uff0c\u8bb8\u591a\u4eba\u4f4e\u58f0\u4e92\u76f8\u62db\u547c\uff1a\u201c\u5feb\u8d70\u5feb\u8d70\uff0c\u798f\u5927\u5e05\u8981\u6bd2\u6b7b\u54b1\u4eec\u3002\u201d\u201c\u8981\u547d\u7684\u5feb\u9003\uff01\u201d\u201c\u5feb\u56de\u5bd3\u6240\u53bb\u670d\u89e3\u6bd2\u836f\u7269\u3002\u201d \u3000\u3000\u7a0b\u7075\u7d20\u5728\u70df\u7ba1\u4e2d\u88c5\u4e86\u836f\u7269\uff0c\u55b7\u51fa\u6bd2\u70df\uff0c\u5927\u5385\u4e0a\u4eba\u4eba\u5438\u8fdb\uff0c\u65e0\u4e00\u5f97\u4ee5\u5e78\u514d\u3002\u8fd9\u6bd2\u70df\u5012\u4e0d\u662f\u81f4\u547d\u4e4b\u7269\uff0c\u4f46\u5438\u8fdb\u8005\u5c11\u4e0d\u514d\u5934\u75bc\u8179\u75db\uff0c\u75db\u4e0a\u5927\u534a\u4e2a\u65f6\u8fb0\u65b9\u7f62\u3002\u8fd9\u4e00\u62db\u5927\u662f\u5389\u5bb3\uff0c\u4e0d\u4f46\u4f7f\u4f17\u536b\u58eb\u7591\u5fc3\u77f3\u4e07\u55d4\u4e0b\u6bd2\uff0c\u66f4\u4f7f\u7fa4\u8c6a\u4ee5\u4e3a\u798f\u5eb7\u5b89\u6709\u610f\u6697\u5bb3\uff0c\u5927\u4e71\u4e4b\u4e2d\uff0c\u5979\u548c\u80e1\u6590\u3001\u5706\u6027\u4fbf\u53ef\u4e58\u673a\u8131\u8eab\u3002 \u3000\u3000\u773c\u89c1\u7fa4\u8c6a\u7eb7\u7eb7\u593a\u95e8\u800c\u8d70\uff0c\u4f46\u5706\u6027\u5374\u6b63\u548c\u6c64\u6c9b\u6597\u5f97\u751a\u662f\u6fc0\u70c8\u3002 \u3000\u3000\u539f\u6765\u6c64\u6c9b\u4e58\u7740\u6df7\u4e71\uff0c\u6253\u5012\u4e86\u62ff\u4f4f\u4ed6\u7684\u536b\u58eb\uff0c\u4fbf\u6b32\u9003\u8d70\uff0c\u5374\u7ed9\u5706\u6027\u62a2\u4e0a\u622a\u4f4f\u3002\u6c64\u6c9b\u4e3a\u4eba\u867d\u7136\u5978\u6076\uff0c\u6b66\u529f\u4fee\u4e3a\u5374\u662f\u6781\u9ad8\uff0c\u5fc3\u4e0b\u607c\u6068\u5706\u6027\u9634\u8c0b\u8bec\u9677\uff0c\u4e00\u67c4\u9752\u94a2\u5251\u62db\u52bf\u51cc\u5389\uff0c\u5251\u5251\u523a\u5411\u5979\u7684\u8981\u5bb3\u3002\u5706\u6027\u5de6\u624b\u6301\u7740\u4e91\u5e1a\uff0c\u53f3\u624b\u821e\u52a8\u8f6f\u97ad\uff0c\u4e5f\u662f\u7acb\u610f\u8981\u5c06\u8fd9\u6740\u6bcd\u4e4b\u4ec7\u6bd9\u4e8e\u97ad\u4e0b\u3002 \u3000\u3000\u8bf4\u5230\u6b66\u529f\uff0c\u5706\u6027\u80dc\u5728\u97ad\u6cd5\u7cbe\u5999\uff0c\u6c64\u6c9b\u5374\u662f\u5185\u529b\u6d51\u539a\u5f97\u591a\uff0c\u4e00\u4e8c\u767e\u62db\u4e4b\u5185\u96be\u5206\u80dc\u8d1f\uff0c\u957f\u6597\u4e0b\u53bb\u8fd8\u662f\u6c64\u6c9b\u4f1a\u5360\u5230\u4e0a\u98ce\uff0c\u53ea\u662f\u4ed6\u5438\u4e86\u6bd2\u70df\uff0c\u809a\u8179\u5267\u75db\uff0c\u4e5f\u9053\u4e2d\u4e86\u5389\u5bb3\u7684\u6bd2\u836f\uff0c\u751f\u6015\u4e00\u7ecf\u4f7f\u529b\uff0c\u6bd2\u6027\u53d1\u4f5c\u66f4\u5feb\uff0c\u52a0\u4e4b\u4f17\u536b\u58eb\u864e\u89c6\u5728\u65c1\uff0c\u82e5\u975e\u4eba\u4eba\u809a\u75db\uff0c\u65e9\u5df2\u4e00\u62e5\u800c\u4e0a\u3002\u4ed6\u773c\u89c1\u5706\u6027\u97ad\u6cd5\u7cbe\u5999\uff0c\u4e00\u65f6\u6740\u5979\u4e0d\u5f97\uff0c\u5fc3\u4e2d\u614c\u4e71\uff0c\u6025\u6b32\u8131\u8eab\u3002 \u3000\u3000\u4f46\u5706\u6027\u5982\u4f55\u80af\u8ba9\u4ed6\u9003\u8d70\uff1f\u5979\u4e8b\u5148\u670d\u4e86\u7a0b\u7075\u7d20\u6240\u7ed9\u7684\u89e3\u836f\uff0c\u4e0d\u6015\u6bd2\u70df\uff0c\u53ea\u662f\u5bf9\u6c64\u6c9b\u811a\u5e95\u6240\u53d1\u7684\u65e0\u5f71\u94f6\u9488\u5374\u662f\u9887\u4e3a\u5fcc\u60ee\u3002\u5979\u867d\u662f\u6709\u5907\u800c\u6765\uff0c\u4e91\u5e1a\u4e2d\u5b89\u4e0a\u4e86\u4e00\u5757\u4e13\u7834\u9540\u94f6\u94c1\u9488\u7684\u5927\u78c1\u77f3\uff0c\u4f46\u90a3\u94f6\u9488\u7a76\u5c5e\u592a\u7ec6\uff0c\u65bd\u653e\u65f6\u53c8\u662f\u65e0\u5f71\u65e0\u8e2a\uff0c\u7edd\u65e0\u534a\u70b9\u5148\u5146\uff0c\u56e0\u6b64\u4e0d\u6562\u8fc7\u5206\u903c\u8fd1\uff0c\u53ea\u662f\u821e\u52a8\u8f6f\u97ad\u8fdc\u653b\u3002 \u3000\u3000\u8fd9\u65f6\u738b\u5251\u82f1\u3001\u5468\u94c1\u9e6a\u7b49\u65e9\u5df2\u4fdd\u62a4\u798f\u5eb7\u5b89\u9000\u5165\u540e\u5802\u3002\u798f\u5eb7\u5b89\u4f20\u4e0b\u53f7\u4ee4\uff0c\u7d27\u95ed\u5e9c\u95e8\uff0c\u8c01\u90fd\u4e0d\u8bb8\u51fa\u53bb\uff0c\u4e00\u9762\u6025\u53ec\u592a\u533b\uff0c\u670d\u98df\u89e3\u6bd2\u836f\u7269\u3002 \u3000\u3000\u7fa4\u8c6a\u89c1\u5e9c\u4e2d\u536b\u58eb\u8981\u5173\u95ed\u5e9c\u95e8\uff0c\u66f4\u52a0\u76f8\u4fe1\u798f\u5eb7\u5b89\u5b58\u5fc3\u52a0\u5bb3\uff0c\u6b64\u65f6\u9762\u4e34\u751f\u6b7b\u5173\u5934\uff0c\u4e5f\u987e\u4e0d\u5f97\u80cc\u8d1f\u4e00\u4e2a\u201c\u72af\u4e0a\u4f5c\u4e71\u201d\u7684\u7f6a\u540d\uff0c\u5f53\u5373\u8702\u62e5\u800c\u51fa\u3002\u4f17\u536b\u58eb\u4e3e\u5175\u5203\u62e6\u963b\uff0c\u7fa4\u8c6a\u4fbf\u5373\u8fd8\u624b\u51b2\u95e8\u3002\u81ea\u5927\u5385\u4ee5\u81f3\u5e9c\u95e8\u987b\u7ecf\u4e09\u9053\u95e8\u6237\uff0c\u6bcf\u4e00\u9053\u95e8\u8fb9\u90fd\u662f\u4e52\u4e52\u4e53\u4e53\u7684\u6597\u5f97\u751a\u662f\u6fc0\u70c8\u3002\u8fd9\u6b21\u5927\u4f1a\u805a\u96c6\u4e86\u6b66\u6797\u5404\u5bb6\u5404\u6d3e\u7684\u9ad8\u624b\uff0c\u867d\u7136\u771f\u6b63\u7b2c\u4e00\u6d41\u7684\u6e05\u9ad8\u4e4b\u58eb\u5e76\u4e0d\u8d74\u4f1a\uff0c\u4f46\u5230\u6765\u7684\u5374\u4e5f\u5747\u975e\u5bfb\u5e38\uff0c\u4f17\u4eba\u9f50\u5fc3\u5916\u51b2\uff0c\u4f17\u536b\u58eb\u5982\u4f55\u963b\u62e6\u5f97\u4f4f\uff1f \u3000\u3000\u5b89\u63d0\u7763\u6309\u4f4f\u4e86\u809a\u5b50\uff0c\u5411\u5927\u667a\u7985\u5e08\u3001\u65e0\u9752\u5b50\u3001\u7530\u5f52\u519c\u7b49\u4e00\u5e72\u9ad8\u624b\u8bf4\u9053\uff1a\u201c\u5978\u4eba\u6363\u4e71\u4f1a\u573a\uff0c\u5404\u4f4d\u4f46\u8bf7\u5b89\u5750\u52ff\u52a8\u3002\u798f\u5927\u5e05\u7231\u624d\u4e0b\u58eb\uff0c\u6c42\u8d24\u82e5\u6e34\uff0c\u5bf9\u5404\u4f4d\u6781\u662f\u793c\u656c\u3002\u5404\u4f4d\u5343\u4e07\u4e0d\u53ef\u8d77\u7591\u3002\u201d \u3000\u3000\u6d77\u5170\u5f3c\u9053\uff1a\u201c\u8fd9\u59d3\u6c64\u7684\u662f\u7f6a\u9b41\u7978\u9996\uff0c\u5148\u62ff\u4ed6\u4e0b\u6765\u518d\u8bf4\u3002\u201d\u545b\u5577\u5577\u4e00\u54cd\uff0c\u4ece\u8eab\u8fb9\u6296\u51fa\u9ed1\u9f99\u53cc\u6756\uff0c\u8d70\u5411\u5385\u5fc3\uff0c\u653b\u5411\u6c64\u6c9b\u3002 \u3000\u3000\u80e1\u6590\u89c1\u5706\u6027\u4e45\u6218\u6c64\u6c9b\u4e0d\u4e0b\uff0c\u5728\u5e9c\u4e2d\u591a\u803d\u4e00\u523b\uff0c\u4fbf\u662f\u591a\u4e00\u5206\u5371\u673a\uff0c\u987e\u4e0d\u5f97\u8eab\u4e0a\u6709\u4f24\uff0c\u62bd\u51fa\u5355\u5200\uff0c\u4fbf\u4e5f\u4e0a\u524d\u5939\u653b\u3002\u6c64\u6c9b\u5927\u53eb\uff1a\u201c\u770b\u6211\u7684\u94f6\u9488\uff01\u201d\u80e1\u6590\u3001\u5706\u6027\u3001\u6d77\u5170\u5f3c\u4e09\u4eba\u90fd\u662f\u4e00\u60ca\uff0c\u51dd\u795e\u63d0\u9632\u3002 \u3000\u3000\u6c64\u6c9b\u731b\u5730\u7eb5\u8d77\uff0c\u7834\u7a97\u800c\u51fa\u3002\u5706\u6027\u548c\u80e1\u6590\u4e00\u9f50\u8dc3\u8d77\uff0c\u5f85\u8981\u8ffd\u51fa\uff0c\u53ea\u89c1\u94f6\u5149\u95ea\u52a8\uff0c\u4e00\u4e1b\u94f6\u9488\u6fc0\u5c04\u800c\u81f3\u3002\u80e1\u6590\u5012\u7ffb\u4e00\u4e2a\u7b4b\u6597\u907f\u5f00\u3002\u5706\u6027\u6025\u821e\u4e91\u5e1a\uff0c\u6321\u4f4f\u5c04\u5411\u8eab\u524d\u7684\u94f6\u9488\u3002\u5c31\u662f\u8fd9\u4e48\u6162\u5f97\u4e00\u6162\uff0c\u6c64\u6c9b\u5df2\u9003\u5f97\u4e0d\u77e5\u53bb\u5411\u3002\u53ea\u542c\u201c\u554a\u54df\uff0c\u554a\u54df\uff01\u201d\u7830\u3001\u7830\u3001\u7830\u6570\u54cd\uff0c\u5c4b\u9876\u8dcc\u4e0b\u4e09\u540d\u536b\u58eb\u6765\uff0c\u5747\u662f\u4f01\u56fe\u963b\u62e6\u6c64\u6c9b\u800c\u88ab\u4ed6\u4e00\u4e00\u523a\u843d\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u53eb\u9053\uff1a\u201c\u6bd2\u6b7b\u798f\u5927\u5e05\u7684\u51f6\u624b\uff0c\u4f60\u4eec\u600e\u5730\u4e0d\u6349\uff1f\u201d\u4f17\u536b\u58eb\u5927\u60ca\uff0c\u90fd\u95ee\uff1a\u201c\u798f\u5927\u5e05\u88ab\u6bd2\u6b7b\u4e86\uff1f\u201d\u7a0b\u7075\u7d20\u4e00\u626f\u5706\u6027\u548c\u80e1\u6590\u7684\u8863\u8896\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5feb\u8d70\uff01\u201d\u4e09\u4eba\u51b2\u5411\u5385\u95e8\u3002 \u3000\u3000\u51fa\u95e8\u4e4b\u9645\uff0c\u80e1\u6590\u548c\u5706\u6027\u4e0d\u81ea\u7981\u90fd\u56de\u8fc7\u5934\u6765\uff0c\u5411\u5c38\u6a2a\u5c31\u5730\u3001\u88ab\u4eba\u8df5\u8e0f\u4e86\u4e00\u9635\u7684\u51e4\u5929\u5357\u770b\u53bb\u3002\u80e1\u6590\u5fc3\u60f3\uff1a\u201c\u4f60\u4e00\u751f\u4f5c\u6076\uff0c\u4eca\u65e5\u7ec8\u906d\u6b64\u62a5\u3002\u201d\u5706\u6027\u7684\u5fc3\u60c5\u5374\u662f\u6742\u4e71\u5f97\u591a\uff1a\u201c\u4f60\u5bb3\u5f97\u6211\u53ef\u601c\u7684\u5988\u5988\u597d\u82e6\u3002\u53ef\u662f\u4f60\u2026\u2026\u4f60\u7ec8\u7a76\u662f\u6211\u4eb2\u751f\u7684\u7239\u7239\u3002\u201d \u3000\u3000\u4e09\u4eba\u5954\u51fa\u5927\u95e8\uff0c\u51e0\u540d\u536b\u58eb\u4e0a\u6765\u62e6\u963b\u3002\u5706\u6027\u6325\u8f6f\u97ad\u5377\u5012\u4e00\u4eba\uff0c\u80e1\u6590\u5de6\u638c\u62cd\u5728\u4e00\u4eba\u80a9\u5934\uff0c\u638c\u529b\u4e00\u5410\uff0c\u5c06\u90a3\u536b\u58eb\u9707\u51fa\u6570\u4e08\uff0c\u8ddf\u7740\u53f3\u811a\u53cd\u8e22\uff0c\u53c8\u8e22\u98de\u4e86\u4e00\u540d\u536b\u58eb\u3002 \u3000\u3000\u6b64\u523b\u5929\u5df2\u5927\u660e\uff0c\u5e9c\u95e8\u5916\u63f4\u5175\u9646\u7eed\u8d76\u5230\u3002\u4e09\u4eba\u907f\u5165\u4e86\u4e00\u6761\u5c0f\u80e1\u540c\u4e2d\u3002\u80e1\u6590\u9053\uff1a\u201c\u9a6c\u59d1\u5a18\u5931\u4e86\u7231\u5b50\uff0c\u4e0d\u77e5\u5982\u4f55\uff1f\u201d\u5706\u6027\u9053\uff1a\u201c\u90a3\u59d3\u8521\u7684\u8001\u5934\u6d3e\u4eba\u5c06\u9a6c\u59d1\u5a18\u548c\u4e24\u4e2a\u5b69\u513f\u9001\u7ed9\u798f\u5eb7\u5b89\uff0c\u6211\u9014\u4e2d\u62e6\u622a\uff0c\u4e00\u4eba\u96be\u4ee5\u5206\u8eab\uff0c\u53ea\u6551\u4e86\u9a6c\u59d1\u5a18\u51fa\u6765\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u90a3\u597d\u6781\u4e86\u3002\u591a\u8c22\u4f60\u5566\uff01\u201d \u3000\u3000\u5706\u6027\u9053\uff1a\u201c\u6211\u5c06\u9a6c\u59d1\u5a18\u5b89\u7f6e\u5728\u57ce\u897f\u90ca\u5916\u4e00\u6240\u7834\u5e99\u4e4b\u4e2d\uff0c\u5f80\u8fd4\u8f6c\u6298\uff0c\u7531\u6b64\u5230\u5f97\u8fdf\u4e86\u3002\u201d\u80e1\u6590\u6c89\u541f\u9053\uff1a\u201c\u90a3\u8521\u5a01\u4e0d\u77e5\u5982\u4f55\u5f97\u6089\u9a6c\u59d1\u5a18\u7684\u771f\u76f8\uff0c\u96be\u9053\u662f\u6211\u4eec\u9732\u4e86\u7834\u7efd\u4e48\uff1f\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u5b9a\u662f\u4ed6\u5077\u5077\u53bb\u67e5\u95ee\u9a6c\u59d1\u5a18\u3002\u9a6c\u59d1\u5a18\u660f\u660f\u6c89\u6c89\u4e4b\u4e2d\uff0c\u4fbf\u8bf4\u4e86\u51fa\u6765\u3002\u201d \u3000\u3000\u80e1\u6590\u9053\uff1a\u201c\u5fc5\u662f\u5982\u6b64\u3002\u798f\u5eb7\u5b89\u5728\u4f1a\u4e2d\u5012\u6ca1\u4e0b\u4ee4\u6349\u6211\u3002\u201d\u5706\u6027\u9053\uff1a\u201c\u82e5\u4e0d\u662f\u7a0b\u5bb6\u59b9\u5b50\u65bd\u8fd9\u5de7\u8ba1\uff0c\u53ea\u6015\u4f60\u96be\u4ee5\u5e73\u5b89\u51fa\u6b64\u5e9c\u95e8\u3002\u201d\u80e1\u6590\u70b9\u4e86\u70b9\u5934\u9053\uff1a\u201c\u54b1\u4eec\u4eca\u65e5\u641e\u6563\u798f\u5eb7\u5b89\u7684\u5927\u4f1a\uff0c\u6559\u4ed6\u56fe\u8c0b\u6210\u7a7a\uff0c\u53ea\u53ef\u60dc\u8ba9\u6c64\u6c9b\u9003\u4e86\u3002\u201d\u8f6c\u5934\u5bf9\u5706\u6027\u9053\uff1a\u201c\u8fd9\u6076\u8d3c\u8eab\u8d25\u540d\u88c2\uff0c\u59d1\u5a18\u2026\u2026\u4f60\u7684\u5927\u4ec7\u5df2\u62a5\u4e86\u4e00\u534a\uff0c\u54b1\u4eec\u5408\u529b\u627e\u4ed6\uff0c\u7ec8\u4e0d\u6210\u4ed6\u80fd\u9003\u5230\u5929\u8fb9\u3002\u201d \u3000\u3000\u5706\u6027\u9eef\u7136\u4e0d\u8bed\uff0c\u5fc3\u60f3\u6211\u662f\u51fa\u5bb6\u4eba\uff0c\u73b0\u4e0b\u8eab\u5206\u5df2\u663e\uff0c\u5c82\u80fd\u518d\u957f\u65f6\u8ddf\u4f60\u5728\u4e00\u8d77\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u5c11\u65f6\u57ce\u95e8\u4e00\u95ed\uff0c\u5230\u5904\u76d8\u67e5\uff0c\u518d\u8981\u51fa\u57ce\u4fbf\u96be\u4e86\u3002\u54b1\u4eec\u8fd8\u662f\u8d76\u7d27\u51fa\u57ce\u3002\u201d \u3000\u3000\u5f53\u4e0b\u4e09\u4eba\u56de\u5230\u4e0b\u5904\u53d6\u4e86\u968f\u8eab\u7269\u54c1\uff0c\u7275\u4e86\u9a86\u51b0\u6240\u8d60\u7684\u767d\u9a6c\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u7b11\u9053\uff1a\u201c\u80e1\u5927\u7237\uff0c\u4f60\u8d62\u6765\u7684\u8fd9\u6240\u5927\u5b85\uff0c\u53ea\u597d\u8fd8\u7ed9\u90a3\u4f4d\u5468\u5927\u4eba\u5566\u3002\u201d\u80e1\u6590\u7b11\u9053\uff1a\u201c\u4ed6\u5e2e\u4e86\u54b1\u4eec\u4e0d\u5c11\u5fd9\uff0c\u4e14\u8ba9\u4ed6\u5347\u5b98\u4e4b\u540e\uff0c\u518d\u53d1\u7b14\u8d22\u3002\u201d\u4ed6\u867d\u5f3a\u4f5c\u7b11\u8bed\uff0c\u4f46\u76ee\u5149\u59cb\u7ec8\u4e0d\u6562\u548c\u5706\u6027\u76f8\u63a5\u3002 \u3000\u3000\u4e09\u4eba\u77e5\u9053\u8ffd\u5175\u4e0d\u4e45\u4fbf\u5230\uff0c\u4e0d\u6562\u5728\u5b85\u4e2d\u591a\u4f5c\u9017\u7559\uff0c\u8d76\u5230\u57ce\u95e8\uff0c\u5e78\u597d\u95ed\u57ce\u4e4b\u4ee4\u5c1a\u672a\u4f20\u5230\u3002\u51fa\u5f97\u57ce\u6765\uff0c\u7531\u5706\u6027\u5e26\u8def\uff0c\u6765\u8eab\u9a6c\u6625\u82b1\u5b89\u8eab\u7684\u7834\u5e99\u3002 \u3000\u3000\u90a3\u5ea7\u5e99\u5b87\u8fdc\u79bb\u5927\u8def\uff0c\u6b8b\u74e6\u9893\u57a3\uff0c\u5341\u5206\u7834\u8d25\uff0c\u5927\u6bbf\u4e0a\u7684\u795e\u50cf\u9752\u9762\u51f9\u9996\uff0c\u8170\u56f4\u6811\u53f6\uff0c\u624b\u91cc\u62ff\u4e86\u4e00\u675f\u9752\u8349\u653e\u5728\u53e3\u4e2d\u4f5c\u5480\u56bc\u4e4b\u72b6\uff0c\u539f\u6765\u662f\u5c1d\u767e\u8349\u7684\u795e\u519c\u6c0f\u3002\u5706\u6027\u9053\uff1a\u201c\u7a0b\u5bb6\u59b9\u5b50\uff0c\u5230\u4e86\u4f60\u8001\u5bb6\u6765\u5566\uff0c\u8fd9\u662f\u5ea7\u836f\u738b\u5e99\u3002\u201d \u3000\u3000\u4e09\u4eba\u8d70\u8fdb\u53a2\u623f\uff0c\u53ea\u89c1\u9a6c\u6625\u82b1\u5367\u5728\u7095\u4e0a\u7684\u7a3b\u8349\u4e4b\u4e2d\uff0c\u6c14\u606f\u5944\u5944\uff0c\u89c1\u4e86\u4e09\u4eba\u4e5f\u4e0d\u76f8\u8bc6\uff0c\u53ea\u662f\u4e0d\u4f4f\u53e3\u7684\u4f4e\u58f0\u53eb\u5524\uff1a\u201c\u6211\u7684\u5b69\u513f\u5462\uff0c\u6211\u7684\u5b69\u513f\u5462\uff1f\u201d \u3000\u3000\u7a0b\u7075\u7d20\u642d\u4e86\u642d\u5979\u7684\u8109\uff0c\u7ffb\u5f00\u5979\u773c\u76ae\u77a7\u4e86\u77a7\u3002\u4e09\u4eba\u6084\u6084\u9000\u51fa\uff0c\u56de\u5230\u6bbf\u4e0a\u3002\u7a0b\u7075\u7d20\u4f4e\u58f0\u9053\uff1a\u201c\u4e0d\u6210\u5566\uff01\u5979\u53d7\u4e86\u9707\u8361\uff0c\u53c8\u5403\u60ca\u5413\uff0c\u518d\u52a0\u5931\u4e86\u5b69\u5b50\uff0c\u4e09\u4ef6\u4e8b\u5939\u653b\uff0c\u5df2\u6d3b\u4e0d\u5230\u660e\u65e5\u6b64\u523b\u3002\u4fbf\u662f\u6211\u5e08\u7236\u590d\u751f\uff0c\u53ea\u6015\u4e5f\u5df2\u6551\u5979\u4e0d\u5f97\u3002\u201d \u3000\u3000\u80e1\u6590\u77a7\u4e86\u9a6c\u6625\u82b1\u7684\u60c5\u72b6\uff0c\u4fbf\u662f\u7a0b\u7075\u7d20\u4e0d\u8bf4\uff0c\u4e5f\u77e5\u5df2\u662f\u547d\u5728\u9877\u523b\uff0c\u60f3\u8d77\u5546\u5bb6\u5821\u4e2d\u5979\u6614\u65e5\u76f8\u5f85\u4e4b\u60c5\uff0c\u4e0d\u7981\u6014\u6014\u7684\u6d41\u4e0b\u6cea\u6765\u3002\u4ed6\u81ea\u5728\u798f\u5eb7\u5b89\u5e9c\u4e2d\u89c1\u5230\u8881\u7d2b\u8863\u6210\u4e86\u5c3c\u59d1\u5706\u6027\uff0c\u5fc3\u4e2d\u4e00\u76f4\u90c1\u90c1\uff0c\u6b64\u523b\u773c\u6cea\u4e00\u6d41\uff0c\u89e6\u52a8\u5fc3\u4e8b\uff0c\u7adf\u662f\u518d\u4e5f\u5fcd\u8010\u4e0d\u4f4f\uff0c\u545c\u545c\u54bd\u54bd\u7684\u54ed\u4e86\u8d77\u6765\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u548c\u5706\u6027\u5982\u4f55\u4e0d\u660e\u767d\u4ed6\u56e0\u4f55\u4f24\u5fc3\uff1f\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u6211\u518d\u53bb\u77a7\u77a7\u9a6c\u59d1\u5a18\u3002\u201d\u7f13\u6b65\u8d70\u8fdb\u53a2\u623f\u3002 \u3000\u3000\u5706\u6027\u7ed9\u4ed6\u8fd9\u4e48\u4e00\u54ed\uff0c\u773c\u5708\u4e5f\u65e9\u7ea2\u4e86\uff0c\u98a4\u58f0\u8bf4\u9053\uff1a\u201c\u80e1\u5927\u54e5\uff0c\u591a\u8c22\u4f60\u5f85\u6211\u7684\u4e00\u7247\u2026\u2026\u4e00\u7247\u2026\u2026\u201d\u8bf4\u5230\u8fd9\u91cc\uff0c\u4e0d\u77e5\u5982\u4f55\u518d\u63a5\u7eed\u4e0b\u53bb\u3002 \u3000\u3000\u80e1\u6590\u6cea\u773c\u6a21\u7cca\u7684\u62ac\u8d77\u5934\u6765\uff0c\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u96be\u9053\u4e0d\u80fd\u2026\u2026\u4e0d\u80fd\u8fd8\u4fd7\u5417\uff1f\u5f85\u6740\u4e86\u90a3\u59d3\u6c64\u7684\uff0c\u62a5\u4e86\u7236\u6bcd\u5927\u4ec7\uff0c\u4e0d\u7528\u518d\u505a\u5c3c\u59d1\u4e86\u3002\u201d \u3000\u3000\u5706\u6027\u6447\u5934\u9053\uff1a\u201c\u5343\u4e07\u522b\u8bf4\u8fd9\u6837\u4eb5\u6e0e\u6211\u4f5b\u7684\u8bdd\u3002\u6211\u5f53\u5e74\u5bf9\u5e08\u7236\u7acb\u4e0b\u91cd\u8a93\uff0c\u7688\u4f9d\u4f5b\u7956\u3002\u8eab\u5165\u7a7a\u95e8\u4e4b\u4eba\uff0c\u518d\u8d77\u4ed6\u5ff5\uff0c\u5df2\u662f\u72af\u6212\uff0c\u4f55\u51b5\u2026\u2026\u4f55\u51b5\u5176\u4ed6\uff1f\u201d\u8bf4\u7740\u957f\u957f\u53f9\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u4e24\u4eba\u5446\u5bf9\u534a\u664c\uff0c\u5fc3\u4e2d\u5747\u6709\u5343\u8a00\u4e07\u8bed\uff0c\u5374\u4e0d\u77e5\u4ece\u4f55\u8bf4\u8d77\u3002 \u3000\u3000\u5706\u6027\u4f4e\u58f0\u9053\uff1a\u201c\u7a0b\u59d1\u5a18\u4eba\u5f88\u597d\uff0c\u4f60\u8981\u597d\u597d\u5f85\u5979\u3002\u4f60\u4ee5\u540e\u522b\u518d\u60f3\u7740\u6211\uff0c\u6211\u4e5f\u6c38\u8fdc\u4e0d\u4f1a\u518d\u8bb0\u5230\u4f60\u3002\u201d \u3000\u3000\u80e1\u6590\u5fc3\u5982\u5200\u5272\uff0c\u9053\uff1a\u201c\u4e0d\uff0c\u6211\u6c38\u8fdc\u6c38\u8fdc\u8981\u8bb0\u7740\u4f60\uff0c\u8bb0\u7740\u4f60\u3002\u201d \u3000\u3000\u5706\u6027\u9053\uff1a\u201c\u5f92\u7136\u81ea\u82e6\uff0c\u590d\u6709\u4f55\u76ca\uff1f\u201d\u4e00\u54ac\u7259\uff0c\u8f6c\u8eab\u8d70\u51fa\u5e99\u95e8\u3002 \u3000\u3000\u80e1\u6590\u8ffd\u4e86\u51fa\u53bb\uff0c\u98a4\u58f0\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u5230\u54ea\u91cc\u53bb\uff1f\u201d\u5706\u6027\u9053\uff1a\u201c\u4f60\u4f55\u5fc5\u7ba1\u6211\uff1f\u6b64\u540e\u4fbf\u5982\u4e00\u5e74\u4e4b\u524d\uff0c\u4f60\u4e0d\u77e5\u4e16\u4e0a\u6709\u6211\uff0c\u6211\u4e0d\u77e5\u4e16\u4e0a\u6709\u4f60\uff0c\u5c82\u4e0d\u5e72\u51c0\uff1f\u201d \u3000\u3000\u80e1\u6590\u4e00\u5446\uff0c\u53ea\u89c1\u5979\u98d8\u7136\u8fdc\u53bb\uff0c\u7adf\u662f\u59cb\u7ec8\u6ca1\u8f6c\u5934\u56de\u987e\u3002\u80e1\u6590\u8eab\u5b50\u6447\u6643\uff0c\u7ad9\u7acb\u4e0d\u5b9a\uff0c\u5750\u5012\u5728\u5e99\u95e8\u5916\u7684\u4e00\u5757\u5927\u77f3\u4e4b\u4e0a\uff0c\u51dd\u671b\u7740\u5706\u6027\u6240\u53bb\u4e4b\u5904\uff0c\u552f\u89c1\u4e00\u6761\u8352\u8349\u5c0f\u8def\uff0c\u9ec4\u6c99\u4e0a\u5370\u7740\u5979\u6d45\u6d45\u7684\u8db3\u5370\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u4e00\u7247\u7a7a\u767d\uff0c\u4f3c\u4e4e\u5728\u60f3\u5343\u767e\u79cd\u7269\u4e8b\uff0c\u5374\u53c8\u4f3c\u4ec0\u4e48\u4e5f\u4e0d\u60f3\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u5c11\u65f6\u5019\uff0c\u5ffd\u542c\u5f97\u524d\u9762\u5c0f\u8def\u4e0a\u9690\u9690\u4f20\u6765\u4e00\u9635\u9a6c\u8e44\u58f0\u3002\u80e1\u6590\u4e00\u8dc3\u800c\u8d77\uff0c\u5fc3\u4e2d\u7b2c\u4e00\u4e2a\u5ff5\u5934\u4fbf\u662f\uff1a\u201c\u5979\u53c8\u56de\u6765\u4e86\u3002\u201d \u3000\u3000\u4f46\u7acb\u5373\u77e5\u9053\u662f\u7a7a\u60f3\uff0c\u5706\u6027\u53bb\u65f6\u5e76\u672a\u9a91\u9a6c\uff0c\u4f55\u51b5\u6240\u6765\u7684\u53c8\u975e\u4e00\u4e58\u4e00\u9a91\u3002\u4f46\u542c\u8e44\u58f0\u5e76\u975e\u5954\u9a70\u751a\u6025\uff0c\u4f3c\u4e4e\u4e5f\u4e0d\u662f\u8ffd\u5175\u3002 \u3000\u3000\u8fc7\u4e86\u7247\u65f6\uff0c\u8e44\u58f0\u6e10\u8fd1\uff0c\u4e5d\u9a91\u9a6c\u81ea\u897f\u800c\u6765\u3002\u80e1\u6590\u51dd\u76ee\u4e00\u770b\uff0c\u53ea\u89c1\u9a6c\u4e0a\u4e00\u4eba\u76f8\u8c8c\u4fca\u79c0\uff0c\u56db\u5341\u5c81\u4e0d\u5230\u5e74\u7eaa\uff0c\u5374\u4e0d\u662f\u798f\u5eb7\u5b89\u662f\u8c01\uff1f \u3000\u3000\u80e1\u6590\u4e00\u89c1\u798f\u5eb7\u5b89\uff0c\u5fc3\u4e0b\u72c2\u6012\u4e0d\u53ef\u6291\u6b62\uff0c\u6697\u60f3\uff1a\u201c\u6b64\u4eba\u6267\u638c\u5929\u4e0b\u5175\u9a6c\u5927\u6743\u3002\u6e05\u653f\u5e9c\u6b3a\u538b\u6c49\u4eba\uff0c\u9664\u4e86\u5f53\u4eca\u7687\u5e1d\u4e7e\u9686\u4e4b\u5916\uff0c\u7f6a\u9b41\u7978\u9996\uff0c\u4fbf\u8981\u6570\u5230\u6b64\u4eba\u4e86\u3002\u4ed6\u5bf9\u9a6c\u59d1\u5a18\u8d1f\u60c5\u8584\u4e49\uff0c\u5bb3\u5f97\u5979\u5bb6\u7834\u4eba\u4ea1\uff0c\u547d\u5728\u9877\u523b\u3002\u4ed6\u4ee5\u5175\u90e8\u5c1a\u4e66\u4e4b\u5c0a\uff0c\u5ffd\u7136\u6765\u5230\u90ca\u5916\uff0c\u968f\u8eab\u4f8d\u4ece\u81ea\u5fc5\u90fd\u662f\u4e00\u7b49\u4e00\u7684\u9ad8\u624b\uff0c\u6211\u867d\u7136\u53ea\u6709\u4e8c\u59b9\u76f8\u52a9\uff0c\u4e5f\u8981\u632b\u632b\u4ed6\u7684\u5a01\u98ce\u3002\u7eb5\u4f7f\u6740\u4ed6\u4e0d\u4e86\uff0c\u4fbf\u662f\u5413\u4ed6\u4e00\u5413\uff0c\u4e5f\u662f\u597d\u7684\u3002\u201d \u3000\u3000\u5f53\u4e0b\u8d70\u5230\u8def\u5fc3\uff0c\u53cc\u624b\u5728\u8170\u95f4\u4e00\u53c9\uff0c\u6012\u76ee\u5411\u7740\u798f\u5eb7\u5b89\u659c\u89c6\u3002 \u3000\u3000\u4e58\u9a6c\u7684\u4e5d\u4eba\u5ffd\u89c1\u6709\u4eba\u62e6\u8def\uff0c\u4e00\u9f50\u52d2\u9a6c\u3002 \u3000\u3000\u4f46\u89c1\u798f\u5eb7\u5b89\u4e0d\u52a8\u58f0\u8272\uff0c\u663e\u662f\u6709\u6043\u65e0\u6050\uff0c\u53ea\u8bf4\u58f0\uff1a\u201c\u52b3\u9a7e\uff01\u201d \u3000\u3000\u80e1\u6590\u621f\u6307\u9a82\u9053\uff1a\u201c\u4f60\u505a\u7684\u597d\u4e8b\uff01\u4f60\u8fd8\u8bb0\u5f97\u9a6c\u6625\u82b1\u4e48\uff1f\u201d \u3000\u3000\u798f\u5eb7\u5b89\u8138\u8272\u5fe7\u90c1\uff0c\u4f3c\u6709\u6ee1\u6000\u5fc3\u4e8b\uff0c\u6de1\u6de1\u7684\u9053\uff1a\u201c\u9a6c\u6625\u82b1\uff1f\u6211\u4e0d\u8bb0\u5f97\u662f\u8c01\u3002\u201d \u3000\u3000\u80e1\u6590\u66f4\u52a0\u6124\u6012\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u563f\u563f\uff0c\u4f60\u8ddf\u9a6c\u6625\u82b1\u751f\u4e0b\u4e24\u4e2a\u513f\u5b50\uff0c\u4e0d\u8bb0\u5f97\u4e86\u4e48\uff1f\u4f60\u6d3e\u4eba\u6740\u6b7b\u5979\u7684\u4e08\u592b\u5f90\u94ee\uff0c\u4e0d\u8bb0\u5f97\u4e86\u4e48\uff1f\u4f60\u6bcd\u5b50\u4e24\u4eba\u4e32\u901a\uff0c\u4e0b\u6bd2\u5bb3\u6b7b\u4e86\u5979\uff0c\u4e5f\u4e0d\u8bb0\u5f97\u4e86\u4e48\uff1f\u201d \u3000\u3000\u798f\u5eb7\u5b89\u7f13\u7f13\u6447\u4e86\u6447\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u5c0a\u9a7e\u8ba4\u9519\u4eba\u4e86\u3002\u201d\u4ed6\u8eab\u65c1\u4e00\u4e2a\u72ec\u81c2\u9053\u4eba\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8fd9\u662f\u4e2a\u75af\u5b50\uff0c\u5728\u8fd9\u91cc\u80e1\u8bf4\u516b\u9053\uff0c\u4ec0\u4e48\u9a6c\u6625\u82b1\u3001\u725b\u79cb\u82b1\u3002\u201d \u3000\u3000\u80e1\u6590\u66f4\u4e0d\u6253\u8bdd\uff0c\u7eb5\u8eab\u8dc3\u8d77\uff0c\u5de6\u62f3\u4fbf\u5411\u798f\u5eb7\u5b89\u9762\u95e8\u6253\u53bb\u3002\u8fd9\u4e00\u62f3\u4e43\u662f\u865a\u52bf\uff0c\u4e0d\u5f85\u798f\u5eb7\u5b89\u4f38\u81c2\u6321\u67b6\uff0c\u53f3\u624b\u4e94\u6307\u6210\u864e\u722a\u4e4b\u5f62\uff0c\u62ff\u5411\u4ed6\u7684\u80f8\u53e3\u3002\u4ed6\u77e5\u9053\u5982\u679c\u4e00\u51fb\u4e0d\u4e2d\uff0c\u798f\u5eb7\u5b89\u5de6\u53f3\u536b\u58eb\u7acb\u65f6\u4fbf\u4f1a\u51fa\u624b\uff0c\u56e0\u6b64\u8fd9\u4e00\u62ff\u65e2\u5feb\u4e14\u51c6\uff0c\u6709\u5982\u661f\u9a70\u7535\u63a3\uff0c\u5b9e\u662f\u4ed6\u751f\u5e73\u6b66\u5b66\u7684\u529b\u4f5c\uff0c\u6599\u60f3\u798f\u5eb7\u5b89\u8eab\u65c1\u7684\u536b\u58eb\u672c\u4e8b\u518d\u9ad8\uff0c\u4e5f\u51b3\u8ba1\u4e0d\u53ca\u62a2\u4e0a\u6765\u5316\u89e3\u8fd9\u4e00\u62db\u8fc5\u96f7\u4e0d\u53ca\u63a9\u8033\u7684\u864e\u722a\u64d2\u62ff\u3002 \u3000\u3000\u798f\u5eb7\u5b89\u201c\u566b\u201d\u7684\u4e00\u58f0\uff0c\u5f84\u4e0d\u7406\u4f1a\u4ed6\u7684\u5de6\u62f3\uff0c\u53f3\u624b\u98df\u6307\u548c\u4e2d\u6307\u9661\u7136\u4f38\u51fa\uff0c\u6210\u526a\u5200\u4e4b\u5f62\uff0c\u70b9\u5411\u4ed6\u53f3\u8155\u7684\u201c\u4f1a\u5b97\u7a74\u201d\u548c\u201c\u9633\u6c60\u7a74\u201d\uff0c\u51fa\u624b\u4e4b\u5feb\uff0c\u6307\u6cd5\u4e4b\u5947\uff0c\u80e1\u6590\u751f\u5e73\u4ece\u6240\u672a\u89c1\u3002 \u3000\u3000\u5728\u8fd9\u7535\u5149\u77f3\u706b\u822c\u7684\u4e00\u77ac\u4e4b\u95f4\uff0c\u80e1\u6590\u5fc3\u5934\u731b\u5730\u4e00\u9707\uff0c\u7acb\u5373\u53d8\u62db\uff0c\u4e94\u6307\u4e00\u52fe\uff0c\u4fbf\u53bb\u6293\u4ed6\u4e24\u6839\u70b9\u7a74\u7684\u624b\u6307\uff0c\u53ea\u6d88\u6293\u4f4f\u4e86\u4e00\u626d\uff0c\u975e\u6559\u4ed6\u6307\u9aa8\u6298\u65ad\u4e0d\u53ef\u3002\u5c82\u77e5\u798f\u5eb7\u5b89\u6b66\u529f\u4fca\u6781\uff0c\u7adf\u4e0d\u7f29\u624b\uff0c\u5176\u4f59\u4e09\u6839\u624b\u6307\u4e00\u4f38\uff0c\u7ffb\u6210\u638c\u5f62\uff0c\u624b\u81c2\u4e0d\u52a8\uff0c\u638c\u529b\u5df2\u5410\u3002 \u3000\u3000\u51e1\u662f\u4f38\u62f3\u53d1\u638c\uff0c\u5fc5\u5148\u540e\u7f29\uff0c\u624d\u884c\u51fa\u51fb\uff0c\u4f46\u798f\u5eb7\u5b89\u8fd9\u4e00\u638c\u624b\u81c2\u5df2\u4f38\u5728\u5916\uff0c\u7adf\u4e0d\u5f2f\u81c2\uff0c\u638c\u529b\u4fbf\u5373\u9001\u51fa\uff0c\u62db\u6570\u56fa\u662f\u5947\u5e7b\u4e4b\u6781\uff0c\u5185\u529b\u4ea6\u662f\u96c4\u6d51\u65e0\u6bd4\u3002 \u3000\u3000\u80e1\u6590\u5927\u9a87\uff0c\u8fd9\u65f6\u8eab\u5f53\u865a\u7a7a\uff0c\u65e0\u6cd5\u501f\u529b\uff0c\u5f53\u4e0b\u5de6\u638c\u6025\u62cd\uff0c\u7830\u7684\u4e00\u54cd\uff0c\u548c\u798f\u5eb7\u5b89\u53cc\u638c\u76f8\u4ea4\uff0c\u5239\u90a3\u95f4\u53ea\u611f\u80f8\u53e3\u6c14\u8840\u7ffb\u817e\uff0c\u501f\u52bf\u5411\u540e\u98d8\u51fa\u4e24\u4e08\u6709\u4f59\u3002\u4ed6\u5438\u4e00\u53e3\u6c14\uff0c\u5410\u4e00\u53e3\u6c14\uff0c\u4fbf\u5728\u534a\u7a7a\u4e4b\u4e2d\uff0c\u6c14\u606f\u5df2\u7136\u8c03\u5300\uff0c\u8f7b\u98d8\u98d8\u7684\u843d\u5728\u5730\u4e0b\uff0c\u4ecd\u662f\u795e\u5b8c\u6c14\u8db3\uff0c\u7a33\u7a33\u7ad9\u5b9a\u3002\u53ea\u542c\u5f97\u516b\u4e5d\u4e2a\u58f0\u97f3\u9f50\u58f0\u559d\u5f69\uff1a\u201c\u597d\uff01\u201d \u3000\u3000\u770b\u90a3\u798f\u5eb7\u5b89\u65f6\uff0c\u4f46\u89c1\u4ed6\u8eab\u5b50\u5fae\u5fae\u4e00\u6643\uff0c\u968f\u5373\u5750\u7a33\uff0c\u8138\u4e0a\u95ea\u8fc7\u4e00\u4e1d\u60ca\u8bb6\uff0c\u7acb\u65f6\u53c8\u56de\u590d\u4e86\u5148\u524d\u90c1\u90c1\u5be1\u6b22\u7684\u795e\u6c14\u3002 \u3000\u3000\u80e1\u6590\u81ea\u7eb5\u8eab\u51fa\u51fb\u81f3\u98d8\u8eab\u843d\u5730\uff0c\u5f53\u771f\u53ea\u662f\u4e00\u970e\u773c\u95f4\uff0c\u53ef\u662f\u8fd9\u4e2d\u95f4\u4e24\u4eba\u865a\u62db\u3001\u64d2\u62ff\u3001\u70b9\u7a74\u3001\u626d\u6307\u3001\u5410\u638c\u3001\u62da\u529b\u3001\u8dc3\u9000\u3001\u8c03\u606f\uff0c\u5b9e\u5df2\u4ea4\u6362\u4e86\u4e03\u516b\u5f0f\u6700\u7cbe\u6df1\u7684\u6b66\u5b66\u53d8\u5316\u3002\u76f8\u8f83\u4e4b\u4e0b\u867d\u662f\u80dc\u8d25\u672a\u5206\uff0c\u4f46\u4e00\u4e2a\u51fa\u5168\u529b\u4ee5\u640f\u51fb\uff0c\u4e00\u4e2a\u968f\u624b\u6325\u9001\uff0c\u6f47\u6d12\u81ea\u5982\uff0c\u80e1\u6590\u663e\u5df2\u8f93\u4e86\u4e00\u7b79\u3002 \u3000\u3000\u80e1\u6590\u4e07\u6599\u4e0d\u5230\u798f\u5eb7\u5b89\u7adf\u6709\u8fd9\u7b49\u7cbe\u6e5b\u8d85\u5999\u7684\u6b66\u529f\uff0c\u6014\u6014\u7684\u7ad9\u7740\uff0c\u5fc3\u4e2d\u53c8\u662f\u60ca\u5947\uff0c\u53c8\u662f\u4f69\u670d\uff0c\u53ef\u53c8\u63a9\u4e0d\u4f4f\u6ee1\u8154\u6124\u6012\u4e4b\u60c5\u3002 \u3000\u3000\u53ea\u542c\u90a3\u72ec\u81c2\u9053\u4eba\u7b11\u9053\uff1a\u201c\u4fca\u5c0f\u5b50\uff0c\u77e5\u9053\u8ba4\u9519\u4eba\u4e86\u5417\uff1f\u8fd8\u4e0d\u78d5\u5934\u8d54\u7f6a\uff1f\u201d \u3000\u3000\u80e1\u6590\u4fa7\u5934\u7ec6\u770b\uff0c\u8fd9\u4eba\u660e\u660e\u662f\u798f\u5eb7\u5b89\uff0c\u53ea\u662f\u88c5\u5f97\u6ee1\u8138\u98ce\u5c18\u4e4b\u8272\uff0c\u53c8\u6362\u4e0a\u4e86\u4e00\u8eab\u655d\u65e7\u8863\u886b\uff0c\u4f46\u59cb\u7ec8\u63a9\u4e0d\u4f4f\u90a3\u80a1\u53d1\u53f7\u65bd\u4ee4\u3001\u7edf\u7387\u8c6a\u96c4\u7684\u5c0a\u8d35\u6c14\u8c61\uff0c\u5982\u679c\u8fd9\u4eba\u76f8\u8c8c\u8ddf\u798f\u5eb7\u5b89\u6781\u50cf\uff0c\u96be\u9053\u8fde\u5927\u5143\u5e05\u7684\u6c14\u5ea6\u98ce\u534e\u4e5f\u5b66\u5f97\u5982\u6b64\u795e\u4f3c\uff1f \u3000\u3000\u80e1\u6590\u5446\u4e86\u4e00\u5446\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u4e00\u5e72\u4eba\u5982\u6b64\u6253\u626e\uff0c\u5fc5\u662f\u53e6\u6709\u9634\u8c0b\uff0c\u6211\u53ef\u4e0d\u4e0a\u8fd9\u4e2a\u5f53\u3002\u201d\u7eb5\u58f0\u53eb\u9053\uff1a\u201c\u798f\u5eb7\u5b89\uff0c\u4f60\u6b66\u529f\u5f88\u597d\uff0c\u6211\u6bd4\u4f60\u4e0d\u4e0a\u3002\u53ef\u662f\u4f60\u505a\u4e0b\u8fd9\u8bb8\u591a\u4f24\u5929\u5bb3\u7406\u4e4b\u4e8b\uff0c\u6211\u660e\u77e5\u4e0d\u654c\uff0c\u7ec8\u662f\u653e\u4f60\u4e0d\u8fc7\uff0c\u4f60\u8bb0\u4f4f\u4e86\u3002\u201d \u3000\u3000\u798f\u5eb7\u5b89\u6de1\u6de1\u7684\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4f60\u6b66\u529f\u5f88\u4fca\u554a\u3002\u6211\u53ef\u4e0d\u662f\u798f\u5eb7\u5b89\u3002\u4f60\u5c0a\u59d3\u5927\u540d\uff1f\u201d\u80e1\u6590\u6012\u9053\uff1a\u201c\u4f60\u8fd8\u88c5\u6a21\u4f5c\u6837\uff0c\u620f\u800d\u4e8e\u6211\uff0c\u96be\u9053\u4f60\u4e0d\u77e5\u9053\u6211\u540d\u5b57\u4e48\uff1f\u201d \u3000\u3000\u798f\u5eb7\u5b89\u8eab\u540e\u4e00\u4e2a\u56db\u5341\u6765\u5c81\u7684\u9ad8\u5927\u6c49\u5b50\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4f60\u6c14\u6982\u5f88\u597d\uff0c\u5f53\u771f\u662f\u5c11\u5e74\u82f1\u96c4\uff0c\u4f69\u670d\u4f69\u670d\u3002\u201d\u80e1\u6590\u5411\u4ed6\u671b\u4e86\u4e00\u773c\uff0c\u4f46\u89c1\u4ed6\u53cc\u76ee\u4e2d\u795e\u5149\u95ea\u70c1\uff0c\u5a01\u98ce\u51db\u51db\uff0c\u663e\u662f\u4e00\u4f4d\u6b66\u529f\u6781\u5f3a\u7684\u9ad8\u624b\uff0c\u5fc3\u4e2d\u6cb9\u7136\u800c\u751f\u94a6\u670d\u4e4b\u5fc3\uff0c\u8bf4\u9053\uff1a\u201c\u9601\u4e0b\u5982\u6b64\u4eba\u624d\uff0c\u4f55\u82e6\u4e3a\u6ee1\u6d32\u8d35\u5b98\u4f5c\u9e70\u72ac\uff1f\u201d\u90a3\u5927\u6c49\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5317\u4eac\u57ce\u8fb9\uff0c\u5929\u5b50\u811a\u4e0b\uff0c\u4f60\u80c6\u6562\u8bf4\u8fd9\u6837\u7684\u8bdd\uff0c\u4e0d\u6015\u6740\u5934\u4e48\uff1f\u201d\u80e1\u6590\u6602\u7136\u9053\uff1a\u201c\u4eca\u65e5\u4e8b\u5df2\u81f3\u6b64\uff0c\u6740\u5934\u4fbf\u6740\uff0c\u53c8\u6015\u600e\u5730\uff1f\u201d \u3000\u3000\u8981\u77e5\u80e1\u6590\u672c\u6765\u751f\u6027\u8c28\u7ec6\uff0c\u7edd\u975e\u83bd\u649e\u4e4b\u5f92\uff0c\u53ea\u662f\u4ed6\u7a76\u5c5e\u5c11\u5e74\uff0c\u8840\u6c14\u65b9\u521a\uff0c\u773c\u770b\u9a6c\u6625\u82b1\u88ab\u798f\u5eb7\u5b89\u5bb3\u5f97\u8fd9\u7b49\u60e8\u6cd5\uff0c\u6fc0\u52a8\u4e86\u4fa0\u4e49\u4e4b\u5fc3\uff0c\u4e00\u5207\u5168\u8c41\u4e86\u51fa\u53bb\uff0c\u4ec0\u4e48\u4e5f\u4e0d\u7406\u4f1a\u4e86\u3002 \u3000\u3000\u4e5f\u8bf4\u4e0d\u5b9a\u7531\u4e8e\u4ed6\u5ff5\u5ff5\u4e0d\u5fd8\u7684\u7f8e\u4e3d\u59d1\u5a18\u5ffd\u7136\u4e4b\u95f4\u53d8\u6210\u4e86\u4e00\u4e2a\u5c3c\u59d1\uff0c\u4ee4\u4ed6\u89c9\u5f97\u4e16\u60c5\u60e8\u9177\uff0c\u4eba\u751f\u60b2\u82e6\uff0c\u8981\u5927\u95f9\u4fbf\u5927\u95f9\u4e00\u573a\uff0c\u6700\u591a\u4e5f\u4e0d\u8fc7\u6740\u5934\u4e27\u547d\uff0c\u53c8\u6709\u4ec0\u4e48\u5927\u4e0d\u4e86\uff1f \u3000\u3000\u4ed6\u624b\u6309\u5200\u67c4\uff0c\u6012\u76ee\u6a2a\u89c6\u7740\u8fd9\u9a6c\u4e0a\u4e5d\u4eba\u3002\u53ea\u89c1\u90a3\u72ec\u81c2\u9053\u4eba\u4e00\u7eb5\u4e0b\u9a6c\uff0c\u4e5f\u6ca1\u89c1\u4ed6\u4f38\u624b\u52a8\u81c2\uff0c\u53ea\u662f\u773c\u524d\u9752\u5149\u4e00\u95ea\uff0c\u4ed6\u624b\u4e2d\u5df2\u591a\u4e86\u4e00\u67c4\u957f\u5251\uff0c\u62d4\u5251\u624b\u6cd5\u4e4b\u5feb\uff0c\u5b9e\u662f\u751f\u5e73\u4ece\u6240\u672a\u89c1\u3002 \u3000\u3000\u80e1\u6590\u6697\u6697\u5403\u60ca\uff1a\u201c\u600e\u5730\u798f\u5eb7\u5b89\u624b\u4e0b\u6536\u7f57\u4e86\u8fd9\u8bb8\u591a\u9ad8\u624b\u4eba\u7269\uff1f\u6628\u65e5\u638c\u95e8\u4eba\u5927\u4f1a\u4e4b\u4e2d\uff0c\u5982\u6709\u8fd9\u4e9b\u4eba\u5728\u573a\u9547\u538b\uff0c\u8bf4\u4e0d\u5b9a\u4fbf\u95f9\u4e0d\u6210\u4e71\u5b50\u3002\u201d\u4ed6\u751f\u6015\u72ec\u81c2\u9053\u4eba\u633a\u5251\u523a\u6765\uff0c\u659c\u8eab\u7565\u95ea\uff0c\u62d4\u5200\u5728\u624b\u3002\u90a3\u9053\u4eba\u7b11\u9053\uff1a\u201c\u770b\u5251\uff01\u201d\u4f46\u89c1\u9752\u5149\u95ea\u52a8\uff0c\u5728\u4e00\u77ac\u4e4b\u95f4\uff0c\u7adf\u5df2\u8fde\u523a\u516b\u5251\u3002 \u3000\u3000\u8fd9\u516b\u5251\u8fc5\u6377\u65e0\u6bd4\uff0c\u80e1\u6590\u90a3\u91cc\u77a7\u5f97\u6e05\u5251\u52bf\u6765\u8def\uff0c\u53ea\u5f97\u987a\u52bf\u6325\u5200\u62db\u67b6\u3002\u4ed6\u5bb6\u4f20\u7684\u80e1\u5bb6\u5200\u6cd5\u5b9e\u662f\u975e\u540c\u5c0f\u53ef\uff0c\u90a3\u72ec\u81c2\u9053\u4eba\u516b\u5251\u867d\u5feb\uff0c\u8fd8\u662f\u4e00\u4e00\u88ab\u4ed6\u6321\u4f4f\u3002\u516b\u5251\u6765\uff0c\u516b\u5200\u6321\uff0c\u5f53\u5f53\u5f53\u5f53\u5f53\u5f53\u5f53\u5f53\uff0c\u8fde\u54cd\u516b\u4e0b\uff0c\u6e05\u6670\u7e41\u5bc6\uff0c\u5e72\u51c0\u5229\u843d\uff0c\u80e1\u6590\u867d\u7136\u7565\u611f\u624b\u5fd9\u811a\u4e71\uff0c\u4f46\u7b2c\u4e5d\u5200\u7acb\u5373\u81ea\u5b88\u8f6c\u653b\uff0c\u56de\u5200\u659c\u524a\u51fa\u53bb\u3002\u90a3\u72ec\u81c2\u9053\u4eba\u957f\u5251\u4e00\u63a0\uff0c\u5200\u5251\u7c98\u4f4f\uff0c\u5374\u534a\u70b9\u58f0\u97f3\u4e5f\u4e0d\u53d1\u51fa\u6765\u3002 \u3000\u3000\u9a6c\u4e0a\u8bf8\u4eba\u53c8\u662f\u9f50\u58f0\u559d\u5f69\uff1a\u201c\u597d\u5251\u6cd5\uff0c\u597d\u5200\u6cd5\uff01\u201d \u3000\u3000\u798f\u5eb7\u5b89\u9053\uff1a\u201c\u9053\u957f\uff0c\u8d70\u5427\uff0c\u522b\u591a\u751f\u4e8b\u7aef\u4e86\u3002\u201d\u90a3\u9053\u4eba\u4e0d\u6562\u8fdd\u62d7\u4e3b\u5b50\u4e4b\u8a00\uff0c\u5e94\u9053\uff1a\u201c\u662f\uff01\u201d\u53ef\u662f\u4ed6\u89c1\u80e1\u6590\u5200\u6cd5\u7cbe\u5947\uff0c\u6597\u5f97\u5174\u8d77\uff0c\u9887\u4e3a\u604b\u604b\u4e0d\u820d\uff0c\u7ffb\u8eab\u4e0a\u9a6c\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u5200\u6cd5\u4e0d\u9519\u554a\uff01\u201d\u80e1\u6590\u5fc3\u4e2d\u94a6\u4f69\uff0c\u9053\uff1a\u201c\u597d\u9053\u4eba\uff0c\u4f60\u7684\u5251\u6cd5\u66f4\u597d\uff01\u201d\u4f46\u8ddf\u7740\u51b7\u7b11\u9053\uff1a\u201c\u53ef\u60dc\uff0c\u53ef\u60dc\uff01\u201d \u3000\u3000\u90a3\u9053\u4eba\u77aa\u773c\u9053\uff1a\u201c\u53ef\u60dc\u4ec0\u4e48\uff1f\u6211\u5251\u6cd5\u4e2d\u6709\u4f55\u7834\u7efd\uff1f\u201d\u80e1\u6590\u9053\uff1a\u201c\u53ef\u60dc\u4f60\u5251\u6cd5\u4e2d\u6beb\u65e0\u7834\u7efd\uff0c\u4e3a\u4eba\u5374\u6709\u5927\u5927\u7684\u7834\u7efd\u3002\u4e00\u4e2a\u6b66\u6797\u9ad8\u624b\uff0c\u5374\u53bb\u505a\u6e05\u653f\u5e9c\u8d35\u5b98\u7684\u5974\u624d\u3002\u201d \u3000\u3000\u90a3\u9053\u4eba\u4ef0\u5929\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u9a82\u5f97\u597d\uff0c\u9a82\u5f97\u597d\uff01\u5c0f\u5144\u5f1f\uff0c\u4f60\u6709\u80c6\u5b50\u518d\u8ddf\u6211\u6bd4\u6bd4\u5251\u4e48\uff1f\u201d\u80e1\u6590\u9053\uff1a\u201c\u6709\u4ec0\u4e48\u4e0d\u6562\uff1f\u6700\u591a\u662f\u6bd4\u4f60\u4e0d\u8fc7\uff0c\u7ed9\u4f60\u6740\u4e86\u3002\u201d\u90a3\u9053\u4eba\u9053\uff1a\u201c\u597d\uff0c\u4eca\u665a\u4e09\u66f4\uff0c\u6211\u5728\u9676\u7136\u4ead\u7554\u7b49\u4f60\u3002\u4f60\u8981\u662f\u6015\u4e86\uff0c\u4fbf\u4e0d\u7528\u6765\u3002\u201d \u3000\u3000\u80e1\u6590\u6602\u7136\u9053\uff1a\u201c\u5927\u4e08\u592b\u53ea\u6015\u6b63\u4eba\u541b\u5b50\uff0c\u5c82\u6015\u9e70\u72ac\u5974\u624d\uff01\u201d \u3000\u3000\u90a3\u4e9b\u4eba\u90fd\u662f\u5927\u62c7\u6307\u4e00\u7fd8\uff0c\u559d\u9053\uff1a\u201c\u8bf4\u5f97\u597d\uff01\u201d\u7eb5\u9a6c\u800c\u53bb\uff0c\u6709\u51e0\u4eba\u8fd8\u662f\u4e0d\u4f4f\u7684\u56de\u5934\u3002 \u3000\u3000\u5f53\u80e1\u6590\u548c\u90a3\u72ec\u81c2\u9053\u4eba\u5200\u5251\u76f8\u4ea4\u4e4b\u65f6\uff0c\u7a0b\u7075\u7d20\u5df2\u4ece\u5e99\u4e2d\u51fa\u6765\uff0c\u89c1\u5230\u798f\u5eb7\u5b89\u65f6\u4e5f\u662f\u5927\u4e3a\u5403\u60ca\uff0c\u8fd9\u65f6\u89c1\u4e5d\u4eba\u8fdc\u53bb\uff0c\u8bf4\u9053\uff1a\u201c\u5927\u54e5\uff0c\u600e\u5730\u798f\u5eb7\u5b89\u5230\u4e86\u8fd9\u91cc\uff1f\u4eca\u665a\u4f60\u53bb\u4e0d\u53bb\u9676\u7136\u4ead\u8d74\u7ea6\uff1f\u201d \u3000\u3000\u80e1\u6590\u6c89\u541f\u9053\uff1a\u201c\u96be\u9053\u4ed6\u771f\u7684\u4e0d\u662f\u798f\u5eb7\u5b89\uff1f\u90a3\u51b3\u8ba1\u4e0d\u4f1a\u3002\u6211\u9a82\u4ed6\u90a3\u4e9b\u536b\u58eb\u4f8d\u4ece\u662f\u9e70\u72ac\u5974\u624d\uff0c\u4ed6\u4eec\u600e\u5730\u5e76\u4e0d\u751f\u6c14\uff0c\u53cd\u800c\u8d5e\u6211\u8bf4\u5f97\u597d\uff1f\u201d\u7a0b\u7075\u7d20\u53c8\u95ee\uff1a\u201c\u4eca\u665a\u53bb\u4e0d\u53bb\u8d74\u7ea6\uff1f\u201d\u4fbf\u9053\uff1a\u201c\u81ea\u7136\u53bb\u554a\u3002\u4e8c\u59b9\uff0c\u4f60\u5728\u8fd9\u91cc\u7167\u6599\u9a6c\u59d1\u5a18\u5427\u3002\u201d\u7a0b\u7075\u7d20\u6447\u5934\u9053\uff1a\u201c\u9a6c\u59d1\u5a18\u662f\u6ca1\u4ec0\u4e48\u53ef\u7167\u6599\u7684\u4e86\u3002\u5979\u795e\u667a\u5df2\u5931\uff0c\u652f\u6491\u4e0d\u5230\u660e\u5929\u65e9\u6668\u3002 \u3000\u3000\u4f60\u7ea6\u6597\u5f3a\u654c\uff0c\u6211\u600e\u80fd\u4e0d\u53bb\uff1f\u201d \u3000\u3000\u80e1\u6590\u9053\uff1a\u201c\u4f60\u62c6\u6563\u4e86\u798f\u5eb7\u5b89\u82e6\u5fc3\u7ecf\u8425\u7684\u638c\u95e8\u4eba\u5927\u4f1a\uff0c\u6b64\u523b\u4ed6\u5fc5\u5df2\u67e5\u77e5\u5176\u4e2d\u539f\u59d4\u3002\u4f60\u82e5\u548c\u6211\u540c\u53bb\uff0c\u5c82\u4e0d\u51f6\u9669\uff1f\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u4f60\u5b64\u8eab\u8d74\u654c\uff0c\u6211\u5982\u4f55\u653e\u5fc3\u5f97\u4e0b\uff1f\u6709\u6211\u5728\u4e00\u65c1\u7167\u6599\uff0c\u603b\u662f\u591a\u4e00\u4e2a\u5e2e\u624b\u3002\u201d\u80e1\u6590\u77e5\u5979\u51b3\u5b9a\u4e86\u7684\u4e8b\u65e0\u6cd5\u8fdd\u62d7\uff0c\u8fd9\u4e49\u59b9\u5e74\u7eaa\u5c0f\u5c0f\uff0c\u5fc3\u5fd7\u5b9e\u6bd4\u81ea\u5df1\u575a\u5f3a\u5f97\u591a\uff0c\u4e5f\u53ea\u5f97\u7531\u5979\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u8f7b\u58f0\u95ee\u9053\uff1a\u201c\u8881\u2026\u2026\u8881\u59d1\u5a18\uff0c\u5979\u8d70\u4e86\u5417\uff1f\u201d\u80e1\u6590\u70b9\u70b9\u5934\uff0c\u5fc3\u4e2d\u4e00\u9178\uff0c\u8f6c\u8fc7\u8eab\u6765\uff0c\u8d70\u5165\u5e99\u5185\u3002\u4ed6\u8d70\u8fdb\u53a2\u623f\uff0c\u53ea\u542c\u9a6c\u6625\u82b1\u5fae\u5f31\u7684\u58f0\u97f3\u4e0d\u4f4f\u5728\u53eb\uff1a\u201c\u5b69\u5b50\uff0c\u5b69\u5b50\uff01\u798f\u516c\u5b50\uff0c\u798f\u516c\u5b50\uff0c\u6211\u8981\u6b7b\u4e86\uff0c\u6211\u53ea\u60f3\u518d\u89c1\u4f60\u4e00\u9762\u3002\u201d\u80e1\u6590\u53c8\u662f\u4e00\u9635\u5fc3\u9178\uff1a\u201c\u60c5\u4e4b\u4e3a\u7269\uff0c\u7adf\u662f\u5982\u6b64\u4e0d\u53ef\u7406\u55bb\u3002\u798f\u5eb7\u5b89\u8fd9\u822c\u5f85\u5979\uff0c\u53ef\u662f\u5979\u5728\u4e34\u6b7b\u4e4b\u65f6\uff0c\u8fd8\u662f\u8fd9\u6837\u7684\u5ff5\u5ff5\u4e0d\u5fd8\u4e8e\u4ed6\u3002\u201d \u3000\u3000\u4e24\u4eba\u8d70\u51fa\u6570\u91cc\uff0c\u627e\u5230\u4e00\u5bb6\u519c\u5bb6\uff0c\u4e70\u4e86\u4e9b\u767d\u7c73\u852c\u83dc\uff0c\u505a\u4e86\u996d\u9971\u9910\u4e00\u987f\uff0c\u56de\u6765\u5728\u795e\u519c\u5e99\u4e2d\u966a\u7740\u9a6c\u6625\u82b1\uff0c\u7b49\u5230\u521d\u66f4\u5929\u65f6\uff0c\u4fbf\u5373\u52a8\u8eab\u3002\u80e1\u6590\u548c\u7a0b\u7075\u7d20\u5546\u91cf\uff0c\u798f\u5eb7\u5b89\u624b\u4e0b\u7684\u6b66\u58eb\u9080\u7ea6\u6bd4\u6b66\uff0c\u5b9a\u662f\u4e0d\u6000\u5584\u610f\uff0c\u4e0d\u5982\u65e9\u4e9b\u524d\u5f80\uff0c\u6697\u4e2d\u77a7\u77a7\u4ed6\u4eec\u6709\u4f55\u9634\u8c0b\u5e03\u7f6e\u3002 \u3000\u3000\u90a3\u9676\u7136\u4ead\u5730\u5904\u8352\u50fb\uff0c\u5176\u540d\u867d\u66f0\u9676\u7136\uff0c\u5b9e\u5219\u662f\u4e00\u5c3c\u5eb5\uff0c\u540d\u53eb\u201c\u6148\u60b2\u5eb5\u201d\uff0c\u5eb5\u4e2d\u4f9b\u5949\u89c2\u97f3\u5927\u58eb\u3002 \u3000\u3000\u80e1\u6590\u548c\u7a0b\u7075\u7d20\u5230\u5f97\u5f53\u5730\uff0c\u4f46\u89c1\u56db\u4e0b\u91cc\u767d\u832b\u832b\u7684\u4e00\u7247\uff0c\u90fd\u662f\u82a6\u82c7\uff0c\u897f\u98ce\u4e00\u54c4\uff0c\u82a6\u7d6e\u98de\u821e\uff0c\u6709\u5982\u4e0b\u96ea\uff0c\u6ee1\u76ee\u5c3d\u662f\u8083\u6740\u82cd\u51c9\u4e4b\u6c14\u3002 \u3000\u3000\u5ffd\u542c\u201c\u554a\u201d\u7684\u4e00\u58f0\uff0c\u4e00\u53ea\u9e3f\u96c1\u98de\u8fc7\u5929\u7a7a\u3002\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u8fd9\u662f\u4e00\u53ea\u5931\u7fa4\u7684\u5b64\u96c1\u4e86\uff0c\u627e\u5bfb\u540c\u4f34\u4e0d\u7740\uff0c\u534a\u591c\u91cc\u8fd8\u5728\u5306\u5306\u5fd9\u5fd9\u7684\u8d76\u8def\u3002\u201d\u5ffd\u542c\u82a6\u82c7\u4e1b\u4e2d\u6709\u4eba\u63a5\u53e3\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\u3002\u5730\u531d\u4e07\u82a6\u5439\u7d6e\u4e71\uff0c\u5929\u7a7a\u4e00\u96c1\u6bd4\u4eba\u8f7b\u3002\u4e24\u4f4d\u771f\u662f\u4fe1\u4eba\uff0c\u8fd9\u4e48\u65e9\u4fbf\u6765\u8d74\u7ea6\u4e86\u3002\u201d \u3000\u3000\u80e1\u7a0b\u4e8c\u4eba\u5403\u4e86\u4e00\u60ca\uff1a\u201c\u6211\u4eec\u8fd8\u60f3\u6765\u67e5\u5bdf\u5bf9\u65b9\u7684\u9634\u8c0b\u5e03\u7f6e\uff0c\u5c82\u77e5\u4ed6\u4eec\u65e9\u4fbf\u5230\u5904\u4f0f\u4e0b\u4e86\u6697\u6869\uff0c\u8fd9\u4eba\u51fa\u53e3\u6210\u8bd7\uff0c\u770b\u6765\u4e5f\u975e\u6cdb\u6cdb\u4e4b\u8f88\u3002\u201d\u80e1\u6590\u6717\u58f0\u9053\uff1a\u201c\u5949\u53ec\u8d74\u7ea6\uff0c\u6562\u4e0d\u65e9\u6765\uff1f\u201d \u3000\u3000\u53ea\u89c1\u82a6\u82c7\u4e1b\u4e2d\u957f\u8eab\u7ad9\u8d77\u4e00\u4e2a\u6ee1\u8138\u4f24\u75a4\u3001\u8eab\u7a7f\u6587\u58eb\u6253\u626e\u7684\u79c0\u624d\u76f8\u516c\uff0c\u62f1\u624b\u8bf4\u9053\uff1a\u201c\u5e78\u4f1a\uff0c\u5e78\u4f1a\u3002\u53ea\u662f\u8bf7\u4e24\u4f4d\u7a0d\u5f85\uff0c\u655d\u4e0a\u548c\u4f17\u5144\u5f1f\u6b63\u5728\u4e0a\u796d\u3002\u201d\u80e1\u6590\u968f\u53e3\u7b54\u5e94\uff0c\u5fc3\u4e0b\u597d\u751f\u5947\u602a\uff1a\u201c\u798f\u5eb7\u5b89\u534a\u591c\u4e09\u66f4\u7684\uff0c\u5230\u8fd9\u8352\u91ce\u4e4b\u5730\u6765\u796d\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u84e6\u5730\u91cc\u542c\u5f97\u4e00\u4eba\u957f\u58f0\u541f\u9053\uff1a\u201c\u6d69\u6d69\u6101\uff0c\u832b\u832b\u52ab\u3002\u77ed\u6b4c\u7ec8\uff0c\u660e\u6708\u7f3a\u3002\u90c1\u90c1\u4f73\u57ce\uff0c\u4e2d\u6709\u78a7\u8840\u3002\u78a7\u4ea6\u6709\u65f6\u5c3d\uff0c\u8840\u4ea6\u6709\u65f6\u706d\uff0c\u4e00\u7f15\u9999\u9b42\u65e0\u65ad\u7edd\u3002\u662f\u8036\uff1f\u975e\u8036\uff1f\u5316\u4e3a\u8774\u8776\u3002\u201d \u3000\u3000\u541f\u5230\u540e\u6765\uff0c\u58f0\u8f6c\u545c\u54bd\uff0c\u8ddf\u7740\u6709\u5341\u4f59\u4eba\u7684\u58f0\u97f3\uff0c\u6216\u957f\u53f9\uff0c\u6216\u4f4e\u6ce3\uff0c\u4e2d\u95f4\u8fd8\u5939\u6742\u7740\u51e0\u4e2a\u5973\u5b50\u7684\u54ed\u58f0\u3002 \u3000\u3000\u80e1\u6590\u542c\u4e86\u90a3\u9996\u77ed\u8bcd\uff0c\u53ea\u89c9\u8bcd\u610f\u60c5\u6df1\u7f20\u7ef5\uff0c\u6240\u796d\u7684\u5893\u4e2d\u4eba\u663e\u662f\u4e00\u4e2a\u5973\u5b50\uff0c\u800c\u4e14\u201c\u78a7\u8840\u201d\u4e91\u4e91\uff0c\u53c8\u5f53\u662f\u6b89\u96be\u800c\u6b7b\uff0c\u9759\u591c\u4e4b\u4e2d\uff0c\u542c\u7740\u90a3\u51c4\u5207\u7684\u4f24\u75db\u4e4b\u97f3\uff0c\u89e6\u52a8\u5fc3\u5883\uff0c\u7adf\u4e5f\u4e0d\u7981\u60b2\u4ece\u4e2d\u6765\uff0c\u4fbf\u60f3\u5927\u54ed\u4e00\u573a\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u60b2\u58f0\u6e10\u6b62\uff0c\u53ea\u89c1\u5341\u4f59\u4eba\u9646\u7eed\u8d70\u4e0a\u4e00\u4e2a\u571f\u4e18\u3002 \u3000\u3000\u80e1\u6590\u8eab\u65c1\u7684\u90a3\u79c0\u624d\u76f8\u516c\u53eb\u9053\uff1a\u201c\u9053\u957f\uff0c\u4f60\u7ea6\u7684\u670b\u53cb\u5230\u5566\u3002\u201d \u3000\u3000\u90a3\u72ec\u81c2\u9053\u4eba\u8bf4\u9053\uff1a\u201c\u5999\u6781\uff0c\u5999\u6781\uff01\u5c0f\u5144\u5f1f\uff0c\u54b1\u4eec\u6765\u62da\u6597\u4e09\u767e\u5408\u3002\u201d \u3000\u3000\u8bf4\u7740\u7eb5\u8eab\u5954\u4e0b\u571f\u4e18\u3002\u80e1\u6590\u4fbf\u8fce\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u90a3\u9053\u4eba\u5954\u5230\u79bb\u80e1\u6590\u5c1a\u6709\u6570\u4e08\u4e4b\u5904\uff0c\u84e6\u5730\u91cc\u7eb5\u8eab\u8dc3\u8d77\uff0c\u534a\u7a7a\u62d4\u5251\uff0c\u501f\u7740\u8fd9\u4e00\u8dc3\u4e4b\u52bf\uff0c\u75be\u523a\u8fc7\u6765\u3002\u8fd9\u4e00\u523a\u51fa\u624b\u4e4b\u5feb\uff0c\u52bf\u9053\u4e4b\u75be\uff0c\u5b9e\u662f\u5a01\u4e0d\u53ef\u5f53\u3002\u80e1\u6590\u89c1\u4ed6\u5982\u6b64\u51f6\u608d\uff0c\u6fc0\u8d77\u4e86\u5c11\u5e74\u4eba\u7684\u521a\u5f3a\u4e4b\u6c14\uff0c\u4e5f\u662f\u7eb5\u8eab\u8dc3\u8d77\uff0c\u534a\u7a7a\u62d4\u5200\u3002\u4e24\u4eba\u5728\u7a7a\u4e2d\u4e00\u51d1\u5408\uff0c\u5f53\u5f53\u5f53\u5f53\u56db\u54cd\uff0c\u5200\u5251\u649e\u51fb\u56db\u4e0b\uff0c\u4e24\u4eba\u4e00\u9f50\u843d\u4e0b\u5730\u6765\u3002 \u3000\u3000\u8fd9\u4e2d\u95f4\u90a3\u9053\u4eba\u653b\u4e86\u4e24\u5251\uff0c\u80e1\u6590\u8fd8\u4e86\u4e24\u5200\u3002\u4e24\u4eba\u56db\u53ea\u811a\u4e00\u843d\u5730\uff0c\u7acb\u65f6\u53c8\u662f\u5f53\u5f53\u5f53\u5f53\u5f53\u5f53\u516d\u54cd\u3002\u571f\u4e18\u4e4b\u4e0a\uff0c\u5f69\u58f0\u5927\u4f5c\u3002 \u3000\u3000\u90a3\u9053\u4eba\u5251\u6cd5\u51cc\u5389\uff0c\u8fc5\u6377\u65e0\u4f26\uff0c\u5728\u5e38\u4eba\u523a\u51fa\u4e00\u5251\u7684\u65f6\u523b\u4e4b\u4e2d\uff0c\u5f80\u5f80\u523a\u51fa\u4e86\u56db\u4e94\u5251\u3002\u80e1\u6590\u5fc3\u60f3\uff1a\u201c\u4f60\u4f1a\u5feb\uff0c\u96be\u9053\u6211\u4fbf\u4e0d\u4f1a\u3002\u201d \u3000\u3000\u5c55\u5f00\u201c\u80e1\u5bb6\u5feb\u5200\u201d\uff0c\u4e5f\u662f\u5728\u5e38\u4eba\u780d\u51fa\u4e00\u5200\u7684\u65f6\u523b\u4e4b\u4e2d\u780d\u51fa\u4e86\u56db\u4e94\u5200\u3002\u76f8\u8f83\u4e4b\u4e0b\uff0c\u90a3\u9053\u4eba\u7684\u5251\u523a\u8fd8\u662f\u5feb\u4e86\u534a\u5206\uff0c\u4f46\u5251\u62db\u8f7b\u7075\uff0c\u5200\u52bf\u6c89\u731b\uff0c\u80e1\u6590\u7684\u5200\u529b\uff0c\u5374\u53c8\u6bd4\u4ed6\u91cd\u4e86\u534a\u5206\u3002 \u3000\u3000\u4e24\u4eba\u4ee5\u5feb\u6253\u5feb\uff0c\u4ec0\u4e48\u817e\u632a\u95ea\u907f\uff0c\u653b\u5b88\u53d8\u5316\uff0c\u5230\u540e\u6765\u5168\u8bf4\u4e0d\u4e0a\u4e86\uff0c\u76f4\u662f\u95ed\u4e86\u773c\u775b\u72e0\u6597\uff0c\u53ea\u542c\u53ee\u53ee\u5f53\u5f53\u5200\u5251\u78b0\u649e\uff0c\u5982\u51b0\u96f9\u4e71\u843d\uff0c\u5982\u4f17\u9a6c\u5954\u817e\uff0c\u53c8\u5982\u6570\u9762\u7faf\u9f13\u540c\u65f6\u51fb\u6253\uff0c\u7e41\u97f3\u5bc6\u70b9\uff0c\u5feb\u901f\u96be\u8a00\u3002 \u3000\u3000\u90a3\u72ec\u81c2\u9053\u4eba\u4e00\u9762\u72e0\u6597\uff0c\u4e00\u9762\u5927\u547c\uff1a\u201c\u75db\u5feb\uff0c\u75db\u5feb\uff01\u201d\u5251\u62db\u8d8a\u6765\u8d8a\u662f\u51cc\u5389\u3002\u80e1\u6590\u6697\u6697\u5fc3\u60ca\uff0c\u9661\u9022\u5f3a\u654c\uff0c\u5f53\u4e0b\u5c06\u751f\u5e73\u6240\u5b66\u5c3d\u6570\u65bd\u5c55\u51fa\u6765\uff0c\u5200\u6cd5\u4e4b\u5f97\u5fc3\u5e94\u624b\u5b9e\u662f\u4ece\u6240\u672a\u6709\uff0c\u81ea\u5df1\u72ec\u4e2a\u513f\u7ec3\u4e60\u4e4b\u65f6\uff0c\u90a3\u6709\u8fd9\u7b49\u5feb\u6cd5\uff1f\u539f\u6765\u4ed6\u8fd9\u80e1\u5bb6\u5200\u6cd5\u7cbe\u5fae\u5947\u5965\u4e4b\u5904\u751a\u591a\uff0c\u4e0d\u9022\u5f3a\u654c\uff0c\u6570\u62db\u95f4\u5373\u8db3\u53d6\u80dc\uff0c\u5176\u5999\u5904\u4e0d\u663e\uff0c\u8fd9\u65f6\u7ed9\u90a3\u72ec\u81c2\u9053\u4eba\u4e00\u903c\uff0c\u624d\u73b0\u51fa\u5200\u6cd5\u4e2d\u7684\u7ef5\u5bc6\u7cbe\u5de7\u6765\u3002 \u3000\u3000\u90a3\u72ec\u81c2\u9053\u4eba\u4e00\u751f\u4e0d\u77e5\u7ecf\u5386\u8fc7\u591a\u5c11\u5927\u9635\u5927\u4ed7\uff0c\u5f53\u6b64\u5feb\u6597\u4e4b\u9645\uff0c\u7aed\u529b\u8981\u5bfb\u8fd9\u5c11\u5e74\u5200\u6cd5\u4e2d\u7684\u7834\u7efd\uff0c\u53ef\u662f\u53ea\u89c1\u4ed6\u5200\u5200\u653b\u5b88\u5e76\u5907\uff0c\u4e0d\u6c42\u5b88\u800c\u81ea\u5b88\uff0c\u4e0d\u52a1\u653b\u5374\u731b\u653b\uff0c\u6bcf\u4e00\u62db\u4e4b\u540e\uff0c\u5747\u4f0f\u4e0b\u7cbe\u5999\u7684\u540e\u7740\uff0c\u54ea\u91cc\u6709\u7834\u7efd\u53ef\u5bfb\uff1f \u3000\u3000\u8fd9\u72ec\u81c2\u9053\u4eba\u7684\u529f\u529b\u5b9e\u6bd4\u80e1\u6590\u6df1\u539a\u5f97\u591a\uff0c\u5018\u82e5\u5e76\u975e\u5feb\u6597\uff0c\u80e1\u6590\u548c\u4ed6\u89c1\u62db\u62c6\u62db\uff0c\u81ea\u6c42\u53d8\u5316\uff0c\u72ec\u81c2\u9053\u4eba\u6b64\u65f6\u5df2\u7136\u5f97\u80dc\u3002\u4f46\u8d8a\u6253\u8d8a\u5feb\u4e4b\u540e\uff0c\u80e1\u6590\u6765\u4e0d\u53ca\u601d\u7d22\uff0c\u53ea\u662f\u5c06\u5e73\u7d20\u7ec3\u719f\u4e86\u4e00\u5957\u201c\u5feb\u5200\u201d\u4f7f\u5c06\u51fa\u6765\u5e94\u4ed8\u3002\u8fd9\u8def\u201c\u5feb\u5200\u201d\u4e43\u660e\u672b\u5927\u4fa0\u201c\u98de\u5929\u72d0\u72f8\u201d\u6240\u521b\uff0c\u4f20\u5230\u80e1\u6590\u4e4b\u7236\u80e1\u4e00\u5200\u624b\u4e0a\uff0c\u53c8\u52a0\u4e86\u8bb8\u591a\u53d8\u5316\u5999\u7740\u3002\u6b64\u65f6\u80e1\u6590\u6301\u4e4b\u4e34\u654c\uff0c\u4e0e\u80e1\u4e00\u5200\u4eb2\u81ea\u51fa\u9635\u5df2\u65e0\u591a\u5927\u5206\u522b\uff0c\u6240\u5dee\u8005\u53ea\u662f\u706b\u5019\u800c\u5df2\u3002 \u3000\u3000\u4e0d\u5230\u4e00\u76cf\u8336\u65f6\u5206\uff0c\u4e24\u4eba\u5df2\u62c6\u89e3\u4e86\u4e94\u767e\u4f59\u62db\uff0c\u5176\u5feb\u53ef\u77e5\u3002\u65f6\u523b\u867d\u77ed\uff0c\u4f46\u90a3\u9053\u4eba\u5df2\u662f\u989d\u5934\u89c1\u6c57\uff0c\u80e1\u6590\u4ea6\u662f\u6c57\u6d41\u6d43\u80cc\uff0c\u4e24\u4eba\u90fd\u53ef\u542c\u5230\u5bf9\u65b9\u7c97\u91cd\u7684\u547c\u5438\u3002 \u3000\u3000\u6b64\u65f6\u5267\u6597\u6b63\u9163\uff0c\u80e1\u6590\u548c\u90a3\u72ec\u81c2\u9053\u4eba\u5fc3\u4e2d\u5374\u90fd\u8d77\u4e86\u60fa\u60fa\u76f8\u60dc\u4e4b\u610f\uff0c\u53ea\u662f\u5251\u523a\u5200\u5288\uff0c\u62db\u6570\u7ef5\u7ef5\u4e0d\u7edd\uff0c\u8c01\u4e5f\u4e0d\u80fd\u5148\u884c\u7f62\u624b\u3002 \u3000\u3000\u5200\u5251\u76f8\u4ea4\uff0c\u53ee\u5f53\u58f0\u4e2d\uff0c\u5ffd\u542c\u5f97\u4e00\u4eba\u957f\u58f0\u553f\u54e8\uff0c\u8ddf\u7740\u8fdc\u5904\u4f20\u6765\u5175\u5203\u78b0\u649e\u548c\u5406\u559d\u4e4b\u58f0\u3002\u90a3\u72ec\u81c2\u9053\u4eba\u4e00\u58f0\u957f\u7b11\uff0c\u6258\u5730\u8df3\u51fa\u5708\u5b50\uff0c\u53eb\u9053\uff1a\u201c\u4e14\u4f4f\uff01\u5c0f\u5144\u5f1f\uff0c\u4f60\u5200\u6cd5\u5f88\u9ad8\uff0c\u8fd9\u5f53\u53e3\u6709\u654c\u4eba\u6765\u5566\uff01\u201d \u3000\u3000\u80e1\u6590\u4e00\u6014\u4e4b\u95f4\uff0c\u53ea\u89c1\u4e1c\u5317\u89d2\u548c\u4e1c\u5357\u89d2\u4e0a\u5f71\u5f71\u7ef0\u7ef0\uff0c\u6709\u516d\u4e03\u4eba\u5954\u4e86\u8fc7\u6765\u3002\u9ed1\u591c\u4e2d\u5200\u5149\u4e00\u95ea\u4e00\u70c1\uff0c\u8fd9\u4e9b\u4eba\u624b\u4e2d\u90fd\u6301\u7740\u5175\u5203\u3002\u53c8\u542c\u5f97\u80cc\u540e\u4f20\u6765\u5406\u559d\u4e4b\u58f0\uff0c\u80e1\u6590\u56de\u8fc7\u5934\u6765\uff0c\u89c1\u897f\u5317\u65b9\u548c\u897f\u5357\u65b9\u4e5f\u5747\u6709\u4eba\u5954\u5230\uff0c\u7ea6\u7565\u4e00\u8ba1\uff0c\u5c11\u8bf4\u4e5f\u6709\u4e8c\u5341\u4eba\u4e4b\u8c31\u3002 \u3000\u3000\u72ec\u81c2\u9053\u4eba\u53eb\u9053\uff1a\u201c\u5341\u56db\u5f1f\uff0c\u4f60\u56de\u6765\uff0c\u8ba9\u4e8c\u54e5\u6765\u6253\u53d1\u3002\u201d\u90a3\u6307\u5f15\u80e1\u6590\u8fc7\u6765\u7684\u4e66\u751f\u624b\u6301\u4e00\u6839\u9ec4\u6f84\u6f84\u7684\u77ed\u68d2\u6a21\u6837\u5175\u5203\uff0c\u672c\u5728\u62e6\u622a\u897f\u5317\u65b9\u8fc7\u6765\u7684\u5bf9\u624b\uff0c\u542c\u5230\u72ec\u81c2\u9053\u4eba\u7684\u53eb\u5524\uff0c\u5e94\u9053\uff1a\u201c\u597d\uff01\u201d \u3000\u3000\u624b\u4e2d\u5175\u5203\u4e00\u6325\uff0c\u7adf\u7136\u53d1\u51fa\u545c\u545c\u58f0\u54cd\uff0c\u53cd\u8eab\u5954\u4e0a\u5c0f\u4e18\uff0c\u548c\u4f17\u4eba\u5e76\u80a9\u7ad9\u7acb\u3002 \u3000\u3000\u6708\u5149\u4e0b\u80e1\u6590\u77a7\u5f97\u5206\u660e\uff0c\u798f\u5eb7\u5b89\u6b63\u7ad9\u5728\u5c0f\u4e18\u4e4b\u4e0a\uff0c\u4ed6\u8eab\u65c1\u7684\u5341\u4f59\u4eba\u4e2d\uff0c\u8fd8\u6709\u4e09\u56db\u4e2a\u662f\u5973\u5b50\u3002\u80e1\u6590\u5927\u559c\uff1a\u201c\u56db\u9762\u516b\u65b9\u6765\u7684\u8fd9\u4e9b\u4eba\u90fd\u548c\u798f\u5eb7\u5b89\u4e3a\u654c\uff0c\u4e0d\u77e5\u662f\u90a3\u4e00\u5bb6\u7684\u82f1\u96c4\u597d\u6c49\uff1f\u77a7\u8fd9\u4e9b\u4eba\u7684\u8f7b\u8eab\u529f\u592b\uff0c\u6b66\u529f\u90fd\u975e\u5bfb\u5e38\u3002\u6211\u548c\u4ed6\u4eec\u9f50\u5fc3\u534f\u529b\uff0c\u5c06\u798f\u5eb7\u5b89\u8fd9\u5978\u8d3c\u64d2\u4f4f\uff0c\u5c82\u4e0d\u662f\u597d\uff1f\u201d\u4f46\u8f6c\u5ff5\u53c8\u60f3\uff1a\u201c\u798f\u5eb7\u5b89\u8fd9\u6076\u8d3c\u60f3\u4e0d\u5230\u6b66\u529f\u7adf\u662f\u5947\u9ad8\uff0c\u624b\u4e0b\u90a3\u4e9b\u4eba\u53c8\u5747\u662f\u786c\u624b\uff0c\u77a7\u4ed6\u4eec\u8fd9\u822c\u8086\u65e0\u5fcc\u60ee\u7684\u6a21\u6837\uff0c\u83ab\u975e\u53e6\u884c\u5b89\u6392\u4e0b\u9634\u8c0b\uff1f\u201d \u3000\u3000\u6b63\u81ea\u601d\u7591\u4e0d\u5b9a\uff0c\u53ea\u89c1\u56db\u65b9\u6765\u4eba\u5747\u5df2\u5954\u8fd1\uff0c\u4e00\u770b\u4e4b\u4e0b\uff0c\u66f4\u662f\u5927\u60d1\u4e0d\u89e3\uff0c\u5954\u6765\u7684\u4e8c\u5341\u4f59\u4eba\u4e4b\u4e2d\uff0c\u534a\u6570\u662f\u8eab\u7a7f\u8840\u7ea2\u50e7\u888d\u7684\u85cf\u50e7\uff0c\u4f59\u4eba\u7a7f\u7684\u5747\u662f\u6e05\u5bab\u536b\u58eb\u7684\u670d\u8272\u3002\u4ed6\u7eb5\u8eab\u9760\u8fd1\u7a0b\u7075\u7d20\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u4e8c\u59b9\uff0c\u54b1\u4eec\u679c\u7136\u9677\u5165\u4e86\u6076\u8d3c\u7684\u5708\u5957\uff0c\u654c\u4eba\u91cc\u5916\u5939\u653b\uff0c\u65e0\u6cd5\u62b5\u6321\uff0c\u5411\u6b63\u897f\u65b9\u51b2\uff01\u201d \u3000\u3000\u7a0b\u7075\u7d20\u5c1a\u672a\u56de\u7b54\uff0c\u6e05\u5bab\u536b\u58eb\u4e2d\u4e00\u4e2a\u9ed1\u987b\u5927\u6c49\u8d8a\u4f17\u800c\u51fa\uff0c\u624b\u6301\u957f\u5251\uff0c\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u662f\u65e0\u5c18\u9053\u4eba\u4e48\uff1f\u4e45\u4ef0\u4f60\u4e03\u5341\u4e8c\u8def\u8ffd\u9b42\u593a\u547d\u5251\u5929\u4e0b\u65e0\u53cc\uff0c\u4eca\u65e5\u6b63\u597d\u9886\u6559\u3002\u201d\u90a3\u72ec\u81c2\u9053\u4eba\u51b7\u51b7\u5730\u9053\uff1a\u201c\u4f60\u65e2\u77e5\u65e0\u5c18\u4e4b\u540d\uff0c\u5c1a\u6765\u6311\u6218\uff0c\u53ef\u7b97\u5f97\u5927\u80c6\u3002\u4f60\u662f\u8c01\uff1f\u201d \u3000\u3000\u80e1\u6590\u542c\u4e86\u90a3\u9ed1\u987b\u536b\u58eb\u7684\u8bdd\uff0c\u7981\u4e0d\u4f4f\u8131\u53e3\u53eb\u9053\uff1a\u201c\u662f\u65e0\u5c18\u9053\u957f\uff1f\u201d\u65e0\u5c18\u7b11\u9053\uff1a\u201c\u6b63\u662f\uff01\u8d75\u4e09\u5f1f\u5938\u4f60\u82f1\u96c4\u4e86\u5f97\uff0c\u679c\u7136\u4e0d\u9519\u3002\u201d \u3000\u3000\u80e1\u6590\u60ca\u559c\u4ea4\u96c6\uff0c\u9053\uff1a\u201c\u53ef\u662f\u2026\u2026\u53ef\u662f\uff0c\u90a3\u798f\u5eb7\u5b89\u2026\u2026\u6211\u8d75\u4e09\u54e5\u5462\uff1f\u201d \u3000\u3000\u90a3\u9ed1\u987b\u5927\u6c49\u56de\u7b54\u65e0\u5c18\u7684\u8bdd\u9053\uff1a\u201c\u5728\u4e0b\u5fb7\u5e03\u3002\u201d\u65e0\u5c18\u9053\uff1a\u201c\u554a\uff0c\u4f60\u4fbf\u662f\u5fb7\u5e03\u3002\u6211\u5728\u56de\u7586\u542c\u4eba\u8a00\u9053\uff1a\u6700\u8fd1\u7687\u5e1d\u8001\u513f\u627e\u5230\u4e86\u4e00\u53ea\u7259\u5c16\u722a\u5229\u7684\u9e70\u72ac\uff0c\u53eb\u4f5c\u4ec0\u4e48\u5fb7\u5e03\uff0c\u79f0\u505a\u4ec0\u4e48\u2018\u6ee1\u6d32\u7b2c\u4e00\u52c7\u58eb\u2019\uff0c\u662f\u4e2a\u4ec0\u4e48\u5fa1\u524d\u4f8d\u536b\u7684\u5934\u513f\u3002\u4fbf\u662f\u4f60\u4e86\uff1f\u201d\u4ed6\u8fde\u8bf4\u4e09\u4e2a\u201c\u4ec0\u4e48\u201d\uff0c\u53ea\u628a\u5fb7\u5e03\u542c\u5f97\u5fc3\u5934\u706b\u8d77\uff0c\u559d\u9053\uff1a\u201c\u4e0d\u9519\uff01\u4f60\u65e2\u77e5\u6211\u540d\uff0c\u8fd8\u6562\u5230\u5929\u5b50\u811a\u4e0b\u6765\u6492\u91ce\uff0c\u5f53\u771f\u662f\u6d3b\u5f97\u4e0d\u8010\u70e6\u4e86\u2026\u2026\u201d \u3000\u3000\u4ed6\u201c\u4e0d\u8010\u70e6\u4e86\u201d\u56db\u5b57\u521a\u8131\u53e3\uff0c\u5bd2\u5149\u4e00\u95ea\uff0c\u65e0\u5c18\u957f\u5251\u5df2\u523a\u5411\u8eab\u524d\u3002\u5fb7\u5e03\u6a2a\u5251\u6321\u67b6\uff0c\u5f53\u7684\u4e00\u54cd\uff0c\u53cc\u5251\u76f8\u4ea4\uff0c\u55e1\u55e1\u4e4b\u58f0\u4e0d\u7edd\uff0c\u663e\u662f\u4e24\u4eba\u5251\u4e0a\u52b2\u529b\u5747\u751a\u6d51\u539a\u3002\u65e0\u5c18\u8d5e\u4e86\u58f0\uff1a\u201c\u4e5f\u8fd8\u53ef\u4ee5\uff01\u201d \u3000\u3000\u5251\u62db\u6e90\u6e90\u9012\u51fa\u3002\u5fb7\u5e03\u7684\u5251\u62db\u8fdc\u6ca1\u65e0\u5c18\u5feb\u6377\uff0c\u4f46\u95e8\u6237\u5b88\u5f97\u6781\u662f\u4e25\u5bc6\uff0c\u5076\u5c14\u8fd8\u523a\u4e00\u5251\uff0c\u5374\u4e5f\u5341\u5206\u7684\u72e0\u8fa3\uff0c\u90a3\u201c\u6ee1\u6d32\u7b2c\u4e00\u52c7\u58eb\u201d\u7684\u79f0\u53f7\uff0c\u679c\u7136\u5e76\u975e\u5e78\u81f4\u3002 \u3000\u3000\u80e1\u6590\u66fe\u542c\u5706\u6027\u8bf4\u8fc7\uff0c\u7ea2\u82b1\u4f1a\u4e8c\u5f53\u5bb6\u65e0\u5c18\u9053\u4eba\u5251\u672f\u4e4b\u7cbe\uff0c\u5f53\u4e16\u6570\u4e00\u6570\u4e8c\uff0c\u60f3\u4e0d\u5230\u81ea\u5df1\u7adf\u80fd\u548c\u4ed6\u62c6\u5230\u6570\u767e\u62db\u4e0d\u8d25\uff0c\u4e0d\u7531\u5f97\u5fc3\u5934\u6697\u559c\uff0c\u53c8\u60f3\uff1a\u201c\u5e78\u4e8f\u6211\u4e0d\u77e5\u4ed6\u4fbf\u662f\u65e0\u5c18\u9053\u957f\uff0c\u5426\u5219\u9707\u4e8e\u4ed6\u7684\u5a01\u540d\uff0c\u5fc3\u4e2d\u4e00\u602f\uff0c\u53ea\u6015\u652f\u6301\u4e0d\u5230\u4e00\u767e\u62db\u4fbf\u8d25\u4e0b\u6765\u4e86\u3002\u201d\u53c8\u60f3\uff1a \u3000\u3000\u201c\u4ed6\u662f\u7ea2\u82b1\u4f1a\u82f1\u96c4\uff0c\u8d75\u4e09\u54e5\u7684\u670b\u53cb\uff0c\u7136\u5219\u90a3\u798f\u5eb7\u5b89\uff0c\u96be\u9053\u5f53\u771f\u6211\u662f\u8ba4\u9519\u4e86\u4eba\uff1f\u201d \u3000\u3000\u6b63\u81ea\u51dd\u795e\u89c2\u770b\u65e0\u5c18\u548c\u5fb7\u5e03\u76f8\u6597\uff0c\u4e24\u540d\u6e05\u5bab\u4f8d\u536b\u6b3a\u8fd1\u8eab\u6765\uff0c\u559d\u9053\uff1a\u201c\u629b\u4e0b\u5175\u5668\uff01\u201d\u80e1\u6590\u9053\uff1a\u201c\u5e72\u4ec0\u4e48\uff1f\u201d\u4e00\u540d\u4f8d\u536b\u9053\uff1a\u201c\u4f60\u80c6\u6562\u62d2\u6355\u4e48\uff1f\u201d\u80e1\u6590\u9053\uff1a\u201c\u62d2\u6355\u4fbf\u600e\u6837\uff1f\u201d\u90a3\u4f8d\u536b\u9053\uff1a\u201c\u5c0f\u8d3c\u597d\u6a2a\uff01\u201d \u3000\u3000\u4e3e\u5200\u780d\u5c06\u8fc7\u6765\u3002\u80e1\u6590\u95ea\u8eab\u907f\u5f00\uff0c\u8fd8\u4e86\u4e00\u5200\u3002\u5c82\u77e5\u53e6\u4e00\u540d\u4f8d\u536b\u624b\u4e2d\u4e00\u67c4\u94c1\u9524\u84e6\u5730\u91cc\u659c\u523a\u6253\u5230\uff0c\u51fb\u5728\u80e1\u6590\u7684\u5200\u53e3\u4e4b\u4e0a\uff0c\u6b64\u4eba\u8182\u529b\u751a\u5927\uff0c\u5175\u5668\u53c8\u662f\u5947\u91cd\u3002\u80e1\u6590\u548c\u65e0\u5c18\u529b\u6218\u4e4b\u4f59\uff0c\u624b\u81c2\u9690\u9690\u9178\u9ebb\uff0c\u4e00\u4e2a\u62ff\u634f\u4e0d\u4f4f\uff0c\u5355\u5200\u8131\u624b\uff0c\u76f4\u98de\u8d77\u6765\u3002\u90a3\u4eba\u4e00\u9524\u56de\u8f6c\uff0c\u4fbf\u5411\u4ed6\u80cc\u5fc3\u6a2a\u51fb\u3002 \u3000\u3000\u80e1\u6590\u5175\u5203\u79bb\u624b\uff0c\u5374\u4e0d\u614c\u4e71\uff0c\u8eab\u5f62\u4e00\u95ea\uff0c\u907f\u5f00\u4e86\u4ed6\u7684\u94c1\u9524\uff0c\u987a\u52bf\u4e00\u4e2a\u8098\u69cc\uff0c\u649e\u6b63\u4ed6\u8170\u773c\u3002\u90a3\u4eba\u5927\u58f0\u53eb\u9053\uff1a\u201c\u554a\u54df\uff0c\u597d\u5c0f\u5b50\uff01\u201d \u3000\u3000\u75db\u5f97\u624b\u4e2d\u94c1\u9524\u9669\u4e9b\u8dcc\u843d\u3002\u8ddf\u7740\u53c8\u6709\u4e24\u540d\u4f8d\u536b\u4e0a\u6765\u5939\u653b\uff0c\u4e00\u4e2a\u6301\u97ad\uff0c\u4e00\u4e2a\u633a\u7740\u4e00\u679d\u77ed\u67aa\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u53eb\u9053\uff1a\u201c\u5927\u54e5\uff0c\u6211\u6765\u5e2e\u4f60\u3002\u201d\u62bd\u51fa\u67f3\u53f6\u5200\uff0c\u6b32\u5f85\u4e0a\u524d\u76f8\u52a9\u3002\u80e1\u6590\u53eb\u9053\uff1a\u201c\u4e0d\u7528\uff0c\u4e14\u77a7\u77a7\u4f60\u5927\u54e5\u7a7a\u624b\u5165\u767d\u5203\u7684\u624b\u6bb5\u3002\u201d \u3000\u3000\u7a0b\u7075\u7d20\u89c1\u4ed6\u5728\u56db\u4e2a\u654c\u4eba\u4e4b\u95f4\u6e38\u8d70\u95ea\u907f\uff0c\u60c5\u52bf\u4f3c\u4e4e\u751a\u9669\uff0c\u4f46\u542c\u4ed6\u8bf4\u5f97\u60a0\u95f2\u81ea\u5728\uff0c\u53c8\u77e5\u4ed6\u6b66\u529f\u4e86\u5f97\uff0c\u4fbf\u7ad9\u5728\u4e00\u65c1\uff0c\u633a\u5200\u6212\u5907\u3002 \u3000\u3000\u80e1\u6590\u5c55\u5f00\u4ece\u5c0f\u4fbf\u5b66\u4f1a\u7684\u201c\u56db\u8c61\u6b65\u6cd5\u201d\uff0c\u4e1c\u8de8\u4e00\u6b65\uff0c\u897f\u9000\u534a\u6b65\uff0c\u5728\u56db\u540d\u9ad8\u624b\u4f8d\u536b\u4e4b\u95f4\u7a7f\u6765\u63d2\u53bb\u3002\u4ed6\u8fd9\u201c\u56db\u8c61\u6b65\u201d\u6309\u7740\u4e1c\u82cd\u9f99\u3001\u897f\u767d\u864e\u3001\u5317\u7384\u6b66\u3001\u5357\u6731\u96c0\u56db\u8c61\u800c\u53d8\uff0c\u6bcf\u8c61\u4e03\u5bbf\uff0c\u53c8\u6309\u4e8c\u5341\u516b\u5bbf\u4e4b\u5f62\u518d\u751f\u53d8\u5316\u3002\u654c\u4eba\u7684\u56db\u4ef6\u5175\u5203\u6709\u8f7b\u6709\u91cd\uff0c\u5de6\u653b\u53f3\u51fb\uff0c\u53ef\u662f\u4ed6\u6b65\u6cd5\u5947\u5999\uff0c\u5f80\u5f80\u5728\u95f4\u4e0d\u5bb9\u53d1\u4e4b\u9645\u907f\u8fc7\u654c\u4eba\u5175\u5203\uff0c\u6709\u65f6\u76f8\u5dee\u4e0d\u8fc7\u6570\u5bf8\u4e4b\u5fae\uff0c\u53ef\u5c31\u662f\u5dee\u7740\u8fd9\u4e48\u51e0\u5bf8\uff0c\u4fbf\u5373\u5937\u7136\u65e0\u635f\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u521d\u65f6\u8fd8\u62c5\u7740\u8001\u5927\u5fc3\u4e8b\uff0c\u4f46\u8d8a\u77a7\u8d8a\u662f\u653e\u5fc3\uff0c\u5230\u540e\u6765\u77a7\u7740\u4ed6\u7cbe\u5999\u7edd\u4f26\u7684\u6b65\u6cd5\uff0c\u7adf\u6709\u70b9\u5fc3\u65f7\u795e\u6021\u8d77\u6765\u3002 \u3000\u3000\u8fd9\u56db\u540d\u4f8d\u536b\u90fd\u662f\u6ee1\u6d32\u4eba\uff0c\u672a\u5165\u6e05\u5bab\u4e4b\u65f6\uff0c\u53f7\u79f0\u201c\u5173\u4e1c\u56db\u6770\u201d\uff0c\u90fd\u7b97\u5f97\u662f\u4e00\u6d41\u9ad8\u624b\u3002\u80e1\u6590\u51ed\u7740\u5de7\u5999\u7684\u201c\u56db\u8c61\u6b65\u201d\u81ea\u4fdd\uff0c\u53ef\u662f\u51e0\u6b21\u4e58\u9699\u53cd\u51fb\uff0c\u5374\u4e5f\u672a\u66fe\u5f97\u624b\uff0c\u6bcf\u4e00\u6b21\u90fd\u662f\u53cd\u9047\u51f6\u9669\uff0c\u4e00\u8f6c\u5ff5\u95f4\uff0c\u5df2\u660e\u5176\u7406\uff0c\u539f\u6765\u9002\u624d\u5229\u65e0\u5c18\u9053\u4eba\u5267\u6597\uff0c\u8017\u529b\u592a\u591a\uff0c\u8fd9\u65f6\u5143\u6c14\u672a\u590d\uff0c\u4e00\u5230\u7d27\u8981\u5173\u5934\uff0c\u5f85\u8981\u52a8\u7528\u771f\u529b\uff0c\u603b\u662f\u5dee\u4e4b\u5398\u6beb\uff0c\u4e0d\u80fd\u53d1\u6325\u62f3\u62db\u4e2d\u7684\u7cbe\u5999\u4e4b\u7740\u3002\u4ed6\u4e00\u7ecf\u60f3\u901a\uff0c\u5f53\u5373\u5e73\u5fc3\u9759\u6c14\uff0c\u53ea\u907f\u4e0d\u653b\uff0c\u5728\u56db\u540d\u8bd7\u536b\u5939\u51fb\u4e4b\u4e0b\u7f13\u7f13\u8c03\u5300\u6c14\u606f\u3002 \u3000\u3000\u90a3\u8fb9\u65e0\u5c18\u6025\u653b\u6570\u5341\u62db\uff0c\u90fd\u7ed9\u5fb7\u5e03\u4e00\u4e00\u6321\u5f00\uff0c\u5374\u4e0d\u7981\u7126\u8e81\u8d77\u6765\uff0c\u6697\u9053\uff1a\u201c\u5341\u5e74\u4e0d\u6765\u4e2d\u539f\uff0c\u4eca\u65e5\u9996\u6b21\u51fa\u624b\u4fbf\u662f\u4e0d\u5229\u3002\u96be\u9053\u5f53\u771f\u8001\u4e86\uff0c\u4e0d\u4e2d\u7528\u4e86\uff1f\u201d\u5176\u5b9e\u8fd9\u5fb7\u5e03\u7684\u6b66\u529f\u5b9e\u662f\u5927\u6709\u8fc7\u4eba\u4e4b\u5904\uff0c\u4f55\u51b5\u65e0\u5c18\u4e0d\u8fc7\u5fc3\u4e0b\u7126\u8e81\uff0c\u5fb7\u5e03\u5374\u5df2\u80cc\u4e0a\u51b7\u6c57\u6dcb\u6f13\uff0c\u8d8a\u6253\u8d8a\u6015\uff0c\u4f46\u89c9\u5bf9\u624b\u62db\u6570\u795e\u51fa\u9b3c\u6ca1\uff0c\u51fa\u5251\u4e4b\u5feb\uff0c\u5b9e\u975e\u4eba\u65b9\u4e4b\u6240\u80fd\u53ca\uff0c\u6697\u60f3\u81ea\u5df1\u7eb5\u6a2a\u5929\u4e0b\uff0c\u4ece\u672a\u9047\u5230\u8fc7\u8fd9\u822c\u52b2\u654c\uff0c\u5f85\u8981\u8ba4\u8f93\u8d25\u9000\uff0c\u5374\u60f3\u4eca\u65e5\u4e00\u8d25\uff0c\u8fd9\u201c\u8e22\u7a7f\u9ec4\u9a6c\u8902\u3001\u5fa1\u524d\u4f8d\u536b\u73ed\u9886\u3001\u6ee1\u6d32\u7b2c\u4e00\u52c7\u58eb\u3001\u7edf\u9886\u5927\u5185\u5341\u516b\u9ad8\u624b\u201d\u4e00\u957f\u4e32\u7684\u8854\u5934\u5374\u5f80\u90a3\u91cc\u6401\u53bb\uff1f\u60f3\u5230\u6b64\u5904\uff0c\u628a\u5fc3\u4e00\u6a2a\uff0c\u8c41\u51fa\u4e86\u6027\u547d\uff0c\u594b\u529b\u62b5\u6321\u3002 \u3000\u3000\u65e0\u5c18\u773c\u89c1\u80e1\u6590\u8d64\u624b\u7a7a\u62f3\uff0c\u4ee5\u4e00\u654c\u56db\uff0c\u81ea\u5df1\u624b\u6709\u5251\uff0c\u5374\u8fde\u4e00\u4e2a\u654c\u4eba\u4e5f\u62fe\u593a\u4e0d\u4e0b\uff0c\u4ed6\u751f\u6027\u6700\u662f\u597d\u80dc\uff0c\u8fd9\u813e\u6c14\u6108\u8001\u5f25\u751a\uff0c\u5f53\u4e0b\u4e00\u5251\u5feb\u4f3c\u4e00\u5251\uff0c\u7740\u7740\u62a2\u653b\uff0c\u6b65\u6b65\u5360\u5148\u3002\u5fb7\u5e03\u89c1\u654c\u4eba\u653b\u52bf\u5927\u76db\uff0c\u5251\u950b\u7ec7\u6210\u4e86\u4e00\u5f20\u5149\u5e55\uff0c\u81ea\u5df1\u5468\u8eab\u8981\u5bb3\u5c3d\u5728\u4ed6\u5251\u5149\u7b3c\u7f69\u4e4b\u4e0b\uff0c\u81ea\u77e5\u4e0d\u654c\uff0c\u6570\u5ea6\u60f3\u8981\u62db\u547c\u4e0b\u5c5e\u4e0a\u6765\u76f8\u52a9\uff0c\u4f46\u4e00\u60f3\u5230\u201c\u5927\u4f19\u513f\u9f50\u4e0a\u201d\u8fd9\u4e94\u4e2a\u5b57\u4e00\u51fa\u53e3\uff0c\u4e00\u751f\u82f1\u540d\u4fbf\u662f\u4ed8\u4e8e\u6d41\u6c34\uff0c\u603b\u662f\u5f3a\u884c\u5fcd\u4f4f\uff0c\u5fc3\u60f3\u81ea\u5df1\u65b9\u5f53\u58ee\u5e74\uff0c\u8fd9\u72ec\u81c2\u9053\u4eba\u5e74\u4e8b\u5df2\u9ad8\uff0c\u5251\u62db\u867d\u72e0\uff0c\u81ea\u5df1\u53ea\u8981\u4e45\u6218\u4e0d\u5c48\uff0c\u62d6\u5f97\u4e45\u4e86\uff0c\u5bf9\u65b9\u6c14\u529b\u7a0d\u8870\uff0c\u4fbf\u6709\u53ef\u4e58\u4e4b\u673a\u3002 \u3000\u3000\u65e0\u5c18\u9ad8\u547c\u9163\u6218\uff0c\u7cbe\u795e\u6108\u957f\u3002\u4f17\u4f8d\u536b\u77a7\u5f97\u5fc3\u4e0b\u9a87\u7136\uff0c\u4f46\u89c1\u4e24\u4eba\u5251\u5149\u5982\u8679\uff0c\u4f7f\u7684\u662f\u4ec0\u4e48\u62db\u6570\u65e9\u5df2\u5206\u8fa8\u4e0d\u6e05\u3002 \u3000\u3000\u5c0f\u4e18\u4e0a\u4f17\u4eba\u4e5f\u662f\u4e00\u58f0\u4e0d\u54cd\uff0c\u9759\u89c2\u4e24\u4eba\u5267\u6597\uff0c\u773c\u89c1\u65e0\u5c18\u6e10\u5360\u4e0a\u98ce\uff0c\u90fd\u60f3\uff1a\u201c\u9053\u957f\u82f1\u98ce\u5982\u6614\uff0c\u795e\u5a01\u4e0d\u51cf\u5f53\u5e74\uff0c\u53ef\u559c\u53ef\u8d3a\uff01\u201d \u3000\u3000\u731b\u542c\u5f97\u65e0\u5c18\u5927\u53eb\u4e00\u58f0\uff1a\u201c\u7740\uff01\u201d\u5f53\u7684\u4e00\u54cd\uff0c\u4e00\u5251\u523a\u5728\u5fb7\u5e03\u80f8\u53e3\uff0c\u8ddf\u7740\u53c8\u662f\u5580\u5587\u4e00\u58f0\uff0c\u624b\u4e2d\u957f\u5251\u5df2\u7136\u6298\u65ad\u3002\u539f\u6765\u5fb7\u5e03\u8863\u5185\u7a7f\u7740\u62a4\u80f8\u94a2\u7532\uff0c\u8fd9\u4e00\u5251\u867d\u7136\u523a\u4e2d\uff0c\u4ed6\u5374\u6beb\u65e0\u635f\u4f24\uff0c\u53cd\u800c\u6298\u4e86\u5bf9\u65b9\u957f\u5251\u3002\u65e0\u5c18\u4e00\u6014\u4e4b\u4e0b\uff0c\u5fb7\u5e03\u5df2\u4e00\u5251\u523a\u4e2d\u4ed6\u53f3\u80a9\u3002 \u3000\u3000\u5c0f\u4e18\u4e0a\u4f17\u4eba\u5927\u60ca\uff0c\u4e24\u4eba\u75be\u5954\u51b2\u4e0b\u6551\u63f4\u3002\u53ea\u542c\u5f97\u65e0\u5c18\u559d\u9053\uff1a\u201c\u725b\u5934\u63b7\u53c9\uff01\u201d\u624b\u4e2d\u65ad\u5251\u98de\u51fa\uff0c\u523a\u5165\u4e86\u5fb7\u5e03\u7684\u54bd\u5589\uff0c\u5fb7\u5e03\u5927\u53eb\u4e00\u58f0\uff0c\u5f80\u540e\u4fbf\u5230\u3002 \u3000\u3000\u65e0\u5c18\u54c8\u54c8\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u662f\u4f60\u8d62\uff0c\u8fd8\u662f\u6211\u8d62\uff1f\u201d\u5fb7\u5e03\u9888\u4e0a\u4e2d\u4e86\u65ad\u5251\uff0c\u867d\u4e0d\u81f4\u547d\uff0c\u5374\u5df2\u6597\u5fd7\u5168\u5931\uff0c\u98a4\u58f0\u9053\uff1a\u201c\u662f\u4f60\u8d62\uff01\u201d\u65e0\u5c18\u7b11\u9053\uff1a\u201c\u4f60\u63a5\u5f97\u6211\u8bb8\u591a\u5251\u62db\uff0c\u53c8\u80fd\u4f24\u6211\u80a9\u5934\uff0c\u5927\u662f\u4e0d\u6613\uff01\u597d\uff0c\u77a7\u5728\u4f60\u523a\u4f24\u6211\u4e00\u5251\u7684\u4efd\u4e0a\uff0c\u9976\u4e86\u4f60\u7684\u6027\u547d\uff01\u201d \u3000\u3000\u4e24\u540d\u4f8d\u536b\u62a2\u4e0a\u6276\u8d77\u5fb7\u5e03\uff0c\u9000\u5728\u4e00\u65c1\u3002 \u3000\u3000\u65e0\u5c18\u5f97\u610f\u6d0b\u6d0b\uff0c\u80a9\u4f24\u867d\u7136\u4e0d\u8f7b\uff0c\u5374\u662f\u6f2b\u4e0d\u5728\u4e4e\uff0c\u7f13\u7f13\u8d70\u4e0a\u571f\u4e18\uff0c\u8ba9\u4eba\u66ff\u4ed6\u5305\u624e\u4f24\u53e3\uff0c\u5140\u81ea\u6307\u6307\u70b9\u70b9\uff0c\u8bc4\u8bba\u80e1\u6590\u7684\u6b65\u6cd5\u3002 \u3000\u3000\u80e1\u6590\u5185\u606f\u7ef5\u7ef5\uff0c\u53ea\u89c9\u7cbe\u529b\u5df2\u590d\uff0c\u6df1\u6df1\u5438\u4e00\u53e3\u6c14\uff0c\u731b\u5730\u62a2\u653b\uff0c\u970e\u606f\u95f4\u62f3\u6253\u8db3\u8e22\uff0c\u4f46\u542c\u5f97\u201c\u554a\u54df\uff01\u201d\u201c\u54ce\u5440\uff01\u201d\u56db\u58f0\u547c\u53eb\uff0c\u5355\u5200\u3001\u94c1\u9524\u3001\u94a2\u97ad\u3001\u82b1\u67aa\uff0c\u56db\u822c\u5175\u5203\u5148\u540e\u98de\u51fa\u3002\u80e1\u6590\u98de\u8db3\u8e22\u5012\u4e24\u4eba\uff0c\u62f3\u5934\u6253\u6655\u4e00\u4eba\uff0c\u8ddf\u7740\u5de6\u638c\u638c\u529b\u4e00\u5410\uff0c\u5c06\u6700\u540e\u4e00\u540d\u536b\u58eb\u6253\u5f97\u53e3\u55b7\u9c9c\u8840\uff0c\u5341\u51e0\u4e2a\u7b4b\u6597\u6eda\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u4f46\u542c\u5f97\u5c0f\u4e18\u4e0a\u4f17\u4eba\u91c7\u58f0\u5927\u4f5c\u3002\u65e0\u5c18\u7684\u58f0\u97f3\u6700\u662f\u54cd\u4eae\uff1a\u201c\u5c0f\u80e1\u6590\uff0c\u6253\u5f97\u5999\u554a\uff01\u201d \u3000\u3000\u571f\u4e18\u4e0a\u5f69\u58f0\u672a\u6b47\uff0c\u53c8\u6709\u4e94\u540d\u4f8d\u536b\u6b3a\u8fd1\u80e1\u6590\u8eab\u8fb9\uff0c\u5374\u90fd\u7a7a\u624b\u4e0d\u6301\u5175\u5203\u3002\u5de6\u8fb9\u4e00\u4eba\u8bf4\u9053\uff1a\u201c\u5927\u5bb6\u7a7a\u624b\u6597\u7a7a\u624b\uff01\u201d\u80e1\u6590\u9053\uff1a\u201c\u597d\uff01\u201d\u521a\u8bf4\u5f97\u4e00\u4e2a\u201c\u597d\u201d\u5b57\uff0c\u7a81\u89c9\u53cc\u8db3\u5df2\u88ab\u4eba\u7d27\u7d27\u62b1\u4f4f\uff0c\u8ddf\u7740\u80cc\u4e0a\u53c8\u6709\u4e00\u4eba\u6251\u4e0a\uff0c\u624b\u81c2\u5982\u94c1\uff0c\u627c\u4f4f\u4e86\u4ed6\u7684\u5934\u9888\uff0c\u540c\u65f6\u53c8\u6709\u4e00\u4eba\u62b1\u4f4f\u4e86\u4ed6\u8170\uff0c\u53e6\u5916\u4e24\u4eba\u4fbf\u6765\u62c9\u4ed6\u53cc\u624b\u3002 \u3000\u3000\u539f\u6765\u8fd9\u4e00\u6b21\u5fb7\u5e03\u6240\u7387\u9886\u7684\u201c\u5927\u5185\u5341\u516b\u9ad8\u624b\u201d\u503e\u5de2\u800c\u51fa\u3002\u90a3\u201c\u5927\u5185\u5341\u516b\u9ad8\u624b\u201d\uff0c\u4e43\u662f\u201c\u56db\u6ee1\u3001\u4e94\u8499\u3001\u4e5d\u85cf\u50e7\u201d\u3002\u4e7e\u9686\u7687\u5e1d\u81ea\u4e0e\u7ea2\u82b1\u4f1a\u6253\u4e86\u4e00\u756a\u4ea4\u9053\u540e\uff0c\u4ece\u6b64\u4e0d\u4fe1\u6c49\u4eba\uff0c\u8fd1\u8eab\u4f8d\u536b\u4e00\u4e2a\u6c49\u4eba\u4e5f\u4e0d\u7528\uff0c\u90fd\u662f\u9009\u7528\u6ee1\u6d32\u3001\u8499\u53e4\u3001\u897f\u85cf\u7684\u52c7\u58eb\u5145\u4efb\u3002\u8fd9\u56db\u6ee1\u3001\u4e94\u8499\u3001\u4e5d\u85cf\u50e7\uff0c\u5c24\u4e3a\u5927\u5185\u4f8d\u536b\u4e2d\u7684\u7cbe\u9009\u3002\u8fd9\u4e94\u4e2a\u8499\u53e4\u4f8d\u536b\u64c5\u4e8e\u6454\u4ea4\u76f8\u6251\u4e4b\u6280\uff0c\u80e1\u6590\u4e00\u4e2a\u6ca1\u63d0\u9632\uff0c\u5df2\u88ab\u7f20\u4f4f\u3002 \u3000\u3000\u4ed6\u4e00\u60ca\u4e4b\u4e0b\uff0c\u968f\u5373\u5927\u559c\uff1a\u201c\u8fd9\u64d2\u62ff\u624b\u6cd5\uff0c\u6b63\u662f\u6211\u5bb6\u4f20\u6b66\u529f\u4e4b\u6240\u957f\u3002\u201d\u4f46\u89c9\u53cc\u624b\u5747\u88ab\u62c9\u4f4f\uff0c\u5f53\u4e0b\u8eab\u5b50\u5411\u540e\u4ef0\u8dcc\uff0c\u53cc\u624b\u987a\u52bf\u7528\u52b2\uff0c\u81ea\u5916\u671d\u5185\u4e00\u5408\uff0c\u7830\u7684\u4e00\u58f0\uff0c\u62c9\u4f4f\u4ed6\u53cc\u624b\u7684\u4e24\u540d\u4f8d\u536b\u8111\u95e8\u78b0\u8111\u95e8\uff0c\u540c\u65f6\u660f\u6655\u8fc7\u53bb\u3002 \u3000\u3000\u80e1\u6590\u53cc\u624b\u8131\u7f1a\uff0c\u53cd\u8fc7\u6765\u6293\u4f4f\u627c\u5728\u81ea\u5df1\u9888\u4e2d\u7684\u90a3\u53ea\u624b\uff0c\u4e00\u626d\u4e4b\u4e0b\uff0c\u5580\u7684\u4e00\u58f0\uff0c\u90a3\u4eba\u8155\u9aa8\u65e9\u65ad\uff0c\u8ddf\u7740\u5580\u5580\u4e24\u54cd\uff0c\u53c8\u626d\u65ad\u4e86\u62b1\u4f4f\u4ed6\u8170\u90a3\u4f8d\u536b\u7684\u81c2\u9aa8\u3002 \u3000\u3000\u8fd9\u4e94\u540d\u8499\u53e4\u4f8d\u536b\u6454\u4ea4\u4e4b\u6280\u751a\u662f\u7cbe\u6e5b\uff0c\u6c49\u6ee1\u8499\u56de\u85cf\u5404\u65cf\u6b66\u58eb\u4e2d\u6781\u5c11\u654c\u624b\u3002\u4f46\u6454\u4ea4\u8bb2\u7a76\u7684\u662f\u5c06\u5bf9\u624b\u6454\u5012\u538b\u4f4f\uff0c\u80e1\u6590\u8fd9\u822c\u5c0f\u5de7\u9634\u635f\u7684\u65ad\u9aa8\u64d2\u62ff\uff0c\u5374\u662f\u6454\u4ea4\u7684\u89c4\u77e9\u6240\u4e0d\u8bb8\u3002\u4e24\u540d\u4f8d\u536b\u9aa8\u8282\u6298\u65ad\uff0c\u5fc3\u4e2d\u5927\u662f\u4e0d\u5fff\uff0c\u867d\u5df2\u65e0\u529b\u518d\u6597\uff0c\u5374\u9f50\u58f0\u6012\u53eb\uff1a\u201c\u72af\u89c4\uff0c\u72af\u89c4\uff01\u201d\u5012\u662f\u53eb\u5f97\u7406\u76f4\u6c14\u58ee\u3002 \u3000\u3000\u80e1\u6590\u7b11\u9053\uff1a\u201c\u6253\u67b6\u8fd8\u6709\u89c4\u77e9\u4e48\uff1f\u4f60\u4eec\u4e94\u4e2a\u6253\u6211\u4e00\u4e2a\uff0c\u72af\u4e0d\u72af\u89c4\uff1f\u201d\u4e24\u540d\u8499\u53e4\u4f8d\u536b\u4e00\u60f3\u4e0d\u9519\uff0c\u4e94\u4e2a\u6253\u4e00\u4e2a\u662f\u5148\u574f\u4e86\u89c4\u77e9\uff0c\u90a3\u201c\u72af\u89c4\u201d\u4e24\u5b57\u4fbf\u558a\u4e0d\u51fa\u53e3\u4e86\u3002 \u3000\u3000\u4f59\u4e0b\u90a3\u4eba\u5140\u81ea\u6b7b\u547d\u62b1\u4f4f\u80e1\u6590\u53cc\u817f\uff0c\u4e00\u518d\u7528\u52b2\uff0c\u8981\u5c06\u4ed6\u6454\u5012\u3002\u80e1\u6590\u559d\u9053\uff1a\u201c\u4f60\u653e\u4e0d\u653e\u624b\uff1f\u201d\u90a3\u4eba\u53eb\u9053\uff1a\u201c\u81ea\u7136\u4e0d\u653e\u3002\u201d\u80e1\u6590\u5de6\u624b\u6293\u4e0b\uff0c\u634f\u4f4f\u4e86\u4ed6\u80cc\u5fc3\u4e0a\u201c\u5927\u690e\u7a74\u201d\u3002\u90a3\u4eba\u767b\u65f6\u5168\u8eab\u9ebb\u8f6f\uff0c\u53cc\u624b\u53ea\u5f97\u677e\u5f00\u3002\u80e1\u6590\u63d0\u8d77\u4ed6\u8eab\u5b50\uff0c\u53cc\u624b\u4f7f\u52b2\uff0c\u201c\u563f\u201d\u7684\u4e00\u58f0\uff0c\u5c06\u4ed6\u63b7\u51fa\u6570\u4e08\u4e4b\u5916\u3002\u4f46\u542c\u5f97\u6251\u901a\u4e00\u54cd\uff0c\u6c34\u82b1\u98de\u6e85\uff0c\u539f\u6765\u4ed6\u843d\u4e0b\u4e4b\u5904\uff0c\u7adf\u662f\u751f\u957f\u82a6\u82c7\u7684\u4e00\u4e2a\u70c2\u6ce5\u6c34\u5858\u3002\u90a3\u4eba\u6454\u5f97\u5934\u660f\u8111\u80c0\uff0c\u9677\u8eab\u6c61\u6ce5\u4e4b\u4e2d\uff0c\u54c7\u54c7\u5927\u53eb\u3002 \u3000\u3000\u80e1\u6590\u4e0e\u56db\u540d\u6ee1\u6d32\u4f8d\u536b\u6e38\u6597\u751a\u4e45\uff0c\u6253\u53d1\u8fd9\u4e94\u540d\u8499\u53e4\u4f8d\u536b\u5374\u662f\u5154\u8d77\u9e58\u843d\uff0c\u5e72\u51c0\u5229\u843d\u3002\u65c1\u89c2\u4f17\u4eba\u4f46\u89c1\u4e94\u540d\u4f8d\u536b\u4e00\u62e5\u800c\u4e0a\uff0c\u62d6\u624b\u62c9\u8db3\uff0c\u5c06\u4ed6\u64d2\u4f4f\uff0c\u8ddf\u7740\u4fbf\u662f\u7830\u562d\u3001\u5580\u5587\u3001\u554a\u54df\uff0c\u201c\u72af\u89c4\uff0c\u72af\u89c4\uff01\u201d\u6251\u901a\uff0c\u201c\u54c7\u54c7\uff01\u201d\u8bf8\u822c\u602a\u58f0\u4e0d\u7edd\u3002\u56db\u540d\u4f8d\u536b\u59d4\u987f\u5728\u5730\uff0c\u4e00\u540d\u4f8d\u536b\u98de\u8d8a\u6570\u4e08\uff0c\u6295\u8eab\u6c34\u5858\u3002 \u3000\u3000\u8fd9\u4e00\u6b21\u5c0f\u4e18\u4e0a\u4f17\u4eba\u4e0d\u518d\u559d\u5f69\uff0c\u5374\u662f\u8f70\u7136\u5927\u7b11\u3002 \u3000\u3000\u54c4\u7b11\u58f0\u4e2d\uff0c\u7ea2\u4e91\u95ea\u5904\uff0c\u4e5d\u540d\u85cf\u50e7\u5df2\u5404\u633a\u5175\u5203\u5c06\u80e1\u6590\u56e2\u56e2\u56f4\u4f4f\u3002\u8fd9\u4e5d\u4eba\u5175\u5203\u5404\u4e0d\u76f8\u540c\uff0c\u6216\u4f7f\u6212\u5200\uff0c\u6216\u4f7f\u9521\u6756\uff0c\u66f4\u6709\u4e9b\u5175\u5203\u5947\u5f62\u602a\u72b6\uff0c\u80e1\u6590\u4ece\u672a\u89c1\u8fc7\uff0c\u81ea\u4e5f\u53eb\u4e0d\u51fa\u540d\u76ee\u3002\u773c\u89c1\u8fd9\u4e5d\u540d\u85cf\u50e7\u6c14\u5ea6\u51dd\u91cd\uff0c\u4eba\u4eba\u4e00\u8a00\u4e0d\u53d1\uff0c\u77a7\u7740\u8fd9\u5408\u56f4\u4e4b\u52bf\uff0c\u6b65\u5c65\u95f4\u65e2\u8f7b\u4e14\u7a33\uff0c\u5b9e\u662f\u52b2\u654c\u3002\u4e5d\u50e7\u9519\u9519\u843d\u843d\uff0c\u4e1c\u7ad9\u4e00\u4e2a\uff0c\u897f\u7ad9\u4e00\u4e2a\uff0c\u4f3c\u662f\u5e03\u6210\u4e86\u9635\u52bf\u3002 \u3000\u3000\u80e1\u6590\u624b\u4e2d\u6ca1\u6709\u5175\u5203\uff0c\u4e0d\u7981\u5fc3\u60ca\uff0c\u8111\u4e2d\u4e00\u95ea\uff1a\u201c\u5411\u4e8c\u59b9\u8981\u5200\u5462\uff0c\u8fd8\u662f\u593a\u654c\u4eba\u7684\u6212\u5200\uff1f\u201d \u3000\u3000\u5ffd\u542c\u5f97\u5c0f\u4e18\u4e0a\u4e00\u4eba\u559d\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u63a5\u5200\uff01\u201d\u53ea\u89c1\u4e00\u67c4\u94a2\u5200\u81ea\u5c0f\u4e18\u4e0a\u63b7\u4e86\u4e0b\u6765\uff0c\u7834\u7a7a\u4e4b\u58f0\uff0c\u545c\u545c\u5927\u4f5c\uff0c\u8db3\u89c1\u8fd9\u4e00\u63b7\u7684\u52b2\u9053\u5927\u5f97\u60ca\u4eba\u3002\u80e1\u6590\u5fc3\u60f3\uff1a\u201c\u8d75\u4e09\u54e5\u7684\u670b\u53cb\u679c\u7136\u4e2a\u4e2a\u6b66\u827a\u7cbe\u5f3a\u3002 \u3000\u3000\u8981\u8fd9\u4e48\u4e00\u63b7\uff0c\u6211\u4fbf\u529e\u4e0d\u5230\u3002\u201d \u3000\u3000\u8fd9\u4e00\u5200\u98de\u6765\uff0c\u9996\u5f53\u5176\u51b2\u7684\u4e24\u540d\u85cf\u50e7\u7adf\u662f\u4e0d\u6562\u7528\u5175\u5203\u53bb\u7838\uff0c\u5206\u5411\u5de6\u53f3\u4e00\u8dc3\u95ea\u5f00\u3002\u80e1\u6590\u5fc3\u5ff5\u5feb\u5982\u7535\u5149\u822c\u7684\u4e00\u95ea\uff1a\u201c\u8fd9\u9635\u6cd5\u4e0d\u77e5\u5982\u4f55\u7834\u5f97\uff1f\u4ed6\u4e8c\u4eba\u95ea\u907f\u98de\u5200\uff0c\u6b63\u597d\u4e58\u673a\u6270\u4e71\u3002\u201d \u3000\u3000\u4ed6\u5ff5\u5934\u8f6c\u5f97\u6781\u5feb\uff0c\u90a3\u5355\u5200\u4e5f\u662f\u6765\u5f97\u6781\u5feb\u3002\u4ed6\u5fc3\u5ff5\u752b\u52a8\uff0c\u767d\u5149\u95ea\u5904\uff0c\u4e00\u67c4\u80cc\u539a\u5203\u8584\u7684\u94a2\u5200\u631f\u7740\u5a01\u731b\u5f02\u5e38\u7684\u7834\u7a7a\u4e4b\u58f0\u5df2\u98de\u5230\u9762\u524d\u3002\u80e1\u6590\u5374\u4e0d\u63a5\u5200\uff0c\u624b\u6307\u5728\u5200\u67c4\u4e0a\u4e00\u642d\uff0c\u8f7b\u8f7b\u62e8\u52a8\u3002\u90a3\u94a2\u5200\u98de\u6765\u4e4b\u52bf\u751a\u731b\uff0c\u5230\u4ed6\u9762\u524d\u65f6\u5140\u81ea\u529b\u9053\u5f3a\u52b2\uff0c\u7ed9\u4ed6\u62e8\u5f97\u6389\u8fc7\u65b9\u5411\uff0c\u6fc0\u5c04\u800c\u4e0a\uff0c\u76f4\u51b2\u4e0a\u5929\u3002 \u3000\u3000\u4e5d\u540d\u85cf\u50e7\u5747\u611f\u5947\u602a\uff0c\u60c5\u4e0d\u81ea\u7981\u7684\u62ac\u5934\u800c\u671b\u3002\u80e1\u6590\u6240\u4e89\u7684\u4fbf\u5728\u8fd9\u7a0d\u7eb5\u5373\u901d\u7684\u826f\u673a\uff0c\u6b3a\u8eab\u62a2\u5230\u624b\u6301\u6210\u5200\u7684\u85cf\u50e7\u8eab\u7554\uff0c\u4e00\u4f38\u624b\u5df2\u5c06\u4ed6\u6212\u5200\u593a\u8fc7\uff0c\u970e\u65f6\u95f4\u5c55\u5f00\u201c\u80e1\u5bb6\u5feb\u5200\u201d\uff0c\u624b\u8d77\u5200\u843d\uff0c\u4e00\u9635\u731b\u780d\u5feb\u5241\uff0c\u8fc5\u6377\u5982\u98ce\u3002\u8fd9\u65f6\u4e0b\u624b\u7adf\u4e0d\u5bb9\u60c5\uff0c\u4e5d\u540d\u85cf\u50e7\u65e0\u4e00\u5f97\u514d\uff0c\u4e0d\u662f\u65ad\u81c2\uff0c\u4fbf\u662f\u6298\u8db3\u3002\u4e5d\u50e7\u5404\u8d1f\u7edd\u827a\uff0c\u53ea\u56e0\u4e00\u65f6\u5931\u5bdf\uff0c\u4e2d\u4e86\u8bf1\u654c\u5206\u5fc3\u4e4b\u8ba1\uff0c\u9877\u523b\u4e4b\u95f4\uff0c\u5c3d\u7686\u8eab\u53d7\u91cd\u4f24\uff0c\u60e8\u547c\u5012\u5730\u3002 \u3000\u3000\u8fd9\u4e00\u573a\u80e1\u6590\u53ef\u8bf4\u80dc\u5f97\u6781\u5de7\uff0c\u4e5f\u662f\u80dc\u5f97\u6781\u9669\u3002 \u3000\u3000\u4e00\u8f6e\u5feb\u5200\u780d\u5b8c\uff0c\u5934\u9876\u90a3\u5200\u521a\u597d\u843d\u4e0b\uff0c\u4ed6\u63b7\u5f00\u6212\u5200\uff0c\u4f38\u624b\u63a5\u4f4f\uff0c\u5200\u4e00\u5165\u624b\uff0c\u53ea\u89c9\u751a\u662f\u6c89\u91cd\uff0c\u6bd4\u5bfb\u5e38\u5355\u5200\u91cd\u4e86\u4e24\u500d\u6709\u4f59\uff0c\u60f3\u89c1\u5200\u4e3b\u8182\u529b\u5947\u5927\uff0c\u6708\u5149\u4e0b\u6620\u7167\u4e00\u770b\uff0c\u53ea\u89c1\u5200\u67c4\u4e0a\u523b\u7740\u4e09\u5b57\uff1a \u3000\u3000\u201c\u5954\u96f7\u624b\uff01\u201d \u3000\u3000\u80e1\u6590\u5927\u559c\uff0c\u53eb\u9053\uff1a\u201c\u591a\u8c22\u6587\u56db\u7237\u63b7\u5200\u76f8\u52a9\uff01\u201d \u3000\u3000\u84e6\u5730\u80cc\u540e\u4e00\u4e2a\u82cd\u8001\u7684\u58f0\u97f3\u53eb\u9053\uff1a\u201c\u770b\u5251\uff01\u201d\u8bdd\u58f0\u672a\u7edd\uff0c\u98ce\u58f0\u98d2\u7136\uff0c\u5df2\u81f3\u80cc\u5fc3\u3002\u80e1\u6590\u4e00\u58f0\uff1a\u201c\u6b64\u4eba\u5251\u6cd5\u5982\u6b64\u51cc\u5389\uff01\u201d\u6025\u5fd9\u56de\u5200\u6321\u67b6\uff0c\u5c82\u77e5\u654c\u5251\u5df2\u7136\u64a4\u56de\uff0c\u8ddf\u7740\u53c8\u662f\u4e00\u5251\u523a\u5230\u3002\u80e1\u6590\u53cd\u624b\u518d\u6321\uff0c\u53c8\u662f\u6321\u4e86\u4e2a\u7a7a\u3002 \u3000\u3000\u4ed6\u6025\u6b32\u8f6c\u8eab\u8fce\u654c\uff0c\u4f46\u80cc\u540e\u90a3\u654c\u4eba\u7684\u5251\u62db\u6765\u5f97\u597d\u4e0d\u8fc5\u6377\uff0c\u7adf\u662f\u903c\u5f97\u4ed6\u65e0\u6687\u8f6c\u8eab\u3002\u4ed6\u5fc3\u4e2d\u5927\u9a87\uff0c\u6025\u7eb5\u800c\u524d\uff0c\u8dc3\u51fa\u534a\u4e08\uff0c\u5de6\u8db3\u4e00\u843d\u5730\uff0c\u5f85\u8981\u8f6c\u8eab\uff0c\u4e0d\u6599\u654c\u4eba\u5982\u5f71\u968f\u5f62\uff0c\u5251\u62db\u53c8\u5df2\u9012\u5230\u3002\u8fd9\u4eba\u5728\u80cc\u540e\u8fde\u523a\u4e94\u5251\uff0c\u80e1\u6590\u63a5\u8fde\u6321\u4e86\u4e94\u6b21\u7a7a\uff0c\u59cb\u7ec8\u65e0\u6cd5\u56de\u8eab\u89c1\u654c\u4e4b\u9762\u3002 \u3000\u3000\u80e1\u6590\u6076\u6597\u534a\u5bb5\uff0c\u548c\u5feb\u5251\u65e0\u53cc\u7684\u65e0\u5c18\u9053\u4eba\u6218\u6210\u5e73\u624b\uff0c\u63a5\u7740\u8fde\u4f24\u56db\u6ee1\u3001\u4e94\u8499\u3001\u4e5d\u85cf\u50e7\u5927\u5185\u5341\u516b\u9ad8\u624b\uff0c\u4e0d\u6599\u5230\u540e\u6765\u7adf\u7ed9\u4eba\u4e00\u52a0\u5077\u88ad\uff0c\u903c\u5f97\u96be\u4ee5\u8f6c\u8eab\u3002 \u3000\u3000\u8fd9\u5df2\u662f\u5904\u4e8e\u5fc5\u8d25\u4e4b\u52bf\uff0c\u4ed6\u60f6\u6025\u4e4b\u4e0b\uff0c\u884c\u9669\u4fa5\u5e78\uff0c\u4f46\u542c\u5f97\u80cc\u540e\u654c\u5251\u53c8\u81f3\uff0c\u8fd9\u4e00\u6b21\u7adf\u4e0d\u62db\u67b6\uff0c\u5411\u524d\u4e00\u6251\uff0c\u4fef\u5367\u5411\u5730\uff0c\u8ddf\u7740\u4e00\u4e2a\u7ffb\u8eab\uff0c\u8138\u5df2\u5411\u5929\uff0c\u8fd9\u624d\u4e00\u5200\u6a2a\u780d\uff0c\u8361\u5f00\u654c\u5251\u3002 \u3000\u3000\u53ea\u542c\u654c\u4eba\u8d5e\u9053\uff1a\u201c\u597d\uff01\u201d\u5de6\u638c\u62cd\u5411\u4ed6\u7684\u80f8\u53e3\u3002\u80e1\u6590\u4e5f\u662f\u5de6\u638c\u62cd\u51fa\uff0c\u53cc\u638c\u76f8\u4ea4\uff0c\u53ea\u89c9\u654c\u4eba\u638c\u529b\u751a\u662f\u67d4\u548c\u6d51\u539a\uff0c\u4f46\u67d4\u548c\u4e4b\u4e2d\uff0c\u5374\u9690\u85cf\u7740\u4e00\u80a1\u8f9b\u8fa3\u7684\u715e\u6c14\u3002\u80e1\u6590\u731b\u7136\u60f3\u8d77\u4e00\u4e8b\uff0c\u8131\u53e3\u53eb\u9053\uff1a\u201c\u539f\u6765\u662f\u4f60\uff01\u201d \u3000\u3000\u90a3\u4eba\u4e5f\u53eb\u9053\uff1a\u201c\u539f\u6765\u662f\u4f60\uff01\u201d \u3000\u3000\u539f\u6765\u4e24\u4eba\u624b\u638c\u76f8\u4ea4\uff0c\u5747\u5373\u5bdf\u89c9\u5bf9\u65b9\u4fbf\u662f\u5728\u798f\u5eb7\u5b89\u5e9c\u6697\u4e2d\u76f8\u6551\u5c11\u5e74\u4e66\u751f\u5fc3\u781a\u4e4b\u4eba\uff0c\u5404\u81ea\u5411\u540e\u8dc3\u5f00\u6570\u6b65\u3002 \u3000\u3000\u80e1\u6590\u51dd\u795e\u770b\u65f6\uff0c\u89c1\u90a3\u4eba\u767d\u987b\u98d8\u52a8\uff0c\u76f8\u8c8c\u53e4\u96c5\uff0c\u624b\u4e2d\u957f\u5251\u5982\u6c34\uff0c\u5374\u662f\u6b66\u5f53\u6d3e\u638c\u95e8\u4eba\u65e0\u9752\u5b50\uff0c\u4e0d\u7531\u5f97\u4e00\u5446\uff0c\u4e00\u65f6\u4e0d\u77e5\u4ed6\u662f\u53cb\u662f\u654c\u3002 \u3000\u3000\u53ea\u542c\u65e0\u5c18\u9053\u4eba\u7b11\u9053\uff1a\u201c\u83f2\u9752\u5144\uff0c\u4f60\u8bf4\u6211\u8fd9\u4e2a\u5c0f\u8001\u5f1f\u6b66\u529f\u5982\u4f55\uff1f\u201d\u65e0\u9752\u5b50\u7b11\u9053\uff1a\u201c\u80fd\u8ddf\u65e0\u5c18\u9053\u4eba\u6597\u5f97\u4e0a\u4e94\u767e\u62db\uff0c\u5929\u4e0b\u80fd\u6709\u51e0\u4eba\uff1f\u8001\u9053\u5f53\u771f\u662f\u5b64\u964b\u5be1\u95fb\uff0c\u7adf\u4e0d\u77e5\u6b66\u6797\u4e2d\u51fa\u4e86\u8fd9\u7b49\u5c11\u5e74\u82f1\u96c4\u3002\u201d\u8bf4\u7740\u957f\u5251\u5165\u9798\uff0c\u4e0a\u524d\u62c9\u7740\u80e1\u6590\u7684\u624b\uff0c\u597d\u751f\u4eb2\u70ed\u3002 \u3000\u3000\u80e1\u6590\u89c1\u4ed6\u82f1\u6c14\u52c3\u52c3\uff0c\u54ea\u91cc\u8fd8\u662f\u638c\u95e8\u4eba\u5927\u4f1a\u4e2d\u6240\u89c1\u90a3\u4e2a\u660f\u660f\u6b32\u7761\u7684\u8001\u9053\uff0c\u751a\u4ee5\u4e3a\u5947\u3002 \u3000\u3000\u65e0\u5c18\u4ece\u5c0f\u4e18\u4e0a\u8d70\u4e86\u4e0b\u6765\uff0c\u7b11\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u8fd9\u4e2a\u725b\u9f3b\u5b50\uff0c\u51fa\u5bb6\u4ee5\u524d\u53eb\u505a\u7ef5\u91cc\u9488\u9646\u83f2\u9752\u3002\u4f60\u53eb\u4ed6\u4e00\u58f0\u5927\u54e5\u5427\u3002\u201d\u80e1\u6590\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u2018\u7ef5\u91cc\u9488\u9646\u83f2\u9752\u2019\u5f53\u5e74\u5a01\u9707\u5929\u4e0b\uff0c\u6210\u540d\u5df2\u5782\u6570\u5341\u5e74\uff0c\u60f3\u4e0d\u5230\u4eca\u65e5\u6709\u5e78\u548c\u4ed6\u4ea4\u624b\u3002\u201d\u6025\u5fd9\u62dc\u5012\uff0c\u8bf4\u9053\uff1a\u201c\u665a\u8f88\u80e1\u6590\uff0c\u53e9\u89c1\u9053\u957f\u3002\u201d\u5ffd\u542c\u8eab\u540e\u4e00\u4e2a\u58f0\u97f3\u9053\uff1a\u201c\u6309\u7406\u8bf4\uff0c\u4f60\u539f\u662f\u665a\u8f88\uff0c\u53ef\u662f\uff0c\u597d\u5144\u5f1f\uff0c\u4ed6\u662f\u6211\u7684\u62dc\u628a\u5b50\u8001\u54e5\u554a\u3002\u201d \u3000\u3000\u80e1\u6590\u4e00\u8dc3\u800c\u8d77\uff0c\u53ea\u89c1\u8eab\u540e\u4e00\u4eba\u957f\u888d\u9a6c\u8902\uff0c\u80a5\u80a5\u80d6\u80d6\uff0c\u6b63\u662f\u5343\u81c2\u5982\u6765\u8d75\u534a\u5c71\u3002\u80e1\u6590\u5bf9\u8fd9\u4f4d\u4e49\u5144\u522b\u6765\u65e0\u65e5\u4e0d\u601d\uff0c\u4f38\u81c2\u7d27\u7d27\u62b1\u4f4f\uff0c\u53eb\u9053\uff1a\u201c\u4e09\u54e5\uff0c\u4f60\u53ef\u60f3\u715e\u5c0f\u5f1f\u4e86\u3002\u201d \u3000\u3000\u8d75\u534a\u5c71\u62c9\u7740\u4ed6\u8f6c\u8fc7\u8eab\u6765\uff0c\u8ba9\u6708\u5149\u7167\u5728\u4ed6\u7684\u8138\u4e0a\uff0c\u51dd\u76ee\u77a7\u4e86\u534a\u664c\uff0c\u559c\u9053\uff1a\u201c\u5144\u5f1f\uff0c\u4f60\u7ec8\u4e8e\u957f\u5927\u6210\u4eba\u4e86\u3002\u505a\u54e5\u54e5\u7684\u4eca\u65e5\u4eb2\u773c\u89c1\u4f60\u8fde\u8d25\u5927\u5185\u5341\u516b\u9ad8\u624b\uff0c\u5b9e\u5728\u662f\u6b22\u559c\u5f97\u7d27\u3002\u201d \u3000\u3000\u80e1\u6590\u5fc3\u4e2d\u4e5f\u662f\u6b22\u559c\u4e0d\u5c3d\u3002\u8fd9\u65f6\u6e05\u5bab\u4f17\u4f8d\u536b\u65e9\u5df2\u9003\u5f97\u5e72\u5e72\u51c0\u51c0\u3002\u4ed6\u5f53\u4e0b\u62c9\u4e86\u7a0b\u7075\u7d20\u8fc7\u6765\uff0c\u548c\u65e0\u5c18\u3001\u8d75\u534a\u5c71\u7b49\u5f15\u89c1\u3002 \u3000\u3000\u8d75\u534a\u5c71\u9053\uff1a\u201c\u5144\u5f1f\uff0c\u7a0b\u5bb6\u59b9\u5b50\uff0c\u6211\u5e26\u4f60\u4eec\u53bb\u89c1\u6211\u4eec\u603b\u8235\u4e3b\u3002\u201d \u3000\u3000\u80e1\u6590\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u9648\u603b\u8235\u4e3b\u2026\u2026\u4ed6\u2026\u2026\u8001\u4eba\u5bb6\u4e5f\u6765\u4e86\u4e48\uff1f\u201d \u3000\u3000\u65e0\u5c18\u7b11\u9053\uff1a\u201c\u4ed6\u65e9\u6328\u8fc7\u4f60\u4e00\u987f\u75db\u9a82\u5566\uff0c\u4ec0\u4e48\u4f24\u5929\u5bb3\u7406\uff0c\u4ec0\u4e48\u8d1f\u5fc3\u8584\u5e78\uff0c\u53ea\u9a82\u5f97\u4ed6\u72d7\u8840\u6dcb\u5934\u3002\u54c8\u54c8\uff01\u6211\u4eec\u603b\u8235\u4e3b\u4e00\u751f\u4e4b\u4e2d\uff0c\u53ea\u6015\u4ece\u672a\u6328\u8fc7\u8fd9\u822c\u5389\u5bb3\u7684\u81ed\u9a82\u3002\u201d\u80e1\u6590\u8fd9\u4e00\u60ca\u66f4\u662f\u975e\u540c\u5c0f\u53ef\uff0c\u98a4\u58f0\u9053\uff1a\u201c\u90a3\u2026\u2026\u90a3\u798f\u5eb7\u5b89\u2026\u2026\u201d \u3000\u3000\u9646\u83f2\u9752\u5fae\u7b11\u9053\uff1a\u201c\u9648\u603b\u8235\u4e3b\u7684\u76f8\u8c8c\u548c\u798f\u5eb7\u5b89\u679c\u7136\u5f88\u50cf\uff0c\u522b\u8bf4\u5c0f\u5144\u5f1f\u548c\u4ed6\u4e8c\u4eba\u90fd\u4e0d\u76f8\u719f\uff0c\u4fbf\u662f\u65e5\u5e38\u89c1\u9762\u4e4b\u4eba\uff0c\u4e5f\u4f1a\u8ba4\u9519\u3002\u201d \u3000\u3000\u65e0\u5c18\u7b11\u9053\uff1a\u201c\u60f3\u5f53\u5e74\u5728\u676d\u5dde\u57ce\u5916\uff0c\u603b\u8235\u4e3b\u4fbf\u66fe\u5047\u626e\u4e86\u798f\u5eb7\u5b89\uff0c\u64d2\u4f4f\u90a3\u4e2a\u4ec0\u4e48\u5a01\u9707\u6cb3\u6714\u738b\u7ef4\u626c\u2026\u2026\u201d \u3000\u3000\u80e1\u6590\u5341\u5206\u60f6\u6050\uff0c\u9053\uff1a\u201c\u4e09\u54e5\uff0c\u4f60\u5feb\u5e26\u6211\u53bb\u8ddf\u9648\u603b\u8235\u4e3b\u78d5\u5934\u8d54\u7f6a\u3002\u201d\u8d75\u534a\u5c71\u7b11\u9053\uff1a\u201c\u4e0d\u77e5\u8005\u4e0d\u7f6a\u3002\u603b\u8235\u4e3b\u8ddf\u4f60\u4ea4\u4e86\u4e00\u638c\uff0c\u5f88\u79f0\u8d5e\u4f60\u6b66\u529f\u4e86\u5f97\uff0c\u53c8\u8bf4\u4f60\u6c14\u8282\u51db\u7136\uff0c\u80cc\u5730\u91cc\u8bf4\u4e86\u4f60\u8bb8\u591a\u597d\u8bdd\u5462\u3002\u201d \u3000\u3000\u4e24\u4eba\u8fd8\u672a\u4e0a\u4e18\uff0c\u9648\u5bb6\u6d1b\u5df2\u7387\u9886\u7fa4\u96c4\u4ece\u571f\u4e18\u4e0a\u8fce\u4e86\u4e0b\u6765\u3002\u80e1\u6590\u62dc\u5012\u5728\u5730\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u4eba\u778e\u4e86\u773c\u73e0\uff0c\u5192\u72af\u603b\u8235\u4e3b\uff0c\u5b9e\u662f\u7f6a\u8be5\u2026\u2026\u201d \u3000\u3000\u9648\u5bb6\u6d1b\u4e0d\u7b49\u4ed6\u8bf4\u5b8c\uff0c\u6025\u5fd9\u4f38\u624b\u6276\u8d77\uff0c\u7b11\u9053\uff1a\u201c\u2018\u5927\u4e08\u592b\u53ea\u6015\u6b63\u4eba\u541b\u5b50\uff0c\u54ea\u6015\u9e70\u72ac\u5974\u624d\uff1f\u2019\u6211\u4eca\u65e5\u4e00\u5230\u5317\u4eac\uff0c\u4fbf\u542c\u5230\u8fd9\u4e24\u53e5\u75db\u5feb\u6dcb\u6f13\u4e4b\u8a00\u3002\u5c0f\u5144\u5f1f\uff0c\u4fbf\u51ed\u4f60\u8fd9\u4e24\u53e5\u8bdd\uff0c\u6211\u4eec\u4fbf\u4e0d\u6789\u4e86\u4e07\u91cc\u8fe2\u8fe2\u7684\u8d70\u8fd9\u4e00\u906d\u3002\u201d \u3000\u3000\u5f53\u4e0b\u8d75\u534a\u5c71\u62c9\u7740\u4ed6\u4e00\u4e00\u7ed9\u7fa4\u96c4\u5f15\u89c1\u3002\u80e1\u6590\u5bf9\u8fd9\u5e72\u4eba\u5fc3\u4eea\u5df2\u4e45\uff0c\u4eca\u665a\u4eb2\u773c\u5f97\u89c1\uff0c\u559c\u6170\u65e0\u5df2\uff0c\u5bf9\u6587\u6cf0\u6765\u63b7\u5200\u76f8\u52a9\u3001\u9a86\u51b0\u8d60\u9001\u5b9d\u9a6c\uff0c\u66f4\u662f\u8fde\u8fde\u79f0\u8c22\uff0c\u606d\u606d\u656c\u656c\u7684\u4ea4\u8fd8\u4e86\u6587\u6cf0\u6765\u7684\u94a2\u5200\uff0c\u4ece\u5730\u4e0b\u62fe\u8d77\u6e05\u5bab\u4f8d\u536b\u9057\u4e0b\u7684\u4e00\u67c4\u5355\u5200\uff0c\u63d2\u5165\u4e86\u8170\u95f4\u5200\u9798\u3002\u4ed6\u81ea\u5df1\u7684\u5355\u5200\u4e3a\u94c1\u9524\u6240\u51fb\uff0c\u5200\u53e3\u5377\u8fb9\uff0c\u5df2\u7136\u65e0\u7528\u3002\u8ddf\u7740\u5fc3\u781a\u8fc7\u6765\u5411\u4ed6\u9053\u8c22\u5728\u798f\u5eb7\u5b89\u5e9c\u4e2d\u89e3\u7a74\u76f8\u6551\u4e4b\u5fb7\u3002\u65e0\u5c18\u9038\u5174\u6a2a\u98de\uff0c\u6307\u624b\u5212\u811a\uff0c\u8c08\u8bba\u9002\u624d\u548c\u80e1\u6590\u53ca\u5fb7\u5e03\u4e24\u4eba\u7684\u6597\u5251\uff0c\u8bf4\u4eca\u665a\u8fd9\u4e24\u573a\u67b6\u6253\u5f97\u9163\u7545\u8fc7\u763e\uff0c\u751f\u5e73\u5c11\u6709\u3002 \u3000\u3000\u9646\u83f2\u9752\u7b11\u9053\uff1a\u201c\u9053\u957f\uff0c\u8bf4\u5230\u6b66\u529f\uff0c\u54b1\u4eec\u8fd9\u4f4d\u5c0f\u5144\u5f1f\u5b9e\u662f\u5341\u5206\u4e86\u5f97\u3002\u53ef\u662f\u8fd8\u6709\u4e00\u4f4d\u5c11\u5e74\u82f1\u96c4\uff0c\u6bd4\u4ed6\u66f4\u5389\u5bb3\u5341\u500d\uff0c\u4f60\u662f\u51b3\u8ba1\u6597\u4ed6\u4e0d\u8fc7\u7684\u3002\u201d\u65e0\u5c18\u53c8\u662f\u9ad8\u5174\uff0c\u53c8\u662f\u4e0d\u670d\uff0c\u5fd9\u95ee\uff1a\u201c\u662f\u8c01\uff0c\u662f\u8c01\uff1f\u8fd9\u4eba\u5728\u54ea\u91cc\uff1f\u201d\u9646\u83f2\u9752\u6447\u5934\u9053\uff1a\u201c\u4f60\u51b3\u975e\u5bf9\u624b\uff0c\u6211\u529d\u4f60\u8fd8\u662f\u522b\u627e\u4ed6\u7684\u597d\u3002\u201d\u65e0\u5c18\u9053\uff1a\u201c\u5478\uff01\u54b1\u4eec\u8001\u54e5\u513f\u4fe9\u5206\u624b\u591a\u5e74\uff0c\u4e00\u89c1\u9762\u4f60\u5c31\u6765\u80e1\u5439\u3002\u6211\u4e0d\u4fe1\u6709\u8fd9\u7b49\u5389\u5bb3\u4eba\u7269\u3002\u201d \u3000\u3000\u9646\u83f2\u9752\u9053\uff1a\u201c\u6628\u665a\u798f\u5eb7\u5b89\u5e9c\u4e2d\uff0c\u5929\u4e0b\u5404\u95e8\u5404\u6d3e\u638c\u95e8\u4eba\u5927\u805a\u4f1a\uff0c\u4f1a\u4e2d\u9ad8\u624b\u5982\u4e91\uff0c\u5404\u6709\u5404\u7684\u80fd\u8010\uff0c\u5404\u6709\u5404\u7684\u7edd\u6280\u3002\u8fd9\u8bdd\u4e0d\u9519\u5427\uff1f\u201d\u65e0\u5c18\u9053\uff1a\u201c\u4e0d\u9519\u4fbf\u600e\u6837\uff1f\u201d\u9646\u83f2\u9752\u9053\uff1a\u201c\u5fc3\u781a\u8001\u5f1f\u53bb\u6363\u4e71\u5927\u4f1a\uff0c\u5931\u624b\u88ab\u64d2\u3002\u8d75\u4e09\u5f1f\u8fd9\u7b49\u672c\u4e8b\uff0c\u4e5f\u53ea\u62a2\u5f97\u4e00\u53ea\u7389\u9f99\u676f\u3002 \u3000\u3000\u897f\u5ddd\u53cc\u4fa0\u5e38\u6c0f\u5144\u5f1f\u9a7e\u4e34\uff0c\u53ea\u6551\u4e86\u4e24\u4e2a\u4eba\u51fa\u6765\u3002\u53ef\u662f\u90a3\u4f4d\u5c11\u5e74\u82f1\u96c4\u54ea\uff0c\u53ea\u4e0d\u8fc7\u773c\u775b\u4e00\u970e\uff0c\u4fbf\u4ece\u4e03\u4f4d\u9ad8\u624b\u7684\u624b\u4e2d\u62a2\u4e0b\u4e03\u53ea\u7389\u9f99\u676f\uff0c\u6454\u5728\u5730\u4e0b\u7838\u5f97\u7c89\u788e\u3002\u4ed6\u53ea\u55b7\u5f97\u51e0\u53e3\u6c14\uff0c\u4fbf\u53eb\u798f\u5eb7\u5b89\u7684\u638c\u95e8\u4eba\u5927\u4f1a\u70df\u98de\u7070\u706d\uff0c\u98ce\u6d88\u4e91\u6563\u3002\u9053\u957f\uff0c\u4f60\u6597\u4e0d\u6597\u5f97\u8fc7\u8fd9\u4f4d\u5c11\u5e74\u82f1\u96c4\uff1f\u201d \u3000\u3000\u7a0b\u7075\u7d20\u77e5\u4ed6\u5728\u8bf4\u81ea\u5df1\uff0c\u8138\u513f\u98de\u7ea2\uff0c\u8eb2\u5230\u4e86\u80e1\u6590\u8eab\u540e\uff0c\u9ed1\u591c\u4e4b\u4e2d\uff0c\u4eba\u4eba\u90fd\u5728\u503e\u542c\u9646\u83f2\u9752\u8bf4\u8bdd\uff0c\u8c01\u4e5f\u6ca1\u5bf9\u5979\u7559\u5fc3\u3002 \u3000\u3000\u4e00\u4e2a\u5c11\u5e74\u7f8e\u5987\u8bf4\u9053\uff1a\u201c\u5e08\u7236\uff0c\u6211\u4eec\u53ea\u542c\u8bf4\u90a3\u638c\u95e8\u4eba\u5927\u4f1a\u7ed9\u4eba\u6405\u6563\u4e86\u5c40\uff0c\u5230\u5e95\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u4f60\u5feb\u8bf4\u5427\uff01\u201d\u8fd9\u7f8e\u5987\u662f\u91d1\u7b1b\u79c0\u624d\u4f59\u9c7c\u540c\u4e4b\u59bb\u674e\u6c85\u82b7\u3002 \u3000\u3000\u9646\u83f2\u9752\u4e8e\u662f\u5c06\u4e00\u4f4d\u201c\u5c11\u5e74\u82f1\u96c4\u201d\u5982\u4f55\u65bd\u5de7\u8ba1\u7838\u788e\u4e03\u53ea\u7389\u9f99\u676f\uff0c\u5982\u4f55\u55b7\u70df\u4e0b\u6bd2\u3001\u4f7f\u5f97\u4eba\u4eba\u809a\u75db\u3001\u56e0\u800c\u7591\u5fc3\u798f\u5eb7\u5b89\u6bd2\u5bb3\u5929\u4e0b\u82f1\u96c4\uff0c\u5982\u4f55\u4f17\u4eba\u5728\u6df7\u4e71\u4e2d\u4e00\u54c4\u800c\u6563\uff0c\u8bf8\u822c\u60c5\u7531\uff0c\u4e00\u4e00\u8bf4\u4e86\u3002\u7fa4\u96c4\u542c\u4e86\uff0c\u65e0\u4e0d\u8d5e\u53f9\u3002 \u3000\u3000\u65e0\u5c18\u9053\uff1a\u201c\u9646\u5144\uff0c\u4f60\u8bf4\u4e86\u534a\u5929\uff0c\u8fd9\u4f4d\u5c11\u5e74\u82f1\u96c4\u5230\u5e95\u662f\u8c01\uff0c\u5374\u59cb\u7ec8\u6ca1\u8bf4\u3002\u201d\u9646\u83f2\u9752\u7b11\u9053\uff1a\u201c\u8fdc\u5728\u5929\u8fb9\uff0c\u8fd1\u5728\u773c\u524d\uff0c\u8fd9\u4f4d\u7a0b\u59d1\u5a18\u4fbf\u662f\u3002\u201d\u62c9\u7740\u80e1\u6590\u7684\u624b\uff0c\u5c06\u4ed6\u8f7b\u8f7b\u4e00\u62c9\uff0c\u9732\u51fa\u4e86\u7a0b\u7075\u7d20\u7684\u8eab\u5b50\u3002 \u3000\u3000\u7fa4\u96c4\u201c\u554a\u201d\u7684\u4e00\u58f0\uff0c\u4e00\u9f50\u671b\u7740\u5979\uff0c\u8c01\u90fd\u4e0d\u4fe1\u8fd9\u6837\u4e00\u4e2a\u7626\u5f31\u6587\u79c0\u7684\u5c0f\u59d1\u5a18\uff0c\u7adf\u4f1a\u5c06\u798f\u5eb7\u5b89\u8fd9\u7b79\u5212\u7ecf\u5e74\u7684\u5929\u4e0b\u638c\u95e8\u4eba\u5927\u4f1a\u6bc1\u4e8e\u6307\u638c\u4e4b\u95f4\uff0c\u53ef\u662f\u9646\u83f2\u9752\u671b\u91cd\u6b66\u6797\uff0c\u5c82\u80fd\u4fe1\u53e3\u80e1\u8a00\uff1f\u8fd9\u5374\u53c8\u4e0d\u7531\u5f97\u4eba\u4e0d\u4fe1\u3002 \u3000\u3000\u539f\u6765\u9646\u83f2\u9752\u4e8e\u5341\u5e74\u524d\u56e0\u540c\u95e8\u7978\u53d8\uff0c\u5e08\u5144\u9a6c\u94b0\u3001\u5e08\u5f1f\u5f20\u53ec\u91cd\u5148\u540e\u60e8\u6b7b\uff0c\u6b66\u5f53\u6d3e\u773c\u89c1\u5f0f\u5fae\uff0c\u4e8e\u662f\u4ed6\u63a5\u638c\u95e8\u6237\uff0c\u7740\u610f\u6574\u987f\u3002 \u3000\u3000\u56e0\u6050\u6e05\u5ef7\u7591\u5fcc\uff0c\u7d22\u6027\u4fbf\u51fa\u4e86\u5bb6\uff0c\u9053\u53f7\u65e0\u9752\u5b50\uff0c\u5341\u5e74\u6765\u6df1\u5c45\u7b80\u51fa\uff0c\u671d\u5ef7\u4e5f\u5c31\u6ca1\u52a0\u6ce8\u76ee\u3002 \u3000\u3000\u8fd9\u6b21\u798f\u5eb7\u5b89\u53ec\u5f00\u638c\u95e8\u4eba\u5927\u4f1a\uff0c\u4e00\u6765\u6b66\u5f53\u6d3e\u81ea\u6765\u4e0e\u5c11\u6797\u6d3e\u9f50\u540d\uff0c\u662f\u6b66\u6797\u4e2d\u6700\u5927\u95e8\u6d3e\u4e4b\u4e00\uff1b\u4e8c\u6765\u5ff5\u7740\u6b66\u5f53\u540d\u624b\u706b\u624b\u5224\u5b98\u5f20\u53ec\u91cd\u6614\u5e74\u4e3a\u671d\u5ef7\u51fa\u529b\u7684\u529f\u52b3\uff0c\u53c8\u4e0d\u77e5\u9646\u83f2\u9752\u7684\u6765\u5386\uff0c\u4fbf\u6566\u8bf7\u6b66\u5f53\u6d3e\u638c\u95e8\u4eba\u4e0b\u5c71\u3002\u9646\u83f2\u9752\u5e74\u7eaa\u867d\u8001\uff0c\u96c4\u5fc3\u72b9\u5728\uff0c\u77e5\u9053\u798f\u5eb7\u5b89\u6b64\u4e3e\u5fc5\u5c06\u4e0d\u5229\u4e8e\u6c5f\u6e56\u540c\u9053\uff0c\u82e5\u662f\u63a8\u8f9e\u4e0d\u53bb\uff0c\u5f92\u60f9\u9ebb\u70e6\uff0c\u5f53\u4e0b\u5b64\u8eab\u8d74\u4f1a\uff0c\u8981\u63a2\u660e\u8fd9\u6b21\u5927\u4f1a\u771f\u76f8\uff0c\u4fdf\u673a\u884c\u4e8b\uff0c\u53ca\u81f3\u5fc3\u781a\u4e3a\u6c64\u6c9b\u6240\u64d2\uff0c\u4ed6\u4fbf\u6697\u4e2d\u51fa\u624b\u76f8\u6551\u3002 \u3000\u3000\u9648\u5bb6\u6d1b\u3001\u970d\u9752\u6850\u7b49\u7ea2\u82b1\u4f1a\u7fa4\u96c4\u81ea\u56de\u7586\u6765\u5230\u5317\u4eac\uff0c\u5374\u4e3a\u8fd9\u65e5\u662f\u9999\u9999\u516c\u4e3b\u901d\u4e16\u5341\u5e74\u7684\u5fcc\u8fb0\uff0c\u5404\u4eba\u8981\u5230\u5979\u5893\u4e0a\u4e00\u796d\u3002 \u3000\u3000\u798f\u5eb7\u5b89\u7684\u638c\u95e8\u4eba\u5927\u4f1a\u88ab\u4eba\u6405\u6563\uff0c\u53c8\u548c\u6b66\u6797\u5404\u95e8\u6d3e\u90fd\u7ed3\u4e0a\u4e86\u51a4\uff0c\u81ea\u662f\u607c\u6012\u5f02\u5e38\uff0c\u4fbf\u6d3e\u5fb7\u5e03\u7387\u961f\u5728\u57ce\u5916\u5404\u5904\u5de1\u67e5\uff0c\u89c1\u6709\u53ef\u7591\u4e4b\u4eba\u7acb\u5373\u683c\u6740\u64d2\u62ff\u3002\u4e0d\u610f\u9676\u7136\u4ead\u7554\u4e00\u6218\uff0c\u6587\u6cf0\u6765\u3001\u8d75\u534a\u5c71\u7b49\u5c1a\u672a\u51fa\u624b\uff0c\u5927\u5185\u5341\u516b\u9ad8\u624b\u5df2\u5c3d\u6570\u94e9\u7fbd\u800c\u9041\u3002 \u3000\u3000\u9648\u5bb6\u6d1b\u7b49\u6df1\u77e5\u6e05\u5ef7\u5b98\u573a\u4e60\u6c14\u3002\u5fb7\u5e03\u7b49\u8d25\u5f97\u5982\u6b64\u72fc\u72c8\uff0c\u7ea2\u82b1\u4f1a\u4eba\u7269\u65e2\u672a\u60ca\u52a8\u7687\u4eb2\u5927\u5b98\uff0c\u4ed6\u4eec\u56de\u53bb\u5b9a\u662f\u6781\u529b\u9690\u7792\uff0c\u65e0\u4eba\u80af\u8bf4\u5728\u9676\u7136\u4ead\u7554\u9047\u654c\uff0c\u51b3\u4e0d\u81f4\u8c03\u52a8\u519b\u9a6c\u524d\u6765\u590d\u4ec7\u3002\u6b64\u5904\u867d\u79bb\u4eac\u57ce\u4e0d\u8fdc\uff0c\u5374\u5c3d\u53ef\u653e\u5fc3\u9017\u7559\u3002\u7fa4\u96c4\u548c\u9646\u83f2\u9752\u662f\u6545\u53cb\u91cd\u9022\uff0c\u548c\u80e1\u6590\u3001\u7a0b\u7075\u7d20\u662f\u65b0\u77e5\u521d\u4f1a\uff0c\u81ea\u5404\u6709\u8bb8\u591a\u8bdd\u8bf4\u3002 \u3000\u3000\u8a00\u8c08\u4e4b\u95f4\uff0c\u5ffd\u542c\u5f97\u8fdc\u8fdc\u4f20\u6765\u4e24\u4e0b\u638c\u58f0\uff0c\u7a0d\u505c\u4e00\u4e0b\uff0c\u53c8\u662f\u8fde\u62cd\u4e09\u4e0b\u3002\u90a3\u4e66\u751f\u6253\u626e\u7684\u201c\u91d1\u7b1b\u79c0\u624d\u201d\u4f59\u9c7c\u540c\u62cd\u638c\u4e09\u4e0b\u76f8\u5e94\uff0c\u4e00\u505c\u4e4b\u540e\uff0c\u8fde\u62cd\u4e24\u4e0b\u3002\u65e0\u5c18\u9053\uff1a\u201c\u4e94\u5f1f\u3001\u516d\u5f1f\u6765\u5566\u3002\u201d \u3000\u3000\u53ea\u89c1\u638c\u58f0\u4f20\u6765\u5904\u98de\u9a70\u8fc7\u6765\u4e24\u4eba\uff0c\u8eab\u5f62\u9ad8\u7626\u3002\u80e1\u6590\u5728\u798f\u5eb7\u5b89\u5e9c\u4e2d\u89c1\u8fc7\uff0c\u77e5\u662f\u897f\u5ddd\u53cc\u4fa0\u5e38\u4f2f\u5fd7\u3001\u5e38\u8d6b\u5fd7\u5230\u4e86\u3002\u53ea\u89c1\u4ed6\u5144\u5f1f\u8eab\u540e\u53c8\u8ddf\u7740\u4e24\u4eba\uff0c\u624b\u4e2d\u5404\u62b1\u7740\u4e00\u4e2a\u5b69\u5b50\uff0c\u5954\u5230\u8fd1\u5904\uff0c\u89c1\u662f\u53cc\u5b50\u95e8\u502a\u4e0d\u5927\u3001\u502a\u4e0d\u5c0f\u5144\u5f1f\u3002\u4ed6\u4e8c\u4eba\u624b\u4e2d\u62b1\u7684\uff0c\u7adf\u7136\u662f\u9a6c\u6625\u82b1\u7684\u4e00\u5bf9\u53cc\u751f\u513f\u5b50\u3002 \u3000\u3000\u539f\u6765\u502a\u4e0d\u5927\u3001\u502a\u4e0d\u5c0f\u770b\u4e2d\u4e86\u8fd9\u5bf9\u5b69\u5b50\uff0c\u5b81\u53ef\u6027\u547d\u4e0d\u8981\uff0c\u4e5f\u662f\u8981\u53bb\u593a\u6765\u3002\u5e38\u6c0f\u5144\u5f1f\u539f\u662f\u53cc\u751f\u5144\u5f1f\uff0c\u542c\u4e86\u502a\u6c0f\u5144\u5f1f\u4e4b\u8a00\uff0c\u6fc0\u52a8\u5fc3\u610f\uff0c\u4e58\u7740\u638c\u95e8\u4eba\u5927\u4f1a\u4e00\u54c4\u800c\u6563\u7684\u5927\u4e71\uff0c\u6df7\u5165\u798f\u5e9c\u5185\u9662\u3002\u5176\u65f6\u798f\u5eb7\u5b89\u548c\u4f17\u536b\u58eb\u8179\u4e2d\u6b63\u81ea\u5927\u75db\uff0c\u5747\u9053\u8eab\u4e2d\u5267\u6bd2\uff0c\u4eba\u4eba\u5fd9\u4e8e\u670d\u836f\u89e3\u6bd2\uff0c\u5e38\u6c0f\u5144\u5f1f\u53c8\u662f\u4e00\u7b49\u4e00\u7684\u9ad8\u624b\uff0c\u6beb\u4e0d\u8d39\u529b\u5730\u6253\u5012\u4e86\u4e03\u516b\u540d\u536b\u58eb\uff0c\u4fbf\u53c8\u5c06\u8fd9\u5bf9\u5b69\u5b50\u62a2\u4e86\u51fa\u6765\u3002 \u3000\u3000\u80e1\u6590\u89c1\u4e86\u8fd9\u5bf9\u5b69\u5b50\uff0c\u60f3\u8d77\u9a6c\u6625\u82b1\u547d\u5728\u9877\u523b\uff0c\u4e0d\u7531\u5f97\u53c8\u559c\u53c8\u60b2\uff0c\u731b\u5730\u60f3\u8d77\u4e00\u4e8b\uff0c\u5bf9\u9648\u5bb6\u6d1b\u9053\uff1a\u201c\u603b\u8235\u4e3b\uff0c\u665a\u8f88\u6709\u4e2a\u6781\u8352\u5510\u7684\u5ff5\u5934\uff0c\u60f3\u6c42\u4f60\u4e00\u4ef6\u4e8b\u3002\u201d\u9648\u5bb6\u6d1b\u9053\uff1a\u201c\u80e1\u5144\u5f1f\u4f46\u8bf4\u4e0d\u59a8\u3002\u4f60\u6211\u4eca\u65e5\u867d\u662f\u521d\u4f1a\uff0c\u4f46\u795e\u4ea4\u5df2\u4e45\uff0c\u4f46\u6559\u529b\u4e4b\u6240\u53ca\uff0c\u65e0\u4e0d\u4f9d\u4ece\u3002\u201d \u3000\u3000\u80e1\u6590\u53ea\u89c9\u8fd9\u756a\u8bdd\u6781\u4e0d\u597d\u610f\u601d\u51fa\u53e3\uff0c\u4e0d\u7981\u9887\u4e3a\u5ff8\u6029\uff0c\u7ea2\u4e86\u8138\u9053\uff1a\u201c\u665a\u8f88\u8fd9\u4e2a\u5ff5\u5934\uff0c\u5b9e\u5728\u662f\u5f02\u60f3\u5929\u5f00\uff0c\u8bf4\u51fa\u6765\u53ea\u6015\u5404\u4f4d\u89c1\u7b11\u3002\u201d \u3000\u3000\u9648\u5bb6\u6d1b\u5fae\u7b11\u9053\uff1a\u201c\u6211\u8f88\u6240\u4f5c\u6240\u4e3a\uff0c\u5728\u65c1\u4eba\u770b\u6765\uff0c\u54ea\u4e00\u4ef6\u4e0d\u662f\u8352\u5510\u4e4b\u6781\uff1f\u54ea\u4e00\u4ef6\u4e0d\u662f\u5f02\u60f3\u5929\u5f00\uff1f\u201d \u3000\u3000\u80e1\u6590\u9053\uff1a\u201c\u603b\u8235\u4e3b\u65e2\u4e0d\u89c1\u602a\uff0c\u6211\u4fbf\u8bf4\u4e86\u3002\u201d\u6307\u7740\u90a3\u4e24\u4e2a\u5b69\u7ae5\u8bf4\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u5b69\u7adf\u662f\u798f\u5eb7\u5b89\u4e4b\u5b50\uff0c\u4ed6\u4eec\u7684\u6bcd\u4eb2\u5374\u662f\u547d\u5728\u5782\u5371\u3002\u201d\u4e8e\u662f\u4ece\u5f53\u5e74\u5728\u5546\u5bb6\u5821\u4e2d\u5982\u4f55\u548c\u9a6c\u6625\u82b1\u76f8\u9047\u4e00\u6bb5\u4e8b\u8bf4\u8d77\uff0c\u76f4\u8bf4\u5230\u9a6c\u6625\u82b1\u4e2d\u6bd2\u4e0d\u6cbb\u3002\u53ea\u542c\u5f97\u7fa4\u96c4\u8840\u8109\u8d32\u5f20\uff0c\u65e0\u4e0d\u5927\u4e3a\u6124\u6012\u3002\u4f9d\u65e0\u5c18\u4e4b\u89c1\uff0c\u7acb\u65f6\u4fbf\u8981\u8d76\u8fdb\u5317\u4eac\u57ce\u4e2d\uff0c\u5c06\u8fd9\u65e0\u60c5\u65e0\u4e49\u7684\u798f\u5eb7\u5b89\u4e00\u5251\u523a\u6b7b\u3002 \u3000\u3000\u7ea2\u82b1\u4f1a\u4e03\u5f53\u5bb6\u6b66\u8bf8\u845b\u5f90\u5929\u5b8f\u9053\uff1a\u201c\u6628\u665a\u5317\u4eac\u95f9\u4e86\u8fd9\u7b49\u5927\u4e8b\u51fa\u6765\uff0c\u54b1\u4eec\u82e5\u518d\u8d38\u7136\u8fdb\u57ce\uff0c\u798f\u5eb7\u5b89\u5b9a\u7136\u523a\u4e0d\u5230\uff0c\u8bf4\u4e0d\u5b9a\u5927\u4f19\u8fd8\u96be\u4ee5\u5168\u8eab\u800c\u9000\u3002\u201d \u3000\u3000\u9648\u5bb6\u6d1b\u70b9\u5934\u9053\uff1a\u201c\u6b64\u523b\u798f\u5eb7\u5b89\u5e9c\u95e8\u524d\u540e\uff0c\u4e0d\u77e5\u6709\u591a\u5c11\u519b\u9a6c\u628a\u5b88\uff0c\u5982\u4f55\u4e0b\u5f97\u4e86\u624b\uff1f\u5355\u662f\u8981\u6df7\u8fdb\u57ce\u95e8\uff0c\u4fbf\u662f\u5927\u5927\u4e0d\u6613\u3002\u6211\u6b64\u756a\u548c\u5404\u4f4d\u5144\u5f1f\u540c\u6765\uff0c\u5fd7\u5728\u4e00\u796d\uff0c\u4e0d\u53ef\u4e3a\u4e86\u6cc4\u4e00\u65f6\u4e4b\u6124\uff0c\u4f7f\u4f17\u5144\u5f1f\u6709\u6240\u635f\u6298\u3002\u80e1\u5144\u5f1f\uff0c\u4f60\u6c42\u6211\u505a\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u80e1\u6590\u9053\uff1a\u201c\u6211\u89c1\u603b\u8235\u4e3b\u4e07\u91cc\u8fe2\u8fe2\uff0c\u4ece\u56de\u7586\u6765\u5230\u5317\u4eac\uff0c\u53ea\u662f\u4e00\u796d\u5893\u4e2d\u8fd9\u4f4d\u59d1\u5a18\uff0c\u60c5\u6df1\u4e49\u91cd\uff0c\u4e16\u6240\u7f55\u89c1\u3002\u5728\u4e0b\u6614\u65e5\u66fe\u53d7\u8fd9\u4f4d\u9a6c\u59d1\u5a18\u4e00\u8a00\u4e4b\u6069\uff0c\u65e0\u4ee5\u4e3a\u62a5\uff0c\u4e2d\u5fc3\u4e0d\u5b89\u3002\u773c\u89c1\u5979\u4e34\u6b7b\u4e4b\u9645\uff0c\u6302\u5ff5\u4e24\u4e8b\uff0c\u6b7b\u96be\u7791\u76ee\u3002\u4e00\u4ef6\u662f\u60f3\u5ff5\u5979\u4e24\u4e2a\u7231\u5b50\uff0c\u5929\u5e78\u5e38\u6c0f\u53cc\u4fa0\u4e24\u4f4d\u524d\u8f88\u5df2\u6551\u4e86\u51fa\u6765\uff0c\u53e6\u4e00\u4ef6\u5374\u662f\u5979\u60f3\u5ff5\u798f\u5eb7\u5b89\u90a3\u5978\u8d3c\uff0c\u4ecd\u76fc\u548c\u4ed6\u4e00\u53d9\u3002\u867d\u8bf4\u5979\u81f3\u6b7b\u4e0d\u609f\uff0c\u53ef\u7b11\u4ea6\u590d\u53ef\u601c\uff0c\u4f46\u60c5\u4e4b\u6240\u953a\u2026\u2026\u201d\u8bf4\u5230\u8fd9\u91cc\uff0c\u5fc3\u4e0b\u9eef\u7136\uff0c\u5df2\u4e0d\u77e5\u5982\u4f55\u63aa\u8bcd\u3002 \u3000\u3000\u9648\u5bb6\u6d1b\u9053\uff1a\u201c\u6211\u660e\u767d\u5566\uff01\u4f60\u662f\u8981\u6211\u5047\u5192\u90a3\u4e2a\u4f24\u5929\u5bb3\u7406\u3001\u8d1f\u5fc3\u8584\u5e78\u7684\u798f\u5eb7\u5b89\uff0c\u53bb\u6170\u4e00\u6170\u8fd9\u4f4d\u591a\u60c5\u591a\u4e49\u7684\u9a6c\u59d1\u5a18\uff1f\u201d\u80e1\u6590\u4f4e\u58f0\u9053\uff1a\u201c\u6b63\u662f\uff01\u201d \u3000\u3000\u7fa4\u96c4\u89c9\u5f97\u80e1\u6590\u8fd9\u4e2a\u8352\u5510\u7684\u5ff5\u5934\u679c\u662f\u5f02\u60f3\u5929\u5f00\u4e4b\u81f3\uff0c\u53ef\u662f\u8c01\u4e5f\u7b11\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u9648\u5bb6\u6d1b\u773c\u671b\u8fdc\u5904\uff0c\u9eef\u7136\u51fa\u795e\uff0c\u8bf4\u9053\uff1a\u201c\u5893\u4e2d\u8fd9\u4f4d\u59d1\u5a18\u4e34\u6b7b\u4e4b\u9645\uff0c\u5982\u80fd\u89c1\u6211\u4e00\u9762\uff0c\u90a3\u662f\u591a\u4e48\u7684\u5feb\u6d3b\uff01\u53ef\u60dc\u7ec8\u96be\u5982\u613f\u2026\u2026\u201d\u8f6c\u5934\u5411\u80e1\u6590\u9053\uff1a\u201c\u597d\uff0c\u6211\u4fbf\u53bb\u89c1\u89c1\u8fd9\u4f4d\u9a6c\u59d1\u5a18\u3002\u201d \u3000\u3000\u80e1\u6590\u597d\u751f\u611f\u6fc0\uff0c\u6697\u60f3\u9648\u5bb6\u6d1b\u53f1\u54a4\u98ce\u4e91\uff0c\u5929\u4e0b\u82f1\u96c4\u8c6a\u6770\u65e0\u4e0d\u63a8\u670d\uff0c\u81ea\u5df1\u53ea\u662f\u4e2a\u65e0\u540d\u665a\u8f88\uff0c\u4eca\u65e5\u521d\u4f1a\uff0c\u4fbf\u6c42\u4ed6\u53bb\u505a\u8fd9\u6837\u4e00\u4ef6\u8352\u8bde\u4e0d\u7ecf\u4e4b\u4e8b\uff0c\u8bdd\u4e00\u51fa\u53e3\uff0c\u5fc3\u4e2d\u4fbf\u5df2\u540e\u6094\uff0c\u4ed6\u5c45\u7136\u4e00\u53e3\u7b54\u5141\uff0c\u4ee5\u540e\u8fd9\u4f4d\u603b\u8235\u4e3b\u4fbf\u662f\u8981\u81ea\u5df1\u8d74\u6c64\u8e48\u706b\uff0c\u4e5f\u662f\u5728\u6240\u4e0d\u8f9e\u4e86\u3002 \u3000\u3000\u7fa4\u96c4\u4e0a\u4e86\u9a6c\uff0c\u7531\u80e1\u6590\u5728\u524d\u5e26\u8def\uff0c\u5929\u5c06\u9ece\u660e\u65f6\u5230\u4e86\u836f\u738b\u5e99\u5916\u3002 \u3000\u3000\u80e1\u6590\u53cc\u624b\u643a\u4e86\u5b69\u5b50\uff0c\u4f34\u540c\u9648\u5bb6\u6d1b\u8d70\u8fdb\u5e99\u53bb\u3002\u53ea\u89c1\u4e00\u95f4\u9634\u68ee\u68ee\u7684\u5c0f\u623f\u4e4b\u4e2d\uff0c\u4e00\u706f\u5982\u8c46\uff0c\u6cb9\u5df2\u70b9\u5e72\uff0c\u706f\u706b\u6b32\u7184\u672a\u7184\u3002\u9a6c\u6625\u82b1\u8eba\u5728\u7095\u4e0a\uff0c\u6c14\u606f\u672a\u65ad\u3002 \u3000\u3000\u4e24\u4e2a\u5b69\u5b50\u6251\u5411\u69bb\u4e0a\uff0c\u5927\u53eb\uff1a\u201c\u5988\u5988\uff0c\u5988\u5988\uff01\u201d\u9a6c\u6625\u82b1\u7741\u5f00\u773c\u6765\uff0c\u89c1\u662f\u7231\u5b50\uff0c\u9661\u7136\u95f4\u7cbe\u795e\u4e00\u632f\uff0c\u4e5f\u4e0d\u77e5\u54ea\u91cc\u6765\u7684\u529b\u6c14\uff0c\u5c06\u4e24\u4e2a\u5b69\u5b50\u7d27\u7d27\u6402\u5728\u6000\u91cc\uff0c\u8bf4\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u5b69\u5b50\uff0c\u5988\u60f3\u5f97\u4f60\u597d\u82e6\uff01\u201d \u3000\u3000\u4e09\u4e2a\u4eba\u76f8\u62e5\u826f\u4e45\uff0c\u5979\u8f6c\u773c\u89c1\u5230\u80e1\u6590\uff0c\u5bf9\u4e24\u4e2a\u5b69\u5b50\u9053\uff1a\u201c\u4ee5\u540e\u4f60\u4eec\u8ddf\u7740\u80e1\u53d4\u53d4\uff0c\u597d\u597d\u542c\u4ed6\u7684\u8bdd\u2026\u2026\u4f60\u4eec\u2026\u2026\u62dc\u4e86\u4ed6\u4f5c\u4e49\u2026\u2026\u4e49\u2026\u2026\u201d \u3000\u3000\u80e1\u6590\u77e5\u5979\u5fc3\u610f\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u6211\u6536\u4e86\u4ed6\u4eec\u4f5c\u4e49\u513f\uff0c\u9a6c\u59d1\u5a18\uff0c\u4f60\u653e\u5fc3\u5427\uff01\u201d\u9a6c\u6625\u82b1\u8138\u9732\u5fae\u7b11\uff0c\u9053\uff1a\u201c\u5feb\u2026\u2026\u5feb\u78d5\u5934\uff0c\u6211\u597d\u2026\u2026\u597d\u653e\u5fc3\u2026\u2026\u201d\u4e24\u4e2a\u5b69\u5b50\u8dea\u5728\u80e1\u6590\u9762\u524d\uff0c\u78d5\u4e0b\u5934\u53bb\u3002 \u3000\u3000\u80e1\u6590\u8ba9\u4ed6\u4eec\u78d5\u4e86\u56db\u4e2a\u5934\uff0c\u4f38\u624b\u62b1\u8d77\u4e24\u4eba\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u9a6c\u59d1\u5a18\uff0c\u4f60\u8fd8\u6709\u4ec0\u4e48\u5429\u5490\u4e48\uff1f\u201d\u9a6c\u6625\u82b1\u9053\uff1a\u201c\u6211\u6b7b\u4e86\u4e4b\u540e\uff0c\u6c42\u4f60\u2026\u2026\u6c42\u4f60\u5c06\u6211\u846c\u2026\u2026\u846c\u5728\u6211\u4e08\u592b\u5f90\u2026\u2026\u5e08\u54e5\u7684\u575f\u65c1\u2026\u2026\u4ed6\u5f88\u53ef\u601c\u2026\u2026\u4ece\u5c0f\u4fbf\u559c\u6b22\u6211\u2026\u2026\u53ef\u662f\u6211\u4e0d\u559c\u6b22\u2026\u2026\u4e0d\u559c\u6b22\u4ed6\u3002\u201d \u3000\u3000\u80e1\u6590\u7a81\u7136\u4e4b\u95f4\uff0c\u60f3\u8d77\u4e86\u90a3\u65e5\u77f3\u5c4b\u62d2\u654c\u3001\u5546\u5b9d\u9707\u5728\u5c4b\u5916\u6797\u4e2d\u51fb\u6b7b\u5f90\u94ee\u7684\u60c5\u666f\u6765\uff0c\u5fc3\u4e2d\u53c8\u662f\u4e00\u9178\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u6211\u4e00\u5b9a\u529e\u5230\u3002\u201d\u6ca1\u6599\u5230\u5979\u4e34\u6b7b\u4e4b\u9645\uff0c\u7adf\u4f1a\u8bb0\u5f97\u4e08\u592b\uff0c\u4f24\u5fc3\u4e4b\u4e2d\u5012\u4e5f\u5fae\u5fae\u6709\u4e9b\u559c\u6b22\u3002\u4ed6\u6df1\u6068\u798f\u5eb7\u5b89\uff0c\u542c\u9a6c\u6625\u82b1\u8bb0\u5f97\u4e08\u592b\uff0c\u4e0d\u8bb0\u5f97\u90a3\u4e2a\u6ca1\u826f\u5fc3\u7684\u60c5\u90ce\uff0c\u90a3\u662f\u518d\u597d\u4e0d\u8fc7\uff0c\u90a3\u77e5\u9a6c\u6625\u82b1\u5e7d\u5e7d\u53f9\u4e86\u53e3\u6c14\uff0c\u8f7b\u8f7b\u5730\u9053\uff1a\u201c\u798f\u516c\u5b50\uff0c\u6211\u591a\u60f3\u518d\u89c1\u4f60\u4e00\u9762\u3002\u201d \u3000\u3000\u9648\u5bb6\u6d1b\u8fdb\u623f\u4e4b\u540e\uff0c\u4e00\u76f4\u7ad9\u5728\u95e8\u8fb9\u6697\u5904\uff0c\u9a6c\u6625\u82b1\u6ca1\u77a7\u89c1\u4ed6\u3002 \u3000\u3000\u80e1\u6590\u6447\u4e86\u6447\u5934\uff0c\u62b1\u7740\u4e24\u4e2a\u5b69\u513f\uff0c\u6084\u6084\u51fa\u623f\uff0c\u9648\u5bb6\u6d1b\u7f13\u6b65\u8d70\u5230\u5979\u7684\u5e8a\u524d\u3002 \u3000\u3000\u80e1\u6590\u8de8\u5230\u9662\u5b50\u4e2d\u65f6\uff0c\u5ffd\u542c\u5f97\u9a6c\u6625\u82b1\u201c\u554a\u201d\u7684\u4e00\u58f0\u53eb\u3002\u8fd9\u58f0\u53eb\u5524\u4e4b\u4e2d\uff0c\u5145\u6ee1\u4e86\u5e78\u798f\u3001\u559c\u60a6\u3001\u6df1\u539a\u65e0\u6bd4\u7684\u7231\u604b\u3002 \u3000\u3000\u5979\u7ec8\u4e8e\u89c1\u5230\u4e86\u5979\u7684\u201c\u5fc3\u4e0a\u4eba\u201d\u2026\u2026 \u3000\u3000\u80e1\u6590\u60d8\u7136\u8d70\u51fa\u5e99\u95e8\uff0c\u5ffd\u542c\u5f97\u7b1b\u58f0\u5e7d\u7136\u54cd\u8d77\uff0c\u662f\u91d1\u7b1b\u79c0\u624d\u4f59\u9c7c\u540c\u5728\u6811\u4e0b\u6a2a\u7b1b\u800c\u5439\u3002\u80e1\u6590\u5fc3\u5934\u4e00\u9707\uff0c\u5728\u5f88\u4e45\u4ee5\u524d\uff0c\u5728\u5c71\u4e1c\u5546\u5bb6\u5821\uff0c\u4f9d\u7a00\u66fe\u542c\u4eba\u8fd9\u6837\u7f20\u7ef5\u6e29\u67d4\u7684\u5439\u8fc7\u3002 \u3000\u3000\u8fd9\u7f20\u7ef5\u6e29\u67d4\u7684\u4e50\u66f2\uff0c\u5f53\u5e74\u5728\u798f\u5eb7\u5b89\u7684\u6d1e\u7bab\u4e2d\u5439\u51fa\u6765\uff0c\u6311\u52a8\u4e86\u9a6c\u6625\u82b1\u7684\u60c5\u6000\uff0c\u7ec8\u4e8e\u917f\u6210\u4e86\u8fd9\u4e00\u573a\u51a4\u5b7d\u3002 \u3000\u3000\u91d1\u7b1b\u79c0\u624d\u7684\u7b1b\u5b50\u58f0\u4e2d\uff0c\u4f3c\u4e4e\u5728\u8bf4\u4e00\u4e2a\u7f8e\u4e3d\u7684\u604b\u7231\u6545\u4e8b\uff0c\u5374\u4e5f\u5728\u6292\u5199\u8fd9\u573a\u7231\u604b\u4e4b\u4e2d\u6240\u5305\u542b\u7684\u82e6\u6da9\u3001\u4f24\u5fc3\u548c\u4e0d\u5e78\u3002\u5e99\u95e8\u5916\u6bcf\u4e2a\u4eba\u90fd\u6014\u6014\u5730\u6c89\u9ed8\u65e0\u8a00\uff0c\u60f3\u5230\u4e86\u81ea\u5df1\u4e00\u751f\u4e4b\u4e2d\u751c\u871c\u7684\u51c4\u51c9\u7684\u5f80\u4e8b\u3002\u80e1\u6590\u60f3\u5230\u4e86\u90a3\u4e2a\u9a91\u5728\u767d\u9a6c\u4e0a\u7684\u7d2b\u886b\u59d1\u5a18\uff0c\u6068\u4e0d\u5f97\u6251\u5728\u5730\u4e0a\u5927\u54ed\u4e00\u573a\u3002\u5373\u4f7f\u662f\u8c6a\u6c14\u903c\u4eba\u7684\u65e0\u5c18\u9053\u957f\uff0c\u4e5f\u60f3\u5230\u4e86\u5f88\u4e45\u5f88\u4e45\u4ee5\u524d\uff0c\u5728\u5f88\u8fdc\u5f88\u8fdc\u7684\u5730\u65b9\uff0c\u90a3\u4e2a\u7f8e\u4e3d\u800c\u53c8\u72e0\u5fc3\u7684\u5b98\u5bb6\u5c0f\u59d0\uff0c\u9a97\u5f97\u4ed6\u65a9\u65ad\u4e86\u81ea\u5df1\u7684\u4e00\u6761\u81c2\u8180\u2026\u2026 \u3000\u3000\u7b1b\u58f0\u60a0\u7f13\u5730\u51c4\u51c9\u5730\u54cd\u7740\u3002 \u3000\u3000\u8fc7\u4e86\u597d\u4e00\u4f1a\u513f\uff0c\u9648\u5bb6\u6d1b\u4ece\u5e99\u95e8\u91cc\u6162\u6162\u8e31\u4e86\u51fa\u6765\u3002\u4ed6\u5411\u80e1\u6590\u70b9\u4e86\u70b9\u5934\u3002\u80e1\u6590\u77e5\u9053\u9a6c\u6625\u82b1\u662f\u79bb\u5f00\u8fd9\u4e16\u754c\u4e86\u3002\u5979\u4e34\u6b7b\u4e4b\u524d\u89c1\u5230\u4e86\u5fc3\u7231\u7684\u4e24\u4e2a\u513f\u5b50\uff0c\u4e5f\u89c1\u5230\u4e86\u201c\u60c5\u90ce\u201d\u3002\u80e1\u6590\u4e0d\u77e5\u9053\u5979\u8ddf\u9648\u5bb6\u6d1b\u8bf4\u4e86\u4e9b\u4ec0\u4e48\uff0c\u662f\u8d23\u5907\u4ed6\u7684\u65e0\u60c5\u8584\u5e78\u5462\uff0c\u8fd8\u662f\u8bc9\u8bf4\u81ea\u5df1\u7ec8\u751f\u4e0d\u6e1d\u7684\u70ed\u60c5\uff1f\u9664\u4e86\u9648\u5bb6\u6d1b\u4e4b\u5916\uff0c\u8fd9\u4e16\u4e0a\u662f\u8c01\u4e5f\u4e0d\u77e5\u9053\u4e86\u3002 \u3000\u3000\u80e1\u6590\u62dc\u6258\u5e38\u6c0f\u53cc\u4fa0\u548c\u502a\u6c0f\u6606\u4ef2\uff0c\u5c06\u9a6c\u6625\u82b1\u7684\u4e24\u4e2a\u5b69\u5b50\u5148\u884c\u5e26\u5230\u56de\u7586\uff0c\u4ed6\u6599\u7406\u4e86\u9a6c\u6625\u82b1\u7684\u4e27\u4e8b\u4e4b\u540e\uff0c\u4fbf\u53bb\u56de\u7586\u548c\u4f17\u4eba\u805a\u4f1a\u3002 \u3000\u3000\u9648\u5bb6\u6d1b\u7387\u9886\u7fa4\u96c4\uff0c\u4e3e\u624b\u548c\u80e1\u6590\u3001\u7a0b\u7075\u7d20\u4f5c\u522b\uff0c\u4e0a\u9a6c\u897f\u53bb\u3002 \u3000\u3000\u80e1\u6590\u59cb\u7ec8\u6ca1\u8ddf\u4ed6\u4eec\u63d0\u5230\u5706\u6027\u3002\u5947\u602a\u7684\u662f\uff0c\u8d75\u534a\u5c71\u3001\u9a86\u51b0\u4ed6\u4eec\u4e5f\u6ca1\u63d0\u8d77\u3002\u662f\u4e0d\u662f\u5706\u6027\u5df2\u7ecf\u4f1a\u5230\u4e86\u4ed6\u4eec\uff0c\u8981\u4ed6\u4eec\u6c38\u8fdc\u522b\u5411\u4ed6\u63d0\u8d77\u5979\u7684\u540d\u5b57\uff1f \u3000"}),showOnHomepage:!0},{name:"\u7b2c\u4e8c\u5341\u7ae0\u3000\u6068\u65e0\u5e38",content:(0,a.jsx)(a.Fragment,{children:"\u5fd9\u4e71\u4e86\u534a\u665a\uff0c\u80e1\u6590\u548c\u7a0b\u7075\u7d20\u5230\u5e99\u540e\u6570\u5341\u4e08\u7684\u5c0f\u6eaa\u4e2d\u6d17\u4e86\u624b\u8138\u3002\u7a0b\u7075\u7d20\u4ece\u80cc\u540e\u5305\u88f9\u4e2d\u53d6\u51fa\u70e7\u997c\uff0c\u4e24\u4eba\u548c\u7740\u6eaa\u4e2d\u6e05\u6c34\u5403\u4e86\u3002\u80e1\u6590\u8fde\u756a\u5267\u6597\uff0c\u53c8\u517c\u5927\u559c\u5927\u60b2\uff0c\u8fd9\u65f6\u53ea\u89c9\u624b\u9178\u811a\u8f6f\uff0c\u795e\u56f0\u529b\u5026\uff0c\u5f53\u4e0b\u8eba\u5728\u6eaa\u7554\u4f11\u606f\u4e86\u5927\u534a\u4e2a\u65f6\u8fb0\uff0c\u8fd9\u624d\u7cbe\u529b\u7a0d\u590d\uff0c\u53c8\u56de\u53bb\u836f\u738b\u5e99\u3002 \u3000\u3000\u4e24\u4eba\u56de\u8fdb\u50e7\u820d\uff0c\u8f7b\u8f7b\u63a8\u5f00\u623f\u95e8\uff0c\u53ea\u89c1\u9a6c\u6625\u82b1\u6b7b\u5728\u5e8a\u4e0a\uff0c\u8138\u542b\u5fae\u7b11\uff0c\u795e\u60c5\u751a\u662f\u6109\u60a6\u3002\u80e1\u6590\u5782\u6cea\u9053\uff1a\u201c\u5979\u8981\u6211\u5c06\u5979\u846c\u5728\u4e08\u592b\u5893\u65c1\u3002\u773c\u4e0b\u98ce\u58f0\u7d27\u6025\uff0c\u5230\u5904\u8ffd\u62ff\u4f60\u6211\u4e8c\u4eba\u3002\u8fd9\u5f53\u513f\u53c8\u54ea\u91cc\u627e\u68fa\u6728\u53bb\uff1f\u4e0d\u5982\u5c06\u5979\u706b\u5316\u4e86\uff0c\u9001\u5979\u9aa8\u7070\u524d\u53bb\u5b89\u846c\u3002\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u80e1\u6590\u5f2f\u4e0b\u8170\u53bb\uff0c\u4f38\u624b\u6b63\u8981\u5c06\u9a6c\u6625\u82b1\u7684\u5c38\u8eab\u62b1\u8d77\uff0c\u7a0b\u6216\u7d20\u7a81\u7136\u6293\u4f4f\u4ed6\u624b\u81c2\uff0c\u53eb\u9053\uff1a\u201c\u4e14\u6162\uff01\u201d \u3000\u3000\u80e1\u6590\u542c\u5979\u8bed\u97f3\u4e25\u91cd\u7d27\u8feb\uff0c\u4fbf\u5373\u7f29\u624b\uff0c\u95ee\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u7a0b\u7075\u7d20\u5c1a\u672a\u56de\u7b54\uff0c\u80e1\u6590\u5df2\u542c\u5230\u8eab\u540e\u6781\u7ec6\u5fae\u7684\u7f13\u7f13\u547c\u5438\u4e4b\u58f0\uff0c\u56de\u8fc7\u5934\u6765\uff0c\u53ea\u89c1\u677f\u95e8\u4e4b\u540e\u8d6b\u7136\u8eb2\u7740\u4e24\u4eba\uff0c\u5374\u662f\u7a0b\u7075\u7d20\u7684\u5927\u5e08\u5144\u6155\u5bb9\u666f\u5cb3\u548c\u4e09\u5e08\u59ca\u859b\u9e4a\u3002 \u3000\u3000\u4fbf\u5728\u6b64\u65f6\uff0c\u7a0b\u7075\u7d20\u624b\u4e00\u626c\uff0c\u4e00\u80a1\u8910\u8272\u7684\u8d64\u874e\u7c89\u98de\u51fa\uff0c\u6253\u5411\u9a6c\u6625\u82b1\u6240\u8eba\u7684\u5e8a\u677f\u5e95\u4e0b\u3002\u80e1\u6590\u5fc3\u5ff5\u4e00\u52a8\uff1a\u201c\u5e8a\u677f\u5e95\u4e0b\uff0c\u5b9a\u662f\u85cf\u7740\u6781\u5389\u5bb3\u7684\u654c\u4eba\u3002\u201d \u3000\u3000\u4f46\u89c1\u859b\u9e4a\u4f38\u624b\u63a8\u5f00\u623f\u95e8\uff0c\u6b63\u8981\u7eb5\u8eab\u51fa\u6765\uff0c\u80e1\u6590\u884c\u52a8\u5feb\u6781\uff0c\u53f3\u624b\u5f2f\u5904\uff0c\u62b1\u4f4f\u4e86\u7a0b\u7075\u7d20\u7684\u7ea4\u8170\uff0c\u5012\u7eb5\u51fa\u95e8\uff0c\u7ecf\u8fc7\u623f\u95e8\u65f6\u98de\u8d77\u4e00\u817f\uff0c\u8e22\u5728\u95e8\u677f\u4e4b\u4e0a\u3002\u90a3\u95e8\u677f\u7830\u7684\u4e00\u58f0\u5411\u540e\u731b\u649e\uff0c\u5c06\u6155\u5bb9\u666f\u5cb3\u548c\u859b\u9e4a\u4e8c\u4eba\u5939\u5728\u95e8\u677f\u548c\u5899\u58c1\u4e4b\u95f4\u3002\u6155\u5bb9\u666f\u5cb3\u5012\u4e5f\u7f62\u4e86\uff0c\u859b\u9e4a\u9ad8\u9ad8\u7684\u4e00\u4e2a\u9a7c\u80cc\u88ab\u7816\u5899\u6324\u5f97\u75db\u6781\uff0c\u5fcd\u4e0d\u4f4f\u9ad8\u58f0\u5927\u53eb\u3002 \u3000\u3000\u80e1\u6590\u548c\u7a0b\u7075\u7d20\u521a\u5728\u95e8\u53e3\u7ad9\u5b9a\uff0c\u53ea\u89c1\u5e8a\u5e95\u4e0b\u8d64\u96fe\u7030\u6f2b\uff0c\u90a3\u80a1\u8d64\u874e\u7c89\u5df2\u88ab\u4eba\u7528\u638c\u529b\u9707\u4e86\u51fa\u6765\uff0c\u8ddf\u7740\u4eba\u5f71\u95ea\u52a8\uff0c\u4e00\u4eba\u957f\u8eab\u7a9c\u51fa\u3002\u53ea\u542c\u5f97\u545b\u5577\u5577\u3001\u545b\u5577\u5577\u4e00\u9635\u6025\u54cd\uff0c\u90a3\u4eba\u63d0\u8d77\u624b\u4e2d\u864e\u6491\uff0c\u5f53\u5934\u5f80\u80e1\u6590\u5934\u9876\u7838\u4e0b\u3002\u80e1\u6590\u4e00\u77a5\u4e4b\u4e0b\uff0c\u5df2\u770b\u6e05\u90a3\u4eba\u9762\u76ee\uff0c\u6b63\u662f\u81ea\u79f0\u201c\u6bd2\u624b\u836f\u738b\u201d\u7684\u77f3\u4e07\u55d4\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u53eb\u9053\uff1a\u201c\u522b\u78b0\u4ed6\u8eab\u5b50\u5175\u5203\uff01\u201d\u80e1\u6590\u5bf9\u5979\u7684\u5e08\u5144\u5e08\u59ca\u65e9\u662f\u6df1\u5177\u6212\u5fc3\uff0c\u77e5\u9053\u8fd9\u4e9b\u4eba\u5468\u8eab\u662f\u6bd2\uff0c\u6cbe\u4e0a\u4e86\u4e00\u4e1d\u534a\u5ffd\u4fbf\u662f\u540e\u60a3\u65e0\u7a77\uff0c\u5f53\u4e0b\u5411\u5de6\u6ed1\u5f00\u4e09\u6b65\uff0c\u907f\u5f00\u4e86\u77f3\u4e07\u55d4\u7684\u864e\u6491\uff0c\u5237\u7684\u4e00\u58f0\uff0c\u5355\u5200\u51fa\u624b\uff0c\u4e00\u62db\u201c\u8c0f\u679c\u56de\u7518\u201d\uff0c\u56de\u5934\u53cd\u51fb\u3002\u8fd9\u4e00\u62db\u56de\u5200\u780d\u5f97\u5feb\u6781\uff0c\u77f3\u4e07\u55d4\u4e0d\u53ca\u8eb2\u95ea\uff0c\u5371\u6025\u4e2d\u864e\u6491\u4e00\u4e3e\uff0c\u786c\u67b6\u4e86\u8fd9\u4e00\u5200\uff0c\u5f53\u7684\u4e00\u58f0\u5927\u54cd\uff0c\u4e24\u4eba\u5404\u81ea\u5411\u540e\u8dc3\u5f00\uff0c\u77f3\u4e07\u55d4\u864e\u6491\u4e2d\u7684\u94c1\u73e0\u53ea\u9707\u5f97\u545b\u5577\u5577\u3001\u545b\u5577\u5577\u7684\u4e71\u54cd\u3002 \u3000\u3000\u8fd9\u65f6\u6155\u5bb9\u666f\u5cb3\u548c\u859b\u9e4a\u5df2\u81ea\u50e7\u820d\u4e2d\u51fa\u6765\uff0c\u7ad9\u5728\u77f3\u4e07\u55d4\u7684\u8eab\u540e\u3002\u77f3\u4e07\u55d4\u548c\u80e1\u6590\u786c\u63a5\u786c\u67b6\u7684\u4ea4\u4e86\u8fd9\u4e00\u62db\uff0c\u4f46\u89c9\u5bf9\u65b9\u5200\u6cd5\u7cbe\u5947\uff0c\u8182\u529b\u5f3a\u52b2\uff0c\u81ea\u5df1\u53f3\u81c2\u9707\u5f97\u9690\u9690\u9178\u9ebb\uff0c\u5f53\u4e0b\u4e0d\u518d\u8fdb\u51fb\u3002 \u3000\u3000\u80e1\u6590\u5fc3\u4e2d\uff0c\u5374\u4e5f\u6697\u81ea\u79f0\u5f02\uff1a\u201c\u8fd9\u4eba\u64c5\u4e8e\u7528\u6bd2\uff0c\u6b66\u529f\u7adf\u4e5f\u8fd9\u822c\u4e86\u5f97\u3002\u6211\u8fd9\u4e00\u62db\u2018\u8c0f\u679c\u56de\u7518\u2019\u5982\u6b64\u51fa\u5176\u4e0d\u610f\u7684\u53cd\u5288\u51fa\u53bb\uff0c\u4ed6\u5c45\u7136\u63a5\u5f97\u4e0b\u6765\u3002\u201d \u3000\u3000\u53ea\u542c\u6155\u5bb9\u666f\u5cb3\u8bf4\u9053\uff1a\u201c\u7a0b\u5e08\u59b9\uff0c\u89c1\u4e86\u5e08\u53d4\u600e\u4e48\u4e0d\u5feb\u78d5\u5934\uff1f\u201d \u3000\u3000\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u54b1\u4eec\u54ea\u91cc\u94bb\u51fa\u4e00\u4e2a\u5e08\u53d4\u6765\u5566\uff1f\u4ece\u6765\u6ca1\u542c\u89c1\u8fc7\u3002\u201d \u3000\u3000\u77f3\u4e07\u55d4\u51b7\u51b7\u7684\u9053\uff1a\u201c\u2018\u6bd2\u624b\u795e\u67ad\u2019\u7684\u540d\u5b57\u542c\u89c1\u8fc7\u6ca1\u6709\uff1f\u4f60\u5e08\u7236\u96be\u9053\u4ece\u6765\u4e0d\u6562\u63d0\u6211\u5417\uff1f\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u2018\u6bd2\u624b\u795e\u67ad\u2019\uff1f\u8fd9\u540d\u5b57\u5012\u4f3c\u4e4e\u542c\u89c1\u8fc7\u7684\u3002\u6211\u5e08\u7236\u8bf4\u4ed6\u4ece\u524d\u786e\u662f\u6709\u8fc7\u4e00\u4e2a\u5e08\u5f1f\uff0c\u53ea\u662f\u4ed6\u6ee5\u7528\u6bd2\u836f\u5bb3\u4eba\uff0c\u65e0\u6076\u4e0d\u4f5c\uff0c\u65e9\u7ed9\u5e08\u7956\u9010\u51fa\u95e8\u5899\u4e86\u3002\u77f3\u524d\u8f88\uff0c\u90a3\u4fbf\u662f\u4f60\u4e48\uff1f\u201d\u77f3\u4e07\u55d4\u5fae\u5fae\u4e00\u7b11\uff0c\u6de1\u7136\u9053\uff1a\u201c\u54b1\u4eec\u8fd9\u4e00\u95e8\u8bb2\u7a76\u4f7f\u7528\u6bd2\u836f\uff0c\u65e2\u7136\u6709\u4e86\u8fd9\u4e2a\u2018\u6bd2\u2019\u5b57\uff0c\u53c8\u4f55\u5fc5\u5047\u60fa\u60fa\u7684\u786c\u5145\u597d\u4eba\uff1f\u59d3\u77f3\u7684\u5b81\u53ef\u505a\u771f\u5c0f\u4eba\uff0c\u4e0d\u5982\u4f60\u5e08\u7236\u8fd9\u822c\u5047\u88c5\u4f2a\u541b\u5b50\u3002\u201d \u3000\u3000\u7a0b\u7075\u7d20\u6012\u9053\uff1a\u201c\u6211\u5e08\u7236\u51e0\u65f6\u5bb3\u8fc7\u4e00\u6761\u65e0\u8f9c\u7684\u4eba\u547d\uff1f\u201d\u77f3\u4e07\u55d4\u9053\uff1a\u201c\u4f60\u5e08\u7236\u5bb3\u6b7b\u7684\u4eba\u96be\u9053\u5c11\u4e86\uff1f\u4ed6\u81ea\u5df1\u81ea\u7136\u8bf4\u4ed6\u4e0b\u624b\u6bd2\u6b7b\u4e4b\u4eba\uff0c\u4e2a\u4e2a\u7f6a\u5927\u6076\u6781\uff0c\u6b7b\u6709\u4f59\u8f9c\uff0c\u53ef\u662f\u5728\u65c1\u4eba\u770b\u6765\uff0c\u5374\u4e5f\u672a\u5fc5\u5982\u6b64\u3002\u81f3\u4e8e\u6b7b\u8005\u7684\u5bb6\u4eba\u5b50\u5973\uff0c\u66f4\u662f\u51b3\u4e0d\u8fd9\u4e48\u60f3\u3002\u201d\u80e1\u6590\u5fc3\u4e2d\u4e00\u51db\uff0c\u6697\u60f3\uff1a\u201c\u6b64\u4eba\u8fd9\u8bdd\u5012\u4e5f\u6709\u51e0\u5206\u9053\u7406\u3002\u201d \u3000\u3000\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u4e0d\u9519\u3002\u6211\u5e08\u7236\u4e5f\u6df1\u6094\u4e00\u751f\u4f24\u4eba\u592a\u591a\uff0c\u540e\u6765\u4fbf\u51fa\u5bb6\u505a\u4e86\u548c\u5c1a\uff0c\u793c\u4f5b\u8d4e\u7f6a\u3002\u4ed6\u8001\u4eba\u5bb6\u8c06\u8c06\u544a\u8beb\u6211\u4eec\u5e08\u5144\u59b9\u56db\u4eba\uff0c\u9664\u975e\u4e07\u4e0d\u5f97\u5df2\uff0c\u51b3\u8ba1\u4e0d\u53ef\u8f7b\u6613\u4f24\u4eba\u3002\u665a\u8f88\u4e00\u751f\uff0c\u5c31\u4ece\u672a\u5bb3\u8fc7\u4e00\u6761\u6027\u547d\u3002\u201d \u3000\u3000\u77f3\u4e07\u55d4\u51b7\u7b11\u9053\uff1a\u201c\u5047\u4ec1\u5047\u4e49\uff0c\u53c8\u6709\u4f55\u76ca\uff1f\u6211\u77a7\u4f60\u806a\u660e\u4f36\u4fd0\uff0c\u5012\u662f\u6211\u95e8\u4e2d\u7684\u6770\u51fa\u4eba\u6750\u3002\u638c\u95e8\u4eba\u5927\u4f1a\u4e2d\u90a3\u51e0\u62db\uff0c\u8981\u5f97\u53ef\u6f02\u4eae\u554a\uff0c\u8fde\u4f60\u5e08\u53d4\u4e5f\u9669\u4e9b\u7740\u4e86\u9053\u513f\u3002\u201d \u3000\u3000\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u4f60\u81ea\u79f0\u662f\u6211\u5e08\u53d4\uff0c\u5192\u7528\u6211\u5e08\u7236\u2018\u6bd2\u624b\u836f\u738b\u2019\u7684\u540d\u5934\u3002\u8981\u662f\u771f\u6b63\u7684\u2018\u6bd2\u624b\u836f\u738b\u2019\u5728\u4e16\uff0c\u4f38\u624b\u53bb\u62ff\u7389\u9f99\u676f\u4e4b\u65f6\uff0c\u5c82\u80fd\u77a7\u4e0d\u51fa\u676f\u4e0a\u5df2\u6cbe\u4e86\u8d64\u874e\u7c89\uff1f\u6211\u5728\u5927\u5385\u4e0a\u55b7\u90a3\u2018\u4e09\u8708\u4e94\u87c6\u70df\u2019\uff0c\u6211\u5e08\u7236\u4ed6\u8001\u4eba\u5bb6\u600e\u4f1a\u61f5\u7136\u4e0d\u89c9\uff1f\u201d \u3000\u3000\u8fd9\u4e24\u53e5\u8bdd\u53ea\u95ee\u5f97\u77f3\u4e07\u55d4\u8138\u988a\u5fae\u8d64\uff0c\u96be\u4ee5\u56de\u7b54\u3002\u8981\u77e5\u4ed6\u5c11\u5e74\u65f6\u548c\u65e0\u55d4\u5927\u5e08\u540c\u95e8\u5b66\u827a\uff0c\u56e0\u7528\u6bd2\u65e0\u8282\uff0c\u591a\u4f24\u597d\u4eba\uff0c\u7ed9\u5e08\u7236\u9010\u51fa\u95e8\u5899\u3002\u6b64\u540e\u6570\u5341\u5e74\u4e2d\uff0c\u66fe\u548c\u65e0\u55d4\u4e89\u6597\u8fc7\u597d\u51e0\u6b21\u3002\u4e24\u4eba\u90fd\u662f\u4f7f\u6bd2\u7684\u5927\u884c\u5bb6\uff0c\u53cc\u65b9\u6240\u4f7f\u836f\u7269\u4e4b\u70c8\uff0c\u6bd2\u7269\u4e4b\u5947\uff0c\u53ef\u60f3\u800c\u77e5\u3002 \u3000\u3000\u6570\u6b21\u6597\u6cd5\uff0c\u77f3\u4e07\u55d4\u6bcf\u4e00\u56de\u5747\u662f\u5c48\u5c45\u4e0b\u98ce\uff0c\u82e5\u4e0d\u662f\u65e0\u55d4\u5927\u5e08\u59cb\u7ec8\u5ff5\u7740\u540c\u95e8\u4e4b\u8c0a\uff0c\u624b\u4e0b\u7559\u60c5\uff0c\u65e9\u5df2\u53d6\u4e86\u4ed6\u7684\u6027\u547d\u3002\u5728\u6700\u540e\u4e00\u6b21\u6597\u6bd2\u4e4b\u9645\uff0c\u77f3\u4e07\u55d4\u7ec8\u4e8e\u88ab\u201c\u65ad\u80a0\u8349\u201d\u718f\u778e\u4e86\u53cc\u76ee\u3002\u4ed6\u9003\u5f80\u7f05\u7538\u91ce\u4eba\u5c71\u4e2d\uff0c\u4ee5\u94f6\u86db\u4e1d\u9010\u6b65\u62d4\u53bb\u201c\u65ad\u80a0\u8349\u201d\u7684\u6bd2\u6027\uff0c\u53cc\u773c\u65b9\u5f97\u590d\u660e\uff0c\u867d\u80fd\u91cd\u89c1\u5929\u65e5\uff0c\u76ee\u529b\u5374\u5df2\u5927\u635f\u3002\u7389\u9f99\u676f\u4e0a\u6cbe\u4e86\u8d64\u874e\u7c89\uff0c\u65f1\u70df\u7ba1\u4e2d\u55b7\u51fa\u6765\u7684\u70df\u96fe\u989c\u8272\u7a0d\u6709\u4e0d\u540c\uff0c\u8fd9\u4e9b\u7ec6\u5fae\u4e4b\u5904\uff0c\u4ed6\u4fbf\u65e0\u6cd5\u5206\u8fa8\u3002 \u3000\u3000\u4f55\u51b5\u7a0b\u7075\u7d20\u683d\u57f9\u6210\u4e86\u201c\u4e07\u6bd2\u4e4b\u738b\u201d\u7684\u6bd2\u8349\u201c\u4e03\u5fc3\u6d77\u68e0\u201d\u4e4b\u540e\uff0c\u8d64\u874e\u7c89\u4e2d\u6df7\u4e0a\u4e86\u4e03\u5fc3\u6d77\u68e0\u53f6\u5b50\u7684\u7c89\u672b\uff0c\u201c\u4e09\u8708\u4e94\u87c6\u70df\u201d\u4e2d\u52a0\u5165\u4e86\u4e03\u5fc3\u6d77\u68e0\u7684\u82b1\u854a\uff0c\u8fd9\u4e00\u6765\uff0c\u4e24\u79cd\u6bd2\u836f\u7684\u5f02\u5473\u5168\u5931\uff0c\u6bd2\u6027\u5374\u66f4\u52a0\u5389\u5bb3\u3002 \u3000\u3000\u77f3\u4e07\u55d4\u5728\u91ce\u4eba\u5c71\u4e2d\u82b1\u4e86\u5341\u5e74\u529f\u592b\uff0c\u624d\u6cbb\u6108\u53cc\u76ee\uff0c\u56de\u5230\u4e2d\u539f\u65f6\u542c\u5230\u65e0\u55d4\u5927\u5e08\u7684\u6b7b\u8baf\uff0c\u53ea\u9053\u65af\u4eba\u4e00\u6b7b\uff0c\u81ea\u5df1\u4fbf\u53ef\u79f0\u96c4\u5929\u4e0b\uff0c\u90a3\u6599\u5e08\u5144\u4e00\u4e2a\u5e74\u7eaa\u8f7b\u8f7b\u7684\u5173\u95e8\u5f1f\u5b50\uff0c\u7adf\u6709\u5982\u6b64\u5389\u5bb3\u7684\u529f\u592b\uff1f\u90a3\u665a\u7a0b\u7075\u7d20\u5316\u88c5\u6210\u4e00\u4e2a\u9f99\u953a\u5e72\u67af\u7684\u8001\u592a\u5a46\uff0c\u5f53\u4e16\u64c5\u4e8e\u7528\u6bd2\u7684\u9ad8\u624b\uff0c\u77f3\u4e07\u55d4\u65e0\u4e0d\u77e5\u6653\uff0c\u4ed6\u5f53\u771f\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\uff0c\u8fd9\u4e2a\u5c0f\u8001\u592a\u5a46\u5728\u65c1\u5438\u51e0\u53e3\u70df\uff0c\u4fbf\u4ee4\u4ed6\u683d\u4e0a\u4e00\u4e2a\u5927\u7b4b\u6597\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u8fd9\u4e24\u53e5\u8bdd\u53ea\u95ee\u5f97\u4ed6\u54d1\u53e3\u65e0\u8a00\uff0c\u6155\u5bb9\u666f\u5cb3\u5374\u9053\uff1a\u201c\u5e08\u59b9\uff0c\u4f60\u5f97\u7f6a\u4e86\u5e08\u53d4\uff0c\u8fd8\u4e0d\u78d5\u5934\u8c22\u7f6a\uff0c\u5f53\u771f\u72c2\u5984\u5927\u80c6\u3002\u4ed6\u8001\u4eba\u5bb6\u4e00\u6012\uff0c\u7acb\u65f6\u53eb\u4f60\u6b7b\u65e0\u846c\u8eab\u4e4b\u5730\u3002\u6211\u548c\u859b\u5e08\u59b9\u90fd\u5df2\u6295\u5165\u4ed6\u8001\u4eba\u5bb6\u7684\u95e8\u4e0b\uff0c\u4f60\u4e56\u4e56\u732e\u51fa\u300a\u836f\u738b\u795e\u7bc7\u300b\uff0c\u8bf4\u4e0d\u5b9a\u4ed6\u8001\u4eba\u5bb6\u4e00\u559c\u6b22\uff0c\u4e5f\u6536\u4e86\u4f60\u8fd9\u5f1f\u5b50\uff0c\u5c82\u4e0d\u662f\u597d\uff1f\u201d \u3000\u3000\u7a0b\u7075\u7d20\u5fc3\u4e2d\u6012\u6781\uff0c\u6697\u60f3\u8fd9\u5e08\u5144\u5e08\u59b9\u80cc\u53db\u5e08\u95e8\uff0c\u6295\u5165\u672c\u6d3e\u5f03\u5f92\u95e8\u4e0b\uff0c\u90a3\u662f\u6b66\u6797\u4e2d\u72af\u89c4\u6700\u4e25\u7684\u201c\u6b3a\u5e08\u706d\u7956\u201d\u5927\u7f6a\uff0c\u4e0d\u8bba\u54ea\u4e00\u95e8\u54ea\u4e00\u6d3e\uff0c\u5747\u8981\u5904\u6b7b\u4e0d\u8d37\u3002\u53ef\u662f\u5979\u8138\u4e0a\u4e0d\u52a8\u58f0\u8272\uff0c\u8bf4\u9053\uff1a\u201c\u539f\u6765\u4e24\u4f4d\u5df2\u6539\u6295\u77f3\u524d\u8f88\u95e8\u4e0b\uff0c\u90a3\u4e48\u5c0f\u59b9\u4e0d\u80fd\u518d\u79f0\u4f60\u4eec\u4e3a\u5e08\u5144\u5e08\u59ca\u4e86\u3002\u59dc\u5e08\u54e5\u5462\uff1f\u4ed6\u4e5f\u6295\u5165\u77f3\u524d\u8f88\u95e8\u4e0b\u4e86\u4e48\uff1f\u201d\u6155\u5bb9\u666f\u5cb3\u9053\uff1a\u201c\u59dc\u5e08\u5f1f\u4e0d\u8bc6\u65f6\u52a1\uff0c\u4e0d\u542c\u6559\u8bf2\uff0c\u5df2\u4e3a\u543e\u5e08\u5904\u6b7b\u3002\u201d \u3000\u3000\u7a0b\u7075\u7d20\u5fc3\u4e2d\u4e00\u9178\uff0c\u59dc\u94c1\u5c71\u4e3a\u4eba\u803f\u76f4\uff0c\u867d\u7136\u884c\u4e8b\u6a2a\u86ee\uff0c\u5728\u5979\u4e09\u4e2a\u5e08\u5144\u59ca\u4e2d\u5374\u662f\u6700\u4e3a\u6b63\u6d3e\uff0c\u4e0d\u6599\u7adf\u6b7b\u4e8e\u77f3\u4e07\u55d4\u4e4b\u624b\uff0c\u53c8\u95ee\uff1a\u201c\u859b\u4e09\u59ca\uff0c\u4f60\u7684\u513f\u5b50\u5c0f\u94c1\u5462\uff1f\u4ed6\u5f88\u597d\u5427\uff1f\u201d\u859b\u9e4a\u51b7\u51b7\u5730\u9053\uff1a\u201c\u4ed6\u4e5f\u6b7b\u4e86\u3002\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u4e0d\u77e5\u751f\u7684\u662f\u4ec0\u4e48\u75c5\uff1f\u201d\u859b\u9e4a\u6012\u9053\uff1a\u201c\u662f\u6211\u7684\u513f\u5b50\uff0c\u8981\u4f60\u591a\u7ba1\u4ec0\u4e48\u95f2\u4e8b\uff1f\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u662f\uff0c\u5c0f\u59b9\u539f\u4e0d\u8be5\u591a\u7ba1\u95f2\u4e8b\u3002\u6211\u8fd8\u6ca1\u606d\u559c\u4e24\u4f4d\u5462\uff0c\u6155\u5bb9\u5927\u54e5\u548c\u859b\u4e09\u59ca\u51e0\u65f6\u6210\u7684\u4eb2\u554a\uff1f\u54b1\u4eec\u540c\u95e8\u5b66\u827a\u4e00\u573a\uff0c\u8fde\u559c\u9152\u4e5f\u4e0d\u8bf7\u5c0f\u59b9\u559d\u4e00\u676f\u3002\u201d \u3000\u3000\u6155\u5bb9\u666f\u5cb3\u3001\u59dc\u94c1\u5c71\u3001\u859b\u9e4a\u4e09\u4eba\u4e00\u751f\u6069\u6028\u7ea0\u845b\uff0c\u51c4\u60e8\u53ef\u6016\u3002 \u3000\u3000\u521d\u65f6\u859b\u9e4a\u82e6\u604b\u6155\u5bb9\u666f\u5cb3\uff0c\u6155\u5bb9\u666f\u5cb3\u5374\u53e6\u5a36\u4e86\u4ed6\u4eba\u3002\u859b\u9e4a\u4e00\u6012\u4e4b\u4e0b\uff0c\u4fbf\u4e0b\u6bd2\u5bb3\u6b7b\u4e86\u4ed6\u7684\u59bb\u5b50\u3002\u6155\u5bb9\u666f\u5cb3\u4e3a\u59bb\u590d\u4ec7\uff0c\u7528\u6bd2\u836f\u6bc1\u4e86\u859b\u9e4a\u7684\u5bb9\u8c8c\uff0c\u4f7f\u5979\u8eab\u5b50\u4f5d\u507b\uff0c\u6210\u4e3a\u4e00\u4e2a\u9a7c\u80cc\u4e11\u5973\u3002\u59dc\u94c1\u5c71\u81ea\u6765\u559c\u6b22\u8fd9\u4e2a\u5e08\u59b9\uff0c\u5979\u867d\u4e11\u964b\u4e0d\u582a\uff0c\u59dc\u94c1\u5c71\u5374\u4e0d\u4ee5\u4e3a\u5acc\uff0c\u5a36\u4e86\u5979\u4e3a\u59bb\u3002\u90a3\u77e5\u6155\u5bb9\u666f\u5cb3\u5728\u4ed6\u4eec\u6210\u4eb2\u751f\u5b50\u4e4b\u540e\uff0c\u5374\u53c8\u60f3\u8d77\u8fd9\u5e08\u59b9\u7684\u79cd\u79cd\u597d\u5904\u6765\uff0c\u4e0d\u65ad\u7684\u5411\u5979\u7ea0\u7f20\uff0c\u7ec8\u4e8e\u548c\u59dc\u94c1\u5c71\u53cd\u8138\u6210\u4ec7\u3002\u59dc\u859b\u592b\u5987\u8feb\u5f97\u94f8\u94c1\u4e3a\u5c4b\uff0c\u4fbf\u662f\u4e3a\u4e86\u6297\u62d2\u5927\u5e08\u5144\u7684\u4fb5\u72af\u3002\u90a3\u77e5\u7ed3\u5c40\u59dc\u94c1\u5c71\u7ec8\u4e8e\u4e3a\u77f3\u4e07\u55d4\u6240\u6740\uff0c\u800c\u6155\u5bb9\u666f\u5cb3\u548c\u859b\u9e4a\u8fd8\u662f\u7ed3\u6210\u4e86\u592b\u5987\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u77e5\u9053\u8fd9\u4e2d\u95f4\u7684\u79cd\u79cd\u66f2\u6298\uff0c\u5bfb\u601d\uff1a\u201c\u4e8c\u5e08\u54e5\u6b7b\u5728\u77f3\u4e07\u55d4\u624b\u4e0b\uff0c\u60f3\u662f\u4ed6\u4e0d\u80af\u80cc\u53db\u5148\u5e08\u6539\u6295\u4ed6\u7684\u95e8\u4e0b\uff0c\u4f46\u4e5f\u672a\u59cb\u4e0d\u662f\u51fa\u4e8e\u5927\u5e08\u54e5\u7684\u4ece\u4e2d\u6311\u62e8\u3002\u4e09\u5e08\u59ca\u7adf\u4f1a\u6539\u5ac1\u5927\u5e08\u54e5\uff0c\u8bf4\u4e0d\u5b9a\u4e5f\u6709\u4e00\u4efd\u8c0b\u6740\u4eb2\u592b\u4e4b\u7f6a\u3002\u201d\u4e8e\u662f\u53f9\u9053\uff1a\u201c\u5c0f\u94c1\u90a3\u65e5\u4e2d\u6bd2\uff0c\u5c0f\u59b9\u8bbe\u6cd5\u76f8\u6551\uff0c\u4e5f\u7b97\u82b1\u8fc7\u4e00\u756a\u5fc3\u8840\u3002\u60f3\u4e0d\u5230\u4ed6\u8fd8\u662f\u6b7b\u5728\u2018\u6843\u82b1\u7634\u2019\u4e0b\uff0c\u90a3\u4e5f\u662f\u547d\u8be5\u5982\u6b64\u4e86\u3002\u201d\u6155\u5bb9\u666f\u5cb3\u8138\u8272\u5927\u53d8\uff0c\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u2026\u2026\u201d\u8bf4\u4e86\u8fd9\u56db\u4e2a\u5b57\uff0c\u7a81\u7136\u4f4f\u53e3\uff0c\u548c\u859b\u9e4a\u5bf9\u671b\u4e86\u4e00\u773c\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u5c0f\u59b9\u4e5f\u53ea\u778e\u731c\u7f62\u4e86\u3002\u201d\u539f\u6765\u6155\u5bb9\u666f\u5cb3\u6709\u4e00\u9879\u72ec\u95e8\u7684\u4e0b\u6bd2\u529f\u592b\uff0c\u4e43\u662f\u5728\u4e91\u8d35\u4ea4\u754c\u4e4b\u5904\uff0c\u6536\u96c6\u4e86\u201c\u6843\u82b1\u7634\u201d\u7684\u7634\u6bd2\uff0c\u5236\u6210\u4e00\u79cd\u6bd2\u5f39\u3002\u59dc\u94c1\u5c71\u3001\u859b\u9e4a\u592b\u5987\u548c\u4ed6\u4ea4\u624b\u591a\u5e74\uff0c\u540e\u6765\u4e5f\u60f3\u51fa\u4e86\u89e3\u6bd2\u4e4b\u6cd5\u3002\u7a0b\u7075\u7d20\u51fa\u8a00\u8bd5\u63a2\uff0c\u6155\u5bb9\u666f\u5cb3\u4e00\u6765\u6b64\u4e8b\u5c5e\u5b9e\uff0c\u4e8c\u6765\u51fa\u5176\u4e0d\u610f\uff0c\u4fbf\u968f\u53e3\u627f\u8ba4\u4e86\u3002\u7a0b\u7075\u7d20\u5fc3\u4e0b\u66f4\u6012\uff0c\u9053\uff1a\u201c\u4e09\u5e08\u59ca\u4f60\u597d\u4e0d\u72e0\u6bd2\uff0c\u4e8c\u5e08\u54e5\u5982\u6b64\u5f85\u4f60\uff0c\u4f60\u7adf\u548c\u5927\u5e08\u54e5\u540c\u8c0b\uff0c\u5bb3\u6b7b\u4e86\u4eb2\u592b\u4eb2\u513f\u3002\u201d\u987b\u77e5\u59dc\u5c0f\u94c1\u4e2d\u4e86\u6155\u5bb9\u666f\u5cb3\u7684\u6843\u82b1\u7634\u6bd2\u5f39\uff0c\u859b\u9e4a\u81ea\u6709\u89e3\u6551\u4e4b\u836f\uff0c\u5979\u65e2\u5fcd\u5fc3\u4e0d\u6551\uff0c\u90a3\u4e48\u59dc\u94c1\u5c71\u3001\u59dc\u5c0f\u94c1\u7236\u5b50\u4e4b\u6b7b\uff0c\u5979\u867d\u975e\u4eb2\u81ea\u4e0b\u624b\uff0c\u5374\u4e5f\u662f\u540c\u8c0b\u3002\u7a0b\u7075\u7d20\u4ece\u6155\u5bb9\u666f\u5cb3\u51b2\u53e3\u800c\u51fa\u7684\u56db\u4e2a\u5b57\u4e2d\uff0c\u4fbf\u731c\u77e5\u4e86\u8fd9\u573a\u4eba\u4f26\u60e8\u53d8\u7684\u5185\u60c5\u3002 \u3000\u3000\u859b\u9e4a\u6025\u6b32\u5c94\u5f00\u8bdd\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u5e08\u59b9\uff0c\u6211\u5e08\u6709\u610f\u5782\u987e\uff0c\u90a3\u662f\u4f60\u7684\u8fd0\u6c14\uff0c\u4f60\u8fd8\u4e0d\u5feb\u78d5\u5934\u62dc\u5e08\uff1f\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u6211\u82e5\u4e0d\u62dc\u5e08\uff0c\u4fbf\u8981\u548c\u4e8c\u5e08\u54e5\u4e00\u6837\u4e86\uff0c\u662f\u4e0d\u662f\uff1f\u201d\u6155\u5bb9\u666f\u5cb3\u9053\uff1a\u201c\u90a3\u5012\u4e5f\u672a\u5fc5\u5c3d\u7136\u3002\u4f60\u6709\u798f\u4e0d\u4eab\uff0c\u522b\u4eba\u53c8\u4f55\u82e6\u6765\u52c9\u5f3a\u4e8e\u4f60\uff1f\u53ea\u662f\u90a3\u90e8\u300a\u836f\u738b\u795e\u7bc7\u300b\uff0c\u4f60\u8be5\u4ea4\u4e86\u51fa\u6765\u3002\u6211\u5e08\u5bbd\u5927\u4e3a\u6000\uff0c\u4f60\u5728\u638c\u95e8\u4eba\u5927\u4f1a\u4e2d\u5192\u72af\u4ed6\u8001\u4eba\u5bb6\u7684\u8fc7\u5904\uff0c\u4e5f\u53ef\u4e0d\u52a0\u8ffd\u7a76\u4e86\u3002\u201d \u3000\u3000\u7a0b\u7075\u7d20\u70b9\u5934\u9053\uff1a\u201c\u8fd9\u8bdd\u662f\u4e0d\u9519\uff0c\u53ea\u662f\u300a\u836f\u738b\u795e\u7bc7\u300b\u4e43\u6211\u5e08\u65e0\u55d4\u5927\u5e08\u4eb2\u624b\u6240\u64b0\uff0c\u54b1\u5e08\u5144\u59b9\u4e09\u4eba\u65e2\u7136\u90fd\u6539\u6295\u77f3\u524d\u8f88\u95e8\u4e0b\uff0c\u81ea\u5f53\u5c3d\u5f03\u5148\u5e08\u6240\u6388\u7684\u529f\u592b\uff0c\u4ece\u5934\u5b66\u8d77\u3002\u77f3\u524d\u8f88\u548c\u5148\u5e08\u95e8\u6237\u4e0d\u540c\uff0c\u867d\u4e0d\u4e00\u5b9a\u80dc\u8fc7\u5148\u5e08\uff0c\u4f46\u5b9a\u7136\u5404\u6709\u6240\u957f\uff0c\u5426\u5219\u4e24\u4f4d\u4e5f\u4e0d\u4f1a\u53e6\u62dc\u660e\u5e08\uff0c\u53c8\u6709\u4ec0\u4e48\u2018\u6709\u798f\u4e0d\u4f1a\u4eab\u2019\u3001\u2018\u662f\u6211\u7684\u8fd0\u6c14\u2019\u8fd9\u4e9b\u8bdd\u4e86\u3002\u90a3\u300a\u836f\u738b\u795e\u7bc7\u300b\u65e2\u5df2\u6ca1\u4ec0\u4e48\u7528\u5904\uff0c\u5c0f\u59b9\u4fbf\u70e7\u4e86\u5b83\u5427\uff01\u201d\u8bf4\u7740\u4ece\u8863\u5305\u4e2d\u53d6\u51fa\u4e00\u672c\u9ec4\u7eb8\u7684\u624b\u6284\u672c\u6765\uff0c\u6643\u4eae\u706b\u647a\uff0c\u4fbf\u5f80\u518c\u5b50\u4e0a\u70b9\u53bb\u3002 \u3000\u3000\u77f3\u4e07\u55d4\u521d\u65f6\u542c\u5979\u8bf4\u8981\u70e7\u300a\u836f\u738b\u795e\u7bc7\u300b\uff0c\u5fc3\u4e0b\u6697\u7b11\uff1a\u201c\u8fd9\u300a\u836f\u738b\u795e\u7bc7\u300b\u662f\u65e0\u55d4\u8d3c\u79c3\u6bd5\u751f\u5fc3\u8840\u4e4b\u6240\u805a\uff0c\u4f60\u5c82\u820d\u5f97\u70e7\u4e86\u5b83\uff1f\u201d \u3000\u3000\u5f85\u89c1\u5979\u53d6\u51fa\u6284\u672c\u548c\u706b\u647a\uff0c\u53c8\u60f3\uff1a\u201c\u4f3c\u4f60\u8fd9\u7b49\u72e1\u72ef\u7684\u5c0f\u4e2b\u5934\uff0c\u660e\u77e5\u4f60\u5e08\u5144\u5e08\u59ca\u5b9a\u8981\u62a2\u593a\u300a\u836f\u738b\u795e\u7bc7\u300b\uff0c\u5c82\u6709\u4e0d\u5047\u9020\u4e00\u672c\u4f2a\u4e66\u6765\u9a97\u4eba\u7684\uff1f\u5728\u6211\u9762\u524d\u88c5\u6a21\u4f5c\u6837\uff0c\u90a3\u4e0d\u662f\u73ed\u95e8\u5f04\u65a7\u4e48\uff1f\u201d\u56e0\u6b64\u867d\u89c1\u5979\u70b9\u706b\u70e7\u4e66\uff0c\u7adf\u662f\u5fae\u7b11\u4e0d\u8bed\uff0c\u7406\u4e5f\u4e0d\u7406\u3002\u5f85\u90a3\u6284\u672c\u70ed\u6c14\u4e00\u718f\uff0c\u7ffb\u626c\u5f00\u6765\uff0c\u53ea\u89c1\u7eb8\u8d28\u9648\u65e7\uff0c\u6284\u672c\u4e2d\u7684\u5b57\u8ff9\u5b9b\u7136\u662f\u65e0\u55d4\u7684\u624b\u8ff9\uff0c\u4e0d\u7531\u5f97\u5403\u4e86\u4e00\u60ca\uff0c\u8f6c\u5ff5\u60f3\u9053\uff1a\u201c\u554a\u54df\u4e0d\u597d\uff01\u8fd9\u4e2b\u5934\u591a\u534a\u5df2\u5c06\u4e66\u4e2d\u6587\u5b57\u8bb0\u5f97\u6eda\u74dc\u70c2\u719f\uff0c\u6b64\u4e66\u5df2\u4e8e\u5979\u65e0\u7528\uff0c\u90a3\u53ef\u4e07\u4e07\u70e7\u4e0d\u5f97\uff01\u201d \u3000\u3000\u5fd9\u9053\uff1a\u201c\u4f4f\u624b\uff01\u201d\u547c\u7684\u4e00\u638c\u5288\u53bb\uff0c\u4e00\u80a1\u75be\u98ce\uff0c\u767b\u65f6\u5c06\u706b\u647a\u6251\u7184\u4e86\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u54a6\uff0c\u8fd9\u4e2a\u6211\u53ef\u4e0d\u61c2\u4e86\u3002\u82e5\u662f\u77f3\u524d\u8f88\u7684\u533b\u836f\u4e4b\u672f\u80dc\u8fc7\u5148\u5e08\uff0c\u6b64\u4e66\u8981\u6765\u4f55\u7528\uff1f\u82e5\u662f\u4e0d\u80fd\u80dc\u8fc7\u5148\u5e08\uff0c\u53c8\u600e\u80fd\u6536\u665a\u8f88\u4e3a\u5f1f\u5b50\uff1f\u201d \u3000\u3000\u6155\u5bb9\u666f\u5cb3\u9053\uff1a\u201c\u6211\u4eec\u8fd9\u4f4d\u5e08\u7236\u7684\u4f7f\u6bd2\u7528\u836f\uff0c\u6bd4\u4e4b\u5148\u5e08\u53ef\u9ad8\u5f97\u592a\u591a\u4e86\u3002\u4f46\u5927\u6d77\u4e0d\u62e9\u7ec6\u6d41\uff0c\u4ed6\u5c71\u4e4b\u77f3\uff0c\u53ef\u4ee5\u653b\u7389\u3002\u8fd9\u300a\u836f\u738b\u795e\u7bc7\u300b\u65e2\u662f\u82b1\u4e86\u5148\u5e08\u6bd5\u751f\u7684\u5fc3\u8840\uff0c\u543e\u5e08\u62ff\u6765\u7ffb\u9605\u7ffb\u9605\uff0c\u4e5f\u53ef\u6307\u51fa\u5176\u4e2d\u8fc7\u8bef\u4e0e\u4e0d\u8db3\u4e4b\u5904\u554a\u3002\u201d\u4ed6\u662f\u79c0\u624d\u51fa\u8eab\uff0c\u8bf4\u8d77\u8bdd\u6765\uff0c\u81ea\u6709\u4e00\u756a\u6587\u7ec9\u7ec9\u7684\u5f3a\u8f9e\u593a\u7406\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u70b9\u5934\u9053\uff1a\u201c\u4f60\u7684\u5b66\u95ee\u8d8a\u6765\u8d8a\u957f\u8fdb\u4e86\u3002\u54fc\uff01\u4e24\u4e2a\u8eb2\u5728\u95e8\u89d2\u843d\u91cc\uff0c\u4e00\u4e2a\u94bb\u5728\u5e8a\u677f\u5e95\u4e0b\uff0c\u60f3\u8981\u6697\u7b97\u80e1\u5927\u54e5\u548c\u6211\u3002\u77f3\u524d\u8f88\uff0c\u6709\u4e00\u4ef6\u4e8b\u665a\u8f88\u60f3\u8981\u8bf7\u6559\uff0c\u82e5\u8499\u6307\u660e\u8ff7\u6d25\uff0c\u665a\u8f88\u53cc\u624b\u5c06\u300a\u836f\u738b\u795e\u7bc7\u300b\u732e\u4e0a\uff0c\u5e76\u6c42\u524d\u8f88\u5f00\u6069\uff0c\u6536\u5f55\u665a\u8f88\u4e3a\u5f92\u3002\u201d \u3000\u3000\u77f3\u4e07\u55d4\u77e5\u5979\u95ee\u7684\u5fc5\u662f\u4e00\u4e2a\u5201\u94bb\u53e4\u602a\u7684\u9898\u76ee\uff0c\u81ea\u5df1\u672a\u5fc5\u80fd\u7b54\uff0c\u4f46\u89c1\u300a\u836f\u738b\u795e\u7bc7\u300b\u6293\u4f4f\u5728\u5979\u7684\u624b\u91cc\uff0c\u5979\u53ea\u987b\u4e00\u4e3e\u624b\u4fbf\u80fd\u6bc1\u53bb\uff0c\u4e0d\u613f\u5c31\u6b64\u548c\u5979\u7834\u8138\uff0c\u4fbf\u9053\uff1a\u201c\u4f60\u8981\u95ee\u6211\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u8d35\u5dde\u82d7\u4eba\u6709\u4e00\u79cd\u2018\u78a7\u8695\u6bd2\u86ca\u2019\u2026\u2026\u201d\u77f3\u4e07\u55d4\u542c\u5230\u201c\u78a7\u8695\u6bd2\u86ca\u201d\u56db\u5b57\uff0c\u8138\u8272\u767b\u65f6\u4e00\u53d8\uff0c\u53ea\u542c\u5979\u7eed\u9053\uff1a\u201c\u5c06\u78a7\u8695\u6bd2\u86ca\u7684\u866b\u5375\u78be\u4e3a\u7c89\u672b\uff0c\u7f6e\u5728\u8863\u670d\u5668\u76bf\u4e4b\u4e0a\uff0c\u65c1\u4eba\u4e0d\u77e5\u8bef\u89e6\uff0c\u90a3\u4fbf\u4e2d\u4e86\u86ca\u6bd2\u3002\u8fd9\u7b97\u662f\u82d7\u4eba\u7684\u4e09\u5927\u86ca\u6bd2\u4e4b\u4e00\uff0c\u662f\u4e48\uff1f\u201d \u3000\u3000\u77f3\u4e07\u55d4\u70b9\u5934\u9053\uff1a\u201c\u4e0d\u9519\u3002\u5c0f\u4e2b\u5934\u77e5\u9053\u7684\u4e8b\u5012\u4e5f\u4e0d\u5c11\u3002\u201d \u3000\u3000\u4ed6\u4ece\u91ce\u4eba\u5c71\u6765\u5230\u4e2d\u539f\uff0c\u5f97\u77e5\u65e0\u55d4\u5927\u5e08\u5df2\u6b7b\uff0c\u4fbf\u8fc1\u6012\u4e8e\u4ed6\u7684\u95e8\u4eba\uff0c\u8981\u5c3d\u6740\u4e4b\u800c\u540e\u5feb\u3002\u4e0d\u6599\u6155\u5bb9\u666f\u5cb3\u4e3a\u4eba\u6781\u65e0\u9aa8\u6c14\uff0c\u4e00\u7ed9\u77f3\u4e07\u55d4\u5236\u4f4f\u4fbf\u5373\u54c0\u6c42\u9976\u547d\uff0c\u5e76\u8bf4\u5e08\u7236\u9057\u4e0b\u4e00\u90e8\u300a\u836f\u738b\u795e\u7bc7\u300b\uff0c\u843d\u5165\u5c0f\u5e08\u59b9\u4e4b\u624b\uff0c\u613f\u610f\u62dc\u4ed6\u4e3a\u5e08\uff0c\u5f15\u5bfc\u4ed6\u53bb\u593a\u53d6\u3002\u77f3\u4e07\u55d4\u867d\u6068\u65e0\u55d4\u5927\u5e08\u5207\u9aa8\uff0c\u4f46\u5fc3\u4e2d\u5bf9\u4ed6\u5b9e\u662f\u5927\u4e3a\u656c\u754f\uff0c\u542c\u8bf4\u4ed6\u6709\u9057\u8457\uff0c\u6599\u60f3\u5176\u4e2d\u4e8e\u4f7f\u6bd2\u7684\u529f\u592b\u5b66\u95ee\uff0c\u5fc5\u6709\u65e0\u6570\u5b9d\u8d35\u4e4b\u6781\u7684\u6cd5\u95e8\uff0c\u5f53\u4e0b\u4fbf\u6536\u4e86\u6155\u5bb9\u666f\u5cb3\u4e3a\u5f92\u3002\u5176\u540e\u53c8\u542c\u4ece\u4ed6\u7684\u6311\u62e8\uff0c\u6740\u4e86\u59dc\u94c1\u5c71\u7236\u5b50\uff0c\u6536\u5f55\u859b\u9e4a\u3002\u77f3\u4e07\u55d4\u548c\u6155\u5bb9\u666f\u5cb3\u3001\u59dc\u94c1\u5c71\u3001\u859b\u9e4a\u4e09\u4eba\u90fd\u52a8\u4e86\u624b\uff0c\u89c1\u4ed6\u4e09\u4eba\u6b66\u529f\u56fa\u662f\u5e73\u5e73\uff0c\u4f7f\u6bd2\u7684\u672c\u9886\u4e5f\u548c\u4ed6\u4eec\u5e08\u7236\u76f8\u5dee\u6781\u8fdc\uff0c\u542c\u8bf4\u7a0b\u7075\u7d20\u53ea\u4e0d\u8fc7\u662f\u4e2a\u5341\u4e03\u516b\u5c81\u7684\u59d1\u5a18\uff0c\u66f4\u662f\u6beb\u6ca1\u653e\u5728\u5fc3\u4e0a\uff0c\u6599\u60f3\u53ea\u8981\u89c1\u5230\u4e86\uff0c\u8fd8\u4e0d\u624b\u5230\u64d2\u6765\uff1f \u3000\u3000\u5728\u638c\u95e8\u4eba\u5927\u4f1a\u4e2d\u7740\u4e86\u5979\u7684\u9053\u513f\uff0c\u77f3\u4e07\u55d4\u4ecd\u672a\u670d\u8f93\uff0c\u53ea\u6068\u53cc\u76ee\u53d7\u4e86\u201c\u65ad\u80a0\u8349\u201d\u7684\u635f\u4f24\uff0c\u773c\u529b\u4e0d\u6d4e\uff0c\u56e0\u800c\u6ca1\u77a7\u51fa\u201c\u8d64\u874e\u7c89\u201d\u548c\u201c\u4e09\u8708\u4e94\u87c6\u201d\u70df\u6765\uff0c\u4f46\u80e1\u6590\u5728\u4f1a\u4e2d\u6240\u663e\u9732\u7684\u6b66\u529f\uff0c\u5374\u4ee4\u4ed6\u9887\u4e3a\u5fcc\u60ee\u3002\u4ed6\u6697\u6697\u8ddf\u968f\u5728\u540e\uff0c\u5f53\u80e1\u6590\u548c\u7a0b\u7075\u7d20\u8d74\u9676\u7136\u4ead\u4e4b\u7ea6\u65f6\uff0c\u5e08\u5f92\u4e09\u4eba\u4fbf\u8eb2\u5165\u836f\u738b\u5e99\u7684\u540e\u9662\u3002\u4ed6\u4e09\u4eba\u7684\u4e3b\u65e8\u662f\u5728\u593a\u53d6\u300a\u836f\u738b\u795e\u7bc7\u300b\uff0c\u89c1\u7ea2\u82b1\u4f1a\u7fa4\u96c4\u4eba\u591a\u52bf\u4f17\uff0c\u4e00\u76f4\u9690\u85cf\u5728\u540e\u9662\uff0c\u4e0d\u6562\u73b0\u8eab\u3002\u76f4\u81f3\u80e1\u7a0b\u4e8c\u4eba\u9001\u522b\u7fa4\u96c4\uff0c\u53c8\u5728\u6eaa\u7554\u996e\u98df\u4f11\u606f\uff0c\u4ed6\u4e09\u4eba\u624d\u85cf\u8eab\u5728\u9a6c\u6625\u82b1\u623f\u4e2d\uff0c\u53ea\u5f85\u80e1\u7a0b\u4e8c\u4eba\u8fdb\u623f\uff0c\u51c6\u62df\u4e00\u51fb\u5f97\u624b\u3002\u90a3\u77e5\u7a0b\u7075\u7d20\u6781\u662f\u7cbe\u4e56\uff0c\u5728\u5343\u94a7\u4e00\u53d1\u4e4b\u9645\u53ca\u65f6\u8b66\u89c9\u3002 \u3000\u3000\u8fd9\u65f6\u542c\u7a0b\u7075\u7d20\u63d0\u5230\u201c\u78a7\u8695\u6bd2\u86ca\u201d\uff0c\u5fc3\u4e0b\u624d\u5927\u662f\u5403\u60ca\uff1a\u201c\u60f3\u4e0d\u5230\u8fd9\u5c0f\u4e2b\u5934\u5982\u6b64\u4e86\u5f97\uff0c\u5979\u540c\u95e8\u7684\u5e08\u5144\u5e08\u59ca\uff0c\u53ef\u8fdc\u8fdc\u4e0d\u53ca\u4e86\u3002\u201d \u3000\u3000\u5f53\u4e0b\u5168\u795e\u6212\u5907\uff0c\u5df2\u65e0\u4e1d\u6beb\u8f7b\u654c\u4e4b\u5ff5\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u53c8\u9053\uff1a\u201c\u78a7\u8695\u6bd2\u86ca\u7684\u866b\u5375\u7c89\u672b\u653e\u5728\u4efb\u4f55\u7269\u4ef6\u5668\u76bf\u4e4b\u4e0a\uff0c\u5747\u662f\u65e0\u8272\u65e0\u81ed\uff0c\u65c1\u4eba\u51b3\u8ba1\u4e0d\u6613\u5bdf\u89c9\u3002\u53ea\u4e0d\u8fc7\u6bd2\u7c89\u4e0d\u7ecf\u8840\u8089\u4e4b\u8eaf\uff0c\u6bd2\u6027\u4e0d\u70c8\uff0c\u6709\u6cd5\u53ef\u89e3\uff0c\u987b\u7ecf\u8840\u8089\u6cbe\u4f20\uff0c\u65b9\u5f97\u81f4\u547d\u3002\u4e16\u4e0a\u4e8b\u96be\u4e24\u5168\uff0c\u6bd2\u7c89\u4e00\u7740\u4eba\u4f53\uff0c\u5374\u6709\u4e00\u5c42\u9690\u9690\u78a7\u7eff\u4e4b\u8272\u3002\u77f3\u524d\u8f88\u5728\u9a6c\u59d1\u5a18\u7684\u5c38\u8eab\u7f6e\u6bd2\uff0c\u82e5\u662f\u53ea\u653e\u5728\u5979\u886b\u4e0a\uff0c\u5012\u662f\u4e0d\u6613\u77a7\u5f97\u51fa\u6765\uff0c\u4f46\u4e3a\u4e86\u505a\u5230\u5c3d\u5584\u5c3d\u7f8e\uff0c\u5374\u8fde\u5979\u8138\u4e0a\u548c\u624b\u4e0a\u90fd\u653e\u7f6e\u4e86\u3002\u201d \u3000\u3000\u80e1\u6590\u542c\u5230\u8fd9\u91cc\uff0c\u8fd9\u624d\u660e\u767d\uff0c\u539f\u6765\u8fd9\u8d70\u65b9\u90ce\u4e2d\u7528\u5fc3\u5982\u6b64\u9634\u9669\uff0c\u7adf\u5728\u9a6c\u6625\u82b1\u7684\u5c38\u8eab\u653e\u7f6e\u5267\u6bd2\uff0c\u81ea\u5df1\u548c\u7a0b\u7075\u7d20\u52bf\u5fc5\u642c\u52a8\u5979\u7684\u5c38\u4f53\uff0c\u81ea\u987b\u4e2d\u6bd2\u65e0\u7591\uff0c\u5fcd\u4e0d\u4f4f\u9a82\u9053\uff1a\u201c\u597d\u6076\u8d3c\uff0c\u53ea\u6015\u4f60\u5bb3\u4eba\u53cd\u800c\u5bb3\u5df1\u3002\u201d \u3000\u3000\u77f3\u4e07\u55d4\u864e\u6491\u4e00\u6447\uff0c\u545b\u5577\u5577\u4e00\u9635\u54cd\u58f0\u8fc7\u53bb\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u4e2b\u5934\u771f\u662f\u6709\u70b9\u773c\u529b\uff0c\u8bc6\u5f97\u6211\u7684\u2018\u78a7\u8695\u6bd2\u86ca\u2019\u3002\u6c49\u4eba\u4e4b\u4e2d\uff0c\u9664\u6211\u4e4b\u5916\uff0c\u4f60\u662f\u7edd\u65e0\u4ec5\u6709\u7684\u7b2c\u4e8c\u4eba\u4e86\uff0c\u5f88\u597d\uff0c\u6709\u89c1\u8bc6\uff0c\u6709\u672c\u4e8b\u3002\u4f60\u5e08\u5144\u5e08\u59ca\u90a3\u91cc\u53ca\u5f97\u4e0a\u4f60\uff1f\u201d \u3000\u3000\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u524d\u8f88\u8c2c\u8d5e\u3002\u665a\u8f88\u6240\u4e0d\u660e\u767d\u7684\u662f\uff0c\u5148\u5e08\u9057\u8457\u300a\u836f\u738b\u795e\u7bc7\u300b\u4e2d\u8bf4\u9053\uff0c\u2018\u78a7\u8695\u6bd2\u86ca\u2019\u653e\u5728\u4eba\u4f53\u4e4b\u4e0a\uff0c\u82e5\u8981\u4e0d\u663e\u78a7\u7eff\u989c\u8272\uff0c\u539f\u4e0d\u4e3a\u96be\uff0c\u5374\u4e0d\u77e5\u77f3\u524d\u8f88\u4f55\u4ee5\u820d\u6b64\u6cd5\u800c\u4e0d\u7528\uff1f\u201d \u3000\u3000\u77f3\u4e07\u55d4\u53cc\u7709\u4e00\u626c\uff0c\u8bf4\u9053\uff1a\u201c\u5f53\u771f\u80e1\u8bf4\u516b\u9053\uff0c\u82d7\u4eba\u4e2d\u4fbf\u662f\u653e\u86ca\u7684\u7956\u5e08\uff0c\u4e5f\u65e0\u6b64\u6cd5\u3002\u4f60\u5e08\u7236\u4ece\u672a\u53bb\u8fc7\u82d7\u7586\uff0c\u77e5\u9053\u4ec0\u4e48\uff1f\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u524d\u8f88\u65e2\u5982\u6b64\u8bf4\uff0c\u665a\u8f88\u539f\u662f\u4e0d\u80fd\u4e0d\u4fe1\uff0c\u4f46\u5148\u5e08\u9057\u8457\u4e4b\u4e2d\uff0c\u786e\u662f\u4f20\u4e0b\u4e00\u6cd5\u3002\u5374\u4e0d\u77e5\u662f\u524d\u8f88\u5bf9\u5462\uff0c\u8fd8\u662f\u5148\u5e08\u5bf9\u3002\u201d\u77f3\u4e07\u55d4\u9053\uff1a\u201c\u662f\u4ec0\u4e48\u6cd5\u5b50\uff0c\u4f60\u5012\u8bf4\u6765\u542c\u542c\u3002\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u665a\u8f88\u8bf4\u4e86\uff0c\u524d\u8f88\u5b9a\u7136\u4e0d\u4fe1\u3002\u662f\u5bf9\u662f\u9519\uff0c\u4e00\u8bd5\u4fbf\u77e5\u3002\u201d\u77f3\u4e07\u55d4\u9053\uff1a\u201c\u5982\u4f55\u8bd5\u6cd5\uff1f\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u524d\u8f88\u53d6\u51fa\u2018\u78a7\u8695\u6bd2\u86ca\u2019\uff0c\u4e0b\u5728\u4eba\u624b\u4e4b\u4e0a\uff0c\u665a\u8f88\u4ee5\u5148\u5e08\u4e4b\u6cd5\u53d6\u836f\u6df7\u5165\uff0c\u4e14\u77a7\u6709\u65e0\u78a7\u7eff\u989c\u8272\u3002\u201d \u3000\u3000\u77f3\u4e07\u55d4\u4e00\u751f\u94bb\u7814\u6bd2\u836f\uff0c\u542c\u8bf4\u6709\u6b64\u5999\u6cd5\uff0c\u5c06\u4fe1\u5c06\u7591\u4e4b\u4f59\uff0c\u786e\u662f\u4e9f\u6b32\u4e00\u77e5\u771f\u4f2a\uff0c\u4fbf\u9053\uff1a\u201c\u653e\u5728\u8c01\u7684\u624b\u4e0a\u4f5c\u8bd5\uff1f\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u81ea\u662f\u7531\u524d\u8f88\u6307\u5b9a\u3002\u201d \u3000\u3000\u77f3\u4e07\u55d4\u5fc3\u60f3\uff1a\u201c\u8981\u4e0b\u5728\u4f60\u7684\u624b\u4e0a\uff0c\u4f60\u5f53\u7136\u4e0d\u80af\u3002\u4e0b\u5728\u90a3\u6c14\u52bf\u864e\u864e\u7684\u5c11\u5e74\u624b\u4e0a\uff0c\u90a3\u4e5f\u4e0d\u7528\u63d0\u8d77\u3002\u201d\u5fae\u4e00\u6c89\u541f\uff0c\u5411\u6155\u5bb9\u666f\u5cb3\u9053\uff1a\u201c\u4f38\u5de6\u624b\u51fa\u6765\uff01\u201d\u6155\u5bb9\u666f\u5cb3\u8df3\u8d77\u8eab\u6765\uff0c\u53eb\u9053\uff1a\u201c\u8fd9\u2026\u2026\u8fd9\u2026\u2026\u5e08\u7236\uff0c\u522b\u4e0a\u8fd9\u4e2b\u5934\u7684\u5f53\uff01\u201d\u77f3\u4e07\u55d4\u6c89\u7740\u8138\u9053\uff1a\u201c\u4f38\u5de6\u624b\u51fa\u6765\uff01\u201d \u3000\u3000\u6155\u5bb9\u666f\u5cb3\u89c1\u5e08\u7236\u7684\u795e\u8272\u5927\u662f\u4e25\u5cfb\uff0c\u539f\u662f\u4e0d\u6562\u6297\u62d2\uff0c\u4f46\u60f3\u90a3\u201c\u78a7\u8695\u6bd2\u86ca\u201d\u4f55\u7b49\u5389\u5bb3\uff0c\u7a0d\u4e00\u6cbe\u8eab\uff0c\u4fbf\u7b97\u5e08\u7236\u7ed9\u89e3\u836f\u6cbb\u6108\uff0c\u4e0d\u81f4\u9001\u547d\uff0c\u53ef\u662f\u8fd9\u4e00\u756a\u53d7\u7f6a\uff0c\u5374\u4e5f\u5b9a\u7136\u96be\u5f53\u65e0\u6bd4\u3002\u4ed6\u4e00\u53ea\u5de6\u624b\u4f38\u51fa\u5c3a\u8bb8\uff0c\u7acb\u5373\u53c8\u98a4\u6296\u7740\u7f29\u4e86\u56de\u53bb\u3002\u77f3\u4e07\u55d4\u51b7\u7b11\u9053\uff1a\u201c\u597d\u5427\uff0c\u4f60\u4e0d\u4ece\u5e08\u547d\uff0c\u90a3\u4e5f\u7531\u4f60\u3002\u201d\u6155\u5bb9\u91cf\u5cb3\u542c\u5230\u201c\u4e0d\u4ece\u5e08\u547d\u201d\u56db\u5b57\uff0c\u8138\u8272\u66f4\u662f\u82cd\u767d\uff0c\u539f\u6765\u4ed6\u62dc\u5e08\u65f6\u66fe\u7acb\u4e0b\u91cd\u8a93\uff0c\u82e5\u662f\u8fdd\u80cc\u5e08\u547d\uff0c\u7518\u53d7\u60e9\u5904\u3002\u4ed6\u4eec\u8fd9\u79cd\u4eba\u6bcf\u65e5\u91cc\u548c\u6bd2\u836f\u6bd2\u7269\u4e3a\u4f0d\uff0c\u201c\u60e9\u5904\u201d\u4e24\u5b57\u8bf4\u6765\u8f7b\u63cf\u6de1\u5199\uff0c\u5176\u5b9e\u4e2d\u95f4\u6240\u5305\u542b\u7684\u60e8\u9177\u6b8b\u5fcd\u4e4b\u5904\uff0c\u4ee4\u4eba\u4e00\u60f3\u5230\u4fbf\u4f1a\u4e0d\u5bd2\u800c\u6817\u3002 \u3000\u3000\u4ed6\u6b63\u5f85\u4f38\u624b\u51fa\u53bb\uff0c\u859b\u9e4a\u5ffd\u9053\uff1a\u201c\u5e08\u7236\uff0c\u6211\u6765\u8bd5\u597d\u4e86\u3002\u201d\u5766\u7136\u4f38\u51fa\u4e86\u5de6\u624b\u3002\u77f3\u4e07\u55d4\u9053\uff1a\u201c\u504f\u4e0d\u8981\u4f60\uff01\u77a7\u4ed6\u7537\u5b50\u6c49\u5927\u4e08\u592b\uff0c\u6709\u6ca1\u8fd9\u4e2a\u79cd\u3002\u201d \u3000\u3000\u6155\u5bb9\u666f\u5cb3\u9053\uff1a\u201c\u6211\u53c8\u4e0d\u662f\u5bb3\u6015\u3002\u6211\u53ea\u60f3\u8fd9\u5c0f\u5e08\u59b9\u8be1\u8ba1\u591a\u7aef\uff0c\u5b9a\u662f\u4e0d\u5b89\u597d\u5fc3\uff0c\u72af\u4e0d\u7740\u4e0a\u5979\u7684\u5f53\u3002\u201d\u7a0b\u7075\u7d20\u70b9\u5934\u9053\uff1a\u201c\u5927\u5e08\u54e5\u679c\u7136\u5389\u5bb3\u5f97\u7d27\u3002\u4ece\u524d\u8ddf\u7740\u5148\u5e08\u7684\u65f6\u5019\uff0c\u5148\u5e08\u6bcf\u4ef6\u4e8b\u8981\u53d7\u4f60\u7684\u6c14\uff0c\u773c\u4e0b\u62dc\u4e86\u4e2a\u65b0\u5e08\u7236\uff0c\u4ecd\u7136\u662f\u5f92\u513f\u5f3a\u8fc7\u4e86\u5e08\u7236\u3002\u201d \u3000\u3000\u77f3\u4e07\u55d4\u660e\u77e5\u5979\u8fd9\u756a\u8bdd\u662f\u6311\u62e8\u79bb\u95f4\uff0c\u4f46\u8fd8\u662f\u51b7\u51b7\u5730\u5411\u6155\u5bb9\u666f\u5cb3\u6a2a\u4e86\u4e00\u773c\u3002\u6155\u5bb9\u666f\u5cb3\u7ed9\u4ed6\u8fd9\u4e00\u773c\u77a7\u5f97\u5fc3\u4e2d\u53d1\u6bdb\uff0c\u53ea\u5f97\u5c06\u5de6\u624b\u4f38\u4e86\u51fa\u6765\u3002 \u3000\u3000\u77f3\u4e07\u55d4\u4ece\u6000\u4e2d\u53d6\u51fa\u4e00\u53ea\u9ec4\u91d1\u5c0f\u76d2\uff0c\u8f7b\u8f7b\u63ed\u5f00\uff0c\u76d2\u4e2d\u6709\u4e09\u6761\u901a\u4f53\u78a7\u7eff\u7684\u5c0f\u8695\uff0c\u8815\u8815\u800c\u52a8\u3002\u4ed6\u7528\u4e00\u53ea\u9ec4\u91d1\u5c0f\u5319\u5728\u76d2\u4e2d\u6311\u4e86\u4e9b\u7eff\u7c89\uff0c\u653e\u5728\u6155\u5bb9\u666f\u5cb3\u638c\u5fc3\u3002\u6155\u5bb9\u666f\u5cb3\u4e00\u6761\u5de6\u81c2\u98a4\u6296\u5f97\u66f4\u52a0\u5389\u5bb3\uff0c\u8138\u4e0a\u5145\u6ee1\u53c8\u6015\u53c8\u6012\u3001\u53c8\u60ca\u53c8\u6068\u7684\u795e\u8272\uff0c\u9762\u988a\u808c\u8089\u4e0d\u4f4f\u8df3\u52a8\uff0c\u773c\u5149\u4e2d\u6d41\u9732\u51fa\u91ce\u517d\u822c\u7684\u5149\u8292\uff0c\u4f3c\u4e4e\u8981\u62e9\u4eba\u800c\u566c\u3002 \u3000\u3000\u80e1\u6590\u5fc3\u60f3\uff1a\u201c\u4e8c\u59b9\u8fd9\u4e00\u7740\u68cb\uff0c\u4e0d\u7ba1\u5982\u4f55\uff0c\u603b\u662f\u5728\u4ed6\u4eec\u5e08\u5f92\u4e4b\u95f4\u4f0f\u4e86\u6df1\u4ec7\u5927\u6068\u3002\u8fd9\u6155\u5bb9\u666f\u5cb3\u65e5\u540e\u4e00\u6709\u673a\u4f1a\uff0c\u5b9a\u8981\u5411\u4ed6\u5e08\u7236\u62a5\u590d\u4eca\u65e5\u4e4b\u4ec7\u3002\u201d \u3000\u3000\u53ea\u89c1\u90a3\u4e9b\u7eff\u7c89\u4e00\u653e\u4e0a\u638c\u5fc3\uff0c\u7247\u523b\u95f4\u4fbf\u900f\u5165\u808c\u80a4\uff0c\u65e0\u5f71\u65e0\u8e2a\uff0c\u4f46\u638c\u5fc3\u4e2d\u9690\u9690\u7559\u7740\u4e00\u5c42\u9752\u6c14\uff0c\u4f3c\u4e4e\u63c9\u634f\u8fc7\u9752\u8349\u3001\u6811\u53f6\u4e00\u822c\u3002 \u3000\u3000\u77f3\u4e07\u55d4\u9053\uff1a\u201c\u5c0f\u599e\u513f\uff0c\u4e14\u77a7\u4f60\u7684\uff0c\u6709\u4ec0\u4e48\u6cd5\u5b50\u53eb\u4ed6\u638c\u5fc3\u4e0d\u663e\u9752\u7eff\u4e4b\u8272\u3002\u201d \u3000\u3000\u7a0b\u7075\u7d20\u4e0d\u53bb\u7406\u4ed6\uff0c\u5374\u8f6c\u5934\u5411\u80e1\u6590\u9053\uff1a\u201c\u5927\u54e5\uff0c\u90a3\u65e5\u5728\u6d1e\u5ead\u6e56\u7554\u767d\u9a6c\u5bfa\u6211\u548c\u4f60\u521d\u6b21\u76f8\u89c1\uff0c\u66fe\u548c\u4f60\u7ea6\u6cd5\u4e09\u7ae0\uff0c\u4f60\u53ef\u8fd8\u8bb0\u5f97\u4e48\uff1f\u201d\u80e1\u6590\u9053\uff1a\u201c\u8bb0\u5f97\u3002\u201d\u5fc3\u60f3\uff1a\u201c\u90a3\u65e5\u5979\u53eb\u6211\u4e0d\u53ef\u8bf4\u8bdd\uff0c\u4e0d\u53ef\u8ddf\u4eba\u52a8\u6b66\uff0c\u4e0d\u53ef\u79bb\u5f00\u5979\u4e09\u6b65\u4e4b\u5916\uff0c\u53ef\u662f\u8fd9\u4e09\u4ef6\u4e8b\uff0c\u6211\u4e00\u4ef6\u4e5f\u6ca1\u505a\u5230\u3002\u201d\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u8bb0\u5f97\u5c31\u597d\u4e86\uff0c\u4eca\u65e5\u4f60\u4ecd\u5f53\u4f9d\u7740\u8fd9\u4e09\u4ef6\u4e8b\u505a\uff0c\u5343\u4e07\u4e0d\u80fd\u518d\u5fd8\u4e86\u3002\u201d\u80e1\u6590\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u77f3\u524d\u8f88\uff0c\u4f60\u8eab\u8fb9\u5b9a\u6709\u9e64\u9876\u7ea2\u548c\u5b54\u96c0\u80c6\u5427\uff1f\u8fd9\u4e24\u79cd\u836f\u7269\u548c\u2018\u78a7\u8695\u6bd2\u86ca\u2019\u65e2\u76f8\u514b\u800c\u53c8\u76f8\u8f85\u3002\u4f60\u82e5\u4e0d\u4fe1\uff0c\u8bf7\u770b\u5148\u5e08\u7684\u9057\u8457\u3002\u201d\u8bf4\u7740\u7ffb\u5f00\u90a3\u672c\u9ec4\u7eb8\u5c0f\u518c\uff0c\u9001\u5230\u77f3\u4e07\u55d4\u773c\u524d\u3002 \u3000\u3000\u77f3\u4e07\u55d4\u4e00\u770b\uff0c\u53ea\u89c1\u679c\u7136\u6709\u4e00\u884c\u5b57\u5199\u7740\u9053\uff1a\u201c\u9e64\u9876\u7ea2\u3001\u5b54\u96c0\u80c6\u4e8c\u7269\uff0c\u548c\u78a7\u8695\u5375\u6df7\u7528\uff0c\u65e0\u8272\u65e0\u81ed\uff0c\u552f\u89c1\u6548\u8f83\u7f13\u3002\u201d\u4ed6\u60f3\u518d\u770b\u4e0b\u53bb\uff0c\u7a0b\u7075\u7d20\u5374\u5c06\u4e66\u5408\u4e0a\u4e86\u3002 \u3000\u3000\u77f3\u4e07\u55d4\u5fc3\u60f3\uff1a\u201c\u65e0\u55d4\u8d3c\u79c3\u679c\u662f\u535a\u5b66\uff0c\u8fd9\u4e00\u4e0b\u987b\u5f97\u4e00\u8bd5\u771f\u4f2a\uff0c\u5018\u82e5\u6240\u8a00\u4e0d\u9519\uff0c\u90a3\u4e48\u8fd9\u672c\u300a\u836f\u738b\u795e\u7bc7\u300b\u4e5f\u975e\u5047\u4e66\u4e86\u3002\u201d\u4ed6\u6bd5\u751f\u94bb\u7814\u6bd2\u836f\u3002\u8fd1\u4e8c\u5341\u5e74\u6765\u66f4\u662f\u5e9f\u5bdd\u5fd8\u98df\uff0c\u4ee5\u6c42\u80dc\u8fc7\u5e08\u5144\uff0c\u5b9e\u5df2\u8ff9\u8fd1\u75af\u72c2\u7684\u5730\u6b65\uff0c\u6b64\u65f6\u89c1\u5230\u8fd9\u672c\u6b8b\u65e7\u7684\u9ec4\u7eb8\u6284\u672c\uff0c\u4fbf\u662f\u5929\u4e0b\u6240\u6709\u7684\u73cd\u5b9d\u805a\u5728\u4e00\u8d77\uff0c\u4ea6\u65e0\u5982\u6b64\u73cd\u8d35\u3002\u4ed6\u5929\u6027\u539f\u662f\u5341\u5206\u6b8b\u5fcd\u51c9\u8584\uff0c\u548c\u6155\u5bb9\u666f\u5cb3\u76f8\u4e92\u5229\u7528\uff0c\u672c\u5c31\u5e76\u65e0\u4ec0\u4e48\u5e08\u5f92\u4e4b\u60c5\uff0c\u53c8\u60f3\u8fd9\u756a\u5728\u4ed6\u638c\u5fc3\u8bd5\u7f6e\u201c\u78a7\u8695\u6bd2\u86ca\u201d\u4e4b\u540e\uff0c\u4ed6\u65e5\u540e\u4e00\u6709\u673a\u4f1a\uff0c\u5b9a\u4f1a\u53cd\u566c\uff0c\u5f53\u4e0b\u5168\u4e0d\u8ba1\u53ca\u4e09\u79cd\u5267\u6bd2\u7684\u836f\u7269\u653e\u5728\u4e00\u8d77\uff0c\u4e8b\u540e\u5982\u4f55\u5316\u89e3\uff0c\u53f3\u624b\u98df\u6307\u7684\u6307\u7532\u4e00\u5f39\uff0c\u4fbf\u6709\u4e00\u9635\u6bb7\u7ea2\u8272\u7684\u8584\u96fe\u6563\u5165\u6155\u5bb9\u666f\u5cb3\u638c\u5fc3\uff0c\u8ddf\u7740\u4e2d\u6307\u7684\u6307\u7532\u4e00\u5f39\uff0c\u53c8\u6709\u4e00\u9752\u9ed1\u8272\u8584\u96fe\u6563\u5165\u4ed6\u638c\u5fc3\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u89c1\u4ed6\u4e0d\u5fc5\u4ece\u6000\u4e2d\u63a2\u53d6\u836f\u74f6\uff0c\u6307\u7532\u8f7b\u5f39\uff0c\u968f\u624b\u4fbf\u80fd\u5c06\u6240\u9700\u6bd2\u836f\u653e\u51fa\uff0c\u624b\u811a\u4e4b\u7075\u4fbf\u5feb\u6377\uff0c\u5c1a\u5728\u5148\u5e08\u548c\u81ea\u5df1\u4e4b\u4e0a\uff0c\u4e0d\u81ea\u7981\u6697\u6697\u60ca\u4f69\uff0c\u51dd\u795e\u770b\u4ed6\u8eab\u4e0a\uff0c\u5fc3\u5ff5\u4e00\u52a8\uff0c\u5df2\u77a7\u51fa\u5176\u4e2d\u7384\u5999\u3002 \u3000\u3000\u539f\u6765\u4ed6\u4e00\u6761\u8170\u5e26\u7f1d\u6210\u4e00\u683c\u683c\u7684\u5c0f\u683c\uff0c\u531d\u8170\u4e00\u5468\uff0c\u4e0d\u4e0b\u4e03\u516b\u5341\u683c\uff0c\u6bcf\u4e00\u683c\u4e2d\u5404\u85cf\u836f\u7c89\u3002\u4ed6\u7ec3\u5f97\u719f\u4e86\uff0c\u624b\u638c\u4e00\u4f38\uff0c\u6307\u7532\u4e2d\u5df2\u6311\u4e86\u6240\u9700\u7684\u836f\u7c89\u3002\u7ec3\u5230\u8fd9\u822c\u795e\u4e0d\u77e5\u9b3c\u4e0d\u89c9\u7684\u5730\u6b65\uff0c\u771f\u4e0d\u77e5\u82b1\u4e86\u591a\u5c11\u529f\u592b\uff0c\u5982\u6b64\u4e00\u4e3e\u624b\u4fbf\u5f39\u51fa\u6bd2\u7c89\uff0c\u5bf9\u65b9\u600e\u80fd\u9632\u5907\u8eb2\u907f\uff1f \u3000\u3000\u90a3\u9e64\u9876\u7ea2\u548c\u5b54\u96c0\u80c6\u4e24\u79cd\u836f\u7c89\u8fd9\u822c\u6563\u5165\u6155\u5bb9\u666f\u5cb3\u7684\u638c\u5fc3\uff0c\u5f53\u771f\u662f\u8fc5\u96f7\u4e0d\u53ca\u63a9\u8033\uff0c\u90a3\u5bb9\u4ed6\u6709\u7f29\u624b\u4f59\u5730\uff1f\u6155\u5bb9\u666f\u5cb3\u672c\u5df2\u7acb\u4e0b\u5fc3\u610f\uff0c\u51b3\u4e0d\u5bb9\u8fd9\u4e24\u79cd\u5267\u6bd2\u7684\u6bd2\u7269\u518d\u6cbe\u81ea\u5df1\u808c\u80a4\uff0c\u62da\u7740\u548c\u77f3\u4e07\u55d4\u7834\u8138\uff0c\u4e5f\u8981\u6297\u62d2\uff0c\u773c\u89c1\u4ed6\u5bf9\u81ea\u5df1\u5982\u6b64\u72e0\u6bd2\uff0c\u5b81\u53ef\u5411\u5c0f\u5e08\u59b9\u5c48\u670d\uff0c\u5e08\u5144\u59b9\u4e09\u4eba\u8054\u624b\uff0c\u4e5f\u80dc\u4e8e\u6b64\u540e\u53d7\u4ed6\u65e0\u7a77\u65e0\u5c3d\u7684\u6298\u78e8\u3002 \u3000\u3000\u90a3\u77e5\u77f3\u4e07\u55d4\u4e0b\u6bd2\u7684\u624b\u6cd5\u5feb\u5982\u7535\u95ea\uff0c\u6155\u5bb9\u666f\u5cb3\u5ff5\u5934\u5c1a\u672a\u8f6c\u5b8c\uff0c\u4e24\u822c\u5267\u6bd2\u5df2\u6cbe\u638c\u5fc3\u3002 \u3000\u3000\u4f46\u89c1\u4e00\u7ea2\u4e00\u9752\u7684\u8584\u96fe\u7247\u523b\u95f4\u4fbf\u5373\u6e17\u5165\u808c\u80a4\uff0c\u624b\u638c\u5fc3\u539f\u6709\u90a3\u5c42\u9690\u9690\u7684\u9752\u7eff\u4e4b\u8272\uff0c\u679c\u7136\u767b\u65f6\u4e0d\u89c1\uff0c\u5df2\u8ddf\u5e73\u5e38\u7684\u808c\u80a4\u6beb\u65e0\u5206\u522b\u3002 \u3000\u3000\u77f3\u4e07\u55d4\u6b22\u53eb\u4e00\u58f0\uff1a\u201c\u597d\uff01\u201d\u4f38\u624b\u4fbf\u5f80\u7a0b\u7075\u7d20\u624b\u4e2d\u7684\u300a\u836f\u738b\u795e\u7bc7\u300b\u6293\u6765\u3002\u7a0b\u7075\u7d20\u7adf\u4e0d\u9000\u7f29\uff0c\u53ea\u662f\u5fae\u5fae\u4e00\u7b11\u3002\u77f3\u4e07\u55d4\u4e94\u6839\u624b\u6307\u5c06\u548c\u4e66\u76ae\u76f8\u78b0\uff0c\u7a81\u7136\u60f3\u8d77\uff1a\u201c\u8fd9\u4e2b\u5934\u662f\u90a3\u8d3c\u79c3\u7684\u5173\u95e8\u5f1f\u5b50\uff0c\u4e66\u4e0a\u600e\u80fd\u6ca1\u6709\u673a\u5173\uff1f\u201d\u6025\u5fd9\u7f29\u624b\uff0c\u5fc3\u4e2d\u6697\u9a82\uff1a\u201c\u8001\u77f3\u554a\u8001\u77f3\uff0c\u4f60\u82e5\u6562\u5c0f\u89d1\u4e86\u8fd9\u4e2b\u5934\uff0c\u4fbf\u6709\u5341\u6761\u6027\u547d\uff0c\u4e5f\u8981\u9001\u5728\u5979\u624b\u91cc\u4e86\u3002\u201d \u3000\u3000\u6155\u5bb9\u666f\u5cb3\u638c\u5fc3\u4e00\u9635\u9ebb\u4e00\u9635\u75d2\uff0c\u8fd9\u9635\u9ebb\u75d2\u76f4\u4f20\u5165\u5fc3\u91cc\uff0c\u4fbf\u4f3c\u6709\u5343\u4e07\u53ea\u8682\u868a\u540c\u65f6\u5728\u54ac\u556e\u5fc3\u810f\u4e00\u822c\uff0c\u98a4\u58f0\u53eb\u9053\uff1a\u201c\u5c0f\u5e08\u59b9\u5feb\u53d6\u89e3\u836f\u7ed9\u6211\u3002\u201d \u3000\u3000\u7a0b\u7075\u7d20\u5947\u9053\uff1a\u201c\u54a6\uff0c\u5927\u5e08\u54e5\uff0c\u4f60\u600e\u4f1a\u5fd8\u4e86\u5148\u5e08\u7684\u53ee\u5631\uff1f\u672c\u95e8\u4e2d\u4eba\u4e0d\u80fd\u653e\u86ca\uff0c\u53c8\u6709\u4e5d\u79cd\u6ca1\u89e3\u836f\u7684\u6bd2\u836f\u51b3\u8ba1\u4e0d\u80fd\u4f7f\u7528\u3002\u201d\u6155\u5bb9\u666f\u5cb3\u4e00\u542c\u6b64\u8a00\uff0c\u80cc\u4e0a\u767b\u65f6\u51fa\u4e86\u4e00\u9635\u51b7\u6c57\uff0c\u8bf4\u9053\uff1a\u201c\u9e64\u9876\u7ea2\uff0c\u5b54\u2026\u2026\u5b54\u2026\u2026\u96c0\u80c6\u5c5e\u4e8e\u4e5d\u5927\u7981\u836f\uff0c\u4f60\u2026\u2026\u4f60\u600e\u5730\u7528\u5728\u6211\u8eab\u4e0a\uff1f\u8fd9\u4e0d\u662f\u8fdd\u80cc\u5148\u5e08\u7684\u8bad\u8bf2\u4e48\uff1f\u201d \u3000\u3000\u7a0b\u7075\u7d20\u51b7\u51b7\u5730\u9053\uff1a\u201c\u5927\u5e08\u54e5\u5c45\u7136\u8fd8\u8bb0\u5f97\u5148\u5e08\uff0c\u5c45\u7136\u8fd8\u8bb0\u5f97\u4e0d\u53ef\u8fdd\u80cc\u5148\u5e08\u7684\u8bad\u8bf2\uff0c\u5f53\u771f\u662f\u5927\u51fa\u5c0f\u59b9\u7684\u610f\u6599\u4e4b\u5916\u4e86\u3002\u90a3\u78a7\u8695\u6bd2\u86ca\u662f\u6211\u653e\u5728\u4f60\u8eab\u4e0a\u7684\u4e48\uff1f\u9e64\u9876\u7ea2\u548c\u5b54\u96c0\u80c6\uff0c\u662f\u6211\u653e\u5728\u4f60\u8eab\u4e0a\u7684\u4e48\uff1f\u5148\u5e08\u8c06\u8c06\u5631\u5490\u54b1\u4eec\uff0c\u4fbf\u662f\u9047\u4e0a\u751f\u6b7b\u5173\u5934\uff0c\u4e5f\u51b3\u4e0d\u53ef\u4f7f\u7528\u4e0d\u80fd\u89e3\u6551\u7684\u6bd2\u836f\uff0c\u8fd9\u662f\u672c\u95e8\u7684\u7b2c\u4e00\u5927\u6212\u3002\u77f3\u524d\u8f88\u548c\u5927\u5e08\u54e5\u3001\u4e09\u5e08\u59ca\u90fd\u5df2\u8131\u79bb\u672c\u95e8\uff0c\u8fd9\u4e9b\u6212\u6761\uff0c\u81ea\u7136\u4e0d\u5fc5\u9075\u5b88\u3002\u5c0f\u59b9\u53ef\u4e0d\u6562\u5fd8\u8bb0\u554a\u3002\u201d \u3000\u3000\u6155\u5bb9\u666f\u5cb3\u4f38\u53f3\u624b\u6293\u7d27\u5de6\u624b\u7684\u8109\u95e8\uff0c\u963b\u6b62\u6bd2\u6c14\u4e0a\u884c\uff0c\u6ee1\u5934\u51b7\u6c57\uff0c\u5df2\u662f\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002\u859b\u9e4a\u53f3\u624b\u4e00\u7ffb\uff0c\u4f38\u77ed\u5200\u5728\u6155\u5bb9\u666f\u5cb3\u5de6\u624b\u5fc3\u4e2d\u5272\u4e86\u4e24\u4e2a\u4ea4\u5dee\u7684\u5341\u5b57\uff0c\u56fe\u4f7f\u6bd2\u6027\u968f\u8840\u5916\u6d41\uff0c\u660e\u77e5\u8fd9\u6cd5\u5b50\u89e3\u6551\u4e0d\u5f97\uff0c\u5374\u4e5f\u53ef\u4f7f\u6bd2\u6027\u7a0d\u51cf\uff0c\u4e00\u9762\u8bf4\u9053\uff1a\u201c\u5c0f\u5e08\u59b9\uff0c\u5e08\u7236\u7684\u9057\u8457\u4e0a\u600e\u4e48\u8bf4\uff1f\u4ed6\u8001\u4eba\u5bb6\u65e2\u4f20\u4e0b\u4e86\u8fd9\u4e09\u79cd\u6bd2\u7269\u5171\u4f7f\u7684\u6cd5\u5b50\uff0c\u5b9a\u7136\u4e5f\u6709\u89e3\u6551\u4e4b\u9053\u3002\u201d \u3000\u3000\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u859b\u4e09\u59ca\u53e3\u4e2d\u7684\u2018\u5e08\u7236\u2019\uff0c\u662f\u6307\u54ea\u4e00\u4f4d\uff1f\u662f\u5c0f\u59b9\u7684\u5e08\u7236\u65e0\u55d4\u5927\u5e08\u5462\uff0c\u8fd8\u662f\u4f60\u4eec\u8d24\u592b\u5987\u7684\u5e08\u7236\u77f3\u524d\u8f88\uff1f\u201d \u3000\u3000\u859b\u9e4a\u542c\u5979\u8f9e\u950b\u5484\u5484\u903c\u4eba\uff0c\u5fc3\u4e2d\u6012\u6781\u6bd2\u9a82\uff0c\u4f46\u4e08\u592b\u7684\u6027\u547d\u5371\u5728\u9877\u523b\uff0c\u6b64\u65f6\u6709\u6c42\u4e8e\u5979\uff0c\u53e3\u5934\u53ea\u5f97\u5c48\u670d\uff0c\u8bf4\u9053\uff1a\u201c\u662f\u611a\u592b\u5987\u8be5\u6b7b\uff0c\u8fd8\u671b\u5c0f\u5e08\u59b9\u5ff5\u5728\u6614\u65e5\u540c\u95e8\u4e4b\u60c5\uff0c\u77a7\u5728\u5148\u5e08\u65e0\u55d4\u5927\u5e08\u7684\u9762\u4e0a\uff0c\u9ad8\u62ac\u8d35\u624b\uff0c\u6551\u4ed6\u4e00\u547d\u3002\u201d \u3000\u3000\u7a0b\u7075\u7d20\u7ffb\u5f00\u300a\u836f\u738b\u795e\u7bc7\u300b\uff0c\u6307\u7740\u4e24\u884c\u5b57\u9053\uff1a\u201c\u5e08\u59ca\u8bf7\u770b\uff0c\u6b64\u4e8b\u987b\u602a\u4e0d\u5f97\u6211\u3002\u201d \u3000\u3000\u859b\u9e4a\u987a\u7740\u5979\u624b\u6307\u770b\u53bb\uff0c\u53ea\u89c1\u518c\u4e0a\u5199\u9053\uff1a\u201c\u78a7\u8695\u6bd2\u86ca\u548c\u9e64\u9876\u7ea2\u3001\u5b54\u96c0\u80c6\u6df7\u7528\uff0c\u5267\u6bd2\u5165\u5fc3\uff0c\u65e0\u6cd5\u53ef\u6cbb\uff0c\u6212\u4e4b\u6212\u4e4b\u3002\u201d\u859b\u9e4a\u5927\u6012\uff0c\u8f6c\u5934\u5411\u77f3\u4e07\u55d4\u9053\uff1a\u201c\u5e08\u7236\uff0c\u8fd9\u4e66\u4e0a\u660e\u660e\u5199\u7740\u8fd9\u4e09\u79cd\u6bd2\u836f\u6df7\u7528\uff0c\u65e0\u836f\u53ef\u6cbb\uff0c\u4f60\u5374\u5982\u4f55\u5728\u666f\u5cb3\u8eab\u4e0a\u8bd5\u7528\uff1f\u201d\u5979\u867d\u53e3\u79f0\u201c\u5e08\u7236\u201d\uff0c\u4f46\u8bf4\u8bdd\u7684\u795e\u60c5\u5df2\u662f\u58f0\u8272\u4ff1\u5389\u3002 \u3000\u3000\u300a\u836f\u738b\u795e\u7bc7\u300b\u4e0a\u8fbe\u4e24\u884c\u5b57\uff0c\u77f3\u4e07\u55d4\u5176\u5b9e\u5e76\u672a\u77a7\u89c1\uff0c\u4f46\u5373\u4f7f\u770b\u5230\u4e86\uff0c\u4ed6\u4e5f\u51b3\u4e0d\u81f4\u56e0\u6b64\u800c\u7a0d\u6709\u987e\u5fcc\uff0c\u8fd9\u65f6\u542c\u859b\u9e4a\u5389\u58f0\u8d23\u95ee\uff0c\u5982\u4f55\u80af\u81ea\u627f\u4e0d\u77e5\uff0c\u4e22\u8fd9\u4e2a\u5927\u8138\uff1f\u53ea\u9053\uff1a\u201c\u5c06\u90a3\u4e66\u7ed9\u6211\u77a7\u77a7\uff0c\u770b\u5176\u4e2d\u8fd8\u6709\u4ec0\u4e48\u53e4\u602a\uff1f\u201d \u3000\u3000\u859b\u9e4a\u6012\u6781\uff0c\u5fc3\u77e5\u518d\u6709\u72b9\u8c6b\uff0c\u4e08\u592b\u6027\u547d\u4e0d\u4fdd\uff0c\u77ed\u5200\u4e00\u6325\uff0c\u5c06\u6155\u5bb9\u666f\u5cb3\u7684\u4e00\u6761\u624b\u81c2\u9f50\u80a9\u65a9\u65ad\u3002\u8981\u77e5\u90a3\u4e09\u79cd\u6bd2\u836f\u5389\u5bb3\u65e0\u6bd4\uff0c\u867d\u81ea\u638c\u5fc3\u6e17\u5165\uff0c\u4f46\u8fd9\u65f6\u6bd2\u6027\u4e0a\u884c\uff0c\u5355\u662f\u5272\u53bb\u624b\u638c\u5df2\u7136\u65e0\u7528\uff0c\u5e78\u597d\u4e09\u836f\u6df7\u7528\uff0c\u53d1\u4f5c\u8f83\u6162\uff0c\u540c\u65f6\u4ed6\u638c\u5fc3\u5e76\u65e0\u4f24\u53e3\uff0c\u6bd2\u836f\u5e76\u975e\u6d41\u5165\u8840\u8109\uff0c\u5272\u53bb\u4e00\u6761\u624b\u81c2\uff0c\u6682\u65f6\u4fdd\u4f4f\u4e86\u6027\u547d\uff0c\u5426\u5219\u65e9\u5df2\u6bd2\u53d1\u8eab\u4ea1\u3002 \u3000\u3000\u859b\u9e4a\u662f\u65e0\u55d4\u5927\u5e08\u4e4b\u5f92\uff0c\u81ea\u6709\u5979\u4e00\u5957\u6b62\u8840\u7597\u4f24\u7684\u672c\u9886\uff0c\u7247\u523b\u95f4\u5305\u624e\u597d\u4e86\u6155\u5bb9\u666f\u5cb3\u7684\u4f24\u53e3\uff0c\u624b\u6cd5\u6781\u662f\u5e72\u51c0\u5229\u843d\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u5927\u5e08\u54e5\uff0c\u4e09\u5e08\u59ca\uff0c\u975e\u662f\u6211\u6709\u610f\u9677\u5bb3\u4e8e\u4f60\u3002\u4f60\u4e24\u4f4d\u80cc\u53db\u5e08\u95e8\uff0c\u6539\u62dc\u5e08\u7236\u7684\u4ec7\u4eba\u4e3a\u5e08\uff0c\u539f\u5df2\u7f6a\u4e0d\u5bb9\u8bdb\uff0c\u52a0\u4e4b\u5bb3\u6b7b\u4e8c\u5e08\u54e5\u7236\u5b50\u4e8c\u4eba\uff0c\u5f53\u771f\u5929\u4eba\u5171\u6124\u3002\u773c\u4e0b\u672c\u95e8\u4f20\u4eba\uff0c\u53ea\u6709\u5c0f\u59b9\u4e00\u4eba\uff0c\u4e24\u4f4d\u53db\u5e08\u7684\u7f6a\u884c\uff0c\u82e5\u4e0d\u662f\u5c0f\u59b9\u624b\u52a0\u60e9\u6212\uff0c\u96be\u9053\u4efb\u7531\u5e08\u7236\u4e00\u4e16\u82f1\u540d\uff0c\u8eab\u540e\u53cd\u800c\u683d\u5728\u4ed6\u4ec7\u4eba\u548c\u5f92\u513f\u7684\u624b\u4e2d\uff1f\u4e8c\u5e08\u54e5\u7236\u5b50\u60e8\u906d\u6a2a\u6b7b\uff0c\u82e5\u4e0d\u662f\u5c0f\u59b9\u51fa\u6765\u4e3b\u6301\u516c\u9053\uff0c\u96be\u9053\u4efb\u7531\u4ed6\u4e8c\u4eba\u6c38\u8fdc\u542b\u51a4\u4e5d\u6cc9\uff1f\u201d \u3000\u3000\u5979\u8eab\u5f62\u7626\u5f31\uff0c\u5e74\u7eaa\u5e7c\u5c0f\uff0c\u4f46\u8fd9\u756a\u8bdd\u4f83\u4f83\u800c\u8a00\uff0c\u8bf4\u6765\u51db\u7136\u751f\u5a01\u3002 \u3000\u3000\u80e1\u6590\u542c\u5f97\u6697\u6697\u70b9\u5934\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u4e24\u4eba\u5351\u9119\u72e0\u6bd2\uff0c\u65e9\u8be5\u6740\u4e86\u3002\u201d \u3000\u3000\u53ea\u542c\u5979\u53c8\u9053\uff1a\u201c\u5927\u5e08\u54e5\u4e00\u81c2\u867d\u53bb\uff0c\u6bd2\u6c14\u5df2\u7136\u653b\u5fc3\uff0c\u4e00\u6708\u4e4b\u5185\uff0c\u4ecd\u5f53\u6bd2\u53d1\u4e0d\u6cbb\u3002\u4e24\u4f4d\u5df2\u53db\u51fa\u672c\u95e8\uff0c\u906d\u4eba\u6bd2\u624b\uff0c\u672c\u4e0e\u5c0f\u59b9\u65e0\u5173\uff0c\u53ea\u662f\u77a7\u5728\u5148\u5e08\u7684\u4efd\u4e0a\uff0c\u8fd9\u91cc\u6709\u4e09\u7c92\u2018\u751f\u751f\u9020\u5316\u4e39\u2019\uff0c\u662f\u5e08\u7236\u4ee5\u6570\u5e74\u5fc3\u8840\u5236\u70bc\u800c\u6210\uff0c\u5c0f\u59b9\u4ee3\u5148\u5e08\u8d50\u4f60\uff0c\u6bcf\u4e00\u7c92\u53ef\u5ef6\u5e08\u5144\u4e09\u5e74\u5bff\u547d\u3002\u5e08\u5144\u670d\u98df\u4e4b\u540e\uff0c\u76fc\u4f60\u8bb0\u7740\u5148\u5e08\u7684\u6069\u5fb7\uff0c\u8fd8\u8bf7\u62ca\u5fc3\u81ea\u95ee\uff1a \u3000\u3000\u5230\u5e95\u662f\u4f60\u539f\u6765\u7684\u5e08\u7236\u5f85\u4f60\u597d\uff0c\u8fd8\u662f\u65b0\u62dc\u7684\u5e08\u7236\u5f85\u4f60\u597d\uff1f\u201d\u8bf4\u7740\u4ece\u6000\u4e2d\u53d6\u51fa\u4e09\u7c92\u7ea2\u8272\u836f\u4e38\uff0c\u6258\u5728\u624b\u91cc\u3002 \u3000\u3000\u859b\u9e4a\u6b63\u8981\u4f38\u624b\u63a5\u8fc7\uff0c\u77f3\u4e07\u55d4\u51b7\u7b11\u9053\uff1a\u201c\u624b\u81c2\u90fd\u5df2\u780d\u65ad\uff0c\u8fd8\u6015\u4ec0\u4e48\u6bd2\u6c14\u653b\u5fc3\uff1f\u8fd9\u4e09\u7c92\u2018\u6b7b\u6b7b\u7d22\u547d\u4e39\u2019\u4e00\u670d\u4e0b\u809a\uff0c\u90a3\u624d\u662f\u6bd2\u6c14\u653b\u5fc3\u5462\u3002\u201d \u3000\u3000\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u4e24\u4f4d\u82e5\u662f\u76f8\u4fe1\u65b0\u5e08\u7236\u7684\u8bdd\uff0c\u90a3\u4e48\u8fd9\u4e09\u7c92\u4e39\u836f\u539f\u662f\u7528\u4e0d\u7740\u4e86\u3002\u201d\u8bf4\u7f62\u4fbf\u8981\u6536\u5165\u6000\u4e2d\u3002\u6155\u5bb9\u666f\u5cb3\u6025\u9053\uff1a\u201c\u4e0d\uff01\u5c0f\u5e08\u59b9\uff0c\u8bf7\u4f60\u7ed9\u6211\u3002\u201d\u859b\u9e4a\u9053\uff1a\u201c\u591a\u8c22\u5c0f\u5e08\u59b9\uff0c\u4ece\u4eca\u800c\u540e\uff0c\u6211\u4e8c\u4eba\u6539\u8fc7\u81ea\u65b0\uff0c\u91cd\u505a\u597d\u4eba\u3002\u201d\u4f4e\u5934\u8d70\u5230\u7a0b\u7075\u7d20\u8eab\u524d\uff0c\u53d6\u8fc7\u4e09\u679a\u4e39\u836f\uff0c\u7a81\u7136\u8eab\u5f62\u4e00\u6643\uff0c\u6012\u559d\uff1a\u201c\u77f3\u4e07\u55d4\uff0c\u4f60\u597d\u6bd2\u7684\u2026\u2026\u201d\u4e00\u53e5\u8bdd\u672a\u8bf4\u5b8c\uff0c\u4fef\u8eab\u6454\u5012\u5728\u5730\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u548c\u80e1\u6590\u90fd\u662f\u5927\u5403\u4e00\u60ca\uff0c\u6ca1\u89c1\u77f3\u4e07\u55d4\u6709\u4f55\u52a8\u5f39\uff0c\u600e\u5730\u5df2\u4e0b\u4e86\u6bd2\u624b\uff1f\u7a0b\u7075\u7d20\u5f2f\u4e0b\u8170\u6765\uff0c\u7ffb\u8fc7\u859b\u9e4a\u8eab\u5b50\uff0c\u8981\u770b\u5979\u5982\u4f55\u88ab\u5bb3\uff0c\u662f\u5426\u6709\u6551\uff0c\u521a\u5c06\u5979\u8eab\u5b50\u6273\u8f6c\uff0c\u7a81\u7136\u53f3\u624b\u624b\u8155\u4e00\u7d27\uff0c\u5df2\u88ab\u859b\u9e4a\u6293\u4f4f\u3002\u7a0b\u7075\u7d20\u77e5\u9053\u4e0d\u597d\uff0c\u5de6\u624b\u5f85\u8981\u5f80\u5979\u5934\u9876\u62cd\u843d\uff0c\u4f46\u53f3\u624b\u8109\u95e8\u88ab\u5979\u6293\u4f4f\uff0c\u5168\u8eab\u9178\u9ebb\uff0c\u7adf\u662f\u52a8\u5f39\u4e0d\u5f97\uff0c\u859b\u9e4a\u53f3\u624b\u63e1\u7740\u77ed\u5200\uff0c\u5200\u5c16\u5df2\u62b5\u5728\u7a0b\u7075\u7d20\u80f8\u53e3\uff0c\u559d\u9053\uff1a\u201c\u5c06\u300a\u836f\u738b\u795e\u7bc7\u300b\u653e\u4e0b\uff01\u201d\u7a0b\u7075\u7d20\u4e00\u5ff5\u4e4b\u4ec1\uff0c\u7adf\u81f4\u53d7\u5236\uff0c\u53ea\u5f97\u5c06\u300a\u836f\u738b\u795e\u7bc7\u300b\u6454\u5728\u5730\u4e0b\u3002 \u3000\u3000\u80e1\u6590\u5f85\u8981\u4e0a\u524d\u76f8\u6551\uff0c\u4f46\u89c1\u859b\u9e4a\u7684\u5200\u5c16\u62b5\u6b63\u4e86\u7a0b\u7075\u7d20\u7684\u5fc3\u53e3\uff0c\u53ea\u8981\u8f7b\u8f7b\u5411\u524d\u4e00\u9001\uff0c\u7acb\u65f6\u6ca1\u547d\uff0c\u5fc3\u4e2d\u867d\u662f\u5927\u6025\uff0c\u5374\u4e0d\u6562\u52a8\u624b\u3002 \u3000\u3000\u859b\u9e4a\u7d27\u7d27\u6293\u7740\u7a0b\u7075\u7d20\u624b\u8155\uff0c\u8bf4\u9053\uff1a\u201c\u5e08\u7236\uff0c\u5f1f\u5b50\u52a9\u4f60\u593a\u5230\u300a\u836f\u738b\u795e\u7bc7\u300b\uff0c\u8bf7\u4f60\u5c06\u78a7\u8695\u6bd2\u86ca\u3001\u9e64\u9876\u7ea2\u3001\u5b54\u96c0\u80c6\u4e09\u79cd\u836f\u7269\uff0c\u653e\u5728\u8fd9\u5c0f\u8d31\u4eba\u7684\u638c\u5fc3\uff0c\u77a7\u5979\u662f\u4e0d\u662f\u4e5f\u6551\u4e0d\u4e86\u81ea\u5df1\u6027\u547d\u3002\u201d\u77f3\u4e07\u55d4\u7b11\u9053\uff1a\u201c\u597d\u5f92\u513f\uff0c\u597d\u5f92\u513f\uff0c\u8fd9\u6cd5\u5b50\u5b9e\u5728\u9ad8\u660e\u3002\u201d\u53d6\u51fa\u91d1\u76d2\uff0c\u7528\u91d1\u5319\u6311\u4e86\u78a7\u8695\u6bd2\u86ca\uff0c\u4e24\u679a\u6307\u7532\u4e2d\u85cf\u4e86\u9e64\u9876\u7ea2\u548c\u5b54\u96c0\u80c6\u7684\u6bd2\u7c89\uff0c\u4fbf\u8981\u5f80\u7a0b\u7075\u7d20\u638c\u5fc3\u653e\u843d\u3002 \u3000\u3000\u6155\u5bb9\u666f\u5cb3\u91cd\u4f24\u4e4b\u540e\uff0c\u867d\u662f\u6447\u6447\u6b32\u5012\uff0c\u5374\u77e5\u8fd9\u662f\u5343\u94a7\u4e00\u53d1\u7684\u673a\u4f1a\uff0c\u53ea\u8981\u7a0b\u7075\u7d20\u638c\u5fc3\u4e5f\u53d7\u4e86\u8fd9\u4e09\u79cd\u6bd2\u836f\uff0c\u5979\u82e5\u6709\u89e3\u836f\uff0c\u52bf\u987b\u53d6\u51fa\u81ea\u7597\uff0c\u81ea\u5df1\u4fbf\u53ef\u593a\u800c\u5148\u7528\uff0c\u5c31\u7b97\u771f\u7684\u6ca1\u6709\u89e3\u836f\uff0c\u4e5f\u662f\u62a5\u4e86\u9002\u624d\u4e4b\u4ec7\uff0c\u53eb\u5979\u4f5c\u6cd5\u81ea\u6bd9\uff0c\u5f53\u4e0b\u594b\u529b\u62e6\u5728\u80e1\u6590\u8eab\u524d\uff0c\u9632\u4ed6\u963b\u6320\u77f3\u4e07\u55d4\u4e0b\u6bd2\u3002 \u3000\u3000\u80e1\u6590\u6b63\u5f53\u65e0\u6cd5\u53ef\u65bd\u4e4b\u9645\uff0c\u7a81\u89c1\u6155\u5bb9\u666f\u5cb3\u62a2\u5728\u81ea\u5df1\u8eab\u524d\uff0c\u5de6\u624b\u547c\u7684\u4e00\u62f3\uff0c\u4fbf\u5f80\u4ed6\u9762\u95e8\u51fb\u53bb\u3002\u6155\u5bb9\u666f\u5cb3\u62ac\u53f3\u624b\u62db\u67b6\uff0c\u80e1\u6590\u6b64\u65f6\u60c5\u6025\u62da\u547d\uff0c\u90a3\u5bb9\u4ed6\u6709\u8fd8\u62db\u4f59\u5730\uff0c\u5de6\u624b\u62f3\u5c1a\u672a\u6253\u5b9e\uff0c\u53f3\u624b\u638c\u51fa\u5982\u98ce\uff0c\u65e0\u58f0\u606f\u7684\u63a8\u5728\u4ed6\u80f8\u53e3\u3002\u8fd9\u4e00\u638c\u867d\u65e0\u58f0\u54cd\uff0c\u529b\u9053\u5374\u662f\u5947\u91cd\uff0c\u53ea\u63a8\u5f97\u6155\u5bb9\u666f\u5cb3\u76f4\u5411\u859b\u9e4a\u649e\u53bb\u3002\u859b\u9e4a\u88ab\u4ed6\u4e00\u649e\uff0c\u767b\u65f6\u6454\u5012\uff0c\u53ef\u662f\u5de6\u624b\u4ecd\u7136\u7262\u7262\u6293\u4f4f\u7a0b\u7075\u7d20\u7684\u624b\u8155\u4e0d\u653e\u3002 \u3000\u3000\u80e1\u6590\u7eb5\u8eab\u4e0a\u524d\uff0c\u5728\u859b\u9e4a\u7684\u9a7c\u80cc\u5fc3\u4e0a\u91cd\u91cd\u8e22\u4e86\u4e00\u811a\uff0c\u859b\u9e4a\u5403\u75db\u4e0d\u8fc7\uff0c\u53ea\u5f97\u677e\u5f00\u4e86\u7a0b\u7075\u7d20\u7684\u624b\u8155\u3002\u8fd9\u51e0\u4e0b\u72b9\u4f3c\u7535\u5149\u77f3\u706b\uff0c\u5b9e\u53ea\u77ac\u606f\u95f4\u7684\u4e8b\uff0c\u859b\u9e4a\u624b\u638c\u521a\u88ab\u9707\u5f00\uff0c\u77f3\u4e07\u55d4\u7684\u624b\u722a\u5df2\u7136\u6293\u5230\u3002\u80e1\u6590\u751f\u6015\u4ed6\u624b\u4e2d\u6bd2\u836f\u78b0\u5230\u7a0b\u7075\u7d20\u8eab\u5b50\uff0c\u53f3\u624b\u6025\u63a0\uff0c\u5728\u4ed6\u80a9\u5934\u4e00\u63a8\uff0c\u77f3\u4e07\u55d4\u53cd\u638c\u64d2\u62ff\uff0c\u5411\u4ed6\u53f3\u624b\u6293\u6765\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u6025\u53eb\uff1a\u201c\u5feb\u9000\uff01\u201d\u80e1\u6590\u82e5\u662f\u65bd\u5c55\u5c0f\u64d2\u62ff\u624b\u4e2d\u7684\u201c\u4e5d\u66f2\u6298\u9aa8\u6cd5\u201d\uff0c\u539f\u53ef\u5c06\u4ed6\u624b\u638c\u7684\u4e94\u6839\u6307\u5934\u7acb\u65f6\u626d\u65ad\uff0c\u4f46\u8fd9\u4eba\u6307\u4e0a\u5e26\u6709\u5267\u6bd2\uff0c\u5982\u4f55\u6562\u78b0\uff1f\u6025\u5fd9\u540e\u8dc3\u800c\u907f\uff0c\u77f3\u4e07\u55d4\u4e00\u6293\u4e0d\u4e2d\uff0c\u987a\u624b\u5c06\u91d1\u5319\u63b7\u51fa\u3002\u8ddf\u7740\u624b\u6307\u8fde\u5f39\uff0c\u6bd2\u7c89\u5316\u4f5c\u70df\u96fe\uff0c\u55b7\u4e0a\u4e86\u80e1\u6590\u7684\u624b\u80cc\u3002 \u3000\u3000\u80e1\u6590\u4e0d\u77e5\u81ea\u5df1\u5df2\u7136\u4e2d\u6bd2\uff0c\u4f46\u60f3\u8fd9\u4e09\u4eba\u5978\u9669\u72e0\u6bd2\u65e0\u6bd4\uff0c\u7acb\u5fc3\u6bd9\u4e4b\u4e8e\u5f53\u573a\uff0c\u5355\u5200\u6325\u51fa\uff0c\u767d\u5149\u95ea\u95ea\uff0c\u5168\u662f\u8fdb\u624b\u62db\u6570\u3002\u77f3\u4e07\u55d4\u864e\u6491\u672a\u53ca\u62db\u67b6\uff0c\u53ea\u89c9\u5de6\u5e73\u4e0a\u4e00\u51c9\uff0c\u4e09\u62a5\u624b\u6307\u5df2\u88ab\u524a\u65ad\u3002\u4ed6\u53c8\u60ca\u53c8\u6015\uff0c\u53f3\u624b\u53c8\u662f\u4e00\u5f39\uff0c\u5f39\u51fa\u4e00\u9635\u70df\u96fe\u3002\u7a0b\u7075\u7d20\u60ca\u53eb\uff1a\u201c\u5927\u54e5\uff0c\u9000\u540e\uff01\u201d\u80e1\u6590\u6321\u5728\u7a0b\u7075\u7d20\u8eab\u524d\uff0c\u4e0d\u6562\u5411\u524d\u8ffd\u51fb\u3002\u773c\u89c1\u77f3\u4e07\u55d4\u3001\u6155\u5bb9\u666f\u5cb3\u3001\u859b\u9e4a\u4e00\u9f50\u9003\u51fa\u4e86\u5e99\u5916\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u63e1\u7740\u80e1\u6590\u7684\u624b\uff0c\u5fc3\u5982\u5200\u5272\uff0c\u81ea\u5df1\u867d\u7136\u5f97\u8131\u5927\u96be\uff0c\u53ef\u662f\u80e1\u6590\u4e3a\u4e86\u76f8\u6551\u81ea\u5df1\uff0c\u624b\u80cc\u4e0a\u5df2\u6cbe\u4e0a\u4e86\u78a7\u8695\u6bd2\u86ca\u3001\u9e64\u9876\u7ea2\u3001\u5b54\u96c0\u80c6\u4e09\u79cd\u521a\u6bd2\uff0c\u300a\u836f\u738b\u795e\u7bc7\u300b\u4e0a\u8bf4\u5f97\u660e\u660e\u767d\u767d\uff1a\u201c\u5267\u6bd2\u5165\u5fc3\uff0c\u65e0\u836f\u53ef\u6cbb\u3002\u201d \u3000\u3000\u96be\u9053\u6325\u5200\u7acb\u523b\u5c06\u4ed6\u53f3\u624b\u780d\u65ad\uff0c\u518d\u8ba9\u4ed6\u670d\u98df\u201c\u751f\u751f\u9020\u5316\u4e39\u201d\uff0c\u5ef6\u7eed\u4e5d\u5e74\u6027\u547d\uff1f\u4e09\u822c\u5267\u6bd2\u5165\u4f53\uff0c\u4ee5\u201c\u751f\u751f\u9020\u5316\u4e39\u201d\u5ef6\u547d\u4e5d\u5e74\uff0c\u6b64\u540e\u518d\u670d\u201c\u751f\u751f\u9020\u5316\u4e39\u201d\u4e5f\u662f\u65e0\u6548\u4e86\u3002 \u3000\u3000\u4ed6\u662f\u81ea\u5df1\u5728\u8fd9\u4e16\u754c\u4e0a\u552f\u4e00\u4eb2\u4eba\uff0c\u548c\u4ed6\u76f8\u5904\u4e86\u8fd9\u4e9b\u65e5\u5b50\u4e4b\u540e\uff0c\u5728\u5979\u5fc3\u5e95\uff0c\u65e9\u5df2\u5c06\u4ed6\u7684\u4e00\u5207\u77a7\u5f97\u6bd4\u81ea\u5df1\u91cd\u8981\u5f97\u591a\u3002\u8fd9\u6837\u597d\u7684\u4eba\uff0c\u96be\u9053\u4fbf\u53ea\u518d\u6d3b\u4e5d\u5e74\uff1f \u3000\u3000\u7a0b\u7075\u7d20\u4e0d\u52a0\u591a\u60f3\uff0c\u8111\u6d77\u4e2d\u5ff5\u5934\u4e00\u8f6c\uff0c\u65e9\u5df2\u6253\u5b9a\u4e86\u4e3b\u610f\uff0c\u53d6\u51fa\u4e00\u9897\u767d\u8272\u836f\u4e38\uff0c\u653e\u5728\u80e1\u6590\u53e3\u4e2d\uff0c\u98a4\u58f0\u9053\uff1a\u201c\u5feb\u541e\u4e0b\uff01\u201d\u80e1\u6590\u4f9d\u8a00\u54bd\u843d\uff0c\u5fc3\u795e\u752b\u5b9a\uff0c\u60f3\u8d77\u9002\u624d\u7684\u60ca\u9669\uff0c\u72b9\u662f\u5fc3\u6709\u4f59\u6016\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u9669\uff0c\u597d\u9669\uff01\u201d\u89c1\u90a3\u300a\u836f\u738b\u795e\u7bc7\u300b\u6389\u5728\u5730\u4e0b\uff0c\u4e00\u9635\u79cb\u98ce\u8fc7\u53bb\uff0c\u5439\u5f97\u4e66\u9875\u4e0d\u4f4f\u7ffb\u8f6c\uff0c\u8bf4\u9053\uff1a\u201c\u53ef\u60dc\u6ca1\u6740\u4e86\u8fd9\u4e09\u4e2a\u6076\u8d3c\uff01 \u3000\u3000\u5e78\u597d\u4ed6\u4eec\u4e5f\u6ca1\u5c06\u4f60\u7684\u4e66\u62a2\u53bb\u3002\u4e8c\u59b9\uff0c\u5018\u82e5\u4f60\u624b\u4e0a\u6cbe\u4e86\u8fd9\u4e09\u79cd\u6bd2\u836f\uff0c\u90a3\u53ef\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u7a0b\u7075\u7d20\u67d4\u80a0\u5bf8\u65ad\uff0c\u771f\u60f3\u653e\u58f0\u75db\u54ed\uff0c\u53ef\u662f\u5374\u54ed\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u80e1\u6590\u89c1\u5979\u8138\u8272\u82cd\u767d\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u4e8c\u59b9\uff0c\u4f60\u7d2f\u5566\uff0c\u5feb\u6b47\u4e00\u6b47\u5427\uff01\u201d\u7a0b\u7075\u7d20\u542c\u5230\u4ed6\u6e29\u67d4\u4f53\u5e16\u7684\u8bf4\u8bdd\uff0c\u66f4\u662f\u8bf4\u4e0d\u51fa\u7684\u4f24\u5fc3\uff0c\u54fd\u54bd\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u2026\u2026\u201d \u3000\u3000\u80e1\u6590\u5ffd\u89c9\u53f3\u624b\u624b\u80cc\u4e0a\u7565\u611f\u9ebb\u75d2\uff0c\u6b63\u8981\u4f38\u5de6\u624b\u53bb\u6414\uff0c\u7a0b\u7075\u7d20\u4e00\u628a\u6293\u4f4f\u4e86\u4ed6\u5de6\u624b\u624b\u8155\uff0c\u98a4\u58f0\u9053\uff1a\u201c\u522b\u52a8\uff01\u201d\u80e1\u6590\u89c9\u5f97\u5979\u624b\u638c\u51b0\u51c9\uff0c\u5947\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u7a81\u7136\u95f4\u773c\u524d\u4e00\u9ed1\uff0c\u5495\u549a\u4e00\u58f0\uff0c\u4ef0\u5929\u6454\u5012\u3002 \u3000\u3000\u80e1\u6590\u8fd9\u4e00\u4ea4\u5012\u5728\u5730\u4e0b\uff0c\u518d\u4e5f\u52a8\u5f39\u4e0d\u5f97\uff0c\u53ef\u662f\u795e\u667a\u5374\u6781\u4e3a\u6e05\u660e\uff0c\u53ea\u89c9\u53f3\u624b\u624b\u80cc\u4e0a\u4e00\u9635\u9ebb\uff0c\u4e00\u9635\u75d2\uff0c\u8d8a\u6765\u8d8a\u662f\u5389\u5bb3\uff0c\u60ca\u95ee\uff1a\u201c\u6211\u4e5f\u4e2d\u4e86\u90a3\u4e09\u5927\u5267\u6bd2\u4e48\uff1f\u201d \u3000\u3000\u7a0b\u7075\u7d20\u6cea\u6c34\u5982\u73cd\u73e0\u65ad\u7ebf\u822c\u987a\u7740\u9762\u988a\u6d41\u4e0b\uff0c\u6251\u7c0c\u7c0c\u7684\u6ef4\u5728\u80e1\u6590\u8863\u4e0a\uff0c\u7f13\u7f13\u70b9\u4e86\u70b9\u5934\u3002\u80e1\u6590\u89c1\u6b64\u60c5\u666f\uff0c\u4e0d\u7981\u51c9\u4e86\u534a\u622a\uff0c\u6697\u60f3\uff1a\u201c\u5979\u8fd9\u822c\u96be\u8fc7\uff0c\u6211\u8eab\u4e0a\u6240\u4e2d\u5267\u6bd2\uff0c\u5b9a\u662f\u65e0\u6cd5\u6551\u6cbb\u4e86\u3002\u201d\u5239\u65f6\u4e4b\u95f4\uff0c\u5fc3\u5934\u6d8c\u4e0a\u4e86\u8bb8\u591a\u5f80\u4e8b\uff1a\u5546\u5bb6\u5821\u4e2d\u548c\u8d75\u534a\u5c71\u7ed3\u62dc\u3001\u4f5b\u5c71\u5317\u5e1d\u5e99\u4e2d\u7684\u60e8\u5267\u3001\u6f47\u6e58\u9053\u4e0a\u7ed3\u8bc6\u8881\u7d2b\u8863\u3001\u6d1e\u5ead\u6e56\u7554\u76f8\u9047\u7a0b\u7075\u7d20\uff0c\u4ee5\u53ca\u638c\u95e8\u4eba\u5927\u4f1a\u3001\u7ea2\u82b1\u4f1a\u7fa4\u96c4\u3001\u77f3\u4e07\u55d4\u2026\u2026\u8fd9\u4e00\u5207\u90fd\u662f\u8fc7\u53bb\u4e86\uff0c\u8fc7\u53bb\u4e86\u2026\u2026 \u3000\u3000\u4ed6\u53ea\u89c9\u5168\u8eab\u6e10\u6e10\u50f5\u786c\uff0c\u624b\u6307\u548c\u811a\u8dbe\u5bd2\u51b7\u5f7b\u9aa8\uff0c\u8bf4\u9053\uff1a\u201c\u4e8c\u59b9\uff0c\u751f\u6b7b\u6709\u547d\uff0c\u4f60\u4e5f\u4e0d\u5fc5\u96be\u8fc7\u3002\u53ea\u53ef\u60dc\u4f60\u4e00\u4e2a\u4eba\u5b64\u82e6\u4f36\u4ec3\uff0c\u505a\u5927\u54e5\u7684\u518d\u4e5f\u4e0d\u80fd\u7167\u6599\u4f60\u4e86\u3002\u90a3\u91d1\u9762\u4f5b\u82d7\u4eba\u51e4\u867d\u662f\u6211\u7684\u6740\u7236\u4e4b\u4ec7\uff0c\u4f46\u4ed6\u6177\u6168\u8c6a\u8fc8\uff0c\u5b9e\u662f\u4e2a\u94c1\u94ee\u94ee\u7684\u597d\u6c49\u5b50\u3002\u6211\u2026\u2026\u6211\u6b7b\u4e4b\u540e\uff0c\u4f60\u53bb\u6295\u5954\u4ed6\u5427\uff0c\u8981\u4e0d\u7136\u2026\u2026\u201d\u8bf4\u5230\u8fd9\u91cc\uff0c\u820c\u5934\u5927\u4e86\u8d77\u6765\uff0c\u8a00\u8bed\u6a21\u7cca\u4e0d\u6e05\uff0c\u7ec8\u4e8e\u518d\u4e5f\u8bf4\u4e0d\u51fa\u6765\u4e86\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u8dea\u5728\u4ed6\u8eab\u65c1\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u522b\u5bb3\u6015\uff0c\u4f60\u867d\u4e2d\u4e09\u79cd\u5267\u6bd2\uff0c\u4f46\u6211\u6709\u89e3\u6551\u4e4b\u6cd5\u3002\u4f60\u4e0d\u4f1a\u52a8\u5f39\uff0c\u4e0d\u4f1a\u8bf4\u8bdd\uff0c\u90a3\u662f\u670d\u4e86\u90a3\u9897\u9ebb\u836f\u836f\u4e38\u7684\u7f18\u6545\u3002\u201d\u80e1\u6590\u542c\u4e86\u5927\u559c\uff0c\u773c\u775b\u767b\u65f6\u53d1\u4eae\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u53d6\u51fa\u4e00\u679a\u91d1\u9488\uff0c\u523a\u7834\u4ed6\u53f3\u624b\u624b\u80cc\u4e0a\u7684\u8840\u7ba1\uff0c\u5c06\u53e3\u5c31\u4e0a\uff0c\u7528\u529b\u542e\u5438\u3002\u80e1\u6590\u5927\u5403\u4e00\u60ca\uff0c\u5fc3\u60f3\uff1a\u201c\u6bd2\u8840\u5438\u5165\u4f60\u53e3\uff0c\u4e0d\u662f\u8fde\u4f60\u4e5f\u6cbe\u4e0a\u4e86\u5267\u6bd2\u4e48\uff1f\u201d\u53ef\u662f\u56db\u80a2\u5bd2\u6c14\u9010\u6b65\u4e0a\u79fb\uff0c\u5168\u8eab\u518d\u4e5f\u4e0d\u542c\u4f7f\u5524\uff0c\u54ea\u91cc\u6323\u624e\u5f97\u4e86\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u5438\u4e00\u53e3\u6bd2\u8840\uff0c\u4fbf\u5410\u5728\u5730\u4e0b\uff0c\u82e5\u662f\u5bfb\u5e38\u6bd2\u836f\uff0c\u5979\u53ef\u4ee5\u7528\u624b\u6307\u6309\u637a\uff0c\u4ece\u7a7a\u5fc3\u91d1\u9488\u4e2d\u5438\u51fa\u6bd2\u8d28\uff0c\u4fbf\u5982\u66ff\u82d7\u4eba\u51e4\u6cbb\u773c\u4e00\u822c\uff0c\u4f46\u78a7\u8695\u6bd2\u86ca\u3001\u9e64\u9876\u7ea2\u3001\u5b54\u96c0\u80c6\u4e09\u5927\u5267\u6bd2\u5165\u4f53\uff0c\u53c8\u5c82\u662f\u6b64\u6cd5\u6240\u80fd\u594f\u6548\uff1f\u5979\u76f4\u5438\u4e86\u56db\u5341\u591a\u53e3\uff0c\u773c\u89c1\u5438\u51fa\u6765\u7684\u8840\u6db2\u5df2\u5168\u5448\u9c9c\u7ea2\u4e4b\u8272\uff0c\u8fd9\u624d\u653e\u5fc3\uff0c\u5401\u4e86\u4e00\u53e3\u957f\u6c14\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u548c\u6211\u90fd\u5f88\u53ef\u601c\u3002\u4f60\u5fc3\u4e2d\u559c\u6b22\u8881\u59d1\u5a18\uff0c\u90a3\u77e5\u9053\u5979\u5374\u51fa\u5bb6\u505a\u4e86\u5c3c\u59d1\u2026\u2026\u6211\u2026\u2026\u6211\u5fc3\u4e2d\u2026\u2026\u201d \u3000\u3000\u5979\u6162\u6162\u7ad9\u8d77\u8eab\u6765\uff0c\u67d4\u60c5\u65e0\u9650\u7684\u77a7\u7740\u80e1\u6590\uff0c\u4ece\u836f\u56ca\u4e2d\u53d6\u51fa\u4e24\u79cd\u836f\u7c89\uff0c\u66ff\u4ed6\u6577\u5728\u624b\u80cc\uff0c\u53c8\u53d6\u51fa\u4e00\u7c92\u9ec4\u8272\u836f\u4e38\uff0c\u585e\u5728\u4ed6\u53e3\u4e2d\uff0c\u4f4e\u4f4e\u5730\u9053\uff1a\u201c\u6211\u5e08\u7236\u8bf4\u4e2d\u4e86\u8fd9\u4e09\u79cd\u5267\u6bd2\uff0c\u65e0\u836f\u53ef\u6cbb\uff0c\u56e0\u4e3a\u4ed6\u53ea\u9053\u4e16\u4e0a\u6ca1\u6709\u4e00\u4e2a\u533b\u751f\uff0c\u80af\u4e0d\u8981\u81ea\u5df1\u7684\u6027\u547d\u6765\u6551\u6d3b\u75c5\u4eba\u3002\u5927\u54e5\uff0c\u4ed6\u4e0d\u77e5\u6211\u2026\u2026\u6211\u4f1a\u5f85\u4f60\u8fd9\u6837\u2026\u2026\u201d \u3000\u3000\u80e1\u6590\u53ea\u60f3\u5f20\u53e3\u5927\u53eb\uff1a\u201c\u6211\u4e0d\u8981\u4f60\u8fd9\u6837\uff0c\u4e0d\u8981\u4f60\u8fd9\u6837\uff01\u201d\u4f46\u9664\u4e86\u773c\u5149\u4e2d\u6d41\u9732\u51fa\u53cd\u5bf9\u7684\u795e\u8272\u4e4b\u5916\uff0c\u5b9e\u5728\u65e0\u6cd5\u8868\u793a\u3002 \u3000\u3000\u7a0b\u7075\u7d20\u6253\u5f00\u5305\u88f9\uff0c\u53d6\u51fa\u5706\u6027\u9001\u7ed9\u5979\u7684\u90a3\u53ea\u7389\u51e4\uff0c\u51c4\u7136\u77a7\u4e86\u4e00\u4f1a\uff0c\u7528\u4e00\u5757\u624b\u5e15\u5305\u4e86\uff0c\u653e\u5728\u80e1\u6590\u6000\u91cc\u3002\u518d\u53d6\u51fa\u4e00\u679d\u8721\u70db\uff0c\u63d2\u5728\u795e\u50cf\u524d\u7684\u70db\u53f0\u4e4b\u4e0a\uff0c\u4e00\u8f6c\u5ff5\u95f4\uff0c\u4ece\u5305\u4e2d\u53e6\u53d6\u4e00\u679d\u8f83\u7ec6\u7684\u8721\u70db\uff0c\u62d7\u53bb\u534a\u622a\uff0c\u6643\u706b\u647a\u70b9\u71c3\u4e86\uff0c\u653e\u5728\u540e\u9662\u5929\u4e95\u4e2d\uff0c\u8ba9\u8721\u70db\u70e7\u4e86\u4e00\u4f1a\uff0c\u518d\u53d6\u56de\u6765\u653e\u5728\u70db\u53f0\u4e4b\u65c1\uff0c\u53e6\u884c\u53d6\u4e00\u679d\u65b0\u70db\u63d2\u4e0a\u70db\u53f0\u3002 \u3000\u3000\u80e1\u6590\u77a7\u7740\u5979\u8fd9\u822c\u7ec6\u5fc3\u5e03\u7f6e\uff0c\u4e0d\u77e5\u662f\u4f55\u7528\u610f\uff0c\u53ea\u542c\u5979\u9053\uff1a\u201c\u5927\u54e5\uff0c\u6709\u4e00\u4ef6\u4e8b\u6211\u672c\u6765\u4e0d\u60f3\u8ddf\u4f60\u8bf4\uff0c\u4ee5\u514d\u60f9\u8d77\u4f60\u4f24\u5fc3\u3002\u73b0\u4e0b\u54b1\u4eec\u8981\u5206\u624b\u4e86\uff0c\u4e0d\u5f97\u4e0d\u8bf4\u3002\u5728\u638c\u95e8\u4eba\u5927\u4f1a\u4e4b\u4e2d\uff0c\u6211\u90a3\u72e0\u6bd2\u7684\u5e08\u53d4\u548c\u7530\u5f52\u519c\u76f8\u9047\u4e4b\u65f6\uff0c\u4f60\u53ef\u77a7\u51fa\u8e4a\u8df7\u6765\u4e48\uff1f\u4ed6\u4e8c\u4eba\u662f\u65e9\u5c31\u76f8\u8bc6\u7684\u3002\u7530\u5f52\u519c\u7528\u6765\u6bd2\u778e\u82d7\u5927\u4fa0\u773c\u775b\u7684\u65ad\u80a0\u8349\uff0c\u5b9a\u662f\u77f3\u4e07\u55d4\u7ed9\u7684\u3002\u4f60\u7239\u7239\u5988\u5988\u6240\u4ee5\u4e2d\u6bd2\uff0c\u90a3\u6bd2\u836f\u591a\u534a\u4e5f\u662f\u77f3\u4e07\u55d4\u914d\u5236\u7684\u3002\u201d \u3000\u3000\u80e1\u6590\u5fc3\u4e2d\u4e00\u51db\uff0c\u53ea\u60f3\u5927\u53eb\u4e00\u58f0\uff1a\u201c\u4e0d\u9519\uff01\u201d \u3000\u3000\u7a0b\u7075\u7d20\u9053\uff1a\u201c\u4f60\u7239\u7239\u5988\u5988\u53bb\u4e16\u4e4b\u65f6\uff0c\u6211\u5c1a\u672a\u51fa\u751f\uff0c\u6211\u90a3\u51e0\u4e2a\u5e08\u5144\u5e08\u59ca\uff0c\u4e5f\u8fd8\u5e74\u7eaa\u5c1a\u5c0f\uff0c\u672a\u66fe\u6295\u5e08\u5b66\u827a\u3002\u90a3\u65f6\u5019\u5f53\u4e16\u64c5\u4e8e\u7528\u6bd2\u4e4b\u4eba\uff0c\u53ea\u6709\u5148\u5e08\u548c\u77f3\u4e07\u55d4\u4e8c\u4eba\u3002\u82d7\u5927\u4fa0\u7591\u5fc3\u6bd2\u836f\u662f\u6211\u5e08\u7236\u7ed9\u7684\uff0c\u56e0\u4e4b\u548c\u4ed6\u5931\u548c\u52a8\u624b\uff0c\u6211\u5e08\u7236\u65e2\u7136\u8bf4\u4e0d\u662f\uff0c\u5f53\u7136\u4e0d\u662f\u4e86\u3002\u6211\u867d\u7591\u5fc3\u8fd9\u4e2a\u5e08\u53d4\uff0c\u53ef\u662f\u5e76\u65e0\u4f50\u8bc1\uff0c\u672c\u6765\u60f3\u6162\u6162\u67e5\u660e\u767d\u4e86\uff0c\u5982\u679c\u662f\u4ed6\uff0c\u518d\u8bbe\u6cd5\u66ff\u4f60\u62a5\u4ec7\u3002\u4eca\u65e5\u4e8b\u5df2\u5982\u6b64\uff0c\u4e0d\u7ba1\u600e\u6837\uff0c\u603b\u4e4b\u662f\u8981\u6740\u4e86\u4ed6\u2026\u2026\u201d\u8bf4\u5230\u8fd9\u91cc\uff0c\u4f53\u5185\u6bd2\u6027\u53d1\u4f5c\uff0c\u8eab\u5b50\u6447\u6643\u4e86\u51e0\u4e0b\uff0c\u6454\u5728\u80e1\u6590\u8eab\u8fb9\u3002 \u3000\u3000\u80e1\u6590\u89c1\u5979\u6162\u6162\u5408\u4e0a\u773c\u775b\uff0c\u53e3\u89d2\u8fb9\u6d41\u51fa\u4e00\u6761\u8840\u4e1d\uff0c\u771f\u5982\u662f\u4e07\u628a\u94a2\u9525\u5728\u5fc3\u4e2d\u94bb\u523a\u4e00\u822c\uff0c\u5f20\u53e3\u5927\u53eb\uff1a\u201c\u4e8c\u59b9\uff0c\u4e8c\u59b9\uff01\u201d\u53ef\u662f\u4fbf\u5982\u6df1\u591c\u68a6\u9b47\uff0c\u4e0d\u8bba\u5982\u4f55\u5927\u547c\u5927\u53f7\uff0c\u603b\u662f\u558a\u4e0d\u51fa\u534a\u70b9\u58f0\u606f\uff0c\u5fc3\u91cc\u867d\u7136\u660e\u767d\uff0c\u5374\u662f\u4e00\u6839\u5c0f\u6307\u5934\u513f\u4e5f\u8f6c\u52a8\u4e0d\u5f97\u3002 \u3000\u3000\u4fbf\u662f\u8fd9\u6837\uff0c\u80e1\u6590\u5e76\u80a9\u548c\u7a0b\u7075\u7d20\u7684\u5c38\u8eab\u8eba\u5728\u5730\u4e0b\uff0c\u4ece\u4e0a\u5348\u6328\u5230\u4e0b\u5348\uff0c\u53c8\u4ece\u4e0b\u5348\u6328\u5230\u9ec4\u660f\u3002\u8981\u77e5\u90a3\u78a7\u8695\u6bd2\u86ca\u3001\u9e64\u9876\u7ea2\u3001\u5b54\u96c0\u80c6\u4e09\u5927\u5267\u6bd2\u7684\u6bd2\u6027\u4f55\u7b49\u5389\u5bb3\uff0c\u867d\u7136\u7a0b\u7075\u7d20\u66ff\u4ed6\u5438\u51fa\u4e86\u6bd2\u8840\uff0c\u4f46\u6bd2\u836f\u5df2\u4fb5\u5165\u8fc7\u8eab\u4f53\uff0c\u5168\u8eab\u808c\u8089\u50f5\u786c\uff0c\u975e\u7b49\u4e00\u65e5\u4e00\u591c\uff0c\u4e0d\u80fd\u52a8\u5f39\u3002\u8fd9\u51e0\u4e2a\u65f6\u8fb0\u4e2d\u4ed6\u5fc3\u4e2d\u4e4b\u82e6\uff0c\u771f\u975e\u5e38\u4eba\u6240\u80fd\u60f3\u8c61\u3002 \u3000\u3000\u773c\u89c1\u5929\u8272\u6e10\u6e10\u9ed1\u4e86\u4e0b\u6765\uff0c\u4ed6\u8eab\u5b50\u5140\u81ea\u4e0d\u80fd\u8f6c\u52a8\uff0c\u53ea\u77e5\u7a0b\u7075\u7d20\u8eba\u5728\u81ea\u5df1\u8eab\u65c1\uff0c\u53ef\u662f\u60f3\u8f6c\u5934\u77a7\u5979\u4e00\u773c\uff0c\u5374\u662f\u4e0d\u80fd\u3002 \u3000\u3000\u53c8\u8fc7\u4e86\u4e24\u4e2a\u591a\u65f6\u8fb0\uff0c\u53ea\u542c\u5f97\u8fdc\u5904\u6811\u6797\u4e2d\u4f20\u6765\u4e00\u58f0\u58f0\u67ad\u9e23\uff0c\u7a81\u7136\u4e4b\u95f4\uff0c\u51e0\u4e2a\u4eba\u7684\u811a\u6b65\u58f0\u6084\u6084\u5230\u4e86\u5e99\u5916\u3002\u53ea\u542c\u5f97\u4e00\u4eba\u4f4e\u58f0\u9053\uff1a\u201c\u859b\u9e4a\uff0c\u4f60\u8fdb\u53bb\u77a7\u77a7\u3002\u201d\u6b63\u662f\u77f3\u4e07\u55d4\u7684\u58f0\u97f3\u3002 \u3000\u3000\u80e1\u6590\u6697\u53eb\uff1a\u201c\u7f62\u4e86\uff0c\u7f62\u4e86\uff01\u6211\u4e00\u52a8\u4e5f\u4e0d\u80fd\u52a8\uff0c\u53ea\u6709\u9759\u5f85\u5bb0\u5272\u7684\u4efd\u513f\u3002\u4e8c\u59b9\u554a\u4e8c\u59b9\uff0c\u4f60\u4e3a\u4e86\u6551\u6211\u6027\u547d\uff0c\u7ed9\u6211\u670d\u4e0b\u9ebb\u836f\uff0c\u53ef\u662f\u836f\u6027\u592a\u70c8\uff0c\u4e0d\u77e5\u4f55\u65f6\u65b9\u6d88\uff0c\u6b64\u523b\u654c\u4eba\u8f6c\u5934\u53c8\u6765\uff0c\u6211\u8fd8\u662f\u8981\u8ddf\u4f60\u540c\u8d74\u9ec4\u6cc9\u3002\u867d\u7136\u6b7b\u4e0d\u8db3\u60dc\uff0c\u53ef\u662f\u8fd9\u756a\u5927\u4ec7\uff0c\u5374\u662f\u518d\u96be\u5f97\u62a5\u4e86\u3002\u201d\u5176\u5b9e\u6b64\u65f6\u9ebb\u836f\u7684\u836f\u6027\u65e9\u9000\uff0c\u4ed6\u6240\u4ee5\u808c\u8089\u50f5\u786c\u6709\u5982\u6b7b\u5c38\uff0c\u5168\u662f\u4e09\u5927\u5267\u6bd2\u4e4b\u6545\u3002 \u3000\u3000\u53ea\u542c\u5f97\u859b\u9e4a\u8f7b\u8f7b\u95ea\u8eab\u8fdb\u6765\uff0c\u8eb2\u5728\u95e8\u540e\uff0c\u5411\u5185\u5f20\u671b\u3002\u5979\u4e0d\u6562\u6643\u4eae\u706b\u647a\uff0c\u9ed1\u6697\u4e2d\u5374\u53c8\u77a7\u4e0d\u89c1\u4ec0\u4e48\uff0c\u4fa7\u8033\u503e\u542c\uff0c\u4f46\u89c9\u5bc2\u65e0\u58f0\u606f\uff0c\u4fbf\u56de\u51fa\u5e99\u95e8\uff0c\u5411\u77f3\u4e07\u55d4\u8bf4\u4e86\u3002 \u3000\u3000\u77f3\u4e07\u55d4\u70b9\u5934\u9053\uff1a\u201c\u90a3\u5c0f\u5b50\u624b\u80cc\u4e0a\u7ed9\u6211\u5f39\u4e0a\u4e86\u4e09\u5927\u5267\u6bd2\uff0c\u8fd9\u5f53\u513f\u4e0d\u662f\u547d\u8d74\u9634\u66f9\uff0c\u4fbf\u662f\u4e00\u6761\u624b\u81c2\u9f50\u80a9\u5207\u4e86\u4e0b\u6765\u3002\u5269\u4e0b\u90a3\u5c0f\u4e2b\u5934\u4e00\u4eba\uff0c\u4f55\u8db3\u9053\u54c9\uff01\u5c31\u53ea\u6015\u4e24\u4e2a\u5c0f\u9b3c\u65e9\u5df2\u9003\u5f97\u8fdc\u4e86\u3002\u201d\u4ed6\u8bdd\u662f\u8fd9\u4e48\u8bf4\uff0c\u4ecd\u662f\u4e0d\u6562\u6258\u5927\uff0c\u53d6\u51fa\u864e\u6491\u545b\u5577\u5577\u7684\u6447\u52a8\uff0c\u62a4\u4f4f\u524d\u80f8\uff0c\u8fd9\u624d\u7f13\u6b65\u8d70\u8fdb\u5e99\u95e8\u3002 \u3000\u3000\u8d70\u5230\u6bbf\u4e0a\uff0c\u9ed1\u6697\u4e2d\u53ea\u89c1\u4e24\u4e2a\u4eba\u8eba\u5728\u5730\u4e0b\uff0c\u4ed6\u4e0d\u6562\u4fbf\u6b64\u8d70\u8fd1\uff0c\u62fe\u8d77\u4e00\u7c92\u77f3\u5b50\uff0c\u5411\u4e24\u4eba\u6295\u53bb\uff0c\u53ea\u89c1\u4e24\u4eba\u4ecd\u662f\u4e00\u52a8\u4e0d\u52a8\uff0c\u5f53\u4e0b\u6643\u4eae\u706b\u647a\u4e00\u770b\uff0c\u89c1\u5730\u4e0b\u90a3\u4e24\u4eba\u6b63\u662f\u80e1\u6590\u548c\u7a0b\u7075\u7d20\u3002\u773c\u89c1\u4e24\u4eba\u5168\u8eab\u50f5\u76f4\uff0c\u663e\u5df2\u6b7b\u53bb\u591a\u65f6\u3002\u77f3\u4e07\u55d4\u5927\u559c\uff0c\u4e00\u63a2\u7a0b\u7075\u7d20\u9f3b\u606f\uff0c\u65e9\u5df2\u989c\u9762\u51b0\u51b7\uff0c\u6ca1\u4e86\u547c\u5438\uff0c\u518d\u4f38\u624b\u53bb\u63a2\u80e1\u6590\u9f3b\u606f\u65f6\uff0c\u80e1\u6590\u53cc\u76ee\u7d27\u95ed\uff0c\u51dd\u4f4f\u547c\u5438\u3002 \u3000\u3000\u77f3\u4e07\u55d4\u4e3a\u4eba\u4e5f\u5f53\u771f\u90d1\u91cd\uff0c\u53ea\u89c9\u4ed6\u989c\u9762\u5fae\u6e29\uff0c\u5e76\u672a\u6b7b\u900f\uff0c\u968f\u624b\u53d6\u51fa\u4e00\u6839\u91d1\u9488\uff0c\u5728\u7a0b\u80e1\u4e24\u4eba\u624b\u5fc3\u4e2d\u5404\u81ea\u523a\u4e86\u4e00\u4e0b\uff0c\u4ed6\u4eec\u82e5\u662f\u4e54\u88c5\u5047\u6b7b\uff0c\u8fd9\u4e48\u4e00\u523a\uff0c\u624b\u638c\u975e\u98a4\u52a8\u4e0d\u53ef\u3002\u7a0b\u7075\u7d20\u771f\u7684\u5df2\u6b7b\uff0c\u80e1\u6590\u808c\u8089\u5c1a\u50f5\uff0c\u91d1\u9488\u867d\u523a\u5165\u4ed6\u638c\u5fc3\u77e5\u89c9\u505a\u4e3a\u9510\u654f\u4e4b\u5904\uff0c\u4ea6\u662f\u7edd\u65e0\u53cd\u5e94\u3002 \u3000\u3000\u6155\u5bb9\u666f\u5cb3\u6068\u6068\u7684\u9053\uff1a\u201c\u8fd9\u4e2b\u5934\u542e\u5438\u60c5\u90ce\u624b\u80cc\u7684\u6bd2\u836f\uff0c\u5c82\u4e0d\u77e5\u60c5\u90ce\u6ca1\u6551\u6d3b\uff0c\u8fde\u5e26\u9001\u4e86\u81ea\u5df1\u7684\u6027\u547d\u3002\u201d \u3000\u3000\u77f3\u4e07\u55d4\u6025\u4e8e\u627e\u90a3\u518c\u300a\u836f\u738b\u795e\u7bc7\u300b\uff0c\u773c\u89c1\u706b\u647a\u5c06\u8981\u70e7\u5c3d\uff0c\u4fbf\u51d1\u5230\u70db\u53f0\u4e0a\u53bb\u70b9\u8721\u70db\u3002\u706b\u7130\u521a\u548c\u70db\u82af\u76f8\u78b0\uff0c\u5fc3\u5ff5\u4e00\u52a8\uff1a\u201c\u8fd9\u679d\u8721\u70db\u6ca1\u70b9\u8fc7\uff0c\u8bf4\u4e0d\u5b9a\u6709\u4ec0\u4e48\u53e4\u602a\u3002\u201d\u89c1\u70db\u53f0\u4e0b\u653e\u7740\u534a\u622a\u70b9\u8fc7\u7684\u8721\u70db\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u534a\u622a\u8721\u70db\u662f\u70b9\u8fc7\u7684\uff0c\u5b9a\u7136\u65e0\u59a8\u3002\u201d\u4e8e\u662f\u62d4\u4e0b\u70db\u53f0\u4e0a\u90a3\u679d\u6ca1\u70b9\u8fc7\u7684\u8721\u70db\uff0c\u6362\u4e0a\u534a\u622a\u6b8b\u70db\uff0c\u7528\u706b\u647a\u70b9\u71c3\u4e86\u3002 \u3000\u3000\u70db\u5149\u4e00\u4eae\uff0c\u4e09\u4eba\u540c\u65f6\u770b\u5230\u4e86\u5730\u4e0b\u7684\u300a\u836f\u738b\u795e\u7bc7\u300b\uff0c\u9f50\u58f0\u559c\u547c\u3002\u77f3\u4e07\u55d4\u6495\u4e0b\u4e00\u5757\u8863\u895f\uff0c\u57ab\u5728\u624b\u4e0a\uff0c\u8fd9\u624d\u9694\u7740\u5e03\u6599\u5c06\u518c\u5b50\u62fe\u8d77\u3002\u51d1\u5230\u70db\u706b\u65c1\u7ffb\u4e66\u4e00\u770b\uff0c\u53ea\u89c1\u5bc6\u5bc6\u5199\u7740\u4e00\u884c\u884c\u7684\u8747\u5934\u5c0f\u6977\uff0c\u679c\u7136\u662f\u5404\u79cd\u533b\u672f\u548c\u836f\u6027\uff0c\u4f46\u7565\u4e00\u68c0\u89c6\uff0c\u5176\u4e2d\u6cbb\u75c5\u6551\u4f24\u7684\u533b\u9053\u5360\u4e86\u4e5d\u6210\u4ee5\u4e0a\u3002\u8bf4\u5230\u6bd2\u836f\u4e4b\u65f6\uff0c\u8981\u65e8\u4e5f\u9610\u8ff0\u89e3\u6bd2\u6551\u6cbb\uff0c\u81f3\u4e8e\u5982\u4f55\u70bc\u6bd2\u65bd\u6bd2\uff0c\u4ee5\u53ca\u8bf8\u822c\u79cd\u690d\u6bd2\u8349\u3001\u57f9\u517b\u6bd2\u866b\u4e4b\u6cd5\uff0c\u5374\u8bf4\u7684\u6781\u4e3a\u7b80\u7565\u3002\u539f\u6765\u65e0\u55d4\u5927\u5e08\u665a\u5e74\u6df1\u6094\u4e00\u751f\u7528\u6bd2\u592a\u591a\uff0c\u4ee5\u81f4\u5728\u6c5f\u6e56\u4e0a\u5f97\u4e86\u4e2a\u201c\u6bd2\u624b\u836f\u738b\u201d\u7684\u540d\u53f7\uff0c\u662f\u4ee5\u4f20\u7ed9\u5f1f\u5b50\u7684\u9057\u4e66\uff0c\u540d\u4e3a\u300a\u836f\u738b\u795e\u7bc7\u300b\uff0c\u4e43\u662f\u4e00\u90e8\u6d4e\u4e16\u6551\u4eba\u7684\u533b\u4e66\u3002 \u3000\u3000\u77f3\u4e07\u55d4\u3001\u6155\u5bb9\u666f\u5cb3\u3001\u859b\u9e4a\u4e09\u4eba\u5904\u5fc3\u79ef\u8651\u60f3\u8981\u52ab\u593a\u5230\u624b\u7684\uff0c\u539f\u60f3\u662f\u4e00\u90e8\u5305\u7f57\u4e07\u6709\u3001\u795e\u5947\u5965\u5999\u7684\u201c\u6bd2\u7ecf\u201d\uff0c\u6b64\u65f6\u4e00\u770b\uff0c\u7adf\u662f\u4e00\u90e8\u533b\u4e66\uff0c\u7eb5\u7136\u5176\u4e2d\u6240\u8f7d\u533b\u672f\u7cbe\u6df1\uff0c\u4e8e\u4ed6\u5374\u662f\u5168\u65e0\u7528\u5904\uff0c\u77f3\u4e07\u55d4\u81ea\u662f\u5927\u5931\u6240\u671b\u3002 \u3000\u3000\u4ed6\u51dd\u601d\u7247\u523b\uff0c\u5bf9\u859b\u9e4a\u9053\uff1a\u201c\u4f60\u641c\u641c\u90a3\u6b7b\u4e2b\u5934\u7684\u8eab\u8fb9\uff0c\u662f\u5426\u53e6\u6709\u522b\u7684\u4e66\u518c\u3002\u8fd9\u4e00\u90e8\u53ea\u662f\u533b\u4e66\uff0c\u6ca1\u4ec0\u4e48\u7528\u3002\u201d\u8bf4\u7740\u968f\u624b\u6254\u5728\u795e\u53f0\u4e4b\u4e0a\u3002\u859b\u9e4a\u4e00\u641c\u7a0b\u7075\u7d20\u7684\u8863\u886b\u548c\u5305\u88f9\uff0c\u9053\uff1a\u201c\u6ca1\u6709\u4e86\u3002\u201d \u3000\u3000\u6155\u5bb9\u666f\u5cb3\u731b\u5730\u60f3\u8d77\u4e00\u4e8b\uff0c\u9053\uff1a\u201c\u6211\u90a3\u5e08\u7236\u5584\u5199\u9690\u5f62\u5b57\u4f53\uff0c\u83ab\u975e\u2026\u2026\u201d\u8fd9\u53e5\u8bdd\u4e00\u51fa\u53e3\uff0c\u767b\u65f6\u597d\u751f\u540e\u6094\uff0c\u6697\u60f3\uff1a\u201c\u8be5\u6b7b\uff01\u8be5\u6b7b\uff01\u6211\u4f55\u5fc5\u8bf4\u4e86\u51fa\u6765\uff1f\u4efb\u4ed6\u4ee5\u4e3a\u6b64\u4e66\u65e0\u7528\uff0c\u6211\u6361\u56de\u53bb\u7ec6\u7ec6\u63a2\u7d22\uff0c\u5c82\u4e0d\u662f\u597d\uff1f\u201d\u4f46\u77f3\u4e07\u55d4\u4f55\u7b49\u673a\u4f36\uff0c\u7acb\u65f6\u9192\u609f\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff01\u201d\u53c8\u62e3\u8d77\u90a3\u90e8\u300a\u836f\u738b\u795e\u7bc7\u300b\u3002 \u3000\u3000\u4e00\u8f6c\u8eab\u95f4\uff0c\u53ea\u89c1\u6155\u5bb9\u666f\u5cb3\u548c\u859b\u9e4a\u53cc\u819d\u6e10\u6e10\u5f2f\u66f2\uff0c\u8eab\u5b50\u8f6f\u4e86\u4e0b\u6765\uff0c\u8138\u4e0a\u4f3c\u7b11\u975e\u7b11\uff0c\u795e\u60c5\u6781\u662f\u8be1\u5f02\u3002\u77f3\u4e07\u55d4\u5927\u5403\u4e00\u60ca\uff0c\u53eb\u9053\uff1a\u201c\u600e\u4e48\u5566\uff1f\u4e03\u5fc3\u6d77\u68e0\uff0c\u4e03\u5fc3\u6d77\u68e0\uff1f\u96be\u9053\u6b7b\u4e2b\u5934\u79cd\u6210\u4e86\u4e03\u5fc3\u6d77\u68e0\uff1f\u8fd9\u2026\u2026\u8fd9\u8721\u70db\u2026\u2026\u201d \u3000\u3000\u8111\u6d77\u4e2d\u72b9\u5982\u7535\u5149\u4e00\u95ea\uff0c\u60f3\u8d77\u4e86\u5c11\u5e74\u65f6\u548c\u65e0\u55d4\u540c\u95e8\u5b66\u827a\u65f6\u7684\u60c5\u666f\u3002\u6709\u4e00\u5929\u665a\u4e0a\uff0c\u5e08\u7236\u8bb2\u5230\u5929\u4e0b\u7684\u6bd2\u7269\u4e4b\u738b\uff0c\u4ed6\u8bf4\u9e64\u9876\u7ea2\u3001\u5b54\u96c0\u80c6\u3001\u58a8\u86db\u6c41\u3001\u8150\u8089\u818f\u3001\u5f69\u8679\u83cc\u3001\u78a7\u8695\u5375\u3001\u876e\u86c7\u6d8e\u3001\u756a\u6728\u9cd6\u3001\u767d\u85af\u82bd\u7b49\u7b49\uff0c\u90fd\u8fd8\u4e0d\u662f\u6700\u5389\u5bb3\u7684\u6bd2\u7269\uff0c\u6700\u53ef\u6015\u7684\u662f\u4e03\u5fc3\u6d77\u68e0\u3002\u8fd9\u6bd2\u7269\u65e0\u8272\u65e0\u81ed\uff0c\u65e0\u5f71\u65e0\u8e2a\uff0c\u518d\u7cbe\u660e\u7ec6\u5fc3\u7684\u4eba\u4e5f\u9632\u5907\u4e0d\u4e86\uff0c\u4e0d\u77e5\u4e0d\u89c9\u4e4b\u95f4\uff0c\u5df2\u662f\u4e2d\u6bd2\u800c\u6b7b\u3002\u6b7b\u8005\u8138\u4e0a\u59cb\u7ec8\u5e26\u7740\u5fae\u7b11\uff0c\u4f3c\u4e4e\u5341\u5206\u5e73\u5b89\u559c\u4e50\u3002\u5e08\u7236\u66fe\u4ece\u6d77\u5916\u5f97\u4e86\u8fd9\u4e03\u5fc3\u6d77\u68e0\u7684\u79cd\u5b50\uff0c\u53ef\u662f\u4e0d\u8bba\u7528\u4ec0\u4e48\u65b9\u6cd5\uff0c\u90fd\u662f\u79cd\u5b83\u4e0d\u6d3b\u3002\u90a3\u5929\u665a\u4e0a\uff0c\u5e08\u5144\u548c\u4ed6\u81ea\u5df1\u90fd\u5411\u5e08\u7236\u8ba8\u4e86\u4e5d\u7c92\u4e03\u5fc3\u6d77\u68e0\u7684\u79cd\u5b50\u3002\u5e08\u7236\u5fae\u7b11\u9053\uff1a\u201c\u5e78\u597d\u8fd9\u4e03\u5fc3\u6d77\u68e0\u96be\u4ee5\u57f9\u690d\uff0c\u5426\u5219\u4e16\u4e0a\u8fd8\u6709\u8c01\u80fd\u5f97\u5e73\u5b89\u3002\u201d \u3000\u3000\u77a7\u6155\u5bb9\u666f\u5cb3\u548c\u859b\u9e4a\u7684\u60c5\u72b6\uff0c\u6b63\u662f\u4e2d\u4e86\u4e03\u5fc3\u6d77\u68e0\u4e4b\u6bd2\uff0c\u4ed6\u7acb\u5373\u5c4f\u4f4f\u547c\u5438\uff0c\u4f38\u624b\u6309\u4f4f\u53e3\u9f3b\uff0c\u6b63\u60f3\u7ec6\u5bdf\u6bd2\u4ece\u4f55\u6765\uff0c\u7a81\u7136\u95f4\u773c\u524d\u4e00\u9ed1\uff0c\u518d\u4e5f\u77a7\u4e0d\u89c1\u4ec0\u4e48\u3002\u4e00\u77ac\u4e4b\u95f4\uff0c\u4ed6\u8fd8\u9053\u662f\u8721\u70db\u7184\u706d\uff0c\u4f46\u968f\u5373\u53d1\u89c9\uff0c\u5374\u662f\u81ea\u5df1\u53cc\u773c\u9661\u7136\u95f4\u5931\u660e\u3002 \u3000\u3000\u201c\u4e03\u5fc3\u6d77\u68e0\uff01\u4e03\u5fc3\u6d77\u68e0\uff01\u201d\u4ed6\u77e5\u9053\u5e78\u4e8f\u5728\u8fdb\u5e99\u4e4b\u524d\uff0c\u53e3\u4e2d\u5148\u542b\u4e86\u5316\u89e3\u767e\u6bd2\u7684\u4e39\u836f\uff0c\u4e03\u5fc3\u6d77\u68e0\u7684\u6bd2\u6027\u4e00\u65f6\u624d\u4e0d\u81f4\u4fb5\u5165\u810f\u8151\uff0c\u4f46\u53cc\u76ee\u5df1\u7136\u62b5\u53d7\u4e0d\u4f4f\uff0c\u7adf\u81ea\u76f2\u4e86\u3002 \u3000\u3000\u80e1\u6590\u4e8b\u5148\u5374\u7ed9\u7a0b\u7075\u7d20\u5582\u4e86\u62b5\u5fa1\u4e03\u5fc3\u6d77\u68e0\u6bd2\u6027\u7684\u89e3\u836f\uff0c\u53cc\u76ee\u65e0\u6059\uff0c\u4e00\u5207\u770b\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u773c\u89c1\u6155\u5bb9\u666f\u5cb3\u548c\u859b\u9e4a\u6162\u6162\u8f6f\u5012\uff0c\u773c\u89c1\u77f3\u4e07\u55d4\u53cc\u624b\u5728\u7a7a\u4e2d\u4e71\u6293\u4e71\u6251\uff0c\u5927\u53eb\uff1a\u201c\u4e03\u5fc3\u6d77\u68e0\uff0c\u4e03\u5fc3\u6d77\u68e0\uff01\u201d\u51b2\u51fa\u5e99\u53bb\u3002\u53ea\u542c\u4ed6\u51c4\u5389\u7684\u53eb\u58f0\u6e10\u6e10\u8fdc\u53bb\uff0c\u9759\u591c\u4e4b\u4e2d\uff0c\u867d\u7136\u9694\u4e86\u826f\u4e45\uff0c\u8fd8\u542c\u5f97\u4ed6\u7684\u53eb\u58f0\u9690\u9690\u4ece\u65f7\u91ce\u95f4\u4f20\u6765\uff0c\u6709\u5982\u53d1\u72c2\u7684\u91ce\u517d\u547c\u53eb\u4e00\u822c\uff1a\u201c\u4e03\u5fc3\u6d77\u68e0\uff01\u4e03\u5fc3\u6d77\u68e0\uff01\u201d \u3000\u3000\u80e1\u6590\u8eab\u65c1\u8eba\u7740\u4e09\u5177\u5c38\u9996\uff0c\u4e00\u4e2a\u662f\u4ed6\u4e49\u7ed3\u91d1\u5170\u7684\u5c0f\u59b9\u5b50\u7a0b\u7075\u7d20\uff0c\u4e24\u4e2a\u662f\u4ed6\u4e49\u59b9\u7684\u5bf9\u5934\u3001\u80cc\u53db\u5e08\u95e8\u7684\u5e08\u5144\u5e08\u59ca\u3002\u7834\u5e99\u4e2d\u4e00\u679d\u9eef\u6de1\u7684\u8721\u70db\uff0c\u968f\u98ce\u6447\u66f3\uff0c\u5ffd\u660e\u5ffd\u6697\uff0c\u4ed6\u8eab\u4e0a\u8bf4\u4e0d\u51fa\u7684\u5bd2\u51b7\uff0c\u5fc3\u4e2d\u8bf4\u4e0d\u51fa\u7684\u51c4\u51c9\u3002 \u3000\u3000\u7ec8\u4e8e\u8721\u70db\u70b9\u5230\u4e86\u5c3d\u5934\uff0c\u5ffd\u5730\u4e00\u4eae\uff0c\u706b\u7130\u5410\u7ea2\uff0c\u4e00\u58f0\u8f7b\u54cd\uff0c\u7834\u5e99\u4e2d\u6f06\u9ed1\u4e00\u56e2\u3002 \u3000\u3000\u80e1\u6590\u5fc3\u60f3\uff1a\u201c\u6211\u4e8c\u59b9\u4fbf\u5982\u8fd9\u8721\u70db\u4e00\u6837\uff0c\u70b9\u5230\u4e86\u5c3d\u5934\uff0c\u518d\u4e5f\u4e0d\u80fd\u53d1\u51fa\u5149\u4eae\u4e86\u3002\u5979\u4e00\u5207\u5168\u7b97\u5230\u4e86\uff0c\u6599\u5f97\u77f3\u4e07\u55d4\u4ed6\u4eec\u4e00\u5b9a\u8fd8\u8981\u518d\u6765\uff0c\u6599\u5230\u4ed6\u5c0f\u5fc3\u8c28\u614e\u4e0d\u6562\u70b9\u65b0\u8721\u70db\uff0c\u4fbf\u5c06\u90a3\u679a\u6df7\u6709\u4e03\u5fc3\u6d77\u68e0\u82b1\u7c89\u7684\u8721\u70db\u5148\u884c\u62d7\u53bb\u534a\u622a\uff0c\u8bf1\u4ed6\u4e0a\u94a9\u3002\u5979\u65e9\u5df2\u6b7b\u4e86\uff0c\u5728\u6b7b\u540e\u8fd8\u662f\u6740\u4e86\u4e24\u4e2a\u4ec7\u4eba\u3002\u5979\u4e00\u751f\u6ca1\u5bb3\u8fc7\u4e00\u4e2a\u4eba\u7684\u6027\u547d\uff0c\u5979\u867d\u662f\u6bd2\u624b\u836f\u738b\u7684\u5f1f\u5b50\uff0c\u751f\u5e73\u5374\u4ece\u672a\u6740\u8fc7\u4eba\u3002\u5979\u662f\u5728\u81ea\u5df1\u6b7b\u4e86\u4e4b\u540e\uff0c\u518d\u6765\u6e05\u7406\u5e08\u7236\u7684\u95e8\u6237\uff0c\u518d\u6765\u6740\u6b7b\u8fd9\u4e24\u4e2a\u72fc\u5fc3\u72d7\u80ba\u7684\u5e08\u5144\u5e08\u59ca\u3002 \u3000\u3000\u201c\u5979\u6ca1\u8ddf\u6211\u8bf4\u81ea\u5df1\u7684\u8eab\u4e16\uff0c\u6211\u4e0d\u77e5\u5979\u7236\u4eb2\u6bcd\u4eb2\u662f\u600e\u6837\u7684\u4eba\uff0c\u4e0d\u77e5\u5979\u4e3a\u4ec0\u4e48\u8981\u8ddf\u65e0\u55d4\u5927\u5e08\u5b66\u4e86\u8fd9\u4e00\u8eab\u53ef\u60ca\u53ef\u6016\u7684\u672c\u4e8b\u3002 \u3000\u3000\u6211\u5e38\u5411\u5979\u8bf4\u6211\u81ea\u5df1\u7684\u4e8b\uff0c\u5979\u603b\u662f\u5173\u5207\u7684\u542c\u7740\u3002\u6211\u591a\u60f3\u542c\u5979\u8bf4\u8bf4\u5979\u81ea\u5df1\u7684\u4e8b\uff0c\u53ef\u662f\u4ece\u4eca\u4ee5\u540e\uff0c\u90a3\u662f\u518d\u4e5f\u542c\u4e0d\u5230\u4e86\u3002 \u3000\u3000\u201c\u4e8c\u59b9\u603b\u662f\u5904\u5904\u60f3\u5230\u6211\uff0c\u5904\u5904\u4e3a\u6211\u6253\u7b97\u3002\u6211\u6709\u4ec0\u4e48\u597d\uff0c\u503c\u5f97\u5979\u5bf9\u6211\u8fd9\u6837\uff1f\u503c\u5f97\u5979\u7528\u81ea\u5df1\u7684\u6027\u547d\uff0c\u6765\u6362\u6211\u7684\u6027\u547d\uff1f\u5176\u5b9e\uff0c\u5979\u6839\u672c\u4e0d\u5fc5\u8fd9\u6837\uff0c\u53ea\u987b\u5272\u4e86\u6211\u7684\u624b\u81c2\uff0c\u7528\u4ed6\u5e08\u7236\u7684\u4e39\u836f\uff0c\u8ba9\u6211\u5728\u8fd9\u4e16\u754c\u4e0a\u518d\u6d3b\u4e5d\u5e74\u3002\u4e5d\u5e74\u7684\u65f6\u5149\uff0c\u90a3\u662f\u8db3\u591f\u8db3\u591f\u4e86\uff01\u6211\u4eec\u4e00\u8d77\u5feb\u5feb\u4e50\u4e50\u7684\u5ea6\u8fc7\u4e5d\u5e74\uff0c\u5c31\u7b97\u5979\u8981\u966a\u7740\u6211\u6b7b\uff0c\u90a3\u65f6\u5019\u518d\u6b7b\u4e0d\u597d\u4e48\uff1f\u201d \u3000\u3000\u5ffd\u7136\u60f3\u8d77\uff1a\u201c\u6211\u8bf4\u2018\u5feb\u5feb\u4e50\u4e50\u2019\uff0c\u8fd9\u4e5d\u5e74\u4e4b\u4e2d\uff0c\u6211\u662f\u4e0d\u662f\u771f\u7684\u4f1a\u5feb\u5feb\u4e50\u4e50\uff1f\u4e8c\u59b9\u77e5\u9053\u6211\u4e00\u76f4\u559c\u6b22\u8881\u59d1\u5a18\uff0c\u867d\u7136\u53d1\u89c9\u5979\u662f\u4e2a\u5c3c\u59d1\uff0c\u4f46\u601d\u5ff5\u4e4b\u60c5\uff0c\u5e76\u4e0d\u7a0d\u51cf\u3002\u90a3\u4e48\u5979\u4eca\u65e5\u5b81\u53ef\u4e00\u6b7b\uff0c\u662f\u4e0d\u662f\u4e3a\u6b64\u5462\uff1f\u201d \u3000\u3000\u5728\u90a3\u65e0\u8fb9\u65e0\u9645\u7684\u9ed1\u6697\u4e4b\u4e2d\uff0c\u5fc3\u4e2d\u601d\u6f6e\u8d77\u4f0f\uff0c\u60f3\u8d77\u4e86\u8bb8\u8bb8\u591a\u591a\u4e8b\u60c5\u3002\u7a0b\u7075\u7d20\u7684\u4e00\u8a00\u4e00\u8bed\uff0c\u4e00\u98a6\u4e00\u7b11\uff0c\u5f53\u65f6\u6f2b\u4e0d\u5728\u610f\uff0c\u6b64\u523b\u8ffd\u5fc6\u8d77\u6765\uff0c\u5176\u4e2d\u6240\u542b\u7684\u67d4\u60c5\u871c\u610f\uff0c\u624d\u6e05\u6e05\u695a\u695a\u7684\u663e\u73b0\u51fa\u6765\u3002 \u3000\u3000\u201c\u5c0f\u59b9\u5b50\u5bf9\u60c5\u90ce\u2014\u2014\u6069\u60c5\u6df1\uff0c \u3000\u3000\u4f60\u83ab\u8d1f\u4e86\u59b9\u5b50\u2014\u2014\u4e00\u6bb5\u60c5\uff0c \u3000\u3000\u4f60\u89c1\u4e86\u5979\u9762\u65f6\u2014\u2014\u8981\u5f85\u5979\u597d\uff0c \u3000\u3000\u4f60\u4e0d\u89c1\u5979\u9762\u65f6\u2014\u2014\u5929\u5929\u8981\u5341\u4e03\u516b\u904d\u6302\u5728\u5fc3\uff01\u201d \u3000\u3000\u738b\u94c1\u5320\u90a3\u9996\u60c5\u6b4c\uff0c\u4f3c\u4e4e\u53c8\u5728\u8033\u8fb9\u7f20\u7ed5\uff0c\u201c\u6211\u8981\u5f85\u5979\u597d\uff0c\u53ef\u662f\u2026\u2026\u53ef\u662f\u2026\u2026\u5979\u5df2\u7ecf\u6b7b\u4e86\u3002\u5979\u6d3b\u7740\u7684\u65f6\u5019\uff0c\u6211\u6ca1\u5f85\u5979\u597d\uff0c\u6211\u5929\u5929\u5341\u4e03\u516b\u904d\u6302\u5728\u5fc3\u4e0a\u7684\uff0c\u662f\u53e6\u4e00\u4e2a\u59d1\u5a18\u3002\u201d \u3000\u3000\u5929\u6e10\u6e10\u4eae\u4e86\uff0c\u9633\u5149\u4ece\u7a97\u4e2d\u5c04\u8fdb\u6765\u7167\u5728\u8eab\u4e0a\uff0c\u80e1\u6590\u5374\u53ea\u611f\u5230\u5bd2\u51b7\uff0c\u5bd2\u51b7\u2026\u2026 \u3000\u3000\u7ec8\u4e8e\uff0c\u4ed6\u89c9\u5230\u8eab\u4e0a\u7684\u808c\u8089\u67d4\u8f6f\u8d77\u6765\uff0c\u624b\u81c2\u53ef\u4ee5\u5fae\u5fae\u62ac\u4e00\u4e0b\u4e86\uff0c\u5927\u817f\u53ef\u4ee5\u52a8\u4e00\u4e0b\u4e86\u3002\u4ed6\u53cc\u624b\u6491\u5730\uff0c\u6162\u6162\u7ad9\u8d77\u8eab\u6765\uff0c\u6df1\u60c5\u65e0\u9650\u5730\u671b\u7740\u7a0b\u7075\u7d20\u3002\u7a81\u7136\u4e4b\u95f4\uff0c\u80f8\u4e2d\u70ed\u8840\u6cb8\u817e\u3002\u201c\u6211\u6d3b\u5728\u8fd9\u4e16\u4e0a\u6709\u4ec0\u4e48\u610f\u601d\uff1f\u4e8c\u59b9\u5bf9\u6211\u8fd9\u4e48\u591a\u60c5\uff0c\u6211\u5374\u662f\u5982\u6b64\u8584\u5e78\u7684\u5f85\u5979\uff01\u6211\u4e0d\u5982\u8ddf\u5979\u4e00\u9f50\u6b7b\u4e86\uff01\u201d \u3000\u3000\u4f46\u4e00\u77a5\u773c\u770b\u5230\u6155\u5bb9\u666f\u5cb3\u548c\u859b\u9e4a\u7684\u5c38\u8eab\uff0c\u7acb\u65f6\u60f3\u8d77\uff1a\u201c\u7239\u5a18\u7684\u5927\u4ec7\u8fd8\u672a\u62a5\uff0c\u5bb3\u6b7b\u4e8c\u59b9\u7684\u77f3\u4e07\u55d4\u8fd8\u6d3b\u5728\u4e16\u4e0a\u3002\u6211\u8fd9\u4e48\u8f7b\u751f\u4e00\u6b7b\uff0c\u4ec0\u4e48\u90fd\u6492\u624b\u4e0d\u7ba1\uff0c\u5c82\u662f\u5927\u4e08\u592b\u7684\u884c\u5f84\uff1f\u201d \u3000\u3000\u5374\u539f\u6765\uff0c\u7a0b\u7075\u7d20\u5728\u4e34\u6b7b\u4e4b\u65f6\uff0c\u8fd9\u4ef6\u4e8b\u4e5f\u6599\u5230\u4e86\u3002\u5979\u5c06\u4e03\u5fc3\u6d77\u68e0\u8721\u70db\u6362\u4e86\u4e00\u679d\u7ec6\u8eab\u7684\uff0c\u6bd2\u836f\u4efd\u91cf\u8f83\u8f7b\u7684\uff0c\u5979\u4e0d\u8981\u77f3\u4e07\u55d4\u5f53\u573a\u4fbf\u6b7b\uff0c\u8981\u80e1\u6590\u6162\u6162\u7684\u53bb\u627e\u4ed6\u62a5\u4ec7\u3002\u77f3\u4e07\u55d4\u773c\u775b\u778e\u4e86\uff0c\u80e1\u6590\u4fbf\u6c38\u8fdc\u4e0d\u4f1a\u518d\u5403\u4ed6\u7684\u4e8f\u3002\u5979\u4e34\u6b7b\u65f6\u5bf9\u80e1\u6590\u8bf4\u9053\uff0c\u5bb3\u6b7b\u4ed6\u7236\u6bcd\u7684\u6bd2\u836f\uff0c\u591a\u534a\u662f\u77f3\u4e07\u55d4\u914d\u5236\u7684\u3002\u90a3\u6216\u8bb8\u662f\u4e8b\u5b9e\uff0c\u6216\u8bb8\u53ea\u662f\u731c\u6d4b\uff0c\u4f46\u8fd9\u8db3\u591f\u53eb\u4ed6\u8bb0\u7740\u7236\u6bcd\u4e4b\u4ec7\uff0c\u4f7f\u4ed6\u4e0d\u81f4\u4e8e\u4e00\u65f6\u51b2\u52a8\uff0c\u81ea\u6740\u6b89\u60c5\u3002 \u3000\u3000\u5979\u4ec0\u4e48\u90fd\u6599\u5230\u4e86\uff0c\u53ea\u662f\uff0c\u5979\u6709\u4e00\u4ef6\u4e8b\u6ca1\u6599\u5230\u3002\u80e1\u6590\u8fd8\u662f\u6ca1\u9075\u7167\u5979\u7684\u7ea6\u6cd5\u4e09\u7ae0\uff0c\u5728\u5979\u5371\u6025\u4e4b\u9645\uff0c\u4ecd\u662f\u51fa\u624b\u548c\u654c\u4eba\u52a8\u6b66\uff0c\u7ec8\u81f4\u8eab\u4e2d\u5267\u6bd2\u3002 \u3000\u3000\u53c8\u6216\u8bb8\uff0c\u8fd9\u4e5f\u662f\u5728\u5979\u610f\u6599\u4e4b\u4e2d\u3002\u5979\u77e5\u9053\u80e1\u6590\u5e76\u6ca1\u7231\u5979\uff0c\u66f4\u6ca1\u6709\u50cf\u81ea\u5df1\u7231\u4ed6\u4e00\u822c\u6df1\u5207\u7684\u7231\u7740\u81ea\u5df1\uff0c\u4e0d\u5982\u5c31\u662f\u8fd9\u6837\u4e86\u7ed3\u3002\u7528\u60c5\u90ce\u8eab\u4e0a\u7684\u6bd2\u8840\uff0c\u6bd2\u6b7b\u4e86\u81ea\u5df1\uff0c\u6551\u4e86\u60c5\u90ce\u7684\u6027\u547d\u3002 \u3000\u3000\u5f88\u51c4\u51c9\uff0c\u5f88\u4f24\u5fc3\uff0c\u53ef\u662f\u5e72\u51c0\u5229\u843d\uff0c\u4e00\u4e86\u767e\u4e86\uff0c\u90a3\u6b63\u4e0d\u6127\u4e3a\u201c\u6bd2\u624b\u836f\u738b\u201d\u7684\u5f1f\u5b50\uff0c\u4e0d\u6127\u4e3a\u5929\u4e0b\u7b2c\u4e00\u6bd2\u7269\u201c\u4e03\u5fc3\u6d77\u68e0\u201d\u7684\u4e3b\u4eba\u3002 \u3000\u3000\u5c11\u5973\u7684\u5fc3\u4e8b\u672c\u6765\u662f\u6781\u96be\u6349\u6478\u7684\uff0c\u50cf\u7a0b\u7075\u7d20\u90a3\u6837\u7684\u5c11\u5973\uff0c\u66f4\u52a0\u6c38\u8fdc\u6ca1\u4eba\u80fd\u731c\u5f97\u900f\u5230\u5e95\u5979\u5fc3\u4e2d\u5728\u60f3\u4e9b\u4ec0\u4e48\u3002 \u3000\u3000\u7a81\u7136\u4e4b\u95f4\uff0c\u80e1\u6590\u660e\u767d\u4e86\u4e00\u4ef6\u4e8b\uff1a\u201c\u4e3a\u4ec0\u4e48\u524d\u5929\u665a\u4e0a\u5728\u9676\u7136\u4ead\u7554\uff0c\u9648\u603b\u8235\u4e3b\u796d\u5960\u90a3\u4e2a\u5893\u4e2d\u59d1\u5a18\u65f6\u7adf\u54ed\u5f97\u90a3\u4e48\u4f24\u5fc3\uff1f\u201d\u539f\u6765\uff0c\u5f53\u4f60\u60f3\u5230\u6700\u4eb2\u7231\u7684\u4eba\u6c38\u8fdc\u4e0d\u80fd\u518d\u89c1\u9762\u65f6\uff0c\u4e0d\u7531\u5f97\u4f60\u4e0d\u54ed\uff0c\u4e0d\u7531\u5f97\u4f60\u4e0d\u54ed\u5f97\u8fd9\u4e48\u4f24\u5fc3\u3002 \u3000\u3000\u4ed6\u5c06\u7a0b\u7075\u7d20\u548c\u9a6c\u6625\u82b1\u7684\u5c38\u8eab\u642c\u5230\u7834\u5e99\u540e\u9662\u3002\u5fc3\u60f3\uff1a\u201c\u4e24\u4eba\u5c38\u8eab\u4e0a\u90fd\u6cbe\u7740\u5267\u6bd2\uff0c\u987b\u5f97\u5c0f\u5fc3\uff0c\u522b\u6cbe\u4e0a\u4e86\u3002\u6211\u8fd8\u6ca1\u62a5\u4ec7\uff0c\u53ef\u6b7b\u4e0d\u5f97\uff01\u201d\u751f\u8d77\u67f4\u706b\uff0c\u5206\u522b\u5c06\u4e24\u4eba\u706b\u5316\u4e86\u3002\u4ed6\u5fc3\u4e2d\u7a7a\u7a7a\u6d1e\u6d1e\uff0c\u4f3c\u4e4e\u81ea\u5df1\u7684\u8eab\u5b50\uff0c\u4e5f\u968f\u7740\u706b\u7130\u6210\u70df\u6210\u7070\uff0c\u968f\u624b\u5728\u5730\u4e0b\u6398\u4e86\u4e2a\u5927\u5751\uff0c\u628a\u6155\u5bb9\u666f\u5cb3\u548c\u859b\u9e4a\u592b\u5987\u846c\u4e86\u3002 \u3000\u3000\u773c\u89c1\u65e5\u5149\u897f\u659c\uff0c\u7a0b\u7075\u7d20\u548c\u9a6c\u6625\u82b1\u5c38\u9aa8\u6210\u7070\uff0c\u4e8e\u662f\u5728\u5e99\u4e2d\u627e\u4e86\u4e24\u4e2a\u5c0f\u5c0f\u74e6\u575b\uff0c\u5c06\u4e24\u4eba\u7684\u9aa8\u7070\u6536\u5165\u575b\u5185\uff0c\u5fc3\u60f3\uff1a\u201c\u6211\u53bb\u5c06\u4e8c\u59b9\u7684\u9aa8\u7070\u846c\u5728\u6211\u7239\u5a18\u575f\u65c1\uff0c\u5979\u867d\u4e0d\u662f\u6211\u4eb2\u59b9\u5b50\uff0c\u4f46\u5979\u5982\u6b64\u5f85\u6211\uff0c\u5c82\u4e0d\u6bd4\u4eb2\u9aa8\u8089\u8fd8\u4eb2\u4e48\uff1f\u9a6c\u59d1\u5a18\u7684\u9aa8\u7070\uff0c\u8981\u5e26\u53bb\u6e56\u5317\u5e7f\u6c34\uff0c\u846c\u5728\u5f90\u5927\u54e5\u7684\u5893\u65c1\u3002\u201d \u3000\u3000\u56de\u5230\u53a2\u623f\uff0c\u4f46\u89c1\u7a0b\u7075\u7d20\u7684\u8863\u670d\u5305\u88f9\u5140\u81ea\u653e\u5728\u684c\u4e0a\uff0c\u51dd\u76ee\u77a7\u4e86\u826f\u4e45\uff0c\u5fcd\u4e0d\u4f4f\u53c8\u6389\u4e0b\u6cea\u6765\u3002 \u3000\u3000\u9694\u4e86\u534a\u664c\uff0c\u8fd9\u624d\u4f38\u624b\u6536\u62fe\uff0c\u89c1\u5230\u5305\u4e2d\u6709\u51e0\u4ef6\u6613\u5bb9\u6539\u88c5\u7684\u7528\u5177\uff0c\u80f6\u6c34\u5047\u987b\uff0c\u4e00\u6982\u5177\u5907\uff0c\u5fc3\u60f3\uff1a\u201c\u6211\u82e5\u5766\u7136\u4ee5\u672c\u6765\u9762\u76ee\u793a\u4eba\uff0c\u8d70\u4e0d\u4e0a\u4e00\u5929\uff0c\u4fbf\u4f1a\u9047\u4e0a\u798f\u5eb7\u5b89\u6d3e\u51fa\u6765\u8ffd\u6355\u7684\u9e70\u722a\uff0c\u867d\u7136\u4e0d\u6015\uff0c\u4f46\u4e00\u8def\u6597\u5c06\u8fc7\u53bb\uff0c\u5982\u4f55\u4e86\u5c40\uff1f\u201d\u4e8e\u662f\u8138\u4e0a\u643d\u4e86\u6613\u5bb9\u836f\u6c34\uff0c\u7c98\u4e0a\u4e09\u7efa\u957f\u987b\uff0c\u5c06\u4e24\u53ea\u9aa8\u7070\u575b\u5305\u5165\u5305\u88f9\uff0c\u626c\u957f\u51fa\u5e99\u3002 \u3000\u3000\u4ed6\u4e00\u8def\u5411\u5357\u8ffd\u8e2a\u77f3\u4e07\u55d4\u3002\u8fd9\u65e5\u4e2d\u5348\uff0c\u5728\u9648\u5b98\u5c6f\u4e00\u5bb6\u996d\u94fa\u4e2d\u6253\u5c16\uff0c\u521a\u5750\u5b9a\u4e0d\u4e45\uff0c\u53ea\u542c\u5f97\u9774\u58f0\u6a50\u6a50\uff0c\u8d70\u8fdb\u56db\u540d\u6b66\u5b98\u6765\u3002\u9886\u5148\u4e00\u4eba\u7626\u957f\u8eab\u6750\uff0c\u6b63\u662f\u9e70\u722a\u96c1\u884c\u95e8\u7684\u66fe\u94c1\u9e25\u3002\u80e1\u6590\u5fc3\u4e0b\u5fae\u5fae\u4e00\u60ca\uff0c\u4fa7\u8fc7\u4e86\u5934\uff0c\u5fc3\u60f3\u81ea\u5df1\u867d\u5df2\u4e54\u88c5\u6539\u626e\uff0c\u4ed6\u672a\u5fc5\u8ba4\u5f97\u51fa\u6765\uff0c\u4f46\u6b64\u4eba\u751a\u662f\u7cbe\u660e\uff0c\u8bf4\u4e0d\u5b9a\u4f1a\u7ed9\u4ed6\u77a7\u51fa\u7834\u7efd\u3002 \u3000\u3000\u996d\u94fa\u4e2d\u7684\u5e97\u5c0f\u4e8c\u624b\u5fd9\u811a\u4e71\uff0c\u5f20\u7f57\u7740\u4f8d\u5019\u56db\u4f4d\u6b66\u5b98\u3002 \u3000\u3000\u80e1\u6590\u5fc3\u60f3\uff1a\u201c\u8fd9\u56db\u4eba\u51fa\u4eac\u5357\u4e0b\uff0c\u591a\u534a\u548c\u6211\u7684\u4e8b\u6709\u5173\uff0c\u5012\u8981\u542c\u4ed6\u4eec\u8bf4\u4e9b\u4ec0\u4e48\u3002\u201d\u53ef\u662f\u66fe\u94c1\u9e25\u7b49\u56db\u4eba\u98ce\u82b1\u96ea\u6708\uff0c\u5c3d\u8bf4\u4e9b\u6ca1\u8981\u7d27\u4e4b\u4e8b\uff0c\u53ea\u542c\u5f97\u4ed6\u597d\u751f\u7eb3\u95f7\u3002\u4fbf\u5728\u6b64\u65f6\uff0c\u5ffd\u542c\u5f97\u5e97\u5916\u9752\u77f3\u677f\u4e0a\u7b03\u7b03\u58f0\u54cd\uff0c\u6709\u4e2a\u76f2\u4eba\u4ee5\u6756\u63a2\u5730\uff0c\u6162\u6162\u8d70\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u90a3\u4eba\u4e00\u8fdb\u996d\u94fa\uff0c\u80e1\u6590\u5fc3\u4e2d\u6026\u6026\u4e71\u8df3\uff0c\u8fd9\u51e0\u65e5\u6765\u4ed6\u4e00\u8def\u6253\u63a2\u77f3\u4e07\u55d4\u7684\u8e2a\u8ff9\uff0c\u8ffd\u5bfb\u800c\u6765\uff0c\u67e5\u77e5\u4ed6\u76f8\u8ddd\u5df2\u7ecf\u4e0d\u8fdc\uff0c\u6b64\u4eba\u76f2\u4e86\u53cc\u773c\uff0c\u884c\u8d70\u4e0d\u5feb\uff0c\u8fdf\u65e9\u7ec8\u987b\u8ffd\u4e0a\uff0c\u4e0d\u6599\u7adf\u5728\u8fd9\u4e2a\u9547\u4e0a\u7684\u996d\u5e97\u4e2d\u72ed\u8def\u76f8\u9022\u3002\u53ea\u89c1\u4ed6\u8863\u886b\u8934\u891b\uff0c\u9762\u76ee\u6194\u60b4\uff0c\u5de6\u624b\u5140\u81ea\u6447\u7740\u90a3\u53ea\u8d70\u65b9\u90ce\u4e2d\u6240\u7528\u7684\u864e\u6491\u3002 \u3000\u3000\u4ed6\u6478\u7d22\u5230\u4e00\u5f20\u65b9\u684c\uff0c\u518d\u6478\u5230\u684c\u8fb9\u7684\u677f\u51f3\uff0c\u6162\u6162\u5750\u4e86\u4e0b\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u5e97\u5bb6\uff0c\u5148\u6253\u4e00\u89d2\u9152\u6765\u3002\u201d\u5e97\u5c0f\u4e8c\u89c1\u4ed6\u662f\u4e2a\u4e5e\u513f\u6a21\u6837\uff0c\u6ca1\u597d\u6c14\u7684\u95ee\u9053\uff1a\u201c\u4f60\u8981\u559d\u9152\uff0c\u6709\u94f6\u5b50\u6ca1\u6709\uff1f\u201d\u77f3\u4e07\u55d4\u4ece\u6000\u4e2d\u53d6\u51fa\u4e00\u952d\u94f6\u5b50\uff0c\u653e\u5728\u684c\u4e0a\u3002\u5e97\u5c0f\u4e8c\u9053\uff1a\u201c\u597d\uff0c\u6211\u53bb\u6253\u9152\u7ed9\u4f60\u3002\u201d \u3000\u3000\u77f3\u4e07\u55d4\u4e00\u8d70\u8fdb\u996d\u94fa\uff0c\u66fe\u94c1\u9e25\u4fbf\u5411\u4e09\u4e2a\u540c\u4f34\u5927\u6253\u624b\u52bf\uff0c\u793a\u610f\u8981\u4e0a\u524d\u6349\u62ff\u3002\u90a3\u65e5\u638c\u95e8\u4eba\u5927\u4f1a\u4e4b\u4e2d\uff0c\u7a0b\u7075\u7d20\u53e3\u55b7\u6bd2\u70df\uff0c\u4f7f\u5f97\u4eba\u4eba\u809a\u75db\uff0c\u7fa4\u8c6a\u7591\u5fc3\u798f\u5eb7\u5b89\u5728\u9152\u6c34\u4e2d\u4e0b\u6bd2\uff0c\u798f\u5eb7\u5b89\u7b49\u5374\u8ba4\u5b9a\u662f\u8fd9\u201c\u6bd2\u624b\u836f\u738b\u201d\u505a\u4e86\u624b\u811a\u3002\u56e0\u6b64\u798f\u5eb7\u5b89\u6d3e\u9063\u5927\u6279\u6b66\u5b98\u536b\u58eb\u5357\u4e0b\uff0c\u4ea4\u4ee3\u4e86\u4e09\u4ef6\u8981\u52a1\uff1a\u7b2c\u4e00\u662f\u8ffd\u6355\u7ea2\u82b1\u4f1a\u7fa4\u96c4\u548c\u80e1\u6590\u3001\u7a0b\u7075\u7d20\u3001\u9a6c\u6625\u82b1\u4e00\u884c\u4eba\uff0c\u5bfb\u56de\u798f\u5eb7\u5b89\u7684\u4e24\u4e2a\u513f\u5b50\uff0c\u8fd9\u662f\u7b2c\u4e00\u4ef6\u8981\u4e8b\uff1b\u7b2c\u4e8c\u662f\u6349\u62ff\u62c6\u6563\u638c\u95e8\u4eba\u5927\u4f1a\u7684\u201c\u7f6a\u9b41\u7978\u9996\u201d\u77f3\u4e07\u55d4\uff1b\u7b2c\u4e09\u662f\u6349\u62ff\u5f97\u6089\u91cd\u5927\u9634\u79c1\u9690\u79d8\u7684\u6c64\u6c9b\u53ca\u5c3c\u59d1\u5706\u6027\u3002 \u3000\u3000\u8fd9\u65f6\u66fe\u94c1\u9e25\u773c\u89c1\u77f3\u4e07\u55d4\u53cc\u76ee\u5df2\u76f2\uff0c\u5fc3\u4e0b\u597d\u751f\u559c\u6b22\uff0c\u4f46\u72b9\u6050\u4ed6\u662f\u5047\u88c5\uff0c\u6162\u6162\u7ad9\u8d77\u8eab\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u5e97\u5bb6\uff0c\u600e\u5730\u4f60\u5e97\u91cc\u684c\u6905\u8fd9\u4e48\u5c11\uff1f\u8981\u627e\u4e2a\u5ea7\u5934\u4e5f\u6ca1\u6709\uff1f\u201d\u4e00\u9762\u8bf4\uff0c\u4e00\u9762\u5411\u5e97\u5c0f\u4e8c\u4f5c\u624b\u52bf\uff0c\u547d\u4ed6\u4e0d\u53ef\u4f5c\u58f0\u3002\u53e6\u4e00\u540d\u6b66\u5b98\u63a5\u53e3\u9053\uff1a\u201c\u5f20\u638c\u67dc\u7684\uff0c\u4eca\u513f\u505a\u4ec0\u4e48\u751f\u610f\uff0c\u5230\u9648\u5b98\u5c6f\u6765\u554a\uff1f\u201d\u66fe\u94c1\u9e25\u9053\uff1a\u201c\u8fd8\u4e0d\u662f\u8fd0\u7c73\u6765\u4e48\uff1f\u674e\u638c\u67dc\uff0c\u4f60\u751f\u610f\u597d\uff1f\u201d\u90a3\u6b66\u5b98\u9053\uff1a\u201c\u597d\u4ec0\u4e48\uff1f\u5de6\u53f3\u6df7\u53e3\u996d\u5403\u7f62\u5566\u3002\u201d \u3000\u3000\u4e24\u4eba\u4e1c\u62c9\u897f\u626f\u7684\u8bf4\u4e86\u51e0\u53e5\u3002\u66fe\u94c1\u9e25\u9053\uff1a\u201c\u6ca1\u5ea7\u4f4d\u5566\uff0c\u54b1\u4eec\u8ddf\u8fd9\u4f4d\u5927\u592b\u642d\u4e2a\u5ea7\u5934\u3002\u201d\u8bf4\u7740\u4fbf\u6253\u6a2a\u5750\u5728\u77f3\u4e07\u55d4\u7684\u684c\u65c1\u3002 \u3000\u3000\u5176\u5b9e\u996d\u5e97\u4e2d\u7a7a\u4f4d\u751a\u591a\uff0c\u4f46\u77f3\u4e07\u55d4\u5e76\u4e0d\u8d77\u7591\uff0c\u5bf9\u4e24\u4eba\u4e5f\u4e0d\u52a0\u7406\u776c\u3002\u66fe\u94c1\u9e25\u624d\u77e5\u4ed6\u662f\u771f\u76f2\uff0c\u80c6\u5b50\u66f4\u52a0\u5927\u4e86\uff0c\u5411\u53e6\u5916\u4e24\u540d\u6b66\u5b98\u62db\u624b\u9053\uff1a\u201c\u8d75\u638c\u67dc\uff0c\u738b\u638c\u67dc\uff0c\u4e00\u8d77\u8fc7\u6765\u559d\u4e24\u76c5\u5427\uff0c\u5c0f\u5f1f\u4f5c\u4e1c\u3002\u201c\u90a3\u4e24\u540d\u6b66\u5b98\u9053\uff1a\u201c\u53e8\u6270\uff0c\u53e8\u6270\uff01\u201d\u4e5f\u8fc7\u6765\u5750\u5728\u77f3\u4e07\u55d4\u8eab\u65c1\u3002 \u3000\u3000\u77f3\u4e07\u55d4\u773c\u775b\u867d\u76f2\uff0c\u8033\u97f3\u4ecd\u662f\u6781\u597d\uff0c\u542c\u7740\u66fe\u94c1\u9e25\u7b49\u56db\u4eba\u6ee1\u5634\u5317\u4eac\u5b98\u8154\uff0c\u5e76\u975e\u672c\u5730\u53e3\u97f3\uff0c\u8bf4\u7684\u662f\u505a\u751f\u610f\uff0c\u4f46\u6ca1\u8bb2\u5f97\u51e0\u53e5\u3002 \u3000\u3000\u4fbf\u9732\u51fa\u4e86\u9a6c\u811a\u3002\u4ed6\u5fae\u4e00\u7422\u78e8\uff0c\u5df2\u731c\u5230\u4e86\u516b\u4e5d\u5206\uff0c\u7ad9\u8d77\u8eab\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u5e97\u5bb6\uff0c\u6211\u4eca\u513f\u95f9\u809a\u5b50\uff0c\u4e0d\u60f3\u5403\u559d\u5566\uff0c\u54b1\u4eec\u56de\u5934\u89c1\u3002\u201d\u66fe\u94c1\u9e25\u6309\u4f4f\u4ed6\u80a9\u5934\uff0c\u7b11\u9053\uff1a\u201c\u5927\u592b\u4f60\u4e0d\u5fd9\uff0c\u54b1\u4eec\u559d\u51e0\u676f\u518d\u8d70\u3002\u201d\u77f3\u4e07\u55d4\u77e5\u9053\u8131\u8eab\u4e0d\u5f97\uff0c\u5fae\u5fae\u51b7\u7b11\uff0c\u4fbf\u53c8\u5750\u4e0b\u3002 \u3000\u3000\u4e00\u4f1a\u513f\u9152\u83dc\u7aef\u4e86\u4e0a\u6765\uff0c\u66fe\u94c1\u9e25\u659f\u4e86\u4e00\u676f\u9152\uff0c\u9053\uff1a\u201c\u5927\u592b\uff0c\u6211\u656c\u4f60\u4e00\u676f\u3002\u201d\u77f3\u4e07\u55d4\u9053\uff1a\u201c\u597d\u597d\uff01\u201d\u4e3e\u676f\u559d\u5e72\uff0c\u9053\uff1a\u201c\u6211\u4e5f\u656c\u5404\u4f4d\u4e00\u676f\u3002\u201d\u53f3\u624b\u63d0\u7740\u9152\u58f6\uff0c\u5de6\u624b\u6478\u7d22\u56db\u4eba\u7684\u9152\u676f\uff0c\u66ff\u6bcf\u4eba\u659f\u4e0a\u4e00\u676f\uff0c\u659f\u9152\u4e4b\u65f6\uff0c\u6307\u7532\u8f7b\u5f39\uff0c\u5728\u5404\u4eba\u9152\u676f\u4e2d\u5f39\u4e0a\u4e86\u6bd2\u836f\uff0c\u624b\u6cd5\u4fbf\u6377\uff0c\u5374\u662f\u8c01\u4e5f\u6ca1\u77a7\u51fa\u6765\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u53f7\u79f0\u201c\u6bd2\u624b\u836f\u738b\u201d\uff0c\u66fe\u94c1\u9e25\u867d\u7136\u6ca1\u89c1\u4e0b\u6bd2\uff0c\u5982\u4f55\u6562\u559d\u4ed6\u6240\u659f\u4e4b\u9152\uff0c\u8f7b\u8f7b\u5de7\u5de7\u7684\uff0c\u4fbf\u5c06\u81ea\u5df1\u4e00\u676f\u9152\u548c\u77f3\u4e07\u55d4\u9762\u524d\u7684\u4e00\u676f\u9152\u6362\u8fc7\u4e86\u3002 \u3000\u3000\u8fd9\u4e00\u62db\u8c01\u90fd\u770b\u5f97\u5206\u660e\uff0c\u4fbf\u53ea\u77f3\u4e07\u55d4\u6ca1\u6cd5\u77a7\u89c1\u3002 \u3000\u3000\u80e1\u6590\u5fc3\u4e2d\u53f9\u606f\uff1a\u201c\u4f60\u53cc\u773c\u5df2\u76f2\uff0c\u8fd8\u5728\u4e0b\u6bd2\u5bb3\u4eba\uff0c\u5f53\u771f\u662f\u81ea\u4f5c\u5b7d\uff0c\u4e0d\u53ef\u6d3b\u3002\u6211\u53c8\u4f55\u5fc5\u518d\u51fa\u624b\u6740\u4f60\uff1f\u201d \u3000\u3000\u4ed6\u7ad9\u8d77\u8eab\u6765\uff0c\u4ed8\u4e86\u5e97\u5e10\u3002\u53ea\u542c\u66fe\u94c1\u9e25\u7b11\u9053\uff1a\u201c\u8bf7\u554a\uff0c\u8bf7\u554a\uff0c\u5927\u5bb6\u5e72\u4e86\u8fd9\u676f\uff01\u201d\u56db\u540d\u6b66\u5b98\u8138\u9732\u5978\u7b11\uff0c\u624b\u4e2d\u4ec0\u4e48\u4e5f\u6ca1\u6709\uff0c\u4e00\u9f50\u8bf4\u9053\uff1a\u201c\u5e72\u676f\uff01\u201d\u53ea\u89c1\u77f3\u4e07\u55d4\u62ff\u7740\u4ed6\u4e0b\u4e86\u6bd2\u836f\u7684\u4e00\u676f\u9152\uff0c\u5634\u89d2\u8fb9\u9732\u51fa\u4e00\u4e1d\u72e1\u733e\u7684\u5fae\u7b11\u3002\u80e1\u6590\u77e5\u4ed6\u6599\u5b9a\u8fd9\u56db\u540d\u6b66\u5b98\u8f6c\u773c\u4fbf\u8981\u6bd2\u53d1\u8eab\u4ea1\uff0c\u662f\u4ee5\u5140\u81ea\u8fd8\u5728\u5f97\u610f\uff0c\u89c1\u5230\u77f3\u4e07\u55d4\u8fd9\u822c\u60c5\u72b6\uff0c\u5fc3\u4e2d\u5ffd\u751f\u601c\u60af\u4e4b\u611f\uff0c\u5927\u8e0f\u6b65\u8d70\u51fa\u4e86\u996d\u5e97\u3002 \u3000\u3000\u6570\u65e5\u4e4b\u540e\uff0c\u5230\u4e86\u6ca7\u5dde\u4e61\u4e0b\u7236\u6bcd\u7684\u575f\u5730\u3002\u5f53\u4ed6\u5e7c\u65f6\uff0c\u6bcf\u9694\u51e0\u5e74\uff0c\u5e73\u56db\u53d4\u4fbf\u5e26\u4ed6\u524d\u6765\u626b\u5893\u3002\u4e09\u5e74\u524d\u4ed6\u53c8\u66fe\u6765\u8fc7\u4e00\u6b21\u3002\u6bcf\u6b21\u5230\u8fd9\u5730\u65b9\uff0c\u4ed6\u603b\u8981\u5728\u7236\u6bcd\u5893\u524d\u5446\u5446\u5750\u4e0a\u51e0\u5929\uff0c\u60f3\u7740\u5404\u79cd\u5404\u6837\u7684\u4e8b\u60c5\uff1a\u5982\u679c\u7239\u7239\u5988\u5988\u8fd9\u65f6\u8fd8\u6d3b\u7740\u2026\u2026\u5982\u679c\u4ed6\u4eec\u77a7\u89c1\u6211\u957f\u5f97\u8fd9\u4e48\u9ad8\u5927\u4e86\u2026\u2026\u5982\u679c\u7239\u7239\u89c1\u6211\u8fd9\u4e48\u4f7f\u5200\uff0c\u4e0d\u77e5\u4f1a\u8bf4\u4ec0\u4e48\u2026\u2026\u3002 \u3000\u3000\u8fd9\u65e5\u4ed6\u6765\u5230\u5893\u5730\u65f6\uff0c\u5929\u8272\u5df2\u7ecf\u5411\u665a\uff0c\u8fdc\u8fdc\u77a7\u89c1\u4e00\u4e2a\u7a7f\u6de1\u84dd\u886b\u5b50\u7684\u5973\u4eba\uff0c\u4e00\u52a8\u4e0d\u52a8\u7684\u7ad9\u5728\u4ed6\u7236\u6bcd\u5893\u65c1\u3002\u8fd9\u5757\u5893\u5730\u4e2d\u6ca1\u522b\u7684\u575f\u5893\uff0c\u201c\u96be\u9053\u8fd9\u5973\u5b50\u7adf\u662f\u6211\u7236\u6bcd\u7684\u76f8\u8bc6\uff1f\u201d \u3000\u3000\u4ed6\u5fc3\u4e2d\u5927\u5947\uff0c\u6162\u6162\u8d70\u8fd1\uff0c\u53ea\u89c1\u90a3\u5973\u5b50\u662f\u4e2a\u76f8\u8c8c\u6781\u7f8e\u7684\u4e2d\u5e74\u5987\u4eba\uff0c\u4e00\u5f20\u74dc\u5b50\u8138\u513f\uff0c\u79c0\u4e3d\u51fa\u4f17\uff0c\u53ea\u662f\u8138\u8272\u8fc7\u4e8e\u82cd\u767d\uff0c\u767d\u5f97\u6ca1\u534a\u70b9\u8840\u8272\u3002\u5979\u89c1\u80e1\u6590\u8d70\u6765\uff0c\u4e5f\u662f\u5fae\u611f\u8bb6\u5f02\uff0c\u62ac\u8d77\u4e86\u5934\u77a7\u7740\u4ed6\u3002 \u3000\u3000\u8fd9\u65f6\u80e1\u6590\u79bb\u5317\u4eac\u5df2\u8fdc\uff0c\u9014\u4e2d\u4e0d\u9047\u8ffd\u9a91\uff0c\u5df2\u4e0d\u518d\u4e54\u88c5\uff0c\u56de\u590d\u4e86\u672c\u6765\u9762\u76ee\uff0c\u4f46\u98ce\u5c18\u4ec6\u4ec6\uff0c\u6ee1\u8eab\u90fd\u662f\u6ce5\u7070\u3002\u90a3\u5973\u5b50\u89c1\u662f\u4e2a\u4e0d\u76f8\u8bc6\u7684\u5c11\u5e74\uff0c\u4e5f\u4e0d\u5728\u610f\uff0c\u8f6c\u8fc7\u4e86\u5934\u53bb\u3002 \u3000\u3000\u8fd9\u4e48\u4e00\u8f6c\u5934\uff0c\u80e1\u6590\u5374\u8ba4\u51fa\u5979\u6765\u2014\u2014\u5979\u662f\u5f53\u5e74\u8ddf\u7740\u7530\u5f52\u519c\u79c1\u5954\u7684\u82d7\u4eba\u51e4\u4e4b\u59bb\u3002\u5f53\u5e74\u5728\u5546\u5bb6\u5821\uff0c\u82d7\u4eba\u51e4\u7684\u5973\u513f\u5927\u53eb\u201c\u5988\u5988\u201d\uff0c\u5f20\u5f00\u4e86\u53cc\u81c2\u8981\u5979\u62b1\uff0c\u5979\u5374\u786c\u8d77\u5fc3\u80a0\uff0c\u8f6c\u8fc7\u4e86\u5934\u53bb\u3002\u5979\u7684\u76f8\u8c8c\u80e1\u6590\u5df2\u8bb0\u4e0d\u8d77\u4e86\u3002\u4f46\u8fd9\u4e48\u72e0\u5fc3\u4e00\u8f6c\u5934\uff0c\u4ed6\u6c38\u8fdc\u90fd\u5fd8\u4e0d\u4e86\u3002 \u3000\u3000\u4ed6\u5fcd\u4e0d\u4f4f\u51b7\u51b7\u5730\u9053\uff1a\u201c\u82d7\u592b\u4eba\uff0c\u4f60\u72ec\u4e2a\u513f\u5728\u8fd9\u91cc\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5979\u9661\u7136\u542c\u5230\u201c\u82d7\u592b\u4eba\u201d\u4e09\u5b57\uff0c\u5168\u8eab\u4e00\u9707\uff0c\u6162\u6162\u56de\u8fc7\u8eab\u6765\uff0c\u8138\u8272\u66f4\u52a0\u767d\u4e86\uff0c\u98a4\u58f0\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u600e\u77e5\u9053\u6211\u2026\u2026\u201d\u8bf4\u4e86\u8fd9\u51e0\u4e2a\u5b57\uff0c\u7f13\u7f13\u4f4e\u4e0b\u4e86\u5934\uff0c\u4e0b\u9762\u7684\u8bdd\u518d\u4e5f\u8bf4\u4e0d\u51fa\u6765\u4e86\u3002 \u3000\u3000\u80e1\u6590\u9053\uff1a\u201c\u6211\u51fa\u4e16\u4e09\u5929\uff0c\u7236\u6bcd\u4fbf\u957f\u7720\u4e8e\u5730\u4e0b\uff0c\u7ec8\u8eab\u4e0d\u77e5\u7236\u6bcd\u4e4b\u7231\uff0c\u4f46\u6bd4\u8d77\u4f60\u7684\u5973\u513f\u6765\uff0c\u6211\u8fd8\u662f\u5feb\u6d3b\u5f97\u591a\u3002\u90a3\u5929\u5546\u5bb6\u5821\u4e2d\uff0c\u4f60\u786c\u7740\u5fc3\u80a0\u4e0d\u80af\u62b1\u5973\u513f\u4e00\u62b1\u2026\u2026\u4e0d\u9519\uff0c\u6211\u6bd4\u4f60\u7684\u5973\u513f\u662f\u5feb\u6d3b\u5f97\u591a\u4e86\u3002\u201d \u3000\u3000\u82d7\u592b\u4eba\u5357\u5170\u8eab\u5b50\u6447\u6447\u6b32\u5012\uff0c\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u662f\u8c01\uff1f\u201d \u3000\u3000\u80e1\u6590\u6307\u7740\u575f\u5893\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u662f\u5230\u8fd9\u91cc\u6765\u53eb\u4e00\u58f0\u2018\u7239\u7239\uff0c\u5988\u5988\uff01\u2019\u53ea\u56e0\u4ed6\u4eec\u6b7b\u4e86\uff0c\u8fd9\u624d\u4e0d\u7b54\u6211\uff0c\u8fd9\u624d\u4e0d\u62b1\u6211\u3002\u201d\u5357\u5170\u9053\uff1a\u201c\u4f60\u662f\u80e1\u5927\u4fa0\u80e1\u4e00\u5200\u2026\u2026\u7684\u2026\u2026\u7684\u4ee4\u90ce\uff1f\u201d\u80e1\u6590\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u59d3\u80e1\u540d\u6590\u3002\u6211\u89c1\u8fc7\u91d1\u9762\u4f5b\u82d7\u5927\u4fa0\uff0c\u4e5f\u89c1\u8fc7\u4ed6\u7684\u5973\u513f\u3002\u201d\u5357\u5170\u4f4e\u58f0\u9053\uff1a\u201c\u4ed6\u4eec\u2026\u2026\u4ed6\u4eec\u5f88\u597d\u5427\uff1f\u201d \u3000\u3000\u80e1\u6590\u65a9\u9489\u622a\u94c1\u5730\u9053\uff1a\u201c\u4e0d\u597d\uff01\u201d \u3000\u3000\u5357\u5170\u8d70\u4e0a\u4e00\u6b65\uff0c\u9053\uff1a\u201c\u4ed6\u4eec\u600e\u4e48\u5566\uff1f\u80e1\u76f8\u516c\uff0c\u6c42\u6c42\u4f60\uff0c\u6c42\u4f60\u8ddf\u6211\u8bf4\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u82d7\u5927\u4fa0\u4e3a\u5978\u4eba\u6240\u5bb3\uff0c\u778e\u4e86\u53cc\u76ee\u3002\u82d7\u59d1\u5a18\u5b64\u82e6\u4f36\u4ec3\uff0c\u6ca1\u5988\u5988\u7167\u987e\u3002\u201d\u5357\u5170\u60ca\u9053\uff1a\u201c\u4ed6\u2026\u2026\u4ed6\u6b66\u529f\u76d6\u4e16\uff0c\u600e\u80fd\u2026\u2026\u201d \u3000\u3000\u80e1\u6590\u5927\u6012\uff0c\u5389\u58f0\u9053\uff1a\u201c\u5728\u6211\u9762\u524d\uff0c\u4f60\u4f55\u5fc5\u5047\u60fa\u60fa\u88c5\u6a21\u4f5c\u6837\uff1f \u3000\u3000\u7530\u5f52\u519c\u884c\u6b64\u6bd2\u8ba1\uff0c\u96be\u9053\u4e0d\u662f\u51fa\u4e8e\u4f60\u7684\u5978\u8c0b\uff1f\u6b64\u5904\u82e5\u4e0d\u662f\u6211\u7236\u6bcd\u7684\u575f\u5893\u6240\u5728\uff0c\u6211\u4e00\u5200\u4fbf\u5c06\u4f60\u6740\u4e86\u3002\u4f60\u5feb\u5feb\u8d70\u5f00\u5427\uff01\u201d \u3000\u3000\u5357\u5170\u98a4\u58f0\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u786e\u662f\u4e0d\u77e5\u3002\u80e1\u76f8\u516c\uff0c\u8fd9\u65f6\u5019\u4ed6\u5df2\u597d\u4e86\u5417\uff1f\u201d \u3000\u3000\u80e1\u6590\u89c1\u5979\u8138\u8272\u6781\u662f\u8bda\u6073\uff0c\u4e0d\u4f3c\u4f5c\u4f2a\uff0c\u4f46\u60f3\u8fd9\u5973\u5b50\u6c34\u6027\u6768\u82b1\u3001\u5978\u6ed1\u51c9\u8584\uff0c\u4ec0\u4e48\u6837\u5b50\u90fd\u88c5\u5f97\u51fa\uff0c\u4e0d\u613f\u8ddf\u5979\u591a\u8bf4\uff0c\u54fc\u4e86\u4e00\u58f0\uff0c\u8f6c\u8eab\u4fbf\u8d70\u3002\u5357\u5170\u5583\u5583\u7684\u9053\uff1a\u201c\u4ed6\u2026\u2026\u4ed6\u7adf\u88ab\u4eba\u5f04\u778e\u4e86\u773c\u775b\uff0c\u5170\u513f\uff0c\u6211\u82e6\u547d\u7684\u5170\u513f\u2026\u2026\u201d\u7a81\u7136\u95f4\u7ffb\u8eab\u6454\u5012\uff0c\u6655\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u80e1\u6590\u542c\u5f97\u58f0\u54cd\uff0c\u56de\u5934\u4e00\u770b\uff0c\u5012\u5403\u4e86\u4e00\u60ca\uff0c\u5fae\u4e00\u8e0c\u8e87\uff0c\u8fc7\u53bb\u4e00\u63a2\u5979\u9f3b\u606f\uff0c\u7adf\u662f\u771f\u7684\u6c14\u53a5\uff0c\u8109\u606f\u5fae\u5f31\uff0c\u8d8a\u8df3\u8d8a\u6162\uff0c\u82e5\u662f\u4e0d\u52a0\u65bd\u6551\uff0c\u7acb\u5373\u4fbf\u8981\u8eab\u4ea1\u3002\u4ed6\u4e07\u4e0d\u6599\u5230\u8fd9\u4e2a\u65e0\u60c5\u65e0\u4e49\u7684\u5973\u5b50\u7adf\u4f1a\u5982\u6b64\uff0c\u5f53\u4e0b\u634f\u5979\u7684\u4eba\u4e2d\uff0c\u5728\u5979\u80c1\u4e0b\u63a8\u62ff\u3002 \u3000\u3000\u8fc7\u4e86\u826f\u4e45\uff0c\u5357\u5170\u624d\u60a0\u60a0\u9192\u8f6c\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u80e1\u76f8\u516c\uff0c\u6211\u6b7b\u4e0d\u8db3\u60dc\uff0c\u53ea\u6c42\u4f60\u544a\u6211\u5b9e\u60c5\uff0c\u4ed6\u548c\u6211\u5170\u513f\u5230\u5e95\u600e\u6837\u4e86\uff1f\u201d\u80e1\u6590\u9053\uff1a\u201c\u96be\u9053\u4f60\u8fd8\u5173\u6000\u4ed6\u4eec\uff1f\u201d \u3000\u3000\u5357\u5170\u9053\uff1a\u201c\u8bf4\u6765\u4f60\u5b9a\u7136\u4e0d\u4fe1\u3002\u4f46\u8fd9\u51e0\u5e74\u6765\uff0c\u6211\u65e5\u65e5\u591c\u591c\uff0c\u60f3\u7740\u7684\u4fbf\u662f\u8fd9\u4e24\u4e2a\u4eba\u3002\u6211\u81ea\u77e5\u5df2\u4e0d\u4e45\u4eba\u4e16\uff0c\u53ea\u76fc\u80fd\u518d\u89c1\u4ed6\u4eec\u4e00\u9762\uff0c\u53ef\u662f\u6211\u54ea\u91cc\u53c8\u6709\u9762\u76ee\u518d\u53bb\u89c1\u4ed6\u7236\u5973\uff1f\u4eca\u65e5\u6211\u5230\u8fd9\u91cc\u6765\uff0c\u56e0\u4e3a\u82d7\u5927\u54e5\u5f53\u5e74\u548c\u6211\u6210\u5a5a\u4e0d\u4e45\uff0c\u4fbf\u5e26\u7740\u6211\u5230\u8fd9\u91cc\uff0c\u6765\u796d\u5960\u4ee4\u5c0a\u4ee4\u5802\uff0c\u82d7\u5927\u54e5\u8bf4\u4ed6\u4e00\u751f\u4e4b\u4e2d\uff0c\u4fbf\u53ea\u4f69\u670d\u80e1\u5927\u4fa0\u592b\u5987\u4e24\u4eba\u3002\u5f53\u5e74\u5728\u8fd9\u5893\u524d\uff0c\u4ed6\u8ddf\u6211\u8bf4\u4e86\u8bb8\u591a\u8bdd\u2026\u2026\u201d \u3000\u3000\u80e1\u6590\u89c1\u5979\u60c5\u8f9e\u771f\u631a\uff0c\u786e\u975e\u865a\u5047\uff0c\u4ed6\u4eba\u867d\u7c97\u8c6a\uff0c\u5fc3\u80a0\u5374\u8f6f\uff0c\u4fbf\u9053\uff1a\u201c\u597d\uff0c\u6211\u4fbf\u8ddf\u4f60\u8bf4\u4e00\u8bf4\u82d7\u5927\u4fa0\u7236\u5973\u7684\u8fd1\u72b6\u3002\u201d\u4e8e\u662f\u5c06\u82d7\u4eba\u51e4\u5982\u4f55\u53cc\u76ee\u4e2d\u6bd2\u3001\u5982\u4f55\u529b\u8d25\u5f3a\u654c\u7b49\u60c5\u7b80\u7565\u8bf4\u4e86\uff0c\u53ea\u662f\u81ea\u5df1\u5982\u4f55\u4ece\u65c1\u63f4\u624b\uff0c\u5374\u8f7b\u8f7b\u4e00\u8a00\u5e26\u8fc7\u3002\u5357\u5170\u7d6e\u7d6e\u8be2\u95ee\u82d7\u4eba\u51e4\u548c\u82d7\u82e5\u5170\u7236\u5973\u7684\u8d77\u5c45\u996e\u98df\uff0c\u5bf9\u82d7\u82e5\u5170\u76f8\u8c8c\u5982\u4f55\u3001\u559c\u6b22\u4ec0\u4e48\u7b49\u7b49\uff0c\u95ee\u5f97\u66f4\u662f\u4ed4\u7ec6\u3002\u4f46\u80e1\u6590\u5728\u82d7\u5bb6\u5306\u5306\u800c\u6765\uff0c\u5306\u5306\u800c\u53bb\uff0c\u5bf9\u8fd9\u4e2a\u5c0f\u59d1\u5a18\u7684\u60c5\u72b6\uff0c\u5374\u662f\u8bf4\u4e0d\u4e0a\u4ec0\u4e48\u3002 \u3000\u3000\u4ed6\u4e00\u76f4\u8bf4\u5230\u5915\u9633\u897f\u4e0b\uff0c\u5357\u5170\u610f\u72b9\u672a\u8db3\uff0c\u5140\u81ea\u95ee\u4e2a\u4e0d\u4f11\u3002\u80e1\u6590\u8bf4\u5230\u540e\u6765\uff0c\u5b9e\u5df2\u65e0\u8bdd\u53ef\u7b54\uff0c\u5357\u5170\u95ee\u4ed6\uff0c\u5979\u5973\u513f\u7a7f\u4ec0\u4e48\u6837\u7684\u8863\u670d\uff0c\u662f\u7ef8\u7684\u8fd8\u662f\u5e03\u7684\uff1f\u662f\u5979\u7236\u4eb2\u5230\u5e97\u4e2d\u4e70\u6765\uff0c\u8fd8\u662f\u6258\u4eba\u7f1d\u5236\uff1f\u7a7f\u4e86\u5408\u4e0d\u5408\u8eab\uff1f\u597d\u4e0d\u597d\u770b\uff1f \u3000\u3000\u80e1\u6590\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u90fd\u4e0d\u77e5\u9053\u3002\u4f60\u65e2\u662f\u8fd9\u6837\u5173\u5fc3\uff0c\u5f53\u5e74\u53c8\u4f55\u5fc5\u2026\u2026\u201d\u7ad9\u8d77\u8eab\u6765\uff0c\u9053\uff1a\u201c\u6211\u8981\u6295\u5e97\u53bb\u5566\u3002\u672c\u6765\u4eca\u65e5\u6211\u8981\u6765\u57cb\u846c\u4e49\u59b9\u7684\u9aa8\u7070\uff0c\u6b64\u523b\u5929\u8272\u5df2\u665a\uff0c\u53ea\u597d\u660e\u5929\u518d\u6765\uff01\u201d\u5357\u5170\u9053\uff1a\u201c\u597d\uff0c\u660e\u5929\u6211\u4e5f\u6765\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u4e0d\uff01\u6211\u518d\u4e5f\u6ca1\u4ec0\u4e48\u8bdd\u8ddf\u4f60\u8bf4\u4e86\u3002\u201d\u4ed6\u987f\u4e86\u4e00\u987f\uff0c\u7ec8\u4e8e\u95ee\u9053\uff1a\u201c\u82d7\u592b\u4eba\uff0c\u6211\u7239\u7239\u5988\u5988\uff0c\u662f\u6b7b\u5728\u82d7\u4eba\u51e4\u624b\u4e0b\u7684\uff0c\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u5357\u5170\u7f13\u7f13\u70b9\u4e86\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4ed6\u2026\u2026\u4ed6\u66fe\u8ddf\u6211\u8bf4\u8d77\u6b64\u4e8b\u2026\u2026\uff0c\u4e0d\u8fc7\uff0c\u8fd9\u662f\u2026\u2026\u201d \u3000\u3000\u6b63\u8bf4\u5230\u8fd9\u91cc\uff0c\u5ffd\u542c\u5f97\u8fdc\u5904\u6709\u4eba\u53eb\u9053\uff1a\u201c\u963f\u5170\uff0c\u963f\u5170\uff01\u2026\u2026\u963f\u5170\uff0c\u963f\u5170\uff01\u4f60\u5728\u54ea\u91cc\uff1f\u201d\u80e1\u6590\u548c\u5357\u5170\u4e00\u542c\uff0c\u540c\u65f6\u8138\u8272\u5fae\u53d8\uff0c\u539f\u6765\u90a3\u6b63\u662f\u7530\u5f52\u519c\u7684\u53eb\u58f0\u3002 \u3000\u3000\u5357\u5170\u9053\uff1a\u201c\u4ed6\u627e\u6211\u6765\u5566\uff01\u660e\u513f\u4e00\u65e9\uff0c\u8bf7\u4f60\u518d\u5230\u8fd9\u91cc\uff0c\u6211\u8ddf\u4f60\u8bf4\u4ee4\u5c0a\u4ee4\u5802\u7684\u4e8b\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u597d\uff0c\u660e\u65e5\u4e00\u65e9\uff0c\u4e00\u51c6\u5728\u6b64\u4f1a\u9762\u3002\u201d\u4ed6\u4e0d\u613f\u8ddf\u7530\u5f52\u519c\u671d\u76f8\uff0c\u9690\u8eab\u5728\u575f\u5893\u4e4b\u540e\uff0c\u5fc3\u60f3\uff1a\u201c\u660e\u65e5\u95ee\u660e\u7239\u7239\u5988\u5988\u8eab\u6545\u7684\u771f\u76f8\uff0c\u82e5\u662f\u5f53\u771f\u548c\u7530\u5f52\u519c\u8fd9\u5978\u8d3c\u6709\u5173\uff0c\u987b\u9976\u4ed6\u4e0d\u5f97\u3002\u6599\u60f3\u82d7\u592b\u4eba\u5b9a\u8981\u66ff\u4ed6\u906e\u63a9\u9690\u7792\uff0c\u4f46\u6211\u53ea\u8981\u7ec6\u5fc3\u67e5\u7a76\uff0c\u5fc5\u80fd\u77a7\u51fa\u7aef\u502a\u3002\u53ea\u4e0d\u77e5\u7530\u5f52\u519c\u5230\u6ca7\u5dde\u6765\uff0c\u5374\u662f\u4e3a\u4e86\u4f55\u4e8b\uff1f\u201d \u3000\u3000\u53ea\u89c1\u5357\u5170\u5feb\u6b65\u8d70\u51fa\u5893\u5730\uff0c\u5374\u4e0d\u662f\u671d\u7740\u7530\u5f52\u519c\u53eb\u58f0\u7684\u65b9\u5411\u8d70\u53bb\uff0c\u5f85\u8d70\u51fa\u6570\u5341\u4e08\u8fdc\uff0c\u53ea\u542c\u5f97\u7530\u5f52\u519c\u8fd8\u5728\u4e0d\u4f4f\u53e3\u7684\u547c\u5524\uff1a \u3000\u3000\u201c\u963f\u5170\uff0c\u963f\u5170\uff0c\u4f60\u5728\u4e0d\u5728\u8fd9\u513f\uff1f\u201d\u5357\u5170\u624d\u5e94\u9053\uff1a\u201c\u6211\u5728\u8fd9\u91cc\u3002\u201d\u7530\u5f52\u519c\u201c\u554a\u201d\u4e86\u4e00\u58f0\uff0c\u5faa\u58f0\u5954\u53bb\u3002\u5357\u5170\u9053\uff1a\u201c\u6211\u968f\u4fbf\u8d70\u8d70\uff0c\u4f60\u4e5f\u4e0d\u8bb8\uff0c\u4fbf\u7ba1\u5f97\u6211\u8fd9\u4e48\u7d27\u3002\u201d\u9690\u9690\u7ea6\u7ea6\u542c\u5f97\u7530\u5f52\u519c\u966a\u7b11\u9053\uff1a\u201c\u8c01\u6562\u7ba1\u4f60\u5566\uff1f\u6211\u8bb0\u6302\u7740\u4f60\u554a\u3002\u8fd9\u513f\u597d\u751f\u8352\u51c9\uff0c\u5c0f\u5fc3\u522b\u5413\u7740\u4e86\u2026\u2026\u201d\u4e24\u4eba\u5e76\u80a9\u8fdc\u53bb\uff0c\u518d\u8bf4\u4e9b\u4ec0\u4e48\uff0c\u4fbf\u542c\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u80e1\u6590\u5fc3\u60f3\uff1a\u201c\u5929\u8272\u5df2\u665a\uff0c\u4e0d\u5982\u4fbf\u5728\u8fd9\u91cc\u966a\u7740\u7239\u5a18\u7761\u4e00\u591c\u3002\u201d \u3000\u3000\u4ece\u5305\u88f9\u53d6\u51fa\u4e9b\u5e72\u7cae\u5403\u4e86\uff0c\u62b1\u819d\u5750\u4e8e\u5893\u65c1\uff0c\u6c89\u601d\u826f\u4e45\uff0c\u79cb\u98ce\u5439\u6765\uff0c\u5fae\u611f\u51c9\u610f\u3002\u5893\u5730\u4e0a\u9ec4\u53f6\u968f\u98ce\u4e71\u821e\uff0c\u4e00\u5f20\u5f20\u6251\u5728\u4ed6\u8138\u4e0a\u8eab\u4e0a\uff0c\u76f4\u5230\u6708\u4e0a\u4e1c\u5c71\uff0c\u8fd9\u624d\u5367\u5012\u3002 \u3000\u3000\u7761\u5230\u4e2d\u591c\uff0c\u5ffd\u542c\u5f97\u9a6c\u8e44\u51fb\u5730\u4e4b\u58f0\uff0c\u8fdc\u8fdc\u4f20\u6765\uff0c\u80e1\u6590\u4e00\u60ca\u800c\u9192\uff0c\u5fc3\u9053\uff1a\u201c\u534a\u591c\u4e09\u66f4\uff0c\u8fd8\u6709\u8c01\u5728\u8352\u90ca\u9a70\u9a6c\uff1f\u201d\u53ea\u542c\u5f97\u8e44\u58f0\u6e10\u8fd1\uff0c\u90a3\u9a6c\u5954\u5f97\u751a\u662f\u8fc5\u6377\u3002\u5f85\u5f97\u76f8\u8ddd\u7ea6\u6709\u4e24\u4e09\u91cc\u8def\uff0c\u8e44\u58f0\u7f13\u4e86\uff0c\u8ddf\u7740\u662f\u4e00\u6b65\u4e00\u6b65\u800c\u884c\uff0c\u4f3c\u4e4e\u9a6c\u4e0a\u4e58\u5ba2\u5df2\u4e0b\u4e86\u9a6c\u80cc\uff0c\u7275\u7740\u9a6c\u5728\u627e\u5bfb\u4ec0\u4e48\u3002\u80e1\u6590\u542c\u5f97\u90a3\u9a6c\u6b63\u662f\u5411\u81ea\u5df1\u7684\u65b9\u5411\u800c\u6765\uff0c\u5f53\u4e0b\u7f29\u5728\u5893\u540e\u7684\u957f\u8349\u4e4b\u4e2d\uff0c\u8981\u77a7\u6765\u7684\u662f\u8c01\u3002 \u3000\u3000\u65b0\u6708\u4e4b\u4e0b\uff0c\u53ea\u89c1\u4e00\u4e2a\u8eab\u6750\u82d7\u6761\u7684\u4eba\u5f71\u7275\u7740\u9a6c\u6162\u6162\u8d70\u8fd1\uff0c\u5f85\u90a3\u4eba\u8d70\u5230\u5893\u524d\u5341\u4f59\u4e08\u65f6\uff0c\u80e1\u6590\u770b\u5f97\u660e\u767d\uff0c\u90a3\u4eba\u7f01\u8863\u5706\u5e3d\uff0c\u6b63\u662f\u5706\u6027\u3002 \u3000\u3000\u4ed6\u4e00\u9897\u5fc3\u5267\u70c8\u8df3\u52a8\uff0c\u4f46\u89c9\u5507\u5e72\u820c\u71e5\uff0c\u624b\u5fc3\u4e2d\u90fd\u662f\u51b7\u6c57\uff0c\u8981\u60f3\u51fa\u58f0\u547c\u5524\uff0c\u4e0d\u77e5\u5982\u4f55\uff0c\u7adf\u662f\u53eb\u4e0d\u51fa\u58f0\u6765\uff0c\u970e\u65f6\u95f4\u601d\u5982\u6f6e\u6d8c\uff1a \u3000\u3000\u201c\u5979\u5230\u8fd9\u91cc\u6765\u505a\u4ec0\u4e48\uff1f\u5979\u662f\u77e5\u9053\u6211\u5728\u8fd9\u91cc\u4e48\uff1f\u662f\u65e0\u610f\u4e2d\u5230\u8fd9\u513f\u5462\uff0c\u8fd8\u662f\u4e3a\u4e86\u5bfb\u6211\u800c\u6765\uff1f\u201d \u3000\u3000\u53ea\u542c\u5f97\u5706\u6027\u8f7b\u8f7b\u5ff5\u7740\u5893\u7891\u4e0a\u7684\u5b57\u9053\uff1a\u201c\u8fbd\u4e1c\u5927\u4fa0\u80e1\u4e00\u5200\u592b\u5987\u4e4b\u5893\uff01\u201d\u5e7d\u5e7d\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u662f\u8fd9\u91cc\u3002\u201d\u5728\u5893\u524d\u4ed4\u7ec6\u5bdf\u770b\uff0c\u81ea\u8a00\u81ea\u8bed\u9053\uff1a\u201c\u5893\u524d\u5e76\u65e0\u7eb8\u7070\uff0c\u90a3\u4e48\u4ed6\u8fd8\u6ca1\u6765\u626b\u8fc7\u5893\u2026\u2026\u201d\u7a81\u7136\u4e4b\u95f4\uff0c\u5267\u70c8\u54b3\u55fd\u8d77\u6765\uff0c\u8d8a\u54b3\u8d8a\u662f\u5389\u5bb3\uff0c\u7adf\u662f\u4e0d\u80fd\u6b62\u6b47\u3002 \u3000\u3000\u53ea\u542c\u5f97\u5979\u54b3\u4e86\u597d\u534a\u664c\uff0c\u624d\u6e10\u6e10\u6b62\u4e86\uff0c\u8f7b\u8f7b\u7684\u9053\uff1a\u201c\u5018\u82e5\u5f53\u5e74\u6211\u4e0d\u662f\u5728\u5e08\u7236\u8ddf\u524d\u7acb\u4e0b\u91cd\u8a93\uff0c\u7ec8\u8eab\u4f34\u7740\u4f60\u6d6a\u8ff9\u5929\u6daf\uff0c\u884c\u4fa0\u4ed7\u4e49\uff0c\u5c82\u4e0d\u662f\u597d\uff1f\u5509\uff0c\u80e1\u5927\u54e5\uff0c\u4f60\u5fc3\u4e2d\u96be\u8fc7\u3002\u4f46\u4f60\u77e5\u4e0d\u77e5\u9053\uff0c\u6211\u53ef\u6bd4\u4f60\u66f4\u662f\u4f24\u5fc3\u5341\u500d\u554a\uff1f\u201d \u3000\u3000\u80e1\u6590\u548c\u5979\u6570\u5ea6\u76f8\u9047\uff0c\u89c1\u5979\u603b\u662f\u82e5\u6709\u60c5\u82e5\u65e0\u60c5\uff0c\u54ea\u91cc\u542c\u5230\u8fc7\u5979\u5410\u9732\u5fc3\u4e2d\u771f\u610f\uff1f\u82e5\u4e0d\u662f\u5979\u53ea\u9053\u8352\u91ce\u4e4b\u4e2d\u5b9a\u7136\u65e0\u4eba\u542c\u89c1\uff0c\u4e5f\u51b3\u4e0d\u4f1a\u6cc4\u6f0f\u5fc3\u4e2d\u7684\u90c1\u79ef\u3002\u5706\u6027\u8bf4\u4e86\u8fd9\u51e0\u53e5\u8bdd\uff0c\u5fc3\u795e\u6fc0\u8361\uff0c\u501a\u7740\u5893\u7891\uff0c\u53c8\u5927\u54b3\u8d77\u6765\u3002 \u3000\u3000\u80e1\u6590\u518d\u4e5f\u5fcd\u8010\u4e0d\u4f4f\uff0c\u7eb5\u8eab\u800c\u51fa\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u600e\u5730\u53d7\u4e86\u98ce\u5bd2\uff1f\u8981\u4fdd\u91cd\u624d\u597d\u3002\u201d \u3000\u3000\u5706\u6027\u5927\u5403\u4e00\u60ca\uff0c\u9000\u4e86\u4e00\u6b65\uff0c\u53cc\u638c\u4ea4\u53c9\uff0c\u4e00\u524d\u4e00\u540e\uff0c\u62a4\u5728\u80f8\u524d\uff0c\u5f85\u5f97\u770b\u6e05\u695a\u7adf\u662f\u80e1\u6590\uff0c\u4e0d\u7531\u5f97\u6ee1\u8138\u901a\u7ea2\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u5706\u6027\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u8fd9\u8f7b\u8584\u5c0f\u5b50\uff0c\u600e\u5730\u2026\u2026\u600e\u5730\u8eb2\u5728\u8fd9\u91cc\uff0c\u9b3c\u9b3c\u795f\u795f\u7684\u5077\u542c\u4eba\u5bb6\u8bf4\u8bdd\uff1f\u201d \u3000\u3000\u80e1\u6590\u5fc3\u4e2d\u5982\u6cb8\uff0c\u518d\u4e5f\u4e0d\u987e\u5fcc\u4ec0\u4e48\uff0c\u5927\u58f0\u9053\uff1a\u201c\u8881\u59d1\u5a18\uff0c\u6211\u5bf9\u4f60\u7684\u4e00\u7247\u771f\u5fc3\uff0c\u4f60\u4e5f\u51b3\u975e\u4e0d\u77e5\u3002\u4f60\u53c8\u4f55\u5fc5\u6789\u7136\u81ea\u82e6\uff1f\u6211\u8ddf\u4f60\u4e00\u540c\u53bb\u7980\u544a\u5c0a\u5e08\uff0c\u8fd8\u4fd7\u56de\u5bb6\uff0c\u4e0d\u505a\u8fd9\u5c3c\u59d1\u4e86\u3002\u4f60\u6211\u5929\u957f\u5730\u4e45\uff0c\u6c38\u76f8\u53ae\u5b88\uff0c\u5c82\u4e0d\u662f\u597d\uff1f\u201d \u3000\u3000\u5706\u6027\u629a\u7740\u5893\u7891\uff0c\u54b3\u5f97\u5f2f\u4e0b\u4e86\u8170\uff0c\u62ac\u4e0d\u8d77\u8eab\u6765\u3002\u80e1\u6590\u751a\u662f\u601c\u60dc\uff0c\u8d70\u8fd1\u4e24\u6b65\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u4f60\u4e0d\u7528\u70e6\u607c\u5566\u2026\u2026\u201d\u5ffd\u89c1\u5979\u4e00\u58f0\u54b3\u55fd\uff0c\u5410\u51fa\u4e00\u53e3\u8840\u6765\uff0c\u4e0d\u7981\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u600e\u5730\u53d7\u4e86\u4f24\uff1f\u201d \u3000\u3000\u5706\u6027\u9053\uff1a\u201c\u662f\u6c64\u6c9b\u90a3\u5978\u8d3c\u4f24\u7684\u3002\u201d\u80e1\u6590\u6012\u9053\uff1a\u201c\u4ed6\u5728\u54ea\u91cc\uff1f\u6211\u8fd9\u4fbf\u627e\u4ed6\u53bb\u3002\u201d\u5706\u6027\u9053\uff1a\u201c\u6211\u5df2\u6740\u4e86\u4ed6\u3002\u201d \u3000\u3000\u80e1\u6590\u5927\u559c\uff0c\u9053\uff1a\u201c\u606d\u559c\u4f60\u624b\u5203\u5927\u4ec7\u3002\u201d\u968f\u5373\u53c8\u95ee\uff1a\u201c\u4f24\u5728\u54ea\u91cc\uff0c\u5feb\u5750\u4e0b\u6b47\u4e00\u6b47\u3002\u201d\u6276\u7740\u5979\u6162\u6162\u5750\u4e0b\u3002\u53c8\u9053\uff1a\u201c\u4f60\u65e2\u5df2\u53d7\u4f24\uff0c\u5c31\u8be5\u597d\u597d\u4f11\u517b\uff0c\u4e0d\u53ef\u978d\u9a6c\u52b3\u987f\uff0c\u8fde\u591c\u5954\u6ce2\u3002\u201d \u3000\u3000\u5706\u6027\u8f6c\u8fc7\u5934\u6765\uff0c\u5411\u4ed6\u770b\u4e86\u4e00\u773c\uff0c\u5fc3\u4e2d\u5728\u8bf4\uff1a\u201c\u6211\u4f55\u5c1d\u4e0d\u77e5\u8be5\u5f53\u597d\u597d\u4f11\u517b\uff0c\u82e5\u4e0d\u662f\u4e3a\u4e86\u4f60\uff0c\u6211\u4f55\u5fc5\u978d\u9a6c\u52b3\u987f\uff0c\u8fde\u591c\u5954\u6ce2\uff1f\u201d \u3000\u3000\u95ee\u9053\uff1a\u201c\u7a0b\u5bb6\u59b9\u5b50\u5462\uff1f\u600e\u4e48\u4e0d\u89c1\u5979\u554a\uff1f\u201d \u3000\u3000\u80e1\u6590\u6cea\u76c8\u4e8e\u7736\uff0c\u98a4\u58f0\u9053\uff1a\u201c\u5979\u2026\u2026\u5979\u5df2\u53bb\u4e16\u4e86\u3002\u201d\u5706\u6027\u5927\u60ca\uff0c\u7ad9\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u201c\u600e\u2026\u2026\u600e\u4e48\u2026\u2026\u53bb\u4e16\u4e86\uff1f\u201d\u80e1\u6590\u9053\uff1a\u201c\u4f60\u5750\u4e0b\uff0c\u6162\u6162\u542c\u6211\u8bf4\u3002\u201d\u4e8e\u662f\u5c06\u81ea\u5df1\u5982\u4f55\u4e2d\u4e86\u77f3\u4e07\u55d4\u7684\u5267\u6bd2\u3001\u7a0b\u7075\u7d20\u5982\u4f55\u820d\u8eab\u76f8\u6551\u7b49\u60c5\u4e00\u4e00\u8bf4\u4e86\u3002\u5706\u6027\u9eef\u7136\u5782\u6cea\u3002\u826f\u4e45\u826f\u4e45\uff0c\u4e24\u4eba\u76f8\u5bf9\u65e0\u8bed\uff0c\u56de\u601d\u7a0b\u7075\u7d20\u7684\u4fa0\u9aa8\u67d4\u80a0\uff0c\u90fd\u662f\u96be\u4ee5\u81ea\u5df2\u3002 \u3000\u3000\u4e00\u9635\u79cb\u98ce\u5439\u6765\uff0c\u5bd2\u610f\u4fb5\u88ad\uff0c\u5706\u6027\u8f7b\u8f7b\u6253\u4e86\u4e2a\u98a4\u3002\u80e1\u6590\u8131\u4e0b\u8eab\u4e0a\u957f\u888d\uff0c\u62ab\u5728\u5979\u7684\u8eab\u4e0a\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u4f60\u7761\u4e00\u5ffd\u513f\u5427\u3002\u201d\u5706\u6027\u9053\uff1a\u201c\u4e0d\uff0c\u6211\u4e0d\u7761\u3002\u6211\u662f\u6765\u8ddf\u4f60\u8bf4\u4e00\u53e5\u8bdd\uff0c\u8fd9\u2026\u2026\u8fd9\u4fbf\u8981\u53bb\u3002\u201d \u3000\u3000\u80e1\u6590\u60ca\u9053\uff1a\u201c\u4f60\u5230\u54ea\u91cc\u53bb\uff1f\u201d\u5706\u6027\u51dd\u671b\u7740\u4ed6\uff0c\u8f7b\u8f7b\u9053\uff1a\u201c\u501f\u5982\u751f\u6b7b\u522b\uff0c\u5b89\u5f97\u957f\u82e6\u60b2\uff1f\u201d \u3000\u3000\u80e1\u6590\u542c\u4e86\u8fd9\u4e24\u53e5\u8bdd\uff0c\u4e0d\u7531\u5f97\u75f4\u4e86\uff0c\u8ddf\u7740\u4f4e\u58f0\u5ff5\u9053\uff1a\u201c\u501f\u5982\u751f\u6b7b\u522b\uff0c\u5b89\u5f97\u957f\u82e6\u60b2\uff1f\u201d \u3000\u3000\u5706\u6027\u9053\uff1a\u201c\u80e1\u5927\u54e5\uff0c\u6b64\u5730\u4e0d\u53ef\u4e45\u7559\uff0c\u4f60\u6025\u901f\u8fdc\u79bb\u4e3a\u662f\u3002\u6211\u5728\u9014\u4e2d\u5f97\u5230\u8baf\u606f\uff0c\u8d76\u6765\u8ddf\u4f60\u8bf4\u77e5\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u4ec0\u4e48\u8baf\u606f\uff1f\u201d\u5706\u6027\u9053\uff1a\u201c\u90a3\u65e5\u548c\u4f60\u522b\u540e\uff0c\u6211\u4fbf\u53bb\u8ffd\u5bfb\u6c64\u6c9b\u3002\u53ef\u662f\u8fd9\u8d3c\u5b50\u6ed1\u6e9c\u5f97\u7d27\uff0c\u7adf\u7ed9\u4ed6\u9003\u5f97\u4e0d\u77e5\u53bb\u5411\u3002\u6211\u60f3\u4ed6\u8001\u5bb6\u662f\u5728\u6e56\u5317\uff0c\u65e2\u662f\u5f97\u7f6a\u4e86\u798f\u5eb7\u5b89\uff0c\u5168\u5bb6\u90fd\u6709\u5e72\u7cfb\uff0c\u4ed6\u5b9a\u8981\u8bbe\u6cd5\u901a\u77e5\u5bb6\u4e2d\u8001\u5c0f\uff0c\u6025\u901f\u9003\u547d\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u4f60\u6599\u5f97\u4e0d\u9519\u3002\u201d\u5706\u6027\u9053\uff1a\u201c\u4ed6\u5916\u53f7\u53eb\u4f5c\u2018\u7518\u9716\u60e0\u4e03\u7701\u2019\uff0c\u6c5f\u6e56\u4e0a\u4ea4\u6e38\u6781\u5176\u5e7f\u9614\uff0c\u4f46\u60f3\u4ed6\u65e2\u662f\u4e2a\u5982\u6b64\u5978\u6ed1\u4e4b\u5f92\uff0c\u672a\u5fc5\u80fd\u5f53\u771f\u7ed3\u4ea4\u5230\u4ec0\u4e48\u597d\u670b\u53cb\u3002\u6b64\u523b\u5927\u7978\u4e34\u5934\uff0c\u975e\u81ea\u5df1\u8d76\u56de\u5bb6\u4e2d\u4e0d\u53ef\u3002\u4e8e\u662f\u6211\u5411\u897f\u5357\u65b9\u75be\u8ffd\u3002\u4e09\u5929\u4e4b\u540e\uff0c\u679c\u7136\u5728\u6e05\u98ce\u5e97\u8ffd\u4e0a\u4e86\u4ed6\u3002\u9ad8\u6881\u7530\u91cc\u4e00\u573a\u6076\u6218\uff0c\u7ec8\u4e8e\u4f7f\u8ba1\u51fb\u6bd9\u4e86\u8fd9\u8d3c\u5b50\uff0c\u4e0d\u8fc7\u6211\u53d7\u4f24\u4e5f\u662f\u4e0d\u8f7b\u3002\u201d\u80e1\u6590\u53f9\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u5706\u6027\u53c8\u9053\uff1a\u201c\u6211\u5728\u5ba2\u5e97\u517b\u4e86\u51e0\u5929\u4f24\uff0c\u89c1\u5230\u798f\u5eb7\u5b89\u624b\u4e0b\u7684\u6b66\u58eb\u63a5\u8fde\u4e24\u6279\u7ecf\u8fc7\uff0c\u5176\u4e2d\u6709\u90a3\u9e70\u722a\u96c1\u884c\u95e8\u7684\u5468\u94c1\u9e6a\u5728\u5185\uff0c\u4fbf\u4e0a\u524d\u62db\u547c\uff0c\u7ea6\u4ed6\u8bf4\u8bdd\u3002\u201d\u80e1\u6590\u60ca\u9053\uff1a\u201c\u4f60\u8eab\u4e0a\u6709\u4f24\uff0c\u4e0d\u6015\u4ed6\u8bb0\u4ec7\u4e48\uff1f\u201d \u3000\u3000\u5706\u6027\u5fae\u7b11\u9053\uff1a\u201c\u6211\u662f\u9001\u4ed6\u4e00\u4ef6\u5927\u5927\u529f\u540d\u3002\u4ed6\u5c31\u7b97\u672c\u6765\u6068\u6211\uff0c\u4e5f\u5c31\u4e0d\u6068\u4e86\u3002\u6211\u5c06\u57cb\u846c\u6c64\u6c9b\u5c38\u4f53\u7684\u5730\u65b9\u6307\u4e86\u7ed9\u4ed6\u770b\uff0c\u4ed6\u53ea\u8981\u5272\u4e86\u9996\u7ea7\u56de\u53bb\u5317\u4eac\uff0c\u4e0d\u662f\u5927\u529f\u4e00\u4ef6\u4e48\uff1f\u4ed6\u679c\u7136\u5f88\u611f\u6fc0\u6211\u3002\u6211\u8bf4\uff1a\u2018\u5468\u8001\u7237\uff0c\u4f60\u82e5\u662f\u5c06\u6211\u64d2\u53bb\uff0c\u81ea\u7136\u53c8\u662f\u4e00\u4ef6\u5927\u529f\uff0c\u53ea\u4e0d\u8fc7\u80e1\u6590\u80e1\u5927\u54e5\u4e00\u5b9a\u653e\u4f60\u4e0d\u8fc7\uff0c\u4ece\u524d\u7684\u8bb8\u591a\u4e8b\u60c5\uff0c\u90fd\u4e0d\u514d\u6296\u9732\u51fa\u6765\u3002\u2019\u90a3\u5468\u94c1\u9e6a\u5012\u5f88\u806a\u660e\uff0c\u8bf4\u9053\uff1a\u2018\u80e1\u5927\u54e5\u7684\u4e3a\u4eba\uff0c\u5144\u5f1f\u662f\u5f88\u4f69\u670d\u7684\uff0c\u51b3\u4e0d\u6562\u5f97\u7f6a\u4ed6\u7684\u670b\u53cb\u3002\u8bf7\u4f60\u8f6c\u544a\u80e1\u5927\u54e5\uff0c\u7530\u5f52\u519c\u7387\u9886\u4e86\u5927\u6279\u597d\u624b\uff0c\u8981\u5230\u6ca7\u5dde\u4ed6\u7956\u575f\u4e4b\u65c1\u57cb\u4f0f\uff0c\u6349\u62ff\u80e1\u5927\u54e5\u3002\u2019\u201d \u3000\u3000\u80e1\u6590\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u5728\u8fd9\u91cc\u57cb\u4f0f\uff1f\u201d\u5706\u6027\u9053\uff1a\u201c\u6b63\u662f\u3002\u6211\u542c\u5468\u94c1\u9e6a\u8fd9\u4e48\u8bf4\uff0c\u77e5\u9053\u4e0d\u5047\uff0c\u5f88\u662f\u7740\u6025\uff0c\u751f\u6015\u6765\u8fdf\u4e86\u4e00\u6b65\uff0c\u5509\uff0c\u8c22\u5929\u8c22\u5730\uff0c\u6ca1\u51fa\u4e71\u5b50\u2026\u2026\u201d \u3000\u3000\u80e1\u6590\u77a7\u7740\u5979\u6194\u60b4\u7684\u5bb9\u989c\uff0c\u5fc3\u60f3\uff1a\u201c\u4f60\u4e3a\u4e86\u6551\u6211\uff0c\u53ea\u6015\u6709\u51e0\u65e5\u51e0\u591c\u6ca1\u7761\u89c9\u4e86\u3002\u201d\u5706\u6027\u53c8\u9053\uff1a\u201c\u90a3\u7530\u5f52\u519c\u4f55\u4ee5\u77e5\u9053\u4f60\u7956\u575f\u846c\u5728\u6b64\u5904\uff1f\u53c8\u600e\u77e5\u4f60\u5b9a\u8981\u524d\u6765\u626b\u5893\uff1f\u80e1\u5927\u54e5\uff0c\u597d\u6c49\u654c\u4e0d\u8fc7\u4eba\u591a\uff0c\u773c\u524d\u4e14\u907f\u8fc7\u4e00\u6b65\u518d\u8bf4\u3002\u201d \u3000\u3000\u80e1\u6590\u9053\uff1a\u201c\u4eca\u65e5\u6211\u89c1\u5230\u82d7\u592b\u4eba\uff0c\u7ea6\u5979\u660e\u65e5\u518d\u6765\u6b64\u5904\u4f1a\u6664\u3002\u201d \u3000\u3000\u5706\u6027\u9053\uff1a\u201c\u82d7\u592b\u4eba\u662f\u8c01\uff1f\u201d\u80e1\u6590\u7ea6\u7565\u8bf4\u4e86\u3002\u5706\u6027\u6025\u9053\uff1a\u201c\u8fd9\u5973\u4eba\u8fde\u4e08\u592b\u5973\u513f\u5c1a\u53ea\u4e0d\u987e\uff0c\u80fd\u5b88\u4ec0\u4e48\u4fe1\u4e49\uff1f\u5feb\u8d81\u65e9\u8d70\u5427\u3002\u201d \u3000\u3000\u80e1\u6590\u89c9\u5f97\u82d7\u592b\u4eba\u5bf9\u4ed6\u7684\u795e\u6001\u5374\u4e0d\u4f3c\u4f5c\u4f2a\uff0c\u53c8\u5f88\u60f3\u77e5\u9053\u7236\u6bcd\u53bb\u4e16\u7684\u771f\u76f8\uff0c\u6781\u76fc\u518d\u548c\u82d7\u592b\u4eba\u4e00\u4f1a\uff0c\u5706\u6027\u9053\uff1a\u201c\u7530\u5f52\u519c\u5df2\u5728\u5de6\u8fd1\uff0c\u90a3\u82d7\u592b\u4eba\u5c82\u6709\u4e0d\u8ddf\u4ed6\u8bf4\u77e5\u4e4b\u7406\uff1f\u80e1\u5927\u54e5\uff0c\u4f60\u600e\u5730\u4e0d\u542c\u6211\u7684\u8bdd\uff1f\u6211\u8fde\u591c\u8d76\u6765\u53eb\u4f60\u907f\u7978\uff0c\u96be\u9053\u4f60\u7adf\u534a\u70b9\u4e5f\u4e0d\u628a\u6211\u653e\u5728\u5fc3\u4e0a\u4e48\uff1f\u201d\u80e1\u6590\u5fc3\u4e2d\u4e00\u51db\uff0c\u9053\uff1a\u201c\u4f60\u8bf4\u5f97\u5bf9\uff0c\u662f\u6211\u7684\u4e0d\u662f\u3002\u201d\u5706\u6027\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u662f\u8981\u4f60\u8ba4\u9519\u3002\u201d\u80e1\u6590\u8fc7\u53bb\u7275\u4e86\u9a6c\u7f30\uff0c\u9053\uff1a\u201c\u597d\uff0c\u4f60\u4e0a\u9a6c\u5427\u3002\u201d\u5706\u6027\u6b63\u8981\u4e0a\u9a6c\uff0c\u5ffd\u542c\u5f97\u56db\u9762\u516b\u65b9\u553f\u54e8\u58f0\u6b64\u8d77\u5f7c\u4f0f\uff0c\u654c\u4eba\u56db\u4e0b\u91cc\u653b\u5230\uff0c\u7adf\u5df2\u5c06\u575f\u5730\u56e2\u56e2\u56f4\u4f4f\u4e86\u3002 \u3000\u3000\u80e1\u6590\u54ac\u7259\u9053\uff1a\u201c\u8fd9\u5973\u4eba\u679c\u7136\u5c06\u6211\u5356\u4e86\u3002\u54b1\u4eec\u5f80\u897f\u95ef\u3002\u201d\u542c\u7740\u8fd9\u553f\u54e8\u4e4b\u58f0\uff0c\u4e0d\u7981\u6697\u81ea\u5fc3\u60ca\uff0c\u6765\u653b\u4e4b\u654c\u4eba\u7740\u5b9e\u4e0d\u5c11\uff0c\u5018\u82e5\u5706\u6027\u5e76\u672a\u53d7\u4f24\uff0c\u4e24\u4eba\u8981\u7a81\u56f4\u9003\u8d70\u539f\u662f\u4e0d\u96be\uff0c\u6b64\u523b\u5374\u6b8a\u65e0\u628a\u63e1\u3002 \u3000\u3000\u5706\u6027\u9053\uff1a\u201c\u4f60\u53ea\u7ba1\u5f80\u897f\u95ef\uff0c\u4e0d\u7528\u987e\u6211\u3002\u6211\u81ea\u6709\u8131\u8eab\u4e4b\u7b56\u3002\u201d \u3000\u3000\u80e1\u6590\u80f8\u53e3\u70ed\u8840\u4e0a\u6d8c\uff0c\u559d\u9053\uff1a\u201c\u54b1\u4fe9\u6b7b\u6d3b\u90fd\u5728\u4e00\u5757\uff01\u4f60\u80e1\u8bf4\u4e9b\u4ec0\u4e48\uff1f\u8ddf\u7740\u6211\u6765\u3002\u201d\u5706\u6027\u88ab\u4ed6\u8fd9\u4e48\u7c97\u58f0\u66b4\u6c14\u7684\u4e00\u559d\uff0c\u5fc3\u4e2d\u751c\u751c\u7684\u53cd\u89c9\u53d7\u7528\uff0c\u81ea\u77e5\u91cd\u4f24\u4e4b\u4f59\uff0c\u4e0d\u80fd\u4f7f\u52a8\u8f6f\u97ad\uff0c\u4e8e\u662f\u4e00\u63d0\u7f30\u7ef3\uff0c\u7eb5\u9a6c\u8ddf\u5728\u80e1\u6590\u8eab\u540e\u3002 \u3000\u3000\u80e1\u6590\u62d4\u5200\u5728\u624b\uff0c\u5954\u51fa\u6570\u4e08\uff0c\u4fbf\u89c1\u4e94\u4e2a\u4eba\u5f71\u5e76\u80a9\u62e6\u4e0a\uff0c\u4ed6\u5fc3\u60f3\uff1a\u201c\u4eca\u65e5\u8981\u8131\u51fa\u91cd\u56f4\uff0c\u987b\u5f97\u5200\u5200\u6740\u624b\uff0c\u53ef\u4e0d\u80fd\u6709\u534a\u5206\u5bb9\u60c5\u3002\u201d \u3000\u3000\u5f53\u4e0b\u5927\u8e0f\u6b65\u76f4\u95ef\u8fc7\u53bb\uff0c\u867d\u662f\u4ee5\u5be1\u654c\u4f17\uff0c\u4ecd\u662f\u5e76\u4e0d\u5148\u884c\u51fa\u624b\uff0c\u5b88\u7740\u540e\u53d1\u5236\u4eba\u7684\u8981\u8bc0\uff0c\u5de6\u80a9\u524d\u5f15\uff0c\u5de6\u638c\u659c\u4f38\uff0c\u53f3\u624b\u63d0\u5200\uff0c\u5782\u5728\u817f\u65c1\u3002 \u3000\u3000\u4e24\u540d\u798f\u5eb7\u5b89\u5e9c\u4e2d\u7684\u6b66\u58eb\u4e00\u6267\u94c1\u97ad\uff0c\u4e00\u633a\u9b3c\u5934\u5200\uff0c\u9f50\u58f0\u5406\u559d\uff0c\u5206\u4ece\u5de6\u53f3\u5411\u4ed6\u5934\u9876\u7838\u4e0b\u3002\u80e1\u6590\u4e00\u89c1\u51fa\u624b\uff0c\u4fbf\u77e5\u4e24\u4eba\u7684\u6b66\u529f\u90fd\u751a\u4e86\u5f97\uff0c\u53ea\u8981\u4e00\u63a5\u4e0a\u624b\uff0c\u975e\u9877\u523b\u95f4\u53ef\u4ee5\u53d6\u80dc\uff0c\u4f59\u4eba\u4e00\u7ecf\u5408\u56f4\uff0c\u8981\u8131\u8eab\u4fbf\u5343\u96be\u4e07\u96be\uff0c\u4e8e\u662f\u659c\u8eab\u9ad8\u7eb5\uff0c\u547c\u7684\u4e00\u5200\uff0c\u5f80\u4e94\u4eba\u4e2d\u6700\u5de6\u4e00\u4eba\u780d\u53bb\u3002\u90a3\u6b66\u58eb\u624b\u4f7f\u957f\u5251\uff0c\u4e3e\u5251\u6321\u67b6\u3002\u80e1\u6590\u8eab\u5728\u534a\u7a7a\uff0c\u5185\u52b2\u8fd0\u5411\u5200\u4e0a\uff0c\u62cd\u62cd\u4e24\u817f\uff0c\u5feb\u5982\u95ea\u7535\u822c\u8e22\u5728\u7b2c\u56db\u540d\u6b66\u58eb\u80f8\u53e3\uff0c\u90a3\u6b66\u58eb\u76f4\u98de\u51fa\u53bb\uff0c\u53e3\u4e2d\u72c2\u55b7\u9c9c\u8840\u3002\u4f7f\u5251\u7684\u6b66\u58eb\u4f46\u89c9\u5175\u5203\u4e0a\u4e00\u80a1\u5de8\u529b\u4f20\u5230\u624b\u81c2\uff0c\u53c8\u538b\u4e0a\u5fc3\u53e3\uff0c\u7acb\u89c9\u524d\u80f8\u540e\u80cc\u6570\u5341\u6839\u808b\u9aa8\u4f3c\u5df2\u4e00\u9f50\u6298\u65ad\uff0c\u4e00\u58f0\u4e5f\u6ca1\u51fa\uff0c\u4fbf\u6b64\u6655\u6b7b\u8fc7\u53bb\u3002 \u3000\u3000\u4f17\u6b66\u58eb\u89c1\u4ed6\u5728\u4e24\u62db\u4e4b\u5185\u4f24\u4e86\u4e24\u4e2a\u540c\u4f34\uff0c\u65e0\u4e0d\u9707\u9a87\u3002\u90a3\u4f7f\u9b3c\u5934\u5200\u7684\u6b66\u58eb\u559d\u9053\uff1a\u201c\u80e1\u5927\u7237\uff0c\u679c\u7136\u597d\u529f\u592b\uff0c\u5728\u4e0b\u53f8\u5f92\u96f7\u9886\u6559\u3002\u201d \u3000\u3000\u90a3\u4f7f\u94c1\u97ad\u7684\u9053\uff1a\u201c\u5728\u4e0b\u8c22\u4e0d\u6321\u9886\u6559\u9ad8\u62db\u3002\u201d\u80e1\u6590\u53eb\u9053\uff1a\u201c\u597d\uff01\u201d\u5355\u5200\u73af\u8eab\u4e00\u7ed5\uff0c\u98d5\u98d5\u98d5\u5200\u5149\u95ea\u52a8\uff0c\u4e09\u4e0b\u865a\u62db\uff0c\u548c\u8eab\u538b\u5c06\u8fc7\u53bb\u3002\u53f8\u5f92\u96f7\u548c\u8c22\u4e0d\u6321\u6025\u9000\u4e24\u6b65\u3002\u7b2c\u4e09\u540d\u6b66\u58eb\u53eb\u9053\uff1a\u201c\u5728\u4e0b\u4e1c\u65b9\u2026\u2026\u201d \u3000\u3000\u53ea\u8bf4\u5230\u7b2c\u56db\u4e2a\u5b57\uff0c\u80e1\u6590\u7684\u5200\u80cc\u5df2\u7830\u4e00\u58f0\uff0c\u51fb\u5728\u4ed6\u7684\u540e\u8111\uff0c\u8111\u9aa8\u7c89\u788e\uff0c\u7acb\u65f6\u6bd9\u547d\uff0c\u7adf\u662f\u4e0d\u77e5\u4ed6\u53eb\u4e1c\u65b9\u4ec0\u4e48\u540d\u5b57\u3002 \u3000\u3000\u53f8\u5f92\u96f7\u548c\u8c22\u4e0d\u6321\u4e25\u5b88\u4f4f\u95e8\u6237\uff0c\u53c8\u9000\u4e86\u4e24\u6b65\uff0c\u5374\u4e0d\u5bb9\u80e1\u6590\u51b2\u8fc7\u3002\u553f\u54e8\u58f0\u4e2d\uff0c\u56db\u540d\u6b66\u58eb\u5954\u5230\u53f8\u5f92\u96f7\u548c\u8c22\u4e0d\u6321\u8eab\u540e\uff0c\u5e76\u80a9\u5c55\u5f00\u3002 \u3000\u3000\u80e1\u6590\u867d\u5728\u77ac\u606f\u4e4b\u95f4\u63a5\u8fde\u4f24\u6bd9\u4e09\u540d\u654c\u4eba\uff0c\u4f46\u90a3\u53f8\u5f92\u96f7\u548c\u8c22\u4e0d\u6321\u9887\u6709\u89c1\u8bc6\uff0c\u7adf\u4e0d\u4e0a\u524d\u63a5\u6218\uff0c\u8fde\u9000\u4e24\u6b21\uff0c\u62e6\u4f4f\u4ed6\u7684\u53bb\u8def\u3002\u80e1\u6590\u5fc3\u4e2d\u6697\u6697\u53eb\u82e6\uff0c\u4f7f\u62db\u201c\u591c\u6218\u516b\u65b9\u85cf\u5200\u5f0f\u201d\uff0c\u5411\u524d\u4e00\u653b\uff0c\u4ee5\u5de6\u8db3\u4e3a\u8f74\uff0c\u8f6c\u4e86\u4e2a\u5708\u5b50\u3002 \u3000\u3000\u8fd9\u4e48\u4e00\u8f6c\uff0c\u5df2\u6570\u6e05\u4e86\u654c\u65b9\u4eba\u6570\uff0c\u897f\u8fb9\u516d\u4eba\uff0c\u4e1c\u8fb9\u516b\u4eba\uff0c\u5357\u5317\u5404\u662f\u4e94\u4eba\uff0c\u4f24\u6bd9\u7684\u4e09\u4eba\u4e0d\u7b97\uff0c\u5bf9\u65b9\u7adf\u662f\u5c1a\u6709\u4e8c\u5341\u56db\u4eba\u3002 \u3000\u3000\u5ffd\u542c\u4e00\u4eba\u6717\u58f0\u957f\u7b11\uff0c\u58f0\u97f3\u6e05\u8d8a\uff0c\u8ddf\u7740\u8bf4\u9053\uff1a\u201c\u80e1\u5144\u5f1f\uff0c\u5e78\u4f1a\uff0c\u5e78\u4f1a\u3002\u6bcf\u89c1\u4f60\u4e00\u6b21\uff0c\u4f60\u6b66\u529f\u4fbf\u957f\u8fdb\u4e00\u5c42\uff0c\u5f53\u771f\u662f\u82f1\u96c4\u51fa\u5728\u5c11\u5e74\uff0c\u4e86\u4e0d\u8d77\u554a\u4e86\u4e0d\u8d77\uff01\u201d\u6b63\u662f\u7530\u5f52\u519c\u7684\u58f0\u97f3\u81ea\u5357\u8fb9\u4f20\u6765\u3002 \u3000\u3000\u80e1\u6590\u4e0d\u52a0\u7406\u4f1a\uff0c\u51dd\u89c6\u7740\u897f\u65b9\u7684\u516d\u540d\u654c\u4eba\uff0c\u53ea\u542c\u90a3\u56db\u540d\u6ca1\u62a5\u8fc7\u540d\u7684\u6b66\u58eb\u5206\u522b\u8bf4\u9053\uff1a\u201c\u5728\u4e0b\u5f20\u5b81\uff01\u201d\u201c\u5728\u4e0b\u4e01\u6587\u6c9b\u9886\u6559\u3002\u201d \u3000\u3000\u201c\u5728\u4e0b\u4e01\u6587\u6df1\u89c1\u8fc7\u80e1\u5927\u7237\uff01\u201d\u201c\u563f\u563f\uff0c\u8001\u592b\u9648\u656c\u592b\uff01\u201d \u3000\u3000\u80e1\u6590\u5411\u524d\u4e00\u51b2\uff0c\u7a81\u7136\u8f6c\u800c\u5411\u5317\uff0c\u5de6\u624b\u4f38\u6307\u5411\u5317\u65b9\u7b2c\u4e8c\u540d\u6b66\u58eb\u80f8\u53e3\u70b9\u53bb\u3002\u90a3\u4eba\u624b\u6301\u4e00\u5bf9\u5224\u5b98\u7b14\uff0c\u6b63\u662f\u6253\u7a74\u7684\u597d\u624b\uff0c\u89c1\u5bf9\u65b9\u4f38\u6307\u70b9\u6765\uff0c\u53f3\u624b\u5224\u5b98\u7b14\u500f\u5730\u4f38\u51fa\uff0c\u70b9\u5411\u4ed6\u53f3\u80a9\u7684\u201c\u7f3a\u76c6\u7a74\u201d\u3002\u8fd9\u4e00\u62db\u53cd\u5b88\u4e3a\u653b\uff0c\u5b9e\u662f\u6781\u5389\u5bb3\u7684\u6740\u7740\uff0c\u80e1\u6590\u867d\u7136\u51fa\u624b\u5728\u5148\uff0c\u4f46\u90a3\u4eba\u7684\u5224\u5b98\u7b14\u957f\u4e86\u4e8c\u5c3a\u4e8c\u5bf8\uff0c\u773c\u770b\u80e1\u6590\u624b\u6307\u5c1a\u672a\u78b0\u5230\u90a3\u4eba\u7a74\u9053\uff0c\u81ea\u5df1\u7f3a\u76c6\u7a74\u5148\u8981\u88ab\u70b9\u3002\u4e0d\u6599\u80e1\u6590\u5de6\u624b\u4e00\u63a0\uff0c\u5df2\u6293\u4f4f\u4e86\u5224\u5b98\u7b14\uff0c\u7528\u529b\u5411\u524d\u4e00\u9001\uff0c\u90a3\u4eba\u201c\u563f\u201d\u7684\u4e00\u58f0\u95f7\u54fc\uff0c\u5224\u5b98\u7b14\u7684\u7b14\u6746\u5df2\u63d2\u5165\u4ed6\u7684\u54bd\u5589\u3002 \u3000\u3000\u4fbf\u5728\u6b64\u65f6\uff0c\u53ea\u542c\u5f97\u8eab\u540e\u4e24\u4eba\u53eb\u9053\uff1a\u201c\u5728\u4e0b\u9ec4\u6a35\uff01\u201d\u201c\u5728\u4e0b\u4f0d\u516c\u6743\uff01\u201d\u91d1\u5203\u5288\u98ce\u4e4b\u58f0\uff0c\u5df2\u63a0\u5230\u80cc\u5fc3\u3002\u80e1\u6590\u5411\u524d\u4e00\u6251\uff0c\u4e24\u67c4\u5355\u5200\u90fd\u780d\u4e86\u4e2a\u7a7a\uff0c\u4ed6\u987a\u52bf\u56de\u8fc7\u5355\u5200\uff0c\u5237\u7684\u4e00\u4e0b\uff0c\u4ece\u4e0b\u800c\u4e0a\u7684\u65a9\u5411\u9ec4\u6a35\u624b\u8155\u3002\u8fd9\u4e00\u62db\u662f\u80e1\u5bb6\u5200\u6cd5\u4e2d\u7684\u7cbe\u5999\u4e4b\u7740\uff0c\u6b66\u529f\u518d\u5f3a\u7684\u4eba\u4e5f\u987b\u7740\u4e86\u9053\u513f\u3002\u4e0d\u6599\u9ec4\u6a35\u7cbe\u4e8e\u5341\u516b\u8def\u5927\u64d2\u62ff\u624b\uff0c\u5e94\u53d8\u6700\u5feb\uff0c\u773c\u89c1\u5200\u950b\u524a\u4e0a\u624b\u8155\uff0c\u5371\u6025\u4e2d\u629b\u53bb\u5175\u5203\uff0c\u624b\u8155\u4e00\u7ffb\uff0c\u4f38\u6307\u5f84\u6765\u6293\u80e1\u6590\u5355\u5200\u7684\u5200\u80cc\u3002\u522b\u77a7\u4ed6\u4e24\u6487\u9f20\u987b\uff0c\u5934\u5c0f\u773c\u7ec6\uff0c\u5f62\u8c8c\u9887\u4e3a\u7325\u5d3d\uff0c\u8fd9\u4e00\u4e0b\u53d8\u62db\u7adf\u662f\u6bd4\u80e1\u6590\u8fd8\u8981\u8fc5\u6377\uff0c\u4e94\u6839\u9e21\u722a\u822c\u7684\u624b\u6307\u4e00\u6296\uff0c\u5df2\u6293\u4f4f\u4e86\u5200\u80cc\u3002\u80e1\u6590\u4ed7\u7740\u529b\u5927\uff0c\u6325\u5200\u5411\u524d\u780d\u51fa\uff0c\u4e0d\u6599\u8fd9\u9ec4\u6a35\u8182\u529b\u4e5f\u662f\u4e0d\u5c0f\uff0c\u6293\u4f4f\u4e86\u5200\u80cc\uff0c\u80e1\u6590\u8fd9\u4e00\u5200\u5c45\u7136\u6ca1\u80fd\u780d\u51fa\u3002\u5c31\u8fd9\u4e48\u5446\u5f97\u4e00\u5446\uff0c\u8eab\u540e\u53c8\u6709\u4e09\u4eba\u540c\u65f6\u653b\u5230\u3002 \u3000\u3000\u80e1\u6590\u4f30\u8ba1\u60c5\u52bf\uff0c\u5f85\u5f97\u80cc\u540e\u4e09\u4eba\u653b\u5230\uff0c\u5c1a\u6709\u4e00\u77ac\u4f59\u6687\uff0c\u987b\u5f53\u5728\u8fd9\u7247\u523b\u95f4\u6599\u7406\u4e86\u9ec4\u6a35\uff0c\u6b64\u65f6\u9677\u8eab\u91cd\u56f4\uff0c\u773c\u524d\u8fd9\u4eba\u53c8\u5b9e\u662f\u52b2\u654c\uff0c\u82e5\u80fd\u4f24\u5f97\u4e86\u4ed6\uff0c\u4fbf\u51cf\u53bb\u4e00\u5206\u5a01\u80c1\u3002\u5f53\u4e0b\u7a81\u7136\u64a4\u624b\u79bb\u5200\uff0c\u53cc\u638c\u51fb\u51fa\uff0c\u7830\u7684\u4e00\u54cd\uff0c\u6253\u5728\u4ed6\u7684\u80f8\u53e3\u3002\u9ec4\u6a35\u4e00\u5446\uff0c\u7adf\u7136\u5e76\u4e0d\u6454\u5012\uff0c\u4f46\u6293\u7740\u5355\u5200\u7684\u624b\u6307\u5374\u7ec8\u4e8e\u653e\u5f00\u4e86\u3002\u80e1\u6590\u4e00\u63a2\u624b\uff0c\u53c8\u5df2\u6293\u4f4f\u5200\u67c4\uff0c\u56de\u8fc7\u8eab\u6765\uff0c\u67b6\u4f4f\u4e86\u4e09\u822c\u5175\u5668\u3002 \u3000\u3000\u90a3\u4e09\u540d\u6b66\u58eb\u4e00\u4e2a\u4f0d\u516c\u6743\uff0c\u4e00\u4e2a\u662f\u8001\u5934\u9648\u656c\u592b\uff0c\u53e6\u4e00\u4e2a\u8eab\u6750\u9b41\u68a7\uff0c\u6bd4\u80e1\u6590\u51e0\u4e4e\u9ad8\u51fa\u4e00\u4e2a\u534a\u5934\uff0c\u624b\u4e2d\u4f7f\u7684\u662f\u6839\u719f\u94dc\u68cd\uff0c\u8db3\u8db3\u6709\u56db\u5341\u4f59\u65a4\uff0c\u6781\u662f\u6c89\u91cd\u3002\u80e1\u6590\u4e00\u6321\u4e4b\u4e0b\uff0c\u80f8\u53e3\u4fbf\u662f\u4e00\u9707\uff0c\u5f85\u8981\u8dc3\u5f00\uff0c\u5de6\u53f3\u53c8\u662f\u4e24\u4eba\u653b\u5230\u3002 \u3000\u3000\u5706\u6027\u9a91\u9a6c\u5728\u540e\uff0c\u4f17\u6b66\u58eb\u90fd\u5728\u56f4\u653b\u80e1\u6590\uff0c\u4e00\u65f6\u6ca1\u4eba\u7406\u5979\u3002\u5979\u867d\u4f24\u91cd\u4e4f\u529b\uff0c\u4f46\u80e1\u6590\u529b\u4f24\u4e94\u4eba\u7684\u7ecf\u8fc7\uff0c\u5374\u662f\u4e00\u62db\u4e00\u5f0f\uff0c\u5168\u90fd\u770b\u5f97\u6e05\u6e05\u695a\u695a\u3002\u5979\u5168\u5fc3\u5173\u6000\u80e1\u6590\u5b89\u5371\uff0c\u80e1\u6590\u7684\u4e00\u95ea\u4e00\u907f\uff0c\u4fbf\u5982\u5979\u81ea\u5df1\u8eb2\u8ba9\u4e00\u822c\uff0c\u4e00\u5200\u4e00\u638c\uff0c\u4fbf\u4f3c\u5979\u81ea\u5df1\u51fa\u624b\uff0c\u773c\u89c1\u4ed6\u8eab\u53d7\u4e94\u4eba\u56f4\u653b\uff0c\u60c5\u52bf\u5371\u6025\uff0c\u5f53\u5373\u4e00\u63d0\u7f30\u7ef3\uff0c\u7eb5\u9a6c\u4fbf\u51b2\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u5979\u9a6c\u97ad\u4e00\u6325\uff0c\u4f7f\u4e00\u62db\u8f6f\u97ad\u97ad\u6cd5\u4e2d\u7684\u201c\u9633\u5173\u6298\u67f3\u201d\uff0c\u5df2\u5708\u4f4f\u90a3\u9b41\u68a7\u5927\u6c49\u7684\u5934\u9888\u3002\u90a3\u5927\u6c49\u6b63\u5728\u81ea\u62a5\u59d3\u540d\uff1a\u201c\u5728\u4e0b\u9ad8\u4e00\u529b\u9886\u6559\u2026\u2026\u201d\u7a81\u7136\u5589\u5934\u4e00\u7d27\uff0c\u5df2\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002\u4ed6\u529b\u6c14\u867d\u5927\uff0c\u4f46\u4e00\u6765\u731b\u5730\u91cc\u547c\u5438\u95ed\u585e\uff0c\u4e8c\u6765\u603b\u662f\u654c\u4e0d\u4f4f\u9a6c\u5339\u7684\u4e00\u51b2\uff0c\u767b\u65f6\u7acb\u8db3\u4e0d\u5b9a\uff0c\u88ab\u9a6c\u5339\u6a2a\u62d6\u800c\u53bb\uff0c\u8fde\u65c1\u8fb9\u7684\u5f20\u5b81\u4e5f\u4e00\u8d77\u5e26\u5012\u3002 \u3000\u3000\u80e1\u6590\u8eab\u65c1\u5c11\u4e86\u4e24\u654c\uff0c\u5237\u5237\u4e24\u5200\uff0c\u5df2\u5c06\u4e01\u6587\u6c9b\u3001\u4e01\u6587\u6df1\u5144\u5f1f\u780d\u7ffb\u5728\u5730\uff0c\u7a81\u89c9\u80cc\u540e\u98ce\u58f0\u98d2\u7136\uff0c\u6709\u4eba\u6b3a\u5230\uff0c\u4e0d\u53ca\u8f6c\u8eab\uff0c\u53cd\u624b\u201c\u5012\u5367\u864e\u602a\u87d2\u7ffb\u8eab\u201d\uff0c\u4e00\u5200\u56de\u65ab\uff0c\u53ea\u542c\u5f97\u201c\u53ee\u201d\u7684\u4e00\u58f0\u8f7b\u54cd\uff0c\u624b\u4e0a\u4e00\u8f7b\uff0c\u5355\u5200\u5df2\u88ab\u654c\u4eba\u7684\u5229\u5203\u524a\u65ad\uff0c\u654c\u5203\u8ddf\u7740\u4fbf\u987a\u52bf\u63a8\u5230\u3002 \u3000\u3000\u80e1\u6590\u5927\u60ca\uff0c\u5de6\u8db3\u4e00\u70b9\uff0c\u5411\u524d\u76f4\u7eb5\u51fa\u4e08\u4f59\uff0c\u4f46\u603b\u662f\u6162\u4e86\u7247\u523b\uff0c\u5de6\u80a9\u80cc\u4e00\u9635\u5267\u75db\uff0c\u5df2\u770b\u6e05\u695a\u5077\u88ad\u7684\u6b63\u662f\u7530\u5f52\u519c\uff0c\u4e0d\u7531\u5f97\u6697\u6697\u5fc3\u60ca\uff0c\u7530\u5f52\u519c\u6b66\u529f\u4e5f\u4e0d\u600e\u4e48\uff0c\u53ef\u662f\u4ed6\u8fd9\u67c4\u5b9d\u5200\u950b\u9510\u7edd\u4f26\uff0c\u5b9e\u6240\u96be\u5f53\u3002 \u3000\u3000\u4ed6\u53f3\u8db3\u843d\u5730\uff0c\u5de6\u638c\u62cd\u51fa\uff0c\u53f3\u624b\u53cd\u52fe\uff0c\u5df2\u4ece\u4e00\u540d\u6b66\u58eb\u624b\u4e2d\u62a2\u5230\u4e00\u67c4\u5355\u5200\uff0c\u8ddf\u7740\u53cd\u624b\u4e00\u5200\uff0c\u8fd9\u62db\u7a7a\u624b\u593a\u767d\u5203\u5e72\u51c0\u5229\u843d\u4e4b\u6781\uff0c\u53cd\u624b\u56de\u653b\u53c8\u662f\u51cc\u5389\u72e0\u8fa3\u65e0\u6bd4\uff0c\u8981\u77e5\u654c\u4eba\u624b\u6301\u5229\u5203\u8ddf\u8e2a\u800c\u81f3\uff0c\u5176\u95f4\u76f8\u5dee\u53ea\u662f\u4e00\u7ebf\uff0c\u53ea\u6d88\u6162\u5f97\u77ac\u606f\uff0c\u4fbf\u662f\u4ee5\u81ea\u5df1\u8840\u8089\u4e4b\u8eaf\uff0c\u53bb\u5582\u7530\u5f52\u519c\u624b\u4e2d\u90a3\u5929\u9f99\u95e8\u9547\u95e8\u4e4b\u5b9d\u7684\u5b9d\u5200\u4e86\u3002\u80e1\u6590\u4e0d\u6562\u4ee5\u5355\u5200\u548c\u654c\u4eba\u5b9d\u5200\u5bf9\u78b0\uff0c\u4e00\u5473\u817e\u632a\u95ea\u8dc3\uff0c\u5c55\u5f00\u8f7b\u8eab\u529f\u592b\u548c\u4ed6\u6e38\u6597\u3002\u4f46\u62c6\u5f97\u4e03\u516b\u62db\uff0c\u5341\u4f59\u540d\u654c\u4eba\u4e00\u9f50\u56f4\u4e86\u4e0a\u6765\uff0c\u53e6\u6709\u4e09\u4eba\u53bb\u653b\u51fb\u5706\u6027\u3002\u80e1\u6590\u5fae\u4e00\u5206\u5fc3\uff0c\u5f53\u7684\u4e00\u54cd\uff0c\u5355\u5200\u53c8\u88ab\u5b9d\u5200\u524a\u65ad\u3002 \u3000\u3000\u8fd9\u67c4\u5b9d\u5200\u7684\u950b\u5229\uff0c\u5b9e\u662f\u5230\u4e86\u524a\u94c1\u5982\u6ce5\u7684\u5730\u6b65\u3002 \u3000\u3000\u7530\u5f52\u519c\u6709\u5fc3\u8981\u7f6e\u80e1\u6590\u6b7b\u5730\uff0c\u5bd2\u5149\u95ea\u95ea\uff0c\u624b\u4e2d\u5b9d\u5200\u7684\u62db\u6570\u4e00\u62db\u7d27\u4f3c\u4e00\u62db\u3002\u4ed6\u5e73\u65f6\u4f7f\u5251\uff0c\u7528\u5200\u5e76\u4e0d\u987a\u624b\uff0c\u4f46\u8fd9\u67c4\u5200\u950b\u5229\u7edd\u4f26\uff0c\u53ea\u987b\u968f\u624b\u6325\u821e\uff0c\u80e1\u6590\u5df2\u51b3\u8ba1\u4e0d\u6562\u6484\u5176\u950b\u8292\u3002\u4ed6\u4f7f\u5f00\u5b9d\u5200\uff0c\u76f4\u903c\u800c\u524d\u3002 \u3000\u3000\u80e1\u6590\u60f3\u518d\u62a2\u4ef6\u5175\u5203\u62db\u67b6\uff0c\u4f46\u5200\u67aa\u4e1b\u4e2d\uff0c\u7adf\u662f\u7f13\u4e0d\u51fa\u624b\u6765\uff0c\u55e4\u7684\u4e00\u58f0\uff0c\u5de6\u80a9\u53c8\u88ab\u4e00\u540d\u6b66\u58eb\u7684\u82b1\u67aa\u67aa\u5c16\u5212\u4e86\u957f\u957f\u4e00\u6761\u53e3\u5b50\u3002 \u3000\u3000\u4f17\u6b66\u58eb\u5927\u53eb\u8d77\u6765\uff1a\u201c\u59d3\u80e1\u7684\u6295\u964d\u5427\uff01\u201d\u201c\u4f60\u662f\u6761\u597d\u6c49\u5b50\uff0c\u4f55\u82e6\u5728\u8fd9\u91cc\u6789\u81ea\u9001\u4e86\u6027\u547d\uff1f\u201d\u201c\u6211\u4eec\u4eba\u591a\uff0c\u4f60\u5be1\u4e0d\u654c\u4f17\uff0c\u8ba4\u8f93\u7f62\u5566\uff0c\u4e0d\u5931\u9762\u5b50\u3002\u201d\u7530\u5f52\u519c\u5374\u4e00\u8a00\u4e0d\u53d1\uff0c\u5200\u5200\u72e0\u8fa3\u7684\u8fdb\u653b\u3002 \u3000\u3000\u80e1\u6590\u80a9\u80cc\u4f24\u53e3\u5947\u75db\uff0c\u773c\u770b\u4fbf\u8981\u547d\u4e27\u5f53\u5730\uff0c\u5ffd\u542c\u5f97\u4e00\u4e2a\u5973\u5b50\u58f0\u97f3\u53eb\u9053\uff1a\u201c\u5927\u54e5\uff0c\u522b\u4f24\u8fd9\u5c11\u5e74\u7684\u6027\u547d\u3002\u201d\u80e1\u6590\u867d\u5728\u54ac\u7259\u9163\u6597\uff0c\u4ecd\u542c\u5f97\u51fa\u662f\u82d7\u592b\u4eba\u7684\u58f0\u97f3\uff0c\u559d\u9053\uff1a\u201c\u8c01\u8981\u4f60\u5047\u4ec1\u5047\u4e49\uff1f\u201d\u5fd9\u4e71\u4e4b\u4e2d\uff0c\u8170\u773c\u91cc\u53c8\u88ab\u4eba\u8e22\u4e2d\u4e00\u817f\u3002\u80e1\u6590\u6012\u6781\uff0c\u53f3\u624b\u75be\u4f38\uff0c\u6293\u4f4f\u4e86\u90a3\u4eba\u8db3\u8e1d\uff0c\u63d0\u5c06\u8d77\u6765\uff0c\u626b\u4e86\u4e2a\u5708\u5b50\u3002\u4f17\u6b66\u58eb\u5fc3\u6709\u987e\u5fcc\uff0c\u4e00\u65f6\u5012\u4e5f\u4e0d\u6562\u8fc7\u5206\u903c\u8fd1\u3002\u80e1\u6590\u624b\u4e2d\u6240\u6293\u4e4b\u4eba\u6b63\u662f\u5f20\u5b81\uff0c\u4ed6\u5175\u5203\u8131\u624b\uff0c\u88ab\u80e1\u6590\u7529\u5f97\u5934\u6655\u8111\u80c0\uff0c\u6323\u624e\u4e0d\u8131\u3002 \u3000\u3000\u80e1\u6590\u89c1\u5706\u6027\u5728\u9a6c\u4e0a\u4e1c\u95ea\u897f\u907f\uff0c\u90a3\u5750\u9a91\u4e5f\u5df2\u4e2d\u4e86\u51e0\u5200\uff0c\u4e0d\u4f4f\u60b2\u5636\uff0c\u5f53\u4e0b\u63d0\u8d77\u5f20\u5b81\uff0c\u51b2\u5230\u5706\u6027\u8eab\u524d\uff0c\u53eb\u9053\uff1a\u201c\u8ddf\u6211\u6765\uff01\u201d\u5706\u6027\u4e00\u8dc3\u4e0b\u9a6c\uff0c\u4e24\u4eba\u5954\u5230\u4e86\u80e1\u4e00\u5200\u7684\u5893\u65c1\u3002\u5893\u8fb9\u7684\u67cf\u6811\u5df2\u9ad8\uff0c\u4e24\u4eba\u501a\u6811\u800c\u6597\uff0c\u654c\u4eba\u56f4\u653b\u8f83\u96be\u3002\u80e1\u6590\u63d0\u8d77\u5f20\u5b81\uff0c\u559d\u9053\uff1a\u201c\u4f60\u4eec\u8981\u4e0d\u8981\u4ed6\u7684\u6027\u547d\uff1f\u201d \u3000\u3000\u7530\u5f52\u519c\u53eb\u9053\uff1a\u201c\u6740\u5f97\u53cd\u8d3c\u80e1\u6590\uff0c\u798f\u5927\u5e05\u91cd\u91cd\u6709\u8d4f\uff01\u201d\u8a00\u4e0b\u4e4b\u610f\uff0c\u7adf\u662f\u8bf4\u5f20\u5b81\u662f\u6b7b\u662f\u6d3b\uff0c\u5e76\u65e0\u5e72\u7cfb\u3002\u4ed6\u773c\u89c1\u4f17\u4eba\u8fdf\u7591\uff0c\u81ea\u5df1\u4fbf\u6325\u5200\u51b2\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u80e1\u6590\u77e5\u9053\u6293\u4f4f\u5f20\u5b81\uff0c\u4e0d\u8db3\u4ee5\u8981\u80c1\u654c\u4eba\u9000\u5f00\uff0c\u5fc3\u60f3\u7530\u5f52\u519c\u5b9d\u5200\u5728\u624b\uff0c\u6b66\u529f\u53c8\u9ad8\uff0c\u8981\u6293\u4ed6\u662f\u6781\u4e0d\u5bb9\u6613\uff0c\u6700\u597d\u662f\u6293\u4f4f\u82d7\u592b\u4eba\u4e3a\u4eba\u8d28\uff0c\u53ef\u662f\u5979\u7ad9\u5f97\u8fdc\u8fdc\u7684\uff0c\u76f8\u8ddd\u5341\u4f59\u4e08\u4e4b\u9065\uff0c\u65e0\u8bba\u5982\u4f55\u51b2\u4e0d\u8fc7\u53bb\u3002\u4f46\u89c1\u7530\u5f52\u519c\u4e00\u6b65\u6b65\u7684\u8d70\u8fd1\uff0c\u5f53\u4e0b\u5728\u5f20\u5b81\u8eab\u8fb9\u4e00\u6478\uff0c\u77a7\u4ed6\u8170\u95f4\u662f\u5426\u5e26\u5f97\u6709\u77ed\u5200\u3001\u5315\u9996\u4e4b\u7c7b\uff0c\u4e5f\u53ef\u7528\u4ee5\u62b5\u6321\u4e00\u9635\u3002\u4e00\u6478\u4e4b\u4e0b\uff0c\u89e6\u624b\u662f\u4e2a\u6c89\u7538\u7538\u7684\u9556\u56ca\uff0c\u80e1\u6590\u5de6\u624b\u70b9\u4e86\u4ed6\u7a74\u9053\uff0c\u53f3\u624b\u6458\u4e0b\u9556\u56ca\uff0c\u6478\u51fa\u4e00\u679d\u94a2\u9556\uff0c\u6382\u4e86\u6382\u4efd\u91cf\uff0c\u89c9\u5f97\u9887\u4e3a\u6c89\u91cd\uff0c\u770b\u51c6\u7530\u5f52\u519c\u7684\u5c0f\u8179\uff0c\u529b\u8fd0\u53f3\u81c2\uff0c\u547c\u7684\u4e00\u58f0\uff0c\u63b7\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u9556\u91cd\u52b2\u5927\uff0c\u53bb\u52bf\u6781\u731b\uff0c\u7530\u5f52\u519c\u5f85\u5f97\u60ca\u89c9\uff0c\u94a2\u9556\u8ddd\u5c0f\u8179\u5df2\u4e0d\u8fc7\u534a\u5c3a\uff0c\u6025\u5fd9\u6325\u5200\u4e00\u683c\u3002\u94a2\u9556\u867d\u7136\u7acb\u65f6\u65a9\u4e3a\u4e24\u622a\uff0c\u4f46\u9556\u5c16\u4f59\u52bf\u4e0d\u8870\uff0c\u649e\u5728\u4ed6\u53f3\u817f\u4e4b\u4e0a\uff0c\u8fd8\u662f\u5212\u7834\u4e86\u76ae\u8089\u3002\u4fbf\u5728\u6b64\u65f6\uff0c\u53ea\u542c\u5f97\u201c\u554a\u201d\u7684\u4e00\u58f0\u60e8\u547c\uff0c\u4e00\u540d\u6b66\u58eb\u54bd\u5589\u4e2d\u9556\uff0c\u5411\u540e\u76f4\u6454\u3002\u7530\u5f52\u519c\u9a82\u9053\uff1a\u201c\u5c0f\u8d3c\uff0c\u77a7\u4f60\u4eca\u65e5\u9003\u5f97\u5230\u54ea\u91cc\u53bb\uff1f\u201d\u4f46\u4e00\u65f6\u5012\u4e5f\u4e0d\u6562\u5192\u8fdb\uff0c\u6307\u6325\u4f17\u6b66\u58eb\uff0c\u56e2\u56e2\u5c06\u4e24\u4eba\u56f4\u4f4f\u3002 \u3000\u3000\u798f\u5eb7\u5b89\u5e9c\u4e2d\u8fd9\u6b21\u6765\u7684\u6b66\u58eb\uff0c\u8fde\u7530\u5f52\u519c\u5728\u5185\u5171\u662f\u4e8c\u5341\u4e03\u4eba\uff0c\u88ab\u80e1\u6590\u5200\u780d\u638c\u51fb\u3001\u9556\u6253\u817f\u8e22\uff0c\u4e00\u5171\u5df2\u4f24\u6bd9\u4e86\u4e5d\u4eba\uff0c\u80e1\u6590\u81ea\u5df1\u53d7\u4f24\u4e5f\u4e0d\u8f7b\u3002\u5bf9\u65b9\u5341\u516b\u4eba\u56db\u5468\u56f4\u4f4f\uff0c\u6b64\u65f6\u5df2\u64cd\u5fc5\u80dc\u4e4b\u7b97\uff0c\u6709\u51e0\u4eba\u7231\u60dc\u80e1\u6590\uff0c\u53c8\u53eb\u4ed6\u6295\u964d\u3002 \u3000\u3000\u80e1\u6590\u4f4e\u58f0\u9053\uff1a\u201c\u6211\u5411\u4e1c\u51b2\u51fa\uff0c\u5f15\u5f00\u4f17\u4eba\uff0c\u4f60\u5feb\u5f80\u897f\u53bb\u3002\u90a3\u5339\u767d\u9a6c\u7cfb\u5728\u677e\u6811\u4e0a\u3002\u201d\u5706\u6027\u9053\uff1a\u201c\u767d\u9a6c\u662f\u4f60\u7684\uff0c\u4e0d\u662f\u6211\u7684\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u8fd9\u5f53\u513f\u8fd8\u5206\u4ec0\u4e48\u4f60\u7684\u6211\u7684\uff01\u6211\u4e0d\u7528\u7167\u987e\u4f60\uff0c\u7ba1\u6559\u80fd\u591f\u7a81\u56f4\u3002\u201d\u5706\u6027\u9053\uff1a\u201c\u6211\u4e0d\u7528\u4f60\u7167\u987e\uff0c\u4f60\u8fd9\u5c31\u53bb\u7f62\u3002\u201d \u3000\u3000\u82e5\u662f\u4f9d\u4e86\u80e1\u6590\u7684\u8ba1\u8bae\uff0c\u4e00\u4e2a\u4e58\u767d\u9a6c\u5954\u9a70\u5982\u98ce\uff0c\u4e00\u4e2a\u6301\u52c7\u529b\u5f53\u8005\u62ab\u9761\uff0c\u672a\u59cb\u4e0d\u80fd\u8131\u9669\u3002\u53ef\u662f\u5706\u6027\u4e0d\u613f\u610f\uff0c\u5176\u5b9e\u5728\u80e1\u6590\u5fc3\u4e2d\uff0c\u4e5f\u662f\u4e0d\u613f\u610f\u3002\u4e5f\u8bb8\uff0c\u4e24\u4eba\u51b3\u8ba1\u4e0d\u613f\u5728\u8fd9\u751f\u6b7b\u5173\u5934\u5206\u5f00\uff1b\u4e5f\u8bb8\uff0c\u4e24\u4eba\u65e9\u5c31\u5fc3\u4e2d\u60b2\u82e6\uff0c\u89c9\u5f97\u8fd8\u662f\u6b7b\u4e86\u5e72\u51c0\u3002 \u3000\u3000\u80e1\u6590\u62c9\u4f4f\u5706\u6027\u7684\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff01\u8881\u59d1\u5a18\uff0c\u54b1\u4fe9\u4fbf\u6b7b\u5728\u4e00\u8d77\u3002\u6211\u2026\u2026\u6211\u5f88\u662f\u559c\u6b22\uff01\u201d \u3000\u3000\u5706\u6027\u8f7b\u8f7b\u6454\u8131\u4e86\u4ed6\u624b\uff0c\u5598\u606f\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u662f\u51fa\u5bb6\u4eba\uff0c\u522b\u53eb\u6211\u8881\u59d1\u5a18\u3002\u6211\u4e5f\u4e0d\u662f\u59d3\u8881\u3002\u201d \u3000\u3000\u80e1\u6590\u5fc3\u4e0b\u9eef\u7136\uff0c\u6697\u60f3\u6211\u4e8c\u4eba\u6b7b\u5230\u4e34\u5934\uff0c\u4f60\u8fd8\u662f\u8fd9\u822c\u77dc\u6301\uff0c\u5bf9\u6211\u4e1d\u6beb\u4e0d\u5047\u8f9e\u8272\u3002 \u3000\u3000\u53ea\u89c1\u4e00\u540d\u6b66\u58eb\u5c06\u5355\u5200\u821e\u6210\u4e00\u56e2\u767d\u5149\uff0c\u4e00\u6b65\u6b65\u903c\u8fd1\u3002\u80e1\u6590\u62fe\u8d77\u4e00\u5757\u77f3\u5934\uff0c\u5411\u767d\u5149\u5708\u6454\u4e86\u8fc7\u53bb\u3002\u90a3\u6b66\u58eb\u5355\u5200\u4e00\u683c\uff0c\u5c06\u77f3\u5934\u51fb\u5f00\u3002\u80e1\u6590\u6293\u4f4f\u8fd9\u4e2a\u7a7a\u9699\uff0c\u4e00\u9556\u63b7\u51fa\uff0c\u6b63\u4e2d\u5176\u80f8\uff0c\u90a3\u6b66\u58eb\u6251\u5012\u5728\u5730\uff0c\u773c\u89c1\u4e0d\u6d3b\u4e86\u3002 \u3000\u3000\u7530\u5f52\u519c\u53eb\u9053\uff1a\u201c\u8fd9\u5c0f\u8d3c\u51f6\u6a2a\u5f97\u7d27\uff0c\u54b1\u4eec\u4e00\u62e5\u800c\u4e0a\uff0c\u96be\u9053\u4ed6\u5f53\u771f\u4fbf\u6709\u4e09\u5934\u516d\u81c2\u4e0d\u6210\uff1f\u201d \u3000\u3000\u80e1\u6590\u62ac\u5934\u671b\u4e86\u4e00\u773c\u5934\u9876\u7684\u661f\u661f\uff0c\u5fc3\u60f3\u518d\u6765\u4e00\u573a\u6fc0\u6218\uff0c\u81ea\u5df1\u6740\u5f97\u4e09\u56db\u540d\u654c\u4eba\uff0c\u661f\u661f\u554a\uff0c\u6708\u4eae\u554a\uff0c\u82b1\u554a\uff0c\u7530\u91ce\u554a\uff0c\u90a3\u4fbf\u6c38\u522b\u4e86\u3002 \u3000\u3000\u7530\u5f52\u519c\u6beb\u65e0\u987e\u5fcc\u7684\u5927\u58f0\u547c\u559d\u6307\u6325\uff0c\u547d\u5341\u516d\u540d\u6b66\u58eb\u4ece\u56db\u65b9\u8fdb\u653b\uff0c\u540c\u65f6\u780d\u843d\uff0c\u4e71\u5200\u5206\u5c38\u3002\u4f17\u6b66\u58eb\u9f50\u58f0\u7b54\u5e94\u3002\u7530\u5f52\u519c\u53eb\u9053\uff1a\u201c\u4ed6\u6ca1\u5175\u5668\uff0c\u8fd9\u4e00\u6b21\u975e\u5c06\u4ed6\u65a9\u6210\u8089\u9171\u4e0d\u53ef\uff01\u201d \u3000\u3000\u82d7\u592b\u4eba\u5ffd\u5730\u8d70\u8fd1\u51e0\u6b65\uff0c\u8bf4\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4e14\u6162\uff0c\u6211\u6709\u51e0\u53e5\u8bdd\u8ddf\u8fd9\u5c11\u5e74\u8bf4\u3002\u201d\u7530\u5f52\u519c\u76b1\u8d77\u4e86\u7709\u5934\uff0c\u9053\uff1a\u201c\u963f\u5170\uff0c\u4f60\u522b\u5230\u8fd9\u513f\u6765\uff0c\u5c0f\u5fc3\u8fd9\u5c0f\u8d3c\u53d1\u8d77\u75af\u6765\uff0c\u4f24\u5230\u4e86\u4f60\u3002\u201d\u82d7\u592b\u4eba\u5374\u751a\u662f\u56fa\u6267\uff0c\u9053\uff1a\u201c\u4ed6\u7acb\u65f6\u4fbf\u8981\u6b7b\u4e86\u3002\u6211\u8ddf\u4ed6\u8bf4\u4e00\u53e5\u8bdd\uff0c\u6709\u4ec0\u4e48\u5e72\u7cfb\uff1f\u201d\u7530\u5f52\u519c\u65e0\u5948\uff0c\u53ea\u662f\u9053\uff1a\u201c\u597d\uff0c\u4f60\u8bf4\u7f62\uff01\u201d \u3000\u3000\u82d7\u592b\u4eba\u9053\uff1a\u201c\u80e1\u76f8\u516c\uff0c\u4f60\u7684\u9aa8\u7070\u575b\u8fd8\u6ca1\u57cb\uff0c\u8fd9\u4fbf\u6b7b\u4e86\u5417\uff1f\u201d \u3000\u3000\u80e1\u6590\u6602\u7136\u9053\uff1a\u201c\u5173\u4f60\u4ec0\u4e48\u4e8b\uff1f\u6211\u4e0d\u613f\u7834\u53e3\u8fb1\u9a82\u5973\u4eba\u3002\u4f60\u6700\u597d\u8d70\u5f97\u8fdc\u4e9b\u3002\u201d\u82d7\u592b\u4eba\u9053\uff1a\u201c\u6211\u7b54\u5e94\u8fc7\u4f60\uff0c\u8981\u8ddf\u4f60\u8bf4\u4f60\u7239\u7239\u7684\u4e8b\u3002\u4f60\u867d\u8f6c\u773c\u4fbf\u6b7b\uff0c\u8981\u4e0d\u8981\u542c\uff1f\u201d \u3000\u3000\u7530\u5f52\u519c\u559d\u9053\uff1a\u201c\u963f\u5170\uff0c\u4f60\u80e1\u95f9\u4ec0\u4e48\uff1f\u4f60\u53c8\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u82d7\u592b\u4eba\u4e0d\u7406\u7530\u5f52\u519c\uff0c\u5bf9\u80e1\u6590\u9053\uff1a\u201c\u6211\u53ea\u8ddf\u4f60\u8bf4\u4e09\u53e5\u8bdd\uff0c\u90fd\u662f\u548c\u4f60\u7239\u7239\u6709\u5173\u7684\u3002\u4f60\u542c\u4e0d\u542c\uff1f\u201d\u80e1\u6590\u9053\uff1a\u201c\u4e0d\u9519\uff01\u6211\u4e0d\u80fd\u5fc3\u4e2d\u5b58\u7740\u4e00\u4e2a\u7591\u56e2\u800c\u6b7b\u3002\u4f60\u8bf4\u5427\uff01\u201d\u82d7\u592b\u4eba\u9053\uff1a\u201c\u6211\u8fd9\u8bdd\u53ea\u80fd\u7ed9\u4f60\u4e00\u4eba\u542c\uff0c\u4f60\u5374\u4e0d\u53ef\u62ff\u4f4f\u4e86\u6211\u8981\u631f\uff0c\u5018\u82e5\u4f60\u4e0d\u7b54\u5e94\uff0c\u6211\u5c31\u4e0d\u8bf4\u4e86\u3002\u201d \u3000\u3000\u80e1\u6590\u9053\uff1a\u201c\u4f60\u5728\u6211\u6b7b\u53bb\u4e4b\u524d\uff0c\u91ca\u660e\u6211\u5fc3\u4e2d\u7591\u56e2\uff0c\u6211\u5341\u5206\u611f\u8c22\uff0c\u5c82\u80fd\u53cd\u6765\u5bb3\u4f60\uff1f\u5929\u4e0b\u7537\u513f\u6c49\u5927\u4e08\u592b\u751a\u591a\uff0c\u4f60\u9053\u90fd\u662f\u7530\u5f52\u519c\u8fd9\u822c\u5351\u9119\u5c0f\u4eba\u4e48\uff1f\u201d \u3000\u3000\u7530\u5f52\u519c\u8138\u4e0a\u66f4\u52a0\u9634\u6c89\u4e86\u3002\u4ed6\u4e0d\u77e5\u5357\u5170\u8981\u8ddf\u80e1\u6590\u8bf4\u4e9b\u4ec0\u4e48\u8bdd\uff0c\u4ed6\u5411\u6765\u4e0d\u6562\u5f97\u7f6a\u4e86\u5979\uff0c\u65e2\u662f\u65e0\u6cd5\u963b\u6b62\uff0c\u5fc3\u60f3\uff1a\u201c\u4e0d\u8bba\u5979\u8bf4\u4ec0\u4e48\uff0c\u603b\u662f\u4e8e\u6211\u58f0\u540d\u4e0d\u5229\uff0c\u81ea\u662f\u522b\u8ba9\u65c1\u4eba\u542c\u89c1\u4e3a\u5999\u3002\u201d \u3000\u3000\u82d7\u592b\u4eba\u7f13\u6b65\u8fc7\u6765\uff0c\u8d70\u5230\u80e1\u6590\u8eab\u524d\uff0c\u5c06\u5634\u5df4\u51d1\u5230\u4ed6\u8033\u8fb9\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u4f60\u5c06\u9aa8\u7070\u575b\u57cb\u5728\u5893\u7891\u4e4b\u540e\u7684\u4e09\u5c3a\u5904\uff0c\u5411\u4e0b\u6316\u6398\uff0c\u6709\u67c4\u5b9d\u5200\u3002\u201d\u8bf4\u4e86\u8fd9\u4e09\u53e5\u8bdd\uff0c\u4fbf\u5373\u9000\u5f00\uff0c\u6717\u58f0\u9053\uff1a\u201c\u6b64\u4e8b\u53ea\u4e0e\u91d1\u9762\u4f5b\u82d7\u4eba\u51e4\u6709\u5173\u3002\u4f60\u65e2\u77e5\u9053\u4e86\u8fd9\u4ef6\u79d8\u5bc6\uff0c\u6b7b\u800c\u65e0\u61be\uff0c\u5feb\u5c06\u9aa8\u7070\u575b\u57cb\u597d\uff0c\u8ba9\u6b7b\u8005\u5165\u571f\u4e3a\u5b89\u3002\u4f60\u4e86\u7ed3\u8fd9\u4ef6\u5fc3\u4e8b\uff0c\u5b89\u5fc3\u9886\u6b7b\u5427\uff01\u201d \u3000\u3000\u80e1\u6590\u5fc3\u4e2d\u4e00\u7247\u8ff7\u60d8\uff0c\u5b9e\u662f\u4e0d\u61c2\u5979\u8fd9\u4e09\u53e5\u8bdd\u7684\u7528\u610f\uff0c\u770b\u6765\u53c8\u4e0d\u50cf\u662f\u6545\u610f\u4f5c\u5f04\u81ea\u5df1\uff0c\u5fc3\u60f3\uff1a\u201c\u4e0d\u7ba1\u5982\u4f55\uff0c\u786e\u662f\u5148\u846c\u4e86\u4e8c\u59b9\u7684\u9aa8\u7070\u518d\u8bf4\u3002\u201d\u4e8e\u662f\u770b\u51c6\u4e86\u5893\u7891\u540e\u4e09\u5c3a\u4e4b\u5904\uff0c\u8fd0\u52b2\u4e8e\u6307\uff0c\u4f38\u624b\u6316\u571f\u3002 \u3000\u3000\u7530\u5f52\u519c\u5fc3\u9053\uff1a\u201c\u539f\u6765\u963f\u5170\u662f\u8ddf\u4ed6\u8bf4\uff0c\u4ed6\u7236\u4eb2\u662f\u6b7b\u4e8e\u82d7\u4eba\u51e4\u4e4b\u624b\u3002\u201d\u5fc3\u4e2d\u5927\u6170\uff0c\u8f6c\u5934\u5411\u5979\u5fae\u5fae\u4e00\u7b11\u3002\u4ed6\u542c\u5357\u5170\u53eb\u80e1\u6590\u57cb\u846c\u9aa8\u7070\u575b\uff0c\u4e0d\u4fbf\u62c2\u9006\u5176\u610f\u800c\u6307\u6325\u6b66\u58eb\u963b\u6b62\uff0c\u53cd\u6b63\u80e1\u6590\u65e9\u6b7b\u8fdf\u6b7b\uff0c\u4e5f\u4e0d\u4e89\u5728\u7247\u523b\u4e4b\u95f4\u3002 \u3000\u3000\u5341\u516d\u540d\u6b66\u58eb\u5404\u6267\u5175\u5203\uff0c\u6bcf\u4eba\u90fd\u76f8\u8ddd\u80e1\u6590\u4e08\u4f59\uff0c\u76ee\u4e0d\u8f6c\u775b\u7684\u76d1\u89c6\u3002 \u3000\u3000\u5706\u6027\u89c1\u80e1\u6590\u6316\u5751\u57cb\u846c\u7a0b\u7075\u7d20\u7684\u9aa8\u7070\uff0c\u5fc3\u60f3\u81ea\u5df1\u4e0e\u4ed6\u7acb\u65f6\u4e5f\u4fbf\u8eab\u5f52\u9ec4\u571f\uff0c\u5f53\u4e0b\u6084\u6084\u8dea\u5012\uff0c\u5408\u5341\u4e3a\u793c\uff0c\u53e3\u4e2d\u8f7b\u8f7b\u8bf5\u7ecf\u3002 \u3000\u3000\u80e1\u6590\u5de6\u80a9\u7684\u4f24\u75db\u8d8a\u6765\u8d8a\u5389\u5bb3\uff0c\u4e24\u53ea\u624b\u6e10\u6e10\u6316\u6df1\uff0c\u4e00\u8f6c\u5934\uff0c\u77a5\u89c1\u5706\u6027\u5408\u5341\u4e0b\u8dea\uff0c\u795e\u6001\u5e84\u4e25\u8083\u7a46\uff0c\u5ffd\u611f\u559c\u6170\uff1a\u201c\u5979\u6f5c\u5fc3\u7688\u4f5b\uff0c\u6211\u4f55\u82e6\u52c9\u5f3a\u8981\u5979\u8fd8\u4fd7\uff1f\u5e78\u4e8f\u5979\u6ca1\u7b54\u5e94\uff0c\u5426\u5219\u5979\u4e34\u6b7b\u4e4b\u65f6\uff0c\u5fc3\u4e2d\u4e0d\u5f97\u5e73\u5b89\u3002\u201d \u3000\u3000\u7a81\u7136\u4e4b\u95f4\uff0c\u4ed6\u53cc\u624b\u624b\u6307\u540c\u65f6\u78b0\u5230\u4e00\u4ef6\u51b0\u51b7\u575a\u786c\u4e4b\u7269\uff0c\u8111\u6d77\u4e2d\u95ea\u8fc7\u82d7\u592b\u4eba\u7684\u90a3\u53e5\u8bdd\uff1a\u201c\u6709\u67c4\u5b9d\u5200\uff01\u201d\u4ed6\u4e0d\u52a8\u58f0\u8272\uff0c\u5411\u4e24\u65c1\u6478\u7d22\uff0c\u679c\u7136\u662f\u4e00\u67c4\u5e26\u9798\u7684\u5355\u5200\uff0c\u6293\u4f4f\u5200\u67c4\u8f7b\u8f7b\u4e00\u62bd\uff0c\u5200\u5203\u62bd\u51fa\u5bf8\u8bb8\uff0c\u6beb\u6ca1\u751f\u9508\uff0c\u5fc3\u60f3\uff1a\u201c\u82d7\u592b\u4eba\u8bf4\u9053\uff1a\u2018\u6b64\u4e8b\u53ea\u4e0e\u91d1\u9762\u4f5b\u82d7\u4eba\u51e4\u6709\u5173\u2019\uff0c\u96be\u9053\u8fd9\u628a\u5200\u662f\u82d7\u5927\u4fa0\u57cb\u5728\u8fd9\u91cc\u7684\uff1f\u96be\u9053\u82d7\u5927\u4fa0\u4e3a\u4e86\u7eaa\u5ff5\u6211\u7239\u7239\uff0c\u5c06\u8fd9\u67c4\u5200\u57cb\u5728\u6211\u7239\u7239\u7684\u575f\u91cc\uff1f\u201d \u3000\u3000\u4ed6\u8fd9\u4e00\u4e0b\u731c\u6d4b\uff0c\u786e\u662f\u6ca1\u731c\u9519\u3002\u53ea\u662f\u4ed6\u5e76\u4e0d\u77e5\u9053\uff0c\u82d7\u4eba\u51e4\u6240\u4ee5\u548c\u82d7\u592b\u4eba\u76f8\u8bc6\u800c\u6210\u5a5a\uff0c\u6b63\u662f\u7531\u4e8e\u8fd9\u53e3\u201c\u51b7\u6708\u5b9d\u5200\u201d\uff1b\u800c\u4ed6\u592b\u5987\u826f\u7f18\u7834\u88c2\uff0c\u4e5f\u662f\u4ece\u8fd9\u53e3\u5b9d\u5200\u800c\u8d77\uff0c\u59cb\u4e8e\u82d7\u4eba\u51e4\u5c06\u8fd9\u5200\u57cb\u846c\u5728\u80e1\u4e00\u5200\u575f\u4e2d\u4e4b\u65f6\u3002 \u3000\u3000\u5f53\u4e16\u9664\u4e86\u82d7\u4eba\u51e4\u548c\u82d7\u592b\u4eba\u4e4b\u5916\uff0c\u6ca1\u7b2c\u4e09\u4eba\u77e5\u9053\u6b64\u4e8b\u3002 \u3000\u3000\u80e1\u6590\u63e1\u4f4f\u4e86\u5200\u67c4\uff0c\u56de\u5934\u5411\u82d7\u592b\u4eba\u77a7\u53bb\uff0c\u53ea\u542c\u5f97\u5979\u5e7d\u5e7d\u8bf4\u9053\uff1a\u201c\u8981\u660e\u767d\u522b\u4eba\u7684\u5fc3\uff0c\u90a3\u662f\u591a\u4e48\u96be\u554a\uff01\u201d\u5979\u957f\u957f\u5730\u53f9\u4e86\u53e3\u6c14\uff0c\u7f13\u6b65\u8fdc\u53bb\u3002 \u3000\u3000\u7530\u5f52\u519c\u53eb\u9053\uff1a\u201c\u963f\u5170\uff0c\u4f60\u5728\u5ba2\u5e97\u91cc\u7b49\u6211\u3002\u5f85\u6211\u6740\u4e86\u8fd9\u5c0f\u8d3c\uff0c\u5927\u4f19\u513f\u559d\u9152\u5e86\u529f\u3002\u201d\u82d7\u592b\u4eba\u4e0d\u7b54\uff0c\u5728\u8352\u91ce\u4e2d\u8d8a\u8d70\u8d8a\u8fdc\u3002 \u3000\u3000\u7530\u5f52\u519c\u8f6c\u8fc7\u5934\u6765\uff0c\u559d\u9053\uff1a\u201c\u5c0f\u8d3c\uff0c\u5feb\u57cb\uff01\u54b1\u4eec\u4e0d\u7b49\u4e86\uff01\u201d \u3000\u3000\u80e1\u6590\u9053\uff1a\u201c\u597d\uff0c\u4e0d\u7b49\u4e86\uff01\u201d\u6293\u8d77\u5200\u67c4\uff0c\u53ea\u89c9\u773c\u524d\u9752\u5149\u4e00\u95ea\uff0c\u5bd2\u6c14\u903c\u4eba\uff0c\u624b\u4e2d\u5df2\u591a\u4e86\u4e00\u67c4\u9752\u68ee\u68ee\u7684\u957f\u5200\uff0c\u5200\u5149\u5982\u6c34\uff0c\u5728\u51b7\u6708\u4e0b\u6d41\u8f6c\u4e0d\u5b9a\u3002 \u3000\u3000\u7530\u5f52\u519c\u548c\u4f17\u6b66\u58eb\u65e0\u4e0d\u5927\u60ca\u3002\u80e1\u6590\u4e58\u4f17\u4eba\u5fc3\u795e\u672a\u5b9a\uff0c\u6325\u5200\u6740\u4e0a\u3002\u5f53\u5577\u5f53\u5577\u51e0\u58f0\u54cd\u5904\uff0c\u4e09\u540d\u6b66\u58eb\u5175\u5203\u524a\u65ad\uff0c\u4e24\u4eba\u624b\u81c2\u65ad\u843d\u3002\u7530\u5f52\u519c\u6a2a\u5200\u65ab\u81f3\uff0c\u80e1\u6590\u4e3e\u5200\u4e00\u683c\uff0c\u94ee\u58f0\u6e05\u54cd\uff0c\u58f0\u5982\u51fb\u78d0\uff0c\u826f\u4e45\u4e0d\u7edd\u3002\u4e24\u4eba\u8dc3\u5f00\u4e09\u6b65\uff0c\u5c31\u6708\u5149\u4e0b\u770b\u624b\u4e2d\u5200\u65f6\uff0c\u90fd\u662f\u4e1d\u6beb\u65e0\u635f\u3002\u539f\u6765\u4e24\u53e3\u5b9d\u5200\uff0c\u6b63\u582a\u5339\u654c\u3002 \u3000\u3000\u80e1\u6590\u4e00\u89c1\u624b\u4e2d\u5355\u5200\u4e0d\u6015\u7530\u5f52\u519c\u7684\u5b9d\u5200\uff0c\u767b\u65f6\u5982\u864e\u6dfb\u7ffc\uff0c\u5c55\u5f00\u80e1\u5bb6\u5200\u6cd5\uff0c\u970e\u65f6\u95f4\u53c8\u4f24\u4e86\u4e09\u540d\u6b66\u58eb\u3002\u7530\u5f52\u519c\u7684\u5b9d\u5200\u867d\u548c\u4ed6\u5404\u4e0d\u76f8\u4e0b\uff0c\u4f46\u5200\u6cd5\u5374\u5927\u5927\u4e0d\u5982\uff0c\u4ed6\u4ee5\u64c5\u4f7f\u7684\u957f\u5251\u548c\u80e1\u6590\u76f8\u6597\uff0c\u5c1a\u4e14\u4e0d\u53ca\uff0c\u4f55\u51b5\u4ee5\u5df1\u4e4b\u77ed\uff0c\u653b\u654c\u4e4b\u957f\uff1f\u4e09\u56db\u62db\u4e00\u8fc7\uff0c\u81c2\u817f\u63a5\u8fde\u4e2d\u5200\uff0c\u82e5\u975e\u8eab\u65c1\u6b66\u58eb\u76f8\u6551\u9000\u5f00\uff0c\u5df2\u7136\u547d\u4e27\u80e1\u6590\u5200\u4e0b\u3002\u6b64\u65f6\u8eab\u4e0a\u6ca1\u5e26\u4f24\u7684\u6b66\u58eb\u5df2\u5be5\u5be5\u65e0\u51e0\uff0c\u4efb\u4f55\u5175\u5203\u9047\u4e0a\u80e1\u6590\u624b\u4e2d\u5b9d\u5200\uff0c\u65e0\u4e0d\u7acb\u65ad\uff0c\u5c3d\u53d8\u7a7a\u624b\u3002 \u3000\u3000\u80e1\u6590\u4e5f\u4e0d\u8d76\u5c3d\u6740\u7edd\uff0c\u53eb\u9053\uff1a\u201c\u6211\u770b\u5404\u4f4d\u4e5f\u90fd\u662f\u597d\u6c49\u5b50\uff0c\u4f55\u5fc5\u6789\u81ea\u9001\u4e86\u6027\u547d\uff1f\u201d \u3000\u3000\u7530\u5f52\u519c\u89c1\u60c5\u52bf\u4e0d\u5bf9\uff0c\u62d4\u8db3\u4fbf\u9003\u3002\u4f17\u6b66\u58eb\u642d\u8d77\u5730\u4e0b\u7684\u4f24\u6bd9\u540c\u4f34\uff0c\u5927\u8d25\u800c\u8d70\u3002\u4f17\u4eba\u76f4\u5230\u6570\u5e74\u4e4b\u540e\uff0c\u82e6\u82e6\u601d\u7d22\uff0c\u7eb7\u7eb7\u8bae\u8bba\uff0c\u8fd8\u662f\u6ca1\u4e1d\u6beb\u5934\u7eea\uff0c\u4e0d\u77e5\u80e1\u6590\u8fd9\u67c4\u5b9d\u5200\u4ece\u4f55\u800c\u6765\u3002\u603b\u89c9\u6b64\u4eba\u884c\u4e8b\u795e\u51fa\u9b3c\u6ca1\uff0c\u4eba\u6240\u96be\u6d4b\uff0c\u201c\u98de\u72d0\u201d\u8fd9\u5916\u53f7\u4fbf\u7531\u6b64\u800c\u4f20\u5f00\u4e86\u3002 \u3000\u3000\u80e1\u6590\u5f39\u5200\u6e05\u5578\uff0c\u5fc3\u4e2d\u611f\u6168\uff0c\u8fd8\u5200\u5165\u9798\uff0c\u5c06\u5b9d\u5200\u653e\u56de\u571f\u5751\u4e4b\u4e2d\uff0c\u4f7f\u5b83\u957f\u4f34\u7236\u4eb2\u4e8e\u5730\u4e0b\uff0c\u518d\u5c06\u7a0b\u7075\u7d20\u7684\u9aa8\u7070\u575b\u4e5f\u8f7b\u8f7b\u653e\u5165\u571f\u5751\uff0c\u62e8\u571f\u63a9\u597d\u3002 \u3000\u3000\u5706\u6027\u53cc\u624b\u5408\u5341\uff0c\u8f7b\u5ff5\u4f5b\u5048\uff1a \u3000\u3000\u201c\u4e00\u5207\u6069\u7231\u4f1a\uff0c\u65e0\u5e38\u96be\u5f97\u4e45\u3002 \u3000\u3000\u751f\u4e16\u591a\u754f\u60e7\uff0c\u547d\u5371\u4e8e\u6668\u9732\u3002 \u3000\u3000\u7531\u7231\u6545\u751f\u5fe7\uff0c\u7531\u7231\u6545\u751f\u6016\u3002 \u3000\u3000\u82e5\u79bb\u4e8e\u7231\u8005\uff0c\u65e0\u5fe7\u4ea6\u65e0\u6016\u3002\u201d \u3000\u3000\u5ff5\u6bd5\uff0c\u6084\u7136\u4e0a\u9a6c\uff0c\u7f13\u6b65\u897f\u53bb\u3002 \u3000\u3000\u80e1\u6590\u8ffd\u5c06\u4e0a\u53bb\uff0c\u7275\u8fc7\u9a86\u51b0\u6240\u8d60\u7684\u767d\u9a6c\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u9a91\u4e86\u8fd9\u9a6c\u53bb\u5427\u3002\u4f60\u8eab\u4e0a\u6709\u4f24\uff0c\u8fd8\u662f\u2026\u2026\u8fd8\u662f\u2026\u2026\u201d\u5706\u6027\u6447\u6447\u5934\uff0c\u7eb5\u9a6c\u4fbf\u884c\u3002 \u3000\u3000\u80e1\u6590\u671b\u7740\u5979\u7684\u80cc\u5f71\uff0c\u90a3\u516b\u53e5\u4f5b\u5048\uff0c\u5728\u8033\u9645\u5fc3\u5934\u4e0d\u4f4f\u76d8\u65cb\u3002 \u3000\u3000\u4ed6\u8eab\u65c1\u90a3\u5339\u767d\u9a6c\u671b\u7740\u5706\u6027\u6e10\u884c\u6e10\u8fdc\uff0c\u4e0d\u7531\u5f97\u7eb5\u58f0\u60b2\u5636\uff0c\u4e0d\u660e\u767d\u8fd9\u4f4d\u65e7\u4e3b\u4eba\u4e3a\u4ec0\u4e48\u7adf\u4e0d\u8f6c\u8fc7\u5934\u6765\u3002 \u3000\u3000\uff08\u5168\u4e66\u5b8c\uff09"}),showOnHomepage:!0},{name:"\u540e\u8bb0",content:(0,a.jsx)(a.Fragment,{children:"\u300a\u98de\u72d0\u5916\u4f20\u300b\u5199\u4e8e\u4e00\u4e5d\u516d\u25cb\u3001\u516d\u4e00\u5e74\u95f4\uff0c\u539f\u5728\u300a\u6b66\u4fa0\u4e0e\u5386\u53f2\u300b\u5c0f\u8bf4\u6742\u5fd7\u8fde\u8f7d\uff0c\u6bcf\u671f\u520a\u8f7d\u516b\u5343\u5b57\u3002 \u3000\u3000\u5728\u62a5\u4e0a\u8fde\u8f7d\u7684\u5c0f\u8bf4\uff0c\u6bcf\u6bb5\u7ea6\u4e00\u5343\u5b57\u81f3\u4e00\u5343\u56db\u767e\u5b57\u3002\u300a\u98de\u72d0\u5916\u4f20\u300b\u5219\u662f\u6bcf\u516b\u5343\u5b57\u6210\u4e00\u4e2a\u6bb5\u843d\uff0c\u6240\u4ee5\u5199\u4f5c\u7684\u65b9\u5f0f\u7565\u6709\u4e0d\u540c\u3002 \u3000\u3000\u6211\u6bcf\u5341\u5929\u5199\u4e00\u6bb5\uff0c\u4e00\u4e2a\u901a\u5bb5\u5199\u5b8c\uff0c\u4e00\u822c\u662f\u534a\u591c\u5341\u4e8c\u70b9\u949f\u5f00\u59cb\uff0c\u5230\u7b2c\u4e8c\u5929\u65e9\u6668\u4e03\u516b\u70b9\u949f\u5de5\u4f5c\u7ed3\u675f\u3002\u4f5c\u4e3a\u4e00\u90e8\u957f\u7bc7\u5c0f\u8bf4\uff0c\u6bcf\u516b\u5343\u5b57\u6210\u4e00\u6bb5\u843d\u7684\u8282\u594f\u662f\u7edd\u5bf9\u4e0d\u597d\u7684\u3002\u8fd9\u6b21\u6240\u4f5c\u7684\u4fee\u6539\uff0c\u4e3b\u8981\u662f\u5c06\u8282\u594f\u8c03\u6574\u5f97\u6d41\u7545\u4e00\u4e9b\uff0c\u6d88\u53bb\u5176\u4e2d\u4e0d\u5fc5\u8981\u7684\u6bb5\u843d\u75d5\u8ff9\u3002 \u3000\u3000\u300a\u98de\u72d0\u5916\u4f20\u300b\u662f\u300a\u96ea\u5c71\u98de\u72d0\u300b\u7684\u201c\u524d\u4f20\u201d\uff0c\u53d9\u8ff0\u80e1\u6590\u8fc7\u53bb\u7684\u4e8b\u8ff9\u3002\u7136\u800c\u8fd9\u662f\u4e24\u90e8\u5c0f\u8bf4\uff0c\u4e92\u76f8\u6709\u8054\u7cfb\uff0c\u5374\u5e76\u4e0d\u662f\u5168\u7136\u7684\u7edf\u4e00\u3002\u5728\u300a\u98de\u72d0\u5916\u4f20\u300b\u4e2d\uff0c\u80e1\u6590\u4e0d\u6b62\u4e00\u6b21\u548c\u82d7\u4eba\u51e4\u76f8\u4f1a\uff0c\u80e1\u6590\u6709\u8fc7\u522b\u7684\u610f\u4e2d\u4eba\u3002\u8fd9\u4e9b\u60c5\u8282\uff0c\u6ca1\u6709\u5728\u4fee\u6539\u300a\u96ea\u5c71\u98de\u72d0\u300b\u65f6\u5f3a\u6c42\u534f\u8c03\u3002 \u3000\u3000\u8fd9\u90e8\u5c0f\u8bf4\u7684\u6587\u5b57\u98ce\u683c\uff0c\u6bd4\u8f83\u8fdc\u79bb\u4e2d\u56fd\u65e7\u5c0f\u8bf4\u7684\u4f20\u7edf\uff0c\u73b0\u5728\u5e76\u6ca1\u6709\u6539\u56de\u6765\uff0c\u4f46\u6709\u4e24\u79cd\u60c5\u5f62\u662f\u6539\u4e86\u7684\uff1a\u7b2c\u4e00\uff0c\u5bf9\u8bdd\u4e2d\u5220\u9664\u4e86\u542b\u6709\u73b0\u4ee3\u6c14\u606f\u7684\u5b57\u773c\u548c\u89c2\u5ff5\uff0c\u4eba\u7269\u7684\u5185\u5fc3\u8bed\u8a00\u4e5f\u662f\u5982\u6b64\u3002 \u3000\u3000\u7b2c\u4e8c\uff0c\u6539\u5199\u4e86\u592a\u65b0\u6587\u827a\u8154\u7684\u3001\u7c7b\u4f3c\u5916\u56fd\u8bed\u6587\u6cd5\u7684\u53e5\u5b50\u3002 \u3000\u3000\u300a\u96ea\u5c71\u98de\u72d0\u300b\u7684\u771f\u6b63\u4e3b\u89d2\uff0c\u5176\u5b9e\u662f\u80e1\u4e00\u5200\u3002\u80e1\u6590\u7684\u6027\u683c\u5728\u300a\u96ea\u5c71\u98de\u72d0\u300b\u4e2d\u5341\u5206\u5355\u8584\uff0c\u5230\u4e86\u672c\u4e66\u4e2d\u624d\u6e10\u6e10\u6210\u5f62\u3002\u6211\u4f01\u56fe\u5728\u672c\u4e66\u4e2d\u5199\u4e00\u4e2a\u6025\u4eba\u4e4b\u96be\u3001\u884c\u4fa0\u4ed7\u4e49\u7684\u4fa0\u58eb\u3002\u6b66\u4fa0\u5c0f\u8bf4\u4e2d\u771f\u6b63\u5199\u4fa0\u58eb\u7684\u5176\u5b9e\u5e76\u4e0d\u5f88\u591a\uff0c\u5927\u591a\u6570\u4e3b\u89d2\u7684\u6240\u4f5c\u6240\u4e3a\uff0c\u4e3b\u8981\u662f\u6b66\u800c\u4e0d\u662f\u4fa0\u3002 \u3000\u3000\u5b5f\u5b50\u8bf4\uff1a\u201c\u5bcc\u8d35\u4e0d\u80fd\u6deb\uff0c\u8d2b\u8d31\u4e0d\u80fd\u79fb\uff0c\u5a01\u6b66\u4e0d\u80fd\u5c48\uff0c\u6b64\u4e4b\u8c13\u5927\u4e08\u592b\u3002\u201d\u6b66\u4fa0\u4eba\u7269\u5bf9\u5bcc\u8d35\u8d2b\u8d31\u5e76\u4e0d\u653e\u5728\u5fc3\u4e0a\uff0c\u66f4\u52a0\u4e0d\u5c48\u4e8e\u5a01\u6b66\uff0c\u8fd9\u5927\u4e08\u592b\u7684\u4e09\u6761\u6807\u51c6\uff0c\u4ed6\u4eec\u90fd\u4e0d\u96be\u505a\u5230\u3002\u5728\u672c\u4e66\u4e4b\u4e2d\uff0c\u6211\u60f3\u7ed9\u80e1\u6590\u589e\u52a0\u4e00\u4e9b\u8981\u6c42\uff0c\u8981\u4ed6\u201c\u4e0d\u4e3a\u7f8e\u8272\u6240\u52a8\uff0c\u4e0d\u4e3a\u54c0\u6073\u6240\u52a8\uff0c\u4e0d\u4e3a\u9762\u5b50\u6240\u52a8\u201d\u3002\u82f1\u96c4\u96be\u8fc7\u7f8e\u4eba\u5173\uff0c\u50cf\u8881\u7d2b\u8863\u90a3\u6837\u7f8e\u8c8c\u7684\u59d1\u5a18\uff0c\u53c8\u4e3a\u80e1\u6590\u6240\u503e\u5fc3\uff0c\u6b63\u5728\u4e24\u60c5\u76f8\u6d3d\u4e4b\u9645\u800c\u8f6f\u8bed\u592e\u6c42\uff0c\u4e0d\u7b54\u5141\u5979\u662f\u5f88\u96be\u7684\u3002\u82f1\u96c4\u597d\u6c49\u603b\u662f\u5403\u8f6f\u4e0d\u5403\u786c\uff0c\u51e4\u5929\u5357\u8d60\u9001\u91d1\u94f6\u534e\u5c4b\uff0c\u80e1\u6590\u81ea\u4e0d\u91cd\u89c6\uff0c\u4f46\u8fd9\u822c\u8bda\u5fc3\u8bda\u610f\u7684\u670d\u8f93\u6c42\u60c5\uff0c\u8981\u518d\u4e0d\u9976\u4ed6\u5c31\u66f4\u96be\u4e86\u3002\u6c5f\u6e56\u4e0a\u6700\u8bb2\u7a76\u9762\u5b50\u548c\u4e49\u6c14\uff0c\u5468\u94c1\u9e6a\u7b49\u4eba\u8fd9\u6837\u7ed9\u8db3\u4e86\u80e1\u6590\u9762\u5b50\uff0c\u4f4e\u58f0\u4e0b\u6c14\u7684\u6c42\u4ed6\u63ed\u5f00\u4e86\u5bf9\u51e4\u5929\u5357\u7684\u8fc7\u8282\uff0c\u80e1\u6590\u4ecd\u662f\u4e0d\u5141\u3002\u4e0d\u7ed9\u4eba\u9762\u5b50\u6050\u6015\u662f\u82f1\u96c4\u597d\u6c49\u6700\u96be\u505a\u5230\u7684\u4e8b\u3002 \u3000\u3000\u80e1\u6590\u6240\u4ee5\u5982\u6b64\uff0c\u53ea\u4e0d\u8fc7\u4e3a\u4e86\u953a\u963f\u56db\u4e00\u5bb6\u56db\u53e3\uff0c\u800c\u4ed6\u8ddf\u953a\u963f\u56db\u7d20\u4e0d\u76f8\u8bc6\uff0c\u6ca1\u4e00\u70b9\u4ea4\u60c5\u3002 \u3000\u3000\u76ee\u7684\u662f\u5199\u8fd9\u6837\u4e00\u4e2a\u6027\u683c\uff0c\u4e0d\u8fc7\u6ca1\u80fd\u5199\u5f97\u6709\u6df1\u5ea6\u3002\u53ea\u662f\u5728\u6211\u6240\u5199\u7684\u8fd9\u8bb8\u591a\u7537\u6027\u4eba\u7269\u4e2d\uff0c\u80e1\u6590\u3001\u4e54\u5cf0\u3001\u6768\u8fc7\u3001\u90ed\u9756\u3001\u4ee4\u72d0\u51b2\u8fd9\u51e0\u4e2a\u662f\u6211\u6bd4\u8f83\u7279\u522b\u559c\u6b22\u7684\u3002 \u3000\u3000\u6b66\u4fa0\u5c0f\u8bf4\u4e2d\uff0c\u53cd\u9762\u4eba\u7269\u88ab\u6b63\u9762\u4eba\u7269\u6740\u6b7b\uff0c\u901a\u5e38\u7684\u5904\u7406\u65b9\u5f0f\u662f\u8ba4\u4e3a\u201c\u8be5\u6b7b\u201d\uff0c\u4e0d\u518d\u591a\u52a0\u7406\u4f1a\u3002\u672c\u4e66\u4e2d\u5199\u5546\u8001\u592a\u8fd9\u4e2a\u4eba\u7269\uff0c\u4f01\u56fe\u8868\u793a\uff1a\u53cd\u9762\u4eba\u7269\u88ab\u6740\uff0c\u4ed6\u7684\u4eb2\u4eba\u5374\u4e0d\u8ba4\u4e3a\u4ed6\u8be5\u6b7b\uff0c\u4ecd\u7136\u5d07\u62dc\u4ed6\uff0c\u6df1\u6df1\u5730\u7231\u4ed6\uff0c\u81f3\u8001\u4e0d\u51cf\uff0c\u81f3\u6b7b\u4e0d\u53d8\uff0c\u5bf9\u4ed6\u7684\u6b7b\u4ea1\u6c38\u8fdc\u611f\u5230\u60b2\u4f24\uff0c\u5bf9\u5bb3\u6b7b\u4ed6\u7684\u4eba\u6c38\u8fdc\u5f3a\u70c8\u618e\u6068\u3002 \u3000\u3000\u4e00\u4e5d\u4e03\u4e94\u5e74\u4e00\u6708"}),showOnHomepage:!0}]}}]);