"use strict";(self.webpackChunkwebsite=self.webpackChunkwebsite||[]).push([[93870],{73052:(e,n,s)=>{s.r(n),s.d(n,{default:()=>a});var t=s(74848);const a=[{name:"\u51b0\u9b44\u5bd2\u5149\u5251",content:(0,t.jsx)(t.Fragment,{children:"\u7b2c\u4e00\u7ae0\u3000\u6a2a\u8de8\u6606\u4ed1\u6765\u7edd\u57df \u3000\u3000\u9488\u8ff7\u8235\u5931\u6015\u6606\u4ed1\u3000\u7a74\u5904\u5de2\u5c45\u4f55\u8db3\u8bba \u3000\u3000\u624b\u628a\u9ed1\u7eb9\u85e4\u7af9\u679d\u3000\u7075\u5c71\u9876\u4e0a\u53e9\u5929\u95e8 \u3000\u3000\u50cf\u4e00\u4e2a\u501a\u5929\u6795\u5730\u7684\u5de8\u4eba\uff0c\u6606\u4ed1\u5c71\u8109\u6a2a\u4e92\u5728\u65b0\u7586\u7684\u8fb9\u5883\uff0c\u90a3\u7ef5\u5ef6\u65e0\u9645\u7684\u5c71\u5cf0\uff0c\u7ec8\u5e74\u63a9\u76d6\u7740 \u55e4\u55e4\u7684\u767d\u96ea\uff0c\u906e\u65ad\u4e86\u897f\u85cf\u5bf9\u4e2d\u56fd\u5185\u9646\u7684\u4ea4\u901a\uff0c\u4ece\u53e4\u4ee5\u6765\uff0c\u9020\u4eba\u897f\u85cf\u7684\u65cf\u4eba\u5df2\u662f\u4e0d\u591a\uff0c\u800c\u8fd9\u4e00 \u6761\u8def\uff0c\u66f4\u662f\u95fb\u540d\u4e2d\u5916\u7684\u8270\u9669\u4e4b\u8def\u3002 \u3000\u3000\u7136\u800c\u6b64\u9645\u5c31\u6709\u4e00\u4e2a\u65c5\u4eba\uff0c\u5c45\u7136\u8d8a\u8fc7\u4e86\u6606\u4ed1\u5c71\uff0c\u8e0f\u8fdb\u4e86\u897f\u85cf\u7684\u571f\u5730\uff0c\u4ed6\u56de\u5934\u4e00\u671b\uff0c\u6606\u4ed1\u5c71 \u5df2\u662f\u8fdc\u8fdc\u7684\u88ab\u6487\u5728\u4ed6\u7684\u80cc\u540e\u4e86\uff0c\u60f3\u8d77\u90a3\u5de2\u5c45\u7a74\u865a\u7684\u8270\u9669\u65c5\u7a0b\uff0c\u4ed6\u4e0d\u7981\u50b2\u7136\u957f\u5578\uff0c\u88ab\u895f\u8fce\u98ce\uff0c \u6717\u541f\u8bd7\u53e5\uff01 \u3000\u3000\u8fd9\u4e00\u4e2a\u65c5\u4eba\u4e0d\u8fc7\u662f\u4e2a\u4e8c\u5341\u591a\u5c81\u7684\u5c11\u5e74\u4eba\uff0c\u540d\u53eb\u505a\u6842\u534e\u751f\uff0c\u6b63\u662f\u6b66\u5f53\u6d3e\u5317\u652f\u638c\u95e8\u4eba\uff0c\u53c8\u662f \u540d\u5217\u201c\u5929\u5c71\u4e03\u5251\u201d\u4e4b\u4e00\u7684\u6842\u4ef2\u660e\u7684\u7b2c\u4e8c\u4e2a\u513f\u5b50\u3002\u867d\u7136\u662f\u4ec6\u4ec6\u98ce\u5c18\uff0c\u4ecd\u63a9\u76d6\u4e0d\u4e86\u4ed6\u7684\u82f1\u98ce\u8c6a \u6c14\u3002 \u3000\u3000\u53ea\u542c\u5f97\u4ed6\u5bf9\u5927\u5c71\uff0c\u7eb5\u58f0\u7b11\u9053\uff1a\u201c\u6666\u660e\u7985\u5e08\u7684\u8bdd\u5012\u4e0d\u662f\u6545\u610f\u5413\u4eba\uff0c\u4f46\u82e5\u8bf4\u6500\u4e0a\u6606\u4ed1\uff0c\u5c31\u80fd \u591f\u6756\u53e9\u5929\u95e8\uff0c\u5374\u4e5f\u672a\u514d\u5938\u5927\uff01\u201d\u539f\u6765\u4ed6\u6717\u541f\u7684\u8fd9\u4e00\u9996\u8bd7\uff0c\u6b63\u662f\u5929\u5c71\u6d3e\u7684\u521b\u6d3e\u79df\u5e08\u3001\u90a3\u4f4d\u660e\u672b \u6e05\u521d\u7684\u4e00\u4ee3\u9ad8\u50e7\u2014\u2014\u6666\u660e\u60ee\u5e08\u5728\u6606\u4ed1\u7edd\u9876\u6240\u9898\u7684\u8bd7\u53e5\u3002 \u3000\u3000\u6842\u534e\u751f\u5e7c\u627f\u5bb6\u5b66\uff0c\u5c11\u8d1f\u76db\u540d\uff0c\u5728\u4e09\u5144\u5f1f\u4e4b\u4e2d\u4ee5\u4ed6\u6700\u4e3a\u6770\u51fa\uff0c\u53ef\u662f\u524d\u51e0\u5e74\u5728\u4e00\u4e2a\u5076\u7136\u7684\u573a \u5408\u4e2d\uff0c\u5374\u8d25\u5728\u5929\u5c71\u6d3e\u7b2c\u56db\u4ee3\u4f20\u4eba\u5510\u6653\u6f9c\u548c\u51af\u745b\u592b\u5987\u7684\u521b\u4e0b\uff0c\u4ed6\u7684\u7236\u4eb2\u540d\u5217\u201c\u5929\u5c71\u4e03\u5251\u201d\uff0c\u672c \u6765\u548c\u5929\u5c71\u6d3e\u7684\u6e0a\u6e90\u6781\u6df1\uff0c\u53ef\u662f\u4ed6\u5c31\u54bd\u4e0d\u4e0b\u8fd9\u53e3\u6c14\u3002\u6545\u6b64\u4ed6\u904d\u6e38\u540d\u5c71\u5927\u5ddd\uff0c\u904d\u8bb6\u9ad8\u4eba\u5f02\u58eb\uff0c\u7acb \u4e0b\u96c4\u5fc3\u5927\u5fd7\uff0c\u8981\u81ea\u6210\u4e00\u5bb6\uff0c\u518d\u521b\u5251\u6d3e\u3002 \u3000\u3000\u4ed6\u56de\u5934\u671b\u4e86\u4e00\u4e0b\u6606\u4ed1\uff0c\u518d\u8f6c\u8fc7\u8eab\u6765\uff0c\u51dd\u671b\u524d\u9762\u7684\u9ad8\u5c71\uff0c\u90a3\u662f\u8db3\u4e0e\u6606\u4ed1\u5171\u6bd4\u9ad8\u7684\u5ff5\u9752\u5510\u53e4 \u62c9\u5c71\uff0c\u7981\u4e0d\u4f4f\u7b11\u9053\uff1a\u201c\u4e00\u5c71\u8fd8\u6709\u4e00\u5c71\u9ad8\uff0c\u6211\u521d\u5230\u5929\u5c71\uff0c\u4ee5\u4e3a\u5929\u5c71\u9ad8\u4e0d\u53ef\u6500\uff0c\u800c\u4eca\u770b\u6765\uff0c\u6606\u4ed1 \u5c71\u548c\u542b\u9752\u5510\u53e4\u62c9\u5c71\u4e5f\u4e0d\u89c1\u5f97\u5c31\u4f4e\u4e8e\u5b83\u4e86\u3002\u542c\u8bf4\u897f\u85cf\u4e0e\u5c3c\u6cca\u5c14\u7684\u4ea4\u754c\uff0c\u8fd8\u6709\u4e00\u5ea7\u559c\u9a6c\u62c9\u96c5\u5c71\uff0c \u90a3\u624d\u662f\u5929\u4e0b\u7b2c\u4e00\u5ea7\u9ad8\u5c71\u3002\u53ef\u89c1\u5f97\u5929\u5916\u6709\u5929\uff0c\u4eba\u5916\u6709\u4eba\uff0c\u8fd9\u53e5\u8bdd\u662f\u4e0d\u9519\u7684\u3002\u6b66\u6797\u4e4b\u4e2d\u8fd1\u767e\u5e74 \u6765\uff0c\u90fd\u516c\u8ba4\u5929\u5c71\u5251\u6cd5\u662f\u5929\u4e0b\u81f3\u9ad8\u65e0\u4e0a\u7684\u5251\u6cd5\uff0c\u54c8\uff0c\u6211\u5c31\u504f\u504f\u4e0d\u4fe1\u8fd9\u4e00\u53e5\u8bdd\u3002\u5f53\u5e74\u6666\u660e\u7985\u5e08\u521b \u7acb\u5929\u5c71\u5251\u672f\uff0c\u53f7\u79f0\u5df2\u64b7\u53d6\u4e86\u548e\u5bb6\u5404\u6d3e\u5251\u672f\u7684\u7cbe\u534e\uff0c\u7136\u800c\u8fd9\u201c\u5404\u5bb6\u5404\u6d3e\u201d\u4f55\u66fe\u5305\u62ec\u4e86\u897f\u85cf\uff0c\u66f4 \u4f55\u66fe\u5305\u62ec\u4e86\u4e2d\u56fd\u4ee5\u5916\u7684\u5730\u65b9\uff1f\u201d\u9762\u5bf9\u5927\u5c71\uff0c\u5ffd\u53d1\u5947\u60f3\uff0c\u8981\u6a2a\u7a7f\u5927\u6f20\uff0c\u6500\u8d8a\u9ad8\u5c71\uff0c\u6d6a\u8ff9\u5929\u6daf\uff0c \u89c2\u5149\u5f02\u57df\uff0c\u5bfb\u6c42\u90a3\u7edd\u4e16\u7684\u6b66\u529f\u3002 \u3000\u3000\u6b63\u81ea\u51a5\u601d\uff0c\u5ffd\u542c\u5f97\u8fdc\u5904\u4f20\u6765\u4e00\u9635\u545c\u545c\u7684\u53f7\u89d2\u58f0\uff0c\u58f0\u5578\u957f\u7a7a\uff0c\u6479\u5c71\u56de\u54cd\uff0c\u58f0\u97f3\u5355\u8c03\u800c\u53c8\u51c4 \u5389\u3002\u8fd9\u65f6\u5929\u8fd1\u9ec4\u660f\uff0c\u5915\u9633\u8fd4\u7167\uff0c\u4e91\u5f69\u8ff7\u79bb\uff0c\u9c9c\u7ea2\u5982\u8840\uff0c\u52a0\u4e0a\u8fd9\u51c4\u5389\u7684\u53f7\u89d2\u58f0\u97f3\uff0c\u9976\u662f\u6842\u534e\u751f \u80c6\u5927\uff0c\u4e5f\u4e0d\u89c9\u6709\u4e9b\u6bdb\u9aa8\u70bc\u7136\u3002 \u3000\u3000\u6842\u534e\u751f\u8ddf\u7740\u90a3\u53f7\u89d2\u7684\u58f0\u97f3\uff0c\u8d70\u4e86\u4e00\u4f1a\uff0c\u8d70\u5230\u4e86\u4e00\u4e2a\u4e24\u5c71\u5939\u5cd9\u7684\u5e7d\u8c37\uff0c\u5c71\u8c37\u4e0b\u4e00\u7fa4\u85cf\u4eba\u5439 \u7740\u957f\u957f\u7684\u53f7\u89d2\uff0c\u62ac\u7740\u4e00\u4e13\u6709\u4e09\u4e2a\u5934\u7684\u5764\u50cf\uff0c\u795e\u7684\u4e00\u5934\u6d82\u767d\u8272\uff0c\u4e00\u5934\u9ed1\u8272\uff0c\u4e00\u5934\u7ea2\u8272\uff0c\u85cf\u6c11\u4eec \u5c31\u56f4\u7ed5\u5728\u62bc\u50cf\u7684\u5468\u56f4\uff0c\u4e14\u821e\u4e14\u6b4c\u3002 \u3000\u3000\u6842\u534e\u751f\u5728\u4eba\u897f\u85cf\u4e4b\u524d\uff0c\u66fe\u641c\u9605\u8fc7\u8bb8\u591a\u6709\u5173\u897f\u85cf\u98ce\u4fd7\u7684\u4e66\u7c4d\uff0c\u4e5f\u66fe\u8ddf\u4e00\u4e9b\u5230\u8fc7\u897f\u85cf\u7684\u9999\u5ba2 \u5b66\u4e60\u85cf\u8bed\uff0c\u77e5\u9053\u8fd9\u4e2a\u795e\u50cf\u4e43\u662f\u5587\u561b\u6559\u4e2d\u7684\u62a4\u6cd5\u7763\u201c\u8282\u90fd\u5df4\u201d\uff0c\u975e\u662f\u91cd\u5927\u7684\u8282\u65e5\u6216\u8005\u8981\u5411\u5c0a\u62bc \u6518\u89e3\u4ec0\u4e48\u7684\u8bdd\uff0c\u4e0d\u4f1a\u62ac\u5b83\u51fa\u6765\u3002\u9a7b\u8db3\u4e00\u542c\uff0c\u4f46\u542c\u5f97\u4ed6\u4eec\u5531\u7684\u662f\u897f\u85cf\u7684\u201c\u62db\u9b42\u6b4c\u201d\uff0c\u6b4c\u8f9e\u7684\u5927 \u610f\u8bf4\u9053\uff1a\u201c\u62dc\u8bf7\u5c0a\u8d35\u7684\u62a4\u6cd5\u795e\uff0c\u4f53\u5ff5\u4ed6\u4eec\u662f\u8fd8\u6765\u7684\u5f02\u4e61\u4eba\uff0c\u8bf7\u5927\u62bc\u4ece\u9b54\u9b3c\u624b\u4e2d\u593a\u56de\u4ed6\u4eec\u7684\u7075 \u9b42\uff0c\u8ba9\u6211\u4eec\u4e5f\u5f97\u5b89\u5fc3\uff01\u201d\u7ffb\u6765\u8986\u53bb\uff0c\u5531\u4e86\u4e00\u904d\uff0c\u53c8\u662f\u4e00\u904d\u3002 \u3000\u3000\u6842\u534e\u751f\u5fc3\u4e2d\u4e00\u51db\uff0c\u60f3\u9053\uff1a\u201c\u90a3\u91cc\u6765\u7684\u5f02\u4e61\u4eba\uff1f\u662f\u67d3\u4e86\u91cd\u75c5\u60eb\u662f\u53d7\u4e86\u4ec0\u4e48\u707e\u96be\uff1f\u8981\u60ca\u52a8\u85cf \u6c11\u8bf7\u51fa\u62a4\u6cd5\u62bc\u6765\u4e3a\u4ed6\u62db\u9b42\uff1f\u201d\u5fc3\u60f3\u201c\u62db\u9b42\u201d\u65e0\u6d4e\u4e8e\u4e8b\uff0c\u81ea\u5df1\u968f\u8eab\u5e26\u6709\u533b\u836f\uff0c\u4e0d\u5982\u8d70\u53bb\u770b\u770b\uff0c \u82e5\u80fd\u6551\u4eba\u4e00\u547d\uff0c\u80dc\u9020\u4e03\u7ea7\u6d6e\u5c60\u3002 \u3000\u3000\u85cf\u6c11\u4eec\u770b\u89c1\u53c8\u662f\u4e00\u4e2a\u5f02\u4e61\u7684\u65c5\u5ba2\u6765\u5230\uff0c\u90fd\u6709\u51e0\u5206\u8be7\u610f\uff0c\u4e00\u4e2a\u957f\u8001\u8d70\u4e86\u4e0a\u6765\uff0c\u6367\u4e0a\u4e00\u4e2a\u7528 \u9abc\u891b\u5934\u505a\u7684\u9152\u5668\uff0c\u76db\u7740\u6ee1\u6ee1\u7684\u9152\uff0c\u9152\u8272\u9752\u7eff\uff0c\u6cdb\u6709\u51e0\u70b9\u8840\u82b1\uff0c\u8fd9\u662f\u897f\u85cf\u5728\u4e3e\u884c\u5b97\u6559\u4eea\u5f0f\u65f6\uff0c \u8fce\u63a5\u5bbe\u5ba2\u7684\u89c1\u9762\u4e4b\u793c\uff0c\u9152\u662f\u7528\u9752\u68f5\u917f\u6210\u7684\u201c\u545b\u545b\u201d\u9152\uff0c\u5e26\u6709\u70b9\u82e6\u6da9\u7684\u5473\u9053\uff0c\u6842\u534e\u751f\u4e00\u53e3\u6c14\u559d \u4e86\uff0c\u53ea\u542c\u5f97\u90a3\u957f\u8001\u8bf4\u9053\uff1a\u201c\u5c0a\u8d35\u7684\u5ba2\u4eba\uff0c\u4e0d\u662f\u6211\u4eec\u6162\u5ba2\uff0c\u8fd9\u91cc\u6709\u4e24\u4e2a\u5728\u9b54\u9b3c\u57ce\u4e2d\u88ab\u52fe\u4e86\u9b42\u9b44 \u7684\u4eba\uff0c\u53ea\u6015\u4ed6\u4eec\u7684\u90aa\u6c14\u4f1a\u51b2\u72af\u4e86\u4f60\uff0c\u8fd8\u662f\u8bf7\u4f60\u5feb\u5feb\u79bb\u5f00\u4e86\u5427\u3002\u201d \u3000\u3000\u6842\u534e\u751f\u8be7\u5f02\u4e4b\u6781\uff0c\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\u9b54\u9b3c\u57ce\uff1f\u201d\u9661\u7136\u95f4\u72c2\u98d9\u9aa4\u8d77\uff0c\u90a3\u957f\u8001\u98a4\u58f0\u53eb\u9053\uff1a\u201c\u4f60 \u77a7\uff0c\u90a3\u4e0d\u5c31\u662f\u9b54\u9b3c\u57ce\uff1f\u201d\u6842\u534e\u751f\u968f\u7740\u4ed6\u4ef0\u5934\u4e00\u671b\uff0c\u4f46\u89c1\u5929\u8fb9\u7684\u4e91\u971e\u4e2d\uff0c\u9690\u9690\u73b0\u51fa\u57ce\u5ed3\u7684\u5f71 \u5b50\uff0c\u8857\u9053\u623f\u5c4b\u4f5b\u5854\u57ce\u5899\uff0c\u4f9d\u7a00\u53ef\u8fa8\uff0c\u970e\u90a3\u4e4b\u95f4\uff0c\u4e91\u5f69\u53d8\u5e7b\uff0c\u8fd9\u4e9b\u5e7b\u50cf\u53c8\u5f52\u4e8e\u65e0\u6709\u3002\u90a3\u4e9b\u85cf \u6c11\uff0c\u8fde\u957f\u8001\u5728\u5185\uff0c\u90fd\u5728\u4f4e\u5934\u819c\u62dc\u3002 \u3000\u3000\u6842\u534e\u751f\u4e0d\u7981\u54d1\u7136\u5931\u7b11\uff0c\u8fd9\u4e43\u662f\u201c\u6d77\u5e02\u5507\u697c\u201d\u7684\u5e7b\u666f\uff0c\u5728\u6d77\u8fb9\u548c\u5728\u6c99\u6f20\u4e4b\u4e0a\u90fd\u4e0d\u96be\u9047\u89c1\u3002 \u3000\u3000\u6842\u534e\u751f\u867d\u7136\u4e0d\u80fd\u50cf\u8fd1\u4ee3\u7684\u79d1\u5b66\u5bb6\u4e00\u6837\u89e3\u91ca\u5b83\u7684\u6210\u56e0\uff08\u6309\uff1a\u8fd9\u662f\u4e00\u79cd\u56e0\u5149\u7ebf\u6298\u5c04\u800c\u751f\u7684\u73b0 \u8c61\u3002\uff09\u4f46\u4ed6\u5728\u6a2a\u8fc7\u65b0\u7586\u7684\u5927\u6208\u58c1\u4e4b\u65f6\uff0c\u4e5f\u66fe\u89c1\u8fc7\u51e0\u6b21\uff0c\u4e00\u70b9\u4e0d\u4ee5\u4e3a\u5f02\u3002 \u3000\u3000\u90a3\u957f\u8001\u773c\u5149\u4e00\u77a5\uff0c\u89c1\u6842\u534e\u751f\u7fd8\u9996\u957f\u5929\uff0c\u5140\u7acb\u4e0d\u62dc\uff0c\u9a87\u7136\u53eb\u9053\uff1a\u201c\u9b54\u57ce\u73b0\u5f71\uff0c\u4f60\u4e0d\u6c42\u9976\uff0c \u8282\u90fd\u5df4\u4e5f\u5e87\u62a4\u4e0d\u4e86\uff01\u201d\u6842\u534e\u751f\u6b63\u60f3\u529d\u4ed6\u4e0d\u8981\u5eb8\u4eba\u81ea\u6270\uff0c\u5ffd\u7136\u72c2\u98ce\u53c8\u8d77\uff0c\u98ce\u4e2d\u6742\u6709\u8bf8\u79cd\u602a\u58f0\uff0c \u6709\u5982\u6218\u9f13\u96f7\u9e23\uff0c\u6709\u5982\u733f\u557c\u864e\u5578\uff0c\u6709\u5982\u60f3\u5987\u54c0\u6ce3\uff0c\u6709\u5982\u6218\u58eb\u9ad8\u6b4c\uff0c\u8bf8\u58f0\u6742\u4f5c\uff0c\u8361\u4eba\u5fc3\u9b44\u3002\u9aa4\u7136 \u95f4\uff0c\u4e00\u80a1\u72c2\u98ce\uff0c\u5939\u7740\u7802\u77f3\u53f0\u6765\uff0c\u628a\u90a3\u5c0a\u4e09\u5934\u62bc\u50cf\u7684\u201c\u8282\u90fd\u5df4\u201d\u987e\u5230\u5730\u4e0a\uff0c\u78b0\u5f97\u7a00\u70c2\uff01 \u3000\u3000\u85cf\u6c11\u4eec\u53d1\u4e00\u58f0\u558a\uff0c\u987e\u4e0d\u5f97\u9ec4\u6c99\u6251\u9762\uff0c\u767b\u65f6\u5728\u72c2\u98ce\u4e2d\u56db\u6563\u5954\u9003\u3002\u8981\u77e5\u9053\u201c\u8282\u90fd\u5df4\u201d\u4e43\u662f\u4ed6 \u4eec\u7684\u62a4\u6cd5\u5927\u795e\uff0c\u795e\u50cf\u5439\u584c\uff0c\u8fd9\u4e43\u662f\u62a4\u6cd5\u62bc\u7ed9\u9b54\u738b\u6253\u8d25\u7684\u51f6\u5146\uff0c\u6559\u4ed6\u4eec\u7109\u5f97\u4e0d\u60e7\uff01 \u3000\u3000\u72c2\u98d9\u6012\u5377\uff0c\u5730\u6697\u5929\u660f\uff0c\u6842\u534e\u751f\u4e5f\u51e0\u4e4e\u7ea6\u5439\u5f97\u7ad9\u7acb\u4e0d\u7a33\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u8fd9\u98ce\u52bf\u679c\u7136\u731b\u70c8\uff0c \u90a3\u98ce\u4e2d\u7684\u602a\u58f0\u66f4\u662f\u60ca\u4eba\uff0c\u98ce\u4ece\u85cf\u6c11\u6240\u8bf4\u7684\u9b54\u9b3c\u57ce\u90a3\u91cc\u53f0\u6765\uff0c\u602a\u4e0d\u5f97\u4ed6\u4eec\u4ee5\u4e3a\u662f\u9b54\u9b3c\u6240\u53d1\u7684\u65cb \u98ce\u4e86\u3002\u201d \u3000\u3000\u5e78\u800c\u72c2\u98ce\u6765\u5f97\u5feb\u53bb\u5f97\u4e5f\u5feb\uff0c\u7247\u523b\u4e4b\u540e\uff0c\u98ce\u505c\u6c99\u9759\uff0c\u53c8\u5177\u6c14\u660e\u5929\u6e05\uff0c\u4f46\u89c1\u6cd5\u5668\u4eea\u4ed7\uff0c\u6492\u4e86 \u6ee1\u5730\uff0c\u5730\u4e0a\u8eba\u7740\u4e24\u4e2a\u88ab\u9ec4\u6c99\u6df9\u6ca1\u4e86\u9762\u5b54\u7684\u4eba\uff0c\u770b\u4ed6\u4eec\u7684\u88c5\u675f\uff0c\u4f3c\u662f\u6c49\u4eba\uff0c\u5f53\u7136\u4e5f\u5c31\u662f\u85cf\u6c11\u4eec \u8981\u4e3a\u4ed6\u4eec\u201c\u62db\u9b42\u201d\u7684\u90a3\u4e24\u4e2a\u201c\u5f02\u4e61\u4eba\u201d\u4e86\u3002 \u3000\u3000\u6842\u534e\u751f\u89e3\u4e0b\u968f\u8eab\u63b3\u5e26\u7684\u6c34\u56ca\uff0c\u62c2\u62ed\u6389\u4ed6\u4eec\u9762\u4e0a\u7684\u5c18\u6c99\uff0c\u7528\u6e05\u6c34\u7ed9\u4ed6\u4eec\u6d17\u5f97\u4e7e\u4e7e\u51c0\u51c0\uff0c\u90a3 \u4e24\u4e2a\u6c49\u4eba\u9732\u51fa\u5e90\u5c71\u771f\u76f8\uff0c\u5374\u4eca\u6842\u534e\u751f\u9a87\u7740\u4e86\u3002 \u3000\u3000\u8fd9\u4e24\u4e2a\u4eba\u4e00\u4e2a\u662f\u5927\u7ea6\u56db\u5341\u6765\u5c81\u7684\u8f67\u987b\u5927\u6c49\uff0c\u53e6\u4e00\u4e2a\u5374\u662f\u5341\u4e09\u56db\u5c81\uff0c\u7709\u6e05\u76ee\u79c0\uff0c\u9762\u5982\u51a0\u738b \u7684\u7ae5\u5b50\uff01\u6842\u534e\u751f\u5931\u60ca\u53eb\u9053\uff1a\u201c\u8fd9\u4e0d\u662f\u5510\u8d5b\u82b1\u7684\u517b\u5b50\u5510\u7075\u5417\uff01\u201d \u3000\u3000\u5510\u8d5b\u82b1\u662f\u56db\u5ddd\u6697\u5668\u540d\u5bb6\u5510\u4e09\u5148\u751f\u7684\u5973\u513f\uff0c\u5979\u7684\u4e08\u592b\u738b\u6556\u5728\u6cb3\u5357\u5de1\u629a\u505a\u603b\u6355\u5934\uff0c\u88ab\u5929\u5c71\u5973 \u4fa0\u51af\u7433\u6240\u6740\uff0c\u5510\u8d5b\u82b1\u6ca1\u6709\u513f\u5b50\uff0c\u5c31\u6536\u517b\u4e86\u8fd9\u4e2a\u5510\u7075\u3002\u5f53\u771f\u662f\u7231\u903e\u4eb2\u751f\uff0c\u73cd\u540c\u62f1\u74a7\uff0c\u4f46\u5374\u6709\u4e00 \u6869\u5947\u602a\u4e4b\u5904\uff0c\u6842\u534e\u751f\u548c\u56db\u5ddd\u5510\u5bb6\u4e43\u662f\u4e16\u4ee3\u4ea4\u60c5\uff0c\u5374\u4ece\u6765\u4e0d\u66f9\u542c\u5230\u8fc7\u5510\u4e8c\u5148\u751f\u6216\u5510\u8d5b\u82b1\u63d0\u8fc7\u8fd9 \u5b69\u5b50\u7684\u6765\u5386\uff0c\u4e0d\u8fc7\u8fd9\u5b69\u4e8e\u5012\u4e70\u806a\u660e\u4f36\u4fd0\uff0c\u60f9\u4eba\u559c\u7231\uff0c\u6842\u534e\u751f\u5728\u5510\u5bb6\u4f5c\u5ba2\u4e4b\u65f6\uff0c\u4e5f\u66fe\u6559\u8fc7\u4ed6\u51e0 \u624b\u6b66\u529f\u3002 \u3000\u3000\u518d\u4ed4\u7ec6\u4e00\u770b\uff0c\u8fd9\u8f67\u987b\u5927\u6c49\u4e5f\u4f3c\u66fe\u76f8\u8bc6\uff0c\u9e2f\u7136\u60f3\u8d77\uff0c\u8fd9\u4eba\u4e43\u662f\u5df2\u6545\u7684\u5f81\u897f\u5927\u5c06\u519b\u5e74\u7fb9\u5c27\u7684 \u5fc3\u8179\u6b66\u58eb\u845b\u817e\u9f99\u3002\u845b\u817e\u9f99\u7684\u6b66\u529f\u5728\u5e74\u7fb9\u5c27\u5e10\u4e0b\u5012\u7b97\u4e0d\u5f97\u662f\u4e0a\u4e0a\u4e4b\u9009\uff0c\u4f46\u5374\u9887\u9976\u667a\u8ba1\uff0c\u719f\u8bfb\u5175 \u4e66\uff0c\u662f\u4ee5\u5e74\u7fb9\u5c27\u5bf9\u4ed6\u4f18\u793c\u6709\u52a0\uff0c\u5374\u53cd\u800c\u5728\u4e00\u822c\u6b66\u58eb\u4e4b\u4e0a\u3002\u540e\u6765\u5e74\u7fb9\u5c27\u56e0\u4e3a\u529f\u9ad8\u9707\u4e3b\uff0c\u88ab\u96cd\u6b63 \u8d2c\u5230\u676d\u5dde\u53bb\u5b88\u57ce\u95e8\uff0c\u7ec8\u4e8e\u8fd8\u662f\u514d\u4e0d\u4e86\u88ab\u671d\u5ef7\u5904\u6b7b\u3002\u5728\u5e74\u7fb9\u5c27\u88ab\u8d2c\u4e4b\u65f6\uff0c\u4eb2\u53cb\u90e8\u5c5e\u98ce\u6d41\u4e91\u6563\uff0c \u5927\u5bb6\u4ee5\u4e3a\u8fd9\u845b\u817e\u9f99\u6216\u8005\u4f1a\u8ffd\u968f\u4ed6\u4eec\u7684\uff0c\u4f46\u845b\u817e\u9f99\u4e5f\u968f\u7740\u4ed6\u7684\u88ab\u8d2c\u800c\u6d88\u58f0\u533f\u8ff9\uff0c\u5f53\u65f6\u4e5f\u9887\u66fe\u5f15 \u8d77\u6c5f\u6e56\u4e0a\u7684\u8c08\u8bba\uff0c\u4f46\u5927\u5bb6\u60f3\u5230\u201c\u6811\u5012\u7334\u5b50\u6563\u201d\u8fd9\u53e5\u8001\u8bdd\uff0c\u4e5f\u5c31\u4e0d\u4ee5\u4e3a\u5f02\u4e86\u3002\u60f3\u4e0d\u5230\u8fd9\u845b\u817e\u9f99 \u7adf\u7136\u4f1a\u51fa\u73b0\u5728\u897f\u85cf\uff0c\u800c\u4e14\u540c\u5510\u8d5b\u82b1\u7684\u517b\u5b50\u5728\u4e00\u8d77\uff01 \u3000\u3000\u8fd9\u771f\u662f\u96be\u4ee5\u60f3\u50cf\u7684\u4e8b\uff0c\u5510\u8d5b\u82b1\u600e\u820d\u5f97\u8ba9\u5510\u7075\u8ddf\u5e74\u7fb9\u5c27\u7684\u6b66\u58eb\u8fdc\u6d89\u897f\u85cf\uff1f\u5510\u5bb6\u4e00\u5411\u4e0d\u7406\u4e16 \u4e8b\uff0c\u4e0e\u5e74\u7fb9\u5c27\u7edd\u65e0\u74dc\u845b\uff0c\u66f4\u662f\u81ea\u5df1\u6df1\u77e5\uff0c\u8fd9\u5b69\u5b50\u53c8\u662f\u600e\u4e48\u8ba4\u8bc6\u845b\u817e\u9f99\u7684\uff1f\u82e5\u8bf4\u662f\u8fd9\u5b69\u5b50\u79c1 \u9003\uff0c\u4ed6\u53c8\u600e\u4e48\u6709\u8fd9\u80c6\u91cf\uff1f\u4ed6\u53c8\u600e\u820d\u5f97\u517b\u6bcd\u7684\u6df1\u601d\uff1f \u3000\u3000\u800c\u66f4\u5947\u602a\u7684\u662f\u4ed6\u4eec\u4e3a\u4ec0\u4e48\u4f1a\u660f\u8ff7\u5728\u8fd9\u6c99\u6f20\u4e4b\u4e0a\uff0c\u4ee5\u81f3\u60ca\u52a8\u85cf\u6c11\u4e3a\u4ed6\u4eec\u62db\u9b42\uff1f \u3000\u3000\u6842\u534e\u751f\u4ed4\u7ec6\u5ba1\u89c6\uff0c\u4ed6\u4eec\u8eab\u4e0a\u5e76\u65e0\u53d7\u4f24\u7684\u8ff9\u8c61\uff0c\u629a\u4ed6\u4eec\u8109\u606f\uff0c\u4e5f\u5f88\u6b63\u5e38\uff0c\u4e0d\u4f3c\u662f\u88ab\u9ad8\u624b\u70b9 \u4e86\u7a74\u9053\uff0c\u4f46\u89c1\u4ed6\u4eec\u53cc\u988a\u6655\u7ea2\uff0c\u6709\u5982\u4e2d\u9152\uff0c\u4e0d\u8bba\u600e\u6837\u6447\u4ffa\uff0c\u603b\u662f\u4e0d\u9192\uff0c\u9976\u662f\u6842\u534e\u751f\u4e43\u6b66\u5b66\u5927\u884c \u5bb6\uff0c\u4e5f\u770b\u4e0d\u51fa\u6240\u4ee5\u7136\u6765\uff01 \u3000\u3000\u6842\u534e\u751f\u7574\u8e48\u4e86\u4e00\u9635\uff0c\u9e2f\u7136\u60f3\u8d77\u6765\u9053\uff1a\u201c\u6211\u4f55\u4e0d\u53d6\u51fa\u5929\u5c71\u96ea\u83b2\u6765\u8bd5\u5b83\u4e00\u8bd5\uff1f\u201d\u5929\u5c71\u96ea\u83b2\u5584 \u89e3\u8bf8\u822c\u90aa\u6bd2\uff0c\u6842\u534e\u751f\u5728\u65b0\u7586\u6f2b\u6e38\u4e4b\u65f6\uff0c\u8d39\u4e86\u65e0\u7a77\u5fc3\u8840\uff0c\u624d\u5728\u5929\u5c71\u5317\u9ad8\u5cf0\u53d6\u4e86\u4e09\u6735\uff0c\u8fd9\u82b1\u5f00\u65f6 \u5927\u5982\u6d77\u7897\uff0c\u707f\u82e5\u4e91\u971e\uff0c\u800c\u4eca\u4e7e\u764c\u6536\u7f29\uff0c\u4e5f\u8fd8\u6709\u62f3\u5934\u822c\u5927\uff0c\u53d6\u51fa\u6765\u65f6\uff0c\u4f46\u89c9\u4e00\u7f15\u5e7d\u9999\uff0c\u6c81\u4eba\u9f3b \u89c2\u3002 \u3000\u3000\u6842\u534e\u751f\u5c06\u5929\u5c71\u96ea\u83b2\u653e\u8fd1\u4ed6\u4eec\u7684\u9f3b\u89c2\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u4ed6\u4eec\u7684\u9f3b\u606f\u6e10\u6e10\u8f6c\u79df\uff0c\u845b\u817e\u9f99\u9996\u5148\u9192 \u8f6c\uff0c\u89c1\u4f73\u534e\u751f\u6309\u5251\u6012\u89c6\uff0c\u9a87\u7136\u53eb\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff0c\u8fd9\u662f\u4ec0\u4e48\u5730\u65b9\uff1f\u201d\u6842\u534e\u751f\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u5f85 \u8fd9\u5b69\u5b50\u9192\u6765\uff0c\u518d\u548c\u4f60\u8bf4\u8bdd\u3002\u201d \u3000\u3000\u8fc7\u4e86\u534a\u664c\uff0c\u5510\u7075\u4e5f\u60a0\u60a0\u9192\u8f6c\uff0c\u4e00\u89c1\u6842\u534e\u751f\uff0c\u559c\u6781\u800c\u547c\uff1a\u201c\u6842\u53d4\u53d4\uff0c\u8fd9\u91cc\u662f\u9b54\u9b3c\u57ce\u5417\uff1f\u201d \u3000\u3000\u6842\u534e\u751f\u5fc3\u5934\u4e00\u8df3\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\u9b54\u9b3c\u57ce\uff1f\u201d\u4f46\u968f\u5373\u9762\u8272\u4e00\u6c89\uff0c\u9053\uff1a\u201c\u5510\u7075\uff0c\u4f60\u4e14\u5f85\u6211\u5148\u95ee \u8fd9\u53ae\u3002\u201d\u8f6c\u8fc7\u800c\u6765\uff0c\u540c\u845b\u817e\u9f99\u559d\u9053\uff1a\u201c\u4f60\u597d\u5927\u80c6\uff0c\u5c45\u7136\u6562\u62d0\u9a97\u5510\u5bb6\u7684\u5b69\u5b50\uff01\u201d \u3000\u3000\u845b\u817e\u9f99\u8fd9\u65f6\u795e\u667a\u5df2\u6e05\uff0c\u542c\u4e86\u5510\u7075\u7684\u79f0\u547c\uff0c\u4e5f\u8ba4\u51fa\u4e86\u6842\u534e\u751f\uff0c\u4ef0\u5929\u7b11\u9053\uff1a\u201c\u597d\u4e00\u4f4d\u6253\u62b1\u4e0d \u5e73\u7684\u4fa0\u5ba2\uff0c\u4e0d\u5206\u7682\u767d\uff0c\u5c31\u80e1\u4e71\u52a0\u4eba\u7f6a\u540d\uff0c\u4f60\u95ee\u8fd9\u4e2a\u5b69\u5b50\u662f\u6211\u62d0\u8d70\u4ed6\u7684\u5417\uff1f\u201d\u5510\u7075\u53eb\u9053\uff1a \u201c\u4e0d\uff0c\u6842\u53d4\u53d4\uff0c\u662f\u6211\u8ddf\u4ed6\u8d70\u7684\u3002\u201d\u6842\u534e\u751f\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u79bb\u5f00\u6bcd\u4eb2\uff0c\u8ddf\u4ed6\u51fa\u8d70\uff1f \u4f60\u77e5\u9053\u4ed6\u662f\u8c01\u5417\uff1f\u201d\u5510\u7075\u9053\uff1a\u201c\u4ed6\u662f\u845b\u817e\u9f99\u53d4\u53d4\u3002\u201d\u81f3\u4e8e\u4e3a\u4ec0\u4e48\u79bb\u5f00\u5510\u5bb6\uff0c\u4ed6\u5374\u907f\u800c\u4e0d\u7b54\uff0c \u770b\u4ed6\u773c\u73e0\u95ea\u52a8\u4e0d\u5b9a\uff0c\u7adf\u4f3c\u5927\u4eba\u4eec\u60f3\u4ec0\u4e48\u4e8b\u60c5\u4f3c\u7684\uff0c\u4e0d\u4f3c\u5b69\u5b50\u7684\u795e\u60c5\u3002 \u3000\u3000\u6842\u534e\u751f\u7591\u5fc3\u5927\u8d77\uff0c\u60f3\u4e0d\u51fa\u845b\u817e\u9f99\u662f\u7528\u4ec0\u4e48\u65b9\u6cd5\u9a97\u8d70\u8fd9\u4e2a\u806a\u660e\u7684\u5b69\u5b50\uff0c\u6309\u5251\u8bf4\u9053\uff1a\u201c\u628a\u5b69 \u5b50\u4ea4\u7ed9\u6211\uff0c\u4f60\u81ea\u5df1\u56de\u53bb\u5510\u5bb6\u8bf7\u7f6a\uff01\u201d\u5510\u7075\u9053\uff1a\u201c\u4e0d\uff0c\u6211\u81ea\u613f\u8ddf\u4ed6\uff0c\u8bf7\u4f60\u4e0d\u8981\u5c06\u845b\u53d4\u53d4\u96be \u4e3a\u3002\u201d\u6842\u534e\u751f\u4e0d\u7406\u5510\u7075\uff0c\u5411\u845b\u817e\u9f99\u65a5\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u4f60\u662f\u600e\u6837\u8ff7\u4e86\u8fd9\u5b69\u5b50\u7684\u5fc3\u7a8d\uff0c\u603b\u4e4b\uff0c\u4f60\u82e5 \u4e0d\u5c06\u8fd9\u5b69\u5b50\u7ed9\u6211\uff0c\u54b1\u4eec\u4eca\u65e5\u5c31\u6309\u7167\u6c5f\u6e56\u7684\u89c4\u77e9\uff0c\u624b\u5e95\u89c1\u4e2a\u5206\u660e\u3002\u201d\u845b\u817e\u9f99\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u6211\u4e0d \u662f\u4f60\u7684\u5bf9\u624b\uff0c\u8fd9\u70b9\u6211\u8fd8\u6709\u81ea\u77e5\u4e4b\u660e\u3002\u201d\u5f53\u5e74\u5e74\u7fb9\u5c27\u8fdb\u5175\u9752\u6d77\uff0c\u6842\u534e\u751f\u4e49\u6551\u4e00\u4e2a\u571f\u53f8\u7a81\u56f4\uff0c\u66fe \u6740\u4f24\u8fc7\u5e74\u7fb9\u5c27\u7684\u51e0\u540d\u6b66\u58eb\uff0c\u845b\u80dc\u9f99\u81ea\u662f\u77e5\u9053\u8fd9\u4ef6\u4e8b\u60c5\u3002\u6545\u6b64\u600e\u6837\u4e5f\u4e0d\u80af\u4e0e\u6842\u534e\u751f\u52a8\u624b\u3002\u6842\u534e \u751f\u9053\uff1a\u201c\u597d\uff0c\u90a3\u5c31\u628a\u5b69\u5b50\u4ea4\u7ed9\u6211\uff01\u201d\u845b\u817e\u9f99\u9053\uff1a\u201c\u4e0d\uff0c\u5b69\u5b50\u4e5f\u4e0d\u80fd\u4ea4\u7ed9\u4f60\u3002\u201d\u6842\u534e\u751f\u6012\u9053\uff1a \u201c\u4e8f\u4f60\u8fd8\u7b97\u662f\u6c5f\u6e56\u4e0a\u7684\u4e00\u53f7\u4eba\u7269\uff0c\u597d\u4e0d\u77e5\u7f9e\uff0c\u4f60\u6015\u6b7b\u4e48\uff1f\u201d \u3000\u3000\u845b\u817e\u9f99\u4ef0\u5929\u7b11\u9053\uff1a\u201c\u82e5\u662f\u6015\u6b7b\uff0c\u6211\u4e5f\u4e0d\u80af\u5386\u8fd9\u4e48\u591a\u7684\u8270\u9669\uff0c\u5e26\u8fd9\u5b69\u5b50\u5230\u897f\u85cf\u6765\u4e86\u3002\u6211\u4e0d \u662f\u6015\u6b7b\uff0c\u6211\u662f\u6015\u6211\u6b7b\u540e\uff0c\u6ca1\u4eba\u6276\u52a9\u8fd9\u4e2a\u5b69\u5b50\uff01\uff01\u201d \u3000\u3000\u6842\u534e\u751f\u65a5\u9053\uff1a\u201c\u80e1\u8bf4\uff01\u8fd9\u5b69\u5b50\u81ea\u6709\u4ed6\u7684\u517b\u6bcd\u7167\u6599\uff0c\u8981\u4f60\u64cd\u4ec0\u4e48\u5fc3\u3002\u201d\u624b\u6309\u5251\u67c4\uff0c\u4f5c\u52bf\u5c31 \u8981\u52a8\u624b\u3002\u5510\u7075\u53eb\u9053\uff1a\u201c\u6842\u53d4\u53d4\uff0c\u4f60\u82e5\u662f\u75bc\u6211\uff0c\u5c31\u8bf7\u4f60\u4e0d\u8981\u628a\u4ed6\u96be\u4e3a\uff01\u201d\u6842\u534e\u751f\u9053\uff1a\u201c\u4e3a\u4ec0 \u4e48\uff1f\u201d\u5510\u9738\u9053\uff1a\u201c\u6211\u8fd9\u4e00\u751f\u662f\u8ddf\u5b9a\u845b\u53d4\u53d4\u7684\u4e86\u3002\u4f60\u82e5\u5c06\u4ed6\u6740\u6b7b\uff0c\u53eb\u6211\u4f9d\u9760\u8c01\u4eba\uff1f\u201d \u3000\u3000\u6842\u534e\u751f\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u76ef\u7740\u5510\u7075\u95ee\u9053\uff1a\u201c\u4f60\u4eca\u5e74\u6015\u6709\u5341\u4e09\u56db\u5c81\u4e86\u5427\uff1f\u600e\u7684\u8fd8\u662f\u8fd9\u822c\u7684\u4e0d \u61c2\u4e8b\u4f53\uff1f\u5510\u516c\u516c\u548c\u4f60\u7684\u5988\u5988\u5f85\u4f60\u8fd8\u4e0d\u591f\u597d\u4e48\uff1f\u4f60\u600e\u4e48\u5c31\u4e0d\u5ff5\u4ed6\u4eec\u7684\u517b\u80b2\u4e4b\u6069\uff1f\u201d\u5510\u7075\u773c\u6cea\u6b32 \u6d41\uff0c\u4ed6\u672c\u60f3\u4e0d\u8bf4\uff0c\u5374\u6015\u4f73\u534e\u751f\u5c06\u845b\u817e\u9f99\u6740\u6389\uff0c\u773c\u73e0\u513f\u8f6c\u4e86\u51e0\u8f6c\uff0c\u7109\u7136\u53eb\u9053\uff1a\u201c\u4e0d\uff0c\u6211\u4e0d\u662f\u5510 \u5bb6\u7684\u5b69\u5b50\uff01\u201d\u6842\u534e\u751f\u6012\u9053\uff1a\u201c\u4f60\u81ea\u5c0f\u4fbf\u662f\u5510\u5bb6\u629a\u517b\uff0c\u517b\u6bcd\u6069\u60c5\u6b64\u751f\u6bcd\u66f4\u6df1\uff1a\u201d\u5510\u7075\u53eb\u9053\uff1a \u201c\u6211\u8fd8\u6709\u81ea\u5df1\u7684\u7236\u4eb2\uff01\u201d\u6842\u534e\u751f\u5fc3\u4e2d\u4e00\u52a8\uff0c\u9053\uff1a\u201c\u8c01\uff1f\u201d\u5510\u7075\u50b2\u7136\u7b54\u9053\uff1a\u201c\u6211\u7684\u7236\u4eb2\u662f\u66fe\u7edf \u7387\u767e\u4e07\u5927\u519b\u7684\u5e74\u5927\u5c06\u519b\uff01\u201d \u3000\u3000\u6842\u534e\u751f\u5fc3\u5934\u4e00\u51db\uff0c\u600e\u6837\u4e5f\u6599\u60f3\u4e0d\u5230\uff1a\u539f\u6765\u5510\u8d5b\u82b1\u6536\u517b\u7684\u7adf\u7136\u662f\u5e74\u7fb9\u5c27\u7684\u513f\u5b50\u3002\u53ea\u542c\u5f97\u5510 \u7075\u786c\u54bd\u53eb\u9053\uff1a\u201c\u517b\u6bcd\u7684\u6df1\u601d\u56fa\u7136\u4e0d\u5bb9\u5fd8\u8bb0\uff0c\u751f\u8eab\u4e4b\u7236\u7684\u51a4\u4ec7\uff0c\u505a\u513f\u5b50\u7684\u66f4\u662f\u4e0d\u80fd\u4e0d\u62a5\u5440\uff01\u201d \u3000\u3000\u5e74\u7fb9\u5c27\u5f53\u5e74\u4e3a\u96cd\u6b63\u4e1c\u5f81\u897f\u8ba8\uff0c\u52a9\u7ea6\u4e3a\u8650\uff0c\u8c6a\u6770\u4e49\u58eb\uff0c\u65e0\u4e0d\u5207\u9f7f\u75db\u6068\uff0c\u4ed6\u540e\u6765\u88ab\u96cd\u6b63\u6740 \u6389\uff0c\u96cd\u6b63\u88ab\u5415\u56db\u5a18\u6740\u6389\uff0c\u8fd9\u4e24\u4ef6\u4e8b\u60c5\u90fd\u66fe\u7ecf\u5927\u5feb\u4eba\u5fc3\u3002\u6842\u534e\u751f\u7709\u5934\u4e00\u76b1\uff0c\u9053\uff1a\u201c\u54a6\uff0c\u4f60\u8fd8\u8981 \u62a5\u4ec0\u4e48\u4ec7\uff1f\u201d\u5510\u7075\u4ee5\u8896\u62ed\u6cea\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u7236\u6bcd\u4e4b\u4ec7\uff0c\u4e0d\u5171\u6234\u5929\uff0c\u96be\u9053\u6211\u7684\u7236\u4eb2\u5c31\u662f\u5e94\u8be5\u6789 \u6b7b\u7684\u5417\uff1f\u201d\u6842\u534e\u751f\u5fc3\u4e2d\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u7684\u7236\u4eb2\u6b63\u662f\u8be5\u6b7b\u7684\u4eba\uff01\u201d\u8fd9\u8bdd\u5728\u820c\u5c16\u6253\u6eda\u4e86\u597d\u51e0 \u904d\uff0c\u7ec8\u4e8e\u8fd8\u662f\u541e\u4e86\u56de\u53bb\u3002\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u5e74\u7fb9\u5c27\u6709\u7f6a\uff0c\u8fd9\u5b69\u5b50\u65e0\u7f6a\u3002\u4ed6\u5c06\u6765\u603b\u5f97\u660e\u767d\u4ed6\u7236\u4eb2\u662f \u600e\u6837\u7684\u4eba\uff0c\u4f46\u8fd9\u65f6\u4ed6\u5e74\u7eaa\u8fd8\u5c0f\uff0c\u6211\u82e5\u8bf4\u4e86\uff0c\u4ed6\u8fd9\u7a1a\u5f31\u7684\u5fc3\u7075\u5982\u4f55\u8d1f\u62c5\u5f97\u8d77\uff0c\u201d\u547c\u4e86\u53e3\u6c14\uff0c\u5fae \u7b11\u95ee\u9053\uff1a\u201c\u4f60\u5f85\u600e\u6837\u62a5\u4ec7\uff1f\u201d \u3000\u3000\u5510\u7075\u9f13\u8d77\u773c\u775b\u5bf9\u6842\u534e\u751f\u770b\u4e86\u51e0\u773c\uff0c\u5bf9\u6842\u534e\u751f\u7684\u654c\u610f\u7a0d\u7a0d\u51cf\u8f7b\uff0c\u8bf4\u9053\uff1a\u201c\u845b\u53d4\u53d4\u6559\u6211\uff0c\u6e05 \u5ef7\u5bf9\u897f\u85cf\u662f\u97ad\u957f\u83ab\u53ca\uff0c\u54b1\u4eec\u5728\u8fd9\u91cc\u7acb\u4e0b\u57fa\u4e1a\uff0c\u5c06\u6765\u4fbf\u53ef\u4ee5\u5728\u8fd9\u91cc\u4e3e\u5175\uff0c\u80dc\u53ef\u4e3a\u738b\uff0c\u8d25\u65b9\u53ef\u4ee5 \u636e\u5730\u56fa\u5b88\u3002\u201d\u8bf4\u8bdd\u7684\u795e\u6c14\uff0c\u4fe8\u7136\u5c31\u50cf\u4ed6\u7236\u4eb2\u751f\u524d\u53d1\u53f7\u65bd\u4eca\u7684\u795e\u60c5\u3002 \u3000\u3000\u6842\u534e\u751f\u5fc3\u9053\uff1a\u201c\u771f\u4e0d\u6127\u662f\u5e74\u7fb9\u5c27\u7684\u513f\u5b50\uff0c\u8fd9\u845b\u817e\u9f99\u4e5f\u4e0d\u6127\u662f\u5e74\u7fb9\u5c27\u7684\u667a\u56ca\uff0c\u5982\u6b64\u6df1\u8c0b\u8fdc \u8651\uff1a\u8fd9\u4e8b\u60c5\u6211\u53ef\u4e0d\u80fd\u4e0d\u7ba1\u4e86\uff01\u201d\u5c06\u5510\u7075\u8f7b\u8f7b\u7684\u62c9\u4e86\u8fc7\u6765\uff0c\u542b\u7b11\u8bf4\u9053\uff1a\u201c\u4f60\u8fd9\u5b69\u4e8e\u5012\u6709\u5fd7\u6c14\uff0c \u4f46\u662f\u975e\u5584\u6076\uff0c\u8fd9\u5f97\u5206\u4e2a\u6e05\u695a\u3002\u201d\u5510\u7075\u9053\uff1a\u201c\u600e\u4e48\uff0c\u4f60\u8bf4\u8bf4\u6211\u542c\u3002\u201d\u6842\u534e\u751f\u9053\uff1a\u201c\u4f60\u73b0\u5728\u53d7\u4e86 \u6bd2\u9999\uff0c\u5e94\u8be5\u5148\u7761\u4e00\u89c9\uff0c\u7761\u9192\u4e4b\u540e\uff0c\u6211\u6301\u76f8\u4f60\u8bf4\u3002\u201d\u8f7b\u8f7b\u4e00\u63c9\uff0c\u5f00\u4e86\u4ed6\u7684\u6655\u7761\u7a74\uff0c\u7136\u540e\u8f6c\u8fc7\u8eab \u6765\uff0c\u6012\u65a5\u845b\u817e\u9f99\u9053\uff1a\u201c\u597d\u5440\uff0c\u4f60\u5c06\u4e00\u4e2a\u597d\u597d\u7684\u5b69\u5b50\u5f15\u4eba\u6b67\u9014\uff0c\u8fd9\u7f6a\u8fc7\u8fd8\u5728\u6740\u4eba\u653e\u706b\u4e4b\u4e0a\uff01\u201d \u3000\u3000\u845b\u817e\u9f99\u9053\uff1a\u201c\u6211\u6551\u4ed6\u4e3a\u7236\u62a5\u4ec7\uff0c\u53c8\u6709\u4ec0\u4e48\u7f6a\u8fc7\u4e86\uff1f\u201d\u6842\u534e\u751f\u9053\uff1a\u201c\u5e74\u7fb9\u5c27\u6b64\u4eba\uff0c\u56fd\u4eba\u7686 \u65e5\u53ef\u6740\uff0c\u600e\u503c\u5f97\u4e3a\u4ed6\u62a5\u4ec7\uff1f\u201d\u845b\u817e\u9f99\u9053\uff1a\u201c\u522b\u4eba\u6740\u5e74\u7fb9\u5c27\u72b9\u81ea\u53ef\u8bf4\uff0c\u96cd\u6b63\u662f\u5e74\u7fb9\u5c27\u4e00\u624b\u6276\u8d77 \u6765\u7684\u4eba\uff0c\u6211\u4e0d\u4e3a\u5e74\u5927\u5c06\u519b\u62a5\u4ec7\uff0c\u600e\u6d88\u5f97\u8fd9\u53e3\u80f8\u4e2d\u6124\u6c14\uff1f\u4f55\u51b5\u5e74\u5927\u5c06\u519b\u751f\u524d\u4ee5\u56fd\u58eb\u65f6\u6211\uff0c\u6211\u81ea \u5f53\u4ee5\u56fd\u58eb\u62a5\u4e4b\uff0c\u522b\u7684\u4eba\u5bf9\u4ed6\u5982\u4f55\u8bae\u8bba\uff0c\u6211\u90fd\u4e0d\u7ba1\u3002\u201d\u6842\u534e\u751f\u5fc3\u9053\uff1a\u201c\u66f9\u64cd\u4e5f\u6709\u77e5\u5fc3\u53cb\uff0c\u6b64\u8bdd \u679c\u7136\u4e0d\u5047\u3002\u201d\u773c\u73e0\u4e00\u8f6c\uff0c\u95ee\u9053\uff1a\u201c\u96cd\u6b63\u4e0d\u7ed9\u5415\u56db\u5a18\u6740\u4e86\u5417\uff1f\u4f60\u8981\u62a5\u4ec7\uff0c\u8fd9\u4ec7\u4e5f\u5df2\u62a5\u4e86\uff01\u201d\u845b \u817e\u9f99\u9053\uff1a\u201c\u96cd\u6b63\u867d\u7136\u6b7b\u4e86\uff0c\u8fd9\u6c5f\u5c71\u8fd8\u662f\u7231\u65b0\u89c9\u7f57\u6c0f\u7684\u6c5f\u5c71\uff01\u201d \u3000\u3000\u6842\u534e\u751f\u51db\u7136\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u60f3\u4e0d\u5230\u4f60\u8fd9\u5e74\u7fb9\u5c27\u7684\u5fc3\u8179\u6b66\u58eb\uff0c\u8bf4\u8bdd\u7684\u53e3\u543b\u7adf\u548c\u6c5f\u6e56\u4e49\u58eb\u76f8 \u540c\uff01\u597d\u5440\uff0c\u4ed6\u82e5\u662f\u4e3a\u6c49\u65cf\u62a5\u4ec7\uff0c\u9a71\u9664\u631e\u864f\uff0c\u8fd9\u6211\u4e0d\u7ba1\u3002\u4f46\u4f60\u5f97\u5e26\u8fd9\u5b69\u5b50\uff0c\u5148\u4ea4\u56de\u4ed6\u7684\u517b\u6bcd\uff0c \u5f85\u4ed6\u957f\u5927\u4e4b\u540e\uff0c\u518d\u7531\u4ed6\u81ea\u62e9\u524d\u9014\uff0c\u8fd9\u8bdd\u4f60\u4f9d\u4e0d\u4f9d\u4ece\uff1f\u201d \u3000\u3000\u845b\u817e\u9f99\u6c89\u601d\u6709\u9877\uff0c\u9053\uff1a\u201c\u4e5f\u7f62\uff0c\u770b\u5728\u4f60\u4eca\u65e5\u6551\u6211\u4eec\u4e8c\u4eba\u6027\u547d\u7684\u4efd\u4e0a\uff0c\u6211\u4e5f\u5f97\u5356\u4f60\u4e00\u4e2a\u4eba \u60c5\u3002\u201d\u5176\u5b9e\u845b\u817e\u9f99\u7acb\u5fd7\u63a8\u7ffb\u6e05\u5ef7\u7684\u76ee\u7684\u5374\u548c\u6c5f\u6e56\u7684\u4e49\u58eb\u5927\u4e0d\u76f8\u540c\uff0c\u4ed6\u662f\u60f3\u5229\u7528\u5e74\u7fb9\u5c27\u7684\u513f\u5b50 \u4f5c\u4e3a\u53f7\u53ec\uff0c\u4ee5\u9042\u4e2a\u4eba\u91ce\u5fc3\uff1a\u540c\u65f6\u89c1\u5510\u7075\u5929\u8d4b\u806a\u660e\uff0c\u662f\u4e2a\u53ef\u4ee5\u6276\u5f97\u8d77\u6765\u4ee5\u56fe\u738b\u9738\u4e4b\u4e1a\u7684\u4eba\uff0c\u6545 \u6b64\u4e0d\u60dc\u8d39\u5c3d\u5fc3\u673a\uff0c\u51b2\u96be\u72af\u9669\uff0c\u5c06\u8fd9\u5b69\u5b50\u8bf1\u51fa\u5510\u5bb6\u3002 \u3000\u3000\u4f73\u534e\u751f\u5374\u628a\u845b\u817e\u9f99\u8fd9\u4eba\u770b\u9519\u4e86\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u5e74\u7fb9\u5c27\u4e00\u6b7b\uff0c\u4ed6\u7684\u65e7\u5c5e\u60f3\u6487\u6e05\u8fd8\u6765\u4e0d\u53ca\uff0c \u8fd9\u4eba\u5c45\u7136\u6709\u6b64\u50bb\u52b2\uff0c\u867d\u8bf4\u611a\u5fe0\uff0c\u4e5f\u8fd8\u7b97\u5f97\u662f\u4e2a\u6709\u8840\u6027\u7684\u7537\u5b50\u3002\u201d\u89c1\u4ed6\u7b54\u5141\uff0c\u6b23\u7136\u8bf4\u9053\uff1a\u201c\u541b \u5b50\u4e00\u8a00\u2026\u2026\u201d\u845b\u817e\u9f99\u63a5\u53e3\u8bf4\u9053\uff1a\u201c\u5feb\u9a6c\u4e00\u97ad\uff01\u201d\u6842\u534e\u751f\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u597d\uff0c\u6211\u76f8\u4fe1\u4f60\uff01\u4f60\u628a \u8fd9\u5b69\u5b50\u5e26\u56de\u5510\u5bb6\uff0c\u6211\u5199\u4e00\u5c01\u4fe1\u7ed9\u5510\u4e8c\u5148\u751f\uff0c\u8bf7\u4ed6\u4eec\u4e0d\u8981\u5c06\u4f60\u602a\u8d23\u3002\u201d\u62ac\u8d77\u4e00\u5757\u85cf\u6c11\u6389\u843d\u5728\u5730 \u4e0a\u7684\u4e00\u5757\u7f8a\u76ae\uff0c\u7528\u5251\u5c16\u523a\u51fa\u4e86\u51e0\u884c\u4e66\u500d\uff0c\u845b\u817e\u9f99\u90d1\u91cd\u7684\u5c06\u7f8a\u76ae\u4e66\u85cf\u5728\u8d34\u8eab\u7684\u6c57\u8863\u888b\u91cc\uff0c\u5176\u5b9e \u5fc3\u4e2d\u6b63\u5728\u53e6\u4f5c\u76d8\u7b97\u3002 \u3000\u3000\u6842\u534e\u751f\u6b63\u60f3\u628a\u5510\u7075\u5f04\u9192\uff0c\u60f3\u8d77\u4e00\u4e8b\uff0c\u4f4f\u624b\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u521a\u624d\u8bf4\u4ec0\u4e48\u9b54\u9b3c\u57ce\uff1f\u662f\u600e\u4e48\u4e2d\u4e86 \u6bd2\u7684\uff1f\u201d\u845b\u817e\u9f99\u9053\uff1a\u201c\u4e3a\u4e86\u8fd9\u9b54\u9b3c\u57ce\uff0c\u6211\u66fe\u7ecf\u6697\u4e2d\u7aa5\u63a2\u4e86\u597d\u51e0\u6b21\u3002\u524d\u51e0\u6b21\u4e0d\u6562\u8d70\u8fd1\uff0c\u53ea\u5728\u5bf9 \u9762\u7684\u5c71\u5cf0\u4e86\u671b\uff0c\u8fd9\u6b21\u7a0d\u5fae\u8d70\u8fd1\uff0c\u5374\u4e0d\u6599\u4e00\u9635\u5f02\u98ce\u5439\u6765\uff0c\u4fbf\u544a\u660f\u8ff7\u4e0d\u9192\u4e86\u3002\u201d\u6842\u534e\u751f\u8be7\u9053\uff1a \u3000\u3000\u201c\u5f53\u771f\u6709\u4ec0\u4e48\u9b54\u9b3c\u57ce\uff1f\u201d\u845b\u817e\u9f99\u9053\uff1a\u201c\u8fd9\u91cc\u7684\u85cf\u4eba\uff0c\u4eba\u4eba\u90fd\u80fd\u8bf4\u4e00\u5927\u4e32\u9b54\u9b3c\u57ce\u7684\u5947\u8ff9\uff0c \u6211\u770b\u53ea\u6015\u6709\u751a\u4e48\u5f02\u4eba\u4f4f\u5728\u91cc\u9762\u4e5f\u8bf4\u4e0d\u5b9a\uff1f\u6211\u5728\u5bf9\u9762\u5c71\u5cf0\u4e86\u671b\uff0c\u5c31\u66fe\u7ecf\u6709\u4e00\u4e24\u6b21\u89c1\u8fc7\u91cc\u9762\u6709\u67ad \u67ad\u7684\u708a\u70df\u5347\u8d77\uff0c\u53f0\u98ce\u4e4b\u65f6\uff0c\u90a3\u5404\u79cd\u5404\u6837\u7684\u602a\u58f0\u4e5f\u771f\u662f\u8361\u4eba\u5fc3\u9b44\uff01\u201d\u6842\u534e\u751f\u9053\uff1a\u201c\u6211\u542c\u8fc7 \u5566\u3002\u201d\u5bf9\u98ce\u4e2d\u7684\u602a\u58f0\uff0c\u5c1a\u4e0d\u89c9\u600e\u4e48\uff0c\u542c\u5f97\u91cc\u9762\u6709\u708a\u70df\u5374\u5927\u662f\u5f15\u8d77\u5fc3\u4e2d\u7684\u7591\u60d1\uff0c\u95ee\u9053\uff1a\u201c\u90a3\u4e48 \u4f60\u89c1\u5230\u5c71\u4e2d\u679c\u7136\u662f\u6709\u4e00\u5ea7\u57ce\u4e48\uff1f\u201d\u845b\u817e\u9f99\u9053\uff1a\u201c\u6628\u665a\u6211\u4eec\u5192\u9669\u8d70\u8fdb\u5c71\u8c37\uff0c\u53ea\u662f\u9690\u9690\u770b\u89c1\u5c71\u9876 \u6709\u4e00\u5ea7\u5706\u5854\u5f62\u7684\u5c16\u9876\uff0c\u8fd8\u672a\u770b\u5f97\u6e05\u695a\uff0c\u4e00\u9635\u72c2\u98ce\u53f0\u6765\uff0c\u98ce\u4e2d\u5e26\u6709\u83ab\u540d\u5176\u5999\u7684\u5f02\u9999\uff0c\u6211\u4eec\u4fbf\u4e00 \u76f4\u7761\u5230\u4f60\u6765\u7684\u65f6\u5019\u624d\u9192\uff01\u201d \u3000\u3000\u4f73\u534e\u751f\u5fc3\u9053\uff1a\u201c\u770b\u6765\u8fd9\u9b54\u9b3c\u57ce\u5f53\u771f\u662f\u6709\u4e00\u4e9b\u53e4\u602a\uff0c\u6211\u65e2\u5230\u6b64\u95f4\uff0c\u5012\u4e0d\u53ef\u4e0d\u53bb\u4e00\u770b\u4e86\u3002\u201d \u3000\u3000\u4f38\u51fa\u624b\u638c\uff0c\u5728\u5510\u7075\u8eab\u4e0a\u8f7b\u8f7b\u7684\u62cd\u4e86\u4e24\u4e0b\uff0c\u89e3\u5f00\u4ed6\u7684\u7a74\u9053\uff0c\u5510\u7075\u4e00\u89c9\u9192\u6765\uff0c\u89c1\u6842\u534e\u751f\u4e0e\u845b \u817e\u9f99\u76f8\u5411\u800c\u7acb\uff0c\u773c\u5149\u795e\u8272\uff0c\u751a\u662f\u67d4\u548c\uff0c\u559c\u800c\u7b11\u9053\uff1a\u201c\u4e24\u4f4d\u53d4\u53d4\uff0c\u4f60\u4eec\u548c\u89e3\u4e86\u4e48\uff1f\u201d\u845b\u817e\u9f99 \u9053\uff1a\u201c\u6211\u548c\u4f60\u7684\u6842\u53d4\u53d4\u672c\u6765\u5c31\u6ca1\u6709\u4ec0\u4e48\u51a4\u4ec7\uff0c\u8bf4\u6e05\u695a\u4e86\uff0c\u4ed6\u5f53\u7136\u4e0d\u4f1a\u518d\u5c06\u54b1\u4eec\u96be\u4e3a\u3002\u201d\u6545\u610f \u5f3a\u8c03\u201c\u54b1\u4eec\u201d\u4e8c\u5b57\uff0c\u5728\u5b69\u5b50\u7684\u5fc3\u7075\u4e2d\u79cd\u4e0b\u8fd9\u6837\u7684\u89c2\u5ff5\uff1a\u53ea\u6709\u4ed6\u624d\u662f\u4e0e\u81ea\u5df1\u7d27\u5bc6\u8054\u7ed3\u5728\u4e00\u8d77\u7684 \u4eba\uff0c\u65e0\u5f62\u4e2d\u628a\u6842\u534e\u751f\u9694\u5f00\u4e00\u5c42\u4e86\u3002\u8fd1\u4ee3\u7684\u513f\u7ae5\u5fc3\u7406\u5b66\u5bb6\u6709\u6240\u8c13\u7684\u201c\u6697\u793a\u6559\u80b2\u201d\u7684\u65b9\u6cd5\uff0c\u845b\u817e \u80fd\u5f53\u7136\u4e0d\u61c2\u5f97\u8fd9\u4e2a\u540d\u8bcd\uff0c\u4f46\u4ed6\u628a\u5510\u7075\u54c4\u5f97\u670d\u670d\u8d34\u8d34\uff0c\u6240\u7528\u7684\u624b\u6bb5\uff0c\u6b63\u662f\u4e0e\u8fd9\u79cd\u6559\u80b2\u65b9\u6cd5\u6697\u6697 \u7b26\u5408\u3002 \u3000\u3000\u5510\u7075\u53eb\u9053\uff1a\u201c\u6842\u53d4\u53d4\uff0c\u4f60\u771f\u662f\u4e00\u4e2a\u5927\u5927\u7684\u597d\u4eba\uff0c\u4f60\u4e0d\u518d\u62e6\u963b\u6211\u66ff\u7236\u4eb2\u62a5\u4ec7\u4e86\u5427\uff1f\u201d\u6842\u534e \u751f\u7709\u5934\u4e00\u76b1\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u662f\u975e\u5584\u6076\uff0c\u4e0d\u662f\u5f88\u7b80\u5355\u7684\u5c31\u80fd\u5206\u522b\u51fa\u6765\uff0c\u5bf9\u4f60\u597d\u7684\u4eba\u4e0d\u89c1\u5f97\u5c31\u662f \u5927\u5bb6\u516c\u8ba4\u7684\u597d\u4eba\u3002\u8981\u77e5\u9053\u81ea\u5df1\u60f3\u505a\u7684\u4e8b\u60c5\u5bf9\u662f\u4e0d\u5bf9\uff0c\u5e94\u8be5\u591a\u542c\u4e00\u4e9b\u4eba\u7684\u8bae\u8bba\u3002\u597d\u5427\uff0c\u73b0\u5728\u6211 \u6240\u8bf4\u7684\u8bdd\u4f60\u672a\u5fc5\u660e\u767d\uff0c\u4f60\u56de\u53bb\u95ee\u95ee\u4f60\u7684\u6bcd\u4eb2\u548c\u5510\u516c\u516c\uff0c\u518d\u8fc7\u51e0\u5e74\uff0c\u5f85\u4f60\u957f\u5927\u4e4b\u540e\uff0c\u4ee5\u4f60\u7684\u806a \u660e\uff0c\u5b9a\u7136\u80fd\u591f\u660e\u767d\u4e8b\u7406\u3002\u201d\u5510\u7075\u542c\u5f97\u751a\u4e3a\u6c14\u95f7\uff0c\u4f3c\u61c2\u975e\u61c2\uff0c\u542c\u8bf4\u4e4b\u540e\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u8bf4\u6765\u8bf4 \u53bb\uff0c\u4f60\u8fd8\u662f\u8981\u6211\u56de\u5510\u5bb6\u5417\uff1f\u201d\u845b\u817e\u9f99\u6697\u6697\u5411\u4ed6\u629b\u4e86\u4e00\u4e2a\u773c\u8272\uff0c\u9053\uff1a\u201c\u7075\u513f\uff0c\u4f60\u7684\u6842\u53d4\u53d4\u662f\u4e00 \u756a\u597d\u610f\uff0c\u54b1\u4eec\u73b0\u5728\u5c31\u8d70\u5427\uff0c\u4f60\u8ddf\u6211\u8d70\uff0c\u51c6\u4fdd\u6ca1\u6709\u9519\u513f\u3002\u201d \u3000\u3000\u6842\u534e\u751f\u76ee\u9001\u845b\u817e\u9f99\u6320\u4e86\u5510\u7075\u8d70\u8fc7\u5c71\u5761\uff0c\u80cc\u5f71\u5189\u518d\u800c\u6ca1\uff0c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u4f46\u89c9\u653e\u4efb\u5510\u7075\u8ddf\u4ed6\uff0c \u603b\u4f3c\u6709\u4e9b\u4e0d\u59a5\uff0c\u4f46\u81ea\u5df1\u6f2b\u6e38\u897f\u85cf\uff0c\u52bf\u53c8\u4e0d\u80fd\u5c06\u4ed6\u5e26\u5728\u8eab\u8fb9\uff0c\u845b\u817e\u9f99\u65e2\u7136\u5141\u8bfa\u5c06\u4ed6\u5e26\u56de\u5510\u5bb6\uff0c \u90a3\u4e5f\u53ea\u5f97\u4efb\u7531\u4ed6\u4eec\u8d70\u4e86\u3002 \u3000\u3000\u6842\u534e\u751f\u6b47\u4e86\u4e00\u4f1a\uff0c\u5403\u4e86\u4e00\u70b9\u5e72\u7cae\uff0c\u5f85\u5f97\u8349\u539f\u65e5\u843d\uff0c\u6708\u4eae\u4e1c\u5347\uff0c\u4fbf\u5411\u7740\u85cf\u6c11\u6240\u6307\u7684\u9b54\u9b3c\u57ce \u65b9\u5411\u800c\u53bb\u3002 \u3000\u3000\u8d70\u8fc7\u4e00\u7247\u8349\u539f\uff0c\u524d\u9762\u662f\u4e00\u7247\u6c99\u6f20\uff0c\u597d\u5728\u8fd9\u6c99\u6f20\u65b9\u5706\u4e0d\u8fc7\u5341\u6570\u91cc\uff0c\u8d70\u4e86\u4e2a\u628a\u65f6\u8fb0\uff0c\u4e5f\u7a7f\u8fc7 \u53bb\u4e86\uff0c\u524d\u9762\u53c8\u662f\u4e00\u7247\u8349\u539f\uff0c\u8d70\u5230\u5348\u591c\u65f6\u5206\uff0c\u5ff5\u9752\u5510\u53e4\u62c9\u5c71\u5df2\u5728\u76ee\u524d\u3002\u6842\u534e\u751f\u8d70\u8fdb\u90a3\u5587\u53ed\u5f62\u7684 \u8c37\u53e3\uff0c\u62ac\u5934\u4e00\u671b\uff0c\u4e14\u89c1\u5c71\u4e0a\u51b0\u5ddd\u4ea4\u9519\uff0c\u4fe8\u82e5\u94f6\u9f99\u4ea4\u821e\uff0c\u5728\u9ed1\u591c\u4e4b\u4e2d\u53d1\u51fa\u4e00\u79cd\u6de1\u6de1\u7684\u84dd\u5149\uff0c\u8393 \u7136\u95f4\u72c2\u98d9\u53c8\u8d77\uff0c\u98ce\u4e2d\u679c\u7136\u5e26\u6709\u4e00\u79cd\u5f02\u9999\uff0c\u4e2d\u4eba\u5982\u9152\uff0c\u6842\u534e\u751f\u60c5\u601d\u660f\u660f\uff0c\u67ab\u67ab\u6b32\u7761\uff0c\u6025\u5fd9\u5c06\u5929 \u5c71\u96ea\u83b2\u62ff\u51fa\uff0c\u653e\u5728\u9f3b\u89c2\u4e0b\u6df1\u6df1\u547c\u5438\uff0c\u4ecd\u5411\u524d\u884c\u3002\u98ce\u6108\u53f0\u6108\u5927\uff0c\u65e5\u95f4\u6240\u542c\u5230\u7684\u5404\u79cd\u5f02\u58f0\uff0c\u53c8\u968f \u7740\u72c2\u98ce\u5439\u6765\uff0c\u4fe8\u5982\u4e07\u9a6c\u5954\u817e\uff0c\u5343\u519b\u8d74\u654c\uff0c\u96c4\u58ee\u3001\u51c4\u67aa\u3001\u54c0\u53f7\u3001\u6e29\u5a49\uff0c\u5404\u79cd\u8c03\u5b50\u90fd\u6709\uff0c\u771f\u50cf\u6781 \u4e0d\u548c\u8c10\u7684\u5927\u5408\u594f\uff0c\u6bd4\u65e5\u95f4\u6240\u542c\uff0c\u66f4\u89c9\u60ca\u5fc3\u52a8\u9b44\u3002\u6842\u534e\u751f\u5835\u7740\u8033\u6735\uff0c\u8d34\u7740\u5c71\u811a\u7684\u5ced\u58c1\u524d\u884c\uff0c\u6708 \u5149\u4e4b\u4e0b\uff0c\u4f46\u89c1\u5c71\u58c1\u4e0a\u65e0\u6570\u5c0f\u5b54\uff0c\u5c31\u50cf\u8702\u5de2\u5bc6\u5e03\u4e00\u822c\uff0c\u5ffd\u7136\u95f4\u5c31\u5728\u81ea\u5df1\u811a\u8e0f\u7684\u5e95\u4e0b\uff0c\u4e5f\u542c\u5f97\u53ee \u53ee\u549a\u549a\u7684\u7c7b\u4f3c\u97f3\u4e50\u7684\u58f0\u97f3\uff01 \u3000\u3000\u6842\u534e\u751f\u604d\u7136\u5931\u7b11\uff0c\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u539f\u6765\u98ce\u4e2d\u7684\u602a\u58f0\uff0c\u5374\u662f\u8fd9\u4e2a\u6765\u7531\u3002\u201d\u4ed6\u5728\u5929\u5c71\u6f2b\u6e38\u4e4b \u65f6\uff0c\u4e5f\u66f9\u7ecf\u542c\u89c1\u8fc7\u8fd9\u79cd\u5730\u5e95\u4e0b\u7684\u594f\u4e50\u58f0\u97f3\uff0c\u521d\u65f6\u4e5f\u66f9\u7ed9\u8fd9\u79cd\u58f0\u97f3\u7591\u60d1\u8fc7\uff0c\u540e\u6765\u624d\u77e5\u9053\u5929\u5c71\u5c71 \u8109\u4e00\u5e26\uff0c\u6709\u8bb8\u591a\u5de8\u5927\u7684\u51b0\u5c71\uff0c\u7531\u4e8e\u5730\u9707\uff0c\u540e\u9762\u9ad8\u5c71\u7684\u5ca9\u77f3\u584c\u4e0b\u6765\uff0c\u628a\u51b0\u5c71\u538b\u5728\u4e0b\u9762\u3002\u51b0\u5c71\u4e00 \u5929\u5929\u7684\u878d\u5316\uff0c\u5ca9\u77f3\u5c31\u4e00\u5929\u5929\u7684\u67b6\u7a7a\u3002\u5ca9\u77f3\u4e2d\u7a7a\u4e4b\u5904\uff0c\u51b0\u6cb3\u6d41\u52a8\uff0c\u6709\u65f6\u4f3c\u4e50\u58f0\uff0c\u6709\u65f6\u4f3c\u811a\u6b65 \u58f0\uff0c\u4eca\u7b2c\u4e00\u6b21\u542c\u5230\u8fd9\u79cd\u58f0\u97f3\u7684\u4eba\u65e0\u4e0d\u5fc3\u60ca\u80c6\u6218\u3002 \u3000\u3000\u6842\u534e\u751f\u518d\u4ed4\u7ec6\u5ba1\u89c6\u5c71\u53f3\u4e0a\u90a3\u4e9b\u8702\u7a9d\u822c\u7684\u6d1e\u5b54\uff0c\u628a\u8033\u6735\u8d34\u4e0a\u53bb\u542c\uff0c\u7531\u4e8e\u6d1e\u5b54\u7684\u5927\u5c0f\u5f62\u72b6\u4e0d \u540c\uff0c\u98ce\u4ece\u6d1e\u5b54\u7a7f\u8fc7\uff0c\u6240\u53d1\u51fa\u7684\u58f0\u97f3\u4e5f\u5f02\uff0c\u8fd9\u4e9b\u8702\u7a9d\u822c\u7684\u6d1e\u5b54\uff0c\u81ea\u662f\u7531\u4e8e\u98ce\u7802\u4fb5\u8680\u800c\u6210\uff0c\u7531\u4e8e \u8fd9\u91cc\u7684\u8c37\u53e3\u72ed\u957f\uff0c\u98ce\u7802\u5439\u6765\uff0c\u53d7\u5230\u5c71\u5ca9\u5ced\u58c1\u7684\u963b\u6321\uff0c\u6240\u4ee5\u5265\u8680\u7684\u73b0\u8c61\u7279\u522b\u663e\u7740\u3002\u53e4\u4ee3\u6c99\u6f20\u4e0e \u8349\u539f\u4e0a\u7684\u5c45\u6c11\uff0c\u65e2\u6ca1\u6709\u8fd1\u4ee3\u5730\u8d28\u5b66\u7684\u5e38\u8bc6\uff0c\u66f4\u4e0d\u6562\u4eb2\u81ea\u53bb\u89c2\u5bdf\uff0c\u90a3\u5c31\u65e0\u602a\u4ed6\u4eec\u4ee5\u4e3a\u662f\u201c\u9b54\u9b3c \u7684\u58f0\u97f3\u201d\u4e86\u3002 \u3000\u3000\u4f46\u6842\u534e\u751f\u4ecd\u662f\u4e0d\u65e0\u7591\u60d1\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u5730\u4e0b\u51b0\u6cb3\u7684\u6d41\u52a8\u548c\u98ce\u4ece\u5ca9\u6d1e\u7a7f\u8fc7\u6784\u6210\u4e86\u8bf8\u79cd\u602a \u58f0\uff0c\u8fd9\u56fa\u7136\u4e0d\u8db3\u4e3a\u5f02\u3002\u4f46\u5728\u85cf\u4eba\u7684\u773c\u4e2d\uff0c\u603b\u662f\u4e00\u4e2a\u795e\u79d8\u7684\u5730\u65b9\uff0c\u66f4\u4f55\u51b5\u8fd9\u91cc\u53c8\u662f\u9669\u6076\u8352\u829c\u4e4b \u5730\uff0c\u4e3a\u4ec0\u4e48\u6709\u4eba\u5374\u504f\u504f\u5728\u8fd9\u79cd\u5730\u65b9\u5c45\u4f4f\uff1f\u662f\u4f55\u5c45\u5fc3\uff1f\u201d\u518d\u60f3\u8fd9\u201c\u9b54\u9b3c\u57ce\u201d\u7684\u4f20\u8bf4\u7531\u6765\u5df2\u4e45\uff0c \u5230\u5e95\u662f\u7531\u4e8e\u8bf8\u822c\u602a\u8c61\u9644\u4f1a\u800c\u6210\uff0c\u8fd8\u662f\u5c71\u4e2d\u786e\u6709\u57ce\u6c60\uff1f\u5bfb\u5e7d\u63a2\u79d8\u4e4b\u5fc3\uff0c\u66f4\u4e0d\u7981\u6cb9\u7136\u800c\u8d77\u3002 \u3000\u3000\u8fd9\u65f6\u72c2\u98d9\u5df1\u6b62\uff0c\u6842\u534e\u751f\u7ee7\u7eed\u767b\u5c71\uff0c\u6500\u8d8a\u8fc7\u51e0\u4e2a\u9669\u9661\u7684\u5c71\u5934\uff0c\u679c\u7136\u522b\u6709\u5929\u5730\uff0c\u4f46\u89c1\u5230\u5904\u662f \u65ad\u74e6\u6b8b\u57a3\uff0c\u8fd8\u6709\u5bfa\u9662\u7684\u5e9f\u589f\u548c\u9ad8\u8038\u7684\u58eb\u5854\u2026\u2026\u663e\u7136\u8fd9\u662f\u4e00\u5ea7\u53e4\u57ce\u7684\u9057\u5740\uff0c\u5947\u602a\u7684\u662f\uff0c\u90a3\u4e9b\u6b8b \u7816\u8d25\u74e6\uff0c\u5df2\u88ab\u626b\u5728\u4e00\u8d77\uff0c\u597d\u50cf\u5728\u4e0d\u4e45\u4e4b\u524d\uff0c\u624d\u521a\u521a\u7ecf\u8fc7\u4eba\u5de5\u7684\u6536\u62fe\u3002 \u3000\u3000\u7a7f\u8fc7\u8fd9\u5ea7\u53e4\u57ce\u7684\u9057\u5740\uff0c\u518d\u95ee\u5c71\u4e0a\u671b\u592b\uff0c\u90a3\u5c31\u66f4\u5947\u602a\u4e86\u3002\u5c71\u4e0a\u7adf\u7136\u6709\u4e00\u5ea7\u5b8c\u6574\u7684\u767d\u5854\uff0c\u7ea6 \u6709\u5341\u4e08\u6765\u9ad8\uff0c\u767d\u642d\u4e4b\u65c1\uff0c\u6709\u4e24\u6392\u623f\u5c4b\uff0c\u5706\u5f62\u7684\u5c4b\u9876\uff0c\u72b6\u5982\u8986\u83b2\uff0c\u8fd9\u79cd\u5f62\u5f0f\u7684\u623f\u5c4b\uff0c\u548c\u897f\u85cf\u4e00 \u822c\u6c11\u5c45\u5927\u5927\u4e0d\u540c\uff0c\u8fd8\u6709\u4e24\u5e62\u95ea\u95ea\u53d1\u5149\u7684\u5efa\uff0c\u66f4\u4e0d\u77e5\u662f\u7528\u4ec0\u4e48\u6750\u6599\u9020\u7684\u3002\u4f46\u4e00\u773c\u671b\u592b\uff0c\u4fbf\u53ef\u4ee5 \u65ad\u5b9a\u8fd9\u4e0d\u662f\u53e4\u4ee3\u9057\u7b1b\u7684\u5efa\uff0c\u800c\u662f\u65b0\u5efa\u4e0d\u4e45\u7684\u623f\u5c4b\u3002\u6842\u534e\u751f\u81ea\u662f\u4e0d\u76f8\u500d\u6709\u751a\u201c\u9b54\u9b3c\u57ce\u201d\uff0c\u4f46\u8fd9 \u6c14\u6c1b\u5374\u771f\u7684\u662f\u8d8a\u6765\u8d8a\u795e\u79d8\u4e86\u3002 \u3000\u3000\u6842\u534e\u751f\u57ab\u9ad8\u80c6\u5927\uff0c\u518d\u5411\u524d\u884c\uff0c\u5ffd\u89c9\u591c\u98ce\u4e2d\u9999\u6c14\u6781\u6d53\uff0c\u867d\u6709\u5929\u5c71\u5bab\u83b2\uff0c\u4ecd\u7136\u6709\u70b9\u76ee\u7729\u5fc3 \u8df3\uff0c\u671b\u8fc7\u53bb\u4f46\u89c1\u5c71\u5761\u4e0a\u5f00\u6709\u65e0\u6570\u5947\u82b1\uff0c\u7ea2\u767d\u84dd\u4e09\u8272\u76f8\u95f4\uff0c\u5728\u8499\u62e2\u7684\u6708\u8272\u4e0b\u66f4\u663e\u5f97\u5a07\u65e0\u7574\uff0c\u6842 \u534e\u751f\u542b\u4e86\u4e24\u74e3\u96ea\u83b2\u82b1\u74e3\uff0c\u8d70\u5165\u82b1\u4e1b\u4e4b\u4e2d\uff0c\u539f\u6765\u968f\u98ce\u6240\u8fed\u7684\u5f02\u9999\uff0c\u4fbf\u662f\u8fd9\u79cd\u5947\u82b1\u7814\u53d1\u3002\u6842\u534e\u751f \u6b63\u5728\u6d41\u8fde\u89c2\u8d4f\uff0c\u5ffd\u542c\u5f97\u4f20\u6765\u4e86\u811a\u6b65\u7684\u58f0\u97f3\u3002 \u3000\u3000\u6842\u534e\u751f\u4ece\u82b1\u4e1b\u91cc\u5411\u5916\u5077\u7aa5\uff0c\u53ea\u89c1\u4e00\u4e2a\u957f\u7740\u4e00\u628a\u5c71\u7f8a\u80e1\u5b50\u7684\u9ed1\u8863\u6b66\u58eb\u5e26\u5f15\u7740\u4e24\u4e2a\u767d\u8863\u5587 \u561b\uff0c\u6b63\u597d\u671d\u7740\u8fd9\u4e2a\u65b9\u5411\u884c\u6765\uff0c\u8fd9\u9ed1\u591c\u6b66\u58eb\u8eab\u6750\u9b41\u4f1f\uff0c\u76f8\u8c8c\u5947\u7279\uff0c\u770b\u6765\u4e0d\u50cf\u662f\u897f\u85cf\u4eba\u3002 \u3000\u3000\u4f46\u90a3\u4e24\u4e2a\u767d\u8863\u5587\u561b\u5374\u66f4\u5f15\u8d77\u6842\u534e\u751f\u7684\u60ca\u8be7\uff0c\u539f\u6765\u897f\u85cf\u7684\u5587\u561b\u5206\u4e3a\u4e09\u6d3e\uff0c\u6e05\u671d\u4ee5\u524d\u638c\u6743\u7684 \u662f\u7ea2\u6559\u5587\u561b\uff0c\u6e05\u4ee3\u5949\u9ec4\u6559\u5587\u561b\u4e3a\u56fd\u6559\uff0c\u7ea2\u6559\u4e00\u8e76\u4e0d\u632f\uff0c\u4f46\u8fd8\u53ef\u4ee5\u7559\u5728\u897f\u85cf\u3002\u53e6\u5916\u6709\u4e00\u6d3e\u767d\u6559 \u5587\u561b\uff0c\u6700\u9ad8\u7684\u79f0\u4e3a\u201c\u6cd5\u738b\u201d\uff0c\u5728\u660e\u671d\u65f6\u5019\uff0c\u4e0e\u7ea2\u6559\u5206\u5ead\u6297\u793c\uff0c\u66fe\u5f97\u660e\u592a\u79df\u5c01\u4e3a\u201c\u704c\u9876\u56fd \u5e08\u201d\uff0c\u5e76\u201c\u8d50\u7edf\u5fa1\u897f\u85cf\u4e09\u90e8\u4e4b\u6559\u8bcf\u201d\uff0c\u767e\u5217\u660e\u672b\u5d07\u796f\u7687\u5e1d\u4e4b\u65f6\u3002\u9ec4\u6559\u9886\u8896\u8fbe\u8d56\u4e94\u4e16\u548c\u73ed\u7985\u56db \u4e16\u85c9\u9752\u6d77\u8499\u53e4\u65cf\u914b\u957f\u56fa\u59cb\u6c57\u7684\u5175\u529b\uff0c\u624d\u63a8\u7ffb\u4e86\u767d\u6559\u6cd5\u738b\u5728\u897f\u85cf\u7684\u7edf\u6cbb\u5730\u4f4d\uff0c\u767d\u6559\u88ab\u9010\u51fa\u897f \u85cf\uff0c\u9003\u5954\u9752\u6d77\uff0c\u4f9d\u9644\u53e6\u4e00\u4f4d\u914b\u957f\u548c\u817e\u6c57\uff0c\u81f3\u4eca\u4e00\u767e\u4f59\u5e74\uff0c\u767d\u6559\u5587\u561b\uff0c\u4ece\u6765\u4e0d\u6562\u8e0f\u5165\u897f\u85cf\u3002 \u3000\u3000\u897f\u85cf\u5587\u561b\u4ee5\u670d\u9970\u989c\u8272\u5206\u522b\uff0c\u8fd9\u4e24\u4e2a\u5587\u561b\u81ea\u662f\u767d\u6559\u5587\u561b\u65e0\u7591\u3002\u6842\u534e\u751f\u5927\u4e3a\u7591\u60d1\uff0c\u5fc3\u4e2d\u60f3 \u9053\uff1a\u201c\u767d\u6559\u9ec4\u6559\u5982\u540c\u6c34\u706b\uff0c\u600e\u5730\u8fd9\u4e24\u4e2a\u767d\u6559\u5587\u561b\u5374\u6562\u5077\u5077\u8fdb\u6765\u4e86\uff1f\u201d \u3000\u3000\u53ea\u542c\u5f97\u90a3\u9ed1\u8863\u6b66\u58eb\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u7684\u738b\u5b50\u542c\u8bf4\u6cd5\u738b\u4f7f\u8005\u524d\u6765\uff0c\u7279\u5730\u8d76\u5230\u6b64\u95f4\uff0c\u606d\u8fce\u5927\u9a7e\u3002 \u3000\u3000\u8fd8\u6709\u51e0\u4f4d\u571f\u53f8\uff0c\u4e5f\u5c06\u8981\u5230\u6765\uff0c\u54c8\u54c8\uff0c\u8fd9\u771f\u662f\u7f55\u9047\u7684\u673a\u7f18\uff0c\u96be\u9022\u7684\u673a\u4f1a\uff01\u201d \u3000\u3000\u6842\u534e\u751f\u542c\u5f97\u83ab\u660e\u5176\u5999\uff0c\u60f3\u9053\uff1a\u201c\u90a3\u91cc\u6765\u7684\u738b\u5b50\uff1f\u82e5\u662f\u85cf\u738b\u4e4b\u5b50\uff0c\u4ed6\u4e3a\u4f55\u4e0d\u7ea6\u5728\u62c9\u8428\uff0c\u5374 \u5728\u8fd9\u4e2a\u53e4\u602a\u7684\u5730\u65b9\u805a\u4f1a\uff1f\u201d\u90a3\u9ed1\u8863\u6b66\u58eb\u7684\u85cf\u8bed\u8bf4\u5f97\u751a\u4e3a\u751f\u786c\uff0c\u66f4\u662f\u5f15\u8d77\u5b83\u7684\u7591\u5fc3\u3002 \u3000\u3000\u90a3\u4e24\u4e2a\u767d\u8863\u5587\u561b\u5495\u5495\u565c\u565c\u7684\u8bf4\u4e86\u4e00\u4e9b\u8bdd\uff0c\u4f46\u8fd9\u65f6\u4ed6\u4eec\u5df2\u8d70\u4e0a\u5c71\u5761\uff0c\u8bf4\u8bdd\u58f0\u97f3\u542c\u4e0d\u6e05\u695a \u4e86\u3002\u8fc7\u4e86\u4e00\u9635\uff0c\u6842\u534e\u751f\u63a2\u51fa\u5934\u6765\uff0c\u5df2\u770b\u4e0d\u89c1\u4ed6\u4eec\u7684\u80cc\u5f71\uff0c\u6b63\u5f85\u51fa\u6765\uff0c\u5ffd\u89c1\u53c8\u662f\u4e00\u4e2a\u767d\u8863\u5587 \u561b\uff0c\u5982\u98de\u5954\u81f3\uff0c\u5c06\u8fd1\u82b1\u4e1b\uff0c\u5ffd\u7136\u4e00\u8de4\u6454\u5012\uff0c\u8bb8\u4e45\u8bb8\u4e45\uff0c\u722c\u4e0d\u8d77\u6765\uff0c\u7adf\u4f3c\u662f\u6655\u5012\u4e86\u3002 \u3000\u3000\u6842\u534e\u751f\u59cb\u800c\u53d1\u695e\uff0c\u7ee7\u800c\u604d\u7136\u3002\u5fc3\u9053\uff1a\u201c\u662f\u4e86\uff0c\u5148\u524d\u90a3\u4e24\u4e2a\u767d\u8863\u5587\u561b\u5e26\u6709\u89e3\u836f\uff0c\u8fd9\u4e2a\u6ca1 \u6709\uff0c\u6240\u4ee5\u88ab\u82b1\u9999\u8ff7\u5012\u4e86\u3002\u201d\u4f46\u4ed6\u4e3a\u4ec0\u4e48\u6ca1\u6709\u89e3\u836f\uff0c\u53c8\u6ca1\u4eba\u5e26\u9886\u5462\uff1f\u8fd9\u4e2a\uff0c\u6842\u534e\u751f\u5374\u731c\u60f3\u4e0d\u900f \u4e86\u3002 \u3000\u3000\u6842\u534e\u751f\u4ece\u82b1\u4e1b\u4e2d\u4e00\u8dc3\u800c\u51fa\uff0c\u4f46\u89c1\u8fd9\u767d\u8863\u5587\u561b\u914d\u989c\u5982\u9189\uff0c\u4e2d\u6bd2\u7684\u60c5\u72b6\u4e0e\u845b\u817e\u9f99\u3001\u5510\u7075\u4e00\u6a21 \u4e00\u6837\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u845b\u817e\u9f99\u65b9\u5230\u5c71\u8c37\u4fbf\u5df2\u4e2d\u6bd2\uff0c\u6b64\u4eba\u5374\u4e00\u76f4\u6765\u5230\u82b1\u524d\uff0c\u5185\u529f\u7684\u6df1\u539a\uff0c\u4e5f\u7b97\u96be \u5f97\u7684\u4e86\uff01\u201d\u5206\u4e86\u4e24\u74e3\u5929\u5c71\u96ea\u83b2\uff0c\u585e\u5165\u4ed6\u7684\u53e3\u4e2d\uff0c\u8fc7\u4e86\u7247\u523b\uff0c\u8fd9\u767d\u8863\u5587\u561b\u6761\u7136\u9192\u8f6c\uff0c\u8df3\u8d77\u8eab \u6765\uff0c\u7528\u85cf\u8bdd\u5927\u58f0\u9a82\u9053\uff1a\u201c\u54fc\uff0c\u4f60\u7528\u7684\u662f\u4ec0\u4e48\u5996\u6cd5\uff1f\u201d\u5288\u9762\u5c31\u662f\u4e00\u62f3\uff0c\u6842\u534e\u751f\u4e00\u638c\u62e8\u5f00\u4e86\uff0c\u53ea \u89c9\u5bf9\u65b9\u7684\u62f3\u529b\u6c89\u91cd\u5f02\u5e38\uff0c\u6b63\u5f85\u8bf4\u8bdd\uff0c\u90a3\u767d\u8863\u5587\u561b\uff0c\u8fd9\u65f6\u5df2\u770b\u6e05\u695a\u4e86\u6842\u534e\u751f\u4e43\u662f\u4e2a\u6c49\u4eba\uff0c\u5927\u4e3a \u8be7\u5f02\uff0c\u7b2c\u4e8c\u62f3\u6253\u5230\u4e2d\u9014\uff0c\u5019\u7684\u6536\u56de\uff0c\u95ee\u9053\uff1a\u201c\u54a6\uff0c\u4f60\u662f\u8c01\uff1f\u201d  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u9b44\u5bd2\u5149\u5251\u300b\u2014\u2014\u7b2c\u4e8c\u7ae0\u3000\u85cf\u8eab\u51b0\u8c37\u9047\u5947\u4eba \u6881\u7fbd\u751f\u300a\u51b0\u9b44\u5bd2\u5149\u5251\u300b \u7b2c\u4e8c\u7ae0\u3000\u85cf\u8eab\u51b0\u8c37\u9047\u5947\u4eba \u3000\u3000\u6842\u534e\u751f\u4e00\u7b11\u8bf4\u9053\uff1a\u201c\u8981\u4e0d\u662f\u6211\uff0c\u4f60\u73b0\u5728\u8fd8\u672a\u9192\u6765\u5462\u3002\u4f60\u662f\u8c01\uff1f\u201d\u90a3\u767d\u8863\u5587\u561b\u53d1\u89c9\u4e86\u53e3\u4e2d \u7684\u4e24\u74e3\u83b2\u74e3\uff0c\u518d\u770b\u4e00\u770b\u8fd9\u84dd\u3001\u767d\u3001\u7ea2\u4e09\u8272\u76f8\u95f4\u7684\u5947\u82b1\uff0c\u5931\u58f0\u8bf4\u9053\uff1a\u201c\u54a6\uff0c\u539f\u6765\u8fd9\u662f\u963f\u4fee\u7f57 \u82b1\uff0c\u6211\u4e00\u5411\u53ea\u4ece\u4f5b\u5178\u4e4b\u4e2d\u77e5\u6709\u6b64\u82b1\uff0c\u4e0d\u60f3\u5728\u8fd9\u513f\u89c1\u5230\u3002\u4f60\u662f\u8c01\uff0c\u600e\u7684\u6709\u8fd9\u6837\u795e\u901a\uff1f\u5c45\u7136\u80fd\u628a \u6211\u6551\u9192\uff1f\u201d\u6842\u534e\u751f\u9053\uff1a\u201c\u6211\u4e0d\u8fc7\u662f\u4e2a\u666e\u901a\u7684\u6c49\u4eba\uff0c\u78b0\u5de7\u5e26\u6709\u80fd\u89e3\u767e\u6bd2\u7684\u5929\u5c71\u96ea\u83b2\uff0c\u78b0\u5de7\u5c06\u4f60 \u6551\u9192\uff0c\u7b97\u4e0d\u4e86\u4ec0\u4e48\uff01\u8fd9\u4e2a\u4ec0\u4e48\u963f\u4fee\u7f57\u82b1\u4e3a\u4ec0\u4e48\u5982\u6b64\u4eca\u4f60\u60ca\u5947\uff1f\u201d\u90a3\u767d\u8863\u5587\u561b\u9053\uff1a\u201c\u963f\u4fee\u7f57\u662f \u711a\u8bed\u4e2d\u9b54\u9b3c\u7684\u610f\u601d\uff0c\u6240\u4ee5\u53c8\u540d\u9b54\u9b3c\u82b1\u3002\u300a\u4f5b\u56fd\u8bb0\u300b\u4e2d\u6240\u8f7d\uff0c\u8bf4\u963f\u4fee\u7f57\u82b1\u5f00\u4e4b\u65f6\uff0c\u4eba\u4e00\u55c5\u5230\u8fd9 \u79cd\u82b1\u9999\uff0c\u5c31\u50cf\u78b0\u5230\u4e86\u9b54\u9b3c\u4e00\u822c\uff0c\u7acb\u523b\u7ed9\u5b83\u8ff7\u9189\u4e86\uff0c\u679c\u7136\u4e0d\u5dee\u3002\u8fd9\u82b1\u53ea\u6709\u5728\u6781\u9ad8\u7684\u51b0\u5cf0\u4e4b\u4e0a\u624d \u6709\uff0c\u5982\u4eca\u4ed6\u4eec\u79fb\u690d\u6b64\u95f4\uff0c\u80fd\u4e3a\u4e5f\u771f\u4e0d\u5c0f\u5462\u3002\u54a6\uff0c\u4f60\u7aef\u7684\u662f\u8c01\uff1f\u4f60\u662f\u4e0d\u662f\u4ed6\u4eec\u7684\u4eba\uff1f\u201d \u3000\u3000\u6842\u534e\u751f\u9053\uff1a\u201c\u4ed6\u4eec\u662f\u8c01\uff1f\u4f60\u53c8\u662f\u8c01\uff1f\u201d\u90a3\u767d\u8863\u5587\u561b\u8be7\u9053\uff1a\u201c\u4f60\u4e0d\u77e5\u9053\u4ed6\u4eec\u662f\u8c01\uff1f\u90a3\u4f60\u6765 \u8fd9\u91cc\u505a\u4ec0\u4e48\uff1f\u201d\u6842\u534e\u751f\u9053\uff1a\u201c\u6211\u5c31\u662f\u60f3\u6765\u63a2\u8fd9\u9b54\u9b3c\u57ce\u4e2d\u7684\u79d8\u5bc6\u3002\u201d\u90a3\u767d\u8863\u5587\u561b\u5583\u5583\u8bf4\u9053\uff1a \u201c\u9b54\u9b3c\u57ce\uff1f\u9b54\u9b3c\u57ce\uff1f\u201d\u6842\u534e\u751f\u9053\uff1a\u201c\u4e0d\u9519\u3002\u85cf\u4eba\u4eec\u662f\u53eb\u8fd9\u91cc\u505a\u9b54\u9b3c\u57ce\u3002\u201d\u90a3\u767d\u8863\u5587\u561b\u7b11\u9053\uff1a \u201c\u9b54\u9b3c\u57ce\u4e2d\u9b54\u9b3c\u82b1\uff0c\u602a\u4e0d\u5f97\u6b64\u4e2d\u6709\u7fa4\u9b54\u4e71\u821e\u4e86\u3002\u5982\u6b64\u8bf4\u6765\uff0c\u4f60\u5f53\u771f\u4e0d\u662f\u4ed6\u4eec\u7684\u4eba\u4e86\uff1f\u65e2\u7136\u4e0d \u662f\uff0c\u4f60\u5feb\u5feb\u4e0b\u5c71\u53bb\u5427\uff01\u201d\u6842\u534e\u751f\u6447\u4e86\u6447\u5934\uff0c\u90a3\u767d\u8863\u5587\u561b\u5e84\u5bb9\u8bf4\u9053\uff1a\u201c\u4f60\u82e5\u4e0d\u8d70\uff0c\u53ea\u6015\u4f60\u6551\u5f97 \u4e86\u6211\uff0c\u6211\u5374\u6551\u4e0d\u4e86\u4f60\u5462\u3002\u5feb\u8d70\uff01\u201d \u3000\u3000\u4f73\u534e\u751f\u89c1\u4ed6\u90d1\u91cd\u76f8\u529d\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u7b49\u4e00\u4e0b\u6211\u5c31\u6084\u6084\u6e9c\u8d70\u3002\u201d\u8fd9\u65f6\u5929\u8fb9\u98de\u6765\u4e86\u4e00\u7247 \u9ed1\u4e91\uff0c\u63a9\u76d6\u4e86\u4e00\u9493\u7709\u6708\u3002\u5c71\u98ce\u53c8\u547c\u547c\u7684\u53f0\u8d77\u6765\uff0c\u90a3\u767d\u8863\u5587\u561b\u75be\u5954\u4e0a\u5c71\uff0c\u6842\u534e\u751f\u4e5f\u8d81\u7740\u591c\u8272\u5982 \u58a8\uff0c\u5077\u5077\u7684\u8ddf\u5728\u5b83\u7684\u540e\u9762\u3002 \u3000\u3000\u5f85\u5f97\u4e4c\u4e91\u6563\u5f00\uff0c\u90a3\u767d\u8863\u5587\u561b\u5df2\u4e0d\u89c1\u4e86\u3002\u6842\u534e\u751f\u501f\u77f3\u969c\u5f62\uff0c\u5077\u5077\u4e00\u770b\uff0c\u539f\u6765\u5df2\u5230\u4e86\u767d\u5854\u524d \u9762\u3002\u8fd9\u767d\u5854\u5f62\u5f0f\u5947\u7279\uff0c\u4e0b\u9762\u662f\u5ea7\u65b9\u5f62\u7684\u5e99\u5b87\uff0c\u5e99\u5b87\u4e2d\u6709\u4e00\u5ea7\u9876\u4e0a\u9020\u4e86\u4e00\u4e2a\u5706\u4ead\u7684\u9ad8\u5854\uff0c\u5854\u7684 \u4e0b\u5c42\uff0c\u56db\u8fb9\u5916\u58c1\u4e0a\u5851\u6709\u4e24\u53ea\u773c\u775b\uff0c\u773c\u775b\u4e0a\u8fd8\u6709\u4e24\u9053\u5f2f\u5f2f\u7684\u7709\u6bdb\uff0c\u773c\u4e0b\u9762\u6709\u4e00\u79cd\u4f3c\u4e4e\u7528\u6765\u8c61\u5fb5 \u9f3b\u5b50\u7684\u4e1c\u897f\uff0c\u5f62\u5982\u201c\uff1f\u201d\uff0c\u8fd9\u79cd\u5947\u5f02\u7684\u5efa\u5f62\u5f0f\uff0c\u4e0d\u4f46\u6842\u534e\u751f\u8d70\u5357\u95ef\u5317\uff0c\u4ece\u6240\u672a\u7779\uff0c\u5373\u5728\u4e66\u672c \u4e0a\u4e5f\u672a\u66fe\u89c1\u8fc7\u3002 \u3000\u3000\u90a3\u4e24\u5e62\u95ea\u95ea\u53d1\u5149\u7684\u5efa\u5c31\u5728\u767d\u5854\u4e4b\u65c1\uff0c\u767d\u5854\u524d\u6709\u4e24\u4e2a\u9ed1\u8863\u6b66\u58eb\u9762\u5bf9\u7740\u9762\uff0c\u5404\u81ea\u5f00\u6b65\u5411\u5bf9\u65b9 \u8d70\u53bb\uff0c\u78b0\u5934\u4e4b\u65f6\uff0c\u4e00\u4e2a\u7acb\u6b63\uff0c\u5411\u540e\u8f6c\u8eab\uff0c\u80cc\u5411\u800c\u8d70\uff0c\u5404\u81ea\u8d70\u5230\u5e99\u7684\u4e00\u7aef\uff0c\u53c8\u518d\u8f6c\u8eab\u76f8\u5411\u800c \u8d70\uff0c\u5468\u800c\u590d\u59cb\uff0c\u4e3a\u72b6\u751a\u662f\u6ed1\u7a3d\u3002 \u3000\u3000\u6842\u534e\u751f\u8f7b\u529f\u8d85\u5353\uff0c\u8d81\u7740\u4ed6\u4eec\u8f6c\u8eab\u7684\u65f6\uff0c\u4e00\u4e2a\u201c\u767d\u96c1\u51b2\u5929\u201d\uff0c\u5df2\u4ece\u4ed6\u4eec\u7684\u5934\u9876\u98de\u8fc7\uff0c\u843d\u5728 \u90a3\u5ea7\u7741\u660e\u69b4\u4eae\u7684\u5efa\u7b51\u4fa7\u9762\uff0c\u4f38\u624b\u4e00\u6478\uff0c\u4f46\u89c9\u89e6\u624b\u51b0\u51b7\uff0c\u539f\u6765\u7684\u8fd9\u5ea7\u623f\u5c4b\uff0c\u7adf\u662f\u575a\u51b0\u6240\u9020\u3002 \u3000\u3000\u53e6\u4e00\u5ea7\u623f\u5c4b\u6ca1\u6709\u8fd9\u6837\u51b0\u51b7\uff0c\u8210\u8210\u6307\u5934\uff0c\u5374\u6709\u54b8\u5473\uff0c\u539f\u6765\u8fd9\u4e00\u5ea7\u662f\u6676\u76d1\u9020\u7684\u3002\u6842\u534e\u751f\u6697\u81ea \u7b11\u9053\uff1a\u201c\u9b54\u9b3c\u57ce\u679c\u7136\u540d\u4e0b\u65e0\u865a\uff0c\u5730\u65b9\u3001\u623f\u5c4b\u3001\u4eba\u7269\uff0c\u6837\u6837\u90fd\u662f\u53e4\u602a\u900f\u9876\u3002\u201d \u3000\u3000\u63a2\u5934\u4e00\u671b\uff0c\u90a3\u4e24\u4e2a\u5b88\u536b\u5e99\u95e8\u7684\u6b66\u58eb\u6b63\u5404\u81ea\u8d70\u5230\u4e00\u7aef\uff0c\u672a\u66fe\u8f6c\u8eab\uff0c\u6842\u534e\u751f\u98de\u8eab\u4e00\u63a0\uff0c\u7aef\u5982 \u4e00\u53f6\u98de\u5760\uff0c\u843d\u5904\u65e0\u58f0\u3002\u5f85\u5f97\u90a3\u4e24\u4e2a\u6b66\u58eb\u8f6c\u8fc7\u8eab\u6765\uff0c\u4ed6\u5df2\u98de\u4e0a\u4e86\u767d\u5854\u7684\u7b2c\u4e00\u5c42\uff0c\u9690\u8eab\u5728\u6bbf\u89d2\u98de \u6a90\u4e4b\u5185\uff0c\u5077\u5077\u7684\u4ece\u5e99\u9876\u4e00\u7247\u900f\u5149\u7684\u7409\u7483\u74e6\u5411\u4e0b\u7aa5\u63a2\u3002 \u3000\u3000\u4f46\u89c1\u5e99\u91cc\u4f9b\u7740\u4e00\u5c0a\u6570\u4e08\u9ad8\u7684\u5de8\u5927\u4f5b\u50cf\uff0c\u4e00\u4e2a\u9e70\u9f3b\u6df1\u76ee\u3001\u957f\u53d1\u62ab\u80a9\uff0c\u7a7f\u4e00\u4ef6\u7f00\u4ee5\u660e\u73e0\u7684\u5927 \u7ea2\u888d\u5b50\uff0c\u76f8\u8c8c\u751a\u662f\u5a01\u4e25\u7684\u4e2d\u5e74\u6c49\u5b50\uff0c\u7ad9\u5728\u4f5b\u50cf\u7684\u4e2d\u95f4\uff0c\u4e24\u65c1\u6709\u4e00\u5217\u50e7\u4fa3\u4e00\u5217\u6b66\u58eb\uff0c\u8fd9\u65f6\u6b63\u6709 \u4e09\u4e2a\u5b98\u540f\u88c5\u675f\u7684\u85cf\u4eba\u5411\u4ed6\u884c\u793c\u3002 \u3000\u3000\u53ea\u542c\u5f97\u4e00\u4e2a\u9ed1\u888d\u50e7\u4fa3\u5531\u540d\u8bf4\u9053\uff1a\u201c\u8428\u8fe6\u85e9\u738b\u4f7f\u8005\uff0c\u5f04\u8d5e\u85e9\u738b\u4f7f\u8005\uff0c\u4e9a\u4e1c\u85e9\u738b\u4f7f\u8005\u8c12\u89c1\u738b \u5b50\u3002\u201d\u6842\u534e\u751f\u5fc3\u4e2d\u4e00\u52a8\uff0c\u60f3\u9053\uff1a\u201c\u539f\u6765\u8fd9\u4eba\u5c31\u662f\u738b\u5b50\uff0c\u770b\u4ed6\u76f8\u8c8c\u88c5\u675f\uff0c\u5206\u660e\u4e0d\u662f\u85cf\u4eba\uff0c\u8fd9\u662f \u90a3\u91cc\u6765\u7684\u738b\u5b50\uff1f\u201d \u3000\u3000\u8fd9\u50e7\u4fa3\u548c\u738b\u5b50\u7684\u897f\u85cf\u8bdd\u90fd\u8bf4\u5f97\u76f8\u5f53\u6d41\u5229\uff0c\u4f46\u542c\u5f97\u90a3\u738b\u5b50\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u671b\u916c\u62a5\uff0c\u4e00\u5fc3\u6276\u52a9 \u4f60\u4eec\u4e09\u5bb6\u85e9\u738b\u505a\u897f\u85cf\u9f0e\u8db3\u800c\u7acb\u7684\u9738\u4e3b\uff0c\u53ea\u8981\u4f60\u4eec\u597d\u597d\u5f85\u6211\u6d3e\u6765\u7684\u4eba\uff0c\u6211\u5c06\u6765\u8fd8\u8981\u6d3e\u5175\u6765\u52a9\u4f60 \u4eec\uff0c\u4f60\u4eec\u7684\u85e9\u738b\u90fd\u660e\u767d\u4e86\u5417\uff1f\u201d\u90a3\u4e09\u4e2a\u85cf\u5b98\u4f9d\u6b21\u8bf4\u9053\uff1a\u201c\u660e\u767d\u5566\uff0c\u6211\u4eec\u7279\u6765\u4e0e\u738b\u5b50\u5b9a\u76df\u3002\u201d \u3000\u3000\u90a3\u738b\u5b50\u54c8\u54c8\u5927\u7b11\uff0c\u9996\u5ea7\u50e7\u4fa3\u8d70\u4e86\u51fa\u6765\uff0c\u6367\u7740\u4e00\u4e2a\u76db\u6ee1\u8840\u9152\u7684\u9abc\u4f53\u5934\uff0c\u606d\u606d\u656c\u656c\u7684\u9012\u7ed9\u738b \u5b50\u3002\u738b\u5b50\u5c06\u50a8\u4f53\u5934\u9ad8\u4e3e\uff0c\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u57fa\u8363\u57fa\u91cc\u8fbe\u5e03\u5609\u65f6\uff1f\u201d\u8fd9\u53e5\u85cf\u8bdd\u7684\u610f\u601d\u662f\uff1a\u201c\u6cbe\u996e\u9601 \u4e0b\u5269\u9152\uff0c\u5f15\u4ee5\u4e3a\u8363\uff0c\u9601\u4e0b\u4fef\u5141\u5426\uff1f\u201d\u6842\u534e\u751f\u6014\u4e86\u4e00\u6014\uff0c\u8fd9\u85cf\u8bdd\u4ed6\u542c\u5f97\u660e\u767d\uff0c\u5374\u4e0d\u77e5\u9053\u5176\u4e2d\u542b \u4e49\u3002\u8428\u8fe6\u85e9\u738b\u7684\u4f7f\u8005\u9996\u5148\u559d\u4e86\u4e00\u53e3\uff0c\u90a3\u738b\u5b50\u63a5\u56de\u9abc\u4f53\u5934\u9152\u5668\uff0c\u4e5f\u559d\u4e86\u4e00\u53e3\uff0c\u4f9d\u6b21\u9012\u7ed9\u5f04\u8d5e\u548c \u4e9a\u4e1c\u7684\u85e9\u738b\u4f7f\u8005\uff0c\u90fd\u662f\u8fd9\u6837\u3002\u4eea\u5f0f\u5b8c\u6210\u4e4b\u540e\uff0c\u90a3\u738b\u5b50\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4ece\u4eca\u4e4b\u540e\uff0c\u54b1\u4eec\u90fd\u662f\u4e00 \u5bb6\u3002\u6211\u5148\u6d3e\u4eba\u7ed9\u4f60\u4eec\u8bad\u7ec3\u519b\u961f\u3002\u201d\u6842\u534e\u751f\u604d\u7136\u5927\u609f\uff0c\u539f\u6765\u8fd9\u4eea\u5f0f\u6b63\u7b49\u5982\u4e2d\u56fd\u7684\u6b4c\u8840\u5b9a\u76df\u3002\u738b \u5b50\u7684\u90a3\u53e5\u8bdd\u662f\u5ba2\u5148\u8ba9\u4e3b\u7684\u793c\u8282\u3002 \u3000\u3000\u90a3\u4e09\u4e2a\u85e9\u738b\u4f7f\u8005\u559d\u4e86\u8840\u9152\uff0c\u9c7c\u8d2f\u9000\u4e0b\u3002\u5f85\u4e86\u4e00\u4f1a\uff0c\u4e24\u65c1\u7684\u50e7\u4fa3\u9ad8\u58f0\u5531\u9053\uff1a\u201c\u6cd5\u738b\u4f7f\u8005 \u5230\uff01\u201d\u738b\u5b50\u6ee1\u9762\u7b11\u5bb9\uff0c\u4eb2\u81ea\u8d70\u4e0a\u53bb\u8fce\u63a5\uff0c\u6765\u7684\u6b63\u662f\u90a3\u9ed1\u8863\u6b66\u58eb\u5e26\u5f15\u6765\u7684\u90a3\u4e24\u4e2a\u767d\u8863\u5587\u561b\u3002\u770b \u6765\u738b\u5b50\u5bf9\u8fd9\u4e24\u4eba\u7684\u91cd\u89c6\u8fdc\u5728\u90a3\u4e09\u4e2a\u85e9\u738b\u4f7f\u8005\u4e4b\u4e0a\u3002 \u3000\u3000\u738b\u5b50\u5f2f\u4e86\u5f2f\u8170\uff0c\u5411\u4ed6\u4eec\u8fd8\u4e86\u4e00\u793c\uff0c\u8bf4\u9053\uff1a\u201c\u6cd5\u738b\u6cd5\u4f53\u5b89\u5eb7\uff1f\u201d\u90a3\u4e24\u4e2a\u767d\u8863\u5587\u561b\u606d\u8eab\u8bf4 \u9053\uff1a\u201c\u6211\u4f5b\u4fdd\u4f51\uff0c\u6258\u5e87\u5e73\u5b89\u3002\u201d\u738b\u5b50\u9053\uff1a\u201c\u8fd9\u4e9b\u5e74\u6765\uff0c\u6cd5\u738b\u8fdc\u79bb\u5723\u5730\uff0c\u6211\u5fc3\u751a\u662f\u4e0d\u5b89\uff0c\u662f\u4ee5 \u613f\u5145\u62a4\u6cd5\uff0c\u8fce\u63a5\u6cd5\u738b\u56de\u85cf\uff0c\u533a\u533a\u4e4b\u610f\uff0c\u4e0d\u77e5\u6cd5\u738b\u660e\u767d\u4e86\u4e48\uff1f\u201d \u3000\u3000\u4e3a\u9996\u7684\u90a3\u4e2a\u767d\u8863\u5587\u561b\u7b54\u9053\uff1a\u201c\u4ef0\u4ed7\u738b\u5b50\u5927\u529b\uff0c\u62a4\u6301\u5723\u6cd5\uff0c\u6cd5\u738b\u6b23\u60a6\u4f55\u4f3c\uff0c\u7279\u9063\u5f1f\u5b50\u752b\u6765 \u5411\u738b\u5b50\u656c\u81f4\u8c22\u610f\uff0c\u5e76\u5373\u5b9a\u76df\u3002\u201d\u6842\u534e\u751f\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u767e\u4f59\u5e74\u6765\uff0c\u897f\u85cf\u5404\u6559\u6d3e\u7eb7 \u4e89\uff0c\u66fe\u5f15\u8d77\u597d\u51e0\u6b21\u5e72\u6208\uff0c\u82e5\u7136\u5728\u9752\u6d77\u7684\u767d\u6559\uff0c\u4e5f\u8981\u6253\u56de\u897f\u85cf\uff0c\u5c82\u4e0d\u66f4\u8981\u6380\u8d77\u6ed4\u5929\u7684\u98ce\u6d6a\u3002\u201d \u3000\u3000\u53ea\u542c\u5f97\u90a3\u738b\u5b50\u54c8\u54c8\u5927\u7b11\uff0c\u53c8\u4e3e\u8d77\u4e86\u50a8\u4f53\u9152\u5668\uff0c\u9ad8\u58f0\u8bf4\u9053\uff1a\u201c\u57fa\u8363\u57fa\u91cc\uff0c\u8fbe\u5e03\u5609\u65f6\uff01\u201d\u4e3a \u9996\u7684\u90a3\u4e2a\u767d\u8863\u5587\u561b\u63a5\u8fc7\u9152\u5668\uff0c\u6b63\u5728\u4fef\u8170\u559d\u9152\uff0c\u5c1a\u672a\u6cbe\u5507\uff0c\u5ffd\u542c\u5f97\u4e24\u65c1\u7684\u50e7\u4fa3\u6b66\u58eb\u5927\u53d1\u4e00\u58f0 \u558a\uff0c\u90a3\u738b\u5b50\u9ad8\u58f0\u559d\u9053\uff1a\u201c\u6765\u8005\u4f55\u4eba\uff1f\u64c5\u95ef\u5723\u5e99\uff01\u201d \u3000\u3000\u6842\u534e\u751f\u5b9a\u775b\u4e00\u770b\uff0c\u6765\u7684\u6b63\u662f\u90a3\u4e2a\u88ab\u81ea\u5df1\u6551\u9192\u7684\u767d\u8863\u5587\u561b\uff0c\u53ea\u89c1\u4ed6\u4e00\u53e3\u5339\u4e3e\u4e5d\u73af\u9521\u6756\uff0c\u5927 \u58f0\u53eb\u9053\uff1a\u201c\u767d\u6559\u6cd5\u738b\u5ea7\u4e0b\u62a4\u6cd5\u4f7f\u8005\u9ea6\u58eb\u8fe6\u5357\uff01\u201d \u3000\u3000\u6b64\u8a00\u4e00\u51fa\uff0c\u4e24\u65c1\u7684\u50e7\u4fa3\u6b66\u58eb\u90fd\u73b0\u51fa\u60ca\u8be7\u7684\u795e\u8272\uff0c\u90a3\u738b\u5b50\u7709\u5934\u4e00\u76b1\uff0c\u8bf4\u9053\uff1a\u201c\u6cd5\u738b\u4f7f\u8005\uff0c \u73b0\u5728\u6b64\u95f4\uff0c\u4f60\u662f\u4f55\u4eba\uff0c\u7adf\u6562\u5047\u5192\uff1f\u201d\u90a3\u81ea\u79f0\u662f\u6cd5\u738b\u62a4\u6cd5\u4f7f\u8005\u7684\u767d\u8863\u5587\u561b\uff0c\u5c06\u4e5d\u73af\u9521\u6756\u8fce\u98ce\u4e00 \u6c64\uff0c\u6756\u5934\u7684\u4e24\u4e32\u91d1\u73e0\u54d7\u7405\u7405\u4f5c\u54cd\uff0c\u4ef0\u5934\u4e00\u7b11\uff0c\u8116\u5b50\u4e0a\u60ac\u6302\u7740\u7684\u4e00\u5c0a\u91d1\u8272\u4f5b\u50cf\u95ea\u95ea\u53d1\u5149\uff0c\u6717\u58f0 \u8bf4\u9053\uff1a\u201c\u6cd5\u5668\u5728\u6b64\uff0c\u5c82\u5bb9\u5047\u5192\uff1f\u201d \u3000\u3000\u5148\u6765\u7684\u90a3\u4e24\u4e2a\u767d\u8863\u5587\u561b\u5341\u5206\u60ca\u8be7\uff0c\u8bf4\u9053\uff1a\u201c\u600e\u4e48\u6cd5\u738b\u53c8\u5c06\u4f60\u6d3e\u6765\uff1f\u201d\u539f\u6765\u8fd9\u4e24\u4e2a\u767d\u8863\u5587 \u561b\u4e43\u662f\u6cd5\u738b\u7684\u5fc3\u8179\uff0c\u8fd9\u6b21\u524d\u6765\u9b54\u9b3c\u57ce\u4e0e\u738b\u5b50\u5b9a\u76df\uff0c\u4e8b\u60c5\u6781\u4e3a\u79d8\u5bc6\uff0c\u6559\u4e2d\u53ea\u662f\u6709\u9650\u51e0\u4eba\u77e5\u9053\u3002 \u3000\u3000\u8fd9\u9ea6\u58eb\u8fe6\u5357\u5728\u767d\u6559\u4e2d\u8f88\u4efd\u751a\u4f4e\uff0c\u804c\u4f4d\u4e5f\u5e76\u4e0d\u662f\u62a4\u6cd5\u4f7f\u8005\uff0c\u6309\u8bf4\u4ed6\u4e0d\u5e94\u77e5\u9053\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u66f4 \u8f6e\u4e0d\u5230\u4ed6\u505a\u4f7f\u8005\uff0c\u4f46\u4ed6\u624b\u6301\u7684\u4e5d\u73af\u9521\u6756\uff0c\u548c\u90a3\u5c0a\u91d1\u8272\u4f5b\u50cf\uff0c\u5374\u7684\u786e\u662f\u6559\u4e2d\u7684\u6cd5\u5668\u3002 \u3000\u3000\u738b\u5b50\u89c1\u4ed6\u4eec\u8fd9\u4ed8\u795e\u6c14\uff0c\u6599\u60f3\u5176\u4e2d\u5fc5\u6709\u8e4a\u8df7\uff0c\u773c\u73e0\u4e00\u8f6c\uff0c\u5f3a\u7b11\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u6cd5\u738b\u52a0\u6d3e\u4f7f \u8005\uff0c\u8db3\u89c1\u90d1\u91cd\u6b64\u4e8b\uff0c\u4f60\u4e5f\u6765\u53c2\u52a0\u8ba2\u76df\u5427\u3002\u57fa\u8363\u57fa\u91cc\uff0c\u8fbe\u5e03\u5609\u65f6\uff01\u201d \u3000\u3000\u9ea6\u58eb\u8fe6\u5357\u53cc\u773c\u4e00\u7ffb\uff0c\u6beb\u4e0d\u5ba2\u6c14\u7684\u5c31\u4ece\u540c\u4f34\u624b\u4e2d\u62a2\u8fc7\u90a3\u9abc\u4f53\u9152\u5668\uff0c\u5ffd\u5730\u6a2a\u638c\u4e00\u51fb\uff0c\u5c06\u90a3\u9ab0 \u4f53\u5934\u5288\u5f97\u7c89\u788e\uff0c\u8840\u9152\u6e85\u4e86\u4e00\u5730\uff0c\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u8ba2\u4ec0\u4e48\u76df\uff1f\u6cd5\u738b\u6709\u547d\uff0c\u53eb\u4f60\u4eec\u4e8c\u4eba\u901f\u8d76\u56de\u53bb\uff0c \u5207\u4e0d\u53ef\u6cbe\u60f9\u90aa\u9b54\u6b6a\u9053\uff01\u201d \u3000\u3000\u5148\u6765\u7684\u90a3\u4e24\u4e2a\u767d\u8863\u5587\u561b\u52c3\u7136\u53d8\u8272\uff0c\u4e00\u4e2a\u559d\u9053\uff1a\u201c\u6cd5\u738b\u5f53\u771f\u6709\u8fd9\u8bf4\u8bdd\uff1f\u201d\u53e6\u4e00\u4e2a\u5531\u9053\uff1a \u201c\u6cd5\u738b\u5723\u8c15\u73b0\u5728\u6211\u624b\u4e2d\uff0c\u4f60\u597d\u5927\u80c6\uff0c\u4e71\u4f20\u6cd5\u65e8\uff01\u201d\u9ea6\u58eb\u8fe6\u5357\u9053\uff1a\u201c\u4f60\u4f20\u7684\u662f\u90a3\u4e2a\u6cd5\u738b\u7684\u6cd5 \u65e8\uff1f\u201d\u90a3\u767d\u8863\u5587\u561b\u65a5\u9053\uff1a\u201c\u8fd8\u80fd\u6709\u51e0\u4e2a\u6cd5\u738b\uff1f\u6211\u4f20\u7684\u662f\u704c\u9876\u56fd\u5e08\u8f6c\u8f6e\u6cd5\u738b\u7b2c\u5341\u4e94\u4e16\u7684\u6cd5 \u65e8\uff01\u201d\u9ea6\u58eb\u8fe6\u5357\u6717\u58f0\u9753\u9053\uff1a\u201c\u7b2c\u5341\u4e94\u4e16\u6cd5\u738b\u65e9\u5df2\u7985\u4f4d\uff0c\u6211\u4f20\u7684\u662f\u704c\u9876\u56fd\u5e08\u8f6c\u8f6e\u6cd5\u738b\u7b2c\u5341\u516d\u4e16 \u7684\u6cd5\u65e8\uff01\u201d \u3000\u3000\u5728\u897f\u85cf\u7684\u7ea2\u3001\u9ec4\u3001\u767d\u4e09\u6d3e\u5587\u561b\u4e4b\u4e2d\uff0c\u67e2\u6709\u767d\u6559\u6cd5\u738b\u53ef\u4ee5\u7985\u4f4d\uff0c\u4f46\u7b2c\u5341\u4e94\u4e16\u6cd5\u738b\u6b63\u5728\u76db \u5e74\uff0c\u96c4\u5fc3\u52c3\u52c3\uff0c\u65ad\u65e0\u7985\u4f4d\u4e4b\u7406\uff0c\u90a3\u4e24\u4e2a\u767d\u8863\u5587\u561b\u5446\u4e86\u4e00\u5446\uff0c\u9f50\u58f0\u559d\u9053\uff1a\u201c\u597d\u547c\uff0c\u539f\u6765\u662f\u4f60\u4eec \u8fd9\u5e2e\u53db\u6559\u90aa\u9b54\uff0c\u7e82\u593a\u4e86\u5f53\u4eca\u6cd5\u738b\u7684\u5927\u4f4d\u3002\u738b\u5b50\uff0c\u8fd9\u4e2a\u4f7f\u8005\u662f\u5047\u7684\uff01\u201d \u3000\u3000\u9ea6\u58eb\u8fe6\u5357\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u4e24\u4e2a\u624d\u662f\u5047\u7684\uff01\u201d\u90a3\u738b\u5b50\u5f53\u7136\u662f\u7ad9\u5728\u5148\u6765\u7684\u90a3\u4e24\u4e2a\u767d\u8863\u5587\u561b\u8fd9 \u8fb9\uff0c\u51b7\u51b7\u7b11\u9053\uff1a\u201c\u4e0d\u95ee\u53ef\u77e5\uff0c\u771f\u4f2a\u7acb\u8fa8\uff0c\u6cd5\u738b\u7684\u4f7f\u8005\u90a3\u6709\u5728\u8fd9\u5723\u5e99\u4e4b\u4e2d\u6363\u4e71\u7684\u9053\u7406\u3002\u201d\u628a\u624b \u4e00\u6325\uff0c\u90a3\u4e24\u4e2a\u767d\u8863\u5587\u561b\u548c\u5e26\u5f15\u4ed6\u4eec\u752b\u6765\u7684\u90a3\u4e2a\u9ed1\u8863\u6b66\u58eb\u7acb\u523b\u6251\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u53ea\u542c\u5f97\u5484\u5484\u4e24\u58f0\uff0c\u90a3\u4e24\u4e2a\u767d\u8863\u5587\u561b\u5df2\u88ab\u9ea6\u58eb\u8fe6\u5357\u7528\u91cd\u624b\u6cd5\u6253\u6655\u5730\u4e0a\u3002\u90a3\u9ed1\u8863\u6b66\u58eb\u4e00\u58f0\u602a \u5578\uff0c\u62d4\u51fa\u4e86\u4e00\u67c4\u7cbe\u5149\u95ea\u95ea\u7684\u6708\u7259\u5f2f\u5200\uff0c\u522b\u7684\u4e00\u5200\uff0c\u4fbf\u5411\u9ea6\u58eb\u8fe6\u5357\u7684\u9888\u9879\u52fe\u4e0b\uff0c\u9ea6\u58eb\u8fe6\u5357\u5c06\u4e5d \u73af\u9521\u6756\u4e00\u6311\uff0c\u53ee\u5f53\u4e00\u58f0\uff0c\u6756\u5934\u7ed9\u6708\u7259\u5f2f\u5200\u52fe\u4e86\u4e00\u4e0b\uff0c\u6e85\u51fa\u4e86\u4e00\u6e9c\u706b\u82b1\uff0c\u9ea6\u58eb\u8fe6\u5357\u56de\u6756\u4e00\u62cd\uff0c \u9521\u6756\u548c\u5200\u950b\u90fd\u78b0\u4e86\u4e00\u4e2a\u5c0f\u5c0f\u7684\u7f3a\u53e3\u3002 \u3000\u3000\u90a3\u9ed1\u8863\u6b66\u58eb\u5220\u7684\u5c06\u5200\u5236\u56de\uff0c\u5200\u5149\u4e00\u8f6c\uff0c\u53c8\u53d6\u4e2d\u76d8\uff0c\u9ea6\u58eb\u8fe6\u5357\u5c06\u9521\u6756\u5c55\u5f00\uff0c\u5b9b\u5982\u4e00\u6761\u624e \u9f99\uff0c\u51cc\u7a7a\u98de\u821e\uff0c\u90a3\u9ed1\u8863\u6b66\u58eb\u4e5f\u628a\u6708\u7259\u5f2f\u5200\u821e\u5f97\u970d\u970d\u751f\u98ce\uff0c\u5288\u3001\u523a\u3001\u52fe\u3001\u7814\uff0c\u6bcf\u6b21\u5200\u6756\u76f8\u4ea4\uff0c \u90fd\u53d1\u51fa\u547c\u5f53\u97f3\u54cd\uff01 \u3000\u3000\u6842\u534e\u751f\u5fc3\u9053\uff1a\u201c\u8fd9\u9ed1\u8863\u6b66\u58eb\u7684\u6708\u7259\u5f2f\u5200\uff0c\u5f0f\u6837\u7279\u522b\uff0c\u4f46\u8bba\u5230\u6b66\u529f\uff0c\u4e5f\u4e0d\u89c1\u5f97\u6709\u4ec0\u4e48\u7279\u5f02 \u4e4b\u5904\u3002\u5230\u662f\u8fd9\u4e2a\u9ea6\u58eb\u8fe6\u5357\u7684\u6756\u6cd5\uff0c\u521a\u731b\u51cc\u5389\uff0c\u52b2\u9053\u5341\u8db3\uff0c\u5374\u5927\u53ef\u4ee5\u4e0e\u4e2d\u571f\u7684\u4f0f\u9b54\u6756\u6cd5\u4e00\u8f83\u96cc \u96c4\u3002\u201d \u3000\u3000\u679c\u7136\u4e0d\u8fc7\u7247\u523b\uff0c\u4f46\u89c1\u9ea6\u58eb\u8fe6\u5357\u4e00\u58f0\u5927\u559d\uff0c\u9521\u6756\u4e00\u6325\uff0c\u4fbf\u5c06\u90a3\u4e2a\u9ed1\u8863\u6b66\u58eb\u7684\u6708\u7259\u5f2f\u5200\u6253\u5f97 \u8131\u624b\u98de\u51fa\u3002 \u3000\u3000\u90a3\u738b\u5b50\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u5495\u5495\u565c\u565c\u7684\u8bf4\u4e86\u51e0\u53e5\uff0c\u5374\u5e76\u4e0d\u662f\u85cf\u8bdd\uff0c\u7ad9\u5728\u7b2c\u4e00\u4f8b\u524d\u9996\u7684\u4e00\u4e2a\u756a \u50e7\u8d64\u624b\u7a7a\u62f3\u7684\u8d70\u4e86\u51fa\u6765\uff0c\u7528\u85cf\u8bdd\u558a\u9053\uff1a\u201c\u628a\u8fd9\u9521\u6756\u7ed9\u6211\u653e\u4e0b\uff01\u201d \u3000\u3000\u9ea6\u58eb\u8fe6\u5357\u5927\u6012\u559d\u9053\uff1a\u201c\u6709\u672c\u4e8b\u7684\u4f60\u4fbf\u6765\u53d6\uff01\u201d\u9521\u6756\u4e00\u4e2a\u76d8\u65cb\uff0c\u821e\u8d77\u4e86\u4e00\u9053\u5706\u5708\uff0c\u5c06\u90a3\u9ec4 \u8863\u756a\u618e\u90fd\u56f4\u5728\u5982\u5c71\u7684\u6756\u5f71\u4e4b\u4e2d\u3002 \u3000\u3000\u90a3\u756a\u618e\u51b7\u51b7\u4e00\u7b11\uff0c\u53cc\u638c\u4e00\u5708\u4e00\u526f\uff0c\u7adf\u7136\u628a\u9ea6\u58eb\u8fe6\u5357\u7684\u9521\u6756\u5f15\u51fa\u5916\u95e8\uff0c\u767b\u65f6\u811a\u8e0f\u4e2d\u5bab\uff0c\u53cd \u638c\u4fbf\u5288\u9ea6\u58eb\u8fe6\u5357\u7684\u624b\u8155\u3002\u4f73\u534e\u751f\u6697\u6697\u60ca\u5f02\uff0c\u5fc3\u9053\uff1a\u201c\u95fb\u8bf4\u5929\u7afa\u6b66\u529f\u81ea\u6210\u4e00\u6d3e\uff0c\u5c11\u6797\u6d3e\u7684\u59cb\u79df \u8fbe\u6469\u79df\u5e08\u4fbf\u662f\u5929\u7afa(\u5370\u5ea6)\u6765\u7684\uff0c\u5929\u7afa\u3001\u897f\u85cf\u3001\u5c3c\u6cca\u5c14\u7b49\u5730\u7586\u571f\u76f8\u90bb\uff0c\u6b66\u529f\u6d41\u6d3e\u5f7c\u6b64\u5f71\u54cd\u3002 \u3000\u3000\u770b\u6765\u8fd9\u9ec4\u8863\u756a\u618e\u7684\u638c\u6cd5\u548c\u4e2d\u56fd\u7684\u5185\u5bb6\u638c\u6cd5\u5404\u6709\u64c5\u573a\uff0c\u90a3\u7c98\u8fde\u4e24\u6b3e\u4e0e\u592a\u6781\u624b\u6cd5\u4e5f\u6709\u70b9\u76f8 \u4f3c\uff0c\u53ef\u89c1\u4e2d\u5916\u6b66\u529f\u867d\u5f02\uff0c\u6b66\u5b66\u7684\u9053\u7406\u5374\u5927\u81f4\u53ef\u4ee5\u76f8\u901a\u3002\u4ee5\u5185\u529f\u800c\u8bba\uff0c\u8fd9\u9ec4\u8863\u756a\u50e7\u7684\u529f\u529b\u5728\u4e2d \u56fd\u4e5f\u53ef\u4ee5\u5230\u7b2c\u4e00\u6d41\u7684\u5883\u754c\u4e86\u3002\u201d \u3000\u3000\u9ea6\u58eb\u8fe6\u5357\u7684\u6756\u6cd5\u9633\u521a\uff0c\u9ec4\u8863\u756a\u50e7\u7684\u638c\u529b\u9634\u67d4\uff0c\u6070\u662f\u5916\u5bb6\u9ad8\u624b\u548c\u5185\u5bb6\u9ad8\u624b\u7684\u6bd4\u62da\uff0c\u6597\u4e86\u534a \u4e2a\u65f6\u8fb0\uff0c\u5f3a\u5f31\u5f62\u52bf\u6e10\u6e10\u5206\u4e86\u51fa\u6765\uff0c\u4f46\u89c1\u9ea6\u58eb\u8fe6\u5357\u6c57\u5982\u96e8\u4e0b\uff0c\u4e5d\u73af\u9521\u6756\u7684\u529b\u9053\u51cf\u5f31\uff0c\u51fa\u624b\u62db\u6570 \u6bcf\u6bcf\u4e3a\u654c\u6240\u5236\uff0c\u529b\u4e0d\u4ece\u5fc3\u3002 \u3000\u3000\u6842\u534e\u751f\u6697\u53eb\u4e0d\u5999\uff0c\u4f46\u89c1\u90a3\u9ec4\u8863\u756a\u50e7\u53cc\u638c\u4e00\u5708\uff0c\u62db\u6570\u4e0e\u6b66\u5f53\u6d3e\u7684\u201c\u6000\u4e2d\u62b1\u6708\u201d\u6709\u4e9b\u76f8\u4f3c\uff0c \u4e00\u5708\u4e00\u5e26\uff0c\u500f\u7684\u5c31\u5c06\u9ea6\u58eb\u8fe6\u5357\u7684\u6756\u5934\u6293\u4f4f\uff01 \u3000\u3000\u8fd9\u4e00\u4e0b\u8fde\u6842\u534e\u751f\u4e5f\u4ee5\u4e3a\u9ea6\u58eb\u8fe6\u5357\u7684\u4e5d\u73af\u9521\u6756\u8981\u88ab\u4ed6\u593a\u51fa\u624b\u4e86\uff0c\u90a3\u77e5\u9ea6\u58eb\u8fe6\u5357\u9521\u6756\u4e00\u6296\uff0c \u9661\u7136\u95f4\u51e0\u70b9\u91d1\u5149\u7535\u5c04\u800c\u51fa\uff0c\u539f\u6765\u4ed6\u6756\u5934\u6240\u7f00\u7ea6\u4e24\u4e32\u91d1\u73e0\uff0c\u5e76\u975e\u9970\u7269\uff0c\u800c\u662f\u6697\u5668\u3002 \u3000\u3000\u90a3\u9ec4\u8863\u756a\u50e7\u731d\u4e0d\u53ca\u9632\uff0c\u6025\u5fd9\u677e\u624b\uff0c\u5374\u88ab\u4e00\u9897\u91d1\u73e0\u6253\u4e2d\u4e86\u773c\u775b\uff0c\u767b\u65f6\u63a9\u76ee\u5927\u53eb\uff0c\u738b\u5b50\u5927 \u6012\uff0c\u628a\u624b\u4e00\u6325\uff0c\u4e24\u65c1\u7684\u618e\u4fa3\u6b66\u58eb\u7eb7\u7eb7\u6d8c\u4e0a\uff0c\u9ea6\u58eb\u8fe6\u5357\u5c06\u4e5d\u73af\u9521\u6756\u6cfc\u98ce\u4e00\u821e\uff0c\u6570\u5341\u9897\u91d1\u73e0\u90fd\u5c04 \u4e86\u51fa\u6765\uff0c\u4f46\u4f17\u6b66\u58eb\u4e0e\u50e7\u4fa3\u5df2\u6709\u9632\u5907\uff0c\u5176\u4e2d\u4e0d\u4e4f\u9ad8\u624b\uff0c\u53ea\u4f24\u4e86\u6709\u9650\u7684\u51e0\u4eba\uff0c\u9ea6\u58eb\u8fe6\u5357\u5374\u5403\u4e86\u4e24 \u5200\uff0c\u80a9\u5934\u4e0a\u53c8\u6467\u4e86\u90a3\u9ec4\u8863\u756a\u618e\u7684\u4e00\u638c\u3002\u90a3\u9ec4\u8863\u756a\u50e7\u7ed9\u4ed6\u6253\u778e\u4e00\u76ee\uff0c\u6124\u6012\u4e4b\u6781\uff0c\u4e0d\u613f\u75bc\u75db\uff0c\u7a77 \u8ffd\u731b\u6253\uff01 \u3000\u3000\u9ea6\u58eb\u8fe6\u5357\u4e5f\u7b97\u673a\u7075\uff0c\u4e00\u4e0b\u5b50\u628a\u90a3\u4e24\u4e32\u91d1\u73e0\u90fd\u53d1\u51fa\u6765\uff0c\u867d\u7136\u4e0d\u80fd\u9000\u654c\uff0c\u4ed6\u5374\u8d81\u6b64\u65f6\u673a\u98de\u5954 \u51fa\u5e99\uff0c\u53ef\u662f\u90a3\u9ec4\u8863\u756a\u50e7\u600e\u80af\u653e\u8fc7\u4ed6\uff0c\u548c\u53e6\u5916\u4e24\u4e2a\u50e7\u4eba\u8ddf\u7740\u4e5f\u8ffd\u51fa\u5e99\u95e8\u3002 \u3000\u3000\u90a3\u9ec4\u8863\u756a\u618e\u8eab\u6cd5\u597d\u4fca\uff0c\u53ea\u89c1\u4ed6\u51cc\u7a7a\u4e00\u8dc3\uff0c\u5728\u534a\u7a7a\u4e2d\u4e00\u4e2a\u8f6c\u8eab\uff0c\u84b2\u6247\u822c\u7684\u5927\u624b\u5df2\u6293\u5230\u4e86\u9ea6 \u58eb\u8fe6\u5357\u7684\u80cc\u5fc3\uff0c\u9ea6\u58eb\u8fe6\u5357\u4e5f\u597d\u751f\u4e86\u5f97\uff0c\u4e00\u89c9\u8111\u540e\u98ce\u751f\uff0c\u970d\u5730\u4fbf\u662f\u53cd\u624b\u4e00\u679a\uff0c\u53ea\u542c\u5f97\u201c\u54ce\u54df\u201d \u4e00\u58f0\uff0c\u90a3\u9ec4\u8863\u756a\u618e\u5934\u4e0a\u811a\u4e0b\uff0c\u4e00\u4e2a\u5012\u683d\u8471\u4fbf\u8dcc\u4e0b\u6765\u3002\u9ea6\u58eb\u8fe6\u5357\u6014\u4e86\u4e00\u6014\uff0c\u5fc3\u9053\uff1a\u201c\u6211\u7684\u6cd5\u6756 \u8fd8\u672b\u89e6\u53ca\u5b83\u7684\u8eab\u5b50\uff0c\u600e\u7684\u4ed6\u4f7f\u8dcc\u5012\u4e86\uff0c\u96be\u9053\u5f53\u771f\u662f\u6709\u4f5b\u529b\u6697\u52a9\u6211\u4e48\uff1f\u201d\u5fc3\u5ff5\u65b9\u52a8\uff0c\u53ea\u542c\u5f97\u53c8 \u662f\u4e24\u58f0\u5c16\u53eb\uff0c\u53e6\u5916\u7684\u4e24\u4e2a\u618e\u4eba\u4e5f\u8dcc\u5012\u4e86\uff01 \u3000\u3000\u8fd9\u51e0\u4e0b\u5b50\u5feb\u5982\u7535\u5149\u77f3\u706b\uff0c\u8ffd\u51fa\u6765\u7684\u4e00\u5927\u7fa4\u50e7\u4fa3\u6b66\u58eb\u8c01\u90fd\u6ca1\u6709\u770b\u6e05\u695a\u8fd9\u4e09\u4e2a\u618e\u4eba\u662f\u600e\u4e48\u6837 \u7ed9\u6253\u5012\u7684\u3002\u731b\u542c\u5f97\u4e00\u58f0\u5927\u559d\uff0c\u547c\u547c\u98ce\u54cd\uff0c\u9ea6\u58eb\u8fe6\u5357\u773c\u775b\u4e00\u82b1\uff0c\u4fe8\u5982\u4e24\u6735\u7ea2\u4e91\u63a0\u7a7a\u800c\u81f3\uff0c\u5b9a\u775b \u4e00\u77a7\uff0c\u53ea\u89c1\u90a3\u738b\u5b50\u548c\u53e6\u4e00\u4e2a\u7ea2\u8863\u756a\u50e7\u5df2\u51c9\u5230\u8ddf\u524d\uff0c\u9ea6\u58eb\u8fe6\u5357\u5927\u5403\u4e00\u60ca\uff0c\u77e5\u9053\u8fd9\u4e9b\u618e\u4fa3\u4e43\u662f\u4ee5 \u67b6\u88c5\u7684\u989c\u8272\u5206\u522b\u5c0a\u5351\uff0c\u7ea2\u8863\u50e7\u4eba\u4f4d\u5c5e\u81f3\u5c0a\uff0c\u602a\u4e0d\u5f97\u6b66\u529f\u8fd9\u4e48\u4e86\u5f97\uff0c\u800c\u90a3\u738b\u5b50\u5c45\u7136\u4e5f\u6709\u5982\u6b64\u8eab \u624b\uff0c\u66f4\u662f\u5927\u5927\u51fa\u4ed6\u610f\u6599\u4e4b\u5916\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u53ea\u89c1\u90a3\u7ea2\u8863\u756a\u50e7\u67b6\u88c5\u4e00\u5c55\uff0c\u201c\u5484\u201d\u7684\u5c06\u9ea6\u58eb\u8fe6\u5357\u6454\u4e86\u4e00\u4e2a\u808b\u6597\uff0c\u90a3\u738b \u5b50\u559d\u9053\uff1a\u201c\u5148\u628a\u4e5d\u73af\u9521\u6756\u7ed9\u6211\u7559\u4e0b\uff01\u201d\u624b\u8155\u4e00\u7ffb\u7acb\u5373\u6293\u4f4f\u6756\u4e0a\u7684\u91d1\u73af\uff0c\u5ffd\u5730\u91cc\u53c8\u662f\u55e4\u55e4\u4e24 \u58f0\uff0c\u90a3\u738b\u5b50\u4e00\u58f0\u5927\u53eb\uff0c\u4f3c\u4e4e\u662f\u53d7\u4e86\u70b9\u4f24\uff0c\u7136\u800c\u4ecd\u662f\u628a\u9ea6\u58eb\u8fe6\u5357\u7ea6\u4e5d\u73af\u9521\u6756\u593a\u5230\u624b\u4e2d\u3002\u90a3\u7ea2\u8863 \u756a\u50e7\u67b6\u88c5\u4e00\u626c\uff0c\u8ddf\u8e2a\u75be\u81f3\uff0c\u970d\u5730\u4fbf\u662f\u5f53\u80f8\u4e00\u638c\uff01 \u3000\u3000\u9ea6\u58eb\u8fe6\u5357\u6b63\u88ab\u7ea2\u8863\u756a\u618e\u7684\u638c\u98ce\u9707\u5f97\u5934\u6655\u76ee\u7729\uff0c\u9661\u7136\u95f4\u5ffd\u89c9\u8eab\u5b50\u4e00\u8f7b\uff0c\u7adf\u4f3c\u817e\u4e91\u9a7e\u96fe\u822c\u7ed9 \u4eba\u63d0\u4e86\u4e0a\u53bb\u3002\u539f\u6765\u662f\u4f73\u534e\u751f\u51fa\u624b\u76f8\u52a9\uff0c\u5148\u524d\u90a3\u4e09\u4e2a\u50e7\u4eba\u90fd\u662f\u7ed9\u4ed6\u7528\u788e\u74e6\u6253\u5012\u7684\u3002 \u3000\u3000\u90a3\u738b\u5b50\u54c7\u54c7\u5927\u53eb\uff0c\u4e00\u7eb5\u8eab\u4e5f\u8df3\u4e0a\u5854\u6765\uff0c\u6842\u534e\u751f\u89c1\u4ed6\u7ed9\u6253\u4e2d\u7a74\u9053\uff0c\u5c45\u7136\u80fd\u8e2a\u9ad8\u8dc3\u4f4e\uff0c\u5fc3\u4e2d \u4e5f\u597d\u751f\u5947\u5f02\uff0c\u4e0d\u6562\u6020\u6162\uff0c\u4e00\u624b\u6293\u8d77\u4e86\u9ea6\u58eb\u8fe6\u5357\uff0c\u5de6\u638c\u8f6c\u8eab\u62cd\u51fa\uff0c\u90a3\u738b\u5b50\u867d\u662f\u52c7\u731b\u65e0\u4f26\uff0c\u5374\u600e \u6321\u5f97\u6842\u534e\u751f\u7684\u5185\u5bb6\u771f\u529b\uff0c\u53ea\u4e00\u638c\u4fbf\u7ed9\u9707\u5f97\u5012\u4e0b\u5730\u6765\uff0c\u90a3\u7ea2\u8863\u756a\u50e7\u8ddf\u8e2a\u800c\u81f3\uff0c\u4e00\u638c\u62cd\u6765\uff0c\u53cc\u638c \u76f8\u4ea4\uff0c\u90a3\u7ea2\u8863\u756a\u50e7\u4e5f\u7ed9\u9707\u5f97\u6447\u6447\u853d\u6643\uff0c\u9e2f\u5730\u4e00\u58f0\u602a\u5578\uff0c\u5c06\u90a3\u5927\u7ea2\u67b6\u88c5\u4e00\u6296\uff0c\u8fce\u5934\u4fbf\u7f69\uff0c\u6842\u534e \u751f\u4e00\u8dc3\u95ea\u5f00\uff0c\u90a3\u7ea2\u8863\u756a\u50e7\u8ddf\u8e2a\u6025\u4e0a\uff0c\u8888\u88df\u98de\u821e\uff0c\u75be\u5377\u800c\u6765\uff0c\u62db\u6570\u751a\u4e3a\u602a\u5f02\uff0c\u6842\u534e\u751f\u867d\u662f\u8f7b\u529f \u7edd\u9876\uff0c\u4f46\u80cc\u4e0a\u4e86\u4e00\u4e2a\u767e\u591a\u65a4\u91cd\u7684\u9ea6\u58eb\u8fe6\u5357\uff0c\u817e\u632a\u95ea\u5c55\uff0c\u5230\u5e95\u4e0d\u80fd\u968f\u5fc3\u6240\u6b32\uff0c\u9669\u9669\u7ed9\u4ed6\u7684\u8888\u88df \u5377\u7ffb\uff0c\u4e24\u4eba\u5728\u5e99\u5b87\u4e0a\u52a8\u624b\uff0c\u8fc5\u5373\u8ffd\u5230\u4e86\u767d\u5854\u7684\u7b2c\u4e8c\u5c42\u3002 \u3000\u3000\u53ea\u89c1\u90a3\u7ea2\u8863\u756a\u50e7\u624b\u6309\u98de\u6a90\uff0c\u9661\u7136\u95f4\u8eab\u5b50\u62d4\u7a7a\u800c\u8d77\uff0c\u5148\u8df3\u4e0a\u4e86\u7b2c\u4e09\u5c42\uff0c\u5c45\u9ad8\u4e34\u4e0b\uff0c\u5927\u7ea2\u67b6 \u6881\u53c8\u4f3c\u706b\u4e91\u4e00\u822c\u75be\u7f69\u4e0b\u6765\uff0c\u5c31\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u4f46\u89c1\u767d\u5149\u4e00\u95ea\uff0c\u4fe8\u5982\u957f\u7a7a\u7535\u95ea\uff0c\u523a\u7a7f\u4e86\u539a\u539a\u7684\u5c42 \u4e91\uff0c\u539f\u6765\u662f\u6842\u534e\u751f\u62d4\u51fa\u4e86\u5bb6\u4f20\u7684\u817e\u86df\u5b9d\u5251\uff0c\u5f53\u5e74\u4ed6\u7684\u7236\u4eb2\u6842\u4ef2\u660e\u66fe\u4ed7\u7740\u8fd9\u628a\u817e\u86df\u5b9d\u5251\uff0c\u6253\u8d25 \u8fc7\u65e0\u6570\u6b66\u6797\u9ad8\u624b\uff0c\u8d62\u5f97\u4e86\u201c\u5929\u5c71\u4e03\u5251\u201d\u4e4b\u4e00\u7684\u540d\u5934\uff0c\u795e\u7269\u5229\u5668\uff0c\u7aef\u7684\u662f\u975e\u540c\u5c0f\u53ef\uff0c\u52a0\u4e0a\u6842\u534e \u751f\u7684\u5185\u5bb6\u771f\u529b\uff0c\u9976\u662f\u90a3\u7ea2\u8863\u756a\u50e7\u529f\u529b\u6df1\u539a\uff0c\u4e5f\u6321\u4e0d\u4f4f\u8fd9\u7a7f\u4e91\u4e00\u5251\uff0c\u4f46\u89c1\u767d\u5149\u4e00\u95ea\u4e4b\u4e0b\uff0c\u90a3\u5927 \u7ea2\u67b6\u88c5\u5df2\u88ab\u523a\u7a7f\u4e86\u4e24\u4e2a\u5927\u6d1e\u3002 \u3000\u3000\u7ea2\u8863\u756a\u50e7\u629b\u4e86\u67b6\u88c5\uff0c\u4e00\u58f0\u6012\u543c\uff0c\u53cc\u638c\u9f50\u626c\uff0c\u6842\u534e\u751f\u672b\u6e05\u695a\u5b83\u7684\u6765\u5386\uff0c\u4e0d\u613f\u4f24\u4ed6\uff0c\u5251\u8bc0\u4e00 \u987f\uff0c\u817e\u86df\u5b9d\u5251\u5728\u4ed6\u9762\u95e8\u4e00\u6643\uff0c\u5f15\u5f00\u4e86\u4ed6\u7684\u773c\u62bc\uff0c\u7ea2\u8863\u756a\u50e7\u53cc\u638c\u6251\u7a7a\uff0c\u5931\u4e86\u91cd\u5fc3\uff0c\u88ab\u6842\u534e\u751f\u5012 \u8f6c\u5251\u67c4\uff0c\u5728\u80f8\u53e3\u8f7b\u8f7b\u4e00\u70b9\uff0c\u767b\u65f6\u7eb5\u4e09\u5c42\u9ad8\u7684\u5854\u9876\u8dcc\u4e86\u4e0b\u6765\uff0c\u6842\u534e\u751f\u7eb5\u58f0\u957f\u7b11\uff0c\u80cc\u8d77\u4e86\u9ea6\u58eb\u8fe6 \u5357\uff0c\u95ea\u7535\u822c\u7684\u8f6c\u5230\u4e86\u767d\u5854\u80cc\u9762\uff0c\u8df3\u4e0b\u5730\u6765\uff0c\u7acb\u523b\u5411\u5c71\u5934\u75be\u8dd1\u3002 \u3000\u3000\u5c71\u4e0a\u4e91\u6d77\u8ff7\u832b\uff0c\u96ea\u5cf0\u8f70\u7acb\uff0c\u96ea\u6708\u4ea4\u8f89\uff0c\u665a\u95f4\u7684\u666f\u8c61\u66f4\u663e\u5f97\u5947\u4e3d\u65e0\u7574\u3002\u6842\u534e\u751f\u80cc\u7740\u9ea6\u58eb\u8fe6 \u5357\uff0c\u8dd1\u4e86\u597d\u4e00\u4f1a\u5b50\uff0c\u6e10\u6e10\u89c9\u5f97\u6709\u70b9\u6c14\u5598\uff0c\u4fef\u8170\u4e00\u671b\uff0c\u811a\u4e0b\u201c\u9b54\u9b3c\u57ce\u201d\u5728\u4e91\u6c14\u5f25\u6f2b\u4e4b\u4e0b\u53ea\u770b\u5f97 \u89c1\u4e00\u4e2a\u5c16\u5c16\u7684\u767d\u5854\u9876\u4e86\u3002\u62ac\u5934\u4e00\u671b\uff0c\u96ea\u5cf0\u9ad8\u63d2\u4e91\u9704\uff0c\u770b\u4e0d\u89c1\u9876\uff0c\u81ea\u5df1\u7ad9\u7acb\u4e4b\u5904\uff0c\u4ec5\u4ec5\u662f\u5728\u5c71 \u8170\uff0c\u5374\u5df2\u6709\u70b9\u201c\u9ad8\u5904\u4e0d\u80dc\u5bd2\u201d\u4e4b\u611f\u4e86\u3002 \u3000\u3000\u6842\u534e\u751f\u89c1\u9ea6\u58eb\u8fe6\u5357\u8fd8\u662f\u660f\u8ff7\u672b\u9192\uff0c\u629a\u4ed6\u8109\u606f\uff0c\u8109\u606f\u751a\u7c97\uff0c\u65e2\u4e0d\u50cf\u662f\u53d7\u4e86\u5185\u4f24\uff0c\u4e5f\u4e0d\u50cf\u662f \u7ed9\u4eba\u70b9\u4e86\u7a74\u9053\uff0c\u5fc3\u4e2d\u751a\u662f\u5947\u602a\uff0c\u518d\u8d70\u4e86\u4e00\u4f1a\uff0c\u5ffd\u89c9\u51b7\u98ce\u4e4b\u4e2d\uff0c\u6709\u4e00\u80a1\u6e29\u6696\u6e7f\u6da6\u7684\u7a7a\u6c14\u6251\u9762\u800c \u6765\uff0c\u5411\u524d\u4e00\u770b\uff0c\u524d\u9762\u662f\u4e00\u4e2a\u4e24\u5cf0\u4e4b\u95f4\u7684\u76c6\u5730\uff0c\u6709\u4e00\u80a1\u55b7\u6cc9\u6b63\u5728\u5636\u5636\u55b7\u6c34\uff0c\u707c\u70ed\u7684\u6c34\u82b1\u88ab\u98ce\u5439 \u6563\uff0c\u5728\u6708\u5149\u4e0b\u5f62\u6210\u4e00\u56e2\u56e2\u767d\u8272\u7684\u82b1\u73af\uff0c\u55b7\u6cc9\u6240\u5728\uff0c\u5730\u6c14\u8f83\u70ed\uff0c\u5728\u51b0\u5ca9\u4e4b\u4e0b\uff0c\u5c45\u7136\u76db\u5f00\u7740\u8bb8\u591a \u4e0d\u77e5\u540d\u5b57\u7684\u82b1\u6735\uff0c\u7aef\u7684\u6709\u5982\u7ae5\u8bdd\u4e2d\u7684\u4e16\u754c\u4e00\u822c\u3002\u6842\u534e\u751f\u5fc3\u9053\uff1a\u201c\u4ed6\u4eec\u8e2a\u662f\u8ffd\u6765\uff0c\u4e5f\u5f97\u8d39\u4e00\u4e9b \u65f6\u5019\u624d\u80fd\u8ffd\u5230\u8fd9\u91cc\uff0c\u6211\u6b63\u597d\u5728\u8fd9\u5c71\u8c37\u4e2d\u6b47\u606f\u4e00\u4f1a\u3002\u201d \u3000\u3000\u6842\u534e\u751f\u8d70\u5230\u55b7\u6cc9\u65c1\u8fb9\uff0c\u5c06\u9ea6\u58eb\u8fe6\u5357\u653e\u4e86\u4e0b\u6765\uff0c\u4ed4\u7ec6\u5ba1\u89c6\uff0c\u7adf\u4e0d\u77e5\u4ed6\u662f\u53d7\u4e86\u4f55\u4f24\uff0c\u7adf\u81f3\u660f \u8ff7\u4e0d\u9192\u3002\u5c06\u5929\u5c71\u96ea\u83b2\u653e\u8fd1\u4ed6\u7684\u9f3b\u89c2\uff0c\u4ea6\u4e0d\u89c1\u6548\uff0c\u663e\u7136\u53c8\u4e0d\u662f\u4e2d\u4e86\u4ec0\u4e48\u6bd2\u4e86\u3002\u6842\u534e\u751f\u65e0\u6cd5\uff0c\u53ea \u597d\u5c06\u771f\u529b\u51dd\u805a\u638c\u5fc3\uff0c\u5728\u4ed6\u810a\u9aa8\u201c\u5929\u67a2\u7a74\u201d\u7684\u5468\u56f4\uff0c\u8f7b\u8f7b\u63c9\u6413\uff0c\u7ed9\u4ed6\u63a8\u8840\u8fc7\u5bab\uff0c\u8fd9\u201c\u5929\u67a2\u7a74\u201d \u4e43\u662f\u7ecf\u8109\u7684\u603b\u7eb2\uff0c\u6842\u534e\u751f\u8fd0\u7528\u4e86\u201c\u8fbe\u6469\u771f\u7ecf\u201d\u4e2d\u6700\u4e0a\u4e58\u7684\u89e3\u7a74\u529f\u592b\uff0c\u6309\u8bf4\u82e5\u662f\u4ed6\u88ab\u70b9\u4e86\u7a74\u9053 \u7684\u8bdd\uff0c\u4e0d\u8bba\u662f\u70b9\u4e86\u90a3\u4e00\u5904\u7a74\u9053\uff0c\u90fd\u53ef\u4ee5\u89e3\u5f00\uff0c\u679c\u7136\u8fc7\u4e86\u4e00\u9635\uff0c\u9ea6\u58eb\u8fe6\u5357\u4fbf\u60a0\u60a0\u9192\u8f6c\uff0c\u5927\u58f0\u53eb \u9053\uff1a\u201c\u5475\uff0c\u539f\u6765\u662f\u4f60\u6551\u6211\uff0c\u8d76\u5feb\u7ed9\u6211\u758f\u901a\u95ed\u8840\u7684\u7ecf\u7edc\u3002\u201d\u53ea\u89c1\u4ed6\u6323\u624e\u4e86\u597d\u4e00\u4f1a\u5b50\uff0c\u4ecd\u662f\u4e0d\u80fd \u52a8\u5f39\u3002 \u3000\u3000\u6842\u534e\u751f\u9053\uff1a\u201c\u4ec0\u4e48\u95ed\u8840\u7684\u7ecf\u7edc\uff0c\u5728\u4ec0\u4e48\u90e8\u4f4d\uff1f\u201d\u9ea6\u58eb\u8fe6\u5357\u4e5f\u61c2\u5f97\u4e00\u70b9\u4e2d\u56fd\u7684\u6b66\u5b66\uff0c\u8bf4 \u9053\uff1a\u201c\u8fd9\u5c31\u50cf\u4f60\u4eec\u5185\u5bb6\u7684\u70b9\u7a74\u5440\uff0c\u6211\u6653\u5f97\u4ec0\u4e48\u90e8\u4f4d\uff0c\u8fd8\u7528\u4f60\u89e3\u5417\uff1f\u201d\u6842\u534e\u751f\u5bb6\u5b66\u6e0a\u6e90\uff0c\u5e08\u53cb \u8f88\u90fd\u662f\u6b66\u6797\u4e2d\u9876\u513f\u5c16\u513f\u7684\u89d2\u8272\uff0c\u4ed6\u5bf9\u5404\u5bb6\u5404\u6d3e\u7684\u70b9\u7a74\u624b\u6cd5\u65e0\u4e0d\u719f\u6089\uff0c\u53ef\u5c31\u662f\u4e0d\u61c2\u8fd9\u79cd\u5f02\u57df\u7684 \u70b9\u7a74\u529f\u592b\u3002 \u3000\u3000\u9ea6\u58eb\u8fe6\u5357\u9053\uff1a\u201c\u4f60\u5b66\u8fc7\u5b50\u5348\u6d41\u95ed\u8840\u6cd5\u5417\uff1f\u201d\u6842\u534e\u751f\u6447\u4e86\u6447\u5934\uff0c\u4ed4\u7ec6\u95ee\u4ed6\uff0c\u539f\u6765\u8fd9\u662f\u4ece\u6b27 \u6d32\u4f20\u5230\u963f\u523a\u4f2f\u56fd\u5bb6\uff0c\u518d\u4ece\u963f\u523a\u4f2f\u56fd\u5bb6\u4f20\u5230\u5370\u5ea6\u3001\u5c3c\u6cca\u5c14\u8bf8\u56fd\uff0c\u518d\u91c7\u7eb3\u4e86\u5370\u5ea6\u745c\u5496\u672f\u7684\u201c\u95ed\u6c14 \u8bc0\u201d\uff0c\u6240\u521b\u51fa\u6765\u7684\u4e00\u79cd\u7c7b\u4f3c\u4e2d\u56fd\u70b9\u7a74\u7684\u529f\u592b\uff0c\u9ea6\u58eb\u8fe6\u5357\u4ece\u73b0\u4efb\u6cd5\u738b\u90a3\u513f\u77e5\u9053\u6709\u8fd9\u79cd\u529f\u592b\uff0c\u90a3 \u662f\u6309\u7740\u65f6\u8fb0\uff0c\u5c06\u4eba\u4f53\u67d0\u4e00\u4e2a\u90e8\u4f4d\u7684\u6c14\u8840\u963b\u6ede\uff0c\u4f7f\u5176\u4e0d\u80fd\u81ea\u7136\u8fd0\u884c\u7684\u529f\u592b\uff0c\u672c\u6765\u8fd9\u79cd\u529f\u592b\u8fdc\u8fdc \u4e0d\u53ca\u4e2d\u56fd\u70b9\u7a74\u6cd5\u7684\u6df1\u5965\u795e\u5947\uff0c\u53ef\u662f\u6842\u534e\u751f\u4e0d\u61c2\u8fd9\u79cd\u529f\u592b\uff0c\u800c\u9ea6\u58eb\u8fe6\u5357\u4e5f\u7565\u89e3\u76ae\u6bdb\uff0c\u65e0\u6cd5\u6559\u7ed9 \u6842\u534e\u751f\u77e5\u9053\u3002 \u3000\u3000\u6842\u534e\u751f\u5c0f\u5fc3\u7ffc\u7ffc\u7684\u8bd5\u7740\u7528\u5404\u79cd\u4e0a\u4e58\u7684\u89e3\u7a74\u624b\u6cd5\u7ed9\u4ed6\u89e3\u7a74\uff0c\u5374\u662f\u4e1d\u6beb\u65e0\u6548\uff0c\u53cd\u800c\u5f04\u5f97\u4ed6\u9891 \u9891\u547c\u75db\uff0c\u6842\u534e\u751f\u6413\u6413\u53cc\u624b\uff0c\u82e6\u7b11\u8bf4\u9053\uff1a\u201c\u6ca1\u529e\u6cd5\u5566\uff01\u201d\u8bdd\u672a\u8bf4\u5b8c\uff0c\u5ffd\u542c\u5f97\u201c\u5484\u776b\u201d\u4e00\u58f0\uff0c\u4e00 \u7c92\u77f3\u5b50\u4e0d\u77e5\u4ece\u4ec0\u4e48\u5730\u65b9\u63b7\u6765\uff0c\u9ea6\u58eb\u8fe6\u5357\u7a81\u7136\u5927\u53eb\u4e00\u58f0\uff0c\u7eb5\u8eab\u8dc3\u8d77\uff0c\u6842\u534e\u751f\u5927\u5403\u4e00\u60ca\uff0c\u6b63\u60f3\u8df3 \u51fa\u53bb\u770b\uff0c\u5374\u88ab\u9ea6\u58eb\u8fe6\u5357\u4e00\u628a\u62c9\u7740\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u539f\u6765\u662f\u61c2\u5f97\u7684\uff0c\u5374\u6545\u610f\u9a97\u6211\uff0c\u62ff\u6211\u7740\u6025\uff01\u201d\u539f \u6765\u8fd9\u7c92\u77f3\u5b50\u6b63\u662f\u4e58\u7740\u9ea6\u58eb\u8fe6\u5357\u6323\u624e\u7740\u8f6c\u8eab\u4e4b\u9645\u6253\u6765\uff0c\u9ea6\u58eb\u8fe6\u5357\u6839\u672c\u6ca1\u6709\u77a7\u89c1\uff0c\u53ea\u89c9\u597d\u4f3c\u6842\u534e \u751f\u7684\u6307\u5934\u89e6\u7740\u5b83\u7684\u8170\u90e8\u67d0\u4e00\u4e2a\u65b9\u4f4d\u4e00\u822c\u3002 \u3000\u3000\u6842\u534e\u751f\u9a7e\u5947\u66f4\u751a\uff0c\u8df3\u4e0a\u51b0\u5c71\u77f3\uff0c\u4f46\u89c1\u6811\u68a2\u98ce\u52a8\uff0c\u91ce\u82b1\u98d8\u843d\uff0c\u5c71\u4e0a\u4e91\u6c14\u5f25\u6f2b\uff0c\u597d\u50cf\u8499\u4e0a\u4e00 \u5c42\u8584\u96fe\u51b0\u9500\uff0c\u90a3\u91cc\u6709\u4eba\u7684\u5f71\u4e8e\uff1f\u6842\u534e\u751f\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u8fd9\u4eba\u63b7\u77f3\u89e3\u7a74\uff0c\u5927\u662f\u4e0d\u51e1\uff1b\u8fd9\u4efd\u8f7b\u529f\uff0c \u66f4\u662f\u65e0\u4eba\u80fd\u53ca\uff0c\u60f3\u4e0d\u5230\u5728\u8fd9\u7a77\u8352\u5f02\u57df\uff0c\u679c\u7136\u78b0\u5230\u5f02\u4eba\uff01\u201d \u3000\u3000\u9ea6\u58eb\u8fe6\u5357\u9053\uff1a\u201c\u5582\uff0c\u4f60\u77a7\u4ec0\u4e48\uff1f\u662f\u4e0d\u662f\u6709\u8ffd\u5175\u6765\uff1f\u201d\u6842\u534e\u751f\u6447\u4e86\u6447\u5934\uff0c\u8dc3\u4e0b\u51b0\u5ca9\uff0c\u53cc\u65b9 \u901a\u4e86\u540d\u59d3\uff0c\u6842\u534e\u751f\u7b11\u9053\uff1a\u201c\u4f60\u73b0\u5728\u603b\u53ef\u4ee5\u544a\u8bc9\u6211\u4e86\u5427\uff0c\u4ed6\u4eec\u662f\u4ec0\u4e48\u4eba\uff1f\u201d\u9ea6\u58eb\u8fe6\u5357\u9053\uff1a\u201c\u90a3 \u738b\u5b50\u662f\u5c3c\u6cca\u5c14\u7684\u738b\u5b50\uff0c\u90a3\u4e9b\u4eba\u662f\u4ed6\u5e26\u6765\u7684\u50e7\u4fa3\u548c\u6b66\u58eb\u3002\u201d \u3000\u3000\u6842\u534e\u751f\u5403\u4e86\u4e00\u60ca\uff0c\u53eb\u9053\uff1a\u201c\u8fd9\u738b\u5b50\u91ce\u5fc3\u4e0d\u5c0f\uff01\u201d\u9ea6\u58eb\u8fe6\u5357\u9053\uff1a\u201c\u4e0d\u9519\u3002\u4f46\u542c\u8bf4\u8fd9\u738b\u5b50\u5e76 \u4e0d\u662f\u5c3c\u6cca\u5c14\u56fd\u738b\u7684\u592a\u5b50\uff0c\u67e2\u662f\u5b83\u7684\u4f84\u513f\u3002\u5c3c\u6cca\u5c14\u56fd\u738b\u819d\u4e0b\u65e0\u513f\uff0c\u4ed6\u60f3\u7ee7\u627f\u738b\u4f4d\uff0c\u6240\u4ee5\u57f9\u690d\u4e86 \u4e00\u6279\u515a\u7fbd\uff0c\u4ece\u963f\u523a\u4f2f\u8bf8\u56fd\u751a\u81f3\u4ece\u6b27\u6d32\u8bf7\u4e86\u8bb8\u591a\u6b66\u58eb\u6765\u3002\u5370\u5ea6\u5a46\u7f57\u6559\u7684\u4e00\u4e9b\u9ad8\u624b\u4e5f\u505a\u4e86\u4ed6\u7684\u5bbe \u5ba2\uff0c\u4ed6\u4e3a\u4e86\u5de9\u56fa\u5b83\u7684\u5730\u4f4d\uff0c\u5f88\u60f3\u7acb\u529f\u56fd\u5916\uff0c\u5148\u628a\u897f\u85cf\u706d\u4e86\uff0c\u505a\u4ed6\u7684\u5c5e\u56fd\u3002\u201d\u6842\u534e\u751f\u201c\u963f\u5440\u201d \u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u4ed6\u9009\u62e9\u8fd9\u4e2a\u795e\u79d8\u7684\u5730\u65b9\u505a\u4ed6\u7684\u5de2\u7a74\u3002\u201d\u9ea6\u58eb\u8fe6\u5357\u9053\uff1a\u201c\u8fd9\u4e2a\u9b54\u9b3c\u57ce\u636e \u8bf4\u672c\u6765\u662f\u4e2a\u53e4\u57ce\uff0c\u540e\u6765\u5730\u5f62\u53d8\u5316\uff0c\u524d\u6709\u6c99\u6f20\uff0c\u540e\u6709\u51b0\u5c71\uff0c\u53e4\u57ce\u4e5f\u5df2\u98ce\u5316\u9053\u5c3d\u3002\u8fd9\u91cc\u7684\u571f\u4eba\u6bcf \u665a\u542c\u5230\u98ce\u4e2d\u602a\u58f0\uff0c\u66f4\u4e0d\u6562\u8fdb\u53bb\u63a2\u9669\uff0c\u5927\u5bb6\u90fd\u8bf4\u8fd9\u4e2a\u662f\u9b54\u9b3c\u57ce\u4e86\u3002\u5c3c\u6cca\u5c14\u738b\u5b50\u5728\u8fd9\u5e9f\u57ce\u7684\u9057\u5740 \u4e0a\u5efa\u623f\u5c4b\u5e99\u5b87\u548c\u767d\u5854\uff0c\u7ecf\u8425\u4e86\u51e0\u5e74\uff0c\u53ef\u53f9\u6ee1\u6e05\u7684\u9a7b\u85cf\u5927\u81e3\u4e00\u70b9\u4e5f\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u6842\u534e\u751f\u9053\uff1a\u201c\u4ed6\u8054\u7edc\u90a3\u51e0\u4e2a\u85e9\u738b\uff0c\u8fd8\u60f3\u5506\u4f7f\u4f60\u4eec\u767d\u6559\u6cd5\u738b\u6253\u56de\u897f\u85cf\uff0c\u8fd9\u6b63\u597d\u7ed9\u4ed6\u6d51\u6c34\u6478 \u9c7c\u7684\u673a\u4f1a\u3002\u201d\u9ea6\u58eb\u8fe6\u5357\u9053\uff1a\u201c\u6211\u4eec\u73b0\u4efb\u7684\u6cd5\u738b\u53ef\u4e0d\u4e0a\u4ed6\u8fd9\u4e2a\u5f53\u3002\u201d \u3000\u3000\u7ecf\u9ea6\u58eb\u8fe6\u5357\u8bf4\u660e\uff0c\u6842\u534e\u751f\u8fd9\u624d\u77e5\u9053\uff0c\u539f\u6765\u5728\u767d\u6559\u5587\u561b\u4e4b\u4e2d\uff0c\u4e5f\u5206\u4e3a\u4e24\u6d3e\uff0c\u524d\u4efb\u6cd5\u738b\u662f\u65e7 \u6d3e\uff0c\u4e3b\u5f20\u7528\u6b66\u529b\u6253\u56de\u897f\u85cf\uff0c\u6240\u4ee5\u4e0d\u60dc\u4e0e\u5c3c\u6cca\u5c14\u738b\u5b50\u52fe\u7ed3\u3002\u73b0\u4ee3\u6cd5\u738b\u672c\u662f\u638c\u7ba1\u5178\u7c4d\u7ecf\u6587\u7684\u6cd5 \u5e08\uff0c\u5728\u6559\u4e2d\u5730\u4f4d\uff0c\u4ec5\u6b21\u4e8e\u524d\u4efb\u6cd5\u738b\uff0c\u4ed6\u4ece\u79d8\u7c4d\u4e4b\u4e2d\u901a\u609f\u4e86\u5bc6\u5b97\u7684\u6b66\u529f\uff0c\u5e76\u901a\u6653\u68b5\u6587\u548c\u5c3c\u6cca\u5c14 \u8bed\u8a00\uff0c\u53c8\u66fe\u5230\u8fc7\u5370\u5ea6\u7684\u5723\u5730\u793c\u4f5b\uff0c\u5728\u767d\u6559\u4e4b\u4e2d\u4ee5\u5b66\u95ee\u6e0a\u535a\u8457\u79f0\uff0c\u5f88\u5f97\u4e00\u4e9b\u4eba\u62e5\u62a4\u3002\u4ed6\u7684\u4e3b\u5f20 \u548c\u524d\u4efb\u6cd5\u738b\u4e0d\u540c\uff0c\u4e3b\u5f20\u548c\u73b0\u5728\u897f\u85cf\u638c\u6743\u7684\u9ec4\u6559\u8c08\u548c\uff0c\u88ab\u79f0\u4e3a\u65b0\u6d3e\u3002\u8fd9\u6b21\u524d\u4efb\u6cd5\u738b\u52fe\u7ed3\u5c3c\u6cca\u5c14 \u738b\u5b50\u7684\u4e8b\u60c5\u6f0f\u51fa\u6765\uff0c\u4e24\u6d3e\u51b2\u7a81\uff0c\u62e5\u62a4\u65b0\u6d3e\u7684\u5341\u5c45\u516b\u4e5d\uff0c\u7ec8\u4e8e\u5728\u524d\u4efb\u6cd5\u738b\u6d3e\u51fa\u4e86\u90a3\u4e24\u4e2a\u4f7f\u8005\u7684 \u7b2c\u4e8c\u5929\uff0c\u5c31\u5c06\u4ed6\u5e9f\u4e86\u3002 \u3000\u3000\u6842\u534e\u751f\u542c\u8bf4\u767d\u6559\u5587\u561b\u4e2d\u6709\u8fd9\u6837\u7684\u4eba\u6750\uff0c\u751a\u4e3a\u5411\u5f80\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u6709\u4ec0\u4e48\u8981\u6211\u6548\u52b3\u7684\u5730\u65b9\uff0c \u6211\u5b9a\u5f53\u5c3d\u529b\u3002\u201d\u9ea6\u58eb\u8fe6\u5357\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u6211\u8fd9\u6b21\u5949\u6cd5\u738b\u9063\u6d3e\uff0c\u867d\u5e78\u4e0d\u8fb1\u4f7f\u547d\uff0c\u963b\u6b62\u4e86\u4ed6\u4eec\u7b7e\u8ba2 \u76df\u7ea6\uff0c\u4f46\u5931\u4e86\u6cd5\u6756\uff0c\u7ec8\u662f\u5947\u803b\u5927\u8fb1\uff0c\u6211\u987b\u5f97\u7acb\u5373\u56de\u53bb\u62a5\u544a\u6cd5\u738b\u3002\u8bf7\u4f60\u4ee3\u6211\u505a\u4e00\u4ef6\u4e8b\u60c5\uff0c\u5230\u62c9 \u8428\u53bb\u89c1\u8fbe\u8d56\u6d3b\u4f5b\uff0c\u62a5\u544a\u4ed6\u4f60\u4eca\u665a\u7684\u6240\u89c1\u6240\u95fb\uff0c\u5e76\u4ee3\u4e3a\u8f6c\u8fbe\u6211\u6559\u6cd5\u738b\u7684\u5fc3\u610f\u3002\u201d\u6842\u534e\u751f\u9053\uff1a \u201c\u542c\u8bf4\u8fbe\u8d56\u548c\u73ed\u60ee\u8fd9\u4e24\u4f4d\u6d3b\u4f5b\u5e76\u4e0d\u662f\u5bfb\u5e38\u4eba\u6240\u6613\u89c1\u7684\u3002\u201d\u9ea6\u58eb\u8fe6\u5357\u9664\u4e0b\u4e86\u8eab\u4e0a\u6240\u5e26\u7684\u90a3\u5c0a\u91d1 \u4f5b\uff0c\u4ea4\u7ed9\u4ed6\u9053\uff1a\u201c\u4f60\u62ff\u8fd9\u4e2a\u4f5c\u4e3a\u4fe1\u7269\u3002\u4ee5\u4f60\u7684\u6b66\u529f\uff0c\u81ea\u53ef\u6084\u6084\u7684\u9020\u5165\u5e03\u8fbe\u62c9\u5bab\u3002\u201d \u3000\u3000\u6842\u534e\u751f\u63a5\u8fc7\u91d1\u4f5b\uff0c\u62ac\u5934\u4e00\u671b\uff0c\u5ffd\u89c1\u5357\u5317\u4e24\u8fb9\u7684\u5c71\u5934\uff0c\u90fd\u51fa\u73b0\u4e86\u9ed1\u8863\u4eba\u7684\u5f71\u5b50\uff0c\u6842\u534e\u751f\u7b11 \u9053\uff1a\u201c\u5c3c\u6cca\u5c14\u738b\u5b50\u6d3e\u6765\u7684\u8ffd\u5175\uff0c\u7ec8\u4e8e\u627e\u5230\u8fd9\u513f\u6765\u4e86\uff01\u201d  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u9b44\u5bd2\u5149\u5251\u300b\u2014\u2014\u7b2c\u4e09\u7ae0\u3000\u9b54\u9b3c\u57ce\u4e2d\u95fb\u7389\u7b1b \u6881\u7fbd\u751f\u300a\u51b0\u9b44\u5bd2\u5149\u5251\u300b \u7b2c\u4e09\u7ae0\u3000\u9b54\u9b3c\u57ce\u4e2d\u95fb\u7389\u7b1b \u3000\u3000\u8bdd\u58f0\u672a\u4e86\uff0c\u5ffd\u542c\u5f97\u8f70\u8f70\u9686\u9686\u4e4b\u58f0\u54cd\u5f7b\u5c71\u8c37\uff0c\u539f\u6765\u5c3c\u6cca\u5c14\u738b\u5b50\u6d3e\u6765\u7684\u90a3\u51e0\u4e2a\u6b66\u58eb\u4e5f\u5df2\u53d1\u73b0 \u4e86\u4ed6\u4eec\u7684\u8e2a\u8ff9\uff0c\u5c06\u4e00\u5757\u5757\u5927\u77f3\u63a8\u4e0b\u6765\u3002\u6842\u534e\u751f\u6012\u9053\uff1a\u201c\u597d\u72e0\u6bd2\u7684\u624b\u6bb5\uff01\u201d\u62c9\u8d77\u4e86\u9ea6\u58eb\u8fe6\u5357\u8df3 \u8dc3\u95ea\u907f\uff0c\u6709\u51e0\u5757\u77f3\u5934\u6eda\u5230\u4e86\u55b7\u6cc9\u7684\u65c1\u8fb9\uff0c\u597d\u5728\u6ca1\u6709\u78b0\u5230\u4ed6\u4eec\u3002\u6709\u4e00\u4e2a\u9ed1\u8863\u6b66\u58eb\u8d70\u4e0b\u5c71\u5761\uff0c\u5927 \u7ea6\u662f\u60f3\u89d1\u51c6\u4ed6\u4eec\u518d\u629b\u5927\u77f3\uff0c\u6842\u534e\u751f\u51b7\u7b11\u9053\uff1a\u201c\u597d\uff0c\u53eb\u4f60\u4e5f\u6211\u4e00\u9897\u77f3\u5934\uff01\u201d\u62ac\u8d77\u4e00\u7c92\u77f3\u5b50\uff0c\u53cc \u6307\u4e00\u5f39\uff0c\u75be\u98de\u800c\u4e0a\uff0c\u90a3\u9ed1\u8863\u6b66\u58eb\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u6842\u534e\u751f\u7adf\u6709\u8fd9\u6837\u7684\u5f39\u6307\u795e\u901a\uff0c\u5c45\u7136\u80fd\u628a\u77f3\u5b50\u5f39 \u4e0a\u6570\u5341\u4e08\u9ad8\u7684\u51b0\u5cb8\uff0c\u767b\u65f6\u7ed9\u6253\u4e2d\u4e86\u7a74\u9053\uff0c\u5e94\u58f0\u800c\u5012\uff01 \u3000\u3000\u5176\u4ed6\u7684\u6b66\u58eb\u90a3\u8fd8\u6562\u518d\u4e0b\u6765\uff0c\u53ea\u662f\u4e0d\u65ad\u7684\u4ece\u5c71\u5cf0\u4e0a\u629b\u6eda\u5de8\u77f3\uff0c\u5ffd\u7136\u95f4\uff0c\u5c71\u8c37\u91cc\u54cd\u8d77\u5de8\u5927\u7684 \u96f7\u58f0\uff0c\u4e07\u5c71\u56de\u5e94\uff0c\u9707\u8033\u6b32\u804b\uff0c\u9ea6\u58eb\u8fe6\u5357\u53eb\u9053\uff1a\u201c\u4e0d\u597d\uff0c\u82e5\u662f\u4ed6\u4eec\u518d\u6eda\u77f3\u5934\uff0c\u5c31\u8981\u5f15\u8d77\u96ea\u5d29 \u5566\uff01\u201d\u4f46\u89c1\u78e8\u76d8\u5927\u7684\u96ea\u5757\u4ece\u60ac\u5ca9\u4e0a\u6f14\u584c\u800c\u4e0b\uff0c\u58f0\u52bf\u6781\u4e3a\u60ca\u4eba\uff01\u8981\u77e5\u9053\u8fd9\u4e9b\u9ad8\u5c71\uff0c\u5c71\u5cad\u79ef\u96ea\uff0c \u5e38\u6cbf\u7740\u5c71\u5761\u5411\u4e0b\u6eda\u52a8\uff0c\u5c24\u5176\u662f\u5728\u897f\u85cf\u7684\u51b0\u5cf0\uff0c\u8981\u6f5c\u4f0f\u7740\u65e0\u6570\u51b0\u5d29\u548c\u96ea\u5d29\u7684\u201c\u69fd\u5370\u201d\uff0c\u82e5\u7136\u9047 \u5230\u5f3a\u98ce\u6216\u5730\u9707\uff0c\u5343\u767e\u5428\u91cd\u7684\u51b0\u5ca9\u548c\u96ea\u5757\u4e5f\u4f1a\u50cf\u706b\u5c71\u7206\u53d1\u4e00\u6837\uff0c\u55b7\u6cfb\u4e0b\u6765\uff01\u4efb\u662f\u5929\u5927\u795e\u901a\u7684\u82f1 \u96c4\u597d\u6c49\uff0c\u4e5f\u4f1a\u88ab\u96ea\u6d3b\u57cb\uff0c\u8fd9\u51e0\u4e2a\u6b66\u58eb\u867d\u7136\u6ca1\u6709\u80fd\u529b\u9020\u6210\u4e00\u5439\u5730\u9707\uff0c\u4f46\u5927\u77f3\u6e90\u6e90\u6eda\u4e0b\uff0c\u9707\u52a8\u51b0 \u5c42\u79ef\u96ea\uff0c\u65f6\u95f4\u4e00\u957f\uff0c\u6ce2\u53ca\u7684\u8303\u56f4\u8d8a\u6765\u8d8a\u5e7f\uff0c\u90a3\u4e5f\u5c31\u6781\u6709\u53ef\u80fd\u5f15\u8d77\u4e00\u6b21\u5de8\u5927\u7684\u96ea\u5d29\u3002\u5728\u8fd9\u6837\u60c5 \u5f62\u4e4b\u4e0b\uff0c\u6842\u534e\u751f\u5176\u52bf\u4e0d\u80fd\u5728\u77f3\u5934\u96ea\u5757\u98de\u821e\u4e4b\u4e0b\u51b2\u4e0a\u53bb\u548c\u4ed6\u4eec\u62da\u547d\uff0c\u53ea\u597d\u65bd\u5c55\u817e\u632a\u95ea\u5c55\u7684\u4e0a\u4e58 \u8f7b\u52ab\u5728\u6ee1\u5c71\u98de\u6eda\u7684\u96ea\u5757\u4e4b\u4e2d\u95ea\u907f\uff0c\u4e00\u65b9\u9762\u53c8\u8981\u7167\u987e\u9ea6\u58eb\u8fe6\u5357\uff0c\u9976\u662f\u4ed6\u6b66\u529f\u5353\u7edd\uff0c\u4e5f\u81ea\u624b\u5fd9\u811a \u4e71\uff0c\u800c\u4e14\u96ea\u5757\u8d8a\u843d\u8d8a\u591a\uff0c\u8d8a\u6eda\u8d8a\u5927\uff0c\u4ed6\u4eec\u7684\u5904\u5883\u4e5f\u8d8a\u6765\u8d8a\u60ca\u9669\u4e86\u65e5\u773c\u89c1\u51b0\u96ea\u77f3\u5934\u6eda\u4e4b\u4e0d\u5df2\uff0c \u9707\u5f97\u5c71\u8c37\u8f70\u9e23\uff0c\u51b0\u5cf0\u4e5f\u597d\u50cf\u8981\u9707\u6296\u8d77\u6765\u4e86\uff0c\u5ffd\u7136\u95f4\u542c\u5f97\u4e00\u7247\u6781\u67d4\u548c\u7684\u7b1b\u58f0\u4ece\u98ce\u4e2d\u8fdc\u8fdc\u4f20\u6765\uff0c \u90a3\u95f7\u96f7\u4e5f\u4f3c\u7684\u96ea\u5757\u8f70\u9e23\uff0c\u7adf\u662f\u538b\u5b83\u4e0d\u4f4f\uff01 \u3000\u3000\u6842\u534e\u751f\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u4e0d\u4f46\u60ca\u5947\u4e8e\u5439\u7b1b\u8005\u7684\u6df1\u539a\u5185\u529f\uff0c\u800c\u4e14\u60ca\u5947\u4e8e\u6240\u5439\u7684\u66f2\u8c03\u3002\u90a3\u7b1b \u58f0\u67d4\u548c\u60a6\u8033\uff0c\u597d\u8f6c\u6781\u4e86\uff0c\u7aef\u7684\u6709\u5982\u201c\u95f4\u5173\u83ba\u8bed\u82b1\u5e95\u6ed1\uff0c\u5e7d\u54bd\u6d41\u6cc9\u6c34\u4e0b\u6ee9\uff01\u201d\u5439\u7684\u7adf\u662f\u6c5f\u5357\u66f2 \u8c03\u3002\u8fd9\u51e0\u5e74\u6765\u6842\u534e\u751f\u90fd\u5728\u8fb9\u7586\u4e4b\u5730\u5954\u9a70\uff0c\u6240\u89c1\u6240\u95fb\uff0c\u65e0\u975e\u662f\u8349\u539f\u5927\u6f20\uff0c\u6714\u98ce\u80e1\u7b4b\uff0c\u90a3\u91cc\u542c\u8fc7 \u8fd9\u6837\u60a6\u8033\u7684\u7389\u7b1b\u98ce\u58f0\uff01 \u3000\u3000\u8fc7\u4e86\u7247\u523b\uff0c\u90a3\u7b1b\u58f0\u4e00\u53d8\uff0c\u4ece\u6c5f\u5357\u60c5\u97f5\u4e00\u53d8\u800c\u4e3a\u5e7d\u71d5\u4e4b\u58f0\uff0c\u604d\u5982\u201c\u94f6\u74f6\u4e4d\u7834\u6c34\u6d46\u88c2\uff0c\u94c1\u9a91 \u7a81\u51fa\u5200\u67aa\u9e23\uff01\u201d\u6168\u5f53\u4ee5\u6177\uff0c\u4eca\u4eba\u8840\u8109\u6124\u5f20\uff0c\u7cbe\u795e\u52c3\u632f\uff0c\u6842\u534e\u751f\u6108\u542c\u6108\u5947\uff1a\u5728\u8fd9\u51b0\u5c71\u8352\u8c37\u4e4b \u95f4\uff0c\u90a3\u91cc\u6765\u7684\u5999\u89e3\u97f3\u5f8b\u7684\u4e2d\u5ddd\u5973\u5b50\uff1f \u3000\u3000\u7136\u800c\u8fd8\u6709\u66f4\u5947\u602a\u7684\u4e8b\u60c5\u6309\u7740\u53d1\u751f\uff0c\u90a3\u7b1b\u58f0\u4e00\u53d1\uff0c\u4ece\u5c71\u4e0a\u6eda\u4e0b\u7684\u77f3\u5934\u96ea\u5757\u5c31\u6e10\u6e10\u5c11\u4e86\uff0c\u5f85 \u5230\u5439\u4e86\u4e24\u5173\uff0c\u5ffd\u542c\u5f97\u5c71\u4e0a\u7684\u6b66\u58eb\u53d1\u51fa\u60ca\u53eb\u4e4b\u58f0\uff0c\u970e\u7136\u95f4\u9759\u4e86\u4e0b\u6765\uff0c\u518d\u6ca1\u6709\u4e00\u5757\u77f3\u5934\u5411\u4e0b\u6eda \u843d\uff01\u518d\u8fc7\u7247\u523b\uff0c\u53ea\u542c\u5f97\u4ece\u201c\u9b54\u9b3c\u57ce\u201d\u4e2d\u4f20\u6765\u4e86\u201c\u5f53\u5f53\u201d\u7684\u949f\u58f0\uff0c\u9ea6\u58eb\u8fe6\u5357\u53eb\u9053\uff1a\u201c\u8fd9\u662f\u4ed6\u4eec \u53ec\u4eba\u56de\u53bb\u7684\u8b66\u949f\u3002\u201d\u679c\u7136\u89c1\u90a3\u51e0\u4e2a\u6b66\u58eb\u8f6c\u8eab\u98de\u8dd1\uff0c\u800c\u4e14\u5c71\u5934\u4e0a\u7684\u54e8\u58f0\u6b64\u8d77\u5f7c\u843d\uff0c\u4e00\u542c\u5c31\u77e5\u9053 \u662f\u4ed6\u4eec\u62db\u547c\u540c\u4f34\u56de\u53bb\u7684\u8baf\u53f7\u3002 \u3000\u3000\u8fc7\u4e86\u7247\u523b\uff0c\u4eba\u6563\u98ce\u505c\uff0c\u5c71\u8c37\u91cc\u53c8\u590d\u5f52\u4e8e\u9759\u5bc2\u3002\u9ea6\u58eb\u8fe6\u5357\u5583\u5583\u8bf4\u9053\uff1a\u201c\u8fd9\u771f\u5947\u602a\u6781\u4e86\uff0c\u4ed6 \u4eec\u4e3a\u4ec0\u4e48\u5bb3\u6015\u8fd9\u4e2a\u7b1b\u58f0\uff1f\u201d\u6b47\u4e86\u4e00\u6b47\uff0c\u6df1\u6df1\u5438\u4e86\u53e3\u6c14\uff0c\u5f80\u4e0b\u7eed\u9053\uff1a\u201c\u6211\u5728\u8def\u4e0a\u4e5f\u66fe\u542c\u8fc7\u4e00\u6b21 \u8fd9\u4e2a\u7b1b\u58f0\u3002\u90a3\u65f6\u6211\u5df2\u53d1\u73b0\u6709\u4e24\u4e2a\u88c5\u675f\u53e4\u602a\u7684\u50e7\u4eba\u8ddf\u8e2a\u6211\uff0c\u6211\u6b63\u60f3\u63ed\u7834\u4ed6\u4eec\uff0c\u540c\u4ed6\u4eec\u9080\u6597\uff0c\u8349 \u539f\u4e0a\u5ffd\u7136\u54cd\u8d77\u7b1b\u58f0\uff0c\u4e0d\u8fc7\u6ca1\u6709\u4eca\u5929\u5439\u5f97\u8fd9\u4e48\u957f\u4e45\uff0c\u90a3\u4e24\u4e2a\u50e7\u4eba\u4e00\u542c\u5230\u7b1b\u58f0\u5c31\u8d76\u5fd9\u9003\u8d70\u4e86\u3002\u4f60 \u521a\u624d\u770b\u5230\u6ca1\u6709\uff1f\u7b1b\u58f0\u4e00\u8d77\uff0c\u5c71\u4e0a\u7684\u90a3\u4e9b\u6b66\u58eb\u4e5f\u5c31\u4e0d\u6562\u518d\u6eda\u77f3\u5934\u4e86\u5462\uff01\u201d\u6842\u534e\u751f\u6e38\u4fa0\u56db\u65b9\uff0c\u6240 \u89c1\u6240\u95fb\u7684\u5947\u4eba\u5f02\u4e8b\u4e0d\u77e5\u591a\u5c11\uff0c\u5374\u662f\u4ece\u65e0\u4e00\u4ef6\u6709\u4eca\u665a\u8fd9\u6837\u7684\u79bb\u5947\u53e4\u602a\uff0c\u5fc3\u4e2d\u4e5f\u662f\u767e\u601d\u4e0d\u5f97\u5176 \u89e3\u3002\u60f3\u4e86\u4e00\u60f3\uff0c\u8bf4\u9053\uff1a\u201c\u4ed6\u4eec\u65e2\u7136\u5bb3\u6015\u8fd9\u4e2a\u7b1b\u58f0\uff0c\u9b54\u9b3c\u57ce\u4e2d\u4e5f\u54cd\u8d77\u4e86\u8b66\u949f\uff0c\u4f60\u6b63\u597d\u8d81\u8fd9\u65f6\u673a \u9003\u4e0b\u5c71\u53bb\u3002\u201d\u9ea6\u58eb\u8fe6\u5357\u9053\uff1a\u201c\u4f60\u5462\uff1f\u201d\u6842\u534e\u751f\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u8fd8\u60f3\u518d\u63a2\u4e00\u63a2\u9b54\u9b3c\u57ce\uff0c\u4e5f \u5e0c\u671b\u80fd\u6709\u673a\u7f18\u89c1\u89c1\u8fd9\u4f4d\u5439\u7b1b\u7684\u4eba\u7269\uff01\u201d\u9ea6\u58eb\u8fe6\u5357\u5411\u6842\u534e\u751f\u90d1\u91cd\u9053\u8c22\uff0c\u5e76\u5c06\u8c12\u89c1\u8fbe\u8d56\u6d3b\u4f5b\u8f6c\u8fbe \u767d\u6559\u6cd5\u738b\u5fc3\u610f\u7684\u4e8b\u60c5\u518d\u4e00\u6b21\u62dc\u6258\uff0c\u7136\u540e\u9053\u522b\u4e0b\u5c71\u3002 \u3000\u3000\u6842\u534e\u751f\u65bd\u5c55\u8f7b\u529f\uff0c\u518d\u8d76\u56de\u201c\u5723\u5e99\u201d\u3002\u4f46\u89c1\u5e99\u95e8\u7d27\u95ed\uff0c\u5b88\u536b\u7684\u6b66\u58eb\u4e5f\u4e0d\u89c1\u4e86\u3002\u6842\u534e\u751f\u8df3\u4e0a \u767d\u5854\u7684\u7b2c\u4e00\u5c42\uff0c\u4ecd\u7136\u7528\u521a\u624d\u7684\u529e\u6cd5\uff0c\u9690\u8eab\u5728\u6bbf\u89d2\u98de\u6a90\u4e4b\u5185\uff0c\u5077\u5077\u7684\u4ece\u5e99\u9876\u4e00\u7247\u900f\u5149\u7684\u7409\u7483\u74e6 \u5411\u4e0b\u7aa5\u63a2\u3002 \u3000\u3000\u4f46\u89c1\u90a3\u738b\u5b50\u53cc\u7709\u7d27\u78e8\uff0c\u6b63\u5728\u548c\u4e00\u73ed\u50e7\u4fa3\u6b66\u58eb\u8bf4\u8bdd\uff0c\u770b\u6837\u5b50\u662f\u5728\u5546\u8bae\u751a\u4e48\u4e8b\u60c5\uff0c\u4ed6\u4eec\u7528\u5c3c \u6cca\u5c14\u8bdd\u4ea4\u8c08\uff0c\u6842\u534e\u751f\u4e00\u53e5\u4e5f\u542c\u4e0d\u61c2\uff0c\u4f46\u4ece\u4ed6\u4eec\u90a3\u7d27\u5f20\u7684\u795e\u8272\u770b\u6765\uff0c\u81ea\u662f\u548c\u4eca\u665a\u7684\u7b1b\u58f0\u6709\u5173 \u4e86\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u5ffd\u542c\u5f97\u6709\u4e09\u58f0\u54e8\u58f0\uff0c\u4e00\u957f\u4e8c\u77ed\uff0c\u6309\u7740\u662f\u4e09\u4e0b\u94c1\u73af\u78b0\u95e8\u7684\u58f0\u97f3\uff0c\u4e5f\u662f\u4e00\u957f\u4e8c \u77ed\uff0c\u738b\u5b50\u53cc\u7709\u4e00\u5c55\uff0c\u7528\u4f4e\u6c89\u6025\u4fc3\u7684\u58f0\u97f3\u8bf4\u4e86\u4e24\u4e2a\u5b57\uff0c\u5e99\u95e8\u5019\u7684\u6253\u5f00\uff0c\u53ea\u89c1\u4e00\u4e2a\u9ed1\u591c\u6b66\u58eb\u5c06\u4e00 \u4e2a\u8eab\u6750\u9ad8\u5927\u7684\u7ea2\u8863\u5587\u561b\u5e26\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u8fd9\u7ea2\u8863\u5587\u561b\u770b\u6765\u5df2\u6709\u516d\u5341\u591a\u5c81\uff0c\u989d\u89d2\u4e5f\u8d77\u4e86\u76b1\u7eb9\uff0c\u4f46\u6ee1\u9762\u7ea2\u5149\uff0c\u7cbe\u795e\u5065\u9532\uff0c\u4e00\u8fdb\u6765\u5c31\u54c8 \u54c8\u5927\u7b11\uff0c\u7528\u85cf\u8bdd\u8bf4\u9053\uff1a\u201c\u738b\u5b50\u5ba0\u53ec\uff0c\u672c\u5e94\u65e9\u65e5\u524d\u6765\u62dc\u8c12\uff0c\u600e\u5948\u6709\u4e9b\u4e8b\u60c5\uff0c\u662f\u4ee5\u6765\u8fdf\uff0c\u8fd8\u671b\u6055 \u7f6a\u3002\u201d\u90a3\u738b\u5b50\u4eb2\u81ea\u51fa\u8fce\uff0c\u6267\u793c\u751a\u606d\uff0c\u7528\u85cf\u8bdd\u5148\u884c\u95ee\u597d\uff0c\u7136\u540e\u8bf4\u9053\uff1a\u201c\u5f97\u85cf\u7075\u4e0a\u4eba\u60e0\u4e34\uff0c\u5b9e\u4e43 \u655e\u56fd\u4e4b\u798f\uff0c\u4e0d\u77e5\u4e0a\u4eba\u53ef\u80af\u5c48\u9a7e\uff0c\u5230\u655d\u56fd\u5c48\u5c31\u7b2c\u4e00\u56fd\u5e08\u4e48\uff1f\u201d \u3000\u3000\u6842\u534e\u751f\u5403\u4e86\u4e00\u60ca\uff0c\u4ed6\u7236\u4eb2\u6842\u4ef2\u660e\u751f\u524d\uff0c\u8db3\u8ff9\u904d\u5386\u8499\u85cf\uff0c\u66fe\u4e0e\u4ed6\u8bf4\u8fc7\uff0c\u897f\u85cf\u7ea2\u6559\u6709\u4e00\u4e2a\u85cf \u7075\u4e0a\u4eba\uff0c\u7cbe\u901a\u5bc6\u5b97\u7684\u5947\u5999\u6b66\u529f\uff0c\u5185\u5916\u529f\u592b\u4ff1\u81fb\u7edd\u9876\uff0c\u5929\u5c71\u5973\u4fa0\uff0c\u4e5f\u662f\u540d\u5217\u5929\u5c71\u4e0a\u5251\u4e4b\u4e00\u7684\u6613 \u5170\u73e0\u5728\u6f2b\u6e38\u897f\u85cf\u65f6\uff0c\u66fe\u548c\u4ed6\u8f83\u91cf\u6b66\u529f\uff0c\u4e5f\u8981\u6253\u4e86\u4e00\u767e\u62db\u5f00\u5916\u624d\u5c06\u4ed6\u6253\u8d25\u3002\u5c3c\u6fc0\u5c14\u738b\u5b50\u4eca\u665a\u63a5 \u8fde\u591f\u4e86\u897f\u85cf\u4e09\u4e2a\u85e9\u738b\u7684\u4f7f\u8005\uff0c\u9752\u6d77\u767d\u6559\u6cd5\u738b\u7684\u4f7f\u8005\uff0c\u53c8\u7ea6\u4e86\u8fd9\u4f4d\u7ea2\u6559\u5587\u561b\u4e2d\u7684\u7b2c\u4e00\u9ad8\u624b\u524d \u6765\uff0c\u56fe\u8c0b\u897f\u85cf\u7684\u91ce\u5fc3\u786e\u662f\u4e0d\u5bb9\u5ffd\u89c6\u3002 \u3000\u3000\u85cf\u7075\u4e0a\u4eba\u5408\u4ec0\u8bf4\u9053\uff1a\u201c\u73b0\u5728\u9ec4\u6559\u638c\u6743\uff0c\u655d\u6559\u5728\u897f\u85cf\u867d\u7136\u4e0d\u5f97\u5176\u9053\u800c\u884c\uff0c\u4f46\u7a76\u4e0d\u4fbf\u8fdc\u79bb\u4e61 \u571f\u3002\u201d\u5c3c\u6cca\u5c14\u738b\u5b50\u8bf4\u9053\uff1a\u201c\u655d\u56fd\u8fd8\u6ca1\u6709\u9009\u5b9a\u767d\u6559\uff0c\u4e0a\u4eba\u82e5\u80af\u5c48\u4efb\u56fd\u5e08\uff0c\u8d35\u6559\u6b63\u53ef\u5728\u655d\u56fd\u63a8 \u884c\uff0c\u6709\u4f55\u4e0d\u53ef\uff1f\u800c\u4e14\u5c06\u6765\u4e5f\u5927\u6709\u673a\u4f1a\u91cd\u56de\u897f\u85cf\u3002\u201d\u85cf\u7075\u4e0a\u4eba\u60f3\u4e86\u4e00\u9635\uff0c\u5ffd\u5730\u53cc\u76ee\u73af\u626b\uff0c\u5c06\u4e24 \u65c1\u7684\u50e7\u4fa3\u6b66\u58eb\u90fd\u6253\u91cf\u4e86\u4e00\u4e0b\uff0c\u8138\u4e0a\u9732\u51fa\u4e86\u63a9\u85cf\u4e0d\u4f4f\u7684\u5931\u671b\u795e\u60c5\u3002 \u3000\u3000\u6842\u534e\u751f\u65b9\u81ea\u5947\u602a\uff0c\u53ea\u542c\u5f97\u90a3\u85cf\u7075\u4e0a\u4eba\u8bf4\u9053\uff1a\u201c\u8fd9\u91cc\u9762\u6ca1\u6709\u63d0\u6469\u8fbe\u591a\uff1f\u201d\u5c3c\u6cca\u5c14\u738b\u5b50\u9053\uff1a \u201c\u54b1\u4eec\u5c0f\u56fd\u7559\u4e0d\u4f4f\u4ed6\uff0c\u4ed6\u5230\u9ea6\u52a0\u53bb\u4e86\u3002\u201d\u85cf\u7075\u4e0a\u4eba\u9053\uff1a\u201c\u5370\u5ea6\u7684\u9f99\u53f6\u5927\u5e08\u4e5f\u6ca1\u6709\u6765\u5417\uff1f\u201d\u5c3c \u6cca\u5c14\u738b\u5b50\u9053\uff1a\u201c\u9f99\u53f6\u5927\u5e08\u524d\u5e74\u5230\u8fc7\u655d\u56fd\u7684\u4eac\u57ce\u52a0\u5fb7\u6ee1\u90fd\uff0c\u4e5f\u8bb8\u660e\u5e74\u8fd8\u4f1a\u518d\u6765\u3002\u201d\u85cf\u7075\u4e0a\u4eba \u9053\uff1a\u201c\u6211\u867d\u7136\u50fb\u5904\u7a77\u8352\uff0c\u5b64\u964b\u5be1\u95fb\uff0c\u4e5f\u66fe\u542c\u8bf4\u63d0\u6469\u8fbe\u591a\u662f\u963f\u523a\u4f2f\u8bf8\u56fd\u4e2d\u7684\u7b2c\u4e00\u9ad8\u624b\uff0c\u9f99\u53f6\u5927 \u5e08\u6211\u5728\u5341\u591a\u5e74\u524d\u5230\u5fb7\u91cc\u8fdb\u9999\u65f6\u66fe\u6709\u7f18\u62dc\u8c12\u8fc7\u4ed6\uff0c\u4ed6\u7684\u6b66\u529f\u786e\u662f\u53ef\u4ee5\u79f0\u5f97\u4e0a\u9707\u4e16\u9a87\u4fd7\u3002\u60f3\u6765\u8fd9 \u4e24\u4eba\u5f53\u53ef\u4e0e\u5929\u5c71\u7684\u6613\u8001\u4e5e\u5a46\u4e00\u8f83\u77ed\u957f\u3002\u201d\u6842\u534e\u751f\u6697\u81ea\u597d\u7b11\uff0c\u6613\u5170\u73e0\u5df2\u6b7b\u53bb\u4e03\u516b\u5e74\u4e86\uff0c\u8fd9\u85cf\u7075 \u4e0a\u4eba\u5374\u672a\u77e5\u9053\uff0c\u5140\u662f\u5ff5\u5ff5\u4e0d\u5fd8\u90a3\u4e00\u5251\u4e4b\u4ec7\u3002 \u3000\u3000\u90a3\u7ea2\u8863\u756a\u50e7\u5728\u5c3c\u6cca\u5c14\u7684\u50e7\u4fa3\u6b66\u58eb\u7fa4\u4e2d\u8eab\u4efd\u6700\u5c0a\uff0c\u542c\u5f97\u85cf\u7075\u4e0a\u4eba\u5982\u6b64\u8bf4\u6cd5\uff0c\u5206\u660e\u662f\u8f7b\u89c6\u4ed6 \u4eec\u7684\u6b66\u529f\uff0c\u9887\u611f\u5c34\u5c2c\u3002\u5c3c\u6cca\u5c14\u738b\u5b50\u9053\uff1a\u201c\u4e0a\u4eba\u60f3\u89c1\u4ed6\u4eec\u4e8c\u4eba\u4e5f\u5e76\u975e\u96be\u4e8b\uff0c\u660e\u5e74\u4f5b\u7956\u8bde\u8fb0\u6211\u5728 \u52a0\u5fb7\u6ee1\u90fd\u5f00\u65e0\u906e\u5927\u4f1a\uff0c\u4ed6\u4eec\u4e24\u4f4d\u5fc5\u5b9a\u4f1a\u6765\u3002\u201d\u201c\u65e0\u906e\u201d\u4e8c\u5b57\u5728\u68b5\u6587\u4e2d\u7684\u89e3\u91ca\u662f\u201c\u5bbd\u5bb9\u65e0\u963b\u201d \u4e4b\u610f\uff0c\u5373\u5723\u8d24\u9053\u4fd7\u8d35\u8d31\u4e0a\u4e0b\u4e00\u5f8b\u53ef\u4ee5\u5e73\u7b49\u53c2\u4e0e\uff0c\u8fd9\u7b49\u65e0\u906e\u5927\u4f1a\u5728\u4f5b\u95e8\u4e2d\u662f\u4e00\u4ef6\u65f7\u4e16\u76db\u4e8b\uff0c\u5728 \u4e2d\u56fd\u4f5b\u6559\u53f2\u4e0a\u4e5f\u67e2\u6709\u6881\u6b66\u5e1d\u5728\u540c\u6cf0\u5bfa\u5f00\u8fc7\u56db\u90e8\u65e0\u906e\u5927\u4f1a\uff0c\u89c1\u4e8e\u300a\u5357\u53f2\u300b\u3002\u6842\u534e\u751f\u542c\u4e86\uff0c\u60bb\u7136 \u5fc3\u52a8\uff0c\u6cb9\u7136\u800c\u8d77\u4e86\u53bb\u5c3c\u6cca\u5c14\u4e00\u89c2\u6cd5\u4f1a\u4e4b\u5fc3\u3002 \u3000\u3000\u85cf\u7075\u4e0a\u4eba\u53f9\u4e86\u53e3\u6c14\u8bf4\u9053\uff1a\u201c\u6211\u90a3\u91cc\u7b49\u5f97\u5230\u660e\u5e74\uff1f\u82e5\u662f\u4ed6\u4eec\u4eca\u65e5\u5728\u6b64\uff0c\u6211\u5c31\u53ef\u4ee5\u501f\u91cd\u4ed6\u4eec \u4e4b\u529b\uff0c\u5171\u53d6\u4e00\u4ef6\u7a00\u4e16\u4e4b\u73cd\u3002\u201d\u5c3c\u6cca\u5c14\u738b\u5b50\u9053\uff1a\u201c\u4ec0\u4e48\u7a00\u4e16\u4e4b\u73cd\uff1f\u8981\u5230\u90a3\u513f\u53bb\u53d6\uff1f\u201d\u85cf\u7075\u4e0a\u4eba \u7b11\u9053\uff1a\u201c\u5c31\u5728\u6b64\u5c71\u4e4b\u4e2d\uff01\u6211\u8d39\u4e86\u51e0\u5341\u5e74\u5fc3\u8840\uff0c\u624d\u77e5\u9053\u4e00\u70b9\u7aef\u502a\uff0c\u5230\u5e95\u662f\u5426\u5982\u6211\u6240\u6599\uff0c\u76ee\u524d\u8fd8 \u4e0d\u6562\u8bf4\u3002\u201d\u5c3c\u6cca\u5c14\u89c1\u4ed6\u4e0d\u80af\u900f\u9732\u8fd9\u4ef6\u7a00\u4e16\u4e4b\u73cd\u662f\u4ec0\u4e48\u4e1c\u897f\uff0c\u751a\u662f\u7eb3\u95f7\uff0c\u95ee\u9053\uff1a\u201c\u4e0a\u4eba\u65e2\u7136\u7b49 \u4e86\u51e0\u5341\u5e74\uff0c\u4e5f\u4e0d\u5fc5\u4e89\u6b64\u4e00\u523b\u3002\u4e0d\u5982\u5148\u5230\u655d\u56fd\uff0c\u5f85\u9080\u8bf7\u4e86\u63d0\u6469\u8fbe\u591a\u3001\u9f99\u53f6\u4e0a\u4eba\u7136\u540e\u518d\u6765\u5427\u3002\u201d \u3000\u3000\u85cf\u7075\u4e0a\u4eba\u6447\u5934\u9053\uff1a\u201c\u4e0d\u7136\uff0c\u4e0d\u7136\u3002\u6211\u5b9e\u5bf9\u738b\u5b50\u8bf4\u5427\uff0c\u6211\u4eca\u6b21\u4e0a\u5c71\u4e00\u6765\u56fa\u662f\u738b\u5b50\u4e4b\u9080\uff0c\u4e8c \u6765\u4e5f\u662f\u4e3a\u4e86\u8fd9\u4ef6\u7a00\u4e16\u7684\u5b9d\u7269\uff01\u6211\u4e00\u5230\u6b64\u5c71\u4fbf\u53d1\u73b0\u6709\u4e9b\u4e0d\u5bf9\uff0c\u4f3c\u4e4e\u662f\u6709\u4e86\u6b66\u529f\u6781\u9ad8\u660e\u7684\u5f02\u4eba\u4e5f \u5230\u4e86\u6b64\u5c71\uff0c\u53ea\u6015\u4ed6\u4eec\u4e5f\u662f\u4e3a\u4e86\u8fd9\u4ef6\u5b9d\u7269\u800c\u6765\u7684\u3002\u201d\u5c3c\u6cca\u5c14\u738b\u5b50\u6025\u5fd9\u95ee\u9053\uff1a\u201c\u4e0a\u4eba\u53d1\u73b0\u4e86\u4ec0\u4e48 \u4e0d\u5bf9\uff1f\u201d\u85cf\u7075\u4e0a\u4eba\u9053\uff1a\u201c\u4f60\u4eec\u521a\u624d\u53ef\u6709\u542c\u5230\u7b1b\u58f0\u4e48\uff1f\u201d\u738b\u5b50\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u85cf\u7075\u4e0a\u4eba\u9053\uff1a \u201c\u5439\u7b1b\u4e4b\u4eba\u5c31\u662f\u5185\u529f\u751a\u6709\u706b\u5019\u7684\u4eba\uff0c\u738b\u5b50\u5e10\u4e0b\uff0c\u4e0d\u4e4f\u9ad8\u660e\u4e4b\u58eb\uff0c\u96be\u9053\u542c\u4e0d\u51fa\u6765\u4e48\uff1f\u201d\u5c3c\u6cca\u5c14 \u738b\u5b50\u8d77\u521d\u4e00\u60ca\uff0c\u7ee7\u800c\u7b11\u9053\uff1a\u201c\u8fd9\u4eba\u5b9a\u4e0d\u662f\u4e3a\u4e86\u5b9d\u7269\u800c\u6765\uff0c\u6211\u5230\u62c5\u5fc3\u5979\u662f\u6211\u7684\u5bf9\u5934\uff01\u201d\u85cf\u7075\u4e0a \u4eba\u9053\uff1a\u201c\u4e0d\u7ba1\u5982\u4f55\uff0c\u8fd9\u5b9d\u7269\u6211\u603b\u4e0d\u80fd\u8ba9\u522b\u4eba\u5148\u53d1\u73b0\u4e86\u3002\u6211\u7eb5\u662f\u5192\u4e86\u5927\u9669\uff0c\u4eca\u665a\u4e5f\u5b9a\u8981\u5c06\u5b83\u53d6 \u5f97\u3002\u738b\u5b50\uff0c\u54b1\u4eec\u4e0d\u5982\u60f3\u4e2a\u4e24\u5168\u5176\u7f8e\u4e4b\u6cd5\uff0c\u4f60\u5148\u52a9\u6211\u53d6\u90a3\u5b9d\u7269\uff0c\u6211\u518d\u52a9\u4f60\u9664\u53bb\u90a3\u5389\u5bb3\u7684\u5bf9\u5934\u3002 \u4f60\u4e0d\u8981\u62c5\u5fc3\uff0c\u8fd9\u4ef6\u5b9d\u7269\u4e00\u5230\u624b\u4e2d\uff0c\u6211\u5c31\u53ef\u4ee5\u65e0\u654c\u4e8e\u5929\u4e0b\uff01\u201d \u3000\u3000\u5c3c\u6cca\u5c14\u738b\u5b50\u534a\u4fe1\u534a\u7591\uff0c\u95ee\u9053\uff1a\u201c\u5982\u4f55\u76f8\u52a9\uff1f\u201d\u85cf\u7075\u4e0a\u4eba\u9053\uff1a\u201c\u4f60\u9009\u4e00\u961f\u6b66\u58eb\u7ed9\u6211\uff0c\u7531\u6211 \u6307\u6325\u3002\u201d\u8bf4\u8bdd\u4e4b\u65f6\uff0c\u53c8\u6253\u91cf\u4e86\u4e24\u65c1\u7684\u50e7\u4fa3\u6b66\u58eb\u4e00\u904d\uff0c\u773c\u4e2d\u5145\u6ee1\u60cb\u60dc\u7684\u795e\u60c5\uff0c\u4ece\u795e\u60c5\u4e2d\u4e0d\u96be\u731c \u5230\u4ed6\u7684\u5fc3\u4e8b\uff0c\u90a3\u662f\u60cb\u60dc\u6b64\u4e2d\u6ca1\u6709\u9ad8\u624b\uff0c\u4f46\u4e3a\u4e86\u6025\u4e8e\u8981\u53d6\u90a3\u4ef6\u7a00\u4e16\u4e4b\u73cd\uff0c\u4e0d\u5f97\u5df2\u800c\u601d\u5176\u6b21\uff0c\u53ea \u597d\u53bb\u5192\u4e00\u5192\u9669\u4e86\u3002 \u3000\u3000\u5c3c\u6cca\u5c14\u738b\u5b50\u7709\u5934\u7565\u76b1\uff0c\u4e0e\u90a3\u7ea2\u8863\u756a\u50e7\u5546\u91cf\u4e86\u4e00\u9635\uff0c\u9009\u51fa\u4e86\u516b\u540d\u5e26\u5200\u6b66\u58eb\u3002 \u3000\u3000\u5c3c\u6cca\u5c14\u7684\u6b66\u58eb\u4eec\u7d20\u4ee5\u52c7\u6b66\u8457\u79f0\uff0c\u4ed6\u4eec\u4eba\u4eba\u90fd\u6709\u4e00\u628a\u5229\u5203\uff0c\u53eb\u505a\u201c\u6208\u514b\u5229\u5200\u201d\uff0c\u4e43\u5982\u65b0 \u6708\uff0c\u5f2f\u6210\u5f27\u5f62\uff0c\u4e0d\u4f46\u7f8e\u89c2\uff0c\u800c\u4e14\u950b\u5229\u4e4b\u6781\uff0c\u8db3\u4e0e\u7f05\u5200\u77ee\u5200\u6bd4\u7f8e\u3002\u85cf\u7075\u4e0a\u4eba\u7709\u7aef\u7a0d\u5c55\uff0c\u81ea\u8a00\u81ea \u8bed\u9053\uff1a\u201c\u597d\u574f\u4e14\u8bd5\u5b83\u4e00\u8bd5\u3002\u201d\u5e26\u4e86\u8fd9\u516b\u540d\u6b66\u58eb\uff0c\u884c\u51fa\u5e99\u95e8\u3002 \u3000\u3000\u6842\u534e\u751f\u5fc3\u4e2d\u4e03\u4e0a\u516b\u843d\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u4ed6\u627e\u7684\u662f\u4ec0\u4e48\u5b9d\u8d1d\uff0c\u5f97\u4e4b\u53ef\u4ee5\u5929\u4e0b\u65e0\u654c\uff1f\u201d\u597d\u5947\u4e4b \u5fc3\u5927\u8d77\uff0c\u9887\u60f3\u6697\u6697\u8ddf\u8e2a\u8fd9\u4e2a\u85cf\u7075\u4e0a\u4eba\uff0c\u770b\u4ed6\u7a76\u7adf\u5230\u90a3\u513f\u6398\u5b9d\u3002\u4f46\u8f6c\u5ff5\u4e00\u60f3\uff1a\u201c\u770b\u4eca\u665a\u8fd9\u4e2a\u60c5 \u5f62\uff0c\u90a3\u4f4d\u7684\u5439\u7b1b\u5f02\u4eba\u53ea\u6015\u5c31\u8981\u6765\u4e86\uff0c\u5982\u82e5\u5931\u4e4b\u4ea4\u81c2\uff0c\u90a3\u53ef\u662f\u7ec8\u751f\u9057\u61be\uff01\u201d\u76f8\u6bd4\u4e4b\u4e0b\uff0c\u65e0\u4ef7\u4e4b \u5b9d\u6613\u6c42\uff0c\u7edd\u4e16\u9ad8\u4eba\u96be\u9047\uff0c\u5fc3\u5ff5\u9042\u51b3\uff0c\u7ec8\u4e8e\u8fd8\u662f\u7559\u4e0b\u3002 \u3000\u3000\u85cf\u7075\u4e0a\u4eba\u53bb\u540e\uff0c\u738b\u5b50\u50e7\u4fa3\u6b66\u58eb\u4eec\u53c8\u5431\u5431\u55b3\u55b3\u7684\u8bb2\u56de\u5c3c\u6cca\u5c14\u8bdd\uff0c\u770b\u6765\u4f3c\u662f\u5546\u8bae\u4e00\u4ef6\u91cd\u5927\u7684 \u4e8b\u60c5\u3002\u6842\u534e\u751f\u542c\u4e0d\u61c2\u4ed6\u4eec\u7684\u8bed\u8a00\uff0c\u5fc3\u4e2d\u53d1\u95f7\u3002\u8fc7\u4e86\u597d\u4e00\u4f1a\u5b50\uff0c\u5ffd\u89c1\u5e99\u4e2d\u8bf8\u4eba\u5764\u8272\u7d27\u5f20\uff0c\u6842\u534e \u751f\u4e5f\u81ea\u5fc3\u8df3\u4e0d\u5df2\uff0c\u4f46\u542c\u5f97\u98ce\u8fed\u7b1b\u58f0\uff0c\u97f3\u7ec6\u800c\u6e05\uff0c\u5047\u82e5\u6e38\u4e1d\u67ad\u7a7a\uff0c\u82e5\u65ad\u82e5\u7eed\uff0c\u8fc7\u4e86\u7247\u523b\uff0c\u7b1b\u58f0 \u81ea\u8fdc\u800c\u8fd1\uff0c\u58f0\u97f3\u4e5f\u6e10\u6e10\u5639\u4eae\uff0c\u66f2\u8c03\u9ad8\u96c5\uff0c\u4ed9\u4e50\u98ce\u98d8\uff0c\u662f\u90a3\u6837\u7684\u7f8e\u5999\u67d4\u548c\uff0c\u4eca\u4eba\u4fd7\u5c18\u5c3d\u6da4\uff0c\u4e0e \u5e99\u4e2d\u7684\u6697\u85cf\u6740\u6c14\uff0c\u6070\u6070\u662f\u4e24\u79cd\u622a\u7136\u76f8\u53cd\u7684\u6c14\u6c1b\uff01 \u3000\u3000\u8f6c\u77ac\u95f4\u7b1b\u58f0\u5230\u4e86\u95e8\u524d\uff0c\u6761\u7136\u800c\u6b62\uff0c\u5916\u9762\u54cd\u8d77\u4e86\u94c1\u73af\u6263\u95e8\u7684\u58f0\u97f3\u3002 \u3000\u3000\u50e7\u4fa3\u6b66\u58eb\u4eec\u90fd\u895f\u4e0d\u4f5c\u58f0\uff0c\u6bcf\u4e2a\u4eba\u90fd\u628a\u773c\u775b\u671b\u7740\u738b\u5b50\uff0c\u5c3c\u6cca\u5c14\u738b\u5b50\u53d6\u51fa\u4e86\u4e00\u4e2a\u9762\u5177\uff0c\u7f69\u5728 \u9762\u4e0a\uff0c\u8ddf\u7740\u6bcf\u4e2a\u4eba\u90fd\u8fd9\u6837\u505a\uff0c\u9762\u5177\u662f\u76ae\u8349\u505a\u7684\uff0c\u7f69\u8fc7\u8033\u540e\uff0c\u53ea\u9732\u51fa\u4e00\u5bf9\u773c\u775b\u548c\u5634\u5507\u90e8\u4f4d\u7684\u4e00 \u6761\u88c2\u7f1d\uff0c\u6837\u5b50\u751a\u662f\u6ed1\u7a3d\u3002 \u3000\u3000\u6842\u534e\u751f\u5fc3\u4e2d\u4e00\u52a8\uff0c\u60f3\u9053\uff1a\u201c\u83ab\u975e\u8fd9\u4e2a\u4eba\u662f\u4ed6\u4eec\u8ba4\u8bc6\u7684\uff0c\u4ed6\u4eec\u6015\u88ab\u8ba4\u51fa\u4e86\u5e90\u5c71\u771f\u76f8\uff1f\u201d\u4f46 \u89c9\u8fd9\u91cc\u7684\u4e8b\u60c5\u8d8a\u6765\u8d8a\u8be1\u79d8\u4e86\uff01 \u3000\u3000\u6263\u95e8\u7684\u58f0\u97f3\u54cd\u5230\u4e86\u7b2c\u5341\u4e09\u4e0b\uff0c\u738b\u5b50\u628a\u624b\u4e00\u6325\uff0c\u94c1\u95e8\u9aa4\u7684\u6253\u5f00\uff0c\u8fd9\u970e\u90a3\u95f4\uff0c\u6842\u534e\u751f\u8fd0\u6c14\u4e5f \u900f\u4e0d\u8fc7\u6765\uff01 \u3000\u3000\u4f46\u89c1\u8fdb\u6765\u7684\u662f\u4e00\u4e2a\u767d\u8863\u5c11\u5973\uff0c\u8138\u5982\u65b0\u6708\uff0c\u79c0\u53d1\u5782\u80a9\uff0c\u4fee\u77ed\u5408\u5ea6\uff0c\u80a4\u8272\u5982\u6307\uff0c\u6d45\u663c\u53cc\u7709\uff0c \u773c\u73e0\u5fae\u78a7\uff0c\u70af\u5a1c\u521a\u5065\uff0c\u517c\u800c\u6709\u4e4b\uff01\u770b\u5979\u7684\u5f62\u8c8c\u4f53\u6001\uff0c\u4f3c\u4e4e\u662f\u4e2a\u5f02\u56fd\u5973\u90ce\uff0c\u6709\u51e0\u5206\u4f3c\u85cf\u4eba\uff0c\u4e5f \u6709\u51e0\u5206\u4f3c\u6c49\u5973\uff0c\u4f46\u6842\u534e\u751f\u4e0d\u8bba\u5728\u6c49\u4eba\u85cf\u4eba\u4e4b\u4e2d\uff0c\u90fd\u8fd8\u672a\u66fe\u89c1\u8fc7\u8fd9\u6837\u59ff\u5bb9\u7edd\u8272\u7684\u5973\u5b50\uff01 \u3000\u3000\u6842\u534e\u751f\u771f\u7684\u6709\u70b9\u4e0d\u6562\u76f8\u4fe1\uff0c\u8fd9\u6837\u4e00\u4f4d\u5f02\u56fd\u7f8e\u4eba\uff0c\u7adf\u7136\u80fd\u5439\u51fa\u4e2d\u56fd\u7684\u6c5f\u5357\u66f2\u8c03\uff01 \u3000\u3000\u66f4\u5947\u602a\u7684\u4e8b\u60c5\u8fd8\u5728\u540e\u5934\uff0c\u53ea\u89c1\u90a3\u767d\u8863\u5c11\u5973\u6a31\u5507\u5fae\u542f\uff0c\u83ba\u58f0\u575c\u575c\u7684\u8bf4\u4e86\u51e0\u53e5\u8bdd\uff0c\u6842\u534e\u751f\u867d \u7136\u4e00\u4e2a\u5b57\u4e5f\u542c\u4e0d\u61c2\uff0c\u4e5f\u89c9\u60a6\u8033\u975e\u5e38\uff0c\u5c31\u50cf\u5979\u6240\u5439\u7684\u7b1b\u58f0\u4e00\u6837\uff0c\u4eca\u4eba\u5fc3\u795e\u6b32\u9189\u3002 \u3000\u3000\u5e99\u4e2d\u8bf8\u4eba\u90fd\u895f\u4e0d\u4f5c\u58f0\uff0c\u5ffd\u7136\u95f4\uff0c\u90a3\u5c11\u5973\u5fae\u5fae\u4e00\u7b11\uff0c\u7adf\u7136\u7528\u6c49\u8bed\u8bf4\u9053\uff1a\u201c\u989d\u5c14\u90fd\u738b\u5b50\uff0c\u4f60 \u4e0d\u6562\u548c\u6211\u89c1\u9762\uff0c\u5927\u7ea6\u4e5f\u77e5\u9053\u4f60\u5728\u8fd9\u91cc\u505a\u7684\uff0c\u662f\u89c1\u4e0d\u5f97\u4eba\u7684\u4e8b\u5427\uff1f\u597d\uff0c\u6211\u4e3a\u4e86\u4fdd\u5b58\u4f60\u7684\u989c\u9762\uff0c \u4e0d\u5728\u4f17\u4e4b\u524d\u8d23\u4f60\uff0c\u4f60\u7acb\u5373\u7ed9\u6211\u56de\u56fd\uff0c\u4eca\u665a\u4e4b\u4e8b\uff0c\u6211\u4e5f\u4e0d\u5411\u4efb\u4f55\u4eba\u63d0\u8d77\uff01\u201d \u3000\u3000\u8fd9\u5c11\u5973\u7adf\u7136\u4f1a\u8bf4\u6c49\u8bed\uff0c\u5df2\u662f\u4e00\u5947\uff0c\u800c\u4e14\u8bf4\u7684\u8fd8\u662f\u5730\u9053\u7684\u5317\u4eac\u8bdd\uff0c\u867d\u7136\u4e0d\u5927\u6d41\u7545\uff0c\u4f46\u54ac\u97f3 \u5ba1\u5b57\uff0c\u751a\u662f\u51c6\u786e\uff01\u800c\u4e14\u542c\u4ed6\u8bed\u6c14\uff0c\u90a3\u5c3c\u6cca\u5c14\u738b\u5b50\u4e5f\u662f\u61c2\u5f97\u6c49\u8bed\u7684\uff01 \u3000\u3000\u6842\u534e\u751f\u8fd9\u4e2a\u7591\u56e2\u76f4\u5230\u4ed6\u540e\u6765\u5230\u4e86\u5c3c\u6cca\u5c14\u4e4b\u540e\u65b9\u624d\u6253\u7834\u3002\u539f\u6765\u5c3c\u6cca\u5c14\u81ea\u6709\u5386\u53f2\u4ee5\u6765\uff0c\u5373\u4e0e \u4e2d\u56fd\u53cb\u597d\u6765\u5f80\u3002\u8fdc\u5728\u4e2d\u56fd\u7684\u664b\u671d\uff0c\u6cd5\u663e\u548c\u5c1a\u5c31\u66fe\u8bbf\u95ee\u8fc7\u5c3c\u6cca\u5c14\uff0c\u4ee5\u540e\u5510\u4ee3\u9ad8\u50e7\u7384\u88c5\u4e5f\u66fe\u5230\u6b64 \u8bbf\u95ee\uff0c\u4e0d\u4e45\uff0c\u5510\u671d\u5c31\u548c\u5c3c\u6cca\u5c14\u4e92\u6d3e\u4f7f\u8282\u3002\u5143\u671d\u65f6\uff0c\u5c3c\u6cca\u5c14\u66f9\u6d3e\u5efa\u7b51\u3001\u5851\u9020\u827a\u5320\u7b49\u516b\u5341\u591a\u4eba\u5230 \u4e2d\u56fd\uff0c\u9996\u9886\u963f\u5c3c\u54e5\u8fd8\u5728\u5143\u671d\u4efb\u8fc7\u5149\u7984\u5927\u592b\u3001\u5927\u53f8\u5f92\u4e4b\u804c\uff0c\u6b64\u540e\u4e2d\u5c3c\u4e24\u56fd\u6765\u5f80\u4ecd\u7edc\u7ece\u4e0d\u7edd\u3002\u6545 \u6b64\u5728\u5c3c\u6cca\u5c14\u7684\u4e0a\u5c42\u675c\u4f1a\u4e4b\u4e2d\uff0c\u65e0\u4e0d\u4ee5\u4f1a\u5199\u6c49\u6587\uff0c\u4f1a\u8bb2\u6c49\u8bed\uff08\u4e3b\u8981\u5373\u662f\u5317\u4eac\u8bdd\uff09\u4e3a\u8363\uff0c\u5c24\u5176\u662f \u7687\u5ba4\u5b50\u5f1f\uff0c\u66f4\u662f\u81ea\u5c0f\u5c31\u6709\u901a\u6653\u6c49\u5b66\u7684\u9e3f\u5112\u4f34\u8bfb\u3002\u8fd9\u767d\u8863\u5c11\u5973\u7528\u6c49\u8bed\u548c\u5c3c\u6cca\u5c14\u738b\u5b50\u4ea4\u8c08\uff0c\u7528\u610f \u81ea\u7136\u662f\u8981\u7792\u8fc7\u5176\u4ed6\u4eba\u4f17\u3002 \u3000\u3000\u53ef\u662f\u90a3\u5c3c\u6cca\u5c14\u738b\u5b50\u4ecd\u7136\u4e0d\u53d1\u4e00\u8a00\uff0c\u767d\u8863\u5c11\u5973\u624b\u6301\u7389\u7b1b\uff0c\u8f7b\u8f7b\u5212\u4e86\u4e00\u9053\u5706\u5f27\uff0c\u8bf4\u9053\uff1a\u201c\u989d \u5c14\u90fd\uff0c\u6211\u5df2\u7ed9\u4f60\u7559\u4e0b\u4e00\u6761\u9000\u8def\uff0c\u4f60\u518d\u4e0d\u542c\u5584\u8a00\uff0c\u90a3\u53ef\u662f\u81ea\u53d6\u5176\u8fb1\u4e86\uff01\u201d\u8bf4\u8bdd\u4e4b\u65f6\uff0c\u7f13\u7f13\u8d70\u8fdb \u90a3\u4e24\u884c\u50e7\u4fa3\u6b66\u58eb\u4e4b\u4e2d\uff0c\u5999\u76ee\u6d41\u76fc\uff0c\u4f3c\u4e4e\u662f\u8981\u5728\u8fd9\u4e9b\u4eba\u4e2d\uff0c\u8ba4\u51fa\u5c3c\u6cca\u5c14\u738b\u5b50\uff01\u5c31\u5728\u5979\u5c06\u8981\u8d70\u5230 \u90a3\u5c0a\u5927\u4f5b\u50cf\u524d\u9762\u7684\u65f6\u5019\uff0c\u4e00\u4e2a\u7ea2\u8863\u50e7\u4eba\u5f92\u7136\u53d1\u96be\uff0c\u8888\u88df\u4e00\u6296\uff0c\u4fdf\u7684\u5411\u767d\u8863\u5c11\u5973\u5f53\u5934\u7f69\u4e0b\uff01 \u3000\u3000\u8fd9\u7ea2\u8863\u50e7\u4eba\u867d\u7136\u4e5f\u662f\u8499\u4e86\u9762\u5177\uff0c\u4f46\u6842\u534e\u751f\u5374\u8ba4\u5f97\u51fa\u4ed6\u6b63\u662f\u90a3\u4e2a\u66fe\u548c\u81ea\u5df1\u4ea4\u8fc7\u624b\u7684\u7ea2\u8863\u756a \u50e7\uff0c\u7a81\u7136\u89c1\u4ed6\u5728\u767d\u8863\u5c11\u5973\u80cc\u540e\u5077\u88ad\uff0c\u8888\u88df\u4e00\u5c55\uff0c\u52bf\u631f\u98ce\u96f7\uff0c\u5b9b\u5982\u4e00\u7247\u706b\u4e91\uff0c\u51cc\u7a7a\u538b\u4e0b\uff0c\u4e5f\u4e0d \u7981\u5403\u4e86\u4e00\u60ca\u3002\u5c82\u77e5\u8fd9\u7ea2\u8863\u756a\u618e\u51fa\u624b\u867d\u5feb\uff0c\u90a3\u767d\u8863\u5c11\u5973\u7adf\u4f3c\u80cc\u540e\u957f\u4e86\u773c\u775b\u4e00\u6837\uff0c\u51fa\u624b\u6bd4\u4ed6\u66f4 \u5feb\uff0c\u5934\u4e5f\u4e0d\u56de\uff0c\u53cd\u624b\u4e00\u6307\uff0c\u7389\u7b1b\u4e00\u6311\uff0c\u90a3\u4e00\u7247\u8888\u88df\u6296\u8d77\u7684\u4eba\u4e91\uff0c\u7adf\u7136\u7ed9\u4ed6\u4e00\u652f\u5c0f\u5c0f\u7684\u7389\u7b1b\u6311 \u5f00\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u5730\u98de\u8eab\u4e00\u8f6c\uff0c\u5237\u3001\u5237\u3001\u5237\u8fde\u8fdb\u4e09\u62db\uff0c\u624b\u4e2d\u7389\u7b1b\uff0c\u6307\u4e1c\u6253\u897f\uff0c\u6307\u5357\u6253 \u5317\uff0c\u7adf\u7136\u662f\u4e00\u6d3e\u51cc\u5389\u7684\u5251\u672f\u62db\u6570\uff01 \u3000\u3000\u5c31\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u5e99\u4e2d\u7684\u6b66\u58eb\u4e5f\u4e00\u9f50\u51fa\u624b\uff0c\u53ea\u542c\u5f97\u545c\u545c\u602a\u5578\uff0c\u6ee1\u5c4b\u5200\u5149\uff0c\u5728\u5979\u80cc\u540e\u548c\u4e24\u4fa7 \u7684\u50e7\u4fa3\u548c\u6b66\u58eb\uff0c\u5404\u628a\u968f\u8eab\u7684\u4f69\u5200\u98de\u51fa\uff0c\u6842\u534e\u751f\u6697\u53eb\u4e0d\u5999\uff0c\u4ed6\u77e5\u9053\u8fd9\u7ea2\u8863\u756a\u50e7\u529f\u529b\u4e0d\u5f31\uff0c\u53ea\u6015 \u767d\u8863\u5c11\u5973\u96be\u4ee5\u540c\u65f6\u5e94\u4ed8\u90a3\u5341\u51e0\u628a\u98de\u5200\uff0c\u4e0d\u6687\u601d\u91cf\uff0c\u5c31\u6293\u4e86\u4e00\u7247\u5c4b\u74e6\uff0c\u634f\u6210\u4e86\u65e0\u6570\u788e\u7247\u6253\u53bb\u3002 \u3000\u3000\u6842\u534e\u751f\u7684\u6697\u5668\u529f\u592b\u672c\u6765\u4e5f\u662f\u4e0a\u4e0a\u4e4b\u9009\uff0c\u600e\u5948\u4ed6\u5012\u60ac\u5728\u5e99\u9876\u7684\u98de\u6a90\u4e4b\u5185\uff0c\u53ea\u817e\u5f97\u51fa\u4e00\u53ea\u624b \u81c2\u53d1\u529b\uff0c\u788e\u74e6\u7528\u201c\u5012\u9152\u91d1\u94b1\u201d\u7684\u624b\u6cd5\u53d1\u51fa\uff0c\u867d\u7136\u4e5f\u6253\u843d\u4e86\u4e94\u516d\u628a\u98de\u5200\uff0c\u8fd8\u662f\u6709\u4e94\u516d\u628a\u98de\u5200\u98de \u5230\u4e86\u767d\u8863\u5c11\u5973\u7684\u80cc\u540e\u3002 \u3000\u3000\u90a3\u767d\u8863\u5c11\u5973\u5ffd\u5730\u4e00\u58f0\u957f\u7b11\uff0c\u7389\u7b1b\u4e00\u6311\uff0c\u4e5f\u4e0d\u77e5\u5979\u7528\u7684\u662f\u4ec0\u4e48\u624b\u6cd5\uff0c\u4e3e\u624b\u4e4b\u95f4\uff0c\u5c31\u628a\u90a3\u7ea2 \u8863\u756a\u50e7\u7684\u8888\u88df\u6311\u4e86\u8fc7\u6765\uff0c\u7389\u7b1b\u4e00\u65cb\uff0c\u5982\u81c2\u4f7f\u6307\uff0c\u8888\u88df\u53cd\u5c55\uff0c\u5c06\u90a3\u4e94\u516d\u628a\u98de\u5200\uff0c\u5168\u90fd\u5377\u4e86\u3002\u8fd9 \u822c\u5947\u5999\u7684\u6536\u6697\u5668\u624b\u6cd5\uff0c\u8fde\u6842\u534e\u751f\u4e5f\u662f\u5927\u51fa\u610f\u6599\uff0c\u4e0d\u7981\u6697\u6697\u53eb\u4e86\u4e00\u58f0\u201c\u60ed\u6127\u201d\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a \u201c\u65e9\u77e5\u5979\u6709\u5982\u6b64\u529f\u592b\uff0c\u4f55\u5fc5\u6211\u6765\u591a\u4e8b\uff1f\u201d \u3000\u3000\u90a3\u7ea2\u8863\u756a\u618e\u5931\u4e86\u8888\u88df\uff0c\u60ca\u60f6\u5931\u63aa\uff0c\u60f3\u907f\u5f00\u65f6\uff0c\u90a3\u907f\u5f97\u4e86\uff1f\u53ea\u89c1\u90a3\u767d\u8863\u5c11\u5973\u4e94\u7b1b\u4e00\u626c\uff0c\u75be \u5982\u63a3\u7535\uff0c\u82e5\u7136\u58f0\u54cd\uff0c\u7389\u7b1b\u5212\u5904\uff0c\u7adf\u628a\u7ea2\u8863\u756a\u50e7\u90a3\u539a\u539a\u7684\u76ae\u9769\u9762\u5177\u5212\u7834\uff0c\u8fd9\u4e00\u4e0b\u7edd\u62db\uff0c\u66f4\u4eca\u6842 \u534e\u751f\u5fc3\u6298\uff0c\u60f3\u90a3\u7389\u7b1b\u4e43\u662f\u4e00\u4ef6\u5149\u6ed1\u7684\u5706\u5f62\u4e50\u5668\uff0c\u4f46\u88ab\u90a3\u5c11\u5973\u8fd0\u7528\u8d77\u6765\uff0c\u7adf\u7136\u80fd\u591f\u50cf\u950b\u5229\u7684\u5200 \u5251\u4e00\u6837\uff0c\u628a\u76ae\u9769\u9762\u5177\u5212\u7a7f\uff0c\u800c\u4e14\u53c8\u4e0d\u4f24\u53ca\u654c\u4eba\u76ae\u8089\uff0c\u8fd9\u624b\u529f\u592b\uff0c\u6842\u534e\u751f\u81ea\u95ee\u4e5f\u672b\u5fc5\u80fd\u591f\uff01 \u3000\u3000\u90a3\u7ea2\u8863\u756a\u618e\u88ab\u5212\u7834\u9762\u5177\uff0c\u767b\u65f6\u5446\u82e5\u6728\u9e21\uff0c\u6b63\u5728\u56f4\u653b\u7684\u50e7\u4fa3\u6b66\u58eb\u4e5f\u65e0\u4e0d\u9a87\u7136\u5931\u8272\uff0c\u90a3\u767d\u8863 \u5c11\u5973\u5374\u5e76\u4e0d\u8d81\u52bf\u8fdb\u62db\uff0c\u4f46\u89c1\u5979\u7389\u7b1b\u4e00\u6a2a\uff0c\u4e00\u53cc\u660e\u5982\u79cb\u6c34\u822c\u7684\u773c\u775b\uff0c\u4ece\u53f3\u5230\u5728\u7684\u81ea\u4e24\u65c1\u50e7\u4fa3\u6b66 \u58eb\u7684\u8138\u4e0a\u7f13\u7f13\u7684\u626b\u8fc7\uff0c\u8bf4\u4e5f\u5947\u602a\uff0c\u90a3\u4e9b\u51f6\u795e\u6076\u715e\u822c\u7684\u6b66\u58eb\u88ab\u5979\u7684\u773c\u5149\u4e00\u626b\uff0c\u4e2a\u4e2a\u895f\u82e5\u5bd2\u8749\uff0c \u5927\u6bbf\u91cc\u9759\u5bc2\u65e0\u58f0\uff0c\u8fde\u4e00\u6839\u9488\u8dcc\u5728\u5730\u4e0b\u90fd\u542c\u5f97\u89c1\u54cd\u3002 \u3000\u3000\u90a3\u767d\u8863\u5c11\u5973\u773c\u5149\u5c04\u5230\u4e86\u7ea2\u8863\u756a\u618e\u7684\u9762\u4e0a\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u9053\u5723\u56fd\u5e08\uff0c\u4f60\u4e0d\u5728\u52a0\u5fb7\u6ee1\u90fd\uff0c\u5374 \u5230\u897f\u85cf\u6765\u4f5c\u62a4\u6cd5\u4e48\uff1f\u201d\u90a3\u7ea2\u8863\u756a\u618e\u4e00\u58f0\u4e0d\u54cd\uff0c\u6454\u4e0b\u9762\u5177\uff0c\u7acb\u523b\u8d70\u51fa\u5e99\u95e8\u3002 \u3000\u3000\u5c3c\u6cca\u5c14\u6b66\u58eb\u4eec\u53d1\u4e00\u58f0\u558a\uff0c\u6709\u597d\u51e0\u4e2a\u4eba\u8ddf\u7740\u90a3\u7ea2\u8863\u756a\u50e7\u4fbf\u8dd1\uff0c\u4eba\u7fa4\u4e2d\u4e0d\u77e5\u662f\u8c01\u5c04\u51fa\u4e86\u4e00\u652f \u54cd\u7bad\uff0c\u6709\u597d\u51e0\u4e2a\u6b66\u58eb\u8f6e\u5200\u53c8\u4e0a\uff0c\u770b\u6765\u8fd9\u51e0\u4e2a\u4eba\u4e43\u662f\u738b\u5b50\u7684\u5fc3\u8179\uff0c\u867d\u7136\u660e\u77e5\u4e0d\u654c\uff0c\u4ecd\u7136\u9f13\u566a\u800c \u524d\u3002 \u3000\u3000\u767d\u8863\u5c11\u5973\u6447\u5934\u4e00\u53f9\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u989d\u5c14\u90fd\u738b\u5b50\uff0c\u4f60\u518d\u4e0d\u542c\u6211\u7684\u529d\u544a\uff0c\u90a3\u5c31\u4f11\u602a\u6211\u5c06\u4f60\u7684 \u9762\u76ae\u4e5f\u622e\u7a7f\u4e86\uff01\u201d\u6325\u52a8\u7389\u7b1b\uff0c\u4f46\u89c1\u78a7\u5149\u8363\u83b9\uff0c\u94ff\u9535\u4e4b\u58f0\u4e0d\u7edd\u4e8e\u8033\uff0c\u51e0\u4e2a\u7167\u9762\uff0c\u5c06\u90a3\u51e0\u4e2a\u6b66\u58eb \u7684\u6708\u7259\u5f2f\u5200\u5168\u90fd\u6253\u98de\uff0c\u7389\u7b1b\u4e1c\u6307\u897f\u5212\uff0c\u6bcf\u6307\u4e00\u4e0b\uff0c\u4fbf\u662f\u201c\u4e0a\u201d\u7684\u4e00\u58f0\uff0c\u970e\u65f6\u95f4\uff0c\u5df2\u6709\u4e94\u516d\u4e2a \u6b66\u58eb\u7684\u76ae\u7f69\u9762\u5177\u88ab\u5979\u622e\u7834\uff01 \u3000\u3000\u767d\u8863\u5c11\u5973\u7eb5\u58f0\u957f\u7b11\uff0c\u7389\u7b1b\u4e00\u505c\uff0c\u5e99\u4e2d\u7684\u50e7\u4fa3\u6b66\u58eb\u6f6e\u6c34\u822c\u7684\u5411\u5916\u9762\u6d8c\u51fa\u53bb\uff0c\u4e0d\u6d88\u7247\u523b\uff0c\u7a7a \u65f7\u7684\u5927\u6bbf\u5c31\u53ea\u5269\u4e0b\u4e86\u5979\u4e00\u4e2a\u4eba\uff01\u6842\u534e\u751f\u629a\u638c\u8d5e\u9053\uff1a\u201c\u7389\u7b1b\u98ce\u58f0\uff0c\u6479\u9b54\u655b\u8ff9\uff0c\u5584\u54c9\uff0c\u5584\u54c9\uff01\u201d \u3000\u3000\u767d\u8863\u5c11\u5973\u9053\uff1a\u201c\u591a\u627f\u9ad8\u660e\u76f8\u52a9\uff0c\u8bf7\u6765\u76f8\u89c1\u3002\u201d \u3000\u3000\u6842\u534e\u751f\u8d70\u8fd1\u90a3\u767d\u8863\u5c11\u5973\uff0c\u5728\u4f5b\u6bbf\u7684\u7409\u7483\u706f\u4e0b\uff0c\u770b\u5f97\u66f4\u771f\u5207\uff0c\u4e5f\u66f4\u89c9\u5f97\u5a07\u52a8\u4eba\uff0c\u5446\u4e86\u4e00 \u5446\uff0c\u90a3\u767d\u8863\u5c11\u5973\u88e3\u887d\u4e00\u798f\uff0c\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u5c0f\u5973\u5b50\u8fd9\u53a2\u6709\u793c\u4e86\u3002\u201d\u6a31\u5507\u542f\u5904\uff0c\u6697\u9999\u88ad\u4eba\uff0c\u6842\u534e \u751f\u5fc3\u795e\u4e00\u8361\uff0c\u6025\u5fd9\u8fd8\u793c\u3002 \u3000\u3000\u4e24\u4eba\u76f8\u89c1\uff0c\u6842\u534e\u751f\u56fa\u7136\u662f\u610f\u4e71\u60c5\u8ff7\uff0c\u90a3\u5c11\u5973\u4e5f\u662f\u53c8\u60ca\u53c8\u559c\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u4e2d\u534e\u4eba\u7269\u4fca \u79c0\uff0c\u679c\u7136\u4e0d\u9519\u3002\u201d\u4e0d\u8fc7\u5979\u7d20\u6027\u77dc\u6301\uff0c\u4e0d\u81f4\u4e8e\u50cf\u6842\u534e\u751f\u90a3\u6837\u90fd\u4ece\u795e\u8272\u4e2d\u8868\u73b0\u51fa\u6765\u3002 \u3000\u3000\u6842\u534e\u751f\u5b9a\u4e86\u5b9a\u795e\uff0c\u8bf4\u9053\uff1a\u201c\u72c2\u751f\u65e0\u793c\uff0c\u6562\u95ee\u5c0f\u59d0\u82b3\u540d\u3002\u201d\u5728\u4e2d\u56fd\u7684\u793c\u4fd7\uff0c\u540c\u4e00\u4f4d\u964c\u751f\u5c11 \u5973\u8bf7\u95ee\u59d3\u540d\uff0c\u90a3\u81ea\u662f\u4e00\u4ef6\u5192\u6627\u4e4b\u4e8b\uff0c\u597d\u5728\u8fd9\u767d\u8863\u5c11\u5973\u5e76\u4e0d\u62d8\u6ce5\u4e2d\u56fd\u7684\u793c\u4fd7\uff0c\u843d\u843d\u5927\u65b9\u7684\u7b11 \u9053\uff1a\u201c\u8fd9\u6709\u4ec0\u4e48\u4e0d\u53ef\u4ee5\uff1f\u6211\u53eb\u534e\u7389\u3002\u201d\u6842\u534e\u751f\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u8fd9\u662f\u4e2d\u56fd\u4eba\u7684\u540d\u5b57\u3002\u201d\u767d\u8863 \u5c11\u5973\u7b11\u9053\uff1a\u201c\u662f\u4e48\uff1f\u6211\u867d\u7136\u6ca1\u6709\u5230\u8fc7\u4e2d\u56fd\u672c\u571f\uff0c\u5bf9\u4e2d\u56fd\u5411\u5f80\u5df2\u4e45\u3002\u542c\u8bf4\u4f60\u4eec\u6c49\u4eba\u5f88\u5b9d\u8d35\u7389 \u77f3\uff0c\u5728\u4f60\u4eec\u7684\u4e66\u4e0a\u4e5f\u628a\u5b83\u5f53\u4f5c\u6d01\u767d\u575a\u8d1e\u7684\u8c61\u5fb5\uff0c\u6240\u4ee5\u6211\u53d6\u4e86\u8fd9\u4e2a\u540d\u5b57\u3002\u201d\u6842\u534e\u751f\u9053\uff1a\u201c\u5c0f\u59d0 \u5bf9\u4e2d\u56fd\u7684\u4e1c\u897f\u771f\u61c2\u5f97\u4e0d\u5c11\u3002\u201d\u90a3\u767d\u8863\u5c11\u5973\u9053\uff1a\u201c\u6211\u5b66\u8fc7\u51e0\u5e74\u6c49\u6587\uff0c\u8981\u8bf4\u61c2\u5f97\u4e2d\u56fd\uff0c\u90a3\u8fd8\u5dee\u5f97 \u8fdc\u5462\uff01\u5148\u751f\u9ad8\u59d3\u5927\u540d\uff1f\u201d\u6842\u534e\u751f\u9053\uff1a\u201c\u6211\u660e\u6842\u534e\u751f\u3002\u201d\u90a3\u767d\u8863\u5c11\u5973\u53c8\u662f\u683c\u683c\u4e00\u7b11\uff0c\u9053\uff1a \u3000\u3000\u201c\u542c\u8bf4\u4f60\u4eec\u4e2d\u56fd\uff0c\u5144\u5f1f\u59d0\u59b9\u7684\u540d\u5b57\u4e2d\u603b\u6709\u4e00\u4e2a\u5b57\u662f\u76f8\u540c\u7684\uff0c\u662f\u4e0d\uff1f\u201d\u6842\u534e\u751f\u9053\uff1a\u201c\u4e0d \u9519\uff0c\u8fd9\u662f\u5728\u4e2d\u56fd\u5bb6\u65cf\u4e2d\u6392\u5b9a\u8f88\u4efd\u7684\u4e60\u60ef\u3002\u201d\u767d\u8863\u5c11\u5973\u7b11\u9053\uff1a\u201c\u4f60\u53eb\u534e\u751f\uff0c\u6211\u53eb\u534e\u7389\uff0c\u82e5\u662f\u5728 \u4e2d\u56fd\uff0c\u522b\u4eba\u5c31\u8981\u5f53\u6211\u4eec\u662f\u5144\u59b9\u4e86\u3002\u201d\u6842\u534e\u751f\u53c8\u662f\u5fc3\u795e\u4e00\u6c64\uff0c\u4f46\u89c1\u5979\u5929\u771f\u65e0\u90aa\uff0c\u843d\u843d\u5927\u65b9\uff0c\u90a3 \u6562\u5192\u6627\uff0c\u4e5f\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6b63\u662f\u8fd9\u6837\u3002\u53ef\u662f\u6211\u90a3\u6709\u8fd9\u4efd\u798f\u6c14\u6709\u4f60\u8fd9\u6837\u7684\u59b9\u5b50\u5462\uff1f\u201d\u767d\u8863\u5c11\u5973 \u7b11\u9053\uff1a\u201c\u4f60\u4eca\u665a\u5e2e\u4e86\u6211\u7684\u5927\u5fd9\uff0c\u770b\u6765\u4f60\u5e74\u7eaa\u4e5f\u5f53\u6bd4\u6211\u5927\uff0c\u597d\uff0c\u4f60\u5c31\u505a\u6211\u7684\u5927\u54e5\u54e5\uff0c\u4e5f\u4e0d\u4e3a \u8fc7\u3002\u201d  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u9b44\u5bd2\u5149\u5251\u300b\u2014\u2014\u7b2c\u56db\u7ae0\u3000\u5bd2\u51b0\u7a9f\u91cc\u89c1\u5947\u73cd \u6881\u7fbd\u751f\u300a\u51b0\u9b44\u5bd2\u5149\u5251\u300b \u7b2c\u56db\u7ae0\u3000\u5bd2\u51b0\u7a9f\u91cc\u89c1\u5947\u73cd \u3000\u3000\u6842\u534e\u751f\u559c\u51fa\u671b\u5916\uff0c\u8be5\u9053\uff1a\u201c\u8fd9\u6211\u53ef\u4e0d\u6562\u5f53\u3002\u201d\u767d\u8863\u5c11\u5973\u9053\uff1a\u201c\u4f5b\u95e8\u4e4b\u4e2d\uff0c\u4e16\u6cd5\u5e73\u7b49\u3002\u53e6 \u7686\u5144\u5f1f\uff0c\u5973\u7686\u59d0\u59b9\u3002\u4f55\u51b5\u4f60\u6211\u6709\u8fd9\u6bb5\u5947\u9022\uff0c\u5144\u59b9\u76f8\u79f0\uff0c\u6709\u4f55\u4e0d\u53ef\u3002\u201d\u6842\u534e\u751f\u9053\uff1a\u201c\u90a3\u4e48\u8bf4\uff0c \u4f60\u662f\u4f5b\u95e8\u5f1f\u5b50\uff1f\u201d\u767d\u8863\u5c11\u5973\u9053\uff1a\u201c\u6211\u4eec\u81ea\u53e4\u4ee5\u6765\uff0c\u90fd\u662f\u4ee5\u4f5b\u6559\u6cbb\u56fd\uff0c\u4e3e\u56fd\u5d07\u4fe1\uff0c\u6211\u81ea\u7136\u4e5f\u4e0d \u4f8b\u5916\u3002\u201d\u6842\u534e\u751f\u7a0d\u5fae\u6709\u70b9\u5931\u671b\uff0c\u5fc3\u9053\uff1a\u201c\u539f\u6765\u6309\u7167\u5979\u4eec\u7684\u6559\u4e49\uff0c\u5f02\u59d3\u5144\u59b9\uff0c\u4ea6\u5c5e\u5bfb\u5e38\u3002\u201d\u4f46 \u542c\u5979\u201c\u5927\u54e5\u54e5\u201d\u4e09\u5b57\u53eb\u5f97\u5982\u6b64\u5a07\u751c\uff0c\u5fc3\u4e2d\u6781\u4e3a\u8212\u7545\u3002 \u3000\u3000\u53ea\u542c\u5f97\u534e\u7389\u95ee\u9053\uff1a\u201c\u5927\u54e5\u54e5\uff0c\u4f60\u662f\u6ee1\u6e05\u7687\u5e1d\u6d3e\u6765\u7684\u4eba\u5417\uff1f\u201d\u6842\u534e\u751f\u9053\uff1a\u201c\u4e0d\u662f\u3002\u201d\u534e\u738b \u9053\uff1a\u201c\u90a3\u4f60\u4e3a\u4f55\u80af\u5192\u6b64\u5947\u9669\uff0c\u95ef\u8fdb\u9b54\u9b3c\u57ce\u4e2d\uff0c\u6765\u4e0e\u4ed6\u4eec\u4f5c\u5bf9\uff1f\u201d\u6842\u534e\u751f\u9053\uff1a\u201c\u6211\u662f\u4e2d\u56fd\u4eba\uff0c \u4ed6\u4eec\u548c\u4e2d\u56fd\u4f5c\u5bf9\uff0c\u6211\u81ea\u7136\u4e5f\u8981\u4e0e\u4ed6\u4eec\u4f5c\u5bf9\u4e86\u3002\u5c0f\u59b9\u5b50\uff0c\u4f60\u53c8\u4e3a\u4ec0\u4e48\u8981\u4e0e\u4ed6\u4eec\u4f5c\u5bf9\uff1f\u201d\u534e\u738b \u9053\uff1a\u201c\u56e0\u4e3a\u6211\u662f\u5c3c\u6cca\u5c14\u4eba\u3002\u201d\u6842\u534e\u751f\u8be7\u9053\uff1a\u201c\u90a3\u4f4d\u989d\u5c14\u90fd\u738b\u5b50\u4e0d\u6b63\u662f\u4f60\u4eec\u5c3c\u6cca\u5c14\u7684\u738b\u5b50 \u4e48\uff1f\u201d\u534e\u7389\u9053\uff1a\u201c\u4e0d\u9519\u5440\uff0c\u6b63\u662f\u4e3a\u6b64\uff0c\u6240\u4ee5\u6211\u624d\u8981\u8d76\u4ed6\u4eec\u56de\u56fd\u3002\u4e2d\u5c3c\u4e16\u4ee3\u4ea4\u597d\uff0c\u4e24\u56fd\u7686\u8499\u5176 \u7a3f\uff1a\u82e5\u7136\u5984\u52a8\u5e72\u6208\uff0c\u4e0d\u4f46\u5c3c\u6cca\u5c14\u4e0e\u897f\u85cf\u751f\u7075\u6d82\u70ad\uff0c\u800c\u4e14\u4e00\u65e6\u5175\u8fde\u7978\u7ed3\uff0c\u4e2d\u56fd\u6240\u53d7\u7684\u707e\u5bb3\u53ef\u80fd \u4e0d\u5927\uff0c\u53ea\u6015\u5c3c\u6cca\u5c14\u5c31\u8981\u56e0\u6b64\u6bc1\u4e86\u3002\u201d \u3000\u3000\u8fd9\u4e00\u756a\u8bdd\u8bf4\u5f97\u6842\u534e\u751f\u8083\u7136\u8d77\u656c\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u5979\u4e0d\u4f46\u59ff\u5bb9\u7edd\u4e16\uff0c\u773c\u5149\u89c1\u8bc6\uff0c\u66f4\u4eca\u4eba\u5fc3 \u6298\u3002\u201d\u4f69\u670d\u4e4b\u4e2d\uff0c\u5374\u53c8\u6709\u65e0\u6570\u7591\u56e2\uff1a\u5c3c\u6cca\u5c14\u738b\u5b50\u4e3a\u4ec0\u4e48\u4e0d\u6562\u9732\u9762\u89c1\u5979\uff1f\u5979\u4e3a\u4f55\u8fdc\u6d89\u5f02\u56fd\uff0c\u5355 \u8eab\u5230\u6b64\uff1f\u96be\u9053\u5979\u65e9\u5df2\u77e5\u9053\u738b\u5b50\u7684\u9634\u8c0b\uff1f\u90a3\u53c8\u662f\u600e\u4e48\u77e5\u9053\u7684\uff1f\u5979\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u8fd9\u8eab\u7edd\u4e16\u7684\u6b66\u529f\uff0c \u53c8\u662f\u4ece\u90a3\u91cc\u5b66\u6765\uff1f\u521d\u521d\u76f8\u8bc6\uff0c\u6842\u534e\u751f\u4e0d\u4fbf\u5bfb\u6839\u7a76\u5e95\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u6211\u603b\u8981\u6162\u6162\u63a2\u542c\u51fa\u6765\u3002\u201d \u3000\u3000\u767d\u8863\u5c11\u5973\u749e\u55e4\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5927\u54e5\u54e5\uff0c\u4f60\u60f3\u4ec0\u4e48\uff1f\u201d\u6842\u534e\u751f\u9053\uff1a\u201c\u6211\u60f3\uff0c\u6211\u60f3\u2026\u2026\u201d\u767d\u8863 \u5c11\u5973\u7b11\u9053\uff1a\u201c\u4f60\u89c9\u5f97\u6211\u6709\u70b9\u5947\u602a\uff0c\u662f\u5417\uff1f\u201d\u6842\u534e\u751f\u5fc3\u601d\u7ed9\u4ed6\u770b\u7834\uff0c\u9762\u4e0a\u4e00\u7ea2\uff0c\u9053\uff1a\u201c\u662f\u6709\u70b9 \u513f\u3002\u201d\u767d\u8863\u5c11\u5973\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u5355\u8eab\u4e00\u4eba\u5230\u6b64\uff0c\u6211\u4e5f\u89c9\u5f97\u4f60\u6709\u70b9\u5947\u602a\u5440\uff01\u201d\u6842\u534e\u751f\u9053\uff1a\u201c\u6211\u662f \u7537\u5b50\uff0c\u7537\u5b50\u7406\u5f53\u6e38\u5b66\u56db\u65b9\uff0c\u589e\u5e7f\u89c1\u8bc6\u3002\u201d\u767d\u8863\u5c11\u5973\u7b11\u9053\uff1a\u201c\u5973\u5b50\u4e0e\u7537\u5b50\u53c8\u6709\u4ec0\u4e48\u4e0d\u540c\uff1f\u7537\u5b50 \u7406\u5f53\u6e38\u5b66\u56db\u65b9\uff0c\u5973\u5b50\u5c31\u4e0d\u8be5\u589e\u5e7f\u89c1\u8bc6\u5417\uff1f\u201d \u3000\u3000\u6842\u534e\u751f\u7ed9\u4ed6\u95ee\u4f4f\uff0c\u5fc3\u4e2d\u66f4\u662f\u4f69\u670d\uff0c\u90a3\u5c11\u5973\u683c\u683c\u7b11\u9053\uff1a\u201c\u4f60\u8bf4\u8981\u589e\u5e7f\u89c1\u8bc6\uff0c\u65e5\u4e0b\u5c31\u6709\u4e00\u4ef6 \u8db3\u4ee5\u589e\u5e7f\u89c1\u8bc6\u7684\u4e8b\u60c5\uff0c\u4f60\u613f\u548c\u6211\u4e00\u540c\u53bb\u5f00\u5f00\u773c\u754c\u5417\uff1f\u201d\u6842\u534e\u751f\u9053\uff1a\u201c\u4f60\u5230\u4ec0\u4e48\u5730\u65b9\uff0c\u6211\u90fd\u613f \u610f\u966a\u4f60\u3002\u201d\u767d\u8863\u5c11\u5973\u5ffd\u5730\u53c8\u662f\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4f5b\u7ecf\u4e0a\u8bf4\uff1a\u53bb\u4f4f\u968f\u7f18\uff0c\u591a\u6b32\u591a\u607c\u3002\u54b1\u4eec\u5076 \u7136\u76f8\u9047\uff0c\u51fa\u4e86\u6b64\u5c71\uff0c\u4e5f\u5c31\u5f53\u5206\u624b\u3002\u4f60\u4e0d\u5fc5\u591a\u6b32\u77e5\u9053\u6211\u7684\u4e8b\u60c5\uff0c\u6211\u4e5f\u4e0d\u6765\u95ee\u4f60\u3002\u514d\u5f97\u5206\u624b\u4e4b \u540e\uff0c\u5f7c\u6b64\u53cd\u589e\u70e6\u607c\u3002\u201d\u8fd9\u756a\u8bdd\u63a2\u542b\u4f5b\u5bb6\u54f2\u7406\uff0c\u4f46\u5728\u65e0\u60c5\u610f\u4e4b\u4e2d\u53c8\u89c1\u6709\u60c5\u610f\uff0c\u6709\u60c5\u610f\u4e4b\u4e2d\u53c8\u4f3c \u65e0\u60c5\u610f\uff0c\u4f73\u534e\u751f\u60f3\u8d77\u7ec8\u987b\u4e00\u522b\uff0c\u4e0d\u89c9\u60af\u7136\u3002 \u3000\u3000\u767d\u8863\u5c11\u5973\u7b11\u9053\uff1a\u201c\u597d\u5427\uff0c\u54b1\u4eec\u73b0\u5728\u8be5\u52a8\u8eab\u4e86\uff0c\u518d\u8fdf\u5c31\u6050\u8d76\u4e0d\u4e0a\u4e86\u3002\u201d\u6842\u534e\u751f\u9053\uff1a\u201c\u4ec0\u4e48 \u4e8b\u60c5\uff1f\u201d\u767d\u8863\u5c11\u5973\u9053\uff1a\u201c\u6211\u5e26\u4f60\u53bb\u5bfb\u89c5\u4e00\u4ef6\u7a00\u4e16\u7684\u5947\u73cd\uff01\u201d\u6842\u534e\u751f\u5fc3\u5934\u4e00\u8df3\uff0c\u53eb\u9053\uff1a\u201c\u662f\u4e0d \u662f\u85cf\u7075\u4e0a\u4eba\u4e5f\u8981\u5bfb\u89c5\u7684\u5b9d\u8d1d\uff1f\u201d\u767d\u8863\u5c11\u5973\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u54b1\u4eec\u53bb\u770b\u770b\u4ed6\u7a76\u7adf\u6709\u6ca1\u6709\u672c\u4e8b\u80fd\u591f\u5c06 \u8fd9\u4ef6\u7a00\u4e16\u4e4b\u73cd\u4ece\u5343\u4e08\u51b0\u7a9f\u4e4b\u4e2d\u53d1\u6398\u51fa\u6765\uff1f\u201d \u3000\u3000\u6842\u534e\u751f\u60ca\u5f02\u4e4b\u6781\uff0c\u4f46\u89c1\u767d\u8863\u5c11\u5973\u5df2\u5c55\u5f00\u7edd\u9876\u8f7b\u529f\uff0c\u76f4\u5954\u5c71\u9876\u3002\u6842\u534e\u751f\u4e0d\u6562\u6020\u6162\uff0c\u63d0\u4e00\u53e3 \u6c14\uff0c\u4ea6\u6b65\u4ea6\u8d8b\u7684\u8ddf\u5728\u5979\u7684\u540e\u9762\u3002\u8d70\u5230\u5929\u660e\u65f6\u4efd\uff0c\u5df2\u7ecf\u53ef\u4ee5\u770b\u5230\u79ef\u96ea\u8986\u76d6\u5cf0\u97f5\u7684\u4e86\u3002 \u3000\u3000\u767d\u8863\u5c11\u5973\u56de\u7738\u4e00\u7b11\uff0c\u67d4\u58f0\u8bf4\u9053\uff1a\u201c\u5927\u54e5\u54e5\uff0c\u4f60\u7d2f\u5417\uff1f\u201d\u6842\u534e\u751f\u9762\u70ed\u5fc3\u8df3\uff0c\u547c\u5438\u9887\u611f\u56f0 \u96be\uff0c\u5c34\u5c2c\u7b11\u9053\uff1a\u201c\u6709\u4e00\u70b9\u513f\uff01\u201d\u767d\u8863\u5c11\u5973\u7f13\u4e0b\u811a\u6b65\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e5f\u7d2f\u4e86\uff01\u597d\u5728\u8fd9\u5c71\u8fd8\u4e0d\u7b97 \u9ad8\uff0c\u6211\u6765\u4e4b\u65f6\uff0c\u7ecf\u8fc7\u559c\u9a6c\u62c9\u96c5\u5c71\uff0c\u90a3\u624d\u7b97\u9ad8\u5462\u3002\u6211\u4e5f\u66fe\u8bd5\u60f3\u6500\u767b\u5cf0\u9876\uff0c\u90a3\u77e5\u521a\u4e0a\u5230\u73e0\u7a46\u6717\u9a6c \u5cf0\u811a\u4e0b\u7684\u96ea\u5761\uff0c\u5c31\u8fde\u6c14\u4e5f\u900f\u4e0d\u8fc7\u6765\u4e86\uff0c\u53ea\u597d\u8d76\u4e0b\u5c71\u3002\u201d\u6842\u534e\u751f\u770b\u5979\u9762\u7ea2\u4e0a\u4e0a\u7684\uff0c\u8273\u82e5\u671d\u971e\uff0c \u5982\u5979\u4e0d\u662f\u6545\u610f\u66ff\u81ea\u5df1\u89e3\u5632\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u4e48\u54b1\u4eec\u53ef\u4ee5\u6b47\u4e00\u4f1a\u5427\uff1f\u201d\u767d\u8863\u5c11\u5973\u9053\uff1a\u201c\u54b1\u4eec\u6162\u4e00\u4e9b \u8d70\uff0c\u5f85\u5230\u7cbe\u62bc\u6062\u590d\uff0c\u518d\u8d76\u4e00\u7a0b\u3002\u201d \u3000\u3000\u8fd9\u65f6\u671d\u9633\u521d\u51fa\uff0c\u4ece\u5c71\u9876\u5012\u6302\u4e0b\u6765\u7684\u51b0\u5ddd\uff0c\u7531\u4e8e\u592a\u9633\u5149\u7684\u6298\u5c04\u548c\u53d1\u5c04\uff0c\u6574\u4e2a\u51b0\u5c42\u90fd\u53d8\u6210\u6d45 \u84dd\u8272\u7684\u900f\u660e\u4f53\uff0c\u90a3\u4e9b\u672a\u66fe\u51dd\u7ed3\u7684\u96ea\u82b1\uff0c\u5728\u9633\u5149\u5e95\u4e0b\uff0c\u6cdb\u51fa\u971e\u8f89\u4e3d\u5f69\uff0c\u5947\u5999\u5f97\u96be\u4ee5\u5f62\u5bb9\uff0c\u767d\u8863 \u5c11\u5973\u8d5e\u53f9\u9053\uff1a\u201c\u771f\u7f8e\uff0c\u771f\u7f8e\uff01\u53ef\u60dc\u5728\u4e2d\u56fd\u7684\u8bd7\u8bcd\u91cc\u9762\uff0c\u6211\u5374\u6ca1\u6709\u8bfb\u8fc7\u4e00\u9996\u541f\u548f\u51b0\u5ddd\u7684\u3002\u201d\u6842 \u534e\u751f\u5fc3\u9053\uff1a\u201c\u53e4\u4ee3\u7684\u8bd7\u4eba\uff0c\u53ea\u6015\u6ca1\u6709\u8c01\u66fe\u5230\u8fc7\u897f\u85cf\uff0c\u4ed6\u4eec\u6839\u672c\u5c31\u6ca1\u6709\u89c1\u8fc7\u51b0\u5ddd\u7684\u5947\u666f\uff0c\u53c8\u600e \u5199\u5f97\u51fa\u6765\uff1f\u201d\u773c\u5149\u4e00\u77a5\uff0c\u89c1\u767d\u8863\u5c11\u5973\u7b11\u8138\u5982\u82b1\uff0c\u6842\u534e\u751f\u60f3\u4e86\u4e00\u60f3\uff0c\u8bf4\u9053\uff1a\u201c\u541f\u548f\u51b0\u5ddd\u7684\u8bdd\u6211 \u4e5f\u672a\u66fe\u89c1\u8fc7\uff0c\u4f46\u6709\u4e00\u9996\u5199\u5c71\u4e2d\u96ea\u666f\u7684\u5230\u4e5f\u4e0e\u773c\u524d\u7684\u666f\u81f4\u6709\u4e9b\u76f8\u4f3c\u3002\u201d\u9065\u6307\u96ea\u82b1\u7f13\u7f13\u541f\u9053\uff1a \u201c\u6625\u5bab\u6ee1\u7a7a\u6765\uff0c\u89e6\u5904\u4f3c\u82b1\u5f00\uff0c\u4e0d\u77e5\u5c71\u91cc\u6811\uff0c\u82e5\u4e2a\u662f\u771f\u6885\uff1f\u201d\u767d\u8863\u5c11\u5973\u62cd\u624b\u8d5e\u9053\uff1a\u201c\u597d\u4e00\u4e2a\uff1a \u82e5\u4e2a\u662f\u771f\u6885\uff1f\u679c\u7136\u5206\u8fa8\u4e0d\u51fa\u6765\u3002\u201d \u3000\u3000\u6842\u534e\u751f\u7684\u6bcd\u4eb2\u662f\u6c5f\u5357\u7b2c\u4e00\u624d\u5973\u5192\u6d63\u83b2\uff08\u6842\u534e\u751f\u7236\u6bcd\u7684\u6545\u4e8b\u89c1\u62d9\u7740\u300a\u4e03\u5251\u4e0b\u5929\u5c71\u300b\u3002\uff09\u6842 \u534e\u751f\u5e7c\u627f\u5bb6\u5b66\uff0c\u5bf9\u4e8e\u7ecf\u53f2\u3001\u8bcd\u7ae0\u3001\u97f3\u4e50\u3001\u56fe\u753b\u3001\u65e0\u4e0d\u51fa\u8272\u5f53\u884c\uff0c\u4e0e\u90a3\u767d\u8863\u5c11\u5973\u8d8a\u8c08\u8d8a\u89c9\u6295 \u673a\uff0c\u5f7c\u6b64\u867d\u7136\u4e0d\u8a00\uff0c\u90fd\u6709\u76f8\u89c1\u6068\u665a\u4e4b\u611f\u3002 \u3000\u3000\u8d70\u4e86\u4e00\u9635\uff0c\u5ffd\u89c9\u5929\u6c14\u6e10\u6696\uff0c\u8f6c\u8fc7\u4e00\u4e2a\u5c71\u5773\uff0c\u4f46\u89c9\u773c\u775b\u4e00\u4eae\uff0c\u5728\u7fa4\u5cf0\u73af\u62b1\u4e4b\u4e2d\uff0c\u7adf\u662f\u767d\u832b \u832b\u7684\u4e00\u7247\u6e56\u6c34\uff0c\u6e56\u8fb9\u7eff\u8349\u5982\u8335\uff0c\u5c71\u9876\u4e0a\u7684\u98de\u7011\u6d41\u6cc9\uff0c\u51b2\u5165\u6e56\u4e2d\uff0c\u90a3\u900f\u660e\u7684\u6cc9\u6c34\u5c31\u50cf\u6eda\u52a8\u7740\u4e94 \u5149\u5341\u8272\u7684\u73cd\u73e0\uff0c\u6e56\u4e2d\u6d6e\u51b0\u7247\u7247\uff0c\u5728\u9633\u5149\u4e0b\u5c06\u5316\u672a\u5316\uff0c\u8000\u773c\u751f\u64b7\u3002\u6842\u534e\u751f\u9053\uff1a\u201c\u85cf\u4eba\u4f20\u8bf4\uff0c\u5ff5 \u9752\u5510\u53e4\u62c9\u5c71\u4e4b\u4e0a\uff0c\u6709\u4e00\u4e2a\u5929\u6e56\uff0c\u679c\u7136\u4e0d\u9519\uff0c\u4f60\u770b\u8fd9\u4e2a\u5927\u6e56\uff0c\u5929\u6c34\u76f8\u8fde\uff0c\u771f\u7684\u50cf\u5728\u5929\u4e0a\u4e00 \u6837\u3002\u201d(\u7fbd\u751f\u6309\uff1a\u8fd9\u4e2a\u5927\u6e56\u5373\u662f\u540e\u6765\u7684\u5730\u7406\u5b66\u5bb6\u52d8\u5bdf\u4e4b\u540e\uff0c\u8ba4\u4e3a\u662f\u4e16\u754c\u7b2c\u4e00\u9ad8\u6e56\u7684\u201c\u817e\u683c\u91cc \u6d77\u201d\u3002\u85cf\u540d\u201c\u7eb3\u6728\u9519\u201d\uff0c\u4ea6\u5373\u201c\u5929\u6e56\u201d\u4e4b\u610f\u3002)\u767d\u8863\u5c11\u5973\u9053\uff1a\u201c\u6b64\u666f\u53ea\u5e94\u5929\u4e0a\u6709\uff0c\u54b1\u4eec\u5230\u4e86 \u8fd9\u513f\uff0c\u4e5f\u50cf\u795e\u4ed9\u4e00\u822c\u4e86\u3002\u53ef\u60dc\u4e0a\u9762\u6ca1\u6709\u4eba\u5c45\u4f4f\u3002\u4f60\u4eec\u4e2d\u56fd\u9676\u6e0a\u660e\u7684\u8bdd\uff1a\u201c\u7ed3\u5e90\u5728\u4eba\u5883\uff0c\u800c\u65e0 \u8f66\u9a6c\u55a7\u3002\u201d\u610f\u5883\u751a\u7f8e\uff0c\u53ef\u60dc\u4ed6\u6240\u60f3\u50cf\u7684\u4e5f\u53ea\u662f\u201c\u5165\u5883\u201d\uff0c\u82e5\u662f\u5728\u592d\u6e56\u4e4b\u4e0a\u7684\u51b0\u5cf0\u7ed3\u5e90\uff0c\u90a3\u5c31 \u662f\u4ed9\u5883\u4e86\u3002\u201d\u4f73\u534e\u751f\u7b11\u9053\uff1a\u201c\u4e8b\u5728\u4eba\u4e3a\uff0c\u5c3c\u6cca\u5c14\u738b\u5b50\u53ef\u4ee5\u5728\u9b54\u9b3c\u57ce\u4e2d\u9020\u5e99\u5efa\u5854\uff0c\u54b1\u4eec\u4e5f\u81ea\u53ef \u4ee5\u5728\u51b0\u5cf0\u4e4b\u4e0a\u9020\u51fa\u697c\u9601\u4ead\u53f0\u3002\u201d\u767d\u8863\u5c11\u5973\u9053\uff1a\u201c\u55ef\uff0c\u4f60\u60f3\u5f97\u771f\u7f8e\uff0c\u6211\u5230\u8fd9\u91cc\uff0c\u4e5f\u5f77\u4f5b\u5230\u4e86\u6211 \u68a6\u4e2d\u7684\u4ed9\u5883\u4e86\u3002\u201d\u53d6\u51fa\u7389\u7b1b\uff0c\u8f7b\u5439\u4e00\u66f2\uff0c\u6842\u534e\u751f\u542c\u90a3\u8c03\u5b50\u6b63\u662f\u82cf\u4e1c\u5761\u7684\u201c\u6c34\u8c03\u6b4c\u5934\u201d\uff0c\u542c\u5230 \u201c\u6211\u6b32\u4e58\u98ce\u5f52\u53bb\uff0c\u53c8\u6050\u743c\u697c\u7389\u5b87\uff0c\u9ad8\u5904\u4e0d\u80dc\u5bd2\uff0c\u8d77\u821e\u5f04\u6e05\u5f71\uff0c\u4f55\u4f3c\u5728\u4eba\u95f4\uff1f\u201d\u7b1b\u58f0\u867d\u505c\uff0c\u9057 \u97f5\u67ad\u67ad\uff0c\u4f46\u89c1\u767d\u8863\u5c11\u5973\u60a0\u7136\u5b58\u601d\uff0c\u604d\u61be\u82e5\u68a6\uff0c\u6b64\u65f6\u6b64\u523b\uff0c\u5374\u4e0d\u77e5\u5979\u5fc3\u4e2d\u60f3\u7684\u4ec0\u4e48\uff1f \u3000\u3000\u8fc7\u4e86\u597d\u4e00\u4f1a\u5b50\uff0c\u767d\u8863\u5c11\u5973\u624d\u597d\u50cf\u4ece\u68a6\u4e2d\u9192\u6765\uff0c\u7b11\u9053\uff1a\u201c\u6211\u8981\u53bb\u627e\u5929\u4e0b\u7b2c\u4e00\u5947\u73cd\uff0c\u5374\u4e0d\u60f3 \u7ed9\u8fd9\u5929\u4e0b\u7b2c\u4e00\u7f8e\u666f\u8ff7\u4f4f\u4e86\u3002\u55ef\uff0c\u54b1\u4eec\u8fd8\u662f\u8d70\u5427\uff01\u201d \u3000\u3000\u7ed5\u8fc7\u51b0\u6e56\uff0c\u8d70\u4e86\u7ea6\u4e00\u4e2a\u65f6\u8fb0\uff0c\u6108\u4e0a\u6108\u9ad8\uff0c\u5c71\u52bf\u4e5f\u6108\u6765\u6108\u9669\uff0c\u4fef\u89c8\u7fa4\u5c71\uff0c\u7247\u7247\u6d6e\u767d\uff0c\u5728\u4e91 \u6c14\u5f25\u6f2b\u4e4b\u4e0b\uff0c\u604d\u5982\u4e91\u6d77\u4e2d\u661f\u7f57\u68cb\u5e03\u7684\u5c9b\u5c7f\u3002\u8fd9\u65f6\u5df2\u662f\u6b63\u5348\u65f6\u5206\uff0c\u4f46\u5bd2\u6c14\u5374\u6108\u6765\u6108\u6d53\uff0c\u767d\u8863\u5c11 \u5973\u5ffd\u9053\uff1a\u201c\u4f60\u542c\uff0c\u4ed6\u4eec\u5728\u90a3\u91cc\u53d1\u6398\u4e86\uff0c\u54b1\u4eec\u6765\u5f97\u6b63\u662f\u65f6\u5019\u3002\u201d\u6842\u534e\u751f\u62ac\u5934\u4e00\u770b\uff0c\u524d\u9762\u662f\u4e00\u5ea7 \u5cfb\u87d1\u7684\u5c71\u5cf0\uff0c\u5c71\u5f62\u50cf\u4e00\u4e2a\u4ead\u4ead\u7389\u7acb\u7684\u5c11\u5973\uff0c\u51dd\u795e\u7ec6\u542c\uff0c\u9690\u7ea6\u6709\u51ff\u77f3\u7684\u58f0\u97f3\uff0c\u597d\u50cf\u5c31\u662f\u4ece\u51b0\u5cf0 \u7684\u5c71\u8179\u91cc\u4f20\u51fa\u6765\u3002 \u3000\u3000\u6842\u534e\u751f\u6ee1\u6000\u7eb3\u95f7\uff0c\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u5230\u5e95\u662f\u4ec0\u4e48\u5b9d\u8d1d\uff0c\u53ef\u79f0\u5f97\u4e0a\u662f\u4e16\u95f4\u5947\u73cd\uff1f\u201d\u767d\u8863\u5c11\u5973 \u9053\uff1a\u201c\u4f60\u4e0d\u4fe1\u4e48\uff1f\u201d\u8981\u4e0d\u662f\u4e16\u95f4\u7b2c\u4e00\u5947\u73cd\uff0c\u85cf\u7075\u4e0a\u4eba\u7109\u80af\u4e3a\u5b83\u8d39\u4e86\u534a\u751f\u5fc3\u529b\u3002\u8fd9\u4ef6\u5b9d\u8d1d\u5c31\u85cf \u5728\u7389\u5973\u5cf0\u7684\u5343\u4e08\u51b0\u7a9f\u4e4b\u4e2d\uff01\u201d\u6842\u534e\u751f\u6108\u542c\u6108\u5947\uff0c\u50ac\u5979\u9053\uff1a\u201c\u597d\u59b9\u5b50\uff0c\u5feb\u70b9\u8bf4\u5427\uff1f\u4f60\u53c8\u662f\u600e\u4e48 \u77e5\u9053\u7684\uff1f\u201d \u3000\u3000\u767d\u8863\u5c11\u5973\u9053\uff1a\u201c\u4e09\u5e74\u524d\u6211\u6709\u7f18\u5f97\u89c1\u5370\u5ea6\u7684\u9f99\u53f6\u5927\u5e08\uff0c\u90a3\u65f6\u6211\u521a\u5b66\u5251\u672f\uff0c\u5411\u4ed6\u8bf7\u76ca\uff0c\u4ed6\u4f20 \u4e86\u6211\u51e0\u70b9\u5185\u529f\u5fc3\u6cd5\uff0c\u5bf9\u4e8e\u5251\u672f\uff0c\u4ed6\u8c26\u8bf4\u4e0d\u662f\u6240\u957f\uff0c\u4e0d\u8fc7\uff0c\u4ed6\u5374\u8fed\u7ed9\u6211\u4e00\u90e8\u68b5\u6587\u79d8\u5178\uff0c\u5185\u4e2d\u5c31 \u8bb0\u8f7d\u6709\u4e00\u4e2a\u795e\u8bdd\u822c\u7684\u79d8\u5bc6\u3002 \u3000\u3000\u201c\u5728\u8fd9\u5ff5\u9752\u5510\u53e4\u62c9\u5c71\u7684\u7389\u5973\u5cf0\u4e0b\uff0c\u6709\u4e00\u4e2a\u51b0\u7a9f\uff0c\u51b0\u7a9f\u91cc\u6709\u7684\u662f\u4ebf\u4e07\u5e74\u6765\u4e98\u53e4\u4e0d\u5316\u7684\u51b0\u96ea \u7cbe\u7075\uff0c\u82e5\u7528\u8fd9\u79cd\u5bd2\u51b0\u5236\u6210\u5200\u5251\uff0c\u575a\u903e\u94a2\u94c1\u3002\u8fd9\u8fd8\u4e0d\u5947\uff0c\u7389\u5973\u5cf0\u672c\u4ea7\u7389\u77f3\uff0c\u51b0\u7a9f\u91cc\u7684\u51b0\u96ea\u7cbe \u7075\uff0c\u4e0e\u7389\u77f3\u51dd\u7ed3\uff0c\u6709\u4e00\u5757\u5927\u7389\u77f3\uff0c\u6b63\u5728\u51b0\u7a9f\u7684\u4e2d\u5fc3\uff0c\u4e0e\u51b0\u5757\u7cbe\u7075\u5316\u800c\u4e3a\u4e00\uff0c\u82e5\u628a\u8fd9\u5757\u7389\u77f3\u6700 \u4e2d\u5fc3\u90a3\u4e00\u90e8\u4efd\u7f8e\u7389\u51ff\u51fa\u6765\uff0c\u6210\u5b9d\u5251\uff0c\u90a3\u4e00\u80a1\u5947\u5bd2\u4e4b\u6c14\uff0c\u5c31\u8db3\u4ee5\u4eca\u4eba\u9000\u907f\u4e09\u820d\uff0c\u4f60\u60f3\u82e5\u5f97\u4e86\u8fd9 \u79cd\u4ebf\u4e07\u5e74\u5bd2\u7389\u6240\u6210\u7684\u51b0\u9b44\u5bd2\u5149\u5251\uff0c\u5c82\u4e0d\u662f\u53ef\u4ee5\u65e0\u654c\u4e8e\u5929\u4e0b\uff01\u201d \u3000\u3000\u6842\u534e\u751f\u4e00\u7b11\u9053\uff1a\u201c\u82e5\u771f\u662f\u5982\u6b64\uff0c\u90a3\u5c31\u662f\u666e\u5929\u4e4b\u4e0b\u6700\u5947\u602a\u7684\u5b9d\u5251\u3002\u4e0d\u8fc7\uff0c\u82e5\u975e\u9ad8\u660e\u4e4b\u58eb\uff0c \u8fd9\u628a\u5251\u5f97\u4e86\u4e5f\u6ca1\u6709\u7528\uff0c\u53cd\u800c\u8981\u51b7\u574f\u4e86\u81ea\u5df1\u3002\u201d\u767d\u8863\u5c11\u5973\u9053\uff1a\u201c\u522b\u8bf4\u5251\u4e86\uff0c\u5c31\u662f\u8fd9\u51b0\uff0c\u4e5f\u4e0d\u662f \u5bfb\u5e38\u4eba\u53ef\u4ee5\u4e0b\u53bb\u7684\u3002\u542c\u8bf4\u85cf\u7075\u4e0a\u4eba\u9047\u6e38\u897f\u85cf\u540d\u5c71\uff0c\u65e0\u610f\u4e2d\u4e5f\u53d1\u73b0\u4e86\u8fd9\u51b0\u7a9f\u7684\u79d8\u5bc6\uff0c\u4ed6\u4e3a\u6b64\u91c7 \u96c6\u4e86\u5404\u79cd\u5947\u836f\uff0c\u70bc\u4e86\u4e00\u79cd\u4e39\u4e38\uff0c\u670d\u4e4b\u53ef\u4ee5\u5fa1\u5bd2\uff0c\u7ecf\u8fc7\u4e86\u51e0\u5341\u5e74\u7684\u51c6\u5907\uff0c\u53c8\u8d39\u4e86\u65e0\u7a77\u5fc3\u8840\uff0c\u6d4b \u51fa\u4e86\u51b0\u7a9f\u7684\u4e2d\u5fc3\u6240\u5728\uff0c\u548c\u5bd2\u6f6e\u6700\u5f31\u7684\u65f6\u8fb0\uff0c\u76f4\u5230\u4eca\u5929\uff0c\u4ed6\u624d\u6562\u5230\u8fd9\u7389\u5973\u5cf0\u6765\u6398\u5b9d\u3002\u201d \u3000\u3000\u6842\u534e\u751f\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u85cf\u7075\u4e0a\u4eba\u4e00\u89c1\u5c3c\u6cca\u5c14\u738b\u5b50\uff0c\u5c31\u95ee\u63d0\u6469\u8fbe\u591a\u548c\u9f99\u53f6\u5927\u5e08\u6709\u6ca1\u6709\u6765\uff1f\u539f \u6765\u4ed6\u662f\u60f3\u627e\u5e2e\u624b\u3002\u201d\u767d\u8863\u5c11\u5973\u9053\uff1a\u201c\u63d0\u6469\u8fbe\u591a\u7ec3\u7684\u662f\u9634\u9633\u638c\u529b\uff0c\u9f99\u53f6\u5927\u5e08\u5219\u662f\u4f5b\u95e8\u9ad8\u5f1f\uff0c\u4ed6 \u4eec\u90fd\u4e0d\u4f1a\u4e0e\u85cf\u7075\u4e0a\u4eba\u4e89\u593a\u8fd9\u628a\u5251\u7684\u3002\u4e0d\u8fc7\u85cf\u7075\u4e0a\u4eba\u7684\u7b97\u76d8\u4e5f\u6253\u5f97\u592a\u5982\u610f\u4e86\uff0c\u50cf\u9f99\u53f6\u5927\u5e08\u8fd9\u7b49 \u9ad8\u4eba\uff0c\u5c82\u80af\u52a9\u4ed6\u6398\u5b9d\uff1f\u201d\u6842\u534e\u751f\u542c\u767d\u8863\u5c11\u5973\u7eb5\u8c08\u5947\u4eba\u5f02\u5b9d\uff0c\u5bf9\u5979\u8eab\u4efd\u66f4\u662f\u6000\u7591\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a \u201c\u5979\u8fd9\u6837\u4e00\u4e2a\u5e74\u7eaa\u8f7b\u8f7b\u7684\u5c11\u5973\uff0c\u9f99\u53f6\u5927\u5e08\u600e\u80af\u628a\u68b5\u6587\u79d8\u5178\u4ed8\u6258\u7ed9\u4ed6\uff0c\u8fd8\u544a\u8bc9\u5979\u8fd9\u4e2a\u5927\u79d8\u5bc6\u3002 \u8fd9\u7b49\u4e16\u95f4\u7684\u9ad8\u4eba\u5f02\u58eb\uff0c\u7adf\u7136\u90fd\u80af\u6298\u8282\u4e0b\u4ea4\uff0c\u5979\u53c8\u662f\u4ec0\u4e48\u4eba\u5462\uff1f\u201d \u3000\u3000\u767d\u8863\u5c11\u5973\u9053\uff1a\u201c\u539f\u6765\u4f60\u5df2\u89c1\u8fc7\u85cf\u7075\u4e0a\u4eba\u4e86\uff0c\u4ed6\u8fd8\u6709\u4ec0\u4e48\u8bf4\u8bdd\u4e0e\u4e3e\u52a8\uff1f\u201d\u6842\u534e\u751f\u9053\uff1a\u201c\u4ed6 \u5411\u5c3c\u6cca\u5c14\u738b\u5b50\u8981\u4e86\u516b\u540d\u6b66\u58eb\u966a\u4ed6\u3002\u201d\u767d\u8863\u5c11\u5973\u9762\u8272\u500f\u53d8\uff0c\u9053\uff1a\u201c\u4ed6\u5927\u7ea6\u662f\u60f3\u501f\u7528\u5c3c\u6cca\u5c14\u6b66\u58eb \u6240\u4f69\u5e26\u7684\u767e\u94a2\u5200\uff0c\u5440\uff01\u6015\u4ed6\u7eb5\u6709\u5fa1\u5bd2\u5947\u836f\uff0c\u8fd9\u516b\u540d\u6b66\u58eb\u4e5f\u7981\u53d7\u4e0d\u4f4f\u90a3\u51b0\u7a9f\u5bd2\u6f6e\u3002\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u5ffd\u542c\u5f97\u5c71\u8179\u4e2d\u4f20\u51fa\u53ee\u53ee\u7684\u94c3\u58f0\uff0c\u8fd9\u65f6\u6842\u534e\u751f\u548c\u767d\u8863\u5c11\u5973\u5df2\u5230\u4e86\u7389\u5973\u638c\u7684\u51b0\u5761 \u4e0a\u9762\uff0c\u6b63\u5bf9\u7740\u51b0\u7a9f\uff0c\u4ed6\u4eec\u8f7b\u529f\u5999\u7edd\uff0c\u5b88\u62a4\u5728\u7a96\u65c1\u8fb9\u7684\u6b66\u58eb\uff0c\u7adf\u7136\u542c\u4e0d\u51fa\u4e00\u70b9\u58f0\u606f\u3002 \u3000\u3000\u4f46\u89c1\u6709\u56db\u4e2a\u5e26\u7740\u6708\u7259\u5f2f\u5200\u7684\u5c3c\u6cca\u5c14\u6b66\u58eb\uff0c\u5728\u51b0\u7a9f\u65c1\u8fb9\u624b\u821e\u8db3\u8e48\uff0c\u5176\u72b6\u751a\u602a\uff0c\u6842\u534e\u751f\u8d77\u521d \u83ab\u540d\u5176\u5999\uff0c\u773c\u5149\u4e00\u77a5\uff0c\u89c1\u767d\u8863\u5c11\u5973\u9762\u6709\u5fe7\u8272\uff0c\u8fd9\u624d\u604d\u7136\u5927\u609f\uff1a\u6562\u60c5\u662f\u8fd9\u56db\u4e2a\u6b66\u58eb\u8010\u4e0d\u4f4f\u51b0\u7a9f \u7684\u5947\u5bd2\uff0c\u6545\u6b64\u8df3\u8dc3\u5982\u72c2\uff0c\u85c9\u4ee5\u589e\u52a0\u4f53\u6e29\u3002 \u3000\u3000\u94c3\u58f0\u6108\u54cd\u6108\u6025\uff0c\u90a3\u56db\u4e2a\u6b66\u58eb\u7a81\u53d1\u602a\u58f0\uff0c\u4e0d\u7ea6\u800c\u540c\u7684\u8dd1\u5230\u6d1e\u53e3\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u626f\u8d77\u56db\u53ea\u540a \u84dd\uff0c\u6bcf\u53ea\u7bee\u4e2d\uff0c\u90fd\u8eba\u7740\u4e00\u4e2a\u9762\u9752\u5507\u767d\u3001\u5944\u5944\u4e00\u606f\u7684\u6b66\u58eb\u3002 \u3000\u3000\u968f\u5728\u540a\u7bee\u4e4b\u540e\uff0c\u85cf\u7075\u4e0a\u4eba\u4e00\u8dc3\u800c\u51fa\uff0c\u8888\u88df\u4e00\u6296\uff0c\u98de\u51fa\u4e86\u6f2b\u5929\u51b0\u5c51\uff0c\u6842\u534e\u751f\u5728\u6570\u5341\u4e08\u4e4b \u5916\uff0c\u4e5f\u81ea\u611f\u5230\u9634\u5bd2\u4e4b\u6c14\uff0c\u77a7\u90a3\u85cf\u7075\u4e0a\u4eba\uff0c\u867d\u7136\u51bb\u5f97\u9762\u8272\u60e8\u767d\uff0c\u4e0d\u8fc7\u4ecd\u662f\u6b65\u5c65\u5b89\u8be6\uff0c\u4e3e\u6b62\u4ece \u5bb9\uff0c\u6842\u534e\u751f\u60f3\u9053\uff1a\u201c\u8fd9\u56db\u4e2a\u6b66\u58eb\u670d\u6709\u5fa1\u5bd2\u7075\u836f\uff0c\u5728\u51b0\u7a9f\u5916\u9762\uff0c\u5c1a\u81ea\u51bb\u5f97\u624b\u821e\u8db3\u8e48\uff0c\u51b0\u7a9f\u4e4b \u4e2d\uff0c\u60f3\u66f4\u662f\u5947\u5bd2\u65e0\u6bd4\uff0c\u8fd9\u85cf\u7075\u4e0a\u4eba\u5c45\u7136\u8fd8\u80fd\u591f\u65bd\u5c55\u4e00\u9e64\u51b2\u5929\u7684\u8f7b\u529f\u51fa\u6765\uff0c\u5185\u529f\u6df1\u539a\uff0c\u786e\u662f\u4e0d \u5bb9\u8f7b\u89c6\uff01\u201d \u3000\u3000\u85cf\u7075\u4e0a\u4eba\u5c06\u540a\u84dd\u4e2d\u7684\u6b66\u58eb\u642c\u5230\u5730\u4e0a\uff0c\u6325\u624b\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u8fd9\u56db\u4e2a\u4e0b\u53bb\uff01\u201d\u539f\u5148\u5b88\u5728\u6d1e\u53e3\u7684 \u56db\u4e2a\u6b66\u58eb\uff0c\u89c1\u540c\u4f34\u51e0\u4e4e\u51bb\u50f5\uff0c\u76f4\u6253\u5bd2\u54a6\uff0c\u90a3\u91cc\u80af\u542c\uff0c\u85cf\u7075\u4e0a\u4eba\u559d\u9053\uff1a\u201c\u4f60\u4eec\u80c6\u6562\u4e0d\u542c\u6211\u7684\u547d \u4ee4\u5417\uff1f\u54fc\uff0c\u54fc\uff0c\u54fc\uff01\u2026\u4f60\u662f\u8c01\uff1f\u201d\u539f\u6765\u5c31\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u767d\u8863\u5c11\u5973\u5df2\u662f\u98de\u8eab\u63a0\u51fa\u3002 \u3000\u3000\u90a3\u56db\u4e2a\u88ab\u85cf\u7075\u4e0a\u4eba\u5a01\u80c1\u7684\u6b66\u58eb\uff0c\u9661\u7136\u95f4\u90fd\u53d1\u51fa\u5c16\u9510\u7684\u53eb\u58f0\uff0c\u968f\u5373\u8dea\u5012\u5730\u4e0a\uff0c\u5411\u767d\u8863\u5c11\u5973 \u5408\u4ec0\u793c\u62dc\u3002\u6842\u534e\u751f\u867d\u7136\u542c\u4e0d\u61c2\u4ed6\u4eec\u7684\u8bf4\u8bdd\uff0c\u4f46\u4ece\u4ed6\u4eec\u90a3\u65e2\u662f\u559c\u60a6\u53c8\u662f\u6050\u60e7\u7684\u795e\u8272\u548c\u58f0\u8c03\uff0c\u4e5f \u731c\u5f97\u5230\u4ed6\u4eec\u662f\u5411\u767d\u8863\u5c11\u5973\u8bf7\u6c42\u6055\u7f6a\u548c\u63f4\u52a9\u3002 \u3000\u3000\u85cf\u7075\u4e0a\u4eba\u773c\u76ae\u4e00\u7ffb\uff0c\u559d\u9053\uff1a\u201c\u6211\u9053\u662f\u8c01\uff0c\u539f\u6765\u4f60\u5c31\u662f\u5728\u9b54\u9b3c\u57ce\u4e2d\u5439\u7b1b\u5413\u4eba\u7684\u5996\u5973\uff0c\u4f60\u6709 \u591a\u5927\u4fee\u4e3a\uff0c\u4e5f\u6562\u89ca\u89ce\u51b0\u7a9f\u7684\u5947\u73cd\uff1f\u201d\u767d\u8863\u5c11\u5973\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u7ba1\u4f60\u4ec0\u4e48\u5947\u73cd\u4e0d\u5947\u73cd\uff0c\u8fd9\u516b \u540d\u6b66\u58eb\u6211\u547d\u4ee4\u4ed6\u4eec\u8fd4\u56fd\u3002\u201d\u85cf\u7075\u4e0a\u4eba\u6012\u5f62\u4e8e\u8272\uff0c\u6253\u91cf\u4e86\u767d\u8863\u5c11\u5973\u4e00\u773c\uff0c\u5374\u5ffd\u7136\u6362\u4e86\u8bed\u8c03\u8bf4 \u9053\uff1a\u201c\u4e5f\u597d\uff0c\u8fd9\u516b\u4e2a\u6b66\u58eb\u672c\u6765\u4e5f\u529e\u4e0d\u4e86\u4ec0\u4e48\u4e8b\uff0c\u4f60\u65e2\u7136\u8981\u6211\u653e\u4ed6\u4eec\uff0c\u4f60\u5c31\u66ff\u4ed6\u4eec\u4e0b\u53bb\u5427\u3002\u6211 \u4e5f\u4e0d\u4f1a\u767d\u767d\u8981\u4f60\u5e2e\u5fd9\uff0c\u51b0\u7a9f\u91cc\u6709\u7684\u662f\u51b0\u5757\u7cbe\u82f1\uff0c\u4f60\u53ef\u4ee5\u53d6\u6765\u5236\u51b0\u5757\u795e\u5f39\u3002\u81f3\u4e8e\u90a3\u5757\u4ebf\u4e07\u65a4\u7684 \u5bd2\u7389\uff0c\u4f60\u53ef\u5c31\u4e0d\u5fc5\u5984\u60f3\u4e86\uff01\u201d \u3000\u3000\u767d\u8863\u5c11\u5973\u51b7\u7b11\u9053\uff1a\u201c\u51b0\u7a9f\u91cc\u7684\u5947\u73cd\u662f\u4f60\u5bb6\u7684\u4e1c\u897f\u4e0d\u6210\uff1f\u8981\u4efb\u4ece\u4f60\u7684\u5206\u914d\uff1f\u201d\u85cf\u7075\u4e0a\u4eba\u6d53 \u7709\u5012\u7f6e\uff0c\u6028\u58f0\u559d\u9053\uff1a\u201c\u6211\u8d39\u4e86\u51e0\u5341\u5e74\u5fc3\u8840\uff0c\u4f60\u5374\u60f3\u6361\u73b0\u6210\uff0c\u5929\u4e0b\u90a3\u6709\u8fd9\u6837\u4fbf\u5b9c\u7684\u4e8b\u60c5\uff1f\u54fc\uff0c \u54fc\uff0c\u4f60\u8fd8\u8bf4\u4e0d\u89ca\u89ce\u51b0\u7a9f\u7684\u5947\u73cd\uff1f\u201d \u3000\u3000\u767d\u8863\u5c11\u5973\u53c8\u662f\u4e00\u58f0\u51b7\u7b11\uff0c\u660e\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u8fd9\u6837\u8bf4\u6cd5\uff0c\u6211\u65e0\u5fc3\u53d8\u4e86\u6709\u5fc3\uff0c\u6211\u5012\u60f3\u628a\u90a3\u51b0\u7a9f \u5bd2\u7389\u53d6\u51fa\u6765\u4e86\u3002\u597d\u5427\uff0c\u54b1\u4eec\u5404\u663e\u795e\u901a\uff0c\u770b\u8c01\u80fd\u628a\u8fd9\u5757\u5bd2\u7389\u53d6\u5230\u624b\u4e2d\uff1f\u201d\u8bdd\u58f0\u672a\u4e86\uff0c\u4f46\u542c\u5f97\u85cf \u7075\u4e0a\u4eba\u4e00\u58f0\u66b4\u559d\uff0c\u98de\u8eab\u75be\u8d77\uff0c\u547c\u7684\u4e00\u638c\uff0c\u51cc\u7a7a\u51fb\u4e0b\uff0c\u767d\u8863\u5c11\u5973\u8f7b\u529f\u7edd\u9876\uff0c\u7109\u80fd\u7ed9\u4ed6\u51fb\u4e2d\uff0c\u4f46 \u662f\u4ed6\u8fd9\u4e00\u638c\u6253\u51fa\uff0c\u5bf9\u9762\u7684\u51b0\u5ca9\u9707\u5f97\u8f70\u7136\u9e23\u52a8\uff0c\u51b0\u5757\u7eb7\u98de\uff0c\u6842\u534e\u751f\u4e5f\u51e0\u4e4e\u7acb\u8db3\u4e0d\u7a33\uff0c\u9a87\u7136\u60f3 \u9053\uff1a\u201c\u85cf\u50e7\u8fd9\u4e00\u638c\u7684\u5a01\u529b\uff0c\u770b\u6765\u6bd4\u5c11\u6797\u6d3e\u7684\u6b66\u6797\u7edd\u5b66\u5927\u529b\u91d1\u521a\u638c\u8fd8\u66f4\u60ca\u4eba\uff0c\u6709\u7f18\u76f8\u9047\uff0c\u6211\u4e5f \u60f3\u8bd5\u4ed6\u4e00\u8bd5\u4e86\uff01\u201d \u3000\u3000\u767d\u8863\u5c11\u5973\u63a5\u8fde\u907f\u4e86\u4ed6\u7684\u4e09\u638c\uff0c\u626c\u58f0\u8bf4\u9053\uff1a\u201c\u5f85\u6211\u5148\u6cbb\u597d\u4e86\u8fd9\u56db\u4e2a\u4eba\u518d\u6765\u548c\u4f60\u6bd4\u5212\u3002\u201d\u85cf \u7075\u4e0a\u4eba\u90a3\u91cc\u80af\u4f9d\uff0c\u4e00\u638c\u7d27\u4f3c\u4e00\u638c\uff0c\u6bcf\u638c\u62cd\u51fa\uff0c\u9690\u9690\u631f\u6709\u98ce\u96f7\u4e4b\u58f0\uff0c\u6253\u5f97\u51b0\u5ca9\u9707\u52a8\uff0c\u7802\u77f3\u7eb7 \u98de\u3002\u5c06\u767d\u8863\u5c11\u5973\u7684\u8eab\u5f62\uff0c\u90fd\u7b3c\u7f69\u5728\u4ed6\u53cc\u638c\u5a01\u529b\u4e4b\u4e0b\uff01 \u3000\u3000\u767d\u8863\u5c11\u5973\u79c0\u7709\u4e00\u6311\uff0c\u7389\u7b1b\u7f13\u7f13\u626c\u8d77\uff0c\u5c31\u5728\u8fd9\u65f6\uff0c\u6842\u534e\u751f\u7acb\u8db3\u7684\u51b0\u5ca9\uff0c\u7ed9\u85cf\u7075\u4e0a\u4eba\u4e00\u638c\u9707 \u584c\uff0c\u6842\u534e\u751f\u8d81\u52bf\u98de\u51fa\uff0c\u5c55\u51fa\u4e86\u8fbe\u6469\u79d8\u7b08\u4e2d\u7684\u201c\u4e94\u79bd\u638c\u6cd5\u201d\uff0c\u534a\u7a7a\u4e2d\u8eab\u5b50\u4e00\u5c48\u4e00\u4f38\uff0c\u53cc\u638c\u5212\u4e86 \u4e00\u9053\u5706\u5f27\uff0c\u4fe8\u5982\u91d1\u9e4f\u5c55\u7fc5\uff0c\u51cc\u7a7a\u76f4\u6251\u4e0b\u6765\u3002 \u3000\u3000\u767d\u8863\u5c11\u5973\u7b11\u9053\uff1a\u201c\u597d\u5427\uff0c\u5927\u54e5\u54e5\uff0c\u4f60\u5c31\u66ff\u6211\u6682\u63a5\u51e0\u62db\uff01\u201d\u8863\u5e26\u8f7b\u98d8\uff0c\u8eab\u6cd5\u7f8e\u5999\u4e4b\u6781\uff0c\u5728 \u4e24\u5927\u9ad8\u624b\u7684\u638c\u5f71\u7ffb\u98de\u4e4b\u4e0b\uff0c\u7adf\u662f\u4ece\u4ece\u5bb9\u5bb9\u7684\u8d70\u51fa\u5708\u5b50\u5916\u9762\u3002\u5979\u53ea\u770b\u4e86\u4e00\u773c\uff0c\u5c31\u77e5\u9053\u6842\u534e\u751f\u7eb5 \u4e0d\u80fd\u80dc\uff0c\u4e5f\u51b3\u4e0d\u81f3\u4e8e\u5728\u4e00\u65f6\u4e09\u523b\u4e4b\u5185\u843d\u8d25\u3002 \u3000\u3000\u85cf\u7075\u4e0a\u4eba\u89c1\u6842\u534e\u751f\u6765\u52bf\u51f6\u731b\uff0c\u987e\u4e0d\u5f97\u62e6\u622a\u767d\u8863\u5c11\u5973\uff0c\u7109\u5730\u4e00\u58f0\u5927\u559d\uff0c\u53cc\u638c\u5e73\u63a8\uff0c\u638c\u529b\u76f8 \u63a5\uff0c\u978b\u7136\u96f7\u9e23\uff0c\u6842\u534e\u751f\u4e00\u4e2a\u7b4b\u6597\u7ffb\u4e86\u4e0b\u6765\uff0c\u85cf\u7075\u4e0a\u4eba\u4e5f\u8ddf\u8ddf\u7684\u5012\u9000\u6570\u6b65\u3002\u8fd9\u4e00\u8f83\u91cf\uff0c\u7adf\u662f\u529f \u529b\u6089\u654c\uff0c\u65a4\u4e24\u76f8\u5f53\u3002\u6842\u534e\u751f\u5fc3\u5934\u6697\u60ca\uff0c\u60f3\u9053\uff1a\u201c\u6211\u501f\u51cc\u7a7a\u4e0b\u51fb\u4e4b\u52bf\uff0c\u4e5f\u4e0d\u80fd\u80dc\u4ed6\u3002\u897f\u57df\u679c\u6709 \u5947\u4eba\uff0c\u770b\u6765\u8fd9\u4e2a\u85cf\u7075\u4e0a\u4eba\u7684\u529f\u529b\u4fbf\u5728\u6211\u4e0a\u3002\u201d \u3000\u3000\u5c82\u77e5\u85cf\u7075\u4e0a\u4eba\u66f4\u662f\u60ca\u5fc3\u52a8\u9b44\uff0c\u4ed6\u81ea\u8d1f\u662f\u897f\u85cf\u7684\u7b2c\u4e00\u9ad8\u624b\uff0c\u5929\u4e0b\u4e4b\u5927\uff0c\u4e5f\u4ec5\u4ec5\u662f\u4f69\u670d\u4e09\u4e2a \u4eba\uff0c\u4e2d\u56fd\u7684\u6613\u5170\u73e0\uff0c\u5370\u5ea6\u7684\u9f99\u53f6\u4e0a\u4eba\uff0c\u963f\u523a\u4f2f\u7684\u63d0\u6469\u8fbe\u591a\u3002\u8fd9\u4e09\u4e2a\u4eba\u90fd\u662f\u767e\u4e16\u7f55\u89c1\u7684\u4e00\u4ee3\u5b97 \u5e08\uff0c\u85cf\u7075\u4e0a\u4eba\u81ea\u662f\u5fc3\u60a6\u8bda\u670d\u3002\u60f3\u4e0d\u5230\u4eca\u665a\u5728\u8fd9\u5ff5\u9752\u5510\u53e4\u62c9\u5c71\u4e4b\u4e0a\uff0c\u7b2c\u4e00\u4e2a\u78b0\u5230\u7684\u767d\u8863\u5c11\u5973\uff0c \u7adf\u662f\u8fde\u5979\u7684\u8863\u89d2\u4e5f\u635e\u4e0d\u7740\uff1b\u7b2c\u4e8c\u4e2a\u78b0\u5230\u7684\u6842\u534e\u751f\uff0c\u786c\u78b0\u786c\u63a5\uff0c\u4e5f\u7adf\u662f\u5360\u4e0d\u4e86\u4e1d\u6beb\u7684\u4fbf\u5b9c\u3002\u800c \u8fd9\u4e2a\u4e24\u4e2a\u4eba\uff0c\u5374\u4e0d\u8fc7\u662f\u4e8c\u5341\u5de6\u53f3\u7684\u9752\u5e74\u7537\u5973\uff01 \u3000\u3000\u85cf\u7075\u4e0a\u4eba\u9a84\u654c\u4e4b\u5fc3\u5c3d\u53bb\uff0c\u8fd0\u4e86\u5168\u529b\uff0c\u53f1\u54c7\u4e00\u58f0\uff0c\u53c8\u662f\u53cc\u638c\u9f50\u51fa\uff0c\u6842\u534e\u751f\u7528\u4e86\u4e00\u62db\u501f\u529b\u53cd \u51fb\u7684\u201c\u53cc\u63a8\u638c\u201d\uff0c\u4f46\u89c9\u85cf\u7075\u4e0a\u4eba\u7684\u638c\u529b\u6709\u5982\u6ce2\u6d6a\u4e00\u822c\u5c42\u5c42\u800c\u81f3\uff0c\u524d\u6d6a\u672a\u6d88\uff0c\u540e\u6d6a\u53c8\u4e0a\uff0c\u4e00\u9677 \u5165\u6f29\u6da1\u4e4b\u4e2d\uff0c\u7adf\u662f\u6d88\u89e3\u4e0d\u4e86\uff0c\u53ea\u597d\u4e00\u53e3\u6c14\u7684\u548c\u4ed6\u786c\u63a5\u4e86\u5341\u591a\u5eff\u62db\u3002 \u3000\u3000\u85cf\u7075\u4e0a\u4eba\u8d8a\u6597\u8d8a\u91cc\uff0c\u53cc\u638c\u7ffb\u98de\u4e4b\u9645\uff0c\u8888\u88df\u4e5f\u6296\u4e86\u8d77\u6765\uff0c\u626c\u8d77\u4e86\u4e09\u80a1\u72c2\u98ce\uff0c\u4e92\u76f8\u51b2\u51fb\u3002\u6842 \u534e\u751f\u559d\u9053\uff1a\u201c\u638c\u6cd5\u8f83\u91cf\u8fc7\u4e86\uff0c\u54b1\u4eec\u518d\u6bd4\u5212\u5175\u5203\uff01\u201d\u85cf\u7075\u4e0a\u4eba\u6709\u610f\u901e\u80fd\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u7528\u4ec0 \u4e48\u5175\u5203\uff0c\u8d2b\u50e7\u4e5f\u53ea\u662f\u4e00\u53cc\u8089\u638c\uff01\u201d \u3000\u3000\u8bdd\u58f0\u672a\u4e86\uff0c\u7a81\u89c1\u4e00\u9053\u7d2b\u8679\uff0c\u7834\u7a7a\u5c04\u51fa\uff0c\u201c\u6ce2\u201d\u7684\u4e00\u58f0\uff0c\u85cf\u7075\u4e0a\u4eba\u638c\u529b\u6240\u6fc0\u8d77\u7684\u6c14\u6d41\uff0c\u5c31 \u50cf\u76ae\u7403\u7ed9\u523a\u7a7f\u4e00\u6837\uff0c\u4e00\u65e0\u9057\u3002\u8fd9\u4e00\u6765\u85cf\u7075\u4e0a\u4eba\u7684\u638c\u529b\u5a01\u52bf\u767b\u65f6\u5927\u51cf\uff0c\u6842\u534e\u751f\u5237\u5237\u5237\u75be\u8fdb\u4e09 \u5251\uff0c\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u5c06\u85cf\u7075\u4e0a\u4eba\u7684\u8888\u88df\u523a\u7834\uff0c\u51b7\u51b7\u7b11\u9053\uff1a\u201c\u8fd8\u662f\u4eae\u51fa\u5175\u5203\u6765\u5427\uff01\u201d\u85cf\u7075\u4e0a\u4eba \u8fd9\u624d\u77e5\u9053\u6842\u534e\u751f\u7684\u5251\u4e43\u662f\u4e00\u628a\u5b9d\u5251\uff0c\u53ea\u51ed\u638c\u529b\uff0c\u4e07\u4e07\u5c01\u95ed\u4e0d\u6765\u3002 \u3000\u3000\u85cf\u7075\u4e0a\u4eba\u8001\u7f9e\u6210\u6012\uff0c\u4e00\u58f0\u51b7\u7b11\u9053\uff1a\u201c\u4e0d\u77e5\u6b7b\u6d3b\u7684\u5c0f\u5b50\uff0c\u8981\u6211\u53d6\u51fa\u5175\u5203\uff0c\u90a3\u5c31\u662f\u8981\u6211\u5c06\u4f60 \u9001\u4e0a\u897f\u5929\u4e86\uff01\u201d\u500f\u7136\u95f4\u53d6\u51fa\u4e86\u4e00\u5bf9\u94dc\u8dcb\uff0c\u53d1\u51fa\u9ec4\u6f84\u6f84\u7684\u5149\u534e\uff0c\u53cc\u8dcb\u4e00\u78b0\uff0c\u9707\u8033\u6b32\u804b\uff0c\u75be\u7684\u5411 \u6842\u534e\u751f\u7684\u5b9d\u5251\u4fbf\u5939\uff0c\u6842\u534e\u751f\u6325\u52a8\u5b9d\u5251\uff0c\u4f46\u542c\u5f97\u65ad\u91d1\u590f\u7389\u4e4b\u58f0\u5ff5\u5509\u4e0d\u7edd\uff0c\u8fd9\u4e00\u5bf9\u94dc\u8dcb\u4e43\u662f\u53e4\u94dc \u52a0\u4e0a\u5176\u4ed6\u5408\u91d1\u6240\u94f8\uff0c\u5b9d\u5251\u7adf\u4e0d\u80fd\u4f24\uff01 \u3000\u3000\u85cf\u7075\u4e0a\u4eba\u6e10\u6e10\u73b0\u51fa\u6025\u71e5\u7684\u795e\u8272\uff0c\u4e00\u53cc\u94dc\u8dcb\u9ad8\u5f97\u9707\u5929\u4ef7\u54cd\uff0c\u90a3\u7b1b\u58f0\u5374\u662f\u8d8a\u6765\u8d8a\u89c1\u67d4\u548c\uff0c\u53ef \u662f\u4efb\u51ed\u90a3\u94dc\u8dcb\u7684\u566a\u58f0\u5982\u4f55\u5f3a\u70c8\uff0c\u90fd\u603b\u662f\u538b\u5b83\u4e0d\u4f4f\u3002\u6842\u534e\u751f\u5fc3\u88e8\u5b81\u9759\uff0c\u542c\u5f97\u7684\u53ea\u662f\u7f8e\u5999\u7684\u7b1b \u58f0\uff0c\u4e00\u67c4\u817e\u86df\u5b9d\u5251\u8d8a\u53d1\u4f7f\u5f97\u6f47\u81ea\u5982\uff0c\u518d\u8fc7\u7247\u523b\uff0c\u7adf\u81ea\u62a2\u4e86\u4e0a\u98ce\uff0c\u5c06\u90a3\u5bf9\u94dc\u8dcb\u538b\u4f4f\uff0c\u85cf\u7075\u4e0a\u4eba \u4e0d\u8bba\u4f7f\u51fa\u4ec0\u4e48\u602a\u5f02\u7684\u62db\u6570\uff0c\u90fd\u88ab\u6842\u534e\u751f\u968f\u624b\u5316\u89e3\uff0c\u800c\u4e14\u7740\u7740\u53cd\u51fb\uff0c\u5c06\u85cf\u7075\u4e0a\u4eba\u8feb\u5f97\u8fde\u8fde\u540e \u9000\u3002 \u3000\u3000\u9661\u7136\u95f4\uff0c\u7b1b\u58f0\u4e00\u8f6c\uff0c\u8d8a\u5439\u8d8a\u9ad8\uff0c\u54cd\u904f\u884c\u4e91\uff0c\u6842\u534e\u751f\u811a\u5c16\u4e00\u70b9\uff0c\u817e\u8eab\u98de\u8d77\uff0c\u65e0\u610f\u4e4b\u4e2d\uff0c\u4e0e \u90a3\u7b1b\u58f0\u914d\u5408\u5f97\u5999\u5230\u6beb\u98a0\uff0c\u4f46\u89c1\u4ed6\u5728\u534a\u7a7a\u4e2d\u4e00\u4e2a\u8f6c\u8eab\uff0c\u500f\u5730\u91cc\u4fbf\u662f\u4e00\u62db\u201c\u98de\u9e1f\u6295\u6797\u201d\uff0c\u51cc\u7a7a\u6740 \u4e0b\uff0c\u90a3\u817e\u86df\u5b9d\u5251\u6240\u6296\u8d77\u7684\u5bd2\u5149\uff0c\u8f68\u5982\u9ed1\u591c\u7e41\u661f\uff0c\u5343\u70b9\u4e07\u70b9\uff0c\u76f4\u9152\u4e0b\u6765\uff01\u53ea\u542c\u5f97\u4e00\u7247\u65ad\u91d1\u590f\u7389 \u4e4b\u58f0\uff0c\u6309\u7740\u4e00\u58f0\u91ce\u72fc\u822c\u7684\u54d4\u53eb\uff0c\u539f\u6765\u85cf\u7075\u4e0a\u4eba\u7684\u4e00\u9762\u94dc\u62d4\u5df2\u88ab\u5b9d\u5251\u523a\u88c2\uff0c\u800c\u4ed6\u8eab\u4e0a\u4e5f\u540c\u65f6\u53d7 \u4e86\u4e03\u5904\u5251\u4f24\u3002 \u3000\u3000\u5f85\u5230\u6842\u534e\u751f\u843d\u5230\u5730\u4e0a\uff0c\u85cf\u7075\u4e0a\u4eba\u4ea6\u5df2\u98de\u9003\u4e0b\u5c71\uff0c\u6842\u534e\u751f\u89c1\u4ed6\u53d7\u4e86\u521b\u4f24\uff0c\u4ecd\u662f\u6377\u5982\u98de\u9e1f\uff0c \u5fc3\u4e2d\u4e5f\u81ea\u9a87\u7136\u3002 \u3000\u3000\u90a3\u767d\u8863\u5c11\u5973\u6536\u4e86\u7389\u7b1b\uff0c\u7f13\u7f13\u8d77\u7acb\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u597d\u5251\u6cd5\uff01\u201d\u6842\u534e\u751f\u9762\u4e0a\u4e00\u7ea2\uff0c\u8bf4\u9053\uff1a \u201c\u4e0d\u662f\u4f60\u7684\u76f8\u52a9\uff0c\u6211\u53ea\u6015\u5df2\u4f24\u5728\u4ed6\u53cc\u8dcb\u4e4b\u4e0b\u3002\u201d\u5c11\u5973\u7b11\u9053\uff1a\u201c\u6211\u5bf9\u4f60\u4f55\u5c1d\u6709\u4ec0\u4e48\u52a9\u529b\uff0c\u90a3\u662f \u4f60\u672c\u8eab\u539f\u6765\u5177\u6709\u7684\u529f\u592b\uff0c\u6bd4\u5982\u706f\u70db\u80fd\u71c3\uff0c\u8427\u7b1b\u53ef\u594f\uff0c\u6211\u4e0d\u8fc7\u5f15\u4ee5\u661f\u661f\u4e4b\u706b\uff0c\u5439\u4ee5\u4e1d\u4e1d\u4e4b\u6c14\u800c \u5df2\uff0c\u4f55\u8db3\u79f0\u9053\uff1f\u201d\u6842\u534e\u751f\u542c\u5979\u8bed\u5e26\u7985\u673a\uff0c\u5185\u8574\u5999\u7406\uff0c\u51dd\u795e\u4e00\u60f3\uff0c\u8c41\u7136\u987f\u609f\uff0c\u5408\u4ec0\u8d5e\u9053\uff1a\u201c\u706f \u70db\u81ea\u71c3\u8427\u81ea\u594f\uff0c\u5916\u9b54\u672c\u662f\u7a7a\u65e0\u6709\uff0c\u8981\u5f85\u9a71\u9b54\u843d\u4e0b\u4e58\uff0c\u6211\u95fb\u6b64\u8a00\u4e09\u987f\u9996\u3002\u201d\u767d\u8863\u5c11\u5973\u7b11\u9053\uff1a \u201c\u5927\u54e5\u54e5\u5999\u89e3\u7985\u7406\uff0c\u6b66\u529f\u4e0a\u53c8\u8fdb\u4e86\u4e00\u5c42\u4e86\u3002\u8bf4\u8001\u5b9e\u8bdd\uff0c\u8bba\u672c\u8eab\u529f\u529b\uff0c\u6211\u8fd8\u5f53\u771f\u4e0d\u662f\u85cf\u7075\u4e0a\u4eba \u7684\u5bf9\u624b\u5462\uff1f\u5927\u54e5\u54e5\uff0c\u4f60\u7684\u5251\u6cd5\u786e\u662f\u7cbe\u5999\uff0c\u90a3\u662f\u4e2d\u571f\u6240\u4f20\u7684\u5417\uff1f\u201d\u6842\u534e\u751f\u9053\uff1a\u201c\u4e0d\uff0c\u6070\u6070\u76f8 \u53cd\uff0c\u90a3\u662f\u4ece\u897f\u571f\u4f20\u6765\u7684\u8fbe\u6469\u5251\u6cd5\uff0c\u4e0d\u8fc7\uff0c\u7ecf\u8fc7\u4e86\u4e00\u5343\u591a\u5e74\uff0c\u5386\u4ee3\u540d\u5bb6\u53c8\u6709\u4e0d\u5c11\u589e\u76ca\uff0c\u5927\u7ea6\u6bd4 \u8fbe\u6469\u79df\u5e08\u6700\u521d\u6240\u4f20\u7684\u5251\u672f\uff0c\u66f4\u89c1\u5b8c\u5907\u4e86\u3002\u201d\u767d\u8863\u5c11\u5973\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u6240\u95ee\u7684\u5012\u662f\u843d\u4e86\u4e0b\u4e58 \u4e86\u3002\u4e2d\u571f\u897f\u571f\u672c\u6765\u5c31\u4e0d\u5e94\u5206\u5f00\uff0c\u4e16\u754c\u5404\u6d3e\u7684\u6b66\u529f\uff0c\u90fd\u53ef\u4ee5\u5408\u800c\u4e3a\u4e00\u3002\u201d\u6842\u534e\u751f\u5fc3\u4e2d\u4e00\u52a8\uff0c\u7b11 \u9053\uff1a\u201c\u6211\u6628\u665a\u89c1\u4f60\u7528\u7389\u7b1b\u4f7f\u51fa\u6781\u7cbe\u5999\u7684\u5251\u672f\uff0c\u4eca\u5927\u6211\u5f00\u773c\u754c\uff0c\u4f69\u670d\u65e0\u5df2\u3002\u82e5\u7136\u54b1\u4eec\u8fd9\u4e24\u5bb6\u5251 \u672f\uff0c\u5408\u800c\u4e3a\u4e00\uff0c\u7eb5\u4e0d\u80fd\u79f0\u96c4\u5929\u4e0b\uff0c\u60f3\u6765\u65b9\u53ef\u4e3a\u6b66\u5b66\u5927\u589e\u5149\u5f69\uff01\u201d\u767d\u8863\u5c11\u5973\u9053\uff1a\u201c\u662f\u4e48\uff1f\u201d\u51dd \u671b\u51b0\u5cf0\uff0c\u5ffd\u5730\u9ed8\u7136\u65e0\u8bed\u3002\u8fc7\u4e86\u597d\u4e00\u4f1a\u5b50\uff0c\u624d\u5730\u51fa\u7684\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u53bb\u4f4f\u968f\u7f18\uff0c\u4f60\u8fd9\u756a\u8bdd\u4e5f\u5f85\u5c06 \u6765\u6709\u7f18\u4e4b\u65f6\u518d\u8bf4\u5427\u3002\u201d\u6842\u534e\u751f\u4e00\u7247\u60af\u7136\uff0c\u62ac\u5934\u4e00\u770b\uff0c\u4f46\u89c1\u65e5\u6b63\u5f53\u5934\uff0c\u51b0\u5cf0\u5728\u9633\u5149\u4e0b\u73b0\u51fa\u5343\u91cd \u4e3d\u5f69\uff0c\u81ea\u5df1\u7684\u5f71\u5b50\u548c\u767d\u8863\u5c11\u5973\u7684\u5f71\u5b50\u5728\u51b0\u5cf0\u4e0b\u51e0\u4e4e\u53e0\u800c\u4e3a\u4e00\uff0c\u6b64\u666f\u6b64\u60c5\uff0c\u5982\u5e7b\u5982\u68a6\uff0c\u5fc3\u4e2d\u4f46 \u613f\u5e7b\u666f\u4e0d\u706d\uff0c\u597d\u68a6\u957f\u7559\u3002 \u3000\u3000\u767d\u8863\u5c11\u5973\u56de\u7738\u4e00\u76fc\uff0c\u8bf4\u9053\uff1a\u201c\u73b0\u5728\u6b63\u662f\u5348\u65f6\uff0c\u5bd2\u6f6e\u6700\u5f31\uff0c\u54b1\u4eec\u8be5\u8fdb\u53bb\u4e86\u3002\u201d\u6842\u534e\u751f\u968f\u5979 \u8d70\u8fdb\u51b0\u7a96\uff0c\u4f46\u89c1\u4e00\u7247\u5bd2\u5149\uff0c\u7a9f\u5185\u51b7\u98ce\u7f15\u7f15\uff0c\u89e6\u4f53\u5982\u5200\u3002\u767d\u8863\u5c11\u5973\u7b11\u9053\uff1a\u201c\u9f99\u53f6\u5927\u5e08\u7ed9\u6211\u7684\u68b5 \u6587\u79d8\u7b08\uff0c\u672c\u6709\u5236\u70bc\u5fa1\u5bd2\u7684\u79d8\u65b9\uff0c\u6211\u4e5f\u70bc\u4e86\u4e03\u7c92\u9633\u548c\u4e38\uff0c\u4e0d\u8fc7\u6211\u4eec\u8fd8\u662f\u8bd5\u51ed\u672c\u8eab\u7684\u529f\u529b\u770b\u770b\u3002 \u82e5\u662f\u8981\u9760\u7075\u4e39\uff0c\u5c06\u6765\u4e5f\u4e0d\u80fd\u7528\u8fd9\u51b0\u9b44\u5bd2\u5149\u5251\u3001\u548c\u4f7f\u90a3\u51b0\u5757\u795e\u5f39\u5462\u3002\u201d \u3000\u3000\u6842\u534e\u751f\u5728\u6d1e\u53e3\u4e00\u5f20\uff0c\u4f46\u89c1\u767d\u832b\u832b\u4e00\u7247\uff0c\u7aef\u7684\u4f3c\u795e\u8bdd\u4e2d\u7684\u4ed9\u5e9c\uff0c\u96fe\u9501\u4e91\u8f6c\u3002\u6842\u534e\u751f\u5c06\u4e00\u5757 \u77f3\u5934\u4e22\u4e0b\uff0c\u542c\u4e0d\u89c1\u58f0\u54cd\uff0c\u770b\u6765\u771f\u662f\u6df1\u4e0d\u53ef\u6d4b\u3002\u767d\u8863\u5c11\u5973\u9053\uff1a\u201c\u4f60\u6015\u4e48\uff1f\u201d\u6842\u534e\u751f\u7b11\u9053\uff1a\u201c\u6709 \u4f60\u4e00\u8d77\uff0c\u6211\u8fd8\u6709\u4ec0\u4e48\u6015\u7684\uff1f\u201d\u62d4\u51fa\u817e\u86df\u5b9d\u5251\u63d2\u5165\u51b0\u58c1\uff0c\u652f\u6301\u4f53\u91cd\uff0c\u65bd\u5c55\u5251\u638c\u4ea4\u66ff\u7684\u5de5\u592b\uff0c\u6cbf \u7740\u51b0\u58c1\u6e9c\u4e0b\uff0c\u9047\u5230\u7279\u522b\u5e73\u6ed1\u4e4b\u5904\uff0c\u5c31\u4ee5\u58c1\u864e\u6e38\u5899\u529f\u5411\u4e0b\u6ed1\u884c\uff0c\u770b\u90a3\u767d\u8863\u5c11\u5973\u65f6\uff0c\u5374\u662f\u4e0d\u7528\u82b1 \u8d39\u5982\u8bb8\u6c14\u529b\uff0c\u4f46\u89c1\u5979\u5f20\u5f00\u53cc\u624b\uff0c\u8d34\u7740\u51b0\u58c1\uff0c\u5411\u4e0b\u6ed1\u884c\uff0c\u7adf\u662f\u5982\u9c7c\u6e38\u6c34\uff0c\u987a\u5229\u4e4b\u6781\uff0c\u500f\u5ffd\u4e4b \u95f4\uff0c\u5c31\u8d76\u8fc7\u4e86\u81ea\u5df1\u7684\u524d\u5934\uff0c\u6842\u534e\u751f\u5fc3\u4e2d\u6697\u53f9\uff1a\u6211\u81ea\u8d1f\u82f1\u96c4\uff0c\u5374\u7adf\u4e0d\u53ca\u4e00\u4e2a\u5c11\u5973\u3002\u5374\u4e0d\u77e5\u5c3c\u6cca \u5c14\u4e43\u662f\u51b0\u96ea\u4e4b\u56fd\uff0c\u6e9c\u51b0\u6ed1\u96ea\u7684\u73a9\u610f\u513f\u4e09\u5c81\u5b69\u7ae5\u4e5f\u4f1a\uff0c\u767d\u8863\u5c11\u5973\u5728\u51b0\u58c1\u4e0a\u6ed1\u884c\uff0c\u5f53\u7136\u8981\u80dc\u8fc7\u6842 \u534e\u751f\u3002\u4e0d\u8fc7\u50cf\u5979\u8fd9\u6837\u65e0\u6240\u51ed\u4f9d\uff0c\u811a\u4e0b\u65e2\u6ca1\u6709\u88c5\u4e0a\u6ed1\u51b0\u7684\u978b\u5b50\uff0c\u624b\u4e0a\u4e5f\u6ca1\u6709\u201c\u51b0\u6316\u201d\uff08\u53ef\u52fe\u7740 \u51b0\u58c1\uff0c\u51cf\u5c0f\u7206\u901f\u7684\u4e00\u79cd\u767b\u5c71\u7528\u5177\u3002\uff09\uff0c\u5c45\u7136\u5982\u9c7c\u6e38\u6c34\uff0c\u8fd9\u79cd\u672c\u9886\uff0c\u786e\u4e5f\u9700\u8981\u6781\u4e0a\u4e58\u7684\u8f7b\u529f\u3002 \u3000\u3000\u8fc7\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u767d\u8863\u5c11\u5973\u5148\u5230\u4e0b\u9762\uff0c\u811a\u8e0f\u5b9e\u5730\uff0c\u62ac\u5934\u4e00\u671b\uff0c\u4f73\u534e\u751f\u8fd8\u5728\u534a\u7a7a\uff0c\u767d\u8863\u5c11\u5973 \u5fae\u5fae\u4e00\u7b11\uff0c\u6279\u51fa\u4e00\u6761\u5f69\u7ef3\uff0c\u957f\u53ef\u5341\u4e08\uff0c\u7ecf\u5979\u5185\u529b\u6325\u52a8\uff0c\u5176\u76f4\u5982\u592b\uff0c\u6842\u534e\u751f\u4e00\u4e2a\u201c\u9e64\u5b50\u7ffb\u8eab\u201d \u98de\u6251\u4e0b\u6765\uff0c\u6293\u7740\u5f69\u7ef3\uff0c\u4e5f\u65bd\u5c55\u4e86\u6781\u4e0a\u4e58\u7684\u201c\u4e00\u82c7\u6e21\u6c5f\u201d\u7684\u7edd\u9876\u8f7b\u529f\uff0c\u501f\u7740\u5f69\u7ef3\u4e00\u6c64\u4e4b\u529b\uff0c\u5f80 \u4e0b\u98de\u5760\uff0c\u767d\u8863\u5c11\u5973\u6536\u77ed\u5f69\u7ef3\uff0c\u8f6c\u77ac\u4e4b\u95f4\u5c31\u628a\u6842\u534e\u751f\u63a5\u4e0b\u6765\u3002 \u3000\u3000\u8fd9\u65f6\u5df2\u5728\u51b0\u7a9f\u4e4b\u4e2d\uff0c\u5bd2\u6c14\u66f4\u6d53\uff0c\u6842\u534e\u751f\u8c03\u5300\u547c\u5438\uff0c\u8fd0\u6c14\u4e00\u8f6c\uff0c\u4e0e\u767d\u8863\u5c11\u5973\u7f13\u7f13\u8d70\u5165\uff0c\u4f46 \u89c1\u56db\u8fb9\u90fd\u662f\u6c34\u6676\u822c\u7684\u51b0\u5ca9\u51b0\u58c1\uff0c\u5c31\u50cf\u5343\u767e\u9762\u660e\u955c\uff0c\u5c42\u5c42\u53cd\u5c04\uff0c\u4e24\u4eba\u7684\u5f71\u5b50\u5728\u51b0\u58c1\u4e0a\u91cd\u53e0\u51fa \u73b0\uff0c\u51e0\u4e4e\u5206\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u8d70\u4e86\u597d\u4e00\u4f1a\uff0c\u5149\u7ebf\u6e10\u6e10\u51cf\u5f31\uff0c\u5bd2\u610f\u66f4\u6d53\uff0c\u518d\u8fc7\u4e00\u4f1a\uff0c\u8fde\u51b0\u58c1\u6240\u53d1\u7684\u90a3\u79cd\u5e7d\u51b7\u7684\u6e05\u5149\u4e5f\u6ca1 \u6709\u4e86\uff0c\u6842\u534e\u751f\u4f46\u89c9\u624b\u8db3\u9ebb\u6728\uff0c\u547c\u5438\u4e5f\u6e10\u6e10\u6709\u70b9\u56f0\u96be\u3002\u767d\u8863\u5c11\u5973\u9053\uff1a\u201c\u8fd9\u91cc\u7684\u51b0\u5c42\u90fd\u5df2\u5316\u6210\u5ca9 \u77f3\uff0c\u4e0d\u50cf\u5916\u9762\u7684\u51b0\u5ca9\u6709\u65b0\u51dd\u7684\u5bd2\u51b0\u3002\u68b5\u6587\u79d8\u5178\u4e2d\u79f0\u547c\u8fd9\u79cd\u51b0\u5c42\u4e3a\u4e07\u8f7d\u7384\u51b0\uff0c\u5176\u5b9e\u4f55\u4e0a\u4e07 \u8f7d\uff1f\u201d\u6842\u534e\u751f\u7528\u5b9d\u5251\u4e00\u5212\uff0c\u5272\u51fa\u4e00\u5757\u201c\u51b0\u5757\u201d\uff0c\u4f46\u89c1\u575a\u786c\u9edc\u9ed1\uff0c\u679c\u7136\u50cf\u662f\u77f3\u5934\uff0c\u4f46\u63e1\u5728\u624b \u4e2d\uff0c\u5374\u662f\u5947\u5bd2\u6f88\u9aa8\uff0c\u6025\u5fd9\u629b\u4e86\u3002 \u3000\u3000\u4e24\u4eba\u501f\u7740\u5b9d\u5251\u7684\u5149\u8292\uff0c\u518d\u5411\u524d\u8d70\uff0c\u7ea6\u83ab\u8d70\u4e86\u4e00\u987f\u996d\u7684\u529f\u592b\uff0c\u5ffd\u7136\u53c8\u5177\u773c\u775b\u4e00\u4eae\uff0c\u524d\u9762\u53d1 \u51fa\u7eff\u83b9\u83b9\u7684\u5e7d\u5149\uff0c\u767d\u8863\u5c11\u5973\u9053\uff1a\u201c\u5bd2\u7389\u5ca9\u5df2\u5728\u9762\u524d\uff0c\u54b1\u4eec\u5c31\u53ef\u4ee5\u53d1\u6398\u90a3\u5757\u4ebf\u4e07\u5e74\u7684\u5bd2\u7389\u4e86\u3002 \u5927\u54e5\u54e5\uff0c\u4f60\u53d7\u5f97\u4e86\u5417\uff1f\u201d\u6842\u534e\u751f\u51bb\u5f97\u7259\u5173\u6253\u6218\uff0c\u4f46\u542c\u5f97\u767d\u8863\u5c11\u5973\u7684\u6e29\u8a00\u8f6f\u8bed\uff0c\u6709\u5982\u4e00\u9053\u6696\u6d41 \u4ece\u5fc3\u5e95\u7f13\u7f13\u6d41\u8fc7\uff0c\u767b\u65f6\u5bd2\u610f\u51cf\u4e86\u8bb8\u591a\u3002 \u3000\u3000\u524d\u9762\u88f9\u571f\u7740\u4e00\u5757\u5927\u5ca9\u77f3\uff0c\u6709\u5982\u7eff\u7389\u5c4f\u98ce\uff0c\u4e24\u8fb9\u5374\u662f\u9edc\u9ed1\u7684\u7384\u51b0\u51b0\u58c1\uff0c\u767d\u8863\u5c11\u5973\u8ba9\u6842\u534e\u751f \u7528\u5b9d\u5251\u5c06\u51b0\u58c1\u4e0a\u9762\u524a\u53bb\u4e00\u5c42\uff0c\u767b\u65f6\u5bd2\u5149\u56db\u5c04\uff0c\u5c06\u51b0\u7a9f\u7167\u8000\u5f97\u5982\u540c\u767d\u663c\uff0c\u767d\u8863\u5c11\u5973\u9053\uff1a\u201c\u8fd9\u4e9b \u90fd\u662f\u4e00\u4e98\u4e00\u53e4\u4e0d\u5316\u7684\u5bd2\u96ea\u7cbe\u82f1\uff0c\u82e5\u662f\u6210\u4e86\u51b0\u5757\u795e\u5f39\uff0c\u90a3\u5c31\u662f\u5929\u4e0b\u7b2c\u4e00\u7b49\u5389\u5bb3\u7684\u6697\u5668\uff01\u201d \u3000\u3000\u90a3\u5757\u5bd2\u7389\u5c71\u77f3\u4e0a\u6709\u8bb8\u591a\u5200\u75d5\uff0c\u767d\u8863\u5c11\u5973\u7b11\u9053\uff1a\u201c\u6211\u4eec\u7684\u6208\u514b\u5229\u5200\u867d\u7136\u950b\u5229\uff0c\u5374\u90a3\u80fd\u5207\u5f00 \u5bd2\u7389\u3002\u82e5\u8981\u51ff\u5c71\u53d6\u5b9d\uff0c\u6700\u5c11\u4e5f\u5f97\u82b1\u51e0\u5e74\u529f\u592b\u3002\u85cf\u7075\u4e0a\u4eba\u5927\u7ea6\u6ca1\u6709\u6599\u5230\u5bd2\u7389\u5ca9\u5982\u6b64\u575a\u786c\uff0c\u5e78\u4e8f \u4ed6\u6ca1\u6709\u5b9d\u5251\u3002\u5927\u54e5\u54e5\uff0c\u8fd9\u56de\u53ef\u8981\u4ef0\u4ed7\u4f60\u4e86\uff01\u201d \u3000\u3000\u6842\u534e\u751f\u62d4\u51fa\u5b9d\u5251\u5f80\u5bd2\u7389\u5ca9\u4e0a\u524a\u53bb\uff0c\u7247\u7247\u7389\u77f3\uff0c\u5e94\u624b\u800c\u843d\uff0c\u524a\u4e86\u4e00\u76cf\u837c\u7684\u65f6\u523b\uff0c\u5251\u5c16\u7ec7 \u7269\uff0c\u94ff\u7136\u6709\u58f0\uff0c\u7adf\u662f\u524a\u4e4b\u4e0d\u52a8\uff0c\u767d\u8863\u5c11\u5973\u9053\uff1a\u201c\u5c06\u5b9d\u5251\u7ed9\u6211\u3002\u201d\u5c0f\u5fc3\u7ffc\u7ffc\u7684\u7528\u5b9d\u5251\u5728\u5ca9\u4e2d\u5fc3 \u90a3\u5757\u5bd2\u7389\u7684\u5468\u56f4\uff0c\u5212\u4e86\u4e00\u9053\u5251\u75d5\uff0c\u4e0e\u6842\u534e\u751f\u5e76\u80a9\u800c\u7acb\uff0c\u65bd\u5c55\u5927\u529b\u5eb9\u722a\u529f\uff0c\u7528\u529b\u4e00\u6293\uff0c\u4f46\u89c9\u5947 \u5bd2\u900f\u9aa8\uff0c\u4e24\u4eba\u5404\u8fd0\u771f\u6c14\u62b5\u5fa1\uff0c\u75be\u559d\u4e00\u58f0\uff0c\u90a3\u5757\u7389\u77f3\u5e94\u624b\u800c\u8d77\uff0c\u662f\u4e00\u5757\u4e09\u5c3a\u89c1\u65b9\u7684\u78a7\u7389\uff0c\u901a\u4f53 \u6676\u83b9\uff0c\u5bd2\u5149\u95ea\u95ea\u3002\u767d\u8863\u5c11\u5973\u559c\u5f62\u4e8e\u8272\uff0c\u8bf4\u9053\uff1a\u201c\u5927\u54e5\u54e5\uff0c\u8fd9\u6b21\u53d6\u5f97\u4e07\u5e74\u5bd2\u7389\uff0c\u5168\u4ed7\u4f60\u7684\u5b9d \u5251\uff0c\u8fd9\u5757\u5bd2\u7389\uff0c\u4f60\u53ef\u4ee5\u53d6\u53bb\u70bc\u5251\uff0c\u5c06\u6765\u53ef\u4ee5\u65e0\u654c\u5929\u4e0b\u3002\u201d\u6842\u534e\u751f\u7b11\u9053\uff1a\u201c\u8981\u4e0d\u662f\u78b0\u5230\u4f60\uff0c\u6211 \u6839\u672c\u5c31\u4e0d\u77e5\u9053\u8fd9\u51b0\u7a9f\u6240\u5728\uff0c\u9051\u8bba\u53d6\u7389\uff0c\u518d\u8bf4\uff0c\u6211\u4eec\u4e2d\u56fd\u6709\u53e5\u8001\u8bdd\uff0c\u53eb\u505a\u7269\u8f7b\u60c5\u91cd\uff0c\u4f60\u8981\u5c06\u5bd2 \u7389\u9001\u6211\uff0c\u8fd9\u4efd\u60c5\u610f\uff0c\u5c31\u6bd4\u5bd2\u7389\u672c\u8eab\u8981\u8d35\u91cd\u5f97\u591a\uff0c\u6211\u5fc3\u9886\u4f60\u7684\u60c5\u610f\uff0c\u5df2\u662f\u7ec8\u751f\u96be\u5fd8\uff01\u201d\u767d\u8863\u5c11 \u5973\u9053\uff1a\u201c\u4f60\u771f\u4f1a\u8bf4\u8bdd\u3002\u8fd9\u4e48\u8bf4\u6211\u5012\u662f\u975e\u8981\u4e0d\u53ef\u4e86\u3002\u201d\u53d6\u51fa\u4e00\u4e2a\u9526\u56ca\uff0c\u5c06\u90a3\u5757\u5bd2\u7389\u653e\u4e86\u8fdb\u53bb\u3002 \u6842\u534e\u751f\u9053\uff1a\u201c\u8fd9\u9526\u56ca\u662f\u4ec0\u4e48\u505a\u7684\uff1f\u5149\u6cfd\u60a6\u76ee\uff0c\u597d\u50cf\u4e0d\u662f\u666e\u901a\u7684\u9526\u7ee3\u3002\u201d\u767d\u8863\u5c11\u5973\u9053\uff1a\u201c\u8fd9\u662f \u897f\u5929\u7afa\u7684\u5929\u8695\u4e1d\u505a\u7684\uff0c\u6c34\u706b\u4e0d\u6d78\uff0c\u4f60\u77a7\u5bd2\u7389\u653e\u5728\u5176\u4e2d\uff0c\u5bd2\u6c14\u4e00\u70b9\u4e5f\u6ca1\u6709\u900f\u51fa\u6765\u3002\u201d\u6842\u534e\u751f\u6478 \u6478\u679c\u7136\uff0c\u8bf4\u9053\uff1a\u201c\u65e2\u6709\u8fd9\u6837\u7684\u5b9d\u56ca\uff0c\u4f60\u5c31\u5c06\u8fd9\u91cc\u7684\u51b0\u9b44\u7cbe\u82f1\u4e5f\u6293\u4e9b\u8fdb\u53bb\uff0c\u5c06\u6765\u4e5f\u597d\u5236\u70bc\u51b0\u9b44 \u795e\u5f39\u3002\u201d\u767d\u8863\u5c11\u5973\u9053\uff1a\u201c\u6b63\u662f\u3002\u201d\u6309\u7740\u53c8\u7b11\u9053\uff1a\u201c\u6211\u8fd9\u6b21\u662f\u6ee1\u8f7d\u800c\u5f52\uff0c\u53ea\u53ef\u60dc\u4f60\u5374\u662f\u5982\u5165\u5b9d \u5c71\u7a7a\u624b\u56de\u4e86\u3002\u201d \u3000\u3000\u6842\u534e\u751f\u7528\u5251\u518d\u524a\u4e0b\u51e0\u7247\u5bd2\u7389\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u51e0\u7247\u7389\u867d\u7136\u4e0d\u80fd\u70bc\u5251\uff0c\u53ef\u4e5f\u597d\u73a9\u5f97\u5f88\u3002\u201d\u767d\u8863 \u5c11\u5973\u5ffd\u9053\uff1a\u201c\u5927\u54e5\u54e5\uff0c\u4f60\u4e14\u4f4f\u624b\uff0c\u77a7\uff0c\u8fd9\u662f\u4ec0\u4e48\uff1f\u201d\u4f46\u89c1\u5728\u5bd2\u7389\u5ca9\u7684\u4e0a\u65b9\uff0c\u6709\u51e0\u884c\u5947\u5f62\u602a\u72b6 \u7684\u6587\u5b57\uff0c\u767d\u8863\u5c11\u5973\u4ed4\u7ec6\u7aef\u8be6\uff0c\u5931\u58f0\u53eb\u9053\uff1a\u201c\u8fd9\u662f\u68b5\u6587\uff0c\u5199\u7684\u662f\u51b0\u5757\u5bd2\u5149\u5251\u7684\u7528\u6cd5\u3002\u8fd9\u4f4d\u5927 \u5e08\uff0c\u6b63\u662f\u7740\u79d8\u7b08\u7684\u90a3\u4f4d\u5370\u5ea6\u524d\u4ee3\u9ad8\u50e7\u3002\u4ed6\u5f53\u65f6\u53d1\u73b0\u6b64\u5b9d\uff0c\u56e0\u4e3a\u6ca1\u6709\u5b9d\u5200\u5b9d\u5251\uff0c\u53d6\u4e4b\u4e0d\u51fa\uff0c\u5374 \u8fd8\u80af\u7559\u5728\u7a9f\u4e2d\u5fcd\u53d7\u5947\u5bd2\u4e4b\u82e6\uff0c\u7814\u7a76\u5bd2\u7389\u7684\u6027\u80fd\uff0c\u5199\u51fa\u7528\u6cd5\uff0c\u6307\u70b9\u540e\u5b66\uff0c\u771f\u771f\u53ef\u4f69\uff01\u201d\u5f53\u4e0b\u76d8 \u819d\u800c\u751f\uff0c\u9ed8\u8bfb\u90a3\u5ca9\u4e0a\u7684\u7ecf\u6587\uff0c\u5e76\u7167\u90a3\u7ecf\u6587\u6240\u8bf4\uff0c\u7ec3\u4e60\u62b5\u6297\u5bd2\u6c14\u7684\u5410\u7eb3\u5999\u6cd5\u3002 \u3000\u3000\u6842\u534e\u751f\u4ed7\u5251\u5728\u65c1\u5b88\u62a4\uff0c\u5bd2\u6c14\u900f\u9aa8\u653b\u5fc3\uff0c\u6e10\u89c9\u96be\u4ee5\u5fcd\u53d7\uff0c\u4ed6\u4eec\u5728\u51b0\u7a9f\u4e0d\u77e5\u65f6\u523b\uff0c\u539f\u6765\u5348\u65f6 \u5df2\u8fc7\uff0c\u6b64\u9645\u5df2\u5c06\u662f\u508d\u665a\u7684\u65f6\u5206\uff0c\u51b0\u7a9f\u4e2d\u5bd2\u6f6e\u6b63\u76db\uff0c\u8981\u4e0d\u662f\u6842\u534e\u751f\u5b66\u7684\u4e43\u662f\u8fbe\u6469\u79df\u5e08\u6240\u4f20\u4e0b\u7684 \u6b63\u5b97\u5185\u529f\uff0c\u65e9\u5df2\u51bb\u50f5\uff01 \u3000\u3000\u6842\u534e\u751f\u6b63\u5728\u51dd\u795e\u8fd0\u6c14\uff0c\u62b5\u5fa1\u5bd2\u6f6e\uff0c\u5ffd\u542c\u5f97\u5916\u9762\u6709\u201c\u64e6\u549b\u201d\u7684\u58f0\u97f3\uff0c\u6842\u534e\u751f\u662f\u6b66\u5b66\u7684\u5927\u884c \u5bb6\uff0c\u4e00\u542c\u5c31\u77e5\u9053\u6709\u8f7b\u529f\u7edd\u9876\u7684\u9ad8\u624b\u6765\u4e86\uff0c\u4e0d\u7981\u5927\u5403\u4e00\u60ca\uff0c\u60f3\u9053\uff1a\u201c\u5c45\u7136\u8fd8\u6709\u4eba\u6709\u8fd9\u822c\u80fd\u8010\uff0c \u6562\u5728\u5bd2\u6f6e\u6b63\u76db\u4e4b\u65f6\uff0c\u8fdb\u5165\u51b0\u7a9f\uff01\u201d \u3000\u3000\u5fc3\u5ff5\u65b9\u52a8\uff0c\u602a\u58f0\u5df2\u8d77\uff0c\u6709\u5982\u67ad\u9e23\uff0c\u6842\u534e\u751f\u4e00\u8dc3\u800c\u524d\uff0c\u62ac\u5934\u4e00\u770b\uff0c\u4f46\u89c1\u4e00\u4e2a\u602a\u4eba\uff0c\u8eab\u5982\u67af \u7af9\uff0c\u9762\u989d\u6df1\u9677\uff0c\u53cc\u773c\u5982\u706b\uff0c\u53d1\u4f3c\u98de\u84ec\uff0c\u76f8\u8c8c\u731d\u67e0\uff0c\u89c1\u6240\u672a\u89c1\u3002\u8fd9\u8fd8\u4e0d\u8db3\u9a87\u5f02\uff0c\u6700\u4ee4\u4eba\u9a87\u5f02\u7684 \u662f\uff1a\u4f46\u89c1\u4ed6\u53cc\u638c\u547c\u547c\u4e71\u5288\uff0c\u6321\u5728\u8eab\u524d\u7684\u5bd2\u51b0\u7adf\u7136\u5982\u9047\u9a84\u9633\uff0c\u89e6\u624b\u800c\u5316\u3002\u8bd5\u60f3\u8fd9\u79cd\u4e07\u8f7d\u7384\u51b0\uff0c \u5373\u7b97\u7528\u5e73\u5e38\u5200\u5251\u6765\u524a\uff0c\u4e5f\u524a\u4e4b\u4e0d\u52a8\uff0c\u7136\u800c\u7adf\u88ab\u4ed6\u638c\u98ce\u4e00\u626b\uff0c\u7adf\u7136\u5316\u6c34\u800c\u878d\uff0c\u5c82\u975e\u5947\u7edd\uff01 \u3000\u3000\u6842\u534e\u751f\u65b9\u81ea\u60ca\u8be7\uff0c\u53ea\u89c1\u90a3\u602a\u4eba\u602a\u773c\u4e00\u7ffb\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u4e24\u4e2a\u5a03\u5a03\u597d\u5927\u7684\u80c6\u5b50\uff0c\u7adf\u6562 \u6f5c\u5165\u7389\u5973\u5cf0\u6765\u53d6\u5b9d\uff01\u201d\u6842\u534e\u751f\u7b11\u9053\uff1a\u201c\u8fd9\u662f\u4ebf\u4e07\u5e74\u6765\u65e0\u4e3b\u4e4b\u7269\uff0c\u8c01\u6709\u672c\u9886\u90fd\u53ef\u6765\u53d6\uff0c\u4f60\u7ba1\u5f97 \u7740\u4e48\uff1f\u201d \u3000\u3000\u90a3\u602a\u4eba\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u90a3\u5757\u4ebf\u4e07\u5e74\u7684\u5bd2\u7389\u4f60\u4eec\u5df2\u62ff\u5230\u624b\u4e86\uff1f\u201d \u6842\u534e\u751f\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u5f85\u600e\u4e48\uff1f\u201d\u90a3\u602a\u4eba\u9053\uff1a\u201c\u62ff\u6765\u7ed9\u6211\uff01\u201d\u6842\u534e\u751f\u5927\u7b11\u9053\uff1a\u201c\u5929\u4e0b\u90a3\u6709\u8fd9 \u6837\u4fbf\u5b9c\u7684\u4e8b\u60c5\uff1f\u6211\u4eec\u5c3d\u8d39\u5fc3\u8840\u53d6\u5f97\u7684\u4e1c\u897f\u8981\u8fed\u7ed9\u4f60\uff0c\u51ed\u4ec0\u4e48\u8981\u9001\u7ed9\u4f60\uff1f\u201d \u3000\u3000\u90a3\u602a\u4eba\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u6709\u672c\u9886\u5230\u51b0\u7a9f\u4e2d\u53d6\u5b9d\uff0c\u6211\u4fbf\u6709\u672c\u9886\u4ece\u4f60\u4eec\u624b\u4e2d\u593a\u5b9d\u3002\u51ed\u4ec0\u4e48\uff1f\u5c31\u51ed \u6211\u8fd9\u53cc\u638c\uff01\u201d \u3000\u3000\u90a3\u602a\u4eba\u4e00\u9762\u53d1\u8bdd\uff0c\u4e00\u9762\u8d70\u6765\uff0c\u76f8\u8ddd\u8fd8\u6709\u5341\u4f59\u4e08\u8fdc\uff0c\u8bf4\u5230\u672b\u4e86\u4e00\u53e5\uff0c\u5ffd\u5730\u98de\u8eab\u75be\u8d77\uff0c\u8eab\u6cd5 \u4e4b\u5feb\uff0c\u65e0\u4ee5\u5f62\u5bb9\uff0c\u201c\u638c\u201d\u5b57\u521a\u521a\u51fa\u53e3\uff0c\u90a3\u53cc\u84b2\u6247\u822c\u7684\u624b\u638c\uff0c\u5df2\u62cd\u5230\u773c\u524d\u3002 \u3000\u3000\u4f46\u89c1\u90a3\u53cc\u624b\u638c\u9c9c\u7ea2\u5982\u8840\uff0c\u597d\u50cf\u5265\u5f00\u4e86\u4e00\u5c42\u76ae\u4f3c\u7684\uff0c\u6842\u534e\u751f\u867d\u7136\u65e9\u6709\u9632\u5907\uff0c\u4ea6\u662f\u5927\u5403\u4e00 \u60ca\uff0c\u5f53\u4e0b\u817e\u86df\u5251\u4e00\u62db\u201c\u76f4\u6307\u5929\u5357\u201d\uff0c\u8fce\u7740\u638c\u5fc3\u4fbf\u523a\uff0c\u90a3\u602a\u4eba\u4f3c\u4e4e\u77e5\u9053\u5b9d\u5251\u5389\u5bb3\uff0c\u624b\u8155\u4e00\u7ffb\uff0c \u638c\u52bf\u98d8\u5ffd\uff0c\u773c\u524d\u7ea2\u5f71\u95ea\u52a8\uff0c\u638c\u98ce\u547c\u547c\uff0c\u540c\u7740\u6842\u534e\u751f\u6251\u9762\u800c\u8fc7\u3002 \u3000\u3000\u602a\u4eba\u602a\u638c\uff0c\u5df2\u4eca\u4eba\u60ca\uff0c\u4f46\u8fd8\u6709\u66f4\u9a87\u4eba\u7684\uff0c\u4ed6\u90a3\u638c\u98ce\uff0c\u70ed\u547c\u547c\u7684\uff0c\u7adf\u7136\u50cf\u662f\u4ece\u9f13\u98ce\u7089\u4e2d\u55b7 \u51fa\u4e00\u822c\uff01\u6842\u534e\u751f\u8fde\u95ea\u6570\u62db\uff0c\u5ffd\u5730\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u6562\u60c5\u5c31\u662f\u96ea\u5c71\u5996\u4eba\u8d64\u795e\u5b50\uff1f\u201d \u3000\u3000\u539f\u6765\u8fd9\u8d64\u795e\u5b50\u662f\u6a2a\u884c\u5eb7\u85cf\u8fb9\u5883\u4e4b\u95f4\u7684\u4e00\u4e2a\u5927\u9b54\u5934\uff0c\u5341\u4f59\u5e74\u524d\uff0c\u88ab\u5929\u5c71\u4e0a\u5251\u4e4b\u4e00\u7684\u6b66\u743c\u7476 \u6253\u8d25\uff0c\u8feb\u4ee4\u4ed6\u5728\u96ea\u5c71\u81ea\u7701\uff0c\u4e0d\u8bb8\u590d\u51fa\u3002\u8d64\u795e\u5b50\u90a3\u80af\u7518\u5fc3\uff0c\u53ef\u662f\u6b66\u743c\u7476\u7684\u672c\u9886\u6bd4\u4ed6\u5927\u5f97\u591a\uff0c\u4ed6 \u8feb\u4e8e\u65e0\u5948\uff0c\u53ea\u597d\u5728\u5927\u96ea\u5c71\u4e0a\u533f\u8ff9\u6f5c\u8e2a\uff0c\u5374\u7528\u5341\u4f59\u5e74\u7684\u529f\u592b\uff0c\u82e6\u7ec3\u8d64\u795e\u9b54\u638c\uff0c\u7ec3\u6cd5\u602a\u5f02\u65e0\u4f26\uff0c \u8981\u5c06\u56db\u80a2\u76ae\u80a4\u5265\u53bb\uff0c\u7528\u6bd2\u8349\u71ac\u6c41\u6d17\uff0c\u6545\u6b64\u624b\u8db3\u90fd\u9c9c\u7ea2\u5982\u8840\uff0c\u89e6\u4eba\u5373\u6b7b\uff0c\u800c\u4e14\u53ef\u4ee5\u7528\u90aa\u529f\uff0c\u5c06 \u4f53\u5185\u7684\u771f\u9633\u4e4b\u6c14\uff0c\u4ece\u638c\u5fc3\u8ffd\u51fa\u3002\u8d64\u795e\u5b50\u7ec3\u8fd9\u79cd\u602a\u5f02\u65e0\u4f26\u7684\u9b54\u638c\uff0c\u672c\u6765\u662f\u51c6\u5907\u7528\u6765\u5bf9\u4ed8\u6b66\u743c\u7476 \u7684\uff0c\u5374\u6599\u4e0d\u5230\uff0c\u9b54\u638c\u8fd8\u672a\u7ec3\u5f97\u5927\u6210\uff0c\u6b66\u743c\u7476\u548c\u6613\u5170\u73e0\u90fd\u5df2\u76f8\u7ee7\u53bb\u4e16\uff0c\u4ed6\u81ea\u4ee5\u4e3a\u5929\u4e0b\u4ece\u6b64\u6ca1\u6709 \u80fd\u5236\u670d\u4ed6\u7684\u4eba\uff0c\u4e8e\u662f\u518d\u4e0b\u96ea\u5c71\uff0c\u91cd\u5230\u897f\u85cf\uff0c\u7b2c\u4e00\u4e2a\u4fbf\u627e\u4ed6\u7684\u65e7\u53cb\u85cf\u7075\u4e0a\u4eba\uff0c\u6253\u542c\u4e00\u4e9b\u8fd1\u5e74\u6765 \u7684\u6d88\u606f\u3002 \u3000\u3000\u85cf\u7075\u4e0a\u4eba\u5728\u5ff5\u9752\u5510\u53e4\u62c9\u5c71\u811a\u5c71\u4e0b\u9047\u5230\u4ed6\uff0c\u5176\u65f6\u6070\u5de7\u662f\u85cf\u7075\u4e0a\u4eba\u88ab\u6842\u534e\u751f\u6253\u8d25\u4e4b\u540e\uff0c\u85cf\u7075 \u4e0a\u4eba\u9042\u5bf9\u8d64\u795e\u5b50\u8bf4\uff0c\u4f60\u522b\u4ee5\u4e3a\u9b54\u638c\u7ec3\u6210\uff0c\u4fbf\u53ef\u65e0\u654c\u5929\u4e0b\uff0c\u8fd9\u795e\u7389\u5973\u5cf0\u7684\u51b0\u7a9f\u4e4b\u4e2d\uff0c\u662f\u4e00\u5757\u4ebf \u4e07\u5e74\u7684\u5bd2\u7389\uff0c\u4fbf\u6070\u5de7\u662f\u4f60\u7684\u514b\u661f\uff0c\u73b0\u4e0b\u6b63\u6709\u4eba\u5728\u51b0\u7a9f\u4e2d\u53d6\u5b9d\uff0c\u51c6\u5907\u7ec3\u6210\u51b0\u5757\u5bd2\u5149\u5251\u6765\u5236\u4f60\u6b7b \u547d\u3002\u4e00\u756a\u8bf4\u8bdd\uff0c\u6fc0\u5f97\u8d64\u795e\u5b50\u7acb\u523b\u8d76\u6765\uff0c\u9020\u4eba\u51b0\u7a9f\uff0c\u8981\u627e\u53d6\u5b9d\u7684\u4eba\u62da\u547d\u3002 \u3000\u3000\u6842\u534e\u751f\u4e0e\u767d\u8863\u5c11\u5973\u5982\u6b64\u5e74\u8f7b\uff0c\u5927\u51fa\u8d64\u795e\u5b50\u610f\u5916\uff0c\u4e0d\u8fc7\uff0c\u63a5\u4e86\u6570\u62db\uff0c\u8d64\u795e\u5b50\u4fbf\u77e5\u9053\u6842\u534e\u751f \u5c5e\u4e8e\u5929\u5c71\u4e0a\u5251\u4e2d\u7684\u4e00\u652f\uff0c\u4e0e\u6614\u65e5\u7684\u5927\u4ec7\u4eba\u6b66\u743c\u7476\u6b63\u662f\u4e00\u5bb6\uff0c\u5f53\u4e0b\u65e2\u60ca\u4e8e\u6842\u534e\u751f\u7684\u7cbe\u5999\u5251\u672f\uff0c \u53c8\u6fc0\u8d77\u65e7\u4ec7\u65b0\u6068\uff0c\u4e8e\u662f\u628a\u90a3\u8d64\u5764\u9b54\u638c\u7684\u5a01\u529b\uff0c\u5c3d\u91cf\u53d1\u6325\u3002 \u3000\u3000\u6842\u534e\u751f\u82e6\u82e6\u62b5\u6321\uff0c\u70ed\u98ce\u6240\u81f3\uff0c\u7384\u51b0\u98de\u6e85\uff0c\u4f73\u534e\u751f\u4f46\u89c9\u5ffd\u51b7\u5ffd\u70ed\uff0c\u6216\u5947\u5bd2\u5947\u70ed\uff0c\u540c\u65f6\u88ad \u81f3\uff0c\u82e5\u975e\u4ed6\u5185\u529f\u6df1\u539a\uff0c\u65e9\u5df2\u660f\u8ff7\uff0c\u9976\u662f\u5982\u6b64\uff0c\u4e5f\u89c9\u547c\u5438\u4e0d\u7545\uff0c\u4f53\u529b\u6e10\u75b2\uff0c\u4fe8\u5982\u5927\u75c5\u4e00\u822c\uff0c\u56de \u9996\u770b\u90a3\u767d\u8863\u5c11\u5973\uff0c\u5374\u8fd8\u5728\u76d8\u819d\u9759\u5750\uff0c\u5bf9\u8fd9\u4e00\u5207\uff0c\u7adf\u4f3c\u4e0d\u89c1\u4e0d\u95fb\u3002 \u3000\u3000\u8d64\u795e\u5b50\u638c\u52bf\u8d8a\u6765\u8d8a\u7d27\uff0c\u70ed\u98ce\u547c\u547c\uff0c\u8fde\u756a\u731b\u5377\uff0c\u6842\u534e\u751f\u4f7f\u51fa\u6d51\u8eab\u672c\u9886\uff0c\u4ee5\u7edd\u5999\u7684\u8eab\u6cd5\u95ea \u907f\uff0c\u4f46\u8d64\u795e\u5b50\u8fd9\u79cd\u6b66\u529f\u592a\u8fc7\u90aa\u95e8\uff0c\u6842\u534e\u751f\u867d\u7136\u95ea\u907f\u5f97\u5b9c\uff0c\u4e0d\u8ba9\u4ed6\u7684\u602a\u638c\u89e6\u53ca\u8eab\u4f53\uff0c\u4f46\u6574\u4e2a\u8eab \u5f62\uff0c\u5374\u59cb\u7ec8\u662f\u5728\u4ed6\u638c\u98ce\u7b3c\u7f69\u4e4b\u4e0b\u3002\u800c\u4e14\u8fd9\u65f6\u5bd2\u6f6e\u6b63\u76db\uff0c\u5947\u5bd2\u5947\u70ed\uff0c\u76f8\u7ee7\u88ad\u6765\uff0c\u6842\u534e\u751f\u547c\u5438\u56f0 \u96be\uff0c\u5934\u660f\u76ee\u7729\uff0c\u7a81\u611f\u5730\u8f6c\u5929\u65cb\uff0c\u770b\u770b\u5c31\u8981\u652f\u6301\u4e0d\u4f4f\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u767d\u8863\u5c11\u5973\u53eb\u9053\uff1a\u201c\u5927\u54e5\u54e5\u56de\u6765\uff0c\u522b\u518d\u7406\u4f1a\u8fd9\u4e2a\u602a\u4eba\uff01\u201d\u55e4\u55e4\u58f0\u54cd\uff0c\u4e00\u9897\u9897\u597d\u50cf\u73cd \u73e0\u5341\u5929\u3001\u4eae\u6676\u6676\u7684\u51b0\u5f39\u7a81\u7136\u4ece\u7a7a\u4e2d\u6d12\u4e0b\uff0c\u88ab\u70ed\u98ce\u4e00\u6c64\uff0c\u6761\u5ffd\u788e\u88c2\u6210\u7c89\uff0c\u767b\u65f6\u6563\u51fa\u4e00\u56e2\u5bd2\u5149\u51b7 \u6c14\uff0c\u8d64\u795e\u5b50\u7981\u4e0d\u4f4f\u673a\u4f36\u4f36\u7684\u6253\u4e86\u4e00\u4e2a\u51b7\u6218\uff0c\u638c\u52bf\u7a0d\u7f13\uff0c\u6842\u534e\u751f\u4e00\u62db\u201c\u795e\u9f99\u6389\u5c3e\u201d\u53cd\u624b\u4e00\u5251\uff0c \u5c06\u8d64\u795e\u5b50\u8feb\u9000\u51e0\u6b65\uff0c\u7acb\u5373\u817e\u8eab\u98de\u8d77\uff0c\u8131\u51fa\u4e86\u8d64\u795e\u5b50\u638c\u529b\u7b3c\u7f69\u7684\u8303\u56f4\uff0c\u56de\u5230\u4e86\u767d\u8863\u5c11\u5973\u8eab\u65c1\u3002 \u3000\u3000\u8d64\u795e\u5b50\u53c8\u60ca\u53c8\u6012\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u85cf\u7075\u4e0a\u4eba\u8bf4\u7684\u679c\u7136\u4e0d\u5047\uff0c\u8fd9\u51b0\u9b44\u795e\u5f39\u5df2\u7ecf\u8fd9\u6837\u5389\u5bb3\uff0c\u82e5 \u662f\u7ed9\u5979\u5c06\u5bd2\u7389\u70bc\u6210\u4e86\u51b0\u5757\u5bd2\u5149\u5251\uff0c\u90a3\u91cc\u8fd8\u6709\u6211\u7acb\u8db3\u4e4b\u5730\uff01\u201d\u6740\u673a\u9661\u8d77\uff0c\u4e00\u58f0\u5927\u543c\uff0c\u72e0\u72e0\u7684\u6251 \u4e0a\u524d\u6765\u3002 \u3000\u3000\u767d\u8863\u5c11\u5973\u5f85\u4ed6\u6251\u5230\u79bb\u8eab\u6570\u4e08\u4e4b\u5730\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6789\u4f60\u6d3b\u5230\u8fd9\u822c\u5e74\u7eaa\uff0c\u5140\u662f\u4e0d\u77e5\u9053 \u8fdb\u9000\uff0c\u5984\u52a8\u65e0\u660e\uff0c\u4f55\u82e6\u6765\u54c9\uff01\u201d\u7389\u624b\u4e00\u626c\uff0c\u4e03\u7c92\u51b0\u5f39\u8fde\u53d1\uff0c\u8d64\u795e\u5b50\u597d\u50cf\u53d1\u72c2\u7684\u91ce\u517d\uff0c\u7a81\u88ab\u730e \u4eba\u63d2\u4e86\u51e0\u67aa\uff0c\u4e00\u58f0\u5389\u53eb\uff0c\u53cc\u773c\u706b\u7ea2\uff0c\u867d\u662f\u6012\u706b\u51b2\u5929\uff0c\u5374\u4e0d\u7531\u5f97\u4ed6\u4e0d\u8fde\u8fde\u7f29\u9000\u3002\u539f\u6765\u4ed6\u5df2\u6709\u4e09 \u5904\u5927\u7a74\uff0c\u6070\u6070\u88ab\u51b0\u5f39\u6253\u4e2d\uff0c\u90a3\u80a1\u5947\u5bd2\u4e4b\u6c14\uff0c\u5faa\u7740\u7a74\u9053\uff0c\u76f4\u653b\u5fc3\u5934\uff01 \u3000\u3000\u8d64\u795e\u5b50\u7ec3\u7684\u90aa\u95e8\u5185\u529f\uff0c\u672c\u4eba\u53ef\u4ee5\u5c06\u4f53\u5185\u7684\u771f\u6c14\uff0c\u51dd\u6210\u4e00\u7247\uff0c\u53d1\u51fa\u70ed\u529b\uff0c\u867d\u53d7\u51b0\u5f39\u6253\u4e2d\uff0c \u4ecd\u53ef\u652f\u6301\u5f97\u4f4f\uff0c\u5f53\u4e0b\u76d8\u819d\u9759\u5750\uff0c\u8fd0\u6c14\u4e09\u8f6c\uff0c\u8feb\u6563\u4e86\u4f53\u5185\u7684\u5bd2\u6c14\uff0c\u53c8\u662f\u4e00\u58f0\u6012\u543c\uff0c\u72c2\u6251\u9762\u524d\u3002 \u3000\u3000\u5c82\u77e5\u8fd9\u79cd\u4e00\u4e98\u4e00\u53e4\u4e0d\u5316\u7684\u51b0\u5757\u7cbe\u82f1\uff0c\u6240\u8574\u85cf\u7684\u9634\u51b7\u4e4b\u6c14\uff0c\u9664\u975e\u7ec3\u6b63\u5b97\u5185\u529f\u7684\u4eba\uff0c\u5e76\u4e14\u5df2 \u7ec3\u5230\u4e86\u901a\u7384\u4e4b\u5883\uff0c\u6216\u8bb8\u8fd8\u53ef\u62b5\u53d7\uff0c\u800c\u5177\u6709\u8fd9\u79cd\u529f\u529b\u7684\u9ad8\u660e\u4e4b\u58eb\uff0c\u5bf0\u5b87\u4e4b\u5185\uff0c\u4ea6\u4e0d\u8fc7\u662f\u6709\u9650\u51e0 \u4eba\u3002\u8d64\u795e\u5b50\u6240\u7ec3\u7684\u9b54\u638c\u795e\u529f\uff0c\u867d\u7136\u53ef\u4ee5\u6682\u65f6\u76f8\u6297\uff0c\u65f6\u95f4\u7a0d\u957f\uff0c\u7ec8\u662f\u652f\u6301\u4e0d\u4f4f\uff01 \u3000\u3000\u4f46\u89c1\u767d\u8863\u5c11\u5973\u7684\u51b0\u5f39\u8d8a\u6253\u8d8a\u6025\uff0c\u8d64\u795e\u5b50\u6709\u5982\u4e00\u53ea\u65e0\u5934\u82cd\u8747\u5728\u7a97\u7eb8\u4e0a\u4e71\u98de\u4e71\u649e\uff0c\u5374\u603b\u662f\u94bb \u4e0d\u8fc7\u53bb\u3002\u5728\u4ed6\u548c\u767d\u8863\u5c11\u5973\u4e4b\u95f4\uff0c\u4fbf\u4f3c\u5e03\u4e86\u4e00\u5c42\u51b0\u5e55\u4f3c\u7684\uff0c\u4efb\u662f\u70ed\u98ce\u547c\u547c\uff0c\u5374\u603b\u5439\u4e0d\u6563\u90a3\u51b7\u96fe \u5bd2\u5149\u3002\u8d64\u795e\u5b50\u53d1\u51fa\u70ed\u98ce\uff0c\u987b\u8981\u8017\u635f\u672c\u8eab\u771f\u529b\uff0c\u800c\u767d\u8863\u5c11\u5973\u7684\u51b0\u5f39\u5374\u662f\u53d6\u4e4b\u4e0d\u5c3d\uff0c\u7528\u4e4b\u4e0d\u7aed\uff0c \u66f4\u4f55\u51b5\u8d64\u795e\u5b50\u7684\u90aa\u95e8\u5185\u529f\uff0c\u4e0d\u8fc7\u7ec3\u4e86\u5341\u591a\u5e74\uff0c\u706b\u5019\u4e5f\u8fd8\u672a\u5230\u7089\u706b\u7eaf\u9752\u4e4b\u5883\u3002 \u3000\u3000\u518d\u8fc7\u7247\u523b\uff0c\u5bd2\u6c14\u6fc0\u8361\uff0c\u8d8a\u6765\u8d8a\u6d53\uff0c\u53ea\u89c1\u8d64\u795e\u5b50\u72c2\u547c\u75be\u821e\uff0c\u5982\u4e2d\u75af\u9b54\uff0c\u5374\u53c8\u5168\u8eab\u98a4\u6296\u3002\u6842 \u534e\u751f\u4e0d\u7981\u9a87\u7136\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u4e16\u95f4\u6697\u5668\uff0c\u6216\u7528\u4ee5\u4f24\u4eba\u6216\u7528\u4ee5\u6253\u7a74\uff0c\u6240\u8bb2\u7a76\u7684\u4e0d\u5916\u51c6\u5934\u548c\u52b2 \u529b\uff0c\u72ec\u6709\u8fd9\u79cd\u51b0\u5f39\uff0c\u5374\u4ee5\u5947\u5bd2\u4f24\u4eba\uff0c\u5f53\u771f\u662f\u5929\u4e0b\u72ec\u4e00\u65e0\u4e8c\u7684\u5947\u602a\u6697\u5668\u4e86\uff01\u201d \u3000\u3000\u767d\u8863\u5c11\u5973\u5c55\u989c\u7b11\u9053\uff1a\u201c\u770b\u4f60\u53ef\u601c\uff0c\u9976\u4f60\u53bb\u5427\uff01\u201d\u7389\u624b\u4e00\u626c\uff0c\u98de\u51fa\u4e86\u4e09\u679a\u51b0\u5f39\uff0c\u8d64\u795e\u5b50\u4e00 \u4e2a\u7b4b\u6597\u5012\u7ffb\uff0c\u5934\u4e5f\u4e0d\u56de\uff0c\u75be\u5954\u800c\u53bb\u3002\u767d\u8863\u5c11\u5973\u7b11\u9053\uff1a\u201c\u6700\u540e\u8fd9\u4e09\u679a\u51b0\u5f39\u90fd\u6253\u4e2d\u4e86\u5b83\u7684\u7075\u67a2 \u7a74\uff0c\u53eb\u4ed6\u6839\u672c\u4e0d\u80fd\u518d\u8fd0\u771f\u6c14\uff0c\u82e5\u662f\u4e03\u5f39\u9f50\u53d1\uff0c\u7acb\u523b\u53ef\u53d6\u4ed6\u6027\u547d\uff0c\u4ed6\u77e5\u9053\u5389\u5bb3\uff0c\u662f\u4ee5\u8d70\u4e86\uff01\u201d \u3000\u3000\u6842\u534e\u751f\u9053\uff1a\u201c\u82e5\u4e0d\u662f\u4f60\u53ca\u65f6\u51fa\u624b\uff0c\u6211\u53ea\u6015\u5df2\u4f24\u5728\u90a3\u9b54\u5934\u7684\u602a\u638c\u4e4b\u4e0b\u3002\u201d\u60f3\u8d77\u9002\u624d\u7684\u5947\u5bd2 \u9177\u70ed\uff0c\u72b9\u6709\u4f59\u6016\uff0c\u4f46\u89c9\u56db\u80a2\u65e0\u529b\uff0c\u5fc3\u795e\u4e0d\u5b9a\uff0c\u4e0d\u81ea\u7981\u7684\u6253\u4e86\u51e0\u4e2a\u5bd2\u895f\uff0c\u767d\u8863\u5c11\u5973\u5fae\u5fae\u4e00\u7b11\uff0c \u638f\u51fa\u4e00\u4e2a\u94f6\u74f6\uff0c\u53d6\u4e86\u4e00\u7c92\u78a7\u7eff\u8272\u7684\u4e39\u4e38\uff0c\u9012\u7ed9\u6842\u534e\u751f\u9053\uff1a\u201c\u4f60\u5728\u8d64\u795e\u5b50\u9b54\u638c\u4e4b\u4e0b\uff0c\u6597\u4e86\u4e00\u767e \u4f59\u62db\uff0c\u72b9\u81ea\u652f\u6301\u5f97\u4f4f\uff0c\u5185\u529f\u6df1\u539a\uff0c\u8fdc\u5728\u6211\u4e0a\uff0c\u53ef\u60dc\u54b1\u4eec\u76f8\u805a\u7684\u65e5\u5b50\u65e0\u591a\uff0c\u8981\u4e0d\u7136\u6211\u5012\u8981\u5411\u4f60 \u597d\u597d\u7684\u9886\u6559\u5462\u3002\u201d\u6842\u534e\u751f\u5fc3\u5185\u4e00\u9178\uff0c\u7f13\u7f13\u5ff5\u9053\uff1a\u201c\u4eba\u95f4\u96be\u5f97\u4e24\u76f8\u6295\uff0c\u95ee\u541b\u4f55\u6545\u8f7b\u8a00\u522b\uff1f\u201d\u767d \u8863\u5c11\u5973\u7b11\u9053\uff1a\u201c\u4f60\u5fd8\u4e86\u6211\u8bf4\u8fc7\u7684\u53bb\u4f4f\u968f\u7f18\u7684\u8bdd\u4e48\uff1f\u4e16\u95f4\u90a3\u6709\u4e0d\u656c\u7684\u7b75\u5e2d\uff0c\u4f60\u82e5\u662f\u5982\u6b64\u6267\u7740\uff0c \u6211\u5c31\u53ea\u6709\u63d0\u65e9\u8d70\u4e86\u3002\u55ef\uff0c\u5feb\u5c06\u8fd9\u7c92\u4e39\u4e38\u670d\u4e0b\u5427\u3002\u201d\u8fd9\u51e0\u53e5\u8bdd\u8bf4\u5f97\u8d85\u8131\u975e\u5e38\uff0c\u8fd1\u4f3c\u7985\u673a\uff0c\u4f46\u5374 \u53c8\u4f3c\u6697\u85cf\u60c5\u610f\uff0c\u6842\u534e\u751f\u4e00\u7247\u832b\u7136\uff0c\u4e0d\u6562\u591a\u8bdd\uff0c\u5c06\u90a3\u7c92\u4e39\u4e38\u4e0b\uff0c\u4f46\u89c9\u4e00\u7f15\u5e7d\u9999\uff0c\u6c81\u4eba\u80ba\u810f\uff0c\u7cbe \u795e\u52c3\u632f\uff0c\u8eab\u4f53\u4e5f\u6696\u548c\u8d77\u6765\u3002\u767d\u8863\u5c11\u5973\u9053\uff1a\u201c\u4f60\u4e0d\u8fc7\u5143\u6c14\u7a0d\u7a0d\u53d7\u635f\uff0c\u90a3\u8d64\u795e\u5b50\u5374\u5fc5\u5b9a\u8981\u5927\u75c5\u4e00 \u573a\u3002\u4f60\u518d\u9759\u5750\u8fd0\u529f\uff0c\u5f85\u5230\u5bd2\u6f6e\u51cf\u5f31\u4e4b\u65f6\uff0c\u54b1\u4eec\u518d\u51fa\u51b0\u7a9f\u3002\u201d \u3000\u3000\u6842\u534e\u751f\u6742\u5ff5\u9891\u751f\uff0c\u60f3\u8d77\u8fd9\u767d\u8863\u5c11\u5973\u7684\u8bf8\u822c\u795e\u79d8\uff0c\u90a3\u91cc\u9759\u5750\u5f97\u7a33\uff0c\u5ffd\u542c\u5f97\u767d\u8863\u5c11\u5973\u5728\u4ed6\u8033 \u8fb9\u8f7b\u8f7b\u5ff5\u9053\uff1a\u201c\u83e9\u63d0\u975e\u6811\uff0c\u660e\u955c\u975e\u53f0\uff0c\u9b54\u7531\u5fc3\u8d77\uff0c\u81ea\u67d3\u5c18\u57c3\u3002\u201d\u6842\u534e\u751f\u5fc3\u5934\u4e00\u51db\uff0c\u6536\u675f\u4e86\u5fc3 \u733f\u610f\u9a6c\uff0c\u771f\u6c14\u6e10\u6e10\u900f\u8fc7\u5341\u4e8c\u91cd\u5173\uff0c\u7ec8\u4e8e\u5230\u4e86\u7269\u6211\u4e24\u5fd8\u4e4b\u5883\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u751f\u4e86\u591a\u4e45\uff0c\u90a3\u767d\u8863\u5c11\u5973\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u53ef\u4ee5\u8d70\u5566\uff01\u201d\u6842\u534e\u751f\u4e00\u8dc3\u800c\u8d77\uff0c\u4f46\u89c9\u7cbe\u795e\u9971 \u6ee1\uff0c\u51b7\u610f\u5168\u6d88\uff0c\u5411\u767d\u8863\u5c11\u5973\u4f5c\u4e86\u4e00\u63d6\uff0c\u7b11\u9053\uff1a\u201c\u591a\u8c22\u4f60\u7684\u6307\u70b9\uff0c\u60f3\u4e0d\u5230\u4f60\u628a\u4e0a\u4e58\u7684\u5185\u529f\u8bc0 \u8981\uff0c\u90fd\u5bd3\u4e8e\u7985\u673a\u5999\u7406\u4e4b\u4e2d\u3002\u201d\u767d\u8863\u5c11\u5973\u9053\uff1a\u201c\u6211\u90a3\u6709\u8fd9\u6837\u7684\u5927\u667a\u6167\uff1f\u8fd9\u90fd\u662f\u4ece\u90a3\u672c\u68b5\u6587\u79d8\u5178 \u4e2d\u89c9\u609f\u7684\u3002\u51b0\u5f39\u6253\u7a74\u7684\u529f\u592b\uff0c\u5219\u662f\u4ece\u5bd2\u7389\u5ca9\u4e0a\u6240\u7559\u7684\u7ecf\u6587\u5b66\u6765\u7684\uff0c\u8bf4\u6765\u6211\u4e5f\u8981\u8c22\u4f60\u52a9\u6211\u8fdb\u5165 \u51b0\u7a9f\u5462\uff01\u201d \u3000\u3000\u4e24\u4eba\u8bf4\u8bf4\u7b11\u7b11\uff0c\u8d70\u51fa\u51b0\u7a9f\uff0c\u4f46\u513f\u7ea2\u65e5\u5f53\u5934\uff0c\u5728\u51b0\u7a9f\u4e2d\u4e0d\u77e5\u65f6\u523b\uff0c\u539f\u6765\u53c8\u5df2\u662f\u7b2c\u4e8c\u5929\u7684\u6b63 \u5348\u65f6\u5206\u4e86\u3002\u6842\u534e\u751f\u7b11\u9053\uff1a\u201c\u6211\u4f46\u613f\u5728\u51b0\u7a9f\u4e2d\u518d\u591a\u7559\u4e00\u4e9b\u65f6\u65e5\u3002\u7389\u59b9\u59b9\uff0c\u4f60\u79bb\u5f00\u8fd9\u91cc\u4e4b\u540e\uff0c\u8981 \u4e0a\u90a3\u513f\uff1f\u4f60\u5bb6\u4e2d\u8fd8\u6709\u751a\u4e48\u4eba\uff1f\u4f60\u7684\u6b66\u529f\u662f\u600e\u4e48\u5b66\u6765\u7684\uff1f\u201d\u767d\u8863\u5c11\u5973\u7b11\u9053\uff1a\u201c\u4f60\u53c8\u6765\u5bfb\u6839\u7a76\u5e95 \u4e86\uff0c\u82e5\u7136\u4ed6\u65e5\u6709\u7f18\u518d\u9047\uff0c\u8fd9\u4e9b\u4e8b\u4f60\u4e0d\u95ee\u81ea\u77e5\u3002\u4eca\u65e5\u54b1\u4eec\u4e14\u5c3d\u60c5\u73a9\u8d4f\u8fd9\u96ea\u5c71\u5947\u666f\uff0c\u9886\u7565\u90a3\u5929\u6e56 \u98ce\u5149\u3002\u4e0d\u8bb8\u8c08\u4e16\u4fd7\u4e4b\u4e8b\u3002\u201d \u3000\u3000\u6842\u534e\u751f\u5927\u559c\uff0c\u4e0e\u767d\u8863\u5c11\u5973\u63a2\u51b0\u5ddd\uff0c\u6e38\u5929\u6e56\uff0c\u53c8\u5728\u7693\u7693\u7684\u51b0\u5cf0\u4e4b\u4e0a\uff0c\u7559\u4e0b\u4e86\u8bb8\u591a\u8db3\u5370\uff0c\u767d \u8863\u5c11\u5973\u6210\u4e0e\u4ed6\u8c08\u8bd7\u8bba\u6587\uff0c\u6216\u4e0e\u4ed6\u8bf4\u7985\u8bba\u5251\uff0c\u5728\u96ea\u5c71\u4e4b\u4e0a\uff0c\u4e0d\u77e5\u4e0d\u89c9\u7684\u8fc7\u4e86\u4e09\u5929\u3002\u8fd9\u4e00\u65e5\u767d\u8863 \u5c11\u5973\u4e0e\u6842\u534e\u751f\u5728\u7389\u5973\u5cf0\u5934\uff0c\u671b\u90a3\u6ee1\u5c71\u7eb5\u6a2a\u4ea4\u9519\u7684\u51b0\u5ddd\uff0c\u5446\u5446\u51fa\u795e\uff0c\u6842\u534e\u751f\u5947\u9053\uff1a\u201c\u8fd9\u51b0\u5ddd\u6709 \u751a\u4e48\u597d\u770b\uff1f\u201d\u767d\u8863\u5c11\u5973\u9053\uff1a\u201c\u4f60\u770b\u8fd9\u4e9b\u51b0\u5ddd\u597d\u50cf\u94f6\u9f99\u98de\u821e\uff0c\u4e34\u8fd1\u770b\u65f6\uff0c\u4e0a\u9762\u51b0\u5c42\u51dd\u7ed3\uff0c\u51e0\u4e4e \u770b\u4e0d\u51fa\u5b83\u5728\u79fb\u52a8\uff0c\u5b9e\u5219\u5728\u51b0\u5c42\u4e4b\u4e0b\uff0c\u4ecd\u662f\u6697\u6d41\u6c79\u6d8c\uff0c\u51b0\u5ddd\u7684\u5947\u5999\uff0c\u8f68\u5728\u6781\u9759\u4e4b\u4e2d\u6709\u6781\u52a8\uff0c \u55ef\uff0c\u6211\u5c06\u6765\u8981\u7ec3\u7684\u51b0\u5757\u5bd2\u5149\u5251\uff0c\u548c\u4e16\u95f4\u4efb\u4f55\u5b9d\u5251\u90fd\u4e0d\u76f8\u540c\uff0c\u5fc5\u987b\u81ea\u521b\u4e00\u6d3e\u6700\u7279\u522b\u7684\u5251\u6cd5\u624d \u884c\u3002\u201d\u4f73\u534e\u751f\u5927\u559c\u9053\uff1a\u201c\u6211\u4e5f\u6b63\u6709\u8fd9\u4e2a\u5fc3\u613f\u3002\u54b1\u4eec\uff0c\u54b1\u4eec\u2026\u2026\u201d\u8bdd\u672a\u8bf4\u5b8c\uff0c\u4f46\u89c1\u767d\u8863\u5c11\u5973\u4ece \u5cf0\u9876\u4e00\u98d8\u800c\u4e0b\uff0c\u62d4\u51fa\u7389\u7b1b\uff0c\u5728\u51b0\u5ddd\u4e0a\u9762\u6325\u821e\u8d77\u6765\uff0c\u5ffd\u75be\u5ffd\u5f90\uff0c\u6709\u5982\u6d41\u6c34\u884c\u4e91\uff0c\u7f8e\u5999\u4e4b\u6781\uff01 \u3000\u3000\u6842\u534e\u751f\u6697\u9053\uff1a\u201c\u82e5\u5c06\u5b83\u6f14\u6210\u5251\u6cd5\uff0c\u679c\u7136\u662f\u5947\u5e7b\u65e0\u6bd4\uff0c\u770b\u6765\u6bd4\u5317\u5929\u5c71\u4ee5\u5947\u8be1\u89c1\u957f\u7684\u767d\u53d1\u9b54 \u5973\u90a3\u4e00\u6d3e\u7684\u5251\u6cd5\uff0c\u8fd8\u8981\u80dc\u8fc7\u51e0\u5206\uff0c\u53ea\u662f\u5176\u4e2d\u597d\u50cf\u8fd8\u6709\u7834\u7efd\uff0c\u82e5\u4f5c\u4e3a\u72ec\u521b\u4e00\u5bb6\u7684\u5251\u6cd5\uff0c\u8fd8\u987b\u5047 \u4ee5\u65f6\u65e5\uff0c\u7ec6\u7ec6\u7422\u78e8\uff01\u201d\u767d\u8863\u5c11\u5973\u821e\u4e86\u4e00\u4f1a\uff0c\u6536\u8d77\u7389\u7b1b\uff0c\u5ffd\u5730\u5bf9\u6842\u534e\u751f\u88e3\u887d\u4e00\u793c\uff0c\u5fae\u5fae\u7b11\u9053\uff1a \u201c\u96be\u5165\u6cd5\u773c\uff0c\u5c1a\u671b\u6307\u6b63\u3002\u201d\u6842\u534e\u751f\u9053\uff1a\u201c\u5c0f\u59b9\u5b50\u4f60\u771f\u662f\u806a\u660e\u7edd\u9876\uff0c\u654f\u6167\u65e0\u4f26\uff0c\u8fd9\u5957\u5251\u6cd5\u662f\u4ece \u51b0\u5ddd\u6d41\u52a8\u4e4b\u4e2d\uff0c\u5999\u609f\u51fa\u6765\u7684\u4e48\uff1f\u201d\u767d\u8863\u5c11\u5973\u9053\uff1a\u201c\u72ec\u521b\u4e00\u5bb6\uff0c\u8c08\u4f55\u5bb9\u6613\uff1f\u6211\u4e0d\u8981\u4f60\u7684\u5949\u627f\uff0c \u4f60\u613f\u4f60\u4f9d\u5b9e\u8bf4\u6765\uff0c\u8fd9\u5251\u6cd5\u6709\u4f55\u4e0d\u8db3\u4e4b\u5904\uff1f\u201d\u6842\u534e\u751f\u9053\uff1a\u201c\u8f7b\u7075\u7fd4\u52a8\uff0c\u5947\u5999\u4e4b\u6781\uff0c\u53ea\u662f\u6697\u85cf\u7684 \u5a01\u529b\u4e0d\u591f\uff0c\u5f97\u51b0\u5ddd\u7684\u6c14\u8c61\uff0c\u5374\u672a\u5f97\u51b0\u5ddd\u7684\u51dd\u91cd\u3002\u201d\u767d\u8863\u5c11\u5973\u9053\uff1a\u201c\u4f60\u90a3\u5957\u8fbe\u6469\u5251\u6cd5\uff0c\u84c4\u52b2\u6df1 \u6c89\uff0c\u5012\u662f\u6b63\u597d\u8865\u6211\u8fd9\u5957\u5251\u6cd5\u7684\u4e0d\u8db3\u3002\u201d\u6842\u534e\u751f\u5fc3\u4e2d\u4e00\u52a8\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u4e48\u54b1\u4eec\u4e0d\u5982\u5c31\u5728\u8fd9\u7389\u5cf0 \u4e0a\u4f4f\u4e0a\u4e09\u5e74\uff0c\u5408\u521b\u521b\u51fa\u4e00\u5957\u65b0\u5947\u7684\u5251\u6cd5\u6765\uff0c\u5c31\u628a\u5b83\u5b9a\u540d\u4e3a\u51b0\u5ddd\u5251\u6cd5\uff01\u201d \u3000\u3000\u767d\u8863\u5c11\u5973\u674f\u8138\u5fae\u7ea2\uff0c\u9ed8\u7136\u4e0d\u8bed\uff0c\u5ffd\u5730\u4ece\u51b0\u5ddd\u91cc\u62ac\u8d77\u51e0\u7247\u6d6e\u51b0\uff0c\u63c9\u788e\u4e86\u51b0\u4e0a\u98d8\u6d6e\u7684\u4e00\u6735\u82b1 \u74e3\uff0c\u53c8\u8f7b\u8f7b\u7684\u5c06\u5b83\u64a4\u4e86\uff0c\u8ba9\u5b83\u968f\u98ce\u800c\u901d\uff0c\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u82b1\u81ea\u98d8\u96f6\u6c34\u81ea\u6d41\uff0c\u51b0\u5149\u6708\u5f71\u4e24\u60a0 \u60a0\uff01\u201d\u8eab\u5f62\u4e00\u8d77\uff0c\u8863\u8882\u98d8\u98d8\uff0c\u8f7b\u70b9\u6d6e\u51b0\uff0c\u6a2a\u8fc7\u51b0\u5ddd\uff0c\u8df3\u4e0a\u51b0\u5d16\uff0c\u661f\u6628\u534a\u542f\uff0c\u4ef0\u671b\u6d6e\u4e91\uff0c\u773c\u5149 \u5728\u6709\u610f\u65e0\u610f\u4e4b\u95f4\uff0c\u6b63\u597d\u4e0e\u6842\u534e\u751f\u76f8\u63a5\uff0c\u6842\u534e\u751f\u5fc3\u795e\u4ff1\u9189\uff0c\u66fc\u58f0\u541f\u9053\uff1a\u201c\u9752\u8d5e\u805a\u7d20\u5389\uff0c\u51b0\u56fd\u4ed9 \u4eba\u504f\u8010\u70ed\uff0c\u9910\u5c3d\u98ce\u9999\u9732\u80a9\u3002\u4fbf\u4e07\u91cc\u51cc\u7a7a\uff0c\u80af\u51ed\u83b2\u53f6\uff0c\u76c8\u76c8\u6b65\u6708\u3002\u6084\u4f3c\u601c\u8f7b\u53bb\u7476\u9619\uff01\u4eba\u4f55\u5728\uff1f \u5fc6\u4f0a\u75f4\u5c0f\uff0c\u70b9\u70b9\u7231\u6e05\u7edd\u3002\u201d\u767d\u8863\u5c11\u5973\u9053\uff1a\u201c\u8fd9\u662f\u751a\u4e48\u8bcd\u724c\uff1f\u201d\u6842\u534e\u751f\u9053\uff1a\u201c\u9713\u68e0\u5e8f\u4e2d\u7b2c\u4e00 (\u8bcd\u724c\u540d)\u3002\u8fd9\u662f\u4e0a\u534a\u9615\u3002\u201d\u767d\u8863\u5c11\u5973\u5e7d\u5e7d\u8bf4\u9053\uff1a\u201c\u53ea\u6101\u5929\u9645\u8d77\u957f\u98ce\uff0c\u60ca\u7834\u9713\u68e0\u7fbd\u8863\u66f2\u3002\u9152\u51b7 \u4f11\u6e29\uff0c\u8bd7\u6b8b\u83ab\u7eed\u3002\u7559\u4e9b\u672a\u5c3d\u7684\u60c5\u97f5\u66f4\u597d\uff0c\u4e0b\u534a\u9615\u4e0d\u542c\u4e5f\u7f62\u3002\u201d \u3000\u3000\u6842\u534e\u751f\u610f\u4e71\u60c5\u8ff7\uff0c\u4e0d\u77e5\u662f\u559c\u662f\u60b2\uff0c\u7adf\u81ea\u75f4\u4e86\u3002\u5ffd\u542c\u5f97\u8fdc\u5904\u5c71\u5934\uff0c\u6709\u7b1b\u58f0\u8f7b\u594f\uff0c\u767d\u8863\u5c11\u5973 \u51c4\u7136\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u7684\u4f8d\u5973\u5524\u6211\u56de\u5bb6\uff0c\u6211\u8981\u53bb\u4e86\uff01\u201d\u6842\u534e\u751f\u60ca\u9053\uff1a\u201c\u4f60\u53bb\u90a3\u513f\uff1f\u201d\u767d\u8863\u5c11 \u5973\u9053\uff1a\u201c\u4ece\u4f55\u5904\u6765\uff0c\u5411\u4f55\u5904\u53bb\uff01\u201d\u6842\u534e\u751f\u53eb\u9053\uff1a\u201c\u96be\u9053\u54b1\u4eec\u5c31\u662f\u8fd9\u6837\u7684\u5206\u624b\u4e86\u5417\uff1f\u4ee5\u540e \u5462\uff1f\u201d\u767d\u8863\u5c11\u5973\u9053\uff1a\u201c\u4ee5\u540e\u7684\u4e8b\u4ee5\u540e\u518d\u8bf4\uff01\u201d\u5ffd\u5730\u4f38\u51fa\u7ea4\u7ea4\u7389\u638c\uff0c\u6309\u4e86\u4e09\u6309\uff0c\u56de\u638c\u7fd8\u6307\uff0c\u6307 \u7740\u6302\u5728\u80f8\u524d\u4f5c\u4e3a\u9970\u7269\u7684\u4e00\u9762\u5c0f\u7389\u955c\uff0c\u660e\u58f0\u541f\u9053\uff1a\u201c\u82e5\u662f\u76f8\u9022\u4f11\u518d\u95ee\uff0c\u5404\u968f\u7f18\u4efd\u5230\u5929\u6daf\uff01\u201d\u98de \u8eab\u63a0\u4e0b\uff0c\u5c55\u5f00\u7edd\u9876\u8f7b\u529f\uff0c\u7adf\u5982\u9752\u5973\u7d20\u5a25\uff0c\u51cc\u98ce\u800c\u53bb\uff01 \u3000\u3000\u6842\u534e\u751f\u5fc3\u4f24\u6b32\u7edd\uff0c\u62ac\u5934\u4e00\u770b\uff0c\u4f46\u89c1\u65b0\u6708\u521d\u5347\uff0c\u51b0\u5cf0\u5982\u955c\uff0c\u53ea\u662f\u751f\u4e86\u4e00\u4e2a\u4eba\u513f\uff0c\u4fbf\u89c9\u5f97\u6ee1 \u76ee\u8352\u51c9\uff0c\u51c4\u51c4\u5bc2\u5bc2\uff01\u56de\u60f3\u8fd9\u51e0\u65e5\u6765\u7684\u79cd\u79cd\u5947\u9047\uff0c\u76f4\u4f3c\u505a\u4e86\u4e00\u573a\u5927\u68a6\uff01\u53ea\u53ef\u60dc\u8fd9\u68a6\u9192\u5f97\u592a\u65e9 \u4e86\u3002 \u3000\u3000\u6842\u534e\u751f\u6ca1\u7cbe\u6253\u91c7\u7684\u4e0b\u5c71\uff0c\u4e00\u8def\u6c89\u601d\uff0c\u60f3\u767d\u8863\u5c11\u5973\u4e34\u8d70\u4e4b\u65f6\uff0c\u7389\u638c\u4e09\u6309\uff0c\u624b\u6307\u955c\u5b50\uff0c\u90a3\u662f \u751a\u4e48\u610f\u601d\uff1f\u518d\u7422\u78e8\u5979\u90a3\u4e24\u53e5\u8bd7\uff0c\u597d\u50cf\u8fd8\u6709\u91cd\u89c1\u7684\u65e5\u5b50\u3002\u5230\u751a\u4e48\u5730\u65b9\u53bb\u89c1\u5979\uff1f\u5728\u751a\u4e48\u65f6\u5019\u53ef\u89c1 \u5979\uff1f\u8d8a\u60f3\u8d8a\u662f\u832b\u7136\uff0c\u4f46\u89c9\u5979\u7559\u4e0b\u7684\u54d1\u8c1c\u771f\u96be\u7d22\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u9b44\u5bd2\u5149\u5251\u300b\u2014\u2014\u7b2c\u4e94\u7ae0\u3000\u5e03\u8fbe\u62c9\u5bab\u53c2\u6d3b\u4f5b \u6881\u7fbd\u751f\u300a\u51b0\u9b44\u5bd2\u5149\u5251\u300b \u7b2c\u4e94\u7ae0\u3000\u5e03\u8fbe\u62c9\u5bab\u53c2\u6d3b\u4f5b \u3000\u3000\u6842\u534e\u751f\u4e0b\u5c71\u4e4b\u540e\uff0c\u9065\u671b\u201c\u9b54\u9b3c\u57ce\u201d\u4e2d\u90a3\u5ea7\u5c3c\u6cca\u5c14\u738b\u5b50\u6240\u4fee\u7684\u767d\u5854\uff0c\u60f3\u8d77\u4e86\u9ea6\u58eb\u8fe6\u5357\u7684\u4ed8 \u6258\uff0c\u5fc3\u4e2d\u4e00\u51db\uff0c\u60f3\u9053\uff1a\u201c\u9b54\u9b3c\u57ce\u4e2d\u90a3\u73ed\u5c3c\u6cca\u5c14\u6b66\u58eb\uff0c\u867d\u7136\u90fd\u5df2\u88ab\u767d\u8863\u5c11\u5973\u8d77\u8dd1\uff0c\u4f46\u5c3c\u6cca\u5c14\u738b \u5b50\u56fe\u8c0b\u897f\u85cf\u7684\u91ce\u5fc3\uff0c\u53ef\u8fd8\u6ca1\u6709\u6d88\u5f2d\u3002\u9ea6\u58eb\u8fe6\u5357\u8bf7\u6211\u5230\u62c9\u8428\u53bb\u53c2\u89c1\u6d3b\u4f5b\uff0c\u6258\u6211\u8f6c\u8fbe\u767d\u6559\u6cd5\u738b\u7684 \u8bda\u610f\uff0c\u6211\u600e\u4e48\u5fd8\u4e86\uff1f\u201d \u3000\u3000\u4e8e\u662f\u4f73\u534e\u751f\u53c8\u4ec6\u4ec6\u98ce\u5c18\uff0c\u524d\u5f80\u62c9\u8428\u3002\u8fd9\u65f6\u5df2\u662f\u521d\u6625\u65f6\u8282\uff0c\u5bf9\u51fa\u7684\u51b0\u96ea\u6e10\u6e10\u6d88\u89e3\uff0c\u8def\u4e0a\u597d \u8d70\u5f97\u591a\uff0c\u8d70\u4e86\u5c06\u8fd1\u4e00\u6708\uff0c\u4fbf\u6765\u5230\u897f\u85cf\u7684\u9996\u5e9c\u3002 \u3000\u3000\u6842\u534e\u751f\u8fdb\u57ce\u4e4b\u65f6\uff0c\u5929\u8272\u5df2\u665a\uff0c\u4f46\u89c1\u8857\u4e0a\u4e2d\u5e73\u9876\u7684\u623f\u5c4b\u4e0e\u5e10\u84ec\u4ea4\u6742\uff0c\u4e0e\u5185\u5730\u57ce\u5e02\u7684\u98ce\u5149\u5927 \u4e0d\u76f8\u540c\u3002\u8857\u4e0a\u884c\u4eba\u7199\u6765\u6518\u5f80\uff0c\u6bcf\u4e00\u5ea7\u5e10\u5e55\u90fd\u6709\u9999\u70df\u9563\u7ed5\uff0c\u70db\u5149\u5a9a\u8000\uff0c\u5728\u8bb8\u591a\u5e10\u84ec\u524d\u9762\uff0c\u90fd\u6709 \u85cf\u4eba\u711a\u9999\u793c\u62dc\u3002\u6842\u534e\u751f\u62c9\u7740\u4e00\u4e2a\u8001\u5934\u9053\uff1a\u201c\u4eca\u5929\u53ef\u662f\u4ec0\u4e48\u8282\u65e5\u5417\uff1f\u201d\u90a3\u8001\u5934\u9053\uff1a\u201c\u4e0d\u662f\u4eca \u5929\uff0c\u662f\u660e\u5929\uff01\u201d\u6307\u6307\u5929\u4e0a\u7684\u660e\u6708\uff0c\u8bf4\u9053\uff1a\u201c\u5ba2\u4eba\uff0c\u4f60\u662f\u4ece\u90a3\u513f\u6765\u7684\uff1f\u4f60\u662f\u4e0d\u662f\u4f5b\u95e8\u7684\u4fe1\u58eb\uff0c \u600e\u4e48\u8fde\u4f5b\u79df\u8bde\u8fb0\u90fd\u5fd8\u8bb0\u4e86\u3002\u201d \u3000\u3000\u6842\u534e\u751f\u62ac\u5934\u4e00\u671b\uff0c\u5929\u4e0a\u660e\u6708\u6b63\u5706\uff0c\u8be7\u800c\u95ee\u9053\uff1a\u201c\u4f5b\u79df\u8bde\u8fb0\u4e0d\u662f\u56db\u6708\u516b\u65e5\u5417\uff1f\u201d\u90a3\u8001\u5934\u6014 \u4e86\u4e00\u6014\uff0c\u5ffd\u5730\u7b11\u9053\uff1a\u201c\u5ba2\u5b98\uff0c\u4f60\u662f\u6c49\u4eba\uff0c\u6709\u6240\u4e0d\u77e5\u4e86\uff0c\u5e78\u4e8f\u61c2\u5f97\u6c49\u5386\uff0c\u8981\u4e0d\u7136\u771f\u4e0d\u61c2\u5f97\u4f60\u56e0 \u4f55\u8be7\u5f02\u4e86\u3002\u660e\u5929\u5c31\u662f\u56db\u6708\u516b\u65e5\u554a\uff01\u201d\u6842\u534e\u751f\u9053\uff1a\u201c\u5929\u4e0a\u7684\u6708\u4eae\u6b63\u5706\u2026\u2026\u201d\u90a3\u8001\u5934\u7b11\u9053\uff1a\u201c\u6211 \u4eec\u662f\u7528\u85cf\u5386\u3002\u4f60\u4eec\u6c49\u4eba\u7528\u7684\u9634\u5386\uff0c\u6708\u4eae\u6b63\u56fe\u4e4b\u65e5\uff0c\u4fbf\u662f\u5341\u4e94\uff0c\u6211\u4eec\u7684\u85cf\u5386\u4e0d\u8fd9\u6837\u7684\uff0c\u6709\u65f6\u6708 \u521d\u4fbf\u5706\uff0c\u6709\u65f6\u6708\u5c3e\u65b9\u5706\u3002\u82e5\u7167\u6c49\u5386\uff0c\u4eca\u5929\u662f\u4e09\u6708\u5341\u56db\u65e5\uff0c\u660e\u5929\u4fbf\u662f\u4e09\u6708\u5341\u4e94\u65e5\uff0c\u56e0\u4e3a\u4eca\u5e74\u7684 \u4f5b\u79df\u8bde\u8fb0\uff0c\u6070\u9022\u6708\u5706\uff0c\u6240\u4ee5\u7279\u522b\u70ed\u95f9\uff0c\u4ece\u6628\u5929\u8d77\uff0c\u5927\u5bb6\u4fbf\u6c90\u6d74\u658b\u6212\uff0c\u711a\u9999\u793c\u4f5b\u4e86\u3002\u201d \u3000\u3000\u6842\u534e\u751f\u5fc3\u5934\u4e00\u52a8\uff0c\u5583\u5583\u8bf4\u9053\uff1a\u201c\u4e09\u6708\u5341\u4e94\uff0c\u4e09\u6708\u5341\u4e94\uff1f\u201d\u731b\u7136\u9192\u609f\uff1a\u767d\u8863\u5c11\u5973\u4e34\u522b\u4e4b\u65f6 \u6240\u4f5c\u7684\u624b\u52bf\uff0c\u7389\u638c\u4e09\u6309\uff0c\u4e09\u4e94\u5341\u4e94\uff0c\u5c82\u4e0d\u6b63\u662f\u8868\u660e\u4e09\u6708\u5341\u4e94\u65e5\u4e4b\u671f\uff1f\u624b\u6307\u7389\u955c\uff0c\u5c82\u4e0d\u6b63\u662f\u4ee3 \u8868\u5929\u4e0a\u6708\u5706\u4e4b\u5bb6\uff1f \u3000\u3000\u90a3\u8001\u5934\u7d6e\u7d6e\u8bf4\u9053\uff1a\u201c\u5ba2\u5b98\uff0c\u4f60\u771f\u6709\u798f\u6c14\uff0c\u4eca\u5e74\u8fbe\u8d56\u6d3b\u4f5b\uff0c\u5c06\u5728\u660e\u5929\u4eb2\u81ea\u4e3b\u6301\u793c\u4f5b\u4eea\u8282\uff0c \u5e03\u8fbe\u62c9\u5bab\u524d\u9762\u7684\u4e09\u5ea7\u5927\u6bbf\u4e5f\u5c06\u5728\u660e\u5929\u5f00\u653e\uff0c\u51c6\u8bb8\u5584\u7537\u4fe1\u5973\u5728\u5927\u6bbf\u7684\u9636\u4e0b\u793c\u4f5b\u3002\u6211\u4eec\u4e00\u751f\u4e4b \u4e2d\uff0c\u4e5f\u672a\u5fc5\u5f97\u89c1\u6d3b\u4f5b\u4e00\u6b21\uff0c\u4f60\u4e00\u5230\u6765\uff0c\u53ea\u8981\u660e\u65e5\u6324\u5f97\u8fdb\u53bb\uff0c\u4fbf\u53ef\u4ee5\u89c1\u7740\u6d3b\u4f5b\u7684\u771f\u9762\u76ee\uff0c\u90a3\u771f \u662f\u5929\u5927\u7684\u798f\u6c14\u554a\uff01\u201d \u3000\u3000\u6842\u534e\u751f\u5927\u559c\u8fc7\u671b\uff0c\u6025\u5fd9\u8c22\u8c22\u8001\u5934\uff0c\u627e\u4e00\u5ea7\u4e13\u95e8\u63a5\u5f85\u5ba2\u5546\u7684\u5e10\u84ec\u4f4f\u4e0b\u3002\u4f46\u8fd9\u4e00\u591c\u90a3\u91cc\u7761\u5f97 \u7740\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u539f\u6765\u534e\u7389\u59b9\u59b9\u662f\u7ea6\u6211\u660e\u65e5\u5348\u591c\u5728\u5e03\u8fbe\u62c9\u5bab\u76f8\u4f1a\uff0c\u53ef\u662f\u5979\u53c8\u600e\u4e48\u80fd\u8fdb\u5e03\u8fbe\u62c9 \u5bab\u5462\uff1f\u96be\u9053\u665a\u95f4\u4e5f\u4e00\u6837\u5f00\u653e\uff0c\u4efb\u4eba\u6e38\u89c8\uff1f\u201d\u7761\u4e0d\u7740\u89c9\uff0c\u53c8\u8d77\u8eab\u5411\u5e10\u84ec\u7684\u4e3b\u4eba\u6253\u542c\uff0c\u6240\u8bf4\u7684\u4e0e \u90a3\u8001\u5934\u4e00\u6837\uff0c\u660e\u5929\u5f00\u653e\u7684\u5c31\u662f\u4e09\u5ea7\u5927\u6bbf\uff0c\u4e00\u526f\u9ec4\u660f\u843d\u65e5\uff0c\u6240\u6709\u793c\u4f5b\u7684\u4eba\u4fbf\u90fd\u8981\u79bb\u53bb\u3002\u90a3\u4f4d\u4e3b \u4eba\u8fd8\u8083\u7136\u8bf4\u9053\uff1a\u201c\u6d3b\u4f5b\u4f55\u7b49\u795e\u5723\uff0c\u5c82\u80fd\u5bb9\u51e1\u4eba\u9020\u5165\u4ed6\u7684\u6df1\u5bab\uff1f\u8ba9\u6211\u4eec\u5728\u5927\u6bbf\u9636\u4e0b\u793c\u62dc\uff0c\u5df2\u662f \u798f\u5206\u4e0d\u6d45\u4e86\uff01\u201d\u6842\u534e\u751f\u5fc3\u5934\u7684\u7591\u56e2\u8d8a\u6765\u8d8a\u91cd\uff0c\u60f3\u9053\uff1a\u201c\u9664\u975e\u662f\u6211\u731c\u9519\u5b83\u7684\u7528\u610f\uff1f\u4f46\u82e5\u4e0d\u662f\u8fd9 \u6837\u89e3\u91ca\uff0c\u53c8\u5177\u4ec0\u4e48\uff1f\u201d\u5bf9\u767d\u8863\u5c11\u5973\u7684\u8eab\u4efd\uff0c\u66f4\u89c9\u8be1\u79d8\uff0c\u4f46\u671b\u660e\u65e5\u65e9\u65e9\u5230\u6765\u3002 \u3000\u3000\u6842\u534e\u751f\u4e00\u591c\u65e0\u7720\uff0c\u597d\u4e0d\u5bb9\u6613\u5230\u7b2c\u4e8c\u5929\u5929\u4eae\uff0c\u7acb\u5373\u8d77\u6765\uff0c\u540c\u4e3b\u4eba\u501f\u4e86\u4e00\u5957\u897f\u85cf\u7684\u670d\u88c5\uff0c\u514d \u5f97\u5728\u8fdb\u9999\u793c\u62dc\u4e4b\u65f6\u60f9\u4eba\u6ce8\u76ee\u3002 \u3000\u3000\u8fbe\u8d56\u6d3b\u4f5b\u5f00\u653e\u5e03\u8fbe\u62c9\u5bab\uff0c\u5e76\u4e14\u4eb2\u81ea\u4e3b\u6301\u4f5b\u79df\u8bde\u8fb0\u7684\u793c\u4f5b\u4eea\u5f0f\uff0c\u8fd9\u4ef6\u4e8b\u60c5\u8f70\u52a8\u4e86\u62c9\u8428\uff0c\u751a \u81f3\u6709\u8bb8\u591a\u5916\u5730\u7684\u5584\u7537\u4fe1\u5973\u4e5f\u4e5f\u95fb\u98ce\u8d76\u81f3\u3002\u6842\u534e\u751f\u4ee5\u4e3a\u5df2\u8d77\u5f97\u65e9\u4e86\uff0c\u90a3\u77e5\u4e00\u51fa\u5e10\u5e55\uff0c\u8857\u9053\u4e0a\u5df2 \u662f\u9ed1\u538b\u538b\u7684\u4eba\u7fa4\uff0c\u6842\u534e\u751f\u968f\u7740\u4eba\u6d41\uff0c\u7f13\u7f13\u884c\u8fdb\u3002 \u3000\u3000\u5e03\u8fbe\u62c9\u5bab\u5efa\u5728\u62c9\u8428\u57ce\u5916\u7684\u8461\u8404\u5c71\u4e0a\uff08\u85cf\u540d\u5e03\u8fbe\u62c9\u5c71\uff0c\u5bab\u4ee5\u51fa\u540d\u3002\uff09\uff0c\u9ad8\u8fbe\u4e00\u5341\u4e09\u5c42\uff0c\u76f8 \u4f20\u662f\u85cf\u738b\u677e\u8d5e\u5e72\u5e03\u5a36\u4e86\u5eb7\u592a\u5b97\u674e\u4e16\u6c11\u7684\u5973\u513f\u6587\u6210\u516c\u4e3b\u4e4b\u540e\uff08\u516c\u5143\u516d\u56db\u4e00\u5e74\uff09\uff0c\u5e94\u6587\u6210\u516c\u4e3b\u6240 \u8c13\u800c\u5efa\uff0c\u7ecf\u8fc7\u5386\u4ee3\u7684\u6269\u5efa\u6574\u4fee\uff0c\u5bcc\u4e3d\u65e0\u6bd4\u3002\u5b83\u7684\u7ed3\u6784\uff0c\u5168\u90fd\u662f\u5c71\u4e00\u5757\u5757\u4e00\u5c3a\u89c1\u65b9\u7684\u77f3\u5934\u4ece\u5c71 \u8170\u4e0b\u5e73\u780c\u4e0a\u53bb\uff0c\u5e03\u8fbe\u62c9\u5bab\u9876\u4e0a\u6709\u4e09\u5ea7\u5e9e\u5927\u7684\u91d1\u9876\uff0c\u8fd8\u6709\u897f\u85cf\u5386\u4ee3\u6d3b\u4f5b\u8089\u8eab\u7684\u516b\u5ea7\u91d1\u5854\uff0c\u5168\u90e8 \u7528\u91d1\u53f6\u5305\u88f9\uff0c\u4e2d\u5d4c\u73e0\u5b9d\uff0c\u8fdc\u8fdc\u671b\u53bb\uff0c\u707f\u70c2\u95ea\u5149\uff0c\u7aef\u7684\u4f3c\u743c\u697c\u738b\u5e9c\uff0c\u58ee\u4e3d\u975e\u5e38\u3002 \u3000\u3000\u6842\u534e\u751f\u968f\u7740\u4eba\u6f6e\uff0c\u5c06\u8fd1\u4e2d\u5348\u65f6\u5206\uff0c\u624d\u6324\u5230\u5e03\u8fbe\u62c9\u5bab\u4e0b\u9762\u7684\u5c71\u5f84\uff0c\u4f46\u89c1\u901a\u5230\u5bab\u95e8\u7684\u5f2f\u66f2\u77f3 \u9636\u4e0a\uff0c\u6709\u4e24\u961f\u62ab\u7740\u9ec4\u8272\u8888\u88df\u7684\u5587\u561b\u4f5c\u4e3a\u524d\u5bfc\uff0c\u524d\u9762\u4e09\u5ea7\u5927\u6bbf\u7684\u95e8\u6237\u5927\u5f00\uff0c\u8fdb\u9999\u793c\u62dc\u7684\u5584\u7537\u4fe1 \u5973\u8ddf\u5728\u5587\u561b\u540e\u9762\uff0c\u9c7c\u8d2f\u800c\u4eba\uff0c\u5f85\u5230\u6842\u534e\u751f\u6324\u8fdb\u91cc\u9762\u5927\u6bbf\u7684\u77f3\u9636\u4e0b\u5df1\u65e0\u63d2\u9488\u4e4b\u5730\uff0c\u540e\u5230\u7684\u4eba\uff0c \u3000\u3000\u597d\u5728\u5bab\u95e8\u5916\u793c\u62dc\u4e86\u3002 \u3000\u3000\u6842\u534e\u751f\u6e38\u76ee\u56db\u987e\uff0c\u60f3\u5728\u4eba\u7fa4\u4e4b\u4e2d\u53d1\u73b0\u767d\u8863\u5c11\u5973\uff0c\u76f4\u5982\u5728\u5927\u6d77\u5bfb\u9488\uff0c\u6beb\u65e0\u8e2a\u5f71\u3002\u6842\u534e\u751f\u6697 \u8fd0\u7528\u5185\u52e4\uff0c\u4ece\u4eba\u4e1b\u4e4b\u4e2d\u6324\u8fdb\uff0c\u9760\u8fd1\u4ed6\u8eab\u8fb9\u7684\u4eba\uff0c\u90fd\u4f3c\u6697\u4e2d\u88ab\u4eba\u63a8\u4e86\u4e00\u628a\u4f3c\u7684\uff0c\u4e0d\u7531\u81ea\u4e3b\u7684\u8ba9 \u5f00\u3002\u597d\u5728\u4eba\u6781\u62e5\u6324\uff0c\u522b\u4eba\u53ea\u4ee5\u4e3a\u662f\u53d7\u4e86\u540e\u9762\u7684\u63a8\u538b\u4e4b\u529b\uff0c\u6ca1\u6709\u770b\u7834\u3002 \u3000\u3000\u6842\u534e\u751f\u8e0f\u904d\u4e86\u4e09\u5ea7\u7684\u6570\u5343\u7ea7\u77f3\u9636\uff0c\u8d39\u4e86\u51e0\u4e4e\u4e00\u4e2a\u591a\u65f6\u8fb0\uff0c\u4ecd\u662f\u627e\u4e0d\u89c1\u767d\u8863\u5c11\u5973\u3002\u4eba\u7fa4\u4ece \u6bbf\u4e0b\u7684\u77f3\u9636\u76f4\u6324\u5230\u6bbf\u5916\u7684\u56de\u5eca\uff0c\u6842\u534e\u751f\u77e5\u9053\u5178\u793c\u5728\u6b63\u4e2d\u7684\u5927\u6bbf\u4e3e\u884c\uff0c\u4fbf\u4e5f\u6324\u5230\u4e86\u8fd9\u5ea7\u5927\u6bbf\u7684 \u56de\u5eca\u4e4b\u4e0a\uff0c\u4f46\u89c1\u6bbf\u4e0a\u6709\u56db\u4e2a\u5927\u98de\u69fd\uff0c\u4e0a\u7f00\u4eba\u9762\u9a6c\u8eab\u7684\u91d1\u50cf\uff0c\u4e0b\u7cfb\u950b\u94c3\uff0c\u96d5\u9542\u5f97\u6781\u5176\u7cbe\u7ec6\u3002\u6842 \u534e\u751f\u7ecf\u8fc7\u4e86\u4e00\u4e2a\u591a\u6708\u7684\u65c5\u884c\uff0c\u4ece\u6ee1\u76ee\u8352\u829c\uff0c\u5bd2\u51bb\u51c4\u6e05\uff0c\u5e38\u5e38\u5728\u6570\u5341\u91cc\u5185\u6e3a\u65e0\u4eba\u70df\u7684\u897f\u85cf\u9ad8 \u539f\uff0c\u6765\u5230\u5e03\u8fbe\u62c9\u5bab\uff0c\u5f77\u4f5b\u5982\u7f6e\u8eab\u5728\u4e00\u4e2a\u534e\u7f8e\u7684\u68a6\u5883\u4e4b\u4e2d\uff01 \u3000\u3000\u6842\u534e\u751f\u7eb5\u76ee\u6d4f\u89c8\uff0c\u4f46\u89c1\u8fc7\u56de\u5eca\u7684\u6881\u3001\u67f1\u3001\u6276\u624b\u4e0a\uff0c\u6216\u88f9\u91d1\uff0c\u6216\u96d5\u9542\uff0c\u6216\u7ed8\u4e0a\u56fe\u6848\u3001\u663c \u5e45\uff0c\u8bf4\u4e0d\u5c3d\u7684\u5bcc\u4e3d\u5e84\u4e25\uff0c\u6842\u534e\u751f\u6697\u6697\u53f9\u9053\uff1a\u201c\u5916\u9762\u5df2\u662f\u5982\u6b64\uff0c\u5bab\u91cc\u9762\u66f4\u4e0d\u77e5\u5982\u4f55\uff1f\u53ea\u8fd9\u4e00\u5ea7 \u5e03\u8fbe\u62c9\u5bab\uff0c\u5c31\u4e0d\u77e5\u8d39\u4e86\u51e0\u8bb8\u4eba\u529b\u8d22\u529b\uff1f\u201d\u5927\u6bbf\u56db\u58c1\uff0c\u91cc\u91cc\u5916\u5916\uff0c\u90fd\u7ed8\u6709\u58c1\u753b\uff0c\u7ed9\u7684\u591a\u534a\u662f\u4f5b \u7ecf\u4e2d\u6545\u4e8b\uff0c\u4eba\u7269\u666f\u50cf\uff0c\u5947\u5947\u602a\u602a\uff0c\u751f\u52a8\u975e\u5e38\u3002\u8981\u77e5\u5e03\u8fbe\u62c9\u5bab\u7684\u58c1\u753b\uff0c\u5929\u4e0b\u95fb\u540d\uff0c\u58c1\u753b\u662f\u7528\u767d \u7ef8\u7c98\u5728\u58c1\u4e0a\uff0c\u518d\u5728\u7ef8\u4e0a\u6d82\u4e0a\u9165\u6cb9\uff0c\u8fd9\u6837\u4f5c\u4e0a\u7684\u753b\uff0c\u8272\u6cfd\u53ef\u4ee5\u5386\u4e45\u4e0d\u53d8\u3002\u6570\u767e\u5e74\u6765\uff0c\u4e0d\u77e5\u6709\u591a \u5c11\u753b\u5e08\uff0c\u6765\u81ea\u4e2d\u56fd\u5185\u5730\uff0c\u6765\u81ea\u5370\u5ea6\uff0c\u6765\u81ea\u5c3c\u6cca\u5c14\u3001\u4e0d\u4e39\uff0c\u5728\u8fd9\u513f\u4f5c\u8fc7\u58c1\u753b\uff0c\u771f\u53ef\u8bf4\u662f\u4e00\u4e2a\u827a \u672f\u7684\u5b9d\u5e93\uff0c\u602a\u4e0d\u5f97\u6842\u534e\u751f\u76ee\u7729\u795e\u8ff7\u3002 \u3000\u3000\u6842\u534e\u751f\u6b63\u81ea\u6324\u5230\u6bbf\u5916\u6b23\u8d4f\u58c1\u753b\uff0c\u5ffd\u89c9\u80cc\u540e\u4e00\u80a1\u5927\u529b\u63a8\u6765\uff0c\u8170\u95f4\u4e00\u9178\uff0c\u7adf\u4f3c\u6709\u4eba\u70b9\u5230\u4e86\u5b83 \u7684\u8f6f\u9ebb\u7a74\u4e0a\uff0c\u6842\u534e\u751f\u4e0d\u7981\u5927\u5403\u4e00\u60ca\uff01 \u3000\u3000\u5e78\u800c\u4ed6\u6b63\u5728\u6697\u8fd0\u5185\u52b2\uff0c\u4e00\u89c9\u6709\u5f02\uff0c\u7acb\u523b\u8fd0\u6c14\u62a4\u7a74\uff0c\u540c\u65f6\u8fc5\u901f\u53cd\u624b\u64d2\u62ff\uff0c\u4f46\u542c\u5f97\u5438\u54df\u7684\u51e0 \u58f0\u54d7\u53eb\uff0c\u5468\u56f4\u8dcc\u5012\u4e86\u597d\u51e0\u4e2a\u4eba\uff0c\u6842\u534e\u751f\u56de\u5934\u4e00\u671b\uff0c\u53ea\u89c1\u62ff\u7740\u7684\u662f\u4e00\u4e2a\u80d6\u5987\uff0c\u6012\u76ee\u800c\u89c4\uff0c\u6c89\u58f0 \u65a5\u9053\uff1a\u201c\u4f60\u505a\u4ec0\u4e48\uff1f\u201d\u6842\u534e\u751f\u4e00\u62ff\u4e4b\u4e0b\uff0c\u65e9\u5df2\u53d1\u89c9\u4e86\u90a3\u80d6\u5987\u4e1d\u6beb\u4e0d\u61c2\u6b66\u529f\uff0c\u6025\u5fd9\u653e\u624b\uff0c\u8fde\u58f0 \u9053\u6b49\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u89c1\u6709\u4eba\u7528\u529b\u6324\u6211\uff0c\u4f38\u624b\u4e71\u6478\uff0c\u6211\u4ee5\u4e3a\u662f\u6709\u5c0f\u57ce\u4e58\u673a\u884c\u7a83\uff0c\u90a3\u77e5\u9519\u62ff\u4e86\u4eba\uff0c \u8bf7\u5927\u5a18\u6055\u7f6a\u3002\u201d\u5e78\u800c\u85cf\u4eba\u5bf9\u7537\u5973\u4e4b\u9632\u8fd8\u4e0d\u82e5\u6c49\u4eba\u91cd\u89c6\uff0c\u6842\u534e\u751f\u8bf4\u7684\u4e5f\u662f\u5b9e\u60c5\uff0c\u90a3\u80d6\u5987\u4eba\u6251\u55e4 \u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5728\u6d3b\u4f5b\u6240\u4f4f\u7684\u5e03\u8fbe\u62c9\u5bab\uff0c\u8c01\u6562\u884c\u7a83\uff01\u4f60\u5927\u7ea6\u662f\u521a\u6765\u4e0d\u4e45\u7684\u6c49\u4eba\uff1f\u201d\u6842\u534e\u751f\u70b9 \u5934\u8bf4\u662f\uff0c\u90a3\u80d6\u5987\u7d6e\u7d6e\u53e8\u53e8\uff0c\u5c1a\u5f85\u8bf4\u8bdd\uff0c\u5ffd\u542c\u5f97\u6bbf\u4e0a\u949f\u9f13\u9f50\u9e23\uff0c\u4e24\u961f\u9ec4\u8863\u561b\u5587\u7ed5\u6bbf\u800c\u8d70\uff0c\u904d\u9152 \u6cd5\u6c34\uff0c\u793c\u4f5b\u7684\u5927\u5178\u5c31\u5c06\u5f00\u59cb\u4e86\uff0c\u767b\u65f6\u6bbf\u91cc\u6bbf\u5916\u8083\u9759\u65e0\u58f0\uff0c\u80d6\u5987\u4eba\u4e5f\u5c31\u4e0d\u518d\u7ea0\u7f20\uff0c\u81ea\u987e\u81ea\u7684\u4f4e \u5934\u793c\u62dc\u3002 \u3000\u3000\u6842\u534e\u751f\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u8fd9\u4e2a\u6697\u4e2d\u5077\u88ad\u7684\u4eba\u6b66\u529f\u786e\u662f\u4e0d\u5f31\uff0c\u4eba\u4e5f\u673a\u4f36\uff0c\u6211\u51fa\u624b\u4e0d\u7b97\u6162\u4e86\uff0c\u8fd8 \u662f\u88ab\u4ed6\u6df7\u5728\u4eba\u5806\u4e4b\u4e2d\u9003\u8131\u3002\u90a3\u51e0\u4e2a\u8dcc\u5012\u7684\u4eba\uff0c\u5f53\u7136\u662f\u4ed6\u6545\u610f\u63a8\u5012\u7684\uff0c\u9020\u6210\u6df7\u4e71\uff0c\u4ee5\u514d\u88ab\u6211\u53d1 \u89c9\u3002\u4ed6\u662f\u4ec0\u4e48\u4eba\uff1f\u4e3a\u4ec0\u4e48\u8981\u5411\u6211\u5077\u88ad\uff1f\u201d\u767e\u601d\u83ab\u5f97\u5176\u89e3\u3002\u8fd9\u65f6\u6bbf\u4e0a\u7684\u949f\u9f13\u5df2\u6572\u4e86\u4e09\u904d\uff0c\u6709\u4e24 \u4e2a\u5927\u5587\u561b\u5e26\u5934\u5ff5\u7ecf\uff0c\u8fc7\u4e86\u4e00\u9635\uff0c\u949f\u58f0\u68b5\u58f0\u4e4b\u4e2d\uff0c\u8fbe\u8d56\u6d3b\u4f5b\u5728\u4ece\u4eba\u7c07\u62e5\u4e4b\u4e0b\uff0c\u7f13\u7f13\u8d70\u51fa\u3002\u6240\u6709 \u89c2\u5149\u7684\u7537\u5973\u8001\u5e7c\uff0c\u90fd\u9ad8\u8bf5\u4f5b\u53f7\uff0c\u4fef\u4f0f\u793c\u62dc\uff0c\u4e0d\u6562\u4ef0\u89c2\u3002 \u3000\u3000\u6842\u534e\u751f\u81ea\u7136\u4e5f\u4e0d\u5f97\u4e0d\u8ddf\u7740\u4ed6\u4eec\u4e00\u9f50\uff0c\u4fef\u4f0f\u793c\u62dc\uff0c\u7136\u800c\u4ed6\u5374\u5077\u5077\u5f20\u671b\uff0c\u8fbe\u8d56\u6d3b\u4f5b\u5927\u7ea6\u662f\u56db \u5341\u5c81\u5de6\u53f3\u7684\u6837\u5b50\uff0c\u5fae\u5fae\u53d1\u80d6\uff0c\u795e\u60c5\u751a\u662f\u5e84\u4e25\uff0c\u4e5f\u4e0d\u89c9\u6709\u4ec0\u4e48\u7279\u5f02\u4e4b\u5904\uff0c\u5438\u5f15\u7740\u6842\u534e\u751f\u773c\u5149 \u7684\uff0c\u5012\u4e0d\u662f\u8fbe\u8d56\u6d3b\u4f5b\uff0c\u800c\u662f\u53e6\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u8fbe\u8d56\u540e\u9762\uff0c\u6709\u597d\u51e0\u4e2a\u76f8\u8c8c\u548c\u670d\u9970\u90fd\u7279\u522b\u7684\u50e7\u4fa3\uff0c\u800c\u4e14\u5404\u5404\u4e0d\u540c\uff0c\u4e00\u770b\u5c31\u77e5\u662f\u4ece\u5916\u56fd\u6765\u7684 \u8d35\u5bbe\uff0c\u5927\u7ea6\u4ece\u5370\u5ea6\u3001\u5c3c\u6cca\u5c14\u3001\u4e0d\u4e39\u3001\u9521\u91d1\u5404\u5730\u6765\u7684\uff0c\u5176\u4e2d\u6709\u4e00\u4e2a\u62ab\u7740\u5927\u7ea2\u88c5\u88c5\u88c5\u7684\u756a\u50e7\uff0c\u6b63 \u662f\u548c\u6842\u534e\u751f\u5728\u9b54\u9b3c\u57ce\u4e2d\u4ea4\u8fc7\u624b\u7684\u90a3\u4e2a\u9b3c\u756a\u50e7\uff0c\u6842\u534e\u751f\u5fc3\u5934\u4e00\u51db\uff1a\u201c\u600e\u4e48\u4ed6\u4e5f\u6765\u4e86\uff1f\u201d\u968f\u5373\u60f3 \u5230\u4ed6\u8fd9\u4e00\u6765\uff0c\u5176\u4e2d\u5b9a\u6709\u5978\u8c0b\u3002 \u3000\u3000\u6d3b\u4f5b\u4e3b\u6301\u7684\u793c\u62dc\u5927\u5178\u4e3a\u65f6\u751a\u77ed\uff0c\u5148\u662f\u628a\u6768\u679d\u7518\u9732\u904d\u6652\u4f5b\u50cf\u4e4b\u524d\uff0c\u7ee7\u800c\u662f\u5448\u732e\u201c\u54c8\u8fbe\u201d \uff08\u5373\u662f\u4e1d\u7ee2\u6240\u505a\u7684\u624b\u5e15\u3002\u732e\u54c8\u8fbe\u662f\u897f\u85cf\u4e00\u79cd\u8868\u793a\u656c\u610f\u7684\u793c\u8282\u3002\u6700\u540e\u662f\u711a\u9999\u793c\u62dc\uff0c\u524d\u540e\u4e0d\u8fc7\u4e00 \u679d\u9999\u7684\u65f6\u523b\uff0c\u5178\u793c\u4fbf\u544a\u5b8c\u6210\u3002\u6d3b\u4f5b\u7684\u62a4\u6cd5\u5f1f\u5b50\u4f20\u8c15\uff0c\u6240\u6709\u524d\u6765\u793c\u62dc\u7684\u5f1f\u5b50\u90fd\u9650\u5728\u9ec4\u660f\u4e4b\u524d\u79bb \u5f00\u3002 \u3000\u3000\u6842\u534e\u751f\u56de\u5230\u5e10\u84ec\uff0c\u4e3b\u4eba\u8fd8\u6ca1\u6709\u56de\u6765\uff0c\u4ed6\u6b47\u4e86\u4e00\u4f1a\uff0c\u517b\u597d\u7cbe\u795e\uff0c\u5403\u8fc7\u665a\u996d\u4e4b\u540e\uff0c\u4e3b\u4eba\u65b9\u81ea \u8d76\u56de\uff0c\u5174\u81f4\u51b2\u51b2\u7684\u5927\u8c08\u4eca\u65e5\u7684\u76db\u5178\uff0c\u548c\u6842\u534e\u751f\u4e92\u76f8\u795d\u798f\uff0c\u5e76\u8bf4\u4eca\u665a\u5e03\u8fbe\u62c9\u5bab\u71c3\u706f\u793c\u4f5b\uff0c\u8bb8\u591a \u5584\u7537\u4fe1\u5973\uff0c\u5b81\u613f\u4e0d\u56de\u5bb6\u98df\u7269\uff0c\u7559\u5728\u8461\u8404\u5c71\u4e0b\u9065\u8d4f\u706f\u9970\uff0c\u4e3b\u4eba\u53f9\u9053\uff1a\u201c\u53ef\u60dc\u6211\u5e74\u8001\u4f53\u8870\uff0c\u8981\u4e0d \u7136\u6211\u4e5f\u5b81\u613f\u631e\u997f\u4e00\u665a\u3002\u5ba2\u5b98\u8fd9\u6837\u96be\u9022\u7684\u80dc\u666f\uff0c\u4f60\u5012\u4e0d\u53ef\u9519\u8fc7\u4e86\u3002\u201d\u6842\u534e\u751f\u8fde\u58f0\u8bf4\u662f\uff0c\u4fbf\u5411\u4e3b \u4eba\u544a\u8f9e\u3002 \u3000\u3000\u5e03\u8fbe\u62c9\u5bab\u7684\u591c\u666f\uff0c\u679c\u7136\u66f4\u662f\u8ff7\u4eba\uff0c\u91d1\u94f8\u7684\u5c4b\u9876\uff0c\u5728\u96ea\u5c71\u6620\u7167\u4e4b\u4e0b\uff0c\u53d1\u51fa\u70b9\u70b9\u91d1\u5149\uff0c\u6781\u4e3a \u58ee\u4e3d\uff0c\u5341\u4e09\u5c42\u7684\u5bab\u6bbf\uff0c\u6bcf\u4e00\u5c42\u7684\u98de\u6a90\u7fd8\u89d2\u90fd\u6302\u6709\u7409\u7483\u706f\u9970\uff0c\u706f\u5149\u3001\u6708\u5149\u3001\u96ea\u5149\u3001\u91d1\u5149\uff0c\u5149\u8f89 \u5f71\u5c04\uff0c\u58ee\u4e3d\u4e4b\u4e2d\u53c9\u6709\u4e00\u79cd\u795e\u79d8\u7684\u6c14\u6c1b\u3002\u6842\u534e\u751f\u65e0\u5fc3\u89c2\u8d4f\uff0c\u5fc3\u4e2d\u6240\u5ff5\u53ea\u662f\u767d\u8863\u5c11\u5973\uff01 \u3000\u3000\u5e03\u8fbe\u62c9\u5bab\u91cd\u91cd\u53e0\u53e0\uff0c\u5c4b\u820d\u76d6\u6ee1\u4e86\u5927\u534a\u4e2a\u5c71\uff0c\u4ece\u7b2c\u4e00\u5c42\u5230\u7b2c\u516d\u5c42\u7684\u623f\u5c4b\uff0c\u5168\u90e8\u6ce5\u7740\u767d\u8272 \u7684\uff0c\u85cf\u4eba\u79f0\u4e3a\u201c\u767d\u5be8\u201d\uff1a\u662f\u5bab\u4e2d\u505a\u6cd5\u4e8b\u7684\u5730\u65b9\uff1b\u4ece\u7b2c\u4e03\u5c42\u5230\u7b2c\u5341\u4e09\u5c42\u79f0\u4e3a\u201c\u7ea2\u5be8\u201d\uff0c\u5374\u5206\u522b \u6ce5\u7740\u7ea2\u3001\u9ec4\u3001\u9ed1\u3001\u732a\u7ea2\u56db\u8272\uff0c\u7ea2\u8272\u6ce5\u5899\uff0c\u9ec4\u8272\u6ce5\u6a90\uff0c\u9ed1\u8272\u5219\u6ce5\u5728\u9876\u7aef\u623f\u7ba1\u4e0e\u7a97\u6cbf\u7684\u95f4\u9694\u5904\uff0c \u732a\u7ea2\u8272\u6ce5\u5728\u4e24\u5ea7\u5927\u6bbf\u51f9\u8fdb\u53bb\u7684\u4e00\u90e8\u4efd\uff0c\u5bab\u9876\u5219\u91d1\u78a7\u8f89\u714c\uff0c\u8fdc\u8fdc\u671b\u53bb\uff0c\u597d\u50cf\u4e00\u7247\u4e94\u8272\u7ea6\u70c2\u7684\u623f \u6d77\uff1a\u4ece\u7b2c\u4e03\u5c42\u5230\u7b2c\u5341\u4e09\u5c42\uff0c\u662f\u5bab\u4e2d\u50e7\u4fa3\u5c45\u4f4f\u7684\u5730\u65b9\u3002 \u3000\u3000\u6842\u534e\u751f\u4ece\u5c71\u4e0b\u8fd9\u4e00\u7247\u7ea6\u70c2\u7684\u80a9\u6d77\uff0c\u5fc3\u4e2d\u5927\u662f\u7574\u66d9\uff0c\u60f3\u9053\uff1a\u201c\u5e03\u8fbe\u62c9\u5bab\u5982\u6b64\u5bbd\u5e7f\uff0c\u534e\u7389\u59b9 \u59b9\u5373\u4f7f\u5728\u8fd9\u5bab\u4e2d\uff0c\u4e5f\u4e0d\u77e5\u5982\u4f55\u5bfb\u627e\uff1f\u800c\u4e14\u706f\u5982\u7e41\u661f\uff0c\u5374\u53c8\u5982\u4f55\u5077\u8fdb\uff1f\u201d\u773c\u770b\u6708\u4eae\u6e10\u6e10\u79fb\u8fd1\u5929 \u5fc3\uff0c\u5fc3\u4e2d\u5927\u6025\uff0c\u6700\u540e\u6cf1\u5b9a\u4e0d\u8bba\u5982\u4f55\uff0c\u4e5f\u8981\u8fdb\u5e03\u8fbe\u62c9\u5bab\u4e00\u63a2\u3002 \u3000\u3000\u4eba\u7fa4\u6324\u5728\u5e03\u8fbe\u62c9\u5bab\u5c71\u4e0b\uff0c\u6842\u534e\u751f\u5374\u5077\u5077\u7ed5\u8fc7\u5c71\u810a\uff0c\u501f\u7740\u5ca9\u77f3\u8349\u6728\u7684\u906e\u853d\uff0c\u86c7\u884c\u5154\u4f0f\uff0c\u6e10 \u6e10\u722c\u8fd1\u4e86\u5e03\u8fbe\u62c9\u5bab\uff0c\u4ed6\u65e9\u51c6\u5907\u597d\u4e86\u4e00\u5957\u5587\u561b\u670d\u9970\uff0c\u6084\u6084\u6362\u4e0a\uff0c\u7b49\u4e86\u4e00\u4f1a\uff0c\u8d81\u7740\u53f0\u98ce\u4e4b\u9645\uff0c\u62ac \u8d77\u4e86\u51e0\u9897\u77f3\u5b50\uff0c\u8f7b\u8f7b\u4e00\u5f39\uff0c\u5c06\u504f\u897f\u4e00\u5c42\u90a3\u5ea7\u5927\u95e8\u4e0a\u7684\u4e09\u76cf\u7409\u7483\u706f\u6253\u788e\uff0c\u897f\u85cf\u9ad8\u539f\uff0c\u98ce\u52bf\u672c \u70c8\uff0c\u4f55\u51b5\u5e03\u8fbe\u62c9\u5bab\u5efa\u5728\u5c71\u4e0a\uff0c\u6240\u4ee5\u706f\u9970\u90fd\u6709\u9632\u98ce\u8bbe\u5907\uff0c\u90a3\u5b88\u95e8\u7684\u5587\u561b\u5728\u5927\u98ce\u8fc7\u540e\uff0c\u53d1\u89c9\u7409\u7483 \u7834\u788e\uff0c\u9887\u4e3a\u5947\u602a\uff0c\u5600\u5600\u5495\u5495\uff1a\u201c\u600e\u4e48\u4eca\u665a\u7684\u98ce\u52bf\u8fd9\u6837\u5389\u5bb3\uff1f\u201d\u614c\u4e0d\u8fed\u7684\u62c5\u4e86\u68af\u5b50\uff0c\u6362\u4e0a\u65b0 \u706f\uff0c\u6842\u534e\u751f\u5219\u8d81\u6b64\u65f6\u673a\uff0c\u65bd\u5c55\u7edd\u5999\u7684\u8f7b\u529f\uff0c\u5018\u8fdb\u4e86\u5927\u95e8\u3002\u5b88\u95e8\u7684\u5587\u561b\u6b63\u5728\u957f\u68af\u4e4b\u4e0a\uff0c\u4e00\u70b9\u4e5f \u6ca1\u6709\u53d1\u89c9\u3002 \u3000\u3000\u6842\u534e\u751f\u4f4e\u5934\u5408\u4ec0\uff0c\u628a\u8888\u88df\u62c9\u8d77\uff0c\u906e\u8fc7\u4e86\u534a\u8fb9\u9762\u5b54\uff0c\u9047\u89c1\u5587\u561b\uff0c\u5c31\u8fdc\u8fdc\u95ea\u5f00\uff0c\u5bab\u5185\u5587\u561b\u4f17 \u591a\uff0c\u522b\u4e2a\u5587\u561b\u89c1\u4ed6\u4e00\u6837\u7684\u670d\u9970\uff0c\u4e0d\u4f1a\u7279\u522b\u6ce8\u610f\uff0c\u7adf\u88ab\u4ed6\u6df7\u8fc7\u4e86\u597d\u51e0\u5ea7\u5bab\u6bbf\u3002 \u3000\u3000\u5bab\u4e2d\u58c1\u753b\u7433\u7405\uff0c\u6bd4\u4e4b\u65e5\u95f4\u6240\u89c1\uff0c\u80dc\u8fc7\u6570\u5341\u767e\u500d\uff0c\u6b64\u5916\u50cf\u5bab\u706f\u3001\u7389\u5668\u3001\u53e4\u5f0f\u684c\u6905\u3001\u9999\u6848\u3001 \u53e4\u8001\u7684\u9999\u7089\u3001\u540d\u5bb6\u7684\u5f69\u7ee3\u2026\u2026\u7b49\u7b49\u534e\u4e3d\u88c5\u9970\uff0c\u5e94\u6709\u5c3d\u6709\uff0c\u6842\u534e\u751f\u6697\u6697\u53f9\u9053\uff1a\u201c\u60f3\u6765\u7687\u5bab\u4e4b\u4e2d \u4ea6\u4e0d\u8fc7\u5982\u662f\u3002\u201d\u4f46\u53ef\u60dc\u662f\u5306\u5306\u4e00\u77a5\uff0c\u800c\u4e14\u5fc3\u4e2d\u6709\u4e8b\uff0c\u4ea6\u65e0\u6687\u6d41\u8fde\u3002 \u3000\u3000\u8033\u542c\u5f97\u4e09\u66f4\u9f13\u54cd\uff0c\u6842\u534e\u751f\u5df2\u5077\u5165\u4e86\u7b2c\u5341\u4e8c\u5c42\u8fbe\u8d56\u6d3b\u4f5b\u7684\u5bdd\u5bab\uff0c\u6842\u534e\u751f\u81ea\u5df1\u8fd8\u4e0d\u77e5\u9053\u3002\u5ffd \u89c1\u6709\u4e24\u4e2a\u5927\u5587\u561b\u8d70\u8fd1\uff0c\u6842\u534e\u751f\u9690\u8eab\u5728\u4f5b\u50cf\u4e4b\u540e\uff0c\u53ea\u542c\u5f97\u4e00\u4e2a\u8bf4\u9053\uff1a\u201c\u6d3b\u4f5b\u8fd9\u4e48\u665a\u4e86\uff0c\u8fd8\u63a5\u89c1 \u5bbe\u5ba2\uff0c\u53ef\u771f\u7d2f\u4e86\u3002\u201d\u53e6\u4e00\u4e2a\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u77e5\u9053\u4eca\u5929\u6765\u7684\u90fd\u662f\u5c0a\u8d35\u7684\u5ba2\u4eba\uff0c\u8fde\u4f69\u6709\u8d1d\u53f6\u7075\u7b26\u7684 \u5973\u62a4\u6cd5\u4e5f\u6765\u4e86\u5462\u3002\u53ea\u6015\u6d3b\u4f5b\u8fd8\u8981\u63a5\u89c1\u8fd9\u4f4d\u5973\u62a4\u6cd5\u3002\u201d\u5148\u5934\u90a3\u4e2a\u9053\uff1a\u201c\u6d3b\u4f5b\u7279\u522b\u4e3a\u5979\u6e05\u626b\u4e86\u4e00 \u5ea7\u5bdd\u5bab\uff0c\u8bf7\u4e86\u85cf\u738b\u7684\u4e24\u4f4d\u516c\u4e3b\u966a\u5979\uff0c\u542c\u8bf4\u54b1\u4eec\u8fd9\u5ea7\u5e03\u8fbe\u62c9\u5bab\uff0c\u5728\u8fbe\u8d56\u4e8c\u4e16\u7684\u65f6\u5019\uff0c\u6709\u4e00\u4f4d\u5370 \u5ea6\u516c\u4e3b\uff0c\u4e5f\u662f\u4f69\u6709\u8d1d\u53f6\u7075\u7b26\u7684\u5973\u62a4\u6cd5\u5728\u8fd9\u91cc\u4f4f\u8fc7\u4e00\u665a\u4e4b\u540e\uff0c\u8fd9\u4e48\u591a\u5e74\u6765\uff0c\u5c31\u4ece\u65e0\u5973\u5b50\u88ab\u51c6\u8bb8 \u8fdb\u5bab\uff0c\u6240\u4ee5\u8fd9\u6b21\u771f\u662f\u65f7\u4e16\u96be\u9022\u7684\u4e8b\u5462\uff01\u201d\u6842\u534e\u751f\u5fc3\u4e2d\u4e00\u52a8\uff0c\u60f3\u9053\uff1a\u201c\u90a3\u91cc\u6765\u7684\u5973\u62a4\u6cd5\uff0c\u5c45\u7136 \u80fd\u8499\u6d3b\u4f5b\u4f18\u793c\uff0c\u5e03\u8fbe\u62c9\u5bab\u4e5f\u8981\u7834\u4f8b\u606d\u8fce\uff1f\u201d \u3000\u3000\u6842\u534e\u751f\u6084\u6084\u7684\u8ddf\u5728\u4ed6\u4eec\u540e\u9762\uff0c\u4e0a\u5230\u4e86\u7b2c\u4e09\u5c42\u697c\u3002\u7b49\u5230\u4ed6\u4eec\u7980\u544a\u4e4b\u540e\u4e0b\u697c\uff0c\u4fbf\u6084\u6084\u7684\u8d70\u5230 \u7a97\u4e0b\uff0c\u4f46\u89c1\u91cc\u9762\u706f\u70db\u8f89\u714c\uff0c\u7eb1\u7a97\u4e0a\u73b0\u51fa\u4e24\u4e2a\u5f71\u5b50\uff0c\u4e00\u4e2a\u662f\u6d3b\u4f5b\uff0c\u4e00\u4e2a\u6b63\u662f\u8036\u5c3c\u6cca\u5c14\u7684\u7ea2\u8863\u50e7 \u4eba\u3002 \u3000\u3000\u53ea\u542c\u5f97\u90a3\u7ea2\u8863\u756a\u50e7\u8bf4\u9053\uff1a\u201c\u6d3b\u4f5b\u4ee5\u7edd\u5927\u795e\u901a\uff0c\u5b8f\u626c\u4f5b\u6cd5\uff0c\u90bb\u56fd\u5c0f\u90a6\uff0c\u540c\u6c90\u601d\u5149\uff0c\u655e\u738b\u5b50 \u672c\u8981\u752b\u6765\u53c2\u8c12\uff0c\u53ea\u4ee5\u56fd\u4e2d\u6709\u4e8b\uff0c\u96be\u53c2\u76db\u5178\uff0c\u7279\u6d3e\u5c0f\u50e7\u5e03\u65bd\u91d1\u5854\uff0c\u5e76\u4ee3\u81f4\u610f\uff0c\u656c\u8bf7\u8bad\u793a\u3002\u201d\u8fbe \u8d56\u6d3b\u4f5b\u9053\uff1a\u201c\u8d35\u56fd\u662f\u4f5b\u79df\u8bde\u751f\u4e4b\u5730\uff0c\u81ea\u53e4\u4ee5\u6765\uff0c\u5373\u4e3a\u4f5b\u56fd\uff0c\u5386\u4ee3\u8d24\u738b\uff0c\u62a4\u6301\u5723\u6cd5\uff0c\u6211\u4f5b\u4f51 \u62a4\uff0c\u56fd\u8fd0\u5fc5\u660c\uff0c\u8d35\u738b\u5b50\u6b64\u6b21\u5e03\u65bd\u91d1\u5854\uff0c\u5408\u5bfa\u540c\u611f\uff0c\u4e5f\u8bf7\u4f60\u4ee3\u4e3a\u81f4\u8c22\u3002\u201d\u7ea2\u8863\u756a\u50e7\u7ee7\u9053\uff1a\u201c\u655d \u738b\u5b50\u8fd8\u6709\u4e00\u4e8b\u7980\u544a\u6d3b\u4f5b\u3002\u201d\u8fbe\u8d56\u9053\uff1a\u201c\u8bf7\u8bf4\u3002\u201d\u7ea2\u8863\u756a\u50e7\u9053\uff1a\u201c\u767d\u6559\u6cd5\u738b\uff0c\u9063\u6709\u4f7f\u8005\u6765\u81f3\u655d \u56fd\uff0c\u8bf7\u655d\u56fd\u52a9\u4ed6\u8fd4\u56de\u897f\u85cf\uff0c\u655d\u56fd\u738b\u5b50\u4ee5\u9ec4\u6559\u65b9\u662f\u6b63\u6559\uff0c\u8fbe\u8d56\u73ed\u7985\u59cb\u662f\u6d3b\u4f5b\uff0c\u6545\u6b64\u5bf9\u4e8e\u767d\u6559\u6cd5 \u738b\u4e4b\u8bf7\uff0c\u5a49\u4e88\u62d2\u7edd\u3002\u738b\u5b50\u8bf4\uff0c\u6b64\u4e8b\u5e94\u6d3b\u4f5b\u95fb\u77e5\u3002\u201d\u6842\u534e\u751f\u542c\u5230\u8fd9\u91cc\uff0c\u5fc3\u4e2d\u6697\u9a82\uff0c\u660e\u660e\u662f\u5c3c\u6cca \u5c14\u7684\u738b\u5b50\u5506\u4f7f\u524d\u4efb\u7684\u767d\u6559\u6cd5\u738b\u8fdb\u653b\u897f\u85cf\uff0c\u5982\u4eca\u5374\u53c8\u8fd9\u7b49\u6311\u62e8\uff0c\u60f9\u4e8b\u751f\u975e\uff0c\u770b\u6765\u5b9e\u662f\u60f3\u5728\u897f\u85cf \u6311\u8d77\u5e72\u6208\uff0c\u4ee5\u4fbf\u4ed6\u6df7\u6c34\u6478\u9c7c\uff0c\u4ece\u4e2d\u53d6\u5229\u3002 \u3000\u3000\u6b63\u60f3\u95ef\u8fdb\u53bb\u63ed\u7834\uff0c\u5ffd\u89c9\u80cc\u540e\u5fae\u98ce\u67ab\u7136\uff0c\u6842\u534e\u751f\u53cd\u624b\u4e00\u638c\uff0c\u53ea\u542c\u5f97\u6709\u4eba\u7528\u85cf\u8bed\u5927\u58f0\u9053\uff1a \u201c\u5927\u80c6\u6076\u5f92\uff0c\u7adf\u6562\u64c5\u95ef\u5723\u5bab\uff01\u201d\u968f\u5373\u4e00\u80a1\u52b2\u98ce\uff0c\u5411\u80cc\u5fc3\u5927\u7a74\u75be\u88ad\uff0c\u6842\u534e\u751f\u7684\u5288\u7a7a\u638c\u7adf\u7136\u963b\u4ed6 \u4e0d\u4f4f\u3002 \u3000\u3000\u56de\u5934\u770b\u65f6\uff0c\u8896\u89c1\u4e24\u4e2a\u50e7\u4eba\uff0c\u4e00\u62ab\u9ed1\u888d\uff0c\u4e00\u62ab\u9ec4\u888d\uff0c\u5934\u7f20\u767d\u5e03\uff0c\u8138\u4f3c\u7384\u575b\uff0c\u4e43\u662f\u4e24\u4e2a\u5370\u5ea6 \u884c\u811a\u50e7\u4eba\u3002\u8fd1\u8eab\u7684\u90a3\u4e2a\u9ed1\u888d\u50e7\u4eba\uff0c\u6b63\u7528\u4e00\u652f\u7af9\u6756\uff0c\u51fa\u624b\u5982\u98ce\uff0c\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u5df2\u8fde\u70b9\u6842\u534e\u751f\u4e0a\u5904 \u5927\u7a74\u3002\u53e6\u4e00\u4e2a\u9ec4\u888d\u50e7\u4eba\uff0c\u624b\u6258\u7d2b\u91d1\u5b5f\u788e\uff0c\u864e\u89c6\u803d\u803d\uff0c\u770b\u6765\u4e5f\u5c31\u8981\u51fa\u624b\u3002 \u3000\u3000\u6842\u534e\u751f\u5fc3\u4e2d\u4e00\u51db\uff0c\u8fd9\u9ed1\u888d\u50e7\u4eba\u70b9\u7a74\u7684\u624b\u6cd5\u654f\u6377\u72e0\u51c6\uff0c\u5b9e\u4e0d\u5728\u4e2d\u539f\u7684\u4e00\u6d41\u9ad8\u624b\u4e4b\u4e0b\uff0c\u6b63\u6b32 \u5206\u8fa8\uff0c\u90a3\u9ec4\u888d\u50e7\u4eba\u4e5f\u5927\u58f0\u53eb\u9053\uff1a\u201c\u5c06\u4ed6\u64d2\u4e0b\u4fbf\u662f\uff0c\u4e0d\u53ef\u60ca\u52a8\u6d3b\u4f5b\u3002\u201d\u624b\u4e2d\u7684\u91d1\u5b5f\u94b5\u4e00\u7ffb\uff0c\u4f46 \u89c9\u4e00\u80a1\u5927\u529b\uff0c\u6709\u5982\u6cf0\u5c71\u538b\u9876\uff0c\u5019\u7684\u5c31\u7f69\u5230\u4e86\u4f73\u534e\u751f\u7684\u9876\u95e8\u3002 \u3000\u3000\u6842\u534e\u751f\u65e0\u6687\u5206\u8fa8\uff0c\u6025\u5fd9\u62d4\u51fa\u817e\u86df\u5b9d\u5251\uff0c\u4f46\u542c\u5f97\u5f53\u4e00\u58f0\uff0c\u58f0\u5982\u949f\u58f0\uff0c\u5b9d\u5251\u523a\u5165\u94b5\u4e2d\uff0c\u90a3\u9ec4 \u888d\u50e7\u4eba\u5c06\u5b5f\u94b5\u98de\u4e00\u822c\u7684\u65cb\u8f6c\uff0c\u7adf\u4f3c\u9690\u9690\u751f\u51fa\u4e00\u80a1\u5438\u529b\uff0c\u817e\u86df\u5b9d\u5251\u5728\u6025\u5207\u4e4b\u95f4\uff0c\u7adf\u7136\u62bd\u4e0d\u51fa \u6765\uff1a\u6842\u534e\u751f\u5403\u4e86\u4e00\u60ca\uff0c\u8fd9\u5370\u5ea6\u50e7\u4eba\u7684\u6b66\u529f\u597d\u602a\uff01\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u53e6\u4e00\u4e2a\u5370\u5ea6\u50e7\u4eba\u6756\u5934\u4e00 \u7fd8\uff0c\u4e58\u52bf\u4fbf\u622e\u6842\u534e\u751f\u80f8\u53e3\u7684\u201c\u7487\u7391\u7a74\u201d\u3001\u80c1\u4e0b\u7684\u201c\u7ae0\u95e8\u7a74\u201d\u548c\u8111\u540e\u7684\u201c\u98ce\u5e9c\u7a74\u201d\uff0c\u8fd9\u4e09\u5904\u7a74 \u9053\u9887\u6709\u8ddd\u79bb\uff0c\u4f46\u4ed6\u6756\u52bf\u98d8\u5ffd\uff0c\u6296\u624b\u4e4b\u95f4\u5e7b\u8d77\u4e86\u65e0\u6570\u6756\u5f71\uff0c\u8fd9\u4e09\u5904\u7a74\u9053\uff0c\u7adf\u7136\u90fd\u5728\u4ed6\u7684\u6756\u5f71\u7b3c \u7f69\u4e4b\u4e0b\uff01 \u3000\u3000\u4f46\u6842\u534e\u751f\u662f\u5929\u5c71\u4e0a\u5251\u7684\u540e\u4ee3\uff0c\u8fbe\u6469\u5251\u6cd5\u7684\u4f20\u4eba\uff0c\u867d\u5371\u4e0d\u4e71\uff0c\u90a3\u9ed1\u888d\u50e7\u4eba\u7684\u7af9\u6756\u521a\u521a\u6296 \u8d77\uff0c\u4ed6\u5ffd\u5730\u5927\u559d\u4e00\u58f0\u3002\u820c\u7efd\u6625\u96f7\uff0c\u9739\u96f3\u75be\u964d\uff0c\u9ed1\u888d\u50e7\u4eba\u9661\u7136\u4e00\u60ca\uff0c\u7af9\u6756\u5931\u53bb\u4e86\u51c6\u5934\uff0c\u88ab\u6842\u534e \u751f\u4e00\u624b\u6293\u4f4f\u6756\u5934\uff0c\u5f80\u524d\u4e00\u8fed\uff0c\u9ed1\u888d\u50e7\u4eba\u767b\u65f6\u56db\u811a\u671d\u5929\u3002\u539f\u6765\u6842\u534e\u751f\u7528\u7684\u662f\u4e0a\u4e58\u7684\u201c\u72ee\u5b50\u543c \u529f\u201d\uff0c\u5bfb\u5e38\u4e4b\u4eba\u88ab\u4ed6\u4e00\u559d\uff0c\u5fc3\u80ba\u4ff1\u5236\uff0c\u8fd9\u4e24\u4e2a\u50e7\u4eba\u80fd\u591f\u62b5\u53d7\u5f97\u4f4f\uff0c\u5185\u529f\u4ea6\u5b9e\u662f\u4e0d\u5f31\u7684\u4e86\u3002 \u3000\u3000\u7528\u91d1\u5b5f\u94b5\u5438\u7740\u6842\u534e\u751f\u5b9d\u5251\u7684\u90a3\u4e2a\u50e7\u4eba\uff0c\u529f\u529b\u66f4\u4e3a\u6df1\u539a\uff0c\u867d\u7136\u4e5f\u5403\u4e86\u4e00\u60ca\uff0c\u4e0d\u8fc7\u4ec5\u4ec5\u9000\u4e86 \u4e24\u6b65\uff0c\u6842\u534e\u751f\u4f55\u7b49\u673a\u7075\uff0c\u8d81\u4ed6\u6c14\u9981\u540e\u9000\u4e4b\u65f6\uff0c\u5b9d\u5251\u7528\u529b\u4e00\u63d2\uff0c\u53ea\u542c\u5f97\u4e00\u7247\u788e\u91d1\u590f\u7389\u4e4b\u58f0\uff0c\u91d1 \u5b5f\u94b5\u7ed9\u4ed6\u7684\u817e\u86df\u5b9d\u5251\u6233\u7a7f\u4e00\u6d1e\uff0c\u6842\u534e\u751f\u7acb\u5373\u628a\u5b9d\u5251\u62bd\u4e86\u51fa\u6765\uff0c\u90a3\u9ec4\u888d\u50e7\u4eba\u6599\u4e0d\u5230\u5b83\u7684\u5b9d\u5251\u7adf \u6709\u6d1e\u91d1\u524a\u94c1\u4e4b\u80fd\uff0c\u60ca\u5f97\u5446\u4e86\uff01 \u3000\u3000\u6842\u534e\u751f\u90a3\u4e00\u58f0\u5927\u543c\uff0c\u679c\u7136\u60ca\u52a8\u4e86\u623f\u4e2d\u7684\u8fbe\u8d56\u6d3b\u4f5b\uff0c\u7acb\u5373\u8d70\u51fa\u697c\u6765\u3002\u6842\u534e\u751f\u53eb\u9053\uff1a\u201c\u6d3b\u4f5b \u5bb9\u7980\u2026\u2026\u201d\u8bdd\u58f0\u521a\u521a\u51fa\u53e3\uff0c\u90a3\u9ec4\u888d\u618e\u4eba\u7684\u7d2b\u91d1\u5b5f\u94b5\u7a81\u7136\u51fa\u624b\uff0c\u631f\u7740\u4e00\u6e9c\u91d1\u5149\uff0c\u9690\u9690\u5e26\u7740\u98ce\u96f7 \u4e4b\u58f0\uff0c\u76d8\u65cb\u98de\u81f3\uff0c\u5f53\u5934\u7f69\u4e0b\uff0c\u6842\u534e\u751f\u60f3\u4e0d\u5230\u5b83\u7684\u7d2b\u91d1\u5b5f\u94b5\uff0c\u4e0d\u80fd\u5f53\u4f5c\u6697\u5668\u4f7f\u7528\uff0c\u98de\u51fa\u53bb\u6253 \u4eba\uff0c\u4e0d\u6562\u6020\u6162\uff0c\u6025\u5fd9\u65bd\u5c55\u201c\u76d8\u9f99\u7ed5\u6b65\u201d\u7684\u8eab\u6cd5\uff0c\u7565\u907f\u5176\u950b\uff0c\u968f\u5373\u5e73\u5251\u4e00\u6863\u3002 \u3000\u3000\u4f46\u542c\u5f97\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u7d2b\u91d1\u5b5f\u94b5\u5728\u5251\u8fb9\u4e00\u64e6\uff0c\u7acb\u5373\u98de\u56de\uff0c\u90a3\u9ed1\u888d\u50e7\u4eba\u7684\u7af9\u6756\u8ddf\u7740\u70b9\u81f3\uff0c \u5c3c\u6cca\u5c14\u7684\u90a3\u4e2a\u7ea2\u8863\u5587\u561b\u559d\u9053\uff1a\u201c\u5927\u80c6\u72c2\u5f92\uff0c\u64c5\u5165\u5723\u5bab\uff0c\u4eb5\u6e0e\u6d3b\u4f5b\uff0c\u7f6a\u8be5\u4e07\u6b7b\uff01\u6d3b\u4f5b\u6cd5\u9a7e\u8bf7 \u56de\uff0c\u8d2b\u50e7\u66ff\u4f60\u5c06\u4ed6\u6599\u7406\uff01\u201d\u88c5\u88c5\u4e00\u5c55\uff0c\u6709\u5982\u4e00\u6735\u706b\u4e91\uff0c\u968f\u7740\u9ec4\u888d\u50e7\u4eba\u90a3\u4e2a\u53c8\u98de\u56de\u6765\u7684\u7d2b\u91d1\u5b5f \u94b5\uff0c\u540c\u65f6\u7f69\u4e0b\u3002 \u3000\u3000\u6842\u534e\u751f\u4e00\u4eba\u4e00\u5251\uff0c\u529b\u6218\u4e09\u4e2a\u9ad8\u624b\uff0c\u5e94\u4ed8\u4e0d\u6687\uff0c\u90a3\u91cc\u8fd8\u80fd\u5206\u5fc3\u8bf4\u8bdd\uff1f\u8fbe\u8d56\u6d3b\u4f5b\u7741\u773c\u4e00\u77a7\uff0c \u89c1\u6842\u534e\u751f\u9888\u9879\u4e0b\u6302\u6709\u4e00\u5c0a\u91d1\u4f5b\uff0c\u90a3\u662f\u9ea6\u58eb\u8fe6\u5357\u7ed9\u6842\u534e\u751f\u4f5c\u4e3a\u500d\u7269\u7684\uff0c\u8fbe\u8d56\u81ea\u7136\u8ba4\u5f97\u8fd9\u662f\u767d\u6559 \u6cd5\u738b\u4f20\u5bb6\u7684\u4e03\u4ef6\u6cd5\u5668\u4e4b\u4e00\uff0c\u5fc3\u4e2d\u5927\u7591\uff0c\u53ea\u9053\u6842\u534e\u751f\u662f\u767d\u6559\u6cd5\u738b\u7684\u523a\u5ba2\uff0c\u4f46\u8f6c\u5ff5\u4e00\u60f3\uff0c\u767d\u6559\u6cd5 \u738b\u867d\u7136\u6ca1\u6709\u81ea\u5df1\u7684\u5c0a\u8d35\uff08\u8fbe\u8d56\u5728\u201c\u6d3b\u4f5b\u201d\u4e4b\u4e2d\uff0c\u53c8\u662f\u81f3\u9ad8\u65e0\u4e0a\u7684\u6d3b\u4f5b\u3002\uff09\uff0c\u800c\u4e14\u4e5f\u4e0e\u81ea\u5df1\u654c \u5bf9\uff0c\u4f46\u4ed6\u5230\u5e95\u662f\u4e00\u6559\u4e4b\u4e13\uff0c\u4e5f\u662f\u6d3b\u4f5b\u7684\u8eab\u4efd\uff0c\u60f3\u6765\u4e0d\u8be5\u51fa\u6b64\u4e0b\u7b56\uff1f\u56e0\u6b64\u53c8\u6000\u7591\u6842\u534e\u751f\u662f\u767d\u6559 \u6cd5\u738b\u7684\u4f7f\u8005\uff0c\u731c\u7591\u4e0d\u5b9a\uff0c\u6545\u6b64\u4ed6\u65e2\u6ca1\u6709\u559d\u6b62\uff0c\u4e5f\u6ca1\u6709\u56de\u53bb\uff0c\u5374\u5728\u4e24\u4e2a\u62a4\u6cd5\u5587\u561b\u7684\u4fdd\u62a4\u4e4b\u4e0b\uff0c \u8d1f\u624b\u89c2\u6218\u3002 \u3000\u3000\u4f46\u89c1\u90a3\u7d2b\u91d1\u5b5f\u94b5\u5f53\u5934\u7f69\u4e0b\uff0c\u6842\u534e\u751f\u65bd\u5c55\u5927\u529b\u7684\u624b\u6cd5\uff0c\u4e00\u638c\u62cd\u51fa\uff0c\u540c\u65f6\u5b9d\u5251\u4e00\u6311\uff0c\u5c06\u7ea2\u8863 \u756a\u50e7\u7684\u8888\u88df\u6311\u5f00\uff0c\u4e00\u4e2a\u8f6c\u8eab\u53c8\u95ea\u5f00\u4e86\u9ed1\u888d\u50e7\u4eba\u7684\u70b9\u7a74\u7af9\u6756\uff0c\u8fbe\u8d56\u6d3b\u4f5b\u867d\u7136\u4e0d\u61c2\u6b66\u529f\uff0c\u4f46\u4ed6\u5374 \u77e5\u9053\u8fd9\u4e24\u4e2a\u5370\u5ea6\u50e7\u4eba\u90fd\u662f\u4e00\u7b49\u4e00\u7684\u9ad8\u624b\uff0c\u800c\u7ea2\u8863\u756a\u50e7\u8eab\u4e3a\u5c3c\u6cca\u5c14\u7684\u56fd\u5e08\u6b66\u529f\u81ea\u4ea6\u4e0d\u5f31\uff0c\u89c1\u6842 \u534e\u751f\u72ec\u6297\u4e09\u5927\u9ad8\u624b\uff0c\u8eab\u6cd5\u7f8e\u5999\uff0c\u5c45\u7136\u6ca1\u6709\u843d\u8d25\uff0c\u4e5f\u4e0d\u7981\u6697\u6697\u559d\u91c7\u3002 \u3000\u3000\u5176\u5b9e\u6842\u534e\u751f\u5374\u662f\u6709\u82e6\u8bf4\u4e0d\u51fa\u6765\uff0c\u8fd9\u4e09\u4e2a\u50e7\u4eba\uff0c\u82e5\u7136\u662f\u4e00\u5bf9\u4e00\u7684\u5355\u6253\u72ec\u6597\uff0c\u6ca1\u4e00\u4e2a\u662f\u4ed6\u5bf9 \u624b\uff0c\u4f46\u4ee5\u4e00\u5bf9\u4e09\uff0c\u5374\u662f\u96be\u4e8e\u5e94\u4ed8\uff0c\u5c24\u5176\u90a3\u9ec4\u888d\u50e7\u4eba\u7684\u7d2b\u91d1\u5b5f\u94b5\uff0c\u65e2\u53ef\u4f5c\u5175\u5668\u4f7f\u7528\uff0c\u53c8\u53ef\u4f5c\u6697 \u5668\u6253\u4eba\uff0c\u66f4\u662f\u9632\u4e0d\u80dc\u9632\u3002\u5e78\u800c\u4ed6\u4ed7\u7740\u5b9d\u5251\u7684\u5a01\u529b\uff0c\u8981\u4e0d\u7136\u65e9\u5df2\u8d25\u9635\u3002 \u3000\u3000\u90a3\u4e09\u4e2a\u50e7\u4eba\u8d8a\u8feb\u201c\u8d8a\u7d27\u201d\uff0c\u6842\u534e\u751f\u5251\u638c\u517c\u65bd\uff0c\u4f7f\u51fa\u4e86\u6d51\u8eab\u672c\u9886\uff0c\u4ecd\u662f\u88ab\u4ed6\u4eec\u8feb\u5f97\u6b65\u6b65\u540e \u9000\uff0c\u5305\u56f4\u5708\u8d8a\u7f29\u8d8a\u5c0f\uff0c\u5f62\u52bf\u8d8a\u6765\u8d8a\u9669\uff0c\u90a3\u9ec4\u888d\u50e7\u624b\u6301\u91d1\u94b5\uff0c\u7a81\u7136\u7eb5\u8eab\u63a0\u8d77\uff0c\u91d1\u94b5\u4e00\u7ffb\uff0c\u52bf\u5982 \u6cf0\u5c71\u538b\u9876\uff0c\u6842\u534e\u751f\u51fa\u638c\u76f8\u6297\uff0c\u7adf\u7136\u88ab\u5b83\u5438\u4f4f\uff0c\u540c\u65f6\u9ed1\u888d\u50e7\u4eba\u7684\u7af9\u6756\u4e5f\u70b9\u5230\u80f8\u524d\uff0c\u6842\u534e\u751f\u4e00\u5265 \u524a\u51fa\uff0c\u5374\u88ab\u7ea2\u8863\u756a\u50e7\u6a2a\u91cc\u7a9c\u6765\uff0c\u8888\u88df\u4e00\u6296\uff0c\u5c06\u5b83\u7684\u5b9d\u5251\u88f9\u4f4f\uff0c\u201c\u535c\u201d\u7684\u4e00\u58f0\uff0c\u9ed1\u888d\u50e7\u4eba\u7684\u90a3 \u6839\u7af9\u6756\u6b63\u6b63\u622e\u5728\u4ed6\u80f8\u53e3\u7684\u201c\u6a80\u4e2d\u7a74\u201d\u4e0a\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u73af\u4f69\u53ee\u5f53\uff0c\u9999\u98ce\u56db\u6563\uff0c\u4e24\u4e2a\u9ec4\u8863\u5587\u561b\u5148\u4e0a\u697c\u7980\u9053\uff1a\u201c\u5973\u62a4\u6cd5\u53c2\u89c1\u6d3b\u4f5b\u201d\uff0c\u8fbe\u8d56\u6d3b \u4f5b\u6025\u5fd9\u8bf4\u201c\u8bf7\uff01\u201d\u6842\u534e\u751f\u6b63\u81ea\u8fd0\u7528\u4e0a\u4e58\u7684\u5185\u529f\uff0c\u808c\u5185\u5e73\u7a7a\u7f29\u51e0\u5bf8\uff0c\u5438\u4f4f\u6446\u888d\u50e7\u4eba\u7684\u7af9\u6756\uff0c\u95fb \u8a00\u53c8\u60ca\u53c8\u559c\uff0c\u659c\u773c\u4e00\u77a5\uff0c\u53ea\u89c1\u4e00\u4e2a\u5c11\u5973\u76c8\u76c8\u7684\u8d70\u4e0a\u697c\u6765\uff0c\u53ef\u4e0d\u6b63\u662f\u81ea\u5df1\u65e5\u601d\u591c\u60f3\u7684\u90a3\u4e2a\u767d\u8863 \u5c11\u5973\uff01 \u3000\u3000\u767d\u8863\u5c11\u5973\u7389\u624b\u4e00\u6307\uff0c\u65a5\u9053\uff1a\u201c\u4f60\u4e0d\u56de\u56fd\uff0c\u5728\u6b64\u4f55\u4e3a\uff1f\u201d\u90a3\u7ea2\u8863\u756a\u50e7\u9762\u8272\u5927\u53d8\uff0c\u8888\u88df\u4e00 \u679a\uff0c\u5411\u8fbe\u8d56\u6d3b\u4f5b\u5408\u4ec0\u65bd\u793c\uff0c\u8fbe\u8d56\u9053\uff1a\u201c\u5973\u62a4\u6cd5\u53eb\u4f60\u56de\u53bb\uff0c\u6211\u4e0d\u591a\u7559\u4f60\u4e86\u3002\u201d\u7ea2\u4f9d\u756a\u50e7\u7528\u5c3c\u6cca \u5c14\u8bdd\u5495\u5495\u565c\u565c\u7684\u8bf4\u4e86\u51e0\u65ec\uff0c\u7acb\u5373\u8d70\u51fa\u5e03\u8fbe\u62c9\u5bab\u3002 \u3000\u3000\u6842\u534e\u751f\u7684\u5b9d\u5251\u672c\u6765\u88ab\u7ea2\u8863\u756a\u50e7\u7684\u8888\u88df\u88f9\u7740\uff0c\u8888\u88df\u4e00\u6492\uff0c\u5b9d\u5251\u7acb\u523b\u524a\u51fa\uff0c\u5f53\u7684\u4e00\u58f0\uff0c\u53c8\u628a \u9ec4\u888d\u5055\u4eba\u7684\u7d2b\u91d1\u5b5f\u94b5\u524a\u53bb\u4e86\u4e00\u5927\u7247\u3002\u5c31\u5728\u6b64\u65f6\uff0c\u53ea\u542c\u5f97\u767d\u8863\u5c11\u5973\u548c\u8fbe\u8d56\u6d3b\u4f5b\u8bf4\u4e86\u51e0\u53e5\u8bdd\uff0c\u8fbe \u8d56\u5411\u90a3\u4e24\u4e2a\u50e7\u4eba\u6325\u624b\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u4ece\u4e2d\u56fd\u6765\u7684\u5c45\u58eb\u4e0d\u4f46\u4e0d\u662f\u523a\u5ba2\uff0c\u800c\u4e14\u6709\u529f\u4f5b\u95e8\uff0c\u4e24\u4f4d\u8bf7\u4f4f \u624b\u5427\u3002\u201d\uff0c\u5176\u5b9e\u7ea2\u8863\u756a\u50e7\u4e00\u8d70\uff0c\u8fd9\u4e24\u4e2a\u50e7\u4eba\u5373\u7b97\u8054\u624b\u5408\u6597\uff0c\u4e5f\u4e0d\u662f\u6842\u534e\u751f\u7684\u5bf9\u624b\uff0c\u6b64\u65f6\u4ed6\u4eec \u6b63\u88ab\u6842\u534e\u751f\u8feb\u5f97\u6c14\u5598\u547c\u547c\uff0c\u8fbe\u8d56\u6d3b\u4f5b\u4e4b\u8a00\u4e00\u51fa\uff0c\u5148\u4f4f\u624b\u7684\u5012\u662f\u6842\u534e\u751f\u3002 \u3000\u3000\u53ea\u89c1\u8fd9\u4e24\u4e2a\u4eba\u6ee1\u8138\u60f6\u6050\u7684\u5764\u60c5\uff0c\u5411\u8fbe\u8d56\u6d3b\u4f5b\u8dea\u4e0b\u987f\u9996\uff0c\u968f\u7740\u53c8\u5411\u767d\u8863\u5c11\u5973\u5c48\u4e86\u534a\u819d\u884c \u793c\uff0c\u6842\u534e\u751f\u867d\u7136\u4e0d\u61c2\u5f97\u4ed6\u4eec\u7684\u8bf4\u8bdd\uff0c\u4e5f\u731c\u5f97\u51fa\u662f\u8bf7\u6c42\u9976\u6055\u7684\u610f\u601d\u3002\u8fd9\u4e24\u4e2a\u50e7\u4eba\u5411\u767d\u8863\u5c11\u5973\u65bd \u793c\u4e4b\u540e\u4e5f\u8ddf\u7740\u4e0b\u697c\u53bb\u4e86\u3002 \u3000\u3000\u6842\u534e\u751f\u8fd9\u4e00\u60ca\u8be7\u6bd4\u5728\u9b54\u9b3c\u57ce\u4e2d\u521d\u4f1a\u767d\u8863\u5c11\u5973\u4e4b\u65f6\u66f4\u751a\uff0c\u4ed6\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u8fd9\u767d\u8863\u5c11\u5973\u7adf\u7136 \u662f\u4ec0\u4e48\u5973\u62a4\u6cd5\uff0c\u8fde\u81f3\u5c0a\u65e0\u4e0a\u7684\u8fbe\u8d56\u6d3b\u4f5b\u4e5f\u5bf9\u5979\u751a\u4e3a\u5c0a\u656c\uff01\u6842\u534e\u751f\u8d70\u4e0a\u524d\u6765\uff0c\u5148\u89c1\u8fc7\u4e86\u6d3b\u4f5b\uff0c \u518d\u5411\u767d\u8863\u5c11\u5973\u65bd\u793c\uff0c\u767d\u8863\u5c11\u5973\u76c8\u76c8\u4e00\u7b11\u3002\u7528\u6c49\u8bed\u8bf4\u9053\uff1a\u201c\u5927\u54e5\u54e5\uff0c\u600e\u4e48\u548c\u6211\u5ba2\u6c14\u8d77\u6765\u4e86\u3002\u201d \u3000\u3000\u8fbe\u8d56\u6d3b\u4f5b\u9053\uff1a\u201c\u4f60\u662f\u767d\u6559\u6cd5\u738b\u7684\u4f7f\u8005\u5417\uff1f\u542c\u5973\u62a4\u6cd5\u6240\u8a00\uff0c\u4f60\u5728\u9b54\u9b3c\u57ce\u4e2d\u66fe\u505a\u4e86\u4e00\u4ef6\u5bf9\u897f \u85cf\u6709\u5229\u7684\u4e8b\u60c5\u3002\u201d\u6842\u534e\u751f\u9053\uff1a\u201c\u6211\u6b63\u8981\u8be6\u7980\u4f5b\u9053\u3002\u201d\u6d3b\u4f5b\u767d\u8863\u5c11\u5973\u9053\uff1a\u201c\u4ed6\u662f\u6211\u5728\u4e2d\u56fd\u8ba4\u7684 \u5927\u54e5\u54e5\uff0c\u6d3b\u4f5b\uff0c\u4f60\u53ef\u4ee5\u76f8\u4fe1\u4ed6\u7684\u8bf4\u8bdd\u3002\u6211\u6765\u897f\u85cf\u592a\u4e45\u4e86\uff0c\u89c1\u8fc7\u6d3b\u4f5b\uff0c\u7406\u5f53\u544a\u8f9e\uff0c\u4ed6\u65e5\u6709\u7f18\uff0c \u518d\u6765\u62dc\u8c12\u3002\u201d\u76c8\u76c8\u4e00\u63d6\uff0c\u8d70\u4e0b\u697c\u53f0\uff0c\u6d3b\u4f5b\u5408\u638c\u76f8\u8fed\uff0c\u4f73\u534e\u751f\u60f3\u4e0d\u5230\u521a\u521a\u89c1\u9762\uff0c\u5979\u53c8\u79bb\u5f00\uff0c\u4e4d \u559c\u8fd8\u60b2\uff0c\u6068\u4e0d\u5f97\u7275\u7740\u5979\u7684\u8865\u8896\uff0c\u7136\u800c\u6d3b\u4f5b\u5728\u65c1\uff0c\u4ed6\u53c8\u600e\u6562\u5192\u6627\u65e0\u793c\uff0c\u53ea\u597d\u773c\u7741\u7741\u7684\u770b\u7740\u767d\u8863 \u5c11\u5973\u79bb\u5f00\uff0c\u5fc3\u4e2d\u8bf4\u4e0d\u5c3d\u8f9b\u9178\u7684\u6ecb\u5473\u3002 \u3000\u3000\u8fbe\u8d56\u6d3b\u4f5b\u9053\uff1a\u201c\u5c45\u58eb\u8bf7\u6765\u9759\u5ba4\uff0c\u8be6\u8bf4\u56e0\u7531\u3002\u201d\u6842\u534e\u751f\u628a\u5728\u9b54\u9b3c\u57ce\u4e2d\u7684\u5947\u9047\uff0c\u5bdf\u7834\u5c3c\u6cca\u5c14 \u738b\u5b50\u7684\u91ce\u5fc3\uff0c\u4e0e\u53ca\u9ea6\u58eb\u8fe6\u5357\u6258\u4ed6\u8f6c\u8fbe\u767d\u6559\u6cd5\u738b\u7684\u8bda\u610f\u7b49\u7b49\uff0c\u90fd\u5411\u6d3b\u4f5b\u4e00\u4e00\u8bf4\u4e86\u3002\u8fbe\u8d56\u6d3b\u4f5b\u53f9 \u9053\uff1a\u201c\u897f\u85cf\u7684\u8c1a\u8bed\u6709\u4e91\uff0c\u6b63\u76f4\u7684\u654c\u4eba\u80dc\u8fc7\u670b\u53cb\uff0c\u8bcf\u5a9a\u7684\u5c0f\u4eba\u5fc5\u6709\u6240\u6c42\u3002\u8fd9\u8bdd\u771f\u662f\u4e0d\u9519\u3002\u201d\u968f \u5373\u5429\u5490\u5bab\u4e2d\u7684\u6267\u4e8b\u5587\u561b\u62db\u547c\u6842\u534e\u751f\u4f4f\u5bbf\uff0c\u6b3a\u5f85\u4ee5\u4e0a\u5bbe\u4e4b\u793c\u3002 \u3000\u3000\u6842\u534e\u751f\u95ee\u90a3\u6267\u4e8b\u5587\u561b\uff1a\u201c\u5973\u62a4\u6cd5\u662f\u4ec0\u4e48\u8eab\u4efd\uff1f\u201d\u90a3\u6267\u4e8b\u5587\u561b\u9732\u51fa\u8654\u656c\u7684\u795e\u60c5\uff0c\u5408\u4ec0\u8bf4 \u9053\uff1a\u201c\u51e1\u5bf9\u4f5b\u95e8\u6709\u6781\u5927\u7684\u529f\u5fb7\u7684\uff0c\u624d\u80fd\u88ab\u5c01\u4e3a\u5927\u62a4\u6cd5\u3002\u201d\u6842\u534e\u751f\u9053\uff1a\u201c\u662f\u8c01\u5c01\u7684\uff1f\u201d\u6267\u4e8b\u5587 \u561b\u9053\uff1a\u201c\u5370\u5ea6\u7684\u90a3\u70c2\u9640\u5bfa\u4e3b\u6301\uff0c\u5728\u5370\u5ea6\u7684\u5730\u4f4d\uff0c\u4ea6\u5373\u7b49\u4e8e\u6d3b\u4f5b\u3002\u90a3\u70c2\u9640\u5bfa\u6709\u4e24\u7247\u8d1d\u53f6\u7075\u7b26\uff0c \u76f8\u4f20\u662f\u4f5b\u79df\u4ee5\u524d\u5728\u83e9\u63d0\u6811\u4e0b\u8bb2\u7ecf\u4e4b\u65f6\uff0c\u6458\u4e0b\u6765\u8d50\u7ed9\u5f1f\u5b50\u8fe6\u53f6\u7684\u3002\u90a3\u70c2\u9640\u5bfa\u6bcf\u4e00\u4e2a\u7532\u5b50\u5f00\u4e00\u6b21 \u4f5b\u6559\u5927\u4f1a\uff0c\u5c06\u8d1d\u53f6\u7075\u7b26\u8d60\u7ed9\u5bf9\u4f5b\u6559\u6709\u5927\u529f\u5fb7\u7684\u4eba\uff0c\u5e76\u5c01\u4e3a\u62a4\u6cd5\u3002\u5927\u4f1a\u516d\u5341\u5e74\u4f8b\u5f00\u4e00\u6b21\uff0c\u4f46\u516d \u5341\u5e74\u4e2d\u5374\u672a\u5fc5\u6709\u4e00\u4e2a\u5927\u529f\u5fb7\u7684\u4eba\uff0c\u662f\u6545\u62a4\u6cd5\u96be\u5f97\uff0c\u5973\u62a4\u6cd5\u66f4\u4e3a\u96be\u5f97\uff01\u201d\u6842\u534e\u751f\u542c\u4e86\uff0c\u53c8\u662f\u6b22 \u559c\uff0c\u53c8\u5177\u60ca\u5947\u3002\u7136\u800c\u4ed6\u8fd8\u662f\u672a\u66fe\u660e\u767d\u767d\u8863\u5c11\u5973\u7684\u8eab\u4efd\u3002\u5979\u662f\u600e\u4e48\u505a\u5230\u7684\u5973\u62a4\u6cd5\uff0c\u5230\u5e95\u662f\u4ec0\u4e48 \u4eba\uff1f \u3000\u3000\u5bab\u4e2d\u7684\u6267\u4e8b\u5587\u561b\uff0c\u5bf9\u767d\u8863\u5c11\u5973\u7684\u6765\u5386\uff0c\u5374\u4e0d\u80af\u591a\u8c08\u534a\u53e5\u3002\u7b2c\u4e8c\u65e5\uff0c\u6842\u534e\u751f\u9047\u5230\u4e86\u4ed6\u4eec\uff0c \u518d\u5411\u4ed6\u4eec\u6253\u542c\u65f6\uff0c\u90a3\u767d\u8863\u5c11\u5973\u5374\u65e9\u5df2\u79bb\u5f00\u4e86\u5e03\u8fbe\u62c9\u5bab\u4e86\u3002\u6842\u534e\u751f\u6005\u6005\u60af\u60af\uff0c\u4e5f\u6b32\u544a\u8f9e\uff0c\u90a3\u6267 \u4e8b\u5587\u561b\u8bf4\u9053\uff1a\u201c\u6d3b\u4f5b\u6cd5\u8c15\uff0c\u8bf4\u662f\u5c45\u58eb\u5982\u679c\u6b22\u559c\u7684\u8bdd\uff0c\u53ef\u4ee5\u5728\u5bab\u4e2d\u591a\u4f4f\u751f\u65f6\u3002\u201d\u6842\u534e\u751f\u6b63\u60f3\u5a49 \u8f9e\uff0c\u90a3\u6267\u4e8b\u5587\u561b\u53c8\u9053\uff1a\u201c\u5973\u62a4\u6cd5\u4e34\u8d70\u4e4b\u65f6\uff0c\u7559\u4e0b\u4e86\u51e0\u53e5\u8bf4\u8bdd\uff0c\u6d3b\u4f5b\u53eb\u6211\u4eec\u8f6c\u544a\u4e8e\u4f60\u3002\u201d\u6842\u534e \u751f\u5fd9\u9053\uff1a\u201c\u4ec0\u4e48\u8bf4\u8bdd\uff1f\u201d\u6267\u4e8b\u5587\u561b\u9053\uff1a\u201c\u5973\u62a4\u6cd5\u8bf4\uff0c\u8bf7\u5c45\u58eb\u5230\u5c3c\u6cca\u5c14\u4e00\u884c\uff0c\u82e5\u662f\u6709\u7f18\uff0c\u81ea\u5f53 \u76f8\u89c1\u3002\u201d\u6842\u534e\u751f\u9053\uff1a\u201c\u6211\u6b63\u8981\u53bb\u5c3c\u6cca\u5c14\u3002\u201d\u6267\u4e8b\u5587\u561b\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u5bab\u4e2d\u6709\u901a\u5c3c\u6cca\u5c14\u6587\u7684\u4eba\uff0c \u5c45\u58eb\u5728\u53bb\u5c3c\u6cca\u5c14\u4e4b\u524d\uff0c\u8981\u4e0d\u8981\u5b66\u5b66\uff1f\u201d\u6842\u534e\u751f\u4e00\u60f3\uff0c\u8bed\u8a00\u4e0d\u901a\u5904\u5904\u963b\u788d\uff0c\u4e0e\u5176\u5230\u5c3c\u6cca\u5c14\u518d \u5b66\uff0c\u4e0d\u5982\u5b66\u4e86\u518d\u53bb\u3002\u4fbf\u5728\u5e03\u8fbe\u62c9\u5bab\u4f4f\u4e0b\uff0c\u65e5\u5915\u82e6\u5b66\uff0c\u5b66\u4e86\u4e24\u4e2a\u591a\u6708\uff0c\u4e00\u4e9b\u5bfb\u5e38\u7684\u7528\u8bed\uff0c\u5df2\u5927 \u81f4\u53ef\u4ee5\u5e94\u4ed8\u3002 \u3000\u3000\u8fd9\u65e5\u4e00\u65e9\uff0c\u6842\u534e\u751f\u51b3\u5b9a\u544a\u8f9e\u4e86\uff0c\u5bab\u4e2d\u6267\u4e8b\u5e2e\u4ed6\u53bb\u8c12\u89c1\u8fbe\u8d56\u6d3b\u4f5b\uff0c\u8d70\u4e0a\u4e86\u7b2c\u5341\u4e09\u5c42\u7684\u5bab \u9876\uff0c\u6709\u4e00\u5ea7\u5c4b\u9876\u82b1\u56ed\uff0c\u8fbe\u8d56\u6d3b\u4f5b\u6b63\u5728\u56ed\u4e2d\u6563\u6b65\u3002\u5728\u8fd9\u5ea7\u5c4b\u9876\u82b1\u56ed\u4e4b\u4e0a\uff0c\u4e0d\u4f46\u53ef\u4ee5\u770b\u89c1\u62c9\u8428\u5168 \u57ce\u7684\u666f\u8272\uff0c\u800c\u4e14\u8fd8\u53ef\u4ee5\u773a\u671b\u79ef\u96ea\u77aa\u4f53\u7684\u559c\u9a6c\u62c9\u96c5\u5c71\u97f5\u3002\u6842\u534e\u751f\u8c12\u89c1\u6d3b\u4f5b\uff0c\u544a\u7980\u6765\u610f\uff0c\u8fbe\u8d56\u6d3b \u4f5b\u5341\u5206\u548c\u853c\uff0c\u66ff\u4ed6\u795d\u798f\u4e4b\u540e\uff0c\u53c8\u544a\u8bc9\u4ed6\uff0c\u82e5\u7136\u5230\u4e86\u5c3c\u6cca\u5c14\u4e4b\u540e\uff0c\u6709\u4ec0\u4e48\u56f0\u96be\uff0c\u53ef\u4ee5\u53bb\u89c1\u5c3c\u6cca \u5c14\u56fd\u738b\uff0c\u8bf7\u4ed6\u5e2e\u52a9\u3002\u7ed9\u4e86\u6842\u534e\u751f\u4e00\u5c01\u4e66\u4fe1\uff0c\u53ef\u4ee5\u5728\u5fc5\u8981\u4e4b\u65f6\uff0c\u5c06\u4fe1\u53bb\u89c1\u56fd\u738b\u3002\u53c8\u544a\u8bc9\u4ed6\uff0c\u4ed6 \u5df2\u6d3e\u9063\u4f7f\u8005\u4e0e\u767d\u6559\u6cd5\u738b\u8c26\u548c\uff0c\u767d\u6559\u6cd5\u738b\u51c6\u5907\u6d3e\u51fa\u62a4\u6cd5\u5f1f\u5b50\u5230\u5c3c\u6cca\u5c14\u53bb\u8ffd\u56de\u6cd5\u6756\uff0c\u8fd9\u4e2a\u5f1f\u5b50\uff0c \u53ef\u80fd\u5c31\u662f\u9ea6\u58eb\u8fe6\u5357\uff0c\u95ee\u6842\u534e\u751f\u8981\u4e0d\u8981\u7b49\u5f85\u9ea6\u58eb\u8fe6\u5357\u540c\u53bb\u3002\u6842\u534e\u751f\u60f3\u89c1\u767d\u8863\u5c11\u5973\u4e4b\u5fc3\u751a\u6025\uff0c\u51b3 \u5b9a\u8fd8\u662f\u5355\u72ec\u52a8\u8eab\u3002"}),showOnHomepage:!0},{name:"\u7b2c\u516d\u7ae0\u3000\u73e0\u5cf0\u811a\u4e0b\u4f1a\u5947\u4eba",content:(0,t.jsx)(t.Fragment,{children:'\u8d70\u4e86\u4e00\u4e2a\u591a\u6708\uff0c\u7a7f\u8fc7\u4e86\u6f20\u6f20\u9ec4\u7802\uff0c\u83bd\u83bd\u8349\u539f\uff0c\u7ec8\u4e8e\u6765\u5230\u4e86\u4e2d\u5c3c\u8fb9\u5883\u7684\u559c\u9a6c\u62c9\u96c5\u5c71\u4e0b\uff0c\u4f46 \u89c1\u5c97\u5ce6\u8d77\u4f0f\uff0c\u7ef5\u5ef6\u65e0\u9645\uff0c\u6676\u83b9\u7684\u96ea\u5cf0\u5c31\u50cf\u4e00\u6392\u6392\u64ce\u5929\u7389\u67f1\uff0c\u9ad8\u63d2\u4e91\u5bb5\u3002\u5929\u5c71\u65e0\u6b64\u78c5\u78f7\uff0c\u6606\u4ed1 \u65e0\u6b64\u96c4\u5947\uff0c\u5ce8\u5d4b\u65e0\u6b64\u58ee\u4e3d\uff0c\u6842\u534e\u751f\u4e5f\u4e0d\u7981\u4f4e\u5934\u793c\u8d5e\uff0c\u679c\u7136\u4e0d\u6127\u662f\u5929\u4e0b\u81f3\u9ad8\u65e0\u4e0a\u7684\u7b2c\u4e00\u540d\u5c71\uff01 \u3000\u3000\u6842\u534e\u751f\u672c\u6765\u53ef\u4ee5\u4ece\u5c71\u811a\u7ed5\u8fc7\uff0c\u4f46\u9762\u5bf9\u8fd9\u5929\u4e0b\u7b2c\u4e00\u9ad8\u5c71\uff0c\u5ffd\u53d1\u5947\u60f3\uff0c\u867d\u7136\u660e\u77e5\u4e0d\u80fd\u6500\u767b\u5cf0 \u9876\uff0c\u4e5f\u60f3\u4e0a\u5c71\u770b\u770b\uff0c\u4e0a\u5230\u90a3\u513f\uff0c\u5c31\u662f\u90a3\u513f\u3002\u7eb5\u4f7f\u803d\u6401\u51e0\u65e5\u884c\u7a0b\uff0c\u4e5f\u53ef\u4ee5\u4e00\u507f\u5fc3\u613f\u3002 \u3000\u3000\u8fd9\u65f6\u65f6\u5e8f\u6b63\u4ea4\u4ef2\u590f\uff0c\u82e5\u5728\u6c5f\u5357\uff0c\u6b63\u662f\u69b4\u82b1\u7167\u773c\u7684\u65f6\u8282\uff0c\u4f46\u5728\u559c\u9a6c\u62c9\u96c5\u5c71\u4e0a\u8fd8\u98d8\u7740\u96ea\u82b1\u3002 \u4e00\u671b\u65e0\u5c3d\u7684\u5343\u4e07\u5ea7\u5c71\u5cf0\uff0c\u90fd\u662f\u767d\u96ea\u77aa\u77aa\uff0c\u6709\u5982\u7409\u7483\u4e16\u754c\u3002\u6842\u534e\u751f\u8d70\u4e86\u4e09\u56db\u5929\uff0c\u8fd8\u4e0a\u5230\u534a\u5c71\uff0c \u5c71\u4e2d\u6c14\u5019\u6108\u6765\u6108\u51b7\uff0c\u4f46\u9ad8\u5c71\u7684\u5947\u666f\uff0c\u4e5f\u6108\u6765\u6108\u591a\uff0c\u6709\u597d\u4e9b\u52a8\u7269\uff0c\u90fd\u662f\u522b\u5904\u89c1\u4e0d\u5230\u7684\u73cd\u79bd\u5f02 \u517d\u3002\u5c0f\u718a\u732b\u5728\u96ea\u5730\u4e0a\u8df3\u8dc3\uff0c\u50cf\u6dd8\u6c14\u7684\u5a03\u5a03\uff0c\u9ec4\u5634\u7684\u5c71\u9e26\u98de\u5230\u4eba\u7684\u5934\u4e0a\u5431\u5431\u55b3\u55b3\u7684\u53eb\uff0c\u5de8\u5927\u7684 \u7280\u725b\u50cf\u51b0\u5ddd\u4e0a\u7684\u5927\u821f\uff0c\u7075\u5de7\u7684\u5fc3\u9ec4\u7f8a\u8dd1\u5f97\u51fa\u98ce\u8fd8\u5feb\u3002\u6700\u5999\u7684\u662f\u8fd9\u4e9b\u73cd\u79bd\u5f02\u517d\u5927\u7ea6\u662f\u56e0\u4e3a\u4ece\u672a \u89c1\u8fc7\u4eba\u7c7b\uff0c\u89c1\u4e86\u4eba\u4e5f\u4e0d\u77e5\u9053\u8eb2\u907f\u3002 \u3000\u3000\u7b2c\u56db\u65e5\u7684\u9ec4\u660f\uff0c\u6842\u534e\u751f\u8d70\u4e86\u4e00\u5929\uff0c\u6e10\u89c9\u75b2\u5026\uff0c\u60f3\u627e\u4e00\u4e2a\u5ca9\u7a74\u6816\u8eab\uff0c\u4ed6\u6a2a\u8fc7\u4e86\u4e00\u6761\u51b0\u5ddd\uff0c \u7d2b\u8272\u7684\u665a\u971e\u6302\u5728\u51b0\u5ddd\u571f\uff0c\u851a\u6210\u4e03\u5f69\uff0c\u5947\u4e3d\u65e0\u7977\uff0c\u9762\u5bf9\u8fd9\u4e16\u4e0a\u96be\u9022\u7684\u5947\u666f\uff0c\u5026\u610f\u5927\u6d88\uff0c\u5374\u4e0d\u6599 \u4e00\u773c\u77a5\u53bb\uff0c\u8fd8\u89c1\u4e86\u4e00\u6869\u6bd4\u51b0\u5ddd\u7f8e\u666f\u66f4\u4eca\u4eba\u60ca\u5947\u7684\u4e8b\u3002 \u3000\u3000\u51b0\u5ddd\u7684\u4fa7\u9762\uff0c\u6709\u4e00\u80a1\u55b7\u6cc9\uff0c\u707c\u70ed\u7684\u6c34\u82b1\uff0c\u88ab\u98ce\u5439\u6563\uff0c\u6620\u7740\u665a\u971e\uff0c\u5f62\u6210\u4e00\u5708\u5708\u6a59\u8272\u7684\u3001\u6de1 \u7d2b\u8272\u7684\u3001\u548c\u6c49\u7ea2\u8272\u7684\u82b1\u6735\uff0c\u5c31\u50cf\u5728\u4f5b\u79df\u8bde\u8fb0\u4e4b\u591c\uff0c\u6842\u534e\u751f\u5728\u5e03\u8fbe\u62c9\u5bab\u4e2d\u6240\u89c1\u7684\u70df\u82b1\u4e00\u6837\uff0c\u55b7 \u6cc9\u65c1\u8fb9\uff0c\u4e5f\u8bb8\u56e0\u4e3a\u5730\u6c14\u8f83\u6696\u7684\u539f\u6545\uff0c\u5f00\u653e\u7740\u4e00\u4e1b\u4e1b\u4e0d\u77e5\u540d\u7684\u91ce\u82b1\u3002 \u3000\u3000\u897f\u85cf\u5404\u5730\uff0c\u672c\u591a\u6e29\u6cc9\uff0c\u5373\u4f7f\u662f\u96ea\u5c71\u4e4b\u4e0a\u89c1\u5230\u707c\u70ed\u7684\u55b7\u6cc9\uff0c\u4e5f\u4e0d\u8db3\u4e3a\u5947\u3002\u4eca\u5f97\u6842\u534e\u751f\u5947\u602a \u7684\u662f\uff0c\u5728\u55b7\u6cc9\u4e4b\u65c1\uff0c\u82b1\u4e1b\u4e4b\u4e2d\uff0c\u7adf\u7136\u6709\u4e00\u4e2a\u5973\u5b69\u5b50\u5728\u90a3\u91cc\u5b09\u620f\uff01 \u3000\u3000\u8fd9\u5973\u5b69\u5b50\u4e0d\u8fc7\u56db\u4e94\u5c81\u7684\u6837\u5b50\uff0c\u9762\u86cb\u7ea2\u6251\u6251\u7684\u5341\u5206\u53ef\u7231\uff0c\u8fd9\u65f6\u5979\u6b63\u5728\u91c7\u64b7\u91ce\u82b1\uff0c\u7f16\u6210\u82b1 \u73af\uff0c\u662f\u90a3\u6837\u7684\u4e13\u5fc3\u6ce8\u610f\uff0c\u6842\u534e\u751f\u6162\u6162\u5411\u5979\u8d70\u6765\uff0c\u5979\u7adf\u7136\u6ca1\u6709\u53d1\u89c9\u3002 \u3000\u3000\u5728\u559c\u9a6c\u62c9\u96c5\u5c71\u7684\u96ea\u5cf0\u4e4b\u4e0a\uff0c\u6709\u4eba\u7c7b\u5c45\u4f4f\uff0c\u5df2\u7ecf\u662f\u4e00\u5947\u8ff9\uff0c\u4f55\u51b5\u662f\u8fd9\u6837\u5c0f\u7684\u5973\u5b69\u5b50\uff1a\u201c\u5979 \u7684\u7236\u6bcd\u662f\u4ec0\u4e48\u4eba\uff1f\u201d\u201c\u5979\u600e\u4e48\u80fd\u9002\u5e94\u9020\u9ad8\u5c71\u4e0a\u7684\u4e25\u5bd2\uff1f\u201d\u79cd\u79cd\u7591\u95ee\uff0c\u5728\u6842\u534e\u751f\u7684\u5fc3\u5934\u5347\u8d77\uff0c \u4ed6\u6162\u6162\u5411\u5979\u8d70\u53bb\uff0c\u6b63\u60f3\u548c\u5979\u62db\u547c\uff0c\u9661\u7136\u95f4\uff0c\u53c8\u6709\u4e00\u4ef6\u4eca\u4eba\u610f\u6599\u4e0d\u5230\u7684\u4e8b\u60c5\u53d1\u751f\u4e86\u3002 \u3000\u3000\u5c31\u5728\u55b7\u6cc9\u540e\u9762\u7684\u51b0\u5ca9\uff0c\u7a81\u7136\u95f4\u8df3\u51fa\u4e24\u4e2a\u602a\u4eba\uff0c\u6df1\u76ee\u9ad8\u9f3b\uff0c\u9ec4\u53d1\u5bbd\u989d\uff0c\u989d\u4e0a\u7f20\u7740\u539a\u539a\u7684\u4e00 \u5c42\u767d\u5e03\uff0c\u6842\u534e\u751f\u5728\u5e03\u8fbe\u62c9\u5bab\u4e24\u6708\u6709\u591a\uff0c\u5bab\u4e2d\u85cf\u4e66\u751a\u5bcc\uff0c\u6842\u534e\u751f\u56e0\u4e3a\u8981\u5230\u5c3c\u6cca\u5c14\u53bb\uff0c\u66f9\u9605\u8bfb\u4e86 \u4e00\u4e9b\u6709\u5173\u5c3c\u6cca\u5c14\u548c\u963f\u523a\u4f2f\u4ee5\u53ca\u6b27\u6d32\u8bf8\u56fd\u98ce\u571f\u4eba\u7269\u7684\u4e66\u7c4d\uff0c\u5982\u4eca\u770b\u8fd9\u4e24\u4e2a\u602a\u4eba\u7684\u76f8\u8c8c\u548c\u88c5\u675f\uff0c \u4f3c\u4e4e\u662f\u963f\u523a\u4f2f\u4eba\uff0c\u53ea\u89c1\u4ed6\u4eec\u4e00\u8dc3\u800c\u51fa\uff0c\u5f20\u5f00\u4e86\u56db\u53ea\u84b2\u6247\u822c\u7684\u5927\u624b\uff0c\u5c31\u5411\u90a3\u5973\u5b69\u5b50\u6293\u53bb\uff0c\u5c06\u90a3 \u5973\u5b69\u5b50\u5413\u5f97\u54c7\u54c7\u5c16\u53eb\u3002 \u3000\u3000\u6842\u534e\u751f\u5927\u6012\uff0c\u7528\u85cf\u8bdd\u65a5\u9053\uff1a\u201c\u5e72\u5417\u6b3a\u4fae\u5c0f\u963f\u5b50\uff01\u201d\u8fd9\u4e24\u4e2a\u602a\u4eba\u4f3c\u4e4e\u542c\u4e0d\u61c2\u4ed6\u7684\u8bf4\u8bdd\uff0c\u4f46 \u4ed6\u4eec\u5728\u9ad8\u5c71\u4e4b\u4e0a\uff0c\u7a81\u7136\u78b0\u5230\u4e86\u6842\u534e\u751f\uff0c\u60ca\u8be7\u4e4b\u5904\uff0c\u6b8a\u4e0d\u5728\u6842\u534e\u751f\u4e4b\u4e0b\u3002\u90a3\u5973\u5b69\u5b50\u53eb\u9053\uff1a\u201c\u4f60 \u4eec\u6b3a\u4fae\u6211\uff0c\u6211\u53eb\u7239\u7239\u6253\u6b7b\u4f60\uff01\u201d\u8bf4\u7684\u7adf\u7136\u662f\u4e00\u53e3\u6d41\u5229\u7684\u6c49\u8bed\uff0c\u90a3\u4e24\u4e2a\u602a\u4eba\u867d\u7136\u4e0d\u61c2\uff0c\u4e5f\u542c\u5f97 \u51fa\u662f\u5728\u9a82\u4ed6\u3002\u4e00\u4e2a\u602a\u4eba\u4ecd\u7136\u4f38\u624b\u53bb\u6293\u90a3\u5c0f\u5973\u5b69\u5b50\uff0c\u53e6\u4e00\u4e2a\u602a\u4eba\u5219\u5411\u6842\u534e\u751f\u5954\u6765\uff0c\u53d1\u51fa\u67e0\u7b11\uff0c \u6325\u821e\u62f3\u5934\uff0c\u4f3c\u662f\u8981\u5411\u6842\u534e\u751f\u653b\u51fb\u3002 \u3000\u3000\u6842\u534e\u751f\u811a\u5c16\u4e00\u70b9\uff0c\u65bd\u5c55\u7edd\u9876\u8f7b\u529f\uff0c\u75be\u4f3c\u79bb\u5f26\u4e4b\u7bad\uff0c\u4ece\u90a3\u4e2a\u602a\u4eba\u7684\u5934\u9876\u4e0a\u4e00\u64e6\u800c\u8fc7\uff0c\u5148\u53bb \u6551\u90a3\u5c0f\u5973\u5b69\u5b50\uff0c\u60f3\u6349\u90a3\u5c0f\u5973\u5b69\u5b50\u7684\u602a\u4eba\uff0c\u624b\u6307\u521a\u521a\u6cbe\u5230\u5973\u5b69\u5b50\u7684\u80a9\u5934\uff0c\u731b\u542c\u5f97\u5934\u4e0a\u547c\u547c\u98ce \u54cd\uff0c\u8feb\u5f97\u653e\u5f00\u4e86\u90a3\u5c0f\u5973\u5b69\u5b50\uff0c\u5411\u6842\u534e\u751f\u4e00\u62f3\u6363\u51fa\uff0c\u6842\u534e\u751f\u7528\u7684\u662f\u8fbe\u6469\u79d8\u7b08\u4e2d\u7684\u201c\u4e94\u79bd\u638c \u6cd5\u201d\uff0c\u51cc\u7a7a\u6251\u4e0b\uff0c\u6709\u5982\u5927\u9e70\u652b\u5154\uff0c\u70ed\u9053\u51cc\u5389\u975e\u5e38\uff0c\u62f3\u638c\u76f8\u4ea4\uff0c\u5484\u631e\u4e00\u58f0\uff0c\u5c06\u90a3\u4e2a\u602a\u4eba\u91cd\u91cd\u7684 \u6454\u4e86\u4e00\u4e2a\u7b4b\u6597\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u53e6\u4e00\u4e2a\u602a\u4eba\u5df2\u8df3\u4e86\u56de\u6765\uff0c\u6842\u534e\u751f\u4e0d\u5f85\u4ed6\u53d1\u52a8\u653b\u51fb\uff0c\u4fbf\u662f\u4e00\u62db\u201c\u53cc\u9f99\u51fa\u6d77\u201d\uff0c\u53cc\u638c \u75be\u62cd\uff0c\u90a3\u602a\u4eba\u594b\u529b\u4e00\u6863\uff0c\u867d\u7136\u4e0d\u81f4\u8dcc\u7ffb\uff0c\u4e5f\u8e48\u8e48\u8ddf\u8ddf\u7684\u5012\u9000\u4e86\u597d\u51e0\u6b65\uff01 \u3000\u3000\u6842\u534e\u751f\u9a82\u9053\uff1a\u201c\u770b\u4f60\u4eec\u8fd8\u6562\u4e0d\u6562\u6b3a\u4fae\u5c0f\u963f\u5b50\uff01\u201d\u90a3\u4e24\u4e2a\u602a\u4eba\u8fd9\u65f6\u5df2\u4f1a\u5408\u4e00\u9f50\uff0c\u5ffd\u5730\u5e76\u80a9 \u800c\u4e0a\uff0c\u540c\u65f6\u53d1\u51fa\u4e00\u58f0\u602a\u5578\uff0c\u56db\u638c\u9f50\u51fa\uff0c\u5411\u6842\u534e\u751f\u8fce\u9762\u62cd\u6765\u3002\u6842\u534e\u751f\u9002\u624d\u53ea\u4e0d\u8fc7\u4e00\u62db\u5c31\u5c06\u4ed6\u4eec \u6253\u5012\uff0c\u81ea\u662f\u6beb\u65e0\u60e7\u610f\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4eba\u9f50\u4e0a\uff0c\u6700\u591a\u4e5f\u4e0d\u8fc7\u80fd\u6321\u4e94\u62db\uff01\u201d\u90a3\u77e5\u4ed6\u4e00\u62db\u4f7f \u51fa\uff0c\u5ffd\u89c9\u4e24\u80a1\u6f5c\u529b\uff0c\u6709\u5982\u6ce2\u6d9b\u4e00\u822c\uff0c\u6761\u7136\u5377\u81f3\uff0c\u7adf\u7136\u8fde\u81ea\u5df1\u7684\u638c\u529b\u4e5f\u8feb\u4e86\u56de\u6765\uff0c\u6842\u534e\u751f\u88ab\u8fd9 \u4e09\u80a1\u529b\u9053\u4e00\u5806\u3002\u9669\u9669\u8dcc\u5012\uff01 \u3000\u3000\u6842\u534e\u751f\u6025\u5fd9\u65bd\u5c55\u5343\u65a4\u5760\u7684\u529f\u592b\uff0c\u5c06\u8eab\u5f62\u5b9a\u4f4f\uff0c\u5316\u638c\u4e3a\u6307\uff0c\u7528\u4e86\u4e00\u62db\u201c\u5212\u5206\u9634\u9633\u201d\uff0c\u4ee5\u94c1 \u6307\u7384\u529f\uff0c\u5212\u8fd9\u4e24\u4e2a\u602a\u4eba\u7684\u624b\u8155\uff0c\u6842\u534e\u751f\u4ece\u8fbe\u6469\u79d8\u7b08\u4e2d\u6240\u7ec3\u7684\u94c1\u6307\u529f\u592b\uff0c\u4e0e\u6b66\u5f53\u6d3e\u7684\u4e00\u6307\u60ee\u529f \u6709\u5f02\u66f2\u540c\u5de5\u4e4b\u5999\uff0c\u7ed9\u4ed6\u5212\u7740\uff0c\u8d5b\u5982\u5200\u5272\uff0c\u7b4b\u8109\u5fc5\u65ad\u3002\u6842\u534e\u751f\u89c1\u4ed6\u4eec\u72c2\u6251\u800c\u6765\uff0c\u4ee5\u4e3a\u8fd9\u4e00\u62db\u5fc5 \u80fd\u7528\u4e0a\uff0c\u90a3\u77e5\u8fd9\u4e24\u4e2a\u602a\u4eba\u7adf\u4f3c\u8bc6\u5f97\u5389\u5bb3\uff0c\u4e00\u89c1\u6842\u534e\u751f\u51dd\u8eab\u4e0d\u52a8\uff0c\u4ed6\u4eec\u7adf\u4e5f\u500f\u7136\u6b62\u6b65\uff0c\u53cc\u638c\u5212 \u4e86\u4e00\u9053\u5706\u5f27\uff0c\u5411\u6842\u534e\u751f\u7f13\u7f13\u63a8\u8fdb\u3002\u8bf4\u4e5f\u5947\u602a\uff0c\u6842\u534e\u751f\u53d8\u5b81\u4e3a\u6307\uff0c\u529b\u4e00\u5f31\uff0c\u5bf9\u65b9\u538b\u8fc7\u6765\u7684\u6f5c\u529b \u4e5f\u76f8\u5e94\u51cf\u5f31\u4e86\u3002 \u3000\u3000\u6842\u534e\u751f\u5fc3\u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\u529f\u592b\uff1f\u5012\u6709\u70b9\u90aa\u95e8\uff01\u6211\u4e14\u518d\u8bd5\u5b83\u4e00\u8bd5\u3002\u201d\u53cc\u638c\u4e00\u9519\uff0c\u4e00\u9053\u201c\u4e94 \u4e01\u5f00\u5c71\u201d\uff0c\u7a81\u7136\u4ee5\u6781\u51f6\u731b\u7684\u5927\u529b\u91d1\u521a\u638c\u6cd5\u53d1\u51fa\uff0c\u5ffd\u89c9\u654c\u4eba\u7684\u4e24\u80a1\u638c\u529b\u5de6\u53f3\u7275\u5f15\uff0c\u4e0d\u4f46\u5c06\u81ea\u5df1 \u7684\u638c\u529b\u5316\u89e3\u4e8e\u65e0\u5f62\uff0c\u800c\u4e14\u987f\u7136\u4e4b\u95f4\uff0c\u597d\u50cf\u8eab\u7f6e\u65cb\u6da1\u4e4b\u4e2d\uff0c\u4e0d\u7531\u81ea\u5df1\u7684\u8ddf\u7740\u654c\u4eba\u7275\u5f15\u4e4b\u529b\uff0c\u6ef4 \u6e9c\u6e9c\u7684\u7816\u4e86\u51e0\u4e2a\u5708\u5b50\u3002\u539f\u6765\u8fd9\u4e24\u4e2a\u602a\u4eba\u4e43\u662f\u963f\u523a\u4f2f\u7b2c\u4e00\u9ad8\u624b\u63d0\u6469\u8fbe\u591a\u7684\u5f1f\u5b50\uff0c\u63d0\u6469\u8fbe\u591a\u7cbe\u7814 \u529b\u5b66\uff0c\u6df1\u77e5\u7528\u4efb\u4f55\u529b\u91cf\u6253\u51fb\u5bf9\u65b9\uff0c\u6709\u6b63\u4f5c\u7528\u5fc5\u6709\u53cd\u4f5c\u7528\uff0c\u5927\u5c0f\u76f8\u7b49\uff0c\u65b9\u5411\u76f8\u53cd\uff0c\u56e0\u6b64\u4ed6\u7ec3\u7684 \u638c\u65b9\u4fbf\u540d\u4e3a\u9634\u9633\u638c\u529b\uff0c\u4ee5\u4e24\u80a1\u638c\u529b\u4e92\u76f8\u6fc0\u649e\uff0c\u518d\u4e0e\u654c\u4eba\u6240\u53d1\u7684\u529b\u91cf\u6c47\u5408\uff0c\u5c06\u654c\u4eba\u7684\u653b\u51fb\u529b\u91cf \u4e5f\u5316\u4e3a\u53cd\u51fb\u7684\u529b\u91cf\uff0c\u548c\u51e0\u80a1\u6d6a\u6f6e\u76f8\u78b0\u4e4b\u65f6\uff0c\u5377\u8d77\u6f29\u6da1\u4e4b\u7406\uff0c\u6b63\u8d1f\u76f8\u540c\u3002\u63d0\u6469\u8fbe\u591a\u529f\u529b\u6df1\u6e5b\uff0c \u4e00\u4eba\u4fbf\u53ef\u53d1\u51fa\u5546\u80a1\u9634\u9633\u638c\u529b\uff0c\u8fd9\u4e24\u4e2a\u5f1f\u5b50\u5374\u5fc5\u987b\u4e24\u4eba\u5408\u4f7f\u624d\u884c\u3002 \u3000\u3000\u6842\u534e\u751f\u8bd5\u4e86\u51e0\u62db\uff0c\u6e10\u5982\u5176\u7406\uff0c\u4ed6\u806a\u660e\u7edd\u9876\uff0c\u4e00\u9762\u5728\u5fc3\u4e2d\u7422\u78e8\u7834\u89e3\u8fd9\u79cd\u638c\u529b\u7684\u65b9\u6cd5\uff0c\u4e00\u9762 \u6539\u7528\u592a\u6781\u63a8\u624b\uff0c\u968f\u7740\u654c\u4eba\u7684\u638c\u529b\u6216\u7275\u6216\u5f15\uff0c\u89c1\u62db\u62c6\u62db\uff0c\u89c1\u5f0f\u7834\u5f0f\uff0c\u8fd8\u4e0d\u65f6\u65bd\u5c55\u70b9\u7a74\u6cd5\uff0c\u5c06\u8fd9 \u4e24\u4e2a\u602a\u4eba\u8feb\u5728\u79bb\u8eab\u516b\u5c3a\u4e4b\u5916\u3002 \u3000\u3000\u8fd9\u6837\u4e00\u6765\uff0c\u53cc\u65b9\u6597\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u5140\u662f\u76f8\u6301\u4e0d\u4e0b\uff0c\u90a3\u5c0f\u5973\u5b69\u53eb\u9053\uff1a\u201c\u53d4\u53d4\u522b\u614c\uff0c\u6211\u53eb\u7239\u7239 \u5e2e\u4f60\u3002\u201d\u6842\u534e\u751f\u6b63\u60f3\u89c1\u5b83\u7684\u7236\u6bcd\uff0c\u7b11\u9053\uff1a\u201c\u597d\u5440\uff0c\u6211\u5c31\u8981\u6253\u4e0d\u8fc7\u8fd9\u4e24\u4e2a\u6076\u4eba\u4e86\uff0c\u4f60\u8d76\u5feb\u53eb\u7239 \u7239\u6765\u5427\u3002\u201d\u90a3\u5973\u5b69\u4e8e\u6458\u4e86\u4e00\u7247\u6811\u53f6\uff0c\u653e\u5728\u5507\u8fb9\u4e00\u5439\uff0c\u58f0\u97f3\u975e\u5e38\u54cd\u4eae\uff0c\u8fc7\u4e86\u4e00\u76cf\u8336\u7684\u65f6\u523b\uff0c\u53ea \u89c1\u4e00\u4e2a\u7626\u957f\u7684\u7537\u5b50\uff0c\u7ea6\u6478\u4e94\u5341\u591a\u5c81\u5e74\u7eaa\uff0c\u6c14\u547c\u547c\u7684\u8dd1\u6765\uff0c\u6028\u58f0\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u4e2a\u81ed\u5bb6\u4f19\u8981\u4e0d\u8981 \u8138\uff0c\u6b3a\u4fae\u5c0f\u963f\u5b50\u4e48\uff1f\u201d\u9661\u7136\u89c1\u7740\u6842\u534e\u751f\u4e0e\u4ed6\u4eec\u6076\u6597\uff0c\u4e24\u4e2a\u4eba\u90fd\u5403\u4e86\u4e00\u60ca\uff0c\u6842\u534e\u751f\u53eb\u9053\uff1a\u201c\u65b9 \u8001\u524d\u8f88\uff0c\u662f\u4f60\uff1f\u201d\u90a3\u7626\u957f\u6c49\u5b50\u4e5f\u53eb\u9053\uff1a\u201c\u534e\u751f\u8001\u5f1f\uff0c\u4f60\u600e\u4e48\u4e0a\u5230\u8fd9\u513f\u6765\u4e86\uff1f\u201d \u3000\u3000\u539f\u6765\u8fd9\u4e2a\u7626\u957f\u6c49\u5b50\u540d\u53eb\u65b9\u4eca\u660e\uff0c\u5728\u5eb7\u7199\u5e74\u95f4\uff0c\u66fe\u662f\u540d\u95fb\u5929\u4e0b\u7684\u52c7\u58eb\uff0c\u4eba\u79f0\u201c\u795e\u62f3\u65e0 \u654c\u201d\uff0c\u5f53\u65f6\u5eb7\u7199\u7684\u5341\u51e0\u4e2a\u7687\u5b50\u4e89\u4f4d\uff0c\u4ed6\u88ab\u5341\u56db\u7687\u5b50\u5141\u889c\u6536\u7f57\u5e10\u4e0b\uff0c\u540e\u6765\u7687\u4f4d\u88ab\u56db\u7687\u5b50\u5141\u6862 \uff08\u5373\u96cd\u6b63\uff09\u6240\u593a\uff0c\u5141\u889c\u4e5f\u88ab\u96cd\u6b63\u6240\u5bb3\uff0c\u65b9\u4eca\u660e\u542c\u5927\u4fa0\u5510\u6653\u6f9c\u6240\u529d\uff0c\u4ece\u6b64\u4e0d\u518d\u6548\u5fe0\u6e05\u5ef7\u3002\u6842\u534e \u751f\u8fd8\u662f\u5341\u591a\u5c81\u7684\u65f6\u5019\uff0c\u66fe\u4e0e\u4ed6\u89c1\u8fc7\u4e00\u9762\uff0c\u522b\u6765\u51e0\u5341\u5e74\uff0c\u4e24\u4eba\u8fd8\u4f9d\u7a00\u8ba4\u5f97\u3002 \u3000\u3000\u65b9\u4eca\u660e\u98de\u5954\u800c\u6765\uff0c\u76f8\u9694\u6570\u4e08\u4e4b\u5916\uff0c\u5c31\u662f\u4e00\u62f3\u6363\u51fa\uff0c\u90a3\u4e24\u4e2a\u602a\u4eba\u6643\u4e86\u51e0\u6643\uff0c\u8fd8\u672a\u8dcc\u5012\uff0c\u5495 \u5495\u565c\u565c\u7684\u9a82\u4e86\u51e0\u53e5\uff0c\u6842\u534e\u751f\u542c\u51fa\u5176\u4e2d\u56db\u4e2a\u5b57\u597d\u50cf\u662f\u8bf4\u201c\u63d0\u6469\u8fbe\u591a\u201d\u3002\u6842\u534e\u751f\u66fe\u542c\u767d\u8863\u5c11\u5973\u8c08 \u8bba\u8fc7\u5929\u4e0b\u9ad8\u624b\uff0c\u5bf9\u8fd9\u4e2a\u540d\u5b57\u5370\u8c61\u6df1\u523b\uff0c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u53eb\u9053\uff1a\u201c\u65b9\u8001\u524d\u8f88\u4e0d\u5fc5\u52a8\u624b\uff0c\u4f60\u4e14\u770b\u6211\u6536 \u62fe\u4ed6\u4eec\u3002\u201d \u3000\u3000\u90a3\u4e24\u4e2a\u602a\u4eba\u8eab\u5f62\u4e00\u6014\uff0c\u5e76\u80a9\u4e00\u7acb\uff0c\u9634\u9633\u638c\u529b\u53c8\u5f90\u5f90\u53d1\u51fa\uff0c\u6842\u534e\u751f\u731b\u5730\u5927\u559d\u4e00\u58f0\uff0c\u4e00\u62db\u6781 \u521a\u731b\u7684\u5927\u529b\u91d1\u521a\u638c\u8fce\u9762\u62cd\u53bb\uff0c\u90a3\u4e24\u4e2a\u602a\u4eba\u6b63\u4e2d\u4e0b\u6000\uff0c\u638c\u529b\u4e00\u53f0\uff0c\u521a\u6b32\u53cd\u51fb\uff0c\u90a3\u6599\u6842\u534e\u751f\u638c\u52bf \u500f\u6536\uff0c\u90a3\u80a1\u91d1\u521a\u638c\u529b\u5728\u77ac\u606f\u4e4b\u95f4\u6d88\u5931\u5f97\u65e0\u5f71\u65e0\u8e2a\uff0c\u4e24\u4e2a\u602a\u4eba\u6251\u4e86\u4e2a\u7a7a\uff0c\u8eab\u5b50\u524d\u503e\uff0c\u6842\u534e\u751f\u4e00 \u58f0\u957f\u5578\uff0c\u53cc\u811a\u9f50\u98de\uff0c\u5c06\u4e24\u4e2a\u602a\u4eba\u90fd\u8e22\u7ffb\u4e86\uff0c\u5176\u4e2d\u4e00\u4e2a\u602a\u4eba\u6b66\u529f\u8f83\u9ad8\uff0c\u4e00\u4e2a\u9ca4\u9c7c\u6253\u633a\u7ffb\u4e86\u8d77 \u6765\uff0c\u8fd8\u60f3\u6323\u624e\uff0c\u6842\u534e\u751f\u8eab\u624b\u4f55\u7b49\u654f\u6377\uff0c\u733f\u81c2\u4e00\u4ef6\uff0c\u6293\u7740\u4e86\u4ed6\u80a9\u5934\u7684\u7435\u7436\u80be\uff0c\u5f80\u5916\u4e00\u7529\uff0c\u8fd9\u6b63 \u662f\u6b66\u6797\u7edd\u5b66\u7684\u5927\u6454\u788e\u624b\u529f\u592b\uff0c\u8fd9\u4e2a\u602a\u4eba\u90a3\u91cc\u7981\u7231\u5f97\u4f4f\uff0c\u4f46\u542c\u5f97\u4e00\u58f0\u88c2\u5e1b\uff0c\u4ed6\u7684\u5916\u8863\u88ab\u6842\u534e\u751f \u6495\u4e86\u4e0b\u6765\uff0c\u7435\u7436\u9aa8\u88ab\u634f\u788e\u4e24\u6839\uff0c\u4fbf\u751f\u751f\u7684\u88ab\u6842\u534e\u751f\u629b\u4e0b\u5c71\u5761\u3002\u539f\u6765\u6842\u534e\u751f\u4e0e\u4ed6\u4eec\u6597\u4e86\u534a\u4e2a\u65f6 \u8fb0\uff0c\u609f\u51fa\u4e86\u5176\u4e2d\u7684\u5999\u7406\uff0c\u77e5\u9053\u5df1\u65b9\u7684\u529b\u9053\u6108\u731b\uff0c\u5bf9\u65b9\u7684\u53cd\u51fb\u529b\u91cf\u4e5f\u6108\u5f3a\uff0c\u56e0\u6b64\u4ed6\u6545\u610f\u5148\u53d1\u6781 \u521a\u731b\u7684\u638c\u529b\uff0c\u8bf1\u4ed6\u4eec\u53cd\u51fb\uff0c\u5374\u7a81\u7136\u5c06\u638c\u529b\u64a4\u56de\uff0c\u8fd9\u6837\u4fbf\u8f7b\u8f7b\u6613\u6613\u7684\u5c06\u4ed6\u4eec\u7684\u9634\u9633\u638c\u529b\u7834\u4e86\u3002 \u8bdd\u867d\u5982\u6b64\uff0c\u4f46\u82e5\u6842\u534e\u751f\u6ca1\u6709\u6536\u53d1\u81ea\u5982\u7684\u6781\u4e0a\u4e58\u6b66\u529f\uff0c\u867d\u609f\u5176\u7406\uff0c\u4e5f\u4e0d\u80fd\u8fd0\u7528\u3002 \u3000\u3000\u90a3\u5973\u5b69\u5b50\u62cd\u638c\u7b11\u9053\uff1a\u201c\u6253\u5f97\u597d\uff0c\u6253\u5f97\u5999\uff0c\u6253\u5f97\u4e24\u4e2a\u602a\u4eba\u5471\u5471\u53eb\u3002\u201d\u65b9\u4eca\u660e\u4e5f\u8d5e\u9053\uff1a\u201c\u6842 \u8001\u5f1f\u8fd9\u624b\u529f\u592b\uff0c\u521a\u67d4\u517c\u5e76\uff0c\u6211\u7a7a\u8d1f\u795e\u62f3\u4e4b\u540d\uff0c\u5bf9\u6b64\u4e5f\u4e0d\u80fd\u4e0d\u5fc3\u670d\u53e3\u670d\u3002\u4e0d\u7792\u4f60\u8bf4\uff0c\u6211\u5df2\u7ecf\u4e0e \u4ed6\u4eec\u6253\u4e86\u51e0\u6b21\u67b6\u4e86\uff0c\u4ed6\u4eec\u6ca1\u6253\u8d62\u6211\uff0c\u6211\u4e5f\u6ca1\u6709\u6253\u8d25\u4e86\u4ed6\u4eec\u3002\u201d \u3000\u3000\u6842\u534e\u751f\u8c26\u865a\u4e86\u51e0\u53e5\uff0c\u95ee\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u662f\u4ec0\u4e48\u4eba\uff1f\u201d\u65b9\u4eca\u660e\u9053\uff1a\u201c\u6211\u9690\u5c45\u5728\u6b64\uff0c\u5230\u4eca\u5e74\u5df2 \u662f\u7b2c\u5341\u4e2a\u5e74\u5934\uff0c\u5341\u5e74\u6765\u4ece\u6ca1\u6709\u4eba\u4e0a\u8fc7\u8fd9\u513f\uff0c\u5927\u7ea6\u5728\u4e94\u5927\u5929\u4e4b\u524d\uff0c\u5374\u4e0d\u77e5\u4ece\u90a3\u91cc\u6765\u7684\u8fd9\u4e24\u4e2a\u602a \u4eba\uff0c\u95ef\u5230\u6211\u7684\u5bb6\u6765\u7d22\u996e\u7d20\u98df\uff0c\u8fd9\u4e5f\u8fd8\u7f62\u4e86\uff0c\u4ed6\u4eec\u8fd8\u8981\u8d76\u6211\u51fa\u53bb\uff0c\u9738\u5360\u6211\u7684\u8717\u5c45\uff0c\u6211\u8fd8\u672a\u89c1\u8fc7 \u8fd9\u6837\u4e0d\u8bb2\u7406\u7684\u4eba\uff0c\u662f\u4ee5\u8fd9\u4e94\u5927\u5929\u6765\uff0c\u51e0\u4e4e\u6bcf\u5929\u90fd\u548c\u4ed6\u4eec\u6253\u4e0a\u4e00\u573a\u3002\u8fd9\u5c0f\u4e2b\u5934\u4e5f\u771f\u80c6\u5927\uff0c\u6211\u672c \u6765\u662f\u5c06\u5979\u5173\u5728\u5bb6\u4e2d\uff0c\u4e0d\u8bb8\u5979\u51fa\u53bb\u7684\uff0c\u90a3\u77e5\u5979\u8d81\u6211\u5728\u82b1\u5703\u6d47\u82b1\u7684\u65f6\u5019\uff0c\u5374\u5077\u5077\u7684\u6e9c\u4e86\u51fa\u53bb\u3002\u201d \u90a3\u5973\u5b69\u5b50\u63ea\u7740\u5c0f\u5634\u513f\u9053\uff1a\u201c\u51e0\u5929\u4e0d\u51c6\u51fa\u53bb\uff0c\u95f7\u6b7b\u4eba\u5566\uff0c\u6211\u8fde\u7280\u725b\u4e5f\u4e0d\u6015\uff0c\u90a3\u6015\u8fd9\u4e24\u4e2a\u602a \u4eba\u3002\u201d\u6842\u534e\u751f\u8be7\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u602a\u4eba\u662f\u505a\u4ec0\u4e48\u6765\u7684\u3002\u53ef\u60dc\u6211\u4e0d\u61c2\u4ed6\u4eec\u7684\u8bf4\u8bdd\uff0c\u6ca1\u529e\u6cd5\u62f7\u95ee\u4ed6 \u4eec\u3002\u201d\u5c06\u6495\u4e0b\u7684\u90a3\u4ef6\u5916\u8863\u4e00\u641c\uff0c\u53ea\u89c1\u5185\u4e2d\u6709\u51e0\u4ef6\u7269\u4ef6\uff0c\u90fd\u662f\u6842\u534e\u751f\u6ca1\u89c1\u8fc7\u7684\uff0c\u6709\u4e24\u4ef6\u4e09\u89d2\u5f62 \u7684\u677f\u5b50\uff0c\u4e00\u652f\u4e94\u5bf8\u6765\u957f\uff0c\u6709\u4e24\u53ea\u811a\u7684\u94c1\u67b6\uff0c\u8fd8\u6709\u51e0\u5f20\u56fe\u753b\u7eb8\uff0c\u6709\u8bb8\u591a\u4e09\u89d2\u5f62\u548c\u5706\u5f62\u3002\u539f\u6765\u8fd9 \u4e24\u4e2a\u602a\u4eba\u4e43\u662f\u63d0\u6469\u8fbe\u591a\u7684\u5f1f\u5b50\uff0c\u63d0\u6469\u8fbe\u591a\u7acb\u4e0b\u5fd7\u613f\uff0c\u8981\u60f3\u6500\u767b\u4e0b\u5929\u4e0b\u7b2c\u4e00\u9ad8\u5cf0\uff0c\u5148\u6d3e\u9063\u4e24\u4e2a \u5f1f\u5b50\u524d\u6765\u6d4b\u91cf\u5730\u5f62\uff0c\u90a3\u4e9b\u4e09\u89d2\u677f\u5706\u89c4\u7b49\u7269\u90fd\u662f\u6d4b\u91cf\u7684\u7528\u5177\u3002\u5374\u6599\u4e0d\u5230\u78b0\u5230\u4e86\u6842\u534e\u751f\uff0c\u88ab\u4f73\u534e \u751f\u786c\u6454\u4e0b\u5c71\uff0c\u90a3\u7435\u7436\u9aa8\u88ab\u634f\u788e\u7684\u602a\u4eba\uff0c\u6eda\u5230\u534a\u5c71\u4fbf\u6b7b\u4e86\uff0c\u53e6\u4e00\u4e2a\u602a\u4eba\u4e5f\u56e0\u4f24\u6b8b\u5e9f\uff0c\u8fc7\u4e86\u5341\u591a \u5e74\u4e5e\u8ba8\u7684\u751f\u6daf\u624d\u56de\u5230\u6b27\u6d32\uff0c\u76f4\u5230\u4e8c\u5341\u5e74\u4e4b\u540e\uff0c\u63d0\u6469\u8fbe\u591a\u624d\u518d\u7387\u9886\u5f1f\u5b50\u4eb2\u81ea\u524d\u6765\u63a2\u9669\uff0c\u8fd9\u662f\u540e \u8bdd\uff0c\u6309\u4e0b\u4e0d\u8868\u3002 \u3000\u3000\u65b9\u4eca\u660e\u5728\u9690\u5c45\u4e4b\u524d\u548c\u6842\u534e\u751f\u867d\u7136\u4ec5\u6709\u4e00\u9762\u4e4b\u7f18\uff0c\u4f46\u5728\u559c\u9a6c\u62c9\u96c5\u5c71\u4e0a\u76f8\u9022\uff0c\u81ea\u662f\u500d\u52a0\u4eb2 \u70ed\uff0c\u5f53\u4e0b\u4fbf\u62db\u547c\u6842\u534e\u751f\u5230\u4ed6\u5bb6\u4e2d\u4f4f\u5bbf\u3002 \u3000\u3000\u4ece\u55b7\u6cc9\u897f\u9762\u8d70\u4e0a\u5c71\u5761\uff0c\u4e0d\u8fc7\u6570\u91cc\u4e4b\u9065\uff0c\u4f7f\u5230\u4e86\u65b9\u4eca\u660e\u7684\u5bb6\uff0c\u4e09\u95f4\u5c4b\u5b50\uff0c\u56f4\u4ee5\u732a\u77f3\u56f4\u5899\uff0c \u867d\u7136\u7b80\u964b\uff0c\u5374\u662f\u6c5f\u5357\u98ce\u683c\uff0c\u6842\u534e\u751f\u5230\u897f\u85cf\u4ee5\u6765\uff0c\u7b2c\u4e00\u6b21\u89c1\u5230\u6c5f\u5357\u4e61\u6751\u7684\u623f\u5c4b\u5f62\u5f0f\uff0c\u751a\u4e3a\u559c \u60a6\u3002\u60f3\u5230\u65b9\u4eca\u660e\u5728\u96ea\u5c71\u4e4b\u4e0a\uff0c\u51ed\u7740\u4e00\u53cc\u7a7a\u624b\uff0c\u5efa\u7acb\u5bb6\u56ed\uff0c\u771f\u4e0d\u77e5\u8d39\u4e86\u591a\u5927\u5fc3\u8840\uff1f\u5ffd\u53c8\u60f3\u8d77\u767d \u8863\u5c11\u5973\u5728\u7389\u5973\u5cf0\u524d\u66fe\u548c\u4ed6\u7b11\u8bed\uff0c\u8bf4\u662f\u5c06\u6765\u8981\u5728\u5929\u6e56\u4e4b\u4e0a\uff0c\u96ea\u5cf0\u4e4b\u5cad\uff0c\u5efa\u7acb\u51b0\u5bab\uff0c\u4f46\u53ea\u6050\u662f\u6d77 \u5e02\u5e7f\u697c\uff0c\u955c\u82b1\u6c34\u6708\u3002\u6842\u534e\u751f\u5fc3\u4e2d\u6697\u60f3\uff0c\u82e5\u5f97\u4e0e\u767d\u8863\u5c11\u5973\u9690\u5c45\uff0c\u8896\u613f\u5f97\u6709\u65b9\u4eca\u660e\u8fd9\u6837\u7684\u7b80\u964b\u5c4b \u5b50\uff0c\u4ea6\u5df2\u5fc3\u6ee1\u610f\u8db3\u3002\u60f3\u8d77\u4e0d\u77e5\u4f55\u5e74\u4f55\u6708\uff0c\u65b9\u5f97\u624d\u6664\u4ed9\u59ec\uff0c\u4e0d\u7981\u5076\u7136\u3002 \u3000\u3000\u5c4b\u65c1\u4e00\u5ea7\u5c0f\u5c0f\u7684\u82b1\u56e2\uff0c\u65b9\u4eca\u660e\u5e2e\u4ed6\u5230\u56ed\u4e2d\u4e00\u770b\uff0c\u5404\u8272\u4e0d\u77e5\u540d\u5b57\u7684\u82b1\u6735\uff0c\u5f00\u5f97\u6b63\u76db\uff0c\u65b9\u4eca \u660e\u7b11\u9053\uff1a\u201c\u53ef\u60dc\u8fd9\u91cc\u627e\u4e0d\u5230\u4e2d\u539f\u7684\u82b1\u53f0\uff0c\u8fd9\u91cc\u9760\u8fd1\u6e29\u6cc9\uff0c\u5730\u6c14\u6e29\u6696\uff0c\u4ec5\u7ba1\u9ad8\u5c71\u4e25\u5bd2\uff0c\u8fd9\u5468\u56f4 \u51e0\u91cc\u4e4b\u5730\uff0c\u5374\u662f\u548c\u6696\u5982\u6625\u3002\u201d \u3000\u3000\u4f73\u534e\u751f\u95ee\u9053\uff1a\u201c\u65b9\u8001\u524d\u8f88\u600e\u4e48\u4f1a\u6765\u5230\u6b64\u5730\u9690\u5c45\uff1f\u201d\u65b9\u4eca\u660e\u7b11\u9053\uff1a\u201c\u6211\u662f\u4eba\u5c71\u552f\u6050\u4e0d\u6df1\uff0c \u6211\u5f53\u5e74\u8f85\u4f50\u5341\u56db\u7687\u5b50\u4e0e\u96cd\u6b63\u4e89\u4f4d\uff0c\u6211\u56fa\u7136\u6068\u6781\u96cd\u6b63\uff0c\u96cd\u6b63\u4e5f\u6068\u6781\u4e86\u6211\u3002\u96cd\u6b63\u624b\u4e0a\uff0c\u6b66\u58eb\u5982 \u4e91\uff0c\u53c8\u6709\u5e74\u7fb9\u5c27\u8fd9\u6837\u4e00\u4f4d\u5fc3\u72e0\u624b\u8fa3\u7684\u5927\u5c06\u519b\u8f85\u52a9\uff0c\u6211\u600e\u80fd\u5728\u4e2d\u539f\u7acb\u8db3\uff0c\u6ca1\u5948\u4f55\u53ea\u597d\u9003\u5230\u8fd9\u91cc \u4f5c\u4e2a\u5316\u5916\u4e4b\u6c11\u3002\u201d\u6842\u534e\u751f\u9053\uff1a\u201c\u5e74\u7fb9\u5c27\u548c\u96cd\u6b63\u65f1\u5df2\u6b7b\u4e86\u3002\u73b0\u5728\u7684\u7687\u5e1d\u662f\u96cd\u6b63\u7684\u513f\u5b50\uff0c\u5e74\u53f7\u4e5f \u5df2\u6539\u79f0\u4e7e\u9686\u4e86\u3002\u201d\u65b9\u4eca\u660e\u53c8\u60ca\u53c8\u559c\uff0c\u6025\u5fd9\u95ee\u9053\uff1a\u201c\u5e74\u7fb9\u5c27\u548c\u96cd\u6b63\u662f\u600e\u4e48\u6b7b\u7684\uff1f\u201d\u6842\u534e\u751f\u9053\uff1a \u201c\u5e74\u7fb9\u5c27\u529f\u9ad8\u9707\u4e3b\uff0c\u88ab\u96cd\u6b63\u8fde\u8d2c\u5341\u516b\u7ea7\uff0c\u8d2c\u5230\u676d\u5dde\u53bb\u5b88\u57ce\u95e8\uff0c\u4fae\u8fb1\u591f\u4e86\uff0c\u540e\u6765\u4ecd\u7136\u4e0d\u514d\u4e00 \u6b7b\u3002\u96cd\u6b63\u5219\u662f\u5415\u56db\u5a18\u5165\u5bab\u5c06\u4ed6\u523a\u6740\u7684\uff01\u201d\u65b9\u4eca\u660e\u4ef0\u5929\u5927\u7b11\uff0c\u8fde\u547c\u75db\u5feb\uff0c\u7ee7\u800c\u53c8\u9053\uff1a\u201c\u6211\u8fd9\u4e24 \u4e2a\u5927\u4ec7\u4eba\u867d\u7136\u90fd\u5df2\u53bb\u6389\uff0c\u4f46\u6211\u5728\u8fd9\u91cc\u4f4f\u5f97\u60ef\u4e86\uff0c\u4e5f\u4e0d\u60f3\u518d\u4e0b\u5c71\u4e86\u3002\u201d \u3000\u3000\u5728\u82b1\u5703\u6d41\u8fde\u4e00\u4f1a\uff0c\u65b9\u4eca\u660e\u62db\u547c\u6842\u534e\u751f\u56de\u5bb6\uff0c\u65b9\u4eca\u660e\u662f\u5230\u4e86\u897f\u85cf\u4e4b\u540e\u624d\u6210\u4eb2\u7684\uff0c\u59bb\u5b50\u662f\u85cf \u65cf\u4eba\uff0c\u66fe\u5b66\u8fc7\u7ea2\u6559\u7684\u6b66\u529f\uff0c\u4f53\u9b44\u975e\u5e38\u5f20\u5efa\uff0c\u7b2c\u4e00\u6b21\u62db\u5f85\u4e08\u592b\u7684\u670b\u53cb\uff0c\u5341\u5206\u9ad8\u5174\uff0c\u7092\u4e0b\u4e00\u5927\u76d8 \u864e\u8089\uff0c\u53e6\u5916\u8fd8\u6709\u4e00\u6876\u7280\u725b\u5976\uff0c\u65b9\u4eca\u660e\u7b11\u9053\uff1a\u201c\u5c71\u4e2d\u65e0\u8336\u65e0\u6d4e\uff0c\u597d\u8bf7\u4f60\u559d\u7280\u725b\u5976\u4e86\u3002\u201d\u6842\u534e\u751f \u8fd9\u51e0\u5929\u6765\u90fd\u56bc\u5e72\u7cae\uff0c\u6709\u9c9c\u8089\u53ef\u98df\uff0c\u725b\u5976\u53ef\u559d\uff0c\u4e14\u5728\u9ad8\u5c71\u4e4b\u4e0a\u5f97\u9047\u6545\u4eba\uff0c\u4f46\u89c9\u6bd4\u5403\u6ee1\u6c49\u7b75\u5e2d\uff0c \u66f4\u4e3a\u53ef\u53e3\u3002 \u3000\u3000\u4e24\u4eba\u5f7b\u591c\u957f\u8c08\uff0c\u65b9\u4eca\u660e\u95ee\u8fd9\u5341\u5e74\u6765\u7684\u6b66\u6797\u6545\u4e8b\uff0c\u542c\u5230\u53ef\u6b4c\u53ef\u6ce3\u4e4b\u5904\uff0c\u51fb\u638c\u9ad8\u547c\uff0c\u8db3\u89c1\u70c8 \u58eb\u66ae\u5e74\u58ee\u5fc3\u672a\u5df2\uff0c\u6842\u534e\u751f\u53c8\u544a\u8bc9\u4ed6\u5728\u897f\u85cf\u9047\u5230\u5e74\u7fb9\u5c27\u513f\u5b50\u7684\u4e8b\uff0c\u65b9\u4eca\u660e\u542c\u8bf4\u5e74\u7fb9\u5c27\u7684\u513f\u5b50\u806a \u660e\u624d\u667a\u4e0d\u51cf\u7236\u4eb2\uff0c\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u4f46\u613f\u4ed6\u4e0d\u8981\u50cf\u5b83\u7684\u7236\u4eb2\u624d\u597d\u3002\u201d \u3000\u3000\u6842\u534e\u751f\u95ee\u65b9\u4eca\u660e\u9053\uff1a\u201c\u73e0\u7a46\u6717\u739b\u5cf0\u662f\u5929\u4e0b\u7b2c\u4e00\u9ad8\u5cf0\uff0c\u65b9\u8001\u524d\u8f88\u5728\u8fd9\u91cc\u4f4f\u4e86\u5c06\u8fd1\u5341\u5e74\uff0c\u53ef \u66fe\u4e0a\u5468\u73e0\u5cf0\u6ca1\u6709\uff1f\u201d\u65b9\u4eca\u660e\u7b11\u9053\uff1a\u201c\u82e5\u80fd\u8fd9\u6837\u5bb9\u6613\u4e0a\u53bb\uff0c\u600e\u79f0\u5f97\u4e0a\u662f\u5929\u4e0b\u7b2c\u4e00\u9ad8\u5cf0\uff1f\u4ece\u8fd9\u91cc \u4e0a\u53bb\uff0c\u4e0d\u4f46\u51b0\u5761\u8d8a\u6765\u8d8a\u9661\uff0c\u800c\u4e14\u547c\u5438\u4e5f\u8d8a\u6765\u8d8a\u96be\uff0c\u591a\u597d\u5185\u529f\uff0c\u4e5f\u4e0d\u80fd\u7981\u53d7\u3002\u542c\u8bf4\u4ee5\u524d\u5929\u5c71\u6d3e \u7684\u521b\u6d3e\u5f1f\u5b50\uff0c\u51cc\u672b\u98ce\u5927\u4fa0\u66f9\u5c1d\u8bd5\u8fc7\u6500\u767b\u73e0\u5cf0\uff0c\u4e5f\u5e9f\u7136\u800c\u8fd4\u5462\u3002\u201d \u3000\u3000\u7b2c\u4e8c\u65e5\u6842\u534e\u751f\u4e0d\u60f3\u4e0b\u5c71\uff0c\u65b9\u4eca\u660e\u7559\u4ed6\u591a\u4f4f\u4e00\u5929\uff0c\u5e2e\u4ed6\u6500\u8d8a\u4e86\u4e24\u5ea7\u51b0\u5cf0\uff0c\u5230\u8fbe\u53ef\u4ee5\u9065\u9065\u671b \u89c1\u73e0\u7a46\u6717\u739b\u5cf0\u7684\u5730\u65b9\uff0c\u6842\u534e\u751f\u9065\u671b\u73e0\u5cf0\uff0c\u5fc3\u4e2d\u6b8a\u4e3a\u611f\u6168\u3002 \u3000\u3000\u73e0\u7a46\u6717\u739b\u5cf0\u9ad8\u63d2\u4e91\u5bb5\uff0c\u6842\u534e\u751f\u60f3\u9053\uff1a\u201c\u6b66\u5b66\u6709\u5982\u767b\u5c71\uff0c\u4e0a\u4e86\u4e00\u5c71\uff0c\u8fd8\u6709\u4e00\u5c71\uff0c\u73e0\u5cf0\u662f\u5929 \u4e0b\u7b2c\u4e00\u9ad8\u5cf0\uff0c\u81ea\u53e4\u4ee5\u6765\uff0c\u65e0\u4eba\u80fd\u4e0a\uff0c\u4ee5\u6b64\u55bb\u6bd4\uff0c\u53ea\u6050\u6b66\u5b66\u4ea6\u7136\u3002\u53e4\u5f80\u4eca\u6765\uff0c\u591a\u5c11\u9ad8\u660e\u4e4b\u58eb\uff0c \u4e5f\u7ec3\u4e0d\u5230\u5929\u4e0b\u7b2c\u4e00\u4e4b\u5883\u3002\u201d\u6005\u671b\u826f\u4e45\uff0c\u9893\u7136\u800c\u8fd4\u3002 \u3000\u3000\u65b9\u4eca\u660e\u4f3c\u4e4e\u77e5\u9053\u6842\u534e\u751f\u7684\u5fc3\u601d\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u8001\u5f1f\u4f55\u5fc5\u611f\u6168\uff0c\u767b\u5cf0\u9020\u6781\uff0c\u5343\u53e4\u6240\u96be\u3002\u8001 \u5f1f\u4e0a\u5230\u8fd9\u513f\uff0c\u4e5f\u8db3\u4ee5\u81ea\u8c6a\u4e86\u3002\u201d\u6842\u534e\u751f\u4e0d\u4ee5\u4e3a\u7136\uff0c\u60f3\u7740\u80fd\u591a\u4e0a\u4e00\u5c71\u4fbf\u662f\u4e00\u5c71\uff0c\u60f3\u662f\u5982\u6b64\u60f3 \u6cd5\uff0c\u4f46\u5728\u73e0\u5cf0\u4e4b\u524d\uff0c\u5374\u4e5f\u4e0d\u81ea\u7981\u7684\u611f\u5230\u672c\u8eab\u7684\u6e3a\u5c0f\uff0c\u4fbf\u548c\u65b9\u4eca\u660e\u56de\u6765\u3002 \u3000\u3000\u521a\u521a\u671b\u89c1\u55b7\u6cc9\uff0c\u5ffd\u53c8\u542c\u5f97\u65b9\u4eca\u660e\u5973\u513f\u5439\u6811\u53f6\u7684\u58f0\u97f3\u3002\u65b9\u4eca\u660e\u52c3\u7136\u5927\u6012\uff0c\u9053\uff1a\u201c\u5c82\u6709\u6b64 \u7406\uff0c\u6211\u4f4f\u6b64\u5c71\u5341\u5e74\uff0c\u4ece\u6765\u5e73\u9759\u65e0\u4e8b\uff0c\u60f3\u4e0d\u5230\u6700\u8fd1\u5374\u63a5\u8fde\u6709\u86ee\u4e0d\u8bb2\u7406\u7684\u4eba\u6765\u627e\u9ebb\u70e6\uff01\u201d\u6842\u534e\u751f \u4e5f\u751a\u4e3a\u5947\u602a\uff0c\u60f3\u9053\uff1a\u201c\u6628\u65e5\u90a3\u4e24\u4e2a\u602a\u4eba\u521a\u521a\u6253\u8dd1\uff0c\u600e\u4e48\u53c8\u6709\u4eba\u6765\u4e86\uff1f\u73e0\u5cf0\u811a\u4e0b\uff0c\u7adf\u6709\u5982\u8bb8\u5947 \u4eba\uff1f\u201d \u3000\u3000\u4e24\u4eba\u98de\u6b65\u5954\u56de\uff0c\u4f46\u513f\u56ed\u95e8\u6253\u5f00\uff0c\u6709\u4e00\u4e2a\u9ed1\u9762\u53d1\u53d1\u7684\u5c11\u5e74\uff0c\u6b63\u5728\u5495\u5495\u565c\u565c\uff0c\u53e3\u8bb2\u6307\u5212\u7684\u9017 \u4ed6\u7684\u5973\u513f\u8c08\u7b11\uff0c\u90a3\u5973\u5b69\u5b50\u4e00\u89c1\u7236\u4eb2\u5230\u6765\uff0c\u5c31\u5927\u58f0\u53eb\u9053\uff1a\u201c\u8fd9\u9ed1\u70ad\u5934\u4e71\u6458\u54b1\u4eec\u7684\u82b1\u6735\uff0c\u8fd8\u6b3a\u4fae \u6211\u3002\u201d\u65b9\u4eca\u660e\u5927\u6012\uff0c\u4e00\u7eb5\u8eab\u8df3\u8fc7\u56f4\u5899\uff0c\u5411\u90a3\u9ed1\u9762\u5c11\u5e74\u5c31\u662f\u4e00\u62f3\uff01 \u3000\u3000\u90a3\u5c11\u5e74\u6b63\u60f3\u8bf4\u8bdd\uff0c\u65b9\u4eca\u660e\u7684\u767e\u6b65\u795e\u62f3\u4f55\u7b49\u5389\u5bb3\uff0c\u4eba\u8fd8\u672a\u5230\uff0c\u62f3\u98ce\u5df2\u5288\u62c8\u6765\uff0c\u65b9\u4eca\u660e\u53c8\u662f \u547c\u7684\u4e00\u62f3\u6253\u51fa\uff0c\u8fd9\u4e00\u62f3\u76f8\u8ddd\u65e2\u8fd1\uff0c\u66f4\u4e3a\u731b\u70c8\u3002\u6842\u534e\u751f\u5fc3\u4e2d\u4e00\u52a8\uff0c\u4ed6\u65c1\u89c2\u8005\u6e05\uff0c\u89c1\u8fd9\u5c11\u5e74\u867d\u7136 \u9762\u76ee\u9edc\u9ed1\uff0c\u957f\u5f97\u9887\u4e3a\u6f02\u4eae\uff0c\u4eba\u4e5f\u4f3c\u4e4e\u6ca1\u6709\u6076\u610f\uff0c\u4e0e\u6628\u65e5\u90a3\u4e24\u4e2a\u602a\u4eba\u5927\u4e0d\u76f8\u7c7b\uff0c\u800c\u4e14\u4e3e\u6b62\u4e4b \u95f4\uff0c\u9690\u9690\u6709\u4e00\u80a1\u5c0a\u8d35\u7684\u6c14\u6982\u3002\u65b9\u4eca\u660e\u4e00\u62f3\u6253\u51fa\uff0c\u6842\u534e\u751f\u963b\u6321\u5df2\u6765\u4e0d\u53ca\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u8fd9\u4e00\u62f3 \u6253\u4e0b\uff0c\u6015\u4e0d\u628a\u4ed6\u884c\u4e2a\u7b4b\u65ad\u9aa8\u6298\uff1f\u201d \u3000\u3000\u5fc3\u5ff5\u65b9\u52a8\uff0c\u53ea\u542c\u5f97\u201c\u4e0a\u201d\u7684\u4e00\u58f0\u3002\u65b9\u4eca\u660e\u7684\u62f3\u5934\u5df2\u6253\u4e2d\u4e86\u90a3\u5c11\u5e74\u7684\u80a9\u818a\uff0c\u8fd9\u4e00\u62f3\u6709\u88c2\u77f3 \u4e4b\u80fd\uff0c\u7eb5\u662f\u94c1\u94f8\u7684\u8eab\u5b50\u4e5f\u7981\u53d7\u4e0d\u8d77\uff0c\u4f46\u8bf4\u4e5f\u5947\u602a\uff0c\u90a3\u5c11\u5e74\u7684\u8eab\u4f53\u7adf\u4f3c\u80fd\u591f\u53d8\u5f62\u4f3c\u7684\uff0c\u4e2d\u4e86\u4e00 \u62f3\uff0c\u8eab\u5b50\u4e5f\u597d\u50cf\u62c9\u957f\u4e86\u8bb8\u591a\uff0c\u808c\u8089\u626d\u66f2\uff0c\u5de6\u638c\u5ffd\u7136\u62d0\u53d8\u62cd\u4e86\u8fc7\u6765\uff0c\u628a\u65b9\u4eca\u660e\u7684\u62f3\u5934\u6293\u7740\u3002 \u3000\u3000\u6842\u534e\u751f\u5927\u5403\u4e00\u60ca\uff0c\u4ed6\u5728\u5e03\u8fbe\u62c9\u5bab\u66fe\u9605\u8bfb\u8fc7\u53d9\u8ff0\u5370\u5ea6\u6b66\u672f\u7684\u4e66\uff0c\u8fd9\u65f6\u60f3\u8d77\uff0c\u5c0f\u9053\uff1a\u201c\u83ab\u975e \u8fd9\u5c31\u662f\u5370\u5ea6\u7684\u745c\u4f3d\u672f\uff0c\u4e66\u4e2d\u8bf4\u5370\u5ea6\u7684\u745c\u4f3d\u672f\u53ef\u4eca\u808c\u8089\u5c48\u66f2\uff0c\u968f\u610f\u53d8\u5f62\uff0c\u7ec3\u5230\u6781\u9ad8\u7684\u5883\u754c\uff0c\u80fd \u8010\u706b\u70e7\u9524\u51fb\uff0c\u53ef\u4ee5\u95ed\u6c14\u4e03\u65e5\u4e0d\u6b7b\uff0c\u4e0e\u4e2d\u56fd\u6781\u4e0a\u4e58\u7684\u5185\u529f\u6709\u5f02\u66f2\u540c\u529f\u4e4b\u5999\u3002\u8fd9\u5c11\u5e74\u529f\u592b\u6df1\u6d45\u672a \u77e5\uff0c\u4f46\u53ea\u4ee5\u4ed6\u80fd\u6321\u5f97\u4f4f\u65b9\u4eca\u660e\u8fd9\u4e00\u62f3\u800c\u8bba\uff0c\u53ea\u6050\u4e5f\u4e0d\u5728\u6211\u4e4b\u4e0b\u4e86\u3002\u201d\u672c\u60f3\u4e0a\u524d\u529d\u89e3\uff0c\u89c1\u6b64\u5947 \u8ff9\uff0c\u4fbf\u53c8\u5fcd\u4f4f\u3002 \u3000\u3000\u518d\u770b\u4e0b\u53bb\uff0c\u90a3\u5c11\u5e74\u7684\u745c\u4f3d\u529f\u592b\u663e\u7136\u8fd8\u6ca1\u6709\u7ec3\u5230\u6781\u9ad8\u7684\u5883\u754c\uff0c\u4f46\u89c1\u4ed6\u5927\u6c57\u6dcb\u9152\uff0c\u652f\u6301\u5f97\u9887 \u4e3a\u5403\u529b\u3002\u4f46\u65b9\u4eca\u660e\u88ab\u4ed6\u6293\u7740\u62f3\u5934\uff0c\u7adf\u4e5f\u4e0d\u80fd\u79fb\u52a8\u4e00\u6b65\u3002\u4e24\u4eba\u5230\u4e86\u6b64\u9645\uff0c\u53cc\u65b9\u771f\u529b\u76f8\u8feb\uff0c\u8c01\u82e5 \u653e\u677e\uff0c\u8c01\u5c31\u53d7\u4f24\uff0c\u53ea\u597d\u5168\u795e\u8d2f\u6ce8\uff0c\u5f7c\u6b64\u50f5\u6301\uff0c\u8fde\u5f00\u53e3\u8bf4\u8bdd\u4e5f\u4e0d\u80fd\u591f\u3002\u65b9\u4eca\u660e\u4e5f\u5f88\u5403\u529b\uff0c\u8fc7\u4e86 \u7247\u523b\uff0c\u5934\u4e0a\u4e5f\u5192\u51fa\u70ed\u817e\u817e\u7684\u767d\u6c14\u6765\u3002\u90a3\u5973\u5b69\u4e8e\u53eb\u9053\uff1a\u201c\u53d4\u53d4\uff0c\u4f60\u5feb\u6765\u5e2e\u6211\u7684\u7239\u7239\u5440\uff01\u201d\u6842\u534e \u751f\u89c1\u6b64\u60c5\u72b6\uff0c\u77e5\u9053\u4e24\u4eba\u529f\u529b\u76f8\u5f53\uff0c\u82e5\u518d\u4e0d\u4e0a\u524d\u89e3\u62c6\uff0c\u53ea\u6050\u90fd\u8981\u53d7\u4f24\uff01\u201d \u3000\u3000\u6842\u534e\u751f\u8fd0\u6c14\u62a4\u8eab\uff0c\u529b\u8d2f\u638c\u5fc3\uff0c\u4e00\u8dc3\u800c\u524d\uff0c\u4f7f\u4e86\u4e00\u62db\u201c\u91ce\u9a6c\u5206\u987b\u201d\uff0c\u53cc\u638c\u4e00\u5e26\uff0c\u500f\u7136\u95f4\u4e24 \u80a1\u5927\u529b\u90fd\u5411\u4ed6\u8eab\u4e0a\u63a8\u6765\uff0c\u5e78\u800c\u6842\u534e\u751f\u65e9\u6709\u9632\u5907\uff0c\u53cc\u638c\u4e00\u5e26\uff0c\u7acb\u5373\u8eab\u5f62\u659c\u98de\uff0c\u4f46\u542c\u5f97\u201c\u8f70\u9686\u201d \u4e00\u58f0\uff0c\u4e00\u68f5\u82b1\u6811\u5df2\u88ab\u6298\u65ad\uff0c\u65b9\u4eca\u660e\u4e0e\u90a3\u5370\u5ea6\u5c11\u5e74\u4ea6\u5df2\u5206\u5f00\u3002 \u3000\u3000\u65b9\u4eca\u660e\u4f59\u6012\u672a\u606f\uff0c\u5ffd\u542c\u5f97\u90a3\u5c11\u5e74\u7528\u85cf\u8bed\u8bf4\u9053\uff1a\u201c\u6211\u5b9e\u65e0\u6076\u610f\uff0c\u8001\u4e08\u5e78\u52ff\u8bef\u4f1a\u3002\u201d\u65b9\u4eca\u660e \u7684\u5973\u513f\u56b7\u9053\uff1a\u201c\u4f60\u5f04\u65ad\u6211\u7684\u82b1\u6811\uff0c\u6211\u8981\u4f60\u8d54\u3002\u201d\u90a3\u5c11\u5e74\u5411\u65b9\u4eca\u660e\u4f5c\u4e86\u4e00\u63d6\uff0c\u8f6c\u8fc7\u8eab\u6765\u5bf9\u90a3\u5973 \u5b69\u5b50\u8bf4\u9053\uff1a\u201c\u8d54\u8fd9\u4e2a\u7ed9\u4f60\u597d\u5417\uff1f\u201d\u638f\u51fa\u4e00\u4e2a\u671b\u8fdc\u955c\u6765\uff0c\u6559\u90a3\u5973\u5b69\u5b50\u5c06\u773c\u775b\u51d1\u4e0a\u955c\u5934\u53bb\u770b\uff0c\u8fdc \u5904\u51b0\u638c\u4e0a\u6b63\u8d70\u8fc7\u4e00\u7fa4\u91ce\u9e7f\uff0c\u5728\u955c\u7b52\u91cc\u4e5f\u770b\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u5973\u5b69\u5b50\u7834\u6d95\u4e3a\u7b11\uff0c\u6025\u5fd9\u8bf4\u9053\uff1a\u201c\u591a\u8c22 \u53d4\u53d4\u3002\u201d\u65b9\u4eca\u660e\u672c\u60f3\u4e0d\u51c6\u5973\u513f\u53d7\u5916\u4eba\u7684\u4e1c\u897f\uff0c\u89c1\u5979\u59d4\u5b9e\u6b22\u559c\uff0c\u4e5f\u597d\u8ba9\u5979\u8981\u4e86\u3002 \u3000\u3000\u8fd9\u4e00\u6765\u654c\u610f\u5927\u6d88\uff0c\u6842\u534e\u751f\u8c03\u5300\u547c\u5438\uff0c\u5fae\u7b11\u8d70\u6765\uff0c\u540c\u90a3\u5370\u5ea6\u5c11\u5e74\u4e92\u76f8\u89c1\u793c\uff0c\u7b11\u9053\uff1a\u201c\u9601\u4e0b \u771f\u597d\u529f\u592b\u3002\u201d\u90a3\u5c11\u5e74\u89c1\u6842\u534e\u751f\u6ca1\u6709\u53d7\u4f24\uff0c\u66f4\u662f\u4f69\u670d\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u542c\u56fd\u4e2d\u9ad8\u50e7\u6240\u8a00\uff0c\u4e2d\u56fd\u7684\u5185 \u5bb6\u4e00\u6d3e\uff0c\u6e90\u51fa\u5c11\u6797\uff0c\u800c\u5c11\u6797\u6d3e\u7684\u4e0a\u4e58\u6b66\u529f\uff0c\u5219\u662f\u7531\u6211\u56fd\u7684\u8fbe\u6469\u7985\u5e08\u5085\u8fc7\u53bb\u7684\u3002\u5982\u4eca\u770b\u6765\uff0c\u5df2 \u662f\u9752\u51fa\u4e8e\u84dd\uff0c\u8d35\u56fd\u7684\u6b66\u5b66\u5b9a\u5fc5\u80dc\u4e8e\u655d\u56fd\u4e86\u3002\u201d \u3000\u3000\u6842\u534e\u751f\u4e00\u542c\uff0c\u679c\u7136\u662f\u4ece\u5370\u5ea6\u6765\u7684\uff0c\u8bf4\u9053\uff1a\u201c\u5144\u53f0\u8fc7\u8c26\u4e86\uff0c\u542c\u8bf4\u8d35\u56fd\u7684\u9f99\u53f6\u5927\u5e08\uff0c\u6b66\u529f\u5df1 \u7ec3\u5230\u901a\u7384\u4e4b\u5883\uff0c\u53ef\u60dc\u6211\u65e0\u7f18\u62dc\u89c1\u3002\u201d\u90a3\u5370\u5ea6\u5c11\u5e74\u9053\uff1a\u201c\u9f99\u53f6\u4e0a\u4eba\u6b63\u662f\u5bb6\u5e08\uff0c\u53ea\u662f\u6211\u8d44\u8d28\u9c81 \u949d\uff0c\u8fd8\u6ca1\u6709\u5b66\u5230\u4ed6\u8001\u4eba\u5bb6\u7ea6\u4e8c\u6210\u529f\u592b\u3002\u201d\u4ed6\u8bf4\u8bdd\u4e4b\u65f6\uff0c\u4e0d\u4f4f\u7684\u6253\u91cf\u6842\u534e\u751f\uff0c\u4f3c\u662f\u5bf9\u4ed6\u77e5\u9053\u6709 \u9f99\u53f6\u5927\u5e08\uff0c\u9887\u8868\u8be7\u610f\u3002 \u3000\u3000\u4e09\u4eba\u4e92\u901a\u59d3\u540d\uff0c\u539f\u6765\u8fd9\u4e2a\u5c11\u5e74\u540d\u53eb\u96c5\u5fb7\u661f\uff0c\u662f\u4ece\u5370\u5ea6\u201c\u738b\u820d\u57ce\u201d\u6765\u7684\uff0c\u201c\u738b\u820d\u57ce\u201d\u5bf9\u6842 \u534e\u751f\u5e76\u4e0d\u964c\u751f\uff0c\u56e0\u4e3a\u90a3\u662f\u4f5b\u6559\u53f2\u4e0a\u4e00\u4e2a\u6709\u540d\u7684\u5730\u65b9\uff0c\u4e2d\u56fd\u7684\u7384\u5958\uff08\u5510\u4e09\u85cf\uff09\u6cd5\u5e08\u66fe\u5728\u90a3\u91cc\u8bb2 \u8fc7\u4f5b\u6cd5\u3002 \u3000\u3000\u6842\u534e\u751f\u95ee\u9053\uff1a\u201c\u5144\u53f0\u72ec\u4e0a\u96ea\u5c71\uff0c\u4e3a\u4e86\u4f55\u4e8b\uff1f\u201d\u96c5\u5fb7\u661f\u6709\u70b9\u89d1\u8774\uff0c\u8bf4\u9053\uff1a\u201c\u5b9e\u4e0d\u76f8\u7792\uff0c\u6211 \u662f\u6765\u627e\u96ea\u83b2\u3002\u201d\u6842\u534e\u751f\u5947\u9053\uff1a\u201c\u6765\u627e\u96ea\u83b2\uff1f\u201d\u96c5\u5fb7\u661f\u9053\uff1a\u201c\u6211\u542c\u8bf4\u5728\u4e2d\u56fd\u7684\u51b0\u5cf0\u4e4b\u4e0a\uff0c\u6709\u4e00 \u79cd\u96ea\u4e2d\u5f00\u653e\u7684\u83b2\u82b1\uff0c\u80fd\u89e3\u767e\u6bd2\uff0c\u4e0d\u77e5\u662f\u5426\uff1f\u201d\u6842\u534e\u751f\u9053\uff1a\u201c\u51b0\u5cf0\u4e4b\u4e0a\u4e5f\u4e0d\u4e00\u5b9a\u4fbf\u4ea7\u96ea\u83b2\uff0c\u636e \u6211\u6240\u77e5\uff0c\u5929\u5c71\u7684\u5357\u5317\u9ad8\u5cf0\uff0c\u5076\u7136\u53ef\u4ee5\u91c7\u5230\u96ea\u83b2\uff0c\u8fd9\u559c\u9a6c\u62c9\u96c5\u5c71\u4e0a\u6709\u6ca1\u6709\u96ea\u83b2\uff0c\u53ef\u8981\u95ee\u65b9\u8001\u5148 \u751f\u624d\u77e5\u6653\u4e86\u3002\u201d\u65b9\u4eca\u660e\u9053\uff1a\u201c\u6211\u5728\u8fd9\u91cc\u4f4f\u4e86\u5c06\u8fd1\u5341\u5e74\uff0c\u4ece\u672a\u89c1\u8fc7\u96ea\u83b2\u3002\u201d\u90a3\u5370\u5ea6\u5c11\u5e74\u751a\u662f\u5931 \u671b\u3002\u6842\u534e\u751f\u60f3\u9053\uff1a\u201c\u4ed6\u5192\u4e86\u6027\u547d\u7684\u5371\u9669\uff0c\u6765\u5230\u73e0\u5cf0\u811a\u4e0b\u5bfb\u627e\u96ea\u83b2\uff0c\u5fc5\u5b9a\u662f\u6781\u9700\u8981\u7684\u4e86\u3002\u201d\u5fae \u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u6b63\u597d\u6709\u4e09\u6735\u96ea\u83b2\uff0c\u4fbf\u9001\u4e00\u6735\u7ed9\u9601\u4e0b\u5427\u3002\u201d\u53d6\u4e86\u51fa\u6765\uff0c\u5947\u9999\u99a5\u90c1\u3002\u90a3\u5c11\u5e74 \u5413\u4e86\u4e00\u8df3\uff0c\u9053\uff1a\u201c\u8fd9\u6837\u7a00\u4e16\u4e4b\u73cd\uff0c\u5144\u53f0\u4e3e\u4ee5\u76f8\u8d60\uff0c\u4f55\u4ee5\u6562\u5f53\u3002\u6211\u5e26\u6709\u4e00\u4e9b\u73e0\u5b9d\uff0c\u613f\u2014\u2014\u201d\u6842 \u534e\u751f\u4e00\u7b11\u622a\u65ad\u4ed6\u7684\u8bf4\u8bdd\uff0c\u9053\uff1a\u201c\u4f60\u8fed\u7ed9\u4e86\u5c0f\u59b9\u59b9\u8fd9\u4e2a\u671b\u8fdc\u955c\uff0c\u6211\u8fed\u7ed9\u4f60\u4e00\u6735\u96ea\u83b2\uff0c\u7528\u4e2d\u56fd\u7684 \u8bdd\u6765\u8bf4\uff0c\u90a3\u662f\u6295\u6843\u62a5\u674e\uff0c\u4f55\u5fc5\u5ba2\u6c14\u3002\u201d\u5176\u5b9e\u96ea\u83b2\u6bd4\u4e4b\u671b\u8fdc\u955c\u90a3\u4e0d\u77e5\u8981\u73cd\u8d35\u51e0\u5341\u4e07\u500d\u3002\u5370\u5ea6\u5c11 \u5e74\u89c1\u4ed6\u5982\u6b64\u6177\u6168\uff0c\u60ca\u8be7\u5f97\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002\u65b9\u4eca\u660e\u4e5f\u751a\u662f\u6b22\u559c\uff0c\u5982\u6b64\u4e00\u6765\uff0c\u8981\u4e86\u4ed6\u7684\u671b\u8fdc\u955c\uff0c\u4e5f \u4e0d\u7b97\u6cbe\u4ed6\u7684\u60c5\u4e86\u3002 \u3000\u3000\u96c5\u5fb7\u661f\u85cf\u597d\u96ea\u83b2\uff0c\u5bf9\u6842\u534e\u751f\u9053\uff1a\u201c\u6df1\u60c5\u539a\u8c0a\uff0c\u65e0\u4ee5\u8a00\u62a5\uff0c\u5144\u53f0\u4ed6\u65e5\u82e5\u5230\u655d\u56fd\uff0c\u5c0f\u5f1f\u5f53\u7565 \u5c3d\u5730\u4e3b\u4e4b\u8c0a\u3002\u201d\u6842\u534e\u751f\u89c1\u4ed6\u884c\u8272\u5306\u5306\uff0c\u5fc3\u9053\uff1a\u201c\u4ed6\u8bf4\u6500\u767b\u559c\u9a6c\u62c9\u96c5\u5c71\uff0c\u53ea\u662f\u4e3a\u4e86\u5bfb\u627e\u96ea\u83b2\uff0c \u770b\u6765\u5e76\u975e\u865a\u8bed\u3002\u201d\u65b9\u4eca\u660e\u9053\uff1a\u201c\u8fd9\u4f4d\u6842\u5144\u4e5f\u8981\u4e0b\u5c71\uff0c\u4e24\u4f4d\u4f55\u4e0d\u5728\u5bd2\u820d\u76d8\u6853\u51e0\u65e5\uff0c\u4e0b\u5c71\u65f6\u4e5f\u6709 \u4e2a\u4f34\u513f\u3002\u201d\u96c5\u5fb7\u661f\u559c\u5f62\u4e8e\u8272\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u53ea\u9053\u6842\u5144\u662f\u5728\u6b64\u5c71\u9690\u5c45\uff0c\u539f\u6765\u4e5f\u662f\u4e0b\u5c71\u53bb\u7684\u3002\u6562\u95ee \u6842\u5144\u5c06\u5f80\u4f55\u5904\uff1f\u201d\u6842\u534e\u751f\u9053\uff1a\u201c\u6211\u60f3\u5f80\u5c3c\u6cca\u5c14\u4e00\u884c\u3002\u201d \u3000\u3000\u96c5\u5fb7\u661f\u5fae\u5fae\u4e00\u6014\uff0c\u5931\u58f0\u8bf4\u9053\uff1a\u201c\u6842\u5144\u4e5f\u662f\u5f80\u5c3c\u6cca\u5c14\u53c2\u52a0\u8003\u8bd5\u7684\u5417\uff1f\u201d\u6842\u534e\u751f\u6014\u4e86\u4e00\u6014\uff0c \u8be7\u9053\uff1a\u201c\u4ec0\u4e48\u8003\u8bd5\uff1f\u201d\u5fc3\u60f3\uff1a\u201c\u83ab\u975e\u5c3c\u6cca\u5c14\u4e5f\u6709\u79d1\u5b66\u4e0d\u6210\uff1f\u4f46\u5f02\u90a6\u4eba\u58eb\uff0c\u5c82\u80fd\u53c2\u52a0\u8003\u8bd5\uff1f\u201d \u96c5\u5fb7\u661f\u9762\u8272\u4e0d\u5927\u81ea\u7136\uff0c\u4f3c\u662f\u540e\u6094\u5931\u8a00\uff0c\u52c9\u5f3a\u7b11\u9053\uff1a\u201c\u539f\u6765\u6842\u5144\u4e0d\u77e5\u6b64\u4e8b\uff0c\u90a3\u5c31\u4e0d\u63d0\u4e5f\u7f62\u3002\u201d \u3000\u3000\u4f73\u534e\u751f\u597d\u5947\u4e4b\u5fc3\u5927\u8d77\uff0c\u7b11\u9053\uff1a\u201c\u8bf4\u8bf4\u4f55\u59a8\uff1f\u201d\u96c5\u5fb7\u661f\u672c\u60f3\u4e0d\u8bf4\uff0c\u4f46\u60f3\u5230\u522b\u4eba\u8fde\u7a00\u4e16\u5947\u73cd \u7684\u5929\u5c71\u96ea\u83b2\u4e5f\u80af\u6168\u7136\u76f8\u8d60\uff0c\u600e\u597d\u6b3a\u7792\u3002\u4fbf\u9053\uff1a\u201c\u4ed6\u4eec\u7684\u8003\u8bd5\u5e76\u975e\u50cf\u8d35\u56fd\u8003\u72b6\u5143\u7684\u90a3\u79cd\u8003\u8bd5\uff0c \u4e43\u662f\u5c3c\u6cca\u5c14\u516c\u4e3b\u8981\u8003\u9009\u4e08\u592b\u3002\u201d \u3000\u3000\u6842\u534e\u751f\u53ea\u5f53\u542c\u6d77\u5916\u5947\u8c08\uff0c\u7b11\u9053\uff1a\u201c\u600e\u6837\u8003\u9009\u4e08\u592b\uff1f\u201d\u96c5\u5fb7\u661f\u9053\uff1a\u201c\u542c\u8bf4\u5c3c\u6cca\u5c14\u516c\u4e3b\u7f8e\u8c8c \u975e\u5e38\uff0c\u800c\u4e14\u6587\u6b66\u53cc\u5168\u3002\u56fd\u4e2d\u8d35\u4ecb\u516c\u5b50\uff0c\u4e2a\u4e2a\u90fd\u60f3\u6c42\u516c\u4e3b\u4e3a\u59bb\uff0c\u516c\u4e3b\u5374\u4e00\u4e2a\u4e5f\u4e0d\u5408\u610f\u3002\u56fd\u738b\u5341 \u5206\u7126\u6025\uff0c\u8bf4\u4ed6\u82e5\u9009\u4e0d\u4e2d\u5408\u610f\u4e4b\u4eba\uff0c\u5c31\u8981\u4e3a\u5979\u4ee3\u62e9\u9a79\u9a6c\u3002\u516c\u4e3b\u4e0d\u5141\uff0c\u81ea\u5df1\u63d0\u51fa\u4e00\u4e2a\u8003\u9009\u4e08\u592b\u7684 \u529e\u6cd5\uff0c\u5148\u8bd5\u6587\uff0c\u540e\u8bd5\u6b66\u827a\uff0c\u542c\u8bf4\u6709\u4e9b\u5f02\u90a6\u4eba\u58eb\uff0c\u4e5f\u95fb\u98ce\u800c\u6765\u3002\u201d\u6842\u534e\u751f\u7b11\u9053\uff1a\u201c\u7b2c\u4e00\u5173\u662f\u5148 \u8bd5\u6587\u624d\uff0c\u90a3\u4e48\u5f02\u90a6\u4eba\u58eb\uff0c\u5982\u4f55\u8bd5\u6cd5\uff1f\u201d\u96c5\u5fb7\u661f\u9053\uff1a\u201c\u8fd9\u4f4d\u5c3c\u6cca\u5c14\u516c\u4e3b\u4e0d\u4f46\u662f\u672c\u56fd\u6765\u7684\u624d\u5973\uff0c \u800c\u4e14\u901a\u6653\u6c49\u6587\u3001\u68b5\u6587\u3001\u963f\u523a\u4f2f\u6587\u3002\u53cd\u6b63\u4ece\u5916\u56fd\u7684\u4e5f\u4e0d\u5916\u4e2d\u56fd\u3001\u5370\u5ea6\u3001\u963f\u523a\u4f2f\u8fd9\u4e9b\u56fd\u5bb6\u3002\u82e5\u662f \u5f02\u90a6\u4eba\u58eb\u5e94\u8bd5\uff0c\u5979\u4fbf\u8003\u4f60\u672c\u56fd\u7684\u6587\u5b66\uff0c\u4e0d\u8fc7\uff0c\u65e0\u8bba\u5982\u4f55\uff0c\u5c3c\u6cca\u5c14\u6587\u5b66\u4e5f\u8981\u7a0d\u4ec5\u4e00\u4e9b\u3002\u201d\u6842\u534e \u751f\u9053\uff1a\u201c\u6b66\u827a\u53c8\u5982\u4f55\u8003\u6cd5\uff1f\u201d\u96c5\u5fb7\u661f\u9053\uff1a\u201c\u8bd5\u6b66\u827a\u7684\u5148\u8981\u901a\u8fc7\u51e0\u5173\u6781\u96be\u7684\u8003\u8bd5\uff0c\u7136\u540e\u518d\u4e0e\u5b83 \u7684\u5bab\u5973\u6bd4\u8bd5\uff0c\u8d62\u5b83\u7684\u5bab\u5973\uff0c\u6700\u540e\u8fd8\u8981\u548c\u5979\u6bd4\u5251\u3002\u201d\u6842\u534e\u751f\u9053\uff1a\u201c\u8fd9\u4f4d\u516c\u4e3b\u5982\u6b64\u6311\u9009\u4e08\u592b\uff0c\u771f \u53ef\u7b97\u5f97\u662f\u5973\u4e2d\u82f1\u8c6a\uff0c\u86fe\u7709\u56fd\u58eb\u4e86\u3002\u201d\u96c5\u5fb7\u661f\u9053\uff1a\u201c\u53ef\u4e0d\u662f\u5417\uff1f\u542c\u8bf4\u4ece\u53bb\u5e74\u5f00\u59cb\u8003\u9009\uff0c\u81f3\u4eca\u8fd8 \u672a\u6709\u5408\u683c\u7684\u4eba\u3002\u201d \u3000\u3000\u6842\u534e\u751f\u5fc3\u4e2d\u4e00\u52a8\uff0c\u4e0d\u81ea\u7981\u7684\u60f3\u8d77\u767d\u8863\u5c11\u5973\u6765\uff0c\u968f\u5373\u54d1\u7136\u5931\u7b11\uff0c\u5fc3\u9053\uff1a\u201c\u7edd\u4e0d\u4f1a\u662f\u5979\uff0c\u82e5 \u7136\u662f\u5979\uff0c\u5979\u5c82\u80fd\u72ec\u51fa\u6df1\u5bab\uff0c\u5728\u9b54\u9b3c\u57ce\u4e2d\u73b0\u8eab\uff0c\u5728\u5e03\u8fbe\u62c9\u5bab\u9732\u9762\uff1f\u800c\u4e14\uff0c\u82e5\u5979\u4fbf\u662f\u516c\u4e3b\uff0c\u5c3c\u6cca \u5c14\u738b\u5b50\u4fbf\u662f\u4ed6\u7684\u957f\u5144\uff0c\u4ed6\u53c8\u600e\u4f1a\u5982\u6b64\u6015\u5979\uff1f\u201d\u867d\u7136\u65ad\u5b9a\u4e86\u767d\u8863\u5c11\u5973\u4e0d\u4f1a\u5c31\u662f\u516c\u4e3b\uff0c\u5374\u4e5f\u7981\u4e0d \u4f4f\u5fc3\u4e2d\u5947\u602a\uff0c\u60f3\u9053\uff1a\u201c\u600e\u4e48\u5c3c\u6cca\u5c14\u6709\u8fd9\u4e48\u591a\u5dfe\u9685\u5947\u624d\uff0c\u5c06\u6765\u82e5\u5230\u4e86\u5c3c\u6cca\u5c14\uff0c\u6211\u5012\u8981\u770b\u770b\u8fd9\u4f4d \u516c\u4e3b\uff0c\u770b\u5979\u80fd\u4e0d\u80fd\u548c\u6211\u7684\u534e\u738b\u59b9\u59b9\u76f8\u6bd4\u3002\u201d \u3000\u3000\u96c5\u5fb7\u661f\u8bf4\u5b8c\u4e4b\u540e\uff0c\u8a00\u4e0d\u7531\u8877\u7684\u7b11\u9053\uff1a\u201c\u4ee5\u5144\u53f0\u7684\u6b66\u529f\uff0c\u82e5\u53bb\u5e94\u8bd5\uff0c\u6216\u8bb8\u80fd\u6709\u51e0\u5206\u628a\u63e1\uff0c \u5144\u53f0\u5176\u6709\u610f\u4e4e\uff1f\u201d\u6842\u534e\u751f\u5927\u7b11\u9053\uff1a\u201c\u6211\u6027\u559c\u6d6a\u6e38\uff0c\u8eab\u4f3c\u95f2\u4e91\u91ce\u9e64\uff0c\u4f11\u8bf4\u6ca1\u6709\u628a\u63e1\uff0c\u5373\u6709\u628a \u63e1\uff0c\u4e5f\u7edd\u4e0d\u613f\u81ea\u6295\u7f57\u7f51\uff0c\u62d8\u675f\u7ec8\u751f\u3002\u800c\u4e14\uff0c\u6211\u5bf9\u5c3c\u6cca\u5c14\u6587\u5b66\u4e00\u7a8d\u4e0d\u901a\uff0c\u5982\u4f55\u5e94\u8bd5\uff1f\u201d\u5176\u5b9e\uff0c \u4ed6\u5fc3\u4e2d\u60f3\u7684\u53ea\u662f\u767d\u8863\u5c11\u5973\uff0c\u7eb5\u4f7f\u5929\u4ed9\u4e0b\u51e1\uff0c\u4e5f\u7edd\u4e0d\u80fd\u4ee4\u4ed6\u79fb\u5fc3\u522b\u5411\u3002\u4e0d\u8fc7\u542c\u5f97\u5c3c\u6cca\u5c14\u516c\u4e3b\u6b64 \u5947\u7279\u884c\u4e3a\uff0c\u7a0d\u7a0d\u5f15\u8d77\u597d\u5947\u4e4b\u5ff5\u800c\u5df2\u3002 \u3000\u3000\u96c5\u5fb7\u661f\u542c\u6842\u534e\u751f\u8bf4\u65e0\u610f\u5e94\u8bd5\uff0c\u795e\u8272\u7a0d\u89c1\u81ea\u7136\uff0c\u95ee\u6842\u534e\u751f\u9053\uff1a\u201c\u5144\u53f0\u8f9b\u8f9b\u82e6\u82e6\u4e0a\u5c71\uff0c\u5927\u7ea6 \u8fd8\u8981\u4f4f\u51e0\u5929\u5427\uff1f\u201d\u6842\u534e\u751f\u4f55\u7b49\u806a\u660e\uff0c\u65e9\u77e5\u5176\u610f\uff0c\u7b11\u9053\uff1a\u201c\u543e\u5144\u82e5\u6709\u8981\u7d27\u7684\u4e8b\u60c5\uff0c\u5c0f\u5f1f\u4e0d\u6562\u5f3a \u7559\u3002\u53cd\u6b63\u54b1\u4eec\u90fd\u662f\u53bb\u540c\u4e00\u5730\u65b9\uff0c\u6216\u8bb8\u5728\u5c3c\u6cca\u5c14\u8fd8\u80fd\u89c1\u9762\u3002\u6211\u5728\u8fd9\u91cc\u5927\u7ea6\u8fd8\u6709\u4e00\u4e24\u5929\u803d\u6401\u3002\u201d \u96c5\u5fb7\u661f\u7acb\u5373\u544a\u8f9e\uff0c\u5e76\u5411\u6842\u534e\u751f\u518d\u6b21\u81f4\u8c22\uff0c\u7136\u540e\uff0c\u4fbf\u72ec\u81ea\u4e0b\u5c71\u53bb\u4e86\u3002 \u3000\u3000\u65b9\u4eca\u660e\u9053\uff1a\u201c\u8fd9\u4eba\u597d\u602a\uff0c\u521a\u624d\u4ed6\u542c\u8bf4\u4f60\u521a\u8981\u4e0b\u5c71\uff0c\u795e\u8272\u751a\u662f\u559c\u60a6\uff0c\u770b\u6765\u5f88\u60f3\u4e0e\u4f60\u7ed3\u4f34\u540c \u884c\u3002\u5c82\u77e5\uff0c\u8f6c\u773c\u4e4b\u95f4\uff0c\u4ed6\u53c8\u53d8\u4e86\u4e3b\u610f\uff0c\u597d\u50cf\u751f\u6015\u4f60\u8981\u4e0e\u4ed6\u540c\u884c\u4e86\u3002\u201d\u6842\u534e\u751f\u9053\uff1a\u201c\u4ed6\u6015\u6211\u62a2 \u4e86\u4ed6\u7684\u516c\u4e3b\u5462\u3002\u5176\u5b9e\u6211\u90a3\u6709\u8fd9\u4e2a\u610f\u601d\u3002\u201d\u8a00\u7f62\u54c8\u54c8\u5927\u7b11\u3002 \u3000\u3000\u6842\u534e\u751f\u5728\u5c71\u4e0a\u966a\u4e86\u65b9\u4eca\u660e\u4e24\u5929\uff0c\u65b9\u4eca\u660e\u7684\u85cf\u65cf\u59bb\u5b50\u672c\u662f\u559c\u9a6c\u62c9\u96c5\u5c71\u4e0b\u7684\u7267\u4eba\u5973\u513f\uff0c\u66fe\u5230 \u8fc7\u5c3c\u6cca\u5c14\uff0c\u8fd9\u4e24\u5929\u4e2d\u544a\u8bc9\u4e86\u6842\u534e\u751f\u8bb8\u591a\u6709\u5173\u5c3c\u6cca\u5c14\u7684\u98ce\u4fd7\u4e60\u60ef\uff0c\u6842\u534e\u751f\u5f97\u76ca\u4e0d\u5c11\u3002\u4e34\u8d70\u90a3 \u5929\uff0c\u65b9\u4eca\u660e\u9001\u4e86\u4ed6\u8bb8\u591a\u8089\uff0c\u4e0e\u679c\u5e72\uff0c\u8089\u94fa\u662f\u7528\u7280\u725b\u8089\u814a\u7684\uff0c\u679c\u5e72\u4e5f\u662f\u559c\u9a6c\u62c9\u96c5\u5c71\u4e0a\u7279\u6709\u7684\u91ce \u679c\u5236\u7684\uff0c\u7269\u8f7b\u60c5\u91cd\uff0c\u6842\u534e\u751f\u751a\u662f\u611f\u6fc0\u3002\u65b9\u4eca\u660e\u592b\u59bb\u9001\u4ed6\u8fc7\u4e86\u4e24\u5904\u51b0\u5761\uff0c\u624d\u4f9d\u4f9d\u4e0d\u820d\u9053\u522b\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u9b44\u5bd2\u5149\u5251\u300b\u2014\u2014\u7b2c\u4e03\u7ae0\u3000\u53e4\u5821\u6df1\u5bb5\u9f99\u864e\u6597 \u6881\u7fbd\u751f\u300a\u51b0\u9b44\u5bd2\u5149\u5251\u300b \u7b2c\u4e03\u7ae0\u3000\u53e4\u5821\u6df1\u5bb5\u9f99\u864e\u6597 \u3000\u3000\u4e0b\u5c71\u4e4b\u540e\uff0c\u4ece\u5317\u9762\u5c71\u811a\u7ed5\u5230\u5c71\u5357\uff0c\u4fbf\u8e0f\u5165\u4e86\u5c3c\u6cca\u5c14\u7684\u56fd\u5883\u4e86\u3002\u8fd9\u65f6\u5df2\u662f\u4ef2\u590f\u65f6\u8282\uff0c\u5904\u5904 \u9e1f\u8bed\u82b1\u9999\uff0c\u4e0e\u5c71\u4e2d\u7684\u51b0\u5929\u96ea\u5730\u76f8\u6bd4\uff0c\u4fe8\u5982\u4e24\u4e2a\u4e16\u754c\u3002 \u3000\u3000\u5c3c\u6cca\u5c14\u662f\u4e2a\u5c71\u56fd\uff0c\u8fd1\u4ee3\u65c5\u884c\u5bb6\u79f0\u4e4b\u4e3a\u201c\u4e1c\u65b9\u745e\u58eb\u201d\uff0c\u98ce\u666f\u5e7d\u7f8e\uff0c\u53ef\u60f3\u800c\u77e5\u3002\u5883\u5185\u6e56\u6cca\u751a \u591a\uff0c\u6cbf\u9014\u7eb5\u76ee\uff0c\u4f46\u89c1\u767d\u96ea\u77aa\u77aa\u7684\u5c71\u638c\u9ad8\u63d2\u4e91\u9704\uff0c\u5c71\u6cc9\u7011\u5e03\u5954\u817e\u4eba\u6e56\uff0c\u7eff\u6811\u4e1b\u4e2d\u99a5\u90c1\u7684\u9c9c\u82b1\u4e89 \u598d\u6597\u8273\uff0c\u5f69\u8272\u7684\u5c0f\u9c7c\u5728\u78a7\u84dd\u7684\u6e56\u91cc\u60a0\u54c9\u6e38\u54c9\u3002\u6842\u534e\u751f\u4e0d\u4f4f\u8d5e\u53f9\uff1a\u201c\u597d\u4e00\u7247\u5b81\u9759\u800c\u7f8e\u4e3d\u7684\u666f \u8c61\uff01\u65e0\u6b8a\u4e16\u5916\u6843\u6e90\u3002\u201d\u60f3\u8d77\u5c3c\u6cca\u5c14\u738b\u5b50\u5728\u8fd9\u6837\u5b81\u9759\u548c\u5e73\u7684\u56fd\u5ea6\u91cc\uff0c\u8fd8\u60f3\u9eef\u6b66\u7a77\u5175\uff0c\u771f\u662f\u4eba\u715e \u98ce\u666f\u3002 \u3000\u3000\u5c3c\u6cca\u5c14\u53c8\u662f\u4e2a\u4f5b\u6559\u53e4\u56fd\uff0c\u4f5b\u79df\u91ca\u8fe6\u725f\u5c3c\u5c31\u662f\u8bde\u751f\u5728\u5c3c\u6cca\u5c14\u5357\u90e8\u5854\u62c9\u4f0a\u5730\u5c71\u533a\u7684\u5170\u6bd7\u5c3c\u56ed \uff08\u53e4\u65f6\u5c5e\u4e8e\u5370\u5ea6\uff09\u3002\u56e0\u6b64\u6cbf\u9014\u53c8\u53ef\u89c1\u5230\u5404\u5730\u6765\u671d\u62dc\u7684\u9999\u5ba2\u3002\u6842\u534e\u751f\u60f3\u8d77\u767d\u8863\u5c11\u5973\u6240\u8bf4\uff0c\u56fd\u4e2d \u660e\u5e74\u5c06\u5f00\u65e0\u906e\u5927\u4f1a\uff0c\u5230\u65f6\u5404\u56fd\u7684\u9ad8\u50e7\u6765\u7684\u5fc5\u5b9a\u66f4\u591a\u3002 \u3000\u3000\u5c3c\u6cca\u5c14\u7684\u666f\u7269\u8fd8\u6709\u4e00\u6837\u4eca\u6842\u534e\u751f\u611f\u89c9\u5174\u8da3\u7684\u4e43\u662f\u4f5b\u5854\uff0c\u642d\u4e0a\u56db\u9762\u663c\u7740\u56db\u5bf9\u773c\u775b\uff0c\u542c\u5c3c\u6cca \u5c14\u7684\u4f5b\u6559\u5f92\u89e3\u91ca\uff0c\u624d\u77e5\u9053\u8fd9\u53eb\u505a\u201c\u6167\u773c\u201d\uff0c\u662f\u8c61\u5fb5\u4f5b\u9640\u7684\u667a\u6167\u548c\u6148\u60b2\u7684\u3002\u6842\u534e\u751f\u7b2c\u4e00\u6b21\u89c1\u5230 \u8fd9\u79cd\u5f62\u5f0f\u7684\u4f5b\u5854\uff0c\u4e43\u662f\u5728\u9b54\u9b3c\u57ce\u4e2d\uff0c\u5f53\u65f6\u751a\u4e3a\u5947\u5f02\uff0c\u5230\u4e86\u5c3c\u6cca\u5c14\u540e\uff0c\u770b\u5f97\u591a\u4e86\uff0c\u770b\u60ef\u4e4b\u540e\uff0c \u786e\u662f\u611f\u5230\u773c\u775b\u663c\u5f97\u5f88\u7f8e\uff0c\u6148\u7965\u660e\u4eae\u800c\u6709\u9b45\u529b\u3002 \u3000\u3000\u8d70\u4e86\u5341\u591a\u5929\uff0c\u4f7f\u5230\u4e86\u5c3c\u6cca\u5c14\u7684\u4eac\u57ce\u2014\u2014\u52a0\u5fb7\u6ee1\u90fd\u3002\u52a0\u5fb7\u6ee1\u90fd\u4f4d\u7f6e\u5728\u5c71\u8c37\u4e4b\u4e2d\uff0c\u7fa4\u5c71\u73af \u62b1\uff0c\u5f62\u6210\u5929\u7136\u7684\u57ce\u5ed3\uff0c\u7ec8\u5e74\u79ef\u96ea\u7684\u559c\u9a6c\u62c9\u96c5\u5c71\u5cf0\uff0c\u50cf\u743c\u697c\u7389\u5b87\u4f3c\u7684\u9ad8\u8038\u5728\u9752\u5c71\u4e4b\u5916\u3002\u201c\u52a0 \u5fb7\u201d\u7684\u610f\u601d\u662f\u201c\u6728\u201d\uff0c\u201c\u6ee1\u90fd\u201d\u7684\u610f\u601d\u662f\u201c\u5e99\u201d\uff0c\u4e00\u8fdb\u4eac\u57ce\u5631\u76ee\u6240\u53ca\uff0c\u90fd\u662f\u5927\u5927\u5c0f\u5c0f\u7684\u5e99 \u5b87\uff0c\u5f88\u591a\u5e99\u5b87\u90fd\u662f\u6728\u7ed3\u6784\u7684\u3002\u5927\u7684\u5e99\u5b87\u96d5\u6881\u753b\u680b\uff0c\u5efa\u5b8f\u4f1f\uff0c\u800c\u4e14\u4e5f\u6709\u98de\u6a90\u79d1\u62f1\uff0c\u5f88\u50cf\u4e2d\u56fd\u591a \u5c42\u7684\u5bab\u6bbf\u5f0f\u5efa\uff0c\u800c\u53c8\u6709\u672c\u8eab\u7684\u7279\u70b9\u3002\u6842\u534e\u751f\u4e00\u8fdb\u4eba\u52a0\u5fb7\u6ee1\u90fd\u4fbf\u8877\u5fc3\u7684\u7231\u4e0a\u4e86\u8fd9\u4e2a\u57ce\u5e02\u3002 \u3000\u3000\u6842\u534e\u751f\u901b\u4e86\u51e0\u6761\u8857\u5e02\uff0c\u8179\u4e2d\u611f\u89c9\u6e34\uff0c\u4fbf\u4e0a\u4e00\u95f4\u9152\u697c\uff0c\u62e3\u4e00\u4e2a\u4e34\u7a97\u7684\u5ea7\u4f4d\u5750\u4e0b\uff0c\u8981\u4e86\u4e00\u58f6 \u201c\u545b\u545b\u201d\u9152\uff0c\u51e0\u6837\u83dc\u80b4\uff0c\u534a\u65a4\u5927\u9762\u3002\u8fd9\u65f6\u6b63\u662f\u5915\u9633\u897f\u843d\u7684\u65f6\u4efd\uff0c\u4ece\u7a97\u53e3\u671b\u51fa\u5916\u9762\uff0c\u6b63\u5bf9\u7740\u57ce \u897f\u7684\u745e\u626c\u5e03\u5c71\uff0c\u5c71\u4e0a\u6709\u4e00\u5ea7\u4e03\u5c42\u5b9d\u5854\uff0c\u5854\u7684\u4e0a\u534a\u90e8\u5168\u662f\u94a2\u5236\u7684\uff0c\u5854\u9876\u94fa\u4ee5\u91d1\u8584\uff0c\u5728\u5915\u9633\u4e0b\uff0c \u53d1\u51fa\u707f\u70c2\u7684\u95ea\u5149\uff0c\u751a\u662f\u597d\u770b\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u5e97\u4f19\u5c06\u9152\u83dc\u9001\u4e0a\uff0c\u5c3c\u6cca\u5c14\u4eba\u4ee5\u597d\u5ba2\u95fb\u540d\u4e8e\u4e16\uff0c\u5e97\u4f19\u77e5\u9053\u6842\u534e\u751f\u662f\u4ece\u4e2d\u56fd\u6765\u7684 \u8fdc\u5ba2\uff0c\u62db\u547c\u5f97\u5206\u5916\u6bb7\u52e4\uff0c\u9891\u9891\u95ee\u4ed6\u9152\u83dc\u662f\u5426\u9002\u53e3\u3002\u5c3c\u6cca\u5c14\u83dc\u6700\u559c\u6b22\u7528\u4e00\u79cd\u540d\u53eb\u201c\u8471\u6ce5\u201d\u7684\u914d \u6599\uff0c\u83dc\u80b4\u91cc\u90fd\u62cc\u6709\u201c\u8471\u6ce5\u201d\uff0c\u201c\u8471\u6ce5\u201d\u662f\u7528\u559c\u9a6c\u62c9\u96c5\u5c71\u9e93\u4e0a\u7279\u4ea7\u7684\u91ce\u8471\u3001\u9614\u53f6\u97ed\u548c\u4e00\u79cd\u7ea2\u849c \u6363\u70c2\u6210\u6ce5\uff0c\u505a\u6210\u4e00\u53ea\u53ea\u7684\u7403\uff0c\u5403\u7684\u65f6\u5019\uff0c\u4fbf\u634f\u4e0b\u4e86\u4e00\u5c0f\u5757\u6765\uff0c\u7528\u6cb9\u70b8\u4e00\u70b8\uff0c\u7136\u540e\u91ae\u5728\u83dc\u80b4 \u91cc\uff0c\u6c64\u91cc\u6216\u9762\u70b9\u91cc\uff0c\u6842\u534e\u751f\u5403\u7740\u62cc\u6709\u201c\u8471\u6ce5\u201d\u7684\u83dc\u80b4\uff0c\u89c9\u5f97\u975e\u5e38\u4e4b\u9999\uff0c\u5927\u4e3a\u8d5e\u8d4f\uff0c\u5e97\u4f19\u7b11 \u9053\uff1a\u201c\u6709\u4e9b\u5916\u5ba2\u5403\u4e0d\u60ef\uff0c\u6709\u4e9b\u4e00\u5403\u5c31\u4e0a\u4e86\u763e\uff0c\u5b81\u613f\u5728\u5c3c\u6cca\u5c14\u5b89\u5bb6\u843d\u6237\uff0c\u4e0d\u80af\u8d70\u4e86\u3002\u201d\u6842\u534e\u751f \u7b11\u9053\uff1a\u201c\u770b\u6765\u6211\u4e5f\u8981\u5728\u4f60\u4eec\u8fd9\u513f\u5b89\u5bb6\u843d\u6237\u4e86\u3002\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u9645\uff0c\u6765\u4e86\u4e00\u4e2a\u5ba2\u4eba\uff0c\u662f\u4e2a\u4e94\u5341\u591a\u5c81\u7684\u8001\u8005\uff0c\u80cc\u7740\u4e00\u4e2a\u9752\u8272\u5e03\u56ca\uff0c\u8863\u886b\u7d20\uff0c\u8fd9\u4eba\u4e00 \u4e0a\u9152\u697c\uff0c\u5e97\u4f19\u548c\u6b63\u5728\u5403\u996d\u7684\u5ba2\u4eba\u90fd\u7eb7\u7eb7\u8d77\u7acb\uff0c\u5411\u4ed6\u8868\u793a\u656c\u610f\u3002\u6842\u534e\u751f\u6697\u81ea\u731c\u6d4b\u4ed6\u7684\u8eab\u4efd\uff0c\u53ea \u542c\u638c\u67dc\u7684\u5411\u4ed6\u95ee\u9053\uff1a\u201c\u5df4\u52d2\u5927\u533b\u751f\uff0c\u4eca\u5929\u7ed9\u8c01\u770b\u75c5\u6765\u4e86\uff1f\u201d \u3000\u3000\u8001\u8005\u5fae\u7b11\u9053\uff1a\u201c\u4eca\u5929\u78b0\u5230\u4e86\u4e00\u5b97\u7591\u96be\u6742\u75c7\uff0c\u628a\u6211\u96be\u4f4f\u4e86\u3002\u6211\u533b\u4e0d\u597d\u7684\u75db\uff0c\u4e0d\u597d\u610f\u601d\u5bf9\u4f60 \u4eec\u8bf4\u3002\u201d\u4f17\u4eba\u7eb7\u7eb7\u7b11\u9053\uff1a\u201c\u4f60\u8001\u5148\u751f\u771f\u4f1a\u8bf4\u7b11\uff0c\u90a3\u6709\u4f60\u533b\u4e0d\u597d\u7684\u75db\uff1f\u201d\u201c\u9664\u975e\u662f\u7f57\u5239\u6ce8\u5b9a\u8981 \u52fe\u4ed6\u7684\u9b42\u9b44\uff0c\u54c8\uff0c\u53ea\u6015\u7eb5\u7136\u5982\u6b64\uff0c\u4f60\u4e5f\u6709\u672c\u4e8b\u4ece\u7f57\u5239\u624b\u4e2d\u5c06\u4ed6\u7684\u6027\u547d\u593a\u8fc7\u6765\u3002\u201d\u542c\u4ed6\u4eec\u7684\u53e3 \u6c14\uff0c\u8fd9\u4f4d\u5df4\u52d2\u8001\u5148\u751f\u5927\u7ea6\u662f\u4ed6\u4eec\u56fd\u4e2d\u7684\u795e\u533b\u3002 \u3000\u3000\u5df4\u52d2\u7684\u773c\u5149\u5411\u6842\u534e\u751f\u671b\u6765\uff0c\u9762\u4e0a\u5fae\u5fae\u9732\u51fa\u8be7\u5f02\u4e4b\u8272\uff0c\u6842\u534e\u751f\u5411\u4ed6\u62f1\u624b\u62db\u547c\uff0c\u5df4\u52d2\u9053\uff1a \u201c\u8bb8\u591a\u5e74\u6ca1\u6709\u89c1\u5230\u4ece\u4e2d\u56fd\u6765\u7684\u5ba2\u4eba\u4e86\uff0c\u5e78\u4f1a\uff0c\u5e78\u4f1a\uff01\u201d\u6842\u534e\u751f\u4fbf\u8bf7\u4ed6\u559d\u9152\uff0c\u5df4\u52d2\u751a\u662f\u8c6a\u723d\uff0c \u53eb\u5e97\u4f19\u6dfb\u4e86\u4e00\u53cc\u7b77\u5b50\uff0c\u518d\u52a0\u51e0\u5f0f\u5c0f\u83dc\uff0c\u4fbf\u5728\u6842\u534e\u751f\u4fa7\u9762\u5750\u4e0b\u3002 \u3000\u3000\u4f73\u534e\u751f\u9053\uff1a\u201c\u8001\u5148\u751f\u533b\u672f\u9ad8\u660e\uff0c\u5c0f\u53ef\u597d\u751f\u4f69\u670d\uff01\u201d\u5df4\u52d2\u9053\uff1a\u201c\u4f60\u542c\u4ed6\u4eec\u80e1\u8bf4\uff0c\u6211\u4e0d\u8fc7\u61c2 \u5f97\u51e0\u5473\u836f\u6027\u800c\u5df2\u3002\u8bf4\u5230\u533b\u672f\uff0c\u8fd8\u662f\u4f60\u4eec\u4e2d\u56fd\u4eba\u6700\u4e3a\u9ad8\u660e\u3002\u8bf4\u8d77\u6765\u6211\u5bf9\u533b\u5b66\u7565\u79f0\u76ae\u6bdb\uff0c\u8fd8\u662f\u62dc \u4f60\u4eec\u4e2d\u56fd\u4eba\u4e4b\u8d50\u3002\u201d\u6842\u534e\u751f\u5947\u9053\uff1a\u201c\u8001\u5148\u751f\u5230\u8fc7\u4e2d\u56fd\u5417\uff1f\u201d\u5df4\u52d2\u9053\uff1a\u201c\u6211\u867d\u7136\u6ca1\u5230\u8fc7\u4e2d\u56fd\uff0c \u5374\u7a0d\u89e3\u6c49\u6587\u3002\u6211\u6700\u521d\u5b66\u533b\uff0c\u4fbf\u662f\u4ece\u4f60\u4eec\u7684\u8425\u5b97\u5148\u54f2\u674e\u65f6\u73cd\u7684\u300a\u672c\u8349\u7eb2\u76ee\u300b\u5f00\u59cb\u7684\u3002\u73b0\u5728\u53c8\u6b63 \u7814\u8bfb\u4ed6\u7684\u300a\u5947\u7ecf\u4eba\u8109\u8003\u300b\u548c\u300a\u6fd2\u6e56\u8109\u5b66\u300b\u7b49\u4e66\u3002\u6211\u6b63\u60f3\u628a\u4ed6\u8bd1\u6210\u6211\u56fd\u6587\u5b57\u3002\u542c\u8bf4\u4f60\u4eec\u8fd1\u4ee3\u8fd8 \u6709\u4e00\u4f4d\u795e\u533b\u5085\u9752\u4e3b\u5148\u751f\uff0c\u66fe\u5230\u8fc7\u5929\u5c71\u5357\u5317\u548c\u897f\u85cf\uff0c\u6211\u542c\u897f\u85cf\u6765\u7684\u9999\u5ba2\u8bf4\u8fc7\uff0c\u4ed6\u4eec\u7684\u7956\u5148\u66fe\u5f97 \u4f20\u9752\u4e3b\u770b\u8fc7\u75c5\uff0c\u4ed6\u4eec\u8fd8\u4fdd\u5b58\u6709\u65b9\u5b50\uff0c\u786e\u662f\u9ad8\u660e\u4e4b\u6781\uff0c\u53ea\u53ef\u60dc\u4ed6\u7684\u533b\u6848\u8fd8\u6ca1\u6709\u4e66\u7c4d\u4f20\u64ad\u3002\u4e5f\u8bb8 \u6709\u4e86\uff0c\u53ef\u662f\u6211\u4eec\u56fd\u4e2d\uff0c\u8fd8\u672a\u89c1\u5230\u3002\u201d \u3000\u3000\u6842\u534e\u751f\u542c\u4e86\uff0c\u597d\u751f\u94a6\u656c\uff0c\u53ea\u662f\u4ed6\u5bf9\u533b\u5b66\u5e76\u4e0d\u5185\u884c\uff0c\u65e0\u6cd5\u6df1\u8c08\uff0c\u4fbf\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u5982\u6b64\u8bf4 \u6765\uff0c\u54b1\u4eec\u4e24\u56fd\u867d\u9694\u7740\u5927\u5c71\uff0c\u5374\u4e5f\u5e76\u4e0d\u964c\u751f\u5462\uff01\u201d\u5df4\u52d2\u9053\uff1a\u201c\u5c82\u5e76\u4e0d\u964c\u751f\uff0c\u5c31\u8bf4\u662f\u4e16\u4ea4\u4e5f\u672a\u5c1d \u4e0d\u53ef\u3002\u6211\u4eec\u56fd\u4e2d\u7684\u8bb8\u591a\u4f20\u8bf4\u90fd\u662f\u4e0e\u4f60\u4eec\u4e2d\u56fd\u6709\u5173\u7684\u3002\u201d\u6307\u4e00\u6307\u7a97\u5916\u7684\u5b9d\u5854\uff0c\u7b11\u9053\uff1a\u201c\u636e\u8bf4\u6211 \u4eec\u7684\u4eac\u57ce\uff0c\u4e5f\u662f\u4f60\u4eec\u4e2d\u56fd\u6765\u7684\u6cd5\u5e08\u6240\u5f00\u8f9f\u7684\u3002\u201d \u3000\u3000\u6842\u534e\u751f\u751a\u611f\u5174\u8da3\uff0c\u7b11\u9053\uff1a\u201c\u6709\u8fd9\u6837\u7684\u4f20\u8bf4\u5417\uff1f\u201d\u5df4\u52d2\u6307\u7740\u745e\u626c\u5e03\u5c71\u8bf4\u9053\uff1a\u201c\u4f60\u77e5\u9053\u745e\u626c \u5e03\u8fd9\u540d\u5b57\u7684\u610f\u601d\u5417\uff1f\u201d\u6842\u534e\u751f\u5bf9\u5c3c\u6cca\u5c14\u6587\u5b57\u7684\u77e5\u8bc6\u6709\u9650\uff0c\u9053\uff1a\u201c\u8bf7\u8001\u4e08\u8be6\u89e3\u3002\u201d\u5df4\u52d2\u9053\uff1a \u201c\u8fd9\u4e2a\u5b57\u7684\u5b57\u4e49\u662f\u201c\u81ea\u4f53\u653e\u5149\u201d\u7684\u610f\u601d\u3002\u636e\u8bf4\u4e45\u8fdc\u4e45\u8fdc\u4ee5\u524d\uff0c\u6709\u4e00\u4f4d\u6bd7\u5a46\u6237\u4f5b\uff0c\u66fe\u6765\u5230\u8fd9\u91cc \u7684\u6e56\u4e0a\uff0c\u770b\u5230\u5404\u6709\u4e2d\u8bb8\u591a\u82b1\u8349\uff0c\u72ec\u6ca1\u6709\u83b2\u82b1\uff0c\u4ed6\u4f7f\u6295\u4e0b\u53bb\u4e00\u652f\u7a83\u6839\u3002\u9884\u8a00\u5c06\u6765\u4f1a\u957f\u51fa\u653e\u5149\u7684 \u83b2\u82b1\uff0c\u6e56\u6c34\u5c06\u53d8\u6210\u5bcc\u9976\u7684\u56fd\u571f\u3002\u90a3\u4e2a\u65f6\u5019\uff0c\u8fd9\u91cc\u8fd8\u662f\u4e00\u7247\u6c6a\u6d0b\u7684\u78a7\u6e56\uff0c\u745e\u626c\u5e03\u5c71\u4e5f\u6d78\u5728\u6c34 \u91cc\uff0c\u4ec5\u4ec5\u9732\u51fa\u5cf0\u9876\u3002\u8fc7\u4e86\u8bb8\u591a\u5e74\u4ee3\uff0c\u6bd7\u5a46\u6237\u4f5b\u6295\u4e0b\u7684\u85c9\u6839\u679c\u7136\u5f00\u51fa\u83b2\u82b1\uff0c\u745e\u626c\u5e03\u5c71\u4e5f\u653e\u51fa\u5149 \u8f89\uff0c\u5982\u540c\u83b2\u82b1\u4e00\u6837\u3002\u5c31\u5728\u8fd9\u4e2a\u65f6\u5019\uff0c\u4e2d\u56fd\u4e94\u53f0\u5c71\u7684\u6587\u6b8a\u83e9\u8428\u6765\u5230\u8fd9\u91cc\uff0c\u4ed6\u7ed5\u6e56\u73af\u884c\u4e00\u5468\uff0c\u53d1 \u89c9\u6e56\u7684\u4f4d\u7f6e\u5f88\u9ad8\uff0c\u6709\u4e00\u5904\u5c71\u77f3\u58c1\u571f\uff0c\u4ed6\u8bf4\uff1a\u5982\u679c\u628a\u6e56\u6c34\u6d41\u51fa\u53bb\uff0c\u8fd9\u88f9\u5c06\u6210\u4e3a\u4e00\u7247\u6c83\u571f\u3002\u4ed6\u62ff \u8d77\u4e86\u4e00\u628a\u65b0\u6708\u5f62\u7684\u5b9d\u5251\u628a\u5357\u9762\u7684\u5c71\u5934\u5288\u5f00\u4e86\u4e00\u4e2a\u7f3a\u53e3\uff0c\u5c06\u6e56\u6c34\u6392\u51fa\uff0c\u73b0\u51fa\u9646\u5730\uff0c\u8ba9\u968f\u6765\u7684\u5f92 \u4f17\u5b9a\u5c45\u4e0b\u6765"\u5176\u4e2d\u4e00\u4e2a\u540d\u53eb\u201c\u6cd5\u6301\u201d\u7684\u505a\u4e86\u56fd\u738b\uff0c\u4ed6\u4eff\u7167\u4e2d\u56fd\u7684\u6837\u5f0f\u5174\u5efa\u4e86\u52a0\u5fb7\u6ee1\u90fd\u3002\u81f3\u4eca \u90a3\u4e2a\u5ce1\u53e3\u7684\u540d\u5b57\u4ecd\u53eb\u505a\u201c\u4f55\u5fb7\u74e6\u201d\uff0c\u610f\u601d\u5c31\u662f\u201c\u5251\u5288\u53e3\u201d\uff0c\u6e56\u6c34\u6392\u51fa\u7684\u6c34\u9053\u6210\u4e86\u4e00\u6761\u6cb3\uff0c\u540d \u53eb\u5df4\u9a6c\u63d0\u6cb3\uff0c\u5343\u5e74\u4e07\u8f7d\u5730\u704c\u6e89\u7740\u8fd9\u4e2a\u5c71\u8c37\u5185\u80a5\u6c83\u7684\u7530\u5730\uff0c\u6211\u4eec\u5c3c\u6cca\u5c14\u4eba\u7684\u5fc3\u76ee\u4e2d\u3002\u5b83\u662f\u4e00\u6761 \u5764\u5723\u7684\u6cb3\u3002\u201d\uff08\u7fbd\u751f\u6309\uff1a\u5728\u82e5\u5e72\u6709\u5173\u5c3c\u6cca\u5c14\u5386\u53f2\u7684\u4e66\u7c4d\u4e2d\uff0c\u53d9\u8ff0\u5230\u8fd9\u6bb5\u795e\u8bdd\u65f6\uff0c\u5c06\u4ece\u4e2d\u56fd\u6765 \u7684\u201c\u5723\u4eba\u201d\u8bd1\u4e3a\u201c\u5b5f\u5b88\u793c\u201d\uff0c\u5176\u5b9e\u5e94\u662f\u201c\u6587\u6b8a\u83e9\u8428\u201d\u3002\u4e2d\u56fd\u73b0\u5728\u7684\u4f5b\u6559\u534f\u4f1a\u526f\u4f1a\u957f\u8d75\u521d\u56db\u5e74 \u524d\u5230\u5c3c\u6cca\u5c14\u8bbf\u95ee\u65f6\uff0c\u5bf9\u6b64\u6bb5\u795e\u8bdd\uff0c\u66fe\u6709\u4e13\u6587\u8c08\u53ca\u3002\uff09 \u3000\u3000\u6842\u534e\u751f\u542c\u4e86\u8fd9\u6bb5\u795e\u8bdd\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u795e\u8bdd\u867d\u4e0d\u8db3\u4fe1\uff0c\u4f46\u4ea6\u53ef\u89c1\u5230\u5c3c\u6cca\u5c14\u4eba\u5bf9\u4e2d\u56fd\u662f\u4f55\u7b49 \u5c0a\u5d07\uff0c\u8fde\u4ed6\u4eec\u7684\u4eac\u57ce\u4e5f\u8ba4\u4e3a\u662f\u4ece\u4e2d\u56fd\u6765\u7684\u83e9\u8428\u5f00\u8f9f\u7684\u3002\u800c\u4eac\u57ce\u4e2d\u7684\u98ce\u7269\uff0c\u786e\u5b9e\u4e5f\u6709\u5f88\u591a\u4e0e\u4e2d \u56fd\u76f8\u540c\u3002\u201d\u5bf9\u52a0\u5fb7\u6ee1\u90fd\uff0c\u76ca\u589e\u4eb2\u5207\u4e4b\u611f\u3002 \u3000\u3000\u4e24\u4eba\u8c08\u5f97\u5f88\u662f\u6295\u673a\uff0c\u6842\u534e\u751f\u60f3\u8d77\u4e00\u4e8b\uff0c\u95ee\u9053\uff1a\u201c\u8d35\u56fd\u56fd\u738b\u6709\u51e0\u4f4d\u5b50\u5973\uff1f\u201d\u5df4\u52d2\u9053\uff1a\u201d\u6709 \u4e00\u4f4d\u516c\u4e3b\u3002\u201d\u6842\u534e\u751f\u5fc3\u9053\uff1a\u201c\u539f\u6765\u9b54\u9b3c\u57ce\u4e2d\u7684\u90a3\u4e2a\u5c3c\u6cca\u5c14\u738b\u5b50\u5e76\u975e\u592a\u5b50\u3002\u201d\u5df4\u52d2\u5fae\u7b11\u9053\uff1a \u201c\u4f60\u5927\u7ea6\u4e5f\u542c\u5230\u5173\u4e8e\u6211\u4eec\u516c\u4e3b\u7684\u62db\u4eb2\u4e8b\u60c5\u4e86\uff1f\u4ee5\u4f60\u7684\u624d\u8c8c\u6b66\u529f\uff0c\u6b63\u53ef\u4e00\u8bd5\u3002\u201d\u6842\u534e\u751f\u6014\u4e86\u4e00 \u6014\uff0c\u60f3\u9053\uff1a\u201c\u4ed6\u600e\u4e48\u77e5\u9053\u6211\u8eab\u6709\u6b66\u529f\uff1f\u201d\u7b11\u9053\uff1a\u201c\u8fd9\u4e8b\u60c5\u6211\u8fde\u60f3\u4e5f\u4e0d\u6562\u4e00\u60f3\u3002\u6765\u6c42\u5a5a\u7684\u5f88\u591a \u5417\uff1f\u201d\u5df4\u52d2\u9053\uff1a\u201c\u4e00\u5e74\u524d\u521a\u5f00\u59cb\u7684\u65f6\u5019\uff0c\u51e0\u4e4e\u6bcf\u5929\u90fd\u6709\u4eba\u6765\u5e94\u8bd5\uff0c\u540e\u6765\u8d8a\u6765\u8d8a\u5c11\uff0c\u73b0\u5728\u5df2\u6709 \u4e09\u4e2a\u6708\u4e0d\u542c\u89c1\u8bf4\u6709\u4eba\u6765\u4e86\u3002\u201d\u6842\u534e\u751f\u5fc3\u4e2d\u4e00\u52a8\uff0c\u9053\uff1a\u201c\u8fd9\u662f\u4f55\u6545\uff1f\u201d\u5df4\u52d2\u9053\uff1a\u201c\u6765\u6c42\u5a5a\u7684\u4eba \u8fde\u5bab\u5973\u4e5f\u6253\u4e0d\u8fc7\uff0c\u538b\u6839\u513f\u5c31\u6ca1\u89c1\u7740\u516c\u4e3b\uff0c\u5f04\u5230\u540e\u6765\uff0c\u8bb8\u591a\u6c42\u5a5a\u8005\u90fd\u77e5\u96be\u800c\u9000\u4e86\u3002\u201d \u3000\u3000\u6842\u534e\u751f\u7b11\u9053\uff1a\u201c\u90a3\u4e48\u56fd\u738b\u627e\u4e0d\u5230\u5973\u5a7f\uff0c\u5c82\u4e0d\u662f\u5f88\u5fc3\u6025\u4e86\uff1f\u201d\u5df4\u52d2\u9053\uff1a\u201c\u662f\u554a\uff0c\u662f\u5f88\u5fc3 \u7126\u3002\u6240\u4ee5\u2026\u2026\u201d\u5ffd\u5730\u6b32\u8a00\u53c8\u6b62\uff0c\u6842\u534e\u751f\u6709\u70b9\u5947\u602a\uff0c\u5ffd\u89c9\u697c\u4e2d\u6c14\u6c1b\u6709\u5f02\uff0c\u62ac\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u4e00\u4e2a \u5e26\u5200\u6b66\u58eb\u8d70\u4e0a\u697c\u6765\uff0c\u638c\u67dc\u8fde\u5fd9\u62db\u547c\uff0c\u90a3\u6b66\u58eb\u6446\u6446\u624b\u9053\uff1a\u201c\u6211\u4e0d\u559d\u9152\u3002\u201d\u8ff3\u81ea\u8d70\u5230\u6842\u534e\u751f\u8fd9\u684c \u5b50\u524d\u9762\uff0c\u540c\u5df4\u52d2\u97a0\u4e86\u4e2a\u8eac\uff0c\u606d\u606d\u656c\u656c\u7684\u906e\u4e0a\u4e00\u4e2a\u94f6\u76d2\uff0c\u4e00\u8a00\u4e0d\u53d1\uff0c\u4fbf\u5373\u8d70\u4e86\u3002\u6842\u534e\u751f\u83ab\u540d\u5176 \u5999\uff0c\u4f46\u89c1\u5df4\u52d2\u6367\u7740\u94f6\u76d2\uff0c\u624b\u6307\u5fae\u5fae\u53d1\u6296\uff0c\u597d\u50cf\u6709\u4ec0\u4e48\u7978\u4e8b\u4e34\u5934\u4e00\u6837\u3002\u6842\u534e\u751f\u56de\u9053\uff1a\u201c\u8001\u4e08\uff0c \u4ec0\u4e48\u4e8b\u60c5\uff1f\u53ef\u662f\u78b0\u5230\u4ec0\u4e48\u4e3a\u96be\u4e4b\u4e8b\uff1f\u201d \u3000\u3000\u5df4\u52d2\u9053\uff1a\u201c\u591a\u8c22\u76f8\u516c\u5173\u6ce8\uff0c\u522b\u65e0\u4ed6\u4e8b\uff0c\u53ea\u662f\u5929\u8272\u4e0d\u65e9\uff0c\u6211\u5e94\u8be5\u56de\u5bb6\u5566\u3002\u201d\u6842\u534e\u751f\u751a\u662f\u6000 \u7591\uff0c\u9053\uff1a\u201c\u90a3\u4e48\u6211\u51e0\u65f6\u518d\u89c1\u8001\u4e08\uff1f\u201d\u5df4\u52d2\u9053\uff1a\u201c\u82e5\u662f\u8fd8\u6709\u7f18\u4efd\u7684\u8bdd\uff0c\u4e09\u65e5\u4e4b\u540e\uff0c\u6211\u518d\u5728\u8fd9\u91cc \u5019\u6559\u4fbf\u662f\u3002\u201d\u6842\u534e\u751f\u4f55\u7b49\u806a\u660e\uff0c\u542c\u4ed6\u8a00\u4e0b\u4e4b\u610f\uff0c\u5b9a\u7136\u6709\u83ab\u6d4b\u4e4b\u7978\uff0c\u6545\u6b64\u8fde\u4ed6\u4e5f\u4e0d\u81ea\u77e5\uff0c\u4e09\u65e5 \u4e4b\u540e\uff0c\u662f\u5426\u5c1a\u6709\u7f18\u76f8\u89c1\u3002\u5728\u9152\u697c\u4e0a\u4e0d\u4fbf\u70b9\u7834\uff0c\u4fbf\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u4e09\u65e5\u4e4b\u540e\uff0c\u6211\u5728\u8fd9\u91cc\u6d01\u69fd \u5019\u6559\u3002\u201d\u7b49\u5230\u5df4\u52d2\u8d70\u540e\uff0c\u4ed6\u5374\u7acb\u5373\u7ed3\u4e86\u9152\u94b1\uff0c\u6084\u6084\u8ddf\u5728\u5df4\u52d2\u540e\u9762\u3002 \u3000\u3000\u534e\u706f\u521d\u4e0a\uff0c\u8857\u9053\u884c\u4eba\u7199\u6765\u6518\u5f80\uff0c\u5df4\u52d2\u4f3c\u4e4e\u6beb\u672a\u53d1\u89c9\u4f73\u534e\u751f\u8ddf\u8e2a\u4ed6\uff0c\u7a7f\u8fc7\u4e86\u597d\u51e0\u6761\u5c0f\u5df7\uff0c \u5230\u4e86\u57ce\u897f\u7684\u4e00\u6761\u51b7\u50fb\u5c0f\u5df7\uff0c\u6709\u4e00\u5ea7\u4e24\u9020\u7684\u5e73\u623f\uff0c\u5df4\u52d2\u56de\u5230\u5bb6\u4e2d\uff0c\u201c\u788e\u201d\u7684\u4e00\u58f0\uff0c\u4fbf\u628a\u5927\u95e8\u5173 \u4e0a\u4e86\u3002 \u3000\u3000\u6842\u534e\u751f\u5f85\u4e86\u4e00\u4f1a\uff0c\u77a7\u7740\u56db\u4e0b\u65e0\u4eba\uff0c\u7acb\u523b\u65bd\u5c55\u8f7b\u529f\uff0c\u8df3\u4e0a\u5c4b\u9876\uff0c\u6084\u6084\u4ece\u4ed6\u5c4b\u540e\u6e9c\u4e0b\uff0c\u53ea\u542c \u5f97\u5df4\u52c3\u5728\u8f6c\u5b50\u91cc\u5509\u58f0\u53f9\u6c14\u3002\u6842\u534e\u751f\u8d70\u8fd1\u53bb\u5077\u5077\u5f20\u671b\uff0c\u4f46\u89c1\u5df4\u52d2\u65e9\u5df2\u6253\u5f00\u4e86\u6b66\u58eb\u8fed\u7ed9\u4ed6\u7684\u90a3\u4e2a \u94f6\u76d2\uff0c\u5c06\u76d2\u4e2d\u4e4b\u7269\u4e00\u4ef6\u4e00\u4ef6\u6446\u5728\u684c\u4e0a\uff0c\u6709\u73cd\u73e0\uff0c\u6709\u7fe1\u7fe0\uff0c\u8fd8\u6709\u51e0\u5757\u91d1\u7efd\uff0c\u6842\u534e\u751f\u5927\u5947\uff1a\u201c\u8fd9 \u6b66\u58eb\u8fed\u7ed9\u4ed6\u8fd9\u4efd\u793c\u7269\u5012\u4e70\u4e0d\u5c11\u554a\uff01\u4ed6\u4e3a\u4ec0\u4e48\u5509\u58f0\u53f9\u6c14\uff1f\u201d\u5f85\u4e86\u7247\u523b\uff0c\u53ea\u89c1\u4ed6\u53c8\u5728\u76d2\u5b50\u91cc\u62ff\u8d77 \u4e00\u5f20\u8bf7\u5e16\uff0c\u6367\u5728\u624b\u91cc\u5446\u5446\u53d1\u6123\u3002 \u3000\u3000\u6842\u534e\u751f\u8d70\u8fdb\u53bb\u9053\uff1a\u201c\u8001\u4e08\u4f55\u4e8b\u62c5\u5fc3\uff0c\u5c0f\u53ef\u613f\u4e3a\u8001\u4e08\u5206\u5fe7\u3002\u201d\u5df4\u52d2\u5413\u4e86\u4e00\u8df3\uff0c\u770b\u89c1\u662f\u6842\u534e \u751f\uff0c\u53f9\u6c14\u8bf4\u9053\uff1a\u201c\u8001\u5f1f\u53e4\u9053\u70ed\u80a0\uff0c\u771f\u771f\u53ef\u4f69\uff01\u8fd9\u4e00\u4ef6\u4e8b\uff0c\u8001\u5f1f\u8fd8\u662f\u4e0d\u8981\u7406\u4f1a\u7684\u597d\u3002\u201d \u3000\u3000\u6842\u534e\u751f\u9053\uff1a\u201c\u6211\u4e0e\u8001\u4e08\u4e00\u89c1\u5982\u6545\uff0c\u8001\u4e08\u65e2\u6709\u4e3a\u96be\u4e4b\u4e8b\uff0c\u6211\u662f\u975e\u7406\u4e0d\u53ef\u3002\u201d\u5df4\u52d2\u9053\uff1a\u201c\u6211 \u77e5\u9053\u8001\u5f1f\u6b66\u529f\u7edd\u4e16\u2026\u2026\u55ef\uff0c\u4f60\u4e0d\u5fc5\u60ca\u5947\uff0c\u6211\u867d\u4e0d\u8bc6\u6b66\u529f\uff0c\u4f46\u7a0d\u901a\u533b\u7406\uff0c\u6211\u770b\u4f60\u76ee\u8574\u795e\u5149\uff0c\u5728 \u9152\u697c\u4e0a\u6211\u6545\u610f\u78b0\u4e86\u4f60\u624b\u8155\u4e00\u4e0b\uff0c\u4f60\u7684\u8109\u818a\u97f3\u5b8f\u800c\u6e05\uff0c\u4e00\u89e6\u5c31\u77e5\u662f\u975e\u5e38\u4e4b\u4eba\u3002\u201d\u6842\u534e\u751f\u5927\u4e3a\u4f69 \u670d\uff0c\u5fc3\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u4eba\u4eba\u79f0\u4ed6\u795e\u533b\uff0c\u679c\u7136\u533b\u7406\u901a\u7384\uff0c\u4e0d\u53ef\u601d\u8bae\u3002\u201d\u5df4\u52d2\u7eed\u9053\uff1a\u201c\u8001\u5f1f\u867d\u662f\u6b66 \u529f\u7edd\u4e16\uff0c\u8fd9\u4e8b\u60c5\u5374\u4e0d\u662f\u6b66\u529f\u6240\u80fd\u89e3\u51b3\uff0c\u6211\u4e5f\u4e0d\u613f\u8001\u5f1f\u4ee5\u4e00\u4e2a\u5f02\u56fd\u4e4b\u4eba\uff0c\u800c\u4e0e\u6211\u672c\u56fd\u7684\u5fa1\u6797\u519b \u4f5c\u5bf9\u3002\u201d \u3000\u3000\u6842\u534e\u751f\u9053\uff1a\u201c\u600e\u4e48\uff1f\u8001\u4e08\u884c\u533b\u6d4e\u4e16\uff0c\u5374\u600e\u7684\u4f1a\u5f97\u7f6a\u4e86\u5fa1\u6797\u519b\uff1f\u201d\u5df4\u52d2\u9053\uff1a\u201c\u8fde\u6211\u4e5f\u83ab\u540d \u5176\u5999\u5440\uff0c\u5982\u4eca\u662f\u7978\u662f\u798f\uff0c\u6211\u4ea6\u96be\u4ee5\u731c\u5ea6\u3002\u201d\u6842\u534e\u751f\u9053\uff1a\u201c\u5c0f\u53ef\u8c2c\u6258\u77e5\u5df2\uff0c\u6562\u8bf7\u8be6\u544a\u3002\u201d\u5df4\u52d2 \u9053\uff1a\u201c\u9002\u624d\u90a3\u4f4d\u6b66\u58eb\u9001\u6765\u7684\u8bf7\u5e16\uff0c\u4e43\u662f\u5fa1\u6797\u519b\u603b\u7763\u5927\u4eba\u7684\u8bf7\u5e16\uff0c\u8981\u6211\u4eca\u665a\u4e09\u66f4\uff0c\u5230\u4ed6\u522b\u5885\u76f8 \u89c1\u3002\u8fd9\u4e8b\u4e0d\u8bb8\u6211\u5bf9\u4efb\u4f55\u4eba\u8bf4\u3002\u201d \u3000\u3000\u6842\u534e\u751f\u5927\u7b11\u9053\uff1a\u201c\u4e5f\u8bb8\u4ed6\u6709\u4ec0\u4e48\u89c1\u4e0d\u5f97\u4eba\u7684\u75c5\uff0c\u8bf7\u4f60\u53bb\u533b\uff0c\u201d\u5df4\u52d2\u6447\u5934\u9053\uff1a\u201c\u4e0d\u4f1a\uff0c\u4e0d \u4f1a\u3002\u55ef\uff0c\u6211\u800c\u4eca\u60f3\u8d77\u6765\u4e86\uff0c\u6211\u4eca\u65e5\u51fa\u5bab\u4e4b\u65f6\uff0c\u9690\u7ea6\u89c1\u5230\u603b\u7763\u5927\u4eba\u4e5f\u5728\u5916\u9762\u5f20\u671b\u3002\u201d\u6842\u534e\u751f \u9053\uff1a\u201c\u554a\uff0c\u539f\u6765\u4f60\u4eca\u65e5\u662f\u8fdb\u7687\u5bab\u770b\u75c5\u4e48\uff1f\u201d\u5df4\u52d2\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u6211\u672c\u4e0d\u5e94\u8be5\u544a\u8bc9\u522b\u4eba\uff0c\u4f46 \u8001\u5f1f\u662f\u4e0a\u56fd\u9ad8\u8d24\uff0c\u96be\u5f97\u53c8\u8fd9\u6837\u4e00\u89c1\u5982\u6545\uff0c\u6211\u5c31\u5bf9\u8001\u5f1f\u8bf4\u4e86\u5427\u3002\u6211\u4eca\u65e5\u5728\u9152\u697c\u4e0a\u4e4b\u8a00\u4e00\u70b9\u4e0d \u6687\uff0c\u6211\u786e\u662f\u9047\u5230\u4e86\u751f\u5e73\u7b2c\u4e00\u5b97\u96be\u533b\u7684\u75c5\u75c7\u3002\u75c5\u4eba\u5c31\u662f\u6211\u4eec\u7687\u4e0a\u3002\u6211\u8bca\u4ed6\u8109\u8c61\uff0c\u597d\u50cf\u662f\u4e2d\u4e86\u4e00 \u79cd\u6162\u6027\u7684\u6bd2\u836f\uff0c\u6bd2\u6027\u4e4b\u602a\uff0c\u524d\u6240\u672a\u89c1\u3002\u53ef\u4ee5\u4ee4\u4ed6\u7cbe\u795e\u75b2\u707f\uff0c\u4e0d\u80fd\u7528\u8111\u3002\u82e5\u7136\u65e0\u6cd5\u89e3\u9664\u6bd2\u6027\uff0c \u4e09\u6708\u4e4b\u540e\uff0c\u5c31\u4f1a\u6a35\u60bb\u800c\u6b7b\u3002\u522b\u7684\u533b\u751f\uff0c\u7edd\u5bf9\u770b\u4e0d\u51fa\u4ed6\u7684\u6b7b\u56e0\uff01\u201d \u3000\u3000\u6842\u534e\u751f\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u8c01\u6562\u8fd9\u6837\u5927\u80c6\uff0c\u6697\u4e2d\u6bd2\u5bb3\u56fd\u738b\uff1f\u201d\u5df4\u52d2\u9053\uff1a\u201c\u662f\u5440\uff0c\u5bab\u4e2d\u9632 \u536b\u68ee\u4e25\uff0c\u5916\u4eba\u7edd\u96be\u6df7\u5f97\u8fdb\u53bb\uff0c\u82e5\u662f\u6211\u8bca\u65ad\u4e0d\u9519\u7684\u8bdd\uff0c\u8fd9\u4e0b\u6bd2\u7684\u4eba\uff0c\u53ea\u6015\u5c31\u662f\u56fd\u738b\u4eb2\u8fd1\u7684 \u4eba\u3002\u201d\u6842\u534e\u751f\u6c89\u541f\u534a\u664c\uff0c\u8bf4\u9053\uff1a\u201c\u83ab\u975e\u5c31\u662f\u90a3\u4f4d\u5fa1\u6797\u519b\u603b\u7ba1\uff1f\u201d\u5df4\u52d2\u9053\uff1a\u201c\u6211\u63a5\u5230\u8fd9\u4e2a\u94f6\u76d2 \u4e4b\u65f6\uff0c\u6700\u521d\u4e5f\u662f\u8fd9\u6837\u7684\u60f3\uff0c\u4e0d\u8fc7\uff0c\u5fa1\u6797\u519b\u603b\u7763\uff0c\u867d\u7136\u65f6\u5e38\u968f\u4f8d\u56fd\u738b\uff0c\u5374\u4e5f\u96be\u4ee5\u4e0b\u6bd2\u3002\u201d\u6842\u534e \u751f\u9053\uff1a\u201c\u8fd9\u6709\u4f55\u96be\uff0c\u53ea\u8981\u6709\u4e00\u4e2a\u673a\u4f1a\uff0c\u628a\u6bd2\u653e\u5230\u56fd\u738b\u7684\u8336\u9152\u91cc\uff0c\u4e0d\u5c31\u884d\u4e86\u4e48\uff1f\u201d\u5df4\u52d2\u9053\uff1a \u201c\u4e0d\uff0c\u8fd9\u662f\u4e00\u79cd\u6162\u6027\u7684\u6bd2\u836f\uff0c\u6bcf\u6b21\u4e0b\u6bd2\u5206\u91cf\u6781\u5fae\uff0c\u6700\u5c11\u8981\u63a5\u8fde\u4e0b\u6bd2\u4e03\u6b21\u3002\u5fa1\u6797\u519b\u603b\u7ba1\u975e\u7ecf\u5949 \u8bcf\uff0c\u4e0d\u80fd\u9020\u4eba\u5185\u5ef7\uff0c\u9664\u975e\u4ed6\u4e70\u901a\u4e86\u56fd\u738b\u8d34\u8eab\u7684\u4eba\u4e0b\u6bd2\u3002\u4f46\u6bd2\u6b7b\u4e86\u56fd\u738b\uff0c\u4ed6\u53c8\u6709\u4ec0\u4e48\u597d\u5904\uff1f\u738b \u4f4d\u662f\u5fc5\u5b9a\u8981\u7687\u5ba4\u4e4b\u4eba\u624d\u80fd\u7ee7\u627f\u7684\u3002\u201d\u6842\u534e\u751f\u9053\uff1a\u201c\u4f60\u4eec\u7684\u56fd\u738b\u53ef\u5f97\u56fd\u4eba\u7231\u6234\u4e48\uff1f\u201d\u5df4\u52d2\u9053\uff1a \u201c\u56fd\u738b\u4ec1\u6148\u7231\u6c11\uff0c\u56fd\u4eba\u5bf9\u4ed6\u5341\u5206\u7231\u6234\uff0c\u201d\u6842\u534e\u751f\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u90a3\u5c31\u4e0d\u662f\u56e0\u4ec7\u4e0b\u6bd2\u7684\u4e86\u3002\u4e2d \u56fd\u5386\u53f2\u4e0a\u4e5f\u6709\u8bb8\u591a\u6bd2\u6740\u56fd\u738b\u7684\u4e8b\uff0c\u90a3\u5341\u5c45\u516b\u4e5d\u662f\u4e3a\u4e86\u8c0b\u593a\u738b\u4f4d\u3002\u201d\u5df4\u52d2\u9053\uff1a\u201c\u516c\u4e3b\u7684\u8d24\u6167\u4e3e \u56fd\u5747\u77e5\u3002\u5979\u662f\u56fd\u738b\u7684\u72ec\u751f\u7231\u5973\u3002\u66f4\u7edd\u65e0\u6bd2\u6b7b\u7236\u4eb2\u4e4b\u7406\u3002\u54e6\u2014\u2014\u201d\u773c\u775b\u4e00\u95ea\uff0c\u4f3c\u662f\u4e3a\u6842\u534e\u751f\u7684 \u8bdd\u8d77\u4e86\u67d0\u79cd\u6000\u7591\uff0c\u6842\u534e\u751f\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u5df4\u52d2\u9762\u8272\u5fae\u53d8\uff0c\u9053\uff1a\u201c\u54b1\u4eec\u4e0d\u5fc5\u80e1\u4e71\u7591\u4e86\u3002\u65f6\u5019\u4e0d \u65e9\uff0c\u6211\u5fc5\u987b\u8d76\u53bb\u8d74\u603b\u7ba1\u5927\u4eba\u7684\u7ea6\u4f1a\u4e86\u3002\u201d\u6842\u534e\u751f\u9053\uff1a\u201c\u4ed6\u7ea6\u4f60\u534a\u591c\u4e09\u66f4\u89c1\u9762\uff0c\u8fd9\u65f6\u95f4\u8d70\u5f97\u771f \u662f\u53e4\u602a\u3002\u201d\u5df4\u52d2\u795e\u8272\u751a\u662f\u4e0d\u5b89\uff0c\u8bf4\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u6211\u4e00\u89c1\u5982\u6545\uff0c\u6211\u6b64\u6b21\u53bb\u4e86\uff0c\u82e5\u7136\u4e09\u65e5\u4e4b\u540e \u4e0d\u56de\uff0c\u90a3\u5c31\u662f\u51f6\u591a\u5409\u5c11\u4e86\u3002\u4f46\u4f60\u5207\u4e0d\u53ef\u5c06\u4eca\u665a\u4e4b\u4e8b\u544a\u8bc9\u522b\u4eba\uff0c\u4e5f\u4e0d\u5fc5\u627e\u6211\u3002\u6211\u65e0\u59bb\u65e0\u5b50\uff0c\u7ec8 \u751f\u94bb\u7814\u533b\u5b66\uff0c\u7559\u4e0b\u4e86\u7684\u533b\u6848\u662f\u6211\u6700\u5b9d\u8d35\u7684\uff0c\u4e09\u65e5\u4e4b\u540e\u6211\u82e5\u4e0d\u56de\uff0c\u8bf7\u4f60\u5c06\u533b\u6848\u53d6\u53bb\u3002\u5440\uff0c\u4e0d\u5982 \u6211\u73b0\u5728\u5c31\u4ea4\u6258\u7ed9\u4f60\u3002\u201d \u3000\u3000\u6842\u534e\u751f\u5ffd\u9053\uff1a\u201c\u4e0d\uff0c\u6211\u966a\u4f60\u53bb\uff01\u201d\u5df4\u52d2\u9053\uff1a\u201c\u4f60\u5982\u4f55\u53bb\u5f97\uff1f\u201d\u6842\u534e\u751f\u9053\uff1a\u201c\u6211\u626e\u4f5c\u4f60\u7684 \u836f\u7ae5\u3002\u201d\u5df4\u52d2\u8fdf\u7591\u534a\u664c\uff0c\u9053\uff1a\u201c\u4e5f\u597d\uff0c\u6211\u5012\u4e0d\u662f\u6015\u6b7b\uff0c\u53ea\u662f\u6211\u6b7b\u4e4b\u540e\uff0c\u56fd\u4e2d\u66f4\u65e0\u4eba\u80fd\u591f\u533b\u6cbb \u56fd\u738b\u4e86\u3002\u201d\u7acb\u5373\u66ff\u6842\u534e\u751f\u6539\u88c5\u6613\u5bb9\uff0c\u5728\u9762\u4e0a\u64e6\u4e86\u70b9\u7164\u7070\uff0c\u518d\u8499\u4e0a\u4e86\u7f20\u5934\u5e03\uff0c\u80cc\u4e0a\u836f\u91cc\uff0c\u679c\u7136 \u50cf\u4e00\u4e2a\u5c3c\u6cca\u5c14\u836f\u7ae5\u3002 \u3000\u3000\u5df4\u52d2\u81ea\u5907\u4e86\u4e00\u8f86\u9a6c\u8f66\uff0c\u90a3\u662f\u4ed6\u4e0b\u4e61\u770b\u75c5\u65f6\u4ee3\u6b65\u7684\uff0c\u603b\u7ba1\u7684\u522b\u5885\u5728\u745e\u626c\u5e03\u5c71\u5c71\u9e93\uff0c\u5df4\u52d2\u4f7f \u4e0e\u6842\u534e\u751f\u9a7e\u8f66\u524d\u5f80\uff0c\u5728\u8def\u4e0a\u6842\u534e\u751f\u95ee\u4ed6\u9053\uff1a\u201c\u4f60\u4eca\u65e5\u8fdb\u5bab\uff0c\u6709\u4eba\u77e5\u9053\u5417\uff1f\u201d\u5df4\u52d2\u9053\uff1a\u201c\u662f\u4e00 \u4e2a\u5c0f\u903c\u4eec\uff08\u5bab\u4e2d\u7684\u50e7\u4fa3\uff09\u6765\u5524\u6211\u7684\uff0c\u4ece\u5fa1\u82b1\u56ed\u7684\u540e\u95e8\u8fdb\u53bb\uff0c\u4ed6\u4e5f\u66fe\u53ee\u5631\u6211\u4e0d\u8981\u8bf4\u7ed9\u522b\u4eba\u77e5 \u9053\u3002\u201d\u4f73\u534e\u751f\u9053\uff1a\u201c\u4f60\u4eec\u4e3a\u4f55\u90a3\u6837\u7684\u6015\u5fa1\u6797\u519b\u603b\u7ba1\uff1f\u201d\u5df4\u52d2\u82e6\u7b11\u9053\uff1a\u201c\u56fd\u738b\u4ec1\u6148\uff0c\u4ed6\u5374\u6070\u5f97 \u5176\u53cd\u3002\u4ed6\u7684\u522b\u5885\u5c31\u662f\u7528\u5939\u62f7\u6253\u72af\u4eba\u7684\u5730\u65b9\uff0c\u7b49\u4e8e\u662f\u4ed6\u79c1\u8bbe\u7684\u76d1\u72f1\u3002\u201d \u3000\u3000\u9a6c\u8f66\u8d70\u4e86\u5927\u7ea6\u534a\u4e2a\u65f6\u8fb0\uff0c\u5230\u4e86\u745e\u626c\u5e03\u5c71\u5c71\u811a\uff0c\u5fa1\u6797\u519b\u603b\u7ba1\u65e9\u5df2\u6d3e\u6709\u4eba\u7b49\u5019\u3002\u4e00\u89c1\u5df4\u52d2\u5230 \u6765\uff0c\u9996\u5148\u6307\u7740\u6842\u534e\u751f\u95ee\u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\u4eba\uff1f\u201d\u5df4\u52d2\u9053\uff1a\u201c\u4ed6\u662f\u6211\u7684\u836f\u7ae5\uff0c\u4e5f\u662f\u6211\u7684\u52a9\u624b\u3002\u201d \u6b66\u58eb\u53eb\u4ed6\u4eec\u7a0d\u5f85\uff0c\u53eb\u540c\u4f34\u56de\u53bb\u8bf7\u793a\uff0c\u4e0d\u4e45\u4f20\u51fa\u8bdd\u6765\u9053\uff1a\u201c\u65e2\u7136\u6709\u4eba\u968f\u6765\uff0c\u90a3\u5c31\u90fd\u8fdb\u53bb\u5427\u3002\u201d \u6b66\u58eb\u5e26\u5f15\u4ed6\u4eec\u4e0a\u5c71\uff0c\u4e0d\u4e45\u5c31\u5230\u4e86\u522b\u5885\u95e8\u524d\u3002\u8fd9\u5ea7\u522b\u5885\u5f62\u5f0f\u53e4\u602a\uff0c\u6781\u4f3c\u4e00\u4e2a\u53e4\u5821\uff0c\u56f4\u5899\u90fd\u7528\u5c71 \u4e0a\u539a\u539a\u7684\u732a\u8272\u77f3\u5934\u780c\u6210\uff0c\u94c1\u95e8\u4e5f\u6cb9\u6210\u7ea2\u8272\uff0c\u6842\u534e\u751f\u968f\u4ed6\u4eec\u8fdb\u53bb\uff0c\u4e00\u76f4\u7a7f\u8fc7\u516d\u4e03\u9053\u94c1\u95e8\uff0c\u6bcf\u8d70 \u8fdb\u4e00\u9053\u95e8\uff0c\u94c1\u95e8\u7acb\u523b\u5173\u4e0a\u3002\u91cc\u9762\u9634\u9634\u68ee\u68ee\u7684\uff0c\u9f3b\u5b50\u91cc\u8fd8\u9690\u9690\u95fb\u5230\u6709\u8840\u8165\u7684\u5473\u9053\u3002 \u3000\u3000\u7a7f\u8fc7\u4e86\u516d\u4e03\u9053\u94c1\u95e8\uff0c\u9020\u4eba\u4e00\u5ea7\u5927\u5385\uff0c\u4f46\u89c1\u4e24\u5eca\u6b66\u58eb\u6797\u7acb\uff0c\u5385\u4e2d\u6446\u7740\u4e00\u5f20\u5706\u684c\uff0c\u6709\u4e00\u4e2a\u6ee1 \u9762\u8679\u987b\uff0c\u8eab\u62ab\u9526\u888d\u7684\u6c49\u5b50\u5750\u5728\u90a3\u91cc\u7b49\u5019\uff0c\u4ed6\u7684\u8eab\u540e\u4e5f\u571f\u7740\u4e24\u4e2a\u6b66\u58eb\uff0c\u5df4\u52c3\u7528\u773c\u793a\u610f\uff0c\u6842\u534e\u751f \u7acb\u523b\u660e\u767d\u8fd9\u4e2a\u4eba\u5c31\u662f\u5fa1\u6797\u519b\u603b\u7ba1\u3002\u5706\u684c\u7684\u53e6\u4e00\u4f8b\u5750\u7740\u4e00\u4e2a\u62ab\u7740\u5927\u7ea2\u67b6\u88c5\u7684\u50e7\u4eba\uff0c\u8fd9\u4e2a\u50e7\u4eba\u5df4 \u52c3\u4e0d\u8ba4\u8bc6\uff0c\u4f46\u5374\u662f\u6842\u534e\u751f\u7684\u76f8\u8bc6\u3002\u539f\u6765\u6b64\u4eba\u975e\u4ed6\uff0c\u6b63\u662f\u5728\u9b54\u9b3c\u57ce\u4e2d\u66fe\u4e0e\u4ed6\u4ea4\u8fc7\u624b\u7684\u7ea2\u8863\u756a \u50e7\u3002\u5e78\u800c\u6842\u534e\u751f\u65f1\u5df2\u6539\u88c5\u6613\u8c8c\uff0c\u90a3\u7ea2\u8863\u756a\u50e7\u770b\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u5fa1\u6797\u519b\u603b\u7ba1\u4e00\u89c1\u5df4\u52d2\u5230\u6765\uff0c\u7acb\u5373\u7ad9\u8d77\u6765\u9053\uff1a\u201c\u795e\u533b\u6765\u4e86\uff0c\u6b22\u8fce\uff0c\u6b22\u8fce\uff01\u201d\u968f\u5373\u95ee\u9053\uff1a \u201c\u4f60\u8fd9\u4e2a\u836f\u7ae5\u53ef\u77e5\u4f60\u4eca\u65e5\u8fdb\u5bab\u533b\u75c5\u4e4b\u4e8b\u4e48\uff1f\u201d\u5df4\u52d2\u9053\uff1a\u201c\u4ed6\u662f\u6211\u552f\u4e00\u7684\u52a9\u624b\uff0c\u522b\u4eba\u4e0d\u77e5\u9053\uff0c \u4ed6\u662f\u77e5\u9053\u7684\u3002\u201d\u603b\u7ba1\u9762\u8272\u4e00\u6c89\uff0c\u968f\u5373\u7b11\u9053\uff1a\u201c\u65e2\u662f\u5982\u6b64\uff0c\u90a3\u5c31\u8ba9\u4ed6\u5728\u9636\u4e0b\u7b49\u5019\u4f60\u5427\u3002\u201d\u7b11\u58f0 \u4e2d\u9690\u9690\u900f\u51fa\u6740\u6c14\uff0c\u6709\u4e24\u540d\u6b66\u58eb\uff0c\u5de6\u53f3\u76d1\u89c6\uff0c\u628a\u6842\u534e\u751f\u6b62\u4f4f\uff0c\u4e0d\u8ba9\u4ed6\u968f\u5df4\u52d2\u767b\u5802\u3002 \u3000\u3000\u5fa1\u6797\u519b\u603b\u7ba1\u5f85\u5df2\u52c3\u5750\u5b9a\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u6211\u4eca\u65e5\u53eb\u4eba\u8fed\u7ed9\u4f60\u7684\u793c\u7269\uff0c\u6536\u5230\u4e86\u5417\uff1f\u201d\u5df4\u52d2\u9053\uff1a \u201c\u6b63\u60f3\u8bf7\u95ee\u603b\u7ba1\u5927\u4eba\uff0c\u4f55\u6545\u539a\u8d60\uff1f\u5c0f\u4eba\u65e0\u529f\u4e0d\u6562\u53d7\u68e3\uff0c\u8fd9\u4efd\u539a\u793c\u5df2\u968f\u8eab\u5e26\u6765\u4e86\u3002\u201d\u8bf4\u7f62\uff0c\u5c06 \u94f6\u76d2\u653e\u7f6e\u5706\u684c\u4e4b\u4e0b\u3002\u603b\u7ba1\u610f\u6b8a\u4e0d\u60a6\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u80af\u53d7\u6211\u7684\u4e1c\u897f\u5417\uff1f\u201d\u5df4\u52d2\u9053\uff1a\u201c\u5c0f\u4eba\u8bf4\u8fc7 \u4e86\uff0c\u65e0\u529f\u4e0d\u6562\u53d7\u68e3\u3002\u201d\u603b\u7ba1\u76ef\u4e86\u5df4\u52d2\u4e00\u773c\uff0c\u9053\uff1a\u201c\u53ea\u8981\u4f60\u542c\u6211\u8bdd\uff0c\u4f60\u5c31\u6709\u5927\u5927\u7684\u529f\u52b3\u3002\u201d\u5df4 \u52d2\u9053\uff1a\u201c\u8bf7\u5927\u4eba\u5429\u9644\u3002\u201d \u3000\u3000\u5fa1\u6797\u519b\u603b\u7ba1\u7d27\u7d27\u76ef\u7740\u5df4\u52d2\uff0c\u9053\uff1a\u201c\u4f60\u4eca\u65e5\u8fdb\u5bab\u7ed9\u56fd\u738b\u6cbb\u75c5\uff0c\u53ef\u770b\u5f97\u51fa\u4ec0\u4e48\u75c5\u4e48\uff1f\u201d\u5df4\u52d2 \u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u8fd9\u4e2a\u2026\u2026\u201d\u603b\u7ba1\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u8fd9\u91cc\u90fd\u662f\u6211\u5fc3\u8179\u4e4b\u4eba\uff0c\u4f46\u8bf4\u65e0\u59a8\uff01\u201d\u5df4\u52d2\u9053\uff1a \u201c\u8fd9\u4e2a\uff0c\u6211\uff0c\u6211\u8fd8\u672a\u2026\u2026\u201d\u603b\u7ba1\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u8fd8\u672a\u770b\u51fa\u75c5\u56e0\uff1f\u54fc\uff01\u5982\u6b64\u8bf4\u6765\uff0c\u4f60\u6789\u6709\u795e\u533b\u4e4b \u540d\u5b57\u4e86\uff1f\u201d\u5df4\u52d2\u6570\u5341\u5e74\u6765\uff0c\u7ed9\u56fd\u4eba\u4e13\u4e3a\u795e\u533b\uff0c\u5bf9\u58f0\u540d\u751a\u4e3a\u91cd\u89c6\uff0c\u7ed9\u4ed6\u4e00\u6fc0\uff0c\u5e94\u58f0\u8bf4\u9053\uff1a\u201c\u6211 \u8fd8\u672a\u60f3\u51fa\u533b\u75c5\u7684\u65b9\u6cd5\u3002\u201d\u5df4\u52d2\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u5df2\u7ecf\u77e5\u9053\u4ed6\u7684\u75c5\u56e0\u4e86\uff0c\u4ed6\u662f\u4ec0\u4e48\u75c5\uff1f\u201d\u5df4\u52d2\u9053\uff1a \u201c\u4e0d\u662f\u4ec0\u4e48\u75c5\u3002\u201d\u603b\u7ba1\u5389\u58f0\u9053\uff1a\u201c\u600e\u4e48\u8bf4\uff1f\u201d\u5df4\u52d2\u9053\uff1a\u201c\u56fd\u738b\u4f3c\u662f\u4e2d\u6bd2\u3002\u201d\u603b\u7ba1\u5fae\u5fae\u4e00\u7b11\uff0c \u9053\uff1a\u201c\u4f60\u80af\u8bf4\u5b9e\u8bdd\uff0c\u5f88\u597d\u3002\u6211\u73b0\u5728\u5c31\u6c42\u4f60\u4e00\u4ef6\u4e8b\u3002\u201d\u5df4\u52d2\u9053\uff1a\u201c\u4e0d\u77e5\u5c0f\u4eba\u80fd\u5426\u529e\u5230\uff1f\u201d\u603b\u7ba1 \u7b11\u9053\uff1a\u201c\u7b80\u5355\u5f97\u5f88\uff0c\u4f60\u660e\u65e5\u5230\u5bab\u4e2d\u5904\u65b9\uff0c\u5c31\u5f53\u662f\u666e\u901a\u7684\u5934\u75db\u75c5\u61ca\u4e86\u3002\u5bab\u4e2d\u82e5\u8981\u4f60\u5199\u4e0b\u75c5\u6848\uff0c \u4f60\u8981\u7f72\u540d\u8bc1\u5b9e\u56fd\u738b\u60a3\u7684\u662f\u5934\u75db\u75c7\uff0c\u4e0d\u662f\u4ed6\u75c5\u3002\u201d\u6842\u534e\u751f\u4e00\u542c\uff0c\u7acb\u5982\u5176\u610f\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u8fd9\u4e2a \u5fa1\u6797\u519b\u603b\u7ba1\u7eb5\u975e\u4eb2\u81ea\u4e0b\u6bd2\uff0c\u4ea6\u5fc5\u662f\u540c\u8c0b\u4e4b\u4eba\u3002\u5df4\u52c3\u662f\u56fd\u4e2d\u6700\u597d\u7684\u533b\u751f\uff0c\u6709\u4ed6\u7b7e\u7f72\u8bc1\u660e\uff0c\u5c06\u6765 \u56fd\u738b\u6bd2\u53d1\u8eab\u6b7b\uff0c\u662f\u65e0\u4eba\u6562\u6000\u7591\u56fd\u738b\u7684\u6b7b\u56e0\u3002\u201d \u3000\u3000\u5df4\u52d2\u62ac\u8d77\u5934\u6765\uff0c\u4e5f\u76ef\u4e86\u90a3\u603b\u7ba1\u4e00\u773c\uff0c\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u5927\u4eba\u521a\u624d\u6559\u6211\u8bf4\u5b9e\u8bdd\uff0c\u8fd9\u6837\u4e00\u6765\uff0c\u6211 \u5374\u662f\u5bf9\u5168\u56fd\u8bf4\u8c0e\u4e86\u3002\u201d\u603b\u7ba1\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4f60\u4e0d\u613f\u610f\uff1f\u54fc\uff0c\u4f60\u60f3\u6e05\u695a\u4e86\uff0c\u4f60\u82e5\u7b54\u5e94\uff0c\u91d1\u94f6 \u73e0\u5b9d\uff0c\u968f\u4fbf\u4f60\u8981\uff0c\u5982\u5176\u4e0d\u7136\uff0c\u54fc\uff01\u201d\u5df4\u52d2\u9053\uff1a\u201c\u6211\u5b81\u613f\u4fdd\u5168\u795e\u533b\u56fd\u624b\u4e4b\u540d\uff0c\u80dc\u4e8e\u8981\u8fd9\u4e9b\u5e26\u4e0d \u5230\u575f\u5893\u53bb\u7684\u73e0\u5b9d\u3002\u201d\u6842\u534e\u751f\u6697\u6697\u559d\u91c7\uff0c\u90a3\u603b\u7ba1\u9762\u8272\u6c89\u6697\uff0c\u4f3c\u4e4e\u5c31\u8981\u53d1\u4f5c\uff0c\u90a3\u7ea2\u8863\u756a\u618e\u5374\u51b7\u7b11 \u9053\uff1a\u201c\u56fd\u738b\u7684\u75db\u53cd\u6b63\u4f60\u533b\u4e0d\u597d\uff0c\u8bf4\u4ec0\u4e48\u56fd\u624b\u795e\u533b\uff0c\u73b0\u5728\u8981\u5f53\u666e\u901a\u75c5\u75c7\u6765\u533b\uff0c\u4f55\u4f24\u4f60\u7684\u4eca \u8a89\uff1f\u201d\u8fd9\u8bdd\u5927\u4eba\u523a\u75db\u4e86\u5df4\u52d2\uff0c\u5c31\u5728\u6b64\u65f6\uff0c\u5ffd\u95fb\u5f97\u6842\u534e\u751f\u54c8\u54c8\u5927\u7b11\u4e4b\u58f0\uff01 \u3000\u3000\u90a3\u4e24\u4e2a\u76d1\u89c6\u6842\u534e\u751f\u7684\u6b66\u58eb\u5927\u5403\u4e86\u4e00\u60ca\uff0c\u559d\u9053\uff1a\u201c\u4f60\u5e72\u4ec0\u4e48\uff1f\u201d\u53ea\u542c\u5f97\u201c\u8f70\u9686\u201d\u4e00\u58f0\uff0c\u6842 \u534e\u751f\u53cd\u624b\u4e00\u638c\uff0c\u5c06\u5de6\u8fb9\u90a3\u540d\u6b66\u58eb\u6253\u4e0b\u77f3\u9636\uff0c\u53f3\u8fb9\u90a3\u540d\u6b66\u58eb\u62d4\u51fa\u6708\u7259\u5f2f\u5200\u5411\u6842\u534e\u751f\u817f\u5f2f\u75be\u65a9\uff0c \u6842\u534e\u751f\u63d0\u8d77\u53f3\u817f\uff0c\u6210\u4e86\u201c\u91d1\u9e21\u72ec\u7acb\u201d\u7684\u59ff\u52bf\uff0c\u90a3\u6b66\u58eb\u65a9\u4e86\u4e2a\u7a7a\uff0c\u8eab\u5f62\u524d\u6251\uff0c\u6842\u534e\u751f\u811a\u5c16\u4e00 \u5f39\uff0c\u5feb\u6377\u65e0\u4f26\uff0c\u5f53\u7405\u4e00\u58f0\uff0c\u53c8\u628a\u90a3\u6b66\u58eb\u7684\u6708\u7259\u5f2f\u5200\u8e22\u51fa\u624b\u53bb\u3002 \u3000\u3000\u4e24\u5eca\u6b66\u58eb\u7eb7\u7eb7\u62d4\u5200\u54d7\u53eb\uff0c\u4f46\u89c1\u6842\u534e\u751f\u54c8\u54c8\u5927\u7b11\uff0c\u8eab\u5f62\u5982\u7bad\uff0c\u98de\u8eab\u4e00\u63a0\uff0c\u4f7f\u5230\u4e86\u5706\u684c\u524d\u9762 \u7ad9\u5b9a\uff0c\u660e\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u6709\u8bdd\u8bb2\uff01\u201d\u90a3\u5fa1\u6797\u519b\u603b\u7ba1\u672c\u6765\u662f\u56fd\u4e2d\u6709\u540d\u7684\u52c7\u58eb\uff0c\u4e24\u818a\u4e4b\u529b\uff0c\u80fd\u4e3e\u5343 \u65a4\uff0c\u89c1\u6842\u534e\u751f\u75be\u5982\u5176\u6765\uff0c\u867d\u5403\u4e00\u60ca\uff0c\u5374\u4e5f\u4e0d\u60e7\uff0c\u8d81\u7740\u6842\u534e\u751f\u8eab\u5f62\u672a\u7a33\uff0c\u53cc\u81c2\u4e00\u4f38\uff0c\u5c06\u4f73\u534e\u751f \u7684\u80a9\u5934\u4e00\u6309\uff0c\u559d\u9053\uff1a\u201c\u5750\u4e0b\u6765\u8bb2\uff01\u201d\u5176\u5b9e\u4ed6\u8fd9\u4e00\u6309\u4e43\u662f\u6709\u610f\u7ed3\u5df4\u52d2\u663e\u663e\u989c\u8272\uff0c\u6ee1\u62df\u4e00\u6309\u4e4b \u4e0b\uff0c\u5c31\u8981\u628a\u6842\u534e\u751f\u7684\u80a9\u813e\u9aa8\u6298\u65ad\u3002 \u3000\u3000\u90a3\u77e5\u89e6\u624b\u4e4b\u5904\uff0c\u5176\u8f6f\u5982\u68c9\uff0c\u5fa1\u6797\u519b\u603b\u7ba1\u65b9\u89c9\u4e0d\u5999\uff0c\u9e2f\u7136\u95f4\u4e00\u80a1\u53cd\u5f39\u4e4b\u529b\u8feb\u6765\uff0c\u5fa1\u6797\u519b\u603b \u7ba1\u53cc\u81c2\u9178\u9ebb\uff0c\u6842\u534e\u751f\u53cc\u80a9\u4e00\u8038\uff0c\u7b11\u9053\uff1a\u201c\u603b\u7ba1\u5927\u4eba\u4e0d\u5750\uff0c\u5c0f\u7684\u600e\u6562\u5750\u4e0b\uff1f\u201d\u90a3\u7ea2\u8863\u756a\u50e7\u4e5f\u5403 \u4e86\u4e00\u60ca\uff0c\u8fd9\u65f6\u4e5f\u8fd8\u672b\u770b\u51fa\u662f\u6842\u534e\u751f\uff0c\u5fc3\u4e2d\u5927\u4e3a\u5947\u602a\uff0c\u60f3\u9053\uff1a\u201c\u8fd9\u5c0f\u836f\u7ae5\u600e\u7684\u4f1a\u6709\u8fd9\u79cd\u4e0a\u4e58\u7684 \u529f\u592b\uff1f\u201d\u4e0d\u7531\u81ea\u5df1\u7684\u4e5f\u7ad9\u4e86\u8d77\u6765\u3002 \u3000\u3000\u5fa1\u6797\u519b\u603b\u7ba1\u6012\u9053\uff1a\u201c\u4f60\u6709\u4f55\u8bdd\uff1f\u201d\u6842\u534e\u751f\u9053\uff1a\u201c\u8bf7\u603b\u7ba1\u5927\u4eba\u5e26\u9886\u6211\u4eec\u8fdb\u5bab\u3002\u201d\u603b\u7ba1\u9053\uff1a \u201c\u505a\u4ec0\u4e48\uff1f\u201d\u6842\u534e\u751f\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u8bf4\u6211\u5e08\u7236\u7ed9\u56fd\u738b\u6cbb\u75c5\uff0c\u54fc\uff0c\u8fd9\u4f60\u4eec\u53ef\u770b\u9519\u4e86\u3002\u8c01\u4e0d\u77e5\u9053 \u6211\u5e08\u7236\u662f\u5168\u56fd\u7b2c\u4e00\u540d\u7684\u5999\u624b\u795e\u533b\uff1f\u4e0d\u8981\u8bf4\u4ed6\u8001\u4eba\u5bb6\uff0c\u56fd\u738b\u8fd9\u70b9\u5c0f\u75c5\uff0c\u5c31\u662f\u6211\u53bb\u533b\uff0c\u4e5f\u5305\u7ba1\u4e00 \u533b\u4fbf\u597d\uff01\u201d\u7ea2\u8863\u756a\u50e7\u9053\uff1a\u201c\u90a3\u4f60\u662f\u8bda\u5fc3\u8981\u548c\u6211\u4eec\u4f5c\u5bf9\u4e86\uff1f\u201d\u6842\u534e\u751f\u9053\uff1a\u201c\u4f60\u4eec\u4e0d\u60f3\u56fd\u738b\u7684\u75c5 \u533b\u597d\u5417\uff1f\u4f60\u4fdd\u8350\u6211\u53bb\u533b\u75c5\uff0c\u533b\u597d\u4e86\u4f60\u4eec\u4e5f\u6709\u529f\u52b3\u5440\uff01\u201d\u7ea2\u8863\u756a\u50e7\u51b7\u7b11\u9053\uff1a\u201c\u597d\uff0c\u597d\uff01\u6211\u73b0\u5728 \u5c31\u5e26\u9886\u4f60\u53bb\uff01\u201d\u6761\u5730\u6a2a\u638c\u5982\u5200\uff0c\u5411\u6842\u534e\u751f\u7684\u5929\u7075\u76d6\u75be\u5288\uff0c\u5c31\u5728\u8fd9\u65f6\uff0c\u5fa1\u6797\u519b\u603b\u7ba1\u4e5f\u9aa4\u7136\u53d1 \u96be\uff0c\u62d4\u51fa\u77ed\u5267\uff0c\u75be\u63d2\u6842\u534e\u751f\u7684\u80cc\u5fc3\u3002 \u3000\u3000\u6842\u534e\u751f\u4e00\u58f0\u957f\u7b11\uff0c\u5de6\u624b\u53cd\u624b\u4e00\u62ff\uff0c\u62ff\u7740\u4e86\u5fa1\u6797\u519b\u603b\u7ba1\u7684\u624b\u8155\uff0c\u5fa1\u6797\u519b\u603b\u7ba1\u5168\u8eab\u9ebb\u6728\uff0c\u52a8 \u5f39\u4e0d\u5f97\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u7ea2\u8863\u756a\u618e\u84b2\u6247\u5de8\u638c\u631f\u98ce\u62cd\u5230\uff0c\u6842\u534e\u751f\u53f3\u624b\u4e00\u6325\uff0c\u6309\u7740\u5f80\u9762\u4e0a\u4e00 \u62b9\uff0c\u5927\u7b11\u8bf4\u9053\uff1a\u201c\u5996\u50e7\uff0c\u4f60\u8fd8\u8ba4\u5f97\u6211\u4e48\uff1f\u201d \u3000\u3000\u6842\u534e\u751f\u8fd9\u4e00\u6325\u4e4b\u52bf\uff0c\u7528\u7684\u6b63\u662f\u201c\u94c1\u7435\u7436\u6307\u201d\u7684\u529f\u592b\uff0c\u7ea2\u8863\u756a\u618e\u88ab\u4ed6\u4e94\u6307\u4e00\u62c2\uff0c\u624b\u8155\u4e0a\u767b \u65f6\u8d77\u4e86\u4e94\u6761\u7ea2\u5370\uff0c\u8fd9\u65f6\u5df2\u770b\u6e05\u695a\u4e86\u6842\u534e\u751f\u5c31\u662f\u5728\u9b54\u9b3c\u57ce\u4e2d\u5c06\u81ea\u5df1\u6253\u8d25\u7684\u90a3\u4e2a\u4e2d\u56fd\u5c11\u5e74\uff0c\u5413\u5f97 \u5012\u9000\u4e09\u6b65\uff0c\u5931\u58f0\u53eb\u9053\uff1a\u201c\u4f60\uff0c\u4f60\uff0c\u4f60\uff01\u4f60\u5c45\u7136\u6562\u5230\u6211\u56fd\u4eac\u57ce\u6363\u4e71\u3002\u201d \u3000\u3000\u6842\u534e\u751f\u9053\uff1a\u201c\u54b1\u4eec\u5230\u56fd\u738b\u9762\u524d\u5206\u8bf4\u3002\u201d\u7ea2\u8863\u756a\u50e7\u8131\u4e0b\u8888\u88df\uff0c\u60f3\u52a8\u624b\u5374\u53c8\u4e0d\u6562\u52a8\u624b\u3002\u6842\u534e \u751f\u7b11\u9053\uff1a\u201c\u6709\u603b\u7ba1\u5927\u4eba\u966a\u4f34\uff0c\u4e5f\u662f\u4e00\u6837\u3002\u597d\uff0c\u5e08\u7236\uff0c\u54b1\u4eec\u8d70\u5427\uff01\u201d\u62d6\u7740\u5fa1\u6797\u519b\u603b\u7ba1\u4fbf\u5f80\u5916 \u95ef\u3002\u5fa1\u6797\u519b\u603b\u7ba1\u529b\u5927\u5982\u864e\uff0c\u4ed6\u90e8\u4e0b\u7684\u6b66\u58eb\u4eec\u4e2a\u4e2a\u7686\u77e5\uff0c\u8fd9\u65f6\u89c1\u603b\u7ba1\u5927\u4eba\u7adf\u4f3c\u7ef5\u7f8a\u4e00\u822c\u88ab\u6842\u534e \u751f\u7275\u7740\uff0c\u663e\u7136\u662f\u5b8c\u5168\u6d88\u5931\u4e86\u62b5\u6297\u4e4b\u529b\uff0c\u8c01\u4e0d\u60ca\u9a87\uff01 \u3000\u3000\u6842\u534e\u751f\u6b63\u81ea\u54c8\u54c8\u5927\u7b11\uff0c\u5ffd\u58f0\u5f97\u80cc\u540e\u6697\u5668\u7834\u7a7a\u4e4b\u58f0\uff0c\u6765\u52bf\u6781\u75be\u4e00\u2026\u2026\u3002 \u3000\u3000\u6842\u534e\u751f\u62d6\u7740\u5fa1\u6797\u519b\u603b\u7ba1\uff0c\u95ea\u52a8\u4e0d\u4fbf\uff0c\u53ea\u542c\u5f97\u90a3\u6697\u5668\u631f\u98ce\u547c\u5578\u4e4b\u58f0\uff0c\u6025\u52b2\u975e\u5e38\uff0c\u6842\u534e\u751f\u8feb \u5f97\u677e\u5f00\u4e86\u624b\uff0c\u4f38\u6307\u75be\u5f39\uff0c\u90a3\u51e0\u679a\u6697\u5668\u662f\u4e09\u5bf8\u6765\u957f\u94dc\u9489\uff0c\u500f\u7684\u98de\u5230\u9762\u95e8\uff0c\u88ab\u6842\u534e\u751f\u65bd\u5c55\u201c\u94c1\u7435 \u7436\u6307\u201d\u7684\u529f\u592b\uff0c\u4e00\u9635\u75be\u5f39\uff0c\u94ee\u94ee\u4e4b\u58f0\uff0c\u8361\u4eba\u5fc3\u9b44\uff0c\u6842\u534e\u751f\u5341\u53ea\u6307\u5934\u90fd\u611f\u5230\u9178\u9ebb\uff0c\u4e0d\u7531\u5f97\u5fc3\u4e2d \u4e00\u51db\uff0c\u60f3\u4e0d\u5230\u5230\u5c3c\u6cca\u5c14\u4eac\u57ce\u7684\u7b2c\u4e00\u665a\u4e0a\uff0c\u5c31\u9047\u5230\u4e86\u5982\u65af\u9ad8\u624b\u3002 \u3000\u3000\u90a3\u5fa1\u6797\u519b\u603b\u7ba1\u5927\u53eb\u4e00\u58f0\uff0c\u4e00\u8de4\u8dcc\u5012\u5730\u4e0a\uff0c\u539f\u6765\u6842\u534e\u751f\u653e\u5f00\u4ed6\u7684\u65f6\u5019\uff0c\u5df2\u662f\u6697\u4e2d\u70b9\u4e86\u4ed6\u7684 \u201c\u5929\u67a2\u7a74\u201d\uff0c\u8fd9\u65f6\u5eca\u4e0b\u4f17\u6b66\u58eb\u7eb7\u7eb7\u8dd1\u5230\uff0c\u6709\u4e9b\u53bb\u6276\u603b\u7ba1\u5927\u4eba\uff0c\u6709\u4e9b\u5219\u5954\u5411\u5df4\u52c3\u3002 \u3000\u3000\u6842\u534e\u751f\u65e0\u6687\u5ba1\u5bdf\u90a3\u6697\u5668\u81ea\u4f55\u800c\u6765\uff0c\u63d0\u4e00\u53e3\u6c14\uff0c\u5148\u53d1\u51fa\u5288\u7a7a\u638c\u7684\u529f\u592b\uff0c\u90a3\u51e0\u4e2a\u6b66\u58eb\u521a\u6328\u8fd1 \u5df4\u52d2\uff0c\u5ffd\u89c9\u6709\u4e00\u80a1\u6697\u529b\u63a8\u6765\uff0c\u83ab\u540d\u5176\u5999\u7684\u90fd\u5012\u5728\u5730\u4e0a\u3002\u6842\u534e\u751f\u638c\u5288\u63e9\u622e\uff0c\u970e\u773c\u4e4b\u95f4\uff0c\u6253\u7ffb\u4e86 \u4e03\u516b\u4e2a\u6b66\u58eb\uff0c\u90a3\u51e0\u4e2a\u8dcc\u5728\u5730\u4e0a\u7684\u6b66\u58eb\u624d\u521a\u521a\u722c\u8d77\uff0c\u6842\u534e\u751f\u51b2\u7740\u4ed6\u4eec\u51b7\u7b11\u9053\uff1a\u201c\u6211\u5e08\u7236\u7684\u6b66\u529f \u6bd4\u6211\u9ad8\u660e\u5341\u500d\uff0c\u4f60\u4eec\u8981\u53bb\u62ff\u4ed6\uff0c\u53ef\u662f\u60f3\u627e\u6b7b\u4e48\uff1f\u201d\u90a3\u51e0\u4e2a\u6b66\u58eb\u7ed9\u4ed6\u4e00\u5413\uff0c\u614c\u5fd9\u9000\u4e0b\uff0c\u679c\u7136\u4e0d \u6562\u6328\u8fd1\u5df4\u52d2\u3002 \u3000\u3000\u6df7\u4e71\u4e4b\u4e2d\uff0c\u5ffd\u542c\u5f97\u6709\u4eba\u559d\u9053\uff1a\u201c\u90fd\u7ed9\u6211\u9000\u4e0b\uff01\u201d\u6842\u534e\u751f\u5b9a\u775b\u4e00\u770b\uff0c\u4f46\u89c1\u4e00\u4e2a\u957f\u53d1\u62ab\u80a9\uff0c \u9ad8\u9f3b\u6df1\u76ee\u7684\u6c49\u5b50\u5927\u8e0f\u6b65\u8d70\u51fa\uff0c\u54ac\u7259\u88c2\u5634\u7684\u5411\u6842\u534e\u751f\u7b11\u9053\uff1a\u201c\u4f60\u662f\u4ece\u4e2d\u56fd\u5417\uff1f\u6b66\u529f\u5f88\u4e0d\u9519 \u5440\uff01\u201d\u4f38\u51fa\u624b\u6765\u4e0e\u6842\u534e\u751f\u4e00\u63e1\uff0c\u6842\u534e\u751f\u6025\u5fd9\u8fd0\u6c14\u76f8\u6297\uff0c\u4f46\u89c9\u4e00\u80a1\u6781\u5f3a\u52b2\u7684\u529b\u9053\u8feb\u6765\uff0c\u500f\u7136\u4e4b \u95f4\uff0c\u5ffd\u53c8\u6d88\u5931\uff0c\u6842\u534e\u751f\u51e0\u4e4e\u7acb\u8db3\u4e0d\u7a33\uff0c\u5e78\u800c\u4ed6\u7684\u6d51\u8eab\u529b\u91cf\u4e5f\u5df2\u5230\u4e86\u63a7\u5236\u81ea\u5982\u7684\u5730\u6b65\uff0c\u4e00\u89c9\u4e0d \u5999\uff0c\u7acb\u5373\u7528\u91cd\u8eab\u6cd5\u5b9a\u4f4f\u8eab\u5f62\uff0c\u624b\u6307\u5fae\u5fae\u4e00\u5e26\uff0c\u5c06\u5bf9\u65b9\u7684\u52b2\u529b\u5316\u5f00\uff0c\u800c\u4e14\u987a\u624b\u8fd8\u5728\u4ed6\u8109\u95e8\u91cd\u516d \u70b9\u4e86\u4e00\u4e0b\u3002\u201d\u8fd9\u4eba\u5374\u4f3c\u6beb\u65e0\u77e5\u89c9\uff0c\u54c8\u54c8\u4e00\u7b11\uff0c\u5c06\u624b\u638c\u62bd\u4e86\u51fa\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u679c\u7136\u4e0d\u9519\uff0c\u6709\u8d44\u683c \u548c\u6211\u6bd4\u8bd5\u4e00\u4e0b\u3002\u201d\u6842\u534e\u751f\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u60f3\u4e0d\u5230\u8fd9\u4e2a\u4eba\u5c45\u7136\u4e5f\u61c2\u5f97\u95ed\u7a74\u7684\u529f\u592b\uff0c\u8bd5\u4ed6\u7684\u529f \u529b\uff0c\u5b9e\u4e0d\u5728\u4e2d\u56fd\u7684\u7b2c\u4e00\u6d41\u9ad8\u624b\u4e4b\u4e0b\uff0c\u90a3\u7ea2\u8863\u756a\u50e7\u770b\u51fa\u4e86\u6842\u534e\u751f\u9762\u4e0a\u7a0d\u6709\u60ca\u60f6\u4e4b\u8272\uff0c\u5f97\u610f\u4e4b \u6781\uff0c\u6307\u7740\u6842\u534e\u751f\u8bf4\u9053\uff1a\u201c\u4e2d\u56fd\u6765\u7684\u5c0f\u5b50\uff0c\u4eca\u665a\u4f60\u7684\u9020\u5316\u5230\u4e86\u3002\u4f60\u77e5\u9053\u8fd9\u4f4d\u6cd5\u5e08\u662f\u8c01\uff1f\u4ed6\u662f\u963f \u523a\u4f2f\u8bf8\u56fd\u7684\u7b2c\u4e00\u9ad8\u624b\u63d0\u6469\u8fbe\u591a\uff0c\u4f60\u597d\u597d\u8bf7\u4ed6\u6307\u6559\u4e00\u756a\u5427\u3002\u201d \u3000\u3000\u6842\u534e\u751f\u65e9\u5c31\u4ece\u767d\u8863\u5c11\u5973\u53e3\u4e2d\u542c\u8fc7\u63d0\u6469\u8fbe\u591a\u7684\u540d\u5b57\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u4e45\u95fb\u963f\u62c9\u4f2f\u8bf8\u56fd\u4e5f\u4e0e\u4e2d \u56fd\u4e00\u6837\uff0c\u4e43\u662f\u6587\u660e\u53e4\u56fd\uff0c\u6b66\u672f\u6e90\u8fdc\u6d41\u957f\uff0c\u8fd9\u4eba\u662f\u4ed6\u4eec\u7684\u7b2c\u4e00\u9ad8\u624b\uff0c\u786e\u662f\u540d\u4e0b\u65e0\u865a\uff0c\u5012\u4e0d\u53ef\u5c0f \u89d1\u4e86\u3002\u201d \u3000\u3000\u63d0\u6469\u8fbe\u591a\u53cc\u638c\u4e00\u62cd\uff0c\u9053\uff1a\u201c\u4f60\u8eab\u4e0a\u85cf\u6709\u5b9d\u5251\uff0c\u53d6\u51fa\u6765\u5427\uff01\u201d\u6842\u534e\u751f\u7684\u817e\u86df\u5b9d\u5251\u8f6f\u786c\u81ea \u5982\uff0c\u8fd9\u65f6\u6b63\u7f20\u5728\u8170\u95f4\u5f53\u4f5c\u8170\u5e26\uff0c\u4e0d\u6599\u63d0\u6469\u8fbe\u591a\u4e00\u773c\u671b\u5f97\u51fa\u6765\u3002 \u3000\u3000\u6842\u534e\u751f\u9053\uff1a\u201c\u4e14\u6162\uff0c\u65e2\u7136\u6bd4\u8bd5\uff0c\u54b1\u4eec\u53ef\u5f97\u8bf4\u6e05\u695a\uff0c\u80dc\u4e86\u5982\u4f55\uff1f\u8d25\u4e86\u5982\u4f55\uff1f\u201d\u90a3\u7ea2\u8863\u756a\u50e7 \u4e0d\u5fcd\u4f4f\u9a82\u9053\uff1a\u201c\u597d\u4e2a\u4e0d\u77e5\u5929\u9ad8\u5730\u539a\u7684\u5c0f\u5b50\uff0c\u63d0\u6469\u8fbe\u591a\u6cd5\u5e08\u8fd8\u80fd\u8f93\u7ed9\u4f60\u5417\uff1f\u201d \u3000\u3000\u63d0\u6469\u8fbe\u591a\u5374\u5927\u7b11\u9053\uff1a\u201c\u597d\uff0c\u4f60\u8fd9\u4e2a\u5c11\u5e74\u4eba\u771f\u6709\u80c6\u91cf\uff0c\u4e09\u5341\u5e74\u6765\uff0c\u6211\u8fd8\u662f\u7b2c\u4e00\u6b21\u542c\u5230\u6709\u4eba \u8fd9\u6837\u95ee\u6211\u3002\u597d\u5427\uff0c\u4f60\u542c\u6e05\u695a\u4e86\uff01\u201d\u968f\u624b\u4e00\u6307\u5c4b\u89d2\u7684\u4e00\u652f\u70db\u5149\u2026\u2026 \u3000\u3000\u5fa1\u6797\u519b\u603b\u7ba1\u8fd9\u5ea7\u5927\u53a6\uff0c\u5e03\u7f6e\u5f97\u975e\u5e38\u534e\u4e3d\uff0c\u56db\u89d2\u6709\u56db\u6839\u4e2d\u7a7a\u7684\u73bb\u7483\u5706\u67f1\uff0c\u5706\u67f1\u5185\u5404\u70b9\u7740\u4e00 \u652f\u725b\u6cb9\u5de8\u70db\uff0c\u70db\u5f71\u6447\u529e\uff0c\u4ece\u73bb\u7483\u7f69\u91cc\u6cdb\u51fa\u5149\u8f89\uff0c\u751a\u4e3a\u522b\u81f4\u3002\u63d0\u6469\u8fbe\u591a\u6307\u7740\u4e1c\u5357\u89d2\u7684\u73bb\u7483\u67f1\u5185 \u7684\u70db\u5149\u8bf4\u9053\uff1a\u201c\u4f60\u5192\u72af\u4e86\u603b\u7ba1\u5927\u4eba\uff0c\u6211\u5728\u6b64\u95f4\u4f5c\u5ba2\uff0c\u4e0d\u80fd\u4e0d\u7ba1\u3002\u4e0d\u8fc7\uff0c\u4f60\u65e2\u6709\u80c6\u91cf\u4e0e\u6211\u6bd4 \u8bd5\uff0c\u6211\u5c31\u7ed9\u4f60\u4e00\u4e2a\u673a\u4f1a\u3002\u201d\u987f\u4e86\u4e00\u987f\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5c31\u4ee5\u8fd9\u652f\u70db\u5149\u4e3a\u9650\uff0c\u82e5\u5728\u70db\u5149\u7184\u706d\u4ee5\u524d\uff0c \u4f60\u7ed9\u51fb\u8d25\u2014\u2014\u90a3\u6ca1\u8bf4\u7684\uff0c\u6211\u53ea\u597d\u5c06\u4f60\u4ea4\u7ed9\u603b\u7ba1\u5927\u4eba\u5904\u7f6e\u3002\u82e5\u5728\u70db\u5149\u5df2\u7184\uff0c\u6211\u5c1a\u672a\u80fd\u5c06\u4f60\u51fb \u5012\uff0c\u90a3\u4e48\uff0c\u8fd9\u91cc\u7684\u4e8b\uff0c\u6211\u6492\u624b\u4e0d\u7ba1\uff01\u201d \u3000\u3000\u8fd9\u652f\u5de8\u70db\u5df2\u70e7\u4e86\u5927\u534a\uff0c\u770b\u6765\u4e0d\u7528\u534a\u4e2a\u65f6\u8fb0\u5c31\u53ef\u4ee5\u70e7\u5b8c\u3002\u6842\u534e\u751f\u5fc3\u4e2d\u597d\u6c14\uff0c\u53eb\u9053\uff1a\u201c\u597d\uff0c \u5c31\u662f\u8fd9\u6837\uff0c\u6765\u5427\uff01\u201d\u4ed6\u4ee5\u4e3a\u63d0\u6469\u8fbe\u591a\u5c0f\u89d1\u4e8e\u4ed6\uff0c\u5c82\u77e5\u63d0\u6469\u8fbe\u591a\u5df2\u662f\u5bf9\u4ed6\u5341\u5206\u91cd\u89c4\uff0c\u53ea\u56e0\u89c1\u4ed6 \u521a\u624d\u9732\u51fa\u4e86\u51e0\u624b\u975e\u51e1\u7684\u6b66\u529f\uff0c\u8fd9\u624d\u4ee5\u534a\u652f\u70db\u5149\u4e3a\u9650\uff0c\u5728\u63d0\u6469\u8fbe\u591a\u5fc3\u4e2d\uff0c\u65f6\u9650\u5df2\u662f\u653e\u5f97\u6781\u5bbd \u4e86\u3002 \u3000\u3000\u63d0\u6469\u8fbe\u591a\u7b11\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4e48\u8bf7\u4f60\u7ad9\u7a33\u4e86\uff01\u201d\u8c08\u7b11\u4e4b\u95f4\uff0c\u6f2b\u4e0d\u7ecf\u610f\u7684\u5ffd\u5730\u53d1\u51fa\u4e00\u638c\uff0c\u6842\u534e \u751f\u5df2\u7ecf\u62d4\u5251\u51fa\u9500\uff0c\u5374\u6545\u610f\u820d\u5251\u4e0d\u7528\uff0c\u4e00\u9762\u65bd\u5c55\u201c\u5343\u65a4\u5760\u201d\u7684\u529f\u592b\uff0c\u53cc\u811a\u7262\u7262\u9489\u5728\u5730\u4e0a\uff0c\u4e00\u9762 \u8fd0\u529b\u5de6\u638c\uff0c\u4ee5\u5927\u529b\u91d1\u521a\u624b\u786c\u63a5\u4ed6\u7684\u638c\u529b\u3002\u5c82\u77e5\u6842\u534e\u751f\u7a0d\u4e3a\u8f7b\u654c\uff0c\u51e0\u4e4e\u5728\u4e00\u62db\u4e4b\u5185\uff0c\u5c31\u88ab\u63d0\u6469 \u8fbe\u591a\u51fb\u5012\u3002 \u3000\u3000\u6842\u534e\u751f\u638c\u529b\u5c06\u53d1\u672a\u53d1\uff0c\u9661\u7136\u95f4\u4f46\u89c9\u4e00\u80a1\u6781\u5927\u7684\u6f5c\u529b\u6392\u5c71\u5012\u6d77\u800c\u81f3\uff0c\u6842\u534e\u751f\u5927\u559d\u4e00\u58f0\uff0c\u4e00 \u638c\u5e73\u63a8\uff0c\u53d1\u51fa\u4e86\u4e5d\u6210\u771f\u529b\uff0c\u4f46\u542c\u5f97\u201c\u6ce2\u201d\u7684\u4e00\u58f0\uff0c\u72c2\u53f0\u6012\u5377\uff0c\u539f\u6765\u662f\u4e24\u80a1\u638c\u529b\u76f8\u9047\uff0c\u6fc0\u8d77\u70c8 \u98ce\uff0c\u7ad9\u5f97\u7a0d\u8fd1\u7684\u6b66\u58eb\u88ab\u638c\u98ce\u63a8\u5f97\u6447\u6447\u853d\u6643\uff0c\u7eb7\u7eb7\u8d70\u907f\u3002 \u3000\u3000\u6842\u534e\u751f\u6697\u53eb\u4e00\u58f0\u201c\u4e0d\u597d\uff01\u201d\u80f8\u53e3\u5982\u7ed9\u94c1\u4e00\u51fb\uff0c\u7acb\u8db3\u4e0d\u7262\uff0c\u5c31\u8981\u8dcc\u5012\uff0c\u6025\u5fd9\u65bd\u5c55\u7edd\u9876\u8f7b \u529f\uff0c\u51cc\u7a7a\u98de\u8d77\u4e8c\u4e08\u591a\u9ad8\uff0c\u624b\u633d\u6784\u6881\uff0c\u638c\u98ce\u547c\u7684\u4e00\u58f0\uff0c\u4ece\u4ed6\u811a\u4e0b\u53f0\u8fc7\u3002\u63d0\u6469\u8fbe\u591a\u5f97\u610f\u4e4b\u6781\uff0c\u783e \u783e\u602a\u7b11\uff0c\u5927\u8e0f\u6b65\u5411\u524d\uff0c\u4ef0\u9996\u671d\u5929\uff0c\u5411\u7740\u6a2a\u6881\uff0c\u53c8\u53d1\u4e00\u638c\uff01 \u3000\u3000\u4e00\u638c\u53d1\u51fa\uff0c\u60ca\u53eb\u4e4b\u58f0\u56db\u8d77\uff0c\u539f\u6765\u8fd9\u6839\u6784\u6881\uff0c\u7adf\u7ed9\u4ed6\u7684\u4e8b\u65b9\u9707\u65ad\u4e86\uff0c\u54d7\u53eb\u58f0\u4e2d\uff0c\u6842\u534e\u751f\u4e00 \u4e2a\u201c\u7ec6\u80f8\u5de7\u7ffb\u4e91\u201d\uff0c\u75be\u63a0\u800c\u4e0b\uff0c\u957f\u5251\u4e00\u62db\u201c\u5012\u5377\u5929\u6cb3\u201d\u3002\u51cc\u7a7a\u523a\u51fb\uff0c\u767b\u65f6\u9152\u4e0b\u4e86\u5343\u767e\u70b9\u5bd2 \u5149\uff0c\u628a\u63d0\u6469\u8fbe\u591a\u7684\u8eab\u5f62\u7f69\u4f4f\u3002 \u3000\u3000\u63d0\u6469\u8fbe\u591a\u4e5f\u662f\u8f7b\u654c\u592a\u6025\uff0c\u60f3\u4e0d\u5230\u6842\u534e\u751f\u7684\u5b9d\u5251\u5389\u5bb3\u4e4b\u6781\uff0c\u5265\u5149\u6fc0\u8361\u4e4b\u4e0b\uff0c\u4f46\u542c\u5f97\u55e4\u55e4\u4e4b \u58f0\u54cd\uff0c\u5c31\u597d\u50cf\u4e00\u4e2a\u5927\u76ae\u7403\u7ed9\u8bb8\u591a\u5229\u9488\u6240\u523a\u4e00\u6837\uff0c\u539f\u6765\u662f\u4ed6\u638c\u529b\u6240\u8361\u8d77\u7684\u6c14\u6d41\u7ed9\u5b9d\u5251\u53cd\u51fb\u4e4b\u529b \u8feb\u6563\uff0c\u63d0\u6469\u8fbe\u591a\u4e5f\u5403\u4e86\u4e00\u60ca\uff0c\u500f\u7136\u95f4\u5265\u5149\u66b4\u957f\uff0c\u6842\u534e\u751f\u7684\u5251\u62db\u4ece\u201c\u5012\u5377\u5929\u6cb3\u201d\u53d8\u4e3a\u201c\u540e\u754c\u5c04 \u65e5\u201d\uff0c\u5251\u5149\u8fc7\u5904\uff0c\u522b\u7684\u4e00\u58f0\uff0c\u5c06\u63d0\u6469\u8fbe\u591a\u7684\u957f\u53d1\u524a\u53bb\u4e00\u7edc\u3002 \u3000\u3000\u63d0\u6469\u8fbe\u591a\u5927\u6012\uff0c\u6709\u62ff\u5411\u5916\u75be\u62cd\uff0c\u5de6\u62f3\u5374\u5411\u5185\u4e00\u62db\uff0c\u6842\u534e\u751f\u6b63\u4f7f\u5230\u201c\u661f\u6d77\u6d6e\u6413\u201d\u7684\u4e00\u62db\uff0c \u4ece\u201c\u5012\u5377\u5929\u6cb3\u201d\u81f3\u201c\u661f\u6d77\u6d6e\u6413\u201d\uff0c\u8fd9\u8fde\u73af\u4e09\u62db\u4e43\u662f\u6842\u534e\u751f\u4ee5\u201c\u8fbe\u6469\u5251\u6cd5\u201d\uff0c\u5408\u4e86\u767d\u8863\u5c11\u5973\u6240 \u521b\u7684\u201c\u51b0\u5ddd\u5251\u6cd5\u201d\u4e4b\u540e\uff0c\u5999\u609f\u51fa\u6765\u7684\u4e8c\u62db\u6781\u5229\u5bb3\u7684\u62db\u6570\uff0c\u8fd9\u4e00\u62db\u5251\u5c16\u659c\u659c\u4e0a\u523a\uff0c\u6b63\u81ea\u4f7f\u5f97\u5f97 \u5fc3\u5e94\u624b\uff0c\u773c\u89c1\u63d0\u6469\u8fbe\u591a\u65e0\u6cd5\u53ef\u907f\uff0c\u835e\u7136\u95f4\u5ffd\u89c9\u6781\u5927\u7684\u6f5c\u529b\uff0c\u5de6\u53f3\u9f50\u6765\uff0c\u4e92\u76f8\u7275\u5f15\uff0c\u6842\u534e\u751f\u987f \u7136\u597d\u50cf\u8eab\u5904\u5728\u4e00\u4e2a\u6781\u4e3a\u6e4d\u6025\u7684\u6f29\u6da1\u4e2d\u5fc3\uff0c\u8eab\u4e0d\u7531\u4e3b\u7684\u88ab\u63a8\u78e8\u5f97\u56e2\u56e2\u4e71\u8f6c\u3002 \u3000\u3000\u5e78\u800c\u6842\u534e\u751f\u5728\u559c\u9a6c\u62c9\u96c5\u5c71\u4e0a\uff0c\u66fe\u7ecf\u548c\u63d0\u6469\u8fbe\u591a\u7ea6\u4e24\u4e2a\u5f1f\u5b50\u6253\u8fc7\u4e00\u4ed7\uff0c\u6df1\u6089\u4ed6\u9634\u9633\u638c\u529b\u7684 \u5965\u5999\uff0c\u6025\u5fd9\u6539\u7528\u9634\u67d4\u4e4b\u529b\uff0c\u4ee5\u8fbe\u6469\u638c\u6cd5\uff0c\u968f\u52bf\u5c48\u4f38\uff0c\u6d88\u89e3\u63d0\u6469\u8fbe\u591a\u653b\u6765\u7684\u6f5c\u529b\uff0c\u867d\u7136\u5982\u6b64\uff0c \u8fd8\u662f\u88ab\u4ed6\u8feb\u5f97\u76f4\u6253\u5708\u5708\u3002 \u3000\u3000\u63d0\u6469\u8fbe\u591a\u4e00\u638c\u7d27\u4f3c\u4e00\u638c\uff0c\u7275\u5f15\u7684\u529b\u9053\u4e5f\u8d8a\u6765\u8d8a\u5927\uff0c\u6842\u534e\u751f\u6697\u53eb\u201c\u4e0d\u5999\u201d\uff0c\u82e5\u7136\u5982\u6b64\u5bf9\u8017 \u4e0b\u53bb\uff0c\u63d0\u6469\u8fbe\u591a\u7684\u529f\u529b\u6bd4\u4ed6\u9ad8\u51fa\u8bb8\u591a\uff0c\u53ea\u6015\u4e0d\u5f85\u70db\u5149\u7184\u706d\uff0c\u81ea\u5df1\u5c31\u8981\u7cbe\u75b2\u529b\u7aed\u3002 \u3000\u3000\u63d0\u6469\u8fbe\u591a\u4ea6\u662f\u5fc3\u4e2d\u7126\u71e5\uff0c\u751f\u6015\u5230\u4e86\u65f6\u9650\uff0c\u4e0d\u80fd\u5c06\u5c11\u5e74\u4eba\u6253\u8d25\uff0c\u4e00\u89c1\u6842\u534e\u751f\u73b0\u51fa\u75b2\u6001\uff0c\u5fc3 \u4e2d\u5927\u559c\uff0c\u731b\u5730\u53cc\u638c\u9f50\u63a8\uff0c\u5168\u7528\u9633\u521a\u4e4b\u529b\uff0c\u638c\u529b\u5982\u72c2\u6d9b\u6012\u98ce\uff0c\u9aa4\u7136\u538b\u81f3\uff0c\u6ee1\u62df\u8fd9\u53cc\u638c\u51fb\u4e0b\uff0c\u6842 \u534e\u751f\u4e0d\u6b7b\u4ea6\u4f24\u3002 \u3000\u3000\u5c82\u6599\u6842\u534e\u751f\u6210\u7af9\u5728\u80f8\uff0c\u89c1\u4ed6\u6539\u7528\u9633\u521a\u638c\u529b\uff0c\u6b63\u5408\u5fc3\u610f\uff0c\u5ffd\u5730\u4e00\u58f0\u957f\u5578\uff0c\u968f\u7740\u638c\u98ce\u76f4\u5347\u51fa \u53bb\uff0c\u6539\u7528\u516b\u5366\u6e38\u8eab\u638c\u6cd5\uff0c\u7ed5\u7740\u63d0\u6469\u8fbe\u591a\u8eab\u5f62\u75be\u8f6c\uff0c\u5251\u5149\u95ea\u70c1\uff0c\u4fe8\u5982\u9ed1\u591c\u7e41\u661f\uff0c\u5343\u70b9\u4e07\u70b9\uff0c\u98d8 \u9152\u4e0b\u6765\uff0c\u4f17\u6b66\u58eb\u770b\u5f97\u76ee\u77aa\u53e3\u5446\uff0c\u4f46\u89c1\u5927\u5385\u4e4b\u4e0a\uff0c\u7adf\u4f3c\u6709\u6570\u767e\u4e2a\u6842\u534e\u751f\uff0c\u5bf9\u63d0\u6469\u8fbe\u591a\u8fd0\u5251\u72c2 \u653b\u3002 \u3000\u3000\u6842\u534e\u751f\u6539\u7528\u5feb\u653b\u7684\u6218\u6cd5\u53cd\u5ba2\u4e3a\u4e3b\uff0c\u5c06\u63d0\u6469\u8fbe\u591a\u4ece\u4e3b\u52a8\u53d8\u4e3a\u88ab\u52a8\uff0c\u679c\u7136\u5927\u5927\u524a\u5f31\u4e86\u4ed6\u9634\u9633 \u638c\u7684\u5a01\u529b\uff0c\u672c\u6765\u82e5\u8bba\u529f\u529b\uff0c\u662f\u63d0\u6469\u8fbe\u591a\u9ad8\u51fa\u8bb8\u591a\uff0c\u82e5\u8bba\u8f7b\u529f\uff0c\u5374\u662f\u6842\u534e\u751f\u7a0d\u80dc\uff0c\u8fd9\u4e00\u8f6e\u5feb \u653b\uff0c\u5251\u70b9\u5982\u96e8\uff0c\u800c\u4e14\u4ed6\u624b\u4e2d\u6240\u6301\u7684\u53c8\u662f\u53ef\u4ee5\u6d1e\u91d1\u524a\u94c1\u7684\u5b9d\u5251\uff0c\u53ea\u8981\u88ab\u4ed6\u5251\u5c16\u622e\u4e0a\u4e00\u70b9\uff0c\u591a\u597d \u5185\u529f\u4e5f\u96be\u7981\u53d7\uff0c\u63d0\u6469\u8fbe\u591a\u5168\u8eab\u5404\u5904\u90fd\u5728\u4ed6\u7684\u5251\u70b9\u653b\u51fb\u4e4b\u5185\uff0c\u8feb\u5f97\u8fd0\u638c\u9632\u8eab\uff0c\u8fd9\u4e00\u6765\u9634\u9633\u638c\u529b \u81ea\u662f\u4e0d\u6613\u53d1\u6325\u3002 \u3000\u3000\u53ef\u662f\u6842\u534e\u751f\u6539\u7528\u8fd9\u6837\u7684\u6218\u6cd5\uff0c\u6bd4\u521a\u624d\u8fd8\u66f4\u5403\u529b\uff0c\u4ed6\u4e4b\u6240\u4ee5\u5982\u6b64\uff0c\u5b9e\u4e43\u884c\u9669\u6c42\u5e78\uff0c\u5e0c\u671b\u5728 \u70db\u7184\u706d\u65f6\uff0c\u53cd\u800c\u5c06\u63d0\u6469\u8fbe\u591a\u51fb\u5012\u3002\u4f46\u63d0\u6469\u8fbe\u591a\u4e43\u662f\u5f53\u4eca\u7684\u6b66\u5b66\u5927\u5e08\uff0c\u4e0d\u4e45\u4fbf\u660e\u5176\u7406\u3002\u53ea\u89c1\u4ed6 \u51dd\u571f\u5982\u5c71\uff0c\u4efb\u7531\u6842\u534e\u751f\u5f3a\u653b\u731b\u6253\uff0c\u811a\u6b65\u7adf\u4e0d\u79fb\u52a8\u5206\u6beb\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u6842\u534e\u751f\u4f46\u89c9\u6c14\u5598\u5fc3\u8df3\uff0c\u989d\u4e0a\u6c57\u73e0\u4e00\u9897\u9897\u6ef4\u4e86\u4e0b\u6765\uff0c\u63d0\u6469\u8fbe\u591a\u8138\u4e0a\u67e0\u7b11\uff0c\u53cc\u638c \u5212\u5f27\uff0c\u4e00\u6b65\u4e00\u6b65\u7684\u8feb\u5c06\u51fa\u53bb\uff0c\u6842\u534e\u751f\u53c8\u4f3c\u521d\u4e0a\u4e4b\u65f6\u4e00\u6837\uff0c\u611f\u5230\u6709\u5982\u8eab\u5904\u6f29\u6da1\u7684\u4e2d\u5fc3\uff0c\u8fdb\u65e2\u4e0d \u80fd\uff0c\u9000\u4ea6\u4e0d\u5f97\u3002 \u3000\u3000\u518d\u8fc7\u4e00\u4f1a\uff0c\u6842\u534e\u751f\u66f4\u611f\u4e0d\u652f\uff0c\u4f46\u89c9\u8033\u9f13\u55e1\u55e1\u4f5c\u54cd\uff0c\u773c\u524d\u91d1\u661f\u8ff8\u73b0\uff0c\u8fd9\u662f\u529b\u8131\u795e\u75b2\u4e4b\u8c61\uff0c \u6842\u534e\u751f\u594b\u529b\u518d\u62c6\u51e0\u62db\uff0c\u731b\u542c\u5f97\u63d0\u6469\u8fbe\u591a\u5927\u559d\u4e00\u58f0\uff0c\u4e00\u638c\u5288\u6765\uff0c\u6842\u534e\u751f\u8ddf\u8ddf\u75be\u9000\u51e0\u6b65\uff0c\u63d0\u6469\u8fbe \u591a\u5982\u5f71\u968f\u5f62\uff0c\u7b2c\u4e8c\u638c\u53c8\u8fde\u73af\u51fb\u5230\uff0c\u6842\u534e\u751f\u672c\u80fd\u7684\u8fd0\u5251\u4e00\u6863\uff0c\u8eab\u5f62\u518d\u9000\u51e0\u6b65\uff0c\u53cc\u817f\u9178\u8f6f\uff0c\u5373\u5c06 \u4ec6\u5012\uff0c\u63d0\u6469\u8fbe\u591a\u7b2c\u4e09\u638c\u53c8\u62cd\u4e86\u8fc7\u6765\uff0c\u6842\u534e\u751f\u88ab\u4ed6\u7684\u5b66\u529b\u7275\u5f15\uff0c\u867d\u6b32\u540e\u9000\uff0c\u5df2\u662f\u529b\u4e0d\u4ece\u5fc3\uff0c\u53cd \u800c\u88ab\u5b83\u7684\u5b66\u529b\u8f66\u5f97\u4e0a\u524d\u51e0\u6b65\uff0c\u5929\u7075\u76d6\u6b63\u6b63\u8fce\u7740\u4ed6\u7684\u638c\u5fc3\uff0c\u8fd9\u4e00\u638c\u5288\u4e0b\uff0c\u7109\u6709\u547d\u5728\uff1f \u3000\u3000\u5374\u5c31\u5728\u8fd9\u7d27\u5f20\u7684\u65f6\u523b\uff0c\u63d0\u6469\u8fbe\u591a\u4e00\u58f0\u957f\u53f9\uff0c\u53cc\u638c\u4e00\u679a\uff0c\u8bf4\u9053\uff1a\u201c\u4e2d\u56fd\u7684\u6b66\u529f\uff0c\u679c\u662f\u795e\u5947 \u83ab\u6d4b\uff0c\u518d\u8fc7\u5341\u5e74\uff0c\u53ea\u6015\u6211\u4e5f\u4e0d\u662f\u4f60\u7684\u5bf9\u624b\u4e86\u3002\u597d\uff0c\u65f6\u9650\u5df2\u5230\uff0c\u6211\u653e\u4f60\u8d70\u5427\u3002\u201d\u6842\u534e\u751f\u5598\u606f\u7a0d \u5b9a\uff0c\u7741\u773c\u4e00\u77a7\uff0c\u53ea\u89c1\u4e1c\u5357\u89d2\u73bb\u7483\u67f1\u5185\u90a3\u652f\u70db\u5149\u5df2\u662f\u5b8c\u5168\u7184\u706d\u3002 \u3000\u3000\u6842\u534e\u751f\u62b1\u62f3\u7b11\u9053\uff1a\u201c\u591a\u8c22\u5927\u5e08\u6307\u6559\uff0c\u54b1\u4eec\u540e\u4f1a\u6709\u671f\u3002\u201d\u63d0\u6469\u8fbe\u591a\u795e\u6001\u600f\u600f\uff0c\u6b63\u6b32\u9000\u4e0b\uff0c \u5ffd\u542c\u5230\u5fa1\u6797\u519b\u603b\u7ba1\u7684\u547b\u541f\u4e4b\u58f0\uff0c\u53ea\u89c1\u90a3\u4e2a\u7ea2\u8863\u756a\u50e7\u6276\u7740\u4ed6\u7f13\u7f13\u8d70\u51fa\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c \u4f60\u7528\u4ec0\u4e48\u5996\u6cd5\uff0c\u5c06\u603b\u7ba1\u5927\u4eba\u6298\u78e8\u6210\u4e86\u8fd9\u6837\uff1f\u201d \u3000\u3000\u539f\u6765\u5fa1\u6797\u519b\u603b\u7ba1\u88ab\u6842\u534e\u751f\u7528\u72ec\u95e8\u624b\u6cd5\uff0c\u70b9\u4e2d\u4e86\u201c\u5929\u67a2\u7a74\u201d\uff0c\u8fd9\u201c\u5929\u67a2\u7a74\u201d\u662f\u80cc\u540e\u5341\u516b\u9053 \u5927\u7a74\u7684\u603b\u7eb2\uff0c\u6842\u534e\u751f\u7684\u70b9\u7a74\u6cd5\u5947\u5999\u975e\u5e38\uff0c\u521d\u65f6\u4e0d\u8fc7\u9ebb\u75d2\uff0c\u6e10\u6e10\u4fbf\u4f3c\u4f53\u5185\u63d2\u8fdb\u4e86\u5343\u767e\u9053\u94f6\u9488\u4e00 \u6837\uff0c\u65e2\u5947\u75d2\u800c\u53c8\u5947\u75db\uff0c\u9976\u8fd9\u5fa1\u6797\u519b\u603b\u7ba1\u662f\u94c1\u77f3\u822c\u7684\u6c49\u5b50\uff0c\u4ea6\u81ea\u7981\u53d7\u4e0d\u8d77\u3002 \u3000\u3000\u6842\u534e\u751f\u7b11\u9053\uff1a\u201c\u603b\u7ba1\u5927\u4eba\uff0c\u4f60\u4ee5\u4e3a\u6211\u5e08\u7236\u4e0d\u4f1a\u89e3\u6bd2\uff0c\u5176\u5b9e\u6211\u5e08\u5f92\u4e8c\u4eba\uff0c\u90fd\u662f\u65e2\u80fd\u653e\u6bd2\uff0c \u53c8\u80fd\u89e3\u6bd2\u7684\u80fd\u624b\u3002\u6211\u53ea\u662f\u5c0f\u65bd\u5176\u6280\uff0c\u5728\u603b\u7ba1\u5927\u4eba\u8eab\u4e0a\uff0c\u643d\u4e0a\u4e86\u4e00\u70b9\u6bd2\u836f\u800c\u5df2\uff0c\u8981\u662f\u6211\u7528\u91cd\u6bd2 \u7684\u8bdd\uff0c\u5927\u4eba\u65e9\u5df2\u4e03\u7a8d\u6d41\u8840\u4e86\u3002\u800c\u4eca\u6211\u7528\u7684\u8f7b\u5fae\u7684\u6bd2\u836f\uff0c\u5927\u4eba\u4f60\u53ef\u4ee5\u591a\u6d3b\u4e03\u5929\u3002\u201d \u3000\u3000\u5fa1\u6797\u519b\u603b\u7ba1\u90a3\u77e5\u9053\u6842\u534e\u751f\u662f\u5904\u58f0\u606b\u5413\uff0c\u542c\u4ed6\u8fd9\u4e48\u4e00\u8bf4\uff0c\u66f4\u89c9\u5947\u75db\u5947\u75d2\uff0c\u9b42\u98de\u9b44\u6563\uff0c\u6323\u624e \u7740\u559d\u9053\uff1a\u201c\u4f60\u600e\u6562\u5982\u6b64\uff0c\u597d\uff0c\u6211\u8981\u4f60\u788e\u4e07\u6bb5\uff01\u201d\u6842\u534e\u751f\u5927\u7b11\u9053\uff1a\u201c\u603b\u7ba1\u5927\u4eba\uff0c\u4f60\u82e5\u628a\u788e\u4e07 \u6bb5\uff0c\u66f4\u6ca1\u6709\u4eba\u4f60\u89e3\u6bd2\u4e86\u3002\u201d \u3000\u3000\u63d0\u6469\u8fbe\u591a\u7741\u773c\u4e00\u77a7\uff0c\u770b\u51fa\u5fa1\u6797\u519b\u603b\u7ba1\u662f\u4e2d\u4e86\u6842\u534e\u751f\u7684\u91cd\u624b\u6cd5\u70b9\u7a74\uff0c\u4f46\u5fa1\u6797\u519b\u603b\u7ba1\u5fcd\u53d7\u4e0d \u4f4f\u80cc\u4e0a\u7684\u5947\u75d2\uff0c\u53cc\u624b\u4e71\u722c\u4e71\u6293\uff0c\u65e9\u5df2\u628a\u80cc\u4e0a\u7684\u8863\u88d8\u6293\u88c2\uff0c\u80cc\u810a\u7ea2\u80bf\u575f\u8d77\uff0c\u771f\u5982\u4e2d\u6bd2\u4e00\u822c\u3002\u63d0 \u6469\u8fbe\u591a\u770b\u4e86\uff0c\u4ea6\u89c9\u60ca\u5fc3\u3002\u4ed6\u5bf9\u4e8e\u4e2d\u56fd\u7684\u70b9\u7a74\u6cd5\u4ea6\u7a0d\u77e5\u4e00\u4e8c\uff0c\u4f46\u89c1\u4e86\u5982\u6b64\u60c5\u52bf\uff0c\u6df1\u77e5\u51ed\u7740\u81ea\u5df1 \u6240\u5b66\uff0c\u7edd\u4e0d\u80fd\u89e3\u3002\u82e5\u8981\u4ed6\u4ee3\u603b\u7ba1\u5411\u6842\u534e\u751f\u6c42\u6cbb\uff0c\u90a3\u5374\u662f\u5927\u5931\u4ed6\u9762\u5b50\u4e4b\u4e8b\u3002\u6545\u6b64\u4ed6\u770b\u4e86\u4e00\u773c\uff0c \u5e76\u4e0d\u8bf4\u7834\uff0c\u90a3\u7ea2\u8863\u756a\u50e7\u6b63\u6b32\u6c42\u4ed6\u518d\u6b21\u51fa\u624b\uff0c\u63d0\u6469\u8fbe\u591a\u5df2\u5148\u6447\u5934\u8bf4\u9053\uff1a\u201c\u6211\u8bf4\u4e00\u662f\u4e00\uff0c\u8bf4\u4e8c\u662f \u4e8c\uff0c\u8fd9\u91cc\u7684\u4e8b\u6211\u8bf4\u8fc7\u4e0d\u7ba1\u4fbf\u4e0d\u7ba1\u4e86\u3002\u201d \u3000\u3000\u5fa1\u6797\u519b\u603b\u7ba1\u89c1\u63d0\u6469\u8fbe\u591a\u6492\u624b\u4e0d\u7ba1\uff0c\u66f4\u89c9\u80c6\u5bd2\uff0c\u653e\u8f6f\u4e86\u53e3\u6c14\u8bf4\u9053\uff1a\u201c\u4f60\u5f85\u5982\u4f55\uff1f\u201d\u6842\u534e\u751f \u9053\uff1a\u201c\u4f60\u5e26\u6211\u8fdb\u5bab\u4e2d\u66ff\u56fd\u738b\u6cbb\u75c5\uff0c\u6211\u4e5f\u4fbf\u66ff\u4f60\u6cbb\u75c5\u3002\u201d\u5fa1\u6797\u519b\u603b\u7ba1\u8e0c\u8e87\u96be\u51b3\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a \u201c\u82e5\u7ed9\u4ed6\u5728\u56fd\u738b\u9762\u524d\u8bf4\u51fa\u6211\u4eca\u665a\u6240\u4e3a\uff0c\u6211\u8fd8\u6709\u547d\u4e48\uff1f\u201d \u3000\u3000\u6b63\u5728\u6b64\u65f6\uff0c\u5ffd\u542c\u5f97\u5916\u9762\u90a3\u91cd\u94c1\u95e8\u8f67\u8f67\u58f0\u54cd\uff0c\u5fa1\u6797\u519b\u603b\u7ba1\u5927\u5403\u4e00\u60ca\uff0c\u60f3\u9053\uff1a\u201c\u5b88\u95e8\u7684\u6b66\u58eb \u600e\u6562\u4e0d\u542c\u6211\u7684\u5429\u5490\u5c31\u628a\u94c1\u95e8\u5f00\u4e86\uff1f\u201d\u53ef\u662f\u4ed6\u5728\u5947\u75db\u653b\u5fc3\u4e4b\u4e0b\uff0c\u65e9\u5df2\u6709\u6c14\u6ca1\u529b\u3002\u60f3\u53f1\u9a82\u4e5f\u9a82\u4e0d \u51fa\u6765\u3002 \u3000\u3000\u4f46\u89c1\u94c1\u95e8\u4e00\u5f00\uff0c\u5927\u5385\u4e2d\u7684\u6b66\u58eb\u773c\u775b\u4e00\u4eae\uff0c\u767b\u65f6\u9e26\u96c0\u65e0\u58f0\u3002\u6842\u534e\u751f\u4e5f\u4e3a\u8fd9\u610f\u5916\u4e4b\u58f0\u611f\u5230\u5947 \u602a\uff0c\u6025\u5fd9\u7740\u65f6\uff0c\u53ea\u89c1\u4e24\u4e2a\u5bab\u5973\uff0c\u8f7b\u79fb\u83b2\u6b65\uff0c\u7f13\u7f13\u7684\u8d70\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u8d70\u4e0a\u77f3\u9636\uff0c\u4e3a\u9996\u7684\u5bab\u5973\u7ad9\u5b9a\uff0c\u5a07\u58f0\u575c\u575c\u7684\u8bf4\u9053\uff1a\u201c\u8fd9\u91cc\u6709\u4e00\u4e2a\u4e2d\u56fd\u6765\u7684\u9752\u5e74\uff0c\u540d\u53eb\u6842\u534e \u751f\u7684\u4e48\uff1f\u201d\u6842\u534e\u751f\u60ca\u5947\u4e4b\u6781\uff0c\u600e\u4e48\u6837\u5730\u60f3\u4e0d\u5230\u4ed6\u521a\u624d\u5230\u52a0\u5fb7\u6ee1\u90fd\uff0c\u6df1\u5bab\u4e4b\u4e2d\uff0c\u7adf\u7136\u4e5f\u77e5\u9053\u662f \u4ed6\u6765\u4e86\u3002\u90a3\u5bab\u5973\u53c8\u95ee\u4e86\u4e00\u58f0\uff0c\u6842\u534e\u751f\u8e0f\u4e0b\u96e8\u6b65\uff0c\u9053\uff1a\u201c\u6211\u4fbf\u662f\u4ece\u4e2d\u56fd\u6765\u7684\u6842\u534e\u751f\u3002\u201d \u3000\u3000\u90a3\u5bab\u5973\u9053\uff1a\u201c\u56fd\u738b\u6709\u547d\uff0c\u8bf7\u4f60\u7acb\u5373\u8fdb\u5bab\u3002\u603b\u7ba1\u5927\u4eba\uff0c\u8fd9\u662f\u56fd\u738b\u7684\u5723\u65e8\uff0c\u53eb\u6211\u4eec\u6765\u4f60\u8fd9\u91cc \u8981\u4eba\uff0c\u4f60\u770b\u6e05\u695a\u4e86\uff01\u201d\u8bf4\u7f62\u4fbf\u5c06\u5723\u65e8\u4ea4\u7ed9\u4e00\u4e2a\u6b66\u58eb\uff0c\u53eb\u4ed6\u8f6c\u4ea4\u7ed9\u5fa1\u6797\u519b\u603b\u7ba1\u3002 \u3000\u3000\u6842\u534e\u751f\u9053\uff1a\u201c\u672a\u77e5\u8d35\u56fd\u738b\u4f55\u4e8b\u89c1\u53ec\uff1f\u201d\u90a3\u5bab\u5973\u9053\uff1a\u201c\u95fb\u8bf4\u5148\u751f\u4ece\u4e2d\u534e\u4e0a\u56fd\u800c\u6765\uff0c\u6df1\u901a\u533b \u672f\uff0c\u662f\u4ee5\u8bf7\u5148\u751f\u8fdb\u5bab\u6cbb\u75c5\u3002\u201d\u6842\u534e\u751f\u6014\u4e86\u4e00\u6014\uff0c\u5fc3\u9053\uff1a\u201c\u6211\u90a3\u91cc\u61c2\u5f97\u4ec0\u4e48\u533b\u672f\uff0c\u521a\u624d\u6240\u8bf4\u4e43 \u662f\u6545\u610f\u5413\u5413\u5fa1\u6797\u519b\u603b\u7ba1\u7684\uff0c\u5374\u60f3\u4e0d\u5230\u8fd9\u4e00\u7eb8\u8bcf\u4e66\u5012\u53cd\u800c\u66ff\u6211\u5b8c\u8c0e\u4e86\u3002\u201d\u8f6c\u5ff5\u4e00\u60f3\uff0c\u53ea\u8981\u5df4\u52d2 \u8bca\u65ad\u65e0\u5dee\uff0c\u56fd\u738b\u7684\u75db\u771f\u662f\u4e2d\u4e86\u6162\u6027\u7684\u6bd2\u836f\u7684\u8bdd\uff0c\u4ed7\u7740\u5929\u5c71\u96ea\u83b2\uff0c\u6599\u60f3\u53ef\u4ee5\u89e3\u6551\uff0c\u773c\u73e0\u4e00\u8f6c\uff0c \u4fbf\u5bf9\u5bab\u5973\u8bf4\u9053\uff1a\u201c\u8981\u6211\u6cbb\u75c5\u4e0d\u96be\uff0c\u4f46\u8fd8\u5f97\u4e24\u4eba\u540c\u53bb\u3002\u201d\u90a3\u5bab\u5973\u9053\uff1a\u201c\u56fd\u738b\u6709\u547d\uff0c\u8d35\u5ba2\u6709\u6240\u8bf7 \u6c42\uff0c\u4ea6\u65e0\u4e0d\u7167\u51c6\u3002\u201d\u6842\u534e\u751f\u9053\uff1a\u201c\u90a3\u4e48\u6211\u4fbf\u8981\u8bf7\u5df4\u52d2\u533b\u751f\u548c\u5fa1\u6797\u519b\u603b\u7ba1\u5927\u4eba\u8d54\u6211\u8fdb\u5bab\u4e00 \u8d9f\u3002\u201d\u5fa1\u6797\u519b\u603b\u7ba1\u9762\u5982\u6b7b\u7070\uff0c\u5ffd\u89c1\u7ea2\u8863\u756a\u50e7\u4e0a\u524d\u8bf4\u9053\uff1a\u201c\u96be\u5f97\u4e0a\u56fd\u8d35\u5bbe\u4e07\u91cc\u8fd8\u6765\uff0c\u603b\u7ba1\u5927\u4eba \u7406\u8be5\u966a\u4ed6\u8fdb\u5bab\u4e00\u8d9f\u3002\u201d\u5fa1\u6797\u519b\u603b\u7ba1\u770b\u51fa\u4ed6\u773c\u775b\u4e2d\u7684\u793a\u610f\uff0c\u4fbf\u5373\u5e94\u5141\u3002\u6842\u534e\u751f\u5fc3\u91cc\u8d77\u7591\uff0c\u4f46\u60f3 \u53ea\u8981\u79bb\u5f00\u4e86\u8fd9\u513f\uff0c\u8fd8\u6015\u4ed6\u4ec0\u4e48\u8be1\u8ba1\u3002 \u3000\u3000\u5fa1\u6797\u519b\u603b\u7ba1\u9053\uff1a\u201c\u4e0b\u5b98\u5076\u611f\u4e0d\u9002\uff0c\u8bf7\u5148\u751f\u8d50\u4ee5\u826f\u836f\uff0c\u624d\u597d\u5949\u966a\u3002\u201d\u4ed6\u4e0d\u6562\u5728\u5bab\u5973\u9762\u524d\uff0c \u8bf4\u51fa\u88ab\u6842\u534e\u751f\u201c\u4e0b\u6bd2\u201d\u7684\u4e8b\uff0c\u6545\u6b64\u53ea\u6709\u79c1\u5411\u6842\u534e\u751f\u6c42\u60c5\u3002\u5bab\u5973\u8be7\u9053\uff1a\u201c\u603b\u7ba1\u5927\u4eba\u8fd9\u6837\u597d\u7684\u4f53 \u9b44\u4e5f\u751f\u75c5\u4e48\uff1f\u82e5\u662f\u5c0f\u75c5\uff0c\u4e0d\u5982\u8bf7\u5219\u4e2a\u533b\u751f\u6cbb\u5427\uff0c\u660e\u65e5\u4f60\u518d\u9020\u5bab\u4e5f\u4fbf\u662f\u4e86\u3002\u201d\u5fa1\u6797\u519b\u603b\u7ba1\u6025 \u9053\uff1a\u201c\u4e0d\uff0c\u4e0d\uff0c\u6211\u8fd9\u75c5\u975e\u5f97\u4e2d\u56fd\u795e\u533b\u6cbb\u7406\u4e0d\u53ef\u3002\u201d\u6842\u534e\u751f\u5fae\u5fae\u4e00\u7b11\uff0c\u4f38\u624b\u642d\u4e0a\u4e86\u5fa1\u6797\u519b\u603b\u7ba1 \u7684\u80a9\u5934\uff0c\u8f7b\u8f7b\u7684\u5728\u4ed6\u7684\u201c\u8d1e\u767d\u7a74\u201d\u70b9\u4e86\u4e00\u4e0b\uff0c\u8bf4\u9053\uff1a\u201c\u5219\u4e2a\u533b\u751f\u8981\u836f\u5230\u56de\u6625\uff0c\u6211\u5219\u662f\u624b\u5230\u56de \u6625\u3002\u5f85\u6cbb\u597d\u56fd\u738b\u4e4b\u540e\uff0c\u6211\u518d\u7ed9\u4f60\u5c06\u75c5\u6839\u5b8c\u5168\u6d88\u6389\u3002\u201d\u5fa1\u6797\u519b\u603b\u7ba1\u7ecf\u4ed6\u4e00\u89e6\uff0c\u679c\u7136\u8212\u670d\u4e86\u8bb8 \u591a\uff0c\u53ea\u662f\u80f8\u53e3\u8fd8\u9690\u9690\u4f5c\u75db\uff0c\u53ea\u597d\u4e56\u4e56\u7684\u8ddf\u7740\u6842\u534e\u751f\u8d70\u3002 \u3000\u3000\u90a3\u4e24\u4e2a\u5bab\u5973\u597d\u4e0d\u5947\u5f02\uff0c\u6084\u6084\u8033\u8bed\uff0c\u6842\u534e\u751f\u662f\u5177\u6709\u4e0a\u4e58\u5185\u529f\u7684\u4eba\uff0c\u51dd\u795e\u4e00\u542c\uff0c\u5728\u767e\u6b65\u4e4b \u5185\uff0c\u6781\u5fae\u7ec6\u7684\u58f0\u97f3\u4e5f\u53ef\u4ee5\u542c\u5f97\u6e05\u6e05\u695a\u695a\u3002\u4f46\u542c\u5f97\u4e00\u4e2a\u5bab\u5973\u8bf4\u9053\uff1a\u201c\u8fd9\u4e2a\u4e2d\u56fd\u5c11\u5e74\u679c\u7136\u4e0d\u51e1\uff0c \u8bf4\u4e0d\u5b9a\u4ed6\u771f\u80fd\u6cbb\u597d\u56fd\u738b\u7684\u602a\u75c5\u3002\u201d\u53e6\u4e00\u4e2a\u9053\uff1a\u201c\u4ed6\u5e94\u8be5\u662f\u4e2a\u7f8e\u7537\u5b50\u624d\u662f\uff0c\u5374\u4e3a\u4f55\u4e5f\u50cf\u54b1\u4eec\u56fd \u4e2d\u7684\u7c97\u6c49\u4e00\u6837\uff0c\u8fd9\u5f20\u9762\u5b54\u9ed1\u5f97\u597d\u4e0d\u6015\u4eba\u3002\u201d\u6842\u534e\u751f\u4e0d\u7981\u54d1\u7136\u5931\u7b11\uff0c\u626f\u4e0b\u4e86\u5706\u684c\u4e0a\u6d01\u767d\u7684\u684c \u5e03\uff0c\u5f80\u9762\u5b54\u4e00\u62b9\uff0c\u5c06\u7164\u7070\u62b9\u5f97\u5e72\u5e72\u51c0\u51c0\uff0c\u767b\u65f6\u9732\u51fa\u4e86\u672c\u6765\u9762\u76ee\uff0c\u4eca\u90a3\u4e24\u4e2a\u5bab\u5973\u770b\u5f97\u5446\u4e86\u3002 \u3000\u3000\u6842\u534e\u751f\u7b11\u9053\uff1a\u201c\u597d\u5566\uff0c\u54b1\u4eec\u53ef\u4ee5\u8d70\u5566\u3002\u201d\u62d6\u7740\u5fa1\u6797\u519b\u603b\u7ba1\uff0c\u8d70\u4e0b\u77f3\u9636\uff0c\u7a7f\u51fa\u4e86\u6570\u9053\u94c1 \u95e8\uff0c\u65e0\u4eba\u6562\u4e88\u62e6\u963b\u3002\u95e8\u5916\u65e9\u505c\u6709\u4e00\u8f86\u9a6c\u8f66\uff0c\u5bbd\u655e\u534e\u4e3d\uff0c\u62d6\u8f66\u7684\u56db\u53ea\u9a6c\u6bdb\u8272\u96ea\u767d\uff0c\u795e\u7ae3\u975e\u5e38\uff0c \u8fd9\u6b63\u662f\u4e24\u4e2a\u5bab\u5973\u4e58\u6765\u7684\u9a6c\u8f66\uff0c\u6842\u534e\u751f\u3001\u5df4\u52d2\u7b49\u4f9d\u6b21\u4e0a\u8f66\uff0c\u56db\u5339\u767d\u9a6c\uff0c\u7acb\u523b\u626c\u8e44\u75be\u8d70\u3002 \u3000\u3000\u6842\u534e\u751f\u60f3\u8d77\u8fd9\u4e00\u65e5\u4e00\u591c\u79cd\u79cd\u5947\u602a\u7684\u906d\u9047\uff0c\u771f\u5982\u540c\u505a\u4e86\u4e00\u573a\u5927\u68a6\u4e00\u822c\uff0c\u5fc3\u4e2d\u4e0d\u4f4f\u5728\u60f3\uff1a \u201c\u56fd\u738b\u600e\u4e48\u77e5\u9053\u6211\u8fd9\u4e2a\u4eba\uff1f\u800c\u4e14\u77e5\u9053\u6211\u80fd\u7ed9\u4ed6\u6cbb\u75c5\uff1f\u90a3\u4e24\u4e2a\u5bab\u5973\u4e3a\u4ec0\u4e48\u8bf4\u6211\u2018\u5e94\u8be5\u2019\u662f\u4e2a\u7f8e \u7537\u5b50\uff1f\u8fd9\u2018\u5e94\u8be5\u2019\u4e24\u5b57\u4ec0\u4e48\u610f\u601d\uff1f\u201d\u5077\u773c\u770b\u90a3\u4e24\u4e2a\u5bab\u5973\uff0c\u53ea\u89c1\u5979\u4eec\u4e5f\u6b63\u5728\u5077\u770b\u4ed6\uff0c\u8138\u4e0a\u8fd8\u6d6e \u51fa\u8be1\u5f02\u7684\u5fae\u7b11\u3002 \u3000\u3000\u6842\u534e\u751f\u6b63\u81ea\u5fc3\u601d\u7eb7\u4e71\uff0c\u5ffd\u542c\u5f97\u80cc\u540e\u6709\u547c\u547c\u7684\u98ce\u58f0\uff0c\u56de\u5934\u4e00\u770b\uff0c\u5927\u5403\u4e00\u60ca\u3002\u7adf\u7136\u662f\u63d0\u6469\u8fbe \u591a\u98de\u5954\u800c\u6765\uff01 \u3000\u3000\u6842\u534e\u751f\u5927\u5403\u4e00\u60ca\uff0c\u62a2\u8fc7\u9a6c\u97ad\uff0c\u75be\u97ad\u90a3\u62c9\u8f66\u7684\u56db\u5339\u767d\u9a6c\u3002\u90a3\u56db\u5339\u767d\u9a6c\u4e43\u662f\u5c3c\u6cca\u5c14\u7684\u5fa1\u9b54\u540d \u9a6c\uff0c\u88ab\u6842\u534e\u751f\u518d\u4e00\u7528\u529b\u97ad\u6253\uff0c\u767b\u65f6\u53d1\u529b\u72c2\u5954\uff0c\u9a6c\u8e44\u5f97\u5f97\uff0c\u8f66\u58f0\u9686\u9686\uff0c\u8f66\u4e0a\u7684\u51e0\u4e2a\u4eba\u7ed9\u629b\u8d77\u8dcc \u843d\u3002\u771f\u5982\u817e\u4e91\u9a7e\u96fe\u4e00\u822c\uff01 \u3000\u3000\u5374\u4e0d\u6599\u63d0\u6469\u8fbe\u591a\u7684\u811a\u529b\u8d5b\u5982\u5954\u9a6c\uff0c\u4f46\u542c\u5f97\u4ed6\u7684\u602a\u7b11\u4e4b\u58f0\uff0c\u6447\u66f3\u957f\u7a7a\uff0c\u81ea\u540e\u8ffd\u6765\uff0c\u8d8a\u6765\u8d8a \u8fd1\uff0c\u731b\u7136\u95f2\u90a3\u56db\u5339\u767d\u9a6c\u4e00\u58f0\u957f\u5636\uff0c\u5341\u516d\u53ea\u9a6c\u8e44\u4e00\u9f50\u8dea\u5012\uff0c\u539f\u6765\u662f\u63d0\u6469\u8fbe\u591a\u5df2\u7136\u8ffd\u5230\uff0c\u6500\u7740\u8f66 \u8f95\uff0c\u4e00\u6309\u4e4b\u4e0b\u3002\u56db\u5339\u5065\u9a6c\uff0c\u7adf\u662f\u4e0d\u80fd\u518d\u8d70\u4e00\u6b65\uff01 \u3000\u3000\u6842\u534e\u751f\u559d\u9053\uff1a\u201c\u4f60\u8bf4\u8fc7\u7684\u8bdd\u4e0d\u7b97\u6570\u4e48\uff1f\u201d\u63d0\u6469\u8fbe\u591a\u7b11\u9053\uff1a\u201c\u6211\u53ea\u8bf4\u8fc7\u4e0d\u7ba1\u53e4\u5821\u91cc\u9762\u7684\u4e8b \u60c5\uff0c\u5982\u4eca\u51fa\u4e86\u53e4\u5821\uff0c\u6211\u53ef\u8981\u7ba1\u5566\uff01\u201d\u4e00\u624b\u6500\u7740\u8f66\u8f95\uff0c\u7acb\u523b\u98de\u8eab\u800c\u8d77\u3002\u4fbf\u5f85\u8df3\u4e0a\u9a6c\u8f66\uff0c\u6842\u534e\u751f \u65e9\u5df2\u62d4\u51fa\u5b9d\u5251\uff0c\u4e00\u62db\u201c\u674e\u5e7f\u5c04\u77f3\u201d\u3002\u5e73\u80f8\u524c\u51fa\uff0c\u63d0\u6469\u8fbe\u591a\u970d\u5730\u4e00\u4e2a\u201c\u51e4\u70b9\u5934\u201d\uff0c\u4f38\u624b\u53cc\u63e9\u4fbf \u6765\u593a\u5251\uff0c\u6842\u534e\u751f\u53d8\u62db\u5feb\u6781\u3002\u5251\u950b\u4e00\u8f6c\uff0c\u7acb\u523b\u524a\u4ed6\u624b\u6307\uff0c\u63d0\u6469\u8fbe\u591a\u679c\u7136\u4e0d\u6127\u662f\u963f\u523a\u4f2f\u8bf8\u56fd\u7684\u7b2c \u4e00\u9ad8\u624b\uff0c\u53f3\u638c\u4e00\u7ffb\uff0c\u8eab\u4e8e\u51cc\u7a7a\u3002\u5de6\u638c\u6761\u7684\u62bd\u51fa\uff0c\u638c\u529b\u4e00\u9707\uff0c\u4fbf\u5c06\u6842\u534e\u751f\u7684\u5251\u5c16\u9707\u6b6a\uff0c\u53f3\u624b\u53cc \u63e9\uff0c\u4ecd\u7136\u95ea\u7535\u822c\u7684\u8ff3\u53d6\u6842\u534e\u751f\u53cc\u76ee\u3002 \u3000\u3000\u6842\u534e\u751f\u8feb\u5f97\u5411\u540e\u9000\u4e86\u4e00\u6b65\u3002\u6324\u5f97\u4e00\u4e2a\u5bab\u5973\u201c\u5431\u54df\u54df\u201d\u7684\u53eb\u51fa\u58f0\u6765\uff0c\u63d0\u6469\u8fbe\u591a\u53cc\u811a\u8e0f\u7740\u8f66 \u8f6e\u3002\u5927\u534a\u8fb9\u8eab\u5b50\u5df2\u4fef\u8fc7\u8f66\u8f95\uff0c\u53ea\u8981\u7528\u529b\u4e00\u6491\u3002\u7acb\u523b\u4fbf\u53ef\u8df3\u5230\u8f66\u4e0a\u3002 \u3000\u3000\u6842\u534e\u751f\u8eab\u5f62\u4e00\u4fef\uff0c\u8fde\u5251\u5982\u98ce\u3002\u6025\u523a\u63d0\u6469\u8fbe\u591a\u7684\u54bd\u5589\u3002\u4f46\u542c\u5f97\u201c\u5580\u5566\u201d\u4e00\u54cd\uff0c\u63d0\u6469\u8fbe\u591a\u4f7f \u51fa\u9634\u9633\u638c\u529b\u3002\u5c06\u6842\u534e\u751f\u7684\u5b9d\u5251\u5f15\u5f00\uff0c\u6842\u534e\u751f\u6ca1\u6709\u523a\u7740\u63d0\u6469\u8fbe\u591a\u3002\u5374\u628a\u8f66\u8f95\u7684\u4e00\u6839\u6a2a\u6728\u524a\u65ad \u4e86\uff01 \u3000\u3000\u4e24\u4e2a\u6b66\u529f\u7edd\u9876\u7684\u9ad8\u624b\uff0c\u4e00\u5728\u8f66\u5916\uff0c\u4e00\u5728\u8f66\u5185\uff0c\u5404\u4ee5\u6700\u4e0a\u4e58\u7684\u6b66\u529f\u76f8\u640f\uff0c\u5176\u95f4\u4e1d\u6beb\u6ca1\u6709\u56de \u65cb\u4e4b\u5730\u3002\u6bcf\u4e00\u62db\u90fd\u8db3\u4ee5\u81f4\u547d\uff0c\u6bd4\u8d77\u9002\u624d\u5728\u5927\u5385\u5185\u62fc\u6597\uff0c\u51f6\u9669\u4f55\u6b62\u767e\u500d\uff01\u4f46\u89c1\u63d0\u6469\u8fbe\u591a\u5316\u557c\u4e00 \u58f0\uff0c\u53cc\u811a\u4e00\u70b9\u8f66\u8f6e\u3002\u8eab\u4e8e\u5df2\u51cc\u7a7a\u800c\u8d77\uff0c\u4e00\u638c\u9707\u5f00\u6842\u534e\u751f\u7684\u5b9d\u5251\uff0c\u53e6\u4e00\u638c\u5c31\u7167\u7740\u6842\u534e\u751f\u7684\u80f8\u53e3 \u5288\u6765\u3002 \u3000\u3000\u6842\u534e\u751f\u6697\u53eb\u4e0d\u5999\uff0c\u767e\u5fd9\u4e4b\u4e2d\uff0c\u65e0\u6687\u601d\u7d22\uff0c\u672c\u80fd\u7684\u51fa\u638c\u76f8\u6297\u3002\u5b9d\u5251\u8ddf\u7740\u4e5f\u5212\u4e86\u4e00\u9053\u534a\u5f27\uff0c \u8f6c\u8fc7\u5251\u950b\uff0c\u4fa7\u88ad\u5f3a\u654c\uff0c\u4ed6\u4e5f\u660e\u77e5\u81ea\u5df1\u7684\u638c\u529b\u62b5\u654c\u4e0d\u4f4f\u3002\u7136\u800c\u5728\u8fd9\u6837\u7edd\u9669\u7684\u60c5\u5f62\u4e4b\u4e0b\uff0c\u9664\u4e86\u62fc \u6b7b\u4e00\u51fb\u4e4b\u5916\u3002\u90a3\u6709\u8fc7\u654c\u4e4b\u529b\uff1f \u3000\u3000\u5c31\u5728\u8fd9\u4e00\u970e\u90a3\uff0c\u63d0\u6469\u8fbe\u591a\u5ffd\u5730\u673a\u4f36\u4f36\u6253\u4e86\u4e00\u4e2a\u51b7\u6218\uff0c\u6842\u534e\u751f\u4e5f\u7a81\u7136\u611f\u5230\u4e00\u9635\u900f\u9aa8\u7684\u5947 \u5bd2\uff0c\u7136\u800c\u6842\u534e\u751f\u4eba\u5728\u8f66\u5185\uff0c\u811a\u8e0f\u5b9e\u5730\uff0c\u6709\u6240\u51ed\u4f9d\uff0c\u5f62\u52bf\u81ea\u597d\u5f97\u591a\uff0c\u4e00\u89c1\u673a\u4e0d\u53ef\u5931\uff0c\u5b9d\u5251\u75be\u5fd9 \u524a\u51fa\uff0c\u4f46\u542c\u5f97\u63d0\u6469\u8fbe\u591a\u60e8\u53eb\u4e00\u58f0\u3002\u767b\u65f6\u8dcc\u843d\u8f66\u4e0b\uff0c\u5de6\u81c2\u4e5f\u88ab\u5b9d\u5251\u524a\u53bb\u4e86\u4e00\u7247\u76ae\u8089\u3002\u63d0\u6469\u8fbe\u591a \u66b4\u8df3\u5982\u96f7\uff0c\u5927\u6012\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b50\u4f7f\u7684\u662f\u4ec0\u4e48\u5996\u6cd5\uff1f\u201d\u6842\u534e\u751f\u90a3\u8fd8\u80af\u4e0e\u4ed6\u642d\u8bdd\uff0c\u7acb\u523b\u97ad\u9a6c\u9a71 \u8f66\uff0c\u98ce\u96f7\u75be\u8d70\uff0c\u63d0\u6469\u8fbe\u591a\u53d7\u4e86\u521b\u4f24\uff0c\u90a3\u91cc\u8fd8\u8ffd\u5f97\u53ca\u3002 \u3000\u3000\u6842\u534e\u751f\u60f3\u8d77\u521a\u624d\u7684\u60c5\u52bf\uff0c\u8d8a\u60f3\u8d8a\u60ca\uff0c\u4e5f\u8d8a\u60f3\u8d8a\u89c9\u5f97\u5947\u602a\u3002\u90a3\u4e00\u9635\u51b7\u98ce\uff0c\u7adf\u4f3c\u5ff5\u9752\u5510\u53e4\u62c9 \u5c71\u5c71\u4e0a\u90a3\u51b0\u7a9f\u4e2d\u7684\u5947\u5bd2\u4e00\u6837\uff0c\u7edd\u5bf9\u4e0d\u662f\u4ece\u5929\u4e0a\u53f0\u4e0b\u6765\u7684\uff0c\u9a6c\u8f66\u4e0a\u5c31\u53ea\u662f\u8fd9\u51e0\u4e2a\u4eba\uff0c\u90a3\u4e48\uff0c\u662f \u8c01\uff0c\u662f\u8c01\u6697\u52a9\u4ed6\u4e00\u81c2\u4e4b\u529b\uff1f  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u9b44\u5bd2\u5149\u5251\u300b\u2014\u2014\u7b2c\u516b\u7ae0\u3000\u738b\u5bab\u5f02\u4e8b\u9732\u9634\u8c0b \u6881\u7fbd\u751f\u300a\u51b0\u9b44\u5bd2\u5149\u5251\u300b \u7b2c\u516b\u7ae0\u3000\u738b\u5bab\u5f02\u4e8b\u9732\u9634\u8c0b \u3000\u3000\u9a6c\u8f66\u8dd1\u5f97\u6781\u5feb\uff0c\u4e0d\u6d88\u534a\u4e2a\u65f6\u8fb0\u5df2\u81ea\u745e\u626c\u5e03\u62c9\u5c71\u5c71\u9e93\uff0c\u9020\u4eba\u7687\u57ce\uff0c\u6842\u534e\u751f\u4e00\u8def\u601d\u7d22\uff0c\u5fc3\u4e0a \u7684\u7591\u4e91\u66f4\u662f\u8d8a\u6765\u8d8a\u91cd\u4e86\u3002\u662f\u8c01\u6697\u52a9\u4ed6\u4e00\u81c2\u4e4b\u529b\uff1f\u4ed6\u4e5f\u66fe\u60f3\u5230\u767d\u8863\u5c11\u5973\uff0c\u4f46\u767d\u8863\u5c11\u5973\u5374\u600e\u80fd\u6765 \u53bb\u65e0\u8e2a\uff1f\u9664\u975e\u5979\u771f\u7684\u662f\u592d\u4e0a\u7684\u4ed9\u5973\u3002\u90a3\u4e48\u662f\u5df4\u52d2\u5417\uff1f\u5df4\u52d2\u5e76\u4e0d\u4f1a\u6b66\u529f\u3002\u662f\u90a3\u4e24\u4e2a\u5bab\u5973\u5417\uff1f\u5979 \u4eec\u53c8\u90a3\u6765\u7684\u51b0\u7a9f\u5bd2\u51b0\uff1f\u83ab\u975e\uff0c\u83ab\u975e\u5c3c\u6cca\u5c14\u7684\u516c\u4e3b\u5c31\u662f\u767d\u8863\u5c11\u5973\uff1f\u8fd9\u60f3\u6cd5\u8fde\u4ed6\u81ea\u5df1\u4e5f\u89c9\u5f97\u592a\u8fc7 \u79bb\u5947\u3002 \u3000\u3000\u9a6c\u8f66\u8fdb\u4eba\u7687\u57ce\uff0c\u7687\u5bab\u5df2\u7ecf\u5728\u671b\u3002\u5df4\u52d2\u5fe7\u5f62\u4e8e\u8272\uff0c\u6084\u6084\u5bf9\u6842\u534e\u751f\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u771f\u6709\u628a \u63e1\uff1f\u201d\u6842\u534e\u751f\u7b11\u9053\uff1a\u201c\u8001\u4e08\u4f46\u8bf7\u653e\u5fc3\u3002\u201d\u5df4\u52d2\u9053\uff1a\u201c\u56fd\u738b\u53d7\u6bd2\u975e\u8f7b\uff0c\u82e5\u533b\u4e0d\u597d\u7684\u8bdd\uff0c\u6211\u56fd\u56fd \u4e2d\u89c4\u77e9\uff0c\u4e3b\u6cbb\u7684\u533b\u751f\u53ef\u5f97\u6b89\u846c\uff01\u201d\u6842\u534e\u751f\u9053\uff1a\u201c\u82e5\u662f\u522b\u6837\u602a\u75c5\uff0c\u6211\u4fbf\u6beb\u65e0\u628a\u63e1\u3002\u82e5\u662f\u4e2d\u6bd2\uff0c \u54c8\u54c8\uff0c\u90a3\u6211\u5305\u4fdd\u836f\u5230\u75c5\u9664\u3002\u8001\u4e08\uff0c\u4f60\u542c\u8fc7\u5929\u5c71\u96ea\u83b2\u89e3\u6bd2\u5947\u6548\u5417\uff1f\u201d\u8bf4\u7f62\u5c06\u4e00\u6735\u5929\u5c71\u96ea\u83b2\u53d6 \u51fa\uff0c\u8fce\u98ce\u4e00\u6643\uff0c\u6ee1\u8f66\u90fd\u662f\u5f02\u9999\uff0c\u4eba\u4eba\u7cbe\u795e\u4e00\u723d\uff0c\u5df4\u52d2\u4ece\u897f\u85cf\u4e00\u672c\u53e4\u4ee3\u7684\u836f\u5178\u91cc\u77e5\u9053\u6709\u5929\u5c71\u96ea \u83b2\uff0c\u5373\u7b97\u662f\u5b54\u96c0\u80c6\uff0c\u9e64\u9876\u7ea2\u90a3\u6837\u7684\u5267\u6bd2\uff0c\u6709\u4e86\u5929\u5c71\u96ea\u83b2\u4ea6\u53ef\u4ee5\u89e3\u6551\uff0c\u5fc3\u5934\u7684\u5927\u77f3\u7acb\u523b\u653e\u4e0b\u3002 \u3000\u3000\u9a6c\u8f66\u5230\u4e86\u5bab\u95e8\u505c\u4e0b\uff0c\u5c3c\u6cca\u5c14\u7684\u738b\u5bab\u8bba\u5b8f\u4f1f\u4e0d\u53ca\u5317\u4eac\u7684\u738b\u5bab\uff0c\u8bba\u5bcc\u4e3d\u4e5f\u4e0d\u53ca\u5e03\u8fbe\u62c9\u5bab\uff0c\u4f46 \u5374\u6709\u4ed6\u72ec\u7279\u7684\u98ce\u683c\uff0c\u738b\u5bab\u540e\u9762\u548c\u4e24\u65c1\u662f\u6210\u7fa4\u7684\u5e99\u5854\uff0c\u5c42\u697c\u98de\u9601\uff0c\u663c\u680b\u96d5\u6881\uff0c\u5c4b\u810a\u548c\u6597\u62f1\u4e0a\u5851 \u6709\u5955\u5955\u5982\u751f\u7684\u5929\u79e4\u9e1f\u517d\uff0c\u6709\u5149\u5f69\u8000\u76ee\u7684\u9752\u94dc\u5854\u9876\uff0c\u548c\u97f3\u60a6\u8033\u7684\u534a\u7a7a\u4e2d\u7684\u98ce\u94c3\uff0c\u738b\u5bab\u5916\u6709\u4e09\u4e08 \u591a\u9ad8\u7684\u5982\u6765\u4f5b\u50cf\uff0c\u6709\u53e3\u5410\u9752\u6cc9\u7684\u94dc\u94f8\u9f99\u5934\uff0c\u90a3\u662f\u878d\u548c\u4e86\u4e1c\u65b9\u548c\u897f\u65b9\u5efa\u827a\u672f\u7684\u4fdd\u79f0\uff0c\u5145\u6ee1\u4e86\u5f02 \u56fd\u7684\u60c5\u8c03\uff0c\u4f46\u4ece\u5f02\u56fd\u60c5\u8c03\u4e2d\u53c8\u53ef\u4ee5\u770b\u51fa\u4e2d\u56fd\u6587\u5316\u7684\u5f71\u54cd\u3002\u6842\u534e\u751f\u4f46\u89c9\u6837\u6837\u65b0\u9c9c\uff0c\u5982\u4eba\u5c71\u9634\u9053 \u4e0a\uff0c\u76ee\u4e0d\u6687\u63a5\u3002\u4ed6\u62d6\u7740\u5fa1\u6797\u519b\u603b\u7ba1\uff0c\u968f\u7740\u5bab\u5973\u8d70\u8fc7\u4e86\u957f\u957f\u7684\u56de\u5eca\u76f4\u4eba\u540e\u5bab\uff0c\u5728\u4e00\u5ea7\u767d\u77f3\u780c\u6210 \u7684\u5bab\u6bbf\u5185\u671d\u89c1\u4e86\u5c3c\u6cca\u5c14\u738b\u3002 \u3000\u3000\u5bab\u4e2d\u7684\u666f\u50cf\u4eca\u6842\u534e\u751f\u751a\u611f\u60ca\u5947\uff0c\u4f46\u89c1\u5c3c\u6cca\u5c14\u738b\u5750\u5728\u9526\u9cd7\u4e4b\u5185\uff0c\u9762\u8272\u7ea2\u6da6\uff0c\u6beb\u65e0\u75c5\u5bb9\uff0c\u80cc \u540e\u571f\u7740\u4e00\u4e2a\u8001\u8005\uff0c\u5df4\u52d2\u8ba4\u5f97\u5b83\u662f\u5fa1\u533b\u3002\u56fd\u738b\u5ea7\u524d\u571f\u7740\u4e00\u4e2a\u9752\u5e74\uff0c\u80cc\u5411\u5916\u95f4\uff0c\u6842\u534e\u751f\u7b49\u4e00\u8fdb\u4eba \u5bab\u6bbf\uff0c\u4fbf\u55c5\u5230\u7a7a\u6c14\u4e2d\u5f25\u6f2b\u7740\u4e00\u80a1\u5f02\u9999\uff0c\u6b63\u662f\u5929\u5c71\u96ea\u83b2\u6240\u7279\u6709\u7684\u9999\u6c14\uff0c\u56fd\u738b\u89c1\u6842\u534e\u751f\u6765\u5230\uff0c\u4ece \u9526\u9cd7\u7f13\u7f13\u8d77\u7acb\uff0c\u4ed6\u9762\u524d\u90a3\u4e2a\u5c11\u5e74\u4e5f\u8f6c\u8fc7\u5934\u6765\uff0c\u4f73\u534e\u751f\u7684\u773c\u5149\u4e0e\u4ed6\u4e00\u89e6\uff0c\u4e24\u4eba\u90fd\u5927\u60d1\u8be7\u5f02\uff0c\u5c24 \u5176\u662f\u90a3\u4e2a\u5c11\u5e74\uff0c\u5168\u8eab\u9661\u7684\u4e00\u9707\uff0c\u51e0\u4e4e\u5446\u4e86\uff01 \u3000\u3000\u4f60\u9053\u8fd9\u5c11\u5e74\u662f\u8c01\uff1f\u4ed6\u6b63\u662f\u6842\u534e\u751f\u5728\u559c\u9a6c\u62c9\u96c5\u5c71\u6240\u9047\u89c1\u7684\u90a3\u4e2a\u5370\u5ea6\u5c11\u5e74\u3002\u6842\u534e\u751f\u5411\u4ed6\u5fae\u5fae \u70b9\u5934\u793a\u610f\uff0c\u968f\u5373\u5411\u56fd\u738b\u4fef\u4f0f\u53e9\u9996\uff0c\u56fd\u738b\u957f\u63d6\u8fd8\u793c\u8bf4\u9053\uff1a\u201c\u4e0a\u56fd\u9ad8\u8d24\uff0c\u4e0d\u5fc5\u591a\u793c\u3002\u201d\u7acb\u5373\u8d50 \u5ea7\uff0c\u6842\u534e\u751f\u95ee\u9053\uff1a\u201c\u4e0d\u77e5\u56fd\u738b\u4f55\u4e8b\u89c1\u53ec\uff1f\u201d\u5c3c\u6cca\u5c14\u738b\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u672c\u6765\u662f\u8bf7\u5148\u751f\u6cbb\u75c5\u7684\uff0c\u5982 \u4eca\u4e0d\u7528\u52b3\u70e6\u5148\u751f\u4e86\u3002\u201d\u6307\u7740\u90a3\u4e2a\u5370\u5ea6\u5c11\u5e74\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u5370\u5ea6\u5360\u5a46\u56fd\u7684\u738b\u5b50\u96c5\u5f97\u661f\uff0c\u5c31\u5728\u6211\u6d3e \u51fa\u9a6c\u8f66\u8fce\u63a5\u5148\u751f\u5927\u9a7e\u4e4b\u540e\uff0c\u4ed6\u5373\u5230\u6765\uff0c\u771f\u662f\u836f\u5230\u75c5\u9664\uff0c\u73b0\u5728\u6211\u5df2\u7ecf\u5b8c\u5168\u597d\u4e86\u3002\u4e0d\u8fc7\u5148\u751f\u65e2\u662f \u8fdc\u9053\u800c\u6765\uff0c\u9a7e\u4e34\u655d\u56fd\uff0c\u867d\u7136\u65e0\u4e8b\uff0c\u4ea6\u8bf7\u5148\u751f\u5728\u738b\u5bab\u91cc\u591a\u4f4f\u51e0\u5929\u3002 \u3000\u3000\u6842\u534e\u751f\u8fd9\u624d\u604d\u7136\u5927\u609f\uff0c\u539f\u6765\u96c5\u5f97\u661f\u4e43\u662f\u5370\u5ea6\u738b\u5b50\uff0c\u800c\u4ed6\u5192\u9669\u6500\u767b\u73e0\u5cf0\uff0c\u5bfb\u627e\u96ea\u83b2\uff0c\u4e3a\u7684 \u4fbf\u662f\u66ff\u5c3c\u6cca\u5c14\u56fd\u738b\u6cbb\u75c5\u3002 \u3000\u3000\u5c3c\u6cca\u5c14\u56fd\u738b\u8fd9\u65f6\u6ce8\u610f\u5230\u4e86\u540c\u6765\u7684\u5fa1\u6797\u519b\u603b\u7ba1\uff0c\u89c1\u4ed6\u795e\u8272\u4e0d\u5b89\uff0c\u7cbe\u5764\u59d4\u987f\uff0c\u751a\u4e3a\u8be7\u5f02\uff0c\u95ee \u9053\uff1a\u201c\u4f60\u4e0e\u8d35\u5bbe\u540c\u6765\uff0c\u6709\u4ec0\u4e48\u4e8b\u5417\uff1f\u201d\u5df4\u52d2\u594f\u9053\uff1a\u201c\u603b\u7ba1\u5927\u4eba\u53eb\u6211\u7b7e\u7f72\u75c5\u6848\uff0c\u4e0d\u8bb8\u8bf4\u662f\u965b\u4e0b \u4e2d\u6bd2\uff0c\u8981\u6211\u8bf4\u662f\u666e\u901a\u7684\u5934\u75db\u75c7\u3002\u201d\u56fd\u738b\u6012\u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d\u5df4\u52d2\u8be6\u8be6\u7ec6\u7ec6\u7684\u5c06\u521a\u624d\u5728 \u53e4\u5821\u91cc\u7684\u4e8b\u60c5\u594f\u660e\uff0c\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u6bbf\u540e\u8d70\u51fa\u4e86\u4e00\u4e2a\u4eba\uff0c\u5fa1\u6797\u519b\u603b\u7ba1\u6323\u624e\u53eb\u9053\uff1a\u201c\u738b\u5b50\u6551\u6211\uff01\u201d \u3000\u3000\u6842\u534e\u751f\u4e00\u770b\uff0c\u53ef\u4e0d\u6b63\u662f\u9b54\u9b3c\u57ce\u4e2d\u6240\u9047\u7684\u90a3\u4e2a\u5c3c\u6cca\u5c14\u738b\u5b50\u3002 \u3000\u3000\u90a3\u5c3c\u6cca\u5c14\u738b\u5b50\u4e00\u58f0\u4e0d\u54cd\uff0c\u60a8\u7684\u8d70\u5230\u5fa1\u6797\u519b\u603b\u7ba1\u8ddf\u524d\uff0c\u9aa4\u7136\u9a82\u9053\uff1a\u201c\u4f60\u8eab\u53d7\u56fd\u738b\u91cd\u4efb\uff0c\u7adf \u6562\u4e0b\u6bd2\uff0c\u56fd\u738b\u80af\u9976\u4f60\uff0c\u6211\u4e5f\u4e0d\u80af\uff01\u201d\u5fa1\u6797\u519b\u603b\u7ba1\u60ca\u5f97\u5446\u4e86\uff0c\u53eb\u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u4f60\uff0c\u4f60\u2026\u2026\u201d\u5c3c \u6cca\u5c14\u738b\u5b50\u7adf\u4ee5\u8fc5\u96f7\u4e0d\u53ca\u63a9\u8033\u7684\u624b\u6cd5\uff0c\u522b\u7684\u4e00\u5200\uff0c\u5c31\u5c06\u4ed6\u5288\u4e3a\u4e24\u6bb5\uff01\u6842\u534e\u751f\u672c\u6765\u53ef\u4ee5\u62e6\u963b\uff0c\u4f46 \u4ed6\u8fdc\u6765\u4e3a\u5ba2\uff0c\u4e0d\u4fbf\u5192\u6627\u51fa\u624b\uff0c\u800c\u4e14\u738b\u5b50\u8fd9\u6837\u5feb\u7684\u4e0d\u7b49\u5df4\u52d2\u8bf4\u5b8c\uff0c\u4fbf\u6740\u4e86\u5fa1\u6797\u519b\u603b\u7ba1\uff0c\u4ea6\u4f7f\u4ed6 \u610f\u6599\u4e0d\u53ca\u3002 \u3000\u3000\u5c3c\u6cca\u5c14\u738b\u5b50\u4e00\u62b9\u5200\u4e0a\u7684\u8840\u75d5\uff0c\u56de\u8eab\u7980\u9053\uff1a\u201c\u53d4\u53d4\uff0c\u4f60\u8eab\u5728\u6df1\u5bab\uff0c\u88ab\u4eba\u4e0b\u6bd2\uff0c\u4e0d\u5fc5\u5ba1\u8baf\uff0c \u5b9a\u662f\u8fd9\u5fa1\u6797\u519b\u603b\u7ba1\u65e0\u7591\u3002\u201d\u56fd\u738b\u542c\u4e86\u5df4\u52d2\u6240\u8bf4\uff0c\u672c\u6765\u5c31\u7591\u5fc3\u662f\u4ed6\uff0c\u867d\u5acc\u4f84\u513f\u66b4\u71e5\uff0c\u4e00\u53e5\u53e3\u4f9b \u4e5f\u8fd8\u672a\u95ee\uff0c\u4fbf\u5c06\u4ed6\u6740\u4e86\uff0c\u5374\u4e5f\u53ea\u9053\u662f\u4f84\u513f\u5bf9\u81ea\u5df1\u7684\u7231\u62a4\u4e0e\u5fe0\u5fc3\uff0c\u4fbf\u9053\uff1a\u201c\u4ed6\u8eab\u4e3a\u5fa1\u6797\u519b\u603b \u7ba1\uff0c\u5e94\u8d1f\u8b66\u536b\u4e4b\u8d23\uff0c\u7eb5\u7136\u975e\u4ed6\u4e0b\u6bd2\uff0c\u6b7b\u7f6a\u4ea6\u662f\u96be\u9976\uff0c\u6740\u4e86\u4ed6\u4e5f\u4fbf\u7b97\u4e86\u3002\u201d\u7acb\u5373\u53eb\u536b\u58eb\u5c06\u603b\u7ba1 \u7684\u5c38\u4f53\u79fb\u5f00\uff0c\u6d17\u5e72\u51c0\u5730\u4e0a\u7684\u8840\u8ff9\uff0c\u5e76\u5429\u5490\u5fa1\u53a8\u6446\u4e0a\u9152\u5e2d\u3002\u56fd\u738b\u7f20\u7ef5\u75c5\u69bb\uff0c\u4e24\u6708\u6709\u4f59\uff0c\u4e00\u65e6\u970d \u7136\u800c\u6108\uff0c\u559c\u6c14\u6d0b\u6d0b\uff0c\u56e0\u6b64\u7279\u5728\u5bab\u4e2d\u591c\u5bb4\uff0c\u4e00\u6765\u916c\u52b3\u5370\u5ea6\u738b\u5b50\uff0c\u4e8c\u6765\u4e3a\u6842\u534e\u751f\u63a5\u98ce\u3002\u559c\u6c14\u6d0b\u6d0b \u4e4b\u4e0b\uff0c\u5bf9\u5fa1\u6797\u519b\u603b\u7ba1\u6240\u5f15\u8d77\u7684\u4e0d\u6109\u5feb\u4e4b\u4e8b\uff0c\u4e5f\u4fbf\u4e0d\u62df\u518d\u8ffd\u7a76\u4e86\u3002 \u3000\u3000\u5df4\u52d2\u5374\u4e0e\u6842\u534e\u751f\u9762\u9762\u76f8\u89d1\uff0c\u4ed6\u4eec\u77e5\u9053\u56fd\u738b\u4e2d\u7684\u662f\u6162\u6027\u6bd2\u836f\uff0c\u7edd\u975e\u5fa1\u6797\u519b\u603b\u7ba1\u6240\u80fd\u4e0b\u624b\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u5fa1\u6797\u519b\u603b\u7ba1\u6700\u5c11\u4e5f\u662f\u540c\u8c0b\uff0c\u800c\u4e14\u4ed6\u4eec\u82e5\u8bf4\u660e\u771f\u76f8\uff0c\u56fd\u738b\u4e3a\u4e86\u8ffd\u7a76\u5acc\u7591\u4eba\u72af\uff0c\u5bab\u4e2d \u52bf\u5fc5\u95f9\u5f97\u5929\u7ffb\u5730\u8986\uff0c\u8bf4\u4e0d\u5b9a\u8fd8\u8981\u5174\u8d77\u5927\u7337\uff0c\u66f4\u517c\u662f\u5728\u56fd\u738b\u521d\u6108\uff0c\u559c\u7b75\u5c06\u5f00\uff0c\u4ed6\u4eec\u4e5f\u5c31\u4e0d\u591a\u8bf4 \u4e86\u3002 \u3000\u3000\u6842\u534e\u751f\u638f\u51fa\u8fbe\u8d56\u6d3b\u4f5b\u7ed9\u4ed6\u90a3\u5c01\u4e66\u4fe1\uff0c\u5448\u7ed9\u56fd\u738b\u9053\uff1a\u201c\u672c\u5e94\u4e00\u5230\u4eac\u57ce\uff0c\u4fbf\u5f53\u8fdb\u8c12\u9646\u4e0b\uff0c\u671b \u965b\u4e0b\u6055\u7f6a\u3002\u201d\u56fd\u738b\u770b\u4e86\u4e66\u4fe1\uff0c\u66f4\u4e3a\u559c\u60a6\uff0c\u9053\uff1a\u201c\u655e\u56fd\u4ee5\u4f5b\u6559\u6cbb\u56fd\uff0c\u5be1\u4eba\u539f\u8be5\u5148\u9063\u4f7f\u95ee\u5019\u6d3b\u4f5b \u624d\u662f\u3002\u201d\u5c3c\u6cca\u5c14\u738b\u5b50\u9053\uff1a\u201c\u4eca\u5e74\u6d3b\u4f5b\u79df\u8bde\u8fb0\uff0c\u6211\u5df2\u6d3e\u9063\u4f7f\u8005\uff0c\u4ee5\u53d4\u53d4\u7684\u540d\u4e49\u7ed9\u5e03\u8fbe\u62c9\u5bab\u6350\u732e \u91d1\u5854\u4e86\uff0c\u53ea\u56e0\u53d4\u53d4\u5c1a\u5728\u75c5\u4e2d\uff0c\u662f\u4ee5\u672a\u53ca\u7980\u77e5\u3002\u201d\u56fd\u738b\u66f4\u559c\u3002\u8bf4\u9053\uff1a\u201c\u4f60\u505a\u5f97\u6b63\u5408\u6211\u7684\u610f\u601d\uff0c \u4ee5\u540e\u56fd\u5bb6\u4e4b\u4e8b\uff0c\u4f60\u77a7\u7740\u8be5\u529e\u7684\u5c31\u7ed9\u6211\u529e\u4e86\u4fbf\u662f\u3002\u201d\u5ffd\u53c8\u60f3\u5230\u4e00\u4e8b\uff0c\u8bf4\u9053\uff1a\u201c\u524d\u4e24\u5929\u6709\u4e00\u4f4d\u4e2d \u56fd\u7684\u50e7\u4eba\u5230\u6765\uff0c\u53ef\u80fd\u662f\u4f60\u7684\u76f8\u8bc6\uff0c\u5f53\u65f6\u6211\u5728\u75c5\u4e2d\uff0c\u65e0\u6687\u53ec\u89c1\uff0c\u73b0\u5728\u6b63\u597d\u8bf7\u4ed6\u540c\u6765\u6b22\u5bb4\u3002\u201d\u6842 \u534e\u751f\u5fc3\u9053\uff1a\u201c\u8fd9\u50e7\u4eba\u662f\u8c01\uff0c\u5c45\u7136\u4e5f\u80fd\u7ed5\u8fc7\u559c\u9a6c\u62c9\u96c5\u5c71\u5230\u8fbe\u6b64\u5730\u3002\u201d\u773c\u5149\u4e00\u7763\uff0c\u4f46\u89c1\u5c3c\u6cca\u5c14\u738b \u5b50\u4e5f\u6709\u8be7\u5f02\u4e4b\u8272\uff0c\u770b\u6765\u8fd9\u50e7\u4eba\u662f\u8c01\uff0c\u4ed6\u4e5f\u4e0d\u77e5\u3002 \u3000\u3000\u7b75\u5e2d\u6446\u5f00\uff0c\u90a3\u4e2d\u56fd\u7684\u50e7\u4eba\u8fd8\u672a\u5230\uff0c\u56fd\u738b\u5148\u8bf7\u96c5\u5fb7\u661f\u3001\u6842\u534e\u751f\u3001\u5df4\u52d2\u7b49\u4eba\u5165\u5e2d\uff0c\u5e76\u53eb\u516b\u4e2a \u5bab\u5973\u51fa\u6765\u966a\u9152\uff0c\u521a\u624d\u5949\u547d\u53bb\u53ec\u5524\u6842\u534e\u751f\u90a3\u4e24\u4e2a\u5bab\u5973\u4e5f\u5728\u5176\u5185\u3002\u56fd\u738b\u7aef\u8d77\u9152\u676f\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4eca \u665a\u6709\u4e09\u4ef6\u5927\u559c\u4e4b\u4e8b\u8981\u8bf4\u2026\u2026\u201d\u738b\u5b50\u51d1\u8da3\u8bf4\u9053\uff1a\u201c\u53d4\u7687\u6bcf\u8bf4\u4e00\u4ef6\u559c\u4e8b\uff0c\u6211\u4eec\u966a\u996e\u4e09\u676f\u3002\u201d\u5c3c\u6cca \u5c14\u56fd\u738b\u54c8\u54c8\u7b54\u8bf4\uff1a\u201c\u7b2c\u4e00\u4ef6\u559c\u4e8b\u662f\u6211\u56e0\u7978\u5f97\u798f\uff0c\u7cca\u91cc\u7cca\u6d82\u7684\u88ab\u597d\u4eba\u4e0b\u6bd2\uff0c\u770b\u7740\u5c31\u8981\u505a\u51a4\u9b3c \u4e86\uff0c\u5374\u5e78\u5f97\u5370\u5ea6\u738b\u5b50\u5230\u6765\uff0c\u60e0\u6211\u4ed9\u82b1\u7075\u836f\uff0c\u5982\u4eca\u4e0d\u4f46\u5bbf\u75be\u987f\u9664\uff0c\u800c\u4e14\u7cbe\u795e\u500d\u6614\uff0c\u5c82\u4e0d\u53ef \u5e86\u3002\u201d\u738b\u5b50\u8d3a\u9053\uff1a\u201c\u5409\u4eba\u5929\u76f8\uff0c\u795d\u53d4\u53d4\u767e\u5c81\u5343\u79cb\uff01\u201d\u7387\u5148\u996e\u4e8c\u5929\u676f\u9152\u3002\u5c3c\u6cca\u5c14\u56fd\u738b\u53c8\u9053\uff1a \u201c\u7b2c\u4e8c\u4ef6\u559c\u4e8b\u662f\u5f97\u63a5\u4f73\u5bbe\uff0c\u6842\u5148\u751f\u4e0e\u738b\u5b50\u90fd\u662f\u8d8a\u8fc7\u4e16\u754c\u7b2c\u4e00\u5927\u5c71\u800c\u6765\uff0c\u539a\u8c0a\u6df1\u60c5\uff0c\u5c06\u6c38\u6807\u53f2 \u518c\u3002\u201d\u738b\u5b50\u8bf4\u9053\uff1a\u201c\u4e24\u4f4d\u4e0a\u56fe\u9ad8\u8d24\uff0c\u5149\u4e34\u655d\u56fd\uff0c\u5b9e\u4e43\u655d\u56fd\u4e4b\u798f\uff0c\u597d\uff0c\u6211\u6bcf\u4f4d\u5404\u656c\u4e09\u676f\uff01\u201d\u656c \u5230\u6842\u534e\u751f\u65f6\uff0c\u9732\u51fa\u5978\u6ed1\u7684\u7b11\u5bb9\uff0c\u4f3c\u662f\u9677\u5a9a\uff0c\u53c8\u4f3c\u5a01\u80c1\uff0c\u6842\u534e\u751f\u5fc3\u9053\uff1a\u201c\u4e0d\u77e5\u9053\u4ed6\u9634\u8c0b\u4fb5\u7565\u897f \u85cf\u4e4b\u4e8b\uff0c\u56fd\u738b\u66fe\u5426\u4e0e\u95fb\uff1f\u5c06\u6765\u6709\u673a\u4f1a\u65f6\u6211\u603b\u8981\u62c6\u7a7f\u4ed6\u3002\u201d\u795e\u8272\u81ea\u5982\uff0c\u4e0d\u5351\u4e0d\u4ea2\uff0c\u4e09\u676f\u9152\u63a5\u8fc7 \u6765\u559d\u4e86\u3002 \u3000\u3000\u56fd\u738b\u505c\u4e86\u4e00\u505c\uff0c\u4e3e\u676f\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u6211\u819d\u4e0b\u65e0\u513f\uff0c\u53ea\u6709\u4e00\u5973\uff0c\u5be1\u4eba\u6b32\u4e3a\u5979\u62e9\u914d\uff0c\u65e0\u5948\u5979\u5fc3 \u6027\u9ad8\u50b2\uff0c\u4e00\u76f4\u672a\u6709\u5408\u610f\u7684\u4eba\uff0c\u5982\u4eca\u96c5\u5fb7\u738b\u5b50\u6551\u4e86\u6211\u7684\u6027\u547d\uff0c\u4eba\u54c1\u76f8\u8c8c\u53c8\u4f3c\u517d\u4e2d\u7684\u9e92\u9e9f\uff0c\u9e1f\u4e2d \u7684\u51e4\u51f0\uff0c\u66f4\u559c\u4ed6\u4e0d\u5acc\u5f03\u5c0f\u56fd\uff0c\u8fdc\u9053\u6c42\u5a5a\uff0c\u7eb5\u7136\u6ca1\u6709\u6551\u6211\u6027\u547d\u4e4b\u4e8b\uff0c\u4e5f\u5e94\u5f53\u7b54\u5141\uff0c\u5982\u4eca\u7531\u6211\u4f5c \u4e3b\uff0c\u518d\u8fc7\u51e0\u65e5\u4fbf\u4e0e\u4ed6\u4eec\u6210\u5a5a\uff0c\u4eca\u591c\u4e4b\u5bb4\uff0c\u6743\u5f53\u5b9a\u5a5a\u4e4b\u9152\u3002\u201d\u4f17\u4eba\u6b22\u58f0\u96f7\u52a8\uff0c\u6842\u534e\u751f\u8fd9\u624d\u77e5\u9053 \u4ed6\u5bfb\u89c5\u96ea\u83b2\u7684\u771f\u6b63\u7528\u610f\uff0c\u5fc3\u4e2d\u6697\u559c\u81ea\u5df1\u66fe\u7389\u6210\u738b\u5b50\u4e4b\u597d\u4e8b\uff0c\u5f53\u4e0b\u4e5f\u4e0a\u524d\u5411\u4ed6\u656c\u9152\u3002 \u3000\u3000\u96c5\u5fb7\u661f\u738b\u5b50\u9ed1\u8138\u6cdb\u7ea2\uff0c\u597d\u4f3c\u6709\u70b9\u4f0d\u5462\u4e0d\u5b89\uff0c\u534a\u664c\u8bf4\u9053\uff1a\u201c\u4e0d\u77e5\u516c\u4e3b\u610f\u4e0b\u5982\u4f55\uff1f\u201d\u56fd\u738b\u54c8 \u54c8\u5927\u7b11\u9053\uff1a\u201c\u5c0f\u5973\u8fd8\u6709\u4e0d\u6ee1\u610f\u7684\u4e48\uff1f\u201d\u7acb\u5373\u53eb\u4e00\u4e2a\u5bab\u5973\u5165\u5185\uff0c\u8bf7\u516c\u4e3b\u62ff\u51fa\u4e00\u4ef6\u73cd\u5b9d\uff0c\u4f5c\u4e3a\u8ba2 \u5a5a\u7684\u4ea4\u6362\u4e4b\u7269\u3002\u4f17\u4eba\u5750\u5b9a\uff0c\u518d\u4ed8\u996e\u9152\uff0c\u6842\u534e\u751f\u5ffd\u89c9\u6709\u4e00\u4e2a\u5bab\u5973\u8f7b\u8f7b\u89e6\u4e86\u4ed6\u4e00\u4e0b\uff0c\u6842\u534e\u751f\u6014\u4e00 \u6014\uff0c\u4ee5\u8896\u996e\u676f\uff0c\u4f5c\u72b6\u996e\u9152\uff0c\u6084\u6084\u63a5\u4e86\u5bab\u5973\u585e\u8fc7\u6765\u7684\u4e00\u4e2a\u7eb8\u56e2\uff0c\u8d81\u7740\u4f17\u4eba\u5411\u96c5\u5fb7\u661f\u656c\u9152\uff0c\u95f9\u54c4 \u54c4\u7684\u65f6\u5019\uff0c\u4ee5\u6781\u5feb\u7684\u624b\u6cd5\uff0c\u6253\u5f00\u4e00\u770b\uff0c\u53ea\u89c1\u4e0a\u9762\u5199\u7740\u51e0\u884c\u8747\u5934\u5c0f\u5b57\uff0c\u7adf\u662f\u6c49\u6587\uff0c\u4e66\u6cd5\u5a1f\u79c0\u4e4b \u6781\uff0c\u5199\u7684\u662f\uff1a\u201c\u901f\u5411\u56fd\u738b\u8bf4\u660e\u96ea\u83b2\u672c\u662f\u4f60\u7684\uff0c\u963b\u6b62\u5370\u5ea6\u738b\u5b50\u4e0e\u516c\u4e3b\u8ba2\u5a5a\u3002\u201d\u6842\u534e\u751f\u5927\u5403\u4e00 \u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u4e3a\u4f55\u8981\u6211\u963b\u6b62\u4ed6\u4eec\u8ba2\u5a5a\uff1f\u83ab\u975e\u516c\u4e3b\u4e0d\u613f\u5ac1\u4ed6\uff1f\u4f46\u4f55\u5fc5\u8981\u6c42\u6211\u51fa \u5934\uff1f\u201d\u773c\u5149\u4e00\u77a5\uff0c\u53ea\u89c1\u90a3\u4e2a\u5bab\u5973\u671d\u7740\u4ed6\u5fae\u5fae\u4e00\u7b11\uff0c\u7b11\u5f97\u5341\u5206\u5947\u602a\uff01\u6842\u534e\u751f\u5fc3\u5934\u4e03\u4e0a\u516b\u843d\uff0c\u5c06 \u79cd\u79cd\u53ef\u7591\u7684\u8ff9\u8c61\u90fd\u8fde\u8d77\u6765\uff0c\u767b\u65f6\u5fc3\u4e71\u5982\u9ebb\uff0c\u4e0d\u77e5\u6240\u53ef\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u5df4\u52d2\u8bf4\u9053\uff1a\u201c\u8001\u673d\u6127\u4f5c\u533b\u5e08\uff0c\u8bf7\u738b\u5b50\u8d50\u6559\uff0c\u5230\u5e95\u662f\u7528\u4ec0\u4e48\u7075\u836f\u6cbb\u597d\u56fd\u738b\u4e4b\u75c5 \u7684\uff1f\u201d\u5c3c\u6cca\u5c14\u738b\u5b50\u7b11\u9053\uff1a\u201c\u5df4\u52d2\u8001\u5148\u751f\uff0c\u4f60\u662f\u56fd\u4e2d\u7684\u7b2c\u4e00\u540d\u533b\uff0c\u7adf\u8fde\u738b\u5b50\u7528\u7684\u662f\u4ec0\u4e48\u836f\u4e5f\u4e0d \u77e5\u9053\u5417\uff1f\u201d\u5df4\u52d2\u9053\uff1a\u201c\u6211\u5fc3\u4e2d\u731c\u662f\u4e00\u79cd\u5947\u82b1\uff0c\u4f46\u8fd9\u79cd\u82b1\u592a\u8fc7\u96be\u5f97\uff0c\u5370\u5ea6\u66f4\u6839\u672c\u6ca1\u6709\uff0c\u6240\u4ee5\u627e \u53ea\u6015\u662f\u731c\u9519\u4e86\uff0c\u4e0d\u6562\u8bf4\u51fa\u6765\u3002\u6216\u8bb8\u53e6\u6709\u5947\u836f\u4e5f\u8bf4\u4e0d\u5b9a\uff0c\u8bf7\u738b\u5b50\u8d50\u77e5\uff0c\u4ee5\u589e\u89c1\u95fb\u3002\u201d\u96c5\u5fb7\u661f\u738b \u5b50\u5fc3\u4e2d\u5fd0\u5fd1\u4e0d\u5b89\uff0c\u9762\u4e0a\u4e00\u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u4e2d\u56fd\u7684\u5929\u5c71\u96ea\u83b2\u3002\u201d\u5df4\u52d2\u6563\u4ef6\u60ca\u8be7\uff0c\u8bf4\u9053\uff1a\u201c\u738b \u5b50\u771f\u662f\u4e86\u4e0d\u8d77\uff0c\u8fd8\u66fe\u6500\u767b\u8fc7\u4e2d\u56fd\u7684\u5929\u5c71\u5417\uff1f\u201d\u96c5\u5fb7\u661f\u738b\u5b50\u4e00\u770b\uff0c\u53ea\u89c1\u6842\u534e\u751f\u4f3c\u7b11\u975e\u7b11\u7684\u5bf9\u7740 \u81ea\u5df1\uff0c\u8fd9\u970e\u90a3\u95f4\uff0c\u4ed6\u7684\u5fc3\u5934\u6bd4\u6842\u534e\u751f\u66f4\u8981\u6df7\u4e71\u4e0d\u5b89\u3002 \u3000\u3000\u591c\u98ce\u4e2d\u9001\u6765\u4e00\u9635\u67d4\u548c\u7684\u949f\u58f0\u58f0\uff0c\u539f\u6765\u5c3c\u6cca\u5c14\u4e43\u662f\u4f5b\u56fd\uff0c\u5bab\u4e2d\u7684\u795e\u5e99\uff0c\u5728\u4e94\u66f4\u5929\u4eae\u7684\u65f6 \u5019\uff0c\u505a\u6bcf\u65e5\u7684\u7b2c\u4e00\u904d\u6cd5\u4e8b\uff0c\u5230\u4e09\u66f4\u591c\u534a\u7684\u65f6\u5019\uff0c\u5219\u505a\u6700\u540e\u4e00\u904d\u6cd5\u4e8b\uff0c\u73b0\u5728\u6b63\u662f\u4e09\u66f4\u65f6\u5206\uff0c\u90a3 \u662f\u50e7\u4eba\u505a\u6700\u540e\u4e00\u904d\u6cd5\u4e8b\u4f20\u6765\u7684\u949f\u58f0\u58f0\u3002\u96c5\u5fb7\u661f\u738b\u5b50\u5fc3\u795e\u4e00\u5b81\uff0c\u60f3\u9053\uff1a\u201c\u6211\u81ea\u5e7c\u4e5f\u66fe\u542c\u8fc7\u9f99\u53f6 \u5927\u5e08\u8bf4\u6cd5\uff0c\u600e\u7684\u5230\u4e86\u5229\u5bb3\u5173\u5934\uff0c\u4fbf\u8d77\u4e86\u8d2a\u586b\u75f4\u5ff5\uff1f\u4f5b\u6212\u8bb9\u8bed\uff0c\u8fd9\u4f4d\u4e2d\u56fd\u5c11\u5e74\u5f53\u65e5\u6168\u7136\u4ee5\u96ea\u83b2 \u76f8\u8d60\uff0c\u6211\u600e\u597d\u7792\u4e86\u4ed6\u7684\u529f\u52b3\uff1f\u201d\u4fbf\u5373\u594f\u7980\u56fd\u738b\u9053\uff1a\u201c\u965b\u4e0b\u5e94\u8be5\u611f\u8c22\u7684\u5b9e\u5728\u662f\u8fd9\u4f4d\u6842\u5148\u751f\uff0c\u8981 \u4e0d\u662f\u4ed6\uff0c\u53ea\u6015\u5929\u4e0b\u6700\u9ad8\u660e\u7684\u533b\u5e08\uff0c\u4e5f\u96be\u4ee5\u89e3\u9664\u9646\u4e0b\u6240\u4e2d\u7684\u5947\u6bd2\u3002\u201d\u56fd\u738b\u8be7\u9053\uff1a\u201c\u600e\u4e48\uff1f\u4f60\u4eec \u662f\u65e9\u5c31\u76f8\u8bc6\u4e86\u7684\uff1f\u662f\u4ed6\u6559\u4f60\u66ff\u80d6\u6cbb\u75c5\u7684\u4e48\uff1f\u201d\u96c5\u5fb7\u661f\u9053\uff1a\u201c\u90a3\u6735\u5929\u5c71\u96ea\u83b2\u4e43\u662f\u8fd9\u4f4d\u6842\u5148\u751f\u6240 \u8d60\u3002\u201d\u5f53\u4e0b\u6beb\u4e0d\u9690\u7792\uff0c\u5c06\u81ea\u5df1\u5728\u559c\u9a6c\u62c9\u96c5\u5c71\u4e0a\u5bfb\u627e\u96ea\u83b2\uff0c\u9047\u89c1\u6842\u534e\u751f\u7684\u7ecf\u8fc7\u8bf4\u4e86\u3002\u6842\u534e\u751f\u597d \u751f\u4f69\u670d\uff0c\u4e5f\u594f\u7980\u56fd\u738b\u9053\uff1a\u201c\u96c5\u5fb7\u661f\u738b\u5b50\u5192\u5c3d\u8270\u9669\uff0c\u5bfb\u627e\u96ea\u83b2\uff0c\u5b8c\u5168\u662f\u4e3a\u4e86\u9646\u4e0b\uff0c\u5b83\u662f\u6709\u5fc3\u4e3a \u965b\u4e0b\u6cbb\u75c5\uff0c\u6211\u4e0d\u8fc7\u662f\u5076\u7136\u8eab\u6709\u6b64\u7269\uff0c\u4e3e\u4ee5\u76f8\u8d60\u800c\u5df2\uff0c\u4f5b\u6cd5\u8bb2\u7a76\u79cd\u56e0\uff0c\u738b\u5b50\u6709\u6b64\u5584\u5ff5\u5f53\u6709\u5584 \u62a5\uff0c\u6211\u4e0d\u6562\u5206\u4ed6\u4e4b\u529f\u3002\u201d\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u51ed\u7740\u96c5\u5fb7\u661f\u8fd9\u4ed8\u80f8\u895f\uff0c\u516c\u4e3b\u7eb5\u7136\u5c5e\u610f\u4e8e\u6211\uff0c\u6211\u4e5f\u5f53\u7389 \u6210\u5b83\u7684\u597d\u4e8b\u3002\u4f55\u51b5\u6211\u5fc3\u76ee\u4e2d\u65e9\u5c31\u6709\u4e86\u610f\u4e2d\u4e4b\u4eba\u3002\u201d\u8fd9\u65f6\u5fc3\u4e2d\u5ffd\u5982\u7535\u5149\u4e00\u95ea\uff0c\u95ea\u8fc7\u767d\u8863\u5c11\u5973\u7684 \u5f71\u5b50\uff0c\u5fc3\u4e2d\u6697\u6697\u7977\u544a\u4f5b\u79df\uff0c\u4f46\u671b\u767d\u8863\u5c11\u5973\u4e0d\u8981\u5c31\u662f\u516c\u4e3b\u4fbf\u597d\u3002 \u3000\u3000\u5c3c\u6cca\u5c14\u56fd\u738b\u751a\u662f\u611f\u52a8\uff0c\u8bf4\u9053\uff1a\u201c\u4e24\u4f4d\u90fd\u662f\u4e0a\u56fd\u9ad8\u8d24\uff0c\u80f8\u895f\u6c14\u5ea6\uff0c\u4ee4\u4eba\u656c\u4f69\u3002\u6842\u5148\u751f\u82e5\u80af \u7559\u4e0b\uff0c\u4fbf\u8bf7\u59d4\u66f2\u51fa\u4efb\u5fa1\u6797\u519b\u603b\u7ba1\u4e4b\u804c\u3002\u201d\u6842\u534e\u751f\u9053\uff1a\u201c\u6211\u8fc7\u60ef\u95f2\u4e91\u91ce\u9e64\u7684\u751f\u6daf\uff0c\u518d\u8bf4\u6211\u65e0\u5fb7 \u65e0\u80fd\uff0c\u800c\u4e14\u662f\u521a\u5230\u8d35\u56fd\uff0c\u66f4\u4e0d\u582a\u5f53\u6b64\u91cd\u4efb\u3002\u201d\u5c3c\u6cca\u5c14\u56fd\u738b\u660e\u77e5\u4ed6\u4e0d\u80af\u5c31\u4efb\uff0c\u987f\u4e86\u4e00\u987f\uff0c\u53c8 \u9053\uff1a\u201c\u82e5\u662f\u6842\u5148\u751f\u4e0d\u80af\u7559\u4e0b\uff0c\u80d6\u4e5f\u4e0d\u6562\u52c9\u5f3a\u3002\u4f46\u81f3\u5c11\u8981\u7b49\u5230\u5403\u4e86\u5c0f\u5973\u559c\u9152\u4e4b\u540e\uff0c\u5bb9\u80d6\u7a0d\u5c3d\u5fc3 \u610f\uff0c\u518d\u9001\u5148\u751f\u542f\u7a0b\u3002\u201d \u3000\u3000\u96c5\u5fb7\u661f\u738b\u5b50\u5fc3\u4e2d\u6b63\u81ea\u5598\u5598\u4e0d\u5b89\uff0c\u8bda\u6050\u8bf4\u51fa\u96ea\u83b2\u662f\u6842\u534e\u751f\u6240\u8d60\u4e4b\u540e\uff0c\u5a5a\u4e8b\u4f1a\u6709\u53d8\u5316\u3002\u800c\u4eca \u4e00\u542c\u56fd\u738b\u53e3\u6c14\uff0c\u4ed6\u4ecd\u7136\u7ef4\u6301\u800c\u8ba9\uff0c\u8fd8\u8bf7\u6842\u534e\u751f\u5403\u81ea\u5df1\u548c\u516c\u4e3b\u7684\u559c\u9152\uff0c\u5fc3\u5934\u4e0a\u7684\u4e00\u5757\u5927\u77f3\uff0c\u767b \u65f6\u653e\u4e0b\u3002\u8981\u77e5\u96c5\u5fb7\u661f\u867d\u8bf4\u80f8\u895f\u5bbd\u5e7f\uff0c\u5176\u5b9e\u4e5f\u662f\u88ab\u5df4\u52d2\u8feb\u5f97\u4ed6\u4e0d\u5f97\u4e0d\u8bf4\u7684\u3002\u800c\u4e14\u4ed6\u4e45\u95fb\u5c3c\u767d\u5c14 \u4e48\u4e3b\u56fd\u8272\u5929\u9999\uff0c\u8fd9\u624d\u4e0d\u60dc\u5192\u5c3d\u98ce\u971c\uff0c\u5386\u5c3d\u8270\u9669\uff0c\u524d\u6765\u6c42\u5a5a\uff0c\u773c\u89c1\u5230\u624b\u4e86\u7684\u7f8e\u4eba\uff0c\u53c8\u5982\u4f55\u80af \u8ba9\uff1f \u3000\u3000\u96c5\u5fb7\u661f\u738b\u5b50\u6b63\u81ea\u6ee1\u809a\u5bc6\u5708\uff0c\u53ea\u89c1\u90a3\u4e2a\u5bab\u5973\u8d70\u4e86\u51fa\u6765\uff0c\u56fd\u738b\u95ee\u9053\uff1a\u201c\u516c\u4e3b\u7684\u793c\u7269\u62ff\u51fa\u6765\u6ca1 \u6709\uff1f\u201d\u5bab\u5973\u8bf4\u9053\uff1a\u201c\u516c\u4e3b\u8bf4\u5979\u4e0d\u60f3\u7834\u4f8b\uff0c\u8bf7\u738b\u5b50\u7167\u5979\u65af\u5b9a\u7684\u529e\u6cd5\uff0c\u524d\u6765\u5e94\u8bd5\u4fbf\u662f\u3002\u201d\u56fd\u738b\u9762 \u8272\u4e00\u6c89\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u4e0d\u61c2\u4e8b\u7684\u5b69\u5b50\uff0c\u4f60\u8bf4\u6e05\u695a\u738b\u5b50\u662f\u6211\u7684\u6551\u547d\u6069\u4eba\u6ca1\u6709\uff1f\u201d\u5bab\u5973\u9053\uff1a\u201c\u8bf4\u8fc7 \u5566\uff01\u201d\u96c5\u5fb7\u661f\u738b\u5b50\u9762\u4e0a\u4e00\u9635\u9752\u4e00\u9635\u7ea2\uff0c\u5c34\u5c2c\u4e4b\u6781\uff0c\u53ea\u542c\u5f97\u56fd\u738b\u65a5\u9053\uff1a\u201c\u4f60\u518d\u53bb\u548c\u5979\u8bf4\uff0c\u82e5\u8fd8 \u4e0d\u542c\uff0c\u4f60\u5c31\u53eb\u5730\u51fa\u6765\uff0c\u8ba9\u4ed6\u4eb2\u81ea\u77bb\u4ef0\u738b\u5b50\u7684\u4e30\u91c7\uff0c\u770b\u5979\u5fc3\u4e0d\u5fc3\u670d\uff1f\u201d\u56fd\u738b\u7684\u8bf4\u8bdd\uff0c\u6697\u4e2d\u66ff\u96c5 \u5fb7\u661f\u738b\u5b50\u89e3\u7a98\uff0c\u96c5\u5fb7\u661f\u5fc3\u795e\u751c\u4e1d\u4e1d\u7684\uff0c\u7741\u5706\u53cc\u773c\uff0c\u76ee\u4e0d\u8f6c\u775b\u7684\u6ce8\u89c6\u7740\u5c4f\u98ce\u80cc\u540e\u7684\u6708\u7259\u6d1e\u95e8\u3002 \u3000\u3000\u8fc7\u4e86\u7247\u523b\uff0c\u53ea\u542c\u5f97\u73af\u4f69\u53ee\u5f53\uff0c\u957f\u88d9\u66f3\u5730\u4e4b\u58f0\uff0c\u6842\u534e\u751f\u6bd4\u96c5\u5fb7\u661f\u66f4\u8981\u7d27\u5f20\uff0c\u5fc3\u4e2d\u78ca\u7136\u60f3 \u9053\uff1a\u201c\u82e5\u7136\u8fd9\u4f4d\u516c\u4e3b\u5f53\u771f\u5c31\u662f\u6211\u7684\u534e\u738b\u59b9\u59b9\uff0c\u90a3\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u90a3\u5973\u5b50\u7f13\u7f13\u81ea\u6708\u7259\u6d1e\u95e8\u8d70\u51fa\uff0c\u6842\u534e\u751f\u5fc3\u4e2d\u4e00\u5bbd\uff0c\u4f46\u89c1\u8fd9\u5c11\u5973\u867d\u7136\u4fcf\u4e3d\u975e\u51e1\uff0c\u5374\u5e76\u4e0d\u662f\u767d \u76fe\u5766\u8863\u5c11\u5973\u3002\u5ffd\u542c\u5f97\u56fd\u738b\u5fae\u8bf4\u9053\uff1a\u201c\u5b9b\u5170\u661f\uff0c\u516c\u4e3b\u4e3a\u4ec0\u4e48\u4e0d\u51fa\u6765\uff1f\u662f\u4e0d\u662f\u5730\u505a\u4ece\u4e86\uff0c\u53eb\u4f60\u7ed9 \u4ed6\u9001\u793c\u7269\u6765\u7684\uff1f\u201d\u539f\u6765\u8fd9\u4e2a\u5bab\u5973\u4e43\u662f\u516c\u4e3b\u7684\u8d34\u8eab\u4f8d\u5973\u3002\u6842\u534e\u751f\u5fc3\u4e0a\u7684\u7591\u56e2\u4ecd\u7136\u60ac\u800c\u672a\u6cf1\uff0c\u96c5 \u5fb7\u661f\u738b\u5b50\u66f4\u662f\u5931\u671b\u4e07\u5206\u3002 \u3000\u3000\u90a3\u5bab\u5973\u9053\uff1a\u201c\u516c\u4e3b\u8bf4\uff0c\u65e2\u7136\u738b\u5b50\u7ed9\u56fd\u738b\u6cbb\u597d\u4e86\u75c5\uff0c\u8003\u8bd5\u53ef\u4ee5\u514d\u6389\u4e00\u534a\u3002\u201d\u56fd\u738b\u9053\uff1a\u201c\u600e \u4e48\u53eb\u505a\u514d\u6389\u4e00\u534a\uff1f\u201d\u5bab\u5973\u9053\uff1a\u201c\u4e0d\u8003\u6587\u5b66\uff0c\u8003\u6b66\u529f\uff0c\u4e5f\u4e0d\u5fc5\u901a\u8fc7\u51e0\u5173\uff0c\u8981\u8d62\u5f97\u4e86\u5a62\u5b50\uff0c\u4fbf\u53ef \u4e0e\u516c\u4e3b\u76f8\u89c1\u3002\u518d\u7531\u516c\u4e3b\u4e0e\u4ed6\u6bd4\u5251\uff0c\u8981\u5f97\u6253\u6210\u5e73\u624b\uff0c\u4fbf\u7b97\u53ca\u683c\u4e86\u3002\u201d\u8bed\u6c14\u4e4b\u95f4\uff0c\u4ecd\u662f\u5484\u5484\u8feb \u4eba\uff0c\u96c5\u5fb7\u661f\u738b\u5b50\u672c\u6765\u4e5f\u6df1\u77e5\u516c\u4e3b\u7684\u6b66\u529f\u5389\u5bb3\uff0c\u5404\u56fd\u6765\u7684\u9ad8\u624b\u8fde\u5b83\u7684\u5bab\u5973\u4e5f\u6253\u4e0d\u8fc7\uff0c\u66f4\u4e0d\u8981\u8bf4 \u66fe\u4e0e\u5979\u672c\u4eba\u6bd4\u8bd5\u4e86\u3002\u56e0\u6b64\u4ed6\u624d\u8d39\u5c3d\u7cbe\u5764\uff0c\u6c42\u53d6\u96ea\u83b2\uff0c\u66ff\u56fd\u738b\u6cbb\u75c5\uff0c\u5e0c\u671b\u53ef\u4ee5\u907f\u514d\u548c\u516c\u4e3b\u6bd4 \u6b66\u3002\u90a3\u77e5\u8bf4\u6765\u8bf4\u53bb\uff0c\u8fd9\u4e00\u5173\u8fd8\u662f\u4e0d\u80fd\u514d\u6389\u3002\u96c5\u5fb7\u661f\u4e43\u662f\u5370\u5ea6\u6b66\u5b66\u5927\u5e08\u9f99\u53f6\u9ad8\u50e7\u7684\u5f1f\u5b50\uff0c\u5e73\u751f \u4e5f\u9887\u4e3a\u81ea\u8d1f\uff0c\u542c\u4e86\u6697\u6697\u6709\u6c14\uff0c\u60f3\u9053\uff1a\u201c\u6211\u82e6\u4e0d\u4e0e\u4ed6\u6bd4\u8bd5\uff0c\u5c82\u4e0d\u88ab\u5979\u770b\u8f7b\u4e86\uff1f\u201d\u4fbf\u9053\uff1a\u201c\u516c\u4e3b \u662f\u5973\u4e2d\u82f1\u6770\uff0c\u5dfe\u5e3c\u987b\u7709\uff0c\u80af\u4e88\u8d50\u6559\uff0c\u90a3\u662f\u6c42\u4e4b\u4e0d\u5f97\u3002\u53ea\u6015\u52a8\u5251\u63e3\u5200\uff0c\u6709\u6240\u5931\u624b\u3002\u201d\u4ed6\u5fc3\u4e2d\u4e4b \u610f\u4e43\u662f\u60f3\u628a\u6b66\u6bd4\u4e5f\u6539\u4e3a\u6587\u6bd4\uff0c\u4e00\u6765\u53ef\u514d\u8bef\u4f24\uff0c\u4e8c\u6765\u867d\u7b97\u8d25\u4e86\uff0c\u4e5f\u5bb9\u6613\u843d\u53f0\u3002 \u3000\u3000\u8bdd\u672a\u8bf4\u5b8c\uff0c\u56fd\u738b\u6025\u9053\uff1a\u201c\u662f\u5440\uff0c\u738b\u5b50\u8fdc\u9053\u524d\u6765\uff0c\u53c8\u6cbb\u597d\u4e86\u6211\u7684\u91cd\u75c5\uff0c\u52c3\u5251\u4f26\u5200\uff0c\u5c82\u662f\u5f85 \u5ba2\u4e4b\u9053\uff1f\u5b9b\u5170\u661f\uff0c\u4f60\u56de\u53bb\u548c\u516c\u4e3b\u8bf4\uff0c\u5c31\u8bf4\u662f\u6211\u7684\u610f\u601d\uff0c\u8fd9\u4e00\u534a\u4e5f\u90fd\u514d\u4e86\u3002\u201d \u3000\u3000\u90a3\u5bab\u5973\u8fdb\u53bb\u4e00\u4f1a\uff0c\u53c8\u51fa\u6765\u9053\uff1a\u201c\u516c\u4e3b\u8bf4\u8981\u514d\u6389\u8003\u8bd5\uff0c\u4e5f\u8fd8\u53ef\uff0c\u4f46\u4ee5\u6709\u4e00\u4ef6\u8058\u7269\uff0c\u5374\u662f\u4e07 \u4e07\u4e0d\u53ef\u5c11\u7684\u3002\u201d\u96c5\u5fb7\u661f\u738b\u5b50\u9053\uff1a\u201c\u4e0d\u77e5\u516c\u4e3b\u8981\u4ec0\u4e48\u8058\u7269\uff1f\u201d\u5fc3\u60f3\u81ea\u5df1\u56fd\u4e2d\u7684\u8d22\u5b9d\u5806\u79ef\u5982\u5c71\uff0c \u4e16\u95f4\u4f55\u7269\uff0c\u4e0d\u53ef\u6c42\u5f97\uff1f\u90a3\u5bab\u5973\u9053\uff1a\u201c\u516c\u4e3b\u542c\u8bf4\u738b\u5b50\u662f\u7528\u96ea\u83b2\u6cbb\u597d\u56fd\u738b\u4e4b\u75c5\u7684\uff0c\u516c\u4e3b\u4e5f\u8981\u4e00\u6735 \u96ea\u83b2\u4f5c\u4e3a\u8058\u7269\u3002\u201d\u96c5\u5fb7\u661f\u738b\u5b50\u987f\u65f6\u5446\u4f4f\uff0c\u4ed6\u660e\u77e5\u6842\u534e\u751f\u8eab\u4e0a\u8fd8\u6709\u4e24\u6735\u5929\u5c71\u96ea\u83b2\uff0c\u4f46\u5374\u600e\u597d\u610f \u601d\u518d\u4e0e\u6842\u534e\u751f\u6c42\u53d6\u8058\u59bb\u7684\u793c\u7269\uff1f\u5fc3\u4e2d\u53d1\u4e86\u4e2a\u72e0\uff0c\u60f3\u9053\uff1a\u201c\u6211\u4fbf\u81ea\u5230\u5929\u5c71\u91c7\u4e0b\u96ea\u83b2\uff0c\u770b\u4f60\u5c1a\u6709 \u4f55\u8bf4\uff1f\u201d\u4f46\u518d\u4e00\u60f3\uff0c\u6b64\u53bb\u5929\u5c71\uff0c\u8c08\u4f55\u5bb9\u6613\uff1f\u80fd\u5426\u91c7\u5230\u96ea\u83b2\uff0c\u4ea6\u672a\u53ef\u77e5\uff0c\u5373\u7b97\u91c7\u5f97\u4ea6\u4e0d\u77e5\u4f55\u5e74 \u4f55\u6708\uff1f\u601d\u5ff5\u53ca\u6b64\uff0c\u4e0d\u7981\u6014\u6014\u7684\u770b\u7740\u6842\u534e\u751f\uff0c\u4f46\u671b\u6842\u534e\u751f\u80fd\u6084\u6084\u8fed\u7ed9\u4ed6\u4e00\u6735\u5929\u5c71\u96ea\u83b2\u3002 \u3000\u3000\u6842\u534e\u751f\u5fc3\u4e2d\u4e00\u52a8\uff0c\u6b63\u81ea\u8e0c\u8e87\uff0c\u5ffd\u542c\u5f97\u90a3\u5bab\u5973\u53c8\u9053\uff1a\u201c\u82e5\u7136\u6ca1\u6709\u5929\u5c71\u96ea\u83b2\uff0c\u90a3\u4e48\u5c31\u8981\u8003 \u8bd5\u3002\u516c\u4e3b\u8bf4\uff0c\u8fd9\u662f\u5bf9\u738b\u5b50\u7279\u522b\u7684\u4f18\u5f85\u4e86\uff0c\u82e5\u662f\u522b\u4eba\uff0c\u7eb5\u6709\u5929\u5c71\u96ea\u83b2\uff0c\u4e5f\u8981\u8003\u8bd5\u3002\u201d\u8bf4\u7740\uff0c\u5ad6 \u4e86\u6842\u534e\u751f\u4e00\u773c\uff0c\u53c8\u5fae\u7b11\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u82e5\u7136\u522b\u4eba\u771f\u7684\u6709\u5929\u5c71\u96ea\u83b2\uff0c\u9001\u7ed9\u516c\u4e3b\uff0c\u516c\u4e3b\u4e5f\u4e0d\u4f1a\u767d\u8981 \u4ed6\u7684\uff0c\u516c\u4e3b\u613f\u610f\u4ee5\u4e00\u4ef6\u5929\u4e0b\u72ec\u4e00\u65e0\u4e8c\u7684\u5947\u73cd\u4e0e\u4ed6\u76f8\u6362\u3002\u201d\u6842\u534e\u751f\u5931\u58f0\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\u5947\u73cd\uff1f\u201d \u3000\u3000\u90a3\u5bab\u5973\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u77e5\u9053\u662f\u4ec0\u4e48\u5947\u73cd\uff0c\u4f46\u542c\u516c\u4e3b\u8bf4\uff0c\u90a3\u4ef6\u5947\u73cd\uff0c\u8c01\u4eba\u5f97\u4e86\uff0c\u4fbf\u53ef\u4ee5\u65e0\u654c \u4e8e\u5929\u4e0b\uff01\u5148\u751f\uff0c\u4f60\u6709\u5929\u5c71\u96ea\u83b2\u4e48\uff1f\u8fd9\u673a\u4f1a\u53ef\u4e07\u4e07\u4e0d\u80fd\u9519\u8fc7\uff01\u7eb5\u7136\u4f60\u4e0d\u6562\u4e0e\u516c\u4e3b\u6bd4\u8bd5\uff0c\u7eb5\u7136\u4f60 \u4e0d\u60f3\u5a36\u516c\u4e3b\u4e3a\u59bb\uff0c\u5f97\u4e86\u8fd9\u4ef6\u7a00\u4e16\u4e4b\u73cd\uff0c\u5df2\u8db3\u4ee5\u79f0\u96c4\u5929\u4e0b\uff01\u201d\u6842\u534e\u751f\u5413\u5f97\u51e0\u4e4e\u8df3\u4e86\u8d77\u6765\uff0c\u5fc3\u4e2d \u60f3\u9053\uff1a\u201c\u8fd9\u516c\u4e3b\u8bf4\u7684\uff0c\u83ab\u975e\u5c31\u662f\u51b0\u5757\u5bd2\u5149\u5251\uff1f\u5440\uff0c\u5440\uff0c\u96be\u9053\uff0c\u8fd9\u4f4d\u516c\u4e3b\u5f53\u771f\u5c31\u662f\u6211\u7684\u534e\u738b\u59b9 \u59b9\uff1f\u4e0d\uff0c\u4e0d\uff0c\u8fd9\u5b9e\u5728\u6559\u4eba\u96be\u4ee5\u76f8\u4fe1\uff01\u201d \u3000\u3000\u56fd\u738b\u542c\u5f97\u83ab\u540d\u5176\u5999\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u6211\u5bab\u4e2d\u90a3\u6709\u8fd9\u6837\u7684\u5947\u73cd\uff1f\u201d\u4f46\u4ed6\u542c\u4e86\u5bab\u5973\u7684\u8bf4\u8bdd\u548c\u5764 \u8272\uff0c\u5982\u6709\u6240\u89e6\uff0c\u987f\u7136\u60f3\u8d77\u4e86\u665a\u95f4\u4e4b\u4e8b\uff0c\u516c\u4e3b\u5bf9\u4ed6\u8bf4\uff0c\u63a2\u542c\u5230\u4ece\u4e2d\u56fd\u6765\u4e86\u4e00\u4f4d\u5c11\u5e74\uff0c\u672c\u9886\u9ad8 \u5f3a\uff0c\u8eab\u6000\u5947\u836f\uff0c\u8bf7\u56fd\u738b\u6d3e\u4eba\u8fce\u63a5\u3002\u56fd\u738b\u7d20\u6765\u4fe1\u4efb\u516c\u4e3b\uff0c\u5f53\u65f6\u75c5\u4e2d\u795e\u5026\uff0c\u4e5f\u65e0\u6687\u95f4\u5b83\u662f\u600e\u4e48\u77e5 \u9053\u7684\uff0c\u800c\u4eca\u89c1\u6b64\u60c5\u72b6\uff0c\u604d\u7136\u5927\u609f\uff1a\u201c\u602a\u4e0d\u5f97\u5973\u513f\u8981\u6211\u6d3e\u4eba\u53bb\u8fce\u63a5\u8fd9\u4f4d\u4e2d\u56fd\u5c11\u5e74\uff0c\u539f\u6765\u5979\u65e9\u5df2 \u5c5e\u610f\u4ed6\u4e86\uff01\u201d\u7559\u5fc3\u4e00\u77a7\uff0c\u4f46\u89c1\u6842\u534e\u751f\u6c14\u5b87\u8f69\u6602\uff0c\u82f1\u633a\u79c0\u62d4\uff0c\u66f4\u80dc\u4e8e\u5370\u5ea6\u738b\u5b50\uff0c\u4e0d\u7531\u5f97\u6697\u6697\u610f \u52a8\uff0c\u4f46\u8f6c\u5ff5\u4e00\u60f3\uff1a\u201c\u96c5\u5fb7\u661f\u5230\u5e95\u662f\u4e2a\u738b\u5b50\uff0c\u6842\u534e\u751f\u53c8\u7109\u80fd\u4e0e\u4e4b\u76f8\u6bd4\uff1f\u201d \u3000\u3000\u6842\u534e\u751f\u4e4d\u559c\u8fd8\u60ca\uff0c\u96c5\u5fb7\u661f\u5446\u5446\u53d1\u695e\uff0c\u82e5\u56fd\u738b\u6414\u9996\u7574\u66d9\uff0c\u5404\u6000\u5fc3\u4e8b\uff0c\u4e00\u65f6\u4e4b\u95f2\uff0c\u8c01\u90fd\u6ca1\u6709 \u8bf4\u8bdd\u3002\u5ffd\u89c1\u4e00\u4e2a\u6b66\u58eb\u5c06\u4e00\u4e2a\u767d\u8863\u5587\u561b\u5e26\u5165\u5bab\u4e2d\uff0c\u56fd\u738b\u9053\uff1a\u201c\u4e2d\u56fd\u9ad8\u50e7\u5230\u4e86\u3002\u201d\u8f6c\u5934\u5bf9\u90a3\u5bab\u5973 \u8bf4\u9053\uff1a\u201c\u4f60\u5148\u56de\u53bb\u5427\uff0c\u529d\u516c\u4e3b\u767e\u4ef6\u601d\u91cf\uff0c\u4e0d\u8981\u80e1\u95f9\uff01\u201d \u3000\u3000\u90a3\u767d\u8863\u5587\u561b\u8d70\u4e0a\u77f3\u9636\uff0c\u6842\u534e\u751f\u53c8\u662f\u4e00\u559c\u4e00\u60ca\uff0c\u539f\u6765\u8fd9\u767d\u8863\u5587\u561b\u6b63\u662f\u9ea6\u58eb\u8fe6\u5357\u3002\u9ea6\u58eb\u8fe6\u5357 \u89c1\u4e86\u6842\u534e\u751f\u5374\u5e76\u4e0d\u592a\u611f\u610f\u5916\uff0c\u539f\u6765\u4ed6\u5df2\u5230\u8fc7\u5e03\u8fbe\u62c9\u5bab\uff0c\u89c1\u8fc7\u8fbe\u8d56\u6d3b\u4f5b\uff0c\u77e5\u9053\u8fbe\u8d56\u6d3b\u4f5b\u6709\u4e66\u4fe1 \u7ed9\u6842\u534e\u751f\u5e26\u4e0e\u56fd\u738b\u3002\u4e0d\u8fc7\uff0c\u6545\u65e7\u76f8\u9022\uff0c\u4ea6\u56e0\u662f\u5fc3\u4e2d\u6b22\u559c\u3002\u53ea\u6709\u90a3\u5c3c\u6cca\u5c14\u738b\u5b50\u5374\u662f\u5fc3\u4e2d\u6697\u53eb\u4e0d \u597d\uff0c\u8bda\u6050\u9ea6\u58eb\u8fe6\u5357\u628a\u9b54\u9b3c\u57ce\u7684\u4e8b\u8bf4\u51fa\u6765\u3002 \u3000\u3000\u9ea6\u58eb\u8fe6\u5357\u5148\u732e\u4e0a\u4e86\u767d\u6559\u6cd5\u738b\u7684\u4e66\u4fe1\uff0c\u56fd\u738b\u5c55\u4fe1\u751a\u559c\uff0c\u770b\u5b8c\u4e4b\u540e\uff0c\u5374\u53c8\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u600e \u4e48\u8d35\u6cd5\u738b\u7684\u6cd5\u6756\u4f1a\u5728\u655d\u56fd\u5462\uff1f\u201d\u539f\u6765\u6cd5\u738b\u8fd9\u5c01\u4fe1\u4e43\u662f\u5411\u5c3c\u6cca\u5c14\u738b\u7d22\u53d6\u4e5d\u5883\u6cd5\u6756\u7684\u3002\u9ea6\u58eb\u8fe6\u5357 \u9053\uff1a\u201c\u5c0f\u50e7\u5728\u897f\u85cf\u4e4b\u65f6\u6240\u5931\uff0c\u90a3\u662f\u8d35\u56fd\u7684\u56fd\u5e08\u593a\u53bb\u7684\u3002\u201d\u8bf4\u65f6\uff0c\u770b\u4e86\u5c3c\u6cca\u5c14\u738b\u5b50\u4e00\u773c\u3002\u539f\u6765 \u5728\u5f53\u65e5\u593a\u6756\u4e4b\u65f6\uff0c\u5c3c\u6cca\u5c14\u738b\u5b50\u4e5f\u66fe\u51fa\u624b\u3002 \u3000\u3000\u5c3c\u6cca\u5c14\u738b\u5b50\u6025\u5fd9\u6253\u65ad\u9ea6\u58eb\u8fe6\u5357\u7684\u8bdd\u5934\uff0c\u5411\u56fd\u738b\u594f\u9053\uff1a\u201c\u4eca\u5e74\u4e8c\u4e09\u6708\u95f4\uff0c\u6211\u4e0e\u56fd\u5e08\u66fe\u5230\u5e03 \u8fbe\u62c9\u5bab\u53c2\u62dc\uff0c\u56de\u6765\u4e4b\u540e\uff0c\u56e0\u53d4\u53d4\u9f99\u4f53\u6b20\u59a5\uff0c\u7ecf\u8fc7\u60c5\u5f62\uff0c\u672a\u53ca\u8be6\u544a\u3002\u201d\u738b\u5b50\u7684\u8bdd\uff0c\u53ea\u6709\u4e00\u534a\u662f \u771f\uff0c\u5230\u8fc7\u5e03\u8fbe\u62c9\u7684\u5bab\u662f\u90a3\u7ea2\u8863\u756a\u50e7\uff0c\u800c\u4ed6\u5219\u53ea\u5728\u9b54\u9b3c\u57ce\u4e2d\uff0c\u5e03\u7f6e\u4fb5\u7565\u897f\u85cf\u7684\u4e8b\u800c\u5df2\u3002\u56fd\u738b \u9053\uff1a\u201c\u90a3\u4e48\uff0c\u4f60\u77e5\u9053\u8fd9\u4ef6\u4e8b\u5417\uff1f\u201d\u738b\u5b50\u9053\uff1a\u201c\u7980\u53d4\u7687\uff0c\u767d\u6559\u6cd5\u738b\u7684\u6cd5\u6756\uff0c\u786e\u662f\u6469\u5170\u6cd5\u5e08\u6240 \u593a\u3002\u201d\u6469\u5170\u4fbf\u662f\u90a3\u7ea2\u8863\u756a\u50e7\u7684\u540d\u5b57\u3002\u4ed6\u7d20\u4e3a\u56fd\u738b\u4fe1\u8d56\uff0c\u56fd\u738b\u542c\u4e86\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u6469\u5170\u6cd5 \u5e08\uff0c\u600e\u4e48\u505a\u51fa\u8fd9\u6837\u7684\u4e8b\uff1f\u201d\u738b\u5b50\u8bf4\u9053\uff1a\u201c\u53d4\u53d4\u4e0d\u95ee\uff0c\u6211\u4e0d\u6562\u8bf4\uff0c\u6469\u5170\u6cd5\u5e08\uff0c\u91ce\u5fc3\u52c3\u52c3\uff0c\u4ed6\u60f3 \u4eff\u7167\u897f\u85cf\u8fbe\u73ed\u60ee\u8d56\u6d3b\u4f5b\u7684\u505a\u6cd5\uff0c\u5c06\u6765\u505a\u672c\u56fd\u7684\u6d3b\u4f5b\uff0c\u8981\u4eca\u6559\u6743\u538b\u5012\u738b\u6743\uff0c\u662f\u4ee5\u4ed6\u62a2\u4e86\u6cd5\u738b\u7684 \u6cd5\u6756\uff0c\u6cd5\u6756\u662f\u4f5b\u6559\u6743\u5a01\u7684\u8c61\u5fb5\u4e4b\u4e00\uff0c\u5c06\u6765\u4ed6\u597d\u81ea\u4e0a\u5c0a\u53f7\uff01\u201d\u56fd\u738b\u60ca\u5f97\u5446\u4e86\uff0c\u738b\u4e8e\u53c8\u9053\uff1a\u201c\u5df4 \u52d2\u521a\u624d\u6240\u8bf4\uff0c\u4eca\u665a\u4ed6\u4eec\u5728\u5fa1\u6797\u519b\u603b\u7ba1\u7684\u53e4\u5821\u4e2d\uff0c\u6469\u5170\u6cd5\u5e08\u4e0d\u662f\u4e5f\u5728\u573a\u5417\uff1f\u201d\u5df4\u52d2\u533b\u751f\u9053\uff1a \u201c\u6b63\u662f\u3002\u56fd\u5e08\u4e5f\u77a5\u529d\u8fc7\u6211\u8981\u542c\u603b\u7ba1\u7684\u8bdd\u3002\u201d\u56fd\u738b\u9707\u6012\u4e4b\u6781\uff0c\u8fde\u53eb\uff1a\u201c\u53cd\u4e86\uff0c\u53cd\u4e86\uff01\u201d\u5c3c\u6cca\u5c14 \u738b\u5b50\u53c8\u9053\uff1a\u201c\u4f9d\u4f84\u513f\u770b\u6765\uff0c\u4e0b\u6bd2\u4e4b\u4e8b\uff0c\u53ea\u6015\u5c31\u662f\u6cd5\u5e08\u7684\u4e3b\u610f\uff0c\u5374\u53eb\u5fa1\u6797\u519b\u5e72\u7684\uff01\u201d\u56fd\u738b\u9053\uff1a \u201c\u4e00\u5b9a\u662f\u8fd9\u6837\uff01\u201d\u738b\u5b50\u9053\uff1a\u201c\u90a3\u4e48\u5c31\u8bf7\u53d4\u53d4\u4e0b\u4ee4\uff0c\u8d76\u5feb\u5c06\u6cd5\u5e08\u902e\u6355\uff01\u201d \u3000\u3000\u5c3c\u6cca\u5c14\u738b\u5b50\u8fd9\u756a\u8bdd\u5927\u51fa\u6842\u534e\u751f\u4e0e\u9ea6\u58eb\u8fe6\u5357\u610f\u6599\u4e4b\u5916\uff0c\u4e24\u4eba\u90fd\u660e\u660e\u77e5\u9053\u738b\u5b50\u8fd9\u756a\u8bf4\u8bdd\uff0c\u4e0d \u5c3d\u4e0d\u5b9e\u4e4b\u5904\u6781\u591a\uff0c\u4f46\u758f\u4e0d\u95f4\u4eb2\uff0c\u4e00\u65f6\u4e4b\u95f4\u4e5f\u4e0d\u4fbf\u5728\u56fd\u738b\u9762\u524d\u622e\u7834\u3002 \u3000\u3000\u9ea6\u58eb\u8fe6\u5357\u53ea\u8981\u5f97\u56de\u6cd5\u6756\uff0c\u4e5f\u4e0d\u613f\u591a\u751f\u4e8b\u7aef\uff0c\u800c\u4e14\u4ed6\u4ea6\u77e5\u9053\u738b\u5b50\u662f\u56fd\u738b\u7684\u4eb2\u4f84\uff0c\u624b\u63e1\u5175\u9a6c \u5927\u6743\uff0c\u96be\u5f97\u4ed6\u80af\u51fa\u5934\uff0c\u8ffd\u6355\u6cd5\u5e08\uff0c\u7f34\u56de\u6cd5\u6756\uff0c\u5982\u6b64\u4e00\u6765\uff0c\u4e24\u5168\u5176\u7f8e\uff0c\u80fd\u591f\u4e0d\u628a\u738b\u5b50\u7275\u8fde\u5728 \u5185\uff0c\u81ea\u662f\u6700\u5999\u4e0d\u8fc7\u3002 \u3000\u3000\u56fd\u738b\u6c89\u541f\u534a\u664c\uff0c\u8bf4\u9053\uff1a\u201c\u6cd5\u5e08\u6b66\u529f\u6781\u5f3a\uff0c\u515a\u7fbd\u4ea6\u591a\uff0c\u770b\u6765\u53ea\u6709\u7531\u4f60\u53bb\u62d8\u6355\u4ed6\uff0c\u6211\u624d\u653e \u5fc3\u3002\u201d\u738b\u5b50\u8bf4\u9053\uff1a\u201c\u8fd9\u6837\u7684\u5927\u4e8b\uff0c\u4f84\u513f\u81ea\u5e94\u4eb2\u81ea\u51fa\u9a6c\uff0c\u4e3a\u53d4\u53d4\u6548\u52b3\u624d\u662f\u3002\u4e0d\u8fc7\uff0c\u4f84\u513f\u5374\u53e6\u6709 \u4e00\u4e2a\u4e3b\u610f\uff0c\u8bf7\u53d4\u53d4\u5353\u593a\u3002\u201d\u56fd\u738b\u9053\uff1a\u201c\u4f60\u8bf4\u4fbf\u662f\u3002\u201d\u738b\u5b50\u9053\uff1a\u201c\u6cd5\u5e08\u6b66\u529f\uff0c\u786e\u662f\u56fd\u4e2d\u65e0\u654c\u3002 \u7eb5\u80fd\u5c06\u4ed6\u64d2\u83b7\uff0c\u6b66\u58eb\u4ea6\u5fc5\u6709\u6b7b\u4f24\u3002\u6842\u5148\u751f\u548c\u96c5\u5fb7\u661f\u738b\u5b50\u90fd\u662f\u672c\u9886\u8d85\u51e1\u7684\u4e0a\u56fd\u9ad8\u8d24\uff0c\u9ea6\u58eb\u8fe6\u5357 \u662f\u6cd5\u738b\u4f7f\u8005\uff0c\u8fd8\u81ea\u4e2d\u56fd\u9752\u6d77\u6765\u81f3\u6b64\u95f4\uff0c\u4ea6\u5fc5\u662f\u6b66\u529f\u4e00\u53e3\u5339\u660e\u4e4b\u58eb\uff0c\u4f84\u513f\u60f3\u501f\u91cd\u4e09\u4f4d\u9ad8\u8d24\u4e4b \u529b\uff0c\u5f80\u6355\u56fd\u5e08\u3002\u82e5\u7136\u662f\u96c5\u5fb7\u661f\u738b\u5b50\u5c06\u4ed6\u64d2\u83b7\uff0c\u90a3\u5c31\u662f\u5927\u5927\u6709\u529f\u6211\u56fd\uff0c\u6309\u6211\u56fd\u6cd5\u5f8b\uff0c\u6709\u975e\u5e38\u4e4b \u529f\uff0c\u4fbf\u6709\u975e\u5e38\u4e4b\u8d4f\uff0c\u90a3\u65f6\u7531\u53d4\u53d4\u4f5c\u4e3b\uff0c\u5c06\u59b9\u59b9\u8bb8\u914d\u7ed9\u4ed6\uff0c\u81ea\u662f\u4e3e\u56fd\u540c\u6b23\uff0c\u59b9\u59b9\u4ea6\u65ad\u4e0d\u4f1a\u6709\u6240 \u5177\u8a00\uff1a\u82e5\u662f\u6842\u5148\u751f\u5c06\u4ed6\u64d2\u83b7\u5462\uff1f\u90a3\u672b\u5982\u4f55\u539a\u8d4f\uff0c\u53d4\u53d4\u4ea6\u5e94\u968f\u4ed6\u6240\u8bf7\uff0c\u6210\u5168\u4ed6\u7684\u5fc3\u613f\u3002\u201d\u8a00\u4e0b \u4e4b\u610f\uff0c\u5373\u7b97\u6842\u534e\u751f\u8bf7\u5a5a\u516c\u4e3b\uff0c\u4ea6\u5fc5\u5141\u51c6\u65e0\u7591\u3002 \u3000\u3000\u6842\u534e\u751f\u751a\u662f\u7591\u5fc3\uff0c\u4ed6\u5728\u9b54\u9b3c\u57ce\u4e2d\uff0c\u66fe\u7aa5\u9690\u79d8\uff0c\u6df1\u77e5\u738b\u5b50\u4e0e\u6cd5\u5e08\u4e43\u662f\u4e00\u4e18\u4e4b\u732a\uff0c\u72fc\u72d9\u4e3a \u5978\u3002\u5374\u4f55\u4ee5\u738b\u5b50\u4eca\u665a\uff0c\u4e0d\u4f46\u5927\u8bf4\u6cd5\u5e08\u7684\u574f\u8bdd\uff0c\u800c\u4e14\u8fd8\u8981\u5c06\u4ed6\u7f6e\u4e8e\u6b7b\u5730\uff1f \u3000\u3000\u6842\u534e\u751f\u786e\u662f\u6709\u6240\u4e0d\u77e5\u3002\u539f\u6765\u6697\u4e2d\u4e0b\u6bd2\u7684\uff0c\u5b9e\u5728\u5c31\u662f\u56fd\u738b\u7684\u4f84\u513f\uff0c\u4e5f\u5c31\u662f\u8fd9\u4f4d\u738b\u5b50\u3002\u539f\u6765 \u5c3c\u6cca\u5c14\u540c\u65f6\u63a5\u53d7\u4e1c\u65b9\u897f\u65b9\u7684\u6587\u5316\u5f71\u54cd\uff0c\u7167\u897f\u6b27\u3001\u4e2d\u4e9a\u3001\u963f\u523a\u4f2f\u3001\u5370\u5ea6\u8bf8\u56fd\u7684\u89c4\u77e9\uff0c\u5973\u513f\u5747\u53ef \u7ee7\u627f\u738b\u4f4d\uff1a\u82e5\u4f9d\u4e2d\u56fd\u7684\u4e60\u60ef\uff0c\u6709\u7537\u5b50\u53ef\u4ee5\u4e3a\u7687\uff0c\u5973\u513f\u65ad\u65ad\u4e0d\u80fd\u4f20\u4f4d\u3002\u56fd\u738b\u6709\u4e00\u5973\uff0c\u53e6\u5916\u6700\u4eb2 \u8fd1\u7684\u5b97\u5ba4\uff0c\u5c31\u662f\u8fd9\u4f4d\u738b\u5b50\u3002\u6240\u4ee5\u738b\u4f4d\u82e5\u4e0d\u662f\u516c\u4e3b\u7ee7\u627f\uff0c\u5c31\u5fc5\u5b9a\u662f\u8fd9\u4f4d\u738b\u5b50\u7ee7\u627f\u3002\u56fd\u4e2d\u7684\u5927\u81e3 \u4e5f\u662f\u5206\u4e3a\u4e24\u6d3e\uff0c\u4e00\u6d3e\u4e3b\u5f20\u62e5\u7acb\u516c\u4e3b\uff0c\u4e00\u6d3e\u4e3b\u5f20\u62e5\u7acb\u738b\u5b50\u3002 \u3000\u3000\u516c\u4e3b\u5e76\u4e0d\u628a\u738b\u4f4d\u653e\u5728\u773c\u4e2d\uff0c\u738b\u5b50\u5374\u662f\u5904\u5fc3\u79ef\u8651\u3002\u4ed6\u4e4b\u6240\u4ee5\u56fe\u8c0b\u897f\u85cf\uff0c\u4ea6\u4e0e\u4e89\u593a\u738b\u4f4d\u6709 \u5173\u3002\u56e0\u4e3a\u82e5\u80fd\u7acb\u529f\u5f02\u57df\uff0c\u4e0d\u4f46\u80fd\u4f7f\u4ed6\u53d7\u56fd\u4eba\u5d07\u62dc\uff0c\u800c\u4e14\u53ef\u4ee5\u72ec\u63fd\u519b\u6743\uff0c\u63a7\u5236\u5168\u56fd\u3002\u4e0d\u6599\u5374\u88ab \u6842\u534e\u751f\u5728\u65e0\u610f\u4e4b\u4e2d\u7834\u574f\u3002\u56e0\u6b64\u738b\u5b50\u56de\u56fd\u4e4b\u540e\uff0c\u4f7f\u4e0e\u6469\u5170\u6cd5\u5e08\u53ca\u5fa1\u6797\u519b\u603b\u7ba1\u5408\u8c0b\uff0c\u6697\u5bb3\u56fd\u738b\u3002 \u3000\u3000\u4ed6\u4eec\u90fd\u5fcc\u60ee\u516c\u4e3b\uff0c\u4e0d\u6562\u7528\u666e\u901a\u5bdf\u770b\u5f97\u51fa\u7684\u6bd2\u836f\uff0c\u56e0\u800c\u7528\u559c\u9a6c\u62c9\u96c5\u5c71\u7279\u6709\u7684\u963f\u4fee\u7f57\u82b1\uff0c\u914d \u5408\u4e86\u5176\u4ed6\u6162\u6027\u6bd2\u836f\uff0c\u5236\u6210\u4e86\u4e00\u79cd\u6740\u4eba\u6beb\u65e0\u75d5\u8ff9\u7684\u6bd2\u836f\uff0c\u672c\u6765\u518d\u8fc7\u4e00\u6708\uff0c\u4fbf\u53ef\u4eca\u56fd\u738b\u65e0\u58f0\u65e0\u55c5 \u7684\u6b7b\u4ea1\uff0c\u5374\u53c8\u4e0d\u6599\u88ab\u6842\u534e\u751f\u7684\u5929\u5c71\u96ea\u83b2\u6551\u4e86\u3002 \u3000\u3000\u738b\u5b50\u751f\u6015\u79d8\u5bc6\u6f0f\uff0c\u56e0\u6b64\u5148\u628a\u5fa1\u6797\u519b\u603b\u7ba1\u6740\u4e86\u706d\u53e3\u3002\u7136\u540e\u4e00\u9762\u6697\u4e2d\u53eb\u6469\u5170\u6cd5\u5e08\u8fde\u591c\u9003\u8d70\uff0c \u4e00\u9762\u5411\u56fd\u738b\u8fdb\u8a00\uff0c\u6fc0\u4f7f\u56fd\u738b\u62d8\u6355\u6cd5\u5e08\u3002\u81f3\u4e8e\u4ed6\u8bf7\u6842\u534e\u751f\u548c\u96c5\u5fb7\u661f\u540c\u53bb\u8ffd\u6355\uff0c\u5b9e\u4e43\u4e00\u77f3\u4e8c\u9e1f\u4e4b \u8ba1\uff0c\u539f\u6765\u63d0\u6469\u8fbe\u591a\u4e5f\u662f\u738b\u5b50\u8bf7\u6765\u7684\uff0c\u4e0d\u8fc7\u63d0\u6469\u8fbe\u591a\u5e76\u4e0d\u77e5\u9053\u738b\u5b50\u8c0b\u5bb3\u56fd\u738b\u7684\u5185\u60c5\uff0c\u738b\u5b50\u662f\u8bb8 \u8bfa\u5c06\u6765\u5e2e\u52a9\u4ed6\u6500\u767b\u73e0\u7a46\u6717\u739b\u5cf0\u5c06\u4ed6\u8bf7\u6765\u7684\u3002\u6469\u5170\u6cd5\u5e08\u9003\u8d70\uff0c\u738b\u5b50\u5df2\u8bf7\u4e86\u63d0\u6469\u8fbe\u591a\u62a4\u9001\u4ed6\u3002\u738b \u5b50\u6df1\u77e5\u6842\u534e\u751f\u548c\u96c5\u5fb7\u661f\u90fd\u662f\u975e\u5e38\u4e4b\u4eba\uff0c\u4e0d\u8bba\u90a3\u4e00\u4e2a\u505a\u4e86\u516c\u4e3b\u7684\u4e08\u592b\uff0c\u90fd\u5c06\u662f\u4ed6\u4e89\u593a\u738b\u4f4d\u7684\u5927 \u969c\u788d\u3002\u662f\u4ee5\u4ed6\u53eb\u6842\u3001\u96c5\u4e8c\u4eba\u524d\u53bb\u8ffd\u6355\uff0c\u5b9e\u662f\u60f3\u501f\u63d0\u6469\u8fbe\u591a\u4e4b\u624b\uff0c\u5c06\u4ed6\u4eec\u9664\u53bb\uff01\u7528\u5fc3\u4e4b\u6bd2\uff0c\u5230 \u4e86\u6781\u70b9\uff01\u6842\u534e\u751f\u4e0e\u5370\u5ea6\u738b\u5b50\u867d\u7136\u806a\u660e\uff0c\u5374\u600e\u6837\u4e5f\u731c\u60f3\u4e0d\u5230\uff01  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u9b44\u5bd2\u5149\u5251\u300b\u2014\u2014\u7b2c\u4e5d\u7ae0\u3000\u51b0\u5f39\u7389\u5251\u4f0f\u9b54\u5934 \u6881\u7fbd\u751f\u300a\u51b0\u9b44\u5bd2\u5149\u5251\u300b \u7b2c\u4e5d\u7ae0\u3000\u51b0\u5f39\u7389\u5251\u4f0f\u9b54\u5934 \u3000\u3000\u5c3c\u6cca\u5c14\u738b\u8bf7\u4ed6\u4eec\u53bb\u8ffd\u6355\u53db\u56fd\u7684\u6469\u5170\u6cd5\u5e08\uff0c\u96c5\u5fb7\u661f\u738b\u5b50\u60f3\u83b7\u5f97\u516c\u4e3b\uff0c\u9ea6\u58eb\u8fe6\u5357\u60f3\u8ffd\u56de\u6cd5 \u6756\uff0c\u90fd\u7b54\u5e94\u4e86\u3002\u6842\u534e\u751f\u7a0d\u7a0d\u8e0c\u8e87\u60f3\u4e86\u4e00\u60f3\uff0c\u8bf4\u9053\uff1a\u201c\u4e3a\u8d35\u56fd\u9664\u5978\uff0c\u81ea\u662f\u4e49\u4e0d\u5bb9\u8f9e\uff0c\u4f46\u6211\u53ef\u4e0d \u671b\u4ec0\u4e48\u916c\u62a5\u3002\u201d\u56fd\u738b\u5927\u559c\uff0c\u53eb\u4eba\u5728\u5fa1\u6168\u91cc\u6311\u9009\u4e86\u4e09\u5339\u6700\u597d\u7684\u963f\u523a\u4f2f\u826f\u9a6c\uff0c\u4fbf\u53eb\u738b\u5b50\u5e26\u9886\u4ed6\u4eec \u53bb\u8ffd\u3002 \u3000\u3000\u51fa\u4e86\u5bab\u95e8\uff0c\u738b\u5b50\u8bf4\u9053\uff1a\u201c\u56fd\u5e08\u82e5\u662f\u9003\u4ea1\uff0c\u5fc5\u5b9a\u8981\u7ecf\u8fc7\u4f55\u5fb7\u6b63\u5ce1\u53e3\uff0c\u5ce1\u53e3\u5c31\u5728\u745e\u626c\u5e03\u5c71\u5357 \u9762\u5341\u91cc\u4e4b\u5730\uff0c\u518d\u8fdb\u53bb\u5c31\u662f\u745e\u829d\u90a3\u5c71\u3002\u5404\u4eba\u8fdb\u5165\u4eac\u57ce\u4e4b\u65f6\uff0c\u90fd\u671b\u5f97\u745e\u626c\u5e03\u5c71\uff0c\u6842\u5148\u751f\u66f4\u662f\u521a\u521a \u4ece\u5c71\u9e93\u90a3\u5ea7\u5fa1\u6797\u519b\u603b\u7ba1\u7684\u522b\u5885\u51fa\u6765\uff0c\u60f3\u5fc5\u8ba4\u8bc6\u9053\u8def\u3002\u5c31\u70e6\u6842\u5148\u751f\u9886\u8def\uff0c\u4ece\u6b63\u8def\u53bb\u8ffd\u3002\u6211\u6284\u5c0f \u8def\u5230\u745e\u626c\u5e03\u5c71\u5c71\u4e0a\u7684\u5cf0\u706b\u53f0\u70b9\u71c3\u70fd\u706b\uff0c\u597d\u53eb\u5b88\u5173\u7684\u4eba\u4e5f\u5e2e\u5fd9\u62e6\u622a\u3002\u201d\u6842\u534e\u751f\u5fc3\u4e2d\u4e00\u52a8\uff0c\u96c5\u5fb7 \u661f\u738b\u5b50\u5374\u5df2\u8bf4\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u5404\u81ea\u8bf7\u4fbf\u3002\u5230\u4f55\u5fb7\u74e6\u5ce1\u53e3\u7684\u8def\uff0c\u6211\u4e5f\u8ba4\u5f97\u3002\u201d \u3000\u3000\u4e09\u9a91\u9a8f\u9a6c\u9010\u7535\u8ffd\u98ce\uff0c\u4e0d\u6d88\u534a\u4e2a\u65f6\u8fb0\uff0c\u4f7f\u5230\u4e86\u745e\u626c\u5e03\u5c71\u5c71\u811a\uff0c\u4f46\u89c1\u90a3\u5ea7\u5fa1\u6797\u519b\u603b\u7ba1\u53e4\u5821\u5f0f \u7684\u522b\u5885\uff0c\u5df2\u6d78\u5728\u878d\u878d\u5927\u706b\u4e4b\u4e2d\uff0c\u6842\u534e\u751f\u5fc3\u9053\uff1a\u201c\u8fd9\u738b\u5b50\u5012\u662f\u6599\u5f97\u4e0d\u9519\uff0c\u90a3\u7ea2\u8863\u756a\u50e7\u679c\u7136\u662f\u8fde \u591c\u9003\u4e86\u3002\u53ea\u4e0d\u77e5\u63d0\u6469\u8fbe\u591a\u662f\u5426\u4e0e\u4ed6\u4e00\u9053\u3002\u201d\u60f3\u53bb\u63d0\u9192\u9ea6\u58eb\u8fe6\u5357\uff0c\u9ea6\u58eb\u8fe6\u5357\u90a3\u5339\u9a6c\u8dd1\u5f97\u6700\u524d\uff0c \u6842\u534e\u751f\u8ffd\u4e86\u597d\u4e00\u4f1a\u5b50\uff0c\u8fd8\u662f\u6ca1\u6709\u8ffd\u4e0a\u3002 \u3000\u3000\u4e1c\u65b9\u5929\u9645\u6e10\u6e10\u9732\u51fa\u66d9\u5149\uff0c\u6842\u534e\u751f\u60f3\u8d77\u8fd9\u4e00\u4e2a\u665a\u4e0a\u7684\u79cd\u79cd\u79bb\u5947\u906d\u9047\uff0c\u601d\u6f6e\u8d77\u4f0f\u4e0d\u5b9a\uff0c\u5ffd\u542c \u5f97\u9ea6\u58eb\u8fe6\u5357\u5728\u524d\u9762\u626c\u58f0\u53eb\u9053\uff1a\u201c\u5feb\u6765\uff0c\u5feb\u6765\uff0c\u6211\u5df2\u542c\u5230\u4ed6\u4eec\u7684\u8f66\u58f0\u4e86\u3002\u201d\u5370\u5ea6\u738b\u5b50\u5220\u5566\u4e00 \u97ad\uff0c\u597d\u50cf\u552f\u6050\u6842\u534e\u751f\u4e0e\u4ed6\u4e89\u529f\u4f3c\u7684\uff0c\u50ac\u5f97\u9a8f\u9a6c\u5982\u98ce\uff0c\u500f\u7684\u8d76\u8fc7\u6842\u534e\u751f\u524d\u9762\u3002\u6842\u534e\u751f\u5fae\u5fae\u4e00 \u7b11\uff0c\u62ac\u5934\u4e00\u770b\uff0c\u4f46\u89c1\u4e24\u5c71\u76f8\u8fde\uff0c\u4e2d\u95f4\u5f00\u4e86\u4e00\u6761\u72ed\u957f\u7684\u5ce1\u8c37\uff0c\u201c\u4f55\u5fb7\u74e6\u201d\u7684\u672c\u4e49\u5373\u201c\u5251\u5288 \u53e3\u201d\uff0c\u770b\u8fd9\u5ce1\u8c37\u5f62\u52bf\uff0c\u5f53\u771f\u5c31\u50cf\u662f\u7528\u4e00\u628a\u5927\u5251\uff0c\u4ece\u4e24\u5c71\u4e4b\u95f4\u5288\u4e0b\u4e00\u822c\u3002\u5ce1\u8c37\u5e7d\u6df1\u66f2\u6298\uff0c\u671b\u4e0d \u5230\u91cc\u8fb9\u60c5\u666f\uff0c\u4f46\u5374\u786e\u5b9e\u53ef\u4ee5\u542c\u95fb\u5f97\u91cc\u9762\u9686\u9686\u7684\u9a6c\u8f66\u58f0\u3002 \u3000\u3000\u4e09\u4eba\u4e4b\u4e2d\uff0c\u9ea6\u58eb\u8fe6\u5357\u56e0\u4e3a\u662f\u5728\u9752\u6d77\u8349\u539f\u957f\u5927\u7684\uff0c\u9a91\u672f\u6700\u597d\uff0c\u4e00\u9a6c\u5f53\u5148\uff0c\u9020\u5165\u5ce1\u8c37\uff0c\u4e0d\u6d88 \u4e00\u523b\uff0c\u4fbf\u8ffd\u4e0a\u4e86\u90a3\u8f86\u9a6c\u8f66\u3002\u9ea6\u58eb\u8fe6\u5357\u5927\u559d\u4e00\u58f0\uff0c\u63d0\u8d77\u6597\u5927\u7684\u7985\u6756\uff0c\u4fbf\u5411\u90a3\u6392\u62c9\u8f66\u7684\u5065\u9a6c\u626b \u53bb\uff0c\u5413\u5f97\u90a3\u51e0\u5339\u9a6c\u8df3\u8dc3\u957f\u5636\uff0c\u5c06\u8f66\u8f86\u4e5f\u629b\u4e86\u8d77\u6765\uff0c\u4f46\u542c\u5f97\u54d7\u5566\u5566\u4e00\u7247\u58f0\u54cd\uff0c\u64a4\u4e0b\u4e86\u6ee1\u5730\u7684\u91d1 \u94f6\u73e0\u5b9d\uff0c\u60f3\u662f\u6469\u5170\u6cd5\u5e08\u706b\u70e7\u603b\u7ba1\u7684\u522b\u5885\u4e4b\u524d\uff0c\u5c06\u603b\u7ba1\u6240\u79ef\u805a\u7684\u73e0\u5b9d\u4e5f\u90fd\u5e26\u8d70\u4e86\u3002 \u3000\u3000\u9ea6\u58eb\u8fe6\u5357\u4e00\u62cd\u9a6c\u978d\uff0c\u5230\u4e86\u90a3\u5339\u9a6c\u8f66\u65c1\u8fb9\uff0c\u559d\u9053\uff1a\u201c\u8fd8\u4e0d\u7ed9\u6211\u6eda\u51fa\u6765\uff1f\u201d\u4e00\u624b\u6309\u7740\u8f66\u8f84\uff0c \u60ee\u6756\u4e00\u8d77\uff0c\u4f5c\u52bf\u6b32\u51fb\uff0c\u5ffd\u542c\u5f97\u9a6c\u8f66\u91cc\u6709\u4eba\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u662f\u4ec0\u4e48\u4e1c\u897f\uff1f\u6562\u5bf9\u6211\u547c\u559d\uff01\u201d\u8f66\u8fbe\u4e00 \u63ed\uff0c\u53ea\u89c1\u4e00\u4e2a\u5f2f\u53d1\u52fe\u9f3b\u7684\u963f\u523a\u4f2f\u6cd5\u5e08\u63a2\u51fa\u5934\u6765\uff0c\u9ea6\u58eb\u8fe6\u5357\u8ba4\u4e0d\u5f97\u63d0\u6469\u8fbe\u591a\uff0c\u6014\u4e86\u4e00\u6014\uff0c\u6b63\u60f3 \u9053\u6b49\uff0c\u9661\u7136\u95f4\u5ffd\u89c9\u4e00\u80a1\u6781\u5927\u7684\u529b\u9053\u649e\u6765\uff0c\u9ea6\u58eb\u8fe6\u5357\u5927\u5403\u4e00\u60ca\uff0c\u7985\u6756\u4e00\u6863\uff0c\u4f46\u542c\u5f97\u201c\u5f53\u201d\u7684\u4e00 \u58f0\uff0c\u9ea6\u58eb\u8fe6\u5357\u864e\u53e3\u9707\u88c2\uff0c\u7985\u6756\u98de\u4e0a\u4e86\u534a\u5929\uff0c\u9ea6\u58eb\u8fe6\u5357\u4f46\u89c9\u5929\u65cb\u5730\u8f6c\uff0c\u4e00\u4e2a\u5012\u683d\u8dcc\u4e0b\u9a6c\u6765\uff0c\u5e78 \u800c\u4ed6\u9a91\u672f\u7cbe\u7edd\uff0c\u660f\u660f\u8ff7\u8ff7\u4e2d\u53cc\u8db3\u5c1a\u81ea\u52fe\u7262\u9a6c\u80cc\uff0c\u90a3\u5339\u9a6c\u62d6\u7740\u4ed6\u75be\u8d70\u4e86\u5341\u4f59\u4e08\u5730\uff0c\u4e00\u58f0\u5389\u53eb\uff0c \u4e5f\u5012\u6bd9\u4e86\u3002\u539f\u6765\u8fd9\u5339\u9a6c\u5df2\u88ab\u63d0\u6469\u8fbe\u591a\u4ed8\u4eab\u529b\u9707\u88c2\u4e86\u8111\u888b\uff0c\u9ea6\u58eb\u8fe6\u5357\u5e78\u4e8f\u95ea\u907f\u5f97\u5feb\uff0c\u53c8\u5f97\u8fd9\u5339 \u9a6c\u66ff\u4ed6\u6321\u4e86\u4e00\u4e0b\u638c\u529b\uff0c\u867d\u662f\u660f\u8ff7\uff0c\u5c1a\u8fd8\u672a\u6b7b\u3002 \u3000\u3000\u5c31\u5728\u6b64\u65f6\uff0c\u96c5\u5fb7\u661f\u738b\u5b50\u7684\u5feb\u9a6c\u5df2\u662f\u75be\u9a70\u800c\u6765\uff0c\u4ed6\u542c\u5f97\u9ea6\u58eb\u8fe6\u5357\u90a3\u4e00\u58f0\u60e8\u53eb\uff0c\u53ea\u9053\u4ed6\u5df2\u906d \u4e86\u6bd2\u624b\uff0c\u8feb\u4e0d\u53ca\u5f85\uff0c\u5c31\u5728\u5feb\u9a6c\u98de\u9a70\u4e4b\u4e2d\uff0c\u4ece\u9a6c\u80cc\u4e0a\u4e00\u8dc3\u800c\u8d77\uff0c\u62bd\u51fa\u4e86\u4e00\u67c4\u94c1\uff0c\u5411\u90a3\u67b6\u9a6c\u8f66\u51cc \u7a7a\u51fb\u4e0b\u3002 \u3000\u3000\u63d0\u6469\u8fbe\u591a\u559d\u9053\uff1a\u201c\u738b\u5b50\u4f11\u5f97\u4e0d\u77e5\u597d\u574f\uff0c\u8d76\u5feb\u8d70\u5f00\uff01\u201d\u53cc\u638c\u62cd\u51fa\uff0c\u547c\u7684\u4e00\u58f0\uff0c\u96c5\u5fb7\u661f\u4e5f\u7ed9 \u4ed6\u7684\u638c\u65b9\u9707\u5f97\u8eab\u5f62\u4e0d\u7a33\uff0c\u6025\u5fd9\u4e00\u4e2a\u5012\u7ffb\uff0c\u5411\u540e\u8dc3\u51fa\u4e08\u8bb8\uff0c\u4f46\u4ed6\u90a3\u67c4\u94c1\uff0c\u4ecd\u662f\u8131\u624b\u63b7\u51fa\uff0c\u788e\u7684 \u4e00\u58f0\u5de8\u54cd\uff0c\u5c06\u8f66\u76d6\u6253\u5f97\u56db\u5206\u4e94\u88c2\uff01\u96c5\u5fb7\u661f\u60ca\u9b42\u7a0d\u5b9a\uff0c\u5931\u58f0\u53eb\u9053\uff1a\u201c\u63d0\u6469\u8fbe\u591a\uff0c\u539f\u6765\u662f\u4f60\uff01\u201d \u63d0\u6469\u8fbe\u591a\u5728\u4e09\u5e74\u4e4b\u524d\uff0c\u66fe\u7ecf\u5230\u8fc7\u5370\u5ea6\u62dc\u89c1\u9f99\u53f6\u5927\u5e08\uff0c\u60f3\u4e0e\u9f99\u53f6\u5927\u5e08\u5207\u78cb\u6b66\u529f\uff0c\u9f99\u53f6\u5927\u5e08\u5374\u53ea \u548c\u4ed6\u8bf4\u4e86\u4e00\u573a\u4f5b\u6cd5\uff0c\u4fbf\u5c06\u4ed6\u8fed\u8d70\u4e86\u3002\u5c31\u5728\u90a3\u4e00\u62dc\u4f1a\u4e4b\u4e2d\uff0c\u96c5\u5fb7\u661f\u738b\u5b50\u89c1\u8fc7\u63d0\u6469\u8fbe\u591a\u4e00\u9762\u3002 \u3000\u3000\u63d0\u6469\u8fbe\u591a\u521a\u624d\u4e00\u6765\u662f\u788d\u4e8e\u9f99\u53f6\u5927\u5e08\u7684\u60c5\u9762\uff0c\u4e8c\u6765\u662f\u4e3a\u4e86\u96c5\u5fb7\u661f\u738b\u5b50\u7684\u8eab\u4efd\uff0c\u6240\u4ee5\u638c\u529b\u53ea \u53d1\u4e86\u4e94\u6210\uff0c\u4f46\u89c1\u96c5\u5fb7\u661f\u5c45\u7136\u6ca1\u6709\u88ab\u4ed6\u9707\u5012\uff0c\u4e5f\u81ea\u6697\u6697\u8be7\u5f02\u3002 \u3000\u3000\u96c5\u5fb7\u661f\u89c1\u662f\u63d0\u6469\u8fbe\u591a\uff0c\u5fc3\u4e2d\u81ea\u662f\u4e0d\u65e0\u5feb\u610f\uff0c\u4f46\u4e3a\u4e86\u8981\u83b7\u5f97\u516c\u4e3b\uff0c\u4ed6\u53c8\u600e\u4e48\u80af\u7a7a\u624b\u800c\u56de\uff0c \u4fbf\u5bf9\u63d0\u6469\u8fbe\u591a\u9053\u8bf4\uff1a\u201c\u6211\u5949\u5c3c\u6cca\u5c14\u738b\u4e4b\u547d\uff0c\u62d8\u6355\u6469\u5170\u6cd5\u5e08\u3002\u201d\u8fd9\u65f6\u8f66\u76d6\u5df2\u63ed\uff0c\u770b\u5f97\u89c1\u6469\u5170\u6cd5 \u5e08\u4fbf\u5750\u5728\u73e0\u5b9d\u5806\u4e2d\u3002 \u3000\u3000\u63d0\u6469\u8fbe\u591a\u51b7\u7b11\u9053\uff1a\u201c\u6709\u6211\u5728\u6b64\uff0c\u8c01\u6562\u6355\u4ed6\uff0c\u6211\u5468\u6e38\u5217\u56fd\uff0c\u5404\u56fd\u56fd\u738b\u65e0\u4e0d\u7ed9\u6211\u51e0\u5206\u8584\u9762\uff0c \u4f60\u56de\u53bb\u5427\uff01\u201d\u96c5\u5fb7\u661f\u7a0d\u4e00\u8e0c\u8e87\uff0c\u8fc8\u6b65\u53c8\u4e0a\uff0c\u8bf4\uff1a\u201c\u5f85\u6211\u5c06\u4eba\u5e26\u8d70\u4e4b\u540e\uff0c\u4f60\u5411\u56fd\u738b\u6c42\u60c5\uff0c\u90a3\u6211 \u4e0d\u7ba1\uff01\u201d \u3000\u3000\u63d0\u6469\u8fbe\u591a\u628a\u773c\u4e00\u77a7\uff0c\u4f46\u89c1\u6842\u534e\u751f\u4e5f\u5df2\u8e2a\u9a6c\u9020\u4eba\u8c37\u53e3\uff0c\u63d0\u6469\u8fbe\u591a\u9762\u8272\u4e00\u6c88\uff0c\u6de1\u6de1\u8bf4\u9053\uff1a \u201c\u6211\u7684\u5b57\u5178\u4e0a\u6ca1\u6709\u6c42\u60c5\u4e24\u5b57\uff01\u201d\u6761\u7136\u95f4\u4e00\u8dc3\u800c\u4e0b\uff0c\u53cc\u638c\u4e00\u5708\uff0c\u7acb\u523b\u4fbf\u5411\u96c5\u5fb7\u661f\u8fdb\u88ad\u3002\u539f\u6765\u4ed6 \u89c1\u6842\u534e\u751f\u4ea6\u5df2\u8d76\u6765\uff0c\u8bda\u6050\u4ed6\u4e0e\u96c5\u5fb7\u661f\u8054\u624b\u5408\u6597\uff0c\u7eb5\u7136\u81ea\u5df1\u53ef\u80dc\uff0c\u53ea\u6015\u4e5f\u8981\u4e00\u4e24\u4e2a\u65f6\u8fb0\uff0c\u591c\u957f \u68a6\u591a\uff0c\u8fde\u7d2f\u4e86\u6469\u5170\u6cd5\u5e08\uff0c\u90a3\u5c31\u4e0d\u4f46\u6709\u635f\u81ea\u5df1\u5a01\u540d\uff0c\u4e5f\u8d1f\u4e86\u5c3c\u6cca\u5c14\u738b\u5b50\u6240\u6258\u3002\u662f\u4ee5\u4ed6\u8feb\u5f97\u901f\u6218 \u901f\u51b3\uff0c\u60f3\u628a\u96c5\u5fb7\u661f\u5148\u51fb\u5012\u624d\u7b97\u3002 \u3000\u3000\u90a3\u77e5\u96c5\u5fb7\u661f\u7684\u745c\u4f3d\u529f\u592b\u5df2\u7ec3\u5230\u516d\u4e03\u6210\u706b\u5019\uff0c\u63d0\u6469\u8fbe\u591a\u53c8\u672a\u7528\u5168\u529b\uff0c\u53cc\u638c\u4e00\u5708\uff0c\u5df2\u89e6\u7740\u5b83 \u7684\u80a9\u5934\uff0c\u7adf\u88ab\u4ed6\u5f3a\u628a\u808c\u8089\u626d\u5f97\u5f2f\u66f2\u53d8\u5f62\uff0c\u63d0\u6469\u8fbe\u591a\u53cc\u638c\u4e00\u6ed1\uff0c\u96c5\u5fb7\u661f\u4e0d\u4f46\u5c06\u4ed6\u7684\u64d2\u62ff\u624b\u5378 \u4e86\uff0c\u800c\u4e14\u8fd8\u8fd8\u4e86\u4e00\u62db\u3002\u63d0\u6469\u8fbe\u591a\u5927\u6012\uff0c\u638c\u529b\u52a0\u5230\u516b\u6210\uff0c\u970d\u5730\u4e00\u5806\uff0c\u96c5\u5fb7\u661f\u8eab\u5f62\u4e00\u6643\uff0c\u547c\u5438\u4e5f \u51e0\u4e4e\u7a92\u606f\uff0c\u4f46\u4ecd\u7136\u6ca1\u6709\u88ab\u4ed6\u51fb\u5012\uff0c\u4e00\u4e2a\u8f6c\u8eab\uff0c\u62bd\u51fa\u4e00\u67c4\u77ed\u5251\uff0c\u8d81\u7740\u63d0\u6469\u8fbe\u591a\u62db\u6570\u7528\u5b8c\uff0c\u53cc\u638c \u672a\u53ca\u64a4\u56de\uff0c\u77ed\u5251\u4e00\u7ffb\uff0c\u95ea\u7535\u822c\u7684\u4fbf\u523a\u4ed6\u624b\u8155\u3002\u8fd9\u65f6\u6842\u534e\u751f\u7684\u5feb\u9a6c\uff0c\u4ea6\u5df2\u5954\u5230\u6570\u4e08\u4e4b\u5185\u4e86\u3002 \u3000\u3000\u773c\u89c1\u8fd9\u4e00\u5251\u5c31\u8981\u523a\u4e2d\uff0c\u9661\u7136\u95f4\u63d0\u6469\u8fbe\u591a\u5ffd\u5730\u4e00\u58f0\u5927\u559d\uff0c\u624b\u81c2\u66b4\u957f\u6559\u5bf8\uff0c\u96c5\u5fb7\u661f\u5fc3\u5934\u4e00 \u9707\uff0c\u77ed\u5251\u5760\u5730\uff0c\u4f46\u89c9\u80f8\u53e3\u5982\u4e2d\u5de8\u9524\uff0c\u80cc\u810a\u5982\u53d7\u5c71\u538b\uff0c\u4e24\u80a1\u6781\u521a\u731b\u7684\u529b\u9053\u524d\u540e\u5939\u653b\uff0c\u96c5\u5fb7\u661f\u90a3 \u91cc\u8fd8\u7981\u53d7\u5f97\u4f4f\u3002\u767b\u65f6\u6655\u6b7b\u8fc7\u53bb\u3002 \u3000\u3000\u6842\u534e\u751f\u4e00\u8dc3\u4e0b\u9a6c\uff0c\u62d4\u51fa\u817e\u86df\u5b9d\u5251\uff0c\u4e00\u6b65\u4e00\u6b65\u5411\u63d0\u6469\u8fbe\u591a\u8feb\u8fd1\uff0c\u63d0\u6469\u8fbe\u591a\u5374\u662f\u54c8\u54c8\u5927\u7b11\uff0c \u9053\uff1a\u201c\u5c31\u662f\u4f60\u4e00\u4e2a\u4eba\u5417\uff1f\u7528\u51b0\u5757\u795e\u5f39\u52a9\u4f60\u4e4b\u4eba\u90a3\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u6842\u534e\u751f\u5fc3\u5934\u4e00\u9707\uff0c\u6628\u665a\u4ed6\u672c\u6765\u5c31\u6000\u7591\u90a3\u7a81\u5982\u5176\u6765\u7684\u51b7\u6c14\u4e43\u662f\u51b0\u7a9f\u7384\u51b0\uff0c\u800c\u4eca\u7ecf\u63d0\u6469\u8fbe\u591a \u8bc1\u5b9e\uff0c\u679c\u7136\u4fbf\u662f\u51b0\u9b44\u795e\u5f39\u3002\u8981\u77e5\u63d0\u6469\u8fbe\u591a\u4e43\u662f\u4e00\u4ee3\u7684\u6b66\u5b66\u5927\u5e08\uff0c\u89c1\u591a\u8bc6\u5e7f\uff0c\u4ed6\u65e2\u7136\u8bf4\u662f\u51b0\u9b44 \u88e8\u5f39\uff0c\u90a3\u81ea\u662f\u65e0\u53ef\u6000\u7591\u7684\u4e86\uff0c\u90a3\u4e48\uff0c\u96be\u9053\u90a3\u767d\u8863\u5c11\u5973\u5f53\u771f\u4fbf\u662f\u5c3c\u6cca\u5c14\u516c\u4e3b\uff1f\u5982\u5176\u4e0d\u7136\uff0c\u4e16\u95f4 \u8fd8\u90a3\u6765\u7684\u51b0\u5757\u795e\u5f39\u3002 \u3000\u3000\u6842\u534e\u751f\u8ff7\u8ff7\u60af\u60af\uff0c\u9661\u7136\u95f4\u4e00\u80a1\u5927\u529b\u63a8\u6765\uff0c\u63d0\u6469\u8fbe\u591a\u7684\u53cc\u638c\u5df2\u7136\u653b\u51fa\uff0c\u6842\u534e\u751f\u7fdf\u7136\u4e00\u60ca\uff0c \u6025\u5fd9\u51cc\u7a7a\u4e00\u8dc3\uff0c\u5728\u95f4\u4e0d\u5bb9\u53d1\u4e4b\u9645\uff0c\u9669\u9669\u907f\u8fc7\u3002\u968f\u5373\u4e00\u62db\u201c\u5929\u5764\u5012\u6302\u201d\uff0c\u817e\u86df\u5b9d\u5251\u5316\u6210\u4e86\u4e00\u9053 \u957f\u8679\uff0c\u51cc\u7a7a\u5012\u5377\u3002 \u3000\u3000\u9976\u662f\u6842\u534e\u751f\u5e94\u4ed8\u5f97\u5b9c\uff0c\u4ea6\u5df2\u843d\u4e86\u4e0b\u98ce\u3002\u63d0\u6469\u8fbe\u591a\u62a2\u4e86\u5148\u624b\uff0c\u653b\u52bf\u53d1\u52a8\uff0c\u6709\u5982\u957f\u6c5f\u5927\u6cb3\uff0c \u6eda\u6eda\u800c\u4e0a\uff0c\u4e94\u5341\u62db\u4e00\u8fc7\uff0c\u6842\u534e\u751f\u4fbf\u6e10\u6e10\u611f\u5230\u547c\u5438\u8feb\u4fc3\uff0c\u5934\u6655\u76ee\u7729\uff0c\u4f46\u89c9\u6bd4\u6628\u591c\u53e4\u5821\u4e4b\u6218\uff0c\u8fd8 \u8981\u5403\u529b\u5f97\u591a\u3002\u539f\u6765\u63d0\u6469\u8fbe\u591a\u6628\u665a\u4e4b\u6218\uff0c\u867d\u7136\u8363\u8fb1\u6709\u5173\uff0c\u5230\u5e95\u4ed6\u4e0e\u6842\u534e\u751f\u4e4b\u95f4\u6ca1\u6709\u6df1\u4ec7\u5927\u6068\uff0c \u8bb8\u591a\u6781\u5389\u5bb3\u6781\u6df1\u5965\u8db3\u4ee5\u81f4\u4eba\u6b7b\u547d\u7684\u6b66\u529f\u8fd8\u6ca1\u6709\u5c3d\u91cf\u53d1\u6325\uff0c\u800c\u4eca\u4ed6\u4e00\u6765\u8981\u62a5\u6628\u665a\u4e00\u5251\u4e4b\u4ec7\uff0c\u4e8c \u6765\u6025\u7740\u8981\u62a4\u9001\u6469\u5170\u6cd5\u5e08\u8131\u9669\uff0c\u624b\u4e0b\u7edd\u4e0d\u7559\u60c5\uff0c\u6545\u6b64\u6628\u665a\u6842\u534e\u751f\u5230\u767e\u62db\u4e4b\u5916\uff0c\u624d\u7a0d\u611f\u4e0d\u652f\uff0c\u800c \u4eca\u53ea\u4e0d\u8fc7\u5230\u4e86\u4e94\u5341\u62db\uff0c\u4fbf\u6e10\u6e10\u611f\u5230\u96be\u4ee5\u5e94\u4ed8\u3002 \u3000\u3000\u6842\u534e\u751f\u6756\u7740\u8eab\u6cd5\u6bd4\u8f83\u8f7b\u7075\uff0c\u4e00\u89c1\u4e0d\u5999\uff0c\u53c8\u91c7\u7528\u6628\u665a\u7684\u6218\u7565\uff0c\u548c\u4ed6\u6e38\u8eab\u7f20\u6597\uff0c\u4f46\u89c1\u4ed6\u6ef4\u6e9c \u6e9c\u4e00\u4e2a\u8f6c\u8eab\uff0c\u767b\u65f6\u94f6\u5149\u904d\u4f53\uff0c\u7d2b\u7535\u98de\u7a7a\uff0c\u5251\u5149\u7ec3\u7ed5\u4e2d\uff0c\u4f46\u89c1\u56db\u65b9\u516b\u9762\u90fd\u662f\u6842\u534e\u751f\u7684\u8eab\u5f71\uff0c\u7aef \u7684\u662f\u7fe9\u82e5\u60ca\u9e3f\uff0c\u77eb\u82e5\u6e38\u9f99\uff0c\u5728\u63d0\u6469\u8fbe\u591a\u53cc\u5802\u7ffb\u98de\u4e4b\u4e0b\uff0c\u6253\u5f97\u96be\u5206\u96be\u89e3\u3002 \u3000\u3000\u90a3\u6599\u63d0\u6469\u8fbe\u591a\u7ecf\u8fc7\u6628\u591c\u4e00\u6218\uff0c\u65e9\u5df2\u80f8\u6709\u6210\u7af9\uff0c\u6697\u7528\u9634\u67d4\u7684\u638c\u529b\uff0c\u6d88\u78e8\u6842\u534e\u751f\u7684\u529b\u6c14\uff0c\u800c \u4e14\u5c06\u4ed6\u5438\u4f4f\uff0c\u4eca\u4ed6\u6446\u8131\u4e0d\u5f00\uff0c\u6218\u5230\u5206\u9645\uff0c\u5ffd\u5730\u4e00\u58f0\u5927\u559d\uff0c\u53cc\u638c\u4e00\u5806\uff0c\u4ee5\u6bd5\u751f\u529f\u529b\u4e4b\u6240\u805a\uff0c\u53d1 \u51fa\u4e86\u6781\u521a\u731b\u7684\u4e00\u638c\uff0c\u6761\u7136\u95f4\u4f46\u89c1\u5251\u4e5d\u56db\u6563\uff0c\u6709\u5982\u6c34\u94f6\u6cfb\u5730\uff0c\u5316\u96e8\u7f24\u7eb7\u3002\u63d0\u6469\u8fbe\u591a\u4e00\u638c\u51fb\u6563\u4e86 \u6842\u534e\u751f\u7684\u62a4\u8eab\u5251\u5149\uff0c\u5f97\u610f\u72c2\u7b11\uff0c\u7acb\u5373\u4e58\u865a\u800c\u5165\uff0c\u9634\u9633\u638c\u529b\uff0c\u4e92\u4e3a\u7275\u5f15\uff0c\u6842\u534e\u751f\u767b\u65f6\u6709\u5982\u4e00\u53f6 \u8f7b\u821f\uff0c\u5728\u60ca\u6d9b\u9a87\u6d6a\u4e4b\u4e2d\uff0c\u98d8\u6447\u4e0d\u5b9a\uff0c\u770b\u770b\u5c31\u8981\u906d\u706d\u9876\u4e4b\u707e\uff01 \u3000\u3000\u5c31\u5728\u8fd9\u6781\u5ea6\u7d27\u5f20\u4e4b\u9645\uff0c\u5ffd\u7136\u95f4\u6709\u4e00\u80a1\u6781\u67d4\u548c\u7684\u7b1b\u58f0\u968f\u7740\u77f3\u98ce\u5439\u4e0b\uff0c\u6b63\u662f\u6842\u534e\u751f\u5728\u9b54\u9b3c\u57ce \u4e4b\u591c\u542c\u5230\u7684\u90a3\u4e2a\u7b1b\u58f0\uff0c\u6842\u534e\u751f\u7cbe\u795e\u4e00\u632f\uff0c\u5237\uff0c\u5237\uff0c\u5237\uff0c\u53d1\u51fa\u8ffd\u98ce\u4e09\u5251\uff0c\u5251\u98ce\u6307\u5904\uff0c\u67ab\u67ab\u4f5c \u54cd\uff0c\u8fd9\u4e00\u9635\u6025\u653b\uff0c\u7adf\u7136\u628a\u63d0\u6469\u8fbe\u591a\u7684\u638c\u529b\u5378\u53bb\u4e03\u6210\uff0c\u8eab\u4e0a\u6240\u53d7\u7684\u91cd\u538b\u767b\u65f6\u5927\u51cf\u3002 \u3000\u3000\u63d0\u6469\u8fbe\u591a\u542c\u4e86\u7b1b\u58f0\uff0c\u5fc3\u5934\u4ea6\u81ea\u5fae\u5fae\u4e00\u51db\uff0c\u62bd\u773c\u770b\u65f6\uff0c\u4f46\u89c1\u6469\u5170\u6cd5\u5e08\u5ffd\u5730\u8dc3\u4e0b\u9a6c\u8f66\uff0c\u7eb5\u8eab \u4e00\u8df3\uff0c\u8df3\u4e0a\u96c5\u5fb7\u661f\u738b\u5b50\u6240\u7559\u4e0b\u7684\u90a3\u5339\u9a8f\u9a6c\uff0c\u626c\u58f0\u53eb\u9053\uff1a\u201c\u6055\u6211\u5148\u8d70\uff0c\u9ea6\u52a0\u518d\u89c1\u3002\u201d\u72c2\u97ad\u540d \u9a6c\uff0c\u75be\u9a70\u5982\u98de\uff0c\u970e\u65f6\u51fa\u4e86\u5ce1\u8c37\u3002\u63d0\u6469\u8fbe\u591a\u751a\u611f\u4e0d\u5feb\uff0c\u6b63\u81ea\u5947\u602a\u6469\u5170\u6cd5\u5e08\u6015\u4e9b\u4ec0\u4e48\uff0c\u5728\u81ea\u5df1\u4fdd \u62a4\u4e4b\u4e0b\uff0c\u7adf\u662f\u8fd9\u6837\u4ea1\u9b42\u5931\u9b44\u7684\u8d76\u5fd9\u9003\u547d\u3002\u5fc3\u5ff5\u672b\u5df2\uff0c\u7b1b\u58f0\u4e00\u6b47\uff0c\u4f46\u89c1\u4e00\u4e2a\u767d\u8863\u5c11\u5973\uff0c\u8863\u8882\u98d8 \u98d8\uff0c\u5df2\u77e5\u4ed9\u5b50\u51cc\u98ce\uff0c\u4ece\u5c71\u9876\u98d8\u843d\uff01\u53ea\u542c\u5f97\u6842\u534e\u751f\u5927\u559c\u5982\u72c2\uff0c\u9ad8\u53eb\u201c\u59b9\u59b9\uff01\u201d \u3000\u3000\u767d\u8863\u5c11\u5973\u5ae3\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u4e0d\u60dc\u5192\u9669\uff0c\u8ffd\u6355\u6469\u5170\uff0c\u6211\u662f\u611f\u6fc0\u5f97\u5f88\u3002\u201d\u5ffd\u5730\u58f0\u8c03\u4e00\u53d8\uff0c \u8f6c\u8fc7\u5934\u6765\uff0c\u5bf9\u63d0\u6469\u8fbe\u591a\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u5c3c\u6cca\u5c14\u4eba\u7d20\u6765\u597d\u5ba2\uff0c\u4f46\u50cf\u4f60\u8fd9\u6837\u80e1\u4f5c\u975e\u4e3a\uff0c\u5374\u662f\u4e0d\u53d7\u6b22 \u8fce\u3002\u4f60\u5feb\u8d70\u5427\uff01\u201d\u63d0\u6469\u8fbe\u591a\u5468\u6e38\u5217\u56fd\uff0c\u6240\u5230\u4e4b\u5904\uff0c\u738b\u516c\u8d35\u65cf\uff0c\u53ea\u8981\u662f\u77e5\u9053\u7684\uff0c\u65e0\u4e0d\u9686\u91cd\u8fce \u63a5\uff0c\u51e0\u66fe\u53d7\u8fc7\u8fd9\u822c\u51b7\u6de1\uff1f\u4e0d\u7531\u5f97\u52c3\u7136\u5927\u6012\uff0c\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u4e2a\u5c0f\u4e2b\u5934\u7adf\u6562\u5bf9\u6211\u65e0\u793c\uff0c\u4f60\u77e5\u4e0d\u77e5 \u6211\u662f\u4f60\u4eec\u738b\u5b50\u8bf7\u6765\u7684\u4e0a\u5bbe\uff1f\u201d\u767d\u8863\u5c11\u5973\u9053\uff1a\u201c\u8bc6\u5f97\u5c0a\u91cd\u81ea\u5df1\u7684\u4eba\u624d\u80fd\u53d7\u4eba\u5c0a\u91cd\uff0c\u4f60\u4eec\u963f\u523a\u4f2f \u4e0d\u662f\u4e5f\u6709\u8fd9\u53e5\u8c1a\u8bed\u4e48\uff1f\u8bf7\u6765\u7684\u5ba2\u4eba\u66f4\u5e94\u8be5\u5b88\u793c\uff0c\u56fd\u738b\u6349\u62ff\u6469\u5170\uff0c\u4f60\u51ed\u4ec0\u4e48\u963b\u6321\uff1f\u8bf7\u6765\u7684\u5ba2\u4eba \u6709\u8fd9\u6837\u65e0\u793c\u7684\u4e48\uff1f\u201d \u3000\u3000\u6842\u534e\u751f\u5ffd\u5730\u53eb\u9053\uff1a\u201c\u59b9\u59b9\uff0c\u5c0f\u5fc3\uff01\u201d\u539f\u6765\u63d0\u6469\u8fbe\u591a\u88ab\u4ed6\u6c14\u5f97\u4e03\u7a8d\u751f\u70df\uff0c\u4e0d\u5f85\u5979\u628a\u8bdd\u8bf4 \u5b8c\uff0c\u9aa4\u7136\u95f4\u4fbf\u662f\u53cc\u638c\u63a8\u51fa\u3002\u6842\u534e\u751f\u6025\u5fd9\u4e00\u5251\u524c\u51fa\uff0c\u5374\u662f\u90a3\u5c11\u5973\u5df2\u968f\u7740\u638c\u98ce\u51cc\u7a7a\u98de\u8d77\uff0c\u59ff\u6001\u7f8e \u597d\uff0c\u795e\u8272\u4ece\u5bb9\uff0c\u767e\u5fd9\u4e2d\u8fd8\u5411\u6842\u534e\u751f\u62a5\u4ee5\u751c\u751c\u4e00\u7b11\uff0c\u5a07\u58f0\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u5173\u5fc3\uff01\u201d \u3000\u3000\u63d0\u6469\u8fbe\u591a\u89c1\u5979\u8fd9\u7b49\u8f7b\u96fe\u7684\u8eab\u6cd5\uff0c\u4e5f\u4e0d\u7981\u6697\u5403\u4e00\u60ca\uff0c\u4ed6\u4e00\u638c\u8361\u5f00\u6842\u534e\u751f\u7684\u5b9d\u5251\uff0c\u6309\u7740\u5411\u4e0a \u4e00\u62db\uff0c\u53d1\u51fa\u4e00\u80a1\u9634\u529b\uff0c\u60f3\u628a\u767d\u8863\u5c11\u5973\u5f3a\u626f\u4e0b\u6765\uff0c\u91cd\u91cd\u7684\u6454\u5979\u4e00\u4e2a\u808b\u6597\uff01 \u3000\u3000\u5c31\u5728\u6b64\u65f6\uff0c\u4e00\u9897\u9897\u597d\u4f3c\u73cd\u73e0\u5927\u5c0f\u3001\u4eae\u6676\u6676\u7684\u4e1c\u897f\u4ece\u7a7a\u4e2d\u9152\u4e0b\u6765\uff0c\u88ab\u638c\u98ce\u9707\u8361\uff0c\u767b\u65f6\u788e\u88c2 \u6210\u7c89\uff0c\u6563\u51fa\u5bd2\u5149\u51b7\u6c14\uff0c\u63d0\u6469\u8fbe\u591a\u4e5f\u4e0d\u7981\u673a\u4f36\u4f36\u7684\u6253\u4e86\u4e00\u4e2a\u51b7\u6218\uff0c\u9000\u540e\u4e09\u6b65\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u539f \u6765\u6628\u665a\u7528\u51b0\u9b44\u795e\u5f39\u6697\u7b97\u6211\u7684\uff0c\u5c31\u662f\u4f60\u8fd9\u4e2a\u4e2b\u5934\uff01\u201d \u3000\u3000\u6842\u534e\u751f\u4e00\u89c1\u767d\u8863\u5c11\u5973\u6765\u5230\uff0c\u65e9\u5c31\u8c03\u5300\u771f\u6c14\u51c6\u5907\u62b5\u53d7\u5947\u5bd2\uff1a\u4ed6\u5b66\u7684\u662f\u8fbe\u6469\u79df\u5e08\u5085\u4e0b\u7684\u4f5b\u95e8 \u7eaf\u6b63\u5185\u529f\uff0c\u800c\u4e14\u5728\u51b0\u7a9f\u4e4b\u65f6\uff0c\u53c8\u6709\u8fc7\u7ecf\u9a8c\uff0c\u51b0\u9b44\u795e\u5f39\u7684\u5bd2\u6c14\uff0c\u81ea\u7136\u4f24\u4e0d\u4e86\u4ed6\u3002\u53ef\u662f\u63d0\u6469\u8fbe\u591a \u9aa4\u7136\u53d7\u88ad\uff0c\u4e5f\u4e0d\u8fc7\u4ec5\u4ec5\u6253\u4e86\u4e00\u4e2a\u51b7\u6218\uff0c\u6842\u534e\u751f\u548c\u767d\u8863\u5c11\u5973\u90fd\u4e0d\u7981\u6697\u6697\u4f69\u670d\uff1a\u201c\u963f\u523a\u4f2f\u7b2c\u4e00\u9ad8 \u624b\uff0c\u679c\u7136\u540d\u4e0d\u865a\u4f20\uff01\u201d \u3000\u3000\u6628\u665a\u63d0\u6469\u8fbe\u591a\u56e0\u4e3a\u662f\u4e00\u624b\u6500\u7740\u9a6c\u8f66\uff0c\u8eab\u5b50\u60ac\u7a7a\u4f5c\u6218\uff0c\u5ffd\u7136\u7834\u51b0\u5757\u88e8\u5f39\u6253\u4e2d\u7a74\u9053\uff0c\u8fd9\u624d\u7ed9 \u6842\u534e\u751f\u4e58\u673a\u523a\u4e86\u4e00\u5251\uff0c\u800c\u4eca\u5728\u5e73\u5730\u4e0a\u640f\u6597\uff0c\u4ed6\u53c8\u5e94\u4ed8\u5f97\u5b9c\uff0c\u6842\u534e\u751f\u867d\u7136\u8d81\u52bf\u53cd\u653b\uff0c\u4ed6\u7684\u638c\u529b \u7ef5\u5bc6\u4e4b\u6781\uff0c\u8ba9\u7740\u5168\u8eab\uff0c\u90a3\u91cc\u65bd\u5f97\u8fdb\u53bb\uff1f \u3000\u3000\u767d\u8863\u5c11\u5973\u8f7b\u98d8\u98d8\u7684\u843d\u4e0b\u5730\u6765\uff0c\u5bf9\u6842\u534e\u751f\u7b11\u9053\uff1a\u201c\u4f60\u77a7\u6211\u7684\u51b0\u9b44\u5bd2\u5149\u5251\u5df2\u6210\u4e86\uff01\u201d\u9661\u7136\u95f4 \u4eae\u51fa\u4e00\u628a\u5bd2\u5149\u95ea\u95ea\uff0c\u901a\u4f53\u6676\u83b9\uff0c\u975e\u91d1\u975e\u94c1\uff0c\u9177\u4f3c\u4e00\u6bb5\u5bd2\u51b0\u7684\u957f\u5251\uff0c\u5f53\u771f\u662f\u4eba\u95f4\u72ec\u4e00\u3001\u4e16\u4e0a\u65e0 \u53cc\u7684\u5f02\u5b9d\u5947\u73cd\uff01 \u3000\u3000\u51b0\u9b44\u5bd2\u5149\u5251\u7565\u4e00\u6325\u52a8\uff0c\u51b7\u6c14\u5bd2\u5149\uff0c\u679c\u7136\u7acb\u523b\u5411\u56db\u9762\u53d1\u5c04\uff0c\u63d0\u6469\u8fbe\u591a\u54ac\u5b9e\u7259\u5173\uff0c\u53cc\u638c\u8fde\u73af \u75be\u626b\uff0c\u547c\u547c\u98ce\u54cd\uff0c\u628a\u5bd2\u5149\u9707\u6563\uff0c\u51b7\u6c14\u8361\u5f00\uff0c\u5c45\u7136\u53c8\u63a5\u4e86\u4e00\u767e\u591a\u62db\uff0c\u767d\u8863\u5c11\u5973\u4f7f\u51fa\u51b0\u5ddd\u5251\u6cd5\uff0c \u653b\u5982\u96f7\u9706\u9707\u6012\uff0c\u9759\u5982\u6c5f\u6d77\u51dd\u5149\uff0c\u6bcf\u53d1\u4e00\u5251\uff0c\u4fbf\u662f\u4e00\u80a1\u523a\u9aa8\u7684\u5bd2\u98ce\uff0c\u66f4\u52a0\u4e0a\u6842\u534e\u751f\u7684\u817e\u86df\u5b9d\u5251 \u4e5f\u662f\u4eba\u95f4\u7f55\u89c1\u7684\u795e\u7269\u5229\u5668\uff0c\u4e24\u4eba\u8054\u5251\u800c\u653b\uff0c\u4e16\u95f4\u7684\u9ad8\u624b\u8c01\u53ef\u4e0e\u4e4b\u62b5\u654c\uff1f \u3000\u3000\u518d\u8fc7\u4e00\u767e\u4f59\u62db\uff0c\u5bd2\u6c14\u6fc0\u6c64\uff0c\u6108\u6765\u6108\u6d53\uff0c\u90a3\u4ebf\u4e07\u5e74\u5bd2\u7389\u6240\u53d1\u51fa\u7684\u9634\u51b7\u4e4b\u6c14\uff0c\u51e0\u4f3c\u5c31\u8981\u51dd\u7ed3 \u6210\u5b9e\u7269\u4e00\u822c\uff0c\u63d0\u6469\u8fbe\u591a\u7684\u638c\u98ce\u867d\u7136\u5f3a\u52b2\uff0c\u5374\u4e5f\u96be\u4ee5\u9a71\u6563\u3002\u63d0\u6469\u8fbe\u591a\u989d\u89d2\u6e10\u6e10\u6c81\u51fa\u6c57\u73e0\uff0c\u5374\u53c8 \u5168\u8eab\u98a4\u6296\u3002 \u3000\u3000\u63d0\u6469\u8fbe\u591a\u662f\u6b66\u5b66\u7684\u5927\u884c\u5bb6\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u6211\u82e5\u518d\u8fd0\u771f\u6c14\u4e0e\u4e4b\u76f8\u6297\uff0c\u7eb5\u7136\u8fd8\u53ef\u4ee5\u652f\u6301\u4e00\u4e24 \u4e2a\u65f6\u8fb0\uff0c\u80dc\u8d25\u4e5f\u8fd8\u96be\u6599\uff0c\u4f46\u5374\u5fc5\u7136\u8981\u5f97\u4e00\u573a\u5927\u75c5\u3002\u6469\u5170\u6cd5\u5e08\u5df2\u5148\u9003\u4e86\uff0c\u6211\u4f55\u82e6\u4e3a\u4ed6\u5356\u547d\uff1f\u201d \u3000\u3000\u6253\u5b9a\u4e3b\u610f\uff0c\u5c06\u771f\u529b\u51dd\u805a\u638c\u4e0a\uff0c\u731b\u53d1\u4e00\u638c\uff0c\u5c06\u53cc\u5251\u4ea4\u7ec7\u7684\u5149\u7f51\u51b2\u5f00\u4e86\u4e00\u4e2a\u7f3a\u53e3\uff0c\u7acb\u523b\u98de\u8eab \u4f7f\u8d70\uff0c\u6842\u534e\u751f\u89c1\u4ed6\u5728\u5bd2\u5149\u51b7\u6c14\u5305\u56f4\u4e4b\u4e0b\uff0c\u82e6\u6218\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u5c45\u7136\u8fd8\u6709\u5982\u6b64\u529f\u529b\uff0c\u4e5f\u4e0d\u7981\u9a87 \u7136\u3002 \u3000\u3000\u8fd9\u65f6\u671d\u9633\u521d\u4e0a\uff0c\u4ece\u767d\u96ea\u7693\u7693\u7684\u5c71\u5cf0\u4e0a\u5c04\u4e0b\u6765\uff0c\u4e3d\u5f69\u971e\u8f89\uff0c\u8000\u76ee\u751f\u64b7\uff0c\u9634\u5bd2\u4e4b\u6c14\u5728\u9633\u5149 \u4e0b\u3002 \u3000\u3000\u6e10\u6e10\u6d88\u6563\uff0c\u6842\u534e\u751f\u5fc3\u4e0a\u7684\u9634\u96fe\u4e5f\u50cf\u88ab\u6625\u98ce\u4e00\u62c2\uff0c\u6d88\u5931\u5f97\u5e72\u5e72\u51c0\u51c0\u4e86\u3002 \u3000\u3000\u6842\u534e\u751f\u9053\uff1a\u201c\u8fd9\u4e0d\u662f\u68a6\u4e48\uff1f\u201d\u516c\u4e3b\u7b11\u9053\uff1a\u201c\u5f53\u7136\u4e0d\u662f\uff01\u201d\u6842\u534e\u751f\u9053\uff1a\u201c\u6211\u771f\u60f3\u4e0d\u5230\u4f60\u5c31 \u662f\u516c\u4e3b\uff1f\u201d\u516c\u4e3b\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u4f60\u6628\u665a\u8fd8\u5411\u90a3\u4e2a\u5370\u5ea6\u738b\u5b50\u9053\u8d3a\uff0c\u5440\uff0c\u5927\u54e5\u54e5\uff0c\u4f60\u771f\u50bb\uff0c\u4f60\u65e9\u5c31 \u5e94\u8be5\u731c\u5230\u4e86\u3002\u201d\u7c89\u8138\u4e0a\u7a81\u7136\u98de\u8d77\u4e24\u7247\u5a07\u7ea2\uff0c\u4ee4\u6842\u534e\u751f\u770b\u5f97\u5982\u75f4\u4f3c\u9189\u3002 \u3000\u3000\u8fc7\u4e86\u534a\u54cd\uff0c\u6842\u534e\u751f\u5e7d\u5e7d\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u4e00\u56fd\u7684\u516c\u4e3b\uff0c\u800c\u4e14\u8fd8\u5c06\u662f\u672a\u6765\u7684\u5973\u738b\uff0c\u6211\u8fd8\u600e\u6562\u9ad8 \u6500\uff0c\u53eb\u4f60\u505a\u59b9\u59b9\uff1f\u201d\u516c\u4e3b\u7b11\u9053\uff1a\u201c\u4f60\u600e\u7684\u4e5f\u6709\u4e16\u4fd7\u4e4b\u89c1\uff1f\u6211\u4e0d\u8fc7\u5076\u7136\u751f\u5728\u5e1d\u738b\u4e4b\u5bb6\uff0c\u8fd9\u53c8\u4f55 \u635f\u4e8e\u4f60\u6211\u7684\u4ea4\u8c0a\uff1f\u5440\uff0c\u4f60\u5b9e\u5728\u4e0d\u77e5\u9053\u6211\u7684\u5fc3\u610f\uff0c\u6211\u90a3\u91cc\u60f3\u505a\u4ec0\u4e48\u635e\u4ec0\u5b50\u7684\u5973\u738b\uff1f\u6211\u628a\u8363\u534e\u5bcc \u8d35\uff0c\u90fd\u5f53\u6210\u8fc7\u773c\u4e91\u70df\uff0c\u4f46\u613f\uff0c\u4f46\u613f\u2026\u2026\u201d\u6842\u534e\u751f\u5fc3\u5934\u751c\u4e1d\u4e1d\u7684\uff0c\u51b2\u53e3\u8bf4\u9053\uff1a\u201c\u4f46\u613f\u4eba\u957f\u4e45\uff0c \u5343\u91cc\u5171\u8749\u5a1f\uff01\u201d\u516c\u4e3b\u8f7b\u5f04\u4e91\u9b13\uff0c\u4e45\u4e45\u4e0d\u8bed\u3002\u8fc7\u4e86\u597d\u4e00\u4f1a\uff0c\u5ffd\u5730\u8bf4\u9053\uff1a\u201c\u6211\u7792\u7740\u7236\u738b\u5077\u51fa\u6df1 \u5bab\uff0c\u4ed6\u75c5\u4f53\u521d\u6108\uff0c\u70e6\u4eba\u670d\u4f8d\uff0c\u6211\u73b0\u5728\u8be5\u56de\u53bb\u5566\u3002\u8c22\u8c22\u4f60\u7684\u5929\u5c71\u96ea\u83b2\u3002\u201d \u3000\u3000\u6842\u534e\u751f\u60f3\u8d77\u6628\u665a\u7684\u8bb8\u591a\u79bb\u5947\u60c5\u4e8b\uff0c\u8bf4\u9053\uff1a\u201c\u516c\u4e3b\uff0c\u4f60\u5f97\u5f53\u5fc3\u4f60\u7684\u5802\u5144\uff01\u201d\u516c\u4e3b\u9053\uff1a\u201c\u6211 \u65e9\u5c31\u77e5\u9053\u4ed6\u91ce\u5fc3\u52c3\u52c3\uff0c\u5176\u5b9e\u6211\u5e76\u4e0d\u60f3\u4e0e\u4ed6\u4e89\u593a\u738b\u4f4d\u3002\u6240\u4ee5\u4ed6\u5728\u9b54\u9b3c\u57ce\u6240\u5e72\u7684\u4e8b\u60c5\uff0c\u6211\u4e5f\u6ca1\u6709 \u544a\u8bc9\u7236\u738b\u3002\u6211\u5728\u6697\u4e2d\u8bbe\u6cd5\u6d88\u5f2d\u4ed6\u7684\u91ce\u5fc3\uff0c\u4e5f\u4fbf\u7b97\u4e86\u3002\u201d\u6842\u534e\u751f\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u4f60\u4e3a\u4eba\u771f\u597d\uff0c\u53ea \u6015\uff0c\u53ea\u6015\u4ed6\u7684\u91ce\u5fc3\u8fd8\u4e0d\u6b62\u6b64\uff1f\u201d\u516c\u4e3b\u9053\uff1a\u201c\u600e\u4e48\uff0c\u6781\u5176\u91cf\u4ed6\u4e5f\u662f\u60f3\u505a\u56fd\u738b\u800c\u5df2\uff01\u201d\u6842\u534e\u751f\u4e45 \u5386\u6c5f\u6e56\uff0c\u6bd4\u8d77\u534e\u7389\u516c\u4e3b\uff0c\u81ea\u7136\u662f\u8f83\u4e3a\u61c2\u5f97\u4eba\u60c5\u4e16\u6545\uff0c\u60f3\u9053\uff1a\u201c\u4f60\u4e0d\u60f3\u505a\u5973\u738b\uff0c\u8fd9\u5fc3\u610f\u4ed6\u53ef\u672a \u66fe\u77e5\u9053\uff01\u201d\u672c\u6765\u4ece\u6628\u665a\u7684\u4e00\u4e9b\u8ff9\u8c61\uff0c\u6842\u534e\u751f\u5bf9\u90a3\u5c3c\u6cca\u5c14\u738b\u5b50\u5df2\u6709\u70b9\u7591\u5fc3\uff0c\u7591\u5fc3\u4ed6\u4e0e\u4e0b\u6bd2\u7684\u4e8b \u60c5\u4e5f\u6709\u70b9\u5173\u7cfb\uff0c\u4f46\u7a76\u7adf\u8fd8\u672b\u6562\u80af\u5b9a\uff0c\u800c\u4e14\u81ea\u5df1\u521d\u5230\u5c3c\u6cca\u5c14\u6765\uff0c\u4e00\u6765\u662f\u758f\u4e0d\u95ee\u4eb2\uff0c\u4e8c\u6765\u4e5f\u4e0d\u613f \u5e72\u9884\u4ed6\u4eec\u7684\u56fd\u4e8b\uff0c\u6545\u6b64\u867d\u6709\u4e00\u70b9\u7591\u5fc3\uff0c\u8bdd\u5230\u53e3\u8fb9\uff0c\u7ec8\u4e8e\u53c8\u541e\u4e86\u56de\u53bb\uff0c\u672a\u66fe\u5bf9\u516c\u4e3b\u660e\u8bf4\u3002 \u3000\u3000\u516c\u4e3b\u62ac\u5934\u4e00\u770b\uff0c\u9053\uff1a\u201c\u5ce1\u53e3\u90a3\u8fb9\u7684\u5c71\u4e5f\u71c3\u8d77\u4e86\u70fd\u706b\uff0c\u5927\u7ea6\u662f\u4ed6\u4eec\u5df2\u77e5\u9053\u4f60\u4eec\u5728\u8fd9\u91cc\u8ffd\u6355 \u6469\u5170\u6cd5\u5e08\u7684\u4e8b\u60c5\u4e86\u3002\u770b\u6765\u4ed6\u4eec\u518d\u8fc7\u7247\u523b\uff0c\u5c31\u4f1a\u8d76\u6765\u5566\uff0c\u6211\u771f\u7684\u5e94\u8be5\u8d70\u4e86\u3002\u201d \u3000\u3000\u6842\u534e\u751f\u8fc8\u4e0a\u4e00\u6b65\uff0c\u95ee\u9053\uff1a\u201c\u6211\u4eec\u4ec0\u4e48\u65f6\u5019\u518d\u89c1\uff1f\u201d\u516c\u4e3b\u5fae\u7b11\u9053\uff1a\u201c\u4f60\u591a\u8bfb\u4e9b\u5c3c\u6cca\u5c14\u7684\u7ecf \u5178\u6587\u5b66\u5427\u3002\u201d\u6842\u534e\u751f\u6014\u4e86\u4e00\u6014\uff0c\u5ffd\u5730\u604d\u7136\u5927\u609f\uff0c\u53eb\u9053\uff1a\u201c\u5475\uff0c\u539f\u6765\u4f60\u662f\u6559\u6211\u53bb\u5e94\u8bd5\uff0c\u90a3\u4e48\u54b1 \u4eec\u5c06\u6765\u8981\u5728\u64c2\u53f0\u4e0a\u518d\u89c1\u4e86\u3002\u4f60\u53ef\u5f97\u624b\u4e0b\u7559\u60c5\u5475\uff01\u201d\u8fd9\u51e0\u53e5\u8bdd\u8bf4\u5b8c\uff0c\u4f46\u89c1\u516c\u4e3b\u5df2\u98d8\u7136\u800c\u53bb\u3002\u6842 \u534e\u751f\u723d\u7136\u82e5\u5931\uff0c\u6b63\u81ea\u75f4\u75f4\u671b\u5979\u7684\u80cc\u5f71\uff0c\u5ffd\u542c\u5f97\u8eab\u7554\u6709\u547b\u541f\u4e4b\u58f0\u3002 \u3000\u3000\u6842\u534e\u751f\u5fc3\u5934\u4e00\u51db\uff0c\u60f3\u9053\uff1a\u201c\u9ea6\u58eb\u8fe6\u5357\u548c\u96c5\u5fb7\u661f\u90fd\u53d7\u4e86\u91cd\u4f24\uff0c\u6211\u600e\u7684\u5c06\u4ed6\u4eec\u5fd8\u4e86\u3002\u201d\u5148\u53bb \u770b\u9ea6\u58eb\u8fe6\u5357\uff0c\u8fd9\u65f6\u9ea6\u58eb\u8fe6\u5357\u5df2\u7ecf\u9192\u8f6c\uff0c\u90a3\u547b\u541f\u4e4b\u58f0\u4fbf\u662f\u4ed6\u53d1\u51fa\u7684\u3002\u6842\u534e\u751f\u5c06\u538b\u5728\u4ed6\u8eab\u4e0a\u7684\u90a3 \u5339\u9a6c\u79fb\u5f00\uff0c\u4f46\u89c1\u4ed6\u808b\u9aa8\u867d\u7136\u65ad\u4e86\u4e24\u6761\uff0c\u90a3\u5374\u662f\u8dcc\u4f24\u7684\uff0c\u5e76\u4e0d\u4e25\u91cd\u3002\u539f\u6765\u63d0\u6469\u8fbe\u591a\u638c\u6bd9\u5b83\u7684\u751f \u9a91\u4e4b\u540e\uff0c\u638c\u529b\u5df2\u662f\u5f3a\u5f29\u4e4b\u672b\uff0c\u867d\u7136\u89e6\u53ca\u4ed6\u7684\u8eab\u4f53\uff0c\u5374\u6ca1\u6709\u4f24\u53ca\u5185\u810f\u3002\u6842\u534e\u751f\u7ed9\u4ed6\u63a8\u8840\u8fc7\u5bab\uff0c \u52a9\u4ed6\u547c\u5438\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u9ea6\u58eb\u8fe6\u5357\u5df2\u7ecf\u80fd\u591f\u8d77\u7acb\u8d70\u52a8\u4e86\u3002 \u3000\u3000\u9ea6\u58eb\u8fe6\u5357\u8212\u5c55\u4e86\u4e00\u4e0b\u7b4b\u9aa8\uff0c\u548b\u820c\u8bf4\u9053\uff1a\u201c\u597d\u5389\u5bb3\u7684\u5bb6\u4f19\uff01\u201d\u4e24\u4eba\u540c\u53bb\u770b\u96c5\u5fb7\u661f\u738b\u5b50\uff0c\u4e0d \u7981\u90fd\u5927\u5403\u4e00\u9a7e\uff0c\u53ea\u89c1\u96c5\u5fb7\u661f\u9762\u5982\u91d1\u7eb8\uff0c\u76ae\u80a4\u7070\u6697\uff0c\u6beb\u65e0\u5149\u6cfd\uff0c\u4e00\u63a2\u4ed6\u7684\u9f3b\u606f\uff0c\u6c14\u82e5\u6e38\u4e1d\uff0c\u9ea6 \u58eb\u8fe6\u5357\u7a0d\u61c2\u533b\u672f\uff0c\u518d\u7ed9\u4ed6\u628a\u4e00\u628a\u8109\u628a\uff0c\u6447\u6447\u5934\u9053\uff1a\u201c\u7cdf\u5566\uff01\u4ed6\u7684\u4e09\u516d\u7ecf\u8109\u5df2\u7ed9\u9707\u52a8\uff0c\u4e94\u810f\u4ea6 \u5df2\u6613\u4f4d\uff0c\u795e\u4ed9\u96be\u6551\uff01\u518d\u591a\u53ef\u4ee5\u6d3b\u534a\u4e2a\u65f6\u8fb0\u3002\u201d \u3000\u3000\u6842\u534e\u751f\u51dd\u781a\u7247\u523b\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u4e5f\u672a\u5fc5\uff01\u201d\u53d6\u51fa\u4e86\u4e00\u6735\u5929\u5c71\u96ea\u83b2\uff0c\u5c06\u96c5\u5fb7\u661f\u6276\u5230\u5c71\u6cc9\u65c1 \u8fb9\uff0c\u638f\u4e86\u4e00\u97a0\u6e05\u6cc9\uff0c\u63c9\u788e\u96ea\u83b2\u82b1\u74e3\uff0c\u64ac\u5f00\u4ed6\u7684\u7259\u5173\uff0c\u5c06\u6574\u6735\u96ea\u8fbe\u548c\u4e00\u97a0\u6e05\u6cc9\u90fd\u704c\u5165\u4ed6\u7684\u53e3 \u4e2d\u3002\u539f\u6765\u5929\u5c71\u96ea\u83b2\u4e0d\u4f46\u80fd\u6cbb\u767e\u6bd2\uff0c\u800c\u4e14\u8fd8\u53ef\u4ee5\u5316\u5f00\u4f53\u5185\u7684\u7600\u8840\uff0c\u7600\u8840\u4e00\u5316\uff0c\u9c9c\u8840\u7684\u5faa\u73af\u6062\u590d \u6b63\u5e38\uff0c\u4fbf\u4e0d\u81f4\u4e8e\u66b4\u6bd9\uff0c\u53ea\u662f\u53d7\u4f24\u592a\u91cd\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u8fd8\u4e0d\u80fd\u82cf\u9192\u3002\u9ea6\u58eb\u8fe6\u5357\u53f9\u9053\uff1a\u201c\u4f60\u767d\u767d\u7cdf \u584c\u4e86\u4e00\u6735\u5929\u5c71\u96ea\u83b2\uff0c\u6700\u591a\u4e0d\u8fc7\u80fd\u4eca\u4ed6\u7684\u751f\u547d\u5ef6\u957f\u6570\u65e5\u3002\u201d\u6842\u534e\u751f\u53cc\u6656\u70af\u70af\uff0c\u6df1\u6df1\u7684\u5438\u4e86\u53e3 \u6c14\uff0c\u5ffd\u9053\uff1a\u201c\u9ea6\u58eb\u8fe6\u5357\uff0c\u6211\u6c42\u4f60\u4e00\u6869\u4e8b\u60c5\u3002\u201d\u9ea6\u58eb\u8fe6\u5357\u9053\uff1a\u201c\u82e5\u6709\u6240\u9700\uff0c\u8d74\u6c64\u8e48\u706b\uff0c\u4ea6\u6240\u4e0d \u8f9e\u3002\u201d\u6842\u534e\u751f\u9053\uff1a\u201c\u597d\uff0c\u54b1\u4eec\u5404\u4ee5\u672c\u8eab\u529f\u529b\uff0c\u7ed9\u4ed6\u7597\u4f24\u3002\u6211\u6559\u4f60\u63a8\u8840\u8fc7\u5bab\u4e4b\u6cd5\u3002\u4f46\u7ed9\u4ed6\u533b\u6cbb \u597d\u4e86\uff0c\u54b1\u4eec\u771f\u6c14\u6d88\u8017\u8fc7\u751a\uff0c\u6050\u6015\u8981\u4e34\u5e8a\u9759\u517b\u51e0\u5929\u3002\u9ea6\u58eb\u8fe6\u5357\u6168\u7136\u8bf4\u9053\uff1a\u201c\u4f60\u7684\u5185\u529f\u6bd4\u6211\u6df1\u539a \u767e\u500d\uff0c\u4f60\u4e0d\u6015\u8017\u635f\u529f\u529b\uff0c\u6211\u4ea6\u81ea\u5f53\u7a0d\u53c2\u7ef5\u529b\u3002\u201d \u3000\u3000\u5176\u5b9e\u4ee5\u6842\u534e\u751f\u672c\u8eab\u7684\u529f\u529b\uff0c\u5df2\u53ef\u4ee5\u66ff\u96c5\u5fb7\u661f\u6253\u901a\u5341\u4e8c\u91cd\u5173\uff0c\u52a0\u4e0a\u4e86\u9ea6\u58eb\u8fe6\u5357\uff0c\u81ea\u662f\u66f4\u6613 \u89c1\u6548\uff0c\u4e0d\u8fc7\u4e00\u76cf\u8336\u7684\u65f6\u523b\uff0c\u96c5\u5fb7\u661f\u7684\u9762\u8272\u4fbf\u6e10\u6e10\u7ea2\u6da6\u8d77\u6765\u3002\u6842\u534e\u751f\u677e\u4e86\u53e3\u6c14\uff0c\u5ffd\u542c\u5f97\u9ea6\u58eb\u8fe6 \u5357\u95ee\u9053\uff1a\u201c\u521a\u624d\u90a3\u4f4d\u7f8e\u8c8c\u7684\u5973\u5b50\u662f\u8c01\uff1f\u201d\u539f\u6765\u516c\u4e3b\u4e34\u8d70\u4e4b\u65f6\uff0c\u9ea6\u58eb\u8fe6\u5357\u521a\u521a\u82cf\u9192\uff0c\u60ca\u9e3f\u4e00 \u77a5\uff0c\u5df2\u9707\u60ca\u4e8e\u5979\u7684\u56fd\u8272\u5929\u59ff\uff0c\u662f\u4ee5\u6709\u6b64\u4e00\u95ee\u3002\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u5979\u5417\uff1f\u5979\u662f\u516c\u4e3b\uff01\u4e5f\u5c31\u662f\u54b1\u4eec\u5728 \u9b54\u9b3c\u57ce\u4e2d\u6240\u542c\u5230\u7684\u90a3\u4f4d\u5439\u7b1b\u7684\u4ed9\u5973\uff01\u201d\u9ea6\u58eb\u8fe6\u5357\u201c\u963f\u5440\u201d\u4e00\u58f0\u53eb\u4e86\u8d77\u6765\uff0c\u5ffd\u5730\u505c\u4e86\u4e0b\u6765\u3002\u6842 \u534e\u751f\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u9ea6\u58eb\u8fe6\u5357\u9053\uff1a\u201c\u90a3\u4f60\u4e3a\u4f55\u8981\u8d39\u5c3d\u5fc3\u8840\u6765\u6551\u6b64\u4eba\uff1f\u201d\u6842\u534e\u751f\u4e00\u542c\u4fbf\u5982\u5176 \u610f\uff0c\u5fc3\u9053\uff1a\u201c\u6211\u82e5\u662f\u4e2a\u5c0f\u4eba\uff0c\u6492\u624b\u4e0d\u7ba1\uff0c\u5728\u516c\u4e3b\u7684\u6c42\u5a5a\u8005\u4e2d\uff0c\u4fbf\u51cf\u5c11\u4e86\u4e00\u4e2a\u5927\u5927\u7684\u52b2\u654c\uff0c\u4f46 \u6211\u5c82\u80af\u8fd9\u4e48\u53bb\u505a\uff1f\u201d\u4fbf\u5bf9\u9ea6\u58eb\u8fe6\u5357\u7b11\u9053\uff1a\u201c\u6211\u867d\u7136\u4e0d\u6562\u4ee5\u4fa0\u4e49\u81ea\u547d\uff0c\u4f46\u82e5\u662f\u89c1\u6b7b\u4e0d\u6551\uff0c\u4e8e\u5fc3 \u4f55\u5b89\uff1f\u201d\u9ea6\u58eb\u8fe6\u5357\u9053\uff1a\u201c\u6211\u5e76\u4e0d\u662f\u4e0d\u60f3\u6551\u4ed6\uff0c\u4e0d\u8fc7\u6211\u540f\u4f69\u670d\u4f60\u7684\u5927\u91cf\u3002\u5982\u679c\u6362\u6211\u662f\u4f60\u2026\u2026\u201d \u3000\u3000\u6842\u534e\u751f\u9053\uff1a\u201c\u4f60\u4fbf\u6837\u4e48\uff1f\u201d\u9ea6\u58eb\u8fe6\u5357\u60f3\u4e86\u4e00\u60f3\uff0c\u4e00\u7b11\u8bf4\u9053\uff1a\u201c\u8fd8\u662f\u6551\u4ed6\uff01\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u96c5\u5fb7\u661f\u738b\u5b50\u60a0\u60a0\u9192\u8f6c\uff0c\u8fd9\u4e00\u756a\u8bdd\u4ed6\u521a\u597d\u542c\u5230\uff0c\u53ef\u662f\u6842\u534e\u751f\u5374\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u6842\u534e\u751f\u5c06\u4ed6\u6276\u4e86\u8d77\u6765\uff0c\u638c\u5fc3\u7d27\u8d34\u4ed6\u80cc\u810a\u7684\u201c\u5929\u67a2\u7a74\u201d\uff0c\u4e00\u80a1\u9633\u548c\u4e4b\u6c14\uff0c\u4f20\u8fdb\u4e86\u4ed6\u7684\u4f53 \u5185\u3002\u96c5\u5fb7\u661f\u738b\u5b50\u5589\u5934\u5495\u5495\u4f5c\u54cd\uff0c\u5ffd\u5730\u4e00\u5927\u53e3\u4e00\u5927\u53e3\u7684\u7600\u8840\u5410\u4e86\u51fa\u6765\uff0c\u6842\u534e\u751f\u5f85\u4ed6\u547c\u5e72\u51c0\u540e\uff0c \u638f\u6e05\u6cc9\u7ed9\u4ed6\u6f31\u53e3\uff0c\u7b11\u9053\uff1a\u201c\u7600\u8840\u5410\u5c3d\uff0c\u518d\u7528\u4eba\u53c2\u9e7f\u8338\u7b49\u4e0a\u54c1\u8865\u836f\u533b\u6cbb\uff0c\u670d\u98df\u534a\u6708\uff0c\u5b9a\u53ef\u5eb7 \u590d\u3002\u8fd9\u4e9b\u836f\u54c1\u867d\u7136\u8d35\u91cd\uff0c\u5c3c\u6cca\u5c14\u7684\u5bab\u4e2d\u8981\u591a\u5c11\u90fd\u6709\uff0c\u738b\u5b50\uff0c\u4f60\u53ef\u4ee5\u653e\u5fc3\uff01\u201d\u96c5\u5fb7\u661f\u738b\u5b50\u773c\u4e2d \u8574\u6cea\uff0c\u6014\u6014\u7684\u671b\u7740\u6842\u534e\u751f\uff0c\u597d\u4e45\u597d\u4e45\uff0c\u8bf4\u4e0d\u51fa\u534a\u53e5\u8bdd\u3002 \u3000\u3000\u5c31\u5728\u6b64\u65f6\uff0c\u4e00\u9a91\u9a6c\u95ef\u8fdb\u5ce1\u8c37\uff0c\u539f\u6765\u662f\u5c3c\u6cca\u5c14\u738b\u5b50\u5230\u4e86\u3002\u4ed6\u4e00\u8fdb\u6765\u4fbf\u626c\u58f0\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u600e \u4e48\u5566\uff1f\u201d\u6842\u534e\u751f\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\uff0c\u96c5\u5fb7\u661f\u738b\u5b50\u53d7\u4e86\u4e00\u70b9\u70b9\u4f24\u3002\u5c3c\u6cca\u5c14\u738b\u5b50\u9053\uff1a\u201c\u90a3\u4e2a\uff0c\u90a3 \u4e2a\u2026\u2026\u201d\u9ea6\u58eb\u8fe6\u5357\u9053\uff1a\u201c\u90a3\u4e2a\u63d0\u6469\u8fbe\u591a\u4e48\uff1f\u4ed6\u65e9\u5df2\u7ed9\u6211\u4eec\u7684\u6842\u5927\u54e5\u6253\u8dd1\u5566\uff01\u201d\u5176\u5b9e\u9ea6\u58eb\u8fe6\u5357 \u5e76\u6ca1\u6709\u77a7\u89c1\u6842\u534e\u751f\u548c\u534e\u7389\u516c\u4e3b\u8054\u5251\u6740\u8d25\u63d0\u6469\u8fbe\u591a\uff0c\u90a3\u65f6\u4ed6\u8fd8\u5728\u660f\u8ff7\u72b6\u6001\u4e4b\u4e2d\uff0c\u8fd9\u8bf4\u8bdd\u4e0d\u8fc7\u60f3 \u5f53\u7136\u8033\u3002\u6842\u534e\u751f\u672c\u60f3\u8bf4\u660e\u771f\u76f8\uff0c\u60f3\u4e86\u4e00\u60f3\uff0c\u51b3\u5b9a\u8fd8\u662f\u7792\u7740\u738b\u5b50\u4e3a\u5999\uff0c\u4e8e\u662f\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a \u201c\u867d\u7136\u8d76\u8dd1\u4e86\u63d0\u6469\u8fbe\u591a\uff0c\u6469\u5c14\u6cd5\u5e08\u5374\u8d70\u8131\u4e86\u3002\u201d \u3000\u3000\u5c3c\u6cca\u5c14\u738b\u4e8e\u542c\u8bf4\u6842\u534e\u751f\u6253\u8dd1\u4e86\u63d0\u6469\u8fbe\u591a\uff0c\u5fc3\u4e2d\u5927\u5403\u4e00\u60ca\uff0c\u4f46\u4ed6\u7d20\u6027\u6df1\u6c89\uff0c\u5e76\u4e0d\u5f62\u4e8e\u795e \u8272\uff0c\u54c8\u54c8\u5927\u7b11\uff0c\u4ece\u9a6c\u80cc\u4e0a\u4e00\u8dc3\u800c\u4e0b\uff0c\u5c06\u624b\u4e2d\u7684\u4e5d\u73af\u9521\u6756\u5f80\u5730\u4e0a\u91cd\u91cd\u4e00\u987f\uff0c\u5411\u9ea6\u58eb\u8fe6\u5357\u9053\uff1a \u201c\u8fd9\u662f\u4e0d\u662f\u4f60\u4eec\u6cd5\u738b\u7684\u4f20\u6559\u6cd5\u6756\uff1f\u201d\u9ea6\u58eb\u8fe6\u5357\u5927\u559c\u9053\uff1a\u201c\u6b63\u662f\uff0c\u591a\u8c22\u738b\u4e8e\uff0c\u4f60\u66ff\u54b1\u4eec\u593a\u56de\u6765 \u4e86\uff1f\u201d\u5c3c\u6cca\u5c14\u738b\u5b50\u9053\uff1a\u201c\u4e0d\u6b62\u593a\u56de\u6cd5\u6756\uff0c\u6469\u5170\u6cd5\u5e08\u4e5f\u7ed9\u6211\u6740\u4e86\u3002\u201d\u539f\u6765\u738b\u5b50\u6284\u5c0f\u8def\u5230\u90a3\u8fb9\u8c37 \u53e3\u62e6\u622a\uff0c\u90a3\u662f\u5c71\u8c37\u4e2d\u4e00\u5904\u9690\u79d8\u7684\u51fa\u53e3\uff0c\u4ed6\u6599\u5b9a\u6469\u5170\u770b\u89c1\u70fd\u706b\uff0c\u5fc5\u7136\u4e0d\u6562\u4ece\u6b63\u9762\u7684\u8c37\u53e3\u51b2\u51fa\uff0c \u5fc3\u4e2d\u65e9\u5df2\u7b97\u597d\uff0c\u82e5\u662f\u6469\u5170\u548c\u63d0\u6469\u8fbe\u591a\u540c\u6765\uff0c\u4ed6\u7d22\u56de\u6cd5\u6756\u4e4b\u540e\uff0c\u4fbf\u53eb\u4ed6\u4eec\u8d76\u5feb\u9003\u8d70\uff0c\u8c01\u77e5\u5374\u53ea \u662f\u6469\u5170\u4e00\u4e2a\u4eba\u6765\uff0c\u56e0\u6b64\u4ed6\u4e00\u89c1\u9762\uff0c\u4e0d\u5f85\u4ed6\u5f00\u58f0\u4fbf\u628a\u4ed6\u6740\u4e86\u3002\u4ed6\u548c\u6469\u5170\u662f\u540c\u8c0b\u6bd2\u5bb3\u56fd\u738b\u7684\u4f19 \u4f34\uff0c\u6469\u5170\u7edd\u6599\u4e0d\u5230\u738b\u5b50\u7adf\u4f1a\u6740\u4ed6\uff0c\u8981\u4e0d\u7136\u4ee5\u6469\u5170\u7684\u6b66\u529f\uff0c\u738b\u5b50\u6700\u591a\u4e0d\u8fc7\u4e0e\u4ed6\u6253\u4e2a\u5e73\u624b\u3002 \u3000\u3000\u9ea6\u58eb\u8fe6\u5357\u63a5\u8fc7\u6cd5\u6756\uff0c\u5c3c\u6cca\u5c14\u738b\u5b50\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u8be5\u56de\u5bab\u53bb\u89c1\u56fd\u738b\u62a5\u559c\u5566\uff01\u6cd5\u6756\u867d\u7136\u662f\u6211\u593a \u56de\u6765\u7684\uff0c\u4f46\u4f60\u4eec\u90fd\u6709\u4e00\u4efd\u529f\u52b3\u3002\u201d\u96c5\u5fb7\u661f\u738b\u5b50\u867d\u7136\u9192\u8f6c\uff0c\u8eab\u4f53\u8fd8\u662f\u865a\u5f31\u4e4b\u6781\uff0c\u6842\u534e\u751f\u5c06\u4ed6\u62b1 \u4e0a\u9a6c\u80cc\uff0c\u548c\u4ed6\u540c\u4e58\u4e00\u5339\u9a8f\u9a6c\uff0c\u9a70\u56de\u4eac\u57ce\u3002 \u3000\u3000\u8d70\u4e86\u4e00\u91cc\uff0c\u96c5\u5fb7\u661f\u5ffd\u5730\u5fae\u5fae\u547b\u541f\uff0c\u547c\u5438\u7d27\u4fc3\uff0c\u6842\u534e\u751f\u6025\u5fd9\u52d2\u4f4f\u9a8f\u9a6c\uff0c\u8ba9\u4ed6\u7f13\u7f13\u800c\u884c\uff0c\u4f4e \u58f0\u95ee\u9053\uff1a\u201c\u4f60\u89c9\u5f97\u600e\u4e48\uff1f\u201d\u96c5\u5fb7\u661f\u65ad\u65ad\u7eed\u7eed\u8bf4\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u53ea\u6015\u4e0d\u884c\u5566\uff01\u201d\u6842\u534e\u751f\u9053\uff1a \u201c\u6ca1\u6709\u7684\u4e8b\uff0c\u4f46\u8bf7\u5b89\u5fc3\uff01\u201d\u96c5\u5fb7\u661f\u98a4\u58f0\u8bf4\u9053\uff1a\u201c\u7075\u9e33\u5c71\u3001\u738b\u820d\u57ce\u3001\u9f99\u53f6\u6069\u5e08\u3001\u6052\u6cb3\u98ce\u5149\uff0c\u4ece \u4eca\u6c38\u522b\u4e86\uff01\u201d\u6842\u534e\u751f\u53eb\u9053\uff1a\u201c\u4f60\u4e0d\u8981\u80e1\u601d\u4e71\u60f3\uff0c\u4f60\u6b7b\u4e0d\u4e86\uff01\u201d\u5c06\u624b\u638c\u8d34\u5728\u4ed6\u7684\u592a\u9633\u7a74\u4e0a\u4e00 \u8bd5\uff0c\u53ea\u89c9\u4ed6\u53d1\u7740\u9ad8\u70ed\uff0c\u6842\u534e\u751f\u4e5f\u4e0d\u7981\u5403\u4e86\u4e00\u60ca\uff0c\u60f3\u9053\uff1a\u201c\u96be\u9053\u6211\u66ff\u4ed6\u6253\u901a\u5341\u4e8c\u91cd\u5173\uff0c\u8fd8\u4e0d\u80fd \u6551\u4ed6\u6027\u547d\uff1f\u4e0d\uff0c\u4e0d\uff0c\u4ed6\u672c\u8eab\u7684\u745c\u4f3d\u529f\u592b\u4e5f\u5df2\u6709\u4e86\u76f8\u5f53\u9020\u8c13\uff0c\u53ea\u8981\u4ed6\u5fc3\u4e2d\u5b89\u5b9a\uff0c\u7edd\u5bf9\u6b7b\u4e0d \u4e86\u3002\u201d\u6b63\u8981\u6307\u5bfc\u4ed6\u81ea\u5df1\u7528\u5410\u7eb3\u7684\u65b9\u6cd5\u9547\u5b9a\u7cbe\u795e\uff0c\u53ea\u89c1\u4ed6\u8fd8\u662f\u65ad\u65ad\u7eed\u7684\u7eed\u53d1\u7740\u7eed\u8bed\uff0c\u597d\u50cf\u5df2\u5728 \u8ff7\u7cca\u7684\u72b6\u6001\u4e4b\u4e2d\uff0c\u6842\u534e\u751f\u751a\u662f\u7740\u6025\uff0c\u5ffd\u89c1\u96c5\u5fb7\u661f\u7741\u5927\u4e86\u773c\uff0c\u98a4\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u6c42\u4f60\u4e00\u4ef6\u4e8b \u60c5\uff01\u201d \u3000\u3000\u6842\u534e\u751f\u9053\uff1a\u201c\u8bf7\u8bf4\u3002\u201d\u96c5\u5fb7\u661f\u9053\uff1a\u201c\u6211\u8981\u4e00\u6735\u5929\u5c71\u96ea\u83b2\u3002\u201d\u6842\u534e\u751f\u6014\u4e86\u4e00\u6014\uff0c\u7b11\u9053\uff1a \u201c\u4f60\u4f53\u5185\u7684\u7600\u8840\u90fd\u5df2\u5316\u5f00\u4e86\uff0c\u4e0d\u9700\u8981\u518d\u7528\u96ea\u83b2\u5566\u3002\u201d\u96c5\u5fb7\u661f\u53cc\u773c\u706b\u7ea2\uff0c\u53ea\u662f\u65ad\u65ad\u7eed\u7eed\u7684\u8bf4 \u9053\uff1a\u201c\u96ea\u83b2\uff0c\u96ea\u83b2\uff0c\u5929\u5c71\u96ea\u83b2\uff0c\u6211\u8981\u5929\u5c71\u96ea\u83b2\uff01\u201d\u90a3\u795e\u60c5\u5c31\u50cf\u5c0f\u5b69\u5b50\u5411\u5927\u4eba\u6492\u6cfc\uff0c\u7d22\u53d6\u73a9\u7269 \u4e00\u6837\u3002\u6842\u534e\u751f\u7ed9\u4ed6\u8f7b\u8f7b\u63a8\u63c9\uff0c\u8bf4\u9053\uff1a\u201c\u738b\u5b50\u4f60\u9192\u9192\uff0c\u54b1\u4eec\u5c31\u8981\u5230\u738b\u5bab\u5566\u3002\u201d\u4f46\u96c5\u5fb7\u661f\u8fd8\u662f\u6ca1 \u6709\u6e05\u9192\uff0c\u597d\u50cf\u9ad8\u70ed\u7684\u75c5\u4eba\u4e00\u6837\u53d1\u7740\u8c10\u8bed\uff0c\u4e0d\u65ad\u7684\u53eb\uff1a\u201c\u96ea\u83b2\uff0c\u96ea\u83b2\uff0c\u5929\u5c71\u96ea\u83b2\uff01\u201d\u6842\u534e\u751f\u5fc3 \u6709\u4e0d\u5fcd\uff0c\u5c06\u4ec5\u6709\u4e00\u6735\u5929\u5c71\u96ea\u83b2\u62ff\u51fa\u6765\uff0c\u96c5\u5fb7\u661f\u53cc\u773c\u653e\u5149\uff0c\u5583\u5583\u8bf4\u9053\uff1a\u201c\u6211\u8d39\u5c3d\u65e0\u7a77\u5fc3\u529b\uff0c\u6c42 \u53d6\u96ea\u83b2\uff0c\u82e5\u5f97\u4e00\u6735\u96ea\u83b2\u966a\u846c\uff0c\u6b7b\u4e86\u65b9\u53ef\u7791\u76ee\u3002\u55ef\uff0c\u7ed9\u6211\uff0c\u7ed9\u6211\uff01\u201d\u6842\u534e\u751f\u5fc3\u9053\uff1a\u201c\u539f\u6765\u4ed6\u662f \u8fd9\u4e2a\u60f3\u6cd5\u3002\u201d\u6709\u4e9b\u4eba\uff08\u5c24\u5176\u662f\u5e1d\u738b\uff09\u5728\u4e34\u6b7b\u4e4b\u65f6\uff0c\u603b\u60f3\u628a\u6700\u5fc3\u7231\u7684\u4e1c\u897f\u6b89\u4e27\uff0c\u4f46\u6842\u534e\u751f\u5374\u60f3 \u4e0d\u5230\u96c5\u5fb7\u661f\u4e5f\u6709\u8fd9\u6837\u7684\u5fc3\u7406\u3002 \u3000\u3000\u6842\u534e\u751f\u4e00\u9635\u8e0c\u8e87\uff0c\u4ed6\u4ece\u5929\u5c71\u6240\u5e26\u6765\u7684\u4e09\u6735\u96ea\u83b2\uff0c\u4e00\u6735\u6551\u4e86\u5c3c\u6cca\u5c14\u56fd\u738b\uff0c\u4e00\u6735\u6551\u4e86\u96c5\u5fb7\u661f \u738b\u5b50\uff0c\u73b0\u5728\u5c31\u8a8a\u4e0b\u4e00\u6735\u4e86\uff0c\u4ed6\u660e\u660e\u77e5\u9053\u96c5\u5fb7\u661f\u738b\u5b50\u4e0d\u518d\u9700\u8981\u5929\u5c71\u96ea\u83b2\u4e86\uff0c\u53ef\u662f\u770b\u4ed6\u773c\u8272\u4e2d\u5145 \u6ee1\u7948\u6c42\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u5df4\u52d2\u8001\u5148\u751f\u8bf4\u8fc7\uff0c\u7ed9\u75c5\u4eba\u6cbb\u75c5\uff0c\u6700\u7d27\u8981\u7684\u662f\u4eca\u75c5\u4eba\u5fc3\u5b89\u3002\u6211\u82e5\u541d\u556c\u4e0d \u4e0e\uff0c\u53ea\u6015\u4ed6\u7684\u75c5\u60c5\u53c8\u4f1a\u6076\u5316\u3002\u4e0d\u5982\u5148\u7ed9\u4e86\u4ed6\u518d\u8bf4\u3002\u201d\u4e8e\u662f\u5fae\u5fae\u4e00\u7b11\uff0c\u5c06\u4ec5\u6709\u7684\u4e00\u6735\u5929\u5c71\u96ea\u83b2 \u4ea4\u7ed9\u4e86\u96c5\u5fb7\u661f\u3002 \u3000\u3000\u96c5\u5fb7\u661f\u63a5\u8fc7\u96ea\u83b2\uff0c\u8138\u4e0a\u6cdb\u51fa\u7b11\u5bb9\uff0c\u9760\u7740\u6842\u534e\u751f\u5bbd\u7684\u80a9\u818a\uff0c\u95ed\u7740\u773c\u775b\u517b\u795e\uff0c\u73b0\u51fa\u5fc3\u6ee1\u610f\u8db3 \u7684\u6837\u5b50\u3002\u6842\u534e\u751f\u5927\u4e3a\u6b23\u6170\uff0c\u7b56\u9a6c\u7f13\u7f13\u800c\u884c\uff0c\u5230\u4e86\u5bab\u95e8\uff0c\u65e9\u5df2\u6709\u4eba\u5019\u9a70\uff0c\u539f\u6765\u5c3c\u6cca\u5c14\u738b\u5b50\u548c\u9ea6 \u58eb\u8fe6\u5357\u5148\u4ed6\u8fdb\u5bab\uff0c\u65e9\u5df2\u628a\u96c5\u5fb7\u661f\u53d7\u4f24\u7684\u4e8b\u60c5\u62a5\u544a\u7ed9\u56fd\u738b\u77e5\u9053\u4e86\u3002 \u3000\u3000\u5bab\u4e2d\u4f8d\u536b\u5c06\u96c5\u5fb7\u661f\u653e\u5728\u4e00\u5f20\u6709\u9526\u8925\u7684\u62c5\u67b6\u4e0a\uff0c\u62ac\u5230\u56fd\u738b\u9762\u524d\uff0c\u56fd\u738b\u4eb2\u4e0b\u5fa1\u5ea7\u8fce\u63a5\uff0c\u4e00\u9762 \u5411\u6842\u534e\u751f\u81f4\u8c22\uff0c\u4e00\u9762\u6170\u95ee\u96c5\u5fb7\u661f\u7684\u53d7\u4f24\u3002\u96c5\u5fb7\u661f\u5ffd\u5730\u4e00\u7b11\u800c\u8d77\uff0c\u8dc3\u4e0b\u62c5\u67b6\uff0c\u5411\u56fd\u738b\u65bd\u4f53\u8bf4 \u9053\uff1a\u201c\u516c\u4e3b\u6240\u8981\u7684\u5929\u5c71\u96ea\u83b2\uff0c\u6211\u5df2\u7ecf\u5e26\u6765\u5566\uff01\u201d \u3000\u3000\u6842\u534e\u751f\u5403\u4e86\u4e00\u60ca\uff0c\u8fd9\u624d\u77e5\u9053\u96c5\u5fb7\u661f\u521a\u624d\u662f\u5047\u88c5\u7684\u3002\u8fd9\u970e\u90a3\u95f4\u6842\u534e\u751f\u5fc3\u5934\u4e00\u7247\u6df7\u4e71\uff0c\u53ea\u542c \u5f97\u56fd\u738b\u559c\u9053\uff1a\u201c\u597d\uff0c\u6211\u7acb\u5373\u53eb\u5bab\u5973\u62ff\u4f60\u7684\u7816\u7269\u4ea4\u7ed9\u5c0f\u5973\u3002\u8fd9\u662f\u5979\u81ea\u5df1\u8981\u7684\uff0c\u5e94\u8be5\u6ca1\u6709\u8bdd\u8bf4 \u5566\uff01\u201d\u540e\u9762\u4e24\u53e5\u662f\u56fd\u738b\u5411\u738b\u5b50\u8bf4\u7684\u3002 \u3000\u3000\u6842\u534e\u751f\u5589\u5934\u786c\u585e\uff0c\u4e00\u6025\u4e4b\u4e0b\uff0c\u6ee1\u9762\u901a\u7ea2\uff0c\u4e0d\u77e5\u8bf4\u4ec0\u4e48\u597d\u3002\u5ffd\u542c\u5f97\u96c5\u5fb7\u661f\u738b\u5b50\u6717\u58f0\u8bf4\u9053\uff1a \u201c\u4e0d\uff0c\u965b\u4e0b\u4f60\u8bef\u4f1a\u4e86\uff0c\u8fd9\u4e0d\u662f\u6211\u7684\u8058\u7269\u3002\u8fd9\u6735\u5929\u5c71\u96ea\u83b2\u662f\u6842\u5148\u751f\u7684\uff0c\u6842\u5148\u751f\u7684\u6b66\u529f\u4eba\u54c1\u80dc\u6211 \u767e\u500d\uff0c\u4e0e\u516c\u4e3b\u6b63\u662f\u5929\u9020\u5730\u8bbe\u7684\u4e00\u5bf9\u74a7\u4eba\uff01\u201d \u3000\u3000\u6b64\u8bdd\u4e00\u51fa\uff0c\u5927\u51fa\u6842\u534e\u751f\u610f\u5916\uff0c\u547c\u4e86\u53e3\u6c14\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u6211\u51e0\u4e4e\u4ee5\u5c0f\u4eba\u4e4b\u5fc3\u5ea6\u541b\u5b50\u4e4b \u8179\uff01\u201d\u53ea\u89c1\u96c5\u5fb7\u661f\u8bf4\u5b8c\u8fd9\u756a\u8bdd\u4e4b\u540e\uff0c\u6447\u6447\u6b32\u5760\uff0c\u4ed6\u635f\u8017\u8fc7\u751a\uff0c\u5982\u4eca\u5f3a\u632f\u7cbe\u795e\uff0c\u6bd5\u7adf\u662f\u652f\u6301\u4e0d \u4f4f\u4e86\u3002 \u3000\u3000\u56fd\u738b\u9762\u8272\u4e00\u53d8\uff0c\u671b\u4e86\u6842\u534e\u751f\u4e00\u773c\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u96c5\u5fb7\u661f\u738b\u5b50\u795e\u667a\u660f\u8ff7\uff0c\u771f\u662f\u4eca\u4eba\u96be\u8fc7\u3002 \u6842\u5148\u751f\u4f60\u4eca\u6b21\u7acb\u4e86\u5927\u529f\uff0c\u6211\u5fc5\u5b9a\u4e0d\u4f1a\u8d1f\u4f60\uff0c\u5bab\u4e2d\u73e0\u5b9d\uff0c\u4efb\u4f60\u7d22\u53d6\uff0c\u82e5\u6b32\u4e3a\u5b98\uff0c\u8dcc\u4fbf\u4efb\u4f60\u505a\u5fa1 \u6797\u519b\u603b\u7ba1\u3002\u201d \u3000\u3000\u56fd\u738b\u53ea\u63d0\u5c01\u8d4f\uff0c\u7edd\u53e3\u4e0d\u8c08\u5a5a\u4e8b\uff0c\u6842\u534e\u751f\u81ea\u662f\u542c\u5f97\u61c2\u4ed6\u7684\u610f\u601d\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u53ea\u8981\u516c\u4e3b\u771f \u5fc3\u7231\u6211\uff0c\u6211\u4e0d\u4f1a\u62a5\u540d\u53c2\u52a0\u6c42\u5a5a\u8005\u7684\u9009\u62d4\u8003\u8bd5\u5417\uff1f\u4f55\u5fc5\u8981\u81ea\u6043\u529f\u52b3\uff0c\u5411\u56fd\u738b\u63d0\u51fa\u5a5a\u4e8b\uff1f\u201d\u5f53\u4e0b \u5411\u56fd\u738b\u8c22\u9053\uff1a\u201c\u6211\u65e9\u5df2\u8bf4\u8fc7\uff0c\u4e0d\u671b\u916c\u62a5\u3002\u73e0\u5b9d\u4e8e\u6211\u65e0\u7528\uff0c\u9ad8\u5b98\u66f4\u4e0d\u613f\u4e3a\u3002\u201d\u56fd\u738b\u9053\uff1a\u201c\u90a3\u4e48 \u5c31\u8bf7\u5148\u751f\u591a\u4f4f\u751f\u65f6\uff0c\u4e5f\u597d\u8ba9\u5be1\u4eba\u5f97\u4ee5\u5e38\u8046\u6559\u76ca\u3002\u201d\u6842\u534e\u751f\u9053\uff1a\u201c\u4e0d\u6562\u9ebb\u70e6\u965b\u4e0b\uff0c\u6211\u4f4f\u5df4\u52d2\u5bb6 \u4e2d\uff0c\u965b\u4e0b\u82e5\u6709\u5ba3\u53ec\uff0c\u6211\u81ea\u5f53\u968f\u65f6\u664b\u8c12\u3002\u201d \u3000\u3000\u56fd\u738b\u5fc3\u4e2d\u867d\u6709\u6b49\u610f\uff0c\u4f46\u8f6c\u5ff5\u4e00\u60f3\uff0c\u5973\u513f\u5bf9\u4ed6\u9887\u4f3c\u6709\u610f\uff0c\u8fd8\u662f\u4e0d\u8981\u8ba9\u4ed6\u5728\u5bab\u4e2d\u4e3a\u5999\u3002\u539f\u6765 \u56fd\u738b\u4ecd\u662f\u60f3\u628a\u516c\u4e3b\u8bb8\u914d\u7ed9\u96c5\u5fb7\u661f\uff0c\u5728\u56fd\u738b\u773c\u4e2d\uff0c\u6842\u534e\u751f\u7684\u624d\u5b66\u6b66\u529f\u4eba\u54c1\uff0c\u867d\u7136\u4e0d\u8f93\u7ed9\u96c5\u5fb7 \u661f\uff0c\u4f46\u6842\u534e\u751f\u5230\u5e95\u4e0d\u662f\u738b\u5b50\u7684\u8eab\u4efd\u3002 \u3000\u3000\u5f53\u4e0b\u56fd\u738b\u91cd\u91cd\u7684\u8d4f\u8d50\u4e86\u5df4\u52d2\u533b\u751f\uff0c\u4fbf\u9001\u4ed6\u4eec\u51fa\u5bab\u3002\u96c5\u5fb7\u661f\u738b\u5b50\u5219\u7559\u5728\u5bab\u4e2d\u517b\u75c5\u3002 \u3000\u3000\u4e24\u4eba\u8d70\u51fa\u6df1\u5bab\uff0c\u604d\u5982\u4e00\u68a6\u3002\u5df4\u52d2\u7b11\u9053\uff1a\u201c\u6211\u6b63\u60f3\u8bd1\u674e\u65f6\u73cd\u7684\u300a\u672c\u8349\u7eb2\u76ee\u300b\u548c\u300a\u5947\u7ecf\u516b\u8109 \u8003\u300b\uff0c\u4e2d\u56fd\u6587\u5b57\u6df1\u5965\uff0c\u6b63\u597d\u8bf7\u4f60\u5e2e\u52a9\u3002\u4ed6\u5e74\u4f60\u505a\u4e86\u9a79\u9a6c\u4e4b\u540e\uff0c\u8fd8\u8981\u501f\u91cd\u4f60\u7684\u5927\u529b\uff0c\u5c06\u8fd9\u4e9b\u533b \u4e66\uff0c\u5927\u91cf\u520a\u5370\u3002\u6842\u534e\u751f\u9053\uff1a\u201c\u6211\u90a3\u91cc\u6709\u8fd9\u4e2a\u798f\u4efd\u3002\u201d\u5df4\u52d2\u7b11\u9053\uff1a\u201c\u6211\u4eec\u8fd9\u91cc\u6709\u53e5\u8c1a\u8bed\uff0c\u53ea\u8981 \u59d1\u5a18\u5bf9\u4f60\u6709\u4e86\u60c5\u610f\uff0c\u90a3\u5c31\u4f3c\u871c\u8702\u53bb\u91c7\u82b1\u871c\uff0c\u8d76\u4e5f\u8d76\u4e0d\u53bb\u7684\u3002\u201d \u3000\u3000\u8c08\u7b11\u4e4b\u95f4\uff0c\u4e0d\u77e5\u4e0d\u89c9\u5df2\u56de\u5230\u5bb6\u95e8\uff0c\u5df4\u52d2\u6253\u5f00\u5927\u95e8\uff0c\u4fbf\u55c5\u5230\u4e00\u80a1\u5e7d\u9999\uff0c\u8d70\u4e0a\u5385\u5802\u4e00\u770b\uff0c\u53ea \u89c1\u8336\u51e0\u571f\u653e\u7740\u4e00\u53e0\u4e66\u7c4d\uff0c\u538b\u7740\u4e00\u7eb8\u82b1\u7bd3\uff0c\u5df4\u52d2\u7b11\u9053\uff1a\u201c\u4f60\u77a7\uff0c\u6709\u4e86\u4f60\u5728\u8fd9\u91cc\uff0c\u82b1\u9999\u81ea\u5df1\u4fbf\u4f1a \u98d8\u8fdb\u6765\u4e86\u3002\u201d \u3000\u3000\u6842\u534e\u751f\u62bd\u51fa\u82b1\u7bd3\u4e00\u770b\uff0c\u53ea\u89c1\u4e0a\u9762\u5199\u7740\u4e00\u9996\u5c0f\u8bcd\uff0c\u8c03\u5bc4\uff0c\u8bcd\u9053\uff1a\u201c\u840d\u6c34\u76f8\u9022\uff0c\u4fbf\u5df2\u662f\u7434\u5fc3 \u5148\u8bb8\u3002\u4e00\u5bb6\u5bf0\u5b87\uff0c\u6d77\u5185\u5b58\u77e5\u5df1\u3002\u7eb5\u6709\u73e0\u5cf0\uff0c\u96be\u9694\u5929\u53f0\u8def\u3002\u4e66\u6b64\u610f\uff0c\u610f\u51ed\u98ce\u7d6e\uff0c\u5439\u5411\u90ce\u8fb9 \u53bb\u3002\u201d\u4e0d\u5fc5\u7f72\u540d\uff0c\u6842\u534e\u751f\u5df2\u77e5\u9053\u662f\u516c\u4e3b\u624b\u5199\u7684\u8bcd\u7bd3\uff0c\u8bcd\u4e2d\u5145\u6ee1\u201c\u56db\u6d77\u4e00\u5bb6\u201d\u4e4b\u610f\uff0c\u7eb5\u6709\u5929\u4e0b \u7b56\u4e00\u9ad8\u5cf0\uff0c\u4e5f\u9694\u4e0d\u65ad\u4e24\u56fd\u7684\u53cb\u8c0a\uff0c\u5f53\u7136\u66f4\u963b\u4e0d\u4e86\u4e24\u4eba\u4e4b\u95f4\u7684\u60c5\u610f\u3002\u6842\u534e\u751f\u8bfb\u540e\uff0c\u5982\u9189\u5982\u75f4\uff0c \u800c\u53c8\u8c41\u7136\u5f00\u6717\u3002 \u3000\u3000\u518d\u68c0\u9605\u90a3\u4e00\u53e0\u4e66\uff0c\u5374\u90fd\u662f\u5c3c\u6cca\u5c14\u7684\u53e4\u5178\u6587\u5b66\uff0c\u6842\u534e\u751f\u5f53\u665a\u5c31\u8bfb\u4e86\u4e00\u665a\uff0c\u8fd9\u4e00\u4e9b\u53e4\u5178\u6587 \u5b66\uff0c\u5927\u534a\u90fd\u4e0e\u4e2d\u56fd\u6709\u5173\u7684\uff0c\u4f8b\u5982\u6167\u8d85\u6cd5\u5e08\u6240\u7740\u7684\u300a\u5f80\u4e94\u5929\u7afa\u56fd\u8bb0\u300b\uff0c\u6167\u8d85\u662f\u5510\u671d\u9ad8\u50e7\uff0c\u7a0d\u540e \u4e8e\u7384\u88c5\u6cd5\u5e08\uff0c\u4ed6\u66fe\u7ee7\u7384\u88c5\u4e4b\u540e\u5230\u8fc7\u5370\u5ea6\uff0c\u4e66\u4e2d\u7684\u300a\u8fe6\u7ef4\u7f57\u536b\u4e00\u8bcd\u300b\uff0c\u6240\u53d9\u8ff0\u7684\u4fbf\u662f\u5c3c\u6cca\u5c14\u4e4b \u884c\uff0c\u6b64\u4e66\u5df2\u8bd1\u6210\u5c3c\u6cca\u5c14\u6587\uff1b\u53e6\u5916\u8fd8\u6709\u5c3c\u6cca\u5c14\u672c\u56fd\u9ad8\u50e7\u6240\u5199\u7684\u7ecf\u201c\u53d8\u6587\u201d\uff0c\u90a3\u662f\u4e00\u79cd\u6709\u97f5\u811a\u7684 \u4f5b\u6559\u6587\u5b66\uff0c\u4f8b\u5982\u963f\u5c3c\u54e5\u7684\u7b49\u7b49\u3002\u8981\u77e5\u5c3c\u6cca\u5c14\u4e43\u662f\u662f\u4f5b\u56fd\uff0c\u6240\u4ee5\u5b83\u7684\u53e4\u5178\u6587\u5b66\uff0c\u591a\u4e0e\u4f5b\u6559\u6709 \u5173\u3002\u6842\u534e\u751f\u867d\u7136\u4e0d\u662f\u4f5b\u6559\u5f92\uff0c\u4f46\u4e00\u6765\u8fd9\u4e9b\u7ecf\u5178\u6587\u5b66\u6bd4\u55bb\u4e30\u5bcc\uff0c\u5999\u8bed\u5982\u73e0\uff1b\u4e8c\u6765\u5fc3\u77e5\u8fd9\u4e9b\u4e66\u662f \u516c\u4e3b\u8981\u4ed6\u770b\u7684\uff0c\u6545\u6b64\u4e5f\u8bfb\u5f97\u6d25\u6d25\u6709\u5473\u3002\u4ed6\u8fd8\u628a\u6240\u6709\u4e0d\u61c2\u7684\u5730\u65b9\u8bb0\u4e0b\u4e86\u8bb0\u53f7\uff0c\u51c6\u5907\u7b2c\u4e8c\u5929\u53bb\u8bf7 \u6559\u5df4\u52d2\u3002 \u3000\u3000\u7b2c\u4e8c\u5929\u4e00\u65f1\uff0c\u4ed6\u8fd8\u6ca1\u6709\u8d77\u5e8a\uff0c\u5df4\u52d2\u5df2\u5148\u6765\u770b\u4ed6\u4e86\uff0c\u4e00\u89c1\u9762\u5c31\u9053\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u4eca\u5929\u51c6\u5907\u53bb \u62a5\u540d\u5411\u516c\u4e3b\u6c42\u5a5a\uff1f\u201d\u6842\u534e\u751f\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u5df4\u52d2\u9053\uff1a\u201c\u4f60\u4e0d\u7528\u53bb\u4e86\u3002\u201d\u6842\u534e\u751f\u5403\u4e86\u4e00\u60ca\uff0c\u6025 \u95f4\u5176\u6545\u3002 \u3000\u3000\u5df4\u52d2\u9053\uff1a\u201c\u56fd\u738b\u6709\u547d\uff0c\u9009\u62d4\u9a79\u9a6c\u4e4b\u4e8b\uff0c\u6682\u65f6\u505c\u987f\uff0c\u8981\u5f85\u767e\u65e5\u4e4b\u540e\uff0c\u65b9\u518d\u4e3e\u884c\u3002\u542c\u8bf4\u4e00\u6765 \u662f\u56e0\u4e3a\u660e\u5e74\u7684\u4f5b\u79df\u8bde\u8fb0\u8981\u4e3e\u884c\u65e0\u906e\u5927\u4f1a\uff0c\u5404\u56fd\u7684\u9ad8\u50e7\u5f02\u58eb\u5728\u4eca\u540e\u8fd9\u51e0\u4e2a\u6708\u4e4b\u5185\uff0c\u5c06\u4f1a\u9646\u7eed\u5230 \u6765\uff0c\u5176\u4e2d\u6216\u6709\u6c42\u5a5a\u4e4b\u4eba\uff0c\u6545\u6b64\u7a0d\u5f85\uff1b\u4e8c\u6765\u662f\u56fd\u738b\u75c5\u4f53\u521d\u6108\uff0c\u8981\u516c\u4e3b\u966a\u4f34\uff0c\u65e0\u6687\u9009\u62d4\u3002\u201d\u5176\u5b9e \u8fd8\u6709\u7b2c\u4e09\u4e2a\u771f\u6b63\u7684\u539f\u56e0\uff0c\u5df4\u52d2\u77e5\u800c\u4e0d\u8bf4\uff0c\u90a3\u662f\u56e0\u4e3a\u56fd\u738b\u60f3\u628a\u516c\u4e3b\u8bb8\u914d\u7ed9\u96c5\u5fb7\u661f\u738b\u5b50\uff0c\u96c5\u5fb7\u661f \u6b63\u5728\u5bab\u4e2d\u517b\u75c5\uff0c\u56fd\u738b\u5e0c\u671b\u5728\u767e\u65e5\u4e4b\u5185\uff0c\u80fd\u4f7f\u4ed6\u4eec\u4e24\u4eba\u56de\u5fc3\u8f6c\u610f\u3002 \u3000\u3000\u6842\u534e\u751f\u7b11\u9053\uff1a\u201c\u90a3\u6709\u4ec0\u4e48\u7d27\u8981\uff1f\u6211\u4fbf\u7b49\u5f85\u767e\u65e5\uff0c\u53c8\u6709\u4f55\u59a8\uff1f\u6211\u6b63\u60f3\u5f97\u591a\u4e00\u4e9b\u65f6\u95f4\uff0c\u597d\u719f \u6089\u8d35\u56fd\u7684\u98ce\u4fd7\u4e60\u60ef\u3002\u6216\u8005\u5230\u5404\u5904\u770b\u770b\u3002\u201d \u3000\u3000\u81ea\u6b64\u6842\u534e\u751f\u4fbf\u5728\u5df4\u52d2\u5bb6\u4e2d\u4f4f\u4e0b\uff0c\u767d\u5929\u8bfb\u4e66\uff0c\u665a\u4e0a\u5e2e\u52a9\u5df4\u52d2\u8bd1\u674e\u65f6\u73cd\u7684\u300a\u672c\u8349\u7eb2\u76ee\u300b\u548c \u300a\u5947\u7ecf\u516b\u548f\u8003\u300b\u3002\u5076\u800c\u62bd\u7a7a\u4e5f\u5230\u8857\u4e0a\u8d70\u8d70\u3002\u5df4\u52d2\u5b66\u95ee\u6e0a\u535a\uff0c\u4e24\u4eba\u5207\u78cb\uff0c\u5f97\u76ca\u4e0d\u5c11\u3002\u4e0d\u77e5\u4e0d\u89c9 \u8fc7\u4e86\u4e00\u4e2a\u591a\u6708\u3002\u8fd9\u4e00\u4e2a\u591a\u6708\u4e4b\u4e2d\uff0c\u53ea\u6709\u9ea6\u58eb\u8fe6\u5357\u56de\u56fd\u4e4b\u65e5\uff0c\u56fd\u738b\u4e3e\u884c\u5bb4\u5237\uff0c\u66fe\u53ec\u6842\u534e\u751f\u8fdb \u5bab\uff0c\u4f46\u5c31\u662f\u5728\u90a3\u6b21\u9001\u522b\u7684\u5bb4\u4f1a\u4e0a\uff0c\u4e5f\u6ca1\u89c1\u7740\u516c\u4e3b\u548c\u96c5\u5fb7\u661f\u3002\u542c\u5fa1\u533b\u8bf4\u738b\u5b50\u53d7\u4f24\u592a\u91cd\uff0c\u867d\u5f97\u96ea \u83b2\u5316\u5f00\u7600\u8840\uff0c\u4ecd\u7136\u672a\u66fe\u5eb7\u590d\u3002 \u3000\u3000\u90a3\u6b21\u4e4b\u540e\uff0c\u738b\u56fd\u5c31\u6ca1\u6709\u518d\u62db\u8fc7\u6842\u534e\u751f\u3002\u6842\u534e\u751f\u867d\u7136\u5bdf\u89c9\u56fd\u738b\u5bf9\u4ed6\u51b7\u6de1\uff0c\u5374\u4e5f\u4e0d\u4ee5\u4e3a\u610f\u3002 \u3000\u3000\u4e00\u65e5\uff0c\u6842\u534e\u751f\u5230\u745e\u626c\u5e03\u5c71\u53c2\u89c2\u83b2\u82b1\u5b9d\u5854\u56de\u6765\uff0c\u89c1\u5df4\u52d2\u7565\u5e26\u6101\u5bb9\uff0c\u6b63\u5728\u6536\u62fe\u884c\u674e\u3002\u6842\u534e\u751f \u95ee\u4ed6\u4f55\u4e8b\uff0c\u5df4\u52d2\u8bf4\uff1a\u201c\u52c3\u63d0\u5c97\u57ce\u739b\u6e05\u52d2\u5bfa\u7684\u4e3b\u6301\u8bf7\u6211\u53bb\u533b\u75c5\uff0c\u6211\u4e0d\u80fd\u4e0d\u53bb\uff0c\u770b\u6765\u54b1\u4eec\u5927\u7ea6\u8981 \u5206\u624b\u4e00\u4e2a\u6708\u4e86\u3002\u201d \u3000\u3000\u539f\u6765\u5c3c\u6cca\u5c14\u4ee5\u4f5b\u6559\u7acb\u56fd\uff0c\u9664\u4e86\u56fd\u90fd\u7684\u795e\u5e99\u4e4b\u5916\uff0c\u4fbf\u4ee5\u52c3\u57ce\u7684\u739b\u6e05\u52d2\u5bfa\u6700\u5927\uff0c\u4e3b\u6301\u7684\u5730\u4f4d \u6781\u9ad8\u3002\u5173\u4e8e\u739b\u6e05\u52d2\u5bfa\u4e5f\u6709\u4e00\u6bb5\u7740\u540d\u7684\u795e\u8bdd\uff0c\u636e\u4f20\u53e4\u5370\u5ea6\u6709\u4e00\u4f4d\u5723\u4eba\u53eb\u739b\u6e05\u52d2\uff0c\u5176\u5f92\u54e5\u62c9\u62ff\u6709 \u4e00\u6b21\u5230\u5c3c\u6cca\u5c14\u8bbf\u95ee\uff0c\u6ca1\u6709\u5f97\u5230\u5c3c\u6cca\u5c14\u4eba\u7684\u6b22\u8fce\uff0c\u4e00\u6012\u4e4b\u4e0b\uff0c\u5c31\u8dd1\u5230\u4e00\u5ea7\u5c71\u4e0a\uff0c\u5367\u7740\u4e0d\u52a8\uff0c\u8fd9 \u4e00\u6765\u5929\u5c31\u4e0d\u4e0b\u96e8\u4e86\u3002\u56fd\u738b\u53ea\u597d\u5230\u5370\u5ea6\u8bf7\u6c42\u739b\u6e05\u52d2\uff0c\u529d\u52a8\u4e86\u6b4c\u62c9\u62ff\u7ad9\u8d77\u6765\uff0c\u4ed6\u811a\u4e00\u7740\u5730\uff0c\u5929\u5c31 \u4e0b\u8d77\u5927\u96e8\u6765\u4e86\uff0c\u4f7f\u5c3c\u6cca\u5c14\u5f97\u514d\u65f1\u707e\u3002\u56fd\u738b\u4e8e\u662f\u4e0b\u4ee4\u5168\u56fd\uff0c\u6bcf\u5e74\u4e3e\u884c\u8282\u65e5\uff0c\u8868\u793a\u5bf9\u739b\u6e05\u52d2\u7684\u611f \u8c22\uff0c\u8fd9\u4e2a\u8282\u65e5\u4fbf\u53eb\u505a\u201c\u739b\u6e05\u52d2\u906e\u62c9\u8282\u201d\uff0c\u8fd9\u4e2a\u8282\u65e5\u76f4\u5230\u73b0\u5728\uff0c\u6bcf\u5e74\u8fd8\u6709\u4e3e\u884c\uff0c\u6210\u4e3a\u5c3c\u6cca\u5c14\u4e24 \u5927\u8282\u65e5\u4e4b\u4e00\u3002(\u53e6\u4e00\u4e2a\u5927\u8282\u65e5\u662f\u201c\u71c3\u706f\u8282\u201d\uff0c\u76f8\u5f53\u4e8e\u4e2d\u56fd\u7684\u5143\u5bb5\u3002\u6bcf\u9022\u6b64\u65e5\u5bb6\u5bb6\u6237\u6237\u5f20\u706f\u7ed3 \u5f69\uff0c\u51c6\u5907\u4e30\u5bcc\u7684\u98df\u7269\uff0c\u6b3e\u5f85\u4eb2\u53cb\uff0c\u5bfa\u5e99\u91cc\u6574\u591c\u6b4c\u5531\uff0c\u9752\u5e74\u7537\u5973\u5c3d\u60c5\u6b4c\u821e\uff0c\u4e92\u76f8\u62dc\u8bbf\uff0c\u70ed\u95f9\u975e \u5e38\u3002)\u56fd\u738b\u9664\u4e86\u8d70\u4e86\u201c\u739b\u6e05\u52d2\u906e\u62c9\u8282\u201d\u4e4b\u5916\uff0c\u5e76\u5728\u52d2\u63d0\u5c97\u57ce(Bhatguon)\u54e5\u62c9\u62ff\u6240\u5367\u7684\u5c71\u4e0a\uff0c \u5efa\u7acb\u4e86\u4e00\u5ea7\u5b8f\u4f1f\u7684\u201c\u739b\u6e05\u52d2\u5bfa\u201d\uff0c\u4f5c\u4e3a\u7eaa\u5ff5\u3002 \u3000\u3000\u6842\u534e\u751f\u7b11\u800c\u95ee\u9053\uff1a\u201c\u4f60\u4e00\u4e2a\u6708\u4e4b\u540e\uff0c\u51c6\u80fd\u56de\u6765\u5417\uff1f\u201d\u5df4\u52d2\u9053\uff1a\u201c\u6765\u56de\u5404\u5341\u5929\uff0c\u7ecf\u6211\u533b\u7684 \u75db\uff0c\u4ece\u6ca1\u6709\u5341\u5929\u8fd8\u672b\u89c1\u597d\u7684\u3002\u5927\u7ea6\u4e00\u4e2a\u6708\u603b\u5dee\u4e0d\u591a\u4e86\u3002\u201d\u6842\u534e\u751f\u4e00\u7b97\u8ddd\u79bb\u9009\u62d4\u9a79\u9a6c\u4e4b\u671f\uff0c\u6574 \u6574\u8fd8\u6709\u4e94\u5341\u5929\uff0c\u4fbf\u9053\uff1a\u201c\u6211\u542c\u8bf4\u52c3\u57ce\u9644\u8fd1\u4e00\u5e26\uff0c\u98ce\u666f\u6781\u4f73\uff0c\u6211\u968f\u8001\u4e08\u8d70\u4e00\u8d9f\u5427\u3002\u201d\u5df4\u52d2\u5f97\u4ed6 \u540c\u884c\uff0c\u81ea\u662f\u6b22\u559c\uff0c\u4e8e\u662f\u7b2c\u4e8c\u65e5\u4e24\u4eba\u4fbf\u4e00\u540c\u5f80\u52c3\u63d0\u5c97\u57ce\u3002\u6842\u534e\u751f\u600e\u6837\u4e5f\u6599\u60f3\u4e0d\u5230\uff0c\u8fd9\u4e00\u53bb\u4e5f\uff0c \u4ed6\u51e0\u4e4e\u4e0d\u80fd\u56de\u6765\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u9b44\u5bd2\u5149\u5251\u300b\u2014\u2014\u7b2c\u5341\u7ae0\u3000\u5e7d\u8c37\u5bd2\u6cc9\u56f0\u8c6a\u6770 \u6881\u7fbd\u751f\u300a\u51b0\u9b44\u5bd2\u5149\u5251\u300b \u7b2c\u5341\u7ae0\u3000\u5e7d\u8c37\u5bd2\u6cc9\u56f0\u8c6a\u6770 \u3000\u3000\u4ece\u52a0\u5fb7\u6ee1\u90fd\u5230\u52c3\u63d0\u5c97\u57ce\u5927\u7ea6\u6709\u4e94\u767e\u591a\u91cc\uff0c\u4ee5\u5341\u65e5\u4e3a\u671f\uff0c\u6bcf\u65e5\u4e0d\u8fc7\u884c\u4e94\u516d\u5341\u91cc\uff0c\u4f46\u56e0\u4e3a\u662f \u9ad8\u539f\u5730\u5e26\uff0c\u5df4\u52d2\u867d\u7136\u7cbe\u795e\u5065\u9532\uff0c\u5230\u5e95\u662f\u4e0a\u4e86\u5e74\u7eaa\u7684\u4eba\uff0c\u8d70\u4e00\u7a0b\u6b47\u4e00\u91cc\u7684\uff0c\u6bcf\u5929\u8d70\u4e94\u516d\u5341\u91cc\uff0c \u4e5f\u8981\u4ece\u65e5\u51fa\u8d70\u81f3\u65e5\u843d\uff0c\u6842\u534e\u751f\u6b63\u597d\u8d81\u6b64\u6b23\u8d4f\u6cbf\u9014\u98ce\u666f\u3002 \u3000\u3000\u8d70\u5230\u4e86\u7b2c\u4e03\u5929\uff0c\u5ffd\u89c1\u524d\u9762\u65e0\u8def\u53ef\u901a\uff0c\u53ea\u5728\u4e24\u5cf0\u4e4b\u95f4\u6a2a\u67b6\u7740\u4e00\u9053\u94c1\u7d22\u6865\uff0c\u98ce\u5439\u94c1\u7d22\uff0c\u6447\u6643 \u4f5c\u54cd\uff0c\u4e0b\u9762\u662f\u6df1\u4e0d\u53ef\u6d4b\u7684\u5e7d\u8c37\uff0c\u8fd8\u6709\u4e00\u9053\u5927\u7011\u5e03\u4ece\u5c71\u5cf0\u4e0a\u51b2\u4e0b\u6765\uff0c\u98de\u73e0\u6e85\u7389\uff0c\u6c34\u6c14\u7ecf\u8fc7\u9633\u5149 \u84b8\u53d1\uff0c\u5c71\u8c37\u4e2d\u597d\u50cf\u5e03\u4e86\u4e00\u5c42\u96fe\u5e55\uff0c\u98ce\u666f\u5947\u7f8e\uff0c\u4f46\u770b\u770b\u5374\u4e0d\u7531\u5f97\u5fc3\u5934\u5fae\u98a4\u3002 \u3000\u3000\u6842\u534e\u751f\u9053\uff1a\u201c\u8001\u4e08\uff0c\u6211\u80cc\u4f60\u8fc7\u53bb\u5427\u3002\u201d\u5df4\u52d2\u9053\uff1a\u201c\u4e0d\u5fc5\uff0c\u6211\u4eec\u56fd\u4e2d\uff0c\u50cf\u8fd9\u79cd\u94c1\u7d22\u6865\u5230\u5904 \u90fd\u6709\uff0c\u6bd4\u8fd9\u6761\u957f\u5341\u51e0\u5eff\u500d\u7684\u90fd\u6709\u5462\u3002\u6211\u4eec\u90fd\u4e60\u60ef\u4e86\uff0c\u4f60\u4e0d\u5fc5\u4e3a\u6211\u62c5\u5fc3\u3002\u4f60\u5148\u8fc7\u53bb\u5427\uff0c\u6211\u6b47\u6b47 \u5c31\u6765\u3002\u201d \u3000\u3000\u6842\u534e\u751f\u542c\u4ed6\u8fd9\u6837\u8bf4\uff0c\u4e00\u4e2a\u4fbf\u5148\u4e0a\u4e86\u94c1\u7d22\u6865\uff0c\u4ed6\u8f7b\u529f\u7edd\u9876\uff0c\u5c65\u9669\u5982\u5937\uff0c\u4e0d\u6599\u8d70\u81f3\u4e2d\u9014\uff0c\u94c1 \u7d22\u5ffd\u7136\u5927\u5927\u7684\u9707\u52a8\uff0c\u6842\u534e\u751f\u53d1\u89c9\u6709\u5f02\uff0c\u5b9a\u775b\u4e00\u770b\uff0c\u53ea\u89c1\u5728\u6865\u7684\u90a3\u8fb9\uff0c\u4e00\u4e2a\u8eab\u6750\u9ad8\u5927\u7684\u9ed1\u8138\u50e7 \u4eba\uff0c\u6b63\u5728\u624b\u6267\u94c1\u7d22\uff0c\u7528\u529b\u731b\u6447\u3002\u6842\u534e\u751f\u559d\u9053\uff1a\u201c\u5927\u548c\u5c1a\uff0c\u4f60\u5e72\u4ec0\u4e48\uff1f\u201d\u90a3\u9ed1\u8138\u50e7\u4eba\u4e0d\u7406\u4e0d \u776c\uff0c\u7528\u529b\u4e00\u6309\uff0c\u94c1\u7d22\u4e0b\u6c88\uff0c\u968f\u5373\u653e\u624b\uff0c\u94c1\u7d22\u8e66\u7684\u5f39\u8d77\uff0c\u6842\u534e\u751f\u5927\u6012\uff0c\u559d\u9053\uff1a\u201c\u6211\u4e0e\u4f60\u7d20\u4e0d\u76f8 \u8bc6\uff0c\u4f60\u5374\u6765\u8c0b\u5bb3\u6211\uff01\u201d\u811a\u5c16\u4e00\u70b9\uff0c\u98de\u8eab\u63a0\u8d77\uff0c\u843d\u4e0b\u4e4b\u65f6\uff0c\u89d1\u51c6\u94c1\u7d22\uff0c\u8f7b\u8f7b\u4e00\u70b9\uff0c\u590d\u53c8\u817e\u8eab\u98de \u8d77\uff0c\u8fd9\u662f\u4e0a\u4e58\u8f7b\u529f\u4e2d\u7684\u201c\u9756\u8712\u70b9\u6c34\u201d\u4e4b\u6280\uff0c\u6842\u534e\u751f\u8d77\u843d\u51e0\u6b21\uff0c\u770b\u770b\u5c31\u8981\u98de\u62e3\u5230\u5bf9\u9762\u5c71\u5cf0\uff0c\u90a3 \u9ed1\u9762\u602a\u50e7\u5ffd\u5730\u54c8\u54c8\u5927\u7b11\uff0c\u51cc\u7a7a\u98de\u8d77\uff0c\u5411\u6842\u534e\u751f\u649e\u6765\uff0c\u6842\u534e\u751f\u8eab\u5b50\u60ac\u7a7a\uff0c\u6025\u5fd9\u7528\u5de6\u811a\u5728\u53f3\u811a\u7684 \u811a\u80cc\u4e0a\u4e00\u8e29\uff0c\u501f\u52bf\u8f6c\u8eab\uff0c\u53cc\u638c\u594b\u529b\u4e00\u63a8\uff0c\u5927\u559d\u4e00\u58f0\uff1a\u201c\u4e0b\u53bb\u201d\u90a3\u77e5\u8fd9\u9ed1\u8138\u602a\u50e7\u7684\u638c\u529b\u96c4\u6d51\u975e \u5e38\uff0c\u7edd\u4e0d\u5728\u4ed6\u4e4b\u4e0b\uff0c\u56db\u638c\u76f8\u4ea4\uff0c\u83b2\u84ec\u96e8\u58f0\uff0c\u6842\u534e\u751f\u7684\u8eab\u5b50\u7adf\u4f3c\u6d41\u661f\u6ba1\u77f3\u4e00\u822c\u5760\u4e0b\u6df1\u8c37\uff0c\u8033\u8fb9 \u542c\u5f97\u8f70\u5982\u96f7\u9e23\u7684\u7011\u5e03\u58f0\u97f3\uff0c\u6842\u534e\u751f\u6697\u53eb\uff1a\u201c\u4e0d\u5999\uff01\u201d\u5371\u6025\u4e2d\u4e00\u4e2a\u7b4b\u6597\u5012\u7ffb\uff0c\u5934\u4e0b\u811a\u4e0a\uff0c\u6b63\u60f3 \u627e\u5bfb\u843d\u8db3\u4e4b\u70b9\uff0c\u4e91\u6c14\u5f25\u6f2b\uff0c\u90a3\u91cc\u770b\u5f97\u6e05\u695a\uff1f\u9661\u7136\u95f4\u53c8\u89c9\u4e00\u80a1\u5927\u529b\u63a8\u6765\uff0c\u6842\u534e\u751f\u8eab\u4e0d\u7531\u5df1\u7684\u843d \u5728\u7011\u5e03\u4e4b\u4e2d\uff0c\u7eb5\u662f\u7edd\u4e16\u82f1\u96c4\uff0c\u4ea6\u96be\u62b5\u6321\u90a3\u6025\u6d41\u51b2\u51fb\u4e4b\u529b\u3002\u6842\u534e\u751f\u6025\u5fd9\u5f00\u4e86\u547c\u5438\uff0c\u8fc7\u4e86\u7247\u523b\uff0c \u6655\u7729\u4e4b\u4e2d\u5ffd\u89c9\u538b\u529b\u4e00\u8f7b\uff0c\u597d\u50cf\u8eab\u5df2\u5230\u4e86\u5b9e\u5730\u3002 \u3000\u3000\u6842\u534e\u751f\u8fd0\u6c14\u4e00\u8f6c\uff0c\u547c\u51fa\u4e86\u4e00\u53e3\u6c14\uff0c\u98de\u8dc3\u800c\u8d77\uff0c\u7741\u773c\u4e00\u77a7\uff0c\u4f46\u89c1\u773c\u524d\u53e6\u6709\u6d1e\u7a74\uff0c\u7eff\u8349\u5982 \u8335\uff0c\u6742\u82b1\u751f\u6811\uff0c\u56de\u5934\u4e00\u770b\uff0c\u90a3\u7011\u5e03\u597d\u50cf\u4e00\u5e45\u7855\u5927\u65e0\u670b\u7684\u6c34\u6302\u5728\u9762\u524d\uff0c\u539f\u6765\u6842\u534e\u751f\u7adf\u88ab\u7011\u5e03\u51b2 \u5230\u4e00\u5904\u65e0\u8def\u53ef\u901a\u7684\u5e7d\u8c37\uff01 \u3000\u3000\u6842\u534e\u751f\u5168\u8eab\u6e7f\u900f\uff0c\u6b63\u5728\u76d8\u7b97\u5982\u4f55\u51fa\u53bb\uff0c\u5ffd\u542c\u5f97\u4fa7\u9762\u6709\u4eba\u54c8\u54c8\u5927\u7b11\uff0c\u6842\u534e\u751f\u4e00\u671b\uff0c\u53ef\u4e0d\u6b63 \u662f\u90a3\u4e2a\u9ed1\u9762\u602a\u618e\uff0c\u4f46\u89c1\u4ed6\u4e5f\u50cf\u843d\u6c64\u9e21\u4e00\u6837\uff0c\u60f3\u5fc5\u4e5f\u662f\u88ab\u7011\u5e03\u51b2\u8fdb\u6765\u7684\u3002\u6842\u534e\u751f\u6012\u9053\uff1a\u201c\u4f60\u662f \u4f55\u4eba\uff0c\u4e3a\u4f55\u52a0\u5bb3\u4e8e\u6211\uff1f\u201d\u90a3\u9ed1\u9762\u602a\u50e7\u597d\u50cf\u542c\u4e0d\u61c2\u4ed6\u8bf4\u7684\u5c3c\u6cca\u5c14\u8bdd\u3002\u53d1\u58f0\u602a\u7b11\uff0c\u77f6\u7391\u5495\u5495\u7684\u9a82 \u4e86\u4e00\u987f\uff0c\u6842\u534e\u751f\u548c\u96c5\u5fb7\u661f\u738b\u5b50\u76f8\u5904\u591a\u65f6\uff0c\u5bf9\u5370\u5ea6\u8bdd\u7565\u61c2\u51e0\u65ec\uff0c\u5374\u4e5f\u542c\u4e0d\u61c2\u4ed6\u9a82\u4e9b\u4ec0\u4e48\uff0c\u53ea\u662f \u542c\u4ed6\u8bf4\u8bdd\u4e2d\u4e0d\u65f6\u53d1\u51fa\u201c\u96c5\u5fb7\u661f\u201d\u8fd9\u4e09\u4e2a\u5b57\u7684\u58f0\u97f3\uff0c\u6842\u534e\u751f\u5fc3\u4e2d\u4e00\u52a8\uff0c\u4f38\u51fa\u4e24\u53ea\u6307\u5934\uff0c\u6307\u6307\u81ea \u5df1\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u8bf4\u96c5\u5fb7\u661f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6842\u534e\u751f\u8fd9\u4e00\u624b\u52bf\u7684\u610f\u601d\uff0c\u8868\u793a\u4ed6\u548c\u96c5\u5fb7\u661f\u4e43\u662f\u670b\u53cb\uff0c\u90a3\u9ed1\u8138\u602a\u50e7\u201c\u54fc\u201d\u7684\u4e00\u58f0\uff0c\u6447\u4e86\u6447 \u5934\uff0c\u610f\u6b8a\u4e0d\u4fe1\uff0c\u6842\u534e\u751f\u4f5c\u4e86\u4e00\u4e2a\u4ece\u5730\u4e0a\u5c06\u4eba\u6276\u8d77\u4e4b\u52bf\uff0c\u53eb\u9053\uff1a\u201c\u96c5\u5fb7\u661f\u662f\u6211\u6551\u7684\uff0c\u4f60\u77e5\u4e0d\u77e5 \u9053\uff1f\u201d\u90a3\u9ed1\u9762\u602a\u50e7\u542c\u4e0d\u61c2\u5b83\u7684\u5c3c\u6cca\u5c14\u8bdd\uff0c\u4f46\u201c\u96c5\u5fb7\u661f\u201d\u8fd9\u4e09\u4e2a\u5b57\u97f3\u6842\u534e\u751f\u662f\u7528\u5370\u5ea6\u97f3\u5ff5\u7684\uff0c \u4ed6\u89c2\u79e4\u5bdf\u8272\uff0c\u518d\u770b\u624b\u52bf\uff0c\u731c\u51fa\u4e86\u8bdd\u4e2d\u7684\u542b\u610f\uff0c\u6014\u4e86\u4e00\u6014\uff0c\u5ffd\u5730\u9762\u8272\u4e00\u53d8\uff0c\u4f5c\u4e86\u4e00\u4e2a\u63a8\u5f00\u4e4b \u52bf\uff0c\u5495\u5495\u565c\u565c\u7ea6\u53c9\u9a82\u4e86\u51e0\u53e5\uff0c\u6842\u534e\u751f\u53ea\u542c\u5f97\u51fa\u201c\u9a97\u4eba\u201d\u3001\u201c\u4e0d\u8bb8\u4f60\u51fa\u53bb\u201d\u7b49\u4e09\u4e24\u53e5\u65ad\u65ad\u7eed\u7eed \u7684\u5b57\u53e5\u3002 \u3000\u3000\u6842\u534e\u751f\u7591\u4e91\u5927\u8d77\uff0c\u5c0f\u9053\uff1a\u201c\u83ab\u975e\u662f\u96c5\u5fb7\u661f\u906d\u4e86\u4e0d\u6d4b\u4e4b\u7978\uff0c\u5374\u600e\u7684\u8d56\u5230\u6211\u7684\u5934\u4e0a\uff1f\u201d\u770b\u795e \u60c5\u4f3c\u4e4e\u8fd9\u9ed1\u9762\u602a\u50e7\u662f\u6307\u4ed6\u5bb3\u4e86\u96c5\u5fb7\u661f\uff0c\u53cc\u65b9\u8a00\u8bed\u4e0d\u901a\uff0c\u6842\u534e\u751f\u65e0\u6cd5\u5206\u8fa9\uff0c\u5c0f\u9053\uff1a\u201c\u6211\u53ea\u6709\u8131 \u6b64\u9669\u5883\uff0c\u5e2e\u4ed6\u5230\u738b\u5bab\u53bb\uff0c\u8fd9\u624d\u80fd\u8bf4\u5f97\u6e05\u695a\u3002\u201d\u4e3b\u610f\u6253\u5b9a\uff0c\u5f80\u6d1e\u53e3\u4fbf\u51b2\uff0c\u90a3\u9ed1\u9762\u602a\u50e7\u5ffd\u5730\u5927\u559d \u4e00\u58f0\uff1a\u201c\u4e0d\u51c6\u51fa\u53bb\uff01\u201d\u53cc\u638c\u4e00\u5806\uff0c\u6842\u534e\u751f\u8e48\u8e48\u8ddf\u8ddf\u7684\u5012\u9000\u51e0\u6b65\uff0c\u9669\u4e9b\u8dcc\u5012\u3002\u672c\u6765\u82e5\u8bba\u529f\u529b\uff0c \u4ed6\u4e0e\u8fd9\u9ed1\u8138\u602a\u50e7\u4e43\u662f\u4f2f\u4ef2\u4e4b\u95f4\uff0c\u4e0d\u8fc7\u4ed6\u5728\u8dcc\u4e0b\u4e4b\u65f6\uff0c\u5148\u88ab\u638c\u529b\u4e00\u9707\uff0c\u518d\u88ab\u7011\u5e03\u4e00\u51b2\uff0c\u5c1a\u672a\u6062 \u590d\u8fc7\u6765\uff0c\u662f\u4ee5\u76f8\u5f62\u89c1\u62d9\u3002 \u3000\u3000\u6842\u534e\u751f\u6012\u9053\uff1a\u201c\u6211\u975e\u51fa\u53bb\u4e0d\u53ef\uff01\u201d\u4e00\u6478\u8170\u95f4\uff0c\u4ed6\u7684\u817e\u86df\u5b9d\u5251\u65e2\u662f\u767e\u94a2\uff0c\u53c8\u53ef\u5316\u4f5c\u7ed5\u6307 \u67d4\uff0c\u5e73\u65f6\u4e0d\u7528\u4e4b\u65f6\uff0c\u4fbf\u56f4\u5728\u8170\u95f4\uff0c\u5f53\u4f5c\u8170\u5e26\uff0c\u5e78\u800c\u6ca1\u6709\u88ab\u7011\u5e03\u51b2\u8d70\uff0c\u6842\u534e\u751f\u89e3\u4e0b\u5b9d\u5251\uff0c\u8fce\u98ce \u4e00\u6296\uff0c\u540c\u90a3\u9ed1\u9762\u602a\u618e\u4e00\u6307\uff0c\u559d\u9053\uff1a\u201c\u95ea\u5f00\uff01\u201d \u3000\u3000\u90a3\u9ed1\u9762\u602a\u4eba\u5140\u7acb\u4e0d\u52a8\uff0c\u8bf4\u7684\u4ecd\u7136\u662f\u6842\u534e\u751f\u542c\u5f97\u61c2\u7684\u90a3\u4e00\u53e5\u8bdd\uff1a\u201c\u4e0d\u8bb8\u51fa\u53bb\uff01\u201d\u4f73\u534e\u751f\u6ca1 \u6cd5\uff0c\u5b9d\u5251\u76d8\u7a7a\u4e00\u821e\uff0c\u5251\u5149\u66b4\u957f\uff0c\u5c0f\u9053\uff1a\u201c\u770b\u4f60\u8ba9\u4e0d\u8ba9\u5f00\uff1f\u201d\u90a3\u77e5\u4ed6\u521a\u521a\u821e\u8d77\u4e00\u6735\u5251\u82b1\uff0c\u53ea\u89c1 \u90a3\u9ed1\u9762\u50e7\u4eba\u4e5f\u62d4\u51fa\u4e00\u628a\u602a\u5200\uff0c\u9ed1\u9edc\u9edc\u7684\u5e76\u65e0\u5200\u950b\uff0c\u5200\u8eab\u5fae\u5f2f\uff0c\u5200\u67c4\u4e0a\u5374\u7f00\u4ee5\u5b9d\u77f3\uff0c\u95ea\u95ea\u53d1 \u5149\uff0c\u6842\u534e\u751f\u90a3\u4e00\u62db\u201c\u96ea\u82b1\u76d6\u9876\u201d\uff0c\u672c\u662f\u62a4\u8eab\u7684\u5251\u6cd5\uff0c\u53ea\u56fe\u5916\u95ef\uff0c\u65e0\u610f\u4f24\u4eba\uff0c\u8fd9\u50e7\u4eba\u5374\u90a3\u91cc\u77e5 \u9053\uff0c\u89c1\u4ed6\u5251\u5149\u4e00\u8d77\uff0c\u7acb\u5373\u4e00\u5200\u780d\u4e0b\u3002 \u3000\u3000\u53ea\u542c\u5f97\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u706b\u82b1\u98de\u6e85\uff0c\u6842\u534e\u751f\u4f46\u89c9\u864e\u53e3\u75bc\u75db\uff0c\u5b9d\u5251\u4e5f\u51e0\u4e4e\u628a\u63e1\u4e0d\u4f4f\uff0c\u5927\u5403\u4e00 \u60ca\uff0c\u8fd9\u628a\u5200\u6beb\u4e0d\u62a2\u773c\uff0c\u60f3\u4e0d\u5230\u7adf\u662f\u6c89\u91cd\u5982\u65af\uff01\u4f46\u89c1\u90a3\u9ed1\u9762\u50e7\u4eba\u5df2\u662f\u5931\u58f0\u60ca\u547c\uff0c\u8fde\u8fd4\u51e0\u6b65\uff0c\u539f \u6765\u4ed6\u8fd9\u628a\u5200\u662f\u7528\u4e0a\u597d\u7684\u94bb\u94c1\uff0c\u52a0\u8fdb\u4e86\u5341\u51e0\u79cd\u7a00\u6709\u91d1\u5c5e\u6240\u6210\u7684\u5b9d\u5200\uff0c\u8db3\u6709\u4e03\u5341\u4e8c\u65a4\u4e4b\u91cd\uff0c\u5374\u88ab \u6842\u534e\u751f\u7684\u817e\u86df\u5b9d\u5251\u524a\u4e86\u4e00\u4e2a\u7f3a\u53e3\uff0c\u4ea6\u5448\u51fa\u4ed6\u610f\u6599\u4e4b\u5916\u3002 \u3000\u3000\u6842\u534e\u751f\u89c1\u72b6\u5927\u559c\uff0c\u5fc3\u77e5\u4ed6\u7684\u5b9d\u5200\u867d\u602a\uff0c\u81ea\u5df1\u7684\u5b9d\u5251\u8fd8\u53ef\u4ee5\u514b\u5236\u5f97\u4f4f\uff0c\u7acb\u5373\u4e00\u4e2a\u7bad\u6b65\u8df3 \u4e0a\uff0c\u4e00\u62db\u201c\u731b\u864e\u593a\u8def\u201d\uff0c\u5b9d\u5251\u5e73\u63a8\uff0c\u53c8\u5f80\u5916\u95ef\u3002\u90a3\u9ed1\u9762\u50e7\u4eba\u52c3\u7136\u5927\u6012\uff0c\u8eab\u5f62\u4e00\u504f\uff0c\u53c8\u62d4\u51fa\u4e00 \u67c4\u62c2\u5c18\uff0c\u5de6\u624b\u6301\u5200\uff0c\u53f3\u624b\u6301\u62c2\u5c18\uff0c\u547c\u7684\u4e00\u58f0\uff0c\u62c2\u5c18\u5148\u843d\uff0c\u8fce\u9762\u626b\u6765\uff01 \u3000\u3000\u6842\u534e\u751f\u662f\u6b66\u5b66\u7684\u5927\u884c\u5bb6\uff0c\u89c1\u4ed6\u8f7b\u8f7b\u4e00\u62c2\uff0c\u5c18\u5c3e\u7adf\u662f\u805a\u800c\u4e0d\u6563\uff0c\u5f62\u5982\u94c1\u7b14\uff0c\u547c\u547c\u631f\u98ce\uff0c\u4fbf \u77e5\u8fd9\u4e00\u62c2\u4e4b\u4e0b\uff0c\u5b9e\u662f\u85cf\u6709\u6781\u5f3a\u7684\u6f5c\u529b\uff0c\u7136\u800c\u6301\u6709\u5b9d\u5251\uff0c\u4e5f\u5e76\u4e0d\u600e\u6837\u5728\u610f\uff0c\u817e\u86df\u5b9d\u5251\u626c\u7a7a\u4e00 \u5c55\uff0c\u5316\u6210\u4e86\u4e00\u9053\u94f6\u8679\uff0c\u4fbf\u8981\u786c\u628a\u5b83\u7684\u94c1\u62c2\u5c18\u524a\u65ad\uff0c\u90a3\u77e5\u5c31\u5728\u5373\u5c06\u63a5\u89e6\u4e4b\u9645\uff0c\u5c18\u5c3e\u5ffd\u5730\u6563\u5f00\uff0c \u6839\u6839\u5982\u523a\u3001\u4e07\u7f15\u5343\u4e1d\u7684\u5c18\u5c3e\uff0c\u597d\u50cf\u53d8\u6210\u4e86\u65e0\u6570\u5229\u9488\uff0c\u658b\u523a\u6842\u534e\u751f\u7684\u6d51\u8eab\u7a74\u9053\u53e3\u6842\u534e\u751f\u7a81\u7136\u78b0 \u5230\u8fd9\u4e00\u602a\u62db\uff0c\u5403\u60ca\u975e\u5c0f\uff0c\u5e78\u800c\u4ed6\u529f\u529b\u6df1\u539a\uff0c\u673a\u8b66\u8fc7\u4eba\uff0c\u4e00\u89c1\u4e0d\u5999\uff0c\u77ac\u606f\u4e4b\u95f4\uff0c\u5df2\u662f\u8fd0\u6c14\u5bf9\u4e86 \u5168\u8eab\u7684\u5927\u7a74\uff0c\u7acb\u5373\u7528\u201c\u98ce\u53f0\u843d\u82b1\u201d\u4e4b\u5f0f\uff0c\u4e00\u98d8\u4e00\u95ea\uff0c\u8131\u51fa\u4e86\u90a3\u62c2\u5c18\u7b3c\u7f69\u8303\u56f4\uff0c\u53cd\u624b\u4e00\u5251\uff0c\u53c8 \u6321\u5f00\u4e86\u90a3\u628a\u602a\u5200\u7684\u4e00\u51fb\u3002\u8fd9\u51e0\u62db\u8fc7\u5982\u7535\u5149\u77f3\u706b\uff0c\u4f46\u89c1\u5251\u5149\u8fc7\u5904\uff0c\u5c18\u5c3e\u88ab\u524a\u4e86\u4e00\u64ae\uff0c\u90a3\u9ed1\u9762\u50e7 \u4eba\u8fde\u58f0\u6012\u543c\uff0c\u53c8\u8ffd\u4e0a\u6765\uff01 \u3000\u3000\u6842\u534e\u751f\u867d\u7136\u524a\u65ad\u4e86\u4ed6\u4e00\u64ae\u62c2\u5c18\uff0c\u8eab\u4e0a\u4e5f\u6709\u4e03\u5904\u7a74\u9053\u7ed9\u4ed6\u523a\u4e2d\uff0c\u867d\u7136\u5c01\u6709\u7a74\u9053\uff0c\u4ea6\u5df2\u76ae\u7834 \u8840\u6d41\uff0c\u5fc3\u4e2d\u9887\u4e3a\u60ca\u9a87\uff0c\u60f3\u9053\uff1a\u8fd9\u9ed1\u9762\u50e7\u4eba\u4e0d\u77e5\u662f\u4ec0\u4e48\u8def\u9053\uff0c\u4e0d\u4f46\u529f\u529b\u6df1\u539a\uff0c\u800c\u4e14\u5175\u5668\u4e4b\u602a\uff0c \u5c42\u51fa\u4e0d\u7a77\uff0c\u770b\u6765\u5e76\u4e0d\u89c1\u5f97\u51fa\u63d0\u6469\u8fbe\u591a\u5dee\u4e86\u591a\u5c11\u3002 \u3000\u3000\u53ea\u89c1\u90a3\u5370\u5ea6\u5f02\u618e\u6325\u5200\u821e\u62c2\uff0c\u6012\u543c\u8ffd\u6765\uff0c\u6842\u534e\u751f\u6478\u4e0d\u900f\u4ed6\u7684\u8def\u6570\uff0c\u597d\u5b88\u800c\u4e0d\u653b\uff0c\u4e24\u4eba\u529f\u529b \u5728\u4f2f\u4ef2\u4e4b\u95f4\uff0c\u6240\u7528\u7684\u5175\u5668\uff0c\u4ea6\u90fd\u662f\u4e16\u4e0a\u7f55\u89c1\u7684\u5b9d\u7269\uff0c\u5f53\u771f\u662f\u65d7\u9f13\u76f8\u5f53\uff0c\u90a3\u50e7\u4eba\u653b\u4e0d\u8fdb\u6765\uff0c\u6842 \u534e\u751f\u4e5f\u95ef\u4e0d\u51fa\u53bb\uff0c\u76f4\u6253\u5230\u53cc\u65b9\u90fd\u7cbe\u75b2\u529b\u7aed\uff0c\u8c37\u5e95\u4e0d\u89c1\u65e5\u5f71\uff0c\u66ae\u8272\u6c89\u6c89\u4e4b\u9645\uff0c\u624d\u5404\u81ea\u7f62\u624b\u3002 \u3000\u3000\u90a3\u5370\u5ea6\u5f02\u50e7\u5c31\u76d8\u819d\u5750\u5728\u6c34\u6d1e\u53e3\uff0c\u4ecd\u7136\u662f\u90a3\u53e5\u6842\u534e\u751f\u542c\u5f97\u61c2\u7684\u5370\u5ea6\u8bdd\uff1a\u201c\u4e0d\u8bb8\u51fa\u53bb\uff01\u201d \u3000\u3000\u6842\u534e\u751f\u4e0d\u7406\u4f1a\u4ed6\uff0c\u81ea\u5411\u5e7d\u8c37\u6df1\u5904\u8d70\u53bb\uff0c\u5728\u66ae\u853c\u82cd\u832b\u4e4b\u4e2d\uff0c\u4f46\u89c1\u5230\u5904\u90fd\u662f\u5947\u82b1\u5f02\u8349\uff0c\u884c\u4e0d \u591a\u8fdc\uff0c\u8fd8\u53d1\u73b0\u4e86\u4e00\u5904\u6e05\u6cc9\uff0c\u6cc9\u6c34\u78a7\u7eff\uff0c\u51c9\u6c14\u6c81\u4eba\uff0c\u6842\u534e\u751f\u6697\u81ea\u7b11\u9053\uff1a\u201c\u8fd9\u5730\u65b9\u5012\u662f\u4e0d\u9519\uff0c\u8981 \u4e0d\u662f\u4eca\u65e5\u6709\u6b64\u5947\u9047\uff0c\u6211\u600e\u77e5\u5e7d\u8c37\u4e4b\u5185\u7adf\u662f\u522b\u6709\u6d1e\u5929\u3002\u201d\u4ed6\u968f\u8eab\u8fd8\u5e26\u6709\u4e00\u4e9b\u5e72\u7cae\uff0c\u5403\u9971\u4e86\u4e4b \u540e\uff0c\u518d\u8f6f\u4e86\u51e0\u65e5\u6cc9\u6c34\uff0c\u6cc9\u6c34\u6e05\u4f8b\u5fae\u7518\uff0c\u996e\u4e86\u4e4b\u540e\uff0c\u7cbe\u795e\u4e3a\u4e4b\u4e00\u723d\u3002 \u3000\u3000\u8fd9\u4e00\u665a\u6842\u534e\u751f\u76d8\u819d\u9759\u5750\uff0c\u95ed\u76ee\u517b\u795e\uff0c\u4e0d\u6562\u719f\u7761\uff0c\u90a3\u9ed1\u9762\u50e7\u4eba\u4e5f\u4e0d\u6765\u9a9a\u6270\u4ed6\uff0c\u4e00\u591c\u65e0\u4e8b\uff0c \u7b2c\u4e8c\u65e5\u4e00\u65e9\u6842\u534e\u751f\u8d70\u8fd1\u6c34\u6d1e\u53e3\uff0c\u53ea\u89c1\u90a3\u9ed1\u9762\u50e7\u4eba\u4e5f\u6b63\u5728\u76d8\u819d\u9759\u5750\uff0c\u4e00\u542c\u5230\u6842\u534e\u751f\u7684\u811a\u6b65\u58f0\uff0c \u4fbf\u7acb\u5373\u8df3\u8d77\u6765\uff0c\u4ecd\u7136\u662f\u90a3\u4e00\u53e5\u8bdd\uff1a\u201c\u4e0d\u8bb8\u51fa\u53bb\uff01\u201d \u3000\u3000\u6842\u534e\u751f\u5927\u4e3a\u5947\u602a\uff0c\u5fc3\u8bf4\uff1a\u201c\u4e3a\u4ec0\u4e48\u4ed6\u4e0d\u51c6\u6211\u51fa\u53bb\uff1f\u82e5\u8bf4\u4ed6\u6000\u7591\u6211\u5bb3\u4e86\u96c5\u5fb7\u661f\uff0c\u4e5f\u5e94\u8be5\u4e0e \u6211\u62da\u547d\u624d\u662f\u3002\u96be\u9053\u5c06\u6211\u5e7d\u7981\u5728\u8fd9\u8c37\u4e2d\uff0c\u5c31\u662f\u4ed6\u7684\u62a5\u590d\u4e4b\u9053\u5417\uff1f\u201d\u5f53\u771f\u662f\u767e\u601d\u4e0d\u5f97\u5176\u89e3\u3002\u6842\u534e \u751f\u6765\u56de\u6f2b\u6b65\uff0c\u89c1\u4ed6\u5e76\u65e0\u4f24\u5bb3\u4e4b\u610f\uff0c\u9042\u53c8\u56de\u5230\u6e05\u6cc9\u7684\u65c1\u8fb9\uff0c\u5c06\u6700\u540e\u6240\u5269\u4e0b\u7684\u5e72\u7cae\u5403\u4e86\uff0c\u53c8\u559d\u4e86 \u51e0\u65e5\u6cc9\u6c34\uff0c\u60f3\u9053\uff1a\u201c\u5df4\u52d2\u5728\u5916\u9762\u4e0d\u77e5\u5982\u4f55\u7740\u6025\u5462\uff1f\u8fd9\u5370\u5ea6\u548c\u5c1a\u4e0d\u51c6\u6211\u51fa\u53bb\uff0c\u6211\u5374\u662f\u975e\u51fa\u53bb\u4e0d \u53ef\u3002\u201d\u4ed6\u6628\u5929\u548c\u8fd9\u9ed1\u9762\u50e7\u4eba\u6253\u4e86\u534a\u5929\uff0c\u5bf9\u5b83\u7684\u6b66\u529f\u8def\u6570\u5df2\u6478\u5230\u4e86\u51e0\u6210\uff0c\u7ecf\u8fc7\u4e00\u591c\u7b79\u601d\uff0c\u60f3\u8bd5 \u7528\u8fbe\u6469\u5251\u6cd5\u4e2d\u7684\u4e03\u5341\u4e8c\u5f0f\u4f0f\u9b54\u5251\u7834\u4ed6\u3002\u4e8e\u662f\u53c8\u51fa\u53bb\u548c\u4ed6\u6076\u6597\u3002 \u3000\u3000\u8fd9\u4e03\u5341\u4e8c\u5f0f\u4f0f\u9b54\u5251\u5a01\u731b\u65e0\u4f26\uff0c\u6842\u534e\u751f\u4e0e\u4ed6\u6076\u6597\uff0c\u6700\u521d\u534a\u4e2a\u65f6\u8fb0\u679c\u7136\u5360\u4e86\u4e0a\u98ce\uff0c\u6740\u5f97\u4ed6\u8fde \u8fde\u540e\u9000\uff0c\u5feb\u8981\u5c06\u4ed6\u8ffd\u5230\u6c34\u6d1e\u53e3\uff0c\u5374\u53c8\u7ed9\u4ed6\u7a33\u4f4f\uff0c\u6253\u4e86\u534a\u5929\uff0c\u4ecd\u662f\u96be\u5206\u80dc\u8d1f\u3002\u6842\u534e\u751f\u5fc3\u4e2d\u6709\u70b9 \u5947\u602a\uff0c\u60f3\u9053\uff1a\u201c\u6211\u4e0e\u4ed6\u7684\u529f\u529b\uff0c\u6628\u5929\u65e9\u5df2\u8bd5\u5c71\u4e43\u662f\u534a\u65a4\u516b\u4e24\uff0c\u4eca\u5929\u6211\u5728\u5251\u6cd5\u4e0a\u5df2\u5360\u4e86\u4e0a\u98ce\uff0c \u4ed6\u5c45\u7136\u5230\u4e86\u540e\u6765\u80fd\u591f\u5b88\u4f4f\uff0c\u800c\u6211\u53cd\u89c9\u6709\u70b9\u7cbe\u795e\u4e0d\u7ee7\u7684\u6837\u5b50\uff0c\u96be\u9053\u5728\u8fd9\u4e00\u591c\u4e4b\u95f4\uff0c\u4ed6\u7684\u529f\u529b\u52a0 \u6df1\uff0c\u800c\u6211\u7684\u529f\u529b\u5374\u6d88\u9000\u4e86\uff1f\u201d \u3000\u3000\u6253\u5230\u65e5\u5f71\u897f\u659c\uff0c\u6842\u534e\u751f\u53c8\u9965\u53c8\u6e34\uff0c\u53cd\u800c\u7ed9\u90a3\u9ed1\u9762\u50e7\u4eba\u4e00\u6b65\u6b65\u7684\u8feb\u9000\u56de\u6765\uff0c\u53ea\u597d\u7f62\u624b\u4e0d \u6597\uff0c\u53c8\u56de\u5230\u6628\u65e5\u4e4b\u5904\uff0c\u5b83\u7684\u4e7e\u7cae\u5df2\u7aed\uff0c\u5e78\u800c\u6cc9\u4e2d\u6709\u9c7c\uff0c\u6842\u534e\u751f\u4fbf\u6349\u4e86\u51e0\u5c3e\u5c0f\u9c7c\uff0c\u70e7\u719f\u6765\u5403\u3002 \u3000\u3000\u8fd9\u6e05\u6cc9\u4e2d\u5c0f\u9c7c\u751a\u591a\uff0c\u70e7\u719f\u6765\u5403\uff0c\u5473\u751a\u7518\u7f8e\uff0c\u6842\u534e\u751f\u5fc3\u9053\uff1a\u201c\u6211\u5373\u7b97\u88ab\u56f0\u5728\u6b64\u4e00\u5e74\u534a\u8f7d\uff0c \u4e5f\u4e0d\u81f4\u4e8e\u997f\u6b7b\u4e86\u3002\u201d\u7ecf\u8fc7\u4e86\u8fd9\u4e24\u65e5\u7684\u89c2\u5bdf\uff0c\u90a3\u9ed1\u8138\u602a\u50e7\u4f3c\u4e4e\u53ea\u662f\u4e0d\u8bb8\u4ed6\u51fa\u53bb\uff0c\u5012\u5e76\u65e0\u52a0\u5bb3\u4e4b \u610f\u3002\u6842\u534e\u751f\u653e\u5fc3\u7761\u4e86\u4e00\u89c9\uff0c\u7b2c\u4e09\u65e5\u4e00\u65e9\u8d77\u6765\uff0c\u89c9\u5f97\u7cbe\u795e\u751a\u597d\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u6628\u65e5\u6211\u672c\u6765\u53ef\u4ee5 \u6253\u80dc\uff0c\u5374\u7ec8\u4e8e\u88ab\u4ed6\u5b88\u4f4f\uff0c\u60f3\u662f\u524d\u665a\u7761\u5f97\u4e0d\u597d\u4e4b\u6545\u3002\u201d\u4e8e\u662f\u53c8\u51fa\u53bb\u5411\u90a3\u9ed1\u8138\u602a\u50e7\u6311\u6218\uff0c\u6700\u521d\u4e00 \u9f13\u4f5c\u6c14\uff0c\u5c06\u4ed6\u8feb\u5f97\u8fde\u8fde\u540e\u9000\uff0c\u4f46\u8fd8\u6ca1\u6709\u8feb\u81f3\u6d1e\u53e3\uff0c\u5374\u53c8\u88ab\u4ed6\u5b88\u7a33\uff0c\u8fd9\u4e00\u6218\u53ea\u6253\u4e09\u4e2a\u65f6\u8fb0\uff0c\u6842 \u534e\u751f\u4fbf\u6e10\u6e10\u611f\u5230\u6c14\u529b\u4e0d\u652f\uff0c\u53ea\u597d\u7f62\u6218\u3002\u56de\u6765\u4e4b\u540e\uff0c\u8d8a\u60f3\u8d8a\u89c9\u5947\u602a\uff1a\u201c\u4eca\u65e5\u7684\u6210\u7ee9\u8fdc\u6bd4\u4e0d\u4e0a\u6628 \u65e5\uff0c\u6628\u65e5\u4e00\u76f4\u5c06\u4ed6\u8feb\u81f3\u6d1e\u53e3\uff0c\u4ed6\u624d\u626d\u8f6c\u52a3\u52bf\uff0c\u800c\u4e14\u662f\u6076\u6597\u534a\u5929\uff0c\u53cc\u65b9\u529b\u7aed\u8fd9\u624d\u7f62\u6218\u7684\u3002\u4eca\u5929 \u5374\u4e0d\u8fc7\u6253\u4e86\u4e09\u4e2a\u65f6\u8fb0\uff0c\u800c\u4e14\u770b\u6765\u5bf9\u65b9\u4f3c\u4e4e\u6bd4\u81ea\u5df1\u66f4\u80fd\u6301\u4e45\u3002\u201d \u3000\u3000\u81ea\u6b64\u6bcf\u65e5\u6842\u534e\u751f\u90fd\u4e0e\u90a3\u9ed1\u9762\u602a\u50e7\u5927\u6218\u4e00\u573a\uff0c\u6842\u534e\u751f\u5bf9\u6b66\u529f\u7684\u201c\u609f\u6027\u201d\u6781\u9ad8\uff0c\u8fde\u65e5\u82e6\u6597\uff0c \u5bf9\u90a3\u50e7\u4eba\u6240\u7528\u7684\uff0c\u5370\u5ea6\u4f5b\u95e8\u7684\u4e0a\u4e58\u5185\u529f\u53c2\u900f\u4e86\u4e0d\u5c11\uff0c\u5bf9\u4ed6\u90a3\u4e9b\u53e4\u602a\u7684\u62db\u6570\u66f4\u662f\u719f\u6781\u5982\u6d41\uff0c\u89c6 \u4f5c\u7b49\u95f2\uff0c\u53ef\u4ee5\u968f\u4fbf\u5e94\u4ed8\u4e86\u3002\u4ed6\u81ea\u5df1\u4e5f\u89c9\u5f97\u83b7\u76ca\u4e0d\u6d45\uff0c\u8bba\u7406\u5728\u529f\u529b\u4e0a\u867d\u7136\u4e0d\u80fd\u901f\u8fdb\uff0c\u4f46\u6700\u5c11\u4e5f \u4e0d\u4f1a\u51cf\u5f31\uff0c\u800c\u5728\u62db\u6570\u4e0a\u66f4\u5e94\u8be5\u5360\u5f97\u7edd\u5bf9\u4e0a\u98ce\uff0c\u4f46\u8bf4\u4e5f\u5947\u602a\uff0c\u4ed6\u7684\u6210\u7ee9\u7adf\u662f\u4e00\u5929\u4e0d\u5982\u4e00\u5929\uff0c\u5230 \u4e86\u7b2c\u5341\u5929\uff0c\u4ed6\u4e0e\u90a3\u9ed1\u9762\u50e7\u4eba\u53ea\u6253\u4e86\u4e00\u4e2a\u65f6\u8fb0\uff0c\u4fbf\u5df2\u611f\u5230\u5026\u610f\uff0c\u90a3\u65f6\uff0c\u4ed6\u7528\u8fbe\u6469\u5251\u6cd5\u4e2d\u7684\u201c\u5927 \u987b\u5f25\u5341\u4e03\u5f0f\u201d\u5df2\u662f\u5b8c\u5168\u5c06\u5bf9\u65b9\u7684\u9000\u8def\u5c01\u4f4f\uff0c\u53ea\u662f\u51fa\u5251\u65e0\u529b\uff0c\u773c\u770b\u5bf9\u65b9\u968f\u610f\u51fa\u624b\uff0c\u4fbf\u8f7b\u63cf\u6de1\u5199 \u7684\u5c06\u81ea\u5df1\u7cbe\u5947\u5965\u5999\u7684\u5251\u62db\u5316\u89e3\u4e8e\u65e0\u5f62\uff0c\u5fc3\u4e2d\u65e0\u9650\u7740\u6025\uff0c\u5374\u662f\u6beb\u65e0\u529e\u6cd5\uff0c\u6253\u5230\u540e\u6765\uff0c\u6842\u534e\u751f\u6e10 \u77e5\u4e0d\u5999\uff0c\u89d1\u51c6\u5bf9\u65b9\u7834\u7efd\uff0c\u4e0d\u6562\u5192\u9669\u6c42\u901e\uff0c\u7528\u5c3d\u6d51\u8eab\u4e4b\u529b\uff0c\u4e00\u62db\u201c\u6469\u51fb\u957f\u7a7a\u201d\uff0c\u9aa4\u4e0b\u6740\u624b\uff0c\u82e5 \u5728\u5f80\u65f6\uff0c\u8fd9\u4e00\u5251\u975e\u628a\u5bf9\u65b9\u523a\u4e2a\u900f\u660e\u7a9f\u8428\u4e0d\u53ef\uff0c\u90a3\u77e5\u8fd9\u4e00\u6b21\u5374\u662f\u529b\u4e0d\u4ece\u5fc3\uff0c\u5251\u52bf\u6b32\u901f\u53cd\u7f13\uff0c\u5251 \u5c16\u773c\u770b\u5c31\u8981\u523a\u5230\u90a3\u9ed1\u9762\u50e7\u4eba\u7684\u80f8\u53e3\uff0c\u5374\u88ab\u4ed6\u5012\u8f6c\u62c2\u5c18\uff0c\u8f7b\u8f7b\u4e00\u5377\uff0c\u201c\u5f53\u561f\u201d\u4e00\u58f0\uff0c\u767b\u65f6\u5c06\u6842 \u534e\u751f\u7684\u817e\u86df\u5b9d\u5251\u593a\u51fa\u624b\u53bb\uff0c\u7529\u5728\u5730\u4e0a\uff0c\u90a3\u9ed1\u9762\u50e7\u4eba\u54c8\u54c8\u5927\u7b11\uff0c\u4ecd\u7136\u56de\u5230\u6e56\u8fb9\u76d8\u819d\u9759\u5750\uff0c\u6839\u672c \u5c31\u4e0d\u518d\u7406\u776c\u4ed6\u4e86\u3002 \u3000\u3000\u6842\u534e\u751f\u62fe\u56de\u5b9d\u5251\uff0c\u4e00\u7247\u832b\u7136\uff0c\u591a\u65e5\u6765\u7684\u6000\u7591\uff0c\u8fd9\u65f6\u5df2\u5f97\u5230\u4e86\u8bc1\u5b9e\uff0c\u5e76\u4e0d\u662f\u90a3\u9ed1\u9762\u618e\u4eba\u7684 \u529f\u529b\u9ad8\u4e86\uff0c\u800c\u4e14\u81ea\u5df1\u7684\u529f\u529b\u51cf\u4e86\uff0c\u800c\u4e14\u662f\u5728\u4e0d\u77e5\u4e0d\u89c9\u4e4b\u4e2d\uff0c\u4e00\u5929\u4e00\u5929\u7684\u51cf\u9000\uff0c\u81ea\u5df1\u6b63\u5f53\u5e74\u5bcc \u529b\u5f3a\uff0c\u4e3a\u4ec0\u4e48\u4f1a\u6709\u8fd9\u6837\u7684\u73b0\u8c61\uff1f \u3000\u3000\u906d\u53d7\u4e86\u8fd9\u6b21\u8d25\u7ee9\uff0c\u6842\u534e\u751f\u4e00\u8fde\u6570\u65e5\u4e0d\u6562\u51fa\u6218\uff0c\u6bcf\u65e5\u53ea\u662f\u82e6\u7ec3\u5185\u529f\uff0c\u81ea\u89c9\u5728\u6b66\u5b66\u7684\u539f\u7406 \u4e0a\uff0c\u6bd4\u8d77\u4ee5\u524d\uff0c\u5df2\u53c2\u900f\u4e86\u4e0d\u5c11\u8bc0\u7a8d\uff0c\u4f46\u5947\u602a\u7684\u662f\uff1a\u771f\u6c14\u8fd0\u8f6c\uff0c\u5374\u53cd\u800c\u4e0d\u4f3c\u4ee5\u524d\u7684\u6d41\u7545\u81ea\u5982\u3002 \u3000\u3000\u5230\u4e86\u4e00\u5929\u65e9\u6668\uff0c\u4ed6\u4f38\u624b\u5230\u6cc9\u6c34\u91cc\u6355\u9c7c\uff0c\u4f46\u89c9\u6cc9\u6c34\u5bd2\u51b7\u4e4b\u6781\uff0c\u51e0\u4e4e\u4e0d\u80fd\u5fcd\u8010\uff0c\u8fd9\u53c8\u662f\u524d\u6240 \u672a\u89c1\u7684\u73b0\u8c61\uff0c\u8fd9\u73b0\u8c61\u80fd\u6709\u4e00\u4e2a\u89e3\u91ca\uff0c\u5b83\u7684\u529f\u529b\u5df2\u51cf\u5f31\u5230\u4e0d\u80fd\u62b5\u6297\u5bd2\u6cc9\u7684\u5730\u6b65\uff01\u56de\u5fc6\u4ee5\u524d\u5728\u5947 \u5bd2\u7684\u51b0\u7a9f\u4e4b\u4e2d\u5c1a\u53ef\u5fcd\u53d7\uff0c\u5982\u4eca\u7adf\u8fde\u9c7c\u513f\u53ef\u4ee5\u751f\u5b58\u7684\u6cc9\u6c34\uff0c\u81ea\u5df1\u4ea6\u89c9\u89e6\u624b\u751f\u5bd2\uff0c\u4e0d\u89c9\u5fc3\u7070\u610f\u51b7 \u65e5\u518d\u8fc7\u4e86\u82e5\u5e72\u65e5\u5b50\uff0c\u6842\u534e\u751f\u8fde\u521d\u6765\u4e4b\u65f6\uff0c\u7ecf\u5e38\u53ef\u4ee5\u642c\u7684\u5927\u77f3\u4e5f\u642c\u4e0d\u52a8\u4e86\uff0c\u867d\u7136\u4ed6\u81ea\u5df1\u5e76\u6ca1\u6709 \u611f\u89c9\u751f\u75c5\uff0c\u4f46\u4f53\u529b\u6bd4\u4e4b\u4e00\u822c\u7684\u58ee\u6c49\uff0c\u5374\u4e5f\u5f3a\u4e0d\u4e86\u591a\u5c11\u4e86\uff01\u6842\u534e\u751f\u8bd5\u51fa\u4e86\u81ea\u5df1\u7684\u4f53\u529b\uff0c\u5f53\u771f\u662f \u4e07\u5ff5\u7686\u7070\uff0c\u4f24\u5fc3\u6b32\u7edd\uff01 \u3000\u3000\u4e3e\u5934\u4e00\u770b\uff0c\u65e5\u5f71\u897f\u79fb\uff0c\u665a\u971e\u5982\u8840\uff0c\u6842\u534e\u751f\u5582\u7136\u53f9\u9053\uff1a\u201c\u53c8\u662f\u4e00\u4e2a\u767d\u5929\u5c06\u8fc7\u53bb\u4e86\u3002\u201d\u4ed6\u81ea \u4ece\u53d1\u89c9\u81ea\u5df1\u7684\u529f\u529b\u6d88\u51cf\u4e4b\u540e\uff0c\u65e0\u5fc3\u518d\u7b97\u65e5\u5b50\uff0c\u81ea\u5df1\u4e5f\u4e0d\u77e5\u9053\u5728\u6d1e\u4e2d\u5df2\u8fc7\u4e86\u591a\u5c11\u65f6\u5149\uff1f\u773c\u770b\u53ef \u843d\u82b1\u6b8b\uff0c\u80f8\u4e2d\u767e\u611f\u4ea4\u96c6\uff0c\u60f3\u9053\uff1a\u201c\u96be\u9053\u6211\u7adf\u542b\u5728\u8fd9\u5e7d\u8c37\u4e4b\u4e2d\uff0c\u6e10\u6e10\u7684\u8870\u8001\u6b7b\u4ea1\uff0c\u5c31\u50cf\u8fd9\u8c37\u4e2d \u7684\u6b8b\u82b1\u4e00\u6837\uff0c\u81ea\u751f\u81ea\u706d\uff0c\u65e0\u4eba\u77e5\u6653\uff1f\u201d\u60f3\u81f3\u6b64\u5904\uff0c\u624b\u6478\u5251\u67c4\uff0c\u4fbf\u60f3\u62d4\u5251\u81ea\u6215\u3002 \u3000\u3000\u8111\u6d77\u4e2d\u5ffd\u5730\u6d6e\u51fa\u516c\u4e3b\u7684\u5029\u5f71\uff0c\u6842\u534e\u751f\u53f9\u4e86\u53e3\u6c14\uff0c\u8f7b\u8f7b\u7684\u5c06\u5b9d\u5251\u53c8\u63d2\u5165\u9500\u4e2d\uff0c\u60f3\u9053\uff1a\u201c\u6211 \u4e0d\u80fd\u6b7b\uff0c\u6211\u4e0d\u80fd\u6b7b\uff01\u201d\u665a\u98ce\u5439\u6765\uff0c\u9690\u9690\u542c\u90a3\u5370\u5ea6\u50e7\u4eba\u7684\u7b11\u58f0\uff0c\u6842\u534e\u751f\u5df2\u6709\u8bb8\u591a\u65e5\u5b50\u672a\u66fe\u548c\u4ed6 \u4ea4\u624b\u4e86\uff0c\u542c\u5230\u4ed6\u7684\u7b11\u58f0\uff0c\u75db\u6068\u4e4b\u6781\uff0c\u8df3\u4e0a\u4e00\u5757\u5ca9\u77f3\uff0c\u8fdc\u8fdc\u671b\u53bb\uff0c\u4f46\u89c1\u90a3\u4e2a\u5370\u5ea6\u50e7\u4eba\u6325\u5200\u72c2 \u821e\uff0c\u6842\u534e\u751f\u529f\u529b\u867d\u5931\uff0c\u4f46\u4ecd\u7136\u662f\u4e00\u4e2a\u6b66\u5b66\u7684\u5927\u884c\u5bb6\uff0c\u770b\u4e00\u4f1a\uff0c\u5fc3\u9053\uff1a\u201c\u539f\u6765\u4ed6\u4ece\u6211\u7684\u8fbe\u6469\u5251 \u6cd5\u4e4b\u4e2d\u4e5f\u9886\u609f\u4e0d\u5c11\u65b0\u62db\uff0c\u770b\u6765\u6211\u4e0e\u4ed6\u6076\u6597\u5341\u5929\uff0c\u5f7c\u6b64\u90fd\u662f\u5f97\u76ca\u4e0d\u6d45\u3002\u201d\u60f3\u8d77\u81ea\u5df1\u7684\u96c4\u5fc3\u5927 \u5fd7\uff0c\u8981\u5bfb\u6c42\u7edd\u4e16\u7684\u6b66\u529f\uff0c\u8fd9\u4e00\u5e74\u591a\u6765\uff0c\u4f3c\u6709\u4e86\u4e0d\u5c11\u6536\u83b7\uff0c\u5fc3\u4e2d\u7a0d\u7a0d\u5b89\u6170\uff0c\u5bf9\u90a3\u50e7\u4eba\u7684\u6068\u610f\u4e5f \u51cf\u4e86\u51e0\u5206\u3002\u53ea\u662f\u4f53\u529b\u65e5\u8870\uff0c\u8eab\u56da\u5e7d\u8c37\uff0c\u7eb5\u7136\u53c2\u900f\u4e86\u4e0a\u4e58\u6b66\u5b66\uff0c\u53c8\u767e\u4ec0\u4e48\u7528\u5904\uff1f \u3000\u3000\u53ea\u89c1\u90a3\u50e7\u4eba\u6325\u5200\u75be\u821e\uff0c\u968f\u610f\u5288\u4e0b\uff0c\u5200\u950b\u6240\u89e6\uff0c\u5ca9\u77f3\u5e94\u624b\u800c\u88c2\uff0c\u6842\u534e\u751f\u5fc3\u8d77\u7591\u4e91\uff1a\u201c\u4e3a\u4ec0 \u4e48\u6211\u7684\u4f53\u529b\u65e5\u8870\uff0c\u800c\u4ed6\u7684\u529f\u529b\u5374\u662f\u4e1d\u6beb\u4e0d\u51cf\uff1f\u201d\u53c8\u60f3\u9053\uff1a\u201c\u82e5\u662f\u4ed6\u5982\u4eca\u8981\u53d6\u6211\u7684\u6027\u547d\uff0c\u771f\u662f \u4e0d\u8d39\u5439\u7070\u4e4b\u529b\u3002\u201d\u5982\u6b64\u4e00\u60f3\uff0c\u66f4\u89c9\u5f97\u90a3\u7b11\u58f0\u523a\u8033\u94bb\u5fc3\uff0c\u9eef\u7136\u8df3\u4e0b\uff0c\u4e0d\u6562\u518d\u770b\u3002 \u3000\u3000\u53c8\u8fc7\u4e86\u597d\u591a\u5929\uff0c\u6842\u534e\u751f\u8eab\u4f53\u66f4\u8870\u5f31\u4e86\uff0c\u4ed6\u521d\u6765\u4e4b\u65f6\uff0c\u662f\u7528\u624b\u6355\u9c7c\uff0c\u540e\u6765\u611f\u5230\u6cc9\u6c34\u592a\u51b7\uff0c \u4fbf\u505a\u4e86\u4e00\u4e2a\u6728\u53c9\u53c9\u9c7c\uff0c\u8fd9\u4e00\u65e5\u4ed6\u624b\u4e3e\u6728\u53c9\u4e5f\u89c9\u5403\u529b\u4e86\uff0c\u597d\u4e45\uff0c\u597d\u4e45\uff0c\u90fd\u6ca1\u6709\u53c8\u5230\u4e00\u6761\u9c7c\uff0c\u60f3 \u8d77\u8eab\u6000\u7edd\u6280\uff0c\u7adf\u7136\u56f0\u987f\u52a0\u65af\uff0c\u4e0d\u89c9\u60b2\u4ece\u4e2d\u6765\uff0c\u96be\u4ee5\u65ad\u7edd\uff0c\u8db4\u5728\u679c\u8fb9\uff0c\u6697\u6697\u53f9\u6c14\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u6709\u6089\u6089\u7d22\u7d20\u7684\u58f0\u97f3\uff0c\u4f3c\u662f\u6709\u4eba\u5411\u81ea\u5df1\u884c\u6765\uff0c\u6842\u534e\u751f\u5fc3\u9053\uff1a\u201c\u83ab\u975e\u662f\u90a3\u4e2a\u5370\u5ea6\u50e7\u4eba \u6765\u4e86\uff1f\u201d\u7741\u773c\u4e00\u77a7\uff0c\u51e0\u7591\u662f\u68a6\uff0c\u4f60\u9053\u662f\u8c01\uff1f\u539f\u6765\u7adf\u662f\u5df4\u52d2\uff0c\u4f46\u89c1\u4ed6\u8863\u886b\u7834\u70c2\uff0c\u624b\u4e0a\u811a\u58eb\u90fd\u6709 \u4e00\u6761\u6761\u7684\u4f24\u75d5\uff0c\u4f46\u53cc\u6628\u70af\u70af\uff0c\u7cbe\u795e\u5374\u4f3c\u975e\u5e38\u5174\u594b\u3002 \u3000\u3000\u6842\u534e\u751f\u53c8\u60ca\u53c8\u559c\uff0c\u6025\u5fd9\u95ee\u9053\uff1a\u201c\u8fd9\u4e0d\u662f\u68a6\u4e48\uff1f\u4f60\u662f\u600e\u4e48\u6765\u7684\uff1f\u201d\u5df4\u52d2\u9053\uff1a\u201c\u6211\u662f\u4ece\u540e\u9762 \u7684\u5c71\u8def\u6765\u7684\u3002\u201d\u5e7d\u8c37\u540e\u9762\uff0c\u5ced\u58c1\u5343\u4e08\uff0c\u8346\u68d8\u6ee1\u9014\uff0c\u6842\u534e\u751f\u529f\u529b\u672a\u51cf\u4e4b\u524d\uff0c\u4e5f\u4e0d\u6562\u8d77\u8fc7\u4ece\u8fd9\u6761 \u8def\u9003\u8d70\u7684\u5ff5\u5934\uff0c\u542c\u5df4\u52d2\u8bf4\u662f\u4ece\u8fd9\u6761\u8def\u6765\u7684\u4e0d\u7981\u7741\u5927\u773c\u775b\u3002\u5df4\u52d2\u7b11\u9053\uff1a\u201c\u6211\u672c\u6765\u4e5f\u662f\u4e0d\u77e5\u9053\u5c71 \u540e\u6709\u8def\u53ef\u901a\u7684\uff0c\u739b\u6e05\u52d2\u5bfa\u6709\u4e2a\u884c\u811a\u50e7\u4eba\uff0c\u66fe\u7ecf\u5230\u8fc7\u8fd9\u5e7d\u8c37\u91c7\u836f\uff0c\u4ed6\u544a\u8bc9\u6211\uff0c\u540e\u9762\u4e00\u5ea7\u5c71\u6709\u4e00 \u4e2a\u5f88\u6df1\u7684\u5c71\u6d1e\uff0c\u5e7d\u8c37\u5916\u9762\u6709\u6761\u5c71\u6eaa\uff0c\u6c34\u6d41\u6e4d\u6025\uff0c\u53ef\u4ee5\u4e58\u6728\u7b4f\u4eba\u5230\u6d1e\u4e2d\uff0c\u51fa\u4e86\u5c71\u6d1e\u4e4b\u540e\uff0c\u6709\u4e00 \u6761\u5f88\u72ed\u7a84\u7684\u5c71\u8def\uff0c\u53ef\u4ee5\u901a\u5230\u8fd9\u91cc\uff0c\u4e0d\u8fc7\u96be\u8d70\u5f97\u5f88\uff0c\u6211\u722c\u4e86\u4e24\u5929\u4e24\u591c\uff0c\u624d\u8d70\u5230\u8fd9\u91cc\u6765\u3002\u8863\u88d8\u90fd \u88ab\u52fe\u7834\u4e86\u3002\u201d \u3000\u3000\u6842\u534e\u751f\u6709\u8bb8\u591a\u7591\u95ee\uff0c\u6682\u65f6\u4e14\u90fd\u6401\u5728\u4e00\u8fb9\uff0c\u5148\u95ee\u9053\uff1a\u201c\u8bf7\u4f60\u770b\u770b\u6211\u662f\u4ec0\u4e48\u75c5\uff1f\u201d\u5df4\u52d2\u7b11 \u9053\uff1a\u201c\u4e0d\u5fc5\u8bca\u6cbb\uff0c\u4f60\u6ca1\u6709\u75c5\uff01\u201d \u3000\u3000\u6842\u534e\u751f\u7cbe\u795e\u52c3\u632f\uff0c\u4e00\u8dc3\u800c\u8d77\uff0c\u53eb\u9053\uff1a\u201c\u6211\u600e\u7684\u6ca1\u6709\u75c5\u5417\uff1f\u201d\u6325\u4e86\u4e00\u4e0b\u624b\u81c2\uff0c\u5ffd\u53c8\u9893\u7136\u8bf4 \u9053\uff1a\u201c\u6211\u4e0d\u76f8\u4fe1\uff0c\u6211\u600e\u7684\u4f1a\u6ca1\u6709\u75c5\uff1f\u201d\u5df4\u52d2\u5c06\u4ed6\u6309\u4e0b\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u89c9\u5f97\u8eab\u4f53\u865a\u8f6f\uff0c\u56db\u80a2\u4e4f \u529b\uff0c\u662f\u4e5f\u4e0d\u662f\uff1f\u201d\u6842\u534e\u751f\u9053\uff1a\u201c\u8fd9\u4e0d\u662f\u75c5\u4e48\uff1f\u201d\u5df4\u52d2\u9053\uff1a\u201c\u8fd9\u4e0d\u662f\u75c5\uff0c\u8fd9\u662f\u56e0\u4e3a\u4f60\u591a\u996e\u4e86\u5bd2 \u6cc9\u4e4b\u6545\u3002\u8fd9\u6cc9\u6c34\u6e05\u5012\u53ef\u7231\uff0c\u4f46\u90a3\u9634\u51b7\u4e4b\u6c14\u5374\u6700\u4e3a\u635f\u8017\u4eba\u4f53\u3002\u4ee5\u524d\u6211\u56fd\u6709\u4e00\u4e2a\u836f\u7269\u5b66\u5bb6\uff0c\u66fe\u5230 \u8fc7\u8fd9\u91cc\u4f5c\u8bd5\u9a8c\uff0c\u636e\u8bf4\u8fd9\u91cc\u5730\u8d28\u7279\u522b\uff0c\u6cc9\u6c34\u4e2d\u7f3a\u4e4f\u67d0\u4e9b\u77ff\u7269\u8d28\uff0c\u4ed6\u628a\u4e00\u6279\u9c7c\u82d7\u653e\u4e0b\u53bb\uff0c\u9c7c\u513f\u957f \u5927\u4e86\u90fd\u662f\u6ca1\u6709\u9aa8\u7684\u3002\u201d\u6842\u534e\u751f\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u6cc9\u4e2d\u4e4b\u9c7c\uff0c\u5982\u6b64\u7518\u7f8e\uff0c\u6211\u8fd8\u628a\u8fd9\u4e9b\u65e0\u9aa8\u9c7c\u5f53\u6210\u4f73 \u54c1\u5462\u3002\u539f\u6765\u662f\u90a3\u4e2a\u836f\u7269\u5b66\u5bb6\u6240\u517b\u7684\uff0c\u4e00\u76f4\u7e41\u6b96\u81f3\u4eca\u3002\u201d\u5df4\u52d2\u9053\uff1a\u201c\u6240\u4ee5\u8981\u4f60\u51fa\u4e86\u8fd9\u4e2a\u5e7d\u8c37\uff0c \u6839\u672c\u4e0d\u5fc5\u7528\u836f\uff0c\u8fc7\u4e86\u4e00\u5e74\u534a\u8f7d\uff0c\u8eab\u4f53\u4fbf\u4f1a\u5982\u521d\uff0c\u529f\u529b\u4e5f\u81ea\u7136\u6062\u590d\u539f\u72b6\u3002\u201d\u6842\u534e\u751f\u9053\uff1a\u201c\u90a3\u5370 \u5ea6\u50e7\u4eba\u53ea\u6015\u4e0d\u4f1a\u8ba9\u6211\u51fa\u53bb\uff0c\u4ed6\u7684\u529f\u529b\uff0c\u5012\u6ca1\u6709\u51cf\u9000\u5462\u3002\u201d\u5df4\u52d2\u9053\uff1a\u201c\u90a3\u4e2a\u5370\u5ea6\u50e7\u4eba\uff1f \u3000\u3000\u5c31\u662f\u90a3\u5929\u5c06\u4f60\u649e\u4e0b\u5e7d\u8c37\u7684\u90a3\u4eba\u5417\uff1f\u201d\u6842\u534e\u751f\u9053\uff1a\u201c\u6b63\u662f\u3002\u201d\u5c06\u56f0\u5728\u5e7d\u8c37\u7684\u7ecf\u8fc7\u60c5\u5f62\uff0c\u8be6 \u7ec6\u5bf9\u5df4\u52d2\u8bf4\u4e86\u4e00\u904d\u3002 \u3000\u3000\u5df4\u52d2\u9053\uff1a\u201c\u4ed6\u996e\u7684\u5927\u7ea6\u662f\u53d6\u81ea\u7011\u5e03\u4e2d\u7684\u5c71\u6c34\uff0c\u90a3\u662f\u4ece\u4e0a\u9762\u9ad8\u5c71\u6d41\u4e0b\u6765\u7684\uff0c\u5bf9\u8eab\u4f53\u6ca1\u6709\u5f71 \u54cd\u3002\u4e0d\u8fc7\uff0c\u542c\u4f60\u8fd9\u4e48\u8bf4\uff0c\u6211\u5012\u7cca\u6d82\u4e86\uff0c\u4ed6\u4e3a\u4ec0\u4e48\u4e0d\u8bb8\u4f60\u51fa\u53bb\uff1f\u201d\u6842\u534e\u751f\u9053\uff1a\u201c\u6211\u8fd8\u4ee5\u4e3a\u4f60\u5728 \u5916\u9762\u63a2\u542c\u5230\u4e86\u4e2a\u4e2d\u539f\u59d4\u3002\u201d\u5df4\u52d2\u9053\uff1a\u201c\u6211\u4ee5\u4e3a\u662f\u738b\u5b50\u6d3e\u4eba\u5bb3\u4f60\u7684\uff0c\u5374\u600e\u7684\u5f04\u51fa\u4e00\u4e2a\u6b66\u529f\u6781\u4e3a \u9ad8\u660e\u7684\u5370\u5ea6\u50e7\u4eba\u51fa\u6765\uff1f\u201d\u6842\u534e\u751f\u9053\uff1a\u201c\u90a3\u4e2a\u738b\u5b50\uff1f\u201d\u5df4\u52d2\u9053\uff1a\u201c\u5f53\u7136\u662f\u655d\u56fd\u7684\u90a3\u4e2a\u738b\u5b50\u3002\u201d \u3000\u3000\u539f\u6765\u90a3\u5929\u5df4\u52d2\u89c1\u6842\u534e\u751f\u5760\u4e0b\u5e7d\u8c37\uff0c\u5fc3\u4e2d\u5f53\u7136\u662f\u60ca\u9a87\u975e\u5e38\uff0c\u53ef\u662f\u4ed6\u4e0d\u61c2\u6b66\u529f\uff0c\u6b32\u6551\u65e0\u4ece\uff0c \u53ea\u597d\u5148\u5230\u90a3\u52c3\u63d0\u5c97\u57ce\uff0c\u4e3a\u739b\u6e05\u52d2\u5bfa\u7684\u4e3b\u6301\u770b\u75c5\uff0c\u5230\u4e86\u739b\u6e05\u52d2\u5bfa\u4e4b\u540e\uff0c\u624d\u77e5\u9053\u539f\u6765\u662f\u738b\u5b50\u529d\u4ed6 \u6765\u7ed9\u4e3b\u6301\u6cbb\u75c5\u7684\uff0c\u738b\u5b50\u8fd8\u6709\u4fe1\u7ed9\u4e3b\u6301\u8bf4\uff0c\u8bf4\u662f\u7b49\u5df4\u52c3\u533b\u597d\u4e86\u4ed6\u7684\u75c5\u4e4b\u540e\uff0c\u51c6\u5907\u9686\u91cd\u7684\u8fce\u63a5\u5df4 \u52d2\u56de\u53bb\uff0c\u5c01\u4ed6\u505a\u592a\u533b\uff0c\u5df4\u52d2\u672c\u6765\u662f\u56fd\u4e2d\u7b2c\u4e00\u540d\u533b\uff0c\u5c01\u4ed6\u505a\u592a\u533b\u6b63\u662f\u5b9e\u81f3\u540d\u5f52\u3002\u56e0\u4e3a\u738b\u5b50\u6709\u8bdd \u4ea4\u5f85\uff0c\u6240\u4ee5\u5df4\u52d2\u533b\u597d\u4e3b\u6301\u4e4b\u540e\uff0c\u5c61\u6b21\u8f9e\u884c\uff0c\u4e3b\u6301\u90fd\u7559\u4f4f\u4e0d\u653e\uff0c\u8981\u5f85\u738b\u5b50\u6765\u63a5\uff0c\u738b\u5b50\u5374\u53c8\u8fdf\u8fdf \u4e0d\u6765\u3002\u5df4\u52d2\u770b\u51fa\u5176\u4e2d\u8df7\u8e4a\uff0c\u53c8\u77e5\u9053\u4e3b\u6301\u5de6\u53f3\u4e5f\u6709\u738b\u5b50\u7684\u4eb2\u4fe1\uff0c\u9042\u4e0d\u6562\u8bf7\u6c42\u4e3b\u6301\u6d3e\u4eba\u53bb\u8425\u6551\u6842 \u534e\u751f\u3002\u5e78\u800c\u739b\u6e05\u52d2\u5bfa\u6709\u4e00\u4e2a\u884c\u811a\u50e7\u4eba\uff0c\u4e0e\u4ed6\u751a\u4e3a\u77e5\u5df1\uff0c\u544a\u8bc9\u4ed6\u8c37\u4e2d\u901a\u8def\u7684\u79d8\u5bc6\uff0c\u5df4\u52d2\u662f\u5f97\u4e86 \u4ed6\u7684\u5e2e\u52a9\uff0c\u6084\u6084\u7684\u9003\u51fa\u739b\u6e05\u52d2\u5bfa\u7684\u3002 \u3000\u3000\u6842\u534e\u751f\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u6211\u56f0\u5728\u8c37\u4e2d\u5c82\u4e0d\u662f\u6709\u4e2a\u591a\u6708\u4e86\uff1f\u201d\u5df4\u52d2\u5c48\u6307\u4e00\u7b97\uff0c\u5982 \u6709\u6240\u611f\uff0c\u5582\u7136\u53f9\u9053\uff1a\u201c\u8ddd\u79bb\u8003\u9009\u9a79\u9a6c\u4e4b\u671f\u6709\u4e09\u5929\u4e86\uff0c\u4f60\u6050\u8981\u8d76\u4e0d\u4e0a\u5566\uff01\u201d \u3000\u3000\u6842\u534e\u751f\u5fc3\u4e2d\u4e00\u9635\u96be\u8fc7\uff0c\u52c9\u5f3a\u7b11\u9053\uff1a\u201c\u6211\u4f46\u6c42\u80fd\u9003\u51fa\u751f\u5929\uff0c\u5df2\u662f\u4e07\u5e78\uff0c\u90a3\u8fd8\u6562\u6709\u6b64\u5962\u671b\uff0c \u5c1a\u5a5a\u516c\u4e3b\uff1f\u201d\u5df4\u52d2\u9053\uff1a\u201c\u4ee5\u4f60\u7684\u4f53\u529b\uff0c\u8981\u722c\u8fc7\u90a3\u6bb5\u5c71\u8def\u3002\u786e\u662f\u53ef\u8651\u3002\u597d\u5728\u6211\u5e26\u6709\u4e00\u6839\u4ece\u8d35\u56fd \u6765\u7684\u4eba\u53c2\uff0c\u90a3\u662f\u7ed9\u4e3b\u6301\u533b\u75c5\u7528\u81b3\u7684\uff0c\u4f60\u8d76\u5feb\u670d\u4e0b\uff0c\u632f\u594b\u7cbe\u795e\uff0c\u652f\u6301\u4e24\u4e09\u5929\u5927\u7ea6\u6ca1\u6709\u95ee\u9898\u3002\u201d \u3000\u3000\u6842\u534e\u751f\u5c06\u90a3\u4eba\u53c2\u56bc\u4e86\u4e00\u6bb5\uff0c\u9053\uff1a\u201c\u597d\u5427\uff0c\u8d81\u90a3\u4e2a\u5370\u5ea6\u50e7\u4eba\u6ca1\u6709\u53d1\u89c1\uff0c\u8d76\u5feb\u8d70\u5427\uff01\u201d\u4e24\u4eba \u653e\u7ecf\u811a\u6b65\uff0c\u9760\u7740\u6811\u6728\u82b1\u8349\u7684\u63a9\u853d\uff0c\u8d70\u5230\u8c37\u540e\u7684\u90a3\u5ea7\u5c71\u811a\uff0c\u6842\u534e\u751f\u6b63\u81ea\u6b22\u559c\uff0c\u5ffd\u542c\u5f97\u6709\u783e\u783e\u7684 \u602a\u7b11\u4e4b\u58f0\uff0c\u56de\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u90a3\u4e2a\u5370\u5ea6\u50e7\u4eba\uff0c\u5df2\u662f\u5982\u98de\u8d76\u81f3\uff01 \u3000\u3000\u53ea\u542c\u5f97\u90a3\u9ed1\u9762\u50e7\u4eba\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4e0d\u8bb8\u51fa\u53bb\uff01\u201d\u5df4\u52d2\u61c2\u5f97\u51e0\u53e5\u5370\u5ea6\u8bdd\uff0c\u6025\u5fd9\u8bf4\u9053\uff1a\u201c\u4f60\u4eec \u7684\u738b\u5b50\u73b0\u5728\u6b63\u5728\u5bab\u4e2d\uff0c\u4ed6\u548c\u6842\u5148\u751f\u662f\u597d\u670b\u53cb\uff0c\u4f60\u82e5\u4e0d\u4fe1\uff0c\u6211\u53ef\u4ee5\u5e26\u4f60\u53bb\u770b\u4ed6\u3002\u201d\u90a3\u9ed1\u9762\u50e7\u4eba \u8bf4\u9053\uff1a\u201c\u4e0d\u884c\uff0c\u51fa\u5230\u5916\u9762\u6211\u5c31\u6ca1\u6cd5\u5236\u4f4f\u4ed6\u4e86\u3002\u9664\u975e\u4f60\u5c06\u738b\u5b50\u5e26\u6765\u89c1\u6211\u3002\u201d \u3000\u3000\u6842\u534e\u751f\u770b\u4ed6\u4eec\u7684\u795e\u8272\uff0c\u77e5\u9053\u8fd9\u9ed1\u9762\u50e7\u4eba\u7edd\u5bf9\u4e0d\u4f1a\u653e\u4ed6\uff0c\u521a\u521a\u71c3\u8d77\u4e00\u70b9\u5e0c\u671b\u4e4b\u706b\u53c8\u544a\u7184 \u706d\uff0c\u4e0d\u7981\u957f\u53f9\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u5427\uff0c\u770b\u6765\u6211\u662f\u547d\u4e2d\u6ce8\u5b9a\uff0c\u8981\u56f0\u6b7b\u5e7d\u8c37\u4e86\uff01\u201d\u7eb5\u8eab\u4e00\u8dc3\uff0c\u649e\u5411 \u5ca9\u77f3\uff0c\u5374\u88ab\u90a3\u50e7\u4eba\u4e00\u628a\u63d0\u4e86\u56de\u6765\uff0c\u6842\u534e\u751f\u6012\u9053\uff1a\u201c\u4f60\u4e0d\u8bb8\u6211\u51fa\u53bb\uff0c\u6211\u8981\u6b7b\u90fd\u4e0d\u884c\u4e48\uff1f\u201d\u90a3\u9ed1 \u9762\u4eba\u4e0d\u77e5\u9053\u4ed6\u8bf4\u4e9b\u4ec0\u4e48\uff1f\u4ecd\u7136\u662f\u90a3\u65ec\u6842\u534e\u751f\u542c\u5f97\u61c2\u7684\u5370\u5ea6\u8bdd\uff1a\u201c\u4e0d\u8bb8\u51fa\u53bb\uff01\u201d \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u5ffd\u542c\u5f97\u4e00\u58f0\u4f5b\u53f7\uff0c\u5df4\u52d2\u542c\u5f97\u4e00\u4e2a\u82cd\u8001\u7684\u58f0\u97f3\uff0c\u7528\u5370\u5ea6\u8bdd\u65a5\u9053\uff1a\u201c\u963f\u8fe6\u7f57\u4f11\u5f97 \u65e0\u793c\u3002\u201d\u6842\u534e\u751f\u5374\u542c\u5f97\u96c5\u5fb7\u661f\u7528\u5c3c\u6cca\u5c14\u8bdd\u8bf4\u9053\uff1a\u201c\u6842\u5144\u597d\u5417\uff1f\u5c0f\u5f1f\u6765\u4e86\uff01\u201d \u3000\u3000\u90a3\u50e7\u4eba\u653e\u5f00\u4e86\u6842\u534e\u751f\uff0c\u5782\u624b\u4f4e\u5934\uff0c\u6ee1\u9762\u60f6\u6050\u4e4b\u8272\u3002\u6842\u534e\u751f\u62ac\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u96c5\u5fb7\u661f\u548c\u4e00\u4e2a \u987b\u7709\u4ff1\u767d\u7684\u5370\u5ea6\u8001\u50e7\u4eba\uff0c\u5df2\u7ad9\u5728\u81ea\u5df1\u7684\u9762\u524d\uff0c\u6842\u534e\u751f\u51e0\u4e4e\u7591\u5fc3\u662f\u5728\u68a6\u4e2d\uff0c\u60f3\u4e0d\u5230\u7adf\u6709\u90a3\u4e48\u51d1 \u5de7\u7684\u4e8b\uff0c\u90a3\u9ed1\u9762\u50e7\u4eba\u521a\u521a\u8bf4\u5230\u96c5\u5fb7\u661f\u738b\u5b50\uff0c\u96c5\u5fb7\u661f\u738b\u5b50\u5c31\u6765\u4e86\u3002 \u3000\u3000\u96c5\u5fb7\u661f\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u5bb6\u5e08\u9f99\u53f6\u4e0a\u4eba\u3002\u201d\u6842\u534e\u751f\u53c8\u60ca\u53c8\u559c\uff0c\u6025\u5fd9\u884c\u793c\uff0c\u9f99\u53f6\u4e0a\u4eba\u79bb\u4ed6\u6709\u4e08 \u8bb8\u4e4b\u8fdd\uff0c\u53cc\u624b\u4f5c\u52bf\u4e00\u6276\uff0c\u6842\u534e\u751f\u4f46\u89c9\u6709\u4e00\u80a1\u6781\u67d4\u548c\u7684\u529b\u9053\uff0c\u5c06\u81ea\u5df1\u8f7b\u8f7b\u6258\u8d77\uff0c\u529b\u9053\u867d\u662f\u67d4 \u548c\uff0c\u5374\u662f\u4eca\u4eba\u6297\u62d2\u4e0d\u5f97\uff0c\u6842\u534e\u751f\u4e0d\u7981\u9a87\u7136\uff0c\u5c0f\u9053\uff1a\u201c\u9f99\u53f6\u4e0a\u4eba\u679c\u7136\u540d\u4e0d\u865a\u4f20\uff0c\u8fd9\u80a1\u771f\u529b\u6bd4\u91d1 \u521a\u638c\u7684\u731b\u529b\u8981\u7cbe\u7eaf\u5f97\u591a\uff0c\u4e2d\u56fd\u7684\u5929\u5c71\u4e03\u5251\uff0c\u5df2\u5148\u540e\u51cb\u8c22\uff0c\u73af\u987e\u5b87\u5185\uff0c\u5982\u4eca\u5177\u6709\u8fd9\u7b49\u4e0a\u4e58\u5185\u529f \u7684\uff0c\u53ea\u6015\u6709\u4ed6\u4e00\u4eba\u4e86\u3002\u201d \u3000\u3000\u5ffd\u542c\u5f97\u9f99\u53f6\u4e0a\u4eba\u7528\u4e2d\u56fd\u8bdd\u8bf4\u9053\uff1a\u201c\u655d\u5e08\u5f1f\u65e0\u793c\uff0c\u8001\u7eb3\u66ff\u4ed6\u966a\u7f6a\u4e86\u3002\u201d\u6842\u534e\u751f\u8fd9\u624d\u77e5\u9053\u8fd9 \u4e2a\u9ed1\u9762\u50e7\u4eba\u4e43\u662f\u9f99\u53f6\u7684\u5e08\u5f1f\uff0c\u9887\u4e3a\u5947\u602a\u4ed6\u4eec\u4e24\u5e08\u5144\u5f1f\u7684\u529f\u529b\u7adf\u662f\u5dee\u5f97\u5982\u6b64\u4e4b\u8fdc\u3002\u6842\u534e\u751f\u6709\u6240 \u4e0d\u77e5\uff0c\u9f99\u53f6\u548c\u5b83\u7684\u5e08\u5f1f\u963f\u8fe6\u7f57\u5e74\u7eaa\u76f8\u5dee\u4e86\u4e09\u5341\u5c81\uff0c\u800c\u4e14\u963f\u8fe6\u7f57\u662f\u5a46\u7f57\u95e8\u5916\u9053\u3002\u867d\u7136\u5f97\u5e08\u7236\u4f20 \u6388\u4e86\u4e0a\u4e58\u6b66\u529f\uff0c\u5374\u600e\u6bd4\u5f97\u4e0a\u9f99\u53f6\u5927\u5e08\u5999\u609f\u4e86\u4e0a\u4e58\u4f5b\u6cd5\u540e\uff0c\u4ee5\u8d85\u4eba\u7684\u5b9a\u529b\u548c\u667a\u6167\uff0c\u6240\u53c2\u900f\u7684\u6b66 \u529f\uff1f \u3000\u3000\u6842\u534e\u751f\u9053\uff1a\u201c\u963f\u8fe6\u7f57\u5927\u5e08\u4e00\u65f6\u8bef\u4f1a\uff0c\u665a\u8f88\u600e\u4e48\u89c1\u602a\uff1f\u201d\u9f99\u53f6\u5927\u5e08\u9053\uff1a\u201c\u4f60\u80f8\u6000\u8c41\u8fbe\uff0c\u9887 \u6709\u4f5b\u6839\uff0c\u6211\u8fd9\u4e2a\u5e08\u5f1f\u53ef\u4e0d\u884c\uff0c\u4fee\u884c\u4e86\u51e0\u5341\u5e74\uff0c\u8d2a\u586b\u75f4\u5ff5\uff0c\u5c1a\u662f\u65e0\u4e00\u80fd\u514d\uff01\u201d\u8bf4\u7f62\u8f6c\u8fc7\u4e86\u8eab\uff0c \u7528\u5370\u5ea6\u8bdd\u548c\u5b83\u7684\u5e08\u5f1f\u8be6\u8bf4\u56e0\u679c\u3002 \u3000\u3000\u8fd9\u8fb9\u53a2\u96c5\u5fb7\u661f\u738b\u5b50\u5219\u548c\u6842\u534e\u751f\u8bf4\u8fd9\u4ef6\u4e8b\u7684\u7ecf\u8fc7\u3002\u539f\u6765\u4ed6\u7684\u56fd\u738b\u6709\u4e8b\uff0c\u963f\u8fe6\u7f57\u5949\u738b\u547d\u6765\u62db \u4ed6\u56de\u53bb\uff0c\u5374\u6b63\u78b0\u5230\u4ed6\u5728\u75c5\u4e2d\uff0c\u660f\u8ff7\u672a\u9192\u3002\u5c3c\u6cca\u5c14\u738b\u5b50\u9a97\u4ed6\u8bf4\u662f\u6842\u534e\u751f\u6253\u4f24\u4ed6\u7684\uff0c\u8fd8\u628a\u6842\u534e\u751f \u7684\u76f8\u8c8c\u753b\u7ed9\u4ed6\u770b\uff0c\u963f\u8fe6\u7f57\u5927\u6012\uff0c\u5c31\u8981\u5c06\u6842\u534e\u751f\u6349\u6765\u62a5\u4ec7\uff01 \u3000\u3000\u5c3c\u6cca\u5c14\u738b\u5b50\u5bf9\u963f\u8fe6\u7f57\u8bf4\uff0c\u8fd9\u4e2a\u4e2d\u56fd\u5c11\u5e74\u6b66\u529f\u6781\u9ad8\uff0c\u4e0d\u53ef\u8f7b\u654c\u3002\u5c3c\u6cca\u5c14\u738b\u5b50\u7b97\u51c6\u4e86\u6842\u534e\u751f \u548c\u5df4\u52c3\u5728\u524d\u5f80\u52c3\u63d0\u6811\u57ce\u7684\u8def\u4e0a\uff0c\u5fc5\u5b9a\u8981\u7ecf\u8fc7\u94c1\u7d22\u6865\uff0c\u6559\u963f\u8fe6\u7f57\u8d81\u90a3\u65f6\u673a\u5077\u88ad\uff0c\u53c8\u544a\u8bc9\u4ed6\u5e7d\u8c37 \u5bd2\u6cc9\u7684\u79d8\u5bc6\uff0c\u82e5\u662f\u5077\u88ad\u4e0d\u6210\uff0c\u53ea\u8981\u5c06\u6842\u534e\u751f\u8feb\u4e0b\u5e7d\u8c37\uff0c\u4fbf\u53ef\u4e0d\u8d39\u6c14\u529b\u5c06\u4ed6\u56f0\u6bd9\u3002 \u3000\u3000\u963f\u8fe6\u7f57\u653e\u5fc3\u4e0d\u4e0b\uff0c\u4e34\u8d70\u4e4b\u524d\uff0c\u53c8\u5165\u5bab\u770b\u4e86\u96c5\u5fb7\u661f\u4e00\u6b21\uff0c\u8fd9\u6b21\u6070\u597d\u5c3c\u6cca\u5c14\u4e0d\u5728\uff0c\u96c5\u5fb7\u661f\u53c8 \u521a\u521a\u6e05\u9192\u8fc7\u6765\uff0c\u4e00\u542c\u963f\u8fe6\u7f57\u8bf4\u51fa\u5c3c\u6cca\u5c14\u738b\u5b50\u7684\u6bd2\u8ba1\uff0c\u53c8\u60ca\u53c8\u6025\uff0c\u5389\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u82e5\u4f24\u4e86\u90a3\u4e2d \u56fd\u5c11\u5e74\u7684\u6027\u547d\uff0c\u6211\u975e\u4f46\u4e0d\u8ba4\u4f60\u505a\u5e08\u53d4\uff0c\u4ece\u6b64\u4e5f\u6c38\u8fdc\u4e0d\u56de\u6545\u4e61\u3002\u201d\u96c5\u5fb7\u661f\u53d7\u4e86\u63d0\u6469\u8fbe\u591a\u7684\u91cd\u521b \u4e4b\u540e\uff0c\u8eab\u4f53\u6781\u5f31\uff0c\u4e00\u6025\u4e4b\u4e0b\uff0c\u8bf4\u5b8c\u4e86\u8fd9\u51e0\u53e5\u8bdd\u4fbf\u53c8\u660f\u8ff7\u4e86\u3002\u963f\u8fe6\u7f57\u5927\u60d1\u4e0d\u89e3\uff0c\u4e5f\u66fe\u6000\u7591\u8fc7\u5c3c \u6cca\u5c14\u738b\u5b50\u8bf4\u7684\u4e0d\u662f\u5b9e\u60c5\uff0c\u4f46\u4e00\u60f3\u4ee5\u5c3c\u6cca\u5c14\u738b\u5b50\u7684\u8eab\u4efd\uff0c\u7eb5\u7b97\u4ed6\u4e0e\u6842\u534e\u751f\u6709\u79c1\u4ec7\uff0c\u4e5f\u4e0d\u5fc5\u5047\u624b \u81ea\u5df1\uff0c\u8f6c\u5ff5\u4e00\u60f3\uff0c\u83ab\u975e\u662f\u96c5\u5fb7\u661f\u738b\u5b50\u8981\u4eb2\u81ea\u4e0e\u6842\u534e\u751f\u7b97\u8d26\uff0c\u6216\u8005\u662f\u8fd8\u8981\u95ee\u4ed6\u53e3\u4f9b\uff1f\u963f\u8fe6\u7f57\u56e0 \u4e3a\u8981\u8d76\u7740\u4e0a\u8def\uff0c\u540c\u65f6\u5fa1\u533b\u89c1\u96c5\u5fb7\u661f\u660f\u5012\uff0c\u4ed6\u4eec\u542c\u4e0d\u61c2\u5370\u5ea6\u8bdd\uff0c\u867d\u7136\u4e0d\u77e5\u963f\u8fe6\u7f57\u4e0e\u96c5\u5fb7\u661f\u8bf4\u4e9b \u4ec0\u4e48\uff0c\u4f46\u5374\u57cb\u6028\u4ed6\u4e0d\u8be5\u523a\u6fc0\u75c5\u4eba\uff0c\u5e76\u7acb\u5373\u5c06\u96c5\u5fb7\u661f\u79fb\u5165\u9759\u5ba4\uff0c\u5728\u4eca\u540e\u6570\u65e5\u4e4b\u5185\uff0c\u4e25\u7981\u5916\u4eba\u63a2 \u8bbf\u3002\u56e0\u6b64\u963f\u8fe6\u7f57\u53ea\u597d\u7559\u4e0b\u4e00\u5c01\u4fe1\u7ed9\u96c5\u5fb7\u661f\uff0c\u4fbf\u5306\u5306\u8d70\u4e86\u3002 \u3000\u3000\u963f\u8fe6\u7f57\u6c99\u5c0f\u8def\u8d76\u8fc7\u4e86\u6842\u534e\u751f\u7684\u524d\u5934\uff0c\u5728\u94c1\u7d22\u6865\u8fb9\u5b88\u5019\uff0c\u4f9d\u7167\u5c3c\u6cca\u5c14\u738b\u5b50\u7684\u8bf4\u8bdd\u5b9e\u884c\u7a81 \u88ad\uff0c\u90a3\u77e5\u6842\u534e\u751f\u7684\u529f\u529b\u4e0e\u4ed6\u534a\u65a4\u516b\u4e24\uff0c\u51cc\u7a7a\u4e92\u640f\uff0c\u53cc\u65b9\u90fd\u7ed9\u9707\u4e0b\u6df1\u8c37\uff0c\u5e78\u800c\u96c5\u5fb7\u661f\u6709\u8bdd\u5429\u5490 \u5728\u524d\uff0c\u6545\u6b64\u963f\u8fe6\u7f57\u53ea\u662f\u4e0d\u8bb8\u6842\u534e\u751f\u51fa\u53bb\uff0c\u5e76\u6ca1\u6709\u4f24\u4ed6\u6027\u547d\u3002 \u3000\u3000\u96c5\u5fb7\u661f\u738b\u5b50\u5c06\u8fd9\u4e00\u6bb5\u7ecf\u8fc7\u8be6\u7531\u8bf4\u5b8c\u4e4b\u540e\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u53c8\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u4ef6\u66f4\u91cd\u8981\u7684\u4e8b\u60c5\uff0c \u516c\u4e3b\u8003\u9009\u5c04\u4e4b\u671f\uff0c\u8ddd\u4eca\u53ea\u6709\u4e09\u5929\u4e86\u3002\u542c\u5c3c\u6cca\u5c14\u56fd\u738b\u8bf4\uff0c\u8fd9\u4e00\u6b21\u65e0\u8bba\u5982\u4f55\u8981\u9009\u51fa\u9a79\u9a6c\uff0c\u5373\u7b97\u6240 \u6709\u7684\u4eba\u90fd\u4e0d\u5408\u516c\u4e3b\u7814\u5b9a\u7684\u6807\u51c6\uff0c\u4e5f\u8981\u62e9\u4f18\u800c\u53d6\u3002\u6842\u5144\uff0c\u8fd9\u4e00\u4e2a\u673a\u4f1a\uff0c\u4f60\u4e07\u4e07\u4e0d\u80fd\u9519\u8fc7\u3002\u6211\u73b0 \u5728\u4e5f\u77e5\u9053\u5f97\u975e\u5e38\u6e05\u695a\u4e86\uff0c\u516c\u4e3b\u6df1\u5fc3\u6240\u7231\u7684\u4f60\u4e00\u4eba\uff01\u201d \u3000\u3000\u6842\u534e\u751f\u82e6\u7b11\u9053\uff1a\u201c\u5c0f\u5f1f\u5f97\u4ee5\u9003\u51fa\u751f\u5929\uff0c\u5b9e\u5df2\u4e07\u5e78\u3002\u5bf9\u5a5a\u4e8b\u4e0d\u6562\u518d\u5728\u5984\u60f3\u3002\u201d\u96c5\u5fb7\u661f\u9053\uff1a \u201c\u8fd9\u5374\u4e3a\u4f55\uff1f\u5144\u53f0\u82e5\u628a\u5c0f\u5f1f\u5f53\u505a\u670b\u53cb\uff0c\u8bf7\u628a\u7591\u96be\u76f4\u8bf4\u3002\u201d\u6842\u534e\u751f\u9053\uff1a\u201c\u4e00\u6765\u6211\u6ca1\u6709\u62a5\u540d\uff0c\u4e8c \u6765\u53ea\u6709\u4e09\u5929\uff0c\u5c0f\u5f1f\u4e5f\u8d76\u4e0d\u56de\u53bb\u3002\u201d \u3000\u3000\u96c5\u5fb7\u661f\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5c0f\u5f1f\u65e9\u5df2\u66ff\u4f60\u62a5\u540d\uff0c\u90a3\u6735\u96ea\u83b2\u5f53\u505a\u4f60\u7684\u793c\u7269\u516c\u4e3b\u4e5f\u6536\u4e0b\u4e86\u3002\u73b0\u5728\u5728 \u56fd\u738b\u7684\u5fc3\u76ee\u4e4b\u4e2d\uff0c\u4e5f\u65e9\u5df2\u628a\u4f60\u5f53\u4f5c\u672a\u6765\u7684\u4e58\u9f99\u4f73\u5a7f\u4e86\u5462\uff01\u201d\u539f\u6765\u96c5\u5fb7\u661f\u75c5\u6108\u4e4b\u540e\uff0c\u5728\u56fd\u738b\u9762 \u524d\u6781\u529b\u4e3a\u6842\u534e\u751f\u6109\u626c\uff0c\u56fd\u738b\u77e5\u9053\u96c5\u5fb7\u661f\u56de\u56fd\u5728\u5373\uff0c\u516c\u4e3b\u53c8\u75f4\u604b\u6842\u534e\u751f\uff0c\u4fbf\u53ea\u5f97\u7531\u4ed6\u53bb\u4e86\u3002 \u3000\u3000\u96c5\u5fb7\u661f\u53c8\u7b11\u9053\uff1a\u201c\u81f3\u4e8e\u4f60\u6015\u5728\u4e09\u5929\u4e4b\u5185\uff0c\u8d76\u4e0d\u56de\u52a0\u5fb7\u6ee1\u90fd\uff0c\u8fd9\u4e2a\u4f60\u4e0d\u7528\u62c5\u5fc3\uff0c\u53ea\u8981\u4f60\u529f \u529b\u6062\u590d\uff0c\u8fd9\u51e0\u767e\u91cc\u7684\u8def\u7a0b\u7b97\u5f97\u4e86\u4ec0\u4e48\uff1f\u4f60\u8fd9\u6b21\u56e0\u5c0f\u5f1f\u5f97\u7978\uff0c\u4fbf\u7740\u843d\u5728\u5c0f\u5f1f\u8eab\u4e0a\uff0c\u7ba1\u6559\u4f60\u56e0\u7978 \u5f97\u798f\u4fbf\u662f\uff01\u201d \u3000\u3000\u9f99\u53f6\u5927\u5e08\u63a5\u9053\uff1a\u201c\u6211\u7684\u5e08\u5f1f\u5c06\u4f60\u56f0\u5728\u5e7d\u8c37\uff0c\u81f4\u4eca\u4f60\u529f\u529b\u6d88\u5931\uff0c\u73b0\u5728\u8001\u8896\u4fbf\u5373\u66ff\u4f60\u6062\u590d\u529f \u529b\uff0c\u4f5c\u4e3a\u8d54\u7f6a\uff01\u201d\u8bf4\u7f62\u5c06\u624b\u638c\u8d34\u5728\u6842\u534e\u751f\u7684\u80cc\u5fc3\uff0c\u6842\u534e\u751f\u7acb\u523b\u611f\u5230\u6709\u4e00\u80a1\u70ed\u6d41\uff0c\u901a\u8fc7\u5168\u8eab\u3002 \u3000\u3000\u9f99\u53f6\u5927\u5e08\u9053\uff1a\u201c\u6211\u73b0\u5728\u7528\u4f5b\u5bb6\u7684\u6bd7\u7f57\u901a\u5173\u5927\u6cd5\u4e3a\u4f60\u5145\u5b9e\u5185\u529b\uff0c\u8fd9\u6bd4\u4f60\u4eec\u4e2d\u56fd\u6b66\u5b66\u4e2d\u7684\u63a8 \u8840\u8fc7\u5bab\uff0c\u529f\u6548\u66f4\u5927\u3002\u4e2d\u5370\u7684\u4e0a\u4e58\u5185\u529f\u867d\u7a0d\u6709\u5dee\u5f02\uff0c\u4f46\u539f\u7406\u5374\u662f\u4e00\u6837\uff0c\u4f60\u5982\u4e0d\u61c2\u745c\u4f3d\u529f\u592b\uff0c\u5c31 \u7528\u4f60\u4eec\u4e2d\u56fd\u7684\u5410\u7eb3\u6cd5\u8fd0\u6c14\u4e0e\u6211\u914d\u5408\uff0c\u4e5f\u4e00\u6837\u53ef\u4ee5\u3002\u201d\u6842\u534e\u751f\u9053\uff1a\u201c\u5f1f\u5b50\u7ecf\u8fc7\u963f\u8fe6\u7f57\u534a\u6708\u6765\u7684 \u6307\u6559\uff0c\u5bf9\u5370\u5ea6\u7684\u4e0a\u4e58\u5185\u529f\uff0c\u4e0d\u6562\u8bf4\u5df2\u7ecf\u53c2\u900f\uff0c\u4f46\u81ea\u4fe1\u4e5f\u7a0d\u7aa5\u95e8\u5f84\u3002\u201d\u9f99\u53f6\u5927\u5e08\u7b11\u9053\uff1a\u201c\u4f60\u5bf9 \u4e8e\u6b66\u5b66\u8fd8\u771f\u6709\u6167\u6839\uff0c\u745c\u4f3d\u6c14\u529f\u4e2d\u7684\u6258\u7389\u6cc9\u4e00\u5f0f\uff0c\u4f60\u61c2\u5f97\u5417\uff1f\u201d\u6842\u534e\u751f\u9053\uff1a\u201c\u7565\u89e3\u76ae\u6bdb\u3002\u201d\u9f99 \u53f6\u5927\u5e08\u9053\u4e86\u4e00\u4e2a\u201c\u597d\u201d\u5b57\uff0c\u5c06\u6842\u534e\u751f\u5012\u63d0\u8d77\u6765\uff0c\u5934\u4e0b\u811a\u4e0a\uff0c\u53cc\u638c\u8d34\u7740\u5b83\u7684\u8db3\u5fc3\uff0c\u4ee5\u6781\u7cbe\u7eaf\u7684 \u5185\u529f\uff0c\u66ff\u4ed6\u6062\u590d\u771f\u529b\u3002\u539f\u6765\u8db3\u8ddf\u7684\u7a74\u9053\u79f0\u4e3a\u201c\u6d8c\u6cc9\u7a74\u201d\uff0c\u4ea6\u79f0\u201c\u7389\u6cc9\u7a74\u201d\uff0c\u5934\u4e0b\u811a\u4e0a\uff0c\u6545\u6b64 \u79f0\u4e3a\u201c\u6258\u7389\u6cc9\u201d\u3002 \u3000\u3000\u9f99\u53f6\u5927\u5e08\u4e00\u9762\u66ff\u4ed6\u6062\u590d\u771f\u529b\uff0c\u4e00\u9762\u6307\u70b9\u4ed6\u745c\u4f3d\u6c14\u529f\u7684\u8bc0\u7a8d\uff0c\u6842\u534e\u751f\u5fc3\u9886\u795e\u4f1a\uff0c\u4f9d\u6cd5\u8fd0 \u529f\uff0c\u4f46\u89c9\u4e00\u80a1\u6781\u67d4\u548c\u7684\u529b\u9053\uff0c\u4ece\u201c\u6d8c\u6cc9\u7a74\u201d\u6162\u6162\u4e0a\u5347\uff0c\u6240\u81f3\u4e4b\u5904\uff0c\u8212\u670d\u975e\u5e38\uff0c\u5927\u7ea6\u7ecf\u8fc7\u4e86\u4e00 \u987f\u996d\u7684\u529f\u592b\uff0c\u5df2\u662f\u529f\u884c\u767e\u7a74\uff0c\u6c14\u900f\u91cd\u5173\uff0c\u9f99\u53f6\u5927\u5e08\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5c45\u58eb\u8bd5\u770b\u529f\u529b\u5982\u4f55\uff1f\u201d \u3000\u3000\u53cc\u638c\u4e00\u679a\uff0c\u6842\u534e\u751f\u624b\u5fc3\u63a5\u5730\uff0c\u8dc3\u8d77\u8eab\u6765\uff0c\u968f\u624b\u4e00\u634f\uff0c\u5c06\u4e00\u5757\u77f3\u5934\u634f\u788e\uff0c\u529f\u529b\u975e\u4f46\u6ca1\u6709\u51cf \u9000\uff0c\u800c\u4e14\u80dc\u4f3c\u4ece\u524d\uff0c\u66f4\u503c\u5f97\u5e86\u5e78\u7684\u662f\uff0c\u6842\u534e\u751f\u7ecf\u8fc7\u4e86\u4e0e\u963f\u8fe6\u7f57\u7684\u5341\u4f59\u65e5\u6076\u6218\uff0c\u53c8\u5f97\u4e86\u9f99\u53f6\u5927 \u5e08\u7684\u6307\u70b9\uff0c\u4ece\u6b64\u5bf9\u4e2d\u5370\u4e24\u5927\u6d41\u6d3e\u7684\u4e0a\u4e58\u5185\u529f\uff0c\u5747\u66f9\u53c2\u900f\uff0c\u4ee5\u540e\u878d\u5408\u8d2f\u901a\uff0c\u7ec8\u4e8e\u6210\u4e3a\u4e00\u4ee3\u7684\u6b66 \u5b66\u5927\u5bb6\uff0c\u8fd9\u662f\u540e\u8bdd\uff0c\u6309\u4e0b\u4e0d\u8868\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u9b44\u5bd2\u5149\u5251\u300b\u2014\u2014\u7b2c\u5341\u4e00\u7ae0 \u6881\u7fbd\u751f\u300a\u51b0\u9b44\u5bd2\u5149\u5251\u300b \u7b2c\u5341\u4e00\u7ae0 \u3000\u3000\u4e14\u8bf4\uff0c\u6842\u534e\u751f\u56e0\u7978\u5f97\u798f\uff0c\u5bf9\u9f99\u53f6\u5927\u5e08\u8877\u5fc3\u62dc\u8c22\u3002\u9f99\u53f6\u5927\u5e08\u9053\uff1a\u201c\u4e0d\u5fc5\u591a\u793c\uff0c\u4f60\u5e94\u8be5\u8d76\u8def \u5566\u3002\u4ece\u8c37\u540e\u9762\u90a3\u6761\u5c71\u8def\u8d70\uff0c\u8def\u65e2\u96be\u884c\uff0c\u53c8\u8017\u8d39\u65f6\u65e5\uff0c\u8fd8\u662f\u4ece\u6c34\u6d1e\u7a7f\u51fa\u53bb\u7684\u597d\u3002\u201d\u6842\u534e\u751f\u770b\u4e86 \u5df4\u52d2\u4e00\u773c\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u4ee5\u6211\u548c\u963f\u8fe6\u7f57\u7b49\u7684\u6b66\u529f\uff0c\u62fc\u4e2a\u5168\u8eab\u6e7f\u900f\uff0c\u7a7f\u8fc7\u7011\u5e03\u8fd8\u4e0d\u7b97\u96be\uff0c\u4f46\u5df4 \u52d2\u5e74\u8fc8\u529b\u8870\uff0c\u7eb5\u7136\u6709\u4eba\u80cc\u4ed6\u51fa\u53bb\uff0c\u4ed6\u4e5f\u7ecf\u53d7\u4e0d\u8d77\u8fd9\u7011\u5e03\u7684\u51b2\u51fb\u4e4b\u529b\u3002\u201d\u9f99\u53f6\u5927\u5e08\u4f3c\u4e4e\u770b\u7834\u6842 \u534e\u751f\u7684\u5fc3\u610f\uff0c\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u90fd\u8bf7\u653e\u5fc3\uff0c\u4f46\u968f\u6211\u6765\uff01\u201d\u6842\u534e\u751f\u8fd9\u624d\u60f3\u8d77\u9f99\u53f6\u5927\u5e08\u548c\u96c5\u5fb7\u661f\u4e43\u662f \u4ece\u524d\u9762\u6765\u7684\uff0c\u770b\u4ed6\u4eec\u7684\u8863\u5c65\uff0c\u53ea\u662f\u978b\u9762\u7565\u6e7f\uff0c\u5fc3\u4e2d\u751a\u89c9\u5947\u602a\u3002 \u3000\u3000\u9f99\u53f6\u5927\u5e08\u9886\u961f\u5148\u884c\uff0c\u5230\u4e86\u7011\u5e03\u524d\u9762\uff0c\u53cc\u638c\u5408\u4ec0\uff0c\u5ffd\u5730\u4e00\u5206\uff0c\u4f46\u89c1\u90a3\u80a1\u7011\u5e03\u4e5f\u968f\u7740\u4ed6\u7684\u638c \u52bf\u5de6\u53f3\u5206\u5f00\uff0c\u4f73\u534e\u751f\u548b\u820c\u96be\u4e0b\u3002\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u5982\u6b64\u7edd\u4e16\u795e\u529f\uff0c\u5373\u51cc\u672a\u98ce\u5927\u4fa0\u590d\u751f\uff0c\u6050\u6015\u4ea6\u4e0d \u8fc7\u5982\u662f\u3002\u201d\u4e00\u884c\u4e94\u4f17\uff0c\u5c31\u5728\u96f7\u9e23\u822c\u7684\u7011\u5e03\u4e4b\u4e0b\uff0c\u7a7f\u51fa\u4e86\u8c37\u53e3\uff0c\u9f99\u53f6\u5927\u5e08\u80cc\u8d77\u4e86\u5df4\u52d2\uff0c\u6500\u767b\u90a3 \u767e\u4e08\u7684\u5ced\u58c1\uff0c\u6842\u534e\u751f\u7b49\u4eba\u5404\u81ea\u65bd\u5c55\u4e0a\u4e58\u8f7b\u529f\uff0c\u8ddf\u5728\u540e\u9762\u3002\u7ed3\u679c\u8fd8\u662f\u9f99\u53f6\u5927\u5e08\u5148\u5230\uff0c\u7b49\u4e86\u8bb8 \u4e45\uff0c\u6842\u534e\u751f\u3001\u963f\u8fe6\u7f57\u548c\u96c5\u5fb7\u661f\u624d\u76f8\u7ee7\u800c\u6765\u3002 \u3000\u3000\u6842\u534e\u751f\u88ab\u56f0\u5e7d\u8c37\uff0c\u7ecf\u5df2\u6708\u4f59\uff0c\u8fd9\u65f6\u91cd\u89c1\u5929\u65e5\uff0c\u604d\u5982\u9694\u4e16\uff0c\u96c5\u5fb7\u661f\u9053\uff1a\u201c\u5c0f\u5f1f\u8fd9\u6b21\u5f97\u4ee5\u7ed3 \u8bc6\u6842\u5144\uff0c\u6df1\u60c5\u539a\u8c0a\uff0c\u7ec8\u8eab\u4e0d\u5fd8\uff01\u201d\u4e0e\u6842\u534e\u751f\u4f9d\u4f9d\u4e0d\u820d\u7684\u9053\u522b\uff0c\u6842\u534e\u751f\u5bf9\u8fd9\u4f4d\u5f02\u56fd\u53cb\u4eba\uff0c\u4e5f\u662f \u540c\u6837\u7684\u96be\u820d\u3002\u4e34\u522b\u65f6\u96c5\u5fb7\u661f\u8bf4\u9053\uff1a\u201c\u636e\u5c0f\u5f1f\u6240\u77e5\uff0c\u516c\u4e3b\u7684\u6c42\u5a5a\u6614\u79bb\u591a\uff0c\u5176\u4e2d\u53ea\u6709\u6ce2\u65af\u7684\u6b66\u58eb \u9093\u5357\u906e\uff0c\u5144\u53f0\u5e94\u8be5\u7a0d\u52a0\u6ce8\u610f\u3002\u201d \u3000\u3000\u6842\u534e\u751f\u62dc\u522b\u4e86\u9f99\u53f6\u5927\u5e08\u548c\u96c5\u5fb7\u661f\uff0c\u8d76\u5230\u52a0\u5fb7\u6ee1\u90fd\uff0c\u6b63\u597d\u662f\u8003\u9009\u9a79\u9a6c\u7684\u524d\u5915\u3002 \u3000\u3000\u5341\u4e00\u534e\u5802\u6b66\u58eb\u62da\u751f\u6b7b\u6842\u534e\u751f\u4ecd\u4f4f\u5728\u5df4\u52d2\u5bb6\u4e2d\uff0c\u7b2c\u4e8c\u5929\u4fbf\u53c2\u52a0\u521d\u8bd5\uff0c\u5e94\u8bd5\u8005\u5171\u6709\u4e00\u767e\u5eff\u56db \u4eba\uff0c\u521d\u8bd5\u5171\u6709\u4e09\u9053\u9898\u76ee\uff0c\u4e00\u662f\u9a70\u9a6c\u5c04\u7bad\uff0c\u4e8c\u662f\u4e3e\u5343\u65a4\u77f3\u62c5\uff0c\u4e09\u662f\u6597\u5fa1\u56ed\u72ee\u864e\u3002\u8bd5\u5f13\u7bad\uff0c\u6842\u534e \u751f\u8fde\u53d1\u5341\u8282\u90fd\u4e2d\u7ea2\u5fc3\u3002\u4e3e\u5343\u65a4\u77f3\u62c5\uff0c\u6842\u534e\u751f\u53ea\u7528\u4e86\u5355\u81c2\u4e4b\u529b\uff0c\u4fbf\u5c06\u77f3\u62c5\u4e3e\u8d77\uff0c\u7ed5\u573a\u4e09\u531d\u3002\u6700 \u540e\u6597\u5fa1\u56ed\u731b\u72ee\uff0c\u6842\u534e\u751f\u4ec5\u4ec5\u7528\u4e86\u4e00\u652f\u9999\u7684\u65f6\u523b\uff0c\u4fbf\u628a\u72ee\u5b50\u964d\u4f0f\uff0c\u5c06\u5b83\u5f53\u505a\u9a6c\u9a91\uff1b\u521d\u8bd5\u4e09\u5173\uff0c \u4e0d\u8d39\u5439\u7070\u4e4b\u529b\uff0c\u5168\u90fd\u901a\u8fc7\u3002\u5176\u4f59\u7684\u4e00\u767e\u4e8c\u5341\u4e09\u4eba\uff0c\u5374\u88ab\u6dd8\u6c70\u8fc7\u534a\uff0c\u5269\u4e0b\u6765\u80fd\u591f\u53c2\u52a0\u590d\u8bd5\u7684\u53ea \u6709\u56db\u5341\u4e03\u4eba\u3002 \u3000\u3000\u6842\u534e\u751f\u521d\u8bd5\u5408\u683c\u4e4b\u540e\uff0c\u5c3c\u6cca\u5c14\u738b\u5b50\u4fbf\u4eb2\u81ea\u5230\u5df4\u52c3\u5bb6\u6765\u5411\u4ed6\u9053\u559c\uff0c\u5e76\u9001\u4e86\u8bb8\u591a\u793c\u7269\u7ed9\u4ed6\u3002 \u3000\u3000\u6842\u534e\u751f\u660e\u660e\u77e5\u9053\u4ed6\u662f\u6545\u610f\u62c9\u62e2\uff0c\u4e5f\u660e\u660e\u77e5\u9053\u8fd9\u6b21\u56f0\u5e7d\u8c37\uff0c\u4e43\u662f\u4ed6\u4ece\u4e2d\u6311\u62e8\u963f\u8fe6\u7f57\uff0c\u4f46\u788d \u4e8e\u56fd\u738b\u60c5\u9762\uff0c\u81ea\u5df1\u53c8\u662f\u5916\u56fd\u5ba2\u4eba\uff0c\u8fd9\u4e00\u5207\u90fd\u53ea\u597d\u653e\u5728\u5fc3\u4e2d\uff0c\u4e0d\u4e88\u8bf4\u7834\uff0c\u5bf9\u738b\u5b50\u4e5f\u865a\u4e0e\u59d4\u86c7\u3002 \u4e00\u5230\u738b\u5b50\u8d70\u540e\uff0c\u4fbf\u6258\u5df4\u52d2\u5c06\u90a3\u4e9b\u793c\u7269\u90fd\u5206\u4e0e\u7a77\u4eba\uff0c\u9152\u8089\u7b49\u98df\u7269\u5219\u9632\u5b83\u6709\u6bd2\uff0c\u5168\u90e8\u6254\u4e0b\u6c9f\u6e20\u3002 \u3000\u3000\u521d\u8bd5\u8fc7\u540e\uff0c\u7b2c\u4e09\u5929\u4e3e\u884c\u590d\u8bd5\uff0c\u590d\u8bd5\u53ea\u6709\u4e00\u4e2a\u9898\u76ee\uff0c\u7531\u516c\u4e3b\u6d3e\u51fa\u56db\u4e2a\u5bab\u5973\uff0c\u8f6e\u6d41\u4e0e\u5404\u4eba\u6bd4 \u5251\uff0c\u80fd\u80dc\u5f97\u8fc7\u5bab\u5973\u7684\u4fbf\u6709\u8d44\u683c\u53c2\u52a0\u6700\u540e\u9009\u62d4\uff0c\u9898\u76ee\u4f3c\u6613\u5b9e\u96be\uff0c\u8fd9\u56db\u4e2a\u5bab\u5973\u7684\u5251\u672f\u90fd\u662f\u516c\u4e3b\u4eb2 \u6388\uff0c\u800c\u4e14\u4e5f\u90fd\u4f1a\u7528\u51b0\u5757\u795e\u5f39\uff0c\u53c2\u52a0\u8986\u8bd5\u7684\u56db\u5341\u4e03\u4eba\uff0c\u6709\u4e8c\u5341\u4e03\u6597\u5251\u5931\u8d25\uff0c\u6709\u5341\u4e09\u4eba\u88ab\u51b0\u9b44\u795e \u5f39\u6253\u4e0b\u64c2\u53f0\u3002\u6842\u534e\u751f\u78b0\u5230\u7684\u90a3\u4e2a\u5bab\u5973\uff0c\u6b63\u662f\u90a3\u665a\u4ece\u5fa1\u6797\u519b\u603b\u7ba1\u522b\u5885\u63a5\u4ed6\u8fdb\u5bab\u7684\u90a3\u4e2a\u5b9b\u5170\u661f\uff0c \u4f73\u534e\u751f\u4ee5\u91d1\u521a\u6307\u7684\u529f\u592b\u5f39\u5f00\u4e86\u56db\u9897\u51b0\u9b44\u795e\u5f39\uff0c\u5230\u7b2c\u5341\u4e8c\u62db\u4fbf\u5c06\u5979\u6253\u8d25\u3002\u5b9b\u5170\u661f\u4e0b\u53f0\u4e4b\u65f6\uff0c\u5411 \u4ed6\u5fae\u5fae\u4e00\u7b11\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u516c\u4e3b\u660e\u4f60\u80cc\u719f\u5979\u8fed\u4f60\u7684\u90a3\u51e0\u672c\u4e66\u3002\u201d \u3000\u3000\u9664\u4e86\u6842\u534e\u751f\u5916\uff0c\u8fd8\u6709\u516d\u4e2a\u6c42\u5a5a\u8005\u4e5f\u90fd\u901a\u8fc7\u4e86\u590d\u8bd5\u3002\u7b2c\u4e00\u4e2a\u662f\u6ce2\u65af\u6b66\u58eb\u9093\u5357\u906e\uff0c\u7b2c\u4e8c\u4e2a\u662f \u5e0c\u814a\u4e00\u4e2a\u5c0f\u90e8\u7684\u738b\u5b50\u514b\u96f7\u65af\uff0c\u7b2c\u4e09\u4e2a\u662f\u5c3c\u6cca\u5c14\u7684\u672c\u56fd\u6b66\u58eb\u62c9\u6c57\u56fe\uff0c\u7b2c\u56db\u4e2a\u662f\u5370\u5ea6\u6b66\u58eb\u6469\u519c\uff0c \u7b2c\u4e94\u4e2a\u662f\u963f\u5bcc\u6c57\u7684\u4e00\u4e2a\u7267\u573a\u573a\u4e3b\u6717\u7eb3\uff0c\u7b2c\u516d\u4e2a\u662f\u4e2d\u4e9a\u7ec6\u4e9a\u6492\u9a6c\u5c14\u7f55\u738b\u56fd\u7684\u4e00\u4e2a\u9752\u5e74\u516c\u7235\u54c8\u5df4 \u5fb7\u3002\u8fde\u6842\u534e\u751f\u5176\u662f\u58eb\u4eba\u3002\u56fd\u738b\u5c06\u4ed6\u4eec\u4e03\u4eba\u5b89\u987f\u5728\u8d35\u5bbe\u9986\u91cc\uff0c\u5bbe\u9986\u5efa\u5728\u745e\u626c\u5e03\u5c71\u5c71\u9e93\uff0c\u4e0b\u4e34\u590f \u4f55\u5fb7\u6cb3\uff0c\u98ce\u666f\u751a\u662f\u5e7d\u7f8e\u3002 \u3000\u3000\u4f4f\u8fdb\u5bbe\u9986\u7684\u8fd9\u4e00\u665a\uff0c\u56fd\u738b\u7279\u8d50\u7f8e\u9152\uff0c\u8ba9\u4ed6\u4eec\u5728\u5927\u5385\u6b22\u5bb4\uff0c\u4ee5\u524d\u8fd9\u4e03\u4e2a\u4eba\u5404\u4f4f\u4e00\u65b9\uff0c\u521d\u8bd5 \u590d\u8bd5\u65f6\u5404\u81ea\u4e3e\u884c\uff0c\u4eca\u665a\u65b9\u662f\u7b2c\u4e00\u6b21\u6b63\u5f0f\u4f1a\u9762\u3002\u6842\u534e\u751f\u8e0f\u5165\u5927\u5385\uff0c\u90a3\u516d\u4e2a\u4eba\u90fd\u5df2\u5148\u5230\u4e86\u3002\u6842\u534e \u751f\u548c\u4ed6\u4eec\u4e00\u4e00\u62db\u547c\uff0c\u53ea\u89c9\u5f97\u6bcf\u4e00\u4e2a\u4eba\u7684\u773c\u5149\u4e2d\uff0c\u90fd\u597d\u4f3c\u542b\u6709\u654c\u610f\u3002\u53ea\u6709\u5e0c\u814a\u738b\u5b50\u514b\u96f7\u65af\u6bd4\u8f83 \u9152\u8131\uff0c\u4f46\u795e\u60c5\u51b7\u50b2\uff0c\u548c\u90a3\u5e2e\u4eba\u4e5f\u4f3c\u662f\u843d\u843d\u96be\u5408\u3002\u8f6e\u5230\u9093\u5357\u906e\u65f6\uff0c\u6842\u534e\u751f\u6697\u6697\u7559\u5fc3\uff0c\u4f46\u89c1\u4ed6\u4e5f \u72e0\u72e0\u7684\u76ef\u7740\u81ea\u5df1\uff0c\u6842\u534e\u751f\u5fc3\u4e2d\u82e6\u7b11\uff0c\u60f3\u9053\uff1a\u201c\u4f55\u4ee5\u4ed6\u5bf9\u6211\u7279\u522b\u4ec7\u89c6\uff1f\u96be\u9053\u4ed6\u77e5\u9053\u4e86\u516c\u4e3b\u5c5e\u610f \u4e8e\u6211\u4e48\uff1f\u201d\u7559\u5fc3\u89c2\u5bdf\uff0c\u4e0d\u4f46\u662f\u9093\u5357\u906e\uff0c\u5176\u4f59\u5404\u4eba\uff0c\u9664\u4e86\u5e0c\u814a\u738b\u5b50\u514b\u96f7\u65af\u4e4b\u5916\uff0c\u4e5f\u90fd\u662f\u4e92\u76f8\u4ec7 \u89c4\u3002\u4e0d\u8fc7\u9093\u5357\u906e\u5bf9\u4ed6\uff0c\u7279\u522b\u8868\u73b0\u5f97\u663e\u7740\u3002\u6842\u534e\u751f\u5927\u5927\u65b9\u65b9\u7684\u548c\u9093\u5357\u906e\u63e1\u624b\u62db\u547c\uff0c\u53cc\u638c\u4e00\u63a5\uff0c \u6842\u534e\u751f\u5ffd\u89c9\u9093\u5357\u906e\u7528\u4e00\u80a1\u6781\u5927\u7684\u6697\u52b2\uff0c\u610f\u56fe\u9707\u4f24\u4ed6\u7684\u7ecf\u8109\u3002 \u3000\u3000\u6842\u534e\u751f\u4f6f\u4f5c\u4e0d\u77e5\uff0c\u6697\u4e2d\u4ee5\u6781\u4e0a\u4e58\u7684\u5185\u529f\u6d88\u89e3\u5bf9\u65b9\u731b\u52b2\uff0c\u9093\u5357\u906e\u7528\u529b\u4e00\u63e1\uff0c\u53ea\u89c9\u6842\u534e\u751f\u624b \u638c\u7ef5\u8f6f\uff0c\u8138\u4e0a\u4e1d\u6beb\u6ca1\u6709\u75db\u82e6\u7684\u79e4\u8272\uff0c\u9093\u5357\u906e\u8bd5\u4e0d\u51fa\u6842\u534e\u751f\u7684\u6df1\u6d45\uff0c\u5fc3\u4e2d\u8be7\u5f02\u975e\u5e38\u3002\u5e0c\u814a\u738b\u5b50 \u514b\u96f7\u65af\u5728\u4e00\u65c1\u6697\u6697\u53d1\u7b11\u3002\u9093\u5357\u906e\u53ea\u5f97\u653e\u5f00\u4e86\u624b\uff0c\u540c\u6842\u534e\u751f\u656c\u9152\u3002 \u3000\u3000\u8fd9\u51e0\u4e2a\u4eba\u56e0\u4e3a\u65e9\u5df2\u84c4\u610f\u5411\u516c\u4e3b\u6c42\u5a5a\uff0c\u90fd\u5b66\u4f1a\u4e86\u8bb2\u5c3c\u6cca\u5c14\u8bdd\uff0c\u5f7c\u6b64\u4e4b\u95f4\uff0c\u53ef\u4ee5\u81ea\u7531\u4ea4\u8c08\uff0c \u7136\u800c\u4ed6\u4eec\u5f7c\u6b64\u4e4b\u95f4\uff0c\u5374\u53c8\u4e92\u76f8\u654c\u89c6\uff0c\u573a\u9762\u663e\u5f97\u751a\u662f\u5c34\u5c2c\u3002\u9093\u5357\u906e\u5411\u6842\u534e\u751f\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a \u201c\u770b\u6765\u8fd9\u9a79\u9a6c\u975e\u541b\u83ab\u5c5e\uff0c\u6211\u4eec\u90fd\u662f\u6765\u966a\u8003\u7684\u4e86\u3002\u201d\u6842\u534e\u751f\u9053\uff1a\u201c\u90a3\u513f\u7684\u8bdd\uff0c\u516c\u4e3b\u6b66\u529f\u7edd\u4e16\uff0c \u53ea\u6015\u660e\u5929\u6211\u4e00\u4e0a\u53f0\u5c31\u4f1a\u7ed9\u4ed6\u6253\u4e0b\u6765\u3002\u201d\u5c3c\u6cca\u5c14\u6b66\u58eb\u62c9\u6c57\u56fe\u8bf4\u9053\uff1a\u201c\u6842\u5148\u751f\u6216\u4e0d\u81f3\u4e8e\uff0c\u5c0f\u5f1f\u5374 \u81ea\u95ee\u7edd\u4e0d\u662f\u516c\u4e3b\u7684\u5bf9\u624b\u3002\u6628\u65e5\u6211\u8d62\u90a3\u5bab\u5973\uff0c\u4e5f\u8d62\u5f97\u6781\u662f\u8270\u96be\u3002\u201d\u514b\u96f7\u65af\u5fae\u7b11\u9053\uff1a\u201c\u6211\u4fa5\u5e78\u5f97 \u4ee5\u53c2\u52a0\u51b3\u8d5b\uff0c\u4f46\u6c42\u4e00\u89c1\u516c\u4e3b\u7684\u59ff\u5bb9\uff0c\u4e8e\u613f\u5df2\u8db3\u3002\u6211\u4eec\u5e0c\u814a\u7684\u54f2\u5b66\u5bb6\u67cf\u62c9\u56fe\u6709\u4e00\u53e5\u8bdd\uff1a\u6700\u7f8e\u7684 \u4e1c\u897f\u5c31\u662f\u4f60\u4e0d\u80fd\u5360\u6709\u7684\u4e1c\u897f\u3002\u5360\u6709\u4e4b\u540e\uff0c\u7f8e\u611f\u53ef\u80fd\u53cd\u800c\u51cf\u5f31\u4e86\u3002\u516c\u4e3b\u4e4b\u7f8e\uff0c\u4e3e\u4e16\u95fb\u540d\uff0c\u6211\u5e0c \u671b\u89c1\u5979\u4e00\u9762\u4e4b\u540e\uff0c\u6709\u8fdc\u7559\u4e0b\u7f8e\u597d\u7684\u8bb0\u5fc6\uff0c\u81f3\u4e8e\u8bf4\u5230\u8981\u5a36\u516c\u4e3b\u4e3a\u59bb\uff0c\u6211\u8fde\u60f3\u4e5f\u4e0d\u6562\u60f3\uff0c\u53ea\u89c9\u5f97 \u5982\u4e00\u6d89\u60f3\uff0c\u4fbf\u662f\u5bf9\u5b83\u7684\u4eb5\u6e0e\u3002\u201d\u9093\u5357\u906e\u54fc\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u597d\u4e00\u4e2a\u8d85\u4e16\u7edd\u4fd7\u7684\u8bd7\u4eba\uff01\u201d\u6842\u534e\u751f \u5fc3\u4e2d\u5374\u9053\uff1a\u201c\u5bf9\u4ed6\u7f8e\u7684\u770b\u6cd5\uff0c\u4ed6\u4e5f\u4e0d\u6562\u8d5e\u540c\u3002\u53ea\u8981\u4e24\u5fc3\u5982\u4e00\uff0c\u4f60\u4f1a\u89c9\u5f97\u5979\u4e00\u5929\u6bd4\u4e00\u5929\u7f8e\uff0c\u73b0 \u5728\u7f8e\uff0c\u5c06\u6765\u4e5f\u7f8e\u3002\u534e\u7389\u59b9\u59b9\u7eb5\u7136\u5230\u4e86\u767d\u53d1\u82cd\u82cd\u7684\u65f6\u5019\uff0c\u5728\u6211\u7684\u5fc3\u76ee\u4e4b\u4e2d\uff0c\u4e5f\u5fc5\u5b9a\u4ecd\u7136\u662f\u4e00\u4e2a \u7edd\u4e16\u7684\u7f8e\u4eba\uff01\u4e0d\u8fc7\u4ed6\u8fd9\u756a\u8bdd\u5374\u786e\u5b9e\u662f\u5bf9\u516c\u4e3b\u8654\u8bda\u7684\u9882\u8d5e\uff0c\u4ed6\u4eec\u5e0c\u814a\u7684\u54f2\u7406\u4e5f\u786e\u662f\u8010\u4eba\u5bfb \u5473\u3002\u201d\u4e0d\u77e5\u4e0d\u89c9\uff0c\u5bf9\u514b\u96f7\u65af\u6709\u4e86\u597d\u611f\u3002 \u3000\u3000\u56fd\u738b\u7684\u4f7f\u8005\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u8bf8\u4f4d\u5bf9\u6211\u56fd\u516c\u4e3b\u7684\u8d5e\u7f8e\uff0c\u73b0\u5728\u6211\u4ee3\u8868\u56fd\u738b\u5411\u8bf8\u4f4d\u5404\u656c\u4e00\u676f\u3002\u201d \u3000\u3000\u7b2c\u4e00\u676f\u4fbf\u662f\u5148\u656c\u6842\u534e\u751f\uff0c\u6842\u534e\u751f\u6beb\u4e0d\u5728\u610f\u7684\u4e00\u676f\u559d\u5c3d\uff0c\u89c1\u4ed6\u656c\u5230\u9093\u5357\u906e\u65f6\uff0c\u9093\u5357\u906e\u7684\u8138 \u4e0a\u5ffd\u9732\u51fa\u8be7\u5f02\u7684\u7b11\u5bb9\uff0c\u6842\u534e\u751f\u5fc3\u4e2d\u4e00\u52a8\uff0c\u8bd5\u4e00\u8fd0\u6c14\uff0c\u7adf\u4f3c\u7565\u6709\u963b\u6ede\u3002 \u3000\u3000\u9152\u7b75\u5c06\u6563\uff0c\u516c\u4e3b\u8d34\u8eab\u7684\u4f8d\u5973\u5b9b\u5170\u661f\u5ffd\u7136\u5230\u6765\uff0c\u4e5f\u5411\u5404\u4eba\u8bf4\u9053\uff0c\u662f\u5949\u4e86\u516c\u4e3b\u4e4b\u547d\uff0c\u752b\u6765\u656c \u9152\uff0c\u4f17\u4eba\u5927\u559c\uff0c\u7eb7\u7eb7\u5e72\u676f\uff0c\u8f6e\u5230\u6842\u534e\u751f\u65f6\uff0c\u4ece\u5b9b\u5170\u661f\u624b\u4e0a\u63a5\u8fc7\u9152\u676f\u65f6\uff0c\u5b9b\u5170\u661f\u6084\u6084\u7684\u585e\u7ed9\u4ed6 \u4e00\u4e2a\u5c0f\u7eb8\u56e2\u3002 \u3000\u3000\u6842\u534e\u751f\u559d\u5b8c\u4e86\u9152\uff0c\u6025\u5fd9\u63a8\u8bf4\u4e0d\u80dc\u9152\u529b\uff0c\u6709\u70b9\u5934\u75db\uff0c\u5148\u56de\u623f\u95f4\u6b47\u606f\u3002\u6253\u5f00\u90a3\u5c0f\u7eb8\u56e2\u4e00\u770b\uff0c \u91cc\u9762\u6709\u4e00\u74e3\u5929\u5c71\u96ea\u83b2\uff0c\u7eb8\u4e0a\u6709\u51e0\u884c\u5b57\u5199\u9053\uff1a\u201c\u7236\u738b\u9001\u6765\u7684\u9152\uff0c\u662f\u7ecf\u8fc7\u6211\u5802\u5144\u4e4b\u624b\u7684\uff0c\u6211\u6015\u4ed6 \u6709\u6240\u4e0d\u5229\u4e8e\u4f60\uff0c\u7279\u9001\u6765\u96ea\u83b2\u4e00\u74e3\uff0c\u5e76\u8bf7\u4f60\u4ee5\u540e\u4e8b\u4e8b\u5f53\u5fc3\u3002\u201d\u6842\u534e\u751f\u5927\u5403\u4e00\u60ca\uff0c\u679c\u7136\u89c9\u5f97\u6709\u70b9 \u795e\u9b42\u56f0\u5026\uff0c\u6025\u5fd9\u5c06\u96ea\u83b2\u56bc\u4e0b\uff0c\u5e76\u4ee5\u745c\u4f3d\u6c14\u529f\u8fd0\u8f6c\u771f\u6c14\uff0c\u8fc7\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u624d\u89c9\u7cbe\u795e\u6e05\u723d\u3002\u539f\u6765 \u90a3\u4e2a\u4f7f\u8005\u4e5f\u662f\u4e0e\u738b\u5b50\u4e32\u901a\u7684\uff0c\u9152\u5012\u4e0d\u662f\u6bd2\u9152\uff0c\u8fd9\u5e76\u975e\u738b\u5b50\u4e0d\u6562\u4e0b\u6bd2\uff0c\u800c\u662f\u6015\u95f9\u51fa\u4e8b\u6765\uff0c\u5404\u56fd \u7684\u6c42\u5a5a\u8005\u90fd\u8fd8\u5728\u6b64\uff0c\u6709\u635f\u5c3c\u6cca\u5c14\u5a01\u8a89\u3002\u4f46\u9152\u58f6\u5374\u662f\u5185\u85cf\u673a\u5173\uff0c\u659f\u7ed9\u6842\u534e\u751f\u7684\u90a3\u676f\u9152\uff0c\u4e43\u662f \u201c\u767e\u65e5\u9189\u201d\uff0c\u7eb5\u7136\u5185\u529f\u591a\u597d\uff0c\u996e\u4e86\u4e4b\u540e\uff0c\u4e0d\u81f3\u5373\u884c\u9189\u5012\uff0c\u4f46\u7cbe\u795e\u5374\u5fc5\u5b9a\u4e0d\u652f\uff0c\u975e\u8fc7\u6570\u65e5\u4e0d\u80fd \u590d\u539f\u3002 \u3000\u3000\u6842\u534e\u751f\u6697\u53eb\u201c\u597d\u9669\u201d\uff0c\u8df5\u51fa\u623f\u95e8\uff0c\u53ea\u542c\u5f97\u5385\u4e2d\u8fd8\u5728\u95f9\u9152\u3002\u514b\u96f7\u65af\u6b63\u5728\u5f39\u4ed6\u7684\u4e03\u5f26\u7434\u3002 \u3000\u3000\u7434\u58f0\u60a0\u626c\uff0c\u5f00\u59cb\u65f6\u67d4\u548c\u4e4b\u6781\uff0c\u50cf\u662f\u60c5\u4eba\u7684\u8d5e\u7f8e\u8bd7\uff1b\u7ee7\u800c\u9ad8\u4ea2\u6fc0\u52a8\uff0c\u53c8\u4f3c\u662f\u51fa\u5f81\u524d\u5915\u7684\u8a93 \u8f9e\u3002\u514b\u96f7\u65af\u62b1\u7740\u4e03\u5f26\u7434\u8df3\u821e\uff0c\u5ffd\u800c\u653e\u58f0\u9ad8\u6b4c\uff0c\u82cd\u51c9\u51c4\u60cb\uff0c\u5531\u5f97\u4eba\u4eba\u5fc3\u5f26\u98a4\u52a8\uff0c\u5c3c\u6cca\u5c14\u6b66\u58eb\u62c9 \u6c57\u56fe\u9053\uff1a\u201c\u4f60\u5531\u7684\u662f\u4ec0\u4e48\u6b4c\uff1f\u201d\u514b\u96f7\u65af\u89c1\u6709\u4eba\u6b23\u8d4f\uff0c\u5f39\u5f97\u66f4\u52a0\u8d77\u52b2\uff0c\u7b54\u9053\uff1a\u201c\u662f\u6211\u4eec\u5e0c\u814a\u4e00 \u4e2a\u65e0\u540d\u8bd7\u4eba\u7684\u8bdd\uff0c\u8fd9\u9996\u8bd7\u662f\u8d5e\u7f8e\u53e4\u4ee3\u7684\u8273\u540e\u6d77\u4f26\u7684\uff0c\u5e0c\u814a\u8bf8\u90a6\u66fe\u4e3a\u5979\u6253\u8fc7\u4e00\u6b21\u5341\u5e74\u6218\u4e89\u3002\u201d \u3000\u3000\u62c9\u6c57\u56fe\u751a\u611f\u5174\u8da3\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u5531\u5f97\u5f88\u597d\u8f6c\uff0c\u53ef\u60dc\u6211\u542c\u4e0d\u61c2\u3002\u4f60\u7528\u5c3c\u6cca\u5c14\u8bdd\u518d\u5531\u4e00\u904d\uff0c\u8ba9 \u5927\u5bb6\u542c\u542c\u3002\u201d\u514b\u96f7\u65af\u672c\u6765\u662f\u4e00\u4e2a\u8bd7\u4eba\uff0c\u4e0d\u6687\u601d\u7d22\uff0c\u7acb\u523b\u5c06\u8fd9\u9996\u8d5e\u7f8e\u8bd7\u7528\u5c3c\u6cca\u5c14\u8bdd\u5531\u51fa\u6765\uff0c\u8fd9 \u9996\u8bd7\u7f8e\u4e3d\u4e4b\u6781\uff0c\u6842\u534e\u751f\u542c\u5f97\u51fa\u4e86\u795e\uff0c\u5728\u5fc3\u4e2d\u6697\u6697\u5c06\u5b83\u8bd1\u6210\u4e2d\u56fd\u7684\u6587\u5b57\u3002\u6b4c\u8bcd\u7684\u5927\u610f\u662f\uff1a \u3000\u3000\u201c\u4f60\u662f\u7c73\u5170\u7684\u9999\u82b1\uff0c\u9ad8\u5362\u7684\u7389\u6842\uff1a \u3000\u3000\u4f60\u662f\u5357\u975e\u7684\u739b\u7391\uff0c\u5370\u5ea6\u7684\u83e9\u63d0\uff1a \u3000\u3000\u5927\u5730\u4e0a\u6240\u6709\u7684\u4e1c\u897f\u90fd\u4e0d\u8db3\u4e0e\u4f60\u6bd4\u62df \u3000\u3000\u5929\u4e0a\u7684\u5973\u795e\u4e5f\u4e3a\u4f60\u5931\u53bb\u4e86\u5149\u8f89\uff01 \u3000\u3000\u4f60\u988a\u4e0a\u7684\u7b11\u5bb9\u50cf\u521d\u5f00\u7684\u73ab\u7470\uff0c \u3000\u3000\u96c4\u5e08\u5341\u4e07\u613f\u4e3a\u4f60\u89c6\u6b7b\u5982\u5f52\uff01\u201d \u3000\u3000\u4e00\u66f2\u672a\u7ec8\uff0c\u5ffd\u542c\u5f97\u201c\u788e\u201d\u7684\u4e00\u58f0\uff0c\u9093\u5357\u906e\u5c06\u4e00\u4e2a\u9152\u676f\u7528\u529b\u63b7\u53bb\uff0c\u73bb\u7483\u676f\u788e\u6210\u7247\u7247\uff0c\u5c06\u514b \u96f7\u65af\u7684\u7434\u5f26\u90fd\u5272\u5f97\u5bf8\u5bf8\u65ad\u4e86\uff0c\u6842\u534e\u751f\u5728\u697c\u68af\u53e3\u671b\u4e0b\u6765\uff0c\u89c1\u72b6\u4e5f\u4e0d\u7981\u5403\u4e86\u4e00\u60ca\uff0c\u9093\u5357\u906e\u7684\u8fd9\u624b \u7edd\u6280\u786e\u662f\u60ca\u4eba\uff0c\u7528\u529b\u7684\u5747\u5300\uff0c\u5999\u5230\u6beb\u5cad\uff0c\u73bb\u7483\u676f\u521a\u521a\u788e\u6210\u4e03\u7247\uff0c\u800c\u6bcf\u4e00\u7247\u73bb\u7483\u53c8\u6070\u6070\u5c06\u4e00\u6839 \u7434\u5f26\u4ece\u4e2d\u95f4\u5272\u65ad\uff0c\u8fd9\u6bd4\u8d77\u4e2d\u56fd\u6b66\u672f\u4e2d\u201c\u6ee1\u5929\u5316\u96e8\u201d\u7684\u6697\u5668\u624b\u6cd5\uff0c\u8981\u96be\u5f97\u591a\u591a\uff01 \u3000\u3000\u4e03\u5f26\u7434\u54d1\u7136\u65e0\u58f0\uff0c\u514b\u96f7\u65af\u6014\u4e86\u4e00\u6014\uff0c\u53ea\u542c\u5f97\u9093\u5357\u906e\u5927\u9a82\u9053\uff1a\u201c\u9b3c\u53eb\u4ec0\u4e48\uff1f\u4f60\u8981\u6b7b\u4fbf\u6b7b\uff0c \u8001\u5b50\u53ef\u4e0d\u966a\u4f60\uff1f\u201d\u514b\u96f7\u65af\u5927\u6012\u9053\uff1a\u201c\u4f60\u4e0d\u53d8\u542c\u4fbf\u5f00\u4e86\u8033\u6735\uff01\u600e\u4e48\u6253\u574f\u4e86\u6211\u7684\u4e03\u5f26\u7434\uff1f\u201d\u9093\u5357 \u906e\u559d\u9053\uff1a\u201c\u4f60\u518d\u591a\u8bf4\u4e00\u53e5\uff0c\u6211\u4fbf\u5c06\u4f60\u4e5f\u50cf\u4e03\u5f26\u7434\u4e00\u6837\u6253\u4e2a\u7a00\u70c2\uff01\u201d\u514b\u96f7\u65af\u62b1\u8d77\u4e03\u5f26\u7434\u559d\u9053\uff1a \u201c\u4f60\u6562\uff1f\u201d\u9093\u5357\u906e\u53eb\u9053\uff1a\u201c\u6211\u6709\u4ec0\u4e48\u4e0d\u6562\uff1f\u201d\u624b\u4e00\u626c\uff0c\u4e24\u679a\u5927\u7ea2\u82f9\u679c\u53c8\u8131\u624b\u98de\u51fa\uff0c\u5c06\u514b\u96f7\u65af \u7684\u4e03\u5f26\u7434\u6253\u843d\u5730\u4e0a\u3002\u5370\u5ea6\u6b66\u58eb\u6469\u519c\u540c\u60c5\u514b\u96f7\u65af\uff0c\u987a\u624b\u62ff\u8d77\u9910\u684c\u4e0a\u7684\u9c7c\u53c9\u4fbf\u5411\u9093\u5357\u906e\u98de\u63b7\uff0c\u963f \u5bcc\u6c57\u7684\u6c42\u5a5a\u8005\u6717\u7eb3\u4e5f\u5e2e\u7740\u514b\u96f7\u65af\u9a82\u9053\uff1a\u201c\u5c82\u6709\u6b64\u7406\uff0c\u4e16\u4e0a\u7adf\u6709\u4f60\u8fd9\u6837\u86ee\u4e0d\u8bb2\u7406\u7684\u4e1c\u897f\uff01\u201d\u9093 \u5357\u906e\u54c8\u54c8\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e5f\u5f39\u7ed9\u4f60\u4eec\u542c\u542c\uff01\u201d\u5341\u6307\u75be\u5f39\uff0c\u5c06\u6469\u519c\u63b7\u6765\u7684\u5200\u53c9\u90fd\u5f39\u5f97\u77e5\u7bad\u98de \u56de\uff0c\u6717\u7eb3\u51b7\u4e0d\u53ca\u9632\uff0c\u7adf\u7ed9\u4e00\u628a\u9910\u5200\u524a\u6389\u4e86\u534a\u8fb9\u8033\u6735\u3002\u9093\u5357\u906e\u5927\u53eb\u9053\uff1a\u201c\u597d\u6781\uff0c\u597d\u6781\uff01\u4eca\u665a\u54b1 \u4eec\u5c31\u5148\u6765\u4e00\u573a\u51b3\u6597\u3002\u54c8\u5df4\u5fb7\uff0c\u4f60\u6709\u80c6\u4e48\uff1f\u201d\u6492\u9a6c\u5c14\u7f55\u7684\u516c\u7235\u54c8\u5df4\u5fb7\u53eb\u9053\uff1a\u201c\u9093\u5357\u906e\uff0c\u6211\u6765\u5e2e \u4f60\uff01\u201d\u5927\u5385\u5185\u51e0\u4e2a\u6c42\u5a5a\u8005\u767b\u65f6\u6253\u6210\u4e00\u56e2\uff0c\u53ea\u6709\u5c3c\u6cca\u5c14\u7684\u6b66\u58eb\u62c9\u6c57\u56fe\u56e0\u4e3a\u4efd\u5c5e\u4e3b\u4eba\uff0c\u4e0d\u6562\u53c2 \u52a0\uff0c\u8dd1\u51fa\u5385\u53bb\u5927\u58f0\u547c\u5524\u3002 \u3000\u3000\u6842\u534e\u751f\u5fc3\u9053\uff1a\u201c\u5440\uff0c\u4ed6\u4eec\u600e\u7684\u559d\u5f97\u9189\u6210\u8fd9\u6837\uff1f\u201d\u8dd1\u4e0b\u697c\u68af\u4e00\u770b\uff0c\u53ea\u89c1\u9093\u5357\u906e\u76ee\u9732\u51f6\u5149\uff0c \u62f3\u52bf\u521a\u731b\u4e4b\u6781\uff0c\u6fc0\u6218\u4e2d\u6717\u7eb3\u4e2d\u4e86\u4e00\u62f3\uff0c\u6447\u6447\u6b32\u5012\uff0c\u6469\u519c\u53cd\u638c\u4e00\u5288\uff0c\u6253\u4e0d\u4e2d\u9093\u5357\u906e\uff0c\u5374\u628a\u54c8\u5df4 \u5fb7\u7684\u8138\u6253\u5f97\u5f00\u4e86\u82b1\uff0c\u9093\u5357\u906e\u8d81\u52bf\u4e00\u62f3\u5411\u6469\u519c\u6253\u53bb\uff0c\u6469\u519c\u4f1a\u745c\u4f3d\u529f\u592b\uff0c\u9093\u5357\u906e\u62f3\u5934\u4ece\u4ed6\u80a9\u4e0a\u6ed1 \u8fc7\uff0c\u5c06\u4e00\u4e2a\u5927\u82b1\u74f6\u6253\u5f97\u7c89\u788e\uff01 \u3000\u3000\u6492\u9a6c\u5c14\u7f55\u7684\u6c42\u5a5a\u8005\u54c8\u5df4\u5fb7\u5e2e\u52a9\u9093\u5357\u906e\uff0c\u62f3\u98ce\u864e\u864e\uff0c\u51f6\u6269\u5f02\u5e38\uff0c\u4ed6\u89c1\u9093\u5357\u906e\u88ab\u6469\u519c\u7f20\u4f4f\uff0c \u4fbf\u6765\u653b\u51fb\u514b\u96f7\u65af\uff0c\u514b\u96f7\u65af\u6e29\u6587\u5c14\u96c5\uff0c\u62f3\u672f\u5374\u662f\u8d85\u5999\u975e\u5e38\uff0c\u54c8\u5df4\u5fb7\u8fd0\u62f3\u5982\u98ce\uff0c\u4e00\u8fde\u6253\u4e86\u4e03\u516b\u62f3 \u90fd\u6ca1\u6709\u6253\u4e2d\u4ed6\uff0c\u6700\u540e\u4e00\u62f3\u54c8\u5df4\u5fb7\u548c\u8eab\u6251\u4e0a\uff0c\u5374\u88ab\u514b\u96f7\u65af\u4e00\u8bb0\u5206\u624b\u52fe\u62f3\uff0c\u54c8\u5df4\u5fb7\u7684\u53cc\u62f3\u90fd\u7834\u683c \u5f00\uff0c\u514b\u96f7\u65af\u8d81\u52bf\u62ff\u7740\u4ed6\u7684\u624b\u8155\uff0c\u5411\u540e\u4e00\u7269\uff0c\u559d\u9053\uff1a\u201c\u8fd8\u8981\u6253\u5417\uff1f\u201d\u6842\u534e\u751f\u6697\u6697\u559d\u91c7\uff0c\u5fc3\u4e2d\u60f3 \u9053\uff1a\u201c\u514b\u96f7\u65af\u8fd9\u4e00\u8bb0\u5206\u624b\u52fe\u62f3\uff0c\u8db3\u53ef\u4e0e\u4e2d\u56fd\u9e70\u722a\u95e8\u7684\u5927\u64d2\u62ff\u624b\u6bd4\u7f8e\uff01\u201d \u3000\u3000\u770b\u7740\u54c8\u5df4\u5fb7\u5c31\u8981\u88ab\u514b\u96f7\u65af\u5236\u670d\uff0c\u90a3\u77e5\u6b64\u4eba\u7cbe\u4e8e\u6454\u86df\u4e4b\u6280\uff0c\u811a\u5c16\u4e00\u53e5\uff0c\u514b\u96f7\u65af\u7acb\u8db3\u4e0d\u7a33\uff0c \u53cd\u800c\u8dcc\u5012\uff0c\u54c8\u5df4\u5fb7\u5de6\u624b\u4e00\u7a7f\uff0c\u4ece\u514b\u65af\u8098\u4e0b\u7a7f\u8fc7\uff0c\u53cd\u626d\u5b83\u7684\u624b\u8155\uff0c\u4e24\u4eba\u5728\u5730\u4e0a\u6eda\u4e86\u51e0\u6eda\uff0c\u53ea\u542c \u5f97\u201c\u5427\u631e\u201d\u96e8\u58f0\uff0c\u54c8\u5df4\u5fb7\u7740\u4e86\u4e00\u8bb0\u8033\u5149\uff0c\u514b\u96f7\u65af\u7684\u80c1\u4e0b\u4e5f\u88ab\u4ed6\u91cd\u91cd\u7684\u6253\u4e86\u4e00\u62f3\uff0c\u4e24\u4eba\u8df3\u4e86\u8d77 \u6765\uff0c\u54c8\u5df4\u5fb7\u5634\u5427\u4e00\u5f20\uff0c\u55b7\u51fa\u4e00\u53e3\u9c9c\u8840\uff0c\u539f\u6765\u4ed6\u5df2\u88ab\u514b\u96f7\u65af\u884c\u843d\u4e86\u4e24\u9897\u95e8\u7259\u3002\u54c8\u5df4\u5fb7\u5927\u58f0\u6012 \u9a82\uff0c\u4e24\u4eba\u53c8\u72e0\u72e0\u7ea6\u4e92\u76f8\u6251\u51fb\u3002 \u3000\u3000\u90a3\u8fb9\u53a2\u9093\u5357\u906e\u4e0e\u6469\u519c\u6076\u6218\uff0c\u4e5f\u662f\u51f6\u9669\u975e\u5e38\uff0c\u6469\u519c\u7684\u745c\u4f3d\u6c14\u529f\u5df2\u7ec3\u5230\u516b\u6210\u706b\u5019\uff0c\u4f46\u9093\u5357\u906e \u7684\u62f3\u52bf\u6709\u5982\u6392\u5c71\u5012\u6d77\u800c\u6765\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u6469\u519c\u5df2\u8fde\u4e2d\u4e86\u4e03\u516b\u62f3\uff0c\u867d\u7136\u6bcf\u4e00\u62f3\u90fd\u88ab\u4ed6\u4ee5\u4e0a\u4e58\u7684\u5370 \u5ea6\u5185\u529f\u5378\u5f00\u52b2\u529b\uff0c\u7ec8\u4e8e\u7981\u53d7\u4e0d\u4f4f\uff0c\u4e2d\u4e86\u7b2c\u516b\u638c\u4e4b\u540e\uff0c\u5df2\u662f\u5927\u6c57\u6dcb\u6f13\uff0c\u5934\u4e0a\u5192\u51fa\u70ed\u817e\u817e\u7684\u767d \u6c14\uff01\u6842\u534e\u751f\u4e43\u662f\u6b66\u672f\u7684\u5927\u884c\u5bb6\uff0c\u6700\u8fd1\u53c8\u53c2\u900f\u4e86\u5370\u5ea6\u5185\u529f\u7684\u79d8\u5965\uff0c\u89c1\u6b64\u60c5\u5f62\uff0c\u77e5\u9053\u6469\u519c\u7684\u771f\u529b \u6d88\u8017\u592a\u751a\uff0c\u518d\u6218\u4e0b\u53bb\uff0c\u5fc5\u7136\u4e0d\u662f\u9093\u5357\u906e\u7684\u5bf9\u624b\uff0c\u679c\u7136\u63a5\u7740\u4e00\u62f3\uff0c\u9093\u5357\u906e\u4fbf\u628a\u6469\u519c\u6253\u5f97\u6447\u6447\u6643 \u6643\uff0c\u9093\u5357\u906e\u6253\u5f97\u6027\u8d77\uff0c\u6469\u519c\u5df2\u7ecf\u8d25\u9000\uff0c\u4ed6\u5c45\u7136\u6251\u4e0a\u53bb\u53c8\u662f\u4e00\u62f3\uff01 \u3000\u3000\u6842\u534e\u751f\u5fc3\u9053\uff1a\u201c\u6211\u518d\u4e0d\u51fa\u53bb\uff0c\u53ea\u6015\u8981\u95f9\u51fa\u4eba\u547d\u6765\u4e86\uff01\u201d\u773c\u89c1\u9093\u5357\u906e\u62f3\u5934\u6b63\u5411\u6469\u519c\u7684\u5929\u7075 \u76d6\u843d\u4e0b\uff0c\u6842\u534e\u751f\u81ea\u697c\u68af\u4e0a\u51cc\u7a7a\u4e00\u8dc3\uff0c\u6377\u5982\u98de\u9e1f\uff0c\u4eba\u672a\u843d\u5730\uff0c\u53f3\u624b\u4e00\u52fe\uff0c\u5df2\u52fe\u7740\u4e86\u9093\u5357\u906e\u7684\u62f3 \u5934\uff0c\u5de6\u638c\u8f7b\u8f7b\u4e00\u63a8\uff0c\u4ee5\u7edd\u5999\u7684\u5de7\u52b2\u5c06\u6469\u519c\u63a8\u5f00\u3002 \u3000\u3000\u9093\u5357\u906e\u5927\u6012\u559d\u9053\uff1a\u201c\u597d\u5440\uff0c\u4f60\u4eec\u4ee5\u591a\u4e3a\u80dc\u5417\uff1f\u201d\u5de6\u62f3\u8fde\u73af\u653b\u51fa\uff0c\u53f3\u62f3\u4e5f\u8fd0\u52a8\u524d\u63a8\u3002\u6842\u534e \u751f\u4ee5\u5355\u638c\u4e4b\u529b\uff0c\u7adf\u7136\u63a5\u4e0d\u4f4f\u4ed6\u7684\u731b\u529b\uff0c\u53ea\u5f97\u653e\u5f00\u4e86\u5b83\u7684\u62f3\u5934\uff0c\u6b63\u60f3\u8bf4\u8bdd\uff0c\u7a81\u7136\u95f4\u54c8\u5df4\u5fb7\u548c\u6717 \u7eb3\u90fd\u540c\u65f6\u5411\u4ed6\u88ad\u6765\uff0c\u6842\u534e\u751f\u56e0\u4e3a\u65e9\u77e5\u9053\u54c8\u5df4\u5fb7\u662f\u9093\u5357\u906e\u7684\u4f19\u4f34\uff0c\u8dc3\u4e0b\u4e4b\u65f6\uff0c\u4fbf\u5df2\u9884\u9632\u4ed6\u7684\u88ad \u51fb\uff0c\u4f46\u90a3\u4e2a\u90ce\u7eb3\u521a\u624d\u5374\u662f\u5e2e\u514b\u96f7\u65af\u7684\uff0c\u800c\u4e14\u8fd8\u66fe\u88ab\u9093\u5357\u906e\u51fb\u4e2d\u4e00\u62f3\uff0c\u4ed6\u7a81\u7136\u5012\u6208\u53cd\u5411\uff0c\u6842\u534e \u751f\u5374\u662f\u610f\u6599\u4e0d\u53ca\uff0c\u51b7\u4e0d\u53ca\u9632\uff0c\u80cc\u5fc3\u7adf\u7136\u7ed3\u7ed3\u5b9e\u5b9e\u7684\u88ab\u4ed6\u6253\u4e86\u4e00\u62f3\u3002 \u3000\u3000\u514b\u96f7\u65af\u6012\u9053\uff1a\u201c\u4f60\u8fd9\u4e2a\u4eba\u600e\u4e48\u8fd9\u6837\u5351\u9119\uff01\u201d\u4e00\u4e2a\u8f6c\u8eab\uff0c\u51fa\u624b\u5982\u98ce\uff0c\u4e5f\u7ed3\u7ed3\u5b9e\u5b9e\u7684\u6253\u4e86\u6717 \u7eb3\u4e00\u62f3\uff0c\u6b63\u60f3\u518d\u6253\u54c8\u5df4\u5fb7\uff0c\u6842\u534e\u751f\u53eb\u9053\uff1a\u201c\u90fd\u8bf7\u4f4f\u624b\uff0c\u54b1\u4eec\u5728\u6b64\u4f5c\u5ba2\uff0c\u8fd9\u6837\u80e1\u95f9\uff0c\u5c82\u4e0d\u662f\u4eba \u7b11\u8bdd\u5417\uff1f\u201d \u3000\u3000\u9093\u5357\u906e\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u61c2\u4e0d\u61c2\u6b66\u58eb\u7684\u89c4\u77e9\uff0c\u4e3a\u7f8e\u4eba\u51b3\u6218\uff0c\u4e43\u662f\u6211\u4eec\u6b66\u58eb\u5149\u8363\u7684\u4f20\u7edf\uff0c\u6709\u4ec0 \u4e48\u7b11\u8bdd\uff1f\u54fc\uff0c\u4f60\u81ea\u5df1\u80c6\u602f\uff0c\u8fd8\u6562\u7b11\u8bdd\u6211\u4eec\uff1f\u4f60\u4eec\u4e2d\u56fd\u4eba\u90fd\u662f\u80c6\u5c0f\u9b3c\uff01\u201d\u8bdd\u672a\u8bf4\u5b8c\uff0c\u8d81\u7740\u6842\u534e \u751f\u672a\u66fe\u7559\u610f\uff0c\u51b7\u4e0d\u9632\u7167\u7740\u6842\u534e\u751f\u7684\u80f8\u53e3\u53c8\u662f\u4e00\u62f3\u3002 \u3000\u3000\u6842\u534e\u751f\u6709\u5fc3\u8bd5\u4ed6\u6c14\u529b\uff0c\u6325\u81c2\u4e00\u683c\uff0c\u4f46\u542c\u5f97\u201c\u84ec\u201d\u7684\u4e00\u58f0\uff0c\u9093\u5357\u906e\u5012\u9000\u4e09\u6b65\uff0c\u6842\u534e\u751f\u4e0a\u8eab \u4e5f\u5fae\u5fae\u4e00\u6643\uff0c\u5fc3\u5934\u4e00\u51db\uff0c\u60f3\u9053\uff1a\u201c\u8fd9\u6076\u6597\u4e86\u534a\u5929\uff0c\u5c45\u7136\u8fd8\u6709\u5982\u6b64\u6c14\u529b\uff0c\u786e\u662f\u4e0d\u5bb9\u5c0f\u89c6\u3002\u201d \u3000\u3000\u9093\u5357\u906e\u66f4\u662f\u5403\u60ca\uff0c\u539f\u6765\u8fd9\u4e00\u573a\u51b3\u6597\uff0c\u4e43\u662f\u4ed6\u548c\u5c3c\u6cca\u5c14\u738b\u5b50\u5b89\u6392\u597d\u4e86\uff0c\u8981\u7b49\u6842\u534e\u751f\u4e0a\u9493 \u7684\u3002\u5176\u4e2d\u8fd8\u6709\u4e24\u4e2a\u540c\u8c0b\u8005\u4e43\u662f\u6492\u9a6c\u5c14\u7f55\u7684\u54c8\u5df4\u5fb7\u548c\u963f\u5bcc\u6c57\u7684\u6717\u7eb3\u3002\u4ed6\u4eec\u672c\u65e9\u5c31\u60f3\u5411\u6842\u534e\u751f\u6311 \u98df\u7684\uff0c\u53ea\u56e0\u6842\u534e\u751f\u672a\u5f85\u5e2d\u6563\uff0c\u4fbf\u5148\u56de\u623f\u6b47\u606f\uff0c\u6545\u6b64\u4ed6\u4eec\u6539\u5411\u514b\u96f7\u65af\u6311\uff0c\u6709\u610f\u5c06\u6842\u534e\u751f\u5f15\u51fa \u6765\u3002\u5e76\u6709\u610f\u53eb\u6717\u7eb3\u5e2e\u514b\u96f7\u65af\uff0c\u4f7f\u5f97\u6842\u534e\u751f\u5bf9\u4ed6\u4e0d\u5982\u9632\u5907\u3002\u81f3\u4e8e\u6469\u519c\u5219\u662f\u5bf9\u4ed6\u4eec\u7684\u9634\u8c0b\u6beb\u4e0d\u77e5 \u60c5\uff0c\u6fc0\u4e8e\u4e49\u6124\u53bb\u5e2e\u514b\u96f7\u65af\u7684\u3002\u9093\u5357\u906e\u4ed6\u4eec\u4e5f\u4fbf\u8d81\u6b64\u65f6\u673a\uff0c\u4e00\u77f3\u4e24\u9e1f\uff0c\u987a\u624b\u5c06\u6469\u519c\u548c\u514b\u96f7\u65af\u6253 \u5f97\u91cd\u4f24\u3002 \u3000\u3000\u9093\u5357\u906e\u81ea\u6043\u662f\u6ce2\u65af\u7684\u7b2c\u4e00\u540d\u6b66\u58eb\uff0c\u53c8\u77e5\u9053\u6842\u534e\u751f\u5df2\u996e\u4e86\u738b\u5b50\u7684\u201c\u767e\u65e5\u9189\u201d\u836f\u9152\uff0c\u66f4\u517c\u6709 \u6717\u7eb3\u5077\u88ad\u6210\u529f\uff0c\u6ee1\u4ee5\u4e3a\u6842\u534e\u751f\u4e0d\u582a\u4e00\u51fb\uff0c\u90a3\u77e5\u53cc\u81c2\u4e00\u652f\uff0c\u81ea\u5df1\u7684\u795e\u529b\u7adf\u7136\u7ed9\u4ed6\u6bd4\u4e86\u4e0b\u53bb\uff0c\u7109 \u5f97\u4e0d\u60ca\u3002\u4f46\u89c1\u6842\u534e\u751f\u8e0f\u4e0a\u4e00\u6b65\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u9093\u5357\u906e\u6211\u63a5\u53d7\u4f60\u7684\u6311\u6218\uff0c\u54b1\u4eec\u4e00\u5bf9\u4e00\u51fa\u4e2a\u8f93 \u8d62\u3002\u201d\u9093\u5357\u906e\u51f6\u6027\u5927\u51cf\uff0c\u773c\u73e0\u4e00\u8f6c\uff0c\u8bf4\u9053\uff1a\u201c\u5f88\u597d\uff0c\u54b1\u4eec\u5c31\u4ee5\u4e00\u652f\u7ea2\u70db\u4e3a\u9650\uff0c\u82e5\u662f\u4e00\u652f\u7ea2\u70db \u70e7\u5b8c\uff0c\u6211\u8fd8\u6253\u4f60\u4e0d\u5012\uff0c\u90a3\u4e48\u6211\u613f\u610f\u8bda\u5fc3\u7684\u548c\u4f60\u4ea4\u4e2a\u670b\u53cb\u3002\u201d\u6842\u534e\u751f\u60f3\u8d77\u63d0\u6469\u8fbe\u591a\u90a3\u665a\u548c\u4ed6\u6bd4 \u6b66\u7684\u60c5\u5f62\uff0c\u5fc3\u4e2d\u6697\u7b11\uff1a\u201c\u4f60\u8fd9\u53ae\u600e\u6bd4\u5f97\u4e0a\u63d0\u6469\u8fbe\u591a\uff0c\u5374\u5c45\u7136\u4e5f\u7528\u4ed6\u7684\u529e\u6cd5\u3002\u201d\u5176\u5b9e\u529e\u6cd5\u867d\u7136 \u76f8\u540c\uff0c\u5fc3\u60c5\u5374\u5b8c\u5168\u4e24\u6837\u3002\u63d0\u6469\u8fbe\u591a\u662f\u81ea\u91cf\u5728\u8721\u70db\u5b8c\u4e4b\u524d\uff0c\u5fc5\u5b9a\u80fd\u628a\u6842\u534e\u751f\u51fb\u5012\uff1a\u800c\u9093\u5357\u906e\u5374 \u662f\u6015\u8f93\u7ed9\u6842\u534e\u751f\uff0c\u4e0d\u8fc7\u81ea\u91cf\u5728\u70e7\u5b8c\u4e00\u652f\u814a\u70db\u7684\u65f6\u95f4\u4e4b\u5185\uff0c\u5927\u7ea6\u8fd8\u53ef\u4ee5\u652f\u6301\u5f97\u4f4f\u800c\u5df2\u3002 \u3000\u3000\u6842\u534e\u751f\u9053\uff1a\u201c\u597d\uff0c\u6211\u63a5\u53d7\u4f60\u7684\u6761\u4ef6\uff0c\u5728\u7ea2\u70db\u70e7\u5b8c\u4e4b\u540e\uff0c\u54b1\u4eec\u82e5\u662f\u4e0d\u5206\u80dc\u8d1f\uff0c\u6211\u4e5f\u613f\u548c\u4f60 \u4ea4\u4e2a\u670b\u53cb\u3002\u201d\u9093\u5357\u906e\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u54c8\u5df4\u5fb7\uff0c\u6717\u7eb3\uff0c\u8bf7\u4f60\u4eec\u6765\u4f5c\u516c\u8bc1\uff0c\u6211\u548c\u6842\u5148\u751f\u6bd4\u62f3\uff0c\u8c01\u82e5 \u662f\u52a8\u7528\u5175\u5668\u6216\u5176\u4ed6\u6697\u5668\uff0c\u5c31\u662f\u4e0d\u5c0a\u91cd\u81ea\u5df1\u6b66\u58eb\u7684\u8eab\u4efd\uff0c\u5e76\u5e94\u8be5\u7acb\u5373\u5224\u8f93\u3002\u201d\u539f\u6765\u4ed6\u5df2\u4ece\u5c3c\u6cca \u5c14\u738b\u5b50\u4e4b\u5904\uff0c\u6253\u542c\u5230\u6842\u534e\u751f\u6709\u4e00\u628a\u5b9d\u5251\uff0c\u540c\u65f6\u4ed6\u4ece\u6b66\u5b66\u7684\u5178\u7c4d\u4e2d\uff0c\u77e5\u9053\u4e2d\u56fd\u5251\u5ba2\u6697\u5668\u7684\u82b1\u6837 \u6700\u591a\uff0c\u4ed6\u867d\u7136\u4e5f\u61c2\u5f97\u4f7f\u7528\u6697\u5668\uff0c\u5374\u662f\u4e0d\u6562\u548c\u4e2d\u56fd\u6765\u7684\u5251\u5ba2\u8f83\u91cf\uff0c\u6545\u6b64\u53ea\u63d0\u51fa\u8f83\u91cf\u62f3\u811a\u4e0a\u7684\u529f \u592b\u3002 \u3000\u3000\u54c8\u5df4\u5fb7\u5e94\u4e86\u4e00\u58f0\uff0c\u58f0\u97f3\u542b\u7cca\u4e0d\u6e05\uff0c\u800c\u4e14\u98a4\u6296\u5f97\u975e\u5e38\u5389\u5bb3\uff0c\u539f\u6765\u4ed6\u5df2\u88ab\u514b\u96f7\u65af\u6253\u5f97\u91cd\u4f24\uff0c \u8fde\u4e24\u9897\u95e8\u7259\u4e5f\u6253\u6298\u4e86\u3002\u4e0d\u8fc7\u4ed6\u8fd8\u662f\u6323\u624e\u7740\u8d70\u51fa\u6765\u3002\u81f3\u4e8e\u6717\u7eb3\uff0c\u5374\u4e0d\u89c1\u4e86\u8e2a\u8ff9\uff0c\u514b\u96f7\u65af\u6447\u6447\u6643 \u6643\u7684\u8d70\u51fa\u6765\u9053\uff1a\u201c\u6211\u6765\u4f5c\u8bc1\u4eba\u3002\u54c8\u5df4\u5fb7\u554a\uff0c\u9093\u5357\u906e\u662f\u4f60\u7684\u670b\u53cb\uff0c\u6842\u5148\u751f\u662f\u6211\u7684\u670b\u53cb\uff0c\u54b1\u4eec\u5927 \u5bb6\u53ef\u90fd\u4e0d\u8bb8\u504f\u8892\u554a\uff01\u201d\u4ed6\u5728\u521a\u624d\u6df7\u6218\u4e4b\u65f6\uff0c\u8138\u4e0a\u88ab\u6293\u4e86\u51e0\u9053\u4f24\u75d5\uff0c\u5c0f\u8179\u53c8\u4e2d\u4e86\u54c8\u5df4\u5fb7\u7684\u4e00\u8bb0 \u91cd\u62f3\uff0c\u53d7\u4f24\u4e4b\u91cd\uff0c\u5b9e\u4e0d\u5728\u54c8\u5df4\u5fb7\u4e4b\u4e0b\u3002 \u3000\u3000\u4e24\u4eba\u6446\u597d\u9635\u52bf\uff0c\u5982\u7bad\u5728\u5f26\uff0c\u6b63\u5f85\u516c\u8bc1\u53d1\u51fa\u53e3\u4ee4\u3002\u514b\u96f7\u65af\u5fae\u5fae\u4e00\u7b11\uff0c\u62ff\u8d77\u4e00\u4e2a\u94f6\u76d8\uff0c\u4e00\u67c4 \u9910\u5200\uff0c\u5411\u6842\u534e\u751f\u8bf4\u9053\uff1a\u201c\u6842\u5148\u751f\uff0c\u6211\u5728\u4f5c\u516c\u8bc1\u4e4b\u524d\uff0c\u5148\u4ee5\u670b\u53cb\u7684\u8eab\u4efd\u9884\u795d\u4f60\u7684\u80dc\u5229\u3002\u6211\u51c6\u5907 \u4e3a\u4f60\u594f\u4e50\u3002\u201d\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u6572\u4e86\u94f6\u76d8\u4e00\u4e0b\uff0c\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u4e3a\u4e86\u4e2d\u56fd\u548c\u5e0c\u814a\u7684\u5149\u8363\uff0c\u6211\u7684\u670b \u53cb\uff0c\u8bf7\u4f60\u594b\u52c7\u4f5c\u6218\uff01\u201d \u3000\u3000\u9093\u5357\u906e\u5706\u7741\u53cc\u773c\uff0c\u559d\u9053\uff1a\u201c\u4f60\u7b49\u7740\u594f\u4e27\u4e50\u5427\uff01\u201d\u514b\u96f7\u65af\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u5fc5\u5982\u6b64\u6050\u60e7\uff0c\u6211\u7684 \u670b\u53cb\u4e0d\u4f1a\u5c06\u4f60\u6253\u6b7b\u7684\u3002\u201d\u5f53\u3001\u5f53\u3001\u5f53\uff0c\u6572\u4e86\u4e09\u4e0b\u94f6\u76d8\uff0c\u6570\u5230\u201c\u4e09\u201d\u5b57\uff0c\u9093\u5357\u906e\u53cc\u81c2\u7b95\u5f20\uff0c\u835e \u7136\u4e00\u538b\uff0c\u4fbf\u8981\u64d2\u62ff\u6842\u534e\u751f\u7684\u53cc\u8155\uff0c\u6842\u534e\u751f\u8eab\u6cd5\u4f55\u7b49\u8f7b\u7075\uff0c\u8fde\u8863\u8896\u4e5f\u6ca1\u6709\u7ed9\u4ed6\u89e6\u7740\uff0c\u4e00\u4e2a\u8f6c \u8eab\uff0c\u75be\u7528\u201c\u659c\u6302\u5355\u97ad\u201d\u4e00\u5f0f\uff0c\u5207\u4ed6\u8109\u95e8\uff0c\u9093\u5357\u906e\u53cc\u624b\u865a\u62b1\uff0c\u500f\u5730\u4ea4\u53c9\u4e00\u526a\uff0c\u6a2a\u80a2\u4e00\u649e\uff0c\u4fbf\u653b \u4e4b\u4e2d\u53c8\u542b\u6709\u5316\u52bf\uff0c\u6842\u534e\u751f\u5fc3\u9053\uff1a\u201c\u6ce2\u65af\u4e0e\u4e2d\u56fd\u5370\u5ea6\u4e00\u6837\uff0c\u540c\u662f\u6587\u660e\u53e4\u56fd\uff0c\u5728\u6b66\u672f\u4e0a\u679c\u7136\u4e5f\u8db3 \u4ee5\u81ea\u6210\u6d41\u6d3e\uff0c\u8fd9\u4e00\u62db\u5c31\u662f\u4e2d\u56fd\u6b66\u5b66\u4e2d\u6240\u6ca1\u6709\u7684\u3002\u201d \u3000\u3000\u9093\u5357\u906e\u89e3\u5f00\u4e86\u6842\u534e\u751f\u7684\u653b\u52bf\uff0c\u7acb\u5373\u53cd\u653b\uff0c\u62f3\u52bf\u7c97\u6269\uff0c\u624b\u811a\u8d77\u5904\uff0c\u5168\u5e26\u52b2\u98ce\uff0c\u529f\u529b\u7adf\u4f3c\u4e0d \u5728\u4e2d\u56fd\u4e00\u6d41\u7684\u5927\u529b\u91d1\u521a\u624b\u4e4b\u4e0b\uff0c\u6842\u534e\u751f\u6709\u610f\u5c06\u4ed6\u620f\u5f04\uff0c\u4ed7\u7740\u8f7b\u529f\u6bd4\u4ed6\u9ad8\u660e\u5f97\u591a\uff0c\u5c55\u5f00\u4e86\u201c\u516b \u5366\u6e38\u8eab\u638c\u6cd5\u201d\u4e0e\u4ed6\u6e38\u6597\uff0c\u4f46\u89c1\u4eba\u5f71\u7ffb\u817e\uff0c\u62f3\u98ce\u864e\u864e\uff0c\u6842\u534e\u751f\u4ee5\u7edd\u9876\u7ecf\u7075\u7684\u8eab\u6cd5\uff0c\u5de6\u8fb9\u4e00\u515c\uff0c \u53f3\u9762\u4e00\u7ed5\uff0c\u8fdb\u5982\u733f\u7334\u7a9c\u679d\uff0c\u9000\u82e5\u9f99\u86c7\u75be\u8d70\uff0c\u8d77\u5982\u9e70\u96c6\u98de\u5929\uff0c\u843d\u82e5\u731b\u864e\u4f0f\u5730\uff0c\u4f46\u89c1\u56db\u9762\u516b\u65b9\u90fd \u662f\u6842\u534e\u751f\u7684\u8eab\u5f71\uff0c\u9093\u5357\u906e\u5012\u5438\u4e86\u4e00\u53e3\u51c9\u6c14\uff0c\u4ed6\u90a3\u6781\u521a\u731b\u7684\u638c\u6cd5\u672c\u6765\u662f\u4e3b\u653b\u7684\uff0c\u8fd9\u65f6\u5374\u4e0d\u5f97\u4e0d \u7f29\u5c0f\u5708\u5b50\uff0c\u62a4\u8eab\u9632\u5b88\uff0c\u9976\u662f\u62f3\u52bf\u7ef5\u5bc6\uff0c\u906e\u62e6\u5f97\u98ce\u96e8\u4e0d\u900f\uff0c\u5728\u4e0d\u5230\u4e00\u523b\u949f\u7684\u65f6\u95f4\u4e4b\u5185\uff0c\u8fd8\u662f\u4e2d \u4e86\u6842\u534e\u751f\u51e0\u638c\u3002\u4f46\u56e0\u4e3a\u6842\u534e\u751f\u4f7f\u7684\u662f\u6e38\u8eab\u638c\u6cd5\uff0c\u8bb2\u7a76\u8f7b\u7075\u7ffb\u52a8\uff0c\u4e00\u6cbe\u5373\u8d70\uff0c\u638c\u529b\u4e0d\u591f\u9053\u52b2\uff0c \u9093\u5357\u906e\u6709\u4e00\u8eab\u6a2a\u7ec3\u7684\u529f\u592b\uff0c\u94dc\u76ae\u94c1\u9aa8\uff0c\u6467\u4e86\u51e0\u638c\uff0c\u867d\u7136\u4e5f\u89c9\u5f97\u75bc\u75db\uff0c\u5374\u4e5f\u8fd8\u633a\u5f97\u4f4f\u3002 \u3000\u3000\u6fc0\u6218\u4e2d\u6842\u534e\u751f\u773c\u5149\u5076\u7136\u4e00\u77a5\uff0c\u5ffd\u89c1\u514b\u96f7\u65af\u8138\u4e0a\u73b0\u51fa\u7126\u6025\u7684\u795e\u60c5\uff0c\u539f\u6765\u5728\u4e0d\u77e5\u4e0d\u89c9\u4e4b\u95f4\uff0c \u90a3\u6839\u7ea2\u70db\u5df2\u70e7\u4e86\u8fc7\u534a\u4e86\u3002\u6842\u534e\u751f\u5fc3\u9053\uff1a\u201c\u514b\u96f7\u65af\u738b\u5b50\u76fc\u671b\u6211\u5f97\u80dc\uff0c\u6211\u53ef\u4e0d\u80fd\u8f9c\u8d1f\u4ed6\u7684\u5fc3 \u610f\u3002\u201d\u638c\u6cd5\u4e00\u53d8\uff0c\u75be\u62a2\u653b\u52bf\uff0c\u638c\u5288\u63e9\u6233\u5168\u90fd\u662f\u300a\u8fbe\u6469\u79d8\u7b08\u300b\u4e2d\u7684\u4e0a\u4e58\u6b66\u529f\uff0c\u9093\u5357\u906e\u6e10\u6e10\u6709\u70b9 \u62db\u67b6\u4e0d\u4f4f\uff0c\u6842\u534e\u751f\u89d1\u51c6\u4e00\u4e2a\u7834\u7efd\uff0c\u559d\u4e00\u58f0\u201c\u7740\uff01\u201d\u970e\u773c\u4e4b\u95f4\uff0c\u8fde\u70b9\u4e86\u4ed6\u201c\u5929\u67a2\u201d\u3001\u201c\u5730 \u9619\u201d\u3001\u201c\u5f52\u85cf\u201d\u3001\u201c\u98ce\u5e9c\u201d\u3001\u201c\u7389\u6795\u201d\u4e94\u5904\u5927\u7a74\uff0c\u5bfb\u5e38\u4e4b\u58eb\uff0c\u88ab\u70b9\u4e2d\u4e00\u5904\uff0c\u5c31\u5fc5\u5c06\u6655\u5012\u65e0 \u7591\uff0c\u7eb5\u6709\u4e00\u7b49\u4e00\u7684\u6b66\u529f\uff0c\u88ab\u4e00\u8fde\u70b9\u4e2d\u8fd9\u4e94\u5904\u5927\u7a74\uff0c\u4e5f\u96be\u7981\u53d7\u3002\u5374\u4e0d\u6599\u9093\u5357\u906e\u505c\u4e86\u4e00\u58f0\uff0c\u597d\u50cf \u9189\u6c49\u4e00\u822c\u8e48\u8e48\u8ddf\u8ddf\u7684\u5012\u9000\u51e0\u6b65\uff0c\u867d\u7136\u662f\u9762\u8272\u60e8\u767d\uff0c\u6447\u6447\u6643\u6643\uff0c\u5374\u5e76\u6ca1\u6709\u8dcc\u5012\u3002 \u3000\u3000\u539f\u6765\u5728\u6ce2\u65af\u548c\u6b27\u6d32\uff0c\u6709\u4e00\u79cd\u6b66\u529f\uff0c\u53eb\u505a\u201c\u5b50\u5348\u6d41\u95ed\u8840\u6cd5\u201d\uff0c\u76f8\u5f53\u4e8e\u4e2d\u56fd\u70b9\u7a74\u7684\u529f\u592b\uff0c\u4e0d \u8fc7\u8981\u5bf9\u51c6\u65f6\u523b\uff0c\u5728\u76f8\u5e94\u7684\u65f6\u523b\u70b9\u4e2d\u4e86\u654c\u4eba\u76f8\u5e94\u7684\u90e8\u4f4d\uff0c\u4ea6\u8db3\u4ee5\u81f4\u4eba\u4e8e\u6b7b\u3002\u9093\u5357\u906e\u7cbe\u7814\u8fc7\u201c\u5b50 \u5348\u6d41\u95ed\u8840\u6cd5\u201d\uff0c\u4e5f\u5b66\u8fc7\u9632\u5fa1\u7684\u65b9\u6cd5\uff0c\u4ed6\u672c\u6765\u4e5f\u60f3\u8fc7\u7528\u8fd9\u65b9\u6cd5\u5236\u80dc\uff0c\u53ea\u56e0\u4f73\u534e\u751f\u8eab\u6cd5\u592a\u5feb\uff0c\u4ed6 \u6839\u672c\u89e6\u4e0d\u5230\u6842\u534e\u751f\u7684\u8eab\u4f53\uff0c\u8fd9\u65b9\u6cd5\u81ea\u662f\u5e94\u7528\u4e0d\u4e0a\u3002\u4f46\u6842\u534e\u751f\u7528\u70b9\u7a74\u6cd5\u5236\u4ed6\uff0c\u4ed6\u7684\u9632\u5fa1\u65b9\u6cd5\u5374 \u521a\u597d\u7528\u4e0a\u4e86\uff0c\u5c31\u5728\u6842\u534e\u751f\u6307\u5c16\u5c06\u89e6\u672a\u89e6\u7684\u4e00\u970e\u90a3\uff0c\u4ed6\u7684\u8eab\u4f53\u4fbf\u7acb\u8d77\u53cd\u5e94\uff0c\u5c06\u5168\u8eab\u8840\u6db2\u5728\u90a3\u51e0 \u79d2\u949f\u7684\u65f6\u95f4\u5185\uff0c\u6682\u65f6\u505c\u6b62\u6d41\u52a8\uff0c\u4e2d\u897f\u6b66\u5b66\u539f\u7406\uff0c\u672c\u5c31\u76f8\u901a\uff0c\u8fd9\u4e00\u6765\u5c45\u7136\u88ab\u4ed6\u907f\u8fc7\u51f6\u9669\u3002\u4f46\u4ed6 \u8fd9\u79cd\u65b9\u6cd5\u5374\u4e5f\u4e0d\u662f\u6700\u9002\u5f53\u7684\u5e94\u4ed8\u4e4b\u6cd5\u3002\uff08\u6700\u9002\u5f53\u7684\u65b9\u6cd5\u662f\u4e2d\u56fd\u4e0a\u4e58\u6b66\u529f\u4e2d\u7684\u95ed\u7a74\u529f\u592b\u3002\uff09\u6545 \u6b64\u88ab\u70b9\u4e2d\u4e4b\u540e\uff0c\u8fd8\u662f\u611f\u5230\u4e00\u9635\u9635\u7684\u9178\u9ebb\uff01 \u3000\u3000\u9093\u5357\u906e\u5fc3\u4e2d\u5927\u9707\uff0c\u60f3\u9053\uff1a\u201c\u4e2d\u56fd\u7684\u70b9\u7a74\u6cd5\u679c\u7136\u662f\u795e\u5947\u83ab\u6d4b\uff0c\u6bd4\u8d77\u6211\u4eec\u7684\u4e8e\u5348\u6d41\u95ed\u8840\u6cd5 \u6765\uff0c\u53ef\u8981\u5389\u5bb3\u5f97\u591a\uff01\u201d\u4ed6\u672c\u6765\u662f\u51c6\u5907\u6371\u8fc7\u8fd9\u4e00\u652f\u70db\u5149\u7684\uff0c\u4f46\u88ab\u6842\u534e\u751f\u70b9\u4e86\u4ed6\u4e94\u5904\u5927\u7a74\u4e4b\u540e\uff0c \u4ed6\u4ed4\u7ec6\u4e00\u60f3\uff0c\u81ea\u5df1\u53ea\u6709\u6371\u6253\u7684\u4efd\u513f\uff0c\u82e5\u518d\u88ab\u4ed6\u63a5\u8fde\u70b9\u4e86\u51e0\u6b21\u7a74\u9053\uff0c\u7cbe\u75b2\u529b\u7aed\uff0c\u90a3\u5c31\u672a\u5fc5\u6371\u5f97 \u4f4f\u6842\u534e\u751f\u7684\u91cd\u62f3\u4e86\u3002\u5982\u6b64\u4e00\u60f3\uff0c\u4ed6\u767b\u65f6\u6539\u53d8\u4e86\u8ba1\u5212\uff0c\u4f5c\u52bf\u4f6f\u653b\uff0c\u6842\u534e\u751f\u6b63\u81ea\u4f7f\u5230\u4e00\u62db\u201c\u5206\u725b \u638c\u201d\u62cd\u6765\uff0c\u9093\u5357\u906e\u62fc\u7740\u63e1\u4ed6\u4e00\u638c\uff0c\u835e\u7136\u5927\u559d\u4e00\u58f0\uff0c\u5316\u62f3\u4e3a\u638c\uff0c\u4e00\u4e0b\u5b50\u5c06\u6842\u534e\u751f\u7684\u53cc\u638c\u63a5\u4f4f\u3002 \u3000\u3000\u6842\u534e\u751f\u5fae\u5fae\u4e00\u6643\uff0c\u7acb\u523b\u731c\u5230\u4e86\u4ed6\u7684\u7528\u610f\uff0c\u5fc3\u9053\uff1a\u201c\u539f\u6765\u4ed6\u662f\u60f3\u4e0e\u6211\u8f83\u91cf\u529b\u529b\uff0c\u5e0c\u671b\u80fd\u642a \u5f97\u8fc7\u8fd9\u4e00\u652f\u70db\u5149\u3002\u201d\u9093\u5357\u906e\u5185\u529b\u4e00\u53d1\uff0c\u52bf\u5982\u6392\u5c71\u5012\u6d77\uff0c\u521a\u731b\u975e\u5e38\uff0c\u6842\u534e\u751f\u4e0d\u6562\u8f7b\u654c\uff0c\u5f53\u4e0b\u6697 \u51dd\u771f\u6c14\uff0c\u58eb\u900f\u638c\u5fc3\uff0c\u4e00\u9762\u4ee5\u6781\u4e0a\u4e58\u7684\u5185\u529f\uff0c\u6d88\u89e3\u654c\u4eba\u7684\u61a8\u52b2\uff0c\u4e00\u9762\u5c06\u5176\u771f\u529b\u767e\u8feb\u8fc7\u53bb\u3002\u5728\u6700 \u521d\u7684\u4e00\u523b\u949f\uff0c\u4e24\u4eba\u90fd\u662f\u5140\u7acb\u5982\u5c71\uff0c\u52a8\u4e5f\u4e0d\u52a8\u3002 \u3000\u3000\u518d\u8fc7\u4e00\u4f1a\uff0c\u4f46\u89c1\u9093\u5357\u906e\u6c57\u6dcb\u5982\u96e8\uff0c\u6c14\u5598\u547c\u547c\uff0c\u4e00\u6b65\u4e00\u6b65\u7684\u5411\u540e\u632a\u52a8\uff0c\u6bcf\u9000\u4e00\u6b65\uff0c\u90a3\u82b1\u5c97 \u77f3\u7684\u9636\u7816\u4e0a\u4fbf\u662f\u4e00\u9053\u6df1\u6df1\u7684\u8db3\u5370\uff0c\u514b\u96f7\u65af\u8bf8\u4eba\u770b\u4e86\uff0c\u4e5f\u81ea\u4e0d\u7981\u6697\u6697\u9a87\u7136\uff0c\u5fc3\u4e2d\u90fd\u5728\u60f3\u9053\uff1a \u201c\u9093\u5357\u906e\u7684\u5185\u52b2\u5982\u6b64\u60ca\u4eba\uff0c\u5374\u4ecd\u7136\u88ab\u6842\u534e\u751f\u8feb\u5f97\u6b65\u6b65\u540e\u9000\uff0c\u72fc\u72fc\u4e0d\u582a\u3002\u770b\u6765\u4e2d\u56fd\u7684\u6b66\u672f\uff0c\u5f53 \u771f\u662f\u4e16\u754c\u7b2c\u4e00\u7684\u4e86\uff01\u201d \u3000\u3000\u8fd9\u65f6\u90a3\u652f\u7ea2\u70db\u5df2\u70e7\u5230\u53ea\u8a8a\u534a\u5bf8\uff0c\u4f46\u9093\u5357\u906e\u4ea6\u5df2\u662f\u6c14\u529b\u67af\u7aed\uff0c\u8d25\u8c61\u6bd5\u9732\uff0c\u65c1\u89c2\u8bf8\u4eba\uff0c\u8c01\u90fd \u770b\u5f97\u51fa\u6765\uff0c\u4ed6\u7edd\u5bf9\u652f\u6301\u4e0d\u5230\u8721\u70db\u70e7\u5b8c\u3002\u514b\u96f7\u65af\u62ff\u8d77\u94f6\u53c9\uff0c\u4f5c\u52bf\u6b32\u671b\uff0c\u5ffd\u542c\u5f97\u7eb7\u4e71\u7684\u811a\u6b65\u58f0\u5982 \u98de\u800c\u81f3\uff0c\u539f\u6765\u662f\u5c3c\u6cca\u5c14\u7684\u6b66\u58eb\u62c9\u6c57\u56fe\u5c06\u4ed6\u4eec\u7684\u738b\u5b50\u8bf7\u6765\u4e86\u3002 \u3000\u3000\u5c3c\u6cca\u5c14\u738b\u5b50\u6413\u7740\u53cc\u624b\u53eb\u9053\uff1a\u201c\u54ce\u5440\uff0c\u4f60\u4eec\u5728\u8fd9\u91cc\u505a\u4ec0\u4e48\uff1f\u77a7\u5728\u4e3b\u4eba\u7684\u4efd\u4e0a\uff0c\u4e0d\u8981\u4f24\u4e86\u548c \u6c14\u5427\u3002\u201d\u514b\u96f7\u65af\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\uff0c\u6842\u5148\u751f\u548c\u9093\u5357\u906e\u4f9d\u7167\u6b66\u58eb\u7684\u89c4\u77e9\u4e3a\u7f8e\u4eba\u51b3\u6597\u3002\u201d\u5c3c\u6cca\u5c14\u738b\u5b50 \u9053\uff1a\u201c\u7559\u4e9b\u6c14\u529b\u660e\u5929\u53bb\u6bd4\u6b66\u5427\u3002\u201d\u6842\u534e\u751f\u53cc\u638c\u4e00\u677e\uff0c\u538b\u529b\u9aa4\u7136\u6d88\u5931\uff0c\u9093\u5357\u906e\u91cd\u5fc3\u4e0d\u7a33\uff0c\u4e00\u8dcb \u8dcc\u5012\uff0c\u514b\u96f7\u65af\u53ee\u53ee\u5f53\u5f53\u7684\u6572\u4e86\u51e0\u4e0b\u94f6\u76d8\u5531\u9053\uff1a\u201c\u6ce2\u65af\u4e4b\u864e\uff0c\u4e0d\u654c\u4e2d\u56fd\u4e4b\u72ee\uff0c\u6211\u7684\u670b\u53cb\u554a\uff0c\u5965 \u6797\u5339\u65af\u5c71\u7684\u91d1\u82f9\u679c\u5c06\u8d50\u7ed9\u4f60\uff0c\u4f60\u662f\u7ef4\u7eb3\u65af\u7684\u5ba0\u513f\uff01\u201d\u5965\u6797\u5339\u65af\uff08OlympUs\uff09\u5c71\u662f\u5e0c\u814a\u795e\u8bdd\u4e2d \u8bf8\u795e\u6240\u4f4f\u7684\u5c71\uff0c\u7ef4\u7eb3\u65af\uff08Venus\uff09\u5219\u662f\u5e0c\u814a\u795e\u8bdd\u4e2d\u7684\u7231\u795e\u3002\u8fd9\u8bd7\u7684\u610f\u601d\u662f\u6842\u534e\u751f\u4eca\u665a\u6bd4\u6b66\u80dc \u4e86\uff0c\u660e\u5929\u4e5f\u5fc5\u5b9a\u80fd\u8d62\u5f97\u516c\u4e3b\u7684\u82b3\u5fc3\u3002 \u3000\u3000\u9093\u5357\u906e\u4e00\u4e2a\u9ca4\u9c7c\u6253\u633a\u7ffb\u8d77\u8eab\u6765\uff0c\u4ed6\u6da8\u7ea2\u4e86\u8138\uff0c\u4f46\u5fc3\u4e2d\u5374\u4e5f\u4e0d\u80fd\u4e0d\u6709\u70b9\u611f\u6fc0\u6842\u534e\u751f\uff0c\u8981\u662f \u6842\u534e\u751f\u521a\u624d\u518d\u52a0\u4e0a\u4e00\u628a\u52b2\uff0c\u4ed6\u7eb5\u4e0d\u6b7b\u4e5f\u5f97\u91cd\u4f24\uff0c\u800c\u4eca\u867d\u7136\u6454\u4e86\u4e00\u8dcb\uff0c\u5931\u4e86\u9762\u5b50\uff0c\u5bf9\u4e8e\u8eab\u4f53\uff0c \u5374\u65e0\u5927\u788d\u3002 \u3000\u3000\u6842\u534e\u751f\u5411\u5c3c\u6cca\u5c14\u738b\u5b50\u65bd\u793c\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u4f60\u4eca\u665a\u6240\u8d50\u7684\u7f8e\u9152\uff0c\u6211\u4eec\u90fd\u559d\u9189\u4e86\uff0c\u80e1\u95f9\u4e00\u573a\uff0c \u771f\u662f\u5931\u793c\uff01\u5c3c\u6cca\u5c14\u738b\u5b50\u8138\u4e0a\u4e00\u7ea2\uff0c\u642d\u8baa\u8bf4\u9053\uff1a\u201c\u6b66\u58eb\u6bd4\u6b66\uff0c\u90a3\u4e5f\u4e0d\u662f\u4ec0\u4e48\u5931\u793c\u7684\u4e8b\u60c5\u3002\u597d \u5427\uff0c\u65e2\u7136\u5f7c\u6b64\u6ca1\u6709\u635f\u4f24\uff0c\u90a3\u5c31\u4e0d\u5fc5\u518d\u6bd4\u4e0b\u53bb\u4e86\u3002\u5927\u5bb6\u65e9\u70b9\u5b89\u7761\u5427\u3002\u201d\u4ed6\u731c\u7591\u6842\u534e\u751f\u5df2\u8bc6\u7834\u4ed6 \u7684\u8be1\u8ba1\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u4e0d\u7ba1\u6211\u613f\u4e0d\u613f\u610f\uff0c\u770b\u6765\u4ed6\u90fd\u5c06\u662f\u9a79\u9a6c\u7684\u4e86\u3002\u6211\u73b0\u5728\u4e0d\u5fc5\u60f9\u4ed6\uff0c\u5c06\u6765\u7684 \u65e5\u5b50\u957f\u7740\u5462\u3002\u201d\u4e8e\u662f\u548c\u6842\u534e\u751f\u9053\u8fc7\u665a\u5b89\uff0c\u5306\u5306\u5fd9\u5fd9\u4fbf\u8d70\u3002 \u3000\u3000\u5c3c\u6cca\u5c14\u738b\u5b50\u65b9\u8d70\uff0c\u5ffd\u542c\u5f97\u201c\u6251\u901a\u201d\u4e00\u58f0\uff0c\u6709\u4e00\u4e2a\u4eba\u8dcc\u5012\u5730\u4e0a\u3002 \u3000\u3000\u90a3\u5012\u4e0b\u53bb\u7684\u4eba\u662f\u54c8\u5df4\u5fb7\uff0c\u539f\u6765\u4ed6\u53d7\u4f24\u751a\u91cd\uff0c\u9002\u624d\u505a\u516c\u8bc1\u4ecd\u662f\u52c9\u5f3a\u652f\u6301\u7684\u3002\u6842\u534e\u751f\u5c06\u4ed6\u6276 \u8d77\uff0c\u9093\u5357\u906e\u9053\uff1a\u201c\u6211\u7684\u670b\u53cb\u4e0d\u5fc5\u4f60\u6765\u8d39\u5fc3\uff0c\u4f60\u7167\u987e\u4f60\u7684\u670b\u53cb\u53bb\u5427\u3002\u201d\u6842\u534e\u751f\u4e00\u770b\uff0c\u53ea\u89c1\u6469\u519c \u6b63\u76d8\u819d\u5750\u5728\u5730\u4e0a\uff0c\u5934\u4e0a\u5192\u51fa\u70ed\u817e\u817e\u7684\u767d\u6c14\uff0c\u9762\u8272\u5df2\u7ecf\u6e10\u8f6c\u7ea2\u6da6\uff0c\u6842\u534e\u751f\u77e5\u9053\u4ed6\u6b63\u4ee5\u745c\u4f3d\u6c14 \u529f\uff0c\u7597\u6cbb\u5185\u4f24\uff0c\u770b\u8fd9\u60c5\u5f62\uff0c\u4ee5\u4ed6\u7684\u5185\u529f\uff0c\u4e0d\u987b\u522b\u4eba\u5e2e\u52a9\u3002\u518d\u770b\u514b\u96f7\u65af\uff0c\u53ea\u89c1\u4ed6\u5934\u4e0a\u9752\u7b4b\u6bd5 \u9732\uff0c\u72c2\u6b4c\u4e4b\u540e\uff0c\u7cbe\u795e\u663e\u5f97\u840e\u9761\u4e0d\u582a\uff0c\u53d7\u4f24\u4ea6\u5448\u4e0d\u6d45\u3002\u4f73\u534e\u751f\u5411\u9093\u5357\u906e\u9053\uff1a\u201c\u597d\u5427\uff0c\u54b1\u4eec\u5404\u81ea \u7167\u6599\u81ea\u5df1\u7684\u670b\u53cb\uff0c\u4f60\u7684\u670b\u53cb\uff0c\u4f24\u5728\u813e\u810f\uff0c\u4f60\u8981\u66ff\u4ed6\u6253\u901a\u4e09\u9633\u7ecf\u8109\uff0c\u63a8\u8840\u8fc7\u5bab\u3002\u201d\u9093\u5357\u906e\u9053\uff1a \u201c\u4e0d\u5fc5\u4f60\u6559\uff0c\u6211\u4eec\u6ce2\u65af\u6b66\u58eb\u4e5f\u61c2\u5f97\u5185\u529f\u6cbb\u7597\u3002\u201d\u6842\u534e\u751f\u89c1\u4ed6\u5982\u6b64\u9a84\u50b2\uff0c\u672c\u60f3\u8be6\u7ec6\u6307\u70b9\u4ed6\u7684\uff0c \u53ea\u597d\u7f62\u4e86\u3002 \u3000\u3000\u6842\u534e\u751f\u8bf7\u514b\u96f7\u65af\u4ef0\u5367\u5730\u4e0a\uff0c\u6b63\u5f85\u8fd0\u529f\u66ff\u4ed6\u7597\u4f24\uff0c\u514b\u96f7\u65af\u9053\uff1a\u201c\u4e14\u6162\uff0c\u6709\u4e00\u4e2a\u4eba\u6bd4\u6211\u4f24\u5f97 \u66f4\u91cd\u5462\uff01\u201d\u6842\u534e\u751f\u8fd9\u65f6\u4e5f\u542c\u5230\u4e86\u65ad\u65ad\u7eed\u7eed\u7684\u547b\u541f\u58f0\uff0c\u539f\u6765\u8fd9\u4eba\u4e43\u662f\u963f\u5bcc\u6c57\u7684\u6b66\u58eb\u6717\u7eb3\uff0c\u4ed6\u662f \u56e0\u4e3a\u6253\u4e86\u6842\u534e\u751f\u4e00\u62f3\uff0c\u88ab\u6842\u534e\u751f\u7684\u5185\u529b\u53cd\u51fb\u81f4\u4f24\u7684\u3002\u521d\u65f6\u4e0d\u89c9\u600e\u6837\uff0c\u8d8a\u6765\u8d8a\u89c9\u75bc\u75db\uff0c\u7ec8\u4e8e\u5fcd \u53d7\u4e0d\u4f4f\u4e86\u3002\u6842\u534e\u751f\u5c06\u4e00\u7c92\u836f\u4e38\u63b7\u7ed9\u4ed6\u9053\uff1a\u201c\u6211\u6ca1\u6709\u9707\u4f24\u4f60\u7684\u5185\u810f\uff0c\u7edd\u65e0\u6027\u547d\u4e4b\u5fe7\uff0c\u4f60\u4e0d\u5fc5\u5bb3 \u6015\u3002\u541e\u4e0b\u8fd9\u9897\u836f\u4e38\uff0c\u5230\u5e8a\u4e0a\u53bb\u9759\u517b\u4e09\u5929\u5427\u3002\u201d\u539f\u6765\u6842\u534e\u751f\u6068\u4ed6\u884c\u4e3a\u5351\u9119\uff0c\u5077\u65bd\u6697\u7b97\uff0c\u6545\u6b64\u8981 \u4ed6\u5728\u4e09\u5929\u4e4b\u5185\u52a8\u5f39\u4e0d\u5f97\uff0c\u8fde\u516c\u4e3b\u4e5f\u89c1\u4e0d\u7740\u3002\u6717\u7eb3\u610f\u6b32\u4e0d\u63a5\uff0c\u4f46\u75db\u5f97\u5b9e\u5728\u5389\u5bb3\uff0c\u53ea\u597d\u4e0d\u987e\u989c \u9762\uff0c\u5c06\u90a3\u9897\u836f\u4e38\u541e\u4e0b\u3002\u8fd9\u836f\u4e38\u7684\u79d8\u65b9\u4e43\u662f\u5929\u5c71\u6d3e\u79df\u5e08\u6666\u670b\u60ee\u5e08\u6240\u4f20\uff0c\u51fa\u4e03\u79cd\u73cd\u8d35\u7684\u836f\u6599\u914d \u6210\uff0c\u5929\u5c71\u96ea\u83b2\u4e5f\u662f\u5176\u4e2d\u4e4b\u4e00\uff0c\u540d\u4e3a\u201c\u78a7\u7075\u4e39\u201d\u3002\u6842\u534e\u751f\u4ea6\u4e0d\u8fc7\u4ec5\u5b58\u4e94\u7c92\u800c\u5df2\u3002\u6717\u7eb3\u541e\u4e0b\u4e86\u8fd9 \u7c92\u201c\u78a7\u7075\u4e39\u201d\u4e4b\u540e\uff0c\u75db\u82e6\u679c\u7136\u51cf\u4e86\u4e00\u5927\u534a\uff0c\u4f46\u808c\u8089\u50f5\u786c\uff0c\u624b\u8db3\u8fd8\u662f\u4e0d\u80fd\u8f6c\u52a8\uff0c\u53ea\u597d\u542c\u6842\u534e\u751f \u7684\u8bdd\uff0c\u4e56\u4e56\u7684\u8bf7\u522b\u4eba\u6276\u4ed6\u4e0a\u5e8a\u9759\u517b\u3002 \u3000\u3000\u6842\u534e\u751f\u4e8e\u662f\u4e13\u5fc3\u4e00\u610f\uff0c\u66ff\u514b\u96f7\u65af\u6cbb\u4f24\uff0c\u4ed6\u5f97\u4e86\u9f99\u53f6\u5927\u5e08\u7684\u6307\u70b9\u4e4b\u540e\uff0c\u5185\u529f\u731b\u8fdb\uff0c\u66f4\u975e\u6614 \u6bd4\uff0c\u4e0d\u8fc7\u4e00\u76cf\u8336\u7684\u65f6\u523b\uff0c\u4fbf\u66ff\u514b\u96f7\u65af\u6253\u901a\u4e86\u5341\u4e8c\u91cd\u5173\uff0c\u518d\u8ba9\u4ed6\u541e\u4e0b\u4e86\u4e00\u7c92\u78a7\u7075\u4e39\u3002\u514b\u96f7\u65af\u7b11 \u9053\uff1a\u201c\u6211\u73b0\u5728\u6bd4\u672b\u53d7\u4f24\u4e4b\u65f6\u66f4\u8981\u7cbe\u795e\uff0c\u4e2d\u56fd\u7684\u6b66\u529f\u548c\u533b\u672f\u771f\u662f\u795e\u5999\uff0c\u6211\u4e5f\u670d\u4e86\u4f60\u4e86\uff01\u201d \u3000\u3000\u518d\u770b\u6469\u519c\uff0c\u6469\u519c\u8fd9\u65f6\u5df2\u7ecf\u7ad9\u8d77\uff0c\u4f46\u5374\u662f\u6101\u7709\u4e0d\u5c55\uff0c\u4f3c\u6709\u91cd\u5fe7\u3002\u6842\u534e\u751f\u9053\uff1a\u201c\u543e\u5144\u4e0d\u5fc5\u62c5 \u5fe7\u3002\u201d\u7a81\u7136\u5c06\u4ed6\u5012\u63d0\u8d77\u6765\uff0c\u5934\u4e0b\u811a\u4e0a\uff0c\u5c06\u624b\u638c\u8d34\u5728\u4ed6\u811a\u8ddf\u7684\u201c\u7389\u6cc9\u7a74\u201d\u4e0a\uff0c\u514b\u96f7\u65af\u5947\u9053\uff1a \u3000\u3000\u201c\u54a6\uff0c\u4f60\u8fd9\u662f\u505a\u4ec0\u4e48\uff1f\u201d\u6842\u534e\u751f\u4e13\u5fc3\u8fd0\u529f\uff0c\u7b11\u800c\u4e0d\u7b54\u3002\u8fc7\u4e86\u7247\u523b\uff0c\u6842\u534e\u751f\u5c06\u624b\u638c\u79fb\u5f00\uff0c \u6469\u519c\u4e00\u8dc3\u800c\u8d77\uff0c\u606d\u606d\u656c\u656c\u7684\u8bf4\u9053\uff1a\u201c\u4f60\u771f\u662f\u4e86\u4e0d\u8d77\uff0c\u7adf\u7136\u6df1\u901a\u6211\u4eec\u5370\u5ea6\u745c\u4f69\u6c14\u529f\u7684\u5965\u5999\u3002\u201d \u539f\u6765\u6842\u534e\u751f\u521a\u624d\u66ff\u4ed6\u6cbb\u7597\u7684\u65b9\u6cd5\uff0c\u6b63\u662f\u9f99\u53f6\u5927\u5e08\u5f53\u65e5\u66ff\u6842\u534e\u751f\u6062\u590d\u529f\u529b\u7684\u201c\u6258\u7389\u6cc9\u201d\u4e00\u5f0f\u3002 \u3000\u3000\u6842\u534e\u751f\u9053\uff1a\u201c\u6211\u5144\u529f\u529b\u5c3d\u590d\uff0c\u4e0d\u5fc5\u62c5\u5fc3\u660e\u5929\u7684\u6b66\u529f\u8003\u8bd5\u4e86\u3002\u5176\u5b9e\u4ee5\u543e\u5144\u7684\u529f\u529b\uff0c\u5373\u65e0\u5c0f \u5f1f\u4e4b\u52a9\uff0c\u6700\u591a\u4e5f\u4e0d\u8fc7\u518d\u8fc7\u4e24\u5929\uff0c\u4fbf\u53ef\u6062\u590d\u5982\u521d\u3002\u201d\u6469\u519c\u9762\u6709\u6127\u8272\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u672c\u6765\u62c5\u5fc3\u660e\u5929 \u7684\u8003\u8bd5\u5462\uff0c\u73b0\u5728\u6211\u529f\u529b\u867d\u590d\uff0c\u5374\u51b3\u5b9a\u660e\u5929\u4e0d\u53c2\u52a0\u4e86\uff01\u201d \u3000\u3000\u6842\u534e\u751f\u9053\uff1a\u201c\u8fd9\u5374\u4e3a\u4f55\uff1f\u201d\u6469\u519c\u9053\uff1a\u201c\u6211\u521d\u5230\u52a0\u5fb7\u6ee1\u90fd\u4e4b\u65f6\uff0c\u66fe\u89c1\u8fc7\u96c5\u5fb7\u661f\u738b\u5b50\uff0c\u738b\u5b50 \u5c31\u63d0\u8d77\u4f60\u7684\u540d\u5b57\uff0c\u5927\u5927\u79f0\u8d5e\u3002\u5982\u4eca\u4e00\u89c1\uff0c\u679c\u7136\u540d\u4e0b\u65e0\u865a\uff0c\u4e0d\u4f46\u6b66\u529f\u8d85\u5999\uff0c\u800c\u4e14\u4eba\u54c1\u5353\u7edd\uff0c\u9664 \u4e86\u4f60\u8fd8\u6709\u8c01\u582a\u5339\u914d\u516c\u4e3b\uff1f\u6211\u4ea4\u5f97\u4f60\u8fd9\u6837\u4e00\u4f4d\u670b\u53cb\u5df2\u7ecf\u5fc3\u6ee1\u610f\u8db3\uff0c\u518d\u4e5f\u4e0d\u60f3\u53bb\u5f53\u9009\u4ec0\u4e48\u9a79\u9a6c \u5566\u3002\u660e\u5929\u4e00\u65e9\uff0c\u6211\u5c31\u5c06\u52a8\u8eab\u56de\u56fd\uff0c\u4f46\u613f\u54b1\u4eec\u540e\u4f1a\u6709\u671f\u3002\u201d\u6842\u534e\u751f\u89c1\u4ed6\u53bb\u610f\u575a\u51b3\uff0c\u53ea\u597d\u4e0e\u4ed6\u63e1 \u522b\u3002 \u3000\u3000\u6842\u534e\u751f\u56de\u5230\u697c\u4e0a\uff0c\u5fc3\u4e2d\u6b8a\u6709\u611f\u89e6\uff0c\u501a\u680f\u770b\u6708\uff0c\u60f3\u9053\uff1a\u201c\u4ed6\u4eec\u6700\u521d\u89c1\u6211\u4e4b\u65f6\uff0c\u90fd\u542b\u6709\u654c \u610f\u3002\u5982\u4eca\uff0c\u5219\u6700\u5c11\u514b\u96f7\u65af\u4e0e\u6469\u519c\u5df2\u548c\u6211\u4ea4\u4e0a\u670b\u53cb\u4e86\u3002\u53ef\u89c1\u53ea\u8981\u4f60\u8bda\u5fc3\u5f85\u4eba\uff0c\u654c\u4eba\u4e5f\u53ef\u4ee5\u5316\u6210 \u670b\u53cb\u3002\u201d \u3000\u3000\u5ffd\u542c\u5f97\u80cc\u540e\u58f0\u54cd\uff0c\u4e00\u56de\u5934\u53ea\u89c1\u514b\u96f7\u65af\u62b1\u7740\u4e03\u5f26\u7434\u8d70\u6765\uff0c\u7b11\u9053\uff1a\u201c\u6211\u5df2\u7ecf\u628a\u65ad\u4e86\u7684\u7434\u5f26\u7eed \u597d\u4e86\uff0c\u51c6\u5907\u660e\u5929\u4e3a\u4f60\u594f\u80dc\u5229\u4e4b\u6b4c\uff01\u201d\u6842\u534e\u751f\u9053\uff1a\u201c\u53ef\u60dc\u6211\u6ca1\u6709\u4f60\u8fd9\u6837\u7684\u8bd7\u624d\uff0c\u8981\u4e0d\u7136\uff0c\u7109\u77e5 \u4e0d\u662f\u6211\u4e3a\u4f60\u732e\u8bd7\u5462\uff1f\u201d\u514b\u96f7\u65af\u9053\uff1a\u201c\u4e0d\uff0c\u4e0d\uff0c\u660e\u5929\u6211\u53ea\u5c06\u662f\u4e00\u4e2a\u770b\u5ba2\u3002\u201d\u6842\u534e\u751f\u9053\uff1a\u201c\u4f60\u4e5f \u4e0d\u53c2\u52a0\u5417\uff1f\u201d\u514b\u96f7\u65af\u9053\uff1a\u201c\u6211\u4eec\u5e0c\u814a\u4eba\u6700\u7231\u6b23\u8d4f\u559c\u5267\uff0c\u4e0e\u5176\u8f9b\u8f9b\u82e6\u82e6\u7684\u626e\u6f14\u5267\u4e2d\u4eba\uff0c\u4e0d\u5982\u5b89 \u95f2\u7684\u5728\u53f0\u4e0b\u505a\u4e2a\u89c2\u4f17\u3002\u6211\u4eec\u5e0c\u814a\u62bc\u8bdd\u4e2d\u6709\u4e00\u4e2a\u65e5\u7ec5\u963f\u6ce2\u7f57\uff08Apolo\uff09\uff0c\u4ed6\u5e38\u5e38\u9a7e\u7740\u65e5\u8f66\uff0c\u53bb \u6b23\u8d4f\u5b87\u5b99\u95f4\u7f8e\u597d\u7684\u4e8b\u7269\u3002\u6211\u613f\u610f\u5b66\u963f\u6ce2\u7f57\u3002\u4f60\u662f\u6211\u89c1\u8fc7\u7684\u7537\u5b50\u4e2d\u6700\u7f8e\u7684\u7537\u5b50\uff0c\u4e0d\u8bba\u5916\u8c8c\u4e0e\u5185 \u5fc3\u3002\u5c3c\u6cca\u5c14\u516c\u4e3b\u5219\u662f\u4e16\u95f4\u7684\u7b2c\u4e00\u7f8e\u4eba\u3002\u4f60\u4eec\u7ed3\u5408\uff0c\u90a3\u6b63\u662f\u4eba\u95f4\u81f3\u7f8e\u7684\u7ed3\u5408\uff0c\u6211\u5f97\u4ee5\u6b23\u8d4f\u4f60\u4eec \u7684\u7ed3\u5408\uff0c\u90a3\u4fbf\u662f\u6211\u6700\u5927\u7684\u5e78\u798f\u4e86\u3002\u518d\u8bf4\uff0c\u6211\u4e00\u89c1\u4f60\u7684\u9762\uff0c\u6211\u5c31\u66fe\u7ecf\u5bf9\u4f60\u8bf4\u8fc7\uff0c\u6211\u662f\u67cf\u62c9\u56fe\u7684 \u4fe1\u5f92\uff0c\u5bf9\u5f85\u7231\u60c5\uff0c\u6211\u81ea\u6709\u6211\u7684\u610f\u5ff5\u3002\u201d\u514b\u96f7\u65af\u6d53\u539a\u7684\u8bd7\u4eba\u6c14\u8d28\uff0c\u4eca\u6842\u534e\u751f\u5927\u4e3a\u611f\u52a8\uff0c\u5f53\u4e0b\u4e0e \u514b\u96f7\u65af\u8bda\u6073\u7684\u63e1\u624b\uff0c\u8c22\u8c22\u4ed6\u7684\u53cb\u8c0a\u3002 \u3000\u3000\u514b\u96f7\u65af\u9053\uff1a\u201c\u4e0d\u8fc7\u6211\u6709\u4e00\u53e5\u8bdd\u529d\u4f60\uff0c\u5bf9\u7f8e\u597d\u7684\u4e1c\u897f\u5e94\u8be5\u73cd\u60dc\u7231\u62a4\u3002\u4f60\u51c6\u5907\u600e\u4e48\u53bb\u7231\u62a4\u4f60 \u7684\u516c\u4e3b\u5462\uff1f\u201d\u6842\u534e\u751f\u5fae\u5fae\u4e00\u7b11\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u8bdd\u6211\u53ea\u80fd\u5411\u516c\u4e3b\u53bb\u8bf4\u3002\u201d\u514b\u96f7\u65af\u4e0d\u5f85\u5b83\u7684\u56de\u7b54\uff0c \u4fbf\u81ea\u884c\u5f80\u4e0b\u8bf4\u9053\uff1a\u201c\u7f8e\u4eba\u597d\u6bd4\u540d\u82b1\uff0c\u5343\u4e07\u4e0d\u53ef\u8ba9\u5979\u5760\u6e9c\u6cbe\u6ce5\uff0c\u4f60\u660e\u767d\u6211\u7684\u610f\u601d\u5417\uff1f\u4f60\u5982\u8ba9\u5979 \u5c06\u6765\u505a\u4ec0\u4e48\u5973\u738b\uff0c\u90a3\u5c31\u662f\u5927\u715e\u98ce\u666f\u4e86\u3002\u505a\u7687\u5e1d\u8fd9\u6837\u7684\u8822\u4e8b\uff0c\u8fde\u6211\u8fd9\u6837\u7684\u4fd7\u4eba\u60f3\u8d77\u6765\u4e5f\u89c9\u5934 \u75db\u3002\u4e0d\u8fc7\u4e3a\u4f60\uff0c\u6211\u5c06\u6765\u4e5f\u8bb8\u4f1a\u5c1d\u8bd5\u505a\u505a\u7687\u5e1d\uff0c\u51c6\u5907\u5c06\u6765\u4f60\u4eec\u5230\u5e0c\u814a\u6765\u73a9\uff0c\u6211\u53ef\u4ee5\u62e8\u51fa\u6700\u534e\u4e3d \u7684\u6e38\u8247\uff0c\u4e0e\u4f60\u4eec\u540c\u8d4f\u7231\u7434\u6d77\u4e0a\u7684\u98ce\u5149\uff01\u201d\u6842\u534e\u751f\u5931\u7b11\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4e48\u6211\u9884\u5148\u591a\u8c22\u4f60\u4e86\uff0c\u53ef\u7231 \u7684\u8bd7\u4eba\u738b\u5b50\uff01\u4f46\u613f\u8fd9\u4e0d\u662f\u8bd7\u4eba\u7684\u5e7b\u60f3\u3002\u201d\u4e24\u4eba\u5728\u7b11\u58f0\u4e2d\u5404\u81ea\u9053\u4e86\u665a\u5b89\u3002 \u3000\u3000\u7b2c\u4e8c\u5929\u4fbf\u662f\u6700\u540e\u8003\u9009\u9a79\u9a6c\u7684\u51b3\u8d5b\uff0c\u5148\u8003\u6b66\u529f\uff0c\u7531\u516c\u4e3b\u4eb2\u81ea\u4e0e\u6c42\u5a5a\u8005\u6bd4\u5251\uff0c\u5730\u70b9\u4fbf\u5728\u5fa1\u82b1 \u56ed\u4e2d\u3002\u6469\u519c\u4e00\u65e9\u79bb\u53bb\uff0c\u514b\u96f7\u65af\u81ea\u52a8\u9000\u51fa\uff0c\u6717\u7eb3\u5367\u5e8a\u4e0d\u8d77\uff0c\u54c8\u5df4\u5fb7\u56e0\u4e3a\u6628\u665a\u53d7\u4e86\u4f24\uff0c\u867d\u5f97\u9093\u5357 \u906e\u66ff\u4ed6\u8fd0\u529f\u6cbb\u7597\uff0c\u4f24\u52bf\u65e0\u788d\uff0c\u529f\u529b\u5374\u8fd8\u672a\u590d\uff0c\u8eab\u4f53\u865a\u8f6f\uff0c\u4e5f\u4e0d\u6562\u53c2\u52a0\u3002\u6240\u4ee5\u6700\u540e\u53c2\u52a0\u7684\u6709\u4e2d \u56fd\u7684\u6842\u534e\u751f\uff0c\u6ce2\u65af\u7684\u9093\u5357\u906e\uff0c\u548c\u5c3c\u6cca\u5c14\u672c\u56fd\u7684\u6b66\u58eb\u62c9\u6c57\u56fe\u3002\u62bd\u7b7e\u7ed3\u679c\uff0c\u7531\u62c9\u6c57\u56fe\u5148\u8bd5\uff0c\u5176\u6b21 \u662f\u9093\u5357\u906e\uff0c\u6700\u540e\u624d\u662f\u6842\u534e\u751f\u3002\u6842\u534e\u751f\u671b\u7740\u56ed\u4e2d\u642d\u8d77\u7684\u9ad8\u53f0\uff0c\u5fc3\u4e2d\u4e0a\u4e0b\u7684\u8df3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u51b0\u9b44\u5bd2\u5149\u5251\u300b\u2014\u2014\u7b2c\u5341\u4e8c\u7ae0\u3000\u6d1e\u623f\u7ea2\u70db\u7ed3\u9e33\u9e2f \u6881\u7fbd\u751f\u300a\u51b0\u9b44\u5bd2\u5149\u5251\u300b \u7b2c\u5341\u4e8c\u7ae0\u3000\u6d1e\u623f\u7ea2\u70db\u7ed3\u9e33\u9e2f \u3000\u3000\u6bd4\u6b66\u7684\u65f6\u523b\u5f00\u59cb\u4e86\uff0c\u53f0\u4e0b\u6324\u6ee1\u4e86\u5404\u56fd\u6765\u7684\u6b66\u58eb\uff0c\u4ed6\u4eec\u4e5f\u662f\u521d\u8bd5\u590d\u8bd5\u4e2d\u843d\u9009\u7684\u4eba\uff0c\u8fd9\u65f6\u90fd \u6000\u7740\u65e2\u7fa1\u4e14\u5992\u7684\u5fc3\u60c5\uff0c\u770b\u7740\u5750\u5728\u5019\u9009\u5e2d\u4e0a\u7684\u4e09\u4e2a\u5e78\u8fd0\u513f\u6842\u534e\u751f\u3001\u9093\u5357\u906e\u548c\u62c9\u6c57\u56fe\u3002 \u3000\u3000\u594f\u4e50\u58f0\u4e2d\uff0c\u9ad8\u53f0\u4e0a\u7684\u6162\u5e55\u7f13\u7f13\u62c9\u5f00\uff0c\u5c3c\u6cca\u5c14\u516c\u4e3b\u4ece\u53f0\u540e\u8f7b\u8f7b\u8d70\u51fa\uff0c\u8fd9\u970e\u90a3\u95f4\u5168\u573a\u5bc2\u9759\u65e0 \u58f0\uff0c\u9057\u61be\u7684\u662f\u516c\u4e3b\u62ab\u7740\u9762\u7eb1\uff0c\u53ef\u662f\u4ece\u5979\u9732\u51fa\u6765\u7684\u90a3\u5bf9\u660e\u77e5\u79cb\u6c34\u7684\u773c\u775b\uff1b\u4ece\u8749\u7ffc\u822c\u9762\u7eb1\u4e2d\u6240\u9732 \u51fa\u6765\u7684\u5bb9\u5149\uff0c\u5df2\u662f\u6559\u4eba\u4e0d\u6562\u8feb\u89c4\u3002\u201c\u96fe\u91cc\u770b\u82b1\u201d\u53e6\u6709\u4e00\u79cd\u7f8e\u611f\uff0c\u5343\u767e\u9053\u6b66\u58eb\u7684\u773c\u5149\u90fd\u6ce8\u5230\u516c \u4e3b\u7684\u9762\u7eb1\uff0c\u867d\u7136\u770b\u4e0d\u771f\u5207\uff0c\u5374\u4e5f\u201c\u611f\u5230\u201d\u4e86\u5979\u90a3\u7edd\u4e16\u98ce\u59ff\uff01\u514b\u96f7\u65af\u60f3\u9053\uff1a\u201c\u62c9\u4e01\u8bed\u5b66\u5bb6\u8bf4\uff0c \u201c\u7f8e\u201d\u4e0d\u4f46\u662f\u7528\u773c\u775b\u6765\u770b\u5230\u7684\uff0c\u800c\u4e14\u662f\u7528\u201c\u5fc3\u201d\u6765\u201c\u611f\u201d\u5230\u7684\uff0c\u8fd9\u8bdd\u8bf4\u5f97\u771f\u771f\u4e0d\u9519\uff01\u201d \u3000\u3000\u4e50\u58f0\u4e00\u505c\uff0c\u53f8\u4ec6\u9ad8\u53eb\u9053\uff1a\u201c\u7b2c\u4e00\u4f4d\u5019\u9009\u4eba\u5c3c\u6cca\u5c14\u6b66\u58eb\u62c9\u6c57\u56fe\uff0c\u8bf7\u6765\u63a5\u53d7\u516c\u4e3b\u7684\u8003\u8bd5\u3002\u8003 \u7684\u662f\u5251\u672f\u3002\u201d\u62c9\u6c57\u56fe\u8d70\u4e0a\u9ad8\u53f0\uff0c\u5411\u516c\u4e3b\u5c48\u819d\u884d\u4e86\u4e00\u793c\uff0c\u8eab\u8eaf\u98a4\u6296\u5f97\u975e\u5e38\u5389\u5bb3\uff0c\u516c\u4e3b\u5fae\u5fae\u4e00 \u7b11\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4e00\u4e2a\u6b66\u58eb\u5728\u7ade\u6280\u573a\u4e0a\uff0c\u5e94\u8be5\u52c7\u6562\u9547\u5b9a\uff0c\u80dc\u8d1f\u8fd8\u5728\u5176\u6b21\uff0c\u6700\u7d27\u8981\u7684\u662f\u8981\u8868\u73b0 \u51fa\u4f60\u6700\u9ad8\u7684\u6c34\u5e73\u3002\u201d\u62c9\u6c57\u56fe\u5f97\u4e86\u516c\u4e3b\u7684\u9f13\u52b1\uff0c\u7a0d\u7a0d\u9547\u5b9a\uff0c\u62d4\u51fa\u5251\u6765\uff0c\u9053\uff1a\u201c\u6211\u5f53\u5c3d\u529b\u800c\u4e3a\uff0c \u8bf7\u516c\u4e3b\u6307\u70b9\uff01\u201d\u4ed6\u5fc3\u60c5\u6fc0\u52a8\uff0c\u58f0\u97f3\u4e5f\u663e\u5f97\u98a4\u6296\u4e0d\u5b89\u3002\u8981\u77e5\u516c\u4e3b\u5728\u56fd\u4eba\u773c\u4e2d\uff0c\u89c4\u82e5\u5973\u5764\uff0c\u62c9\u6c57 \u56fe\u73b0\u5728\u63a5\u8fd1\u516c\u4e3b\uff0c\u5fc3\u4e2d\u65e2\u662f\u559c\u60a6\uff0c\u53c8\u662f\u6050\u60e7\u3002 \u3000\u3000\u516c\u4e3b\u5fae\u5fae\u4e00\u7b11\uff0c\u62ff\u51fa\u4e86\u4e00\u6839\u7389\u7b1b\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5c31\u7528\u8fd9\u652f\u7b1b\u5f53\u4f5c\u5251\u4f7f\uff0c\u4f60\u7528\u5fc3\u63a5\u62db\u5427\uff1f\u201d \u3000\u3000\u7389\u7b1b\u8f7b\u8f7b\u5212\u4e86\u534a\u9053\u5706\u5f27\uff0c\u540c\u62c9\u6c57\u56fe\u864e\u53e3\u4e00\u70b9\uff0c\u53f0\u4e0b\u90fd\u662f\u5404\u56fd\u4f7f\u5251\u597d\u624b\uff0c\u4e00\u89c1\u516c\u4e3b\u8fd9\u4e00\u62db \u6b63\u662f\u5251\u672f\u4e2d\u4e00\u4e2a\u6781\u7f8e\u597d\u7684\u59ff\u5f0f\uff0c\u4e0d\u7981\u559d\u8d77\u5f69\u6765\u3002\u62c9\u6c57\u56fe\u6a2a\u5251\u4e00\u5c01\uff0c\u5251\u5c16\u98a4\u52a8\uff0c\u53ea\u542c\u5f97\u201c\u55e4\u201d \u7684\u4e00\u58f0\uff0c\u7389\u7b1b\u8f7b\u8f7b\u5212\u8fc7\uff0c\u5c06\u4ed6\u7684\u8863\u8896\u5212\u7a7f\u4e86\uff0c\u516c\u4e3b\u9053\uff1a\u201c\u518d\u9547\u5b9a\u4e9b\uff01\u201d\u62c9\u6c57\u56fe\u9762\u4e0a\u4e00\u7ea2\uff0c\u53d1 \u4e86\u4e2a\u72e0\uff0c\u907f\u5f00\u516c\u4e3b\u7684\u773c\u5149\uff0c\u53ea\u5f53\u9762\u524d\u7684\u4e0d\u662f\u516c\u4e3b\uff0c\u800c\u662f\u4e0e\u4ed6\u51b3\u6218\u7684\u654c\u4eba\uff0c\u8fd9\u6837\u4e00\u6765\uff0c\u5251\u672f\u4f7f \u51fa\uff0c\u65b9\u59cb\u4e2d\u89c4\u4e2d\u77e9\u3002 \u3000\u3000\u62c9\u6c57\u56fe\u6b66\u529f\u5176\u5b9e\u4e0d\u5f31\uff0c\u957f\u5251\u5c55\u5f00\uff0c\u9690\u9690\u5e26\u7740\u98ce\u96f7\u4e4b\u58f0\uff0c\u6c89\u96c4\u8fc5\u901f\uff0c\u517c\u800c\u6709\u4e4b\uff0c\u4f46\u516c\u4e3b\u5374 \u662f\u4ece\u5bb9\u5e94\u4ed8\uff0c\u7389\u7b1b\u76d8\u65cb\u98de\u821e\uff0c\u62db\u62db\u85cf\u7740\u65e0\u7a77\u53d8\u5316\uff0c\u53f0\u4e0b\u6b66\u58eb\u90fd\u5728\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u82e5\u7136\u662f\u6211\uff0c\u53ea \u6015\u8fd9\u65f6\u65e9\u5df2\u8d25\u4e86\uff01\u201d\u5927\u7ea6\u8fc7\u4e86\u4e00\u652f\u9999\u7684\u65f6\u523b\uff0c\u62c9\u6c57\u56fe\u5df2\u63a5\u8fde\u88ab\u7389\u7b1b\u70b9\u4e2d\u4e09\u4e0b\uff0c\u5e78\u800c\u516c\u4e3b\u4e0d\u662f \u7528\u5251\uff0c\u8981\u4e0d\u7136\u8fd0\u4e2d\u4e09\u4e0b\uff0c\u6015\u4e0d\u7834\u523a\u4e09\u5904\u900f\u660e\u7684\u7a9f\u8428\uff0c\u62c9\u6c57\u56fe\u5c06\u5251\u4e00\u629b\uff0c\u5c48\u819d\u8bf4\u9053\uff1a\u201c\u516c\u4e3b\uff0c \u6211\u5b9e\u5728\u4e0d\u6210\uff0c\u60ed\u6127\u5f97\u5f88\u3002\u201d\u516c\u4e3b\u9053\uff1a\u201c\u4e0d\uff0c\u4f60\u5df2\u5c3d\u4e86\u4f60\u7684\u529b\u4e86\u3002\u5728\u54b1\u4eec\u7684\u6b66\u58eb\u4e2d\uff0c\u6ca1\u6709\u8c01\u7684 \u5251\u672f\u53ef\u4ee5\u6bd4\u5f97\u4e0a\u4e86\uff0c\u4f60\u5e94\u8be5\u586b\u8865\u5fa1\u6797\u519b\u603b\u7ba1\u4e4b\u804c\u3002\u201d\u62c9\u6c57\u56fe\u7684\u5931\u8d25\u4e43\u5728\u610f\u6599\u4e4b\u4e2d\uff0c\u5f97\u4efb\u5fa1\u6797 \u519b\u603b\u7ba1\u5374\u5728\u610f\u6599\u4e4b\u5916\uff0c\u4e8e\u662f\u6b22\u5929\u559c\u5730\u7684\u4e0b\u53f0\u3002 \u3000\u3000\u7b2c\u4e8c\u4e2a\u8f6e\u5230\u4e86\u9093\u5357\u906e\uff0c\u4ed6\u6709\u610f\u5356\u5f04\u6b66\u529f\uff0c\u53f8\u4eea\u5b98\u4e00\u53eb\u51fa\u4ed6\u7684\u540d\u5b57\uff0c\u4ed6\u5c31\u8dc3\u4e0a\u9ad8\u53f0\uff0c\u53f0\u9ad8 \u4e09\u4e08\uff0c\u4e5f\u4e0d\u89c1\u4ed6\u600e\u6837\u7eb5\u8eab\u4f5c\u52bf\uff0c\u53ea\u662f\u811a\u5c16\u8f7b\u8f7b\u70b9\u5730\u4fbf\u8dc3\u4e0a\u53bb\u4e86\u3002\u4ed6\u5411\u516c\u4e3b\u884d\u4e86\u793c\u540e\uff0c\u5374\u5e76\u4e0d \u62d4\u5251\uff0c\u53ea\u662f\u63ea\u7740\u516c\u4e3b\u624b\u4e2d\u7684\u90a3\u7ba1\u7389\u7b1b\u3002 \u3000\u3000\u516c\u4e3b\u6de1\u6de1\u7684\u8bf4\u9053\uff1a\u201c\u6211\u8fd8\u662f\u7528\u8fd9\u652f\u7b1b\uff0c\u8bf7\u4f60\u62d4\u5251\u3002\u201d\u9093\u5357\u906e\u6781\u4e3a\u81ea\u8d1f\uff0c\u51e0\u66fe\u53d7\u8fc7\u5982\u6b64\u8f7b \u89c6\uff0c\u5fc3\u4e2d\u751f\u6c14\uff0c\u672c\u5f85\u4e0d\u4f9d\uff0c\u4f46\u8f6c\u5ff5\u4e00\u60f3\uff1a\u201c\u5979\u7528\u8fd9\u652f\u7389\u7b1b\uff0c\u5fc5\u7136\u7ed9\u6211\u6253\u8d25\u65e0\u7591\u3002\u4f46\u80fd\u4e2d\u9009\uff0c \u6211\u53c8\u4f55\u5fc5\u4e89\u8fd9\u53e3\u95ee\u6c14\uff1f\u201d\u601d\u5ff5\u53ca\u6b64\uff0c\u4e0d\u6012\u53cd\u559c\uff0c\u4e8e\u662f\u201c\u641c\u201d\u7684\u4e00\u58f0\u62d4\u51fa\u4e00\u628a\u5f27\u5f62\u7684\u957f\u5251\uff0c\u6717 \u58f0\u8bf4\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u8c28\u4f9d\u516c\u4e3b\u4e4b\u547d\uff0c\u8bf7\u6055\u6211\u653e\u8086\u3002\u201d\u8e0f\u524d\u4e00\u6b65\uff0c\u522b\u7684\u4e00\u5251\uff0c\u4fbf\u5411\u516c\u4e3b\u7389\u81c2 \u524a\u4e0b\u3002 \u3000\u3000\u8fd9\u4e00\u5251\u62db\u5f0f\u5947\u5e7b\uff0c\u516c\u4e3b\u5fc3\u5934\u4e00\u51db\uff0c\u79fb\u6b65\u95ea\u5f00\uff0c\u53cd\u624b\u70b9\u4ed6\u7684\u201c\u80a9\u4e95\u7a74\u201d\uff0c\u9093\u5357\u906e\u8fd9\u628a\u5251\u5f0f \u6837\u7279\u522b\uff0c\u5251\u8eab\u7565\u4f5c\u5f27\u5f62\uff0c\u65e2\u53ef\u523a\u524a\uff0c\u53c8\u53ef\u52fe\u62ff\uff0c\u517c\u6709\u4e2d\u56fd\u5175\u5668\u4e2d\u864e\u5934\u9493\u4e0e\u4e94\u884c\u5251\u4e4b\u5229\uff0c\u5b83\u7684 \u5251\u672f\u914d\u5408\u4e86\u8fd9\u628a\u5f62\u5f0f\u7279\u522b\u7684\u957f\u5251\uff0c\u679c\u7136\u4e0e\u4f17\u4e0d\u540c\uff0c\u6bcf\u4e00\u62db\u5f0f\u90fd\u662f\u51fa\u4eba\u610f\u8868\u3002 \u3000\u3000\u516c\u4e3b\u51dd\u795e\u5e94\u4ed8\uff0c\u628a\u5979\u65b0\u521b\u7684\u51b0\u5ddd\u5251\u6cd5\u65bd\u5c55\u51fa\u6765\uff0c\u70c8\u65e5\u4e4b\u4e0b\uff0c\u7389\u7b1b\u98de\u821e\u7adf\u662f\u5e26\u7740\u68ee\u68ee\u5bd2 \u610f\u3002\u6218\u5230\u9163\u5904\uff0c\u4f46\u89c1\u7389\u7b1b\u751f\u8f89\uff0c\u5251\u5149\u95ea\u95ea\uff0c\u76d8\u65cb\u8fdb\u9000\uff0c\u8d77\u843d\u53d8\u5316\uff0c\u4e0d\u53ef\u540d\u72b6\uff0c\u4e0d\u53ef\u6349\u6478\u3002\u6253 \u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u4ecd\u662f\u96be\u5206\u96be\u89e3\uff01\u53f0\u4e0b\u7684\u5404\u56fd\u6b66\u58eb\uff0c\u770b\u5f97\u60ca\u5fc3\u52a8\u9b44\uff0c\u624b\u4e0a\u90fd\u634f\u7740\u4e00\u628a\u6c57\u3002 \u3000\u3000\u516c\u4e3b\u8fd9\u5957\u201c\u51b0\u5ddd\u5251\u6cd5\u201d\uff0c\u8fd9\u6b21\u8fd8\u662f\u7b2c\u4e8c\u6b21\u62ff\u6765\u5e94\u7528\uff0c\u8d77\u521d\u4e0d\u5927\u7eaf\u719f\uff0c\u6218\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u6e10 \u6e10\u719f\u80fd\u751f\u5de7\uff0c\u5f53\u771f\u662f\u50cf\u51b0\u5ddd\u4e00\u6837\uff0c\u8868\u9762\u9759\u6b62\uff0c\u5185\u91cc\u6697\u6d41\u6c79\u6d8c\uff0c\u5a01\u529b\u4e4b\u5927\uff0c\u96be\u4ee5\u60f3\u50cf\uff0c\u9093\u5357\u906e \u5012\u5438\u4e86\u4e00\u53e3\u51b7\u6c14\uff0c\u8fd9\u624d\u77e5\u9053\u5389\u5bb3\uff01 \u3000\u3000\u518d\u8fc7\u4e00\u76cf\u8336\u7684\u65f6\u523b\uff0c\u516c\u4e3b\u5251\u62db\u4e00\u53d8\uff0c\u7389\u7b1b\u4e1c\u6307\u897f\u5212\uff0c\u8868\u9762\u770b\u6765\uff0c\u597d\u50cf\u6f2b\u4e0d\u7ecf\u5fc3\uff0c\u8f6f\u7ef5\u7ef5 \u7684\u6beb\u4e0d\u7740\u529b\uff0c\u5176\u5b9e\u6b63\u662f\u67d4\u5982\u67f3\u7d6e\uff0c\u7fe9\u82e5\u60ca\u9e3f\uff0c\u9759\u5982\u5904\u5b50\uff0c\u52c3\u5982\u8131\u5154\uff0c\u62db\u62db\u90fd\u85cf\u7740\u7cbe\u5947\u7684\u53d8 \u5316\u3002\u9093\u5357\u906e\u90a3\u5957\u53e4\u602a\u7684\u5251\u672f\uff0c\u7adf\u662f\u6e10\u6e10\u4e3a\u5979\u514b\u5236\uff0c\u529b\u4e0d\u4ece\u5fc3\u3002\u9093\u5357\u906e\u5fc3\u4e2d\u7126\u6025\uff0c\u6076\u5ff5\u7a81\u751f\uff0c \u628a\u5168\u8eab\u771f\u529b\u51dd\u805a\u5251\u5c16\uff0c\u5927\u559d\u4e00\u58f0\uff0c\u9661\u7136\u4e00\u5251\u5288\u4e0b\uff0c\u4ed6\u5fc3\u4e2d\u65e9\u6253\u4e86\u5982\u610f\u7b97\u76d8\uff0c\u516c\u4e3b\u5373\u7b97\u6321\u5f97\u4f4f \u8fd9\u4e00\u5251\uff0c\u5979\u624b\u4e2d\u90a3\u6839\u7389\u7b1b\u4e5f\u5fc5\u5b9a\u8981\u7ed9\u5288\u65ad\uff1b\u82e5\u662f\u6321\u4e0d\u4f4f\u5462\uff0c\u90a3\u4e48\u516c\u4e3b\u53ef\u80fd\u7389\u6ba1\u9999\u6d88\uff0c\u867d\u7136\u53ef \u60dc\uff0c\u4f46\u4e5f\u603b\u80dc\u4e8e\u843d\u5728\u522b\u4eba\u4e4b\u624b\u3002 \u3000\u3000\u53f0\u4e0b\u4e0d\u4e4f\u5404\u56fd\u7684\u5251\u672f\u540d\u5bb6\uff0c\u9093\u5357\u906e\u4e00\u51fa\u6b64\u62db\uff0c\u4e0d\u5c11\u4eba\u770b\u51fa\u4e86\u4ed6\u6b79\u6bd2\u7684\u7528\u610f\uff0c\u7eb7\u7eb7\u559d\u9a82\uff0c \u5c31\u5728\u559d\u9a82\u58f0\u4e2d\uff0c\u53ea\u89c1\u516c\u4e3b\u7389\u7b1b\u4e00\u6325\uff0c\u9661\u7136\u95f4\u53ea\u542c\u5f97\u9093\u5357\u906e\u4e00\u58f0\u5389\u53eb\uff0c\u7acb\u5373\u4ece\u4e09\u4e08\u7684\u9ad8\u53f0\u8dcc \u4e0b\uff0c\u6253\u4e86\u51e0\u4e2a\u6eda\uff0c\u624d\u52c9\u5f3a\u6323\u624e\u8d77\u6765\uff0c\u9762\u9752\u5507\u767d\uff0c\u6d51\u8eab\u4e0a\u4e0b\uff0c\u4ecd\u662f\u6296\u4e2a\u4e0d\u505c\u3002 \u3000\u3000\u539f\u6765\u516c\u4e3b\u5728\u4ed6\u90a3\u4e00\u5251\u5288\u4e0b\u4e4b\u9645\uff0c\u7a81\u7136\u4ece\u7389\u7b1b\u4e4b\u4e2d\uff0c\u5439\u51fa\u4e09\u9897\u51b0\u5757\u795e\u5f39\uff0c\u672c\u6765\u4ee5\u9093\u5357\u906e\u7684 \u529f\u529b\uff0c\u8fd8\u53ef\u4ee5\u627f\u53d7\u5f97\u8d77\u3002\u4f46\u4ed6\u4e00\u6765\u662f\u56e0\u4e3a\u6628\u665a\u548c\u6842\u534e\u751f\u6076\u6597\u4e86\u4e00\u573a\uff0c\u771f\u529b\u6d88\u8017\u4e0d\u5c11\uff1b\u4e8c\u6765\u4ed6 \u7edd\u5bf9\u60f3\u4e0d\u5230\u516c\u4e3b\u7684\u51b0\u9b44\u795e\u5f39\u7adf\u7136\u4f1a\u4ece\u7b1b\u4e2d\u5439\u51fa\u3002\u8fd9\u4e09\u9897\u51b0\u5f39\uff0c\u90fd\u6253\u4e2d\u4e86\u5b83\u7684\u7a74\u9053\uff0c\u5947\u51b7\u653b \u5fc3\uff0c\u603b\u7b97\u9093\u5357\u906e\u529f\u529b\u6df1\u6e5b\uff0c\u6ca1\u6709\u5f53\u573a\u51b7\u6bd9\uff0c\u4f46\u4ea6\u5c11\u4e0d\u4e86\u5927\u75c5\u4e00\u573a\u3002 \u3000\u3000\u5c31\u5728\u4f17\u6b66\u58eb\u60ca\u6115\u4e4b\u4e2d\uff0c\u53f8\u4eea\u5b98\u51fa\u58f0\u53eb\u9053\uff1a\u201c\u6700\u540e\u4e00\u4f4d\u5019\u9009\u4eba\uff0c\u4e2d\u56fd\u6b66\u58eb\u6842\u534e\u751f\u8bf7\u4e0a\u53f0\u4e0e \u516c\u4e3b\u6bd4\u5251\uff01\u201d \u3000\u3000\u6842\u534e\u751f\u4ece\u4fa7\u9762\u7684\u957f\u68af\uff0c\u7f13\u7f13\u8d70\u4e0a\u9ad8\u53f0\uff0c\u672c\u6765\u4ed6\u4e5f\u53ef\u4ee5\u4e00\u8dc3\u800c\u4e0a\uff0c\u4f46\u4ed6\u5374\u4e0d\u613f\u5728\u516c\u4e3b\u9762 \u524d\uff0c\u6545\u610f\u5356\u5f04\u6b66\u529f\u3002\u4e0a\u5230\u53f0\u6765\uff0c\u4f46\u89c1\u516c\u4e3b\u7684\u773c\u4e2d\uff0c\u542b\u6709\u4e07\u79cd\u67d4\u60c5\uff0c\u5343\u822c\u871c\u610f\uff0c\u6842\u534e\u751f\u5fc3\u5934\u4e00 \u6c64\uff0c\u767b\u65f6\u75f4\u4e86\u3002 \u3000\u3000\u53ea\u542c\u5f97\u516c\u4e3b\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u8bf7\u62d4\u5251\u5427\uff01\u201d\u6842\u534e\u751f\u8fd9\u624d\u8bb0\u8d77\u662f\u8981\u548c\u516c\u4e3b\u6bd4\u5251\uff0c\u4e8e\u662f\u5411\u516c\u4e3b\u65bd \u4e86\u4e00\u793c\uff0c\u8bf4\u9053\uff1a\u201c\u5ba2\u4e0d\u61f5\u4e3b\uff0c\u8bf7\u516c\u4e3b\u8d50\u62db\u3002\u201d \u3000\u3000\u516c\u4e3b\u9053\u4e86\u4e00\u58f0\uff1a\u201c\u4e5f\u597d\u3002\u201d\u628a\u7389\u7b1b\u4e00\u629b\uff0c\u53eb\u9053\uff1a\u201c\u5b9b\u5170\u661f\uff0c\u5c06\u6211\u7684\u51b0\u9b44\u5bd2\u5149\u5251\u62ff\u6765\uff01\u201d \u3000\u3000\u90a3\u4e2a\u5bab\u5973\u65e9\u5df2\u5728\u53f0\u540e\u51c6\u5907\uff0c\u7acb\u5373\u5e94\u58f0\u800c\u51fa\uff0c\u5c06\u5bd2\u7389\u5251\u5323\u6367\u4e0a\uff0c\u516c\u4e3b\u5fae\u5fae\u4e00\u7b11\uff0c\u62d4\u5251\u51fa \u9500\uff0c\u767b\u65f6\u4e00\u9053\u5bd2\u5149\u75be\u5c04\u800c\u51fa\uff0c\u53f0\u524d\u90a3\u51e0\u6392\u6b66\u58eb\uff0c\u4f46\u89c9\u51b7\u6c14\u68ee\u68ee\uff0c\u76ae\u80a4\u8d77\u7c9f\uff0c\u90fd\u4e0d\u7981\u5403\u4e86\u4e00 \u60ca\uff0c\u4eba\u4eba\u8be7\u5f02\uff0c\u5929\u4e0b\u7adf\u6709\u8fd9\u6837\u7684\u5b9d\u5251\u3002 \u3000\u3000\u6842\u534e\u751f\u8ddf\u7740\u4e5f\u62d4\u51fa\u5251\u6765\uff0c\u4ed6\u7684\u5251\u4e5f\u662f\u7a00\u4e16\u5947\u73cd\uff0c\u5fae\u4e00\u6325\u52a8\uff0c\u5251\u5c16\u7adf\u5e26\u7740\u9690\u9690\u7684\u5578\u58f0\uff0c\u6709 \u82e5\u9f99\u541f\uff0c\u82e5\u662f\u6842\u534e\u751f\u5148\u884c\u4eae\u5251\uff0c\u4f17\u6b66\u58eb\u51c6\u4f1a\u5403\u60ca\uff0c\u4f46\u5982\u4eca\u5728\u51b0\u5757\u5bd2\u5149\u5251\u7684\u5bf9\u6bd4\u4e4b\u4e0b\uff0c\u5374\u4e0d\u514d \u9eef\u7136\u5931\u8272\uff01\u4f17\u6b66\u58eb\u90fd\u5728\u60f3\u9053\uff1a\u201c\u82e5\u662f\u6211\u5728\u53f0\u4e0a\uff0c\u4f11\u8bf4\u6bd4\u5251\uff0c\u53ea\u6015\u8fd9\u5947\u5bd2\u4e4b\u6c14\uff0c\u5148\u5c31\u96be\u6321\uff01\u201d \u6842\u534e\u751f\u9053\u4e86\u4e00\u4e2a\u201c\u8bf7\u201d\u5b57\uff0c\u53ea\u89c1\u516c\u4e3b\u9999\u80a9\u4e00\u6643\uff0e\u51b0\u5757\u5bd2\u5149\u5251\u6a2a\u7a7a\u4e00\u63a0\uff0c\u75be\u5982\u7535\u63a3\uff0c\u5411\u6842\u534e\u751f \u9888\u9879\u524a\u6765\uff0c\u6842\u534e\u751f\u5fc3\u4e2d\u5145\u6ee1\u67d4\u60c5\uff0c\u5ffd\u89c1\u516c\u4e3b\u4e00\u51fa\u624b\u5c31\u662f\u8fd9\u6837\u795e\u5947\u7684\u62db\u6570\uff0c\u5fc3\u4e2d\u4e00\u51db\uff0c\u767e\u5fd9\u4e2d \u7528\u4e86\u4e00\u62db\u201c\u4e91\u6a2a\u79e6\u5cad\u201d\uff0c\u63a5\u7740\u4e00\u62db\u201c\u96ea\u62e5\u84dd\u5173\u201d\uff0c\u597d\u4e0d\u5bb9\u6613\u624d\u5c06\u516c\u4e3b\u7684\u653b\u52bf\u89e3\u5f00\uff0c\u516c\u4e3b\u4e1d\u6beb \u4e0d\u7f13\uff0c\u4e00\u5251\u7d27\u4f3c\u4e00\u5251\uff0c\u628a\u6842\u534e\u751f\u8feb\u5f97\u8fd0\u8fd4\u51e0\u6b65\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u5c0f\u5fc3\u63a5\u62db\uff01\u201d\u6842\u534e\u751f\u53d1\u89c9\u5979\u773c \u4e2d\u6709\u8d23\u5907\u4e4b\u610f\uff0c\u5fc3\u5934\u4e00\u9192\uff0c\u60f3\u9053\uff1a\u201c\u662f\u4e86\uff0c\u6211\u82e5\u4e0d\u663e\u51fa\u672c\u9886\uff0c\u7eb5\u7136\u5730\u6545\u610f\u8ba9\u6211\uff0c\u5f53\u7740\u5404\u56fd\u6b66 \u58eb\uff0c\u8fd9\u6837\u8d62\u4e86\uff0c\u4e5f\u4e0d\u5149\u5f69\uff01\u201d \u3000\u3000\u5f53\u4e0b\u7cbe\u795e\u51dd\u805a\uff0c\u5251\u8bc0\u4e00\u987f\uff0c\u4e00\u62db\u201c\u661f\u6d77\u6d0b\u6413\u201d\uff0c\u5251\u5149\u5982\u8679\uff0c\u8fd8\u653b\u8fc7\u53bb\u3002\u516c\u4e3b\u5fae\u9732\u7b11\u5bb9\uff0c \u907f\u62db\u8fdb\u62db\uff0c\u4e24\u4eba\u65d7\u9f13\u76f8\u5f53\uff0c\u6740\u5f97\u4e2a\u96be\u5206\u96be\u89e3\u3002 \u3000\u3000\u6fc0\u6218\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u516c\u4e3b\u4e1d\u6beb\u4e0d\u8ba9\uff0c\u8feb\u5f97\u6842\u534e\u751f\u5c06\u6d51\u8eab\u672c\u9886\u90fd\u65bd\u5c55\u51fa\u6765\uff0c\u4e24\u67c4\u5b9d\u5251\uff0c\u76d8\u65cb \u98de\u821e\uff0c\u6597\u5230\u75be\u5904\uff0c\u4f46\u89c1\u5bd2\u5149\u4e00\u7247\uff0c\u5251\u6c14\u5343\u6761\uff0c\u76f4\u628a\u4f17\u6b66\u58eb\u770b\u5f97\u773c\u82b1\u9563\u4e71\uff0c\u7adf\u7136\u5206\u4e0d\u51fa\u8c01\u662f\u516c \u4e3b\uff0c\u8c01\u662f\u534e\u751f\uff01 \u3000\u3000\u6842\u534e\u751f\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u8981\u4e0d\u662f\u6211\u719f\u8bc6\u5979\u7684\u51b0\u5ddd\u5251\u6cd5\uff0c\u4eca\u756a\u5fc5\u5b9a\u843d\u8d25\u65e0\u7591\uff01\u201d\u539f\u6765\u516c\u4e3b\u5728\u5ff5 \u9752\u5510\u53e4\u62c9\u5c71\u7684\u51b0\u5ddd\uff0c\u521b\u51fa\u8fd9\u5957\u201c\u51b0\u5ddd\u5251\u6cd5\u201d\u65f6\uff0c\u66fe\u548c\u6842\u534e\u751f\u5171\u540c\u7814\u7a76\uff0c\u8981\u77e5\u4efb\u4f55\u4e00\u5957\u65b0\u521b\u7684 \u7684\u5251\u6cd5\uff0c\u867d\u7136\u5a01\u529b\u5947\u5927\uff0c\u4f46\u603b\u6709\u672a\u66fe\u5b8c\u5907\u7684\u5730\u65b9\uff0c\u6842\u534e\u751f\u65e2\u719f\u77e5\u201c\u51b0\u5ddd\u5251\u6cd5\u201d\u7684\u4f18\u52a3\u6240\u5728\uff0c \u4e0d\u987b\u516c\u4e3b\u9976\u8ba9\uff0c\u6e10\u6e10\u4fbf\u5360\u4e86\u4e0a\u98ce\u3002\u5404\u56fd\u5251\u672f\u7684\u540d\u5bb6\uff0c\u4e5f\u81ea\u6709\u4eba\u770b\u5f97\u51fa\u6765\u3002 \u3000\u3000\u518d\u8fc7\u4e00\u4f1a\uff0c\u6842\u534e\u751f\u7740\u7740\u53cd\u653b\uff0c\u5c06\u516c\u4e3b\u8feb\u5f97\u8fde\u8fde\u540e\u9000\uff0c\u516c\u4e3b\u7389\u624b\u4e00\u626c\uff0c\u4ee5\u201c\u6ee1\u5929\u82b1\u96e8\u201d\u7684 \u624b\u6cd5\uff0c\u98de\u51fa\u5341\u51e0\u9897\u51b0\u9b44\u795e\u5f39\uff0c\u6842\u534e\u751f\u65e9\u6709\u6240\u5907\uff0c\u4e94\u6307\u75be\u5f39\u5c06\u51b0\u9b44\u795e\u5f39\u90fd\u5728\u53f0\u4e0a\u5f39\u88c2\uff0c\u51b7\u6c14\u5bd2 \u5149\uff0c\u51dd\u805a\u5982\u7f51\uff0c\u53f0\u4e0a\u767d\u832b\u832b\u4e00\u7247\uff0c\u53f0\u4e0b\u524d\u51e0\u6392\u7684\u6b66\u58eb\u7eb7\u7eb7\u5411\u540e\u79fb\u52a8\u3002\u5c31\u5728\u8fd9\u4e00\u970e\u90a3\uff0c\u6842\u534e\u751f \u4e58\u7740\u516c\u4e3b\u53d1\u51fa\u51b0\u5f39\uff0c\u5251\u52bf\u7565\u7f13\u4e4b\u9645\uff0c\u7a81\u7136\u4e00\u8dc3\u800c\u4e0a\uff0c\u5251\u5cf0\u4e00\u6311\uff0c\u6070\u6070\u5c06\u516c\u4e3b\u7684\u9762\u7eb1\u6311\u5f00\uff0c\u767b \u65f6\u9e26\u96c0\u65e0\u58f0\uff0c\u7aef\u7684\u662f\u4e00\u6839\u9488\u8dcc\u5728\u5730\u4e0b\u90fd\u542c\u5f97\u89c1\u54cd\uff01 \u3000\u3000\u6842\u534e\u751f\u8fd9\u4e00\u5251\u771f\u662f\u7edd\u9876\u795e\u5947\u7684\u4e00\u5251\uff0c\u950b\u5229\u7684\u5251\u5c16\u6070\u6070\u5c06\u516c\u4e3b\u7684\u9762\u7eb1\u6311\u5f00\uff0c\u5374\u6ca1\u6709\u4f24\u53ca\u516c \u4e3b\u7684\u4e00\u4e1d\u6beb\u53d1\uff0c\u5728\u8fd9\u970e\u52a8\u4eba\u5fc3\u7684\u4e00\u970e\u90a3\u95f4\uff0c\u53f0\u4e0b\u9759\u5bc2\u77e5\u6b7b\uff0c\u5f85\u5230\u4f17\u4eba\u770b\u6e05\u695a\u4e86\u516c\u4e3b\u6ca1\u6709\u53d7 \u4f24\uff0c\u63a5\u7740\u4fbf\u7206\u51fa\u60ca\u5929\u52a8\u5730\u7684\u6b22\u547c\uff0c\u4e3a\u516c\u4e3b\u7edd\u4e16\u7684\u5bb9\u989c\u3002\u4e3a\u6842\u534e\u751f\u8d85\u51e1\u7684\u5251\u672f\uff0c\u6b22\u547c\uff0c\u6b22\u547c\uff0c \u6b22\u547c\uff01 \u3000\u3000\u5e0c\u814a\u738b\u5b50\u514b\u96f7\u65af\u4e00\u8db3\u8e48\u5728\u6905\u4e0a\uff0c\u5f39\u8d77\u4e86\u4ed6\u7684\u4e03\u5f26\u7434\uff0c\u9ad8\u58f0\u5531\u51fa\u4e86\u4ed6\u7684\u201c\u5373\u5174\u8bd7\u201d\u3002 \u3000\u3000\u201c\u4f60\u8de8\u8fc7\u4e86\u4e16\u754c\u7b2c\u4e00\u9ad8\u5cf0\uff0c \u3000\u3000\u5e26\u6765\u4e86\u7231\u60c5\u7684\u68a6\u60f3\uff1b \u3000\u3000\u4f60\u4e00\u5251\u6311\u5f00\u4e86\u516c\u4e3b\u7684\u9762\u7eb1\uff0c \u3000\u3000\u5c06\u7231\u60c5\u7684\u79cd\u5b50\u64ad\u5728\u5979\u5fc3\u4e0a\u3002 \u3000\u3000\u554a\uff0c\u4f60\u8fd9\u795e\u5947\u7684\u4e00\u5251\u554a\uff01 \u3000\u3000\u80dc\u8fc7\u4e86\u4e18\u6bd4\u7279\u7684\u5f13\u5251\u3002\u201d \u3000\u3000\u514b\u96f7\u65af\u7684\u6b4c\u58f0\u88ab\u6df9\u6ca1\u5728\u6b22\u4e50\u7684\u58f0\u97f3\u7684\u6d77\u6d0b\uff0c\u7136\u800c\u6842\u534e\u751f\u8fd8\u662f\u5728\u4eba\u4e1b\u4e2d\u53d1\u73b0\u4e86\u4ed6\uff0c\u5411\u4ed6\u6295 \u4ee5\u611f\u8c22\u7684\u773c\u5149\uff0c\u514b\u96f7\u65af\u771f\u7684\u50cf\u4ed6\u6240\u8bf4\u7684\u54f2\u5b66\u5bb6\uff0c\u4e00\u6837\u6b23\u8d4f\u7740\u522b\u4eba\u7684\u5e78\u798f\uff0c\u81ea\u5df1\u4e5f\u5c31\u611f\u5230\u975e\u5e38 \u6ee1\u8db3\u4e86\uff01 \u3000\u3000\u516c\u4e3b\u5411\u6842\u534e\u751f\u5fae\u5fae\u4e00\u7b11\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u4f60\u8d62\u4e86\u6211\u5566\uff01\u201d\u8bed\u5e26\u53cc\u5173\uff0c\u6842\u534e\u751f\u5411\u5979\u4e00\u671b\uff0c\u516c\u4e3b \u7f9e\u7ea2\u4e86\u8138\uff0c\u66f3\u5730\u957f\u88d9\uff0c\u5411\u53f0\u4e0b\u76c8\u76c8\u4e00\u793c\uff0c\u4fbf\u7fe9\u7136\u7684\u8fd4\u5230\u5e55\u540e\u3002\u63a5\u7740\u53f8\u4eea\u5b98\u8d70\u51fa\u6765\u5ba3\u5e03\uff0c\u4f73\u534e \u751f\u5df2\u901a\u8fc7\u4e86\u6b66\u529f\u7684\u8003\u8bd5\uff0c\u53ea\u5f85\u660e\u65e5\u516c\u4e3b\u8003\u4ed6\u7684\u6587\u5b66\uff0c\u4fbf\u53ef\u4ee5\u51b3\u5b9a\u4ed6\u662f\u5426\u5f53\u9009\u9a79\u9a6c\u4e86\u3002 \u3000\u3000\u6842\u534e\u751f\u5728\u5404\u56fd\u6b66\u58eb\u7684\u7c07\u62e5\u4e0b\u56de\u5230\u5bbe\u9986\uff0c\u514b\u96f7\u65af\u4e5f\u518d\u4e00\u6b21\u7684\u5411\u4ed6\u9053\u8d3a\u3002\u4e3a\u4e86\u4e0d\u59a8\u788d\u4ed6\u660e\u592d \u7684\u5e94\u8bd5\uff0c\u5927\u5bb6\u95f9\u4e86\u4e00\u9635\uff0c\u4fbf\u5411\u4ed6\u9053\u8fc7\u201c\u665a\u5b89\u201d\uff0c\u5404\u81ea\u6563\u4e86\u3002 \u3000\u3000\u4f46\u6842\u534e\u751f\u5374\u90a3\u91cc\u7761\u5f97\u7740\u89c9\uff0c\u8fd9\u4e00\u665a\u4ed6\u5f7b\u591c\u65e0\u7720\uff0c\u60f3\u8d77\u4e86\u5373\u5c06\u5f97\u5230\u516c\u4e3b\uff0c\u4e5f\u60f3\u8d77\u4e86\u81ea\u5df1\u51fa \u56fd\u4e4b\u65f6\u6240\u7acb\u7684\u5fd7\u613f\uff1a\u8981\u535a\u91c7\u5404\u56fd\u7684\u6b66\u529f\uff0c\u72ec\u521b\u4e00\u5bb6\u7684\u5251\u672f\uff0c\u8fd9\u5fd7\u613f\u770b\u6765\u4e5f\u53ef\u4ee5\u5b8c\u6210\u4e86\u3002\u4ed6\u5df2 \u53c2\u900f\u4e86\u5370\u5ea6\u7684\u4e0a\u4e58\u5185\u529f\uff0c\u53c8\u89c2\u6469\u4e86\u5c3c\u6cca\u5c14\u3001\u6ce2\u65af\u3001\u5e0c\u814a\u3001\u963f\u62c9\u4f2f\u8bf8\u56fd\u7684\u5251\u672f\uff0c\u4ed6\u51c6\u5907\u64b7\u53d6\u8fd9 \u51e0\u4e2a\u6587\u660e\u53e4\u56fd\u7684\u5251\u672f\u7cbe\u534e\uff0c\u5c06\u6765\u90fd\u91c7\u542b\u5728\u4ed6\u548c\u516c\u4e3b\u5408\u521b\u7684\u201c\u51b0\u5ddd\u5251\u6cd5\u201d\u4e4b\u5185\u3002 \u3000\u3000\u7b2c\u4e8c\u65e5\u516c\u4e3b\u5728\u56fd\u738b\u9762\u524d\u4eb2\u81ea\u8003\u5b83\u7684\u6587\u5b66\uff0c\u4ed6\u5bf9\u7b54\u5982\u6d41\uff0c\u5bf9\u5c3c\u6cca\u5c14\u7684\u53e4\u8bd7\u7ecf\u5178\uff0c\u968f\u610f\u5f15 \u7528\uff0c\u5982\u6570\u5bb6\u73cd\uff0c\u4eca\u5230\u56fd\u738b\u4e5f\u5927\u5927\u60ca\u5947\u3002\u4e0d\u8fc7\uff0c\u8fd9\u91cc\u9762\u5374\u6709\u4e00\u4e2a\u56fd\u738b\u4e0d\u77e5\u9053\u7684\u79d8\u5bc6\uff0c\u516c\u4e3b\u6240\u8003 \u7684\u5341\u4e4b\u4e03\u516b\u90fd\u662f\u5728\u516c\u4e3b\u8981\u4ed6\u719f\u8bfb\u7684\u90a3\u5341\u51e0\u90e8\u4e66\u4e4b\u5185\uff0c\u8fd8\u6709\u5341\u4e4b\u4e8c\u4e09\u5219\u662f\u4ee5\u524d\u516c\u4e3b\u548c\u4ed6\u8c08\u8fc7 \u7684\u3002\u4e0d\u8fc7\u5728\u8003\u4ed6\u4e2d\u56fd\u7684\u6587\u5b66\u65f6\uff0c\u6842\u534e\u751f\u5374\u7684\u786e\u663e\u793a\u51fa\u4ed6\u7684\u6e0a\u535a\uff0c\u516c\u4e3b\u51c6\u5bab\u4e2d\u7684\u6c49\u5b66\u5927\u5e08\u53c2\u52a0 \u53d1\u95ee\uff0c\u4ed6\u89e3\u91ca\u7684\u7ecf\u53f2\u5965\u4e49\uff0c\u8fde\u5927\u5e08\u4eec\u4e5f\u95fb\u6240\u672a\u95fb\u3002\u56fd\u738b\u5230\u4e86\u8fd9\u4e2a\u65f6\u5019\uff0c\u5bf9\u6842\u534e\u751f\u7684\u6587\u6b66\u5168 \u624d\uff0c\u4ea6\u81ea\u6df1\u6df1\u4f69\u670d\u3002\u89c9\u5f97\u8fd9\u4f4d\u4e2d\u56fd\u9752\u5e74\uff0c\u867d\u7136\u4e0d\u662f\u4ec0\u4e48\u8d35\u65cf\uff0c\u4f46\u5374\u5b9e\u5728\u80dc\u8fc7\u5404\u56fd\u738b\u5b50\u3002 \u3000\u3000\u4e0d\u8fc7\u6709\u4e00\u4ef6\u610f\u60f3\u4e0d\u5230\u7684\u4e8b\u662f\uff0c\u516c\u4e3b\u6700\u540e\u8fd8\u8003\u4ed6\u4e24\u9053\u9898\u76ee\uff0c\u8bd5\u5b83\u7684\u6025\u624d\uff0c\u4e00\u4e2a\u9898\u76ee\u662f\u8981\u4ed6 \u731c\u4e00\u4e2a\u6b27\u6d32\u8457\u540d\u7684\u6545\u4e8b\u4e2d\u7684\u8c1c\uff0c\u53e6\u4e00\u4e2a\u9898\u76ee\u5219\u8981\u4ed6\u4ee5\u516c\u4e3b\u7684\u540d\u5b57\u505a\u4e00\u5bf8\u65e0\u540d\u8054\uff0c\uff08\u8fd9\u4e24\u9898\u76ee \u53ca\u8003\u8bd5\u7684\u7ecf\u8fc7\u60c5\u5f62\uff0c\u8be6\u89c1\u62d9\u7740\u201c\u51b0\u5ddd\u5929\u5973\u4f20\u201d\u7b2c\u4e09\u518c\uff0c\u8fd9\u91cc\u4e0d\u8d58\u3002\u4e00\u5e78\u800c\u6842\u534e\u751f\u4e5f\u6709\u70b9\u6025 \u624d\uff0c\u7ec8\u4e8e\u4e5f\u901a\u8fc7\u4e86\u3002\u5f53\u56fd\u738b\u4eb2\u53e3\u5ba3\u5e03\u4ed6\u5f53\u9009\u9a79\u9a6c\u65f6\uff0c\u4ed6\u559c\u6b22\u5f97\u51e0\u4e4e\u6655\u5012\uff01 \u3000\u3000\u5a5a\u4e8b\u5df2\u5b9a\uff0c\u56fd\u738b\u5c06\u6842\u534e\u751f\u5b89\u987f\u5728\u745e\u626c\u5e03\u51fa\u7684\u590f\u5bab\uff0c\u5a5a\u671f\u5b9a\u5728\u4e09\u65e5\u4e4b\u540e\u4e3e\u884c\uff0c\u90a3\u6b63\u662f\u5c3c\u6cca \u5c14\u4e00\u5e74\u4e00\u5ea6\u7684\u72c2\u6b22\u8282\u65e5\u2014\u2014\u71c3\u706f\u4f73\u8282\u3002\u8fd9\u4e09\u65e5\u5c31\u597d\u50cf\u6709\u4e09\u5e74\u8fd9\u4e48\u957f\u4e45\uff0c\u6842\u534e\u751f\u597d\u4e0d\u5bb9\u6613\u7b49\u5230 \u4f73\u671f\uff0c\u7167\u5c3c\u6cca\u5c14\u738b\u5ba4\u7684\u5927\u5a5a\u4e4b\u793c\uff0c\u65e5\u95f4\u5728\u745e\u626c\u5e03\u51fa\u7684\u4f5b\u5bfa\u63a5\u53d7\u4e86\u9ad8\u50e7\u7684\u795d\u798f\uff0c\u7b7e\u4e0b\u4e86\u5a5a\u4e66\uff0c \u6842\u534e\u751f\u4ecd\u56de\u590f\u5bab\uff0c\u7b49\u5230\u665a\u4e0a\uff0c\u56fd\u738b\u518d\u6d3e\u4eba\u63a5\u4ed6\u4eba\u738b\u5bab\u5b8c\u6210\u5a5a\u793c\u3002 \u3000\u3000\u6842\u534e\u751f\u8fd9\u4e00\u5929\u5c31\u597d\u50cf\u5728\u68a6\u5883\u91cc\u98d8\u6d6e\u4f3c\u7684\uff0c\u5f53\u771f\u662f\u8fde\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u542b\u5728\u5c3c\u6cca\u5c14\u7f14\u7ed3\u5947\u7f18\uff0c \u5230\u4e86\u9ec4\u660f\uff0c\u4ece\u590f\u5bab\u4e2d\u7684\u51c9\u53f0\u671b\u4e0b\u53bb\uff0c\u4f46\u89c1\u56ed\u4e2d\u6302\u6ee1\u8bb8\u591a\u6c34\u6676\u73bb\u7483\u7684\u5404\u8272\u98ce\u706f\uff0c\u7eff\u6811\u679d\u5934\uff0c\u53c8 \u904d\u7f00\u6c34\u6676\u8461\u8404\uff0c\u4f5c\u4e3a\u88c5\u9970\uff0c\u4e00\u773c\u671b\u592b\uff0c\u4fe8\u5982\u94f6\u82b1\u96ea\u6d6a\uff0c\u73e0\u5b9d\u4e7e\u5764\u3002\u5189\u5411\u8fdc\u671b\uff0c\u52a0\u5fb7\u6ee1\u90fd\u57ce\u5185 \u7684\u706f\u706b\uff0c\u5bc6\u82e5\u7e41\u661f\uff0c\u7b19\u6b4c\u9690\u9690\u53ef\u95fb\u3002\u539f\u6765\u5c3c\u6cca\u5c14\u7684\u201c\u71c3\u706f\u8282\u201d\u4e43\u662f\u4e00\u4e2a\u7231\u60c5\u7684\u8282\u65e5\uff0c\u5230\u4e86\u6b64 \u65e5\uff0c\u5bb6\u5bb6\u6237\u6237\uff0c\u7ed3\u5f69\u5f20\u706f\uff0c\u7eff\u5973\u7ea2\u7537\uff0c\u5c3d\u60c5\u6b4c\u821e\uff0c\u8fde\u5bfa\u5e99\u91cc\u4e5f\u662f\u6574\u591c\u6b4c\u5531\u4e0d\u4f11\u3002\u4eca\u5e74\u4ed6\u4eec\u7684 \u516c\u4e3b\u6070\u597d\u5728\u8fd9\u4e2a\u4f73\u8282\u7ed3\u5a5a\uff0c\u56e0\u4e4b\u66f4\u662f\u4e3e\u56fd\u5982\u72c2\uff0c\u6bd4\u5f80\u5e74\u8981\u70ed\u95f9\u571f\u5341\u500d\u767e\u500d\u3002 \u3000\u3000\u9ec4\u660f\u8fc7\u540e\u4e0d\u4e45\uff0c\u4e00\u8f86\u9a6c\u8f66\u6765\u5230\uff0c\u6b63\u662f\u4ee5\u524d\u63a5\u6842\u534e\u751f\u8fc7\u7684\u90a3\u8f86\u9a6c\u8f66\uff0c\u66f3\u8f66\u7684\u4e5f\u4ecd\u7136\u662f\u90a3\u56db \u5339\u62bc\u9a8f\u7684\u767d\u9a6c\uff0c\u4e0d\u8fc7\uff0c\u4e0a\u4e00\u6b21\u662f\u63a5\u6842\u534e\u751f\u4eba\u5bab\u66ff\u56fd\u738b\u6cbb\u75c5\uff0c\u8fd9\u4e00\u6b21\u5374\u662f\u6309\u4ed6\u4eba\u5bab\u4e0e\u516c\u4e3b\u6210 \u4eb2\uff0c\u5fc3\u60c5\u81ea\u662f\u5927\u5927\u4e0d\u540c\u7684\u4e86\u3002 \u3000\u3000\u5949\u547d\u524d\u6765\u63a5\u6842\u534e\u751f\u7684\u5fa1\u6797\u519b\u7684\u4e00\u4f4d\u5c11\u5e74\u519b\u5b98\uff0c\u9a6c\u8f66\u7ecf\u8fc7\u5c71\u8c37\uff0c\u5ffd\u542c\u5f97\u94c1\u9a91\u9a70\u9aa4\uff0c\u5200\u67aa\u94ff \u545c\u4e4b\u58f0\uff0c\u6842\u534e\u751f\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d\u9a6c\u8f66\u6761\u7684\u505c\u4e0b\uff0c\u53ea\u89c1\u4e00\u5f6a\u9ed1\u8863\u9a91\u961f\uff0c\u75be\u800c\u51b2\u6765\uff0c\u547c\u559d\u4e4b \u58f0\u6b64\u8d77\u5f7c\u843d\uff1a\u201c\u54b1\u4eec\u4e0d\u80fd\u8ba9\u5916\u56fd\u4eba\u5a36\u54b1\u4eec\u7684\u516c\u4e3b\uff01\u201d\u201c\u516c\u4e3b\u5ac1\u4e86\u4ed6\uff0c\u54b1\u4eec\u7684\u56fd\u5bb6\u5c31\u8981\u7ed9\u5e76\u541e \u4e86\uff01\u201d\u201c\u4e0d\u884c\uff0c\u4e0d\u884c\uff0c\u4e00\u5b9a\u8981\u8d76\u4ed6\u56de\u53bb\uff01\u201d\u201c\u96be\u9053\u672c\u56fd\u5c31\u6ca1\u6709\u597d\u7537\u5b50\u4e48\uff1f\u4e0d\u6b22\u8fce\u8fd9\u4e2a\u4e2d\u56fd\u65b0 \u90ce\uff01\u201d\u90a3\u519b\u5b98\u60ca\u60f6\u5931\u8272\uff0c\u9053\uff1a\u201c\u4e0d\u597d\u4e86\uff0c\u4ed6\u4eec\u4e0d\u6b22\u8fce\u4f60\uff0c\u4e3e\u884c\u5175\u53d8\u5566\uff01\u4f60\u5feb\u5feb\u9003\u8d70\u4e86\u5427\u3002 \u3000\u3000\u6842\u534e\u751f\u9053\uff1a\u201c\u6211\u4e0d\u9003\uff01\u201d\u90a3\u519b\u5b98\u9053\uff1a\u201c\u4f60\u4e0d\u9003\u4e0d\u6253\u7d27\uff0c\u6211\u53ef\u4e0d\u80fd\u966a\u4f60\u9001\u547d\uff01\u201d\u53cc\u624b\u4e00 \u6380\uff0c\u8981\u628a\u6842\u534e\u751f\u6380\u4e0b\u8f66\u53bb\uff0c\u6842\u534e\u751f\u4e0d\u6687\u601d\u7d22\uff0c\u53cd\u624b\u4e00\u70b9\uff0c\u4fbf\u70b9\u4e86\u4ed6\u7684\u6655\u7a74\u3002\u8fd9\u65f6\uff0c\u90a3\u5f6a\u8f66 \u9a6c\uff0c\u5df2\u5c06\u9a6c\u8f66\u56f4\u7740\uff0c\u6842\u534e\u751f\u7ad9\u51fa\u6765\u8981\u4e0e\u4ed6\u4eec\u8bf4\u8bdd\uff0c\u4f46\u4ed6\u4eec\u9f13\u566a\u5982\u96f7\uff0c\u90a3\u91cc\u8bf4\u5f97\u6e05\u695a\u3002 \u3000\u3000\u4f73\u534e\u751f\u5438\u4e86\u53e3\u6c14\uff0c\u4ee5\u6781\u4e0a\u4e58\u7684\u5185\u529f\u5410\u51fa\u58f0\u97f3\uff0c\u6717\u6717\u8bf4\u9053\uff1a\u201c\u82e5\u7136\u6211\u4e0d\u53d7\u8d35\u56fd\u56fd\u4eba\u6b22\u8fce\uff0c \u6211\u4e00\u5b9a\u56de\u53bb\u3002\u4f46\u6700\u5c11\u4f60\u4eec\u4e5f\u5f97\u8ba9\u6211\u5230\u4eac\u57ce\u4e00\u770b\uff01\u201d\u8fd9\u51e0\u53e5\u8bdd\u5c06\u90a3\u4e00\u5927\u7247\u566a\u58f0\u90fd\u538b\u4e0b\u53bb\uff0c\u6709\u4e00 \u4e2a\u519b\u5b98\u53eb\u9053\uff1a\u201c\u4e0d\u8981\u4e2d\u4ed6\u7684\u8ba1\uff0c\u4ed6\u662f\u60f3\u53bb\u6c42\u516c\u4e3b\u5e87\u62a4\u4ed6\uff01\u201d\u63d0\u8d77\u957f\u67aa\uff0c\u5220\u7684\u4e00\u6867\u4fbf\u523a\uff0c\u6842\u534e \u751f\u4e00\u624b\u6293\u4f4f\u67aa\u5c16\uff0c\u53eb\u9053\uff1a\u201c\u6211\u5bf9\u4f60\u4eec\u7684\u738b\u4f4d\u7edd\u65e0\u89ca\u89ce\u4e4b\u5fc3\uff0c\u4f60\u4eec\u4e3a\u4ec0\u4e48\u4e0d\u8bb8\u6211\u53bb\u89c1\u6211\u7684\u59bb \u5b50\uff01\u201d\u90a3\u519b\u5b98\u88ab\u4ed6\u6293\u7740\u67aa\u5c16\uff0c\u529b\u6323\u4e0d\u8131\uff0c\u5927\u6012\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u542c\uff0c\u4ed6\u8fd8\u8981\u628a\u516c\u4e3b\u5e26\u8d70\u5462\uff0c\u54b1\u4eec \u56fd\u4e2d\u7684\u5b9d\u8d1d\u4e1c\u897f\uff0c\u53ef\u4e0d\u5bb9\u5916\u56fd\u4eba\u5e26\u8d70\uff01\u201d\u767b\u65f6\u6709\u6570\u5341\u652f\u957f\u77db\u77ed\u5251\u7b49\u5404\u5f0f\u5175\u5668\uff0c\u5411\u6842\u534e\u751f\u6eaf \u6765\uff01 \u3000\u3000\u6842\u534e\u751f\u624b\u6307\u4e00\u677e\uff0c\u5c06\u90a3\u519b\u5b98\u6454\u4e86\u4e00\u4e2a\u5e7c\u6597\uff0c\u7acb\u5373\u62d4\u51fa\u817e\u86df\u5b9d\u5251\uff0c\u56f4\u8eab\u4e00\u7ed5\uff0c\u5251\u5149\u8fc7\u5904\uff0c \u4f46\u542c\u5f97\u4e00\u7247\u65ad\u91d1\u590f\u7389\u4e4b\u58f0\uff0c\u5341\u51e0\u6761\u5175\u5668\u5168\u90fd\u7ed9\u4ed6\u524a\u65ad\u4e86\uff0c\u53ef\u662f\u90a3\u4e00\u5e2e\u9ed1\u8863\u6b66\u58eb\u8fd8\u662f\u8702\u62e5\u800c \u6765\uff0c\u4f73\u534e\u751f\u4e00\u6765\u56e0\u4e3a\u662f\u559c\u65e5\uff0c\u4e8c\u6765\u66f4\u4e0d\u613f\u4f24\u53ca\u5c3c\u6cca\u5c14\u4eba\u7684\u611f\u60c5\uff0c\u867d\u6709\u5b9d\u5251\uff0c\u53ef\u7edd\u4e0d\u6562\u5c06\u4efb\u4f55 \u4e00\u4eba\u4f24\u5bb3\uff0c\u53ea\u662f\u628a\u5175\u5668\u524a\u65ad\u4fbf\u7b97\uff0c\u53ef\u662f\u8fd9\u6837\u4e00\u6765\uff0c\u65bd\u5c55\u5251\u672f\u7b49\u4e8e\u53d7\u4e86\u4e00\u5c42\u675f\u7f1a\uff0c\u7aef\u7684\u8981\u975e\u5e38 \u5c0f\u5fc3\uff0c\u5f04\u5f97\u6842\u534e\u751f\u5341\u5206\u72fc\u72d9\u3002\u82e5\u4e0d\u662f\u4ed6\u95ea\u907f\u5f97\u5b9c\uff0c\u597d\u51e0\u6b21\u5c31\u8981\u9669\u4e9b\u53d7\u4f24\u3002 \u3000\u3000\u6b63\u5728\u5e94\u4ed8\u4e3a\u96be\uff0c\u5ffd\u542c\u5f97\u9a6c\u8e44\u4e4b\u58f0\u6709\u5982\u66b4\u98ce\u9aa4\u96e8\uff0c\u53c8\u662f\u4e00\u5f6a\u8f66\u9a6c\u51b2\u6765\uff0c\u6842\u534e\u751f\u6697\u53eb\u4e00\u58f0\uff1a \u201c\u82e6\u4e5f\uff01\u201d\u5c31\u5728\u8fd9\u65f6\uff0c\u53ea\u542c\u5f97\u90a3\u5f6a\u8f66\u9a6c\u540c\u58f0\u558a\u9053\uff1a\u201c\u606d\u8fce\u9a79\u9a6c\u5165\u5bab\uff01\u201d\u201c\u53db\u519b\u5feb\u5feb\u675f\u624b\u5c31 \u7f1a\uff01\u201d\u5927\u51fa\u6842\u534e\u751f\u610f\u5916\uff0c\u539f\u6765\u8fd9\u5f6a\u8f66\u9a6c\u4e43\u662f\u6551\u9a7e\u6765\u7684\u3002 \u3000\u3000\u8fd9\u4e00\u5f6a\u8f66\u9a6c\u6bd4\u90a3\u961f\u9ed1\u8863\u9a91\u5175\u7684\u4eba\u6570\u591a\u4e86\u51e0\u500d\uff0c\u767b\u65f6\u628a\u9ed1\u591c\u9a91\u5175\u622a\u6210\u4e86\u597d\u51e0\u5904\uff0c\u629b\u51fa\u4e86\u7eca \u9a6c\u7d22\uff0c\u4e0d\u6d88\u4e00\u4f1a\uff0c\u5c31\u628a\u90a3\u961f\u53db\u4e71\u7684\u9a91\u5175\u5c3d\u90fd\u6349\u4e86\u3002\u8fd9\u5f6a\u8f66\u9a6c\u9886\u961f\u7684\u519b\u5b98\u8fc7\u6765\u5411\u6842\u534e\u751f\u606d\u656c\u65bd \u793c\uff0c\u5374\u539f\u6765\u662f\u65b0\u4efb\u7684\u5fa1\u6797\u519b\u603b\u7ba1\u62c9\u6c57\u56fe\u3002 \u3000\u3000\u62c9\u6c57\u56fe\u4eb2\u9a7e\u9a6c\u8f66\uff0c\u5c06\u6842\u534e\u751f\u62a4\u9001\u5165\u5bab\uff0c\u6842\u534e\u751f\u5411\u4ed6\u81f4\u8c22\uff0c\u62c9\u6c57\u56fe\u9053\uff1a\u201c\u6211\u8499\u516c\u4e3b\u8d4f\u8bc6\uff0c \u5347\u4ee5\u91cd\u4efb\uff0c\u7c89\u8eab\u788e\u9aa8\uff0c\u4e0d\u8db3\u56fe\u62a5\u3002\u8fd9\u6b21\u6765\u8fdf\uff0c\u7d2f\u9a79\u9a6c\u53d7\u4e86\u865a\u60ca\uff0c\u9a79\u9a6c\u4e0d\u8d23\u7f5a\u6211\uff0c\u6211\u5df2\u611f\u6fc0\u4e0d \u5c3d\u3002\u201d\u6842\u534e\u751f\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u6211\u5fb7\u6ea5\u624d\u758f\uff0c\u5f97\u914d\u516c\u4e3b\uff0c\u81ea\u77e5\u975e\u4efd\uff0c\u602a\u4e0d\u5f97\u4ed6\u4eec\u53cd\u5bf9\u6211\uff0c\u6211\u4e5f\u65e0 \u989c\u518d\u5728\u8d35\u56fd\u4f4f\u4e0b\u53bb\u4e86\u3002\u201d\u62c9\u6c57\u56fe\u4f4e\u58f0\u8baf\uff1a\u201c\u9a79\u9a6c\u4f60\u6b66\u529f\u7edd\u4e16\uff0c\u624d\u5fb7\u517c\u4f18\uff0c\u4e0d\u4f46\u5404\u56fd\u6b66\u58eb\u5fc3 \u670d\uff0c\u6211\u4eec\u56fd\u4e2d\uff0c\u4e00\u767e\u4e2a\u4fbf\u6700\u5c11\u6709\u4e5d\u5341\u4e5d\u4e2a\u4e3a\u516c\u4e3b\u6b23\u5e86\u3002\u8fd9\u6b21\u53db\u4e71\u662f\u738b\u5b50\u641e\u51fa\u6765\u7684\uff0c\u53ea\u6709\u4ed6\u4e0d \u670d\u4f60\uff0c\u6015\u4f60\u593a\u4ed6\u738b\u4f4d\u3002\u4ed6\u672c\u6765\u8981\u6211\u53c2\u52a0\u53db\u53d8\u7684\uff0c\u6211\u5047\u4f5c\u7b54\u5e94\uff0c\u6697\u4e2d\u5df2\u62a5\u544a\u516c\u4e3b\u4e86\u3002\u201d\u6842\u534e\u751f \u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\u3002\u201d\u5fc3\u60c5\u987f\u7136\u5f00\u6717\u3002\u62c9\u6c57\u56fe\u9053\uff1a\u201c\u6211\u8981\u8fd8\u8bf7\u6559\u9a79\u9a6c\uff0c\u8fd9\u5e2e\u53db\u519b\uff0c\u4f60\u770b\u5982\u4f55\u5904 \u7f6e\uff1f\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u8981\u4e0d\u8981\u62a5\u544a\u7687\u4e0a\uff1f\u201d\u6842\u534e\u751f\u4e00\u60f3\uff0c\u82e5\u662f\u6b64\u4e3e\u63ed\u7a7f\uff0c\u53ea\u6015\u5c3c\u6cca\u5c14\u56fd\u4f1a\u6709\u5185\u4e71\uff0c \u4fbf\u9053\uff1a\u201c\u4e8b\u60c5\u5df2\u7ecf\u8fc7\u53bb\uff0c\u8fd8\u662f\u7b97\u4e86\u5427\u3002\u53db\u519b\u7531\u4f60\u5904\u7f6e\u597d\u4e86\uff0c\u4f46\u6211\u5e0c\u671b\u4f60\u4e0d\u8981\u60e9\u7f5a\u4ed6\u4eec\u3002\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u5ffd\u89c1\u5c3c\u6cca\u5c14\u738b\u5b50\u7387\u9886\u4e86\u51e0\u9a91\u9a6c\u75be\u9a70\u800c\u6765\uff0c\u4e00\u89c1\u6842\u534e\u751f\u5c31\u9053\uff1a\u201c\u542c\u8bf4\u8def\u4e0a\u51fa\u4e86 \u4e8b\u60c5\uff0c\u9a70\u9a6c\u53d7\u60ca\u4e86\u5427\uff1f\u201d\u6842\u534e\u751f\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\uff0c\u6709\u51e0\u4e2a\u519b\u58eb\u80e1\u95f9\uff0c\u65e9\u5df2\u7ed9\u603b\u7ba1\u5927\u4eba\u6349\u53bb\u4e86\uff0c \u591a\u8c22\u4f60\u7684\u5173\u5fc3\u3002\u201d\u5c3c\u6cca\u5c14\u738b\u5b50\u89c1\u6842\u534e\u751f\u9762\u65e0\u5f02\u8272\uff0c\u731c\u4e0d\u900f\u4ed6\u77e5\u662f\u4e0d\u77e5\uff0c\u8f6c\u8fc7\u5934\u5bf9\u62c9\u6c57\u56fe\u9053\uff1a \u201c\u4f60\u8fd9\u6b21\u7acb\u4e86\u5927\u529f\uff0c\u56fd\u738b\u5b9a\u7136\u53c8\u8981\u5347\u8d4f\u4f60\u4e86\u3002\u201d\u62c9\u6c57\u56fe\u9053\uff1a\u201c\u6211\u4e0d\u671b\u5347\u8d4f\uff0c\u53ea\u76fc\u5927\u5bb6\u80fd\u591f\u540c \u5fc3\u5408\u529b\uff0c\u56fd\u5bb6\u6c38\u4fdd\u592a\u5e73\uff0c\u90a3\u4fbf\u597d\u4e86\u3002\u201d\u738b\u5b50\u51b7\u51b7\u4e00\u7b11\uff0c\u5bf9\u6842\u534e\u751f\u9053\uff1a\u201c\u6211\u5e0c\u671b\u4f60\u53d7\u5230\u6211\u4eec\u56fd \u4eba\u7684\u6b22\u8fce\uff0c\u4f4f\u5f97\u79f0\u5fc3\u5982\u610f\u3002\u201d\u6842\u534e\u751f\u9053\uff1a\u201c\u8c22\u8c22\u4f60\u7684\u795d\u798f\uff0c\u6211\u7684\u613f\u671b\u548c\u603b\u7ba1\u5927\u4eba\u7684\u613f\u671b\u4e00 \u6837\u3002\u201d \u3000\u3000\u4e0d\u4e00\u4f1a\u6765\u5230\u4eac\u57ce\uff0c\u4f46\u89c1\u4eba\u5c71\u4eba\u6d77\uff0c\u9163\u821e\u9ad8\u6b4c\uff0c\u4e00\u89c1\u9a79\u9a6c\u7684\u8f66\u9a7e\u6765\u5230\uff0c\u767b\u65f6\u9f13\u638c\u5982\u96f7\uff0c\u81ea \u884c\u8ba9\u9053\u3002\u6842\u534e\u751f\u5fc3\u4e2d\u5927\u6170\uff0c\u60f3\u9053\uff1a\u201c\u62c9\u6c57\u56fe\u6240\u8a00\u975e\u5047\uff0c\u4ed6\u4eec\u679c\u7136\u662f\u6b22\u8fce\u6211\u7684\u3002\u201d \u3000\u3000\u9a6c\u8f66\u7f13\u7f13\u800c\u884c\uff0c\u5c06\u8fd1\u7687\u5bab\uff0c\u53ea\u89c1\u4e00\u5927\u961f\u6c11\u95f4\u6b4c\u624b\uff0c\u81ea\u52a8\u96c6\u5408\u8d77\u6765\uff0c\u62c9\u8d77\u7434\u5f26\uff0c\u5439\u8d77\u5587 \u53ed\uff0c\u6253\u8d77\u9523\u9f13\uff0c\u9ad8\u58f0\u5531\u9053\uff1a \u3000\u3000\u201c\u4eca\u665a\u7684\u665a\u98ce\u7279\u522b\u82b3\u99a8\uff0c \u3000\u3000\u5728\u7231\u60c5\u7684\u8282\u65e5\u91cc\u8c01\u4e0d\u6b22\u6b23\uff1f \u3000\u3000\u6b22\u8fce\u4f60\u554a\uff0c\u8de8\u8fc7\u73e0\u5cf0\u7684\u8d35\u5ba2\uff0c \u3000\u3000\u4ece\u4eca\u4e4b\u540e\uff0c\u4f60\u548c\u6211\u4eec\u662f\u4e00\u5bb6\u4eba\u3002 \u3000\u3000\u516c\u4e3b\u548c\u9a79\u9a6c\u7f14\u7ed3\u9e33\u76df\uff0c \u3000\u3000\u559c\u9a6c\u62c9\u96c5\u5c71\u7a7f\u8fc7\u4e86\u7ea2\u7ef3\uff0c \u3000\u3000\u52a0\u5fb7\u6ee1\u90fd\u2014\u2014\u5317\u4eac\uff01 \u3000\u3000\u4e2d\u56fd\u548c\u5c3c\u6cca\u5c14\u6c38\u8fdc\u76f8\u4eb2\uff01\u201d \u3000\u3000\u6842\u534e\u751f\u6d41\u4e0b\u4e86\u611f\u6fc0\u7684\u73e0\u6cea\uff0c\u5bf9\u5c3c\u6cca\u5c14\u738b\u5b50\u5fae\u7b11\u9053\uff1a\u201c\u4f60\u6240\u795d\u798f\u7684\u5168\u90fd\u5b9e\u73b0\u4e86\uff0c\u591a\u8c22\u4f60\u548c \u4f60\u7684\u767e\u59d3\uff0c\u4e2d\u56fd\u548c\u5c3c\u6cca\u5c14\u6c38\u8fdc\u76f8\u4eb2\uff1f\u201d\u5c3c\u6cca\u5c14\u738b\u5b50\u9762\u8272\u82cd\u767d\uff0c\u4e00\u53e5\u8bdd\u4e5f\u8bf4\u4e0d\u51fa\u6765\u3002\u4ed6\u672c\u610f\u662f \u8981\u62ff\u53db\u519b\u7684\u4e8b\u60f3\u6765\u6253\u51fb\u6842\u534e\u751f\uff0c\u4f7f\u5f97\u6842\u534e\u751f\u5fc3\u7070\u610f\u51b7\u7684\uff0c\u90a3\u77e5\u56fd\u4eba\u62e5\u6234\u516c\u4e3b\uff0c\u5bf9\u516c\u4e3b\u6240\u9009\u4e2d \u7684\u4eba\u4e5f\u8868\u793a\u4e86\u70ed\u70c8\u7684\u6b22\u8fce\u3002 \u3000\u3000\u8fdb\u4e86\u738b\u5bab\uff0c\u63a5\u53d7\u56fd\u738b\u7684\u795d\u798f\u4e4b\u540e\uff0c\u516c\u4e3b\u8d34\u8eab\u7684\u5bab\u5973\u5b9b\u5170\u661f\u63d0\u8d77\u4e00\u76cf\u7eb1\u706f\uff0c\u4fbf\u5f15\u6842\u534e\u751f\u8fdb \u5165\u6d1e\u623f\u3002 \u3000\u3000\u5bbf\u613f\u7ec8\u507f\uff0c\u9e33\u76df\u7f14\u7ed3\u3002\u6d1e\u623f\u7ea2\u70db\u9ad8\u70e7\uff0c\u5e7d\u9999\u6de1\u96c5\uff0c\u6842\u534e\u751f\u7591\u5e7b\u7591\u68a6\uff0c\u5bf9\u7740\u516c\u4e3b\uff0c\u75f4\u75f4\u6ce8 \u76ee\uff0c\u597d\u4e45\uff0c\u597d\u4e45\uff0c\u4e24\u4eba\u90fd\u8bf4\u4e0d\u51fa\u4e00\u53e5\u8bdd\u3002\u95e8\u5916\u5ffd\u542c\u5f97\u201c\u749e\u55e4\u201d\u7684\u7b11\u58f0\uff0c\u90a3\u662f\u5bab\u5973\u5b9b\u5170\u661f\u5728\u5077 \u7b11\u3002 \u3000\u3000\u6842\u534e\u751f\u9762\u4e0a\u4e00\u7ea2\uff0c\u62ac\u8d77\u5934\u6765\uff0c\u53ea\u89c1\u6d1e\u623f\u7684\u5e03\u7f6e\u5b8c\u5168\u4f9d\u7167\u4e2d\u56fd\u7684\u5f0f\u6837\uff0c\u5f53\u4e2d\u8d34\u7740\u4e00\u526f\u7ea2\u7eb8 \u5bf9\u8054\uff0c\u5199\u7684\u662f\uff1a \u3000\u3000\u201c\u534e\u4e25\u5999\u5883\u5055\u8c01\u6e38\uff1f\u770b\u9f99\u53f6\u62c8\u82b1\uff0c\u91ca\u8fe6\u5fae\u7b11\uff1b\u7389\u7b1b\u4ed9\u97f3\u9080\u5ba2\u548c\uff0c\u542c\u76f8\u5982\u9f13\u745f\uff0c\u5343\u664b\u5439 \u8427\u3002\u201d \u3000\u3000\u8fd9\u4ed8\u5bf9\u8054\uff0c\u6b63\u662f\u8003\u8bd5\u6587\u5b66\u4e4b\u65f6\uff0c\u6842\u534e\u751f\u4e3a\u516c\u4e3b\u505a\u7684\u5d4c\u540d\u8054\uff0c\u5728\u804c\u9996\u5d4c\u4e0a\u516c\u4e3b\u7684\u82b3\u540d\u201c\u534e \u7389\u201d\u4e8c\u5b57\uff0c\u56e0\u4e3a\u5c3c\u6cca\u5c14\u662f\u4f5b\u6559\u56fd\u5bb6\uff0c\u6240\u4ee5\u8054\u4e0a\u5168\u7528\u4f5b\u5178\uff0c\u8868\u793a\u7231\u6155\u4e4b\u60c5\u3002\u6842\u534e\u751f\u6700\u521d\u5728\u51b0\u5cf0 \u4e0a\u89c1\u516c\u4e3b\u65f6\uff0c\u662f\u5148\u95fb\u7389\u7b1b\uff0c\u540e\u89c1\u4ed9\u59ff\u7684\uff0c\u6240\u4ee5\u4e0b\u8054\u5c31\u4ee5\u5f53\u65f6\u60c5\u666f\uff0c\u5e76\u7528\u4e24\u4e2a\u4e2d\u56fd\u7684\u5178\u6545\u6765\u8868 \u793a\u6c42\u5076\u4e4b\u8bed\u3002\u516c\u4e3b\u4eca\u665a\u5c31\u5c06\u8fd9\u4ed8\u5bf9\u8054\u5728\u6d1e\u623f\u4e4b\u4e2d\uff0c\u5f53\u771f\u662f\u5999\u5230\u4e0d\u80fd\u518d\u5999\u3002 \u3000\u3000\u4e24\u8fb9\u8054\u8bed\u7684\u4e2d\u95f4\uff0c\u5d4c\u6709\u4e00\u5757\u78a7\u7389\u5c4f\u98ce\uff0c\u5c4f\u98ce\u4e0a\u79bb\u51fa\u4e86\u4e00\u9996\u5c0f\u8bcd\uff0c\u8c03\u5bc4\u300a\u70b9\u7eac\u5507\u300b\uff0c\u8bcd \u9053\uff1a \u3000\u3000\u201c\u7389\u5251\u51b0\u5f39\uff0c\u7aef\u7684\u662f\u5947\u7f18\u5947\u9047\u3002\u96ea\u83b2\u9e33\u8c31\uff0c\u51b7\u9999\u98de\u4eba\u8bd7\u53e5\u3002\u7eb5\u6709\u73e0\u5cf0\uff0c\u96be\u9694\u5218\u90ce\u8def\u3002\u4e91 \u6df1\u5904\uff0c\u613f\u540c\u5055\u9690\uff0c\u7686\u5c4b\u51b0\u5ddd\u4f4f\u3002\u201d \u3000\u3000\u6842\u534e\u751f\u8f7b\u8f7b\u5ff5\u4e86\u4e00\u904d\uff0c\u5982\u9189\u5982\u75f4\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u539f\u6765\u4f60\u7684\u5fc3\u610f\u5b8c\u5168\u548c\u6211\u4e00\u6837\u3002\u201d\u4e24\u4eba\u5e76 \u80a9\u7acb\u5728\u7a97\u524d\uff0c\u9065\u671b\u559c\u9a6c\u62c9\u96c5\u5c71\u7684\u819b\u77aa\u96ea\u5cf0\uff0c\u4e24\u9897\u5fc3\u6eb6\u6210\u4e86\u4e00\u9897\u3002 \u3000\u3000\u5176\u540e\u4e94\u5e74\uff0c\u56fd\u738b\u5e74\u8001\u4f53\u8870\uff0c\u738b\u5b50\u8c0b\u4ed6\u65e5\u6025\uff0c\u56fd\u4e2d\u4e3a\u4e86\u738b\u4f4d\u7684\u627f\u7ee7\u95ee\u9898\uff0c\u6f5c\u4f0f\u7740\u5185\u4e71\u7684\u5371 \u673a\u3002\u4e00\u5929\u665a\u4e0a\uff0c\u534e\u7389\u516c\u4e3b\u7ec8\u4e8e\u548c\u6842\u534e\u751f\u6084\u6084\u51fa\u8d70\uff0c\u5728\u5ff5\u9752\u5510\u53e4\u62c9\u5c71\u7684\u5929\u6e56\u4e4b\u4e0a\u8fbe\u8d77\u51b0\u5bab\u3002 \u3000\u3000\u8bfb\u8005\u6b32\u77e5\u8be6\u60c5\u8bf7\u7eed\u770b\u62d9\u8457\u300a\u51b0\u5ddd\u5929\u5973\u4f20\u300b\u3002 \u3000\u3000\u8fd9\u91cc\u4e0d\u518d\u5199\u4e86\u3002 \u3000\u3000(\u5168\u4e66\u5b8c)'}),showOnHomepage:!0}]}}]);