"use strict";(self.webpackChunkwebsite=self.webpackChunkwebsite||[]).push([[29051],{25599:(e,n,t)=>{t.r(n),t.d(n,{default:()=>a});var s=t(74848);const a=[{name:"\u7b2c\u4e00\u7ae0\u3000\u9752\u57ce\u6b7b\u58eb",content:(0,s.jsx)(s.Fragment,{children:"\u6668\u3002 \u3000\u3000\u4e45\u96ea\u521d\u6674\uff0c\u9177\u5bd2\u5374\u4f7f\u5f97\u957f\u8857\u4e0a\u7684\u79ef\u96ea\u90fd\u7ed3\u6210\u51b0\uff0c\u5c4b\u6a90\u4e0b\u7684\u51b0\u67f1\u5982\u72fc\u7259\u4ea4\u9519\uff0c\u4eff\u4f5b\u6b63\u7b49\u5f85 \u7740\u62e9\u4eba\u800c\u566c\u3002 \u3000\u3000\u53ef\u662f\u8857\u4e0a\u5374\u6ca1\u6709\u4eba\uff0c\u5bb6\u5bb6\u6237\u6237\u7684\u95e8\u7a97\u90fd\u7d27\u7d27\u5730\u5173\u7740\uff0c\u5bc6\u4e91\u4f4e\u538b\uff0c\u5929\u5730\u95f4\u7adf\u4f3c\u5145\u6ee1\u4e86\u4e00\u79cd \u8db3\u4ee5\u51bb\u7ed3\u4e00\u5207\u751f\u547d\u7684\u6740\u6c14\u3002 \u3000\u3000\u6ca1\u6709\u98ce\uff0c\u8fde\u98ce\u90fd\u4f3c\u88ab\u51bb\u6b7b\u3002 \u3000\u3000\u7ae5\u94dc\u5c71\u62e5\u7740\u8c82\u88d8\uff0c\u5750\u5728\u957f\u8857\u8fd1\u5934\u5904\u7684\u4e00\u5f20\u864e\u76ae\u4ea4\u6905\u4e0a\uff0c\u9762\u5bf9\u7740\u8fd9\u6761\u6b7b\u5bc2\u7684\u957f\u8857\uff0c\u5fc3\u91cc\u89c9 \u5f97\u5f88\u6ee1\u610f\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u7684\u547d\u4ee4\u65e9\u5df2\u88ab\u5f7b\u5e95\u6267\u884c\u3002 \u3000\u3000\u4ed6\u5df2\u5c06\u8fd9\u6761\u957f\u8857\u8f9f\u4e3a\u6218\u573a\uff0c\u4e0d\u51fa\u534a\u4e2a\u65f6\u8fb0\uff0c\u4ed6\u5c31\u8981\u4ee5\u897f\u57ce\u8001\u675c\u706b\u70eb\u7684\u8840\uff0c\u6765\u6d17\u6e05\u8fd9\u6761\u8857 \u4e0a\u51b0\u51b7\u7684\u79ef\u96ea\u3002 \u3000\u3000\u5728\u90a3\u4e00\u523b\u5230\u6765\u4e4b\u524d\uff0c\u82e5\u6709\u4e00\u4e2a\u4eba\u6562\u8d70\u4e0a\u8fd9\u6761\u957f\u8857\uff0c\u4ed6\u5c31\u8981\u780d\u65ad\u8fd9\u53ea\u811a\u3002 \u3000\u3000\u8fd9\u662f\u4ed6\u7684\u57ce\u5e02\uff0c\u65e0\u8bba\u8c01\u90fd\u4f11\u60f3\u5728\u4ed6\u7684\u5730\u76d8\u4e0a\u63d2\u4e00\u811a\u3002 \u3000\u3000\u897f\u57ce\u8001\u675c\u4e5f\u4f11\u60f3\u3002 \u3000\u3000\u9664\u4e86\u536b\u516b\u592a\u7237\u5916\uff0c\u4ed6\u7edd\u4e0d\u5141\u8bb8\u4efb\u4f55\u4eba\u5728\u4ed6\u9762\u524d\uff0c\u6321\u4f4f\u4ed6\u7684\u8def\u3002 \u3000\u3000\u6570\u5341\u6761\u9752\u8863\u52b2\u88c5\u7684\u5927\u6c49\uff0c\u675f\u624b\u8083\u7acb\u5728\u4ed6\u8eab\u540e\u3002 \u3000\u3000\u4ed6\u8eab\u65c1\u5374\u8fd8\u6446\u7740\u4e24\u5f20\u540c\u6837\u7684\u864e\u76ae\u4ea4\u6905\uff0c\u4e00\u4e2a\u8138\u8272\u60e8\u767d\u3001\u6ee1\u9762\u50b2\u6c14\u7684\u5e74\u8f7b\u4eba\uff0c\u8eab\u4e0a\u62ab\u7740\u4ef7 \u503c\u5343\u91d1\u7684\u7d2b\u8c82\uff0c\u61d2\u6d0b\u6d0b\u5730\u9760\u5728\u5de6\u9762\u4e00\u5f20\u6905\u5b50\u4e0a\uff0c\u7528\u5c0f\u6307\u52fe\u7740\u67c4\u9576\u7740\u5b9d\u77f3\u7684\u4e4c\u9798\u957f\u5251\uff0c\u4e0d\u505c\u5730 \u7529\u6765\u7529\u53bb\u3002 \u3000\u3000\u5bf9\u4ed6\u8bf4\u6765\uff0c\u8fd9\u4ef6\u4e8b\u6839\u672c\u5c31\u5f88\u65e0\u804a\uff0c\u5f88\u65e0\u8da3\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u8981\u6740\u7684\u5e76\u4e0d\u662f\u897f\u57ce\u8001\u675c\u8fd9\u79cd\u4eba\uff0c\u8fd9\u79cd\u4eba\u8fd8\u4e0d\u914d\u4ed6\u51fa\u624b\u3002 \u3000\u3000\u53f3\u9762\u7684\u4e00\u4e2a\u4eba\u5e74\u7eaa\u66f4\u8f7b\uff0c\u6b63\u5728\u7528\u4e00\u67c4\u96ea\u4eae\u7684\u96c1\u7fce\u5200\uff0c\u4fee\u81ea\u5df1\u7684\u6307\u7532\u3002 \u3000\u3000\u4ed6\u663e\u7136\u5c3d\u91cf\u60f3\u4f5c\u51fa\u4ece\u5bb9\u9547\u5b9a\u7684\u6837\u5b50\u6765\uff0c\u4f46\u4e00\u5f20\u957f\u6ee1\u4e86\u9752\u6625\u75d8\u7684\u8138\uff0c\u5374\u5df2\u56e0\u5174\u594b\u800c\u53d1\u7ea2\u3002 \u3000\u3000\u7ae5\u94dc\u5c71\u5f88\u4e86\u89e3\u8fd9\u5e74\u8f7b\u4eba\u7684\u5fc3\u6000\u3002 \u3000\u3000\u4ed6\u81ea\u5df1\u7b2c\u4e00\u6b21\u88ab\u536b\u516b\u592a\u7237\u6d3e\u51fa\u6765\u6267\u884c\u4efb\u52a1\u65f6\uff0c\u4e5f\u540c\u6837\u7d27\u5f20\u7684\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u4e5f\u77e5\u9053\uff0c\u8fd9\u5e74\u8f7b\u4eba\u65e2\u7136\u80fd\u5728\u536b\u516b\u592a\u7237\u95e8\u4e0b\u7684\u5341\u4e09\u592a\u4fdd\u4e2d\u540d\u5217\u5341\u4e8c\uff0c\u624b\u4e0a\u7684\u4e00\u67c4\u96c1 \u7fce\u5200\uff0c\u5c31\u5fc5\u5b9a\u4e0d\u4f1a\u4ee4\u4eba\u5931\u671b\u3002 \u3000\u3000\u7d27\u95ed\u7740\u7684\u5c4b\u5b50\u91cc\uff0c\u5ffd\u7136\u4f20\u51fa\u4e00\u9635\u5b69\u5b50\u7684\u54ed\u58f0\uff0c\u5212\u7834\u4e86\u5929\u5730\u95f4\u7684\u5bc2\u9759\u3002 \u3000\u3000\u54ed\u58f0\u521a\u54cd\u8d77\uff0c\u5c31\u505c\u6b62\uff0c\u5b69\u5b50\u7684\u5634\u5df4\u663e\u7136\u5df2\u88ab\u5927\u4eba\u4eec\u5835\u4f4f\u3002 \u3000\u3000\u4e00\u6761\u76ae\u6bdb\u5df2\u8131\u843d\u7684\u8001\u72d7\uff0c\u5939\u7740\u5c3e\u5df4\uff0c\u4ece\u5899\u89d2\u7684\u72d7\u6d1e\u91cc\u94bb\u51fa\u6765\uff0c\u7a9c\u8fc7\u957f\u8857\u3002 \u3000\u3000\u90a3\u8138\u4e0a\u957f\u7740\u9752\u6625\u75d8\u7684\u5c11\u5e74\uff0c\u770b\u7740\u8fd9\u6761\u72d7\u7a9c\u5230\u8857\u5fc3\uff0c\u773c\u775b\u91cc\u4eff\u4f5b\u5e26\u7740\u79cd\u5f88\u5947\u602a\u7684\u8868\u60c5\uff0c\u5de6 \u624b\u6162\u6162\u7684\u4f38\u5165\u8863\u895f\u91cc\uff0c\u7a81\u53c8\u5f88\u5feb\u5730\u6325\u51fa\u3002 \u3000\u3000\u5200\u5149\u4e00\u95ea\uff0c\u72d7\u5df2\u88ab\u9489\u6b7b\u5728\u8857\u5fc3\uff0c\u5200\u6070\u5de7\u8d2f\u7a7f\u4e86\u5b83\u7684\u54bd\u5589\uff0c\u5b83\u7684\u8840\u6d41\u8fc7\u96ea\u5730\u65f6\uff0c\u4e5f\u540c\u6837\u662f \u9c9c\u7ea2\u7684\u3002 \u3000\u3000\u7ae5\u94dc\u5c71\u7cbe\u795e\u4e00\u632f\uff0c\u8131\u53e3\u800c\u8d5e\u9053\uff1a\u201c\u597d\uff0c\u5341\u4e8c\u5f1f\u597d\u5feb\u7684\u51fa\u624b\u3002\u201d\u8fd9\u5c11\u5e74\u663e\u7136\u4e5f\u5bf9\u81ea\u5df1\u7684\u51fa \u624b\u5f88\u6ee1\u610f\uff0c\u50b2\u7136\u9053\uff1a\u201c\u7ae5\u8001\u4e09\u65e2\u7136\u5df2\u4f20\u4ee4\u4e0b\u53bb\uff0c\u65e0\u8bba\u662f\u4eba\u662f\u72d7\uff0c\u53ea\u8981\u6562\u95ef\u5230\u8fd9\u91cc\u6765\uff0c\u6211\u6bb5\u5341 \u4e8c\u90fd\u8981\u4ed6\u7684\u547d\u3002\u201d \u3000\u3000\u7ae5\u94dc\u5c71\u4ef0\u9762\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6709\u8f9b\u56db\u5f1f\u548c\u5341\u4e8c\u90ce\u8fd9\u6837\u7684\u5c11\u5e74\u8c6a\u6770\u5728\u8fd9\u91cc\uff0c\u83ab\u8bf4\u53ea\u6709\u4e00\u4e2a\u897f \u57ce\u8001\u675c\uff0c\u5c31\u7b97\u662f\u5341\u4e2a\uff0c\u53c8\u4f55\u8db3\u60e7\uff1f\u201d \u3000\u3000\u8f9b\u56db\u5374\u51b7\u51b7\u9053\uff1a\u201c\u53ea\u6015\u4eca\u65e5\u662f\u8f6e\u4e0d\u5230\u6211\u6765\u51fa\u624b\u3002\u201d \u3000\u3000\u4ed6\u5c0f\u6307\u4e0a\u52fe\u7740\u7684\u957f\u5251\u7a81\u7136\u505c\u6b62\u6643\u52a8\uff0c\u7ae5\u94dc\u5c71\u7684\u7b11\u58f0\u4e5f\u7a81\u7136\u505c\u987f\u3002 \u3000\u3000\u53e4\u8001\u800c\u503e\u659c\u7684\u957f\u8857\u53e6\u4e00\u5934\uff0c\u5df2\u6709\u4e00\u884c\u4eba\u5f88\u5feb\u5730\u8d70\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u4e00\u884c\u4e8c\u5341\u516d\u516b\u4e2a\u4eba\uff0c\u5168\u90fd\u662f\u9ed1\u77ed\u8884\u3001\u624e\u811a\u88e4\uff0c\u811a\u4e0a\u8584\u5e95\u5feb\u9774\uff0c\u8e0f\u5728\u51b0\u96ea\u4e0a\uff0c\u201c\u6c99\u6c99\u201d\u5730 \u53d1\u54cd\u3002 \u3000\u3000\u4e3a\u9996\u7684\u4e00\u4e2a\u4eba\uff0c\u6d53\u7709\u5927\u773c\uff0c\u6ee1\u9762\u7cbe\u608d\u4e4b\u8272\uff0c\u6b63\u662f\u897f\u57ce\u7b2c\u4e00\u6761\u597d\u6c49\uff0c\u201c\u5927\u773c\u201d\u8001\u675c\u3002 \u3000\u3000\u770b\u5230\u4e86\u8fd9\u4e2a\u4eba\uff0c\u7ae5\u94dc\u5c71\u7684\u8138\u7acb\u523b\u7ef7\u7d27\uff0c\u8fde\u77b3\u5b54\u90fd\u4f3c\u5df2\u6536\u7f29\u3002 \u3000\u3000\u4e00\u4e2a\u52b2\u88c5\u4f69\u5251\u5c11\u5e74\u4ece\u540e\u9762\u7a9c\u51fa\u6765\uff0c\u4e00\u6b65\u7a9c\u5230\u4ed6\u8eab\u540e\uff0c\u6276\u5251\u800c\u7acb\u3002 \u3000\u3000\u53ea\u542c\u5200\u5f26\u4e4b\u58f0\u6025\u54cd\uff0c\u540e\u9762\u7684\u6570\u5341\u6761\u9752\u8863\u5927\u6c49\uff0c\u4e00\u4e2a\u4e2a\u90fd\u5df2\u5f13\u4e0a\u5f26\uff0c\u5200\u51fa\u9798\uff0c\u4e25\u9635\u800c\u5f85\u3002 \u3000\u3000\u6740\u6c14\u66f4\u6d53\uff0c\u9664\u4e86\u90a3\u4e00\u9635\u9635\u5982\u5200\u950b\u78e8\u64e6\u7684\u811a\u6b65\u58f0\u4e4b\u5916\uff0c\u5929\u5730\u95f4\uff0c\u518d\u4e5f\u542c\u4e0d\u89c1\u522b\u7684\u58f0\u97f3\u3002 \u3000\u3000\u773c\u89c1\u5bf9\u9762\u8fd9\u4e00\u884c\u4eba\u5df2\u8d8a\u8d70\u8d8a\u8fd1\uff0c\u8c01\u77e5\u5c31\u5728\u8fd9\u65f6\uff0c\u8857\u9053\u65c1\u4e00\u6247\u7a84\u95e8\u7a81\u7136\u88ab\u63a8\u5f00\uff0c\u5341\u4e09\u56db\u4e2a \u767d\u8863\u4eba\u9c7c\u8d2f\u8d70\u4e86\u51fa\u6765\uff0c\u8fce\u4e0a\u4e86\u897f\u57ce\u8001\u675c\uff0c\u5176\u4e2d\u4e00\u4e2a\u4eba\u4f4e\u4f4e\u8bf4\u4e86\u4e24\u53e5\u8bdd\uff0c\u897f\u57ce\u8001\u675c\u7adf\u4e00\u8a00\u4e0d \u53d1\uff0c\u539f\u5730\u7ad9\u4f4f\u3002 \u3000\u3000\u8fd9\u4e00\u884c\u767d\u8863\u4eba\u90fd\u5411\u7ae5\u94dc\u5c71\u8d70\u4e86\u8fc7\u6765\uff0c\u7ae5\u94dc\u5c71\u8fd9\u624d\u770b\u51fa\u4ed6\u4eec\u8eab\u4e0a\u7adf\u53ea\u7a7f\u7740\u4ef6\u767d\u9ebb\u5355\u8863\uff0c\u80cc \u540e\u80cc\u7740\u5377\u8349\u5e2d\uff0c\u624b\u4e0a\u63d0\u7740\u6839\u77ed\u6756\uff0c\u8d64\u8db3\u7a7f\u7740\u8349\u978b\u3002 \u3000\u3000\u5728\u8fd9\u79cd\u9177\u5bd2\u7684\u5929\u6c14\u91cc\uff0c\u8fd9\u4e9b\u4eba\u770b\u6765\u4e1d\u6beb\u6ca1\u6709\u5bd2\u51b7\u754f\u7f29\u4e4b\u8272\uff0c\u53ea\u4e0d\u8fc7\u624b\u811a\u90fd\u5df2\u51bb\u5f97\u53d1\u9752\uff0c \u8138\u4e5f\u662f\u94c1\u9752\u7684\uff0c\u9752\u4e2d\u900f\u767d\u7684\u8138\u4e0a\uff0c\u5b8c\u5168\u6ca1\u6709\u8868\u60c5\uff0c\u7adf\u50cf\u6b7b\u4eba\u7684\u8138\u4e00\u6837\uff0c\u663e\u5f97\u8bf4\u4e0d\u51fa\u7684\u8be1\u79d8\u53ef \u6015\u3002 \u3000\u3000\u8d70\u8fc7\u90a3\u6b7b\u72d7\u65c1\u8fb9\u65f6\uff0c\u5176\u4e2d\u4e00\u4eba\u7a81\u7136\u4fef\u4e0b\u8eab\uff0c\u89e3\u4e0b\u80cc\u540e\u7684\u8349\u5e2d\uff0c\u5377\u8d77\u4e86\u8fd9\u6761\u6b7b\u72d7\uff0c\u7528\u672c\u6765 \u7cfb\u8349\u5e2d\u7684\u957f\u7ef3\u6346\u8d77\uff0c\u6302\u5728\u6728\u4ed7\u4e0a\uff0c\u518d\u5927\u6b65\u8ffd\u4e0a\u4ed6\u7684\u540c\u4f34\u3002 \u3000\u3000\u6bb5\u5341\u4e8c\u7684\u8138\u8272\u5df2\u53d8\u4e86\uff0c\u5de6\u624b\u53c8\u6162\u6162\u5730\u4f38\u5165\u6000\u91cc\uff0c\u4f3c\u4e4e\u53c8\u8981\u53d1\u5200\u3002 \u3000\u3000\u7ae5\u94dc\u5c71\u5374\u7528\u773c\u8272\u6b62\u4f4f\u4e86\u4ed6\uff0c\u538b\u4f4e\u58f0\u97f3\u9053\uff1a\u201c\u8fd9\u4e9b\u4eba\u770b\u6765\u90fd\u900f\u7740\u70b9\u53e4\u602a\uff0c\u6211\u4eec\u4e0d\u5982\u5148\u6478\u6e05 \u4ed6\u4eec\u7684\u6765\u610f\u518d\u8bf4\u3002\u201d \u3000\u3000\u6bb5\u5341\u4e8c\u51b7\u7b11\u9053\uff1a\u201c\u5c31\u7b97\u4ed6\u4eec\u73b0\u5728\u770b\u6765\u6709\u70b9\u53e4\u602a\uff0c\u53d8\u6210\u6b7b\u4eba\u540e\u4e5f\u4e0d\u4f1a\u6709\u4ec0\u4e48\u53e4\u602a\u4e86\u3002\u201d \u3000\u3000\u4ed6\u5634\u91cc\u867d\u8fd9\u4e48\u6837\u8bf4\uff0c\u6bd5\u7adf\u8fd8\u662f\u6ca1\u6709\u51fa\u624b\u3002 \u3000\u3000\u7ae5\u94dc\u5c71\u5374\u53c8\u6c89\u58f0\u5524\u9053\uff1a\u201c\u7ae5\u626c\uff01\u201d \u3000\u3000\u8eab\u540e\u90a3\u52b2\u88c5\u4f69\u5251\u7684\u5c11\u5e74\uff0c\u7acb\u523b\u5e94\u58f0\u9053\uff1a\u201c\u5728\u3002\u201d \u3000\u3000\u7ae5\u94dc\u5c71\u9053\uff1a\u201c\u7b49\u4e00\u4f1a\u4f60\u5148\u53bb\u4f30\u91cf\u4ed6\u4eec\u7684\u6b66\u529f\uff0c\u4e00\u4e0d\u5bf9\u5c31\u8d76\u7d27\u56de\u6765\uff0c\u5343\u4e07\u83ab\u6b7b\u7f20\u6ee5\u6597\u3002\u201d \u3000\u3000\u7ae5\u626c\u7684\u773c\u775b\u91cc\u5df2\u53d1\u51fa\u4e86\u5149\uff0c\u6276\u5251\u9053\uff1a\u201c\u5f1f\u5b50\u660e\u767d\uff01\u201d \u3000\u3000\u53ea\u89c1\u521a\u624d\u8bf4\u8bdd\u7684\u90a3\u767d\u8863\u4eba\u4e00\u6446\u624b\uff0c\u4e00\u884c\u4eba\u7ade\u5168\u90fd\u5728\u4e00\u4e08\u5916\u7ad9\u4f4f\u3002 \u3000\u3000\u8fd9\u4eba\u9752\u6e17\u6e17\u7684\u4e00\u5f20\u9a6c\u8138\uff0c\u53cc\u773c\u72ed\u957f\uff0c\u98a7\u9aa8\u9ad8\u8038\uff0c\u4e00\u5f20\u5927\u5634\u4e0d\u5408\u7684\u65f6\u5019\u90fd\u5df2\u5c06\u54a7\u5230\u8033\u4e0b\uff0c \u88c5\u675f\u6253\u626e\u867d\u7136\u4e5f\u8ddf\u522b\u7684\u4eba\u6ca1\u4ec0\u4e48\u4e24\u6837\uff0c\u4f46\u65e0\u8bba\u8c01\u4e00\u773c\u5c31\u53ef\u770b\u51fa\uff0c\u4ed6\u5fc5\u5b9a\u662f\u8fd9\u4e9b\u4eba\u4e4b\u4e2d\u7684\u9996 \u9886\u3002 \u3000\u3000\u7ae5\u94dc\u5c71\u5f53\u7136\u4e5f\u5df2\u770b\u51fa\uff0c\u4e00\u53cc\u53d1\u4eae\u7684\u773c\u775b\u6b63\u76ef\u5728\u8fd9\u4eba\u8eab\u4e0a\uff0c\u7a81\u7136\u95ee\u9053\uff1a\u201c\u5c0a\u59d3\u5927\u540d\uff1f\u201d \u3000\u3000\u8fd9\u4eba\u9053\uff1a\u201c\u58a8\u767d\u3002\u201d \u3000\u3000\u7ae5\u94dc\u5c71\u9053\uff1a\u201c\u54ea\u91cc\u6765\u7684\uff1f\u201d \u3000\u3000\u58a8\u767d\u9053\uff1a\u201c\u9752\u57ce\u3002\u201d \u3000\u3000\u7ae5\u94dc\u5c71\u9053\uff1a\u201c\u6765\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u58a8\u767d\u51b7\u51b7\u9053\uff1a\u201c\u4f46\u671b\u80fd\u591f\u5316\u5e72\u6208\u4e3a\u738b\u5e1b\u3002\u201d \u3000\u3000\u7ae5\u94dc\u5c71\u7a81\u7136\u7eb5\u58f0\u957f\u7b11\uff0c\u9053\uff1a\u201c\u539f\u6765\u670b\u53cb\u662f\u60f3\u6765\u529d\u67b6\u7684\u3002\u201d \u3000\u3000\u58a8\u767d\u9053\uff1a\u201c\u6b63\u662f\u3002\u201d \u3000\u3000\u7ae5\u94dc\u5c71\u9053\uff1a\u201c\u8fd9\u573a\u67b6\u5c31\u51ed\u4f60\u4e5f\u80fd\u529d\u5f97\u4e86\u4e48\uff1f\u201d \u3000\u3000\u58a8\u767d\u8138\u4e0a\u8fd8\u662f\u5168\u65e0\u8868\u60c5\uff0c\u8fde\u8bdd\u90fd\u4e0d\u8bf4\u4e86\u3002 \u3000\u3000\u7ae5\u626c\u65e9\u5df2\u8dc3\u8dc3\u6b32\u8bd5\uff0c\u6b64\u523b\u4e00\u4e2a\u7bad\u6b65\u7a9c\u51fa\u53bb\uff0c\u5389\u58f0\u9053\uff1a\u201c\u8981\u529d\u67b6\u4e5f\u5bb9\u6613\uff0c\u53ea\u4e0d\u8fc7\u5148\u5f97\u95ee\u95ee \u6211\u638c\u4e2d\u8fd9\u67c4\u5251\u7b54\u4e0d\u7b54\u5e94\u3002\u201d \u3000\u3000\u4ed6\u4e00\u53cd\u624b\uff0c\u201c\u545b\u201d\u7684\u4e00\u58f0\uff0c\u5251\u5df2\u51fa\u9798\u3002 \u3000\u3000\u58a8\u767d\u8fde\u770b\u90fd\u6ca1\u6709\u770b\u4ed6\u4e00\u773c\uff0c\u540e\u9762\u5374\u6709\u4e2a\u6700\u7626\u6700\u5c0f\u7684\u767d\u8863\u4eba\u7a9c\u4e86\u51fa\u6765\uff0c\u7adf\u662f\u4e2a\u5341\u56db\u4e94\u5c81\u7684 \u5b69\u5b50\u3002 \u3000\u3000\u7ae5\u626c\u76b1\u7709\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u9b3c\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u767d\u8863\u7ae5\u5b50\u7684\u8138\u4e0a\u5c45\u7136\u4e5f\u662f\u51b7\u51b0\u51b0\u7684\u5168\u65e0\u8868\u60c5\uff0c\u6de1\u6de1\u9053\uff1a\u201c\u6765\u95ee\u95ee\u4f60\u7684\u8fd9\u67c4\u5251\u7b54\u4e0d\u7b54 \u5e94\u3002\u201d \u3000\u3000\u7ae5\u626c\u6012\u9053\uff1a\u201c\u5c31\u51ed\u4f60\uff1f\u201d \u3000\u3000\u81ea\u8863\u7ae5\u5b50\u9053\uff1a\u201c\u4f60\u662f\u7528\u5251\u7684\uff0c\u6211\u6070\u5de7\u4e5f\u662f\u7528\u5251\u7684\u3002\u201d \u3000\u3000\u5ba3\u626c\u7a81\u7136\u4e5f\u7eb5\u58f0\u72c2\u7b11\uff0c\u9053\uff1a\u201c\u597d\uff0c\u6211\u5c31\u5148\u6253\u53d1\u4e86\u4f60\u518d\u8bf4\u3002\u201d\u65e0\u58f0\u4e2d\uff0c\u4ed6\u638c\u4e2d\u7684\u5251\u5df2\u6bd2\u86c7 \u822c\u523a\u51fa\uff0c\u76f4\u523a\u8fd9\u767d\u8863\u7ae5\u5b50\u7684\u5fc3\u53e3\u3002 \u3000\u3000\u767d\u8863\u7ae5\u5b50\u53cc\u624b\u4e00\u5206\uff0c\u7adf\u4e5f\u4ece\u77ed\u68cd\u4e2d\u62bd\u51fa\u4e86\u67c4\u7a84\u5251\u3002 \u3000\u3000\u7ae5\u626c\u4e00\u7740\u201c\u6bd2\u86c7\u5410\u4fe1\u201d\u523a\u8fc7\u53bb\uff0c\u4ed6\u5c45\u7136\u4e0d\u907f\u4e0d\u95ea\uff0c\u8fde\u773c\u775b\u90fd\u6ca1\u6709\u970e\u4e00\u970e\u3002 \u3000\u3000\u53ea\u542c\u201c\u54e7\u201d\u7684\u4e00\u58f0\uff0c\u7ae5\u626c\u624b\u91cc\u7684\u5251\uff0c\u5df2\u523a\u5165\u4e86\u4ed6\u7684\u5fc3\u53e3\u3002 \u3000\u3000\u9c9c\u8840\u7ea2\u82b1\u822c\u98de\u6e85\u800c\u51fa\u65f6\uff0c\u4ed6\u624b\u91cc\u7684\u5251\uff0c\u7adf\u4e5f\u523a\u51fa\u4e00\u7740\u201c\u6bd2\u86c7\u5410\u4fe1\u201d\uff0c\u523a\u5165\u4e86\u7ae5\u626c\u7684\u5fc3 \u53e3\u3002 \u3000\u3000\u7a81\u7136\u95f4\uff0c\u6240\u6709\u7684\u52a8\u4f5c\u5168\u90fd\u505c\u987f\uff0c\u8fde\u547c\u5438\u90fd\u4f3c\u4e4e\u5df2\u5b8c\u5168\u505c\u987f\u3002 \u3000\u3000\u5239\u90a3\u95f4\uff0c\u8fd9\u4e00\u6218\u5df2\u7ed3\u675f\uff01 \u3000\u3000\u6bcf\u4e2a\u4eba\u7684\u8138\u8272\u90fd\u53d8\u4e86\uff0c\u51e0\u4e4e\u4e0d\u80fd\u76f8\u4fe1\u4e16\u4e0a\u771f\u6709\u8fd9\u4e48\u6837\u7684\u4eba\uff0c\u771f\u6709\u8fd9\u4e48\u6837\u7684\u4e8b\u3002 \u3000\u3000\u9c9c\u8840\u96e8\u4e00\u822c\u843d\u4e0b\uff0c\u96fe\u4e00\u822c\u6d88\u6563\u3002 \u3000\u3000\u96ea\u5730\u4e0a\u5df2\u591a\u4e86\u70b9\u70b9\u8840\u82b1\uff0c\u9c9c\u8273\u5982\u7ea2\u6885\u3002 \u3000\u3000\u767d\u8863\u7ae5\u5b50\u7684\u8138\u4e0a\uff0c\u8fd8\u662f\u5b8c\u5168\u6ca1\u6709\u8868\u60c5\uff0c\u53ea\u4e0d\u8fc7\u4e00\u53cc\u773c\u775b\u9634\u607b\u607b\u6b7b\u9c7c\u822c\u51f8\u51fa\uff0c\u4ed6\u8fd8\u662f\u770b\u7740 \u7ae5\u626c\uff0c\u773c\u775b\u91cc\u7ade\u4f3c\u8fd8\u5e26\u7740\u6781\u51b7\u9177\u7684\u8ba5\u6d88\u4e4b\u610f\u3002 \u3000\u3000\u7ae5\u626c\u7684\u8138\u5374\u5df2\u5b8c\u5168\u626d\u66f2\u53d8\u5f62\uff0c\u773c\u775b\u91cc\u66f4\u5145\u6ee1\u4e86\u60ca\u8bb6\u3001\u6124\u6012\u3001\u6050\u60e7\u3002 \u3000\u3000\u4ed6\u4e5f\u4e0d\u500c\u4e16\u4e0a\u7ade\u771f\u7684\u6709\u8fd9\u79cd\u4eba\uff0c\u8fd9\u79cd\u4e8b\u3002 \u3000\u3000\u4ed6\u6b7b\u4e5f\u4e0d\u4fe1\uff01 \u3000\u3000\u4ed6\u4eec\u5c31\u8fd9\u6837\u9762\u9762\u76f8\u5bf9\u7740\u7ad9\u5728\u90a3\u91cc\uff0c\u7a81\u7136\u95f4\uff0c\u4e24\u4e2a\u4eba\u7684\u773c\u775b\u5168\u90fd\u53d8\u5f97\u7a7a\u6d1e\u3001\u65e0\u795e\u3002 \u3000\u3000\u7136\u540e\u4e24\u4e2a\u4eba\u5c31\u5168\u5012\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u4e00\u4e2a\u767d\u8863\u4eba\u4ece\u540e\u9762\u6162\u6162\u5730\u8d70\u51fa\u6765\uff0c\u89e3\u4e0b\u4e86\u80cc\u540e\u7684\u8349\u5e2d\uff0c\u5377\u8d77\u4e86\u6b7b\u8005\u7684\u5c38\u4f53\uff0c\u7528\u7cfb\u8349\u5e2d\u7684 \u957f\u7ef3\u6346\u4f4f\uff0c\u6302\u5728\u77ed\u6756\u4e0a\uff0c\u53c8\u6162\u6162\u5730\u8d70\u4e86\u56de\u53bb\u3002 \u3000\u3000\u4ed6\u8138\u4e0a\u4e5f\u4ecd\u7136\u51b7\u51b0\u51b0\u7684\u5168\u65e0\u8868\u60c5\uff0c\u5c31\u548c\u4ed6\u7684\u540c\u4f34\u521a\u624d\u5377\u8d77\u90a3\u6761\u6b7b\u72d7\u65f6\u5b8c\u5168\u4e00\u6837\u3002 \u3000\u3000\u72c2\u98ce\u7a81\u8d77\uff0c\u4ece\u8fdc\u65b9\u5439\u8fc7\u6765\uff0c\u98ce\u4e2d\u8fd8\u5e26\u7740\u8fdc\u5c71\u4e0a\u7684\u51b0\u78b4\u5b50\u3002 \u3000\u3000\u7ae5\u94dc\u5c71\u8eab\u540e\u7684\u5927\u6c49\u4eec\uff0c\u5374\u53ea\u89c9\u5f97\u638c\u5fc3\u5728\u5192\u6c57\u3002 \u3000\u3000\u58a8\u767d\u51dd\u89c6\u7740\u91cd\u94dc\u5c71\uff0c\u6de1\u6de1\u9053\uff1a\u201c\u9601\u4e0b\u662f\u5426\u5df2\u80af\u5316\u5e72\u6208\u4e3a\u7389\u5e1b\uff1f\u201d \u3000\u3000\u6bb5\u5341\u4e8c\u7a81\u7136\u7eb5\u51fa\u53bb\uff0c\u5389\u58f0\u9053\uff1a\u201c\u4f60\u8fd8\u5f97\u518d\u95ee\u95ee\u6211\u8fd9\u67f3\u5200\u2026\u2026\u201d \u3000\u3000\u4e00\u4e2a\u767d\u8863\u4eba\u6162\u6162\u5730\u4ece\u58a8\u81ea\u8eab\u540e\u8d70\u51fa\u6765\uff0c\u9053\uff1a\u201c\u6211\u6765\u95ee\u3002\u201d \u3000\u3000\u6bb5\u5341\u4e8c\u9053\uff1a\u201c\u4f60\u4e5f\u662f\u7528\u5200\uff1f\u201d \u3000\u3000\u8fd9\u767d\u8863\u4eba\u9053\uff1a\u201c\u6b63\u662f\u3002\u201d \u3000\u3000\u4ed6\u7684\u624b\u4e00\u5206\uff0c\u679c\u7136\u4ece\u77ed\u6756\u4e2d\u62bd\u51fa\u4e86\u4e00\u67c4\u5200\u3002 \u3000\u3000\u6bb5\u5341\u4e8c\u8fd9\u624d\u770b\u51fa\uff0c\u4ed6\u4eec\u624b\u91cc\u7684\u77ed\u6756\uff0c\u6709\u5bbd\u6709\u7a84\uff0c\u6709\u5706\u6709\u6241\uff0c\u91cc\u9762\u85cf\u7684\u5175\u5668\u663e\u7136\u90fd\u4e0d\u540c\u3002 \u3000\u3000\u522b\u4eba\u7528\u7684\u82e5\u662f\u5251\uff0c\u4ed6\u4eec\u5c31\u7528\u5251\u6765\u5bf9\u4ed8\uff0c\u522b\u4eba\u7528\u7684\u82e5\u662f\u5200\uff0c\u4ed6\u4eec\u5c31\u4e5f\u7528\u5200\u3002 \u3000\u3000\u6bb5\u5341\u4e8c\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u597d\uff0c\u4f60\u5148\u770b\u8fd9\u4e00\u5200\u3002\u201d \u3000\u3000\u4ed6\u8eab\u5f62\u534a\u8f6c\uff0c\u96c1\u7fce\u5200\u5df2\u5e26\u7740\u52b2\u98ce\uff0c\u6025\u524a\u8fd9\u767d\u8863\u4eba\u7684\u5de6\u80a9\u3002 \u3000\u3000\u767d\u8863\u4eba\u5c45\u7136\u4e5f\u4e0d\u907f\u4e0d\u95ea\uff0c\u638c\u4e2d\u5200\u4e5f\u4ee5\u4e00\u7740\u201c\u7acb\u5288\u534e\u5c71\u201d\uff0c\u6025\u524a\u6bb5\u5341\u4e8c\u7684\u5de6\u80a9\u3002 \u3000\u3000\u4f46\u6bb5\u5341\u4e8c\u7684\u6b66\u529f\uff0c\u5374\u663e\u7136\u4e0d\u662f\u7ae5\u626c\u80fd\u6bd4\u5f97\u4e0a\u7684\uff0c\u4ed6\u62db\u5f0f\u660e\u660e\u5df2\u7528\u8001\uff0c\u7a81\u7136\u60ac\u5d16\u52d2\u9a6c\uff0c\u8f6c \u8eab\u9519\u6b65\uff0c\u5200\u950b\u53cd\u8f6c\uff0c\u7531\u516b\u65b9\u85cf\u5200\u5f0f\uff0c\u7a81\u7136\u53d8\u4e3a\u5012\u6253\u91d1\u949f\uff0c\u5200\u5149\u5982\u5339\u7ec3\u822c\u53cd\u64a9\u767d\u8863\u4eba\u7684\u80f8\u808b\u3002 \u3000\u3000\u54ea\u77e5\u767d\u8863\u4eba\u4e5f\u60ac\u5d16\u52d2\u9a6c\uff0c\u7531\u516b\u65b9\u85cf\u5200\u5f0f\uff0c\u53d8\u4e3a\u5012\u6253\u91d1\u949f\uff01 \u3000\u3000\u4ed6\u51fa\u624b\u867d\u7136\u6162\u4e86\u534a\u7740\uff0c\u4f46\u6bb5\u5341\u4e8c\u82e5\u4e0d\u53d8\u62db\uff0c\u7eb5\u7136\u80fd\u5c06\u5bf9\u65b9\u7acb\u6bd9\u5200\u4e0b\uff0c\u81ea\u5df1\u4e5f\u4e07\u4e07\u907f\u4e0d\u5f00 \u5bf9\u65b9\u7684\u8fd9\u4e00\u5200\uff01 \u3000\u3000\u767d\u8863\u4eba\u4e0d\u8981\u547d\uff0c\u4ed6\u5374\u8fd8\u662f\u8981\u547d\u7684\u3002 \u3000\u3000\u4ed6\u4e00\u5200\u524a\u51fa\u65f6\uff0c\u5df2\u5148\u9632\u5230\u4e86\u8fd9\u4e00\u7740\uff0c\u7a81\u7136\u6e05\u5578\u4e00\u58f0\uff0c\u632f\u81c2\u800c\u8d77\uff0c\u51cc\u7a7a\u7ffb\u8eab\uff0c\u6325\u5200\u523a\u5411\u767d \u8863\u4eba\u7684\u5de6\u9888\u3002 \u3000\u3000\u8fd9\u4e00\u7740\u4ed6\u4ee5\u4e0a\u51cc\u4e0b\uff0c\u5360\u5c3d\u5148\u673a\uff0c\u767d\u8863\u4eba\u5168\u8eab\u90fd\u4f3c\u5df2\u5728\u4ed6\u5200\u98ce\u7b3c\u7f69\u4e0b\uff0c\u975e\u4f46\u65e0\u6cd5\u53d8\u62db\uff0c\u8fde \u95ea\u907f\u90fd\u65e0\u6cd5\u95ea\u907f\u3002 \u3000\u3000\u53ef\u6015\u7684\u662f\uff0c\u4ed6\u6839\u672c\u4e5f\u4e0d\u60f3\u95ea\u907f\u3002 \u3000\u3000\u6bb5\u5341\u4e8c\u4e00\u5200\u780d\u5728\u4ed6\u9888\u4e0a\u65f6\uff0c\u4ed6\u7684\u5200\u4e5f\u5df2\u523a\u5165\u4e86\u6bb5\u5341\u4e8c\u7684\u5c0f\u8179\uff01 \u3000\u3000\u4e09\u5c3a\u957f\u7684\u5200\u950b\uff0c\u5b8c\u5168\u90fd\u523a\u4e86\u8fdb\u53bb\uff0c\u53ea\u5269\u4e0b\u4e00\u622a\u5200\u67c4\u3002 \u3000\u3000\u6bb5\u5341\u4e8c\u72c2\u543c\u4e00\u58f0\uff0c\u6574\u4e2a\u4eba\u5c31\u50cf\u662f\u65d7\u82b1\u706b\u7bad\u4f3c\u7684\uff0c\u76f4\u7a9c\u4e0a\u4e24\u4e08\uff01 \u3000\u3000\u9c9c\u8840\u96e8\u70b9\u822c\u5730\u843d\u4e0b\u6765\uff0c\u70b9\u70b9\u5168\u90fd\u843d\u5728\u8fd9\u767d\u8863\u4eba\u7684\u8eab\u4e0a\u3002 \u3000\u3000\u4ed6\u7684\u4e00\u8eab\u767d\u8863\u7a81\u7136\u5df2\u88ab\u67d3\u7ea2\uff0c\u4f46\u8138\u4e0a\u5374\u8fd8\u662f\u51b7\u51b0\u51b0\u5168\u65e0\u8868\u60c5\uff0c\u76f4\u7b49\u6bb5\u5341\u4e8c\u4ece\u534a\u7a7a\u4e2d\u8dcc\u4e0b \u6765\uff0c\u4ed6\u624d\u5012\u4e0b\u53bb\u3002 \u3000\u3000\u5bf9\u4ed6\u6765\u8bf4\uff0c\u6b7b\uff0c\u5c31\u50cf\u662f\u56de\u5bb6\u4e00\u6837\uff0c\u6839\u672c\u5c31\u4e0d\u662f\u4ef6\u503c\u5f97\u754f\u60e7\u7684\u4e8b\u3002 \u3000\u3000\u7ae5\u94dc\u5c71\u8138\u8272\u5df2\u53d8\u4e86\uff0c\u970d\u7136\u957f\u8eab\u800c\u8d77\uff0c\u5389\u58f0\u9053\uff1a\u201c\u8fd9\u7b97\u662f\u4ec0\u4e48\u6b66\u529f\uff1f\u201d \u3000\u3000\u58a8\u767d\u6de1\u6de1\u9053\uff1a\u201c\u8fd9\u672c\u5c31\u4e0d\u80fd\u7b97\u4ec0\u4e48\u6b66\u529f\u3002\u201d \u3000\u3000\u7ae5\u94dc\u5c71\u6012\u9053\uff1a\u201c\u8fd9\u7b97\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u58a8\u767d\u9053\uff1a\u201c\u8fd9\u53ea\u80fd\u7b97\u4e00\u70b9\u6559\u8bad\u3002\u201d \u3000\u3000\u7ae5\u94dc\u5c71\u9053\uff1a\u201c\u6559\u8bad\uff1f\u201d \u3000\u3000\u58a8\u767d\u9053\uff1a\u201c\u8fd9\u6559\u8bad\u544a\u8bc9\u6211\u4eec\uff0c\u4f60\u82e5\u4e00\u5b9a\u8981\u6740\u522b\u4eba\uff0c\u522b\u4eba\u4e5f\u540c\u6837\u80fd\u6740\u4f60\uff01\u201d \u3000\u3000\u8f9b\u56db\u7a81\u7136\u51b7\u7b11\u9053\uff1a\u201c\u53ea\u6015\u672a\u5fc5\u3002\u201d \u3000\u3000\u4ed6\u8fd8\u662f\u7528\u5c0f\u6307\u52fe\u7740\u5251\u4e0a\u7684\u4e1d\u5e26\uff0c\u6162\u6162\u5730\u8d70\u4e86\u51fa\u6765\uff0c\u5251\u9798\u62d6\u5728\u51b0\u96ea\u4e0a\uff0c\u53d1\u51fa\u4e00\u9635\u9635\u523a\u8033\u7684 \u78e8\u64e6\u58f0\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u60e8\u767d\u7684\u8138\u4e0a\uff0c\u5374\u4f3c\u5df2\u6709\u4e86\u5149\uff0c\u773c\u775b\u91cc\u4e5f\u5728\u53d1\u7740\u5149\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u6211\u82e5\u8981\u6740\u4f60\u65f6\uff0c\u4f60 \u5c31\u4f11\u60f3\u6740\u5f97\u4e86\u6211\u7684\u3002\u201d \u3000\u3000\u4e00\u4e2a\u767d\u8863\u4eba\u6de1\u6de1\u9053\uff1a\u201c\u53ea\u6015\u672a\u5fc5\u3002\u201d \u3000\u3000\u4ed6\u7684\u8bdd\u8bf4\u5b8c\uff0c\u4eba\u5df2\u5230\u4e86\u8f9b\u56db\u9762\u524d\uff0c\u8eab\u624b\u663e\u7136\u6bd4\u521a\u624d\u4e24\u4eba\u5feb\u5f97\u591a\u3002 \u3000\u3000\u8f9b\u56db\u9053\uff1a\u201c\u672a\u5fc5\uff1f\u201d \u3000\u3000\u767d\u8863\u4eba\u9053\uff1a\u201c\u65e0\u8bba\u591a\u8f9b\u8fa3\u72e0\u6bd2\u7684\u5251\u6cd5\uff0c\u90fd\u6709\u4eba\u53ef\u7834\u7684\u3002\u201d \u3000\u3000\u8f9b\u56db\u51b7\u7b11\u9053\uff1a\u201c\u6740\u4eba\u7684\u5251\u6cd5\uff0c\u5c31\u65e0\u4eba\u80fd\u7834\u3002\u201d \u3000\u3000\u767d\u8863\u4eba\u9053\uff1a\u201c\u6709\u4e00\u79cd\u4eba\u3002\u201d \u3000\u3000\u8f9b\u56db\u9053\uff1a\u201c\u54ea\u79cd\u4eba\uff1f\u201d \u3000\u3000\u767d\u8863\u4eba\u9053\uff1a\u201c\u4e0d\u6015\u6b7b\u7684\u4eba\uff01\u201d \u3000\u3000\u8f9b\u56db\u9053\uff1a\u201c\u4f60\u5c31\u662f\u4e0d\u6015\u6b7b\u7684\u4eba\uff1f\u201d \u3000\u3000\u767d\u8863\u4eba\u51b7\u51b7\u9053\uff1a\u201c\u751f\u6709\u4f55\u6b22\uff0c\u6b7b\u6709\u4f55\u60e7\uff1f\u201d \u3000\u3000\u8f9b\u56db\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u6d3b\u7740\u5c31\u662f\u4e3a\u4e86\u51c6\u5907\u8981\u6b7b\u7684\u4e48\uff1f\u201d \u3000\u3000\u767d\u8863\u4eba\u9053\uff1a\u201c\u4e5f\u8bb8\u662f\u7684\uff01\u201d \u3000\u3000\u8f9b\u56da\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u6211\u4e0d\u5982\u5c31\u6210\u5168\u4e86\u4f60\u3002\u201d \u3000\u3000\u4ed6\u7684\u5251\u7a81\u7136\u51fa\u9798\uff0c\u5239\u90a3\u95f4\u5df2\u523a\u51fa\u4e03\u5251\uff0c\u5251\u98ce\u5982\u7834\u7af9\uff0c\u5251\u5149\u5982\u95ea\u7535\uff0c\u53ea\u89c1\u6ee1\u5929\u5251\u5f71\u5982\u82b1\u96e8 \u7f24\u7eb7\uff0c\u4ee4\u4eba\u6839\u672c\u5c31\u65e0\u6cd5\u5206\u8fa8\u4ed6\u7684\u51fa\u624b\u65b9\u4f4d\u3002 \u3000\u3000\u767d\u8863\u4eba\u4e5f\u6839\u672c\u4e0d\u60f3\u5206\u8fa8\uff0c\u4e5f\u4e0d\u60f3\u95ea\u907f\uff0c\u53ea\u662f\u9759\u9759\u5730\u7ad9\u5728\u90a3\u91cc\uff0c\u9759\u9759\u5730\u7b49\u7740\u3002 \u3000\u3000\u4ed6\u65e9\u5df2\u51c6\u5907\u8981\u6b7b\u7684\uff0c\u5bf9\u65b9\u7684\u5251\u4ece\u4ec0\u4e48\u5730\u65b9\u523a\u8fc7\u6765\uff0c\u4ed6\u6839\u672c\u5c31\u4e0d\u5728\u4e4e\u3002 \u3000\u3000\u8f9b\u56db\u4e03\u5251\u523a\u51fa\uff0c\u8fd9\u767d\u8863\u4eba\u7adf\u8fde\u52a8\u90fd\u6ca1\u6709\u52a8\uff0c\u8f9b\u56db\u7684\u5251\u4e00\u53d1\u5373\u6536\uff0c\u4e03\u5251\u90fd\u88ab\u8feb\u6210\u4e86\u865a\u62db\uff0c \u7a81\u7136\u4e00\u6ed1\u6b65\u5df2\u5230\u4e86\u767d\u8863\u4eba\u80cc\u540e\u3002\u4ed6\u5df2\u7b97\u51c6\u4e86\u8fd9\u90e8\u4f4d\u6b63\u662f\u767d\u8863\u4eba\u7684\u6b7b\u89d2\uff0c\u6ca1\u6709\u4eba\u80fd\u5728\u6b7b\u89d2\u4e2d\u51fa \u624b\u3002 \u3000\u3000\u4ed6\u8981\u6740\u8fd9\u4e2a\u4eba\uff0c\u7edd\u4e0d\u7ed9\u4e00\u70b9\u673a\u4f1a\u7ed9\u8fd9\u4e2a\u4eba\u6740\u4ed6\u3002 \u3000\u3000\u8fd9\u4e00\u62db\u523a\u51fa\uff0c\u865a\u62db\u5df2\u53d8\u6210\u5b9e\u62db\uff0c\u5251\u5149\u95ea\u7535\u822c\u523a\u5411\u767d\u8863\u4eba\u7684\u80cc\u810a\u3002 \u3000\u3000\u53ea\u542c\u201c\u54e7\u201d\u7684\u4e00\u58f0\uff0c\u5251\u950b\u5df2\u5165\u8089\uff01 \u3000\u3000\u4ed6\u751a\u81f3\u53ef\u4ee5\u611f\u89c9\u5230\u5251\u950b\u5728\u78e8\u64e6\u7740\u5bf9\u65b9\u7684\u9aa8\u5934\uff0c\u4f46\u5c31\u5728\u8fd9\u65f6\uff0c\u4ed6\u8d6b\u7136\u53d1\u73b0\u8fd9\u4e00\u5251\u5e76\u6ca1\u6709\u523a \u4e0a\u5bf9\u65b9\u80cc\u810a\uff0c\u5374\u523a\u4e0a\u4e86\u5bf9\u65b9\u80f8\u819b\u3002 \u3000\u3000\u5c31\u5728\u4ed6\u62db\u5f0f\u5df2\u7528\u8001\u7684\u90a3\u4e00\u5239\u90a3\u95f4\uff0c\u767d\u8863\u4eba\u7adf\u7a81\u7136\u8f6c\u8eab\uff0c\u4ee5\u80f8\u819b\u8fce\u4e0a\u4e86\u4ed6\u7684\u5251\u950b\u3002 \u3000\u3000\u6ca1\u6709\u4eba\u80fd\u60f3\u5230\u8fd9\u4e00\u7740\uff0c\u65e0\u8bba\u8c01\u4e5f\u4e0d\u4f1a\u7528\u81ea\u5df1\u7684\u8840\u8089\u4e4b\u8eaf\u6765\u62b5\u6321\u5251\u950b\u3002 \u3000\u3000\u4f46\u767d\u8863\u4eba\u7adf\u4ee5\u4ed6\u81ea\u5df1\u4f5c\u6b66\u5668\u3002 \u3000\u3000\u8f9b\u56db\u7684\u8138\u8272\u53d8\u4e86\uff0c\u7528\u529b\u62d4\u5251\uff0c\u5251\u950b\u663e\u7136\u5df2\u62ab\u5bf9\u65b9\u7684\u808b\u9aa8\u5939\u4f4f\u3002 \u3000\u3000\u4ed6\u60f3\u6492\u624b\u65f6\uff0c\u767d\u8863\u4eba\u7684\u5251\u5df2\u65e0\u58f0\u65e0\u606f\u5730\u523a\u4e86\u8fc7\u6765\uff0c\u5c31\u50cf\u662f\u4e2a\u6e29\u67d4\u7684\u5c11\u5973\uff0c\u5c06\u4e00\u6735\u9c9c\u82b1\u6162 \u6162\u5730\u63d2\u5165\u74f6\u4e2d\u4e00\u6837\uff0c\u5c06\u5251\u950b\u6162\u6162\u5730\u523a\u5165\u4ed6\u7684\u80f8\u819b\u3002 \u3000\u3000\u4ed6\u751a\u81f3\u8fde\u75db\u82e6\u90fd\u6ca1\u6709\u611f\u89c9\u5230\uff0c\u5df2\u89c9\u5f97\u80f8\u819b\u4e0a\u4e00\u9635\u5bd2\u51b7\u3002 \u3000\u3000\u7136\u540e\uff0c\u4ed6\u6574\u4e2a\u4eba\u5c31\u7a81\u7136\u5168\u90e8\u51b7\u900f\u3002 \u3000\u3000\u9c9c\u8840\u7ea2\u82b1\u822c\u6e85\u5c04\u51fa\u6765\uff0c\u4ed6\u4eec\u9762\u5bf9\u9762\u5730\u7ad9\u7740\uff0c\u4f60\u770b\u7740\u6211\uff0c\u6211\u770b\u7740\u4f60\uff01 \u3000\u3000\u767d\u8863\u4eba\u8138\u4e0a\u662f\u5168\u65e0\u8868\u60c5\uff0c\u8f9b\u56db\u7684\u8138\u5374\u5df2\u56e0\u60ca\u60e7\u800c\u626d\u66f2\u53d8\u5f62\u3002 \u3000\u3000\u4ed6\u7684\u5251\u6cd5\u867d\u7136\u6bd4\u8f83\u9ad8\u5f97\u591a\uff0c\u51fa\u624b\u867d\u7136\u6bd4\u767d\u8863\u4eba\u5feb\u5f97\u591a\uff0c\u4f46\u7ed3\u679c\u5374\u662f\u540c\u6837\u7684\u3002 \u3000\u3000\u8fd9\u4e00\u6218\u7a81\u7136\u5df2\u7ed3\u675f\u3002 \u3000\u3000\u7ae5\u94dc\u5c71\u970d\u7136\u7ad9\u8d77\uff0c\u53c8\u5750\u4e0b\uff0c\u8138\u4e0a\u5df2\u5168\u65e0\u8840\u8272\u3002 \u3000\u3000\u4ed6\u5e76\u4e0d\u662f\u6ca1\u6709\u770b\u8fc7\u6740\u4eba\uff0c\u4e5f\u4e0d\u662f\u6ca1\u770b\u8fc7\u4eba\u88ab\u6740\uff0c\u4f46\u4ed6\u5374\u4ece\u672a\u60f3\u5230\u8fc7\uff0c\u6740\u4eba\u7adf\u662f\u4ef6\u5982\u6b64\u60e8 \u70c8\u3001\u5982\u6b64\u53ef\u6015\u7684\u4e8b\u3002 \u3000\u3000\u6740\u4eba\u548c\u88ab\u4eba\u6740\u90fd\u540c\u6837\u60e8\u70c8\uff0c\u540c\u6837\u53ef\u6015\u3002 \u3000\u3000\u4ed6\u7a81\u7136\u89c9\u5f97\u60f3\u5410\u3002 \u3000\u3000\u58a8\u767d\u51dd\u89c6\u7740\u4ed6\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u82e5\u8981\u6740\u4eba\uff0c\u522b\u4eba\u4e5f\u540c\u6837\u80fd\u6740\u4f60\uff0c\u8fd9\u6559\u8bad\u4f60\u73b0\u5728\u60f3\u5fc5\u5df2\u8be5\u76f8 \u4fe1\u4e86\u3002\u201d \u3000\u3000\u7ae5\u94dc\u5c71\u6162\u6162\u5730\u70b9\u4e86\u70b9\u5934\uff0c\u4ec0\u4e48\u8bdd\u90fd\u6ca1\u6709\u8bf4\uff0c\u56e0\u4e3a\u4ed6\u6839\u672c\u5df2\u65e0\u8bdd\u53ef\u8bf4\u3002 \u3000\u3000\u58a8\u767d\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u4e5f\u8be5\u660e\u767d\uff0c\u6740\u4eba\u548c\u88ab\u6740\uff0c\u5f80\u5f80\u4f1a\u540c\u6837\u75db\u82e6\u3002\u201d \u3000\u3000\u5ba3\u94dc\u5c71\u627f\u8ba4\uff0c\u4ed6\u5df2\u4e0d\u80fd\u4e0d\u627f\u8ba4\u3002 \u3000\u3000\u58a8\u767d\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u4e3a\u4f55\u8fd8\u8981\u6740\u4eba\uff1f\u201d \u3000\u3000\u7ae5\u94dc\u5c71\u7684\u53cc\u62f3\u7d27\u63e1\uff0c\u5ffd\u7136\u9053\uff1a\u201c\u6211\u53ea\u60f3\u660e\u767d\uff0c\u4f60\u4eec\u8fd9\u4e48\u6837\u505a\uff0c\u7a76\u7adf\u662f\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u58a8\u767d\u9053\uff1a\u201c\u4e0d\u4e3a\u4ec0\u4e48\uff01\u201d \u3000\u3000\u7ae5\u94dc\u5c71\u9053\uff1a\u201c\u4f60\u4eec\u4e0d\u662f\u8001\u675c\u627e\u6765\u7684\uff1f\u201d \u3000\u3000\u58a8\u767d\u9053\uff1a\u201c\u4e0d\u662f\uff0c\u6211\u65e2\u4e0d\u8ba4\u5f97\u4f60\uff0c\u4e5f\u4e0d\u8ba4\u5f97\u4ed6\uff01\u201d \u3000\u3000\u7ae5\u94dc\u5c71\u9053\uff1a\u201c\u4f46\uff0c\u4f60\u4eec\u5374\u4e0d\u60dc\u4e3a\u4ed6\u800c\u6b7b\u3002\u201d \u3000\u3000\u58a8\u767d\u9053\uff1a\u201c\u6211\u4eec\u4e5f\u4e0d\u662f\u4e3a\u4ed6\u800c\u6b7b\u7684\uff0c\u6211\u4eec\u6b7b\uff0c\u53ea\u4e0d\u8fc7\u662f\u60f3\u8981\u522b\u4eba\u6d3b\u7740\u800c\u5df2\u3002\u201d \u3000\u3000\u4ed6\u770b\u4e86\u770b\u8840\u6cca\u4e2d\u7684\u5c38\u4f53\uff0c\u53c8\u9053\uff1a\u201c\u8fd9\u4e9b\u4eba\u867d\u5df2\u6b7b\u4e86\uff0c\u4f46\u5374\u81f3\u5c11\u6709\u4e09\u5341\u4e2a\u4eba\u53ef\u4ee5\u56e0\u4ed6\u4eec\u4e4b \u6b7b\u800c\u6d3b\u4e0b\u53bb\uff0c\u4f55\u51b5\uff0c\u4ed6\u4eec\u672c\u6765\u4e5f\u4e0d\u5fc5\u6b7b\uff01\u201d \u3000\u3000\u7ae5\u94dc\u5c71\u5403\u60ca\u5730\u770b\u7740\u4ed6\u9053\uff1a\u201c\u4f60\u4eec\u771f\u662f\u7531\u9752\u57ce\u6765\u7684\uff1f\u201d \u3000\u3000\u58a8\u767d\u9053\uff1a\u201c\u4f60\u4e0d\u4fe1\uff1f\u201d \u3000\u3000\u7ae5\u94dc\u5c71\u5b9e\u5728\u4e0d\u4fe1\uff0c\u4ed6\u53ea\u89c9\u5f97\u8fd9\u4e9b\u4eba\u672c\u8be5\u662f\u4ece\u5730\u72f1\u4e2d\u6765\u7684\u3002 \u3000\u3000\u4e16\u4e0a\u672c\u4e0d\u8be5\u6709\u8fd9\u79cd\u4eba\u3002 \u3000\u3000\u58a8\u767d\u9053\uff1a\u201c\u4f60\u5df2\u7b54\u5e94\uff1f\u201d \u3000\u3000\u7ae5\u94dc\u5c71\u9053\uff1a\u201c\u7b54\u5e94\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u58a8\u767d\u9053\uff1a\u201c\u5316\u5e72\u6208\u4e3a\u7389\u5e1b\u3002\u201d \u3000\u3000\u7ae5\u94dc\u5c71\u5ffd\u7136\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u6211\u5c31\u7b97\u7b54\u5e94\u4e5f\u6ca1\u6709\u7528\u3002\u201d \u3000\u3000\u58a8\u767d\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u7ae5\u94dc\u5c71\u9053\uff1a\u201c\u56e0\u4e3a\uff0c\u8fd8\u6709\u4e2a\u4eba\u7edd\u4e0d\u7b54\u5e94\u3002\u201d \u3000\u3000\u58a8\u767d\u9053\uff1a\u201c\u8c01\uff1f\u201d \u3000\u3000\u7ae5\u94dc\u5c71\u9053\uff1a\u201c\u536b\u516b\u592a\u7237\uff01\u201d \u3000\u3000\u58a8\u767d\u9053\uff1a\u201c\u4f60\u4e0d\u59a8\u53eb\u4ed6\u6765\u627e\u6211\u3002\u201d \u3000\u3000\u7ae5\u94dc\u5c71\u9053\uff1a\u201c\u5230\u54ea\u91cc\u53bb\u627e\uff1f\u201d \u3000\u3000\u58a8\u767d\u51b7\u6de1\u7684\u76ee\u5149\u5ffd\u7136\u773a\u671b\u8fdc\u65b9\uff0c\u8fc7\u4e86\u5f88\u4e45\uff0c\u624d\u7f13\u7f13\u9053\uff1a\u201c\u957f\u5b89\u57ce\u91cc\uff0c\u51b7\u9999\u56ed\u4e2d\u7684\u6885\u82b1\uff0c \u73b0\u5728\u60f3\u5fc5\u5df2\u5f00\u4e86\u2026\u2026\u201d \u3000\u3000\u536b\u516b\u592a\u7237\u5fc3\u60c5\u597d\u7684\u65f6\u5019\uff0c\u4e5f\u4f1a\u50cf\u666e\u901a\u4eba\u4e00\u6837\uff0c\u5fae\u7b11\u7740\u62cd\u4f60\u7684\u80a9\u8180\uff0c\u8bf4\u4ed6\u81ea\u5df1\u8ba4\u4e3a\u5f97\u610f\u7684 \u7b11\u8bdd\u3002 \u3000\u3000\u4f46\u5f53\u4ed6\u6124\u6012\u65f6\uff0c\u4ed6\u5374\u4f1a\u53d8\u5f97\u548c\u4f60\u8ba4\u5f97\u7684\u4efb\u4f55\u4eba\u90fd\u4e0d\u4e00\u6837\u4e86\u3002 \u3000\u3000\u4ed6\u90a3\u5f20\u901a\u5e38\u603b\u662f\u7ea2\u5149\u6ee1\u9762\u7684\u8138\uff0c\u7a81\u7136\u5c31\u4f1a\u53d8\u5f97\u50cf\u662f\u53ea\u9965\u997f\u800c\u6124\u6012\u7684\u72ee\u5b50\uff0c\u773c\u775b\u91cc\u4e5f\u4f1a\u5c04 \u51fa\u4e00\u79cd\u72ee\u5b50\u822c\u51cc\u5389\u800c\u53ef\u6015\u7684\u5149\u8292\u3002 \u3000\u3000\u4ed6\u770b\u6765\u7b80\u76f4\u5df2\u53d8\u6210\u53ea\u6012\u72ee\uff0c\u968f\u65f6\u968f\u523b\u90fd\u4f1a\u5c06\u4efb\u4f55\u4e00\u4e2a\u89e6\u6012\u4ed6\u7684\u4eba\u6293\u8fc7\u6765\uff0c\u6495\u6210\u788e\u7247\uff0c\u518d \u4e00\u7247\u7247\u541e\u4e0b\u53bb\u3002\u73b0\u5728\u6b63\u662f\u4ed6\u6124\u6012\u7684\u65f6\u5019\u3002 \u3000\u3000\u7ae5\u94dc\u5c71\u76b1\u7740\u7709\u5934\uff0c\u7ad9\u5728\u4ed6\u9762\u524d\uff0c\u8fd9\u5a01\u9547\u4e00\u65b9\u7684\u6b66\u6797\u5927\u8c6a\uff0c\u73b0\u5728\u5374\u50cf\u662f\u7a81\u7136\u53d8\u6210\u4e86\u53ea\u7f94 \u7f8a\uff0c\u8fde\u6c14\u90fd\u4e0d\u6562\u5598\u3002 \u3000\u3000\u536b\u516b\u592a\u7237\u7528\u4e00\u53cc\u6ee1\u5e03\u7ea2\u4e1d\u7684\u773c\u775b\u77aa\u7740\u4ed6\uff0c\u54ac\u7740\u7259\u9053\uff1a\u201c\u4f60\u8bf4\u90a3\u5a4a\u5b50\u517b\u7684\u6df7\u86cb\u53eb\u58a8\u767d\uff1f\u201d \u3000\u3000\u7ae5\u94dc\u5c71\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u536b\u516b\u592a\u7237\u9053\uff1a\u201c\u4f60\u8bf4\uff0c\u4ed6\u662f\u4ece\u9752\u57ce\u6765\u7684\uff1f\u201d \u3000\u3000\u7ae5\u94dc\u5c71\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u536b\u516b\u592a\u7237\u9053\uff1a\u201c\u9664\u6b64\u4e4b\u5916\uff0c\u4f60\u5c31\u4ec0\u4e48\u90fd\u4e0d\u77e5\u9053\u4e86\uff1f\u201d \u3000\u3000\u7ae5\u94dc\u5c71\u7684\u5934\u5782\u5f97\u66f4\u4f4e\uff0c\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u536b\u516b\u592a\u7237\u5589\u5499\u91cc\u53d1\u51fa\u6012\u72ee\u822c\u7684\u4f4e\u543c\uff1a\u201c\u90a3\u5a4a\u5b50\u517b\u7684\u6740\u4e86\u6211\u4e24\u4e2a\u597d\u5f92\u5f1f\uff0c\u4f60\u5374\u8fde\u4ed6\u7684\u6765\u5386 \u90fd\u4e0d\u77e5\u9053\uff0c\u4f60\u8fd8\u6709\u8138\u6765\u89c1\u6211\uff0c\u6211\u5165\u6b7b\u4f60\u7684\u4eb2\u5a18\u5976\u5976\u3002\u201d \u3000\u3000\u4ed6\u7a81\u7136\u4ece\u6905\u5b50\u4e0a\u8df3\u8d77\uff0c\u51b2\u8fc7\u6765\uff0c\u4e00\u628a\u63ea\u4f4f\u4e86\u7ae5\u94dc\u5c71\u7684\u8863\u895f\uff0c\u4e00\u4e0b\u5b50\u5c31\u6495\u6210\u4e24\u534a\uff0c\u63a5\u7740\u53c8 \u6b63\u6b63\u53cd\u53cd\u7ed9\u4e86\u7ae5\u94dc\u5c71\u5341\u516d\u516b\u4e2a\u8033\u62ec\u5b50\u3002 \u3000\u3000\u7ae5\u94dc\u5c71\u7684\u5634\u89d2\u5df2\u88ab\u6253\u5f97\u4e0d\u505c\u5730\u6d41\u8840\uff0c\u4f46\u770b\u6765\u5374\u4e00\u70b9\u6124\u6012\u75db\u82e6\u7684\u8868\u60c5\u90fd\u6ca1\u6709\uff0c\u53cd\u800c\u597d\u50cf\u89c9 \u5f97\u5f88\u6b22\u559c\uff0c\u5f88\u5b89\u5fc3\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u77e5\u9053\u536b\u516b\u592a\u7237\u6253\u5f97\u8d8a\u51f6\uff0c\u9a82\u5f97\u8d8a\u51f6\uff0c\u5c31\u8868\u793a\u8fd8\u5c06\u4ed6\u5f53\u505a\u81ea\u5df1\u4eba\u3002 \u3000\u3000\u53ea\u8981\u536b\u516b\u592a\u7237\u8fd8\u5c06\u4ed6\u5f53\u505a\u81ea\u5df1\u4eba\uff0c\u4ed6\u8fd9\u6761\u547d\u5c31\u7b97\u6361\u56de\u6765\u4e86\u3002 \u3000\u3000\u536b\u516b\u592a\u7237\u82e5\u662f\u5bf9\u4ed6\u5ba2\u5ba2\u6c14\u6c14\uff0c\u4ed6\u4eca\u5929\u5c31\u4f11\u60f3\u6d3b\u7740\u8d70\u51fa\u8fd9\u5c4b\u5b50\u3002 \u3000\u3000\u5341\u516d\u516b\u4e2a\u8033\u5149\u6253\u5b8c\uff0c\u536b\u516b\u592a\u7237\u53c8\u7ed9\u4ed6\u809a\u5b50\u4e0a\u6dfb\u4e86\u4e00\u811a\u3002 \u3000\u3000\u7ae5\u94dc\u5c71\u867d\u7136\u5df2\u88ab\u6253\u5f97\u4e00\u8138\u8840\uff0c\u4e00\u5934\u51b7\u6c57\uff0c\u5374\u8fd8\u662f\u4e56\u4e56\u5730\u7ad9\u5728\u90a3\u91cc\uff0c\u8fde\u52a8\u90fd\u4e0d\u6562\u52a8\u3002 \u3000\u3000\u536b\u516b\u592a\u7237\u603b\u7b97\u5598\u4e86\u53e3\u6c14\uff0c\u77aa\u7740\u4ed6\u6012\u543c\u9053\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u5c0f\u56db\u5b50\u4ed6\u4eec\u662f\u53bb\u5e2e\u4f60\u6740\u4eba\u7684\uff1f\u201d \u3000\u3000\u7ae5\u94dc\u5c71\u9053\uff1a\u201c\u77e5\u9053\u3002\u201d \u3000\u3000\u536b\u516b\u592a\u7237\u9053\uff1a\u201c\u73b0\u5728\u4ed6\u4eec\u5df2\u88ab\u4eba\u5f04\u6b7b\uff0c\u4f60\u53cd\u800c\u6d3b\u8e66\u4e71\u8df3\u5730\u56de\u6765\u4e86\uff0c\u4f60\u7b97\u662f\u4e2a\u4ec0\u4e48\u4e1c \u897f\uff1f\u201d \u3000\u3000\u7ae5\u94dc\u5c71\u9053\uff1a\u201c\u6211\u4e0d\u662f\u4e2a\u4e1c\u897f\uff0c\u53ef\u662f\u6211\u4e5f\u4e0d\u6562\u4e0d\u56de\u6765\u3002\u201d \u3000\u3000\u536b\u516b\u592a\u7237\u9053\uff1a\u201c\u4f60\u4e2a\u738b\u516b\u86cb\uff0c\u4f60\u4e0d\u6562\u4e0d\u56de\u6765\uff1f\u4f60\u96be\u9053\u4e0d\u4f1a\u5939\u7740\u5c3e\u5df4\u9003\u5f97\u8fdc\u8fdc\u7684\uff0c\u4e5f\u514d\u5f97 \u8ba9\u6211\u8001\u4eba\u5bb6\u770b\u89c1\u751f\u6c14\u3002\u201d \u3000\u3000\u7ae5\u94dc\u5c71\u9053\uff1a\u201c\u6211\u4e5f\u77e5\u9053\u4f60\u8001\u4eba\u5bb6\u4f1a\u751f\u6c14\uff0c\u6240\u4ee5\u4f60\u8001\u4eba\u5bb6\u8981\u6253\u5c31\u6253\uff0c\u8981\u6740\u5c31\u6740\uff0c\u6211\u90fd\u6ca1\u8bdd \u8bf4\uff0c\u4f46\u82e5\u8981\u6211\u80cc\u7740\u4f60\u8001\u4eba\u5bb6\u9003\u8d70\uff0c\u6211\u6b7b\u4e5f\u4e0d\u80af\u3002\u201d \u3000\u3000\u536b\u516b\u592a\u7237\u77aa\u7740\u4ed6\uff0c\u7a81\u7136\u5927\u7b11\u9053\uff1a\u201c\u597d\uff0c\u6709\u79cd\uff01\u201d \u3000\u3000\u4ed6\u4f38\u624b\u62e5\u4f4f\u4e86\u7ae5\u94dc\u5c71\u7684\u80a9\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u5927\u5bb6\u770b\u770b\uff0c\u8fd9\u624d\u662f\u6211\u7684\u597d\u513f\u5b50\uff0c\u4f60\u4eec\u5168\u90e8 \u8be5\u5b66\u5b66\u4ed6\uff0c\u505a\u9519\u4e8b\u6015\u4ec0\u4e48\uff1f\u4ed6\u5976\u5976\u7684\u6709\u8c01\u8fd9\u4e00\u8f88\u5b50\u6ca1\u505a\u8fc7\u9519\u4e8b\uff0c\u8fde\u6211\u536b\u5929\u9e4f\u90fd\u505a\u8fc7\u9519\u4e8b\uff0c\u4f55 \u51b5\u522b\u4eba\u3002\u201d \u3000\u3000\u4ed6\u4e00\u7b11\uff0c\u5927\u5385\u91cc\u5341\u6765\u4e2a\u4eba\u7acb\u523b\u5168\u90e8\u677e\u4e86\u4e00\u53e3\u6c14\u3002 \u3000\u3000\u536b\u516b\u592a\u7237\u9053\uff1a\u201c\u4f60\u4eec\u6709\u8c01\u77e5\u9053\u58a8\u767d\u90a3\u5a4a\u5b50\u517b\u7684\u662f\u4e2a\u4ec0\u4e48\u4e1c\u897f\uff1f\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u867d\u7136\u662f\u95ee\u5927\u5bb6\u7684\uff0c\u4f46\u4ed6\u7684\u773c\u775b\u5374\u53ea\u76ef\u5728\u4e00\u4e2a\u4eba\u8eab\u4e0a\u3002 \u3000\u3000\u8fd9\u4eba\u767d\u767d\u7684\u8138\uff0c\u7559\u7740\u4e24\u6487\u5c0f\u80e1\u5b50\uff0c\u770b\u6765\u5f88\u65af\u6587\uff0c\u4e5f\u5f88\u548c\u6c14\u3002 \u3000\u3000\u4e0d\u8ba4\u5f97\u4ed6\u7684\u4eba\uff0c\u8c01\u4e5f\u770b\u4e0d\u51fa\u8fd9\u65af\u65af\u6587\u6587\u7684\u767d\u9762\u4e66\u751f\uff0c\u5c31\u662f\u536b\u516b\u592a\u7237\u95e8\u4e0b\u7b2c\u4e00\u53f7\u6700\u53ef\u6015\u7684 \u4eba\u7269\u3001\u9ed1\u81ea\u4e24\u9053\u5168\u90fd\u95fb\u540d\u4e27\u80c6\u7684\u201c\u94c1\u9525\u5b50\u97e9\u8d1e\u201d\u3002 \u3000\u3000\u4ed6\u8fd9\u4eba\u7684\u786e\u5c31\u50cf\u662f\u94c1\u9525\u5b50\uff0c\u65e0\u8bba\u4f60\u6709\u591a\u786c\u7684\u58f3\uff0c\u4ed6\u90fd\u80fd\u628a\u4f60\u94bb\u51fa\u4e2a\u5927\u6d1e\u6765\u3002 \u3000\u3000\u4f46\u770b\u8d77\u6765\u4ed6\u5374\u7edd\u5bf9\u662f\u4e2a\u6e29\u548c\u53cb\u5584\u7684\u4eba\uff0c\u8138\u4e0a\u603b\u662f\u5e26\u7740\u79cd\u5b89\u8be6\u7684\u5fae\u7b11\uff0c\u8bf4\u8bdd\u7684\u58f0\u97f3\u7f13\u6162\u800c \u7a33\u5b9a\u3002 \u3000\u3000\u4ed6\u786e\u5b9a\u4e86\u6ca1\u6709\u522b\u4eba\u56de\u7b54\u8fd9\u53e5\u6d3b\u4e4b\u540e\uff0c\u624d\u7f13\u7f13\u9053\uff1a\u201c\u591a\u5e74\u524d\uff0c\u6709\u4e00\u5bb6\u59d3\u58a8\u7684\u4eba\uff0c\u4e3a\u4e86\u907f\u7978 \u800c\u9690\u5c45\u5230\u9752\u57ce\u5c71\uff0c\u58a8\u767d\u4e5f\u8bb8\u5c31\u662f\u8fd9\u4e00\u5bb6\u7684\u4eba\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u53c8\u7b11\u4e86\uff0c\u813e\u7768\u56db\u987e\uff0c\u5927\u7b11\u9053\uff1a\u201c\u6211\u65e9\u5c31\u8bf4\u8fc7\uff0c\u5929\u4e0b\u7684\u4e8b\uff0c\u8fd9\u5c0f\u5b50\u597d\u50cf\u6ca1\u6709\u4e00\u6837\u4e0d \u77e5\u9053\u7684\u3002\u201d \u3000\u3000\u97e9\u8d1e\u5fae\u7b11\u9053\uff1a\u201c\u4f46\u6211\u5374\u4e5f\u4e0d\u77e5\u9053\u4ed6\u4eec\u7684\u9690\u5c45\u5904\uff0c\u53ea\u4e0d\u8fc7\u6bcf\u9694\u4e09\u4e94\u5e74\uff0c\u4ed6\u4eec\u81ea\u5df1\u5374\u8981\u51fa\u5c71 \u4e00\u6b21\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u51fa\u6765\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u7ba1\u95f2\u4e8b\uff01\u201d \u3000\u3000\u536b\u516b\u592a\u7237\u7684\u8138\u53c8\u6c89\u4e86\u4e0b\u53bb\uff0c\u4ed6\u4e00\u5411\u4e0d\u559c\u6b22\u591a\u7ba1\u95f2\u4e8b\u7684\u4eba\u3002 \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u4ed6\u4eec\u4e0d\u80fd\u4e0d\u7ba1\u95f2\u4e8b\uff0c\u56e0\u4e3a\u4ed6\u4eec\u81ea\u79f0\u662f\u58a8\u7fdf\u7684\u540e\u4ee3\uff0c\u58a8\u5bb6\u7684\u540e\u4ee3\uff0c\u58a8\u5bb6\u7684\u5f1f \u5b50\uff0c\u672c\u5c31\u4e0d\u80fd\u505a\u4e00\u4e2a\u72ec\u5584\u5176\u8eab\u7684\u9690\u58eb\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u76b1\u7709\u9053\uff1a\u201c\u58a8\u7fdf\u53c8\u662f\u4ec0\u4e48\u4e1c\u897f\uff1f\u201d \u3000\u3000\u97e9\u8d1e\u6de1\u6de1\u4e00\u6652\u9053\uff1a\u201c\u4ed6\u4e0d\u662f\u4e1c\u897f\uff0c\u662f\u4e2a\u4eba\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u53cd\u800c\u7b11\u4e86\uff0c\u6562\u5728\u4ed6\u9762\u524d\u9876\u649e\u4ed6\u7684\u4eba\u5e76\u4e0d\u591a\u3002 \u3000\u3000\u5c31\u50cf\u662f\u5927\u591a\u6570\u88ab\u79f0\u4e3a\u201c\u592a\u7237\u201d\u7684\u4eba\u4e00\u6837\uff0c\u5076\u5c14\u4ed6\u4e5f\u559c\u6b22\u6709\u4eba\u6765\u9876\u649e\u4ed6\u3002 \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u58a8\u7fdf\u5c31\u662f\u58a8\u5b50\uff0c\u58a8\u5bb6\u7684\u7cbe\u795e\uff0c\u5c31\u5728\u4e4e\u6025\u4eba\u4e4b\u96be\uff0c\u751a\u81f3\u4e0d\u60dc\u6469\u9876\u653e\u8e35\u3001\u8d74\u6c64\u8e48 \u706b\u7684\uff0c\u6240\u4ee5\u58a8\u5bb6\u7684\u5f1f\u5b50\uff0c\u7edd\u4e0d\u80fd\u505a\u9690\u58eb\uff0c\u53ea\u80fd\u505a\u4e49\u58eb\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u53c8\u6c89\u4e0b\u4e86\u8138\uff0c\u9053\uff1a\u201c\u96be\u9053\u58a8\u767d\u90a3\u4e2a\u738b\u516b\u86cb\u4e5f\u662f\u4e2a\u4e49\u58eb\uff1f\u201d \u3000\u3000\u97e9\u8d1e\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4e49\u58eb\u4e5f\u6709\u5f88\u591a\u79cd\u7684\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u54e6\uff01\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u6709\u79cd\u4e49\u58eb\uff0c\u505a\u7684\u4e8b\u770b\u6765\u867d\u51a0\u5195\u5802\u7687\uff0c\u5176\u5b9e\u6697\u5730\u91cc\u5374\u522b\u6709\u4f01\u56fe\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u8fd9\u79cd\u4e49\u58eb\u597d\u5bf9\u4ed8\u3002\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u600e\u4e48\u5bf9\u4ed8\uff1f\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u5bb0\u4e00\u4e2a\u5c11\u4e00\u4e2a\u3002\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u5bb0\u4e0d\u5f97\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u5bb0\u4e0d\u5f97\uff1f\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u4e49\u58eb\u5c31\u8ddf\u541b\u5b50\u4e00\u6837\uff0c\u65e0\u8bba\u771f\u5047\uff0c\u90fd\u5bb0\u4e0d\u5f97\u7684\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u5c45\u7136\u5927\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u82e5\u5bb0\u4e86\u4ed6\u4eec\uff0c\u5c31\u4e00\u5b9a\u4f1a\u6709\u4eba\u8bf4\u4f60\u662f\u4e2a\u4e0d\u4ec1\u4e0d\u4e49\u7684\u5c0f \u4eba\u3002\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u6240\u4ee5\u4ed6\u4eec\u5bb0\u4e0d\u5f97\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u77aa\u773c\u9053\uff1a\u201c\u5f53\u7136\u5bb0\u4e0d\u5f97\uff0c\u8c01\u8bf4\u8981\u5bb0\u4ed6\u4eec\uff0c\u6211\u5c31\u5148\u5bb0\u4e86\u4ed6\uff01\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u4f55\u51b5\uff0c\u8981\u5bb0\u4ed6\u4eec\u4e5f\u662f\u4ef6\u4e0d\u5bb9\u6613\u7684\u4e8b\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u90a3\u738b\u516b\u86cb\u96be\u9053\u771f\u6709\u4e24\u4e0b\u5b50\uff1f\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u4ed6\u672c\u8eab\u4e5f\u8bb8\u5e76\u4e0d\u53ef\u6015\uff0c\u53ef\u6015\u7684\u662f\u4ed6\u624b\u4e0b\u90a3\u4e9b\u6b7b\u58eb\u3002\u201d \u3000\u3000\u97e9\u8d1e\u53c8\u9053\uff1a\u201c\u6b7b\u58eb\u7684\u610f\u601d\uff0c\u5c31\u662f\u8bf4\u8fd9\u4e9b\u4eba\u968f\u65f6\u90fd\u5728\u51c6\u5907\u4e3a\u4ed6\u800c\u6b7b\u7684\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u90a3\u4e9b\u4eba\u96be\u9053\u4e0d\u8981\u547d\uff1f\u201d \u3000\u3000\u97e9\u8d1e\u70b9\u70b9\u5934\u9053\uff1a\u201c\u4e0d\u8981\u547d\u7684\u4eba\uff0c\u5c31\u662f\u6700\u53ef\u6015\u7684\u4eba\uff0c\u4e0d\u8981\u547d\u7684\u6b66\u529f\uff0c\u5c31\u662f\u6700\u53ef\u6015\u7684\u6b66 \u529f\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u5728\u7b49\u7740\u4ed6\u89e3\u91ca\u3002 \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u56e0\u4e3a\u4f60\u6740\u4ed6\u4e00\u5200\uff0c\u4ed6\u540c\u6837\u53ef\u4ee5\u6740\u4f60\u4e00\u5200\u3002\u201d\u536b\u5929\u9e4f\u663e\u7136\u5bf9\u8fd9\u89e3\u91ca\u8fd8\u4e0d\u6ee1\u610f\u3002 \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u4f60\u7684\u51fa\u624b\u7eb5\u7136\u6bd4\u4ed6\u7094\uff0c\u4f46\u4f60\u6740\u4ed6\u65f6\u4ed6\u8fd8\u662f\u53ef\u4ee5\u6740\u4e86\u4f60\uff0c\u56e0\u4e3a\u4f60\u4e00\u5200\u780d\u4e0b\uff0c\u4ed6 \u6839\u672c\u4e0d\u60f3\u95ea\u907f\uff0c\u6240\u4ee5\u5728\u4f60\u5200\u950b\u780d\u5728\u4ed6\u8089\u91cc\u90a3\u4e00\u77ac\u95f4\uff0c\u4ed6\u5df2\u6709\u8db3\u591f\u7684\u65f6\u95f4\u6740\u201d\uff01\u201d \u3000\u3000\u536b\u5929\u9e4f\u7a81\u7136\u8d70\u8fc7\u53bb\uff0c\u7528\u529b\u4e00\u62cd\u4ed6\u80a9\u5934\uff0c\u9053\uff1a\u201c\u8bf4\u5f97\u597d\uff01\u8bf4\u5f97\u6709\u7406\uff01\u201d \u3000\u3000\u97e9\u8d1e\u770b\u7740\u4ed6\uff0c\u5df2\u660e\u767d\u4ed6\u7684\u610f\u601d\u3002 \u3000\u3000\u4e0d\u662f\u4ec7\u654c\uff0c\u5c31\u662f\u670b\u53cb\u3002 \u3000\u3000\u6211\u82e5\u6740\u4e0d\u4e86\u4f60\uff0c\u5c31\u4ea4\u4f60\u8fd9\u4e2a\u670b\u53cb\u3002 \u3000\u3000\u8fd9\u4e0d\u4f46\u662f\u536b\u5929\u9e4f\u7684\u539f\u5219\uff0c\u4e5f\u662f\u53e4\u5f80\u4eca\u6765\uff0c\u6240\u6709\u6b66\u6797\u5927\u8c6a\u5171\u540c\u7684\u539f\u5bf9\u4ed6\u4eec\u8fd9\u4e9b\u4eba\u6765\u8bf4\uff0c\u8fd9 \u539f\u5219\u65e0\u7591\u662f\u7edd\u5bf9\u6b63\u786e\u7684\u3002 \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u7ae5\u8001\u5927\u8bf4\u8fc7\uff0c\u4ed6\u4eec\u8981\u5230\u957f\u5b89\u57ce\u53bb\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u6162\u6162\u5730\u70b9\u4e86\u70b9\u5934\uff0c\u9053\uff1a\u201c\u542c\u8bf4\u51b7\u9999\u56ed\u662f\u4e2a\u597d\u5730\u65b9\uff0c\u6211\u4e5f\u65e9\u5c31\u60f3\u53bb\u770b\u770b\u4e86\u3002\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u51b7\u9999\u56ed\u5360\u5730\u5343\u4ea9\uff0c\u79cd\u7740\u4e07\u5343\u6885\u82b1\uff0c\u73b0\u5728\u6b63\u662f\u6885\u82b1\u5f00\u5f97\u6700\u8273\u7684\u65f6\u5019\uff0c\u6240 \u4ee5\u2026\u2026\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u6240\u4ee5\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u65e2\u7136\u8981\u53bb\uff0c\u4e0d\u5982\u5c31\u7d22\u6027\u5c06\u90a3\u5730\u65b9\u5168\u5305\u4e0b\u6765\u3002\u201d \u3000\u3000\u5de5\u5929\u9e4f\u9053\uff1a\u201c\u6709\u7406\u3002\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u7b49\u58a8\u767d\u53bb\u4e86\uff0c\u6211\u4eec\u5c31\u597d\u597d\u5730\u8bf7\u8bf7\u4ed6\uff0c\u8ba9\u4ed6\u770b\u770b\u536b\u516b\u592a\u7237\u7684\u573a\u9762\uff0c\u4ed6\u82e5\u4e0d\u662f\u5446 \u5b50\uff0c\u4ee5\u540e\u60f3\u5fc5\u5c31\u4e0d\u4f1a\u8ddf\u6211\u4eec\u4f5c\u5bf9\u4e86\uff01\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u4ed6\u662f\u4e0d\u662f\u5446\u5b50\uff1f\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u5f53\u7136\u4e0d\u662f\uff01\u201d \u3000\u3000\u536b\u5929\u9e4f\u62ca\u638c\u5927\u7c7b\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u597d\u4e3b\u610f\u3002\u201d \u3000\u3000\u957f\u5eca\u91cc\u5f88\u5b89\u9759\uff0c\u5eca\u5916\u4e5f\u79cd\u7740\u6885\u82b1\u3002 \u3000\u3000\u7ae5\u94dc\u5c71\u548c\u97e9\u8d1e\u6162\u6162\u5730\u8d70\u5728\u957f\u5eca\u4e0a\uff0c\u4ed6\u4eec\u672c\u5c31\u662f\u8001\u670b\u53cb\uff0c\u5374\u5df2\u6709\u591a\u5e74\u672a\u89c1\u3002 \u3000\u3000\u98ce\u5f88\u51b7\uff0c\u51b7\u98ce\u4e2d\u5145\u6ee1\u4e86\u6885\u82b1\u7684\u9999\u6c14\u3002 \u3000\u3000\u7ae5\u94dc\u5c71\u5ffd\u7136\u505c\u4e0b\u6765\uff0c\u51dd\u89c6\u7740\u97e9\u8d1e\u9053\uff1a\u201c\u6709\u4ef6\u4e8b\u6211\u603b\u89c9\u5f97\u5947\u602a\u3002\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u7ae5\u94dc\u5c71\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u53ea\u8981\u4f60\u8bf4\u51fa\u6765\u7684\u8bdd\uff0c\u8001\u7237\u5b50\u5c31\u8ba4\u4e3a\u662f\u597d\u4e3b\u610f\uff1f\u201d \u3000\u3000\u97e9\u8d1e\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u56e0\u4e3a\u90a3\u65e9\u5c31\u662f\u4ed6\u7684\u4e3b\u610f\uff0c\u6211\u53ea\u4e0d\u8fc7\u66ff\u4ed6\u8bf4\u51fa\u6765\u800c\u5df2\u3002\u201d \u3000\u3000\u7ae5\u94dc\u5c71\u9053\uff1a\u201c\u65e2\u7136\u662f\u4ed6\u7684\u4e3b\u610f\uff0c\u4e3a\u4ec0\u4e48\u8981\u4f60\u8bf4\u51fa\u6765\uff1f\u201d \u3000\u3000\u97e9\u8d1e\u6c89\u541f\u9053\uff1a\u201c\u4f60\u8ddf\u7740\u8001\u7237\u5b50\u5df2\u6709\u591a\u4e45\uff1f\u201d \u3000\u3000\u7ae5\u94dc\u5c71\u9053\uff1a\u201c\u4e5f\u6709\u5341\u591a\u5e74\u4e86\u3002\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u4f60\u770b\u4ed6\u662f\u4e2a\u4ec0\u4e48\u6837\u7684\u4eba\uff1f\u201d \u3000\u3000\u7ae5\u94dc\u5c71\u8fdf\u7591\u7740\u9053\uff1a\u201c\u4f60\u770b\u5462\uff1f\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u6211\u60f3\u4f60\u4e00\u5b9a\u8ba4\u4e3a\u4ed6\u662f\u4e2a\u5f88\u7c97\u91ce\u3001\u5f88\u66b4\u8e81\uff0c\u4ece\u6765\u4e5f\u4e0d\u61c2\u5f97\u7528\u5fc3\u673a\u7684\u4eba\u3002\u201d \u3000\u3000\u7ae5\u94dc\u5c71\u9053\uff1a\u201c\u4ed6\u96be\u9053\u4e0d\u662f\uff1f\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u6614\u5e74\u4e2d\u539f\u516b\u6770\u7eb5\u6a2a\u5929\u4e0b\uff0c\u5927\u5bb6\u90fd\u8ba4\u4e3a\u6700\u7cbe\u660e\u7684\u5c31\u662f\u5218\u4e09\u7237\uff0c\u6700\u5389\u5bb3\u7684\u662f\u674e\u4e03 \u7237\uff0c\u6700\u7cca\u6d82\u7684\u5c31\u662f\u536b\u516b\u7237\u3002\u201d \u3000\u3000\u7ae5\u94dc\u5c71\u9053\uff1a\u201c\u6211\u4e5f\u542c\u8bf4\u8fc7\u3002\u201d \u3000\u3000\u97e9\u8d1e\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4f46\u73b0\u5728\u6700\u7cbe\u660e\u7684\u5218\u4e09\u7237\u548c\u6700\u5389\u5bb3\u7684\u674e\u4e03\u7237\u90fd\u5df2\u6b7b\u4e86\uff0c\u6700\u7cca\u6d82\u7684\u536b\u516b \u7237\u5374\u8fd8\u6d3b\u7740\uff0c\u800c\u4e14\u8fc7\u5f97\u5f88\u597d\u3002\u201d \u3000\u3000\u7ae5\u94dc\u5c71\u7b11\u4e86\uff0c\u4ed6\u5ffd\u7136\u5df2\u660e\u767d\u97e9\u8d1e\u7684\u610f\u601d\u3002 \u3000\u3000\u53ea\u6709\u4f1a\u88c5\u7cca\u6d82\u3001\u4e5f\u80af\u88c5\u7cca\u6d82\u7684\u4eba\uff0c\u624d\u662f\u771f\u6b63\u6700\u7cbe\u660e\u3001\u6700\u5389\u5bb3\u7684\u3002 \u3000\u3000\u7ae5\u94dc\u5c71\u5ffd\u53c8\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u88c5\u7cca\u6d82\u4e5f\u4e0d\u662f\u5bb9\u6613\u4e8b\u3002\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u7684\u786e\u4e0d\u662f\u3002\u201d \u3000\u3000\u7ae5\u94dc\u5c71\u9053\uff1a\u201c\u770b\u6765\uff0c\u4f60\u5c31\u662f\u4e0d\u4f1a\u88c5\u7cca\u6d82\u3002\u201d \u3000\u3000\u97e9\u8d1e\u82e6\u7b11\u9053\uff1a\u201c\u73b0\u5728\u6211\u5c31\u7b97\u771f\u7684\u7cca\u6d82\uff0c\u4e5f\u4e0d\u80fd\u9732\u51fa\u7cca\u6d82\u7684\u6837\u5b50\u6765\u3002\u201d \u3000\u3000\u7ae5\u94dc\u5c71\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u56e0\u4e3a\u7cca\u6d82\u4eba\u8eab\u65c1\uff0c\u603b\u5f97\u6709\u4e2a\u7cbe\u660e\u7684\u4eba\uff0c\u73b0\u5728\u6211\u626e\u7684\u5c31\u662f\u8fd9\u4e2a\u7cbe\u660e\u7684\u4eba\u3002\u201d \u3000\u3000\u7ae5\u94dc\u5c71\u9053\uff1a\u201c\u6240\u4ee5\u53ea\u8981\u4f60\u8bf4\u51fa\u6765\u7684\uff0c\u8001\u7237\u5b50\u5c31\u8ba4\u4e3a\u662f\u597d\u4e3b\u610f\u3002\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u5c31\u7b97\u540e\u6765\u53d1\u73b0\u90a3\u5e76\u4e0d\u662f\u597d\u4e3b\u610f\uff0c\u9519\u7684\u4e5f\u662f\u6211\uff0c\u4e0d\u662f\u8001\u7237\u5b50\u3002\u201d \u3000\u3000\u7ae5\u94dc\u5c71\u9053\uff1a\u201c\u6240\u4ee5\u522b\u4eba\u6068\u7684\u4e5f\u662f\u4f60\uff0c\u4e0d\u662f\u8001\u7237\u5b50\u3002\u201d \u3000\u3000\u97e9\u8d1e\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u73b0\u5728\u4e5f\u8be5\u660e\u767d\uff0c\u7cbe\u660e\u4eba\u4e3a\u4ec0\u4e48\u603b\u662f\u6b7b\u5f97\u7279\u522b\u65e9\u4e86\u3002\u201d \u3000\u3000\u7ae5\u94dc\u5c71\u5ffd\u7136\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4f46\u6709\u79cd\u4eba\u4e00\u5b9a\u6b7b\u5f97\u6bd4\u7cbe\u660e\u4eba\u8fd8\u65e9\u3002\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u54ea\u79cd\u4eba\uff1f\u201d \u3000\u3000\u7ae5\u94dc\u5c71\u9053\uff1a\u201c\u8ddf\u8001\u7237\u5b50\u4f5c\u5bf9\u7684\u4eba\u3002\u201d \u3000\u3000\u97e9\u8d1e\u4e5f\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u6240\u4ee5\u6211\u4e00\u76f4\u90fd\u5f88\u540c\u60c5\u8fd9\u79cd\u4eba\uff0c\u4ed6\u4eec\u8981\u6d3b\u7740\u5b9e\u5728\u4e0d\u5bb9\u6613\u3002\u201d \u3000\u3000\u51af\u516d\u6162\u6162\u5730\u8d70\u8fc7\u4e00\u6761\u79ef\u96ea\u7684\u5c0f\u5f84\uff0c\u8fdc\u8fdc\u770b\u8fc7\u53bb\uff0c\u5df2\u770b\u89c1\u51b7\u9999\u56ed\u4e2d\u90a3\u7247\u707f\u70c2\u5982\u706b\u7130\u7684\u6885 \u82b1\u3002 \u3000\u3000\u201c\u53bb\u5c06\u51b7\u9999\u56ed\u5305\u4e0b\u6765\uff0c\u628a\u672c\u6765\u4f4f\u5728\u90a3\u91cc\u7684\u5ba2\u4eba\u8d76\u51fa\u53bb\uff0c\u65e0\u8bba\u662f\u6d3b\u7740\u7684\uff0c\u8fd8\u662f\u6b7b\u7684\uff0c\u5168\u90fd \u8d76\u51fa\u53bb\u3002\u201d \u3000\u3000\u8fd9\u662f\u536b\u516b\u592a\u7237\u7684\u547d\u4ee4\uff0c\u4e5f\u662f\u536b\u516b\u592a\u7237\u53d1\u4ee4\u7684\u5178\u578b\u65b9\u6cd5\u3002 \u3000\u3000\u4ed6\u53ea\u6d3e\u4f60\u53bb\u505a\u4e00\u4ef6\u4e8b\uff0c\u800c\u4e14\u8981\u4f60\u975e\u6210\u529f\u4e0d\u53ef\u3002 \u3000\u3000\u81f3\u4e8e\u4f60\u600e\u6837\u53bb\u505a\uff0c\u4ed6\u5c31\u5b8c\u5168\u4e0d\u7ba1\u4e86\uff0c\u8fd9\u4ef6\u4e8b\u6709\u591a\u5c11\u56f0\u96be\uff0c\u4ed6\u66f4\u4e0d\u7ba1\u3002 \u3000\u3000\u6240\u6709\u7684\u56f0\u96be\uff0c\u90fd\u8981\u4f60\u81ea\u5df1\u53bb\u514b\u670d\uff0c\u82e5\u4f60\u4e0d\u80fd\u514b\u670d\uff0c\u5c31\u6839\u672c\u4e0d\u914d\u505a\u536b\u516b\u592a\u7237\u95e8\u4e0b\u7684\u5f1f\u5b50\u3002 \u3000\u3000\u51af\u516d\u662f\u53d7\u547d\u800c\u6765\u7684\u3002 \u3000\u3000\u4ed6\u4e00\u5411\u662f\u4e2a\u8c28\u614e\u7684\u4eba\uff0c\u975e\u5e38\u8c28\u614e\u3002 \u3000\u3000\u4ed6\u5df2\u5c06\u6240\u6709\u53ef\u80fd\u53d1\u751f\u7684\u56f0\u96be\uff0c\u5168\u90fd\u4ed4\u7ec6\u5730\u60f3\u8fc7\u4e00\u904d\u3002 \u3000\u3000\u7a7f\u8fc7\u8fd9\u6761\u79ef\u96ea\u7684\u5c0f\u5f84\uff0c\u5c31\u662f\u51b7\u9999\u56ed\u7684\u95e8\u623f\uff0c\u5f53\u503c\u7684\u7ba1\u4e8b\uff0c\u901a\u5e38\u90fd\u5728\u95e8\u623f\u91cc\uff0c\u4ed6\u5e0c\u671b\u8fd9\u7ba1 \u4e8b\u662f\u4e2a\u806a\u660e\u4eba\u3002 \u3000\u3000\u806a\u660e\u4eba\u90fd\u77e5\u9053\uff0c\u536b\u516b\u592a\u7237\u7684\u8981\u6c42\u662f\u7edd\u4e0d\u5bb9\u62d2\u7edd\u7684\u3002 \u3000\u3000\u51b7\u9999\u56ed\u4eca\u5929\u5f53\u503c\u7684\u7ba1\u4e8b\u662f\u4e09\u5341\u591a\u5c81\u7684\u4e2d\u5e74\u4eba\uff0c\u4ed6\u770b\u6765\u867d\u4e0d\u592a\u806a\u660e\uff0c\u5374\u4e5f\u4e0d\u7b28\u3002 \u3000\u3000\u201c\u5728\u4e0b\u6768\u8f69\uff0c\u516c\u5b50\u65e0\u8bba\u662f\u6765\u8d4f\u82b1\u996e\u9152\uff0c\u8fd8\u662f\u60f3\u5728\u8fd9\u91cc\u6d41\u8fde\u51e0\u5929\uff0c\u90fd\u53ea\u7ba1\u5429\u5490\u3002\u201d \u3000\u3000\u51af\u516d\u7684\u56de\u7b54\u76f4\u63a5\u800c\u7b80\u77ed\uff1a\u201c\u6211\u4eec\u8981\u5c06\u8fd9\u91cc\u5168\u90fd\u5305\u4e0b\u6765\u3002\u201d \u3000\u3000\u6768\u8f69\u663e\u5f97\u5f88\u610f\u5916\uff0c\u5374\u8fd8\u662f\u5fae\u7b11\u7740\u9053\uff1a\u201c\u8fd9\u91cc\u4e00\u5171\u6709\u4e8c\u5341\u4e00\u4e2a\u9662\u5b50\uff0c\u5341\u56db\u5ea7\u697c\uff0c\u4e03\u95f4\u5927 \u5385\uff0c\u4e8c\u5341\u516b\u95f4\u82b1\u5385\uff0c\u4e24\u767e\u591a\u95f4\u5ba2\u623f\uff0c\u516c\u5b50\u8981\u5168\u5305\u4e0b\u6765\uff1f\u201d \u3000\u3000\u51af\u516d\u9053\uff1a\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u6768\u8f69\u6c89\u541f\u7740\uff1a\u201c\u516c\u5b50\u4e00\u5171\u8981\u6765\u591a\u5c11\u4eba\uff1f\u201d \u3000\u3000\u51af\u516d\u9053\uff1a\u201c\u5c31\u7b97\u53ea\u6765\u4e00\u4e2a\u4eba\uff0c\u4e5f\u8981\u5168\u5305\u4e0b\u6765\u3002\u201d \u3000\u3000\u6768\u8f69\u6c89\u4e0b\u4e86\u8138\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u90a3\u5c31\u5f97\u770b\u6765\u7684\u662f\u4ec0\u4e48\u4eba\u4e86\u3002\u201d \u3000\u3000\u51af\u516d\u9053\uff1a\u201c\u662f\u536b\u516b\u592a\u7237\u3002\u201d \u3000\u3000\u6768\u8f69\u52a8\u5bb9\u9053\uff1a\u201c\u536b\u516b\u592a\u7237\uff0c\u4fdd\u5b9a\u5e9c\u7684\u536b\u516b\u592a\u7237\uff1f\u201d \u3000\u3000\u51af\u516d\u70b9\u70b9\u5934\uff0c\u5fc3\u91cc\u89c9\u5f97\u5f88\u6ee1\u610f\uff0c\u536b\u516b\u592a\u7237\u7684\u540d\u5934\uff0c\u6bd5\u7adf\u662f\u5f88\u5c11\u6709\u4eba\u4e0d\u77e5\u9053\u7684\u3002 \u3000\u3000\u6768\u8f69\u770b\u7740\u4ed6\uff0c\u773c\u775b\u91cc\u5ffd\u7136\u9732\u51fa\u79cd\u72e1\u733e\u7684\u7b11\u610f\uff0c\u8bf4\u9053\uff1a\u201c\u536b\u516b\u592a\u7237\u7684\u5429\u5490\uff0c\u5728\u4e0b\u672c\u6765\u4e0d\u6562 \u8fdd\u80cc\u7684\uff0c\u53ea\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u51af\u516d\u9053\uff1a\u201c\u4e0d\u8fc7\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u6768\u8f69\u9053\uff1a\u201c\u521a\u624d\u4e5f\u6709\u4f4d\u5ba2\u5b98\u8981\u5c06\u8fd9\u5730\u65b9\u5305\u4e0b\u6765\uff0c\u800c\u4e14\u51fa\u4e86\u4e00\u5343\u4e24\u94f6\u5b50\u4e00\u5929\u7684\u9ad8\u4ef7\uff0c\u5728\u4e0b \u8fd8\u6ca1\u6709\u7b54\u5e94\uff0c\u73b0\u5728\u82e5\u662f\u7b54\u5e94\u4e86\u516c\u5b50\uff0c\u600e\u4e48\u53bb\u5411\u90a3\u4f4d\u5ba2\u5b98\u4ea4\u5f85\uff1f\u201d \u3000\u3000\u51af\u516d\u76b1\u4e86\u76b1\u7709\u5934\uff0c\u9053\uff1a\u201c\u90a3\u4e2a\u4eba\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u6768\u8f69\u6ca1\u6709\u56de\u7b54\uff0c\u76ee\u5149\u5374\u4ece\u4ed6\u80a9\u5934\u4e0a\u770b\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u51af\u516d\u56de\u8fc7\u8eab\uff0c\u5c31\u770b\u89c1\u4e86\u4e00\u5f20\u9752\u4e2d\u900f\u767d\u3001\u5b8c\u5168\u6ca1\u6709\u8868\u60c5\u7684\u8138\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u5c31\u7ad9\u5728\u4ed6\u8eab\u540e\u7684\u5c4b\u89d2\u91cc\uff0c\u8eab\u4e0a\u7a7f\u7740\u4ef6\u5f88\u5355\u8584\u7684\u767d\u9ebb\u8863\u886b\uff0c\u80cc\u540e\u80cc\u7740\u5377\u5e2d\uff0c\u624b\u91cc\u63d0 \u7740\u6839\u77ed\u6756\u3002 \u3000\u3000\u51af\u516d\u521a\u624d\u8fdb\u6765\u65f6\uff0c\u5e76\u6ca1\u6709\u770b\u89c1\u8fd9\u4e2a\u4eba\uff0c\u73b0\u5728\u8fd9\u4e2a\u4eba\u597d\u50cf\u4e5f\u6ca1\u6709\u770b\u89c1\u4ed6\uff0c\u4e00\u53cc\u51b0\u51b7\u51b7\u3001\u5b8c \u5168\u6ca1\u6709\u8868\u60c5\u7684\u773c\u775b\uff0c\u4eff\u4f5b\u6b63\u5728\u51dd\u89c6\u7740\u8fdc\u65b9\u3002 \u3000\u3000\u8fd9\u4e16\u4e0a\u6240\u6709\u7684\u4e00\u5207\u4eba\uff0c\u4e00\u5207\u4e8b\uff0c\u597d\u50cf\u90fd\u6ca1\u6709\u88ab\u4ed6\u770b\u5728\u773c\u91cc\uff0c\u4ed6\u5173\u5fc3\u7684\u4eff\u4f5b\u53ea\u662f\u8fdc\u65b9\u865a\u65e0 \u7f25\u7f08\u5904\u4e00\u4e2a\u865a\u65e0\u7f25\u7f08\u7684\u5730\u65b9\u3002\u53ea\u6709\u5728\u90a3\u91cc\uff0c\u4ed6\u624d\u80fd\u83b7\u5f97\u771f\u6b63\u7684\u5e73\u9759\u4e0e\u5b89\u4e50\u3002 \u3000\u3000\u51af\u516d\u53ea\u770b\u4e86\u4e00\u773c\uff0c\u5c31\u8f6c\u56de\u8eab\uff0c\u4ed6\u5df2\u77e5\u9053\u8fd9\u4e2a\u4eba\u662f\u8c01\u4e86\uff0c\u5e76\u4e0d\u60f3\u770b\u5f97\u592a\u4ed4\u7ec6\uff0c\u66f4\u4e0d\u60f3\u8ddf\u8fd9 \u4e2a\u4eba\u8bf4\u8bdd\uff0c\u4ed6\u77e5\u9053\u65e0\u8bba\u540c\u8fd9\u4e2a\u4eba\u8bf4\u4ec0\u4e48\uff0c\u90fd\u662f\u4ef6\u975e\u5e38\u611a\u8822\u7684\u4e8b\u3002 \u3000\u3000\u6768\u8f69\u7684\u773c\u775b\u91cc\uff0c\u8fd8\u5e26\u7740\u90a3\u79cd\u72e1\u733e\u7684\u7b11\u610f\u3002 \u3000\u3000\u51af\u516d\u5fae\u7b11\u9053\uff1a\u201c\u4f60\u662f\u505a\u751f\u610f\u7684\uff1f\u201d \u3000\u3000\u6768\u8f69\u9053\uff1a\u201c\u5728\u4e0b\u672c\u5c31\u662f\u4e2a\u751f\u610f\u4eba\u3002\u201d \u3000\u3000\u51af\u516d\u9053\uff1a\u201c\u505a\u751f\u610f\u662f\u4e3a\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6768\u8f69\u7b11\u9053\uff1a\u201c\u5f53\u7136\u662f\u4e3a\u4e86\u8d5a\u94b1\u3002\u201d \u3000\u3000\u51af\u516d\u9053\uff1a\u201c\u597d\uff0c\u6211\u51fa\u4e00\u5343\u4e94\u767e\u4e24\u94f6\u5b50\u4e00\u5929\uff0c\u518d\u7ed9\u4f60\u4e00\u5343\u4e24\u56de\u6263\u3002\u201d \u3000\u3000\u4ed6\u77e5\u9053\u548c\u751f\u610f\u4eba\u8c08\u4ea4\u6613\uff0c\u8fdc\u6bd4\u548c\u4e00\u4e2a\u4e0d\u8981\u547d\u7684\u4eba\u8c08\u4ea4\u6613\u5bb9\u6613\u5f97\u591a\u3002\u5728\u536b\u516b\u592a\u7237\u624b\u4e0b\u591a \u5e74\uff0c\u4ed6\u5df2\u5b66\u4f1a\u5982\u4f55\u505a\u51fa\u6b63\u786e\u7684\u5224\u65ad\u548c\u9009\u62e9\u3002 \u3000\u3000\u6768\u8f69\u663e\u7136\u5df2\u88ab\u6253\u52a8\u4e86\uff0c\u5374\u542c\u90a3\u767d\u8863\u4eba\u51b7\u51b7\u9053\uff1a\u201c\u6211\u51fa\u4e00\u5343\u4e94\u767e\u4e24\uff0c\u518d\u52a0\u8fd9\u4e2a\u3002\u201d \u3000\u3000\u51af\u516d\u53ea\u89c9\u5f97\u8eab\u540e\u7a81\u7136\u6709\u51b7\u68ee\u68ee\u7684\u5200\u98ce\u63a0\u8fc7\uff0c\u5fcd\u4e0d\u4f4f\u56de\u5934\u3002 \u3000\u3000\u767d\u8863\u4eba\u5df2\u4ece\u77ed\u6756\u91cc\u62bd\u51fa\u67c4\u8584\u5200\uff0c\u53cd\u624b\u4e00\u5200\uff0c\u7adf\u5728\u817f\u80a1\u95f4\u524a\u4e0b\u4e00\u7247\u8840\u6dcb\u6dcb\u7684\u8089\uff0c\u6162\u6162\u5730\u653e \u5728\u684c\u4e0a\uff0c\u8138\u4e0a\u8fd8\u662f\u5168\u65e0\u8868\u60c5\uff0c\u7ade\u4f3c\u5b8c\u5168\u4e0d\u89c9\u5f97\u75db\u82e6\u3002 \u3000\u3000\u51af\u516d\u770b\u7740\u4ed6\uff0c\u5df2\u53ef\u611f\u89c9\u5230\u773c\u89d2\u5728\u4e0d\u505c\u5730\u8df3\uff0c\u8fc7\u4e86\u5f88\u4e45\uff0c\u624d\u7f13\u7f13\u9053\uff1a\u201c\u8fd9\u4ef7\u94b1\u6211\u4e5f\u51fa\u5f97 \u8d77\u3002\u201d \u3000\u3000\u767d\u8863\u4eba\u4e00\u53cc\u51b7\u6f20\u7a7a\u6d1e\u7684\u773c\u775b\uff0c\u53ea\u770b\u4e86\u4ed6\u4e00\u773c\uff0c\u53c8\u51dd\u89c6\u7740\u8fdc\u65b9\u3002 \u3000\u3000\u51af\u516d\u6162\u6162\u5730\u62bd\u51fa\u67c4\u77ed\u5200\uff0c\u4e5f\u5728\u81ea\u5df1\u80a1\u95f4\u5272\u4e0b\u4e86\u4e00\u7247\u3002\u4ed6\u5272\u5f97\u5f88\u6162\uff0c\u5f88\u4ed4\u7ec6\uff0c\u4ed6\u65e0\u8bba\u505a\u4ec0 \u4e48\u4e8b\uff0c\u90fd\u4e00\u5411\u5f88\u4ed4\u7ec6\uff0c\u8089\u5272\u4e0b\u867d\u7136\u5f88\u75db\u82e6\uff0c\u4f46\u536b\u516b\u592a\u7237\u7684\u547d\u4ee4\u82e5\u65e0\u6cd5\u5b8c\u6210\uff0c\u5c31\u4e00\u5b9a\u4f1a\u66f4\u75db \u82e6\u3002\u8fd9\u4e00\u6b21\u4ed6\u7684\u5224\u65ad\u548c\u9009\u62e9\u4e5f\u540c\u6837\u6b63\u786e\uff0c\u4e5f\u8bb8\u4ed6\u6839\u672c\u5c31\u6ca1\u6709\u4ec0\u4e48\u9009\u62e9\u7684\u4f59\u5730\u3002 \u3000\u3000\u4e24\u7247\u8840\u6dcb\u6dcb\u7684\u8089\u653e\u5728\u684c\u4e0a\uff0c\u6768\u8f69\u5df2\u7ecf\u8f6f\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u767d\u8863\u4eba\u53c8\u770b\u4e86\u51af\u516d\u4e00\u773c\uff0c\u7a81\u7136\u6325\u5200\uff0c\u5272\u4e0b\u4e86\u81ea\u5df1\u7684\u4e00\u53ea\u8033\u6735\u3002 \u3000\u3000\u51af\u516d\u53ea\u89c9\u5f97\u81ea\u5df1\u7684\u624b\u81c2\u5df2\u50f5\u786c\u3002\u4ed6\u5272\u8fc7\u522b\u4eba\u7684\u8033\u6735\uff0c\u5f53\u65f6\u53ea\u89c9\u5f97\u6709\u79cd\u6b8b\u9177\u7684\u5feb\u610f\uff0c\u4f46\u5272 \u81ea\u5df1\u7684\u8033\u6735\u5374\u662f\u53e6\u5916\u4e00\u56de\u4e8b\u4e86\uff0c\u4ed6\u672c\u53ef\u6325\u5200\u6740\u4e86\u8fd9\u767d\u8863\u4eba\uff0c\u53ef\u662f\u97e9\u8d1e\u7684\u8bdd\u4ed6\u4e5f\u6ca1\u6709\u5fd8\u8bb0\u3002 \u3000\u3000\u2014\u2014\u4f60\u51fa\u624b\u7eb5\u7136\u6bd4\u4ed6\u5feb\uff0c\u4f46\u4f60\u6740\u4ed6\u65f6\uff0c\u4ed6\u8fd8\u662f\u53ef\u4ee5\u6740\u4e86\u4f60\u3002 \u3000\u3000\u8c28\u614e\u7684\u4eba\uff0c\u5927\u591a\u6570\u90fd\u73cd\u60dc\u81ea\u5df1\u7684\u6027\u547d\u3002\u51af\u516d\u662f\u4e2a\u8c28\u614e\u7684\u4eba\uff0c\u4ed6\u6162\u6162\u5730\u62ac\u8d77\u5934\uff0c\u5272\u4e0b\u4e86\u81ea \u5df1\u7684\u8033\u6735\uff0c\u5272\u5f97\u66f4\u6162\uff0c\u66f4\u4ed4\u7ec6\u3002 \u3000\u3000\u767d\u8863\u4eba\u7684\u80a9\u4e0a\u5df2\u88ab\u4ed6\u81ea\u5df1\u7684\u9c9c\u8840\u67d3\u7ea2\uff0c\u4e00\u53cc\u51b7\u6f20\u7a7a\u6d1e\u7684\u773c\u775b\u91cc\uff0c\u7ade\u5ffd\u7136\u9732\u51fa\u6b8b\u9177\u5feb\u610f\u7684 \u8868\u60c5\uff0c\u51af\u516d\u7684\u8fd9\u53ea\u8033\u6735\uff0c\u5c31\u597d\u50cf\u662f\u4ed6\u5272\u4e0b\u6765\u7684\u4e00\u6837\u3002 \u3000\u3000\u4e24\u53ea\u8840\u6dcb\u6dcb\u7684\u8033\u6735\u653e\u5728\u684c\u4e0a\uff0c\u6768\u8f69\u4f3c\u4e4e\u8fde\u7ad9\u90fd\u7ad9\u4e0d\u4f4f\u4e86\u3002 \u3000\u3000\u767d\u8863\u4eba\u671b\u671b\u51af\u516d\u8033\u7554\u6d41\u4e0b\u7684\u9c9c\u8840\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u8fd9\u4ef7\u94b1\u4f60\u4e5f\u51fa\u5f97\u8d77\uff1f\u201d \u3000\u3000\u4ed6\u7a81\u7136\u6325\u5200\uff0c\u5411\u81ea\u5df1\u5de6\u8155\u4e0a\u780d\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u51af\u516d\u7684\u5fc3\u4e5f\u5df2\u968f\u4ed6\u8fd9\u4e00\u5200\u6c89\u4e0b\u3002\u5c31\u5728\u8fd9\u65f6\uff0c\u4ed6\u5ffd\u7136\u611f\u89c9\u5230\u4e00\u9635\u98ce\u5439\u8fc7\uff0c\u98ce\u4e2d\u4eff\u4f5b\u5e26\u7740\u79cd \u5947\u5f02\u7684\u9999\u6c14\u3002\u7136\u540e\u4ed6\u5c31\u770b\u89c1\u4e86\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u4e00\u4e2a\u5973\u4eba\u3002 \u3000\u3000\u4e00\u773c\u770b\u8fc7\u53bb\uff0c\u51af\u516d\u53ea\u89c9\u5f97\u81ea\u5df1\u4ece\u6765\u4e5f\u6ca1\u770b\u5230\u8fc7\u8fd9\u4e48\u7f8e\u4e3d\u7684\u5973\u4eba\uff0c\u5979\u5c31\u50cf\u662f\u88ab\u8fd9\u9635\u98ce\u5439\u8fdb \u6765\u7684\u3002 \u3000\u3000\u767d\u8863\u4eba\u770b\u89c1\u5979\u65f6\uff0c\u7acb\u523b\u5c31\u53d1\u89c9\u81ea\u5df1\u63e1\u5200\u7684\u624b\u5df2\u88ab\u5979\u6258\u7740\u3002 \u3000\u3000\u5979\u4e5f\u6b63\u5728\u5fae\u7b11\u7740\uff0c\u770b\u7740\u4ed6\uff0c\u591a\u4e48\u6e29\u67d4\u800c\u751c\u871c\uff0c\u8bf4\u8bdd\u7684\u58f0\u97f3\u540c\u6837\u751c\u871c\uff0c\u201c\u5200\u780d\u5728\u8089\u4e0a\uff0c\u662f \u4f1a\u75bc\u7684\u3002\u201d \u3000\u3000\u767d\u8863\u4eba\u51b7\u51b7\u9053\uff1a\u201c\u8fd9\u4e0d\u662f\u4f60\u7684\u8089\u3002\u201d \u3000\u3000\u8fd9\u7f8e\u4e3d\u7684\u5973\u4eba\u67d4\u58f0\u9053\uff1a\u201c\u867d\u7136\u4e0d\u662f\u6211\u7684\u8089\uff0c\u6211\u4e5f\u4e00\u6837\u4f1a\u5fc3\u75bc\u3002\u201d \u3000\u3000\u5979\u6625\u7b0b\u822c\u7684\u7ea4\u7ea4\u624b\u6307\u8f7b\u8f7b\u4e00\u6307\uff0c\u5c31\u597d\u50cf\u5728\u4e3a\u4ed6\u7684\u60c5\u4eba\u4ece\u74f6\u4e2d\u6458\u4e0b\u4e00\u6735\u9c9c\u82b1\u3002 \u3000\u3000\u767d\u8863\u4eba\u5c31\u53d1\u89c9\u81ea\u5df1\u624b\u91cc\u7684\u5200\uff0c\u5ffd\u7136\u5df2\u5230\u4e86\u5979\u7684\u624b\u91cc\u3002 \u3000\u3000\u767e\u70bc\u7cbe\u94a2\u7684\u5feb\u5200\uff0c\u8584\u800c\u950b\u5229\u3002 \u3000\u3000\u5979\u5341\u6307\u7ea4\u7ea4\uff0c\u8f7b\u8f7b\u4e00\u62d7\uff0c\u53c8\u4eff\u4f5b\u5728\u62d7\u65ad\u82b1\u679d\uff0c\u53ea\u542c\u201c\u5494\u201d\u7684\u4e00\u58f0\uff0c\u8fd9\u67c4\u767e\u70bc\u7cbe\u94a2\u7684\u5feb \u5200\uff0c\u7adf\u5df2\u88ab\u5979\u62d7\u65ad\u4e86\u4e00\u622a\u3002 \u3000\u3000\u201c\u4f55\u51b5\uff0c\u8fd9\u5730\u65b9\u6211\u65e9\u5df2\u5305\u4e0b\u6765\u4e86\uff0c\u4f60\u4eec\u53c8\u4f55\u5fc5\u4e89\u6765\u4e89\u53bb\uff1f\u201d \u3000\u3000\u5979\u5634\u91cc\u8bf4\u7740\u8bdd\uff0c\u7adf\u5c06\u62d7\u65ad\u7684\u90a3\u4e00\u622a\u94a2\u5200\uff0c\u7528\u4e24\u6839\u624b\u6307\u5939\u8d77\uff0c\u653e\u5728\u5634\u91cc\uff0c\u6162\u6162\u5730\u541e\u4e86\u4e0b \u53bb\u3002\u7136\u540e\u5979\u7f8e\u4e3d\u7684\u8138\u4e0a\u5c31\u9732\u51fa\u79cd\u6ee1\u610f\u7684\u8868\u60c5\uff0c\u50cf\u662f\u521a\u541e\u4e0b\u4e00\u9897\u7f8e\u5473\u7684\u7cd6\u679c\u4e00\u6837\u3002 \u3000\u3000\u51af\u516d\u6014\u4f4f\uff0c\u4ed6\u51e0\u4e4e\u4e0d\u80fd\u76f8\u4fe1\u81ea\u5df1\u7684\u773c\u775b\uff0c\u751a\u81f3\u8fde\u767d\u8863\u4eba\u7684\u773c\u775b\u91cc\u4e5f\u4e0d\u7981\u9732\u51fa\u60ca\u5413\u4e4b\u8272\u3002 \u3000\u3000\u4e16\u4e0a\u600e\u4e48\u53ef\u80fd\u6709\u8fd9\u4e48\u5947\u602a\u7684\u4e8b\u3001\u8fd9\u4e48\u53ef\u6015\u7684\u6b66\u529f\uff1f\u5979\u96be\u9053\u5c31\u4e0d\u6015\u5200\u950b\u5272\u70c2\u5979\u7684\u80a0\u80c3\uff1f \u3000\u3000\u8fd9\u7f8e\u4e3d\u7684\u5973\u4eba\u5374\u53c8\u5c06\u94a2\u5200\u62d7\u4e0b\u4e00\u5757\uff0c\u541e\u4e86\u4e0b\u53bb\uff0c\u8f7b\u8f7b\u53f9\u4e86\u53e3\u6c14\uff0c\u5fae\u7b11\u7740\u9053\uff1a\u201c\u8fd9\u628a\u5200\u5012 \u771f\u4e0d\u9519\uff0c\u975e\u4f46\u94a2\u6027\u5f88\u597d\uff0c\u70bc\u5f97\u4e5f\u5f88\u7eaf\uff0c\u6bd4\u6211\u6628\u5929\u5403\u7684\u90a3\u628a\u5200\u6ecb\u5473\u597d\u591a\u4e86\u3002\u201d \u3000\u3000\u51af\u516d\u5fcd\u4e0d\u4f4f\u9053\uff1a\u201c\u4f60\u5929\u5929\u5403\u5200\uff1f\u201d \u3000\u3000\u8fd9\u7f8e\u4e3d\u7684\u5973\u4eba\u9053\uff1a\u201c\u5403\u5f97\u5e76\u4e0d\u591a\uff0c\u6bcf\u5929\u53ea\u5403\u4e09\u67c4\uff0c\u5200\u5251\u4e5f\u540c\u732a\u8089\u4e00\u6837\uff0c\u82e5\u662f\u5403\u5f97\u5927\u591a \u4e86\uff0c\u80a0\u80c3\u4f1a\u4e0d\u8212\u670d\u7684\u3002\u201d \u3000\u3000\u51af\u516d\u76f4\u7740\u773c\u775b\u770b\u7740\u5979\u3002\u4ed6\u5f88\u5c11\u5728\u7f8e\u4e3d\u7684\u5973\u4eba\u9762\u524d\u5931\u6001\uff0c\u4f46\u73b0\u5728\u4ed6\u5df2\u5b8c\u5168\u6ca1\u6cd5\u5b50\u63a7\u5236\u81ea \u5df1\u3002 \u3000\u3000\u8fd9\u7f8e\u4e3d\u7684\u5973\u4eba\u770b\u7740\u4ed6\uff0c\u53c8\u9053\uff1a\u201c\u50cf\u4f60\u624b\u91cc\u8fd9\u628a\u5200\uff0c\u5c31\u4e0d\u592a\u597d\u5403\u4e86\u3002\u201d \u3000\u3000\u51af\u516d\u53c8\u5fcd\u4e0d\u4f4f\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5979\u7b11\u4e86\u7b11\uff0c\u6de1\u6de1\u9053\uff1a\u201c\u4f60\u8fd9\u628a\u5200\u4ee5\u524d\u6740\u7684\u4eba\u5927\u591a\u4e86\uff0c\u8840\u8165\u5473\u592a\u91cd\u3002\u201d \u3000\u3000\u767d\u8863\u4eba\u770b\u7740\u5979\uff0c\u7a81\u7136\u8f6c\u8fc7\u5934\uff0c\u5927\u6b65\u8d70\u4e86\u51fa\u53bb\u3002\u4ed6\u4e0d\u6015\u6b7b\uff0c\u53ef\u8981\u4ed6\u5c06\u4e00\u67c4\u94a2\u5200\u62d7\u6210\u4e00\u5757\u5757 \u541e\u4e0b\u53bb\uff0c\u4ed6\u6839\u672c\u5c31\u505a\u4e0d\u5230\u3002\u6ca1\u6709\u4eba\u80fd\u505a\u5f97\u5230\uff0c\u8fd9\u6839\u672c\u5c31\u662f\u4ef6\u4e0d\u53ef\u601d\u8bae\u7684\u4e8b\u3002 \u3000\u3000\u5979\u53c8\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u770b\u6765\u4ed6\u5df2\u4e0d\u60f3\u8ddf\u6211\u4e89\u4e86\uff0c\u4f60\u5462\uff1f\u201d \u3000\u3000\u51af\u516d\u4e0d\u5f00\u53e3\uff0c\u4ed6\u6839\u672c\u65e0\u6cd5\u5f00\u53e3\u3002 \u3000\u3000\u8fd9\u7f8e\u4e3d\u7684\u5973\u4eba\u53c8\u9053\uff1a\u201c\u7537\u5b50\u6c49\u5927\u4e08\u592b\uff0c\u65e0\u8bba\u8ddf\u5973\u4eba\u4e89\u4ec0\u4e48\uff0c\u5c31\u7b97\u4e89\u8d62\u4e86\uff0c\u4e5f\u4e0d\u662f\u4ef6\u5149\u8363 \u7684\u4e8b\uff0c\u4f60\u8bf4\u5bf9\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u51af\u516d\u7ec8\u4e8e\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8bf7\u6559\u5c0a\u59d3\u5927\u540d\uff0c\u5728\u4e0b\u56de\u53bb\u4e5f\u597d\u4ea4\u6301\u3002\u201d \u3000\u3000\u5979\u4e5f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u53ea\u4e0d\u8fc7\u662f\u4e2a\u4e2b\u5934\uff0c\u4f60\u95ee\u51fa\u6211\u7684\u540d\u5b57\uff0c\u4e5f\u6ca1\u6709\u7528\u3002\u201d \u3000\u3000\u8fd9\u4e2a\u98ce\u534e\u7edd\u4ee3\u3001\u7f8e\u8273\u7167\u4eba\uff0c\u6b66\u529f\u66f4\u6df1\u4e0d\u53ef\u6d4b\u7684\u5973\u4eba\uff0c\u7adf\u53ea\u4e0d\u8fc7\u662f\u4e2a\u4e2b\u5934\u3002 \u3000\u3000\u5979\u7684\u4e3b\u4eba\u53c8\u662f\u4e2a\u4ec0\u4e48\u6837\u7684\u4eba\u7269\uff1f \u3000\u3000\u201c\u4f60\u4e0d\u59a8\u56de\u53bb\u8f6c\u544a\u536b\u516b\u592a\u7237\uff0c\u5c31\u8bf4\u8fd9\u5730\u65b9\u5df2\u88ab\u5357\u6d77\u5a18\u5b50\u5305\u4e0b\u6765\u4e86\uff0c\u4ed6\u8001\u4eba\u5bb6\u82e5\u662f\u6709\u7a7a\uff0c \u968f\u65f6\u90fd\u53ef\u4ee5\u8bf7\u8fc7\u6765\u73a9\u51e0\u5929\u3002\u201d \u3000\u3000\u51af\u516d\u9053\uff1a\u201c\u5357\u6d77\u5a18\u5b50\uff1f\u201d \u3000\u3000\u8fd9\u7f8e\u4e3d\u7684\u5973\u4eba\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u5357\u6d77\u5a18\u5b50\u5c31\u662f\u6211\u7684\u4e3b\u4eba\uff0c\u201d\u56de\u53bb\u544a\u8bc9\u536b\u516b\u592a\u7237\uff0c\u4ed6\u4e00\u5b9a\u77e5 \u9053\u7684\u3002\u201d \u7b2c\u4e8c\u7ae0\u3000\u5357\u6d77\u5a18\u5b50>> \u53e4\u9f99\u300a\u4e5d\u6708\u9e70\u98de\u300b \u7b2c\u4e8c\u7ae0\u3000\u5357\u6d77\u5a18\u5b50 \u3000\u3000\u536b\u516b\u592a\u7237\u6109\u5feb\u65f6\u548c\u6124\u6012\u65f6\uff0c\u82e5\u662f\u53d8\u4e3a\u4e0d\u540c\u7684\u4e24\u4e2a\u4eba\uff0c\u90a3\u4e48\u4ed6\u73b0\u5728\u7684\u6837\u5b50\uff0c\u5c31\u662f\u7b2c\u4e09\u4e2a\u4eba \u4e86\u3002\u4ece\u6765\u4e5f\u6ca1\u6709\u4eba\u770b\u89c1\u8fc7\u4ed6\u50cf\u73b0\u5728\u8fd9\u4e48\u6837\u7d27\u5f20\uff0c\u8fd9\u4e48\u6837\u60ca\u8bb6\uff0c\u751a\u81f3\u8fde\u4ed6\u90a3\u5f20\u603b\u662f\u7ea2\u5149\u6ee1\u9762\u7684 \u8138\uff0c\u73b0\u5728\u90fd\u5df2\u53d8\u6210\u4e86\u94c1\u9752\u8272\u3002 \u3000\u3000\u201c\u5357\u6d77\u5a18\u5b50\uff01\u96be\u9053\u5979\u771f\u8fd8\u6ca1\u6709\u6b7b\uff1f\u201d \u3000\u3000\u4ed6\u63e1\u7d27\u53cc\u62f3\uff0c\u58f0\u97f3\u91cc\u4e5f\u5145\u6ee1\u4e86\u7d27\u5f20\u548c\u60ca\u8bb6\uff0c\u751a\u81f3\u8fd8\u4eff\u4f5b\u5e26\u6709\u79cd\u8bf4\u4e0d\u51fa\u7684\u6050\u60e7\u3002 \u3000\u3000\u6ca1\u6709\u4eba\u6562\u51fa\u58f0\uff0c\u8c01\u4e5f\u60f3\u4e0d\u5230\u8fd9\u4e16\u4e0a\u5c45\u7136\u8fd8\u6709\u4f7f\u536b\u516b\u592a\u7237\u7d27\u5f20\u6050\u60e7\u7684\u4eba\u3002 \u3000\u3000\u536b\u5929\u9e4f\u7a81\u53c8\u77aa\u8d77\u773c\u775b\uff0c\u5927\u58f0\u9053\uff1a\u201c\u4f60\u4eec\u77e5\u4e0d\u77e5\u9053\u5357\u6d77\u5a18\u5b50\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u867d\u7136\u662f\u95ee\u5927\u5bb6\u7684\uff0c\u4f46\u773c\u775b\u5374\u8fd8\u662f\u76ef\u5728\u97e9\u8d1e\u4e00\u4e2a\u4eba\u8eab\u4e0a\uff0c\u4f46\u8fd9\u6b21\u5374\u8fde\u97e9\u8d1e\u4e5f\u6ca1\u6709\u5f00 \u53e3\u3002 \u3000\u3000\u536b\u5929\u9e4f\u5df2\u51b2\u8fc7\u6765\uff0c\u4e00\u628a\u63ea\u4f4f\u4ed6\u8863\u895f\uff0c\u5389\u58f0\u9053\uff1a\u201c\u4f60\u8fde\u5357\u6d77\u5a18\u5b50\u90fd\u4e0d\u77e5\u9053\uff0c\u4f60\u8fd8\u77e5\u9053\u4ec0 \u4e48\uff1f\u201d \u3000\u3000\u97e9\u8d1e\u7684\u8138\u5ffd\u7136\u4e5f\u53d8\u5f97\u50cf\u662f\u90a3\u4e9b\u767d\u8863\u4eba\u4e00\u6837\uff0c\u5b8c\u5168\u6ca1\u6709\u8868\u60c5\uff0c\u4e00\u53cc\u773c\u775b\u4e5f\u4eff\u4f5b\u5728\u51dd\u89c6\u7740\u8fdc \u65b9\u3002 \u3000\u3000\u536b\u5929\u9e4f\u77aa\u7740\u4ed6\uff0c\u8138\u4e0a\u7684\u6012\u5bb9\u4f3c\u5728\u6e10\u6e10\u9000\u4e86\uff0c\u6293\u4f4f\u4ed6\u8863\u895f\u7684\u624b\u4e5f\u6e10\u6e10\u677e\u5f00\uff0c\u5ffd\u7136\u957f\u957f\u53f9\u4e86 \u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8fd9\u4e5f\u4e0d\u80fd\u602a\u4f60\uff0c\u4f60\u5e74\u7eaa\u8fd8\u8f7b\uff0c\u5357\u6d77\u5a18\u5b50\u98a0\u5012\u4f17\u751f\u3001\u7eb5\u6a2a\u5929\u4e0b\u65f6\uff0c\u4f60\u53ea\u6015\u8fd8\u6ca1\u6709 \u751f\u51fa\u6765\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u53c8\u633a\u8d77\u80f8\uff0c\u5927\u58f0\u9053\uff1a\u201c\u4f46\u6211\u5374\u89c1\u8fc7\u5979\uff0c\u666e\u5929\u4e4b\u4e0b\uff0c\u4eb2\u773c\u89c1\u8fc7\u5979\u771f\u9762\u76ee\u7684\uff0c\u9664\u4e86\u6211\u536b \u5929\u9e4f\u4e4b\u5916\uff0c\u7edd\u4e0d\u4f1a\u518d\u6709\u7b2c\u4e8c\u4e2a\u4eba\u3002\u201d \u3000\u3000\u4ed6\u7684\u8138\u4e0a\u53c8\u5f00\u59cb\u53d1\u51fa\u4e86\u7ea2\u5149\uff0c\u80fd\u4eb2\u773c\u89c1\u5230\u5357\u6d77\u5a18\u5b50\u7684\u771f\u9762\u76ee\uff0c\u7adf\u597d\u50cf\u662f\u4ef6\u975e\u5e38\u503c\u5f97\u9a84\u505a \u7684\u4e8b\u3002 \u3000\u3000\u6bcf\u4e2a\u4eba\u5fc3\u91cc\u90fd\u60f3\u95ee\u3002 \u3000\u3000\u201c\u8fd9\u5357\u6d77\u5a18\u5b50\u7a76\u7adf\u662f\u4ec0\u4e48\u4eba\uff0c\u957f\u5f97\u7a76\u7adf\u662f\u4ec0\u4e48\u6837\u5b50\uff1f\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u5f53\u7136\u5e76\u6ca1\u6709\u4eba\u6562\u771f\u7684\u95ee\u51fa\u6765\uff0c\u5728\u536b\u516b\u592a\u7237\u9762\u524d\uff0c\u65e0\u8bba\u4efb\u4f55\u4eba\u90fd\u53ea\u80fd\u56de\u7b54\uff0c\u4e0d\u80fd\u53d1 \u95ee\uff0c\u536b\u516b\u592a\u7237\u4e00\u5411\u4e0d\u559c\u6b22\u591a\u5634\u7684\u4eba\u3002 \u3000\u3000\u4e16\u4e0a\u53c8\u6709\u8c01\u559c\u6b22\u591a\u5634\u7684\u4eba\u3002 \u3000\u3000\u536b\u5929\u9e4f\u7a81\u53c8\u5927\u58f0\u9053\uff1a\u201c\u5357\u6d77\u5a18\u5b50\u5c31\u662f\u5343\u9762\u89c2\u97f3\uff0c\u8fd9\u610f\u601d\u5c31\u662f\u8bf4\uff0c\u5979\u4e0d\u4f46\u6709\u5343\u624b\u5343\u773c\uff0c\u8fd8 \u6709\u4e00\u5343\u5f20\u4e0d\u540c\u7684\u8138\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u95ee\u51af\u516d\uff1a\u201c\u4f60\u9047\u89c1\u7684\u90a3\u4e2a\u5973\u4eba\uff0c\u957f\u5f97\u4ec0\u4e48\u6837\u5b50\uff1f\u201d \u3000\u3000\u51af\u516d\u8bb7\u8bb7\u9053\uff1a\u201c\u957f\u5f97\u597d\u50cf\u8fd8\u4e0d\u9519\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u662f\u957f\u5f97\u4e0d\u9519\uff0c\u8fd8\u662f\u975e\u5e38\u6f02\u4eae\uff1f\u201d \u3000\u3000\u51af\u516d\u5782\u4e0b\u5934\u9053\uff1a\u201c\u662f\u975e\u5e38\u6f02\u4eae\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u5979\u770b\u6765\u6709\u591a\u5927\u5e74\u7eaa\uff1f\u201d \u3000\u3000\u51af\u516d\u7684\u5934\u5782\u5f97\u66f4\u4f4e\uff0c\u4ed6\u5ffd\u7136\u53d1\u73b0\u81ea\u5df1\u7adf\u6ca1\u6709\u770b\u51fa\u90a3\u5973\u4eba\u7684\u5e74\u7eaa\u3002 \u3000\u3000\u4ed6\u7b2c\u4e00\u773c\u770b\u89c1\u5979\u65f6\uff0c\u53ea\u89c9\u5f97\u5979\u867d\u7136\u8fd8\u5f88\u5e74\u8f7b\uff0c\u4f46\u81f3\u5c11\u4e5f\u6709\u4e8c\u5341\u4e94\u516d\u3002 \u3000\u3000\u4f46\u540e\u6765\u542c\u89c1\u5979\u8bf4\u8bdd\uff0c\u4ed6\u53c8\u89c9\u5f97\u5979\u597d\u50cf\u53ea\u4e0d\u8fc7\u662f\u4e2a\u5341\u4e94\u516d\u5c81\u7684\u5c0f\u59d1\u5a18\u3002 \u3000\u3000\u4f46\u5f53\u4ed6\u53c8\u591a\u770b\u4e86\u5979\u4e24\u773c\u65f6\uff0c\u5c31\u53d1\u73b0\u5979\u773c\u89d2\u4f3c\u5df2\u6709\u4e86\u76b1\u7eb9\uff0c\u5e94\u8be5\u5df2\u6709\u4e09\u5341\u591a\u4e86\u3002 \u3000\u3000\u73b0\u5728\u60f3\u8d77\u6765\uff0c\u4ee5\u5979\u624b\u62d7\u94a2\u5200\u3001\u53e3\u541e\u5200\u950b\u90a3\u79cd\u529f\u592b\uff0c\u82e5\u6ca1\u6709\u7ec3\u8fc7\u56db\u4e94\u5341\u5e74\u82e6\u529f\uff0c\u53c8\u600e\u4f1a\u6709 \u90a3\u4e48\u6df1\u7684\u706b\u5019\uff1f \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u4f60\u770b\u4e0d\u51fa\u5979\u6709\u591a\u5927\u7684\u5e74\u7eaa\uff1f\u201d \u3000\u3000\u51af\u516d\u5782\u4e0b\u5934\uff0c\u5782\u5f97\u66f4\u4f4e\u3002 \u3000\u3000\u536b\u5929\u9e4f\u7a81\u7136\u4e00\u62cd\u5df4\u638c\uff0c\u9053\uff1a\u201c\u8fd9\u5973\u4eba\u5f88\u53ef\u80fd\u5c31\u662f\u5343\u9762\u89c2\u97f3\u3002\u201d \u3000\u3000\u51af\u516d\u5fcd\u4e0d\u4f4f\u9053\uff1a\u201c\u5979\u9000\u9690\u82e5\u5df2\u6709\u4e09\u56db\u5341\u5e74\uff0c\u73b0\u5728\u5c82\u975e\u5df2\u5e94\u8be5\u662f\u4e2a\u8001\u592a\u5a46\uff1f\u201d \u3000\u3000\u536b\u5929\u9e4f\u51b7\u7b11\u9053\uff1a\u201c\u5979\u5341\u4e03\u516b\u5c81\u65f6\uff0c\u5c31\u6709\u4eba\u8ba4\u4e3a\u5979\u662f\u4e2a\u8001\u592a\u5a46\uff0c\u8fc7\u4e86\u4e8c\u4e09\u5341\u5e74\u540e\uff0c\u5374\u53c8\u6709 \u4eba\u8bf4\u5979\u53ea\u4e0d\u8fc7\u662f\u4e2a\u5c0f\u59d1\u5a18\u3002\u201d \u3000\u3000\u51af\u516d\u6014\u4f4f\uff0c\u4ed6\u5b9e\u5728\u60f3\u4e0d\u901a\u3002 \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\u5316\u8eab\u5343\u767e\uff0c\u4f60\u770b\u89c1\u8fc7\u7684\u4efb\u4f55\u4e00\u4e2a\u4eba\uff0c\u90fd\u6709\u53ef\u80fd\u662f\u5979\u6539\u626e\u7684\uff0c\u636e\u8bf4\u6709 \u4e00\u6b21\u5c11\u6797\u666e\u6cd5\u5927\u5e08\u5728\u6cf0\u5c71\u8bb2\u7ecf\uff0c\u542c\u7ecf\u7684\u4eba\u4e2d\u8fd8\u6709\u51e0\u4f4d\u662f\u666e\u6cd5\u5927\u5e08\u7684\u8001\u670b\u53cb\uff0c\u542c\u4e86\u4e24\u5929\u4e24\u591c \u540e\uff0c\u5ffd\u7136\u53c8\u6709\u4e2a\u666e\u6cd5\u5927\u5e08\u6765\u4e86\uff0c\u4e8e\u662f\u8fd9\u624d\u6709\u4eba\u53d1\u73b0\uff0c\u5148\u524d\u8bb2\u7ecf\u7684\u90a3\u666e\u6cd5\u5927\u5e08\uff0c\u7adf\u662f\u5357\u6d77\u5a18 \u5b50\uff01\u201d \u3000\u3000\u8fd9\u79cd\u4e8b\u7b80\u76f4\u50cf\u662f\u795e\u8bdd\uff0c\u51e0\u4e4e\u6ca1\u6709\u4eba\u76f8\u4fe1\uff0c\u4f46\u6bcf\u4e2a\u4eba\u5374\u53c8\u77e5\u9053\uff0c\u536b\u516b\u592a\u7237\u662f\u4ece\u4e0d\u8bf4\u8c0e\u7684\u3002 \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u65e0\u8bba\u8c01\u53ea\u8981\u770b\u8fc7\u5357\u6d77\u5a18\u5b50\u771f\u9762\u76ee\u4e00\u773c\u90fd\u5fc5\u6b7b\u65e0\u7591\uff0c\u6240\u4ee5\u5c31\u7b97\u5728\u5979\u58f0\u540d\u6700\u76db \u65f6\uff0c\u4e5f\u6ca1\u6709\u4eba\u77e5\u9053\u5979\u662f\u4e2a\u600e\u4e48\u6837\u7684\u4eba\uff0c\u53ea\u6709\u6211\u77e5\u9053\u2026\u2026\u53ea\u6709\u6211\u77e5\u9053\u2026\u2026\u201d \u3000\u3000\u4ed6\u58f0\u97f3\u8d8a\u8bf4\u8d8a\u4f4e\uff0c\u8138\u4e0a\u5ffd\u7136\u9732\u51fa\u79cd\u5f88\u5947\u602a\u7684\u8868\u60c5\uff0c\u8fc7\u4e86\u5f88\u4e45\uff0c\u624d\u7f13\u7f13\u9053\uff1a\u201c\u5979\u63a5\u653e\u6697\u5668 \u548c\u5c0f\u5de7\u64d2\u62ff\u7684\u529f\u592b\uff0c\u5728\u5f53\u65f6\u5df2\u6ca1\u6709\u4eba\u80fd\u6bd4\u5f97\u4e0a\uff0c\u6613\u5bb9\u672f\u4e4b\u7cbe\u5999\uff0c\u66f4\u662f\u524d\u65e0\u53e4\u4eba\uff0c\u540e\u65e0\u6765\u8005\uff0c \u4f46\u5c31\u5728\u5979\u58f0\u540d\u6700\u76db\u65f6\uff0c\u5374\u5ffd\u7136\u5931\u8e2a\u4e86\uff0c\u8c01\u4e5f\u4e0d\u77e5\u9053\u662f\u4e3a\u4e86\u4ec0\u4e48\uff0c\u66f4\u4e0d\u77e5\u9053\u5979\u53bb\u4e86\u54ea\u91cc\uff0c\u8fd9\u4e09 \u5341\u5e74\u6765\uff0c\u6c5f\u6e56\u4e2d\u4ece\u6765\u4e5f\u6ca1\u6709\u4eba\u518d\u542c\u5230\u8fc7\u5979\u7684\u6d88\u606f\uff0c\u8fde\u6211\u90fd\u6ca1\u6709\u542c\u5230\u3002\u201d \u3000\u3000\u5927\u5bb6\u9762\u9762\u76f8\u89d1\u5e76\u4e0d\u6562\u8bf4\u8bdd\u3002 \u3000\u3000\u73b0\u5728\u6bcf\u4e2a\u4eba\u90fd\u5df2\u770b\u51fa\u6765\u536b\u516b\u592a\u7237\u548c\u5357\u6d77\u5a18\u5b50\u4e4b\u95f4\uff0c\u5fc5\u5b9a\u6709\u79cd\u795e\u79d8\u800c\u4e0d\u540c\u5bfb\u5e38\u7684\u5173\u7cfb\u3002 \u3000\u3000\u4f46\u5927\u5bb6\u5fc3\u91cc\u5374\u66f4\u597d\u5947\u3002 \u3000\u3000\u8fd9\u5357\u6d77\u5a18\u5b50\u65e2\u7136\u5df2\u5931\u8e2a\u4e86\u4e09\u5341\u5e74\uff0c\u4e3a\u4ec0\u4e48\u53c8\u7a81\u7136\u51fa\u73b0\u4e86\u5462\uff1f \u3000\u3000\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u536b\u5929\u9e4f\u7a81\u7136\u5927\u58f0\u9053\uff1a\u201c\u8001\u4e48\uff0c\u4f60\u8fc7\u6765\u3002\u201d \u3000\u3000\u4e00\u4e2a\u7a7f\u7740\u94f6\u72d0\u62ab\u80a9\u3001\u957f\u8eab\u7389\u7acb\u7684\u5c11\u5e74\uff0c\u5e94\u58f0\u8d70\u4e86\u51fa\u6765\u3002 \u3000\u3000\u4ed6\u7684\u8863\u7740\u5f88\u534e\u4e3d\uff0c\u526a\u88c1\u5f97\u4e5f\u975e\u5e38\u5408\u8eab\uff0c\u4e00\u5f20\u975e\u5e38\u6f02\u4eae\u7684\u8138\u4e0a\uff0c\u4e0d\u7b11\u65f6\u4e5f\u4eff\u4f5b\u5e26\u7740\u4e09\u5206\u7b11 \u610f\uff0c\u770b\u6765\u663e\u7136\u5f88\u8ba8\u5973\u4eba\u6b22\u559c\uff0c\u53ea\u4e0d\u8fc7\u773c\u775b\u91cc\u8fd8\u5e26\u7740\u4e9b\u7ea2\u4e1d\uff0c\u7ecf\u5e38\u663e\u5f97\u6709\u70b9\u7761\u7720\u4e0d\u8db3\u7684\u6837\u5b50\u3002 \u3000\u3000\u4e5f\u8bb8\u6bcf\u4e00\u4e2a\u80fd\u8ba8\u5973\u4eba\u6b22\u5fc3\u7684\u5c11\u5e74\uff0c\u90fd\u96be\u514d\u6709\u70b9\u7761\u7720\u4e0d\u8db3\u7684\u3002 \u3000\u3000\u8fd9\u5c11\u5e74\u6b63\u662f\u536b\u516b\u592a\u7237\u95e8\u4e0b\u5341\u4e09\u592a\u4fdd\u4e2d\u7684\u8001\u4e48\u201c\u7c89\u90ce\u541b\u201d\u897f\u95e8\u5341\u4e09\u3002 \u3000\u3000\u536b\u5929\u9e4f\u7528\u4e00\u53cc\u5200\u950b\u822c\u7684\u773c\u775b\u76ef\u7740\u4ed6\uff0c\u8fc7\u4e86\u5f88\u4e45\uff0c\u624d\u51b7\u51b7\u9053\uff1a\u201c\u516b\u6708\u4e2d\u79cb\u90a3\u5929\u665a\u4e0a\uff0c\u4f60\u662f \u4e0d\u662f\u4ea4\u4e86\u4e00\u4e2a\u53eb\u6797\u633a\u7684\u670b\u53cb\uff1f\u201d \u3000\u3000\u897f\u95e8\u5341\u4e09\u4eff\u4f5b\u6709\u70b9\u5403\u60ca\uff0c\u5374\u7ec8\u4e8e\u8fd8\u662f\u5782\u5934\u627f\u8ba4\uff1a\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u81ea\u4ece\u4f60\u8ddf\u90a3\u5a4a\u5b50\u517b\u7684\u642d\u4e0a\u4e86\u4e4b\u540e\uff0c\u8fd9\u4e2a\u6708\u6765\uff0c\u4f60\u505a\u4e86\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u897f\u95e8\u5341\u4e09\u7684\u8138\u7a81\u7136\u6da8\u7ea2\uff0c\u4f3c\u4e4e\u8fde\u8bdd\u90fd\u8bf4\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u536b\u5929\u9e4f\u51b7\u7b11\u9053\uff1a\u201c\u6211\u4e5f\u77e5\u9053\u4f60\u4e0d\u6562\u8bf4\uff0c\u597d\uff01\u97e9\u8d1e\uff0c\u4f60\u66ff\u4ed6\u8bf4\u3002\u201d \u3000\u3000\u97e9\u8d1e\u60f3\u4e5f\u4e0d\u60f3\uff0c\u7acb\u523b\u5c31\u6162\u6162\u5730\u8bf4\uff1a\u201c\u516b\u6708\u4e8c\u5341\u65e5\u7684\u90a3\u5929\u665a\u4e0a\uff0c\u4ed6\u5230\u5b98\u5e93\u91cc\u53bb\u501f\u4e86\u4e09\u4e07\u4e24 \u94f6\u5b50\u3002\u516b\u6708\u4e09\u5341\uff0c\u4ed6\u53c8\u53bb\u501f\u4e86\u4e00\u6b21\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u51b7\u7b11\u9053\uff1a\u201c\u5341\u5929\u5c31\u82b1\u4e86\u4e09\u4e07\u4e24\uff0c\u8fd9\u4e24\u4e2a\u738b\u516b\u86cb\u51fa\u624b\u5012\u771f\u5927\u65b9\u3002\u201d \u3000\u3000\u97e9\u8d1e\u53c8\u63a5\u7740\u8bf4\u4e0b\u53bb\uff1a\u201c\u4e5d\u6708\u521d\u516d\u7684\u665a\u4e0a\uff0c\u4ed6\u4eec\u5728\u9189\u4e2d\u548c\u4ece\u5173\u5916\u6765\u7684\u6606\u4ed1\u5f1f\u5b50\u4e89\u98ce\uff0c\u5f53\u65f6 \u867d\u7136\u5fcd\u4e86\u53e3\u6c14\uff0c\u4f46\u7b49\u5230\u6606\u4ed1\u4e09\u4fa0\u77e5\u9053\u4ed6\u4eec\u7684\u6765\u5386\uff0c\u8fde\u591c\u8d70\u4e86\u4e4b\u540e\uff0c\u4ed6\u4eec\u5374\u8ffd\u51fa\u516b\u5341\u91cc\uff0c\u5c06\u6606 \u4ed1\u4e09\u4fa0\u5168\u90fd\u6740\u5f97\u4e00\u4e2a\u4e0d\u7559\u3002\u201d \u3000\u3000\u536b\u516b\u592a\u7237\u51b7\u51b7\u9053\uff1a\u201c\u770b\u6765\u6606\u4ed1\u95e8\u4e0b\u7684\u5b50\u5f1f\uff0c\u81ea\u4ece\u9f99\u9053\u4eba\u6b7b\u4e86\u540e\uff0c\u5c31\u4e00\u4ee3\u4e0d\u5982\u4e00\u4ee3\u4e86\u3002\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u6740\u4e86\u4eba\u4e4b\u540e\uff0c\u4ed6\u4eec\u7684\u5174\u81f4\u53cd\u800c\u66f4\u9ad8\uff0c\u7adf\u4e58\u7740\u9152\u5174\uff0c\u95ef\u5165\u77f3\u5bb6\u5e84\uff0c\u5c06\u4e00\u53cc\u624d\u5341 \u56db\u5c81\u7684\u5b6a\u751f\u59d0\u59b9\u67b6\u51fa\u6765\uff0c\u966a\u4e86\u4ed6\u4eec\u4e00\u5929\u4e00\u591c\u3002\u201d \u3000\u3000\u8bf4\u5230\u8fd9\u91cc\uff0c\u897f\u95e8\u5341\u4e09\u7684\u773c\u775b\u91cc\u5df2\u9732\u51fa\u4e5e\u601c\u4e4b\u8272\uff0c\u4e0d\u505c\u5730\u6084\u6084\u5411\u97e9\u8d1e\u6253\u773c\u8272\u3002\u4f46\u97e9\u8d1e\u5374\u50cf \u662f\u6ca1\u6709\u770b\u89c1\uff0c\u63a5\u7740\u53c8\u9053\uff1a\u201c\u4ece\u6b64\u4e4b\u540e\uff0c\u4ed6\u4eec\u7684\u80c6\u5b50\u66f4\u5927\uff0c\u4e5d\u6708\u5341\u4e09\u90a3\u5929\u2026\u2026\u201d \u3000\u3000\u897f\u95e8\u5341\u4e09\u4e0d\u7b49\u4ed6\u518d\u8bf4\u4e0b\u53bb\uff0c\u5df2\u201c\u5657\u201d\u5730\u8dea\u4e86\u4e0b\u53bb\uff0c\u76f4\u633a\u633a\u5730\u8dea\u5728\u536b\u516b\u592a\u7237\u7684\u9762\u524d\uff0c\u53cd\u624b \u6495\u5f00\u4e86\u81ea\u5df1\u7684\u8863\u895f\uff0c\u9053\uff1a\u201c\u5f1f\u5b50\u9519\u4e86\uff0c\u4f60\u8001\u4eba\u5bb6\u6740\u4e86\u6211\u5427\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u77aa\u7740\u4ed6\uff0c\u671b\u4e86\u534a\u5929\uff0c\u7a81\u7136\u5927\u7b11\uff0c\u9053\uff1a\u201c\u597d\uff0c\u6709\u79cd\uff0c\u5927\u4e08\u592b\u6562\u505a\u6562\u5f53\uff0c\u6740\u51e0\u4e2a\u4e0d\u6210 \u6750\u7684\u5c0f\u4f19\u5b50\uff0c\u73a9\u51e0\u4e2a\u751f\u5f97\u7f8e\u7684\u5c0f\u59d1\u5a18\uff0c\u4ed6\u5a18\u7684\u7b97\u5f97\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u897f\u95e8\u5341\u4e09\u5403\u60ca\u5730\u5f20\u5927\u4e86\u773c\u775b\uff0c\u9053\uff1a\u201c\u4f60\u8001\u4eba\u5bb6\u4e0d\u602a\u6211\uff1f\u201d \u3000\u3000\u536b\u5929\u9e4f\u7b11\u4e86\u7b11\u9053\uff1a\u201c\u6211\u602a\u4f60\u4ec0\u4e48\uff1f\u90a3\u4e24\u4e2a\u5c0f\u59d1\u5a18\u82e5\u662f\u4e0d\u559c\u6b22\u4f60\uff0c\u96be\u9053\u4e0d\u4f1a\u4e00\u5934\u649e\u6b7b\uff1f\u4e3a \u4ec0\u4e48\u8981\u966a\u4f60\u4e00\u5929\u4e00\u591c\uff1f\u82e5\u662f\u559c\u6b22\u4f60\uff0c\u53c8\u6709\u8c01\u7ba1\u5f97\u7740\uff1f\u5c0f\u59d1\u5a18\u7231\u4e0a\u4e86\u4e2a\u5c0f\u4f19\u5b50\uff0c\u672c\u6765\u5c31\u662f\u5929\u7ecf \u5730\u4e49\u7684\u4e8b\uff0c\u8fde\u5929\u738b\u8001\u5b50\u90fd\u7ba1\u4e0d\u7740\u3002\u201d \u3000\u3000\u897f\u95e8\u5341\u4e09\u5fcd\u4e0d\u4f4f\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u56de\u7980\u4f60\u8001\u4eba\u5bb6\uff0c\u5979\u4eec\u524d\u51e0\u5929\u8fd8\u5077\u5077\u5730\u6765\u627e\u8fc7\u6211\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u53c8\u5927\u7b11\uff0c\u9053\uff1a\u201c\u7537\u5b50\u6c49\u6d3b\u5728\u4e16\u4e0a\uff0c\u5c31\u5f97\u8981\u6709\u80c6\u5b50\u6740\u4eba\uff0c\u6709\u672c\u4e8b\u52fe\u5f15\u5c0f\u59d1\u5a18\uff0c\u5426\u5219 \u8fd8\u4e0d\u5982\u4e00\u5934\u649e\u6b7b\u7b97\u4e86\u3002\u201d \u3000\u3000\u4ed6\u7b11\u58f0\u7a81\u7136\u505c\u987f\uff0c\u77aa\u7740\u897f\u95e8\u5341\u4e09\uff0c\u9053\uff1a\u201c\u6211\u65e2\u7136\u4e0d\u602a\u4f60\uff0c\u4f60\u77e5\u4e0d\u77e5\u9053\u6211\u53eb\u4f60\u51fa\u6765\u5e72\u4ec0 \u4e48\uff1f\u201d \u3000\u3000\u897f\u95e8\u5341\u4e09\u9053\uff1a\u201c\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u90a3\u5a4a\u5b50\u517b\u7684\u6797\u633a\uff0c\u672c\u6765\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u897f\u95e8\u5341\u4e09\u9053\uff1a\u201c\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u7a81\u7136\u98de\u8d77\u4e00\u811a\uff0c\u5c06\u4ed6\u8e22\u5f97\u6eda\u51fa\u53bb\u4e00\u4e08\u5f00\u5916\uff0c\u53c8\u8ffd\u8fc7\u53bb\uff0c\u4e00\u628a\u63ea\u4f4f\u4ed6\u5934\u53d1\uff0c\u628a\u4ed6\u6574 \u4e2a\u4eba\u90fd\u62c9\u4e86\u8d77\u6765\uff0c\u6b63\u6b63\u53cd\u53cd\u7ed9\u4e86\u4ed6\u5341\u4e03\u516b\u4e2a\u8033\u62ec\u5b50\uff0c\u7136\u540e\u624d\u95ee\u9053\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u6211\u4e3a\u4ec0\u4e48\u8981 \u6253\u4f60\uff1f\u201d \u3000\u3000\u897f\u95e8\u5341\u4e09\u5403\u5403\u9053\uff1a\u201c\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u4ed6\u7684\u786e\u4e0d\u77e5\u9053\uff0c\u4ed6\u7b80\u76f4\u5df2\u88ab\u6253\u5f97\u6014\u4f4f\u4e86\u3002 \u3000\u3000\u536b\u5929\u9e4f\u5389\u58f0\u9053\uff1a\u201c\u7537\u5b50\u6c49\u5927\u4e08\u592b\uff0c\u6740\u4eba\u653e\u706b\u90e8\u7b97\u4e0d\u4e86\u4ec0\u4e48\uff0c\u4f46\u82e5\u81ea\u5df1\u7684\u670b\u53cb\u7a76\u7adf\u662f\u4ec0\u4e48 \u4eba\u90fd\u4e0d\u77e5\u9053\uff0c\u90a3\u624d\u771f\u662f\u4e2a\u6d3b\u6df7\u86cb\uff0c\u780d\u5934\u4e00\u767e\u6b21\u90fd\u4e0d\u5acc\u591a\u3002\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u521a\u8bf4\u5b8c\uff0c\u5ffd\u7136\u95f4\uff0c\u4eba\u5f71\u4e00\u95ea\uff0c\u897f\u95e8\u5341\u4e09\u65c1\u8fb9\u5df2\u591a\u4e86\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u5927\u5385\u91cc\u4e8c\u4e09\u5341\u53cc\u773c\u775b\uff0c\u7adf\u5168\u90fd\u6ca1\u6709\u770b\u6e05\u8fd9\u4e2a\u4eba\u662f\u4ece\u4ec0\u4e48\u5730\u65b9\u6765\u7684\u3002 \u3000\u3000\u706f\u5149\u7167\u8000\u4e0b\uff0c\u53ea\u89c1\u8fd9\u4e2a\u4eba\u767d\u767d\u51c0\u51c0\u4e00\u5f20\u8138\uff0c\u7626\u7626\u9ad8\u9ad8\u7684\u8eab\u6750\uff0c\u957f\u5f97\u5f88\u79c0\u6c14\uff0c\u6001\u5ea6\u4e5f\u5f88\u65af \u6587\uff0c\u795e\u60c5\u95f4\u8fd8\u4eff\u4f5b\u5e26\u7740\u513f\u5206\u5c0f\u59d1\u5a18\u7684\u7f9e\u6da9\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u500f\u5ffd\u800c\u6765\u843d\u5730\u65e0\u58f0\uff0c\u8f7b\u529f\u4e4b\u9ad8\u8fde\u5341\u4e09\u592a\u4fdd\u4e2d\u90fd\u6ca1\u6709\u4e00\u4e2a\u4eba\u80fd\u6bd4\u5f97\u4e0a\u3002 \u3000\u3000\u4ed6\u8eab\u5b50\u4e00\u7ad9\u7a33\uff0c\u5c31\u957f\u63d6\u5230\u5730\uff0c\u9053\uff1a\u201c\u665a\u8f88\u4e01\u9e9f\uff0c\u7279\u6765\u62dc\u89c1\u536b\u516b\u592a\u536b\u5929\u9e4f\u77aa\u7740\u4ed6\uff0c\u5389\u58f0 \u9053\uff1a\u201c\u4f60\u5c45\u7136\u6562\u6765\uff1f\u201d \u3000\u3000\u4e01\u9e9f\u9053\uff1a\u201c\u665a\u8f88\u4e0d\u6562\u4e0d\u6765\uff01\u201d \u3000\u3000\u536b\u5929\u9e4f\u7a81\u7136\u5927\u7b11\u9053\uff1a\u201c\u597d\uff01\u6709\u79cd\uff0c\u6211\u8001\u4eba\u5bb6\u5c31\u559c\u6b22\u4f60\u4eec\u8fd9\u4e9b\u6709\u79cd\u7684\u5c0f\u4f19\u5b50\uff01\u201d \u3000\u3000\u4ed6\u653e\u5f00\u4e86\u897f\u95e8\u5341\u4e09\uff0c\u53c8\u9053\uff1a\u201c\u4f60\u8fd9\u6df7\u86cb\u73b0\u5728\u603b\u7b97\u660e\u767d\u4e86\u5427\uff0c\u6797\u633a\u5c31\u662f\u4e01\u9e9f\uff0c\u4f60\u80fd\u4ea4\u5f97\u5230 \u4ed6\u8fd9\u79cd\u670b\u53cb\uff0c\u9020\u5316\u603b\u7b97\u4e0d\u9519\uff01\u201d \u3000\u3000\u897f\u95e8\u5341\u4e8c\u5403\u60ca\u5730\u770b\u7740\u4ed6\u7684\u670b\u53cb\uff0c\u6bcf\u4e2a\u4eba\u90fd\u5728\u770b\u7740\u4ed6\u8fd9\u4e2a\u670b\u53cb\uff0c\u4e01\u9e9f\u8fd9\u540d\u5b57\u6bcf\u4e2a\u4eba\u90fd\u542c\u89c1 \u8fc7\u7684\uff0c\u4f46\u5374\u6ca1\u6709\u4eba\u80fd\u60f3\u5f97\u5230\uff0c\u8fd9\u65af\u65af\u6587\u6587\u7684\u3001\u50cf\u5c0f\u59d1\u5a18\u4e00\u6837\u7684\u5c11\u5e74\uff0c\u5c45\u7136\u5c31\u662f\u6b66\u6797\u540e\u8d77\u4e00\u4ee3 \u9ad8\u624b\u4e2d\uff0c\u8f7b\u529f\u6700\u9ad8\u7684\u201c\u98ce\u90ce\u541b\u201d\u4e01\u9e9f\u3002 \u3000\u3000\u9664\u4e86\u97e9\u8d1e\u548c\u536b\u516b\u592a\u7237\u5916\uff0c\u7684\u786e\u6ca1\u6709\u522b\u4eba\u80fd\u60f3\u5f97\u5230\u3002 \u3000\u3000\u4e01\u9e9f\u7684\u8138\u5374\u5df2\u7ea2\u4e86\u3002 \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u6211\u63cd\u8fd9\u5c0f\u6df7\u86cb\uff0c\u4e3a\u7684\u5c31\u662f\u8981\u628a\u4f60\u63cd\u51fa\u6765\u3002\u201d \u3000\u3000\u4e01\u9e9f\u7ea2\u7740\u8138\u9053\uff1a\u201c\u5374\u4e0d\u77e5\u524d\u8f88\u6709\u4f55\u5429\u5490\uff1f\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u6211\u6709\u4ef6\u4e8b\u8981\u4f60\u66ff\u6211\u53bb\u505a\uff0c\u8fd9\u4ef6\u4e8b\u4e5f\u975e\u8981\u4f60\u53bb\u505a\u4e0d\u53ef\u3002\u201d \u3000\u3000\u4ed6\u7684\u8868\u60c5\u5ffd\u7136\u53d8\u5f97\u5f88\u4e25\u8083\uff0c\u63a5\u7740\u9053\uff1a\u201c\u53ef\u662f\u6211\u4e5f\u4e0d\u60f3\u8981\u4f60\u53bb\u9001\u6b7b\uff0c\u6240\u4ee5\u6211\u8fd8\u60f3\u770b\u770b\u4f60\u7684 \u8f7b\u529f\u7a76\u7adf\u600e\u4e48\u6837\u3002\u201d \u3000\u3000\u4e01\u9e9f\u8fd8\u7ad9\u7740\uff0c\u4ed6\u7684\u80a9\u6ca1\u6709\u79fb\uff0c\u81c2\u6ca1\u6709\u4e3e\uff0c\u4eff\u4f5b\u8fde\u6307\u5c16\u90fd\u6ca1\u6709\u52a8\u3002 \u3000\u3000\u4f46\u5c31\u5728\u8fd9\u65f6\uff0c\u4ed6\u7684\u4eba\u5ffd\u7136\u50cf\u71d5\u5b50\u822c\u98de\u4e86\u8d77\u6765\uff0c\u53c8\u50cf\u662f\u4e00\u9635\u98ce\u4f3c\u7684\uff0c\u4ece\u4f17\u4eba\u5934\u9876\u4e0a\u5439\u8fc7\u3002 \u3000\u3000\u7b49\u5230\u8fd9\u9635\u98ce\u5439\u56de\u6765\u7684\u65f6\u5019\uff0c\u4ed6\u7684\u4eba\u7adf\u53c8\u597d\u597d\u7684\u7ad9\u5728\u539f\u6765\u7684\u5730\u65b9\uff0c\u624b\u91cc\u5374\u53c8\u591a\u4e86\u76cf\u706f\u7b3c\u3002 \u3000\u3000\u8fd9\u76cf\u706f\u7b3c\u539f\u6765\u662f\u9ad8\u60ac\u5728\u5385\u5916\u4e00\u6839\u7af9\u7aff\u4e0a\u7684\uff0c\u8fd9\u7af9\u7aff\u81f3\u5c11\u6709\u4e09\u4e08\u591a\u9ad8\uff0c\u8ddd\u79bb\u4ed6\u7ad9\u7740\u7684\u5730 \u65b9\uff0c\u81f3\u5c11\u6709\u4e94\u516d\u4e08\u8fdc\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u500f\u5ffd\u6765\u53bb\uff0c\u8fde\u6c14\u90fd\u6ca1\u6709\u5598\u3002 \u3000\u3000\u536b\u5929\u9e4f\u62ca\u638c\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff01\u522b\u4eba\u90fd\u8bf4\u2018\u98ce\u90ce\u541b\u2019\u8f7b\u529f\u4e4b\u9ad8\uff0c\u5df2\u53ef\u540d\u5217\u5728\u5929\u4e0b\u4e94\u5927\u9ad8 \u624b\u4e4b\u4e2d\uff0c\u4eca\u65e5\u4e00\u89c1\uff0c\u679c\u7136\u540d\u4e0d\u865a\u4f20\u3002\u201d \u3000\u3000\u4ed6\u7528\u529b\u62cd\u7740\u4e01\u9e9f\u7684\u80a9\uff0c\u53c8\u9053\uff1a\u201c\u4f60\u8fd9\u6837\u7684\u8f7b\u529f\uff0c\u5c3d\u53ef\u53bb\u4e86\u3002\u201d \u3000\u3000\u4e01\u9e9f\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u5230\u54ea\u91cc\u53bb\uff1f\u201d \u3000\u3000\u5de5\u5927\u9e4f\u9053\uff1a\u201c\u5230\u51b7\u9999\u56ed\u53bb\uff0c\u770b\u770b\u90a3\u5357\u6d77\u5a18\u5b50\u7a76\u7adf\u662f\u771f\u662f\u5047\uff1f\u201d \u3000\u3000\u4e01\u9e9f\u7684\u8138\u8272\u7a81\u7136\u82cd\u767d\u3002 \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u4f60\u77e5\u9053\u5357\u6d77\u5a18\u5b50\uff1f\u201d \u3000\u3000\u4e01\u9e9f\u70b9\u70b9\u5934\u3002 \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u4f60\u4e5f\u77e5\u9053\u5979\u7684\u5389\u5bb3\uff1f\u201d \u3000\u3000\u4e01\u9e9f\u53c8\u70b9\u70b9\u5934\u3002 \u3000\u3000\u536b\u5929\u9e4f\u53c8\u76ef\u7740\u4ed6\u770b\u4e86\u534a\u5929\uff0c\u7a81\u53c8\u95ee\u9053\uff1a\u201c\u4f60\u5e08\u7236\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u4e01\u9e9f\u4e3a\u96be\u7740\uff0c\u5ffd\u7136\u8d70\u4e0a\u4e24\u6b65\uff0c\u5728\u4ed6\u8eab\u65c1\u8f7b\u8f7b\u8bf4\u4e86\u4e2a\u540d\u5b57\u3002 \u3000\u3000\u536b\u5929\u9e4f\u7acb\u523b\u52a8\u5bb9\uff0c\u9053\uff1a\u201c\u8fd9\u5c31\u96be\u602a\u4f60\u77e5\u9053\u4e86\uff0c\u6614\u5e74\u5929\u5c71\u4e00\u6218\uff0c\u4f60\u5e08\u7236\u4e5f\u66fe\u9886\u6559\u8fc7\u5979\u7684\u624b \u6bb5\u3002\u201d \u3000\u3000\u4e01\u9e9f\u7ea2\u7740\u8138\uff0c\u9053\uff1a\u201c\u665a\u8f88\u867d\u4e0d\u6562\u5984\u81ea\u83f2\u8584\uff0c\u5374\u8fd8\u6709\u70b9\u81ea\u77e5\u4e4b\u660e\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u4f46\u6709\u4ef6\u4e8b\u5374\u662f\u4f60\u4e0d\u77e5\u9053\u7684\u3002\u201d \u3000\u3000\u4e01\u9e9f\u9053\uff1a\u201c\u8bf7\u6559\uff01\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u5357\u6d77\u5a18\u5b50\u4e3a\u4e86\u8981\u9a7b\u989c\u957f\u751f\uff0c\u7ec3\u4e86\u79cd\u90aa\u95e8\u7684\u5185\u529f\uff0c\u4f46\u4e5f\u4e0d\u77e5\u9053\u4e3a\u4e86\u4ec0\u4e48\uff0c\u5374 \u6ca1\u6709\u7ec3\u597d\uff0c\u6240\u4ee5\u6bcf\u5929\u4e00\u5230\u4e86\u5b50\u5348\u6b63\u65f6\uff0c\u771f\u6c14\u5c31\u4f1a\u7a81\u7136\u8d70\u5c94\uff0c\u81f3\u5c11\u6709\u534a\u76cf\u8336\u7684\u65f6\u95f4\uff0c\u5168\u8eab\u50f5 \u6728\uff0c\u8fde\u52a8\u90fd\u4e0d\u80fd\u52a8\u3002\u201d \u3000\u3000\u4e01\u9e9f\u9759\u9759\u5730\u542c\u7740\u3002 \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u53ef\u662f\u5979\u7684\u884c\u8e2a\u7d20\u6765\u5f88\u9690\u79d8\uff0c\u771f\u6c14\u8d70\u5c94\u7684\u8fd9\u4e00\u523b\uff0c\u65f6\u95f4\u53c8\u975e\u5e38\u77ed\uff0c\u6240\u4ee5\u867d\u7136 \u6709\u4eba\u77e5\u9053\u5979\u8fd9\u552f\u4e00\u7684\u5f31\u70b9\uff0c\u4e5f\u4e0d\u6562\u53bb\u627e\u5979\u7684\uff01\u201d \u3000\u3000\u4ed6\u6162\u6162\u5730\u63a5\u7740\u9053\uff1a\u201c\u73b0\u5728\u6211\u4eec\u65e2\u5df2\u77e5\u9053\u5979\u8fd9\u51e0\u5929\u5fc5\u5b9a\u5728\u51b7\u9999\u56ed\uff0c\u4f60\u7684\u8f7b\u529f\u53c8\u5982\u6b64\u9ad8\u660e\uff0c \u53ea\u8981\u80fd\u627e\u5f97\u5230\u5979\u7684\u7ec3\u529f\u5904\uff0c\u5c31\u4e0d\u59a8\u5728\u4e8e\u5348\u6b63\u65f6\u90a3\u4e00\u523b\uff0c\u60f3\u6cd5\u5b50\u8fdb\u53bb\u63ed\u5f00\u5979\u7684\u9762\u5177\u6765\u2026\u2026\u201d \u3000\u3000\u4e01\u9e9f\u5fcd\u4e0d\u4f4f\u95ee\uff1a\u201c\u9762\u5177\uff1f\u662f\u4ec0\u4e48\u9762\u5177\uff1f\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u5979\u5e73\u65f6\u8138\u4e0a\u603b\u662f\u6234\u7740\u4e2a\u9762\u5177\u7684\uff0c\u56e0\u4e3a\u5979\u6ca1\u6709\u6613\u5bb9\u6539\u626e\u65f6\uff0c\u4e5f\u5f80\u5f80\u4e0d\u613f\u4ee5\u771f \u9762\u76ee\u793a\u4eba\u3002\u201d \u3000\u3000\u4e01\u9e9f\u9053\uff1a\u201c\u65e2\u7136\u6ca1\u6709\u4eba\u89c1\u8fc7\u5979\u7684\u771f\u9762\u76ee\uff0c\u665a\u8f88\u867d\u7136\u80fd\u63ed\u5f00\u5979\u7684\u9762\u5177\uff0c\u4e5f\u540c\u6837\u5206\u4e0d\u51fa\u5979\u662f \u771f\u7684\u8fd8\u662f\u5047\u7684\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u6211\u89c1\u8fc7\u5979\u7684\u771f\u9762\u76ee\uff0c\u5979\u6709\u4e2a\u5f88\u7279\u522b\u7684\u6807\u8bb0\uff0c\u4f60\u53ea\u8981\u770b\u89c1\uff0c\u5c31\u4e00\u5b9a\u80fd\u8ba4\u51fa \u6765\u3002\u201d \u3000\u3000\u4e01\u9e9f\u9053\uff1a\u201c\u4ec0\u4e48\u6807\u8bb0\uff1f\u201d\u536b\u5929\u9e4f\u4e5f\u7a81\u7136\u4fef\u8fc7\u8eab\uff0c\u5728\u4ed6\u8033\u65c1\u8f7b\u8f7b\u8bf4\u4e86\u4e24\u53e5\u8bdd\u3002 \u3000\u3000\u4e01\u9e9f\u7684\u8138\u8272\u53d8\u4e86\u53d8\uff0c\u53c8\u4e3a\u96be\u4e86\u5f88\u4e45\uff0c\u624d\u8bd5\u63a2\u7740\u9053\uff1a\u201c\u524d\u8f88\u65e2\u7136\u89c1\u8fc7\u5979\u9762\u76ee\uff0c\u60f3\u5fc5\u662f\u5979\u7684 \u670b\u53cb\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u81ea\u5df1\u53bb\u770b\u770b\u5979\u662f\u771f\u662f\u5047\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u9762\u4e0a\u7a81\u53c8\u73b0\u51fa\u6012\u5bb9\uff0c\u6012\u58f0\u9053\uff1a\u201c\u6211\u53eb\u4f60\u53bb\uff0c\u4f60\u5c31\u5f97\u53bb\uff0c\u522b\u7684\u4e8b\u4f60\u6700\u597d\u5c11\u7ba1\u3002\u201d \u3000\u3000\u4e01\u9e9f\u4e0d\u8bf4\u8bdd\u4e86\uff0c\u536b\u516b\u592a\u7237\u76db\u6012\u65f6\uff0c\u6ca1\u6709\u4eba\u6562\u8bf4\u8bdd\u3002\u536b\u5929\u9e4f\u77aa\u7740\u4ed6\uff0c\u5389\u58f0\u95ee\u9053\uff1a\u201c\u4f60\u53bb\u4e0d \u53bb\uff1f\u201d \u3000\u3000\u4e01\u9e9f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u665a\u8f88\u65e2\u7136\u5df2\u77e5\u9053\u4e86\u8fd9\u79d8\u5bc6\uff0c\u60f3\u4e0d\u53bb\u53ea\u6015\u4e5f\u4e0d\u884c\u4e86\uff01\u201d \u3000\u3000\u536b\u5929\u9e4f\u7a81\u53c8\u5927\u7b11\u9053\uff1a\u201c\u597d\uff0c\u4f60\u679c\u7136\u662f\u4e2a\u806a\u660e\u4eba\uff0c\u6211\u8001\u4eba\u5bb6\u4e00\u5411\u559c\u6b22\u806a\u660e\u4eba\uff01\u201d \u3000\u3000\u4ed6\u7528\u529b\u62cd\u7740\u4e01\u9e9f\u7684\u80a9\uff0c\u53c8\u9053\uff1a\u201c\u53ea\u8981\u4f60\u53bb\uff0c\u522b\u7684\u65e0\u8bba\u4ec0\u4e48\u4e8b\uff0c\u6211\u90fd\u7b54\u5e94\u3002\u201d \u3000\u3000\u4e01\u9e9f\u5ffd\u7136\u4e5f\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u73b0\u5728\u665a\u8f88\u53ea\u60f3\u6c42\u524d\u8f88\u7b54\u5e94\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u4e01\u9e9f\u9053\uff1a\u201c\u665a\u8f88\u60f3\u6253\u4e00\u4e2a\u4eba\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u4f60\u8981\u6253\u8c01\uff1f\u201d \u3000\u3000\u97e9\u8d1e\u5ffd\u7136\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u3002\u201d \u3000\u3000\u4e01\u9e9f\u679c\u7136\u5df2\u8f6c\u8fc7\u8eab\u6765\uff0c\u6162\u6162\u5730\u8d70\u5230\u4ed6\u9762\u524d\uff0c\u5fae\u7b11\u7740\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u7684\u786e\u662f\u60f3\u6253\u4f60\uff01\u201d \u3000\u3000\u4ed6\u7b11\u5f97\u8fd8\u662f\u5f88\u6e29\u67d4\u3001\u5f88\u5bb3\u7f9e\u7684\u6837\u5b50\uff0c\u53ef\u662f\u4ed6\u7684\u624b\u5374\u5df2\u7a81\u7136\u6325\u51fa\uff0c\u4e00\u62f3\u6253\u5728\u97e9\u8d1e\u7684\u9f3b\u6881 \u4e0a\u3002 \u3000\u3000\u97e9\u8d1e\u6574\u4e2a\u4eba\u90fd\u5df2\u88ab\u6253\u5f97\u98de\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u4e01\u9e9f\u8fd9\u624d\u8f6c\u53e3\u8eab\uff0c\u5411\u536b\u516b\u592a\u7237\u4e00\u63d6\u5230\u5730\uff0c\u5fae\u7b11\u7740\u9053\uff1a\u201c\u665a\u8f88\u8fd9\u5c31\u5230\u51b7\u9999\u56ed\u53bb\uff0c\u4e94\u5929\u4e4b \u5185\uff0c\u5fc5\u6709\u6d88\u606f\u3002\u201d \u3000\u3000\u201c\u6d88\u606f\u201d\u4e24\u4e2a\u5b57\u8bf4\u51fa\u6765\uff0c\u4ed6\u7684\u4eba\u5df2\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u536b\u5929\u9e4f\u5c45\u7136\u4e5f\u53f9\u4e86\u53e3\u6c14\uff0c\u54fa\u54fa\u9053\uff1a\u201c\u8fd9\u4e00\u4ee3\u7684\u5e74\u8f7b\u4eba\uff0c\u597d\u50cf\u6bd4\u6211\u4eec\u90a3\u4e00\u4ee3\u8fd8\u4e0d\u662f\u4e1c\u897f\uff0c \u8fd9\u5012\u771f\u662f\u4ef6\u8981\u547d\u7684\u4e8b\u2026\u2026\u201d \u7b2c\u4e09\u7ae0\u3000\u6444\u9b42\u5927\u6cd5>> \u53e4\u9f99\u300a\u4e5d\u6708\u9e70\u98de\u300b \u7b2c\u4e09\u7ae0\u3000\u6444\u9b42\u5927\u6cd5 \u3000\u3000\u9ad8\u5899\uff0c\u5bd2\u591c\u3002 \u3000\u3000\u9ad8\u5899\u4e0b\u7684\u89d2\u95e8\u91cc\uff0c\u5ffd\u7136\u6709\u4e00\u4e2a\u4eba\u6084\u6084\u5730\u8d70\u51fa\u6765\uff0c\u975e\u5e38\u82f1\u4fca\u7684\u4e00\u5f20\u8138\uff0c\u5df2\u88ab\u6253\u80bf\u4e86\u534a\u8fb9\uff0c \u6b63\u662f\u90a3\u98ce\u6d41\u6210\u6027\u7684\u897f\u95e8\u5341\u4e09\u3002 \u3000\u3000\u4ed6\u4e00\u8d70\u51fa\u8fd9\u6761\u5df7\u5b50\uff0c\u7adf\u6709\u8f86\u53d1\u4eae\u7684\u9ed1\u6f06\u9a6c\u8f66\u6025\u5f1b\u800c\u6765\uff0c\u9aa4\u7136\u5728\u4ed6\u8eab\u65c1\u505c\u4e0b\u3002 \u3000\u3000\u8f66\u95e8\u4e00\u5f00\uff0c\u4ed6\u5c31\u8df3\u4e86\u8fdb\u53bb\uff0c\u8f66\u53a2\u91cc\u5df2\u6709\u4e00\u676f\u9152\u5728\u7b49\u7740\u4ed6\u3002 \u3000\u3000\u4e00\u676f\u6e29\u5f97\u6070\u5230\u597d\u5904\u7684\u9648\u5e74\u5973\u513f\u7ea2\uff0c\u4e00\u53cc\u6bd4\u5973\u513f\u7ea2\u66f4\u9189\u4eba\u7684\u59d0\u59b9\u82b1\u3002 \u3000\u3000\u59d0\u59d0\u770b\u8d77\u6765\uff0c\u5c31\u50cf\u662f\u59b9\u59b9\u7684\u5f71\u5b50\uff0c\u59b9\u59b9\u867d\u5a07\u61a8\uff0c\u59d0\u59d0\u66f4\u52a8\u4eba\u3002 \u3000\u3000\u4e00\u4e2a\u5c11\u5e74\u4eba\uff0c\u62e5\u7740\u8c82\u88d8\uff0c\u7aef\u7740\u9152\u676f\uff0c\u61d2\u6d0b\u6d0b\u5730\u501a\u5728\u59d0\u59d0\u6000\u91cc\uff0c\u5374\u5c06\u59b9\u59b9\u63a8\u7ed9\u4e86\u897f\u95e8\u5341 \u4e09\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u4eca\u5929\u6328\u4e86\u63cd\uff0c\u4f60\u8d76\u5feb\u597d\u597d\u7684\u5b89\u6170\u5b89\u6170\u4ed6\uff01\u201d \u3000\u3000\u59b9\u59b9\u5df2\u5728\u8f7b\u543b\u7740\u897f\u95e8\u5341\u4e09\u88ab\u6253\u80bf\u4e86\u7684\u90a3\u534a\u8fb9\u8138\u3002 \u3000\u3000\u9a6c\u8f66\u53c8\u6025\u9a70\u800c\u53bb\uff0c\u9a70\u5411\u957f\u5b89\uff01 \u3000\u3000\u5bd2\u51e4\u5982\u5200\uff0c\u5df2\u662f\u5c81\u672b\uff0c\u8f66\u53a2\u91cc\u5374\u6e29\u6696\u5982\u6625\u5929\u3002 \u3000\u3000\u897f\u95e8\u5341\u4e09\u4e00\u53e3\u6c14\u559d\u4e0b\u90a3\u676f\u9152\uff0c\u624d\u770b\u4e86\u90a3\u5750\u62e5\u8c82\u88d8\u7684\u5c11\u5e74\u4e00\u773c\u3002 \u3000\u3000\u9053\uff1a\u201c\u4f60\u77e5\u9053\u6211\u4f1a\u6765\uff1f\u201d \u3000\u3000\u8fd9\u5c11\u5e74\u4eba\u5f53\u7136\u5c31\u662f\u4e01\u9e9f\uff0c\u53ea\u4e0d\u8fc7\u73b0\u5728\u770b\u6765\u5374\u5df2\u4e0d\u50cf\u662f\u521a\u624d\u90a3\u4e2a\u4eba\u4e86\u3002 \u3000\u3000\u521a\u624d\u90a3\u4e2a\u4e01\u9e9f\uff0c\u662f\u4e2a\u5f88\u65af\u6587\u3001\u5f88\u5bb3\u7f9e\u7684\u5c11\u5e74\uff0c\u73b0\u5728\u8fd9\u4e2a\u4e01\u9e9f\uff0c\u5374\u662f\u4e2a\u653e\u8361\u4e0d\u7f81\u7684\u98ce\u6d41\u6d6a \u5b50\u3002 \u3000\u3000\u4ed6\u7528\u773c\u89d2\u779f\u7740\u897f\u95e8\u5341\u4e09\uff0c\u61d2\u6d0b\u6d0b\u5730\u5fae\u7b11\u7740\uff0c\u9053\uff1a\u201c\u6211\u5f53\u7136\u77e5\u9053\uff0c\u90a3\u8001\u738b\u516b\u86cb\u4e0d\u53eb\u4f60\u6765\u7b49 \u6211\u7684\u6d88\u606f\uff0c\u8fd8\u80fd\u53eb\u8c01\u6765\uff1f\u201d \u3000\u3000\u897f\u95e8\u5341\u4e09\u4e5f\u7b11\u4e86\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u65e2\u7136\u5f88\u6709\u79cd\uff0c\u521a\u624d\u4e3a\u4ec0\u4e48\u4e0d\u6562\u5f53\u7740\u4ed6\u7684\u9762\uff0c\u53eb\u4ed6\u8001\u738b\u516b \u86cb\uff1f\u4e3a\u4ec0\u4e48\u8981\u53d8\u6210\u90a3\u79cd\u9f9f\u5b59\u5b50\u7684\u6837\u5b50\uff01\u201d \u3000\u3000\u59d0\u59d0\u59b9\u59b9\u90fd\u5403\u5403\u7684\u7b11\u4e86\u3002 \u3000\u3000\u5979\u4eec\u7684\u5e74\u7eaa\u90fd\u4e0d\u5927\uff0c\u53ef\u662f\u770b\u5979\u4eec\u8eab\u6750\uff0c\u5c31\u7b97\u662f\u778e\u5b50\uff0c\u4e5f\u770b\u5f97\u51fa\u5979\u4eec\u90fd\u5df2\u4e0d\u7b97\u662f\u5b69\u5b50\u3002 \u3000\u3000\u897f\u95e8\u5341\u4e09\u53c8\u7b11\u9053\uff1a\u201c\u4e0d\u7ba1\u600e\u4e48\u6837\uff0c\u4f60\u521a\u624d\u63cd\u97e9\u8d1e\u90a3\u4e00\u62f3\uff0c\u63cd\u5f97\u771f\u75db\u5feb\uff01\u201d \u3000\u3000\u4e01\u9e9f\u9053\uff1a\u201c\u56e0\u4e3a\u4ed6\u8bf4\u7684\u8bdd\uff0c\u5168\u90fd\u662f\u90a3\u8001\u738b\u516b\u86cb\u53eb\u4ed6\u8bf4\u7684\uff0c\u4ed6\u53ea\u4e0d\u8fc7\u662f\u4e2a\u6d3b\u5080\u5121\u800c\u5df2\u3002\u201d \u3000\u3000\u4ed6\u51b7\u7b11\u4e86\u4e00\u58f0\uff0c\u53c8\u8bf4\u9053\uff1a\u201c\u90a3\u8001\u738b\u516b\u86cb\uff0c\u5176\u5b9e\u662f\u4e2a\u8001\u72d0\u72f8\uff0c\u5374\u504f\u504f\u8981\u88c5\u6210\u8001\u864e\u7684\u6837\u5b50\uff0c \u53ea\u53ef\u60dc\u4ed6\u80fd\u7792\u5f97\u8fc7\u522b\u4eba\uff0c\u5374\u7792\u4e0d\u8fc7\u6211\u3002\u201d \u3000\u3000\u897f\u95e8\u5341\u4e09\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u96be\u602a\u8001\u5934\u5b50\u8bf4\u4f60\u5389\u5bb3\uff0c\u4ed6\u679c\u7136\u6ca1\u6709\u770b\u9519\u3002\u201d \u3000\u3000\u4e01\u9e9f\u51b7\u51b7\u9053\uff1a\u201c\u8fd9\u4e00\u4ee3\u7684\u5e74\u8f7b\u4eba\uff0c\u80fd\u5728\u6c5f\u6e56\u6210\u540d\u7684\uff0c\u6709\u54ea\u4e2a\u4e0d\u5389\u5bb3\uff0c\u771f\u6b63\u5389\u5bb3\u7684\uff0c\u4ed6\u53ea \u6015\u8fd8\u6ca1\u6709\u770b\u89c1\u54e9\u3002\u201d \u3000\u3000\u897f\u95e8\u5341\u4e09\u9053\uff1a\u201c\u6c5f\u6e56\u4e2d\u96be\u9053\u8fd8\u6709\u50cf\u4f60\u8fd9\u4e48\u5389\u5bb3\u7684\u4eba\uff1f\u201d \u3000\u3000\u4e01\u9e9f\u9053\uff1a\u201c\u50cf\u6211\u8fd9\u6837\u7684\u4eba\uff0c\u81f3\u5c11\u6709\u5341\u6765\u4e2a\uff0c\u53ea\u6709\u4f60\u4eec\u8fd9\u4e9b\u9f9f\u5b59\u5b50\uff0c\u6574\u5929\u8eb2\u5728\u8001\u5934\u5b50\u7684\u88e4 \u88c6\u91cc\uff0c\u5916\u9762\u7684\u5929\u6709\u591a\u9ad8\uff0c\u5730\u6709\u591a\u539a\uff0c\u4f60\u4eec\u8fde\u5f71\u5b50\u90fd\u6478\u4e0d\u5230\u3002\u201d \u3000\u3000\u4ed6\u51b7\u7b11\u7740\u53c8\u9053\uff1a\u201c\u6211\u770b\u4f60\u4eec\u4e0d\u662f\u5341\u4e09\u592a\u4fdd\uff0c\u662f\u5403\u5f97\u592a\u9971\u4e86\uff0c\u6240\u4ee5\u6491\u5f97\u5934\u6655\u8111\u80c0\uff0c\u8001\u5934\u5b50 \u653e\u4e2a\u5c41\u4f60\u4eec\u90fd\u4ee5\u4e3a\u662f\u9999\u7684\u3002\u201d \u3000\u3000\u897f\u95e8\u5341\u4e09\u975e\u4f46\u6ca1\u6709\u751f\u6c14\uff0c\u53cd\u800c\u53f9\u4e86\u53e3\u6c14\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u8fd1\u6765\u4ed6\u4eec\u7684\u786e\u5403\u5f97\u592a\u9971\uff0c\u65e5\u5b50\u4e5f\u8fc7 \u5f97\u592a\u8212\u670d\u4e86\uff0c\u6240\u4ee5\u4e00\u51fa\u4e86\u4e8b\uff0c\u5c31\u6b7b\u4e86\u4e24\u4e2a\u3002\u201d \u3000\u3000\u4e01\u9e9f\u9053\uff1a\u201c\u5728\u4f60\u770b\u6765\uff0c\u90a3\u4e5f\u7b97\u662f\u4ef6\u5927\u4e8b\uff1f\u201d \u3000\u3000\u897f\u95e8\u5341\u4e09\u9053\uff1a\u201c\u867d\u7136\u4e0d\u5927\uff0c\u4e5f\u4e0d\u592a\u5c0f\uff0c\u81f3\u5c11\u8fde\u8001\u5934\u5b50\u90fd\u5df2\u51c6\u5907\u4e3a\u8fd9\u4ef6\u4e8b\u51fa\u624b\u4e86\u3002\u201d \u3000\u3000\u4e01\u9e9f\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u897f\u95e8\u5341\u4e09\u9053\uff1a\u201c\u5c31\u56e0\u4e3a\u4ed6\u5df2\u51c6\u5907\u51fa\u624b\uff0c\u6240\u4ee5\u624d\u627e\u4f60\u5230\u51b7\u9999\u56ed\u53bb\u63a2\u542c\u6d88\u606f\u3002\u201d \u3000\u3000\u4e01\u9e9f\u9053\uff1a\u201c\u4f60\u4ee5\u4e3a\u4ed6\u771f\u662f\u4e3a\u4e86\u5bf9\u4ed8\u58a8\u767d\uff0c\u624d\u60f3\u5230\u51b7\u9999\u56ed\u53bb\u7684\uff1f\u201d \u3000\u3000\u897f\u95e8\u5341\u4e09\u9053\uff1a\u201c\u96be\u9053\u4e0d\u662f\uff1f\u201d \u3000\u3000\u4e01\u9e9f\u9053\uff1a\u201c\u5c31\u7b97\u6839\u672c\u6ca1\u6709\u58a8\u767d\u8fd9\u4e2a\u4eba\uff0c\u6211\u4fdd\u8bc1\u4ed6\u8fd8\u662f\u4e00\u6837\u8981\u5230\u51b7\u9999\u56ed\u53bb\u3002\u201d \u3000\u3000\u897f\u95e8\u5341\u4e09\u76ee\u5149\u95ea\u52a8\uff0c\u8bf4\u9053\uff1a\u201c\u5982\u679c\u4ed6\u4e0d\u627e\u4f60\uff0c\u4f60\u4e5f\u4e00\u6837\u8981\u53bb\u63a2\u542c\u5357\u6d77\u5a18\u5b50\u7684\u884c\u8e2a\uff1f\u201d \u3000\u3000\u4e01\u9e9f\u9053\uff1a\u201c\u4e00\u70b9\u4e5f\u4e0d\u9519\uff01\u201d \u3000\u3000\u897f\u95e8\u5341\u4e09\u9053\uff1a\u201c\u662f\u4e3a\u4e86\u53e6\u5916\u4e00\u4ef6\u4e8b\uff0c\u90a3\u624d\u662f\u771f\u6b63\u7684\u5927\u4e8b\uff1f\u201d \u3000\u3000\u4e01\u9e9f\u70b9\u5934\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u897f\u95e8\u5341\u4e09\u7684\u773c\u775b\u4eae\u4e86\uff0c\u9053\uff1a\u201c\u5357\u6d77\u5a18\u5b50\u83ab\u975e\u4e5f\u662f\u4e3a\u4e86\u8fd9\u4ef6\u4e8b\u624d\u6765\u7684\uff1f\u201d \u3000\u3000\u4e01\u9e9f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u603b\u7b97\u53d8\u5f97\u806a\u660e\u4e86\u4e9b\u3002\u201d \u3000\u3000\u897f\u95e8\u5341\u4e09\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u4e0d\u4f46\u80fd\u4ee4\u4f60\u4eec\u8001\u5934\u5b50\u548c\u4f60\u51fa\u624b\uff0c\u800c\u4e14\u628a\u5df2\u7ecf\u5931\u8e2a\u4e86\u4e09\u5341\u5e74\u7684\u5357\u6d77 \u5a18\u5b50\u60ca\u52a8\u51fa\u6765\uff0c\u770b\u6765\u5012\u771f\u662f\u4ef6\u5927\u4e8b\uff01\u201d \u3000\u3000\u4ed6\u7684\u8138\u5df2\u56e0\u5174\u594b\u800c\u53d1\u7ea2\uff0c\u4ed6\u663e\u7136\u4e5f\u662f\u4e2a\u4e0d\u7518\u5bc2\u5bde\u7684\u5c11\u5e74\u3002 \u3000\u3000\u4e01\u9e9f\u7684\u773c\u775b\u91cc\u4e5f\u5728\u53d1\u5149\uff0c\u9053\uff1a\u201c\u9664\u4e86\u4f60\u4eec\u77e5\u9053\u7684\u8fd9\u4e9b\u4eba\u5916\uff0c\u636e\u6211\u6240\u77e5\uff0c\u4e94\u5929\u4e4b\u5185\uff0c\u81f3\u5c11 \u8fd8\u6709\u516d\u4e03\u4e2a\u4eba\uff0c\u8981\u8d76\u5230\u51b7\u9999\u56ed\u53bb\uff01\u201d \u3000\u3000\u897f\u95e8\u5341\u4e09\u9053\uff1a\u201c\u516d\u4e03\u4e2a\u4ec0\u4e48\u6837\u7684\u4eba\uff1f\u201d \u3000\u3000\u4e01\u9e9f\u8bf4\u9053\uff1a\u201c\u5f53\u7136\u90fd\u662f\u5f88\u6709\u4e24\u4e0b\u5b50\u7684\u4eba\u3002\u201d \u3000\u3000\u897f\u95e8\u5341\u4e09\u9053\uff1a\u201c\u4ed6\u4eec\u4e5f\u77e5\u9053\u8001\u5934\u5b50\u8fd9\u6b21\u5df2\u51c6\u5907\u51fa\u624b\uff1f\u201d \u3000\u3000\u4e01\u9e9f\u6de1\u6de1\u9053\uff1a\u201c\u8fd9\u4e9b\u4eba\u5e74\u7eaa\u867d\u7136\u4e0d\u5927\uff0c\u4f46\u672a\u5fc5\u4f1a\u5c06\u4f60\u4eec\u7684\u8001\u5934\u5b50\u770b\u5728\u773c\u91cc\u3002\u201d \u3000\u3000\u897f\u95e8\u5341\u4e09\u52c9\u5f3a\u7b11\u4e86\u7b11\u9053\uff1a\u201c\u8001\u5934\u5b50\u4e5f\u5e76\u4e0d\u662f\u4e2a\u5bb9\u6613\u5bf9\u4ed8\u7684\u4eba\u3002\u201d \u3000\u3000\u4e01\u9e9f\u9053\uff1a\u201c\u53ef\u662f\u6c5f\u6e56\u4e2d\u540e\u8d77\u4e00\u4ee3\u7684\u9ad8\u624b\uff0c\u5374\u6ca1\u6709\u51e0\u4e2a\u4eba\u770b\u5f97\u8d77\u4ed6\u7684\u3001\u6b63\u5982\u4ed6\u4e5f\u770b\u4e0d\u8d77\u8fd9 \u4e9b\u5e74\u8f7b\u4eba\u4e00\u6837\u3002\u201d \u3000\u3000\u897f\u95e8\u5341\u4e09\u5fcd\u4e0d\u4f4f\u9053\uff1a\u201c\u4e0d\u7ba1\u600e\u4e48\u6837\uff0c\u5e74\u8f7b\u4eba\u7684\u7ecf\u9a8c\u603b\u662f\u6bd4\u8f83\u5dee\u4e9b\u3002\u201d \u3000\u3000\u4e01\u78f7\u9053\uff1a\u201c\u7ecf\u9a8c\u5e76\u4e0d\u662f\u51b3\u5b9a\u80dc\u8d1f\u7684\u6700\u5927\u5173\u952e\uff01\u201d \u3000\u3000\u897f\u95e8\u5341\u4e09\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u4e01\u9e9f\u9053\uff1a\u201c\u636e\u6211\u6240\u77e5\uff0c\u8fd9\u6b21\u53ea\u8981\u662f\u6562\u5230\u51b7\u9999\u56ed\u53bb\u7684\u4eba\uff0c\u7edd\u6ca1\u6709\u4e00\u4e2a\u4eba\u6b66\u529f\u662f\u5728\u536b\u5929\u9e4f\u4e4b \u4e0b\u7684\uff0c\u5c24\u5176\u662f\u5176\u4e2d\u4e00\u4e2a\u2026\u2026\u201d \u3000\u3000\u897f\u95e8\u5341\u4e09\u9053\uff1a\u201c\u4f60\uff1f\u201d \u3000\u3000\u4e01\u9e9f\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u6211\u672c\u6765\u5f53\u7136\u4e5f\u6709\u91ce\u5fc3\u7684\uff0c\u4f46\u81ea\u4ece\u77e5\u9053\u8fd9\u4e2a\u4eba\u8981\u6765\u540e\uff0c\u6211\u5df2\u51c6\u5907\u5728\u65c1 \u8fb9\u770b\u770b\u70ed\u95f9\u5c31\u7b97\u4e86\u3002\u201d\u897f\u95e8\u5341\u4e09\u76b1\u7709\u9053\uff1a\u201c\u8fde\u4f60\u4e5f\u670d\u4ed6\uff1f\u201d \u3000\u3000\u4e01\u9e9f\u53c8\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u8bf4\u8fc7\uff0c\u6211\u662f\u4e2a\u5f88\u6709\u81ea\u77e5\u4e4b\u660e\u7684\u4eba\u3002\u201d \u3000\u3000\u897f\u95e8\u5341\u4e09\u663e\u5f97\u6709\u70b9\u4e0d\u670d\u6c14\u7684\u6837\u5b50\uff0c\u9053\uff1a\u201c\u90a3\u4e2a\u4eba\u7a76\u7adf\u662f\u8c01\uff1f\u201d \u3000\u3000\u4e01\u9e9f\u6162\u6162\u5730\u559d\u4e86\u53e3\u9152\uff0c\u60a0\u7136\u9053\uff1a\u201c\u4f60\u6709\u6ca1\u6709\u542c\u8bf4\u8fc7\u5c0f\u674e\u98de\u5200\uff1f\u201d \u3000\u3000\u897f\u95e8\u5341\u4e09\u8038\u7136\u52a8\u5bb9\uff0c\u51e0\u4e4e\u8fde\u624b\u91cc\u7684\u9152\u676f\u90fd\u62ff\u4e0d\u7a33\u4e86\u3002 \u3000\u3000\u201c\u5c0f\u674e\u98de\u5200\uff01\u201d \u3000\u3000\u8fd9\u56db\u4e2a\u5b57\u672c\u8eab\u5c31\u4eff\u4f5b\u6709\u79cd\u6151\u4eba\u7684\u9b54\u529b\u3002 \u3000\u3000\u897f\u95e8\u5341\u4e09\u5931\u58f0\u8bf4\u9053\uff1a\u201c\u5c0f\u674e\u98de\u5200\u4e5f\u8981\u6765\uff1f\u201d \u3000\u3000\u4e01\u9e9f\u53c8\u7b11\u4e86\u7b11\uff0c\u6de1\u6de1\u9053\uff1a\u201c\u5c0f\u674e\u98de\u5200\u82e5\u4e5f\u8981\u6765\uff0c\u4f60\u4eec\u7684\u8001\u5934\u5b50\u548c\u5343\u9762\u89c2\u97f3\uff0c\u53ea\u6015\u90fd\u4e5f\u8981 \u8eb2\u5230\u516b\u5343\u91cc\u5916\u53bb\u4e86\u3002\u201d \u3000\u3000\u897f\u95e8\u5341\u4e09\u677e\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u6211\u4e5f\u77e5\u9053\u5c0f\u674e\u63a2\u82b1\u5df2\u6709\u591a\u5e74\u4e0d\u95ee\u6c5f\u6e56\u4e2d\u7684\u4e8b\uff0c\u6709\u4eba\u751a\u81f3\u8bf4\uff0c\u4ed6 \u4e5f\u8ddf\u6614\u65e5\u7684\u540d\u4fa0\u6c88\u6d6a\u90a3\u4e9b\u4eba\u4e00\u6837\uff0c\u5230\u4e86\u6d77\u5916\u7684\u4ed9\u5c71\uff0c\u5578\u50b2\u4e91\u971e\uff0c\u6210\u4e86\u5730\u4e0a\u7684\u6563\u4ed9\u3002\u201d \u3000\u3000\u4e01\u9e9f\u9053\uff1a\u201c\u4ed6\u5c31\u662f\u666e\u5929\u4e4b\u4e0b\uff0c\u552f\u4e00\u5f97\u5230\u8fc7\u5c0f\u674e\u98de\u5200\u771f\u4f20\u7684\u4eba\u3002\u201d \u3000\u3000\u897f\u95e8\u5341\u4e09\u53c8\u4e0d\u7981\u8038\u7136\u52a8\u5bb9\uff0c\u9053\uff1a\u201c\u4f46\u6c5f\u6e56\u4e2d\u4e3a\u4ec0\u4e48\u4ece\u6765\u4e5f\u6ca1\u6709\u4eba\u542c\u8bf4\u8fc7\u5c0f\u674e\u98de\u5200\u6709\u5f92 \u5f1f\uff1f\u201d \u3000\u3000\u4e01\u9e9f\u9053\uff1a\u201c\u56e0\u4e3a\u4ed6\u4e95\u6ca1\u6709\u771f\u6b63\u62dc\u5728\u5c0f\u674e\u63a2\u82b1\u95e8\u4e0b\uff0c\u4ed6\u548c\u5c0f\u674e\u63a2\u82b1\u7684\u5173\u7cfb\uff0c\u6211\u4e5f\u662f\u6700\u8fd1\u624d \u77e5\u9053\u7684\u3002\u201d \u3000\u3000\u897f\u95e8\u5341\u4e09\u9053\uff1a\u201c\u6211\u4eec\u600e\u4e48\u8fd8\u4e0d\u77e5\u9053\uff1f\u201d \u3000\u3000\u4e01\u9e9f\u6de1\u6de1\u9053\uff0c\u201c\u8fd9\u4e5f\u8bb8\u53ea\u56e0\u4e3a\u4f60\u4eec\u90fd\u5403\u5f97\u592a\u9971\u4e86\u3002\u201d \u3000\u3000\u897f\u95e8\u5341\u4e09\u82e6\u7b11\uff0c\u5374\u8fd8\u662f\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u201d \u3000\u3000\u4e01\u9e9f\u53c8\u6162\u6162\u5730\u559d\u4e86\u53e3\u9152\uff0c\u624d\u7f13\u7f13\u9053\uff1a\u201c\u4ed6\u59d3\u53f6\uff0c\u53eb\u53f6\u5f00\u3002\u201d \u3000\u3000\u53f6\u5f00\uff01\u897f\u95e8\u5341\u4e09\u6c89\u9ed8\u7740\uff0c\u773c\u775b\u91cc\u95ea\u95ea\u53d1\u5149\uff0c\u663e\u7136\u5df2\u51b3\u5b9a\u5c06\u8fd9\u540d\u5b57\u8bb0\u5728\u5fc3\u91cc\u3002 \u3000\u3000\u4e01\u9e9f\u53c8\u9053\uff1a\u201c\u53f6\u5f00\u867d\u7136\u4e86\u4e0d\u8d77\uff0c\u53e6\u5916\u90a3\u4e9b\u5e74\u8f7b\u4eba\u4e5f\u540c\u6837\u5f88\u53ef\u6015\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u53c8\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u662f\u7c89\u90ce\u541b\uff0c\u6211\u662f\u98ce\u90ce\u541b\uff0c\u4f60\u77e5\u4e0d\u77e5\u9053\u53e6\u5916\u8fd8\u6709\u51e0\u4e2a\u90ce\u541b\uff1f\u201d \u3000\u3000\u897f\u95e8\u5341\u4e09\u70b9\u4e86\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6211\u77e5\u9053\u6709\u4e2a\u6728\u90ce\u541b\uff0c\u6709\u4e2a\u94c1\u90ce\u541b\uff0c\u597d\u50cf\u8fd8\u6709\u4e2a\u9b3c\u90ce\u541b\u3002\u201d \u3000\u3000\u4e01\u9e9f\u60a0\u7136\u9053\uff1a\u201c\u8fd9\u6b21\u4f60\u8bf4\u4e0d\u5b9a\u4e5f\u53ef\u89c1\u5230\u4ed6\u4eec\u7684\uff0c\u53ea\u4e0d\u8fc7\u7b49\u4f60\u89c1\u5230\u4ed6\u4eec\u65f6\uff0c\u4e5f\u8bb8\u5c31\u4f1a\u540e\u6094 \u4e86\u3002\u201d \u3000\u3000\u897f\u95e8\u5341\u4e09\u9053\uff1a\u201c\u540e\u6094\uff1f\u201d \u3000\u3000\u4e01\u9e9f\u7684\u773c\u775b\u91cc\u5ffd\u7136\u9732\u51fa\u79cd\u5f88\u5947\u602a\u7684\u8868\u60c5\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u56e0\u4e3a\u65e0\u8bba\u8c01\u89c1\u5230\u8fd9\u4e9b\u4eba\uff0c\u90fd\u4e0d\u4f1a\u597d \u53d7\u7684\uff0c\u6240\u4ee5\u4f60\u8fd8\u662f\u6c38\u8fdc\u83ab\u8981\u89c1\u5230\u4ed6\u4eec\u7684\u597d\u3002\u201d \u3000\u3000\u591c\uff0c\u65e0\u4e91\u65e0\u6708\u3002 \u3000\u3000\u9a6c\u8f66\u5df2\u505c\u5728\u51b7\u9999\u56ed\u540e\u7684\u4e00\u4e2a\u8349\u68da\u91cc\uff0c\u8fd9\u8349\u68da\u7adf\u597d\u50cf\u662f\u4e3a\u4ed6\u4eec\u51c6\u5907\u597d\u5728\u8fd9\u91cc\u7684\u3002 \u3000\u3000\u90a3\u4e00\u53cc\u53ef\u7231\u7684\u5b6a\u751f\u59d0\u59b9\uff0c\u5374\u5df2\u8737\u66f2\u7740\u8eab\u5b50\uff0c\u9760\u5728\u89d2\u843d\u91cc\u7761\u7740\u4e86\u3002 \u3000\u3000\u897f\u95e8\u5341\u4e09\u770b\u7740\u59b9\u59b9\u5df2\u5b8c\u5168\u6210\u719f\u7684\u80f4\u4f53\uff0c\u5fcd\u4e0d\u4f4f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4eca\u5929\u665a\u4e0a\uff0c\u6211\u4eec\u96be\u9053\u5c31 \u6b47\u5728\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u4e01\u9e9f\u70b9\u4e86\u70b9\u5934\uff0c\u4ef0\u5934\u9053\uff1a\u201c\u4f60\u82e5\u5df2\u618b\u4e0d\u4f4f\uff0c\u4e0d\u59a8\u628a\u6211\u5f53\u505a\u778e\u5b50\u3002\u201d \u3000\u3000\u897f\u95e8\u5341\u4e09\u4e5f\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u6211\u5012\u8fd8\u6ca1\u6709\u6025\u6210\u8fd9\u6837\u5b50\uff0c\u53ea\u5947\u602a\u4f60\u4eca\u5929\u600e\u4e48\u4f1a\u5ffd\u7136\u53d8\u5f97\u5982\u6b64\u5b89 \u5206\u7684\uff1f\u2019\u4e01\u9e9f\u9053\uff1a\u201c\u4eca\u5929\u665a\u4e0a\u6211\u6709\u7ea6\u4f1a\u3002\u201d \u3000\u3000\u897f\u95e8\u5341\u4e09\u9053\uff1a\u201c\u6709\u7ea6\u4f1a\uff1f\u8ddf\u4ec0\u4e48\u4eba\u7ea6\u4f1a\uff1f\u201d \u3000\u3000\u4e01\u9e9f\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u5f53\u7136\u662f\u4e00\u4e2a\u5973\u4eba\u3002\u201d \u3000\u3000\u897f\u95e8\u5341\u4e09\u7acb\u523b\u6025\u6025\u95ee\u9053\uff1a\u201c\u5979\u957f\u5f97\u600e\u4e48\u6837\uff1f\u201d\u4e01\u9e9f\u7b11\u5f97\u5f88\u795e\u79d8\u9053\uff1a\u201c\u957f\u5f97\u5f88\u7f8e\u3002\u201d \u3000\u3000\u897f\u95e8\u5341\u4e09\u66f4\u6025\u4e86\uff0c\u9053\uff1a\u201c\u96be\u9053\u4f60\u60f3\u4e00\u4e2a\u4eba\u6e9c\u53bb\uff0c\u628a\u6211\u7529\u5728\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u4e01\u9e9f\u9053\uff1a\u201c\u4f60\u8981\u53bb\u4e5f\u884c\u3002\u201d \u3000\u3000\u897f\u95e8\u5341\u4e09\u9053\uff1a\u201c\u6211\u5c31\u77e5\u9053\u4f60\u4e0d\u662f\u4e2a\u91cd\u8272\u8f7b\u53cb\u7684\u4eba\u3002\u201d \u3000\u3000\u4e01\u9e9f\u5ffd\u7136\u9053\uff1a\u201c\u53ea\u4e0d\u8fc7\uff0c\u6211\u4eec\u8fd9\u4e00\u53bb\uff0c\u672a\u5fc5\u80fd\u6d3b\u7740\u53e3\u6765\u7684\u3002\u201d \u3000\u3000\u897f\u95e8\u5341\u4e09\u52a8\u5bb9\u9053\uff1a\u201c\u4f60\u7ea6\u7684\u662f\u8c01\uff1f\u201d \u3000\u3000\u4e01\u9e9f\u9053\uff1a\u201c\u5343\u9762\u89c2\u97f3\uff0c\u5357\u6d77\u5a18\u5b50\u3002\u201d \u3000\u3000\u897f\u95e8\u5341\u4e09\u6014\u4f4f\u3002 \u3000\u3000\u4e01\u9e9f\u7528\u773c\u89d2\u779f\u7740\u4ed6\uff0c\u9053\uff1a\u201c\u4f60\u8fd8\u60f3\u4e0d\u60f3\u53bb\uff1f\u201d \u3000\u3000\u897f\u95e8\u5341\u4e09\u56de\u7b54\u5012\u5f88\u5e72\u8106\uff1a\u201c\u4e0d\u60f3\u3002\u201d \u3000\u3000\u4ed6\u53c8\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u4f60\u771f\u7684\u51c6\u5907\u4eca\u5929\u665a\u4e0a\u5c31\u53bb\uff1f\u201d \u3000\u3000\u4e01\u9e9f\u8bf4\u9053\uff1a\u201c\u6211\u4e5f\u6025\u7740\u60f3\u770b\u770b\u8fd9\u4f4d\u98a0\u5012\u4f17\u751f\u7684\u5357\u6d77\u5a18\u5b50\u7a76\u7adf\u662f\u4e2a\u4ec0\u4e48\u6837\u7684\u7f8e\u4eba\u3002\u201d \u3000\u3000\u897f\u95e8\u5341\u4e09\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u73b0\u5728\u8fd8\u7b49\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e01\u9e9f\u9053\uff1a\u201c\u7b49\u4e00\u4e2a\u4eba\u3002\u201d \u3000\u3000\u897f\u95e8\u5341\u4e09\u9053\uff1a\u201c\u7b49\u8c01\uff1f\u201d \u3000\u3000\u8fd9\u4e24\u4e2a\u5b57\u521a\u8bf4\u51fa\u6765\uff0c\u4ed6\u5c31\u542c\u89c1\u5916\u9762\u90a3\u8f66\u592b\u5728\u5f39\u6307\u4f5c\u54cd\u3002 \u3000\u3000\u4e01\u9e9f\u7684\u773c\u775b\u53c8\u53d1\u4eae\u4e86\uff0c\u9053\uff1a\u201c\u6765\u4e86\uff01\u201d \u3000\u3000\u897f\u95e8\u5341\u4e09\u63a8\u5f00\u8f66\u7a97\uff0c\u5c31\u770b\u89c1\u8fdc\u5904\u9ed1\u6697\u4e2d\u6709\u4e2a\u4eba\u8eab\u62ab\u84d1\u8863\uff0c\u5934\u6234\u7b20\u5e3d\uff0c\u624b\u91cc\u63d0\u7740\u4e09\u6839\u957f\u7684 \u7af9\u7aff\uff0c\u7af9\u7aff\u5728\u5730\u4e0a\u4e00\u70b9\uff0c\u4ed6\u7684\u4eba\u5df2\u63a0\u8fc7\u4e94\u4e08\uff0c\u8f7b\u98d8\u98d8\u5730\u843d\u5728\u8349\u68da\u5916\u3002 \u3000\u3000\u4e01\u9e9f\u9053\uff1a\u201c\u4f60\u770b\u4ed6\u8f7b\u529f\u5982\u4f55\uff1f\u201d \u3000\u3000\u897f\u95e8\u5341\u4e09\u82e6\u7b11\u9053\uff1a\u201c\u8fd9\u91cc\u7684\u4eba\u770b\u6765\u679c\u7136\u5168\u90e8\u6709\u4e24\u4e0b\u5b50\u3002\u201d \u3000\u3000\u8fd9\u65f6\u90a3\u4e2a\u4eba\u5df2\u89e3\u4e0b\u4e86\u84d1\u8863\uff0c\u6302\u5728\u67f1\u5b50\u4e0a\uff0c\u5fae\u7b11\u7740\u9053\uff1a\u201c\u6211\u8fd9\u5012\u4e0d\u662f\u4e3a\u4e86\u8981\u70ab\u8000\u8f7b\u529f\uff0c\u53ea \u4e0d\u8fc7\u6015\u5728\u96ea\u5730\u4e0a\u7559\u4e0b\u8db3\u8ff9\u800c\u5df2\u3002\u201d \u3000\u3000\u4e86\u9e9f\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u4f60\u505a\u4e8b\u8fd9\u4e48\u8c28\u614e\u3002\u201d \u3000\u3000\u8fd9\u4eba\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u8fd8\u60f3\u591a\u6d3b\u4e24\u5e74\u3002\u201d \u3000\u3000\u4ed6\u6162\u6162\u5730\u8d70\u8fc7\u6765\uff0c\u53c8\u8131\u4e0b\u4e86\u5934\u4e0a\u7684\u7b20\u5e3d\uff0c\u897f\u95e8\u5341\u4e09\u8fd9\u624d\u770b\u51fa\u4ed6\u662f\u4e2a\u4e09\u5341\u591a\u5c81\u7684\u4e2d\u5e74\u4eba\uff0c \u72d0\u76ae\u888d\u5b50\u5916\uff0c\u8fd8\u5957\u7740\u4ef6\u84dd\u5e03\u7f69\u888d\uff0c\u770b\u6765\u5c31\u50cf\u662f\u4e2a\u89c4\u89c4\u77e9\u77e9\u7684\u751f\u610f\u4eba\uff0c\u53ea\u4e0d\u8fc7\u4e00\u53cc\u70af\u70af\u6709\u795e\u7684 \u773c\u775b\u91cc\uff0c\u603b\u662f\u5e26\u7740\u6781\u7cbe\u660e\u800c\u72e1\u733e\u7684\u5fae\u7b11\u3002 \u3000\u3000\u4e01\u9e9f\u4e5f\u5fae\u7b11\u7740\u9053\uff1a\u201c\u8fd9\u4f4d\u5c31\u662f\u51b7\u9999\u56ed\u91cc\u7684\u6768\u5927\u603b\u7ba1\u6768\u8f69\u3002\u201d \u3000\u3000\u6768\u8f69\u770b\u4e86\u897f\u95e8\u5341\u4e09\u4e00\u773c\uff0c\u63a5\u7740\u9053\uff1a\u201c\u8fd9\u4f4d\u60f3\u5fc5\u5c31\u662f\u536b\u516b\u592a\u7237\u95e8\u4e0b\u7684\u9ad8\u8db3\u5341\u4e09\u516c\u5b50\uff0c\u5e78\u4f1a \u5e78\u4f1a\u3002\u201d \u3000\u3000\u897f\u95e8\u5341\u4e09\u5403\u60ca\u5730\u770b\u7740\u4ed6\uff0c\u5fcd\u4e0d\u4f4f\u63a5\u7740\u95ee\u9053\uff1a\u201c\u4f60\u5c31\u662f\u6211\u516d\u54e5\u4e0a\u6b21\u6765\u89c1\u8fc7\u7684\u90a3\u4e2a\u6768\u8f69\uff1f\u201d \u3000\u3000\u6768\u8f69\u9053\uff1a\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u897f\u95e8\u5341\u4e09\u82e6\u7b11\u9053\uff1a\u201c\u4ed6\u5c45\u7136\u8bf4\u4f60\u53ea\u4e0d\u8fc7\u662f\u4e2a\u80c6\u5c0f\u7684\u751f\u610f\u4eba\uff0c\u770b\u6765\u4ed6\u7684\u786e\u5403\u5f97\u592a\u9971\u4e86\u3002\u201d \u3000\u3000\u6768\u8f69\u6de1\u6de1\u9053\uff1a\u201c\u6211\u672c\u6765\u5c31\u662f\u4e2a\u80c6\u5c0f\u7684\u751f\u610f\u4eba\uff0c\u4ed6\u6ca1\u6709\u770b\u9519\u3002\u201d \u3000\u3000\u4e01\u9e9f\u9053\uff1a\u201c\u6211\u5374\u770b\u9519\u4e86\u3002\u201d \u3000\u3000\u6768\u8f69\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u4e01\u9e9f\u7b11\u9053\uff1a\u201c\u6211\u8fd8\u4ee5\u4e3a\u4f60\u5c31\u662f\u2018\u98de\u72d0\u2019\u6768\u5929\u54e9\u3002\u201d \u3000\u3000\u6768\u8f69\u76b1\u4e86\u76b1\u7709\uff0c\u897f\u95e8\u5341\u4e09\u4e5f\u4e0d\u7981\u4e3a\u4e4b\u52a8\u5bb9\u3002 \u3000\u3000\u201c\u98de\u72d0\u201d\u6768\u5929\u8fd9\u540d\u5b57\u4ed6\u542c\u8fc7\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u6c5f\u6e56\u4e2d\u6ca1\u6709\u542c\u8bf4\u8fc7\u8fd9\u540d\u5b57\u7684\u4eba\u8fd8\u5f88\u5c11\uff0c\u4ed6\u4e0d\u4f46\u662f\u8fd1\u5341\u5e74\u6765\u6c5f\u6e56\u4e2d\u6700\u51fa\u540d\u7684\u72ec\u884c \u76d7\uff0c\u4e5f\u662f\u8fd1\u5341\u5e74\u6765\u8f6f\u529f\u7ec3\u5f97\u6700\u597d\u7684\u4e00\u4e2a\uff0c\u636e\u8bf4\u4f60\u5c31\u7b97\u7528\u624b\u94d0\u811a\u9563\u9501\u4f4f\u4e86\u4ed6\uff0c\u518d\u628a\u4ed6\u5168\u8eab\u90fd\u7528 \u725b\u7b4b\u6346\u5f97\u7d27\u7d27\u7684\uff0c\u5173\u5728\u4e00\u95f4\u53ea\u6709\u4e00\u4e2a\u5c0f\u6c14\u7a97\u7684\u7262\u623f\u91cc\uff0c\u4ed6\u8fd8\u662f\u4e00\u6837\u80fd\u9003\u5f97\u51fa\u53bb\u3002 \u3000\u3000\u50cf\u8fd9\u4e48\u6837\u4e00\u4e2a\u4eba\uff0c\u5c45\u7136\u80af\u5230\u51b7\u9999\u56ed\u91cc\u6765\u505a\u7ba1\u4e8b\u7684\uff0c\u5f53\u7136\u7edd\u4e0d\u4f1a\u6ca1\u6709\u4f01\u56fe\u3002 \u3000\u3000\u4ed6\u6240\u56fe\u8c0b\u7684\uff0c\u5f53\u7136\u4e5f\u51b3\u4e0d\u4f1a\u662f\u4ef6\u5f88\u666e\u901a\u7684\u4e8b\u3002 \u3000\u3000\u897f\u95e8\u5341\u4e09\u5ffd\u7136\u53d1\u89c9\u8fd9\u4ef6\u4e8b\u5df2\u53d8\u5f97\u8d8a\u6765\u8d8a\u6709\u8da3\uff0c\u4e5f\u540c\u6837\u53d8\u5f97\u8d8a\u6765\u8d8a\u53ef\u6015\u4e86\u3002 \u3000\u3000\u4e01\u9e9f\u597d\u50cf\u4e5f\u77e5\u9053\u81ea\u5df2\u592a\u591a\u5634\uff0c\u7acb\u523b\u6539\u53d8\u8bdd\u9898\uff0c\u9053\uff1a\u201c\u90a3\u4f4d\u5357\u6d77\u5a18\u5b50\u5df2\u6765\u4e86\uff1f\u201d \u3000\u3000\u6768\u8f69\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u521a\u5230\u3002\u201d \u3000\u3000\u4e01\u9e9f\u9053\uff1a\u201c\u4f60\u770b\u89c1\u4e86\u5979\uff1f\u201d \u3000\u3000\u6768\u8f69\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u6211\u53ea\u770b\u89c1\u5979\u95e8\u4e0b\u7684\u4e00\u4e9b\u5bb6\u4e01\u548c\u4e2b\u5934\u3002\u201d \u3000\u3000\u4e01\u9e9f\u9053\uff1a\u201c\u5979\u4eec\u4e00\u5171\u6709\u591a\u5c11\u4eba\uff1f\u201d\u6768\u8f69\u9053\uff1a\u201c\u4e09\u5341\u4e03\u4e2a\u3002\u201d \u3000\u3000\u4e01\u9e9f\u9053\uff1a\u201c\u90a3\u4e2a\u4f1a\u5403\u5200\u7684\u5973\u4eba\uff0c\u5728\u4e0d\u5728\uff1f\u201d \u3000\u3000\u6768\u8f69\u53c8\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u5979\u53eb\u94c1\u59d1\uff0c\u5728\u90a3\u4e9b\u4eba\u91cc\u9762\uff0c\u597d\u50cf\u4e5f\u662f\u4e2a\u7ba1\u4e8b\u7684\u3002\u201d \u3000\u3000\u4e01\u9e9f\u7b11\u9053\uff1a\u201c\u83ab\u5fd8\u8bb0\u4f60\u4e5f\u662f\u505a\u7ba1\u4e8b\u7684\uff0c\u4f60\u4eec\u4e24\u4e2a\u5c82\u975e\u662f\u5929\u751f\u7684\u4e00\u5bf9\u3002\u201d \u3000\u3000\u6768\u8f69\u677f\u7740\u8138\uff0c\u4e0d\u5f00\u53e3\uff0c\u770b\u6765\u4ed6\u5e76\u4e0d\u662f\u4e2a\u559c\u6b22\u5f00\u73a9\u7b11\u7684\u4eba\u3002 \u3000\u3000\u4e01\u9e9f\u5e72\u54b3\u4e86\u4e24\u58f0\uff0c\u53ea\u597d\u53c8\u6539\u53e3\u95ee\u9053\uff1a\u201c\u4ed6\u4eec\u4f4f\u5728\u54ea\u4e2a\u9662\u5b50\u91cc\uff1f\u201d \u3000\u3000\u6768\u8f69\u9053\uff1a\u201c\u542c\u6d9b\u697c\u3002\u201d \u3000\u3000\u4e86\u9e9f\u9053\uff1a\u201c\u73b0\u5728\u8ddd\u79bb\u5b50\u5348\u65f6\u8fd8\u6709\u591a\u5c11\u65f6\u5019\uff1f\u201d \u3000\u3000\u6768\u8f69\u9053\uff1a\u201c\u5df2\u4e0d\u5230\u534a\u4e2a\u65f6\u8fb0\uff0c\u91cc\u9762\u6709\u6572\u66f4\u7684\u4eba\uff0c\u4f60\u4e00\u8fdb\u53bb\u5c31\u53ef\u4ee5\u542c\u89c1\u3002\u201d \u3000\u3000\u4e01\u78f7\u7684\u773c\u775b\u53c8\u53d1\u51fa\u5149\uff0c\u9053\uff1a\u201c\u770b\u6765\u6211\u518d\u559d\u676f\u9152\uff0c\u5c31\u53ef\u4ee5\u52a8\u8eab\u4e86\u3002\u201d \u3000\u3000\u6768\u8f69\u770b\u7740\u4ed6\uff0c\u8fc7\u4e86\u5f88\u4e45\uff0c\u5ffd\u7136\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u8fd9\u6b21\u5408\u4f19\uff0c\u56e0\u4e3a\u6211\u9700\u8981\u4f60\uff0c\u4f60\u4e5f\u9700\u8981\u6211\u3002\u201d \u3000\u3000\u4e01\u9e9f\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u6211\u4eec\u672c\u6765\u5c31\u662f\u597d\u4f19\u4f34\u3002\u201d \u3000\u3000\u6768\u8f69\u6de1\u6de1\u9053\uff1a\u201c\u4f46\u6211\u4eec\u5374\u4e0d\u662f\u670b\u53cb\uff0c\u8fd9\u4e00\u70b9\u4f60\u6700\u597d\u8bb0\u4f4f\u3002\u201d \u3000\u3000\u4ed6\u4e0d\u8ba9\u4e01\u9e9f\u518d\u8bf4\u8bdd\uff0c\u5c31\u6162\u6162\u5730\u8f6c\u8fc7\u8eab\uff0c\u6234\u8d77\u7b20\u5e3d\uff0c\u62ab\u4e0a\u84d1\u8863\uff0c\u624b\u91cc\u7684\u7af9\u7aff\u8f7b\u8f7b\u4e00\u70b9\uff0c\u4eba \u5df2\u5728\u4e94\u4e08\u5916\uff0c\u7136\u540e\u5c31\u5ffd\u7136\u770b\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u4e01\u9e9f\u76ee\u9001\u4ed6\u8eab\u5f71\u6d88\u5931\uff0c\u5fae\u7b11\u7740\u9053\uff1a\u201c\u597d\u8eab\u624b\uff0c\u679c\u7136\u4e0d\u6127\u662f\u2018\u98de\u72d0\u2019\u3002\u201d \u3000\u3000\u897f\u95e8\u5341\u4e09\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u4ed6\u771f\u7684\u5c31\u662f\u90a3\u4e2a\u2018\u98de\u72d0\u2019\u6768\u5929\uff1f\u201d \u3000\u3000\u4e01\u9e9f\u9053\uff1a\u201c\u98de\u72d0\u53ea\u6709\u4ed6\u8fd9\u4e00\u4e2a\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u53c8\u53f9\u4e86\u53e3\u6c14\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u4e5f\u5e78\u597d\u53ea\u6709\u4ed6\u8fd9\u4e48\u6837\u4e00\u4e2a\u3002\u201d \u3000\u3000\u8131\u4e0b\u8c82\u88d8\uff0c\u91cc\u9762\u5c31\u662f\u5957\u7d27\u8eab\u7684\u591c\u884c\u8863\uff0c\u662f\u9ed1\u8272\u7684\uff0c\u9ed1\u5f97\u5c31\u50cf\u662f\u8fd9\u65e0\u8fb9\u65e0\u9645\u7684\u591c\u8272\u4e00\u6837\u3002 \u3000\u3000\u4e01\u9e9f\u5df2\u8131\u4e0b\u4e86\u8c82\u88d8\uff0c\u5374\u5e76\u6ca1\u6709\u518d\u559d\u4ed6\u90a3\u6700\u540e\u7684\u4e00\u676f\u9152\u3002 \u3000\u3000\u4ed6\u7684\u773c\u6674\u95ea\u95ea\u53d1\u5149\uff0c\u8138\u4e0a\u5df2\u770b\u4e0d\u89c1\u7b11\u5bb9\u3002\u6f06\u9ed1\u7684\u591c\u884c\u8863\uff0c\u7d27\u7d27\u88f9\u5728\u4ed6\u7626\u524a\u800c\u7075\u654f\u7684\u8eab\u5b50 \u4e0a\u3002 \u3000\u3000\u5ffd\u7136\u4e4b\u95f4\uff0c\u4ed6\u50cf\u662f\u53c8\u53d8\u6210\u4e86\u53e6\u5916\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u5df2\u4e0d\u662f\u521a\u624d\u90a3\u4e2a\u653e\u8361\u4e0d\u7f81\u7684\u98ce\u6d41\u6d6a\u5b50\uff0c\u5df2\u53d8\u5f97\u975e\u5e38\u6c89\u7740\uff0c\u975e\u5e38\u53ef\u6015\u3002 \u3000\u3000\u897f\u95e8\u5341\u4e09\u51dd\u76ee\u770b\u7740\u4ed6\uff0c\u773c\u775b\u5e26\u7740\u79cd\u5f88\u5947\u602a\u7684\u8868\u60c5\uff0c\u4eff\u4f5b\u662f\u7fa1\u6155\uff0c\u53c8\u4eff\u4f5b\u662f\u5ac9\u5992\u3002 \u3000\u3000\u4e01\u9e9f\u9053\uff1a\u201c\u4f60\u6700\u597d\u5c31\u5728\u8fd9\u91cc\u7b49\u7740\uff0c\u4e00\u4e2a\u65f6\u8fb0\u4e4b\u5185\uff0c\u6211\u5c31\u4f1a\u56de\u6765\u7684\u3002\u201d \u3000\u3000\u897f\u95e8\u5341\u4e09\u5ffd\u7136\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u82e5\u4e0d\u56de\u6765\u5462\uff1f\u201d \u3000\u3000\u4e01\u9e9f\u4e5f\u7b11\u4e86\u7b11\uff0c\u6de1\u6de1\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u5c31\u53ef\u4ee5\u628a\u5979\u4eec\u4e24\u4e2a\u5168\u90fd\u5e26\u8d70\uff0c\u4f60\u5c82\u975e\u65e9\u5c31\u8fd9\u4e48\u60f3 \u4e86\uff1f\u2026\u2026\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u8bf4\u5b8c\u65f6\uff0c\u4ed6\u7684\u4eba\u5df2\u6d88\u5931\u5728\u9ed1\u6697\u91cc\u3002 \u3000\u3000\u897f\u95e8\u5341\u4e09\u4e8e\u662f\u5750\u5728\u90a3\u91cc\uff0c\u8fde\u52a8\u90fd\u6ca1\u6709\u52a8\u3002\u4ed6\u672c\u6765\u603b\u4ee5\u4e3a\u4ed6\u7684\u6b66\u529f\u7edd\u4e0d\u5728\u522b\u7684\u5e74\u8f7b\u4eba\u4e4b \u4e0b\uff0c\u73b0\u5728\u624d\u77e5\u9053\u81ea\u5df1\u60f3\u9519\u4e86\u3002\u8fd9\u4e00\u4ee3\u7684\u5e74\u8f7b\u4eba\uff0c\u8fdc\u6bd4\u4ed6\u60f3\u8c61\u4e2d\u53ef\u6015\u5f97\u591a\u3002 \u3000\u3000\u4ed6\u62ac\u8d77\u624b\uff0c\u8f7b\u629a\u7740\u81ea\u5df1\u88ab\u6253\u80bf\u4e86\u7684\u8138\uff0c\u773c\u775b\u91cc\u53c8\u9732\u51fa\u79cd\u5f88\u75db\u82e6\u7684\u8868\u60c5\u3002 \u3000\u3000\u59d0\u59d0\u672c\u6765\u597d\u50cf\u5df2\u7761\u5f97\u5f88\u6c89\uff0c\u8fd9\u65f6\u5979\u5ffd\u7136\u7ffb\u4e86\u4e2a\u8eab\uff0c\u62b1\u4f4f\u4e86\u4ed6\u7684\u817f\u3002 \u3000\u3000\u897f\u95e8\u5341\u4e09\u8fd8\u662f\u6ca1\u6709\u52a8\u3002 \u3000\u3000\u59d0\u59d0\u4e0d\u662f\u4ed6\u7684\uff0c\u59b9\u59b9\u624d\u662f\u3002 \u3000\u3000\u8c01\u77e5\u59d0\u59d0\u53c8\u5ffd\u7136\u5728\u4ed6\u817f\u4e0a\u54ac\u4e86\u4e00\u53e3\uff0c\u54ac\u5f97\u5f88\u91cd\uff0c\u5f53\u7136\u5f88\u75db\u3002 \u3000\u3000\u4f46\u897f\u95e8\u5341\u4e09\u773c\u91cc\u7684\u75db\u82e6\u4e4b\u8272\u5374\u5ffd\u7136\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u53d1\u73b0\u4e00\u4e2a\u4eba\u82e5\u60f3\u80dc\u8fc7\u522b\u4eba\uff0c\u5e76\u4e0d\u4e00\u5b9a\u8981\u9760\u6b66\u529f\u7684\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u8138\u4e0a\u53c8\u9732\u51fa\u5fae\u7b11\uff0c\u5fae\u7b11\u7740\u5c06\u4e01\u9e9f\u6ca1\u6709\u559d\u7684\u90a3\u676f\u9152\u4e00\u53e3\u6c14\u559d\u4e0b\u53bb\u2026\u2026 \u3000\u3000\u542c\u6d9b\u697c\u542c\u7684\u5e76\u4e0d\u662f\u6d77\u6d9b\u3002 \u3000\u3000\u51b7\u9999\u56ed\u91cc\u9664\u4e86\u79cd\u7740\u4e07\u682a\u6885\u82b1\u5916\uff0c\u8fd8\u6709\u7740\u51e0\u767e\u682a\u82cd\u677e\uff0c\u51e0\u5343\u7aff\u4fee\u7af9\u3002 \u3000\u3000\u542c\u6d9b\u697c\u5916\uff0c\u7af9\u6d6a\u5982\u6d77\u3002 \u3000\u3000\u4e01\u9e9f\u4f0f\u5728\u7af9\u6797\u7684\u9ed1\u6697\u5904\uff0c\u6253\u5f00\u4e86\u7cfb\u5728\u8170\u4e0a\u7684\u4e00\u53ea\u76ae\u56ca\uff0c\u62ff\u51fa\u4e86\u4e00\u652f\u55b7\u7b52\u3002 \u3000\u3000\u55b7\u7b52\u91cc\u88c5\u6ee1\u4e86\u4e00\u79cd\u9ed1\u8272\u7684\u539f\u6cb9\uff0c\u662f\u4ed6\u4ece\u5eb7\u85cf\u90a3\u8fb9\u7684\u7267\u4eba\u5904\uff0c\u7528\u76d0\u6362\u6765\u7684\u3002 \u3000\u3000\u4ed6\u65cb\u52a8\u4e86\u55b7\u7b52\u4e0a\u7684\u87ba\u65cb\u76d6\u5b50\uff0c\u6709\u98ce\u5439\u8fc7\u7684\u65f6\u5019\uff0c\u4ed6\u5c31\u5c06\u7b52\u4e2d\u7684\u539f\u6cb9\uff0c\u5f88\u4ed4\u7ec6\u5730\u55b7\u4e86\u51fa \u53bb\uff0c\u55b7\u5f97\u5f88\u7ec6\u5bc6\u3002 \u3000\u3000\u90a3\u96fe\u4e00\u822c\u7684\u6cb9\u73e0\uff0c\u5c31\u968f\u7740\u98ce\u5439\u51fa\uff0c\u6d12\u5728\u542c\u6d9b\u697c\u7684\u5c4b\u4e0a\u3002 \u3000\u3000\u7136\u540e\u4ed6\u5c31\u85cf\u8d77\u55b7\u7b52\uff0c\u53c8\u53d6\u51fa\u5341\u4f59\u7c92\u6bd4\u68a7\u6850\u5b50\u7565\u5927\u4e9b\u7684\u5f39\u4e38\uff0c\u7528\u98df\u4e2d\u4e24\u6307\u4e4b\u529b\uff0c\u5f39\u4e86\u51fa \u53bb\uff0c\u4e5f\u6253\u5728\u5bf9\u9762\u7684\u5c4b\u6a90\u4e0a\u3002 \u3000\u3000\u7a81\u7136\u95f4\uff0c\u53ea\u542c\u201c\u84ec\u201d\u7684\u4e00\u58f0\uff0c\u542c\u6d9b\u697c\u7684\u5c4b\u6a90\uff0c\u5df2\u53d8\u6210\u4e00\u7247\u706b\u6d77\uff0c\u9c9c\u7ea2\u7684\u706b\u82d7\uff0c\u7a9c\u8d77\u4e09\u4e08 \u5f00\u5916\u3002 \u3000\u3000\u8fdc\u5904\u4f20\u6765\u66f4\u9f13\uff0c\u6b63\u662f\u5b50\u65f6\u3002 \u3000\u3000\u66f4\u9f13\u58f0\u5374\u88ab\u60ca\u53eb\u58f0\u6df9\u6ca1\u3002 \u3000\u3000\u201c\u706b\uff01\u201d \u3000\u3000\u6570\u5341\u6761\u4eba\u5f71\uff0c\u60ca\u547c\u7740\u4ece\u542c\u6d9b\u697c\u91cc\u7a9c\u4e86\u51fa\u6765\uff0c\u5982\u6b64\u731b\u70c8\u7684\u706b\u52bf\uff0c\u5c31\u8fde\u6700\u9547\u9759\u7684\u4eba\u4e5f\u96be\u514d\u60ca \u60f6\u5931\u63aa\u3002 \u3000\u3000\u4e5f\u5c31\u662f\u8fd9\u4e00\u5239\u90a3\u95f4\uff0c\u4e01\u9e9f\u4ece\u697c\u540e\u4e00\u6247\u534a\u5f00\u7684\u7a97\u5b50\u91cc\uff0c\u8f7b\u70df\u822c\u63a0\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u5e03\u7f6e\u5f97\u975e\u5e38\u5e7d\u9759\u7684\u5c0f\u5385\uff0c\u9759\u6084\u65e0\u4eba\u3002 \u3000\u3000\u4e01\u9e9f\u7a81\u7136\u5927\u53eb\u3002 \u3000\u3000\u201c\u706b\uff0c\u5931\u706b\u4e86\uff01\u201d \u3000\u3000\u6ca1\u6709\u4eba\u6765\uff0c\u6ca1\u6709\u5e94\u58f0\u3002 \u3000\u3000\u4e01\u9e9f\u5df2\u63a8\u5f00\u95e8\u7a9c\u51fa\u53bb\uff0c\u4ed6\u5e76\u4e0d\u77e5\u9053\u5357\u6d77\u5a18\u5b50\u7684\u7ec3\u529f\u5904\u5728\u54ea\u91cc\uff0c\u6240\u4ee5\u4ed6\u7684\u52a8\u4f5c\u5fc5\u987b\u5feb\u3002 \u3000\u3000\u4ed6\u8fd8\u5f97\u78b0\u78b0\u8fd0\u6c14\u3002 \u3000\u3000\u4ed6\u7684\u8fd0\u6c14\u597d\u50cf\u8fd8\u4e0d\u574f\uff0c\u7b2c\u4e09\u6247\u95e8\u662f\u4ece\u91cc\u9762\u95e9\u8d77\u7684\uff0c\u4ed6\u62bd\u5200\u6311\u8d77\u95e8\u95e9\uff0c\u91cc\u9762\u662f\u4e2a\u4f5b\u5802\u3002 \u3000\u3000\u6848\u4e0a\u7684\u94dc\u7089\u91cc\uff0c\u71c3\u7740\u9f99\u9999\uff0c\u4e00\u7f15\u7f15\u9999\u70df\u7efc\u7ed5\uff0c\u4f7f\u5f97\u5e7d\u9759\u7684\u4f5b\u5802\uff0c\u66f4\u5e73\u6dfb\u4e86\u51e0\u5206\u795e\u79d8\u3002 \u3000\u3000\u9999\u6848\u540e\u9ec4\u6162\u4f4e\u5782\uff0c\u4eff\u4f5b\u4e5f\u6ca1\u6709\u4eba\u3002 \u3000\u3000\u4f46\u4e01\u9e9f\u5374\u4e0d\u4fe1\u4e00\u95f4\u4ece\u91cc\u9762\u95e9\u8d77\u95e8\u7684\u5c4b\u5b50\u91cc\uff0c\u4f1a\u6ca1\u6709\u4eba\u3002 \u3000\u3000\u4ed6\u6beb\u4e0d\u72b9\u8c6b\u5c31\u7a9c\u4e86\u8fdb\u53bb\uff0c\u4e00\u628a\u6380\u8d77\u4e86\u4f4e\u5782\u7684\u795e\u5e54\u3002 \u3000\u3000\u4ed6\u6014\u4f4f\u3002 \u3000\u3000\u795e\u9992\u540e\u7adf\u6709\u56db\u4e2a\u4eba\u3002 \u3000\u3000\u56db\u4e2a\u7a7f\u7740\u7d2b\u7f0e\u957f\u888d\u7684\u4eba\uff0c\u4e00\u5934\u9752\u4e1d\u9ad8\u9ad8\u633d\u8d77\uff0c\u8138\u4e0a\u6234\u7740\u4e2a\u7528\u6a80\u6728\u96d5\u6210\u7684\u9762\u5177\u3002 \u3000\u3000\u56db\u4e2a\u4eba\u7684\u7a7f\u675f\u6253\u626e\u7adf\u5b8c\u5168\u4e00\u6837\uff0c\u5168\u90fd\u52a8\u4e5f\u4e0d\u52a8\u5730\u76d8\u819d\u800c\u5750\uff0c\u697c\u5916\u95ea\u52a8\u7684\u706b\u5149\uff0c\u7167\u7740\u4ed6\u4eec \u8138\u4e0a\u72f0\u72de\u5446\u677f\u7684\u9762\u5177\uff0c\u66f4\u663e\u5f97\u8bf4\u4e0d\u51fa\u7684\u8be1\u79d8\u53ef\u6016\u3002 \u3000\u3000\u8fd9\u56db\u4e2a\u4eba\u5168\u90fd\u53ef\u80fd\u662f\u5357\u6d77\u5a18\u5b50\uff0c\u4f46\u5357\u6d77\u5a18\u5b50\u5374\u53ea\u6709\u4e00\u4e2a\u3002 \u3000\u3000\u4e01\u9e9f\u77e5\u9053\u8fd9\u79cd\u673a\u4f1a\u7edd\u4e0d\u4f1a\u6709\u7b2c\u4e8c\u6b21\u4e86\uff0c\u4ed6\u51b3\u5b9a\u5192\u4e00\u6b21\u9669\u3002 \u3000\u3000\u4ed6\u7a9c\u8fc7\u53bb\uff0c\u63ed\u5f00\u4e86\u7b2c\u4e00\u4eba\u7684\u9762\u5177\u3002 \u3000\u3000\u9762\u5177\u4e0b\u662f\u4e00\u5f20\u82cd\u767d\u800c\u7f8e\u4e3d\u7684\u8138\uff0c\u957f\u957f\u7684\u776b\u6bdb\uff0c\u76d6\u5728\u7d27\u95ed\u7684\u773c\u5e18\u4e0a\uff0c\u65e0\u8bba\u8c01\u90fd\u770b\u5f97\u51fa\u5979\u7edd \u4e0d\u4f1a\u8d85\u8fc7\u4e8c\u5341\uff0c\u5357\u6d77\u5a18\u5b50\u7edd\u4e0d\u4f1a\u8fd9\u4e48\u5e74\u8f7b\u3002 \u3000\u3000\u4e01\u9e9f\u5df2\u63ed\u8d77\u7b2c\u4e8c\u4eba\u7684\u3002 \u3000\u3000\u8fd9\u4eba\u7ade\u8d6b\u7136\u662f\u4e2a\u7537\u4eba\uff0c\u8138\u4e0a\u8fd8\u6709\u9752\u9eea\u9eea\u7684\u80e1\u78b4\u5b50\u3002 \u3000\u3000\u5357\u6d77\u5a18\u5b50\u5f53\u7136\u66f4\u4e0d\u4f1a\u662f\u7537\u4eba\u3002 \u3000\u3000\u7b2c\u4e09\u4e2a\u4eba\u770b\u6765\u867d\u7136\u4e5f\u5f88\u5e74\u8f7b\uff0c\u4f46\u773c\u89d2\u4e0a\u5374\u5df2\u6709\u4e86\u9c7c\u5c3e\u822c\u7684\u76b1\u7eb9\u3002 \u3000\u3000\u7b2c\u56db\u4e2a\u4eba\u662f\u4e2a\u6ee1\u9762\u76b1\u7eb9\u3001\u8fde\u5634\u90fd\u5df2\u762a\u4e86\u4e0b\u53bb\u7684\u8001\u592a\u5a46\u3002 \u3000\u3000\u4e01\u78f7\u53c8\u6014\u4f4f\u3002 \u3000\u3000\u4ed6\u4e95\u6ca1\u6709\u770b\u89c1\u4ed6\u60f3\u770b\u5230\u7684\u90a3\u5f20\u8138\uff0c\u4f46\u8fd9\u65f6\u4ed6\u5df2\u65e0\u6cd5\u518d\u505c\u7559\u4e0b\u53bb\u3002 \u3000\u3000\u4ed6\u8f6c\u8eab\u3001\u4eba\u5df2\u968f\u7740\u8fd9\u8f6c\u8eab\u4e4b\u52bf\u8dc3\u8d77\uff0c\u5c31\u5728\u8fd9\u65f6\uff0c\u4ed6\u4eff\u4f5b\u770b\u89c1\u90a3\u8138\u4e0a\u5e26\u7740\u80e1\u78b4\u5b50\u7684\u7537\u4eba\u7684 \u624b\u52a8\u4e86\u52a8\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u4e0d\u5bf9\u4e86\uff0c\u60f3\u95ea\u907f\uff0c\u4f46\u8fd9\u4eba\u7684\u51fa\u624b\u7adf\u5feb\u5f97\u4ee4\u4eba\u65e0\u6cd5\u601d\u8bae\u3002 \u3000\u3000\u4ed6\u521a\u770b\u89c1\u8fd9\u4eba\u7684\u624b\u4e00\u52a8\uff0c\u5df2\u89c9\u5f97\u8170\u4e0a\u4e00\u9635\u523a\u75db\uff0c\u5c31\u50cf\u662f\u88ab\u5c16\u9488\u8f7b\u8f7b\u523a\u4e86\u4e00\u4e0b\u3002 \u3000\u3000\u7136\u540e\u4ed6\u5c31\u8dcc\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u4f5b\u5802\u91cc\u8fd8\u662f\u90a3\u4e48\u5e7d\u96c5\uff0c\u5916\u9762\u95ea\u52a8\u7684\u706b\u5149\u5df2\u706d\u4e86\uff0c\u94dc\u7089\u4e2d\u9999\u70df\u7f00\u7ed5\uff0c\u5374\u5df2\u6362\u4e86\u79cd\u6e05\u6de1\u7684\u6c89 \u9999\u6728\u3002 \u3000\u3000\u4e01\u9e9f\u5f20\u5f00\u773c\uff0c\u5ffd\u7136\u53d1\u73b0\u81ea\u5df1\u8eab\u4e0a\u5df2\u6362\u4e86\u4ef6\u5973\u4eba\u7a7f\u7684\u4e61\u88d9\u3002 \u3000\u3000\u4ed6\u5927\u60ca\u4e4b\u4e0b\uff0c\u4f38\u624b\u6478\u4e86\u6478\u5934\u53d1\uff0c\u4ed6\u7684\u5934\u53d1\u7adf\u5df2\u88ab\u68b3\u6210\u4e86\u4e00\u79cd\u5f53\u65f6\u5973\u4eba\u6700\u559c\u6b22\u68b3\u7684\u6768\u5983\u5815 \u9a6c\u9afb\uff0c\u6b6a\u6b6a\u7684\u53d1\u9afb\uff0c\u8fd8\u63d2\u7740\u6839\u98ce\u5934\u9497\u3002 \u3000\u3000\u201c\u98ce\u90ce\u541b\u201d\u4e01\u9e9f\u4ece\u5341\u516d\u4e03\u5c81\u7684\u65f6\u5019\uff0c\u5c31\u5f00\u59cb\u95ef\u8361\u6c5f\u6e56\uff0c\u4e0d\u51fa\u4e09\u5e74\uff0c\u5df2\u535a\u5f97\u5f88\u5927\u7684\u540d\u58f0\u3002 \u3000\u3000\u6c5f\u6e56\u4e2d\u4eba\u4eba\u90fd\u77e5\u9053\uff0c\u4ed6\u4e0d\u4f46\u8f7b\u529f\u6781\u9ad8\uff0c\u800c\u4e14\u975e\u5e38\u673a\u8b66\uff0c\u4e5f\u975e\u5e38\u6c89\u5f97\u4f4f\u6c14\u3002 \u3000\u3000\u4f46\u73b0\u5728\u4ed6\u5374\u5df2\u5fcd\u4e0d\u4f4f\u8981\u8df3\u4e86\u8d77\u6765\u3002 \u3000\u3000\u4ed6\u6ca1\u6709\u8df3\u8d77\u6765\uff0c\u56e0\u4e3a\u4ed6\u4ece\u8170\u90e8\u4ee5\u4e0b\uff0c\u5df2\u5b8c\u5168\u662f\u8f6f\u7684\uff0c\u8fde\u4e00\u70b9\u529b\u6c14\u90fd\u4f7f\u4e0d\u51fa\u3002 \u3000\u3000\u4ed6\u6574\u4e2a\u4eba\u90fd\u8f6f\u4e86\uff0c\u5fc3\u4e5f\u6c89\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u9999\u6848\u4e0a\u4e00\u5ea7\u4e09\u5c3a\u9ad8\u7684\u5357\u6d77\u89c2\u97f3\u83e9\u8428\uff0c\u624b\u62c8\u7740\u666e\u6e21\u4f17\u751f\u7684\u6768\u67f3\u679d\uff0c\u4eff\u4f5b\u6b63\u5728\u770b\u7740\u4ed6\u5fae\u7b11\u3002 \u3000\u3000\u4ece\u7f2d\u7ed5\u7684\u9999\u70df\u4e2d\u770b\u8fc7\u53bb\uff0c\u5979\u7684\u7b11\u5bb9\u770b\u6765\u4e5f\u4eff\u4f5b\u5e26\u7740\u79cd\u8bf4\u4e0d\u51fa\u7684\u8be1\u79d8\u4e4b\u610f\u3002 \u3000\u3000\u4e01\u9e9f\u5ffd\u7136\u53d1\u73b0\u8fd9\u89c2\u97f3\u83e9\u8428\u7684\u8138\uff0c\u7adf\u548c\u521a\u624d\u90a3\u6234\u7740\u9762\u5177\u7684\u7f8e\u4e3d\u5c11\u5973\u5b8c\u5168\u4e00\u6837\u3002 \u3000\u3000\u96be\u9053\u90a3\u5c11\u5973\u5c31\u662f\u5357\u6d77\u5a18\u5b50\uff1f \u3000\u3000\u4f46\u51fa\u624b\u5236\u4f4f\u4ed6\u7684\uff0c\u5374\u662f\u90a3\u8138\u4e0a\u957f\u7740\u80e1\u78b4\u5b50\u7684\u7537\u4eba\uff0c\u4ed6\u672c\u5df2\u8ba4\u4e3a\u8fd9\u7537\u4eba\u5c31\u662f\u5357\u6d77\u5a18\u5b50\u6539\u626e \u7684\u3002 \u3000\u3000\u4f46\u73b0\u5728\u4ed6\u5374\u5df2\u5b8c\u5168\u8ff7\u60d1\uff0c\u751a\u81f3\u8fde\u60f3\u90fd\u4e0d\u6562\u591a\u60f3\u3002 \u3000\u3000\u4ed6\u6015\u60f3\u591a\u4e86\u4f1a\u53d1\u75af\u3002 \u3000\u3000\u5e78\u597d\u8fd9\u65f6\u4ed6\u5c31\u7b97\u8981\u60f3\uff0c\u4e5f\u6ca1\u6cd5\u5b50\u518d\u60f3\u4e0b\u53bb\u4e86\uff0c\u4f5b\u5802\u7684\u95e8\u5df2\u6162\u6162\u5730\u88ab\u63a8\u5f00\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u6162\u6162\u5730\u8d70\u4e86\u8fdb\u6765\uff0c\u8138\u4e0a\u5e26\u7740\u79cd\u7f8e\u4e3d\u800c\u8be1\u79d8\u7684\u5fae\u7b11\uff0c\u5c31\u50cf\u795e\u6848\u4e2d\u89c2\u97f3\u83e9\u8428\u7684\u7b11\u5bb9\u4e00 \u6837\u3002 \u3000\u3000\u4e01\u9e9f\u770b\u770b\u89c2\u97f3\u795e\u50cf\uff0c\u518d\u770b\u770b\u5979\uff0c\u5ffd\u7136\u53f9\u4e86\u53e3\u6c14\uff0c\u95ed\u4e0a\u773c\u775b\u2026\u2026\u8fd9\u5c11\u5973\u7684\u8138\u7b80\u76f4\u5c31\u662f\u8fd9\u89c2 \u97f3\u83e9\u8428\u7684\u8138\u3002 \u3000\u3000\u4ed6\u4e5f\u4e0d\u60f3\u518d\u770b\uff0c\u4ed6\u6015\u770b\u591a\u4e86\u4f1a\u53d1\u75af\u3002 \u3000\u3000\u53ea\u53ef\u60dc\u4e0d\u770b\u4e5f\u540c\u6837\u4f1a\u53d1\u75af\u7684\u3002 \u3000\u3000\u8fd9\u5c11\u5973\u5df1\u8d70\u5230\u4ed6\u9762\u524d\uff0c\u5ffd\u7136\u7b11\u9053\uff1a\u201c\u4f60\u4eca\u5929\u5934\u53d1\u68b3\u5f97\u597d\u6f02\u4eae\uff0c\u662f\u8c01\u66ff\u4f60\u68b3\u7684\uff1f\u201d \u3000\u3000\u4e01\u9e9f\u5fcd\u4e0d\u4f4f\u5f20\u5f00\u773c\uff0c\u77aa\u6614\u5979\uff0c\u9053\uff1a\u201c\u6211\u6b63\u60f3\u95ee\u4f60\uff0c\u662f\u8c01\u66ff\u6211\u68b3\u7684\uff1f\u201d \u3000\u3000\u8fd9\u5c11\u5973\u5374\u4eff\u4f5b\u5f88\u60ca\u8bb6\uff0c\u9053\uff0c\u201c\u96be\u9053\u8fde\u4f60\u81ea\u5df1\u4e5f\u4e0d\u77e5\u9053\uff1f\u201d \u3000\u3000\u4e01\u9e9f\u9053\uff1a\u201c\u6211\u600e\u4e48\u4f1a\u77e5\u9053\uff1f\u201d \u3000\u3000\u8fd9\u5c11\u5973\u9053\uff1a\u201c\u4f60\u96be\u9053\u8fde\u4e00\u70b9\u90fd\u60f3\u4e0d\u8d77\u6765\uff1f\u201d \u3000\u3000\u4e01\u9e9f\u82e6\u7b11\u9053\uff1a\u201c\u6211\u600e\u4e48\u4f1a\u60f3\u8d77\u6765\uff0c\u6211\u6839\u672c\u8fde\u4e00\u70b9\u77e5\u89c9\u90fd\u6ca1\u6709\uff0c\u800c\u4e14\u4f60\u5c31\u7b97\u6253\u7834\u6211\u7684\u5934\uff0c \u6211\u4e5f\u731c\u4e0d\u51fa\u4f60\u4eec\u4e3a\u4ec0\u4e48\u8981\u628a\u6211\u626e\u6210\u4e2a\u5973\u4eba\uff1f\u201d \u3000\u3000\u8fd9\u5c11\u5973\u4eff\u4f5b\u66f4\u5403\u60ca\uff0c\u9053\uff1a\u201c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u4f60\u8bf4\u662f\u6211\u4eec\u628a\u4f60\u626e\u6210\u5973\u4eba\u7684\uff1f\u96be\u9053\u4f60\u5df2\u8fde\u4f60\u672c\u6765 \u5c31\u662f\u4e2a\u5973\u4eba\u90fd\u5fd8\u4e86\uff1f\u201d \u3000\u3000\u4e01\u9e9f\u5fcd\u4e0d\u4f4f\u53eb\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u201c\u8c01\u8bf4\u6211\u672c\u6765\u5c31\u662f\u4e2a\u5973\u4eba\u7684\uff1f\u201d \u3000\u3000\u8fd9\u5c11\u5973\u5403\u60ca\u5730\u770b\u7740\u4ed6\uff0c\u8138\u4e0a\u7684\u8868\u60c5\uff0c\u5c31\u597d\u50cf\u7a81\u7136\u770b\u89c1\u4e2a\u75af\u5b50\u4e00\u6837\u3002 \u3000\u3000\u4e86\u9e9f\u53c8\u5fcd\u4e0d\u4f4f\u9053\uff1a\u201c\u4f60\u82e5\u8bf4\u6211\u672c\u6765\u5c31\u662f\u4e2a\u5973\u4eba\uff0c\u4f60\u4e00\u5b9a\u75af\u4e86\uff01\u201d \u3000\u3000\u8fd9\u5c11\u5973\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4e0d\u662f\u6211\u75af\u4e86\uff0c\u662f\u4f60\uff01\u201d \u3000\u3000\u5979\u5ffd\u7136\u56de\u5934\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u5927\u5bb6\u5168\u6765\u770b\u5440\uff0c\u4e01\u5c0f\u59b9\u600e\u4e48\u4f1a\u5ffd\u7136\u53d8\u6210\u8fd9\u6837\u5b50\u4e86\uff1f\u201d \u3000\u3000\u4e01\u5c0f\u59b9\uff1f \u3000\u3000\u201c\u98ce\u90ce\u541b\u201d\u4e01\u9e9f\u7adf\u53d8\u6210\u4e86\u4e01\u5c0f\u59b9\uff01 \u3000\u3000\u4e01\u9e9f\u60f3\u7b11\u4e5f\u7b11\u4e0d\u51fa\uff0c\u60f3\u54ed\u4e5f\u54ed\u4e0d\u51fa\uff0c\u53ea\u89c1\u95e8\u5916\u5df2\u6709\u56db\u4e94\u4e2a\u5973\u4eba\u8d70\u4e86\u8fdb\u6765\uff0c\u5176\u4e2d\u6709\u4e00\u4e2a\u4e5f \u6b63\u662f\u521a\u624d\u8fd8\u6234\u7740\u9762\u5177\u7684\u4e2d\u5e74\u7f8e\u5987\u3002 \u3000\u3000\u539f\u6765\u5979\u5c31\u662f\u94c1\u59d1\uff0c\u56e0\u4e3a\u90a3\u5c11\u5973\u6b63\u5728\u62db\u547c\u5979\u3002 \u3000\u3000\u201c\u94c1\u59d1\uff0c\u4f60\u5feb\u6765\u770b\u770b\uff0c\u4e01\u5c0f\u59b9\u521a\u624d\u8fd8\u662f\u597d\u597d\u7684\uff0c\u73b0\u5728\u600e\u4e48\u5ffd\u7136\u53d8\u6210\u2026\u2026\u53d8\u6210\u8fd9\u6837\u5b50\uff1f\u201d \u3000\u3000\u94c1\u59d1\u4e5f\u5728\u770b\u7740\u4e01\u9e9f\uff0c\u5fae\u7b11\u7740\u9053\uff1a\u201c\u5979\u770b\u6765\u5c82\u975e\u8fd8\u662f\u597d\u597d\u7684\uff0c\u800c\u4e14\u5934\u53d1\u68b3\u5f97\u6bd4\u5e73\u65f6\u90fd\u6f02 \u4eae\u3002\u201d \u3000\u3000\u8fd9\u5c11\u5973\u9053\uff1a\u201c\u53ef\u662f\u2026\u2026\u53ef\u662f\u5979\u5c45\u7136\u4e0d\u80af\u627f\u8ba4\u81ea\u5df1\u662f\u4e2a\u5973\u4eba\u3002\u201d \u3000\u3000\u4e01\u9e9f\u5df2\u7ecf\u5c3d\u91cf\u63a7\u5236\u7740\u81ea\u5df1\uff0c\u4ed6\u77e5\u9053\u73b0\u5728\u975e\u51b7\u9759\u4e0b\u6765\u4e0d\u53ef\u3002 \u3000\u3000\u4f46\u4ed6\u5374\u8fd8\u662f\u5fcd\u4e0d\u4f4f\u8981\u5206\u8fa9\uff1a\u201c\u6211\u672c\u6765\u5c31\u4e0d\u662f\u4e2a\u5973\u4eba\uff01\u201d \u3000\u3000\u94c1\u59d1\u770b\u7740\u4ed6\uff0c\u5ffd\u7136\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u4e86\u89e3\u4f60\u7684\u5fc3\u60c5\uff0c\u6709\u65f6\u8fde\u6211\u4e5f\u5e0c\u671b\u81ea\u5df1\u4e0d\u662f\u4e2a\u5973 \u4eba\uff0c\u5728\u8fd9\u4e2a\u4e16\u754c\u91cc\uff0c\u505a\u5973\u4eba\u7684\u786e\u592a\u5403\u4e8f\u4e86\u3002\u201d \u3000\u3000\u4e01\u9e9f\u4e5f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5176\u5b9e\u6211\u5012\u4e0d\u53cd\u5bf9\u505a\u5973\u4eba\uff0c\u53ef\u60dc\u6211\u4e00\u751f\u4e0b\u6765\u5c31\u662f\u4e2a\u7537\u7684\uff0c\u4e00\u76f4\u5230 \u521a\u624d\u8fd8\u662f\u4e2a\u7537\u7684\u3002\u201d \u3000\u3000\u4ed6\u5b9e\u5728\u5df2\u7528\u4e86\u6700\u5927\u7684\u529b\u91cf\uff0c\u6765\u63a7\u5236\u4ed6\u81ea\u5df1\u3002 \u3000\u3000\u94c1\u59d1\u7684\u8138\u4e0a\u5374\u9732\u51fa\u5f88\u60ca\u8bb6\u7684\u8868\u60c5\uff0c\u5ffd\u7136\u56de\u5934\u95ee\u53e6\u4e00\u4e2a\u5973\u4eba\uff1a\u201c\u4f60\u4eec\u51e0\u65f6\u8ba4\u5f97\u4e86\u5c0f\u59b9 \u7684\uff1f\u201d \u3000\u3000\u201c\u4e5f\u6709\u4e24\u4e09\u4e2a\u6708\u4e86\u3002\u201d \u3000\u3000\u201c\u5979\u662f\u4e2a\u7537\u4eba\uff1f\u8fd8\u662f\u4e2a\u5973\u4eba\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u662f\u4e2a\u5973\u4eba\u3002\u201d \u3000\u3000\u6240\u6709\u5973\u4eba\u90fd\u5728\u5403\u5403\u5730\u7b11\uff1a\u201c\u4e01\u5c0f\u59b9\u82e5\u662f\u4e2a\u7537\u4eba\uff0c\u6211\u4eec\u5bb6\u5c31\u5168\u90fd\u662f\u7537\u4eba\u4e86\u3002\u201d \u3000\u3000\u4e01\u9e9f\u5df2\u89c9\u5f97\u81ea\u5df1\u7684\u8138\u5728\u53d1\u9752\uff0c\u5374\u8fd8\u662f\u5fcd\u8010\u7740\uff0c\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u6211\u4e5f\u4e0d\u662f\u4e01\u5c0f\u59b9\u3002\u201d \u3000\u3000\u94c1\u59d1\u5e26\u7740\u7b11\u95ee\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u662f\u8c01\uff1f\u201d \u3000\u3000\u4e01\u9e9f\u9053\uff1a\u201c\u6211\u4e5f\u59d3\u4e01\uff0c\u53eb\u4e01\u9e9f\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u53eb\u4e01\u7075\u7433\u3002\u201d \u3000\u3000\u4e01\u9e9f\u9053\uff1a\u201c\u4e0d\u662f\u4e01\u7075\u7433\uff0c\u662f\u4e01\u9e9f\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u4e0d\u662f\u4e86\u9e9f\uff0c\u662f\u4e01\u7075\u7433\uff0c\u4f60\u600e\u4e48\u4f1a\u8fde\u81ea\u5df1\u7684\u540d\u5b57\u90fd\u5fd8\u4e86\u3002\u201d \u3000\u3000\u90a3\u4e2a\u957f\u5f97\u8ddf\u89c2\u97f3\u83e9\u8428\u4e00\u6837\u7684\u5c11\u5973\u5ffd\u7136\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u5e78\u597d\u5979\u8bf4\u8bdd\u7684\u58f0\u97f3\u8fd8\u6ca1\u6709\u53d8\uff0c\u65e0\u8bba \u8c01\u90fd\u542c\u5f97\u51fa\u90a3\u662f\u5973\u4eba\u7684\u58f0\u97f3\u3002\u201d \u3000\u3000\u4e01\u9e9f\u51b7\u7b11\u9053\uff1a\u201c\u65e0\u8bba\u8c01\u90fd\u5e94\u8be5\u542c\u5f97\u51fa\u6211\u662f\u7537\u2026\u2026\u201d \u3000\u3000\u4ed6\u7684\u58f0\u97f3\u5ffd\u7136\u505c\u987f\uff0c\u51b7\u6c57\u7a81\u7136\u4ece\u80cc\u810a\u4e0a\u5192\u51fa\u6765\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u53d1\u89c9\u81ea\u5df1\u8bf4\u8bdd\u7684\u58f0\u97f3\u4e5f\u53d8\u4e86\uff0c\u53d8\u5f97\u53c8\u5c16\u53c8\u8f7b\uff0c\u7ade\u771f\u7684\u548c\u5973\u4eba\u4e00\u6837\u3002 \u3000\u3000\u2014\u2014\u96be\u9053\u6211\u771f\u7684\u5df2\u5ffd\u7136\u53d8\u6210\u5973\u4eba\uff1f \u3000\u3000\u4ed6\u53ea\u89c9\u4e00\u79cd\u8bf4\u4e0d\u51fa\u7684\u6050\u60e7\u4e4b\u610f\uff0c\u50cf\u5c16\u9488\u822c\u523a\u4eba\u4e86\u4ed6\u7684\u540e\u8111\u3002 \u3000\u3000\u4ed6\u60f3\u8bd5\u7740\u8fd0\u52a8\u4e00\u4e0b\u4ed6\u8eab\u4e0a\u67d0\u90e8\u4efd\u808c\u8089\uff0c\u53ea\u53ef\u60dc\u4ed6\u4ece\u8170\u90e8\u4ee5\u4e0b\uff0c\u7adf\u5df2\u5b8c\u5168\u9ebb\u6728\u3002 \u3000\u3000\u4ed6\u751a\u81f3\u60f3\u4f38\u624b\u6478\u6478\u90a3\u90e8\u4f4d\uff0c\u53ef\u662f\u5f53\u7740\u8fd9\u4e48\u591a\u5973\u4eba\uff0c\u4ed6\u5b9e\u5728\u53c8\u6ca1\u6709\u8fd9\u79cd\u52c7\u6c14\u3002 \u3000\u3000\u94c1\u59d1\u770b\u7740\u4ed6\uff0c\u773c\u775b\u91cc\u4eff\u4f5b\u5145\u6ee1\u4e86\u540c\u60c5\u548c\u601c\u60af\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u6700\u8fd1\u4f60\u5fc3\u60c5\u4e0d\u597d\uff0c\u53c8\u559d\u4e86\u5f88\u591a \u9152\uff0c\u96be\u514d\u4f1a\u5fd8\u8bb0\u4e00\u4e9b\u4e8b\u7684\uff0c\u4f55\u51b5\uff0c\u4ee5\u524d\u7684\u4e8b\uff0c\u4f60\u672c\u5c31\u4e0d\u613f\u610f\u518d\u60f3\u8d77\u3002\u201d \u3000\u3000\u4e01\u9e9f\u53ea\u6709\u542c\u7740\u3002 \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u4f46\u6211\u4eec\u90fd\u53ef\u4ee5\u63d0\u9192\u4f60\uff0c\u5f80\u4e8b\u867d\u7136\u60b2\u4f24\uff0c\u4f46\u82e5\u5b8c\u5168\u5fd8\u8bb0\u4e86\uff0c\u5bf9\u81ea\u5df1\u4e0d\u597d\u3002\u201d \u3000\u3000\u4e01\u9e9f\u53ea\u597d\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u597d\uff0c\u4f60\u8bf4\u5427\uff0c\u6211\u5728\u542c\u7740\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u4f60\u53eb\u4e01\u7075\u7433\uff0c\u662f\u4e2a\u975e\u5e38\u597d\u770b\u7684\u5973\u5b69\u5b50\uff0c\u4f60\u672c\u6765\u6709\u4e2a\u5f88\u597d\u7684\u60c5\u4eba\uff0c\u540e\u6765\u5374\u4e3a\u4e00 \u4e2a\u4eba\u95f9\u7ffb\u4e86\uff0c\u6240\u4ee5\u4f60\u8dd1\u5230\u6d77\u8fb9\u8981\u81ea\u6740\uff0c\u5e78\u597d\u5fc3\u59d1\u6551\u4e86\u4f60\u3002\u201d \u3000\u3000\u90a3\u5fae\u7b11\u5982\u89c2\u97f3\u7684\u5c11\u5973\u539f\u6765\u53eb\u5fc3\u59d1\uff0c\u5979\u7acb\u523b\u63a5\u7740\u9053\uff1a\u201c\u82e5\u4e0d\u662f\u6211\u62c9\u5f97\u5feb\uff0c\u90a3\u5929\u4f60\u5df2\u8df3\u4e0b\u6d77 \u53bb\u3002\u201d \u3000\u3000\u4e01\u9e9f\u54ac\u7740\u7259\uff0c\u4e0d\u5f00\u53e3\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u53d8\u5f97\u5f88\u6015\u542c\u89c1\u81ea\u5df1\u7684\u58f0\u97f3\u3002 \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u4f60\u90a3\u60c5\u4eba\u59d3\u53f6\uff0c\u53eb\u53f6\u5f00\uff0c\u4ed6\u2026\u2026\u201d \u3000\u3000\u53f6\u5f00\uff01 \u3000\u3000\u542c\u89c1\u8fd9\u540d\u5b57\uff0c\u4e01\u9e9f\u53ea\u89c9\u5f97\u81ea\u5df1\u8111\u5b50\u91cc\u201c\u8f70\u201d\u7684\u4e00\u54cd\u3002 \u3000\u3000\u5ffd\u7136\u95f4\uff0c\u4ed6\u4ec0\u4e48\u90fd\u660e\u767d\u4e86\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u81ea\u5df1\u843d\u4eba\u4e00\u4e2a\u6700\u6076\u6bd2\u3001\u6700\u8be1\u8c32\u3001\u4e5f\u6700\u5de7\u5999\u7684\u5708\u5957\u91cc\u3002 \u3000\u3000\u8fd9\u5708\u5957\u672c\u662f\u4e3a\u53f6\u5f00\u800c\u51c6\u5907\u7684\uff0c\u4ed6\u5374\u7cca\u91cc\u7cca\u6d82\u5730\u6389\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u94c1\u59d1\u5728\u8bf4\u4ec0\u4e48\uff0c\u4ed6\u5df2\u5b8c\u5168\u542c\u4e0d\u89c1\uff0c\u4ed6\u6b63\u5728\u62fc\u547d\u96c6\u4e2d\u601d\u60f3\u3002 \u3000\u3000\u4ed6\u4e00\u5b9a\u8981\u60f3\u6cd5\u5b50\u4ece\u8fd9\u5708\u5957\u91cc\u8131\u8eab\u51fa\u6765\uff0c\u4f46\u4ed6\u4e5f\u77e5\u9053\u8fd9\u7edd\u4e0d\u662f\u4ef6\u5bb9\u6613\u7684\u4e8b\u3002 \u3000\u3000\u975e\u5e38\u4e0d\u5bb9\u6613\u3002 \u3000\u3000\u65f6\u95f4\u4eff\u4f5b\u5df2\u8fc7\u4e86\u5f88\u4e45\uff0c\u94c1\u59d1\u7684\u8bdd\u5374\u8fd8\u6ca1\u6709\u505c\u3002 \u3000\u3000\u539f\u6765\u5979\u5df2\u5c06\u8fd9\u4e9b\u8bdd\u53cd\u53cd\u590d\u590d\u4ed6\u8bf4\u4e86\u5f88\u591a\u6b21\uff0c\u597d\u50cf\u5728\u5f3a\u8feb\u4e01\u9e9f\u63a5\u53d7\u8fd9\u4ef6\u4e8b\u3002 \u3000\u3000\u201c\u4f60\u90a3\u60c5\u4eba\u59d3\u53f6\uff0c\u53eb\u53f6\u5f00\uff0c\u4ed6\u672c\u6765\u662f\u6614\u5e74\u2018\u795e\u5200\u5802\u2019\u7684\u5802\u4e3b\u7684\u513f\u5b50\uff0c\u540e\u6765\u8fc7\u7ee7\u7ed9\u53f6\u5bb6 \u7684\uff01\u201d \u3000\u3000\u201c\u4f60\u7684\u7236\u4eb2\u53eb\u4e01\u4e58\u98ce\uff0c\u4f60\u7684\u59d1\u59d1\u53eb\u4e01\u767d\u4e91\uff0c\u672c\u662f\u53f6\u5bb6\u7684\u4ec7\u4eba\uff0c\u4f46\u540e\u6765\u8fd9\u4ef6\u4ec7\u6068\u5374\u88ab\u53f6\u5f00 \u5316\u89e3\u5f00\u4e86\uff0c\u4f60\u4eec\u7684\u60c5\u611f\uff0c\u53cd\u800c\u56e0\u6b64\u800c\u66f4\u52a0\u6df1\u539a\u3002\u201d \u3000\u3000\u201c\u4f60\u672c\u6765\u5df2\u975e\u4ed6\u4e0d\u5ac1\uff0c\u4ed6\u672c\u6765\u4e5f\u5df2\u975e\u4f60\u4e0d\u5a36\uff0c\u4f46\u8fd9\u65f6\u5374\u5ffd\u7136\u51fa\u73b0\u4e86\u4e2a\u53eb\u4e0a\u5b98\u5c0f\u4ed9\u7684\u5973 \u4eba\u3002\u201d \u3000\u3000\u201c\u8fd9\u5973\u4eba\u636e\u8bf4\u662f\u6614\u5e74\u5a01\u9547\u5929\u4e0b\u7684\u2018\u91d1\u94b1\u5e2e\u2019\u5e2e\u4e3b\u4e0a\u5b98\u91d1\u8679\uff0c\u548c\u5f53\u65f6\u5929\u4e0b\u7b2c\u4e00\u7f8e\u4eba\u6797\u4ed9\u513f \u6240\u751f\u7684\u5973\u513f\u3002\u6797\u4ed9\u513f\u867d\u7136\u7f8e\u4e3d\u5982\u4ed9\u5b50\uff0c\u5374\u4e13\u95e8\u5f15\u8bf1\u7537\u4eba\u4e0b\u5730\u72f1\u3002\u201d \u3000\u3000\u201c\u5979\u751f\u7684\u5973\u513f\uff0c\u4e5f\u4e00\u6837\u6076\u6bd2\uff0c\u4f60\u8ddf\u53f6\u5f00\uff0c\u5c31\u662f\u88ab\u5979\u62c6\u6563\u7684\u3002\u201d \u3000\u3000\u201c\u8fd9\u4ef6\u4e8b\u4f60\u5f53\u7136\u4e0d\u4f1a\u5fd8\u8bb0\uff0c\u4e5f\u7edd\u4e0d\u80fd\u5fd8\u8bb0\uff01\u201d \u3000\u3000\u4e01\u9e9f\u542c\u7740\u5979\u8bf4\u4e86\u4e00\u904d\uff0c\u53c8\u8bf4\u4e00\u904d\uff0c\u5ffd\u7136\u53d1\u73b0\u81ea\u5df1\u7684\u601d\u60f3\u975e\u4f46\u5df2\u5b8c\u5168\u65e0\u6cd5\u96c6\u4e2d\uff0c\u800c\u4e14\u4f3c\u5df2 \u611f\u5230\u88ab\u5979\u8bf4\u7684\u8bdd\u5de6\u53f3\u4e86\u3002 \u3000\u3000\u5ffd\u7136\u95f4\uff0c\u4ed6\u7ade\u5df2\u5bf9\u8fd9\u4e2a\u53eb\u4e0a\u5b98\u5c0f\u4ed9\u7684\u5973\u4eba\uff0c\u751f\u51fa\u79cd\u8bf4\u4e0d\u51fa\u7684\u75db\u6068\u4e4b\u610f\u3002 \u3000\u3000\u4ed6\u5df2\u51e0\u4e4e\u5feb\u8981\u627f\u8ba4\u81ea\u5df1\u5c31\u662f\u4e01\u7075\u7433\uff0c\u627f\u8ba4\u81ea\u5df1\u672c\u6765\u5c31\u662f\u4e2a\u5973\u4eba\u3002 \u3000\u3000\u7089\u4e2d\u7684\u9999\u70df\u4e00\u9635\u9635\u98d8\u8fc7\u6765\uff0c\u968f\u7740\u4ed6\u7684\u547c\u5438\uff0c\u6e17\u5165\u4ed6\u7684\u8111\u5b50\u91cc\u3002 \u3000\u3000\u4ed6\u7adf\u4f3c\u5df2\u5c06\u5b8c\u5168\u5931\u53bb\u5224\u65ad\u662f\u975e\u7684\u80fd\u529b\u3002 \u3000\u3000\u94c1\u59d1\u770b\u7740\u4ed6\uff0c\u8138\u4e0a\u5df2\u9732\u51fa\u4e00\u79cd\u8be1\u79d8\u800c\u5f97\u610f\u7684\u5fae\u7b11\uff0c\u6162\u6162\u5730\u53c8\u63a5\u7740\u9053\uff1a\u201c\u4f60\u53eb\u4e01\u7075\u7433\uff0c\u662f \u4e2a\u975e\u5e38\u597d\u770b\u7684\u5973\u5b69\u5b50\uff0c\u4f60\u2026\u2026\u201d \u3000\u3000\u4e01\u9e9f\u7a81\u7136\u7528\u5c3d\u6240\u6709\u7684\u529b\u6c14\u54ac\u4e86\u54ac\u5634\u5507\uff0c\u5267\u75db\u4f7f\u5f97\u4ed6\u7a81\u7136\u6e05\u9192\u3002 \u3000\u3000\u4ed6\u7acb\u523b\u5927\u543c\u9053\uff1a\u201c\u4e0d\u8981\u518d\u8bf4\u4e86\uff0c\u6211\u5df2\u660e\u767d\u4f60\u7684\u610f\u601d\uff01\u201d \u3000\u3000\u6b83\u59d1\u5fae\u7b11\u9053\uff1a\u201c\u4f60\u771f\u5df2\u660e\u767d\uff1f\u201d \u3000\u3000\u4e01\u9e9f\u9053\uff1a\u201c\u6211\u4e00\u5b9a\u957f\u5f97\u5f88\u8c61\u4e01\u7075\u7433\uff0c\u6240\u4ee5\u4f60\u4eec\u60f3\u5229\u7528\u6211\u6765\u5bb3\u53f6\u5f00\uff01\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u4f60\u672c\u6765\u5c31\u662f\u4e01\u7075\u7433\u3002\u201d \u3000\u3000\u4e01\u9e9f\u9053\uff1a\u201c\u5176\u5b9e\u4f60\u7528\u4e0d\u7740\u8fd9\u4e48\u6837\u505a\uff0c\u4f60\u4eec\u8981\u6211\u505a\u7684\u4e8b\u6211\u53ef\u4ee5\u7b54\u5e94\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u4e01\u9e9f\u8bf4\u9053\uff1a\u201c\u4f46\u4f60\u4eec\u4e5f\u5f97\u7b54\u5e94\u6211\u51e0\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff0c\u201c\u4f60\u8bf4\u3002\u201d \u3000\u3000\u4e01\u9e9f\u9053\uff1a\u201c\u6211\u8981\u4f60\u5148\u544a\u8bc9\u6211\uff0c\u4f60\u4eec\u7a76\u7adf\u662f\u6070\u5de7\u53d1\u73b0\u6211\u50cf\u4e01\u7075\u7433\uff0c\u624d\u5b9a\u4e0b\u8fd9\u5708\u5957\u7684\uff0c\u8fd8\u662f \u65e9\u5df2\u7b97\u51c6\u4e86\u6211\u8981\u6765\uff1f\u201d \u3000\u3000\u94c1\u59d1\u5ffd\u7136\u4e0d\u5f00\u53e3\u4e86\u3002 \u3000\u3000\u4e01\u9e9f\u9053\uff1a\u201c\u7136\u540e\u4f60\u4eec\u81f3\u5c11\u8fd8\u5f97\u89e3\u5f00\u6211\u7684\u7a74\u9053\uff0c\u8ba9\u6211\u89c1\u89c1\u5357\u6d77\u5a18\u5b50\uff0c\u8fd9\u4ef6\u4e8b\u6210\u529f\u4e4b\u540e\uff0c\u6211 \u81f3\u5c11\u8fd8\u5f97\u8981\u5360\u4e00\u4efd\uff01\u201d \u3000\u3000\u94c1\u59d1\u5ffd\u53c8\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u5357\u6d77\u5a18\u4e8e\u672c\u6765\u4e00\u76f4\u90fd\u5728\u8fd9\u91cc\uff0c\u4f60\u96be\u9053\u770b\u4e0d\u89c1\uff1f\u201d \u3000\u3000\u4e01\u9e9f\u5374\u95ee\u9053\uff1a\u201c\u5979\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u53ea\u542c\u4e00\u4e2a\u4f18\u96c5\u800c\u795e\u79d8\u7684\u58f0\u97f3\u7f13\u7f13\u9053\uff1a\u201c\u5c31\u5728\u8fd9\u91cc\uff01\u201d \u3000\u3000\u8fd9\u58f0\u97f3\u8d6b\u7136\u7adf\u662f\u795e\u6848\u4e0a\u90a3\u89c2\u97f3\u795e\u50cf\u53d1\u51fa\u6765\u7684\u3002 \u3000\u3000\u4e01\u9e9f\u970d\u7136\u53e3\u5934\uff0c\u770b\u4e86\u8fd9\u795e\u79d8\u7684\u96d5\u50cf\u4e00\u773c\uff0c\u76ee\u5149\u7adf\u518d\u4e5f\u65e0\u6cd5\u79fb\u5f00\u3002 \u3000\u3000\u4ece\u7f25\u7f08\u6c24\u6c32\u7684\u70df\u971e\u4e2d\u770b\u8fc7\u53bb\uff0c\u4ed6\u5ffd\u7136\u53d1\u73b0\u96d5\u50cf\u7adf\u5df2\u6362\u4e86\u4e00\u5f20\u8138\u3002 \u3000\u3000\u672c\u6765\u5e26\u7740\u5fae\u7b11\u7684\u8138\uff0c\u73b0\u5728\u7adf\u5df2\u53d8\u5f97\u51b7\u6f20\u4e25\u5389\uff0c\u7709\u5b57\u95f4\u7adf\u4f3c\u8fd8\u5e26\u7740\u6012\u610f\u3002 \u3000\u3000\u8fd9\u4e2a\u6ca1\u6709\u751f\u547d\u7684\u96d5\u50cf\uff0c\u5ffd\u7136\u95f4\u7adf\u4f3c\u5df2\u53d8\u5f97\u6709\u4e86\u751f\u547d\u3002 \u3000\u3000\u201c\u6211\u5c31\u662f\u4f60\u60f3\u89c1\u7684\u4eba\uff0c\u6240\u4ee5\u4f60\u73b0\u5728\u5c31\u5e94\u8be5\u770b\u7740\u6211\uff0c\u6211\u8bf4\u7684\u8bdd\uff0c\u6bcf\u4e2a\u5b57\u4f60\u90fd\u4e0d\u53ef\u4e0d\u4fe1\uff01\u201d \u3000\u3000\u70df\u96fe\u7f2d\u7ed5\uff0c\u8fd9\u58f0\u97f3\u7adf\u771f\u7684\u662f\u5979\u53d1\u51fa\u6765\u7684\u3002 \u3000\u3000\u4e01\u9e9f\u53ea\u89c9\u5f97\u5168\u8eab\u90fd\u5df2\u51b0\u51b7\uff0c\u7adf\u4e0d\u7531\u81ea\u4e3b\u5730\u70b9\u4e86\u70b9\u5934\uff0c\u5fc3\u91cc\u867d\u7136\u4e0d\u60f3\u518d\u770b\uff0c\u4f46\u76ee\u5149\u5374\u504f\u504f \u65e0\u6cd5\u4ece\u8fd9\u795e\u79d8\u800c\u5996\u5f02\u7684\u96d5\u50cf\u4e0a\u79fb\u5f00\u3002 \u3000\u3000\u201c\u4f60\u5c31\u662f\u4e01\u7075\u7433\uff0c\u53f6\u5f00\u672c\u6765\u662f\u4f60\u7684\u60c5\u4eba\uff0c\u4f60\u7684\u4e08\u592b\uff0c\u4f46\u4e0a\u5b98\u5c0f\u4ed9\u5374\u4ece\u4f60\u8eab\u8fb9\u62a2\u8d70\u4e86 \u4ed6\u3002\u201d \u3000\u3000\u201c\u73b0\u5728\uff0c\u4ed6\u4eec\u65e5\u65e5\u591c\u591c\u3001\u65f6\u65f6\u523b\u523b\u90fd\u53ae\u5b88\u5728\u4e00\u8d77\uff0c\u4f60\u5374\u53ea\u5269\u4e0b\u5b64\u5b64\u5355\u5355\u7684\u4e00\u4e2a\u4eba\uff01\u201d \u3000\u3000\u4e01\u9e9f\u770b\u7740\u5979\uff0c\u8138\u4e0a\u7adf\u4e0d\u7531\u81ea\u4e3b\u5730\u9732\u51fa\u4e00\u79cd\u75db\u82e6\u800c\u60b2\u4f24\u7684\u8868\u60c5\u3002 \u3000\u3000\u201c\u6211\u77e5\u9053\u4f60\u548c\u5979\uff0c\u8fd9\u79cd\u4ec7\u6068\u672c\u5c31\u662f\u4efb\u4f55\u4eba\u90fd\u5fd8\u4e0d\u4e86\u7684\uff0c\u6240\u4ee5\u4f60\u4e00\u5b9a\u8981\u62a5\u590d\u3002\u201d \u3000\u3000\u4e01\u9e9f\u8138\u4e0a\u679c\u7136\u53c8\u9732\u51fa\u6028\u6bd2\u4ec7\u6068\u4e4b\u8272\uff0c\u5583\u5583\u9053\uff1a\u201c\u6211\u4e00\u5b9a\u8981\u62a5\u590d\u2026\u2026\u6211\u4e00\u5b9a\u8981\u62a5\u590d\u2026\u2026\u201d \u3000\u3000\u201c\u73b0\u5728\u53f6\u5f00\u5f88\u5feb\u5c31\u8981\u5e26\u7740\u90a3\u53ef\u6068\u7684\u5973\u4eba\u5230\u8fd9\u91cc\u6765\u4e86\uff0c\u4f60\u6b63\u597d\u6709\u673a\u4f1a\u3002\u201d \u3000\u3000\u4e01\u9e9f\u5728\u542c\u7740\uff0c\u53d1\u4eae\u7684\u773c\u775b\u5df2\u6e10\u6e10\u53d8\u5f97\u8ff7\u60d8\u800c\u7a7a\u6d1e\uff0c\u4f46\u8138\u4e0a\u7684\u6028\u6bd2\u4e4b\u8272\u5374\u66f4\u5f3a\u70c8\u3002 \u3000\u3000\u201c\u53f6\u5f00\u7edd\u5bf9\u60f3\u4e0d\u5230\u4f60\u4f1a\u5728\u8fd9\u91cc\uff0c\u6240\u4ee5\u4f60\u7684\u5ffd\u7136\u51fa\u73b0\uff0c\u4ed6\u4e00\u5b9a\u4f1a\u89c9\u5f97\u5f88\u5403\u60ca\u3002\u201d \u3000\u3000\u201c\u4f46\u4ed6\u5374\u4e5f\u7edd\u4e0d\u4f1a\u5bf9\u4f60\u6709\u8b66\u6212\u4e4b\u610f\uff0c\u6240\u4ee5\u4f60\u5c31\u53ef\u4e58\u673a\u5c06\u90a3\u6076\u6bd2\u7684\u5973\u4eba\u4ece\u4ed6\u8eab\u8fb9\u62a2\u8d70\uff0c\u5e26 \u5230\u8fd9\u91cc\u6765\uff0c\u6bc1\u4e86\u5979\u90a3\u5f20\u7f8e\u4e3d\u7684\u8138\uff0c\u53eb\u5979\u4ee5\u540e\u6c38\u8fdc\u4e5f\u6ca1\u6cd5\u5b50\u52fe\u5f15\u522b\u7684\u7537\u4eba\u3002\u201d \u3000\u3000\u201c\u6211\u7684\u610f\u601d\u73b0\u5728\u4f60\u5df2\u660e\u767d\u4e86\u4e48\uff1f\u201d \u3000\u3000\u4e01\u9e9f\u6162\u6162\u5730\u70b9\u4e86\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6211\u5df2\u660e\u767d\u4e86\u3002\u201d \u3000\u3000\u201c\u4f60\u662f\u4e0d\u662f\u80af\u7167\u6211\u7684\u8bdd\u53bb\u505a\uff1f\u201d \u3000\u3000\u4e01\u9e9f\u9053\uff1a\u201c\u662f\uff01\u201d \u3000\u3000\u201c\u53ea\u8981\u662f\u6211\u8bf4\u7684\u8bdd\uff0c\u4f60\u5168\u90fd\u76f8\u4fe1\uff1f\u201d \u3000\u3000\u4e01\u9e9f\u9053\uff1a\u201c\u662f\uff01\u201d \u3000\u3000\u201c\u597d\uff0c\u4f60\u73b0\u5728\u5c31\u7ad9\u8d77\u6765\uff0c\u4f60\u7684\u7a74\u9053\u5df2\u89e3\u5f00\u4e86\uff0c\u4f60\u5df2\u7ecf\u53ef\u4ee5\u7ad9\u8d77\u6765\u4e86\u3002\u201d\u4e01\u9e9f\u679c\u7136\u6162\u6162\u5730 \u7ad9\u8d77\u6765\u3002 \u3000\u3000\u4ed6\u65e9\u5df2\u5b8c\u5168\u9ebb\u6728\u8f6f\u762b\u7684\u4e24\u6761\u817f\uff0c\u73b0\u5728\u7adf\u4f3c\u5df2\u7a81\u7136\u6709\u4e86\u529b\u91cf\u3002 \u3000\u3000\u201c\u597d\uff0c\u4f60\u8eab\u4e0a\u6709\u628a\u5200\uff0c\u73b0\u5728\u6211\u8981\u4f60\u7528\u8fd9\u628a\u5200\u53bb\u66ff\u6211\u6740\u4e00\u4e2a\u4eba\u3002\u201d \u3000\u3000\u4e01\u9cde\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u201c\u6768\u8f69\u3002\u201d \u3000\u3000\u4e01\u9e9f\u6162\u6162\u5730\u8f6c\u8fc7\u8eab\uff0c\u6162\u6162\u5730\u4ece\u5fc3\u59d1\u548c\u94c1\u59d1\u9762\u524d\u8d70\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u4ed6\u7684\u76ee\u5149\u76f4\u89c6\u5728\u524d\u65b9\uff0c\u624b\u91cc\u7d27\u63e1\u7740\u6000\u4e2d\u7684\u5200\uff0c\u5fc3\u91cc\u53ea\u6709\u4e00\u4e2a\u5ff5\u5934\u2014\u2014\u201c\u7528\u8fd9\u628a\u5200\uff0c\u53bb\u6740 \u6768\u8f69\uff01\u201d \u3000\u3000\u95e8\u623f\u91cc\u867d\u7136\u751f\u4e86\u706b\uff0c\u5374\u8fd8\u662f\u5f88\u5bd2\u51b7\u3002 \u3000\u3000\u6768\u8f69\u9759\u9759\u5730\u5750\u5728\u706b\u76c6\u65c1\uff0c\u770b\u6765\u5df2\u89c9\u5f97\u6709\u4e9b\u7126\u6025\u4e0d\u5b89\u3002 \u3000\u3000\u4ed6\u5728\u7b49\u4e01\u9e9f\u7684\u6d88\u606f\u3002 \u3000\u3000\u4e01\u9e9f\u7adf\u76f4\u5230\u73b0\u5728\u8fd8\u6ca1\u6709\u6d88\u606f\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u4e00\u4e2a\u4eba\u6162\u6162\u5730\u63a8\u5f00\u4e86\u95e8\uff0c\u6162\u6162\u5730\u8d70\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u4e00\u4e2a\u5f88\u7f8e\u7684\u5973\u4eba\uff0c\u6ee1\u5934\u4e4c\u9ed1\u7684\u9752\u4e1d\uff0c\u633d\u7740\u4e2a\u65f6\u65b0\u7684\u5815\u9a6c\u9afb\uff0c\u53d1\u9afb\u4e0a\u8fd8\u63d2\u7740\u51e4\u5934\u9497\u3002\u6768\u8f69 \u7ad9\u8d77\u6765\uff0c\u5fae\u7b11\u9053\uff1a\u201c\u59d1\u5a18\u6709\u4ec0\u4e48\u5429\u5490\uff1f\u201d\u4ed6\u663e\u7136\u5df2\u5c06\u8fd9\u5973\u4eba\u5f53\u505a\u5357\u6d77\u5a18\u5b50\u7684\u95e8\u4e0b\uff0c\u8fde\u770b\u4e5f\u4e0d \u6562\u591a\u770b\u4e00\u773c\u3002 \u3000\u3000\u8fd9\u5973\u4eba\u5374\u4e00\u76f4\u5728\u76ef\u7740\u4ed6\uff0c\u773c\u775b\u91cc\u5e26\u7740\u79cd\u5f88\u5947\u602a\u7684\u8868\u60c5\u3002 \u3000\u3000\u6768\u8f69\u5fcd\u4e0d\u4f4f\u53c8\u62ac\u5934\u770b\u4e86\u4ed6\u4e00\u773c\uff0c\u5ffd\u7136\u53d1\u73b0\u4ed6\u5f88\u50cf\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u8fd9\u5973\u4eba\u7684\u773c\u775b\u4ecd\u7136\u662f\u5728\u76ef\u7740\u4ed6\uff0c\u4e00\u5b57\u5b57\u9053\uff1a\u201c\u4f60\u5c31\u662f\u6768\u8f69\uff1f\u201d \u3000\u3000\u6768\u8f69\u70b9\u70b9\u5934\uff0c\u5ffd\u7136\u5931\u58f0\u60ca\u53eb\u9053\uff1a\u201c\u4f60\u662f\u4e01\u9cde\uff01\u201d \u3000\u3000\u4e01\u9e9f\u9053\uff1a\u201c\u6211\u4e0d\u662f\u4e01\u9e9f\uff0c\u662f\u4e01\u7075\u7433\u3002\u201d \u3000\u3000\u6768\u8f69\u5403\u60ca\u5730\u770b\u7740\u4ed6\uff0c\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u600e\u4e48\u4f1a\u53d8\u6210\u8fd9\u4e2a\u6837\u5b50\u7684\uff1f\u201d \u3000\u3000\u4e01\u9e9f\u9053\uff1a\u201c\u6211\u672c\u6765\u5c31\u662f\u8fd9\u4e2a\u6837\u5b50\uff0c\u6211\u672c\u6765\u5c31\u662f\u4e2a\u5973\u4eba\u3002\u201d \u3000\u3000\u6768\u8f69\u7684\u8138\u8272\u4e5f\u53d8\u4e86\uff0c\u9053\uff1a\u201c\u4f60\u83ab\u975e\u75af\u4e86\uff01\u201d \u3000\u3000\u4e01\u9e9f\u9053\uff1a\u201c\u6211\u6ca1\u6709\u75af\uff0c\u75af\u7684\u662f\u4f60\uff0c\u6240\u4ee5\u6211\u8981\u6740\u4e86\u4f60\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u4ece\u6000\u4e2d\u62bd\u51fa\u67c4\u77ed\u5200\uff0c\u4e00\u5200\u523a\u5165\u6768\u8f69\u7684\u80f8\u819b\u3002\u6768\u8f69\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u4ed6\u4f1a\u7a81\u7136\u4e0b\u8fd9\u79cd\u6bd2 \u624b\uff0c\u6839\u672c\u5c31\u6ca1\u6709\u63d0\u9632\uff0c\u4e5f\u6765\u4e0d\u53ca\u8eb2\u907f\u3002\u9c9c\u8840\u82b1\u96e8\u822c\u5730\u4ece\u4ed6\u80f8\u819b\u4e0a\u98de\u6e85\u51fa\u6765\uff0c\u4e00\u70b9\u70b9\u6d12\u5728\u4e01\u9e9f \u8863\u670d\u4e0a\u3002 \u3000\u3000\u4e01\u9e9f\u7684\u8138\u4e0a\u5374\u5168\u65e0\u8868\u60c5\uff0c\u51b7\u51b7\u5730\u770b\u7740\u6768\u8f69\u5012\u4e0b\u53bb\uff0c\u7136\u540e\u5c31\u6162\u6162\u5730\u8f6c\u8fc7\u8eab\u3002 \u3000\u3000\u95e8\u5916\u51b7\u96fe\u51c4\u8ff7\uff0c\u591c\u66f4\u6df1\u4e86\u3002 \u3000\u3000\u4ed6\u6162\u6162\u5730\u8d70\u5165\u96fe\u91cc\uff0c\u9ed1\u6697\u4e2d\u5ffd\u7136\u53c8\u4f20\u6765\u90a3\u4f18\u7f8e\u800c\u795e\u79d8\u7684\u58f0\u97f3\uff1a\u201c\u4f60\u505a\u5f97\u5f88\u597d\uff0c\u53ef\u662f\u4f60\u5df2 \u7ecf\u592a\u7d2f\u4e86\uff0c\u5df2\u7d2f\u5f97\u8fde\u773c\u775b\u90fd\u7741\u4e0d\u5f00\u3002\u201d \u3000\u3000\u4e01\u9e9f\u9053\uff1a\u201c\u6211\u7684\u786e\u592a\u7d2f\u4e86\uff01\u201d \u3000\u3000\u4ed6\u7684\u773c\u775b\u679c\u7136\u6162\u6162\u5730\u95ed\u4e0a\u3002 \u3000\u3000\u201c\u8fd9\u91cc\u5c31\u662f\u5f20\u5f88\u8212\u670d\u7684\u5e8a\uff0c\u73b0\u5728\u4f60\u5df2\u53ef\u7761\u4e0b\u53bb\uff0c\u7b49\u5230\u53f6\u5f00\u548c\u90a3\u6076\u6bd2\u5973\u4eba\u6765\u5230\u65f6\uff0c\u6211\u4eec\u4f1a \u53eb\u9192\u4f60\u7684\uff01\u201d \u3000\u3000\u5730\u4e0a\u79ef\u7740\u5f88\u539a\u7684\u51b0\u96ea\uff0c\u4f46\u4e01\u9e9f\u5374\u5df2\u8eba\u4e0b\u53bb\u3002 \u3000\u3000\u5c31\u50cf\u662f\u771f\u7684\u8eba\u5728\u4e00\u5f20\u5f88\u8212\u670d\u7684\u5e8a\u4e0a\uff0c\u5ffd\u7136\u95f4\u5c31\u5df2\u7761\u7740\u3002 \u7b2c\u56db\u7ae0\u3000\u7ea2\u989c\u8584\u547d>> \u53e4\u9f99\u300a\u4e5d\u6708\u9e70\u98de\u300b \u7b2c\u56db\u7ae0\u3000\u7ea2\u989c\u8584\u547d \u3000\u3000\u96fe\u8d8a\u6765\u8d8a\u6d53\u4e86\u3002 \u3000\u3000\u59b9\u59b9\u4e00\u76f4\u7761\u5f97\u90fd\u5f88\u719f\uff0c\u59d0\u59d0\u8f7b\u8f7b\u5730\u5598\u606f\u7740\uff0c\u773c\u5e18\u7ec8\u4e8e\u4e5f\u95ed\u8d77\uff0c\u8138\u4e0a\u8fd8\u5e26\u7740\u75b2\u5026\u800c\u6ee1\u8db3\u7684 \u751c\u7b11\u3002 \u3000\u3000\u897f\u95e8\u5341\u4e09\u770b\u7740\u5979\u4eec\uff0c\u5fc3\u91cc\u5ffd\u7136\u4e5f\u89c9\u5f97\u6709\u79cd\u8bf4\u4e0d\u51fa\u7684\u6109\u5feb\u548c\u5f97\u610f\uff0c\u5c31\u597d\u50cf\u4ed6\u5df2\u5c06\u4e01\u9e9f\u51fb\u8d25 \u4e86\u4e00\u6837\u3002 \u3000\u3000\u201c\u4e00\u4e2a\u4eba\u603b\u4e0d\u80fd\u662f\u6bcf\u4ef6\u4e8b\u90fd\u5f97\u80dc\u7684\uff0c\u6211\u4e5f\u603b\u6709\u6bd4\u4f60\u5f3a\u7684\u5730\u65b9\u3002\u201d \u3000\u3000\u4ed6\u5fae\u7b11\u7740\uff0c\u6b63\u60f3\u559d\u676f\u9152\uff0c\u8f66\u53a2\u5916\u5ffd\u7136\u6709\u4eba\u5728\u6572\u95e8\u3002 \u3000\u3000\u662f\u4e0d\u662f\u4e01\u9e9f\u56de\u6765\u4e86\u3002 \u3000\u3000\u8f66\u7a97\u4e0a\u7684\u5e18\u5b50\u5df2\u7136\u62c9\u4e86\u4e0b\u6765\uff0c\u4ed6\u770b\u4e0d\u89c1\u95e8\u5916\u662f\u4ec0\u4e48\u4eba\u3002 \u3000\u3000\u201c\u8c01\uff1f\u201d \u3000\u3000\u6ca1\u6709\u56de\u5e94\u3002\u897f\u95e8\u5341\u4e09\u8fdf\u7591\u7740\uff0c\u7ec8\u4e8e\u5fcd\u4e0d\u4f4f\u63a8\u5f00\u8f66\u95e8\u3002\u5916\u9762\u4e5f\u6ca1\u6709\u4eba\u3002 \u3000\u3000\u5916\u9762\u4e00\u7247\u9ed1\u6697\uff0c\u51b7\u96fe\u521a\u521a\u4ece\u5730\u9762\u4e0a\u5347\u8d77\u3002 \u3000\u3000\u521a\u624d\u662f\u8c01\u5728\u6572\u95e8\uff1f \u3000\u3000\u4ed6\u62c9\u7d27\u4e86\u8863\u895f\uff0c\u518d\u95ee\uff0c\u6ca1\u6709\u56de\u5e94\uff0c\u90a3\u4e2a\u4e00\u76f4\u5728\u5916\u9762\u671b\u98ce\u7684\u8f66\u592b\u5462\uff1f \u3000\u3000\u5929\u6c14\u5b9e\u5728\u592a\u51b7\uff0c\u4ed6\u672c\u4e0d\u60f3\u79bb\u5f00\u8fd9\u6e29\u6696\u7684\u8f66\u53a2\uff0c\u53ef\u662f\u4e00\u4e2a\u4eba\u505a\u4e86\u4e8f\u5fc3\u4e8b\u540e\uff0c\u603b\u96be\u514d\u4f1a\u7591\u795e \u7591\u9b3c\u7684\u3002 \u3000\u3000\u4ed6\u7ec8\u4e8e\u7a7f\u4e0a\u9774\u5b50\uff0c\u8df3\u4e0b\u8f66\uff0c\u56db\u9762\u4e00\u7247\u9ed1\u6697\uff0c\u5bd2\u51b7\u800c\u5bc2\u9759\u3002 \u3000\u3000\u90a3\u4e2a\u7a7f\u7740\u9752\u5e03\u68c9\u8884\u7684\u8f66\u592b\uff0c\u8eb2\u5728\u4e00\u5806\u7a3b\u8349\u91cc\uff0c\u5934\u6795\u7740\u819d\u76d6\uff0c\u624b\u62b1\u7740\u5934\uff0c\u4f3c\u4e4e\u7761\u7740\u4e86\u3002 \u3000\u3000\u521a\u624d\u6572\u95e8\u7684\u4eba\u5462\uff1f\u96be\u9053\u4ed6\u542c\u9519\u4e86\uff1f \u3000\u3000\u4ed6\u7edd\u4e0d\u4f1a\u542c\u9519\u7684\u3002 \u3000\u3000\u4ed6\u7684\u5e74\u7eaa\u8fd8\u8f7b\uff0c\u773c\u775b\u548c\u8033\u6735\u4e00\u5411\u90fd\u5f88\u7075\u3002 \u3000\u3000\u8fd9\u8f66\u592b\u4e5f\u4e0d\u77e5\u9053\u662f\u4e01\u9e9f\u4ece\u4ec0\u4e48\u5730\u65b9\u627e\u6765\u7684\uff0c\u521a\u624d\u771f\u6709\u4eba\u6765\u8fc7\uff0c\u4ed6\u7ec8\u4e8e\u542c\u89c1\u4e00\u4e9b\u52a8\u9759\u3002 \u3000\u3000\u897f\u95e8\u5341\u4e09\u8d70\u8fc7\u53bb\uff0c\u6b63\u60f3\u63a8\u9192\u4ed6\u95ee\u95ee\u3002 \u3000\u3000\u8f66\u592b\u7a81\u7136\u4ece\u8349\u5806\u4e0a\u5f39\u8d77\uff0c\u51cc\u7a7a\u4e00\u4e2a\u7ffb\u8eab\uff0c\u7bad\u4e00\u822c\u7a9c\u4e86\u51fa\u53bb\uff0c\u8eab\u624b\u4e4b\u5feb\uff0c\u867d\u7136\u6bd4\u4e0d\u4e0a\u4e01 \u9e9f\uff0c\u5374\u7edd\u4e0d\u5728\u897f\u95e8\u5341\u4e09\u4e4b\u4e0b\u3002 \u3000\u3000\u897f\u95e8\u5341\u4e09\u7adf\u6ca1\u6709\u770b\u89c1\u4ed6\u7684\u9762\u76ee\uff0c\u4f46\u7a0d\u5fae\u4e00\u8fdf\u7591\u95f4\uff0c\u8fd9\u8f66\u592b\u7684\u4eba\u5f71\u5df2\u6d88\u5931\u5728\u9ed1\u6697\u91cc\u3002 \u3000\u3000\u51b7\u96fe\u51c4\u8ff7\uff0c\u5bd2\u98ce\u5982\u5200\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u6fc0\u7075\u7075\u6253\u4e86\u4e2a\u5bd2\u5664\uff0c\u51b3\u5b9a\u5148\u5230\u8f66\u53a2\u91cc\u7b49\u4e01\u9e9f\u56de\u6765\u518d\u8bf4\u3002 \u3000\u3000\u8f66\u53a2\u7684\u95e8\u7adf\u53c8\u5173\u4e86\u8d77\u6765\uff0c\u4e5f\u4e0d\u77e5\u662f\u5426\u662f\u4ed6\u81ea\u5df1\u521a\u624d\u968f\u624b\u5e26\u4e0a\u7684\u3002 \u3000\u3000\u5d4c\u5728\u8f66\u9876\u4e0b\u90a3\u76cf\u5236\u9020\u5f97\u5f88\u7cbe\u5de7\u7684\u94dc\u706f\uff0c\u8fd8\u662f\u4eae\u7740\uff0c\u67d4\u548c\u7684\u706f\u5149\u4ece\u7d2b\u7ed2\u7a97\u5e18\u91cc\u900f\u51fa\u6765\u3002 \u3000\u3000\u897f\u95e8\u5341\u4e09\u5b9e\u5728\u5f88\u540e\u6094\uff0c\u521a\u624d\u672c\u4e0d\u8be5\u79bb\u5f00\u8f66\u53a2\u7684\uff0c\u4ed6\u5f88\u5feb\u5730\u8d70\u56de\u53bb\uff0c\u62c9\u5f00\u8f66\u53a2\u95e8\u3002 \u3000\u3000\u7136\u540e\u4ed6\u7684\u5fc3\u5c31\u6c89\u4e86\u4e0b\u53bb\uff0c\u6574\u4e2a\u4eba\u90fd\u6014\u5728\u8f66\u53a2\u5916\uff0c\u8fde\u52a8\u90fd\u4e0d\u4f1a\u52a8\u4e86\u3002 \u3000\u3000\u8f66\u53a2\u91cc\u7adf\u591a\u4e86\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u4e00\u4e2a\u79c3\u9876\u9e70\u9f3b\u3001\u6ee1\u9762\u7ea2\u5149\u7684\u9526\u888d\u8001\u4eba\uff0c\u7b95\u952f\u5728\u4ed6\u521a\u624d\u5750\u7684\u5730\u65b9\u3002 \u3000\u3000\u8d6b\u7136\u6b63\u662f\u536b\u516b\u592a\u7237\u3002 \u3000\u3000\u90a3\u59d0\u59b9\u4e24\u4eba\u8fd8\u662f\u8737\u66f2\u5728\u89d2\u843d\u91cc\uff0c\u7761\u5f97\u66f4\u6c89\u4e86\u3002\u536b\u516b\u592a\u7237\u4e00\u53cc\u70af\u70af\u6709\u795e\u7684\u773c\u775b\uff0c\u6b63\u5200\u950b\u822c \u77aa\u7740\u4ed6\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u4e0a\u6765\u3002\u201d \u3000\u3000\u897f\u95e8\u5341\u4e09\u5782\u4e0b\u4e86\u5934\uff0c\u8de8\u4e0a\u8f66\u53a2\uff0c\u773c\u775b\u5ffd\u7136\u77a5\u89c1\u521a\u624d\u90a3\u4e2a\u8f66\u592b\u7adf\u5df2\u56de\u5230\u8349\u5806\u4e0a\u6253\u5428\u4e86\uff0c\u8fde \u59ff\u52bf\u90fd\u6ca1\u6709\u6539\u53d8\uff0c\u597d\u50cf\u6839\u672c\u5c31\u6ca1\u6709\u52a8\u8fc7\u3002 \u3000\u3000\u8f66\u53a2\u5f88\u4f4e\uff0c\u65e0\u8bba\u8c01\u90fd\u7ad9\u4e0d\u76f4\u7684\u3002 \u3000\u3000\u897f\u95e8\u5341\u4e09\u5374\u4e0d\u6562\u5750\u4e0b\u6765\uff0c\u53ea\u6709\u5782\u7740\u5934\uff0c\u5f2f\u7740\u8170\uff0c\u7ad9\u5728\u90a3\u91cc\u3002 \u3000\u3000\u536b\u516b\u5927\u7237\u51b7\u51b7\u5730\u770b\u7740\u4ed6\uff0c\u9053\uff1a\u201c\u4f60\u90a3\u597d\u670b\u53cb\u5462\uff1f\u201d \u3000\u3000\u897f\u95e8\u5341\u4e09\u9053\uff1a\u201c\u4ed6\u5df2\u7ecf\u8fdb\u53bb\u4e86\u3002\u201d \u3000\u3000\u897f\u95e8\u5341\u4e09\u5934\u5782\u5f97\u66f4\u4f4e\uff0c\u4ed6\u65e0\u6cd5\u56de\u7b54\uff0c\u4e5f\u4e0d\u6562\u56de\u7b54\uff0c\u56e0\u4e3a\u4ed6\u521a\u624d\u6839\u672c\u5c31\u5fd8\u4e86\u65f6\u95f4\u3002 \u3000\u3000\u521a\u624d\u4ed6\u7b80\u76f4\u8fde\u4ec0\u4e48\u90fd\u5fd8\u4e86\u3002 \u3000\u3000\u536b\u516b\u7237\u77aa\u7740\u4ed6\uff0c\u5389\u58f0\u9053\uff1a\u201c\u4ed6\u8d70\u4e86\u4e4b\u540e\uff0c\u4f60\u5728\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u897f\u95e8\u5341\u4e09\u66f4\u4e0d\u6562\u56de\u7b54\u3002 \u3000\u3000\u4ed6\u65e9\u5df2\u77e5\u9053\u81ea\u5df1\u505a\u7684\u4e8b\u5f88\u6709\u70b9\u89c1\u4e0d\u5f97\u4eba\u3002 \u3000\u3000\u7537\u5b50\u6c49\u5927\u4e08\u592b\uff0c\u73a9\u51e0\u4e2a\u751f\u5f97\u8d31\u7684\u5973\u4eba\uff0c\u867d\u7136\u7b97\u4e0d\u4e86\u4ec0\u4e48\uff0c\u53ef\u662f\u5728\u8352\u5730\u91cc\u73a9\u670b\u53cb\u7684\u5973\u4eba\uff0c \u5374\u5b8c\u5168\u662f\u53e6\u5916\u4e00\u56de\u4e8b\u4e86\u3002 \u3000\u3000\u536b\u516b\u7237\u51b7\u7b11\u9053\uff1a\u201c\u770b\u6765\u4f60\u771f\u662f\u8272\u80c6\u5305\u5929\uff0c\u96be\u9053\u4f60\u5c31\u4e0d\u6015\u4e01\u9e9f\u77e5\u9053\uff1f\u201d \u3000\u3000\u897f\u95e8\u5341\u4e09\u7ea2\u7740\u8138\uff0c\u55eb\u5685\u7740\uff1a\u201c\u6211\u4eec\u2026\u2026\u6211\u4eec\u662f\u597d\u670b\u53cb\u3002\u201d \u3000\u3000\u536b\u516b\u592a\u7237\u6012\u9053\uff1a\u201c\u4f60\u4eec\u65e2\u7136\u662f\u597d\u670b\u53cb\uff0c\u4f60\u600e\u4e48\u80fd\u5bf9\u597d\u670b\u53cb\u505a\u8fd9\u6837\u7684\u4e8b\uff0c\u4ed6\u82e5\u5728\u80cc\u5730\u91cc\u62a2 \u4e86\u4f60\u7684\u5973\u4eba\uff0c\u4f60\u4f1a\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u897f\u95e8\u5341\u4e09\u4e0d\u6562\u642d\u8154\u3002 \u3000\u3000\u536b\u516b\u592a\u7237\u9053\uff1a\u201c\u4f60\u82e5\u4ee5\u4e3a\u4e01\u9e9f\u4e0d\u4f1a\u51fa\u624b\uff0c\u4f60\u5c31\u9519\u4e86\uff0c\u8fd9\u79cd\u4e8b\u53ea\u8981\u662f\u7537\u4eba\u5c31\u4e00\u5b9a\u4f1a\u51fa\u624b \u7684\u3002\u201d \u3000\u3000\u897f\u95e8\u5341\u4e09\u53ea\u6709\u627f\u8ba4\u3002 \u3000\u3000\u536b\u516b\u592a\u7237\u9053\uff1a\u201c\u51ed\u4f60\u8fd9\u70b9\u672c\u4e8b\uff0c\u4ed6\u4e00\u4e2a\u4eba\u5c31\u53ef\u5bf9\u4ed8\u4f60\u516b\u4e2a\uff0c\u4ed6\u77e5\u9053\u4e86\u8fd9\u4ef6\u4e8b\u540e\uff0c\u82e5\u8981\u5bf9 \u4ed8\u4f60\uff0c\u4f60\u51c6\u5907\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u897f\u95e8\u5341\u4e09\u9f13\u8d77\u52c7\u6c14\uff0c\u5583\u5583\u9053\uff1a\u201c\u6211\u60f3\u4ed6\u5927\u6982\u4e0d\u4f1a\u77e5\u9053\u3002\u201d \u3000\u3000\u536b\u516b\u592a\u7237\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u60f3\u4ed6\u5927\u6982\u4e0d\u4f1a\u77e5\u9053\uff0c\u4f60\u51ed\u54ea\u70b9\u8fd9\u4e48\u60f3\uff1f\u201d \u3000\u3000\u897f\u95e8\u5341\u4e09\u82e6\u7b11\u9053\uff1a\u201c\u6211\u81ea\u5df1\u5f53\u7136\u7edd\u4e0d\u4f1a\u544a\u8bc9\u4ed6\u7684\u2026\u2026\u201d \u3000\u3000\u536b\u516b\u592a\u7237\u6253\u65ad\u4e86\u4ed6\u7684\u8bdd\uff0c\u9053\uff1a\u201c\u4f60\u867d\u7136\u4e0d\u4f1a\u8bf4\uff0c\u53ef\u662f\u8fd9\u5973\u4eba\u5462\uff1f\u201d \u3000\u3000\u897f\u95e8\u5341\u4e09\u9053\uff1a\u201c\u662f\u5979\u81ea\u5df1\u8981\u7684\uff0c\u5979\u600e\u4e48\u4f1a\u544a\u8bc9\u522b\u4eba\uff1f\u201d \u3000\u3000\u536b\u516b\u592a\u7237\u9053\uff1a\u201c\u4f60\u4ee5\u4e3a\u5979\u771f\u7684\u770b\u4e0a\u4f60\uff0c\u6240\u4ee5\u624d\u52fe\u5f15\u4f60\uff1f\u201d\u897f\u95e8\u5341\u4e09\u867d\u7136\u4e0d\u6562\u627f\u8ba4\uff0c\u5374\u4e0d \u613f\u5426\u8ba4\u3002 \u3000\u3000\u536b\u516b\u592a\u7237\u9053\uff1a\u201c\u6211\u95ee\u4f60\uff0c\u8fd9\u4e24\u4e2a\u5973\u4eba\u662f\u4e0d\u662f\u4f60\u4eec\u4ece\u77f3\u5bb6\u5e84\u62a2\u6765\u7684\uff1f\u201d \u3000\u3000\u897f\u95e8\u5341\u4e09\u70b9\u70b9\u5934\u3002 \u3000\u3000\u536b\u516b\u592a\u7237\u9053\uff1a\u201c\u4f60\u96be\u9053\u4ee5\u4e3a\u5979\u4eec\u5f88\u613f\u610f\u88ab\u4f60\u4eec\u62a2\u8d70\uff1f\u201d \u3000\u3000\u4e16\u4e0a\u7edd\u6ca1\u6709\u4efb\u4f55\u4eba\u613f\u610f\u88ab\u4eba\u534a\u591c\u62a2\u8d70\u7684\u3002 \u3000\u3000\u536b\u516b\u592a\u7237\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u96be\u9053\u8fd8\u770b\u4e0d\u51fa\uff0c\u8fd9\u5a4a\u5b50\u52fe\u5f15\u4f60\uff0c\u4e3a\u7684\u5c31\u662f\u8ba9\u4f60\u8ddf\u4e01\u9e9f\u4e89\u51e4\u5403\u918b\uff0c \u5979\u4eec\u624d\u6709\u62a5\u590d\u7684\u673a\u4f1a\u3002\u201d \u3000\u3000\u897f\u95e8\u5341\u4e09\u663e\u7136\u8fd8\u6709\u70b9\u4e0d\u670d\u6c14\uff0c\u5fcd\u4e0d\u4f4f\u9053\uff1a\u201c\u5979\u4e5f\u8bb8\u2026\u2026\u201d \u3000\u3000\u536b\u516b\u592a\u7237\u6012\u9053\uff1a\u201c\u96be\u9053\u4f60\u8fd8\u4ee5\u4e3a\u5979\u662f\u771f\u7684\u770b\u4e0a\u4e86\u4f60\uff1f\u4f60\u6709\u54ea\u70b9\u6bd4\u4e01\u9e9f\u5f3a\uff1f\u800c\u4e14\uff0c\u4e00\u4e2a\u5341 \u56db\u4e94\u5c81\u7684\u5c0f\u59d1\u5a18\u5c31\u7b97\u751f\u5f97\u518d\u8d31\uff0c\u4e5f\u4e0d\u4f1a\u5f53\u7740\u81ea\u5df1\u59b9\u59b9\u9762\u524d\uff0c\u505a\u8fd9\u79cd\u4e8b\u7684\u3002\u201d \u3000\u3000\u897f\u95e8\u5341\u4e09\u4e0d\u6562\u518d\u8fa9\u4e86\u3002 \u3000\u3000\u536b\u516b\u592a\u7237\u9053\uff1a\u201c\u4f55\u51b5\uff0c\u4f60\u4eec\u521a\u624d\u5728\u8f66\u53a2\u73a9\u7684\u628a\u620f\uff0c\u6211\u8fdc\u8fdc\u5c31\u542c\u89c1\u4e86\uff0c\u5979\u59b9\u59b9\u53c8\u4e0d\u662f\u732a\uff0c \u4f60\u4eec\u5c31\u5728\u5979\u65c1\u8fb9\uff0c\u5979\u96be\u9053\u8fd8\u80fd\u771f\u7684\u7761\u5f97\u7740\uff1f\u201d \u3000\u3000\u897f\u95e8\u5341\u4e09\u7684\u8138\u8272\u53c8\u53d8\u4e86\uff0c\u4ed6\u5ffd\u7136\u60f3\u5230\uff0c\u8fd9\u4ef6\u4e8b\u7684\u786e\u53ef\u80fd\u662f\u5979\u59d0\u59b9\u65e9\u5df2\u8bf4\u597d\u4e86\u7684\uff0c\u6240\u4ee5\u4e01 \u9e9f\u521a\u624d\u8d70\uff0c\u59d0\u59d0\u7acb\u523b\u5c31\u9192\u4e86\uff0c\u59b9\u59b9\u5374\u4e00\u76f4\u5728\u9163\u7761\uff0c\u4e3a\u7684\u5c31\u662f\u6545\u610f\u8981\u4f7f\u4ed6\u4eec\u65b9\u4fbf\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u53d1\u73b0\uff0c\u59dc\u6bd5\u7adf\u8fd8\u662f\u8001\u7684\u8fa3\u3002 \u3000\u3000\u536b\u516b\u5927\u7237\u5ffd\u53c8\u95ee\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u5a4a\u5b50\u662f\u4e0d\u662f\u751f\u957f\u5728\u77f3\u5bb6\u5e84\u7684\uff1f\u201d \u3000\u3000\u897f\u95e8\u5341\u4e09\u9053\uff1a\u201c\u597d\u50cf\u4e0d\u662f\uff0c\u6211\u4ee5\u524d\u4e5f\u5230\u8fc7\u77f3\u5bb6\u5e84\u53bb\u8fc7\uff0c\u5374\u4ece\u672a\u89c1\u8fc7\u5979\u4eec\u3002\u201d \u3000\u3000\u536b\u516b\u592a\u7237\u51b7\u7b11\u9053\uff1a\u201c\u679c\u7136\u4e0d\u51fa\u6211\u4eec\u6240\u6599\u3002\u201d \u3000\u3000\u4ed6\u76ee\u5149\u5200\u950b\u822c\u76ef\u5728\u8fd9\u59d0\u59b9\u4e24\u4eba\u8eab\u4e0a\uff0c\u6162\u6162\u5730\u63a5\u7740\u9053\uff1a\u201c\u50cf\u8fd9\u6837\u4e24\u4e2a\u5982\u82b1\u4f3c\u7389\u7684\u5c0f\u59d1\u5a18\uff0c \u8fde\u6211\u90fd\u5b9e\u5728\u4e0d\u5fcd\u770b\u7740\u5979\u4eec\u6b7b\u5728\u6211\u9762\u524d\u3002\u201d \u3000\u3000\u59d0\u59b9\u4e24\u4eba\u8fd8\u662f\u5782\u7740\u5934\u8737\u4f0f\u5728\u90a3\u91cc\uff0c\u9f3b\u606f\u8fd8\u662f\u5f88\u5747\u5300\uff0c\u5c45\u7136\u8fd8\u597d\u50cf\u7761\u5f97\u5f88\u6c89\u3002 \u3000\u3000\u536b\u516b\u592a\u7237\u7a81\u53c8\u8f6c\u5934\uff0c\u77aa\u7740\u897f\u95e8\u5341\u4e09\uff0c\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u6740\u5979\u4eec\u7684\u65f6\u5019\uff0c\u6211\u5b8c\u5168\u95ed\u4e0a\u773c\u775b \u7684\u3002\u201d \u3000\u3000\u897f\u95e8\u5341\u4e09\u6014\u4e86\u6014\uff0c\u9053\uff1a\u201c\u6211\uff1f\u201d \u3000\u3000\u536b\u5929\u9e4f\u6c89\u58f0\u9053\uff0c\u201c\u4e0d\u9519\uff0c\u4f60\u3002\u201d \u3000\u3000\u897f\u95e8\u5341\u4e09\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u8981\u6740\u5979\u4eec\uff1f\u201d \u3000\u3000\u536b\u5929\u9e4f\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u82e5\u820d\u4e0d\u5f97\u6740\u5979\u4eec\uff0c\u6211\u4e5f\u53ef\u4ee5\u8ba9\u5979\u4eec\u6740\u4e86\u4f60\u3002\u201d \u3000\u3000\u897f\u95e8\u5341\u4e09\u8138\u8272\u5df2\u53d1\u767d\uff0c\u9053\uff1a\u201c\u4f46\u4e01\u9e9f\u56de\u6765\u65f6\uff0c\u82e5\u770b\u89c1\u5979\u4eec\u5df2\u6b7b\u4e86\uff0c\u5c82\u975e\u2026\u201d \u3000\u3000\u536b\u516b\u592a\u7237\u6253\u65ad\u4e86\u4ed6\u7684\u8bdd\uff0c\u9053\uff1a\u201c\u4ed6\u770b\u4e0d\u89c1\u7684\u3002\u201d \u3000\u3000\u897f\u95e8\u5341\u4e09\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u536b\u516b\u592a\u7237\u9053\uff1a\u201c\u6b7b\u4eba\u662f\u4ec0\u4e48\u90fd\u770b\u4e0d\u89c1\u7684\u3002\u201d \u3000\u3000\u897f\u95e8\u5341\u4e09\u5931\u58f0\u9053\uff1a\u201c\u4e01\u9e9f\u4e5f\u5f97\u6b7b\uff1f\u201d \u3000\u3000\u536b\u516b\u592a\u7237\u9053\uff1a\u201c\u4ed6\u4e0d\u6b7b\uff0c\u4f60\u5c31\u6b7b\u3002\u201d \u3000\u3000\u897f\u95e8\u5341\u4e09\u770b\u7740\u4ed6\uff0c\u7ec8\u4e8e\u5df2\u660e\u767d\u4ed6\u7684\u610f\u601d\u3002 \u3000\u3000\u4ed6\u8981\u4e01\u9e9f\u5230\u8fd9\u91cc\u6765\u7684\u65f6\u5019\uff0c\u5df2\u6ca1\u6709\u6253\u7b97\u8981\u4e01\u9e9f\u6d3b\u4e0b\u53bb\u3002 \u3000\u3000\u65e0\u8bba\u8fd9\u4ef6\u4e8b\u662f\u5426\u53d1\u751f\uff0c\u65e0\u8bba\u662f\u5426\u80fd\u63a2\u67e5\u51fa\u5357\u6d77\u5a18\u5b50\u7684\u771f\u76f8\uff0c\u4ed6\u53ea\u8981\u4e00\u56de\u6765\uff0c\u5c31\u5f97\u6b7b\uff01\u975e \u6b7b\u4e0d\u53ef\u3002 \u3000\u3000\u6240\u4ee5\u536b\u5929\u9e4f\u624d\u4f1a\u8ddf\u5230\u8fd9\u91cc\u6765\uff0c\u90a3\u8f66\u592b\u5f53\u7136\u4e5f\u65e9\u5df2\u6362\u4e86\u4ed6\u95e8\u4e0b\u7684\u4eba\u3002 \u3000\u3000\u897f\u95e8\u5341\u4e09\u770b\u7740\u4ed6\u8138\u4e0a\u51b7\u9759\u800c\u6b8b\u9177\u7684\u8868\u60c5\uff0c\u51e0\u4e4e\u4e0d\u80fd\u76f8\u4fe1\u4ed6\u5c31\u662f\u90a3\u4e2a\u6027\u5982\u70c8\u706b\u3001\u80f8\u65e0\u57ce \u5e9c\u3001\u7c97\u91ce\u800c\u66b4\u8e81\u7684\u8001\u4eba\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u95f4\u4e5f\u50cf\u662f\u5b8c\u5168\u53d8\u6210\u4e86\u53e6\u5916\u4e00\u4e2a\u4eba\uff0c\u53d8\u5f97\u6bd4\u4e01\u9e9f\u66f4\u5f7b\u5e95\u3002 \u3000\u3000\u897f\u95e8\u5341\u4e09\u5ffd\u7136\u53d1\u73b0\u4e00\u4e2a\u4eba\u82e5\u60f3\u5728\u6c5f\u6e56\u4e2d\u51fa\u4eba\u5934\u5730\uff0c\u5c31\u597d\u50cf\u90fd\u6709\u51e0\u79cd\u5b8c\u5168\u4e0d\u540c\u7684\u9762\u76ee\uff0c\u5c31 \u8fde\u4ed6\u4eec\u8eab\u8fb9\u6700\u4eb2\u8fd1\u7684\u4eba\uff0c\u90fd\u5f88\u96be\u77e5\u9053\u4ed6\u4eec\u7684\u771f\u9762\u76ee\u7a76\u7adf\u662f\u4ec0\u4e48\u6837\u5b50\u3002 \u3000\u3000\u536b\u5929\u9e4f\u5200\u950b\u822c\u7684\u76ee\u5149\u8fd8\u662f\u505c\u5728\u4ed6\u8138\u4e0a\uff0c\u6de1\u6de1\u9053\uff1a\u201c\u7b49\u6b7b\u6bd4\u6b7b\u8fd8\u75db\u82e6\uff0c\u4f60\u82e5\u771f\u7684\u6709\u601c\u9999\u501f \u7389\u4e4b\u5fc3\uff0c\u5c31\u4e0d\u5982\u8ba9\u5979\u4eec\u5feb\u6b7b\u6765\u5f97\u5feb\u4e50\u3002\u201d \u3000\u3000\u897f\u95e8\u5341\u4e09\u54ac\u4e86\u54ac\u7259\uff0c\u7a81\u7136\u51fa\u624b\uff0c\u4e2d\u6307\u6307\u8282\u51f8\u8d77\uff0c\u4ee5\u9e70\u5599\u62f3\u51fb\u5411\u59b9\u59b9\u810a\u690e\u4e0b\u7684\u6b7b\u7a74\uff0c\u59d0\u59d0 \u6bd5\u7adf\u521a\u624d\u8fd8\u5411\u4ed6\u5949\u732e\u51fa\u706b\u4e00\u822c\u7684\u70ed\u60c5\uff0c\u4ed6\u6bd5\u7adf\u4e0d\u662f\u4e2a\u5fc3\u72e0\u624b\u8fa3\u7684\u4eba\u3002 \u3000\u3000\u8c01\u77e5\u5c31\u5728\u8fd9\u65f6\uff0c\u4e00\u76f4\u50cf\u662f\u6b7b\u4e00\u822c\u6c89\u7761\u7740\u7684\u59d0\u59b9\u4e24\u4eba\uff0c\u7a81\u7136\u540c\u65f6\u7ffb\u8eab\uff0c\u624b\u91cc\u5df2\u591a\u4e86\u5bf9\u5f62\u72b6 \u5947\u7279\u78a7\u5149\u95ea\u95ea\u7684\u5f2f\u5200\u3002 \u3000\u3000\u5979\u4eec\u672c\u6765\u6e29\u67d4\u5f97\u5c31\u50cf\u662f\u5bf9\u9e3d\u5b50\uff0c\u4f46\u73b0\u5728\u7684\u51fa\u624b\uff0c\u5374\u6bd4\u6bd2\u86c7\u8fd8\u6bd2\uff0c\u6bd4\u8c7a\u72fc\u8fd8\u72e0\u3002 \u3000\u3000\u59d0\u59d0\u4e00\u7ffb\u8eab\uff0c\u811a\u5df2\u8e22\u5728\u4ed6\u5c0f\u8179\u4e0a\uff0c\u5b50\u91cc\u7684\u5f2f\u5200\uff0c\u5df2\u95ea\u7535\u822c\u53bb\u5272\u536b\u516b\u592a\u7237\u7684\u54bd\u5589\u3002 \u3000\u3000\u897f\u95e8\u5341\u4e09\u75bc\u5f97\u773c\u6cea\u9f3b\u6d95\u4e00\u8d77\u6d41\u51fa\uff0c\u6367\u7740\u5c0f\u8179\u5f2f\u4e0b\u8170\u65f6\uff0c\u59b9\u59b9\u5df2\u6325\u5200\u6025\u65a9\u4ed6\u7684\u5de6\u9888\u3002 \u3000\u3000\u536b\u516b\u592a\u7237\u8138\u4e0a\u7adf\u5168\u65e0\u8868\u60c5\uff0c\u7adf\u4f3c\u65e9\u5df2\u7b97\u51c6\u4e86\u5979\u4eec\u6709\u8fd9\u4e00\u7740\u3002 \u3000\u3000\u59d0\u59b9\u4e24\u4eba\u7684\u5200\u521a\u6325\u51fa\uff0c\u53ea\u542c\u201c\u53ee\uff0c\u53ee\uff0c\u53ee\uff0c\u53ee\u201d\u56db\u58f0\u54cd\uff0c\u56db\u67c4\u5200\u7684\u5200\u950b\u90e8\u5df2\u88ab\u6253\u65ad\u3002 \u3000\u3000\u536b\u516b\u592a\u7237\u624b\u91cc\u5df2\u5ffd\u7136\u51fa\u73b0\u4e86\u6839\u4e00\u5c3a\u4e09\u5bf8\u957f\u7684\u77ed\u68cd\u3002 \u3000\u3000\u77ed\u68cd\u662f\u6f06\u9ed1\u7684\uff0c\u6697\u65e0\u5149\u534e\uff0c\u4e5f\u770b\u4e0d\u51fa\u6709\u4ec0\u4e48\u5947\u7279\u7684\u5730\u65b9\u3002 \u3000\u3000\u4f46\u90a3\u56db\u67c4\u5bd2\u5149\u609f\u7709\u3001\u767e\u70bc\u7cbe\u94a2\u6253\u9020\u7684\u5f2f\u5200\uff0c\u7adf\u88ab\u5b83\u4e00\u6572\u800c\u65ad\u3002 \u3000\u3000\u59d0\u59b9\u4e24\u4eba\u5403\u60ca\u5730\u770b\u7740\u624b\u91cc\u7684\u534a\u622a\u65ad\u5200\uff0c\u51e0\u4e4e\u8fd8\u4e0d\u80fd\u76f8\u4fe1\u8fd9\u662f\u771f\u7684\u3002 \u3000\u3000\u7136\u540e\u5979\u4eec\u624d\u611f\u89c9\u5230\u624b\u81c2\u4e0a\u4e00\u9635\u9178\u75db\uff0c\u8fde\u534a\u622a\u65ad\u5200\u90fd\u62ff\u4e0d\u7a33\u4e86\u3002 \u3000\u3000\u536b\u516b\u5927\u7237\u51b7\u51b7\u5730\u770b\u7740\u5979\u4eec\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u4eec\u7684\u968f\u8eab\u53cc\u5b9d\uff0c\u8fd8\u6709\u4e00\u4ef6\u4e3a\u4ec0\u4e48\u4e0d\u4f7f\u51fa\u6765\uff1f\u201d \u3000\u3000\u59d0\u59d0\u5ffd\u7136\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u539f\u6765\u4f60\u65e9\u5df2\u770b\u51fa\u4e86\u6211\u4eec\u7684\u6765\u5386\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u54fc\u3002\u201d \u3000\u3000\u59d0\u59d0\u9053\uff1a\u201c\u665a\u8f88\u6b63\u662f\u4e1c\u6d77\u7b77\u5b50\u5c9b\uff0c\u73cd\u73e0\u57ce\uff0c\u6b27\u9633\u57ce\u4e3b\u7684\u95e8\u4e0b\uff0c\u7279\u6765\u62dc\u89c1\u536b\u516b\u5927\u7237\u7684\u3002\u201d \u3000\u3000\u5979\u770b\u6765\u5e76\u6ca1\u6709\u60ca\u60f6\u6050\u60e7\u7684\u8868\u60c5\uff0c\u53ea\u4e0d\u8fc7\u5bf9\u536b\u516b\u592a\u7237\u8fd9\u4e2a\u4eba\u597d\u50cf\u5f88\u662f\u5c0a\u656c\u3002 \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u4f60\u4eec\u662f\u6765\u62dc\u8bbf\u6211\u7684\uff1f\u201d \u3000\u3000\u59d0\u59d0\u9053\uff1a\u201c\u6b27\u9633\u57ce\u4e3b\u4e5f\u65e9\u5df2\u4e45\u95fb\u536b\u516b\u592a\u7237\u7684\u5927\u540d\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u662f\u4ed6\u53eb\u4f60\u4eec\u6765\u7684\uff1f\u201d \u3000\u3000\u59d0\u59d0\u9053\uff1a\u201c\u6b63\u662f\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u4f60\u4eec\u8eb2\u5728\u77f3\u5bb6\u5e84\uff0c\u5c31\u662f\u4e3a\u8981\u7b49\u7740\u770b\u6211\uff1f\u201d \u3000\u3000\u59d0\u59d0\u9053\uff1a\u201c\u4f60\u8001\u4eba\u5bb6\u5e9c\u4e0a\u95e8\u7981\u68ee\u4e25\uff0c\u50cf\u6211\u4eec\u59d0\u59b9\u8fd9\u79cd\u4eba\uff0c\u60f3\u89c1\u5230\u4f60\u8001\u4eba\u5bb6\u5f53\u7136\u4e0d\u662f\u4ef6\u5bb9 \u6613\u4e8b\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u51b7\u7b11\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u4eec\u5c31\u6545\u610f\u8ba9\u6211\u8fd9\u597d\u8272\u80c6\u5c0f\u7684\u767b\u5f92\u5b50\u770b\u89c1\u4f60\u4eec\uff0c\u4f60\u4eec\u65e9\u5c31\u7b97\u51c6\u4e86 \u4ed6\u8fdf\u65e9\u4e00\u5b9a\u4f1a\u53bb\u627e\u4f60\u4eec\u7684\u3002\u201d \u3000\u3000\u59d0\u59d0\u7684\u8138\u5c45\u7136\u7ea2\u4e86\uff0c\u7ea2\u7740\u8138\u7b11\u9053\uff1a\u201c\u4e0d\u7792\u4f60\u8001\u4eba\u5bb6\uff0c\u6211\u4eec\u5b9e\u5728\u4e5f\u6ca1\u6709\u60f3\u5230\u4ed6\u4f1a\u5728\u534a\u591c\u91cc \u53bb\u627e\u6211\u4eec\u7684\uff0c\u4ed6\u7528\u7684\u6cd5\u5b50\u867d\u7136\u4e0d\u597d\uff0c\u5374\u5f88\u6709\u6548\u3002\u201d \u3000\u3000\u536b\u5927\u9e4f\u7a81\u7136\u5927\u7b11\u9053\uff1a\u201c\u4e45\u95fb\u6b27\u9633\u57ce\u4e3b\u7684\u95e8\u4e0b\uff0c\u90fd\u662f\u806a\u660e\u7f8e\u4e3d\u7684\u59d0\u59b9\u82b1\uff0c\u4eca\u65e5\u4e00\u89c1\u679c\u7136\u4e0d \u5047\u3002\u201d \u3000\u3000\u4ed6\u4ef0\u9762\u800c\u7b11\uff0c\u4f3c\u5df2\u5fd8\u4e86\u5979\u4eec\u7684\u62a4\u8eab\u53cc\u5b9d\u8fd8\u6709\u4e00\u4ef6\u672a\u4f7f\u51fa\u6765\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u59d0\u59b9\u4e24\u4eba\u5df2\u53c8\u540c\u65f6\u51fa\u624b\uff0c\u53ea\u542c\u201c\u94ee\u201d\u7684\u4e00\u58f0\uff0c\u5df2\u6709\u6570\u5341\u70b9\u5bd2\u661f\uff0c\u4ece\u5979\u4eec\u8863\u8896 \u4e2d\u66b4\u5c04\u800c\u51fa\uff0c\u66b4\u96e8\u822c\u6025\u6253\u536b\u5929\u9e4f\u7684\u80f8\u819b\u3002 \u3000\u3000\u536b\u5929\u9e4f\u7b11\u58f0\u4e0d\u7edd\uff0c\u53ea\u4e0d\u8fc7\u5c06\u624b\u91cc\u7684\u77ed\u68cd\u5f88\u5feb\u5730\u753b\u4e86\u4e2a\u5706\u5f27\u3002 \u3000\u3000\u90a3\u6570\u5341\u70b9\u66b4\u96e8\u822c\u7684\u5bd2\u5149\uff0c\u7adf\u50cf\u662f\u7a81\u7136\u88ab\u4e00\u79cd\u5947\u5f02\u7684\u529b\u91cf\u5438\u5f15\uff0c\u6295\u5165\u4e86\u8fd9\u5706\u5f27\uff0c\u53c8\u662f\u201c\u53ee \u53ee\u53ee\u201d\u4e00\u8fde\u4e32\u8f7b\u54cd\u540e\uff0c\u8fd9\u6570\u5341\u70b9\u5bd2\u5149\u5c31\u5df2\u5168\u90e8\u88ab\u8fd9\u77ed\u68cd\u7c98\u4f4f\uff0c\u5c31\u50cf\u662f\u4e00\u7fa4\u82cd\u8747\u9489\u5728\u4e00\u6839\u94c1\u67f1 \u4e0a\u3002 \u3000\u3000\u59d0\u59b9\u4e24\u4eba\u53c8\u6014\u4f4f\u3002 \u3000\u3000\u536b\u5929\u9e4f\u6de1\u6de1\u9053\uff1a\u201c\u6211\u65e9\u5df2\u77e5\u9053\u4f60\u4eec\u82e5\u4e0d\u5c06\u8fd9\u4e00\u5b9d\u4f7f\u51fa\u6765\uff0c\u662f\u7edd\u4e0d\u4f1a\u7f62\u624b\u7684\u3002\u201d \u3000\u3000\u59b9\u59b9\u5ffd\u7136\u4e5f\u957f\u957f\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u770b\u6765\u4ed6\u4eec\u90fd\u770b\u9519\u4f60\u4e86\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u59b9\u59b9\u9053\uff1a\u201c\u4ed6\u4eec\u4ee5\u4e3a\u4f60\u5df2\u8001\u4e86\uff0c\u4ee5\u4e3a\u4eca\u65e5\u4e4b\u6c5f\u6e56\uff0c\u5df2\u662f\u4ed6\u4eec\u8fd9\u4e00\u4ee3\u5e74\u8f7b\u4eba\u7684\u5929\u4e0b\uff0c\u4f46\u73b0 \u5728\u4ee5\u6211\u770b\u6765\uff0c\u4f60\u4e00\u4e2a\u4eba\u5c31\u53ef\u4ee5\u62b5\u5f97\u4e0a\u4ed6\u4eec\u5341\u4e2a\u3002\u201d \u3000\u3000\u5979\u5782\u7740\u5934\uff0c\u7528\u773c\u89d2\u5077\u5077\u5730\u779f\u7740\u536b\u5929\u9e4f\uff0c\u773c\u6ce2\u4e2d\u5e26\u7740\u79cd\u8bf4\u4e0d\u51fa\u7684\u6e29\u67d4\u5d07\u656c\u4e4b\u8272\u3002 \u3000\u3000\u5c11\u5973\u4eec\u53ea\u6709\u5728\u770b\u7740\u5979\u4eec\u5fc3\u76ee\u4e2d\u771f\u6b63\u7684\u82f1\u96c4\u65f6\uff0c\u624d\u4f1a\u6709\u8fd9\u79cd\u773c\u8272\u3002 \u3000\u3000\u536b\u516b\u592a\u7237\u770b\u6765\u4e5f\u4eff\u4f5b\u5ffd\u7136\u5e74\u8f7b\u4e86\u8bb8\u591a\uff0c\u5fae\u7b11\u7740\u9053\uff1a\u201c\u59dc\u662f\u8001\u7684\u8fa3\uff0c\u8fd9\u53e5\u8bdd\u5e74\u8f7b\u4eba\u90fd\u5e94\u8be5 \u8bb0\u7740\u7684\u3002\u201d \u3000\u3000\u59b9\u59b9\u5782\u7740\u5934\u9053\uff1a\u201c\u6211\u4eec\u521a\u624d\u51fa\u624b\uff0c\u5b9e\u5728\u662f\u4e0d\u5f97\u5df2\u7684\uff0c\u6211\u4eec\u59d0\u59b9\u90fd\u662f\u53ef\u601c\u4eba\uff0c\u522b\u4eba\u53eb\u6211\u4eec \u505a\u4ec0\u4e48\uff0c\u6211\u4eec\u5c31\u5f97\u505a\u4ec0\u4e48\uff0c\u65e2\u4e0d\u80fd\u53cd\u6297\uff0c\u4e5f\u4e0d\u6562\u53cd\u6297\u3002\u201d \u3000\u3000\u5979\u8bf4\u7740\u8bf4\u7740\uff0c\u773c\u6cea\u4f3c\u5df2\u5c06\u6d41\u4e0b\u3002 \u3000\u3000\u536b\u516b\u592a\u7237\u9762\u4e0a\u5df2\u9732\u51fa\u4e86\u540c\u60c5\u4e4b\u8272\uff0c\u53f9\u606f\u7740\u9053\uff1a\u201c\u6211\u4e0d\u602a\u4f60\u4eec\uff0c\u6b27\u9633\u57ce\u4e3b\u5bf9\u95e8\u4e0b\u5b50\u5f1f\u7684\u624b \u6bb5\uff0c\u6c5f\u6e56\u4e2d\u4eba\u4eba\u77e5\u9053\u7684\u3002\u201d \u3000\u3000\u59d0\u59d0\u6068\u58f0\u9053\uff1a\u201c\u4f46\u9664\u4e86\u4f60\u8001\u4eba\u5bb6\u8fd9\u79cd\u5927\u82f1\u96c4\u5916\uff0c\u53ef\u6709\u8c01\u4f1a\u4f53\u8c05\u6211\u4eec\u7684\u75db\u82e6\u5462\uff1f\u201d \u3000\u3000\u536b\u516b\u592a\u7237\u7684\u58f0\u97f3\u4e5f\u53d8\u5f97\u5f88\u6e29\u67d4\uff0c\u9053\uff1a\u201c\u53ea\u8981\u4f60\u4eec\u8bf4\u51fa\u4f60\u4eec\u7684\u6765\u610f\uff0c\u6211\u7edd\u4e0d\u4f1a\u4e3a\u96be\u4f60\u4eec \u7684\u3002\u201d \u3000\u3000\u59d0\u59d0\u9053\uff1a\u201c\u5728\u4f60\u8001\u4eba\u5bb6\u9762\u524d\uff0c\u6211\u4eec\u4e5f\u4e0d\u6562\u8bf4\u8c0e\u3002\u201d \u3000\u3000\u59b9\u59b9\u9053\uff1a\u201c\u4f60\u8001\u4eba\u5bb6\u5f53\u7136\u4e5f\u5df2\u77e5\u9053\uff0c\u6211\u4eec\u662f\u4e3a\u4e86\u53f6\u5f00\u548c\u4e0a\u5b98\u5c0f\u4ed9\u6765\u7684\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u4e3a\u4e86\u8fd9\u4ef6\u4e8b\uff0c\u73cd\u73e0\u57ce\u91cc\u4e00\u5171\u6765\u4e86\u591a\u5c11\u4eba\uff1f\u201d \u3000\u3000\u59b9\u59b9\u9053\uff1a\u201c\u53ea\u6709\u6211\u4eec\u59d0\u59b9\u4e24\u4e2a\u3002\u201d \u3000\u3000\u59d0\u59d0\u9053\uff1a\u201c\u6b27\u9633\u57ce\u4e3b\u7684\u610f\u601d\uff0c\u5e76\u4e0d\u662f\u771f\u7684\u60f3\u8981\u90a3\u4e9b\u4e1c\u897f\uff0c\u53ea\u4e0d\u8fc7\u8981\u6211\u4eec\u6765\u770b\u770b\uff0c\u53f6\u5f00\u7a76 \u7adf\u662f\u4e2a\u600e\u4e48\u6837\u7684\u4eba\uff0c\u7a76\u7adf\u6709\u591a\u5389\u5bb3\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u4f60\u4eec\u5f88\u5feb\u5c31\u4f1a\u770b\u5f97\u5230\u7684\uff0c\u4ed6\u5f88\u5feb\u5c31\u4f1a\u6765\u4e86\u3002\u201d \u3000\u3000\u59d0\u59d0\u9053\uff1a\u201c\u53ef\u662f\u6211\u4eec\u2026\u2026\u201d \u3000\u3000\u536b\u5929\u9e4f\u5fae\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u5df2\u7ecf\u53ef\u4ee5\u8d70\u4e86\uff0c\u4ee5\u540e\u6709\u673a\u4f1a\uff0c\u968f\u65f6\u90fd\u53ef\u4ee5\u53bb\u770b\u6211\uff0c\u7528\u4e0d\u7740\u518d\u8eb2\u5728 \u77f3\u5bb6\u5e84\u7b49\u3002\u201d \u3000\u3000\u59d0\u59d0\u4e5f\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u4ee5\u540e\u6211\u4eec\u4e00\u5b9a\u4f1a\u53bb\u62dc\u8bbf\u4f60\u8001\u4eba\u5bb6\u3002\u201d \u3000\u3000\u59b9\u59b9\u7acb\u523b\u63a5\u7740\u9053\uff1a\u201c\u6211\u4eec\u4e00\u5b9a\u4f1a\u53bb\u3002\u201d \u3000\u3000\u59d0\u59b9\u4e24\u4eba\u751c\u751c\u5730\u7b11\u7740\uff0c\u8f6c\u8eab\u63a8\u5f00\u4e86\u8f66\u53a2\u7684\u95e8\uff0c\u8df3\u4e86\u51fa\u53bb\uff0c\u5c31\u50cf\u662f\u4e00\u53cc\u521a\u98de\u51fa\u7b3c\u5b50\u7684\u71d5 \u5b50\u3002 \u3000\u3000\u4e00\u76f4\u5782\u5934\u4e27\u6c14\u7ad9\u5728\u90a3\u91cc\u7684\u897f\u95e8\u5341\u4e09\uff0c\u597d\u50cf\u89c9\u5f97\u5f88\u610f\u5916\u3002 \u3000\u3000\u4ed6\u60f3\u4e0d\u5230\u536b\u516b\u592a\u7237\u4f1a\u8ba9\u5979\u4eec\u8d70\uff0c\u5c31\u5728\u8fd9\u65f6\uff0c\u4ed6\u5ffd\u7136\u542c\u89c1\u4e24\u58f0\u5f88\u5947\u602a\u7684\u58f0\u97f3\uff0c\u5c31\u50cf\u662f\u9525\u5b50 \u523a\u5165\u8089\u91cc\u3002 \u3000\u3000\u63a5\u7740\uff0c\u4ed6\u53c8\u542c\u89c1\u4e24\u58f0\u5c16\u9510\u800c\u77ed\u4fc3\u7684\u60e8\u547c\u3002 \u3000\u3000\u4ed6\u5fcd\u4e0d\u4f4f\u56de\u5934\u53bb\u770b\uff0c\u5c31\u770b\u89c1\u4e00\u4e2a\u7a7f\u7740\u9752\u5e03\u68c9\u8884\u7684\u4eba\uff0c\u6b63\u7ad9\u5728\u8f66\u53a2\u5916\uff0c\u7528\u4e00\u6761\u96ea\u767d\u624b\u4e2d\u64e6 \u9525\u5b50\u4e0a\u7684\u8840\u3002 \u3000\u3000\u4ed6\u624b\u91cc\u62ff\u7684\uff0c\u7adf\u8d6b\u7136\u771f\u662f\u4e00\u67c4\u53d1\u4eae\u7684\u9525\u5b50\u3002 \u3000\u3000\u97e9\u8d1e\uff01 \u3000\u3000\u897f\u95e8\u5341\u4e09\u76f4\u5230\u73b0\u5728\u624d\u77e5\u9053\uff0c\u628a\u4ed6\u4eec\u9001\u5230\u8fd9\u91cc\u6765\u7684\u8f66\u592b\u7ade\u662f\u97e9\u8d1e\u3002 \u3000\u3000\u97e9\u8d1e\u7684\u9f3b\u5b50\u662f\u6b6a\u7740\u7684\uff0c\u9f3b\u6881\u5df2\u88ab\u4e01\u9e9f\u4e00\u62f3\u6253\u788e\uff0c\u8fd9\u6b6a\u659c\u788e\u88c2\u7684\u9f3b\u5b50\uff0c\u4f7f\u5f97\u4ed6\u8138\u4e0a\u770b\u6765\u603b \u597d\u50cf\u5e26\u7740\u79cd\u5947\u7279\u800c\u8be1\u5f02\u7684\u8868\u60c5\u3002 \u3000\u3000\u536b\u516b\u5927\u7237\u8138\u4e0a\u5374\u65e0\u8868\u60c5\uff0c\u5ffd\u7136\u9053\uff1a\u201c\u4e24\u4e2a\u90fd\u6b7b\u4e86\uff1f\u201d \u3000\u3000\u97e9\u8d1e\u70b9\u70b9\u5934\u3002 \u3000\u3000\u536b\u516b\u592a\u7237\u6de1\u6de1\u9053\uff1a\u201c\u770b\u6765\u4f60\u5b9e\u5728\u4e0d\u662f\u601c\u9999\u501f\u7389\u7684\u4eba\u3002\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u6211\u4e0d\u662f\u3002\u201d \u3000\u3000\u536b\u516b\u592a\u7237\u76ee\u4e2d\u9732\u51fa\u7b11\u610f\uff0c\u9053\uff1a\u201c\u4e01\u9e9f\u82e5\u77e5\u9053\u4f60\u6740\u4e86\u5979\u4eec\uff0c\u4f60\u7684\u9f3b\u5b50\u5c31\u66f4\u5371\u9669\u4e86\u3002\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u4ed6\u4e0d\u4f1a\u77e5\u9053\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u6b7b\u4eba\u662f\u4ec0\u4e48\u4e8b\u90fd\u4e0d\u4f1a\u77e5\u9053\u7684\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u7b11\u4e86\uff0c\u4ed6\u559c\u6b22\u522b\u4eba\u5b66\u4ed6\u8bf4\u8bdd\u7684\u53e3\u6c14\u3002 \u3000\u3000\u97e9\u8d1e\u5374\u53c8\u9053\uff1a\u201c\u4ed6\u8d70\u7684\u65f6\u5019\uff0c\u53ea\u8981\u6211\u4eec\u7b49\u4ed6\u4e00\u4e2a\u65f6\u8fb0\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u4ed6\u5f53\u7136\u5df2\u5c06\u65f6\u95f4\u7b97\u5f97\u5f88\u51c6\u3002\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\u4ed6\u90fd\u7b97\u5f97\u5f88\u51c6\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u51b7\u51b7\u9053\uff1a\u201c\u4ed6\u7684\u786e\u662f\u4e2a\u5f88\u5389\u5bb3\u7684\u4eba\uff0c\u552f\u4e00\u7684\u7f3a\u70b9\u5c31\u662f\u592a\u5e74\u8f7b\u3002\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u4ed6\u6c38\u8fdc\u4e0d\u4f1a\u8d70\u7684\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u6b7b\u4eba\u662f\u4e0d\u4f1a\u8d70\u7684\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u53c8\u7b11\u4e86\u3002 \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u73b0\u5728\u65e9\u5df2\u8fc7\u4e86\u4e00\u4e2a\u65f6\u8fb0\uff0c\u4ed6\u8fd8\u6ca1\u6709\u56de\u6765\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u76ee\u5149\u95ea\u52a8\uff0c\u9053\uff1a\u201c\u6240\u4ee5\u4ed6\u53ea\u6015\u5df2\u6c38\u8fdc\u4e0d\u4f1a\u56de\u6765\u4e86\uff01\u201d \u3000\u3000\u97e9\u8d1e\u70b9\u70b9\u5934\u3002 \u3000\u3000\u536b\u5929\u9e4f\u51b7\u7b11\u7740\u7f13\u7f13\u9053\uff1a\u201c\u6240\u4ee5\u8fd9\u4e2a\u5357\u6d77\u5a18\u5b50\uff0c\u7edd\u4e0d\u4f1a\u662f\u5047\u7684\u3002\u201d \u3000\u3000\u97e9\u8d1e\u540c\u610f\u9053\uff1a\u201c\u80fd\u8ba9\u4e01\u9e9f\u7559\u4e0b\u7684\u4eba\u5e76\u4e0d\u591a\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u7684\u8138\u8272\u5ffd\u53c8\u53d8\u5f97\u5f88\u9634\u6c89\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u9752\u57ce\u5c71\u7684\u58a8\u767d\uff0c\u73cd\u73e0\u57ce\u7684\u6b27\u9633\uff0c\u518d\u52a0\u4e0a\u5357\u6d77 \u5a18\u5b50\u2026\u2026\u8fd9\u4e16\u4e0a\uff0c\u672c\u6765\u5df2\u6ca1\u6709\u4ec0\u4e48\u4e8b\u80fd\u6253\u52a8\u4ed6\u4eec\u7684\u4e86\uff0c\u4f46\u73b0\u5728\u4ed6\u4eec\u90fd\u5df2\u51fa\u624b\u3002\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u53f6\u5f00\u82e5\u77e5\u9053\uff0c\u4e00\u5b9a\u4f1a\u89c9\u5f97\u5f88\u6109\u5feb\uff01\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u6109\u5feb\uff1f\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u80fd\u591f\u8981\u8fd9\u4e9b\u4eba\u51fa\u624b\uff0c\u5e76\u4e0d\u662f\u4ef6\u5bb9\u6613\u7684\u4e8b\uff0c\u9664\u4e86\u4ed6\u4e4b\u5916\uff0c\u4e16\u4e0a\u4e5f\u8bb8\u5df2\u6ca1\u6709\u7b2c\u4e8c \u4e2a\u4eba\u8fd8\u80fd\u5f15\u52a8\u4ed6\u4eec\u5230\u8fd9\u91cc\u6765\uff4c\u201d\u536b\u5929\u9e4f\u6c89\u9ed8\u7740\uff0c\u5c45\u7136\u4e5f\u5df2\u627f\u8ba4\u3002 \u3000\u3000\u897f\u95e8\u5341\u4e09\u5f53\u7136\u66f4\u4e0d\u6562\u5f00\u53e3\uff0c\u4f46\u5fc3\u91cc\u5374\u66f4\u597d\u5947\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u53d1\u89c9\u6bcf\u4e2a\u4eba\u63d0\u8d77\u53f6\u5f00\u8fd9\u540d\u5b57\u65f6\uff0c\u90fd\u4f1a\u9732\u51fa\u79cd\u5f88\u5947\u602a\u7684\u8868\u60c5\uff0c\u65e0\u8bba\u662f\u656c\u4f69\uff0c\u662f\u618e \u6076\uff0c\u8fd8\u662f\u754f\u60e7\uff0c\u90fd\u8868\u73b0\u5f97\u975e\u5e38\u660e\u663e\u5f3a\u70c8\u3002 \u3000\u3000\u4e00\u4e2a\u964c\u751f\u7684\u5e74\u8f7b\u4eba\uff0c\u600e\u4e48\u4f1a\u6709\u8fd9\u4e48\u5927\u7684\u9b54\u529b\uff0c\u8fd9\u5c82\u975e\u4ee4\u4eba\u4e0d\u53ef\u6069\u8bae\uff1f \u3000\u3000\u897f\u95e8\u5341\u4e09\u53ea\u89c9\u5f97\u81ea\u5df1\u5f88\u5e78\u8fd0\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u4e0d\u662f\u53f6\u5f00\uff0c\u4ed6\u5ffd\u7136\u53d1\u89c9\u505a\u4e00\u4e2a\u5e73\u51e1\u5eb8\u4fd7\u7684\u4eba\uff0c\u6709\u65f6\u4e5f\u662f\u4ef6\u5f88\u5e78\u8fd0\u7684\u4e8b\u3002 \u3000\u3000\u536b\u5929\u9e4f\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u624d\u7f13\u7f13\u9053\uff1a\u201c\u4e00\u5e74\u4e4b\u524d\uff0c\u6211\u8fd8\u6ca1\u542c\u89c1\u8fc7\u53f6\u5f00\u8fd9\u540d\u5b57\u3002\u201d \u3000\u3000\u4f46\u73b0\u5728\u4ed6\u597d\u50cf\u5ffd\u7136\u5df2\u53d8\u6210\u4e86\u6c5f\u6e56\u4e2d\u6700\u6709\u540d\u7684\u4eba\uff01 \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\u5d1b\u8d77\u6c5f\u6e56\uff0c\u7684\u786e\u662f\u4e2a\u5947\u8ff9\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u8981\u9020\u6210\u5947\u8ff9\u4e5f\u4e0d\u662f\u4ef6\u5bb9\u6613\u7684\u4e8b\u3002\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u7edd\u4e0d\u662f\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u4ed6\u771f\u6709\u4f20\u8bf4\u4e2d\u90a3\u4e48\u53ef\u6015\uff1f\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u4ed6\u5e76\u6ca1\u6709\u6740\u8fc7\u4ec0\u4e48\u4eba\uff0c\u751a\u81f3\u6839\u672c\u5c31\u4e0d\u51fa\u624b\uff0c\u6c5f\u6e56\u4e2d\u51e0\u4e4e\u6ca1\u6709\u4eba\u77e5\u9053\u4ed6\u7684\u6b66\u529f \u6df1\u6d45\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u4e5f\u8bb8\u8fd9\u5c31\u662f\u4ed6\u7684\u53ef\u6015\u4e4b\u5904\u3002\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u4f46\u662f\u6700\u53ef\u6015\u7684\uff0c\u8fd8\u662f\u4ed6\u7684\u5200\uff01\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u4ec0\u4e48\u5200\uff1f\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u98de\u5200\uff01\u201d \u3000\u3000\u4ed6\u8138\u4e0a\u5ffd\u53c8\u9732\u51fa\u79cd\u5f88\u5947\u602a\u7684\u8868\u60c5\uff0c\u4e00\u5b57\u5b57\u63a5\u7740\u9053\uff1a\u201c\u636e\u8bf4\u4ed6\u7684\u98de\u5200\u53ea\u8981\u51fa\u624b\uff0c\u4e5f\u4ece\u672a\u843d \u7a7a\u8fc7\u4e00\u6b21\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u7684\u8138\u8272\u4e5f\u53d8\u4e86\uff0c\u4ed6\u5ffd\u7136\u60f3\u8d77\u4e00\u53e5\u6d3b\uff1a\u201c\u5c0f\u674e\u98de\u5200\uff0c\u5200\u4e0d\u865a\u53d1\uff01\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u672c\u8eab\u5c31\u50cf\u662f\u6709\u79cd\u8db3\u4ee5\u593a\u4eba\u9b42\u9b44\u7684\u9b54\u529b\u3002 \u3000\u3000\u6570\u5341\u5e74\u6765\uff0c\u6c5f\u6e56\u4e2d\u4ece\u6ca1\u6709\u4efb\u4f55\u4eba\u5bf9\u8fd9\u53e5\u8bdd\u6709\u8fc7\u4e1d\u6beb\u6000\u7591\uff0c\u66f4\u6ca1\u6709\u4efb\u4f55\u4eba\u6562\u53bb\u8bd5\u4e00\u8bd5\u3002 \u3000\u3000\u751a\u81f3\u8fde\u6614\u5e74\u54b8\u9707\u5929\u4e0b\u7684\u5c11\u6797\u56db\u5927\u9ad8\u50e7\u90fd\u4e0d\u6562\uff01 \u3000\u3000\u4e8c\u5341\u5e74\u524d\uff0c\u5c0f\u674e\u63a2\u82b1\u72ec\u4e0a\u5d69\u5c71\uff0c\u7adf\u5c06\u6b66\u6797\u4e2d\u4ece\u672a\u6709\u4eba\u6562\u8f7b\u8d8a\u96f7\u6c60\u4e00\u6b65\u7684\u5c11\u6797\u5bfa\uff0c\u5f53\u505a\u4e86 \u65e0\u4eba\u4e4b\u5730\uff0c\u5c11\u6797\u5bfa\u4e0a\u4e0b\u6570\u767e\u9ad8\u624b\uff0c\u7adf\u6ca1\u6709\u4e00\u4e2a\u6562\u51fa\u624b\u7684\u3002 \u3000\u3000\u4eca\u65e5\u4e4b\u53f6\u5f00\u96be\u9053\u4e5f\u6709\u90a3\u6837\u7684\u5a01\u98ce\u3001\u90a3\u6837\u7684\u8c6a\u6c14\uff1f \u3000\u3000\u5c31\u7b97\u4ed6\u4e5f\u6709\u90a3\u6837\u7684\u672c\u4e8b\uff0c\u73cd\u73e0\u57ce\u4e3b\u548c\u5357\u6d77\u5a18\u5b50\u7684\u624b\u6bb5\uff0c\u4e5f\u7edd\u4e0d\u662f\u90a3\u4e9b\u51fa\u5bb6\u4eba\u80fd\u6bd4\u5f97\u4e0a \u7684\u3002 \u3000\u3000\u536b\u5929\u9e4f\u7f13\u7f13\u9053\uff1a\u201c\u73cd\u73e0\u57ce\u8fdc\u5728\u5929\u5916\uff0c\u57ce\u4e3b\u6b27\u9633\u5144\u59b9\u6b66\u529f\u4e4b\u5947\u7edd\uff0c\u5c31\u8fde\u6614\u5e74\u7684\u767e\u6653\u751f\u90fd\u83ab \u6d4b\u9ad8\u6df1\uff0c\u6240\u4ee5\u624d\u6ca1\u6709\u5c06\u4ed6\u4eec\u5217\u5728\u5175\u5668\u8c31\u4e0a\u3002\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u90a3\u4e5f\u56e0\u4e3a\u7b77\u5b50\u5c9b\u4e0a\u7684\u5f1f\u5b50\uff0c\u90fd\u662f\u540c\u80de\u53cc\u751f\u7684\u5144\u5f1f\u59d0\u59b9\uff0c\u5c31\u50cf\u662f\u7b77\u5b50\u4e00\u6837\uff0c\u4ece \u6765\u5206\u4e0d\u5f00\u7684\uff0c\u6240\u4ee5\u5175\u5668\u8c31\u4e0a\u4e0d\u5217\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u70b9\u70b9\u5934\u9053\uff1a\u201c\u5175\u5668\u8c31\u4e0a\u4e0d\u5217\u9b54\u90fd\u9ad8\u624b\uff0c\u4f46\u7adf\u8fde\u767e\u6653\u751f\u81ea\u5df1\u4e5f\u4e0d\u80fd\u4e0d\u627f\u8ba4\uff0c\u82e5\u4ee5\u6740 \u4eba\u5236\u654c\u7684\u6b66\u529f\u800c\u8bba\uff0c\u9b54\u6559\u4e2d\u81f3\u5c11\u6709\u4e03\u4e2a\u4eba\u53ef\u6392\u5728\u5175\u5668\u8c31\u4e0a\u7684\u524d\u4e8c\u5341\u4eba\u4e4b\u5185\u3002\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u9b54\u6559\u4e2d\u4eba\uff0c\u4e92\u76f8\u731c\u7591\uff0c\u4e92\u76f8\u6b8b\u6740\uff0c\u9b54\u5bab\u4e2d\u7684\u9ad8\u624b\uff0c\u636e\u8bf4\u65e9\u5df2\u5feb\u6b7b\u5149\u4e86\uff01\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u4f46\u5357\u6d77\u5a18\u5b50\u5343\u53d8\u4e07\u5316\uff0c\u9b54\u529f\u79d8\u6280\uff0c\u7edd\u4e0d\u5728\u9b54\u6559\u4e03\u5927\u5929\u738b\u4e4b\u4e0b\u3002\u201d \u3000\u3000\u97e9\u8d1e\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u8001\u4eba\u5bb6\u624b\u91cc\u8fd9\u6839\u5341\u65b9\u5982\u610f\u68d2\uff0c\u53ea\u6015\u4e5f\u53ef\u548c\u6614\u5e74\u5175\u5668\u8c31\u4e0a\uff0c\u6392\u540d\u7b2c \u4e00\u7684\u5929\u673a\u68d2\u6bd4\u4e00\u6bd4\u9ad8\u4e0b\u4e86\uff01\u201d \u3000\u3000\u536b\u5929\u9e4f\u7a81\u7136\u7eb5\u58f0\u5927\u7b11\uff0c\u9053\uff1a\u201c\u53f6\u5f00\u82e5\u77e5\u9053\u6211\u4eec\u8fd9\u4e9b\u4eba\u5728\u8fd9\u91cc\u7b49\u7740\u4ed6\uff0c\u4ed6\u8fd8\u6562\u6765\u4e48\uff1f\u201d \u3000\u3000\u7a81\u7136\u4e00\u4e2a\u4eba\u60a0\u7136\u9053\uff1a\u201c\u4ed6\u4e00\u5b9a\u4f1a\u6765\u7684\uff0c\u56e0\u4e3a\u4ed6\u975e\u6765\u4e0d\u53ef\u3002\u201d \u3000\u3000\u8fd9\u58f0\u97f3\u4f18\u96c5\u800c\u795e\u79d8\uff0c\u8bf4\u8bdd\u7684\u4eba\u4eff\u4f5b\u5c31\u5728\u4ed6\u4eec\u8eab\u65c1\uff0c\u53c8\u4eff\u4f5b\u5728\u5f88\u8fdc\u3002 \u3000\u3000\u536b\u5929\u9e4f\u7684\u7b11\u58f0\u7a81\u7136\u505c\u987f\uff0c\u8138\u8272\u4e5f\u53d8\u4e86\uff0c\u8fc7\u4e86\u5f88\u4e45\u624d\u8bd5\u63a2\u7740\u95ee\u9053\uff1a\u201c\u5357\u6d77\u5a18\u5b50\uff1f\u201d \u3000\u3000\u201c\u591a\u5e74\u7684\u6545\u4eba\uff0c\u4f60\u96be\u9053\u8fde\u6211\u7684\u58f0\u97f3\u90fd\u542c\u4e0d\u51fa\u6765\uff1f\u201d\u90a3\u58f0\u97f3\u4eff\u4f5b\u66f4\u8fd1\uff0c\u5374\u770b\u4e0d\u89c1\u4eba\u3002 \u3000\u3000\u536b\u5929\u9e4f\u989d\u4e0a\u4f3c\u5df2\u6709\u4e86\u51b7\u6c57\uff0c\u52c9\u5f3a\u7b11\u9053\uff1a\u201c\u65e2\u5df2\u6765\u4e86\u4e3a\u4f55\u4e0d\u73b0\u8eab\u76f8\u89c1\uff1f\u201d \u3000\u3000\u201c\u4f60\u771f\u7684\u60f3\u89c1\u6211\uff1f\u201d \u3000\u3000\u201c\u591a\u5e74\u6e34\u601d\uff0c\u4f46\u6c42\u4e00\u89c1\u3002\u201d \u3000\u3000\u201c\u597d\uff0c\u4f60\u8ddf\u6211\u6765\u3002\u201d \u3000\u3000\u90a3\u58f0\u97f3\u4eff\u4f5b\u53c8\u5df2\u5230\u4e86\u8fdc\u65b9\u7684\u9ed1\u6697\u4e2d\uff0c\u9ed1\u6697\u4e2d\u5ffd\u7136\u4eae\u8d77\u70b9\u706f\u5149\u3002 \u3000\u3000\u78a7\u7cbc\u8d3c\u7684\u706f\u5149\uff0c\u5c31\u50cf\u662f\u9b3c\u706b\uff0c\u5728\u5bd2\u98ce\u4e2d\u95ea\u70c1\u4e0d\u505c\uff0c\u5374\u8fd8\u662f\u770b\u4e0d\u89c1\u4eba\u6d74\u536b\u5929\u9e4f\u8fdf\u7591\u7740\uff0c\u5ffd \u7136\u62cd\u4e86\u62cd\u97e9\u8d1e\u7684\u80a9\u9053\uff1a\u201c\u4f60\u4e5f\u8ddf\u6211\u4e00\u8d77\u6765\u3002\u201d \u3000\u3000\u897f\u95e8\u5341\u4e09\u603b\u7b97\u5750\u4e86\u4e0b\u6765\uff0c\u5fc3\u91cc\u5374\u6bd4\u521a\u624d\u5f2f\u8170\u7ad9\u7740\u7684\u65f6\u5019\u8fd8\u8981\u96be\u53d7\u3002 \u3000\u3000\u5929\u5730\u95f4\u4eff\u4f5b\u5df2\u53ea\u5269\u4e0b\u4ed6\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u536b\u516b\u592a\u7237\u662f\u4ed6\u7684\u5e08\u5085\uff0c\u5374\u5e26\u7740\u90a3\u4e2a\u591a\u5634\u7684\u97e9\u8d1e\u8d70\u4e86\uff0c\u597d\u50cf\u6839\u672c\u5df2\u5fd8\u4e86\u8fd8\u6709\u4ed6\u8fd9\u4e48\u6837\u4e00\u4e2a \u4eba\u5728\u65c1\u8fb9\u3002 \u3000\u3000\u8fd9\u4e16\u4e0a\u7adf\u4f3c\u6ca1\u6709\u4e00\u4e2a\u4eba\u770b\u91cd\u4ed6\uff0c\u7b80\u76f4\u5c31\u6ca1\u6709\u4e00\u4e2a\u4eba\u5c06\u4ed6\u770b\u5728\u773c\u91cc\u3002 \u3000\u3000\u2014\u2014\u2014\u4e2a\u4eba\u82e5\u5df2\u8fde\u81ea\u5df1\u90fd\u8f7b\u89c6\u81ea\u5df1\uff0c\u53c8\u600e\u80fd\u671f\u671b\u522b\u4eba\u770b\u91cd\u4f60\u3002 \u3000\u3000\u4ed6\u7528\u529b\u63e1\u7d27\u4e86\u53cc\u62f3\uff0c\u5fc3\u91cc\u5145\u6ee1\u4e86\u59d4\u5c48\u548c\u6124\u6012\uff0c\u4ed6\u53d1\u8a93\u8981\u505a\u51e0\u4ef6\u60ca\u4eba\u7684\u4e8b\uff0c\u8ba9\u5927\u5bb6\u90fd\u77e5\u9053 \u4ed6\u897f\u95e8\u5341\u4e09\u5e76\u4e0d\u662f\u4e2a\u6ca1\u51fa\u606f\u7684\u4eba\uff0c\u8ba9\u5927\u5bb6\u90fd\u8dea\u5728\u4ed6\u9762\u524d\uff0c\u543b\u4ed6\u7684\u811a\u3002 \u3000\u3000\u53ea\u4e0d\u8fc7\uff0c\u8981\u600e\u4e48\u6837\u624d\u80fd\u505a\u51fa\u60ca\u4eba\u7684\u4e8b\u5462\uff1f\u4ed6\u6839\u672c\u8fde\u4e00\u70b9\u5934\u7eea\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u8fd9\u4f7f\u4ed6\u53c8\u89c9\u5f97\u5f88\u60b2\u54c0\u3002 \u3000\u3000\u4e0d\u5982\u8fd8\u662f\u627e\u4e2a\u5730\u65b9\u53bb\u75db\u75db\u5feb\u7094\u5730\u5927\u559d\u4e00\u987f\uff0c\u7b49\u5230\u559d\u9189\u4e86\u65f6\uff0c\u5c31\u4f1a\u89c9\u5f97\u81ea\u5df1\u662f\u4e2a\u6253\u904d\u5929\u4e0b \u65e0\u654c\u624b\u7684\u5927\u82f1\u96c4\u4e86\u3002\u53ea\u53ef\u60dc\u8fd9\u5927\u82f1\u96c4\u73b0\u5728\u8fd8\u662f\u8981\u81ea\u5df1\u53bb\u5957\u9a6c\u8d76\u8f66\u3002 \u3000\u3000\u4ed6\u53f9\u4e86\u53e3\u6c14\uff0c\u6ca1\u7cbe\u6253\u91c7\u5730\u7ad9\u4e86\u8d77\u6765\uff0c\u5ffd\u7136\u542c\u5230\u8f66\u53a2\u5916\u6709\u4eba\u8bf4\uff1a\u201c\u4f60\u4e00\u4e2a\u4eba\u5750\u5728\u8fd9\u91cc\uff0c\u4e5f \u4e0d\u89c9\u5f97\u5bc2\u5bde\uff1f\u201d \u3000\u3000\u8fd8\u662f\u521a\u624d\u90a3\u795e\u79d8\u800c\u4f18\u96c5\u7684\u58f0\u97f3\uff0c\u53e3\u6c14\u5374\u6bd4\u4ee5\u524d\u66f4\u6162\u3002\u897f\u95e8\u5341\u4e09\u5168\u8eab\u90fd\u5df2\u51b0\u51b7\uff0c\u5c31\u597d\u50cf\u4e00 \u4e0b\u5b50\u8dcc\u5165\u4e86\u4e2a\u6df1\u4e0d\u89c1\u5e95\u7684\u51b0\u6d1e\u91cc\u3002 \u3000\u3000\u4ed6\u5df2\u770b\u89c1\u4e86\u8fd9\u4e2a\u4eba\uff0c\u770b\u5f97\u5f88\u6e05\u695a\u3002 \u3000\u3000\u5979\u7684\u8138\u662f\u6b7b\u7070\u8272\u7684\uff0c\u8f7b\u67d4\u7684\u957f\u888d\u4e0a\u9c9c\u8840\u6dcb\u6f13\uff0c\u54bd\u5589\u4e0a\u8fd8\u6709\u4e2a\u8840\u6d1e\uff0c\u8d6b\u7136\u6b63\u662f\u521a\u624d\u5df2\u6b7b\u5728 \u97e9\u8d1e\u9525\u4e0b\u7684\u90a3\u4e2a\u59d0\u59d0\u3002 \u3000\u3000\u5979\u6b7b\u7070\u8272\u7684\u8138\u4e0a\uff0c\u5b8c\u5168\u6ca1\u6709\u4efb\u4f55\u8868\u60c5\uff0c\u7f8e\u4e3d\u7684\u773c\u775b\u5df2\u6b7b\u9c7c\u822c\u51f8\u51fa\u6765\uff0c\u5634\u89d2\u4e5f\u5e26\u7740\u8840\u6e0d\uff0c \u5728\u9ed1\u6697\u4e2d\u770b\u6765\uff0c\u66f4\u662f\u8bf4\u4e0d\u51fa\u7684\u8be1\u79d8\u53ef\u6016\u3002 \u3000\u3000\u897f\u95e8\u5341\u4e09\u7684\u817f\u5df2\u8f6f\u4e86\uff0c\u51b7\u6c57\u5df2\u6e7f\u900f\u4e86\u91cd\u8863\u3002\u4ed6\u5b9e\u5728\u4e0d\u6562\u518d\u770b\u5979\uff0c\u4f46\u4e5f\u4e0d\u77e5\u4e3a\u4e86\u4ec0\u4e48\uff0c\u76ee \u5149\u7adf\u504f\u504f\u65e0\u6cd5\u4ece\u5979\u8138\u4e0a\u79fb\u5f00\u3002 \u3000\u3000\u201c\u4f60\u770b\u7740\u6211\u2026\u2026\u6211\u77e5\u9053\u4f60\u4e00\u5b9a\u4f1a\u770b\u7740\u6211\u7684\uff01\u201d \u3000\u3000\u8fd9\u672c\u4e0d\u662f\u5979\u751f\u524d\u8bf4\u8bdd\u7684\u58f0\u97f3\uff0c\u4f46\u8fd9\u58f0\u97f3\u5374\u7684\u786e\u662f\u5979\u53d1\u51fa\u6765\u7684\u3002 \u3000\u3000\u201c\u6211\u672c\u6765\u662f\u771f\u5fc3\u559c\u6b22\u4f60\u7684\uff0c\u672c\u6765\u5df2\u51b3\u5fc3\u6c38\u8fdc\u966a\u7740\u4f60\uff0c\u4ed6\u4eec\u5374\u72e0\u5fc3\u6740\u4e86\u6211\uff0c\u8ba9\u4f60\u5b64\u5355\u5355 \u7684\uff0c\u6ca1\u6709\u4eba\u966a\u4f34\u3002\u201d \u3000\u3000\u90a3\u58f0\u97f3\u53c8\u53d8\u5f97\u51c4\u51c9\u800c\u5e7d\u6028\uff0c\u90a3\u6b7b\u9c7c\u822c\u51f8\u51fa\u7684\u773c\u775b\u91cc\u7adf\u4f3c\u6709\u4e24\u884c\u670d\u6cea\u6d41\u4e0b\u3002 \u3000\u3000\u897f\u95e8\u5341\u4e09\u53ea\u89c9\u5f97\u81ea\u5df1\u7684\u5fc3\u5df2\u788e\u4e86\uff0c\u521a\u624d\u7684\u6050\u60e7\uff0c\u5ffd\u7136\u53c8\u53d8\u6210\u4e86\u6ee1\u8154\u60b2\u6124\u3002 \u3000\u3000\u8fd9\u4e16\u4e0a\u6bd5\u7adf\u8fd8\u662f\u6709\u4eba\u770b\u91cd\u4ed6\u7684\uff0c\u4f46\u8fd9\u4e2a\u4eba\u5374\u5df2\u6b7b\u4e86\uff0c\u800c\u4e14\u5c31\u6b7b\u5728\u4ed6\u9762\u524d\u3002 \u3000\u3000\u4ed6\u5374\u53ea\u6709\u5728\u65c1\u8fb9\u773c\u7741\u7741\u5730\u770b\u7740\u3002 \u3000\u3000\u201c\u4ed6\u4eec\u597d\u72e0\u7684\u5fc3\uff0c\u7adf\u5f53\u7740\u4f60\u7684\u9762\u6740\u4e86\u6211\uff0c\u4ed6\u4eec\u6839\u672c\u6ca1\u6709\u628a\u4f60\u5f53\u505a\u4eba\u3002\u201d \u3000\u3000\u5979\u7684\u58f0\u97f3\u66f4\u5e7d\u6028\u3002 \u3000\u3000\u201c\u53ef\u662f\u6211\u77e5\u9053\u4f60\u4e00\u5b9a\u4e0d\u4f1a\u4f7f\u6211\u5c31\u8fd9\u4e48\u542b\u51a4\u800c\u6b7b\u7684\uff0c\u4f60\u4e00\u5b9a\u4f1a\u66ff\u6211\u62a5\u4ec7\uff0c\u8ba9\u4ed6\u4eec\u77e5\u9053\uff0c\u4f60 \u5e76\u4e0d\u662f\u4e2a\u80c6\u5c0f\u65e0\u7528\u7684\u61e6\u592b\uff01\u201d \u3000\u3000\u897f\u95e8\u5341\u4e09\u63e1\u7d27\u53cc\u62f3\uff0c\u6162\u6162\u5730\u70b9\u4e86\u70b9\u5934\uff0c\u6068\u6068\u9053\uff1a\u201c\u6211\u4f1a\u8ba9\u4ed6\u4eec\u77e5\u9053\u7684\uff0c\u6211\u4e00\u5b9a\u4f1a\u8ba9\u4ed6\u4eec \u77e5\u9053\uff01\u201d \u3000\u3000\u201c\u8fd9\u91cc\u6709\u67c4\u5200\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u53bb\u6740\u4e86\u4ed6\u4eec\uff1f\u201d \u3000\u3000\u534a\u7a7a\u4e2d\u5ffd\u7136\u6709\u6837\u4e1c\u897f\u843d\u4e0b\u6765\uff0c\u201c\u53ee\u201d\u7684\u4e00\u58f0\uff0c\u843d\u5728\u5730\u4e0a\uff0c\u679c\u7136\u662f\u67c4\u950b\u5229\u7684\u5200\u3002 \u3000\u3000\u201c\u4f60\u53ea\u8981\u80fd\u6740\u4e86\u97e9\u8d1e\u548c\u536b\u5929\u9e4f\uff0c\u4f60\u5c31\u662f\u6c5f\u6e56\u4e2d\u6700\u4e86\u4e0d\u8d77\u7684\u5927\u82f1\u96c4\u4e86\uff0c\u4ece\u6b64\u4ee5\u540e\uff0c\u7edd\u6ca1\u6709 \u4eba\u6562\u518d\u770b\u4e0d\u8d77\u4f60\uff0c\u6211\u6b7b\u5728\u4e5d\u6cc9\u4e4b\u4e0b\u4e5f\u7791\u76ee\u4e86\u3002\u201d \u3000\u3000\u58f0\u97f3\u53c8\u6e10\u6e10\u98d8\u5ffd\uff0c\u6e10\u6e10\u8d70\u8fdc\u3002 \u3000\u3000\u201c\u8fd9\u662f\u6211\u6700\u540e\u7684\u8981\u6c42\uff0c\u4f60\u4e00\u5b9a\u8981\u7b54\u5e94\u6211\uff0c\u4e00\u5b9a\u8981\u7b54\u5e94\u6211\u2026\u2026\u201d\u58f0\u97f3\u8d8a\u6765\u8d8a\u8fdc\uff0c\u7ec8\u4e8e\u6d88\u5931 \u5728\u51c4\u8ff7\u7684\u51b7\u96fe\u4e2d\u3002 \u3000\u3000\u7136\u540e\u5979\u7684\u4eba\u5c31\u5012\u4e0b\u53bb\u3002 \u3000\u3000\u9ed1\u6697\uff0c\u65e0\u8fb9\u65e0\u9645\u7684\u9ed1\u6697\u3002 \u3000\u3000\u897f\u95e8\u5341\u4e09\u7a81\u7136\u51b2\u51fa\u53bb\uff0c\u63e1\u8d77\u5979\u7684\u624b\u3002 \u3000\u3000\u5979\u7684\u624b\u65e9\u5df2\u50f5\u786c\uff0c\u663e\u7136\u5df2\u6b7b\u4e86\u5f88\u4e45\u5f88\u4e45\u4f46\u521a\u624d\u7684\u786e\u662f\u5979\u5728\u8bf4\u8bdd\uff0c\u5730\u4e0a\u7684\u786e\u6709\u67c4\u95ea\u52a8\u7740\u5bd2 \u5149\u7684\u77ed\u5200\u3002 \u3000\u3000\u897f\u95e8\u5341\u4e09\u7528\u4ed6\u638c\u5fc3\u5df2\u6c81\u51fa\u51b7\u6c57\u7684\u624b\uff0c\u62fe\u8d77\u4e86\u8fd9\u67c4\u5200\u3002 \u3000\u3000\u201c\u2026\u2026\u4f60\u53ea\u8981\u6740\u4e86\u536b\u5929\u9e4f\uff0c\u4f60\u5c31\u662f\u6c5f\u6e56\u4e2d\u6700\u4e86\u4e0d\u8d77\u7684\u5927\u82f1\u96c4\u2026\u2026\u201d \u3000\u3000\u4ed6\u7684\u8138\u5df2\u56e0\u5174\u594b\u800c\u626d\u66f2\uff0c\u4f46\u4e00\u53cc\u773c\u775b\u5374\u662f\u7a7a\u7a7a\u6d1e\u6d1e\u7684\uff0c\u5c31\u50cf\u6b7b\u4eba\u4e00\u6837\u3002 \u3000\u3000\u4ed6\u63e1\u7d27\u4e86\u8fd9\u67c4\u5200\uff0c\u85cf\u5728\u8863\u8896\u91cc\uff0c\u6162\u6162\u5730\u8d70\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u51c4\u8ff7\u7684\u51b7\u96fe\uff0c\u8ff7\u6f2b\u7740\u5927\u5730\u3002 \u3000\u3000\u98ce\u66f4\u51b7\u4e86\u3002 \u3000\u3000\u4f46\u4ed6\u5374\u5df2\u5b8c\u5168\u4e0d\u89c9\u5f97\u5bd2\u51b7\uff0c\u4ed6\u5fc3\u91cc\u53ea\u5269\u4e0b\u4e00\u4e2a\u5ff5\u5934\uff1a\u201c\u7528\u8fd9\u67c4\u5200\u53bb\u6740\u4e86\u536b\u5929\u9e4f\uff01\u201d \u3000\u3000\u65e0\u98ce\u65e0\u96ea\uff0c\u5374\u6709\u4e00\u9635\u9635\u6697\u9999\u6d6e\u52a8\uff0c\u9999\u6c81\u4eba\u5fc3\u3002 \u3000\u3000\u4e00\u7247\u7247\u7cbc\u7cbc\u7684\u9b3c\u706b\u5728\u98ce\u4e2d\u95ea\u70c1\uff0c\u536b\u5929\u9e4f\u548c\u97e9\u8d1e\u8d70\u5728\u79ef\u96ea\u7684\u5c0f\u5f84\u4e0a\u3002 \u3000\u3000\u4ed6\u4eec\u90fd\u77e5\u9053\u73b0\u5728\u5df2\u5230\u4e86\u5e94\u8be5\u95ed\u7740\u5634\u7684\u65f6\u5019\uff0c\u5e94\u8be5\u95ed\u5634\u7684\u65f6\u5019\uff0c\u4ed6\u4eec\u7edd\u4e0d\u5f00\u53e3\u3002 \u3000\u3000\u8def\u5f88\u6ed1\uff0c\u96ea\u5df2\u7ed3\u6210\u51b0\uff0c\u8fbd\u9614\u7684\u56ed\u6797\u4e2d\uff0c\u53ea\u6709\u5be5\u5be5\u51e0\u70b9\u706f\u706b\uff0c\u758f\u82e5\u6668\u661f\u3002 \u3000\u3000\u5c0f\u5f84\u4ece\u4e00\u7247\u6885\u6797\u4e2d\u7a7f\u51fa\u53bb\uff0c\u6885\u82b1\u4e0a\u79ef\u7740\u96ea\uff0c\u96ea\u4e5f\u662f\u9999\u7684\u3002 \u3000\u3000\u5ffd\u7136\u95f4\uff0c\u524d\u9762\u4e5f\u51fa\u73b0\u4e86\u4e00\u70b9\u706b\uff0c\u4e00\u884c\u5341\u4f59\u4e2a\u767d\u8863\u4eba\uff0c\u5e7d\u7075\u822c\u8ddf\u5728\u9b3c\u706b\u540e\uff0c\u5ffd\u7136\u95f4\u53c8\u5168\u90e8 \u6d88\u5931\u3002 \u3000\u3000\u536b\u5929\u9e4f\u8d70\u51fa\u6885\u6797\uff0c\u624d\u770b\u51fa\u524d\u9762\u6709\u4e00\u6392\u4f4e\u6a90\u7684\u5e73\u623f\uff0c\u5efa\u7b51\u7684\u5f62\u5f0f\u5f88\u5947\u7279\u3002 \u3000\u3000\u90a3\u4e9b\u5e7d\u7075\u822c\u7684\u767d\u8863\u4eba\uff0c\u60f3\u5fc5\u5df2\u8d70\u4e86\u8fdb\u53bb\u3002\u5c31\u5728\u8fd9\u65f6\uff0c\u5f15\u8def\u7684\u9b3c\u706b\u4e5f\u7a81\u7136\u6d88\u5931\uff0c\u98ce\u4e2d\u5374\u53c8 \u54cd\u8d77\u4e86\u90a3\u4f18\u96c5\u800c\u795e\u79d8\u7684\u58f0\u97f3\u3002 \u3000\u3000\u8fd9\u6b21\u5979\u53ea\u8bf4\u4e86\u4e24\u4e2a\u5b57\uff1a\u201c\u8bf7\u8fdb\u3002\u201d \u3000\u3000\u8d70\u8fdb\u53bb\u4e4b\u540e\uff0c\u624d\u53d1\u89c9\u8fd9\u5c4b\u5b50\u975e\u4f46\u4e0d\u4f4e\uff0c\u800c\u4e14\u663e\u5f97\u7279\u522b\u9ad8\u9614\u3002 \u3000\u3000\u5730\u4e0a\u94fa\u6ee1\u4e86\u5d2d\u65b0\u7684\u3001\u4e00\u5c18\u4e0d\u67d3\u7684\u8349\u5e2d\uff0c\u8fce\u9762\u4e00\u5e45\u5c4f\u98ce\u4e0a\uff0c\u753b\u7740\u79ef\u96ea\u7684\u9ad8\u5c71\uff0c\u9c9c\u7ea2\u7684\u82b1 \u6811\uff0c\u770b\u6765\u4e0d\u50cf\u662f\u4e2d\u539f\u7684\u98ce\u7269\u3002 \u3000\u3000\u518d\u770b\u753b\u4e0a\u7684\u9898\u5b57\uff0c\u624d\u77e5\u9053\u753b\u7684\u662f\u6d77\u5916\u6276\u6851\u5c9b\u4e0a\u7684\u666f\u8272\uff0c\u90a3\u9c9c\u7ea2\u7684\u82b1\u6811\uff0c\u6b63\u662f\u6276\u6851\u7684\u540d\u79cd \u6a31\u82b1\u3002 \u3000\u3000\u6a31\u82b1\u867d\u4e5f\u5982\u6885\u82b1\u540c\u6837\u9c9c\u8273\uff0c\u5374\u5c11\u4e86\u6885\u82b1\u7684\u51e0\u5206\u6c14\u8282\uff0c\u4e00\u8eab\u50b2\u9aa8\u3002 \u3000\u3000\u8fd9\u4e00\u6392\u5e73\u623f\uff0c\u663e\u7136\u4e5f\u662f\u4f9d\u7167\u6276\u6851\u5c9b\u4e0a\u7684\u5f62\u5f0f\u5efa\u7684\uff0c\u5c4b\u5b50\u91cc\u7adf\u6ca1\u6709\u684c\u6905\uff0c\u53ea\u6446\u7740\u51e0\u5f20\u77ee \u51e0\uff0c\u51e0\u4e0a\u7684\u9752\u9521\u70db\u53f0\uff0c\u70db\u6697\u706b\u4f4e\uff0c\u5c4b\u91cc\u8fd8\u71c3\u7740\u4e00\u7089\u9999\uff0c\u9999\u6c14\u5374\u5f88\u6d53\u90c1\u3002 \u3000\u3000\u6b63\u4e2d\u7684\u4e00\u5f20\u957f\u51e0\uff0c\u6446\u7740\u4e2a\u4e09\u5c3a\u9ad8\u7684\u89c2\u97f3\u4f5b\u50cf\u3002\u624b\u62c8\u6768\u67f3\uff0c\u9762\u9732\u5fae\u7b11\u3002 \u3000\u3000\u4e24\u4e2a\u767d\u8863\u5982\u96ea\u7684\u7edd\u8272\u4e3d\u4eba\uff0c\u5782\u80a9\u655b\u76ee\uff0c\u8083\u7acb\u5728\u65c1\uff0c\u5e74\u7eaa\u8f83\u957f\u7684\u98ce\u534e\u7edd\u4ee3\uff0c\u4eea\u6001\u4e07\u5343\uff0c\u5e74 \u7eaa\u8f83\u8f7b\u7684\u5374\u66f4\u7f8e\uff0c\u7f8e\u5f97\u8d85\u51e1\u8131\u4fd7\uff0c\u7f8e\u5f97\u4ee4\u4eba\u4e0d\u53ef\u601d\u8bae\u3002 \u3000\u3000\u5979\u4eec\u5f53\u7136\u5c31\u662f\u94c1\u59d1\u548c\u5fc3\u59d1\u3002 \u3000\u3000\u90a3\u4e9b\u767d\u8863\u4eba\u5df2\u76d8\u819d\u5750\u5728\u8349\u5e2d\u4e0a\uff0c\u4e00\u4e2a\u4e2a\u8138\u4e0a\u4ecd\u7136\u5168\u65e0\u8868\u60c5\uff0c\u76ee\u5149\u4ecd\u4f3c\u51dd\u89c6\u5728\u8fdc\u65b9\u3002 \u3000\u3000\u4ed6\u4eec\u7684\u4eba\u867d\u5728\u8fd9\u5c4b\u5b50\u91cc\uff0c\u5374\u5b8c\u5168\u4e0d\u50cf\u662f\u8fd9\u4e16\u754c\u4e0a\u7684\u4eba\u3002 \u3000\u3000\u9999\u70df\u7f2d\u7ed5\uff0c\u5c4b\u5b50\u91cc\u663e\u5f97\u8bf4\u4e0d\u51fa\u7684\u795e\u79d8\u5b89\u9759\u3002 \u3000\u3000\u73b0\u5728\u8fd8\u4e0d\u662f\u5e94\u8be5\u8bf4\u8bdd\u7684\u65f6\u5019\u3002 \u3000\u3000\u536b\u5929\u9e4f\u4e5f\u5728\u8349\u5e2d\u4e0a\u76d8\u819d\u5750\u4e0b\uff0c\u7136\u540e\u624d\u770b\u89c1\u5c4f\u98ce\u540e\u6709\u4e24\u4e2a\u5251\u7709\u661f\u76ee\u3001\u975e\u5e38\u82f1\u4fca\u7684\u9526\u8863\u5c11 \u5e74\uff0c\u50b2\u7136\u6276\u5251\u800c\u7acb\uff0c\u5251\u9798\u4e0a\u8fd8\u9576\u6ee1\u4e86\u9f99\u773c\u822c\u5927\u7684\u660e\u73e0\uff0c\u6bcf\u4e00\u7c92\u90fd\u662f\u4ef7\u503c\u8fde\u57ce\u3001\u4eba\u95f4\u5c11\u6709\u7684\u5b9d \u7269\u3002 \u3000\u3000\u4ed6\u4eec\u4e0d\u4f46\u9762\u8c8c\u6781\u76f8\u4f3c\uff0c\u7709\u5b57\u95f4\u4e5f\u540c\u6837\u5e26\u7740\u79cd\u903c\u4eba\u7684\u50b2\u6c14\uff0c\u7adf\u4f3c\u5b8c\u5168\u6ca1\u6709\u5c06\u5c4b\u5b50\u91cc\u8fd9\u4e9b\u4eba \u770b\u5728\u773c\u91cc\u3002 \u3000\u3000\u536b\u5929\u9e4f\u548c\u97e9\u8d1e\u5bf9\u671b\u4e86\u4e00\u773c\uff0c\u5fc3\u91cc\u5df2\u90fd\u77e5\u9053\uff0c\u8fd9\u4e24\u4e2a\u5c11\u5e74\u4eba\u4e00\u5b9a\u662f\u4ece\u73cd\u73e0\u57ce\u6765\u7684\u3002 \u3000\u3000\u53c8\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u8fd9\u5144\u5f1f\u4e24\u4eba\u4e2d\uff0c\u8eab\u6750\u8f83\u9ad8\u7684\u4e00\u4eba\u7a81\u7136\u95ee\u9053\uff1a\u201c\u5357\u6d77\u592b\u4eba\u7a76\u7adf\u5728\u54ea\u91cc\uff1f\u65e2 \u7136\u53eb\u6211\u4eec\u6765\u4e86\uff0c\u4e3a\u4ec0\u4e48\u8fd8\u4e0d\u51fa\u6765\u76f8\u89c1\uff1f\u201d \u3000\u3000\u4ed6\u7684\u8bdd\u521a\u8bf4\u5b8c\uff0c\u4f18\u96c5\u800c\u795e\u79d8\u7684\u58f0\u97f3\u53c8\u7a81\u7136\u54cd\u4e86\u8d77\u6765\u3002 \u3000\u3000\u201c\u6211\u5c31\u5728\u8fd9\u91cc\uff0c\u4e24\u4f4d\u96be\u9053\u770b\u4e0d\u89c1\uff1f\u201d \u3000\u3000\u58f0\u97f3\u7ade\u662f\u90a3\u89c2\u97f3\u4f5b\u50cf\u53d1\u51fa\u6765\u7684\uff0c\u94c1\u59d1\u548c\u5fc3\u59d1\uff0c\u8fde\u5634\u5507\u90fd\u6ca1\u6709\u52a8\u3002 \u3000\u3000\u5144\u5f1f\u4e24\u4eba\u8138\u8272\u53c8\u53d8\u4e86\u53d8\uff0c\u4e00\u4e2a\u51b7\u51b7\u9053\uff1a\u201c\u6211\u4eec\u5144\u5f1f\u4e0d\u8fdc\u5343\u91cc\u800c\u6765\uff0c\u5e76\u4e0d\u662f\u6765\u770b\u6ce5\u96d5\u5076\u50cf \u7684\u3002\u201d \u3000\u3000\u201c\u4f60\u4eec\u8981\u770b\u7684\u4eba\u5c31\u662f\u6211\u3002\u201d \u3000\u3000\u201c\u4f60\u5c31\u662f\u2018\u5343\u9762\u89c2\u97f3\uff0c\u5357\u6d77\u5a18\u5b50\u2019\uff1f\u201d \u3000\u3000\u201c\u6211\u5c31\u662f\u3002\u201d \u3000\u3000\u5144\u5f1f\u4e24\u4eba\u7a81\u7136\u540c\u65f6\u51b7\u7b11\uff0c\u540c\u65f6\u62d4\u5251\uff0c\u5251\u5982\u5339\u7ec3\uff0c\u5411\u8fd9\u89c2\u97f3\u4f5b\u50cf\u523a\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u4ed6\u4eec\u7684\u51fa\u624b\u3001\u62db\u5f0f\u3001\u8eab\u6cd5\uff0c\u7adf\u90fd\u5b8c\u5168\u4e00\u6837\uff0c\u4e00\u4e2a\u4eba\u5c31\u50cf\u662f\u53e6\u4e00\u4e2a\u4eba\u7684\u5f71\u5b50\u3002 \u3000\u3000\u4ed6\u4eec\u7684\u5251\u6cd5\u8fc5\u6025\u8f7b\u7075\uff0c\u4e00\u5251\u523a\u51fa\u540e\uff0c\u65b9\u5411\u7a81\u7136\u6539\u53d8\uff0c\u5251\u5149\u9519\u843d\uff0c\u5982\u82b1\u96e8\u7f24\u7eb7\uff0c\u7a81\u53c8 \u201c\u5578\u201d\u7684\u4e00\u54cd\uff0c\u4e24\u9053\u5251\u5149\u7adf\u4f3c\u5df2\u5408\u4e8c\u4e3a\u4e00\uff0c\u95ea\u7535\u822c\u523a\u5411\u89c2\u97f3\u4f5b\u50cf\u7684\u8138\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u4ed6\u4eec\u5ffd\u7136\u53d1\u73b0\u8fd9\u89c2\u97f3\u4f5b\u50cf\u8138\u4e0a\u7684\u8868\u60c5\u7adf\u5df2\u53d8\u4e86\uff0c\u53d8\u5f97\u4e25\u8083\u800c\u51b7\u6f20\uff0c\u4e5f\u5c31 \u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u90a3\u51e4\u534e\u7edd\u4ee3\u7684\u4e2d\u5e74\u7f8e\u5987\uff0c\u5df2\u7a81\u7136\u51fa\u624b\uff01 \u3000\u3000\u53ea\u542c\u201c\u556a\u201d\u7684\u4e00\u58f0\uff0c\u4e24\u67c4\u5251\u950b\u5df2\u5168\u90e8\u88ab\u5939\u5728\u638c\u5fc3\uff0c\u63a5\u7740\u53c8\u662f\u201c\u8e66\u201d\u7684\u4e00\u54cd\uff0c\u90a3\u5251\u950b\u7adf\u786c \u751f\u751f\u88ab\u5979\u6253\u65ad\u4e86\u4e00\u622a\u3002 \u3000\u3000\u73cd\u73e0\u5144\u5f1f\u663e\u7136\u662f\u56e0\u4e3a\u89c2\u97f3\u4f5b\u50cf\u8868\u60c5\u7684\u6539\u53d8\u800c\u9707\u60ca\u5931\u624b\uff0c\u6b64\u523b\u5c45\u7136\u4e34\u5371\u4e0d\u4e71\uff0c\u811a\u6b65\u4e00\u6ed1\uff0c \u5df2\u540c\u65f6\u540e\u9000\u4e86\u516b\u5c3a\uff0c\u56de\u5230\u5c4f\u98ce\u540e\uff0c\u4e24\u67c4\u65ad\u5251\u53c8\u5df2\u5165\u9798\u3002 \u3000\u3000\u4ed6\u4eec\u5e94\u53d8\u867d\u5feb\uff0c\u4f46\u8138\u4e0a\u5374\u8fd8\u662f\u5fcd\u4e0d\u4f4f\u9732\u51fa\u4e86\u60ca\u8bb6\u4e4b\u8272\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u4eec\u5df2\u770b\u89c1\u8fd9\u7f8e\u4e3d\u7684\u5973\u4eba\uff0c\u7adf\u5c06\u4ed6\u4eec\u7684\u65ad\u5251\u5403\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u4ed6\u4eec\u51e0\u4e4e\u4e0d\u76f8\u4fe1\u81ea\u5df1\u7684\u773c\u775b\uff0c\u8fd9\u4e24\u67c4\u5251\u7684\u950b\u5229\uff0c\u4ed6\u4eec\u81ea\u5df1\u5f53\u7136\u77e5\u9053\u5f97\u5f88\u6e05\u695a\u3002 \u3000\u3000\u8fd9\u5973\u5b50\u7684\u80a0\u80c3\u96be\u9053\u771f\u662f\u94c1\u94f8\u7684\uff1f \u3000\u3000\u5357\u6d77\u5a18\u5b50\u90a3\u79cd\u795e\u79d8\u7684\u58f0\u97f3\u5374\u4f3c\u5728\u8f7b\u8f7b\u53f9\u606f\uff0c\u9053\uff1a\u201c\u6b27\u9633\u57ce\u4e3b\u4e0d\u8be5\u53eb\u4f60\u4eec\u6765\u7684\u3002\u201d \u3000\u3000\u73cd\u73e0\u5144\u5f1f\u73b0\u5728\u5df2\u53ea\u6709\u542c\u7740\u3002 \u3000\u3000\u5357\u6d77\u5a18\u5b50\u9053\uff1a\u201c\u5c31\u51ed\u4f60\u4eec\u5144\u5f1f\u8fd9\u6837\u7684\u4eba\uff0c\u53c8\u600e\u4e48\u80fd\u5bf9\u4ed8\u53f6\u5f00\u3002\u201d \u3000\u3000\u73cd\u73e0\u5144\u5f1f\u7ec8\u4e8e\u5fcd\u4e0d\u4f4f\u6297\u58f0\u9053\uff1a\u201c\u53f6\u5f00\u4e5f\u53ea\u4e0d\u8fc7\u662f\u4e2a\u4eba\u3002\u201d \u3000\u3000\u4ed6\u4eec\u5144\u5f1f\u4e24\u4eba\uff0c\u867d\u7136\u53ea\u6709\u4e00\u4e2a\u4eba\u8bf4\u8bdd\uff0c\u53e6\u4e00\u4eba\u7684\u5634\u5507\u4eff\u4f5b\u4e5f\u5728\u52a8\u3002 \u3000\u3000\u5357\u6d77\u5a18\u5b50\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u53f6\u5f00\u4e5f\u662f\u4e2a\u4eba\uff0c\u4f46\u5374\u7edd\u4e0d\u662f\u666e\u901a\u4eba\u3002\u201d \u3000\u3000\u73cd\u73e0\u5144\u5f1f\u5634\u89d2\u5e26\u7740\u51b7\u7b11\uff0c\u6ee1\u8138\u4e0d\u670d\u6c14\u7684\u6837\u5b50\u3002 \u3000\u3000\u5357\u6d77\u5a18\u5b50\u6de1\u6de1\u4ed6\u8bf4\u9053\uff1a\u201c\u82e5\u8bba\u6b66\u529f\uff0c\u6211\u4eec\u8fd9\u4e9b\u4eba\u4e4b\u4e2d\uff0c\u4e5f\u8bb8\u6ca1\u6709\u4e00\u4e2a\u80fd\u6bd4\u5f97\u4e0a\u4ed6\u7684\u3002\u201d \u3000\u3000\u73cd\u73e0\u5144\u5f1f\u51b7\u7b11\u9053\uff1a\u201c\u4ed6\u82e5\u6765\u4e86\uff0c\u6211\u5144\u5f1f\u7b2c\u4e00\u4e2a\u5148\u8981\u53bb\u9886\u6559\u9886\u6559\u3002\u201d \u3000\u3000\u5357\u6d77\u5a18\u5b50\u4eff\u4f5b\u53c8\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4ed6\u73b0\u5728\u8bf4\u4e0d\u5b9a\u5c31\u5df2\u6765\u4e86\u3002\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u8bf4\u51fa\u6765\uff0c\u4e0d\u4f46\u536b\u5929\u9e4f\u500f\u7136\u52a8\u5bb9\uff0c\u5c31\u8fde\u58a8\u767d\u51b7\u6de1\u5982\u6b7b\u4eba\u7684\u8138\u4e0a\uff0c\u4e5f\u4e0d\u7981\u9732\u51fa\u79cd\u5947\u602a \u7684\u8868\u60c5\u3002 \u3000\u3000\u73cd\u73e0\u5144\u5f1f\u53d8\u8272\u9053\uff1a\u201c\u4ed6\u73b0\u5728\u771f\u7684\u5df2\u6765\u4e86\uff1f\u201d \u3000\u3000\u5357\u6d77\u5a18\u5b50\u9053\uff1a\u201c\u5c31\u5728\u4f60\u4eec\u5230\u8fbe\u91cc\u6765\u7684\u65f6\u5019\uff0c\u4ed6\u7684\u9a6c\u8f66\uff0c\u4e5f\u9a76\u5165\u4e86\u51b7\u9999\u56ed\u3002\u201d \u3000\u3000\u73cd\u73e0\u5144\u5f1f\u9053\uff1a\u201c\u4e0a\u5b98\u5c0f\u4ed9\u5462\uff1f\u201d \u3000\u3000\u5357\u6d77\u5a18\u5b50\u9053\uff1a\u201c\u4e0a\u5b98\u5c0f\u4ed9\u82e5\u4e0d\u6765\uff0c\u4ed6\u53c8\u600e\u4e48\u4f1a\u6765\uff1f\u201d \u3000\u3000\u539f\u6765\u53f6\u5f00\u662f\u4e3a\u4e86\u4e0a\u5b98\u5c0f\u4ed9\u800c\u6765\u7684\u3002 \u3000\u3000\u73cd\u73e0\u5144\u5f1f\u9053\uff1a\u201c\u5979\u5c31\u662f\u4e0a\u5b98\u91d1\u8679\u548c\u6797\u4ed9\u513f\u7684\u5973\u513f\uff1f\u201d \u3000\u3000\u5357\u6d77\u5a18\u5b50\u9053\uff1a\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u73cd\u73e0\u5144\u5f1f\u9053\uff1a\u201c\u4e0a\u5b98\u91d1\u8679\u548c\u5c0f\u674e\u63a2\u82b1\u6d3b\u7740\u65f6\u5df2\u52bf\u4e0d\u4e24\u7acb\uff0c\u4ed6\u7684\u5973\u513f\u53c8\u600e\u4f1a\u8ddf\u7740\u53f6\u5f00\uff1f\u201d \u3000\u3000\u5357\u6d77\u5a18\u5b50\u9053\uff1a\u201c\u56e0\u4e3a\u963f\u98de\u5c06\u5979\u4ea4\u7ed9\u53f6\u5f00\uff0c\u8981\u53f6\u5f00\u4fdd\u62a4\u5979\u5230\u8fd9\u91cc\u6765\u3002\u201d \u3000\u3000\u73cd\u73e0\u5144\u5f1f\u9053\uff1a\u201c\u8fd9\u4e8b\u548c\u98de\u5251\u5ba2\u53c8\u6709\u4ec0\u4e48\u5173\u7cfb\uff1f\u201d \u3000\u3000\u5357\u6d77\u5a18\u5b50\u9053\uff1a\u201c\u6797\u4ed9\u513f\u7ea2\u989c\u8584\u547d\uff0c\u665a\u5e74\u6f66\u5012\uff0c\u5979\u8fd9\u4e00\u751f\u4e2d\uff0c\u53ea\u6709\u4e00\u4e2a\u771f\u6b63\u4fe1\u4efb\u7684\u4eba\uff0c\u5c31 \u662f\u963f\u98de\uff0c\u6240\u4ee5\u4e34\u7ec8\u65f6\uff0c\u5c31\u53eb\u5979\u7684\u5973\u513f\u53bb\u627e\u963f\u98de\u3002\u201d \u3000\u3000\u73cd\u73e0\u5144\u5f1f\u9053\uff1a\u201c\u5979\u600e\u4e48\u80fd\u8bc1\u660e\u81ea\u5df1\u5c31\u662f\u6797\u4ed9\u513f\u7684\u5973\u513f\uff1f\u201d \u3000\u3000\u5357\u6d77\u5a18\u5b50\u9053\uff1a\u201c\u5979\u5f53\u7136\u6709\u5f88\u597d\u7684\u6cd5\u5b50\u8bc1\u660e\uff0c\u5426\u5219\u963f\u98de\u53c8\u600e\u4e48\u4f1a\u76f8\u4fe1\uff1f\u201d \u3000\u3000\u5979\u5ffd\u53c8\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u5144\u5f1f\u5bf9\u8fd9\u4ef6\u4e8b\u77e5\u9053\u7684\u597d\u50cf\u5e76\u4e0d\u591a\u3002\u201d \u3000\u3000\u73cd\u73e0\u5144\u5f1f\u9053\uff1a\u201c\u6211\u4eec\u53ea\u77e5\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u5357\u6d77\u5a18\u5b50\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u73cd\u73e0\u5144\u5f1f\u9053\uff1a\u201c\u6211\u77e5\u9053\u57ce\u4e3b\u8981\u6211\u4eec\u6765\u5c06\u4e0a\u5b98\u5c0f\u4ed9\u5e26\u56de\u53bb\u7684\u3002\u201d \u3000\u3000\u5357\u6d77\u5a18\u5b50\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u4eec\u5c31\u8981\u5c06\u5979\u5e26\u56de\u53bb\uff1f\u201d \u3000\u3000\u73cd\u73e0\u5144\u5f1f\u9053\uff1a\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u5357\u6d77\u5a18\u5b50\u9053\uff1a\u201c\u73b0\u5728\u65e2\u5df2\u6765\u4e86\uff0c\u4f60\u4eec\u4e3a\u4ec0\u4e48\u8fd8\u4e0d\u53bb\uff1f\u201d \u3000\u3000\u73cd\u73e0\u5144\u5f1f\u4e0d\u518d\u8bf4\u8bdd\uff0c\u7a81\u7136\u51cc\u7a7a\u7ffb\u8eab\uff0c\u63a0\u8fc7\u5c4f\u98ce\uff0c\u4e00\u970e\u773c\u5c31\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u536b\u5929\u9e4f\u8131\u53e3\u800c\u8d5e\uff1a\u201c\u597d\u8eab\u624b\uff01\u201d \u3000\u3000\u5357\u6d77\u5a18\u5b50\u7684\u58f0\u97f3\u5374\u5ffd\u7136\u53d8\u5f97\u5f88\u51b7\u6de1\uff0c\u51b7\u51b7\u4ed6\u8bf4\u9053\uff1a\u201c\u9001\u4e24\u53e3\u68fa\u6750\u5230\u98d8\u9999\u522b\u9662\u53bb\uff0c\u4e3a\u4ed6\u4eec \u5144\u5f1f\u51c6\u5907\u540e\u4e8b\u3002\u201d \u3000\u3000\u73cd\u73e0\u5144\u5f1f\u7684\u5251\u950b\u867d\u7136\u88ab\u6298\u65ad\uff0c\u53ef\u662f\u90a3\u51fa\u624b\u4e00\u5251\u7684\u53d8\u5316\uff0c\u5251\u98ce\u7834\u7a7a\u7684\u529b\u91cf\uff0c\u548c\u4ed6\u4eec\u8eab\u6cd5\u4e4b \u8f7b\u7075\uff0c\u914d\u5408\u4e4b\u4f73\u5999\uff0c\u65e0\u7591\u5df2\u662f\u5f53\u4eca\u6b66\u6797\u4e2d\u7b2c\u4e00\u6d41\u7684\u9ad8\u624b\u3002 \u3000\u3000\u5c24\u5176\u662f\u90a3\u4e00\u7740\u53cc\u5251\u5408\u58c1\uff0c\u98de\u8679\u8d2f\u65e5\uff0c\u5176\u5a01\u529b\u4e4b\u5f3a\uff0c\u5c31\u8fde\u536b\u5929\u9e4f\u4e5f\u672a\u5fc5\u6709\u628a\u63e1\u80fd\u62b5\u6321\u3002 \u3000\u3000\u4f46\u662f\u5728\u5357\u6d77\u5a18\u5b50\u770b\u6765\uff0c\u597d\u50cf\u4ed6\u4eec\u53ea\u8981\u53bb\u627e\u53f6\u5f00\u4ea4\u624b\uff0c\u5c31\u5df2\u7ecf\u662f\u4e24\u4e2a\u6b7b\u4eba\u4e86\u3002 \u3000\u3000\u5357\u6d77\u5a18\u5b50\u5f53\u7136\u7edd\u4e0d\u4f1a\u770b\u9519\u3002 \u3000\u3000\u5927\u5385\u4e2d\u5ffd\u7136\u53d8\u5f97\u9759\u5bc2\u5982\u575f\u5893\uff0c\u5927\u5bb6\u7adf\u4f3c\u90fd\u5728\u7b49\u5f85\u7740\u522b\u4eba\u5c06\u73cd\u73e0\u5144\u5f1f\u7684\u5c38\u4f53\u62ac\u56de\u6765\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u536b\u5929\u9e4f\u624d\u6c89\u541f\u7740\u9053\uff1a\u201c\u4e0a\u5b98\u91d1\u8679\u7eb5\u6a2a\u4e4b\u65f6\uff0c\u795e\u5200\u515a\u8fd8\u672a\u5d1b\u8d77\uff0c\u73b0\u5728\u795e \u5200\u515a\u7684\u540e\u4ee3\u90fd\u5df2\u957f\u5927\u6210\u4eba\uff0c\u4e0a\u5b98\u5c0f\u4ed9\u7684\u5e74\u7eaa\u60f3\u5fc5\u5df2\u6709\u4e0d\u5c0f\u3002\u201d \u3000\u3000\u5357\u6d77\u5a18\u5b50\u7684\u58f0\u97f3\u9053\uff1a\u201c\u5979\u7b97\u6765\u81f3\u5c11\u5df2\u5e94\u8be5\u6709\u4e8c\u5341\u591a\u4e86\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u4e8c\u5341\u591a\u5c81\u7684\u5973\u4eba\uff0c\u96be\u9053\u4e00\u76f4\u6ca1\u6709\u6210\u4eb2\uff1f\u201d \u3000\u3000\u5357\u6d77\u5a18\u5b50\u9053\uff1a\u201c\u5979\u82e5\u5df2\u6709\u4e86\u592b\u5a7f\uff0c\u53c8\u600e\u4f1a\u8981\u53f6\u5f00\u6765\u4fdd\u62a4\u5979\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u6797\u4ed9\u513f\u53f7\u79f0\u5929\u4e0b\u7b2c\u4e00\u7f8e\u4eba\uff0c\u5979\u5973\u513f\u4e5f\u5e94\u8be5\u957f\u5f97\u4e0d\u4e11\u3002\u201d \u3000\u3000\u5357\u6d77\u5a18\u5b50\u9053\uff1a\u201c\u975e\u4f46\u4e0d\u4e11\uff0c\u800c\u4e14\u4e5f\u53ef\u4ee5\u7b97\u662f\u4eba\u95f4\u5c11\u6709\u7684\u7f8e\u4eba\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u65e2\u7136\u662f\u4e2a\u7f8e\u4eba\uff0c\u4e3a\u4ec0\u4e48\u8fd8\u627e\u4e0d\u5230\u5a46\u5bb6\uff1f\u201d \u3000\u3000\u5357\u6d77\u5a18\u5b50\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u53ea\u56e0\u5979\u867d\u7136\u7f8e\u5982\u5929\u4ed9\uff0c\u4f46\u5979\u7684\u667a\u529b\uff0c\u5374\u8fde\u4e03\u516b\u5c81\u7684\u5b69\u5b50\u90fd\u6bd4 \u4e0d\u4e0a\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u76b1\u7709\u9053\uff1a\u201c\u8fd9\u4e48\u6837\u7684\u4e00\u4e2a\u7f8e\u4eba\uff0c\u96be\u9053\u7adf\u662f\u4e2a\u767d\u75f4\uff1f\u201d \u3000\u3000\u5357\u6d77\u5a18\u5b50\u9053\uff1a\u201c\u5979\u5e76\u4e0d\u662f\u4e2a\u5929\u751f\u7684\u4f4e\u80fd\u513f\uff0c\u636e\u8bf4\u53ea\u4e0d\u8fc7\u662f\u56e0\u4e3a\u5979\u5728\u516d\u5c81\u7684\u65f6\u5019\uff0c\u53d7\u4e86\u4e00 \u6b21\u91cd\u4f24\uff0c\u8111\u529b\u53d7\u635f\uff0c\u6240\u4ee5\u667a\u6167\u4e00\u76f4\u505c\u987f\u5728\u4e03\u5c81\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u54e6\u3002\u201d \u3000\u3000\u5357\u6d77\u5a18\u5b50\u9053\uff1a\u201c\u53ef\u662f\u5979\u7684\u7f8e\u4e3d\uff0c\u5374\u8db3\u4ee5\u4ee4\u4efb\u4f55\u7537\u4eba\u52a8\u5fc3\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5929\u5992\u7ea2\u989c\uff0c\u9020\u5316\u5f04\u4eba\uff0c\u770b\u672a\u5979\u7684\u547d\u8fd0\uff0c\u7adf\u4f3c\u6bd4\u5979\u7684\u6bcd\u4eb2\u8fd8\u8981\u60b2 \u60e8\u3002\u201d \u3000\u3000\u5357\u6d77\u5a18\u5b50\u9053\uff1a\u201c\u8fd9\u4e48\u6837\u4e00\u4e2a\u5973\u4eba\uff0c\u82e5\u662f\u6ca1\u6709\u4eba\u4fdd\u62a4\u5979\uff0c\u4e5f\u4e0d\u77e5\u8981\u88ab\u591a\u5c11\u7537\u4eba\u6b3a\u9a97\u73a9 \u5f04\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u6797\u4ed9\u513f\u4e34\u6b7b\u65f6\uff0c\u5979\u8fd8\u662f\u653e\u5fc3\u4e0d\u4e0b\uff0c\u624d\u8981\u627e\u98de\u5251\u5bb9\u6765\u4fdd\u62a4\u5979\u3002\u201d \u3000\u3000\u5357\u6d77\u5a18\u5b50\u9053\uff1a\u201c\u4f46\u963f\u98de\u4e00\u751f\u6d41\u6d6a\uff0c\u5230\u73b0\u5728\u8fd8\u6ca1\u6709\u5bb6\uff0c\u6240\u4ee5\u4ed6\u5728\u6c5f\u5357\u9047\u89c1\u53f6\u5f00\u65f6\uff0c\u5c31\u5c06\u8fd9 \u526f\u62c5\u5b50\u4ea4\u7ed9\u4e86\u53f6\u5f00\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u4ed6\u96be\u9053\u4e5f\u80fd\u50cf\u6797\u4ed9\u513f\u4fe1\u4efb\u4ed6\u4e00\u6837\u4fe1\u4efb\u53f6\u5f00\uff1f\u201d \u3000\u3000\u5357\u6d77\u5a18\u5b50\u9053\uff1a\u201c\u65e0\u8bba\u8c01\u90fd\u53ef\u4ee5\u4fe1\u4efb\u53f6\u5f00\u7684\uff0c\u8fd9\u4e2a\u4eba\u6d12\u8131\u4e0d\u7f81\uff0c\u4e0d\u62d8\u5c0f\u8282\uff0c\u4f46\u662f\u670b\u53cb\u6258\u4ed6 \u7684\u4e8b\uff0c\u4ed6\u5c31\u662f\u8d74\u6c64\u8e48\u706b\uff0c\u4e5f\u5728\u6240\u4e0d\u8f9e\u3002\u201d \u3000\u3000\u58a8\u767d\u4e00\u76f4\u5728\u9759\u9759\u5730\u542c\u7740\uff0c\u6b64\u523b\u7a81\u7136\u9053\uff1a\u201c\u597d\uff0c\u597d\u7537\u513f\uff01\u597d\u6c49\u5b50\uff01\u201d \u3000\u3000\u5357\u6d77\u5a18\u5b50\u9053\uff1a\u201c\u5c31\u4e3a\u4e86\u4ed6\u7b54\u5e94\u7167\u987e\u4e0a\u5b98\u5c0f\u4ed9\uff0c\u4ed6\u7684\u60c5\u4eba\u4e01\u7075\u7433\u624d\u4f1a\u8ddf\u4ed6\u5435\u7ffb\uff0c\u4e00\u6012\u800c \u53bb\uff0c\u5230\u73b0\u5728\u8fd8\u6ca1\u6709\u6d88\u606f\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u7b11\u4e86\u7b11\u9053\uff1a\u201c\u6211\u4e5f\u542c\u8bf4\u8fc7\u4e01\u5bb6\u8fd9\u4f4d\u5e7a\u59d1\u5a18\uff0c\u662f\u4e2a\u918b\u575b\u5b50\u3002\u201d\u5357\u6d77\u5a18\u5b50\u53f9\u9053\uff1a\u201c\u4e16 \u4e0a\u7684\u5973\u4eba\uff0c\u53c8\u6709\u54ea\u4e2a\u662f\u4e0d\u5403\u918b\u7684\u3002\u201d \u3000\u3000\u76f4\u5230\u73b0\u5728\uff0c\u5979\u8bf4\u8bdd\u624d\u50cf\u662f\u4e2a\u5973\u4eba\uff0c\u624d\u6709\u4e86\u4e9b\u4eba\u7c7b\u7684\u611f\u60c5\u3002 \u3000\u3000\u536b\u5929\u9e4f\u6c89\u541f\u7740\uff0c\u53c8\u9053\uff1a\u201c\u6614\u5e74\u91d1\u94b1\u5e2e\u5a01\u9738\u5929\u4e0b\uff0c\u5357\u4e03\u5317\u516d\u5341\u4e8c\u7701\uff0c\u5168\u90e8\u5728\u4ed6\u4eec\u63a7\u5236\u4e4b \u4e0b\uff0c\u5e2e\u4e2d\u7684\u8d22\u5bcc\uff0c\u5bcc\u53ef\u654c\u56fd\uff0c\u4f46\u4e0a\u5b98\u91d1\u8679\u672c\u8eab\u5374\u662f\u4e2a\u5f88\u8282\u4fed\u7684\u4eba\u3002\u201d \u3000\u3000\u5357\u6d77\u5a18\u5b50\u9053\uff1a\u201c\u4ed6\u5e76\u4e0d\u662f\u8282\u4fed\uff0c\u53ea\u4e0d\u8fc7\u4e16\u4e0a\u6240\u6709\u7684\u5962\u534e\u4eab\u53d7\uff0c\u90fd\u4e0d\u80fd\u8ba9\u4ed6\u52a8\u5fc3\u800c\u5df2\u3002\u201d \u3000\u3000\u9664\u4e86\u6743\u529b\u5916\uff0c\u4e16\u4e0a\u7edd\u6ca1\u6709\u4efb\u4f55\u4e8b\u80fd\u8ba9\u4e0a\u5b98\u91d1\u8679\u771f\u7684\u52a8\u5fc3\u3002 \u3000\u3000\u5c31\u8fde\u6797\u4ed9\u513f\u90a3\u6837\u7684\u7edd\u4ee3\u7f8e\u4eba\uff0c\u5728\u4ed6\u770b\u6765\uff0c\u4e5f\u53ea\u4e0d\u8fc7\u662f\u4e2a\u5de5\u5177\u3002 \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u636e\u8bf4\u4e0a\u5b98\u91d1\u8679\u751f\u524d\uff0c\u5df2\u5c06\u91d1\u94b1\u5e2e\u7684\u8d22\u5bcc\u548c\u4ed6\u7684\u6b66\u529f\u5fc3\u6cd5\uff0c\u5168\u90e8\u6536\u85cf\u5230\u4e00\u4e2a \u5f88\u79d8\u5bc6\u7684\u5730\u65b9\u3002\u201d \u3000\u3000\u5357\u6d77\u5a18\u5b50\u9053\uff1a\u201c\u6c5f\u6e56\u4e2d\u7684\u786e\u4e45\u5df2\u6709\u4e86\u8fd9\u79cd\u4f20\u8bf4\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u4f46\u4e0a\u5b98\u91d1\u8679\u53bb\u4e16\u81f3\u4eca\uff0c\u5df2\u6709\u4e8c\u5341\u591a\u5e74\uff0c\u5374\u4ece\u672a\u6709\u4eba\u80fd\u627e\u5230\u8fd9\u7b14\u5b9d\u85cf\u3002\u2019\u5357 \u6d77\u5a18\u5b50\u9053\uff1a\u201c\u7684\u786e\u4ece\u6ca1\u627e\u5230\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u773c\u775b\u91cc\u95ea\u7740\u5149\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u4f46\u8fd9\u5b9d\u85cf\u7684\u6240\u5728\u5730\uff0c\u5e76\u4e0d\u662f\u6ca1\u6709\u4eba\u77e5\u9053\u3002\u201d \u3000\u3000\u5357\u6d77\u5a18\u5b50\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u77e5\u9053\u8fd9\u79d8\u5bc6\u7684\uff0c\u53ea\u6709\u8346\u65e0\u547d\uff0c\u4f46\u4ed6\u4e5f\u662f\u4e2a\u5bf9\u4efb\u4f55\u4e8b\u90fd\u7edd\u4e0d\u52a8\u5fc3\u7684\u4eba\uff0c\u6240\u4ee5 \u591a\u5e74\u6765\uff0c\u4ece\u672a\u5bf9\u8fd9\u7b14\u5b9d\u85cf\u6709\u8fc7\u91ce\u5fc3\u3002\u201d \u3000\u3000\u5357\u6d77\u5a18\u5b50\u9053\uff1a\u201c\u4ed6\u672c\u5c31\u662f\u4e0a\u5b98\u91d1\u8679\u7684\u5f71\u5b50\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u4ed6\u5251\u6cd5\u72e0\u6bd2\uff0c\u51fa\u624b\u65e0\u60c5\uff0c\u522b\u4eba\u4e5f\u4e0d\u6562\u6253\u4ed6\u7684\u4e3b\u610f\uff0c\u4f55\u51b5\u4ed6\u7684\u884c\u8e2a\u4e5f\u4e00\u5411\u98d8 \u5ffd\u4e0d\u5b9a\uff0c\u5c31\u7b97\u6709\u4eba\u60f3\u627e\u4ed6\uff0c\u4e5f\u627e\u4e0d\u5230\u3002\u201d \u3000\u3000\u5357\u6d77\u5a18\u5b50\u9053\uff1a\u201c\u5c31\u7b97\u627e\u5230\u4e86\uff0c\u4e5f\u5fc5\u5b9a\u5df2\u6b7b\u5728\u4ed6\u5251\u4e0b\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u4ed6\u5df2\u5c06\u8fd9\u79d8\u5bc6\u544a\u8bc9\u4e86\u4e0a\u5b98\u91d1\u8679\u552f\u4e00\u7684\u9aa8\u8089\uff01\u201d \u3000\u3000\u5357\u6d77\u5a18\u5b50\u9053\uff1a\u201c\u4e0a\u5b98\u5c0f\u4ed9\uff1f\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6b63\u662f\u4e0a\u5b98\u5c0f\u4ed9\uff0c\u6240\u4ee5\u5979\u73b0\u5728\u4e0d\u4f46\u662f\u4e16\u4e0a\u6700\u7f8e\u4e3d\u7684\u5973\u4eba\uff0c\u4e5f\u662f\u4e16\u4e0a\u6700 \u5bcc\u6709\u7684\u5973\u4eba\uff0c\u518d\u52a0\u4e0a\u4e0a\u5b98\u91d1\u8679\u7559\u4e0b\u7684\u6b66\u529f\u5fc3\u6cd5\uff0c\u65e0\u8bba\u8c01\u53ea\u8981\u80fd\u627e\u5230\u5979\uff0c\u4e0d\u4f46\u7acb\u523b\u53ef\u4ee5\u5bcc\u7532\u5929 \u4e0b\uff0c\u800c\u4e14\u5fc5\u5c06\u7eb5\u6a2a\u6b66\u6797\uff0c\u8fd9\u8bf1\u60d1\u5b9e\u5728\u4e0d\u5c0f\u3002\u201d \u3000\u3000\u5357\u6d77\u5a18\u5b50\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u5979\u81ea\u5df1\u5e76\u4e0d\u77e5\u9053\uff0c\u5979\u53ea\u4e0d\u8fc7\u8fd8\u662f\u4e2a\u4e03\u516b\u5c81\u7684\u5b69\u5b50\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u6240\u4ee5\u65e0\u8bba\u8c01\u8981\u4fdd\u62a4\u8fd9\u4e48\u6837\u4e00\u4e2a\u4eba\uff0c\u90fd\u51e0\u4e4e\u662f\u4e0d\u53ef\u80fd\u7684\u4e8b\u3002\u201d \u3000\u3000\u5357\u6d77\u5a18\u5b50\u9053\uff1a\u201c\u53ef\u80fd\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u4e0d\u53ef\u80fd\uff01\u201d \u3000\u3000\u5357\u6d77\u5a18\u5b50\u9053\uff1a\u201c\u522b\u4eba\u4e0d\u80fd\u53f6\u5f00\u80fd\uff01\u201d \u3000\u3000\u536b\u5929\u9e4f\u51b7\u7b11\u9053\uff1a\u201c\u4ed6\u5c31\u7b97\u662f\u6b66\u6797\u4e2d\u7684\u7edd\u4ee3\u5947\u624d\uff0c\u6b66\u529f\u5c31\u7b97\u5df2\u80fd\u65e0\u654c\u4e8e\u5929\u4e0b\uff0c\u4f46\u53ea\u51ed\u4ed6\u4e00 \u4e2a\u4eba\uff0c\u96be\u9053\u5c31\u80fd\u62b5\u6297\u5f97\u4e86\u5929\u4e0b\u6b66\u6797\u4e2d\u7684\u6570\u5341\u9ad8\u624b\uff1f\u201d \u3000\u3000\u5357\u6d77\u5a18\u5b50\u8bf4\u9053\uff1a\u201c\u4ed6\u5e76\u4e0d\u662f\u53ea\u6709\u4e00\u4e2a\u4eba\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u4e0d\u662f\uff1f\u201d \u3000\u3000\u5357\u6d77\u5a18\u5b50\u9053\uff1a\u201c\u4e00\u5fc3\u60f3\u6740\u4e86\u4ed6\uff0c\u593a\u8d70\u4e0a\u5b98\u5c0f\u4ed9\u7684\u4eba\uff0c\u56fa\u7136\u4e0d\u5c11\uff0c\u4f46\u4e3a\u4e86\u6614\u65e5\u7684\u6069\u4e49\uff0c\u51b3 \u5b9a\u8981\u5168\u529b\u4fdd\u62a4\u4ed6\u7684\u4eba\uff0c\u4e5f\u6709\u597d\u51e0\u4e2a\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u6614\u65e5\u7684\u6069\u4e49\uff1f\u201d \u3000\u3000\u5357\u6d77\u5a18\u5b50\u9053\uff1a\u201c\u83ab\u5fd8\u8bb0\u4ed6\u662f\u5c0f\u674e\u63a2\u82b1\u552f\u4e00\u7684\u4f20\u4eba\uff0c\u6614\u5e74\u53d7\u8fc7\u5c0f\u674e\u63a2\u82b1\u6069\u60e0\u7684\u4eba\u4e5f\u5e76\u4e0d \u5c11\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u51b7\u51b7\u9053\uff1a\u201c\u4e8b\u9694\u591a\u5e74\uff0c\u90a3\u4e9b\u4eba\u7eb5\u7136\u8fd8\u6ca1\u6709\u6b7b\uff0c\u53ea\u6015\u5df2\u5c06\u4ed6\u7684\u6069\u60c5\u5fd8\u4e86\uff0c\u6069\u60c5\u603b\u662f \u6bd4\u4ec7\u6068\u5fd8\u5f97\u5feb\u7684\u3002\u201d \u3000\u3000\u5357\u6d77\u5a18\u5b50\u9053\uff1a\u201c\u81f3\u5c11\u8fd8\u6709\u4e00\u4e2a\u4eba\u672a\u66fe\u5fd8\u8bb0\uff01\u201d \u3000\u3000\u5de5\u5929\u9e4f\u9053\uff1a\u201c\u8c01\uff1f\u201d \u3000\u3000\u5357\u6d77\u5a18\u5b50\u9053\uff1a\u201c\u6211\uff01\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u8bf4\u51fa\u6765\uff0c\u5927\u5bb6\u53c8\u4e0d\u7981\u5168\u90e8\u8038\u7136\u52a8\u5bb9\u3002 \u3000\u3000\u5357\u6d77\u5a18\u5b50\u9053\uff1a\u201c\u4f60\u4eec\u82e5\u4ee5\u4e3a\u6211\u4e5f\u60f3\u6765\u56fe\u8c0b\u4e0a\u5b98\u5c0f\u4ed9\u7684\u8bdd\uff0c\u4f60\u4eec\u5c31\u9519\u4e86\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u76ee\u5149\u95ea\u52a8\uff0c\u9053\uff1a\u201c\u4f60\u627e\u6211\u4eec\u5230\u8fd9\u91cc\u6765\uff0c\u662f\u4e3a\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5357\u6d77\u5a18\u5b50\u9053\uff1a\u201c\u6211\u53ea\u4e0d\u8fc7\u60f3\u8981\u4f60\u4eec\u770b\u5728\u6211\u7684\u9762\u4e0a\uff0c\u6253\u6d88\u8fd9\u4e2a\u4e3b\u610f\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u4f60\u8981\u6211\u4eec\u653e\u8fc7\u53f6\u5f00\u3002\u201d \u3000\u3000\u5357\u6d77\u5a18\u5b50\u9053\uff1a\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u6211\u82e5\u4e0d\u7b54\u5e94\u5462\uff1f\u201d \u3000\u3000\u5357\u6d77\u5a18\u5b50\u51b7\u51b7\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u4eec\u5c31\u4e0d\u4f46\u662f\u53f6\u5f00\u7684\u5bf9\u5934\uff0c\u4e5f\u662f\u6211\u7684\u5bf9\u5934\uff0c\u4eca\u65e5\u4f60\u4eec\u82e5\u60f3\u6d3b\u7740 \u8d70\u51fa\u8fd9\u5c4b\u5b50\uff0c\u53ea\u6015\u5f88\u4e0d\u5bb9\u6613\uff01\u201d \u3000\u3000\u536b\u5929\u9e4f\u7a81\u7136\u5927\u7b11\uff0c\u9053\uff1a\u201c\u6211\u660e\u767d\u4e86\uff0c\u6211\u603b\u7b97\u660e\u767d\u4e86\u3002\u201d \u3000\u3000\u5357\u6d77\u5a18\u5b50\u9053\uff1a\u201c\u4f60\u660e\u767d\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u536b\u5929\u9e4f\u7684\u7b11\u58f0\u7a81\u7136\u505c\u987f\uff0c\u9053\uff1a\u201c\u4f60\u8981\u6211\u4eec\u6253\u6d88\u8fd9\u4e3b\u610f\u53ea\u4e0d\u8fc7\u60f3\u4e00\u4e2a\u4eba\u72ec\u541e\u800c\u5df2\uff0c\u4f60\u6545\u610f \u5c06\u53f6\u5f00\u8bf4\u5f97\u6d3b\u7075\u6d3b\u73b0\uff0c\u5176\u5b9e\u4f60\u60f3\u5fc5\u5df2\u6709\u4e86\u5bf9\u4ed8\u4ed6\u7684\u6cd5\u5b50\u3002\u201d \u3000\u3000\u5357\u6d77\u5a18\u5b50\u7684\u58f0\u97f3\u4e5f\u53d8\u4e86\uff0c\u7a81\u7136\u9053\uff1a\u201c\u536b\u516b\uff0c\u4f60\u770b\u7740\u6211\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u5374\u5df2\u8f6c\u8fc7\u5934\uff0c\u53bb\u770b\u95e8\u53e3\u7684\u5c4f\u51e4\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u8981\u662f\u60f3\u7528\u9b54\u6559\u4e2d\u7684\u52fe\u9b42\u6444\u5fc3\u5927\u6cd5\u6765 \u5bf9\u4ed8\u6211\uff0c\u4f60\u5c31\u627e\u9519\u4eba\u4e86\u3002\u201d \u3000\u3000\u5357\u6d77\u5a18\u5b50\u9053\uff1a\u201c\u6211\u53ea\u4e0d\u8fc7\u60f3\u63d0\u9192\u4f60\uff0c\u4e09\u5341\u5e74\u524d\u6211\u5df2\u653e\u8fc7\u4f60\u4e00\u6b21\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u4e0d\uff0c\u4e09\u5341\u5e74\u524d\uff0c\u6211\u51e0\u4e4e\u5df2\u6b7b\u5728\u4f60\u624b\u91cc\u3002\u201d \u3000\u3000\u5357\u6d77\u5a18\u5b50\u9053\uff1a\u201c\u90a3\u65f6\u4f60\u53d1\u4e0b\u91cd\u8a93\uff0c\u53ea\u8981\u6211\u518d\u770b\u5230\u4f60\uff0c\u6211\u65e0\u8bba\u8981\u4f60\u505a\u4ec0\u4e48\u4f60\u90fd\u7edd\u4e0d\u8fdd\u80cc\uff0c \u5426\u5219\u5c31\u5b81\u613f\u88ab\u5229\u5203\u7a7f\u80f8\u800c\u6b7b\u3002\u201d \u3000\u3000\u5979\u7684\u58f0\u97f3\u7a81\u53c8\u53d8\u5f97\u9634\u68ee\u800c\u53ef\u6016\uff0c\u51b7\u51b7\u5730\u63a5\u7740\u9053\uff1a\u201c\u8fd9\u4e9b\u8bdd\u4f60\u8fd8\u8bb0\u4e0d\u8bb0\u5f97\uff1f\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u6211\u5f53\u7136\u8bb0\u5f97\uff0c\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u5357\u6d77\u5a18\u5b50\u9053\uff1a\u201c\u4e0d\u8fc7\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u8fd9\u4e9b\u8bdd\u6211\u662f\u5bf9\u5357\u6d77\u5a18\u5b50\u8bf4\u7684\u3002\u201d \u3000\u3000\u5357\u6d77\u5a18\u5b50\u9053\uff1a\u201c\u6211\u5c31\u662f\u5357\u6d77\u5a18\u4e8e\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u4f60\u4e0d\u662f\uff01\u201d \u3000\u3000\u4ed6\u5634\u89d2\u5e26\u7740\u79cd\u5947\u7279\u7684\u51b7\u7b11\uff0c\u4e00\u5b57\u5b57\u63a5\u7740\u9053\uff1a\u201c\u5357\u6d77\u5a18\u5b50\u65e9\u5df2\u6b7b\u4e86\uff0c\u4f60\u4ee5\u4e3a\u6211\u8fd8\u4e0d\u77e5 \u9053\uff1f\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u8bf4\u51fa\u6765\uff0c\u8fde\u58a8\u767d\u4e5f\u4e0d\u7981\u52a8\u5bb9\uff01 \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u5728\u540e\u9762\u90a3\u8349\u5bee\u4e2d\uff0c\u4f60\u95ee\u6211\u600e\u4f1a\u542c\u4e0d\u51fa\u4f60\u7684\u58f0\u97f3\uff0c\u90a3\u65f6\u6211\u5c31\u5df2\u77e5\u9053\uff0c\u4f60\u7edd\u4e0d \u662f\u5357\u6d77\u5a18\u5b50\uff0c\u5c31\u77e5\u9053\u5979\u65e9\u5df2\u6b7b\u4e86\uff0c\u5426\u5219\u6211\u53c8\u600e\u4e48\u6562\u6765\u3002\u201d \u3000\u3000\u90a3\u795e\u79d8\u7684\u58f0\u97f3\u6c89\u5bc2\u4e86\u5f88\u4e45\uff0c\u624d\u5f90\u5f90\u9053\uff1a\u201c\u4f60\u600e\u4e48\u4f1a\u77e5\u9053\uff1f\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u56e0\u4e3a\u4f60\u4e0d\u8be5\u95ee\u8fd9\u53e5\u8bdd\u7684\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u6211\u6839\u672c\u5c31\u542c\u4e0d\u51fa\u5979\u8bf4\u8bdd\u7684\u58f0\u97f3\uff0c\u6211\u867d\u7136\u662f\u552f\u4e00\u89c1\u8fc7\u5979\u771f\u9762\u76ee\u7684\u4eba\uff0c\u5374\u4ece\u6765\u4e5f\u6ca1\u6709 \u542c\u89c1\u5979\u8bf4\u8fc7\u4e00\u4e2a\u5b57\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u7b11\u5f97\u5f88\u5947\u602a\uff0c\u63a5\u7740\u53c8\u9053\uff1a\u201c\u4f60\u867d\u7136\u77e5\u9053\u6211\u662f\u552f\u4e00\u89c1\u8fc7\u5979\u771f\u9762\u76ee\u8fd8\u80fd\u6d3b\u7740\u7684\u4eba\uff0c\u5374 \u4e00\u5b9a\u4e5f\u4e0d\u77e5\u9053\u6211\u4eec\u4e4b\u95f4\u7684\u4e8b\uff0c\u56e0\u4e3a\u5979\u7edd\u4e0d\u4f1a\u5c06\u8fd9\u4ef6\u4e8b\u544a\u8bc9\u4f60\u3002\u201d \u3000\u3000\u90a3\u58f0\u97f3\u53c8\u6c89\u5bc2\u4e86\u5f88\u4e45\uff0c\u624d\u5fcd\u4e0d\u4f4f\u95ee\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u90a3\u662f\u4e2a\u79d8\u5bc6\uff0c\u5929\u4e0b\u7edd\u6ca1\u6709\u522b\u4eba\u4f1a\u77e5\u9053\u7684\u79d8\u5bc6\u3002\u201d \u3000\u3000\u8fd9\u8001\u4eba\u7684\u8138\u4e0a\uff0c\u5ffd\u7136\u53d1\u51fa\u4e00\u79cd\u9752\u6625\u7684\u5149\u8f89\uff0c\u5c31\u50cf\u662f\u5df2\u56de\u5230\u591a\u5e74\u524d\uff0c\u4ed6\u8fd8\u5145\u6ee1\u4e86\u68a6\u60f3\u7684\u5c11 \u5e74\u65f6\uff0c\u7136\u540e\u4ed6\u5c31\u8bf4\u51fa\u4e86\u4e00\u6bb5\u5947\u5f02\u800c\u7f8e\u4e3d\u7684\u6545\u4e8b\uff0c\u7f8e\u4e3d\u5f97\u5c31\u50cf\u8bf4\u795e\u8bdd\uff1a\u201c\u4e09\u5341\u5e74\u524d\uff0c\u6211\u8fd8\u662f\u4e2a \u559c\u6b22\u60f9\u662f\u751f\u975e\u7684\u5e74\u8f7b\u4eba\uff0c\u6709\u4e00\u6b21\u5728\u82d7\u7586\u95ef\u4e86\u7978\uff0c\u9003\u7a9c\u5165\u6df1\u5c71\uff0c\u5374\u5728\u6df1\u5c71\u91cc\u8ff7\u4e86\u8def\u3002\u201d \u3000\u3000\u201c\u82d7\u5c71\u4e2d\u4e0d\u4f46\u5230\u5904\u90fd\u53ef\u80fd\u9047\u5230\u6bd2\u86c7\u731b\u517d\uff0c\u800c\u4e14\u7634\u6c14\u6781\u91cd\uff0c\u6211\u4e3a\u4e86\u8eb2\u907f\u6bcf\u5929\u9ec4\u660f\u65f6\u90fd\u4f1a\u51fa \u73b0\u4e00\u6b21\u7684\u6843\u82b1\u7634\uff0c\u8eb2\u5165\u4e86\u4e00\u4e2a\u5f88\u6df1\u7684\u5c71\u6d1e\u91cc\u3002\u201d \u3000\u3000\u201c\u90a3\u5c71\u6d1e\u539f\u662f\u72d0\u7a74\uff0c\u6211\u60f3\u6740\u6761\u72d0\u72f8\uff0c\u70e4\u6765\u5145\u9965\uff0c\u5c31\u4e3a\u4e86\u53bb\u8ffd\u8fd9\u6761\u72d0\u72f8\uff0c\u6211\u624d\u9047\u89c1\u4e86\u90a3\u4ef6 \u6211\u8fd9\u4e00\u751f\u4e2d\u6c38\u8fdc\u4e5f\u65e0\u6cd5\u5fd8\u8bb0\u7684\u4e8b\u3002\u201d \u3000\u3000\u4ed6\u5200\u950b\u822c\u7684\u773c\u775b\u4e5f\u5df2\u53d8\u5f97\u975e\u5e38\u6e29\u67d4\uff0c\u7136\u540e\u4ed6\u63a5\u7740\u53c8\u8bf4\u4e86\u4e0b\u53bb\uff1a\u201c\u6211\u5c06\u90a3\u6761\u72d0\u72f8\u4e00\u76f4\u8ffd\u5230 \u5c71\u6d1e\u6700\u6df1\u5904\uff0c\u624d\u53d1\u73b0\u540e\u9762\u7684\u5c71\u58c1\u4e0b\uff0c\u8fd8\u6709\u6761\u79d8\u5bc6\u7684\u51fa\u8def\u3002\u201d \u3000\u3000\u201c\u6211\u62e8\u5f00\u67af\u85e4\u8d70\u8fdb\u53bb\uff0c\u6ca1\u591a\u4e45\u4e4b\u540e\uff0c\u5c31\u542c\u89c1\u4e00\u9635\u9635\u6d41\u6c34\u58f0\uff0c\u6cbf\u7740\u6c34\u58f0\u518d\u5f80\u524d\u8d70\uff0c\u5929\u5149\u8c41 \u7136\u5f00\u6717\uff0c\u5916\u9762\u7adf\u662f\u4e2a\u4e16\u5916\u6843\u6e90\u7684\u4eba\u95f4\u4ed9\u5883\u3002\u201d \u3000\u3000\u201c\u90a3\u65f6\u6b63\u662f\u66ae\u6625\u65f6\u8282\u767e\u82b1\u9f50\u653e\uff0c\u7eff\u8349\u5982\u8335\uff0c\u5c71\u4e0a\u6709\u9053\u6cc9\u6c34\u6d41\u4e0b\u6765\uff0c\u7ade\u662f\u6eda\u70ed\u7684\u3002\u201d \u3000\u3000\u201c\u7136\u540e\u6211\u5c31\u5ffd\u7136\u53d1\u73b0\u90a3\u6e29\u6cc9\u6c34\u6c60\u4e2d\uff0c\u7adf\u6709\u4e2a\u7f8e\u4e3d\u7684\u5c11\u5973\u5728\u6c90\u6d74\u3002\u201d \u3000\u3000\u8bf4\u5230\u8fd9\u91cc\uff0c\u5927\u5bb6\u5f53\u7136\u90fd\u5df2\u77e5\u9053\u4ed6\u8bf4\u7684\u8fd9\u5c11\u5973\u662f\u4ec0\u4e48\u4eba\u4e86\u3002 \u3000\u3000\u536b\u5929\u9e4f\u76ee\u5149\u6e29\u67d4\u5730\u51dd\u6ce8\u5728\u8fdc\u65b9\uff0c\u4eff\u4f5b\u53c8\u770b\u5230\u4e86\u90a3\u9526\u7ee3\u7684\u5c71\u8c37\uff0c\u90a3\u6c90\u6d74\u5728\u6e29\u6cc9\u4e2d\u7684\u7f8e\u4eba\u3002 \u3000\u3000\u201c\u90a3\u65f6\u5979\u4e5f\u5f88\u5e74\u8f7b\uff0c\u4e4c\u9ed1\u53d1\u5149\u7684\u5934\u53d1\uff0c\u53c8\u5149\u6ed1\uff0c\u53c8\u67d4\u8f6f\uff0c\u5c31\u50cf\u662f\u7f0e\u5b50\u4e00\u6837\uff0c\u5c24\u5176\u662f\u5979\u7684 \u773c\u775b\uff0c\u6211\u4ece\u6765\u4e5f\u6ca1\u6709\u770b\u89c1\u8fc7\u90a3\u4e48\u7f8e\u4e3d\u7684\u773c\u775b\u3002\u201d \u3000\u3000\u201c\u6211\u5c31\u50cf\u662f\u4e2a\u5446\u4e8e\u822c\u5730\u770b\u7740\u5979\uff0c\u5df2\u5b8c\u5168\u770b\u5f97\u75f4\u4e86\u3002\u201d \u3000\u3000\u201c\u5979\u8d77\u5148\u597d\u50cf\u89c9\u5f97\u5f88\u60ca\u60f6\uff0c\u5f88\u6124\u6012\uff0c\u4f46\u540e\u6765\u4e5f\u6162\u6162\u5730\u5e73\u9759\u4e0b\u6765\uff0c\u4e5f\u5728\u9759\u9759\u5730\u770b\u7740\u6211\u3002\u201d \u3000\u3000\u201c\u6211\u4eec\u5c31\u8fd9\u6837\u4e92\u76f8\u51dd\u89c6\u7740\uff0c\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u5979\u8138\u4e0a\u5ffd\u7136\u9732\u51fa\u4e86\u4e00\u4e1d\u5fae\u7b11\uff0c\u5927\u5730\u4e0a\u6240\u6709 \u7684\u82b1\u6735\uff0c\u5c31\u4eff\u4f5b\u5df2\u5728\u90a3\u4e00\u77ac\u95f4\u5168\u90e8\u5f00\u653e\u3002\u201d \u3000\u3000\u201c\u6211\u4e0d\u7531\u81ea\u4e3b\u5411\u5979\u8d70\u4e86\u8fc7\u53bb\uff0c\u7ade\u5fd8\u4e86\u524d\u9762\u662f\u4e2a\u6c34\u6c60\uff0c\u4e5f\u5fd8\u4e86\u8eab\u4e0a\u8fd8\u7a7f\u7740\u8863\u88f3\u978b\u5b50\uff0c\u6211\u7b80 \u76f4\u4ec0\u4e48\u90fd\u5fd8\u4e86\uff0c\u53ea\u60f3\u8d70\u8fc7\u53bb\u62b1\u4f4f\u5979\u2026\u2026\u201d \u3000\u3000\u542c\u5230\u8fd9\u65f6\uff0c\u6bcf\u4e2a\u4eba\u8138\u4e0a\u90fd\u4e0d\u7981\u9732\u51fa\u6e29\u67d4\u4e4b\u8272\uff0c\u4eff\u4f5b\u90fd\u5728\u5e7b\u60f3\u7740\u90a3\u4e00\u523b\u7684\u6e29\u99a8\u548c\u751c\u871c\u3002 \u3000\u3000\u53c8\u8fc7\u4e86\u5f88\u4e45\uff0c\u536b\u5929\u9e4f\u624d\u53f9\u606f\u7740\uff0c\u6162\u6162\u4ed6\u8bf4\u4e0b\u53bb\u3002 \u3000\u3000\u201c\u6211\u4eec\u59cb\u7ec8\u6ca1\u6709\u8bf4\u8fc7\u4e00\u4e2a\u5b57\uff0c\u4e5f\u6ca1\u95ee\u8fc7\u5bf9\u65b9\u7684\u59d3\u540d\u548c\u6765\u5386\u3002\u201d \u3000\u3000\u201c\u6240\u6709\u4e00\u5207\u4e8b\uff0c\u90fd\u53d1\u751f\u5f97\u5f88\u81ea\u7136\uff0c\u4e00\u70b9\u4e5f\u6ca1\u6709\u52c9\u5f3a\uff0c\u5c31\u597d\u50cf\u4e0a\u5929\u65e9\u5df2\u5b89\u6392\u597d\u6211\u4eec\u8fd9\u4e48\u6837 \u4e24\u4e2a\u4eba\uff0c\u5728\u8fd9\u5730\u65b9\u89c1\u9762\u7684\u3002\u201d \u3000\u3000\u201c\u76f4\u5230\u5929\u8272\u5df2\u5b8c\u5168\u9ed1\u6697\uff0c\u5979\u5df2\u8981\u8d70\u7684\u65f6\u5019\uff0c\u6211\u624d\u77e5\u9053\u5979\u662f\u4ec0\u4e48\u4eba\u3002\u201d \u3000\u3000\u201c\u56e0\u4e3a\u76f4\u5230\u90a3\u65f6\uff0c\u6211\u624d\u53d1\u73b0\u5979\u989d\u89d2\u4e0a\u7684\u5934\u53d1\u79df\u76d6\u4e0b\u5212\u7740\u4e00\u6735\u9ed1\u8272\u7684\u83b2\u82b1\u3002\u201d \u3000\u3000\u201c\u90a3\u6b63\u662f\u5357\u6d77\u5a18\u5b50\u7684\u6807\u5fd7\uff0c\u6211\u60ca\u8bb6\u4e4b\u4e2d\uff0c\u505a\u51fa\u4e86\u4e00\u4ef6\u4ee4\u6211\u540e\u6094\u7ec8\u751f\u7684\u4e8b\u3002\u201d \u3000\u3000\u201c\u6211\u8131\u53e3\u53eb\u51fa\u4e86\u5979\u7684\u540d\u5b57\u3002\u201d \u3000\u3000\u201c\u5c31\u5728\u90a3\u4e00\u77ac\u95f4\uff0c\u5979\u7684\u4eba\u7a81\u7136\u53d8\u4e86\uff0c\u6e29\u67d4\u7f8e\u4e3d\u7684\u773c\u775b\uff0c\u7a81\u7136\u51fa\u73b0\u4e86\u6740\u673a\uff0c\u7adf\u5411\u6211\u65bd\u51fa\u9b54 \u6559\u4e2d\u6700\u53ef\u6015\u7684\u6b66\u529f\u5927\u5929\u9b54\u624b\uff0c\u4eff\u4f5b\u8981\u5c06\u6211\u7684\u5fc3\u6458\u51fa\u6765\u3002\u201d \u3000\u3000\u201c\u6211\u4e0d\u60f3\u95ea\u907f\uff0c\u4e5f\u4e0d\u80fd\u95ea\u907f\uff0c\u90a3\u65f6\u6211\u7684\u786e\u89c9\u5f97\uff0c\u80fd\u6b7b\u5728\u5979\u624b\u91cc\uff0c\u5df2\u662f\u4ef6\u975e\u5e38\u5e78\u798f\u7684 \u4e8b\u3002\u201d \u3000\u3000\u201c\u4e5f\u8bb8\u5c31\u56e0\u4e3a\u8fd9\u4e00\u70b9\uff0c\u5979\u624d\u4e0d\u5fcd\u771f\u7684\u4e0b\u624b\uff0c\u6211\u751a\u81f3\u53c8\u53ef\u611f\u89c9\u5230\u5979\u7684\u624b\u5df2\u63d2\u5165\u6211\u7684\u80f8\u819b\uff0c \u5979\u90a3\u53cc\u67d4\u82e5\u65e0\u9aa8\u7684\u7ea4\u7ea4\u7389\u624b\uff0c\u7adf\u50cf\u662f\u5ffd\u7136\u53d8\u6210\u4e00\u67c4\u950b\u5229\u7684\u5200\uff0c\u6211\u751a\u81f3\u5df2\u95ed\u4e0a\u773c\u775b\uff0c\u51c6\u5907\u6b7b \u4e86\u3002\u201d \u3000\u3000\u201c\u4f46\u662f\u5979\u5374\u5ffd\u7136\u5c06\u624b\u7f29\u4e86\u56de\u53bb\uff0c\u7b49\u6211\u5f20\u5f00\u773c\u65f6\uff0c\u5979\u7684\u4eba\u5df2\u4e0d\u89c1\u4e86\u3002\u201d \u3000\u3000\u201c\u591c\u8272\u5df2\u7b3c\u7f69\u7740\u5c71\u8c37\uff0c\u5c71\u8c37\u8fd8\u662f\u540c\u6837\u79c0\u4e3d\uff0c\u4f46\u5979\u5374\u4f3c\u5df2\u5ffd\u7136\u6d88\u5931\u5728\u6625\u98ce\u91cc\u3002\u201d \u3000\u3000\u201c\u6211\u5c31\u597d\u50cf\u521a\u505a\u4e86\u573a\u68a6\u4f3c\u7684\uff0c\u82e5\u4e0d\u662f\u80f8\u819b\u4e0a\u8fd8\u5728\u6d41\u7740\u8840\uff0c\u6211\u7b80\u76f4\u4e0d\u80fd\u76f8\u4fe1\u8fd9\u662f\u4ef6\u771f\u7684 \u4e8b\u3002\u201d \u3000\u3000\u201c\u6211\u8dea\u5728\u5730\u4e0a\uff0c\u6c42\u5979\u56de\u6765\uff0c\u518d\u8ba9\u6211\u89c1\u5979\u4e00\u9762\uff0c\u4f46\u6211\u5fc3\u91cc\u77e5\u9053\u5979\u662f\u6c38\u8fdc\u4e0d\u4f1a\u518d\u56de\u6765\u7684 \u4e86\u3002\u201d \u3000\u3000\u201c\u6240\u4ee5\uff0c\u6211\u53c8\u53d1\u8a93\uff0c\u53ea\u8981\u518d\u89c1\u5230\u5979\uff0c\u65e0\u8bba\u5979\u8981\u6211\u505a\u4ec0\u4e48\uff0c\u6211\u90fd\u4e0d\u4f1a\u8fdd\u80cc\u5979\u7684\u610f\u601d\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u81ea\u4ece\u90a3\u4e00\u5929\u4e4b\u540e\uff0c\u6211\u5c31\u6c38\u8fdc\u518d\u4e5f\u6ca1\u6709\u89c1\u7740\u5979\uff0c\u6c38\u8fdc\u4e5f\u6ca1\u6709\u2026\u2026\u201d \u3000\u3000\u4ed6\u58f0\u97f3\u8d8a\u8bf4\u8d8a\u4f4e\uff0c\u7ec8\u4e8e\u53d8\u6210\u4e86\u58f0\u957f\u957f\u7684\u53f9\u606f\u3002 \u3000\u3000\u8fd9\u662f\u4e2a\u7f8e\u4e3d\u3001\u51c4\u51c9\uff0c\u800c\u4e14\u5145\u6ee1\u4e86\u68a6\u5e7b\u7684\u795e\u79d8\u7684\u6545\u4e8b\u3002 \u3000\u3000\u8fd9\u6545\u4e8b\u7f8e\u4e3d\u5f97\u5c31\u50cf\u662f\u795e\u8bdd\uff0c\u4f60\u53ea\u8981\u770b\u89c1\u94c1\u59d1\u548c\u536b\u5929\u9e4f\u8138\u4e0a\u7684\u8868\u60c5\u5c31\u77e5\u9053\u8fd9\u6545\u4e8b\u6bcf\u4e2a\u5b57\u90fd \u662f\u771f\u7684\u3002 \u3000\u3000\u94c1\u59d1\u7f8e\u4e3d\u800c\u51b7\u6f20\u7684\u8138\uff0c\u7ade\u4f3c\u5df2\u56e0\u60b2\u75db\u548c\u9707\u60ca\u800c\u53d8\u5f62\u3002 \u3000\u3000\u5fc3\u59d1\u7684\u795e\u8272\u4e5f\u53d8\u4e86\u3002\u53ea\u6709\u90a3\u6728\u96d5\u7684\u89c2\u97f3\u795e\u50cf\uff0c\u8fd8\u662f\u624b\u62c8\u7740\u6768\u67f3\u679d\uff0c\u5728\u7f2d\u7ed5\u7684\u70df\u96fe\u4e2d\u5fae\u5fae \u542b\u7b11\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u536b\u5929\u9e4f\u624d\u6062\u590d\u9547\u9759\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u6240\u4ee5\u6211\u77e5\u9053\u5357\u6d77\u5a18\u5b50\u5df2\u6b7b\u4e86\uff0c\u6211\u77e5\u9053 \u9b54\u6559\u4e2d\u6709\u79cd\u795e\u79d8\u7684\u8179\u8bed\u672f\uff0c\u4f60\u4eec\u5229\u7528\u8fd9\u6728\u5076\u5c31\u60f3\u628a\u6211\u5413\u8d70\uff0c\u4e5f\u672a\u514d\u60f3\u5f97\u592a\u5929\u771f\u4e86\u3002\u201d \u3000\u3000\u5fc3\u59d1\u5ffd\u7136\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u90a3\u4e9b\u8bdd\u90fd\u662f\u6211\u501f\u89c2\u97f3\u795e\u50cf\u7684\u5634\u8bf4\u7684\uff0c\u53ef\u662f\u6211\u8bf4\u7684\u8bdd\u4e5f\u4e00\u6837\u6709 \u6548\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u5fc3\u59d1\u9053\uff1a\u201c\u4f60\u82e5\u4e00\u5b9a\u8fd8\u8981\u6253\u4e0a\u5b98\u5c0f\u4ed9\u7684\u4e3b\u610f\uff0c\u6211\u4fdd\u8bc1\u4f60\u4e00\u5b9a\u4f1a\u540e\u6094\u7684\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u7a81\u7136\u5927\u7b11\uff0c\u9053\uff1a\u201c\u6211\u536b\u516b\u81ea\u5341\u4e09\u5c81\u51fa\u9053\uff0c\u5728\u6c5f\u6e56\u4e2d\u6df7\u4e86\u4e94\u516d\u5341\u5e74\uff0c\u81f3\u4eca\u8fd8\u6ca1\u6709\u4e3a \u4efb\u4f55\u4e00\u4ef6\u4e8b\u540e\u6094\u8fc7\u3002\u201d \u3000\u3000\u5fc3\u59d1\u9053\uff1a\u201c\u4f60\u4e00\u5b9a\u4e0d\u80af\u653e\u8fc7\u4ed6\u4eec\uff1f\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u6211\u53ea\u5e0c\u671b\u4f60\u4eec\u80fd\u5c06\u8fd9\u7897\u996d\u5206\u7ed9\u5927\u5bb6\u5403\uff0c\u83ab\u8981\u4e00\u4e2a\u72ec\u541e\u3002\u201d \u3000\u3000\u5fc3\u59d1\u51b7\u7b11\u9053\uff1a\u201c\u597d\uff0c\u5ff5\u5728\u4f60\u6614\u5e74\u548c\u672c\u95e8\u7956\u5e08\u7237\u7684\u90a3\u4e00\u70b9\u60c5\u4efd\uff0c\u6211\u73b0\u5728\u53ef\u4ee5\u8ba9\u4f60\u6d3b\u7740\u8d70\u51fa \u53bb\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u7136\u540e\u5462\uff1f\u201d \u3000\u3000\u5fc3\u59d1\u9053\uff1a\u201c\u53ea\u8981\u4f60\u4e00\u8d70\u51fa\u8fd9\u95f4\u5c4b\u5b50\uff0c\u4ece\u6b64\u5c31\u662f\u6211\u5357\u6d77\u95e8\u7684\u5bf9\u5934\uff0c\u4f60\u6700\u597d\u5c31\u8d76\u5feb\u53bb\u51c6\u5907\u540e \u4e8b\uff0c\u56e0\u4e3a\u4f60\u968f\u65f6\u90fd\u8bf4\u4e0d\u5b9a\u4f1a\u6b7b\u7684\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u4f60\u4eec\u82e5\u4e00\u5b9a\u8ddf\u6211\u4f5c\u5bf9\uff0c\u4e5f\u672a\u5fc5\u8fd8\u80fd\u6d3b\u591a\u4e45\u7684\u3002\u201d \u3000\u3000\u4ed6\u51b7\u7b11\u7740\uff0c\u970d\u7136\u957f\u8eab\u800c\u8d77\uff0c\u5ffd\u7136\u53c8\u5411\u58a8\u767d\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u6211\u4eec\u4ee5\u524d\u7684\u6069\u6028\uff0c\u4e5f\u4e0d\u59a8\u4e00\u7b14 \u52fe\u9500\uff0c\u4ece\u73b0\u5728\u8d77\uff0c\u4f60\u6211\u662f\u53cb\u662f\u654c\uff0c\u4e5f\u5c31\u770b\u4f60\u4e86\u3002\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u4e00\u8bf4\u5b8c\uff0c\u4ed6\u5c31\u5934\u4e5f\u4e0d\u56de\u5730\u8d70\u4e86\u51fa\u53bb\u3002 \u7b2c\u4e94\u7ae0\u3000\u98de\u72d0\u6768\u5929>> \u53e4\u9f99\u300a\u4e5d\u6708\u9e70\u98de\u300b \u7b2c\u4e94\u7ae0\u3000\u98de\u72d0\u6768\u5929 \u3000\u3000\u95e8\u5916\u51b7\u96fe\u51c4\u8ff7\uff0c\u591c\u66f4\u6df1\uff0c\u98ce\u66f4\u51b7\u3002 \u3000\u3000\u536b\u5929\u9e4f\u8fce\u7740\u98ce\u957f\u957f\u5438\u4e86\u53e3\u6c14\uff0c\u5ffd\u7136\u9053\uff1a\u201c\u97e9\u8d1e\u3002\u201d \u3000\u3000\u97e9\u8d1e\u5df2\u8ddf\u8fc7\u6765\uff0c\u9053\uff1a\u201c\u5728\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u90a3\u98d8\u9999\u522b\u9662\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u6211\u4eec\u73b0\u5728\u5c31\u53bb\uff1f\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u5148\u4e0b\u624b\u7684\u4e3a\u5f3a\uff0c\u8fd9\u53e5\u606c\u4f60\u8be5\u542c\u8bf4\u8fc7\u7684\u3002\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u53ef\u662f\u90a3\u53f6\u5f00\u2026\u2026\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u53f6\u5f00\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u53f6\u5f00\u73b0\u5728\u5fc5\u5b9a\u5df2\u6709\u9632\u5907\uff0c\u6211\u4eec\u73b0\u5728\u82e5\u53bb\u8ddf\u4ed6\u786c\u62fc\u4e00\u573a\uff0c\u4e0d\u8bba\u8c01\u80dc\u8c01\u8d1f\uff0c\u53cc\u65b9 \u90fd\u96be\u514d\u8981\u6709\u4f24\u635f\uff0c\u5c82\u975e\u8ba9\u522b\u4eba\u6e14\u7fc1\u5f97\u5229\u4e86\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u8c01\u8bf4\u6211\u4eec\u662f\u8981\u8ddf\u4ed6\u53bb\u6253\u67b6\u7684\uff1f\u201d \u3000\u3000\u97e9\u8d1e\uff1a\u201c\u4e0d\u662f\uff1f\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u5f53\u7136\u4e0d\u662f\u3002\u201d \u3000\u3000\u4ed6\u5634\u89d2\u53c8\u9732\u51fa\u4e86\u72d0\u72f8\u4e00\u6837\u7684\u5fae\u7b11\uff0c\u60a0\u7136\u9053\uff1a\u201c\u6211\u4eec\u662f\u597d\u610f\u53bb\u5411\u4ed6\u901a\u98ce\u62a5\u4fe1\uff0c\u662f\u8ddf\u4ed6\u4ea4\u670b \u53cb\u53bb\u7684\u3002\u201d \u3000\u3000\u97e9\u8d1e\u7684\u773c\u775b\u4eae\u4e86\uff0c\u5fae\u7b11\u7740\u9053\uff1a\u201c\u56e0\u4e3a\u5c0f\u674e\u63a2\u82b1\u6614\u65e5\u4e5f\u5bf9\u6211\u4eec\u6709\u6069\uff0c\u6211\u4eec\u8fd9\u6b21\u6765\u5e76\u4e0d\u662f\u4e3a \u4e86\u8981\u7b97\u8ba1\u4ed6\uff0c\u800c\u662f\u4e3a\u4e86\u62a5\u6069\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u4e00\u70b9\u4e5f\u4e0d\u9519\u3002\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u5357\u6d77\u5a18\u5b50\u65e2\u7136\u6b7b\u4e86\uff0c\u522b\u7684\u4eba\u5df2\u4e0d\u8db3\u4e3a\u8651\uff0c\u6211\u4eec\u4e00\u5b9a\u8981\u529d\u4ed6\u4e58\u8fd9\u4e2a\u597d\u673a\u4f1a\uff0c\u5148 \u4e0b\u624b\u628a\u90a3\u4e9b\u5bf9\u4ed6\u6709\u91ce\u5fc3\u7684\u4eba\u9664\u53bb\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u4ed6\u662f\u4e2a\u806a\u660e\u4eba\uff0c\u4e00\u5b9a\u4f1a\u660e\u767d\u7684\u3002\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u4f55\u51b5\u4ed6\u8fd8\u6709\u6211\u4eec\u505a\u4ed6\u7684\u540e\u76fe\uff0c\u4ed6\u65e0\u8bba\u8981\u6740\u4ec0\u4e48\u4eba\uff0c\u6211\u4eec\u90fd\u53ef\u4ee5\u5e2e\u4ed6\u63d0\u5200\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u5927\u7b11\u9053\uff1a\u201c\u597d\uff0c\u4f60\u679c\u7136\u8d8a\u6765\u8d8a\u61c2\u4e8b\u4e86\uff0c\u4e5f\u4e0d\u5728\u6211\u5bf9\u4f60\u4e00\u756a\u82e6\u5fc3\u3002\u201d \u3000\u3000\u4ed6\u4eec\u5df2\u8d70\u5165\u4e86\u6885\u6797\uff0c\u4e00\u9635\u9635\u6625\u98ce\u5439\u8fc7\uff0c\u8ff7\u96fe\u4e2d\u5ffd\u7136\u51fa\u73b0\u4e86\u4e00\u4e2a\u5e7d\u7075\u822c\u7684\u4eba\u5f71\u3002 \u3000\u3000\u536b\u5929\u9e4f\u4f4e\u559d\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u201c\u662f\u6211\uff01\u201d \u3000\u3000\u8fd9\u4eba\u5782\u7740\u5934\u8d70\u6765\uff0c\u7adf\u662f\u897f\u95e8\u5341\u4e09\u3002 \u3000\u3000\u536b\u5929\u9e4f\u6c89\u4e0b\u4e86\u8138\uff0c\u9053\uff1a\u201c\u8c01\u53eb\u4f60\u5230\u8fd9\u91cc\u6765\u7684\uff1f\u201d \u3000\u3000\u897f\u95e8\u5341\u4e09\u9894\u9996\u9053\uff1a\u201c\u5f1f\u5b50\u6709\u4ef6\u8981\u7d27\u7684\u4e8b\uff0c\u8981\u7980\u62a5\u4f60\u8001\u4eba\u5bb6\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u897f\u95e8\u5341\u4e09\u8d70\u8fd1\u51e0\u6b65\uff0c\u8d70\u5f97\u66f4\u8fd1\u4e9b\uff0c\u9053\uff1a\u201c\u6211\u77e5\u9053\u53f6\u5f00\u2026\u2026\u201d \u3000\u3000\u4ed6\u58f0\u97f3\u5b9e\u5728\u592a\u4f4e\uff0c\u536b\u5929\u9e4f\u53ea\u597d\u628a\u8033\u6735\u51d1\u8fc7\u53bb\u3002 \u3000\u3000\u4ed6\u4e00\u751f\u6740\u4eba\u65e0\u6570\uff0c\u968f\u65f6\u968f\u5730\u90fd\u5728\u63d0\u9632\u7740\u522b\u4eba\u6740\u4ed6\uff0c\u4f46\u6b64\u65f6\u4ed6\u5374\u662f\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\uff0c\u4ed6\u6700\u5ba0 \u7231\u7684\u8fd9\u4e2a\u5f92\u5f1f\u624b\u91cc\uff0c\u7adf\u6709\u628a\u51c6\u5907\u523a\u4eba\u4ed6\u80f8\u819b\u7684\u5200\u3002 \u3000\u3000\u4e24\u4e2a\u4eba\u8eab\u5b50\u5df2\u51d1\u5728\u4e00\u8d77\u3002 \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u6709\u4ec0\u4e48\u8bdd\u7094\u8bf4\u3002\u201d \u3000\u3000\u897f\u95e8\u5341\u4e09\u9053\uff1a\u201c\u6211\u8981\u4f60\u6b7b\u3002\u201d \u3000\u3000\u542c\u5230\u8fd9\u4e2a\u201c\u6b7b\u201d\u5b57\uff0c\u536b\u5929\u9e4f\u624d\u5403\u4e86\u4e00\u60ca\uff0c\u4f46\u95ea\u907f\u5df2\u6765\u4e0d\u53ca\u4e86\u3002 \u3000\u3000\u4ed6\u5df2\u80fd\u611f\u89c9\u5230\u51b0\u51b7\u7684\u5200\u950b\uff0c\u523a\u5165\u4e86\u4ed6\u7684\u76ae\u88d8\uff0c\u523a\u5728\u4ed6\u80f8\u819b\u4e0a\uff0c\u4ed6\u751a\u81f3\u5df2\u80fd\u611f\u89c9\u5230\u6b7b\u7684\u6ecb \u5473\u3002 \u3000\u3000\u5728\u8fd9\u95f4\u4e0d\u5bb9\u53d1\u7684\u4e00\u5239\u90a3\u95f4\uff0c\u897f\u95e8\u5341\u4e09\u7a81\u7136\u60e8\u547c\u7740\u5012\u4e0b\u3002 \u3000\u3000\u4ed6\u624b\u91cc\u90a3\u67c4\u6740\u4eba\u7684\u5200\uff0c\u5728\u591c\u8272\u4e2d\u95ea\u7740\u78a7\u5149\uff0c\u5200\u950b\u4e0a\u5df2\u6cbe\u7740\u8840\u8ff9\u3002 \u3000\u3000\u662f\u536b\u5929\u9e4f\u7684\u8840\u3002\u536b\u5929\u9e4f\u8eab\u5b50\u8fd9\u624d\u5f00\u59cb\u53d1\u6296\uff0c\u624d\u771f\u6b63\u611f\u89c9\u5230\u6b7b\u7684\u6050\u614c\u3002 \u3000\u3000\u897f\u95e8\u5341\u4e09\u4ef0\u9762\u5012\u5728\u96ea\u5730\u4e0a\uff0c\u773c\u73e0\u5df2\u7a81\u51fa\uff0c\u8033\u3001\u9f3b\u3001\u773c\u3001\u53e3\u4e2d\uff0c\u7a81\u7136\u540c\u65f6\u6709\u9c9c\u8840\u6d41\u51fa\u3002 \u3000\u3000\u8840\u7adf\u662f\u9ed1\u7684\u3002 \u3000\u3000\u536b\u5929\u9e4f\u8f6c\u5934\u53bb\u770b\u97e9\u8d1e\uff0c\u97e9\u8d1e\u4e5f\u5df2\u5413\u5f97\u5446\u4f4f\u3002 \u3000\u3000\u897f\u95e8\u5341\u4e09\u663e\u7136\u4e0d\u662f\u88ab\u4ed6\u6740\u4e86\u7684\u3002 \u3000\u3000\u7a76\u7adf\u662f\u8c01\u5728\u6697\u4e2d\u51fa\u624b\uff0c\u6551\u4e86\u536b\u5929\u9e4f\u8fd9\u6761\u547d\uff1f \u3000\u3000\u5de5\u5929\u9e4f\u5df2\u6ca1\u7a7a\u518d\u60f3\u4e86\uff0c\u8fd9\u6885\u6797\u51b7\u96fe\u4e2d\uff0c\u5904\u5904\u90fd\u4eff\u4f5b\u9690\u85cf\u7740\u6740\u673a\u3002 \u3000\u3000\u4ed6\u8dfa\u4e86\u8dfa\u811a\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5feb\u9000\u51fa\u53bb\u3002\u201d \u3000\u3000\u7a81\u542c\u4e00\u4eba\u9053\uff1a\u201c\u4f60\u7ad9\u7740\u4e0d\u80fd\u52a8\uff0c\u5426\u5219\u5200\u6bd2\u4e00\u53d1\uff0c\u5c31\u5fc5\u6b7b\u65e0\u7591\u4e86\u3002\u201d \u3000\u3000\u58f0\u8457\u6e05\u8106\u59a9\u5a9a\uff0c\u4e00\u4e2a\u4eba\u5e7d\u7075\u822c\u5730\u5728\u96fe\u4e2d\u51fa\u73b0\uff0c\u8d6b\u7136\u7ade\u662f\u94c1\u59d1\u3002 \u3000\u3000\u536b\u5929\u9e4f\u6115\u7136\u9053\uff1a\u201c\u521a\u624d\u662f\u4f60\u6551\u4e86\u6211\uff1f\u201d \u3000\u3000\u94c1\u59d1\u70b9\u70b9\u5934\u3002 \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u53eb\u4ed6\u6765\u6740\u6211\u7684\u4e5f\u662f\u4f60\uff1f\u201d \u3000\u3000\u94c1\u59d1\u53c8\u70b9\u70b9\u5934\u3002 \u3000\u3000\u53ea\u6709\u88ab\u5979\u6444\u5fc3\u5927\u6cd5\u6240\u8ff7\u7684\u4eba\uff0c\u624d\u4f1a\u505a\u5f97\u51fa\u8fd9\u79cd\u4e8b\u3002 \u3000\u3000\u5de5\u5929\u9e4f\u9053\uff1a\u201c\u4f60\u65e2\u7136\u53eb\u4ed6\u6765\u6740\u6211\uff0c\u4e3a\u4ec0\u4e48\u53c8\u8981\u6765\u6551\u6211\uff1f\u201d \u3000\u3000\u94c1\u59d1\u82cd\u767d\u7684\u8138\u4e0a\u5e26\u7740\u79cd\u65e0\u6cd5\u63cf\u53d9\u7684\u8868\u60c5\uff0c\u8c01\u4e5f\u731c\u4e0d\u51fa\u5979\u5fc3\u91cc\u5728\u60f3\u4ec0\u4e48\uff0c\u66f4\u731c\u4e0d\u51fa\u5979\u4e3a\u4ec0 \u4e48\u8981\u8fd9\u6837\u505a\u3002 \u3000\u3000\u53ef\u662f\u5979\u770b\u7740\u536b\u5929\u9e4f\u7684\u65f6\u5019\uff0c\u773c\u775b\u91cc\u5374\u4eff\u4f5b\u6709\u79cd\u5f88\u5f3a\u70c8\u7684\u8868\u60c5\u3002 \u3000\u3000\u5979\u672c\u4e0d\u662f\u5bb9\u6613\u52a8\u611f\u60c5\u7684\u3002 \u3000\u3000\u5979\u51e0\u4e4e\u5df2\u6ca1\u6709\u611f\u60c5\u3002 \u3000\u3000\u536b\u5929\u9e4f\u770b\u7740\u5979\uff0c\u773c\u775b\u5ffd\u7136\u4e5f\u9732\u51fa\u79cd\u65e0\u6cd5\u63cf\u53d9\u7684\u611f\u60c5\u3002\u5ffd\u7136\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u662f\u5979\u7684\u5973 \u513f\uff1f\u201d \u3000\u3000\u94c1\u59d1\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u536b\u5929\u9e4f\u5012\u9000\u4e86\u4e24\u6b65\uff0c\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u2026\u2026\u4f60\u2026\u2026\u4f60\u96be\u9053\u4e5f\u662f\u6211\u7684\u2026\u2026\u201d \u3000\u3000\u201c\u5973\u513f\u201d\u8fd9\u4e24\u4e2a\u5b57\u4ed6\u5e76\u6ca1\u6709\u8bf4\u51fa\u6765\uff0c\u4ed6\u597d\u50cf\u4e0d\u6562\u8bf4\u51fa\u6765\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u4e0d\u5fc5\u8bf4\u51fa\u6765\uff0c\u522b\u4eba\u4e5f\u77e5\u9053\u7684\u3002 \u3000\u3000\u94c1\u59d1\u5c45\u7136\u5e76\u6ca1\u6709\u5426\u8ba4\uff0c\u76ee\u4e2d\u7684\u795e\u8272\u53c8\u53d8\u5f97\u5f88\u60b2\u4f24\uff0c\u5ffd\u7136\u9053\uff1a\u201c\u5979\u8fd9\u4e00\u751f\u4e2d\uff0c\u53ea\u6709\u4f60\u4e00\u4e2a \u7537\u4eba\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u540e\u9000\u4e86\u4e24\u6b65\uff0c\u8eab\u5b50\u7a81\u7136\u53c8\u5f00\u59cb\u53d1\u6296\u3002 \u3000\u3000\u2014\u2014\u5357\u6d77\u5a18\u5b50\u8fd9\u4e00\u751f\u4e2d\uff0c\u5c45\u7136\u53ea\u6709\u4ed6\u4e00\u4e2a\u7537\u4eba\u3002 \u3000\u3000\u4ed6\u5fc3\u91cc\u4e5f\u4e0d\u77e5\u9053\u662f\u611f\u52a8\uff0c\u662f\u60ca\u8bb6\uff0c\u8fd8\u662f\u60b2\u4f24\u3002 \u3000\u3000\u94c1\u59d1\u7684\u773c\u775b\u91cc\u4f3c\u5df2\u6709\u6cea\u5149\uff0c\u9053\uff1a\u201c\u6240\u4ee5\u6211\u4e0d\u80fd\u770b\u7740\u4f60\u6b7b\u3002\u201d \u3000\u3000\u5979\u5f53\u7136\u4e0d\u80fd\u3002 \u3000\u3000\u4e16\u4e0a\u7edd\u6ca1\u6709\u4efb\u4f55\u4e00\u4e2a\u4eba\uff0c\u80fd\u773c\u89c1\u7740\u81ea\u5df1\u7236\u4eb2\u6b7b\u5728\u522b\u4eba\u5200\u4e0b\u7684\u3002 \u3000\u3000\u2014\u2014\u96be\u9053\u5979\u7adf\u771f\u7684\u662f\u6211\u7684\u4eb2\u751f\u5973\u513f\uff1f \u3000\u3000\u536b\u5929\u9e4f\u51e0\u4e4e\u4e0d\u76f8\u4fe1\uff0c\u5374\u5df2\u4e0d\u80fd\u4e0d\u4fe1\u3002 \u3000\u3000\u4ed6\u4e00\u751f\u4e2d\u6700\u5927\u7684\u9057\u61be\uff0c\u5c31\u662f\u6ca1\u6709\u5973\u513f\uff0c\u8c01\u77e5\u5230\u4e86\u5782\u66ae\u7684\u665a\u5e74\uff0c\u7adf\u5ffd\u7136\u6709\u4e86\u4e2a\u5973\u513f\u3002 \u3000\u3000\u5982\u6b64\u7f8e\u4e3d\uff0c\u5982\u6b64\u503c\u5f97\u9a84\u505a\u7684\u5973\u513f\u3002 \u3000\u3000\u4ed6\u770b\u7740\u5979\uff0c\u773c\u775b\u91cc\u4e5f\u4e0d\u7981\u6709\u4e86\u6cea\u5149\uff0c\u5df2\u5b8c\u5168\u5fd8\u4e86\u81ea\u5df1\u521a\u624d\u8fd8\u60f3\u53eb\u4eba\u53bb\u6740\u4e86\u5979\u97f5\u3002 \u3000\u3000\u8840\u6d53\u4e8e\u6c34\u3002 \u3000\u3000\u5c31\u8fde\u91ce\u517d\u4e5f\u6709\u4eb2\u60c5\uff0c\u4f55\u51b5\u662f\u4eba\uff01 \u3000\u3000\u536b\u5929\u9e4f\u98a4\u6296\u7740\u4f38\u51fa\u624b\uff0c\u4f3c\u4e4e\u60f3\u53bb\u6478\u6478\u5979\u7684\u5934\u53d1\uff0c\u6478\u6478\u5979\u7684\u8138\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u53c8\u4e0d\u6562\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u6885\u6797\u5916\u5ffd\u7136\u53c8\u6709\u4e2a\u4eba\u51b2\u4e86\u8fdb\u6765\uff0c\u5403\u60ca\u5730\u770b\u7740\u4ed6\u3002 \u3000\u3000\u5fc3\u59d1\u4e5f\u6765\u4e86\u3002 \u3000\u3000\u94c1\u59d1\u5ffd\u7136\u957f\u957f\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u4e0d\u8be5\u6765\u7684\u3002\u201d \u3000\u3000\u5fc3\u59d1\u7528\u529b\u54ac\u7740\u5634\u5507\uff0c\u5ffd\u7136\u5927\u58f0\u9053\uff1a\u201c\u6211\u4e3a\u4ec0\u4e48\u4e0d\u8be5\u6765\u2026\u2026\u4ed6\u65e2\u7136\u662f\u4f60\u7684\u7236\u4eb2\uff0c\u5c31\u662f\u6211\u7684 \u7956\u7236\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u80fd\u6765\u770b\u770b\u4ed6\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u53c8\u6014\u4f4f\u3002 \u3000\u3000\u539f\u6765\u4ed6\u4e0d\u4f46\u6709\u4e86\u5973\u513f\uff0c\u8fd8\u6709\u4e86\u5b59\u5973\u3002 \u3000\u3000\u4ed6\u53ea\u89c9\u5f97\u5168\u8eab\u7684\u8840\u90fd\u70ed\u4e86\uff0c\u51e0\u4e4e\u5df2\u5fcd\u4e0d\u4f4f\u8981\u5927\u53eb\u8d77\u6765\u3002 \u3000\u3000\u8c01\u77e5\u5c31\u5728\u8fd9\u65f6\uff0c\u5fc3\u59d1\u7a81\u7136\u53cd\u8eab\u51fa\u624b\uff0c\u95ea\u7535\u822c\u70b9\u4e86\u4ed6\u80f8\u524d\u4e03\u5904\u7a74\u9053\u3002 \u3000\u3000\u97e9\u8d1e\u672c\u6765\u4e00\u76f4\u5728\u65c1\u8fb9\u770b\u7740\uff0c\u9047\u89c1\u8fd9\u79cd\u4e8b\uff0c\u4ed6\u4e5f\u53ea\u6709\u5728\u65c1\u8fb9\u770b\u7740\u3002 \u3000\u3000\u770b\u89c1\u5fc3\u59d1\u51fa\u624b\u65f6\uff0c\u4ed6\u60f3\u6551\u5df2\u6765\u4e0d\u53ca\u4e86\uff0c\u8c01\u77e5\u5fc3\u59d1\u7adf\u53c8\u6276\u4f4f\u4e86\u536b\u5929\u9e4f\uff0c\u9053\uff1a\u201c\u5200\u4e0a\u5df2\u89c1\u4e86 \u8840\uff0c\u4ed6\u60f3\u5fc5\u5df2\u4e2d\u4e86\u6bd2\uff0c\u4f60\u5feb\u62b1\u8d77\u4ed6\u8ddf\u6211\u6765\u3002\u201d \u3000\u3000\u539f\u6765\u5979\u51fa\u624b\u662f\u4e3a\u4e86\u6551\u4eba\uff0c\u97e9\u8d1e\u53f9\u4e86\u53e3\u6c14\uff0c\u4eca\u5929\u4ed6\u770b\u89c1\u7684\u548c\u542c\u89c1\u7684\u8fd9\u4e9b\u4e8b\uff0c\u4ed6\u77e5\u9053\u81ea\u5df1\u8fd9 \u8f88\u5b50\u90fd\u6c38\u8fdc\u5fd8\u4e0d\u4e86\u7684\u3002 \u3000\u3000\u4ed6\u8fd9\u4e00\u751f\u4e2d\uff0c\u4e5f\u4ece\u6765\u6ca1\u6709\u9047\u89c1\u8fc7\u8fd9\u4e48\u5947\u79d8\u7684\u4e8b\u3002 \u3000\u3000\u4f5b\u5802\u91cc\u71c3\u7740\u9999\uff0c\u9999\u70df\u7f2d\u7ed5\uff0c\u4eff\u4f5b\u6885\u6797\u4e2d\u7684\u51b7\u96fe\u4e00\u6837\u3002 \u3000\u3000\u97e9\u8d1e\u5c06\u536b\u5929\u9e4f\u653e\u4e86\u4e0b\u6765\uff0c\u653e\u5728\u4e00\u5f20\u8f6f\u69bb\u4e0a\u3002 \u3000\u3000\u795e\u6848\u524d\u6446\u7740\u7684\u51e0\u4e2a\u84b2\u56e2\u4e0a\uff0c\u5750\u7740\u4e2a\u4e91\u9b13\u9ad8\u9afb\u7684\u9526\u8863\u5c11\u5973\uff0c\u4eff\u4f5b\u5f88\u7f8e\u3002 \u3000\u3000\u5979\u91cd\u7709\u655b\u76ee\uff0c\u76d8\u819d\u5750\u5728\u90a3\u91cc\uff0c\u7adf\u50cf\u662f\u8001\u50e7\u5165\u5b9a\u4e00\u6837\u3002 \u3000\u3000\u8fd9\u4e48\u591a\u4eba\u4ece\u5916\u9762\u8d70\u8fdb\u6765\uff0c\u5979\u5c45\u7136\u4e0d\u95fb\u4e0d\u95ee\uff0c\u597d\u50cf\u6839\u672c\u6ca1\u6709\u770b\u5230\u3002 \u3000\u3000\u4f46\u97e9\u8d1e\u5374\u5fcd\u4e0d\u4f4f\u8981\u53bb\u770b\u770b\u5979\u3002 \u3000\u3000\u653e\u7740\u8fd9\u4e48\u7f8e\u7684\u5c11\u5973\u5728\u9762\u524d\uff0c\u82e5\u662f\u8fde\u770b\u90fd\u4e0d\u770b\uff0c\u8fd9\u4e2a\u4eba\u4e00\u5b9a\u4e0d\u662f\u4e2a\u7537\u4eba\u3002 \u3000\u3000\u97e9\u8d1e\u603b\u7b97\u8fd8\u662f\u4e2a\u7537\u4eba\u3002 \u3000\u3000\u4ed6\u770b\u4e86\u4e00\u773c\uff0c\u5c31\u5fcd\u4e0d\u4f4f\u8981\u591a\u770b\u4e24\u773c\uff0c\u4ed6\u5ffd\u7136\u53d1\u73b0\u8fd9\u5c11\u5973\u5f88\u50cf\u4e00\u4e2a\u4eba\uff0c\u50cf\u4e01\u9e9f\u3002 \u3000\u3000\u7eb5\u6a2a\u6c5f\u6e56\u7684\u201c\u98ce\u90ce\u541b\u201d\uff0c\u600e\u4e48\u4f1a\u5ffd\u7136\u53d8\u6210\u4e86\u4e2a\u5973\u4eba\uff1f \u3000\u3000\u97e9\u8d1e\u5f53\u7136\u4e0d\u4f1a\u76f8\u4fe1\u8fd9\u79cd\u4e8b\uff0c\u4f46\u8d8a\u770b\u8d8a\u50cf\uff0c\u8fd9\u5c11\u5987\u5c31\u7b97\u4e0d\u662f\u4e01\u9e9f\uff0c\u4e5f\u4e00\u5b9a\u662f\u4e01\u9e9f\u7684\u59d0\u59b9\u3002 \u3000\u3000\u4e01\u9e9f\u7684\u4eba\u5462\uff1f \u3000\u3000\u4ed6\u82e5\u662f\u5df2\u88ab\u94c1\u59d1\u5979\u4eec\u6740\u4e86\uff0c\u4ed6\u7684\u59d0\u59b9\u53c8\u600e\u4e48\u80fd\u5b89\u5fc3\u5730\u5750\u5728\u8fd9\u91cc\uff1f \u3000\u3000\u97e9\u8d1e\u5e76\u4e0d\u662f\u4e2a\u5f88\u597d\u5947\u7684\u4eba\uff0c\u4e00\u5411\u4e0d\u592a\u559c\u6b22\u7ba1\u522b\u4eba\u7684\u95f2\u4e8b\u3002 \u3000\u3000\u53ef\u662f\u73b0\u5728\u4ed6\u5b9e\u5728\u89c9\u5f97\u5f88\u5947\u602a\uff0c\u6bcf\u4e2a\u4eba\u90fd\u591a\u591a\u5c11\u5c11\u96be\u514d\u6709\u70b9\u597d\u5947\u5fc3\u7684\u3002 \u3000\u3000\u97e9\u8d1e\u6bd5\u7adf\u8fd8\u662f\u4e2a\u4eba\u3002 \u3000\u3000\u94c1\u59d1\u548c\u5fc3\u59d1\u5df2\u5728\u4e3a\u536b\u5929\u9e4f\u6cbb\u4f24\u7597\u6bd2\uff0c\u597d\u50cf\u5e76\u6ca1\u6709\u6ce8\u610f\u5230\u4ed6\u3002 \u3000\u3000\u97e9\u8d1e\u5fcd\u4e0d\u4f4f\u6162\u6162\u8d70\u8fc7\u53bb\uff0c\u4fcf\u4fcf\u5524\u9053\uff1a\u201c\u4e01\u9e9f\u3002\u201d \u3000\u3000\u9526\u8863\u5c11\u5973\u679c\u7136\u62ac\u8d77\u5934\u6765\u770b\u4e86\u4ed6\u4e00\u773c\uff0c\u5374\u50cf\u662f\u6839\u672c\u4e0d\u8ba4\u5f97\u8fd9\u4e2a\u4eba\u4e00\u6837\uff0c\u6447\u4e86\u6447\u5934\u9053\uff1a\u201c\u6211 \u4e0d\u662f\u4e01\u9e9f\u3002\u201d \u3000\u3000\u97e9\u8d1e\u53c8\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u201d \u3000\u3000\u9526\u8863\u5c11\u5973\u9053\uff1a\u201c\u6211\u662f\u4e01\u7075\u7433\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\uff01 \u3000\u3000\u8fd9\u540d\u5b57\u97e9\u8d1e\u662f\u542c\u89c1\u8fc7\u7684\u2026\u2026\u4e01\u7075\u7433\u5c82\u975e\u5c31\u662f\u53f6\u5f00\u7684\u60c5\u4eba\uff1f \u3000\u3000\u5979\u957f\u5f97\u600e\u4e48\u4f1a\u8ddf\u4e01\u9e9f\u4e00\u6a21\u4e00\u6837\uff1f\u5979\u8ddf\u4e01\u9e9f\u53c8\u6709\u4ec0\u4e48\u5173\u7cfb\uff1f \u3000\u3000\u8fd9\u9526\u8863\u5c11\u5973\u53c8\u95ed\u8d77\u4e86\u773c\u775b\uff0c\u8fde\u770b\u90fd\u4e0d\u518d\u770b\u4ed6\u4e86\u3002 \u3000\u3000\u94c1\u59d1\u5374\u5728\u770b\u7740\u4ed6\u3002 \u3000\u3000\u97e9\u8d1e\u4e00\u56de\u5934\uff0c\u5c31\u89e6\u53ca\u4e86\u94c1\u59d1\u7684\u76ee\u5149\u3002 \u3000\u3000\u6bd4\u5200\u5149\u8fd8\u4eae\u7684\u76ee\u5149\u3002 \u3000\u3000\u97e9\u8d1e\u5f3a\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4ed6\u8001\u4eba\u5bb6\u60f3\u5fc5\u5df2\u8131\u9669\u4e86\u5427\uff1f\u201d \u3000\u3000\u94c1\u59d1\u70b9\u70b9\u5934\uff0c\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u4f60\u770b\u4ed6\u662f\u4e01\u9e9f\uff0c\u8fd8\u662f\u4e01\u7075\u7433\uff1f\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u6211\u770b\u4e0d\u51fa\u3002\u201d \u3000\u3000\u8fd9\u5012\u4e0d\u662f\u5047\u8bdd\uff0c\u4ed6\u7684\u786e\u770b\u4e0d\u51fa\uff0c\u4e5f\u5206\u4e0d\u51fa\u3002 \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u4f60\u5e94\u8be5\u770b\u5f97\u51fa\u7684\uff0c\u65e0\u8bba\u8c01\u90fd\u8be5\u770b\u5f97\u51fa\u5979\u662f\u4e2a\u5973\u4eba\u3002\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u4ed6\u73b0\u5728\u7684\u786e\u662f\u4e2a\u5973\u4eba\uff0c\u201d\u94c1\u59d1\u9053\uff1a\u201c\u4ee5\u524d\u96be\u9053\u4e0d\u662f\uff1f\u201d \u3000\u3000\u97e9\u8d1e\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u6211\u53ea\u4e0d\u8fc7\u6709\u70b9\u5947\u602a\uff0c\u4e01\u9e9f\u600e\u4e48\u4f1a\u5ffd\u7136\u4e0d\u89c1\u4e86\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u4f60\u5f88\u5173\u5fc3\u4ed6\uff1f\u201d \u3000\u3000\u97e9\u8d1e\u6478\u4e86\u6478\u6b6a\u659c\u7684\u9f3b\u5b50\uff0c\u9053\uff1a\u201c\u4ed6\u6253\u6b6a\u4e86\u6211\u7684\u9f3b\u5b50\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u4f60\u60f3\u62a5\u590d\uff1f\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u6ca1\u6709\u4eba\u80fd\u5728\u6253\u6b6a\u6211\u9f3b\u5b50\u4e4b\u540e\uff0c\u5c31\u4e00\u8d70\u4e86\u4e4b\u7684\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u4ed6\u80fd\u4e0d\u80fd\u6b7b\uff1f\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u4ed6\u4e5f\u4e0d\u50cf\u5f88\u5feb\u5c31\u4f1a\u6b7b\u7684\u4eba\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u53ef\u662f\u4ed6\u504f\u504f\u5df2\u6b7b\u4e86\u3002\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u4f60\u662f\u8bf4\uff0c\u4e01\u9e9f\u5df2\u6b7b\u4e86\uff1f\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u4f46\u4e01\u7075\u7433\u8fd8\u6d3b\u7740\u3002\u201d \u3000\u3000\u94c1\u59d1\u51dd\u89c6\u7740\u4ed6\uff0c\u8fc7\u4e86\u5f88\u4e45\uff0c\u624d\u5f90\u5f90\u9053\uff1a\u201c\u4f60\u5df2\u770b\u4e86\u51fa\u6765\uff1f\u201d \u3000\u3000\u97e9\u8d1e\u53c8\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u6211\u770b\u4e0d\u51fa\uff0c\u6211\u662f\u731c\u51fa\u6765\u7684\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u4f60\u8fd8\u731c\u51fa\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u53f6\u5f00\u867d\u7136\u662f\u4e2a\u5f88\u7cbe\u660e\u7684\u4eba\uff0c\u4f46\u662f\u5bf9\u81ea\u5df1\u7684\u8001\u60c5\u4eba\uff0c\u603b\u4e0d\u4f1a\u6709\u4ec0\u4e48\u6212\u5907\u7684\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u8bf4\u5f97\u597d\u3002\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u5047\u5982\u8fd9\u4e16\u4e0a\u53ea\u6709\u4e00\u4e2a\u4eba\u80fd\u6697\u7b97\u53f6\u5f00\uff0c\u518d\u5c06\u4e0a\u5b98\u5c0f\u4ed9\u4ece\u4ed6\u624b\u91cc\u62a2\u8fc7\u6765\uff0c\u90a3\u4e48\u8fd9 \u4e2a\u4eba\u4e00\u5b9a\u5c31\u662f\u4e01\u7075\u7433\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u8bf4\u5f97\u597d\u3002\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u4e01\u7075\u7433\u662f\u7edd\u4e0d\u4f1a\u6697\u7b97\u53f6\u5f00\u7684\uff0c\u6240\u4ee5\u2026\u2026\uff09\uff52\u94c1\u59d1\u9053\uff1a\u201c\u6240\u4ee5\u600e\u4e48 \u6837\uff1f\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u5047\u5982\u6709\u4e2a\u4eba\u957f\u5f97\u8ddf\u4e01\u7075\u7433\u5f88\u50cf\uff0c\u53ef\u4ee5\u6539\u626e\u6210\u4e01\u7075\u7433\uff0c\u90a3\u4e48\u8fd9\u4e2a\u4eba\u5c82\u975e\u5c31\u6b63\u662f \u5bf9\u4ed8\u53f6\u5f00\u7684\u6700\u597d\u6b66\u5668\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\u82e5\u662f\u7537\u7684\u5462\uff1f\u201d \u3000\u3000\u97e9\u8d1e\u5fae\u7b11\u9053\uff1a\u201c\u65e0\u8bba\u4ed6\u662f\u7537\u662f\u5973\u90fd\u6ca1\u5173\u7cfb\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u636e\u8bf4\u5357\u6d77\u5a18\u5b50\u4e0d\u4f46\u6613\u5bb9\u672f\u5999\u7edd\u5929\u4e0b\uff0c\u800c\u4e14\u8fd8\u6709\u79cd\u624b\u6cd5\u80fd\u63a7\u5236\u522b\u4eba\u54bd\u5589\u7684\u808c \u8089\uff0c\u4f7f\u4ed6\u7684\u58f0\u97f3\u4e5f\u6539\u53d8\u3002\u201d \u3000\u3000\u94c1\u59d1\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u77e5\u9053\u7684\u5012\u4e0d\u5c11\u3002\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\u82e5\u662f\u4e0d\u542c\u8bdd\uff0c\u6ca1\u5173\u7cfb\uff0c\u56e0\u4e3a\u5357\u6d77\u95e8\u8fd8\u6709\u79cd\u80fd\u63a7\u5236\u522b\u4eba\u5fc3\u7075\u7684\u6444\u9b42\u5927 \u6cd5\u3002\u201d \u3000\u3000\u94c1\u59d1\u53c8\u76ef\u7740\u4ed6\u770b\u4e86\u534a\u5929\uff0c\u624d\u5f90\u5f90\u9053\uff1a\u201c\u636e\u8bf4\u6c5f\u6e56\u4e2d\u4eba\u90fd\u53eb\u4f60\u9525\u5b50\u3002\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u4e0d\u6562\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u636e\u8bf4\u522b\u4eba\u65e0\u8bba\u6709\u591a\u786c\u7684\u58f3\uff0c\u4f60\u90fd\u80fd\u628a\u5b83\u9525\u5f00\u3002\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u8fd9\u53ea\u4e0d\u8fc7\u662f\u4f20\u8a00\u800c\u5df2\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u53ef\u662f\u8fd9\u4f20\u8bf4\u770b\u6765\u597d\u50cf\u5e76\u4e0d\u5047\u3002\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u6211\u7eb5\u7136\u8fd8\u6709\u70b9\u540d\u5802\uff0c\u4e5f\u662f\u536b\u516b\u592a\u7237\u4e00\u624b\u6559\u51fa\u6765\u7684\u3002\u201d \u3000\u3000\u94c1\u59d1\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u7528\u4e0d\u7740\u63d0\u9192\u6211\uff0c\u6211\u65e9\u5c31\u77e5\u9053\u4f60\u662f\u4ed6\u6700\u4eb2\u4fe1\u7684\u4eba\u3002\u201d \u3000\u3000\u97e9\u8d1e\u677e\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u53ea\u8981\u592b\u4eba\u660e\u767d\u8fd9\u4e00\u70b9\uff0c\u6211\u5c31\u653e\u5fc3\u4e86\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u6211\u65e2\u7136\u8ba9\u4f60\u5230\u8fd9\u91cc\u6765\uff0c\u5c31\u6ca1\u6709\u518d\u6253\u7b97\u7792\u7740\u4f60\u3002\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u591a\u8c22\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u4f60\u73b0\u5728\u662f\u4e0d\u662f\u5df2\u5b8c\u5168\u660e\u767d\u4e86\uff1f\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u8fd8\u6709\u51e0\u70b9\u4e0d\u660e\u767d\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u4f60\u8bf4\u3002\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u592b\u4eba\u83ab\u975e\u65e9\u5df2\u7b97\u51c6\u4e86\u4e01\u9e9f\u8981\u5230\u8fd9\u91cc\u6765\uff1f\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6240\u4ee5\u6211\u65e9\u5df2\u51c6\u5907\u597d\u4e86\uff0c\u5728\u8fd9\u91cc\u7b49\u7740\u4ed6\u3002\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u4f46\u592b\u4eba\u53c8\u600e\u77e5\u4ed6\u4e00\u5b9a\u4f1a\u6765\uff1f\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u6709\u4eba\u544a\u8bc9\u4e86\u6211\u3002\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\u662f\u9686\uff1f\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u662f\u4e2a\u670b\u53cb\u3002\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u662f\u4e01\u9e9f\u7684\u670b\u53cb\uff0c\u8fd8\u662f\u592b\u4eba\u7684\u670b\u53cb\uff1f\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u82e5\u4e0d\u662f\u4e01\u9e9f\u7684\u670b\u53cb\uff0c\u53c8\u600e\u4e48\u4f1a\u77e5\u9053\u4ed6\u7684\u884c\u8e2a\u3002\u201d \u3000\u3000\u97e9\u8d1e\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6709\u65f6\u5019\u670b\u53cb\u7684\u786e\u6bd4\u4ec7\u654c\u8fd8\u53ef\u6015\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u53c8\u95ee\u9053\uff1a\u201c\u592b\u4eba\u4ee5\u524d\u89c1\u8fc7\u4e01\u7075\u7433\u6ca1\u6709\uff1f\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u90a3\u4e48\u592b\u4eba\u53c8\u600e\u77e5\u4e01\u9e9f\u8ddf\u5979\u957f\u5f97\u5f88\u50cf\uff1f\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u636e\u8bf4\u4ed6\u4eec\u672c\u662f\u53cc\u751f\u5144\u59b9\u3002\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u54e6\uff01\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u4ed6\u4eec\u90a3\u8fb9\u7684\u4e60\u4fd7\uff0c\u53cc\u80de\u80ce\u751f\u4e0b\u6765\u82e5\u662f\u4e00\u7537\u4e00\u5973\uff0c\u5176\u4e2d\u4e00\u4e2a\u5c31\u4e00\u5b9a\u8981\u9001\u5230\u5916\u9762 \u53bb\u517b\u3002\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u8fd9\u79cd\u4e60\u4fd7\u6211\u4eec\u90a3\u8fb9\u4e5f\u6709\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u6240\u4ee5\u6c5f\u6e56\u4e2d\u6709\u5f88\u591a\u4eba\u4e0d\u77e5\u9053\uff0c\u4e01\u9e9f\u4e5f\u662f\u4ed6\u4eec\u4e01\u5bb6\u7684\u540e\u4ee3\u3002\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u592b\u4eba\u53c8\u600e\u4e48\u4f1a\u77e5\u9053\u7684\uff1f\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u662f\u4e2a\u670b\u53cb\u544a\u8bc9\u6211\u7684\u3002\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u8fd8\u662f\u521a\u624d\u8bf4\u7684\u90a3\u4e2a\u670b\u53cb\uff1f\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u97e9\u8d1e\u70b9\u4e86\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4ed6\u65e2\u7136\u662f\u4e01\u9e9f\u7684\u597d\u670b\u53cb\uff0c\u5f53\u7136\u77e5\u9053\u5f88\u591a\u522b\u4eba\u4e0d\u77e5\u9053\u7684\u4e8b\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u5f88\u60f3\u77e5\u9053\u8fd9\u4e2a\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u97e9\u8d1e\u6de1\u6de1\u5730\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u4e0d\u60f3\u8ddf\u4ed6\u4ea4\u670b\u53cb\u3002\u201d \u3000\u3000\u94c1\u59d1\u76ee\u4e2d\u4e5f\u6709\u4e86\u7b11\u610f\uff0c\u9053\uff1a\u201c\u4f60\u5b9e\u5728\u662f\u4e2a\u5f88\u7cbe\u660e\u7684\u4eba\u3002\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u800c\u4e14\u662f\u4e2a\u9525\u5b50\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u800c\u4e14\u662f\u6709\u773c\u5149\u7684\u9525\u5b50\u3002\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u9f3b\u5b50\u867d\u7136\u5df2\u88ab\u6253\u6b6a\u4e86\uff0c\u5e78\u597d\u4e5f\u8fd8\u5f88\u7075\u3002\u201d \u3000\u3000\u94c1\u59d1\u5fae\u7b11\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u82e5\u80af\u66ff\u6211\u5230\u4e00\u4e2a\u5730\u65b9\u53bb\u770b\u770b\uff0c\u90a3\u771f\u662f\u518d\u597d\u4e5f\u6ca1\u6709\u4e86\u3002\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u4f46\u8bf7\u5429\u5490\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u4f60\u80af\u53bb\uff1f\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u592b\u4eba\u5c31\u7b97\u8981\u6211\u53bb\u8d74\u6c64\u8e48\u706b\uff0c\u6211\u4e5f\u4e00\u6837\u4f1a\u53bb\u7684\u3002\u201d \u3000\u3000\u94c1\u59d1\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u96be\u602a\u536b\u516b\u592a\u7237\u4fe1\u4efb\u4f60\uff0c\u770b\u6765\u4f60\u679c\u7136\u662f\u4e2a\u591f\u4e49\u6c14\u7684\u4eba\u3002\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u80fd\u5f97\u5230\u592b\u4eba\u4e00\u53e5\u5938\u5956\uff0c\u97e9\u8d1e\u6b7b\u800c\u65e0\u6028\u3002\u201d \u3000\u3000\u94c1\u59d1\u5ae3\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u5e76\u4e0d\u60f3\u53eb\u4f60\u53bb\u6b7b\uff0c\u53ea\u4e0d\u8fc7\u8981\u4f60\u5230\u98d8\u9999\u522b\u9662\u53bb\u3002\u97e9\u8d1e\u9053\uff1a\u201c\u987a\u4fbf \u4e5f\u53bb\u770b\u770b\u90a3\u4f4d\u53ea\u6709\u4e03\u5c81\u5927\u7684\u5927\u7f8e\u4eba\u3002\u201d \u3000\u3000\u98d8\u9999\u522b\u9662\u98d8\u7740\u82b1\u9999\u3002 \u3000\u3000\u7a97\u6237\u91cc\u7684\u706f\u8fd8\u4eae\u7740\uff0c\u7a97\u4e0a\u6709\u4e24\u4e2a\u4eba\u7684\u5f71\u5b50\uff0c\u4e00\u4e2a\u7537\u4eba\uff0c\u4e00\u4e2a\u5973\u4eba\u3002 \u3000\u3000\u770b\u4e0d\u89c1\u73cd\u73e0\u5144\u5f1f\u3002 \u3000\u3000\u96ea\u5730\u4e0a\u5374\u6709\u67c4\u6298\u65ad\u4e86\u7684\u5251\uff0c\u5251\u67c4\u4e0a\u7684\u5251\u950b\u5728\u706f\u4e0b\u95ea\u7740\u5149\u3002 \u3000\u3000\u770b\u6765\u73cd\u73e0\u5144\u5f1f\u4eca\u5929\u7684\u8fd0\u6c14\u5b9e\u5728\u4e0d\u597d\u3002 \u3000\u3000\u5ffd\u7136\u95f4\uff0c\u7a97\u6237\u5f00\u4e86\u3002 \u3000\u3000\u4e00\u4e2a\u975e\u5e38\u7f8e\u7684\u5973\u4eba\uff0c\u624b\u91cc\u62b1\u7740\u4e2a\u6ce5\u5a03\u5a03\uff0c\u7ad9\u5728\u7a97\u53e3\u3002 \u3000\u3000\u5979\u7684\u8138\u767d\u91cc\u900f\u7ea2\uff0c\u773c\u775b\u53c8\u5706\u53c8\u4eae\uff0c\u7ea2\u7ea2\u7684\u5c0f\u5634\u534a\u5f20\u7740\uff0c\u663e\u5f97\u8bf4\u4e0d\u51fa\u7684\u5a07\u5a9a\uff0c\u8bf4\u4e0d\u51fa\u7684\u5929 \u771f\u3002 \u3000\u3000\u5979\u672c\u8eab\u770b\u6765\u5c31\u50cf\u4e2a\u6ce5\u5a03\u5a03\u3002 \u3000\u3000\u53ef\u662f\u5979\u7684\u8eab\u6750\u5374\u4e0d\u50cf\u662f\u4e2a\u6ce5\u5a03\u5a03\u3002 \u3000\u3000\u5979\u8eab\u4e0a\u6bcf\u4e00\u5206\u3001\u6bcf\u4e00\u5bf8\uff0c\u90fd\u4eff\u4f5b\u5728\u53d1\u5c04\u7740\u4e00\u79cd\u4ee4\u4eba\u4e0d\u53ef\u6297\u62d2\u7684\u70ed\u529b\u3002 \u3000\u3000\u5b69\u5b50\u7684\u8138\uff0c\u5987\u4eba\u7684\u8eab\u6750\uff0c\u8fd9\u867d\u7136\u5f88\u4e0d\u76f8\u79f0\uff0c\u5374\u5f62\u6210\u4e86\u4e00\u79cd\u5947\u5999\u7684\u7ec4\u5408\uff0c\u7ec4\u5408\u6210\u4e00\u79cd\u7f8e\u5999 \u7684\u8bf1\u60d1\uff0c\u4e00\u79cd\u8db3\u4ee5\u4ee4\u5927\u591a\u6570\u7537\u4eba\u72af\u7f6a\u7684\u8bf1\u60d1\u3002 \u3000\u3000\u8981\u4fdd\u62a4\u8fd9\u4e48\u6837\u7684\u4e00\u4e2a\u5973\u4eba\uff0c\u5b9e\u5728\u4e0d\u5bb9\u6613\u3002 \u3000\u3000\u5979\u8eab\u540e\u8fd8\u6709\u4e2a\u7537\u4eba\uff0c\u770b\u8d77\u6765\u5f88\u5e74\u8f7b\uff0c\u5f88\u82f1\u4fca\u3002 \u3000\u3000\u53f6\u5f00\u663e\u7136\u4e5f\u662f\u4e2a\u975e\u5e38\u597d\u770b\u7684\u7537\u4eba\uff0c\u53ea\u53ef\u60dc\u4ed6\u7ad9\u5f97\u6bd4\u8f83\u8fdc\u3002 \u3000\u3000\u97e9\u8d1e\u867d\u7136\u4e5f\u770b\u89c1\u4e86\u4ed6\uff0c\u5374\u770b\u4e0d\u6e05\u4ed6\u7684\u8138\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u624b\u91cc\u62b1\u7740\u6ce5\u5a03\u5a03\uff0c\u5634\u91cc\u8f7b\u8f7b\u5730\u54fc\u7740\u9996\u513f\u6b4c\uff0c\u58f0\u97f3\u4e5f\u751c\u5f97\u5f88\u3002 \u3000\u3000\u53ea\u542c\u53f6\u5f00\u9053\uff1a\u201c\u5916\u9762\u98ce\u5f88\u51b7\uff0c\u4f60\u4e3a\u4ec0\u4e48\u8fd8\u4e0d\u5173\u4e0a\u7a97\u5b50\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u7684\u5634\u5658\u5f97\u66f4\u9ad8\uff0c\u9053\uff1a\u201c\u5b9d\u5b9d\u592a\u95f7\u4e86\uff0c\u5b9e\u5728\u60f3\u900f\u900f\u98ce\u3002\u201d \u3000\u3000\u53f6\u5f00\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5b9d\u5b9d\u5df2\u7ecf\u7761\u4e86\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u53ef\u4ed6\u504f\u504f\u4e0d\u80af\u7761\uff0c\u5b9d\u5b9d\u7cbe\u795e\u8fd8\u597d\u5f97\u5f88\u3002\u201d \u3000\u3000\u53f6\u5f00\u82e6\u7b11\u9053\uff1a\u201c\u8fd9\u4e48\u665a\u4e86\u8fd8\u4e0d\u7761\uff0c\u5b9d\u5b9d\u662f\u4e2a\u574f\u5b69\u5b50\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u7acb\u523b\u53eb\u8d77\u6765\uff1a\u201c\u5b9d\u5b9d\u4e0d\u662f\u574f\u5b69\u5b50\uff0c\u5b9d\u5b9d\u4e56\u5f97\u5f88\u3002\u201d \u3000\u3000\u5979\u4f38\u51fa\u4e00\u53ea\u53c8\u767d\u53c8\u5ae9\u7684\u624b\uff0c\u8f7b\u8f7b\u62cd\u7740\u6000\u91cc\u7684\u6ce5\u5a03\u5a03\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u5b9d\u5b9d\u4e0d\u8981\u54ed\uff0c\u4ed6\u624d\u662f\u4e2a \u574f\u4eba\uff0c\u5b9d\u5b9d\u4e0d\u54ed\uff0c\u5988\u5988\u5582\u5976\u7ed9\u4f60\u5403\u3002\u201d \u3000\u3000\u5979\u7adf\u771f\u7684\u8981\u89e3\u5f00\u8863\u895f\uff0c\u5582\u5976\u7ed9\u8fd9\u6ce5\u5a03\u5a03\u5403\u4e86\u3002 \u3000\u3000\u5979\u7684\u80f8\u819b\u6210\u719f\u800c\u9ad8\u8038\u3002 \u3000\u3000\u97e9\u8d1e\u8fdc\u8fdc\u5730\u770b\u7740\uff0c\u5fc3\u5df2\u8df3\u4e86\u8d77\u6765\uff0c\u8df3\u5f97\u597d\u5feb\u3002 \u3000\u3000\u8c01\u77e5\u5c31\u5728\u8fd9\u65f6\uff0c\u53f6\u5f00\u5374\u5ffd\u7136\u8d76\u8fc7\u53bb\uff0c\u201c\u7830\u201d\u5730\u5173\u8d77\u4e86\u7a97\u5b50\u3002 \u3000\u3000\u53ea\u542c\u4e0a\u5b98\u5c0f\u4ed9\u5728\u7a97\u5b50\u91cc\u5403\u5403\u5730\u7b11\u9053\uff1a\u201c\u4f60\u62c9\u6211\u5e72\u4ec0\u4e48\uff1f\u4f60\u662f\u4e0d\u662f\u4e5f\u8981\u5403\u5976\uff1f\u54fc\u2026\u2026\u201d \u3000\u3000\u4f5b\u5802\u91cc\u7684\u9999\u5df2\u71c3\u5c3d\u4e86\u3002 \u3000\u3000\u536b\u516b\u592a\u7237\u95ed\u7740\u773c\u8eba\u5728\u8f6f\u69bb\u4e0a\uff0c\u8138\u8272\u5f88\u7ea2\u6da6\uff0c\u4f3c\u5df2\u7761\u7740\u3002 \u3000\u3000\u94c1\u59d1\u542c\u97e9\u8d1e\u8bf4\u5b8c\u4e86\uff0c\u624d\u8bf4\u9053\uff1a\u201c\u7a97\u5b50\u5173\u4e0a\uff0c\u4f60\u5c31\u56de\u6765\u4e86\uff1f\u201d \u3000\u3000\u97e9\u8d1e\u82e6\u7b11\u9053\uff1a\u201c\u6211\u603b\u4e0d\u80fd\u4e5f\u8fdb\u53bb\u62a2\u7740\u5403\u5976\u3002\u201d \u3000\u3000\u94c1\u59d1\u773c\u4e2d\u53c8\u9732\u51fa\u7b11\u610f\uff0c\u9053\uff1a\u201c\u770b\u8d77\u6765\u4f60\u597d\u50cf\u5f88\u7fa1\u6155\u53f6\u5f00\u3002\u201d \u3000\u3000\u97e9\u8d1e\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u4e5f\u5f88\u540c\u60c5\u4ed6\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u4f60\u540c\u60c5\u4ed6\uff1f\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u6574\u5929\u966a\u7740\u8fd9\u4e48\u6837\u4e00\u4e2a\u5973\u4eba\uff0c\u5b9e\u5728\u4e0d\u662f\u4ef6\u597d\u53d7\u7684\u4e8b\u3002\u201d \u3000\u3000\u5fc3\u59d1\u5ffd\u7136\u9053\uff1a\u201c\u5979\u662f\u4e0d\u662f\u5f88\u7f8e\uff1f\u201d \u3000\u3000\u97e9\u8d1e\u5077\u5077\u779f\u4e86\u5979\u4e00\u773c\uff0c\u9053\uff1a\u201c\u8fd8\u7b97\u8fc7\u5f97\u53bb\u3002\u201d \u3000\u3000\u8fd9\u4e0d\u662f\u8001\u5b9e\u8bdd\uff0c\u4f46\u5374\u662f\u806a\u660e\u8bdd\u3002 \u3000\u3000\u6ca1\u6709\u4efb\u4f55\u5973\u4eba\uff0c\u613f\u610f\u542c\u7740\u7537\u4eba\u5728\u81ea\u5df1\u9762\u524d\u5938\u5956\u53e6\u4e00\u4e2a\u5973\u4eba\u7684\u3002 \u3000\u3000\u5fc3\u59d1\u51b7\u51b7\u9053\uff1a\u201c\u542c\u8bf4\u767d\u75f4\u90fd\u957f\u5f97\u5f88\u7f8e\u7684\u3002\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u5fc3\u59d1\u5ffd\u53c8\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u5e78\u597d\u7f8e\u4eba\u5e76\u975e\u4e00\u5b9a\u90fd\u662f\u767d\u75f4\u3002\u201d \u3000\u3000\u5979\u81ea\u5df1\u5f53\u7136\u4e5f\u662f\u4e2a\u7f8e\u4eba\uff0c\u975e\u5e38\u7f8e\u3002 \u3000\u3000\u94c1\u59d1\u5ffd\u53c8\u95ee\u9053\uff1a\u201c\u5728\u98d8\u9999\u522b\u9662\u91cc\uff0c\u662f\u4e0d\u662f\u53ea\u6709\u4ed6\u4eec\u4e24\u4e2a\u4eba\uff1f\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u6211\u524d\u524d\u540e\u540e\u90fd\u770b\u8fc7\u4e86\uff0c\u597d\u50cf\u6ca1\u6709\u522b\u7684\u4eba\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u662f\u597d\u50cf\u6ca1\u6709\uff0c\u8fd8\u662f\u7684\u786e\u6ca1\u6709\uff1f\u201d \u3000\u3000\u97e9\u8d1e\u60f3\u4e86\u60f3\u9053\uff1a\u201c\u7684\u786e\u6ca1\u6709\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u4e5f\u8bb8\u6709\u522b\u7684\u4eba\u5df2\u7761\u4e86\u5462\uff1f\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u522b\u7684\u5c4b\u5b50\u91cc\u90fd\u6ca1\u6709\u8d77\u706b\uff0c\u8fd9\u4e48\u51b7\u7684\u5929\uff0c\u8c01\u4e5f\u4e0d\u4f1a\u5728\u4e00\u4e2a\u6ca1\u6709\u8d77\u706b\u7684\u5c4b\u5b50\u7761\u89c9 \u7684\u3002\u201d \u3000\u3000\u94c1\u59d1\u7ec8\u4e8e\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u770b\u6765\u4f60\u4e0d\u4f46\u806a\u660e\uff0c\u800c\u4e14\u5f88\u7ec6\u5fc3\u3002\u201d \u3000\u3000\u5fc3\u59d1\u5ffd\u7136\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u9f3b\u5b50\u6b6a\u4e86\u4e00\u70b9\u3002\u201d \u3000\u3000\u94c1\u59d1\u77aa\u4e86\u5979\u4e00\u773c\uff0c\u9053\uff1a\u201c\u4f60\u53c8\u4e0d\u60f3\u5ac1\u7ed9\u4ed6\uff0c\u4f60\u7ba1\u4eba\u5bb6\u9f3b\u5b50\u6b6a\u4e0d\u6b6a\u3002\u201d \u3000\u3000\u5fc3\u59d1\u9053\uff1a\u201c\u9f3b\u5b50\u6b6a\u7684\u7537\u4eba\uff0c\u4e5f\u5e76\u4e0d\u4e00\u5b9a\u5c31\u662f\u5ac1\u4e0d\u5f97\u7684\u3002\u201d \u3000\u3000\u94c1\u59d1\u53c8\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u5c0f\u9b3c\uff0c\u80e1\u8bf4\u516b\u9053\u7684\uff0c\u4e5f\u4e0d\u6015\u4eba\u5bb6\u542c\u4e86\u7b11\u8bdd\u3002\u201d \u3000\u3000\u97e9\u8d1e\u5ffd\u7136\u53d1\u89c9\u81ea\u5df1\u7684\u5fc3\u53c8\u5728\u8df3\uff0c\u8df3\u5f97\u5f88\u5feb\u3002 \u3000\u3000\u8fd9\u79cd\u53ef\u80fd\u4ed6\u5e76\u4e0d\u662f\u6ca1\u6709\u60f3\u5230\u8fc7\uff0c\u53ea\u662f\u4e0d\u6562\u60f3\u800c\u5df2\u3002 \u3000\u3000\u5979\u4eec\u662f\u4e0d\u662f\u53c8\u60f3\u51fa\u4e2a\u96be\u9898\u8ba9\u4ed6\u505a\u4e86\u3002 \u3000\u3000\u94c1\u59d1\u679c\u7136\u53c8\u5728\u95ee\u4ed6\uff1a\u201c\u4f60\u6b66\u529f\u662f\u4e0d\u662f\u8ddf\u536b\u516b\u592a\u7237\u5b66\u7684\uff1f\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u4e0d\u662f\u3002\u201d \u3000\u3000\u4ed6\u5e76\u4e0d\u662f\u536b\u5929\u9e4f\u7684\u5f1f\u5b50\uff0c\u4e5f\u4e0d\u662f\u201c\u5341\u4e09\u592a\u4fdd\u201d\u4e2d\u7684\u4e00\u4e2a\u3002 \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u4f60\u7528\u7684\u5175\u5203\u5c31\u662f\u9525\u5b50\uff1f\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u6211\u8fd8\u6ca1\u542c\u8bf4\u8fc7\u6c5f\u6e56\u4e2d\u6709\u4eba\u7528\u9525\u5b50\u505a\u5175\u5203\u7684\u3002\u201d \u3000\u3000\u97e9\u8d1e\u7b11\u9053\uff1a\u201c\u90a3\u672c\u662f\u6211\u968f\u4fbf\u627e\u6765\u7528\u7684\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u9525\u5b50\u4e5f\u6709\u72ec\u95e8\u62db\u5f0f\uff1f\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u6ca1\u6709\uff0c\u4f46\u65e0\u8bba\u54ea\u79cd\u5175\u5203\u7684\u62db\u5f0f\uff0c\u90fd\u53ef\u4ee5\u7528\u9525\u5b50\u4f7f\u51fa\u6765\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u542c\u4f60\u8fd9\u4e48\u8bf4\uff0c\u4f60\u4f1a\u7684\u6b66\u529f\u62db\u5f0f\u4e00\u5b9a\u5f88\u4e0d\u5c11\u3002\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u6742\u800c\u4e0d\u7cbe\u3002\u201d \u3000\u3000\u5fc3\u59d1\u53c8\u201c\u5657\u54e7\u201d\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u4f60\u8fd9\u4e2a\u4eba\u5c45\u7136\u4e5f\u4f1a\u5047\u5ba2\u6c14\u3002\u201d \u3000\u3000\u97e9\u8d1e\u7684\u5fc3\u8df3\u5f97\u53c8\u5feb\u4e86\u3002 \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u4f60\u8ddf\u7740\u536b\u516b\u592a\u7237\u6ca1\u6709\u51e0\u5e74\uff0c\u5c31\u5df2\u6210\u4e86\u4ed6\u95e8\u4e0b\u6700\u5f97\u529b\u7684\u4eba\uff0c\u6b66\u529f\u60f3\u5fc5\u662f\u4e0d \u9519\u3002\u201d \u3000\u3000\u97e9\u8d1e\u53ea\u6709\u627f\u8ba4\uff1a\u201c\u8fd8\u7b97\u8fc7\u5f97\u53bb\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u6240\u4ee5\u6211\u8fd8\u60f3\u8bf7\u4f60\u505a\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u4f46\u8bf7\u5429\u5490\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u8d8a\u5feb\u8d8a\u597d\uff0c\u4eca\u5929\u665a\u4e0a\u53c8\u6b63\u597d\u662f\u4e0b\u624b\u7684\u597d\u673a\u4f1a\u3002\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u6211\u60f3\u73b0\u5728\u5c31\u8981\u4e01\u7075\u7433\u53bb\u52a8\u624b\u3002\u201d \u3000\u3000\u97e9\u8d1e\u6c89\u601d\u7740\uff0c\u9053\uff1a\u201c\u5374\u4e0d\u77e5\u53f6\u5f00\u4f1a\u4e0d\u4f1a\u8ba4\u51fa\u5979\u6765\uff1f\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u7edd\u4e0d\u4f1a\u7684\uff0c\u5c31\u7b97\u5979\u8fd8\u6709\u70b9\u7834\u7efd\uff0c\u5728\u706f\u5149\u4e0b\u4e5f\u770b\u4e0d\u51fa\u6765\u3002\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u4f46\u4ed6\u4eec\u672c\u662f\u8001\u60c5\u4eba\uff0c\u82e5\u662f\u591a\u770b\u51e0\u773c\uff0c\u4e5f\u8bb8\u5c31\u2026\u2026\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u6211\u4eec\u600e\u4e48\u4f1a\u7ed9\u673a\u4f1a\u8ba9\u4ed6\u770b\u6e05\u695a\uff0c\u53ea\u8981\u4ed6\u4e00\u8ba9\u4e01\u7075\u7433\u8fd1\u4ed6\u7684\u8eab\uff0c\u5927\u529f\u4e5f\u5c31\u544a \u6210\u3002\u201d \u3000\u3000\u5fc3\u59d1\u7b11\u9053\uff1a\u201c\u4ed6\u51fa\u624b\u672c\u6765\u5c31\u5f88\u5feb\u7684\uff0c\u5426\u5219\u53c8\u600e\u80fd\u4e00\u62f3\u6253\u6b6a\u4f60\u7684\u9f3b\u5b50\u3002\u201d \u3000\u3000\u97e9\u8d1e\u53ea\u6709\u82e6\u7b11\uff0c\u5fc3\u91cc\u5374\u662f\u751c\u7684\u3002 \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u53ea\u4e0d\u8fc7\uff0c\u6211\u4eec\u4e5f\u4e0d\u80fd\u4e0d\u591a\u52a0\u5c0f\u5fc3\uff0c\u4ee5\u9632\u4e07\u4e00\uff0c\u6240\u4ee5\u6211\u60f3\u8981\u4f60\u966a\u7740\u4ed6\u53bb\u3002\u201d \u3000\u3000\u97e9\u8d1e\u6014\u4e86\u6014\uff0c\u9053\uff1a\u201c\u6211\u600e\u4e48\u80fd\u966a\u4ed6\u53bb\uff1f\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u4e0d\u80fd\uff1f\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u6211\u2026\u2026\u7b97\u4ec0\u4e48\u4eba\u5462\uff1f\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u7b97\u8fd9\u91cc\u7684\u7ba1\u4e8b\uff0c\u5e26\u4ed6\u53bb\u627e\u53f6\u5f00\uff0c\u56e0\u4e3a\u8fd9\u5730\u65b9\u4e01\u7075\u7433\u6ca1\u6765\u8fc7\uff0c\u5f53\u7136\u4e0d\u8ba4\u5f97 \u8def\u3002\u201d \u3000\u3000\u97e9\u8d1e\u5fcd\u4e0d\u4f4f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u592b\u4eba\u60f3\u5f97\u771f\u5468\u5230\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u82e5\u662f\u60f3\u4e0d\u5468\u5230\uff0c\u53c8\u600e\u4e48\u6562\u51fa\u624b\u52a8\u53f6\u5f00\uff1f\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u73b0\u5728\u6211\u53ea\u62c5\u5fc3\u4e00\u4ef6\u4e8b\u4e86\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u62c5\u5fc3\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u62c5\u5fc3\u53f6\u5f00\u7684\u98de\u5200\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u4f60\u6015\uff1f\u201d \u3000\u3000\u97e9\u8d1e\u82e6\u7b11\u9053\uff1a\u201c\u6211\u53ea\u6015\u8fd9\u4f4d\u4e01\u7075\u7433\u59d1\u5a18\u4e0d\u80fd\u4e00\u51fa\u624b\u5c31\u5236\u4ed6\u7684\u6b7b\u547d\uff0c\u53ea\u6240\u4ed6\u8fd8\u6709\u673a\u4f1a\u51fa \u624b\u3002\u201d \u3000\u3000\u94c1\u59d1\u51b7\u51b7\u9053\uff1a\u201c\u83ab\u5fd8\u8bb0\u6211\u4e5f\u6709\u5200\uff0c\u5728\u6211\u7684\u5200\u4e0b\uff0c\u6ca1\u6709\u4eba\u8fd8\u80fd\u6d3b\u5f97\u4e86\u3002\u201d \u3000\u3000\u5979\u5ffd\u7136\u6325\u624b\uff0c\u4e00\u67c4\u5200\u201c\u53ee\u201d\u7684\u843d\u5728\u4e01\u9e9f\u7684\u9762\u524d\u3002 \u3000\u3000\u4e00\u67c4\u78a7\u7cbc\u7cbc\u7684\u5200\u3002 \u3000\u3000\u4e01\u9e9f\u7acb\u523b\u7741\u5f00\u4e86\u773c\u775b\uff0c\u76f4\u52fe\u52fe\u5730\u770b\u7740\u8fd9\u67c4\u5200\u3002 \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u6361\u8d77\u8fd9\u67c4\u5200\u6765\uff0c\u85cf\u5728\u8863\u8896\u91cc\u3002\u201d \u3000\u3000\u4e01\u9e9f\u679c\u7136\u5c31\u6361\u8d77\u5200\uff0c\u85cf\u5165\u8896\u5957\u3002 \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u73b0\u5728\u4f60\u62ac\u8d77\u5934\u770b\u770b\u8fd9\u4e2a\u4eba\u3002\u201d \u3000\u3000\u5979\u6307\u7740\u97e9\u8d1e\u3002 \u3000\u3000\u4e01\u9e9f\u5c31\u62ac\u8d77\u5934\uff0c\u773c\u775b\u76f4\u52fe\u52fe\u5730\u770b\u7740\u97e9\u8d1e\u3002 \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u4f60\u8ba4\u5f97\u8fd9\u4e2a\u4eba\u4e48\uff1f\u201d \u3000\u3000\u4e01\u9e9f\u70b9\u70b9\u5934\u3002 \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u53f6\u5f00\u662f\u4e2a\u65e0\u60c5\u65e0\u4e49\u7684\u4eba\uff0c\u629b\u4e0b\u4e86\u4f60\uff0c\u53bb\u627e\u522b\u7684\u5973\u4eba\u4e86\uff0c\u6240\u4ee5\u4f60\u770b\u89c1\u4ed6\uff0c\u5c31\u8981 \u7528\u8fd9\u67c4\u5200\u6740\u4e86\u4ed6\uff0c\u7136\u540e\u5e26\u90a3\u4e2a\u5973\u4eba\u56de\u6765\u3002\u201d \u3000\u3000\u4e01\u9e9f\u9053\uff1a\u201c\u6211\u4e00\u5b9a\u8981\u6740\u4e86\u4ed6\uff0c\u7136\u540e\u5e26\u90a3\u4e2a\u5973\u4eba\u56de\u6765\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u4f60\u73b0\u5728\u5c31\u53bb\u5427\u3002\u201d \u3000\u3000\u4e01\u9e9f\u9053\uff1a\u201c\u6211\u73b0\u5728\u5c31\u53bb\u3002\u201d \u3000\u3000\u4ed6\u8138\u4e0a\u5e26\u7740\u79cd\u5f88\u5947\u602a\u7684\u8868\u60c5\uff0c\u4eff\u4f5b\u832b\u7136\u65e0\u77e5\uff0c\u53c8\u4eff\u4f5b\u5f88\u75db\u82e6\u3002 \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u8fd8\u4e0d\u53bb\uff1f\u201d \u3000\u3000\u4e01\u9e9f\u9053\uff1a\u201c\u6211\u53bb\u3002\u201d \u3000\u3000\u4ed6\u5634\u91cc\u867d\u7136\u8bf4\u53bb\uff0c\u5374\u8fd8\u662f\u5750\u5728\u90a3\u91cc\uff0c\u52a8\u4e5f\u4e0d\u529f\u3002 \u3000\u3000\u5fc3\u59d1\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u770b\u6765\u4ed6\u5bf9\u53f6\u5f00\u771f\u4e0d\u9519\uff0c\u5230\u4e86\u8fd9\u79cd\u65f6\u5019\uff0c\u5c45\u7136\u8fd8\u4e0d\u5fcd\u5fc3\u53bb\u6740\u4ed6\u3002\u201d \u3000\u3000\u94c1\u59d1\u51b7\u7b11\u9053\uff1a\u201c\u4ed6\u4f1a\u53bb\u7684\u3002\u201d \u3000\u3000\u5979\u5f53\u7136\u77e5\u9053\u4e00\u4e2a\u4eba\u7684\u5fc3\u7075\u7eb5\u7136\u5df2\u53d7\u4e86\u63a7\u5236\uff0c\u4f46\u4f60\u82e5\u8981\u4ed6\u53bb\u505a\u4e00\u4ef6\u4ed6\u6700\u4e0d\u613f\u610f\u7684\u4e8b\uff0c\u4ed6\u7684 \u7406\u667a\u8fd8\u662f\u4f1a\u4f5c\u6700\u540e\u4e00\u756a\u6323\u624e\u7684\u3002 \u3000\u3000\u8fd9\u672c\u662f\u5f88\u6b63\u5e38\u7684\u73b0\u8c61\uff0c\u6240\u4ee5\u5979\u65e9\u5df2\u6709\u4e86\u51c6\u5907\u3002 \u3000\u3000\u5979\u5ffd\u7136\u62cd\u4e86\u62cd\u638c\u3002 \u3000\u3000\u65c1\u8fb9\u7684\u4e00\u6247\u95e8\u7adf\u7acb\u523b\u65e0\u98ce\u81ea\u5f00\uff0c\u4e00\u4e2a\u4eba\u6162\u6162\u5730\u8d70\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u4e00\u4e2a\u4e09\u5341\u591a\u5c81\u7684\u4e2d\u5e74\u4eba\uff0c\u8eab\u4e0a\u7a7f\u7740\u4ef6\u72d0\u76ae\u888d\u5b50\uff0c\u5916\u9762\u8fd8\u5957\u7740\u4ef6\u84dd\u5e03\u7f69\u886b\uff0c\u770b\u6765\u5c31\u50cf\u662f\u4e2a \u89c4\u89c4\u77e9\u77e9\u7684\u751f\u610f\u4eba\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u8d6b\u7136\u7adf\u662f\u98de\u72d0\u6768\u5929\uff01 \u3000\u3000\u4e01\u9e9f\u7684\u8138\u5ffd\u7136\u95f4\u5df2\u56e0\u6050\u60e7\u800c\u626d\u66f2\uff0c\u8eab\u5b50\u4e5f\u5f00\u59cb\u4e0d\u505c\u5730\u53d1\u6296\u3002 \u3000\u3000\u6768\u5929\u51b7\u51b7\u5730\u770b\u7740\u4ed6\uff0c\u8138\u4e0a\u4e00\u70b9\u8868\u60c5\u4e5f\u6ca1\u6709\uff0c\u80f8\u53e3\u4e0a\u7adf\u8d6b\u7136\u63d2\u7740\u628a\u5200\uff0c\u8863\u670d\u4e0a\u8fd8\u5e26\u7740\u8840 \u8ff9\u3002 \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u4f60\u8ba4\u5f97\u8fd9\u4e2a\u4eba\u4e48\uff1f\u201d \u3000\u3000\u4e01\u9e9f\u70b9\u70b9\u5934\uff0c\u8138\u4e0a\u7684\u8868\u60c5\u66f4\u6050\u60e7\u3002 \u3000\u3000\u4ed6\u5f53\u7136\u8ba4\u5f97\u8fd9\u4e2a\u4eba\uff0c\u4ed6\u7684\u8bb0\u5fc6\u5e76\u6ca1\u6709\u5b8c\u5168\u4e27\u5931\u3002 \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u4ed6\u73b0\u5728\u5df2\u7ecf\u662f\u4e2a\u6b7b\u4eba\u4e86\uff0c\u4f60\u8fd8\u8bb0\u4e0d\u8bb0\u5f97\u662f\u8c01\u6740\u4e86\u4ed6\u7684\uff1f\u201d \u3000\u3000\u4e01\u9e9f\u9053\uff1a\u201c\u662f\u2026\u2026\u662f\u6211\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u4ed6\u672c\u6765\u662f\u4f60\u7684\u597d\u670b\u53cb\uff0c\u4f46\u4f60\u5374\u6740\u4e86\u4ed6\u3002\u201d \u3000\u3000\u4e01\u9e9f\u9053\uff1a\u201c\u662f\u4f60\u8981\u6211\u53bb\u6740\u7684\u3002\u201d \u3000\u3000\uff0c\u94c1\u59d1\u9053\uff1a\u201c\u73b0\u5728\u6211\u8981\u4f60\u53bb\u6740\u53f6\u5f00\uff0c\u4f60\u53bb\u4e0d\u53bb\uff1f\u201d \u3000\u3000\u4e01\u9e9f\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u53bb\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u4f60\u73b0\u5728\u5c31\u53bb\u3002\u201d \u3000\u3000\u4ed6\u679c\u7136\u7ad9\u4e86\u8d77\u6765\uff0c\u6162\u6162\u5730\u8d70\u4e86\u51fa\u53bb\uff0c\u4ed6\u7684\u8eab\u5b50\u8fd8\u5728\u53d1\u6296\u3002 \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u5728\u95e8\u5916\u7b49\u7740\uff0c\u7b49\u97e9\u8d1e\u5e26\u4f60\u53bb\u3002\u201d \u3000\u3000\u4e01\u9e9f\u9053\uff1a\u201c\u6211\u5728\u95e8\u5916\u7b49\u7740\uff0c\u7b49\u97e9\u8d1e\u5e26\u6211\u53bb\uff0c\u6211\u4e00\u5b9a\u8981\u6740\u4e86\u53f6\u5f00\u3002\u201d \u3000\u3000\u7b49\u4ed6\u8d70\u51fa\u95e8\uff0c\u94c1\u59d1\u624d\u5bf9\u97e9\u8d1e\u7b11\u7b11\uff0c\u9053\uff1a\u201c\u73b0\u5728\u4f60\u603b\u8be5\u77e5\u9053\uff0c\u4ed6\u90a3\u597d\u670b\u53cb\u662f\u8c01\u4e86\u5427\uff1f\u201d \u3000\u3000\u97e9\u8d1e\u53ea\u6709\u770b\u7740\u6768\u5929\u82e6\u7b11\u3002 \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u4f60\u4e0d\u8ba4\u5f97\u4ed6\uff1f\u201d \u3000\u3000\u6768\u5929\u5ffd\u7136\u51b7\u51b7\u9053\uff1a\u201c\u4ed6\u4e0d\u8ba4\u5f97\u6211\uff0c\u4ed6\u4e0d\u60f3\u4ea4\u6211\u8fd9\u4e2a\u670b\u53cb\u3002\u201d \u3000\u3000\u4ed6\u4e00\u53cd\u624b\uff0c\u62d4\u4e0b\u4e86\u63d2\u5728\u80f8\u53e3\u7684\u5200\uff0c\u5374\u53ea\u6709\u5200\u67c4\u3002 \u3000\u3000\u53ea\u542c\u201c\u5657\u201d\u7684\u4e00\u58f0\uff0c\u4e00\u622a\u5200\u950b\u81ea\u5200\u67c4\u91cc\u5f39\u4e86\u51fa\u6765\uff0c\u7528\u6307\u5c16\u4e00\u6309\uff0c\u5200\u950b\u5c31\u53c8\u9000\u5165\u5200\u67c4\u3002 \u3000\u3000\u539f\u6765\u7adf\u662f\u628a\u6740\u4e0d\u6b7b\u4eba\u7684\u5200\u3002 \u3000\u3000\u97e9\u8d1e\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4e16\u4e0a\u65e2\u7136\u6709\u8fd9\u79cd\u5200\uff0c\u5c31\u96be\u602a\u4f1a\u6709\u4f60\u8fd9\u79cd\u670b\u53cb\u4e86\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u53ef\u662f\u4f60\u6700\u597d\u8bb0\u4f4f\uff0c\u8fd9\u79cd\u670b\u53cb\uff0c\u5e76\u4e0d\u662f\u6ca1\u6709\u7528\u5904\u7684\u3002\u201d \u3000\u3000\u7a7f\u8fc7\u51e0\u767e\u682a\u6885\u82b1\uff0c\u53c8\u6765\u5230\u98d8\u9999\u522b\u9662\u3002 \u3000\u3000\u4e01\u9e9f\u4e00\u76f4\u9759\u9759\u5730\u8ddf\u5728\u97e9\u8d1e\u8eab\u540e\uff0c\u97e9\u8d1e\u8d70\u4e00\u6b65\uff0c\u4ed6\u5c31\u8d70\u4e00\u6b65\u3002 \u3000\u3000\u97e9\u8d1e\u5ffd\u7136\u505c\u4e0b\u6765\u3002 \u3000\u3000\u4e01\u9e9f\u4e5f\u505c\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u97e9\u8d1e\u56de\u8fc7\u5934\uff0c\u76ef\u7740\u4ed6\u9053\uff1a\u201c\u4f60\u7684\u670b\u53cb\u897f\u95e8\u5341\u4e09\u5df2\u6b7b\u4e86\u3002\u201d \u3000\u3000\u4e01\u9e9f\u9053\uff1a\u201c\u897f\u95e8\u5341\u4e09\u5df2\u6b7b\u4e86\uff1f\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u4f60\u60f3\u4e0d\u60f3\u77e5\u9053\u4ed6\u662f\u6b7b\u5728\u4ec0\u4e48\u4eba\u624b\u4e0a\u7684\uff1f\u201d \u3000\u3000\u4e01\u9e9f\u9053\uff1a\u201c\u6211\u4e0d\u60f3\u77e5\u9053\u4ed6\u662f\u6b7b\u5728\u4ec0\u4e48\u4eba\u624b\u4e0a\u7684\u3002\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u4f46\u4f60\u82e5\u771f\u662f\u4ed6\u7684\u597d\u670b\u53cb\uff0c\u5c31\u5e94\u8be5\u66ff\u4ed6\u62a5\u4ec7\u3002\u201d \u3000\u3000\u4e01\u9e9f\u9053\uff1a\u201c\u6211\u82e5\u771f\u662f\u4ed6\u7684\u597d\u670b\u53cb\uff0c\u5c31\u5e94\u8be5\u66ff\u4ed6\u62a5\u4ec7\u3002\u201d \u3000\u3000\u4f60\u8bf4\u4e00\u53e5\u8bdd\uff0c\u4ed6\u5c31\u8ddf\u4f60\u8bf4\u4e00\u904d\uff0c\u4f46\u4f60\u6c38\u8fdc\u4e0d\u77e5\u9053\u4ed6\u662f\u4e0d\u662f\u5df2\u771f\u7684\u4e86\u89e3\u4f60\u7684\u610f\u601d\u3002 \u3000\u3000\u97e9\u8d1e\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u50cf\u4f60\u8fd9\u4e48\u806a\u660e\u7684\u4eba\uff0c\u5c45\u7136\u4e5f\u53d7\u4eba\u63a7\u5236\uff0c\u6211\u7b80\u76f4\u4e0d\u76f8\u4fe1\u3002\u201d \u3000\u3000\u4ed6\u7528\u773c\u89d2\u779f\u7740\u4e01\u9e9f\uff0c\u4e01\u9e9f\u8138\u4e0a\u5374\u8fde\u4e00\u70b9\u8868\u60c5\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u97e9\u8d1e\u53c8\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u524d\u9762\u6709\u706f\u5149\u7684\u5730\u65b9\uff0c\u5c31\u662f\u98d8\u9999\u522b\u9662\u3002\u201d \u3000\u3000\u4e01\u9e9f\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u4f60\u771f\u7684\u80fd\u5fcd\u5fc3\u4e0b\u624b\uff1f\u201d \u3000\u3000\u4e01\u9e9f\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u5176\u5b9e\u4f60\u672c\u6765\u4e0d\u5fc5\u771f\u6740\u4e86\u4ed6\u7684\u3002\u201d \u3000\u3000\u4e01\u9e9f\u9053\uff1a\u201c\u6211\u4e0d\u5fc5\uff1f\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u4f60\u53ef\u4ee5\u62b1\u4f4f\u4ed6\uff0c\u70b9\u4f4f\u4ed6\u7684\u7a74\u9053\uff0c\u8ba9\u4ed6\u52a8\u4e0d\u4e86\u3002\u201d \u3000\u3000\u97e9\u8d1e\u53c8\u9053\uff1a\u201c\u90a3\u65f6\u6211\u5c31\u4f1a\u628a\u90a3\u4e2a\u574f\u5973\u4eba\u5e26\u8d70\uff0c\u5e26\u5f97\u8fdc\u8fdc\u7684\uff0c\u8ba9\u5979\u6c38\u8fdc\u4e5f\u770b\u4e0d\u89c1\u53f6\u5f00\u3002\u201d \u3000\u3000\u4e01\u9e9f\u9053\uff1a\u201c\u8ba9\u5979\u6c38\u8fdc\u4e5f\u770b\u4e0d\u89c1\u53f6\u5f00\uff1f\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u4ee5\u540e\u5c31\u53ef\u4ee5\u6c38\u8fdc\u8ddf\u53f6\u5f00\u53ae\u5b88\u5728\u4e00\u8d77\u4e86\u3001\u201d\u4ed6\u770b\u7740\u4e01\u9e9f\uff0c\u4e01\u9e9f\u8ff7\u60d8\u7684\u773c \u775b\u91cc\uff0c\u679c\u7136\u50cf\u662f\u53d1\u51fa\u4e86\u5149\u3002 \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u6211\u8bf4\u7684\u8fd9\u6cd5\u5b50\u662f\u4e0d\u662f\u5f88\u597d\uff1f\u201d \u3000\u3000\u4e01\u9e9f\u9053\uff1a\u201c\u4ee5\u540e\u6211\u5c31\u53ef\u4ee5\u6c38\u8fdc\u8ddf\u53f6\u5f00\u53ae\u5b88\u5728\u4e00\u8d77\u4e86\uff1f\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u800c\u4e14\u6211\u8fd8\u53ef\u4ee5\u4fdd\u8bc1\uff0c\u4ee5\u540e\u6c38\u8fdc\u518d\u4e5f\u6ca1\u6709\u4eba\u4f1a\u6765\u62c6\u6563\u4f60\u4eec\u3002\u201d \u3000\u3000\u4e01\u9e9f\u60f3\u4e86\u60f3\uff0c\u76ee\u4e2d\u53c8\u9732\u51fa\u6050\u60e7\u4e4b\u8272\uff0c\u9053\uff1a\u201c\u53ef\u662f\u6211\u6740\u4e86\u6768\u5929\uff0c\u4ed6\u505a\u9b3c\u4e5f\u4e0d\u4f1a\u653e\u8fc7\u6211 \u7684\u3002\u201d \u3000\u3000\u97e9\u8d1e\u5fae\u7b11\u9053\uff1a\u201c\u4f60\u5e76\u6ca1\u6709\u6740\u6b7b\u4ed6\uff0c\u4ed6\u4e95\u6ca1\u6709\u6b7b\u3002\u201d \u3000\u3000\u4e01\u9e9f\u9053\uff1a\u201c\u6211\u660e\u660e\u6740\u4e86\u4ed6\u3002\u201d \u3000\u3000\u97e9\u8d1e\u62ff\u51fa\u4e86\u90a3\u67c4\u4ed6\u521a\u4ece\u5730\u4e0a\u6361\u8d77\u6765\u7684\u5200\uff0c\u9053\uff1a\u201c\u4f60\u662f\u7528\u8fd9\u628a\u5200\u6740\u4e86\u4ed6\u7684\uff1f\u201d \u3000\u3000\u4e01\u9e9f\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u4f46\u8fd9\u67c4\u5200\u5374\u662f\u6740\u4e0d\u6b7b\u4eba\u7684\uff0c\u4f60\u770b\u2026\u2026\u201d \u3000\u3000\u4ed6\u5fae\u7b11\u7740\uff0c\u53cd\u624b\u5c06\u8fd9\u67c4\u5200\u5411\u81ea\u5df1\u80f8\u4e0a\u523a\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u4ed6\u8138\u4e0a\u7684\u7b11\u5bb9\u7a81\u7136\u50f5\u786c\u3002 \u3000\u3000\u521a\u624d\u4ed6\u8f7b\u8f7b\u4e00\u6309\uff0c\u5200\u950b\u5c31\u7f29\u4e86\u56de\u53bb\u3002 \u3000\u3000\u4f46\u73b0\u5728\u5200\u950b\u7adf\u4e0d\u80af\u56de\u53bb\u4e86\u3002 \u3000\u3000\u4ed6\u8f7b\u8f7b\u4e00\u523a\uff0c\u5200\u950b\u7adf\u5df2\u523a\u5165\u4e86\u4ed6\u7684\u80f8\u819b\uff0c\u523a\u5f97\u867d\u4e0d\u6df1\uff0c\u5374\u5df2\u89c1\u4e86\u8840\u3002 \u3000\u3000\u201c\u89c1\u8840\u5c01\u5589\uff0c\u5fc5\u6b7b\u65e0\u6551\u3002\u201d \u3000\u3000\u97e9\u8d1e\u53ea\u89c9\u5f97\u5168\u8eab\u90fd\u5df2\u51b0\u51b7\uff0c\u4ece\u5fc3\u53e3\u4e00\u76f4\u51b7\u5230\u4e86\u811a\u5e95\u3002\u7a81\u542c\u4e00\u4eba\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u6700\u597d\u7ad9\u7740\u4e0d \u8981\u52a8\uff0c\u6bd2\u6c14\u4e00\u52a8\u5c31\u53d1\uff0c\u4f60\u5c31\u6b7b\u5b9a\u4e86\u3002\u201d \u3000\u3000\u97e9\u8d1e\u5f53\u7136\u7ad9\u7740\u4e0d\u6562\u52a8\uff0c\u4ed6\u5df2\u542c\u51fa\u4e86\u8fd9\u662f\u5fc3\u59d1\u7684\u58f0\u97f3\u3002 \u3000\u3000\u5fc3\u59d1\u679c\u7136\u5df2\u4ece\u6885\u6797\u5916\u8d70\u4e86\u8fc7\u6765\uff0c\u540e\u9762\u8fd8\u8ddf\u7740\u4e00\u4e2a\u4eba\uff0c\u7adf\u662f\u6768\u5929\u3002 \u3000\u3000\u97e9\u8d1e\u8fde\u817f\u90fd\u8f6f\u4e86\uff0c\u52c9\u5f3a\u7b11\u4e00\u7b11\uff0c\u5374\u504f\u504f\u7b11\u4e0d\u51fa\u3002 \u3000\u3000\u5fc3\u59d1\u51b7\u51b7\u5730\u770b\u7740\u4ed6\uff0c\u9053\uff1a\u201c\u8fd9\u628a\u5200\u662f\u9b54\u5200\uff0c\u867d\u7136\u6740\u4e0d\u6b7b\u522b\u4eba\uff0c\u5374\u6740\u5f97\u6b7b\u4f60\u3002\u201d \u3000\u3000\u6768\u5929\u51b7\u7b11\u9053\uff1a\u201c\u4e16\u4e0a\u65e2\u7136\u6709\u4f60\u8fd9\u79cd\u4eba\uff0c\u5c31\u6709\u8fd9\u79cd\u5200\u3002\u201d \u3000\u3000\u5fc3\u59d1\u5ae3\u7136\u9053\uff1a\u201c\u4e00\u70b9\u4e5f\u4e0d\u9519\uff0c\u8fd9\u79cd\u5200\u672c\u5c31\u662f\u4e13\u95e8\u4e3a\u4e86\u5bf9\u4ed8\u4ed6\u8fd9\u79cd\u4eba\u7684\u3002\u201d \u3000\u3000\u97e9\u8d1e\u54b3\u58f0\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u53ea\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u5fc3\u59d1\u6c89\u4e0b\u4e86\u8138\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u53ea\u4e0d\u8fc7\u662f\u60f3\u51fa\u5356\u6211\u4eec\u800c\u5df2\uff0c\u6240\u4ee5\u4f60\u5c31\u5f97\u6b7b\u3002\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u4f46\u671b\u59d1\u5a18\u770b\u5728\u536b\u516b\u592a\u7237\u9762\u4e0a\uff0c\u653e\u8fc7\u6211\u8fd9\u4e00\u6b21\u3002\u201d \u3000\u3000\u5fc3\u59d1\u9053\uff1a\u201c\u4f60\u8fd8\u60f3\u6d3b\u4e0b\u53bb\uff1f\u201d \u3000\u3000\u97e9\u8d1e\u70b9\u70b9\u5934\uff0c\u51b7\u6c57\u5df2\u6eda\u6eda\u800c\u4e0b\u3002 \u3000\u3000\u5fc3\u59d1\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u5c31\u4e56\u4e56\u5730\u7ad9\u5728\u8fd9\u91cc\uff0c\u4e00\u52a8\u90fd\u4e0d\u80fd\u52a8\uff0c\u8fde\u5934\u90fd\u4e0d\u80fd\u70b9\uff0c\u7b49\u6211\u9ad8\u5174\u7684\u65f6 \u5019\uff0c\u4e5f\u8bb8\u4f1a\u6765\u6551\u4f60\u7684\u3002\u201d \u3000\u3000\u97e9\u8d1e\u82e6\u7740\u8138\u9053\uff1a\u201c\u5374\u4e0d\u77e5\u59d1\u5a18\u4ec0\u4e48\u65f6\u5019\u4f1a\u9ad8\u5174\uff1f\u201d \u3000\u3000\u5fc3\u59d1\u60a0\u7136\u9053\uff1a\u201c\u8fd9\u5c31\u96be\u8bf4\u4e86\uff0c\u901a\u5e38\u6211\u603b\u662f\u5f88\u9ad8\u5174\uff0c\u53ef\u662f\u4e00\u770b\u89c1\u4f60\u8fd9\u79cd\u4eba\uff0c\u6211\u8bf4\u4e0d\u5b9a\u53c8\u4f1a \u5ffd\u7136\u53d8\u5f97\u5f88\u751f\u6c14\u3002\u201d \u3000\u3000\u97e9\u8d1e\u54ac\u7740\u7259\uff0c\u53ea\u6068\u4e0d\u5f97\u4e00\u62f3\u6253\u788e\u5979\u7684\u9f3b\u5b50\u3002 \u3000\u3000\u53ea\u53ef\u60dc\u4ed6\u5c31\u7b97\u771f\u6709\u8fd9\u79cd\u672c\u4e8b\uff0c\u4ed6\u4e5f\u4e0d\u6562\u52a8\uff0c\u8fde\u6307\u5c16\u90fd\u4e0d\u6562\u52a8\u3002 \u3000\u3000\u5fc3\u59d1\u5ffd\u7136\u4f38\u51fa\u624b\uff0c\u8f7b\u629a\u7740\u4ed6\u7684\u8138\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u5176\u5b9e\u6211\u672c\u60f3\u5ac1\u7ed9\u4f60\u7684\uff0c\u53ef\u60dc\u4f60\u7adf\u8fde\u4e00\u70b9\u8003 \u9a8c\u90fd\u7ecf\u4e0d\u8d77\uff0c\u771f\u53eb\u6211\u5931\u671b\u5f97\u5f88\u3002\u201d \u3000\u3000\u5979\u53f9\u4e86\u53e3\u6c14\uff0c\u5728\u97e9\u8d1e\u8138\u4e0a\u62e7\u4e86\u4e00\u628a\uff0c\u53c8\u6b63\u6b63\u53cd\u53cd\u7ed9\u4e86\u4ed6\u5341\u6765\u4e2a\u8033\u522e\u5b50\u3002\u97e9\u8d1e\u7b80\u76f4\u5df2\u5fcd\u4e0d \u4f4f\u8981\u5410\u8840\uff0c\u5374\u53c8\u53ea\u6709\u5fcd\u53d7\u7740\u3002 \u3000\u3000\u5fc3\u59d1\u597d\u50cf\u8fd9\u624d\u89c9\u5f97\u6ee1\u610f\u4e86\uff0c\u56de\u8fc7\u5934\u5bf9\u6768\u5929\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u73b0\u5728\u4f60\u5df2\u53ef\u5e26\u8fd9\u4f4d\u4e01\u59d1\u5a18\u8d70 \u4e86\u3002\u201d \u3000\u3000\u6768\u5929\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u5fc3\u59d1\u5fae\u7b11\u7740\uff0c\u770b\u7740\u4ed6\uff0c\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u51b3\u4e0d\u4f1a\u50cf\u4ed6\u8fd9\u4e48\u6ca1\u826f\u5fc3\u7684\uff0c\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u6768\u5929\u9053\uff1a\u201c\u6211\u81f3\u5c11\u4e0d\u4f1a\u50cf\u4ed6\u8fd9\u4e48\u7b28\u3002\u201d \u3000\u3000\u97e9\u8d1e\u5ffd\u7136\u89c9\u5f97\u81ea\u5df1\u5b9e\u5728\u5f88\u7b28\uff0c\u7b80\u76f4\u6068\u4e0d\u5f97\u81ea\u5df1\u4e00\u5934\u649e\u6b7b\uff0c\u4e01\u9e9f\u770b\u7740\u4ed6\uff0c\u8138\u4e0a\u8fd8\u662f\u4e00\u70b9\u8868 \u60c5\u4e5f\u6ca1\u6709\u3002 \u3000\u3000\u6768\u5929\u62cd\u4e86\u62cd\u4ed6\u7684\u80a9\uff0c\u9053\uff1a\u201c\u8ddf\u6211\u6765\u3002\u201d \u3000\u3000\u4e01\u9e9f\u5c31\u8ddf\u7740\u4ed6\u8d70\u4e86\u3002 \u3000\u3000\u6768\u5929\u8d70\u4e00\u6b65\uff0c\u4e01\u9e9f\u5c31\u8d70\u4e00\u6b65\uff0c\u4e24\u4e2a\u4eba\u5f88\u5feb\u5730\u5c31\u5df2\u8d70\u51fa\u6885\u6797\u3002\u665a\u98ce\u4e2d\u9690\u7ea6\u4f20\u6765\u4e00\u9635\u6b4c\u58f0\uff0c \u6b63\u662f\u5b69\u5b50\u4eec\u5531\u6765\u54c4\u5a03\u5a03\u7684\u90a3\u79cd\u6b4c\u58f0\u3002 \u3000\u3000\u96fe\u66f4\u6d53\u4e86\u3002 \u3000\u3000\u7a97\u6237\u91cc\u7684\u706f\u8fd8\u4eae\u7740\uff0c\u6768\u5929\u6572\u95e8\u3002 \u3000\u3000\u201c\u8c01\uff1f\u201d \u3000\u3000\u201c\u5728\u4e0b\u6768\u8f69\uff0c\u662f\u8fd9\u91cc\u7684\u7ba1\u4e8b\u7684\u3002\u201d \u3000\u3000\u201c\u6768\u7ba1\u4e8b\u83ab\u975e\u4e0d\u77e5\u9053\u73b0\u5728\u662f\u4ec0\u4e48\u65f6\u5019\u4e86\uff1f\u201d\u7537\u4eba\u7684\u58f0\u97f3\uff0c\u5e76\u4e0d\u592a\u5ba2\u6c14\u3002 \u3000\u3000\u65e0\u8bba\u8c01\u542c\u89c1\u534a\u591c\u6709\u4eba\u6765\u6572\u95e8\uff0c\u90fd\u4e0d\u4f1a\u592a\u5ba2\u6c14\u7684\u3002 \u3000\u3000\u6768\u5929\u9053\uff1a\u201c\u5728\u4e0b\u4e5f\u77e5\u9053\u65f6\u5df2\u4e0d\u65e9\uff0c\u53ef\u662f\u6709\u4f4d\u5ba2\u4eba\uff0c\u4e00\u5b9a\u6025\u7740\u8981\u6765\u89c1\u53f6\u516c\u5b50\u3002\u201d \u3000\u3000\u201c\u8981\u6765\u627e\u6211\uff1f\u201d \u3000\u3000\u201c\u662f\u4f4d\u59d3\u4e01\u7684\u59d1\u5a18\uff0c\u4e01\u7075\u7433\u59d1\u5a18\u3002\u201d \u3000\u3000\u201c\u5f00\u95e8\u7684\u4e00\u5b9a\u5c31\u662f\u53f6\u5f00\u3002\u201d\u6768\u5929\u5df2\u544a\u8bc9\u4e01\u9e9f\uff0c\u4e01\u9e9f\u6b63\u7ad9\u5728\u95e8\u53e3\u3002 \u3000\u3000\u95e8\u91cc\u7684\u706f\u5149\u7167\u51fa\u6765\uff0c\u521a\u597d\u7167\u5728\u4ed6\u8eab\u4e0a\uff0c\u4e00\u4e2a\u7a7f\u7740\u5f88\u968f\u4fbf\u3001\u957f\u5f97\u5374\u5f88\u597d\u770b\u7684\u5e74\u8f7b\u4eba\u521a\u62c9\u5f00 \u95e8\uff0c\u5c31\u6014\u5f80\uff0c\u8138\u4e0a\u7684\u8868\u60c5\u53c8\u662f\u60ca\u8bb6\uff0c\u53c8\u662f\u6b22\u559c\u3002 \u3000\u3000\u201c\u771f\u7684\u662f\u4f60\u3002\u201d \u3000\u3000\u4e01\u9e9f\u5782\u4e0b\u4e86\u5934\u9053\uff1a\u201c\u771f\u7684\u662f\u6211\u3002\u201d \u3000\u3000\u53f6\u5f00\u5927\u7b11\uff0c\u5927\u7b11\u7740\u8df3\u51fa\u6765\uff0c\u4e00\u628a\u62b1\u4f4f\u4e86\u5979\uff1a\u201c\u4f60\u4e0d\u751f\u6211\u6c14\u4e86\uff1f\u201d \u3000\u3000\u4ed6\u4e5f\u62b1\u4f4f\u4e86\u53f6\u5f00\uff0c\u4ed6\u7684\u624b\u5df2\u70b9\u4e0a\u4e86\u53f6\u5f00\u8111\u540e\u7684\u201c\u592a\u6795\u7a74\u201d\u3002\u53f6\u5f00\u60ca\u547c\uff0c\u653e\u624b\uff0c\u5403\u60ca\u5730\u77aa \u7740\u4e01\u9e9f\u3002 \u3000\u3000\u4e01\u9e9f\u9053\uff1a\u201c\u4f60\u4e0d\u8be5\u4e3a\u4e86\u90a3\u4e2a\u574f\u5973\u4eba\u79bb\u5f00\u6211\u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u53f9\u4e86\u53e3\u6c14\uff0c\u5012\u4e0b\u3002 \u7b2c\u516d\u7ae0\u3000\u4e03\u5c81\u7f8e\u4eba>> \u53e4\u9f99\u300a\u4e5d\u6708\u9e70\u98de\u300b \u7b2c\u516d\u7ae0\u3000\u4e03\u5c81\u7f8e\u4eba \u3000\u3000\u53f6\u5f00\u5012\u5728\u5730\u4e0a\u3002 \u3000\u3000\u8fd9\u4e2a\u5927\u5bb6\u8ba4\u4e3a\u6c5f\u6e56\u4e2d\u6700\u96be\u5bf9\u4ed8\u7684\u4e00\u4e2a\u4eba\uff0c\u5ffd\u7136\u5c31\u5df2\u5012\u4e0b\uff0c\u52a8\u4e5f\u52a8\u4e0d\u4e86\u3002 \u3000\u3000\u5ffd\u7136\u95f4\uff0c\u8fd9\u4ef6\u4e8b\u5c31\u5df2\u7ed3\u675f\u3002 \u3000\u3000\u6768\u5929\u5728\u65c1\u8fb9\u770b\u7740\uff0c\u4e5f\u663e\u5f97\u5f88\u5403\u60ca\uff0c\u4ed6\u597d\u50cf\u4e5f\u60f3\u4e0d\u5230\u8fd9\u4ef6\u4e8b\u7adf\u7ed3\u675f\u5f97\u5982\u6b64\u5bb9\u6613\u3002 \u3000\u3000\u770b\u6765\u5927\u5bb6\u4ee5\u524d\u6839\u672c\u5c31\u4e0d\u5fc5\u90a3\u4e48\u7d27\u5f20\u7684\u3002 \u3000\u3000\u4e01\u9e9f\u5782\u9996\u770b\u7740\u5730\u4e0a\u7684\u53f6\u5f00\uff0c\u8138\u4e0a\u5e26\u7740\u79cd\u8ff7\u60d8\u7684\u8868\u60c5\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u4e00\u4e2a\u4eba\u4ece\u5c4b\u91cc\u51b2\u51fa\u6765\uff0c\u4e00\u4e2a\u975e\u5e38\u7f8e\u7684\u4eba\uff0c\u624b\u91cc\u62b1\u7740\u4e2a\u6ce5\u5a03\u5a03\u3002 \u3000\u3000\u5979\u770b\u5230\u4e86\u5730\u4e0a\u7684\u53f6\u5f00\uff0c\u7f8e\u4e3d\u7684\u773c\u775b\u91cc\u5145\u6ee1\u4e86\u6124\u6012\u548c\u60ca\u8bb6\uff0c\u5ffd\u7136\u5927\u53eb\uff1a\u201c\u4f60\u4eec\u6253\u6b7b\u4e86\u4ed6\uff0c \u4ed6\u662f\u4e2a\u597d\u4eba\uff0c\u4f60\u4eec\u4e3a\u4ec0\u4e48\u8981\u6253\u6b7b\u4ed6\uff1f\u201d \u3000\u3000\u6768\u5929\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u4f60\u5c31\u662f\u4e0a\u5b98\u5c0f\u4ed9\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u70b9\u70b9\u5934\uff1a\u201c\u4f60\u6253\u6b7b\u4e86\u4ed6\uff0c\u4f60\u4e00\u5b9a\u662f\u4e2a\u574f\u4eba\u3002\u201d \u3000\u3000\u4e01\u9e9f\u5ffd\u7136\u5927\u53eb\uff1a\u201c\u4f60\u624d\u662f\u574f\u5973\u4eba\u2026\u2026\u201d \u3000\u3000\u4ed6\u5927\u53eb\u7740\u6251\u8fc7\u53bb\uff0c\u4eff\u4f5b\u8981\u53bb\u6390\u65ad\u8fd9\u5973\u4eba\u7684\u54bd\u5589\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u7684\u624b\u5374\u88ab\u62c9\u4f4f\u2026\u2026\u88ab\u94c1\u59d1\u62c9\u4f4f\u3002 \u3000\u3000\u201c\u4f60\u7684\u4e8b\u5df2\u505a\u5b8c\u4e86\uff0c\u73b0\u5728\u4e00\u5b9a\u5f88\u7d2f\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u53bb\u8eba\u4e0b\u7761\u4e00\u89c9\uff1f\u201d \u3000\u3000\u58f0\u97f3\u8fd8\u662f\u90a3\u4e48\u795e\u79d8\u800c\u4f18\u96c5\u3002 \u3000\u3000\u4e01\u9e9f\u773c\u775b\u53c8\u53d1\u76f4\uff0c\u6162\u6162\u5730\u70b9\u4e86\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6211\u7d2f\u4e86\uff0c\u6211\u8981\u7761\u4e86\u3002\u201d \u3000\u3000\u4ed6\u7adf\u771f\u7684\u8eba\u4e86\u4e0b\u53bb\uff0c\u5c31\u8eba\u5728\u95e8\u5916\u7684\u96ea\u5730\u4e0a\uff0c\u5c31\u597d\u50cf\u8eba\u5728\u5f20\u6700\u8212\u670d\u7684\u5e8a\u4e0a\u4e00\u6837\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u53c8\u5403\u60ca\u5730\u770b\u7740\u4ed6\uff0c\u5ffd\u53c8\u5927\u53eb\uff1a\u201c\u6211\u4e0d\u662f\u574f\u5973\u4eba\uff0c\u6211\u662f\u4e2a\u4e56\u5b69\u5b50\uff0c\u4f60\u624d\u662f\u574f\u5973 \u4eba\uff0c\u6240\u4ee5\u4f60\u73b0\u5728\u6b7b\u4e86\u3002\u201d \u3000\u3000\u94c1\u59d1\u67d4\u58f0\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4ed6\u624d\u662f\u4e2a\u574f\u5973\u4eba\uff0c\u53f6\u5f00\u4e5f\u662f\u4e2a\u574f\u7537\u4eba\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u53f6\u5f00\u662f\u597d\u4eba\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u4ed6\u4e0d\u662f\u597d\u4eba\uff0c\u4ed6\u4e00\u76f4\u4e0d\u80af\u8ba9\u4f60\u5582\u5976\u7ed9\u5b9d\u5b9d\u5403\uff0c\u5bf9\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u60f3\u4e86\u60f3\uff0c\u9053\uff1a\u201c\u5bf9\uff0c\u4ed6\u4e00\u76f4\u4e0d\u80af\u8ba9\u6211\u5582\u5976\u7ed9\u5b9d\u5b9d\u5403\u3002\u201d \u3000\u3000\u94c1\u59d1\u76ef\u7740\u5979\u7684\u773c\u775b\u9053\uff1a\u201c\u5b9d\u5b9d\u73b0\u5728\u4e00\u5b9a\u997f\u5f97\u8981\u547d\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u5bf9\uff0c\u5b9d\u5b9d\u65e9\u5c31\u997f\u4e86\uff0c\u5b9d\u5b9d\u4e0d\u54ed\uff0c\u5988\u5988\u5582\u5976\u7ed9\u4f60\u5403\u3002\u201d \u3000\u3000\u5979\u7adf\u771f\u7684\u62c9\u5f00\u4e86\u8863\u895f\uff0c\u9732\u51fa\u4e86\u575a\u633a\u96ea\u767d\u7684\u4e73\u623f\u3002 \u3000\u3000\u6768\u5929\u547c\u5438\u7acb\u523b\u505c\u6b62\uff0c\u5fc3\u8df3\u5374\u52a0\u5feb\u4e86\u4e09\u500d\u3002 \u3000\u3000\u94c1\u59d1\u53f9\u4e86\u53e3\u6c14\uff0c\u76ee\u4e2d\u5374\u6709\u4e86\u7b11\u610f\uff0c\u9053\uff1a\u201c\u770b\u6765\u5979\u7b80\u76f4\u8fde\u4e03\u5c81\u90e8\u4e0d\u5230\u3002\u201d \u3000\u3000\u5fc3\u59d1\u51b7\u7b11\u9053\uff1a\u201c\u90a3\u5f97\u770b\u4f60\u770b\u7684\u662f\u4ec0\u4e48\u5730\u65b9\u4e86\u3002\u201d \u3000\u3000\u94c1\u59d1\u7b11\u4e86\u201d\u5fc3\u59d1\u9053\uff1a\u201c\u4f60\u770b\u5979\u5bf9\u80f8\u812f\uff0c\u6211\u5c31\u4e0d\u4fe1\u5979\u8fd8\u6ca1\u6709\u78b0\u8fc7\u7537\u4eba\u3002\u201d \u3000\u3000\u5979\u54ac\u7740\u5634\u5507\uff0c\u773c\u775b\u91cc\u5145\u6ee1\u4e86\u5ac9\u5992\u3002 \u3000\u3000\u65e0\u8bba\u54ea\u4e2a\u5973\u4eba\uff0c\u770b\u89c1\u4e0a\u5b98\u5c0f\u4ed9\u7684\u80f8\u819b\uff0c\u90fd\u4e00\u5b9a\u4f1a\u5ac9\u5992\u7684\u3002 \u3000\u3000\u94c1\u59d1\u5df2\u8d70\u5230\u4e0a\u5b98\u5c0f\u4ed9\u8eab\u65c1\uff0c\u6402\u4f4f\u4e86\u5979\u7684\u80a9\uff0c\u9053\uff1a\u201c\u4f60\u7684\u5b9d\u5b9d\u597d\u6f02\u4eae\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u8138\u4e0a\u7acb\u523b\u9732\u51fa\u7eaf\u771f\u751c\u7f8e\u7684\u7b11\u5bb9\uff0c\u9053\uff1a\u201c\u4ed6\u672c\u6765\u5c31\u662f\u4e2a\u4e56\u5b9d\u5b9d\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u4f60\u8ba9\u6211\u62b1\u62b1\u597d\u4e0d\u597d\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u8fdf\u7591\u7740\uff0c\u9053\uff1a\u201c\u53ef\u662f\u4f60\u4e00\u5b9a\u8981\u5c0f\u5fc3\u70b9\uff0c\u4e0d\u80fd\u62b1\u5f97\u592a\u7d27\uff0c\u5b9d\u5ba4\u6015\u75bc\u3002\u201d \u3000\u3000\u94c1\u59d1\u7b11\u9053\uff1a\u201c\u6211\u77e5\u9053\uff0c\u6211\u4e5f\u6709\u4e2a\u5b9d\u5b9d\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u53c8\u8fdf\u7591\u4e86\u534a\u664c\uff0c\u7ec8\u4e8e\u5c06\u6ce5\u5a03\u5a03\u4ea4\u7ed9\u4e86\u5979\u3002 \u3000\u3000\u94c1\u59d1\u63a5\u8fc7\u6ce5\u5a03\u5a03\uff0c\u5ffd\u7136\u8f6c\u8eab\u5c31\u8dd1\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u7acb\u523b\u5927\u53eb\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u8981\u62a2\u8d70\u6211\u7684\u5b9d\u5b9d\u2026\u2026\u4f60\u662f\u4e2a\u574f\u5973\u4eba\u3002\u201d \u3000\u3000\u94c1\u59d1\u5728\u524d\u9762\u8dd1\uff0c\u5979\u5c31\u5728\u540e\u9762\u8ffd\u3002 \u3000\u3000\u4e24\u4e2a\u4eba\u4e00\u524d\u4e00\u540e\uff0c\u5f88\u5feb\u5c31\u8dd1\u51fa\u53bb\u4e86\u3002\u6768\u5929\u8fd8\u662f\u5446\u5446\u5730\u7ad9\u5728\u90a3\u91cc\uff0c\u597d\u50cf\u5f88\u60ca\u5947\uff0c\u53c8\u597d\u50cf\u5f88 \u540c\u60c5\u3002 \u3000\u3000\u5fc3\u59d1\u8e6c\u4e86\u5979\u4e00\u773c\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u5582\u5976\u7684\u5927\u59d1\u5a18\u5df2\u8d70\u4e86\uff0c\u4f60\u8fd8\u5728\u53d1\u4ec0\u4e48\u5446\uff1f\u201d \u3000\u3000\u6768\u5929\u52c9\u5f3a\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u53ea\u4e0d\u8fc7\u89c9\u5f97\u8fd9\u4ef6\u4e8b\u597d\u50cf\u592a\u7b80\u5355\u4e86\u3002\u201d \u3000\u3000\u5fc3\u59d1\u9053\uff1a\u201c\u65e0\u8bba\u591a\u7528\u96be\u7684\u4e8b\uff0c\u4f60\u53ea\u8981\u4e8b\u5148\u8ba1\u5212\u597d\uff0c\u52a8\u624b\u65f6\u90fd\u4f1a\u5f88\u7b80\u5355\u7684\u3002\u201d \u3000\u3000\u6768\u5929\u53f9\u4e86\u53e3\u6c14\uff0c\u4ed6\u4e0d\u80fd\u4e0d\u627f\u8ba4\uff1a\u201c\u8fd9\u4ef6\u4e8b\u8ba1\u5212\u5f97\u5b9e\u5728\u5f88\u597d\u3002\u201d \u3000\u3000\u5fc3\u59d1\u770b\u7740\u4ed6\u3001\u5ffd\u53c8\u5ae3\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u7684\u80f8\u812f\u6bd4\u5979\u8fd8\u597d\u770b\u5f97\u591a\uff0c\u4f60\u4fe1\u4e0d\u4fe1\uff1f\u201d \u3000\u3000\u6768\u5929\u6014\u4e86\u6014\uff0c\u8138\u5df2\u6da8\u7ea2\u4e86\uff0c\u5403\u5403\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u2026\u2026\u201d \u3000\u3000\u5fc3\u59d1\u5a9a\u7b11\u9053\uff1a\u201c\u4ee5\u540e\u6211\u4f1a\u8ba9\u4f60\u770b\u770b\u7684\uff0c\u90a3\u65f6\u4f60\u5c31\u76f8\u4fe1\u4e86\u3002\u201d \u3000\u3000\u6768\u5929\u5fc3\u8df3\u5f97\u66f4\u5feb\u3002 \u3000\u3000\u5fc3\u59d1\u9053\uff1a\u201c\u73b0\u5728\u4f60\u5148\u628a\u8fd9\u59d3\u53f6\u7684\u5f04\u56de\u53bb\u3002\u201d \u3000\u3000\u6768\u5929\u9053\uff1a\u201c\u8fd9\u4e01\u2026\u2026\u4e01\u59d1\u5a18\u5462\uff1f\u201d \u3000\u3000\u5fc3\u59d1\u9053\uff1a\u201c\u4ed6\u4f1a\u8ddf\u6211\u8d70\u7684\u3002\u201d \u3000\u3000\u5979\u7528\u529b\u8e22\u4e86\u4e01\u9e9f\u4e00\u811a\uff0c\u53c8\u56de\u5934\u5411\u6768\u5929\u4e00\u7b11\uff0c\u67d4\u58f0\u9053\uff0c\u201c\u53ea\u8981\u4f60\u80af\u505a\u4e2a\u4e56\u5b69\u5b50\uff0c\u5988\u5988\u4ee5\u540e \u4e5f\u4f1a\u5582\u5976\u7ed9\u4f60\u5403\u3002\u201d \u3000\u3000\u94c1\u59d1\u8dd1\u8fdb\u4e86\u4f5b\u5802\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u4e5f\u8ddf\u7740\u8ffd\u4e86\u8fdb\u6765\uff1a\u201c\u628a\u5b9d\u5b9d\u8fd8\u7ed9\u6211\uff0c\u5feb\u8fd8\u7ed9\u6211\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u4f60\u4e56\u4e56\u5730\u5730\u5750\u4e0b\u6765\uff0c\u6211\u5c31\u8fd8\u7ed9\u4f60\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u7acb\u523b\u5728\u84b2\u56e2\u4e0a\u5750\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u6211\u8fd8\u6709\u51e0\u53e5\u8bdd\u95ee\u4f60\uff0c\u4f60\u4e5f\u8981\u4e56\u4e56\u8ddf\u6211\u8bf4\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u70b9\u70b9\u5934\u3002 \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u4f60\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u201d \u3000\u3000\u201c\u4e0a\u5b98\u5c0f\u4ed9\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u4f60\u7238\u7238\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6211\u7238\u7238\u662f\u4e2a\u795e\u4ed9\uff0c\u6211\u4ece\u6765\u4e5f\u6ca1\u6709\u89c1\u8fc7\u4ed6\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u4f60\u5988\u5988\u5462\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u5988\u5988\u5728\u7761\u89c9\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u5728\u4ec0\u4e48\u5730\u65b9\u7761\u89c9\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u5728\u4e00\u4e2a\u957f\u957f\u7684\u6728\u5934\u76d2\u5b50\u91cc\u7761\u89c9\uff0c\u5df2\u7761\u4e86\u5f88\u4e45\u5f88\u4e45\u4e86\u3002\u201d \u3000\u3000\u5979\u8138\u4e0a\u9732\u51fa\u4e86\u60b2\u54c0\u4e4b\u8272\uff0c\u53c8\u9053\uff1a\u201c\u5979\u8bf4\u5979\u5f88\u5feb\u5c31\u4f1a\u9192\u7684\uff0c\u53ef\u662f\u5979\u4e00\u76f4\u90fd\u6ca1\u6709\u9192\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u4f60\u5988\u5988\u7761\u7740\u4e86\u540e\uff0c\u4f60\u5c31\u8ddf\u7740\u8c01\u4e86\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6211\u5c31\u8ddf\u4e00\u4e2a\u4f1a\u98de\u7684\u53d4\u53d4\uff0c\u5988\u5988\u8981\u6211\u53eb\u4ed6\u98de\u53d4\u53d4\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u7136\u540e\u5462\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u540e\u6765\u98de\u53d4\u53d4\u5c31\u53bb\u627e\u53f6\u5f00\uff0c\u53eb\u6211\u8ddf\u7740\u4ed6\u3002\u201d \u3000\u3000\u94c1\u59d1\u76ee\u4e2d\u9732\u51fa\u6ee1\u610f\u4e4b\u8272\uff0c\u9053\uff1a\u201c\u90a3\u4e2a\u98de\u53d4\u53d4\u4e00\u5b9a\u5bf9\u4f60\u5f88\u597d\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4ed6\u5f88\u559c\u6b22\u6211\uff0c\u4ed6\u5bf9\u6211\u5f88\u597d\uff0c\u5f88\u597d\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u4ed6\u662f\u4e0d\u662f\u9001\u4e86\u5f88\u591a\u4e1c\u897f\u7ed9\u4f60\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4ed6\u66ff\u6211\u4e70\u65b0\u8863\u670d\u7a7f\uff0c\u53c8\u66ff\u6211\u4e70\u597d\u4e1c\u897f\u5403\u54e9\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u53ea\u624b\u7684\u53d4\u53d4\u5462\uff0c\u662f\u4e0d\u662f\u4e5f\u9001\u4e86\u5f88\u591a\u4e1c\u897f\u7ed9\u4f60\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u76b1\u7709\u9053\uff1a\u201c\u4e00\u53ea\u624b\u7684\u53d4\u53d4\uff1f\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u4f60\u96be\u9053\u4e0d\u8bb0\u5f97\u4ed6\u4e86\uff1f\u4ed6\u8eab\u4e0a\u7a7f\u7740\u4ef6\u9ec4\u8863\u670d\uff0c\u6837\u5b50\u770b\u8d77\u6765\u5f88\u51f6\u7684\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u7a81\u7136\u62cd\u624b\u7b11\u9053\uff1a\u201c\u6211\u60f3\u8d77\u6765\u4e86\uff0c\u6709\u4e00\u5929\u4ed6\u53bb\u627e\u98de\u53d4\u53d4\uff0c\u770b\u89c1\u4e86\u6211\uff0c\u8fd8\u5e26\u6211\u53bb\u6349 \u8774\u8776\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u4ed6\u6ca1\u6709\u9001\u4e1c\u897f\u7ed9\u4f60\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u94c1\u59d1\u6c89\u4e0b\u4e86\u8138\uff0c\u9053\uff1a\u201c\u771f\u7684\u6ca1\u6709\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u771f\u7684\u3002\u201d \u3000\u3000\u94c1\u59d1\u76ee\u5149\u95ea\u52a8\uff0c\u9053\uff1a\u201c\u4ed6\u6709\u6ca1\u6709\u544a\u8bc9\u4f60\u4ec0\u4e48\u8bdd\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6709\u3002\u201d \u3000\u3000\u94c1\u59d1\u7acb\u523b\u8ffd\u95ee\u9053\uff1a\u201c\u4ed6\u544a\u8bc9\u4f60\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4ed6\u8bf4\u6709\u4e2a\u5730\u65b9\uff0c\u6709\u597d\u591a\u597d\u591a\u597d\u73a9\u7684\u4e1c\u897f\uff0c\u8981\u6211\u957f\u5927\u4e86\u53bb\u62ff\u3002\u201d \u3000\u3000\u94c1\u59d1\u7684\u773c\u775b\u53c8\u4eae\u4e86\uff0c\u9053\uff1a\u201c\u4ed6\u6709\u6ca1\u6709\u544a\u8bc9\u4f60\uff0c\u90a3\u4e2a\u5730\u65b9\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u70b9\u70b9\u5934\u3002 \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u4f60\u8bb0\u4f4f\u4e86\u4e48\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4ed6\u8ddf\u6211\u8bf4\u4e86\u597d\u591a\u597d\u591a\u904d\uff0c\u4e00\u5b9a\u8981\u6211\u8bb0\u4f4f\u3002\u201d \u3000\u3000\u94c1\u59d1\u7b11\u4e86\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u662f\u4e2a\u53c8\u806a\u660e\u3001\u53c8\u542c\u8bdd\u7684\u4e56\u5b69\u5b50\uff0c\u53ea\u8981\u4f60\u628a\u4ed6\u8bf4\u7684\u8bdd\u544a\u8bc9 \u6211\uff0c\u6211\u5c31\u628a\u5b9d\u5b9d\u8fd8\u7ed9\u4f60\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u53ef\u662f\u90a3\u4e2a\u53d4\u53d4\u8bf4\uff0c\u53eb\u6211\u5343\u4e07\u4e0d\u80fd\u544a\u8bc9\u522b\u4eba\u7684\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u4f60\u544a\u8bc9\u6211\u6ca1\u5173\u7cfb\uff0c\u6211\u662f\u4ed6\u5f88\u597d\u5f88\u597d\u7684\u670b\u53cb\uff0c\u4ed6\u4e0d\u4f1a\u602a\u4f60\u7684\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u8fdf\u7591\u7740\u9053\uff1a\u201c\u53ef\u662f\u4ed6\u8bf4\uff0c\u53ea\u8981\u6211\u628a\u8fd9\u4ef6\u4e8b\u544a\u8bc9\u522b\u4eba\uff0c\u6211\u5988\u5988\u5c31\u6c38\u8fdc\u4e0d\u4f1a\u9192 \u4e86\u3002\u201d \u3000\u3000\u94c1\u59d1\u53c8\u6c89\u4e0b\u8138\uff0c\u9053\uff1a\u201c\u4f60\u82e5\u4e0d\u544a\u8bc9\u6211\uff0c\u6211\u5c31\u628a\u5b9d\u5b9d\u6454\u6b7b\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u7684\u8138\u8272\u53d8\u4e86\uff0c\u5927\u53eb\u9053\uff1a\u201c\u4f60\u4e0d\u80fd\u6454\u6b7b\u6211\u7684\u5b9d\u5b9d\uff0c\u4ed6\u662f\u4e2a\u4e56\u5b9d\u5b9d\u3002\u201d \u3000\u3000\u94c1\u59d1\u51b7\u51b7\u9053\uff1a\u201c\u6211\u77e5\u9053\u4ed6\u53c8\u4e56\u53c8\u542c\u8bdd\uff0c\u53ef\u662f\u53ea\u8981\u6211\u5f80\u5730\u4e0a\u4e00\u6454\uff0c\u4f60\u4ee5\u540e\u5c31\u518d\u4e5f\u89c1\u4e0d\u5230\u4ed6 \u4e86\uff0c\u4e5f\u6ca1\u6709\u4eba\u966a\u4f60\u73a9\u4e86\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u5df2\u7ecf\u5feb\u54ed\u4e86\u51fa\u6765\uff0c\u6d41\u7740\u6cea\u9053\uff1a\u201c\u6c42\u6c42\u4f60\u2026\u2026\u6c42\u6c42\u4f60\u2026\u2026\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u6c42\u6211\u4e5f\u6ca1\u6709\u7528\u7684\uff0c\u9664\u975e\u4f60\u80fd\u628a\u90a3\u5730\u65b9\u544a\u8bc9\u6211\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u53ea\u8981\u6211\u544a\u8bc9\u4f60\uff0c\u4f60\u5c31\u628a\u5b9d\u5b9d\u8fd8\u7ed9\u6211\uff1f\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u800c\u4e14\u8fd8\u5e2e\u4f60\u4e70\u597d\u591a\u597d\u591a\u65b0\u8863\u670d\u7a7f\uff0c\u597d\u591a\u597d\u591a\u4e1c\u897f\u5403\uff0c\u201d\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u597d\uff0c \u6211\u544a\u8bc9\u4f60\uff0c\u90a3\u5730\u65b9\u5c31\u5728\u2026\u2026\u201d \u3000\u3000\u5979\u8fd8\u6ca1\u6709\u8bf4\u51fa\u6765\uff0c\u94c1\u59d1\u7a81\u53c8\u5927\u58f0\u9053\uff1a\u201c\u7b49\u4e00\u7b49\u518d\u8bf4\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u94c1\u59d1\u51b7\u7b11\uff0c\u9053\uff1a\u201c\u56e0\u4e3a\u8fd9\u4ef6\u4e8b\u4f60\u53ea\u80fd\u544a\u8bc9\u6211\u4e00\u4e2a\u4eba\uff0c\u5343\u4e07\u4e0d\u80fd\u8ba9\u522b\u4eba\u542c\u89c1\u3002\u201d \u3000\u3000\u53ea\u542c\u95e8\u5916\u6709\u4eba\u8f7b\u8f7b\u54b3\u55fd\u4e86\u4e00\u58f0\uff0c\u6768\u5929\u5df2\u62b1\u7740\u53f6\u5f00\u8d70\u8fdb\u6765\u3002 \u3000\u3000\u5fc3\u59d1\u4e5f\u540c\u65f6\u8d70\u4e86\u8fdb\u6765\uff0c\u4e01\u9e9f\u8ddf\u5728\u540e\u9762\u3002 \u3000\u3000\u94c1\u59d1\u6c89\u7740\u8138\uff0c\u5389\u58f0\u9053\uff1a\u201c\u8c01\u53eb\u4f60\u628a\u4ed6\u4eec\u5e26\u56de\u6765\u7684\uff1f\u201d \u3000\u3000\u5fc3\u59d1\u9053\uff1a\u201c\u4e0d\u5e26\u56de\u6765\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u4f60\u96be\u9053\u4e0d\u4f1a\u6740\u4e86\u4ed6\u4eec\uff1f\u201d \u3000\u3000\u5fc3\u59d1\u9053\uff1a\u201c\u4e24\u4e2a\u4eba\u90fd\u6740\uff1f\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u4f60\u8fd8\u60f3\u7559\u4e0b\u8c01\uff1f\u201d \u3000\u3000\u5fc3\u59d1\u9053\uff1a\u201c\u73b0\u5728\u5c31\u6740\uff1f\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u73b0\u5728\u5c31\u6740\uff01\u201d \u3000\u3000\u53f6\u5f00\u8737\u66f2\u5728\u5730\u4e0a\uff0c\u770b\u6765\u5df2\u7ecf\u50cf\u662f\u4e2a\u6b7b\u4eba\uff0c\u4e01\u9e9f\u867d\u7136\u8fd8\u80fd\u7ad9\u7740\uff0c\u53ef\u662f\u4e24\u773c\u53d1\u76f4\uff0c\u522b\u4eba\u8bf4\u8981 \u6740\u4ed6\uff0c\u4ed6\u5374\u597d\u50cf\u542c\u4e0d\u89c1\u3002 \u3000\u3000\u5fc3\u59d1\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8fd9\u4e48\u597d\u770b\u7684\u7537\u4eba\uff0c\u6211\u5b9e\u5728\u820d\u4e0d\u5f97\u4e0b\u624b\u3002\u201d \u3000\u3000\u6768\u5929\u51b7\u51b7\u9053\uff1a\u201c\u6211\u820d\u5f97\u3002\u201d \u3000\u3000\u5fc3\u59d1\u779f\u4e86\u4ed6\u4e00\u773c\uff0c\u5a07\u7b11\u9053\uff1a\u201c\u4f60\u5728\u5403\u918b\u3002\u201d \u3000\u3000\u5fc3\u59d1\u9053\uff1a\u201c\u597d\uff0c\u6211\u7ed9\u4f60\u5200\u3002\u201d \u3000\u3000\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u4e00\u67c4\u5200\u843d\u5728\u5730\u4e0a\u3002 \u3000\u3000\u6768\u5929\u5f2f\u8170\u6361\u4e86\u8d77\u6765\uff0c\u770b\u7740\u4e01\u9e9f\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u6740\u4e86\u6211\u4e00\u6b21\uff0c\u73b0\u5728\u6211\u4e5f\u8981\u6740\u4f60\u4e00\u6b21\u3001\u8fd9\u7b14 \u5e10\u73b0\u5728\u5c31\u53ef\u4ee5\u7ed3\u6e05\u4e86\uff0c\u7528\u4e0d\u7740\u7b49\u5230\u540e\u6765\u3002\u201d \u3000\u3000\u4e01\u9e9f\u770b\u7740\u4ed6\u624b\u91cc\u7684\u5200\uff0c\u8fd8\u662f\u4e00\u70b9\u53cd\u5e94\u4e5f\u6ca1\u6709\u3002 \u3000\u3000\u6768\u5929\u76ee\u4e2d\u9732\u51fa\u6740\u673a\uff0c\u4e00\u5200\u523a\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u7a81\u542c\u4e00\u4eba\u5927\u559d\u9053\uff1a\u201c\u7b49\u4e00\u7b49\u3002\u201d \u3000\u3000\u6768\u5929\u7f29\u56de\u624b\uff0c\u76b1\u7740\u7709\u56de\u8fc7\u5934\uff0c\u624d\u53d1\u73b0\u53eb\u4ed6\u7b49\u4e00\u7b49\u7684\u4eba\u662f\u536b\u5929\u9e4f\u3002 \u3000\u3000\u536b\u5929\u9e4f\u4e0d\u77e5\u4ec0\u4e48\u65f6\u5019\u5df2\u9192\u4e86\uff0c\u4ece\u8f6f\u69bb\u4e0a\u6162\u6162\u5730\u5750\u4e86\u8d77\u6765\u3002 \u3000\u3000\u94c1\u59d1\u76b1\u7709\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u8981\u4ed6\u7b49\u4e00\u7b49\uff1f\u201d \u3000\u3000\u5de5\u5929\u9e4f\u9053\uff1a\u201c\u8fd9\u4e24\u4eba\u4f60\u4e00\u5b9a\u8981\u6740\uff1f\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u975e\u6740\u4e0d\u53ef\u3002\u201d \u3000\u3000\u536b\u592d\u9e4f\u9053\uff1a\u201c\u5c31\u5728\u8fd9\u91cc\u6740\uff1f\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u5c31\u5728\u8fd9\u91cc\u3002\u201d \u3000\u3000\u5de5\u5929\u9e4f\u9053\uff1a\u201c\u4f5b\u5802\u91cc\u4e5f\u80fd\u6740\u4eba\uff1f\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u6211\u4eec\u4f9b\u7684\u4f5b\uff0c\u672c\u5c31\u662f\u6740\u4eba\u7684\u4f5b\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u4e5f\u77e5\u9053\u4f60\u7edd\u4e0d\u4f1a\u7559\u4e0b\u53f6\u5f00\u7684\uff0c\u53ef\u662f\u8fd9\u59d3\u4e01\u53ed\u2026\u2026\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u4f60\u60f3\u7559\u4e0b\u4ed6\uff1f\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u73b0\u5728\u4ed6\u5df2\u65e0\u5f02\u662f\u4e2a\u5e9f\u4eba\uff0c\u53c8\u4f55\u5fc5\u8fd8\u8981\u4ed6\u7684\u547d\u3002\u201d \u3000\u3000\u6768\u5929\u51b7\u51b7\u9053\uff1a\u201c\u536b\u516b\u592a\u7237\u83ab\u975e\u52a8\u4e86\u601c\u9999\u60dc\u7389\u4e4b\u5fc3\uff0c\u60f3\u56de\u53bb\u6536\u623f\u518d\u517b\u4e2a\u513f\u5b50\uff1f\u201d \u3000\u3000\u536b\u5929\u9e4f\u6012\u9053\uff1a\u201c\u4f60\u662f\u4ec0\u4e48\u4eba\uff0c\u600e\u6562\u5728\u6211\u9762\u524d\u5982\u6b64\u65e0\u793c\uff1f\u201d \u3000\u3000\u6768\u5929\u9053\uff1a\u201c\u6211\u53ea\u4e0d\u8fc7\u63d0\u9192\u4f60\u4e00\u58f0\uff0c\u4e5f\u514d\u5f97\u4f60\u5931\u671b\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u5931\u671b\uff1f\u201d \u3000\u3000\u6768\u5929\u9053\uff0c\u201c\u8fd9\u4f4d\u4e01\u59d1\u5a18\u662f\u4e0d\u4f1a\u517b\u513f\u5b50\u7684\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u4f60\u4ee5\u4e3a\u6211\u4e0d\u77e5\u9053\u4ed6\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u6768\u5929\u9053\uff1a\u201c\u65e2\u7136\u77e5\u9053\uff0c\u4e3a\u4ec0\u4e48\u8fd8\u8981\u7559\u4e0b\u4ed6\u7684\u547d\uff1f\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u7b49\u4f60\u5230\u4e86\u6211\u8fd9\u79cd\u5e74\u7eaa\uff0c\u4f60\u5c31\u4f1a\u77e5\u9053\uff0c\u80fd\u4e0d\u6740\u7684\u4eba\uff0c\u8fd8\u662f\u4e0d\u6740\u7684\u597d\u3002\u201d \u3000\u3000\u4ed6\u53f9\u606f\u7740\uff0c\u6162\u6162\u9053\uff1a\u201c\u5c11\u5e74\u65f6\u6740\u4eba\u5927\u591a\uff0c\u7b49\u5230\u8001\u5e74\u65f6\uff0c\u5c31\u96be\u514d\u8981\u540e\u6094\u4e86\u3002\u201d \u3000\u3000\u6768\u5929\u51b7\u7b11\u9053\uff1a\u201c\u536b\u516b\u592a\u7237\u7684\u5fc3\uff0c\u51e0\u65f6\u53d8\u5f97\u8fd9\u4e48\u8f83\u7684\uff1f\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u521a\u624d\u3002\u201d \u3000\u3000\u6768\u5929\u9053\uff1a\u201c\u521a\u624d\uff1f\u201d \u3000\u3000\u536b\u5929\u9e4f\u53f9\u9053\uff1a\u201c\u4e00\u4e2a\u4eba\u77e5\u9053\u81ea\u5df1\u6709\u4e86\u513f\u5973\u65f6\uff0c\u5fc3\u60c5\u5c31\u4f1a\u8ddf\u4ee5\u524d\u4e0d\u540c\u4e86\u3002\u201d \u3000\u3000\u94c1\u59d1\u7a81\u7136\u51b7\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u6709\u4e86\u513f\u5973\uff0c\u4f60\u4ee5\u4e3a\u6211\u771f\u662f\u4f60\u7684\u5973\u513f\uff1f\u201d \u3000\u3000\u536b\u5929\u9e4f\u6115\u7136\u9053\uff1a\u201c\u4f60\u4e0d\u662f\uff1f\u201d \u3000\u3000\u94c1\u59d1\u51b7\u7b11\u9053\uff1a\u201c\u5357\u6d77\u5a18\u5b50\u8fd9\u4e00\u751f\u4e2d\uff0c\u7537\u4eba\u4e5f\u4e0d\u77e5\u6709\u8fc7\u591a\u5c11\u4e2a\uff0c\u513f\u5973\u5374\u504f\u504f\u8fde\u534a\u4e2a\u4e5f\u6ca1 \u6709\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u9053\uff1a\u201c\u4f60\u5462\uff1f\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u6211\u4e0d\u662f\u4f60\u7684\u5973\u513f\uff0c\u4e5f\u4e0d\u662f\u5979\u7684\u5973\u513f\u3002\u201d \u3000\u3000\u5de5\u5929\u9e4f\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u7a76\u7adf\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u5929\u9b54\u65e0\u76f8\uff0c\u4e07\u5999\u65e0\u65b9\uff0c\u4e0a\u5929\u5165\u5730\uff0c\u552f\u6211\u72ec\u5c0a\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u7a81\u7136\u53d8\u8272\uff0c\u9053\uff1a\u201c\u4f60\u662f\u9b54\u6559\u95e8\u4e0b\uff1f\u201d \u3000\u3000\u5fc3\u59d1\u60a0\u7136\u9053\uff1a\u201c\u597d\u53eb\u536b\u516b\u7237\u5f97\u77e5\uff0c\u5979\u5c31\u662f\u56db\u5927\u516c\u4e3b\u4e2d\u7684\u4e09\u516c\u4e3b\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u9762\u4e0a\u5df2\u65e0\u8840\u8272\uff0c\u8fde\u8bdd\u90fd\u5df2\u8bf4\u4e0d\u51fa\u4e86\u3002 \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u5357\u6d77\u5a18\u5b50\u662f\u672c\u6559\u7684\u53db\u5f92\uff0c\u81ea\u8ba4\u4e3a\u5df2\u53ef\u4e0e\u672c\u6559\u6559\u4e3b\u5206\u5ead\u6297\u793c\uff0c\u6240\u4ee5\u6211\u5c31\u6545\u610f\u5165 \u5979\u95e8\u4e0b\uff0c\u5148\u5b66\u5979\u7684\u9b54\u529f\uff0c\u7528\u5979\u6559\u7ed9\u6211\u7684\u529f\u592b\u6740\u4e86\u5979\u3002\u201d \u3000\u3000\u5fc3\u59d1\u9053\uff1a\u201c\u8fd9\u662f\u672c\u6559\u4e2d\u7684\u2018\u4ee5\u7259\u8fd8\u7259\uff0c\u795e\u9f99\u65e0\u76f8\u5927\u6cd5\u2019\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u8138\u5982\u6b7b\u7070\uff0c\u5583\u5583\u9053\uff1a\u201c\u539f\u6765\u4f60\u4e0d\u662f\u6211\u7684\u5973\u513f\u2026\u2026\u539f\u6765\u6211\u6ca1\u6709\u5973\u513f\u2026\u2026\u201d \u3000\u3000\u4ed6\u53cd\u53cd\u590d\u590d\u4ed6\u8bf4\u7740\u8fd9\u4e24\u53e5\u8bdd\uff0c\u7adf\u4f3c\u5df2\u53d8\u5f97\u75f4\u5446\u4e86\uff0c\u8fd9\u4ef6\u4e8b\u5bf9\u4ed6\u7684\u6253\u51fb\uff0c\u5b9e\u5728\u6bd4\u780d\u4ed6\u4e00\u5200 \u8fd8\u8981\u4ee4\u4ed6\u75db\u82e6\u3002 \u3000\u3000\u5fc3\u59d1\u5374\u53c8\u9053\uff1a\u201c\u6211\u4eec\u521a\u624d\u6545\u610f\u6551\u4f60\uff0c\u53ea\u4e0d\u8fc7\u56e0\u4e3a\u90a3\u65f6\u6740\u4e86\u4f60\uff0c\u5bf9\u6211\u4eec\u5e76\u6ca1\u6709\u597d\u5904\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u4f46\u73b0\u5728\u97e9\u8d1e\u5df2\u77e5\u9053\u6211\u662f\u4f60\u7684\u5973\u513f\uff0c\u7236\u4eb2\u6b7b\u4e86\uff0c\u5bb6\u8d22\u81ea\u7136\u662f\u7531\u5973\u513f\u7ee7\u627f\u7684\u3002\u201d \u3000\u3000\u94c1\u59d1\u53c8\u9053\uff1a\u201c\u672c\u6559\u8fd1\u5e74\u6765\u4eba\u6750\u8f88\u51fa\uff0c\u91cd\u632f\u96c4\u98ce\u3001\u552f\u6211\u72ec\u5c0a\u7684\u65f6\u5019\uff0c\u4e5f\u53c8\u5feb\u5230\u4e86\uff0c\u6240\u7f3a\u5c11 \u7684\u53ea\u4e0d\u8fc7\u662f\u4e00\u4e9b\u8d22\u529b\u800c\u5df2\u3002\u201d \u3000\u3000\u5fc3\u59d1\u9053\uff1a\u201c\u4f46\u6709\u4e86\u4f60\u548c\u4e0a\u5b98\u91d1\u8679\u7684\u8d22\u5bcc\u540e\uff0c\u6211\u4eec\u5c31\u5df2\u4e07\u4e8b\u5177\u5907\u4e86\u3002\u201d \u3000\u3000\u536b\u5929\u9e4f\u5634\u91cc\u8fd8\u662f\u5728\u53cd\u53cd\u590d\u590d\u4ed6\u8bf4\u7740\u90a3\u4e24\u53e5\u8bdd\uff0c\u7a81\u7136\u5927\u559d\u4e00\u58f0\uff0c\u5410\u51fa\u4e86\u4e00\u53e3\u9c9c\u8840\u3002 \u3000\u3000\u7136\u540e\u4ed6\u5c31\u5012\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u94c1\u59d1\u8fde\u770b\u90fd\u4e0d\u518d\u770b\u4ed6\u4e00\u773c\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u6768\u5929\uff0c\u73b0\u5728\u4f60\u8fd8\u4e0d\u52a8\u624b\uff1f\uff1f\u201d \u3000\u3000\u6768\u5929\u4e5f\u5df2\u9762\u65e0\u4eba\u8272\uff0c\u9b54\u6559\u7684\u53ef\u6015\uff0c\u4ed6\u4ee5\u524d\u53ea\u4e0d\u8fc7\u542c\u8bf4\u800c\u5df2\uff0c\u73b0\u5728\u5374\u5df2\u4eb2\u8eab\u4f53\u4f1a\u5230\u3002 \u3000\u3000\u4ed6\u624b\u91cc\u7d27\u7d27\u63e1\u7740\u90a3\u67c4\u78a7\u7eff\u78a7\u7eff\u7684\u9b54\u5200\uff0c\u7b2c\u4e8c\u6b21\u523a\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u4e01\u9e9f\u52a8\u4e5f\u4e0d\u52a8\u5730\u7ad9\u7740\uff0c\u65e2\u4e0d\u77e5\u9053\u8eb2\u907f\uff0c\u4e5f\u4e0d\u77e5\u9053\u95ea\u907f\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u7a81\u542c\u5916\u9762\u4e00\u58f0\u60e8\u547c\uff0c\u51c4\u5389\u7684\u53eb\u58f0\uff0c\u7adf\u4f3c\u597d\u51e0\u4e2a\u4eba\u540c\u65f6\u53d1\u51fa\u6765\u7684\u3002\u53c8\u50cf\u662f\u65e0\u6570 \u6761\u997f\u72fc\u540c\u65f6\u88ab\u4eba\u5272\u65ad\u4e86\u54bd\u5589\uff0c\u51c4\u5389\u7684\u547c\u58f0\u7a81\u7136\u54cd\u8d77\uff0c\u53c8\u7a81\u7136\u505c\u6b62\u3002 \u3000\u3000\u6768\u5929\u7684\u624b\u4e00\u677e\uff0c\u4f3c\u5df2\u8fde\u5200\u90fd\u62ff\u4e0d\u7a33\u4e86\uff0c\u5fc3\u59d1\u66ae\u5730\u8f6c\u8eab\uff0c\u62c9\u5f00\u4e86\u95e8\u3002 \u3000\u3000\u4e00\u4e2a\u767d\u8863\u4eba\u52a8\u4e5f\u4e0d\u52a8\u5730\u7ad9\u5728\u95e8\u5916\uff0c\u96ea\u767d\u7684\u957f\u888d\u4e0a\uff0c\u6e85\u6ee1\u4e86\u6885\u82b1\u822c\u7684\u9c9c\u8840\u3002\u80cc\u540e\u80cc\u7740\u5377\u8349 \u5e2d\uff0c\u624b\u91cc\u62ff\u7740\u6839\u77ed\u68cd\u3002 \u3000\u3000\u58a8\u767d\u6765\u4e86\u3002 \u3000\u3000\u5fc3\u59d1\u975e\u4f46\u9762\u4e0d\u6539\u8272\uff0c\u53cd\u800c\u5ae3\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u65e2\u7136\u6765\u4e86\uff0c\u4e3a\u4ec0\u4e48\u7ad9\u5728\u95e8\u53e3\u5462\uff1f\u5feb\u8bf7\u8fdb\u6765 \u5750\u3002\u201d \u3000\u3000\u58a8\u767d\u9053\uff1a\u201c\u7ad9\u7740\u5c31\u5f88\u597d\u3002\u201d \u3000\u3000\u5fc3\u59d1\u9053\uff1a\u201c\u4f60\u5230\u8fd9\u91cc\u6765\uff0c\u96be\u9053\u5c31\u662f\u4e3a\u4e86\u7ad9\u5728\u8fd9\u91cc\u770b\u95e8\u7684\uff1f\u201d \u3000\u3000\u58a8\u767d\u9053\uff1a\u201c\u6211\u5230\u8fd9\u91cc\u6765\uff0c\u4e5f\u4e0d\u662f\u4e3a\u4e86\u4e0a\u5b98\u5c0f\u4ed9\u3002\u201d \u3000\u3000\u5fc3\u59d1\u9053\uff1a\u201c\u771f\u7684\u4e0d\u662f\uff1f\u201d \u3000\u3000\u58a8\u767d\u9053\uff1a\u201c\u4e0d\u662f\u3002\u201d \u3000\u3000\u5fc3\u59d1\u9053\uff1a\u201c\u542c\u8bf4\u5728\u9752\u57ce\u5c71\u91cc\u90a3\u5730\u65b9\uff0c\u5f00\u9500\u4e5f\u5f88\u5927\uff0c\u4e5f\u5f88\u7f3a\u94b1\u7528\u3002\u201d \u3000\u3000\u58a8\u767d\u9053\uff1a\u201c\u6211\u4eec\u6709\u6765\u8def\u3002\u201d \u3000\u3000\u5fc3\u59d1\u7728\u4e86\u7728\u773c\uff0c\u5a9a\u7b11\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u96be\u9053\u662f\u4e3a\u4e86\u6211\u6765\u7684\uff1f\u201d \u3000\u3000\u5979\u672c\u6765\u4e00\u76f4\u51b7\u5982\u79cb\u971c\uff0c\u4eff\u4f5b\u795e\u5723\u4e0d\u53ef\u4fb5\u72af\u7684\u6837\u5b50\uff0c\u4f46\u73b0\u5728\u5374\u5df2\u53d8\u4e86\uff0c\u53d8\u6210\u4e86\u4efb\u4f55\u7537\u4eba\u90fd \u60f3\u4fb5\u72af\u4e00\u4e0b\u7684\u5973\u4eba\u3002 \u3000\u3000\u8c01\u77e5\u58a8\u767d\u5374\u662f\u65e0\u52a8\u4e8e\u8877\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u6211\u4e0d\u662f\u4e3a\u4e86\u5973\u4eba\u6765\u7684\u3002\u201d \u3000\u3000\u5fc3\u59d1\u7b11\u9053\uff1a\u201c\u4e0d\u662f\u4e3a\u4e86\u5973\u4eba\uff0c\u4f60\u2026\u2026\u4f60\u559c\u6b22\u7537\u4eba\uff1f\u201d \u3000\u3000\u58a8\u767d\u9053\uff1a\u201c\u6211\u662f\u4e3a\u4e86\u53f6\u5f00\u6765\u7684\u3002\u201d \u3000\u3000\u5fc3\u59d1\u9053\uff1a\u201c\u4f60\u559c\u6b22\u4ed6\uff1f\u201d \u3000\u3000\u58a8\u767d\u9053\uff1a\u201c\u6211\u559c\u6b22\u6740\u4e86\u4ed6\u3002\u201d \u3000\u3000\u5fc3\u59d1\u9053\uff1a\u201c\u4f60\u8ddf\u4ed6\u6709\u4ec7\uff1f\u201d \u3000\u3000\u58a8\u767d\u9053\uff1a\u201c\u6709\u3002\u201d \u3000\u3000\u5fc3\u59d1\u9053\uff0c\u201c\u4ed6\u6740\u4e86\u4f60\u8001\u5b50\uff1f\u8fd8\u662f\u62a2\u4e86\u4f60\u8001\u5a46\uff1f\u201d \u3000\u3000\u58a8\u81ea\u6c89\u4e0b\u8138\uff0c\u9053\uff1a\u201c\u6211\u53ea\u5e0c\u671b\u4f60\u4eec\u80fd\u628a\u4ed6\u4ea4\u7ed9\u6211\u5e26\u56de\u53bb\u3002\u201d \u3000\u3000\u5fc3\u59d1\u9053\uff1a\u201c\u6211\u4eec\u672c\u6765\u5c31\u8981\u6740\u4e86\u4ed6\u7684\uff0c\u4f60\u8981\u52a8\u624b\uff0c\u4e5f\u65e0\u6240\u8c13\uff0c\u53ea\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u58a8\u767d\u9053\uff1a\u201c\u53ea\u4e0d\u8fc7\u600e\u6837\uff1f\u201d \u3000\u3000\u5fc3\u59d1\u9053\uff1a\u201c\u6211\u53c8\u600e\u77e5\u4f60\u662f\u8981\u6740\u4ed6\uff1f\u8bf4\u4e0d\u5b9a\u4f60\u662f\u60f3\u6551\u4ed6\u5462\uff1f\u201d \u3000\u3000\u58a8\u767d\u6c89\u541f\u7740\uff0c\u9053\uff1a\u201c\u6211\u53ef\u4ee5\u5f53\u4f60\u4eec\u7684\u9762\u6740\u4e86\u4ed6\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u597d\uff0c\u7ed9\u4ed6\u5200\uff0c\u8ba9\u4ed6\u4e0b\u624b\u3002\u201d \u3000\u3000\u6768\u5929\u4e00\u6325\u624b\uff0c\u629b\u51fa\u624b\u91cc\u7684\u5200\uff0c\u201c\u53ee\u201d\u7684\u4e00\u58f0\uff0c\u843d\u5728\u58a8\u767d\u811a\u4e0b\u3002 \u3000\u3000\u58a8\u767d\u7528\u811a\u5c16\u52fe\u8d77\uff0c\u4f38\u624b\u6284\u4f4f\uff0c\u6162\u6162\u5730\u8d70\u4e86\u8fdb\u672a\uff0c\u773c\u775b\u76ef\u7740\u5730\u4e0a\u7684\u53f6\u5f00\uff0c\u7a81\u7136\u4e00\u5200\u523a\u51fa\u3002 \u3000\u3000\u4ed6\u7684\u51fa\u624b\u597d\u5feb\u3002 \u3000\u3000\u4f46\u8fd9\u4e00\u5200\u5374\u4e0d\u662f\u523a\u5411\u53f6\u5f00\u7684\uff0c\u5200\u5c16\u95ea\u7535\u822c\u5411\u94c1\u59d1\u523a\u4e86\u8fc7\u53bb\uff0c\u94c1\u7ad9\u4eff\u4f5b\u5b8c\u5168\u60f3\u4e0d\u5230\u4ed6\u8fd9\u4e00 \u7740\uff0c\u7ade\u6765\u4e0d\u53ca\u95ea\u907f\uff0c\u58a8\u767d\u5200\u5df2\u523a\u4e0a\u5979\u5fc3\u53e3\uff0c\u94c1\u59d1\u7684\u8138\u8272\u6ca1\u6709\u53d8\uff0c\u4ed6\u7684\u8138\u8272\u53cd\u800c\u53d8\u4e86\u3002 \u3000\u3000\u4ed6\u5df2\u611f\u89c9\u5230\u8fd9\u67c4\u5200\u950b\u7adf\u662f\u6d3b\u7684\uff0c\u4e00\u5200\u523a\u4e2d\uff0c\u5200\u950b\u7adf\u7f29\u4e86\u56de\u6765\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u53ea\u542c\u201c\u53ee\u201d\u7684\u4e00\u54cd\uff0c\u5200\u67c4\u91cc\u7adf\u5c04\u51fa\u4e09\u70b9\u5bd2\u661f\uff0c\u6253\u5728\u58a8\u767d\u81ea\u5df1\u80f8\u819b\u4e0a\u3002 \u3000\u3000\u4ed6\u8eab\u5b50\u4e00\u9707\uff0c\u773c\u73e0\u5b50\u5374\u4f3c\u5df2\u51f8\u4e86\u51fa\u6765\uff0c\u51b7\u51b0\u51b0\u7684\u4e00\u5f20\u8138\u4e5f\u5df2\u56e0\u60ca\u8bb6\u6050\u60e7\u800c\u626d\u66f2\u53d8\u5f62\u3002 \u3000\u3000\u94c1\u59d1\u51b7\u51b7\u5730\u770b\u7740\u4ed6\uff0c\u9053\uff1a\u201c\u8fd9\u662f\u67c4\u9b54\u5200\uff0c\u9b54\u5200\u4e0d\u6740\u4e3b\u4eba\u3002\u201d \u3000\u3000\u539f\u6765\u5200\u4e22\u5728\u5730\u4e0a\u65f6\uff0c\u90a3\u201c\u53ee\u201d\u7684\u4e00\u58f0\u54cd\uff0c\u5200\u67c4\u4e2d\u7684\u673a\u7c27\u5df2\u53d8\u4e86\u3002 \u3000\u3000\u58a8\u767d\u7684\u8138\u7531\u767d\u53d8\u7ea2\uff0c\u5ffd\u7136\u53c8\u53d8\u6210\u6b7b\u7070\u8272\uff0c\u54ac\u7740\u7259\u9053\uff1a\u201c\u4f60\u6740\u4e86\u6211\u65e0\u59a8\uff0c\u6211\u4e3b\u4eba\u4e0d\u4f1a\u653e\u8fc7 \u4f60\u7684\u3002\u201d \u3000\u3000\u94c1\u59d1\u76b1\u7709\u9053\uff1a\u201c\u4f60\u8fd8\u6709\u4e3b\u4eba\u2026\u2026\u4f60\u7684\u4e3b\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u58a8\u767d\u5589\u5499\u91cc\u683c\u683c\u53d1\u54cd\uff0c\u5374\u5df2\u8bf4\u4e0d\u51fa\u8bdd\u6765\uff0c\u5ffd\u7136\u72c2\u543c\u4e00\u58f0\uff0c\u5411\u94c1\u59d1\u6251\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u94c1\u59d1\u52a8\u4e5f\u4e0d\u52a8\u3002 \u3000\u3000\u58a8\u81ea\u7684\u624b\u5df2\u6390\u4e0a\u4e86\u5979\u7684\u54bd\u5589\uff0c\u53ef\u662f\u4ed6\u81ea\u5df1\u5374\u5df2\u5148\u5012\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u94c1\u59d1\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8fd9\u91cc\u7684\u4eba\u597d\u50cf\u5feb\u8981\u6b7b\u5149\u4e86\u5427\uff1f\u201d \u3000\u3000\u5fc3\u59d1\u9053\uff1a\u201c\u53ea\u5269\u4e0b\u53f6\u5f00\u548c\u4e01\u7075\u7433\u4e24\u4e2a\u3002\u201d \u3000\u3000\u6768\u5929\u9053\uff1a\u201c\u6211\u4eec\u4e3a\u4ec0\u4e48\u4e0d\u8ba9\u4ed6\u4eec\u4f5c\u4e00\u5bf9\u540c\u547d\u9e33\u9e2f\uff1f\u201d\u5fc3\u59d1\u9053\uff1a\u201c\u4f60\u51fa\u624b\u5feb\u4e9b\uff0c\u4ed6\u4eec\u73b0\u5728 \u4e5f\u4e0d\u80fd\u518d\u6d3b\u7740\u53d7\u7f6a\u4e86\u3002\u201d \u3000\u3000\u6768\u5929\u5ffd\u7136\u4ece\u81ea\u5df1\u8896\u5b50\u91cc\u62bd\u51fa\u67c4\u5200\uff0c\u4e00\u5200\u5411\u53f6\u5f00\u523a\u51fa\uff1a\u201c\u8fd9\u6b21\u6211\u5148\u6740\u4ed6\u3002\u201d \u3000\u3000\u7a81\u7136\u95f4\uff0c\u53c8\u6709\u4e00\u4e2a\u4eba\u559d\u9053\uff1a\u201c\u7b49\u4e00\u7b49\u3002\u201d \u3000\u3000\u8fd9\u6b21\u53eb\u4ed6\u7b49\u4e00\u7b49\u7684\u4eba\uff0c\u7adf\u662f\u94c1\u59d1\u3002 \u3000\u3000\u6768\u5929\u5fcd\u4e0d\u4f4f\u53eb\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u8fd8\u8981\u7b49\u4e00\u7b49\uff1f\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u58a8\u767d\u662f\u4e3a\u4e86\u4ed6\u800c\u6765\u7684\uff0c\u800c\u4e14\u4e0d\u60dc\u5192\u7740\u751f\u547d\u4e4b\u9669\uff0c\u8981\u5e26\u4ed6\u56de\u53bb\u3002\u201d \u3000\u3000\u5fc3\u59d1\u9053\uff1a\u201c\u4ed6\u82e5\u771f\u7684\u8ddf\u53f6\u5f00\u6709\u4ec7\uff0c\u672c\u6765\u662f\u53ef\u4ee5\u5728\u8fd9\u91cc\u52a8\u624b\u7684\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u53ea\u4e0d\u8fc7\uff0c\u770b\u6765\u4ed6\u597d\u50cf\u4e00\u5b9a\u8981\u5c06\u53f6\u5f00\u5e26\u56de\u53bb\u3002\u201d \u3000\u3000\u5fc3\u59d1\u9053\uff1a\u201c\u4ed6\u4e3a\u4ec0\u4e48\u8981\u8fd9\u4e48\u505a\u5462\uff1f\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u58a8\u767d\u4e0d\u662f\u5446\u5b50\uff0c\u4ed6\u8fd9\u6837\u505a\u5f53\u7136\u6709\u7528\u610f\u3002\u201d \u3000\u3000\u5fc3\u59d1\u773c\u73e0\u5b50\u8f6c\u52a8\u7740\uff0c\u9053\uff1a\u201c\u83ab\u975e\u53f6\u5f00\u8eab\u4e0a\u6709\u4ec0\u4e48\u79d8\u5bc6\uff1f\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u5f88\u53ef\u80fd\u3002\u201d \u3000\u3000\u5fc3\u59d1\u7b11\u9053\uff1a\u201c\u597d\uff0c\u6211\u5148\u6765\u641c\u4e00\u641c\u4ed6\u3002\u201d \u3000\u3000\u6768\u5929\u9053\uff1a\u201c\u4ed6\u662f\u4e2a\u7537\u4eba\uff0c\u4e0d\u5982\u8fd8\u662f\u8ba9\u6211\u6765\u52a8\u624b\u7684\u597d\u3002\u201d \u3000\u3000\u5fc3\u59d1\u77aa\u773c\u9053\uff1a\u201c\u7537\u4eba\u4e3a\u4ec0\u4e48\u6211\u5c31\u641c\u4e0d\u5f97\uff1f\u6211\u5c31\u559c\u6b22\u641c\u7537\u4eba\u7684\u8eab\uff0c\u5c24\u5176\u662f\u6f02\u4eae\u7684\u7537\u4eba\u3002\u201d \u3000\u3000\u6768\u53c8\u54ac\u4e86\u54ac\u7259\uff0c\u95ed\u4e0a\u4e86\u5634\u3002 \u3000\u3000\u5fc3\u59d1\u53c8\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u82e5\u5403\u918b\uff0c\u7b49\u4f1a\u513f\u6211\u4e5f\u53ef\u4ee5\u641c\u4e00\u641c\u4f60\u3002\u201d \u3000\u3000\u5979\u5a9a\u7b11\u7740\uff0c\u8e72\u4e0b\u8eab\uff0c\u4f38\u624b\u53bb\u89e3\u53f6\u5f00\u7684\u8863\u895f\u3002 \u3000\u3000\u53ef\u662f\u5979\u7684\u624b\u521a\u4f38\u51fa\u53bb\uff0c\u7a81\u7136\u60ca\u547c\u4e86\u4e00\u58f0\uff0c\u7f29\u56de\u4e86\u624b\uff0c\u5c31\u597d\u50cf\u88ab\u6bd2\u86c7\u54ac\u4e86\u4e00\u53e3\u3002 \u3000\u3000\u94c1\u59d1\u76b1\u7709\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\u5927\u60ca\u5c0f\u602a\u7684\uff0c\u96be\u9053\u4f60\u4ece\u6765\u6ca1\u78b0\u8fc7\u7537\u4eba\uff1f\u201d \u3000\u3000\u5fc3\u59d1\u6ee1\u9762\u60ca\u8bb6\u4e4b\u8272\uff0c\u9053\uff1a\u201c\u4f46\u4ed6\u5374\u662f\u4e2a\u5973\u4eba\u3002\u201d \u3000\u3000\u94c1\u59d1\u52a8\u5bb9\u9053\uff1a\u201c\u5973\u4eba\uff1f\u4f60\u8bf4\u53f6\u5f00\u662f\u4e2a\u5973\u4eba\uff1f\u201d \u3000\u3000\u5fc3\u59d1\u9053\uff1a\u201c\u662f\u4e2a\u4e0d\u6298\u4e0d\u6263\u3001\u8d27\u771f\u4ef7\u5b9e\u7684\u5973\u4eba\uff0c\u80f8\u812f\u597d\u50cf\u6bd4\u4e0a\u5b98\u5c0f\u4ed9\u8fd8\u5927\u3002\u201d \u3000\u3000\u94c1\u59d1\u76ee\u5149\u95ea\u52a8\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u4e01\u7075\u7433\u662f\u4e2a\u7537\u4eba\uff0c\u53f6\u5f00\u53cd\u800c\u662f\u4e2a\u5973\u4eba\uff0c\u8fd9\u4ef6\u4e8b\u60c5\u771f\u6709\u8da3\u3002\u201d \u3000\u3000\u5fc3\u59d1\u9053\uff1a\u201c\u7b80\u76f4\u8d8a\u6765\u8d8a\u6709\u8da3\u4e86\u3002\u201d \u3000\u3000\u94c1\u59d1\u6c89\u7740\u8138\uff0c\u9053\uff1a\u201c\u4e0d\u7ba1\u4ed6\u662f\u7537\u662f\u5973\u5148\u780d\u4e0b\u4ed6\u4e24\u53ea\u624b\u518d\u8bf4\u3002\u201d \u3000\u3000\u5fc3\u59d1\u4e00\u628a\u593a\u8fc7\u6768\u5929\u624b\u91cc\u7684\u5200\uff0c\u4e00\u5200\u780d\u4e0b\u3002 \u7b2c\u4e03\u7ae0\u3000\u8981\u547d\u5a03\u5a03>> \u53e4\u9f99\u300a\u4e5d\u6708\u9e70\u98de\u300b \u7b2c\u4e03\u7ae0\u3000\u8981\u547d\u5a03\u5a03 \u3000\u3000\u8fd9\u628a\u5200\u5bd2\u5149\u56db\u5c04\uff0c\u663e\u7136\u5f88\u950b\u5229\uff0c\u8981\u780d\u4e0b\u4e00\u4e2a\u4eba\u7684\u624b\u6765\uff0c\u5b9e\u5728\u6bd4\u5200\u5207\u8c46\u8150\u8fd8\u5bb9\u6613\u3002 \u3000\u3000\u8c01\u77e5\u5c31\u5728\u8fd9\u65f6\uff0c\u672c\u6765\u8fde\u52a8\u4e5f\u4e0d\u80fd\u52a8\u7684\u53f6\u5f00\uff0c\u7a81\u7136\u7ffb\u8eab\uff0c\u4e00\u811a\u8e22\u5411\u5fc3\u59d1\u7684\u809a\u5b50\u3002 \u3000\u3000\u5fc3\u59d1\u5927\u60ca\uff0c\u540e\u9000\uff0c\u6070\u597d\u9000\u5728\u6768\u5929\u9762\u524d\u3002 \u3000\u3000\u6768\u5929\u65e9\u5df2\u7b49\u7740\u5979\u4e86\uff0c\u53f3\u624b\u95ea\u7535\u822c\u70b9\u4e86\u5979\u80cc\u540e\u4e94\u5904\u7a74\u9053\uff0c\u5de6\u624b\u62e6\u8170\u4e00\u628a\u5c06\u5979\u62b1\u4f4f\u3002 \u3000\u3000\u94c1\u59d1\u7684\u8138\u8272\u53d8\u4e86\u3002 \u3000\u3000\u6768\u5929\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u6700\u597d\u4e0d\u8981\u52a8\uff0c\u5426\u5219\u6211\u5c31\u5148\u6740\u4e86\u4f60\u8fd9\u5b9d\u8d1d\u5973\u513f\u3002\u201d \u3000\u3000\u94c1\u59d1\u6ca1\u6709\u52a8\u3002 \u3000\u3000\u5979\u5f53\u7136\u7edd\u4e0d\u662f\u8f7b\u4e3e\u5984\u52a8\u7684\u4eba\u3002 \u3000\u3000\u8fd9\u65f6\u53f6\u5f00\u5df2\u7b11\u563b\u563b\u5730\u4ece\u5730\u4e0a\u7ad9\u4e86\u8d77\u6765\uff0c\u7b11\u5f97\u53c8\u7f8e\u53c8\u751c\u3002 \u3000\u3000\u94c1\u59d1\u5fcd\u4e0d\u4f4f\u9053\uff1a\u201c\u2026\u2026\u4f60\u771f\u7684\u662f\u4e2a\u5973\u4eba\uff1f\u201d \u3000\u3000\u53f6\u5f00\u5ae3\u7136\u9053\uff1a\u201c\u662f\u4e2a\u4e0d\u6298\u4e0d\u6263\u3001\u8d27\u771f\u4ef7\u5b9e\u7684\u5973\u4eba\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u53f6\u5f00\uff1f\u201d \u3000\u3000\u8fd9\u4e2a\u201c\u53f6\u5f00\u201d\u7b11\u9053\uff1a\u201c\u53f6\u5f00\u662f\u4e2a\u4e0d\u6298\u4e0d\u6263\u3001\u8d27\u771f\u4ef7\u5b9e\u7684\u7537\u4eba\uff0c\u6211\u600e\u4e48\u4f1a\u662f\u53f6\u5f00\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u4e01\u7075\u7433\u3002\u201d \u3000\u3000\u94c1\u59d1\u6115\u7136\u9053\uff1a\u201c\u4f60\u662f\u4e01\u7075\u7433\uff1f\u201d \u3000\u3000\u201c\u662f\u4e2a\u4e0d\u6298\u4e0d\u6263\u3001\u8d27\u771f\u4ef7\u5b9e\u7684\u4e01\u7075\u7433\u3002\u201d \u3000\u3000\u94c1\u59d1\u6014\u4f4f\u3002 \u3000\u3000\u5979\u8138\u4e0a\u7684\u8868\u60c5\uff0c\u50cf\u662f\u5ffd\u7136\u88ab\u4eba\u54ac\u4e86\u4e00\u53e3\u3002 \u3000\u3000\u90a3\u4e2a\u4e01\u7075\u7433\u8fd8\u52a8\u4e5f\u4e0d\u52a8\u5730\u7ad9\u5728\u90a3\u91cc\u3002 \u3000\u3000\u4e01\u7075\u7433\u8fc7\u53bb\u770b\u4ed6\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u4e00\u70b9\u4e5f\u4e0d\u50cf\u6211\u561b\uff0c\u6211\u603b\u8981\u6bd4\u4f60\u6f02\u4eae\u591a\u4e86\u3002\u201d \u3000\u3000\u5979\u4eec\u5b9e\u5728\u4e00\u70b9\u4e5f\u4e0d\u50cf\u3002 \u3000\u3000\u94c1\u59d1\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u4f60\u82e5\u662f\u4e01\u7075\u7433\uff0c\u53f6\u5f00\u5462\uff1f\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u53f6\u5f00\u65e9\u5c31\u6765\u4e86\u3002\u201d \u3000\u3000\u94c1\u59d1\u6115\u7136\u9053\uff1a\u201c\u4ed6\u65e9\u5c31\u6765\u4e86\uff1f\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u4e0d\u4f46\u65e9\u5c31\u6765\u4e86\uff0c\u800c\u4e14\u4e00\u76f4\u90fd\u5728\u4f60\u9762\u524d\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u83ab\u975e\u662f\u6768\u5929\uff1f\u201d \u3000\u3000\u6768\u5929\u7b11\u9053\uff1a\u201c\u6768\u5929\u5c31\u662f\u6768\u5929\uff0c\u4e0d\u662f\u53f6\u5f00\u3002\u201d \u3000\u3000\u94c1\u59d1\u51e0\u4e4e\u8981\u75af\u4e86\uff0c\u5fcd\u4e0d\u4f4f\u5927\u53eb\u9053\uff1a\u201c\u53f6\u5f00\u7a76\u7adf\u662f\u8c01\uff1f\u201d \u3000\u3000\u53ea\u542c\u4e00\u4e2a\u4eba\u60a0\u7136\u9053\uff1a\u201c\u662f\u6211\u3002\u201d \u3000\u3000\u201c\u7a76\u7adf\u8c01\u662f\u53f6\u5f00\uff1f\u201d \u3000\u3000\u4e01\u9e9f\u9053\uff1a\u201c\u662f\u6211\uff0c\u6211\u5c31\u662f\u53f6\u5f00\u3002\u201d \u3000\u3000\u4ed6\u8138\u4e0a\u90a3\u79cd\u8ff7\u60d8\u75f4\u5446\u7684\u8868\u60c5\uff0c\u5ffd\u7136\u5b8c\u5168\u4e0d\u89c1\u4e86\uff0c\u773c\u775b\u4e5f\u4e0d\u518d\u53d1\u76f4\u3002 \u3000\u3000\u5ffd\u7136\u95f4\uff0c\u4ed6\u5df2\u5b8c\u5168\u53d8\u4e86\u4e2a\u4eba\u3002 \u3000\u3000\u94c1\u59d1\u770b\u7740\u4ed6\uff0c\u8138\u4e0a\u8fde\u5403\u60ca\u7684\u8868\u60c5\u90fd\u6ca1\u6709\u4e86\uff0c\u4ec0\u4e48\u8868\u60c5\u90fd\u6ca1\u6709\u4e86\u3002 \u3000\u3000\u5979\u6574\u4e2a\u4eba\u90fd\u5df2\u53d1\u786c\uff0c\u786c\u5f97\u50cf\u662f\u5757\u6728\u5934\u2026\u2026\u5979\u81ea\u5df1\u4e5f\u89c9\u5f97\u81ea\u5df1\u50cf\u662f\u5757\u6728\u5934\u3002 \u3000\u3000\u5979\u8fd9\u4e00\u751f\u4e2d\uff0c\u4ece\u6765\u4e5f\u6ca1\u6709\u8fd9\u4e48\u5403\u60ca\u8fc7\u3002 \u3000\u3000\u4e01\u7075\u7433\u5403\u5403\u5730\u7b11\u7740\uff0c\u4ece\u6000\u91cc\u638f\u5757\u96ea\u767d\u7684\u4e1d\u5dfe\uff0c\u629b\u7ed9\u53f6\u5f00\uff0c\u9053\uff1a\u201c\u5feb\u628a\u4f60\u8138\u4e0a\u8fd9\u4e9b\u80ed\u8102\u64e6 \u5e72\u51c0\uff0c\u514d\u5f97\u6211\u770b\u7740\u6076\u5fc3\u3002\u201d \u3000\u3000\u53f6\u5f00\u5fae\u7b11\u9053\uff1a\u201c\u4f60\u6076\u5fc3\uff1f\u4f46\u5374\u504f\u504f\u6709\u5f88\u591a\u4eba\u8ba4\u4e3a\u6211\u7f8e\u6781\u4e86\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u7f8e\u4e2a\u5c41\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u82e5\u662f\u4e0d\u7f8e\uff0c\u600e\u4e48\u4f1a\u6709\u4eba\u8ba4\u4e3a\u6211\u50cf\u4e01\u7075\u7433\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u5fcd\u4e0d\u4f4f\u7b11\u9053\uff1a\u201c\u6211\u82e5\u771f\u7684\u50cf\u4f60\u8fd9\u6837\u5b50\uff0c\u6211\u65e9\u5c31\u4e00\u5934\u649e\u6b7b\u4e86\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u82e5\u771f\u7684\u50cf\u4f60\u8fd9\u6837\u5b50\uff0c\u4f60\u77e5\u9053\u6211\u4f1a\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u4e01\u7075\u7433\u633a\u8d77\u80f8\u9053\uff1a\u201c\u6211\u8fd9\u6837\u53c8\u6709\u54ea\u70b9\u4e0d\u597d\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4e5f\u6ca1\u4ec0\u4e48\u4e0d\u597d\uff0c\u53ea\u4e0d\u8fc7\u80f8\u633a\u5f97\u592a\u9ad8\u4e86\u4e9b\uff0c\u6240\u4ee5\u624d\u4f1a\u88ab\u4eba\u5bb6\u770b\u7834\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u7684\u8138\u7ea2\u4e86\uff0c\u5ffd\u7136\u4f38\u624b\u53bb\u89e3\u5fc3\u59d1\u7684\u8863\u895f\u3002 \u3000\u3000\u5fc3\u59d1\u672c\u6765\u4e00\u76f4\u5782\u7740\u5934\uff0c\u597d\u50cf\u5944\u5944\u4e00\u606f\u7684\u6837\u5b50\uff0c\u6b64\u523b\u624d\u5fcd\u4e0d\u4f4f\u5927\u53eb\u9053\uff1a\u201c\u4f60\u60f3\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u4e5f\u4e0d\u60f3\u5e72\u4ec0\u4e48\uff0c\u53ea\u4e0d\u8fc7\u4f60\u521a\u624d\u8981\u641c\u6211\u7684\u8eab\uff0c\u6211\u73b0\u5728\u4e5f\u8981\u641c\u641c\u4f60\u7684\u8eab\uff0c\u6211\u8fd9 \u4eba\u4e00\u5411\u4e0d\u5403\u4e8f\u7684\u3002\u201d \u3000\u3000\u6768\u5929\u9053\uff1a\u201c\u8981\u641c\u4e5f\u8be5\u8f6e\u5230\u6211\u641c\u4e86\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u4f46\u5979\u662f\u4e2a\u5973\u4eba\u3002\u201d \u3000\u3000\u6768\u5929\u9053\uff1a\u201c\u5973\u4eba\u4e3a\u4ec0\u4e48\u6211\u5c31\u62fd\u4e0d\u5f97\uff0c\u6211\u5c31\u559c\u6b22\u641c\u5973\u4eba\u7684\u8eab\uff0c\u5c24\u5176\u662f\u6f02\u4eae\u5973\u4eba\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u5927\u7b11\uff0c\u6768\u5929\u4e5f\u5927\u7b11\u3002 \u3000\u3000\u4ed6\u4eec\u6709\u8d44\u683c\u7b11\uff0c\u56e0\u4e3a\u4ed6\u4eec\u505a\u7684\u8fd9\u4ef6\u4e8b\uff0c\u5b9e\u5728\u662f\u7cbe\u5f69\u7edd\u4f26\u3002 \u3000\u3000\u94c1\u59d1\u770b\u6765\u5374\u4f3c\u5df2\u8fde\u54ed\u90fd\u54ed\u4e0d\u51fa\u6765\u4e86\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u5df2\u4ece\u5979\u624b\u91cc\u62a2\u56de\u4e86\u6ce5\u5a03\u5a03\uff1a\u201c\u5b9d\u5b9d\u4e56\uff0c\u4e56\u5b9d\u5b9d\uff0c\u5988\u5988\u518d\u4e5f\u4e0d\u4f1a\u8ba9\u574f\u4eba\u62a2\u8d70\u4f60 \u4e86\u3002\u201d \u3000\u3000\u8fd9\u6ce5\u5a03\u5a03\u624d\u662f\u5979\u5173\u5fc3\u7684\uff0c\u522b\u7684\u4eba\u65e0\u8bba\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\uff0c\u5979\u90fd\u4e0d\u7ba1\uff0c\u5979\u4e5f\u4e0d\u80fd\u7ba1\u3002 \u3000\u3000\u5b69\u5b50\u4eec\u5c82\u975e\u603b\u4ee5\u4e3a\u81ea\u5df1\u7684\u5e7b\u60f3\u662f\u771f\u5b9e\u7684\u3002 \u3000\u3000\u4f46\u94c1\u59d1\u7684\u5e7b\u60f3\u5374\u5df2\u6210\u4e86\u6ce1\u5f71\u3002 \u3000\u3000\u5979\u672c\u6765\u4ee5\u4e3a\u6240\u6709\u7684\u4eba\u90fd\u5df2\u4eba\u4e86\u5979\u7684\u5708\u5957\u91cc\uff0c\u73b0\u5728\u624d\u77e5\u9053\u539f\u6765\u5979\u81ea\u5df1\u4e00\u76f4\u90fd\u5728\u53f6\u5f00\u7684\u5708\u5957 \u91cc\uff0c\u5979\u7684\u5e7b\u60f3\u5c82\u975e\u4e5f\u6b63\u5982\u8fd9\u767d\u75f4\u624b\u91cc\u7684\u6ce5\u5a03\u5a03\u4e00\u6837\uff1f \u3000\u3000\u5979\u770b\u7740\u53f6\u5f00\uff0c\u5fcd\u4e0d\u4f4f\u957f\u957f\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6211\u73b0\u5728\u624d\u76f8\u4fe1\u4e86\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u76f8\u4fe1\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u94c1\u59d1\u82e6\u7b11\u9053\uff1a\u201c\u76f8\u4fe1\u4f60\u662f\u5929\u4e0b\u6700\u96be\u7f20\u3001\u6700\u53ef\u6015\u7684\u4e00\u4e2a\u4eba\u3002\u201d \u3000\u3000\u53f6\u5f00\u4e5f\u53f9\u4e86\u53e3\u6c14\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u6211\u627f\u8ba4\uff0c\u6211\u7684\u786e\u4e0d\u80fd\u7b97\u662f\u4e2a\u541b\u5b50\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u80fd\u627f\u8ba4\u81ea\u5df1\u4e0d\u662f\u4e2a\u541b\u5b50\uff0c\u4e5f\u662f\u4ef6\u4e0d\u5bb9\u6613\u7684\u4e8b\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u80af\u81ea\u5df1\u8ba4\u8f93\u66f4\u4e0d\u5bb9\u6613\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u4f60\u65e9\u5df2\u77e5\u9053\u6211\u4eec\u8fd9\u4e9b\u4eba\u4f1a\u5728\u8fd9\u91cc\u7b49\u7740\u4f60\u4e86\uff1f\u201d \u3000\u3000\u53f6\u5f00\u70b9\u70b9\u5934\u3002 \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u5c31\u8ddf\u6768\u5929\u5546\u91cf\u597d\uff0c\u53eb\u4ed6\u6545\u610f\u6765\u6295\u9760\u6211\uff0c\u8ba9\u6211\u4ee5\u4e3a\u4e01\u9e9f\u5c31\u662f\u4e01\u7075\u7433\u7684\u5144 \u5f1f\uff0c\u518d\u5e2e\u7740\u6211\u51fa\u4e3b\u610f\uff0c\u8981\u6211\u5c06\u4e01\u9e9f\u626e\u6210\u4e01\u7075\u7433\uff1f\u201d \u3000\u3000\u53f6\u5f00\u7b11\u9053\uff1a\u201c\u8fd9\u672c\u6765\u5c31\u662f\u4e2a\u597d\u4e3b\u610f\uff0c\u6211\u77e5\u9053\u4f60\u4e00\u5b9a\u4f1a\u63a5\u53d7\u7684\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u7136\u540e\u4f60\u518d\u4ee5\u4e01\u9e9f\u7684\u8eab\u4efd\u51fa\u73b0\uff0c\u6545\u610f\u8ba9\u6211\u6293\u4f4f\u4f60\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u672c\u6765\u5c31\u662f\u4e01\u9e9f\u3002\u201d \u3000\u3000\u94c1\u59d1\u4e0d\u61c2\uff0c\u9053\uff1a\u201c\u4f60\u7a76\u7adf\u662f\u53f6\u5f00\uff1f\u8fd8\u662f\u4e01\u9e9f\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u53f6\u5f00\u4e5f\u5c31\u662f\u4e01\u9e9f\u3002\u201d \u3000\u3000\u94c1\u59d1\u66f4\u4e0d\u61c2\u4e86\u3002 \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4e01\u9e9f\u53ea\u4e0d\u8fc7\u662f\u6211\u4ee5\u524d\u95ef\u6c5f\u6e56\u7684\u65f6\u5019\uff0c\u7528\u8fc7\u7684\u4e00\u4e2a\u540d\u5b57\u3002\u201d \u3000\u3000\u94c1\u59d1\u7ec8\u4e8e\u61c2\u4e86\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u4f60\u4e00\u5171\u7a76\u7adf\u7528\u8fc7\u51e0\u4e2a\u540d\u5b57\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4e0d\u591a\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u4f60\u7528\u8fc7\u7684\u540d\u5b57\uff0c\u5168\u90fd\u51fa\u540d\u3002\u201d \u3000\u3000\u53f6\u5f00\u7b11\u9053\uff1a\u201c\u6211\u8fd0\u6c14\u4e00\u5411\u4e0d\u9519\u3002\u201d \u3000\u3000\u94c1\u59d1\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u770b\u6765\u6211\u5b9e\u5728\u4e0d\u8be5\u9009\u4e2d\u4f60\u8fd9\u4e48\u6837\u7684\u4e00\u4e2a\u4eba\u505a\u5bf9\u624b\u7684\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u5ae3\u7136\u9053\uff1a\u201c\u4f60\u9009\u9519\u4e86\uff0c\u6211\u5374\u6ca1\u6709\u9009\u9519\u3002\u201d \u3000\u3000\u5979\u770b\u7740\u53f6\u5f00\uff0c\u7f8e\u4e3d\u7684\u773c\u775b\u91cc\u5145\u6ee1\u4e86\u7231\u6155\u548c\u5c0a\u656c\u3002 \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u4f60\u96be\u9053\u6839\u672c\u5c31\u6ca1\u6709\u8ddf\u4ed6\u5435\u7ffb\uff1f\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u8c01\u8bf4\u6211\u6ca1\u6709\uff0c\u6211\u8ddf\u4ed6\u4e0d\u77e5\u5435\u7ffb\u8fc7\u591a\u5c11\u6b21\u3002\u201d \u3000\u3000\u5979\u7ea2\u7740\u8138\u4e00\u7b11\uff0c\u53c8\u9053\uff1a\u201c\u53ef\u662f\u6211\u4eec\u6bcf\u6b21\u5435\u7ffb\u4e4b\u540e\uff0c\u4e0d\u51fa\u4e09\u5929\uff0c\u6211\u5c31\u53c8\u60f3\u53bb\u627e\u4ed6\u4e86\u3002\u201d \u3000\u3000\u94c1\u59d1\u53f9\u9053\uff1a\u201c\u6211\u672c\u6838\u65e9\u5c31\u60f3\u5230\u7684\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u60f3\u5230\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u50cf\u4ed6\u8fd9\u6837\u7684\u7537\u4eba\u5e76\u4e0d\u591a\uff0c\u6211\u82e5\u662f\u4f60\uff0c\u6211\u4e5f\u7edd\u4e0d\u4f1a\u771f\u7684\u4e0d\u7406\u4ed6\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u6240\u4ee5\u6211\u4e00\u5b9a\u4f1a\u597d\u597d\u5730\u770b\u7740\u4ed6\uff0c\u4e0d\u8ba9\u522b\u4eba\u6765\u6253\u4ed6\u7684\u4e3b\u610f\u3002\u201d \u3000\u3000\u5979\u7684\u7b11\u5bb9\u770b\u6765\u4e5f\u53d8\u5f97\u6709\u70b9\u50cf\u72d0\u72f8\u4e86\u3002 \u3000\u3000\u94c1\u59d1\u53c8\u53f9\u9053\uff1a\u201c\u4e0d\u7ba1\u600e\u4e48\u6837\uff0c\u6211\u8fde\u505a\u68a6\u90fd\u60f3\u4e0d\u5230\u4f60\u4f1a\u626e\u6210\u53f6\u5f00\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u53f6\u5f00\u65e2\u7136\u4e0d\u5728\uff0c\u603b\u5f97\u6709\u4e2a\u4eba\u4fdd\u62a4\u5c0f\u4ed9\u7684\uff0c\u7528\u6211\u6765\u4fdd\u62a4\u5979\uff0c\u5c82\u975e\u518d\u5b89\u5168\u4e5f\u6ca1 \u6709\u4e86\u3002\u201d \u3000\u3000\u5979\u60a0\u7136\u63a5\u7740\u9053\uff1a\u201c\u7531\u4f60\u770b\u7740\u5979\uff0c\u975e\u4f46\u522b\u4eba\u52a8\u4e0d\u4e86\u5979\uff0c\u53f6\u5f00\u4e5f\u52a8\u4e0d\u4e86\u4e86\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u53f6\u5f00\u6839\u672c\u5c31\u4e0d\u4f1a\u6253\u5979\u7684\u4e3b\u610f\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u4f60\u597d\u50cf\u5f88\u81ea\u4fe1\uff1f\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u6211\u4e00\u76f4\u90fd\u6709\u81ea\u4fe1\uff0c\u6240\u4ee5\u8c01\u4e5f\u4f11\u60f3\u6765\u6311\u62e8\u79bb\u95f4\u3002\u201d \u3000\u3000\u94c1\u59d1\u53ea\u6709\u82e6\u7b11\u7740\u8f6c\u5411\u53f6\u5f00\uff1a\u201c\u6211\u4e5f\u60f3\u4e0d\u5230\u6211\u7684\u6444\u9b42\u5927\u6cd5\uff0c\u5bf9\u4f60\u597d\u50cf\u8fde\u4e00\u70b9\u7528\u4e5f\u6ca1\u6709\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u7684\u786e\u7528\u5904\u4e0d\u5927\u3002\u201d\u3001\u94c1\u59d1\u9053\uff1a\u201c\u5176\u5b9e\u6211\u65e9\u5c31\u8be5\u60f3\u5230\u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u60f3\u5230\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u542c\u8bf4\u4f60\u7684\u6bcd\u4eb2\uff0c\u4ee5\u524d\u4e5f\u662f\u672c\u6559\u4e2d\u7684\u4eba\uff0c\u53ef\u662f\u4e3a\u4e86\u4e00\u4e2a\u59d3\u767d\u7684\uff0c\u4e8c\u5341\u5e74\u524d\u5c31\u5df2 \u53db\u6559\u4e86\u3002\u201d \u3000\u3000\u53f6\u5f00\u76ee\u4e2d\u9732\u51fa\u75db\u82e6\u4e4b\u8272\uff0c\u4ed6\u663e\u7136\u4e0d\u613f\u542c\u522b\u4eba\u63d0\u8d77\u8fd9\u56de\u4e8b\u3002 \u3000\u3000\u6240\u4ee5\u94c1\u59d1\u5c31\u504f\u504f\u8981\u63d0\uff1a\u201c\u9b54\u6559\u4e2d\u6709\u56db\u5927\u5929\u738b\uff0c\u56db\u5927\u516c\u4e3b\uff0c\u4f60\u6bcd\u4eb2\u5c31\u662f\u5176\u4e2d\u4e4b\u4e00\uff0c\u6211\u4e5f\u662f \u5176\u4e2d\u4e4b\u4e00\uff0c\u6240\u4ee5\u4f60\u672c\u8be5\u53eb\u6211\u4e00\u58f0\u59d1\u59d1\u624d\u5bf9\u3002\u201d \u3000\u3000\u53f6\u5f00\u6c89\u7740\u8138\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u8981\u6740\u6211\uff0c\u8fd9\u5f53\u7136\u4e5f\u662f\u5176\u4e2d\u539f\u56e0\u4e4b\u4e00\u3002\u201d \u3000\u3000\u94c1\u59d1\u4e5f\u6c89\u4e0b\u8138\uff0c\u9053\uff1a\u201c\u6211\u4e0d\u5426\u8ba4\uff0c\u672c\u6559\u7684\u53db\u5f92\uff0c\u6ca1\u6709\u4e00\u4e2a\u80fd\u9003\u8131\u95e8\u89c4\u5904\u6cbb\u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u54e6\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u4e0d\u4f46\u5979\u672c\u8eab\u8981\u53d7\u95e8\u89c4\u5904\u5206\uff0c\u5979\u7684\u540e\u4ee3\u4e5f\u4e00\u6837\uff0c\u201d\u53f6\u5f00\u9053\uff1a\u201c\u6211\u53ea\u5e0c\u671b\u4f60\u660e\u767d \u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u4f60\u8bf4\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u5bb6\u6bcd\u65e9\u5df2\u4e0d\u662f\u4f60\u4eec\u9b54\u6559\u4e2d\u4eba\uff0c\u548c\u4f60\u4eec\u518d\u4e5f\u6ca1\u6709\u534a\u70b9\u5173\u7cfb\u3002\u201d \u3000\u3000\u94c1\u59d1\u51b7\u51b7\u9053\uff1a\u201c\u65e0\u8bba\u8c01\u53ea\u8981\u5165\u4e86\u672c\u6559\u4e00\u5929\uff0c\u5c31\u7ec8\u751f\u90fd\u662f\u672c\u6559\u7684\u4eba\uff0c\u8fd9\u79cd\u5173\u7cfb\u6c38\u8fdc\u4e5f\u65a9\u4e0d \u65ad\u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u6de1\u6de1\u9053\uff1a\u201c\u4f60\u65e2\u7136\u662f\u4e2a\u806a\u660e\u4eba\uff0c\u73b0\u5728\u5c31\u4e0d\u8be5\u8bf4\u8fd9\u79cd\u8bdd\u7684\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u73b0\u5728\u4f60\u597d\u50cf\u53ea\u6709\u7b49\u7740\u6211\u6765\u5904\u6cbb\u4f60\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u6211\u8bf4\u8fd9\u4e9b\u8bdd\u4e0d\u8fc7\u8981\u4f60\u660e\u767d\uff0c\u4f60\u7684\u8840\u91cc\u4e5f\u6709\u6211\u4eec\u7684\u8840\uff0c\u53ea\u8981\u4f60\u613f\u610f\u56de\u6765\uff0c\u6211\u4eec \u968f\u65f6\u90fd\u6b22\u8fce\u4f60\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u4f1a\u8bb0\u7740\u7684\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u53ef\u662f\u4ed6\u7edd\u4e0d\u4f1a\u56de\u53bb\u7684\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u4eec\u4e24\u4e2a\u4eba\u90fd\u8981\u540e\u6094\u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u672c\u6559\u8fd9\u6b21\u5728\u795e\u5c71\u7edd\u9876\uff0c\u91cd\u7acb\u5b97\u738b\uff0c\u518d\u5f00\u6559\u95e8\uff0c\u56db\u5927\u5929\u738b\u548c\u56db\u5927\u516c\u4e3b\u7684\u4e09\u9879\u51b3 \u8bae\u4e2d\uff0c\u5176\u4e2d\u6709\u4e00\u6837\u5c31\u662f\u8981\u5904\u6cbb\u53db\u5f92\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u8981\u6211\u5c0f\u5fc3\u4e9b\uff1f\u201d \u3000\u3000\u94c1\u59d1\u51b7\u51b7\u9053\uff1a\u201c\u4e94\u5341\u5e74\u6765\uff0c\u672c\u6559\u4e00\u5171\u53ea\u6709\u4e94\u4e2a\u53db\u5f92\uff0c\u5982\u4eca\u5df2\u6b7b\u4e86\u56db\u4e2a\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u518d\u52a0\u4e0a\u6211\u5c31\u662f\u4e94\u4e2a\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u6211\u597d\u50cf\u5df2\u6b7b\u4e86\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u4f60\u9003\u8fc7\u4e86\u7b2c\u4e00\u6b21\uff0c\u672a\u5fc5\u8fd8\u80fd\u9003\u8fc7\u7b2c\u4e8c\u6b21\uff0c\u5c31\u7b97\u53c8\u9003\u8fc7\u4e86\u7b2c\u4e8c\u6b21\uff0c\u8fd8\u6709\u7b2c\u4e09 \u6b21\u3001\u7b2c\u56db\u6b21\uff0c\u53ea\u8981\u4f60\u4e0d\u6b7b\uff0c\u4f60\u5c31\u5f97\u65f6\u65f6\u523b\u523b\u5730\u63d0\u9632\u7740\uff0c\u6240\u4ee5\u4f60\u5c31\u7b97\u6d3b\u7740\u4e5f\u4f11\u60f3\u8fc7\u4e00\u5929\u5b89\u7a33\u7684 \u65e5\u5b50\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u79e9\u59d1\u9053\uff1a\u201c\u4f60\u4e0d\u5728\u4e4e\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u5f88\u5728\u4e4e\uff0c\u4e5f\u5f88\u6015\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u73b0\u5728\u5c31\u5e26\u7740\u4e0a\u5b98\u5c0f\u4ed9\u8ddf\u6211\u56de\u53bb\uff0c\u5c06\u529f\u62b5\u7f6a\u3002\u201d \u3000\u3000\u53f6\u5f00\u7b11\u4e86\u3002 \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u6211\u8bf4\u7684\u8bdd\u5e76\u4e0d\u597d\u7b11\u3002\u201d \u3000\u3000\u53f6\u5f00\u5fae\u7b11\u7740\uff0c\u9053\uff1a\u201c\u6211\u4e5f\u5f88\u6015\u72d7\u54ac\u6211\uff0c\u96be\u9053\u6211\u5c31\u8be5\u8ddf\u7740\u72d7\u53bb\u5403\u5c4e\uff1f\u201d \u3000\u3000\u4e01\u7075\u7433\u5403\u5403\u5730\u7b11\u4e86\uff0c\u7b11\u5f97\u5f2f\u4e0b\u4e86\u8170\u3002 \u3000\u3000\u94c1\u59d1\u7684\u8138\u8272\u5374\u5df2\u94c1\u9752\u3002 \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u65e9\u5c31\u77e5\u9053\u4f60\u4eec\u8981\u6765\u5bf9\u4ed8\u6211\u4e86\uff0c\u53ef\u662f\u6211\u8fd9\u4e48\u6837\uff0c\u5374\u4e0d\u662f\u4e3a\u4e86\u8981\u5bf9\u4ed8\u4f60\u4eec\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u53f6\u5f00\u6de1\u6de1\u7b11\u9053\uff1a\u201c\u82e5\u662f\u4e3a\u4e86\u5bf9\u4ed8\u4f60\u4eec\uff0c\u6211\u6839\u672c\u5c31\u4e0d\u5fc5\u8d39\u8fd9\u4e48\u591a\u4e8b\u3002\u201d \u3000\u3000\u94c1\u59d1\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u5f53\u7136\u77e5\u9053\u536b\u5929\u9e4f\u548c\u58a8\u767d\u4e5f\u5bf9\u4ed8\u4f60\uff0c\u6240\u4ee5\u4f60\u6545\u610f\u5148\u8ba9\u6211\u4eec\u5f97\u624b\uff0c\u597d\u6559\u4ed6 \u4eec\u8ddf\u6211\u706b\u62fc\uff0c\u7b49\u6211\u4eec\u5148\u81ea\u76f8\u6b8b\u6740\uff0c\u4f60\u624d\u597d\u6697\u7b97\u4e8e\u6211\u3002\u201d \u3000\u3000\u53f6\u5f00\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u82e5\u662f\u4e3a\u5bf9\u4ed8\u536b\u5929\u9e4f\u548c\u58a8\u767d\uff0c\u6211\u66f4\u4e0d\u5fc5\u8d39\u8fd9\u4e48\u5927\u7684\u4e8b\u4e86\u3002\u201d \u3000\u3000\u3002 \u3000\u3000\u4e01\u7075\u7433\u7b11\u9053\uff1a\u201c\u4ed6\u60c5\u613f\u626e\u6210\u4e2a\u5973\u4eba\uff0c\u5b9e\u5728\u4e0d\u662f\u4ef6\u5bb9\u6613\u7684\u4e8b\u3002\u201d \u3000\u3000\u94c1\u59d1\u5fcd\u4e0d\u4f4f\u9053\uff1a\u201c\u4f60\u8fd9\u4e48\u6837\u505a\uff0c\u7a76\u7adf\u662f\u4e3a\u4e86\u8981\u5bf9\u4ed8\u8c01\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u662f\u53e6\u5916\u4e00\u4e2a\u4eba\uff0c\u8fd9\u4e2a\u4eba\u6bd4\u4f60\u4eec\u52a0\u8d77\u6765\u8fd8\u8981\u53ef\u6015\u5f97\u591a\u3002\u201d \u3000\u3000\u94c1\u59d1\u4e0d\u4f4f\u51b7\u7b11\u3002 \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u4eec\u8981\u5230\u8fd9\u91cc\u6765\uff0c\u4f60\u4eec\u672c\u6765\u4e0d\u4f1a\u77e5\u9053\u7684\u3002\u201d \u3000\u3000\u8fd9\u4e00\u70b9\u94c1\u59d1\u5012\u4e0d\u80fd\u4e0d\u627f\u8ba4\u3002 \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u53ef\u662f\u8fd9\u4e2a\u4eba\u5374\u77e5\u9053\u4e86\uff0c\u6240\u4ee5\u4ed6\u6545\u610f\u5c06\u6d88\u606f\u6563\u5e03\u51fa\u53bb\uff0c\u8ba9\u4f60\u4eec\u5230\u8fd9\u91cc\u6765\u627e \u6211\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u4ed6\u4e5f\u60f3\u8ba9\u6211\u4eec\u5148\u8ddf\u4f60\u62fc\u4e00\u573a\uff0c\u4ed6\u624d\u6e14\u7fc1\u5f97\u5229\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4e0d\u9519\u3002\uff01\u201d \u3000\u3000\u94c1\u59d1\u663e\u7136\u4e5f\u5df2\u88ab\u6253\u52a8\uff0c\u6c89\u541f\u7740\u9053\uff1a\u201c\u597d\u51e0\u4e2a\u6708\u524d\uff0c\u6211\u4eec\u7684\u786e\u66fe\u7ecf\u63a5\u5230\u4e00\u5c01\u65e0\u5934\u4fe1\uff0c\u4fe1\u4e0a \u8bf4\u7684\uff0c\u662f\u4f60\u8ddf\u4e0a\u5b98\u5c0f\u4ed9\u7684\u79d8\u5bc6\uff0c\u82e5\u4e0d\u662f\u8fd9\u5c01\u4fe1\uff0c\u6211\u4eec\u6839\u672c\u5c31\u4e0d\u4f1a\u60f3\u5230\u6765\u6253\u4f60\u7684\u4e3b\u610f\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u4eec\u63a5\u5230\u4e86\u8fd9\u4e48\u6837\u4e00\u5c01\u4fe1\uff0c\u96be\u9053\u4e00\u70b9\u4e5f\u4e0d\u89c9\u5f97\u5947\u602a\uff1f\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u56e0\u4e3a\u4ed6\u5728\u90a3\u4fe1\u4e0a\u8bf4\uff0c\u4ed6\u662f\u4f60\u7684\u4ec7\u4eba\uff0c\u5bc4\u8fd9\u5c01\u4fe1\u7ed9\u6211\u4eec\uff0c\u4e3a\u7684\u53ea\u4e0d\u8fc7\u662f\u8981\u501f\u6211 \u4eec\u7684\u624b\uff0c\u66ff\u4ed6\u62a5\u4ec7\u3002\u201d \u3000\u3000\u53f6\u5f00\u53f9\u9053\uff1a\u201c\u8fd9\u5012\u4e5f\u4e0d\u80fd\u7b97\u4e0d\u5408\u7406\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u7ecf\u8fc7\u6211\u4eec\u67e5\u8bc1\u540e\uff0c\u53d1\u73b0\u4ed6\u8bf4\u7684\u5e76\u4e0d\u5047\uff0c\u6240\u4ee5\u6211\u4eec\u624d\u51b3\u5b9a\u52a8\u624b\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u58a8\u767d\u3001\u536b\u516b\u592a\u7237\u548c\u6b27\u9633\u57ce\u4e3b\uff0c\u60f3\u5fc5\u4e5f\u56e0\u4e3a\u63a5\u5230\u4e86\u4e00\u5c01\u540c\u6837\u7684\u4fe1\uff0c\u6240\u4ee5\u624d\u51fa\u624b \u7684\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u73b0\u5728\u6211\u624d\u60f3\u5230\uff0c\u4ed6\u5199\u8fd9\u5c01\u4fe1\uff0c\u4e3a\u7684\u53ef\u80fd\u771f\u662f\u8981\u5229\u7528\u6211\u4eec\u6765\u5148\u8ddf\u4f60\u62fc\u4e00\u573a\uff0c\u7136 \u540e\u4ed6\u518d\u6765\u6361\u4fbf\u5b9c\u3002\u201d \u3000\u3000\u53f6\u5f00\u82e6\u7b11\u9053\uff1a\u201c\u4f60\u603b\u7b97\u60f3\u901a\u4e86\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u4f60\u4e5f\u4e0d\u77e5\u9053\u662f\u8c01\u5199\u7684\u8fd9\u5c01\u4fe1\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u8fde\u731c\u90fd\u731c\u4e0d\u51fa\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u4f60\u4eec\u7684\u884c\u52a8\uff0c\u4ed6\u5168\u90fd\u77e5\u9053\u5f97\u4e00\u6e05\u4e8c\u695a\uff0c\u4f46\u4f60\u4eec\u5374\u8fde\u4ed6\u662f\u8c01\u90fd\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6b63\u56e0\u4e3a\u5982\u6b64\uff0c\u6240\u4ee5\u6211\u624d\u89c9\u5f97\u4ed6\u53ef\u6015\u3002\u201d \u3000\u3000\u94c1\u59d1\u53f9\u4e86\u53e3\u6c14\uff0c\u60a0\u7136\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u6211\u4eec\u4e5f\u5b9e\u5728\u5f88\u60f3\u89c1\u89c1\u4ed6\u4e86\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u672c\u6765\u5df2\u7b97\u51c6\u4f60\u4eec\u5f97\u624b\u4e4b\u540e\uff0c\u4ed6\u4e00\u5b9a\u5c31\u4f1a\u51fa\u73b0\u7684\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u4e00\u76f4\u5728\u7b49\u7740\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u4e5f\u5f88\u60f3\u770b\u770b\u4ed6\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u6211\u4eec\u7adf\u5728\u65e0\u610f\u4e2d\u63ed\u7a7f\u4e86\u4f60\u7684\u79d8\u5bc6\uff0c\u6240\u4ee5\u4f60\u4e5f\u7b49\u4e0d\u4e0b\u53bb\u4e86\u3002\u201d \u3000\u3000\u53f6\u5f00\u53f9\u9053\uff1a\u201c\u4f60\u8ba4\u4e3a\u4ed6\u73b0\u5728\u5df2\u4e0d\u80af\u6765\u4e86\uff1f\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u770b\u6765\u662f\u8fd9\u6837\u3002\u201d \u3000\u3000\u53f6\u5f00\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4ed6\u597d\u50cf\u4e0d\u613f\u5f53\u9762\u89c1\u6211\uff0c\u5426\u5219\u53c8\u4f55\u5fc5\u7b49\u5230\u73b0\u5728\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u73b0\u5728\u5c31\u7b97\u518d\u7b49\u4e0b\u53bb\uff0c\u4e5f\u6ca1\u6709\u7528\u4e86\u3002\u201d \u3000\u3000\u53f6\u5f00\u627f\u8ba4\u3002\u94c1\u59d1\u5ffd\u7136\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u4f60\u73b0\u5728\u4e3a\u4ec0\u4e48\u8fd8\u4e0d\u8d70\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u8fdf\u65e9\u6211\u603b\u4f1a\u8d70\u7684\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u4f60\u6700\u597d\u5feb\u8d70\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u54e6\uff01\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u5e26\u7740\u4f60\u7684\u4e24\u4e2a\u5973\u4eba\u4e00\u8d77\u8d70\uff0c\u6211\u4fdd\u8bc1\u4ee5\u540e\u7edd\u4e0d\u518d\u627e\u4f60\u4eec\u3002\u201d \u3000\u3000\u53f6\u5f00\u4e5f\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u4f60\u96be\u9053\u5c31\u53eb\u6211\u8fd9\u4e48\u6837\u4e00\u8d70\u4e86\u4e4b\uff1f\u201d \u3000\u3000\u94c1\u59d1\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u8d70\u53c8\u80fd\u600e\u4e48\u6837\uff1f\u96be\u9053\u8fd8\u80fd\u6740\u4e86\u6211\uff1f\u201d \u3000\u3000\u53f6\u5f00\u5fae\u7b11\u9053\uff1a\u201c\u9b54\u6559\u4e2d\u7684\u4eba\uff0c\u662f\u4e0d\u662f\u6740\u4e0d\u5f97\u7684\uff1f\u201d \u3000\u3000\u94c1\u59d1\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u82e5\u4e00\u5b9a\u8981\u548c\u672c\u6559\u4f5c\u5bf9\uff0c\u6211\u4e5f\u5e76\u65e0\u6240\u8c13\uff0c\u53ea\u4e0d\u8fc7\u6211\u4e5f\u53ef\u4ee5\u4fdd\u8bc1\uff0c\u65e0\u8bba\u8c01 \u548c\u672c\u6559\u4f5c\u5bf9\uff0c\u90fd\u7edd\u4e0d\u4f1a\u6709\u4ec0\u4e48\u597d\u4e0b\u573a\u3002\u201d \u3000\u3000\u53f6\u5f00\u53c8\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8fd9\u5012\u4e0d\u5047\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u4f60\u82e5\u6740\u4e86\u672c\u6559\u4e2d\u7684\u4e00\u4e2a\u4eba\uff0c\u6211\u4fdd\u8bc1\u4f60\u4eec\u4ece\u6b64\u4ee5\u540e\uff0c\u518d\u4e5f\u4f11\u60f3\u8fc7\u4e00\u5929\u592a\u5e73\u65e5 \u5b50\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u82e5\u653e\u4e86\u4f60\u5462\uff1f\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u6211\u521a\u624d\u5df2\u7b54\u5e94\u8fc7\u4f60\uff0c\u4ece\u6b64\u4ee5\u540e\uff0c\u4f60\u4eec\u65e0\u8bba\u5230\u54ea\u91cc\u53bb\uff0c\u672c\u6559\u4e2d\u7684\u4eba\u90fd\u7edd\u4e0d\u4f1a\u518d \u53bb\u627e\u4f60\u3002\u201d \u3000\u3000\u53f6\u5f00\u6c89\u541f\u7740\uff0c\u9053\uff1a\u201c\u8fd9\u6761\u4ef6\u597d\u50cf\u8fd8\u4e0d\u574f\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u5e94\u8be5\u8003\u8651\u8003\u8651\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u53ef\u662f\u4f60\u521a\u624d\u8fd8\u8981\u6211\u4eec\u8ddf\u7740\u4f60\u56de\u53bb\u7684\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u73b0\u5728\u6211\u5df2\u6539\u53d8\u4e86\u4e3b\u610f\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u7684\u4e3b\u610f\u65e2\u7136\u968f\u65f6\u90fd\u4f1a\u6539\u53d8\uff0c\u6211\u53c8\u600e\u4e48\u80fd\u76f8\u4fe1\u4f60\u7684\u8bdd\uff1f\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u4f60\u53ea\u597d\u76f8\u4fe1\u3002\u201d \u3000\u3000\u53f6\u5f00\u53c8\u7b11\u4e86\u3002 \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u6211\u63d0\u9192\u4f60\uff0c\u8fde\u674e\u5bfb\u6b22\u90fd\u4e0d\u613f\u548c\u672c\u6559\u4f5c\u5bf9\uff0c\u4f55\u51b5\u4f60\u3002\u201d \u3000\u3000\u5979\u51b7\u7b11\u7740\uff0c\u53c8\u9053\uff1a\u201c\u83ab\u5fd8\u8bb0\u4f60\u8fd8\u5e26\u7740\u4e2a\u53ea\u6709\u4e03\u5c81\u5927\u7684\u5b69\u5b50\uff0c\u5c31\u7b97\u4f60\u80fd\u7167\u987e\u81ea\u5df1\uff0c\u5979\u82e5\u4e07 \u4e00\u6709\u4e2a\u4ec0\u4e48\u610f\u5916\uff0c\u4f60\u4e5f\u4e00\u6837\u4e0d\u597d\u4ea4\u5f85\u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u5fcd\u4e0d\u4f4f\u770b\u4e86\u4e0a\u5b98\u5c0f\u4ed9\u4e00\u773c\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u6b63\u5728\u8f7b\u8f7b\u62b1\u7740\u6000\u91cc\u7684\u6ce5\u5a03\u5a03\uff0c\u62ac\u8d77\u5934\u6765\uff0c\u5411\u4ed6\u5ae3\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5b9d\u5b9d\u5df2\u7761\u89c9 \u4e86\uff0c\u521a\u624d\u4f60\u6551\u4e86\u4ed6\uff0c\u73b0\u5728\u6211\u53ef\u4ee5\u8ba9\u4f60\u62b1\u4ed6\u4e00\u4e0b\u3002\u201d \u3000\u3000\u53f6\u5f00\u7728\u4e86\u7728\u773c\uff0c\u9053\uff1a\u201c\u4ed6\u4f1a\u4e0d\u4f1a\u628a\u5c3f\u6492\u5728\u6211\u8eab\u4e0a\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u7b11\u9053\uff1a\u201c\u5b9d\u5b9d\u4e0d\u4f1a\u7684\uff0c\u5b9d\u5b9d\u53c8\u4e56\u53c8\u542c\u8bdd\u3002\u201d \u3000\u3000\u5979\u7adf\u7136\u771f\u7684\u8d70\u8fc7\u6765\uff0c\u5c06\u6ce5\u5a03\u5a03\u4ea4\u7ed9\u4e86\u53f6\u5f00\u3002 \u3000\u3000\u53f6\u5f00\u53ea\u6709\u63a5\u8fc7\u6765\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u6211\u53ea\u62b1\u4e00\u4e0b\u5c31\u591f\u4e86\uff0c\u6211\u4e00\u5411\u5f88\u5bb9\u6613\u77e5\u8db3\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u62c9\u8fc4\u4e86\u4e01\u7075\u7433\u7684\u624b\uff0c\u7b11\u9053\uff1a\u201c\u7b49\u4ed6\u62b1\u8fc7\u4e86\uff0c\u4f60\u4e5f\u53ef\u4ee5\u62b1\u4e00\u4e0b\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u8d76\u7d27\u6447\u5934\uff0c\u9053\uff1a\u201c\u6211\u6628\u5929\u5df2\u7ecf\u62b1\u8fc7\u4ed6\u4e86\uff0c\u8fd9\u4e48\u5f00\u5fc3\u7684\u4e8b\uff0c\u4e0d\u80fd\u5929\u5929\u505a\u7684\uff0c\u5c31\u50cf\u5403 \u7cd6\u4e00\u6837\uff0c\u82e5\u662f\u5929\u5929\u5403\uff0c\u5c31\u2026\u2026\u201d \u3000\u3000\u5979\u7684\u58f0\u97f3\u7a81\u7136\u505c\u987f\uff0c\u8138\u8272\u5df2\u53d8\u4e86\uff0c\u5403\u60ca\u5730\u77aa\u7740\u4e0a\u5b98\u5c0f\u4ed9\uff0c\u5931\u58f0\u9053\uff1a\u201c\u4f60\u2026\u2026\u201d \u3000\u3000\u4e00\u4e2a\u201c\u4f60\u201d\u521a\u8bf4\u51fa\u6765\uff0c\u5979\u4eba\u5df2\u5012\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u53ea\u542c\u90a3\u6ce5\u5a03\u5a03\u809a\u5b50\u91cc\u201c\u6ce2\u201d\u7684\u4e00\u54cd\uff0c\u53f6\u5f00\u7684\u8138\u8272\u4e5f\u53d8\u4e86\uff0c\u7a81\u7136\u5f2f\u4e0b\u8170\u53bb\uff0c\u5c31 \u50cf\u662f\u88ab\u4eba\u5728\u809a\u5b50\u4e0a\u91cd\u91cd\u6253\u4e86\u4e00\u62f3\u3002 \u3000\u3000\u4ed6\u7684\u624b\u5df2\u677e\u5f00\uff0c\u624b\u91cc\u6ce5\u5a03\u5a03\u8dcc\u5728\u5730\u4e0a\uff0c\u201c\u5657\u201d\u7684\u4e00\u58f0\uff0c\u8dcc\u5f97\u7c89\u788e\u3002 \u3000\u3000\u4e00\u4ef6\u4eae\u4eae\u7684\u4e1c\u897f\u4ece\u7c89\u788e\u7684\u6ce5\u5a03\u5a03\u809a\u5b50\u91cc\u6eda\u51fa\u6765\uff0c\u7adf\u662f\u4e2a\u6253\u9020\u5f97\u6781\u7cbe\u5de7\u7684\u673a\u7c27\u6697\u5668\u94a2\u7b52\u3002 \u3000\u3000\u53f6\u5f00\u53cc\u624b\u63a5\u7740\u809a\u5b50\uff0c\u6ee1\u8138\u51b7\u6c57\u6eda\u6eda\u800c\u843d\uff0c\u60f3\u8bf4\u8bdd\uff0c\u5374\u8fde\u4e00\u4e2a\u5b57\u4e5f\u8bf4\u4e0d\u51fa\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u5658\u7740\u5c0f\u5634\u9053\uff1a\u201c\u4f60\u770b\u4f60\uff0c\u6454\u7834\u4e86\u6211\u7684\u5b9d\u5b9d\uff0c\u96be\u602a\u4f60\u809a\u5b50\u8981\u75db\u4e86\u3002\u201d \u3000\u3000\u53f6\u5f00\u770b\u7740\u4ed6\uff0c\u773c\u775b\u91cc\u5145\u6ee1\u4e86\u6050\u60e7\u548c\u60ca\u8bb6\uff0c\u7a81\u7136\u5927\u543c\u4e00\u58f0\uff1a\u201c\u4f60\u2026\u2026\u201d \u3000\u3000\u8fd9\u4e2a\u5b57\u6ca1\u8bf4\u5b8c\uff0c\u4ed6\u4eba\u4e5f\u5df2\u5012\u4e0b\u3002 \u3000\u3000\u94c1\u59d1\u7684\u8138\u8272\u4e5f\u53d8\u4e86\uff0c\u8fd9\u53d8\u5316\u5b9e\u5728\u8fde\u5979\u90fd\u89c9\u5f97\u5927\u5403\u4e00\u60ca\u3002 \u3000\u3000\u53ea\u6709\u6768\u5929\u5374\u8fd8\u662f\u9762\u5e26\u7740\u5fae\u7b11\uff0c\u7528\u4e00\u53ea\u624b\u6402\u7740\u5fc3\u59d1\u7684\u8170\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u4e5f\u7b11\u4e86\uff0c\u7b11\u5f97\u53c8\u751c\u871c\uff0c\u53c8\u5a07\u5a9a\uff0c\u8138\u4e0a\u90a3\u79cd\u75f4\u75f4\u5446\u5446\u7684\u8868\u60c5\uff0c\u5df2\u5b8c\u5168\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u94c1\u59d1\u5fcd\u4e0d\u4f4f\u53f9\u4e86\u53e3\u6c14\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u662f\u4f60\uff0c\u539f\u6765\u662f\u4f60\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u5a07\u7b11\u9053\uff1a\u201c\u8fde\u4f60\u4e5f\u60f3\u4e0d\u5230\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff0c\u201c\u6211\u5b9e\u5728\u8fde\u505a\u68a6\u90fd\u60f3\u4e0d\u5230\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4f60\u4e5f\u4f69\u670d\u6211\u3002\u201d \u3000\u3000\u94c1\u59d1\u82e6\u7b11\u9053\uff1a\u201c\u770b\u6765\u6211\u60f3\u4e0d\u4f69\u670d\u90fd\u5f88\u96be\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u62cd\u624b\u7b11\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u5c45\u7136\u4e5f\u6709\u4eba\u4f69\u670d\u6211\uff0c\u6211\u7b80\u76f4\u5f00\u5fc3\u6b7b\u4e86\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u53f6\u5f00\u4e00\u5b9a\u66f4\u4f69\u670d\u4f60\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u4ed6\u4e00\u5fc3\u4e00\u610f\u5730\u4fdd\u62a4\u4f60\uff0c\u60f3\u4e0d\u5230\u4f60\u6839\u672c\u7adf\u7528\u4e0d\u7740\u4ed6\u6765\u4fdd\u62a4\uff0c\u4ed6\u4e00\u5fc3\u60f3\u627e\u51fa\u90a3\u4e2a \u4e3b\u8c0b\u8981\u5bb3\u4f60\u7684\u4eba\uff0c\u60f3\u4e0d\u5230\u8fd9\u4e2a\u4eba\u5c31\u662f\u4f60\u81ea\u5df1\u3002\u201d \u3000\u3000\u5979\u53c8\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u53f6\u5f00\u5440\u53f6\u5f00\uff0c\u4f60\u81ea\u4ee5\u4e3a\u806a\u660e\u7edd\u9876\uff0c\u81ea\u4ee5\u4e3a\u4e86\u4e0d\u8d77\uff0c\u5176\u5b9e\u4f60\u8fde\u4eba\u5bb6 \u4e00\u6839\u624b\u6307\u5934\u90fd\u6bd4\u4e0d\u4e0a\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u7b11\u9053\uff1a\u201c\u4f60\u96be\u9053\u5fd8\u4e86\u6211\u662f\u4ec0\u4e48\u4eba\u7684\u5973\u513f\uff1f\u201d \u3000\u3000\u94c1\u59d1\u7b11\u9053\uff1a\u201c\u6211\u65e9\u5c31\u8be5\u60f3\u5230\u7684\u3002\u201d \u3000\u3000\u5979\u7684\u786e\u65e9\u5c31\u8be5\u60f3\u5230\u7684\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u548c\u6797\u4ed9\u513f\u7684\u5973\u513f\uff0c\u53c8\u600e\u4e48\u4f1a\u662f\u4e2a\u81ea\u75f4\u3002 \u3000\u3000\u66d9\u8272\u521a\u521a\u964d\u4e34\uff0c\u706f\u5149\u5df2\u6697\u6de1\u4e0b\u6765\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u7684\u773c\u775b\u5374\u66f4\u4eae\uff0c\u73b0\u5728\u65e0\u8bba\u8c01\u90fd\u5df2\u770b\u5f97\u51fa\uff0c\u5979\u7edd\u4e0d\u662f\u4e2a\u767d\u75f4\u3002 \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u4ed6\u4eec\u90fd\u4ee5\u4e3a\u4f60\u662f\u5446\u5b50\uff0c\u662f\u767d\u75f4\uff0c\u5374\u4e0d\u77e5\u771f\u6b63\u7684\u81fc\u75f4\u5e76\u4e0d\u662f\u4f60\uff0c\u5728\u4f60\u773c\u775b\u91cc\u770b \u6765\uff0c\u4ed6\u4eec\u624d\u662f\u771f\u6b63\u7684\u767d\u75f4\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4e0d\u662f\u767d\u75f4\u7684\u7537\u4eba\u8fd8\u4e0d\u591a\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u6768\u5929\u4e0d\u662f\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4ed6\u5f53\u7136\u4e0d\u662f\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u53ea\u6709\u4ed6\u77e5\u9053\u4f60\u7684\u79d8\u5bc6\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u7528\u773c\u779f\u7740\u6768\u5929\uff0c\u5a9a\u7b11\u9053\uff1a\u201c\u4e00\u4e2a\u5973\u4eba\u81f3\u5c11\u603b\u5f97\u627e\u4e00\u4e2a\u80fd\u4f7f\u5979\u4f9d\u9760\u7684\u7537\u4eba\uff0c\u5426\u5219 \u5979\u5c82\u4e0d\u592a\u5bc2\u5bde\u4e86\u3002\u201d \u3000\u3000\u94c1\u59d1\u51b7\u7b11\u9053\uff1a\u201c\u770b\u6765\u4f60\u5e76\u6ca1\u6709\u627e\u9519\u4eba\uff0c\u50cf\u4ed6\u8fd9\u6837\u7684\u7537\u4eba\uff0c\u5b9e\u5728\u4e0d\u591a\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u7b11\u5f97\u66f4\u751c\uff0c\u9053\uff1a\u201c\u6211\u7684\u773c\u5149\u4e00\u5411\u90fd\u4e0d\u9519\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u90a3\u5c01\u4fe1\u662f\u4f60\u5199\u7684\uff1f\u8fd8\u662f\u4ed6\u5199\u7684\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u5f53\u7136\u662f\u4ed6\uff0c\u4ed6\u5199\u7684\u5b57\u6bd4\u6211\u6f02\u4eae\u591a\u4e86\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u4f60\u8981\u6211\u4eec\u5230\u8fd9\u91cc\u6765\uff0c\u4e3a\u4e86\u4f60\u627e\u53f6\u5f00\u62fc\u547d\uff0c\u7b49\u6211\u4eec\u4e24\u8d25\u4ff1\u4f24\uff0c\u4f60\u624d\u597d\u5750\u4eab\u5176 \u6210\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u67d4\u58f0\u9053\uff1a\u201c\u6211\u603b\u89c9\u5f97\u8fd9\u4e16\u4e0a\u7684\u4eba\u592a\u6324\u4e86\uff0c\u591a\u6b7b\u51e0\u4e2a\u4e5f\u6ca1\u5173\u7cfb\u3002\u201d \u3000\u3000\u94c1\u59d1\u53f9\u9053\uff1a\u201c\u770b\u6765\u4f60\u8fd9\u8ba1\u5212\u5b9e\u5728\u662f\u5929\u8863\u65e0\u7f1d\uff0c\u795e\u51fa\u9b3c\u6ca1\uff0c\u96be\u602a\u53f6\u5f00\u4e0a\u4e86\u4f60\u7684\u5f53\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u8981\u4ed6\u4e0a\u5f53\uff0c\u7684\u786e\u5e76\u4e0d\u662f\u4ef6\u5bb9\u6613\u4e8b\u3002\u201d \u3000\u3000\u94c1\u59d1\u7a81\u7136\u51b7\u7b11\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u4f60\u8fd8\u662f\u505a\u9517\u4e86\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u94c1\u59d1\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u4e0d\u8be5\u628a\u6211\u4eec\u4e5f\u62c9\u8fdb\u8fd9\u5708\u6d51\u6c34\u91cc\u7684\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u6211\u8bf4\u8fc7\uff0c\u65e0\u8bba\u8c01\u8981\u8ddf\u672c\u6559\u4f5c\u5bf9\uff0c\u90fd\u7edd\u6ca1\u6709\u4ec0\u4e48\u597d\u5904\uff0c\u4f60\u4e5f\u4e0d\u4f8b\u5916\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u77aa\u7740\u773c\uff0c\u9053\uff1a\u201c\u8c01\u8bf4\u6211\u8981\u8ddf\u4f60\u4eec\u4f5c\u5bf9\u7684\uff1f\u6211\u6839\u672c\u5c31\u6ca1\u6709\u8fd9\u610f\u601d\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u4f60\u771f\u7684\u6ca1\u6709\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6211\u5f53\u7136\u6ca1\u6709\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u53ef\u662f\u4f60\u2026\u2026\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u6253\u65ad\u4e86\u5979\u7684\u8bdd\uff0c\u9053\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u4f60\u4eec\u7684\u9b54\u6559\u6700\u8fd1\u8ddf\u4e00\u4e2a\u4eba\u6709\u4e86\u5bc6\u7ea6\uff1f\u201d \u3000\u3000\u94c1\u59d1\u7684\u8138\u8272\u53c8\u53d8\u4e86\u3002 \u3000\u3000\u5979\u5f53\u7136\u77e5\u9053\uff0c\u4f46\u5979\u5374\u60f3\u4e0d\u51fa\u4e0a\u5b98\u5c0f\u4ed9\u600e\u4e48\u4f1a\u77e5\u9053\u7684\uff0c\u8fd9\u672c\u662f\u4e2a\u6781\u5927\u7684\u79d8\u5bc6\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u70b9\u4e86\u70b9\u5934\uff0c\u53c8\u9053\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u8ddf\u4f60\u4eec\u9b54\u6559\u8ba2\u7ea6\u7684\u90a3\u4e2a\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u94c1\u59d1\u7684\u773c\u775b\u7a81\u7136\u4eae\u4e86\u8d77\u6765\uff1a\u201c\u8fd9\u4e2a\u4eba\u96be\u9053\u5c31\u662f\u4f60\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u5ae3\u7136\u9053\uff1a\u201c\u5176\u5b9e\u4f60\u65e9\u5c31\u8be5\u60f3\u5230\u7684\u3002\u201d \u3000\u3000\u94c1\u59d1\u82e6\u7b11\u9053\uff1a\u201c\u6211\u8fd8\u662f\u8fde\u505a\u68a6\u90fd\u60f3\u4e0d\u5230\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4f60\u81f3\u5c11\u603b\u8be5\u77e5\u9053\uff0c\u4f60\u4eec\u7684\u9b54\u6559\u56db\u5927\u5929\u738b\u662f\u591a\u7cbe\u660e\u3001\u591a\u5389\u5bb3\u7684\u4eba\u3002\u201d \u3000\u3000\u94c1\u59d1\u627f\u8ba4\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4e0d\u662f\u6211\u4eec\u65e9\u5df2\u6709\u4e86\u5bc6\u7ea6\uff0c\u4ed6\u4eec\u53c8\u600e\u4e48\u4f1a\u4e3a\u4e86\u4e00\u5c01\u65e0\u5934\u4fe1\u800c\u52b3\u5e08\u52a8\u4f17\uff01\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u4ed6\u4eec\u96be\u9053\u65e9\u5df2\u77e5\u9053\u90a3\u5c01\u4fe1\u662f\u4f60\u5199\u7684\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u6b63\u8272\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u6b63\u662f\u6211\u4eec\u65e9\u5c31\u5546\u91cf\u597d\u4e86\u7684\uff0c\u4ed6\u600e\u4e48\u4f1a\u4e0d\u77e5\u9053\uff1f\u201d \u3000\u3000\u94c1\u59d1\u4e5f\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u4f60\u505a\u7684\u4e8b\uff0c\u597d\u50cf\u6bcf\u4ef6\u90fd\u662f\u522b\u4eba\u8fde\u505a\u68a6\u90fd\u60f3\u4e0d\u5230\u7684\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u5ae3\u7136\u9053\uff1a\u201c\u6211\u82e5\u4e0d\u662f\u8fd9\u6837\u4e00\u4e2a\u4eba\uff0c\u4f60\u4eec\u7684\u9b54\u6559\u53c8\u600e\u4e48\u80af\u8ddf\u6211\u8ba2\u653b\u5b88\u540c\u76df\u7684\u5bc6 \u7ea6\u3002\u201d \u3000\u3000\u5fc3\u59d1\u5fcd\u4e0d\u4f4f\u9053\uff1a\u201c\u6211\u4eec\u65e2\u7136\u662f\u670b\u53cb\uff0c\u4f60\u4e3a\u4ec0\u4e48\u8fd8\u4e0d\u653e\u4e86\u6211\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u7b11\u9053\uff1a\u201c\u4f60\u770b\u6211\uff0c\u7adf\u5dee\u70b9\u628a\u4f60\u5fd8\u4e86\u3002\u201d \u3000\u3000\u5fc3\u59d1\u4e5f\u7b11\u9053\uff1a\u201c\u53ea\u8981\u4f60\u73b0\u5728\u80fd\u60f3\u8d77\u6765\uff0c\u5c31\u597d\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6768\u5929\uff0c\u4f60\u4e3a\u4ec0\u4e48\u8fd8\u4e0d\u62cd\u5f00\u8fd9\u4f4d\u59d1\u5a18\u7684\u7a74\u9053\uff1f\u201d \u3000\u3000\u6768\u592d\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u4ed6\u5fae\u7b11\u7740\uff0c\u4e00\u638c\u62cd\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u5fc3\u59d1\u7a81\u7136\u4e00\u58f0\u60e8\u547c\uff0c\u4e00\u53e3\u9c9c\u8840\u968f\u7740\u60ca\u547c\u58f0\u55b7\u4e86\u51fa\u6765\u3002 \u3000\u3000\u8eab\u5b50\u7a81\u7136\u8f6f\u8f6f\u5730\u5f2f\u4e86\u4e0b\u6765\uff0c\u810a\u690e\u7adf\u5df2\u88ab\u4ed6\u4e00\u638c\u751f\u751f\u5730\u62cd\u65ad\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u76b1\u7709\u9053\uff1a\u201c\u6211\u53ea\u4e0d\u8fc7\u8981\u4f60\u62cd\u5f00\u5979\u7684\u7a74\u9053\uff0c\u8c01\u53eb\u4f60\u7528\u8fd9\u4e48\u5927\u529b\u6c14\u7684\u3002\u201d \u3000\u3000\u6768\u5929\u9053\uff1a\u201c\u6211\u5c82\u975e\u5df2\u7ecf\u62cd\u5f00\u4e86\u5979\u7684\u7a74\u9053\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u53ef\u662f\u5979\u4e5f\u88ab\u4f60\u62cd\u6b7b\u4e86\u3002\u201d \u3000\u3000\u6768\u5929\u6de1\u6de1\u9053\uff1a\u201c\u6211\u53ea\u7ba1\u62cd\u5f00\u5979\u7684\u7a74\u9053\uff0c\u5979\u662f\u6b7b\u662f\u6d3b\uff0c\u6211\u7ba1\u4e0d\u7740\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u5ae3\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u8bdd\u4e5f\u5e76\u4e0d\u662f\u5b8c\u5168\u6ca1\u6709\u9053\u7406\u3002\u201d \u3000\u3000\u94c1\u59d1\u7a81\u7136\u51cc\u7a7a\u7ffb\u8eab\uff0c\u60f3\u51b2\u51fa\u53bb\u3002 \u3000\u3000\u53ef\u662f\u5979\u7684\u53bb\u8def\u5df2\u88ab\u4e0a\u5b98\u5c0f\u4ed9\u6321\u4f4f\u3002 \u3000\u3000\u5979\u54ac\u4e86\u54ac\u7259\uff0c\u4e00\u628a\u62c9\u4e0b\u4e86\u81ea\u5df1\u7684\u5934\u53d1\uff0c\u62ac\u8155\u62bd\u51fa\u67c4\u5f2f\u5200\u3002\u5200\u5149\u4e00\u95ea\uff0c\u7adf\u4e0d\u662f\u523a\u5411\u4e0a\u5b98\u5c0f \u4ed9\uff0c\u53cd\u800c\u5411\u5979\u81ea\u5df1\u7684\u80a9\u5934\u523a\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u8c01\u77e5\u4e0a\u5b98\u5c0f\u4ed9\u7684\u8863\u8896\u91cc\u4e5f\u98de\u51fa\u4e86\u6761\u7f0e\u5e26\uff0c\u5ffd\u7136\u95f4\u5c31\u50cf\u6bd2\u86c7\u822c\u7f20\u4f4f\u4e86\u5979\u7684\u624b\u3002 \u3000\u3000\u201c\u6211\u60f3\u6b7b\u4e5f\u4e0d\u884c\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u5f53\u7136\u53ef\u4ee5\u6b7b\uff0c\u4f46\u6211\u5374\u4e0d\u60f3\u6b7b\u5728\u4f60\u624b\u91cc\u3002\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u6211\u5e76\u6ca1\u6709\u8981\u6740\u4f60\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u6de1\u6de1\u9053\uff1a\u201c\u6211\u77e5\u9053\uff0c\u4f60\u53ea\u4e0d\u8fc7\u60f3\u7528\u795e\u5200\u5316\u8840\u3001\u9b54\u8840\u5927\u6cd5\u6765\u5bf9\u4ed8\u6211\u800c\u5df2\uff0c\u4f60\u7684\u8840 \u6cfc\u51fa\u6765\uff0c\u6211\u53ea\u8981\u6cbe\u4e0a\u4e00\u70b9\uff0c\u8fd8\u4e0d\u5982\u88ab\u4f60\u4e00\u5200\u6740\u4e86\u53cd\u800c\u75db\u5feb\u4e9b\u3002\u201d \u3000\u3000\u94c1\u59d1\u53d8\u8272\u9053\uff1a\u201c\u4f60\u4e5f\u77e5\u9053\u9b54\u8840\u5927\u6cd5\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4f60\u4eec\u9b54\u6559\u7684\u5341\u5927\u795e\u529f\uff0c\u6211\u4e0d\u77e5\u9053\u7684\u5012\u8fd8\u4e0d\u591a\u3002\u201d \u3000\u3000\u94c1\u59d1\u7a81\u7136\u5f20\u5634\uff0c\u50cf\u662f\u8981\u54ac\u65ad\u81ea\u5df1\u7684\u820c\u5934\u3002 \u3000\u3000\u53ef\u662f\u5979\u7684\u4e0b\u5df4\u5ffd\u7136\u4e5f\u88ab\u7f20\u4f4f\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u7684\u51fa\u624b\uff0c\u7adf\u4eff\u4f5b\u6bd4\u5979\u7684\u601d\u60f3\u52a8\u5f97\u8fd8\u5feb\u3002 \u3000\u3000\u94c1\u59d1\u7684\u5168\u8eab\u90fd\u5df2\u51b7\u900f\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u53f9\u9053\uff1a\u201c\u6211\u8bf4\u8fc7\uff0c\u4f60\u4eec\u7684\u5341\u5927\u9b54\u529f\uff0c\u5728\u6211\u9762\u524d\u662f\u8fde\u4e00\u70b9\u7528\u90fd\u6ca1\u6709\u7684\uff0c\u6211\u751a\u81f3\u53ef \u4ee5\u8868\u6f14\u4e00\u4e24\u79cd\u7ed9\u4f60\u770b\u770b\u3002\u201d \u3000\u3000\u5979\u5ffd\u7136\u653e\u5f00\u4e86\u94c1\u59d1\u7684\u4e0b\u5df4\uff0c\u593a\u4e0b\u4e86\u90a3\u67c4\u5f2f\u5200\uff0c\u9001\u5230\u81ea\u5df1\u5634\u91cc\uff0c\u7adf\u50cf\u662f\u5403\u7518\u8517\u4e00\u6837\uff0c\u5c06\u8fd9 \u67c4\u5200\u4e00\u622a\u622a\u54ac\u65ad\uff0c\u541e\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u5979\u53c8\u5fae\u7b11\u7740\u9053\uff1a\u201c\u4f60\u770b\uff0c\u4f60\u4eec\u7684\u56bc\u94c1\u5927\u6cd5\uff0c\u6211\u5c82\u975e\u4e5f\u4e00\u6837\u80fd\u7528\u3002\u201d \u3000\u3000\u94c1\u59d1\u8fde\u773c\u73e0\u5b50\u90fd\u4f3c\u5df2\u56e0\u6050\u60e7\u800c\u51f8\u51fa\uff0c\u60ca\u58f0\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u7a76\u7adf\u60f3\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4f60\u81ea\u5df1\u5e94\u8be5\u77e5\u9053\u7684\uff0c\u4e3a\u4ec0\u4e48\u8fd8\u8981\u95ee\u6211\uff1f\u201d \u3000\u3000\u94c1\u59d1\u9053\uff1a\u201c\u4f60\u65e2\u7136\u662f\u9b54\u6559\u7684\u76df\u53cb\uff0c\u4e3a\u4ec0\u4e48\u8981\u5bf9\u6211\u4eec\u4e0b\u6bd2\u624b\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u67d4\u58f0\u9053\uff1a\u201c\u5c31\u56e0\u4e3a\u6211\u662f\u9b54\u6559\u7684\u76df\u53cb\uff0c\u6240\u4ee5\u624d\u60f3\u4e0d\u5230\u6211\u4f1a\u5bf9\u4f60\u4eec\u4e0b\u6bd2\u624b\uff0c\u6240\u4ee5\u6211 \u624d\u53ef\u4ee5\u653e\u5fc3\u6740\u4e86\u4f60\u4eec\u3002\u201d \u3000\u3000\u5979\u5fae\u7b11\u7740\u53c8\u9053\uff1a\u201c\u4f60\u81ea\u5df1\u4e5f\u8bf4\u8fc7\uff0c\u6211\u4eec\u7684\u4e8b\uff0c\u90fd\u662f\u522b\u4eba\u8fde\u505a\u68a6\u90fd\u60f3\u4e0d\u5230\u7684\u3002\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u8fd8\u6ca1\u6709\u8bf4\u5b8c\uff1b\u5979\u5df2\u7a81\u7136\u51fa\u624b\uff0c\u624b\u91cc\u7684\u534a\u622a\u5f2f\u5200\uff0c\u5df2\u523a\u5165\u4e86\u94c1\u59d1\u7684\u54bd\u5589\u3002 \u3000\u3000\u94c1\u59d1\u773c\u73e0\u5b50\u7acb\u523b\u51f8\u51fa\uff0c\u8fde\u4e00\u4e2a\u5b57\u90fd\u6ca1\u6709\u518d\u8bf4\u51fa\u6765\uff0c\u5c31\u5df2\u5012\u4e0b\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u770b\u7740\u5979\u5012\u4e0b\u53bb\uff0c\u8f7b\u8f7b\u53f9\u606f\u9053\uff1a\u201c\u6211\u4ece\u6765\u4e5f\u4e0d\u89c9\u5f97\u6740\u4eba\u662f\u4ef6\u6109\u5feb\u7684\u4e8b\uff0c\u4e3a\u4ec0\u4e48\u504f \u504f\u6709\u5f88\u591a\u4eba\u559c\u6b22\u6740\u4eba\u5462\uff1f\u201d \u3000\u3000\u6768\u5929\u5fae\u7b11\u9053\uff1a\u201c\u56e0\u4e3a\u8fd9\u4e16\u4e0a\u7684\u4eba\u5df2\u5927\u591a\u4e86\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u5ae3\u7136\u9053\uff1a\u201c\u770b\u6765\u8fd9\u4e16\u4e0a\u4e5f\u53ea\u6709\u4f60\u624d\u662f\u6211\u7684\u77e5\u5df1\u3002\u201d \u3000\u3000\u6768\u5929\u9053\uff1a\u201c\u6211\u672c\u6765\u5c31\u662f\u6761\u72d0\u72f8\uff0c\u4f1a\u98de\u7684\u72d0\u72f8\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u7b11\u9053\uff1a\u201c\u8fd9\u5916\u53f7\u8d77\u5f97\u5012\u771f\u4e0d\u9519\u3002\u201d \u3000\u3000\u6768\u5929\u9053\uff1a\u201c\u4e00\u4e2a\u4eba\u7684\u540d\u5b57\u4f1a\u8d77\u9519\uff0c\u5916\u53f7\u5374\u662f\u7edd\u4e0d\u4f1a\u9519\u7684\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u53ef\u662f\u90a3\u5144\u5f1f\u4e24\u4e2a\u4eba\u5374\u5e76\u4e0d\u50cf\u73cd\u73e0\uff0c\u6700\u591a\u4e5f\u53ea\u4e0d\u8fc7\u50cf\u4e24\u4e2a\u571f\u8c46\u800c\u5df2\u3002\u201d \u3000\u3000\u6768\u5929\u5927\u7b11\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u73b0\u5728\u4ed6\u4eec\u4eba\u5462\uff1f\u201d \u3000\u3000\u6768\u5929\u9053\uff1a\u201c\u521a\u624d\u4ed6\u4eec\u8981\u6211\u5e26\u4ed6\u4eec\u5230\u98d8\u9999\u522b\u9662\u53bb\uff0c\u6211\u5c31\u5c06\u4ed6\u4eec\u5e26\u8fdb\u4e86\u68fa\u6750\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u53f9\u9053\uff1a\u201c\u53ef\u60dc\u4e86\u4e24\u53e3\u68fa\u6750\u3002\u201d \u3000\u3000\u6768\u5929\u9053\uff1a\u201c\u7136\u540e\u6211\u5c31\u628a\u4ed6\u4eec\u7684\u65ad\u5251\uff0c\u653e\u5728\u98d8\u9999\u522b\u9662\u5916\u7684\u96ea\u5730\u4e0a\uff0c\u6545\u610f\u8ba9\u97e9\u8d1e\u770b\u89c1\uff0c\u522b\u4eba \u624d\u4f1a\u8ba4\u4e3a\u4ed6\u4eec\u662f\u88ab\u53f6\u5f00\u6740\u4e86\u7684\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u53c8\u7b11\u9053\uff1a\u201c\u4f60\u679c\u7136\u662f\u6761\u72d0\u72f8\u3002\u201d\u6768\u5929\u9053\uff1a\u201c\u4ed6\u4eec\u82e5\u662f\u771f\u5230\u4e86\u98d8\u9999\u522b\u9662\uff0c\u786c\u903c\u7740 \u5192\u724c\u53f6\u5f00\u3001\u4e01\u7075\u7433\u51fa\u624b\uff0c\u628a\u620f\u5c82\u975e\u65e9\u5c31\u63ed\u7a7f\u4e86\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4f60\u5343\u4e07\u83ab\u5c0f\u770b\u4e86\u8fd9\u4f4d\u4e01\u59d1\u5a18\uff0c\u5979\u7684\u529f\u592b\u5f88\u4e0d\u9519\u3002\u201d \u3000\u3000\u6768\u5929\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u6211\u4ece\u6765\u4e5f\u4e0d\u6562\u5c0f\u770b\u4efb\u4f55\u5973\u4eba\u7684\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u53c8\u95ee\uff1a\u201c\u97e9\u8d1e\u5462\uff1f\u201d \u3000\u3000\u6768\u5929\u9053\uff1a\u201c\u4ed6\u60f3\u5fc5\u8fd8\u7ad9\u5728\u90a3\u6885\u6797\u91cc\uff0c\u7b49\u7740\u5fc3\u59d1\u53bb\u6551\u4ed6\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4ed6\u60f3\u5fc5\u5df2\u7b49\u5f97\u6025\u6b7b\u4e86\u3002\u201d \u3000\u3000\u6768\u5929\u7b11\u9053\uff1a\u201c\u4e00\u4e2a\u4eba\u5b64\u5b64\u5355\u5355\u5730\u7ad9\u5728\u96ea\u5730\u91cc\uff0c\u90a3\u6ecb\u5473\u7684\u786e\u4e0d\u597d\u53d7\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u773c\u6ce2\u6d41\u52a8\uff0c\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u8fd8\u4e0d\u53bb\u89e3\u9664\u4ed6\u7684\u75db\u82e6\uff1f\u201d \u3000\u3000\u6768\u5929\u9053\uff1a\u201c\u7528\u4e0d\u7740\u6211\u53bb\uff0c\u4ed6\u81ea\u5df1\u8fdf\u65e9\u4f1a\u66ff\u81ea\u5df1\u89e3\u51b3\u7684\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u53ef\u662f\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u53bb\u8ba9\u4ed6\u5c11\u53d7\u70b9\u7f6a\u5462\uff1f\u4e00\u4e2a\u4eba\u603b\u8be5\u518d\u505a\u4e00\u4e24\u4ef6\u597d\u4e8b\u7684\u3002\u201d \u3000\u3000\u6768\u5929\u9053\uff1a\u201c\u4f60\u8981\u6211\u53bb\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u67d4\u58f0\u9053\uff1a\u201c\u6211\u8981\u4f60\u53bb\uff0c\u6211\u559c\u6b22\u5e38\u5e38\u505a\u597d\u4e8b\u7684\u4eba\u3002\u201d \u3000\u3000\u6768\u5929\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u672c\u6765\u89c4\u5b9a\u81ea\u5df1\uff0c\u4e00\u5929\u6700\u591a\u53ea\u6740\u4e00\u4e2a\u4eba\u7684\uff0c\u4eca\u5929\u770b\u6837\u5b50\u5374\u8981\u7834\u4f8b \u4e86\u3002\u201d"}),showOnHomepage:!0},{name:"\u7b2c\u516b\u7ae0\u3000\u91d1\u94b1\u5e2e\u4e3b",content:(0,s.jsx)(s.Fragment,{children:"\u6768\u5929\u8d70\u4e86\uff0c\u66d9\u8272\u5df2\u7167\u8fdb\u7a97\u6237\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u770b\u7740\u5012\u5728\u5730\u4e0a\u7684\u58a8\u767d\u3001\u536b\u5929\u9e4f\u3001\u5fc3\u59d1\u548c\u94c1\u59d1\uff0c\u8138\u4e0a\u53c8\u9732\u51fa\u751c\u67d4\u7684\u5fae\u7b11\uff0c\u5583\u5583 \u9053\uff1a\u201c\u8fd9\u5730\u65b9\u770b\u6765\u7684\u786e\u5bbd\u655e\u591a\u4e86\u2026\u2026\u201d \u3000\u3000\u66d9\u8272\u7167\u8fdb\u7a97\u6237\uff0c\u8fd9\u4e00\u591c\u867d\u7136\u957f\uff0c\u603b\u7b97\u5df2\u8fc7\u53bb\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u4fef\u4e0b\u8eab\uff0c\u8f7b\u8f7b\u6447\u7740\u53f6\u5f00\u7684\u8eab\u5b50\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u5929\u65e9\u5df2\u4eae\u4e86\uff0c\u4f60\u8fd9\u61d2\u866b\u8fd8\u4e0d\u8d77 \u6765\uff1f\u201d \u3000\u3000\u53f6\u5f00\u547b\u541f\u4e86\u4e00\u58f0\uff0c\u7adf\u771f\u7684\u5f20\u5f00\u773c\u6674\uff0c\u832b\u7136\u56db\u4e0b\u671b\u4e86\u4e00\u773c\uff0c\u4eff\u4f5b\u60f3\u6323\u624e\u7740\u7ad9\u8d77\u6765\uff0c\u53c8\u8dcc \u5012\uff0c\u4ed6\u5168\u8eab\u5df2\u8fde\u4e00\u70b9\u529b\u6c14\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u770b\u7740\u4ed6\uff0c\u773c\u775b\u91cc\u5145\u6ee1\u4e86\u5173\u6000\uff0c\u9053\uff1a\u201c\u4f60\u4e0d\u8212\u670d\uff1f\u201d \u3000\u3000\u53f6\u5f00\u70b9\u70b9\u5934\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u6211\u597d\u50cf\u75c5\u4e86\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4ec0\u4e48\u75c5\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u7b28\u75c5\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u7b11\u9053\uff1a\u201c\u7b28\u4e5f\u662f\u75c5\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4e0d\u4f46\u662f\u75c5\uff0c\u800c\u4e14\u662f\u79cd\u5f88\u5389\u5bb3\u7684\u75c5\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u55ef\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u72d7\u718a\u4ed6\u5976\u5976\u662f\u600e\u4e48\u6b7b\u7684\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u662f\u7b28\u6b7b\u7684\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u7b11\u9053\uff1a\u201c\u600e\u4e48\u4f1a\u6709\u7b28\u6b7b\u7684\u4eba\uff1f\u201d \u3000\u3000\u53f6\u5f00\u53f9\u9053\uff1a\u201c\u6211\u672c\u6765\u4e5f\u4e0d\u76f8\u4fe1\uff0c\u73b0\u5728\u624d\u77e5\u9053\uff0c\u8fd9\u4e16\u4e0a\u7b28\u6b7b\u7684\u4eba\u597d\u50cf\u5e76\u4e0d\u5c11\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4f60\u6015\u4f60\u81ea\u5df1\u4e5f\u4f1a\u7b28\u6b7b\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u5df2\u7ecf\u75c5\u5f97\u5f88\u5389\u5bb3\u4e86\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u53f9\u9053\uff1a\u201c\u5176\u5b9e\u4f60\u5e76\u4e0d\u7b28\uff0c\u53ea\u4e0d\u8fc7\u5fc3\u592a\u8f6f\u4e86\u4e00\u70b9\u800c\u5df2\u3002\u201d \u3000\u3000\u53f6\u5f00\u82e6\u7b11\u9053\uff1a\u201c\u82e5\u662f\u5fc3\u4e0d\u8f6f\uff0c\u6211\u600e\u4e48\u4f1a\u66ff\u4eba\u5bb6\u62b1\u6ce5\u5a03\u5a03\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u90a3\u4e0d\u662f\u6ce5\u5a03\u5a03\uff0c\u90a3\u662f\u6211\u7684\u597d\u5b9d\u5b9d\uff0c\u4e56\u5b9d\u5b9d\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4ed6\u597d\u50cf\u5e76\u4e0d\u4e56\uff0c\u4ed6\u4f1a\u54ac\u4eba\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u4e5f\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u4f46\u662f\u4ed6\u5e76\u4e0d\u60f3\u771f\u7684\u54ac\u6b7b\u4f60\uff0c\u5426\u5219\u4f60\u7528\u4e0d\u7740\u7b49\u5230\u7b28\u6b7b\u5df2\u7ecf\u88ab\u6bd2\u6b7b \u4e86\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u628a\u5b83\u4ea4\u7ed9\u6211\u7684\u65f6\u5019\uff0c\u5df2\u626d\u5f00\u4e86\u5b83\u809a\u5b50\u91cc\u7684\u673a\u7c27\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u5e76\u6ca1\u6709\u5b8c\u5168\u626d\u5f00\uff0c\u53ea\u5f00\u4e86\u4e00\u534a\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u7b49\u6211\u770b\u89c1\u4e01\u7075\u7433\u5012\u4e0b\u53bb\uff0c\u624b\u4e0a\u4e00\u7528\u529b\uff0c\u673a\u7c27\u5b8c\u5168\u5f00\u4e86\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u7b11\u9053\uff1a\u201c\u4ed6\u867d\u7136\u53ee\u4e86\u4f60\u4e00\u4e0b\uff0c\u53ef\u662f\u4f60\u4e5f\u62a5\u4e86\u4ec7\u3002\u201d \u3000\u3000\u5979\u6307\u7740\u5730\u4e0a\u7834\u788e\u7684\u6ce5\u5a03\u5a03\u9053\uff1a\u201c\u4f60\u770b\uff0c\u5b83\u73b0\u5728\u5c82\u975e\u5df2\u7ecf\u88ab\u4f60\u6454\u6b7b\u4e86\u3002\u201d \u3000\u3000\u53f6\u5f00\u6ca1\u6709\u770b\u8fd9\u6ce5\u5a03\u5a03\u3002 \u3000\u3000\u82e5\u6709\u597d\u51e0\u4e2a\u6b7b\u4eba\u5728\u65c1\u8fb9\u65f6\uff0c\u8c01\u4e5f\u4e0d\u4f1a\u53bb\u770b\u6ce5\u5a03\u5a03\u7684\u3002 \u3000\u3000\u770b\u7740\u5730\u4e0a\u7684\u5c38\u8eab\uff0c\u53f6\u5f00\u5fcd\u4e0d\u4f4f\u957f\u53f9\u9053\uff1a\u201c\u770b\u6765\u4f60\u679c\u7136\u4e0d\u6127\u662f\u4e0a\u5b98\u91d1\u8679\u548c\u6797\u4ed9\u513f\u7684\u5973 \u513f\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6797\u4ed9\u513f\u7684\u5fc3\u6bd2\uff0c\u4e0a\u5b98\u91d1\u8679\u7684\u624b\u72e0\uff0c\u8fd9\u4e24\u79cd\u4f18\u70b9\u4f60\u4e00\u4e2a\u4eba\u5c31\u5360\u5168\u4e86\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u5fae\u7b11\u9053\uff1a\u201c\u4f60\u6162\u6162\u5c31\u4f1a\u53d1\u73b0\uff0c\u6211\u522b\u7684\u4f18\u70b9\u8fd8\u5f88\u591a\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u73b0\u5728\u6211\u53ea\u60f3\u95ee\u4f60\u4e00\u53e5\u8bdd\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4f60\u95ee\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u4eba\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u8fd8\u662f\u9762\u4e0d\u6539\u8272\uff0c\u5fae\u7b11\u9053\uff1a\u201c\u5f53\u7136\u662f\u4eba\uff0c\u662f\u4e2a\u5973\u4eba\uff0c\u800c\u4e14\u8fd8\u662f\u4e2a\u5f88\u597d\u770b\u7684\u5973 \u4eba\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u6211\u770b\u4f60\u5e76\u4e0d\u50cf\u662f\u4e2a\u4eba\uff0c\u4eba\u4e0d\u4f1a\u505a\u51fa\u8fd9\u79cd\u4e8b\u6765\u7684\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u8981\u5bb3\u6211\uff0c\u6211\u660e\u767d\uff0c\u56e0\u4e3a\u4f60\u8981\u62a5\u4ec7\uff0c\u56e0\u4e3a\u6211\u6070\u5de7\u662f\u5c0f\u674e\u63a2\u82b1\u7684\u5f1f\u5b50\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u7b11\u9053\uff1a\u201c\u8fd9\u771f\u662f\u5de7\u5f97\u5f88\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f46\u8fd9\u4e9b\u4eba\u5374\u8ddf\u4f60\u5b8c\u5168\u65e0\u51a4\u65e0\u4ec7\uff0c\u4f60\u4e3a\u4ec0\u4e48\u8981\u6740\u4e86\u5979\u4eec\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u56e0\u4e3a\u4e00\u6837\u4e1c\u897f\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4ec0\u4e48\u4e1c\u897f\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4f60\u770b\u8fd9\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5979\u679c\u7136\u62ff\u51fa\u4e86\u4e00\u6837\u4e1c\u897f\uff0c\u9ec4\u6f84\u6f84\u7684\uff0c\u95ea\u7740\u91d1\u5149\u3002 \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u8fd9\u662f\u4e00\u6587\u94b1\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4ec0\u4e48\u94b1\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u91d1\u94b1\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4f60\u770b\u4e0d\u770b\u5f97\u51fa\u94b1\u4e0a\u7684\u5b57\uff1f\u201d \u3000\u3000\u53f6\u5f00\u5f53\u7136\u770b\u5f97\u51fa\uff0c\u94b1\u4e0a\u6709\u56db\u4e2a\u5b57\u3002 \u3000\u3000\u201c\u5f79\u9b3c\u901a\u795e\u3002\u201d \u3000\u3000\u7b2c\u4e00\u7f15\u9633\u5149\u4ece\u7a97\u5916\u7167\u8fdb\u6765\uff0c\u6070\u5de7\u7167\u5728\u8fd9\u679a\u91d1\u94b1\u4e0a\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u7684\u773c\u775b\u91cc\u4e5f\u5728\u95ea\u7740\u5149\uff0c\u9053\uff1a\u201c\u94b1\u80fd\u5f79\u9b3c\uff0c\u4e5f\u80fd\u901a\u795e\uff0c\u4f60\u6162\u6162\u4e5f\u4f1a\u53d1\u73b0\uff0c\u8fd9\u4e16\u4e0a \u7edd\u6ca1\u6709\u6bd4\u94b1\u518d\u597d\u7684\u4e1c\u897f\u4e86\u3002\u201d \u3000\u3000\u53f6\u5f00\u5df2\u8038\u7136\u52a8\u5bb9\uff0c\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u6614\u5e74\u91d1\u94b1\u5e2e\u7684\u6807\u5fd7\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u91d1\u94b1\u5e2e\u662f\u4e0a\u5b98\u91d1\u8679\u521b\u7acb\u7684\uff0c\u6211\u6070\u5de7\u662f\u4e0a\u5b98\u91d1\u8679\u7684\u5973\u513f\u3002\u201d \u3000\u3000\u53f6\u5f00\u53f9\u9053\uff1a\u201c\u771f\u662f\u592a\u5de7\u4e86\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4e0a\u5b98\u91d1\u8679\u867d\u7136\u6b7b\u4e86\uff0c\u6211\u5374\u8fd8\u6ca1\u6709\u6b7b\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u8981\u91cd\u632f\u91d1\u94b1\u5e2e\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6211\u81f3\u5c11\u603b\u4e0d\u80fd\u773c\u770b\u7740\u91d1\u94b1\u5e2e\u5c31\u6b64\u6bc1\u706d\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u4f60\u5df2\u8ba1\u5212\u4e86\u5f88\u4e45\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4e0d\u4f46\u5df2\u8ba1\u5212\u4e86\u5f88\u4e45\uff0c\u800c\u4e14\u8ba1\u5212\u5f97\u5f88\u597d\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u8fde\u6768\u5929\u90fd\u88ab\u4f60\u6536\u4e70\u4e86\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4ed6\u672c\u5c31\u662f\u6761\u72d0\u72f8\uff0c\u4f1a\u98de\u7684\u72d0\u72f8\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4e0d\u4f46\u4f1a\u98de\uff0c\u800c\u4e14\u8fd8\u4f1a\u54ac\u4eba\uff0c\u4e13\u54ac\u670b\u53cb\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u7b11\u4e86\u7b11\u9053\uff1a\u201c\u5e78\u597d\u6211\u5e76\u4e0d\u662f\u4ed6\u7684\u670b\u53cb\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u662f\u4ed6\u7684\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u662f\u4ed6\u7684\u8001\u677f\uff0c\u662f\u4ed6\u7684\u5e2e\u4e3b\u3002\u201d \u3000\u3000\u53f6\u5f00\u52a8\u5bb9\u9053\uff1a\u201c\u4f60\u5df2\u7ecf\u662f\u91d1\u94b1\u5e2e\u7684\u5e2e\u4e3b\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u60a0\u7136\u9053\uff1a\u201c\u7236\u4eb2\u7684\u4e8b\u4e1a\uff0c\u5c82\u975e\u603b\u662f\u7531\u5b50\u5973\u7ee7\u627f\u7684\uff1f\u201d \u3000\u3000\u53f6\u5f00\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u9664\u4e86\u6768\u5929\u5916\uff0c\u4f60\u7684\u4f19\u8ba1\u8fd8\u6709\u591a\u5c11\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4f19\u8ba1\u4e0d\u8ba1\u5176\u6570\uff0c\u5927\u4f19\u8ba1\u5374\u53ea\u6709\u516d\u4e2a\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u516d\u4e2a\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u91d1\u94b1\u5e2e\u7684\u89c4\u77e9\uff0c\u672c\u5e2e\u6709\u4e24\u4f4d\u62a4\u6cd5\uff0c\u56db\u5927\u5802\u4e3b\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u8fd9\u89c4\u77e9\u6211\u4ee5\u524d\u600e\u4e48\u4e0d\u77e5\u9053\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u56e0\u4e3a\u8fd9\u662f\u521a\u8ba2\u7684\u89c4\u77e9\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u662f\u8c01\u8ba2\u7684\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6211\u3002\u201d \u3000\u3000\u53f6\u5f00\u53ea\u6709\u82e6\u7b11\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u73b0\u5728\u56db\u5927\u5802\u4e3b\u6211\u5df2\u627e\u5168\u4e86\uff0c\u6768\u5929\u5c31\u662f\u4e4b\u4e00\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u8fd8\u6709\u4e09\u4e2a\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u7b11\u5f97\u5f88\u795e\u79d8\uff0c\u9053\uff1a\u201c\u4f60\u4ee5\u540e\u603b\u4f1a\u6162\u6162\u77e5\u9053\u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u73b0\u5728\u6211\u731c\u4e0d\u51fa\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4f60\u8fde\u505a\u68a6\u90fd\u60f3\u4e0d\u5230\u3002\u201d \u3000\u3000\u53f6\u5f00\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4e24\u5927\u62a4\u6cd5\u5462\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4e24\u5927\u62a4\u6cd5\u7b49\u4e8e\u662f\u6211\u7684\u5de6\u53f3\u624b\uff0c\u6211\u5f53\u7136\u4e0d\u80fd\u9a6c\u864e\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u53ea\u627e\u5230\u4e00\u4e2a\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u7b11\u5f97\u66f4\u795e\u79d8\uff0c\u9053\uff1a\u201c\u73b0\u5728\u6211\u6b63\u5728\u627e\u7b2c\u4e8c\u4e2a\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u627e\u8c01\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4f60\u3002\u201d \u3000\u3000\u53f6\u5f00\u5927\u7b11\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6211\u5e76\u4e0d\u662f\u5728\u8bf4\u7b11\u8bdd\uff0c\u53ea\u8981\u4f60\u7b54\u5e94\uff0c\u4f60\u5c31\u662f\u91d1\u94b1\u5e2e\u7684\u7b2c\u4e00\u62a4\u6cd5\u3002\u201d \u3000\u3000\u53f6\u5f00\u7b11\u9053\uff1a\u201c\u6211\u82e5\u7b54\u5e94\uff0c\u4f60\u80af\u76f8\u4fe1\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u4e5f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u4e0d\u76f8\u4fe1\u3002\u201d \u3000\u3000\u5979\u51dd\u89c6\u7740\u53f6\u5f00\uff0c\u53f9\u606f\u7740\u53c8\u9053\uff1a\u201c\u4f60\u770b\u6765\u5b9e\u5728\u4e0d\u50cf\u662f\u4e2a\u80fd\u8ba9\u5973\u4eba\u76f8\u4fe1\u7684\u7537\u4eba\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u90a3\u4e48\u6211\u4eec\u8fd9\u4ea4\u6613\u5c82\u975e\u6839\u672c\u5c31\u8c08\u4e0d\u6210\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u53f9\u7740\uff1a\u201c\u6240\u4ee5\u8fd9\u5b9e\u5728\u662f\u4ef6\u5f88\u9057\u61be\u7684\u4e8b\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u53ea\u597d\u6740\u4e86\u6211\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6211\u5e76\u4e0d\u7740\u6025\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u7740\u6025\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4f60\u6025\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4e07\u4e00\u6211\u5ffd\u7136\u53c8\u6709\u4e86\u529b\u6c14\uff0c\u4e00\u4e0b\u5b50\u8df3\u8d77\u6765\u628a\u4f60\u6293\u4f4f\uff0c\u7cca\u91cc\u7cca\u6d82\u628a\u4f60\u5f53\u6ce5\u5a03\u5a03\u6454 \u7834\u4e86\uff0c\u5c82\u975e\u5f88\u4e0d\u597d\u610f\u601d\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u7b11\u9053\uff1a\u201c\u90a3\u5b9e\u5728\u5f88\u4e0d\u597d\u610f\u601d\uff0c\u5e78\u597d\u4f60\u4e0d\u4f1a\u5ffd\u7136\u6709\u529b\u6c14\u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4f60\u4e2d\u7684\u9488\u4e0a\u867d\u7136\u6ca1\u6709\u6bd2\uff0c\u5374\u6709\u8ff7\u836f\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u8ff7\u836f\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4e00\u79cd\u80fd\u8ba9\u4eba\u6d51\u8eab\u8f6f\u7ef5\u7ef5\u7684\u8ff7\u836f\uff0c\u53ea\u6709\u4e00\u53e3\u6c14\u559d\u4e0b\u4e94\u65a4\u9152\u53bb\uff0c\u624d\u80fd\u89e3\u5f97 \u5f00\u3002\u201d \u3000\u3000\u53f6\u5f00\u7b11\u9053\uff1a\u201c\u8fd9\u79cd\u836f\u4e00\u5b9a\u662f\u9152\u9b3c\u505a\u51fa\u6765\u7684\uff0c\u6070\u5de7\u6211\u4e5f\u662f\u4e2a\u9152\u9b3c\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4e0d\u5de7\u7684\u662f\uff0c\u8fd9\u9644\u8fd1\u8fde\u4e00\u4e24\u9152\u90fd\u6ca1\u6709\u3002\u201d \u3000\u3000\u53f6\u5f00\u7684\u7b11\u53c8\u53d8\u6210\u82e6\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u5b9e\u5728\u4e0d\u662f\u4e2a\u597d\u4e3b\u4eba\uff0c\u9152\u4e5f\u4e0d\u4e3a\u5ba2\u4eba\u51c6\u5907\u4e00\u70b9\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u773c\u6ce2\u6d41\u52a8\uff0c\u5a9a\u7b11\u9053\uff1a\u201c\u4f60\u5e94\u8be5\u77e5\u9053\uff0c\u6211\u4e00\u5411\u53ea\u5582\u5976\u7ed9\u522b\u4eba\u5403\u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u53ef\u60dc\u6211\u4e0d\u662f\u6ce5\u5a03\u5a03\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u7b11\u9053\uff1a\u201c\u8c01\u8bf4\u4f60\u4e0d\u662f\uff1f\u6211\u4ee5\u540e\u5c31\u8981\u628a\u4f60\u5f53\u505a\u6211\u7684\u6ce5\u5a03\u5a03\u3002\u201d \u3000\u3000\u5979\u7b11\u5f97\u867d\u751c\uff0c\u53f6\u5f00\u5fc3\u91cc\u5374\u5df2\u53d1\u51b7\u3002 \u3000\u3000\u8981\u662f\u771f\u505a\u4e86\u8fd9\u4e2a\u5973\u4eba\u7684\u6ce5\u5a03\u5a03\uff0c\u90a3\u79cd\u6ecb\u5473\u4e00\u5b9a\u6bd4\u6b7b\u8fd8\u96be\u53d7\u3002\u5c31\u5728\u8fd9\u65f6\uff0c\u4ed6\u770b\u89c1\u6768\u5929\u8d70\u4e86 \u8fdb\u6765\u3002 \u3000\u3000\u6768\u5929\u7684\u8138\u8272\u5f88\u96be\u770b\uff0c\u770b\u6765\u5c31\u50cf\u662f\u4e2a\u5ac9\u5992\u7684\u4e08\u592b\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u76b1\u7740\u7709\u56de\u8fc7\u5934\uff0c\u7acb\u523b\u53c8\u5ae3\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u770b\u6765\u5e76\u4e0d\u50cf\u521a\u6740\u8fc7\u4eba\u7684\u6837\u5b50\uff0c\u4f60\u6740 \u8fc7\u4eba\u4e4b\u540e\uff0c\u603b\u662f\u5f88\u5f00\u5fc3\u7684\u3002\u201d \u3000\u3000\u6768\u5929\u6c89\u7740\u8138\uff0c\u9053\uff1a\u201c\u6211\u5b9e\u5728\u6ca1\u6cd5\u5b50\u5f00\u5fc3\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6768\u5929\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u6ca1\u6709\u4eba\u53ef\u6740\u3002\u2019\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4eba\u5462\uff1f\u201d \u3000\u3000\u6768\u5929\u9053\uff1a\u201c\u4eba\u4e0d\u89c1\u4e86\u3002\u201d \u3000\u3000\u4eba\u4e0d\u89c1\u4e86\uff01 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u53c8\u76b1\u8d77\u4e86\u7709\u9053\uff1a\u201c\u4f60\u662f\u8bf4\uff0c\u97e9\u8d1e\u4e0d\u89c1\u4e86\uff1f\u201d\u6768\u5929\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4ed6\u6574\u4e2a\u4eba\u90fd\u4e0d\u89c1\u4e86\uff1f\u201d \u3000\u3000\u6768\u5929\u9053\uff1a\u201c\u5b8c\u5b8c\u5168\u5168\u7684\u4e0d\u89c1\u4e86\uff0c\u8fde\u4e00\u6839\u9aa8\u5934\u90fd\u6ca1\u6709\u7559\u4e0b\u6765\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u96be\u9053\u4ed6\u5ffd\u7136\u88ab\u4e2a\u5927\u602a\u7269\u541e\u4e86\u4e0b\u53bb\uff1f\u201d \u3000\u3000\u6768\u5929\u9053\uff1a\u201c\u4ed6\u662f\u81ea\u5df1\u8d70\u7684\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4f60\u67e5\u8fc7\u4e86\u96ea\u5730\u4e0a\u7684\u811a\u5370\uff1f\u201d \u3000\u3000\u6768\u5929\u9053\uff1a\u201c\u67e5\u8fc7\u4e09\u904d\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u811a\u5370\u662f\u5f80\u4ec0\u4e48\u5730\u65b9\u53bb\u7684\uff1f\u201d \u3000\u3000\u6768\u5929\u9053\uff1a\u201c\u51fa\u4e86\u6885\u6797\uff0c\u811a\u5370\u5ffd\u7136\u4e0d\u89c1\u4e86\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4f60\u6ca1\u6709\u5230\u9644\u8fd1\u627e\u8fc7\uff1f\u201d \u3000\u3000\u6768\u5929\u9053\uff1a\u201c\u627e\u8fc7\u4e09\u904d\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4f60\u627e\u4e0d\u5230\uff1f\u201d \u3000\u3000\u6768\u5929\u9053\uff1a\u201c\u8fde\u4e00\u6839\u9aa8\u5934\u90fd\u627e\u4e0d\u5230\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u5730\u4e0a\u6709\u6ca1\u6709\u522b\u4eba\u7684\u811a\u5370\uff1f\u201d \u3000\u3000\u6768\u5929\u9053\uff1a\u201c\u8fd8\u662f\u53ea\u6709\u521a\u624d\u51e0\u4e2a\u4eba\u7684\u811a\u5370\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u53ea\u6709\u5fc3\u59d1\u3001\u4e01\u9e9f\u3001\u6211\u4eec\u7684\u811a\u5370\uff1f\u201d \u3000\u3000\u6768\u5929\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6240\u4ee5\u4ed6\u4e5f\u4e0d\u53ef\u80fd\u662f\u88ab\u522b\u4eba\u6740\u4e86\u518d\u67b6\u8d70\u7684\uff1f\u201d \u3000\u3000\u6768\u5929\u9053\uff1a\u201c\u7edd\u4e0d\u53ef\u80fd\u3002\u201d \u3000\u3000\u5728\u5730\u4e0a\u7559\u4e0b\u811a\u5370\u7684\u4eba\uff0c\u73b0\u5728\u90fd\u7edd\u4e0d\u53ef\u80fd\u5230\u90a3\u91cc\u53bb\u6740\u4eba\u3002\u4e0a\u5b98\u5c0f\u4ed9\u6c89\u541f\u7740\u9053\uff1a\u201c\u4ed6\u4e2d\u4e86 \u6bd2\uff0c\u53ea\u8981\u4e00\u8d70\u52a8\uff0c\u7acb\u523b\u5c31\u53ef\u6bd2\u53d1\u81f4\u547d\u3002\u201d \u3000\u3000\u6768\u5929\u9053\uff1a\u201c\u8fd9\u4e0d\u9519\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6240\u4ee5\u6211\u4eec\u672c\u6765\u90fd\u4ee5\u4e3a\u4ed6\u7edd\u4e0d\u6562\u8d70\u52a8\u7684\u3002\u201d \u3000\u3000\u6768\u5929\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u53ef\u662f\u4ed6\u73b0\u5728\u5374\u5df2\u8d70\u4e86\u3002\u201d \u3000\u3000\u6768\u5929\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u5ffd\u7136\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f46\u6211\u4eec\u5374\u9519\u4e86\uff0c\u6211\u4eec\u5168\u90fd\u770b\u9519\u4e86\u4ed6\u3002\u201d \u3000\u3000\u6768\u5929\u540c\u610f\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u53f9\u9053\uff1a\u201c\u539f\u6765\u4ed6\u624d\u662f\u6240\u6709\u7684\u8fd9\u4e9b\u4eba\u91cc\u9762\uff0c\u6700\u4e0d\u597d\u5bf9\u4ed8\u7684\u4e00\u4e2a\u3002\u201d \u3000\u3000\u6768\u5929\u4e5f\u540c\u610f\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u76ee\u5149\u95ea\u52a8\uff0c\u9053\uff1a\u201c\u4ed6\u60f3\u5fc5\u65e9\u5df2\u770b\u7a7f\u8fd9\u4ef6\u4e8b\u6709\u6eaa\u8df7\uff0c\u6240\u4ee5\u6545\u610f\u5047\u88c5\u4e2d\u6bd2\uff0c\u8ba9\u522b\u4eba \u4e0d\u9632\u5907\u4ed6\uff0c\u4ed6\u624d\u597d\u5168\u8eab\u800c\u9000\u3002\u201d \u3000\u3000\u6768\u5929\u9053\uff1a\u201c\u4ed6\u7684\u5916\u53f7\u53eb\u9525\u5b50\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4e00\u4e2a\u4eba\u7684\u5916\u53f7\uff0c\u662f\u7edd\u4e0d\u4f1a\u9519\u7684\u3002\u201d \u3000\u3000\u6768\u5929\u9053\uff1a\u201c\u6240\u4ee5\u65e0\u8bba\u4f60\u5916\u9762\u6709\u591a\u4e48\u539a\u7684\u58f3\uff0c\u4ed6\u90fd\u80fd\u9525\u51fa\u6d1e\u6765\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u6c89\u541f\u7740\uff0c\u5f90\u5f90\u9053\uff1a\u201c\u5bf9\u4ed8\u8fd9\u79cd\u4eba\uff0c\u53ea\u6709\u4e24\u4e2a\u6cd5\u5b50\u3002\u201d \u3000\u3000\u6768\u5929\u5728\u542c\u7740\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u82e5\u4e0d\u80fd\u628a\u4ed6\u62c9\u8fc7\u6765\u505a\u6211\u4eec\u7684\u670b\u53cb\uff0c\u5c31\u5f97\u8d76\u5feb\u6740\u4e86\u4ed6\u3002\u201d \u3000\u3000\u6768\u5929\u9053\uff1a\u201c\u53ef\u60dc\u4ed6\u73b0\u5728\u5df2\u8d70\u4e86\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4e16\u4e0a\u7edd\u6ca1\u6709\u4e00\u4e2a\u4eba\uff0c\u80fd\u7a81\u7136\u4e00\u4e0b\u5b50\u5b8c\u5168\u6d88\u5931\u7684\u3002\u201d \u3000\u3000\u6768\u5929\u9053\uff1a\u201c\u4f46\u662f\u6211\u5374\u627e\u4e0d\u5230\u4ed6\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u627e\u4e0d\u5230\u4ed6\uff0c\u5e76\u4e0d\u8868\u793a\u522b\u4eba\u7535\u627e\u4e0d\u5230\u4ed6\u3002\u201d \u3000\u3000\u5979\u8d70\u8fc7\u53bb\u62cd\u4e86\u62cd\u6768\u5929\u7684\u80a9\uff0c\u5fae\u7b11\u7740\u9053\uff1a\u201c\u83ab\u5fd8\u8bb0\u8fd8\u6709\u6211\u54e9\u3002\u201d \u3000\u3000\u6768\u5929\u9053\uff1a\u201c\u4f60\u8981\u53bb\u627e\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u67d4\u58f0\u9053\uff1a\u201c\u4f60\u4e56\u4e56\u5730\u966a\u5c0f\u53f6\u5728\u8fd9\u91cc\u7b49\u7740\uff0c\u6211\u5e26\u7cd6\u7cd6\u56de\u6765\u7ed9\u4f60\u4eec\u5403\u3002\u201d \u3000\u3000\u6768\u5929\u5750\u4e0b\u6765\uff0c\u5750\u5728\u53f6\u5f00\u5bf9\u9762\u3002 \u3000\u3000\u4ed6\u89c4\u89c4\u77e9\u77e9\u5730\u5750\u5728\u90a3\u91cc\uff0c\u770b\u6765\u771f\u662f\u89c4\u89c4\u77e9\u77e9\u7684\u751f\u610f\u4eba\u3002 \u3000\u3000\u53f6\u5f00\u770b\u7740\u4ed6\uff0c\u5ffd\u7136\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5979\u8bf4\u5979\u8981\u5e26\u7cd6\u56de\u6765\u7ed9\u6211\u4eec\u5403\u3002\u201d \u3000\u3000\u6768\u5929\u9053\uff1a\u201c\u55ef\u3002\u201d \u3000\u3000\u53f6\u5f00\u82e6\u7b11\u9053\uff1a\u201c\u81ea\u4ece\u4e09\u5c81\u4ee5\u540e\uff0c\u6211\u5c31\u6ca1\u6709\u5403\u8fc7\u7cd6\u4e86\u3002\u201d \u3000\u3000\u6768\u5929\u9053\uff1a\u201c\u54e6\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u73b0\u5728\u6211\u53ea\u60f3\u559d\u70b9\u9152\u3002\u201d \u3000\u3000\u6768\u5929\u9053\uff1a\u201c\u4f60\u82e5\u4e0d\u559d\u9152\uff0c\u90a3\u624d\u662f\u602a\u4e8b\u3002\u201d \u3000\u3000\u53f6\u5f00\u7b11\u9053\uff1a\u201c\u4f60\u7684\u786e\u5f88\u4e86\u89e3\u6211\uff0c\u6211\u4eec\u6bd5\u7adf\u662f\u8001\u670b\u53cb\u4e86\u3002\u201d \u3000\u3000\u6768\u5929\u51b7\u51b7\u9053\uff1a\u201c\u50cf\u6211\u8fd9\u6837\u7684\u670b\u53cb\uff0c\u4f60\u5e78\u597d\u8fd8\u6709\u51e0\u4e2a\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4e0d\u7ba1\u4f60\u600e\u4e48\u6837\u5bf9\u6211\uff0c\u6211\u4eec\u6bd5\u7adf\u8fd8\u662f\u8001\u670b\u53cb\uff0c\u670b\u53cb\u8ddf\u9152\u4e00\u6837\uff0c\u90fd\u662f\u8001\u7684\u597d\u3002\u201d \u3000\u3000\u6768\u5929\u9053\uff1a\u201c\u4f60\u771f\u7684\u8fd9\u4e48\u60f3\u559d\u9152\uff1f\u201d \u3000\u3000\u53f6\u5f00\u53f9\u9053\uff1a\u201c\u4f60\u77e5\u9053\uff0c\u6211\u73b0\u5728\u7684\u5fc3\u60c5\u5f88\u4e0d\u597d\u3002\u201d \u3000\u3000\u6768\u5929\u627f\u8ba4\uff1a\u201c\u65e0\u8bba\u8c01\u9047\u7740\u4f60\u8fd9\u79cd\u4e8b\uff0c\u5fc3\u60c5\u90fd\u4e0d\u4f1a\u597d\u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u5fc3\u60c5\u4e0d\u597d\u7684\u4eba\uff0c\u603b\u662f\u60f3\u559d\u70b9\u9152\u7684\u3002\u201d \u3000\u3000\u6768\u5929\u4e5f\u540c\u610f\uff1a\u201c\u9664\u4e86\u559d\u9152\u5916\uff0c\u4f60\u7684\u786e\u5df2\u6ca1\u4ec0\u4e48\u4e8b\u597d\u505a\u7684\u4e86\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u82e5\u770b\u5728\u6211\u4eec\u662f\u8001\u670b\u53cb\u7684\u4efd\u4e0a\uff0c\u5c31\u8be5\u5f04\u70b9\u9152\u7ed9\u6211\u3002\u201d \u3000\u3000\u6768\u5929\u8003\u8651\u7740\u5ffd\u7136\u7ad9\u8d77\u6765\uff0c\u9053\uff1a\u201c\u597d\uff0c\u6211\u53bb\u66ff\u4f60\u627e\u9152\uff0c\u4f60\u6700\u597d\u4e56\u4e56\u5730\u5728\u8fd9\u91cc\u7b49\u7740\uff0c\u83ab\u8981\u60f3 \u9003\u8d70\u3002\u201d \u3000\u3000\u53f6\u5f00\u770b\u7740\u4ed6\u8d70\u51fa\u53bb\uff0c\u773c\u775b\u5df2\u4eae\u4e86\u8d77\u6765\u3002 \u3000\u3000\u4eba\uff0c\u603b\u662f\u6709\u4eba\u6027\u7684\u3002 \u3000\u3000\u4ed6\u5bf9\u8fd9\u4eba\u6027\u5ffd\u7136\u53c8\u5145\u6ee1\u4e86\u5e0c\u671b\uff0c\u53c8\u89c9\u5f97\u6768\u5929\u8fd9\u4e2a\u4eba\u5e76\u4e0d\u80fd\u7b97\u592a\u6768\u5929\u5c45\u7136\u5f88\u5feb\u5c31\u56de\u6765\u4e86\uff0c \u624b\u91cc\u63d0\u7740\u4e2a\u5927\u94dc\u58f6\uff0c\u4efd\u91cf\u597d\u50cf\u5f88\u91cd\u3002 \u3000\u3000\u58f6\u91cc\u7684\u9152\u5c31\u7b97\u6ca1\u6709\u88c5\u6ee1\uff0c\u81f3\u5c11\u4e5f\u6709\u4e94\u516d\u65a4\u3002 \u3000\u3000\u53f6\u5f00\u559d\u9152\u4e00\u5411\u5f88\u5feb\u7684\uff0c\u4ed6\u5df2\u51b3\u5b9a\uff0c\u7b49\u81ea\u5df1\u7684\u529b\u6c14\u6062\u590d\u4e86\u4e4b\u540e\uff0c\u4e5f\u7edd\u4e0d\u5411\u6768\u5929\u62a5\u590d\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u82e5\u662f\u8fd8\u80af\u53bb\u66ff\u4ed6\u7684\u8001\u670b\u53cb\u627e\u9152\u559d\uff0c\u8fd9\u4e2a\u4eba\u603b\u7b97\u8fd8\u4e0d\u662f\u65e0\u53ef\u6551\u836f\u7684\u3002 \u3000\u3000\u6768\u5929\u9053\uff1a\u201c\u4f60\u6ca1\u6709\u9003\u3002\u201d \u3000\u3000\u53f6\u5f00\u7b11\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u77e5\u9053\u9003\u4e0d\u4e86\u7684\u3002\u201d \u3000\u3000\u6768\u5929\u9053\uff1a\u201c\u5f88\u597d\u3002\u201d \u3000\u3000\u4ed6\u628a\u94dc\u58f6\u6446\u5728\u5730\u4e0a\u3002 \u3000\u3000\u53f6\u5f00\u8fde\u7ad9\u90fd\u7ad9\u4e0d\u8d77\u6765\uff0c\u9053\uff1a\u201c\u4f60\u4e0d\u80fd\u9001\u8fc7\u6765\uff1f\u201d \u3000\u3000\u201c\u6211\u8ddf\u4f60\u8fd8\u662f\u8ddd\u79bb\u8fdc\u4e00\u70b9\u597d\u3002\u201d \u3000\u3000\u53f6\u5f00\u53f9\u4e86\u53e3\u6c14\uff0c\u53ea\u597d\u6323\u624e\u7740\u722c\u8fc7\u6765\uff0c\u51d1\u7740\u5634\u53bb\u559d\u4e86\u4e00\u53e3\u3002 \u3000\u3000\u53ea\u559d\u4e86\u4e00\u53e3\u3002 \u3000\u3000\u4ed6\u7684\u8138\u8272\u5ffd\u7136\u53d8\u4e86\uff1a\u201c\u8fd9\u4e0d\u662f\u9152\u3002\u201d \u3000\u3000\u6768\u5929\u51b7\u51b7\u5730\u770b\u7740\u4ed6\uff0c\u8138\u4e0a\u4e00\u70b9\u8868\u60c5\u4e5f\u6ca1\u6709\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u6211\u4eec\u4e5f\u4e0d\u662f\u670b\u53cb\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u4e3a\u4ec0\u4e48\u9a97\u6211\uff1f\u201d \u3000\u3000\u6768\u5929\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u60f3\u770b\u770b\u4f60\u5728\u5730\u4e0a\u722c\u7684\u65f6\u5019\uff0c\u662f\u4ec0\u4e48\u6837\u5b50\u3002\u201d \u3000\u3000\u53f6\u5f00\u8fde\u6307\u5c16\u90fd\u5df2\u51b7\u900f\uff0c\u7b80\u76f4\u6068\u4e0d\u5f97\u4e00\u4e0b\u5b50\u6251\u8fc7\u53bb\uff0c\u628a\u8fd9\u58f6\u51b7\u6c34\uff0c\u5168\u90fd\u704c\u5728\u4ed6\u8116\u5b50\u91cc\u3002 \u3000\u3000\u6768\u5929\u51b7\u7b11\u9053\uff1a\u201c\u8fd9\u53ea\u4e0d\u8fc7\u662f\u58f6\u6c34\u800c\u5df2\uff0c\u6211\u6ca1\u6709\u704c\u4e00\u58f6\u5c3f\u6765\u7ed9\u4f60\u559d\uff0c\u5df2\u7ecf\u662f\u4f60\u7684\u8fd0\u6c14 \u4e86\u3002\u201d \u3000\u3000\u53f6\u5f00\u53c8\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u5b9e\u5728\u4e0d\u61c2\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4f1a\u5982\u6b64\u6068\u6211\uff1f\u201d \u3000\u3000\u6768\u5929\u9053\uff1a\u201c\u6211\u4e00\u5411\u4e0d\u559c\u6b22\u6ce5\u5a03\u5a03\u3002\u201d \u3000\u3000\u53f6\u5f00\u5ffd\u7136\u660e\u767d\u4e86\uff0c\u9053\uff1a\u201c\u4f60\u5728\u5403\u918b\uff1f\u201d \u3000\u3000\u4ed6\u5403\u60ca\u5730\u770b\u7740\u6768\u5929\uff1a\u201c\u4f60\u96be\u9053\u771f\u7684\u559c\u6b22\u4e0a\u5b98\u5c0f\u4ed9\uff1f\u4f60\u96be\u9053\u8fd8\u4e0d\u660e\u767d\u5979\u662f\u4e2a\u4ec0\u4e48\u6837\u7684\u5973 \u4eba\uff1f\u201d \u3000\u3000\u6768\u65e0\u773c\u5185\u7684\u808c\u8089\u5728\u8df3\u52a8\uff0c\u7d27\u63e1\u7740\u53cc\u62f3\uff0c\u4e00\u5b57\u5b57\u9053\uff1a\u201c\u6211\u53ea\u660e\u767d\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u8bf4\u3002\u201d \u3000\u3000\u6768\u5929\u7684\u8138\u53d1\u9752\uff0c\u5389\u58f0\u9053\uff1a\u201c\u53ea\u8981\u4f60\u518d\u5f00\u53e3\u8bf4\u4e00\u4e2a\u5b57\uff0c\u6211\u5c31\u6253\u6389\u4f60\u7684\u6ee1\u5634\u7259\u9f7f\u3002\u201d \u3000\u3000\u5634\u91cc\u82e5\u662f\u6ca1\u6709\u7259\u9f7f\uff0c\u90a3\u6ecb\u5473\u4e5f\u4e0d\u597d\u53d7\u7684\u3002 \u3000\u3000\u53f6\u5f00\u53ea\u6709\u53f9\u606f\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u53d1\u73b0\uff0c\u65e0\u8bba\u591a\u806a\u660e\u7684\u7537\u4eba\u82e5\u662f\u771f\u559c\u6b22\u4e0a\u4e00\u4e2a\u5973\u4eba\u65f6\uff0c\u4ed6\u5728\u8fd9\u4e2a\u5973\u4eba\u9762\u524d\u7acb\u523b\u5c31\u4f1a \u53d8\u6210\u5446\u5b50\u3002 \u3000\u3000\u73b0\u5728\u8be5\u600e\u4e48\u529e\u5462\uff1f\u4e00\u70b9\u529e\u6cd5\u4e5f\u6ca1\u6709\uff0c\u65e0\u8bba\u8c01\u5230\u4e86\u8fd9\u79cd\u65f6\u5019\uff0c\u90fd\u53ea\u6709\u7b49\u7740\u3002 \u3000\u3000\u7b49\u6b7b\uff1f \u3000\u3000\u53f6\u5f00\u53ea\u89c9\u6ee1\u5634\u53d1\u82e6\uff0c\u4ed6\u73b0\u5728\u771f\u7684\u60f3\u559d\u9152\u4e86\u3002 \u3000\u3000\u6768\u5929\u6162\u6162\u5730\u7ad9\u8d77\u6765\uff0c\u63a8\u5f00\u7a97\u5b50\u3002\u7a97\u5916\u7684\u98ce\u597d\u51b7\u3002 \u3000\u3000\u6768\u5929\u957f\u957f\u5730\u5438\u4e86\u53e3\u6c14\uff0c\u7a81\u542c\u4e00\u4e2a\u4eba\u5728\u8eab\u540e\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u5728\u627e\u6211\uff1f\u201d \u7b2c\u4e5d\u7ae0\u3000\u5d69\u9633\u94c1\u5251>> \u53e4\u9f99\u300a\u4e5d\u6708\u9e70\u98de\u300b \u7b2c\u4e5d\u7ae0\u3000\u5d69\u9633\u94c1\u5251 \u3000\u3000\u97e9\u8d1e\uff01 \u3000\u3000\u9525\u5b50\u7adf\u5df2\u5230\u4e86\u4ed6\u7684\u8eab\u540e\u3002 \u3000\u3000\u6768\u5929\u6ca1\u6709\u56de\u5934\uff0c\u8eab\u5b50\u9661\u7136\u62d4\u8d77\uff0c\u51cc\u7a7a\u7ffb\u8eab\uff0c\u8d34\u5728\u5c4b\u9876\u4e0a\u3002 \u3000\u3000\u4ed6\u6ca1\u6709\u770b\u89c1\u97e9\u8d1e\u3002 \u3000\u3000\u95e8\u5916\u5374\u53c8\u6709\u4e00\u4e2a\u4eba\u7684\u58f0\u97f3\u4f20\u8fdb\u6765\uff1a\u201c\u597d\u8f7b\u529f\uff0c\u679c\u7136\u4e0d\u6127\u662f\u98de\u72d0\u3002\u201d \u3000\u3000\u8fd9\u53c8\u662f\u97e9\u8d1e\u7684\u58f0\u97f3\u3002 \u3000\u3000\u6768\u5929\u4e00\u7ffb\u8155\uff0c\u4ece\u8170\u7554\u62ff\u4e0b\u4e86\u6761\u94f6\u5149\u95ea\u95ea\u7684\u7ec3\u5b50\u67aa\uff0c\u5728\u5c4b\u9876\u4e0a\u6ed1\u51fa\u4e00\u4e08\uff0c\u8d34\u7740\u5835\u58c1\u6ed1\u4e0b\uff0c \u6ed1\u5230\u95e8\u540e\uff0c\u7a81\u7136\u6325\u67aa\u51b2\u51fa\u3002 \u3000\u3000\u95e8\u5916\u4e5f\u6ca1\u6709\u4eba\u3002 \u3000\u3000\u53ea\u542c\u8eab\u540e\u4e00\u4e2a\u4eba\u9053\uff1a\u201c\u6211\u5728\u8fd9\u91cc\u3002\u201d \u3000\u3000\u97e9\u8d1e\u7adf\u5df2\u4ece\u5916\u9762\u7ed5\u8fc7\u6765\uff0c\u81ea\u7a97\u5916\u4e00\u63a0\u800c\u5165\uff0c\u53c8\u5230\u4e86\u4ed6\u8eab\u540e\u3002 \u3000\u3000\u6768\u5929\u53cd\u624b\u6325\u67aa\uff0c\u4e00\u6761\u8f6f\u5175\u5203\u7ade\u88ab\u4ed6\u6296\u5f97\u7b14\u76f4\uff0c\u76f4\u523a\u97e9\u8d1e\u54bd\u5589\u3002 \u3000\u3000\u65e0\u8bba\u8c01\u90fd\u770b\u5f97\u51fa\uff0c\u4ed6\u5728\u8fd9\u6761\u7ec3\u5b50\u67aa\u4e0a\u81f3\u5c11\u5df2\u6709\u4e8c\u5341\u5e74\u7684\u529f\u592b\u3002 \u3000\u3000\u8c01\u77e5\u97e9\u8d1e\u7684\u6b66\u529f\uff0c\u7adf\u8fdc\u6bd4\u4ed6\u60f3\u8c61\u4e2d\u7684\u8fd8\u53ef\u6015\u5341\u500d\u3002 \u3000\u3000\u7a81\u7136\u51fa\u624b\uff0c\u5c31\u5df2\u6284\u4f4f\u4e86\u4ed6\u7684\u67aa\u5c16\u3002 \u3000\u3000\u6768\u5929\u60f3\u4e0d\u5230\u8fd9\u4eba\u7684\u51fa\u624b\u7adf\u5982\u6b64\u5feb\uff0c\u731b\u4e00\u632b\u8155\uff0c\u5168\u529b\u593a\u67aa\u3002 \u3000\u3000\u97e9\u8d1e\u7684\u624b\u7adf\u53c8\u7a81\u7136\u677e\u5f00\u3002 \u3000\u3000\u6768\u5929\u91cd\u5fc3\u9aa4\u5931\uff0c\u8e09\u8dc4\u540e\u9000\u3002 \u3000\u3000\u97e9\u8d1e\u5df2\u95ea\u7535\u822c\u5730\u6251\u4e86\u8fc7\u6765\uff0c\u4e00\u4f38\u624b\uff0c\u5c31\u5df2\u70b9\u4e86\u4ed6\u524d\u80f8\u7684\u5927\u7a74\u3002 \u3000\u3000\u53f6\u5f00\u53f9\u4e86\u53e3\u6c14\uff0c\u4ed6\u4e5f\u5b9e\u5728\u60f3\u4e0d\u5230\uff0c\u8fd9\u4e2a\u88ab\u4ed6\u4e00\u62f3\u6253\u6241\u4e86\u9f3b\u5b50\u7684\u4eba\uff0c\u7adf\u6709\u8fd9\u4e48\u9ad8\u7684\u6b66\u529f\u3002 \u3000\u3000\u201c\u7830\u201d\u7684\uff0c\u6768\u5929\u5df2\u91cd\u91cd\u5730\u8dcc\u5728\u5730\u4e0a\uff0c\u97e9\u8d1e\u8fde\u770b\u90fd\u4e0d\u518d\u770b\u4ed6\u4e00\u773c\uff0c\u56de\u8eab\u62c9\u4f4f\u53f6\u5f00\uff0c\u6c89\u58f0 \u9053\uff1a\u201c\u4f60\u8fd8\u80fd\u4e0d\u80fd\u7ad9\u8d77\u6765\uff1f\u201d \u3000\u3000\u53f6\u5f00\u6447\u6447\u5934\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u4f60\u771f\u662f\u6765\u6551\u6211\u7684\uff1f\u201d \u3000\u3000\u97e9\u8d1e\u6c89\u7740\u8138\u6ca1\u5f00\u53e3\uff0c\u62e6\u8170\u628a\u4ed6\u6284\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u201c\u4f60\u5148\u8ddf\u6211\u8d70\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u8fd8\u6709\u4e01\u7075\u7433\u3002\u201d \u3000\u3000\u97e9\u8d1e\u76b1\u4e86\u76b1\u7709\u9053\uff1a\u201c\u4f60\u8fd8\u8981\u5e26\u5979\u8d70\uff1f\u201d \u3000\u3000\u53f6\u5f00\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u521a\u624d\u8fd8\u6709\u4eba\u8bf4\uff0c\u6211\u8fd9\u4eba\u6700\u5927\u7684\u6bdb\u75c5\uff0c\u5c31\u662f\u5fc3\u592a\u8f6f\u3002\u201d \u3000\u3000\u97e9\u8d1e\u51b7\u51b7\u9053\uff1a\u201c\u73b0\u5728\u4f60\u7684\u817f\u4e5f\u5f88\u8f6f\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u5e78\u597d\u5c0f\u4e01\u53ea\u4e0d\u8fc7\u662f\u88ab\u70b9\u4e86\u7a74\u9053\uff0c\u4f60\u53ea\u8981\u62cd\u5f00\u5979\u7684\u7a74\u9053\u5c31\u884c\u4e86\u3002\u201d \u3000\u3000\u4ed6\u8d76\u7d27\u53c8\u7b11\u4e86\u7b11\uff0c\u63a5\u7740\u9053\uff1a\u201c\u53ea\u4e0d\u8fc7\u4f60\u51fa\u624b\u5343\u4e07\u4e0d\u80fd\u50cf\u6768\u5929\u90a3\u4e48\u91cd\uff0c\u6211\u5e76\u4e0d\u60f3\u8981\u4e2a\u6b7b\u8001 \u5a46\u3002\u201d \u3000\u3000\u5730\u5ba4\u91cc\u9634\u6697\u6f6e\u6e7f\uff0c\u800c\u4e14\u51b7\u5f97\u8981\u547d\u3002 \u3000\u3000\u5e78\u597d\u5c4b\u89d2\u8fd8\u6709\u5f20\u6728\u677f\u5e8a\uff0c\u5e8a\u4e0a\u5c45\u7136\u8fd8\u6709\u6761\u68c9\u88ab\u3002 \u3000\u3000\u53f6\u5f00\u5012\u5728\u5e8a\u4e0a\uff0c\u624d\u957f\u957f\u5410\u51fa\u53e3\u6c14\uff0c\u4ed6\u77e5\u9053\u81ea\u5df1\u4e0d\u5fc5\u505a\u4eba\u5bb6\u7684\u6ce5\u5a03\u5a03\u4e86\u3002 \u3000\u3000\u4e01\u7075\u7433\u7528\u529b\u6413\u7740\u624b\uff0c\u9053\uff1a\u201c\u8fd9\u5730\u65b9\u597d\u51b7\u3002\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u51b7\u6bd4\u4e0d\u51b7\u597d\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u56e0\u4e3a\u4f60\u603b\u7b97\u8fd8\u6d3b\u7740\uff0c\u6b7b\u4eba\u5c31\u4e0d\u4f1a\u89c9\u5f97\u51b7\u4e86\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u53f9\u4e86\u53e3\u6c14\uff0c\u51c4\u7136\u7b11\u9053\uff1a\u201c\u4e0d\u7ba1\u600e\u4e48\u6837\uff0c\u80fd\u6d3b\u7740\u603b\u662f\u4e0d\u574f\u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u4e5f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5b9e\u5728\u4e0d\u574f\u3002\u201d \u3000\u3000\u4ed6\u770b\u7740\u97e9\u8d1e\uff0c\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u4f60\u7684\u9f3b\u5b50\u600e\u4e48\u6837\u4e86\uff1f\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u8fd8\u5728\u75bc\u3002\u201d \u3000\u3000\u53f6\u5f00\u82e6\u7b11\u9053\uff1a\u201c\u6211\u7684\u9f3b\u5b50\u82e5\u8fd8\u5728\u75db\u65f6\uff0c\u6211\u5c31\u7edd\u4e0d\u4f1a\u53bb\u6551\u90a3\u4e2a\u6253\u6241\u6211\u9f3b\u5b50\u7684\u4eba\u3002\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u4e5f\u8bb8\u6211\u7684\u5fc3\u6bd4\u4f60\u8fd8\u8f6f\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u5e78\u597d\u4f60\u7684\u5fc3\u5e76\u4e0d\u574f\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u53c8\u95ee\u9053\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u4e00\u4ef6\u4e8b\uff1f\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u89c1\u8fc7\u5f88\u591a\u5f53\u5730\u7684\u6b66\u6797\u9ad8\u624b\uff0c\u90fd\u53ef\u4ee5\u7b97\u662f\u4e00\u7b49\u4e00\u7684\u9ad8\u624b\uff0c\u90a3\u5176\u4e2d\u6b66\u529f\u6700\u9ad8\u7684 \u4e00\u4e2a\u4f60\u77e5\u4e0d\u77e5\u9053\u662f\u8c01\uff1f\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u662f\u6211\uff01\u201d \u3000\u3000\u53f6\u5f00\u53c8\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u4f60\u597d\u50cf\u5e76\u4e0d\u592a\u8c26\u865a\u3002\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u6211\u4e00\u5411\u5f88\u4f46\u767d\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6240\u4ee5\u6211\u5947\u602a\u3002\u201d \u3000\u3000\u53f6\u5f00\u6447\u6447\u5934\uff0c\u53c8\u9053\uff1a\u201c\u5947\u602a\u7684\u4e8b\u5f88\u591a\u3002\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u4f60\u53ef\u4ee5\u4e00\u4ef6\u4ef6\u4ed6\u8bf4\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u5df2\u8d70\u8fc7\u53bb\uff0c\u4f9d\u504e\u5728\u53f6\u5f00\u8eab\u65c1\uff0c\u63e1\u7740\u53f6\u5f00\u7684\u624b\uff0c\u5979\u4e5f\u5728\u542c\u7740\u3002 \u3000\u3000\u53f6\u5f00\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u542c\u8bf4\u4f60\u4e2d\u4e86\u4e00\u52a8\u5c31\u6b7b\u7684\u6bd2\uff0c\u73b0\u5728\u4f60\u52a8\u4e86\uff0c\u5374\u8fd8\u6d3b\u7740\u3002\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u65e0\u8bba\u4ec0\u4e48\u6bd2\uff0c\u90fd\u6709\u89e3\u836f\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u8fde\u9b54\u6559\u7684\u6bd2\u4f60\u4e5f\u80fd\u89e3\uff1f\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u6211\u8fd8\u6d3b\u7740\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6240\u4ee5\u6211\u5728\u5947\u602a\u3002\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u5947\u602a\u6211\u8fd8\u80fd\u6d3b\u7740\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u5947\u602a\u4f60\u6d3b\u5f97\u5e76\u4e0d\u597d\u3002\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u6211\u6d3b\u5f97\u4e3a\u4ec0\u4e48\u4e0d\u597d\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u50cf\u4f60\u8fd9\u6837\u7684\u4eba\uff0c\u672c\u8be5\u6d3b\u5f97\u66f4\u597d\u4e9b\u7684\u3002\u201d \u3000\u3000\u97e9\u8d1e\u6c89\u541f\u7740\uff0c\u9053\uff1a\u201c\u4f60\u662f\u8bf4\uff0c\u6211\u672c\u4e0d\u8be5\u5728\u536b\u5927\u9e4f\u95e8\u4e0b\u8ba8\u996d\u5403\u7684\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u4ed6\u5fae\u7b11\u7740\uff0c\u53c8\u9053\uff1a\u201c\u536b\u5929\u9e4f\u5e76\u4e0d\u662f\u4e2a\u5f88\u597d\u7684\u4e3b\u4eba\uff0c\u4f60\u672c\u4e0d\u8be5\u5982\u6b64\u59d4\u5c48\u81ea\u5df1\uff0c\u66f4\u4e0d\u5e94\u8be5\u7ad9 \u5728\u90a3\u91cc\u6328\u6211\u4e00\u62f3\u7684\u3002\u201d \u3000\u3000\u97e9\u8d1e\u6c89\u9ed8\u7740\uff0c\u4f3c\u5728\u8003\u8651\u6709\u4e9b\u8bdd\u4ed6\u662f\u4e0d\u662f\u5e94\u8be5\u8bf4\u51fa\u6765\u3002 \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u6328\u6211\u90a3\u4e00\u62f3\uff0c\u663e\u7136\u662f\u56e0\u4e3a\u4f60\u4e0d\u613f\u5728\u522b\u4eba\u9762\u524d\u663e\u9732\u4f60\u7684\u6b66\u529f\u3002\u201d \u3000\u3000\u97e9\u8d1e\u7ec8\u4e8e\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6211\u6709\u539f\u56e0\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u77e5\u9053\u5176\u4e2d\u4e00\u5b9a\u6709\u539f\u56e0\u3002\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u6211\u5728\u907f\u4ec7\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u907f\u4ec7\uff1f\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u6211\u7684\u4ec7\u5bb6\u7edd\u5bf9\u60f3\u4e0d\u5230\u6211\u4f1a\u907f\u5728\u536b\u5929\u9e4f\u5bb6\u91cc\u505a\u98df\u5ba2\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u672c\u6765\u7684\u540d\u5b57\u4e0d\u662f\u97e9\u8d1e\uff1f\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u4e0d\u662f\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u7684\u4ec7\u5bb6\u662f\u8c01\uff1f\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u662f\u4e2a\u5f88\u53ef\u6015\u7684\u4eba\u3002\u201d \u3000\u3000\u53f6\u5f00\u53f9\u9053\uff1a\u201c\u6211\u60f3\u5f97\u5230\uff0c\u8fde\u4f60\u8fd9\u79cd\u4eba\u90fd\u5728\u8eb2\u907f\u4ed6\uff0c\u4ed6\u5f53\u7136\u53ef\u6015\u3002\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u4e5f\u8be5\u60f3\u5230\uff0c\u6211\u4e3a\u4ec0\u4e48\u8981\u6551\u4f60\u4e86\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u60f3\u8981\u6211\u52a9\u4f60\u4e00\u81c2\u4e4b\u529b\uff0c\u5bf9\u4ed8\u4f60\u7684\u4ec7\u5bb6\u3002\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u662f\u4e2a\u5f88\u6709\u7528\u7684\u670b\u53cb\uff0c\u4e5f\u662f\u4e2a\u6069\u6028\u5206\u660e\u7684\u4eba\u3002\u201d \u3000\u3000\u53f6\u5f00\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u60f3\u592a\u8c26\u865a\u3002\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u4e00\u4e2a\u6069\u6028\u5206\u660e\u7684\u4eba\uff0c\u4e3a\u4e86\u62a5\u90a3\u6551\u547d\u4e4b\u6069\uff0c\u5f80\u5f80\u4ec0\u4e48\u4e8b\u90fd\u80af\u505a\u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u73b0\u5728\u81f3\u5c11\u5e94\u8be5\u544a\u8bc9\u6211\uff0c\u4f60\u7a76\u7adf\u8981\u6211\u505a\u4ec0\u4e48\u3002\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u4ee5\u540e\u6211\u5f53\u7136\u4f1a\u544a\u8bc9\u4f60\uff0c\u73b0\u5728\u2026\u2026\u201d \u3000\u3000\u4ed6\u7a81\u7136\u6539\u53d8\u8bdd\u9898\uff0c\u9053\uff1a\u201c\u4f60\u53d7\u7684\u4f24\u597d\u50cf\u5e76\u4e0d\u91cd\uff0c\u600e\u4e48\u8fde\u7ad9\u90fd\u7ad9\u4e0d\u8d77\u6765\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u8fd8\u6ca1\u6709\u559d\u9152\u3002\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u73b0\u5728\u4f60\u60f3\u559d\u9152\uff1f\u201d \u3000\u3000\u53f6\u5f00\u5fae\u7b11\u9053\uff1a\u201c\u559d\u4e86\u9152\u4e4b\u540e\uff0c\u6211\u7684\u5fc3\u4e5f\u8bb8\u4f1a\u66f4\u8f6f\uff0c\u817f\u5374\u7edd\u4e0d\u4f1a\u8f6f\u4e86\u3002\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u9152\u80fd\u6cbb\u4f60\u7684\u4f24\uff1f\u201d \u3000\u3000\u53f6\u5f00\u7b11\u9053\uff1a\u201c\u6211\u53d7\u7684\u4f24\u5f88\u7279\u522b\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u5fcd\u4e0d\u4f4f\u63d2\u53e3\u7b11\u9053\uff1a\u201c\u6211\u76f8\u4fe1\u6709\u5f88\u591a\u4eba\uff0c\u4e00\u5b9a\u90fd\u613f\u610f\u53d7\u4f60\u8fd9\u79cd\u4f24\u7684\u3002\u201d \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u597d\uff0c\u6211\u53bb\u66ff\u4f60\u627e\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u9152\u4e0d\u80fd\u5c11\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u7b11\u9053\uff1a\u201c\u4e0b\u9152\u83dc\u4e5f\u4e0d\u80fd\u5c11\uff0c\u6700\u597d\u518d\u627e\u5957\u7537\u4eba\u8863\u670d\u6765\uff0c\u6211\u5b9e\u5728\u770b\u4e0d\u60ef\u4ed6\u8fd9\u79cd\u4e0d\u7537\u4e0d \u5973\u7684\u6837\u5b50\u3002\u201d \u3000\u3000\u97e9\u8d1e\u626b\u4e86\u5979\u4e00\u773c\uff0c\u6de1\u6de1\u9053\uff1a\u201c\u4f60\u7684\u6837\u5b50\u597d\u50cf\u4e5f\u8ddf\u4ed6\u5dee\u4e0d\u591a\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u8138\u7ea2\u4e86\uff0c\u5979\u5ffd\u7136\u60f3\u8d77\u81ea\u5df1\u8eab\u4e0a\u7a7f\u7684\u662f\u5957\u7537\u4eba\u8863\u670d\u3002 \u3000\u3000\u6709\u5f88\u591a\u4eba\u90fd\u662f\u8fd9\u6837\u5b50\u7684\uff0c\u53ea\u80fd\u770b\u5f97\u89c1\u522b\u4eba\u7684\u9519\uff0c\u5374\u5fd8\u4e86\u81ea\u5df1\u7684\u3002 \u3000\u3000\u97e9\u8d1e\u5df2\u8d70\u4e86\uff0c\u8fd9\u5730\u65b9\u53ea\u6709\u4e00\u6247\u95e8\uff0c\u4e0a\u9762\u4e5f\u662f\u51b7\u9999\u56ed\u91cc\u7684\u4e00\u5904\u522b\u9662\uff0c\u97e9\u8d1e\u8ba4\u4e3a\u4e0a\u5b98\u5c0f\u4ed9\u7edd \u5bf9\u60f3\u4e0d\u5230\u4ed6\u4eec\u8fd8\u5728\u51b7\u9999\u56ed\uff0c\u53f6\u5f00\u4e5f\u540c\u610f\u3002 \u3000\u3000\u8d8a\u662f\u660e\u663e\u7684\u5730\u65b9\uff0c\u4eba\u4eec\u53cd\u800c\u8d8a\u4e0d\u4f1a\u7559\u610f\uff0c\u8fd9\u4e5f\u6b63\u662f\u4eba\u7c7b\u7684\u5f31\u70b9\u4e4b\u4e00\u3002 \u3000\u3000\u4e01\u7075\u7433\u53f9\u9053\uff1a\u201c\u9664\u4e86\u6211\u4eec\u4e24\u4e2a\u4eba\u5916\uff0c\u53ea\u6709\u4e0a\u5bab\u5c0f\u4ed9\u77e5\u9053\u6211\u4eec\u7684\u884c\u52a8\uff0c\u6211\u4eec\u672c\u8be5\u60f3\u5230\u6d88\u606f \u662f\u5979\u6545\u610f\u6cc4\u6f0f\u51fa\u53bb\u7684\uff0c\u8fd9\u672c\u662f\u4ef6\u5f88\u660e\u663e\u7684\u4e8b\u3002\u201d \u3000\u3000\u53f6\u5f00\u82e6\u7b11\uff1a\u201c\u4e5f\u8bb8\u5c31\u56e0\u4e3a\u592a\u660e\u663e\u4e86\uff0c\u6240\u4ee5\u6211\u4eec\u624d\u60f3\u4e0d\u5230\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u6211\u4eec\u4e5f\u5e94\u8be5\u60f3\u5230\uff0c\u4e0a\u5b98\u91d1\u8679\u548c\u6797\u4ed9\u513f\u7684\u5973\u513f\u82e5\u662f\u767d\u75f4\uff0c\u5929\u4e0b\u7684\u4eba\u90fd\u5e94\u8be5\u662f \u767d\u75f4\u4e86\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u5979\u4e00\u5b9a\u628a\u6211\u4eec\u770b\u6210\u767d\u75f4\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u770b\u6765\u5979\u597d\u50cf\u6bd4\u5979\u7239\u5a18\u8fd8\u5389\u5bb3\u3002\u201d \u3000\u3000\u53f6\u5f00\u53f9\u9053\uff1a\u201c\u4e0a\u5b98\u91d1\u8679\u5927\u4e13\u6a2a\uff0c\u6797\u4ed9\u513f\u592a\u8f6f\u5f31\uff0c\u8fd9\u4e24\u79cd\u6bdb\u75c5\u5979\u5374\u6ca1\u6709\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u4f46\u5979\u8fd8\u6709\u5f31\u70b9\u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u5979\u82e5\u6ca1\u6709\u5f31\u70b9\u6211\u4eec\u600e\u4e48\u80fd\u5230\u8fd9\u91cc\u6765\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u5979\u552f\u4e00\u505a\u9519\u7684\u4e8b\uff0c\u5c31\u662f\u4f4e\u4f30\u4e86\u97e9\u8d1e\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u6211\u4e0d\u559c\u6b22\u8fd9\u4e2a\u4eba\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4e0d\u559c\u6b22\u97e9\u8d1e\uff1f\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u55ef\u3002\u201d\u201c\u53f6\u5f00\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4ed6\u4e5f\u5e76\u6ca1\u8981\u4f60\u559c\u6b22\u4ed6\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u7728\u4e86\u7728\u773c\u775b\uff0c\u9053\uff1a\u201c\u8fd9\u4e5f\u8bb8\u53ea\u56e0\u4e3a\u4ed6\u77e5\u9053\u6211\u5feb\u8981\u505a\u4f60\u8001\u5a46\u4e86\u3002\u201d \u3000\u3000\u53f6\u5f00\u597d\u50cf\u5403\u4e86\u4e00\u60ca\uff1a\u201c\u4f60\u8bf4\u4ec0\u4e48\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u7b11\u9053\uff1a\u201c\u4f60\u8bf4\u4f60\u4e0d\u60f3\u8981\u4e2a\u6b7b\u8001\u5a46\uff0c\u6211\u73b0\u5728\u5e76\u6ca1\u6709\u6b7b\u3002\u201d \u3000\u3000\u53f6\u5f00\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u8fd9\u4e2a\u4eba\u7684\u8033\u6735\u5012\u8fd8\u771f\u957f\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u6211\u5f53\u65f6\u867d\u7136\u4e0d\u80fd\u52a8\uff0c\u4e5f\u4e0d\u80fd\u8bf4\u8bdd\uff0c\u4f46\u4f60\u4eec\u8bf4\u7684\u8bdd\uff0c\u6211\u6bcf\u53e5\u90fd\u542c\u5f97\u6e05\u6e05\u695a \u695a\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u54e6\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u561f\u8d77\u5634\uff0c\u9053\uff1a\u201c\u90a3\u4e2a\u4eba\u8981\u5582\u4f60\u5403\u5976\u7684\u65f6\u5019\uff0c\u6211\u771f\u6068\u4e0d\u5f97\u54ac\u5979\u4e00\u53e3\u3002\u201d \u3000\u3000\u53f6\u5f00\u53f9\u9053\uff1a\u201c\u8001\u5b9e\u8bf4\uff0c\u6211\u4e5f\u5f88\u60f3\u54ac\u5979\u4e00\u53e3\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u53c8\u7b11\u4e86\uff0c\u5ffd\u7136\u62b1\u4f4f\u4e86\u53f6\u5f00\u7684\u8116\u5b50\uff0c\u8f7b\u8f7b\u9053\uff1a\u201c\u8001\u5b9e\u8bf4\uff0c\u4f60\u51c6\u5907\u5728\u4ec0\u4e48\u65f6\u5019\u5a36 \u6211\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u5728\u4f60\u4e0d\u5403\u918b\u7684\u65f6\u5019\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u7b11\u9053\uff1a\u201c\u50bb\u74dc\uff0c\u5973\u4eba\u82e5\u4e0d\u5403\u918b\uff0c\u5c31\u4e0d\u662f\u5973\u4eba\u4e86\uff0c\u8fd9\u9053\u7406\u4f60\u90fd\u4e0d\u61c2\u3002\u201d \u3000\u3000\u7a81\u542c\u4e00\u4eba\u51b7\u51b7\u9053\uff1a\u201c\u4ed6\u4e0d\u61c2\uff0c\u4ed6\u53ea\u4f1a\u6740\u4eba\u3002\u201d \u3000\u3000\u5730\u5ba4\u7684\u95e8\u5728\u4e0a\u9762\uff0c\u58f0\u97f3\u5c31\u662f\u4ece\u4e0a\u9762\u4f20\u4e0b\u6765\u7684\u3002 \u3000\u3000\u97e9\u8d1e\u8d70\u7684\u65f6\u5019\uff0c\u4ed6\u4eec\u5e76\u6ca1\u6709\u5c06\u8fd9\u6247\u95e8\u4ece\u91cc\u9762\u62f4\u8d77\uff0c\u73b0\u5728\u518d\u60f3\u53bb\u62f4\uff0c\u5df2\u6765\u4e0d\u53ca\u4e86\u3002 \u3000\u3000\u8fd9\u53e5\u8bdd\u521a\u8bf4\u5b8c\uff0c\u5df2\u6709\u4e2a\u4eba\u8d70\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u4e01\u7075\u7433\u5148\u5403\u4e86\u4e00\u60ca\uff0c\u53c8\u5618\u4e86\u53e3\u6c14\uff0c\u6765\u7684\u4e0d\u662f\u4e0a\u5b98\u5c0f\u4ed9\uff0c\u603b\u7b97\u662f\u4e0d\u5e78\u4e2d\u7684\u5927\u5e78\u3002 \u3000\u3000\u6765\u7684\u662f\u4e2a\u7537\u4eba\u3002 \u3000\u3000\u662f\u4e2a\u65e0\u8bba\u8c01\u90fd\u4e0d\u613f\u89c1\u5230\u7684\u90a3\u79cd\u7537\u4eba\u2026\u2026\u65e0\u8bba\u8c01\u90fd\u4e0d\u613f\u9047\u89c1\u50f5\u5c38\u7684\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u770b\u6765\u5c31\u50cf\u662f\u4e2a\u50f5\u5c38\uff0c\u8138\u662f\u6b7b\u7070\u8272\u7684\uff0c\u98a7\u9aa8\u9ad8\u8038\uff0c\u9e70\u9f3b\u9614\u53e3\uff0c\u597d\u50cf\u8fde\u4e00\u4e1d\u8089\u90fd\u6ca1 \u6709\uff0c\u773c\u775b\u91cc\u5374\u95ea\u52a8\u7740\u4e00\u79cd\u60e8\u78a7\u7684\u5149\u3002 \u3000\u3000\u4ed6\u7684\u8eab\u6750\u5f88\u9ad8\uff0c\u8eab\u4e0a\u7a7f\u7740\u4ef6\u7ee3\u6ee1\u4e86\u9ed1\u7261\u4e39\u7684\u9c9c\u7ea2\u957f\u888d\u3002\u8896\u5b50\u4e5f\u5f88\u957f\uff0c\u76d6\u4f4f\u4e86\u4e00\u53cc\u624b\u3002 \u3000\u3000\u65e0\u8bba\u8c01\u770b\u89c1\u8fd9\u4e48\u6837\u4e00\u4e2a\u4eba\uff0c\u90fd\u96be\u514d\u8981\u5927\u5403\u4e00\u60ca\u7684\uff0c\u4e01\u7075\u7433\u5374\u53cd\u800c\u677e\u4e86\u53e3\u6c14\u3002 \u3000\u3000\uff19\uff17 \u3000\u3000\u5979\u60f3\u8bf4\u8fd9\u4e2a\u4eba\u81f3\u5c11\u8fd8\u6bd4\u4e0a\u5b98\u5c0f\u4ed9\u597d\u770b\u4e9b\u3002 \u3000\u3000\u5728\u5979\u773c\u4e2d\uff0c\u8fd9\u4e16\u4e0a\u7b80\u76f4\u5df2\u6ca1\u6709\u6bd4\u4e0a\u5b98\u5c0f\u4ed9\u66f4\u53ef\u6015\u7684\u4eba\u4e86\u3002 \u3000\u3000\u53f6\u5f00\u770b\u7740\u8fd9\u4e2a\u4eba\u8d70\u4e0b\u6765\uff0c\u5fc3\u4e5f\u8ddf\u7740\u6c89\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u4ed6\u770b\u5230\u8fd9\u4e2a\u4eba\u8d70\u8def\u7684\u59ff\u6001\uff0c\u5c31\u77e5\u9053\u4e01\u7075\u7433\u7edd\u4e0d\u662f\u8fd9\u4e2a\u4eba\u7684\u5bf9\u624b\u3002 \u3000\u3000\u4ed6\u81ea\u5df1\u73b0\u5728\u5374\u8fde\u4e01\u7075\u7433\u90fd\u6bd4\u4e0d\u4e0a\uff0c\u5c31\u7b97\u662f\u4e2a\u5341\u6765\u5c81\u7684\u5b69\u5b50\uff0c\u4e5f\u53ef\u4ee5\u4e00\u62f3\u628a\u4ed6\u6253\u5012\u3002 \u3000\u3000\u4e01\u7075\u7433\u5374\u5df2\u8df3\u8d77\u6765\uff0c\u5927\u58f0\u9053\uff1a\u201c\u4f60\u51ed\u4ec0\u4e48\u4e0d\u95ee\u9752\u7ea2\u7682\u767d\u5c31\u95ef\u8fdb\u4e86\u4eba\u5bb6\u5c4b\u91cc\u6765\uff0c\u4f60\u61c2\u4e0d\u61c2 \u89c4\u77e9\uff1f\u201d \u3000\u3000\u8fd9\u4eba\u51b7\u51b7\u9053\uff1a\u201c\u6211\u4e0d\u61c2\uff0c\u6211\u4e5f\u53ea\u61c2\u6740\u4eba\uff0c\u4f46\u6211\u5374\u6bd4\u4e0d\u4e0a\u4f60\u3002\u201d \u3000\u3000\u53f6\u5f00\u82e6\u7b11\u9053\uff1a\u201c\u4f60\u592a\u5ba2\u6c14\u4e86\u3002\u201d \u3000\u3000\u8fd9\u4eba\u9053\uff1a\u201c\u521a\u624d\u6211\u5df2\u6570\u4e86\u4e00\u904d\uff0c\u8fd9\u5730\u65b9\u524d\u524d\u540e\u540e\u3001\u91cc\u91cc\u5916\u5916\uff0c\u4e00\u5171\u6b7b\u4e86\u516b\u5341\u4e09\u4e2a\u4eba\u3002\u201d \u3000\u3000\u58a8\u5bb6\u7684\u5f1f\u5b50\uff0c\u94c1\u59d1\u7684\u95e8\u4e0b\u548c\u51b7\u9999\u56ed\u4e2d\u7684\u7ba1\u4e8b\u4eec\uff0c\u7adf\u5df2\u6ca1\u6709\u4e00\u4e2a\u6d3b\u7684\u3002 \u3000\u3000\u8fd9\u4eba\u9634\u4fa7\u607b\u5730\u7b11\u9053\uff1a\u201c\u4e00\u591c\u4e2d\u5c31\u6740\u4e86\u516b\u5341\u4e09\u4e2a\u4eba\uff0c\u597d\u5927\u7684\u624b\u7b14\uff0c\u597d\u5927\u7684\u6c14\u9b44\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u4ee5\u4e3a\u4eba\u90fd\u662f\u6211\u6740\u7684\uff1f\u201d \u3000\u3000\u8fd9\u4eba\u9053\uff1a\u201c\u6211\u53ea\u77e5\u9053\u4ed6\u4eec\u90fd\u6b7b\u4e86\uff0c\u4f60\u5374\u8fd8\u6d3b\u7740\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6d3b\u7740\u7684\u5e76\u4e0d\u6b62\u6211\u4e00\u4e2a\u3002\u201d \u3000\u3000\u8fd9\u4eba\u9053\uff1a\u201c\u53ea\u6709\u4f60\u4e00\u4e2a\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u6ca1\u6709\u770b\u89c1\u522b\u7684\u4eba\uff1f\u201d \u3000\u3000\u8fd9\u4eba\u9053\uff1a\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u4e0a\u5b98\u5c0f\u4ed9\u5462\uff1f\u201d \u3000\u3000\u8fd9\u4eba\u9053\uff1a\u201c\u6211\u6b63\u60f3\u95ee\u4f60\u4eec\uff0c\u5979\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u6211\u4eec\u600e\u4e48\u4f1a\u77e5\u9053\u5979\u5728\u54ea\u91cc\uff1f\u6211\u4eec\u4e5f\u5728\u8eb2\u7740\u5979\u3002\u201d \u3000\u3000\u8fd9\u4eba\u7b11\u4e86\u3002 \u3000\u3000\u4e01\u7075\u7433\u4e0d\u559c\u6b22\u8fd9\u79cd\u7b11\uff0c\u6ca1\u6709\u4eba\u559c\u6b22\u8fd9\u79cd\u7b11\u3002 \u3000\u3000\u8fd9\u4eba\u9634\u607b\u607b\u5730\u7b11\u9053\uff1a\u201c\u5979\u672c\u662f\u8ddf\u7740\u4f60\u4eec\u7684\uff0c\u4f60\u4eec\u5374\u5728\u8eb2\u7740\u5979\uff1f\u201d \u3000\u3000\u53f6\u5f00\u7684\u5fc3\u5728\u5f80\u4e0b\u6c89\uff0c\u4ed6\u5df2\u77e5\u9053\u8fd9\u4ef6\u4e8b\u7684\u786e\u5f88\u96be\u89e3\u91ca\u3002 \u3000\u3000\u4e01\u7075\u7433\u5374\u662f\u4e00\u526f\u7406\u76f4\u6c14\u58ee\u7684\u6837\u5b50\uff0c\u5927\u58f0\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5979\u662f\u8ddf\u6211\u4eec\u6765\u7684\uff0c\u90a3\u53ea\u4e0d\u8fc7\u56e0\u4e3a\u6211 \u4eec\u4e0a\u4e86\u5979\u7684\u5f53\u3002\u201d \u3000\u3000\u8fd9\u4eba\u51b7\u7b11\u3002 \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u4eba\u90fd\u662f\u5979\u6740\u7684\u3002\u201d \u3000\u3000\u8fd9\u4eba\u51b7\u7b11\u7740\u6253\u65ad\u5979\u7684\u8bdd\uff0c\u9053\uff1a\u201c\u5979\u4e3a\u4ec0\u4e48\u4e0d\u8fde\u4f60\u4eec\u4e5f\u4e00\u8d77\u6740\u4e86\uff1f\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u56e0\u4e3a\u97e9\u8d1e\u5c06\u6211\u4eec\u6551\u4e86\u51fa\u6765\u3002\u201d \u3000\u3000\u8fd9\u4eba\u9053\uff1a\u201c\u97e9\u8d1e\u5462\uff1f\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u627e\u9152\u53bb\u4e86\u3002\u201d \u3000\u3000\u8fd9\u4eba\u9053\uff1a\u201c\u8fd9\u79cd\u65f6\u5019\uff0c\u4f60\u4eec\u8fd8\u60f3\u559d\u9152\uff0c\u4ed6\u8fd8\u80af\u53bb\u66ff\u4f60\u627e\u9152\uff1f\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u4f60\u4e0d\u4fe1\uff1f\u201d \u3000\u3000\u8fd9\u4eba\u9053\uff1a\u201c\u4e0a\u5b98\u5c0f\u4ed9\u6740\u4eba\u7684\u65f6\u5019\uff0c\u4f60\u4eec\u90fd\u5728\u65c1\u8fb9\u770b\u7740\uff1f\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u4e5f\u88ab\u5979\u70b9\u4e86\u7a74\u9053\u3002\u201d \u3000\u3000\u8fd9\u4eba\u9053\uff1a\u201c\u4f60\u5462\uff1f\u201d \u3000\u3000\u4ed6\u95ee\u7684\u662f\u53f6\u5f00\uff0c\u4e01\u7075\u7433\u5374\u6447\u5934\u9053\uff1a\u201c\u4ed6\u4e5f\u4e2d\u4e86\u6697\u7b97\uff0c\u5168\u8eab\u5df2\u8fde\u4e00\u70b9\u529b\u6c14\u90fd\u6ca1\u6709\uff0c\u600e \u4e48\u2026\u2026\u201d \u3000\u3000\u8bf4\u5230\u8fd9\u91cc\uff0c\u5979\u624d\u53d1\u73b0\u81ea\u5df1\u8bf4\u9519\u4e86\u8bdd\u3002 \u3000\u3000\u8fd9\u4eba\u7684\u773c\u775b\u91cc\u5df2\u53d1\u51fa\u4e86\u5149\uff0c\u77aa\u7740\u53f6\u5f00\uff0c\u9634\u68ee\u68ee\u5730\u9053\uff1a\u201c\u4f60\u5df2\u8fde\u4e00\u70b9\u529b\u6c14\u90fd\u6ca1\u6709\uff1f\u201d \u3000\u3000\u53f6\u5f00\u53ea\u6709\u82e6\u7b11\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u53d1\u73b0\uff0c\u8981\u5973\u4eba\u4e0d\u591a\u5634\uff0c\u7b80\u76f4\u8981\u6bd4\u9a86\u9a7c\u7a7f\u8fc7\u9488\u773c\u8fd8\u56f0\u96be\u3002 \u3000\u3000\u8fd9\u4eba\u76ef\u7740\u4ed6\uff0c\u4e00\u5b57\u5b57\u9053\uff1a\u201c\u4f60\u82e5\u771f\u7684\u5df2\u8fde\u4e00\u70b9\u529b\u6c14\u90fd\u6ca1\u6709\u4e86\uff0c\u6211\u5c31\u6740\u4e86\u4f60\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u5927\u559d\u4e00\u58f0\uff0c\u6251\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u5979\u7684\u6b66\u529f\u5e76\u4e0d\u5f31\uff0c\u6b64\u523b\u593a\u547d\u91d1\u94c3\u867d\u4e0d\u5728\u8eab\u4e0a\uff0c\u4f46\u8fd9\u5168\u529b\u4e00\u51fb\uff0c\u4e5f\u4e0d\u662f\u522b\u4eba\u80fd\u8f7b\u6613\u62db\u67b6 \u7684\u3002 \u3000\u3000\u8c01\u77e5\u8fd9\u4eba\u957f\u8896\u4e00\u6325\uff0c\u7adf\u5c06\u5979\u4eba\u6325\u4e86\u8fc7\u53bb\uff0c\u201c\u7830\u201d\u7684\u4e00\u58f0\uff0c\u649e\u5728\u5899\u4e0a\u3002 \u3000\u3000\u8fd9\u4eba\u7684\u624b\u5df2\u4ece\u957f\u8896\u4e2d\u4f38\u51fa\uff0c\u95ea\u7535\u822c\u5411\u53f6\u5f00\u7684\u54bd\u5589\u6293\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u8fd9\u53ea\u624b\u7adf\u662f\u7ea2\u7684\u3002\u8840\u7ea2\uff01 \u3000\u3000\u7ea2\u9b54\u624b\uff01 \u3000\u3000\u65e0\u8bba\u8c01\u53ea\u8981\u88ab\u7ea2\u9b54\u624b\u4e00\u6293\uff0c\u90fd\u5fc5\u6b7b\u65e0\u7591\u3002\u53f6\u5f00\u5e76\u4e0d\u60f3\u6b7b\uff0c\u4e5f\u4e0d\u6562\u62db\u67b6\uff0c\u53ea\u6709\u7528\u5c3d\u5168\u8eab\u529b \u6c14\uff0c\u60f3\u5f80\u540e\u9000\u3002 \u3000\u3000\u5ffd\u7136\u95f4\uff0c\u4ed6\u5c45\u7136\u5df2\u51cc\u7a7a\u98de\u8d77\u3002 \u3000\u3000\u4ed6\u7684\u529b\u6c14\u7adf\u53c8\u5ffd\u7136\u6765\u4e86\uff0c\u5f80\u540e\u4e00\u9000\uff0c\u4eba\u5df2\u98de\u8d77\uff0c\u8d34\u7740\u5899\u58c1\u6ed1\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u7ea2\u9b54\u624b\u5e76\u6ca1\u6709\u4e58\u52bf\u8ffd\u51fb\uff0c\u53ea\u51b7\u51b7\u5730\u770b\u7740\u4ed6\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u8bf4\u4f60\u5df2\u8fde\u4e00\u70b9\u529b\u6c14\u90fd\u6ca1\u6709\uff0c\u8fd9 \u529b\u6c14\u662f\u4ece\u54ea\u91cc\u672a\u7684\uff1f\u201d \u3000\u3000\u65f6\u5f00\u82e6\u7b11\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u61c2\u3002\u201d \u3000\u3000\u8fd9\u662f\u5b9e\u8bdd\uff0c\u662f\u53e5\u6ca1\u6709\u4eba\u4f1a\u76f8\u4fe1\u7684\u5b9e\u8bdd\u3002 \u3000\u3000\u53ea\u542c\u95e8\u5916\u4e00\u4e2a\u4eba\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u53ea\u61c2\u5f97\u6740\u4eba\uff1f\u201d \u3000\u3000\u8fd9\u6b21\u6765\u7684\u4eba\u4e5f\u4e0d\u662f\u4e0a\u5b98\u5c0f\u4ed9\uff0c\u800c\u662f\u4e2a\u9ad8\u5927\u7684\u9ed1\u8863\u4eba\uff0c\u8eab\u540e\u80cc\u7740\u67c4\u957f\u5251\u3002 \u3000\u3000\u5251\u662f\u9ed1\u7684\uff0c\u8863\u670d\u662f\u9ed1\u7684\uff0c\u8138\u4e5f\u662f\u9eef\u9ed1\u7684\uff0c\u4e00\u53cc\u6f06\u9ed1\u7684\u7738\u5b50\u95ea\u95ea\u53d1\u5149\u3002 \u3000\u3000\u4ed6\u672c\u6765\u662f\u4e2a\u5f88\u9ad8\u5927\u7684\u4eba\uff0c\u5374\u5e76\u4e0d\u663e\u5f97\u81c3\u80bf\u3002 \u3000\u3000\u4ed6\u6574\u4e2a\u770b\u6765\u5c31\u50cf\u662f\u4e00\u53ea\u9ed1\u8272\u7684\u9e70\uff0c\u77eb\u5065\u3001\u527d\u608d\uff0c\u5145\u6ee1\u4e86\u91ce\u6027\u7684\u52a8\u529b\u3002 \u3000\u3000\u7ea2\u9b54\u624b\u62ac\u8d77\u5934\uff0c\u770b\u89c1\u4e86\u4ed6\u80cc\u540e\u7684\u957f\u5251\uff0c\u77b3\u5b54\u7a81\u7136\u6536\u7f29\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u53d1\u4eae\u7684\u773c\u775b\uff0c\u4e5f\u6b63\u5728\u76ef\u7740\u90a3\u53ea\u8840\u7ea2\u7684\u624b\u2026\u2026\u4eff\u4f5b\u90a3\u5e76\u4e0d\u662f\u53ea\u6709\u8840\u6709\u8089\u7684\u624b\u3002 \u3000\u3000\u4f60\u53ea\u6709\u5728\u5669\u68a6\u4e2d\u624d\u80fd\u770b\u89c1\u8fd9\u4e48\u6837\u4e00\u53ea\u624b\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u7684\u77b3\u5b54\u4e5f\u5728\u6536\u7f29\uff0c\u4e00\u5b57\u5b57\u9053\uff1a\u201c\u4f60\u662f\u4f0a\u591c\u54ed\uff1f\u201d \u3000\u3000\u4f0a\u591c\u54ed\u70b9\u70b9\u5934\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u9752\u9b54\u65e5\u54ed\uff0c\u8d64\u9b54\u591c\u54ed\uff0c\u5929\u5730\u7686\u54ed\uff0c\u65e5\u6708\u4e0d\u51fa\u3002\u201d \u3000\u3000\u4f0a\u591c\u54ed\u6de1\u6de1\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u4f0a\u591c\u54ed\u9053\uff1a\u201c\u4f60\u662f\u5d69\u9633\u90ed\u5bb6\u7684\u4eba\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u90ed\u5b9a\u3002\u201d \u3000\u3000\u4f0a\u591c\u54ed\u51b7\u51b7\u9053\uff1a\u201c\u5d69\u9633\u94c1\u5251\uff0c\u6740\u4eba\u65e0\u7b97\uff0c\u53ea\u6015\u8fd8\u6bd4\u4e0d\u4e0a\u8fd9\u4e2a\u4eba\u3002\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u53f6\u5f00\uff1f\u201d \u3000\u3000\u4f0a\u591c\u54ed\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u4f60\u4e5f\u77e5\u9053\u4ed6\u3002\u201d \u3000\u3000\u90ed\u5b9a\u51b7\u51b7\u9053\uff1a\u201c\u4e00\u591c\u4e4b\u95f4\uff0c\u8fde\u4f24\u516b\u5341\u4e09\u6761\u4eba\u547d\uff0c\u8fd9\u5e76\u4e0d\u5bb9\u6613\u3002\u201d \u3000\u3000\u4f0a\u591c\u54ed\u9053\uff0c\u201c\u4f46\u4ed6\u4e00\u53e3\u5426\u8ba4\u3002\u201d \u3000\u3000\u90ed\u5b9a\u51b7\u7b11\u3002 \u3000\u3000\u4f0a\u591c\u54ed\u9053\uff1a\u201c\u636e\u4ed6\u8bf4\u6740\u4eba\u7684\u51f6\u624b\u662f\u4e0a\u5b98\u5c0f\u4ed9\u3002\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u4e0a\u5b98\u5c0f\u4ed9\u662f\u4e2a\u767d\u75f4\uff0c\u4e16\u4e0a\u6ca1\u6709\u6740\u4eba\u7684\u767d\u75f4\u3002\u201d \u3000\u3000\u4f0a\u591c\u54ed\u9053\uff1a\u201c\u4f60\u4e0d\u4fe1\uff1f\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u4e0d\u4fe1\u3002\u201d \u3000\u3000\u4f0a\u591c\u54ed\u9053\uff1a\u201c\u4ed6\u8bf4\u4ed6\u81ea\u5df1\u9669\u4e9b\u6b7b\u5728\u4e0a\u5b98\u5c0f\u4ed9\u624b\u91cc\uff0c\u53ea\u6015\u4ed6\u5df2\u5168\u65e0\u4e1d\u6beb\u529b\u6c14\u3002\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u4ed6\u770b\u6765\u5e76\u4e0d\u50cf\u4e2d\u4e86\u6697\u7b97\u7684\u4eba\u3002\u201d \u3000\u3000\u4f0a\u591c\u54ed\u9053\uff1a\u201c\u4f60\u4e0d\u4fe1\uff1f\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u4e0d\u4fe1\u3002\u201d \u3000\u3000\u4f0a\u591c\u54ed\u9053\uff1a\u201c\u4ed6\u8bf4\u4ed6\u73b0\u5728\u8fd8\u6d3b\u7740\uff0c\u53ea\u4e0d\u8fc7\u56e0\u4e3a\u97e9\u8d1e\u6551\u4e86\u4ed6\u3002\uff0c\u90ed\u5b9a\u9053\uff1a\u201c\u636e\u6211\u6240\u77e5\uff0c\u97e9 \u8d1e\u624d\u662f\u4e2d\u4e86\u6697\u7b97\u7684\u4eba\u3002\u201d \u3000\u3000\u4f0a\u591c\u54ed\u9053\uff1a\u201c\u4ed6\u8bf4\u97e9\u8d1e\u6b64\u523b\u4e0d\u5728\u8fd9\u91cc\uff0c\u662f\u66ff\u4ed6\u6253\u9152\u53bb\u4e86\u3002\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u73b0\u5728\u597d\u50cf\u5e76\u4e0d\u662f\u559d\u9152\u7684\u65f6\u5019\u3002\u201d \u3000\u3000\u4f0a\u591c\u54ed\u9053\uff1a\u201c\u4ed6\u8bf4\u7684\u8bdd\u4f60\u5b8c\u5168\u4e0d\u4fe1\uff1f\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u5b8c\u5168\u4e0d\u4fe1\u3002\u201d \u3000\u3000\u4f0a\u591c\u54ed\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u4fe1\u3002\u201d \u3000\u3000\u53f6\u5f00\u53f9\u4e86\u53e3\u6c14\uff0c\u8fde\u4ed6\u81ea\u5df1\u4e5f\u89c9\u5f97\u8fd9\u4e9b\u8bdd\u5b9e\u5728\u5f88\u96be\u4ee4\u4eba\u76f8\u4fe1\u3002 \u3000\u3000\u4e01\u7075\u7433\u5ffd\u7136\u9053\uff1a\u201c\u4f60\u4eec\u77e5\u9053\u97e9\u8d1e\u53d7\u4e86\u6697\u7b97\uff0c\u77e5\u9053\u4e0a\u5b98\u5c0f\u4ed9\u662f\u8ddf\u6211\u4eec\u6765\u7684\uff1f\u201d \u3000\u3000\u90ed\u5b9a\u51dd\u89c6\u7740\u5979\uff0c\u6162\u6162\u5730\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u8fd9\u4e9b\u4e8b\u662f\u8c01\u544a\u8bc9\u4f60\u4eec\u7684\uff1f\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u4e00\u4e2a\u4fa5\u5e78\u672a\u6b7b\u7684\u4eba\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\u4ed6\u8bf4\u7684\u662f\u771f\u8bdd\uff1f\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u4ed6\u662f\u6211\u7684\u670b\u53cb\u3002\u201d \u3000\u3000\uff29\uff2c\uff29 \u3000\u3000\u4e01\u7075\u7433\u5fcd\u4e0d\u4f4f\u51b7\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u6709\u8fd9\u4e48\u6837\u7684\u670b\u53cb\uff0c\u771f\u662f\u8d70\u8fd0\u4e86\u3002\u201d \u3000\u3000\u4f0a\u591c\u54ed\u9053\uff1a\u201c\u4ed6\u867d\u7136\u4e0d\u662f\u6211\u7684\u670b\u53cb\uff0c\u4ed6\u7684\u8bdd\u6211\u4e5f\u76f8\u4fe1\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4f0a\u591c\u54ed\u9053\uff1a\u201c\u4e8b\u5b9e\u4ff1\u5728\uff0c\u6211\u4e0d\u80fd\u4e0d\u4fe1\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\u5b9e\uff1f\u201d \u3000\u3000\u4f0a\u591c\u54ed\u9053\uff1a\u201c\u4f60\u4eec\u6740\u4e86\u6240\u6709\u77e5\u9053\u5185\u60c5\u7684\u4eba\uff0c\u85cf\u8d77\u4e86\u4e0a\u5b98\u5c0f\u4ed9\uff0c\u51c6\u5907\u4ee5\u540e\u5ac1\u7978\u7ed9\u522b\u4eba\uff0c\u91d1 \u94b1\u5e2e\u7684\u5b9d\u85cf\u5c82\u975e\u5c31\u5df2\u7a33\u7a33\u5730\u843d\u5165\u4f60\u4eec\u624b\u91cc\uff1f\u201d \u3000\u3000\u4e01\u7075\u7433\u8138\u8272\u53d8\u4e86\u3002 \u3000\u3000\u5979\u5ffd\u7136\u4e5f\u53d1\u89c9\uff0c\u8fd9\u63a8\u6d4b\u5b9e\u5728\u4e0d\u80fd\u7b97\u4e0d\u5408\u7406\u3002 \u3000\u3000\u90ed\u5b9a\u8fd8\u5728\u51dd\u89c6\u7740\u5979\uff0c\u6df1\u6df1\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u8bdd\u82e5\u6709\u4eba\u8bc1\u660e\uff0c\u6211\u4e5f\u76f8\u4fe1\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u773c\u775b\u4eae\u4e86\uff0c\u9053\uff1a\u201c\u6211\u4eec\u8bf4\u7684\u8bdd\uff0c\u5e78\u597d\u8fd8\u6709\u4e00\u4e2a\u4eba\u53ef\u4ee5\u8bc1\u660e\u3002\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u97e9\u8d1e\uff1f\u201d \u3000\u3000\u4e86\u7075\u7433\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u4ed6\u53bb\u66ff\u4f60\u4eec\u627e\u9152\u53bb\u4e86\uff1f\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u65e2\u7136\u53ea\u4e0d\u8fc7\u662f\u53bb\u627e\u9152\uff0c\u5f53\u7136\u5f88\u5feb\u5c31\u4f1a\u56de\u6765\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u4f60\u6700\u597d\u7b49\u4ed6\u56de\u672a\u3002\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u597d\uff0c\u6211\u4eec\u7b49\u3002\u201d \u3000\u3000\u4f0a\u591c\u54ed\u9053\uff1a\u201c\u4f60\u771f\u7684\u8981\u7b49\uff1f\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u6211\u5df2\u8bf4\u8fc7\u3002\u201d \u3000\u3000\u4f0a\u591c\u54ed\u9053\uff1a\u201c\u7b49\u4ed6\u4eec\u7684\u5e2e\u624b\u6765\uff0c\u5c06\u6211\u4eec\u4e5f\u4e00\u8d77\u6740\u4e86\uff1f\u201d \u3000\u3000\u90ed\u5b9a\u6c89\u4e0b\u4e86\u8138\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u662f\u4f60\uff0c\u6211\u662f\u6211\uff0c\u5e76\u4e0d\u662f\u6211\u4eec\u3002\u201d \u3000\u3000\u4f0a\u591c\u54ed\u76ef\u7740\u4ed6\uff0c\u76ee\u5149\u9634\u68ee\u5982\u9b3c\u706b\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u83ab\u975e\u8fd8\u4e0d\u66fe\u4e0e\u6211\u4e3a\u4f0d\uff1f\u201d \u3000\u3000\u90ed\u5b9a\u51b7\u7b11\uff0c\u51b7\u7b11\u7684\u610f\u601d\u4e5f\u662f\u9ed8\u8ba4\u3002 \u3000\u3000\u4f0a\u591c\u54ed\u9053\uff1a\u201c\u6614\u5e74\u5d69\u9633\u94c1\u5251\u5728\u5175\u5668\u8c31\u4e2d\u6392\u540d\u7b2c\u56db\uff0c\u7684\u786e\u53ef\u4ee5\u7b97\u662f\u4e86\u4e0d\u8d77\u7684\u5927\u82f1\u96c4\uff0c\u53ea\u53ef \u60dc\u2026\u2026\u201d \u3000\u3000\u4ed6\u6c89\u7740\u8138\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u4f60\u5e76\u4e0d\u662f\u90ed\u5d69\u9633\uff0c\u90ed\u5d69\u9633\u7684\u5c38\u9996\u53ea\u6015\u65e9\u5df2\u5316\u6210\u7070\u4e86\u3002\u201d \u3000\u3000\u90ed\u5b9a\u9ed1\u9edd\u9edd\u7684\u8138\uff0c\u5ffd\u7136\u53d8\u5f97\u94c1\u9752\u3002 \u3000\u3000\u4f0a\u591c\u54ed\u51b7\u51b7\u9053\uff1a\u201c\u6b7b\u4eba\u5c31\u662f\u6b7b\u4eba\uff0c\u6240\u6709\u7684\u6b7b\u4eba\u90fd\u4e00\u6837\uff0c\u83ab\u5fd8\u8bb0\u5927\u5251\u5ba2\u6b7b\u4e86\uff0c\u5c38\u8eab\u4e5f\u8ddf\u522b \u4eba\u4e00\u6837\u4f1a\u8150\u70c2\u53d1\u81ed\u7684\u3002\u201d \u3000\u3000\u90ed\u5b9a\u7d27\u63e1\u53cc\u62f3\uff0c\u4e00\u5b57\u5b57\u9053\uff1a\u201c\u4f60\u6700\u597d\u83ab\u8981\u5fd8\u8bb0\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u4f0a\u591c\u54ed\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u90ed\u5b9a\u5389\u58f0\u9053\uff1a\u201c\u90ed\u5d69\u9633\u867d\u6b7b\u4e86\uff0c\u5d69\u9633\u94c1\u5251\u5374\u6ca1\u6709\u6b7b\u3002\u201d \u3000\u3000\u4f0a\u591c\u54ed\u51b7\u7b11\u9053\uff1a\u201c\u5d69\u9633\u94c1\u5251\u96be\u9053\u8fd8\u60f3\u5e26\u7740\u8fd9\u6740\u4eba\u7684\u51f6\u624b\u6765\u5bf9\u4ed8\u6211\uff1f\u201d \u3000\u3000\u90ed\u5b9a\u4e0d\u8bf4\u8bdd\u4e86\u3002 \u3000\u3000\u4f0a\u591c\u54ed\u9053\uff1a\u201c\u90ed\u5d69\u9633\u662f\u6b7b\u5728\u5211\u65e0\u547d\u5251\u4e0b\u7684\uff0c\u5211\u65e0\u547d\u7684\u5251\u6cd5\uff0c\u4f20\u81ea\u4e0a\u5b98\u91d1\u8679\u3002\u201d \u3000\u3000\u90ed\u5b9a\u7684\u62f3\u53c8\u63e1\u7d27\u3002 \u3000\u3000\u4f0a\u591c\u54ed\u9053\uff1a\u201c\u4f60\u82e5\u662f\u90ed\u5bb6\u7684\u597d\u5b50\u5b59\uff0c\u5c31\u8be5\u4e0e\u6211\u8054\u624b\uff0c\u9664\u4e86\u53f6\u5f00\uff0c\u627e\u51fa\u4e0a\u5b98\u5c0f\u4ed9\uff0c\u518d\u4ece\u4e0a \u5b98\u91d1\u8679\u624b\u4e0a\u7684\u6b66\u529f\u79d8\u7b08\u4e2d\uff0c\u627e\u51fa\u4ed6\u4eec\u5251\u6cd5\u4e2d\u7684\u7455\u75b5\uff0c\u4e0e\u5211\u65e0\u547d\u51b3\u4e00\u80dc\u8d1f\uff0c\u4e3a\u90ed\u5d69\u9633\u6b7b\u540e\u7684\u82f1 \u7075\u51fa\u4e00\u53e3\u6c14\u3002\u201d \u3000\u3000\u4ed6\u770b\u6765\u867d\u7136\u5b64\u50fb\u53e4\u602a\uff0c\u4f46\u8bf4\u51fa\u6765\u7684\u8bdd\u5374\u6781\u6709\u717d\u52a8\u529b\u3002 \u3000\u3000\u90ed\u5b9a\u5df2\u4e0d\u7981\u8038\u7136\u52a8\u5bb9\u3002 \u3000\u3000\u4f0a\u591c\u54ed\u770b\u7740\u4ed6\u8138\u4e0a\u7684\u8868\u60c5\uff0c\u60a0\u7136\u9053\uff1a\u201c\u4f60\u7684\u610f\u601d\u5982\u4f55\uff1f\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u5f88\u597d\u3002\u201d \u3000\u3000\u4f0a\u591c\u54ed\u9053\uff1a\u201c\u4f60\u5df2\u7b54\u5e94\uff1f\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u55ef\u3002\u201d \u3000\u3000\u4f0a\u591c\u54ed\u5927\u7b11\u9053\uff1a\u201c\u53ea\u8981\u4f60\u6211\u8054\u624b\uff0c\u522b\u8bf4\u533a\u533a\u4e00\u4e2a\u53f6\u5f00\uff0c\u653e\u773c\u5929\u4e0b\uff0c\u53c8\u6709\u8c01\u80fd\u4e0e\u6211\u4eec\u8f83\u4e00 \u65e5\u4e4b\u77ed\u957f\uff1f\u201d \u3000\u3000\u90ed\u5b9a\u4e00\u7ffb\u624b\u8155\uff0c\u53c8\u63e1\u4f4f\u4e86\u5251\u67c4\u3002 \u3000\u3000\u4f0a\u591c\u54ed\u7684\u7b11\u58f0\u9aa4\u7136\u505c\u987f\uff0c\u76ef\u7740\u53f6\u5f00\u9634\u6d4b\u607b\u5730\u9053\uff1a\u201c\u8fd9\u5730\u65b9\u522b\u65e0\u9000\u8def\uff0c\u770b\u6765\u4eca\u65e5\u4f60\u5df2\u6b7b\u5b9a \u4e86\u3002\u201d \u7b2c\u5341\u7ae0\u3000\u7fa4\u9e70\u98de\u8d77>> \u53e4\u9f99\u300a\u4e5d\u6708\u9e70\u98de\u300b \u7b2c\u5341\u7ae0\u3000\u7fa4\u9e70\u98de\u8d77 \u3000\u3000\u6e05\u6668\uff0c\u6674\u3002 \u3000\u3000\u51e4\u5374\u6bd4\u6628\u591c\u66f4\u51b7\uff0c\u96ea\u6eb6\u7684\u65f6\u5019\uff0c\u603b\u662f\u6bd4\u4e0b\u96ea\u65f6\u8fd8\u51b7\u7684\u3002 \u3000\u3000\u73b0\u5728\u96ea\u5df2\u5c06\u6eb6\uff0c\u4e1c\u65b9\u5df2\u6709\u9633\u5149\u7167\u5c04\uff0c\u7167\u7740\u707f\u70c2\u7684\u6885\u6797\u3002 \u3000\u3000\u5730\u5ba4\u4e2d\u5374\u4ecd\u662f\u9634\u6c89\u7684\u3002 \u3000\u3000\u4e01\u7075\u7433\u5df2\u8d70\u8fc7\u6765\uff0c\u4f9d\u504e\u5728\u53f6\u5f00\u8eab\u65c1\u3002 \u3000\u3000\u53f6\u5f00\u9759\u9759\u5730\u7ad9\u7740\uff0c\u65e2\u6ca1\u6709\u5f00\u53e3\uff0c\u4e5f\u6ca1\u6709\u52a8\uff0c\u773c\u775b\u91cc\u7adf\u4f3c\u8fd8\u5e26\u7740\u79cd\u5947\u602a\u7684\u7b11\u610f\u3002 \u3000\u3000\u4f0a\u591c\u54ed\u76ef\u7740\u4ed6\u7684\u624b\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u4f60\u5bf9\u4ed8\u4ed6\uff0c\u6211\u6740\u4e86\u8fd9\u5973\u4eba\u518d\u6765\u52a9\u4f60\u3002\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u55ef\u3002\u201d \u3000\u3000\u4f0a\u591c\u54ed\u9053\uff1a\u201c\u5c0f\u5fc3\u4ed6\u7684\u98de\u5200\u3002\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u4f60\u4e5f\u5f97\u5c0f\u5fc3\uff0c\u5c0f\u5fc3\u6211\u7684\u5251\u3002\u201d \u3000\u3000\u4f0a\u591c\u54ed\u6115\u7136\u9053\uff1a\u201c\u5c0f\u5fc3\u4f60\u7684\u5251\uff1f\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u55ef\uff01\u201d \u3000\u3000\u7a81\u7136\u95f4\uff0c\u5251\u5149\u4e00\u95ea\uff0c\u4ed6\u7684\u5251\u5df2\u51fa\u624b\uff0c\u95ea\u7535\u822c\u5411\u4f0a\u591c\u54ed\u523a\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u5251\u5149\u5e76\u4e0d\u50cf\u95ea\u7535\u3002\u5251\u662f\u4e4c\u9ed1\u7684\uff0c\u4e95\u6ca1\u6709\u4ec0\u4e48\u5149\u534e\uff0c\u4f46\u68ee\u5bd2\u7684\u5251\u6c14\u5374\u6bd4\u95ea\u7535\u66f4\u6151\u4eba\u3002 \u3000\u3000\u8fd9\u5c31\u662f\u5d69\u9633\u94c1\u5251\u3002 \u3000\u3000\u666e\u5929\u4e4b\u4e0b\uff0c\u72ec\u4e00\u65e0\u4e8c\u7684\u5d69\u9633\u94c1\u5308\u3002 \u3000\u3000\u5251\u4e00\u51fa\u9798\uff0c\u4f0a\u591c\u54ed\u5c31\u89c9\u5f97\u6709\u80a1\u6151\u4eba\u7684\u5251\u6c14\uff0c\u903c\u5230\u4e86\u4ed6\u7684\u7709\u776b\u3002 \u3000\u3000\u4ed6\u5927\u60ca\uff0c\u66b4\u6012\uff0c\u72c2\u543c\u4e00\u58f0\uff0c\u7ea2\u9b54\u624b\u5df2\u8840\u7bad\u822c\u98de\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u6614\u5e74\u9752\u9b54\u624b\u5728\u5175\u5668\u8c31\u4e2d\u6392\u540d\u7b2c\u4e5d\uff0c\u5176\u5b9e\u5b83\u7684\u5a01\u529b\u5e76\u4e0d\u5728\u6392\u540d\u7b2c\u516d\u7684\u97ad\u795e\u86c7\u97ad\u3001\u6392\u540d\u7b2c\u4e03 \u7684\u91d1\u521a\u94c1\u62d0\u4e4b\u4e0b\uff0c\u53ea\u4e0d\u8fc7\u56e0\u4e3a\u8fd9\u4ef6\u5175\u5668\u592a\u90aa\uff0c\u6240\u4ee5\u767e\u6653\u751f\u6545\u610f\u6291\u4f4e\u4e86\u5b83\u3002 \u3000\u3000\u7ea2\u9b54\u624b\u5236\u4f5c\u5f97\u6bd4\u9752\u9b54\u624b\u66f4\u7cbe\u5de7\uff0c\u62db\u5f0f\u4e5f\u66f4\u602a\u5f02\u6bd2\u8fa3\u3002 \u3000\u3000\u5175\u5668\u4e5f\u5982\u4e16\u4e0a\u5f88\u591a\u522b\u7684\u4e8b\u4e00\u6837\uff0c\u603b\u662f\u5728\u4e0d\u505c\u5730\u8fdb\u5316\u7740\u7684\u3002 \u3000\u3000\u53ea\u89c1\u4e00\u9053\u9c9c\u7ea2\u8272\u7684\u5149\u8292\u95ea\u52a8\uff0c\u5939\u5e26\u7740\u79cd\u4ee4\u4eba\u4f5c\u5455\u7684\u8840\u8165\u6c14\u3002 \u3000\u3000\u90ed\u5b9a\u51b7\u7b11\uff0c\u540e\u9000\u4e24\u6b65\uff0c\u7a81\u7136\u957f\u5578\u4e00\u58f0\uff0c\u51b2\u5929\u98de\u8d77\uff0c\u94c1\u5251\u7adf\u5df2\u5316\u505a\u4e86\u4e00\u9053\u4e4c\u9ed1\u7684\u957f\u8679\u3002 \u3000\u3000\u4ed6\u7684\u4eba\u5e26\u5251\u7adf\u5df2\u4f3c\u5408\u800c\u4e3a\u4e00\u3002 \u3000\u3000\u8fd9\u6b63\u662f\u5d69\u9633\u94c1\u5251\u7684\u6740\u624b\uff0c\u51e0\u4e4e\u5df2\u63a5\u8fd1\u65e0\u575a\u4e0d\u6467\u3002 \u3000\u3000\u53ea\u542c\u201c\u53ee\u201d\u7684\u4e00\u54cd\uff0c\u7ea2\u9b54\u624b\u5df2\u88ab\u8fd9\u4e00\u5251\u51fb\u788e\uff0c\u788e\u6210\u4e86\u65e0\u6570\u7247\uff0c\u770b\u6765\u5c31\u5982\u6ee1\u5929\u8840\u96e8\u3002 \u3000\u3000\u90ed\u5b9a\u957f\u5578\u4e0d\u7edd\uff0c\u51cc\u7a7a\u5012\u7ffb\uff0c\u957f\u8679\u4e00\u5251\u5316\u505a\u65e0\u6570\u70b9\u5149\u5f71\u3002 \u3000\u3000\u6ee1\u5929\u8840\u96e8\u7acb\u523b\u88ab\u538b\u4e86\u4e0b\u53bb\uff0c\u4f0a\u591c\u54ed\u4e5f\u5df2\u5728\u5251\u6c14\u7b3c\u7f69\u4e0b\u3002 \u3000\u3000\u4ed6\u65e0\u8bba\u5411\u4efb\u4f55\u65b9\u5411\u95ea\u907f\uff0c\u90fd\u5df2\u907f\u4e0d\u5f00\u4e86\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u5578\u58f0\u7a81\u7edd\uff0c\u5251\u6c14\u987f\u6536\uff0c\u90ed\u5b9a\u8eab\u5f62\u843d\u4e0b\u65f6\uff0c\u94c1\u5251\u5df2\u5165\u9798\u3002 \u3000\u3000\u4f0a\u591c\u54ed\u7684\u624b\u5782\u843d\uff0c\u6574\u4e2a\u4eba\u90fd\u4f3c\u5df2\u5446\u4f4f\u4e86\uff0c\u9634\u68ee\u602a\u5f02\u7684\u8138\u4e0a\uff0c\u6c57\u843d\u5982\u96e8\u3002 \u3000\u3000\u90ed\u5b9a\u51b7\u51b7\u5730\u770b\u7740\u4ed6\uff0c\u4e00\u5b57\u5b57\u9053\uff1a\u201c\u4f60\u8981\u548c\u6211\u8054\u624b\uff0c\u4f60\u8fd8\u4e0d\u914d\u3002\u201d \u3000\u3000\u4f0a\u591c\u54ed\u54ac\u4e86\u54ac\u7259\uff1a\u201c\u4f60\u4e3a\u4f55\u4e0d\u7d22\u6027\u4e00\u5251\u6740\u4e86\u6211\uff1f\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u4f60\u4e5f\u4e0d\u914d\u3002\u201d \u3000\u3000\u4f0a\u591c\u54ed\u9053\uff1a\u201c\u4f60\u8981\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u8981\u4f60\u6eda\u3002\u201d \u3000\u3000\u4f0a\u591c\u54ed\u7a81\u53c8\u9634\u607b\u607b\u5730\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u6211\u82e5\u8d70\u4e86\uff0c\u603b\u6709\u4e00\u5929\u4f60\u8981\u540e\u6094\u7684\u3002\u201d \u3000\u3000\u4ed6\u5e76\u6ca1\u6709\u9003\u3002 \u3000\u3000\u4ed6\u6162\u6162\u5730\u8d70\u8fc7\u90ed\u5b9a\u9762\u524d\uff0c\u6162\u6162\u5730\u8d70\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u788e\u88c2\u4e86\u7684\u7ea2\u9b54\u624b\u843d\u5728\u5730\u4e0a\uff0c\u4e5f\u50cf\u662f\u4e00\u6ef4\u6ef4\u9c9c\u8840\u3002 \u3000\u3000\u90ed\u5b9a\u8f6c\u8fc7\u8eab\u9762\u5bf9\u53f6\u5f00\u3002 \u3000\u3000\u53f6\u5f00\u5728\u5fae\u7b11\u3002 \u3000\u3000\u90ed\u5b9a\u6c89\u7740\u8138\u9053\uff1a\u201c\u4f60\u5f88\u6c89\u5f97\u4f4f\u6c14\u3002\u201d \u3000\u3000\u53f6\u5f00\u70b9\u70b9\u5934\u3002 \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u4f60\u4e0d\u6015\u6211\u8ddf\u4ed6\u8054\u624b\u5bf9\u4ed8\u4f60\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u77e5\u9053\u3002\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u77e5\u9053\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u53f6\u5f00\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u6211\u77e5\u9053\u5d69\u9633\u94c1\u5251\u662f\u597d\u4eba\uff0c\u7edd\u4e0d\u4f1a\u8ddf\u90a3\u79cd\u4eba\u8054\u624b\u505a\u4efb\u4f55\u4e8b\u7684\u3002\u201d \u3000\u3000\u90ed\u5b9a\u51dd\u89c6\u7740\u4ed6\uff0c\u4f46\u773c\u775b\u91cc\u5e26\u7740\u79cd\u5f88\u5947\u602a\u7684\u8868\u60c5\uff0c\u8fc7\u4e86\u5f88\u4e45\uff0c\u624d\u5f90\u5f90\u9053\uff1a\u201c\u90ed\u5d69\u9633\u662f\u6211\u7684 \u957f\u5144\u3002\u201d \u3000\u3000\u53f6\u5f00\u5fae\u7b11\u9053\uff1a\u201c\u679c\u7136\u662f\u6709\u5176\u5144\uff0c\u5fc5\u6709\u5176\u5f1f\u3002\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u4ed6\u82f1\u96c4\u4e00\u4e16\uff0c\u7adf\u4e0d\u5e78\u6b7b\u5728\u5211\u65e0\u547d\u624b\u91cc\u3002\u201d \u3000\u3000\u53f6\u5f00\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u90a3\u4e5f\u6b63\u662f\u5c0f\u674e\u63a2\u82b1\u751f\u5e73\u6700\u5927\u7684\u61be\u4e8b\u3002\u201d \u3000\u3000\u5d69\u9633\u94c1\u5251\u4e0e\u5c0f\u674e\u98de\u5200\u60fa\u60fa\u76f8\u501f\uff0c\u7531\u4e92\u76f8\u5c0a\u91cd\u7684\u654c\u4eba\uff0c\u53d8\u6210\u4e86\u4e92\u76f8\u5c0a\u91cd\u7684\u670b\u53cb\uff0c\u4ed6\u4eec\u4e00\u751f \u4e92\u76f8\u5c0a\u91cd\uff0c\u90ed\u5d69\u9633\u4e3a\u4e86\u66ff\u674e\u5bfb\u6b22\u8d74\u7ea6\uff0c\u624d\u6b7b\u5728\u5211\u65e0\u547d\u7684\u5251\u4e0b\uff0c\u90a3\u867d\u7136\u662f\u4e00\u6bb5\u6068\u4e8b\uff0c\u5374\u4e5f\u662f\u4e00 \u6bb5\u4f73\u8bdd\u3002 \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u4f0a\u591c\u54ed\u5e76\u6ca1\u6709\u8bf4\u9519\uff0c\u6211\u6b64\u6765\u7684\u786e\u662f\u4e3a\u4e86\u4e0a\u5b98\u91d1\u8679\u7684\u79d8\u7b08\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u77e5\u9053\u3002\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u6240\u4ee5\u6211\u8fd8\u662f\u8981\u7b49\u97e9\u8d1e\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u77e5\u9053\u3002\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u4f60\u7684\u8bdd\uff0c\u6211\u672c\u4e0d\u8be5\u76f8\u4fe1\uff0c\u6211\u59d1\u4e14\u76f8\u4fe1\u4f60\uff0c\u53ea\u56e0\u4e3a\u4f60\u662f\u674e\u5bfb\u6b22\u552f\u4e00\u7684\u4f20\u4eba\u3002\u201d \u3000\u3000\u53f6\u5f00\u53f9\u9053\uff1a\u201c\u4ed6\u8001\u4eba\u5bb6\u5e76\u6ca1\u6709\u771f\u7684\u5c06\u6211\u6536\u4e3a\u5f1f\u5b50\uff0c\u4ed6\u7684\u6b66\u529f\u6211\u4e5f\u672a\u5f97\u5341\u4e00\u3002\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u4f46\u4ed6\u5374\u5c06\u4ed6\u7684\u98de\u5200\u7edd\u6280\u4f20\u7ed9\u4e86\u4f60\u3002\u201d \u3000\u3000\u53f6\u5f00\u6ca1\u6709\u5426\u8ba4\u3002 \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u5bb6\u5144\u5728\u4e16\u65f6\uff0c\u6700\u5927\u7684\u613f\u671b\uff0c\u5c31\u662f\u627e\u5c0f\u674e\u98de\u5200\u4e00\u8f83\u9ad8\u4e0b\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u77e5\u9053\u3002\u201d \u3000\u3000\u90ed\u5b9a\u9eef\u7136\u9053\uff1a\u201c\u5174\u4e91\u5e84\u5916\uff0c\u67ab\u6797\u4e00\u6218\uff0c\u4ed6\u7ec8\u4e8e\u8d25\u5728\u5c0f\u674e\u98de\u5200\u4e4b\u4e0b\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4ed6\u5e76\u6ca1\u6709\u8d25\u3002\u201d \u3000\u3000\u90ed\u5b9a\u53c8\u53f9\u9053\uff1a\u201c\u4ed6\u8d25\u4e86\uff0c\u8d25\u5c31\u662f\u8d25\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f46\u90a3\u4e00\u6218\u5374\u88ab\u5929\u4e0b\u6b66\u6797\u4e2d\u4eba\u8ba4\u4e3a\u662f\u524d\u65e0\u53e4\u4eba\u3001\u540e\u65e0\u6765\u8005\u7684\u4e00\u6218\u3002\u201d \u3000\u3000\u90a3\u4e00\u6218\u674e\u5bfb\u6b22\u672c\u6709\u4e09\u6b21\u673a\u4f1a\u53ef\u81f4\u90ed\u5d69\u9633\u6b7b\u547d\uff0c\u5374\u90fd\u672a\u51fa\u624b\u3002\u5230\u540e\u6765\u674e\u5bfb\u6b22\u5200\u949d\u5203\u6298\uff0c\u90ed \u5d69\u9633\u8bf4\u4e0d\u5b9a\u5df2\u53ef\u81f4\u4ed6\u4e8e\u6b7b\u5730\uff0c\u4f46\u90ed\u5d69\u9633\u975e\u4f46\u4e5f\u672a\u51fa\u624b\uff0c\u53cd\u800c\u5fc3\u7518\u60c5\u613f\u7684\u8ba4\u8d25\u670d\u8f93\u4e86\u3002 \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u50cf\u4ed6\u4eec\u90a3\u6837\uff0c\u624d\u771f\u6b63\u662f\u7537\u5b50\u6c49\u5927\u4e08\u592b\uff0c\u624d\u771f\u6b63\u65e0\u6127\u4e8e\u82f1\u96c4\u672c\u8272\u3002\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u53ea\u4e0d\u8fc7\u65e0\u8bba\u5982\u4f55\uff0c\u5d69\u9633\u94c1\u5251\u603b\u7b97\u662f\u8d25\u5728\u5c0f\u674e\u98de\u5200\u4e4b\u4e0b\u3002\u201d \u3000\u3000\u53f6\u5f00\u53ea\u6709\u6c89\u9ed8\uff0c\u4ed6\u5df2\u4e0d\u80fd\u518d\u8bf4\u4ec0\u4e48\u3002 \u3000\u3000\u90ed\u5b9a\u770b\u7740\u4ed6\uff0c\u76ee\u4e2d\u7a81\u7136\u53c8\u6709\u7cbe\u5149\u66b4\u5c04\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u636e\u8bf4\u8fd1\u65e5\u6765\u53c8\u6709\u4eba\u91cd\u4f5c\u5175\u5668\u8c31\uff0c\u5df2\u5c06 \u4f60\u7684\u98de\u5200\uff0c\u8bc4\u4e3a\u5929\u4e0b\u7b2c\u4e00\u3002\u201d \u3000\u3000\u53f6\u5f00\u82e6\u7b11\uff0c\u4ed6\u4e5f\u542c\u8fc7\u8fd9\u53e5\u8bdd\u3002 \u3000\u3000\u81ea\u4ece\u4ed6\u542c\u5230\u8fd9\u53e5\u8bdd\u7684\u90a3\u4e00\u5929\uff0c\u4ed6\u5c31\u5df2\u77e5\u9053\u4ed6\u6709\u9ebb\u70e6\u8981\u6765\u4e86\uff0c\u6b66\u6797\u597d\u6c49\u4eec\uff0c\u7edd\u6ca1\u6709\u4efb\u4f55\u4eba \u4f1a\u5fc3\u7518\u60c5\u613f\u88ab\u5217\u5728\u522b\u4eba\u4e4b\u4e0b\u7684\u3002 \u3000\u3000\u5c31\u51ed\u8fd9\u4e00\u53e5\u8bdd\uff0c\u5df2\u8db3\u591f\u5f15\u8d77\u65e0\u6570\u51f6\u6740\uff0c\u65e0\u6570\u8840\u6218\u3002 \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u6240\u4ee5\u65e0\u8bba\u4f60\u8bf4\u7684\u8bdd\u662f\u771f\u662f\u5047\uff0c\u6b64\u4e8b\u8fc7\u540e\u6211\u8fd8\u662f\u8981\u4e00\u8f83\u80dc\u8d1f\uff0c\u770b\u4e00\u770b\u4eca\u65e5\u7684\u5d69 \u9633\u94c1\u5251\uff0c\u662f\u4e0d\u662f\u4e0d\u5728\u98de\u5200\u4e4b\u4e0b\u3002\u201d \u3000\u3000\u53f6\u5f00\u8fd8\u662f\u53ea\u6709\u82e6\u7b11\u3002 \u3000\u3000\u4e01\u7075\u7433\u5374\u5fcd\u4e0d\u4f4f\u9053\uff1a\u201c\u4f60\u6700\u597d\u660e\u767d\u4e00\u4f53\u4e8b\u3002\u201d \u3000\u3000\u90ed\u5b9a\u5728\u542c\u7740\u3002 \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u4ed6\u7684\u5200\u88ab\u8bc4\u4e3a\u5929\u4e0b\u7b2c\u4e00\uff0c\u662f\u56e0\u4e3a\u4ed6\u7684\u5200\u6551\u8fc7\u5f88\u591a\u4eba\uff0c\u5e76\u4e0d\u662f\u56e0\u4e3a\u6740\u4eba\u3002\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u6211\u4e5f\u542c\u8bf4\u8fc7\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u82e5\u8981\u80dc\u8fc7\u4ed6\uff0c\u5c31\u8be5\u53bb\u6551\u4eba\uff0c\u4e0d\u8be5\u53bb\u6740\u4eba\u3002\u201d \u3000\u3000\u90ed\u5b9a\u6c89\u7740\u8138\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u6211\u82e5\u6740\u4e86\u4ed6\uff0c\u5c31\u5df2\u80dc\u8fc7\u4ed6\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u53f9\u9053\uff1a\u201c\u4f60\u9519\u4e86\uff0c\u4f60\u5c31\u7b97\u771f\u7684\u80fd\u6740\u4e86\u4ed6\uff0c\u4e5f\u4e0d\u80fd\u80dc\u8fc7\u4ed6\u7684\u3002\u201d \u3000\u3000\u90ed\u5b9a\u51b7\u7b11\u3002 \u3000\u3000\u51b7\u7b11\u7684\u610f\u601d\uff0c\u6709\u65f6\u4e5f\u662f\u5426\u8ba4\u3002 \u3000\u3000\u4e01\u7075\u7433\u4e5f\u5fcd\u4e0d\u4f4f\u7b11\u9053\uff1a\u201c\u4f60\u83ab\u4ee5\u4e3a\u4f60\u80dc\u4e86\u7ea2\u9b54\u624b\uff0c\u5c31\u5df2\u5f88\u4e86\u4e0d\u8d77\uff0c\u7ea2\u9b54\u624b\u867d\u7136\u6bd4\u9752\u9b54\u624b \u66f4\u8981\u6076\u6bd2\u7075\u5de7\uff0c\u5374\u8fd8\u662f\u6bd4\u4e0d\u4e0a\u9752\u9b54\u624b\u7684\u3002\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u56e0\u4e3a\u4f0a\u591c\u54ed\u8fd9\u4e2a\u4eba\u65e2\u6ca1\u6709\u6c14\u9b44\uff0c\u4e5f\u6ca1\u6709\u4eba\u6027\u3002\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u4ed6\u770b\u6765\u867d\u7136\u662f\u5b64\u9ad8\u9a84\u505a\uff0c\u5176\u5b9e\u5374\u662f\u4e2a\u82b1\u8a00\u5de7\u8bed\u3001\u5077\u673a\u53d6\u5de7\u7684\u4eba\uff0c\u5c31\u51ed\u8fd9\u4e00 \u70b9\u4ed6\u5df2\u7ecf\u6bd4\u4e0d\u4e0a\u9752\u9b54\u624b\u4e86\u3002\u201d \u3000\u3000\u90ed\u5b9a\u770b\u7740\u5979\uff0c\u773c\u775b\u91cc\u4e5f\u9732\u51fa\u79cd\u5f88\u5947\u602a\u7684\u8868\u60c5\u3002 \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u53e4\u5f80\u4eca\u6765\uff0c\u771f\u6b63\u7684\u6b66\u6797\u9ad8\u624b\uff0c\u90fd\u662f\u7279\u7acb\u72ec\u884c\uff0c\u4e0d\u53d7\u5f71\u54cd\u7684\u4eba\uff0c\u4e00\u4e2a\u4eba\u82e5\u8fde \u81ea\u5df1\u72ec\u7279\u7684\u4e2a\u6027\u90fd\u6ca1\u6709\uff0c\u53c8\u600e\u4e48\u80fd\u7ec3\u5f97\u51fa\u72ec\u7279\u7684\u6b66\u529f\u6765\uff1f\u201d \u3000\u3000\u90ed\u5b9a\u5ffd\u7136\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u8bdd\u5e76\u4e0d\u662f\u6ca1\u6709\u9053\u7406\uff0c\u53ea\u53ef\u60dc\u4f60\u7684\u8bdd\u592a\u591a\u4e86\u3002\u201d \u3000\u3000\u4ed6\u80cc\u8f6c\u8eab\uff0c\u9762\u5bf9\u7740\u5899\uff0c\u7ade\u8fde\u770b\u90fd\u4e0d\u518d\u770b\u4e01\u7075\u7433\u4e00\u773c\u3002 \u3000\u3000\u4e01\u7075\u7433\u5374\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u770b\u6765\u8fd9\u4eba\u5012\u771f\u662f\u6709\u4e2a\u6027\u7684\u4eba\u3002\u201d \u3000\u3000\u53f6\u5f00\u5fae\u7b11\u9053\uff1a\u201c\u4ed6\u7684\u786e\u662f\u7684\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u7728\u7740\u773c\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u4ed6\u5374\u6709\u70b9\u4e0d\u660e\u662f\u975e\uff0c\u4e0d\u77e5\u597d\u6b79\uff0c\u5c45\u7136\u5c06\u6768\u5929\u90a3\u79cd\u4eba\u5f53\u4f5c\u4e86\u670b \u53cb\u3002\u201d \u3000\u3000\u53f6\u5f00\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u4ee5\u524d\u5c82\u4e0d\u4e5f\u66fe\u5c06\u6768\u5929\u5f53\u505a\u670b\u53cb\uff1f\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u73b0\u5728\u624d\u4f1a\u8fd9\u4e48\u5012\u9709\u3002\u201d \u3000\u3000\u90ed\u5b9a\u672c\u6765\u4f3c\u5df2\u51b3\u5fc3\u4e0d\u542c\u4ed6\u4eec\u8bf4\u7684\u8bdd\uff0c\u6b64\u523b\u5ffd\u53c8\u56de\u8fc7\u5934\uff0c\u9053\uff1a\u201c\u6768\u5929\u4e0d\u662f\u4e2a\u597d\u670b\u53cb\u3002\u201d \u3000\u3000\u53f6\u5f00\u4e0d\u80fd\u4e0d\u627f\u8ba4\uff1a\u201c\u4ed6\u4e0d\u662f\u3002\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u4ed6\u51fa\u5356\u4e86\u4f60\u4eec\uff1f\u201d \u3000\u3000\u53f6\u5f00\u4e5f\u4e0d\u80fd\u5426\u8ba4\u3002 \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u4ed6\u548c\u4e0a\u5b98\u5c0f\u4ed9\u4e32\u901a\uff0c\u51fa\u5356\u4e86\u4f60\u4eec\uff1f\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u4ed6\u597d\u50cf\u5df2\u88ab\u4e0a\u5b98\u5c0f\u4ed9\u8ff7\u4f4f\u4e86\u3002\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u4f46\u4f60\u4eec\u672c\u6765\u4e5f\u662f\u8981\u4fdd\u62a4\u4e0a\u5b98\u5c0f\u4ed9\u7684\uff0c\u9664\u53bb\u4f60\u4eec\uff0c\u5bf9\u4e0a\u5b98\u5c0f\u4ed9\u4e5f\u6ca1\u6709\u597d\u5904\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u5979\u8981\u91cd\u632f\u91d1\u94b1\u5e2e\uff0c\u6768\u5929\u5df2\u505a\u4e86\u91d1\u94b1\u5e2e\u7684\u5802\u4e3b\u3002\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u6240\u4ee5\u5979\u8981\u9664\u53bb\u6240\u6709\u53ef\u80fd\u8ddf\u91d1\u94b1\u5e2e\u4f5c\u5bf9\u7684\u4eba\u3002\u201d \u3000\u3000\u4e86\u7075\u7433\u53f9\u9053\uff1a\u201c\u4f60\u603b\u7b97\u660e\u767d\u4e86\u3002\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u91d1\u94b1\u5e2e\u8981\u662f\u518d\u5ea6\u5174\u8d77\uff0c\u6211\u4e5f\u4e00\u5b9a\u4f1a\u8ddf\u4ed6\u4eec\u4f5c\u5bf9\u7684\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u6240\u4ee5\u4ed6\u7ea6\u4f60\u6765\uff0c\u6050\u6015\u4e5f\u4e0d\u4f1a\u6709\u4ec0\u4e48\u597d\u610f\u3002\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u73b0\u5728\u6211\u5df2\u6765\u4e86\uff0c\u5979\u4eec\u4e3a\u4ec0\u4e48\u4e0d\u5bf9\u6211\u4e0b\u624b\uff1f\u96be\u9053\u65e9\u5df2\u77e5\u9053\u4f60\u4eec\u4f1a\u88ab\u97e9\u8d1e\u6551\u8d70\uff1f \u6545\u610f\u8981\u6211\u6765\u5bf9\u4ed8\u4f60\u4eec\uff1f\u96be\u9053\u97e9\u8d1e\u4e5f\u662f\u91d1\u94b1\u5e2e\u7684\u4eba\uff0c\u6545\u610f\u5c06\u4f60\u4eec\u6551\u51fa\u6765\u5bf9\u4ed8\u6211\uff1f\u201d \u3000\u3000\u4e01\u7075\u7433\u8bf4\u4e0d\u51fa\u6765\u4e86\u3002 \u3000\u3000\u5979\u60f3\u7684\u5e76\u6ca1\u6709\u8fd9\u4e48\u591a\uff0c\u73b0\u5728\u624d\u60f3\u5230\uff0c\u8fd9\u5e76\u975e\u6ca1\u6709\u53ef\u80fd\u3002 \u3000\u3000\u53f6\u5f00\u5ffd\u7136\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u65e0\u8bba\u5982\u4f55\uff0c\u97e9\u8d1e\u603b\u662f\u6211\u4eec\u7684\u6551\u547d\u6069\u4eba\u3002\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u4ed6\u6709\u7406\u7531\u6551\u4f60\u4eec\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6709\u3002\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u4ed6\u662f\u4e0d\u662f\u4e5f\u6709\u7406\u7531\u51fa\u5356\u4f60\u4eec\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u4e0d\u613f\u8fd9\u4e48\u6837\u60f3\u3002\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u4f60\u662f\u4e2a\u6069\u6028\u5206\u660e\u7684\u4eba\u3002\u201d \u3000\u3000\u53f6\u5f00\u82e6\u7b11\u9053\uff1a\u201c\u6709\u4eba\u8fd9\u4e48\u8bf4\u8fc7\u3002\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u97e9\u8d1e\u82e5\u662f\u4f60\u4eec\u7684\u670b\u53cb\uff0c\u73b0\u5728\u5c31\u65e9\u5df2\u8be5\u56de\u6765\u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u5e76\u4e0d\u662f\u6bcf\u4e2a\u5730\u65b9\u90fd\u80fd\u627e\u5230\u9152\u7684\u3002\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u636e\u6211\u6240\u77e5\uff0c\u8fd9\u5730\u65b9\u5e94\u8be5\u6709\u4e2a\u9152\u7a96\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4e5f\u8bb8\u4e0a\u5b98\u5c0f\u4ed9\u5df2\u5c06\u90a3\u9152\u7a96\u6bc1\u4e86\u3002\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u56e0\u4e3a\u53ea\u6709\u9152\u624d\u53ef\u4ee5\u89e3\u6211\u7684\u6bd2\u3002\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u4f60\u73b0\u5728\u5e76\u6ca1\u6709\u559d\u9152\uff0c\u4f46\u4f60\u4e2d\u7684\u6bd2\u4e5f\u5df2\u89e3\u4e86\u3002\u201d \u3000\u3000\u53f6\u5f00\u4e5f\u8bf4\u4e0d\u51fa\u8bdd\u6765\u4e86\u3002 \u3000\u3000\u90ed\u5b9a\u51b7\u51b7\u4ed6\u8bf4\u9053\uff1a\u201c\u7528\u9152\u6765\u89e3\u6bd2\uff0c\u4e0d\u4f46\u8352\u8c2c\u900f\u9876\uff0c\u800c\u4e14\u5904\u5904\u77db\u76fe\uff0c\u5c31\u8fde\u4e09\u5c81\u7684\u5b69\u5b50\uff0c\u53ea \u6015\u90fd\u4e0d\u4f1a\u76f8\u4fe1\u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u4e0d\u60f3\u8fa9\u767d\uff0c\u4e5f\u4e0d\u80fd\u8fa9\u767d\u3002 \u3000\u3000\u90ed\u5b9a\u770b\u7740\u4ed6\uff0c\u5ffd\u7136\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f46\u4e5f\u4e0d\u77e5\u4e3a\u4e86\u4ec0\u4e48\uff0c\u6211\u5c45\u7136\u76f8\u4fe1\u4e86\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u7684\u773c\u775b\u4eae\u4e86\u8d77\u6765\uff0c\u7b11\u9053\uff1a\u201c\u6211\u5c31\u77e5\u9053\u4f60\u662f\u4e2a\u660e\u767d\u4eba\u3002\u201d \u3000\u3000\u90ed\u5b9a\u53c8\u6c89\u4e0b\u4e86\u8138\uff0c\u9053\uff1a\u201c\u4e5f\u8bb8\u5c31\u56e0\u4e3a\u6211\u4e0d\u662f\u4e2a\u660e\u767d\u4eba\uff0c\u6240\u4ee5\u6211\u624d\u4f1a\u76f8\u4fe1\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u4f60\u653e\u5fc3\uff0c\u6211\u4eec\u7edd\u4e0d\u4f1a\u8ba9\u4f60\u540e\u6094\u7684\u3002\u201d \u3000\u3000\u90ed\u5b9a\u51b7\u51b7\u9053\uff1a\u201c\u4f46\u4f60\u82e5\u627e\u4e0d\u5230\u4e0a\u5b98\u5c0f\u4ed9\u3001\u6768\u5929\u548c\u97e9\u8d1e\uff0c\u6211\u5374\u4e00\u5b9a\u4f1a\u8981\u4f60\u4eec\u540e\u6094\u7684\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u7528\u4e0d\u7740\u4f60\u8bf4\uff0c\u6211\u4eec\u4e5f\u4e00\u5b9a\u8981\u627e\u5230\u4ed6\u4eec\u3002\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u6211\u7ed9\u4f60\u4eec\u4e09\u5341\u516d\u4e2a\u65f6\u8fb0\u53bb\u627e\u3002\u201d \u3000\u3000\u4ed6\u4e0d\u8ba9\u4e01\u7075\u7433\u5f00\u53e3\uff0c\u63a5\u7740\u7ffc\u9053\uff1a\u201c\u4e09\u5929\u4e4b\u540e\u6211\u8fd8\u4f1a\u56de\u5230\u8fd9\u91cc\u6765\u627e\u4f60\u4eec\uff0c\u4e3a\u4e86\u4f60\u4eec\u81ea\u5df1 \u597d\uff0c\u6211\u5e0c\u671b\u4f60\u4eec\u80fd\u627e\u5230\u90a3\u4e9b\u4eba\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u6709\u4e09\u5929\u529f\u592b\uff0c\u60f3\u5fc5\u5df2\u8db3\u591f\u4e86\u3002\u201d \u3000\u3000\u90ed\u5b9a\u5df2\u8d70\u51fa\u53bb\uff0c\u5ffd\u53c8\u56de\u5934\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u4ef6\u4e8b\uff0c\u6211\u8981\u544a\u8bc9\u4f60\u4eec\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u6211\u4eec\u5728\u542c\u3002\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u8981\u627e\u4f60\u4eec\u7b97\u5e10\u7684\u4eba\uff0c\u5e76\u4e0d\u53ea\u6211\u4e00\u4e2a\uff0c\u5c31\u7b97\u6211\u76f8\u4fe1\u4f60\u4eec\u7684\u8bdd\uff0c\u522b\u4eba\u4e5f\u7edd\u4e0d\u4f1a\u76f8 \u4fe1\u7684\uff0c\u6240\u4ee5\u8fd9\u4e24\u5929\u4f60\u4eec\u6700\u597d\u5c0f\u5fc3\u3002\u201d \u3000\u3000\u53f6\u5f00\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u9664\u4e86\u4f60\u548c\u4f0a\u591c\u54ed\u5916\uff0c\u8fd8\u6709\u4e9b\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u90ed\u5b9a\u6c89\u541f\u7740\uff0c\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u4f60\u6709\u6ca1\u6709\u53bb\u730e\u8fc7\u72d0\uff1f\u201d \u3000\u3000\u53f6\u5f00\u70b9\u70b9\u5934\u3002 \u3000\u3000\u90ed\u5b9a\u76ee\u5149\u4f3c\u5df2\u5230\u4e86\u9065\u8fdc\u5904\uff0c\u5f90\u5f90\u9053\uff1a\u201c\u730e\u72d0\u6700\u597d\u7684\u65f6\u5019\uff0c\u901a\u5e38\u662f\u5728\u4e5d\u6708\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u4e5d\u6708\uff1f\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u90a3\u65f6\u79cb\u9ad8\u6c14\u723d\uff0c\u8fbd\u9614\u7684\u539f\u91ce\u4e0a\uff0c\u53ea\u8981\u6709\u4e00\u53ea\u72d0\u72f8\u51fa\u73b0\uff0c\u5c31\u4f1a\u6709\u65e0\u6570\u53ea\u82cd\u9e70\u98de \u8d77\uff0c\u53ea\u8981\u6709\u9e70\u98de\u8d77\uff0c\u90a3\u53ea\u72d0\u72f8\u5c31\u6b7b\u5b9a\u4e86\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u4f60\u73b0\u5728\u4e3a\u4ec0\u4e48\u8981\u8bf4\u8fd9\u4e9b\u8bdd\uff1f\u73b0\u5728\u5e76\u4e0d\u662f\u4e5d\u6708\u3002\u201d \u3000\u3000\u90ed\u5b9a\u5f90\u5f90\u9053\uff1a\u201c\u4f46\u73b0\u5728\u5374\u662f\u730e\u72d0\u7684\u65f6\u5019\uff0c\u5df2\u6709\u7fa4\u9e70\u98de\u8d77\u2026\u2026\u201d \u3000\u3000\u4ed6\u773c\u775b\u91cc\u95ea\u7740\u5149\uff0c\u4eff\u4f5b\u5df2\u770b\u5230\u65e0\u6570\u53ea\u77eb\u5065\u7684\u82cd\u9e70\uff0c\u5728\u957f\u5b89\u57ce\u4e0a\u7684\u5929\u7a7a\u4e2d\u98de\u7fd4\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u7ec8\u4e8e\u660e\u767d\uff1a\u201c\u96be\u9053\u6211\u4eec\u5c31\u662f\u90a3\u53ea\u72d0\u72f8\uff1f\u201d \u3000\u3000\u90ed\u5b9a\u6ca1\u6709\u518d\u8bf4\u8bdd\u3002 \u3000\u3000\u4ed6\u5934\u4e5f\u4e0d\u56de\u5730\u8d70\u4e0a\u77f3\u9636\uff0c\u8d70\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u4e01\u7075\u7433\u76ee\u9001\u7740\u4ed6\u8d70\u51fa\u53bb\uff0c\u75f4\u75f4\u5730\u6014\u4e86\u534a\u664c\uff0c\u5583\u5583\u9053\uff1a\u201c\u8fd9\u4eba\u7a76\u7adf\u662f\u6211\u4eec\u7684\u670b\u53cb\uff0c\u8fd8\u662f\u6211 \u4eec\u7684\u4ec7\u654c\uff1f\u201d \u3000\u3000\u53f6\u5f00\u6ca1\u6709\u56de\u7b54\uff0c\u4ed6\u4eff\u4f5b\u4e5f\u4e0d\u77e5\u9053\u8be5\u600e\u4e48\u6837\u56de\u7b54\u3002 \u3000\u3000\u4e01\u7075\u7433\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4e0d\u7ba1\u600e\u4e48\u6837\uff0c\u8fd9\u4e2a\u4eba\u5374\u4e0d\u80fd\u7b97\u662f\u4e2a\u574f\u4eba\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u7684\u786e\u4e0d\u80fd\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u4ed6\u4e0d\u4f46\u5f88\u6b63\u76f4\uff0c\u800c\u4e14\u8fd8\u5f88\u6709\u8da3\u3002\u201d \u3000\u3000\u53f6\u5f00\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4ed6\u770b\u6765\u4e5f\u5f88\u559c\u6b22\u4f60\u3002\u201d \u3000\u3000\u4e86\u7075\u7433\u9053\uff1a\u201c\u4ed6\u559c\u6b22\u6211\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u770b\u5f97\u51fa\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u7537\u4eba\u82e5\u662f\u559c\u6b22\u4e0a\u4e00\u4e2a\u5973\u4eba\uff0c\u4ed6\u770b\u5230\u8fd9\u4e2a\u5973\u4eba\u65f6\uff0c\u773c\u775b\u91cc\u7684\u8868\u60c5\u90fd\u4f1a\u4e0d\u4e00\u6837 \u7684\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u5ffd\u7136\u7b11\u4e86\uff1a\u201c\u4f60\u5728\u5403\u918b\u4e86\u3002\u201d \u3000\u3000\u5979\u7b11\u5f97\u5c31\u50cf\u662f\u7b2c\u4e00\u6735\u5728\u6625\u98ce\u4e2d\u5f00\u653e\u7684\u767e\u5408\uff1a\u201c\u6211\u559c\u6b22\u5403\u918b\u7684\u7537\u4eba\uff0c\u60f3\u4e0d\u5230\u4f60\u5c45\u7136\u4e5f\u4f1a\u5403 \u918b\u4e86\u3002\u201d \u3000\u3000\u53f6\u5f00\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u73b0\u5728\u5e76\u4e0d\u60f3\u5403\u918b\uff0c\u53ea\u60f3\u5403\u4e00\u53ea\u7096\u5f97\u5f88\u70c2\u7684\u5927\u8e44\u8180\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u770b\u7740\u4ed6\uff0c\u773c\u775b\u91cc\u9732\u51fa\u79cd\u5f88\u5947\u602a\u7684\u8868\u60c5\uff0c\u54ac\u7740\u5634\u5507\u9053\uff1a\u201c\u8fd8\u6709\u5462\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u5927\u76c6\u6c34\uff0c\u4e00\u5f20\u53c8\u8f6f\u53c8\u5e72\u51c0\u7684\u5e8a\u2026\u2026\u201d \u3000\u3000\u4ed6\u770b\u7740\u5979\uff0c\u773c\u775b\u91cc\u4e5f\u5e26\u7740\u79cd\u5f88\u5947\u602a\u7684\u8868\u60c5\u3002 \u3000\u3000\u4e01\u7075\u7433\u547b\u541f\u822c\u53f9\u4e86\u53e3\u6c14\uff0c\u8f7b\u8f7b\u9053\uff1a\u201c\u4f60\u60f3\u7684\u4e8b\u4e3a\u4ec0\u4e48\u4f1a\u8ddf\u6211\u4e00\u6837\uff1f\u201d \u3000\u3000\u53f6\u5f00\u5fae\u7b11\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u4eec\u5df2\u5f88\u4e45\u6ca1\u6709\u89c1\u9762\u4e86\uff0c\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u4e01\u7075\u7433\u7684\u8138\u7a81\u7136\u7ea2\u4e86\uff0c\u5ffd\u7136\u8df3\u8d77\u6765\u54ac\u4e86\u4ed6\u4e00\u53e3\uff1a\u201c\u4f60\u5b9e\u5728\u4e0d\u662f\u597d\u4e1c\u897f\uff0c\u6211\u54ac\u6b7b\u4f60\u2026\u2026\u201d \u3000\u3000\u5e8a\u5f88\u8f6f\uff0c\u4e5f\u5f88\u5e72\u51c0\u3002 \u3000\u3000\u53f6\u5f00\u8eba\u5728\u5e8a\u4e0a\uff0c\u4ed6\u8fd8\u6ca1\u6709\u88ab\u54ac\u6b7b\uff0c\u53ef\u662f\u770b\u8d77\u6765\u4e5f\u5e76\u4e0d\u50cf\u5f88\u5feb\u6d3b\u7684\u6837\u5b50\u3002 \u3000\u3000\u4e01\u7075\u7433\u4f0f\u5728\u4ed6\u80f8\u819b\u4e0a\u3002 \u3000\u3000\u4ed6\u7684\u80f8\u819b\u5bbd\u9614\u800c\u575a\u5b9e\u3002 \u3000\u3000\u5c4b\u5b50\u91cc\u5f88\u6e29\u6696\uff0c\u5c31\u50cf\u662f\u6625\u5929\u4e00\u6837\uff0c\u76c6\u91cc\u7684\u706b\u8fd8\u5f88\u65fa\u3002 \u3000\u3000\u5728\u8fd9\u4e48\u6e29\u6696\u7684\u5c4b\u5b50\u91cc\uff0c\u4e00\u4e2a\u4eba\u662f\u4e0d\u5fc5\u7a7f\u5927\u591a\u8863\u670d\u7684\u3002 \u3000\u3000\u4e24\u4e2a\u4eba\u66f4\u4e0d\u5fc5\u3002 \u3000\u3000\u4e01\u7075\u7433\u5ffd\u7136\u8f7b\u8f7b\u53f9\u4e86\u53e3\u6c14\uff0c\u8f7b\u8f7b\u9053\uff1a\u201c\u6211\u4eec\u8fd8\u6ca1\u6709\u6210\u4eb2\uff0c\u672c\u4e0d\u8be5\u8fd9\u6837\u5b50\u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u55ef\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u68a6\u5403\u822c\u4f4e\u8bed\u9053\uff1a\u201c\u6211\u603b\u89c9\u5f97\u8fd9\u6837\u5b50\u662f\u4e0d\u9053\u5fb7\u7684\uff0c\u6211\u603b\u89c9\u5f97\u6211\u4eec\u597d\u50cf\u72af\u4e86\u7f6a\u4e00\u6837\uff0c \u4f46\u4e5f\u4e0d\u77e5\u9053\u4e3a\u4e86\u4ec0\u4e48\uff0c\u6211\u6bcf\u6b21\u90fd\u6ca1\u6cd5\u5b50\u62d2\u7edd\u4f60\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u77e5\u9053\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u4f60\u77e5\u9053\uff1f\u201d \u3000\u3000\u53f6\u5f00\u770b\u7740\u5979\uff0c\u773c\u775b\u91cc\u5145\u6ee1\u4e86\u7231\u601c\u7684\u7b11\u610f\uff0c\u6df1\u6df1\u9053\uff1a\u201c\u4f60\u6ca1\u6709\u62d2\u7edd\u6211\uff0c\u53ea\u56e0\u4e3a\u4f60\u6bd4\u6211\u66f4\u559c \u6b22\u505a\u8fd9\u79cd\u72af\u7f6a\u7684\u4e8b\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u7684\u8138\u53c8\u7ea2\u4e86\uff0c\u7528\u529b\u54ac\u7740\u4ed6\u7684\u8033\u6735\uff0c\u6068\u6068\u9053\uff1a\u201c\u4f60\u8fd9\u4e2a\u574f\u4eba\uff0c\u4f60\u8fd8\u77e5\u9053\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u7a81\u542c\u4e00\u4eba\u9053\uff1a\u201c\u4ed6\u8fd8\u77e5\u9053\u6740\u4eba\u3002\u201d \u3000\u3000\u8fd9\u58f0\u97f3\u6e05\u8106\u5a07\u7f8e\uff0c\u800c\u4e14\u8fd8\u4eff\u4f5b\u5e26\u7740\u79cd\u5b69\u5b50\u822c\u7684\u5929\u771f\u3002 \u3000\u3000\u662f\u4e0a\u5b98\u5c0f\u4ed9\u3002 \u3000\u3000\u201c\u6211\u4eec\u6ca1\u6709\u53bb\u627e\u5979\uff0c\u5979\u53cd\u800c\u627e\u4e0a\u95e8\u6765\u4e86\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u722c\u4e86\u8d77\u6765\u3002 \u3000\u3000\u5979\u5f53\u7136\u6ca1\u6709\u771f\u7684\u722c\u8d77\u6765\uff0c\u5979\u60f3\u722c\u8d77\u6765\u7684\u65f6\u5019\uff0c\u624d\u53d1\u73b0\u81ea\u5df1\u8eab\u4e0a\u5c11\u4e86\u70b9\u4e1c\u897f\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u4ece\u91cc\u9762\u62f4\u7740\u7684\u95e8\uff0c\u5ffd\u7136\u5f00\u4e86\uff0c\u4e0a\u5b98\u5c0f\u4ed9\u751c\u751c\u5730\u5fae\u7b11\u7740\uff0c\u59d7\u59d7\u5730\u8d70\u4e86\u8fdb\u6765\uff0c\u624b \u91cc\u5c45\u7136\u53c8\u62b1\u7740\u4e2a\u6ce5\u5a03\u5a03\uff0c\u4e00\u53cc\u773c\u775b\u4e0d\u505c\u5730\u5728\u4e24\u4e2a\u4eba\u8138\u4e0a\u6253\u8f6c\u3002 \u3000\u3000\u8fd9\u6b21\u4e01\u7075\u7433\u5b9e\u5728\u662f\u771f\u7684\u60f3\u5c06\u8fd9\u53cc\u773c\u73e0\u5b50\u6316\u51fa\u6765\u4e86\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u6447\u7740\u5934\uff0c\u5403\u5403\u5730\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u505a\u8fd9\u79cd\u4e8b\u7684\u65f6\u5019\uff0c\u672c\u8be5\u7528\u5f20\u684c\u5b50\u628a\u95e8\u9876\u4e0a\u7684\uff0c\u4f60 \u4eec\u603b\u8be5\u77e5\u9053\uff0c\u8981\u4ece\u5916\u9762\u6311\u5f00\u91cc\u9762\u7684\u95e8\u95e9\uff0c\u5e76\u4e0d\u56f0\u96be\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u6068\u58f0\u9053\uff1a\u201c\u8c01\u60f3\u5230\u4f1a\u6709\u8fd9\u4e48\u4e0d\u8981\u8138\u7684\u4eba\u95ef\u8fdb\u6765\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u7b11\u9053\uff1a\u201c\u6211\u4e0d\u8981\u8138\uff0c\u4f60\u4eec\u5462\uff1f\u5929\u8fd8\u6ca1\u9ed1\u5c31\u8fd9\u6837\u5b50\u4e86\uff0c\u4f60\u4eec\u7f9e\u4e0d\u7f9e\uff1f\u201d \u3000\u3000\u4e01\u7075\u7433\u7684\u8138\u7ea2\u4e86\uff0c\u8d76\u7d27\u6539\u53d8\u8bdd\u9898\uff0c\u5927\u58f0\u9053\uff1a\u201c\u4f60\u6765\u5f97\u6b63\u597d\uff0c\u6211\u4eec\u6b63\u8981\u53bb\u6211\u4f60\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u662f\u4f60\u4eec\u5077\u5077\u6e9c\u4e86\uff0c\u4e3a\u4ec0\u4e48\u53c8\u8981\u627e\u6211\uff1f\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u4f60\u81ea\u5df1\u505a\u4e8b\uff0c\u4e3a\u4ec0\u4e48\u8981\u8d56\u5728\u6211\u4eec\u5934\u4e0a\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u60a0\u7136\u9053\uff1a\u201c\u53c8\u4e0d\u662f\u6211\u8d56\u4f60\u4eec\u7684\uff0c\u4eba\u5bb6\u8981\u8ba4\u4e3a\u662f\uff0c\u6211\u53c8\u6709\u4ec0\u4e48\u6cd5\u5b50\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u4f60\u627f\u8ba4\u662f\u4f60\u6740\u7684\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6211\u627f\u8ba4\u3002\u201d\u5979\u7b11\u4e86\u7b11\u53c8\u9053\uff1a\u201c\u4e0d\u8fc7\u6211\u53ea\u5728\u4f60\u4eec\u9762\u524d\u627f\u8ba4\uff0c\u82e5\u6709\u522b\u4eba\u5728\uff0c \u6211\u5c31\u4e0d\u627f\u8ba4\u4e86\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u6012\u9053\uff1a\u201c\u4e0d\u627f\u8ba4\u5c31\u6740\u4e86\u4f60\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u7b11\u9053\uff1a\u201c\u4f60\u82e5\u771f\u7684\u6740\u4e86\u6211\uff0c\u5c31\u66f4\u7cdf\u4e86\uff0c\u8fd9\u4ef6\u4e8b\u5c31\u66f4\u53d8\u5f97\u6b7b\u65e0\u5bf9\u8bc1\uff0c\u4f60\u4eec\u5c31\u7b97\u8df3 \u5230\u9ec4\u6cb3\u91cc\u53bb\u4e5f\u6d17\u4e0d\u6e05\u4e86\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u54ac\u4e86\u54ac\u7259\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u6211\u4eec\u603b\u6709\u6cd5\u5b50\u53eb\u4f60\u627f\u8ba4\u7684\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u54e6\uff1f\u6211\u60f3\u542c\u542c\u4f60\u4eec\u6709\u4ec0\u4e48\u6cd5\u5b50\uff1f\u201d \u3000\u3000\u4e86\u7075\u7433\u9053\uff1a\u201c\u4f60\u82e5\u4e0d\u627f\u8ba4\uff0c\u6211\u5c31\u6316\u51fa\u4f60\u8fd9\u53cc\u773c\u73e0\u5b50\u6765\uff0c\u770b\u4f60\u8fd8\u6562\u4e0d\u6562\u8d56\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4f60\u662f\u51c6\u5907\u73b0\u5728\u6316\uff0c\u8fd8\u662f\u5728\u522b\u4eba\u9762\u524d\u6316\u5462\uff1f\u201d \u3000\u3000\u5979\u5fae\u7b11\u7740\uff0c\u60a0\u7136\u9053\uff1a\u201c\u73b0\u5728\u6211\u6839\u672c\u5c31\u627f\u8ba4\u4e86\uff0c\u4f60\u4eec\u6839\u672c\u4e0d\u5fc5\u903c\u6211\uff0c\u82e5\u662f\u7b49\u5230\u522b\u4eba\u5728\u65c1\u8fb9 \u65f6\uff0c\u6bcf\u4e2a\u4eba\u90fd\u77e5\u9053\u6211\u53ea\u4e0d\u8fc7\u662f\u4e2a\u767d\u75f4\uff0c\u53ea\u4f1a\u62b1\u7740\u6ce5\u5a03\u5a03\u5582\u5976\uff0c\u4f60\u4eec\u5c31\u7b97\u771f\u7684\u5fcd\u5fc3\u5bf9\u6211\u4e0b\u8fd9\u79cd \u6bd2\u624b\uff0c\u522b\u4eba\u4e5f\u4e0d\u4f1a\u7b54\u5e94\u7684\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u6c14\u5f97\u8138\u90fd\u9752\u4e86\uff0c\u5374\u504f\u504f\u60f3\u4e0d\u51fa\u6cd5\u5b50\u6765\u5bf9\u4ed8\u5979\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u67d4\u58f0\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u4eec\u65e2\u4e0d\u80fd\u6740\u6211\uff0c\u4e5f\u4e0d\u80fd\u903c\u6211\uff0c\u5c31\u7b97\u628a\u6211\u6293\u4f4f\uff0c\u4e5f\u4e00\u6837\u8fde\u534a\u70b9 \u7528\u90fd\u6ca1\u6709\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u6068\u6068\u9053\uff1a\u201c\u4f60\u8003\u8651\u5f97\u5f88\u5468\u5230\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u82e5\u662f\u6ca1\u6709\u8003\u8651\u5468\u5230\uff0c\u53c8\u600e\u4e48\u4f1a\u6562\u6765\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u5df2\u6c14\u5f97\u5feb\u75af\u4e86\uff0c\u5fcd\u4e0d\u4f4f\u6253\u4e86\u53f6\u5f00\u4e00\u62f3\uff0c\u9053\uff1a\u201c\u4f60\u600e\u4e48\u4e0d\u8bf4\u8bdd\uff1f\u201d \u3000\u3000\u53f6\u5f00\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u6ca1\u6709\u8bdd\u8bf4\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u5ae3\u7136\u9053\uff1a\u201c\u6bd5\u7adf\u8fd8\u662f\u4f60\u806a\u660e\uff0c\u8fd8\u662f\u4f60\u60f3\u5f97\u5f00\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u800c\u4e14\u6211\u4e5f\u5f88\u653e\u5fc3\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u653e\u5fc3\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u73b0\u5728\u6211\u4eec\u867d\u7136\u6ca1\u6cd5\u5b50\u5bf9\u4ed8\u4f60\uff0c\u4f60\u4e5f\u4e0d\u4f1a\u5bf9\u4ed8\u6211\u4eec\u7684\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u56e0\u4e3a\u4f60\u8fd8\u8981\u903c\u7740\u6211\u4eec\u8ddf\u522b\u4eba\u62fc\u547d\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u7b11\u9053\uff1a\u201c\u4e00\u70b9\u4e5f\u4e0d\u9519\uff0c\u90ed\u5b9a\u3001\u4f0a\u591c\u54ed\u4ed6\u4eec\uff0c\u90fd\u662f\u5f88\u96be\u5bf9\u4ed8\u7684\u4eba\uff0c\u6211\u4e0d\u8d39\u5439\u7070\u4e4b \u529b\uff0c\u5c31\u627e\u5230\u4e86\u4f60\u8fd9\u6837\u7684\u597d\u5e2e\u624b\uff0c\u5e2e\u7740\u6211\u53bb\u5bf9\u4ed8\u4ed6\u4eec\uff0c\u6211\u53c8\u600e\u820d\u5f97\u8ba9\u4f60\u6b7b\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u53c8\u5fcd\u4e0d\u4f4f\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u624d\u6545\u610f\u8ba9\u97e9\u8d1e\u6551\u6211\u4eec\u8d70\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u7728\u4e86\u7728\u773c\u9053\uff1a\u201c\u4f60\u731c\u5462\uff1f\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u96be\u9053\u97e9\u8d1e\u4e5f\u662f\u4f60\u624b\u4e0b\u7684\u4eba\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u5f88\u53ef\u80fd\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u4e48\u6837\u8bf4\uff0c\u6211\u53cd\u800c\u77e5\u9053\u4ed6\u4e0d\u662f\u4e86\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u968f\u4fbf\u4f60\u600e\u6837\u60f3\u90fd\u884c\u3002\u3001\u4e01\u7075\u7433\u9053\uff1a\u201c\u6240\u4ee5\u53ea\u8981\u6211\u4eec\u627e\u5230\u4ed6\uff0c\u5c31\u53ef\u4ee5\u8bc1\u660e \u4f60\u662f\u4e2a\u600e\u4e48\u6837\u7684\u4eba\u4e86\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u522b\u4eba\u4f1a\u76f8\u4fe1\u90a3\u6837\u7684\u8bdd\uff1f\u201d \u3000\u3000\u5979\u53f9\u4e86\u53e3\u6c14\uff0c\u6447\u7740\u5934\u9053\uff1a\u201c\u6211\u770b\u4f60\u624d\u771f\u7684\u53ea\u4e0d\u8fc7\u662f\u4e2a\u4e03\u5c81\u5927\u7684\u5b69\u5b50\uff0c\u97e9\u8d1e\u82e5\u662f\u771f\u80fd\u63ed\u7a7f \u6211\u7684\u79d8\u5bc6\uff0c\u6211\u53c8\u600e\u4e48\u4f1a\u8ba9\u4f60\u4eec\u627e\u5230\u4ed6\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u53d8\u8272\u9053\uff1a\u201c\u83ab\u975e\u4f60\u4e5f\u628a\u4ed6\u6740\u4e86\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u5e76\u6ca1\u6709\u5426\u8ba4\uff0c\u60a0\u7136\u9053\uff1a\u201c\u4e0d\u7ba1\u600e\u4e48\u6837\uff0c\u8fd9\u4ef6\u4e8b\u9664\u975e\u6211\u81ea\u5df1\u80af\u5728\u522b\u4eba\u9762\u524d\u627f\u8ba4\uff0c \u5426\u5219\u4f60\u4eec\u5c31\u53ea\u6709\u80cc\u7740\u8fd9\u51a4\u540d\u4e86\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u54ac\u7740\u7259\uff0c\u6068\u6068\u9053\uff1a\u201c\u597d\u72e0\u6bd2\u7684\u5973\u4eba\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u6de1\u6de1\u9053\uff1a\u201c\u80cc\u7740\u8fd9\u6837\u7684\u51a4\u540d\uff0c\u5b9e\u5728\u4e0d\u662f\u4ef6\u503c\u5f97\u9ad8\u5174\u7684\u4e8b\uff0c\u73b0\u5728\u957f\u5b89\u57ce\u91cc\uff0c\u81f3\u5c11 \u6709\u5341\u516d\u516b\u4e2a\u4eba\u60f3\u8981\u4f60\u4eec\u7684\u8111\u888b\uff0c\u6240\u4ee5\u2026\u2026\u201d \u3000\u3000\u53f6\u5f00\u7ec8\u4e8e\u5f00\u53e3\u9053\uff1a\u201c\u6240\u4ee5\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u5c31\u8d76\u5feb\u60f3\u4e2a\u6cd5\u5b50\uff0c\u8ba9\u6211\u627f\u8ba4\u5440\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u80af\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u522b\u4eba\u53cd\u6b63\u8fdf\u65e9\u603b\u8981\u77e5\u9053\u91d1\u94b1\u5e2e\u4e3b\u662f\u8c01\u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u53f9\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u4ed6\u4eec\u5927\u6982\u8981\u7b49\u6211\u6b7b\u4e86\u4e4b\u540e\u624d\u4f1a\u77e5\u9053\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u5f88\u53ef\u80fd\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u96be\u9053\u4f60\u80af\u5148\u544a\u8bc9\u4ed6\u4eec\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u53ea\u8981\u4f60\u80af\u7b54\u5e94\u6211\u4e00\u4ef6\u4e8b\uff0c\u6211\u5148\u6b7b\u4e5f\u65e0\u59a8\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u8981\u6211\u7b54\u5e94\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u7b54\u5e94\u5ac1\u7ed9\u6211\u3002\u201d \u3000\u3000\u53f6\u5f00\u6014\u4e86\u6014\uff0c\u9053\uff1a\u201c\u4f60\u8981\u8c01\u5ac1\u7ed9\u4f60\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u8981\u4f60\u3002\u201d \u3000\u3000\u53f6\u5f00\u7b11\u4e86\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4f60\u7b11\u4ec0\u4e48\uff0c\u7537\u4eba\u53ef\u4ee5\u5a36\u8001\u5a46\uff0c\u5973\u4eba\u96be\u9053\u5c31\u4e0d\u80fd\u5a36\u4e2a\u8001\u516c\uff1f\u201d\u5979\u5c45\u7136\u6ca1\u6709 \u7b11\uff0c\u677f\u7740\u8138\u53c8\u8bf4\u9053\uff1a\u201c\u4f55\u51b5\uff0c\u6211\u662f\u5929\u4e0b\u7b2c\u4e00\u5927\u5e2e\u7684\u5e2e\u4e3b\uff0c\u4ee5\u6211\u7684\u8eab\u4efd\u5c31\u7b97\u5a36\u4e2a\u5341\u4e2a\u516b\u4e2a\u8001 \u516c\uff0c\u4e5f\u662f\u5929\u7ecf\u5730\u4e49\u7684\u4e8b\u3002\u201d \u3000\u3000\u53f6\u5f00\u597d\u50cf\u5df2\u6709\u70b9\u7b11\u4e0d\u51fa\u4e86\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6211\u672c\u6765\u662f\u60f3\u8981\u4f60\u505a\u7b2c\u4e00\u62a4\u6cd5\u7684\uff0c\u5374\u53c8\u4e0d\u80fd\u4fe1\u4efb\u4f60\uff0c\u6240\u4ee5\u53ea\u597d\u52c9\u5f3a\u8981\u4f60\u505a \u8001\u516c\u4e86\uff0c\u8001\u516c\u6211\u603b\u53ef\u4ee5\u7ba1\u5f97\u4f60\u7684\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u8138\u5df2\u6c14\u5f97\u901a\u7ea2\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u5fc5\u52c9\u5f3a\uff0c\u4ed6\u5df2\u7ecf\u5ac1\u7ed9\u4e86\u6211\uff0c\u6839\u672c\u5c31\u8f6e\u4e0d\u5230\u4f60\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u7b11\u4e86\u7b11\uff0c\u60a0\u7136\u9053\uff1a\u201c\u83ab\u5fd8\u8bb0\u7537\u4eba\u4e5f\u4e00\u6837\u53ef\u4ee5\u6539\u5ac1\u7684\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u7ec8\u4e8e\u5fcd\u4e0d\u4f4f\u53eb\u4e86\u8d77\u6765\uff1a\u201c\u6211\u6b7b\u4e5f\u4e0d\u4f1a\u8ba9\u4ed6\u5ac1\u7ed9\u4f60\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u53f9\u4e86\u53e3\u6c14\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u5c31\u53ea\u597d\u53bb\u6b7b\u4e86\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u53c8\u7528\u529b\u6253\u4e86\u53f6\u5f00\u4e00\u62f3\uff0c\u6068\u6068\u9053\uff1a\u201c\u4f60\u600e\u4e48\u53c8\u4e0d\u8bf4\u8bdd\u4e86\uff0c\u96be\u9053\u5ffd\u7136\u53d8\u6210\u4e86\u54d1\u5df4\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u6b63\u5728\u8003\u8651\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u53c8\u53eb\u4e86\u8d77\u6765\uff1a\u201c\u4f60\u5728\u8003\u8651\uff0c\u8003\u8651\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u5728\u8003\u8651\u5e94\u8be5\u600e\u6837\u628a\u5979\u6254\u51fa\u53bb\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u7684\u95f7\u6c14\u7acb\u523b\u5e73\u4e86\uff0c\u5c55\u989c\u7b11\u9053\uff1a\u201c\u4f60\u7684\u786e\u5e94\u8be5\u518d\u8003\u8651\u8003\u8651\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u53f9\u9053\uff1a\u201c\u751f\u610f\u4e0d\u6210\u4ec1\u4e49\u5728\uff0c\u4f60\u5c31\u662f\u4e0d\u7b54\u5e94\uff0c\u4e5f\u4e0d\u8be5\u8fd9\u6837\u5bf9\u6211\u7684\uff0c\u6211\u81f3\u5c11\u603b\u662f\u4f60 \u7684\u5ba2\u4eba\u3002\u201d \u3000\u3000\u4e86\u7075\u7433\u9053\uff1a\u201c\u6211\u4eec\u5e76\u6ca1\u6709\u8bf7\u4f60\u6765\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4f46\u6211\u5374\u5df2\u7ecf\u6765\u4e86\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u4f60\u600e\u4e48\u4f1a\u627e\u5230\u8fd9\u91cc\u6765\u7684\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u91cc\u4e0d\u4f46\u6709\u6700\u597d\u7684\u53a8\u5b50\uff0c\u8fd8\u6709\u6700\u8212\u670d\u7684\u5e8a\uff0c\u6211\u6070\u5de7\u53c8\u77e5\u9053\u4f60\u4eec \u90fd\u662f\u559c\u6b22\u4eab\u53d7\u7684\u4eba\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u773c\u73e0\u5b50\u8f6c\u4e86\u8f6c\uff0c\u9053\uff1a\u201c\u4f60\u65e2\u7136\u662f\u5ba2\u4eba\uff0c\u5c31\u8be5\u505a\u4e9b\u5ba2\u4eba\u7684\u6837\u5b50\u51fa\u6765\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u5ba2\u4eba\u5e94\u8be5\u662f\u4ec0\u4e48\u6837\u5b50\u7684\uff1f\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u4f60\u81f3\u5c11\u5e94\u8be5\u5148\u51fa\u53bb\uff0c\u8ba9\u6211\u4eec\u597d\u597d\u6765\u8fce\u63a5\u4f60\u3002\u201d \u3000\u3000\u5979\u73b0\u5728\u706b\u6c14\u5df2\u6d88\u4e86\uff0c\u5ffd\u7136\u53c8\u53d8\u5f97\u673a\u7075\u4e86\u8d77\u6765\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u7b11\u9053\uff1a\u201c\u6211\u660e\u767d\u4f60\u7684\u610f\u601d\u4e86\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u4f60\u5e94\u8be5\u660e\u767d\u4e86\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6211\u8f6c\u8fc7\u8eab\u53bb\uff0c\u4e0d\u770b\u4f60\u4eec\u884c\u4e0d\u884c\uff1f\u201d \u3000\u3000\u4e01\u7075\u7433\u6068\u5f97\u7259\u75d2\u75d2\u7684\uff0c\u4f46\u4eba\u5bb6\u786c\u662f\u4e0d\u80af\u51fa\u53bb\uff0c\u5979\u4e5f\u6ca1\u6cd5\u5b50\u3002 \u3000\u3000\u5e78\u597d\u4e0a\u5b98\u5c0f\u4ed9\u5df2\u771f\u7684\u8f6c\u8fc7\u4e86\u8eab\uff0c\u9762\u5bf9\u7740\u5899\uff0c\u60a0\u7136\u9053\uff1a\u201c\u6211\u771f\u5947\u602a\uff0c\u5728\u8fd9\u79cd\u5929\u6c14\u91cc\uff0c\u4f60\u4eec \u5c45\u7136\u597d\u50cf\u4e00\u70b9\u4e5f\u4e0d\u6015\u51b7\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u6ca1\u6709\u5f00\u53e3\uff0c\u4e5f\u6ca1\u7a7a\u5f00\u53e3\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u542c\u8bf4\u4f60\u4ee5\u524d\u8eab\u4e0a\u603b\u662f\u6302\u7740\u5f88\u591a\u94c1\u94c3\u94db\u7684\uff0c\u82e5\u662f\u4e0d\u6458\u4e0b\u6765\uff0c\u5c82\u975e\u66f4\u597d \u73a9\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u672c\u5c31\u5728\u540e\u6094\uff0c\u5979\u8eab\u4e0a\u82e5\u6234\u7740\u90a3\u4e9b\u8981\u547d\u7684\u91d1\u94c3\uff0c\u65e9\u5df2\u5c06\u4e0a\u5b98\u5c0f\u4ed9\u5934\u4e0a\u6253\u51fa\u597d\u51e0\u4e2a\u6d1e \u6765\u4e86\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u4e0a\u5b98\u5c0f\u4ed9\u7a81\u7136\u5927\u53eb\u4e86\u4e00\u58f0\uff0c\u5c31\u597d\u50cf\u5ffd\u7136\u89c1\u5230\u4e86\u9b3c\u4e00\u6837\uff0c\u649e\u7834\u7a97\u6237\uff0c\u7a9c\u4e86\u51fa \u53bb\uff0c\u624b\u91cc\u7684\u6ce5\u5a03\u5a03\u4e5f\u6389\u5728\u5730\u4e0a\uff0c\u6454\u5f97\u7c89\u788e\u3002 \u3000\u3000\u4e01\u7075\u7433\u4e5f\u53eb\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u201c\u4e0d\u7ba1\u600e\u4e48\u6837\uff0c\u4e5f\u4e0d\u80fd\u8ba9\u5979\u8d70\u3002\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u8fd8\u6ca1\u6709\u8bf4\u5b8c\uff0c\u53f6\u5f00\u4e5f\u5df2\u7a9c\u51fa\u7a97\u5b50\u3002 \u3000\u3000\u5973\u4eba\u7a7f\u8863\u670d\u603b\u662f\u6162\u4e9b\u7684\uff0c\u7b49\u5979\u7a7f\u597d\u8863\u670d\u65f6\uff0c\u4e0a\u5b98\u5c0f\u4ed9\u65e9\u5df2\u8fde\u5f71\u5b50\u90fd\u770b\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u53f6\u5f00\u662f\u4e2a\u5f88\u5947\u602a\u7684\u4eba\uff0c\u4ed6\u672c\u6765\u5e76\u4e0d\u60f3\u592a\u51fa\u540d\uff0c\u6240\u4ee5\u4ed6\u521d\u4eba\u6c5f\u6e56\u65f6\uff0c\u7528\u597d\u51e0\u4e2a\u540d\u5b57\u3002 \u3000\u3000\u4f46\u4e16\u754c\u4e0a\u7684\u4e8b\u5f80\u5f80\u4e5f\u5f88\u5947\u602a\uff0c\u4e0d\u60f3\u51fa\u540d\u7684\u4eba\uff0c\u53cd\u800c\u504f\u504f\u4f1a\u51fa\u540d\u3002 \u3000\u3000\u4ed6\u7528\u8fc7\u7684\u540d\u5b57\u51e0\u4e4e\u90fd\u5df2\u5f88\u6709\u540d\u4e86\uff0c\u5176\u4e2d\u6700\u6709\u540d\u7684\u4e00\u4e2a\uff0c\u5f53\u7136\u8fd8\u662f\u98ce\u90ce\u541b\u3002\u56e0\u4e3a\u4ed6\u7684\u8f7b\u529f \u5b9e\u5728\u5f88\u9ad8\uff0c\u6709\u4eba\u751a\u81f3\u8ba4\u4e3a\u4ed6\u7684\u98de\u5200\u8fd8\u6bd4\u4e0d\u4e0a\u5c0f\u674e\u63a2\u82b1\uff0c\u4f46\u8f7b\u529f\u5374\u5df2\u4e0d\u5728\u4efb\u4f55\u4eba\u4e4b\u4e0b\u3002 \u3000\u3000\u8fd8\u6709\u7684\u4eba\u751a\u81f3\u8ba4\u4e3a\uff0c\u8fd1\u516b\u5341\u5e74\u6765\uff0c\u6b66\u6797\u4e2d\u8f7b\u529f\u6700\u9ad8\u7684\u4e00\u4e2a\u4eba\u5c31\u662f\u4ed6\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u5c45\u7136\u6ca1\u6709\u8ffd\u5230\u4e0a\u5b98\u5c0f\u4ed9\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u4e00\u51fa\u4e86\u90a3\u5c4b\u5b50\uff0c\u5c31\u597d\u50cf\u5ffd\u7136\u5947\u8ff9\u822c\u6d88\u5931\u4e86\u3002 \u3000\u3000\u53f6\u5f00\u8ffd\u51fa\u4e86\u5f88\u8fdc\uff0c\u5374\u8fde\u5979\u7684\u5f71\u5b50\u90fd\u6ca1\u6709\u770b\u89c1\u3002 \u3000\u3000\u73b0\u5728\u5df2\u662f\u9ec4\u660f\u3002 \u3000\u3000\u9ec4\u660f\u7684\u98ce\u66f4\u51b7\uff0c\u53f6\u5f00\u5e76\u4e0d\u60f3\u50cf\u50bb\u5b50\u4e00\u6837\u7ad9\u5728\u9732\u5929\u91cc\u559d\u897f\u5317\u98ce\u3002 \u3000\u3000\u65e2\u7136\u8feb\u4e0d\u5230\uff0c\u5c31\u53ea\u6709\u5148\u56de\u53bb\u518d\u8bf4\u3002\u201d \u3000\u3000\u4e5f\u4e0d\u77e5\u4e3a\u4e86\u4ec0\u4e48\uff0c\u4ed6\u8fd1\u6765\u5bf9\u4e01\u7075\u7433\u5df2\u8d8a\u6765\u8d8a\u70ed\u5fc3\u3002 \u3000\u3000\u4ed6\u4ece\u539f\u6765\u7684\u8def\u9000\u56de\u53bb\uff0c\u521a\u624d\u88ab\u649e\u7834\u7684\u7a97\u6237\uff0c\u88ab\u51b7\u98ce\u5439\u5f97\u201c\u5657\u9c81\u5657\u9c81\u201d\u7684\u76f4\u54cd\u3002 \u3000\u3000\u4ed6\u6b63\u60f3\u63a5\u8fd1\u7a97\u6237\uff0c\u5ffd\u7136\u6014\u4f4f\u4e86\uff0c\u8fd9\u5c4b\u5b50\u91cc\u7adf\u7136\u53d8\u5f97\u70ed\u95f9\u8d77\u6765\u4e86\u3002 \u7b2c\u5341\u4e00\u7ae0\u3000\u4e1c\u6d77\u7389\u7bab>> \u53e4\u9f99\u300a\u4e5d\u6708\u9e70\u98de\u300b \u7b2c\u5341\u4e00\u7ae0\u3000\u4e1c\u6d77\u7389\u7bab \u3000\u3000\u5c0f\u5c0f\u7684\u4e00\u95f4\u5c4b\u5b50\uff0c\u5f53\u4e2d\u7adf\u6709\u4e86\u516b\u4e5d\u4e2a\u4eba\uff0c\u51e0\u4e4e\u5168\u90fd\u662f\u5973\u4eba\uff0c\u800c\u4e14\u5168\u90fd\u662f\u5f88\u5e74\u8f7b\u3001\u5f88\u7f8e\u8273 \u7684\u5c11\u5973\uff0c\u5374\u53c8\u504f\u504f\u5168\u90e8\u7a7f\u7740\u9053\u88c5\u3002 \u3000\u3000\u54ea\u91cc\u6765\u7684\u8fd9\u4e48\u591a\u5973\u9053\u58eb\uff1f \u3000\u3000\u53f6\u5f00\u51e0\u4e4e\u5df2\u8ba4\u4e3a\u81ea\u5df1\u8d70\u9519\u4e86\u5730\u65b9\uff0c\u4f46\u4e01\u7075\u7433\u5374\u8fd8\u5728\u5c4b\u5b50\u91cc\u3002 \u3000\u3000\u5979\u52a8\u4e5f\u4e0d\u52a8\u5730\u5750\u5728\u90a3\u91cc\uff0c\u773c\u91cc\u5145\u6ee1\u4e86\u60ca\u8bb6\u4e4b\u8272\uff0c\u4e0d\u4f46\u60ca\u8bb6\uff0c\u7adf\u7136\u8fd8\u6709\u4e9b\u6050\u60e7\u3002 \u3000\u3000\u5979\u8eab\u540e\u7ad9\u7740\u4e24\u4e2a\u5973\u9053\u4eba\uff0c\u524d\u9762\u8fd8\u6709\u4e94\u4e2a\uff0c\u4f46\u5979\u7684\u773c\u775b\uff0c\u5374\u76ef\u5728\u4e00\u4e2a\u7537\u4eba\u8eab\u4e0a\u3002 \u3000\u3000\u4e00\u4e2a\u8001\u4eba\uff0c\u4e00\u4e2a\u8001\u9053\u4eba\u3002 \u3000\u3000\u4ed6\u5c31\u5750\u5728\u9760\u7a97\u7684\u4e00\u5f20\u6905\u5b50\u4e0a\uff0c\u8eab\u4e0a\u7a7f\u7740\u4ef6\u9526\u4e61\u9053\u888d\uff0c\u94f6\u4e1d\u822c\u7684\u5934\u53d1\uff0c\u633d\u6210\u4e86\u4e2a\u9053\u58eb\u9afb\uff0c \u659c\u63d2\u7740\u6839\u6676\u83b9\u5706\u6da6\u7684\u7bab\u3002 \u3000\u3000\u4ed6\u7684\u5e74\u7eaa\u81f3\u5c11\u4e5f\u5e94\u8be5\u5728\u516d\u5341\u4ee5\u4e0a\uff0c\u4f46\u8138\u8272\u5374\u662f\u7ea2\u6da6\u7684\uff0c\u8fde\u4e00\u6761\u76b1\u7eb9\u90fd\u627e\u4e0d\u5230\uff0c\u4e00\u53cc\u773c\u775b \u4e5f\u4ecd\u7136\u9ed1\u767d\u5206\u660e\uff0c\u70af\u70af\u6709\u5149\u3002 \u3000\u3000\u7eb5\u7136\u662f\u5750\u5728\u90a3\u91cc\uff0c\u5979\u4e5f\u770b\u5f97\u51fa\u4ed6\u8eab\u6750\u4ecd\u7136\u662f\u7b14\u633a\u7684\uff0c\u7edd\u6ca1\u6709\u4e1d\u6beb\u9f99\u949f\u8001\u6001\uff0c\u9886\u4e0b\u94f6\u4e1d\u822c \u7684\u957f\u9aef\u98d8\u62c2\uff0c\u4fee\u5f97\u5e72\u51c0\u800c\u6574\u9f50\u3002 \u3000\u3000\u53f6\u5f00\u4ece\u6765\u4e5f\u6ca1\u6709\u770b\u8fc7\u88c5\u9970\u5982\u6b64\u8273\u4e3d\u3001\u5982\u6b64\u6ce8\u610f\u4eea\u8868\u7684\u9053\u4eba\u3002 \u3000\u3000\u4e01\u7075\u7433\u5df2\u770b\u89c1\u4ed6\uff0c\u5979\u4eff\u4f5b\u60f3\u53eb\uff0c\u5374\u6ca1\u6709\u53eb\u51fa\u6765\u3002 \u3000\u3000\u5979\u7adf\u7136\u5df2\u88ab\u4eba\u70b9\u4f4f\u4e86\u7a74\u9053\u3002 \u3000\u3000\u53f6\u5f00\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u770b\u6765\u8fd9\u4e2a\u5c4b\u5b50\u7684\u98ce\u6c34\u771f\u4e0d\u9519\uff0c\u5ba2\u4eba\u521a\u8d70\u4e86\u4e00\u4e2a\uff0c\u53c8\u6765\u4e86\u516b\u4e2a\uff0c\u201d\u8fd9 \u9526\u888d\u94f6\u987b\u7684\u8001\u9053\u4eba\u4e5f\u6b63\u5728\u76ef\u7740\u4ed6\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u4f60\u5c31\u662f\u53f6\u5f00\uff1f\u201d \u3000\u3000\u53f6\u5f00\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6811\u53f6\u7684\u53f6\uff0c\u5f00\u5fc3\u7684\u5f00\uff0c\u201d\u9053\u4eba\u9053\uff1a\u201c\u98ce\u90ce\u541b\u4e5f\u662f\u4f60\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6709\u65f6\u5019\u662f\u7684\u3002\u201d \u3000\u3000\u9053\u4eba\u6c89\u7740\u8138\u51b7\u51b7\u9053\uff1a\u201c\u8fd1\u5e74\u6765\uff0c\u6c5f\u6e56\u4e2d\u679c\u7136\u662f\u4eba\u624d\u8f88\u51fa\uff0c\u4e00\u591c\u95f4\u8fde\u4f24\u516b\u5341\u4e09\u6761\u4eba\u547d\u7684\u597d \u6c49\uff0c\u6614\u65e5\u8d2b\u9053\u8fde\u4e00\u4e2a\u90fd\u672a\u66fe\u9047\u89c1\u8fc7\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u4e5f\u6ca1\u89c1\u8fc7\u3002\u201d \u3000\u3000\u9053\u4eba\u5389\u58f0\u9053\uff1a\u201c\u4f60\u5728\u8d2b\u9053\u9762\u524d\uff0c\u8bf4\u8bdd\u4e5f\u6562\u5982\u6b64\u8f7b\u8584\u3002\u201d \u3000\u3000\u53f6\u5f00\u7b11\u4e86\u7b11\u9053\uff1a\u201c\u9053\u957f\u82e5\u662f\u770b\u4e0d\u60ef\u8f7b\u8584\u7684\u4eba\uff0c\u4e3a\u4f55\u8981\u5230\u8f7b\u8584\u4eba\u7684\u5c4b\u91cc\u6765\uff1f\u201d \u3000\u3000\u9053\u4eba\u9053\uff1a\u201c\u4f60\u4e0d\u77e5\u9053\u6211\u662f\u8c01\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u9053\u4eba\u9053\uff1a\u201c\u8d2b\u9053\u7389\u7bab\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4e1c\u6d77\u7389\u7bab\uff1f\u201d \u3000\u3000\u9053\u4eba\u9053\uff1a\u201c\u6b63\u662f\u3002\u201d \u3000\u3000\u53f6\u5f00\u53c8\u53f9\u4e86\u53e3\u6c14\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u6211\u672c\u6765\u5b9e\u5728\u5e94\u8be5\u5927\u5403\u4e00\u60ca\u7684\uff0c\u53ea\u53ef\u60dc\u6211\u4eca\u5929\u5403\u60ca\u7684\u6b21\u6570\u5df2 \u5927\u591a\u4e86\u3002\u201d \u3000\u3000\u4e1c\u6d77\u7389\u7bab\uff01\uff0c\u65e0\u8bba\u8c01\u542c\u89c1\u8fd9\u540d\u5b57\uff0c\u672c\u90fd\u8be5\u5927\u5403\u4e00\u60ca\u3002 \u3000\u3000\u6614\u65e5\u767e\u6653\u751f\u4f5c\u5175\u5668\u8c31\uff0c\u4e1c\u6d77\u7389\u7bab\u540d\u5217\u7b2c\u5341\uff0c\u8fd9\u7389\u7bab\u9053\u5165\uff0c\u4e5f\u6b63\u662f\u5f53\u5e74\u6b66\u6797\u5341\u5927\u9ad8\u624b\u4e2d\uff0c \u9664\u4e86\u5c0f\u674e\u63a2\u82b1\u5916\u7855\u679c\u4ec5\u5b58\u7684\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u636e\u8bf4\u4ed6\u6e38\u8e2a\u5e38\u5728\u6d77\u5916\uff0c\u53f6\u5f00\u5b9e\u5728\u60f3\u4e0d\u5230\u4ed6\u5c45\u7136\u4e5f\u5230\u4e86\u8fd9\u91cc\u3002 \u3000\u3000\u7389\u7bab\u9053\u4eba\u6c89\u58f0\u9053\uff1a\u201c\u8d2b\u9053\u662f\u4e3a\u4e86\u4ec0\u4e48\u800c\u6765\u7684\uff0c\u4f60\u60f3\u5fc5\u4e5f\u8be5\u77e5\u9053\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u7389\u7bab\u9053\u4eba\u9053\uff1a\u201c\u770b\u6765\u4f60\u5e76\u4e0d\u50cf\u5982\u6b64\u611a\u8822\u7684\u4eba\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u53ef\u662f\u6211\u4f1a\u88c5\u50bb\u3002\u201d \u3000\u3000\u90a3\u4e9b\u5e74\u8f7b\u5973\u9053\u4eba\u4eec\uff0c\u672c\u5df2\u5728\u5077\u5077\u5730\u770b\u7740\u4ed6\uff0c\u73b0\u5728\u53c8\u5fcd\u4e0d\u4f4f\u5077\u5077\u5730\u7b11\u4e86\u3002 \u3000\u3000\u738b\u7bab\u9053\u4eba\u8138\u8272\u53c8\u53d8\u4e86\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u672c\u8be5\u88c5\u6b7b\u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u7389\u7bab\u9053\u4eba\u9053\uff1a\u201c\u8d2b\u9053\u4e0d\u6740\u6b7b\u4eba\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6d3b\u7684\u4f60\u90fd\u6740\u4e86\uff1f\u201d \u3000\u3000\u7389\u7bab\u9053\u4eba\u9053\uff1a\u201c\u53ea\u6740\u60f3\u6b7b\u7684\u4eba\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u5e78\u597d\u6211\u5e76\u4e0d\u60f3\u6b7b\u3002\u201d \u3000\u3000\u7389\u7bab\u9053\u4eba\u9053\uff1a\u201c\u4e00\u4e2a\u4eba\u82e5\u60f3\u597d\u597d\u5730\u6d3b\u7740\uff0c\u5728\u8d2b\u9053\u9762\u524d\u5c31\u8be5\u8bf4\u5b9e\u8bdd\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u8bf4\u7684\u672c\u5c31\u662f\u5b9e\u8bdd\u3002\u201d \u3000\u3000\u7389\u7bab\u9053\u4eba\u9053\uff1a\u201c\u8fd9\u6ce5\u5a03\u5a03\u662f\u8c01\u7684\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u662f\u4e0a\u5b98\u5c0f\u4ed9\u7684\u3002\u201d \u3000\u3000\u7389\u7bab\u9053\u4eba\uff1a\u201c\u5979\u672c\u5728\u8fd9\u5c4b\u5b50\u91cc\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u5979\u662f\u6211\u7b2c\u4e00\u4e2a\u5ba2\u4eba\u3002\u201d \u3000\u3000\u7389\u8427\u9053\u4eba\u9053\uff1a\u201c\u73b0\u5728\u5979\u4eba\u5462\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u7389\u7bab\u9053\u4eba\u51b7\u51b7\u9053\uff1a\u201c\u5979\u521a\u624d\u8fd8\u5728\u8fd9\u91cc\uff0c\u73b0\u5728\u4f60\u5c31\u4e0d\u77e5\u9053\u5979\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u73b0\u5728\u4f60\u8fd8\u5728\u8fd9\u91cc\uff0c\u7b49\u4e00\u4f1a\u4f60\u8981\u5230\u54ea\u91cc\u53bb\uff0c\u6211\u4e5f\u4e0d\u4f1a\u77e5\u9053\u3002\u201d \u3000\u3000\u7389\u7bab\u9053\u4eba\u5ffd\u7136\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u751f\u547d\u5982\u6b64\u53ef\u8d35\uff0c\u4e3a\u4ec0\u4e48\u504f\u504f\u6709\u4eba\u4e00\u5b9a\u60f3\u6b7b\uff1f\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u62bd\u51fa\u4e86\u8170\u5e26\u4e0a\u90a3\u6839\u6676\u83b9\u5706\u6da6\u7684\u767d\u7389\u8427\u3002 \u3000\u3000\u6614\u5e74\u7684\u5175\u5668\u8c31\u4e0a\u4e1c\u6d77\u7389\u7bab\u540d\u5217\u7b2c\u5341\uff0c\u7389\u7bab\u9053\u4eba\u6b66\u529f\u6e0a\u535a\uff0c\u636e\u8bf4\u8eab\u517c\u5341\u4e09\u5bb6\u4e4b\u957f\uff0c\u638c\u4e2d\u8fd9 \u6839\u7389\u7bab\uff0c\u65e2\u53ef\u6253\u7a74\uff0c\u4e5f\u53ef\u4f5c\u5251\u7532\uff0c\u7bab\u7ba1\u4e2d\u8fd8\u85cf\u7740\u6781\u5389\u5bb3\u7684\u6697\u5668\u3002 \u3000\u3000\u53f6\u5f00\u672c\u4ee5\u4e3a\u4ed6\u5df2\u51c6\u5907\u51fa\u624b\u4e86\u3002 \u3000\u3000\u8c01\u77e5\u7389\u7bab\u9053\u4eba\u8fd8\u662f\u5750\u7740\u6ca1\u52a8\uff0c\u53cd\u800c\u8f7b\u629a\u7bab\u7ba1\uff0c\u5439\u594f\u4e86\u8d77\u6765\u3002 \u3000\u3000\u4ed6\u7684\u7bab\u58f0\u5f00\u59cb\u65f6\u5f88\u8f7b\u67d4\uff0c\u5c31\u4eff\u4f5b\u81ea\u4e91\u4e0b\u3001\u9752\u5c71\u4e0a\uff0c\u4e00\u7f15\u6e05\u6cc9\u7f13\u7f13\u6d41\u8fc7\uff0c\u4ee4\u4eba\u5fc3\u91cc\u5145\u6ee1\u4e86 \u5b81\u9759\u548c\u6b22\u4e50\u3002 \u3000\u3000\u7136\u540e\u4ed6\u7bab\u58f0\u6e10\u6e10\u4f4e\u6c89\uff0c\u5c06\u4eba\u5f15\u4eba\u4e86\u53e6\u4e00\u4e2a\u66f4\u7f8e\u4e3d\u7684\u68a6\u5883\u4e2d\u3002 \u3000\u3000\u5728\u8fd9\u4e2a\u68a6\u5883\u91cc\u65e2\u6ca1\u6709\u5fe7\u8651\u548c\u75db\u82e6\uff0c\u66f4\u6ca1\u6709\u6124\u6012\u4e0e\u4e89\u6740\u3002\u65e0\u8bba\u51c6\u542c\u5230\u7bab\u58f0\uff0c\u90fd\u7edd\u4e0d\u4f1a\u518d\u60f3 \u5230\u90a3\u79cd\u5351\u9119\u9669\u6076\u7684\u4e8b\u3002 \u3000\u3000\u4f46\u5c31\u5728\u8fd9\u65f6\uff0c\u7389\u7bab\u9053\u4eba\u81ea\u5df1\u5374\u505a\u4e86\u4ef6\u5f88\u5351\u9119\u9669\u6076\u7684\u4e8b\u3002 \u3000\u3000\u4ed6\u7684\u7bab\u7ba1\u4e2d\u7adf\u7a81\u7136\u98de\u51fa\u4e86\u4e09\u70b9\u5bd2\u661f\uff0c\u6025\u6253\u53f6\u5f00\u7684\u524d\u80f8\u3002 \u3000\u3000\u662f\u4e27\u95e8\u9489\u4e00\u7c7b\u7684\u6697\u5668\uff0c\u6765\u52bf\u6025\u5982\u95ea\u7535\u3002 \u3000\u3000\u5728\u8fd9\u79cd\u4f18\u7f8e\u548c\u5e73\u7684\u4e50\u58f0\u4e2d\uff0c\u53c8\u6709\u8c01\u4f1a\u63d0\u9632\u522b\u4eba\u6076\u6bd2\u7684\u6697\u7b97\uff1f \u3000\u3000\u53ef\u662f\u53f6\u5f00\u5374\u597d\u50cf\u65e9\u5c31\u5728\u9632\u5907\u7740\u3002 \u3000\u3000\u65e0\u8bba\u591a\u6076\u6bd2\u7684\u6697\u5668\uff0c\u5230\u4e86\u4ed6\u9762\u524d\uff0c\u5c31\u597d\u50cf\u5df2\u53d8\u5f97\u8fde\u4e00\u70b9\u7528\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u6709\u4e00\u79cd\u5947\u7279\u7684\u65b9\u6cd5\u6765\u63a5\u6697\u5668\uff0c\u4ed6\u624b\u4e0a\u7ade\u4f3c\u6709\u79cd\u5947\u5f02\u7684\u5438\u5f15\u529b\u3002 \u3000\u3000\u4ed6\u7684\u624b\u4e00\u62db\uff0c\u4e09\u70b9\u5bd2\u661f\u5c31\u65e0\u5f71\u65e0\u8e2a\u3002 \u3000\u3000\u96be\u9053\u8fd9\u5c31\u662f\u6b66\u6797\u4e2d\u65e9\u5df2\u7edd\u4f20\u7684\u5185\u529f\u201c\u4e07\u6d41\u5f52\u5b97\u201d\uff1f \u3000\u3000\u7389\u8427\u9053\u4eba\u8138\u8272\u5df2\u6709\u4e9b\u53d8\u4e86\u3002 \u3000\u3000\u53f6\u5f00\u5374\u5fae\u7b11\u7740\u9053\uff1a\u201c\u518d\u5439\u4e0b\u53bb\uff0c\u83ab\u8981\u505c\uff0c\u6211\u559c\u6b22\u542c\u5439\u7bab\u3002\u201d \u3000\u3000\u7389\u8427\u9053\u4eba\u679c\u7136\u6ca1\u6709\u505c\uff0c\u53ef\u662f\u7bab\u58f0\u5374\u53d8\u4e86\uff0c\u53d8\u5f97\u5145\u6ee1\u4e86\u4e00\u79cd\u539f\u59cb\uff0c\u7684\u6311\u9017\u529b\uff0c\u5c31\u50cf\u662f\u6709\u4e2a \u601d\u6625\u7684\u5c11\u5973\u5728\u6625\u95fa\u91cc\u8f97\u8f6c\u53cd\u4fa7\uff0c\u4e0d\u65ad\u547b\u541f\u3002 \u3000\u3000\u7537\u4eba\u5fc3\u91cc\u6700\u539f\u59cb\u7684\u4e00\u79cd\u6b32\u671b\u662f\u4ec0\u4e48\uff1f\u4e24\u4e2a\u8ddd\u79bb\u53f6\u5f00\u6700\u8fd1\u7684\u5973\u9053\u4eba\uff0c\u6b63\u5728\u770b\u7740\u4ed6\u5a9a\u7b11\uff0c\u7b11 \u5bb9\u4e2d\u4e5f\u5145\u6ee1\u4e86\u6311\u9017\u529b\u3002 \u3000\u3000\u53f6\u5f00\u4e0d\u80fd\u4e0d\u53bb\u770b\u5979\u4eec\uff0c\u4ed6\u53d1\u73b0\u81ea\u5df1\u7adf\u597d\u50cf\u5ffd\u7136\u53d8\u6210\u4e86\u4e2a\u7b2c\u4e00\u6b21\u770b\u89c1\u8d64\u88f8\u5973\u4eba\u7684\u5c11\u5e74\u3002 \u3000\u3000\u5728\u4ed6\u60f3\u8c61\u4e2d\uff0c\u5979\u4eec\u7adf\u4f3c\u5df2\u53d8\u6210\u5b8c\u5168\u8d64\u88f8\u7684\u4e00\u4e00\u96ea\u767d\u7684\u80f8\u819b\uff0c\u7ea4\u7ec6\u7684\u8170\uff0c\u4fee\u957f\u7684\u817f\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u53d1\u73b0\u81ea\u5df1\u7684\u8eab\u4f53\u7adf\u5df2\u4e0d\u7531\u81ea\u4e3b\u5728\u5f00\u59cb\u53d8\u5316\uff0c\u8fd9\u79cd\u6b32\u671b\u672c\u5c31\u662f\u4efb\u4f55\u7537\u4eba\u90e8\u65e0\u6cd5\u63a7\u5236 \u7684\u3002 \u3000\u3000\u5979\u4eec\u7b11\u5f97\u66f4\u5a9a\uff0c\u5a9a\u773c\u5982\u4e1d\u3002 \u3000\u3000\u5979\u4f3c\u7684\u8170\u80a2\u626d\u52a8\uff0c\u4eff\u4f5b\u6b63\u5728\u9080\u8bf7\u3002 \u3000\u3000\u53c8\u6709\u8c01\u8fd8\u80fd\u79bb\u5f00\u5979\u4eec\u6b63\u5728\u626d\u66f2\u70ab\u8000\u7740\u7684\u5730\u65b9\uff1f \u3000\u3000\u53c8\u6709\u8c01\u8fd8\u80fd\u6ce8\u610f\u5230\u522b\u7684\u4e8b\uff1f \u3000\u3000\u53e6\u4e24\u4e2a\u5973\u9053\u4eba\uff0c\u7adf\u5df2\u67b6\u8d77\u4e86\u4e01\u7075\u7433\uff0c\u5728\u5411\u5916\u9000\u3002 \u3000\u3000\u6b64\u65f6\u6b64\u523b\uff0c\u82e5\u662f\u522b\u7684\u7537\u4eba\uff0c\u4e00\u5b9a\u4e0d\u4f1a\u6ce8\u610f\u5230\u5979\u4eec\u7684\u3002 \u3000\u3000\u4f46\u53f6\u5f00\u4e0d\u662f\u522b\u7684\u7537\u4eba\u3002 \u3000\u3000\u53f6\u5f00\u5c31\u662f\u53f6\u5f00\uff01 \u3000\u3000\u4ed6\u7684\u773c\u775b\u4eff\u4f5b\u8fd8\u5728\u76ef\u7740\u90a3\u626d\u52a8\u7684\u8170\u80a2\uff0c\u4ed6\u4eba\u5374\u5df2\u63a0\u8d77\u3002 \u3000\u3000\u5ffd\u7136\u95f4\uff0c\u7bab\u58f0\u505c\u987f\u3002 \u3000\u3000\u4e00\u6839\u6676\u83b9\u5706\u6da6\u7684\u7389\u8427\uff0c\u5df2\u659c\u659c\u70b9\u4e86\u8fc7\u6765\uff0c\u6025\u6253\u4ed6\u8170\u4e0a\u7684\u9ebb\u8170\u7a74\u3002 \u3000\u3000\u8fd9\u662f\u5224\u5b98\u7684\u62db\u5f0f\uff0c\u8ba4\u7a74\u3001\u6253\u7a74\u5feb\u3002 \u3000\u3000\u8fd9\u65f6\u5224\u5b98\u7b14\u5df2\u53d8\u6210\u4e86\u5251\uff0c\u5251\u8d70\u8f7b\u7075\uff0c\u5df2\u5c06\u53f6\u5f00\u7684\u8eab\u5f62\u56f4\u4f4f\u3002 \u3000\u3000\u53f6\u5f00\u773c\u770b\u7740\u4e01\u7075\u7433\u88ab\u4eba\u5e26\u8d70\uff0c\u7ade\u662f\u4ed6\u5e73\u751f\u672a\u9047\u7684\u9ad8\u624b\u3002 \u3000\u3000\u4ed6\u82e5\u662f\u518d\u53bb\u4e3a\u4e01\u7075\u7433\u5fe7\u8651\u62c5\u5fc3\uff0c\u4ed6\u81ea\u5df1\u5c31\u968f\u65f6\u90fd\u53ef\u80fd\u88ab\u51fb\u5012\u3002 \u3000\u3000\u4ed6\u7684\u8eab\u5f62\u7a81\u7136\u505c\u987f\uff0c\u5b8c\u5168\u505c\u987f\uff0c\u7adf\u50cf\u662f\u4e00\u53ea\u65cb\u8f6c\u4e0d\u606f\u7684\u9640\u87ba\uff0c\u7a81\u7136\u88ab\u9489\u6b7b\u5728\u5730\u4e0a\u3002 \u3000\u3000\u9ad8\u624b\u51b3\u6218\u4e2d\uff0c\u7edd\u6ca1\u6709\u4efb\u4f55\u4eba\u4f1a\u505a\u8fd9\u79cd\u4e8b\u7684\u3002 \u3000\u3000\u7389\u8427\u9053\u4eba\u8eab\u7ecf\u767e\u6218\uff0c\u5404\u5f0f\u5404\u6837\u7684\u5bf9\u624b\u90fd\u9047\u89c1\u8fc7\uff0c\u5374\u4e5f\u4ece\u672a\u89c1\u8fc7\u8fd9\u79cd\u4e8b\u3002 \u3000\u3000\u4ed6\u7684\u7389\u7bab\u4e00\u7740\u51fb\u51fa\uff0c\u4e5f\u7a81\u7136\u505c\u987f\u3002 \u3000\u3000\u4ed6\u731c\u4e0d\u900f\u53f6\u5f00\u7684\u7528\u610f\u3002 \u3000\u3000\u4f46\u4ed6\u5374\u5df2\u770b\u51fa\u53f6\u5f00\u662f\u4e2a\u7edd\u9876\u806a\u660e\u7684\u4eba\uff0c\u806a\u660e\u7684\u4eba\u7edd\u4e0d\u4f1a\u7a81\u7136\u505a\u51fa\u592a\u611a\u8822\u7684\u4e8b\uff0c\u8fd9\u5176\u4e2d\u96be \u9053\u53c8\u6709\u9634\u8c0b\uff1f \u3000\u3000\u7389\u7bab\u9053\u4eba\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6ca1\u6709\u610f\u601d\u3002\u201d \u3000\u3000\u7389\u7bab\u9053\u4eba\u9053\uff1a\u201c\u6ca1\u6709\u610f\u601d\u662f\u4ec0\u4e48\u610f\u601d\uff1f\uff0c\u53f6\u5f00\u9053\uff1a\u201c\u6ca1\u6709\u610f\u601d\u5c31\u662f\u6ca1\u6709\u610f\u601d\u3002\u201d \u3000\u3000\u7389\u7bab\u9053\u4eba\u9053\uff1a\u201c\u4f60\u60f3\u6b7b\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4e0d\u60f3\u3002\u201d \u3000\u3000\u7389\u7bab\u9053\u4eba\u9053\uff1a\u201c\u4f60\u83ab\u975e\u4e0d\u77e5\u521a\u624d\u90a3\u4e00\u77ac\u95f4\uff0c\u6211\u5df2\u53ef\u8ba9\u4f60\u6b7b\u5341\u6b21\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u77e5\u9053\u3002\u201d \u3000\u3000\u4ed6\u7b11\u4e86\u7b11\uff0c\u6de1\u6de1\u9053\uff1a\u201c\u53ef\u662f\u6211\u4e5f\u77e5\u9053\uff0c\u6211\u4e00\u505c\u4e0b\uff0c\u4f60\u4e5f\u4f1a\u505c\u4e0b\u6765\u7684\u3002\u201d \u3000\u3000\u7389\u7bab\u9053\u4eba\uff1a\u201c\u6211\u82e5\u4e0d\u505c\u5462\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u90a3\u4e48\u6211\u73b0\u5728\u5c31\u5df2\u6b7b\u4e86\u5341\u6b21\u3002\u201d \u3000\u3000\u7389\u7bab\u9053\u4eba\u7684\u8138\u8272\u7a81\u7136\u82cd\u767d\uff0c\u4ed6\u663e\u7136\u5df2\u5728\u540e\u6094\uff0c\u53ea\u53ef\u60dc\u73b0\u5728\u540e\u6094\u5df2\u8fdf\uff0c\u8fd9\u79cd\u673a\u4f1a\u4e00\u9519\u8fc7\uff0c \u662f\u6c38\u8fdc\u4e0d\u4f1a\u518d\u6765\u7684\u3002 \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u505c\u4e0b\u6765\uff0c\u4e5f\u56e0\u4e3a\u6211\u73b0\u5728\u6ca1\u6709\u628a\u63e1\u80fd\u80dc\u4f60\u3002\u201d\u7389\u7bab\u9053\u4eba\u51b7\u7b11\u3002 \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u56e0\u4e3a\u73b0\u5728\u6211\u7684\u5fc3\u5df2\u4e71\uff0c\u4f60\u8eab\u65c1\u53c8\u6709\u8fd9\u4e48\u591a\u6f02\u4eae\u7684\u5e2e\u624b\uff0c\u65e0\u8bba\u8c01\u770b\u89c1\u81ea\u5df1\u5fc3\u7231 \u7684\u5973\u4eba\u88ab\u4eba\u67b6\u8d70\uff0c\u5fc3\u90fd\u4f1a\u4e71\u7684\u3002\u201d \u3000\u3000\u738b\u7bab\u9053\u4eba\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u5012\u5f88\u4f46\u767d\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u4e0d\u60f3\u9a97\u4f60\uff0c\u4e5f\u9a97\u4e0d\u8fc7\u4f60\uff0c\u4f60\u5f53\u7136\u4e5f\u77e5\u9053\u6211\u7684\u5fc3\u5df2\u4e71\u4e86\u3002\u201d \u3000\u3000\u7389\u7bab\u9053\u4eba\u9053\uff1a\u201c\u5fc3\u4e71\u4e86\u5c31\u5f97\u6b7b\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u771f\u7684\u6709\u628a\u63e1\u6740\u6211\uff1f\u201d \u3000\u3000\u7389\u7bab\u9053\u4eba\u6ca1\u6709\u5f00\u53e3\uff0c\u4ed6\u6ca1\u6709\u628a\u63e1\uff0c\u56e0\u4e3a\u8fd9\u5c11\u5e74\u6b66\u529f\u4e4b\u7cbe\u5947\u8d85\u8131\uff0c\u5e94\u53d8\u4e4b\u673a\u8b66\u5947\u8be1\uff0c\u7adf\u662f \u4ed6\u751f\u5e73\u6240\u9047\u7684\u5bf9\u624b\u4e2d\uff0c\u6700\u4ee4\u4eba\u96be\u6d4b\u7684\u4e00\u4e2a\u3002 \u3000\u3000\u4f55\u51b5\u4ed6\u8fd8\u6709\u5200\uff0c\u98de\u5200\uff01 \u3000\u3000\u53f6\u5f00\u7684\u98de\u5200\u8fd8\u6ca1\u6709\u51fa\u624b\uff0c\u7389\u7bab\u9053\u4eba\u5f53\u7136\u5e76\u4e0d\u60f3\u903c\u7740\u4ed6\u51fa\u624b\u3002 \u3000\u3000\u53f6\u5f00\u6de1\u6de1\u9053\uff1a\u201c\u4f60\u6211\u8fdf\u65e9\u603b\u96be\u514d\u8981\u4e00\u6218\u7684\uff0c\u4f46\u5374\u4e0d\u5728\u4eca\u591c\u3002\u201d \u3000\u3000\u738b\u7bab\u9053\u4eba\u9053\uff1a\u201c\u5728\u4ec0\u4e48\u65f6\u5019\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u5728\u6211\u5fc3\u4e0d\u4e71\u7684\u65f6\u5019\uff0c\u5728\u6211\u6709\u628a\u63e1\u80dc\u4f60\u7684\u65f6\u5019\u3002\u201d \u3000\u3000\u7389\u7bab\u9053\u4eba\u51b7\u7b11\u9053\uff1a\u201c\u5c31\u7b97\u771f\u6709\u90a3\u4e48\u4e00\u5929\uff0c\u6211\u4e3a\u4ec0\u4e48\u8981\u7b49\u5230\u90a3\u5929\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u56e0\u4e3a\u4f60\u975e\u7b49\u4e0d\u53ef\u3002\u201d \u3000\u3000\u7389\u7bab\u9053\u4eba\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u73b0\u5728\u4f60\u5c31\u7b97\u80fd\u6740\u6211\uff0c\u4e5f\u4e0d\u4f1a\u51fa\u624b\u7684\uff0c\u56e0\u4e3a\u4f60\u771f\u6b63\u60f3\u8981\u7684\u662f\u4e0a\u5b98\u5c0f\u4ed9\u3002\u201d \u3000\u3000\u7389\u7bab\u9053\u4eba\u4e0d\u80fd\u5426\u8ba4\u3002 \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u73b0\u5728\u4f60\u5c31\u7b97\u6740\u4e86\u6211\uff0c\u4e5f\u5f97\u4e0d\u5230\u5c0f\u5b98\u5c0f\u4ed9\u3002\u6240\u4ee5\u4f60\u7ed1\u8d70\u4e86\u4e01\u7075\u7433\uff0c\u60f3\u8981\u6211\u7528\u4e0a \u5b98\u5c0f\u4ed9\u6765\u6362\u5979\u7684\u751f\u547d\u3002\u201d \u3000\u3000\u7389\u7bab\u9053\u4eba\u7a81\u7136\u957f\u957f\u53f9\u606f\u9053\uff1a\u201c\u4f60\u679c\u7136\u4e0d\u7b28\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u8bf4\u8c0e\u3002\u201d \u3000\u3000\u7389\u7bab\u9053\u4eba\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u73b0\u5728\u6211\u771f\u7684\u4e0d\u77e5\u9053\u4e0a\u5b98\u5c0f\u4ed9\u5728\u54ea\u91cc\u3002\u201d \u3000\u3000\u7389\u7bab\u9053\u4eba\u51b7\u51b7\u9053\uff1a\u201c\u90a3\u4e48\u6211\u4e5f\u4e0d\u77e5\u9053\u4e01\u7075\u7433\u5728\u54ea\u91cc\u3002\u201d \u3000\u3000\u53f6\u5f00\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u53ef\u4ee5\u60f3\u6cd5\u5b50\u53bb\u627e\u3002\u201d \u3000\u3000\u7389\u7bab\u9053\u4eba\u9053\uff1a\u201c\u5341\u4e8c\u4e2a\u65f6\u8fb0\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u53ef\u4ee5\u3002\u201d \u3000\u3000\u7389\u7bab\u9053\u4eba\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u660e\u5929\u6b64\u523b\uff0c\u4f60\u82e5\u8fd8\u4e0d\u628a\u4e0a\u5b98\u5c0f\u4ed9\u4ea4\u7ed9\u6211\u3001\u4f60\u4eca\u751f\u5c31\u518d\u4e5f\u4f11\u60f3\u89c1 \u5230\u4e01\u7075\u7433\u3002\u201d \u3000\u3000\u4ed6\u6162\u6162\u5730\u63a5\u7740\u9053\uff1a\u201c\u91d1\u73af\u65e0\u60c5\uff0c\u98de\u5200\u6709\u60c5\uff0c\u94c1\u5251\u597d\u540d\uff0c\u7389\u7bab\u597d\u8272\uff0c\u8fd9\u53e5\u8bdd\u4f60\u603b\u542c\u8bf4 \u8fc7\u3002\u201d \u3000\u3000\u53f6\u5f00\u5f53\u7136\u542c\u8bf4\u8fc7\u3002 \u3000\u3000\u7389\u7bab\u9053\u4eba\u9053\uff1a\u201c\u4e01\u7075\u7433\u662f\u4e2a\u597d\u770b\u7684\u5973\u4eba\uff0c\u6211\u662f\u4e2a\u597d\u8272\u7684\u7537\u4eba\uff0c\u6240\u4ee5\u4f60\u6700\u597d\u8d76\u5feb\u627e\u5230\u4e0a\u5bab \u5c0f\u4ed9\uff0c\u5426\u5219\u2026\u2026\u201d \u3000\u3000\u4ed6\u6ca1\u6709\u518d\u8bf4\u4e0b\u53bb\u3002 \u3000\u3000\u4ed6\u7684\u610f\u601d\u65e0\u8bba\u8c01\u90fd\u53ef\u4ee5\u542c\u5f97\u51fa\u6765\u3002\u7389\u7bab\u9053\u4eba\u5df2\u8d70\u4e86\uff0c\u5e26\u7740\u4ed6\u5e74\u8f7b\u800c\u7f8e\u4e3d\u7684\u5973\u5f1f\u5b50\u4eec\u4e00\u8d77 \u8d70\u4e86\u3002 \u3000\u3000\u201c\u660e\u65e5\u6b64\u523b\u6211\u518d\u6765\u3002\u201d \u3000\u3000\u5341\u4e8c\u4e2a\u65f6\u8fb0\u3002 \u3000\u3000\u8c01\u80fd\u6709\u628a\u63e1\u5728\u5341\u4e8c\u4e2a\u65f6\u8fb0\u4e2d\u627e\u5230\u4e0a\u5b98\u5c0f\u4ed9\uff1f\u8c01\u80fd\u6709\u628a\u63e1\u5728\u77ed\u77ed\u7684\u4e00\u5929\u4e2d\u627e\u5230\u72d0\u72f8\u822c\u72e1 \u733e\u3001\u876e\u86c7\u822c\u9634\u6bd2\u7684\u5973\u4eba\uff1f \u3000\u3000\u53f6\u5f00\u4e5f\u6ca1\u6709\u628a\u63e1\u3002 \u3000\u3000\u53ef\u662f\uff0c\u94c1\u5251\u597d\u540d\uff0c\u7389\u7bab\u597d\u8272\u3002\u53c8\u6709\u8c01\u80fd\u653e\u5fc3\u8ba9\u81ea\u5df1\u5fc3\u7231\u7684\u5973\u4eba\uff0c\u5446\u5728\u4e00\u4e2a\u597d\u8272\u7684\u7537\u4eba\u8eab \u65c1\uff1f \u3000\u3000\u591c\u8272\u964d\u4e34\uff0c\u53f6\u5f00\u9759\u9759\u5730\u5750\u5728\u9ed1\u6697\u91cc\uff0c\u4ed6\u6ca1\u6709\u71c3\u706f\uff0c\u4ed6\u8fde\u52a8\u90fd\u61d2\u5f97\u52a8\u3002 \u3000\u3000\u5c4b\u5b50\u91cc\u4eff\u4f5b\u8fd8\u7559\u7740\u4e01\u7075\u7433\u8eab\u4e0a\u7684\u9999\u6c14\uff0c\u9ed1\u6697\u4e2d\u4eff\u4f5b\u53c8\u51fa\u73b0\u4e86\u5979\u90a3\u53cc\u5145\u6ee1\u4e86\u6050\u60e7\u7684\u773c\u775b\u3002 \u3000\u3000\u8981\u600e\u4e48\u6837\u624d\u80fd\u6551\u51fa\u5979\uff1f\u8981\u600e\u4e48\u6837\u624d\u80fd\u627e\u5230\u4e0a\u5b98\u5c0f\u4ed9\uff1f \u3000\u3000\u53f6\u5f00\u7ade\u8fde\u4e00\u70b9\u5934\u7eea\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u8fd9\u91cc\u5f88\u9759\uff0c\u662f\u5f88\u9002\u4e8e\u601d\u7d22\u7684\u5730\u65b9\uff0c\u4ed6\u7684\u53cd\u5e94\u672c\u6781\u5feb\uff0c\u601d\u60f3\u672c\u6781\u7075\u6d3b\u3002 \u3000\u3000\u4f46\u73b0\u5728\u4ed6\u7684\u5934\u8111\u4f3c\u4e4e\u53d8\u6210\u4e86\u5757\u6728\u5934\u3002 \u3000\u3000\u8fd9\u65f6\u5916\u9762\u9759\u6084\u6084\u7684\u9662\u5b50\u91cc\uff0c\u5ffd\u7136\u4f20\u6765\u4e86\u4e00\u9635\u56a3\u95f9\u7684\u4eba\u58f0\uff0c\u597d\u50cf\u4e00\u4e0b\u5b50\u6709\u5f88\u591a\u4eba\u62e5\u4e86\u8fdb \u6765\u3002 \u3000\u3000\u5927\u5bb6\u8bae\u8bba\u7eb7\u7eb7\uff0c\u8c08\u8bba\u7684\u7adf\u662f\u90ed\u5b9a\u3002 \u3000\u3000\u201c\u5d69\u9633\u94c1\u5251\u7684\u5144\u5f1f\uff0c\u679c\u7136\u662f\u540d\u4e0d\u865a\u4f20\u3002\u201d \u3000\u3000\u201c\u5357\u5bab\u5144\u5f1f\u672c\u4e0d\u8be5\u627e\u4ed6\u6bd4\u5251\u7684\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u5357\u5b98\u5144\u5f1f\u4e5f\u662f\u8d6b\u8d6b\u6709\u540d\u7684\u6b66\u6797\u4e16\u5bb6\u5b50\u5f1f\uff0c\u600e\u4e48\u53d7\u5f97\u4e86\u4ed6\u90a3\u79cd\u8f7b\u89c6\u3002\u201d \u3000\u3000\u201c\u5c24\u5176\u662f\u5357\u5b98\u8fdc\uff0c\u4e0d\u4f46\u6709\u4e00\u8eab\u5bb6\u4f20\u7684\u6b66\u529f\uff0c\u800c\u4e14\u8fd8\u662f\u5578\u4e91\u5251\u5ba2\u7684\u5165\u5ba4\u5f1f\u5b50\uff0c\u5251\u6cd5\u4e4b\u9ad8\uff0c \u636e\u8bf4\u5df2\u53ef\u7b97\u662f\u5f53\u4eca\u6c5f\u6e56\u4e2d\u7684\u4e03\u5927\u9ad8\u624b\u4e4b\u4e00\u3002\u201d \u3000\u3000\u201c\u6240\u4ee5\u8fd9\u4e00\u6218\u5927\u5bb6\u672c\u6765\u90fd\u770b\u597d\u5357\u5b98\u8fdc\u7684\uff0c\u90ed\u5b9a\u6bd5\u7adf\u662f\u4e2a\u521d\u51fa\u9053\u7684\u4eba\u3002\u201d \u3000\u3000\u201c\u636e\u6211\u6240\u77e5\uff0c\u5409\u7965\u8336\u9986\u91cc\u5374\u6709\u5f88\u591a\u4eba\u4ee5\u5341\u535a\u4e00\uff0c\u8d4c\u5357\u5bab\u80dc\u3002\u201d \u3000\u3000\u201c\u65e9\u77e5\u5982\u6b64\uff0c\u6211\u4e5f\u8be5\u8d4c\u4e00\u4e0b\u5b50\u7684\u3002\u201d \u3000\u3000\u201c\u90a3\u65f6\u4f60\u6562\u8d4c\u90ed\u5b9a\u80dc\uff1f\u201d \u3000\u3000\u201c\u6709\u8c01\u60f3\u5f97\u5230\uff0c\u50cf\u5357\u5bab\u8fdc\u8fd9\u4e48\u6709\u540d\u7684\u5251\u5ba2\uff0c\u7adf\u8fde\u90ed\u5b9a\u5341\u62db\u90fd\u63a5\u4e0d\u4f4f\u3002\u201d \u3000\u3000\u201c\u5d69\u9633\u94c1\u5251\uff0c\u679c\u7136\u771f\u9738\u9053\uff0c\u5c24\u5176\u662f\u4ed6\u90a3\u6700\u540e\u4e00\u62db\u2018\u5929\u5730\u4ff1\u711a\u2019\uff0c\u6211\u6562\u6253\u8d4c\uff0c\u6c5f\u6e56\u4e2d\u80fd\u63a5 \u5f97\u4e0b\u4ed6\u8fd9\u4e00\u62db\u7684\u4eba\uff0c\u7edd\u4e0d\u4f1a\u8d85\u8fc7\u4e94\u4e2a\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e00\u4e0b\u5d69\u9633\u94c1\u5251\u90ed\u5b9a\u53ef\u771f\u662f\u51fa\u8db3\u4e86\u98ce\u5934\uff0c\u8fde\u90a3\u51e0\u4e2a\u5e73\u65e5\u773c\u9ad8\u4e8e\u9876\u7684\u9556\u5c40\u8001\u603b\uff0c\u90fd\u62a2\u7740 \u8981\u4f5c\u4e1c\uff0c\u8bf7\u4ed6\u53bb\u559d\u9152\u3002\u201d \u3000\u3000\u201c\u73b0\u5728\u4ed6\u5df2\u7ecf\u662f\u57ce\u91cc\u6700\u51fa\u98ce\u5934\u7684\u4eba\uff0c\u83ab\u8bf4\u9556\u5c40\u91cc\u7684\u4eba\u8981\u8bf7\u4ed6\u559d\u9152\uff0c\u8fde\u6211\u90fd\u60f3\u8bf7\u8bf7\u4ed6\uff0c\u80fd \u8ddf\u8fd9\u79cd\u4eba\u559d\u676f\u9152\uff0c\u6211\u9762\u5b50\u4e5f\u6709\u5149\u91c7\u3002\u201d \u3000\u3000\u201c\u73b0\u5728\u4ed6\u82e5\u60f3\u53bb\u627e\u5973\u4eba\uff0c\u6211\u6562\u4fdd\u8bc1\uff0c\u4e00\u5b9a\u6709\u5f88\u591a\u5973\u4eba\u60c5\u613f\u5012\u8d34\u3002\u201d \u3000\u3000\u201c\u4ed6\u867d\u7136\u4e0d\u80fd\u7b97\u662f\u4e2a\u5c0f\u767d\u8138\uff0c\u5012\u771f\u6709\u70b9\u9ed1\u91cc\u4fcf\u3002\u201d \u3000\u3000\u201c\u542c\u8bf4\u76ae\u80a4\u9ed1\u7684\u4eba\uff0c\u5bf9\u5973\u4eba\u90fd\u6709\u4e00\u624b\u3002\u201d \u3000\u3000\u201c\u76ae\u80a4\u9ed1\u7684\u5973\u4eba\uff0c\u90a3\u5730\u65b9\u4e5f\u2026\u2026\u201d \u3000\u3000\u4e0b\u9762\u8bf4\u7684\u8bdd\u7adf\u8d8a\u6765\u8d8a\u4e0d\u50cf\u8bdd\u4e86\u3002 \u3000\u3000\u53f6\u5f00\u6ca1\u6709\u518d\u542c\u4e0b\u53bb\u3002 \u3000\u3000\u521a\u624d\u5916\u9762\u90a3\u4e48\u9759\uff0c\u539f\u6765\u662f\u56e0\u4e3a\u4eba\u4eec\u90fd\u8d76\u7740\u53bb\u770b\u90ed\u5b9a\u548c\u5357\u5bab\u8fdc\u7684\u51b3\u6218\u4e86\u3002\u82e5\u662f\u5728\u5e73\u65f6\uff0c\u53f6 \u5f00\u4e5f\u4f1a\u53bb\u770b\u770b\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u5357\u5bab\u8fdc\u8fd9\u4e2a\u4eba\uff0c\u4e5f\u786e\u5b9e\u77e5\u9053\u8fd9\u4e2a\u4eba\u5251\u6cd5\u5f97\u8fc7\u771f\u4f20\u3002 \u3000\u3000\u8fd1\u5e74\u6765\uff0c\u4ed6\u4e00\u76f4\u90fd\u662f\u5728\u6c5f\u6e56\u4e2d\u5f88\u9732\u950b\u8292\u7684\u4eba\uff0c\u4f46\u73b0\u5728\u4ed6\u7684\u5149\u8292\u663e\u7136\u5df2\u88ab\u90ed\u5b9a\u62a2\u5c3d\u3002 \u3000\u3000\u90ed\u5b9a\u73b0\u5728\u60f3\u5fc5\u4e00\u5b9a\u5f88\u6109\u5feb\u3002 \u3000\u3000\u5c11\u5e74\u6210\u540d\uff0c\u672c\u5c31\u662f\u4eba\u751f\u4e2d\u6700\u4ee4\u4eba\u6109\u5feb\u7684\u51e0\u4ef6\u4e8b\u4e4b\u4e00\u3002 \u3000\u3000\u53f6\u5f00\u4e86\u89e3\u8fd9\u79cd\u611f\u89c9\uff0c\u53ef\u662f\u4ed6\u5e76\u4e0d\u7fa1\u6155\u3002 \u3000\u3000\u4ed6\u53ea\u60f3\u627e\u4e2a\u5b89\u9759\u7684\u5730\u65b9\uff0c\u9759\u9759\u5730\u559d\u4e24\u676f\u9152\uff0c\u9152\u867d\u7136\u4f1a\u9ebb\u75f9\u4eba\u7684\u5934\u8111\uff0c\u4e00\u65f6\u4e5f\u53ef\u4ee5\u4ee4\u4eba\u7684 \u5934\u8111\u6e05\u9192\u3002 \u3000\u3000\u4ed6\u7ad9\u4e86\u8d77\u6765\uff0c\u6162\u6162\u5730\u8d70\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u6ca1\u6709\u4eba\u6ce8\u610f\u4ed6\uff0c\u751a\u81f3\u6ca1\u6709\u4eba\u770b\u4ed6\u4e00\u773c\uff0c\u53ea\u6709\u8d62\u5bb6\u624d\u662f\u4eba\u4eec\u6ce8\u610f\u7684\u5bf9\u8c61\u3002 \u3000\u3000\u4ed6\u73b0\u5728\u5374\u662f\u4e2a\u8f93\u5bb6\u3002 \u3000\u3000\u7a84\u5df7\u7684\u5c3d\u5934\uff0c\u6709\u5bb6\u5c0f\u5c0f\u7684\u9152\u94fa\uff0c\u8fde\u62db\u724c\u90fd\u5df2\u88ab\u6cb9\u70df\u718f\u9ed1\u3002 \u3000\u3000\u5c4b\u5b50\u91cc\u706f\u5149\u660f\u6697\uff0c\u4e00\u4e2a\u6ca1\u7cbe\u6253\u91c7\u7684\u4f19\u8ba1\uff0c\u6b63\u5750\u5728\u5c0f\u70ad\u7089\u65c1\u70e4\u706b\u3002 \u3000\u3000\u5ba2\u4eba\u4e5f\u53ea\u6709\u4e00\u4e2a\uff0c\u80cc\u5bf9\u7740\u95e8\uff0c\u5750\u5728\u6700\u9634\u6697\u7684\u4e00\u4e2a\u89d2\u843d\u91cc\uff0c\u72ec\u81ea\u559d\u7740\u95f7\u9152\u3002 \u3000\u3000\u4ed6\u60f3\u5fc5\u4e5f\u8ddf\u53f6\u5f00\u4e00\u6837\uff0c\u662f\u4e2a\u8f93\u5bb6\uff0c\u662f\u4e2a\u5931\u610f\u7684\u4eba\u3002 \u3000\u3000\u82e5\u662f\u5728\u5e73\u65f6\uff0c\u53f6\u5f00\u8bf4\u4e0d\u5b9a\u4f1a\u8fc7\u53bb\uff0c\u627e\u4ed6\u559d\u4e24\u676f\u2014\u2014\u540c\u662f\u5929\u6daf\u6ca6\u843d\u4eba\uff0c\u76f8\u9022\u4f55\u5fc5\u66fe\u76f8\u8bc6\uff1f \u4f46\u73b0\u5728\u4ed6\u5374\u5b81\u613f\u5b64\u72ec\u3002 \u3000\u3000\u4f19\u8ba1\u6ca1\u7cbe\u6253\u91c7\u5730\u8d70\u8fc7\u6765\uff0c\u66ff\u4ed6\u6446\u4e86\u53cc\u7b77\u5b50\uff0c\u4e0a\u9762\u8fd8\u5e26\u7740\u9709\u70b9\u7684\u7af9\u7b77\u5b50\u3002 \u3000\u3000\u53ef\u662f\u53f6\u5f00\u4e0d\u5728\u4e4e\u3002 \u3000\u3000\u201c\u8981\u70b9\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u9152\uff0c\u4e94\u65a4\u9152\uff0c\u968f\u4fbf\u4ec0\u4e48\u9152\u90fd\u884c\u3002\u201d \u3000\u3000\u201c\u4e0d\u5207\u70b9\u5364\u83dc\uff1f\u201d \u3000\u3000\u201c\u6709\u73b0\u6210\u7684\uff0c\u5c31\u7ed9\u6211\u6765\u4e00\u70b9\u3002\u201d \u3000\u3000\u8fd9\u5ba2\u4eba\u770b\u6765\u5e76\u4e0d\u6311\u5254\uff0c\u4f19\u8ba1\u5634\u89d2\u7ec8\u4e8e\u9732\u51fa\u4e86\u4e00\u4e1d\u7b11\u5bb9\uff1a\u201c\u90a3\u4f4d\u5ba2\u4eba\u5207\u4e86\u4e2a\u5c0f\u62fc\u76d8\uff0c\u6211\u5c31 \u7ed9\u4f60\u7167\u6837\u672a\u4e00\u789f\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u201c\u884c\u3002\u201d \u3000\u3000\u90a3\u4f4d\u5ba2\u4eba\u663e\u7136\u4e5f\u4e0d\u6311\u5254\u3002 \u3000\u3000\u4e00\u4e2a\u5931\u610f\u7684\u4eba\uff0c\u53c8\u8fd8\u80fd\u6311\u5254\u4ec0\u4e48\u5462\uff1f \u3000\u3000\u9152\u8fd8\u6ca1\u6709\u6765\uff0c\u53f6\u5f00\u5c31\u9759\u9759\u5730\u7b49\u7740\uff0c\u4ed6\u672c\u4e0d\u671f\u671b\u8fd9\u79cd\u5730\u65b9\u4f1a\u6709\u4ec0\u4e48\u6bb7\u52e4\u7684\u62db\u5f85\u3002 \u3000\u3000\u90a3\u8fb9\u7684\u5ba2\u4eba\u4e00\u76f4\u6ca1\u6709\u56de\u8fc7\u5934\u6765\u770b\u770b\u4ed6\uff0c\u6b64\u523b\u5374\u7a81\u7136\u9053\uff1a\u201c\u6211\u8fd9\u91cc\u6709\u9152\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u8fc7\u6765\u5148 \u559d\u4e00\u676f\uff1f\u201d \u3000\u3000\u8fd9\u58f0\u97f3\u5f88\u719f\uff0c\u8fd9\u4eba\u662f\u8c01\uff1f \u3000\u3000\u53f6\u5f00\u56de\u8fc7\u5934\uff0c\u8fd9\u4eba\u6de1\u6de1\u5730\u53c8\u9053\uff1a\u201c\u5176\u5b9e\u4f60\u5e94\u8be5\u8fc7\u6765\u656c\u6211\u4e00\u676f\u7684\uff0c\u4f60\u6b20\u6211\u7684\u60c5\u3002\u201d \u3000\u3000\u201c\u662f\u4f60\u3002\u201d \u3000\u3000\u53f6\u5f00\u7ec8\u4e8e\u542c\u51fa\u4e86\u4ed6\u7684\u58f0\u97f3\u3002 \u3000\u3000\u8fd9\u4e2a\u5728\u5c0f\u9152\u94fa\u91cc\u72ec\u81ea\u559d\u7740\u95f7\u9152\u7684\u5931\u610f\u8005\uff0c\u7ade\u662f\u73b0\u5728\u8fd9\u57ce\u91cc\u7684\u98ce\u4e91\u4eba\u7269\u90ed\u5b9a\u3002 \u3000\u3000\u90ed\u5b9a\u7ec8\u4e8e\u56de\u8fc7\u5934\uff0c\u6de1\u6de1\u5730\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u60f3\u4e0d\u5230\u662f\u6211\uff1f\u201d \u3000\u3000\u53f6\u5f00\u7684\u786e\u60f3\u4e0d\u5230\u3002 \u3000\u3000\u4ed6\u8d70\u8fc7\u53bb\uff0c\u5750\u4e0b\uff0c\u770b\u7740\u90ed\u5b9a\uff0c\u9053\uff1a\u201c\u4f60\u672c\u4e0d\u8be5\u5728\u8fd9\u91cc\u7684\u3002\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u8fd9\u79cd\u5730\u65b9\uff0c\u672c\u53ea\u6709\u6211\u8fd9\u79cd\u4eba\u624d\u4f1a\u6765\u3002\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u53f6\u5f00\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u4f60\u73b0\u5728\u5df2\u6210\u4e86\u8fd9\u91cc\u6700\u51fa\u98ce\u5934\u7684\u4eba\uff1f\u201d \u3000\u3000\u90ed\u5b9a\u51b7\u51b7\u9053\uff1a\u201c\u5c31\u56e0\u4e3a\u6211\u523a\u4e86\u5357\u5b98\u8fdc\u4e00\u5251\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u80fd\u6218\u80dc\u5357\u5b98\u8fdc\uff0c\u5e76\u4e0d\u662f\u4ef6\u5bb9\u6613\u4e8b\u3002\u201d \u3000\u3000\u90ed\u5b9a\u51b7\u7b11\u3002 \u3000\u3000\u53f6\u5f00\u770b\u7740\u4ed6\uff0c\u9053\uff1a\u201c\u73b0\u5728\u57ce\u91cc\u4e5f\u4e0d\u77e5\u6709\u591a\u5c11\u5927\u4eba\u7269\u5728\u62a2\u7740\u8981\u8bf7\u4f60\u559d\u9152\uff0c\u4f60\u4e3a\u4ec0\u4e48\u53cd\u800c\u4e00 \u4e2a\u4eba\u8dd1\u5230\u8fd9\u79cd\u5730\u65b9\u6765\uff1f\u201d \u3000\u3000\u90ed\u5b9a\u6ca1\u6709\u56de\u7b54\uff0c\u5374\u66ff\u4ed6\u5012\u4e86\u676f\u9152\uff0c\u9053\uff1a\u201c\u4f60\u8bf4\u5f97\u5927\u591a\uff0c\u559d\u5f97\u592a\u5c11\u3002\u201d \u3000\u3000\u53f6\u5f00\u4e3e\u676f\u4e00\u996e\u800c\u5c3d\u3002 \u3000\u3000\u90ed\u5b9a\u4e5f\u5728\u770b\u7740\u4ed6\uff0c\u5ffd\u7136\u9053\uff1a\u201c\u4f60\u4ee5\u524d\u6709\u6ca1\u6709\u6218\u80dc\u8fc7\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u6709\u3002\u201d \u3000\u3000\u90ed\u5b9a\uff1a\u201c\u4f60\u6218\u80dc\u7684\u65f6\u5019\uff0c\u662f\u4e0d\u662f\u4e5f\u6709\u5f88\u591a\u5927\u4eba\u7269\u8981\u62a2\u7740\u8bf7\u4f60\u559d\u9152\uff1f\u201d\u53f6\u5f00\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u4f60\u4e0d\u53bb\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4e0d\u53bb\u3002\u201d \u3000\u3000\u90ed\u5b9a\u7b11\u4e86\uff0c\u7b11\u5bb9\u4e2d\u5374\u5e26\u7740\u79cd\u8bf4\u4e0d\u51fa\u7684\u5bc2\u5bde\u4e4b\u610f\uff0c\u53c8\u559d\u4e86\u676f\u9152\uff0c\u624d\u5f90\u5f90\u9053\uff1a\u201c\u4ee5\u524d\u6211\u603b\u662f \u60f3\u6218\u80dc\u522b\u4eba\uff0c\u538b\u5012\u522b\u4eba\uff0c\u53ef\u662f\u73b0\u5728\u2026\u2026\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u73b0\u5728\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u90ed\u5b9a\u51dd\u89c6\u7740\u624b\u91cc\u7684\u7a7a\u676f\uff0c\u9053\uff1a\u201c\u73b0\u5728\u6211\u624d\u77e5\u9053\uff0c\u80dc\u5229\u7684\u6ecb\u5473\u5e76\u4e0d\u5982\u6211\u60f3\u8c61\u4e2d\u90a3\u4e48\u597d\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u5c06\u624b\u91cc\u7684\u7a7a\u676f\u91cd\u91cd\u5730\u653e\u5728\u684c\u4e0a\uff0c\u9053\uff1a\u201c\u4f60\u770b\u8fd9\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u8fd9\u662f\u4e2a\u7a7a\u9152\u676f\u3002\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u4e00\u4e2a\u4eba\u6218\u80dc\u4e86\u4e4b\u540e\uff0c\u6709\u65f6\u4e5f\u4f1a\u5ffd\u7136\u53d8\u5f97\u50cf\u8fd9\u7a7a\u9152\u676f\u4e2d\u7684\u9152\u4e00\u6837\uff0c\u7a81\u7136\u53d8\u7a7a \u4e86\u3002\u8fd9\u79cd\u611f\u89c9\u4ed6\u867d\u7136\u6ca1\u6709\u8bf4\u51fa\u6765\uff0c\u53ef\u662f\u53f6\u5f00\u80fd\u4e86\u89e3\u8fd9\u79cd\u65e0\u6cd5\u5f62\u5bb9\u7684\u7a7a\u865a\u548c\u5bc2\u5bde\uff0c\u4ed6\u4e5f\u66fe\u4f53\u9a8c \u8fc7\u3002\u4ed6\u6ca1\u6709\u518d\u8bf4\u4ec0\u4e48\uff0c\u66ff\u90ed\u5b9a\u5012\u6ee1\u4e86\u7a7a\u676f\uff0c\u5fae\u7b11\u9053\uff1a\u201c\u4f60\u4e5f\u8bf4\u5f97\u5927\u591a\u3002\u559d\u5f97\u592a\u5c11\u3002\u201d \u3000\u3000\u90ed\u5b9a\u4e3e\u676f\u3002 \u3000\u3000\u53f6\u5f00\u5fae\u7b11\u7740\uff0c\u53c8\u9053\uff1a\u201c\u65e0\u8bba\u5982\u4f55\uff0c\u80dc\u5229\u7684\u6ecb\u5473\u81f3\u5c11\u603b\u6bd4\u5931\u8d25\u597d\u3002\u201d \u3000\u3000\u5bd2\u98ce\u5728\u7a97\u5916\u547c\u5578\u3002 \u3000\u3000\u5c0f\u70ad\u7089\u91cc\u7684\u706b\u5c06\u7184\u706d\uff0c\u90a3\u6ca1\u7cbe\u6253\u91c7\u7684\u4f19\u8ba1\uff0c\u5c06\u8116\u5b50\u7f29\u5728\u7834\u68c9\u8884\u91cc\uff0c\u4f3c\u5df2\u5feb\u7761\u7740\u4e86\u3002 \u3000\u3000\u5728\u5982\u6b64\u5bd2\u591c\u91cc\uff0c\u53ea\u6709\u5bb6\u624d\u662f\u6e29\u6696\u7684\u3002 \u3000\u3000\u6d41\u6d6a\u5728\u5929\u6daf\u7684\u6d6a\u5b50\u4eec\uff0c\u4f60\u4eec\u7684\u5bb6\u5728\u54ea\u91cc\uff1f\u4f60\u4eec\u4e3a\u4ec0\u4e48\u8fd8\u4e0d\u56de\u53bb\uff1f \u3000\u3000\u6df7\u6d4a\u7684\u9152\uff0c\u51b7\u5f97\u53d1\u82e6\uff0c\u53ef\u662f\u51b7\u9152\u559d\u4e0b\u809a\u5b50\u91cc\u540e\uff0c\u4e5f\u4f1a\u53d8\u6210\u4e00\u56e2\u706b\u3002 \u3000\u3000\u5df2\u559d\u4e86\u51e0\u676f\uff1f\u8c01\u53bb\u8bb0\u5b83\uff1f\u8c01\u8bb0\u5f97\u6e05\uff1f \u3000\u3000\u53f6\u5f00\u6ee1\u6ee1\u5730\u5012\u4e86\u4e00\u676f\uff0c\u5f88\u5feb\u5730\u559d\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u4ed6\u60f3\u9189\uff1f\u60f3\u9003\u907f\uff1f \u3000\u3000\u82e5\u662f\u9047\u89c1\u4e86\u4e00\u4e9b\u65e0\u6cd5\u89e3\u51b3\u3001\u65e0\u53ef\u4eb2\u4f55\u7684\u4e8b\uff0c\u53c8\u6709\u8c01\u4e0d\u60f3\u5927\u9189\u4e00\u573a\uff1f \u3000\u3000\u90ed\u5b9a\u770b\u7740\u4ed6\uff0c\u9053\uff1a\u201c\u6211\u672c\u6765\u53ea\u60f3\u4e00\u4e2a\u4eba\u5728\u8fd9\u91cc\u5927\u9189\u4e00\u573a\uff0c\u5374\u60f3\u4e0d\u5230\u4f1a\u5728\u8fd9\u91cc\u9047\u89c1\u4f60\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u60f3\u4e0d\u5230\u6211\u4f1a\u5230\u8fd9\u79cd\u5730\u65b9\u6765\u559d\u9152\uff1f\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u6211\u60f3\u4e0d\u5230\u4f60\u4f1a\u4e00\u4e2a\u4eba\u6765\u3002\u201d \u3000\u3000\u53f6\u5f00\u53c8\u5e72\u4e86\u4e00\u676f\uff0c\u5ffd\u7136\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u6211\u81ea\u5df1\u4e5f\u60f3\u4e0d\u5230\u3002\u201d \u3000\u3000\u4ed6\u7b11\u5f97\u5f88\u82e6\u3002 \u3000\u3000\u90ed\u5b9a\u4e0d\u61c2\uff1a\u201c\u4f60\u81ea\u5df1\u4e5f\u60f3\u4e0d\u5230\uff1f\u201d \u3000\u3000\u53f6\u5f00\u6c89\u9ed8\u7740\uff0c\u8fc7\u4e86\u5f88\u4e45\uff0c\u624d\u95ee\u9053\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u4e1c\u6d77\u7389\u7bab\uff1f\u201d \u3000\u3000\u90ed\u5b9a\u5f53\u7136\u77e5\u9053\uff0c\u8bf4\u9053\uff1a\u201c\u53ef\u662f\u6211\u6ca1\u6709\u89c1\u8fc7\u4ed6\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u89c1\u8fc7\u3002\u201d \u3000\u3000\u4e1c\u6d77\u7389\u7bab\u5df2\u6709\u5f88\u591a\u5e74\u672a\u66fe\u5728\u6c5f\u6e56\u4e2d\u51fa\u73b0\u8fc7\uff0c\u90ed\u5b9a\u5fcd\u4e0d\u4f4f\u95ee\uff1a\u201c\u4f60\u51e0\u65f6\u89c1\u8fc7\u4ed6\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u521a\u624d\u3002\u201d \u3000\u3000\u90ed\u5b9a\u7684\u773c\u775b\u91cc\u7a81\u7136\u53d1\u51fa\u5149\uff1a\u201c\u4f60\u4eec\u5df2\u4ea4\u8fc7\u624b\uff1f\u201d\u53f6\u5f00\u70b9\u70b9\u5934\u3002 \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u4f60\u4e5f\u80dc\u4e86\u4ed6\uff1f\u6240\u4ee5\u4f60\u624d\u5230\u8fd9\u91cc\u6765\u559d\u9152\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u6ca1\u6709\u80dc\uff0c\u4e5f\u6ca1\u6709\u8d25\u3002\u201d \u3000\u3000\u90ed\u5b9a\u53c8\u4e0d\u61c2\u3002 \u3000\u3000\u5728\u4ed6\u7684\u601d\u60f3\u4e2d\u4e24\u4eba\u53ea\u8981\u4e00\u4ea4\u4e0a\u624b\uff0c\u5c31\u4e00\u5b9a\u8981\u5206\u51fa\u80dc\u8d1f\u3002 \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u4eec\u867d\u7136\u5df2\u4ea4\u624b\uff0c\u5374\u6ca1\u6709\u7ee7\u7eed\u4e0b\u53bb\u3002\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u4e0d\u60f3\u8d25\u7ed9\u4ed6\u3002\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u4f60\u6ca1\u6709\u628a\u63e1\u80dc\u4ed6\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u4f60\u5df1\u770b\u51fa\u4ed6\u7684\u6b66\u529f\u6bd4\u4f60\u9ad8\uff1f\u201d \u3000\u3000\u53f6\u5f00\u7b11\u4e86\u7b11\u9053\uff1a\u201c\u4ed6\u7684\u6b66\u529f\u5f88\u6e0a\u535a\uff0c\u4e5f\u8bb8\u6b63\u56e0\u4e3a\u5982\u6b64\uff0c\u6240\u4ee5\u4e0d\u80fd\u7cbe\u7eaf\u3002\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u4f60\u672c\u6765\u53ef\u4ee5\u80dc\u4ed6\u7684\uff1f\u201d \u3000\u3000\u53f6\u5f00\u5e76\u4e0d\u5426\u8ba4\u3002 \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u53ef\u662f\u4eca\u5929\u4f60\u5374\u6ca1\u6709\u628a\u63e1\u80dc\u4ed6\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u5b8c\u5168\u6ca1\u6709\u3002\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u7684\u5fc3\u5f88\u4e71\u3002\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u4f60\u770b\u6765\u5e76\u4e0d\u50cf\u65f6\u5e38\u4f1a\u5fc3\u4e71\u7684\u4eba\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u672c\u6765\u5c31\u4e0d\u662f\u65f6\u5e38\u4f1a\u5fc3\u4e71\u7684\u4eba\uff0c\u53ef\u662f\u4eca\u5929\u2026\u2026\u201d \u3000\u3000\u90ed\u5b9a\u7a81\u7136\u660e\u767d\uff1a\u201c\u96be\u9053\u90a3\u4f4d\u4e01\u59d1\u5a18\u5df2\u843d\u5165\u7389\u7bab\u624b\u91cc\uff1f\u201d \u3000\u3000\u53f6\u5f00\u70b9\u70b9\u5934\uff0c\u518d\u6b21\u4e3e\u676f\uff0c\u4e00\u996e\u800c\u5c3d\u3002 \u3000\u3000\u90ed\u5b9a\u4e5f\u5e72\u4e86\u4e00\u676f\uff0c\u53c8\u4e00\u676f\uff0c\u201c\u94c1\u5251\u597d\u540d\uff0c\u7389\u7bab\u597d\u8272\u3002\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u4ed6\u5f53\u7136\u542c\u8bf4\u8fc7\u3002 \u3000\u3000\u4ed6\u7a81\u7136\u593a\u8fc7\u53f6\u5f00\u7684\u9152\u676f\uff0c\u5927\u58f0\u9053\uff1a\u201c\u4eca\u5929\u4f60\u7edd\u4e0d\u80fd\u559d\u9189\u3002\u201d \u3000\u3000\u53f6\u5f00\u82e6\u7b11\u3002 \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u4f60\u4e00\u5b9a\u8981\u60f3\u6cd5\u5b50\u8d76\u5feb\u5c06\u5979\u6551\u51fa\u6765\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u60f3\u4e0d\u51fa\u6cd5\u5b50\u3002\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u7389\u7bab\u60f3\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4ed6\u8981\u6211\u7528\u4e0a\u5b98\u5c0f\u4ed9\u53bb\u5c06\u5979\u6362\u56de\u6765\u3002\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u4f60\u4e0d\u80af\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u80af\uff0c\u53ef\u662f\u6211\u627e\u4e0d\u5230\u4e0a\u5b98\u5c0f\u4ed9\u3002\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u4f60\u4e5f\u4e0d\u77e5\u9053\u5979\u5e84\u54ea\u91cc\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6ca1\u6709\u4eba\u77e5\u9053\u3002\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u5979\u771f\u7684\u4e0d\u662f\u4f20\u8bf4\u4e2d\u90a3\u6837\u7684\u767d\u75f4\uff1f\u201d \u3000\u3000\u53f6\u5f00\u82e6\u7b11\u9053\uff1a\u201c\u6211\u672c\u6765\u4e5f\u88ab\u5979\u9a97\u8fc7\u4e86\uff0c\u6211\u8fd9\u4e00\u751f\u4e2d\u4ece\u6765\u4e5f\u6ca1\u6709\u9047\u89c1\u8fc7\u6bd4\u5979\u66f4\u72e1\u733e\u3001\u66f4\u53ef \u6015\u7684\u4eba\u3002\u201d \u3000\u3000\u90ed\u5b9a\u51dd\u89c6\u7740\u4ed6\uff0c\u8fc7\u4e86\u5f88\u4e45\uff0c\u624d\u5f90\u5f90\u9053\uff1a\u201c\u8fd9\u4e9b\u8bdd\u672c\u4e0d\u80fd\u76f8\u4fe1\u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u660e\u767d\u3002\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u53ef\u662f\u73b0\u5728\u6211\u76f8\u4fe1\u4e86\u3002\u201d \u3000\u3000\u53f6\u5f00\u4e5f\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u624d\u5f90\u5f90\u9053\uff1a\u201c\u6211\u672c\u4e0d\u613f\u5c06\u8fd9\u4ef6\u4e8b\u544a\u8bc9\u4f60\uff0c\u53ef\u662f\u73b0\u5728\u6211\u5374\u8bf4\u4e86\u51fa \u6765\u3002\u201d \u3000\u3000\u4ed6\u5e76\u6ca1\u6709\u53bb\u770b\u90ed\u5b9a\u3002 \u3000\u3000\u90ed\u5b9a\u4e5f\u4e0d\u518d\u770b\u4ed6\u3002 \u3000\u3000\u4ed6\u4eec\u7ade\u4eff\u4f5b\u5728\u5c3d\u91cf\u907f\u514d\u63a5\u89e6\u5230\u5bf9\u65b9\u7684\u76ee\u5149\u3002 \u3000\u3000\u4ed6\u4eec\u90fd\u4e0d\u662f\u90a3\u79cd\u559c\u6b22\u5c06\u81ea\u5df1\u60c5\u611f\u6d41\u9732\u51fa\u6765\uff0c\u8ba9\u522b\u4eba\u77e5\u9053\u7684\u4eba\u3002 \u3000\u3000\u96be\u9053\u4ed6\u4eec\u90fd\u751f\u6015\u81ea\u5df1\u7684\u60c5\u611f\u4e00\u65f6\u6fc0\u52a8\uff0c\u4f1a\u6d41\u4e0b\u6cea\u6765\uff1f \u3000\u3000\u4f46\u53cb\u60c5\u8fd9\u4ef6\u4e8b\uff0c\u672c\u5c31\u4e0d\u662f\u7528\u773c\u775b\u770b\u7684\u3002\u4ed6\u4eec\u867d\u7136\u4e0d\u53bb\u770b\uff0c\u53cb\u60c5\u5374\u5df2\u5728\u4ed6\u4eec\u5fc3\u91cc\u6492\u4e0b\u4e86\u79cd \u7c7d\u751f\u51fa\u4e86\u6839\u3002 \u3000\u3000\u8fd9\u7684\u786e\u662f\u4ef6\u5f88\u5947\u602a\u7684\u4e8b\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u5f80\u5f80\u4f1a\u5728\u6700\u5947\u4f84\u7684\u65f6\u5019\uff0c\u6700\u5947\u602a\u7684\u5730\u65b9\uff0c\u548c\u4e00\u4e2a\u6700\u60f3\u4e0d\u5230\u7684\u4eba\u4ea4\u6210\u670b\u53cb\uff0c\u751a\u81f3\u8fde \u4ed6\u95e8\u81ea\u5df1\u90fd\u4e0d\u77e5\u9053\u8fd9\u79cd\u60c5\u611f\u662f\u600e\u4e48\u6765\u7684\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u90ed\u5b9a\u5ffd\u7136\u9053\uff1a\u201c\u4e0a\u5b98\u5c0f\u4ed9\u867d\u7136\u627e\u4e0d\u5230\uff0c\u4f46\u4e1c\u6d77\u7389\u7bab\u5374\u4e00\u5b9a\u53ef\u4ee5\u627e \u5230\u3002\u201d \u3000\u3000\u53f6\u5f00\u5728\u542c\u7740\u3002 \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u4ed6\u662f\u4e2a\u559c\u6b22\u4eab\u53d7\u7684\u4eba\uff0c\u8fd9\u57ce\u91cc\u7684\u597d\u5730\u65b9\u5374\u4e0d\u591a\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6700\u597d\u7684\u5730\u65b9\u672c\u6765\u662f\u51b7\u9999\u56ed\uff0c\u4f46\u73b0\u5728\u5374\u5df2\u53ea\u51b7\u4e0d\u9999\u4e86\u3002\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u4f46\u4ed6\u8fd8\u662f\u5f88\u53ef\u80fd\u4f1a\u4f4f\u5728\u90a3\u91cc\uff0c\u636e\u8bf4\u4ed6\u65e0\u8bba\u5230\u54ea\u91cc\uff0c\u90fd\u4e00\u5411\u6709\u5f88\u591a\u968f\u4ece\u7684 \u4eba\u3002\u201d \u3000\u3000\u53f6\u5f00\u7b11\u9053\uff1a\u201c\u5c31\u7b97\u4ed6\u5728\u90a3\u91cc\u53c8\u5982\u4f55\uff1f\u201d \u3000\u3000\u201c\u4ed6\u5728\u90a3\u91cc\uff0c\u4e01\u59d1\u5a18\u4e5f\u5c31\u5728\u90a3\u91cc\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u8981\u6211\u53bb\u6551\u5979\uff1f\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u4f60\u4e0d\u53bb\u3002\u201d \u3000\u3000\u53f6\u5f00\u82e6\u7b11\u9053\uff1a\u201c\u6211\u73b0\u5728\u7684\u5fc3\u66f4\u4e71\uff0c\u66f4\u6ca1\u6709\u628a\u63e1\u80dc\u4ed6\u3002\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u6211\u96be\u9053\u4e0d\u662f\u4eba\uff1f\u201d \u3000\u3000\u53f6\u5f00\u970d\u7136\u62ac\u8d77\u5934\uff0c\u51dd\u89c6\u7740\u4ed6\uff0c\u9053\uff1a\u201c\u4f60\u2026\u2026\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u6211\u96be\u9053\u4e0d\u80fd\u8ddf\u4f60\u4e00\u8d77\u53bb\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u53ef\u662f\u2026\u2026\u53ef\u662f\u4e01\u7075\u7433\u8fd8\u5728\u4ed6\u624b\u91cc\u3002\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u6211\u660e\u767d\u4f60\u7684\u610f\u601d\uff0c\u4f60\u662f\u6295\u9f20\u5fcc\u5668\uff0c\u6015\u4ed6\u7528\u4e01\u59d1\u5a18\u6765\u5bf9\u4ed8\u4f60\uff0c\u6015\u4ed6\u4f24\u5bb3\u4e86\u4e01\u59d1 \u5a18\u3002\u201d \u3000\u3000\u53f6\u5f00\u70b9\u70b9\u5934\u3002 \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u4f46\u4f60\u5374\u5fd8\u4e86\u4e00\u70b9\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u4ed6\u4e00\u5b9a\u4ee5\u4e3a\u4f60\u73b0\u5728\u6b63\u6025\u7740\u627e\u4e0a\u5b98\u5c0f\u4ed9\uff0c\u4e00\u5b9a\u60f3\u4e0d\u5230\u4f60\u4f1a\u53bb\u627e\u4ed6\u7684\uff0c\u6240\u4ee5\u4ed6\u5c31 \u4e00\u5b9a\u4e0d\u4f1a\u6709\u8b66\u6212\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u4f55\u51b5\uff0c\u4ed6\u66f4\u4e0d\u4f1a\u60f3\u5230\u6211\u4eec\u5df2\u6210\u4e86\u670b\u53cb\u3002\u201d \u3000\u3000\u670b\u53cb\uff01 \u3000\u3000\u8fd9\u662f\u591a\u4e48\u6e29\u6696\u3001\u591a\u4e48\u795e\u5723\u7684\u4e24\u4e2a\u5b57\u3002 \u3000\u3000\u8fd9\u4e24\u4e2a\u5b57\u7adf\u771f\u7684\u4ece\u8fd9\u4e2a\u9a84\u505a\u51b7\u9177\u7684\u5e74\u8f7b\u4eba\u5634\u91cc\u8bf4\u4e86\u51fa\u6765\u3002 \u3000\u3000\u53f6\u5f00\u8fd8\u80fd\u8bf4\u4ec0\u4e48\uff1f\u8fd8\u9700\u8981\u8bf4\u4ec0\u4e48\uff1f \u3000\u3000\u4ed6\u4ec0\u4e48\u90fd\u4e0d\u518d\u60a6\uff0c\u4ed6\u5df2\u7ad9\u4e86\u8d77\u6765\uff0c\u731b\u7136\u7528\u529b\u63e1\u4f4f\u4e86\u90ed\u5b9a\u7684\u80a9\u3002 \u3000\u3000\u201c\u6211\u4eec\u8d70\u3002\u201d\u201c\u8d70\uff01\u201d \u7b2c\u5341\u4e8c\u7ae0\u3000\u51b7\u591c\u79bb\u9b42>> \u53e4\u9f99\u300a\u4e5d\u6708\u9e70\u98de\u300b \u7b2c\u5341\u4e8c\u7ae0\u3000\u51b7\u591c\u79bb\u9b42 \u3000\u3000\u51b7\u9999\u56ed\u3002 \u3000\u3000\u591c\u51b7\uff0c\u6885\u9999\uff0c\u4eba\u8e2a\u5df2\u6773\u3002 \u3000\u3000\u6885\u6797\u91cc\u7c41\u7c41\u7684\u54cd\uff0c\u662f\u98ce\uff1f\u8fd8\u662f\u6628\u5929\u5728\u6b7b\u5728\u8fd9\u91cc\u7684\u51a4\u9b42\uff1f \u3000\u3000\u201c\u4f60\u4e00\u76f4\u90fd\u6ca1\u6709\u518d\u89c1\u5230\u97e9\u8d1e\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\u4ed6\u8bf4\u4e0d\u5b9a\u8fd8\u5728\u8fd9\u91cc\u3002\u201d \u3000\u3000\u53f6\u5f00\u53f9\u9053\uff1a\u201c\u6211\u53ea\u5e0c\u671b\u627e\u5230\u7684\u4e0d\u662f\u4ed6\u7684\u5c38\u4f53\u3002\u201d \u3000\u3000\u90a3\u4e9b\u4eba\u7684\u5c38\u4f53\u5462\uff1f \u3000\u3000\u627e\u4e0d\u5230\u3002 \u3000\u3000\u542c\u6d9b\u697c\u4e0a\u4e0b\uff0c\u8fde\u8840\u8ff9\u90fd\u88ab\u6d17\u5f97\u5e72\u5e72\u51c0\u51c0\u3002 \u3000\u3000\u662f\u8c01\u66ff\u4ed6\u4eec\u6536\u5c38\u7684\u5462\uff1f \u3000\u3000\u201c\u536b\u5929\u9e4f\u4ed6\u4eec\u7684\u5c38\u4f53\u6628\u591c\u8fd8\u5728\u8fd9\u91cc\u3002\u201d \u3000\u3000\u201c\u662f\u8c01\u66ff\u4ed6\u4eec\u6536\u4e86\u5c38\uff1f\u201d \u3000\u3000\u6ca1\u6709\u56de\u7b54\uff0c\u6ca1\u6709\u4eba\u80fd\u56de\u7b54\u3002 \u3000\u3000\u521a\u9694\u591c\u7684\u51b0\u96f9\uff0c\u665a\u4e0a\u53c8\u7ed3\u6210\u4e86\u51b0\u3002 \u3000\u3000\u98ce\u522e\u5728\u8138\u4e0a\uff0c\u5df2\u4e0d\u50cf\u662f\u51e4\uff0c\u800c\u50cf\u662f\u5200\u3002 \u3000\u3000\u5bd2\u6885\u5728\u51b7\u9999\u4e2d\u5374\u66f4\u9999\u3002 \u3000\u3000\u201c\u4f60\u770b\u89c1\u706f\u706b\u6ca1\u6709\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u201c\u7389\u7bab\u96be\u9053\u4e0d\u5728\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u7a81\u7136\u95f4\uff0c\u7ed3\u4e86\u51b0\u7684\u5c0f\u5f84\u4e0a\uff0c\u7adf\u4f3c\u54cd\u8d77\u4e86\u4e00\u9635\u5f88\u8f7b\u7684\u811a\u6b65\u58f0\u3002 \u3000\u3000\u5982\u6b64\u5bd2\u591c\uff0c\u6709\u8c01\u4f1a\u5728\u96ea\u5f84\u4e0a\u72ec\u884c\uff1f\u83ab\u975e\u662f\u90a3\u4e9b\u4eba\u7684\u9b3c\u9b42\uff1f \u3000\u3000\u9b3c\u9b42\u53c8\u5982\u4f55\u6709\u811a\u6b65\u58f0\uff1f \u3000\u3000\u8fd8\u662f\u6ca1\u6709\u706f\u5149\uff0c\u65e0\u661f\uff0c\u65e0\u6708\u3002 \u3000\u3000\u9ed1\u6697\u4e2d\u4eff\u4f5b\u51fa\u73b0\u4e86\u6761\u4eba\u5f71\uff0c\u6b63\u6162\u6162\u5730\u8d70\u51fa\u4e86\u6885\u6797\u4e2d\u7684\u5c0f\u5f84\u3002 \u3000\u3000\u4ed6\u8d70\u5f97\u5f88\u6162\uff0c\u8fd8\u4e0d\u65f6\u5728\u4e1c\u5f20\u897f\u671b\uff0c\u7adf\u4f3c\u5728\u5bfb\u627e\u7740\u4ec0\u4e48\u3002 \u3000\u3000\u5982\u6b64\u5bd2\u51b7\u7684\u6df1\u591c\u91cc\uff0c\u5728\u8fd9\u65e0\u4eba\u7684\u6885\u6797\u4e2d\uff0c\u4ed6\u5bfb\u627e\u7684\u662f\u4ec0\u4e48\uff1f \u3000\u3000\u8d70\u5f97\u8fd1\u4e86\uff0c\u624d\u542c\u51fa\u4ed6\u5634\u91cc\u7ade\u4e00\u76f4\u5728\u5583\u5583\u81ea\u8bed\uff1a\u201c\u9152\u5462\u2026\u2026\u4ec0\u4e48\u5730\u65b9\u6709\u9152\u2026\u2026\u201d \u3000\u3000\u53f6\u5f00\u51e0\u4e4e\u5fcd\u4e0d\u4f4f\u53eb\u4e86\u51fa\u6765\uff1a\u201c\u97e9\u8d1e\uff01\u201d \u3000\u3000\u8fd9\u4e2a\u4eba\u7adf\u8d6b\u7136\u771f\u7684\u662f\u97e9\u8d1e\u3002 \u3000\u3000\u96be\u9053\u4ed6\u5c45\u7136\u8fd8\u5728\u66ff\u53f6\u5f00\u627e\u9152\uff1f \u3000\u3000\u96ea\u5149\u53cd\u6620\uff0c\u7167\u4e0a\u4e86\u4ed6\u7684\u8138\uff0c\u4ed6\u7684\u8138\u4e0a\u7adf\u8d6b\u7136\u5168\u662f\u8840\uff0c\u4e14\u4e5f\u7ed3\u6210\u4e86\u51b0\u3002 \u3000\u3000\u53f6\u5f00\u53ea\u89c9\u5f97\u80f8\u4e2d\u4e00\u9635\u6c14\u8840\u4e0a\u6d8c\uff0c\u7acb\u523b\u4ece\u4ed6\u9690\u85cf\u7684\u5c0f\u77f3\u540e\u51b2\u4e86\u51fa\u53bb\uff0c\u51b2\u5230\u97e9\u8d1e\u9762\u524d\uff0c\u4e00\u628a \u63e1\u4f4f\u4e86\u97e9\u8d1e\u7684\u80a9\u3002 \u3000\u3000\u97e9\u8d1e\u770b\u4e86\u4ed6\u4e00\u773c\uff0c\u5ffd\u7136\u9053\uff1a\u201c\u9152\u5462\uff1f\u2026\u2026\u4f60\u77e5\u4e0d\u77e5\u9053\u4ec0\u4e48\u5730\u65b9\u6709\u9152\uff1f\u201d \u3000\u3000\u4ed6\u7adf\u5df2\u4e0d\u8ba4\u5f97\u53f6\u5f00\uff0c\u53ef\u8fd8\u5728\u4e3a\u53f6\u5f00\u627e\u9152\u3002 \u3000\u3000\u4ed6\u7684\u8138\u7adf\u5df2\u51e0\u4e4e\u5b8c\u5168\u7834\u788e\u599e\u66f2\uff0c\u7adf\u50cf\u662f\u4e2a\u5df2\u88ab\u4eba\u4e00\u811a\u8e29\u70c2\u4e86\u7684\u786c\u58f3\u679c\u3002 \u3000\u3000\u53f6\u5f00\u4e0d\u5fcd\u518d\u770b\uff1a\u201c\u4f60\u2026\u2026\u4f60\u600e\u4e48\u4f1a\u53d8\u6210\u8fd9\u6837\u5b50\u7684\uff1f\u8fd9\u662f\u8c01\u4e0b\u7684\u6bd2\u624b\uff1f\u201d \u3000\u3000\u97e9\u8d1e\u4f3c\u4e4e\u60f3\u7b11\uff0c\u5374\u7b11\u4e0d\u51fa\uff0c\u5634\u91cc\u8fd8\u662f\u5583\u5583\u5730\u5728\u95ee\uff1a\u201c\u9152\u5462\uff1f\u4ec0\u4e48\u5730\u65b9\u6709\u9152\uff1f\u201d \u3000\u3000\u53f6\u5f00\u7684\u5fc3\uff0c\u4e5f\u597d\u50cf\u88ab\u4eba\u91cd\u91cd\u8e29\u4e86\u4e00\u811a\u3002 \u3000\u3000\u90ed\u5b9a\u5c31\u5728\u8eab\u540e\uff0c\u5fcd\u4e0d\u4f4f\u9053\uff1a\u201c\u4ed6\u5c31\u662f\u97e9\u8d1e\uff1f\u201d \u3000\u3000\u53f6\u5f00\u70b9\u70b9\u5934\u3002 \u3000\u3000\u90ed\u5b9a\u4e5f\u4e0d\u7981\u53f9\u606f\uff0c\u9053\uff1a\u201c\u770b\u6765\u4ed6\u662f\u5728\u66ff\u4f60\u627e\u9152\u7684\u65f6\u5019\uff0c\u88ab\u4eba\u75db\u6bb4\u4e86\u4e00\u987f\uff0c\u6253\u5f97\u4ed6\u8fde\u8bb0\u5fc6 \u90fd\u4e27\u5931\u3002\u201d \u3000\u3000\u53f6\u5f00\u7528\u529b\u63e1\u7d27\u53cc\u62f3\uff0c\u9eef\u7136\u9053\uff1a\u201c\u4e0d\u8fc7\u4ed6\u8fd8\u8bb0\u5f97\u66ff\u6211\u627e\u9152\u3002\u201d \u3000\u3000\u90ed\u5b9a\u53f9\u9053\uff1a\u201c\u770b\u6765\u4ed6\u4e5f\u662f\u4e2a\u597d\u670b\u53cb\uff0c\u201d\u53f6\u5f00\u6068\u58f0\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u6211\u4e0d\u77e5\u9053\u8fd9\u662f\u8c01\u4e0b\u7684\u6bd2 \u624b\uff0c\u5426\u5219\u2026\u2026\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u6211\u60f3\u8fd9\u7edd\u4e0d\u662f\u4e0a\u5b98\u5c0f\u4ed9\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u4e00\u4e2a\u5973\u4eba\uff0c\u7edd\u4e0d\u4f1a\u6709\u8fd9\u4e48\u91cd\u7684\u624b\u3002\u201d \u3000\u3000\u97e9\u8d1e\u5b9e\u5728\u88ab\u6253\u5f97\u592a\u60e8\uff0c\u4e0d\u4f46\u8138\u5df2\u7834\u788e\u626d\u66f2\uff0c\u8fde\u808b\u9aa8\u90fd\u5df2\u9677\u843d\u4e0b\u53bb\uff0c\u81f3\u5c11\u65ad\u4e86\u516d\u4e03\u6839\u3002\u4ed6 \u600e\u4e48\u80fd\u6d3b\u5230\u73b0\u5728\u7684\uff1f \u3000\u3000\u5728\u8fd9\u79cd\u51b0\u5929\u96ea\u5730\u91cc\u4ed6\u600e\u4e48\u8fd8\u6ca1\u6709\u51bb\u6b7b\uff1f \u3000\u3000\u53f6\u5f00\u60f3\u95ee\uff0c\u4f46\u97e9\u8d1e\u5374\u5df2\u7529\u8131\u4ed6\u7684\u624b\uff1a\u201c\u653e\u5f00\u6211\uff0c\u6211\u8981\u53bb\u627e\u9152\u3002\u201d \u3000\u3000\u9664\u4e86\u8fd9\u4ef6\u4e8b\u5916\uff0c\u4ed6\u5df2\u8bb0\u4e0d\u5f97\u522b\u7684\u3002 \u3000\u3000\u53f6\u5f00\u53f9\u4e86\u53e3\u6c14\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u597d\uff0c\u6211\u5e26\u4f60\u53bb\u627e\u9152\u3002\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u8bf4\u5b8c\uff0c\u4ed6\u5df2\u70b9\u4e86\u97e9\u8d1e\u7684\u7761\u7a74\uff0c\u5c06\u97e9\u8d1e\u62e6\u8170\u6258\u4e86\u8d77\u6765\u3002 \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u53ea\u8981\u80fd\u5b89\u5b89\u9759\u9759\u5730\u7761\u4e00\u5929\uff0c\u4ed6\u4e5f\u8bb8\u4f1a\u6e05\u9192\u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u53f9\u9053\uff1a\u201c\u4f46\u613f\u5982\u6b64\u3002\u201d \u3000\u3000\u5c4b\u5b50\u91cc\u6709\u5e8a\uff0c\u4e5f\u6709\u706f\u3002 \u3000\u3000\u53f6\u5f00\u5c06\u97e9\u8d1e\u653e\u5728\u5e8a\u4e0a\uff0c\u9053\uff1a\u201c\u4f60\u6709\u6ca1\u6709\u706b\u71a0\u5b50\uff1f\u201d \u3000\u3000\u90ed\u5b9a\u5df2\u71c3\u8d77\u706f\uff0c\u706f\u5149\u7167\u5728\u97e9\u8d1e\u8138\u4e0a\uff0c\u66f4\u60e8\u4e0d\u5fcd\u7779\u3002 \u3000\u3000\u53f6\u5f00\u867d\u4e0d\u5fcd\u770b\uff0c\u5374\u4e0d\u80fd\u4e0d\u770b\uff0c\u4ed6\u4e00\u5b9a\u8981\u67e5\u51fa\u8fd9\u662f\u8c01\u4e0b\u7684\u6bd2\u624b\u3002 \u3000\u3000\u4ed6\u867d\u7136\u662f\u4e2a\u4e0d\u613f\u8bb0\u4f4f\u522b\u4eba\u4ec7\u6068\u7684\u4eba\uff0c\u4f46\u8fd9\u6b21\u7684\u60c5\u51b5\u5374\u4e0d\u540c\u3002 \u3000\u3000\u82e5\u4e0d\u662f\u4e3a\u4e86\u66ff\u4ed6\u627e\u9152\uff0c\u97e9\u8d1e\u53c8\u600e\u4e48\u4f1a\u843d\u5f97\u8fd9\u4e48\u60e8\u3002 \u3000\u3000\u4e3a\u4e86\u8fd9\u6837\u7684\u670b\u53cb\uff0c\u65e0\u8bba\u4ec0\u4e48\u4e8b\u4ed6\u90fd\u5e94\u8be5\u505a\u3002 \u3000\u3000\u90ed\u5b9a\u4e5f\u5728\u51dd\u89c6\u7740\u97e9\u8d1e\u7684\u8138\uff0c\u9053\uff1a\u201c\u8fd9\u4e0d\u662f\u94c1\u5668\u6253\u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u70b9\u70b9\u5934\uff0c\u82e5\u662f\u88ab\u94c1\u5668\u6253\u4f24\uff0c\u4f24\u75d5\u4e5f\u53ef\u4ee5\u770b\u5f97\u51fa\u3002 \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u96be\u9053\u6709\u8fd9\u4e48\u91cd\u7684\u624b\u6cd5\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u97e9\u8d1e\u7684\u6b66\u529f\u5e76\u4e0d\u5f31\uff0c\u80fd\u4e00\u62f3\u6253\u5230\u4ed6\u7684\u8138\uff0c\u8fd9\u6837\u7684\u4eba\u5e76\u4e0d\u591a\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u60f3\u8d77\u81ea\u5df1\u4e5f\u66fe\u4e00\u62f3\u6253\u5728\u97e9\u8d1e\u8138\u4e0a\uff0c\u4f46\u662f\u90a3\u6b21\u7684\u4f24\u75d5\u5374\u6bd4\u73b0\u5728\u8f7b\u5f97\u591a\uff0c\u663e\u7136\u8fd9\u4eba\u7684 \u624b\u4e0d\u4f46\u6bd4\u4ed6\u91cd\uff0c\u624b\u4e0a\u4e00\u5b9a\u8fd8\u6709\u522b\u7684\u529f\u592b\u3002 \u3000\u3000\u89e3\u5f00\u8863\u895f\uff0c\u808b\u9aa8\u65ad\u4e86\u4e94\u6839\u3002 \u3000\u3000\u5982\u6b64\u5bd2\u5929\uff0c\u97e9\u8d1e\u7a7f\u7684\u8863\u670d\u5f53\u7136\u4e5f\u5f88\u539a\u3002 \u3000\u3000\u90ed\u5b9a\u76b1\u7709\u9053\uff1a\u201c\u9694\u7740\u8fd9\u4e48\u539a\u7684\u8863\u670d\uff0c\u8fd8\u80fd\u4e00\u62f3\u6253\u65ad\u4ed6\u4e94\u6839\u808b\u9aa8\uff0c\u8fd9\u79cd\u4eba\u5b9e\u5728\u4e0d\u591a\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u800c\u4e14\u8fd9\u53ea\u662f\u786c\u4f24\uff0c\u5e76\u6ca1\u6709\u5185\u4f24\u3002\u201d \u3000\u3000\u82e5\u4e0d\u662f\u8863\u670d\u4e0a\u6ca1\u6709\u94c1\u5668\u7684\u75d5\u8ff9\uff0c\u65e0\u8bba\u8c01\u90fd\u4f1a\u8ba4\u4e3a\u8fd9\u662f\u88ab\u4e00\u67c4\u94c1\u9524\u6253\u4f24\u7684\u3002 \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u96be\u9053\u8fd9\u4eba\u7684\u624b\u7adf\u8ddf\u94c1\u9524\u4e00\u6837\u786c\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u770b\u4ed6\u7684\u4f24\u75d5\uff0c\u4e5f\u4e0d\u50cf\u662f\u88ab\u94c1\u7802\u638c\u4e00\u7c7b\u7684\u529f\u592b\u6253\u4f24\u7684\u3002\u201d \u3000\u3000\u90ed\u5b9a\u70b9\u70b9\u5934\u9053\uff1a\u201c\u82e5\u662f\u90a3\u4e00\u7c7b\u7684\u638c\u529b\uff0c\u5fc5\u5b9a\u4f1a\u9707\u4f24\u5185\u8151\u3002\u201d \u3000\u3000\u53f6\u5f00\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6240\u4ee5\u6211\u5b9e\u5728\u4e0d\u660e\u767d\uff0c\u8fd9\u7a76\u7adf\u662f\u79cd\u4ec0\u4e48\u6837\u7684\u529f\u592b\uff1f\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u4f60\u8fdf\u65e9\u2026\u2026\u201d \u3000\u3000\u4ed6\u7684\u58f0\u97f3\u7a81\u7136\u505c\u987f\uff0c\u65e0\u8a00\u7684\u5bd2\u98ce\u4e2d\uff0c\u7adf\u7a81\u7136\u4f20\u6765\u4e86\u4e00\u9635\u51c4\u51c9\u7684\u7bab\u58f0\u3002 \u3000\u3000\u4e1c\u6d77\u7389\u8427\uff01 \u3000\u3000\u90ed\u5b9a\u4e00\u7ffb\u624b\uff0c\u5df2\u6247\u706d\u4e86\u706f\u5149\uff1a\u201c\u4ed6\u679c\u7136\u5728\u8fd9\u91cc\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u80fd\u4e0d\u80fd\u5728\u8fd9\u91cc\u66ff\u6211\u2026\u2026\u201d \u3000\u3000\u90ed\u5b9a\u7acb\u523b\u6253\u65ad\u4ed6\u7684\u8bdd\uff1a\u201c\u97e9\u8d1e\u5df2\u7761\u7740\uff0c\u7528\u4e0d\u7740\u6211\u5728\u8fd9\u91cc\u770b\u5b88\uff0c\u4f60\u5374\u4e0d\u80fd\u4e00\u4e2a\u4eba\u53bb\u3002\u201d \u3000\u3000\u8fd9\u5c31\u662f\u53cb\u60c5\uff0c\u53cb\u60c5\u5c31\u662f\u4e86\u89e3\u548c\u5173\u5207\u3002 \u3000\u3000\u53f6\u5f00\u770b\u7740\u97e9\u8d1e\u9053\uff1a\u201c\u53ef\u662f\u4ed6\u2026\u2026\u201d \u3000\u3000\u90ed\u5b9a\u53c8\u6253\u65ad\u4e86\u4ed6\u7684\u8bdd\u9053\uff1a\u201c\u73b0\u5728\u4ed6\u7684\u6b7b\u6d3b\u5bf9\u522b\u4eba\u5df2\u6ca1\u6709\u5f71\u54cd\uff0c\u6240\u4ee5\u4ed6\u624d\u80fd\u6d3b\u5230\u73b0\u5728\uff0c\u53ef \u662f\u4f60\u2026\u2026\u201d \u3000\u3000\u4ed6\u6ca1\u6709\u518d\u8bf4\u4e0b\uff0c\u4e5f\u4e0d\u5fc5\u8bf4\u4e0b\u53bb\u3002 \u3000\u3000\u53f6\u5f00\u53ea\u89c9\u5f97\u80f8\u4e2d\u7684\u8840\u53c8\u70ed\u4e86\uff0c\u4e5f\u4e0d\u80fd\u4e0d\u627f\u8ba4\u4ed6\u8bf4\u7684\u8bdd\u6709\u9053\u7406\u3002 \u3000\u3000\u201c\u597d\uff0c\u6211\u4eec\u8d70\u3002\u201d \u3000\u3000\u51c4\u51c9\u7684\u7bab\u58f0\uff0c\u5728\u5bd2\u591c\u4e2d\u542c\u6765\uff0c\u4ee4\u4eba\u7684\u5fc3\u90fd\u788e\u4e86\u3002 \u3000\u3000\u7bab\u58f0\u662f\u4ece\u6885\u6797\u5916\u4f20\u6765\u7684\u3002 \u3000\u3000\u6885\u6797\u5916\u7684\u5047\u5c71\u65c1\uff0c\u6709\u4e2a\u5c0f\u5c0f\u7684\u516b\u89d2\u4ead\uff0c\u4ead\u5b50\u91cc\u6709\u6761\u6726\u80e7\u7684\u4eba\u5f71\uff0c\u90a3\u4eba\u6b63\u5728\u5439\u7bab\u3002 \u3000\u3000\u53f6\u5f00\u4ed6\u4eec\u4ece\u540e\u9762\u6084\u6084\u5730\u7ed5\u4e86\u8fc7\u53bb\uff0c\u4ed6\u4eec\u7684\u884c\u52a8\u5f53\u7136\u4e0d\u4f1a\u53d1\u51fa\u4efb\u4f55\u58f0\u97f3\u3002 \u3000\u3000\u5439\u7bab\u7684\u4eba\u8fd8\u5728\u5439\u7bab\uff0c\u7bab\u58f0\u4f3c\u5728\u98a4\u6296\u3002 \u3000\u3000\u53f6\u5f00\u5ffd\u7136\u53d1\u73b0\u8fd9\u5e76\u4e0d\u662f\u201c\u4e1c\u6d77\u7389\u7bab\u201d\u7684\u7bab\u58f0\uff0c\u518d\u8d70\u8fd1\u4e9b\uff0c\u53c8\u53d1\u73b0\u8fd9\u4eba\u8eab\u4e0a\u867d\u7a7f\u7740\u9053\u888d\uff0c \u8170\u80a2\u5374\u5f88\u7ea4\u7ec6\uff0c\u7adf\u662f\u4e2a\u5973\u9053\u4eba\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u7bab\u58f0\u7a81\u7136\u505c\u987f\uff0c\u5439\u7bab\u7684\u5973\u9053\u4eba\uff0c\u7adf\u4f3c\u5728\u4f4e\u4f4e\u54ed\u6ce3\u3002 \u3000\u3000\u53f6\u5f00\u8fdf\u7591\u7740\uff0c\u7ec8\u4e8e\u8d70\u8fc7\u53bb\uff0c\u8f7b\u8f7b\u54b3\u55fd\u4e86\u4e00\u58f0\uff0c\u8fd9\u5973\u9053\u4eba\u5374\u4f3c\u7a81\u7136\u88ab\u62bd\u4e86\u4e00\u97ad\u5b50\uff0c\u5168\u8eab\u90fd \u98a4\u6296\u8d77\u6765\uff0c\u54c0\u58f0\u9053\uff1a\u201c\u6211\u5439\u2026\u2026\u6211\u7edd\u4e0d\u6562\u518d\u505c\u4e0b\u6765\u4e86\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u53ef\u662f\u6211\u5e76\u6ca1\u6709\u8981\u4f60\u4e0d\u505c\u5730\u5439\u4e0b\u53bb\u3002\u201d \u3000\u3000\u5973\u9053\u4eba\u56de\u8fc7\u5934\uff0c\u770b\u89c1\u4ed6\uff0c\u867d\u7136\u4e5f\u5403\u4e86\u4e00\u60ca\uff0c\u5374\u53c8\u4eff\u4f5b\u677e\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u662f\u4f60\u3002\u201d \u3000\u3000\u5979\u8ba4\u5f97\u53f6\u5f00\uff0c\u53f6\u5f00\u4e5f\u8ba4\u5f97\u5979\u3002 \u3000\u3000\u5979\u5c31\u662f\u7389\u7bab\u9053\u4eba\u7684\u5973\u5f1f\u5b50\u4e2d\uff0c\u957f\u5f97\u6700\u5a9a\u7684\u4e00\u4e2a\u3002 \u3000\u3000\u53f6\u5f00\u5fcd\u4e0d\u4f4f\u95ee\uff1a\u201c\u4f60\u600e\u4e48\u4f1a\u4e00\u4e2a\u4eba\u5230\u8fd9\u91cc\u5439\u7bab\uff1f\u201d \u3000\u3000\u5973\u9053\u4eba\u9053\uff1a\u201c\u662f\u2026\u2026\u662f\u522b\u4eba\u903c\u6211\u6765\u7684\u3002\u201d \u3000\u3000\u201c\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u662f\u4e2a\u8499\u7740\u8138\u7684\u4eba\u3002\u201d \u3000\u3000\u201c\u4ed6\u4e3a\u4ec0\u4e48\u8981\u903c\u4f60\u5230\u8fd9\u91cc\u5439\u7bab\uff1f\u201d \u3000\u3000\u201c\u6211\u4e5f\u4e0d\u77e5\u9053\uff0c\u4ed6\u903c\u6211\u5230\u8fd9\u91cc\u6765\uff0c\u53eb\u6211\u4e00\u76f4\u5439\uff0c\u5426\u5219\u4ed6\u5c31\u8981\u8131\u5149\u6211\u7684\u8863\u670d\uff0c\u628a\u6211\u540a\u5728\u8fd9 \u91cc\u3002\u201d \u3000\u3000\u201c\u4f60\u600e\u4e48\u4f1a\u843d\u5728\u4ed6\u624b\u91cc\u7684\uff1f\u201d \u3000\u3000\u201c\u90a3\u65f6\u6211\u6b63\u2026\u2026\u6b63\u5728\u540e\u9762\uff0c\u53ea\u6709\u6211\u4e00\u4e2a\u4eba\uff0c\u60f3\u4e0d\u5230\u4ed6\u7adf\u7a81\u7136\u95ef\u4e86\u8fdb\u6765\u3002\u201d \u3000\u3000\u53f6\u5f00\u5f53\u7136\u77e5\u9053\u201c\u540e\u9762\u201d\u662f\u4ec0\u4e48\u610f\u601d\uff0c\u5973\u5b69\u5b50\u65b9\u4fbf\u65f6\uff0c\u5f53\u7136\u4e5f\u53ea\u6709\u4e00\u4e2a\u4eba\uff0c\u8fd9\u79cd\u4e8b\u5979\u5f53\u7136 \u4e0d\u597d\u610f\u601d\u8bf4\u51fa\u53e3\u3002 \u3000\u3000\u4f46\u53f6\u5f00\u5374\u53c8\u95ee\u9053\uff1a\u201c\u90a3\u65f6\u4f60\u7a76\u7adf\u5728\u4ec0\u4e48\u5730\u65b9\uff1f\u201d \u3000\u3000\u201c\u5c31\u5728\u5409\u7965\u6808\u540e\u9762\u90a3\u9662\u5b50\u3002\u201d \u3000\u3000\u5409\u7965\u6808\u5c31\u662f\u53f6\u5f00\u4f4f\u7684\u90a3\u5ba2\u6808\uff0c\u90a3\u91cc\u4e0d\u4f46\u6709\u6700\u597d\u7684\u53a8\u5b50\uff0c\u4e5f\u6709\u6700\u8212\u670d\u7684\u5e8a\u3002 \u3000\u3000\u559c\u6b22\u4eab\u53d7\u7684\u4eba\u5f53\u7136\u4f1a\u4f4f\u5728\u90a3\u91cc\u3002 \u3000\u3000\u53f6\u5f00\u53f9\u4e86\u53e3\u6c14\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u539f\u6765\u4f60\u4eec\u5c31\u5728\u6211\u540e\u9762\u7684\u9662\u5b50\u91cc\uff0c\u6211\u5374\u5230\u8fd9\u91cc\u6765\u627e\u3002\u201d \u3000\u3000\u5973\u9053\u4eba\u7d27\u7d27\u95ed\u7740\u5634\uff0c\u6b7b\u4e5f\u4e0d\u5f00\u53e3\u4e86\uff0c\u5979\u77e5\u9053\u81ea\u5df1\u5df2\u8bf4\u6f0f\u4e86\u5634\uff0c\u73b0\u5728\u5c31\u7b97\u4e0d\u5f00\u53e3\uff0c\u4e5f\u5df2\u6765 \u4e0d\u53ca\u3002 \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6709\u53e5\u8bdd\u6211\u8981\u95ee\u4f60\uff0c\u4f60\u4e5f\u53ef\u4ee5\u4e0d\u8bf4\u3002\u201d \u3000\u3000\u5973\u9053\u4eba\u95ed\u7740\u5634\u3002 \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f46\u4f60\u82e5\u4e0d\u8bf4\uff0c\u6211\u5c31\u5c06\u4f60\u7559\u5728\u8fd9\u91cc\u8ba9\u90a3\u4e2a\u8499\u9762\u4eba\u518d\u6765\u627e\u4f60\u3002\u201d \u3000\u3000\u5973\u9053\u4eba\u8138\u4e0a\u7acb\u523b\u9732\u51fa\u6050\u60e7\u4e4b\u8272\uff0c\u62a2\u7740\u9053\uff1a\u201c\u6211\u8bf4\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u4eec\u5e26\u8d70\u7684\u90a3\u4e01\u59d1\u5a18\uff0c\u662f\u4e0d\u662f\u4e5f\u5728\u90a3\u9662\u5b50\u91cc\uff1f\u201d \u3000\u3000\u5973\u9053\u4eba\u867d\u7136\u8fd8\u662f\u4e0d\u5f00\u53e3\uff0c\u5374\u5df2\u7b49\u4e8e\u9ed8\u8ba4\u3002 \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u5582\uff0c\u6211\u4eec\u4e0d\u59a8\u505a\u4e2a\u4ea4\u6613\uff0c\u4f60\u5e26\u6211\u53bb\u627e\u5979\uff0c\u6211\u5c31\u9001\u4f60\u56de\u53bb\u3002\u201d \u3000\u3000\u5973\u9053\u4eba\u6ca1\u6709\u62d2\u7edd\uff0c\u5979\u5bf9\u90a3\u8499\u9762\u4eba\u7684\u6050\u60e7\uff0c\u5df2\u8fdc\u6bd4\u5979\u5bf9\u4efb\u4f55\u4e8b\u7684\u6050\u60e7\u90fd\u6df1\u3002 \u3000\u3000\u5979\u6b7b\u4e5f\u4e0d\u613f\u7559\u5728\u8fd9\u91cc\u3002 \u3000\u3000\u90a3\u8499\u9762\u4eba\u662f\u8c01\uff1f\u4e3a\u4ec0\u4e48\u8981\u903c\u7740\u5979\u5230\u8fd9\u91cc\u6765\u5439\u7bab\uff1f \u3000\u3000\u96be\u9053\u4ed6\u5df2\u77e5\u9053\u53f6\u5f00\u5230\u8fd9\u91cc\u6765\u627e\u7389\u7bab\uff0c\u6240\u4ee5\u7279\u5730\u7528\u8fd9\u6cd5\u5b50\u6765\u6307\u70b9\u53f6\u5f00\u4e00\u6761\u660e\u8def\u3002 \u3000\u3000\u4ed6\u4e3a\u4ec0\u4e48\u8981\u8fd9\u6837\u505a\uff1f\u4ed6\u662f\u4e0d\u662f\u53e6\u6709\u76ee\u7684\uff1f \u3000\u3000\u8fd9\u4e9b\u95ee\u9898\uff0c\u53f6\u5f00\u5f53\u7136\u90fd\u4e0d\u80fd\u89e3\u91ca\uff0c\u4ed6\u5fcd\u4e0d\u4f4f\u53c8\u95ee\uff1a\u201c\u90a3\u8499\u9762\u4eba\u7a76\u7adf\u662f\u4e2a\u4ec0\u4e48\u6837\u7684\u4eba\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4e0d\u662f\u4eba\uff0c\u7b80\u76f4\u662f\u4e2a\u9b3c\uff0c\u6076\u9b3c\u3002\u201d\u60f3\u8d77\u4e86\u8fd9\u4e2a\u4eba\uff0c\u5979\u7684\u8eab\u5b50\u53c8\u5f00\u59cb\u53d1\u6296\u3002 \u3000\u3000\u663e\u7136\u8fd9\u4e2a\u4eba\u4e00\u51fa\u624b\u5c31\u5236\u4f4f\u4e86\u5979\uff0c\u5979\u5df2\u5b8c\u5168\u6ca1\u6709\u62b5\u6297\u7684\u80fd\u529b\u3002 \u3000\u3000\u53ef\u662f\u4e1c\u6d77\u7389\u7bab\u7684\u5973\u5f1f\u5b50\uff0c\u6b66\u529f\u4e5f\u7edd\u4e0d\u4f1a\u592a\u5dee\u3002 \u3000\u3000\u53f6\u5f00\u770b\u7740\u90ed\u5b9a\uff0c\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u4e0d\u9519\uff0c\u73b0\u5728\u867d\u4e0d\u662f\u4e5d\u6708\uff0c\u4f46\u5374\u5df2\u6709\u7fa4\u9e70\u98de \u8d77\uff0c\u800c\u4e14\u5168\u90fd\u98de\u5230\u4e86\u8fd9\u91cc\u3002\u201d \u3000\u3000\u88ab\u8925\u8fd8\u662f\u51cc\u4e71\u7684\uff0c\u6795\u4e0a\u4e5f\u8bb8\u8fd8\u6709\u7740\u4e01\u7075\u7433\u7684\u53d1\u4e1d\u3002 \u3000\u3000\u4e00\u56de\u5230\u8fd9\u91cc\uff0c\u53f6\u5f00\u7684\u5fc3\u5c31\u5f00\u59cb\u9690\u9690\u53d1\u75db\u2014\u2014\u5979\u73b0\u5728\u600e\u4e48\u6837\u4e86\uff0c\u4e1c\u6d77\u7389\u7bab\u4f1a\u4e0d\u4f1a\u2026 \u3000\u3000\u2026 \u3000\u3000\u53f6\u5f00\u8fde\u60f3\u90fd\u4e0d\u6562\u60f3\u3002\u90ed\u5b9a\u770b\u7740\u5e8a\u4e0a\u51cc\u4e71\u7684\u88ab\u8925\uff0c\u773c\u91cc\u53c8\u9732\u51fa\u79cd\u5947\u602a\u7684\u8868\u60c5\u3002 \u3000\u3000\u4ed6\u6ca1\u6709\u518d\u770b\u7b2c\u4e8c\u773c\uff0c\u4ed6\u7684\u5fc3\u4eff\u4f5b\u4e5f\u5728\u9690\u9690\u53d1\u75db\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u603b\u7b97\u5df2\u5b8c\u5168\u660e\u767d\u4e86\u53f6\u5f00\u548c\u4e01\u7075\u7433\u7684\u5173\u7cfb\u3002 \u3000\u3000\u97e9\u8d1e\u5df2\u88ab\u653e\u5230\u5e8a\u4e0a\uff0c\u7761\u5f97\u4ecd\u5f88\u6c89\u3002\u7761\u89c9\u5b9e\u5728\u662f\u4e2a\u5f88\u5947\u602a\u7684\u4e8b\u60c5\u3002 \u3000\u3000\u90a3\u5973\u9053\u4eba\u4f4e\u5782\u7740\u5934\uff0c\u7ad9\u5728\u5c4b\u89d2\uff0c\u82cd\u81ea\u7684\u8138\u4e0a\uff0c\u603b\u7b97\u5df2\u6709\u4e86\u4e9b\u8840\u8272\u3002 \u3000\u3000\u4e1c\u6d77\u7389\u7bab\u7684\u5973\u5f1f\u5b50\u90fd\u5f88\u7f8e\uff0c\u5979\u5c24\u5176\u7f8e\u3002 \u3000\u3000\u5979\u7f8e\u5f97\u548c\u4e01\u7075\u7433\u4e0d\u540c\uff0c\u4e0d\u4f46\u7f8e\uff0c\u800c\u4e14\u5a9a\uff0c\u5979\u5df2\u662f\u4e2a\u5b8c\u5168\u6210\u719f\u7684\u5973\u4eba\u3002 \u3000\u3000\u65e0\u8bba\u8c01\u770b\u89c1\u5979\u9ec4\u660f\u65f6\u5728\u8427\u58f0\u4e2d\u6b3e\u6446\u8170\u80a2\u3001\u5a9a\u773c\u5982\u4e1d\u7684\u795e\u60c5\u90fd\u96be\u514d\u4f1a\u5fc3\u52a8\u7684\u3002 \u3000\u3000\u53f6\u5f00\u770b\u4e86\u5979\u4e00\u773c\u9053\uff1a\u201c\u5750\u3002\u201d \u3000\u3000\u5973\u9053\u4eba\u6162\u6162\u5730\u6447\u4e86\u6447\u5934\uff0c\u5ffd\u7136\u9053\uff1a\u201c\u73b0\u5728\u6211\u53ef\u4e0d\u53ef\u4ee5\u56de\u53bb\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4e0d\u53ef\u4ee5\u3002\u201d \u3000\u3000\u5973\u9053\u4eba\u5782\u4e0b\u5934\uff0c\u54ac\u7740\u5634\u5507\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u82e5\u60f3\u5229\u7528\u6211\u6765\u631f\u80c1\u7389\u7bab\u9053\u4eba\uff0c\u4f60\u4eec\u5c31\u9519\u4e86\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u5973\u9053\u4eba\u9053\uff1a\u201c\u4f60\u4eec\u5c31\u7b97\u5f53\u7740\u4ed6\u7684\u9762\u524d\u6740\u4e86\u6211\uff0c\u4ed6\u4e5f\u4e0d\u4f1a\u5173\u5fc3\u7684\u3002\u201d \u3000\u3000\u5979\u7709\u773c\u4eff\u4f5b\u5e26\u7740\u79cd\u5e7d\u6028\u4e4b\u8272\uff0c\u8f7b\u8f7b\u5730\u63a5\u7740\u9053\uff1a\u201c\u6211\u4ece\u6765\u4e5f\u6ca1\u6709\u770b\u89c1\u4ed6\u5173\u5fc3\u8fc7\u4efb\u4f55\u4eba\u3002\u201d \u3000\u3000\u90ed\u5b9a\u51dd\u89c6\u7740\u5979\uff0c\u5ffd\u7136\u9053\uff1a\u201c\u6211\u4eec\u82e5\u5728\u4f60\u9762\u524d\u6740\u4e86\u4ed6\u5462\uff1f\u201d \u3000\u3000\u5973\u9053\u4eba\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u4f1a\u6389\u4e00\u6ef4\u773c\u6cea\u3002\u201d \u3000\u3000\u5979\u8bf4\u5f97\u5f88\u5e72\u8106\uff0c\u8fde\u8003\u8651\u90fd\u6ca1\u6709\u8003\u8651\u3002 \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u4e3a\u4ec0\u4e48\u8981\u56de\u53bb\uff1f\u201d \u3000\u3000\u5973\u9053\u4eba\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u2026\u2026\u6211\u2026\u2026\u201d \u3000\u3000\u53f6\u5f00\u660e\u767d\u5979\u7684\u610f\u601d\u3002 \u3000\u3000\u5979\u4e00\u5b9a\u8981\u56de\u53bb\uff0c\u53ea\u56e0\u6839\u672c\u6ca1\u6709\u522b\u7684\u5730\u65b9\u53ef\u53bb\u3002 \u3000\u3000\u53f6\u5f00\u5e76\u4e0d\u662f\u4e2a\u5fc3\u80a0\u5f88\u786c\u7684\u4eba\uff0c\u5ffd\u7136\u95ee\uff1a\u201c\u8d35\u59d3\uff1f\u201d \u3000\u3000\u201c\u6211\u59d3\u5d14\u3002\u201d \u3000\u3000\u201c\u5d14\uff1f\u201d \u3000\u3000\u201c\u5d14\u2026\u2026\u5d14\u7389\u771f\u3002\u201d \u3000\u3000\u53f6\u5f00\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u5750\u4e0b\u6765\uff0c\u96be\u9053\u6015\u8fd9\u6905\u5b50\u4f1a\u54ac\u4eba\uff1f\u201d \u3000\u3000\u5d14\u7389\u771f\u4e5f\u5fcd\u4e0d\u4f4f\u7b11\u4e86\uff0c\u5979\u53d1\u73b0\u81ea\u5df1\u5728\u7b11\u7684\u65f6\u5019\uff0c\u7f8e\u4e3d\u7684\u8138\u4e0a\u7acb\u523b\u9732\u51fa\u7ea2\u971e\u3002 \u3000\u3000\u53f6\u5f00\u770b\u89c1\u5979\u968f\u7740\u7bab\u58f0\u626d\u52a8\u8170\u80a2\u7684\u65f6\u5019\uff0c\u672c\u4ee5\u4e3a\u5979\u662f\u4e2a\u5fd8\u8bb0\u4e86\u7f9e\u803b\u7684\u5973\u4eba\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u624d\u53d1\u73b0\u5979\u8fd8\u662f\u4fdd\u7559\u7740\u4e00\u4efd\u5c11\u5973\u7684\u5a07\u7f9e\u548c\u7eaf\u771f\u3002 \u3000\u3000\u53ea\u4e0d\u8fc7\uff0c\u65e0\u8bba\u8c01\u5728\u4e0d\u5f97\u5df2\u7684\u65f6\u5019\uff0c\u90fd\u96be\u514d\u4f1a\u4f5c\u51fa\u4e00\u4e9b\u4ee4\u522b\u4eba\u89c9\u5f97\u53ef\u803b\u3001\u81ea\u5df1\u4e5f\u4f1a\u540e\u6094\u7684 \u4e8b\u3002 \u3000\u3000\u6709\u65f6\u4eba\u5c31\u50cf\u662f\u4e00\u53ea\u88ab\u8499\u7740\u773c\u775b\u63a8\u78e8\u7684\u9a74\u5b50\uff0c\u751f\u6d3b\u5c31\u50cf\u662f\u4e00\u6761\u97ad\u5b50\u3002 \u3000\u3000\u5f53\u97ad\u5b50\u62bd\u5230\u4f60\u80cc\u4e0a\u65f6\uff0c\u4f60\u53ea\u6709\u5f80\u524d\u8d70\uff0c\u867d\u7136\u8fde\u4f60\u81ea\u5df1\u4e5f\u4e0d\u77e5\u9053\u8981\u8d70\u5230\u4ec0\u4e48\u65f6\u5019\u4e3a\u6b62\u3002 \u3000\u3000\u53f6\u5f00\u8f7b\u8f7b\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u82e5\u4e0d\u613f\u56de\u53bb\uff0c\u5c31\u53ef\u4ee5\u4e0d\u5fc5\u56de\u53bb\u3002\u201d \u3000\u3000\u5d14\u7389\u771f\u53c8\u5782\u4e0b\u5934\uff1a\u201c\u53ef\u662f\u6211\u2026\u2026\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u660e\u767d\u4f60\u7684\u610f\u601d\uff0c\u53ef\u662f\u8fd9\u4e16\u754c\u5f88\u5927\uff0c\u4f60\u6162\u6162\u5c31\u4f1a\u53d1\u73b0\u6709\u5f88\u591a\u5730\u65b9\u90fd\u53ef\u4ee5\u53bb \u7684\u3002\u201d \u3000\u3000\u5d14\u7389\u771f\u4e5f\u660e\u767d\u4e86\u4ed6\u7684\u610f\u601d\uff0c\u5979\u5fcd\u4e0d\u4f4f\u62ac\u8d77\u5934\u770b\u4e86\u4ed6\u4e00\u773c\uff0c\u773c\u775b\u91cc\u5145\u6ee1\u4e86\u611f\u6fc0\u3002 \u3000\u3000\u53f6\u5f00\u9053\uff1a\u2018\u4f60\u4e5f\u4e0d\u5fc5\u5e2e\u6211\u4eec\u53bb\u627e\u4e01\u59d1\u5a18\uff0c\u53ea\u8981\u544a\u8bc9\u6211\u4eec\u5979\u5728\u54ea\u91cc\u5c31\u884c\u4e86\u3002\u201d\u5d14\u7389\u771f\u8fdf\u7591 \u7740\uff0c\u7ec8\u4e8e\u9053\uff1a\u201c\u5c31\u5728\u540e\u9762\u7684\u90a3\u4e2a\u9662\u5b50\u91cc\u3002\u201d \u3000\u3000\u53f6\u5f00\u7b49\u7740\u5979\u8bf4\u4e0b\u53bb\u3002 \u3000\u3000\u5d14\u7389\u771f\u9053\uff1a\u201c\u90a3\u4e2a\u9662\u5b50\u5f88\u5927\uff0c\u4e00\u5171\u597d\u50cf\u6709\u5341\u4e09\u56db\u95f4\u623f\uff0c\u4e01\u59d1\u5a18\u5c31\u88ab\u9501\u5728\u6700\u540e\u9762\u7684\u4e00\u95f4\u504f \u623f\u91cc\uff0c\u7a97\u53f0\u7684\u5916\u9762\u6446\u7740\u4e09\u76c6\u814a\u6885\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6709\u6ca1\u6709\u4eba\u5728\u90a3\u91cc\u770b\u5b88\u5979\uff1f\u201d \u3000\u3000\u5d14\u7389\u771f\u9053\uff1a\u201c\u53ea\u6709\u4e00\u4e2a\u4eba\u5728\u91cc\u9762\u966a\u5979\uff0c\u56e0\u4e3a\u5979\u8fd8\u4e0d\u80fd\u8d70\u52a8\uff0c\u7389\u7bab\u9053\u4eba\u4e5f\u4e0d\u6015\u5979\u4f1a\u8dd1\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u7389\u7bab\u9053\u4eba\u7761\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u5d14\u7389\u771f\u9053\uff1a\u201c\u4ed6\u665a\u4e0a\u5f88\u5c11\u7761\u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4e0d\u7761\u5728\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5d14\u7389\u771f\u54ac\u7d27\u7259\uff0c\u6ca1\u6709\u56de\u7b54\uff0c\u4f46\u8138\u4e0a\u53c8\u9732\u51fa\u90a3\u79cd\u60b2\u6124\u5e7d\u6028\u4e4b\u8272\u3002 \u3000\u3000\u5979\u4e0d\u5fc5\u518d\u8bf4\u4e86\u3002 \u3000\u3000\u201c\u7389\u7bab\u597d\u8272\u201d\uff0c\u4ed6\u73b0\u5728\u5e94\u8be5\u5df2\u6709\u4e03\u5341\u5c81\uff0c\u770b\u8d77\u6765\u5374\u8fdc\u6bd4\u5b9e\u9645\u7684\u5e74\u7eaa\u8f7b\u3002 \u3000\u3000\u4ed6\u6709\u5f88\u591a\u7f8e\u4e3d\u800c\u5e74\u8f7b\u7684\u5973\u5f1f\u5b50\u3002 \u3000\u3000\u4ed6\u665a\u4e0a\u5728\u5e72\u4ec0\u4e48\uff0c\u53f6\u5f00\u5f53\u7136\u5df2\u53ef\u731c\u5f97\u51fa\u6765\u3002 \u3000\u3000\u90ed\u5b9a\u9762\u4e0a\u5df2\u73b0\u51fa\u6012\u5bb9\uff0c\u5ffd\u7136\u9053\uff1a\u201c\u4f60\u4eec\u662f\u4e0d\u662f\u88ab\u4ed6\u6240\u903c\uff0c\u624d\u8ddf\u7740\u4ed6\u7684\uff1f\u201d \u3000\u3000\u5d14\u7389\u771f\u6447\u6447\u5934\uff0c\u6005\u7136\u9053\uff1a\u201c\u6211\u4eec\u672c\u6765\u90fd\u662f\u8d2b\u82e6\u4eba\u5bb6\u7684\u5b50\u5973\u3002\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u4f60\u4eec\u90fd\u662f\u88ab\u4ed6\u6765\u4e70\u6765\u7684\uff1f\u201d \u3000\u3000\u5d14\u7389\u771f\u5934\u5782\u5f97\u66f4\u4f4e\uff0c\u773c\u6cea\u5df2\u6d41\u4e0b\u9762\u988a\u3002 \u3000\u3000\u90ed\u5b9a\u7a81\u7136\u7528\u529b\u4e00\u62cd\u684c\u5b50\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u5c31\u7b97\u6ca1\u6709\u4e01\u59d1\u5a18\u8fd9\u4ef6\u4e8b\uff0c\u6211\u4e5f\u7edd\u4e0d\u4f1a\u653e\u8fc7\u4ed6\u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u53ef\u662f\u73b0\u5728\u2026\u2026\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u6211\u77e5\u9053\uff0c\u73b0\u5728\u6211\u4eec\u5f53\u7136\u8981\u5148\u6551\u51fa\u4e01\u59d1\u5a18\u518d\u8bf4\u3002\u201d \u3000\u3000\u5d14\u7389\u771f\u5ffd\u7136\u53c8\u9053\uff1a\u201c\u4ed6\u665a\u4e0a\u867d\u7136\u4e0d\u7761\uff0c\u53ef\u662f\u5230\u4e86\u5929\u5feb\u4eae\u7684\u65f6\u5019\uff0c\u4e00\u5b9a\u8981\u7761\u4e09\u4e2a\u65f6\u8fb0\u3002\u201d \u3000\u3000\u73b0\u5728\u8ddd\u79bb\u5929\u4eae\u81f3\u5c11\u8fd8\u6709\u534a\u4e2a\u591a\u65f6\u8fb0\uff0c\u51ac\u5929\u7684\u591c\u603b\u662f\u6bd4\u8f83\u957f\u3002 \u3000\u3000\u53f6\u5f00\u770b\u4e86\u770b\u5929\u8272\u9053\uff1a\u201c\u597d\uff0c\u6211\u4eec\u7b49\u3002\u201d \u3000\u3000\u5e8a\u4e0a\u97e9\u8d1e\u5ffd\u7136\u7ffb\u4e86\u4e2a\u8eab\uff0c\u53d1\u51fa\u68a6\u5403\u2014\u2014\u53f6\u5f00\u70b9\u4e86\u4ed6\u7a74\u9053\uff0c\u7528\u7684\u529b\u91cf\u5e76\u4e0d\u5927\u3002 \u3000\u3000\u4ed6\u4eff\u4f5b\u8fd8\u5728\u8bf4\uff1a\u201c\u9152\u5462\u2026\u2026\u4ec0\u4e48\u5730\u65b9\u6709\u9152\u2026\u2026\u201d \u3000\u3000\u53cd\u53cd\u590d\u590d\u8bf4\u4e86\u51e0\u904d\u540e\uff0c\u4ed6\u7a81\u7136\u4ece\u5e8a\u4e0a\u8df3\u8d77\u6765\uff0c\u5927\u53eb\u9053\uff1a\u201c\u59d3\u5415\u7684\u6211\u8ba4\u5f97\u4f60\uff0c\u4f60\u597d\u72e0\u3002\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u8bf4\u5b8c\uff0c\u4ed6\u53c8\u5012\u4e0b\uff0c\u6ee1\u5934\u90fd\u662f\u51b7\u6c57\u3002 \u3000\u3000\u53f6\u5f00\u52a8\u5bb9\u9053\uff1a\u201c\u59d3\u5415\u7684\uff1f\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u770b\u6765\u6253\u4f24\u4ed6\u7684\u90a3\u4e2a\u4eba\u4e00\u5b9a\u59d3\u5415\u3002\u201d \u3000\u3000\u53f6\u5f00\u6c89\u601d\u7740\uff0c\u9053\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u6c5f\u6e56\u4e2d\u6709\u4ec0\u4e48\u59d3\u5415\u7684\u9ad8\u624b\uff1f\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u8fd1\u5e74\u6765\u597d\u50cf\u53ea\u6709\u4e00\u4e2a\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u5415\u8fea\uff1f\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u2018\u767d\u8863\u5251\u5ba2\u2019\u5415\u8fea\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u89c1\u8fc7\u4ed6\u51fa\u624b\uff1f\u201d \u3000\u3000\u90ed\u5b9a\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u6211\u53ea\u77e5\u9053\u4ed6\u867d\u7136\u662f\u2018\u94f6\u621f\u6e29\u4faf\u2019\u5415\u98ce\u5148\u7684\u5802\u4f84\u3002\u7ec3\u7684\u5374\u662f\u6b66\u5f53\u5251 \u6cd5\uff0c\u6b66\u5f53\u662f\u5185\u5bb6\u6b63\u5b97\uff0c\u7edd\u4e0d\u4f1a\u2026\u2026\u201d \u3000\u3000\u53f6\u5f00\u7a81\u7136\u6253\u65ad\u4e86\u4ed6\u7684\u8bdd\uff0c\u9053\uff1a\u201c\u4f60\u8bf4\u4ed6\u662f\u8c01\u7684\u4f84\u5b50\uff1f\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u5415\u51e4\u5148\u94f6\u621f\u6e29\u4faf\uff0c\u6614\u5e74\u5175\u5668\u8c31\u4e0a\u6392\u540d\u7b2c\u4e94\u3002\u201d\u53f6\u5f00\u7684\u773c\u775b\u91cc\u7a81\u7136\u53d1\u51fa\u4e86\u5149\uff0c \u9053\uff1a\u201c\u5415\u51e4\u5148\uff0c\u6211\u600e\u4f1a\u5fd8\u4e86\u8fd9\u4e2a\u4eba\u3002\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u4f60\u8ba4\u4e3a\u662f\u4ed6\u4e48\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u94f6\u4e7e\u6e29\u4faf\u5728\u5175\u5668\u8c31\u4e0a\u6392\u540d\u7b2c\u4e94\uff0c\u5728\u522b\u4eba\u5df2\u662f\u4ef6\u5f88\u503c\u5f97\u8363\u8000\u7684\u4e8b\uff0c\u53ef\u662f\u5728\u4ed6\u770b \u6765\uff0c\u5374\u662f\u79cd\u803b\u8fb1\u3002\u201d \u3000\u3000\u90ed\u5b9a\u4e86\u89e3\u8fd9\u79cd\u5fc3\u60c5\uff0c\u6709\u5f88\u591a\u4eba\u90fd\u4e0d\u80fd\u5fcd\u53d7\u5c48\u5c45\u4eba\u4e0b\u7684\u3002 \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f46\u4ed6\u4e5f\u77e5\u9053\u767e\u6653\u751f\u7edd\u4e0d\u4f1a\u9519\uff0c\u6240\u4ee5\u4ed6\u6bc1\u4e86\u81ea\u5df1\u7684\u94f6\u621f\uff0c\u7ec3\u6210\u4e86\u53e6\u4e00\u79cd\u53ef\u6015\u7684 \u6b66\u529f\u3002\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u4ec0\u4e48\u6b66\u529f\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4ed6\u7684\u624b\uff01\u201d \u3000\u3000\u90ed\u5b9a\u7684\u773c\u775b\u4e5f\u4eae\u4e86\u3002 \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u636e\u8bf4\u4ed6\u5df2\u5c06\u4ed6\u7684\u624b\u7ec3\u6210\u94a2\u94c1\u822c\u575a\u786c\u950b\u5229\u3002\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u4f60\u662f\u542c\u8c01\u8bf4\u7684\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4e00\u4e2a\u66fe\u7ecf\u4eb2\u773c\u770b\u8fc7\u4ed6\u90a3\u53ea\u624b\u7684\u4eba\uff0c\u4e00\u4e2a\u7edd\u4e0d\u4f1a\u770b\u9519\u7684\u4eba\u3002\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u5c0f\u674e\u63a2\u82b1\uff1f\u201d \u3000\u3000\u53f6\u5f00\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4e16\u4e0a\u82e5\u6709\u4e00\u4e2a\u4eba\u80fd\u8d64\u624b\u5c06\u97e9\u8d1e\u6253\u6210\u8fd9\u6837\u5b50\uff0c\u8fd9\u4e2a\u4eba\u5c31\u4e00\u5b9a\u662f\u5415\u51e4 \u5148\u3002\u201d\u90ed\u5b9a\u9053\uff1a\u201c\u53ef\u662f\u4ed6\u591a\u5e74\u524d\u5c31\u5df2\u5931\u8e2a\u4e86\u3002\u201d\u53f6\u5f00\u51b7\u7b11\u9053\uff1a\u201c\u8fde\u6b7b\u4e86\u7684\u4eba\u90fd\u53ef\u80fd\u590d\u6d3b\uff0c\u4f55 \u51b5\u662f\u5931\u8e2a\u4e86\u7684\u4eba\u3002\u201d\u90ed\u5b9a\u9053\uff1a\u201c\u4f60\u8ba4\u4e3a\u4ed6\u4e5f\u5df2\u5230\u4e86\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u8bf4\u8fc7\uff0c\u73b0\u5728\u867d\u4e0d\u662f\u4e5d\u6708\uff0c\u5374\u662f\u730e\u72d0\u7684\u65f6\u5019\u3002\u201d \u3000\u3000\u90ed\u5b9a\u7684\u773c\u775b\u91cc\u95ea\u7740\u5149\u9053\uff1a\u201c\u5415\u51e4\u5148\u65e0\u7591\u4e5f\u662f\u53ea\u9e70\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4e5f\u8bb8\u4ed6\u5df2\u53ef\u7b97\u662f\u7fa4\u9e70\u4e2d\u6700\u53ef\u6015\u7684\u4e00\u53ea\u9e70\u3002\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u4ed6\u82e5\u771f\u7684\u6765\u4e86\uff0c\u4f60\u8981\u627e\u4ed6\uff1f\u201d \u3000\u3000\u53f6\u5f00\u671b\u7740\u5e8a\u4e0a\u7684\u97e9\u8d1e\uff0c\u7d27\u7d27\u95ed\u4f4f\u4e86\u5634\u3002 \u3000\u3000\u4ed6\u5df2\u4e0d\u5fc5\u518d\u5f00\u53e3\u3002 \u3000\u3000\u90ed\u5b9a\u7684\u773c\u775b\u66f4\u4eae\uff0c\u5374\u4eff\u4f5b\u51dd\u89c6\u7740\u8fdc\u65b9\uff0c\u5583\u5583\u9053\uff1a\u201c\u80fd\u4e0e\u6614\u5e74\u5175\u5668\u8c31\u4e0a\u6392\u540d\u7b2c\u4e94\u7684\u4eba\u51b3\u4e00 \u80dc\u8d1f\uff0c\u5012\u4e5f\u662f\u4eba\u751f\u4e00\u5927\u5feb\u4e8b\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f46\u8fd9\u5374\u4e0d\u662f\u4f60\u7684\u4e8b\u3002\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u4e0d\u662f\uff1f\u201d \u3000\u3000\u53f6\u5f00\u7684\u8868\u60c5\u5f88\u4e25\u8083\uff0c\u9053\uff1a\u201c\u7edd\u4e0d\u662f\u3002\u201d \u3000\u3000\u90ed\u5b9a\u7b11\u4e86\u7b11\uff0c\u63a5\u7740\u9053\uff1a\u201c\u4e0d\u5fc5\u6015\u6211\u62a2\u4f60\u7684\u751f\u610f\uff0c\u97e9\u8d1e\u662f\u4f60\u7684\u670b\u53cb\uff0c\u4e0d\u662f\u6211\u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u7ec8\u4e8e\u4e5f\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u53e5\u8bdd\u6211\u5e0c\u671b\u4f60\u6700\u597d\u83ab\u8981\u5fd8\u8bb0\u3002\u201d \u3000\u3000\u90ed\u5b9a\u7684\u8868\u60c5\u4e5f\u53d8\u5f97\u5f88\u4e25\u8083\uff0c\u9053\uff1a\u201c\u4f60\u6700\u597d\u4e5f\u83ab\u8981\u5fd8\u8bb0\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u4ed6\u51dd\u89c6\u7740\u53f6\u5f00\uff0c\u6162\u6162\u5730\u63a5\u7740\u9053\uff1a\u201c\u6211\u4e0d\u60f3\u770b\u89c1\u4f60\u88ab\u4eba\u6253\u5f97\u50cf\u97e9\u8d1e\u8fd9\u6837\u5b50\u3002\u201d \u3000\u3000\u53f6\u5f00\u5ffd\u7136\u8f6c\u8fc7\u8eab\uff0c\u63a8\u5f00\u4e86\u7a97\u6237\u3002 \u3000\u3000\u7a97\u5916\u51b7\u98ce\u5982\u5200\uff0c\u4f46\u4ed6\u7684\u5fc3\u5374\u662f\u70ed\u7684\uff0c\u5c31\u50cf\u662f\u521a\u559d\u4e0b\u6ee1\u6ee1\u4e00\u676f\u9187\u9152\u3002 \u3000\u3000\u8fdc\u65b9\u7684\u7a7a\u8c37\uff0c\u672c\u662f\u4e00\u7247\u9ed1\u6697\uff0c\u6b64\u523b\u5374\u5df2\u521a\u521a\u53d8\u6210\u4e86\u7070\u767d\u8272\u3002 \u3000\u3000\u7136\u540e\u4ed6\u5c31\u542c\u5230\u4e86\u4e00\u58f0\u9e21\u557c\u3002 \u3000\u3000\u201c\u662f\u6700\u540e\u9762\u9760\u5de6\u7684\u4e00\u95f4\u5c4b\u5b50\uff0c\u7a97\u53f0\u5916\u9762\u8fd8\u6446\u7740\u4e09\u76c6\u814a\u6885\u3002\u201d \u7b2c\u5341\u4e09\u7ae0\u3000\u6d77\u5e02\u8703\u697c>> \u53e4\u9f99\u300a\u4e5d\u6708\u9e70\u98de\u300b \u7b2c\u5341\u4e09\u7ae0\u3000\u6d77\u5e02\u8703\u697c \u3000\u3000\u540e\u9762\u7684\u9662\u5b50\u679c\u7136\u5f88\u5927\uff0c\u4e1c\u65b9\u867d\u5df2\u73b0\u51fa\u66d9\u8272\uff0c\u7a97\u5b50\u5374\u8fd8\u4eae\u7740\u706f\u3002 \u3000\u3000\u5c4b\u91cc\u6709\u4eba\u5728\u5927\u7b11\uff1a\u201c\u8d2b\u9053\u6b64\u756a\u91cd\u5165\u7ea2\u5c18\uff0c\u5c31\u662f\u8981\u770b\u770b\u4eca\u65e5\u4e4b\u6c5f\u6e56\uff0c\u7a76\u7adf\u662f\u8c01\u5bb6\u7684\u5929 \u4e0b\uff1f\u201d\u8fd9\u662f\u7389\u7bab\u9053\u4eba\u7684\u58f0\u97f3\u3002 \u3000\u3000\u5c4b\u5b50\u91cc\u5c45\u7136\u8fd8\u6709\u53e6\u5916\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u201c\u665a\u8f88\u5f53\u7136\u4e0d\u6562\u548c\u9053\u957f\u4e89\u4e00\u65e5\u4e4b\u77ed\u957f\uff0c\u53ea\u53ef\u60dc\u6c5f\u6e56\u4e2d\u5374\u504f\u504f\u8fd8\u6709\u4e9b\u4e0d\u77e5\u5929\u9ad8\u5730\u539a\u7684\u65e0\u77e5 \u5c0f\u8f88\u3002\u201d \u3000\u3000\u8fd9\u4e0d\u662f\u7389\u7bab\u9053\u4eba\u7684\u58f0\u97f3\uff0c\u542c\u6765\u5374\u5f88\u719f\u3002 \u3000\u3000\u4f0a\u591c\u54ed\u3002 \u3000\u3000\u4ed6\u679c\u7136\u662f\u4e2a\u5f88\u4f1a\u6295\u673a\u53d6\u5de7\u7684\u8bcc\u5a9a\u5c0f\u4eba\u3002 \u3000\u3000\u770b\u6765\u4ed6\u7adf\u5df2\u6295\u9760\u4e86\u7389\u7bab\u9053\u4eba\u3002 \u3000\u3000\u53f6\u5f00\u7684\u5fc3\u6c89\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u7389\u7bab\u9053\u4eba\u4e0d\u4f46\u6ca1\u6709\u7761\uff0c\u800c\u4e14\u8fd8\u591a\u4e86\u4e2a\u5e2e\u624b\u3002\u53ea\u542c\u7389\u7bab\u9053\u4eba\u5728\u95ee\uff1a\u201c\u4f60\u77e5\u9053\u8fd9\u79cd\u65e0\u77e5\u7684\u5c0f \u8f88\u6709\u4e9b\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u201c\u5d69\u9633\u90ed\u5b9a\u3001\u6b66\u5f53\u5415\u8fea\u3001\u9525\u5b50\u97e9\u8d1e\u3001\u98de\u72d0\u6768\u5929\u3001\u5357\u6d77\u73cd\u73e0\u3001\u9752\u57ce\u58a8\u6c0f\u2026\u2026\u636e\u6211\u6240\u77e5\u9053\u7684 \u5df2\u6709\u8fd9\u4e9b\u4eba\u5230\u957f\u5b89\u6765\u4e86\u3002\u201d \u3000\u3000\u4ed6\u663e\u7136\u8fd8\u6ca1\u6709\u5fd8\u8bb0\u5175\u5668\u88ab\u6bc1\u7684\u4ec7\u6068\uff0c\u7b2c\u4e00\u4e2a\u63d0\u5230\u7684\u540d\u5b57\u5c31\u662f\u90ed\u5b9a\u3002 \u3000\u3000\u4ed6\u5b9e\u5728\u5f88\u5e0c\u671b\u770b\u7740\u738b\u7bab\u9053\u4eba\u6740\u4e86\u90ed\u5b9a\u3002 \u3000\u3000\u7389\u7bab\u9053\u4eba\u95f4\uff1a\u201c\u8fd8\u6709\u6ca1\u6709\u522b\u4eba\u8981\u6765\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u6709\uff0c\u81f3\u5c11\u8fd8\u6709\u4e2a\u53f6\u5f00\u3002\u201d\u4f0a\u591c\u54ed\u51b7\u7b11\u9053\uff1a\u201c\u53f6\u5f00\u4e0d\u8db3\u60e7\u3002\u201d \u3000\u3000\u201c\u54e6\uff1f\u201d\u7389\u7bab\u9053\u4eba\u663e\u5f97\u5f88\u60ca\u8bb6\uff0c\u53f6\u5f00\u7684\u6b66\u529f\uff0c\u4ed6\u5df2\u9886\u6559\u8fc7\uff0c\u201c\u56e0\u4e3a\u8fd9\u4e2a\u4eba\u5df2\u7b49\u4e8e\u662f\u4e2a\u6b7b \u4eba\u3002\u201d \u3000\u3000\u201c\u54e6\uff1f\u201d \u3000\u3000\u201c\u73b0\u5728\u957f\u5b89\u57ce\u91cc\uff0c\u8981\u6740\u4ed6\u7684\u4e5f\u4e0d\u77e5\u9053\u6709\u591a\u5c11\uff0c\u4ed6\u7b80\u76f4\u5df2\u6b7b\u5b9a\u4e86\u3002\u201d \u3000\u3000\u7389\u7bab\u9053\u4eba\u5927\u7b11\u9053\uff1a\u201c\u7389\u5bb9\uff0c\u8fd8\u4e0d\u4e3a\u4f0a\u5148\u751f\u659f\u9152\u3002\u201d \u3000\u3000\u770b\u6765\u4ed6\u7adf\u6253\u7b97\u4f5c\u957f\u591c\u4e4b\u996e\uff0c\u8fde\u4e00\u70b9\u7761\u89c9\u7684\u610f\u601d\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u4f46\u53f6\u5f00\u73b0\u5728\u5374\u53ea\u5269\u4e0b\u4e24\u4e2a\u65f6\u8fb0\uff0c\u6b64\u523b\u82e5\u4e0d\u51fa\u624b\uff0c\u4ee5\u540e\u7684\u673a\u4f1a\u66f4\u5c11\uff0c\u90ed\u5b9a\u9644\u5728\u4ed6\u8033\u8fb9\uff0c\u6162 \u6162\u9053\uff1a\u201c\u6211\u5728\u8fd9\u91cc\u7275\u5236\u4f4f\u4ed6\u4eec\uff0c\u4f60\u53bb\u6551\u4eba\u3002\u201d\u53f6\u5f00\u575a\u51b3\u6447\u5934\uff1a\u201c\u4e0d\u884c\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u4e0d\u884c\uff1f\u201d \u3000\u3000\u53f6\u5f00\u51b7\u51b7\u9053\uff1a\u201c\u6211\u4e0d\u60f3\u66ff\u4f60\u6536\u5c38\u3002\u201d\u4ed6\u7684\u58f0\u97f3\u867d\u51b7\uff0c\u4f46\u8fd9\u79cd\u60c5\u611f\u5374\u8fdc\u6bd4\u9187\u9152\u66f4\u80fd\u4ee4\u4eba\u53d1 \u70ed\u3002 \u3000\u3000\u90ed\u5b9a\u89e3\u5f00\u4e86\u8863\u895f\uff0c\u4e5f\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u96be\u9053\u60f3\u6536\u4e01\u7075\u7433\u7684\u5c38\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u80b2\u6cd5\u5b50\uff0c\u4e00\u5b9a\u6709\u6cd5\u5b50\u7684\u2026\u2026\u201d \u3000\u3000\u5176\u5b9e\u4ed6\u4e00\u70b9\u6cd5\u5b50\u4e5f\u6ca1\u6709\uff0c\u4ed6\u7684\u5fc3\u53c8\u4e71\u4e86\uff0c\u4e3a\u4e86\u4e01\u7075\u7433\u7684\u5b89\u5168\uff0c\u4ed6\u7edd\u4e0d\u80fd\u5192\u4e00\u70b9\u9669\u3002 \u3000\u3000\u90ed\u5b9a\u77e5\u9053\uff0c\u4ed6\u5df2\u51c6\u5907\u51b2\u8fdb\u53bb\uff0c\u4ed6\u5e76\u4e0d\u662f\u4e2a\u5f88\u51b7\u9759\u7684\u4eba\u3002 \u3000\u3000\u4ed6\u8ba4\u4e3a\u53ea\u8981\u81ea\u5df1\u4e00\u51b2\u8fdb\u53bb\uff0c\u53f6\u5f00\u5c31\u53ea\u597d\u5230\u540e\u9762\u53bb\u6551\u4eba\u7684\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u9519\u4e86\u3002 \u3000\u3000\u4ed6\u82e5\u51b2\u8fdb\u53bb\uff0c\u53f6\u5f00\u7edd\u4e0d\u4f1a\u629b\u4e0b\u4ed6\uff0c\u4ed6\u4eec\u867d\u7136\u53ef\u4ee5\u5bf9\u4ed8\u4f0a\u591c\u54ed\u548c\u7389\u7bab\u9053\u4eba\uff0c\u53ef\u662f\u4e01\u7075\u7433\u8fd8 \u5728\u7389\u7bab\u9053\u4eba\u624b\u91cc\u3002 \u3000\u3000\u7389\u7bab\u9053\u5165\u82e5\u7528\u4e01\u7075\u7433\u6765\u8981\u631f\u53f6\u5f00\uff0c\u53f6\u5f00\u5c31\u975e\u6b7b\u4e0d\u53ef\u3002 \u3000\u3000\u4ed6\u7684\u8eab\u5b50\u5df2\u817e\u8d77\u2014\u2014 \u3000\u3000\u7a81\u7136\u95f4\uff0c\u7a97\u5b50\u91cc\u4e00\u58f0\u60ca\u547c\uff0c\u662f\u4f0a\u591c\u54ed\u7684\u60ca\u547c\u58f0\u3002 \u3000\u3000\u201c\u4f60\u2026\u2026\u4f60\u8fd9\u662f\u4e8e\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u738b\u7bab\u9053\u4eba\u7684\u58f0\u97f3\u51b0\u51b7\uff1a\u201c\u6211\u8981\u6740\u4e86\u4f60\u3002\u201d \u3000\u3000\u201c\u6211\u597d\u610f\u524d\u6765\uff0c\u4f60\u7adf\u8981\u6740\u6211\uff1f\u201d \u3000\u3000\u7389\u7bab\u9053\u4eba\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u5c06\u6211\u770b\u6210\u4ec0\u4e48\u4eba\uff1f\u7adf\u60f3\u6765\u5229\u7528\u6211\uff0c\u4f60\u624d\u662f\u65e0\u77e5\u7684\u9f20\u8f88\uff0c\u6211\u4e0d\u6740\u4f60 \u6740\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u5c4b\u91cc\u5df2\u54cd\u8d77\u4e86\u4e00\u9635\u684c\u6905\u78b0\u5012\u58f0\u3001\u676f\u76d8\u8dcc\u788e\u58f0\u2014\u2014\u90ed\u5b9a\u7684\u8eab\u5b50\u867d\u5df2\u8df3\u8d77\uff0c\u5374\u5df2\u53d8\u4e86\u65b9\u5411\uff0c \u8d34\u7740\u5899\u7a9c\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u53f6\u5f00\u4e5f\u6ca1\u6709\u843d\u540e\u3002 \u3000\u3000\u4ed6\u4eec\u90fd\u5df2\u770b\u51fa\uff0c\u73b0\u5728\u6b63\u662f\u6551\u4eba\u7684\u597d\u673a\u4f1a\uff0c\u4f0a\u591c\u54ed\u6700\u5c11\u53ef\u4ee5\u62b5\u6321\u7389\u7bab\u9053\u4eba\u4e8c\u4e09\u5341\u62db\u3002 \u3000\u3000\u8fd9\u65f6\u95f4\u867d\u7136\u4e0d\u957f\uff0c\u4f46\u53ea\u8981\u4ed6\u4eec\u7684\u884c\u52a8\u591f\u5feb\uff0c\u5c31\u5df2\u8db3\u591f\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u4eec\u5df2\u8fde\u4e00\u5239\u90a3\u90fd\u803d\u8bef\u4e0d\u5f97\u3002 \u3000\u3000\u5e78\u597d\u7a97\u53f0\u4e0a\u6446\u7740\u814a\u6885\uff0c\u662f\u4e2a\u5f88\u660e\u663e\u7684\u6807\u5fd7\uff0c\u4ed6\u4eec\u8fde\u627e\u90fd\u4e0d\u5fc5\u627e\u3002 \u3000\u3000\u7a97\u5b50\u91cc\u4e5f\u4eae\u7740\u706f\u3002 \u3000\u3000\u7a97\u4e0a\u6709\u4e24\u6761\u4eba\u5f71\uff0c\u4e00\u4e2a\u662f\u68b3\u7740\u9053\u9afb\u7684\u5973\u9053\u4eba\uff0c\u4e00\u4e2a\u6b63\u662f\u4e01\u7075\u7433\u3002 \u3000\u3000\u770b\u5979\u4eec\u7684\u59ff\u6001\uff0c\u4eff\u4f5b\u6b63\u5728\u5bf9\u5750\u7740\u4e0b\u68cb\u3002 \u3000\u3000\u90ed\u5b9a\u5df2\u649e\u7834\u7a97\u6237\uff0c\u51b2\u4e86\u8fdb\u53bb\uff0c\u4ed6\u65e0\u8bba\u505a\u4ec0\u4e48\u4e8b\u90fd\u5e72\u8106\u5f97\u5f88\u3002 \u3000\u3000\u53f6\u5f00\u7684\u5fc3\u5374\u6c89\u4e86\u4e0b\u53bb\uff0c\u4ed6\u77e5\u9053\u91cc\u9762\u7684\u90a3\u4eba\u5f71\u7edd\u4e0d\u662f\u4e01\u7075\u7433\u3002 \u3000\u3000\u4e01\u7075\u7433\u7edd\u4e0d\u4f1a\u4e0b\u68cb\u7684\uff0c\u5979\u7684\u5927\u54e5\u4e01\u7075\u9e64\u867d\u7136\u662f\u6b64\u9053\u7684\u9ad8\u624b\uff0c\u5979\u5374\u8fde\u5b50\u90fd\u4e0d\u4f1a\u6446\u3002 \u3000\u3000\u5979\u4e00\u5411\u8ba4\u4e3a\u4e24\u4e2a\u4eba\u5750\u5728\u90a3\u91cc\uff0c\u5c06\u4e00\u4e9b\u9ed1\u767d\u7684\u77f3\u5934\u5f80\u4e00\u5757\u6728\u677f\u4e0a\u6446\u6765\u6446\u53bb\uff0c\u662f\u4ef6\u5f88\u65e0\u804a\u7684 \u4e8b\u3002 \u3000\u3000\u201c\u8fd9\u96be\u9053\u53c8\u662f\u4e2a\u9677\u9631\uff1f\u201d \u3000\u3000\u53ef\u662f\u90ed\u5b9a\u65e2\u7136\u5df2\u95ef\u4e86\u8fdb\u53bb\uff0c\u53f6\u5f00\u4e5f\u53ea\u597d\u786c\u7740\u5934\u76ae\u5f80\u4e0b\u8df3\u3002 \u3000\u3000\u4e00\u95ef\u8fdb\u5c4b\u5b50\uff0c\u90ed\u5b9a\u4e5f\u7acb\u523b\u5c31\u53d1\u73b0\u4e01\u7075\u7433\u5e76\u4e0d\u5728\u8fd9\u5c4b\u5b50\u91cc\u3002 \u3000\u3000\u5750\u5728\u5973\u9053\u4eba\u5bf9\u9762\u7684\u8fd9\u5c11\u5973\uff0c\u867d\u7136\u7a7f\u7740\u4e01\u7075\u7433\u7684\u8863\u670d\uff0c\u68b3\u7740\u548c\u4e01\u7075\u7433\u4e00\u6837\u7684\u53d1\u5f0f\uff0c\u5374\u4e0d\u662f \u4e01\u7075\u7433\u3002 \u3000\u3000\u82e5\u662f\u6362\u4e86\u522b\u4eba\uff0c\u4e00\u5b9a\u4f1a\u5403\u60ca\u3001\u53d1\u6014\u3002 \u3000\u3000\u4f46\u90ed\u5b9a\u505a\u4e8b\u5374\u6709\u4ed6\u81ea\u5df1\u72ec\u7279\u7684\u65b9\u5f0f\uff0c\u4ed6\u7684\u624b\u4e00\u7ffb\uff0c\u5251\u5df2\u51fa\u9798\uff0c\u5251\u67c4\u5df2\u6253\u5728\u90a3\u5973\u9053\u5165\u7684\u54bd \u5589\u4e0a\u3002 \u3000\u3000\u5979\u8fde\u60ca\u547c\u90fd\u6ca1\u6709\u53d1\u51fa\uff0c\u5c31\u5df2\u5012\u4e0b\u3002 \u3000\u3000\u53e6\u4e00\u4e2a\u5c11\u5973\u4e5f\u6ca1\u6709\u8fc7\u6765\uff0c\u56e0\u4e3a\u90ed\u5b9a\u7684\u5251\u950b\u5df2\u903c\u4f4f\u5979\u7684\u54bd\u5589\u3002 \u3000\u3000\u201c\u4e01\u59d1\u5a18\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u8fd9\u5c11\u5973\u8138\u8272\u867d\u5df2\u5413\u5f97\u53d1\u9752\uff0c\u4f46\u5374\u6446\u51fa\u5b81\u6b7b\u4e5f\u4e0d\u8bf4\u7684\u795e\u60c5\u3002 \u3000\u3000\u90ed\u5b9a\u4e5f\u6ca1\u6709\u518d\u95ee\uff0c\u5de6\u624b\u5df2\u4f38\u51fa\uff0c\u6293\u4f4f\u4e86\u5979\u7684\u8863\u895f\uff0c\u4e00\u628a\u5c31\u5c06\u5979\u91cc\uff29\uff32\uff17\u91cc\u5916\u5916\u4e94\u4eba\u4ef6\u8863 \u670d\u5168\u90e8\u6495\u6210\u4e86\u4e24\u534a\uff0c\u9732\u51fa\u4e86\u5979\u96ea\u767d\u7684\u8eab\u5b50\uff0c\u9ad8\u8038\u7684\u80f8\u819b\uff0c\u7ea4\u7ec6\u7684\u8170\u3002 \u3000\u3000\u8fd9\u5c11\u5973\u7684\u8138\u4f3c\u5df2\u5413\u5f97\u53d1\u7eff\u3002 \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u4f60\u518d\u4e0d\u8bf4\uff0c\u6211\u5c31\u5c06\u4f60\u6495\u6210\u4e24\u534a\uff01\u201d\u8fd9\u5c11\u5973\u5df2\u5413\u5f97\u8fde\u58f0\u97f3\u90fd\u53d1\u4e0d\u51fa\u6765\uff0c\u53ea\u662f\u6307 \u4e86\u6307\u89d2\u843d\u91cc\u7684\u8863\u67dc\u3002 \u3000\u3000\u8863\u67dc\u5f88\u5927\u3002 \u3000\u3000\u53f6\u5f00\u51b2\u8fc7\u53bb\uff0c\u62c9\u5f00\uff0c\u91cc\u9762\u679c\u7136\u6709\u4e00\u4e2a\u4eba\uff0c\u4e00\u4e2a\u7a7f\u7740\u9053\u88c5\u7684\u5973\u4eba\uff0c\u4f3c\u5df2\u88ab\u70b9\u4e86\u7761\u7a74\uff0c\u5374\u6b63 \u662f\u4e01\u7075\u7433\u3002 \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u5728\u4e0d\u5728\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u5728\uff01\u201d \u3000\u3000\u4e24\u53e5\u8bdd\u4e00\u5171\u53ea\u6709\u56db\u4e2a\u5b57\uff0c\u53f6\u5f00\u5df2\u62b1\u8d77\u4e86\u4e86\u7075\u7433\uff0c\u7a9c\u51fa\u4e86\u7a97\u6237\u3002 \u3000\u3000\u90ed\u5b9a\u8f7b\u8f7b\u62cd\u4e86\u62cd\u8fd9\u5c11\u5973\u5fae\u5fae\u51f8\u8d77\u7684\u5c0f\u8179\uff0c\u5fae\u7b11\u9053\uff1a\u201c\u4f60\u5df2\u5feb\u53d1\u80d6\u4e86\uff0c\u4ee5\u540e\u8bb0\u4f4f\u5343\u4e07\u4e0d\u80fd \u5403\u8089\u3002\u201d \u3000\u3000\u706f\u5df2\u5439\u7184\uff0c\u66d9\u8272\u521a\u67d3\u4e0a\u7a97\u7eb8\u3002 \u3000\u3000\u5d14\u7389\u771f\u6b63\u5728\u7528\u4e00\u5757\u5e03\u5dfe\u66ff\u97e9\u8d1e\u64e6\u51b7\u6c57\uff0c\u5979\u679c\u7136\u6ca1\u6709\u8d70\u3002 \u3000\u3000\u770b\u89c1\u53f6\u5f00\u62b1\u7740\u4e01\u7075\u7433\u56de\u6765\uff0c\u5979\u5c45\u7136\u7b11\u4e86\u3002 \u3000\u3000\u5e8a\u4e0a\u7684\u97e9\u8d1e\u72b9\u5728\u6c89\u7761\uff0c\u53f6\u5f00\u53ea\u6709\u5c06\u4e01\u7075\u7433\u653e\u5728\u6905\u5b50\u4e0a\u3002 \u3000\u3000\u4ed6\u603b\u7b97\u677e\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u5d14\u7389\u771f\u9053\uff1a\u201c\u540e\u9762\u6ca1\u6709\u4eba\u5728\u8ffd\uff1f\u201d \u3000\u3000\u53f6\u5f00\u6447\u6447\u5934\uff0c\u5fae\u7b11\u9053\uff1a\u201c\u7389\u7bab\u9053\u4eba\u5c31\u7b97\u53d1\u73b0\u5979\u5df2\u88ab\u6551\u8d70\uff0c\u4e5f\u7edd\u4e0d\u4f1a\u60f3\u5230\u6211\u4eec\u7684\u4eba\u8fd8\u5728\u8fd9 \u91cc\u3002\u201d \u3000\u3000\u90ed\u5b9a\u4e5f\u5df2\u56de\u6765\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u73b0\u5728\u6211\u4eec\u5e0c\u671b\u4ed6\u8ffd\u5230\u8fd9\u91cc\u6765\uff0c\u5c31\u7b97\u4ed6\u4e0d\u6765\uff0c\u6211\u4e5f\u4f1a\u53bb\u627e\u4ed6 \u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u7b11\u9053\uff1a\u201c\u82e5\u4e0d\u662f\u4f60\uff0c\u6211\u771f\u4e0d\u77e5\u9053\u600e\u4e48\u6837\u624d\u80fd\u8ba9\u90a3\u5973\u5b69\u5b50\u8bf4\u5b9e\u8bdd\u3002\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u8981\u5973\u4eba\u8bf4\u5b9e\u8bdd\u5e76\u4e0d\u96be\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u4e00\u4e2a\u5973\u4eba\u7684\u8863\u670d\u82e5\u7a81\u7136\u88ab\u6495\u5149\uff0c\u5f88\u5c11\u8fd8\u6709\u6562\u4e0d\u8bf4\u5b9e\u8bdd\u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u770b\u4e0d\u51fa\u4f60\u5bf9\u4ed8\u5973\u4eba\u4e5f\u5f88\u6709\u7ecf\u9a8c\u3002\u201d \u3000\u3000\u90ed\u5b9a\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u6211\u7ec3\u7684\u5e76\u4e0d\u662f\u7ae5\u5b50\u529f\u3002\u201d \u3000\u3000\u53f6\u5f00\u4e5f\u7b11\u4e86\uff1a\u201c\u50cf\u4f60\u8fd9\u6837\u7684\u7537\u4eba\uff0c\u60f3\u7ec3\u7ae5\u5b50\u529f\u53ea\u6015\u90fd\u5f88\u96be\u3002\u201d \u3000\u3000\u90ed\u5b9a\u770b\u4e86\u4e01\u7075\u7433\u4e00\u773c\uff0c\u7acb\u523b\u5c31\u8f6c\u8fc7\u773c\u6674\uff0c\u9053\uff1a\u201c\u5979\u662f\u4e0d\u662f\u88ab\u4eba\u70b9\u4e86\u54d1\u7a74\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u55ef\uff01\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u73b0\u5728\u5979\u5df2\u4e0d\u5fc5\u518d\u54d1\u4e0b\u53bb\u3002\u201d \u3000\u3000\u53f6\u5f00\u5fae\u7b11\u7740\uff0c\u62cd\u5f00\u4e86\u4e01\u7075\u7433\u7684\u7a74\u9053\uff0c\u770b\u5230\u4e86\u4e01\u7075\u7433\u90a3\u53cc\u7f8e\u4e3d\u7684\u773c\u775b\u53c8\u5df2\u5f20\u5f00\u6765\u770b\u7740\u4ed6\uff0c \u4ed6\u5b9e\u5728\u89c9\u5f97\u6109\u5feb\u6781\u4e86\u3002 \u3000\u3000\u4e01\u7075\u7433\u5374\u4f3c\u8fd8\u6ca1\u6709\u7761\u9192\uff0c\u773c\u76ae\u6726\u80e7\uff0c\u80b4\u4e86\u4ed6\u4e24\u773c\uff0c\u8fdf\u7591\u7740\u9053\uff1a\u201c\u53f6\u5f00\uff01\u201d \u3000\u3000\u53f6\u5f00\u7b11\u9053\uff1a\u201c\u4f60\u96be\u9053\u4e0d\u8ba4\u5f97\u6211\u4e86\uff1f\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u6211\u8ba4\u5f97\u4f60\u3002\u201d \u3000\u3000\u5979\u7a81\u7136\u4f38\u51fa\u624b\u3002\u5979\u7684\u624b\u91cc\u7ade\u6709\u628a\u5200\uff0c\u4e00\u5200\u523a\u5165\u4e86\u53f6\u5f00\u7684\u80f8\u819b\u3002 \u3000\u3000\u9c9c\u8840\u7bad\u4e00\u822c\u55b7\u51fa\u6765\uff0c\u76f4\u55b7\u5728\u4e01\u7075\u7433\u8138\u4e0a\uff0c\u5979\u82cd\u767d\u7684\u8138\uff0c\u7acb\u523b\u88ab\u9c9c\u8840\u67d3\u7ea2\u3002 \u3000\u3000\u53f6\u5f00\u7684\u8138\u4e0a\u5374\u5df2\u5168\u65e0\u8840\u8272\uff0c\u5403\u60ca\u5730\u770b\u7740\u5979\u3002 \u3000\u3000\u6bcf\u4e2a\u4eba\u90fd\u5728\u5403\u60ca\u5730\u770b\u7740\u5979\uff0c\u65e0\u8bba\u8c01\u90fd\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u5979\u4f1a\u5411\u53f6\u5f00\u4e0b\u8fd9\u79cd\u6bd2\u624b\u3002 \u3000\u3000\u4e01\u7075\u7433\u5374\u5728\u5927\u7b11\uff0c\u75af\u72c2\u5730\u5927\u7b11\uff0c\u7acb\u523b\u8df3\u8d77\u6765\uff0c\u7a81\u7136\u7a9c\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u53f6\u5f00\u4e00\u53ea\u624b\u6309\u4f4f\u80f8\u819b\u4e0a\u7684\u521b\u53e3\uff0c\u60f3\u8ffd\uff0c\u4eba\u5df2\u5012\u4e0b\uff0c\u98a4\u58f0\u9053\uff1a\u201c\u8ffd\u2026\u2026\u8ffd\u5979\u56de\u6765\u3002\u201d \u3000\u3000\u4e0d\u7b49\u4ed6\u8bf4\uff0c\u90ed\u5b9a\u5df2\u8ffd\u51fa\u3002 \u3000\u3000\u53f6\u5f00\u60f3\u8fc7\u53bb\u770b\u770b\u4ed6\u4eec\u662f\u5f80\u54ea\u8fb9\u8d70\u7684\uff0c\u53ef\u662f\u817f\u5df2\u53d1\u8f6f\uff0c\u773c\u524d\u7a81\u7136\u53d8\u6210\u4e86\u4e00\u7247\u9ed1\u6697\u3002 \u3000\u3000\u7edd\u671b\u7684\u9ed1\u6697\u3002 \u3000\u3000\u4ed6\u6700\u540e\u770b\u89c1\u7684\uff0c\u662f\u5d14\u7389\u771f\u90a3\u53cc\u5145\u6ee1\u4e86\u60ca\u60e7\u548c\u5173\u5207\u7684\u773c\u775b\u3002 \u3000\u3000\u4ed6\u6700\u540e\u542c\u89c1\u7684\uff0c\u662f\u4ed6\u81ea\u5df1\u7684\u5934\u649e\u5728\u684c\u5b50\u4e0a\u7684\u58f0\u97f3\u3002 \u3000\u3000\u51cc\u6668\u3002 \u3000\u3000\u5929\u7a7a\u8fd8\u662f\u7070\u6697\u7684\uff0c\u4eba\u90fd\u8fd8\u5728\u6c89\u7761\u3002 \u3000\u3000\u4e01\u7075\u7433\u50cf\u662f\u53ea\u7f9a\u7f8a\uff0c\u5728\u4e00\u91cd\u91cd\u5c4b\u810a\u4e0a\u8df3\u8dc3\u7740\uff0c\u8fd8\u4e0d\u65f6\u53d1\u51fa\u75af\u72c2\u7684\u7b11\u58f0\u3002 \u3000\u3000\u201c\u6211\u5df2\u6740\u4e86\u53f6\u5f00\uff0c\u6211\u5df2\u6740\u4e86\u53f6\u5f00\u2026\u2026\u201d \u3000\u3000\u5979\u7adf\u4f3c\u89c9\u5f97\u8fd9\u662f\u4ef6\u975e\u5e38\u503c\u5f97\u9ad8\u5174\u7684\u4e8b\u3002 \u3000\u3000\u201c\u5979\u75af\u4e86\u3002\u201d \u3000\u3000\u90ed\u5b9a\u5df2\u5c06\u81ea\u5df1\u7684\u8f7b\u529f\u65bd\u5c55\u5230\u6781\u9650\uff0c\u8fd8\u662f\u8ffd\u51fa\u4e86\u5f88\u8fdc\uff0c\u624d\u8ffd\u4e0a\u5979\u3002 \u3000\u3000\u201c\u4e01\u59d1\u5a18\uff0c\u8ddf\u6211\u56de\u53bb\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u77aa\u4e86\u4ed6\u4e00\u773c\uff0c\u7adf\u5df2\u5b8c\u5168\u4e0d\u8ba4\u5f97\u4ed6\uff0c\u7a81\u7136\u4e00\u5200\u5411\u4ed6\u523a\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u5200\u4e0a\u8fd8\u6709\u8840\uff0c\u53f6\u5f00\u7684\u8840\u3002 \u3000\u3000\u90ed\u5b9a\u54ac\u4e86\u54ac\u7259\uff0c\u56de\u8eab\u53cd\u624b\uff0c\u53bb\u593a\u5979\u7684\u5200\u3002 \u3000\u3000\u4ed6\u5e76\u6ca1\u6709\u593a\u4e0b\u5979\u7684\u5200\uff0c\u53ef\u662f\u4ed6\u53e6\u4e00\u53ea\u624b\u5df2\u95ea\u7535\u822c\u5730\u6263\u5728\u5979\u5de6\u9888\u540e\u3002 \u3000\u3000\u4e01\u7075\u7433\u7684\u773c\u775b\u7a81\u7136\u53d1\u76f4\uff0c\u4eba\u5df2\u5012\u4e0b\u3002 \u3000\u3000\u56db\u9762\u65e0\u4eba\uff0c\u5c4b\u810a\u4e0a\u7684\u971c\u767d\u5982\u94f6\u3002 \u3000\u3000\u4e01\u7075\u7433\u7684\u547c\u53eb\uff0c\u5c45\u7136\u5e76\u6ca1\u6709\u5c06\u7389\u7bab\u9053\u4eba\u60ca\u52a8\u51fa\u6765\u3002 \u3000\u3000\u90ed\u5b9a\u5df2\u62b1\u8d77\u4e86\u4e86\u7075\u7433\uff0c\u4ed6\u6025\u7740\u8981\u8d76\u56de\u53bb\u770b\u770b\u53f6\u5f00\u7684\u4f24\u52bf\uff0c\u5df2\u987e\u4e0d\u5f97\u7537\u5973\u4e4b\u5acc\u3002 \u3000\u3000\u53ef\u662f\u90a3\u5c4b\u5b50\u91cc\u5df2\u6ca1\u6709\u4eba\u4e86\u2026\u2026\u5df2\u6ca1\u6709\u6d3b\u4eba\u4e86\u3002 \u3000\u3000\u4e00\u76f4\u6c89\u7761\u660f\u8ff7\u7740\u7684\u97e9\u8d1e\uff0c\u5df2\u88ab\u4e00\u67c4\u957f\u5251\u9489\u6b7b\u5728\u5e8a\u4e0a\u3002 \u3000\u3000\u5730\u4e0a\u7684\u8840\u8ff9\u5df2\u51dd\u7ed3\uff0c\u662f\u53f6\u5f00\u7684\u8840\u3002 \u3000\u3000\u684c\u89d2\u4e0a\u4e5f\u6709\u8840\u8ff9\uff0c\u4e5f\u662f\u53f6\u5f00\u7684\u8840\u3002 \u3000\u3000\u4f46\u53f6\u5f00\u5374\u5df2\u4e0d\u89c1\u4e86\uff0c\u5d14\u7389\u771f\u4e5f\u5df2\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u662f\u8c01\u7684\u957f\u5251\uff1f\u662f\u8c01\u4e0b\u7684\u6bd2\u624b\uff1f\u4e3a\u4ec0\u4e48\u8981\u5bf9\u4e00\u4e2a\u534a\u6b7b\u4e0d\u6d3b\u7684\u4eba\u4e0b\u6bd2\u624b\uff1f \u3000\u3000\u53f6\u5f00\u5230\u54ea\u91cc\u53bb\u4e86\uff0c\u96be\u9053\u5df2\u88ab\u5d14\u7389\u771f\u5e26\u56de\u53bb\u732e\u7ed9\u4e86\u738b\u7bab\u9053\u4eba\uff1f \u3000\u3000\u65e0\u8bba\u5982\u4f55\uff0c\u4ed6\u5b9e\u5728\u5df2\u51f6\u591a\u5409\u5c11\u3002 \u3000\u3000\u5c4b\u5b50\u5f88\u5c0f\uff0c\u4f46\u5374\u6536\u62fe\u5f97\u5f88\u5e72\u51c0\u3002 \u3000\u3000\u5c4b\u89d2\u91cc\u6709\u4e2a\u5c0f\u5c0f\u7684\u6728\u67dc\uff0c\u662f\u9501\u7740\u7684\uff0c\u65c1\u8fb9\u7684\u5986\u53f0\u4e0a\uff0c\u6446\u7740\u9762\u94dc\u955c\u3002 \u3000\u3000\u51b7\u98ce\u5439\u5f97\u7a97\u7eb8\u7c0c\u7c0c\u7684\u4e00\u54cd\uff0c\u95e8\u4e0a\u6302\u7740\u5e03\u5e18\uff0c\u95e8\u5916\u4f20\u6765\u4e00\u9635\u9635\u7684\u836f\u9999\u3002 \u3000\u3000\u53f6\u5f00\u5e76\u6ca1\u6709\u6b7b\u3002 \u3000\u3000\u4ed6\u5df2\u9192\u4e86\u8fc7\u6765\uff0c\u4ed6\u9192\u6765\u65f6\uff0c\u5c31\u53d1\u73b0\u81ea\u5df1\u662f\u5728\u8fd9\u4e48\u6837\u4e00\u4e2a\u5730\u65b9\u3002 \u3000\u3000\u7136\u540e\u4ed6\u624d\u53d1\u73b0\u81ea\u5df1\u662f\u8d64\u88f8\u88f8\u5730\u8eba\u5728\u5e8a\u4e0a\uff0c\u76d6\u7740\u4e09\u6761\u5f88\u539a\u7684\u68c9\u88ab\u3002 \u3000\u3000\u4ed6\u80f8\u819b\u4e0a\u7684\u4f24\u53e3\u5df2\u88ab\u4eba\u7528\u767d\u5e03\u5305\u624e\u4e86\u8d77\u6765\uff0c\u5305\u624e\u5f97\u5f88\u597d\u3002 \u3000\u3000\u662f\u8c01\u66ff\u4ed6\u5305\u624e\u7684\uff1f\u8fd9\u91cc\u7a76\u7adf\u662f\u4ec0\u4e48\u5730\u65b9\uff1f \u3000\u3000\u4ed6\u60f3\u5750\u8d77\u6765\uff0c\u4f46\u80f8\u819b\u4e0a\u4eff\u4f5b\u8fd8\u63d2\u7740\u4e00\u628a\u5200\uff0c\u53ea\u8981\u4e00\u52a8\uff0c\u5c31\u75bc\u5f97\u5168\u8eab\u90fd\u4eff\u4f5b\u8981\u6495\u88c2\u3002 \u3000\u3000\u4ed6\u60f3\u547c\u558a\uff0c\u4f46\u8fd9\u65f6\u95e8\u5e18\u5df2\u6380\u8d77\uff0c\u5df2\u6709\u4e2a\u4eba\u7aef\u7740\u7897\u836f\u6162\u6162\u5730\u8d70\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u5d14\u7389\u771f\u3002 \u3000\u3000\u5979\u5df2\u8131\u4e0b\u4e86\u5979\u7684\u9053\u888d\uff0c\u8eab\u4e0a\u662f\u5957\u9752\u5e03\u8863\u88d9\uff0c\u86fe\u7709\u6de1\u626b\uff0c\u4e0d\u65bd\u8102\u7c89\uff0c\u7709\u76ee\u95f4\u5374\u5e26\u7740\u6d53\u6d53\u7684 \u5fe7\u601d\u3002 \u3000\u3000\u770b\u89c1\u53f6\u5f00\u5df2\u9192\uff0c\u5979\u7684\u7709\u4e5f\u5df2\u8212\u5c55\u5f00\u4e86\u3002 \u3000\u3000\u201c\u6211\u600e\u4e48\u4f1a\u5230\u8fd9\u91cc\u6765\u7684\uff1f\u201d \u3000\u3000\u53f6\u5f00\u95ee\u51fa\u4e86\u8fd9\u53e5\u8bdd\uff0c\u7acb\u523b\u5c31\u53d1\u89c9\u8fd9\u662f\u53e5\u5e9f\u8bdd\uff0c\u5f53\u7136\u662f\u5d14\u7389\u771f\u5c06\u4ed6\u6551\u5230\u8fd9\u91cc\u6765\u7684\u3002 \u3000\u3000\u5d14\u7389\u771f\u5df2\u8d70\u8fc7\u6765\uff0c\u5c06\u836f\u7897\u8f7b\u8f7b\u5730\u653e\u5728\u5e8a\u7554\u7684\u5c0f\u51e0\u4e0a\u3002 \u3000\u3000\u5979\u6bcf\u4e00\u4e2a\u52a8\u4f5c\u770b\u6765\u90fd\u90a3\u4e48\u6e29\u67d4\uff0c\u5df2\u5b8c\u5168\u4e0d\u662f\u90a3\u4e2a\u968f\u7740\u7bab\u58f0\u626d\u52a8\u8170\u80a2\u7684\u5973\u9053\u4eba\u3002 \u3000\u3000\u53f6\u5f00\u770b\u7740\u5979\uff0c\u5ffd\u7136\u6709\u4e86\u79cd\u5f88\u5b89\u5168\u7684\u611f\u89c9\uff0c\u5fc3\u4e5f\u5df2\u5b9a\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u4f46\u4ed6\u5374\u8fd8\u662f\u5fcd\u4e0d\u4f4f\u8981\u95ee\uff1a\u201c\u8fd9\u91cc\u662f\u4ec0\u4e48\u5730\u65b9\uff1f\u201d \u3000\u3000\u5d14\u7389\u771f\u5782\u7740\u5934\uff0c\u8f7b\u8f7b\u5730\u5439\u7740\u836f\uff0c\u8fc7\u4e86\u5f88\u4e45\u624d\u56de\u7b54\uff1a\u201c\u662f\u522b\u4eba\u7684\u5bb6\u3002\u201d \u3000\u3000\u201c\u662f\u8c01\u7684\u5bb6\uff1f\u201d \u3000\u3000\u201c\u662f\u4e2a\u505a\u8336\u53f6\u4e70\u5356\u7684\u751f\u610f\u4eba\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u8ba4\u5f97\u4ed6\uff1f\u201d \u3000\u3000\u5d14\u7389\u771f\u6ca1\u6709\u56de\u7b54\u8fd9\u53e5\u8bdd\uff0c\u5374\u8f7b\u8f7b\u9053\uff1a\u201c\u4f60\u53d7\u7684\u4f24\u5f88\u91cd\uff0c\u6211\u6015\u7389\u7bab\u9053\u4eba\u4ed6\u4eec\u627e\u6765\uff0c\u53ea\u597d\u5e26 \u4f60\u8d76\u5feb\u8d70\u3002\u201d \u3000\u3000\u5979\u662f\u4e2a\u5f88\u7ec6\u5fc3\u7684\u5973\u4eba\uff0c\u60f3\u5f97\u5f88\u5468\u5230\u3002 \u3000\u3000\u53f6\u5f00\u82e5\u662f\u7559\u5728\u90a3\u5c4b\u5b50\u91cc\uff0c\u8bf4\u4e0d\u5b9a\u4e5f\u65e9\u5df2\u88ab\u4e00\u67c4\u957f\u5251\u9489\u6b7b\u5728\u5e8a\u4e0a\u3002 \u3000\u3000\u5d14\u738b\u771f\u53c8\u9053\uff1a\u201c\u53ef\u662f\u6211\u7b2c\u4e00\u6b21\u5230\u957f\u5b89\u57ce\uff0c\u4e00\u4e2a\u4eba\u4e5f\u4e0d\u8ba4\u5f97\uff0c\u90a3\u65f6\u5929\u521a\u4eae\uff0c\u6211\u5b9e\u5728\u4e0d\u77e5\u9053 \u5e94\u8be5\u5e26\u4f60\u5230\u4ec0\u4e48\u5730\u65b9\u53bb\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u5c31\u95ef\u5230\u8fd9\u4eba\u5bb6\u91cc\uff1f\u201d \u3000\u3000\u5d14\u7389\u771f\u70b9\u5934\u9053\uff1a\u201c\u8fd9\u662f\u4e2a\u5f88\u5e73\u51e1\u7684\u5c0f\u6237\u4eba\u5bb6\uff0c\u7edd\u5bf9\u6ca1\u6709\u4eba\u60f3\u5230\u4f60\u4f1a\u5728\u8fd9\u91cc\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u8fd9\u91cc\u7684\u4e3b\u4eba\u4f60\u5f53\u7136\u4e5f\u4e0d\u8ba4\u5f97\uff1f\u201d \u3000\u3000\u5d14\u7389\u771f\u53ea\u597d\u627f\u8ba4\uff1a\u201c\u6211\u4e0d\u8ba4\u5f97\u3002\u201d \u3000\u3000\u5979\u8bf4\u8fc7\uff0c\u5728\u957f\u5b89\u57ce\u91cc\u5979\u4e00\u4e2a\u4eba\u90fd\u4e0d\u8ba4\u5f97\u3002 \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u73b0\u5728\u4ed6\u4eec\u7684\u4eba\u5462\uff1f\u201d \u3000\u3000\u5d14\u7389\u771f\u8fdf\u7591\u7740\uff0c\u53c8\u8fc7\u4e86\u5f88\u4e45\uff0c\u624d\u8f7b\u8f7b\u9053\uff1a\u201c\u5df2\u88ab\u6211\u6740\u4e86\u3002\u201d \u3000\u3000\u5979\u5782\u7740\u5934\uff0c\u4e0d\u6562\u53bb\u770b\u53f6\u5f00\uff0c\u5979\u6015\u53f6\u5f00\u4f1a\u9a82\u5979\u3002 \u3000\u3000\u53ef\u662f\u53f6\u5f00\u8fde\u4e00\u4e2a\u5b57\u4e5f\u6ca1\u6709\u8bf4\u3002 \u3000\u3000\u4ed6\u5e76\u4e0d\u662f\u90a3\u79cd\u9053\u8c8c\u5cb8\u7136\u7684\u9053\u5b66\u541b\u5b50\uff0c\u4ed6\u77e5\u9053\u82e5\u4e0d\u662f\u5d14\u7389\u771f\uff0c\u73b0\u5728\u4ed6\u5df2\u4e0d\u77e5\u6b7b\u5728\u8c01\u7684\u624b \u4e0b\u3002 \u3000\u3000\u957f\u5b89\u57ce\u91cc\u8981\u6740\u4ed6\u7684\u4eba\u5b9e\u5728\u4e0d\u5c11\u3002 \u3000\u3000\u4e00\u4e2a\u534a\u751f\u4e0d\u719f\u7684\u5973\u4eba\uff0c\u5192\u7740\u751f\u547d\u7684\u5371\u9669\u6551\u4e86\u4ed6\uff0c\u53c8\u5728\u5168\u5fc3\u5168\u610f\u5730\u7167\u987e\u7740\u4ed6\uff0c\u4e3a\u4e86\u4ed6\u7684\u5b89 \u5168\uff0c\u7adf\u4e0d\u60c5\u6740\u4eba\u3002 \u3000\u3000\u4f60\u53eb\u4ed6\u600e\u4e48\u8fd8\u5fcd\u5fc3\u8d23\u5907\u5979\uff0c\u600e\u4e48\u8fd8\u80fd\u9a82\u5f97\u51fa\u53e3\u3002 \u3000\u3000\u5d14\u7389\u771f\u9053\uff1a\u201c\u6211\u95ef\u8fdb\u6765\u7684\u65f6\u5019\uff0c\u6709\u4e24\u4e2a\u4eba\u7761\u5728\u5e8a\u4e0a\uff0c\u6211\u672c\u6765\u4ee5\u4e3a\u4ed6\u4eec\u662f\u592b\u5987\u3002\u201d \u3000\u3000\u53f6\u5f00\u7ec8\u4e8e\u5fcd\u4e0d\u4f4f\u95ee\uff1a\u201c\u96be\u9053\u4ed6\u4eec\u4e0d\u662f\uff1f\u201d \u3000\u3000\u5d14\u7389\u771f\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u90a3\u5973\u7684\u5df2\u6709\u4e09\u5341\u591a\u5c81\uff0c\u7537\u7684\u5374\u6700\u591a\u53ea\u6709\u5341\u4e03\u516b\u5c81\uff0c\u6211\u903c\u7740\u4ed6\u4eec\u4e00 \u95ee\uff0c\u8fd9\u5b69\u5b50\u5c31\u8bf4\u4e86\u5b9e\u8bdd\u3002\u201d \u3000\u3000\u539f\u6765\u4e08\u592b\u5230\u5916\u5730\u4e70\u8336\u53bb\u4e86\uff0c\u59bb\u5b50\u5c31\u52fe\u5f15\u4e86\u5728\u4ed6\u4eec\u5bb6\u91cc\u6253\u6742\u7684\u5b66\u5f92\u3002 \u3000\u3000\u5d14\u7389\u771f\u7684\u8138\u4f3c\u5df2\u6709\u4e9b\u53d1\u7ea2\uff0c\u63a5\u7740\u9053\uff1a\u201c\u8fd9\u4e24\u4eba\u4e00\u4e2a\u80cc\u53db\u4e86\u81ea\u5df1\u7684\u4e08\u592b\uff0c\u4e00\u4e2a\u80cc\u53db\u4e86\u81ea\u5df1 \u7684\u5e08\u5085\uff0c\u6240\u4ee5\u6211\u624d\u4f1a\u6740\u4e86\u4ed6\u4eec\uff0c\u6211\u2026\u2026\u6211\u53ea\u5e0c\u671b\u4f60\u4e0d\u8981\u8ba4\u4e3a\u6211\u662f\u4e2a\u5fc3\u72e0\u624b\u8fa3\u7684\u5973\u4eba\u3002\u201d \u3000\u3000\u53f6\u5f00\u770b\u7740\u5979\uff0c\u5fc3\u91cc\u5ffd\u7136\u6709\u4e00\u79cd\u8bf4\u4e0d\u51fa\u7684\u6ecb\u5473\u3002 \u3000\u3000\u5979\u4e3a\u4ed6\u505a\u4e86\u8fd9\u4e9b\u4e8b\uff0c\u4e3a\u4ed6\u5192\u4e86\u8fd9\u4e48\u5927\u7684\u5371\u9669\uff0c\u53ef\u662f\u5979\u5e76\u4e0d\u8981\u4ed6\u611f\u6fc0\uff0c\u66f4\u4e0d\u8981\u4ed6\u62a5\u7b54\u3002 \u3000\u3000\u5979\u552f\u4e00\u5e0c\u671b\u7684\uff0c\u7adf\u53ea\u4e0d\u8fc7\u662f\u5e0c\u671b\u4ed6\u4e0d\u8981\u770b\u8f7b\u5979\u3002 \u3000\u3000\u4ed6\u7684\u770b\u6cd5\u5bf9\u5979\u7adf\u5982\u6b64\u91cd\u8981\u3002 \u3000\u3000\u53f6\u5f00\u5fcd\u4e0d\u4f4f\u53f9\u4e86\u53e3\u6c14\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u6211\u4e5f\u5e0c\u671b\u4f60\u660e\u767d\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u82e5\u6709\u4eba\u8ba4\u4e3a\u4f60\u8fd9\u6837\u505a\u5f97\u4e0d\u5bf9\uff0c\u8ba4\u4e3a\u4f60\u662f\u4e2a\u5fc3\u72e0\u624b\u8fa3\u7684\u5973\u4eba\uff0c\u90a3\u4eba\u4e00\u5b9a\u662f\u4e2a\u4f2a \u541b\u5b50\uff0c\u662f\u4e2a\u5927\u6df7\u86cb\u3002\u201d \u3000\u3000\u4ed6\u5fae\u7b11\u7740\uff0c\u63a5\u7740\u9053\uff1a\u201c\u6211\u5e0c\u671b\u4f60\u76f8\u4fe1\u6211\uff0c\u6211\u7edd\u4e0d\u662f\u8fd9\u79cd\u6df7\u86cb\u3002\u201d \u3000\u3000\u5d14\u7389\u771f\u7b11\u4e86\uff0c\u5979\u7b11\u7684\u65f6\u5019\uff0c\u5c31\u4eff\u4f5b\u5bd2\u51ac\u5df2\u7ecf\u8fc7\u53bb\uff0c\u6625\u5929\u5df2\u7ecf\u5230\u6765\u3002 \u3000\u3000\u201c\u836f\u53ef\u4ee5\u4eba\u53e3\u4e86\uff0c\u4f60\u559d\u4e0b\u53bb\u597d\u4e0d\u597d\uff1f\u201d \u3000\u3000\u5979\u6276\u8d77\u53f6\u5f00\uff0c\u5c31\u50cf\u662f\u6bcd\u4eb2\u54c4\u5b69\u5b50\u4e00\u6837\uff0c\u5c06\u8fd9\u7897\u836f\u4e00\u53e3\u53e3\u5582\u4ed6\u559d\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u201c\u8fd9\u662f\u6211\u81ea\u5df1\u914d\u7684\u836f\uff0c\u6211\u4e0d\u6562\u627e\u5927\u592b\uff0c\u6211\u6015\u522b\u4eba\u4f1a\u4ece\u5927\u592b\u5634\u91cc\u67e5\u51fa\u4f60\u7684\u884c\u8e2a\u3002\u201d \u3000\u3000\u5979\u5b9e\u5728\u662f\u4e2a\u975e\u5e38\u7ec6\u5fc3\u7684\u5973\u4eba\uff0c\u6bcf\u4e00\u70b9\u90fd\u60f3\u5f97\u975e\u5e38\u5468\u5230\u3002 \u3000\u3000\u53f6\u5f00\u770b\u7740\u5979\uff0c\u5fc3\u91cc\u5145\u6ee1\u4e86\u6e29\u6696\u548c\u611f\u6fc0\uff0c\u5fae\u7b11\u9053\uff1a\u201c\u6211\u9047\u89c1\u4f60\uff0c\u771f\u7684\u662f\u8fd0\u6c14\uff0c\u65e0\u8bba\u4ec0\u4e48\u4e8b \u4f60\u597d\u50cf\u90fd\u80fd\u60f3\u5f97\u5230\u3002\u201d \u3000\u3000\u5d14\u7389\u771f\u8fdf\u7591\u7740\uff0c\u5ffd\u7136\u9053\uff1a\u201c\u4f46\u6211\u5374\u8fd8\u662f\u60f3\u4e0d\u901a\u5979\u4e3a\u4ec0\u4e48\u8981\u6740\u4f60\uff1f\u201d \u3000\u3000\u53f6\u5f00\u7684\u7b11\u5bb9\u9eef\u6de1\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u5d14\u738b\u771f\u9053\uff1a\u201c\u6211\u77e5\u9053\u6211\u672c\u4e0d\u8be5\u63d0\u8d77\u8fd9\u4ef6\u4e8b\u7684\uff0c\u53ef\u662f\u6211\u5b9e\u5728\u60f3\u4e0d\u901a\uff0c\u4f60\u4e0d\u987e\u4e00\u5207\u5730\u53bb\u6551 \u5979\uff0c\u5979\u4e3a\u4ec0\u4e48\u8981\u5bf9\u4f60\u4e0b\u8fd9\u79cd\u6bd2\u624b\uff1f\u201d \u3000\u3000\u53f6\u5f00\u5374\u53c8\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u6211\u60f3\u2026\u2026\u5979\u4e00\u5b9a\u6709\u539f\u56e0\u7684\u3002\u201d \u3000\u3000\u5d14\u7389\u771f\u9053\uff1a\u201c\u4ec0\u4e48\u539f\u56e0\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6c5f\u6e56\u4e2d\u6709\u5f88\u591a\u90aa\u95e8\u6b6a\u9053\u7684\u4e8b\uff0c\u6211\u8bf4\u7ed9\u4f60\u542c\u4f60\u4e5f\u672a\u5fc5\u77e5\u9053\u3002\u201d \u3000\u3000\u5d14\u7389\u771f\u9053\uff1a\u201c\u4f60\u96be\u9053\u4e00\u70b9\u90fd\u4e0d\u602a\u5979\uff1f\u201d \u3000\u3000\u53f6\u5f00\u6447\u4e86\u6447\u5934\uff0c\u9053\uff1a\u201c\u5979\u8fd9\u4e48\u505a\uff0c\u4e00\u5b9a\u662f\u88ab\u6444\u5fc3\u672f\u4e00\u7c7b\u7684\u90aa\u6cd5\u6240\u8ff7\uff0c\u7b49\u5979\u82cf\u9192\u540e\uff0c\u5979\u4e00 \u5b9a\u4f1a\u6bd4\u6211\u66f4\u75db\u82e6\uff0c\u6211\u600e\u4e48\u8fd8\u80fd\u602a\u5979\u3002\u201d \u3000\u3000\u4ed6\u7684\u58f0\u97f3\u91cc\u5145\u6ee1\u4e86\u5173\u6000\u3002 \u3000\u3000\u522b\u4eba\u51e0\u4e4e\u4e00\u5200\u5c06\u4ed6\u6740\u6b7b\uff0c\u4ed6\u5374\u8fd8\u5728\u5173\u5fc3\u7740\u90a3\u4e2a\u4eba\u6e05\u9192\u540e\u7684\u611f\u89c9\u3002 \u3000\u3000\u81f3\u4e8e\u4ed6\u81ea\u5df1\u7684\u75db\u82e6\uff0c\u4ed6\u5374\u8fde\u4e00\u70b9\u90fd\u4e0d\u5728\u4e4e\u3002 \u3000\u3000\u5d14\u7389\u771f\u770b\u7740\u4ed6\uff0c\u7f8e\u4e3d\u7684\u773c\u775b\u91cc\u7a81\u7136\u6cea\u73e0\u4e00\u8fde\u4e32\u6d41\u4e0b\u3002 \u3000\u3000\u201c\u4f60\u5728\u54ed\uff1f\u201d \u3000\u3000\u201c\u4f60\u4e3a\u4ec0\u4e48\u5ffd\u7136\u4f24\u5fc3\uff1f\u201d \u3000\u3000\u5d14\u7389\u771f\u6162\u6162\u5730\u62ed\u4e86\u62ed\u6cea\u75d5\uff0c\u52c9\u5f3a\u7b11\u9053\uff1a\u201c\u6211\u5e76\u4e0d\u662f\u4f24\u5fc3\uff0c\u6211\u53ea\u4e0d\u8fc7\u5728\u60f3\uff0c\u5047\u5982\u6709\u4e00\u5929\uff0c \u80fd\u6709\u4e2a\u4eba\u4f1a\u8fd9\u6837\u5bf9\u6211\uff0c\u5904\u5904\u90fd\u66ff\u6211\u60f3\uff0c\u90a3\u4e48\u6211\u2026\u201d \u3000\u3000\u5979\u6ca1\u6709\u8bf4\u5b8c\u8fd9\u53e5\u8bdd\uff0c\u5979\u7684\u6cea\u53c8\u5df2\u6d41\u4e0b\uff0c\u56e0\u4e3a\u5979\u77e5\u9053\u81ea\u5df1\u662f\u6c38\u8fdc\u4e5f\u4e0d\u4f1a\u9047\u7740\u8fd9\u4e48\u6837\u4e00\u4e2a\u4eba \u7684\u3002\u56e0\u4e3a\u5979\u77e5\u9053\u8fd9\u4e2a\u4eba\u73b0\u5728\u867d\u7136\u5728\u5979\u6000\u62b1\u91cc\uff0c\u4f46\u5fc3\u91cc\u5374\u5728\u60f3\u7740\u522b\u4eba\uff0c\u800c\u4e14\u5f88\u5feb\u5c31\u4f1a\u79bb\u5f00\u5979\u3002 \u3000\u3000\u5979\u5e76\u4e0d\u662f\u5ac9\u5992\uff0c\u4e5f\u4e0d\u662f\u75db\u82e6\uff0c\u53ea\u4e0d\u8fc7\u89c9\u5f97\u6709\u79cd\u8bf4\u4e0d\u51fa\u7684\u611f\u4f24\u3002 \u3000\u3000\u5979\u5df2\u662f\u4e2a\u6210\u719f\u7684\u5973\u4eba\uff0c\u5979\u8fd9\u4e00\u751f\u90fd\u5f88\u5bc2\u5bde\u3002 \u3000\u3000\u5bc2\u5bde\uff0c\u591a\u4e48\u53ef\u6015\u7684\u5bc2\u5bde\u2026\u2026 \u3000\u3000\u51b0\u51b7\u7684\u6cea\u73e0\uff0c\u4e00\u6ef4\u6ef4\u843d\u5728\u53f6\u5f00\u8138\u4e0a\uff0c\u4f46\u53f6\u5f00\u7684\u5fc3\u91cc\u5374\u5728\u53d1\u70ed\uff0c\u70ed\u5f97\u53d1\u75bc\u3002 \u3000\u3000\u4ed6\u5e76\u4e0d\u662f\u4e2a\u94c1\u77f3\u5fc3\u80a0\u7684\u4eba\uff0c\u4e5f\u4e0d\u662f\u5757\u6728\u5934\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u53c8\u80fd\u600e\u4e48\u6837\uff1f \u3000\u3000\u5c4b\u5b50\u91cc\u6e10\u6e10\u6697\u4e86\uff0c\u9ec4\u660f\u53c8\u65e0\u58f0\u65e0\u606f\u5730\u6084\u4fcf\u6765\u4e34\u3002 \u3000\u3000\u9ec4\u660f\u603b\u662f\u7f8e\u7684\uff0c\u7f8e\u5f97\u4eca\u4eba\u5fc3\u75bc\u3002 \u3000\u3000\u5d14\u7389\u771f\u5c06\u65e9\u4e0a\u716e\u7684\u51b7\u996d\uff0c\u7528\u9171\u6cb9\u62cc\u7740\u5403\u4e86\u4e00\u7897\uff0c\u5374\u66ff\u53f6\u5f00\u71ac\u4e86\u9505\u7a00\u7ca5\u3002 \u3000\u3000\u5979\u7ea2\u7740\u8138\u9053\uff1a\u201c\u6211\u672c\u6765\u60f3\u4e70\u70b9\u4eba\u53c2\u6765\u71ac\u6c64\u7684\uff0c\u53ef\u662f\u6211\u2026\u2026\u201d \u3000\u3000\u5979\u6ca1\u6709\u94b1\uff0c\u53f6\u5f00\u4e5f\u6ca1\u6709\uff0c\u4ed6\u5ffd\u7136\u6ce8\u610f\u5230\u5979\u672c\u6765\u5934\u4e0a\u7684\u4e00\u6839\u78a7\u7389\u7c2a\u5df2\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u201c\u6211\u672c\u6765\u60f3\u6253\u5f00\u90a3\u67dc\u5b50\uff0c\u770b\u770b\u91cc\u9762\u662f\u4e0d\u662f\u6709\u94f6\u5b50\u7684\uff0c\u53ef\u662f\u6211\u53c8\u4e0d\u6562\u3002\u201d \u3000\u3000\u5979\u5b9e\u5728\u662f\u4e2a\u672c\u6027\u5f88\u5584\u826f\u7684\u5973\u5b69\u5b50\uff0c\u800c\u4e14\u6709\u4e00\u79cd\u771f\u6b63\u7684\u5973\u6027\u6e29\u67d4\u3002 \u3000\u3000\u53f6\u5f00\u6162\u6162\u5730\u555c\u7740\u7ca5\uff0c\u5fc3\u91cc\u5ffd\u7136\u6709\u4e86\u79cd\u5947\u5999\u7684\u611f\u89c9\uff0c\u5047\u5982\u4ed6\u53ea\u4e0d\u8fc7\u662f\u4e2a\u505a\u5c0f\u4e70\u5356\u7684\u751f\u610f \u4eba\uff0c\u5047\u5982\u4ed6\u4eec\u662f\u592b\u59bb\uff0c\u5047\u5982\u4ed6\u4eec\u90fd\u6ca1\u6709\u8fc7\u53bb\u90a3\u4e9b\u5f80\u4e8b\uff0c\u4ed6\u4eec\u662f\u4e0d\u662f\u4f1a\u6d3b\u5f97\u66f4\u5e78\u798f\uff1f \u3000\u3000\u53ef\u662f\u73b0\u5728\u2026\u2026\u5047\u5982\u73b0\u5728\u4ed6\u4e5f\u80fd\u629b\u5f00\u4e00\u5207\uff0c\u5047\u5982\u5979\u4e5f\u613f\u610f\u966a\u4f34\u4ed6\uff0c\u5047\u5982\u2026\u2026 \u3000\u3000\u53f6\u5f00\u6ca1\u6709\u518d\u60f3\u4e0b\u53bb\uff0c\u4ed6\u4e0d\u80fd\u60f3\u4e0b\u53bb\uff0c\u5b81\u9759\u7684\u751f\u6d3b\uff0c\u5bf9\u4ed6\u662f\u79f0\u4e0d\u53ef\u6297\u62d2\u7684\u8bf1\u60d1\uff0c\u53ef\u662f\u4ed6\u8fd9 \u4eba\u5374\u504f\u504f\u597d\u50cf\u751f\u6765\u5c31\u4e0d\u80fd\u8fc7\u8fd9\u79cd\u65e5\u5b50\uff0c\u4e16\u4e0a\u53c8\u6709\u51e0\u4e2a\u4eba\u80fd\u968f\u5fc3\u6240\u6b32\uff0c\u9009\u62e9\u81ea\u5df1\u7684\u751f\u6d3b\u65b9\u5f0f\uff1f \u3000\u3000\u591c\u8272\u6e10\u6e10\u6df1\u4e86\uff0c\u4ed6\u4eec\u90fd\u6ca1\u6709\u8bf4\u8bdd\uff0c\u4eff\u4f5b\u5728\u5168\u5fc3\u5168\u610f\u5730\u4eab\u53d7\u8fd9\u7247\u523b\u5b81\u9759\uff0c\u56e0\u4e3a\u4ed6\u4eec\u77e5\u9053\u8fd9 \u79cd\u65e5\u5b50\u662f\u5f88\u5feb\u5c31\u4f1a\u7ed3\u675f\u7684\uff0c\u53f6\u5f00\u4ec0\u4e48\u90fd\u4e0d\u613f\u53bb\u60f3\uff0c\u53ea\u89c9\u5f97\u773c\u76ae\u6e10\u6e10\u6c89\u91cd\uff0c\u4ed6\u6d41\u4e86\u5f88\u591a\u8840\uff0c\u89c9 \u5f97\u5f88\u75b2\u5026\uff0c\u800c\u4e14\u5f88\u51b7\u3002 \u3000\u3000\u6726\u6726\u80e7\u80e7\u4e2d\uff0c\u4ed6\u89c9\u5f97\u81ea\u5df1\u4eff\u4f5b\u5728\u6e10\u6e10\u5730\u6c89\u5165\u4e00\u4e2a\u51b0\u7a96\u91cc\uff0c\u4ed6\u51b7\u5f97\u5168\u8eab\u90fd\u5728\u53d1\u6296\uff0c\u51b7\u5f97\u5634 \u5507\u90fd\u53d1\u4e86\u9752\u3002\u53ef\u662f\u5979\u5df2\u5c06\u8fd9\u91cc\u6240\u6709\u7684\u68c9\u88ab\u90fd\u66ff\u4ed6\u76d6\u4e0a\u4e86\u2014\u2014\u73b0\u5728\u600e\u4e48\u529e\u5462\uff1f \u3000\u3000\u4ed6\u7684\u8138\u8272\u8d8a\u6765\u8d8a\u53ef\u6015\uff0c\u6296\u5f97\u5c31\u50cf\u662f\u4e00\u7247\u5bd2\u98ce\u4e2d\u7684\u53f6\u5b50\uff0c\u6709\u4ec0\u4e48\u6cd5\u5b50\u624d\u80fd\u4f7f\u4ed6\u6e29\u6696\uff1f\u53ea\u8981 \u80fd\u8ba9\u4ed6\u6e29\u6696\uff0c\u65e0\u8bba\u8981\u5979\u505a\u4ec0\u4e48\uff0c\u5979\u90fd\u5fc3\u7518\u60c5\u613f\u7684\uff0c\u5979\u7684\u8138\u5ffd\u7136\u7ea2\u4e86\u3002\u5979\u5df2\u60f3\u5230\u4e86\u4e00\u4e2a\u6cd5\u5b50\uff0c \u4e00\u79cd\u4eba\u7c7b\u6700\u539f\u59cb\u7684\u4e92\u76f8\u53d6\u6696\u65b9\u6cd5\u3002 \u3000\u3000\u53f6\u5f00\u4e0d\u518d\u53d1\u6296\u3001\u8138\u4e0a\u4e5f\u6e10\u6e10\u6709\u4e86\u8840\u8272\uff0c\u7136\u540e\u4ed6\u5c31\u53d1\u73b0\uff0c\u6709\u4e2a\u4eba\u6b63\u8d64\u88f8\u88f8\u5730\u7761\u5728\u4ed6\u8eab\u65c1\u7528 \u529b\u62b1\u4f4f\u4e86\u4ed6\uff0c\u5979\u7684\u8eab\u5b50\u5149\u6ed1\u800c\u67d4\u8f6f\uff0c\u70ed\u5f97\u5c31\u50cf\u662f\u4e00\u56e2\u706b\u3002 \u3000\u3000\u53d1\u73b0\u53f6\u5f00\u773c\u775b\u5728\u770b\u7740\u5979\uff0c\u5979\u8138\u4e0a\u4eff\u4f5b\u4e5f\u71c3\u70e7\u4e86\u8d77\u6765\uff0c\u5624\u549b\u4e00\u58f0\uff0c\u5c06\u5934\u7f29\u5165\u4e86\u88ab\u91cc\u3002 \u3000\u3000\u53f6\u5f00\u5fc3\u91cc\u662f\u4ec0\u4e48\u6ecb\u5473\uff1f\u90a3\u7edd\u4e0d\u662f\u611f\u6fc0\u4e24\u4e2a\u5b57\u6240\u80fd\u5f62\u5bb9\u7684\uff0c\u90a3\u5df2\u4e0d\u662f\u4efb\u4f55\u8a00\u8bed\u6240\u80fd\u5f62\u5bb9 \u7684\uff0c\u4ed6\u611f\u89c9\u5230\u5979\u7684\u8eab\u5b50\u4e5f\u5728\u8f7b\u8f7b\u53d1\u6296\uff0c\u4f46\u90a3\u4e5f\u5f53\u7136\u4e0d\u662f\u56e0\u4e3a\u51b7\u3002 \u3000\u3000\u7a97\u5916\u4e00\u7247\u9ed1\u6697\uff0c\u51b7\u98ce\u5728\u9ed1\u6697\u4e2d\u547c\u5578\uff0c\u53ef\u662f\u9ed1\u6697\u4e0e\u5bd2\u51b7\u90fd\u5df2\u8ddd\u79bb\u4ed6\u4eec\u5f88\u8fdc\uff0c\u4ed6\u4eec\u7adf\u5ffd\u7136\u6709 \u4e86\u4e00\u4e2a\u5b8c\u5168\u5c5e\u4e8e\u4ed6\u4eec\u81ea\u5df1\u7684\u4e16\u754c\u3002\u8fd9\u4e16\u754c\u91cc\u5145\u6ee1\u4e86\u5e78\u798f\u548c\u5b81\u9759\u3002\u53ea\u53ef\u60dc\u8fd9\u79cd\u5e78\u798f\u5c31\u50cf\u662f\u6d77\u5e02 \u8703\u697c\uff0c\u867d\u7f8e\u4e3d\uff0c\u5374\u865a\u5e7b\uff0c\u53c8\u50cf\u662f\u91ce\u82b1\u7684\u5f00\u653e\uff0c\u867d\u7f8e\u4e3d\u5374\u77ed\u6682\u3002\u7a81\u7136\u95f4\uff0c\u95e8\u88ab\u63a8\u5f00\uff0c\u4e00\u4e2a\u4eba\u95ef \u4e86\u8fdb\u6765\u3002 \u3000\u3000\u4e00\u4e2a\u4ed6\u4eec\u6c38\u8fdc\u4e5f\u60f3\u4e0d\u5230\u7684\u4eba\u3002 \u3000\u3000\u706f\u8fd8\u6ca1\u6709\u706d\u3002 \u3000\u3000\u706f\u5149\u7167\u5728\u8fd9\u4eba\u8138\u4e0a\uff0c\u8fd9\u4eba\u7684\u8138\u8272\u662f\u94c1\u9752\u7684\uff0c\u773c\u775b\u91cc\u4e5f\u5145\u6ee1\u4e86\u6124\u6012\u7684\u6740\u6c14\uff0c\u6068\u6068\u5730\u77aa\u7740\u4ed6 \u4eec\uff0c\u4eff\u4f5b\u6068\u4e0d\u5f97\u4e00\u5200\u5c06\u4ed6\u4eec\u6740\u6b7b\u5728\u5e8a\u4e0a\uff0c\u4ed6\u4eec\u5374\u4e0d\u8ba4\u5f97\u8fd9\u4e2a\u4eba\uff0c\u8fde\u89c1\u90fd\u6ca1\u6709\u89c1\u8fc7\u3002 \u3000\u3000\u5d14\u7389\u771f\u5df2\u5931\u58f0\u5927\u53eb\uff1a\u201c\u4f60\u662f\u4ec0\u4e48\u4eba\uff0c\u4e3a\u4ec0\u4e48\u95ef\u5230\u8fd9\u91cc\u6765\uff1f\u201d \u3000\u3000\u8fd9\u4eba\u6068\u6068\u5730\u77aa\u7740\u5979\uff0c\u7a81\u7136\u51b7\u7b11\u9053\uff1a\u201c\u8fd9\u662f\u6211\u7684\u5bb6\uff0c\u6211\u4e3a\u4ec0\u4e48\u4e0d\u80fd\u6765\uff1f\u201d \u3000\u3000\u5d14\u7389\u771f\u6014\u4f4f\uff0c\u53f6\u5f00\u4e5f\u6014\u4f4f\u3002 \u3000\u3000\u8fd9\u4e00\u5bb6\u4e3b\u4eba\u7ade\u7a81\u7136\u56de\u6765\u4e86\u3002\u4e00\u4e2a\u7537\u4eba\u56de\u5230\u81ea\u5df1\u5bb6\u91cc\u65f6\uff0c\u82e5\u53d1\u73b0\u6709\u4e24\u4e2a\u964c\u751f\u7537\u5973\u7761\u5728\u81ea\u5df1 \u5e8a\u4e0a\uff0c\u65e0\u8bba\u600e\u4e48\u6124\u6012\uff0c\u90fd\u662f\u503c\u5f97\u540c\u60c5\u7684\uff0c\u5d14\u7389\u771f\u672c\u6765\u4e5f\u5f88\u5403\u60ca\uff0c\u5f88\u6124\u6012\uff0c\u73b0\u5728\u5374\u50cf\u662f\u53ea\u6cc4\u4e86 \u6c14\u7684\u76ae\u7403\uff0c\u8fde\u8bdd\u90fd\u8bf4\u4e0d\u51fa\u4e86\u3002 \u3000\u3000\u8fd9\u4eba\u54ac\u7259\u77aa\u4f4f\u5979\uff0c\u6012\u543c\u9053\uff1a\u201c\u6211\u51fa\u53bb\u624d\u4e24\u4e2a\u6708\uff0c\u4f60\u5c31\u6562\u5728\u5bb6\u91cc\u5077\u4eba\u4e86\uff0c\u4f60\u96be\u9053\u4e0d\u6015\u6211\u5bb0 \u4e86\u4f60\uff1f\u201d \u3000\u3000\u5d14\u7389\u771f\u53c8\u5403\u4e86\u4e00\u60ca\uff1a\u201c\u4f60\u2026\u2026\u4f60\u8bf4\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u6211\u95ee\u4f60\uff0c\u4f60\u4e3a\u4ec0\u4e48\u8981\u505a\u8fd9\u79cd\u89c1\u4e0d\u5f97\u4eba\u7684\u4e8b\uff0c\u8fd9\u91ce\u7537\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u96be\u9053\u8fd9\u4eba\u7684\u773c\u775b\u6709\u6bdb\u75c5\uff0c\u7adf\u5c06\u5979\u770b\u6210\u4e86\u81ea\u5df1\u7684\u59bb\u5b50\uff1f \u3000\u3000\u5d14\u7389\u771f\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u662f\u4e0d\u662f\u770b\u9519\u4eba\u4e86\uff1f\u201d \u3000\u3000\u8fd9\u4eba\u66f4\u6124\u6012\uff1a\u201c\u6211\u770b\u9519\u4e86\u4eba\uff1f\u4f60\u5341\u516d\u5c81\u5c31\u5ac1\u7ed9\u4e86\u6211\uff0c\u5c31\u7b97\u70e7\u6210\u4e86\u7070\uff0c\u6211\u4e5f\u8ba4\u5f97\u4f60\u3002\u201d \u3000\u3000\u5d14\u7389\u771f\u5fcd\u4e0d\u4f4f\u5927\u53eb\uff1a\u201c\u4f60\u75af\u4e86\uff0c\u6211\u8fde\u89c1\u90fd\u6ca1\u6709\u89c1\u8fc7\u4f60\u3002\u201d \u3000\u3000\u201c\u4f60\u96be\u9053\u8fd8\u6562\u4e0d\u627f\u8ba4\u662f\u6211\u7684\u8001\u5a46\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u4e0d\u662f\u3002\u201d \u3000\u3000\u201c\u4f60\u82e5\u4e0d\u662f\u6211\u7684\u8001\u5a46\uff0c\u4e3a\u4ec0\u4e48\u7761\u5728\u6211\u7684\u5e8a\u4e0a\uff1f\u201d \u3000\u3000\u5d14\u7389\u771f\u53c8\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u8fd9\u4eba\u53c8\u77aa\u7740\u53f6\u5f00\uff0c\u72e0\u72e0\u9053\uff1a\u201c\u4f60\u53c8\u662f\u4ec0\u4e48\u4e1c\u897f\uff1f\u4e3a\u4ec0\u4e48\u548c\u6211\u8001\u5a46\u7761\u5728\u5e8a\u4e0a\uff1f\u201d \u3000\u3000\u53f6\u5f00\u4e5f\u4e0d\u77e5\u8be5\u8bf4\u4ec0\u4e48\uff0c\u4ed6\u5ffd\u7136\u53d1\u73b0\u53c8\u9047\u7740\u4e86\u4ef6\u53c8\u8352\u5510\u53c8\u8352\u8c2c\u7684\u4e8b\uff0c\u4ed6\u5b9e\u5728\u4e0d\u77e5\u9053\u7a76\u7adf\u662f \u600e\u4e48\u56de\u4e8b\u3002 \u3000\u3000\u8fd9\u4eba\u9053\uff1a\u201c\u5e78\u597d\u6211\u662f\u4e2a\u5bbd\u5927\u4e3a\u6000\u7684\u4eba\uff0c\u4e0d\u7ba1\u4f60\u4eec\u505a\u4e86\u4ec0\u4e48\u4e8b\uff0c\u6211\u90fd\u539f\u8c05\u4e86\u4f60\u4eec\uff0c\u4f46\u73b0\u5728 \u6211\u65e2\u7136\u5df2\u56de\u6765\u4e86\uff0c\u4f60\u603b\u8be5\u8d77\u6765\u628a\u8fd9\u70ed\u88ab\u7a9d\u8ba9\u7ed9\u6211\u4e86\u5427\uff1f\u201d \u3000\u3000\u4ed6\u5c45\u7136\u771f\u7684\u8d70\u8fc7\u6765\uff0c\u597d\u50cf\u5df2\u51c6\u5907\u8131\u8863\u4e0a\u5e8a\u7761\u89c9\u3002 \u3000\u3000\u5d14\u7389\u771f\u53c8\u5927\u53eb\uff0c\u7528\u529b\u62e6\u4f4f\u53f6\u5f00\uff1a\u201c\u6211\u4e0d\u662f\u4ed6\u7684\u8001\u5a46\uff0c\u6211\u6839\u672c\u4e0d\u8ba4\u5f97\u4ed6\uff0c\u4f60\u5343\u4e07\u4e0d\u80fd\u8d77\u6765 \u8ba9\u4ed6\u3002\u201d \u3000\u3000\u53f6\u5f00\u5f53\u7136\u4e0d\u4f1a\u8d77\u6765\uff0c\u53ef\u662f\u4ed6\u8be5\u600e\u4e48\u529e\u5462\uff1f \u3000\u3000\u4e00\u4e2a\u4eba\u8d64\u88f8\u88f8\u5730\u8eba\u5728\u522b\u4eba\u5e8a\u4e0a\uff0c\u9047\u89c1\u8fd9\u79cd\u4e8b\uff0c\u4f60\u8bf4\u4ed6\u600e\u4e48\u529e\uff1f\u5c31\u5728\u8fd9\u65f6\uff0c\u7a81\u7136\u95e8\u5916\u4f20\u5165 \u4e86\u4e00\u9635\u5927\u7b11\u58f0\uff0c\u4e00\u4e2a\u4eba\u6367\u7740\u809a\u5b50\uff0c\u5927\u7b11\u7740\u8d70\u4e86\u8fdb\u6765\u3002\u770b\u89c1\u4e86\u8fd9\u4e2a\u4eba\uff0c\u53f6\u5f00\u66f4\u7b11\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\uff01\u8fd9\u4e2a\u8981\u547d\u7684\u4eba\uff0c\u7adf\u504f\u504f\u53c8\u5728\u8fd9\u79cd\u8981\u547d\u7684\u65f6\u5019\u51fa\u73b0\u4e86\u3002 \u7b2c\u5341\u56db\u7ae0\u3000\u593a\u547d\u98de\u5200>> \u53e4\u9f99\u300a\u4e5d\u6708\u9e70\u98de\u300b \u7b2c\u5341\u56db\u7ae0\u3000\u593a\u547d\u98de\u5200 \u3000\u3000\u6709\u79cd\u4eba\u4f60\u60f3\u627e\u4ed6\u7684\u65f6\u5019\uff0c\u6253\u7834\u5934\u4e5f\u627e\u4e0d\u5230\uff0c\u4f60\u4e0d\u60f3\u89c1\u4ed6\u7684\u65f6\u5019\uff0c\u4ed6\u5374\u504f\u504f\u4f1a\u5ffd\u7136\u51fa\u73b0\u5728 \u4f60\u7684\u773c\u524d\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u597d\u50cf\u5c31\u662f\u8fd9\u79cd\u4eba\u3002 \u3000\u3000\u5979\u4e00\u53ea\u624b\u6367\u7740\u809a\u5b50\uff0c\u4e00\u53ea\u624b\u6307\u7740\u53f6\u5f00\uff0c\u5403\u5403\u5730\u7b11\u9053\uff1a\u201c\u4f60\u5360\u4e86\u4eba\u5bb6\u7684\u5c4b\u5b50\uff0c\u53c8\u5360\u4e86\u4eba\u5bb6 \u7684\u5e8a\uff0c\u4eba\u5bb6\u56de\u6765\uff0c\u4ec0\u4e48\u8bdd\u90fd\u4e0d\u8bf4\uff0c\u53ea\u4e0d\u8fc7\u53eb\u4f60\u8ba9\u5f00\uff0c\u4f60\u90fd\u4e0d\u80af\uff0c\u8fd9\u672a\u514d\u592a\u4e0d\u50cf\u8bdd\u4e86\u5427\u3002\u201d \u3000\u3000\u8bdd\u6ca1\u6709\u8bf4\u5b8c\uff0c\u5979\u5df2\u7b11\u51fa\u4e86\u773c\u6cea\uff0c\u7b11\u5f2f\u4e86\u8170\u3002 \u3000\u3000\u53f6\u5f00\u53cd\u800c\u6c89\u4f4f\u4e86\u6c14\u3002\u73b0\u5728\u4ed6\u603b\u7b97\u5df2\u660e\u767d\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\u4e86\u3002 \u3000\u3000\u8fd9\u5973\u4eba\u4e0d\u4f46\u662f\u6761\u72d0\u72f8\uff0c\u7b80\u76f4\u662f\u4e2a\u9b3c\uff0c\u7b80\u76f4\u4ec0\u4e48\u4e8b\u90fd\u505a\u5f97\u51fa\uff0c\u4ec0\u4e48\u82b1\u6837\u90fd\u60f3\u5f97\u51fa\u6765\u3002 \u3000\u3000\u5d14\u7389\u771f\u5403\u60ca\u5730\u770b\u7740\u5979\uff0c\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u5979\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u5979\u4e0d\u662f\u4eba\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u7b11\u9053\uff1a\u201c\u5bf9\u4e86\uff0c\u6211\u672c\u6765\u5c31\u4e0d\u662f\u4eba\uff0c\u6211\u662f\u4e2a\u6d3b\u795e\u4ed9\uff0c\u65e0\u8bba\u4f60\u85cf\u5230\u4ec0\u4e48\u5730\u65b9\u53bb\uff0c\u6211 \u8fd8\u662f\u4e00\u627e\u5c31\u627e\u5230\u3002\u201d \u3000\u3000\u53f6\u5f00\u5e76\u6ca1\u6709\u95ee\u5979\u662f\u600e\u4e48\u627e\u5230\u7684\u3002 \u3000\u3000\u5979\u663e\u7136\u4e00\u76f4\u90fd\u5728\u6697\u4e2d\u76ef\u7740\u53f6\u5f00\uff0c\u5c31\u50cf\u662f\u4e2a\u9b3c\u5f71\u5b50\u4e00\u6837\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u53ef\u662f\u6211\u5012\u771f\u6ca1\u6709\u60f3\u5230\uff0c\u9053\u58eb\u59d1\u5a18\u4f1a\u628a\u4f60\u5f04\u5230\u8fd9\u4e48\u6837\u4e00\u4e2a\u5730\u65b9\uff0c\u8981\u4e0d\u662f\u5979 \u6025\u7740\u66ff\u4f60\u53bb\u6293\u836f\uff0c\u8fd9\u6b21\u6211\u771f\u7684\u5dee\u70b9\u627e\u4e0d\u5230\u4f60\u4e86\u3002\u201d \u3000\u3000\u5979\u8d70\u8fc7\u53bb\uff0c\u62ff\u8d77\u5e8a\u5934\u7684\u7a7a\u836f\u7897\u55c5\u4e86\u55c5\uff0c\u53c8\u7b11\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u5979\u5b9e\u5728\u4e0d\u80fd\u7b97\u662f\u4e2a\u597d\u5927\u592b\uff0c\u8fd9 \u79cd\u836f\u4f60\u5c31\u7b97\u559d\u516b\u767e\u65a4\u4e0b\u53bb\uff0c\u4e5f\u4e00\u6837\u6ca1\u6709\u7528\u3002\u201d \u3000\u3000\u5d14\u7389\u771f\u5df2\u6c14\u5f97\u6ee1\u8138\u901a\u7ea2\uff0c\u5374\u8fd8\u662f\u5fcd\u4e0d\u4f4f\u8981\u95ee\uff1a\u201c\u4f60\u80fd\u6cbb\u597d\u4ed6\u7684\u4f24\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u662f\u4e2a\u597d\u5927\u592b\uff0c\u53ef\u662f\u6211\u5374\u66ff\u4ed6\u8bf7\u4e86\u4e00\u4e2a\u6700\u597d\u7684\u5927\u592b\u6765\u3002\u201d \u3000\u3000\u521a\u624d\u90a3\u4e2a\u6124\u6012\u7684\u4e08\u592b\uff0c\u73b0\u5728\u5df2\u8fde\u4e00\u70b9\u706b\u6c14\u90fd\u6ca1\u6709\u4e86\uff0c\u6b63\u770b\u7740\u4ed6\u4eec\u5fae\u7b11\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u8fd9\u4f4d\u5c31\u662f\u6614\u5e74\u5999\u624b\u795e\u533b\u7684\u552f\u4e00\u4f20\u4eba\u2018\u5999\u624b\u90ce\u4e2d\u2019\u534e\u5b50\u6e05\uff0c\u4f60\u89c1\u591a\u8bc6\u5e7f\uff0c \u60f3\u5fc5\u4e00\u5b9a\u77e5\u9053\u4ed6\u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u7684\u786e\u77e5\u9053\u3002 \u3000\u3000\u534e\u5bb6\u7236\u5b50\u7684\u786e\u90fd\u662f\u6c5f\u6e56\u4e2d\u6709\u540d\u7684\u795e\u533b\uff0c\u533b\u6cbb\u5916\u4f24\uff0c\u66f4\u6709\u72ec\u95e8\u7684\u4f20\u6388\u3002 \u3000\u3000\u53ef\u662f\u8fd9\u7236\u5b50\u4e24\u4eba\u90fd\u6709\u540c\u6837\u7684\u6bdb\u75c5\uff0c\u5077\u75c5\u4eba\u3002 \u3000\u3000\u4ed6\u4eec\u6839\u672c\u4e0d\u9700\u8981\u53bb\u5077\u7684\uff0c\u53ef\u662f\u4ed6\u4eec\u5929\u751f\u7684\u559c\u6b22\u5077\uff0c\u65e0\u8bba\u4ec0\u4e48\u90fd\u5077\u3002 \u3000\u3000\u53bb\u627e\u4ed6\u4eec\u6cbb\u4f24\u533b\u75c5\u7684\u4eba\uff0c\u5f80\u5f80\u4f1a\u62ab\u4ed6\u4eec\u5077\u5f97\u5e72\u5e72\u51c0\u51c0\u3002\u201c\u5999\u624b\u201d\u8fd9\u4e24\u4e2a\u5b57\uff0c\u5c31\u662f\u8fd9\u6837\u6765 \u7684\u3002 \u3000\u3000\u53f6\u5f00\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u9601\u4e0b\u975e\u4f46\u533b\u9053\u9ad8\u660e\uff0c\u800c\u4e14\u8fd8\u5f88\u4f1a\u505a\u620f\u3002\u201d \u3000\u3000\u534e\u5b50\u6e05\u4e5f\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u70b9\u4f60\u5c31\u4e0d\u61c2\u4e86\uff0c\u8981\u5b66\u5077\uff0c\u5c31\u4e00\u5b9a\u8981\u5b66\u4f1a\u505a\u620f\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u534e\u5b50\u6e05\u9053\uff1a\u201c\u56e0\u4e3a\u4f60\u4e00\u5b9a\u8981\u5b66\u4f1a\u626e\u6210\u5404\u5f0f\u5404\u6837\u7684\u4eba\uff0c\u624d\u80fd\u5230\u5404\u5730\u65b9\u53bb\u5077\u5404\u5f0f\u5404\u6837\u7684\u4e1c \u897f\u3002\u201d\u4ed6\u5fae\u7b11\u7740\u53c8\u9053\uff1a\u201c\u8b6c\u5982\u8bf4\uff0c\u4f60\u82e5\u8981\u5230\u5e99\u91cc\u53bb\u5077\u7ecf\uff0c\u5c31\u4e00\u5b9a\u5f97\u626e\u6210\u548c\u5c1a\uff0c\u82e5\u8981\u53bb\u5077\u7a91 \u5b50\uff0c\u5c31\u4e00\u5b9a\u8981\u626e\u6210\u5ad6\u5ba2\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u82e5\u8981\u5230\u5927\u5b57\u53f7\u7684\u5e97\u5bb6\u53bb\u5077\uff0c\u5c31\u4e00\u5b9a\u5f97\u5148\u626e\u6210\u5927\u8001\u677f\u7684\u6837\u5b50\u53bb\u8e29\u9053\u3002\u201d \u3000\u3000\u534e\u5b50\u6e05\u629a\u638c\u9053\uff1a\u201c\u9601\u4e0b\u5f53\u771f\u662f\u4e3e\u4e00\u53cd\u4e09\uff0c\u4e00\u70b9\u5c31\u900f\uff0c\u82e5\u8981\u5b66\u8fd9\u4e00\u884c\uff0c\u6211\u6562\u4fdd\u8bc1\u4e0d\u51fa\u4e09\u4e2a \u6708\uff0c\u5c31\u53ef\u4ee5\u6210\u4e3a\u4e13\u5bb6\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u5ae3\u7136\u9053\uff1a\u201c\u4ed6\u73b0\u5728\u5c31\u5df2\u7ecf\u662f\u4e2a\u4e13\u5bb6\uff0c\u6240\u4ee5\u4f60\u53bb\u66ff\u4ed6\u6cbb\u4f24\u7684\u65f6\u5019\uff0c\u6700\u597d\u5c0f\u5fc3\u70b9\uff0c \u5426\u5219\u4f60\u8bf4\u4e0d\u5b9a\u53cd\u800c\u4f1a\u88ab\u4ed6\u7ed9\u5077\u5f97\u5e72\u5e72\u51c0\u51c0\u3002\u201d \u3000\u3000\u534e\u5b50\u6e05\u7b11\u9053\uff1a\u201c\u6211\u5077\u4eba\u5bb6\u5df2\u5077\u4e86\u51e0\u5341\u5e74\uff0c\u80fd\u88ab\u522b\u4eba\u5077\u4e00\u6b21\uff0c\u5012\u4e5f\u6709\u8da3\u3002\u201d\u4ed6\u5fae\u7b11\u7740\u8d70\u8fc7 \u53bb\uff0c\u53c8\u9053\uff1a\u201c\u53ea\u8981\u5200\u4e0a\u6ca1\u6709\u6bd2\uff0c\u6211\u4e5f\u6562\u4fdd\u8bc1\uff0c\u4e0d\u51fa\u4e09\u5929\uff0c\u9601\u4e0b\u5c31\u53c8\u53ef\u4ee5\u53bb\u6740\u4eba\u4e86\u3002\u201d \u3000\u3000\u5d14\u7389\u771f\u5ffd\u7136\u5927\u58f0\u9053\uff1a\u201c\u7b49\u4e00\u7b49\u3002\u201d \u3000\u3000\u534e\u5b50\u6e05\u9053\uff1a\u201c\u8fd8\u7b49\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5d14\u738b\u771f\u9053\uff1a\u201c\u6211\u600e\u4e48\u77e5\u9053\u4f60\u662f\u771f\u7684\u6765\u66ff\u4ed6\u6cbb\u4f24\u7684\uff1f\u201d \u3000\u3000\u4e0a\u5bab\u5c0f\u4ed9\u6253\u65ad\u5979\u7684\u8bdd\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u73b0\u5728\u6211\u82e5\u8981\u6740\u4ed6\uff0c\u7b80\u76f4\u6bd4\u5403\u8c46\u8150\u8fd8\u5bb9\u6613\uff0c\u6211\u4f55\u5fc5\u8d39\u8fd9 \u4e48\u5927\u7684\u4e8b\uff1f\u201d \u3000\u3000\u5d14\u738b\u771f\u51b7\u7b11\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff0c\u201c\u4f60\u4e0d\u4fe1\uff1f\u201d \u3000\u3000\u5d14\u738b\u771f\u8fd8\u662f\u5728\u51b7\u7b11\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u8eab\u5b50\u7a81\u7136\u8f7b\u98d8\u98d8\u98de\u8d77\uff0c\u5c31\u50cf\u662f\u4e00\u6735\u4e91\u4e00\u6837\uff0c\u98d8\u8fc7\u4e86\u4ed6\u4eec\u7684\u5934\u9876\uff0c\u5d14\u7389\u771f\u53ea\u89c9\u5f97 \u7a81\u7136\u6709\u53ea\u51b0\u51b7\u7684\u624b\u4f38\u8ff8\u4e86\u88ab\u7a9d\uff0c\u5728\u5979\u7684\u80f8\u819b\u4e0a\u8f7b\u8f7b\u634f\u4e86\u4e00\u628a\uff0c\u518d\u770b\u4e0a\u5b98\u5c0f\u4ed9\u53c8\u5df2\u8f7b\u98d8\u98d8\u5730\u98de \u4e86\u56de\u53bb\uff0c\u7ad9\u5728\u539f\u6765\u7684\u5730\u65b9\uff0c\u7b11\u563b\u563b\u5730\u770b\u7740\u5979\uff1a\u201c\u542c\u8bf4\u4e1c\u6d77\u7389\u7bab\u4f1a\u91c7\u8865\uff0c\u53ef\u662f\u4f60\u8eab\u4e0a\u5012\u8fd8\u5f88\u7ed3 \u5b9e\uff0c\u770b\u6765\u4f60\u5bf9\u4ed8\u7537\u4eba\u60f3\u5fc5\u4e5f\u5f88\u6709\u4e00\u5957\u3002\u201d \u3000\u3000\u5d14\u7389\u771f\u8138\u4e0a\u4e00\u9635\u7ea2\uff0c\u4e00\u9635\u9752\uff0c\u6c14\u5f97\u51e0\u4e4e\u5df2\u7ecf\u5feb\u54ed\u4e86\u51fa\u6765\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u60a0\u7136\u9053\uff1a\u201c\u8fd9\u672c\u662f\u5973\u4eba\u503c\u5f97\u9a84\u505a\u7684\u4e8b\uff0c\u6709\u4ec0\u4e48\u597d\u96be\u4e3a\u60c5\u7684\uff0c\u51e0\u65f6\u6709\u7a7a\uff0c\u8bf4\u4e0d\u5b9a \u6211\u4e5f\u8981\u8ddf\u4f60\u5b66\u4e24\u624b\u3002\u201d \u3000\u3000\u5d14\u738b\u771f\u7684\u8138\u8272\u5df2\u53d1\u767d\uff0c\u5979\u77e5\u9053\u8fd9\u5973\u4eba\u662f\u5728\u5b58\u5fc3\u4fae\u8fb1\u5979\uff0c\u53ef\u662f\u5979\u53ea\u6709\u5fcd\u53d7\u3002\u4e3a\u4ec0\u4e48\u4eba\u4eec\u603b \u662f\u8981\u4e3a\u5df2\u7ecf\u8fc7\u53bb\u4e86\u7684\u4e8b\uff0c\u4ed8\u51fa\u75db\u82e6\u7684\u4ee3\u4ef7\u5462\uff1f \u3000\u3000\u4e3a\u4ec0\u4e48\u6709\u4e9b\u4eba\u4e00\u5b9a\u8981\u8ba9\u522b\u4eba\u89c9\u5f97\u75db\u82e6\uff0c\u81ea\u5df1\u624d\u611f\u89c9\u5230\u5feb\u4e50\uff1f\u5d14\u7389\u771f\u6cea\u5df2\u6d41\u4e0b\uff0c\u4e0a\u5b98\u5c0f\u4ed9 \u8138\u4e0a\u5374\u9732\u51fa\u4e86\u80dc\u5229\u7684\u5fae\u7b11\u3002 \u3000\u3000\u53f6\u5f00\u5ffd\u7136\u9053\uff1a\u201c\u6eda\u51fa\u53bb\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u597d\u50cf\u5403\u4e86\u4e00\u60ca\uff1a\u201c\u4f60\u53eb\u8c01\u6eda\u51fa\u53bb\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\uff01\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6211\u597d\u5fc3\u597d\u610f\u8bf7\u4e86\u4eba\u6765\u66ff\u4f60\u6cbb\u4f24\uff0c\u4f60\u5374\u53eb\u6211\u6eda\u51fa\u53bb\u3002\u201d \u3000\u3000\u53f6\u5f00\u5bd2\u7740\u8138\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u53eb\u4f60\u6eda\u51fa\u53bb\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u8138\u8272\u4e5f\u6709\u70b9\u53d8\u4e86\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u96be\u9053\u4e0d\u6015\u6211\u6740\u4e86\u4f60\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u4ee5\u4e3a\u4f60\u771f\u7684\u80fd\u6740\u6211\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4f60\u4e5f\u4e0d\u4fe1\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u53ea\u60f3\u63d0\u9192\u4f60\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u4ed6\u7684\u624b\u6162\u6162\u5730\u4ece\u88ab\u4e0b\u4f38\u51fa\uff0c\u624b\u91cc\u8d6b\u7136\u6709\u67c4\u5200\uff0c\u4e09\u5bf8\u4e03\u5206\u957f\u7684\u5200\uff0c\u98de\u5200\uff01 \u3000\u3000\u8584\u800c\u5229\u7684\u5200\u950b\uff0c\u5728\u706f\u4e0b\u95ea\u95ea\u53d1\u5149\u3002\u4e0a\u5b98\u5c0f\u4ed9\u7684\u8138\u4f3c\u5df2\u88ab\u5200\u5149\u6620\u6210\u4e86\u94c1\u9752\u8272\uff0c\u534e\u5b50\u6e05\u7684\u8138 \u4f3c\u5df2\u53d1\u7eff\u3002\u5c0f\u674e\u98de\u5200\uff01\u8fd9\u5c31\u662f\u4ece\u5c0f\u674e\u63a2\u82b1\u4e00\u8109\u76f8\u4f20\u4e0b\u6765\u7684\u98de\u5200\uff01\u8fd9\u5c31\u662f\u4ece\u4e0d\u865a\u53d1\u7684\u98de\u5200\uff0c\u6c5f \u6e56\u4e2d\u65e0\u8bba\u591a\u53ef\u6015\u7684\u9ad8\u624b\uff0c\u90fd\u4ece\u6765\u4e5f\u6ca1\u6709\u4eba\u80fd\u8eb2\u8fc7\u8fd9\u51fa\u624b\u4e00\u5200\u3002\u53f6\u5f00\u51b7\u51b7\u9053\uff1a\u201c\u6211\u672c\u6765\u4e0d\u613f\u6740 \u4eba\u7684\uff0c\u53ef\u662f\u4f60\u6700\u597d\u83ab\u8981\u903c\u6211\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u73b0\u5728\u8fd8\u80fd\u6740\u4eba\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u60f3\u8bd5\u8bd5\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u4e5f\u4e0d\u6562\u53bb\u8bd5\u3002 \u3000\u3000\u6ca1\u6709\u4eba\u6562\uff01\u6ca1\u6709\u4eba\u6562\u7528\u81ea\u5df1\u7684\u751f\u547d\u4f5c\u8d4c\u6ce8\uff0c\u6765\u4f5c\u8fd9\u51e0\u4e4e\u5df2\u8f93\u5b9a\u4e86\u7684\u5b64\u6ce8\u4e00\u63b7\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u957f\u957f\u5438\u4e86\u53e3\u6c14\uff0c\u52c9\u5f3a\u7b11\u9053\uff1a\u201c\u96be\u9053\u4f60\u4e0d\u60f3\u4f60\u7684\u4f24\u5feb\u597d\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u53ea\u60f3\u8981\u4f60\u6eda\u51fa\u53bb\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u4e0d\u4f1a\u6eda\uff0c\u6211\u8d70\u51fa\u53bb\u884c\u4e0d\u884c\uff1f\u201d \u3000\u3000\u5979\u771f\u7684\u8bf4\u8d70\u5c31\u8d70\uff0c\u534e\u5b50\u6e05\u5f53\u7136\u8d70\u5f97\u66f4\u5feb\uff0c\u8d70\u5230\u95e8\u53e3\uff0c\u5979\u5374\u7a81\u53c8\u56de\u5934\uff0c\u9053\uff1a\u201c\u6709\u4ef6\u4e8b\uff0c\u5dee \u70b9\u5fd8\u4e86\u544a\u8bc9\u4f60\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\u2019\uff1a\u201c\u4f60\u60f3\u4e0d\u60f3\u77e5\u9053\u90a3\u4f4d\u4e01\u59d1\u5a18\u73b0\u5728\u7684\u4e0b\u843d\uff1f\u201d \u3000\u3000\u53f6\u5f00\u4e0d\u8bf4\u8bdd\u4e86\uff0c\u4ed6\u5f53\u7136\u60f3\u77e5\u9053\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u5979\u73b0\u5728\u6b63\u548c\u90ed\u5b9a\u5728\u4e00\u8d77\uff0c\u548c\u4f60\u4eec\u4e00\u6837\uff0c\u4e5f\u7761\u5728\u4e00\u5f20\u5e8a\u4e0a\u3002\u201d \u3000\u3000\u53f6\u5f00\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u8981\u5728\u6211\u9762\u524d\u8bf4\u8fd9\u79cd\u8bdd\uff0c\u4f60\u660e\u77e5\u6ca1\u6709\u7528\u7684\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u60a0\u7136\u9053\uff1a\u201c\u4f60\u4e0d\u4fe1\u4ed6\u4eec\u4f1a\u505a\u8fd9\u79cd\u4e8b\uff1f\u201d \u3000\u3000\u53f6\u5f00\u5f53\u7136\u4e0d\u4fe1\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u60a0\u7136\u9053\uff1a\u201c\u4ed6\u4eec\u672c\u6765\u4e5f\u8bb8\u4f1a\u5bf9\u4f60\u5f88\u5fe0\u5b9e\u7684\uff0c\u53ef\u662f\uff0c\u5047\u5982\u4e01\u59d1\u5a18\u4e5f\u51b7\u5f97\u8981\u547d\uff0c\u90ed \u5b9a\u4e5f\u50cf\u8fd9\u4f4d\u9053\u58eb\u59d1\u5a18\u4e00\u6837\u597d\u5fc3\u5462\uff1f\u5047\u5982\u4e01\u59d1\u5a18\u8eab\u4e0a\u6709\u4e2a\u89c1\u4e0d\u5f97\u4eba\u7684\u5730\u65b9\uff0c\u4e2d\u4e86\u4ec0\u4e48\u6bd2\uff0c\u90ed\u5b9a \u4e3a\u4e86\u6551\u5979\uff0c\u662f\u4e0d\u662f\u4f1a\u66ff\u5979\u542e\u51fa\u6765\u5462\uff1f\u201d \u3000\u3000\u53f6\u5f00\u7684\u8138\u8272\u4e5f\u53d8\u4e86\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u53c8\u9732\u51fa\u80dc\u5229\u7684\u5fae\u7b11\uff0c\u633d\u8d77\u534e\u5b50\u6e05\u7684\u624b\uff0c\u7b11\u9053\uff0c\u201c\u4ed6\u5bf9\u6211\u867d\u7136\u65e0\u60c5\uff0c\u6211\u5374\u4e0d\u80fd\u5bf9 \u4ed6\u65e0\u4e49\uff0c\u7559\u4e0b\u4e00\u5305\u836f\u7ed9\u4ed6\uff0c\u6211\u4eec\u8d70\u3002\u201d \u3000\u3000\u8fd9\u6b21\u5979\u603b\u7b97\u771f\u7684\u8d70\u4e86\u3002 \u3000\u3000\u53f6\u5f00\u672c\u5df2\u5750\u8d77\u6765\uff0c\u73b0\u5728\u5ffd\u7136\u5012\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u5d14\u7389\u771f\u51fa\u58f0\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u600e\u6837\u4e86\uff1f\u201d \u3000\u3000\u53f6\u5f00\u53f9\u4e86\u53e3\u6c14\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u5e78\u597d\u4f60\u5c06\u6211\u7684\u5200\u653e\u5728\u6795\u4e0b\uff0c\u5e78\u597d\u5979\u6ca1\u6709\u8bd5\u3002\u201d \u3000\u3000\u5d14\u7389\u771f\u9053\uff1a\u201c\u4f60\u521a\u624d\u6839\u672c\u65e0\u529b\u4f24\u5979\u3002\u201d \u3000\u3000\u53f6\u5f00\u770b\u7740\u624b\u91cc\u7684\u5200\uff0c\u8138\u4e0a\u8868\u60c5\u53d8\u5f97\u5f88\u4e25\u8083\uff0c\u9053\uff1a\u201c\u8fd9\u628a\u5200\u5e76\u4e0d\u662f\u53ea\u7528\u624b\u5c31\u53ef\u4ee5\u53d1\u51fa\u53bb \u7684\u3002\u8981\u7528\u5168\u8eab\u6240\u6709\u7684\u7cbe\u795e\u548c\u529b\u91cf\uff0c\u624d\u80fd\u53d1\u51fa\u4e00\u5200\uff0c\u53ef\u662f\u6211\u73b0\u5728\u2026\u2026\u201d \u3000\u3000\u4ed6\u73b0\u5728\u5df2\u8fde\u8bf4\u8bdd\u90fd\u89c9\u5f97\u5f88\u5403\u529b\u3002 \u3000\u3000\u5d14\u7389\u771f\u770b\u7740\u4ed6\uff0c\u6cea\u53c8\u6d41\u4e0b\uff1a\u201c\u6211\u77e5\u9053\u4f60\u662f\u4e3a\u4e86\u6211\uff0c\u624d\u8d76\u5979\u8d70\u7684\uff0c\u53ef\u662f\u4f60\u4f55\u5fc5\u4e3a\u4e86\u6211\u5192\u8fd9 \u79cd\u9669\u2026\u2026\u6211\u672c\u5c31\u662f\u4e2a\u6d3b\u8be5\u53d7\u4fae\u8fb1\u7684\u4eba\u3002\u201d \u3000\u3000\u53f6\u5f00\u67d4\u58f0\u9053\uff1a\u201c\u6ca1\u6709\u4eba\u5e94\u8be5\u53d7\u4fae\u8fb1\uff1b\u4e5f\u6ca1\u6709\u4eba\u6709\u6743\u4fae\u8fb1\u522b\u4eba\u3002\u201d\u4ed6\u7684\u58f0\u97f3\u867d\u6e29\u67d4\uff0c\u5374\u5f88 \u575a\u51b3\uff1a\u201c\u4ed6\u8001\u4eba\u5bb6\u4f20\u6388\u6211\u8fd9\u67c4\u5200\uff0c\u53ea\u662f\u4e3a\u4e86\u8981\u6211\u8ba9\u5929\u4e0b\u7684\u4eba\u90fd\u660e\u767d\u8fd9\u9053\u7406\uff0c\u800c\u4e14\u83ab\u8981\u5fd8 \u8bb0\u3002\u201d \u3000\u3000\u5d14\u7389\u771f\u7684\u773c\u775b\u4e5f\u4eae\u4e86\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u6211\u60f3\u4ed6\u8001\u4eba\u5bb6\u4e00\u5b9a\u662f\u4e2a\u4e86\u4e0d\u8d77\u7684\u4eba\u3002\u201d \u3000\u3000\u53f6\u5f00\u76ee\u5149\u9065\u89c6\u5728\u8fdc\u65b9\uff0c\u5e26\u7740\u79cd\u8bf4\u4e0d\u51fa\u7684\u5b64\u5bc2\u4e4b\u8272\uff1a\u201c\u4ed6\u81ea\u5df1\u5e38\u8bf4\u4ed6\u53ea\u4e0d\u8fc7\u662f\u4e2a\u5f88\u5e73\u51e1\u7684 \u4eba\uff0c\u53ef\u662f\u4ed6\u505a\u7684\u4e8b\uff0c\u5374\u662f\u7edd\u6ca1\u6709\u4efb\u4f55\u4eba\u80fd\u505a\u5f97\u5230\u7684\u3002\u201d \u3000\u3000\u8fd9\u4e5f\u6b63\u662f\u674e\u5bfb\u6b22\u7684\u4f1f\u5927\u4e4b\u5904\uff0c\u6240\u4ee5\u4e0d\u7ba1\u4ed6\u5728\u4ec0\u4e48\u5730\u65b9\uff0c\u90fd\u6c38\u8fdc\u6d3b\u5728\u4eba\u4eec\u7684\u5fc3\u91cc\u3002 \u3000\u3000\u706f\u5149\u5df2\u6e10\u6e10\u5fae\u5f31\uff0c\u706f\u6cb9\u4f3c\u5df2\u5c06\u67af\u3002 \u3000\u3000\u5d14\u7389\u771f\u5ffd\u7136\u53c8\u957f\u957f\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u73b0\u5728\u6211\u53ea\u62c5\u5fc3\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u62c5\u5fc3\u5979\u4f1a\u5c06\u6211\u7684\u4e0b\u843d\u544a\u8bc9\u522b\u4eba\uff0c\u4f60\u62c5\u5fc3\u5979\u8fd8\u4f1a\u518d\u56de\u6765\uff1f\u201d \u3000\u3000\u5d14\u7389\u771f\u9053\uff1a\u201c\u55ef\uff01\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u5979\u4e0d\u4f1a\u8fd9\u6837\u505a\u7684\uff0c\u5979\u53ea\u5e0c\u671b\u6211\u7684\u4f24\u5feb\u597d\u3002\u201d \u3000\u3000\u5d14\u7389\u771f\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u56e0\u4e3a\u5979\u8981\u6211\u53bb\u66ff\u5979\u5bf9\u4ed8\u522b\u4eba\u3002\u201d \u3000\u3000\u5d14\u7389\u771f\u8fd8\u662f\u4e0d\u61c2\u3002 \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u90a3\u5929\u5979\u6545\u610f\u5c06\u7389\u7bab\u5f15\u53bb\u627e\u6211\uff0c\u4e3a\u7684\u5c31\u662f\u8981\u6211\u8ddf\u4ed6\u706b\u5e76\uff0c\u5979\u8fd8\u5e0c\u671b\u6211\u53bb\u66ff\u5979\u6740 \u90ed\u5b9a\uff0c\u6740\u4f0a\u591c\u54ed\uff0c\u6740\u6240\u6709\u53ef\u80fd\u4f1a\u6321\u4f4f\u8def\u7684\u4eba\u3002\u201d \u3000\u3000\u5d14\u7389\u771f\u9053\uff1a\u201c\u53ef\u662f\uff0c\u5979\u4e5f\u65e0\u6cd5\u77e5\u9053\uff0c\u4f60\u7edd\u4e0d\u4f1a\u53bb\u66ff\u5979\u6740\u4eba\u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u82e6\u7b11\u9053\uff1a\u201c\u53ea\u8981\u6211\u4eec\u62fc\u8d77\u6765\uff0c\u65e0\u8bba\u8c01\u80dc\u8c01\u8d1f\uff0c\u5979\u90fd\u53ef\u4ee5\u6e14\u7fc1\u5f97\u5229\u3002\u201d \u3000\u3000\u53f6\u5f00\u70b9\u70b9\u5934\u53c8\u9053\uff1a\u201c\u6240\u4ee5\u5979\u5e76\u4e0d\u5e0c\u671b\u6211\u53d7\u4f24\uff0c\u66f4\u4e0d\u5e0c\u671b\u8fd9\u4e48\u5feb\u5c31\u6b7b\u3002\u201d \u3000\u3000\u5d14\u7389\u771f\u53ea\u89c9\u5f97\u624b\u811a\u51b0\u51b7\uff0c\u5979\u5b9e\u5728\u60f3\u4e0d\u5230\u4e16\u4e0a\u7adf\u6709\u5982\u6b64\u9634\u9669\u6076\u6bd2\u7684\u5973\u4eba\u3002 \u3000\u3000\u53f6\u5f00\u76ee\u4e2d\u5e26\u7740\u6df1\u601d\u4e4b\u8272\uff0c\u5ffd\u7136\u53c8\u9053\uff1a\u201c\u6240\u4ee5\u6709\u4ef6\u4e8b\u6211\u66f4\u60f3\u4e0d\u901a\u3002\u201d \u3000\u3000\u5d14\u7389\u771f\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u53f6\u5f00\u6c89\u541f\u7740\uff0c\u9053\uff1a\u201c\u903c\u7740\u4f60\u5230\u51b7\u9999\u9662\u53bb\u5439\u7bab\u7684\u90a3\u4e2a\u4eba\uff0c\u53ef\u80fd\u5c31\u662f\u7389\u7bab\u6d3e\u53bb\u7684\u3002\u201d \u3000\u3000\u5d14\u7389\u771f\u6115\u7136\u9053\uff1a\u201c\u4ed6\u4e3a\u4ec0\u4e48\u8981\u505a\u8fd9\u79cd\u4e8b\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u56e0\u4e3a\u4ed6\u65e9\u5df2\u77e5\u9053\u4f60\u662f\u4e2a\u672c\u6027\u5f88\u5584\u826f\u7684\u4eba\uff0c\u65e9\u5df2\u77e5\u9053\u4f60\u5bf9\u4ed6\u4e0d\u6ee1\uff0c\u5df2\u7ecf\u60f3\u79bb\u5f00 \u4ed6\u4e86\u3002\u201d \u3000\u3000\u5d14\u7389\u771f\u5782\u4e0b\u5934\uff0c\u8f7b\u8f7b\u9053\uff1a\u201c\u6700\u8fd1\u6211\u7684\u786e\u603b\u5728\u60f3\u6cd5\u5b50\u907f\u7740\u4ed6\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4ed6\u4e5f\u77e5\u9053\u6211\u4e00\u5b9a\u4f1a\u5230\u51b7\u9999\u9662\u53bb\u627e\uff0c\u6240\u4ee5\u4ed6\u6545\u610f\u8981\u4f60\u5728\u90a3\u91cc\u7b49\uff0c\u6545\u610f\u8ba9\u4f60\u5c06\u4e01 \u7075\u7433\u7684\u4e0b\u843d\u900f\u9732\u7ed9\u6211\u3002\u201d \u3000\u3000\u5d14\u7389\u771f\u53c8\u4e0d\u61c2\u4e86\uff1a\u201c\u96be\u9053\u4ed6\u6545\u610f\u60f3\u8981\u4f60\u53bb\u5c06\u4e01\u59d1\u5a18\u6551\u51fa\u6765\uff1f\u201d \u3000\u3000\u53f6\u5f00\u70b9\u70b9\u5934\uff0c\u9053\uff0c\u201c\u56e0\u4e3a\u4ed6\u5df2\u7528\u6444\u5fc3\u672f\u4e00\u7c7b\u7684\u90aa\u6cd5\uff0c\u63a7\u5236\u4e86\u4e01\u7075\u7433\uff0c\u53eb\u4e01\u7075\u7433\u4e00\u770b\u89c1\u6211 \u5c31\u6740\u4e86\u6211\u3002\u201d \u3000\u3000\u5d14\u7389\u771f\u52a8\u5bb9\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6240\u4ee5\u4ed6\u6545\u610f\u5728\u90a3\u5c4b\u5b50\u7684\u7a97\u5916\uff0c\u6446\u4e86\u4e09\u76c6\u814a\u6885\uff0c\u4e3a\u7684\u5c31\u662f\u8ba9\u4f60\u5bb9 \u6613\u627e\u5230\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f46\u4ed6\u4e3a\u4e86\u6015\u6211\u7591\u5fc3\uff0c\u6240\u4ee5\u4e5f\u4e0d\u80fd\u8ba9\u6211\u6709\u5bb9\u6613\u5f97\u624b\u7684\u673a\u4f1a\u3002\u201d \u3000\u3000\u5d14\u7389\u771f\u9053\uff0c\u201c\u6240\u4ee5\u4ed6\u53c8\u6545\u610f\u5f04\u4e86\u90a3\u4e48\u591a\u7384\u865a\uff0c\u8ba9\u4f60\u6c38\u8fdc\u60f3\u4e0d\u5230\u8fd9\u4e00\u70b9\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4ed6\u5c06\u4e01\u7075\u7433\u52ab\u8d70\uff0c\u6839\u672c\u5c31\u4e0d\u662f\u4e3a\u4e86\u4e0a\u5b98\u5c0f\u4ed9\uff0c\u800c\u662f\u4e3a\u4e86\u8981\u6211\u7684\u547d\u3002\u201d \u3000\u3000\u5d14\u7389\u771f\u54ac\u7740\u7259\u6068\u6068\u9053\uff1a\u201c\u6211\u4ee5\u524d\u5b9e\u5728\u4e0d\u77e5\u9053\u4ed6\u4e5f\u662f\u4e2a\u8fd9\u4e48\u9634\u9669\u6076\u6bd2\u7684\u4eba\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f46\u4ed6\u5374\u7edd\u4e0d\u662f\u91d1\u94b1\u5e2e\u7684\u4eba\uff0c\u56e0\u4e3a\u4e0a\u5b98\u5c0f\u4ed9\u5e76\u4e0d\u60f3\u8981\u6211\u6b7b\uff0c\u4e5f\u5e76\u4e0d\u77e5\u9053\u4ed6\u7528\u7684 \u8fd9\u4e00\u7740\uff0c\u6240\u4ee5\u6211\u6709\u4e9b\u60f3\u4e0d\u901a\u3002\u201d \u3000\u3000\u5d14\u7389\u771f\u9053\uff1a\u201c\u60f3\u4e0d\u901a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u60f3\u4e0d\u901a\u4ed6\u600e\u4e48\u4e5f\u4f1a\u6444\u5fc3\u672f\u8fd9\u4e00\u7c7b\u90aa\u6cd5\u7684\u3002\u201d \u3000\u3000\u5d14\u7389\u771f\u9053\uff1a\u201c\u4f1a\u8fd9\u79cd\u90aa\u672f\u7684\u4eba\u5f88\u5c11\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f1a\u7684\u4eba\u5e76\u4e0d\u5c11\uff0c\u53ef\u662f\u771f\u6b63\u7cbe\u901a\u7684\u4eba\u5374\u6ca1\u6709\u51e0\u4e2a\uff0c\u5176\u4e2d\u5927\u591a\u6570\u662f\u9b54\u6559\u4e2d\u7684 \u4eba\u3002\u201d \u3000\u3000\u5d14\u738b\u771f\u52a8\u5bb9\u9053\uff1a\u201c\u9b54\u6559\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u4e5f\u542c\u8bf4\u8fc7\uff1f\u201d \u3000\u3000\u5d14\u7389\u771f\u9053\uff1a\u201c\u6211\u59cb\u7ec8\u4ee5\u4e3a\u90a3\u53ea\u4e0d\u8fc7\u662f\u4f20\u8bf4\u800c\u5df2\uff0c\u60f3\u4e0d\u5230\u8fd9\u4e16\u4e0a\u7adf\u771f\u7684\u6709\u9b54\u6559\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u6ca1\u6709\u542c\u7389\u7bab\u8c08\u8d77\u8fc7\u9b54\u6559\uff1f\u201d \u3000\u3000\u5d14\u7389\u771f\u9053\uff1a\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u8ddf\u7740\u4ed6\u5df2\u6709\u591a\u4e45\uff1f\u201d \u3000\u3000\u5d14\u7389\u771f\u5782\u4e0b\u773c\u5e18\uff0c\u9053\uff1a\u201c\u5feb\u4e24\u5e74\u4e86\u3002\u201d \u3000\u3000\u5979\u8138\u4e0a\u53c8\u9732\u51fa\u79cd\u8bf4\u4e0d\u51fa\u7684\u60b2\u75db\u618e\u6076\u4e4b\u8272\uff0c\u8fd9\u4e24\u5e74\u6765\u5979\u60f3\u5fc5\u5c31\u50cf\u751f\u6d3b\u5728\u5730\u72f1\u91cc\u4e00\u6837\u3002 \u3000\u3000\u53f6\u5f00\u7b49\u5979\u60c5\u7eea\u521a\u521a\u5e73\u5b9a\uff0c\u624d\u95ee\uff1a\u201c\u8fd9\u4e24\u5e74\u6765\u4ed6\u5e73\u65f6\u90fd\u5728\u4ec0\u4e48\u5730\u65b9\uff1f\u201d \u3000\u3000\u5d14\u7389\u771f\u9053\uff1a\u201c\u4ed6\u6709\u6761\u5f88\u5927\u7684\u6d77\u8239\uff0c\u5e73\u65f6\u4ed6\u90fd\u5728\u8239\u4e0a\uff0c\u4f46\u6bcf\u9694\u4e00\u4e24\u4e2a\u6708\uff0c\u90fd\u4f1a\u627e\u4e2a\u6d77\u53e3\u505c \u6cca\uff0c\u8865\u5145\u7cae\u98df\u548c\u6e05\u6c34\u3002\u201d \u3000\u3000\u5979\u60f3\u4e86\u60f3\uff0c\u63a5\u7740\u9053\uff1a\u201c\u53ef\u662f\u51e0\u4e2a\u6708\u524d\uff0c\u4ed6\u5374\u5728\u4e00\u4e2a\u6ca1\u6709\u4eba\u7684\u8352\u5c9b\u4e0a\u505c\u7559\u4e86\u516d\u4e03\u5929\uff0c\u5374\u6ca1 \u6709\u5e26\u522b\u7684\u4eba\u53bb\uff0c\u4e5f\u4e0d\u8bb8\u6211\u4eec\u4e0b\u8239\u3002\u201d \u3000\u3000\u53f6\u5f00\u7684\u773c\u775b\u4eae\u4e86\uff0c\u4ed6\u5ffd\u7136\u60f3\u8d77\u94c1\u59d1\u8bf4\u7684\u8bdd\uff1a\u201c\u2026\u8fd9\u6b21\u672c\u6559\u5728\u795e\u5c71\u805a\u5408\uff0c\u53e6\u9009\u6559\u5b97\uff0c\u91cd\u5f00 \u6559\u95e8\uff0c\u65b0\u4efb\u7684\u56db\u5927\u5929\u738b\u548c\u516c\u4e3b\u2026\u2026\u201d \u3000\u3000\u5d14\u7389\u771f\u9053\uff1a\u201c\u4f60\u5728\u60f3\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u53f6\u5f00\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff0c\u201c\u6211\u672c\u5c31\u5728\u6000\u7591\uff0c\u5374\u4e00\u76f4\u4e0d\u6562\u76f8\u4fe1\u3002\u201d \u3000\u3000\u5d14\u7389\u771f\u9053\uff1a\u201c\u6000\u7591\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6000\u7591\u7389\u7bab\u4e5f\u4eba\u4e86\u9b54\u6559\uff0c\u800c\u4e14\u662f\u9b54\u6559\u4e2d\u7684\u56db\u5927\u5929\u738b\u4e4b\u4e00\u3002\u201d \u3000\u3000\u5d14\u7389\u771f\u7684\u8138\u8272\u82cd\u767d\uff0c\u5ffd\u7136\u63e1\u4f4f\u4ed6\u7684\u624b\uff0c\u9053\uff1a\u201c\u4f60\u7684\u4f24\u53e3\u75bc\u4e0d\u75bc\uff1f\u201d \u3000\u3000\u53f6\u5f00\u70b9\u70b9\u5934\u3002 \u3000\u3000\u5d14\u7389\u771f\u9053\uff1a\u201c\u636e\u8bf4\u9b54\u6559\u7528\u7684\u5200\u90fd\u6709\u6bd2\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff0c\u201c\u4e0d\u9519\uff01\u201d \u3000\u3000\u5d14\u7389\u771f\u9053\uff1a\u201c\u5200\u4e0a\u82e5\u6709\u6bd2\uff0c\u4f60\u7684\u4f24\u53e3\u7adf\u53ea\u6709\u75db\uff1f\u201d \u3000\u3000\u5200\u4e0a\u82e5\u6709\u6bd2\uff0c\u5c31\u4e0d\u4f1a\u89c9\u5f97\u75db\u82e6\uff0c\u53ea\u4f1a\u89c9\u5f97\u9ebb\u6728\u3002 \u3000\u3000\u53f6\u5f00\u7b11\u9053\uff1a\u201c\u5200\u4e0a\u5c31\u662f\u6709\u6bd2\uff0c\u4e5f\u6bd2\u4e0d\u6b7b\u6211\u3002\u201d \u3000\u3000\u5d14\u7389\u771f\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u662f\u4e2a\u5947\u602a\u7684\u4eba\uff0c\u6211\u7684\u8840\u91cc\u6709\u79cd\u6297\u6bd2\u4e4b\u529b\uff0c\u5c24\u5176\u53ef\u4ee5\u6d88\u51cf\u9b54\u6559\u7684\u6bd2\u3002\u201d \u3000\u3000\u5d14\u7389\u771f\u5403\u60ca\u5730\u77aa\u5927\u4e86\u773c\u775b\uff0c\u9053\uff1a\u201c\u8fd9\u662f\u5929\u751f\u7684\uff1f\u201d \u3000\u3000\u53f6\u5f00\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u662f\u6700\u8fd1\u624d\u6709\u7684\u3002\u201d \u3000\u3000\u5d14\u7389\u771f\u9053\uff1a\u201c\u600e\u4e48\u4f1a\u6709\u7684\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u7684\u6bcd\u4eb2\uff0c\u6614\u5e74\u672c\u662f\u9b54\u6559\u4e2d\u7684\u5927\u516c\u4e3b\u3002\u201d \u3000\u3000\u5d14\u7389\u771f\u66f4\u5403\u60ca\uff0c\u5fcd\u4e0d\u4f4f\u95ee\uff1a\u201c\u73b0\u5728\u5462\uff1f\u201d \u3000\u3000\u53f6\u5f00\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u73b0\u5728\u5979\u53ea\u4e0d\u8fc7\u662f\u4e2a\u5f88\u5e73\u51e1\u7684\u8001\u5987\u4eba\uff0c\u6b63\u5728\u4e00\u4e2a\u5b81\u9759\u7684\u5730\u65b9\uff0c\u5b89\u4eab\u5979 \u7684\u4f59\u5e74\uff0c\u5e0c\u671b\u5979\u7684\u513f\u5b50\u80fd\u65f6\u5e38\u56de\u53bb\u770b\u770b\u5979\u3002\u201d \u3000\u3000\u5d14\u7389\u771f\u9053\uff1a\u201c\u53ef\u662f\u4f60\u5374\u5f88\u5c11\u56de\u53bb\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u56e0\u4e3a\u5979\u8fd8\u6709\u4e2a\u513f\u5b50\u5728\u966a\u7740\u5979\u3002\u201d\u4ed6\u7684\u76ee\u5149\u4eff\u4f5b\u53c8\u5728\u6148\u7965\u5730\u51dd\u89c6\u7740\u8fdc\u65b9\uff0c\u5f90\u5f90 \u9053\uff1a\u201c\u8fd9\u4e2a\u513f\u5b50\u867d\u4e0d\u662f\u5979\u4eb2\u751f\u7684\uff0c\u5374\u6bd4\u6211\u8fd9\u4e2a\u4eb2\u751f\u7684\u513f\u5b50\u66f4\u5b5d\u987a\u3002\u201d \u3000\u3000\u5d14\u7389\u771f\u9053\uff1a\u201c\u4ed6\u957f\u5f97\u4e5f\u8ddf\u4f60\u4e00\u6837\uff1f\u2019\u53f6\u5f00\u5fae\u7b11\u9053\uff1a\u201c\u4ed6\u8ddf\u6211\u4e0d\u4e00\u6837\uff0c\u4ed6\u662f\u4e2a\u5f88\u5947\u602a\u7684 \u4eba\uff0c\u4f46\u5374\u6bd4\u6211\u597d\u770b\uff0c\u5e9f\u8bdd\u4e5f\u6ca1\u6709\u6211\u8fd9\u4e48\u591a\uff0c\u6211\u5e0c\u671b\u4ee5\u540e\u80fd\u5e38\u89c1\u5230\u4ed6\u3002\u201d \u3000\u3000\u5d14\u7389\u771f\u5ae3\u7136\u9053\uff1a\u201c\u6211\u4e5f\u5e0c\u671b\u80fd\u89c1\u5230\u4ed6\uff0c\u4ed6\u65e2\u7136\u662f\u4f60\u7684\u5144\u5f1f\uff0c\u90a3\u4e48\u4e00\u5b9a\u4e5f\u662f\u4e2a\u5f88\u597d\u7684 \u4eba\u3002\u201d \u3000\u3000\u5979\u5fc3\u91cc\u5ffd\u7136\u5145\u6ee1\u4e86\u5bf9\u672a\u6765\u5e78\u798f\u7684\u61a7\u61ac\uff0c\u5fcd\u4e0d\u4f4f\u53c8\u95ee\uff1a\u201c\u4ed6\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u201d \u3000\u3000\u53f6\u5f00\u8bf4\u51fa\u4e86\u4ed6\u7684\u540d\u5b57\uff1a\u201c\u5085\u7ea2\u96ea\uff01\u201d \u3000\u3000\u534e\u5b50\u6e05\u7559\u4e0b\u7684\u836f\u6709\u4e24\u5305\uff0c\u4e00\u5305\u5185\u670d\uff0c\u4e00\u5305\u5916\u6577\u3002\u5185\u670d\u7684\u836f\u6027\u5f88\u5e73\u548c\uff0c\u4eff\u4f5b\u8fd8\u6709\u9547\u9759\u7684\u529f \u6548\uff0c\u6240\u4ee5\u53f6\u5f00\u7761\u5f97\u5f88\u6c89\uff0c\u4ed6\u9192\u6765\u89c9\u5f97\u5f88\u6109\u5feb\uff0c\u56e0\u4e3a\u4ed6\u4f24\u53e3\u7684\u75db\u82e6\u4f3c\u5df2\u51cf\u8f7b\u4e86\u5f88\u591a\uff0c\u800c\u4e14\u95e8\u5916 \u53c8\u98d8\u6765\u4e86\u71ac\u7ca5\u7684\u9999\u6c14\u3002 \u3000\u3000\u5d14\u7389\u771f\u60f3\u5fc5\u6b63\u5728\u53a8\u623f\u91cc\u66ff\u4ed6\u71ac\u7ca5\uff0c\u9633\u5149\u7167\u5728\u7a97\u6237\u4e0a\uff0c\u98ce\u5f88\u8f7b\uff0c\u4eca\u5929\u60f3\u5fc5\u662f\u4e2a\u5f88\u597d\u7684\u5929 \u6c14\u3002 \u3000\u3000\u53f6\u5f00\u51e0\u4e4e\u5df2\u5c06\u6240\u6709\u7684\u70e6\u607c\u5168\u90fd\u5fd8\u4e86\uff0c\u5927\u58f0\u9053\uff1a\u201c\u7ca5\u716e\u597d\u4e86\u6ca1\u6709\uff0c\u5feb\u6dfb\u4e09\u5927\u7897\u7ed9\u6211\u3002\u201d \u3000\u3000\u201c\u6765\u4e86\u3002\u201d \u3000\u3000\u95e8\u5e18\u5ffd\u7136\u6380\u8d77\uff0c\u4e00\u5927\u7897\u7ca5\u5e73\u7a7a\u98de\u4e86\u8fdb\u6765\uff0c\u201c\u7830\u201d\u7684\u6253\u5728\u5899\u4e0a\uff0c\u53f6\u5f00\u6014\u4f4f\uff0c\u6ee1\u6ee1\u7684\u9e21\u7ca5\u6162 \u6162\u6d41\u4e0b\uff0c\u6709\u4e2a\u4eba\u51b7\u7b11\u7740\uff0c\u5ffd\u7136\u5728\u95e8\u53e3\u51fa\u73b0\u3002 \u3000\u3000\u4f0a\u591c\u54ed\u3002 \u3000\u3000\u4ed6\u8eab\u4e0a\u8fd8\u7a7f\u7740\u90a3\u4ef6\u7ee3\u6ee1\u4e86\u9ed1\u7261\u4e39\u7684\u9c9c\u7ea2\u957f\u888d\uff0c\u770b\u6765\u8fd8\u662f\u50cf\u4e2a\u50f5\u5c38\u3002 \u3000\u3000\u53f6\u5f00\u5ffd\u7136\u5bf9\u4ed6\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u65e9\u3002\u201d \u3000\u3000\u4f0a\u591c\u54ed\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u9192\u5f97\u867d\u4e0d\u65e9\uff0c\u5012\u771f\u5de7\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u4f0a\u591c\u54ed\u9053\uff1a\u201c\u4f60\u82e5\u518d\u8fdf\u9192\u7247\u523b\uff0c\u53ea\u6015\u5c31\u6c38\u8fdc\u4e5f\u4e0d\u4f1a\u9192\u4e86\u3002\u201d \u3000\u3000\u53f6\u5f00\u53c8\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u6765\u5f97\u867d\u4e0d\u5de7\uff0c\u5012\u771f\u65e9\u3002\u201d \u3000\u3000\u4f0a\u591c\u54ed\u51b7\u51b7\u9053\uff1a\u201c\u65e9\u8d77\u7684\u96c0\u513f\u5403\u98df\uff0c\u665a\u8d77\u7684\u96c0\u513f\u5403\u5c4e\uff0c\u6211\u82e5\u975e\u8d77\u5f97\u65e9\uff0c\u53c8\u600e\u4e48\u4f1a\u51d1\u5de7\u770b \u89c1\u90a3\u4e2a\u80cc\u53db\u4e86\u5e08\u95e8\u7684\u5973\u53db\u5f92\u3002\u201d \u3000\u3000\u53f6\u5f00\u53f9\u9053\uff1a\u201c\u770b\u6765\u8d77\u5f97\u592a\u65e9\u4e5f\u4e0d\u662f\u597d\u4e8b\uff0c\u5979\u82e5\u975e\u8d77\u5f97\u65e9\uff0c\u53c8\u600e\u4e48\u4f1a\u649e\u89c1\u9b3c\uff1f\u201d \u3000\u3000\u4f0a\u591c\u54ed\u9053\uff1a\u201c\u90a3\u53ea\u602a\u4f60\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u602a\u6211\uff1f\u201d \u3000\u3000\u4f0a\u591c\u54ed\u9053\uff1a\u201c\u5979\u82e5\u975e\u5df2\u88ab\u4f60\u8ff7\u4f4f\u4e86\uff0c\u53c8\u600e\u4e48\u4f1a\u4e00\u5927\u65e9\u5c31\u8d77\u6765\uff0c\u6e9c\u56de\u90a3\u5ba2\u6808\u53bb\u66ff\u4f60\u6253\u542c\u97e9 \u8d1e\u7684\u6d88\u606f\uff1f\u201d \u3000\u3000\u53f6\u5f00\u7684\u5fc3\u6c89\u4e86\u4e0b\u6765\uff0c\u6628\u5929\u665a\u4e0a\uff0c\u4ed6\u95ee\u8fc7\u5d14\u7389\u771f\u3002\u5979\u5f53\u771f\u4e0d\u77e5\u9053\u97e9\u8d1e\u600e\u4e48\u6837\u4e86\uff0c\u5979\u770b\u89c1\u53f6 \u5f00\u53d7\u4f24\uff0c\u53ea\u987e\u7740\u5e26\u53f6\u5f00\u8d76\u5feb\u9003\u8d70\uff0c\u54ea\u91cc\u8fd8\u987e\u5f97\u4e86\u522b\u4eba\u3002 \u3000\u3000\u53f6\u5f00\u867d\u6ca1\u518d\u95ee\uff0c\u4e5f\u6ca1\u6709\u8d23\u5907\u5979\uff0c\u53ef\u662f\u5fc3\u91cc\u5374\u4e0d\u514d\u6709\u70b9\u60ed\u6127\uff0c\u6709\u70b9\u96be\u53d7\uff0c\u4ed6\u89c9\u5f97\u81ea\u5df1\u5bf9\u4e0d \u8d77\u97e9\u8d1e\u3002 \u3000\u3000\u6240\u4ee5\u5d14\u7389\u771f\u5fc3\u91cc\u4e5f\u5f88\u96be\u53d7\u3002\u53f6\u5f00\u770b\u5f97\u51fa\uff0c\u5374\u60f3\u4e0d\u5230\u4ed6\u8bf4\u4e00\u53e5\u8bdd\uff0c\u5979\u5c31\u4f1a\u4e0d\u987e\u4e00\u5207\uff0c\u53bb\u4e3a \u4ed6\u505a\u4efb\u4f55\u4e8b\u3002 \u3000\u3000\u4f0a\u591c\u54ed\u9053\uff1a\u201c\u5979\u7b97\u51c6\u7389\u7bab\u4e00\u5b9a\u5df2\u8d70\u4e86\uff0c\u5374\u60f3\u4e0d\u5230\u6211\u5c45\u7136\u8fd8\u7559\u5728\u90a3\u91cc\u3002\u201d\u53f6\u5f00\u5fcd\u4e0d\u4f4f\u95ee \u9053\uff1a\u201c\u90a3\u5929\u665a\u4e0a\u4ed6\u6ca1\u6709\u6740\u4e86\u4f60\uff1f\u201d \u3000\u3000\u4f0a\u591c\u54ed\u9053\uff1a\u201c\u4f60\u4ee5\u4e3a\u4ed6\u771f\u8981\u6740\u4e86\u6211\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4e0d\u662f\u771f\u7684\uff1f\u201d \u3000\u3000\u4f0a\u591c\u54ed\u9053\uff1a\u201c\u6211\u4eec\u53ea\u4e0d\u8fc7\u662f\u5728\u505a\u620f\uff0c\u7279\u5730\u505a\u7ed9\u4f60\u770b\u7684\uff0c\u597d\u8ba9\u4f60\u6709\u673a\u4f1a\u53bb\u6551\u4eba\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u90a3\u65f6\u4f60\u4eec\u5df2\u53d1\u73b0\u6211\u5728\u5916\u9762\uff1f\u201d \u3000\u3000\u4f0a\u591c\u54ed\u9053\uff1a\u201c\u4f60\u4eec\u4e00\u8fdb\u4e86\u90a3\u9662\u5b50\uff0c\u4ed6\u5c31\u5df2\u77e5\u9053\u3002\u201d \u3000\u3000\u53f6\u5f00\u53f9\u4e86\u53e3\u6c14\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u770b\u6765\u6211\u5012\u4f4e\u4f30\u4e86\u4ed6\u3002\u201d \u3000\u3000\u4f0a\u591c\u54ed\u9053\uff1a\u201c\u4ed6\u5df2\u4f4e\u4f30\u4e86\u4f60\uff0c\u4ed6\u8ba4\u4e3a\u4f60\u5df2\u6b7b\u5b9a\u4e86\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u5462\uff1f\u201d \u3000\u3000\u4f0a\u591c\u54ed\u9053\uff1a\u201c\u6211\u77e5\u9053\u8981\u4f60\u8fd9\u79cd\u4eba\u6b7b\uff0c\u5e76\u4e0d\u662f\u4ef6\u5bb9\u6613\u7684\u4e8b\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u8fd9\u6b21\u4f60\u603b\u7b97\u6ca1\u6709\u770b\u9519\u3002\u201d \u3000\u3000\u4f0a\u591c\u54ed\u9053\uff1a\u201c\u4f46\u73b0\u5728\u4f60\u82e5\u4e0d\u5c06\u4e0a\u5b98\u5c0f\u4ed9\u4ea4\u51fa\u6765\uff0c\u8fd8\u662f\u6b7b\u5b9a\u4e86\u4e00\u3002\u201d \u3000\u3000\u53f6\u5f00\u53f9\u9053\uff1a\u201c\u8fd9\u6b21\u4f60\u770b\u9519\u4e86\u3002\u201d \u3000\u3000\u4f0a\u591c\u54ed\u9053\uff1a\u201c\u4f60\u6700\u597d\u660e\u767d\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u8bf4\u3002\u201d \u3000\u3000\u4f0a\u591c\u54ed\u9053\uff1a\u201c\u6211\u559c\u6b22\u6740\u4eba\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u8fd9\u662f\u5b9e\u8bdd\u3002\u201d \u3000\u3000\u4f0a\u591c\u54ed\u9053\uff1a\u201c\u6211\u6700\u60f3\u6740\u7684\u5c31\u662f\u4f60\uff01\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u8fd9\u4e5f\u662f\u5b9e\u8bdd\u3002\u201d \u3000\u3000\u4f0a\u591c\u54ed\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u82e5\u4e0d\u8d76\u5feb\u5c06\u4e0a\u5b98\u5c0f\u4ed9\u4ea4\u51fa\u6765\uff0c\u6211\u7edd\u4e0d\u4f1a\u518d\u7b49\u7684\uff0c\u6211\u5b81\u53ef\u4e0d\u8981\u5979\uff0c\u4e5f \u8981\u6740\u4e86\u4f60\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u6700\u597d\u4e5f\u660e\u767d\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u4f0a\u591c\u54ed\u9053\uff1a\u201c\u6211\u4e5f\u8ba9\u4f60\u8bf4\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u4e0d\u559c\u6b22\u6740\u4eba\uff0c\u4f46\u4f60\u8fd9\u79cd\u4eba\u5374\u662f\u4f8b\u5916\u3002\u201d \u3000\u3000\u4f0a\u591c\u54ed\u51b7\u7b11\u9053\uff1a\u201c\u73b0\u5728\u4f60\u80fd\u6740\u5f97\u4e86\u6211\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u4e0d\u80fd\uff0c\u5b83\u80fd\u3002\u201d \u3000\u3000\u4ed6\u7684\u624b\u4e00\u7ffb\uff0c\u5200\u5df2\u5728\u624b\u3002\u4e09\u5bf8\u4e03\u5206\u957f\u7684\u5200\uff0c\u98de\u5200\u3002\u4f0a\u591c\u54ed\u770b\u7740\u8fd9\u67c4\u5200\uff0c\u77b3\u5b54\u7acb\u523b\u6536\u7f29\u3002 \u3000\u3000\u4ed6\u5f53\u7136\u77e5\u9053\u8fd9\u5c31\u662f\u5c0f\u674e\u63a2\u82b1\u4e00\u8109\u76f8\u4f20\u7684\u98de\u5200\uff0c\u4ece\u4e0d\u865a\u53d1\u7684\u98de\u5200\u3002 \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u53ea\u5e0c\u671b\u4f60\u83ab\u8981\u903c\u6211\u6740\u4f60\u3002\u201d \u3000\u3000\u4ed6\u6bcf\u6b21\u51fa\u624b\u4e4b\u524d\uff0c\u90fd\u8981\u8bf4\u8fd9\u53e5\u8bdd\u3002 \u3000\u3000\u56e0\u4e3a\u8fd9\u67c4\u5200\u5e76\u975e\u662f\u7528\u624b\u53d1\u51fa\u6765\u7684\uff0c\u8981\u53d1\u8fd9\u67c4\u5200\uff0c\u5c31\u5f97\u4f7f\u51fa\u5168\u8eab\u7684\u7cbe\u795e\u548c\u529b\u91cf\uff0c\u5200\u4e00\u53d1 \u51fa\uff0c\u5c31\u8fde\u4ed6\u81ea\u5df1\u4e5f\u65e0\u6cd5\u63a7\u5236\u3002 \u3000\u3000\u4f0a\u591c\u54ed\u9053\u76ef\u7740\u8fd9\u67c4\u5200\uff0c\u5f90\u5f90\u9053\uff0c\u201c\u6211\u8ba4\u5f97\u8fd9\u67c4\u5200\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u8ba4\u5f97\u6700\u597d\u3002\u201d \u3000\u3000\u4f0a\u591c\u54ed\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u4f60\u4e0d\u662f\u5c0f\u674e\u63a2\u82b1\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u4e0d\u662f\u3002\u201d \u3000\u3000\u4f0a\u591c\u54ed\u9053\uff1a\u201c\u4f60\u73b0\u5728\u53ea\u4e0d\u8fc7\u662f\u4e2a\u53d7\u4e86\u4f24\u7684\u5e9f\u7269\uff0c\u4f60\u8fd9\u628a\u5200\u8fde\u6761\u72d7\u90fd\u6740\u4e0d\u6b7b\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u8fd9\u67c4\u5200\u4e0d\u6740\u72d7\uff0c\u53ea\u6740\u4eba\u3002\u201d \u3000\u3000\u4f0a\u591c\u54ed\u5927\u7b11\u9053\uff1a\u201c\u6211\u5012\u8981\u8bd5\u8bd5\u5b83\u80fd\u4e0d\u80fd\u6740\u5f97\u6b7b\u6211\u3002\u201d \u3000\u3000\u4ed6\u4eba\u5df2\u63a0\u8d77\uff0c\u5411\u53f6\u5f00\u6251\u4e86\u8fc7\u53bb\u3002\u4ed6\u539f\u6709\u4e00\u53cc\u4e13\u7834\u6697\u5668\u7684\u624b\uff0c\u4f46\u8fd9\u67c4\u5200\u4e0d\u662f\u6697\u5668\u3002 \u3000\u3000\u8fd9\u67c4\u5200\u51e0\u4e4e\u4e5f\u5df2\u4e0d\u662f\u5200\uff0c\u800c\u662f\u4e00\u79cd\u65e0\u575a\u4e0d\u6467\u3001\u4e0d\u53ef\u6297\u62d2\u7684\u529b\u91cf\u3002 \u3000\u3000\u5200\u5149\u4e00\u95ea\uff0c\u4f0a\u591c\u54ed\u7684\u8eab\u5b50\u7a81\u7136\u5728\u7a7a\u4e2d\u626d\u66f2\u3001\u8dcc\u4e0b\u3002\u4ed6\u6ca1\u6709\u547c\u558a\uff0c\u4e5f\u6ca1\u6709\u6323\u624e\uff0c\u7a81\u7136\u95f4\u5c31 \u50cf\u662f\u7a7a\u9ebb\u888b\u822c\u8f6f\u762b\u5728\u5730\u4e0a\u3002 \u3000\u3000\u4ed6\u7684\u54bd\u5589\u4e0a\u5df2\u591a\u4e86\u4e00\u67c4\u5200\u3002 \u3000\u3000\u98de\u5200\uff01\u5929\u4e0a\u5730\u4e0b\uff0c\u72ec\u4e00\u65e0\u4e8c\u7684\u98de\u5200\u3002 \u7b2c\u5341\u4e94\u7ae0\u3000\u60fa\u60fa\u76f8\u60dc>> \u53e4\u9f99\u300a\u4e5d\u6708\u9e70\u98de\u300b \u7b2c\u5341\u4e94\u7ae0\u3000\u60fa\u60fa\u76f8\u60dc \u3000\u3000\u53f6\u5f00\u9759\u9759\u5730\u5750\u5728\u90a3\u91cc\uff0c\u773c\u775b\u91cc\u5e26\u7740\u79cd\u65e0\u6cd5\u63cf\u53d9\u7684\u8868\u60c5\uff0c\u4eff\u4f5b\u662f\u4f36\u60af\uff0c\u53c8\u4eff\u4f5b\u7a81\u7136\u89c9\u5f97\u5f88 \u5bc2\u5bde\u3002 \u3000\u3000\u6740\u4eba\uff0c\u5e76\u4e0d\u662f\u4ef6\u6109\u5feb\u7684\u4e8b\u3002 \u3000\u3000\u4f46\u7a97\u5916\u5374\u4f20\u6765\u4e86\u4e00\u9635\u94f6\u94c3\u822c\u7684\u7b11\u58f0\uff0c\u662f\u4e0a\u5b98\u5c0f\u4ed9\u7684\u7b11\u58f0\u3002 \u3000\u3000\u201c\u597d\u5feb\u7684\u5200\u3002\u201d \u3000\u3000\u7b11\u58f0\u8fd8\u5728\u7a97\u5916\uff0c\u5979\u4eba\u5374\u5df2\u4ece\u95e8\u5916\u63a0\u8fdb\u6765\uff0c\u8f7b\u76c8\u50cf\u662f\u53ea\u71d5\u5b50\u3002 \u3000\u3000\u53f6\u5f00\u8fd8\u662f\u9759\u9759\u5730\u5750\u5728\u90a3\u91cc\uff0c\u751a\u81f3\u8fde\u770b\u90fd\u6ca1\u6709\u770b\u5979\u4e00\u773c\u3002 \u3000\u3000\u73b0\u5728\u5979\u65e0\u8bba\u4ec0\u4e48\u65f6\u5019\u51fa\u73b0\uff0c\u53f6\u5f00\u90fd\u5df2\u4e0d\u4f1a\u89c9\u5f97\u60ca\u5f02\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u62cd\u624b\u7b11\u9053\uff1a\u201c\u6211\u679c\u7136\u6ca1\u6709\u770b\u9519\u4f60\uff0c\u6211\u4ece\u6765\u4e5f\u6ca1\u6709\u770b\u89c1\u8fc7\u8fd9\u4e48\u5feb\u7684\u5200\u3002\u201d \u3000\u3000\u53f6\u5f00\u7a81\u7136\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u8fd8\u60f3\u518d\u770b\u770b\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6211\u4e0d\u60f3\uff0c\u6211\u4e5f\u77e5\u9053\u4f60\u4e0d\u4f1a\u6740\u6211\u7684\uff0c\u7528\u8fd9\u79cd\u5200\u6765\u6740\u4e00\u4e2a\u5b64\u82e6\u4f36\u6c40\u7684\u5973\u5b69 \u5b50\uff0c\u5c0f\u674e\u63a2\u82b1\u77e5\u9053\u4e86\uff0c\u4e00\u5b9a\u4f1a\u5f88\u751f\u6c14\u3002\u201d\u5979\u5a07\u7b11\u7740\u53c8\u9053\uff1a\u201c\u4f55\u51b5\uff0c\u4f60\u672c\u8be5\u611f\u6fc0\u6211\u624d\u662f\uff0c\u82e5\u4e0d \u662f\u6211\u6628\u5929\u53eb\u534e\u5b50\u6e05\u7559\u4e0b\u90a3\u4e24\u5305\u836f\uff0c\u4f60\u4eca\u5929\u4e5f\u672a\u5fc5\u80fd\u6740\u5f97\u4e86\u4ed6\u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u4e0d\u80fd\u5426\u8ba4\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u5ae3\u7136\u9053\uff1a\u201c\u53ef\u662f\u6211\u4e5f\u5f88\u611f\u6fc0\u4f60\uff0c\u4f60\u603b\u7b97\u5df2\u4e3a\u6211\u6740\u4e86\u4e00\u4e2a\u4eba\u4e86\u3002\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u5c31\u50cf\u662f\u6761\u97ad\u5b50\uff0c\u4e00\u97ad\u5b50\u62bd\u5728\u53f6\u5f00\u8138\u4e0a\u3002 \u3000\u3000\u660e\u77e5\u8981\u88ab\u4eba\u5229\u7528\uff0c\u8fd8\u662f\u88ab\u4eba\u5229\u7528\u4e86\uff0c\u8fd9\u7684\u786e\u4e0d\u662f\u4ef6\u597d\u53d7\u7684\u4e8b\u3002 \u3000\u3000\u53f6\u5f00\u51b7\u51b7\u9053\uff1a\u201c\u6211\u65e2\u5df2\u6740\u4e86\u4e00\u4e2a\uff0c\u5c31\u8fd8\u80fd\u6740\u7b2c\u4e8c\u4e2a\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6211\u76f8\u4fe1\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u6700\u597d\u8d76\u5feb\u8d70\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4f60\u53c8\u8981\u8d76\u6211\u8d70\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u662f\uff01\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u8f7b\u8f7b\u53f9\u606f\u9053\uff1a\u201c\u6211\u957f\u5f97\u96be\u9053\u6bd4\u90a3\u5973\u9053\u58eb\u96be\u770b\uff1f\u6211\u96be\u9053\u5c31\u4e0d\u80fd\u50cf\u5979\u4e00\u6837\u7684\u4f8d\u5019 \u4f60\uff1f\u201d \u3000\u3000\u5e8a\u5934\u7684\u51e0\u4e0a\uff0c\u5df2\u6446\u7740\u5957\u6d17\u5f97\u5e72\u5e72\u51c0\u51c0\u3001\u53e0\u5f97\u6574\u6574\u9f50\u9f50\u7684\u8863\u670d\u3002 \u3000\u3000\u8fd9\u5f53\u7136\u4e5f\u662f\u5d14\u7389\u771f\u66ff\u4ed6\u51c6\u5907\u7684\u3002 \u3000\u3000\u53ef\u662f\u5979\u4eba\u5462\uff1f \u3000\u3000\u4e01\u7075\u7433\u5462\uff1f \u3000\u3000\u53f6\u5f00\u62ff\u8d77\u4e86\u8863\u670d\uff0c\u4ed6\u5df2\u6ca1\u6709\u6cd5\u5b50\u518d\u8eba\u4e0b\u53bb\uff0c\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4f60\u8981\u8d70\u4e86\uff1f\u5230\u54ea\u91cc\u53bb\uff1f\u201d \u3000\u3000\u53f6\u5f00\u8fd8\u662f\u4e0d\u5f00\u53e3\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u60a0\u7136\u9053\uff1a\u201c\u4f60\u82e5\u662f\u627e\u5979\uff0c\u6211\u529d\u4f60\u4e0d\u5982\u8eba\u4e0b\u53bb\u517b\u517b\u795e\uff0c\u56e0\u4e3a\u4f60\u4e00\u5b9a\u627e\u4e0d\u5230\u5979 \u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u60f3\u5f00\u53e3\uff0c\u53c8\u95ed\u4f4f\u3002 \u3000\u3000\u4ed6\u5df2\u5f88\u4e86\u89e3\u4e0a\u5b98\u5c0f\u4ed9\uff0c\u5979\u82e5\u4e0d\u60f3\u8bf4\u7684\u4e8b\uff0c\u6ca1\u6709\u4eba\u80fd\u95ee\u5f97\u51fa\u6765\uff0c\u5979\u82e5\u60f3\u8bf4\uff0c\u5c31\u6839\u672c\u4e0d\u5fc5 \u95ee\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4f60\u82e5\u60f3\u53bb\u627e\u4e86\u7075\u7433\uff0c\u5c31\u4e0d\u5982\u966a\u6211\u5728\u8fd9\u91cc\u8c08\u8c08\u5fc3\uff0c\u56e0\u4e3a\u4f60\u5c31\u7b97\u627e\u5230\u4e86\u5979\uff0c \u4e5f\u53ea\u6709\u89c9\u5f97\u66f4\u96be\u53d7\u3002\u201d \u3000\u3000\u53f6\u5f00\u4e0d\u542c\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4e5f\u8bb8\u4f60\u73b0\u5728\u8fd8\u80fd\u627e\u4e00\u4e2a\u4eba\u3002\u201d \u3000\u3000\u53f6\u5f00\u5df2\u5728\u7a7f\u9774\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u73b0\u5728\u4f60\u552f\u4e00\u53ef\u4ee5\u627e\u5f97\u5230\u7684\u4eba\u5c31\u662f\u97e9\u8d1e\uff0c\u800c\u4e14\u4e00\u627e\u5c31\u53ef\u627e\u5230\uff0c\u4f60\u77e5\u9053\u4e3a\u4ec0 \u4e48\uff1f\u201d \u3000\u3000\u53f6\u5f00\u4e0d\u95ee\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u56e0\u4e3a\u4ed6\u5df2\u8eba\u5728\u68fa\u6750\u91cc\uff0c\u8fde\u52a8\u90fd\u4e0d\u4f1a\u52a8\u4e86\u3002\u201d \u3000\u3000\u53f6\u5f00\u970d\u7136\u7ad9\u4e86\u8d77\u6765\uff0c\u76ee\u5149\u706b\u70ac\u822c\u77aa\u7740\u5979\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u660e\u77e5\u9053\u4ed6\u4e0d\u662f\u6211\u6740\u7684\uff0c\u77aa\u7740\u6211\u5e72\u4ec0\u4e48\uff1f\u4f60\u7740\u60f3\u66ff\u4ed6\u62a5\u4ec7\u5c31\u8be5 \u5148\u627e\u51fa\u4ed6\u7684\u4ec7\u4eba\u6765\u3002\u201d \u3000\u3000\uff19\uff11\uff11 \u3000\u3000\u5979\u6de1\u6de1\u5730\u63a5\u7740\u9053\uff1a\u201c\u53ef\u662f\u6211\u529d\u4f60\u4e0d\u8981\u53bb\uff0c\u4f60\u73b0\u5728\u552f\u4e00\u5e94\u8be5\u505a\u7684\u4e8b\uff0c\u5c31\u662f\u8eba\u4e0b\u53bb\u597d\u597d\u7761\u4e00 \u89c9\u3002\u201d \u3000\u3000\u53f6\u5f00\u6ca1\u6709\u542c\u5979\u8bf4\u5b8c\u8fd9\u53e5\u8bdd\uff0c\u4eba\u5df2\u51b2\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u68fa\u5df2\u76d6\uff0c\u5374\u8fd8\u6ca1\u6709\u4e0a\u9489\uff0c\u8584\u8584\u7684\u68fa\u6750\uff0c\u77ed\u77ed\u7684\u4eba\u751f\u3002 \u3000\u3000\u97e9\u8d1e\u7684\u8138\uff0c\u770b\u6765\u4eff\u4f5b\u8fd8\u5728\u6c89\u7761\uff0c\u4ed6\u672c\u662f\u5728\u6c89\u7761\u4e2d\u6b7b\u7684\u3002 \u3000\u3000\u201c\u6211\u4eec\u53d1\u73b0\u4ed6\u7684\u65f6\u5019\uff0c\u4ed6\u5df2\u7ecf\u65e0\u6551\u4e86\uff0c\u53ea\u597d\u5148\u4e70\u53e3\u68fa\u6750\uff0c\u6682\u65f6\u5c06\u4ed6\u6536\u6b93\uff0c\u4f46\u6211\u4eec\u5374\u8fde\u4ed6 \u59d3\u4ec0\u4e48\u90fd\u4e0d\u77e5\u9053\uff0c\u53ea\u5e0c\u671b\u4ed6\u8fd8\u6709\u4eb2\u621a\u670b\u53cb\u6765\u6536\u4ed6\u7684\u5c38\u3002\u201d \u3000\u3000\u8fd9\u5ba2\u6808\u7684\u638c\u67dc\uff0c\u5012\u4e0d\u662f\u4e2a\u523b\u8584\u7684\u4eba\u3002 \u3000\u3000\u68fa\u6750\u867d\u8584\uff0c\u81f3\u5c11\u603b\u6bd4\u8349\u5e2d\u5f3a\u3002 \u3000\u3000\u201c\u8c22\u8c22\u4f60\u3002\u201d \u3000\u3000\u53f6\u5f00\u771f\u7684\u5f88\u611f\u6fc0\uff0c\u4f46\u5374\u66f4\u5185\u759a\u3001\u6094\u6068\uff0c\u82e5\u4e0d\u662f\u4e3a\u4e86\u4ed6\uff0c\u97e9\u8d1e\u5c31\u4e0d\u4f1a\u53d7\u4f24\uff0c\u82e5\u4e0d\u662f\u4ed6\u7684\u758f \u5ffd\u5927\u610f\uff0c\u97e9\u8d1e\u7684\u4f24\u672c\u53ef\u6cbb\u597d\u7684\uff0c\u53ef\u662f\u73b0\u5728\u97e9\u8d1e\u5df2\u6b7b\u4ed6\u5374\u8fd8\u6d3b\u7740\u3002 \u3000\u3000\u201c\u4ed6\u600e\u4e48\u6b7b\u7684\uff1f\u201d \u3000\u3000\u201c\u662f\u88ab\u4e00\u67c4\u5251\u9489\u6b7b\u5728\u5e8a\u4e0a\u7684\u3002\u201d \u3000\u3000\u201c\u5251\u5462\uff1f\u201d \u3000\u3000\u201c\u5251\u8fd8\u5728\u3002\u201d \u3000\u3000\u5251\u5728\u95ea\u7740\u5149\u3002 \u3000\u3000\u662f\u4e00\u67c4\u5f62\u5f0f\u5f88\u53e4\u96c5\u7684\u957f\u5251\uff0c\u7cbe\u94a2\u767e\u70bc\uff0c\u975e\u5e38\u950b\u5229\uff0c\u5251\u80cc\u4e0a\u5e26\u7740\u677e\u7eb9\u3002 \u3000\u3000\u8840\u8ff9\u5df2\u6d17\u51c0\uff0c\u7528\u9ec4\u5e03\u5305\u7740\u3002 \u3000\u3000\u201c\u6211\u4eec\u5e97\u91cc\u7684\u4e24\u4e2a\u4f19\u8ba1\uff0c\u8d39\u4e86\u5f88\u5927\u7684\u529b\u6c14\uff0c\u624d\u5c06\u8fd9\u5251\u62d4\u51fa\u6765\u3002\u201d \u3000\u3000\u638c\u67dc\u7684\u5728\u8ba8\u597d\u9080\u529f\u3002 \u3000\u3000\u4ed6\u867d\u7136\u5e76\u4e0d\u662f\u523b\u8584\u7684\u4eba\uff0c\u4f46\u4e5f\u5e0c\u671b\u80fd\u5f97\u5230\u70b9\u597d\u5904\uff0c\u80fd\u5f97\u5230\u4e9b\u8865\u507f\u65f6\uff0c\u4ed6\u4e5f\u4e0d\u60f3\u9519\u8fc7\u3002 \u3000\u3000\u53f6\u5f00\u5374\u597d\u50cf\u542c\u4e0d\u61c2\u8fd9\u610f\u601d\u3002 \u3000\u3000\u4ed6\u5fc3\u91cc\u5374\u5728\u601d\u7d22\u7740\u522b\u7684\u4e8b\uff1a\u201c\u8fd9\u4e00\u5251\u83ab\u975e\u4ece\u7a97\u5916\u63b7\u5165\uff0c\u523a\u5165\u4e86\u97e9\u8d1e\u7684\u8138\uff0c\u518d\u9489\u5728\u5e8a\u4e0a \u7684\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4e00\u63b7\u4e4b\u529b\u5b9e\u5728\u4e0d\u5c0f\u3002\u201d \u3000\u3000\u638c\u67dc\u7684\u53c8\u9053\uff1a\u201c\u8ddf\u5927\u7237\u4f60\u4e00\u8d77\u4f4f\u5e97\u7684\u90a3\u4f4d\u59d1\u5a18\uff0c\u524d\u5929\u665a\u4e0a\u4e5f\u56de\u6765\u8fc7\u4e00\u6b21\uff0c\u5979\u597d\u50cf\u4e5f\u75c5 \u4e86\uff0c\u662f\u88ab\u90a3\u4f4d\u51fb\u8d25\u4e86\u5357\u5b98\u8fdc\u7684\u90ed\u5927\u4fa0\u62b1\u56de\u6765\u7684\u3002\u201d \u3000\u3000\u201c\u4ed6\u4eec\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u201c\u4e0d\u77e5\u9053\uff0c\u4ed6\u4eec\u53ea\u51fa\u73b0\u4e86\u4e00\u4e0b\u5b50\u3002\u201d \u3000\u3000\u4e00\u4e2a\u4f19\u8ba1\u8865\u5145\u7740\u9053\uff1a\u201c\u90a3\u5929\u665a\u4e0a\u662f\u6211\u5f53\u503c\uff0c\u6211\u521a\u8fdb\u4e86\u9662\u5b50\uff0c\u5c31\u770b\u89c1\u5c4b\u91cc\u6709\u9053\u5149\u8292\u4e00\u95ea\uff0c \u5c31\u50cf\u95ea\u7535\u4e00\u6837\u3002\u201d \u3000\u3000\u201c\u7b49\u6211\u8d76\u8fc7\u53bb\u65f6\uff0c\u5927\u7237\u4f60\u7684\u8fd9\u4f4d\u670b\u53cb\u5df2\u88ab\u9489\u6b7b\u5728\u5e8a\u4e0a\u3002\u201d \u3000\u3000\u7136\u540e\u90ed\u5927\u4fa0\u5c31\u62b1\u7740\u90a3\u4f4d\u59d1\u5a18\u56de\u6765\u4e86\uff0c\u90ed\u5927\u4fa0\u548c\u5357\u5b98\u8fdc\u6bd4\u5251\u65f6\uff0c\u6211\u4e5f\u62bd\u7a7a\u53bb\u770b\u4e86\uff0c\u6240\u4ee5\u6211 \u8ba4\u5f97\u4ed6\u3002\u201d \u3000\u3000\u201c\u7b49\u6211\u53bb\u62a5\u544a\u4e86\u638c\u67dc\uff0c\u518d\u56de\u53bb\u770b\u65f6\uff0c\u90ed\u5927\u4fa0\u548c\u90a3\u4f4d\u59d1\u5a18\u53c8\u4e0d\u89c1\u4e86\u3002\u201d \u3000\u3000\u53f6\u5f00\u731c\u5f97\u4e0d\u9519\u3002 \u3000\u3000\u8fd9\u4e00\u5251\u679c\u7136\u662f\u4ece\u7a97\u5916\u63b7\u8fdb\u53bb\u7684\uff0c\u6240\u4ee5\u8fd9\u5e97\u4f19\u624d\u4f1a\u770b\u89c1\u90a3\u95ea\u7535\u822c\u7684\u5251\u5149\u3002 \u3000\u3000\u7b49\u8fd9\u51f6\u624b\u60f3\u53d6\u56de\u4ed6\u51f6\u5668\u65f6\uff0c\u90ed\u5b9a\u5df2\u56de\u6765\u3002 \u3000\u3000\u4ed6\u662f\u4e58\u5d14\u7389\u771f\u5df2\u5c06\u53f6\u5f00\u5e26\u8d70\u540e\uff0c\u90ed\u5b9a\u8fd8\u6ca1\u6709\u5e26\u4e01\u7075\u7433\u56de\u6765\u524d\uff0c\u5728\u90a3\u7247\u523b\u95f4\u4e0b\u624b\u7684\u3002 \u3000\u3000\u90a3\u65f6\u95f4\u5e76\u4e0d\u957f\uff0c\u4e5f\u8bb8\u4ed6\u6839\u672c\u6ca1\u65f6\u95f4\u53d6\u56de\u8fd9\u67c4\u5251\uff0c\u4e5f\u8bb8\u4ed6\u6025\u5207\u95f4\u6ca1\u6709\u5c06\u5251\u62d4\u51fa\u6765\uff0c\u4e24\u4e2a\u4f19 \u8ba1\u8d39\u4e86\u5f88\u5927\u7684\u529b\uff0c\u624d\u5c06\u8fd9\u67c4\u5251\u62d4\u51fa\u6765\u7684\u3002 \u3000\u3000\u201c\u90ed\u5b9a\u53c8\u5c06\u4e01\u7075\u7433\u5e26\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4eec\u4e3a\u4ec0\u4e48\u4e0d\u5728\u8fd9\u91cc\u7b49\uff1f\u53c8\u6ca1\u6709\u53bb\u627e\u4ed6\uff1f\u201d \u3000\u3000\u8fd9\u4e9b\u95ee\u9898\uff0c\u53f6\u5f00\u4e0d\u613f\u53bb\u60f3\uff0c\u73b0\u5728\u4ed6\u5fc3\u91cc\u53ea\u60f3\u7740\u4e00\u4ef6\u4e8b\u2014\u2014\u7edd\u4e0d\u80fd\u8ba9\u97e9\u8d1e\u767d\u6b7b\u3002 \u3000\u3000\u4ed6\u5fc3\u91cc\u7684\u6b49\u759a\u6094\u6068\uff0c\u5df2\u5c06\u53d8\u4e3a\u6124\u6012\u3002 \u3000\u3000\u201c\u8fd9\u67c4\u5251\u4f60\u80fd\u4e0d\u80fd\u8ba9\u6211\u5e26\u8d70\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u53ef\u4ee5\u2026\u2026\u201d \u3000\u3000\u53f6\u5f00\u8bf4\u8d70\u5c31\u8d70\u3002 \u3000\u3000\u638c\u67dc\u7684\u6025\u4e86\uff1a\u201c\u5927\u7237\u4f60\u96be\u9053\u4e0d\u51c6\u5907\u6536\u4f60\u8fd9\u4f4d\u670b\u53cb\u7684\u5c38\uff1f\u201d \u3000\u3000\u201c\u6211\u4f1a\u6765\u7684\uff0c\u660e\u540e\u5929\u6211\u4e00\u5b9a\u6765\u3002\u201d \u3000\u3000\u53f6\u5f00\u5e76\u4e0d\u662f\u4e0d\u660e\u767d\u8fd9\u638c\u67dc\u7684\u610f\u601d\uff0c\u53ea\u4e0d\u8fc7\u4e00\u4e2a\u4eba\u56ca\u7a7a\u5982\u6d17\u3001\u8eab\u65e0\u5206\u6587\u7684\u65f6\u5019\uff0c\u5c31\u53ea\u597d\u88c5 \u88c5\u50bb\u4e86\u3002 \u3000\u3000\u9633\u5149\u707f\u70c2\u3002 \u3000\u3000\u5341\u5929\u6765\uff0c\u4eca\u5929\u662f\u7b2c\u4e00\u6b21\u770b\u5230\u5982\u6b64\u707f\u70c2\u7684\u9633\u5149\u3002 \u3000\u3000\u8857\u4e0a\u7684\u79ef\u96ea\u5df2\u6eb6\uff0c\u6ce5\u6cde\u6ee1\u8def\u3002 \u3000\u3000\u4f46\u8857\u4e0a\u7684\u4eba\u5374\u8fd8\u662f\u5f88\u591a\uff0c\u5927\u5bb6\u90fd\u60f3\u4e58\u7740\u8fd9\u96be\u5f97\u7684\u597d\u5929\u6c14\uff0c\u51fa\u53bb\u8d70\u8d70\u3002 \u3000\u3000\u201c\u516b\u65b9\u9556\u5c40\u201d\u7684\u91d1\u5b57\u62db\u724c\uff0c\u5728\u9633\u5149\u4e0b\u770b\u6765\uff0c\u6c14\u6d3e\u66f4\u4e0d\u51e1\u3002 \u3000\u3000\u4e00\u4e2a\u7a7f\u7740\u9752\u5e03\u68c9\u7946\u7684\u8001\u4eba\uff0c\u6b63\u5728\u95e8\u524d\u6253\u626b\u7740\u79ef\u96ea\u548c\u6ce5\u6cde\u3002 \u3000\u3000\u53f6\u5f00\u5927\u6b65\u8d70\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u4ed6\u53ea\u8981\u8d70\u5f97\u7a0d\u5fae\u5feb\u4e9b\uff0c\u80f8\u53e3\u7684\u4f24\u5c31\u4f1a\u53d1\u75bc\uff0c\u4f46\u4ed6\u5374\u8fd8\u662f\u8d70\u5f97\u5f88\u5feb\u3002\u8089\u4f53\u4e0a\u7684\u75db\u82e6\uff0c\u4ed6\u4e00 \u70b9\u4e5f\u4e0d\u5728\u4e4e\u3002 \u3000\u3000\u4ed6\u8d70\u8fdb\u9662\u5b50\u7684\u65f6\u5019\uff0c\u6b63\u6709\u4e24\u4e2a\u4eba\u4ece\u524d\u9762\u7684\u5927\u5385\u91cc\u51fa\u6765\u3002 \u3000\u3000\u4e00\u4e2a\u56db\u5341\u591a\u5c81\u7684\u4e2d\u5e74\u4eba\uff0c\u8863\u7740\u5f88\u534e\u4e3d\uff0c\u76f8\u8c8c\u5f88\u5a01\u6b66\uff0c\u624b\u91cc\u634f\u7740\u53cc\u94c1\u80c6\uff0c\u201c\u53ee\u53ee\u5f53\u5f53\u201d\u7684 \u54cd\u3002 \u3000\u3000\u53e6\u4e00\u4e2a\u5e74\u7eaa\u8f83\u8f7b\uff0c\u5374\u7559\u7740\u5f88\u6574\u9f50\u7684\u5c0f\u80e1\u5b50\uff0c\u767d\u751f\u751f\u7684\u8138\uff0c\u5e72\u5e72\u51c0\u51c0\u7684\u624b\u3002 \u3000\u3000\u53f6\u5f00\u8fce\u8fc7\u53bb\u3002 \u3000\u3000\u4ed6\u5fc3\u60c5\u597d\u7684\u65f6\u5019\uff0c\u672c\u662f\u4e2a\u5f88\u6709\u793c\u8c8c\u7684\u4eba\uff0c\u5f88\u5ba2\u6c14\u7684\u4eba\uff0c\u53ef\u662f\u4ed6\u73b0\u5728\u5fc3\u60c5\u5e76\u4e0d\u597d\u3002 \u3000\u3000\u4ed6\u8fde\u62b1\u62f3\u90fd\u6ca1\u6709\u62b1\u62f3\uff0c\u5c31\u95ee\u9053\uff1a\u201c\u8fd9\u91cc\u7684\u603b\u9556\u5934\u662f\u8c01\uff1f\u201d \u3000\u3000\u634f\u7740\u94c1\u80c6\u7684\u4e2d\u5e74\u4eba\u4e0a\u4e0a\u4e0b\u4e0b\u770b\u4ed6\u4e24\u773c\uff0c\u6c89\u7740\u8138\u9053\uff1a\u201c\u8fd9\u91cc\u7684\u603b\u9556\u5934\u5c31\u662f\u6211\u3002\u201d \u3000\u3000\u5bf9\u4e00\u4e2a\u65e0\u793c\u7684\u4eba\uff0c\u4ed6\u5f53\u7136\u4e5f\u4e0d\u4f1a\u592a\u5ba2\u6c14\u3002 \u3000\u3000\u94c1\u80c6\u9547\u516b\u65b9\u6234\u9ad8\u5c97\uff0c\u5e76\u4e0d\u662f\u597d\u60f9\u7684\u4eba\u3002 \u3000\u3000\u201c\u4f60\u53c8\u662f\u4ec0\u4e48\u4eba\uff0c\u6765\u627e\u8c01\u7684\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u5c31\u662f\u6765\u627e\u4f60\u7684\u3002\u201d \u3000\u3000\u6234\u9ad8\u5c97\u9053\uff1a\u201c\u6709\u4f55\u89c1\u6559\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6709\u4e24\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u6234\u9ad8\u5c97\u9053\uff1a\u201c\u4f60\u4e0d\u59a8\u5148\u8bf4\u4e00\u4ef6\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u8981\u6765\u501f\u4e94\u767e\u4e24\u94f6\u5b50\uff0c\u4e09\u5929\u4e4b\u5185\u5c31\u8fd8\u7ed9\u4f60\u3002\u201d \u3000\u3000\u6234\u9ad8\u5c97\u7b11\u4e86\uff0c\u773c\u775b\u91cc\u5168\u65e0\u7b11\u610f\uff0c\u51b7\u51b7\u5730\u76ef\u7740\u53f6\u5f00\u7684\u80f8\u819b\u9053\uff1a\u201c\u4f60\u53d7\u4e86\u4f24\u3002\u201d \u3000\u3000\u53f6\u5f00\u7684\u4f24\u53e3\u53c8\u5df2\u5d29\u88c2\uff0c\u8840\u6e0d\u5df2\u6e17\u8fc7\u8863\u88f3\u3002 \u3000\u3000\u6234\u9ad8\u5c97\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u82e5\u4e0d\u60f3\u518d\u53d7\u4e00\u6b21\u4f24\uff0c\u5c31\u6700\u597d\u8d76\u5feb\u4ece\u4f60\u6765\u7684\u90a3\u6761\u8def\u6eda\u56de\u53bb\uff01\u201d \u3000\u3000\u53f6\u5f00\u51dd\u89c6\u7740\u4ed6\uff0c\u5f90\u5f90\u9053\uff1a\u201c\u6211\u4e45\u5df2\u542c\u8bf4\u94c1\u80c6\u9547\u516b\u65b9\u662f\u4e2a\u6a2a\u884c\u9738\u9053\u7684\u4eba\uff0c\u770b\u6765\u679c\u7136\u6ca1\u6709\u8bf4 \u9519\u3002\u201d \u3000\u3000\u6234\u9ad8\u5c97\u51b7\u7b11\u3002 \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u5411\u4f60\u501f\u4e94\u767e\u4e24\u94f6\u5b50\uff0c\u4f60\u53ef\u4ee5\u4e0d\u501f\uff0c\u53c8\u4f55\u5fc5\u518d\u8981\u6211\u53d7\u4e00\u6b21\u4f24\uff1f\u53c8\u4f55\u5fc5\u8981\u6211\u6eda \u56de\u53bb\uff1f\u201d \u3000\u3000\u6234\u9ad8\u5c97\u6012\u9053\uff1a\u201c\u6211\u5c31\u8981\u4f60\u6eda\u3002\u201d \u3000\u3000\u4ed6\u7a81\u7136\u51fa\u624b\uff0c\u6293\u53f6\u5f00\u7684\u8863\u895f\uff0c\u50cf\u662f\u60f3\u5c06\u53f6\u5f00\u4e00\u628a\u6293\u8d77\u6765\uff0c\u6454\u51fa\u53bb\u3002 \u3000\u3000\u4ed6\u7684\u624b\u575a\u786c\u7c97\u7cd9\uff0c\u9752\u7b4b\u66b4\u9732\uff0c\u663e\u7136\u7ec3\u8fc7\u9e70\u722a\u529f\u4e00\u7c7b\u7684\u529f\u592b\u3002 \u3000\u3000\u53f6\u5f00\u6ca1\u6709\u52a8\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u8fd9\u4e00\u6293\uff0c\u5e76\u6ca1\u6709\u6293\u4f4f\u53f6\u5f00\u7684\u8863\u895f\u3002 \u3000\u3000\u4ed6\u6293\u4f4f\u4e86\u53f6\u5f00\u7684\u624b\u3002 \u3000\u3000\u53f6\u5f00\u7684\u624b\u5df2\u8fce\u4e0a\u53bb\uff0c\u4e24\u4e2a\u4eba\u5341\u6307\u4e92\u52fe\uff0c\u6234\u9ad8\u5c97\u51b7\u7b11\u7740\u8f7b\u53f1\u4e00\u58f0\uff1a\u201c\u65ad\uff01\u201d \u3000\u3000\u4ed6\u81ea\u6043\u9e70\u722a\u529f\u5df2\u7ec3\u5230\u516b\u4e5d\u6210\u706b\u5019\uff0c\u7ade\u60f3\u5c06\u53f6\u5f00\u7684\u4e94\u6307\u6298\u65ad\u3002 \u3000\u3000\u53f6\u5f00\u7684\u624b\u6307\u5f53\u7136\u6ca1\u6709\u65ad\u3002 \u3000\u3000\u6234\u9ad8\u5c97\u5ffd\u7136\u89c9\u5f97\u5bf9\u65b9\u624b\u6307\u4e0a\u7684\u529b\u91cf\u7ade\u8fdc\u6bd4\u4ed6\u66f4\u5f3a\u5341\u500d\u3002\u53ea\u8981\u4e00\u7528\u529b\uff0c\u4ed6\u7684\u4e94\u6839\u624b\u6307\u53cd\u800c \u5c31\u8981\u88ab\u6298\u65ad\u3002 \u3000\u3000\u2014\u2014\u98de\u5200\u672c\u662f\u7528\u6307\u529b\u53d1\u51fa\u7684\uff0c\u82e5\u6ca1\u6709\u5f3a\u52b2\u7684\u6307\u529b\uff0c\u600e\u4e48\u80fd\u53d1\u5f97\u51fa\u90a3\u65e0\u575a\u4e0d\u6467\u7684\u98de\u5200\u3002 \u3000\u3000\u6234\u9ad8\u5c97\u8138\u8272\u53d8\u4e86\uff0c\u989d\u4e0a\u5df2\u5192\u51fa\u9ec4\u8c46\u822c\u7684\u51b7\u6c57\u3002 \u3000\u3000\u53ef\u662f\u53f6\u5f00\u4e5f\u6ca1\u6709\u7528\u529b\uff0c\u53ea\u662f\u51b7\u51b7\u5730\u770b\u7740\u4ed6\uff0c\u6de1\u6de1\u9053\uff1a\u201c\u4f60\u62d7\u65ad\u8fc7\u51e0\u4e2a\u4eba\u7684\u624b\u6307\u4e86\uff1f\u201d \u3000\u3000\u6234\u9ad8\u5c97\u54ac\u7740\u7259\uff0c\u4e0d\u6562\u5f00\u53e3\u3002 \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u4e0b\u6b21\u8981\u62d7\u522b\u4eba\u7684\u624b\u6307\u65f6\uff0c\u6700\u597d\u60f3\u60f3\u6b64\u65f6\u6b64\u523b\u3002\u201d \u3000\u3000\u4ed6\u7a81\u7136\u677e\u5f00\u624b\uff0c\u626d\u5934\u5c31\u8d70\u3002 \u3000\u3000\u90a3\u4e00\u76f4\u80cc\u8d1f\u7740\u53cc\u624b\uff0c\u5728\u65c1\u8fb9\u51b7\u773c\u65c1\u89c2\u7684\u5e74\u8f7b\u4eba\u9053\uff1a\u201c\u8bf7\u7559\u6b65\u3002\u201d \u3000\u3000\u53f6\u5f00\u505c\u4e0b\uff1a\u201c\u4f60\u6709\u4e94\u767e\u4e24\u94f6\u5b50\u501f\uff1f\u201d \u3000\u3000\u8fd9\u5e74\u8f7b\u4eba\u7b11\u4e86\u7b11\uff0c\u53cd\u95ee\u9053\uff1a\u201c\u670b\u53cb\u5c0a\u59d3\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u53f6\u3002\u201d \u3000\u3000\u5e74\u8f7b\u4eba\u9053\uff1a\u201c\u6811\u53f6\u7684\u53f6\uff1f\u201d \u3000\u3000\u53f6\u5f00\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u5e74\u8f7b\u4eba\u51dd\u89c6\u7740\u4ed6\uff0c\u9053\uff1a\u201c\u53f6\u5f00\uff1f\u201d \u3000\u3000\u53f6\u5f00\u53c8\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5f00\u5fc3\u7684\u5f00\u3002\u201d \u3000\u3000\u6234\u9ad8\u5c97\u8038\u7136\u52a8\u5bb9\u9053\uff1a\u201c\u9601\u4e0b\u5c31\u662f\u53f6\u5f00\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6b63\u662f\u3002\u201d \u3000\u3000\u6234\u9ad8\u5c97\u957f\u957f\u5410\u51fa\u53e3\u6c14\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u9601\u4e0b\u4e3a\u4f55\u4e0d\u65e9\u8bf4\uff1f\u201d \u3000\u3000\u53f6\u5f00\u6de1\u6de1\u9053\uff1a\u201c\u6211\u5e76\u4e0d\u662f\u6765\u6253\u79cb\u98ce\u7684\uff0c\u53ea\u4e0d\u8fc7\u662f\u6765\u501f\u800c\u5df2\uff0c\u800c\u4e14\u53ea\u501f\u4e09\u5929\u3002\u201d \u3000\u3000\u6234\u9ad8\u5c97\u9053\uff1a\u201c\u4e94\u767e\u4e24\u5df2\u591f\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u53ea\u4e0d\u8fc7\u60f3\u4e70\u4e24\u53e3\u68fa\u6750\u3002\u201d \u3000\u3000\u6234\u9ad8\u5c1a\u4e0d\u6562\u518d\u95ee\uff0c\u540e\u9762\u5df2\u6709\u4e2a\u673a\u8b66\u7684\u5e10\u623f\u9001\u6765\u4e86\u4e94\u767e\u4e24\u94f6\u7968\u3002 \u3000\u3000\u201c\u8bf7\u6536\u4e0b\u3002\u201d \u3000\u3000\u53f6\u5f00\u5e76\u4e0d\u5ba2\u6c14\uff0c\u97e9\u8d1e\u7684\u4e27\u4e8b\u56fa\u7136\u8981\u529e\uff0c\u4f0a\u591c\u54ed\u7684\u5c38\u4f53\u4e5f\u8981\u6536\u6b93\u3002 \u3000\u3000\u4ed6\u5e76\u4e0d\u662f\u90a3\u79cd\u6740\u4e86\u4eba\u540e\u5c31\u4e0d\u7ba1\u7684\u4eba\uff0c\u4ed6\u9700\u8981\u8fd9\u7b14\u94b1\u3002 \u3000\u3000\u524d\u5028\u540e\u606d\u7684\u6234\u9ad8\u5c97\u53c8\u5728\u95ee\uff1a\u201c\u9601\u4e0b\u521a\u624d\u662f\u8bf4\u6709\u4e24\u4ef6\u4e8b\u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u8fd8\u8981\u6253\u542c\uff0d\u4e2a\u4eba\u3002\u201d \u3000\u3000\u6234\u9ad8\u5c97\u9053\uff1a\u201c\u8c01\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u5415\u8fea\uff0c\u767d\u8863\u5251\u5ba2\u5415\u8fea\u3002\u201d \u3000\u3000\u6234\u9ad8\u5c97\u8138\u4e0a\u5ffd\u7136\u9732\u51fa\u79cd\u5f88\u5947\u602a\u7684\u8868\u60c5\u3002 \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u636e\u8bf4\u4ed6\u5df2\u5230\u957f\u5b89\uff0c\u4f60\u77e5\u4e0d\u77e5\u9053\u4ed6\u5728\u54ea\u91cc\uff1f\u201d\u90a3\u7559\u7740\u5c0f\u80e1\u5b50\u7684\u5e74\u8f7b\u4eba\u5ffd\u7136\u7b11\u4e86 \u7b11\uff0c\u9053\uff1a\u201c\u5c31\u5728\u8fd9\u91cc\u3002\u201d \u3000\u3000\u8fd9\u5e74\u8f7b\u4eba\u6001\u5ea6\u5f88\u65af\u6587\uff0c\u957f\u5f97\u5f88\u79c0\u6c14\uff0c\u8eab\u4e0a\u679c\u7136\u7a7f\u7740\u4ef6\u96ea\u767d\u957f\u888d\uff0c\u76ee\u5149\u95ea\u52a8\u95f4\uff0c\u5e26\u7740\u79cd\u8bf4 \u4e0d\u51fa\u7684\u51b7\u6f20\u9ad8\u505a\u4e4b\u610f\u3002 \u3000\u3000\u53f6\u5f00\u7ec8\u4e8e\u770b\u6e05\u4e86\u4ed6\u3002 \u3000\u3000\u201c\u4f60\u5c31\u662f\u5415\u8fea\uff1f\u201d \u3000\u3000\u201c\u662f\uff01\u201d \u3000\u3000\u53f6\u5f00\u89e3\u5f00\u4e86\u5de6\u624b\u63d0\u7740\u7684\u9ec4\u5e03\u5305\u88b1\uff0c\u53d6\u51fa\u4e86\u90a3\u67c4\u5251\uff0c\u53cd\u624b\u634f\u4f4f\u5251\u5c16\uff0c\u9012\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u201c\u4f60\u8ba4\u4e0d\u8ba4\u5f97\u8fd9\u67c4\u5251\uff1f\u201d \u3000\u3000\u5415\u8fea\u53ea\u770b\u4e86\u4e00\u773c\uff1a\u201c\u8fd9\u662f\u6b66\u5f53\u7684\u677e\u7eb9\u5251\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u662f\u4e0d\u662f\u53ea\u6709\u6b66\u5f53\u5f1f\u5b50\u624d\u80fd\u7528\u8fd9\u67c4\u5251\uff1f\u201d \u3000\u3000\u5415\u8fea\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u8fd9\u662f\u4e0d\u662f\u4f60\u7684\u5251\uff1f\u201d \u3000\u3000\u5415\u8fea\u9053\uff1a\u201c\u4e0d\u662f\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u7684\u5251\u5462\uff1f\u201d \u3000\u3000\u5415\u8fea\u50b2\u7136\u9053\uff1a\u201c\u6211\u8fd1\u5e74\u5df2\u4e0d\u7528\u5251\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u7528\u624b\uff1f\u201d \u3000\u3000\u5415\u8fea\u4e00\u76f4\u80cc\u7740\u53cc\u624b\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6709\u4e9b\u4eba\u7684\u624b\uff0c\u4e5f\u4e00\u6837\u662f\u5229\u5668\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u53ef\u662f\u4f60\u82e5\u8981\u4ece\u7a97\u5916\u6740\u4eba\uff0c\u8fd8\u662f\u5f97\u7528\u5251\u3002\u201d \u3000\u3000\u5415\u8fea\u76b1\u4e86\u76b1\u7709\uff0c\u597d\u50cf\u542c\u4e0d\u61c2\u8fd9\u53e5\u8bdd\u3002 \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u56e0\u4e3a\u4f60\u7684\u624b\u4e0d\u591f\u957f\u3002\u201d \u3000\u3000\u5415\u8fea\u9053\uff1a\u201c\u4f60\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u7684\u610f\u601d\u4f60\u5e94\u8be5\u660e\u767d\u3002\u201d \u3000\u3000\u5415\u8fea\u9053\uff1a\u201c\u4f60\u662f\u8bf4\uff0c\u6211\u7528\u8fd9\u67c4\u5251\u6740\u4e86\u4eba\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u4e0d\u627f\u8ba4\uff1f\u201d \u3000\u3000\u5415\u8fea\u9053\uff1a\u201c\u6211\u6740\u4e86\u8c01\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u6740\u4eba\u4ece\u4e0d\u95ee\u5bf9\u65b9\u7684\u540d\u5b57\uff1f\u201d \u3000\u3000\u660c\u8fea\u9053\uff1a\u201c\u73b0\u5728\u6211\u6b63\u5728\u95ee\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4ed6\u59d3\u97e9\uff0c\u53eb\u97e9\u8d1e\u3002\u201d \u3000\u3000\u201c\u97e9\u8d1e\uff1f\u201d\u5415\u8fea\u56de\u8fc7\u5934\u6765\u95ee\u6234\u9ad8\u5c97\uff0c\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u8fd9\u4e2a\u4eba\uff1f\u201d \u3000\u3000\u6234\u9ad8\u5c97\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4ed6\u662f\u536b\u5929\u9e4f\u7684\u667a\u56ca\uff0c\u522b\u4eba\u90fd\u53eb\u4ed6\u9525\u5b50\u3002\u201d \u3000\u3000\u5415\u8fea\u76ee\u4e2d\u9732\u51fa\u4e86\u8f7b\u8511\u4e4b\u8272\uff0c\u95ee\u53f6\u5f00\uff1a\u201c\u8fd9\u9525\u5b50\u662f\u4f60\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u662f\u6211\u670b\u53cb\u3002\u201d \u3000\u3000\u5415\u8fea\u9053\uff1a\u201c\u4f60\u60f3\u66ff\u4ed6\u590d\u4ec7\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u201c\u4f60\u8ba4\u4e3a\u662f\u6211\u6740\u4e86\u4ed6\u7684\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u5415\u8fea\u505a\u7136\u9053\uff1a\u201c\u5c31\u7b97\u662f\u6211\u6740\u7684\u53c8\u5982\u4f55\uff1f\u8fd9\u79cd\u4eba\u83ab\u8bf4\u53ea\u6740\u4e86\u4e00\u4e2a\uff0c\u5c31\u7b97\u6740\u4e86\u5341\u4e2a\u516b\u4e2a\uff0c\u4e5f \u4e0d\u59a8\u4e00\u8d77\u7b97\u5728\u6211\u7684\u5e10\u4e0a\u3002\u201d \u3000\u3000\u53f6\u5f00\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u4ee5\u4e3a\u4f60\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u5415\u8fea\u9053\uff1a\u201c\u662f\u4e2a\u4e0d\u6015\u522b\u4eba\u6765\u627e\u6211\u9ebb\u70e6\u7684\u4eba\uff0c\u7b49\u4f60\u7684\u4f24\u597d\u4e86\uff0c\u968f\u65f6\u90fd\u53ef\u4ee5\u6765\u627e\u6211\u590d\u4ec7\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u90a3\u5012\u4e0d\u5fc5\u3002\u201d \u3000\u3000\u5415\u8fea\u9053\uff1a\u201c\u4e0d\u5fc5\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4e0d\u5fc5\u7b49\u3002\u201d \u3000\u3000\u5415\u8fea\u9053\uff1a\u201c\u4f60\u73b0\u5728\u5c31\u60f3\u52a8\u624b\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4eca\u5929\u7684\u5929\u6c14\u4e0d\u9519\uff0c\u8fd9\u5730\u65b9\u4e5f\u4e0d\u9519\u3002\u201d \u3000\u3000\u5415\u8fea\u770b\u4e86\u770b\u4ed6\uff0c\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u4f60\u521a\u624d\u8bf4\u8981\u4e70\u4e24\u53e3\u68fa\u6750\uff0c\u4e00\u53e3\u5c31\u662f\u7ed9\u97e9\u8d1e\u7684\uff1f\u201d \u3000\u3000\u53f6\u5f00\u70b9\u70b9\u5934\u3002 \u3000\u3000\u5415\u8fea\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u53e3\u5462\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u7ed9\u4f0a\u591c\u54ed\u3002\u201d \u3000\u3000\u5415\u8fea\u9053\uff1a\u201c\u7ea2\u9b54\u624b\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u5415\u8fea\u9053\uff1a\u201c\u4ed6\u5df2\u6b7b\u5728\u4f60\u624b\u4e0b\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u6740\u4eba\u540e\u7edd\u4e0d\u4f1a\u5fd8\u4e86\u66ff\u4eba\u6536\u5c38\u3002\u201d \u3000\u3000\u5415\u8fea\u9053\uff1a\u201c\u597d\uff0c\u4f60\u82e5\u6b7b\u4e86\uff0c\u8fd9\u4e24\u53e3\u68fa\u6750\u6211\u5c31\u66ff\u4f60\u4e70\u3002\u4f60\u7684\u68fa\u6750\u6211\u4e5f\u4e70\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u7528\u4e0d\u7740\uff0c\u6211\u82e5\u6b7b\u4e86\uff0c\u4f60\u4e0d\u59a8\u5c06\u6211\u7684\u5c38\u4f53\u62ff\u53bb\u5582\u72d7\u3002\u201d \u3000\u3000\u5415\u8fea\u7a81\u7136\u5927\u7b11\uff0c\u4ef0\u9762\u7b11\u9053\uff1a\u201c\u597d\uff01\u597d\u6781\u4e86\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u82e5\u6b7b\u4e86\u5462\uff1f\u201d \u3000\u3000\u5415\u8fea\u9053\uff1a\u201c\u6211\u82e5\u6b7b\u4e86\uff0c\u4f60\u4e0d\u59a8\u5c06\u6211\u7684\u5c38\u4f53\u4e00\u5757\u5757\u5272\u4e0b\u6765\uff0c\u4f9b\u5728\u97e9\u8d1e\u7684\u7075\u4f4d\u524d\uff0c\u5403\u4e00\u5757 \u8089\uff0c\u4e0b\u4e00\u53e3\u9152\u3002\u201d \u3000\u3000\u53f6\u5f00\u4e5f\u5927\u7b11\uff0c\u9053\uff1a\u201c\u597d\uff0c\u597d\u6781\u4e86\uff0c\u7537\u5b50\u6c49\u8981\u66ff\u670b\u53cb\u590d\u4ec7\uff0c\u6b63\u5f53\u5982\u6b64\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u8f6c\u8fc7\u8eab\uff0c\u80cc\u671d\u7740\u5415\u8fea\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u7684\u4f24\u53e3\u53c8\u88ab\u4ed6\u7684\u5927\u7b11\u5d29\u88c2\uff0c\u53c8\u8ff8\u51fa\u4e86\u8840\u3002 \u3000\u3000\u9633\u5149\u707f\u70c2\u3002 \u3000\u3000\u6709\u5f88\u591a\u4eba\u90fd\u559c\u6b22\u5728\u8fd9\u79cd\u5929\u6c14\u6740\u4eba\uff0c\u56e0\u4e3a\u8840\u5e72\u5f97\u5feb\u3002 \u3000\u3000\u4ed6\u81ea\u5df1\u82e5\u88ab\u6740\uff0c\u8840\u4e5f\u5e72\u5f97\u5feb\u3002 \u3000\u3000\u5415\u8fea\u7ad9\u5728\u592a\u9633\u4e0b\uff0c\u8fd8\u662f\u80cc\u8d1f\u7740\u53cc\u624b\u3002 \u3000\u3000\u4ed6\u5bf9\u81ea\u5df1\u8fd9\u53cc\u624b\u7684\u73cd\u60dc\uff0c\u5c31\u50cf\u5b88\u8d22\u5974\u73cd\u60dc\u81ea\u5df1\u7684\u8d22\u5bcc\u4e00\u6837\uff0c\u8fde\u770b\u90fd\u4e0d\u613f\u88ab\u4eba\u770b\u3002 \u3000\u3000\u53f6\u5f00\u7f13\u7f13\u5730\u8d70\u8fc7\u53bb\uff0c\u7b2c\u4e8c\u6b21\u5c06\u5251\u9012\u7ed9\u4ed6\u3002 \u3000\u3000\u201c\u8fd9\u662f\u4f60\u7684\u5251\u3002\u201d \u3000\u3000\u5415\u8fea\u51b7\u7b11\u7740\u63a5\u8fc7\u6765\uff0c\u7a81\u7136\u6325\u624b\uff0c\u957f\u5251\u8131\u624b\u98de\u51fa\uff0c\u201c\u593a\u201d\u5730\u9489\u5728\u4e94\u4e08\u5916\u7684\u4e00\u68f5\u6811\u4e0a\u3002 \u3000\u3000\u5251\u950b\u5165\u6728\uff0c\u51e0\u4e4e\u5df2\u6ca1\u5230\u5251\u67c4\u3002 \u3000\u3000\u8fd9\u4e00\u63b7\u4e4b\u529b\uff0c\u5df2\u8db3\u591f\u7a7f\u8fc7\u4efb\u4f55\u4eba\u7684\u8eab\u5b50\uff0c\u5c06\u4eba\u9489\u5728\u5e8a\u4e0a\u3002 \u3000\u3000\u53f6\u5f00\u7684\u77b3\u5b54\u6536\u7f29\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u597d\uff0c\u679c\u7136\u662f\u6740\u4eba\u7684\u5251\u3002\u201d \u3000\u3000\u5415\u8fea\u53c8\u80cc\u8d1f\u53cc\u624b\uff0c\u505a\u7136\u9053\uff1a\u201c\u6211\u8bf4\u8fc7\uff0c\u6211\u5df2\u4e0d\u7528\u5251\uff0c\u201d\u53f6\u5f00\u9053\uff1a\u201c\u6211\u542c\u8bf4\u8fc7\u4e86\u3002\u201d \u3000\u3000\u5415\u8fea\u9053\uff1a\u201c\u4f60\u6740\u4eba\u81ea\u7136\u4e5f\u4e0d\u7528\u5251\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4ece\u6765\u4e0d\u7528\u3002\u201d \u3000\u3000\u5415\u8fea\u76ef\u7740\u4ed6\u7684\u624b\uff0c\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u4f60\u7684\u5200\u5462\uff1f\u201d \u3000\u3000\u4ed6\u5f53\u7136\u77e5\u9053\u53f6\u5f00\u7684\u5200\u3002 \u3000\u3000\u6c5f\u6e56\u4e2d\u4eba\u51e0\u4e4e\u5df2\u6ca1\u6709\u4eba\u4e0d\u77e5\u9053\u53f6\u5f00\u7684\u5200\u3002 \u3000\u3000\u53f6\u5f00\u51dd\u89c6\u7740\u4ed6\uff0c\u7b49\u4e86\u5f88\u4e45\uff0c\u624d\u51b7\u51b7\u9053\uff1a\u201c\u5200\u5728\u3002\u201d \u3000\u3000\u4ed6\u7684\u624b\u4e00\u7ffb\uff0c\u5200\u5df2\u5728\u624b\uff0c\u96ea\u4eae\u7684\u5200\uff0c\u5200\u950b\u8584\u800c\u5229\uff0c\u5728\u9633\u4e0b\u95ea\u52a8\u7740\u8db3\u4ee5\u593a\u4eba\u9b42\u9b44\u7684\u5bd2\u5149\u3002 \u3000\u3000\u82e5\u662f\u5728\u522b\u4eba\u624b\u4e0a\uff0c\u8fd9\u67c4\u5200\u5e76\u4e0d\u80fd\u7b97\u5229\u5203\uff0c\u4f46\u6b64\u523b\u5200\u5728\u53f6\u5f00\u624b\u4e0a\u3002 \u3000\u3000\u53f6\u5f00\u7684\u624b\u5e72\u71e5\u800c\u7a33\u5b9a\uff0c\u5c31\u5982\u540c\u8fdc\u5c71\u4e4b\u5dc5\u3002 \u3000\u3000\u5415\u8fea\u7684\u77b3\u5b54\u4e5f\u7a81\u7136\u6536\u7f29\uff0c\u8fdc\u5728\u4e94\u4e08\u5916\u7684\u6234\u9ad8\u5c97\uff0c\u5374\u5df2\u8fde\u547c\u5438\u90fd\u5df2\u505c\u987f\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u611f\u89c9\u5230\u4e00\u79cd\u4ece\u6765\u4e5f\u6ca1\u6709\u4f53\u9a8c\u8fc7\u7684\u6740\u6c14\u3002 \u3000\u3000\u5415\u8fea\u8131\u53e3\u9053\uff1a\u201c\u597d\uff01\u679c\u7136\u662f\u6740\u4eba\u7684\u5200\u3002\u201d \u3000\u3000\u53f6\u5f00\u7b11\u4e86\u7b11\uff0c\u7a81\u7136\u6325\u5200\u3002 \u3000\u3000\u5200\u5149\u4e00\u95ea\u4e0d\u89c1\u3002 \u3000\u3000\u8fd9\u67c4\u5200\u5c31\u4f3c\u5df2\u7a81\u7136\u6d88\u5931\u5728\u98ce\u4e2d\uff0c\u7a81\u7136\u65e0\u5f71\u65e0\u8e2a\u3002 \u3000\u3000\u5c31\u7b97\u773c\u775b\u6700\u5229\u7684\u4eba\uff0c\u4e5f\u53ea\u770b\u89c1\u5200\u5728\u8fdc\u5904\u95ea\u4e86\u95ea\uff0c\u5c31\u770b\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u8fd9\u4e00\u5200\u7684\u529b\u91cf\u548c\u901f\u5ea6\uff0c\u7edd\u6ca1\u6709\u4efb\u4f55\u4eba\u80fd\u5f62\u5bb9\u3002 \u3000\u3000\u5415\u8fea\u5df2\u4e0d\u7981\u8038\u7136\u52a8\u5bb9\uff0c\u5931\u58f0\u95ee\uff1a\u201c\u4f60\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u53f6\u5f00\u6de1\u6de1\u9053\uff1a\u201c\u4f60\u65e2\u4e0d\u7528\u5251\uff0c\u6211\u4e3a\u4f55\u8981\u7528\u5200\uff1f\u201d \u3000\u3000\u5415\u8fea\u51dd\u89c6\u7740\u4ed6\uff0c\u773c\u775b\u91cc\u5df2\u9732\u51fa\u5f88\u5947\u602a\u7684\u8868\u60c5\uff0c\u8fc7\u4e86\u5f88\u4e45\uff0c\u5ffd\u7136\u4f38\u51fa\u624b\uff1a\u201c\u4f60\u770b\u770b\u6211\u7684 \u624b\u3002\u201d \u3000\u3000\u5728\u522b\u4eba\u770b\u6765\uff0c\u8fd9\u5e76\u4e0d\u80fd\u7b97\u662f\u53ea\u5f88\u5947\u7279\u7684\u624b\u3002 \u3000\u3000\u624b\u6307\u662f\u7ea4\u957f\u7684\uff0c\u6307\u7532\u526a\u5f97\u5f88\u77ed\uff0c\u6c38\u8fdc\u4fdd\u6301\u7740\u5e72\u51c0\uff0c\u6b63\u914d\u5408\u4e00\u4e2a\u6709\u4fee\u517b\u7684\u5e74\u8f7b\u4eba\u3002 \u3000\u3000\u4f46\u53f6\u5f00\u5374\u5df2\u770b\u51fa\u4e86\u8fd9\u53ea\u624b\u7684\u5947\u7279\u4e4b\u5904\u3002 \u3000\u3000\u8fd9\u53ea\u624b\u770b\u6765\u7adf\u4f3c\u5b8c\u5168\u6ca1\u6709\u7b4b\u7edc\u8840\u8109\uff0c\u5149\u6ed1\u7ec6\u5bc6\u7684\u76ae\u80a4\uff0c\u5e26\u7740\u80a1\u91d1\u5c5e\u822c\u7684\u5149\u6cfd\u3002 \u3000\u3000\u8fd9\u53ea\u624b\u4e0d\u50cf\u662f\u9aa8\u9abc\u8840\u8089\u7ec4\u6210\uff0c\u770b\u6765\u5c31\u50cf\u662f\u4e00\u79cd\u5947\u7279\u7684\u91d1\u5c5e\uff0c\u4e0d\u662f\u9ec4\u91d1\uff0c\u5374\u6bd4\u9ec4\u91d1\u66f4\u8d35 \u91cd\uff0c\u4e0d\u662f\u94a2\u94c1\uff0c\u5374\u6bd4\u94a2\u94c1\u66f4\u575a\u786c\u3002 \u3000\u3000\u5415\u8fea\u51dd\u89c6\u7740\u81ea\u5df1\u7684\u8fd9\u53ea\u624b\uff0c\u5f90\u5f90\u9053\uff1a\u201c\u4f60\u770b\u6e05\u4e86\uff0c\u8fd9\u4e0d\u662f\u624b\uff0c\u8fd9\u662f\u6740\u4eba\u7684\u5229\u5668\u3002\u201d \u3000\u3000\u53f6\u5f00\u4e0d\u80fd\u4e0d\u627f\u8ba4\u3002 \u3000\u3000\u5415\u8fea\u9053\uff1a\u201c\u4f60\u77e5\u9053\u5bb6\u53d4\uff1f\u201d \u3000\u3000\u4ed6\u8bf4\u7684\u5c31\u662f\u201c\u6e29\u4faf\u94f6\u621f\u201d\u5415\u51e4\u5148\u3002 \u3000\u3000\u53f6\u5f00\u5f53\u7136\u77e5\u9053\u3002 \u3000\u3000\u5415\u8fea\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u4ed6\u6614\u65e5\u7ec3\u7684\u529f\u592b\uff0c\u6211\u7684\u8fd0\u6c14\u5374\u6bd4\u4ed6\u597d\uff0c\u56e0\u4e3a\u6211\u4e03\u5c81\u65f6\u5c31\u5f00\u59cb\u7ec3\u8fd9\u79cd\u529f \u592b\u3002\u201d \u3000\u3000\u5415\u51e4\u5148\u662f\u6210\u540d\u540e\u624d\u5f00\u59cb\u7ec3\u7684\uff0c\u53ea\u7ec3\u6210\u4e86\u4e09\u6839\u624b\u6307\u3002 \u3000\u3000\u5415\u8fea\u9053\uff1a\u201c\u4ed6\u7ec3\u8fd9\u79cd\u529f\u592b\uff0c\u53ea\u56e0\u4ed6\u4e00\u5411\u4e0d\u613f\u5c48\u5c45\u4eba\u4e0b\u3002\u201d \u3000\u3000\u5175\u5668\u8c31\u4e0a\u6392\u540d\uff0c\u6e29\u4faf\u94f6\u621f\u5728\u5929\u673a\u795e\u68d2\u3001\u9f99\u51e4\u53cc\u73af\u3001\u5c0f\u674e\u98de\u5200\u548c\u5d69\u9633\u94c1\u5251\u4e4b\u4e0b\u3002 \u3000\u3000\u5415\u8fea\u9053\uff1a\u201c\u767e\u6653\u751f\u4f5c\u5175\u5668\u8c31\u540e\uff0c\u5bb6\u53d4\u82e6\u7ec3\u5341\u5e74\uff0c\u518d\u51fa\u6c5f\u6e56\uff0c\u8981\u4ee5\u8fd9\u53ea\u624b\uff0c\u548c\u6392\u540d\u5728\u4ed6\u4e4b \u4e0a\u7684\u90a3\u4e9b\u4eba\u4e89\u4e00\u65e5\u4e4b\u77ed\u957f\u3002\u201d \u3000\u3000\u4ed6\u6ca1\u6709\u518d\u8bf4\u4e0b\u53bb\u3002 \u3000\u3000\u56e0\u4e3a\u5415\u51e4\u5148\u8d25\u4e86\uff0c\u8d25\u5728\u4e00\u4e2a\u5973\u4eba\u624b\u4e0b\u3002 \u3000\u3000\u4e00\u4e2a\u7f8e\u4e3d\u5982\u4ed9\u5b50\uff0c\u5374\u4e13\u5f15\u7537\u4eba\u4e0b\u5730\u72f1\u7684\u5973\u4eba\u2014\u2014\u6797\u4ed9\u513f\u3002 \u3000\u3000\u5415\u8fea\u9053\uff1a\u4ea7\u5bb6\u53d4\u4e5f\u8bf4\u8fc7\uff0c\u8fd9\u5df2\u4e0d\u662f\u624b\uff0c\u800c\u662f\u6740\u4eba\u7684\u5229\u5668\uff0c\u5df1\u53ef\u5217\u540d\u5728\u5175\u5668\u8c31\u4e0a\u3002 \u3000\u3000\u201d \u3000\u3000\u53f6\u5f00\u4e00\u76f4\u5728\u9759\u9759\u5730\u542c\u7740\uff0c\u4ed6\u77e5\u9053\u5415\u8fea\u8bf4\u7684\u6bcf\u4e2a\u5b57\u90fd\u662f\u771f\u5b9e\u7684\u3002 \u3000\u3000\u4ed6\u4ece\u4e0d\u6253\u65ad\u522b\u4eba\u7684\u5b9e\u8bdd\u3002 \u3000\u3000\u5415\u8fea\u5df2\u62ac\u8d77\u5934\uff0c\u51dd\u89c6\u7740\u4ed6\uff0c\u9053\uff1a\u201c\u4f60\u600e\u4e48\u80fd\u4ee5\u4e00\u53cc\u7a7a\u624b\uff0c\u6765\u5bf9\u4ed8\u8fd9\u79cd\u6740\u4eba\u7684\u79d1\u5668\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u8bd5\u8bd5\u3002\u201d \u3000\u3000\u5415\u8feb\u4e0d\u518d\u95ee\uff0c\u53f6\u5f00\u4e5f\u4e0d\u518d\u8bf4\u3002\u73b0\u5728\u65e0\u8bba\u518d\u8bf4\u4ec0\u4e48\uff0c\u90fd\u5df2\u662f\u591a\u4f59\u7684\u3002 \u3000\u3000\u9633\u5149\u707f\u70c2\u3002 \u3000\u3000\u53ef\u662f\u8fd9\u9633\u5149\u707f\u70c2\u7684\u9662\u5b50\uff0c\u73b0\u5728\u5374\u5ffd\u7136\u5145\u6ee1\u4e86\u4e00\u79cd\u8bf4\u4e0d\u51fa\u7684\u8083\u6740\u4e4b\u610f\u3002 \u3000\u3000\u6234\u9ad8\u5c97\u5ffd\u7136\u89c9\u5f97\u5f88\u51b7\u3002 \u3000\u3000\u9633\u5149\u4e5f\u5f88\u6e29\u6696\uff0c\u53ef\u662f\u4ed6\u5ffd\u7136\u89c9\u5f97\u767e\u822c\u5bd2\u610f\uff0c\u4e5f\u4e0d\u77e5\u4ece\u54ea\u91cc\u94bb\u4e86\u51fa\u6765\uff0c\u94bb\u5165\u4e86\u4ed6\u8863\u9886\uff0c\u94bb \u5165\u4e86\u4ed6\u7684\u5fc3\u3002 \u3000\u3000\u5200\u5df2\u98de\u4eba\u4e91\u6df1\u5904\uff0c\u5251\u5df2\u6ca1\u4eba\u6811\u91cc\u3002 \u3000\u3000\u8fd9\u65e2\u4e0d\u662f\u5200\u5bd2\uff0c\u4e5f\u4e0d\u662f\u5251\u6c14\uff0c\u4f46\u6bd4\u5200\u950b\u5251\u5203\u66f4\u51b7\uff0c\u66f4\u903c\u4eba\u3002 \u3000\u3000\u6234\u9ad8\u5c97\u51e0\u4e4e\u5df2\u4e0d\u613f\u518d\u7559\u5728\u8fd9\u9662\u5b50\u91cc\uff0c\u53ef\u662f\u4ed6\u5f53\u7136\u4e5f\u820d\u4e0d\u5f97\u8d70\u3002 \u3000\u3000\u65e0\u8bba\u8c01\u90fd\u53ef\u4ee5\u60f3\u8c61\u5f97\u5230\uff0c\u8fd9\u4e00\u6218\u5fc5\u662f\u8fd1\u5e74\u6765\u6700\u60ca\u5fc3\u52a8\u9b44\u7684\u4e00\u6218\uff0c\u5fc5\u5c06\u6c38\u5782\u6b66\u6797\u3002 \u3000\u3000\u80fd\u4eb2\u773c\u5728\u65c1\u770b\u7740\u8fd9\u4e00\u6218\uff0c\u4e5f\u662f\u4e00\u4e2a\u4eba\u4e00\u751f\u4e2d\u96be\u5f97\u7684\u673a\u9047\u3002 \u3000\u3000\u65e0\u8bba\u8c01\u90fd\u4e0d\u613f\u9519\u8fc7\u673a\u4f1a\u7684\u3002 \u3000\u3000\u6234\u9ad8\u5c97\u53ea\u5e0c\u671b\u4ed6\u4eec\u5feb\u4e9b\u5f00\u59cb\uff0c\u5feb\u4e9b\u7ed3\u675f\u3002 \u3000\u3000\u53ef\u662f\u53f6\u5f00\u5e76\u6ca1\u6709\u51fa\u624b\u3002 \u3000\u3000\u5415\u8fea\u4e5f\u6ca1\u6709\u3002 \u3000\u3000\u8fde\u6234\u9ad8\u5c97\u8fd9\u65c1\u89c2\u8005\uff0c\u90fd\u5df2\u53d7\u4e0d\u4e86\u8fd9\u79cd\u65e0\u5f62\u7684\u53ef\u6015\u7684\u538b\u529b\uff0c\u4f46\u4ed6\u4eec\u5374\u50cf\u662f\u6839\u672c\u65e0\u52a8\u4e8e\u8877\u3002 \u3000\u3000\u662f\u4e0d\u662f\u56e0\u4e3a\u8fd9\u538b\u529b\u672c\u5c31\u662f\u4ed6\u4eec\u81ea\u5df1\u53d1\u51fa\u6765\u7684\uff0c\u6240\u4ee5\u4ed6\u4eec\u624d\u611f\u89c9\u4e0d\u5230\uff1f \u3000\u3000\u6216\u8bb8\u662f\u56e0\u4e3a\u4ed6\u4eec\u672c\u8eab\u5df2\u53d8\u6210\u4e86\u4e00\u5757\u94a2\uff0c\u4e00\u5757\u5ca9\u77f3\uff0c\u4e16\u4e0a\u5df2\u6ca1\u6709\u4efb\u4f55\u4e00\u79cd\u538b\u529b\u80fd\u52a8\u6447\u4ed6 \u4eec\uff1f \u3000\u3000\u6234\u9ad8\u5c97\u770b\u4e0d\u51fa\u3002 \u3000\u3000\u4ed6\u53ea\u80fd\u770b\u5f97\u51fa\uff0c\u53f6\u5f00\u7684\u795e\u6001\u8fd8\u662f\u5f88\u9547\u5b9a\uff0c\u5f88\u51b7\u9759\uff0c\u521a\u624d\u56e0\u4ec7\u6068\u800c\u751f\u51fa\u7684\u6012\u706b\uff0c\u73b0\u5728\u5df2\u5b8c \u5168\u5e73\u606f\u3002 \u3000\u3000\u4ed6\u5f53\u7136\u77e5\u9053\uff0c\u5728\u8fd9\u79cd\u65f6\u5019\uff0c\u6124\u6012\u548c\u6fc0\u52a8\u5e76\u4e0d\u80fd\u81f4\u80dc\uff0c\u5374\u80fd\u81f4\u547d\u3002 \u3000\u3000\u5415\u8fea\u7684\u50b2\u6c14\u4e5f\u5df2\u4e0d\u89c1\u4e86\uff0c\u5728\u8fd9\u79cd\u7edd\u4e0d\u80fd\u6709\u4e1d\u6beb\u758f\u5ffd\u7684\u751f\u6b7b\u51b3\u6218\u4e2d\uff0c\u9a84\u505a\u4e5f\u540c\u6837\u662f\u79cd\u81f4\u547d \u7684\u9519\u8bef\u3002 \u3000\u3000\u9a84\u505a\u3001\u6124\u6012\u3001\u9893\u4e27\u3001\u5fe7\u8651\u3001\u80c6\u602f\u2026\u2026\u90fd\u540c\u6837\u53ef\u4ee5\u4ee4\u4eba\u4f5c\u51fa\u81f4\u6b7b\u7684\u9519\u8bef\u5224\u65ad\u3002 \u3000\u3000\u6234\u9ad8\u5c97\u4e5f\u66fe\u770b\u89c1\u4e0d\u5c11\u9ad8\u624b\u51b3\u6218\uff0c\u8fd9\u4e9b\u9519\u8bef\uff0c\u6b63\u662f\u4efb\u4f55\u4eba\u90fd\u65e0\u6cd5\u5b8c\u5168\u907f\u514d\u7684\u3002 \u3000\u3000\u53ef\u662f\u73b0\u5728\uff0c\u4ed6\u5ffd\u7136\u53d1\u73b0\u8fd9\u4e24\u4e2a\u5e74\u8f7b\u4eba\u7adf\u4f3c\u8fde\u4e00\u70b9\u9519\u8bef\u4e5f\u6ca1\u6709\u3002 \u3000\u3000\u4ed6\u4eec\u7684\u5fc3\u60c5\uff0c\u4ed6\u4eec\u7684\u795e\u6001\uff0c\u4ed6\u4eec\u7ad9\u7740\u7684\u59ff\u52bf\uff0c\u90fd\u662f\u7edd\u5bf9\u5b8c\u7f8e\u7684\u3002 \u3000\u3000\u8fd9\u4e00\u6218\u7a76\u7adf\u662f\u8c01\u80fd\u80dc\uff1f \u3000\u3000\u6234\u9ad8\u5c97\u4e5f\u770b\u4e0d\u51fa\u3002\u4ed6\u53ea\u77e5\u9053\u6709\u5f88\u591a\u4eba\u90fd\u8ba4\u4e3a\u53f6\u5f00\u5df2\u662f\u5f53\u4eca\u6b66\u6797\u4e2d\uff0c\u6700\u53ef\u6015\u7684\u4e00\u4e2a\u654c\u624b\u3002 \u3000\u3000\u4ed6\u5df2\u77e5\u9053\u6709\u4eba\u8bf4\u8fc7\uff0c\u73b0\u5728\u82e5\u662f\u91cd\u4f5c\u5175\u5668\u8c31\uff0c\u53f6\u5f00\u7684\u5200\uff0c\u5df2\u53ef\u540d\u5217\u7b2c\u4e00\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u73b0\u5728\u6ca1\u6709\u5200\u3002 \u3000\u3000\u867d\u7136\u6ca1\u6709\u5200\uff0c\u5374\u504f\u504f\u8fd8\u662f\u6709\u79cd\u5200\u950b\u822c\u7684\u9510\u6c14\u3001\u6740\u6c14\u3002 \u3000\u3000\u53f6\u5f00\u80fd\u80dc\u5417\uff1f\u6234\u9ad8\u5c97\u5e76\u4e0d\u80fd\u786e\u5b9a\u3002 \u3000\u3000\u4ed6\u4e5f\u4e0d\u77e5\u9053\u5415\u8fea\u662f\u5426\u80fd\u80dc\u3002\u6234\u9ad8\u5c97\u4e5f\u4e0d\u80fd\u786e\u5b9a\u3002 \u3000\u3000\u53f6\u5f00\u770b\u6765\u5b9e\u5728\u592a\u9547\u5b9a\uff0c\u5927\u6709\u628a\u63e1\uff0c\u9664\u4e86\u5200\u4e4b\u5916\uff0c\u4ed6\u4e00\u5b9a\u8fd8\u6709\u79cd\u66f4\u53ef\u6015\u7684\u6b66\u529f\uff0c\u4e00\u79cd\u4efb\u4f55 \u4eba\u90fd\u65e0\u6cd5\u601d\u8bae\u4e5f\u60f3\u4e0d\u5230\u7684\u6b66\u529f\u3002 \u3000\u3000\u73b0\u5728\u82e5\u6709\u4eba\u6765\u8ddf\u6234\u9ad8\u5c97\u6253\u8d4c\uff0c\u4ed6\u4e5f\u53ef\u80fd\u4f1a\u8bf4\u53f6\u5f00\u80dc\u7684\u3002\u4ed6\u8ba4\u4e3a\u53f6\u5f00\u80dc\u7684\u673a\u4f1a\uff0c\u81f3\u5c11\u6bd4\u5415 \u8fea\u591a\u4e24\u6210\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u9519\u4e86\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u770b\u4e0d\u51fa\u53f6\u5f00\u6b64\u523b\u7684\u5fc3\u60c5\uff0c\u4e5f\u770b\u4e0d\u51fa\u53f6\u5f00\u5df2\u770b\u51fa\u7684\u4e00\u4e9b\u4e8b\u3002 \u3000\u3000\u4e00\u4e9b\u5df2\u8db3\u591f\u4ee4\u53f6\u5f00\u80c3\u91cc\u6d41\u51fa\u82e6\u6c34\u6765\u7684\u4e8b\u3002 \u3000\u3000\u81ea\u4ece\u5415\u8fea\u7684\u5251\u63b7\u51fa\u540e\uff0c\u53f6\u5f00\u5df2\u5bf9\u8fd9\u4e2a\u9a84\u50b2\u7684\u5e74\u8f7b\u4eba\u8d77\u4e86\u79cd\u60fa\u60fa\u76f8\u60dc\u7684\u597d\u611f\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u542c\u8fc7\u4e24\u53e5\u8bdd\uff1a\u201c\u4ec7\u654c\u548c\u670b\u53cb\u95f4\u7684\u5206\u522b\uff0c\u5c31\u6b63\u5982\u751f\u4e0e\u6b7b\u4e4b\u95f4\u7684\u5206\u522b\u3002\u201d \u3000\u3000\u201c\u82e5\u6709\u4eba\u60f3\u8981\u4f60\u6b7b\uff0c\u4f60\u5c31\u5f97\u8981\u4ed6\u6b7b\uff0c\u8fd9\u5176\u95f4\u7edd\u65e0\u9009\u62e9\u3002\u201d \u3000\u3000\u8fd9\u662f\u963f\u98de\u5bf9\u4ed6\u8bf4\u8fc7\u7684\u8bdd\u3002 \u3000\u3000\u963f\u98de\u662f\u5728\u5f31\u8089\u5f3a\u98df\u7684\u539f\u91ce\u4e2d\u751f\u957f\u7684\uff0c\u8fd9\u6b63\u662f\u539f\u91ce\u4e0a\u7684\u6cd5\u5219\uff0c\u4e5f\u662f\u751f\u6b7b\u6cd5\u5219\u3002\u5728\u8fd9\u79cd\u751f\u6b7b \u4e00\u77ac\u95f4\u7684\u51b3\u6218\u4e2d\uff0c\u7edd\u4e0d\u80fd\u5bf9\u654c\u4eba\u5b58\u53cb\u60c5\uff0c\u66f4\u4e0d\u80fd\u6709\u7231\u5fc3\u3002 \u3000\u3000\uff10\uff10\uff19 \u3000\u3000\u53f6\u5f00\u660e\u767d\u8fd9\u9053\u7406\u3002\u4ed6\u77e5\u9053\u73b0\u5728\u4ed6\u81f4\u80dc\u7684\u56e0\u7d20\uff0c\u5e76\u4e0d\u662f\u5feb\u4e0e\u72e0\uff0c\u800c\u662f\u7a33\u4e0e\u51c6\u3002 \u3000\u3000\u56e0\u4e3a\u5415\u8fea\u5f88\u53ef\u80fd\u6bd4\u4ed6\u66f4\u5feb\u3001\u66f4\u72e0\u3002 \u3000\u3000\u56e0\u4e3a\u73b0\u5728\u4ed6\u7684\u80f8\u819b\uff0c\u6b63\u5982\u706b\u7130\u71c3\u70e7\u822c\u75db\u82e6\uff0c\u4ed6\u7684\u4f24\u53e3\u4e0d\u4f46\u5df2\u8ff8\u88c2\uff0c\u7adf\u5df2\u5728\u6e83\u70c2\u3002\u201c\u5999\u624b \u90ce\u4e2d\u201d\u7ed9\u4ed6\u7684\uff0c\u5e76\u4e0d\u662f\u7075\u4e39\uff0c\u4e5f\u4e0d\u4f1a\u9020\u6210\u5947\u8ff9\u3002 \u3000\u3000\u75db\u82e6\u6709\u65f6\u867d\u80fd\u4ee4\u4eba\u6e05\u9192\uff0c\u53ea\u53ef\u60dc\u4ed6\u7684\u4f53\u529b\uff0c\u5df2\u65e0\u6cd5\u548c\u4ed6\u7684\u7cbe\u795e\u914d\u5408\uff0c\u6240\u4ee5\u4ed6\u4e00\u51fa\u624b\uff0c\u5c31 \u5f97\u5236\u5bf9\u65b9\u7684\u6b7b\u547d\uff0c\u81f3\u5c11\u8981\u6709\u4e03\u6210\u628a\u63e1\u65f6\uff0c\u4ed6\u624d\u80fd\u51fa\u624b\u3002 \u3000\u3000\u4ed6\u6240\u4ee5\u5fc5\u9700\u7b49\uff0c\u7b49\u5bf9\u65b9\u9732\u51fa\u7834\u7efd\uff0c\u7b49\u5bf9\u65b9\u5df2\u8870\u5f31\uff0c\u5d29\u6e83\uff0c\u7b49\u5bf9\u65b9\u7ed9\u4ed6\u673a\u4f1a\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u5df2\u5931\u671b\u3002\u76f4\u5230\u73b0\u5728\uff0c\u4ed6\u8fd8\u662f\u65e0\u6cd5\u4ece\u5415\u8fea\u8eab\u4e0a\u627e\u51fa\u4e00\u70b9\u7834\u7efd\u6765\u3002 \u3000\u3000\u5415\u8fea\u770b\u6765\u53ea\u4e0d\u8fc7\u662f\u968f\u968f\u4fbf\u4fbf\u5730\u7ad9\u7740\uff0c\u5168\u8eab\u4e0a\u4e0b\uff0c\u6bcf\u4e00\u5904\u770b\u6765\u90fd\u4eff\u4f5b\u662f\u7a7a\u95e8\u3002 \u3000\u3000\u53f6\u5f00\u65e0\u8bba\u8981\u4ece\u4ec0\u4e48\u5730\u65b9\u4e0b\u624b\uff0c\u770b\u6765\u597d\u50cf\u90fd\u5f88\u5bb9\u6613\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u5ffd\u53c8\u60f3\u5230\u4e86\u5c0f\u674e\u63a2\u82b1\u5bf9\u4ed6\u8bf4\u8fc7\u7684\u8bdd\uff0c\u6614\u5e74\u963f\u98de\u4e0e\u5415\u51e4\u5148\u7684\u90a3\u4e00\u6218\uff0c\u53ea\u6709\u674e\u5bfb\u6b22\u662f \u5728\u65c1\u8fb9\u4eb2\u773c\u770b\u7740\u7684\u3002 \u3000\u3000\u90a3\u65f6\u7684\u5415\u51e4\u5148\uff0c\u6b63\u5982\u6b64\u523b\u7684\u5415\u8fea\u3002 \u3000\u3000\u201c\u90a3\u65f6\u963f\u98de\u7684\u5251\uff0c\u4eff\u4f5b\u53ef\u4ee5\u968f\u4fbf\u523a\u5165\u4ed6\u8eab\u4e0a\u4efb\u4f55\u90e8\u4f4d\u3002\u201d \u3000\u3000\u201c\u4f46\u7a7a\u95e8\u5927\u591a\uff0c\u53cd\u800c\u53d8\u6210\u4e86\u6ca1\u6709\u7a7a\u95e8\u3002\u201d \u3000\u3000\u201c\u4ed6\u6574\u4e2a\u7684\u4eba\u90fd\u4f3c\u5df2\u53d8\u6210\u4e86\u4e00\u7247\u7a7a\u7075\u3002\u201d \u3000\u3000\u201c\u8fd9\u7a7a\u7075\u4e8c\u5b57\uff0c\u4e5f\u6b63\u662f\u6b66\u5b66\u4e2d\u81f3\u9ad8\u81f3\u6df1\u7684\u5883\u754c\u3002\u201d \u3000\u3000\u201c\u6211\u7684\u98de\u5200\u51fa\u624b\uff0c\u81f3\u5c11\u6709\u4e5d\u6210\u628a\u63e1\u3002\u2019\u201d\u4f46\u90a3\u65f6\u6211\u82e5\u662f\u963f\u98de\uff0c\u6211\u7684\u98de\u5200\u5c31\u672a\u5fc5\u6562\u5411\u5415\u51e4 \u5148\u51fa\u624b\u3002\u201d \u3000\u3000\u53ea\u8981\u662f\u674e\u5bfb\u6b22\u8bf4\u8fc7\u7684\u8bdd\uff0c\u53f6\u5f00\u5c31\u6c38\u8fdc\u90fd\u4e0d\u4f1a\u5fd8\u8bb0\u3002 \u3000\u3000\u73b0\u5728\u5415\u8fea\u5176\u4eba\u662f\u4e0d\u662f\u4e5f\u5df2\u6210\u4e86\u4e00\u7247\u7a7a\u7075\uff1f \u3000\u3000\u53f6\u5f00\u5ffd\u7136\u53d1\u89c9\u81ea\u5df1\u4f4e\u4f30\u4e86\u8fd9\u4e2a\u5e74\u8f7b\u4eba\uff0c\u8fd9\u4e2a\u4eba\u624d\u771f\u6b63\u662f\u4ed6\u5e73\u751f\u672a\u66fe\u9047\u89c1\u7684\u9ad8\u624b\u3002 \u3000\u3000\u4ed6\u867d\u7136\u5e76\u6ca1\u6709\u72af\u4efb\u4f55\u81f4\u547d\u7684\u9519\u8bef\uff0c\u53ef\u662f\u4ed6\u5374\u5df2\u5931\u53bb\u4e00\u70b9\u6700\u91cd\u8981\u7684\u81f4\u80dc\u56e0\u7d20\u3002 \u3000\u3000\u4ed6\u5df2\u5931\u53bb\u4e86\u81f4\u80dc\u7684\u4fe1\u5fc3\u3002 \u3000\u3000\u5415\u8fea\u51b7\u51b7\u5730\u770b\u7740\u4ed6\uff0c\u773c\u775b\u8d8a\u6765\u8d8a\u4eae\uff0c\u8d8a\u6765\u8d8a\u51b7\u9177\uff0c\u5ffd\u7136\u53c8\u8bf4\u51fa\u4e86\u4e09\u5b57\uff1a\u201c\u4f60\u8f93\u4e86\u3002\u201d \u3000\u3000\u201c\u4f60\u8f93\u4e86\u3002\u201d \u3000\u3000\u53f6\u5f00\u8fd8\u672a\u51fa\u624b\uff0c\u5415\u8fea\u5c31\u5df2\u8bf4\u4ed6\u8f93\u4e86\u3002 \u3000\u3000\u8fd9\u4e09\u4e2a\u5b57\u5e76\u4e0d\u662f\u591a\u4f59\u7684\uff0c\u5374\u50cf\u662f\u4e00\u67c4\u5251\uff0c\u53c8\u523a\u4f24\u4e86\u53f6\u5f00\u7684\u4fe1\u5fc3\u3002 \u3000\u3000\u53f6\u5f00\u5c45\u7136\u6ca1\u6709\u53cd\u9a73\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u5ffd\u7136\u53d1\u73b0\u5415\u8fea\u7ec8\u4e8e\u7ed9\u4e86\u4ed6\u4e00\u70b9\u673a\u4f1a\u2014\u2014\u4e00\u4e2a\u4eba\u5728\u5f00\u53e3\u8bf4\u8bdd\u65f6\uff0c\u7cbe\u795e\u548c\u808c\u8089\u90e8\u4f1a\u677e \u5f1b\u3002 \u3000\u3000\u4ed6\u9762\u4e0a\u9732\u51fa\u75db\u82e6\u4e4b\u8272\uff0c\u56e0\u4e3a\u4ed6\u77e5\u9053\u82e5\u662f\u8868\u73b0\u5f97\u8d8a\u75db\u82e6\uff0c\u5415\u8fea\u5c31\u8d8a\u4e0d\u4f1a\u653e\u8fc7\u4ed6\u7684\u3002 \u3000\u3000\u5728\u8fd9\u79cd\u751f\u6b7b\u51b3\u6218\u4e2d\uff0c\u82e5\u6709\u6cd5\u5b50\u80fd\u6298\u78e8\u81ea\u5df1\u7684\u5bf9\u624b\uff0c\u65e0\u8bba\u8c01\u90fd\u4e0d\u4f1a\u653e\u8fc7\u7684\u3002 \u3000\u3000\u5415\u8fea\u679c\u7136\u53c8\u51b7\u51b7\u5730\u63a5\u7740\u9053\uff1a\u201c\u4f60\u7684\u4f53\u529b\u5df2\u65e0\u6cd5\u518d\u652f\u6301\u4e0b\u53bb\uff0c\u8fdf\u65e9\u4e00\u5b9a\u4f1a\u5d29\u6e83\uff0c\u6240\u4ee5\u4f60\u4e0d \u5fc5\u51fa\u624b\uff0c\u6211\u5df2\u77e5\u9053\u4f60\u8f93\u4e86\u3002\u201d \u3000\u3000\u5c31\u5728\u4ed6\u8bf4\u51fa\u6700\u540e\u4e00\u4e2a\u5b57\u7684\u65f6\u5019\uff0c\u53f6\u5f00\u5df2\u51fa\u624b\u3002 \u3000\u3000\u8fd9\u5df2\u662f\u4ed6\u6240\u80fd\u627e\u5230\u7684\u6700\u597d\u673a\u4f1a\u3002 \u3000\u3000\u5415\u8fea\u521a\u8bf4\u5b8c\u4e86\u8fd9\u53e5\u8bdd\uff0c\u6b63\u662f\u7cbe\u795e\u548c\u808c\u8089\u6700\u677e\u5f1b\u7684\u65f6\u5019\u3002 \u3000\u3000\u4ed6\u7684\u8eab\u5f62\u867d\u7136\u8fd8\u662f\u6ca1\u6709\u7834\u7efd\uff0c\u4f46\u53f6\u5f00\u5df2\u6709\u673a\u4f1a\u5c06\u7834\u7efd\u627e\u51fa\u6765\u3002 \u3000\u3000\u53f6\u5f00\u6ca1\u6709\u7528\u5200\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u51fa\u624b\u7684\u901f\u5ea6\uff0c\u5e76\u4e0d\u6bd4\u4ed6\u7684\u5200\u6162\u3002 \u3000\u3000\u4ed6\u7684\u5de6\u624b\u865a\u634f\u5982\u8c79\u722a\u3001\u9e70\u722a\uff0c\u53f3\u624b\u4e94\u6307\u5c48\u4f38\uff0c\u8c01\u4e5f\u770b\u4e0d\u51fa\u4ed6\u662f\u8981\u7528\u62f3\uff1f\u7528\u638c\uff1f\u662f\u8981\u7528\u9e70 \u722a\u529f\uff1f\u8fd8\u662f\u8981\u7528\u94c1\u6307\u529f\uff1f \u3000\u3000\u4ed6\u7684\u51fa\u624b\u53d8\u5316\u9519\u843d\uff0c\u4e5f\u6ca1\u6709\u4eba\u80fd\u770b\u5f97\u51fa\u4ed6\u653b\u51fb\u7684\u90e8\u4f4d\u3002 \u3000\u3000\u4ed6\u5fc5\u9700\u5148\u5f15\u52a8\u5415\u8fea\u7684\u8eab\u6cd5\uff0c\u53ea\u8981\u4e00\u52a8\uff0c\u7a7a\u95e8\u5c31\u53ef\u80fd\u53d8\u5b9e\uff0c\u5c31\u4e8c\u5b9a\u4f1a\u6709\u7834\u7efd\u9732\u51fa\u3002 \u3000\u3000\u5415\u8fea\u679c\u7136\u52a8\u4e86\uff0c\u4ed6\u9732\u51fa\u7684\u7a7a\u95e8\u662f\u5728\u5934\u9876\u3002\u53f6\u5f00\u53cc\u62f3\u9f50\u51fa\uff0c\u6025\u653b\u4ed6\u7684\u5934\u9876\uff0c\u8fd9\u662f\u81f4\u547d\u7684\u653b \u51fb\u3002\u53ef\u662f\u4ed6\u81ea\u5df1\u7684\u5fc3\u5374\u5df2\u6c89\u4e86\u4e0b\u53bb\u3002\u56e0\u4e3a\u4ed6\u5df2\u53d1\u89c9\uff0c\u81ea\u5df1\u8fd9\u4e00\u62db\u9732\u51fa\uff0c\u524d\u80f8\u7684\u7a7a\u95e8\u4e5f\u9732\u4e86\u51fa \u6765\u3002 \u3000\u3000\u80f8\u819b\u4e0a\u662f\u4ed6\u5168\u8eab\u6700\u8106\u5f31\u7684\u4e00\u73af\uff0c\u56e0\u4ed6\u80f8\u819b\u4e0a\u672c\u5df2\u6709\u4e86\u4f24\u53e3\u3002 \u3000\u3000\u65e0\u8bba\u8c01\u77e5\u9053\u81ea\u5df1\u8eab\u4e0a\u6700\u8106\u5f31\u7684\u90e8\u4f4d\u53ef\u80fd\u53d7\u4eba\u653b\u51fb\u65f6\uff0c\u5fc3\u90fd\u4f1a\u865a\uff0c\u624b\u90fd\u4f1a\u8f6f\u4e86\u3002 \u3000\u3000\u53f6\u5f00\u7684\u653b\u52bf\u5df2\u8fdc\u4e0d\u53ca\u4ed6\u5e73\u65f6\u4e4b\u5f3a\uff0c\u901f\u5ea6\u5df2\u8fdc\u4e0d\u5982\u4ed6\u5e73\u65f6\u5feb\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u53d1\u89c9\uff0c\u8fd9\u7834\u7efd\u672c\u662f\u5415\u8fea\u6545\u610f\u9732\u51fa\u6765\u7684\u3002 \u3000\u3000\u5415\u8fea\u5148\u6545\u610f\u7ed9\u4ed6\u51fa\u624b\u7684\u673a\u4f1a\uff0c\u518d\u6545\u610f\u9732\u51fa\u4e2a\u7834\u7efd\uff0c\u4e3a\u7684\u53ea\u4e0d\u8fc7\u662f\u8981\u4ed6\u5c06\u81ea\u5df1\u8eab\u4e0a\u6700\u8106\u5f31 \u7684\u90e8\u4f4d\u66b4\u9732\u3002 \u3000\u3000\u8fd9\u6b63\u662f\u4e2a\u81f4\u547d\u7684\u9677\u9631\uff0c\u4f46\u662f\u4ed6\u7adf\u5df2\u50cf\u778e\u5b50\u822c\u843d\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u4ed6\u518d\u60f3\u8865\u6551\uff0c\u5df2\u6765\u4e0d\u53ca\u4e86\u3002 \u3000\u3000\u5415\u8fea\u7684\u624b\uff0c\u5ffd\u7136\u5df2\u5230\u4e86\u4ed6\u7684\u80f8\u819b\u3002 \u3000\u3000\u8fd9\u4e0d\u662f\u624b\uff0c\u8fd9\u672c\u5c31\u662f\u6740\u4eba\u7684\u5229\u5668\u3002 \u3000\u3000\u6234\u9ad8\u5c97\u5df2\u8038\u7136\u53d8\u8272\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u624d\u77e5\u9053\u81ea\u5df1\u521a\u624d\u770b\u9519\u4e86\uff0c\u4ed6\u5df2\u770b\u51fa\u8fd9\u662f\u65e0\u6cd5\u95ea\u907f\u7684\u81f4\u547d\u653b\u51fb\u3002 \u3000\u3000\u8c01\u77e5\u5c31\u5728\u8fd9\u65f6\uff0c\u53f6\u5f00\u7684\u8eab\u5b50\u5ffd\u7136\u51ed\u7a7a\u63a0\u8d77\uff0c\u5c31\u50cf\u662f\u5ffd\u7136\u88ab\u4e00\u9635\u98ce\u5439\u8d77\u6765\u7684\uff0c\u6ca1\u6709\u4eba\u80fd\u5728 \u8fd9\u79cd\u65f6\u5019\u3001\u8fd9\u79cd\u59ff\u6001\u4e2d\u98de\u8eab\u8dc3\u8d77\uff0c\u8fd9\u51e0\u4e4e\u662f\u4e0d\u53ef\u80fd\u7684\u4e8b\u3002 \u3000\u3000\u4f46\u53f6\u5f00\u7684\u8f7b\u529f\uff0c\u7adf\u5df2\u8fbe\u5230\u4e86\u4e0d\u53ef\u80fd\u7684\u5883\u754c\u3002 \u3000\u3000\u6234\u9ad8\u5c97\u5fcd\u4e0d\u4f4f\u5931\u58f0\u5927\u547c\uff1a\u201c\u597d\u8f7b\u529f\uff01\u201d \u3000\u3000\u5415\u8fea\u4e5f\u4e0d\u7981\u8131\u53e3\u8d5e\u9053\uff1a\u201c\u597d\u8f7b\u529f\u3002\u201d \u3000\u3000\u8fd9\u4e24\u53e5\u8bdd\u4ed6\u4eec\u540c\u65f6\u8bf4\u51fa\uff0c\u6700\u540e\u4e00\u4e2a\u5b57\u8fd8\u6ca1\u6709\u8bf4\u5b8c\uff0c\u53f6\u5f00\u5df2\u51ed\u7a7a\u8dcc\u4e0b\u3002 \u3000\u3000\u5415\u8fea\u7684\u624b\uff0c\u5df2\u6253\u5728\u4ed6\u80ef\u9aa8\u4e0a\u3002 \u3000\u3000\u53f6\u5f00\u4f7f\u51fa\u90a3\u6551\u547d\u7684\u4e00\u638c\u65f6\uff0c\u77e5\u9053\u81ea\u5df1\u8eb2\u8fc7\u4e86\u5415\u8fea\u7b2c\u4e00\u62db\uff0c\u7b2c\u4e8c\u62db\u7ade\u662f\u518d\u4e5f\u8eb2\u4e0d\u8fc7\u7684\u4e86\u3002 \u3000\u3000\u4ed6\u8eab\u5b50\u51cc\u7a7a\u7ffb\u8d77\u65f6\uff0c\u4e0b\u534a\u8eab\u7684\u7a7a\u95e8\u5df2\u5927\u7834\uff0c\u4ed6\u53ea\u6709\u8fd9\u4e48\u6837\u505a\uff0c\u4ed6\u7684\u80f8\u819b\u5df2\u7edd\u5bf9\u53d7\u4e0d\u4e86\u5415 \u8fea\u90a3\u4e00\u51fb\u3002 \u3000\u3000\u53ef\u662f\u80ef\u9aa8\u4e0a\u8fd9\u4e00\u51fb\u4e5f\u540c\u6837\u4e0d\u597d\u53d7\u3002 \u3000\u3000\u4ed6\u53ea\u89c9\u5f97\u5415\u8fea\u7684\u624b\uff0c\u5c31\u50cf\u662f\u4e00\u67c4\u94a2\u9525\uff0c\u9525\u5165\u4e86\u4ed6\u7684\u9aa8\u7f1d\u91cc\u3002 \u3000\u3000\u4ed6\u751a\u81f3\u53ef\u4ee5\u542c\u5f97\u89c1\u81ea\u5df1\u9aa8\u5934\u788e\u88c2\u7684\u58f0\u97f3\u3002 \u3000\u3000\u5730\u4e5f\u662f\u786c\u7684\u3002 \u3000\u3000\u53f6\u5f00\u4ece\u6ca1\u6709\u60f3\u5230\uff0c\u8fd9\u6ee1\u662f\u6ce5\u6cde\u7684\u571f\u5730\uff0c\u4e5f\u662f\u786c\u5f97\u50cf\u94c1\u677f\u4e00\u6837\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u8dcc\u4e0b\u6765\u65f6\uff0c\u6700\u5148\u7740\u5730\u7684\u4e00\u90e8\u4efd\uff0c\u6b63\u662f\u4ed6\u7684\u9aa8\u5934\u5df2\u788e\u88c2\u7684\u90a3\u4e00\u90e8\u4efd\u3002 \u3000\u3000\u4ed6\u51e0\u4e4e\u5df2\u75bc\u5f97\u8981\u6655\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u4ed6\u5ffd\u53c8\u8b66\u9192\uff0c\u56e0\u4e3a\u4ed6\u53d1\u73b0\u5415\u8fea\u7684\u624b\uff0c\u53c8\u5df2\u5230\u4e86\u4ed6\u7684\u80f8\u819b\uff0c\u8fd9\u4e00\u6765\u4ed6\u624d\u662f\u771f\u6b63\u65e0\u6cd5\u95ea\u907f \u7684\uff0c\u4e5f\u65e0\u6cd5\u4f38\u624b\u53bb\u62db\u67b6\u3002 \u3000\u3000\u4ed6\u7684\u624b\u662f\u624b\uff0c\u5415\u8fea\u7684\u624b\u5374\u662f\u6740\u4eba\u7684\u5229\u5668\u3002 \u3000\u3000\u6b7b\u662f\u4ec0\u4e48\u6ecb\u5473\uff1f \u3000\u3000\u53f6\u5f00\u8fd8\u6ca1\u6709\u5f00\u59cb\u60f3\uff0c\u5c31\u542c\u6234\u9ad8\u5c97\u5927\u547c\uff1a\u201c\u624b\u4e0b\u7559\u60c5\u3002\u201d \u3000\u3000\u5415\u8fea\u7684\u624b\u5df2\u505c\u987f\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u4e0d\u8981\u6211\u5728\u8fd9\u65f6\u6740\u4ed6\uff1f\u201d \u3000\u3000\u6234\u9ad8\u5c97\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u4f60\u4f55\u5fc5\u4e00\u5b9a\u8981\u6740\u4ed6\uff1f\u201d \u3000\u3000\u5415\u8fea\u9053\uff1a\u201c\u8c01\u8bf4\u6211\u8981\u6740\u4ed6\uff1f\u201d \u3000\u3000\u6234\u9ad8\u5c97\u9053\uff1a\u201c\u53ef\u662f\u4f60\u2026\u2026\u201d \u3000\u3000\u5415\u8fea\u51b7\u7b11\u9053\uff1a\u201c\u6211\u82e5\u771f\u7684\u8981\u6740\u4ed6\uff0c\u51ed\u4f60\u4e00\u53e5\u8bdd\u5c31\u80fd\u62e6\u5f97\u4f4f\uff1f\u201d \u3000\u3000\u6234\u9ad8\u5c97\u82e6\u7b11\uff0c\u4ed6\u77e5\u9053\u81ea\u5df1\u62e6\u4e0d\u4f4f\uff0c\u4e16\u4e0a\u4e5f\u8bb8\u6839\u672c\u6ca1\u6709\u4eba\u80fd\u62e6\u5f97\u4f4f\u3002 \u3000\u3000\u5415\u8fea\u9053\uff1a\u201c\u6211\u82e5\u771f\u7684\u8981\u6740\u4ed6\uff0c\u4ed6\u5df2\u6b7b\u4e86\u5341\u6b21\u3002\u201d \u3000\u3000\u8fd9\u5e76\u4e0d\u662f\u5927\u8bdd\u3002 \u3000\u3000\u53f6\u5f00\u770b\u7740\u8fd9\u9a84\u50b2\u7684\u5e74\u8f7b\u4eba\uff0c\u75db\u82e6\u867d\u5df2\u4ee4\u4ed6\u7684\u8138\u6536\u7f29\uff0c\u4f46\u662f\u4ed6\u7684\u4e00\u53cc\u773c\u775b\uff0c\u5374\u53d8\u5f97\u51fa\u5947\u7684 \u5e73\u9759\uff0c\u751a\u81f3\u8fd8\u5e26\u7740\u7b11\u610f\u3002 \u3000\u3000\u4ed6\u4e3a\u4ec0\u4e48\u7b11\uff1f \u3000\u3000\u88ab\u4eba\u51fb\u8d25\uff0c\u96be\u9053\u662f\u4ef6\u5f88\u6709\u8da3\u7684\u4e8b\uff1f \u3000\u3000\u5415\u8fea\u5df2\u8f6c\u8fc7\u5934\uff0c\u76ef\u7740\u4ed6\uff0c\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\u6211\u4e3a\u4ec0\u4e48\u4e0d\u6740\u4f60\uff1f\u201d \u3000\u3000\u53f6\u5f00\u6447\u6447\u5934\u3002 \u3000\u3000\u660c\u8fea\u9053\uff1a\u201c\u56e0\u4e3a\u4f60\u672c\u5df2\u53d7\u4f24\uff0c\u5426\u5219\u4ee5\u4f60\u8f7b\u529f\u4e4b\u9ad8\uff0c\u7eb5\u7136\u4e0d\u80fd\u80dc\u6211\uff0c\u6211\u4e5f\u65e0\u6cd5\u8ffd\u4e0a\u4f60\u3002\u201d \u3000\u3000\u53f6\u5f00\u7b11\u4e86\uff1a\u201c\u4f60\u6839\u672c\u7528\u4e0d\u7740\u8ffd\uff0c\u56e0\u4e3a\u6211\u7eb5\u7136\u4e0d\u80fd\u80dc\u4f60\uff0c\u4e5f\u4e0d\u4f1a\u9003\u7684\u3002\u201d \u3000\u3000\u5415\u8fea\u53c8\u76ef\u7740\u4ed6\uff0c\u8fc7\u4e86\u5f88\u4e45\uff0c\u624d\u6162\u6162\u5730\u70b9\u4e86\u70b9\u5934\uff1a\u201c\u6211\u76f8\u4fe1\u3002\u201d \u3000\u3000\u4ed6\u773c\u775b\u91cc\u4e5f\u9732\u51fa\u79cd\u548c\u53f6\u5f00\u540c\u6837\u7684\u8868\u60c5\uff0c\u63a5\u7740\u9053\uff1a\u201c\u6211\u76f8\u4fe1\u4f60\u7edd\u4e0d\u662f\u90a3\u79cd\u4eba\uff0c\u6240\u4ee5\u6211\u66f4\u4e0d \u80fd\u6740\u4f60\uff0c\u56e0\u4e3a\u6211\u8fd8\u8981\u7b49\u4f60\u7684\u4f24\u597d\u4e86\u4ee5\u540e\uff0c\u518d\u4e0e\u6211\u4e00\u51b3\u80dc\u8d1f\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u2026\u2026\u201d \u3000\u3000\u5415\u8fea\u6253\u65ad\u4e86\u4ed6\u7684\u8bdd\uff0c\u9053\uff1a\u201c\u5c31\u56e0\u4e3a\u6211\u76f8\u4fe1\u4f60\u4e0d\u4f1a\u9003\uff0c\u6240\u4ee5\u6211\u77e5\u9053\u4f60\u4e00\u5b9a\u4f1a\u6765\u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u5230\u4e86\u90a3\u4e00\u5929\uff0c\u6211\u8fd8\u662f\u8d25\u5728\u4f60\u624b\u4e0b\uff0c\u4f60\u5c31\u8981\u6740\u6211\u4e86\uff1f\u201d \u3000\u3000\u5415\u8fea\u70b9\u70b9\u5934\uff1a\u201c\u5230\u4e86\u90a3\u4e00\u5929\uff0c\u4f60\u82e5\u80dc\u4e86\u6211\uff0c\u6211\u4e5f\u60c5\u613f\u6b7b\u5728\u4f60\u624b\u4e0b\u3002\u201d \u3000\u3000\u53f6\u5f00\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4e16\u4e8b\u5982\u68cb\uff0c\u53d8\u5316\u65e0\u5e38\uff0c\u4f60\u53c8\u600e\u77e5\u6211\u4eec\u4e00\u5b9a\u80fd\u7b49\u5230\u90a3\u4e00\u5929\uff1f\u201d \u3000\u3000\u5415\u8fea\u9053\uff1a\u201c\u6211\u77e5\u9053\u3002\u201d \u3000\u3000\u7a81\u7136\u5899\u5916\u4e00\u4eba\u53f9\u606f\u9053\uff1a\u201c\u4f46\u6709\u4ef6\u4e8b\u4f60\u5374\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u5415\u8fea\u6ca1\u6709\u95ee\uff0c\u4e5f\u6ca1\u6709\u8ffd\u51fa\u6765\u770b\u770b\u3002 \u3000\u3000\u4ed6\u5728\u542c\u3002 \u3000\u3000\u5899\u5916\u7684\u4eba\u5f90\u5f90\u9053\uff1a\u201c\u4eca\u65e5\u4f60\u82e5\u771f\u7684\u60f3\u6740\u4ed6\uff0c\u73b0\u5728\u4f60\u4e5f\u5df2\u662f\u4e2a\u6b7b\u4eba\u4e86\uff0c\u4ed6\u8eab\u4e0a\u5e76\u4e0d\u6b62\u4e00\u628a \u5200\u3002\u201d \u3000\u3000\u5415\u8fea\u7684\u77b3\u5b54\u7a81\u7136\u6536\u7f29\u3002 \u3000\u3000\u5c31\u5728\u4ed6\u77b3\u5b54\u6536\u7f29\u7684\u4e00\u5239\u90a3\u95f4\uff0c\u4ed6\u4eba\u5df2\u7a9c\u51fa\u5899\u5916\u3002 \u3000\u3000\u6234\u9ad8\u5c97\u6ca1\u6709\u8ddf\u51fa\u53bb\uff0c\u5374\u8d76\u8fc7\u6765\uff0c\u6276\u8d77\u4e86\u53f6\u5f00\uff0c\u53f9\u606f\u7740\u9053\uff1a\u201c\u6211\u5b9e\u5728\u60f3\u4e0d\u5230\u4f60\u5c45\u7136\u4f1a \u8d25\u3002\u201d \u3000\u3000\u53f6\u5f00\u5374\u5728\u5fae\u7b11\uff1a\u201c\u6211\u4e5f\u60f3\u4e0d\u5230\u4f60\u5c45\u7136\u4f1a\u6551\u6211\u3002\u201d \u3000\u3000\u6234\u9ad8\u5c97\u82e6\u7b11\u9053\uff1a\u201c\u5e76\u4e0d\u662f\u6211\u6551\u4f60\u7684\uff0c\u6211\u4e5f\u6551\u4e0d\u4e86\u4f60\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u53ea\u8981\u4f60\u6709\u8fd9\u610f\u601d\uff0c\u5c31\u5df2\u8db3\u591f\u3002\u201d \u3000\u3000\u6234\u9ad8\u5c97\u52c9\u5f3a\u7b11\u4e86\u7b11\uff0c\u5ffd\u7136\u7ad9\u8d77\u6765\uff0c\u5927\u58f0\u5429\u5490\uff1a\u201c\u5957\u9a6c\u5907\u8f66\u3002\u201d \u7b2c\u5341\u516d\u7ae0\u3000\u864e\u7a74\u5a07\u5a03>> \u53e4\u9f99\u300a\u4e5d\u6708\u9e70\u98de\u300b \u7b2c\u5341\u516d\u7ae0\u3000\u864e\u7a74\u5a07\u5a03 \u3000\u3000\u8f66\u53a2\u5bbd\u5927\uff0c\u5f88\u8212\u670d\u3002 \u3000\u3000\u8fd9\u672c\u662f\u501f\u7ed9\u6258\u8fd0\u9556\u8d27\u7684\u541d\u5546\u4eec\uff0c\u8d70\u8fdc\u8def\u65f6\u5750\u7684\u3002 \u3000\u3000\u516b\u65b9\u9556\u5c40\u4e0d\u4f46\u4fe1\u7528\u6781\u597d\uff0c\u4e3a\u5ba2\u4eba\u4eec\u60f3\u5f97\u4e5f\u5f88\u5468\u5230\u3002 \u3000\u3000\u53f6\u5f00\u60f3\u4e0d\u5230\u6234\u9ad8\u5c97\u5c45\u7136\u662f\u4e2a\u5f88\u5468\u5230\u7684\u4eba\u3002 \u3000\u3000\u4ed6\u5148\u5728\u8f66\u53a2\u91cc\u57ab\u8d77\u4e86\u5f88\u539a\u7684\u68c9\u88ab\uff0c\u53c8\u81ea\u5df1\u6276\u7740\u53f6\u5f00\u5750\u4e0a\u8f66\u3002 \u3000\u3000\u201c\u4f60\u7684\u4f24\u4e0d\u8f7b\uff0c\u4e00\u5b9a\u8981\u8d76\u5feb\u53bb\u627e\u4e2a\u597d\u5927\u592b\u3002\u201d \u3000\u3000\u4ed6\u7684\u5468\u5230\u548c\u5173\u5fc3\uff0c\u5df2\u4f7f\u5f97\u53f6\u5f00\u4e0d\u80fd\u4e0d\u611f\u6fc0\u3002 \u3000\u3000\u53f6\u5f00\u53f9\u4e86\u53e3\u6c14\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u4f60\u672c\u4e0d\u8be5\u8fd9\u4e48\u6837\u5bf9\u6211\u7684\uff0c\u6211\u5bf9\u4f60\u7684\u6001\u5ea6\u5e76\u4e0d\u597d\u3002\u201d \u3000\u3000\u6234\u9ad8\u5c97\u9053\uff1a\u201c\u65e0\u8bba\u8c01\u5728\u4f60\u5f53\u65f6\u90a3\u79cd\u5fc3\u60c5\u4e0b\uff0c\u6001\u5ea6\u90fd\u4e0d\u4f1a\u597d\u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u53f9\u9053\uff1a\u201c\u770b\u6765\u6211\u4e0d\u4f46\u4f4e\u4f30\u4e86\u5415\u8fea\uff0c\u4e5f\u770b\u9519\u4e86\u4f60\u3002\u201d \u3000\u3000\u6234\u9ad8\u5c97\u4e5f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4ed6\u7684\u786e\u662f\u6211\u751f\u5e73\u672a\u89c1\u7684\u9ad8\u624b\uff0c\u4f46\u5374\u8fd8\u662f\u672a\u5fc5\u80fd\u6bd4\u5f97\u4e0a\u4f60\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u5df2\u8d25\u4e86\u3002\u201d \u3000\u3000\u6234\u9ad8\u5c97\u9053\uff1a\u201c\u53ef\u662f\u4ed6\u82e5\u771f\u7684\u8981\u6740\u4f60\uff0c\u73b0\u5728\u5df2\u6b7b\u5728\u4f60\u624b\u4e0b\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u4e5f\u76f8\u4fe1\u8fd9\u53e5\u8bdd\uff1f\u201d \u3000\u3000\u6234\u9ad8\u5c97\u70b9\u70b9\u5934\u3002 \u3000\u3000\u53f6\u5f00\u51dd\u89c6\u7740\u4ed6\uff0c\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u5728\u5899\u5916\u8bf4\u8fd9\u53e5\u8bdd\u7684\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u6234\u9ad8\u5c97\u6447\u6447\u5934\uff1a\u201c\u6211\u6b63\u60f3\u95ee\u4f60\uff0c\u4f60\u4e00\u5b9a\u77e5\u9053\u4ed6\u662f\u8c01\u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u6234\u9ad8\u5c97\u9053\uff0c\u201c\u56e0\u4e3a\u4ed6\u4e0d\u4f46\u8bf4\u51fa\u4e86\u4f60\u4e0d\u613f\u8bf4\u7684\u8bdd\uff0c\u800c\u4e14\u751f\u6015\u5415\u8fea\u518d\u4e0b\u6bd2\u624b\uff0c\u6240\u4ee5\u6545\u610f\u5c06\u4ed6 \u5f15\u5f00\u3002\u201d \u3000\u3000\u53f6\u5f00\u53c8\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u60f3\u5f97\u7684\u786e\u5f88\u5468\u5230\uff0c\u5374\u60f3\u9519\u4e86\u3002\u201d \u3000\u3000\u6234\u9ad8\u5c97\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\u4e0d\u662f\u4f60\u7684\u670b\u53cb\uff1f\u201d \u3000\u3000\u53f6\u5f00\u82e6\u7b11\u9053\uff1a\u201c\u6211\u672c\u6765\u4ee5\u4e3a\u4ed6\u662f\u6211\u7684\u670b\u53cb\u3002\u201d \u3000\u3000\u6234\u9ad8\u5c97\u9053\uff1a\u201c\u73b0\u5728\u5462\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u73b0\u5728\u53ea\u5e0c\u671b\u4ee5\u524d\u4ece\u6765\u6ca1\u6709\u89c1\u8fc7\u4ed6\uff0c\u4ee5\u540e\u4e5f\u6c38\u8fdc\u4e0d\u8981\u89c1\u5230\u4ed6\u3002\u201d \u3000\u3000\u6234\u9ad8\u5c97\u9053\uff1a\u201c\u4f60\u77e5\u9053\u4ed6\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u53f6\u5f00\u6ca1\u6709\u56de\u7b54\u8fd9\u53e5\u8bdd\uff0c\u5374\u53cd\u95ee\u9053\uff1a\u201c\u4f60\u8981\u5e26\u6211\u53bb\u6211\u7684\u5927\u592b\u662f\u8c01\uff1f\u201d \u3000\u3000\u6234\u9ad8\u5c97\u9053\uff1a\u201c\u90a3\u4e2a\u5927\u592b\u4e5f\u662f\u4e2a\u5f88\u53e4\u602a\u7684\u4eba\uff0c\u533b\u9053\u5374\u5f88\u9ad8\u3002\u201d \u3000\u3000\u53f6\u5f00\u5ffd\u7136\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u533b\u9053\u9ad8\u660e\u7684\u5927\u592b\uff0c\u813e\u6c14\u597d\u50cf\u90fd\u6709\u4e9b\u53e4\u602a\u7684\uff0c\u5c31\u6b63\u5982\u771f\u6b63\u7684\u6b66\u6797 \u9ad8\u624b\uff0c\u813e\u6c14\u4e5f\u90fd\u6709\u4e9b\u53e4\u602a\u4e00\u6837\u3002\u201d \u3000\u3000\u53f6\u5f00\u5fae\u7b11\u7740\uff0c\u9053\uff1a\u201c\u4f60\u7684\u813e\u6c14\u5e76\u4e0d\u53e4\u602a\u3002\u201d \u3000\u3000\u6234\u9ad8\u5c97\u9053\uff1a\u201c\u6211\u600e\u4e48\u80fd\u7b97\u6b66\u6797\u9ad8\u624b\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f46\u6211\u5374\u77e5\u9053\uff0c\u8fd1\u5e74\u6765\u516b\u65b9\u9556\u5c40\u4fdd\u7684\u9556\uff0c\u4ece\u6765\u4e5f\u6ca1\u6709\u51fa\u8fc7\u4e00\u6b21\u5c94\u5b50\u3002\u201d \u3000\u3000\u6234\u9ad8\u5c97\u7b11\u9053\uff1a\u201c\u90a3\u53ea\u4e0d\u8fc7\u56e0\u4e3a\u6211\u8fd9\u4e24\u5e74\u6765\u7684\u8fd0\u6c14\u4e0d\u9519\uff0c\u800c\u4e14\u6709\u5f88\u591a\u5f88\u597d\u7684\u670b\u53cb\u7167\u987e\u3002\u201d \u3000\u3000\u53f6\u5f00\u6162\u6162\u5730\u70b9\u4e86\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6211\u76f8\u4fe1\u4f60\u4e00\u5b9a\u6709\u5f88\u591a\u597d\u670b\u53cb\u3002\u201d \u3000\u3000\u6234\u9ad8\u5c97\u8fd8\u60f3\u8bf4\u4ec0\u4e48\uff0c\u4f46\u5219\uff0c\u5f00\u5374\u5df2\u95ed\u4e0a\u4e86\u773c\u775b\u3002 \u3000\u3000\u4ed6\u770b\u6765\u7684\u786e\u5f88\u75b2\u5026\uff0c\u4ed6\u5e76\u4e0d\u662f\u94c1\u6253\u7684\u3002 \u3000\u3000\u6234\u9ad8\u5c97\u53c8\u62c9\u8fc7\u6761\u68c9\u88ab\uff0c\u8f7b\u8f7b\u5730\u76d6\u5728\u4ed6\u8eab\u4e0a\uff0c\u8138\u4e0a\u5374\u5e26\u7740\u79cd\u5f88\u5947\u602a\u7684\u8868\u60c5\u3002 \u3000\u3000\u770b\u4ed6\u8fd9\u79cd\u8868\u60c5\uff0c\u5c31\u597d\u50cf\u6068\u4e0d\u5f97\u7528\u8fd9\u6761\u68c9\u88ab\u8499\u8d77\u53f6\u5f00\u7684\u5934\uff0c\u6d3b\u6d3b\u5730\u95f7\u6b7b\u8fd9\u4e2a\u4eba\u3002 \u3000\u3000\u4f46\u4ed6\u5374\u53ea\u4e0d\u8fc7\u5c06\u68c9\u88ab\u76d6\u5230\u53f6\u5f00\u8eab\u4e0a\u3002 \u3000\u3000\u53f6\u5f00\u4f3c\u5df2\u7761\u7740\u3002 \u3000\u3000\u73b0\u5728\u5c31\u7b97\u771f\u7684\u6709\u4eba\u8981\u7528\u68c9\u88ab\u95f7\u6b7b\u4ed6\uff0c\u4ed6\u4e5f\u4e0d\u4f1a\u77e5\u9053\uff0c\u4ed6\u66f4\u4e0d\u80fd\u53cd\u6297\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u771f\u7684\u7761\u7740\u4e86\u3002 \u3000\u3000\u65e5\u6b63\u5f53\u4e2d\uff0c\u6b63\u5348\u3002 \u3000\u3000\u9a6c\u8f66\u8fd8\u5728\u7ee7\u7eed\u524d\u8d70\uff0c\u65c5\u7a0b\u4eff\u4f5b\u8fd8\u6709\u5f88\u957f\u3002 \u3000\u3000\u201c\u4f60\u4e00\u5b9a\u8981\u8d76\u5feb\u627e\u4e2a\u597d\u5927\u592b\u2026\u2026\u201d \u3000\u3000\u53ef\u662f\u6234\u9ad8\u5c97\u8981\u627e\u7684\u8fd9\u597d\u5927\u592b\uff0c\u5374\u672a\u514d\u4f4f\u5f97\u592a\u8fdc\u4e86\u4e9b\u3002 \u3000\u3000\u4ed6\u770b\u7740\u6c89\u7761\u4e2d\u7684\u53f6\u5f00\uff0c\u5634\u91cc\u6b63\u5728\u56bc\u7740\u4e00\u6761\u9e21\u817f\u3002 \u3000\u3000\u4ed6\u65e9\u5df2\u6709\u51c6\u5907\uff0c\u51c6\u5907\u8981\u8d70\u5f88\u957f\u7684\u8def\uff0c\u6240\u4ee5\u8fde\u5348\u996d\u90fd\u51c6\u5907\u5728\u8f66\u4e0a\u3002 \u3000\u3000\u4ed6\u672c\u6765\u5c31\u662f\u4e2a\u5f88\u5468\u5230\u7684\u4eba\uff0c\u4f46\u5374\u53ea\u6709\u4e00\u4e2a\u4eba\u5403\u7684\u5348\u996d\uff0c\u53ea\u6709\u4e00\u6761\u9e21\u817f\uff0c\u4e00\u5757\u725b\u8089\uff0c\u4e00\u5f20 \u997c\uff0c\u4e00\u74f6\u9152\u3002 \u3000\u3000\u4ed6\u597d\u4f3c\u65e9\u5df2\u7b97\u51c6\u4e86\u53f6\u5f00\u8981\u7761\u7740\uff0c\u56e0\u4e3a\u4e34\u4e0a\u8f66\u4e4b\u524d\uff0c\u4ed6\u7ed9\u53f6\u5f00\u559d\u4e86\u4e00\u7897\u4fdd\u517b\u5143\u6c14\u7684\u53c2\u6c64\u3002 \u3000\u3000\u725b\u8089\u5364\u5f97\u4e0d\u9519\uff0c\u9e21\u817f\u7684\u6ecb\u5473\u4e5f\u5f88\u597d\uff0c\u867d\u7136\u6bd4\u4e0d\u4e0a\u4ed6\u5e73\u65f6\u5403\u7684\u5348\u996d\uff0c\u53ef\u662f\u5728\u6267\u884c\u4efb\u52a1\u65f6\uff0c \u4e00\u5207\u4e8b\u90fd\u4e0d\u80fd\u4e0d\u5c06\u5c31\u4e9b\u7684\u3002 \u3000\u3000\u4ed6\u867d\u7136\u662f\u4e2a\u5f88\u8bb2\u7a76\u996e\u98df\u7684\u4eba\uff0c\u73b0\u5728\u4e5f\u5df2\u89c9\u5f97\u5f88\u6ee1\u610f\u4e86\u3002 \u3000\u3000\u4f55\u51b5\uff0c\u73b0\u5728\u4ed6\u7684\u4efb\u52a1\u773c\u770b\u7740\u5c31\u5df2\u5c06\u5b8c\u6210\uff0c\u518d\u8fc7\u4e00\u4e2a\u591a\u65f6\u8fb0\uff0c\u5c31\u53ef\u4ee5\u5c06\u53f6\u5f00\u4ea4\u51fa\u53bb\uff0c\u4ed6\u8fd8 \u6765\u5f97\u53ca\u8d76\u56de\u53bb\u4eab\u53d7\u4e00\u987f\u4e30\u5bcc\u7684\u665a\u9910\u3002 \u3000\u3000\u559d\u5b8c\u4e86\u6700\u540e\u4e00\u53e3\u9152\uff0c\u4ed6\u5ffd\u7136\u4e5f\u89c9\u5f97\u5f88\u75b2\u5026\u3002 \u3000\u3000\u4ed6\u672c\u6ca1\u6709\u7761\u5348\u89c9\u7684\u4e60\u60ef\uff0c\u53ef\u662f\u73b0\u5728\u80fd\u4e58\u673a\u5c0f\u7761\u534a\u4e2a\u65f6\u8fb0\u4e5f\u4e0d\u9519\uff0c\u7cbe\u795e\u517b\u8db3\u4e86\uff0c\u665a\u9910\u540e\u8fd8 \u53ef\u4ee5\u5b89\u6392\u4e00\u4e24\u4e2a\u6709\u8da3\u7684\u8282\u76ee\u3002 \u3000\u3000\u8f66\u5b50\u5728\u6447\u52a8\uff0c\u5c31\u50cf\u662f\u6447\u7bee\u4e00\u6837\u3002 \u3000\u3000\u4ed6\u95ed\u4e0a\u4e86\u773c\u775b\uff0c\u5fc3\u91cc\u5df2\u5f00\u59cb\u5728\u8ba1\u5212\u7740\u665a\u4e0a\u5e94\u8be5\u53bb\u627e\u8c01\uff1f\u662f\u90a3\u4e2a\u6700\u4f1a\u6492\u5a07\u7684\u5c0f\u5996\u7cbe\uff1f\u8fd8\u662f \u90a3\u4e2a\u529f\u592b\u7279\u522b\u597d\u7684\u8001\u5996\u7cbe\uff1f \u3000\u3000\u8fd9\u4e9b\u8282\u76ee\u90fd\u662f\u5f88\u8d39\u94b1\u7684\uff0c\u4f46\u4ed6\u5374\u5df2\u6709\u4e24\u5e74\u4e0d\u5fc5\u518d\u4e3a\u91d1\u94b1\u70e6\u607c\u3002 \u3000\u3000\u201c\u4e5f\u8bb8\u5e94\u8be5\u628a\u4e24\u4e2a\u90fd\u627e\u6765\uff0c\u6bd4\u8f83\u6bd4\u8f83\u3002\u201d \u3000\u3000\u6240\u4ee5\u73b0\u5728\u5fc5\u9700\u517b\u8db3\u7cbe\u795e\u3002 \u3000\u3000\u4ed6\u5634\u89d2\u5e26\u7740\u5fae\u7b11\uff0c\u7ec8\u4e8e\u7761\u7740\u3002 \u3000\u3000\u4ed6\u597d\u50cf\u53ea\u7761\u4e86\u4e00\u4e0b\u5b50\uff0c\u53ef\u662f\u4ed6\u9192\u6765\u7684\u65f6\u5019\uff0c\u53f6\u5f00\u7adf\u4e5f\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u8f66\u95e8\u8fd8\u662f\u5173\u7740\u7684\uff0c\u9a6c\u8f66\u8fd8\u5728\u7ee7\u7eed\u524d\u884c\u3002 \u3000\u3000\u53f6\u5f00\u5374\u5df2\u65e0\u5f71\u65e0\u8e2a\u3002 \u3000\u3000\u6234\u9ad8\u5c97\u7684\u8138\u8272\u7a81\u7136\u82cd\u767d\uff0c\u5927\u58f0\u5429\u5490\uff1a\u201c\u505c\u8f66\uff01\u201d \u3000\u3000\u4ed6\u51b2\u4e0b\u53bb\uff0c\u62c9\u4f4f\u4e86\u90a3\u4e2a\u8d76\u8f66\u7684\uff1a\u201c\u4f60\u6709\u6ca1\u6709\u770b\u89c1\u90a3\u59d3\u53f6\u7684\u4e0b\u8f66\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u201c\u4ed6\u4eba\u5462\uff1f\u201d \u3000\u3000\u8d76\u8f66\u7684\u51b7\u7b11\uff1a\u201c\u4f60\u8ddf\u4ed6\u4e00\u8d77\u5728\u8f66\u91cc\u4f60\u4e0d\u77e5\u9053\uff0c\u6211\u600e\u4e48\u77e5\u9053\u3002\u201d \u3000\u3000\u8fd9\u8d76\u8f66\u7684\u663e\u7136\u4e0d\u662f\u4ed6\u7684\u5c5e\u4e0b\uff0c\u5bf9\u4ed6\u7684\u6001\u5ea6\u5e76\u4e0d\u5c0a\u656c\u3002 \u3000\u3000\u6234\u9ad8\u5c97\u5ffd\u7136\u89c9\u5f97\u80c3\u90e8\u6536\u7f29\uff0c\u5fcd\u4e0d\u4f4f\u8981\u5c06\u521a\u5403\u4e0b\u53bb\u7684\u9e21\u817f\u548c\u725b\u8089\u5168\u5410\u51fa\u6765\u3002 \u3000\u3000\u8d76\u8f66\u7684\u4e00\u53cc\u773c\u775b\u5374\u5728\u76ef\u7740\u4ed6\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u6700\u597d\u8fd8\u662f\u8d76\u5feb\u4e0a\u8f66\uff0c\u8ddf\u6211\u4e00\u8d77\u53bb\u4ea4\u5dee\u3002\u201d \u3000\u3000\u6234\u9ad8\u5c97\u5e76\u6ca1\u6709\u60f3\u9003\uff0c\u4ed6\u77e5\u9053\u65e0\u8bba\u9003\u5230\u4ec0\u4e48\u5730\u65b9\u53bb\uff0c\u90fd\u6ca1\u6709\u7528\u7684\u3002 \u3000\u3000\u9a6c\u8f66\u5f00\u59cb\u5f80\u524d\u8d70\u7684\u65f6\u5019\uff0c\u4ed6\u5c31\u4f0f\u5728\u8f66\u7a97\u4e0a\uff0c\u4e0d\u505c\u5730\u5455\u5410\u3002 \u3000\u3000\u6050\u60e7\u5c31\u50cf\u662f\u81ed\u9c7c\u4e00\u6837\uff0c\u603b\u662f\u4ee4\u4eba\u5455\u5410\u3002 \u3000\u3000\u9a6c\u8f66\u8f6c\u8fc7\u4e00\u4e2a\u5c71\u62d7\u540e\uff0c\u524d\u9762\u7adf\u662f\u4e00\u6761\u8857\u9053\u3002 \u3000\u3000\u4e00\u6761\u548c\u57ce\u91cc\u4e00\u6837\u975e\u5e38\u70ed\u95f9\u7684\u8857\u9053\uff0c\u4e24\u65c1\u6709\u5404\u5f0f\u5404\u6837\u7684\u5e97\u94fa\uff0c\u8857\u4e0a\u6709\u5404\u5f0f\u5404\u6837\u7684\u4eba\u3002 \u3000\u3000\u4f60\u82e5\u4ed4\u7ec6\u53bb\u770b\uff0c\u5c31\u4f1a\u53d1\u73b0\u8fd9\u6761\u8857\u9053\u548c\u57ce\u91cc\u6700\u70ed\u95f9\u7684\u8857\u9053\u7adf\u662f\u5b8c\u5168\u4e00\u6a21\u4e00\u6837\u7684\uff0c\u8fde\u8857\u9053\u4e24 \u65c1\u7684\u5e97\u94fa\uff0c\u62db\u724c\u90fd\u5b8c\u5168\u4e00\u6837\u3002 \u3000\u3000\u5230\u4e86\u8fd9\u91cc\uff0c\u65e0\u8bba\u8c01\u90fd\u4f1a\u4ee5\u4e3a\u81ea\u5df1\u5ffd\u7136\u53c8\u56de\u5230\u4e86\u957f\u5b89\u57ce\u91cc\u3002 \u3000\u3000\u53ef\u662f\u8d70\u8fc7\u8fd9\u6761\u8857\uff0c\u524d\u9762\u5c31\u53c8\u662f\u4e00\u7247\u8352\u5c71\u3002 \u3000\u3000\u73b0\u5728\u9a6c\u8f66\u7684\u901f\u5ea6\u5df2\u7f13\u4e86\u4e0b\u6765\uff0c\u8857\u4e0a\u7684\u884c\u4eba\uff0c\u795e\u60c5\u4eff\u4f5b\u90fd\u5f88\u60a0\u95f2\uff0c\u597d\u88e3\u5e76\u6ca1\u6709\u7279\u522b\u6ce8\u610f\u8fd9 \u8f86\u5927\u8f66\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u4eec\u8ba4\u5f97\u8fd9\u8f86\u8f66\uff0c\u4e5f\u8ba4\u5f97\u8fd9\u4e2a\u8d76\u8f66\u7684\u4eba\u3002 \u3000\u3000\u82e5\u662f\u4e2a\u964c\u751f\u7684\u4eba\uff0c\u8d76\u7740\u8f66\u8d70\u5165\u8fd9\u6761\u8857\u9053\uff0c\u65e0\u8bba\u4ed6\u662f\u8c01\uff0c\u4e0d\u51fa\u4e00\u5239\u90a3\uff0c\u4ed6\u5c31\u4f1a\u6b7b\u5728\u8857\u5934\u3002 \u3000\u3000\u8fd9\u6761\u8857\u5f53\u7136\u4e0d\u4f1a\u6709\u731b\u864e\uff0c\u5374\u6709\u4e2a\u6bd4\u731b\u864e\u66f4\u53ef\u6015\u7684\u4eba\u3002 \u3000\u3000\u9a6c\u8f66\u5df2\u9a76\u4eba\u4e86\u4e00\u5bb6\u5ba2\u6808\u7684\u9662\u5b50\u3002 \u3000\u3000\u8fd9\u5bb6\u5ba2\u6808\u7684\u5b57\u53f7\u662f\u9e3f\u5bbe\uff0c\u4e5f\u6b63\u548c\u53f6\u5f00\u5728\u57ce\u91cc\u6295\u5bbf\u7684\u90a3\u7b20\u5bb6\uff0c\u5b8c\u5168\u4e00\u6a21\u4e00\u6837\u3002 \u3000\u3000\u4e00\u4e2a\u80a9\u4e0a\u642d\u7740\u62b9\u5e03\u3001\u5343\u91cc\u63d0\u7740\u6c34\u58f6\u7684\u4f19\u8ba1\uff0c\u5df2\u8fce\u4e86\u4e0a\u6765\uff1a\u201c\u6234\u603b\u9556\u5934\u662f\u4e00\u4e2a\u4eba\u6765\u7684\uff1f\u201d \u3000\u3000\u6234\u9ad8\u5c97\u52c9\u5f3a\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u53ea\u6709\u4e00\u4e2a\u4eba\u3002\u201d \u3000\u3000\u4f19\u8ba1\u8138\u4e0a\u5168\u65e0\u8868\u60c5\uff1a\u201c\u623f\u95f4\u65e9\u5df2\u66ff\u603b\u9556\u5934\u51c6\u5907\u597d\u4e86\uff0c\u8bf7\u968f\u6211\u6765\u3002\u201d \u3000\u3000\u540e\u9762\u7684\u8de8\u9662\u91cc\uff0c\u6709\u4e03\u95f4\u5f88\u5bbd\u5927\u7684\u5957\u623f\uff0c\u4e5f\u6b63\u548c\u7389\u7bab\u9053\u4eba\u4f4f\u7684\u90a3\u4e2a\u8de8\u9662\u4e00\u6837\u3002 \u3000\u3000\u524d\u9762\u7684\u5ba2\u5385\u91cc\uff0c\u684c\u4e0a\u5df2\u6446\u597d\u4e86\u4e00\u58f6\u9152\uff0c\u4e00\u4e2a\u5f88\u7cbe\u81f4\u7684\u4e03\u8272\u62fc\u76d8\uff0c\u4e00\u4e2a\u4eba\u6b63\u80cc\u5bf9\u7740\u95e8\uff0c\u5728 \u81ea\u659f\u81ea\u996e\u3002 \u3000\u3000\u4e00\u4e2a\u53d1\u9afb\u5806\u4e91\u3001\u6ee1\u5934\u73e0\u7fe0\u3001\u7a7f\u5f97\u975e\u5e38\u534e\u4e3d\u7684\u7edd\u4ee3\u4f73\u4eba\u3002 \u3000\u3000\u6234\u9ad8\u5c97\u5782\u7740\u5934\u8d70\u8fdb\u6765\uff0c\u5782\u7740\u5934\u7ad9\u5728\u5979\u8eab\u540e\uff0c\u8fde\u5927\u6c14\u90fd\u4e0d\u6562\u51fa\u3002 \u3000\u3000\u5979\u6ca1\u6709\u51fa\u58f0\uff0c\u6162\u6162\u5730\u7aef\u8d77\u9152\u676f\uff0c\u6d45\u6d45\u5730\u555c\u4e86\u53e3\u9152\uff0c\u624d\u95ee\u9053\uff1a\u201c\u4f60\u4e00\u4e2a\u4eba\u6765\u7684\uff1f\u201d \u3000\u3000\u6234\u9ad8\u5c97\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u201c\u8fd8\u6709\u4e2a\u4eba\u5462\uff1f\u201d \u3000\u3000\u201c\u8d70\u4e86\u3002\u201d\u6234\u9ad8\u5c97\u7684\u58f0\u97f3\u5df2\u5728\u53d1\u6296\u3002 \u3000\u3000\u8fd9\u7edd\u8272\u4e3d\u4eba\u5df2\u7f13\u7f13\u5730\u56de\u8fc7\u5934\u53bb\uff0c\u8138\u4e0a\u5e26\u7740\u79cd\u4ed9\u5b50\u822c\u7684\u5fae\u7b11\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\uff01 \u3000\u3000\u5979\u5f53\u7136\u5c31\u662f\u4e0a\u5b98\u5c0f\u4ed9\u3002 \u3000\u3000\u6234\u9ad8\u5c97\u770b\u89c1\u4e86\u8fd9\u4ed9\u5b50\u822c\u7f8e\u4e3d\u7684\u5973\u4eba\uff0c\u5374\u8fdc\u6bd4\u770b\u89c1\u4e86\u6076\u9b54\u8fd8\u6050\u60e7\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u770b\u7740\u4ed6\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u4f60\u96be\u9053\u662f\u5728\u8bf4\uff0c\u53f6\u5f00\u5df2\u8d70\u4e86\uff1f\u201d \u3000\u3000\u6234\u9ad8\u5c97\u70b9\u4e86\u70b9\u5934\uff0c\u7259\u9f7f\u6253\u6218\uff0c\u4f3c\u5df2\u8fde\u8bdd\u90fd\u8bf4\u4e0d\u51fa\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6211\u8981\u4f60\u66ff\u4ed6\u51c6\u5907\u7684\u90a3\u7897\u53c2\u6c64\uff0c\u4ed6\u6ca1\u6709\u559d\uff1f\u201d \u3000\u3000\u201c\u4ed6\u2026\u2026\u4ed6\u559d\u4e86\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u7136\u540e\u5462\uff1f\u201d \u3000\u3000\u6234\u9ad8\u5c97\u9053\uff1a\u201c\u7136\u540e\u6211\u5c31\u6276\u4ed6\u4e0a\u4e86\u8f66\u3002\u201d \u3000\u3000\u867d\u7136\u662f\u4e25\u51ac\uff0c\u4f46\u4ed6\u5374\u5df2\u6ee1\u5934\u5927\u6c57\u3002\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u5728\u8f66\u4e0a\u4ed6\u7761\u7740\u4e86\u6ca1\u6709\uff1f\u201d \u3000\u3000\u6234\u9ad8\u5c97\u9053\uff1a\u201c\u7761\u7740\u4e86\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4ed6\u7684\u4f24\u52bf\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u6234\u9ad8\u5c97\u9053\uff1a\u201c\u4f24\u5f97\u4e0d\u8f7b\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8fd9\u6211\u5c31\u4e0d\u61c2\u4e86\uff0c\u4e00\u4e2a\u53d7\u4e86\u91cd\u4f24\u3001\u53c8\u7761\u7740\u4e86\u7684\u4eba\uff0c\u4f60\u600e\u4e48\u4f1a\u653e \u4ed6\u8d70\u7684\uff1f\u201d \u3000\u3000\u6234\u9ad8\u5c97\u63a5\u7740\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u6ca1\u6709\u653e\u4ed6\u8d70\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6211\u4e5f\u77e5\u9053\u662f\u4ed6\u81ea\u5df1\u8981\u8d70\u7684\uff0c\u53ef\u662f\u4f60\u96be\u9053\u5c31\u4e0d\u80fd\u7559\u4f4f\u4ed6\uff1f\u201d \u3000\u3000\u6234\u9ad8\u5c97\u7684\u6c57\u8d8a\u64e6\u8d8a\u591a\uff1a\u201c\u4ed6\u8d70\u7684\u65f6\u5019\uff0c\u6211\u6839\u672c\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4f60\u8ddf\u4ed6\u4e0d\u662f\u5750\u4e00\u8f86\u8f66\u6765\u7684\uff1f\u201d \u3000\u3000\u6234\u9ad8\u5c97\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u8fd9\u53c8\u5947\u602a\u4e86\uff0c\u4f60\u8ddf\u4ed6\u5750\u4e00\u8f86\u8f66\u4e0a\uff0c\u4ed6\u8d70\u7684\u65f6\u5019\uff0c\u4f60\u600e\u4e48\u4f1a\u4e0d\u77e5\u9053\uff1f\u201d \u3000\u3000\u6234\u9ad8\u5c97\u9053\uff1a\u201c\u56e0\u4e3a\u2026\u2026\u56e0\u4e3a\u2026\u2026\u56e0\u4e3a\u6211\u4e5f\u7761\u7740\u4e86\u3002\u201d \u3000\u3000\u4ed6\u7ec8\u4e8e\u9f13\u8db3\u4e86\u52c7\u6c14\uff0c\u8bf4\u51fa\u4e86\u8fd9\u53e5\u8bdd\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u5ffd\u7136\u7b11\u4e86\uff0c\u7b11\u5f97\u53c8\u6e29\u67d4\uff0c\u53c8\u751c\u871c\uff1a\u201c\u6211\u77e5\u9053\u4f60\u4e00\u5b9a\u4e5f\u5f88\u7d2f\uff0c\u6700\u8fd1\u4f60\u4e00\u76f4\u90fd\u5fd9\u5f97 \u5f88\u3002\u201d \u3000\u3000\u6234\u9ad8\u5c97\u8138\u4e0a\u5df2\u65e0\u4eba\u8272\uff1a\u201c\u6211\u2026\u2026\u6211\u4e0d\u7d2f\uff0c\u4e00\u70b9\u4e5f\u4e0d\u7d2f\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u67d4\u58f0\u9053\uff1a\u201c\u4f60\u7684\u5e94\u916c\u90a3\u4e48\u591a\uff0c\u4e0d\u4f46\u8981\u5e94\u916c\u5ba2\u4eba\uff0c\u8fd8\u5f97\u8981\u5e94\u916c\u90a3\u4e9b\u5927\u5927\u5c0f\u5c0f\u7684\u5996 \u7cbe\uff0c\u600e\u4e48\u4f1a\u4e0d\u7d2f\u5462\uff1f\u201d \u3000\u3000\u5979\u8f7b\u8f7b\u53f9\u606f\u7740\uff0c\u53c8\u9053\uff1a\u201c\u6211\u60f3\u4f60\u5df2\u7ecf\u5e94\u8be5\u597d\u597d\u7684\u4f11\u606f\u4e00\u9635\u5b50\u4e86\uff0c\u6211\u5c31\u5148\u8ba9\u4f60\u4f11\u606f\u4e8c\u5341\u5e74 \u5427\u3002\u201d \u3000\u3000\u6234\u9ad8\u5c97\u5931\u58f0\u9053\uff1a\u201c\u4e8c\u2026\u2026\u4e8c\u5341\u5e74\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u6de1\u6de1\u9053\uff1a\u201c\u4e8c\u5341\u5e74\u540e\uff0c\u4f60\u4e00\u5b9a\u53c8\u662f\u6761\u751f\u9f99\u6d3b\u864e\u822c\u7684\u597d\u6c49\u4e86\u3002\u201d \u3000\u3000\u5979\u638c\u91cc\u62ff\u7740\u53cc\u9576\u94f6\u7684\u8c61\u7259\u7b77\u5b50\uff0c\u5ffd\u7136\u5411\u6234\u9ad8\u5c97\u54bd\u5589\u70b9\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u6234\u9ad8\u5c97\u6ca1\u6709\u95ea\u907f\u3002\u4ed6\u4e0d\u6562\u95ea\u907f\uff0c\u4e5f\u6839\u672c\u4e0d\u80fd\u95ea\u907f\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u7684\u51fa\u624b\uff0c\u8fd9\u4e16\u4e0a\u5df2\u5f88\u5c11\u6709\u4eba\u80fd\u95ea\u907f\u5f97\u5f00\u3002 \u3000\u3000\u4f46\u662f\uff0c\u5c31\u5728\u8fd9\u4e00\u5239\u90a3\u95f4\uff0c\u7a81\u7136\u6709\u5200\u5149\u4e00\u95ea\u3002 \u3000\u3000\u201c\u53ee\u201d\u7684\u4e00\u58f0\uff0c\u4e0a\u5b98\u5c0f\u4ed9\u624b\u91cc\u7684\u8c61\u7259\u7b77\u5b50\u5df2\u4ece\u4e2d\u800c\u65ad\uff0c\u5200\u5149\u7684\u52b2\u529b\u672a\u7edd\uff0c\u53c8\u98de\u4e86\u51fa\u53bb\uff0c \u201c\u5f53\u201d\u7684\uff0c\u9489\u5728\u5899\u4e0a\u3002 \u3000\u3000\u4e00\u67c4\u4e09\u5bf8\u4e03\u5206\u957f\u7684\u5200\u3002 \u3000\u3000\u98de\u5200\u3002 \u3000\u3000\u98de\u5200\u9489\u5728\u5899\u4e0a\uff0c\u5200\u950b\u7adf\u5df2\u5b8c\u5168\u9489\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u624b\u6276\u7740\u95e8\uff0c\u6162\u6162\u5730\u8d70\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u53f6\u5f00\u3002 \u3000\u3000\u53f6\u5f00\u5c45\u7136\u8fd8\u662f\u6765\u4e86\u3002 \u3000\u3000\u4ed6\u7684\u98de\u5200\u51fa\u624b\uff0c\u6740\u4eba\u7684\u65f6\u5019\u5c11\uff0c\u6551\u4eba\u7684\u65f6\u5019\u591a\u3002 \u3000\u3000\u4ed6\u7684\u8138\u4e0a\u6ca1\u6709\u4ec0\u4e48\u8840\u8272\uff0c\u6323\u624e\u7740\u8d70\u8fc7\u6765\uff0c\u62cd\u4e86\u62cd\u6234\u9ad8\u5c97\u7684\u80a9\uff1a\u201c\u4f60\u6551\u6211\u4e00\u6b21\uff0c\u6211\u4e5f\u6551\u4f60 \u4e00\u6b21\uff0c\u73b0\u5728\u6211\u4eec\u7684\u4eba\u60c5\u5df2\u7ed3\u6e05\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u53c8\u7b11\u4e86\uff1a\u201c\u6211\u8bf4\u7684\u679c\u7136\u4e0d\u9519\uff0c\u4f60\u8eab\u4e0a\u679c\u7136\u4e0d\u6b62\u5e26\u7740\u4e00\u628a\u5200\u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u4e5f\u7b11\u4e86\u7b11\uff1a\u201c\u5415\u8fea\u5462\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4ed6\u600e\u4e48\u4f1a\u8ffd\u5f97\u4e0a\u6211\uff1f\u201d\u5979\u51dd\u89c6\u7740\u53f6\u5f00\uff0c\u7b11\u5f97\u66f4\u6e29\u67d4\uff1a\u201c\u9664\u4e86\u4f60\u4e4b\u5916\uff0c\u4e16 \u4e0a\u8fd8\u6709\u4ec0\u4e48\u7537\u4eba\u80fd\u8ffd\u5f97\u4e0a\u6211\u3002\u201d \u3000\u3000\u8fd9\u662f\u53e5\u5f88\u6709\u8da3\u7684\u53cc\u5173\u8bed\uff0c\u6709\u8da3\u6781\u4e86\u3002 \u3000\u3000\u53f6\u5f00\u542c\u4e0d\u61c2\u3002 \u3000\u3000\u2014\u2014\u88c5\u50bb\u5c31\u662f\u4ed6\u62ff\u624b\u672c\u9886\u4e4b\u4e00\u3002 \u3000\u3000\u4ed6\u751a\u81f3\u8fde\u770b\u90fd\u6ca1\u6709\u770b\u5979\uff0c\u76ee\u5149\u56db\u9762\u6253\u91cf\u7740\uff0c\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8fd9\u771f\u662f\u4e2a\u597d\u5730\u65b9\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4f60\u559c\u6b22\u8fd9\u5730\u65b9\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u82e5\u4e00\u76f4\u7761\u7740\uff0c\u5230\u73b0\u5728\u624d\u9192\uff0c\u4e00\u5b9a\u4ee5\u4e3a\u8fd8\u5728\u57ce\u91cc\uff0c\u4e00\u5b9a\u60f3\u4e0d\u5230\u91d1\u94b1\u5e2e\u7684\u603b\u8235 \u4f1a\u5728\u8fd9\u4e48\u6837\u4e00\u4e2a\u5730\u65b9\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u53f9\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u4f60\u597d\u50cf\u662f\u4e0d\u80af\u597d\u597d\u7761\u4e00\u4e0b\u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u6de1\u6de1\u9053\uff1a\u201c\u6211\u7684\u5e94\u916c\u5e76\u4e0d\u591a\uff0c\u8ba4\u5f97\u7684\u5996\u7cbe\u4e5f\u53ea\u6709\u4e00\u4e2a\u3002\u6240\u4ee5\u6211\u603b\u4e0d\u592a\u7d2f\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u5f53\u7136\u77e5\u9053\u4ed6\u8bf4\u7684\u5996\u7cbe\u662f\u8c01\uff0c\u53ef\u662f\u5979\u88c5\u50bb\u7684\u672c\u4e8b\u4e5f\u7edd\u4e0d\u6bd4\u53f6\u5f00\u5dee\u3002 \u3000\u3000\u5979\u5403\u5403\u5730\u7b11\u7740\u9053\uff1a\u201c\u6211\u672c\u6765\u4ee5\u4e3a\u4f60\u4f1a\u5f88\u7d2f\u7684\uff0c\u6700\u8fd1\u6211\u770b\u5230\u4f60\u7684\u65f6\u5019\uff0c\u4f60\u603b\u662f\u5728\u5e8a\u4e0a\uff0c\u5e8a \u4e0a\u7684\u5996\u7cbe\uff0c\u5374\u4e0d\u6b62\u4e00\u4e2a\uff0c\u6240\u4ee5\u7279\u5730\u53eb\u4eba\u66ff\u4f60\u51c6\u5907\u4e86\u7897\u53c2\u6c64\uff0c\u517b\u517b\u4f60\u7684\u5143\u6c14\uff0c\u8c01\u77e5\u4f60\u5c45\u7136\u4e0d\u9886 \u60c5\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u5df2\u9886\u8fc7\u4e86\u60c5\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u7728\u7740\u773c\uff0c\u9053\uff1a\u201c\u90a3\u7897\u53c2\u6c64\u4f60\u771f\u7684\u559d\u4e86\u4e0b\u53bb\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u90a3\u7897\u53c2\u6c64\u4e0b\u7684\u8865\u836f\u8fd8\u4e0d\u591f\uff0c\u82e5\u8981\u53eb\u6211\u771f\u7684\u7761\u4e00\u89c9\uff0c\u6700\u5c11\u5f97\u7528\u5341\u6765\u65a4\u8865 \u836f\u624d\u884c\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8fd9\u90fd\u602a\u6211\uff0c\u7adf\u5fd8\u4e86\u4f60\u662f\u9b54\u6559\u4e2d\u5927\u516c\u4e3b\u7684\u5927\u5c11\u7237\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u4e0d\u80fd\u602a\u6234\u603b\u9556\u5934\uff0c\u6211\u76f8\u4fe1\u4ed6\u81ea\u5df1\u4e5f\u4e0d\u77e5\u9053\u81ea\u5df1\u662f\u600e\u4e48\u4f1a\u7761\u7740\u7684\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff0c\u201c\u53ef\u662f\u4f60\u77e5\u9053\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u4e00\u4e0a\u8f66\uff0c\u5c31\u53d1\u73b0\u4e86\u4ed6\u4e3a\u4ed6\u81ea\u5df1\u4e00\u4e2a\u4eba\u51c6\u5907\u7684\u9152\u83dc\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4f60\u8eab\u4e0a\u96be\u9053\u4e5f\u603b\u662f\u5e26\u7740\u80fd\u4ee4\u4eba\u7761\u7740\u7684\u8865\u836f\uff1f\u201d \u3000\u3000\u53f6\u5f00\u7b11\u4e86\u7b11\u9053\uff1a\u201c\u6211\u53ea\u4e0d\u8fc7\u5410\u4e86\u70b9\u53e3\u6c34\u5728\u4ed6\u7684\u9e21\u817f\u4e0a\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u7b11\u4e86\uff1a\u201c\u4f60\u7684\u53e3\u6c34\u91cc\u8fd8\u6709\u53c2\u6c64\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6240\u4ee5\u90a3\u6761\u9e21\u817f\u7684\u6ecb\u5473\u4e00\u5b9a\u5f88\u4e0d\u9519\u3002\u201d \u3000\u3000\u6234\u9ad8\u5c97\u5782\u7740\u5934\uff0c\u8138\u4e0a\u7684\u8868\u60c5\uff0c\u5c31\u50cf\u662f\u5ffd\u7136\u88ab\u4eba\u585e\u4e86\u4e00\u5634\u70c2\u6ce5\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\u8fd9\u4f4d\u6234\u603b\u9556\u5934\u662f\u60f3\u5e26\u4f60\u6765\u627e\u6211\u7684\uff1f\u201d \u3000\u3000\u53f6\u5f00\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u53e3\u6c34\u91cc\u7684\u4e00\u70b9\u53c2\u6c64\uff0c\u5c31\u80fd\u8ba9\u4eba\u7761\u7740\uff0c\u90a3\u79cd\u53c2\u6c64\u9664\u4e86\u4f60\u4e4b\u5916\uff0c\u8fd8\u6709\u8c01 \u80fd\u505a\u5f97\u51fa\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4f60\u65e2\u7136\u5df2\u8d70\u4e86\uff0c\u4e3a\u4ec0\u4e48\u8fd8\u8981\u6765\uff1f\u201d \u3000\u3000\u53f6\u5f00\u4e5f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u597d\u50cf\u5df2\u6ca1\u6709\u522b\u7684\u5730\u65b9\u53ef\u53bb\u3002\u201d \u3000\u3000\u8fd9\u662f\u5b9e\u8bdd\u3002 \u7b2c\u5341\u4e03\u7ae0\u3000\u67d4\u60c5\u871c\u610f>> \u53e4\u9f99\u300a\u4e5d\u6708\u9e70\u98de\u300b \u7b2c\u5341\u4e03\u7ae0\u3000\u67d4\u60c5\u871c\u610f \u3000\u3000\u4ed6\u81ea\u5df1\u77e5\u9053\u81ea\u5df1\u7684\u4f24\u52bf\uff0c\u82e5\u662f\u7559\u5728\u957f\u5b89\u57ce\uff0c\u5f88\u53ef\u80fd\u8bdd\u4e0d\u8fc7\u4eca\u5929\u3002 \u3000\u3000\u2014\u2014\u4ed6\u6b63\u50cf\u662f\u6761\u88ab\u730e\u4eba\u4eec\u8ffd\u9010\u7684\u72d0\u72f8\uff0c\u957f\u5b89\u57ce\u91cc\u5374\u5df2\u6709\u7fa4\u9e70\u98de\u8d77\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u5ae3\u7136\u9053\uff1a\u201c\u4f60\u603b\u7b97\u8fd8\u6709\u70b9\u826f\u5fc3\uff0c\u603b\u7b97\u8fd8\u77e5\u9053\u53ea\u6709\u6211\u662f\u771f\u6b63\u5bf9\u4f60\u597d\u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6240\u4ee5\u6211\u6839\u672c\u5c31\u6ca1\u6709\u8d70\uff0c\u6211\u4e00\u76f4\u90fd\u7559\u5728\u8f66\u91cc\u3002\u201d \u3000\u3000\u6234\u9ad8\u5c97\u9053\uff1a\u201c\u4f60\u6ca1\u8d70\uff1f\u201d \u3000\u3000\u53f6\u5f00\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u90a3\u8f66\u5b50\u5f88\u8212\u670d\uff0c\u5ea7\u4f4d\u4e5f\u5f88\u5bbd\u5927\uff0c\u4f4d\u5b50\u4e0b\u53c8\u662f\u7a7a\u7684\uff0c\u50cf\u6211\u8fd9\u79cd\u4e0d\u592a\u80d6 \u7684\u4eba\uff0c\u6b63\u597d\u53ef\u4ee5\u8212\u8212\u670d\u670d\u5730\u8eba\u5728\u91cc\u9762\u3002\u201d \u3000\u3000\u6234\u9ad8\u5c97\u54ac\u7740\u7259\u9053\uff1a\u201c\u6211\u53ea\u6709\u4e00\u4ef6\u4e8b\u8fd8\u4e0d\u660e\u767d\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u6234\u9ad8\u5c97\u6068\u6068\u9053\uff1a\u201c\u4f60\u65e2\u7136\u662f\u51c6\u5907\u8981\u6765\u7684\uff0c\u4e3a\u4ec0\u4e48\u8981\u800d\u8fd9\u4e00\u624b\u82b1\u6837\uff1f\u201d \u3000\u3000\u53f6\u5f00\u6de1\u6de1\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u4e0d\u613f\u522b\u4eba\u5c06\u6211\u770b\u6210\u4e2a\u7b28\u86cb\uff0c\u6211\u65e0\u8bba\u5230\u4ec0\u4e48\u5730\u65b9\u53bb\uff0c\u90fd\u5f97\u5148\u5f04\u6e05\u695a \u53bb\u7684\u7a76\u7adf\u662f\u4ec0\u4e48\u5730\u65b9\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u53c8\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u73b0\u5728\u4f60\u603b\u7b97\u5df2\u77e5\u9053\u8fd9\u91cc\u662f\u4ec0\u4e48\u5730\u65b9\u4e86\u3002\u201d \u3000\u3000\u53f6\u5f00\u7b11\u9053\uff1a\u201c\u6211\u8bf4\u8fc7\uff0c\u8fd9\u5b9e\u5728\u662f\u4e2a\u597d\u5730\u65b9\uff0c\u8fde\u6211\u90fd\u60f3\u4e0d\u5230\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u53f9\u606f\u7740\u9053\uff1a\u201c\u5e78\u597d\u73b0\u5728\u6211\u4e5f\u660e\u767d\u4e86\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u7528\u773c\u89d2\u779f\u7740\u6234\u9ad8\u5c97\uff0c\u9053\uff1a\u201c\u6211\u603b\u7b97\u5df2\u77e5\u9053\u771f\u6b63\u7684\u7b28\u86cb\u662f\u8c01\u4e86\u3002\u201d \u3000\u3000\u6234\u9ad8\u5c97\u9053\uff1a\u201c\u6211\u2026\u2026\u201d \u3000\u3000\u4ed6\u53ea\u8bf4\u51fa\u8fd9\u4e00\u4e2a\u5b57\u3002 \u3000\u3000\u8fd9\u4e2a\u5b57\u662f\u5f00\u53e3\u97f3\uff0c\u4ed6\u7684\u5634\u521a\u5f20\u5f00\uff0c\u7a81\u7136\u53d1\u73b0\u94f6\u5149\u4e00\u95ea\uff0c\u5df2\u5c04\u5165\u4ed6\u7684\u5634\u91cc\u3002 \u3000\u3000\u4ed6\u53ea\u89c9\u5f97\u5634\u91cc\u751c\u751c\u7684\uff0c\u51c9\u51c9\u7684\uff0c\u5c31\u597d\u50cf\u5403\u4e86\u5757\u51b0\u7cd6\u4e00\u6837\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u5fae\u7b11\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u559c\u6b22\u5403\uff0c\u5929\u4e0b\u6740\u4eba\u7684\u6697\u5668\uff0c\u7edd\u6ca1\u6709\u4e00\u6837\u6bd4\u6211\u8fd9\u51b0\u7cd6\u94f6\u4e1d\u66f4 \u751c\u3001\u66f4\u597d\u5403\u7684\u4e86\uff0c\u4f60\u8bf4\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u6234\u9ad8\u5c97\u6ca1\u6709\u56de\u7b54\u3002\u3001\u4ed6\u7684\u8138\u8272\u7a81\u7136\u53d8\u6210\u6b7b\u9ed1\u8272\uff0c\u54bd\u5589\u5df2\u7a81\u7136\u88ab\u585e\u6ce8\uff0c\u5c31\u597d\u50cf\u6709\u53cc\u770b\u4e0d\u89c1 \u7684\u624b\uff0c\u7a81\u7136\u627c\u4f4f\u4e86\u4ed6\u7684\u54bd\u5589\u3002 \u3000\u3000\u4ed6\u7684\u547c\u5438\u7a81\u7136\u505c\u987f\u3002 \u3000\u3000\u4ed6\u6b7b\u7684\u65f6\u5019\uff0c\u5634\u91cc\u8fd8\u662f\u751c\u7684\u3002 \u3000\u3000\u8fd9\u51b0\u7cd6\u94f6\u4e1d\u771f\u751c\uff0c\u7b80\u76f4\u751c\u5f97\u8981\u547d\uff0c\u751c\u5f97\u6b7b\u4eba\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u8fd9\u4eba\u5c82\u975e\u4e5f\u751c\u5f97\u5f88\uff1f \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u7b11\u5f97\u8fd8\u662f\u90a3\u4e48\u751c\uff0c\u6bd4\u51b0\u7cd6\u8fd8\u751c\u3002 \u3000\u3000\u53f6\u5f00\u5374\u6ca1\u6709\u7b11\uff0c\u4e5f\u7b11\u4e0d\u51fa\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4f60\u4e0d\u9ad8\u5174\uff1f\u201d \u3000\u3000\u53f6\u5f00\u95ed\u7740\u5634\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4ed6\u6551\u8fc7\u4f60\uff0c\u4f60\u4e5f\u6551\u8fc7\u4ed6\uff0c\u4f60\u4eec\u7684\u5e10\u5c82\u975e\u5df2\u7ed3\u6e05\uff1f\u6211\u6740\u4e86\u4ed6\uff0c\u8ddf\u4f60\u4e5f\u6ca1\u6709 \u5173\u7cfb\u3002\u201d \u3000\u3000\u53f6\u5f00\u5fcd\u4e0d\u4f4f\u9053\uff1a\u201c\u4f60\u81f3\u5c11\u4e0d\u5fc5\u5728\u6211\u9762\u524d\u6740\u4ed6\u7684\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6211\u4e00\u5b9a\u8981\u5728\u4f60\u9762\u524d\u6740\u4ed6\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u8981\u4f60\u660e\u767d\u4e24\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u53f6\u5f00\u5728\u542c\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4f60\u82e5\u60f3\u8981\u4e00\u4e2a\u7b28\u86cb\u53d8\u5f97\u4e0d\u6bd4\u522b\u4eba\u7b28\uff0c\u53ea\u6709\u4e00\u4e2a\u6cd5\u5b50\u3002\u201d \u3000\u3000\u5979\u5fae\u7b11\u7740\uff0c\u770b\u7740\u5730\u4e0a\u7684\u6234\u9ad8\u5c97\uff1a\u201c\u73b0\u5728\u4ed6\u5c82\u4e0d\u662f\u5df2\u4e0d\u6bd4\u522b\u4eba\u7b28\u4e86\uff1f\u201d \u3000\u3000\u6b7b\u4eba\u5c31\u662f\u6b7b\u4eba\uff0c\u6b7b\u4eba\u90fd\u662f\u4e00\u6837\u7684\uff0c\u65e2\u6ca1\u6709\u7279\u522b\u806a\u660e\u7684\u6b7b\u4eba\uff0c\u4e5f\u6ca1\u6709\u7279\u522b\u7b28\u7684\u6b7b\u4eba\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u6162\u6162\u5730\u63a5\u7740\u9053\uff1a\u201c\u6211\u8fd8\u8981\u4f60\u660e\u767d\uff0c\u6211\u82e5\u8981\u6740\u4e00\u4e2a\u4eba\uff0c\u4ed6\u5c31\u5df2\u6b7b\u5b9a\u4e86\uff0c\u4e16\u4e0a\u7edd\u6ca1 \u6709\u4efb\u4f55\u4eba\u80fd\u6551\u5f97\u4e86\u4ed6\uff0c\u8fde\u4f60\u4e5f\u4e0d\u80fd\u3002\u201d \u3000\u3000\u53f6\u5f00\u53c8\u95ed\u4e0a\u5634\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u770b\u7740\u4ed6\uff0c\u5ffd\u53c8\u5ae3\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u73b0\u5728\u8fd8\u6d3b\u7740\uff0c\u53ea\u56e0\u4e3a\u6211\u6839\u672c\u5c31\u4e0d\u60f3\u6740\u4f60\uff0c\u4e5f \u4e0d\u4f1a\u62ff\u51b0\u7cd6\u94f6\u4e1d\u7ed9\u4f60\u5403\u7684\uff0c\u4f60\u53c8\u4f55\u5fc5\u95ed\u7740\u5634\uff1f\u201d \u3000\u3000\u8fd9\u5012\u4e0d\u662f\u5047\u8bdd\u3002\u5979\u82e5\u771f\u7684\u60f3\u6740\u53f6\u5f00\uff0c\u673a\u4f1a\u5b9e\u5728\u591a\u5f97\u5f88\u3002 \u3000\u3000\u53f6\u5f00\u5374\u5728\u51b7\u7b11\uff0c\u4ed6\u663e\u7136\u5e76\u4e0d\u9886\u60c5\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u5fae\u7b11\u7740\uff0c\u53c8\u9053\uff1a\u201c\u5176\u5b9e\u4f60\u6709\u65f6\u4e5f\u7b28\u5f97\u5f88\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u7528\u4f60\u7684\u5200\u53bb\u5bf9\u4ed8\u5415\u8fea\uff1f\u201d \u3000\u3000\u53f6\u5f00\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u624d\u7f13\u7f13\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u60f3\u8bc1\u660e\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u60f3\u77e5\u9053\u97e9\u8d1e\u7a76\u7adf\u662f\u4e0d\u662f\u6b7b\u5728\u4ed6\u5251\u4e0b\u7684\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u53f9\u9053\uff1a\u201c\u4f60\u82e5\u4e5f\u6b7b\u5728\u4ed6\u624b\u4e0b\uff0c\u5c31\u7b97\u77e5\u9053\u4e86\uff0c\u53c8\u6709\u4ec0\u4e48\u7528\uff1f\u201d \u3000\u3000\u53f6\u5f00\u4e5f\u5fcd\u4e0d\u4f4f\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u6211\u672c\u6765\u7684\u786e\u4f4e\u4f30\u4e86\u4ed6\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4ed6\u7684\u6b66\u529f\u6bd4\u4f60\u60f3\u8c61\u4e2d\u8fd8\u9ad8\uff1f\u201d \u3000\u3000\u53f6\u5f00\u70b9\u70b9\u5934\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u73b0\u5728\u4f60\u5df2\u77e5\u9053\u97e9\u8d1e\u4e0d\u662f\u6b7b\u5728\u4ed6\u5251\u4e0b\u7684\uff1f\u201d \u3000\u3000\u53f6\u5f00\u53c8\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4ed6\u82e5\u771f\u7684\u6740\u4e86\u97e9\u8d1e\uff0c\u5c31\u4e00\u5b9a\u4e5f\u4f1a\u6740\u6211\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4ed6\u82e5\u771f\u6740\u4f60\u65f6\uff0c\u4f60\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u53f6\u5f00\u6de1\u6de1\u9053\uff1a\u201c\u4f60\u81ea\u5df1\u8bf4\u8fc7\u7684\uff0c\u6211\u8eab\u4e0a\u5e26\u7684\u4e0d\u6b62\u4e00\u628a\u5200\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u5ae3\u7136\u9053\uff1a\u201c\u6240\u4ee5\u6211\u4e5f\u8bf4\u8fc7\uff0c\u5e78\u597d\u4ed6\u5e76\u6ca1\u6709\u771f\u7684\u60f3\u6740\u4f60\u3002\u201d \u3000\u3000\u53f6\u5f00\u51b7\u51b7\u9053\uff1a\u201c\u5bf9\u4f60\u8bf4\u6765\uff0c\u8fd9\u5e76\u4e0d\u597d\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6709\u4ec0\u4e48\u4e0d\u597d\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u97e9\u8d1e\u65e2\u4e0d\u662f\u4ed6\u6740\u7684\uff0c\u5c31\u4e00\u5b9a\u662f\u4f60\u6740\u7684\uff0c\u4f60\u6740\u4e86\u97e9\u8d1e\uff0c\u518d\u5ac1\u7978\u7ed9\u4ed6\uff0c\u4e3a\u7684\u5c31\u662f \u60f3\u8981\u6211\u53bb\u8ddf\u4ed6\u62fc\u547d\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u51dd\u89c6\u7740\u4ed6\uff0c\u7f8e\u4e3d\u7684\u773c\u775b\u91cc\uff0c\u5e26\u7740\u79cd\u8c01\u4e5f\u8bf4\u4e0d\u51fa\u662f\u4ec0\u4e48\u8868\u60c5\u7684\u8868\u60c5\uff0c\u8fc7\u4e86\u5f88\u4e45\u624d \u6162\u6162\u4ed6\u8bf4\u9053\uff1a\u201c\u4f60\u771f\u7684\u8ba4\u4e3a\u4e00\u5b9a\u662f\u6211\u6740\u4e86\u97e9\u8d1e\uff1f\u201d \u3000\u3000\u53f6\u5f00\u4e5f\u5728\u76ef\u7740\u5979\uff0c\u9053\uff1a\u201c\u9664\u4e86\u4f60\uff0c\u6211\u60f3\u4e0d\u51fa\u7b2c\u4e8c\u4eba\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4f60\u4e0d\u4fe1\uff1f\u201d\u5979\u8f7b\u8f7b\u53f9\u606f\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u6211\u5c31\u77e5\u9053\u4f60\u4e0d\u4f1a\u76f8\u4fe1\u7684\uff0c\u73b0\u5728\u65e0\u8bba \u6211\u8bf4\u4ec0\u4e48\uff0c\u4f60\u90fd\u4e0d\u4f1a\u76f8\u4fe1\u3002\u201d \u3000\u3000\u53f6\u5f00\u627f\u8ba4\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u53ef\u662f\u5047\u5982\u6211\u80fd\u8bc1\u660e\u6211\u6ca1\u6709\u6740\u4ed6\uff0c\u4f60\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u80fd\u8bc1\u660e\uff1f\u600e\u4e48\u8bc1\u660e\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6211\u5f53\u7136\u6709\u6cd5\u5b50\u3002\u201d \u3000\u3000\u53f6\u5f00\u51b7\u7b11\u9053\uff1a\u201c\u6211\u5c31\u77e5\u9053\u4f60\u6709\u6cd5\u5b50\uff0c\u4f60\u751a\u81f3\u6709\u6cd5\u5b50\u53ef\u4ee5\u8bc1\u660e\u97e9\u8d1e\u662f\u6211\u6740\u4e86\u7684\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6211\u6709\u8bc1\u636e\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u4e5f\u77e5\u9053\u4f60\u6709\u8bc1\u636e\uff0c\u4f60\u968f\u65f6\u90fd\u53ef\u4ee5\u5236\u9020\u51fa\u51e0\u767e\u4e2a\u8bc1\u636e\u6765\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6211\u53ea\u6709\u4e00\u4e2a\u8bc1\u636e\uff0c\u6211\u62ff\u51fa\u8fd9\u4e2a\u8bc1\u636e\u6765\uff0c\u4f60\u82e5\u8fd8\u662f\u4e0d\u76f8\u4fe1\u6211\uff0c\u6211\u5c31\u60c5\u613f\u8ba9 \u4f60\u6740\u4e86\u6211\uff0c\u66ff\u97e9\u8d1e\u590d\u4ec7\u3002\u201d \u3000\u3000\u5979\u8bf4\u5f97\u592a\u80af\u5b9a\uff0c\u5927\u6709\u628a\u63e1\u3002 \u3000\u3000\u53f6\u5f00\u51e0\u4e4e\u5df2\u88ab\u5979\u6253\u52a8\u4e86\uff0c\u4f46\u7acb\u523b\u53c8\u8b66\u544a\u81ea\u5df1\uff0c\u7edd\u4e0d\u80fd\u76f8\u4fe1\uff1a\u201c\u65e0\u8bba\u4f60\u62ff\u51fa\u4ec0\u4e48\u8bc1\u636e\u6765\uff0c \u6211\u90fd\u7edd\u4e0d\u4f1a\u76f8\u4fe1\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4f60\u82e5\u4e07\u4e00\u76f8\u4fe1\u4e86\u5462\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u82e5\u771f\u7684\u80fd\u4f7f\u6211\u76f8\u4fe1\u4f60\u6ca1\u6709\u6740\u97e9\u8d1e\uff0c\u6211\u5c31\u2026\u2026\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4f60\u5c31\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u53f9\u606f\u7740\uff0c\u9053\uff1a\u201c\u4f60\u77e5\u9053\u6211\u7edd\u4e0d\u4f1a\u5bf9\u4f60\u600e\u4e48\u6837\u7684\uff0c\u6211\u65e2\u4e0d\u60f3\u6740\u4f60\uff0c\u4e5f\u4e0d\u60f3\u4f24\u4f60\u7684 \u5fc3\uff0c\u6211\u53ea\u4e0d\u8fc7\u8981\u4f60\u7b54\u5e94\u6211\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4e00\u4ef6\u65e2\u4e0d\u4f1a\u4f24\u5bb3\u5230\u522b\u4eba\uff0c\u4e5f\u4e0d\u4f1a\u4f24\u5bb3\u5230\u4f60\u81ea\u5df1\u7684\u4e8b\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u597d\uff0c\u6211\u7b54\u5e94\u3002\u201d\u4ed6\u7edd\u4e0d\u76f8\u4fe1\u4e0a\u5b98\u5c0f\u4ed9\u80fd\u62ff\u5f97\u51fa\u90a3\u79cd\u8bc1\u636e\u6765\uff0c\u4e16\u4e0a\u51e0\u4e4e\u5df2\u6ca1\u6709 \u4efb\u4f55\u4e00\u4ef6\u4e8b\u3001\u6ca1\u6709\u4efb\u4f55\u4e00\u4e2a\u4eba\u80fd\u8ba9\u4ed6\u76f8\u4fe1\u4e0a\u5b98\u5c0f\u4ed9\u7684\u8bdd\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u60f3\u9519\u4e86\u3002\u8fd9\u4e16\u4e0a\u8fd8\u6709\u4e00\u4e2a\u4eba\uff0c\u80fd\u8bc1\u660e\u4e0a\u5b98\u5c0f\u4ed9\u5e76\u6ca1\u6709\u6740\u8fc7\u97e9\u8d1e\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u662f\u8c01\u5462\uff1f \u3000\u3000\u8fd9\u4e2a\u4eba\u5c31\u662f\u97e9\u8d1e\u81ea\u5df1\u3002 \u3000\u3000\u97e9\u8d1e\u5e76\u6ca1\u6709\u6b7b\uff0c\u4ed6\u5c45\u7136\u53c8\u6d3b\u751f\u751f\u5730\u51fa\u73b0\u5728\u53f6\u5f00\u773c\u524d\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u62db\u4e86\u62db\u624b\uff0c\u4ed6\u5c31\u4ece\u540e\u9762\u8d70\u4e86\u51fa\u6765\uff0c\u624b\u91cc\u8fd8\u6367\u7740\u4e00\u575b\u9152\uff0c\u5fae\u7b11\u7740\u8d70\u5230\u53f6\u5f00\u9762\u524d\uff0c \u9053\uff1a\u201c\u9152\u6211\u603b\u7b97\u5df2\u66ff\u4f60\u627e\u5230\u4e86\uff0c\u82e5\u662f\u8fd8\u4e0d\u591f\uff0c\u6211\u8fd8\u53ef\u4ee5\u66ff\u4f60\u53bb\u62ff\u3002\u201d\u53f6\u5f00\u6014\u4f4f\u3002 \u3000\u3000\u8fd9\u6b21\u4ed6\u7684\u786e\u662f\u771f\u7684\u6014\u4f4f\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\u662f\u4e0d\u662f\u97e9\u8d1e\uff1f\u201d \u3000\u3000\u5f53\u7136\u662f\u3002 \u3000\u3000\u53f6\u5f00\u770b\u5f97\u51fa\u8fd9\u4e2a\u4eba\u7684\u9f3b\u5b50\u4e0a\uff0c\u8fd8\u7559\u7740\u88ab\u4ed6\u4e00\u62f3\u6253\u8fc7\u7684\u4f24\u75d5\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4ed6\u662f\u4e0d\u662f\u8fd8\u6d3b\u7740\uff1f\u201d \u3000\u3000\u4ed6\u5f53\u7136\u8fd8\u6d3b\u7740\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u97e9\u8d1e\u65e2\u7136\u8fd8\u6d3b\u7740\uff0c\u6211\u5c31\u6ca1\u6709\u6740\u97e9\u8d1e\u3002\u201d \u3000\u3000\u8fd9\u9053\u7406\u4e5f\u6b63\u5982\u4e00\u52a0\u4e00\u7b49\u4e8e\u4e8c\u540c\u6837\u7b80\u5355\uff0c\u540c\u6837\u6b63\u786e\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u8f7b\u8f7b\u5410\u51fa\u53e3\u6c14\uff0c\u60a0\u7136\u7b11\u9053\uff1a\u201c\u73b0\u5728\u4f60\u603b\u8be5\u76f8\u4fe1\u6211\u6ca1\u6709\u6740\u4e86\u4ed6\u5427\uff1f\u201d \u3000\u3000\u53f6\u5f00\u6ca1\u6709\u8bf4\u8bdd\u3002 \u3000\u3000\u4ed6\u73b0\u5728\u5f53\u7136\u5df2\u660e\u767d\uff0c\u6b7b\u7684\u90a3\u4e2a\u4eba\uff0c\u5e76\u4e0d\u662f\u97e9\u8d1e\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4f60\u8ba4\u5f97\u97e9\u8d1e\uff0c\u6211\u82e5\u5c06\u4e00\u4e2a\u4eba\u6613\u5bb9\u6539\u626e\u6210\u4ed6\u7684\u6837\u5b50\u3002\u7edd\u5bf9\u7792\u4e0d\u8fc7\u4f60\u7684\u3002\u201d \u3000\u3000\u4e16\u4e0a\u5e76\u6ca1\u6709\u90a3\u4e48\u7cbe\u5999\u7684\u6613\u5bb9\u672f\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u82e5\u771f\u7684\u80fd\u6539\u626e\u6210\u53e6\u5916\u4e00\u4e2a\u4eba\uff0c\u8fde\u4ed6\u81ea\u5df1\u7684\u4eb2\u4eba\u670b\u53cb\u90fd\u7792\u8fc7\uff0c\u90a3\u5c31\u6ca1\u6709\u6613\u5bb9\u672f\u4e86\u3002 \u3000\u3000\u90a3\u5c31\u5df2\u7ecf\u662f\u795e\u8bdd\u3001\u5947\u8ff9\uff0c\u800c\u4e14\u662f\u5f88\u8352\u8c2c\u7684\u795e\u8bdd\uff0c\u7edd\u4e0d\u53ef\u80fd\u53d1\u751f\u7684\u5bc4\u8ff9\u3002 \u3000\u3000\uff12\u5ddd \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4f46\u662f\u90a3\u5929\u665a\u4e0a\u4f60\u89c1\u5230\u90a3\u4e2a\u2018\u97e9\u8d1e\u2019\u65f6\uff0c\u4ed6\u7684\u8138\u5df2\u88ab\u6253\u6bc1\u4e86\uff0c\u6240\u4ee5\u624d\u7792\u8fc7 \u4e86\u4f60\u3002\u201d \u3000\u3000\u53f6\u5f00\u53ea\u6709\u82e6\u7b11\uff0c\u82e6\u7b11\u7740\u9053\uff1a\u201c\u770b\u6765\u91d1\u94b1\u5e2e\u7684\u4eba\u624d\uff0c\u679c\u7136\u4e0d\u5c11\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u7684\u786e\u4e0d\u5c11\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u5148\u5c06\u4e00\u4e2a\u4eba\u6613\u5bb9\u6539\u626e\u6210\u97e9\u8d1e\uff0c\u518d\u6253\u6bc1\u4ed6\u7684\u8138\uff0c\u53eb\u4ed6\u6765\u9a97\u6211\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u662f\u97e9\u8d1e\u81ea\u5df1\u52a8\u624b\u6253\u7684\uff0c\u4ed6\u7684\u62f3\u5934\u4e5f\u5f88\u786c\uff0c\u81f3\u5c11\u6bd4\u6211\u786c\u3002\u201d \u3000\u3000\u53f6\u5f00\u53f9\u9053\uff1a\u201c\u4f46\u6211\u5374\u8fd8\u662f\u60f3\u4e0d\u901a\uff0c\u600e\u4e48\u4f1a\u6709\u4eba\u80af\u66ff\u4f60\u505a\u8fd9\u79cd\u4e8b\uff0c\u6328\u4e86\u4e00\u987f\u6bd2\u6253\u540e\uff0c\u8fd8\u66ff \u4f60\u53bb\u9a97\u4eba\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4f60\u521a\u624d\u4ece\u8f66\u53a2\u91cc\u51fa\u6765\u65f6\uff0c\u770b\u89c1\u5916\u9762\u90a3\u4e9b\u4eba\u6ca1\u6709\uff1f\u201d \u3000\u3000\u53f6\u5f00\u70b9\u70b9\u5934\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u70b9\u4e86\u5934\uff0c\u9053\uff1a\u201c\u53ea\u8981\u6211\u968f\u4fbf\u5429\u5490\u4e00\u58f0\uff0c\u65e0\u8bba\u4ec0\u4e48\u4e8b\uff0c\u4ed6\u4eec\u90fd\u80af\u53bb\u4e3a\u6211\u505a\u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u7b49\u4ed6\u4eec\u7684\u4e8b\u505a\u5b8c\u4e86\u4e4b\u540e\uff0c\u4f60\u8fd8\u662f\u4e00\u6837\u8981\u6740\u4e86\u4ed6\u4eec\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u6de1\u6de1\u9053\uff1a\u201c\u6211\u672c\u5c31\u662f\u4e2a\u5fc3\u72e0\u624b\u8fa3\u7684\u5973\u4eba\uff0c\u90a3\u4e9b\u4eba\u7684\u6027\u547d\uff0c\u5728\u6211\u770b\u6765\uff0c\u6839\u672c\u5c31\u4e00 \u6587\u4e0d\u503c\u3002\u201d \u3000\u3000\u5979\u51dd\u89c6\u7740\u53f6\u5f00\uff0c\u7075\u6d3b\u7684\u773c\u775b\u53c8\u9732\u51fa\u79cd\u5947\u602a\u7684\u8868\u60c5\uff0c\u8f7b\u8f7b\u5730\u63a5\u7740\u9053\uff1a\u201c\u53ef\u662f\u6211\u5bf9\u4f60\u2026\u6211\u5bf9 \u4f60\u600e\u4e48\u6837\uff0c\u4f60\u81ea\u5df1\u5fc3\u91cc\u4e5f\u8be5\u77e5\u9053\u3002\u201d \u3000\u3000\u4e3a\u4e86\u8981\u8ba9\u53f6\u5f00\u76f8\u4fe1\u97e9\u8d1e\u662f\u6b7b\u5728\u5415\u8fea\u5251\u4e0b\u7684\uff0c\u5979\u4e0d\u60dc\u6740\u4eba\u3002 \u3000\u3000\u73b0\u5728\u4e3a\u4e86\u8981\u8ba9\u53f6\u5f00\u76f8\u4fe1\u5979\u6ca1\u6709\u6740\u97e9\u8d1e\uff0c\u5979\u53c8\u4e0d\u60dc\u8ba9\u97e9\u8d1e\u518d\u6d3b\u7740\u51fa\u73b0\u3002 \u3000\u3000\u4e3a\u4e86\u8981\u8ba9\u53f6\u5f00\u76f8\u4fe1\u97e9\u8d1e\u662f\u670b\u53cb\uff0c\u5979\u5df2\u4e0d\u77e5\u8d39\u4e86\u591a\u5c11\u5fc3\u8840\uff0c\u53ef\u662f\u73b0\u5728\u5979\u7684\u4e00\u5207\u5fc3\u8840\uff0c\u663e\u7136 \u5df2\u767d\u8d39\u4e86\u3002 \u3000\u3000\u73b0\u5728\u53f6\u5f00\u5f53\u7136\u5df2\u77e5\u9053\uff0c\u97e9\u8d1e\u4e5f\u662f\u91d1\u94b1\u5e2e\u4e2d\u7684\u4eba\uff0c\u4ed6\u4eec\u505a\u7684\u4e00\u5207\uff0c\u53ea\u4e0d\u8fc7\u8981\u53f6\u5f00\u7b54\u5e94\u5979\u4e00 \u4ef6\u4e8b\u3002\u8fd9\u4ef6\u4e8b\u7a76\u7adf\u662f\u4ec0\u4e48\u6837\u7684\u4e8b\uff0c\u53f6\u5f00\u8fde\u60f3\u90fd\u4e0d\u6562\u60f3\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u65e0\u8bba\u4ec0\u4e48\u7a00\u5947\u53e4\u602a\u7684\u4e8b\uff0c\u4e0a\u5b98\u5c0f\u4ed9\u90fd\u80fd\u60f3\u5f97\u51fa\u6765\u7684\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u8fd8\u5728\u51dd\u89c6\u7740\u4ed6\uff0c\u6162\u6162\u9053\uff1a\u201c\u6211\u53ea\u8981\u4f60\u7b54\u5e94\u6211\uff0c\u7559\u5728\u8fd9\u91cc\uff0c\u7b49\u4f60\u7684\u4f24\u53e3\u7ed9\u4e86\u75a4\u4e4b \u540e\u518d\u8d70\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u5c31\u662f\u8fd9\u4ef6\u4e8b\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u5c31\u662f\u8fd9\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u53f6\u5f00\u53c8\u6014\u4f4f\u3002 \u3000\u3000\u5979\u81ea\u5df1\u4e5f\u627f\u8ba4\u81ea\u5df1\u662f\u4e2a\u5fc3\u72e0\u624b\u8fa3\u7684\u5973\u4eba\uff0c\u522b\u4eba\u7684\u6027\u547d\uff0c\u5728\u5979\u773c\u4e2d\u770b\u6765\uff0c\u6839\u672c\u4e00\u6587\u4e0d\u503c\u3002 \u3000\u3000\u5979\u82b1\u4e86\u90a3\u4e48\u591a\u7684\u5fc3\u8840\uff0c\u727a\u7272\u4e86\u90a3\u4e48\u591a\u4ee3\u4ef7\uff0c\u4e3a\u7684\u53ea\u4e0d\u8fc7\u8981\u53f6\u5f00\u7b54\u5e94\u5979\u8fd9\u4e48\u6837\u4e00\u4ef6\u4e8b\u3002 \u3000\u3000\u8fd9\u4ef6\u4e8b\u975e\u4f46\u6ca1\u6709\u4f24\u5bb3\u5230\u4efb\u4f55\u4eba\uff0c\u5bf9\u53f6\u5f00\u4e5f\u53ea\u6709\u597d\u5904\u3002 \u3000\u3000\u5979\u7b97\u6765\u7b97\u53bb\uff0c\u4e3a\u7684\u7adf\u4e0d\u662f\u81ea\u5df1\uff0c\u800c\u662f\u53f6\u5f00\u3002 \u3000\u3000\u53f6\u5f00\u770b\u7740\u5979\uff0c\u5fc3\u91cc\u5ffd\u7136\u6d8c\u8d77\u4e00\u79cd\u4ed6\u81ea\u5df1\u4e5f\u65e0\u6cd5\u4e86\u89e3\u7684\u611f\u60c5\u3002 \u3000\u3000\u2014\u2014\u6211\u5bf9\u522b\u4eba\u867d\u7136\u5fc3\u72e0\u624b\u8fa3\uff0c\u53ef\u662f\u6211\u5bf9\u4f60\u600e\u4e48\u6837\uff0c\u4f60\u81ea\u5df1\u5fc3\u91cc\u4e5f\u5f88\u660e\u767d\u3002 \u3000\u3000\u53f6\u5f00\u4e00\u76f4\u4e0d\u660e\u767d\uff0c\u5c31\u7b97\u660e\u767d\u4e5f\u4e00\u76f4\u4e0d\u80fd\u76f8\u4fe1\uff0c\u4e0d\u613f\u76f8\u4fe1\u3002 \u3000\u3000\u53ef\u662f\u73b0\u5728\u4ed6\u5df2\u4e0d\u80fd\u4e0d\u76f8\u4fe1\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u672c\u53ef\u4e58\u6b64\u673a\u4f1a\uff0c\u7528\u5404\u79cd\u7a00\u5947\u53e4\u602a\u7684\u6cd5\u5b50\u6765\u6298\u78e8\u4ed6\u7684\u3002 \u3000\u3000\u5979\u770b\u7740\u53f6\u5f00\u65f6\uff0c\u773c\u775b\u91cc\u9732\u51fa\u7684\u90a3\u79cd\u60c5\u611f\uff0c\u96be\u9053\u662f\u771f\u7684\uff1f \u3000\u3000\u90a3\u81f3\u5c11\u6709\u51e0\u5206\u662f\u771f\u7684\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u60a0\u60a0\u5730\u53c8\u8bf4\uff1a\u201c\u6211\u672c\u6765\u6709\u5f88\u591a\u6cd5\u5b50\u53ef\u4ee5\u628a\u4f60\u7559\u5728\u8fd9\u91cc\u7684\uff0c\u4f46\u662f\u6211\u4e0d\u613f\u52c9\u5f3a\u4f60\uff0c \u6240\u4ee5\u6211\u624d\u8981\u4f60\u81ea\u5df1\u7b54\u5e94\u3002\u201d \u3000\u3000\u53f6\u5f00\u7ec8\u4e8e\u957f\u957f\u5730\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6211\u672c\u6765\u5c31\u5df2\u7b54\u5e94\u3002\u201d \u3000\u3000\u540e\u9662\u91cc\u6709\u4e2a\u5c0f\u5c0f\u7684\u53a8\u623f\uff0c\u53a8\u623f\u91cc\u4f20\u6765\u4e86\u9635\u9635\u7ca5\u9999\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u6b63\u5728\u53a8\u623f\u91cc\u66ff\u4ed6\u716e\u7ca5\uff0c\u662f\u7528\u4eba\u53c2\u7096\u7684\u9e21\u7ca5\u3002\u201c\u6211\u672c\u6765\u60f3\u5728\u7ca5\u91cc\u52a0\u70b9\u4eba\u53c2\u7684\uff0c\u53ef \u662f\u6211\u2026\u2026\u201d \u3000\u3000\u53f6\u5f00\u5ffd\u7136\u60f3\u8d77\u4e86\u5d14\u7389\u771f\uff0c\u60f3\u8d77\u5d14\u7389\u771f\u4e3a\u4ed6\u7096\u7684\u7ca5\u3002 \u3000\u3000\u5979\u7684\u786e\u662f\u4e2a\u5584\u826f\u800c\u53ef\u7231\u7684\u5973\u5b69\u5b50\uff0c\u5979\u7684\u8eab\u4e16\u5374\u53c8\u504f\u504f\u90a3\u4e48\u60b2\u60e8\uff0c\u906d\u9047\u504f\u504f\u53c8\u90a3\u4e48\u4e0d\u5e78\u3002 \u3000\u3000\u73b0\u5728\u5979\u66f4\u5df2\u4e0d\u77e5\u9053\u906d\u9047\u5230\u4ec0\u4e48\u4e8b\u3002 \u3000\u3000\uff4f\uff2a\uff19 \u3000\u3000\u8fd8\u6709\u4e01\u7075\u7433\u3002 \u3000\u3000\u73b0\u5728\u5979\u662f\u4e0d\u662f\u5df1\u6062\u590d\u4e86\u795e\u667a\uff1f\u90ed\u5b9a\u662f\u4e0d\u662f\u8fd8\u5728\u7167\u987e\u7740\u5979\uff1f\u4ed6\u73b0\u5728\u5728\u54ea\u91cc\uff1f\u2026\u2026 \u3000\u3000\u5979\u82e5\u77e5\u9053\u81ea\u5df1\u4e00\u5200\u523a\u4f24\u4e86\u53f6\u5f00\uff0c\u5979\u7684\u75db\u82e6\u4e00\u5b9a\u6bd4\u53f6\u5f00\u7684\u5200\u4f24\u66f4\u6df1\u3002 \u3000\u3000\u8fd9\u4e9b\u4e8b\uff0c\u672c\u90fd\u662f\u53f6\u5f00\u4e0d\u613f\u53bb\u60f3\u7684\uff0c\u5374\u53c8\u504f\u504f\u4e0d\u80fd\u4e0d\u53bb\u60f3\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u60f3\u4e86\u53c8\u80fd\u600e\u4e48\u6837\uff1f \u3000\u3000\u4ed6\u5df2\u7b54\u5e94\u4e86\u4e0a\u5b98\u5c0f\u4ed9\uff0c\u4ed6\u7684\u4f24\u52bf\u8fdc\u6bd4\u4ed6\u60f3\u8c61\u4e2d\u66f4\u4e25\u91cd\u3002 \u3000\u3000\u521a\u624d\u4ed6\u4e00\u76f4\u5728\u63d0\u7740\u4e00\u80a1\u52b2\uff0c\u8fd9\u4e00\u8eba\u4e0b\u6765\uff0c\u4ed6\u624d\u77e5\u9053\uff0c\u521a\u624d\u80fd\u652f\u6301\u90a3\u4e48\u4e45\uff0c\u5b9e\u5728\u662f\u5947\u8ff9\u3002 \u3000\u3000\u4ed6\u4e0d\u4f46\u4f24\u53e3\u5728\u75db\uff0c\u5168\u8eab\u7684\u7b4b\u9aa8\u90fd\u5728\u75db\u3002\u53c8\u9178\u53c8\u75db\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u5df2\u6367\u7740\u7897\u7ca5\u8d70\u8fdb\u6765\uff0c\u5ae3\u7136\u9053\uff1a\u201c\u8fd9\u662f\u6211\u81ea\u5df1\u4eb2\u624b\u505a\u7684\uff0c\u4f60\u5c1d\u5c1d\u770b\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u5979\u5c45\u7136\u4e5f\u4f1a\u4e0b\u53a8\u623f\uff1f\u5c45\u7136\u4f1a\u7096\u7ca5\uff1f \u3000\u3000\u201c\u8fc7\u4e24\u5929\u7b49\u4f60\u7a0d\u4e3a\u597d\u4e00\u70b9\u65f6\uff0c\u6211\u518d\u4e0b\u53a8\u623f\u7092\u51e0\u6837\u83dc\u7ed9\u4f60\u5403\uff0c\u6211\u4fdd\u8bc1\u8fde\u9e3f\u5bbe\u697c\u7684\u5927\u5e08\u5085\uff0c \u4e5f\u6ca1\u6709\u6211\u7684\u624b\u827a\u597d\u3002\u201d \u3000\u3000\u7ca5\u7684\u6ecb\u5473\u679c\u7136\u4e0d\u9519\uff0c\u53f6\u5f00\u4e5f\u5b9e\u5728\u997f\u4e86\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u53c8\u7b11\u9053\uff1a\u201c\u8fd9\u7ca5\u91cc\u4e5f\u6709\u8865\u836f\uff0c\u53ef\u4e0d\u662f\u90a3\u79cd\u5403\u4e86\u8981\u4eba\u7761\u89c9\u7684\u8865\u836f\uff0c\u662f\u771f\u6b63\u7684\u8865 \u836f\u3002\u201d \u3000\u3000\u5979\u5df2\u6d17\u5c3d\u4e86\u8102\u7c89\uff0c\u6362\u4e0a\u4e86\u5957\u5f88\u6734\u7d20\u7684\u9752\u5e03\u8863\u88d9\uff0c\u73b0\u5728\u65e0\u8bba\u51c6\u770b\u89c1\u5979\uff0c\u90fd\u7edd\u4e0d\u4f1a\u76f8\u4fe1\u5979\u5c31 \u662f\u91d1\u94b1\u5e2e\u7684\u5e2e\u4e3b\uff0c\u66f4\u4e0d\u4f1a\u76f8\u4fe1\u5979\u662f\u90a3\u79cd\u5fc3\u72e0\u5fc3\u8fa3\u7684\u5973\u4eba\u3002 \u3000\u3000\u73b0\u5728\u5979\u5c31\u50cf\u662f\u53c8\u53d8\u4e86\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u5979\u4ece\u4e00\u4e2a\u767d\u75f4\uff0c\u53d8\u6210\u4e86\u4e00\u4e2a\u6076\u9b54\uff0c\u73b0\u5728\u53c8\u53d8\u5f97\u50cf\u662f\u4e2a\u6e29\u67d4\u7684\u767e\u4f9d\u767e\u987a\u7684\u59bb\u5b50\uff0c\u8282\u4fed\u800c\u80fd \u5e72\u7684\u4e3b\u5987\u3002 \u3000\u3000\u53f6\u5f00\u770b\u7740\u5979\uff0c\u73b0\u5728\u8fde\u4ed6\u90fd\u5206\u4e0d\u6e05\u771f\u6b63\u7684\u5979\uff0c\u7a76\u7adf\u662f\u4e2a\u4ec0\u4e48\u6837\u7684\u5973\u4eba\u3002 \u3000\u3000\u4e5f\u8bb8\u6bcf\u4e2a\u4eba\u90fd\u6709\u4e24\u79cd\u9762\u76ee\u7684\u3002 \u3000\u3000\u6bcf\u4e2a\u4eba\u90fd\u6709\u5584\u826f\u7684\u4e00\u9762\uff0c\u4e5f\u6709\u90aa\u6076\u7684\u4e00\u9762\uff0c\u8fde\u53f6\u5f00\u81ea\u5df1\u90fd\u4e0d\u4f8b\u5916\u3002\u53ea\u4e0d\u8fc7\u4ed6\u603b\u662f\u80fd\u5c06\u90aa \u6076\u7684\u90a3\u4e00\u9762\u63a7\u5236\u5f97\u5f88\u597d\u800c\u5df2\u3002 \u3000\u3000\u4ed6\u662f\u4e0d\u662f\u4e5f\u80fd\u8ba9\u4e0a\u5b98\u5c0f\u4ed9\u5c06\u90aa\u6076\u7684\u90a3\u9762\u9501\u8d77\u6765\u5462\uff1f \u3000\u3000\u4ed6\u6ca1\u6709\u628a\u63e1\uff0c\u4f46\u4ed6\u5374\u5df2\u51b3\u5fc3\u8981\u8bd5\u4e00\u8bd5\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u5582\u5b8c\u4e86\u7ca5\uff0c\u6b63\u5728\u770b\u7740\u53f6\u5f00\u80ef\u9aa8\u4e0a\u7684\u4f24\uff0c\u8f7b\u8f7b\u53f9\u606f\u7740\uff0c\u9053\uff1a\u201c\u4f60\u7684\u4f24\u52bf\u771f\u4e0d\u8f7b\uff0c \u770b\u6765\u5415\u8fea\u90a3\u53ea\u624b\uff0c\u7b80\u76f4\u5c31\u50cf\u662f\u94c1\u6253\u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u82e6\u7b11\u9053\uff1a\u201c\u4e0d\u50cf\u662f\u94c1\u6253\u7684\uff0c\u624b\u4e0a\u7edd\u6ca1\u6709\u90a3\u4e48\u53ef\u6015\u7684\u94c1\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u53f9\u606f\u7740\uff0c\u6162\u6162\u9053\uff1a\u201c\u6211\u672c\u6765\u7684\u786e\u662f\u60f3\u8ba9\u4f60\u53bb\u627e\u5415\u8feb\u66ff\u97e9\u8d1e\u590d\u4ec7\uff0c\u6211\u60f3\u8981\u4f60\u66ff\u6211 \u6740\u4e86\u4ed6\u3002\u201d \u3000\u3000\u53f6\u5f00\u5728\u542c\u7740\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u73b0\u5728\u5c0f\u674e\u63a2\u82b1\u3001\u98de\u5251\u5ba2\u548c\u8346\u65e0\u547d\u867d\u7136\u53ef\u80fd\u8fd8\u6d3b\u7740\uff0c\u4f46\u5374\u5df2\u7edd\u4e0d\u4f1a\u518d\u8fc7\u95ee \u6c5f\u6e56\u4e2d\u7684\u4e8b\u4e86\u3002\u201d \u3000\u3000\u8fd9\u4e09\u4e2a\u4eba\u5df2\u4e0d\u7b97\u662f\u771f\u6b63\u6d3b\u5728\u7ea2\u5c18\u4e2d\u7684\u4eba\uff0c\u4ed6\u4eec\u7684\u884c\u8e2a\u5df2\u8fdb\u5165\u4e86\u795e\u8bdd\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u9664\u4e86\u4ed6\u4eec\u4e09\u4e2a\u4eba\u4e4b\u5916\uff0c\u8fd9\u4e16\u4e0a\u771f\u6b63\u80fd\u5a01\u80c1\u5230\u6211\u7684\u4eba\uff0c\u4e5f\u53ea\u6709\u4e09\u4e2a\u4eba\u3002\u201d \u3000\u3000\u53f6\u5f00\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u54ea\u4e09\u4e2a\uff1f\u2019\u4e0a\u5b98\u5c0f\u4ed9\u7728\u4e86\u7728\u773c\uff0c\u9053\uff1a\u201c\u4f60\u731c\u5462\uff1f\u201d \u3000\u3000\u53f6\u5f00\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u5f53\u7136\u4e5f\u628a\u6211\u7b97\u5728\u91cc\u9762\u4e86\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6211\u6ca1\u6709\u3002\u201d \u3000\u3000\u53f6\u5f00\u6014\u4e86\u6014\uff0c\u53c8\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u6211\u96be\u9053\u4e0d\u80fd\u7b97\u662f\u9ad8\u624b\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u5ae3\u7136\u9053\uff1a\u201c\u82e5\u8bba\u6b66\u529f\uff0c\u4f60\u5f53\u7136\u662f\u7edd\u5bf9\u7684\u9ad8\u624b\uff0c\u82e5\u8bba\u806a\u660e\u673a\u667a\uff0c\u4f60\u4e5f\u7edd\u4e0d\u6bd4\u4efb\u4f55 \u4eba\u5dee\uff0c\u4f60\u7684\u98de\u5200\uff0c\u4e5f\u662f\u5c0f\u674e\u98de\u5200\u4e4b\u540e\uff0c\u4e16\u4e0a\u6700\u53ef\u6015\u7684\u4e00\u79cd\u6b66\u5668\u3002\u201d \u3000\u3000\u8fd9\u662f\u5b9e\u8bdd\u3002 \u3000\u3000\u53f6\u5f00\u4ece\u4e0d\u6253\u65ad\u522b\u4eba\u7684\u5b9e\u8bdd\uff0c\u66f4\u4e0d\u613f\u6253\u65ad\u522b\u4eba\u79f0\u8d5e\u4ed6\u7684\u8bdd\u3002 \u3000\u3000\u65e0\u8bba\u5982\u4f55\uff0c\u88ab\u4eba\u79f0\u8d5e\u662f\u4ef6\u5f88\u6109\u5feb\u7684\u4e8b\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u53ef\u662f\u4f60\u7684\u5fc3\u4e0d\u591f\u9ed1\uff0c\u624b\u6bb5\u4e5f\u4e0d\u591f\u6bd2\u8fa3\uff0c\u4f60\u7684\u98de\u5200\u51fa\u4e8e\uff0c\u603b\u662f\u6551\u4eba\u7684\u65f6\u5019 \u591a\uff0c\u6740\u4eba\u7684\u65f6\u5019\u5c11\uff0c\u201d\u53f6\u5f00\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u6240\u4ee5\u6211\u4e0d\u80fd\u5a01\u80c1\u4f60\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u51dd\u89c6\u7740\u4ed6\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u6211\u8ba4\u4e3a\u4f60\u4e0d\u80fd\u5a01\u80c1\u6211\uff0c\u6700\u91cd\u8981\u7684\uff0c\u8fd8\u662f\u56e0\u4e3a\u2026\u2026\u56e0\u4e3a\u6211 \u4eec\u662f\u670b\u53cb\uff0c\u6211\u7edd\u4e0d\u4f1a\u771f\u7684\u4f24\u5bb3\u4f60\uff0c\u6211\u76f8\u4fe1\u4f60\u4e5f\u4e0d\u5fcd\u4f24\u5bb3\u6211\u3002\u201d \u3000\u3000\u5979\u7684\u773c\u775b\u6e29\u67d4\u800c\u771f\u8bda\uff0c\u65e0\u8bba\u8c01\u5728\u8bf4\u8bdd\u65f6\uff0c\u90fd\u4e0d\u4f1a\u6709\u8fd9\u4e48\u771f\u8bda\u7684\u773c\u6674\u3002 \u3000\u3000\u53f6\u5f00\u5fc3\u91cc\u5ffd\u7136\u53c8\u6d8c\u51fa\u4e00\u79cd\u4ed6\u81ea\u5df1\u4e5f\u4e0d\u613f\u627f\u8ba4\u7684\u611f\u60c5\uff0c\u7acb\u523b\u6539\u53d8\u8bdd\u9898\uff0c\u9053\uff1a\u201c\u6211\u65e2\u7136\u4e0d \u7b97\uff0c\u4e1c\u6d77\u7389\u7bab\u7b97\u4e0d\u7b97\u5176\u4e2d\u4e00\u4e2a\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4e0d\u7b97\u3002\u201d \u3000\u3000\u53f6\u5f00\u76b1\u7709\u9053\uff1a\u201c\u4ed6\u4e5f\u4e0d\u7b97\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4e09\u5341\u5e74\u524d\uff0c\u4ed6\u5df2\u80fd\u5217\u540d\u5728\u5175\u5668\u8c31\u4e2d\u7684\u524d\u5341\u540d\u4e4b\u5185\uff0c\u73b0\u5728\u53c8\u4f3c\u5df2\u5165\u4e86\u9b54 \u6559\uff0c\u4ed6\u7684\u6b66\u529f\u5f53\u7136\u5f88\u53ef\u6015\uff0c\u4f46\u5374\u4e0d\u80fd\u5a01\u80c1\u4e8e\u6211\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u56e0\u4e3a\u4ed6\u5df2\u8d70\u4e86\uff0c\u800c\u4e14\u4ed6\u6709\u5f31\u70b9\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u7389\u7bab\u597d\u8272\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u6240\u4ee5\u627e\u4e00\u70b9\u4e5f\u4e0d\u6015\u4ed6\uff0c\u53ea\u8981\u662f\u597d\u8272\u7684\u4eba\uff0c\u6211\u5c31\u6709\u6cd5\u5b50\u5bf9\u4ed8\u3002\u201d \u3000\u3000\u8fd9\u4e5f\u662f\u5b9e\u8bdd\u3002 \u3000\u3000\u5979\u4e0d\u4f46\u6781\u7f8e\uff0c\u6781\u806a\u660e\uff0c\u800c\u4e14\u51b7\u9177\u65e0\u60c5\uff0c\u8fd9\u79cd\u5973\u4eba\u6070\u5de7\u6b63\u662f\u597d\u8272\u4e4b\u5f92\u7684\u514b\u661f\u3002 \u3000\u3000\u4e00\u4e2a\u5e74\u8f7b\u7f8e\u4e3d\u7684\u5973\u4eba\uff0c\u672c\u5c31\u6709\u5f88\u591a\u6cd5\u5b50\u53bb\u5bf9\u4ed8\u4e00\u4e2a\u597d\u8272\u7684\u8001\u4eba\u3002 \u3000\u3000\u8fd9\u4e16\u4e0a\u672c\u5c31\u6709\u5f88\u591a\u6781\u6709\u667a\u6167\u7684\u8001\u4eba\uff0c\u4f1a\u88ab\u4e00\u4e2a\u6700\u611a\u6627\u7684\u5c11\u5973\u9a97\u5f97\u5bb6\u7834\u4eba\u4ea1\uff0c\u8eab\u8d25\u540d\u88c2\u3002 \u3000\u3000\u53f6\u5f00\u5fc3\u91cc\u53f9\u606f\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u738b\u7bab\u8fdf\u65e9\u603b\u8981\u6b7b\u5728\u4e0a\u5b98\u5c0f\u4ed9\u624b\u4e0a\u7684\uff0c\u4ed6\u540c\u60c5\u7684\u5e76\u4e0d\u662f\u7389\u7bab\uff0c\u800c\u662f\u90a3\u4e9b\u603b\u4e0d\u80af\u627f\u8ba4 \u81ea\u5df1\u5bf9\u5c11\u5973\u5931\u53bb\u5438\u5f15\u529b\u7684\u8001\u4eba\u3002 \u3000\u3000\u201c\u7389\u7bab\u4e0d\u80fd\u7b97\uff0c\u90ed\u5b9a\u5462\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u90ed\u5b9a\u4e5f\u4e0d\u80fd\u7b97\u3002\u201d \u3000\u3000\u53f6\u5f00\u4e0d\u540c\u610f\uff1a\u201c\u636e\u6211\u6240\u77e5\u4ed6\u7684\u5251\u6cd5\u4e4b\u9ad8\uff0c\u5df2\u4e0d\u5728\u6614\u5e74\u7684\u5d69\u9633\u94c1\u5251\u4e4b\u4e0b\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4ed6\u7684\u5251\u6cd5\u5f88\u53ef\u80fd\u5df2\u5728\u90ed\u5d69\u9633\u4e4b\u4e0a\uff0c\u5357\u5bab\u8fdc\u5df2\u7b97\u662f\u6b66\u6797\u4e2d\u7684\u4e00\u6d41\u5251\u5ba2\uff0c\u5374 \u8fde\u4ed6\u5341\u62db\u90fd\u63a5\u4e0d\u4f4f\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u90a3\u4e00\u6218\u4f60\u770b\u89c1\u4e86\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u5f53\u4ee3\u6b66\u6797\u9ad8\u624b\u7684\u51b3\u6218\uff0c\u6211\u53ea\u8981\u80fd\u8d76\u4e0a\uff0c\u5c31\u7edd\u4e0d\u4f1a\u9519\u8fc7\u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u5fae\u7b11\u9053\uff1a\u201c\u6709\u65f6\u4f60\u751a\u81f3\u4f1a\u5728\u5899\u5916\u5077\u5077\u5730\u770b\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u5ae3\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4ed6\u7684\u51fa\u624b\u5a01\u731b\u800c\u6c89\u7740\uff0c\u53d8\u5316\u4e5f\u5f88\u5feb\uff0c\u51e0\u4e4e\u5df2\u53ef\u7b97\u662f\u65e0\u61c8\u53ef \u51fb\uff0c\u53ef\u662f\u4ed6\u7684\u4eba\u4e5f\u6709\u5f31\u70b9\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4ed6\u5927\u591a\u60c5\u3002\u201d \u3000\u3000\u53f6\u5f00\u4e0d\u80fd\u4e0d\u627f\u8ba4\uff0c\u90ed\u5b9a\u7684\u786e\u662f\u4e2a\u591a\u60c5\u7684\u4eba\u3002 \u3000\u3000\u4ed6\u7684\u5916\u8868\u770b\u6765\u867d\u7136\u575a\u5f3a\u800c\u51b7\u9177\uff0c\u5176\u5b9e\u5374\u662f\u4e2a\u611f\u60c5\u5f88\u4e30\u5bcc\u3001\u5f88\u5bb9\u6613\u6fc0\u52a8\u7684\u4eba\uff0c\u6709\u65f6\u751a\u81f3\u8fd8 \u6709\u70b9\u591a\u6101\u5584\u611f\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u591a\u60c5\u7684\u4eba\uff0c\u5c31\u96be\u514d\u8106\u5f31\uff0c\u4e00\u4e2a\u4eba\u7684\u672c\u8eab\u82e5\u662f\u5f88\u8106\u5f31\uff0c\u65e0\u8bba\u4ed6\u7684\u5251\u6cd5\u591a\u4e48 \u575a\u5f3a\uff0c\u90fd\u5df2\u4e0d\u8db3\u60e7\u3002\u201d \u3000\u3000\u53f6\u5f00\u53f9\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u4ed6\u60f3\u5230\u4e86\u90ed\u5b9a\uff0c\u5c31\u60f3\u5230\u4e86\u4e01\u7075\u7433\uff0c\u4e01\u7075\u7433\u4e0d\u4f46\u591a\u60c5\uff0c\u800c\u4e14\u75f4\u60c5\u3002\u4ed6\u4e0d\u613f\u518d\u60f3\u4e0b\u53bb\uff1a\u201c\u73cd \u73e0\u57ce\u4e3b\u5462\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u73cd\u73e0\u57ce\u4e3b\u5144\u59b9\uff0c\u7684\u786e\u53ef\u4ee5\u7b97\u5f97\u4e0a\u662f\u5947\u4eba\uff0c\u4ed6\u4eec\u7684\u5251\u6cd5\u4e4b\u5947\uff0c\u4e5f\u53ef\u79f0\u662f\u5929 \u4e0b\u7b2c\u4e00\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u8054\u73e0\u56db\u767e\u4e5d\u5341\u5251\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u70b9\u70b9\u5934\u9053\uff1a\u201c\u8fd9\u5144\u59b9\u4e24\u4eba\uff0c\u5404\u751f\u5177\u5f02\u50cf\uff0c\u4e00\u4e2a\u53f3\u81c2\u6bd4\u5de6\u81c2\u957f\u4e03\u5bf8\uff0c\u4e00\u4e2a\u5de6\u81c2\u6bd4 \u53f3\u81c2\u957f\u4e03\u5bf8\uff0c\u4e00\u624b\u4f7f\u957f\u5251\uff0c\u4e00\u624b\u4f7f\u77ed\u5251\uff0c\u800c\u4e14\u672c\u662f\u5b6a\u751f\u5144\u59b9\uff0c\u5fc3\u610f\u76f8\u901a\uff0c\u8054\u624b\u653b\u654c\uff0c\u4e24\u4e2a\u4eba \u5c31\u50cf\u4e00\u4e2a\u4eba\uff0c\u5251\u6cd5\u65bd\u5c55\u5f00\u6765\uff0c\u4e00\u524d\u4e00\u540e\u597d\u50cf\u53d8\u6210\u4e86\u56db\u4e2a\u4eba\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u636e\u8bf4\u4ed6\u4eec\u7684\u8054\u73e0\u56db\u767e\u4e5d\u5341\u5251\u53ea\u8981\u4e00\u53d1\u52a8\uff0c\u5929\u4e0b\u65e0\u4eba\u80fd\u7834\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u975e\u4f46\u65e0\u4eba\u80fd\u7834\uff0c\u800c\u4e14\u4e16\u4e0a\u4e5f\u5f88\u5c11\u6709\u4eba\u80fd\u63a5\u5f97\u4f4f\u4ed6\u4eec\u8fd9\u56db\u767e\u4e5d\u5341\u5251\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4ed6\u4eec\u7b97\u4e0d\u7b97\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4e0d\u7b97\u3002\u201d \u3000\u3000\u53f6\u5f00\u5f88\u610f\u5916\uff1a\u201c\u4ed6\u4eec\u4e5f\u4e0d\u7b97\uff1f\u4e3a\u4ec0\u4e48\uff1f\u2026\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u56e0\u4e3a\u4ed6\u4eec\u5df2\u6b7b\u4e86\u3002\u201d \u3000\u3000\u53f6\u5f00\u66f4\u610f\u5916\uff1a\u201c\u51e0\u65f6\u6b7b\u7684\uff1f\u600e\u4e48\u6b7b\u7684\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u6de1\u6de1\u9053\uff1a\u201c\u6bcf\u4e2a\u4eba\u90fd\u96be\u514d\u8981\u4e00\u6b7b\uff0c\u4f60\u53c8\u4f55\u5fc5\u60ca\u5947\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4ed6\u4eec\u4eba\u867d\u5df2\u6b7b\uff0c\u53ef\u662f\u4ed6\u4eec\u7684\u5251\u6cd5\u5e76\u6ca1\u6709\u6b7b\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4ed6\u4eec\u7684\u5251\u6cd5\u7eb5\u7136\u80fd\u6d41\u4f20\uff0c\u53ef\u662f\u5230\u54ea\u91cc\u624d\u80fd\u627e\u5230\u4ed6\u4eec\u90a3\u6837\u4e00\u53cc\u5947\u7279\u7684\u5144 \u59b9\uff0c\u6765\u7ec3\u4ed6\u4eec\u90a3\u79cd\u5947\u7279\u7684\u5251\u6cd5\uff1f\u201d \u3000\u3000\u53f6\u5f00\u53c8\u4e0d\u7981\u53f9\u606f\u3002 \u3000\u3000\u53e4\u5f80\u4eca\u6765\uff0c\u4e5f\u4e0d\u77e5\u6709\u591a\u5c11\u7edd\u4e16\u7684\u5251\u6cd5\uff0c\u4e5f\u90fd\u6b63\u5982\u8fd9\u8054\u73e0\u56db\u767e\u4e5d\u5341\u5251\uff0c\u4eff\u4f5b\u6619\u82b1\u4e00\u73b0\uff0c\u5c31 \u5df2\u6210\u7edd\u54cd\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4f60\u82e5\u4e00\u76f4\u5f80\u8fd9\u4e9b\u540d\u4eba\u4e0a\u9762\u53bb\u60f3\uff0c\u5c31\u6c38\u8fdc\u4e0d\u4f1a\u8bf4\u5bf9\u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u90a3\u4e09\u4e2a\u4eba\uff0c\u96be\u9053\u90e8\u4e0d\u662f\u540d\u4eba\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u81f3\u5c11\u4e0d\u662f\u8fd9\u79cd\u540d\u4eba\u3002\u201d \u3000\u3000\u53f6\u5f00\u6c89\u541f\u7740\uff0c\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u5085\u7ea2\u96ea\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6211\u77e5\u9053\uff0c\u4ed6\u662f\u4f60\u7684\u670b\u53cb\uff0c\u4e5f\u53ef\u7b97\u662f\u4f60\u7684\u5144\u5f1f\uff0c\u4ed6\u7684\u4eba\u5f88\u602a\uff0c\u5200\u6cd5\u4e5f\u5f88 \u602a\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4e0d\u662f\u602a\uff0c\u662f\u5feb\uff0c\u5feb\u5f97\u60ca\u4eba\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6211\u89c1\u8fc7\u4ed6\u51fa\u624b\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u55ef\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4ed6\u51fa\u624b\u90a3\u4e00\u5200\u7684\u5feb\u4e0e\u51c6\uff0c\u5df2\u53ef\u548c\u6614\u65e5\u7684\u98de\u5251\u5ba2\u524d\u540e\u8f89\u6620\uff0c\u53ef\u662f\u2014\u2014\u201d\u53f6 \u5f00\u9053\uff1a\u201c\u53ef\u662f\u4ed6\u8fd8\u4e0d\u80fd\u7b97\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4e0d\u80fd\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff0c\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u56e0\u4e3a\u4ed6\u6839\u672c\u5df2\u4e0d\u613f\u518d\u51fa\u6c5f\u6e56\uff0c\u4ed6\u5bf9\u4eba\u751f\u4f3c\u5df2\u5f88\u538c\u5026\uff0c\u4ed6\u53ea\u60f3\u505a\u4e2a\u4e0e\u4eba\u65e0 \u4e89\u7684\u9690\u58eb\uff0c\u5e76\u4e0d\u60f3\u505a\u540d\u626c\u5929\u4e0b\u7684\u82f1\u96c4\uff0c\u4f55\u51b5\u4ed6\u8fd8\u6709\u79cd\u53ef\u6015\u7684\u6076\u75be\uff0c\u5c31\u8c61\u662f\u4ed6\u7684\u9644\u9aa8\u4e4b\u75bd\u3002\u201d \u3000\u3000\u8fd9\u6b21\u4e0a\u5b98\u5c0f\u4ed9\u53c8\u6ca1\u6709\u8bf4\u9519\u3002 \u3000\u3000\u5979\u5bf9\u5f53\u4e16\u82f1\u96c4\u7684\u6b66\u529f\u6765\u5386\u3001\u6027\u683c\u813e\u6c14\uff0c\u7adf\u5168\u90e8\u4e86\u5982\u6307\u638c\u3002 \u3000\u3000\u5979\u4e0d\u4f46\u5206\u6790\u5f97\u5f88\u6e05\u695a\uff0c\u800c\u4e14\u5224\u65ad\u6781\u6b63\u786e\u3002 \u3000\u3000\u6700\u53ef\u6015\u7684\u662f\uff0c\u65e0\u8bba\u8c01\u53ea\u8981\u6709\u4e1d\u6beb\u5f31\u70b9\uff0c\u90fd\u7edd\u5bf9\u7792\u4e0d\u8fc7\u5979\u7684\u3002 \u3000\u3000\u53f6\u5f00\u5f53\u7136\u89c9\u5f97\u5979\u53c8\u53d8\u4e86\uff0c\u53c8\u5df2\u4ece\u4e00\u4e2a\u8d24\u6167\u7684\u59bb\u5b50\uff0c\u53d8\u6210\u4e86\u4e00\u4e2a\u5bf9\u5929\u4e0b\u5927\u4e8b\u90fd\u4e86\u5982\u6307\u638c\u7684 \u7eb5\u6a2a\u5bb6\uff0c\u53d8\u6210\u4e86\u4e00\u4e2a\u51b3\u80dc\u4e8e\u5343\u91cc\u4e4b\u5916\u7684\u5175\u6cd5\u5bb6\u3002 \u3000\u3000\u5979\u751a\u81f3\u5df2\u53d8\u5f97\u6709\u70b9\u50cf\u662f\u9752\u6885\u56ed\u4e2d\uff0c\u716e\u9152\u8bba\u82f1\u96c4\u7684\u66f9\u64cd\u3002 \u3000\u3000\u8fd9\u53d8\u5316\u5b9e\u5728\u592a\u5927\u3002 \u3000\u3000\u53f6\u5f00\u672c\u6765\u5df2\u89c9\u5f97\u5f88\u75b2\u5026\uff0c\u542c\u4e86\u8fd9\u756a\u8bdd\uff0c\u7cbe\u795e\u5374\u4f3c\u7a81\u7136\u632f\u594b\u8d77\u6765\u3002 \u3000\u3000\u4ed6\u5fcd\u4e0d\u4f4f\u518d\u95ee\uff1a\u201c\u4f60\u8bf4\u7684\u90a3\u4e09\u4eba\uff0c\u7a76\u7adf\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u6211\u8bf4\u7684\u4e09\u4e2a\u4eba\uff0c\u624d\u771f\u6b63\u662f\u4e16\u754c\u4e0a\u6700\u53ef\u6015\u7684\u4eba\uff0c\u56e0\u4e3a\u4ed6\u4eec\u51e0\u4e4e\u5df2\u6ca1\u6709\u5f31\u70b9\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u773c\u775b\u91cc\u5ffd\u7136\u53d1\u51fa\u4e86\u5149\uff0c\u63a5\u7740\u8bf4\uff1a\u201c\u7b2c\u4e00\u4e2a\u4eba\u59d3\u58a8\uff0c\u53eb\u58a8\u4e94\u661f\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u58a8\u4e94\u661f\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4f60\u6ca1\u6709\u542c\u8bf4\u8fc7\u8fd9\u4e2a\u4eba\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4ed6\u4e5f\u662f\u9752\u57ce\u58a8\u5bb6\u7684\u4eba\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u70b9\u70b9\u5934\u9053\uff1a\u201c\u4ed6\u624d\u771f\u6b63\u662f\u90a3\u4e9b\u9752\u57ce\u6b7b\u58eb\u7684\u4e3b\u4eba\uff0c\u58a8\u767d\u4e5f\u53ea\u4e0d\u8fc7\u662f\u4ed6\u7684\u5974\u624d\u800c \u5df2\u3002\u201d \u3000\u3000\u58a8\u767d\u4e5f\u53ef\u7b97\u662f\u4e2a\u5f88\u53ef\u6015\u7684\u4eba\uff0c\u4f46\u5374\u4e0d\u8fc7\u662f\u8fd9\u4e2a\u4eba\u7684\u5974\u624d\u3002 \u3000\u3000\u201c\u4f60\u6740\u4e86\u6211\uff0c\u6211\u7684\u4e3b\u4eba\u4e00\u5b9a\u4f1a\u8981\u4f60\u6b7b\u5f97\u66f4\u60e8\u7684\u2026\u2026\u201d \u3000\u3000\u60f3\u5230\u4e86\u58a8\u767d\u4e34\u6b7b\u524d\u7684\u8bc5\u5492\uff0c\u60f3\u8d77\u4e86\u4ed6\u90a3\u79cd\u51c4\u5389\u7684\u8868\u60c5\uff0c\u8fde\u53f6\u5f00\u5fc3\u91cc\u90fd\u4e0d\u7981\u89c9\u5f97\u6709\u70b9\u53d1 \u51b7\u3002 \u3000\u3000\u201c\u8fd9\u58a8\u4e94\u661f\u7a76\u7adf\u662f\u4e2a\u600e\u4e48\u6837\u7684\u4eba\uff1f\u4ed6\u7684\u6b66\u529f\u7a76\u7adf\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6211\u8bf4\u4e0d\u51fa\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u4e5f\u8bf4\u4e0d\u51fa\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5c31\u56e0\u4e3a\u6211\u4e5f\u8bf4\u4e0d\u51fa\uff0c\u6240\u4ee5\u624d\u53ef\u6015\u3002\u201d \u3000\u3000\u5979\u63a5\u7740\u53c8\u9053\uff1a\u201c\u522b\u7684\u59d1\u4e14\u4e0d\u8bf4\uff0c\u4ed6\u624b\u4e0b\u81f3\u5c11\u6709\u4e94\u767e\u4eba\uff0c\u968f\u65f6\u90fd\u53ef\u4ee5\u4e3a\u4ed6\u53bb\u6b7b\uff0c\u5c31\u51ed\u8fd9\u4e00 \u70b9\uff0c\u4f60\u5df2\u53ef\u60f3\u8c61\u4ed6\u662f\u4e2a\u591a\u4e48\u53ef\u6015\u7684\u4eba\u4e86\u3002\u201d \u3000\u3000\u60f3\u5230\u90a3\u4e9b\u6b7b\u58eb\u4eec\u4ece\u5bb9\u5c31\u6b7b\u65f6\u7684\u60b2\u58ee\u60e8\u70c8\uff0c\u53f6\u5f00\u53c8\u4e0d\u7981\u6bdb\u9aa8\u60ca\u7136\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6211\u8bf4\u7684\u7b2c\u4e8c\u4e2a\u4eba\uff0c\u4f60\u5df2\u8ddf\u4ed6\u4ea4\u8fc7\u624b\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u5415\u8fea\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5415\u8fea\uff0c\u4f60\u4e5f\u8bb8\u4e00\u76f4\u90fd\u4f4e\u4f30\u4e86\u4ed6\u3002\u201d \u3000\u3000\u53f6\u5f00\u82e6\u7b11\u9053\uff1a\u201c\u81f3\u5c11\u6211\u73b0\u5728\u5df2\u4e0d\u80fd\u518d\u4f4e\u4f30\u4ed6\uff0c\u6211\u5df2\u51e0\u4e4e\u6b7b\u5728\u4ed6\u624b\u4e0b\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4f46\u4f60\u5374\u8fd8\u662f\u4e0d\u4f1a\u77e5\u9053\uff0c\u4ed6\u771f\u6b63\u53ef\u6015\u7684\u5730\u65b9\u5728\u54ea\u91cc\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4ed6\u7684\u6b66\u529f\u4f60\u5df2\u89c1\u8fc7\uff0c\u4f60\u89c9\u5f97\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4ed6\u82e5\u5b88\u65f6\u65e0\u61c8\u53ef\u51fb\uff0c\u653b\u51fb\u65f6\u4e00\u53d1\u5982\u96f7\u9706\uff0c\u800c\u4e14\uff0c\u51fa\u624b\u673a\u53d8\u5de7\u8bc8\uff0c\u7adf\u80fd\u5148\u5e03\u597d \u5708\u5957\uff0c\u5f15\u4eba\u4e0a\u94a9\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4f46\u4f60\u7684\u98de\u5200\u82e5\u51fa\u624b\uff0c\u4ed6\u8fd8\u662f\u672a\u5fc5\u80fd\u95ea\u907f\u5f97\u5f00\u3002\u201d \u3000\u3000\u53f6\u5f00\u6ca1\u6709\u627f\u8ba4\uff0c\u5374\u4e5f\u6ca1\u6709\u5426\u8ba4\uff0c\u5bf9\u4ed6\u7684\u98de\u5200\uff0c\u4ed6\u81ea\u5df1\u4ece\u4e0d\u613f\u8bc4\u8bba\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u8fd9\u4eba\u6700\u53ef\u6015\u4e4b\u5904\uff0c\u4e00\u5171\u6709\u5341\u516d\u4e2a\u5b57\uff0c\u4f60\u53ea\u8bf4\u51fa\u4e86\u56db\u4e2a\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u54ea\u56db\u4e2a\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u673a\u53d8\u5de7\u8bc8\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u8fd8\u6709\u5341\u4e8c\u4e2a\u662f\u4ec0\u4e48\u5b57\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6df1\u6c89\u51b7\u9177\uff0c\u673a\u53d8\u5de7\u8bc8\uff0c\u5fc3\u5982\u8c7a\u72fc\uff0c\u8c8c\u4f3c\u541b\u5b50\u3002\u201d \u3000\u3000\u53f6\u5f00\u7b11\u9053\uff1a\u201c\u4ed6\u8fd8\u662f\u4e2a\u5e74\u8f7b\u4eba\uff0c\u8fd9\u5341\u516d\u4e2a\u5b57\uff0c\u8bf4\u5f97\u4e5f\u8bb8\u8fc7\u4efd\u4e86\u4e9b\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\u4ed6\u4e3a\u4ec0\u4e48\u80fd\u51fb\u8d25\u4f60\uff1f\u201d \u3000\u3000\u53f6\u5f00\u6447\u6447\u5934\u3002\u4ed6\u4e0d\u662f\u4e0d\u77e5\u9053\uff0c\u53ea\u662f\u4e0d\u613f\u8bf4\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u5374\u66ff\u4ed6\u8bf4\u4e86\u51fa\u6765\uff1a\u201c\u4ed6\u80fd\u80dc\u4f60\uff0c\u53ea\u56e0\u4e3a\u4f60\u7684\u98de\u5200\u672a\u51fa\u624b\u3002\u201d \u3000\u3000\u5979\u53c8\u95ee\uff1a\u201c\u4f46\u4f60\u77e5\u4e0d\u77e5\u9053\uff0c\u4f60\u7684\u98de\u5200\u4e3a\u4ec0\u4e48\u4f1a\u6ca1\u6709\u51fa\u624b\uff1f\u201d \u3000\u3000\u8fd9\u6b21\u53f6\u5f00\u60f3\u8bf4\u8bdd\uff0c\u4e0a\u5b98\u5c0f\u4ed9\u5374\u4e0d\u8ba9\u4ed6\u8bf4\u51fa\u6765\uff0c\u5c31\u5df2\u62a2\u7740\u9053\uff0c\u201c\u56e0\u4e3a\u4ed6\u81ea\u5df1\u5148\u5c06\u5251\u63b7\u4e86\u51fa \u53bb\uff0c\u4f60\u5f53\u7136\u4e0d\u80fd\u518d\u7528\u5200\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u96be\u9053\u4ed6\u5148\u5c31\u5df2\u7b97\u51c6\u4e86\u8fd9\u4e00\u70b9\uff0c\u6240\u4ee5\u6839\u672c\u4e0d\u7528\u5251\u7684\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u53ef\u662f\u4ed6\u81ea\u5df1\u4e5f\u518d\u4e09\u58f0\u660e\uff0c\u4ed6\u7684\u624b\u4e5f\u662f\u6740\u4eba\u7684\u5229\u5668\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u90a3\u53ea\u56e0\u4e3a\u4ed6\u5df2\u7b97\u51c6\u4e86\u4f60\u662f\u4e2a\u4ec0\u4e48\u6837\u7684\u4eba\uff0c\u4ed6\u77e5\u9053\u8d8a\u662f\u8fd9\u6837\u8bf4\uff0c\u4f60\u8d8a\u4e0d\u4f1a \u518d\u4f7f\u51fa\u98de\u5200\u6765\u7684\uff0c\u6240\u4ee5\u4e50\u5f97\u6545\u4f5c\u5927\u65b9\u3002\u201d \u3000\u3000\u53f6\u5f00\u82e6\u7b11\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\u6700\u540e\u4ed6\u4e3a\u4ec0\u4e48\u4e0d\u6740\u4f60\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u56e0\u4e3a\u2026\u2026\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u53c8\u6253\u65ad\u4e86\u4ed6\u7684\u8bdd\uff0c\u9053\uff1a\u201c\u56e0\u4e3a\u4ed6\u77e5\u9053\u81ea\u5df1\u82e5\u662f\u771f\u7684\u8981\u4e0b\u6740\u624b\uff0c\u4f60\u7684\u98de\u5200\u4e5f\u53ef\u80fd \u51fa\u624b\u7684\uff0c\u4ed6\u5f53\u7136\u4e5f\u77e5\u9053\u4f60\u8eab\u4e0a\u5e26\u7684\u4e0d\u6b62\u4e00\u628a\u5200\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u53ef\u662f\uff0c\u4ed6\u6700\u540e\u53c8\u548c\u6211\u518d\u5ea6\u9080\u6218\u2026\u2026\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u8fd9\u6b21\u5df2\u5bf9\u4f60\u624b\u4e0b\u7559\u60c5\uff0c\u4e0b\u6b21\u7eb5\u7136\u518d\u6218\uff0c\u4f60\u80fd\u5bf9\u4ed6\u4e0b\u6740\u624b\uff1f\u201d\u5979\u7b11\u4e86\u7b11\uff0c \u53c8\u9053\uff1a\u201c\u4f55\u51b5\uff0c\u7ecf\u8fc7\u8fd9\u4e00\u6218\u4e4b\u540e\uff0c\u4f60\u5df2\u89c9\u5f97\u4ed6\u662f\u4e2a\u82f1\u96c4\uff0c\u5df2\u5bf9\u4ed6\u8d77\u4e86\u60fa\u60fa\u76f8\u60dc\u4e4b\u5fc3\uff0c\u4ee5\u540e\u4ed6 \u7eb5\u7136\u8fd8\u8981\u903c\u4f60\u51fa\u624b\uff0c\u4f60\u4e5f\u4f1a\u5c3d\u91cf\u907f\u514d\u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u4e0d\u80fd\u5426\u8ba4\uff0c\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6240\u4ee5\u4ed6\u4e0d\u4f46\u51fb\u8d25\u4e86\u4f60\uff0c\u4e0d\u4f46\u4ea4\u4e86\u4f60\u8fd9\u4e48\u6837\u4e00\u4e2a\u6709\u7528\u7684\u670b \u53cb\uff0c\u8fd8\u535a\u5f97\u4e86\u5fc5\u5c06\u4f20\u626c\u5929\u4e0b\u7684\u4fa0\u4e49\u540d\u58f0\u3002\u201d \u3000\u3000\u5979\u6162\u6162\u5730\u63a5\u7740\u9053\uff1a\u201c\u6240\u4ee5\u6211\u624d\u8bf4\u4ed6\uff0c\u6df1\u6c89\u51b7\u9177\uff0c\u673a\u53d8\u5de7\u8bc8\uff0c\u5fc3\u5982\u8c7a\u72fc\uff0c\u8c8c\u4f3c\u541b\u5b50\uff0c\u8fd9\u5341 \u516d\u4e2a\u5b57\uff0c\u4e00\u70b9\u4e5f\u6ca1\u6709\u9519\u3002\u201d \u3000\u3000\u53f6\u5f00\u53ea\u6709\u82e6\u7b11\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4ed6\u4e0d\u4f46\u6709\u6743\u672f\uff0c\u6709\u57ce\u5e9c\uff0c\u8fd8\u6709\u9634\u8c0b\uff0c\u6709\u91ce\u5fc3\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u624d\u5e0c\u671b\u6211\u80fd\u66ff\u4f60\u6740\u4e86\u4ed6\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u627f\u8ba4\uff1a\u201c\u8fd9\u4e2a\u4eba\u6d3b\u5728\u4e16\u4e0a\uff0c\u5bf9\u6211\u7684\u786e\u662f\u79cd\u5a01\u80c1\u3002\u201d \u3000\u3000\u201c\u81f3\u5c11\u76f4\u5230\u73b0\u5728\uff0c\u6211\u8fd8\u6ca1\u6709\u60f3\u51fa\u4e2a\u4e07\u65e0\u4e00\u5931\u7684\u6cd5\u5b50\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u8ba4\u4e3a\u4ed6\u6bd4\u58a8\u4e94\u661f\u66f4\u53ef\u6015\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u70b9\u70b9\u5934\uff0c\u663e\u5f97\u6709\u4e9b\u75b2\u4e4f\uff1a\u201c\u4f46\u662f\u6700\u53ef\u6015\u7684\uff0c\u5374\u8fd8\u662f\u7b2c\u4e09\u4e2a\u4eba\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u7b2c\u4e09\u4e2a\u4eba\u53c8\u662f\u8c01\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u97e9\u8d1e\u3002\u201d \u3000\u3000\u53f6\u5f00\u6014\u4f4f\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4f60\u60f3\u4e0d\u5230\u662f\u4ed6\uff1f\u201d \u3000\u3000\u53f6\u5f00\u53c8\u5728\u82e6\u7b11\uff1a\u201c\u4ed6\u7684\u786e\u662f\u5f88\u9634\u6c89\u3001\u5f88\u6709\u673a\u8c0b\u7684\u4eba\uff0c\u53ef\u662f\u2026\u2026\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u53ef\u662f\u4f60\u5374\u4e0d\u76f8\u4fe1\u4ed6\u4f1a\u6bd4\u58a8\u4e94\u661f\u548c\u5415\u8fea\u66f4\u53ef\u6015\uff1f\u201d \u3000\u3000\u53f6\u5f00\u627f\u8ba4\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4f60\u8ba4\u4e3a\u4ed6\u7684\u6b66\u529f\u592a\u5dee\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4ed6\u2026\u2026\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4f60\u6ca1\u6709\u628a\u63e1\uff0c\u56e0\u4e3a\u4f60\u6839\u672c\u4e0d\u77e5\u9053\u4ed6\u7684\u6b66\u529f\u662f\u4e0d\u662f\u771f\u7684\u6bd4\u4f60\u5dee\uff0c\u4e16\u4e0a\u4e5f\u8bb8 \u8fd8\u6ca1\u6709\u4eba\u77e5\u9053\u4ed6\u771f\u5b9e\u7684\u6b66\u529f\u7a76\u7adf\u662f\u600e\u4e48\u6837\u3002\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u4e5f\u4e0d\u77e5\u9053\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u53f6\u5f00\u6c89\u541f\u7740\uff0c\u9053\uff1a\u201c\u4f60\u8ba4\u4e3a\u4ed6\u5e76\u4e0d\u662f\u771f\u7684\u5bf9\u4f60\u5fe0\u5fc3\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6211\u6ca1\u6709\u628a\u63e1\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f46\u4f60\u5374\u4e00\u76f4\u5c06\u4ed6\u7559\u5728\u8eab\u8fb9\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u56e0\u4e3a\u76f4\u5230\u73b0\u5728\u4e3a\u6b62\uff0c\u6211\u8fd8\u6ca1\u6709\u53d1\u73b0\u4ed6\u5bf9\u6211\u505a\u8fc7\u4e00\u70b9\u4e0d\u5fe0\u7684\u4e8b\uff0c\u6211\u6839\u672c\u5c31 \u6293\u4e0d\u5230\u4ed6\u4e00\u70b9\u9519\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4e5f\u8bb8\u4ed6\u6839\u672c\u5c31\u5bf9\u4f60\u5f88\u5fe0\u5b9e\uff0c\u4e5f\u8bb8\u4f60\u5bf9\u4ed6\u7684\u7591\u5fc3\u6839\u672c\u5c31\u9519\u4e86\uff0c\u5973\u4eba\u7684\u7591\u5fc3\u75c5\u672c \u5c31\u6bd4\u8f83\u5927\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4f46\u5973\u4eba\u5374\u6709\u79cd\u5947\u5f02\u7684\u611f\u89c9\uff0c\u5c31\u597d\u50cf\u6709\u7b2c\u4e09\u53ea\u773c\u775b\u4e00\u6837\uff0c\u5f80\u5f80\u5c31\u80fd\u770b\u51fa\u4e00 \u4e9b\u7537\u4eba\u770b\u4e0d\u51fa\u7684\u4e8b\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u770b\u51fa\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6211\u65e9\u5df2\u611f\u89c9\u5230\uff0c\u5728\u6211\u6700\u4eb2\u4fe1\u7684\u51e0\u4e2a\u52a9\u624b\u4e2d\uff0c\u6709\u4e00\u4e2a\u5978\u7ec6\uff0c\u53ea\u8981\u6211\u4e00\u4e0d\u5c0f \u5fc3\uff0c\u5c31\u53ef\u80fd\u6bc1\u5728\u4ed6\u5343\u91cc\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u6000\u7591\u8fd9\u4e2a\u4eba\u5c31\u662f\u97e9\u8d1e\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u56e0\u4e3a\u4ed6\u7684\u5acc\u7591\u6700\u5927\uff0c\u6211\u751a\u81f3\u6000\u7591\u5730\u5c31\u662f\u9b54\u6559\u4e2d\u7684\u56db\u5927\u5929\u738b\u4e4b\u4e00\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f46\u4f60\u5374\u6ca1\u6709\u8bc1\u636e\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u53f9\u9053\uff1a\u201c\u8fde\u4e00\u70b9\u8bc1\u636e\u90fd\u6ca1\u6709\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6240\u4ee5\u771f\u6b63\u7684\u5978\u7ec6\u4e5f\u5f88\u53ef\u80fd\u4e0d\u662f\u4ed6\uff0c\u662f\u522b\u4eba\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u5c31\u56e0\u4e3a\u6211\u5b8c\u5168\u6ca1\u6709\u628a\u63e1\uff0c\u6240\u4ee5\u6211\u4e00\u76f4\u4e0d\u80fd\u5bf9\u4ed6\u4e0b\u624b\uff0c\u4ed6\u7684\u786e\u5e2e\u6211\u505a\u8fc7\u5f88 \u591a\u4e8b\uff0c\u7684\u786e\u662f\u4e2a\u597d\u5e2e\u624b\uff0c\u6211\u82e5\u4e0d\u660e\u4e0d\u81ea\u5730\u9664\u53bb\u4e86\u4ed6\uff0c\u4e0d\u4f46\u522b\u4eba\u770b\u89c1\u8981\u5bd2\u5fc3\uff0c\u6211\u81ea\u5df1\u4e5f\u89c9\u5f97\u53ef \u60dc\u3002\u201d \u3000\u3000\u53f6\u5f00\u6de1\u6de1\u9053\uff1a\u201c\u770b\u6765\u8fd9\u2018\u91d1\u94b1\u5e2e\u2019\u7684\u5e2e\u4e3b\uff0c\u5e76\u4e0d\u662f\u5bb9\u6613\u5f53\u7684\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u7684\u786e\u4e0d\u5bb9\u6613\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u4e3a\u4ec0\u4e48\u4e00\u5b9a\u8981\u505a\u8fd9\u79cd\u53c8\u5403\u529b\u3001\u53c8\u5371\u9669\u7684\u4e8b\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u76ee\u5149\u51dd\u89c6\u8fdc\u65b9\uff0c\u8fc7\u4e86\u5f88\u4e45\uff0c\u624d\u5f90\u5f90\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u662f\u4e0a\u5b98\u5c0f\u4ed9\uff0c\u662f\u4e0a\u5b98\u91d1\u8679\u7684\u5973 \u513f\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u53ea\u6709\u7b49\u7740\u90a3\u4e2a\u5978\u7ec6\u5148\u5bf9\u4f60\u4e0b\u624b\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u70b9\u70b9\u5934\uff0c\u957f\u53f9\u9053\uff1a\u201c\u6211\u53ea\u6709\u7b49\u7740\u4ed6\u5148\u51fa\u624b\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4ed6\u7684\u51fa\u624b\u4e00\u51fb\uff0c\u5f88\u53ef\u80fd\u6bc1\u4e86\u4f60\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u5f88\u53ef\u80fd\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u60f3\u5b89\u5fc3\u5730\u7761\u4e00\u665a\u4e0a\uff0c\u5374\u4e0d\u5bb9\u6613\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u7684\u76ee\u5149\u5df2\u81ea\u8fdc\u65b9\u6536\u56de\uff0c\u6b63\u51dd\u89c6\u7740\u4ed6\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u8fd9\u4e9b\u5e74\u6765\uff0c\u6211\u53ea\u6709\u5728\u4f60\u966a\u7740\u6211 \u7684\u90a3\u51e0\u4e2a\u665a\u4e0a\u624d\u80fd\u5b89\u5fc3\u7684\u7761\u7740\u3002\u201d \u3000\u3000\u53f6\u5f00\u907f\u5f00\u4e86\u5979\u7684\u76ee\u5149\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u90a3\u662f\u4ee5\u524d\u7684\u4e8b\u4e86\uff0c\u90a3\u65f6\u6211\u8fd8\u4e0d\u77e5\u9053\u4f60\u662f\u4e2a\u600e\u4e48\u6837\u7684 \u4eba\uff0c\u73b0\u5728\u2026\u2026\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u63e1\u4f4f\u4e86\u4ed6\u7684\u624b\uff0c\u9053\uff1a\u201c\u73b0\u5728\u4e5f\u4e00\u6837\uff0c\u53ea\u8981\u4f60\u80af\u7559\u5728\u6211\u8eab\u8fb9\uff0c\u6211\u5c31\u4ec0\u4e48\u4eba\u90fd\u4e0d\u6015 \u4e86\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u4e0d\u6015\u6211\u2026\u2026\u2019\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6211\u4e0d\u6015\u4f60\uff0c\u6211\u4fe1\u4efb\u4f60\uff0c\u6211\u8fd9\u4e00\u8f88\u5b50\uff0c\u771f\u6b63\u4fe1 \u4efb\u7684\u53ea\u6709\u4f60\u4e00\u4e2a\u4eba\u3002\u201d\u5979\u7684\u58f0\u97f3\u6e29\u67d4\u5982\u6625\u98ce\uff0c\u6162\u6162\u5730\u63a5\u7740\u9053\uff1a\u201c\u53ea\u8981\u6211\u4eec\u4e24\u4e2a\u4eba\u80fd\u5728\u4e00\u8d77\uff0c \u5c31\u7b97\u6709\u5341\u4e2a\u5415\u8fea\uff0c\u5341\u4e2a\u97e9\u8d1e\u4e00\u8d77\u6765\u5bf9\u4ed8\u6211\uff0c\u6211\u4e5f\u6709\u628a\u63e1\u80fd\u5c06\u4ed6\u4eec\u6253\u56de\u53bb\uff0c\u53ea\u8981\u6211\u4eec\u5728\u4e00\u8d77\uff0c \u8fd9\u5929\u4e0b\u5c31\u662f\u6211\u4eec\u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u6ca1\u6709\u518d\u5f00\u53e3\uff0c\u8fde\u773c\u775b\u90fd\u5df2\u9616\u8d77\u3002\u4ed6\u5c45\u7136\u7761\u7740\u4e86\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u51dd\u89c6\u7740\u4ed6\uff0c\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u624d\u8f7b\u8f7b\u5730\u653e\u4e0b\u4ed6\u7684\u624b\uff0c\u8f7b\u8f7b\u5730\u8d70\u4e86\u51fa\u53bb\uff0c\u5979\u770b\u7740 \u53f6\u5f00\u7684\u65f6\u5019\uff0c\u773c\u775b\u91cc\u5145\u6ee1\u4e86\u81ea\u4fe1\uff0c\u597d\u50cf\u5df2\u77e5\u9053\u8fd9\u4e2a\u4eba\u662f\u5c5e\u4e8e\u5979\u7684\uff0c\u770b\u6765\u5979\u7adf\u4f3c\u5df2\u6709\u975e\u5e38\u7684\u628a \u63e1\u3002 \u3000\u3000\u97e9\u8d1e\u4f4e\u7740\u5934\uff0c\u5782\u7740\u624b\uff0c\u8083\u7acb\u5728\u9662\u5b50\u91cc\uff0c\u4e5f\u7b49\u4e86\u5f88\u4e45\uff0c\u56e0\u4e3a\u4e0a\u5b98\u5c0f\u4ed9\u8981\u4ed6\u5728\u8fd9\u91cc\u7b49\u3002 \u3000\u3000\u201c\u4e0a\u5b98\u5c0f\u4ed9\u5c31\u7b97\u8981\u4ed6\u7ad9\u5728\u70ed\u9505\u4e0a\u7b49\uff0c\u4ed6\u4e5f\u7edd\u4e0d\u4f1a\u79fb\u52a8\u534a\u6b65\uff0c\u4ed6\u7684\u670d\u4ece\u548c\u5fe0\u5fc3\uff0c\u4ee4\u4eba\u4e0d\u80fd \u4e0d\u611f\u52a8\u3002\u4e0a\u5b98\u5c0f\u4ed9\u6b63\u8d70\u4e0b\u53f0\u9636\uff0c\u770b\u7740\u4ed6\uff0c\u773c\u775b\u91cc\u4e5f\u4e0d\u7981\u9732\u51fa\u6ee1\u610f\u4e4b\u8272\u3002\u65e0\u8bba\u591a\u6311\u5254\u7684\u4eba\uff0c\u6709 \u4e86\u8fd9\u4e48\u6837\u4e00\u4e2a\u5e2e\u624b\uff0c\u90fd\u5df2\u8be5\u5fc3\u6ee1\u610f\u8db3\u4e86\u3002\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6211\u8981\u4f60\u627e\u7684\u4eba\uff0c\u4f60\u5df2\u627e\u9f50\u4e86\uff1f\u201d \u3000\u3000\u97e9\u8d1e\u70b9\u70b9\u5934\u9053\uff1a\u201c\u53eb\u4ed6\u4eec\u8fdb\u6765\u3002\u201d \u3000\u3000\u97e9\u8d1e\u62cd\u4e86\u62cd\u624b\uff0c\u5916\u9762\u7adf\u6709\u5341\u4e2a\u4eba\u8d70\u4e86\u8fdb\u6765\uff0c\u5176\u4e2d\u6709\u7537\u6709\u5973\uff0c\u6709\u8001\u6709\u5c11\uff0c\u6709\u8d27\u90ce\uff0c\u6709\u5c0f \u8d29\uff0c\u6709\u4e09\u59d1\u516d\u5a46\uff0c\u4e5f\u6709\u5e02\u4e95\u597d\u6c49\uff0c\u4ed6\u4eec\u7684\u88c5\u675f\u6253\u626e\u867d\u4e0d\u540c\uff0c\u5176\u5b9e\u5374\u662f\u540c\u4e00\u79cd\u4eba\u3002 \u3000\u3000\u91d1\u94b1\u5e2e\u95e8\u4e0b\uff0c\u53ea\u6709\u4e00\u79cd\u4eba\u2014\u2014\u7edd\u5bf9\u5fe0\u5fc3\uff0c\u7edd\u5bf9\u670d\u4ece\u7684\u4eba\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u8bf4\u7684\u8bdd\uff0c\u5c31\u662f\u547d\u4ee4\u3002\u8fd9\u6b21\u5979\u7684\u547d\u4ee4\u5f88\u5546\u5355\uff1a\u201c\u5230\u957f\u5b89\u57ce\u53bb\uff0c\u4f20\u64ad\u53f6\u5f00\u7684\u6b7b\u8baf\uff0c \u65e0\u8bba\u4f60\u4eec\u7528\u4ec0\u4e48\u6cd5\u5b50\uff0c\u53ea\u4e0d\u8fc7\u4e00\u5b9a\u8981\u4ee4\u4eba\u76f8\u4fe1\u53f6\u5f00\u5df2\u6b7b\u4e86\uff0c\u53ea\u8981\u8fd8\u6709\u4e00\u4e2a\u4eba\u8ba4\u4e3a\u53f6\u5f00\u662f\u6d3b\u7740 \u7684\uff0c\u4f60\u4eec\u5c31\u5f97\u6b7b\u3002\u201d \u3000\u3000\u5979\u7684\u547d\u4ee4\u867d\u7b80\u77ed\uff0c\u5374\u6709\u6548\u3002\u770b\u7740\u8fd9\u4e9b\u4eba\u8d70\u51fa\u53bb\uff0c\u5979\u773c\u775b\u91cc\u53c8\u4e0d\u7981\u9732\u51fa\u4e86\u6ee1\u610f\u4e4b\u8272\u3002\u53eb\u8fd9 \u4e9b\u4eba\u53bb\u4f20\u64ad\u8c23\u8a00\uff0c\u5c31\u7b49\u4e8e\u8981\u871c\u8702\u53bb\u4f20\u64ad\u82b1\u7c89\u4e00\u6837\u5bb9\u6613\u3002\u5979\u77e5\u9053\u8fd9\u6b21\u7684\u8ba1\u5212\u4e5f\u4e00\u5b9a\u540c\u6837\u6709\u6548\u3002 \u7b2c\u5341\u516b\u7ae0\u3000\u76f8\u89c1\u6068\u665a>> \u53e4\u9f99\u300a\u4e5d\u6708\u9e70\u98de\u300b \u7b2c\u5341\u516b\u7ae0\u3000\u76f8\u89c1\u6068\u665a \u3000\u3000\u201c\u53f6\u5f00\u6b7b\u4e86\uff01\u201d \u3000\u3000\u201c\u53f6\u5f00\u600e\u4e48\u4f1a\u6b7b\uff1f\u201d \u3000\u3000\u201c\u6bcf\u4e2a\u4eba\u90fd\u4f1a\u6b7b\u7684\uff0c\u53f6\u5f00\u4e5f\u662f\u4eba\u3002\u201d \u3000\u3000\u201c\u4f46\u4ed6\u5374\u662f\u4e2a\u5f88\u4e0d\u5bb9\u6613\u6b7b\u7684\u4eba\uff0c\u636e\u8bf4\u4ed6\u5df2\u53ef\u7b97\u662f\u4e2a\u5929\u4e0b\u7b2c\u4e00\u9ad8\u624b\u3002\u201d \u3000\u3000\u201c\u5929\u4e0b\u7b2c\u4e00\u9ad8\u624b\u4e5f\u4e00\u6837\u4f1a\u6b7b\u7684\uff0c\u4ee5\u524d\u90a3\u4e9b\u5929\u4e0b\u7b2c\u4e00\u9ad8\u624b\u5c82\u4e0d\u662f\u5c31\u5168\u90fd\u6b7b\u5149\u4e86\u3002\u201d \u3000\u3000\u201c\u9ad8\u624b\u4e2d\u6c38\u8fdc\u8fd8\u6709\u9ad8\u624b\uff0c\u4e00\u4e2a\u4eba\u82e5\u662f\u505a\u4e86\u5929\u4e0b\u7b2c\u4e00\u9ad8\u624b\uff0c\u6b7b\u5f97\u4e5f\u8bb8\u53cd\u800c\u6bd4\u522b\u4eba\u5feb\u4e9b\u3002\u201d \u3000\u3000\u201c\u4f46\u6211\u5374\u8fd8\u662f\u60f3\u4e0d\u51fa\u6709\u8c01\u80fd\u6740\u4ed6\u3002\u201d \u3000\u3000\u201c\u662f\u4e24\u4e2a\u4eba\u6740\u4e86\u4ed6\u7684\u3002\u201d \u3000\u3000\u201c\u54ea\u4e24\u4e2a\u4eba\uff1f\u201d \u3000\u3000\u201c\u4e00\u4e2a\u5415\u8fea\u3002\u201d \u3000\u3000\u201c\u5415\u8fea\uff1f\u662f\u4e0d\u662f\u6b66\u5f53\u7684\u2018\u767d\u8863\u5251\u5ba2\u2019\u5415\u8fea\uff1f\u201d \u3000\u3000\u201c\u5c31\u662f\u4ed6\u3002\u201d \u3000\u3000\u201c\u4ed6\u7684\u6b66\u529f\u6bd4\u53f6\u5f00\u9ad8\uff1f\u201d \u3000\u3000\u201c\u90a3\u5012\u4e0d\u89c1\u5f97\uff0c\u53f6\u5f00\u82e5\u4e0d\u662f\u5df2\u5148\u4f24\u5728\u53e6\u4e00\u4e2a\u4eba\u624b\u4e0b\uff0c\u8fd9\u6b21\u7edd\u4e0d\u4f1a\u6b7b\u3002\u201d \u3000\u3000\u201c\u6709\u8c01\u80fd\u4f24\u5f97\u4e86\u4ed6\uff1f\u8fd9\u4e2a\u4eba\u53c8\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u662f\u4e2a\u5973\u4eba\uff0c\u636e\u8bf4\u5979\u672c\u6765\u662f\u53f6\u5f00\u6700\u559c\u6b22\u7684\u5973\u4eba\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u50cf\u53f6\u5f00\u8fd9\u4e48\u806a\u660e\u7684\u4eba\uff0c\u4e5f\u4f1a\u4e0a\u5973\u4eba\u7684\u5f53\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u82f1\u96c4\u6700\u96be\u8fc7\u7f8e\u4eba\u5173\u7684\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u5973\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u5979\u59d3\u4e01\uff0c\u53eb\u4e01\u7075\u7433\uff01\u201d \u3000\u3000\u4e01\u7075\u7433\u7761\u5728\u5e8a\u4e0a\uff0c\u5c4b\u5b50\u91cc\u5f88\u9634\u6697\uff0c\u88ab\u7a9d\u91cc\u5374\u662f\u6e29\u6696\u7684\uff0c\u5979\u5df2\u7761\u4e86\u5f88\u4e45\uff0c\u4f46\u5374\u4e00\u76f4\u8fde\u52a8\u90fd \u6ca1\u6709\u52a8\u3002 \u3000\u3000\u5979\u89c9\u5f97\u5f88\u75b2\u5026\uff0c\u5c31\u50cf\u662f\u521a\u8d70\u5b8c\u4e00\u6bb5\u53c8\u8fdc\u53c8\u96be\u8d70\u7684\u8def\uff0c\u53c8\u50cf\u662f\u521a\u505a\u4e86\u4e00\u4e2a\u975e\u5e38\u53ef\u6015\u7684\u6076 \u68a6\u3002\u5728\u68a6\u4e2d\uff0c\u5979\u597d\u50cf\u66fe\u7ecf\u7528\u529b\u523a\u4e86\u53f6\u5f00\u4e00\u5200\u3002 \u3000\u3000\u90a3\u5f53\u7136\u53ea\u4e0d\u8fc7\u662f\u68a6\uff0c\u5979\u5f53\u7136\u7edd\u4e0d\u4f1a\u4f24\u5bb3\u53f6\u5f00\u7684\uff0c\u5979\u5b81\u53ef\u81ea\u5df1\u6b7b\uff0c\u4e5f\u4e0d\u4f1a\u4f24\u5bb3\u53f6\u5f00\u3002 \u3000\u3000\u5c4b\u5b50\u91cc\u6709\u4e86\u811a\u6b65\u58f0\u3002 \u3000\u3000\u201c\u83ab\u975e\u662f\u53f6\u5f00\uff1f\u201d \u3000\u3000\u4e01\u7075\u7433\u771f\u5e0c\u671b\u81ea\u5df1\u4e00\u5f20\u5f00\u773c\uff0c\u5c31\u80fd\u770b\u5230\u53f6\u5f00\uff0c\u53ef\u60dc\u5979\u770b\u89c1\u7684\u5374\u662f\u90ed\u5b9a\u3002 \u3000\u3000\u90ed\u5b9a\u7684\u8138\u8272\u770b\u6765\u4e5f\u5f88\u75b2\u5026\uff0c\u5f88\u6194\u60b4\uff0c\u53ef\u662f\u773c\u775b\u91cc\u5374\u5e26\u7740\u6b22\u559c\u6b23\u6170\u4e4b\u8272\uff1a\u201c\u4f60\u9192\u4e86\u2026\u2026\u201d \u3000\u3000\u4e01\u7075\u7433\u4e0d\u7b49\u4ed6\u8bf4\u5b8c\u8fd9\u4e24\u53e5\u8bdd\uff0c\u5c31\u5df2\u62a2\u7740\u95ee\u9053\uff1a\u201c\u8fd9\u91cc\u662f\u4ec0\u4e48\u5730\u65b9\uff1f\u6211\u600e\u4e48\u4f1a\u5230\u8fd9\u91cc\u6765 \u7684\uff1f\u53f6\u5f00\u5462\uff1f\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u8fd9\u91cc\u662f\u5ba2\u6808\uff0c\u4f60\u4e2d\u4e86\u7389\u7bab\u7684\u8ff7\u836f\uff0c\u6211\u6551\u4f60\u5230\u8fd9\u91cc\u6765\u7684\u3002\u201d \u3000\u3000\u7389\u7bab\u7a81\u7136\u51fa\u73b0\uff0c\u5f53\u7740\u53f6\u5f00\u7684\u9762\u5c06\u5979\u52ab\u8d70\uff0c\u8fd9\u4e9b\u4e8b\u4e01\u7075\u7433\u5f53\u7136\u8fd8\u8bb0\u5f97\u3002\u4ee5\u540e\u53c8\u53d1\u751f\u4e86\u4ec0\u4e48 \u4e8b\uff0c\u90ed\u5b9a\u662f\u600e\u4e48\u6551\u5979\u51fa\u6765\u7684\uff0c\u5979\u5c31\u5b8c\u5168\u4e0d\u6e05\u695a\u4e86\u3002 \u3000\u3000\u53ef\u662f\u5979\u4e5f\u4e0d\u5173\u5fc3\u3002\u5979\u5173\u5fc3\u7684\u53ea\u6709\u4e00\u4e2a\u4eba\uff1a\u201c\u53f6\u5f00\u5462\uff1f\u53f6\u5f00\u5728\u4e0d\u5728\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u90ed\u5b9a\u6447\u6447\u5934\uff1a\u201c\u4ed6\u4e0d\u5728\uff0c\u6211\u2026\u2026\u6211\u4e00\u76f4\u6ca1\u6709\u89c1\u8fc7\u4ed6\u3002\u201d \u3000\u3000\u4ed6\u6ca1\u6709\u8bf4\u51fa\u771f\u76f8\uff0c\u56e0\u4e3a\u4ed6\u751f\u6015\u4e01\u7075\u7433\u8fd8\u53d7\u4e0d\u4e86\u8fd9\u79cd\u523a\u6fc0\u3002 \u3000\u3000\u5979\u82e5\u662f\u77e5\u9053\u81ea\u5df1\u4e00\u5200\u523a\u4f24\u4e86\u53f6\u5f00\uff0c\u4f1a\u591a\u4e48\u60b2\u4f24\u75db\u82e6\uff0c\u90ed\u5b9a\u8fde\u60f3\u90fd\u4e0d\u6562\u60f3\u3002 \u3000\u3000\u4e01\u7075\u7433\u7684\u8138\u8272\u6c89\u4e86\u4e0b\u53bb\uff0c\u9053\uff1a\u201c\u4f60\u4e00\u76f4\u6ca1\u6709\u89c1\u5230\u53f6\u5f00\uff1f\u662f\u4e0d\u662f\u56e0\u4e3a\u4f60\u4e00\u76f4\u6ca1\u6709\u53bb\u627e \u4ed6\uff1f\u201d \u3000\u3000\u90ed\u5b9a\u53ea\u6709\u627f\u8ba4\u3002 \u3000\u3000\u4e01\u7075\u7433\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u628a\u6211\u6551\u5230\u8fd9\u91cc\uff0c\u5374\u4e0d\u53bb\u544a\u8bc9\u4ed6\uff0c\u4f60\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u90ed\u5b9a\u65e0\u6cd5\u56de\u7b54\uff0c\u4ed6\u81ea\u5df1\u4e5f\u4e0d\u4e86\u89e3\u81ea\u5df1\u662f\u4ec0\u4e48\u610f\u601d\u3002 \u3000\u3000\u4ed6\u4eec\u4f3c\u4e4e\u662f\u7d20\u4e0d\u76f8\u8bc6\u7684\u4eba\uff0c\u4f46\u4ed6\u5374\u966a\u7740\u53f6\u5f00\uff0c\u5192\u9669\u53bb\u6551\u51fa\u4e86\u5979\u3002 \u3000\u3000\u4e3a\u4e86\u6015\u7389\u7bab\u7684\u627e\u53bb\uff0c\u4ed6\u624d\u5c06\u5979\u5e26\u5230\u8fd9\u91cc\u6765\uff0c\u4e3a\u4e86\u7167\u987e\u5979\uff0c\u4ed6\u5df2\u5728\u8fd9\u9634\u6697\u7684\u6597\u5ba4\u4e2d\u803d\u4e86\u4e09 \u5929\uff0c\u4e5f\u4e0d\u77e5\u53d7\u4e86\u591a\u5c11\u82e6\uff0c\u591a\u5c11\u59d4\u5c48\u3002 \u3000\u3000\u4e00\u4e2a\u795e\u667a\u5df2\u5b8c\u5168\u4e27\u5931\u7684\u5973\u4eba\uff0c\u5e76\u4e0d\u662f\u5bb9\u6613\u4f8d\u5019\u7684\uff0c\u4f55\u51b5\u4ed6\u672c\u5c31\u6ca1\u6709\u4f8d\u5019\u522b\u4eba\u7684\u7ecf\u9a8c\u3002 \u3000\u3000\u8fd9\u4e09\u5929\u6765\uff0c\u4ed6\u51e0\u4e4e\u8fde\u773c\u775b\u90fd\u6ca1\u6709\u9616\u8d77\u8fc7\uff0c\u6362\u6765\u7684\u5374\u662f\u5979\u7684\u51b7\u7b11\u548c\u6000\u7591\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u5b81\u613f\u88ab\u6000\u7591\uff0c\u4e5f\u4e0d\u613f\u8bf4\u51fa\u771f\u76f8\uff0c\u4e0d\u613f\u5979\u518d\u53d7\u523a\u6fc0\u3002 \u3000\u3000\u4e01\u7075\u7433\u8fd8\u5728\u77aa\u7740\u4ed6\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u6211\u5728\u95ee\u4f60\u7684\u8bdd\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u5f00\u53e3\uff1f\u201d \u3000\u3000\u90ed\u5b9a\u8fd8\u662f\u4e0d\u5f00\u53e3\u3002 \u3000\u3000\u4ed6\u4e0d\u80fd\u5f00\u53e3\uff0c\u4ed6\u5fc3\u91cc\u7684\u8bdd\uff0c\u8fde\u4e00\u4e2a\u5b57\u90fd\u4e0d\u80fd\u8bf4\u51fa\u6765\u3002 \u3000\u3000\u4e01\u7075\u7433\u7684\u624b\u5728\u88ab\u7a9d\u4e2d\u6478\u7d22\u2014\u2014\u5979\u8eab\u4e0a\u8fd8\u662f\u7a7f\u7740\u8863\u670d\u7684\u3002 \u3000\u3000\u6240\u4ee5\u5979\u7684\u8138\u8272\u603b\u7b97\u5df2\u7a0d\u5fae\u597d\u770b\u4e86\u4e9b\uff0c\u5374\u53c8\u95ee\u9053\uff1a\u201c\u6211\u5df2\u5728\u8fd9\u91cc\u803d\u4e86\u591a\u4e45\uff1f\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u597d\u50cf\u5df2\u7ecf\u5feb\u4e09\u5929\u4e86\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u51e0\u4e4e\u8df3\u4e86\u8d77\u6765\uff1a\u201c\u4e09\u5929\uff1f\u6211\u5df2\u5728\u8fd9\u91cc\u803d\u4e86\u4e09\u5929\uff1f\u4f60\u4e5f\u4e00\u76f4\u90fd\u5728\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u90ed\u5b9a\u70b9\u70b9\u5934\u3002 \u3000\u3000\u4e01\u7075\u7433\u773c\u775b\u77aa\u5f97\u66f4\u5927\u4e86\uff1a\u201c\u8fd9\u4e09\u5929\u6765\uff0c\u6211\u96be\u9053\u4e00\u76f4\u90fd\u662f\u7761\u7740\u7684\uff1f\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u8bdd\u7684\u58f0\u97f3\u5f88\u8f7b\uff0c\u56e0\u4e3a\u4ed6\u8bf4\u7684\u662f\u8c0e\u8bdd\u3002 \u3000\u3000\u8fd9\u4e09\u5929\u6765\uff0c\u4e01\u7075\u7433\u5e76\u4e0d\u662f\u4e00\u76f4\u7761\u7740\u7684\uff0c\u5979\u505a\u8fc7\u5f88\u591a\u4e8b\uff0c\u5f88\u591a\u4ee4\u4eba\u610f\u60f3\u4e0d\u5230\u3001\u54ed\u7b11\u4e0d\u5f97\u7684 \u4e8b\u3002 \u3000\u3000\u8fd9\u4e9b\u4e8b\u53ea\u6709\u90ed\u5b9a\u4e00\u4e2a\u4eba\u77e5\u9053\uff0c\u4ed6\u6c38\u8fdc\u4e5f\u4e0d\u4f1a\u518d\u5411\u522b\u4eba\u63d0\u8d77\u3002 \u3000\u3000\u4e01\u7075\u7433\u54ac\u7740\u5634\u5507\uff0c\u8fdf\u7591\u4e86\u5f88\u4e45\uff0c\u7ec8\u4e8e\u8fd8\u662f\u5fcd\u4e0d\u4f4f\u8bf4\u9053\uff1a\u201c\u4f60\u5462\uff1f\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u6211\uff1f\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u6211\u7761\u7740\u7684\u65f6\u5019\u4f60\u5728\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u90ed\u5b9a\u82e6\u7b11\u9053\uff1a\u201c\u6211\u6ca1\u6709\u4e8e\u4ec0\u4e48\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u4eff\u4f5b\u677e\u4e86\u53e3\u6c14\uff0c\u5374\u8fd8\u662f\u677f\u7740\u8138\u9053\uff1a\u201c\u6211\u5e0c\u671b\u4f60\u8bf4\u7684\u4e0d\u5047\uff0c\u56e0\u4e3a\u4f60\u82e5\u662f\u5728\u8bf4\u8c0e\uff0c\u6211 \u8fdf\u65e9\u603b\u4f1a\u67e5\u51fa\u6765\u7684\u3002\u201d \u3000\u3000\u90ed\u5b9a\u53ea\u6709\u542c\u7740\u3002 \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u4f60\u6551\u4e86\u6211\uff0c\u6211\u4ee5\u540e\u4f1a\u62a5\u7b54\u4f60\uff0c\u4f46\u6211\u82e5\u67e5\u51fa\u4f60\u5728\u8bf4\u8c0e\uff0c\u6211\u5c31\u8981\u4f60\u7684\u547d\u3002\u201d \u3000\u3000\u5979\u7adf\u4f3c\u8fde\u770b\u90fd\u61d2\u5f97\u770b\u90ed\u5b9a\u4e00\u773c\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u73b0\u5728\u6211\u53ea\u5e0c\u671b\u4f60\u51fa\u53bb\uff0c\u5feb\u70b9\u51fa\u53bb\u3002\u201d \u3000\u3000\u90ed\u5b9a\u4e5f\u6ca1\u770b\u7740\u5979\u3002 \u3000\u3000\u4ed6\u5fc3\u91cc\u5728\u95ee\u81ea\u5df1\uff1a\u201c\u6211\u7a76\u7adf\u662f\u5728\u5e72\u4ec0\u4e48\uff1f\u6211\u4e3a\u4ec0\u4e48\u8981\u53d7\u8fd9\u79cd\u4fae\u8fb1\u59d4\u5c48\uff1f\u201d \u3000\u3000\u4ed6\u8d70\u4e86\u51fa\u53bb\uff0c\u5934\u4e5f\u4e0d\u56de\u5730\u8d70\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u770b\u7740\u4ed6\u7626\u524a\u75b2\u5026\u7684\u80cc\u5f71\u6d88\u5931\u5728\u95e8\u5916\uff0c\u4e01\u7075\u7433\u53cd\u800c\u4e0d\u7981\u6709\u4e9b\u6b49\u606f\u3002 \u3000\u3000\u5979\u5e76\u4e0d\u8ba8\u538c\u8fd9\u4e2a\u4eba\uff0c\u4e5f\u5e76\u4e0d\u662f\u4e0d\u77e5\u9053\u8fd9\u4e2a\u4eba\u5bf9\u5979\u7684\u611f\u60c5\u3002 \u3000\u3000\u53ef\u662f\u5979\u53ea\u6709\u88c5\u4f5c\u4e0d\u77e5\u9053\uff0c\u5979\u7edd\u4e0d\u80fd\u8ba9\u8fd9\u79cd\u611f\u60c5\u518d\u53d1\u5c55\u4e0b\u53bb\u3002 \u3000\u3000\u56e0\u4e3a\u5979\u5fc3\u91cc\u53ea\u6709\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u53f6\u5f00\uff0c\u5979\u4e00\u5b9a\u8981\u8d76\u5feb\u627e\u5230\u53f6\u5f00\u3002 \u3000\u3000\u5979\u7b2c\u4e00\u4e2a\u8981\u627e\u7684\u5730\u65b9\uff0c\u5f53\u7136\u5c31\u662f\u9e3f\u5bbe\u5ba2\u6808\u3002 \u3000\u3000\u53ef\u662f\u9e3f\u5bbe\u5ba2\u6808\u91cc\u7684\u4eba\u770b\u89c1\u5979\uff0c\u90fd\u597d\u50cf\u770b\u89c1\u4e86\u9b3c\uff0c\u53c8\u538c\u6076\uff0c\u53c8\u6050\u60e7\u3002 \u3000\u3000\u4e00\u4e2a\u7528\u5200\u523a\u4f24\u4e86\u81ea\u5df1\u60c5\u4eba\u7684\u5973\u4eba\uff0c\u65e0\u8bba\u8d70\u5230\u54ea\u91cc\uff0c\u90fd\u4e0d\u4f1a\u53d7\u6b22\u8fce\u7684\u3002 \u3000\u3000\u201c\u4f60\u4eec\u6709\u6ca1\u6709\u89c1\u5230\u90a3\u4f4d\u53f6\u516c\u5b50\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u201c\u4f60\u4eec\uff0c\u4e5f\u4e0d\u77e5\u9053\u4ed6\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u201c\u4e0d\u77e5\u9053\u2014\u2014\u53f6\u516c\u5b50\u7684\u4e8b\uff0c\u6211\u4eec\u5b8c\u5168\u4e0d\u77e5\u9053\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u5230\u9556\u5c40\u91cc\u53bb\u6253\u542c\u6253\u542c\uff1f\u201d \u3000\u3000\u4e8e\u662f\u4e01\u7075\u7433\u5c31\u5230\u4e86\u864e\u51e4\u9556\u5c40\u3002 \u3000\u3000\u864e\u51e4\u9556\u5c40\u7684\u9556\u5934\u4eec\u542c\u89c1\u201c\u4e01\u7075\u7433\u201d\u7684\u540d\u5b57\u65f6\uff0c\u8868\u60c5\u4e5f\u548c\u9e3f\u5bbe\u5ba2\u6808\u7684\u4f19\u8ba1\u4eec\u5dee\u4e0d\u591a\u3002 \u3000\u3000\u201c\u6211\u4eec\u548c\u53f6\u5927\u4fa0\u4e00\u5411\u6ca1\u6709\u4ea4\u5f80\uff0c\u4f46\u82e5\u8981\u6253\u542c\u4ed6\u7684\u6d88\u606f\uff0c\u4e0d\u59a8\u5230\u516b\u65b9\u9556\u5c40\u53bb\uff0c\u90a3\u91cc\u7684\u603b\u9556 \u5934\u2018\u94c1\u80c6\u9707\u516b\u65b9\u201d\u6234\u9ad8\u5c97\uff0c\u542c\u8bf4\u662f\u53f6\u5927\u4fa0\u7684\u751f\u6b7b\u4e4b\u4ea4\u3002\u201d\u4e01\u7075\u7433\u5fc3\u91cc\u5728\u5947\u602a\uff0c\u4e3a\u4ec0\u4e48\u5979\u4e00\u76f4 \u6ca1\u6709\u542c\u8bf4\u53f6\u5f00\u6709\u8fd9\u4e48\u4e00\u4e2a\u201c\u751f\u6b7b\u4e4b\u4ea4\u201d\u7684\u670b\u53cb\uff1f\u5979\u60f3\u518d\u95ee\uff0c\u4e5f\u6ca1\u6cd5\u5b50\u518d\u95ee\uff0c\u5979\u5b9e\u5728\u4e5f\u5f88\u770b\u4e0d \u60ef\u8fd9\u4e9b\u9556\u5934\u4eec\u7684\u8138\u8272\u3002\u2019\u4e0d\u7ba1\u600e\u4e48\u6837\uff0c\u53cd\u6b63\u53ea\u8981\u627e\u5230\u6234\u9ad8\u5c97\uff0c\u5c31\u53ef\u4ee5\u5411\u51fa\u53f6\u5f00\u7684\u4e0b\u843d\u3002 \u3000\u3000\u4e86\u3002\u201d \u3000\u3000\u5979\u5fc3\u91cc\u603b\u7b97\u89c9\u5f97\u8e0f\u5b9e\u4e86\u4e9b\uff0c\u56e0\u4e3a\u5979\u4e0d\u77e5\u9053\u5979\u5df2\u6c38\u8fdc\u6ca1\u6cd5\u5b50\u518d\u4ece\u6234\u9ad8\u5c97\u7684\u5634\u91cc\u95ee\u51fa\u4e00\u53e5\u8bdd \u6765\u3002 \u3000\u3000\u516b\u65b9\u9556\u5c40\u7684\u9662\u5b50\u91cc\uff0c\u6b63\u80b2\u51e0\u4e2a\u4f19\u8ba1\u5728\u6d17\u5237\u7740\u4e00\u8f86\u9ed1\u6f06\u5927\u8f66\u3002 \u3000\u3000\u4e00\u4e2a\u8eab\u6750\u5f88\u9ad8\u3001\u8138\u8272\u5f88\u6c89\u91cd\u7684\u4e2d\u5e74\u4eba\uff0c\u80cc\u8d1f\u8005\u53cc\u624b\uff0c\u7ad9\u5728\u77f3\u9636\u4e0a\u770b\u7740\uff0c\u6b63\u662f\u8fd9\u91cc\u7684\u526f\u603b \u9556\u5934\uff0c\u201c\u94c1\u638c\u5f00\u7891\u201d\u675c\u540c\u3002 \u3000\u3000\u4e01\u7075\u7433\u51b2\u8fc7\u53bb\uff1a\u201c\u4f60\u5c31\u662f\u6234\u9ad8\u5c97\u603b\u9556\u5934\uff1f\u201d \u3000\u3000\u5979\u8bf4\u8bdd\u867d\u7136\u4e0d\u5927\u5ba2\u6c14\uff0c\u8138\u8272\u867d\u7136\u4e0d\u592a\u597d\u770b\uff1b\u4f46\u653e\u6bd5\u7adf\u8fd8\u662f\u5f88\u7f8e\u7684\u5973\u5b69\u5b50\uff0c\u800c\u4e14\u5f88\u5e74\u8f7b\u3002 \u3000\u3000\u675c\u540c\u4e0a\u4e0a\u4e0b\u4e0b\u6253\u91cf\u4e86\u5979\u4e24\u773c\uff0c\u52c9\u5f3a\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u59d1\u5a18\u8d35\u59d3\uff0c\u627e\u4ed6\u6709\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u201c\u6211\u59d3\u4e01\uff0c\u60f3\u627e\u4ed6\u6253\u542c\u4e00\u4e2a\u4eba\u3002\u201d \u3000\u3000\u542c\u5230\u201c\u4e01\u201d\u5b57\uff0c\u675c\u540c\u7684\u8138\u8272\u5df2\u53d8\u4e86\uff1a\u201c\u4f60\u59d3\u4e01\uff1f\u83ab\u975e\u662f\u4e86\u7075\u7433\uff1f\u201d \u3000\u3000\u4e01\u7075\u7433\u70b9\u70b9\u5934\u9053\uff1a\u201c\u4ed6\u5728\u4e0d\u5728\u8fd9\u91cc\uff1f\u6211\u60f3\u5f53\u9762\u95ee\u4ed6\u51e0\u53e5\u8bdd\u3002\u201d \u3000\u3000\u675c\u540c\u6c89\u7740\u8138\uff0c\u770b\u7740\u5979\uff0c\u7a81\u7136\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u60f3\u627e\u53f6\u5f00\uff1f\u201d \u3000\u3000\u4e01\u7075\u7433\u773c\u775b\u4eae\u4e86\u9053\uff1a\u201c\u4f60\u4e5f\u8ba4\u5f97\u53f6\u5f00\uff1f\u4ed6\u5728\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u675c\u540c\u51b7\u51b7\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4ed6\u5728\u8fd9\u91cc\uff0c\u4ed6\u662f\u8ddf\u6234\u603b\u9556\u5934\u4e00\u8d77\u56de\u6765\u7684\uff0c\u5c31\u662f\u5750\u8fd9\u8f86\u8f66\u56de\u6765 \u7684\u3002\u201d \u3000\u3000\u4ed6\u8138\u4e0a\u8868\u60c5\u663e\u7136\u60b2\u54c0\u800c\u6124\u6012\uff0c\u53ea\u53ef\u60dc\u4e01\u7075\u7433\u4e00\u70b9\u4e5f\u6ca1\u6709\u770b\u51fa\u6765\u3002 \u3000\u3000\u53ea\u8981\u60f3\u5230\u80fd\u518d\u89c1\u53f6\u5f00\uff0c\u522b\u7684\u4e8b\u5979\u5df2\u5168\u90fd\u4e0d\u5728\u4e4e\u3002 \u3000\u3000\u201c\u4ed6\u4eec\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u675c\u540c\u51b7\u7b11\u7740\u8f6c\u8fc7\u8eab\uff1a\u201c\u4f60\u8ddf\u6211\u6765\u3002\u201d \u3000\u3000\u5927\u5385\u91cc\u9634\u68ee\u68ee\u7684\uff0c\u5c31\u50cf\u662f\u575f\u5893\u4e00\u6837\u3002\u56e0\u4e3a\u8fd9\u4e2a\u5927\u5385\u73b0\u5728\u5df2\u53d8\u6210\u4e86\u575f\u5893\u3002 \u3000\u3000\u4e01\u7075\u7433\u4e00\u8d70\u8fdb\u53bb\uff0c\u5c31\u770b\u89c1\u4e86\u4e24\u53e3\u68fa\u6750\u3002 \u3000\u3000\u4e24\u53e3\u5d2d\u65b0\u7684\u68fa\u6750\uff0c\u8fd8\u6ca1\u6709\u9489\u4e0a\u76d6\u3002 \u3000\u3000\u68fa\u6751\u91cc\u6709\u4e24\u4e2a\u4eba\u7684\u5c38\u4f53\uff0c\u6ca1\u6709\u5934\u7684\u5c38\u4f53\u3002 \u3000\u3000\u675c\u540c\u51b7\u51b7\u9053\uff1a\u201c\u4ed6\u4eec\u662f\u4e00\u8d77\u5750\u8f66\u51fa\u53bb\u7684\uff0c\u4e5f\u662f\u4e00\u8d77\u5750\u8f66\u56de\u6765\u7684\uff0c\u53ea\u4e0d\u8fc7\uff0c\u4ed6\u4eec\u4eba\u867d\u7136\u56de \u6765\u4e86\uff0c\u5934\u5374\u6ca1\u6709\u56de\u6765\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u6839\u672c\u6ca1\u6709\u542c\u6e05\u695a\u4ed6\u8bf4\u7684\u8bdd\uff0c\u5979\u5df2\u8ba4\u51fa\u4e86\u5176\u4e2d\u4e00\u5177\u5c38\u4f53\u4e0a\u7a7f\u7740\u7684\u8863\u88f3\u2014\u2014\u751f\u6b7b\u4e4b \u4ea4\uff01 \u3000\u3000\u2014\u2014\u636e\u8bf4\u53f6\u5f00\u548c\u6234\u9ad8\u5c97\u662f\u751f\u6b7b\u4e4b\u4ea4\uff0c\u4ed6\u4eec\u4e00\u8d77\u51fa\u53bb\u7684\uff0c\u73b0\u5728\u53c8\u4e00\u8d77\u8eba\u5728\u68fa\u6750\u91cc\u3002 \u3000\u3000\u4e01\u7075\u7433\u53ea\u89c9\u5f97\u6574\u4e2a\u5c4b\u5b50\u90fd\u5728\u65cb\u8f6c\uff0c\u9e3f\u5bbe\u5ba2\u6808\u7684\u4f19\u8ba1\u548c\u516b\u65b9\u9556\u5c40\u7684\u9556\u5934\u4eec\uff0c\u4e5f\u90fd\u5728\u56f4\u7740\u5979 \u65cb\u8f6c\uff0c\u6bcf\u4e2a\u8138\u4e0a\u90fd\u5e26\u7740\u79cd\u6b8b\u9177\u7684\u51b7\u7b11\u3002 \u3000\u3000\u201c\u4ed6\u4eec\u65e9\u5df2\u77e5\u9053\u53f6\u5f00\u6b7b\u4e86\uff1f\u201d \u3000\u3000\u201c\u53f6\u5f00\u96be\u9053\u771f\u7684\u6b7b\u4e86\uff1f\u201d \u3000\u3000\u4e01\u7075\u7433\u60f3\u653e\u58f0\u5927\u54ed\uff0c\u5374\u4e0d\u77e5\u9053\u81ea\u5df1\u53eb\u51fa\u6765\u6ca1\u6709\u3002 \u3000\u3000\u9634\u68ee\u68ee\u7684\u5927\u5385\uff0c\u9634\u68ee\u68ee\u7684\u706f\u5149\u3002 \u3000\u3000\u4e01\u7075\u7433\u9192\u6765\u65f6\u3001\u53d1\u73b0\u81ea\u5df1\u8fd8\u662f\u8eba\u5728\u521a\u624d\u5012\u4e0b\u53bb\u7684\u5730\u65b9\u3002 \u3000\u3000\u6ca1\u6709\u4eba\u6765\u6276\u5979\u4e00\u628a\uff0c\u4e5f\u6ca1\u6709\u4eba\u6765\u5b89\u6170\u5979\u4e00\u53e5\u3002 \u3000\u3000\u675c\u540c\u8fd8\u662f\u80cc\u8d1f\u7740\u53cc\u624b\uff0c\u7ad9\u5728\u90a3\u91cc\uff0c\u51b7\u51b7\u5730\u770b\u7740\u5979\uff0c\u8138\u4e0a\u5e26\u7740\u79cd\u8bf4\u4e0d\u51fa\u7684\u618e\u6076\u4e4b\u610f\u3002 \u3000\u3000\u4e01\u7075\u7433\u52c9\u5f3a\u7740\u7ad9\u8d77\u6765\uff0c\u54ac\u7740\u7259\u9053\uff1a\u201c\u4ed6\u2026\u2026\u4ed6\u662f\u6b7b\u5728\u8c01\u624b\u4e0a\u7684\uff1f\u201d \u3000\u3000\u675c\u540c\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u4e0d\u77e5\u9053\uff1f\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u6211\u600e\u4e48\u4f1a\u77e5\u9053\u3002\u201d \u3000\u3000\u675c\u540c\u9053\uff1a\u201c\u4f60\u5e94\u8be5\u77e5\u9053\u7684\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u5927\u58f0\u9053\uff1a\u201c\u4f60\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\uff0c\u7a76\u7adf\u662f\u8c01\u6740\u4e86\u4ed6\u3002\u201d \u3000\u3000\u675c\u540c\u4e5f\u5728\u54ac\u7740\u7259\uff0c\u4ece\u7259\u7f1d\u91cc\u5410\u51fa\u4e24\u4e2a\u5b57\uff1a\u201c\u662f\u4f60\uff01\u201d \u3000\u3000\u8fd9\u4e24\u4e2a\u5b57\u5c31\u50cf\u662f\u628a\u94c1\u9524\uff0c\u6253\u5f97\u4e86\u7075\u7433\u8fde\u7ad9\u90fd\u7ad9\u4e0d\u4f4f\u4e86\uff1a\u201c\u662f\u6211\uff1f\u675c\u540c\u51b7\u51b7\u9053\uff1a\u201c\u82e5\u4e0d\u662f \u4f60\u5148\u4e00\u5200\u523a\u4f24\u4e86\u4ed6\uff0c\u4ed6\u600e\u4e48\u80fd\u8d25\u5728\u5415\u8fea\u624b\u4e0b\uff1f\u6234\u603b\u9556\u5934\u82e5\u4e0d\u662f\u4e3a\u4e86\u8981\u5e26\u4ed6\u53bb\u6cbb\u4f24\uff0c\u53c8\u600e\u4e48\u4f1a \u8ddf\u4ed6\u4e00\u8d77\u6b7b\u5728\u8f66\u4e0a\uff1f\u201d \u3000\u3000\u4e01\u7075\u7433\u7684\u5fc3\u5df2\u788e\u88c2\uff0c\u6574\u4e2a\u4eba\u90fd\u4f3c\u788e\u88c2\u3002 \u3000\u3000\u5979\u53c8\u60f3\u8d77\u4e86\u6076\u68a6\u91cc\u7684\u4e8b\uff0c\u53c8\u60f3\u8d77\u7389\u7bab\u76ef\u7740\u5979\u65f6\uff0c\u90a3\u53cc\u5145\u6ee1\u4e86\u90aa\u6076\u7684\u773c\u775b\u3002 \u3000\u3000\u2014\u2014\u5feb\u7528\u8fd9\u628a\u5200\u53bb\u6740\u4e86\u53f6\u5f00\u2026\u2026 \u3000\u3000\u96be\u9053\u90a3\u4e0d\u662f\u68a6\uff1f\u96be\u9053\u5979\u7adf\u771f\u7684\u505a\u51fa\u90a3\u79cd\u53ef\u6015\u7684\u4e8b\uff1f \u3000\u3000\u4e01\u7075\u7433\u4e0d\u4fe1\uff0c\u6b7b\u4e5f\u4e0d\u4fe1\u3002 \u3000\u3000\u5979\u51b2\u8fc7\u53bb\uff0c\u4e00\u628a\u63ea\u4f4f\u4e86\u675c\u540c\u7684\u8863\u895f\u5636\u58f0\u5927\u547c\uff1a\u201c\u4f60\u8bf4\u8c0e\u3002\u201d \u3000\u3000\u675c\u540c\u51b7\u51b7\u9053\uff1a\u201c\u6211\u662f\u4e0d\u662f\u5728\u8bf4\u8c0e\uff0c\u4f60\u81ea\u5df1\u5fc3\u91cc\u5e94\u8be5\u77e5\u9053\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u5927\u53eb\uff1a\u201c\u6211\u77e5\u9053\u4f60\u5728\u8bf4\u8c0e\uff0c\u4f60\u518d\u8bf4\u4e00\u4e2a\u5b57\u6211\u5c31\u6740\u4e86\u4f60\u3002\u201d \u3000\u3000\u675c\u540c\u51b7\u7b11\uff0c\u7a81\u7136\u51fa\u624b\uff0c\u659c\u780d\u4e01\u7075\u7433\u7684\u80a9\u3002 \u3000\u3000\u4ed6\u60f3\u4e0d\u5230\u4e01\u7075\u7433\u7684\u6b66\u529f\u7adf\u7136\u6bd4\u4ed6\u60f3\u8c61\u4e2d\u9ad8\u51fa\u5f88\u591a\u3002 \u3000\u3000\u4ed6\u7684\u94c1\u638c\u524a\u51fa\uff0c\u4e01\u7075\u7433\u5df2\u7a81\u7136\u8f6c\u8eab\uff0c\u4e00\u4e2a\u65f6\u62f3\u6253\u5728\u4ed6\u808b\u9aa8\u4e0a\u3002 \u3000\u3000\u4ed6\u5df2\u7acb\u523b\u88ab\u6253\u5f97\u649e\u5728\u5899\u4e0a\uff0c\u75db\u5f97\u5f2f\u4e0b\u4e86\u8170\u3002 \u3000\u3000\u4e01\u7075\u7433\u5374\u5df2\u53c8\u51b2\u4e86\u8fc7\u53bb\uff0c\u4e00\u628a\u5c06\u4ed6\u63ea\u4e86\u8d77\u6765\uff0c\u5636\u58f0\u9053\uff1a\u201c\u4f60\u8bf4\uff0c\u4f60\u662f\u4e0d\u662f\u5728\u8bf4\u8c0e\uff1f\u201d \u3000\u3000\u675c\u540c\u82cd\u81ea\u7684\u8138\uff0c\u51b7\u6c57\u6eda\u6eda\u800c\u51fa\uff0c\u4e0d\u505c\u5730\u5598\u606f\u7740\uff0c\u7a81\u53c8\u51b7\u7b11\u9053\uff1a\u201c\u597d\uff0c\u4f60\u6740\u4e86\u6211\u5427\uff0c\u4f60\u8fde \u53f6\u5f00\u90fd\u80fd\u6740\uff0c\u8fd8\u6709\u4ec0\u4e48\u4eba\u4e0d\u80fd\u6740\uff0c\u53ea\u4e0d\u8fc7\u4f60\u5c31\u7b97\u6740\u4e86\u6211\uff0c\u6211\u8fd8\u662f\u53ea\u6709\u8fd9\u51e0\u53e5\u8bdd\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u7a81\u7136\u677e\u5f00\u4e86\u5b50\uff0c\u5168\u8eab\u90fd\u5728\u53d1\u6296\uff0c\u6296\u5f97\u5c31\u50cf\u662f\u6025\u98ce\u4e2d\u7684\u94dc\u94c3\u3002 \u3000\u3000\u5927\u5385\u56db\u5468\uff0c\u4eff\u4f5b\u6709\u5343\u767e\u5bf9\u773c\u775b\u5728\u770b\u7740\u5979\uff0c\u6bcf\u53cc\u773c\u775b\u91cc\u90fd\u5145\u6ee1\u4e86\u618e\u6068\u548c\u538c\u6076\u3002 \u3000\u3000\u201c\u6211\u672c\u8be5\u6740\u4e86\u4f60\uff0c\u66ff\u6234\u603b\u9556\u5934\u548c\u53f6\u5f00\u62a5\u4ec7\u7684\uff0c\u53ef\u662f\u4f60\u8fd9\u79cd\u5973\u4eba\uff0c\u6839\u672c\u4e0d\u503c\u5f97\u6211\u4eec\u6740\u4f60\uff0c \u4f60\u8d70\u5427\u2026\u2026\u4f60\u8d70\u5427\u2026\u2026\u4f60\u8d70\u5427\u2026\u2026\u201d \u3000\u3000\u2018\u6211\u6740\u4e86\u53f6\u5f00\u2026\u2026\u6211\u7adf\u771f\u7684\u505a\u51fa\u4e86\u8fd9\u79cd\u53ef\u6015\u7684\u4e8b\uff1f\u201d\u4e01\u7075\u7433\u63a9\u7740\u8138\u72c2\u5954\uff0c\u5954\u51fa\u4e86\u9556\u5c40\uff0c \u5954\u4e0a\u4e86\u957f\u8857\u3002\u8857\u9053\u4f3c\u5728\u65cb\u8f6c\uff0c\u5929\u5730\u4f3c\u4e4e\u5728\u65cb\u8f6c\u3002\u5979\u5012\u4e86\u4e0b\u53bb\uff0c\u5012\u5728\u8857\u4e0a\u3002\u8857\u4e0a\u7684\u6ce5\u6cde\u4e5f\u662f\u51b0 \u51b7\u7684\uff0c\u6ce5\u6cde\u91cc\u8fd8\u5e26\u7740\u51b0\u78b4\u5b50\uff0c\u53ef\u662f\u5979\u4e0d\u5728\u4e4e\u3002\u8857\u9053\u4e0a\u7684\u4eba\u90fd\u5728\u770b\u7740\u5979\uff0c\u597d\u50cf\u90fd\u5df2\u77e5\u9053\u5979\u662f\u4e2a \u6740\u4eba\u7684\u5973\u51f6\u624b\u3002\u5979\u4e5f\u4e0d\u5728\u4e4e\u3002\u5979\u5e0c\u671b\u81ea\u5df1\u80fd\u53d8\u4f5c\u6ce5\u6cde\uff0c\u8ba9\u8fd9\u4e9b\u4eba\u5728\u5979\u8eab\u4e0a\u8df5\u8e0f\uff0c\u5979\u5e0c\u671b\u81ea\u5df1 \u80fd\u53d8\u4f5c\u98de\u7070\uff0c\u8ba9\u8fd9\u523a\u9aa8\u7684\u51b7\u98ce\u5c06\u5979\u5439\u6563\uff0c\u6563\u5165\u6ce5\u6cde\u4e2d\u3002\u4f46\u8fd9\u65f6\u5374\u6709\u4e00\u53ea\u624b\uff0c\u5c06\u5979\u62c9\u4e86\u8d77\u6765\u3002 \u4e00\u53ea\u575a\u5f3a\u7a33\u5b9a\u7684\u624b\uff0c\u4e00\u5f20\u5145\u6ee1\u4e86\u60b2\u4f24\u548c\u540c\u60c5\u7684\u8138\u3002\u5979\u4e00\u76f4\u6ca1\u6709\u6d41\u6cea\uff0c\u5979\u5df2\u8fde\u54ed\u90fd\u54ed\u4e0d\u51fa\uff0c\u770b \u5230\u4e86\u8fd9\u5f20\u8138\uff0c\u5979\u7684\u773c\u6cea\u624d\u6cc9\u6c34\u822c\u7684\u8ff8\u53d1\u3002\u90ed\u5b9a\u6276\u8d77\u4e86\u5979\uff0c\u5979\u5374\u5df2\u54ed\u5012\u5728\u4ed6\u6000\u91cc\u3002\u4ed6\u8ba9\u5979\u54ed\uff0c \u4ed6\u5e0c\u671b\u5979\u7684\u60b2\u4f24\u80fd\u53d1\u6cc4\u3002\u7b49\u5979\u54ed\u591f\u4e86\u65f6\uff0c\u5979\u624d\u53d1\u73b0\u81ea\u5df1\u53c8\u56de\u5230\u4e86\u90a3\u9634\u6697\u7684\u6597\u5ba4\u91cc\u3002\u706f\u5149\u660f \u6697\uff0c\u90ed\u5b9a\u6b63\u5750\u5728\u5b64\u706f\u4e0b\u770b\u7740\u5979\uff0c\u4ed6\u4e5f\u5e76\u6ca1\u6709\u8bf4\u4ec0\u4e48\u5b89\u6170\u5979\u7684\u8bdd\uff0c\u53ef\u662f\u4ed6\u7684\u76ee\u5149\u5df2\u662f\u79cd\u5b89\u6170\u3002 \u4e01\u7075\u7433\u7ec8\u4e8e\u6323\u624e\u7740\uff0c\u5750\u4e86\u8d77\u6765\uff0c\u75f4\u75f4\u5730\u770b\u7740\u90a3\u76cf\u660f\u706f\uff0c\u4e5f\u4e0d\u77e5\u9053\u8fc7\u4e86\u591a\u4e45\uff0c\u624d\u75f4\u75f4\u4ed6\u8bf4\u9053\uff1a \u201c\u6211\u6740\u4e86\u4ed6\u2026\u662f\u6211\u6740\u4e86\u4ed6\u3002\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u4e0d\u662f\u4f60\uff01\u201d\u4ed6\u7684\u58f0\u97f3\u6e29\u67d4\u800c\u575a\u5b9a\uff1a\u201c\u8fd9\u4ef6\u4e8b\u6839\u672c\u5c31\u4e0d\u80fd\u602a\u4f60\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u4f60\u77e5\u9053\uff1f\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u662f\u6211\u548c\u53f6\u5f00\u6551\u4f60\u51fa\u6765\u7684\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u6211\u523a\u4ed6\u90a3\u4e00\u5200\u65f6\uff0c\u4f60\u4e5f\u5728\u65c1\u8fb9\u770b\u7740\uff1f\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u5c31\u56e0\u4e3a\u6211\u5728\u65c1\u8fb9\u770b\u7740\uff0c\u6240\u4ee5\u6211\u624d\u77e5\u9053\u90a3\u6839\u672c\u4e0d\u80fd\u602a\u4f60\uff0c\u56e0\u4e3a\uff0c\u90a3\u65f6\u7684\u4f60\uff0c\u5df2 \u6839\u672c\u4e0d\u662f\u4f60\u81ea\u5df1\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u5782\u7740\u5934\uff0c\u770b\u7740\u81ea\u5df1\u7684\u624b\u3002\u4e0d\u7ba1\u600e\u4e48\u6837\uff0c\u5200\u603b\u662f\u5728\u8fd9\u53cc\u624b\u4e0a\uff0c\u8fd9\u662f\u4e8b\u5b9e\uff0c\u5979\u81ea\u5df1\u77e5 \u9053\u81ea\u5df1\u5fc3\u91cc\u7684\u6b49\u759a\u548c\u75db\u82e6\uff0c\u662f\u6c38\u8fdc\u65e0\u6cd5\u89e3\u8131\u7684\u3002\u65e0\u8bba\u4ec0\u4e48\u4eba\uff0c\u65e0\u8bba\u7528\u4ec0\u4e48\u8bdd\u5b89\u6170\u5979\u90fd\u6ca1\u6709 \u7528\u3002 \u3000\u3000\u90ed\u5b9a\u6162\u6162\u5730\u63a5\u7740\u53c8\u9053\uff1a\u201c\u4f60\u82e5\u60f3\u66ff\u53f6\u5f00\u62a5\u4ec7\uff0c\u5c31\u4e0d\u8be5\u518d\u6298\u78e8\u4f60\u81ea\u5df1\uff0c\u6211\u4eec\u5e94\u8be5\u53bb\u627e\u7684\u4eba \u662f\u7389\u7bab\uff0c\u662f\u5415\u8fea\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u6211\u4eec\uff1f\u201d \u3000\u3000\u90ed\u5b9a\u70b9\u70b9\u5934\uff1a\u201c\u6211\u4eec\uff0c\u6211\u548c\u4f60\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u4f46\u8fd9\u4ef6\u4e8b\u5374\u5b8c\u5168\u8ddf\u4f60\u6ca1\u6709\u5173\u7cfb\u3002\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u600e\u4e48\u4f1a\u6ca1\u6709\u5173\u7cfb\uff0c\u4f60\u662f\u6211\u7684\u670b\u53cb\uff0c\u53f6\u5f00\u4e5f\u662f\u6211\u7684\u670b\u53cb\uff0c\u4f60\u4eec\u7684\u4e8b\u5c31\u662f\u6211\u7684 \u4e8b\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u970d\u7136\u62fe\u8d77\u5934\uff0c\u51dd\u89c6\u7740\u4ed6\uff0c\u8fc7\u4e86\u5f88\u4e45\uff0c\u624d\u6162\u6162\u9053\uff1a\u201c\u4f60\u4e00\u76f4\u4e0d\u80af\u5c06\u8fd9\u4ef6\u4e8b\u544a\u8bc9\u6211\uff0c \u5b81\u53ef\u5fcd\u53d7\u6211\u7684\u4fae\u8fb1\u4e5f\u4e0d\u80af\u544a\u8bc9\u6211\uff0c\u4e3a\u7684\u53ea\u4e0d\u8fc7\u6015\u6211\u4f24\u5fc3\u3002\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u6211\u2026\u2026\u201d \u3000\u3000\u4e01\u7075\u7433\u4e0d\u8ba9\u4ed6\u5f00\u53e3\uff0c\u62a2\u7740\u53c8\u9053\uff1a\u201c\u73b0\u5728\u4f60\u8981\u53bb\u66ff\u53f6\u5f00\u62a5\u4ec7\uff0c\u4e5f\u53ea\u56e0\u4e3a\u4f60\u77e5\u9053\u6211\u7edd\u4e0d\u662f\u7389 \u7bab\u548c\u5415\u8fea\u7684\u5bf9\u624b\u3002\u201d \u3000\u3000\u90ed\u5b9a\u4e5f\u4f4e\u4e0b\u5934\uff0c\u770b\u7740\u81ea\u5df1\u7684\u624b\uff0c\u56e0\u4e3a\u4ed6\u4e0d\u6562\u63a5\u89e6\u5979\u7684\u773c\u5149\u3002 \u3000\u3000\u4e01\u7075\u7433\u7684\u773c\u775b\u91cc\u5df2\u6ca1\u6709\u6cea\uff1a\u201c\u4f60\u7684\u610f\u601d\uff0c\u6211\u5df2\u7ecf\u5b8c\u5168\u660e\u767d\uff0c\u73b0\u5728\u6211\u4e5f\u5e0c\u671b\u4f60\u4e5f\u660e\u767d\u6211\u7684 \u610f\u601d\u3002\u201d \u3000\u3000\u90ed\u5b9a\u5728\u542c\u7740\u3002 \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u8fd9\u662f\u6211\u7684\u4e8b\uff0c\u6211\u4e0d\u60f3\u8981\u4f60\u7ba1\uff0c\u7389\u7bab\u548c\u5415\u8fea\u65e0\u8bba\u662f\u591a\u4e48\u53ef\u6015\u7684\u4eba\uff0c\u6211\u90fd\u6709\u6cd5 \u5b50\u5bf9\u4ed8\u4ed6\u4eec\uff0c\u4e5f\u7528\u4e0d\u7740\u4f60\u62c5\u5fc3\u3002\u201d \u3000\u3000\u90ed\u5b9a\u5fcd\u4e0d\u4f4f\u95ee\uff1a\u201c\u4f60\u6709\u6cd5\u5b50\uff1f\u201d \u3000\u3000\u4e01\u7075\u7433\u63e1\u7d27\u4e86\u53cc\u62f3\uff0c\u9053\uff1a\u201c\u6211\u662f\u4e2a\u5973\u4eba\uff0c\u5973\u4eba\u8981\u5bf9\u4ed8\u7537\u4eba\uff0c\u603b\u4f1a\u6709\u6cd5\u5b50\u7684\u3002\u201d \u3000\u3000\u5979\u7684\u58f0\u97f3\u4e5f\u53d8\u5f97\u51b7\u9177\u800c\u575a\u5b9a\u3002\u5979\u672c\u662f\u4e2a\u5929\u771f\u800c\u5a07\u7f8e\u7684\u5973\u5b69\u5b50\uff0c\u4f46\u73b0\u5728\u4f3c\u5df2\u53d8\u6210\u4e86\u53e6\u5916\u4e00 \u4e2a\u4eba\u3002 \u3000\u3000\u90ed\u5b9a\u7684\u5fc3\u5728\u5f80\u4e0b\u6c89\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u89c9\u5f97\u6709\u79cd\u8bf4\u4e0d\u51fa\u7684\u6050\u60e7\uff0c\u4ed6\u5df2\u611f\u89c9\u5230\u4e86\u4e01\u7075\u7433\u4e00\u5b9a\u4f1a\u505a\u51fa\u4e9b\u5f88\u53ef\u6015\u7684\u4e8b\u3002 \u3000\u3000\u4ed6\u60f3\u963b\u6b62\uff0c\u5374\u4e0d\u77e5\u600e\u4e48\u4f6f\u963b\u6b62\u3002 \u3000\u3000\u4e01\u7075\u7433\u7ad9\u8d77\u6765\uff0c\u6162\u6162\u5730\u8d70\u5230\u5c0f\u7a97\u524d\uff0c\u770b\u7740\u7a97\u5916\u7684\u591c\u8272\u3002 \u3000\u3000\u591c\u8272\u8fd8\u4e0d\u6df1\u3002 \u3000\u3000\u5979\u5ffd\u7136\u56de\u8fc7\u5934\u95ee\uff1a\u201c\u4f60\u8eab\u4e0a\u6709\u6ca1\u6709\u94f6\u5b50\uff1f\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u6709\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u6709\u591a\u5c11\uff1f\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u4e0d\u5c11\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u62e2\u4e86\u62e2\u4e86\u5934\u53d1\uff0c\u9053\uff1a\u201c\u73b0\u5728\u65f6\u5019\u8fd8\u4e0d\u592a\u665a\uff0c\u6211\u60f3\u4e0a\u8857\u53bb\u4e70\u70b9\u4e1c\u897f\uff0c\u5403\u987f\u996d\uff0c\u4f60\u966a \u6211\u53bb\u597d\u4e0d\u597d\uff1f\u201d \u3000\u3000\u9152\u697c\u679c\u7136\u8fd8\u6ca1\u6709\u6253\u70ca\uff0c\u4e01\u7075\u7433\u53eb\u4e86\u4e03\u516b\u6837\u83dc\uff0c\u5979\u5403\u5f97\u5f88\u6162\uff0c\u8fd8\u559d\u4e86\u70b9\u9152\u3002 \u3000\u3000\u7136\u540e\u5979\u5c31\u5728\u957f\u5b89\u57ce\u91cc\u6700\u70ed\u95f9\u7684\u4e00\u6761\u8857\u4e0a\u95f2\u901b\u7740\uff0c\u4e70\u4e86\u4e9b\u80ed\u8102\u82b1\u7c89\uff0c\u4e70\u4e86\u51e0\u4ef6\u8272\u5f69\u5f88\u9c9c\u8273 \u7684\u8863\u670d\uff0c\u8fd8\u4e70\u4e86\u4e9b\u4ef7\u94b1\u4e0d\u8d35\u3001\u5374\u5f88\u597d\u770b\u7684\u9996\u9970\u3002 \u3000\u3000\u8fd9\u4e9b\u4e1c\u897f\u672c\u5c31\u662f\u5973\u5b69\u5b50\u4eec\u6700\u559c\u6b22\u7684\uff0c\u5c24\u5176\u662f\u50cf\u5979\u8fd9\u79cd\u5e74\u7eaa\u7684\u5973\u5b69\u5b50\u3002 \u3000\u3000\u8fd9\u4e9b\u4e8b\u672c\u6765\u5c31\u5f88\u6b63\u5e38\u3002 \u3000\u3000\u53ef\u662f\uff0c\u5728\u5979\u8fd9\u79cd\u60c5\u51b5\u4e0b\uff0c\u5c45\u7136\u8fd8\u6709\u5fc3\u60c5\u505a\u8fd9\u4e9b\u4e8b\uff0c\u5c31\u5f88\u4e0d\u6b63\u5e38\u4e86\u3002 \u3000\u3000\u5979\u663e\u5f97\u5f88\u51b7\u9759\u3002 \u3000\u3000\u53ea\u6709\u4e00\u4e2a\u5df2\u4e0b\u4e86\u6781\u5927\u51b3\u5fc3\u7684\u4eba\uff0c\u624d\u4f1a\u5ffd\u7136\u53d8\u5f97\u8fd9\u4e48\u51b7\u9759\u3002 \u3000\u3000\u5979\u7a76\u7adf\u4e0b\u4e86\u4ec0\u4e48\u51b3\u5fc3\uff1f \u3000\u3000\u90ed\u5b9a\u5fc3\u91cc\u7684\u90a3\u79cd\u60f3\u6cd5\u66f4\u6df1\u4e86\uff0c\u4f46\u5374\u53ea\u6709\u9ed8\u9ed8\u5730\u8ddf\u7740\u5979\u8d70\uff0c\u4ec0\u4e48\u6d3b\u90fd\u4e0d\u80fd\u8bf4\u3002 \u3000\u3000\u65e0\u8bba\u5979\u5df2\u4e0b\u5b9a\u51b3\u5fc3\u8981\u505a\u4ec0\u4e48\u4e8b\uff0c\u5979\u6bd5\u7adf\u8fd8\u6ca1\u6709\u505a\u51fa\u6765\u3002 \u3000\u3000\u901b\u7740\u901b\u7740\uff0c\u5ffd\u7136\u53c8\u901b\u5230\u516b\u65b9\u9556\u5c40\uff0c\u4e01\u7075\u7433\u5c06\u624b\u91cc\u7684\u5927\u5305\u5c0f\u5305\u5168\u90fd\u4ea4\u7ed9\u4e86\u90ed\u5b9a\uff0c\u4ece\u4ece\u5bb9\u5bb9 \u5730\u8d70\u8fdb\u53bb\u3002\u95e8\u6237\u53e3\u7684\u9556\u4f19\u4eec\uff0c\u5403\u60ca\u5730\u770b\u7740\u5979\uff0c\u5c45\u7136\u6ca1\u6709\u4eba\u6765\u62e6\u963b\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u4eec\u90fd\u5df2\u53d1\u89c9\u4e86\u8fd9\u5973\u5b69\u5b50\u7adf\u4f3c\u5ffd\u7136\u53d8\u4e86\uff0c\u53d8\u5f97\u592a\u5feb\uff0c\u53d8\u5f97\u592a\u53ef\u6015\u3002 \u3000\u3000\u4e00\u4e2a\u521a\u624d\u662f\u90a3\u4e48\u60b2\u60e8\u3001\u90a3\u4e48\u6fc0\u52a8\u7684\u5973\u5b69\u5b50\uff0c\u7adf\u4f1a\u5ffd\u7136\u53d8\u5f97\u5982\u6b64\u51b7\u9759\uff0c\u8fd9\u7b80\u76f4\u662f\u4ef6\u65e0\u6cd5\u601d \u8bae\u7684\u4e8b\u3002 \u3000\u3000\u751a\u81f3\u8fde\u675c\u540c\u770b\u89c1\u5979\u65f6\uff0c\u90fd\u89c9\u5f97\u5403\u60ca\uff1a\u201c\u4f60\u53c8\u6765\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u6211\u60f3\u8bf7\u4f60\u53bb\u8f6c\u544a\u7389\u7bab\u9053\u4eba\u548c\u5415\u8fea\uff0c\u4ed6\u4eec\u82e5\u60f3\u627e\u4e0a\u5b98\u5c0f\u4ed9\uff0c\u82e5\u60f3\u5f97\u5230\u90a3\u4e9b\u79d8 \u7b08\u548c\u5b9d\u85cf\uff0c\u5c31\u53eb\u4ed6\u4eec\u660e\u5929\u4e2d\u5348\uff0c\u5728\u9e3f\u5bbe\u5ba2\u6808\u7b49\u6211\u3002\u201d \u3000\u3000\u675c\u540c\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u600e\u4e48\u80fd\u627e\u5f97\u5230\u4ed6\u4eec\uff1f\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u60f3\u6cd5\u5b50\u53bb\u627e\uff0c\u82e5\u662f\u627e\u4e0d\u5230\uff0c\u4f60\u5c31\u6700\u597d\u81ea\u5df1\u4e00\u5934\u649e\u6b7b\u3002\u201d \u3000\u3000\u5979\u7684\u58f0\u97f3\u4e5f\u5f88\u5e73\u9759\uff0c\u5634\u89d2\u751a\u81f3\u8fd8\u5e26\u7740\u5fae\u7b11\u3002 \u3000\u3000\u4f46\u8fd9\u79cd\u5fae\u7b11\u5374\u6bd4\u4ec0\u4e48\u8868\u60c5\u90fd\u53ef\u6015\uff0c\u675c\u540c\u7adf\u8fde\u4e00\u53e5\u8bdd\u90fd\u4e0d\u6562\u8bf4\u4e86\u3002 \u3000\u3000\u4e01\u7075\u7433\u5df2\u7ecf\u4ece\u4ece\u5bb9\u5bb9\u5730\u8d70\u51fa\u53bb\uff0c\u5c45\u7136\u5df2\u627e\u4e86\u4e2a\u5c0f\u9762\u9986\uff0c\u5403\u4e86\u5927\u534a\u7897\u9762\uff0c\u53c8\u559d\u4e86\u4e00\u70b9\u9152\u3002 \u3000\u3000\u5979\u5fae\u7b11\u7740\u9053\uff1a\u201c\u4eca\u5929\u7684\u80c3\u53e3\u5f88\u597d\u3002\u201d \u3000\u3000\u770b\u7740\u5979\u7684\u5fae\u7b11\uff0c\u90ed\u5b9a\u4e5f\u8fd9\u4e00\u65ec\u8bdd\u90fd\u8bf4\u4e0d\u51fa\u4e86\u3002 \u3000\u3000\u8fd9\u65f6\u591c\u5df2\u5f88\u6df1\uff0c\u4ed6\u4eec\u8e0f\u7740\u4e25\u51ac\u51c4\u51c9\u800c\u5e73\u9759\u7684\u591c\u8272\uff0c\u6f2b\u6162\u5730\u56de\u5230\u5c0f\u5ba2\u6808\uff0c\u56de\u5230\u90a3\u95f4\u9634\u6697\u7684 \u6597\u5ba4\u3002 \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u6211\u8981\u7761\u89c9\u4e86\u3002\u201d \u3000\u3000\u90ed\u5b9a\u9ed8\u9ed8\u5730\u70b9\u4e86\u70b9\u5934\uff0c\u6b63\u51c6\u5907\u51fa\u53bb\u3002 \u3000\u3000\u4e01\u7075\u7433\u5374\u5ffd\u7136\u7b11\u4e86\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u5fc5\u51fa\u53bb\uff0c\u8fd9\u5f20\u5e8a\u591f\u6211\u4eec\u4e24\u4e2a\u4eba\u7761\u89c9\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u5374\u5df2\u62c9\u5f00\u4e86\u88ab\u8925\uff1a\u201c\u4f60\u5148\u7761\u8fdb\u53bb\uff0c\u6211\u559c\u6b22\u7761\u5728\u5916\u9762\u3002\u201d \u3000\u3000\u5979\u7684\u58f0\u97f3\u8fd8\u662f\u5f88\u5e73\u9759\uff0c\u5374\u50cf\u662f\u6bcd\u4eb2\u53eb\u5b69\u5b50\u4e0a\u5e8a\u7761\u89c9\u4e00\u6837\u3002 \u3000\u3000\u90ed\u5b9a\u7adf\u5b8c\u5168\u65e0\u6cd5\u62d2\u7edd\uff0c\u53ea\u6709\u76f4\u633a\u633a\u7684\u7761\u4e0b\uff0c\u8eab\u5b50\u7d27\u7d27\u7684\u8d34\u7740\u5899\u3002 \u3000\u3000\u4e01\u7075\u7433\u4e5f\u7761\u4e86\u4e0b\u53bb\uff0c\u5fae\u7b11\u7740\u9053\uff1a\u201c\u4eca\u5929\u665a\u4e0a\u6211\u4e5f\u8bb8\u4f1a\u505a\u6076\u68a6\u7684\uff0c\u4f60\u6700\u597d\u4e0d\u8981\u88ab\u6211\u5413\u5f97\u8df3 \u8d77\u6765\u3002\u201d \u3000\u3000\u90ed\u5b9a\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u9664\u4e86\u70b9\u5934\u5916\uff0c\u4ed6\u8fde\u52a8\u90fd\u4e0d\u6562\u52a8\u3002 \u3000\u3000\u4e01\u7075\u7433\u5ffd\u7136\u53c8\u8f7b\u8f7b\u5730\u53f9\u4e86\u53e3\u6c14\uff0c\u5583\u54fa\u9053\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\uff0c\u6211\u4ece\u6765\u6ca1\u6709\u8ddf\u522b\u7684\u7537\u4eba\u5728\u4e00\u5f20 \u5e8a\u4e0a\u7761\u8fc7\u3001\u6211\u672c\u6765\u4ee5\u4e3a\u8fd9\u4e00\u8f88\u5b50\u518d\u4e5f\u4e0d\u4f1a\u8ddf\u522b\u7684\u7537\u4eba\u7761\u5728\u4e00\u5f20\u5e8a\u4e0a\u4e86\u2026\u2026\u201d \u3000\u3000\u5979\u7684\u58f0\u97f3\u8d8a\u8bf4\u8d8a\u4f4e\uff0c\u8fc7\u4e86\u534a\u664c\uff0c\u7adf\u4f3c\u5df2\u771f\u7684\u7761\u7740\u3002 \u3000\u3000\u591c\u5f88\u9759\u3002\u5979\u7684\u547c\u5438\u5f88\u8f7b\uff0c\u8f7b\u5f97\u5c31\u50cf\u662f\u6625\u98ce\u3002 \u3000\u3000\u90ed\u5b9a\u4e5f\u5026\u4e86\uff0c\u4e5f\u60f3\u7761\u4e00\u4f1a\u513f\u3001\u53ef\u4ed6\u600e\u4e48\u80fd\u7761\u5f97\u7740\uff1f \u3000\u3000\u4ed6\u7684\u5fc3\u4ece\u6765\u4e5f\u6ca1\u6709\u50cf\u8fd9\u6837\u4e71\u8fc7\uff0c\u4ed6\u60f3\u8d77\u4e86\u5f88\u591a\u4e8b\uff0c\u5f88\u591a\u4ed6\u5e94\u8be5\u60f3\u7684\u4e8b\uff0c\u4e5f\u6709\u5f88\u591a\u4ed6\u4e0d\u8be5 \u60f3\u7684\u4e8b\u3002 \u3000\u3000\u4ed6\u505a\u68a6\u4e5f\u6ca1\u6709\u60f3\u5230\u81ea\u5df1\u4f1a\u8ddf\u4e01\u7075\u7433\u7761\u5728\u4e00\u5f20\u5e8a\u4e0a\uff0c\u4e5f\u505a\u68a6\u90fd\u6ca1\u6709\u60f3\u5230\uff0c\u4ed6\u8ddf\u4e00\u4e2a\u5973\u5b69\u5b50 \u7761\u5728\u5e8a\u4e0a\u65f6\uff0c\u4f1a\u50cf\u73b0\u5728\u8fd9\u79cd\u60c5\u51b5\u3002 \u3000\u3000\u4ed6\u662f\u4e2a\u7537\u4eba\uff0c\u8840\u6c14\u65b9\u521a\u7684\u7537\u4eba\u3002\u4ed6\u4e5f\u6709\u8fc7\u5973\u4eba\uff0c\u5728\u8fd9\u65b9\u9762\uff0c\u4ed6\u5e76\u4e0d\u50cf\u5916\u8868\u770b\u6765\u90a3\u4e48\u4e25 \u8083\u3002 \u3000\u3000\u73b0\u5728\u7761\u5728\u4ed6\u8eab\u65c1\u7684\uff0c\u6b63\u662f\u4ed6\u4e00\u751f\u4e2d\u603b\u662f\u68a6\u60f3\u80fd\u5f97\u5230\u7684\u90a3\u4e2a\u5973\u4eba\uff0c\u81ea\u4ece\u7b2c\u4e00\u773c\u770b\u89c1\u5979\uff0c\u4ed6 \u5c31\u5bf9\u8fd9\u4e2a\u5973\u4eba\u6709\u4e86\u79cd\u8fde\u81ea\u5df1\u90fd\u65e0\u6cd5\u89e3\u91ca\u7684\u611f\u60c5\u3002 \u3000\u3000\u53ef\u662f\u73b0\u5728\u4ed6\u5374\u5b8c\u5168\u6ca1\u6709\u90a3\u79cd\u5fc3\u60c5\uff0c\u4ed6\u5fc3\u91cc\u53ea\u6709\u6050\u60e7\u548c\u60b2\u4f24\u3002 \u3000\u3000\u4ed6\u5df2\u77e5\u9053\u4e01\u7075\u7433\u4e0b\u5b9a\u51b3\u5fc3\u8981\u53bb\u505a\u7684\uff0c\u662f\u4ec0\u4e48\u4e8b\u4e86\u3002 \u3000\u3000\u53ea\u6709\u4e00\u4e2a\u5df2\u51b3\u5fc3\u8981\u6b7b\u7684\u5973\u4eba\uff0c\u624d\u4f1a\u6709\u8fd9\u4e48\u53ef\u6015\u7684\u6539\u53d8\u3002\u4ed6\u4e5f\u5df2\u4e0b\u4e86\u51b3\u5fc3\uff0c\u4ed6\u7edd\u4e0d\u80fd\u8ba9\u4e01 \u7075\u7433\u6b7b\uff0c\u53ea\u8981\u80fd\u8ba9\u8fd9\u4e2a\u5973\u4eba\u6d3b\u7740\uff0c\u4ed6\u4e0d\u60dc\u53bb\u505a\u4efb\u4f55\u4e8b\u3002 \u3000\u3000\u591c\u66f4\u9759\uff0c\u51b7\u98ce\u5728\u7a97\u5916\u547c\u5578\uff0c\u4ed6\u5ffd\u7136\u53d1\u89c9\u4e01\u7075\u7433\u8eab\u5b50\u5df2\u5f00\u59cb\u98a4\u6296\u3002 \u3000\u3000\u4e0d\u505c\u5730\u98a4\u6296\uff0c\u4e0d\u505c\u5730\u547b\u541f\uff0c\u4e0d\u505c\u5730\u8f7b\u4f4d\u3002 \u3000\u3000\u661f\u5149\u4ece\u7a97\u5916\u7167\u8fdb\u6765\uff0c\u7167\u5728\u5979\u8138\u4e0a\uff0c\u5979\u8138\u4e0a\u5df2\u6d41\u6ee1\u4e86\u6cea\u3002 \u3000\u3000\u4ed6\u7684\u5fc3\u4e5f\u50cf\u662f\u5728\u88ab\u5200\u5272\u7740\uff0c\u51e0\u4e4e\u5df2\u5fcd\u4e0d\u4f4f\u8981\u7ffb\u8fc7\u8eab\u53bb\uff0c\u7d27\u7d27\u5730\u62e5\u62b1\u4f4f\u5979\uff0c\u544a\u8bc9\u5979\u751f\u547d\u4e2d \u8fd8\u6709\u5f88\u591a\u503c\u5f97\u73cd\u60dc\u7684\u4e8b\uff0c\u65e0\u8bba\u4ec0\u4e48\u6df1\u75db\u7684\u4f24\u75d5\uff0c\u90fd\u4f1a\u6162\u6162\u7684\u5e73\u590d\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u4e0d\u6562\u8fd9\u4e48\u505a\uff0c\u4e5f\u4e0d\u80fd\u8fd9\u4e48\u6837\u505a\u3002\u4ed6\u53ea\u6709\u966a\u7740\u5979\u6d41\u6cea\uff0c\u76f4\u5230\u6cea\u5df2\u5c06\u5e72\u7684\u65f6\u5019\uff0c\u4ed6\u624d \u6726\u80e7\u5730\u7761\u53bb\u3002 \u3000\u3000\u7136\u540e\u4ed6\u7684\u8eab\u5b50\u7a81\u7136\u98a4\u6296\uff0c\u4e0d\u505c\u5730\u98a4\u6296\u3002 \u3000\u3000\u8fd9\u65f6\u4ed6\u82e5\u5f20\u5f00\u773c\u6765\uff0c\u5c31\u4f1a\u53d1\u73b0\u4e01\u7075\u7433\u6b63\u5728\u51dd\u89c6\u7740\u4ed6\uff0c\u773c\u6674\u91cc\u4e5f\u5145\u6ee1\u4e86\u60b2\u4f24\u3001\u540c\u60c5\u3001\u601c\u60dc \u548c\u611f\u6fc0\u3002 \u3000\u3000\u4e00\u79cd\u6c38\u8fdc\u65e0\u6cd5\u7528\u8a00\u8bed\u6765\u8868\u8fbe\uff0c\u4e5f\u6c38\u8fdc\u65e0\u6cd5\u62a5\u7b54\u7684\u611f\u6fc0\u2026\u2026 \u3000\u3000\u90ed\u5b9a\u9192\u6765\u7684\u65f6\u5019\uff0c\u5929\u5df2\u4eae\u4e86\u3002 \u3000\u3000\u4e01\u7075\u7433\u5df1\u6362\u4e86\u4e00\u8eab\u6628\u591c\u521a\u4e70\u6765\u7684\u8863\u670d\uff0c\u6b63\u5750\u5728\u7a97\u524d\u68b3\u5986\u3002 \u3000\u3000\u5979\u7684\u52a8\u4f5c\u8f7b\u67d4\u800c\u4f18\u7f8e\uff0c\u5979\u7684\u8138\u5728\u7a97\u5916\u7684\u65e5\u5149\u4e0b\u770b\u6765\uff0c\u663e\u5f97\u8bf4\u4e0d\u51fa\u7684\u5bb9\u5149\u7115\u53d1\u3002 \u3000\u3000\u5c31\u8fde\u8fd9\u9634\u6697\u7684\u6597\u5ba4\uff0c\u90fd\u4f3c\u5df2\u56e0\u5979\u8fd9\u4eba\u800c\u53d8\u5f97\u6709\u4e86\u751f\u547d\uff0c\u6709\u4e86\u5149\u5f69\u3002 \u3000\u3000\u90ed\u5b9a\u5df2\u770b\u5f97\u75f4\u4e86\u3002 \u3000\u3000\u2014\u2014\u5047\u5982\u8fd9\u662f\u4ed6\u7684\u5bb6\uff0c\u5047\u5982\u8fd9\u5c31\u662f\u4ed6\u7684\u59bb\u5b50\uff0c\u4ed6\u4e00\u89c9\u9192\u6765\uff0c\u770b\u89c1\u4ed6\u7684\u59bb\u5b50\u5728\u7a97\u4e0b\u68b3\u5986\u3002 \u3000\u3000\u90a3\u4e48\u4e16\u4e0a\u8fd8\u6709\u4ec0\u4e48\u6837\u7684\u5e78\u798f\u80fd\u6bd4\u5f97\u4e0a\u8fd9\u79cd\u5e78\u798f\uff1f \u3000\u3000\u4ed6\u7684\u5fc3\u53c8\u5728\u523a\u75db\u3002 \u3000\u3000\u4ed6\u4e0d\u60f3\u518d\u60f3\u4e0b\u53bb\uff0c\u8fde\u60f3\u90fd\u4e0d\u6562\u60f3\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u8fd9\u5149\u8f89\u707f\u70c2\u3001\u7f8e\u4e3d\u7684\u4e00\u523b\uff0c\u53ea\u4e0d\u8fc7\u662f\u6b7b\u4ea1\u7684\u524d\u594f\u3002 \u3000\u3000\u6b7b\u4ea1\u7684\u672c\u8eab\uff0c\u6709\u65f6\u672c\u5c31\u5f88\u7f8e\u4e3d\u7684\u3002 \u3000\u3000\u4e01\u7075\u7433\u5ffd\u7136\u9053\uff1a\u201c\u4f60\u9192\u4e86\u3002\u201d \u3000\u3000\u90ed\u5b9a\u70b9\u70b9\u5934\uff0c\u5750\u8d77\u6765\u52c9\u5f3a\u7b11\u9053\uff1a\u201c\u6211\u7761\u5f97\u4e00\u5b9a\u8ddf\u6b7b\u4eba\u4e00\u6837\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u67d4\u58f0\u9053\uff1a\u201c\u4f60\u5e94\u8be5\u597d\u597d\u7761\u4e00\u89c9\uff0c\u6211\u77e5\u9053\u4f60\u5df2\u6709\u597d\u51e0\u5929\u6ca1\u7761\u4e86\u3002\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u73b0\u5728\u662f\u4ec0\u4e48\u65f6\u5019\uff1f\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u597d\u50cf\u5df2\u7ecf\u5feb\u5230\u6b63\u5348\u3002\u201d \u3000\u3000\u90ed\u5b9a\u7684\u5fc3\u6c89\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u4e0b\u5348\u3002 \u3000\u3000\u2014\u2014\u53eb\u4ed6\u4eec\u660e\u5929\u6b63\u5348\uff0c\u5728\u9e3f\u5bbe\u5ba2\u6808\u7b49\u6211\u3002 \u3000\u3000\u6b63\u5348\u672c\u662f\u4e00\u5929\u4e2d\u6700\u5149\u660e\u7684\u65f6\u5019\uff0c\u4f46\u73b0\u5728\u5bf9\u4ed6\u4eec\u8bf4\u6765\uff0c\u5374\u662f\u6b7b\u4ea1\u7684\u65f6\u523b\u3002 \u3000\u3000\u4e01\u7075\u7433\u5ffd\u7136\u7ad9\u8d77\u6765\uff0c\u5728\u4ed6\u9762\u524d\u8f6c\u4e86\u4e2a\u8eab\uff0c\u5fae\u7b11\u7740\u9053\uff1a\u201c\u4f60\u770b\u6211\u6253\u626e\u5f97\u7f8e\u4e0d\u7f8e\uff1f\u201d \u3000\u3000\u5979\u7684\u786e\u7f8e\u3002 \u3000\u3000\u5979\u770b\u6765\u4ece\u6765\u4e5f\u6ca1\u6709\u50cf\u6b64\u523b\u8fd9\u6837\u8f89\u714c\u7f8e\u4e3d\uff0c\u56e0\u4e3a\u5979\u4ece\u6765\u4e5f\u6ca1\u6709\u8fd9\u4e48\u6837\u6253\u626e\u8fc7\u3002 \u3000\u3000\u5979\u770b\u6765\u5c31\u50cf\u662f\u4e00\u53ea\u521d\u5c55\u5f00\u5f69\u5c4f\u7684\u5b54\u96c0\u3002 \u3000\u3000\u8fd9\u4e5f\u8bb8\u53ea\u56e0\u5979\u76f4\u5230\u6b64\u523b\uff0c\u624d\u771f\u6b63\u53d8\u6210\u4e00\u4e2a\u6210\u719f\u7684\u5973\u4eba\u3002 \u3000\u3000\u8fd9\u79cd\u8f89\u714c\u7684\u7f8e\u4e3d\uff0c\u5374\u4f7f\u5f97\u90ed\u5b9a\u66f4\u75db\u82e6\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u60f3\u8d77\u4ed6\u6bcd\u4eb2\u6b7b\u7684\u65f6\u5019\uff0c\u5728\u5165\u6b93\u65f6\uff0c\u4e5f\u6b63\u662f\u5979\u4e00\u751f\u4e2d\u6253\u626e\u5f97\u6700\u7f8e\u4e3d\u7684\u65f6\u5019\u3002 \u3000\u3000\u4e01\u7075\u7433\u51dd\u89c6\u7740\u4ed6\uff0c\u53c8\u5728\u95ee\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u8bf4\u8bdd\uff1f\u4f60\u5728\u60f3\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u90ed\u5b9a\u6ca1\u6709\u56de\u7b54\u8fd9\u53e5\u8bdd\uff0c\u53ea\u662f\u75f4\u75f4\u5730\u770b\u7740\u5979\uff0c\u5ffd\u7136\u95ee\uff1a\u201c\u4f60\u8981\u8d70\uff1f\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u53ea\u4e0d\u8fc7\u51fa\u53bb\u4e00\u8d8a\u3002\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u53bb\u89c1\u7389\u7bab\u548c\u5415\u8fea\uff1f\u201d \u3000\u3000\u4e01\u7075\u7433\u70b9\u4e86\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4f60\u77e5\u9053\uff0c\u6211\u8fdf\u65e9\u603b\u662f\u975e\u8981\u89c1\u4ed6\u4eec\u4e00\u6b21\u4e0d\u53ef\u7684\u3002\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u6211\u4e5f\u8fdf\u65e9\u603b\u662f\u8981\u89c1\u4ed6\u4eec\u4e00\u6b21\u4e0d\u53ef\u7684\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u4f60\u8981\u966a\u6211\u53bb\uff1f\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u4f60\u4e0d\u80af\uff1f\u201d \u3000\u3000\u4e01\u7075\u7433\u5ae3\u7136\u9053\uff1a\u201c\u6211\u4e3a\u4ec0\u4e48\u4e0d\u80af\uff0c\u6709\u4f60\u966a\u6211\u53bb\u6700\u597d\u3002\u201d \u3000\u3000\u90ed\u5b9a\u53c8\u6014\u4f4f\u3002 \u3000\u3000\u4ed6\u672c\u6765\u60f3\u4e0d\u5230\u4e01\u7075\u7433\u4f1a\u8ba9\u4ed6\u53bb\u7684\u2014\u2014\u201c\u8fd9\u662f\u6211\u7684\u4e8b\uff0c\u6211\u4e0d\u8981\u4f60\u7ba1\u3002\u201d \u3000\u3000\u4ed6\u60f3\u4e0d\u5230\u5979\u4eca\u5929\u5c45\u7136\u4f1a\u6539\u53d8\u4e3b\u610f\u3002 \u3000\u3000\u4e01\u7075\u7433\u5fae\u7b11\u9053\uff1a\u201c\u4f60\u82e5\u8981\u53bb\uff0c\u5c31\u5f97\u8d76\u5feb\u8d77\u6765\uff0c\u5148\u6d17\u4e2a\u8138\uff0c\u6d17\u8138\u6c34\u6211\u5df2\u66ff\u4f60\u6253\u597d\u4e86\u3002\u201d \u3000\u3000\u5c4b\u89d2\u679c\u7136\u653e\u7740\u4e00\u76c6\u6c34\u3002 \u3000\u3000\u90ed\u5b9a\u8df3\u4e0b\u5e8a\uff0c\u773c\u775b\u91cc\u56e0\u5174\u594b\u800c\u53d1\u51fa\u4e86\u5149\uff0c\u4ed6\u89c9\u5f97\u5168\u8eab\u90fd\u5145\u6ee1\u4e86\u529b\u91cf\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u7389\u7bab\u548c\u5415\u8fea\u90fd\u662f\u6781\u53ef\u6015\u7684\u5bf9\u624b\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u4e0d\u5728\u4e4e\u3002 \u3000\u3000\u8fd9\u4e00\u6218\u662f\u80dc\u662f\u8d1f\uff0c\u4ed6\u90fd\u4e0d\u5728\u4e4e\u3002 \u3000\u3000\u552f\u4e00\u91cd\u8981\u7684\u4e8b\uff0c\u73b0\u5728\u4e01\u7075\u7433\u5df2\u4e0d\u662f\u4e00\u4e2a\u4eba\u53bb\u6b7b\u4e86\uff0c\u4ed6\u5ffd\u7136\u89c9\u5f97\u8fd9\u4e00\u6218\u5e76\u4e0d\u662f\u6ca1\u6709\u5e0c\u671b \u7684\uff0c\u4ed6\u5168\u8eab\u90fd\u5145\u6ee1\u4e86\u4fe1\u5fc3\u548c\u529b\u91cf\u3002 \u3000\u3000\u4ed6\u5f2f\u4e0b\u8170\uff0c\u7528\u53cc\u624b\u6367\u8d77\u4e86\u4e00\u63ac\u6c34\u3002 \u3000\u3000\u51b0\u51b7\u7684\u6c34\uff0c\u5c31\u50cf\u662f\u5200\u950b\u4e00\u6837\uff0c\u5374\u4f7f\u5f97\u4ed6\u66f4\u6e05\u9192\uff0c\u66f4\u632f\u594b\u3002 \u3000\u3000\u4e01\u7075\u7433\u5df2\u8d70\u8fc7\u53bb\uff0c\u8d70\u5230\u4ed6\u8eab\u540e\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u4f60\u4e5f\u4e0d\u5fc5\u592a\u7740\u6025\uff0c\u53cd\u6b63\u4ed6\u4eec\u4e00\u5b9a\u4f1a\u7b49\u7684\u3002\u201d \u3000\u3000\u90ed\u5b9a\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u53eb\u4ed6\u4eec\u591a\u7b49\u7b49\u4e5f\u597d\uff0c\u6211\u2026\u2026\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u4ed6\u6ca1\u6709\u8bf4\u5b8c\uff0c\u4ed6\u5ffd\u7136\u53d1\u89c9\u4e00\u6837\u4e1c\u897f\u649e\u5728\u4ed6\u540e\u8170\u7684\u7a74\u9053\u4e0a\u3002 \u3000\u3000\u4ed6\u7acb\u523b\u5012\u4e0b\u3002 \u3000\u3000\u53ea\u542c\u4e01\u7075\u7433\u8f7b\u8f7b\u9053\uff1a\u201c\u6211\u4e0d\u80fd\u4e0d\u8fd9\u4e48\u505a\uff0c\u4e0d\u80fd\u8ba9\u4f60\u53bb\u4e3a\u6211\u6b7b\uff0c\u4f60\u4e00\u5b9a\u8981\u539f\u8c05\u6211\u3002\u201d \u3000\u3000\u90ed\u5b9a\u867d\u7136\u542c\u5f97\u89c1\u5979\u7684\u8bdd\uff0c\u5374\u4e0d\u80fd\u52a8\uff0c\u4e5f\u4e0d\u80fd\u5f00\u53e3\u3002 \u3000\u3000\u4e01\u7075\u7433\u5df2\u6276\u8d77\u4e86\u4ed6\uff0c\u6276\u5230\u5e8a\u4e0a\uff0c\u8ba9\u4ed6\u8eba\u597d\uff0c\u7ad9\u5728\u5e8a\u5934\u770b\u7740\u4ed6\u3002 \u3000\u3000\u5979\u7684\u773c\u775b\uff0c\u53c8\u5145\u6ee1\u4e86\u601c\u60af\u3001\u611f\u6fc0\u548c\u60b2\u4f24\uff1a\u201c\u4f60\u5bf9\u6211\u7684\u5fc3\u610f\uff0c\u6211\u5df2\u5b8c\u5168\u77e5\u9053\uff0c\u4f60\u662f\u4e2a\u600e\u4e48 \u6837\u7684\u4eba\uff0c\u6211\u4e5f\u5b8c\u5168\u660e\u767d\uff0c\u53ea\u53ef\u60dc\u2026\u2026\u53ea\u53ef\u60dc\u6211\u4eec\u76f8\u89c1\u592a\u665a\u4e86\u3002\u201d \u7b2c\u5341\u4e5d\u7ae0\u3000\u7518\u4e3a\u60c5\u6b7b>> \u53e4\u9f99\u300a\u4e5d\u6708\u9e70\u98de\u300b \u7b2c\u5341\u4e5d\u7ae0\u3000\u7518\u4e3a\u60c5\u6b7b \u3000\u3000\u201c\u53ea\u53ef\u60dc\u6211\u4eec\u76f8\u89c1\u592a\u665a\u4e86\u3002\u201d \u3000\u3000\u8fd9\u5c31\u662f\u4e01\u7075\u7433\u5bf9\u90ed\u5b9a\u8bf4\u7684\u6700\u540e\u4e00\u53e5\u8bdd\uff0c\u4e5f\u662f\u5979\u552f\u4e00\u80fd\u8bf4\u7684\u4e00\u53e5\u8bdd\uff0c\u53e4\u5f80\u4eca\u6765\uff0c\u4e0d\u77e5\u6709\u591a \u5c11\u4eba\u8bf4\u8fc7\u8fd9\u53e5\u8bdd\uff0c\u4e5f\u4e0d\u77e5\u6709\u591a\u5c11\u4eba\u542c\u8fc7\u3002\u53ef\u662f\u9664\u975e\u4f60\u771f\u7684\u8bf4\u8fc7\uff0c\u771f\u7684\u542c\u8fc7\uff0c\u4f60\u7edd\u5bf9\u65e0\u6cd5\u60f3\u8c61 \u8bf4\u8fd9\u53e5\u8bdd\u65f6\u6709\u591a\u5c11\u8f9b\u9178\uff0c\u591a\u5c11\u75db\u82e6\u3002 \u3000\u3000\u770b\u7740\u4e01\u7075\u7433\u5934\u4e5f\u4e0d\u56de\u5730\u8d70\u51fa\u53bb\uff1b\u90ed\u5b9a\u53ea\u89c9\u5f97\u6574\u4e2a\u4eba\u90fd\u4f3c\u5df2\u53d8\u6210\u7a7a\u7684\uff0c\u7a7a\u8361\u8361\u7684\uff0c\u98d8\u5165\u51b7 \u800c\u6f6e\u6e7f\u7684\u9634\u973e\u4e2d\uff0c\u53c8\u7a7a\u8361\u8361\u7684\uff0c\u6c89\u5165\u4e07\u52ab\u4e0d\u590d\u7684\u6df1\u6e0a\u91cc\u3002 \u3000\u3000\u4e25\u51ac\u4e2d\u96be\u5f97\u4e00\u89c1\u7684\u9633\u5149\u3001\u521a\u4ece\u4e1c\u65b9\u5347\u8d77\uff0c\u7167\u5165\u4e86\u9634\u6697\u7684\u6597\u5ba4\u3002 \u3000\u3000\u53ef\u662f\u5bf9\u90ed\u5b9a\u6765\u8bf4\uff0c\u8fd9\u5c4b\u5b50\u91cc\u5df2\u53ea\u5269\u4e0b\u4e00\u7247\u65e0\u9645\u7684\u5bd2\u51b7\u548c\u9ed1\u6697\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u81ea\u5df1\u4e00\u751f\u4e2d\uff0c\u5df2\u6c38\u8fdc\u4e0d\u4f1a\u518d\u6709\u9633\u5149\u548c\u6e29\u6696\uff0c\u56e0\u4e3a\u5979\u8fd9\u4e00\u53bb\uff0c\u662f\u5fc5\u5b9a\u6c38\u8fdc\u518d\u4e5f\u4e0d\u4f1a \u56de\u6765\u7684\u4e86\u3002\u4ed6\u77e5\u9053\u81ea\u5df1\u6c38\u8fdc\u518d\u4e5f\u89c1\u4e0d\u5230\u5979\u3002 \u3000\u3000\u5973\u4eba\u8981\u5bf9\u4ed8\u7537\u4eba\uff0c\u663e\u7136\u6709\u5f88\u591a\u6cd5\u5b50\uff0c\u4f46\u662f\u5979\u8981\u53bb\u5bf9\u4ed8\u7684\u4eba\uff0c\u5374\u5b9e\u5728\u592a\u5371\u9669\uff0c\u592a\u53ef\u6015\u3002 \u3000\u3000\u4f55\u51b5\uff0c\u5c31\u7b97\u5979\u771f\u7684\u80fd\u5bf9\u4ed8\u4ed6\u4eec\uff0c\u5979\u81ea\u5df1\u4e5f\u7edd\u4e0d\u4f1a\u518d\u6d3b\u7740\u56de\u6765\u3002 \u3000\u3000\u56e0\u4e3a\u5979\u672c\u5c31\u51b3\u5fc3\u53bb\u6c42\u6b7b\u7684\u3002 \u3000\u3000\u5979\u523a\u4e86\u53f6\u5f00\u4e00\u5200\uff0c\u5979\u7684\u75db\u82e6\u548c\u6094\u6068\uff0c\u5df2\u53ea\u6709\u201c\u6b7b\u201d\u624d\u80fd\u89e3\u8131\u3002 \u3000\u3000\u5979\u65e9\u5df2\u51b3\u5fc3\u4ee5\u201c\u6b7b\u201d\u6765\u8d4e\u7f6a\u3002 \u3000\u3000\u73b0\u5728\u7389\u7bab\u548c\u5415\u8fea\u662f\u4e0d\u662f\u5df2\u7ecf\u5728\u9e3f\u5bbe\u5ba2\u6808\u91cc\u7b49\u7740\u5979\uff0c\u7b49\u7740\u5c06\u5979\u5bb0\u5272\uff1f \u3000\u3000\u50cf\u4ed6\u4eec\u90a3\u6837\u7684\u7537\u4eba\uff0c\u8981\u5bf9\u4ed8\u4e00\u4e2a\u5973\u4eba\uff0c\u4e5f\u6709\u5f88\u591a\u6cd5\u5b50\u7684\u3002 \u3000\u3000\u4ed6\u4eec\u4f1a\u7528\u51fa\u4ec0\u4e48\u6837\u7684\u6cd5\u5b50\u6765\uff1f \u3000\u3000\u60f3\u5230\u7389\u7bab\u7684\u4e11\u6076\uff0c\u5415\u8fea\u7684\u51b7\u9177\uff0c\u90ed\u5b9a\u5df2\u4e0d\u6562\u518d\u60f3\u4e0b\u53bb\u3002 \u3000\u3000\u5bd2\u51ac\u4e2d\u7684\u9633\u5149\uff0c\u6c38\u8fdc\u662f\u8f7b\u67d4\u6e29\u6696\u7684\uff0c\u5c31\u50cf\u662f\u60c5\u4eba\u7684\u629a\u6478\u3002 \u3000\u3000\u9633\u5149\u6070\u5de7\u8d34\u5728\u4ed6\u8138\u4e0a\uff0c\u4ed6\u7684\u6cea\u5df2\u6d41\u4e0b\u6765\u3002 \u3000\u3000\u6b63\u5348\uff0c\u9e3f\u5bbe\u5ba2\u6808\u3002 \u3000\u3000\u4e01\u7075\u7433\u8d70\u8fdb\u53bb\u7684\u65f6\u5019\uff0c\u9633\u5149\u5df2\u7167\u5728\u5916\u9762\u90a3\u7eff\u8272\u7684\u91d1\u5b57\u62db\u724c\u4e0a\u3002 \u3000\u3000\u5979\u8eab\u4e0a\u5e76\u6ca1\u6709\u6234\u7740\u5979\u7684\u593a\u547d\u91d1\u94c3\uff0c\u4e5f\u6ca1\u6709\u5e26\u4efb\u4f55\u6b66\u5668\u3002 \u3000\u3000\u4eca\u5929\u5979\u51c6\u5907\u8981\u7528\u7684\u6b66\u5668\uff0c\u662f\u5979\u7684\u51b3\u5fc3\uff0c\u5979\u7684\u52c7\u6c14\uff0c\u5979\u7684\u667a\u6167\u4e0e\u7f8e\u4e3d\u3002 \u3000\u3000\u5979\u5bf9\u81ea\u5df1\u5145\u6ee1\u4e86\u81ea\u4fe1\u3002 \u3000\u3000\u4e16\u4e0a\u4e5f\u4e0d\u77e5\u6709\u591a\u5c11\u7537\u4eba\uff0c\u662f\u6b7b\u5728\u5973\u4eba\u8fd9\u79cd\u6b66\u5668\u4e0b\u7684\u3002 \u3000\u3000\u5979\u7684\u786e\u662f\u4e2a\u975e\u5e38\u7f8e\u4e3d\u7684\u5973\u4eba\uff0c\u800c\u4e14\u4eca\u5929\u53c8\u523b\u610f\u6253\u626e\u8fc7\u3002 \u3000\u3000\u770b\u89c1\u5979\u8d70\u8fdb\u53bb\uff0c\u7537\u4eba\u7684\u773c\u775b\u91cc\u90fd\u4e0d\u7981\u9732\u51fa\u7231\u6155\u548c\u6b32\u671b\u3002 \u3000\u3000\u53ea\u6709\u90a3\u5584\u826f\u7684\u8001\u638c\u67dc\uff0c\u5374\u663e\u5f97\u6709\u4e9b\u5fe7\u8651\u62c5\u5fc3\uff0c\u4eff\u4f5b\u5df2\u770b\u51fa\u4eca\u5929\u5fc5\u5c06\u6709\u707e\u7978\u964d\u5230\u8fd9\u5e74\u8f7b\u7684 \u5973\u5b69\u5b50\u8eab\u4e0a\u201c\u6700\u8fd1\u4ed6\u770b\u89c1\u7684\u51f6\u6740\u548c\u7978\u4e8b\u5df2\u5927\u591a\u3002\u4e01\u7075\u7433\u4e00\u8fdb\u95e8\uff0c\u4ed6\u5c31\u4ece\u67dc\u53f0\u91cc\u8fce\u51fa\u6765\uff0c\u52c9\u5f3a \u4f5c\u51fa\u7b11\u8138\uff0c\u95f4\u9053\uff1a\u201c\u662f\u4e0d\u662f\u4e01\u59d1\u5a18\uff1f\u201d \u3000\u3000\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u201c\u4e86\u59d1\u5a18\uff0c\u4f60\u7684\u4e24\u4f4d\u5ba2\u4eba\uff0c\u5df2\u7ecf\u5728\u540e\u9662\u91cc\u7b49\u7740\u3002\u201d \u3000\u3000\u7389\u7bab\u548c\u5415\u8fea\u5c45\u7136\u771f\u7684\u5168\u90e8\u6765\u4e86\u3002 \u3000\u3000\u4e01\u7075\u7433\u5ffd\u7136\u53d1\u89c9\u81ea\u5df1\u7684\u5fc3\u5728\u8df3\uff0c\u8df3\u5f97\u5f88\u5feb\u3002 \u3000\u3000\u867d\u7136\u5979\u5df2\u4e0b\u4e86\u5fc5\u6b7b\u7684\u51b3\u5fc3\uff0c\u4f46\u5374\u8fd8\u662f\u4e0d\u80fd\u4e0d\u7d27\u5f20\u3002 \u3000\u3000\u5979\u5f53\u7136\u4e5f\u77e5\u9053\u8fd9\u4e24\u4e2a\u4eba\u7684\u5371\u9669\u548c\u53ef\u6015\u3002 \u3000\u3000\u201c\u6765\u7684\u53ea\u6709\u4e24\u4e2a\u4eba\uff1f\u201d \u3000\u3000\u8001\u638c\u67dc\u70b9\u70b9\u5934\uff0c\u5ffd\u7136\u538b\u4f4e\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u59d1\u5a18\u82e5\u662f\u6ca1\u4ec0\u4e48\u8981\u7d27\u7684\u4e8b\uff0c\u4e0d\u5982\u8fd8\u662f\u56de\u53bb\u5427\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u660e\u77e5\u662f\u6211\u7ea6\u4ed6\u4eec\u6765\u7684\uff0c\u4e3a\u4ec0\u4e48\u8981\u6211\u56de\u53bb\uff01\u201d \u3000\u3000\u8001\u638c\u67dc\u8fdf\u7591\u7740\uff1a\u201c\u56e0\u4e3a\u2026\u2026\u201d \u3000\u3000\u4ed6\u7ec8\u4e8e\u8fd8\u662f\u6ca1\u6709\u8bf4\u51fa\u5fc3\u91cc\u7684\u5fe7\u8651\u548c\u6050\u60e7\uff0c\u53ea\u4e0d\u8fc7\u8f7b\u8f7b\u5730\u53f9\u4e86\u53e3\u6c14\u3002\u4e01\u7075\u7433\u5df2\u5fae\u7b11\u7740\u8d70\u8fdb \u53bb\uff0c\u5fc3\u91cc\u5374\u5e76\u4e0d\u662f\u4e0d\u77e5\u9053\u8fd9\u8001\u4eba\u7684\u597d\u610f\u3002 \u3000\u3000\u53ef\u662f\u5979\u5df2\u6ca1\u6709\u7b2c\u4e8c\u6761\u8def\u8d70\uff0c\u5c31\u7b97\u660e\u77e5\u5728\u91cc\u9762\u7b49\u7740\u5979\u7684\u662f\u6bd2\u86c7\u6076\u9b3c\uff0c\u5979\u4e5f\u975e\u53bb\u4e0d\u53ef\u3002 \u3000\u3000\u540e\u9662\u91cc\u521a\u6253\u626b\u8fc7\uff0c\u5385\u5802\u5df2\u6253\u626b\u5e72\u51c0\uff0c\u5730\u4e0a\u5149\u79c3\u79c3\u7684\uff0c\u663e\u5f97\u66f4\u8352\u5bd2\u51b7\u843d\u3002 \u3000\u3000\u201c\u90a3\u4e24\u4f4d\u5ba2\u4eba\u5c31\u5728\u5385\u91cc\u3002\u201d\u5e26\u8def\u7684\u4f19\u8ba1\u8bf4\u8fc7\u8fd9\u53e5\u8bdd\uff0c\u7acb\u523b\u5c31\u6084\u4fcf\u9000\u51fa\u9662\u5b50\u3002 \u3000\u3000\u4ed6\u663e\u7136\u5df2\u770b\u51fa\u4eca\u5929\u8fd9\u7ea6\u4f1a\u5e76\u4e0d\u662f\u597d\u73a9\u7684\u3002 \u3000\u3000\u5ba2\u5385\u7684\u95e8\u5f00\u7740\uff0c\u91cc\u9762\u5e76\u65e0\u4eba\u58f0\uff0c\u738b\u7bab\u9053\u4eba\u548c\u5415\u8fea\u90fd\u4e0d\u662f\u559c\u6b22\u8bf4\u8bdd\u7684\u4eba\uff0c\u66f4\u4e0d\u559c\u6b22\u7b11\u3002 \u3000\u3000\u4ed6\u4eec\u7b11\u7684\u65f6\u5019\uff0c\u901a\u5e38\u90fd\u53ea\u56e0\u4e3a\u4ed6\u4eec\u8981\u6740\u7684\u4eba\uff0c\u5df2\u6b7b\u5728\u4ed6\u4eec\u9762\u524d\u3002 \u3000\u3000\u4e01\u7075\u7433\u6df1\u6df1\u5730\u5438\u4e86\u53e3\u6c14\uff0c\u8138\u4e0a\u9732\u51fa\u6700\u751c\u871c\u7684\u7b11\u5bb9\uff0c\u7528\u6700\u4f18\u96c5\u7684\u59ff\u6001\u8d70\u8fdb\u53bb\u3002 \u3000\u3000\u5728\u91cc\u9762\u7b49\u7740\u4ed6\u7684\uff0c\u679c\u7136\u6b63\u662f\u7389\u7bab\u9053\u4eba\u548c\u5415\u8fea\u3002 \u3000\u3000\u8fd9\u5c4b\u5b50\u91cc\u4e5f\u53ea\u6709\u9633\u5149\uff0c\u4f46\u65e0\u8bba\u8c01\u53ea\u8981\u4e00\u8d70\u8fdb\u6765\uff0c\u90fd\u7acb\u523b\u4f1a\u89c9\u5f97\u81ea\u5df1\u597d\u50cf\u662f\u8d70\u4eba\u4e86\u4e2a\u51b0\u7a96 \u91cc\u3002 \u3000\u3000\u7389\u7bab\u9053\u4eba\u5c31\u5750\u5728\u8fce\u95e8\u7684\u4e00\u5f20\u6905\u5b50\u4e0a\uff0c\u4ed6\u8981\u5750\u4e0b\u6765\uff0c\u9009\u7684\u6c38\u8fdc\u90fd\u662f\u6700\u8212\u670d\u7684\u4e00\u5f20\u501a\u5b50\u3002 \u3000\u3000\u4ed6\u7684\u670d\u9970\u8fd8\u662f\u90a3\u4e48\u534e\u4e3d\uff0c\u770b\u6765\u8fd8\u662f\u90a3\u4e48\u8dbe\u9ad8\u6c14\u626c\uff0c\u4e0d\u53ef\u4e00\u4e16\u3002 \u3000\u3000\u5c4b\u5b50\u91cc\u867d\u7136\u53e6\u5916\u8fd8\u6709\u4e00\u4e2a\u4eba\uff0c\u4ed6\u5374\u597d\u50cf\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u4ed6\u6839\u672c\u5c31\u4ece\u672a\u5c06\u4efb\u4f55\u4eba\u770b\u5728\u773c\u91cc\u3002 \u3000\u3000\u5415\u8fea\u5374\u5728\u770b\u7740\u4ed6\uff0c\u8138\u4e0a\u7684\u8868\u60c5\uff0c\u5c31\u597d\u50cf\u4e00\u4e2a\u6f20\u4e0d\u5173\u5fc3\u7684\u6e38\u4eba\uff0c\u6b63\u7ad9\u5728\u517d\u680f\u91cc\uff0c\u770b\u7740\u4e00\u6761 \u5df2\u5782\u8001\u7684\u72ee\u5b50\u5728\u7b3c\u4e2d\u5411\u4ed6\u8000\u6b66\u626c\u5a01\u4e00\u6837\u3002 \u3000\u3000\u4ed6\u82cd\u767d\u7684\u8138\u4e0a\uff0c\u5e26\u7740\u79cd\u51b7\u6f20\u8f7b\u8511\u7684\u4e0d\u5c51\u4e4b\u8272\uff0c\u56e0\u4e3a\u4ed6\u77e5\u9053\u8fd9\u6761\u72ee\u5b50\u7684\u76ae\u6bdb\u867d\u534e\u4e3d\uff0c\u4f46\u662f \u7259\u5df1\u949d\uff0c\u722a\u5df2\u79c3\uff0c\u5df2\u6839\u672c\u65e0\u6cd5\u5a01\u80c1\u4ed6\u3002 \u3000\u3000\u4ed6\u7684\u795e\u8272\u51b7\u6f20\uff0c\u88c5\u675f\u7b80\u6734\uff0c\u5c4b\u5b50\u91cc\u867d\u7136\u8fd8\u6709\u540c\u6837\u8212\u670d\u7684\u6905\u5b50\uff0c\u4ed6\u5374\u5b81\u613f\u7ad9\u7740\u3002 \u3000\u3000\u4e01\u7075\u7433\u7ad9\u5728\u95e8\u53e3\uff0c\u770b\u7740\u4ed6\u4eec\uff0c\u7b11\u5f97\u66f4\u751c\u871c\u3002\u8fd9\u4e24\u4e2a\u6b63\u662f\u6781\u9c9c\u660e\u5f3a\u70c8\u7684\u5bf9\u6bd4\uff0c\u5979\u7b2c\u4e00\u773c\u770b \u89c1\u4ed6\u4eec\uff0c\u5c31\u77e5\u9053\u4ed6\u4eec\u7edd\u4e0d\u80fd\u548c\u5e73\u5171\u5904\u7684\u3002 \u3000\u3000\u201c\u6211\u59d3\u4e01\u3002\u201d\u5979\u5fae\u7b11\u7740\u8d70\u8fdb\u95e8\uff1a\u201c\u53eb\u4e01\u7075\u7433\u3002\u201d \u3000\u3000\u7389\u8427\u9053\u4eba\u51b7\u51b7\u9053\uff1a\u201c\u6211\u8ba4\u5f97\u4f60\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4f4d\u5f7c\u6b64\u4e5f\u8ba4\u5f97\uff1f\u201d \u3000\u3000\u7389\u7bab\u9053\u4eba\u505a\u7136\u9053\uff1a\u201c\u4ed6\u5e94\u8be5\u77e5\u9053\u6211\u662f\u8c01\u3002\u201d\u4ed6\u7684\u624b\u5728\u8f7b\u629a\u7740\u4ed6\u7684\u767d\u7389\u7bab\uff1a\u201c\u4ed6\u5e94\u8be5\u8ba4\u5f97 \u8fd9\u7ba1\u7bab\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u7b11\u4e86\uff1a\u201c\u662f\u4e0d\u662f\u6bcf\u4e2a\u4eba\u90fd\u5e94\u8be5\u8ba4\u5f97\u8fd9\u7ba1\u7bab\uff1f\u5426\u5219\u5c31\u8be5\u6b7b\uff1f\u201d \u3000\u3000\u5979\u7528\u773c\u89d2\u779f\u7740\u5415\u8fea\uff0c\u5415\u8fea\u8138\u4e0a\u5b8c\u5168\u6ca1\u6709\u8868\u60c5\u3002 \u3000\u3000\u4ed6\u663e\u7136\u5e76\u4e0d\u662f\u4e2a\u5bb9\u6613\u88ab\u6253\u52a8\u7684\u4eba\u3002 \u3000\u3000\u4e01\u7075\u7433\u773c\u73e0\u5b50\u8f6c\u4e86\u8f6c\uff0c\u5ae3\u7136\u9053\uff1a\u201c\u6211\u5b9e\u5728\u60f3\u4e0d\u5230\u5415\u516c\u5b50\u4e5f\u4f1a\u6765\u7684\uff0c\u6211\u2026\u2026\u201d \u3000\u3000\u5415\u8fea\u5ffd\u7136\u6253\u65ad\u4e86\u5979\u7684\u8bdd\uff0c\u6de1\u6de1\u9053\uff1a\u201c\u4f60\u5e94\u8be5\u60f3\u5f97\u5230\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5415\u8fea\u9053\uff1a\u201c\u4e0a\u5b98\u91d1\u8679\u7559\u4e0b\u6765\u7684\u5b9d\u85cf\u548c\u79d8\u7b08\uff0c\u672c\u5c31\u5f88\u4ee4\u4eba\u52a8\u5fc3\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u5415\u516c\u5b50\u4e5f\u52a8\u4e86\u5fc3\uff1f\u201d \u3000\u3000\u5415\u8fea\u9053\uff1a\u201c\u6211\u4e5f\u662f\u4eba\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u90a3\u5b9d\u85cf\u548c\u79d8\u7b08\u7684\u5730\u70b9\uff0c\u5415\u516c\u5b50\u4e5f\u7edd\u4e0d\u4f1a\u77e5\u9053\u7684\u3002\u201d \u3000\u3000\u5415\u8fea\u627f\u8ba4\u3002 \u3000\u3000\u4e01\u7075\u7433\u7684\u773c\u775b\u53d1\u7740\u5149\uff0c\u9053\uff1a\u201c\u4f46\u6211\u5374\u77e5\u9053\uff0c\u53ea\u6709\u6211\u77e5\u9053\u3002\u201d \u3000\u3000\u5415\u8fea\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u8fd9\u79d8\u5bc6\u6211\u672c\u4e0d\u613f\u8bf4\u51fa\u6765\u7684\uff0c\u4f46\u73b0\u5728\u5374\u5df2\u4e0d\u80fd\u4e0d\u8bf4\u3002\u201d \u3000\u3000\u5415\u8fea\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e01\u7075\u7433\u53f9\u4e86\u53e3\u6c14\uff0c\u7b11\u5f97\u4eff\u4f5b\u5df2\u6709\u70b9\u51c4\u51c9\uff1a\u201c\u56e0\u4e3a\u73b0\u5728\u53f6\u5f00\u5df2\u6b7b\u4e86\uff0c\u5c31\u51ed\u6211\u4e00\u4e2a\u4eba\u7684\u529b \u91cf\uff0c\u662f\u7edd\u5bf9\u6ca1\u6cd5\u5b50\u5f97\u5230\u90a3\u5b9d\u85cf\u7684\u3002\u201d \u3000\u3000\u660c\u8feb\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u627e\u6211\u4eec\u6765\uff1f\u201d \u3000\u3000\u4e01\u7075\u7433\u70b9\u70b9\u5934\uff1a\u201c\u6211\u7b97\u6765\u7b97\u53bb\uff0c\u5929\u4e0b\u7684\u82f1\u96c4\u8c6a\u6770\uff0c\u7edd\u6ca1\u6709\u4efb\u4f55\u4eba\u80fd\u6bd4\u5f97\u4e0a\u4e24\u4f4d\u3002\u201d \u3000\u3000\u5415\u8fea\u53ea\u4e0d\u8fc7\u5728\u542c\u7740\uff0c\u7389\u7bab\u5374\u5728\u51b7\u7b11\u3002 \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u4eca\u5929\u6211\u8bf7\u4e24\u4f4d\u6765\uff0c\u5c31\u4e3a\u4e86\u8981\u5c06\u8fd9\u79d8\u5bc6\u544a\u8bc9\u4e24\u4f4d\uff0c\u56e0\u4e3a\u2026\u201d \u3000\u3000\u5415\u8fea\u7a81\u7136\u53c8\u6253\u65ad\u4e86\u5979\u7684\u8bdd\uff1a\u201c\u4f60\u4e0d\u5fc5\u544a\u8bc9\u627e\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u6014\u4e86\u6014\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5415\u8fea\u6de1\u6de1\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u5df2\u4e0d\u60f3\u77e5\u9053\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u6014\u4f4f\uff0c\u7b11\u5bb9\u4f3c\u5df2\u50f5\u786c\u3002 \u3000\u3000\u5415\u8fea\u9053\uff1a\u201c\u4f46\u6211\u5374\u77e5\u9053\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u5fcd\u4e0d\u4f4f\u95ee\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u5415\u8fea\u9053\uff1a\u201c\u5047\u5982\u6709\u4e24\u4e2a\u4eba\u540c\u65f6\u77e5\u9053\u8fd9\u79d8\u5bc6\uff0c\u80fd\u6d3b\u7740\u8d70\u51fa\u53bb\u7684\uff0c\u5c31\u5fc5\u5b9a\u53ea\u6709\u4e00\u4e2a\u3002\u201d\u3001\u4e01 \u7075\u7433\u5374\u5df2\u7b11\u4e0d\u51fa\u4e86\u3002 \u3000\u3000\u5415\u8fea\u5374\u7b11\u4e86\u7b11\u9053\uff1a\u201c\u90a3\u5b9d\u85cf\u867d\u4eca\u4eba\u52a8\u5fc3\uff0c\u4f46\u6211\u5374\u4e0d\u60f3\u4e3a\u4e86\u5b83\u548c\u4e1c\u6d77\u7389\u7bab\u62fc\u547d\u3002\u201d \u3000\u3000\u7389\u7bab\u9053\u4eba\u5ffd\u7136\u4e5f\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u770b\u6765\u4f60\u662f\u4e2a\u806a\u660e\u4eba\u3002\u201d \u3000\u3000\u5415\u8fea\u9053\uff1a\u201c\u9053\u957f\u4e5f\u5df2\u660e\u767d\u4e86\u5979\u7684\u610f\u601d\uff1f\u201d \u3000\u3000\u7389\u7bab\u9053\u4eba\u9053\uff1a\u201c\u5979\u4e0d\u5982\u4f60\u806a\u660e\u3002\u201d \u3000\u3000\u5415\u8fea\u9053\uff1a\u201c\u53ef\u662f\u5979\u4e5f\u4e0d\u592a\u7b28\uff0c\u800c\u4e14\u5f88\u7f8e\u3002\u201d \u3000\u3000\u7389\u7bab\u9053\u4eba\u9053\uff1a\u201c\u5979\u603b\u662f\u559c\u6b22\u81ea\u4f5c\u806a\u660e\uff0c\u6211\u4e00\u5411\u4e0d\u559c\u6b22\u81ea\u4f5c\u806a\u660e\u7684\u5973\u4eba\u3002\u201d \u3000\u3000\u5415\u8fea\u5fae\u7b11\u9053\uff1a\u201c\u4e16\u4e0a\u7684\u5973\u4eba\uff0c\u53c8\u6709\u51e0\u4e2a\u4e0d\u559c\u6b22\u81ea\u4f5c\u806a\u660e\uff1f\u201d \u3000\u3000\u7389\u7bab\u9053\u4eba\u76ee\u5149\u9489\u5b50\u822c\u7684\u76ef\u5728\u4ed6\u8138\u4e0a\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u7a76\u7adf\u60f3\u8bf4\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5415\u8fea\u6de1\u6de1\u9053\uff1a\u201c\u6211\u53ea\u4e0d\u8fc7\u5728\u63d0\u9192\u9053\u957f\uff0c\u50cf\u5979\u8fd9\u6837\u7684\u5973\u4eba\uff0c\u4e16\u4e0a\u5e76\u4e0d\u591a\u3002\u201d \u3000\u3000\u7389\u7bab\u9053\u4eba\u4e0d\u7531\u81ea\u4e3b\u770b\u4e86\u4e01\u7075\u7433\u4e24\u773c\uff0c\u773c\u775b\u91cc\u4e5f\u4e0d\u7981\u9732\u51fa\u8d5e\u8d4f\u4e4b\u8272\uff0c\u5ffd\u7136\u53f9\u4e86\u53e3\u6c14\uff0c\u5583\u5583 \u9053\uff1a\u201c\u53ef\u60dc\u3001\u5b9e\u5728\u53ef\u60dc\u3002\u201d \u3000\u3000\u5415\u8fea\u9053\uff1a\u201c\u53ef\u60dc\uff1f\u201d \u3000\u3000\u738b\u7bab\u9053\u4eba\u9053\uff1a\u201c\u4e00\u67c4\u5251\u82e5\u5df2\u6709\u4e86\u7f3a\u53e3\uff0c\u4f60\u770b\u4e0d\u770b\u5f97\u51fa\uff1f\u201d \u3000\u3000\u5415\u8fea\u70b9\u70b9\u5934\u3002 \u3000\u3000\u7389\u7bab\u9053\u4eba\u9053\uff1a\u201c\u8fd9\u5973\u4eba\u5df2\u6709\u7f3a\u53e3\u3002\u201d \u3000\u3000\u5415\u8fea\u9053\uff1a\u201c\u4f60\u770b\u5f97\u51fa\uff1f\u201d \u3000\u3000\u4ed6\u5f53\u7136\u660e\u767d\u7389\u7bab\u9053\u4eba\u7684\u610f\u601d\uff0c\u4e01\u7075\u7433\u548c\u53f6\u5f00\u7684\u5173\u7cfb\uff0c\u65e9\u5df2\u4e0d\u662f\u79d8\u5bc6\u3002 \u3000\u3000\u7389\u7bab\u9053\u4eba\uff1a\u201c\u6211\u82e5\u770b\u4e0d\u51fa\uff0c\u5979\u4e0a\u6b21\u843d\u5728\u6211\u624b\u91cc\uff0c\u6211\u5df2\u4e0d\u4f1a\u653e\u8fc7\u5979\u3002\u201d \u3000\u3000\u5415\u8fea\u4e5f\u66fe\u542c\u8bf4\uff0c\u90ed\u5d69\u9633\u4ece\u4e0d\u7528\u6709\u7f3a\u53e3\u7684\u5251\uff0c\u7389\u7bab\u4ece\u4e0d\u7528\u6709\u8fc7\u7537\u4eba\u7684\u5973\u4eba\u3002 \u3000\u3000\u4ed6\u770b\u7740\u7389\u7bab\u9053\u4eba\uff0c\u4e0d\u518d\u5f00\u53e3\uff0c\u773c\u775b\u91cc\u53c8\u9732\u51fa\u79cd\u8ba5\u8bbd\u7684\u7b11\u610f\u3002 \u3000\u3000\u7389\u7bab\u9053\u4eba\u9053\uff1a\u201c\u4f60\u8fd8\u4e0d\u61c2\uff1f\u201d \u3000\u3000\u5415\u8fea\u9053\uff1a\u201c\u6211\u53ea\u4e0d\u8fc7\u5728\u5947\u602a\u3002\u201d \u3000\u3000\u5415\u8fea\u9053\uff1a\u201c\u5947\u602a\u4f60\u4e3a\u4ec0\u4e48\u9009\u8fd9\u5f20\u6905\u5b50\u5750\u4e0b\u6765\uff1f\uff1f\u738b\u7bab\u9053\u5165\u9053\uff1a\u201c\u4f60\u5e94\u8be5\u770b\u5f97\u51fa\uff0c\u8fd9\u5730\u65b9 \u53ea\u6709\u8fd9\u5f20\u6905\u5b50\u6700\u597d\u3002\u201d \u3000\u3000\u5415\u8fea\u6de1\u6de1\u9053\uff1a\u201c\u6211\u770b\u5f97\u51fa\uff0c\u53ef\u662f\u6211\u4e5f\u77e5\u9053\uff0c\u8fd9\u6905\u5b50\u4ee5\u524d\u4e00\u5b9a\u4e5f\u6709\u4eba\u4eba\u5750\u8fc7\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u7ed3\u675f\u4e86\u8fd9\u6b21\u8c08\u8bdd\uff0c\u5ffd\u7136\u4ece\u4e01\u7075\u7433\u8eab\u65c1\u5927\u6b65\u8d70\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u4e01\u7075\u7433\u7684\u5fc3\u5728\u5f80\u4e0b\u6c89\uff0c\u8840\u4e5f\u5f80\u4e0b\u6c89\uff0c\u5168\u8eab\u90fd\u5df2\u51b0\u51b7\u3002 \u3000\u3000\u738b\u7bab\u9053\u4eba\u6b63\u5728\u770b\u7740\u5979\uff0c\u4ece\u5934\u770b\u5230\u811a\uff0c\u53c8\u4ece\u811a\u5c16\u518d\u6162\u6162\u5730\u770b\u5230\u5979\u7684\u773c\u775b\u3002 \u3000\u3000\u4ed6\u7684\u76ee\u5149\u4f3c\u5df2\u7a7f\u900f\u4e86\u5979\u7684\u8863\u670d\u3002 \u3000\u3000\u4e01\u7075\u7433\u53ea\u89c9\u5f97\u81ea\u5df1\u5c31\u597d\u50cf\u662f\u5b8c\u5168\u8d64\u88f8\u7740\u7684\u3002 \u3000\u3000\u5979\u5e76\u4e0d\u662f\u6ca1\u6709\u7ed9\u7537\u4eba\u770b\u8fc7\uff0c\u4f46\u73b0\u5728\u5979\u5374\u662f\u53d7\u4e0d\u4e86\uff0c\u7a81\u7136\u8f6c\u8eab\uff0c\u60f3\u51b2\u51fa\u53bb\u3002 \u3000\u3000\u5979\u5e76\u4e0d\u6015\u6b7b\uff0c\u53ef\u662f\u4e5f\u77e5\u9053\uff0c\u8fd9\u4e16\u4e0a\u8fd8\u6709\u4e9b\u8fdc\u6bd4\u6b7b\u66f4\u53ef\u6015\u7684\u4e8b\u3002 \u3000\u3000\u8c01\u77e5\u5979\u521a\u8f6c\u8eab\uff0c\u7389\u7bab\u9053\u4eba\u5df2\u5230\u4e86\u5979\u9762\u524d\uff0c\u80cc\u8d1f\u7740\u53cc\u624b\uff0c\u6321\u4f4f\u4e86\u5979\u7684\u53bb\u8def\uff0c\u8fd8\u662f\u7528\u540c\u6837\u7684 \u773c\u8272\u5728\u770b\u7740\u5979\u3002 \u3000\u3000\u4e01\u7075\u7433\u63e1\u7740\u53cc\u62f3\uff0c\u4e00\u6b65\u6b65\u540e\u9000\uff0c\u9000\u5230\u4ed6\u521a\u624d\u5750\u7684\u90a3\u5f20\u6905\u5b50\u4e0a\u5750\u4e0b\uff0c\u5ffd\u7136\u9053\uff1a\u201c\u6211\u2026\u2026\u6211 \u77e5\u9053\u4f60\u7edd\u4e0d\u4f1a\u78b0\u6211\u7684\u3002\u201d \u3000\u3000\u7389\u8427\u9053\u4eba\u9053\uff1a\u201c\u54e6\uff1f\u2019\u4e01\u7075\u7433\u9053\uff1a\u201c\u6211\u7684\u786e\u5df2\u6709\u4e86\u7f3a\u53e3\uff0c\u800c\u4e14\u8fd8\u662f\u5f88\u5927\u7684\u7f3a\u53e3\u3002\u201d \u3000\u3000\u7389\u7bab\u9053\u4eba\u7b11\u4e86\uff0c\u5fae\u7b11\u7740\u9053\uff1a\u201c\u6211\u672c\u6765\u4ee5\u4e3a\u4f60\u5df2\u957f\u5927\u4e86\uff0c\u56e0\u4e3a\u4f60\u4eca\u5929\u8981\u6765\u505a\u7684\uff0c\u672c\u662f\u5927\u4eba \u505a\u7684\u4e8b\uff0c\u73b0\u5728\u6211\u624d\u77e5\u9053\u4f60\u5b9e\u5728\u8fd8\u662f\u4e2a\u5b69\u5b50\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u4ece\u4e0d\u80af\u627f\u8ba4\u81ea\u5df1\u662f\u4e2a\u5b69\u5b50\uff0c\u5c24\u5176\u5728\u53f6\u5f00\u9762\u524d\u66f4\u4e0d\u80af\u3002 \u3000\u3000\u4f46\u73b0\u5728\u5979\u5374\u53ea\u6709\u627f\u8ba4\u3002 \u3000\u3000\u7389\u7bab\u9053\u4eba\u60a0\u60a0\u9053\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\uff0c\u5b69\u5b50\u8981\u505a\u5927\u4eba\u7684\u4e8b\uff0c\u603b\u662f\u5371\u9669\u5f97\u5f88\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u9f13\u8d77\u52c7\u6c14\uff0c\u9053\uff1a\u201c\u6211\u5374\u770b\u4e0d\u51fa\u73b0\u5728\u6709\u4ec0\u4e48\u5371\u9669\u3002\u201d \u3000\u3000\u7389\u7bab\u9053\u4eba\u9053\uff1a\u201c\u672c\u6765\u6211\u7684\u786e\u4ece\u4e0d\u78b0\u5df2\u6709\u8fc7\u7537\u4eba\u7684\u5973\u4eba\uff0c\u5bf9\u4f60\u5374\u53ef\u4ee5\u7834\u4f8b\u4e00\u6b21\u3002\u3001\u4e01\u7075\u7433 \u5df2\u4e0d\u80fd\u52a8\uff0c\u4ece\u811a\u5c16\u5230\u6307\u5c16\u90fd\u5df2\u4e0d\u80fd\u52a8\uff0c\u8fde\u5934\u90fd\u4e0d\u80fd\u52a8\u3002\u7389\u7bab\u9053\u4eba\u770b\u7740\u5979\u7684\u8138\u8272\u5df2\u53d8\u4e86\u3002\u4e01\u7075 \u7433\u53ea\u89c9\u5f97\u4ed6\u7684\u773c\u775b\u91cc\u4eff\u4f5b\u5ffd\u7136\u6709\u4e86\u79cd\u5947\u5f02\u7684\u5438\u5f15\u529b\uff0c\u5438\u5f15\u4f4f\u5979\u7684\u76ee\u5149\uff0c\u5c06\u5979\u7684\u6574\u4e2a\u4eba\u90fd\u5438 \u4f4f\u3002\u5979\u60f3\u6323\u624e\uff0c\u60f3\u9003\u907f\uff0c\u5374\u53ea\u80fd\u75f4\u75f4\u5730\u5750\u5728\u90a3\u91cc\uff0c\u770b\u7740\u4ed6\u3002\u4ed6\u7684\u773c\u775b\u91cc\u4eff\u4f5b\u5728\u95ea\u52a8\u7740\u78a7\u5149\uff0c \u5c31\u50cf\u662f\u5ffd\u7136\u4eae\u8d77\u4e86\u4e00\u70b9\u9b3c\u706b\u3002\u4e86\u7075\u7433\u770b\u7740\u8fd9\u53cc\u773c\u775b\uff0c\u7ec8\u4e8e\u5b8c\u5168\u60f3\u8d77\u4e86\u4e0a\u6b21\u7684\u4e8b\u3002\u201d\u2026\u2026\u53bb\u6740 \u53f6\u5f00\uff01\u62ff\u8fd9\u628a\u5200\u53bb\u6740\u53f6\u5f00\u3002\u201d \u3000\u3000\u8fd9\u6b21\u4ed6\u8981\u5979\u505a\u7684\u4e8b\uff0c\u662f\u4e0d\u662f\u6bd4\u4e0a\u4e00\u6b21\u66f4\u53ef\u6015\uff1f \u3000\u3000\u5979\u5df2\u7528\u5c3d\u4e86\u5168\u8eab\u529b\u6c14\u6323\u624e\uff0c\u51b7\u6c57\u5df2\u6e7f\u900f\u4e86\u5979\u7684\u8863\u670d\uff0c\u4f46\u5979\u5374\u8fd8\u6446\u4e0d\u8131\u3002 \u3000\u3000\u7389\u7bab\u9053\u4eba\u773c\u4e2d\u7684\u90a3\u70b9\u9b3c\u706b\uff0c\u4f3c\u5df2\u5c06\u5979\u6700\u540e\u7684\u4e00\u5206\u529b\u6c14\u90fd\u71c3\u5c3d\u3002 \u3000\u3000\u5979\u5df2\u53ea\u6709\u670d\u4ece\u3002 \u3000\u3000\u65e0\u8bba\u7389\u7bab\u9053\u4eba\u53eb\u5979\u505a\u4ec0\u4e48\uff0c\u5979\u90fd\u5df2\u5b8c\u5168\u65e0\u6cd5\u53cd\u6297\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u7a81\u542c\u201c\u7830\u201d\u7684\u4e00\u58f0\uff0c\u95e8\u7a81\u7136\u88ab\u649e\u5f00\uff0c\u4e00\u4e2a\u4eba\u6807\u67aa\u822c\u7ad9\u5728\u95e8\u5916\u3002 \u3000\u3000\u7389\u7bab\u9053\u4eba\u4e00\u60ca\uff0c\u56de\u8eab\u6012\u559d\uff1a\u201c\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u201c\u5d69\u9633\u90ed\u5b9a\u3002\u201d \u3000\u3000\u90ed\u5b9a\u6bd5\u7adf\u8fd8\u662f\u53ca\u65f6\u8d76\u6765\u4e86\u3002 \u3000\u3000\u4ed6\u600e\u4e48\u80fd\u6765\u7684\uff1f\u662f\u8c01\u89e3\u5f00\u4e86\u4ed6\u7684\u7a74\u9053\uff1f \u3000\u3000\u662f\u4e0a\u5b98\u5c0f\u4ed9\uff1f\u8fd8\u662f\u5415\u8fea\uff1f \u3000\u3000\u4ed6\u4eec\u5f53\u7136\u77e5\u9053\uff0c\u53ea\u8981\u90ed\u5b9a\u4e00\u5230\u8fd9\u91cc\uff0c\u4ed6\u548c\u7389\u7bab\u9053\u4eba\u4e4b\u95f4\u5c31\u5fc5\u5b9a\u53ea\u6709\u4e00\u4e2a\u80fd\u6d3b\u7740\u8d70\u51fa\u53bb\u3002 \u3000\u3000\u9633\u5149\u4e4d\u73b0\uff0c\u53c8\u6c89\u6ca1\u5728\u9634\u4e91\u91cc\uff0c\u9177\u5bd2\u53c8\u5f81\u670d\u4e86\u5927\u5730\u3002 \u3000\u3000\u51b7\u98ce\u5982\u5200\u3002 \u3000\u3000\u90ed\u5b9a\u548c\u7389\u7bab\u9053\u4eba\u5c31\u7ad9\u5728\u8fd9\u5200\u950b\u822c\u7684\u51b7\u98ce\u91cc\uff0c\u4e24\u4e2a\u4eba\u5fc3\u91cc\u4e5f\u90fd\u660e\u767d\uff0c\u4ed6\u4eec\u4e4b\u95f4\u5fc5\u5b9a\u8981\u6709\u4e00 \u4e2a\u5012\u4e0b\u53bb\u3002 \u3000\u3000\u65e0\u8bba\u8c01\u8981\u8d70\u51fa\u8fd9\u9662\u5b50\uff0c\u90fd\u53ea\u6709\u4e00\u6761\u8def\u2014\u2014\u4ece\u5bf9\u65b9\u7684\u5c38\u4f53\u4e0a\u8d70\u8fc7\u3002 \u3000\u3000\u90ed\u5b9a\u7684\u5251\u5df2\u5728\u624b\u3002 \u3000\u3000\u5251\u662f\u9edd\u9ed1\u7684\uff0c\u6697\u65e0\u5149\u534e\uff0c\u5374\u5e26\u7740\u79cd\u6bd4\u5bd2\u98ce\u66f4\u51db\u51bd\u7684\u6740\u6c14\u3002 \u3000\u3000\u8fd9\u67c4\u5251\u5c31\u50cf\u662f\u4ed6\u7684\u4eba\u4e00\u6837\u3002 \u3000\u3000\u7389\u7bab\u5374\u83b9\u767d\u5706\u6da6\u3002 \u3000\u3000\u8fd9\u4e24\u4e2a\u4eba\u6070\u5de7\u4e5f\u662f\u4e2a\u6781\u5f3a\u70c8\u9c9c\u660e\u7684\u5bf9\u6bd4\u3002 \u3000\u3000\u90ed\u5b9a\u51dd\u89c6\u7740\u4ed6\u624b\u91cc\u7684\u7389\u7bab\uff0c\u4e00\u76f4\u5728\u5c3d\u91cf\u907f\u514d\u63a5\u89e6\u5230\u4ed6\u7684\u773c\u775b\u3002 \u3000\u3000\u738b\u7bab\u9053\u4eba\u773c\u91cc\u7684\u6012\u706b\u53c8\u4eae\u8d77\uff0c\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u4f60\u662f\u90ed\u5d69\u9633\u7684\u540e\u4eba\uff1f\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u7389\u7bab\u9053\u4eba\u9053\uff1a\u201c\u4e8c\u5341\u5e74\u524d\uff0c\u6211\u5df2\u6709\u5fc3\u548c\u90ed\u5d69\u9633\u4e00\u8f83\u9ad8\u4f4e\uff0c\u53ea\u53ef\u60dc\u4ed6\u6b7b\u4e86\u3002\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u6211\u8fd8\u6d3b\u7740\u3002\u201d \u3000\u3000\u738b\u7bab\u9053\u4eba\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u7b97\u4ec0\u4e48\u4e1c\u897f\uff1f\u5d69\u9633\u94c1\u5251\uff0c\u5728\u5175\u5668\u8c31\u4e2d\u6392\u540d\u7b2c\u56db\uff0c\u4f60\u624b\u91cc\u7684\u5251\u5374\u8fde \u4e00\u6587\u90fd\u4e0d\u503c\u3002\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u7389\u7bab\u9053\u4eba\u9053\uff1a\u201c\u4f60\u6839\u672c\u4e0d\u914d\u7528\u8fd9\u67c4\u5251\u7684\u3002\u201d \u3000\u3000\u90ed\u5b9a\u95ed\u4e0a\u4e86\u5634\u3002 \u3000\u3000\u4ed6\u4e5f\u4e00\u76f4\u52c9\u5f3a\u63a7\u5236\u7740\u81ea\u5df1\u7684\u6012\u6c14\u3002 \u3000\u3000\u6124\u6012\u6709\u65f6\u867d\u7136\u4e5f\u662f\u79cd\u529b\u91cf\uff0c\u4f46\u5728\u4e0e\u9ad8\u624b\u76f8\u4e89\u65f6\uff0c\u5374\u5982\u6bd2\u836f\u822c\u6307\u4ee4\u4eba\u81f4\u547d\u3002 \u3000\u3000\u7389\u7bab\u9053\u4eba\u76ef\u7740\u4ed6\uff0c\u5f90\u5f90\u9053\uff1a\u201c\u636e\u8bf4\u4f60\u4e5f\u662f\u53f6\u5f00\u7684\u670b\u53cb\u3002\u201d \u3000\u3000\u90ed\u5b9a\u627f\u8ba4\u3002 \u3000\u3000\u7389\u7bab\u9053\u4eba\u9053\uff1a\u201c\u4f60\u4eec\u662f\u79cd\u4ec0\u4e48\u6837\u7684\u670b\u53cb\uff1f\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u670b\u53cb\u5c31\u662f\u670b\u53cb\uff0c\u771f\u6b63\u7684\u670b\u53cb\u53ea\u6709\u4e00\u79cd\u3002\u201d \u3000\u3000\u7389\u7bab\u9053\u4eba\u9053\uff1a\u201c\u4f46\u4f60\u4eec\u8fd9\u79cd\u670b\u53cb\u5374\u597d\u50cf\u5f88\u7279\u522b\u3002\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u7389\u7bab\u9053\u4eba\u51b7\u51b7\u9053\uff1a\u201c\u53f6\u5f00\u6b7b\u4e86\u540e\uff0c\u4f60\u5c45\u7136\u7acb\u523b\u5c31\u51c6\u5907\u63a5\u6536\u4ed6\u7684\u5973\u4eba\uff0c\u50cf\u4f60\u8fd9\u79cd\u670b\u53cb\uff0c\u5c82 \u975e\u5c11\u89c1\u5f97\u5f88\u3002\u201d \u3000\u3000\u90ed\u5b9a\u7a81\u7136\u89c9\u5f97\u4e00\u9635\u6012\u706b\u4e0a\u6d8c\uff0c\u5fcd\u4e0d\u4f4f\u62ac\u8d77\u4e86\u5934\u3002 \u3000\u3000\u7389\u7bab\u9053\u4eba\u7684\u773c\u775b\u6b63\u5728\u7b49\u7740\u4ed6\u3002 \u3000\u3000\u4ed6\u7684\u76ee\u5149\u7acb\u523b\u88ab\u5438\u4f4f\uff0c\u5c31\u50cf\u662f\u94c1\u9489\u9047\u5230\u4e86\u78c1\u77f3\u4e00\u6837\u3002 \u3000\u3000\u4e01\u7075\u7433\u4e00\u76f4\u5750\u5728\u6905\u5b50\u4e0a\uff0c\u5598\u606f\u7740\uff0c\u76f4\u5230\u6b64\u65f6\u624d\u8d70\u5230\u95e8\u53e3\u3002 \u3000\u3000\u5979\u770b\u89c1\u4e86\u7389\u7bab\u9053\u4eba\u7684\u773c\u775b\uff0c\u4e5f\u770b\u89c1\u4e86\u90ed\u5b9a\u7684\u773c\u775b\u3002 \u3000\u3000\u5979\u7684\u5fc3\u7acb\u523b\u53c8\u6c89\u4e0b\u3002 \u3000\u3000\u7389\u7bab\u9053\u4eba\u773c\u4e2d\u7684\u9b3c\u706b\uff0c\u8fdf\u65e9\u4e5f\u5fc5\u5b9a\u4f1a\u5c06\u90ed\u5b9a\u5168\u8eab\u7684\u529b\u91cf\u71c3\u5c3d\u3002 \u3000\u3000\u5979\u7edd\u4e0d\u80fd\u773c\u770b\u7740\u90ed\u5b9a\u8ddf\u5979\u4e00\u6837\u5f80\u4e0b\u6c89\uff0c\u6c89\u5165\u4e86\u4e07\u52ab\u4e0d\u590d\u7684\u6df1\u6e0a\u3002 \u3000\u3000\u600e\u5948\u5979\u5374\u504f\u504f\u53ea\u6709\u770b\u7740\u3002 \u3000\u3000\u73b0\u5728\u5979\u7edd\u4e0d\u80fd\u63d0\u9192\u90ed\u5b9a\uff0c\u90ed\u5b9a\u82e5\u662f\u5206\u5fc3\uff0c\u6b7b\u5f97\u5fc5\u5b9a\u66f4\u5feb\u3002 \u3000\u3000\u98ce\u66f4\u51b7\uff0c\u9634\u4e91\u4e2d\u4eff\u4f5b\u53c8\u6709\u96ea\u82b1\u98d8\u843d\u3002 \u3000\u3000\u96ea\u843d\u4e0b\u7684\u65f6\u5019\uff0c\u8840\u5f88\u53ef\u80fd\u4e5f\u5df2\u6e85\u51fa\u3002 \u3000\u3000\u5f53\u7136\u662f\u90ed\u5b9a\u7684\u8840\u3002\u4ed6\u672c\u4e0d\u5fc5\u548c\u7389\u7bab\u9053\u4eba\u62fc\u547d\u7684\uff0c\u4ed6\u672c\u6765\u53ef\u4ee5\u6d3b\u5f97\u5f88\u597d\uff0c\u5f88\u5feb\u4e50\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u4e3a\u4ec0\u4e48\u4f1a\u53d8\u6210\u8fd9\u6837\u5b50\uff1f \u3000\u3000\u4e01\u7075\u7433\u77e5\u9053\uff0c\u53ea\u6709\u5979\u77e5\u9053\u3002 \u3000\u3000\u2014\u2014\u8fd8\u6ca1\u6709\u4eab\u53d7\u5230\u7231\u60c5\u7684\u751c\u871c\uff0c\u5374\u5df2\u5c1d\u5c3d\u4e86\u7231\u60c5\u7684\u75db\u82e6\u3002 \u3000\u3000\u4e0a\u5929\u5bf9\u4ed6\u5c82\u975e\u4e0d\u516c\u5e73\uff1f \u3000\u3000\u4e01\u7075\u7433\u7684\u6cea\u5df1\u5c06\u843d\uff0c\u8fd8\u672a\u843d\uff0c\u7a81\u542c\u7389\u7bab\u9053\u4eba\u9053\uff1a\u201c\u629b\u4e0b\u4f60\u7684\u5251\uff0c\u8dea\u4e0b\u3002\u201d \u3000\u3000\u4ed6\u7684\u58f0\u97f3\u91cc\uff0c\u4e5f\u4eff\u4f5b\u5e26\u7740\u79cd\u5947\u5f02\u7684\u529b\u91cf\uff0c\u4e00\u79cd\u4ee4\u4eba\u65e0\u6cd5\u6297\u62d2\u7684\u529b\u91cf\u3002 \u3000\u3000\u90ed\u5b9a\u63e1\u5251\u7684\u624b\u5df2\u4e0d\u518d\u7a33\u5b9a\uff0c\u6574\u4e2a\u4eba\u90fd\u4f3c\u5df2\u5728\u53d1\u6296\u3002 \u3000\u3000\u7389\u7bab\u9053\u4eba\u6162\u6162\u9053\uff1a\u201c\u4f60\u4f55\u5fc5\u518d\u6323\u624e\uff1f\u4f55\u5fc5\u518d\u53d7\u82e6\uff1f\u53ea\u8981\u4f60\u4e00\u677e\u624b\uff0c\u6240\u6709\u7684\u75db\u82e6\u5c31\u5b8c\u5168\u8fc7 \u53bb\u4e86\u3002\u201d \u3000\u3000\u6b7b\u4eba\u5f53\u7136\u4e0d\u4f1a\u518d\u6709\u75db\u82e6\u3002 \u3000\u3000\u53ea\u8981\u4e00\u677e\u624b\uff0c\u5c31\u7acb\u523b\u53ef\u4ee5\u89e3\u8131\u3002 \u3000\u3000\u8fd9\u5b9e\u5728\u592a\u5bb9\u6613\u3002 \u3000\u3000\u90ed\u5b9a\u63e1\u5251\u7684\u624b\u80cc\u4e0a\uff0c\u9752\u7b4b\u521a\u521a\u6d88\u5931\uff0c\u529b\u91cf\u4e5f\u521a\u521a\u6d88\u5931\u3002 \u3000\u3000\u4ed6\u7684\u624b\u6b63\u6e10\u6e10\u5728\u653e\u677e\u2026\u2026 \u3000\u3000\u8fd9\u4e00\u6218\u5df2\u5c06\u8fc7\u53bb\uff0c\u4ed6\u5df2\u4e0d\u5fc5\u518d\u51fa\u624b\u3002 \u3000\u3000\u591a\u5e74\u6765\u4ed6\u4ece\u672a\u66fe\u4e0e\u4eba\u8fd1\u8eab\u8089\u640f\uff0c\u4ed6\u5df2\u5b66\u4f1a\u4e86\u66f4\u5bb9\u6613\u7684\u6cd5\u5b50\uff0c\u4e0d\u8d39\u5439\u7070\u4e4b\u529b\uff0c\u5c31\u53ef\u4ee5\u5c06\u5bf9 \u65b9\u51fb\u5012\u3002 \u3000\u3000\u8fd9\u4f7f\u4ed6\u53d8\u5f97\u66f4\u9a84\u50b2\uff0c\u4e5f\u53d8\u61d2\u4e86\u3002 \u3000\u3000\u4ed6\u5df2\u8d70\u60ef\u4e86\u8fd1\u8def\uff0c\u53ef\u662f\u8fd9\u6b21\u4ed6\u7ec8\u4e8e\u8d70\u9519\u4e86\u4e00\u6b65\u3002 \u3000\u3000\u8fd1\u8def\u7edd\u4e0d\u662f\u6b63\u8def\u3002 \u3000\u3000\u90ed\u5b9a\u624b\u91cc\u7684\u5251\u4f3c\u5df2\u5c06\u843d\u4e0b\uff0c\u7a81\u53c8\u63e1\u7d27\uff0c\u5251\u5149\u4e00\u95ea\uff0c\u98de\u51fb\u800c\u672a\u3002 \u3000\u3000\u5d69\u9633\u94c1\u5251\u7684\u5251\u6cd5\uff0c\u672c\u4e0d\u662f\u4ee5\u53d8\u5316\u82b1\u4fcf\u89c1\u957f\u7684\u3002 \u3000\u3000\u90ed\u5b9a\u7684\u5251\u6cd5\u4e5f\u4e00\u6837\u3002 \u3000\u3000\u6ca1\u6709\u628a\u63e1\u65f6\uff0c\u4ed6\u7edd\u4e0d\u51fa\u624b\uff0c\u53ea\u8981\u4e00\u5251\u523a\u51fa\uff0c\u5c31\u5fc5\u5b9a\u8981\u6709\u6548\u3002 \u3000\u3000\u7b80\u5355\uff0c\u8fc5\u901f\uff0c\u786e\u5b9e\uff0c\u6709\u6548\u3002 \u3000\u3000\u8fd9\u6b63\u662f\u201c\u5d69\u9633\u94c1\u5251\u201d\u5251\u6cd5\u7684\u7cbe\u534e\u6240\u5728\u3002 \u3000\u3000\u6240\u4ee5\u8fd9\u4e00\u5251\u5e76\u6ca1\u6709\u523a\u5411\u7389\u7bab\u9053\u4eba\u54bd\u5589\uff0c\u80f8\u819b\u7684\u9762\u79ef\uff0c\u8fdc\u6bd4\u54bd\u5589\u5927\u5f97\u591a\u3002 \u3000\u3000\u76ee\u6807\u7684\u9762\u79ef\u8d8a\u5927\uff0c\u8d8a\u4e0d\u5bb9\u6613\u5931\u624b\u3002 \u3000\u3000\u9ad8\u624b\u76f8\u4e89\uff0c\u53ea\u8981\u6709\u4e00\u70b9\u9519\u8bef\uff0c\u5c31\u5fc5\u5b9a\u662f\u81f4\u547d\u7684\u9519\u8bef\u3002 \u3000\u3000\u7389\u7bab\u9053\u4eba\u5df1\u5c06\u5168\u90e8\u7cbe\u795e\u529b\u91cf\uff0c\u90fd\u96c6\u4e2d\u5728\u4ed6\u7684\u773c\u775b\u4e0a\uff0c\u81ea\u4ee5\u4e3a\u5df2\u63a7\u5236\u4e86\u5168\u5c40\u3002 \u3000\u3000\u53ea\u53ef\u60dc\u773c\u775b\u5e76\u4e0d\u662f\u6b66\u5668\u3002 \u3000\u3000\u65e0\u8bba\u591a\u53ef\u6015\u7684\u773c\u775b\uff0c\u4e5f\u7edd\u5bf9\u65e0\u6cd5\u62b5\u6321\u4f4f\u8fd9\u96f7\u9706\u95ea\u7535\u822c\u7684\u4e00\u5251\u3002 \u3000\u3000\u4ed6\u6325\u624b\u626c\u8d77\u767d\u7389\u7bab\u65f6\uff0c\u5251\u950b\u5df2\u4ece\u4ed6\u7bab\u4e0b\u7a7f\u8fc7\uff0c\u523a\u5165\u4e86\u4ed6\u7684\u80f8\u819b\u3002 \u3000\u3000\u96ea\u82b1\u5f00\u59cb\u98d8\u843d\uff0c\u8840\u4e5f\u5df2\u6e85\u51fa\u3002 \u3000\u3000\u4f46\u5374\u4e0d\u662f\u90ed\u5b9a\u7684\u8840\u2014\u2014\u7389\u7bab\u9053\u4eba\u80f8\u819b\u91cc\u6e85\u51fa\u7684\u8840\uff0c\u4e5f\u540c\u6837\u662f\u9c9c\u7ea2\u7684\u3002 \u3000\u3000\u4ed6\u7684\u8138\u7acb\u523b\u626d\u66f2\uff0c\u773c\u775b\u51f8\u51fa\uff0c\u4f46\u773c\u4e2d\u7684\u9b3c\u706b\u5374\u5df2\u706d\u4e86\u3002 \u3000\u3000\u4ed6\u8fd8\u6ca1\u6709\u5012\u4e0b\u53bb\uff0c\u4e00\u53cc\u51f8\u51fa\u7684\u773c\u775b\uff0c\u8fd8\u5728\u72e0\u72e0\u5730\u77aa\u7740\u90ed\u5b9a\uff0c\u5ffd\u7136\u54fc\u58f0\u9053\uff1a\u201c\u4f60\u53eb\u90ed \u5b9a\uff1f\u201d \u3000\u3000\u90ed\u5b9a\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u9547\u5b9a\u7684\u5b9a\uff01\u201d \u3000\u3000\u7389\u7bab\u9053\u4eba\u957f\u53f9\u9053\uff1a\u201c\u4f60\u679c\u7136\u9547\u5b9a\uff0c\u6211\u5374\u770b\u8f7b\u4e86\u4f60\u3002\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u6211\u5374\u6ca1\u6709\u770b\u8f7b\u4f60\uff0c\u6211\u65e9\u5df2\u8ba1\u5212\u597d\u5bf9\u4ed8\u4f60\u7684\u6cd5\u5b50\u3002\uff0c\u7389\u7bab\u9053\u4eba\u60e8\u7b11\u9053\uff1a\u201c\u4f60\u7528 \u7684\u6cd5\u5b50\u5f88\u4e0d\u9519\u3002\u201d\u90ed\u5b9a\u9053\uff1a\u201c\u4f60\u7528\u7684\u6cd5\u5b50\u5374\u9519\u4e86\u3002\u201d \u3000\u3000\u7389\u7bab\u9053\u4eba\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u4ee5\u4f60\u7684\u6b66\u529f\uff0c\u672c\u4e0d\u5fc5\u7528\u8fd9\u79cd\u90aa\u9b54\u5916\u9053\u7684\u6cd5\u5b50\u6765\u5bf9\u4ed8\u6211\u3002\u201d \u3000\u3000\u7389\u7bab\u9053\u4eba\u4e00\u53cc\u773c\u775b\u7a7a\u8361\u8361\u51dd\u89c6\u7740\u8fdc\u65b9\uff0c\u6162\u6162\u9053\uff1a\u201c\u6211\u672c\u6765\u7684\u786e\u4e0d\u5fc5\u7528\u7684\uff0c\u53ea\u4e0d\u8fc7\u4e00\u4e2a\u4eba \u82e5\u662f\u5df2\u5b66\u4f1a\u4e86\u5bb9\u6613\u7684\u6cd5\u5b50\u6c42\u80dc\uff0c\u5c31\u4e0d\u613f\u518d\u8d39\u529b\u4e86\u2026\u2026\u201d \u3000\u3000\u4ed6\u8bf4\u5f97\u5f88\u6162\uff0c\u58f0\u97f3\u91cc\u4e5f\u5145\u6ee1\u4e86\u6094\u6068\u3002 \u3000\u3000\u76f4\u5230\u73b0\u5728\u4ed6\u624d\u660e\u767d\uff0c\u80dc\u5229\u662f\u7edd\u6ca1\u6709\u4fa5\u5e78\u7684\uff0c\u4f60\u8981\u5f97\u80dc\uff0c\u5c31\u4e00\u5b9a\u8981\u4ed8\u51fa\u4ee3\u4ef7\u3002 \u3000\u3000\u90ed\u5b9a\u4e5f\u4e0d\u505c\u5730\u53f9\u606f\u3002 \u3000\u3000\u7389\u7bab\u9053\u4eba\u5ffd\u7136\u5636\u58f0\u5927\u547c\uff1a\u201c\u62d4\u51fa\u4f60\u7684\u5251\uff0c\u8ba9\u6211\u8eba\u4e0b\u53bb\uff0c\u8ba9\u6211\u6b7b\u3002\u201d \u3000\u3000\u5251\u950b\u8fd8\u7559\u5728\u4ed6\u7684\u80f8\u819b\u91cc\u3002 \u3000\u3000\u4ed6\u5df2\u5f00\u59cb\u4e0d\u505c\u5730\u54b3\u55fd\uff0c\u5598\u606f\u3002 \u3000\u3000\u82e5\u662f\u4e0d\u62d4\u51fa\u8fd9\u67c4\u5251\u6765\uff0c\u4e5f\u8bb8\u4ed6\u8fd8\u53ef\u4ee5\u591a\u8bdd\u7247\u523b\uff0c\u4f46\u73b0\u5728\u4ed6\u53ea\u6c42\u901f\u6b7b\u3002 \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u8fd8\u6709\u4ec0\u4e48\u8bdd\u8981\u7559\u4e0b\u6765\uff1f\u201d \u3000\u3000\u7389\u7bab\u9053\u4eba\u9053\uff1a\u201c\u6ca1\u6709\uff0c\u4e00\u4e2a\u5b57\u4e5f\u6ca1\u6709\u3002\u201d \u3000\u3000\u90ed\u5b9a\u53f9\u9053\uff1a\u201c\u597d\uff0c\u4f60\u653e\u5fc3\u6b7b\u5427\uff0c\u6211\u4e00\u5b9a\u4f1a\u5b89\u6392\u4f60\u7684\u540e\u4e8b\u3002\u201d \u3000\u3000\u4ed6\u7ec8\u4e8e\u62d4\u51fa\u4e86\u4ed6\u7684\u5251\u3002 \u3000\u3000\u62d4\u5251\u65f6\uff0c\u4ed6\u624b\u8098\u5411\u540e\u64a4\uff0c\u80f8\u819b\u524d\u5c31\u4e0d\u514d\u8981\u9732\u51fa\u7a7a\u95e8\u3002 \u3000\u3000\u7a81\u7136\u95f4\uff0c\u201c\u53ee\u201d\u7684\u4e00\u54cd\u3002\u767d\u7389\u7bab\u91cc\u7a81\u7136\u6709\u4e09\u70b9\u5bd2\u661f\u66b4\u5c04\u800c\u51fa\uff0c\u9489\u5165\u4e86\u4ed6\u7684\u80f8\u819b\u3002 \u3000\u3000\u90ed\u5b9a\u7ade\u88ab\u6253\u5f97\u4ef0\u9762\u8dcc\u5012\u3002 \u3000\u3000\u7389\u7bab\u9053\u4eba\u5374\u8fd8\u7ad9\u7740\uff0c\u5598\u606f\u7740\uff0c\u54af\u54af\u5730\u7b11\u9053\uff1a\u201c\u73b0\u5728\u6211\u53ef\u4ee5\u653e\u5fc3\u6b7b\u4e86\uff0c\u56e0\u4e3a\u6211\u77e5\u9053\u4f60\u4e00\u5b9a \u4f1a\u8ddf\u7740\u6765\u7684\u3002\u201d \u3000\u3000\u4ed6\u7ec8\u4e8e\u5012\u4e0b\u53bb\uff0c\u5012\u5728\u4ed6\u81ea\u5df1\u7684\u8840\u6cca\u4e2d\u3002 \u3000\u3000\u96ea\u82b1\u6b63\u4e00\u7247\u7247\u843d\u4e0b\u6765\uff0c\u843d\u5728\u4ed6\u60e8\u767d\u7684\u8138\u4e0a\u2026\u2026 \u3000\u3000\u201c\u9e3f\u798f\u5f53\u5934\uff0c\u5bbe\u81f3\u5982\u5f52\u3002\u201d \u3000\u3000\u9e3f\u5bbe\u5ba2\u6808\u7684\u5927\u95e8\u5916\uff0c\u5df2\u8d34\u8d77\u4e86\u6625\u8054\uff0c\u51c6\u5907\u8fc7\u5e74\u4e86\u3002 \u3000\u3000\u4eca\u591c\u5c31\u5df2\u662f\u9664\u5915\u3002 \u3000\u3000\u6709\u5bb6\u7684\u5ba2\u4eba\u548c\u4f19\u8ba1\uff0c\u90fd\u5df2\u8d76\u56de\u5bb6\u53bb\uff0c\u751f\u610f\u5174\u9686\u7684\u5ba2\u6808\uff0c\u5ffd\u7136\u53d8\u5f97\u51b7\u6e05\u6e05\u7684\u3002 \u3000\u3000\u53a8\u623f\u91cc\u5374\u5728\u5fd9\u7740\uff0c\u56e0\u4e3a\u8001\u638c\u67dc\u7684\u5bb6\u5c31\u5728\u8fd9\u5ba2\u6808\u91cc\uff0c\u8fd8\u6709\u51e0\u4e2a\u5355\u8eab\u7684\u4f19\u8ba1\uff0c\u4e5f\u51c6\u5907\u7559\u4e0b\u6765 \u5403\u5e74\u591c\u996d\uff0c\u5403\u5b8c\u4e86\u518d\u597d\u597d\u8d4c\u4e00\u573a\u3002 \u3000\u3000\u98ce\u4e2d\u5145\u6ee1\u4e86\u70e4\u9e21\u70e7\u8089\u7684\u9999\u6c14\uff0c\u4e00\u9635\u9635\u5439\u5230\u540e\u9662\u3002 \u3000\u3000\u540e\u9662\u7684\u53a2\u623f\u91cc\uff0c\u5df2\u71c3\u8d77\u4e86\u706f\u3002 \u3000\u3000\u53ea\u6709\u4e45\u5df2\u4e60\u60ef\u4e8e\u6d41\u6d6a\u7684\u6d6a\u5b50\u4eec\uff0c\u624d\u77e5\u9053\u7559\u5728\u9006\u65c5\u4e2d\u8fc7\u5e74\u7684\u6ecb\u5473\u3002 \u3000\u3000\u4e01\u7075\u7433\u6b63\u5750\u5728\u5b64\u706f\u4e0b\uff0c\u770b\u7740\u5e8a\u4e0a\u7684\u90ed\u5b9a\u3002 \u3000\u3000\u90ed\u5b9a\u53d1\u4eae\u7684\u773c\u775b\u5df2\u95ed\u8d77\uff0c\u8138\u662f\u6b7b\u7070\u8272\u7684\uff0c\u82e5\u4e0d\u662f\u8fd8\u6709\u4e00\u70b9\u5fae\u5f31\u7684\u547c\u5438\uff0c\u770b\u6765\u5df2\u65e0\u5f02\u6b7b \u4eba\u3002 \u3000\u3000\u4ed6\u8fd8\u6ca1\u6709\u6b7b\uff0c\u53ef\u662f\u4ed6\u8fd8\u80fd\u6d3b\u591a\u4e45\u5462\uff1f \u3000\u3000\u73b0\u5728\u4ed6\u8fd8\u80fd\u6d3b\u7740\uff0c\u53ea\u56e0\u4e3a\u7389\u7bab\u9053\u4eba\u7684\u6697\u5668\u4e0a\u5c45\u7136\u6ca1\u6709\u6bd2\u3002 \u3000\u3000\u767d\u7389\u6c38\u8fdc\u662f\u7eaf\u6d01\u5c0a\u8d35\u7684\u3002 \u3000\u3000\u7389\u7bab\u9053\u4eba\u7684\u4eba\u867d\u7136\u5df2\u53d8\uff0c\u4ed6\u7684\u767d\u7389\u7bab\u6ca1\u6709\u53d8\u3002 \u3000\u3000\u4ed6\u603b\u7b97\u8fd8\u662f\u4e3a\u81ea\u5df1\u4fdd\u7559\u4e86\u4e00\u70b9\u5e72\u51c0\u5730\uff0c\u4ed6\u6bd5\u7adf\u8fd8\u662f\u4e2a\u503c\u5f97\u9a84\u50b2\u7684\u4eba\u3002 \u3000\u3000\u53ef\u662f\u6697\u5668\u53d1\u51fa\u65f6\uff0c\u4e24\u4eba\u7684\u8ddd\u79bb\u5b9e\u5728\u592a\u8fd1\uff0c\u90a3\u4e09\u679d\u767d\u7389\u9489\uff0c\u51e0\u4e4e\u5df2\u6253\u65ad\u4e86\u90ed\u5b9a\u7684\u5fc3\u8109\u3002 \u3000\u3000\u4ed6\u80fd\u6d3b\u5230\u73b0\u5728\uff0c\u5df2\u7ecf\u662f\u4e2a\u5947\u8ff9\u3002 \u3000\u3000\u4e01\u7075\u7433\u5c31\u8fd9\u4e48\u6837\u5750\u5728\u5e8a\u5934\uff0c\u5df2\u4e0d\u77e5\u5750\u4e86\u591a\u4e45\uff1b\u8138\u4e0a\u7684\u6cea\u75d5\u6e7f\u4e86\u53c8\u5e72\uff0c\u5e72\u4e86\u53c8\u6e7f\u3002 \u3000\u3000\u5916\u9762\u5ffd\u7136\u54cd\u8d77\u4e86\u6572\u95e8\u58f0\u3002 \u3000\u3000\u201c\u8c01\uff1f\u201d \u3000\u3000\u6572\u95e8\u7684\u662f\u4e2a\u5e74\u8f7b\u7684\u4f19\u8ba1\uff0c\u52c9\u5f3a\u5e26\u7740\u7b11\uff0c\u9053\uff0c\u201c\u6211\u4eec\u638c\u67dc\u7684\u7279\u5730\u53eb\u6211\u6765\u8bf7\u59d1\u5a18\uff0c\u5230\u524d\u9762\u6765 \u5403\u5e74\u591c\u996d\u3002\u201d \u3000\u3000\u201c\u5403\u5e74\u591c\u996d\uff1f\u3001\u4e01\u7075\u7433\u5fc3\u91cc\u84e6\u5730\u4e00\u60ca\uff1a\u201c\u4eca\u5929\u5df2\u7ecf\u662f\u9664\u5915\uff1f\u201d \u3000\u3000\u4f19\u8ba1\u70b9\u70b9\u5934\u3002 \u3000\u3000\u770b\u7740\u8fd9\u4e2a\u8fde\u8fc7\u5e74\u90fd\u5df2\u5fd8\u4e86\u7684\u5e74\u8f7b\u5973\u4eba\uff0c\u4ed6\u5fc3\u91cc\u4e5f\u4e0d\u7981\u89c9\u5f97\u5f88\u540c\u60c5\uff0c\u5f88\u96be\u53d7\u3002 \u3000\u3000\u4e01\u7075\u7433\u75f4\u75f4\u5730\u5750\u5728\u90a3\u91cc\uff0c\u65e2\u6ca1\u6709\u8bf4\u8bdd\uff0c\u5fc3\u91cc\u4e5f\u4e0d\u77e5\u5728\u60f3\u4ec0\u4e48\u3002 \u3000\u3000\u4f19\u8ba1\u53c8\u95ee\u5979\u4e24\u904d\uff0c\u5979\u5374\u5df2\u542c\u4e0d\u89c1\u3002 \u3000\u3000\u9eef\u6de1\u7684\u5b64\u706f\uff0c\u5782\u6b7b\u7684\u75c5\u4eba\uff0c\u4f60\u82e5\u662f\u5979\uff0c\u4f60\u8fd8\u6709\u6ca1\u6709\u5fc3\u53bb\u5403\u4eba\u5bb6\u7684\u5e74\u591c\u996d\uff1f \u3000\u3000\u4f19\u8ba1\u8f7b\u8f7b\u5730\u53f9\u606f\u4e00\u58f0\uff0c\u6162\u6162\u5730\u5173\u4e0a\u95e8\uff09\u9000\u4e86\u51fa\u53bb\u3001\u5fc3\u91cc\u89c9\u5f97\u9178\u9178\u7684\u3002 \u3000\u3000\u4e00\u4e2a\u5982\u6b64\u5e74\u8f7b\uff0c\u5982\u6b64\u7f8e\u4e3d\u7684\u5973\u5b69\u5b50\uff09\u906d\u9047\u4e3a\u4ec0\u4e48\u4f1a\u5982\u6b64\u53ef\u601c\uff1f \u3000\u3000\u201c\u53c8\u8fc7\u5e74\u4e86\u2026\u2026\u53c8\u662f\u4e00\u5e74\u3002\u201d \u3000\u3000\u4ece\u4e01\u7075\u7433\u6709\u8bb0\u5fc6\u65f6\u5f00\u59cb\uff0c\u8fc7\u5e74\u7684\u65f6\u5019\uff0c\u603b\u662f\u5145\u6ee1\u4e86\u6b22\u4e50\u7684\u3002 \u3000\u3000\u4ece\u521d\u4e00\u5230\u5341\u4e94\uff0c\u63a5\u8fde\u7740\u534a\u4e2a\u6708\u3001\u8c01\u4e5f\u4e0d\u8bb8\u751f\u6c14\uff0c\u66f4\u4e0d\u8bb8\u8bf4\u4e0d\u5409\u7965\u8fd9\u672c\u5c31\u662f\u4e2a\u5409\u7965\u7684\u65e5 \u5b50\uff0c\u53ef\u662f\u4eca\u5e74\u5462\uff1f \u3000\u3000\u5916\u9762\u5ffd\u7136\u54cd\u8d77\u4e86\u4e00\u9635\u9707\u8033\u7684\u7206\u7af9\u58f0\u3002 \u3000\u3000\u7206\u7af9\u4e00\u58f0\u9664\u65e7\uff0c\u6843\u7b26\u4e07\u70b9\u66f4\u65b0\u4e00\u4e00\u65e7\u7684\u4e00\u5e74\u5df2\u8fc7\u53bb\uff0c\u65b0\u5e74\u4e2d\u603b\u6709\u65b0\u5e0c\u671b\u7684\u3002 \u3000\u3000\u53ef\u662f\u5979\u8fd8\u6709\u4ec0\u4e48\u5e0c\u671b\uff1f \u3000\u3000\u7206\u7af9\u58f0\u60ca\u9192\u4e86\u90ed\u5b9a\uff0c\u4ed6\u5ffd\u7136\u5f20\u5f00\u773c\u775b\uff0c\u4eff\u4f5b\u60f3\u95ee\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\u58f0\u97f3\uff1f\u201d\u53ea\u53ef\u60dc\u4ed6\u7684\u5634\u5507 \u867d\u5728\u52a8\uff0c\u5374\u8bf4\u4e0d\u51fa\u4e00\u4e2a\u5b57\u3002 \u3000\u3000\u4e01\u7075\u7433\u660e\u767d\u4ed6\u7684\u610f\u601d\u3001\u52c9\u5f3a\u9732\u51fa\u7b11\u8138\uff0c\u9053\uff1a\u201c\u660e\u5929\u5c31\u8fc7\u5e74\u4e86\uff0c\u5916\u9762\u6709\u4eba\u5728\u653e\u97ad\u70ae\u3002\u201d \u3000\u3000\u2014\u2014\u53c8\u662f\u4e00\u5e74\uff0c\u603b\u7b97\u53c8\u8fc7\u4e86\u4e00\u5e74\u3002 \u3000\u3000\u90ed\u5b9a\u51dd\u89c6\u7740\u7a97\u5916\u7684\u9ed1\u6697\uff1b\u5e0c\u671b\u8fd8\u80fd\u770b\u5230\u592a\u9633\u5347\u8d77\uff0c\u53ef\u662f\u5c31\u7b97\u770b\u89c1\u53c9\u5982\u4f55\uff1f \u3000\u3000\u4ed6\u5ffd\u7136\u5f00\u59cb\u4e0d\u505c\u5730\u54b3\u55fd\u3002 \u3000\u3000\u4e01\u7075\u7433\u67d4\u58f0\u9053\uff1a\u201c\u4f60\u60f3\u4e0d\u60f3\u559d\u7897\u70ed\u6c64\uff1f\u4eca\u5929\u665a\u4e0a\u4ed6\u4eec\u4e00\u5b9a\u7ed9\u4f60\u7096\u4e86\u9e21\u6c64\u3002\u201d \u3000\u3000\u90ed\u5b9a\u7528\u529b\u6447\u5934\u3002 \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u4f60\u60f3\u8981\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u90ed\u5b9a\u770b\u7740\u5979\uff0c\u7ec8\u4e8e\u8bf4\u51fa\u4e09\u4e2a\u5b57\uff1a\u201c\u4f60\u8d70\u5427\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u8981\u6211\u8d70\uff1f\u201d \u3000\u3000\u90ed\u5b9a\u7b11\u4e86\u7b11\u3001\u7b11\u5f97\u5f88\u51c4\u51c9\uff1a\u201c\u6211\u77e5\u9053\u6211\u5df2\u4e0d\u884c\u4e86\uff0c\u4f60\u4e0d\u5fc5\u518d\u966a\u7740\u6211\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u7528\u529b\u63e1\u4f4f\u4ed6\u7684\u624b\uff1a\u201c\u6211\u4e00\u5b9a\u8981\u966a\u7740\u4f60\uff0c\u770b\u7740\u4f60\u597d\u8d77\u6765\uff0c\u6211\u77e5\u9053\u4f60\u4e00\u5b9a\u53ef\u4ee5\u8bdd\u4e0b \u53bb\u3002\u201d \u3000\u3000\u90ed\u5b9a\u53c8\u6447\u4e86\u6447\u5934\uff0c\u95ed\u4e0a\u773c\u775b\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u82e5\u8fde\u81ea\u5df2\u90fd\u5df2\u5bf9\u81ea\u5df1\u7684\u751f\u547d\u5931\u53bb\u4fe1\u5fc3\uff0c\u8fd8\u6709\u8c01\u80fd\u6551\u4ed6\uff1f \u3000\u3000\u4e01\u7075\u7433\u54ac\u7740\u5634\u5507\uff0c\u5fcd\u7740\u773c\u6cea\u9053\uff1a\u201c\u4f60\u82e5\u771f\u7684\u89c9\u5f97\u81ea\u5df1\u8981\u6b7b\u4e86\uff0c\u4f60\u5c31\u5bf9\u4e0d\u8d77\u6211\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u2026\u2026\u56e0\u4e3a\u6211\u5df2\u51c6\u5907\u5ac1\u7ed9\u4f60\u3002\u201d\u4e01\u7075\u7433\u67d4\u58f0\u9053\uff1a\u201c\u96be\u9053\u4f60\u8981\u6211\u505a\u5be1\u5987\uff1f\u201d \u3000\u3000\u90ed\u5b9a\u82cd\u767d\u7684\u8138\u4e0a\uff0c\u7a81\u7136\u6709\u4e86\u7ea2\u6655\uff1a\u201c\u771f\u7684\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u662f\u771f\u7684\u3002\u201d\u4e01\u7075\u7433\u53c8\u4e0b\u4e86\u51b3\u5fc3\uff1a\u201c\u6211\u4eec\u968f\u65f6\u90fd\u53ef\u4ee5\u6210\u4eb2\u3002\u201d \u3000\u3000\u53ea\u8981\u80fd\u8ba9\u90ed\u5b9a\u6d3b\u4e0b\u53bb\uff0c\u65e0\u8bba\u8981\u5979\u505a\u4ec0\u4e48\uff0c\u5979\u90fd\u662f\u5fc3\u7518\u60c5\u613f\u7684\u3002\u201d \u3000\u3000\u201c\u660e\u5929\u5c31\u662f\u4e2a\u5409\u7965\u7684\u65e5\u5b50\uff0c\u6211\u4eec\u5df2\u4e0d\u5fc5\u518d\u7b49\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u6211\u2026\u2026\u201d \u3000\u3000\u201c\u6240\u4ee5\u4f60\u4e00\u5b9a\u8981\u6d3b\u4e0b\u53bb\uff0c\u4e00\u5b9a\uff01\u201d \u3000\u3000\u8001\u638c\u67dc\u5750\u5728\u67dc\u53f0\u91cc\uff0c\u8138\u4e0a\u5df2\u5e26\u7740\u51e0\u5206\u9152\u610f\u3002 \u3000\u3000\u8fd9\u67dc\u53f0\u4ed6\u5df2\u5750\u4e86\u4e8c\u5341\u5e74\uff0c\u770b\u6765\u8fd8\u5f97\u7ee7\u7eed\u5750\u4e0b\u53bb\uff0c\u770b\u7740\u4eba\u6765\u4eba\u5f80\u3002 \u3000\u3000\u5404\u5f0f\u5404\u6837\u7684\u4eba\uff0c\u5404\u5f0f\u5404\u6837\u7684\u60b2\u6b22\u79bb\u5408\uff0c\u751f\u8001\u75c5\u6b7b\u3002 \u3000\u3000\u4ed6\u770b\u5f97\u5b9e\u5728\u592a\u591a\uff0c\u6bcf\u5f53\u9152\u540e\uff0c\u4ed6\u5fc3\u91cc\u603b\u4f1a\u6709\u8bf4\u4e0d\u51fa\u7684\u538c\u5026\u4e4b\u610f\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u73b0\u5728\u60c5\u613f\u4e00\u4e2a\u4eba\u5750\u5728\u8fd9\u91cc\u3002 \u3000\u3000\u4ed6\u6ca1\u6709\u60f3\u5230\u4e01\u7075\u7433\u4f1a\u6765\uff0c\u5fcd\u4e0d\u4f4f\u8bd5\u63a2\u7740\u95ee\uff1a\u201c\u59d1\u5a18\u8fd8\u6ca1\u6709\u7761\uff1f\u75c5\u4eba\u662f\u4e0d\u662f\u5df2\u597d\u4e86\u4e9b\uff1f\u201d \u3000\u3000\u4e01\u7075\u7433\u52c9\u5f3a\u7b11\u4e86\u7b11\uff0c\u5ffd\u7136\u9053\uff1a\u201c\u660e\u5929\u4f60\u80fd\u4e0d\u80fd\u66ff\u6211\u529e\u5341\u51e0\u684c\u9152\uff1f\u201d \u3000\u3000\u201c\u660e\u5929\uff1f\u660e\u5929\u662f\u5927\u5e74\u521d\u4e00\uff0c\u6050\u6015\u2026\u2026\u201d \u3000\u3000\u201c\u4e00\u5b9a\u8981\u660e\u5929\uff0c\u201d\u4e01\u7075\u7433\u7b11\u5f97\u5f88\u51c4\u51c9\uff0c\u201c\u518d\u8fdf\uff0c\u6050\u6015\u5c31\u6765\u4e0d\u53ca\u4e86\u3002\u201d \u3000\u3000\u8001\u638c\u67dc\u8fdf\u7591\u7740\uff1a\u201c\u59d1\u5a18\u8981\u8bf7\u4eba\u559d\u6625\u9152\uff1f\u201d \u3000\u3000\u201c\u4e0d\u662f\u6625\u9152\uff0c\u662f\u559c\u9152\u3002\u201d \u3000\u3000\u8001\u638c\u67dc\u7741\u5927\u4e86\u773c\u775b\uff0c\u201c\u559c\u9152\uff01\u96be\u9053\u59d1\u5a18\u4f60\u660e\u5929\u5c31\u8981\u6210\u4eb2\uff1f\u201d \u3000\u3000\u4e01\u7075\u7433\u5782\u4e0b\u5934\uff0c\u53c8\u70b9\u70b9\u5934\u3002 \u3000\u3000\u8001\u638c\u67dc\u7b11\u4e86\uff0c\u7acb\u523b\u4e5f\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u51b2\u51b2\u559c\u4e5f\u597d\uff0c\u75c5\u4eba\u4e00\u51b2\u559c\uff0c\u75c5\u9a6c\u4e0a\u5c31\u4f1a\u597d\u7684\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u672c\u5c31\u77e5\u9053\u4ed6\u7edd\u4e0d\u4f1a\u660e\u767d\uff0c\u5374\u4e5f\u4e0d\u60f3\u89e3\u91ca\uff1a\u201c\u6240\u4ee5\u6211\u5e0c\u671b\u8fd9\u559c\u4e8b\u80fd\u529e\u5f97\u70ed\u95f9\u4e9b\uff0c\u8d8a \u70ed\u95f9\u8d8a\u597d\u3002\u201d \u3000\u3000\u8001\u638c\u67dc\u7684\u7cbe\u795e\u5df2\u632f\u4f5c\uff0c\u6700\u8fd1\u51f6\u6740\u4e0d\u6837\u7684\u4e8b\u4ed6\u5df2\u770b\u5f97\u5927\u591a\uff0c\u4ed6\u4e5f\u5e0c\u671b\u80fd\u6cbe\u4e9b\u559c\u6c14\uff1a\u201c\u884c\uff0c \u8fd9\u4ef6\u4e8b\u5c31\u5305\u5728\u6211\u8eab\u4e0a\u3002\u201d \u3000\u3000\u201c\u660e\u5929\u665a\u4e0a\u884c\u4e0d\uff1f\u201d \u3000\u3000\u8001\u638c\u67dc\u62cd\u7740\u80f8\uff1a\u201c\u51c6\u5b9a\u5c31\u662f\u660e\u5929\u665a\u4e0a\u3002\u201d \u3000\u3000\u81ea\u4ece\u8ba4\u5f97\u53f6\u5f00\u90a3\u4e00\u5929\u5f00\u59cb\uff0c\u4e01\u7075\u7433\u5c31\u4ece\u6765\u6ca1\u60f3\u5230\u81ea\u5df1\u8fd8\u4f1a\u5ac1\u7ed9\u522b\u4eba\u3002 \u3000\u3000\u53ef\u662f\u660e\u5929\u665a\u4e0a\u2026\u2026 \u7b2c\u4e8c\u5341\u7ae0\u3000\u9664\u5915\u4e4b\u591c>> \u53e4\u9f99\u300a\u4e5d\u6708\u9e70\u98de\u300b \u7b2c\u4e8c\u5341\u7ae0\u3000\u9664\u5915\u4e4b\u591c \u3000\u3000\u7ea2\u697c\uff0c\u7ea2\u7a97\uff0c\u7ea2\u684c\u5b50\uff0c\u7ea2\u7f57\u5e10\uff0c\u4ec0\u4e48\u90fd\u662f\u7ea2\u7684\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u751c\u751c\u5730\u77e2\u7740\uff0c\u770b\u7740\u53f6\u5f00\uff1a\u201c\u4f60\u8bf4\u8fd9\u6837\u50cf\u4e0d\u50cf\u6d1e\u623f\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4e0d\u50cf\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u561f\u8d77\u4e86\u5634\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\u5730\u65b9\u4e0d\u50cf\uff1f\u96be\u9053\u6211\u4e0d\u50cf\u65b0\u5a18\u5b50\uff1f\u201d \u3000\u3000\u5979\u7a7f\u7740\u7ea2\u8884\uff0c\u7ea2\u88d9\uff0c\u7ea2\u7ee3\u978b\uff0c\u8138\u4e5f\u662f\u7ea2\u7ea2\u7684\u3002 \u3000\u3000\u53f6\u5f00\u7684\u773c\u775b\u4e00\u76f4\u90fd\u5728\u56de\u907f\u7740\u5979\uff1a\u201c\u4f60\u50cf\u65b0\u5a18\u5b50\uff0c\u6211\u5374\u4e0d\u50cf\u65b0\u90ce\u3002\u201d \u3000\u3000\u4ed6\u4e5f\u7a7f\u7740\u4e00\u8eab\u65b0\u8863\u88f3\uff0c\u8138\u4e5f\u88ab\u70db\u5149\u6620\u7ea2\u4e86\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u770b\u7740\u4ed6\uff0c\u5ae3\u7136\u9053\uff1a\u201c\u8c01\u8bf4\u4f60\u4e0d\u50cf\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u8bf4\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u53bb\u7167\u7167\u955c\u5b50\u3002\u201d \u3000\u3000\u53f6\u5f00\u6de1\u6de1\u9053\uff1a\u201c\u7528\u4e0d\u7740\u7167\u955c\u5b50\uff0c\u6211\u4e5f\u770b\u5f97\u89c1\u6211\u81ea\u5df1\uff0c\u800c\u4e14\u770b\u5f97\u5f88\u6e05\u695a\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u8fd9\u4e00\u8f88\u4e8e\u6700\u5927\u7684\u957f\u5904\uff0c\u5c31\u662f\u6c38\u8fdc\u90fd\u80fd\u770b\u6e05\u6211\u81ea\u5df1\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u7ad9\u8d77\u6765\uff0c\u63a8\u5f00\u7a97\u5b50\u3002\u7a97\u5916\u4e00\u7247\u548c\u5e73\u5b89\u9759\uff0c\u5bb6\u5bb6\u6237\u6237\u95e8\u4e0a\u90fd\u8d34\u7740\u9c9c\u7ea2\u7684\u6625\u8054\uff0c\u51e0\u4e2a \u7a7f\u7740\u65b0\u8863\u3001\u6234\u7740\u65b0\u5e3d\u5b50\u7684\u5b69\u5b50\u6b63\u63a9\u7740\u8033\u6735\uff0c\u5728\u95e8\u53e3\u653e\u7206\u7af9\uff0c\u8fd9\u4e00\u5207\u663e\u7136\u90fd\u662f\u4e0a\u5b98\u5c0f\u4ed9\u7279\u5730\u4e3a \u4ed6\u5b89\u6392\u7684\uff0c\u5e0c\u671b\u8fd9\u79cd\u8fc7\u5e74\u7684\u6c14\u6c1b\u8ba9\u4ed6\u53d8\u5f97\u5f00\u5fc3\u4e9b\u3001\u6700\u8fd1\u8fd9\u4e24\u5929\u4ed6\u4e00\u76f4\u5f88\u95f7\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u53c8\u5728\u95ee\uff1a\u201c\u4f60\u559c\u4e0d\u559c\u6b22\u8fc7\u5e74\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u600e\u4e48\u4f1a\u4e0d\u77e5\u9053\uff1f\u201d \u3000\u3000\u53f6\u5f00\u51dd\u89c6\u7740\u8fdc\u65b9\uff0c\u9664\u5915\u591c\u7684\u82cd\u7a79\uff0c\u4e5f\u548c\u522b\u7684\u665a\u4e0a\u540c\u6837\u9ed1\u6697\u3002 \u3000\u3000\u201c\u6211\u597d\u50cf\u4ece\u6765\u4e5f\u6ca1\u6709\u8fc7\u8fc7\u65b0\u5e74\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u53f6\u5f00\u7684\u773c\u775b\u91cc\uff0c\u4eff\u4f5b\u5e26\u7740\u79cd\u8bf4\u4e0d\u51fa\u7684\u56f0\u60d1\u548c\u5bc2\u5bde\uff0c\u8fc7\u4e86\u5f88\u4e45\uff0c\u624d\u6162\u6162\u9053\uff1a\u201c\u4f60\u5e94\u8be5\u77e5 \u9053\uff0c\u8fd9\u4e16\u4e0a\u672c\u5c31\u6709\u79cd\u4eba\u662f\u7edd\u4e0d\u8fc7\u5e74\u7684\u3002\u201d \u3000\u3000\u201c\u54ea\u79cd\u4eba\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\u5bb6\u7684\u4eba\u3002\u201d \u3000\u3000\u6d41\u6d6a\u5929\u6daf\u7684\u6d6a\u5b50\u4eec\uff0c\u4ed6\u4eec\u51e0\u65f6\u4eab\u53d7\u8fc7\u201c\u8fc7\u5e74\u201d\u7684\u5409\u7965\u548c\u6b22\u4e50\uff0c\u522b\u4eba\u5728\u8fc7\u5e74\u7684\u65f6\u5019\uff0c\u5c82\u975e \u4e5f\u6b63\u662f\u4ed6\u4eec\u6700\u5bc2\u5bde\u7684\u65f6\u5019\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u5ffd\u7136\u8f7b\u8f7b\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5176\u5b9e\u6211\u2026\u2026\u6211\u4e00\u6837\u4e5f\u4ece\u6765\u6ca1\u6709\u8fc7\u8fc7\u5e74\u3002\u201d \u3000\u3000\u201c\u54e6\uff1f\u201d \u3000\u3000\u201c\u4f60\u5f53\u7136\u77e5\u9053\u6211\u6bcd\u4eb2\u662f\u4e2a\u4ec0\u4e48\u6837\u7684\u4eba\uff0c\u4f46\u4f60\u5374\u6c38\u8fdc\u4e5f\u4e0d\u4f1a\u77e5\u9053\u5979\u665a\u5e74\u8fc7\u7684\u4ec0\u4e48\u6837\u7684\u65e5 \u5b50\uff0c\u522b\u4eba\u5728\u8fc7\u5e74\u7684\u65f6\u5019\uff0c\u5979\u603b\u662f\u62b1\u7740\u6211\uff0c\u5077\u5077\u5730\u8eb2\u5728\u88ab\u7a9d\u91cc\u6d41\u6cea\u3002\u201d \u3000\u3000\u53f6\u5f00\u6ca1\u6709\u56de\u5934\uff0c\u4e5f\u6ca1\u6709\u5f00\u53e3\u3002\u4ed6\u80fd\u60f3\u8c61\u5230\u90a3\u79cd\u60c5\u51b5\u2014\u2014\u65e0\u8bba\u8c01\u90fd\u5fc5\u987b\u4e3a\u81ea\u5df1\u7684\u7f6a\u5b7d\u4ed8\u51fa \u4ee3\u4ef7\u3002 \u3000\u3000\u6797\u4ed9\u513f\u4e5f\u4e0d\u80fd\u4f8b\u5916\uff0c\u53ef\u662f\u4e0a\u5b98\u5c0f\u4ed9\u5462\uff1f\u96be\u9053\u5979\u4e00\u751f\u4e0b\u6765\u5c31\u6709\u7f6a\uff1f \u3000\u3000\u4e3a\u4ec0\u4e48\u4e0d\u80fd\u50cf\u522b\u7684\u5b69\u5b50\u4e00\u6837\uff0c\u4eab\u53d7\u7ae5\u5e74\u7684\u5e78\u798f\u6b22\u4e50\uff1f\u5979\u4eca\u5929\u53d8\u6210\u8fd9\u4e48\u6837\u4e00\u4e2a\u4eba\uff0c\u662f\u8c01\u9020 \u6210\u7684\uff1f\u662f\u8c01\u7684\u9519\uff1f \u3000\u3000\u53f6\u5f00\u4e5f\u4e0d\u7981\u8f7b\u8f7b\u53f9\u606f\u3002 \u3000\u3000\u201c\u540c\u662f\u5929\u6daf\u6ca6\u843d\u4eba\uff0c\u76f8\u9022\u4f55\u5fc5\u66fe\u76f8\u8bc6\u3002\u201d\u4e0a\u5b98\u5c0f\u4ed9\u5e7d\u5e7d\u5730\u53f9\u606f\u7740\uff1a\u201c\u5176\u5b9e\u4f60\u4e5f\u8be5\u77e5\u9053\u6211 \u4eec\u672c\u662f\u540c\u6837\u7684\u4eba\uff0c\u4f60\u5bf9\u6211\u4e3a\u4ec0\u4e48\u603b\u662f\u8fd9\u4e48\u51b7\u6de1\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u90a3\u53ea\u56e0\u4f60\u5df2\u53d8\u4e86\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u8d70\u8fc7\u6765\uff0c\u9760\u8fd1\u4ed6\uff1a\u201c\u4f60\u8ba4\u4e3a\u6211\u73b0\u5728\u5df2\u53d8\u6210\u4e2a\u4ec0\u4e48\u6837\u780c\u4eba\uff1f\u201d \u3000\u3000\u53f6\u5f00\u6c89\u9ed8\uff0c\u53ea\u6709\u6c89\u9ed8\uff0c\u4ed6\u4ece\u4e0d\u613f\u5f53\u522b\u4eba\u7684\u9762\uff0c\u53bb\u4f24\u5bb3\u522b\u4eba\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u7a81\u7136\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u82e5\u8ba4\u4e3a\u6211\u5df2\u53d8\u5f97\u548c\u2026\u5979\u4e00\u6837\uff0c\uff0c\u4f60\u5c31\u9517\u4e86\u3002\u201d\u3002 \u3000\u3000\u53f6\u5f00\u4e5f\u77e5\u9053\u5979\u8bf4\u7684\u201c\u5979\u201d\u662f\u8c01\u3002 \u3000\u3000\u4ed6\u7684\u786e\u8ba4\u4e3a\u4e0a\u5b98\u5c0f\u4ed9\u5df2\u53d8\u5f97\u548c\u6614\u5e74\u7684\u6797\u4ed9\u513f\u4e00\u6837\uff0c\u751a\u81f3\u8fdc\u6bd4\u6797\u4ed9\u513f\u66f4\u53ef\u6015\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u5ffd\u7136\u8f6c\u8fc7\u8eab\u5b50\uff0c\u76ef\u7740\u4ed6\u7684\u773c\u775b\uff0c\u9053\uff1a\u201c\u770b\u7740\u6211\uff0c\u6211\u6709\u8bdd\u95ee\u4f60\u3002\u201d \u3000\u3000\u53f6\u5f00\u82e6\u7b11\u9053\uff1a\u201c\u4f60\u95ee\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6211\u82e5\u544a\u8bc9\u4f60\uff0c\u6211\u8fd9\u4e00\u8f88\u5b50\u8fd8\u6ca1\u6709\u7537\u4eba\u78b0\u8fc7\u6211\uff0c\u4f60\u4fe1\u4e0d\u4fe1\uff1f\u201d \u3000\u3000\u53f6\u5f00\u6ca1\u6709\u56de\u7b54\uff0c\u4e5f\u65e0\u6cd5\u56de\u7b54\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4f60\u82e5\u4ee5\u4e3a\u6211\u5bf9\u522b\u7684\u7537\u4eba\uff0c\u4e5f\u8ddf\u5bf9\u4f60\u4e00\u6837\uff0c\u4f60\u5c31\u66f4\u9519\u4e86\u3002\u201d \u3000\u3000\u53f6\u5f00\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u4e3a\u4ec0\u4e48\u8981\u8fd9\u6837\u5bf9\u6211\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u54ac\u7740\u5634\u5507\uff0c\u9053\uff1a\u201c\u4f60\u5fc3\u91cc\u96be\u9053\u8fd8\u4e0d\u660e\u767d\uff1f\u4e3a\u4ec0\u4e48\u8fd8\u8981\u95ee\uff1f\u201d \u3000\u3000\u5979\u770b\u7740\u4ed6\uff0c\u773c\u775b\u91cc\u5145\u6ee1\u4e86\u5e7d\u6028\uff0c\u65e0\u8bba\u8c01\u770b\u5230\u5979\u8fd9\u53cc\u773c\u775b\u90fd\u5e94\u8be5\u660e\u767d\u5979\u7684\u611f\u60c5\u3002 \u3000\u3000\u96be\u9053\u5979\u5bf9\u53f6\u5f00\u7adf\u662f\u771f\u5fc3\u7684\uff1f \u3000\u3000\u96be\u9053\u53f6\u5f00\u771f\u7684\u4e0d\u4fe1\uff1f \u3000\u3000\u5e76\u4e0d\u662f\u4e0d\u4fe1\uff0c\u800c\u662f\u4e0d\u80fd\u76f8\u4fe1\uff0c\u4e0d\u6562\u76f8\u4fe1\u3002 \u3000\u3000\u2014\u2014\u53f6\u5f00\u5ffd\u7136\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4eca\u5929\u662f\u5927\u5e74\u591c\uff0c\u6211\u4eec\u4e3a\u4ec0\u4e48\u8981\u8bf4\u8fd9\u79cd\u4e0d\u5f00\u5fc3\u7684\u4e8b\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u56e0\u4e3a\u4e0d\u7ba1\u6211\u8bf4\u4e0d\u8bf4\uff0c\u4f60\u90fd\u662f\u4e00\u6837\u4e0d\u5f00\u5fc3\u7684\u3002\u201d\u5979\u4e0d\u8ba9\u53f6\u5f00\u5206\u8fa9\uff0c\u62a2\u9752\u53c8 \u9053\uff1a\u201c\u56e0\u4e3a\u6211\u77e5\u9053\u4f60\u5fc3\u91cc\u603b\u662f\u5728\u60f3\u7740\u4e01\u7075\u7433\u3002\u201d \u3000\u3000\u53f6\u5f00\u4e0d\u80fd\u5426\u8ba4\uff0c\u53ea\u6709\u82e6\u7b11\u9053\uff1a\u201c\u6211\u8ddf\u5979\u8ba4\u5f97\u5df2\u4e0d\u6b62\u4e00\u5929\u4e86\uff0c\u5979\u5b9e\u5728\u662f\u4e2a\u5f88\u597d\u7684\u5973\u5b69\u5b50\uff0c \u5bf9\u6211\u4e5f\u662f\u4e00\u76f4\u90fd\u5f88\u597d\uff0c\u201d\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6211\u5bf9\u4f60\u4e0d\u597d\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u4eec\u4e0d\u540c\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6709\u4ec0\u4e48\u4e0d\u540c\uff1f\u201d \u3000\u3000\u53f6\u5f00\u53f9\u606f\u7740\uff0c\u9053\uff1a\u201c\u4f60\u662f\u4e2a\u5f88\u4e86\u4e0d\u8d77\u7684\u5973\u4eba\uff0c\u4f60\u6709\u624d\u80fd\uff0c\u4e5f\u6709\u91ce\u5fc3\uff0c\u4f60\u8fd8\u6709\u5f88\u591a\u4e8b\u53ef\u4ee5 \u505a\uff0c\u53ef\u662f\u2026\u2026\u5979\u5374\u53ea\u6709\u4f9d\u9760\u6211\u3002\u201d \u3000\u3000\u8fd9\u662f\u4ed6\u7684\u771f\u5fc3\u8bdd\uff0c\u4e5f\u662f\u4ed6\u7b2c\u4e00\u6b21\u5bf9\u4e0a\u5b98\u5c0f\u4ed9\u8bf4\u51fa\u771f\u5fc3\u8bdd\u3002\u73b0\u5728\u4ed6\u5df2\u4e0d\u80fd\u4e0d\u8bf4\uff0c\u4ed6\u5e76\u4e0d\u662f \u4e2a\u5b8c\u5168\u4e0d\u52a8\u5fc3\u7684\u6728\u5934\u4eba\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u5782\u4e0b\u5934\u9053\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u8ba4\u4e3a\u4e0d\u8bba\u4f60\u5230\u4ec0\u4e48\u5730\u65b9\u53bb\u4e86\uff0c\u4e0d\u7ba1\u4f60\u53bb\u4e86\u591a\u4e45\uff0c\u5979\u90fd\u4f1a \u7b49\u4f60\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u5979\u4e00\u5b9a\u4f1a\u7b49\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u7a81\u53c8\u51b7\u7b11\u3002 \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u4e0d\u4fe1\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6211\u53ea\u4e0d\u8fc7\u60f3\u63d0\u9192\u4f60\uff0c\u6709\u4e9b\u5973\u4eba\uff0c\u662f\u7ecf\u4e0d\u8d77\u8003\u9a8c\u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u76f8\u4fe1\u5979\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4f60\u6709\u6ca1\u6709\u542c\u8bf4\u8fc7\u5e84\u5468\u7684\u6545\u4e8b\uff1f\u201d \u3000\u3000\u53f6\u5f00\u542c\u8fc7\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4ed6\u4eec\u672c\u6765\u4e5f\u662f\u5bf9\u6069\u7231\u592b\u5987\uff0c\u53ef\u662f\u5e84\u5468\u4e00\u6b7b\uff0c\u4ed6\u7684\u59bb\u5b50\u7acb\u523b\u5c31\u6539\u5ac1\u7ed9\u522b \u4eba\u3002\u201d \u3000\u3000\u53f6\u5f00\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5e78\u597d\u6211\u65e2\u6ca1\u6709\u59bb\u5b50\uff0c\u4e5f\u6ca1\u6709\u5e84\u5468\u90a3\u4e48\u5927\u7684\u795e\u901a\uff0c\u66f4\u4e0d\u4f1a\u88c5\u6b7b\u3002\u2019 \u4ed6\u5df2\u4e0d\u60f3\u518d\u7ee7\u7eed\u4e89\u8fa9\u8fd9\u4ef6\u4e8b\u3002\u4e01\u7075\u7433\u5bf9\u4ed6\u7684\u611f\u60c5\uff0c\u672c\u662f\u4ed6\u4eec\u4e24\u4e2a\u4eba\u4e4b\u95f4\u7684\u4e8b\uff0c\u672c\u5c31\u4e0d\u5fc5\u8981\u522b \u4eba\u4e86\u89e3\u3002 \u3000\u3000\u97ad\u70ae\u58f0\u5df2\u5be5\u843d\uff0c\u591c\u66f4\u6df1\uff0c\u5bb6\u5bb6\u6237\u6237\u90fd\u5df2\u5173\u8d77\u4e86\u95e8\uff0c\u7a97\u5b50\u91cc\u7684\u706f\u5149\u5374\u8fd8\u4eae\u7740\uff0c\u5b69\u5b50\u4eec\u5df2\u56de \u53bb\uff0c\u7b49\u7740\u62ff\u538b\u5c81\u94b1\u3002\u9664\u5915\u591c\u672c\u5c31\u4e0d\u662f\u72c2\u6b22\u4e4b\u591c\uff0c\u800c\u662f\u4e3a\u4e86\u8ba9\u5bb6\u4eba\u4eec\u56f4\u7089\u56e2\u805a\uff0c\u8fc7\u4e00\u4e2a\u5e73\u9759\u5e78 \u798f\u7684\u665a\u4e0a\uff0c\u53ef\u662f\u50cf\u53f6\u5f00\u8fd9\u79cd\u6d6a\u5b50\uff0c\u8981\u7b49\u5230\u4ec0\u4e48\u65f6\u5019\u624d\u80fd\u4eab\u53d7\u8fd9\u79cd\u5e78\u798f\u548c\u5e73\u9759\uff1f \u3000\u3000\u4ed6\u7adf\u5ffd\u7136\u53d8\u5f97\u5f88\u8427\u7d22\uff0c\u6b63\u51c6\u5907\u8f6c\u8fc7\u8eab\u53bb\u627e\u676f\u9152\u559d\u3002\u5c31\u5728\u8fd9\u65f6\uff0c\u591c\u7a7a\u4e2d\u5ffd\u7136\u54cd\u8d77\u4e86\u4e00\u9635\u8f7b \u5fae\u800c\u5947\u7279\u7684\u547c\u54e8\u58f0\u3002\u4e00\u53ea\u9e3d\u5b50\u8fdc\u8fdc\u5730\u98de\u6765\uff0c\u843d\u5728\u5bf9\u9762\u5c4b\u6a90\u4e0a\u3002\u7fbd\u6bdb\u7adf\u662f\u6f06\u9ed1\u7684\uff0c\u9ed1\u5f97\u53d1\u4eae\uff0c \u770b\u6765\u7adf\u50cf\u662f\u53ea\u9ed1\u9e70\u4e00\u6837\u3002 \u3000\u3000\u53f6\u5f00\u4ece\u6765\u4e5f\u6ca1\u6709\u770b\u89c1\u8fc7\u8fd9\u4e48\u4e0d\u5e73\u51e1\u7684\u9e3d\u5b50\uff0c\u5fcd\u4e0d\u4f4f\u505c\u4e0b\u811a\u6b65\uff0c\u591a\u770b\u4e86\u51e0\u773c\u3002\u7136\u540e\u4ed6\u624d\u53d1 \u73b0\u4e0a\u5b98\u5c0f\u4ed9\u773c\u775b\u91cc\u4f3c\u5df2\u53d1\u4e86\u5149\u3002\u5ffd\u7136\u4e5f\u4ece\u8eab\u4e0a\u62ff\u51fa\u4e86\u4e2a\u94dc\u54e8\uff0c\u8f7b\u8f7b\u4e00\u5439\uff0c\u8fd9\u9ed1\u9e3d\u5b50\u7acb\u523b\u98de\u8fc7 \u6765\uff0c\u7a7f\u7a97\u800c\u5165\uff0c\u843d\u5728\u5979\u7684\u624b\u638c\u4e0a\uff0c\u94a2\u5599\u5229\u722a\uff0c\u95ea\u95ea\u53d1\u5149\u7684\u773c\u775b\uff0c\u770b\u6765\u7adf\u4f3c\u6bd4\u9e70\u66f4\u5065\u58ee\u96c4\u731b\u3002 \u3000\u3000\u8fd9\u662f\u8c01\u5bb6\u517b\u7684\u9e3d\u5b50\uff1f\u53f6\u5f00\u5fc3\u91cc\u5df2\u9690\u9690\u611f\u89c9\u5230\uff0c\u8fd9\u9e3d\u5b50\u7684\u4e3b\u4eba\uff0c\u4e00\u5b9a\u4e5f\u662f\u4e2a\u5f88\u53ef\u6015\u7684\u4eba\u3002 \u9e3d\u722a\u4e0a\u7cfb\u7740\u4e2a\u4e4c\u9ed1\u7684\u94c1\u7ba1\uff0c\u4e0a\u5b98\u5c0f\u4ed9\u89e3\u4e0b\u6765\uff0c\u4ece\u9762\u91cc\u53d6\u51fa\u4e86\u4e00\u4e2a\u7eb8\u5377\u3002 \u3000\u3000\u7eca\u7ea2\u7684\u7eb8\u7b3a\u4e0a\uff0c\u5199\u6ee1\u4e86\u6bd4\u8747\u5934\u8fd8\u5c0f\u7684\u5b57\uff0c\u4e0a\u5b98\u5c0f\u4ed9\u5df2\u8d70\u5230\u706f\u4e0b\uff0c\u5f88\u4ed4\u7ec6\u5730\u770b\u4e86\u4e00\u904d\uff0c\u53c8 \u770b\u4e86\u4e00\u904d\u3002\u5979\u770b\u5f97\u5f88\u4e13\u5fc3\uff0c\u4eff\u4f5b\u8fde\u53f6\u5f00\u90fd\u5df2\u5fd8\u4e86\u3002 \u3000\u3000\u53f6\u5f00\u5374\u5728\u770b\u7740\u5979\uff0c\u706f\u5149\u7167\u7740\u5979\u7684\u8138\uff0c\u5979\u5ae3\u7ea2\u7684\u8138\u5df2\u53d8\u5f97\u82cd\u767d\uff0c\u795e\u60c5\u4e25\u8083\u800c\u6c89\u91cd\uff0c\u5728\u8fd9\u4e00 \u77ac\u95f4\uff0c\u5979\u4f3c\u5df2\u53d8\u6210\u4e86\u53e6\u5916\u4e00\u4e2a\u4eba\uff0c\u53d8\u6210\u4e86\u4e0a\u5b98\u91d1\u8679\u3002 \u3000\u3000\u8fd9\u5c01\u4e66\u4fe1\u663e\u7136\u975e\u5e38\u79d8\u5bc6\uff0c\u975e\u5e38\u91cd\u8981\u3002\u53f6\u5f00\u5e76\u4e0d\u60f3\u523a\u63a2\u522b\u4eba\u7684\u79d8\u5bc6\uff0c\u4f46\u5bf9\u8fd9\u53ea\u9e3d\u5b50\u5374\u8fd8\u662f \u89c9\u5f97\u5f88\u597d\u5947\u3002 \u3000\u3000\u4ed6\u770b\u7740\u9e3d\u5b50\uff0c\u9e3d\u5b50\u5c45\u7136\u4e5f\u5728\u72e0\u72e0\u5730\u76ef\u7740\u4ed6\u3002\u4ed6\u60f3\u53bb\u6478\u6478\u5b83\u53d1\u4eae\u7684\u7fbd\u6bdb\uff0c\u8fd9\u9e3d\u5b50\u5374\u7a81\u7136\u98de \u8d77\u6765\uff0c\u731b\u5544\u4ed6\u7684\u624b\u3002\u53f6\u5f00\u53f9\u4e86\u53e3\u6c14\uff0c\u5583\u5583\u9053\uff1a\u201c\u8fd9\u4e48\u51f6\u7684\u9e3d\u5b50\u5012\u771f\u662f\u5929\u4e0b\u5c11\u6709\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u5ffd\u7136\u62ac\u8d77\u5934\u6765\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u79cd\u9e3d\u5b50\u672c\u6765\u5c31\u5f88\u5c11\u6709\uff0c\u636e\u6211\u6240\u77e5\uff0c\u5929\u4e0b\u4e00\u5171\u4e5f \u53ea\u6709\u4e09\u53ea\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8981\u517b\u8fd9\u4e48\u6837\u4e00\u53ea\u9e3d\u5b50\uff0c\u771f\u4e0d\u662f\u5bb9\u6613\u4e8b\uff0c\u80fd\u517b\u5f97\u8d77\u5b83\u7684\u4eba\uff0c\u5929 \u4e0b\u4e5f\u7edd\u4e0d\u8d85\u51fa\u4e09\u4e2a\u3002\u201d \u3000\u3000\u53f6\u5f00\u66f4\u5947\u602a\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u53cd\u95ee\u9053\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u8fd9\u79cd\u9e3d\u5b50\u5e73\u5e38\u5403\u7684\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u53f6\u5f00\u6447\u6447\u5934\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6211\u5c31\u77e5\u9053\u4f60\u6c38\u8fdc\u4e5f\u60f3\u4e0d\u5230\u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u52c9\u5f3a\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u5b83\u5403\u7684\u603b\u4e0d\u4f1a\u662f\u4eba\u8089\u5427\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u4e5f\u7b11\u4e86\u7b11\uff0c\u5374\u6ca1\u6709\u56de\u7b54\uff0c\u5ffd\u7136\u62cd\u4e86\u62cd\u624b\u5524\u9053\uff1a\u201c\u5c0f\u7fe0\u3002\u201d \u3000\u3000\u4e00\u4e2a\u7b11\u5f97\u5f88\u751c\u3001\u9152\u7a9d\u5f88\u6df1\u7684\u5c0f\u59d1\u5a18\uff0c\u5e94\u58f0\u8d70\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4f60\u7684\u5200\u5462\uff1f\u201d \u3000\u3000\u5c0f\u7fe0\u7acb\u523b\u5c31\u4ece\u6000\u91cc\u62ff\u51fa\u4e00\u628a\u5f2f\u5f2f\u7684\u3001\u67c4\u4e0a\u9576\u7740\u660e\u73e0\u7684\u94f6\u5200\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u5f88\u597d\uff0c\u73b0\u5728\u4f60\u53ef\u4ee5\u5582\u5b83\u4e86\u3002\u201d \u3000\u3000\u5c0f\u7fe0\u7acb\u523b\u89e3\u5f00\u4e86\u8863\u670d\uff0c\u4ece\u8eab\u4e0a\u5272\u4e0b\u7247\u8840\u6dcb\u6dcb\u7684\u8089\u6765\uff0c\u8138\u4e0a\u867d\u5df2\u75db\u51fa\u4e86\u51b7\u6c57\uff0c\u5374\u8fd8\u662f\u5728\u751c \u751c\u5730\u7b11\u7740\u3002 \u3000\u3000\u90a3\u9e3d\u5b50\u5df2\u98de\u8d77\uff0c\u9e70\u822c\u98de\u8fc7\u53bb\uff0c\u53fc\u8d77\u4e86\u8fd9\u7247\u8089\uff0c\u98de\u51fa\u7a97\u5916\u3002 \u3000\u3000\u5b83\u4e5f\u50cf\u5f88\u591a\u4eba\u4e00\u6837\uff0c\u5403\u996d\u7684\u65f6\u5019\uff0c\u4e5f\u4e0d\u613f\u6709\u522b\u4eba\u5728\u65c1\u8fb9\u770b\u7740\u3002 \u3000\u3000\u53f6\u5f00\u8038\u7136\u52a8\u5bb9\uff0c\u9053\uff1a\u201c\u5b83\u5403\u7684\u771f\u662f\u4eba\u8089\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u975e\u4f46\u4eba\u8089\uff0c\u800c\u4e14\u4e00\uff0c\u5b9a\u8981\u4ece\u6d3b\u4eba\u8eab\u4e0a\u5272\u4e0b\u7684\u8089\uff0c\u8fd8\u4e00\u5b9a\u8981\u662f\u5e74\u8f7b\u7684\u5973\u5b69 \u5b50\u3002\u201d \u3000\u3000\u53f6\u5f00\u53ea\u89c9\u5f97\u80c3\u5728\u6536\u7f29\uff0c\u51e0\u4e4e\u5df2\u5fcd\u4e0d\u4f4f\u8981\u5455\u5410\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u8fd9\u53ea\u9e3d\u5b50\u662f\u4ece\u54ea\u91cc\u98de\u6765\u7684\uff1f\u201d \u3000\u3000\u53f6\u5f00\u6447\u6447\u5934\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u2018\u5b83\u5df2\u98de\u4e86\u51e0\u5343\u91cc\u8def\uff0c\u800c\u4e14\u8fd8\u4e3a\u6211\u5e26\u6765\u4e86\u4e00\u4e2a\u5f88\u91cd\u8981\u7684\u6d88\u606f\uff0c\u5c31\u7b97\u662f\u6211\u81ea \u5df1\u5272\u5757\u8089\u7ed9\u5b83\u5403\uff0c\u6211\u4e5f\u613f\u610f\u3002\u201d\u53f6\u5f00\u5fcd\u4e0d\u4f4f\u95ee\uff1a\u201c\u4ec0\u4e48\u6d88\u606f\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u9b54\u6559\u7684\u6d88\u606f\u3002\u201d \u3000\u3000\u53f6\u5f00\u53c8\u4e0d\u7981\u52a8\u5bb9\uff0c\u9053\uff1a\u201c\u8fd9\u53ea\u9e3d\u5b50\u7684\u4e3b\u4eba\u96be\u9053\u662f\u9b54\u6559\u7684\u6559\u4e3b\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4e0d\u662f\u6559\u4e3b\uff0c\u662f\u4e00\u4f4d\u516c\u4e3b\uff0c\u5f88\u7f8e\u7684\u516c\u4e3b\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u5979\u600e\u4e48\u4f1a\u8ddf\u4f60\u901a\u6d88\u606f\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u56e0\u4e3a\u5979\u4e5f\u662f\u4eba\uff0c\u53ea\u8981\u662f\u4eba\uff0c\u6211\u5c31\u6709\u6cd5\u5b50\u6536\u4e70\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u5979\u77e5\u9053\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u5979\u53ea\u77e5\u9053\u9b54\u6559\u7684\u5927\u5929\u738b\u4e2d\uff0c\u5df2\u6709\u4e09\u4e2a\u4eba\u5230\u4e86\u957f\u5b89\uff0c\u5374\u4e0d\u77e5\u9053\u4ed6\u4eec\u5728\u8fd9\u91cc \u7528\u7684\u662f\u4ec0\u4e48\u8eab\u4efd\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u5979\u4e5f\u4e0d\u77e5\u9053\u8fd9\u4e09\u4e2a\u4eba\u7684\u540d\u5b57\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u53f9\u9053\uff1a\u201c\u5c31\u7b97\u77e5\u9053\u4e5f\u6ca1\u6709\u7528\uff0c\u65e0\u8bba\u8c01\u5165\u4e86\u9b54\u6559\u540e\uff0c\u90fd\u5f97\u5c06\u81ea\u5df1\u8fc7\u53bb\u7684\u4e00\u5207\u5b8c\u5168 \u653e\u5f03\uff0c\u8fde\u672c\u6765\u7684\u540d\u5b57\u4e5f\u4e0d\u80fd\u518d\u7528\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6240\u4ee5\u5979\u53ea\u77e5\u9053\u8fd9\u4e09\u4e2a\u4eba\u9b54\u6559\u4e2d\u7528\u7684\u540d\u5b57\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u9b54\u6559\u4e2d\u7684\u56db\u5927\u5929\u738b\uff0c\u540d\u5b57\u90fd\u5f88\u7edd\uff0c\u4e00\u4e2a\u53eb\u201d\u789f\u513f\u5e03\u201d\uff0c\u4e00\u4e2a\u53eb \u2018\u591a\u5c14\u7532\u2019\uff0c\u4e00\u4e2a\u53eb\u2018\u5e03\u8fbe\u62c9\u2019\uff0c\u4e00\u4e2a\u53eb\u2018\u73ed\u5bdf\u5df4\u90a3\u2019\u3002\u8fd9\u90fd\u662f\u53e4\u8001\u7684\u85cf\u6587\uff0c\u2018\u789f\u513f\u5e03\u2019\u7684 \u610f\u601d\u8c61\u5f81\u7740\u667a\u6167\u3002\u2018\u591a\u5c14\u7532\u201d\u7684\u610f\u601d\uff0c\u8c61\u5f81\u7740\u6743\u6cd5\u3002\u2019\u5e03\u8fbe\u62c9\u2019\u662f\u5b64\u5cf0\u3002\u2018\u73ed\u5bdf\u5df4\u90a3\u2019\u662f\u7231 \u6b32\u4e4b\u795e\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u73b0\u5728\u9664\u4e86\u591a\u5c14\u7532\u5929\u738b\u8fd8\u7559\u5b88\u5728\u9b54\u5c71\u4e4b\u5916\uff0c\u5176\u4f59\u7684\u4e09\u5927\u5929\u738b\uff0c\u90fd\u5df2\u5230\u4e86\u957f \u5b89\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u8fd9\u6d88\u606f\u53ef\u9760\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u7edd\u5bf9\u53ef\u9760\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u4e5f\u731c\u4e0d\u51fa\u4ed6\u4eec\u662f\u8c01\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6211\u53ea\u60f3\u5230\u4e86\u4e00\u4e2a\u4eba\uff0c\u2018\u73ed\u5bdf\u5df4\u90a3\u2019\u5929\u738b\uff0c\u5f88\u53ef\u80fd\u5c31\u662f\u7389\u8427\u9053\u4eba\u3002\u201d \u3000\u3000\u7389\u7bab\u9053\u4eba\u8fd9\u4e00\u751f\u4e2d\uff0c\u7684\u786e\u5145\u6ee1\u4e86\u7231\u6b32\u3002 \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u80fd\u4e0d\u80fd\u4ece\u7389\u7bab\u9053\u4eba\u53e3\u4e2d\uff0c\u95ee\u51fa\u90a3\u4e24\u4e2a\u4eba\u6765\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4e0d\u80fd\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u4e5f\u4e0d\u80fd\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6211\u5c31\u7b97\u6709\u6cd5\u5b50\u80fd\u8ba9\u5404\u79cd\u4eba\u8bf4\u5b9e\u8bdd\uff0c\u4e5f\u6709\u4e00\u79cd\u4eba\u662f\u4f8b\u5916\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6b7b\u4eba\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u70b9\u70b9\u5934\u3002 \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u600e\u4e48\u6b7b\u7684\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6709\u4eba\u6740\u4e86\u4ed6\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u662f\u8c01\u6740\u4e86\u4e1c\u6d77\u7389\u7bab\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u6de1\u6de1\u9053\uff1a\u201c\u5728\u8fd9\u957f\u5b89\u57ce\u91cc\uff0c\u80fd\u6740\u4ed6\u7684\u4eba\u5e76\u4e0d\u6b62\u4e00\u4e2a\u3002\u201d \u3000\u3000\u53f6\u5f00\u6c89\u601d\u7740\uff0c\u5ffd\u7136\u957f\u957f\u53f9\u606f\uff0c\u9053\uff1a\u201c\u6211\u5728\u8fd9\u91cc\u624d\u4e0d\u8fc7\u5341\u6765\u5929\uff0c\u957f\u5b89\u57ce\u91cc\u5374\u4f3c\u5df2\u6709\u5f88\u591a\u53d8 \u5316\uff0c\u53d1\u751f\u4e86\u5f88\u591a\u4e8b\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u51dd\u89c6\u7740\u4ed6\uff0c\u8f7b\u8f7b\u9053\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u5df2\u60f3\u8d70\uff1f\u201d \u3000\u3000\u53f6\u5f00\u52c9\u5f3a\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u6211\u7684\u4f24\u5df2\u597d\u4e86\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u76ee\u4e2d\u53c8\u9732\u51fa\u5e7d\u6028\u4e4b\u8272\uff0c\u9053\uff1a\u201c\u4f24\u4e00\u597d\u5c31\u8981\u8d70\uff1f\u201d \u3000\u3000\u53f6\u5f00\u907f\u5f00\u4e86\u5979\u7684\u773c\u775b\uff0c\u9053\uff1a\u201c\u6211\u8fdf\u65e9\u603b\u662f\u8981\u8d70\u7684\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4f60\u51c6\u5907\u4ec0\u4e48\u65f6\u5019\u8d70\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u660e\u5929\u2026\u2026\u201d\u4ed6\u52c9\u5f3a\u7b11\u7740\u8bf4\uff1a\u201c\u6211\u82e5\u662f\u660e\u5929\u8d70\uff0c\u8fd8\u53ef\u4ee5\u5230\u957f\u5b89\u57ce\u53bb\u62dc\u62dc\u5e74\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u54ac\u7740\u5634\u5507\uff0c\u5c45\u7136\u4e5f\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u9664\u4e86\u62dc\u5e74\u5916\uff0c\u4f60\u8fd8\u53ef\u4ee5\u8d76\u4e0a\u4e00\u987f\u559c\u9152\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u8c01\u7684\u559c\u9152\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u6de1\u6de1\u9053\uff1a\u201c\u5f53\u7136\u662f\u4f60\u7684\u670b\u53cb\uff0c\u4e00\u4e2a\u8ddf\u4f60\u5f88\u8981\u597d\u7684\u670b\u53cb\u3002\u201d \u7b2c\u4e8c\u5341\u4e00\u7ae0\u3000\u9e3f\u5bbe\u5ba2\u6808>> \u53e4\u9f99\u300a\u4e5d\u6708\u9e70\u98de\u300b \u7b2c\u4e8c\u5341\u4e00\u7ae0\u3000\u9e3f\u5bbe\u5ba2\u6808 \u3000\u3000\u53f6\u5f00\u771f\u7684\u8d70\u4e86\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u5c45\u7136\u6ca1\u6709\u7559\u4ed6\uff0c\u53ea\u4e0d\u8fc7\u633d\u4f4f\u4ed6\u7684\u624b\uff0c\u4e00\u76f4\u9001\u4ed6\u5230\u8857\u5934\u3002 \u3000\u3000\u65e0\u8bba\u8c01\u770b\u5230\u4ed6\u4eec\uff0c\u90fd\u4e00\u5b9a\u4f1a\u8ba4\u4e3a\u4ed6\u4eec\u662f\u73e0\u8054\u58c1\u5408\uff0c\u5f88\u7406\u60f3\u7684\u4e00\u5bf9\u3002\u4f46\u4ed6\u4eec\u7a76\u7adf\u662f\u60c5\u4eba\uff1f \u662f\u670b\u53cb\uff1f\u8fd8\u662f\u51a4\u5bb6\u5bf9\u5934\uff1f\u8fd9\u53ea\u6015\u8fde\u4ed6\u4eec\u81ea\u5df1\u90fd\u5206\u4e0d\u6e05\u695a\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u5f88\u6c89\u9ed8\uff0c\u663e\u5f97\u5fc3\u4e8b\u91cd\u91cd\u3002\u53f6\u5f00\u8fd9\u4e00\u8d70\uff0c\u662f\u4e0d\u662f\u8fd8\u53ef\u80fd\u56de\u5230\u5979\u8eab\u8fb9\u6765\uff1f\u4ed6\u4eec\u8fd8\u6709 \u6ca1\u6709\u76f8\u805a\u7684\u65f6\u5019\uff1f \u3000\u3000\u672a\u6765\u7684\u4e8b\uff0c\u53c8\u6709\u8c01\u80fd\u77e5\u9053\uff1f\u8c01\u6562\u9884\u6d4b\uff1f \u3000\u3000\u53f6\u5f00\u5ffd\u7136\u9053\uff1a\u201c\u6211\u60f3\u4e86\u5f88\u4e45\uff0c\u5374\u8fd8\u662f\u60f3\u4e0d\u51fa\u789f\u513f\u5e03\u548c\u5e03\u8fbe\u62c9\u5929\u738b\u662f\u4ec0\u4e48\u4eba\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u5e7d\u5e7d\u5730\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u65e2\u7136\u60f3\u4e0d\u51fa\uff0c\u53c8\u4f55\u5fc5\u53bb\u60f3\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u4e0d\u80fd\u4e0d\u60f3\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u8f7b\u8f7b\u53f9\u9053\uff1a\u201c\u4eba\u4eec\u4e3a\u4ec0\u4e48\u603b\u662f\u8981\u53bb\u60f3\u4e00\u4e9b\u4ed6\u672c\u4e0d\u8be5\u60f3\u7684\u4e8b\u3002\u201d \u3000\u3000\u53f6\u5f00\u4e0d\u6562\u56de\u7b54\u8fd9\u53e5\u8bdd\uff0c\u4e5f\u4e0d\u56de\u7b54\u3002 \u3000\u3000\u4ed6\u53ea\u6709\u6c89\u9ed8\uff0c\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u5374\u53c8\u5fcd\u4e0d\u4f4f\u9053\uff1a\u201c\u6211\u60f3\uff0c\u2018\u789f\u513f\u5e03\u2019\u5929\u738b\u4e00\u5b9a\u662f\u4e2a\u5f88\u6709\u667a\u8c0b \u7684\u4eba\uff0c\u2018\u5e03\u8fbe\u62c9\u2019\u5929\u738b\u4e00\u5b9a\u5f88\u5b64\u9ad8\u9a84\u505a\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u70b9\u70b9\u5934\uff1a\u201c\u9b54\u6559\u4e2d\u53d6\u7684\u540d\u5b57\uff0c\u5f53\u7136\u7edd\u4e0d\u4f1a\u662f\u6ca1\u6709\u9053\u7406\u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4ee5\u4f60\u770b\uff0c\u73b0\u5728\u957f\u5b89\u57ce\u91cc\u6700\u6709\u667a\u6167\u7684\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u662f\u4f60\uff01\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u63a5\u9053\uff1a\u201c\u53ea\u6709\u667a\u8005\uff0c\u624d\u6709\u6167\u5251\u3002\u201d \u3000\u3000\u2014\u2014\u53ea\u6709\u4f60\u7684\u6167\u5251\uff0c\u624d\u80fd\u65a9\u65ad\u6211\u8981\u7f20\u4f4f\u4f60\u7684\u60c5\u4e1d\u3002 \u3000\u3000\u8fd9\u53e5\u8bdd\u5979\u5e76\u6ca1\u6709\u8bf4\u51fa\u6765\uff0c\u4e5f\u4e0d\u5fc5\u8bf4\u51fa\u672a\uff0c\u53f6\u5f00\u5f53\u7136\u80fd\u4e86\u89e3\u3002 \u3000\u3000\u4ed6\u5728\u82e6\u7b11\uff1a\u201c\u5927\u667a\u82e5\u611a\uff0c\u771f\u6b63\u7684\u806a\u660e\u4eba\uff0c\u770b\u8d77\u6765\u4e5f\u8bb8\u50cf\u4e2a\u5446\u5b50\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u4e5f\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u957f\u5b89\u57ce\u91cc\uff0c\u770b\u6765\u50cf\u5446\u5b50\u7684\u4eba\u960e\u4e0d\u5c11\uff0c\u771f\u6b63\u7684\u5446\u5b50\u4e5f\u4e0d\u5c11\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u8ba4\u4e3a\u6700\u9a84\u505a\u7684\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4f60\uff01\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u6de1\u6de1\u9053\uff1a\u201c\u53ea\u6709\u6700\u9a84\u505a\u7684\u4eba\uff0c\u624d\u4f1a\u62d2\u7edd\u522b\u4eba\u7684\u771f\u60c5\u597d\u610f\u3002\u201d \u3000\u3000\u5979\u8bf4\u7684\u201c\u522b\u4eba\u201d\u5f53\u7136\u5c31\u662f\u5979\u81ea\u5df1\u3002 \u3000\u3000\u2014\u2014\u96be\u9053\u5979\u5bf9\u53f6\u5f00\u771f\u7684\u6709\u4e00\u756a\u771f\u60c5\uff1f \u3000\u3000\u53f6\u5f00\u8f6c\u8fc7\u5934\uff0c\u9065\u89c6\u7740\u8fdc\u65b9\u7684\u4e00\u6735\u767d\u4e91\uff0c\u4e16\u4e0a\u53c8\u6709\u51e0\u4e2a\u4eba\u80fd\u50cf\u767d\u4e91\u822c\u60a0\u95f2\u81ea\u5728\uff0c\u65e0\u62d8\u65e0 \u675f\uff1f \u3000\u3000\u6bcf\u4e2a\u4eba\u5fc3\u91cc\u5c82\u975e\u90fd\u6709\u628a\u9501\u94fe\uff1f \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u5ffd\u7136\u53c8\u95ee\u9053\uff1a\u201c\u9664\u4e86\u4f60\u4e4b\u5916\uff0c\u4e5f\u8bb8\u8fd8\u6709\u4e00\u4e24\u4e2a\u4eba\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u8c01\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u5415\u8fea\u3001\u90ed\u5b9a\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4ed6\u4eec\u5f53\u7136\u90fd\u7edd\u4e0d\u662f\u9b54\u6559\u4e2d\u7684\u4eba\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u662f\u4e0d\u662f\u56e0\u4e3a\u4ed6\u4eec\u7684\u51fa\u8eab\u597d\uff0c\u5bb6\u4e16\u597d\uff0c\u6240\u4ee5\u5c31\u4e0d\u4f1a\u5165\u9b54\u6559\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u53ea\u4e0d\u8fc7\u89c9\u5f97\u4ed6\u4eec\u90fd\u6ca1\u6709\u9b54\u6559\u95e8\u4e0b\u7684\u90a3\u79cd\u90aa\u6c14\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4e0d\u7ba1\u600e\u4e48\u6837\uff0c\u2018\u789f\u513f\u5e02\u2019\u548c\u2018\u5e03\u62c9\u8fbe\u2019\u90fd\u5df2\u5728\u957f\u5b89\u57ce\uff0c\u4e5f\u8bb8\u5c31\u662f\u4f60\u6700\u60f3 \u4e0d\u5230\u7684\u4e24\u3001\u4eba\uff0c\u56e0\u4e3a\u4ed6\u4eec\u7684\u884c\u8e2a\u4e00\u5411\u90fd\u662f\u522b\u4eba\u6c38\u8fdc\u60f3\u4e0d\u5230\u7684\uff0c\u8fd9\u624d\u771f\u6b63\u662f\u9b54\u6559\u6700\u90aa\u7684\u5730 \u65b9\u3002\u201d \u3000\u3000\u53f6\u5f00\u53f9\u4e86\u53e3\u6c14\uff0c\u4e5f\u4e0d\u7981\u9732\u51fa\u4f18\u8651\u4e4b\u8272\u3002 \u3000\u3000\u9b54\u6559\u95e8\u4e0b\uff0c\u4e0d\u5230\u7edd\u5bf9\u5fc5\u8981\u65f6\uff0c\u662f\u6c38\u8fdc\u4e5f\u4e0d\u4f1a\u9732\u51fa\u5f62\u8ff9\u6765\u7684\uff0c\u5f80\u5f80\u8981\u7b49\u5230\u5df2\u6b7b\u5728\u4ed6\u4eec\u624b\u91cc \u65f6\uff0c\u624d\u80fd\u770b\u51fa\u4ed6\u4eec\u7684\u771f\u9762\u76ee\u3002 \u3000\u3000\u4ed6\u4eec\u8fd9\u6b21\u5230\u957f\u5b89\u6765\uff0c\u771f\u6b63\u8981\u627e\u7684\u5bf9\u8c61\u662f\u8c01\uff1f \u3000\u3000\u662f\u4e0a\u5b98\u5c0f\u4ed9\uff1f\u8fd8\u662f\u53f6\u5f00\uff1f \u3000\u3000\u53f6\u5f00\u52c9\u5f3a\u7b11\u9053\uff1a\u201c\u53ea\u8981\u4ed6\u4eec\u7684\u786e\u5df2\u5230\u4e86\u957f\u5b89\u57ce\uff0c\u6211\u8fdf\u65e9\u603b\u4f1a\u627e\u5230\u4ed6\u4eec\u7684\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u56e0\u4e3a\uff0c\u4eca\u5929\u4f60\u4e00\u5b9a\u8981\u5148\u5230\u9e3f\u5bbe\u5ba2\u6808\u53bb\u559d\u9152\u3002\u201d\u5979\u7f8e\u4e3d\u7684\u773c\u775b\u91cc\uff0c\u5e26\u7740\u79cd \u9488\u5c16\u822c\u7684\u7b11\u610f\uff1a\u201c\u56e0\u4e3a\u4f60\u82e5\u4e0d\u53bb\uff0c\u6709\u5f88\u591a\u4eba\u90fd\u4f1a\u4f24\u5fc3\u7684\uff01\u201d \u3000\u3000\u4f46\u53f6\u5f00\u5374\u6ca1\u6709\u5230\u9e3f\u5bbe\u5ba2\u6808\u53bb\uff0c\u76f4\u5230\u9ec4\u660f\u524d\uff0c\u4ed6\u8fd8\u6ca1\u6709\u5728\u9e3f\u5bbe\u5ba2\u6808\u51fa\u73b0\u8fc7\u3002 \u3000\u3000\u5927\u5e74\u521d\u4e00\uff0c\u5348\u540e\u3002 \u3000\u3000\u4eca\u5929\u4e0a\u5348\u65f6\uff0c\u5929\u6c14\u5c45\u7136\u5f88\u6674\u6717\uff0c\u84dd\u592d\u767d\u4e91\uff0c\u9633\u5149\u7167\u8000\uff0c\u5927\u5730\u5df2\u6709\u4e86\u6625\u8272\u3002 \u3000\u3000\u90ed\u5b9a\u7684\u6c14\u8272\u770b\u6765\u4e5f\u597d\u5f97\u591a\u4e86\uff0c\u201c\u4eba\u9022\u559c\u4e8b\u7cbe\u795e\u723d\u201d\uff0c\u4e00\u53e5\u5df2\u8bf4\u4e86\u51e0\u5343\u51e0\u767e\u5e74\u7684\u8bdd\uff0c\u591a\u591a \u5c11\u5c11\u603b\u662f\u6709\u4e9b\u9053\u7406\u7684\u3002 \u3000\u3000\u4e01\u7075\u7433\u6b63\u6367\u7740\u7897\u53c2\u6c64\uff0c\u5728\u4e00\u53e3\u4e00\u53e3\u5730\u5582\u4ed6\u3002 \u3000\u3000\u4ed6\u4eec\u4e00\u76f4\u5f88\u5c11\u8bf4\u8bdd\uff0c\u8c01\u4e5f\u4e0d\u77e5\u9053\u8be5\u8bf4\u4e9b\u4ec0\u4e48\uff0c\u5fc3\u91cc\u66f4\u4e0d\u77e5\u662f\u751c\uff1f \u3000\u3000\u662f\u9178\uff1f\u662f\u82e6\uff1f \u3000\u3000\u4eba\u751f\u5c82\u975e\u672c\u5c31\u662f\u8fd9\u6837\u5b50\u7684\u3002 \u3000\u3000\u547d\u8fd0\u7684\u5b89\u6392\uff0c\u65e2\u7136\u6ca1\u6709\u4eba\u80fd\u53cd\u6297\uff0c\u90a3\u4e48\u4ed6\u4eec\u53c8\u4f55\u5fc5\uff1f \u3000\u3000\u4e01\u7075\u7433\u4e5f\u626b\u201c\u8d77\u4e86\u7cbe\u795e\uff0c\u9732\u51fa\u4e86\u7b11\u8138\uff0c\u770b\u6765\u5c31\u50cf\u662f\u8fd9\u51ac\u5929\u7684\u9633\u5149\u4e00\u6837\u3002\u90ed\u5b9a\u60f3\u591a\u770b\u5979\u51e0 \u773c\uff0c\u53c8\u4e0d\u6562\uff0c\u53ea\u6709\u5782\u7740\u5934\u770b\u7740\u5979\u4e00\u53cc\u767d\u751f\u751f\u7684\u624b\uff0c\u5ffd\u7136\u9053\uff1a\u201c\u8fd9\u4eba\u53c2\u662f\u4e0d\u662f\u5f88\u8d35\uff1f\u201d \u3000\u3000\u4e01\u7075\u7433\u70b9\u70b9\u5934\u3002 \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u6211\u4eec\u80fd\u4e70\u5f97\u8d77\uff1f\u201d \u3000\u3000\u4e86\u7075\u7433\u9053\uff1a\u201c\u4e70\u4e0d\u8d77\u3002\u201d \u3000\u3000\u90ed\u5b9a\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u662f\u2026\u2026\u201d \u3000\u3000\u4e01\u7075\u7433\u7a81\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u662f\u6211\u8d4a\u6765\u7684\uff0c\u56e0\u4e3a\u6211\u60f3\u4eca\u5929\u4e00\u5b9a\u6709\u5f88\u591a\u4eba\u4f1a\u9001\u793c\u6765\uff0c\u957f\u5b89\u57ce \u91cc\uff0c\u4e00\u5b9a\u6709\u5f88\u591a\u4eba\u8981\u6765\u770b\u770b\u6211\u4eec\uff0c\u559d\u4e24\u676f\u6211\u4eec\u7684\u559c\u9152\uff0c\u8fd9\u4e9b\u4eba\u4e00\u5b9a\u90fd\u4e0d\u4f1a\u662f\u5f88\u5c0f\u6c14\u7684\u4eba\u3002\u201d \u3000\u3000\u90ed\u5b9a\u8fdf\u7591\u7740\uff0c\u9053\uff1a\u201c\u6211\u4eec\u7684\u4e8b\uff0c\u5df2\u7ecf\u6709\u5f88\u591a\u4eba\u77e5\u9053\uff1f\u201d \u3000\u3000\u4e01\u7075\u7433\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6240\u4ee5\u6211\u5df2\u53eb\u638c\u67dc\u7684\u66ff\u6211\u4eec\u51c6\u5907\u4e86\u5341\u4e8c\u684c\u559c\u9152\u3002\u201d \u3000\u3000\u90ed\u5b9a\u5fcd\u4e0d\u4f4f\u62ac\u8d77\u5934\uff0c\u770b\u7740\u5979\uff0c\u4e5f\u4e0d\u77e5\u662f\u6b22\u559c\uff0c\u8fd8\u662f\u60b2\u4f24\uff1a\u201c\u5176\u5b9e\u4f60\u672c\u4e0d\u5fc5\u8fd9\u4e48\u505a\u7684\uff0c \u6211\u2026\u2026\u201d \u3000\u3000\u4e01\u7075\u7433\u6ca1\u6709\u8ba9\u4ed6\u8bf4\u4e0b\u53bb\uff0c\u63e1\u4f4f\u4e86\u4ed6\u7684\u624b\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u4f60\u53ea\u8981\u6253\u8d77\u7cbe\u795e\u6765\uff0c\u8d76\u5feb\u628a\u4f24\u517b \u597d\uff0c\u5343\u4e07\u4e0d\u8981\u8ba9\u6211\u505a\u5be1\u5987\u3002\u201d \u3000\u3000\u90ed\u5b9a\u4e5f\u7b11\u4e86\uff0c\u7b11\u5f97\u867d\u8f9b\u9178\uff0c\u5374\u4e5f\u5e26\u6709\u51e0\u5206\u751c\u871c\u3002 \u3000\u3000\u4e0d\u7ba1\u600e\u4e48\u6837\uff0c\u4ed6\u90fd\u5df2\u4e0b\u4e86\u51b3\u5fc3\uff0c\u8981\u597d\u597d\u7167\u987e\u8fd9\u4e2a\u53ef\u7231\u7684\u5973\u4eba\uff0c\u7167\u987e\u5979\u4e00\u8f88\u5b50\u3002 \u3000\u3000\u5c31\u51ed\u8fd9\u70b9\u51b3\u5fc3\uff0c\u4ed6\u5df2\u4e0d\u4f1a\u6b7b\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u81ea\u5df1\u5fc3\u751f\u7684\u6597\u5fd7\uff0c\u5f80\u5f80\u6bd4\u4efb\u4f55\u836f\u90fd\u6709\u6548\u3002 \u3000\u3000\u8001\u638c\u67a2\u7684\u5ffd\u7136\u5728\u95e8\u5916\u547c\u5524\uff1a\u201c\u4e01\u59d1\u5a18\uff0c\u4f60\u5df2\u8be5\u51fa\u6765\u6253\u626e\u6253\u626e\u4e86\uff0c\u6211\u4e5f\u627e\u4eba\u6765\u66ff\u90ed\u516c\u5b50\u6d17 \u6d17\u6fa1\u6362\u8863\u88f3\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u62cd\u4e86\u62cd\u90ed\u5b9a\u7684\u624b\uff0c\u63a8\u95e8\u8d70\u51fa\u53bb\uff0c\u770b\u7740\u8fd9\u5584\u826f\u7684\u8001\u4eba\uff0c\u5fcd\u4e0d\u4f4f\u8f7b\u8f7b\u53f9\u9053\uff1a\u201c\u4f60\u771f\u662f \u4e2a\u597d\u4eba\u3002\u201d\u539f\u6765\u8fd9\u4e16\u754c\u4e0a\u8fd8\u662f\u5230\u5904\u90fd\u6709\u597d\u4eba\u7684\u3002 \u3000\u3000\u8001\u638c\u67dc\u5fae\u7b11\u9053\uff1a\u201c\u4eca\u5929\u662f\u5927\u5e74\u521d\u4e00\uff0c\u6211\u53ea\u76fc\u671b\u4eca\u5e74\u5927\u5bb6\u90fd\u8fc7\u5f97\u987a\u9042\uff0c\u5927\u5bb6\u90fd\u5f00\u5fc3\u3002\u201d \u3000\u3000\u4ed6\u662f\u4e2a\u597d\u4eba\uff0c\u6240\u4ee5\u624d\u4f1a\u6709\u8fd9\u79cd\u613f\u671b\uff0c\u53ef\u662f\u4ed6\u7684\u613f\u671b\u662f\u4e0d\u662f\u80fd\u5b9e\u73b0\uff1f \u3000\u3000\u4e01\u7075\u7433\u5fc3\u91cc\u5ffd\u7136\u89c9\u5f97\u4e00\u201c\u9635\u9178\u8f6f\uff0c\u6cea\u73e0\u5df2\u51e0\u4e4e\u5fcd\u4e0d\u4f4f\u8981\u6d41\u4e0b\u6765\u3002\u5979\u632f\u4f5c\u7cbe\u795e\uff0c\u52c9\u5f3a\u7b11\u4e86 \u7b11\uff0c\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u73b0\u5728\u662f\u4e0d\u662f\u5df2\u7ecf\u6709\u4eba\u9001\u4e86\u793c\u6765\uff1f\u201d \u3000\u3000\u8001\u638c\u67dc\u7b11\u9053\uff1a\u201c\u9001\u793c\u7684\u4eba\u53ef\u771f\u4e0d\u5c11\uff0c\u6211\u5df2\u628a\u9001\u6765\u7684\u793c\u90fd\u8bb0\u4e86\u5e10\uff0c\u4e01\u59d1\u5a18\u662f\u4e0d\u662f\u60f3\u53bb\u770b \u770b\uff1f\u201d \u3000\u3000\u4e01\u7075\u7433\u5f88\u60f3\u53bb\u770b\u770b\u3002 \u3000\u3000\u5979\u5df2\u60f3\u5230\u4e00\u5b9a\u4f1a\u6709\u5f88\u591a\u5947\u602a\u7684\u4eba\uff0c\u9001\u4e00\u4e9b\u5947\u602a\u7684\u793c\u7269\u6765\u3002 \u3000\u3000\u4e01\u7075\u7433\u60f3\u5230\u4e86\u5f88\u591a\u4e8b\uff0c\u5374\u8fd8\u662f\u6ca1\u6709\u60f3\u5230\uff0c\u7b2c\u4e00\u4e2a\u9001\u793c\u7684\u4eba\uff0c\u7adf\u662f\u201c\u98de\u72d0\u201d\u6768\u5929\uff01 \u3000\u3000\u5e10\u7c3f\u4e0a\u7b2c\u4e00\u4e2a\u540d\u5b57\u5c31\u662f\u4ed6\uff01 \u3000\u3000\u201c\u6768\u5929\uff1a\u793c\u54c1\u56db\u8272\u3002\u73e0\u82b1\u4e00\u5bf9\uff0c\u78a7\u7389\u956f\u4e00\u53cc\uff0c\u8d64\u91d1\u5934\u9762\u5168\u5957\uff1b\u7eaf\u91d1\u53e4\u94b1\u56db\u5341\u679a\uff0c\u5171\u91cd\u56db \u767e\u4e24\u3002\u201d \u3000\u3000\u7eaf\u91d1\u53e4\u94b1\uff0c\u8fd9\u610f\u601d\u663e\u7136\u662f\u8bf4\uff0c\u4ed6\u7684\u793c\u662f\u4ee3\u8868\u91d1\u94b1\u5e2e\u9001\u7684\uff0c\u4e5f\u5c31\u662f\u4ee3\u8868\u4e0a\u5b98\u5c0f\u4ed9\u9001\u7684\u3002 \u3000\u3000\u4e01\u7075\u7433\u63e1\u7d27\u53cc\u62f3\uff0c\u5fc3\u91cc\u4e0d\u7981\u5728\u51b7\u7b11\u3002\u5979\u5e0c\u671b\u4e0a\u5b98\u5c0f\u4ed9\u665a\u4e0a\u4e0d\u559d\u559c\u9152\u3002 \u3000\u3000\u5415\u8fea\u5c45\u7136\u4e5f\u9001\u4e86\u793c\u6765\uff0c\u662f\u548c\u516b\u65b9\u9556\u5c40\u7684\u675c\u540c\u4e00\u8d77\u9001\u6765\u7684\uff0c\u9664\u4e86\u793c\u54c1\u56db\u5305\u5916\uff0c\u8fd8\u6709\u201c\u6781\u54c1 \u4f24\u836f\u4e00\u74f6\u201d\u3002 \u3000\u3000\u4e01\u7075\u7433\u53c8\u4e0d\u7981\u51b7\u7b11\u3002 \u3000\u3000\u5979\u5df2\u51b3\u5fc3\u4e0d\u7528\u8fd9\u74f6\u836f\uff0c\u4e0d\u7ba1\u5415\u8fea\u662f\u4e0d\u662f\u771f\u7684\u597d\u610f\uff0c\u5979\u90fd\u4e0d\u80fd\u5192\u8fd9\u79cd\u9669\u3002 \u3000\u3000\u8fd8\u6709\u4e9b\u4eba\u7684\u540d\u5b57\uff0c\u4e01\u7075\u7433\u4f3c\u66fe\u76f8\u8bc6\uff0c\u5374\u53c8\u8bb0\u4e0d\u592a\u6e05\u4e86\uff0c\u8fd9\u4e9b\u4eba\u597d\u50cf\u90fd\u662f\u4e01\u5bb6\u7684\u4e16\u5bb6\u65e7 \u53cb\u3002 \u3000\u3000\u4e01\u5bb6\u672c\u5c31\u662f\u6b66\u6797\u7684\u4e16\u5bb6\uff0c\u6545\u65e7\u6ee1\u5929\u4e0b\uff0c\u5176\u4e2d\u5f53\u7136\u4e5f\u6709\u5f88\u591a\u4eba\u5230\u4e86\u957f\u5b89\u3002 \u3000\u3000\u53ef\u662f\u4e01\u5bb6\u7684\u4eba\u5462\uff1f\u8fd9\u4e2a\u4e5f\u66fe\u5728\u6b66\u6797\u4e2d\u663e\u8d6b\u4e00\u65f6\u7684\u5bb6\u65cf\uff0c\u5982\u4eca\u5df2\u53d8\u6210\u4ec0\u4e48\u6837\u5b50\uff1f \u3000\u3000\u4e01\u7075\u7433\u8fde\u60f3\u90fd\u4e0d\u6562\u60f3\u3002 \u3000\u3000\u5979\u7ee7\u7eed\u770b\u4e0b\u53bb\uff0c\u53c8\u770b\u5230\u4e00\u4e2a\u610f\u5916\u7684\u540d\u5b57\u3002 \u3000\u3000\u5d14\u7389\u8d1e\u3002 \u3000\u3000\u5979\u5c45\u7136\u8fd8\u6ca1\u6709\u6b7b\u3002 \u3000\u3000\u8fd9\u4e9b\u65e5\u5b50\u6765\uff0c\u5979\u4e3a\u4ec0\u4e48\u4e00\u4e0b\u90fd\u6ca1\u6709\u51fa\u73b0\u8fc7\uff1f\u5979\u662f\u4e0d\u662f\u4e5f\u5df2\u77e5\u9053\u53f6\u5f00\u7684\u6b7b\u8baf\uff1f \u3000\u3000\u8001\u638c\u67dc\u5728\u65c1\u8fb9\u5fae\u7b11\u9053\uff1a\u201c\u6211\u5b9e\u5728\u60f3\u4e0d\u5230\u4e01\u59d1\u5a18\u5728\u957f\u5b89\u57ce\u91cc\u7adf\u6709\u8fd9\u4e48\u591a\u670b\u53cb\uff0c\u4eca\u5929\u665a\u4e0a\uff0c \u60f3\u5fc5\u4e00\u5b9a\u70ed\u95f9\u5f97\u5f88\u3002\u201d \u3000\u3000\u4ed6\u4eec\u7684\u559c\u4e8b\u770b\u6765\u786e\u5b9e\u5df2\u8f70\u52a8\u4e86\u957f\u5b89\u3002 \u3000\u3000\u4e01\u7075\u7433\u5ffd\u7136\u53d1\u73b0\u81ea\u5df1\u539f\u6765\u4e5f\u662f\u4e2a\u540d\u4eba\u4e00\u90a3\u662f\u4e0d\u662f\u56e0\u4e3a\u53f6\u5f00\uff1f \u3000\u3000\u5979\u53c8\u7981\u6b62\u81ea\u5df1\u518d\u60f3\u4e0b\u53bb\uff0c\u65e0\u8bba\u5982\u4f55\uff0c\u5979\u4eca\u5929\u7edd\u4e0d\u80fd\u53bb\u60f3\u53f6\u5f00\uff0c\u81f3\u5c11\u4eca\u5929\u2026\u2026\u4eca\u5929\u7edd\u4e0d \u60f3\u3002 \u3000\u3000\u5979\u770b\u5230\u6700\u540e\u4e00\u4e2a\u540d\u5b57\uff0c\u5fc3\u5ffd\u7136\u6c89\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u201c\u5357\u5b98\u6d6a\uff0c\u5b57\u753b\u4e00\u5377\u3002\u201d \u3000\u3000\u5979\u77e5\u9053\u8fd9\u540d\u5b57\uff0c\u4e5f\u77e5\u9053\u8fd9\u4e2a\u4eba\u3002 \u3000\u3000\u6bcf\u4e2a\u4e16\u5bb6\u5927\u65cf\u4e2d\uff0c\u90fd\u5fc5\u5b9a\u4f1a\u6709\u4e00\u4e24\u4e2a\u7279\u522b\u51f6\u72e0\u6076\u6bd2\u7684\u4eba\u3002 \u3000\u3000\u5357\u5bab\u6d6a\u5c31\u662f\u201c\u5357\u5b98\u4e16\u5bb6\u201d\u4e2d\u6700\u53ef\u6015\u7684\u4eba\u3002 \u3000\u3000\u4ed6\u662f\u4e2a\u58f0\u540d\u72fc\u85c9\u7684\u5927\u76d7\uff0c\u662f\u5357\u5b98\u4e16\u5bb6\u7684\u4e0d\u8096\u5b50\u5f1f\uff0c\u4f46\u4ed6\u5374\u4e5f\u662f\u5357\u5b98\u8fdc\u7684\u5ae1\u4eb2\u53d4\u53d4\u3002 \u3000\u3000\u5357\u5b98\u8fdc\u5df2\u4f24\u5728\u90ed\u5b9a\u5251\u4e0b\uff0c\u5357\u5bab\u6d6a\u5ffd\u7136\u5728\u8fd9\u91cc\u51fa\u73b0\uff0c\u662f\u4e3a\u4e86\u4ec0\u4e48\uff1f \u3000\u3000\u4e01\u7075\u7433\u5fcd\u4e0d\u4f4f\u95ee\uff1a\u201c\u4f60\u770b\u8fc7\u8fd9\u4eba\u9001\u6765\u7684\u5b57\u753b\u6ca1\u6709\uff1f\u201d \u3000\u3000\u8001\u638c\u67dc\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u4e01\u59d1\u5a18\u82e5\u662f\u60f3\u770b\u770b\uff0c\u6211\u73b0\u5728\u5c31\u53ef\u4ee5\u53bb\u62ff\u51fa\u6765\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u5f53\u7136\u4e5f\u5f88\u60f3\u770b\u770b\u3002 \u3000\u3000\u753b\u5377\u5df2\u5c55\u5f00\uff0c\u4e0a\u9762\u53ea\u753b\u7740\u4e24\u4e2a\u4eba\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u624b\u63e1\u957f\u5251\uff0c\u7ad9\u5728\u4e00\u5bf9\u7ea2\u70db\u524d\uff0c\u5251\u4e0a\u8fd8\u5728\u6ef4\u7740\u8840\u3002 \u3000\u3000\u4ed6\u8eab\u4e0a\u7684\u8863\u7740\u5251\u9970\uff0c\u90fd\u753b\u5f97\u5f88\u751f\u52a8\uff0c\u4f46\u4e00\u5f20\u8138\u5374\u662f\u7a7a\u767d\u7684\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u7adf\u6ca1\u6709\u8138\u3002 \u3000\u3000\u53e6\u4e00\u4e2a\u4eba\u5df2\u5012\u5728\u4ed6\u5251\u4e0b\uff0c\u8eab\u4e0a\u7a7f\u7684\uff0c\u8d6b\u7136\u7ade\u662f\u90ed\u5b9a\u7684\u6253\u626e\u3002 \u3000\u3000\u4e01\u7075\u7433\u8138\u8272\u5df2\u53d8\u4e86\u3002 \u3000\u3000\u5357\u5bab\u6d6a\u7684\u610f\u601d\u5df2\u5f88\u660e\u663e\uff0c\u4ed6\u662f\u6765\u66ff\u5357\u5bab\u8fdc\u590d\u4ec7\u7684\uff0c\u4ed6\u4eca\u5929\u665a\u4e0a\u5c31\u8981\u90ed\u5b9a\u6b7b\u5728\u4ed6\u7684\u5251\u4e0b\uff0c \u6b7b\u5728\u559c\u5802\u91cc\u7684\u90a3\u5bf9\u9f99\u51e4\u82b1\u70db\u524d\u3002 \u3000\u3000\u90ed\u5b9a\u5df2\u53d7\u4e86\u91cd\u4f24\uff0c\u5df2\u6ca1\u6709\u53cd\u6297\u4e4b\u529b\u3002 \u3000\u3000\u8001\u638c\u67dc\u7684\u4e5f\u5df2\u770b\u51fa\u5979\u7684\u6050\u60e7\uff0c\u6025\u7740\u8981\u5c06\u8fd9\u5377\u753b\u6536\u8d77\u6765\uff0c\u5ffd\u542c\u5916\u9762\u6709\u4eba\u95f4\uff1a\u201c\u8fd9\u91cc\u662f\u4e0d\u662f \u9e3f\u5bbe\u5ba2\u6808\uff1f\u201d \u3000\u3000\u95ee\u8bdd\u7684\u662f\u4e2a\u9ec4\u888d\u9ed1\u53d1\u7684\u4e2d\u5e74\u4eba\uff0c\u8eab\u4e0a\u7684\u957f\u888d\u76d6\u819d\uff0c\u9ec4\u5f97\u53d1\u4eae\uff0c\u9ec4\u5f97\u50cf\u662f\u91d1\u5b50\uff0c\u4e00\u5f20\u8138\u5374 \u662f\u9634\u60e8\u60e8\u7684\uff0c\u5168\u65e0\u8868\u60c5\u3002 \u3000\u3000\u5c31\u8fd9\u4e48\u6837\u4e00\u4e2a\u4eba\uff0c\u770b\u6765\u5df2\u7ecf\u5f88\u5947\u79d8\u8be1\u5f02\uff0c\u66f4\u5947\u602a\u7684\u662f\uff0c\u4ed6\u8eab\u540e\u8fd8\u6709\u4e09\u4e2a\u4eba\uff0c\u88c5\u675f\u795e\u60c5\u5c45 \u7136\u4e5f\u8ddf\u4ed6\u5b8c\u5168\u4e00\u6a21\u4e00\u6837\u3002 \u3000\u3000\u8001\u638c\u67dc\u5fc3\u91cc\u867d\u7136\u6709\u70b9\u53d1\u6bdb\uff0c\u5374\u4e0d\u80fd\u4e0d\u6253\u8d77\u7b11\u8138\uff1a\u201c\u5c0f\u53f7\u6b63\u662f\u9e3f\u5bbe\u3002\u201d \u3000\u3000\u9ec4\u8863\u4eba\u9053\uff1a\u201c\u90ed\u5b9a\u90ed\u516c\u5b50\u548c\u4e86\u7075\u7433\u4e01\u59d1\u5a18\u7684\u559c\u4e8b\uff0c\u662f\u4e0d\u662f\u5c31\u5728\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u201c\u6b63\u662f\u5728\u8fd9\u91cc\u3002\u201d \u3000\u3000\u8001\u638c\u67dc\u5077\u5077\u770b\u4e86\u4e01\u7075\u7433\u4e00\u773c\uff0c\u4e01\u7075\u7433\u8138\u4e0a\u4e5f\u5e26\u7740\u5f88\u60ca\u5947\u7684\u8868\u60c5\u3002 \u3000\u3000\u663e\u7136\u4e5f\u4e0d\u8ba4\u5f97\u8fd9\u56db\u4e2a\u4eba\u3002 \u3000\u3000\u5979\u65e2\u7136\u6ca1\u6709\u53cd\u5e94\uff0c\u8001\u638c\u67dc\u53ea\u6709\u642d\u6c55\u7740\u95f4\u9053\uff0c\u201c\u5ba2\u5b98\u662f\u6765\u627e\u90ed\u516c\u5b50\u7684\uff1f\u201d \u3000\u3000\u9ec4\u8863\u4eba\u9053\uff1a\u201c\u4e0d\u662f\u3002\u201d \u3000\u3000\u201c\u662f\u6765\u9001\u793c\u7684\uff1f\u201d \u3000\u3000\u201c\u4e5f\u4e0d\u662f\u3002\u201d \u3000\u3000\u8001\u638c\u67dc\u52c9\u5f3a\u8d54\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u9001\u793c\u4e5f\u4e00\u6837\u53ef\u4ee5\u559d\u559c\u9152\uff0c\u56db\u4f4d\u5c31\u8bf7\u540e\u9762\u5750\uff0c\u5148\u8bf7\u7528\u8336\u3002\u201d \u3000\u3000\u9ec4\u8863\u9053\uff1a\u201c\u6211\u4eec\u4e0d\u559d\u8336\uff0c\u4e5f\u4e0d\u662f\u6765\u559d\u559c\u9152\u7684\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u5ffd\u7136\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u4eec\u83ab\u975e\u60f3\u6765\u770b\u65b0\u5a18\u5b50\uff1f\u201d \u3000\u3000\u9ec4\u8863\u4eba\u51b7\u51b7\u5730\u770b\u4e86\u5979\u4e00\u773c\uff0c\u9053\uff1a\u201c\u4f60\u5c31\u662f\u65b0\u5a18\u5b50\uff1f\u201d \u3000\u3000\u4e01\u7075\u7433\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u4eec\u5047\u5982\u8981\u770b\uff0c\u73b0\u5728\u5c31\u53ef\u770b\u4e86\u3002\u201d \u3000\u3000\u9ec4\u8863\u4eba\u7ffb\u4e86\u7ffb\u767d\u773c\uff0c\u9053\uff1a\u201c\u6211\u4eec\u8981\u6765\u770b\u7684\u5e76\u4e0d\u662f\u65b0\u5a18\u2018\u6c0f\u201d\u4e01\u7075\u7433\u9053\uff1a\u201c\u4f60\u4eec\u6765\u770b\u4ec0 \u4e48\uff1f\u201d \u3000\u3000\u9ec4\u8863\u4eba\u9053\uff1a\u201c\u6765\u770b\u770b\u4eca\u5929\u665a\u4e0a\u6709\u6ca1\u6709\u6562\u5230\u8fd9\u91cc\u6765\u60f9\u662f\u751f\u975e\u7684\u4eba\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u7728\u4e86\u7728\u773c\uff0c\u9053\uff1a\u201c\u5047\u5982\u6709\u5462\uff1f\u201d \u3000\u3000\u9ec4\u8863\u4eba\u51b7\u51b7\u9053\uff1a\u201c\u4e0d\u80fd\u6709\uff0c\u4e5f\u4e0d\u4f1a\u6709\u3002\u201d \u3000\u3000\u4e86\u7075\u7433\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u9ec4\u8863\u4eba\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u4eec\u5df2\u5949\u547d\u4fdd\u62a4\u8fd9\u91cc\u7684\u5b89\u5168\uff0c\u4fdd\u62a4\u65b0\u4eba\u5e73\u5e73\u5b89\u5b89\u5730\u8ff8\u6d1e\u623f\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u6709\u4f60\u4eec\u5728\u8fd9\u91cc\uff0c\u5c31\u4e0d\u4f1a\u518d\u6709\u4eba\u6765\u60f9\u662f\u751f\u975e\uff1f\u201d \u3000\u3000\u9ec4\u8863\u4eba\u9053\u201c\u82e5\u662f\u6709\u4e00\u4e2a\u4eba\u6562\u6765\uff0c\u957f\u5b89\u57ce\u91cc\u4eca\u591c\u5c31\u8981\u591a\u4e00\u4e2a\u6b7b\u4eba\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u82e5\u6709\u4e00\u767e\u4e2a\u4eba\u6562\u6765\uff0c\u957f\u5b89\u57ce\u91cc\u5c31\u8981\u591a\u4e00\u767e\u4e2a\u6b7b\u4eba\uff1f\u201d \u3000\u3000\u9ec4\u8863\u4eba\u9053\uff1a\u201c\u591a\u4e00\u767e\u96f6\u56db\u4e2a\u3002\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u5df2\u8bf4\u5f97\u5f88\u660e\u767d\uff0c\u4ed6\u4eec\u56db\u4eba\u663e\u7136\u4e0d\u662f\u4e00\u767e\u4e2a\u4eba\u7684\u653e\u624b\uff0c\u53ef\u662f\u6765\u7684\u4eba\u4e5f\u4f11\u60f3\u6d3b\u7740\u56de \u53bb\u3002 \u3000\u3000\u4e01\u7075\u7433\u8f7b\u8f7b\u5410\u51fa\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u662f\u5949\u4e86\u8c01\u7684\u547d\u4ee4\u800c\u6765\u7684\uff1f\u201d \u3000\u3000\u9ec4\u8863\u4eba\u4e00\u53e5\u8bdd\u4e5f\u4e0d\u518d\u8bf4\uff0c\u677f\u7740\u8138\uff0c\u4e00\u4e2a\u8ddf\u7740\u4e00\u4e2a\uff0c\u8d70\u8fdb\u4e86\u6446\u559c\u9152\u7684\u5927\u5385\u3002 \u3000\u3000\u7136\u540e\u56db\u4e2a\u4eba\u5c31\u5206\u6210\u56db\u4e2a\u65b9\u5411\uff0c\u52a8\u4e5f\u4e0d\u52a8\u5730\u7ad9\u5728\u56db\u4e2a\u89d2\u843d\u91cc\u3002 \u3000\u3000\u8001\u638c\u67dc\u7684\u4e5f\u4e0d\u7981\u5410\u51fa\u53e3\u6c14\uff0c\u8fd8\u6ca1\u6709\u5f00\u53e3\uff0c\u7a81\u7136\u5916\u9762\u5df2\u6709\u4eba\u5728\u95ee\uff1a\u201c\u8fd9\u91cc\u662f\u4e0d\u662f\u9e3f\u5bbe\u5ba2 \u6808\uff1f\u201d \u3000\u3000\u8fd9\u6b21\u6765\u7684\uff0c\u7adf\u662f\u4e2a\u9e51\u8863\u767e\u7ed3\u3001\u62ab\u5934\u6563\u53d1\u7684\u4e5e\u4e10\uff0c\u8fd8\u80cc\u7740\u53ea\u7834\u7834\u70c2\u70c2\u7684\u5927\u9ebb\u888b\u3002 \u3000\u3000\u4ed6\u5f53\u7136\u4e0d\u4f1a\u662f\u6765\u9001\u793c\u7684\uff0c\u4e16\u4e0a\u53ea\u6709\u8981\u94b1\u8981\u98df\u7684\u4e5e\u4e10\uff0c\u4ece\u6765\u4e5f\u6ca1\u6709\u9001\u793c\u7684\u4e5e\u4e10\u3002 \u3000\u3000\u8001\u638c\u67dc\u76b1\u4e86\u76b1\u7709\uff0c\u9053\uff1a\u201c\u4f60\u6765\u5f97\u5927\u65e9\u4e86\uff0c\u73b0\u5728\u8fd8\u6ca1\u6709\u5230\u53d1\u8d4f\u7684\u65f6\u5019\u3002\u201d \u3000\u3000\u8fd9\u4e5e\u4e10\u5374\u51b7\u7b11\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\u6211\u662f\u6765\u8ba8\u8d4f\u7684\uff1f\u201d \u3000\u3000\u8001\u638c\u67dc\u6014\u4e86\u6014\u9053\uff1a\u201c\u4f60\u4e0d\u662f\uff1f\u201d \u3000\u3000\u4e5e\u4e10\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u5c31\u7b97\u628a\u8fd9\u5ba2\u6808\u9001\u7ed9\u6211\uff0c\u6211\u4e5f\u672a\u5fc5\u4f1a\u8981\uff0c\u201d\u4e5e\u4e10\u7684\u53e3\u6c14\u5012\u4e0d\u5c0f\u3002 \u3000\u3000\u8001\u638c\u67dc\u82e6\u7b11\u9053\uff1a\u201c\u96be\u9053\u4f60\u4e5f\u662f\u7c73\u559d\u559c\u9152\u7684\uff1f\u201d \u3000\u3000\u201c\u4e0d\u662f\u3002\u201d \u3000\u3000\u201c\u4f60\u6765\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u6765\u9001\u793c\u3002\u201d \u3000\u3000\u50cf\u9001\u793c\u7684\u4e0d\u9001\uff0c\u4e0d\u50cf\u9001\u793c\u6765\u7684\uff0c\u53cd\u800c\u9001\u6765\u4e86\u3002 \u3000\u3000\u8001\u638c\u67dc\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u793c\u7269\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u201c\u5c31\u5728\u8fd9\u91cc\u3002\u201d \u3000\u3000\u4e5e\u4e10\u5c06\u80cc\u4e0a\u7684\u7834\u9ebb\u888b\u5f80\u67dc\u53f0\u4e0a\u4e00\uff0c\u63b7\uff0c\u5341\u51e0\u9897\u6676\u83b9\u5706\u6da6\u7684\u73cd\u73e0\uff0c\u7684\u6e9c\u6e9c\u4ece\u9ebb\u888b\u91cc\u6eda\u4e86\u51fa \u6765\u3002 \u3000\u3000\u8001\u638c\u67dc\u6014\u4f4f\u3002 \u3000\u3000\u4e01\u7075\u7433\u4e5f\u5403\u4e86\u4e00\u60ca\u3002 \u3000\u3000\u5c31\u53ea\u8fd9\u5341\u51e0\u9897\u73cd\u73e0\uff0c\u5df2\u4ef7\u503c\u4e0d\u83f2\uff0c\u5979\u867d\u7136\u751f\u957f\u5728\u8c6a\u5bcc\u4e4b\u5bb6\uff0c\u5374\u4e5f\u5f88\u5c11\u89c1\u5230\u8fc7\u3002 \u3000\u3000\u8c01\u77e5\u9ebb\u888b\u91cc\u7684\u4e1c\u897f\u8fd8\u4e0d\u6b62\u8fd9\u4e9b\uff0c\u4e00\u6253\u5f00\u9ebb\u888b\uff0c\u6ee1\u5c4b\u90fd\u662f\u73e0\u5149\u5b9d\u6c14\uff0c\u73cd\u73e0\u3001\u739b\u7459\u3001\u732b\u513f \u773c\u3001\u7956\u6bcd\u7eff\uff0c\u5947\u73cd\u5f02\u5b9d\uff0c\u6570\u4e5f\u6570\u4e0d\u6e05\uff0c\u4e5f\u4e0d\u77e5\u6709\u591a\u5c11\u3002 \u3000\u3000\u8001\u638c\u67dc\u5df2\u5f20\u5927\u4e86\u773c\u775b\uff0c\u8fde\u5634\u90fd\u5408\u4e0d\u62e2\u6765\uff0c\u4ed6\u8fde\u505a\u68a6\u90fd\u6ca1\u6709\u770b\u89c1\u8fc7\u8fd9\u4e48\u591a\u73e0\u5b9d\u3002 \u3000\u3000\u4e5e\u4e10\u9053\uff1a\u201c\u8fd9\u4e9b\u90fd\u662f\u9001\u7ed9\u4e01\u59d1\u5a18\u6dfb\u5986\u7684\uff0c\u4f60\u597d\u751f\u6536\u4e0b\u3002\u201d \u3000\u3000\u8001\u638c\u67dc\u5012\u62bd\u4e86\u53e3\u51c9\u6c14\uff0c\u8d54\u7b11\u9053\uff1a\u201c\u5927\u7237\u9ad8\u59d3\uff1f\u201d \u3000\u3000\u4e5e\u4e10\u51b7\u51b7\u9053\uff1a\u201c\u6211\u4e0d\u662f\u5927\u7237\uff0c\u6211\u662f\u4e2a\u7a77\u8981\u996d\u7684\u3002\u201d \u3000\u3000\u4ed6\u8eab\u5b50\u4e00\u8f6c\uff0c\u4eba\u5df2\u5230\u4e86\u95e8\u5916\uff0c\u8eab\u624b\u4e4b\u5feb\uff0c\u6c5f\u6e56\u4e2d\u4e5f\u4e0d\u591a\u89c1\u3002 \u3000\u3000\u4e01\u7075\u7433\u60f3\u62e6\u4f4f\u4ed6\uff0c\u5df2\u6765\u4e0d\u53ca\u4e86\uff0c\u518d\u8d76\u51fa\u53bb\uff0c\u8857\u4e0a\u4eba\u6765\u4eba\u5f80\uff0c\u5374\u5df2\u770b\u4e0d\u89c1\u4e5e\u4e10\u7684\u5f71\u5b50\u3002 \u3000\u3000\u4ed6\u7a76\u7adf\u662f\u4ec0\u4e48\u4eba\uff1f\u4e3a\u4ec0\u4e48\u8981\u9001\u5982\u6b64\u91cd\u7684\u793c\uff1f \u3000\u3000\u8001\u638c\u67dc\u5ffd\u7136\u9053\uff1a\u201c\u8fd9\u91cc\u8fd8\u6709\u5f20\u62dc\u5e16\u3002\u201d \u3000\u3000\u9c9c\u7ea2\u7684\u62dc\u5e16\uff0c\u4e0a\u9762\u5199\u7740\uff1a\u90ed\u516c\u5b50\u3001\u4e01\u59d1\u5a18\u5927\u559c\uff01\u789f\u513f\u5e03\u3001\u591a\u5e03\u7532\u3001\u5e03\u8fbe\u62c9\u3001\u73ed\u7d22\u5df4\u90a3\u540c \u8d3a\u3002 \u3000\u3000\u4e01\u7075\u7433\u53c8\u6014\u4f4f\u3002 \u3000\u3000\u8001\u638c\u67dc\u9053\uff1a\u201c\u4e01\u59d1\u5a18\u4e5f\u4e0d\u8ba4\u5f97\u4ed6\u4eec\u56db\u4f4d\uff1f\u201d \u3000\u3000\u4e01\u7075\u7433\u82e6\u7b11\u9053\uff1a\u201c\u975e\u4f46\u4e0d\u8ba4\u5f97\uff0c\u8fde\u8fd9\u56db\u4e2a\u540d\u5b57\u90fd\u6ca1\u542c\u8fc7\u3002\u201d \u3000\u3000\u50cf\u8fd9\u4e48\u7a00\u5947\u53e4\u602a\u7684\u540d\u5b57\uff0c\u542c\u8fc7\u7684\u4eba\u786e\u5b9e\u4e0d\u591a\u3002 \u3000\u3000\u8001\u638c\u67dc\u76b1\u7709\u9053\uff1a\u201c\u59d1\u5a18\u7740\u8fde\u4ed6\u4eec\u7684\u540d\u5b57\u90fd\u672a\u542c\u8fc7\uff0c\u4ed6\u4eec\u600e\u4e48\u4f1a\u9001\u5982\u6b64\u91cd\u7684\u793c\uff1f\u201d\u4e01\u7075\u7433 \u4e5f\u60f3\u4e0d\u901a\u3002 \u3000\u3000\u8001\u638c\u67dc\u53ea\u597d\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u7ba1\u600e\u6837\uff0c\u4eba\u5bb6\u9001\u793c\u6765\uff0c\u603b\u662f\u597d\u610f\u3002\u201d \u3000\u3000\u4e86\u7075\u7433\u53f9\u4e86\u53e3\u6c14\uff0c\u8fd8\u6ca1\u6709\u5f00\u53e3\uff0c\u5916\u9762\u5c45\u7136\u53c8\u6709\u4eba\u5728\u95f4\uff1a\u201c\u8fd9\u91cc\u662f\u4e0d\u662f\u9e3f\u5bbe\u5ba2\u6808\uff1f\u201d \u3000\u3000\u5b8c\u5168\u540c\u6837\u7684\u4e00\u53e5\u8bdd\uff0c\u6765\u7684\u5374\u662f\u5b8c\u5168\u4e0d\u540c\u7684\u4e09\u4e2a\u4eba\u3002 \u3000\u3000\u524d\u4e24\u6b21\u6765\u7684\u4eba\uff0c\u5df2\u7ecf\u662f\u602a\u4eba\uff0c\u8fd9\u6b21\u6765\u7684\u4eba\u5374\u66f4\u5947\u602a\u3002 \u3000\u3000\u5982\u6b64\u4e25\u5bd2\u5929\u6c14\uff0c\u8fd9\u4e2a\u4eba\u8eab\u4e0a\u5c45\u7136\u53ea\u7a7f\u7740\u4ef6\u84dd\u886b\uff0c\u5934\u4e0a\u5374\u6234\u9876\u5f62\u5f0f\u5947\u53e4\u7684\u9ad8\u5e3d\uff0c\u8721\u9ec4\u7684 \u8138\uff0c\u7a00\u7a00\u758f\u758f\u7684\u5c71\u7f8a\u80e1\u5b50\uff0c\u770b\u6765\u4eff\u4f5b\u5927\u75c5\u521d\u6108\uff0c\u5374\u53c8\u504f\u504f\u4e00\u70b9\u90fd\u4e0d\u6015\u51b7\u3002 \u3000\u3000\u4ed6\u5de6\u624b\u62ff\u7740\u628a\u96e8\u4f1e\uff0c\u53f3\u624b\u63d0\u7740\u53e3\u7bb1\u5b50\uff0c\u96e8\u4f1e\u5f88\u7834\u65e7\uff0c\u7bb1\u5b50\u5374\u5f88\u597d\u770b\uff0c\u770b\u6765\u975e\u9769\u975e\u6728\uff0c\u867d \u4e0d\u77e5\u7528\u4ec0\u4e48\u505a\u7684\uff0c\u4f46\u65e0\u8bba\u8c01\u90fd\u53ef\u4ee5\u770b\u5f97\u51fa\u8fd9\u662f\u53e3\u5f88\u503c\u94b1\u3001\u4e5f\u5f88\u7279\u522b\u7684\u7bb1\u4e88\uff0c\u624b\u628a\u4e0a\u751a\u81f3\u9576\u7740 \u78a7\u7389\u3002 \u3000\u3000\u4ed6\u8eab\u4e0a\u7a7f\u7684\u867d\u5355\u8584\uff0c\u6c14\u6d3e\u5374\u5f88\u5927\uff0c\u4e24\u773c\u4e0a\u7ffb\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u8fd9\u91cc\u662f\u4e0d\u662f\u6709\u4e2a\u59d3\u90ed\u7684\u5728\u529e\u559c \u4e8b\uff1f\u201d \u3000\u3000\u8001\u638c\u67dc\u70b9\u70b9\u5934\uff0c\u770b\u7740\u4ed6\u624b\u91cc\u7684\u7bb1\u5b50\uff0c\u8bd5\u63a2\u7740\u95ee\uff1a\u201c\u5ba2\u5b98\u662f\u6765\u9001\u793c\u7684\uff1f\u201d \u3000\u3000\u201c\u4e0d\u662f\u3002\u201d \u3000\u3000\u201c\u662f\u6765\u559d\u559c\u9152\u7684\uff1f\u201d \u3000\u3000\u201c\u4e5f\u4e0d\u662f\u3002\u201d \u3000\u3000\u8001\u638c\u67dc\u53ea\u6709\u82e6\u7b11\uff0c\u8fde\u95ee\u90fd\u6ca1\u6cd5\u5b50\u518d\u95ee\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u4e01\u7075\u7433\u5374\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u4f60\u5c31\u662f\u5357\u5b98\u6d6a\uff1f\u201d \u3000\u3000\u84dd\u8863\u4eba\u51b7\u7b11\uff0c\u9053\uff1a\u201c\u5357\u5b98\u6d6a\u7b97\u4ec0\u4e48\u4e1c\u897f\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u677e\u4e86\u53e3\u6c14\uff0c\u5c55\u989c\u7b11\u9053\uff1a\u201c\u4ed6\u7684\u786e\u4e0d\u662f\u4e2a\u4e1c\u897f\u3002\u201d \u3000\u3000\u84dd\u8863\u4eba\u9053\uff1a\u201c\u6211\u662f\u4e1c\u897f\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u6014\u4e86\u6014\uff0c\u81ea\u5df1\u8bf4\u81ea\u5df1\u662f\u201c\u4e1c\u897f\u201d\u7684\u4eba\uff0c\u5979\u4e5f\u662f\u4ece\u6765\u6ca1\u89c1\u8fc7\u3002 \u3000\u3000\u84dd\u8863\u4eba\u677f\u7740\u8138\uff0c\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u95ee\uff0c\u6211\u662f\u4ec0\u4e48\u4e1c\u897f\uff1f\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u6211\u6b63\u60f3\u95f4\u3002\u201d \u3000\u3000\u84dd\u8863\u4eba\u9053\uff1a\u201c\u6211\u662f\u793c\u7269\uff0c\u201d\u4e01\u7075\u7433\u9053\uff1a\u201c\u4f60\u59d3\u674e\uff1f\u201d \u3000\u3000\u84dd\u8863\u4eba\u9053\uff1a\u201c\u4e0d\u662f\u59d3\u674e\uff0c\u662f\u793c\u7269\uff0c\u201d\u4e01\u7075\u7433\u7741\u5927\u4e86\u773c\u775b\uff0c\u770b\u7740\u4ed6\uff0c\u8fd9\u4e2a\u4eba\u7684\u786e\u50cf\u662f\u4e2a\u602a \u7269\u3002\u602a\u7269\u5979\u5012\u89c1\u8fc7\uff0c\u53ef\u662f\u4e00\u4e2a\u4f1a\u8bf4\u8bdd\u3001\u4f1a\u8d70\u8def\u7684\u201c\u602a\u7269\u201d\uff0c\u5979\u7b80\u76f4\u8fde\u542c\u90fd\u6ca1\u542c\u8fc7\u3002 \u3000\u3000\u84dd\u8863\u4eba\u9053\uff1a\u201c\u4f60\u5c31\u662f\u4e01\u7075\u7433\uff1f\u201d \u3000\u3000\u4e01\u7075\u7433\u70b9\u70b9\u5934\u3002 \u3000\u3000\u84dd\u8863\u4eba\u9053\uff1a\u201c\u6240\u4ee5\u6709\u4eba\u9001\u6211\u6765\u505a\u8d3a\u793c\uff0c\u4f60\u61c2\u4e0d\u61c2\uff1f\u201d \u3000\u3000\u4e01\u7075\u7433\u8fd8\u662f\u4e0d\u61c2\uff0c\u8bd5\u63a2\u7740\u95ee\u9053\uff1a\u201c\u4f60\u662f\u8bf4\uff0c\u6709\u4eba\u628a\u4f60\u5f53\u505a\u793c\u7269\u9001\u7ed9\u6211\uff1f\u201d \u3000\u3000\u84dd\u8863\u4eba\u53f9\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u603b\u7b97\u61c2\u4e86\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u6211\u4e0d\u61c2\u3002\u201d \u3000\u3000\u84dd\u8863\u4eba\u76b1\u7709\u9053\uff1a\u201c\u8fd8\u4e0d\u61c2\uff1f\u201d \u3000\u3000\u4e01\u7075\u7433\u82e6\u7b11\u9053\uff1a\u201c\u6211\u8981\u4f60\u8fd9\u4e48\u6837\u4e00\u4e2a\u793c\u7269\u4e8e\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u84dd\u8863\u4eba\u9053\uff1a\u201c\u5f53\u7136\u6709\u7528\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u6709\u4ec0\u4e48\u7528\uff1f\u201d \u3000\u3000\u84dd\u8863\u4eba\u9053\uff1a\u201c\u6211\u80fd\u6551\u4eba\u7684\u547d\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u6551\u8c01\u7684\u547d\uff1f\u201d \u3000\u3000\u84dd\u8863\u4eba\u9053\uff1a\u201c\u6551\u4f60\u8001\u516c\u90ed\u5b9a\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u52a8\u5bb9\u9053\uff1a\u201c\u4f60\u80fd\u6551\u5f97\u4e86\u4ed6\uff1f\u201d \u3000\u3000\u84dd\u8863\u4eba\u51b7\u51b7\u9053\uff1a\u201c\u6211\u82e5\u6551\u4e0d\u4e86\u4ed6\uff0c\u5929\u4e0b\u5c31\u7edd\u6ca1\u6709\u7b2c\u4e8c\u4e2a\u4eba\u8fd8\u80fd\u6551\u5f97\u4e86\u4ed6\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u770b\u7740\u4ed6\u5947\u5f02\u7684\u88c5\u675f\uff0c\u8721\u9ec4\u7684\u8138\uff0c\u770b\u7740\u4ed6\u5de6\u624b\u7684\u96e8\u4f1e\uff0c\u53f3\u624b\u7684\u7bb1\u4e0b\u3002 \u3000\u3000\u5979\u7684\u8138\u5ffd\u7136\u95f4\u56e0\u5174\u594b\u800c\u53d1\u7ea2\u3002 \u3000\u3000\u84dd\u8863\u4eba\u6c89\u7740\u8138\u9053\uff1a\u201c\u6211\u4e0d\u662f\u6765\u7ed9\u4f60\u770b\u7684\uff0c\u4e5f\u4e0d\u559c\u6b22\u4f60\u76ef\u7740\u6211\u770b\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u773c\u775b\u91cc\u53d1\u7740\u5149\uff0c\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u662f\u4ec0\u4e48\u4eba\u4e86\u3002\u201d \u3000\u3000\u84dd\u8863\u4eba\u9053\uff1a\u201c\u6211\u662f\u8c01\uff1f\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u4f60\u59d3\u845b\uff0c\u4f60\u5c31\u662f\u2018\u4e07\u5b9d\u7bb1\uff0c\u4e7e\u5764\u4f1e\uff0c\u960e\u738b\u6ca1\u6cd5\u7ba1\u2019\u845b\u75c5\u3002\u201d \u3000\u3000\u84dd\u8863\u4eba\u9053\uff1a\u201c\u4f60\u89c1\u8fc7\u845b\u75c5\uff1f\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u6211\u6ca1\u6709\u89c1\u8fc7\uff0c\u53ef\u662f\u6211\u542c\u53f6\u5f00\u8c08\u8d77\u8fc7\u3002\u201d \u3000\u3000\u84dd\u8863\u4eba\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u4ed6\u8bf4\u845b\u75c5\u4ece\u5c0f\u5c31\u591a\u75c5\uff0c\u800c\u4e14\u6ca1\u6709\u4eba\u80fd\u6cbb\u5f97\u4e86\u4ed6\u7684\u75c5\uff0c\u6240\u4ee5\u4ed6\u5c31\u60f3\u6cd5\u5b50\u81ea\u5df1 \u6cbb\uff0c\u5230\u540e\u6765\u7adf\u6210\u4e86\u5929\u4e0b\u7b2c\u4e00\u795e\u533b\uff0c\u8fde\u960e\u738b\u90fd\u7ba1\u4e0d\u4e86\u4ed6\uff0c\u56e0\u4e3a\u6b7b\u4eba\u4e5f\u5e38\u5e38\u88ab\u4ed6\u6551\u6d3b\u3002\u201d \u3000\u3000\u84dd\u8863\u4eba\u7a81\u7136\u53c8\u51b7\u7b11\uff0c\u9053\uff1a\u201c\u53f6\u5f00\u53c8\u7b97\u662f\u4ec0\u4e48\u4e1c\u897f\uff1f\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u4ed6\u4e0d\u662f\u4e1c\u897f\uff0c\u4ed6\u662f\u4f60\u7684\u670b\u53cb\uff0c\u6211\u77e5\u9053\u2026\u2026\u201d \u3000\u3000\u5979\u5ffd\u7136\u8fc7\u53bb\uff0c\u7528\u529b\u63e1\u4f4f\u84dd\u8863\u4eba\u7684\u624b\uff0c\u5598\u606f\u7740\u9053\uff1a\u201c\u662f\u4e0d\u662f\u53f6\u5f00\u53eb\u4f60\u6765\u7684\uff1f\u4ed6\u662f\u4e0d\u662f\u8fd8\u6ca1 \u6709\u6b7b\uff1f\u201d \u3000\u3000\u84dd\u8863\u4eba\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u627e\u9519\u4eba\u4e86\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u6211\u6ca1\u6709\u3002\u201d \u3000\u3000\u84dd\u8863\u4eba\u9053\uff1a\u201c\u4f60\u662f\u65b0\u5a18\u5b50\uff0c\u4f60\u5e94\u8be5\u53bb\u627e\u4f60\u7684\u8001\u516c\uff0c\u4e3a\u4ec0\u4e48\u62c9\u4f4f\u6211\uff1f\u201d \u3000\u3000\u4ed6\u8bdd\u91cc\u663e\u7136\u8fd8\u6709\u6df1\u610f\u3002 \u3000\u3000\u2014\u2014\u4f60\u65e2\u7136\u5df2\u5ac1\u7ed9\u4e86\u90ed\u5b9a\uff0c\u5c31\u4e0d\u8be5\u518d\u62c9\u4f4f\u6211\uff0c\u4e5f\u4e0d\u8be5\u518d\u627e\u53f6\u5f00\u3002 \u3000\u3000\u4e01\u7075\u7433\u7684\u624b\u6162\u6162\u677e\u5f00\uff0c\u5782\u4e0b\uff0c\u5934\u4e5f\u5782\u4e0b\uff0c\u9eef\u7136\u9053\uff1a\u201c\u4e5f\u8bb8\u6211\u771f\u7684\u627e\u9519\u4eba\u4e86\u3002\u201d \u3000\u3000\u84dd\u8863\u4eba\u9053\uff1a\u201c\u4f46\u6211\u5374\u6ca1\u6709\u627e\u9519\u3002\u201d \u3000\u3000\u5201\u4e00\u7075\u7433\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u8981\u627e\u90ed\u5b9a\uff1f\u201d \u3000\u3000\u84dd\u8863\u4eba\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4f60\u82e5\u4e0d\u60f3\u505a\u5be1\u5987\uff0c\u5c31\u8d76\u5feb\u5e26\u6211\u53bb\u3002\u201d \u3000\u3000\u73e0\u5b9d\u8fd8\u5806\u5728\u67dc\u53f0\u4e0a\uff0c\u84dd\u8863\u4eba\u4e00\u76f4\u8fde\u770b\u90fd\u6ca1\u6709\u770b\u4e00\u773c\uff0c\u95e8\u5916\u7684\u51b7\u98ce\uff0c\u5374\u504f\u504f\u8981\u5c06\u90a3\u5f20\u8840\u7ea2 \u7684\u62dc\u5e16\u5439\u5230\u4ed6\u811a\u4e0b\u3002 \u3000\u3000\u4ed6\u4e5f\u6ca1\u6709\u53bb\u6361\uff0c\u53ea\u4e0d\u8fc7\u4f4e\u5934\u770b\u4e86\u4e00\u773c\u3002 \u3000\u3000\u53ea\u770b\u4e86\u4e00\u773c\uff0c\u4ed6\u8138\u4e0a\u4e5f\u5df2\u9732\u51fa\u79cd\u5947\u602a\u7684\u8868\u60c5\uff0c\u5ffd\u7136\u9053\uff1a\u201c\u8fd9\u662f\u8c01\u9001\u6765\u7684\uff1f\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u662f\u4e2a\u4e5e\u4e10\u3002\u201d \u3000\u3000\u84dd\u8863\u4eba\u9053\uff1a\u201c\u4ec0\u4e48\u6837\u7684\u4e5e\u4e10\uff1f\u201d \u3000\u3000\u4e01\u7075\u7433\u8fdf\u7591\u7740\uff0c\u5979\u6ca1\u6709\u5f04\u6e05\u695a\uff0c\u5979\u7684\u5fc3\u592a\u4e71\u3002 \u3000\u3000\u8001\u638c\u67dc\u603b\u7b97\u8fd8\u6bd4\u8f83\u6e05\u9192\u51b7\u9759\uff0c\u9053\uff1a\u201c\u662f\u4e2a\u5e74\u7eaa\u4e0d\u592a\u5927\u7684\u4e5e\u4e10\uff0c\u603b\u662f\u559c\u6b22\u7ffb\u767d\u773c\uff0c\u8bf4\u8d77\u8bdd \u6765\uff0c\u603b\u50cf\u662f\u8981\u627e\u4eba\u5435\u67b6\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u4e5f\u60f3\u8d77\u4e86\u4e00\u4ef6\uff1a\u201c\u4ed6\u7684\u8eab\u6cd5\u5f88\u5feb\uff0c\u800c\u4e14\u5f88\u5947\u602a\u3002\u201d \u3000\u3000\u84dd\u8863\u4eba\u9053\uff1a\u201c\u54ea\u70b9\u5947\u602a\uff1f\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff0c\u201c\u4ed6\u8eab\u5b50\u6253\u8f6c\u7684\u65f6\u5019\uff0c\u5c31\u50cf\u662f\u4e2a\u9640\u87ba\u4e00\u6837\u3002\u201d \u3000\u3000\u84dd\u8863\u4eba\u6c89\u7740\u8138\uff0c\u8fc7\u4e86\u5f88\u4e45\uff0c\u5ffd\u7136\u53c8\u95ee\u9053\uff1a\u201c\u8fd9\u4e9b\u73e0\u5b9d\u91cc\uff0c\u662f\u4e0d\u662f\u6709\u5757\u4e0a\u9762\u523b\u7740\u56db\u4e2a\u5996\u9b54 \u7684\u7389\u724c\uff1f\u201d \u3000\u3000\u201c\u6709\u7684\u3002\u201d \u3000\u3000\u8001\u638c\u67dc\u5f88\u5feb\u5c31\u627e\u4e86\u51fa\u6765\uff0c\u4e0a\u9762\u523b\u7740\u7684\uff0c\u662f\u56db\u4e2a\u9b54\u795e\uff0c\u4e00\u4e2a\u624b\u6267\u667a\u78d0\uff0c\u4e00\u4e2a\u624b\u6267\u6cd5\u6756\uff0c\u4e00 \u4e2a\u624b\u6258\u5c71\u5cf0\uff0c\u8fd8\u6709\u4e00\u4e2a\u624b\u91cc\u7adf\u6258\u7740\u4e2a\u8d64\u88f8\u7684\u5973\u4eba\u3002\u84dd\u8863\u4eba\u770b\u7740\u8fd9\u5757\u7389\u724c\uff0c\u77b3\u5b54\u4f3c\u5728\u6536\u7f29\u3002 \u3000\u3000\u4e01\u7075\u7433\u5fcd\u4e0d\u4f4f\u95ee\uff1a\u201c\u4f60\u77e5\u9053\u8fd9\u4e2a\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u84dd\u8863\u4eba\u6ca1\u6709\u56de\u7b54\uff0c\u5374\u5728\u51b7\u7b11\u3002 \u3000\u3000\u90ed\u5b9a\u5c45\u7136\u5df2\u80fd\u7ad9\u8d77\u6765\u3002\u8fd9\u84dd\u8863\u4eba\u7684\u795e\u901a\uff0c\u7adf\u4f3c\u771f\u7684\u8fde\u960e\u738b\u90fd\u6ca1\u6cd5\u5b50\u7ba1\u3002\u53ef\u662f\u4e01\u7075\u7433\u8981\u8c22 \u4ed6\u7684\u65f6\u5019\uff0c\u5c31\u53d1\u73b0\u4ed6\u5df2\u4e0d\u89c1\u4e86\uff0c\u4e01\u7075\u7433\u4e5f\u6ca1\u6cd5\u5b50\u53bb\u627e\u4ed6\u3002\u5979\u5df2\u7a7f\u4e0a\u4e86\u65b0\u5a18\u5b50\u7684\u5409\u670d\uff0c\u8001\u638c\u67dc \u8bf7\u6765\u7684\u559c\u5a18\uff0c\u6b63\u5728\u66ff\u5979\u62b9\u6700\u540e\u4e00\u70b9\u80ed\u8102\u3002 \u3000\u3000\u5ba2\u4eba\u4eec\u5df2\u5230\u4e86\u5f88\u591a\uff0c\u5176\u4e2d\u662f\u4e0d\u662f\u6709\u4ed6\u4eec\u7684\u719f\u4eba\uff1f\u6768\u5929\u548c\u5415\u8fea\u662f\u4e0d\u662f\u5df2\u6765\u4e86\uff1f\u4e01\u7075\u7433\u5b8c\u5168 \u4e0d\u77e5\u9053\u3002\u5979\u73b0\u5728\u5f53\u7136\u4e0d\u80fd\u518d\u51fa\u53bb\u4e1c\u5f20\u897f\u671b\uff0c\u5979\u5750\u5728\u5e8a\u6cbf\uff0c\u5168\u8eab\u4f3c\u5df2\u5b8c\u5168\u50f5\u786c\u3002 \u3000\u3000\u5916\u9762\u4e50\u58f0\u60a0\u626c\uff0c\u4e00\u4e2a\u559c\u5a18\u8dd1\u51fa\u53bb\u770b\u4e86\u770b\uff0c\u53c8\u8dd1\u56de\u6765\u4fcf\u6084\u9053\uff1a\u201c\u5ba2\u4eba\u5df2\u5feb\u5750\u6ee1\u4e86\uff0c\u65b0\u90ce\u500c \u4e5f\u5df2\u7ecf\u5728\u7b49\u7740\u62dc\u5929\u5730\uff0c\u65b0\u5a18\u5b50\u4e5f\u8be5\u51fa\u53bb\u4e86\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u6ca1\u6709\u52a8\u3002 \u3000\u3000\u2014\u2014\u845b\u75c5\u662f\u4e0d\u662f\u53f6\u5f00\u627e\u6765\u7684\uff1f\u53f6\u5f00\u662f\u4e0d\u662f\u8fd8\u6ca1\u6709\u6b7b\uff1f \u3000\u3000\u5979\u7684\u5fc3\u5728\u7ede\u75db\u3002 \u3000\u3000\u5728\u5916\u9762\u7b49\u7740\u7684\u82e5\u662f\u53f6\u5f00\uff0c\u5979\u65e9\u5df2\u50cf\u71d5\u5b50\u822c\u98de\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u4e00\u4e00\u4e00\u53f6\u5f00\u5462\uff1f \u3000\u3000\u4e01\u7075\u7433\u52c9\u5f3a\u5fcd\u8010\u7740\uff0c\u63a7\u5236\u7740\u81ea\u5df1\uff0c\u73b0\u5728\u7edd\u4e0d\u80fd\u8ba9\u773c\u6cea\u6eda\u4e0b\u6765\u3002\u8fd9\u672c\u662f\u5979\u81ea\u5df1\u5fc3\u7518\u60c5\u613f \u7684\u3002\u90ed\u5b9a\u662f\u4e2a\u597d\u4eba\uff0c\u4e5f\u662f\u6761\u7537\u5b50\u6c49\uff0c\u5bf9\u5979\u7684\u611f\u60c5\uff0c\u4e5f\u8bb8\u6bd4\u53f6\u5f00\u66f4\u6df1\u539a\u771f\u631a\u3002 \u3000\u3000\u53f6\u5f00\u5bf9\u5979\u603b\u662f\u5ffd\u51b7\u5ffd\u70ed\uff0c\u540a\u513f\u90ce\u5f53\u7684\u6837\u5b50\u3002\u4f55\u51b5\uff0c\u90ed\u5b9a\u8fd8\u6551\u4e86\u5979\u7684\u547d\uff0c\u4e3a\u4e86\u62a5\u6069\u800c\u5ac1\u7684 \u5973\u4eba\uff0c\u5979\u5e76\u4e0d\u662f\u7b2c\u4e00\u4e2a\u3002\u5979\u5728\u5b89\u6170\u81ea\u5df1\uff0c\u529d\u81ea\u5df1\uff0c\u53ef\u662f\u5979\u5fc3\u91cc\u8fd8\u662f\u5fcd\u4e0d\u4f4f\u8981\u95ee\u81ea\u5df1\uff0c\u201c\u8fd9\u6837 \u7a76\u7adf\u662f\u5bf9\uff1f\u8fd8\u662f\u9519\uff1f\u201d \u3000\u3000\u8fd9\u95ee\u9898\u6c38\u8fdc\u4e5f\u6ca1\u6709\u4eba\u80fd\u56de\u7b54\u7684\u3002 \u3000\u3000\u4e50\u58f0\u6e10\u6025\uff0c\u5916\u9762\u5df2\u6709\u4eba\u6765\u50ac\u4e86\u3002\u4e01\u7075\u7433\u7ec8\u4e8e\u7ad9\u8d77\u6765\uff0c\u4eff\u4f5b\u5df2\u7528\u5c3d\u4e86\u5168\u8eab\u529b\u6c14\uff0c\u624d\u7ad9\u8d77 \u6765\u3002\u559c\u5a18\u7528\u7ea2\u4e2d\u8499\u4f4f\u4e86\u5979\u7684\u8138\uff0c\u4e24\u4e2a\u4eba\u6276\u7740\u5979\u3002 \u3000\u3000\u6162\u6162\u5730\u8d70\u4e86\u51fa\u53bb\u3002\u8d70\u8fc7\u957f\u5eca\uff0c\u8d70\u8fc7\u9662\u5b50\uff0c\u5927\u5385\u91cc\u5435\u5f97\u5f88\uff0c\u6709\u5404\u5f0f\u5404\u6837\u7684\u58f0\u97f3\u3002\u53ea\u53ef\u60dc\u5176 \u4e2d\u504f\u504f\u5c11\u4e86\u4e00\u79cd\u5979\u6700\u60f3\u542c\u7684\u58f0\u97f3\u2014\u2014\u53f6\u5f00\u7684\u7b11\u58f0\u3002 \u3000\u3000\u73b0\u5728\u65e0\u8bba\u53f6\u5f00\u662f\u4e0d\u662f\u8fd8\u6d3b\u7740\uff0c\u90fd\u5df2\u4e0d\u91cd\u8981\u4e86\u3002 \u3000\u3000\u5979\u5df2\u8d70\u5230\u90ed\u5b9a\u8eab\u65c1\uff0c\u5df2\u542c\u89c1\u4e86\u559c\u5b98\u5728\u5927\u58f0\u9053\uff1a\u201c\u4e00\u62dc\u5929\u5730\u3002\u201d \u3000\u3000\u559c\u5a18\u4eec\u6b63\u51c6\u5907\u6276\u7740\u5979\u62dc\u4e0b\u53bb\uff0c\u7a81\u542c\u4e00\u58f0\u60ca\u547c\uff0c\u4e00\u9635\u8863\u8882\u5e26\u98ce\u58f0\u6765\u5230\u5979\u9762\u524d\u3002 \u3000\u3000\u5357\u5b98\u6d6a\uff1f \u3000\u3000\u4e01\u7075\u7433\u7acb\u523b\u60f3\u8d77\u4e86\u90a3\u5e45\u753b\uff0c\u60f3\u8d77\u4e86\u753b\u4e0a\u90a3\u4e2a\u6ca1\u6709\u8138\u7684\u4eba\uff0c\u90a3\u67c4\u6ef4\u7740\u8840\u7684\u5251\u3002\u5979\u518d\u4e5f\u987e\u4e0d \u4e86\u522b\u7684\uff0c\u5ffd\u7136\u62ac\u8d77\u624b\uff0c\u6380\u8d77\u4e86\u8499\u5728\u8138\u4e0a\u7684\u7ea2\u4e2d\u3002\u5979\u7acb\u523b\u770b\u5230\u4e86\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u4e00\u4e2a\u9ed1\u8863\u4f69\u5251\u3001\u8138\u8272\u60e8\u767d\u3001\u5c31\u50cf\u662f\u5e7d\u7075\u822c\u7a81\u7136\u51fa\u73b0\u7684\u4eba\u3002\u8fd9\u4eba\u5c31\u7ad9\u5728\u5979\u9762\u524d\uff0c\u624b\u91cc\u8fd8\u63d0 \u7740\u6a80\u6728\u5323\u5b50\u3002\u5b88\u5728\u56db\u89d2\u7684\u9ec4\u8863\u4eba\u5df2\u51c6\u5907\u8f6c\u8fc7\u6765\uff0c\u90ed\u5b9a\u7684\u8138\u4e0a\u4e5f\u5df2\u53d8\u4e86\u989c\u8272\u3002 \u3000\u3000\u4e01\u7075\u7433\u5ffd\u7136\u51b7\u7b11\uff0c\u9053\uff1a\u201c\u5357\u5b98\u6d6a\uff0c\u6211\u5c31\u77e5\u9053\u4f60\u4f1a\u672a\u7684\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u6211\u4e0d\u662f\u5357\u5bab\u6d6a\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u4f60\u4e0d\u662f\uff1f\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u6211\u662f\u6765\u9001\u793c\u7684\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u76f4\u5230\u73b0\u5728\u624d\u6765\u9001\u793c\uff1f\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u867d\u7136\u9001\u5f97\u8fdf\u4e86\u4e9b\uff0c\u603b\u6bd4\u4e0d\u9001\u597d\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u770b\u7740\u4ed6\u624b\u91cc\u63d0\u7740\u7684\u6a80\u6728\u5323\u5b50\uff0c\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u4f60\u9001\u6765\u7684\u793c\uff1f\u201d \u3000\u3000\u9ed1\u8863\u4eba\u70b9\u70b9\u5934\uff0c\u4e00\u53ea\u624b\u6258\u8d77\u6728\u5323\uff0c\u4e00\u53ea\u624b\u6380\u5f00\u76d6\u5b50\u3002\u7ad9\u5728\u4e01\u7075\u7433\u65c1\u8fb9\u7684\u559c\u5a18\u5ffd\u7136\u5927\u53eb\u4e00 \u58f0\uff0c\u6655\u4e86\u8fc7\u53bb\u3002\u5979\u5df2\u770b\u89c1\u4e86\u5323\u5b50\u91cc\u88c5\u7684\u662f\u4ec0\u4e48\uff0c\u8fd9\u9ed1\u8863\u4eba\u9001\u6765\u7684\u793c\u7269\uff0c\u7adf\u662f\u9897\u8840\u6dcb\u6dcb\u7684\u4eba \u5934\u3002 \u3000\u3000\u662f\u8c01\u7684\u4eba\u5934\uff1f \u3000\u3000\u9f99\u51e4\u82b1\u70db\u9ad8\u71c3\uff0c\u662f\u7ea2\u7684\uff0c\u9c9c\u7ea2\u3002\u8840\u4e5f\u662f\u7ea2\u7684\uff0c\u8fd8\u6ca1\u6709\u5e72\u3002\u4e01\u7075\u7433\u7684\u8138\u5374\u5df2\u60e8\u767d\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u770b\u7740\u5979\uff0c\u6de1\u6de1\u9053\uff1a\u201c\u4f60\u82e5\u8ba4\u4e3a\u6211\u9001\u7684\u793c\u6709\u6076\u610f\uff0c\u4f60\u5c31\u9519\u4e86\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u51b7\u7b11\u9053\uff1a\u201c\u8fd9\u96be\u9053\u8fd8\u662f\u597d\u610f\uff1f\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u975e\u4f46\u662f\u597d\u610f\uff0c\u800c\u4e14\u6211\u53ef\u4ee5\u4fdd\u8bc1\uff0c\u4eca\u5929\u6765\u7684\u5ba2\u4eba\u91cc\uff0c\u7edd\u6ca1\u6709\u4efb\u4f55\u4eba\u9001\u7684\u793c\u6bd4 \u6211\u8fd9\u4efd\u793c\u66f4\u8d35\u91cd\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u9ed1\u8863\u4eba\u6307\u7740\u5323\u5b50\u91cc\u7684\u4eba\u5934\uff0c\u9053\uff1a\u201c\u56e0\u4e3a\u8fd9\u4e2a\u4eba\u82e5\u662f\u4e0d\u6b7b\uff0c\u4e24\u4f4d\u4eca\u5929\u53ea\u6015\u5c31\u5f88\u96be\u5e73\u5e73\u5b89\u5b89 \u5730\u8fc7\u4f60\u4eec\u7684\u6d1e\u623f\u82b1\u70db\u591c\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u662f\u4e2a\u4e00\u5fc3\u8981\u6765\u53d6\u4f60\u4eec\u9888\u4e0a\u4eba\u5934\u7684\u4eba\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u8038\u7136\u5931\u58f0\uff0c\u9053\uff1a\u201c\u662f\u5357\u5bab\u6d6a\uff1f\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5c31\u662f\u4ed6\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u8f7b\u8f7b\u5410\u51fa\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u672c\u6765\u4e5f\u662f\u5357\u5b98\u6d6a\u7684\u4ec7\u4eba\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u73b0\u5728\u5462\uff1f\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u73b0\u5728\u662f\u4e2a\u5df2\u9001\u8fc7\u4e86\u793c\uff0c\u6b63\u7b49\u7740\u8981\u559d\u559c\u9152\u7684\u5ba2\u4eba\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u770b\u7740\u4ed6\uff0c\u5ffd\u7136\u53d1\u73b0\u81ea\u5df1\u597d\u50cf\u5df2\u6ca1\u6709\u4ec0\u4e48\u8bdd\u53ef\u4ee5\u518d\u95ee\u3002 \u3000\u3000\u5927\u5385\u4e2d\u62e5\u6324\u7740\u5404\u5f0f\u5404\u6837\u7684\u4eba\uff0c\u4eba\u4e1b\u91cc\u7a81\u7136\u6709\u4e2a\u9488\u4e00\u822c\u5c16\u9510\u7684\u58f0\u97f3\u51b7\u51b7\u9053\uff1a\u201c\u6234\u7740\u4eba\u76ae\u9762 \u5177\u6765\u559d\u559c\u9152\uff0c\u53ea\u6015\u5f88\u4e0d\u65b9\u4fbf\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u8138\u4e0a\u867d\u7136\u8fd8\u662f\u5168\u65e0\u8868\u60c5\uff0c\u77b3\u5b54\u5374\u5df2\u7a81\u7136\u6536\u7f29\uff0c\u5389\u58f0\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u90a3\u58f0\u97f3\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u6c38\u8fdc\u4e0d\u4f1a\u77e5\u9053\u6211\u662f\u8c01\u7684\uff0c\u6211\u5374\u77e5\u9053\u4f60\u5c31\u662f\u5357\u5b98\u6d6a\u3002\u201d \u3000\u3000\u9ed1\u8863\u4eba\u7a81\u7136\u51fa\u624b\uff0c\u8fde\u5323\u5b50\u5e26\u4eba\u5934\uff0c\u4e00\u8d77\u5411\u4e01\u7075\u7433\u8138\u4e0a\u6454\u4e86\u8fc7\u53bb\uff0c\u80cc\u540e\u7684\u5251\u5df2\u51fa\u9798\u3002 \u3000\u3000\u5251\u5149\u4e00\u95ea\uff0c\u76f4\u523a\u90ed\u5b9a\u80f8\u819b\u3002 \u3000\u3000\u8fd9\u53d8\u5316\u5b9e\u5728\u592a\u5feb\uff0c\u4ed6\u7684\u51fa\u624b\u66f4\u5feb\u3002 \u3000\u3000\u90ed\u5b9a\u80fd\u7ad9\u7740\u5df2\u5f88\u52c9\u5f3a\uff0c\u54ea\u91cc\u8fd8\u80fd\u907f\u5f97\u5f00\u4ed6\u8fd9\u95ea\u7535\u822c\u7684\u4e00\u5251\u3002 \u3000\u3000\u4e01\u7075\u7433\u4e5f\u53ea\u6709\u770b\u7740\u3002 \u3000\u3000\u4e00\u9897\u8840\u6dcb\u6dcb\u7684\u4eba\u5934\u8fce\u9762\u6454\u8fc7\u6765\uff0c\u65e0\u8bba\u8c01\u90fd\u4f1a\u5403\u4e00\u60ca\u7684\u3002 \u3000\u3000\u7b49\u5979\u8eb2\u8fc7\u53bb\u65f6\uff0c\u5251\u950b\u8ddd\u79bb\u90ed\u5b9a\u7684\u80f8\u819b\u5df2\u4e0d\u53ca\u4e00\u5c3a\u3002 \u3000\u3000\u5979\u624b\u91cc\u7eb5\u7136\u6709\u593a\u547d\u7684\u91d1\u9556\uff0c\u4e5f\u672a\u5fc5\u6765\u5f97\u53ca\u51fa\u624b\uff0c\u4f55\u51b5\u65b0\u5a18\u5b50\u8eab\u4e0a\uff0c\u5f53\u7136\u7edd\u4e0d\u4f1a\u5e26\u7740\u51f6 \u5668\u3002 \u3000\u3000\u2014\u2014\u6ca1\u6709\u8138\u7684\u4eba\uff0c\u6ef4\u7740\u8840\u7684\u5251\u3002 \u3000\u3000\u773c\u770b\u7740\u90a3\u5e45\u56fe\u753b\u5df2\u5c06\u53d8\u4e3a\u771f\u5b9e\uff0c\u773c\u770b\u7740\u90ed\u5b9a\u5df2\u5c06\u6b7b\u5728\u4ed6\u5251\u4e0b\u3002 \u3000\u3000\u8fd9\u4e16\u4e0a\u51e0\u4e4e\u6ca1\u6709\u4eba\u80fd\u6551\u5f97\u4e86\u4ed6\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u7a81\u7136\u53c8\u6709\u5200\u5149\u4e00\u95ea\u3002 \u3000\u3000\u96ea\u4eae\u7684\u5200\u5149\uff0c\u6bd4\u95ea\u7535\u8fd8\u5feb\uff0c\u6bd4\u95ea\u7535\u8fd8\u4eae\uff0c\u4eff\u4f5b\u662f\u4ece\u5de6\u8fb9\u7684\u7a97\u5916\u5c04\u4eba\u7684\u3002 \u3000\u3000\u5200\u5149\u4e00\u4eae\u8d77\uff0c\u4e01\u7075\u7433\u5df2\u7a7f\u7a97\u800c\u51fa\uff0c\u629b\u4e0b\u6ee1\u5802\u7684\u5bbe\u5ba2\uff0c\u629b\u4e0b\u4e86\u5251\u950b\u4e0b\u7684\u90ed\u5b9a\u3002 \u3000\u3000\u629b\u4e0b\u4e86\u4e00\u5207\uff01 \u3000\u3000\u56e0\u4e3a\u5979\u77e5\u9053\u8fd9\u4e00\u5200\u5fc5\u5b9a\u80fd\u6551\u5f97\u4e86\u90ed\u5b9a\uff01\u5fc5\u5b9a\u80fd\u51fb\u9000\u8fd9\u9ed1\u8863\u4eba\uff01 \u3000\u3000\u8fd9\u662f\u6551\u547d\u7684\u5200\uff01\u5df2\u6551\u8fc7\u65e0\u6570\u4eba\u7684\u547d\uff01 \u3000\u3000\u5979\u77e5\u9053\uff0c\u4e16\u4e0a\u53ea\u6709\u4e00\u4e2a\u4eba\u80fd\u53d1\u51fa\u8fd9\u4e00\u5200\u3002 \u3000\u3000\u53ea\u6709\u4e00\u4e2a\u4eba\uff01 \u3000\u3000\u5979\u7edd\u4e0d\u80fd\u8ba9\u8fd9\u4e2a\u4eba\u5c31\u8fd9\u4e48\u6837\u4e00\u8d70\u4e86\u4e4b\uff0c\u5979\u5c31\u7b97\u6b7b\uff0c\u4e5f\u8981\u518d\u770b\u4e00\u770b\u8fd9\u4e2a\u4eba\u3002 \u7b2c\u4e8c\u5341\u4e8c\u7ae0\u3000\u56db\u5927\u5929\u738b>> \u53e4\u9f99\u300a\u4e5d\u6708\u9e70\u98de\u300b \u7b2c\u4e8c\u5341\u4e8c\u7ae0\u3000\u56db\u5927\u5929\u738b \u3000\u3000\u591c\u8272\u6df1\u6c89\u3002 \u3000\u3000\u591c\u7a7a\u4e2d\u53ea\u6709\u51e0\u70b9\u758f\u661f\uff0c\u6de1\u6de1\u7684\u661f\u5149\u4e0b\uff0c\u8fdc\u5904\u4eff\u4f5b\u6709\u6761\u4eba\u5f71\u4e00\u95ea\u3002 \u3000\u3000\u5979\u8ffd\u5f97\u867d\u7136\u5feb\uff0c\u8fd9\u4e2a\u4eba\u5374\u66f4\u5feb\u3002 \u3000\u3000\u5979\u7a7f\u7a97\u800c\u51fa\uff0c\u4f46\u8fd9\u4e2a\u4eba\u5df2\u5230\u4e86\u5341\u4e08\u5f00\u5916\u3002 \u3000\u3000\u53ef\u662f\u5979\u5e76\u4e0d\u653e\u5f03\uff0c\u5979\u660e\u77e5\u81ea\u5df1\u662f\u7edd\u5bf9\u8feb\u4e0d\u4e0a\u8fd9\u4e2a\u4eba\u7684\uff0c\u53ef\u662f\u5979\u4e00\u5b9a\u8981\u8ffd\u3002 \u3000\u3000\u5979\u7528\u51fa\u4e86\u5168\u8eab\u7684\u529b\u91cf\u8ffd\u8fc7\u53bb\u3002 \u3000\u3000\u8fdc\u5904\u66f4\u9ed1\u6697\uff0c\u8fde\u4eba\u5f71\u90fd\u770b\u4e0d\u89c1\u4e86\u3002\u6a2a\u5df7\u91cc\u6709\u4e2a\u53e4\u8001\u7684\u67ef\u5802\uff0c\u8fd8\u71c3\u7740\u76cf\u5b64\u706f\u3002 \u3000\u3000\u5728\u8fd9\u53e4\u8001\u7684\u957f\u5b89\u57ce\u91cc\uff0c\u5230\u5904\u90fd\u53ef\u4ee5\u770b\u5230\u8fd9\u79cd\u8bcd\u5802\uff0c\u7834\u65e7\uff0c\u51b7\u843d\uff0c\u65e0\u4eba\u3002 \u3000\u3000\u5979\u5ffd\u7136\u505c\u4e0b\u6765\uff0c\u653e\u58f0\u5927\u547c\uff01 \u3000\u3000\u201c\u53f6\u5f00\uff0c\u6211\u77e5\u9053\u662f\u4f60\uff0c\u6211\u77e5\u9053\u4f60\u8fd8\u6ca1\u6709\u8d70\u8fdc\uff0c\u4e00\u5b9a\u8fd8\u542c\u5f97\u89c1\u6211\u8bf4\u8bdd\u3002\u201d \u3000\u3000\u9ed1\u6697\u4e2d\u5bc2\u65e0\u56de\u5e94\uff0c\u53ea\u6709\u51e0\u682a\u8fd8\u672a\u51cb\u96f6\u7684\u53e4\u67cf\uff0c\u5728\u5bd2\u98ce\u4e2d\u53f9\u606f\u3002 \u3000\u3000\u201c\u4e0d\u7ba1\u4f60\u60f3\u4e0d\u60f3\u51fa\u6765\u89c1\u6211\uff0c\u4f60\u90fd\u8be5\u542c\u5b8c\u6211\u8bf4\u7684\u8bdd\u3002\u201d \u3000\u3000\u5979\u54ac\u7740\u5634\u5507\uff0c\u52c9\u5f3a\u5fcd\u4f4f\u773c\u6cea\u3002 \u3000\u3000\u201c\u6211\u5e76\u6ca1\u6709\u505a\u5bf9\u4e0d\u8d77\u4f60\u7684\u4e8b\uff0c\u4f60\u82e5\u4e0d\u613f\u518d\u89c1\u6211\uff0c\u6211\u4ea6\u4e0d\u602a\u4f60\uff0c\u4f46\u662f\u2026\u2026\u4f46\u662f\u6211\u53ef\u4ee5 \u6b7b\uff01\u201d \u3000\u3000\u5979\u5ffd\u7136\u7528\u529b\u6495\u5f00\u8863\u895f\uff0c\u9732\u51fa\u8d64\u88f8\u7684\u80f8\u819b\u3002 \u3000\u3000\u5728\u9ed1\u6697\u4e2d\u770b\u6765\uff0c\u5979\u7684\u80f8\u819b\u50cf\u7f0e\u5b50\u822c\u53d1\u7740\u5149\uff0c\u98ce\u5374\u51b7\u5982\u5200\u3002 \u3000\u3000\u5979\u8eab\u5b50\u53c8\u5f00\u59cb\u4e0d\u505c\u7684\u53d1\u6296\u3002 \u3000\u3000\u201c\u6211\u77e5\u9053\u4f60\u4e5f\u8bb8\u4e0d\u76f8\u4fe1\u6211\uff0c\u6211\u77e5\u9053\u2026\u2026\u4f46\u662f\u8fd9\u4e00\u6b21\u6211\u5374\u6b7b\u7ed9\u4f60\u770b\uff01\u201d \u3000\u3000\u5979\u4f38\u51fa\u98a4\u6296\u7684\u624b\uff0c\u4ece\u5934\u4e0a\u62d4\u4e0b\u6068\u516b\u5bf8\u957f\u7684\u91d1\u9497\uff0c\u7528\u5168\u8eab\u529b\u6c14\uff0c\u5f80\u81ea\u5df1\u5fc3\u53e3\u523a\u4e0b\u53bb\u3002 \u3000\u3000\u5979\u662f\u771f\u7684\u60f3\u6b7b\uff01 \u3000\u3000\u5bf9\u5979\u8bf4\u6765\uff0c\u8fd9\u4e16\u754c\u5df2\u6ca1\u6709\u4ec0\u4e48\u503c\u5f97\u7559\u604b\u7684\u5730\u65b9\u3002 \u3000\u3000\u5bb6\u65e2\u60e8\u53d8\uff0c\u5144\u59b9\u98d8\u96f6\uff0c\u5929\u4e0a\u5730\u4e0b\uff0c\u5979\u5df2\u53ea\u5269\u4e0b\u4e00\u4e2a\u53ef\u4ee5\u4f9d\u8d56\u7684\u4eba\u3002 \u3000\u3000\u5979\u672c\u5df2\u51b3\u5fc3\u4e00\u8f88\u5b50\u8ddf\u7740\u8fd9\u4e2a\u4eba\uff0c\u53ef\u662f\u73b0\u5728\u8fd9\u4e2a\u4eba\u5374\u5df2\u8fde\u89c1\u90fd\u4e0d\u613f\u518d\u89c1\u5979\u4e00\u9762\u3002 \u3000\u3000\u91d1\u9497\u523a\u5165\u80f8\u819b\uff0c\u9c9c\u8840\u6e85\u51fa\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u9ed1\u6697\u4e2d\u5ffd\u7136\u6709\u6761\u4eba\u5f71\u8f7b\u4e91\u822c\u98de\u8fc7\u6765\uff0c\u63e1\u4f4f\u4e86\u5979\u7684\u624b\u3002 \u3000\u3000\u201c\u53ee\u201d\u7684\u4e00\u58f0\uff0c\u91d1\u9497\u843d\u5728\u5c4b\u810a\u4e0a\u3002\u9c9c\u7ea2\u7684\u8840\uff0c\u6d41\u8fc7\u767d\u7699\u7684\u80f8\u819b\u3002 \u3000\u3000\u5979\u7ec8\u4e8e\u770b\u89c1\u4e86\u8fd9\u4e2a\u4eba\uff0c\u8fd9\u4e2a\u4ee4\u5979\u9b42\u7275\u68a6\u8426\u3001\u65e0\u8bba\u6b7b\u6d3b\u90e8\u5fd8\u4e0d\u4e86\u7684\u4eba\uff01 \u3000\u3000\u5979\u7ec8\u4e8e\u89c1\u5230\u53f6\u5f00\u3002 \u3000\u3000\u591c\u8272\u51c4\u8ff7\uff0c\u6de1\u6de1\u7684\u661f\u5149\uff0c\u7167\u7740\u53f6\u5f00\u7684\u8138\u3002 \u3000\u3000\u4ed6\u770b\u6765\u4eff\u4f5b\u8fd8\u662f\u8001\u6837\u5b50\uff0c\u773c\u775b\u8fd8\u662f\u90a3\u4e48\u660e\u4eae\uff0c\u5634\u89d2\u8fd8\u662f\u5e26\u7740\u5fae\u7b11\u3002 \u3000\u3000\u53ef\u662f\u4f60\u82e5\u4ed4\u7ec6\u770b\u4e00\u770b\uff0c\u4f60\u5c31\u4f1a\u53d1\u73b0\uff0c\u4ed6\u7684\u773c\u775b\u53d1\u4eae\uff0c\u53ea\u4e0d\u8fc7\u662f\u56e0\u4e3a\u6cea\u5149\u3002 \u3000\u3000\u4ed6\u867d\u7136\u8fd8\u662f\u5728\u7b11\uff0c\u7b11\u5bb9\u4e2d\u5374\u5145\u6ee1\u4e86\u51c4\u51c9\u548c\u60b2\u4f24\u3002 \u3000\u3000\u201c\u4f60\u4e0d\u5fc5\u8fd9\u4e48\u6837\u505a\u7684\uff0c\u201d\u4ed6\u8f7b\u8f7b\u53f9\u606f\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u8981\u4f24\u5bb3\u81ea\u5df1\uff1f\u201d \u3000\u3000\u4e01\u7075\u7433\u770b\u7740\u4ed6\uff0c\u75f4\u75f4\u5730\u770b\u7740\u4ed6\uff0c\u6574\u4e2a\u4eba\u90fd\u4f3c\u5df2\u75f4\u4e86\u3002 \u3000\u3000\u76f8\u89c1\u4e0d\u5982\u4e0d\u89c1\u3002 \u3000\u3000\u2026\u2026\u4e3a\u4ec0\u4e48\u82cd\u5929\u4e00\u5b9a\u8981\u5b89\u6392\u4ed6\u4eec\u518d\u89c1\u8fd9\u4e00\u6b21\uff1f\u4e3a\u4ec0\u4e48\uff1f \u3000\u3000\u53f6\u5f00\u663e\u7136\u4e5f\u5728\u52c9\u5f3a\u63a7\u5236\u7740\u81ea\u5df1\uff1a\u201c\u6211\u77e5\u9053\u4f60\u6ca1\u6709\u5bf9\u4e0d\u8d77\u6211\uff0c\u4f60\u4e5f\u6ca1\u6709\u9517\uff0c\u9517\u7684\u662f\u6211\u3002\u201d \u3000\u3000\u201c\u4f60\u2026\u2026\u201d \u3000\u3000\u53f6\u5f00\u4e0d\u8ba9\u5979\u8bf4\u4e0b\u53bb\uff1a\u201c\u4f60\u4ec0\u4e48\u90fd\u4e0d\u5fc5\u8bf4\uff0c\u6211\u4ec0\u4e48\u90fd\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u4f60\u2026\u2026\u4f60\u771f\u7684\u77e5\u9053\uff1f\u201d \u3000\u3000\u53f6\u5f00\u70b9\u70b9\u5934\uff0c\u9eef\u7136\u9053\uff1a\u201c\u6211\u82e5\u662f\u4f60\uff0c\u6211\u4e00\u5b9a\u4e5f\u4f1a\u8fd9\u4e48\u6837\u505a\uff0c\u90ed\u5b9a\u662f\u4e2a\u5f88\u6709\u524d\u9014\u7684\u5e74\u8f7b \u4eba\uff0c\u662f\u4e2a\u597d\u4eba\uff0c\u4f60\u5f53\u7136\u7edd\u4e0d\u80fd\u770b\u7740\u4ed6\u4e3a\u4f60\u800c\u6b7b\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u6cea\u6c34\u53c8\u6625\u6cc9\u822c\u6d8c\u51fa\uff1a\u201c\u53ef\u662f\u6211\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u662f\u4e2a\u5f88\u5584\u826f\u7684\u5973\u5b69\u5b50\uff0c\u4f60\u77e5\u9053\u53ea\u6709\u8fd9\u4e48\u6837\u505a\uff0c\u624d\u80fd\u8ba9\u90ed\u5b9a\u89c9\u5f97\u8fd8\u53ef\u4ee5\u6d3b\u4e0b\u53bb\u3002\u201d \u3000\u3000\u53f6\u5f00\u53f9\u606f\u9053\uff1a\u201c\u4e00\u4e2a\u4eba\u82e5\u5df1\u8fde\u81ea\u5df1\u90fd\u4e0d\u60f3\u518d\u6d3b\u4e0b\u53bb\uff0c\u5929\u4e0b\u5c31\u7edd\u5bf9\u518d\u4e5f\u6ca1\u6709\u4eba\u80fd\u6551\u5f97\u4e86 \u4ed6\uff0c\u8fde\u845b\u75c5\u4e5f\u4e00\u6837\u4e0d\u80fd\u3002\u201d \u3000\u3000\u4ed6\u7684\u786e\u4e86\u89e3\u90ed\u5b9a\uff0c\u66f4\u4e86\u89e3\u5979\u3002 \u3000\u3000\u4e16\u4e0a\u7edd\u6ca1\u6709\u4efb\u4f55\u4e8b\u4ef6\u6bd4\u8fd9\u79cd\u540c\u60c5\u548c\u4e86\u89e3\u66f4\u73cd\u8d35\u3002 \u3000\u3000\u4e01\u7075\u7433\u5c31\u50cf\u662f\u4e2a\u53d7\u4e86\u59d4\u5c48\u7684\u5b69\u5b50\uff0c\u5ffd\u7136\u6251\u5728\u4ed6\u6000\u91cc\uff0c\u653e\u58f0\u75db\u54ed\u8d77\u6765\u3002 \u3000\u3000\u53f6\u5f00\u5c31\u8ba9\u5979\u54ed\u3002 \u3000\u3000\u54ed\u4e5f\u662f\u79cd\u53d1\u6cc4\uff0c\u4ed6\u5e0c\u671b\u5979\u5fc3\u91cc\u7684\u59d4\u5c48\u548c\u60b2\u75db\uff0c\u80fd\u968f\u7740\u5979\u7684\u773c\u6cea\u4e00\u8d77\u6d41\u51fa\u6765\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u81ea\u5df1\u5462\uff1f \u3000\u3000\u4ed6\u7edd\u4e0d\u80fd\u54ed\uff0c\u751a\u81f3\u8fde\u9ed8\u9ed8\u5730\u6d41\u51e0\u6ef4\u773c\u6cea\u90fd\u4e0d\u884c\uff0c\u4ed6\u77e5\u9053\u5728\u4ed6\u4eec\u4e24\u4e2a\u4eba\u4e4b\u95f4\uff0c\u81f3\u5c11\uff0c\u8981\u6709 \u4e00\u4e2a\u4eba\u662f\u575a\u5f3a\u7684\u3002 \u3000\u3000\u4ed6\u4e00\u5b9a\u8981\u575a\u5f3a\u8d77\u6765\uff0c\u65e0\u8bba\u591a\u4e48\u5927\u7684\u59d4\u5c48\u548c\u60b2\u75db\uff0c\u4ed6\u90fd\u4e00\u5b9a\u8981\u60f3\u6cd5\u5b50\u9690\u85cf\u5728\u5fc3\u91cc\uff0c\u54ac\u7740\u7259 \u5fcd\u53d7\u3002 \u3000\u3000\u4ed6\u80fd\u5fcd\u53d7\u3002 \u3000\u3000\u591c\u66f4\u6df1\uff0c\u98ce\u66f4\u51b7\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u5979\u7684\u75db\u54ed\u7ec8\u4e8e\u53d8\u6210\u4e86\u4f4e\u4f4d\uff0c\u53f6\u5f00\u624d\u8f7b\u8f7b\u63a8\u5f00\u5979\uff0c\u9053\uff1a\u201c\u4f60\u5e94\u8be5\u56de\u53bb \u4e86\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u6115\u7136\u9053\uff1a\u201c\u4f60\u53eb\u6211\u56de\u53bb\uff1f\u56de\u5230\u54ea\u91cc\u53bb\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u56de\u5230\u4f60\u521a\u624d\u51fa\u6765\u7684\u5730\u65b9\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u522b\u4eba\u4e00\u5b9a\u5df2\u7b49\u5f97\u5f88\u7740\u6025\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u7684\u4eba\u7a81\u53c8\u51b0\u51b7\u50f5\u786c\uff1a\u201c\u4f60\u2026\u2026\u4f60\u8fd8\u662f\u8981\u6211\u56de\u53bb\u5ac1\u7ed9\u90ed\u5b9a\uff1f\u201d \u3000\u3000\u53f6\u5f00\u786c\u8d77\u4e86\u5fc3\u80a0\u9053\uff1a\u201c\u4f60\u7edd\u4e0d\u80fd\u8fd9\u4e48\u629b\u4e0b\u4ed6\u3002\u4f60\u4e5f\u5e94\u77e5\u9053\uff0c\u4f60\u82e5\u8fd9\u6837\u4e00\u8d70\uff0c\u4ed6\u4e00\u5b9a\u6ca1\u6cd5 \u5b50\u518d\u6d3b\u4e0b\u53bb\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u4e5f\u4e0d\u80fd\u4e0d\u627f\u8ba4\uff0c\u90ed\u5b9a\u4e4b\u6240\u4ee5\u8fd8\u6709\u6c42\u751f\u7684\u6597\u5fd7\uff0c\u5168\u662f\u56e0\u4e3a\u5979\u3002 \u3000\u3000\u53f6\u5f00\u7684\u5fc3\u5df2\u62bd\u7d27\uff1a\u201c\u90ed\u5b9a\u82e5\u771f\u7684\u6b7b\u4e86\uff0c\u975e\u4f46\u6211\u7edd\u4e0d\u80fd\u539f\u8c05\u4f60\uff0c\u4f60\u81ea\u5df1\u4e5f\u4e00\u5b9a\u6c38\u8fdc\u4e0d\u4f1a\u539f \u8c05\u81ea\u5df1\u7684\u3002\u201d \u3000\u3000\u2014\u2014\u90a3\u4e48\uff0c\u6211\u4eec\u4e24\u4e2a\u4eba\u5c31\u7b97\u80fd\u5728\u4e00\u8d77\uff0c\u4e5f\u5fc5\u5c06\u75db\u82e6\u4e00\u8f88\u5b50\u3002 \u3000\u3000\u4ed6\u6ca1\u6709\u8bf4\u51fa\u4e0b\u9762\u7684\u8bdd\uff0c\u4ed6\u77e5\u9053\u4e01\u7075\u7433\u4e00\u5b9a\u4e5f\u80fd\u4e86\u89e3\u3002 \u3000\u3000\u4e01\u7075\u7433\u5782\u7740\u5934\uff0c\u8fc7\u4e86\u5f88\u4e45\uff0c\u624d\u51c4\u51c9\u9053\uff1a\u201c\u6211\u56de\u53bb\uff0c\u4f60\u5462\uff1f\u201d \u3000\u3000\u201c\u6211\u80fd\u6d3b\u5f97\u4e0b\u53bb\u7684\u3002\u201d\u53f6\u5f00\u60f3\u52c9\u5f3a\u81ea\u5df1\u7b11\u4e00\u7b11\uff0c\u5374\u7b11\u4e0d\u51fa\uff1a\u201c\u4f60\u5e94\u8be5\u77e5\u9053\u6211\u4e00\u5411\u662f\u4e2a\u575a \u5f3a\u7684\u4eba\u3002\u201d \u3000\u3000\u201c\u6211\u4eec\u4ee5\u540e\u96be\u9053\u6c38\u8fdc\u4e5f\u4e0d\u80fd\u518d\u89c1\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u8fd8\u80fd\u518d\u89c1\u3002\u201d \u3000\u3000\u53f6\u5f00\u7684\u5fc3\u5728\u523a\u75db\uff0c\u8fd9\u662f\u4ed6\u7b2c\u4e00\u6b21\u5bf9\u5979\u8bf4\u8c0e\uff0c\u4ed6\u4e0d\u80fd\u4e0d\u8fd9\u4e48\u6837\u8bf4\u201c\u2026\u2026\u53ea\u8981\u4e8b\u60c5\u8fc7\u53bb\uff0c\u6211 \u4eec\u5f53\u7136\u8fd8\u80fd\u518d\u89c1\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u5ffd\u7136\u62ac\u8d77\u5934\uff0c\u76ef\u7740\u4ed6\uff1a\u201c\u597d\uff0c\u6211\u7b54\u5e94\u4f60\uff0c\u6211\u56de\u53bb\uff0c\u53ef\u662f\u4f60\u4e5f\u8981\u7b54\u5e94\u6211\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u201c\u4f60\u8bf4\u3002\u201d \u3000\u3000\u201c\u82e5\u662f\u4e8b\u60c5\u5df2\u8fc7\u53bb\uff0c\u6211\u8fd8\u662f\u627e\u4e0d\u5230\u4f60\uff0c\u6240\u4ee5\u4f60\u4e00\u5b9a\u8981\u544a\u8bc9\u6211\uff0c\u4f60\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u53f6\u5f00\u907f\u5f00\u4e86\u5979\u7684\u76ee\u5149\uff1a\u201c\u53ea\u8981\u77e5\u9053\u4e8b\u60c5\u5df1\u8fc7\u53bb\uff0c\u7528\u4e0d\u7740\u4f60\u627e\u6211\uff0c\u6211\u4f1a\u53bb\u627e\u4f60\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u6211\u82e5\u80fd\u597d\u597d\u89e3\u51b3\u6240\u6709\u7684\u4e8b\uff0c\u90ed\u5b9a\u82e5\u80fd\u597d\u597d\u7684\u6d3b\u7740\uff0c\u4f60\u5c31\u4f1a\u6765\u627e\u6211\uff1f\u201d \u3000\u3000\u53f6\u5f00\u70b9\u70b9\u5934\u3002 \u3000\u3000\u201c\u4f60\u8bf4\u7684\u662f\u771f\u8bdd\uff1f\u4f60\u771f\u7684\u6ca1\u6709\u9a97\u6211\uff1f\u201d \u3000\u3000\u201c\u771f\u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u7684\u5fc3\u5df2\u788e\u4e86\u3002 \u3000\u3000\u4ed6\u81ea\u5df1\u77e5\u9053\u81ea\u5df1\u8bf4\u7684\u5e76\u4e0d\u662f\u771f\u6d3b\uff0c\u4f46\u4e01\u7075\u7433\u5374\u5df2\u5b8c\u5168\u76f8\u4fe1\u3002 \u3000\u3000\u2014\u2014\u4eba\u4eec\u4e3a\u4ec0\u4e48\u603b\u662f\u8981\u6b3a\u9a97\u4e00\u4e2a\u5bf9\u81ea\u5df1\u6700\u4fe1\u4efb\u7684\u4eba\uff1f \u3000\u3000\u56e0\u4e3a\u4ed6\u65e0\u53ef\u5948\u4f55\u3002 \u3000\u3000\u2014\u2014\u751f\u547d\u4e2d\u4e3a\u4ec0\u4e48\u8981\u6709\u8fd9\u4e48\u591a\u65e0\u53ef\u5948\u4f55\u7684\u60b2\u4f24\u548c\u75db\u82e6\uff1f \u3000\u3000\u4ed6\u4e0d\u77e5\u9053\uff0c\u4e5f\u65e0\u6cd5\u4e86\u89e3\u3002 \u3000\u3000\u4ed6\u53ea\u77e5\u9053\u81ea\u5df1\u53ea\u6709\u8fd9\u4e00\u6761\u8def\u53ef\u8d70\uff0c\u4e00\u6761\u5bc2\u5bde\u800c\u6f2b\u957f\u7684\u8def\u3002 \u3000\u3000\u2014\u2014\u2014\u4e2a\u771f\u6b63\u7684\u7537\u5b50\u6c49\uff0c\u82e5\u662f\u5230\u4e86\u5fc5\u8981\u7684\u65f6\u5019\uff0c\u603b\u4f1a\u727a\u7272\u81ea\u5df1\uff0c\u6210\u5168\u522b\u4eba\u7684\u3002 \u3000\u3000\u4e01\u7075\u7433\u7ec8\u4e8e\u4e0b\u5b9a\u51b3\u5fc3\uff1a\u201c\u597d\uff0c\u6211\u73b0\u5728\u5c31\u8d70\uff0c\u6211\u76f8\u4fe1\u4f60\u3002\u201d \u3000\u3000\u201c\u6211\u2026\u2026\u6211\u4ee5\u540e\u4e00\u5b9a\u4f1a\u53bb\u627e\u4f60\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u70b9\u70b9\u5934\uff0c\u6162\u6162\u5730\u8f6c\u8fc7\u8eab\uff0c\u4eff\u4f5b\u5df2\u4e0d\u6562\u518d\u591a\u770b\u4ed6\u4e00\u773c\u3002 \u3000\u3000\u5979\u751f\u6015\u81ea\u5df1\u4f1a\u6539\u53d8\u4e3b\u610f\u3002 \u3000\u3000\u5979\u8f6c\u8fc7\u8eab\uff0c\u5c06\u661f\u5149\u7559\u5728\u80cc\u540e\uff0c\u5c06\u751f\u547d\u4e5f\u7559\u5728\u80cc\u540e\uff0c\u5979\u7528\u529b\u63e1\u7d27\u53cc\u62f3\uff0c\u7528\u51fa\u4e86\u6240\u6709\u7684\u529b \u91cf\uff0c\u7ec8\u4e8e\u8bf4\u51fa\u4e86\u4e09\u4e2a\u5b57\uff1a\u201c\u4f60\u8d70\u5427\u3002\u201d \u3000\u3000\u53f6\u5f00\u8d70\u4e86\u3002 \u3000\u3000\u4ed6\u6ca1\u6709\u518d\u8bf4\u4e00\u53e5\u8bdd\uff0c\u4ed6\u4e0d\u6562\u518d\u8bf4\u3002\u4ed6\u4e5f\u7528\u51fa\u4e86\u6240\u6709\u7684\u529b\u91cf\uff0c\u624d\u63a7\u5236\u4f4f\u81ea\u5df1\u3002 \u3000\u3000\u5bd2\u98ce\u5982\u5200\uff0c\u4ed6\u8fce\u98ce\u98de\u5954\uff0c\u5954\u4e86\u5f88\u4e45\uff0c\u7136\u540e\u5c31\u5f2f\u4e0b\u4e86\u8170\uff0c\u5f00\u59cb\u4e0d\u505c\u5455\u5410\u3002 \u3000\u3000\u4e01\u7075\u7433\u4e5f\u5728\u5455\u5410\u3002 \u3000\u3000\u5979\u4e0d\u505c\u5730\u5455\u5410\uff0c\u8fde\u80c6\u6c41\u82e6\u6c34\u90fd\u5410\u51fa\u6765\u4e86\u3002 \u3000\u3000\u53ef\u662f\u5979\u5df2\u4e0b\u5b9a\u51b3\u5fc3\uff0c\u53f6\u5f00\u65e2\u7136\u8fd8\u6ca1\u6709\u6b7b\uff0c\u5979\u5c31\u7edd\u4e0d\u80fd\u5ac1\u7ed9\u522b\u4eba\u3002 \u3000\u3000\u65e0\u8bba\u5728\u4ec0\u4e48\u60c5\u51b5\u4e0b\u90fd\u4e0d\u80fd\u53bb\u5ac1\u522b\u4eba\uff0c\u5c31\u7b97\u6b7b\uff0c\u4e5f\u4e0d\u80fd\u3002 \u3000\u3000\u5979\u5df2\u51b3\u5fc3\u8981\u56de\u53bb\u544a\u8bc9\u90ed\u5b9a\uff0c\u5c06\u5979\u7684\u611f\u60c5\u3001\u5979\u7684\u75db\u82e6\u90fd\u544a\u8bc9\u90ed\u5b9a\u3002 \u3000\u3000\u90ed\u5b9a\u82e5\u771f\u7684\u662f\u4e2a\u7537\u5b50\u6c49\uff0c\u5c31\u5e94\u8be5\u4e86\u89e3\uff0c\u5c31\u5e94\u8be5\u81ea\u5df1\u7ad9\u8d77\u6765\uff0c\u6d3b\u4e0b\u53bb\u3002 \u3000\u3000\u5979\u76f8\u4fe1\u90ed\u5b9a\u662f\u4e2a\u7537\u5b50\u6c49\u3002 \u3000\u3000\u5979\u76f8\u4fe1\u8fd9\u4e00\u5207\u4e8b\u90fd\u4f1a\u5706\u6ee1\u89e3\u51b3\u7684\uff0c\u5230\u90a3\u65f6\uff0c\u53f6\u5f00\u4e00\u5b9a\u5c31\u4f1a\u6765\u627e\u5979\u3002 \u3000\u3000\u7528\u4e0d\u4e86\u591a\u4e45\uff0c\u6240\u6709\u7684\u82e6\u96be\uff0c\u5f88\u5feb\u5c31\u4f1a\u8fc7\u53bb\uff0c\u5979\u6709\u4fe1\u5fc3\u3002 \u3000\u3000\u9e3f\u5bbe\u5ba2\u6808\u7684\u5927\u5385\u91cc\uff0c\u706f\u5149\u4f9d\u65e7\u8f89\u714c\uff0c\u8fd8\u6709\u4e00\u9635\u9635\u60a0\u626c\u7684\u7b1b\u58f0\u4f20\u51fa\u3002 \u3000\u3000\u6765\u3002 \u3000\u3000\u73b0\u5728\u90a3\u4e2a\u9ed1\u8863\u4eba\u4e00\u5b9a\u5df2\u9003\u8d70\uff0c\u90ed\u5b9a\u4e00\u5b9a\u8fd8\u6d3b\u7740\uff0c\u5927\u5bb6\u4e00\u5b9a\u8fd8\u5728\u7b49\u7740\u5979\u3002 \u3000\u3000\u5979\u8dc3\u4e0b\u5c4b\u810a\uff0c\u8d70\u5165\u5927\u5385\u3002 \u3000\u3000\u5979\u7684\u4eba\u5ffd\u7136\u5b8c\u5168\u51b0\u51b7\uff0c\u5c31\u50cf\u662f\u5ffd\u7136\u843d\u5165\u4e86\u4e00\u4e2a\u5bd2\u51b7\u9ed1\u6697\u7684\u4e07\u4e08\u6df1\u6e0a\u91cc\u3002 \u3000\u3000\u5c31\u50cf\u662f\u5ffd\u7136\u843d\u5165\u4e86\u5730\u72f1\u91cc\u3002 \u3000\u3000\u5927\u5385\u91cc\u751a\u81f3\u5df2\u53d8\u5f97\u6bd4\u5730\u72f1\u91cc\u8fd8\u53ef\u6015\u3002 \u3000\u3000\u5730\u72f1\u91cc\u71c3\u70e7\u7740\u6c38\u4e0d\u7184\u706d\u7684\u706b\u7130\uff0c\u706b\u7130\u662f\u7ea2\u7684\u3002 \u3000\u3000\u8fd9\u5927\u5385\u91cc\u4e5f\u662f\u7ea2\u7684\uff0c\u4f46\u6700\u7ea2\u7684\u5374\u4e0d\u662f\u90a3\u5bf9\u9f99\u51e4\u82b1\u70db\uff0c\u4e5f\u4e0d\u662f\u4eba\u8eab\u4e0a\u7684\u8863\u670d\uff0c\u800c\u662f\u8840\u3002 \u3000\u3000\u9c9c\u8840\uff01 \u3000\u3000\u5979\u542c\u770b\u5f97\u5230\u7684\u4eba\uff0c\u90fd\u5df2\u5012\u4e86\u4e0b\u53bb\uff0c\u5012\u5728\u8840\u6cca\u4e2d\uff0c\u8fd9\u5927\u5385\u91cc\u5df2\u53ea\u5269\u4e0b\u4e00\u4e2a\u6d3b\u4eba\uff0c\u4e00\u4e2a\u4eba\u8fd8 \u5728\u5439\u7b1b\u3002 \u3000\u3000\u4ed6\u7684\u8138\u4e0a\u5df2\u5b8c\u5168\u6ca1\u6709\u8840\u8272\uff0c\u773c\u775b\u53d1\u76f4\uff0c\u4eba\u5df2\u50f5\u786c\uff0c\u4f46\u5374\u8fd8\u5728\u4e0d\u505c\u5730\u5439\u3002 \u3000\u3000\u4ed6\u867d\u7136\u8fd8\u6d3b\u7740\uff0c\u5374\u5df2\u5931\u53bb\u4e86\u9b42\u9b44\u3002 \u3000\u3000\u6ca1\u6709\u4eba\u80fd\u5f62\u5bb9\u8fd9\u79cd\u7b1b\u58f0\u542c\u5728\u4e01\u7075\u7433\u8033\u91cc\u65f6\uff0c\u662f\u4ec0\u4e48\u6ecb\u5473\uff0c\u751a\u81f3\u6ca1\u6709\u4eba\u80fd\u60f3\u8c61\u3002 \u3000\u3000\u90ed\u5b9a\u5df2\u6c38\u8fdc\u542c\u4e0d\u5230\u5979\u7684\u89e3\u91ca\u548c\u82e6\u8877\uff0c\u5df2\u5012\u5728\u8840\u6cca\u4e2d\uff0c\u548c\u90a3\u9ed1\u8863\u4eba\u5012\u5728\u4e00\u8d77\uff0c\u8fd8\u6709\u90a3\u4e2a\u5584 \u826f\u7684\u8001\u4eba\uff0c\u8fd8\u6709\u2026\u2026 \u3000\u3000\u4e01\u7075\u7433\u6ca1\u6709\u518d\u770b\u4e0b\u53bb\uff0c\u5979\u7684\u773c\u524d\u53ea\u6709\u4e00\u7247\u9c9c\u7ea2\u7684\u8840\uff0c\u5df2\u770b\u4e0d\u5230\u522b\u7684\u3002 \u3000\u3000\u8fd9\u7a76\u7adf\u662f\u8c01\u4e0b\u7684\u6bd2\u624b\uff1f\u7a76\u7adf\u662f\u4e3a\u4e86\u4ec0\u4e48\uff1f \u3000\u3000\u5979\u5df2\u65e0\u6cd5\u601d\u7d22\uff0c\u5979\u5012\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u4e01\u7075\u7433\u518d\u6b21\u5f20\u5f00\u773c\u65f6\uff0c\u7b2c\u4e00\u773c\u770b\u89c1\u7684\uff0c\u662f\u53e3\u534e\u8d35\u800c\u7cbe\u7f8e\u7684\u7bb1\u5b50\u3002 \u3000\u3000\u4e07\u5b9d\u7bb1\u3002 \u3000\u3000\u90a3\u84dd\u8863\u9ad8\u51a0\u7684\u8001\u4eba\uff0c\u6b63\u7ad9\u5728\u5e8a\u524d\uff0c\u51dd\u89c6\u7740\u5979\uff0c\u773c\u775b\u91cc\u4e5f\u5145\u6ee1\u4e86\u60b2\u75db\u548c\u601c\u60af\u3002 \u3000\u3000\u4e01\u7075\u7433\u60f3\u6323\u624e\u7740\u5750\u8d77\u6765\uff0c\u845b\u75c5\u5374\u6309\u4f4f\u4e86\u5979\u7684\u80a9\uff0c\u5979\u53ea\u6709\u518d\u8eba\u4e0b\u3002 \u3000\u3000\u5979\u77e5\u9053\u662f\u8fd9\u8001\u4eba\u6551\u4e86\u5979\uff0c\u53ef\u662f\u2026\u2026 \u3000\u3000\u201c\u90ed\u5b9a\u5462\uff1f\u4f60\u6709\u6ca1\u6709\u6551\u4ed6\uff1f\u201d \u3000\u3000\u845b\u75c5\u9eef\u7136\u6447\u5934\uff0c\u957f\u957f\u53f9\u606f\uff0c\u9053\uff1a\u201c\u6211\u53bb\u8fdf\u4e86\u2026\u2026\u201d \u3000\u3000\u4e01\u7075\u7433\u7a81\u7136\u5927\u53eb\uff1a\u201c\u4f60\u53bb\u8fdf\u4e86\uff1f\u2026\u4f60\u4e3a\u4ec0\u4e48\u8981\u6e9c\u8d70\uff1f\u201d \u3000\u3000\u845b\u75c5\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u8981\u8d76\u7740\u53bb\u627e\u4eba\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u8fd8\u5728\u53eb\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u8981\u53bb\u627e\u4eba\uff1f\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5979\u5df1\u5b8c\u5168\u65e0\u6cd5\u63a7\u5236\u81ea\u5df1\uff0c\u845b\u75c5\u624d\u6c89\u58f0\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u4e00\u5b9a\u8981\u53bb\u627e\u4eba\u6765\u5236\u6b62\u8fd9\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u4f60\u65e9\u5df2\u77e5\u9053\u4f1a\u6709\u8fd9\u4ef6\u4e8b\u53d1\u751f\uff1f\u201d \u3000\u3000\u845b\u75c5\u53f9\u9053\uff1a\u201c\u770b\u89c1\u4e86\u90a3\u888b\u73e0\u5b9d\uff0c\u770b\u89c1\u4e86\u90a3\u56db\u4e2a\u4eba\u7684\u540d\u5b57\u65f6\uff0c\u6211\u5c31\u5df2\u77e5\u9053\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u4f60\u77e5\u9053\u90a3\u56db\u4e2a\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u845b\u75c5\u70b9\u70b9\u5934\u3002 \u3000\u3000\u201c\u4ed6\u4eec\u7a76\u7adf\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u662f\u9b54\u6559\u4e2d\u7684\u56db\u5927\u5929\u738b\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u53c8\u5012\u4e0b\uff0c\u5c31\u50cf\u662f\u7a81\u7136\u88ab\u4e00\u67c4\u94c1\u9524\u51fb\u5012\uff0c\u8fde\u52a8\u90fd\u4e0d\u80fd\u52a8\u4e86\u3002 \u3000\u3000\u845b\u75c5\u5f90\u5f90\u5730\u9053\uff1a\u201c\u5f53\u65f6\u6211\u6ca1\u6709\u8bf4\u51fa\u6765\uff0c\u5c31\u56e0\u4e3a\u6015\u4f60\u4eec\u542c\u4e86\u540e\u4f1a\u60ca\u614c\u6050\u60e7\uff0c\u6211\u4e0d\u613f\u610f\u5f71\u54cd \u5230\u4f60\u4eec\u7684\u559c\u4e8b\u3002\u201d \u3000\u3000\u559c\u4e8b\uff01 \u3000\u3000\u90a3\u7b97\u662f\u4ec0\u4e48\u6837\u7684\u559c\u4e8b\uff1f \u3000\u3000\u4e01\u7075\u7433\u53c8\u60f3\u8df3\u8d77\u6765\uff0c\u53c8\u60f3\u5927\u53eb\uff0c\u5374\u5df2\u8fde\u53eb\u7684\u529b\u6c14\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u845b\u75c5\u9053\uff1a\u201c\u4f55\u51b5\u6211\u4e5f\u770b\u89c1\u4e86\u90a3\u56db\u4e2a\u9ec4\u8863\u4f7f\u8005\uff0c\u6211\u8ba4\u4e3a\u91d1\u94b1\u5e2e\u65e2\u7136\u5df2\u63d2\u624b\u8981\u7ba1\uff0c\u5c31\u7b97\u9b54\u6559 \u7684\u56db\u5927\u5929\u738b\uff0c\u4e5f\u4e0d\u80fd\u7a0d\u6709\u987e\u5fcc\u3002\u201d\u4ed6\u9eef\u7136\u53f9\u606f\uff0c\u53c8\u9053\uff1a\u201c\u4f46\u6211\u5374\u60f3\u4e0d\u5230\u8fd9\u4ef6\u4e8b\u4e2d\u9014\u7ade\u53c8\u6709\u4e86 \u53d8\u5316\u3002\u201d \u3000\u3000\u201c\u4f60\u662f\u4e0d\u662f\u8ba4\u4e3a\u53f6\u5f00\u4e00\u5b9a\u4f1a\u5728\u6697\u4e2d\u7167\u987e\u7684\uff1f\u201d \u3000\u3000\u845b\u75c5\u53ea\u6709\u627f\u8ba4\u3002 \u3000\u3000\u201c\u6240\u4ee5\u4f60\u60f3\u4e0d\u5230\u53f6\u5f00\u4f1a\u8d70\uff0c\u4e5f\u60f3\u4e0d\u5230\u6211\u4f1a\u8d70\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u7684\u58f0\u97f3\u5f88\u865a\u5f31\u3002 \u3000\u3000\u5979\u6574\u4e2a\u4eba\u90fd\u4f3c\u5df2\u7a7a\u4e86\u3002 \u3000\u3000\u845b\u75c5\u53f9\u9053\uff1a\u201c\u6211\u5e94\u8be5\u60f3\u5230\u4ed6\u53ef\u80fd\u4f1a\u8d70\u7684\uff0c\u56e0\u4e3a\u4ed6\u5e76\u6ca1\u6709\u770b\u89c1\u90a3\u5757\u7389\u724c\uff0c\u4e5f\u6ca1\u6709\u770b\u89c1\u90a3\u888b \u73e0\u5b9d\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u5fcd\u4e0d\u4f4f\u95ee\uff1a\u201c\u4ed6\u4eec\u9001\u90a3\u888b\u73e0\u5b9d\u6765\uff0c\u96be\u9053\u4e5f\u6709\u7279\u6b8a\u7684\u610f\u601d\uff1f\u201d \u3000\u3000\u201c\u6709\uff01\u201d \u3000\u3000\u201c\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u845b\u75c5\u4e00\u5b57\u5b57\u9053\uff1a\u201c\u4ed6\u4eec\u9001\u90a3\u888b\u73e0\u5b9d\u6765\uff0c\u662f\u6765\u4e70\u547d\u7684\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u9a87\u7136\u9053\uff1a\u201c\u662f\u4e70\u547d\u7684\uff1f\u201d \u3000\u3000\u845b\u75c5\u9053\uff1a\u201c\u9b54\u6559\u4e2d\u7684\u5927\u5929\u738b\uff0c\u4e00\u5411\u5f88\u5c11\u81ea\u5df1\u51fa\u624b\u6740\u4eba\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u845b\u75c5\u9053\uff1a\u201c\u56e0\u4e3a\u4ed6\u4eec\u76f8\u4fe1\u5730\u72f1\u8f6e\u56de\uff0c\u4ece\u4e0d\u613f\u6b20\u4e0b\u6765\u751f\u503a\u3002\u6240\u4ee5\u4ed6\u4eec\u6bcf\u6b21\u81ea\u5df1\u51fa\u6765\u6740\u4eba \u524d\uff0c\u90fd\u4f1a\u5148\u4ed8\u4e00\u7b14\u4ee3\u4ef7\uff0c\u4e70\u4eba\u7684\u547d\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u5ffd\u7136\u53c8\u95ee\uff1a\u201c\u4f60\u600e\u4e48\u4f1a\u77e5\u9053\u6211\u8d70\u4e86\uff0c\u53f6\u5f00\u4e5f\u8d70\u4e86\uff1f\u201d \u3000\u3000\u201c\u6709\u4eba\u544a\u8bc9\u6211\u7684\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u201c\u90a3\u4e2a\u5439\u7b1b\u4eba\u3002\u201d \u3000\u3000\u60f3\u8d77\u4e86\u90a3\u51c4\u51c9\u7684\u7b1b\u58f0\uff0c\u4e01\u7075\u7433\u4e0d\u7981\u6253\u4e86\u4e2a\u5bd2\u5664\uff1a\u201c\u4ed6\u4eb2\u773c\u770b\u89c1\u4e86\u8fd9\u4ef6\u4e8b\uff1f\u201d \u3000\u3000\u845b\u75c5\u957f\u53f9\u9053\uff1a\u201c\u4ece\u5934\u5230\u5c3e\uff0c\u4ed6\u90fd\u5728\u770b\u7740\uff0c\u6240\u4ee5\u82e5\u4e0d\u662f\u9047\u89c1\u4e86\u6211\uff0c\u4ed6\u53ea\u6015\u7ec8\u751f\u90fd\u8981\u53d8\u6210\u4e86 \u75af\u766b\u7684\u5e9f\u4eba\u4e86\u3002\u201d \u3000\u3000\u65e0\u8bba\u8c01\u770b\u89c1\u8fd9\u79cd\u4e8b\uff0c\u90fd\u4f1a\u88ab\u5413\u75af\u7684\u3002 \u3000\u3000\u4e86\u7075\u7433\u53c8\u95ee\uff1a\u201c\u4ed6\u4e5f\u770b\u89c1\u4e86\u90a3\u56db\u5927\u5929\u738b\u7684\u771f\u9762\u76ee\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u56db\u5927\u5929\u738b\u4e3a\u590d\u4ec7\u6740\u4eba\u65f6\uff0c\u8138\u4e0a\u603b\u662f\u6234\u7740\u9b54\u795e\u7684\u9762\u5177\u3002\u201d \u3000\u3000\u201c\u590d\u4ec7\uff1f\u4ed6\u4eec\u662f\u4e3a\u8c01\u590d\u4ec7\uff1f\u201d \u3000\u3000\u201c\u7389\u7bab\u9053\u4eba\u3002\u201d \u3000\u3000\u845b\u75c5\u9053\uff1a\u201c\u7389\u7bab\u9053\u4eba\u662f\u6b7b\u5728\u90ed\u5b9a\u624b\u4e0b\u7684\u3002\u201d \u3000\u3000\u201c\u7389\u7bab\u9053\u4eba\u4e5f\u662f\u56db\u5927\u5929\u738b\u4e4b\u4e00\uff1f\u201d \u3000\u3000\u201c\u4ed6\u5c31\u662f\u7231\u6b32\u5929\u738b\uff0c\u73ed\u5bdf\u5df4\u90a3\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u7528\u529b\u63e1\u7d27\u4e86\u53cc\u624b\uff0c\u8eab\u5b50\u8fd8\u662f\u5728\u4e0d\u505c\u5730\u53d1\u6296\uff1a\u201c\u90ed\u5b9a\u6740\u7389\u7bab\u9053\u4eba\uff0c\u662f\u4e3a\u4e86\u6211\u3002\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u6211\u82e5\u4e0d\u8ffd\u51fa\u53bb\uff0c\u53f6\u5f00\u5c31\u4e0d\u4f1a\u8d70\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u53c8\u5728\u6d41\u6cea\uff1a\u201c\u53f6\u5f00\u82e5\u4e0d\u8d70\uff0c\u4e5f\u8bb8\u5c31\u4e0d\u4f1a\u53d1\u751f\u8fd9\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u845b\u75c5\u5374\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u4f60\u7528\u4e0d\u7740\u57cb\u6028\u81ea\u5df1\uff0c\u8fd9\u4e00\u5207\u672c\u5c31\u5728\u4ed6\u4eec\u7684\u8ba1\u5212\u4e4b\u4e2d\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u4e0d\u61c2\u3002 \u3000\u3000\u845b\u75c5\u9053\uff1a\u201c\u90a3\u9ed1\u8863\u4eba\u5e76\u4e0d\u662f\u5357\u5bab\u6d6a\uff0c\u6211\u8ba4\u5f97\u5357\u5bab\u6d6a\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u53c8\u5403\u4e86\u4e00\u60ca\uff1a\u201c\u4ed6\u4e0d\u662f\u5357\u5b98\u6d6a\u662f\u8c01\uff1f\u201d \u3000\u3000\u845b\u75c5\u9053\uff1a\u201c\u4ed6\u4e5f\u662f\u9b54\u6559\u4e2d\u7684\u4eba\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u4ed6\u5ffd\u7136\u51fa\u73b0\uff0c\u5c31\u662f\u4e3a\u4e86\u8981\u903c\u53f6\u5f00\u51fa\u624b\uff1f\u201d \u3000\u3000\u845b\u75c5\u53f9\u9053\uff1a\u201c\u4ed6\u4eec\u7684\u786e\u65e9\u5df2\u7b97\u51c6\u4e86\u53f6\u5f00\u4e00\u5b9a\u4f1a\u51fa\u624b\u6551\u90ed\u5b9a\uff0c\u4e5f\u7b97\u51c6\u4e86\u53ea\u8981\u53f6\u5f00\u4e00\u73b0\u884c \u8e2a\uff0c\u4f60\u5c31\u4e00\u5b9a\u4f1a\u8ffd\u51fa\u53bb\u3002\u2019\u4ed6\u4eec\u5f53\u7136\u4e5f\u7b97\u51c6\u4e86\u53ea\u8981\u4e01\u7075\u7433\u4e00\u8ffd\u51fa\u53bb\uff0c\u53f6\u5f00\u5c31\u4e00\u5b9a\u4f1a\u8d70\u3002\u9b54\u6559 \u4e2d\u7684\u56db\u5927\u5929\u738b\u884c\u52a8\u4e4b\u524d\uff0c\u4e00\u5b9a\u90fd\u65e9\u5df2\u6709\u4e86\u6781\u5b8c\u7f8e\u5468\u5bc6\u7684\u8ba1\u5212\u3002\u6240\u4ee5\u4ed6\u4eec\u53ea\u8981\u51fa\u624b\uff0c\u5c31\u5f88\u5c11\u843d \u7a7a\u3002\u4e01\u7075\u7433\u6068\u6068\u9053\uff1a\u201c\u8fd9\u4e48\u6837\u770b\u6765\uff0c\u90a3\u4e2a\u6545\u610f\u63ed\u7834\u9ed1\u8863\u4eba\u9634\u8c0b\u3001\u6545\u610f\u8bf4\u4ed6\u662f\u5357\u5b98\u6d6a\u7684\u4eba\uff0c\u5f88 \u53ef\u80fd\u5c31\u662f\u56db\u5927\u5929\u738b\u4e4b\u4e00\u3002\u201d \u3000\u3000\u201c\u5f88\u53ef\u80fd\u3002\u201d \u3000\u3000\u845b\u75c5\u5ffd\u7136\u53c8\u9053\uff1a\u201c\u4f60\u542c\u4e0d\u542c\u5f97\u51fa\u4ed6\u7684\u58f0\u97f3\uff1f\u201d \u3000\u3000\u4e01\u7075\u7433\u542c\u4e0d\u51fa\u3002 \u3000\u3000\u201c\u6211\u53ea\u89c9\u5f97\u90a3\u4eba\u8bf4\u8bdd\u7684\u58f0\u97f3\uff0c\u6bd4\u5c16\u9488\u8fd8\u523a\u8033\u3002\u201d \u3000\u3000\u201c\u4f60\u542c\u4e0d\u542c\u5f97\u51fa\u4ed6\u662f\u7537\u662f\u5973\uff1f\u201d \u3000\u3000\u201c\u662f\u7537\u7684\u3002\u201d \u3000\u3000\u201c\u4e00\u4e2a\u4eba\u8bf4\u8bdd\u7684\u58f0\u97f3\uff0c\u662f\u4ece\u5589\u5499\u91cc\u4e00\u6761\u5e26\u5b50\u822c\u7684\u5668\u5b98\u53d1\u51fa\u6765\u7684\u3002\u201d \u3000\u3000\u845b\u75c5\u7f13\u7f13\u9053\uff1a\u201c\u7537\u4eba\u6210\u957f\u4e4b\u540e\uff0c\u4e0a\u6761\u5e26\u4e8e\u5c31\u4f1a\u6e10\u6e10\u53d8\u7c97\uff0c\u6240\u4ee5\u7537\u4eba\u8bf4\u8bdd\u7684\u58f0\u97f3\uff0c\u603b\u6bd4\u5973 \u4eba\u4f4e\u6c89\u7c97\u54d1\u4e9b\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u4ece\u6765\u4e5f\u6ca1\u6709\u542c\u8fc7\u8fd9\u4e9b\u4e8b\uff0c\u53ef\u662f\u5979\u6bcf\u4e2a\u5b57\u90fd\u76f8\u4fe1\u3002 \u3000\u3000\u56e0\u4e3a\u5979\u77e5\u9053\u845b\u75c5\u662f\u5929\u4e0b\u65e0\u53cc\u7684\u795e\u533b\uff0c\u5bf9\u4eba\u7c7b\u8eab\u4f53\u7684\u6784\u9020\uff0c\u5f53\u7136\u6bd4\u4efb\u4f55\u4eba\u61c2\u5f97\u7684\u90fd\u591a\u3002 \u3000\u3000\u5979\u4e5f\u542c\u8bf4\u8fc7\uff0c\u9b54\u6559\u4e2d\u6709\u79cd\u529f\u592b\uff0c\u53ef\u4ee5\u4f7f\u4e00\u4e2a\u4eba\u5589\u5499\u91cc\u8fd9\u6761\u5e26\u5b50\u6536\u7f29\uff0c\u58f0\u97f3\u6539\u53d8\u3002 \u3000\u3000\u845b\u75c5\u9053\uff1a\u201c\u6240\u4ee5\u4e00\u4e2a\u6b63\u5e38\u7684\u7537\u4eba\uff0c\u8bf4\u8bdd\u7684\u58f0\u97f3\u7edd\u4e0d\u4f1a\u592a\u5c16\u9510\uff0c\u9664\u975e\u2026\u2026\u201d \u3000\u3000\u4e01\u7075\u7433\u62a2\u7740\u9053\uff1a\u201c\u9664\u975e\u4ed6\u662f\u7528\u5047\u55d3\u5b50\u8bf4\u51fa\u6765\u7684\u3002\u201d \u3000\u3000\u845b\u75c5\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4f60\u518d\u60f3\u60f3\uff0c\u4ed6\u8bf4\u8bdd\u4e3a\u4ec0\u4e48\u8981\u7528\u5047\u55d3\u5b50\uff1f\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u56e0\u4e3a\u4ed6\u6015\u6211\u542c\u51fa\u4ed6\u7684\u58f0\u97f3\u6765\u3002\u201d \u3000\u3000\u845b\u75c5\u9053\uff1a\u201c\u56e0\u4e3a\u4f60\u4e00\u5b9a\u89c1\u8fc7\u4ed6\uff0c\u542c\u8fc7\u4ed6\u7684\u58f0\u97f3\u3002\u201d \u3000\u3000\u845b\u75c5\u53c8\u9053\uff1a\u201c\u90a3\u5929\u53bb\u8d3a\u559c\u7684\u90fd\u6709\u4e9b\u4ec0\u4e48\u4eba\uff1f\u5176\u4e2d\u53c8\u6709\u51e0\u4e2a\u662f\u4f60\u89c1\u8fc7\u7684\uff1f\u201d \u3000\u3000\u4e01\u7075\u7433\u4e0d\u77e5\u9053\uff1a\u201c\u6211\u6839\u672c\u6ca1\u6709\u673a\u4f1a\u770b\u3002\u201d\u5979\u54ac\u7740\u7259\u9053\uff1a\u201c\u6709\u673a\u4f1a\u770b\u89c1\u7684\u4eba\uff0c\u73b0\u5728\u5df2\u5168\u90fd \u88ab\u6740\u4e86\u706d\u53e3\u3002\u201d \u3000\u3000\u845b\u75c5\u4e5f\u4e0d\u7981\u63e1\u7d27\u4e86\u53cc\u62f3\u3002 \u3000\u3000\u9b54\u6559\u884c\u52a8\u7684\u8ba1\u5212\uff0c\u4e0d\u4f46\u5468\u5bc6\uff0c\u800c\u4e14\u72e0\u6bd2\u3002 \u3000\u3000\u201c\u4f46\u4ed6\u4eec\u8fd8\u662f\u7559\u4e0b\u4e86\u4e00\u6761\u7ebf\u7d22\u3002\u201d\u845b\u75c5\u6c89\u601d\u7740\u8bf4\u3002 \u3000\u3000\u201c\u4ec0\u4e48\u7ebf\u7d22\uff1f\u201d \u3000\u3000\u845b\u75c5\u9053\uff1a\u201c\u4e3b\u6301\u8fd9\u6b21\u884c\u52a8\u7684\u51f6\u624b\uff0c\u5f53\u65f6\u4e00\u5b9a\u5728\u90a3\u559c\u5802\u91cc\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u4e00\u5b9a\u5728\u3002\u201d \u3000\u3000\u845b\u75c5\u9053\uff1a\u201c\u5f53\u65f6\u5728\u559c\u5802\u4e2d\u7684\u4eba\uff0c\u73b0\u5728\u8fd8\u6d3b\u7740\u7684\u4e00\u5b9a\u5c31\u662f\u51f6\u624b\uff0c\u51f6\u624b\u5f88\u53ef\u80fd\u5c31\u662f\u56db\u5927\u5929 \u738b\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u773c\u775b\u91cc\u53d1\u51fa\u4e86\u5149\uff1a\u201c\u6240\u4ee5\u6211\u4eec\u53ea\u8981\u80fd\u67e5\u51fa\u5f53\u65f6\u5728\u559c\u5802\u4e2d\u6709\u4e9b\u4ec0\u4e48\u4eba\uff0c\u518d\u67e5\u51fa\u73b0\u5728 \u8fd8\u6709\u4e9b\u4ec0\u4e48\u4eba\u6d3b\u7740\uff0c\u5c31\u77e5\u9053\u56db\u5927\u5929\u738b\u7a76\u7adf\u662f\u8c01\u4e86\u3002\u201d \u3000\u3000\u845b\u75c5\u70b9\u4e86\u70b9\u5934\uff0c\u4ed6\u7684\u773c\u775b\u5e76\u6ca1\u6709\u53d1\u5149\uff0c\u56e0\u4e3a\u4ed6\u77e5\u9053\u8fd9\u4ef6\u4e8b\u8bf4\u6765\u867d\u7b80\u5355\uff0c\u8981\u53bb\u505a\u5374\u5f88\u4e0d\u5bb9 \u6613\u3002 \u3000\u3000\u201c\u53ea\u53ef\u60dc\u6211\u4eec\u73b0\u5728\u4e0d\u77e5\u9053\u5f53\u65f6\u5728\u90a3\u559c\u5802\u4e2d\u6709\u4e9b\u4ec0\u4e48\u4eba\u9001\u8fc7\u793c\uff1f\u6b7b\u7684\u53c8\u662f\u4e9b\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u6bcf\u4e2a\u6765\u9001\u793c\u7684\u4eba\uff0c\u6211\u4eec\u90fd\u5df2\u8bb0\u5728\u793c\u7c3f\u4e0a\u3002\u201d \u3000\u3000\u845b\u75c5\u7684\u773c\u775b\u4e5f\u4eae\u4e86\u3002 \u3000\u3000\u845b\u75c5\u7acb\u523b\u95ee\u9053\uff1a\u201c\u90a3\u793c\u7c3f\u5462\uff1f\u201d \u3000\u3000\u4e86\u7075\u7433\u9053\uff1a\u201c\u60f3\u5fc5\u8fd8\u5728\u9e3f\u5bbe\u5ba2\u6808\u7684\u5e10\u623f\u91cc\u3002\u201d \u3000\u3000\u845b\u75c5\u9053\uff1a\u201c\u73b0\u5728\u5929\u8fd8\u6ca1\u6709\u4eae\uff0c\u90a3\u4e9b\u6b7b\u5c38\u60f3\u5fc5\u4e5f\u8fd8\u5728\u559c\u5802\u91cc\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u79bb\u9e3f\u5bbe\u4e0d\u8fdc\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u8df3\u8d77\u6765\uff0c\u9053\uff1a\u201c\u90a3\u6211\u4eec\u8fd8\u7b49\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u845b\u75c5\u770b\u7740\u5979\uff0c\u76ee\u4e2d\u9732\u51fa\u5fe7\u8651\u4e4b\u8272\u3002\u5979\u53d7\u7684\u523a\u6fc0\u5df2\u5927\u591a\uff0c\u73b0\u5728\u82e5\u662f\u518d\u56de\u5230\u90a3\u559c\u5802\u91cc\uff0c\u518d\u770b \u89c1\u90a3\u4e9b\u9c9c\u8840\u548c\u5c38\u4f53\uff0c\u751a\u81f3\u5f88\u53ef\u80fd\u4f1a\u53d1\u75af\uff0c\u4ed6\u60f3\u8bf4\u670d\u5979\uff0c\u8981\u5979\u7559\u4e0b\u6765\uff0c\u53ef\u662f\u4ed6\u8fd8\u6ca1\u6709\u5f00\u53e3\uff0c\u4e01 \u7075\u7433\u5df2\u51b2\u51fa\u53bb\uff0c\u8fd9\u5973\u5b69\u5b50\u7adf\u6bd4\u4ed6\u60f3\u8c61\u4e2d\u575a\u5f3a\u5f97\u591a\u3002 \u3000\u3000\u559c\u5802\u4e2d\u6ca1\u6709\u4eba\u4e00\u4e00\u8fde\u6b7b\u4eba\u90fd\u6ca1\u6709\uff0c\u845b\u75c5\u7684\u62c5\u5fc3\uff0c\u7ade\u5b8c\u5168\u662f\u591a\u4f59\uff0c\u4ed6\u4eec\u5230\u4e86\u9e3f\u5bbe\u5ba2\u6808\uff0c\u7acb \u523b\u5c31\u53d1\u73b0\u6240\u6709\u7684\u5c38\u4f53\u90fd\u5df2\u88ab\u642c\u8d70\uff0c\u5e10\u623f\u91cc\u4e5f\u662f\u7a7a\u7684\uff0c\u6ca1\u6709\u4eba\uff0c\u66f4\u6ca1\u6709\u793c\u7c3f\uff0c\u6240\u6709\u7684\u793c\u7269\u4e5f\u5168 \u90e8\u88ab\u642c\u7a7a\u3002 \u3000\u3000\u4e01\u7075\u7433\u6014\u4f4f\uff0c\u73b0\u5728\u591c\u8fd8\u5f88\u6df1\uff0c\u5979\u79bb\u5f00\u8fd9\u91cc\u5e76\u6ca1\u6709\u591a\u4e45\uff0c\u9b54\u6559\u7684\u884c\u52a8\uff0c\u5b9e\u5728\u5feb\u5f97\u53ef\u6015\u3002 \u3000\u3000\u845b\u75c5\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u56db\u5927\u5929\u738b\u9001\u6765\u7684\u90a3\u888b\u73e0\u5b9d\uff0c\u672c\u6765\u662f\u4e0d\u662f\u4e5f\u5728\u8fd9\u5e10\u623f\u91cc\uff0c\u201d\u4e01\u7075\u7433\u70b9\u70b9 \u5934\u3002 \u3000\u3000\u845b\u75c5\u9053\uff1a\u201c\u90a3\u4e48\u8fd9\u4ef6\u4eab\u5c31\u4e00\u5b9a\u4e0d\u662f\u9b54\u6559\u4e2d\u4eba\u505a\u7684\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u845b\u75c5\u9053\uff1a\u201c\u56e0\u4e3a\u90a3\u888b\u73e0\u5b9d\u672c\u662f\u4ed6\u4eec\u7528\u6765\u4e70\u547d\u7684\uff0c\u73b0\u5728\u547d\u5df2\u88ab\u4ed6\u4eec\u4e70\u53bb\uff0c\u4ed6\u4eec\u5c31\u4e0d\u4f1a\u6536\u56de \u90a3\u4e9b\u73e0\u5b9d\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u6240\u4ee5\u5c38\u4f53\u4e5f\u4e0d\u662f\u4ed6\u4eec\u642c\u8d70\u7684\uff1f\u201d \u3000\u3000\u845b\u75c5\u9053\uff1a\u201c\u7edd\u4e0d\u662f\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u4e0d\u662f\u4ed6\u4eec\u662f\u8c01\uff1f\u9664\u4e86\u4ed6\u4eec\u5916\uff0c\u8fd8\u6709\u8c01\u4f1a\u6709\u8fd9\u4e48\u5feb\u7684\u624b\u811a\uff1f\u201d \u3000\u3000\u201c\u8981\u642c\u7a7a\u90a3\u5c38\u4f53\u548c\u793c\u7269\uff0c\u5e76\u4e0d\u662f\u4ef6\u5bb9\u6613\u4e8b\uff0c\u522b\u4eba\u8981\u90a3\u4e9b\u5c38\u4f53\uff0c\u4e5f\u5b8c\u5168\u6ca1\u6709\u7528\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u5b9e\u5728\u60f3\u4e0d\u901a\uff0c\u845b\u75c5\u4e5f\u60f3\u4e0d\u901a\u3002 \u3000\u3000\u98ce\u4ece\u7a97\u5916\u5439\u8fdb\u6765\uff0c\u5439\u5230\u5979\u8eab\u4e0a\uff0c\u5979\u5ffd\u7136\u6fc0\u7075\u7075\u6253\u4e86\u4e2a\u5bd2\u5664\uff0c\u98ce\u5439\u8fdb\u6765\u7684\u65f6\u5019\uff0c\u7ade\u8d6b\u7136\u53c8 \u6709\u4e00\u9635\u5b99\u58f0\u968f\u98ce\u4f20\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u7b1b\u58f0\u51c4\u51c9\u800c\u60b2\u54c0\uff0c\u4e01\u7075\u7433\u7acb\u523b\u53c8\u60f3\u8d77\u4e86\u90a3\u5439\u7b1b\u4eba\u82cd\u767d\u7684\u8138\u3002\u5979\u5fcd\u4e0d\u4f4f\u95ee\uff1a\u201c\u4f60\u521a\u624d\u6ca1\u6709 \u628a\u4ed6\u5e26\u8d70\uff1f\u201d \u3000\u3000\u845b\u75c5\u6447\u6447\u5934\u3002 \u3000\u3000\u201c\u4ed6\u4e3a\u4ec0\u4e48\u8fd8\u7559\u5728\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u201c\u4ed6\u53c8\u770b\u89c1\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u845b\u75c5\u548c\u4e01\u7075\u7433\u5df2\u540c\u65f6\u7a7f\u7a97\u800c\u51fa\uff0c\u4ed6\u4eec\u90fd\u77e5\u9053\uff0c\u80fd\u56de\u7b54\u8fd9\u95ee\u9898\u7684\u53ea\u6709\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u4ed6\u4eec\u4e00\u5b9a\u8981\u627e\u5230\u8fd9\u4e2a\u5439\u7b1b\u7684\u4eba\u3002"}),showOnHomepage:!0},{name:"\u7b2c\u4e8c\u5341\u4e09\u7ae0\u3000\u5439\u7b1b\u7684\u4eba",content:(0,s.jsx)(s.Fragment,{children:"\u6ca1\u6709\u4eba\u3002\u6b7b\u4eba\u6d3b\u4eba\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u6709\u7684\u706f\u706b\u5df2\u6b8b\uff0c\u6709\u7684\u706f\u5149\u5df2\u706d\uff0c\u51b7\u6e05\u6e05\u7684\u5ba2\u6808\uff0c\u51b7\u6e05\u6e05\u7684\u9662\u5b50\u3002 \u3000\u3000\u5c38\u4f53\u867d\u7136\u5df2\u88ab\u642c\u8d70\uff0c\u9662\u5b50\u8fd8\u662f\u5145\u6ee1\u4e86\u8840\u8165\u6c14\uff0c\u665a\u98ce\u66f4\u51b7\u5f97\u53ef\u4ee5\u4ee4\u4eba\u8840\u6db2\u51dd\u7ed3\u3002 \u3000\u3000\u90a3\u5439\u7b1b\u7684\u4eba\u5462\uff1f \u3000\u3000\u7f25\u7f25\u7f08\u7f08\u7684\u7b1b\u58f0\uff0c\u542c\u6765\u4eff\u4f5b\u5f88\u8fd1\uff0c\u53c8\u4eff\u4f5b\u5f88\u8fdc\u3002 \u3000\u3000\u4ed6\u4eec\u5728\u5c4b\u91cc\u65f6\uff0c\u7b1b\u58f0\u4eff\u4f5b\u5c31\u5728\u9662\u5b50\u91cc\uff0c\u4ed6\u4eec\u5230\u4e86\u9662\u5b50\u91cc\uff0c\u7b1b\u58f0\u5374\u53c8\u5728\u5899\u5916\u3002 \u3000\u3000\u5899\u5916\u7684\u591c\u8272\u6d53\u5982\u58a8\u3002 \u3000\u3000\u4ed6\u4eec\u63a0\u8fc7\u79ef\u96ea\u7684\u5899\u5934\uff0c\u65e0\u8fb9\u7684\u591c\u8272\u4e2d\uff0c\u53ea\u6709\u4e00\xb7\u76cf\u5b64\u706f\uff0c\u95ea\u70c1\u5982\u9b3c\u706b\u3002 \u3000\u3000\u706f\u4e0b\u4eff\u4f5b\u6709\u6761\u5e7d\u7075\u822c\u7684\u4eba\u5f71\uff0c\u4eff\u4f5b\u6b63\u5728\u5439\u7b1b\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u662f\u8c01\uff1f \u3000\u3000\u662f\u4e0d\u662f\u521a\u624d\u90a3\u4e2a\u5439\u7b1b\u4eba\uff1f \u3000\u3000\u4ed6\u4e3a\u4ec0\u4e48\u8981\u4e00\u4e2a\u4eba\u5728\u5b64\u706f\u4e0b\u5439\u7b1b\uff1f\u83ab\u975e\u662f\u7279\u5730\u5728\u7b49\u4ed6\u4eec\uff1f \u3000\u3000\u5982\u6b64\u9ed1\u591c\uff0c\u4ed6\u8fd8\u5b64\u96f6\u96f6\u5730\u7559\u5728\u8fd9\u91cc\u7b49\u4ed6\u4eec\uff0c\u662f\u4e3a\u4e86\u4ec0\u4e48\uff1f \u3000\u3000\u8fd9\u4e9b\u95ee\u9898\uff0c\u4e5f\u53ea\u6709\u4e00\u4e2a\u4eba\u80fd\u56de\u7b54\u3002 \u3000\u3000\u5b64\u706f\u60ac\u5728\u4e00\u6839\u67af\u679d\u4e0a\u968f\u98ce\u6447\u6643\u3002 \u3000\u3000\u4e01\u7075\u7433\u770b\u8fc7\u8fd9\u79cd\u706f\u7b3c\uff0c\u662f\u9e3f\u5bbe\u5ba2\u6808\u5728\u665a\u4e0a\u8fce\u5ba2\u7528\u7684\u706f\u7b3c\u3002 \u3000\u3000\u4f46\u5979\u5374\u770b\u4e0d\u6e05\u8fd9\u4e2a\u4eba\u3002 \u3000\u3000\u5979\u60f3\u51b2\u8fc7\u53bb\uff0c\u845b\u75c5\u5df2\u62c9\u4f4f\u4e86\u5979\uff0c\u5979\u53ef\u4ee5\u611f\u89c9\u5230\u8fd9\u8001\u4eba\u7684\u624b\u5fc3\u5168\u662f\u51b7\u6c57\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u5e74\u7eaa\u8d8a\u5927\uff0c\u8d8a\u63a5\u8fd1\u6b7b\u4ea1\u7684\u65f6\u5019\uff0c\u4e3a\u4ec0\u4e48\u53cd\u800c\u8d8a\u6015\u6b7b\uff1f \u3000\u3000\u4e01\u7075\u7433\u54ac\u7740\u5634\u5507\uff0c\u538b\u4f4e\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u4f60\u4e0d\u59a8\u5148\u56de\u5ba2\u6808\uff0c\u6211\u4e00\u4e2a\u4eba\u8fc7\u53bb\u770b\u770b\u3002\u201d \u3000\u3000\u845b\u75c5\u53f9\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u5979\u8bef\u4f1a\u4e86\u4ed6\u7684\u610f\u601d\uff0c\u4ed6\u5e76\u4e0d\u662f\u5728\u4e3a\u81ea\u5df1\u62c5\u5fc3\uff0c\u800c\u662f\u5728\u4e3a\u5979\u3002 \u3000\u3000\u201c\u6211\u5df2\u662f\u4e2a\u8001\u4eba\uff0c\u5df2\u6ca1\u6709\u4ec0\u4e48\u53ef\u6015\uff0c\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u4e01\u7075\u7433\u6253\u65ad\u4e86\u4ed6\u7684\u8bdd\uff0c\u9053\uff1a\u201c\u6211\u660e\u767d\u4f60\u7684\u610f\u601d\uff0c\u53ef\u662f\u6211\u4e00\u5b9a\u8981\u8fc7\u53bb\u770b\u770b\u3002\u201d \u3000\u3000\u7b1b\u58f0\u7a81\u7136\u505c\u987f\uff0c\u9ed1\u6697\u4e2d\u5ffd\u7136\u6709\u4eba\u51b7\u51b7\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u4eec\u4e00\u76f4\u5728\u627e\u6211\uff0c\u73b0\u5728\u4e3a\u4ec0\u4e48\u8fd8\u4e0d \u6765\uff1f\u201d \u3000\u3000\u58f0\u97f3\u5c16\u9510\uff0c\u6bd4\u5c16\u9488\u8fd8\u523a\u8033\u3002 \u3000\u3000\u4e01\u7075\u7433\u624b\u5fc3\u4e5f\u51fa\u4e86\u51b7\u6c57\u3002 \u3000\u3000\u5979\u542c\u8fc7\u8fd9\u58f0\u97f3\u3002 \u3000\u3000\u65e0\u8bba\u8c01\u542c\u8fc7\u8fd9\u58f0\u97f3\uff0c\u53ea\u8981\u542c\u8fc7\u4e00\u6b21\uff0c\u5c31\u6c38\u8fdc\u4e5f\u5fd8\u4e0d\u4e86\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u96be\u9053\u5c31\u662f\u9b54\u6559\u4e2d\u7684\u56db\u5927\u5929\u738b\u4e4b\u4e00\uff1f \u3000\u3000\u845b\u75c5\u8138\u8272\u5df2\u53d8\u4e86\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u4f60\u7a76\u7adf\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u5b64\u706f\u4e0b\u6709\u4eba\u5728\u51b7\u7b11\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u8fc7\u6765\u770b\u770b\u6211\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u4e01\u7075\u7433\u5f53\u7136\u8981\u8fc7\u53bb\u3002 \u3000\u3000\u5979\u7eb5\u7136\u660e\u77e5\u9053\u4e00\u8fc7\u53bb\u5c31\u5fc5\u6b7b\u65e0\u7591\uff0c\u4e5f\u975e\u8fc7\u53bb\u770b\u770b\u4e0d\u53ef\u3002 \u3000\u3000\u4f46\u845b\u75c5\u5374\u8fd8\u662f\u5728\u7d27\u7d27\u63e1\u7740\u5979\u7684\u624b\uff0c\u62a2\u7740\u9053\uff1a\u201c\u6211\u8fdf\u65e9\u603b\u4f1a\u77e5\u9053\u4f60\u662f\u8c01\u7684\uff0c\u6211\u5e76\u4e0d\u7740 \u6025\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u6211\u7740\u6025\u3002\u201d \u3000\u3000\u5979\u7a81\u7136\u56de\u8eab\u4e00\u649e\uff0c\u4e00\u4e2a\u65f6\u62f3\u6253\u5728\u845b\u75c5\u808b\u9aa8\u4e0a\uff0c\u5979\u4eba\u5df2\u51b2\u8fc7\u53bb\u3002 \u3000\u3000\u706f\u5149\u5374\u5ffd\u7136\u706d\u4e86\u3002 \u3000\u3000\u5bd2\u98ce\u5439\u8fc7\u5927\u5730\uff0c\u5927\u5730\u4e00\u7247\u9ed1\u6697\u3002 \u3000\u3000\u53ef\u662f\u4e01\u7075\u7433\u5df2\u51b2\u5230\u8fd9\u4e2a\u4eba\u9762\u524d\uff0c\u5df2\u770b\u6e05\u4e86\u8fd9\u4e2a\u4eba\u7684\u8138\uff0c\u4e00\u5f20\u82cd\u767d\u800c\u626d\u66f2\u7684\u8138\uff0c\u4e00\u53cc\u5145\u6ee1 \u4e86\u60ca\u5413\u6050\u60e7\u7684\u773c\u775b\uff0c\u773c\u775b\u5df2\u51f8\u51fa\uff0c\u6b63\u6b7b\u9c7c\u822c\u77aa\u7740\u4e01\u7075\u7433\u3002 \u3000\u3000\u4e01\u7075\u7433\u4e5f\u770b\u8fc7\u8fd9\u5f20\u8138\uff0c\u770b\u8fc7\u8fd9\u4e2a\u4eba\u3002 \u3000\u3000\u8fd9\u6b63\u662f\u90a3\u4e2a\u75f4\u75f4\u5730\u7ad9\u5728\u8840\u6cca\u4e2d\uff0c\u5df2\u88ab\u5413\u75af\u4e86\u7684\u5439\u7b1b\u4eba\uff1b\u4e5f\u6b63\u662f\u559c\u5802\u4e2d\u552f\u4e00\u8fd8\u6d3b\u7740\u7684\u4eba\u3002 \u3000\u3000\u96be\u9053\u4ed6\u5c31\u662f\u6740\u4eba\u7684\u51f6\u624b\uff1f \u3000\u3000\u4e01\u7075\u7433\u63e1\u7d27\u53cc\u62f3\uff0c\u5ffd\u7136\u53d1\u89c9\u4e00\u6ef4\u9c9c\u8840\u6b63\u6162\u6162\u4ece\u4ed6\u773c\u89d2\u6c81\u51fa\uff0c\u6d41\u8fc7\u4ed6\u82cd\u767d\u7684\u8138\u3002 \u3000\u3000\u5bd2\u98ce\u5439\u8fc7\uff0c\u5979\u5fcd\u4e0d\u4f4f\u53c8\u6fc0\u7075\u7075\u6253\u4e86\u4e2a\u5bd2\u5664\u3002 \u3000\u3000\u5979\u5ffd\u7136\u53d1\u73b0\u8fd9\u4e2a\u4eba\u7adf\u5df2\u662f\u4e2a\u6b7b\u4eba\u3002 \u3000\u3000\u6b7b\u4eba\u600e\u4e48\u4f1a\u8bf4\u8bdd\uff1f \u3000\u3000\u6b7b\u4eba\u600e\u4e48\u4f1a\u5439\u8200\uff1f \u3000\u3000\u6b7b\u4eba\u7edd\u4e0d\u4f1a\u8bf4\u8bdd\uff0c\u66f4\u4e0d\u4f1a\u5439\u7b1b\u3002 \u3000\u3000\u4ed6\u624b\u91cc\u6839\u672c\u6ca1\u6709\u7b1b\u3002 \u3000\u3000\u521a\u624d\u7684\u7b1b\u58f0\uff0c\u662f\u4ece\u54ea\u91cc\u53d1\u51fa\u6765\u7684\uff1f \u3000\u3000\u4e01\u7075\u7433\u4e00\u6b65\u6b65\u5411\u540e\u9000\uff0c\u521a\u9000\u51fa\u4e24\u6b65\uff0c\u7a81\u7136\u95f4\uff0c\u4e00\u53ea\u624b\u4f38\u51fa\u6765\uff0c\u95ea\u7535\u822c\u63e1\u4f4f\u4e86\u5979\u7684\u624b\u3002 \u3000\u3000\u51b0\u51b7\u7684\u624b\uff0c\u51b0\u51b7\u800c\u50f5\u786c\u3002 \u3000\u3000\u6b7b\u4eba\u600e\u4e48\u8fd8\u80fd\u51fa\u624b\uff1f \u3000\u3000\u4e01\u7075\u7433\u7684\u624b\u4e5f\u5df2\u51b0\u51b7\uff0c\u51e0\u4e4e\u53c8\u8981\u6655\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u5979\u6ca1\u6709\u6655\u8fc7\u53bb\uff0c\u56e0\u4e3a\u5979\u53d1\u73b0\u8fd9\u53ea\u624b\u662f\u4ece\u6b7b\u4eba\u8eab\u5b50\u540e\u9762\u4f38\u51fa\u6765\u7684\u3002 \u3000\u3000\u4f46\u8fd9\u53ea\u624b\u5b9e\u5728\u592a\u51b7\uff0c\u6bd4\u6b7b\u4eba\u7684\u624b\u8fd8\u51b7\u3002 \u3000\u3000\u4e0d\u4f46\u51b7\uff0c\u800c\u4e14\u786c\uff0c\u6bd4\u94c1\u8fd8\u786c\u3002 \u3000\u3000\u8fd9\u5b9e\u5728\u4e0d\u50cf\u662f\u6d3b\u4eba\u7684\u624b\uff0c\u4e01\u7075\u7433\u7528\u5c3d\u5168\u8eab\u529b\u6c14\uff0c\u4e5f\u6323\u4e0d\u8131\u3002 \u3000\u3000\u6b7b\u4eba\u8eab\u540e\u53c8\u4f20\u51fa\u4e86\u90a3\u6bd4\u9488\u5c16\u8fd8\u7ec6\u7684\u58f0\u97f3\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u771f\u7684\u60f3\u770b\u770b\u6211\u662f\u8c01\uff1f\u201d \u3000\u3000\u4e01\u7075\u7433\u7528\u529b\u54ac\u7740\u5634\u5507\uff0c\u5634\u5507\u5df2\u88ab\u54ac\u51fa\u8840\u6765\u3002 \u3000\u3000\u201c\u4f60\u82e5\u77e5\u9053\u6211\u662f\u8c01\uff0c\u4f60\u5c31\u5f97\u6b7b\u3002\u201d\u4ed6\u7684\u624b\u66f4\u7528\u529b\uff1a\u201c\u73b0\u5728\u4f60\u8fd8\u60f3\u4e0d\u60f3\u770b\u6211\uff1f\u201d \u3000\u3000\u4e01\u7075\u7433\u7a81\u7136\u7528\u529b\u70b9\u5934\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u82e5\u662f\u6d3b\u5230\u5979\u8fd9\u79cd\u60c5\u51b5\uff0c\u6b7b\u8fd8\u6709\u4ec0\u4e48\u53ef\u6015\u7684\uff1f \u3000\u3000\u5979\u76ef\u7740\u8fd9\u4e2a\u4eba\u7684\u624b\uff0c\u8fd9\u53ea\u624b\u5728\u9ed1\u6697\u4e2d\u770b\u6765\uff0c\u5c31\u50cf\u662f\u91d1\u5c5e\u822c\u53d1\u7740\u5149\u3002 \u3000\u3000\u4ed6\u7684\u8863\u8896\u662f\u85cf\u9752\u8272\u7684\uff0c\u4e0a\u9762\u7ee3\u7740\u9752\u8272\u7684\u5c71\u5cf0\u3002 \u3000\u3000\u201c\u5e03\u8fbe\u62c9\u201d\u5929\u738b\u3002 \u3000\u3000\u5b64\u5cf0\u3002 \u3000\u3000\u4e01\u7075\u7433\u7684\u5fc3\u4e5f\u5728\u53d1\u51b7\u3002 \u3000\u3000\u5979\u751a\u81f3\u5e0c\u671b\u81ea\u5df1\u9047\u7740\u7684\u662f\u9b3c\u3002 \u3000\u3000\u5728\u6c5f\u6e56\u4e2d\u4eba\u5fc3\u91cc\uff0c\u9b54\u6559\u4e2d\u7684\u56db\u5927\u5929\u738b\uff0c\u5b9e\u5728\u6bd4\u5389\u9b3c\u8fd8\u53ef\u6015\u3002 \u3000\u3000\u5979\u4e0d\u6015\u6b7b\u3002 \u3000\u3000\u53ef\u662f\u5979\u4e5f\u77e5\u9053\uff0c\u4e00\u4e2a\u4eba\u82e5\u662f\u843d\u5165\u9b54\u6559\u624b\u91cc\uff0c\u90a3\u906d\u9047\u4e5f\u4e00\u5b9a\u6bd4\u6b7b\u66f4\u53ef\u6015\u3002 \u3000\u3000\u5979\u4ece\u8fd9\u4e2a\u4eba\u7684\u624b\u770b\u5230\u8863\u8896\uff0c\u518d\u6162\u6162\u5730\u5f80\u4e0a\u770b\u2026\u2026\u5979\u7ec8\u4e8e\u770b\u5230\u4e86\u4ed6\u7684\u8138\u3002 \u3000\u3000\u4e00\u5f20\u6b7b\u4eba\u822c\u82cd\u767d\u51b7\u6f20\u7684\u8138\u3002 \u3000\u3000\u5728\u4e01\u7075\u7433\u773c\u4e2d\u770b\u6765\uff0c\u8fd9\u5f20\u8138\u5df2\u6bd4\u6b7b\u4eba\u66f4\u53ef\u6015\u3002\u5979\u7ec8\u4e8e\u5fcd\u4e0d\u4f4f\u5730\u5927\u53eb\uff1a\u201c\u662f\u4f60\uff1f\u201d \u3000\u3000\u201c\u4f60\u60f3\u4e0d\u5230\u662f\u6211\uff1f\u201d \u3000\u3000\u201c\u4f60\u2026\u2026\u4f60\u5c31\u662f\u5e03\u8fbe\u62c9\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u6211\u5c31\u662f\u5e03\u8fbe\u62c9\uff0c\u5c31\u662f\u5b64\u5cf0\u4e4b\u738b\uff0c\u9ad8\u4e0d\u53ef\u6500\uff0c\u5b64\u7acb\u4e91\u9704\u7684\u5c71\u5cf0\uff0c\u65e0\u8bba\u8c01\u770b\u5230\u4e86\u6211 \u7684\u771f\u9762\u76ee\uff0c\u90fd\u53ea\u6709\u4e24\u6761\u8def\u53ef\u8d70\u3002\u201d \u3000\u3000\u4e24\u6761\u8def\uff1f\u9664\u4e86\u6b7b\u8def\u5916\uff0c\u5c45\u7136\u8fd8\u6709\u6761\u522b\u7684\u8def\uff1f\u201c\u4f60\u5e76\u4e0d\u662f\u975e\u6b7b\u4e0d\u53ef\u7684\uff0c\u53ea\u8981\u4f60\u80af\u5165\u6211\u4eec\u7684 \u6559\uff0c\u5c31\u662f\u6211\u4eec\u7684\u4eba\uff0c\u5c31\u53ef\u4ee5\u6c38\u8fdc\u6d3b\u4e0b\u53bb\u3002\u201d \u3000\u3000\u201c\u6c38\u8fdc\u6d3b\u4e0b\u53bb\uff1f\u201d\u4e01\u7075\u7433\u7a81\u7136\u51b7\u7b11\uff1a\u201c\u6211\u81f3\u5c11\u5df2\u770b\u8fc7\u4e03\u516b\u4e2a\u4f60\u4eec\u9b54\u6559\u7684\u4eba\uff0c\u50cf\u91ce\u732b\u4e00\u6837 \u88ab\u4eba\u5272\u4e0b\u4e86\u8111\u888b\u3002\u201d \u3000\u3000\u201c\u4ed6\u4eec\u5c31\u7b97\u6b7b\uff0c\u4e5f\u6b7b\u5f97\u6109\u5feb\u3002\u201d \u3000\u3000\u201c\u6109\u5feb\uff1f\u6709\u4ec0\u4e48\u6109\u5feb\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u6740\u4ed6\u4eec\u7684\u4eba\uff0c\u90fd\u5df2\u4ed8\u51fa\u4ee3\u4ef7\u3002\u201d \u3000\u3000\u60f3\u5230\u559c\u5802\u4e2d\u7684\u8840\u6cca\u548c\u5c38\u4f53\uff0c\u4e01\u7075\u7433\u51e0\u4e4e\u5fcd\u4e0d\u4f4f\u8981\u5455\u5410\u3002 \u3000\u3000\u5b64\u5cf0\u5929\u738b\u9053\uff1a\u201c\u73b0\u5728\u4f60\u867d\u7136\u6d3b\u7740\uff0c\u4e5f\u662f\u751f\u4e0d\u5982\u6b7b\uff0c\u53ef\u662f\u53ea\u8981\u4f60\u80af\u5165\u6211\u4eec\u7684\u6559\uff0c\u65e0\u8bba\u4f60\u662f \u6b7b\u662f\u6d3b\uff0c\u90fd\u6ca1\u6709\u4eba\u6562\u6b3a\u8d1f\u4f60\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u53c8\u7528\u529b\u54ac\u4f4f\u4e86\u5634\u5507\uff0c\u8fd9\u53e5\u8bdd\u7684\u786e\u5df2\u6253\u52a8\u4e86\u5979\u3002 \u3000\u3000\u6700\u8fd1\u5979\u53d7\u5230\u59d4\u5c48\u5b9e\u5728\u5927\u591a\u3002 \u3000\u3000\u5b64\u5cf0\u5929\u738b\u770b\u7740\u5979\uff0c\u5140\u9e70\u822c\u7684\u773c\u775b\u91cc\uff0c\u5e26\u7740\u79cd\u8f7b\u8511\u7684\u8ba5\u8bee\u4e4b\u610f\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u5e76\u4e0d \u662f\u771f\u7684\u60f3\u6b7b\uff0c\u6ca1\u6709\u4eba\u771f\u7684\u60f3\u6b7b\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u5782\u4e0b\u4e86\u5934\u3002 \u3000\u3000\u5979\u8fd8\u5e74\u8f7b\uff0c\u8fd8\u6ca1\u6709\u771f\u6b63\u4eab\u53d7\u8fc7\u4eba\u751f\uff0c\u4e3a\u4ec0\u4e48\u4e00\u5b9a\u8981\u6b7b\uff1f \u3000\u3000\u4e00\u4e2a\u53d7\u5c3d\u4e86\u59d4\u5c48\u548c\u6298\u78e8\u7684\u5973\u5b69\u5b50\uff0c\u6709\u673a\u4f1a\u53bb\u6298\u78e8\u6298\u78e8\u522b\u4eba\uff0c\u5c82\u975e\u4e5f\u662f\u4ef6\u5f88\u6109\u5feb\u7684\u4e8b\u3002 \u3000\u3000\u8fd9\u8bf1\u60d1\u5b9e\u5728\u592a\u5927\u3002 \u3000\u3000\u80fd\u62d2\u7edd\u8fd9\u79cd\u8bf1\u60d1\u7684\u5973\u5b69\u5b50\uff0c\u4e16\u4e0a\u672c\u5c31\u4e0d\u591a\uff0c\u4f55\u51b5\u4e01\u7075\u7433\u672c\u662f\u4e2a\u4e89\u5f3a\u597d\u80dc\u7684\u4eba\u3002 \u3000\u3000\u5b64\u5cf0\u5929\u738b\u5f53\u7136\u77e5\u9053\u8fd9\u4e00\u70b9\uff0c\u6de1\u6de1\u9053\uff1a\u201c\u4f60\u4e0d\u59a8\u8003\u8651\u8003\u8651\uff0c\u53ea\u4e0d\u8fc7\u6211\u8fd8\u8981\u63d0\u9192\u4f60\u4e24\u4ef6 \u4e8b\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u5728\u542c\u7740\u3002 \u3000\u3000\u5b64\u5cf0\u5929\u738b\u9053\uff1a\u201c\u8981\u5165\u6211\u4eec\u7684\u6559\uff0c\u5e76\u4e0d\u662f\u4ef6\u5bb9\u6613\u7684\u4e8b\uff0c\u4f60\u80fd\u6709\u8fd9\u4e48\u6837\u4e00\u4e2a\u673a\u4f1a\uff0c\u5b9e\u5728\u662f\u4f60 \u7684\u8fd0\u6c14\u3002\u201d \u3000\u3000\u4ed6\u6162\u6162\u5730\u63a5\u7740\u9053\uff1a\u201c\u53ea\u56e0\u4e3a\u73b0\u5728\u6b63\u662f\u672c\u6559\u91cd\u5f00\u6559\u95e8\uff0c\u53e6\u7acb\u6559\u5b97\u7684\u65f6\u5019\uff0c\u4f60\u9519\u8fc7\u8fd9\u6b21\u673a \u4f1a\uff0c\u4e00\u5b9a\u4f1a\u540e\u6094\u7ec8\u751f\u7684\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u8981\u6211\u62dc\u5728\u4f60\u7684\u95e8\u4e0b\uff1f\u201d \u3000\u3000\u5b64\u5cf0\u5929\u738b\u505a\u7136\u9053\uff1a\u201c\u80fd\u62dc\u5728\u6211\u7684\u95e8\u4e0b\uff0c\u4e5f\u662f\u4f60\u7684\u8fd0\u6c14\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff0c\u201c\u6211\u662f\u4e0d\u662f\u5bf9\u4f60\u6709\u7528\uff1f\u201d \u3000\u3000\u5b64\u5cf0\u5929\u738b\u6ca1\u6709\u5426\u8ba4\u3002 \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u6211\u5bf9\u4f60\u6709\u4ec0\u4e48\u7528\uff1f\u201d \u3000\u3000\u5b64\u5cf0\u5929\u738b\u9053\uff1a\u4ee5\u540e\u4f60\u81ea\u7136\u4f1a\u77e5\u9053\u7684\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u73b0\u5728\u2026\u2026\u201d \u3000\u3000\u5b64\u5cf0\u5929\u738b\u6253\u65ad\u4e86\u5979\u7684\u8bdd\uff1a\u201c\u4f60\u5bf9\u6211\u6709\u7528\uff0c\u6211\u5bf9\u4f60\u66f4\u6709\u7528\uff0c\u4eba\u4e0e\u4eba\u4e4b\u95f4\uff0c\u672c\u5c31\u662f\u4e92\u76f8\u5229 \u7528\uff0c\u4f60\u6709\u80fd\u591f\u88ab\u4eba\u5229\u7528\u7684\u4ef7\u503c\uff0c\u6240\u4ee5\u624d\u80fd\u6d3b\u4e0b\u53bb\u3002\u53e6\u5916\u6211\u8fd8\u8981\u63d0\u9192\u4f60\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u8fdf\u7591\u7740\uff0c\u9053\uff1a\u201c\u4f60\u8bf4\u4f60\u8fd8\u8981\u63d0\u9192\u6211\u4e00\u4ef6\u4e8b\uff1f\u201d \u3000\u3000\u5b64\u5cf0\u5929\u738b\u9053\uff1a\u201c\u4f60\u4e0d\u5fc5\u7b49\u845b\u75c5\u6765\u6551\u4f60\uff0c\u4ed6\u7edd\u4e0d\u4f1a\u6551\u4f60\u7684\uff0c\u4ed6\u4e5f\u4e0d\u6562\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u53c8\u5fcd\u4e0d\u4f4f\u95ee\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5b64\u5cf0\u5929\u738b\u9053\uff1a\u201c\u56e0\u4e3a\u4ed6\u4e5f\u662f\u672c\u6559\u4e2d\u7684\u5f1f\u5b50\uff0c\u591a\u5e74\u524d\u5c31\u5df2\u5165\u6559\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u6014\u4f4f\u3002 \u3000\u3000\u5b64\u5cf0\u5929\u738b\u9053\uff1a\u201c\u4f60\u4e0d\u4fe1\uff1f\u201d \u3000\u3000\u4e01\u7075\u7433\u5b9e\u5728\u4e0d\u4fe1\u3002 \u3000\u3000\u5979\u8ba4\u5f97\u845b\u75c5\u867d\u4e0d\u4e45\uff0c\u53ef\u662f\u5979\u5bf9\u8fd9\u4e2a\u4eba\u4e00\u5411\u90fd\u5f88\u5c0a\u656c\u3002 \u3000\u3000\u56e0\u4e3a\u5979\u77e5\u9053\u845b\u75c5\u662f\u53f6\u5f00\u7684\u670b\u53cb\uff0c\u662f\u4e2a\u6781\u5b64\u9ad8\u3001\u6781\u6709\u624d\u80fd\u7684\u4eba\u3002 \u3000\u3000\u5979\u7edd\u4e0d\u76f8\u4fe1\u53f6\u5f00\u7684\u670b\u53cb\uff0c\u4f1a\u662f\u4e2a\u8138\u4e0a\u4e00\u76f4\u6234\u7740\u4f2a\u5584\u9762\u5177\u7684\u5351\u9119\u5c0f\u4eba\u3002 \u3000\u3000\u53ef\u662f\u845b\u75c5\u5df2\u8d70\u8fc7\u6765\u3002\u5782\u7740\u624b\uff0c\u7ad9\u5728\u5b64\u5cf0\u5929\u738b\u8eab\u65c1\uff0c\u5c31\u50cf\u662f\u5974\u624d\u7ad9\u5728\u4e3b\u4eba\u8eab\u65c1\u4e00\u6837\u3002 \u3000\u3000\u4e01\u7075\u7433\u7684\u5fc3\u6c89\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u5b64\u5cf0\u5929\u738b\u51b7\u51b7\u9053\uff1a\u201c\u73b0\u5728\u4f60\u4fe1\u4e0d\u4fe1\uff1f\u201d \u3000\u3000\u4e01\u7075\u7433\u867d\u7136\u5df2\u4e0d\u80fd\u4e0d\u4fe1\uff0c\u5374\u8fd8\u662f\u5fcd\u4e0d\u4f4f\u8981\u95ee\u845b\u75c5\uff1a\u201c\u4f60\u771f\u7684\u662f\u9b54\u6559\u95e8\u4e0b\uff1f\u201d \u3000\u3000\u845b\u75c5\u5c45\u7136\u627f\u8ba4\u3002 \u3000\u3000\u4e01\u7075\u7433\u63e1\u7d27\u53cc\u62f3\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u6211\u8fd8\u4ee5\u4e3a\u4f60\u4e00\u76f4\u90fd\u5728\u5173\u5fc3\u6211\uff0c\u5e2e\u7740\u6211\uff0c\u6211\u8fd8\u4ee5\u4e3a\u4f60\u662f\u6211\u7684 \u670b\u53cb\uff0c\u60f3\u4e0d\u5230\u4f60\u7adf\u662f\u8fd9\u79cd\u65e0\u803b\u7684\u5c0f\u4eba\u3002\u201d \u3000\u3000\u845b\u75c5\u7684\u8138\u4e0a\u5168\u65e0\u8868\u60c5\uff0c\u5c31\u50cf\u662f\u5df2\u53d8\u6210\u4e86\u4e2a\u804b\u5b50\u3002 \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u6211\u4e00\u76f4\u90fd\u5f88\u5c0a\u656c\u4f60\uff0c\u4e0d\u4f46\u5c0a\u656c\u4f60\u7684\u533b\u9053\uff0c\u4e5f\u5c0a\u656c\u4f60\u662f\u4e2a\u541b\u5b50\uff0c \u4f60\u4e3a\u4ec0\u4e48\u8981\u81ea\u7518\u5815\u843d\u5462\uff1f\u201d \u3000\u3000\u5b64\u5cf0\u5929\u738b\u9053\uff1a\u201c\u52a0\u5165\u672c\u6559\uff0c\u672c\u4e0d\u662f\u81ea\u7518\u5815\u843d\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u957f\u957f\u5410\u51fa\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u597d\uff0c\u5f88\u597d\uff0c\u4f60\u8d76\u5feb\u6740\u4e86\u6211\u5427\u3002\u201d \u3000\u3000\u5b64\u5cf0\u5929\u738b\u9053\uff1a\u201c\u4f60\u5df1\u51b3\u5b9a\uff1f\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u5b64\u5cf0\u5929\u738b\u9053\uff1a\u201c\u4f60\u5b81\u613f\u6b7b\uff1f\u201d \u3000\u3000\u4e86\u7075\u7433\u9053\uff1a\u201c\u662f\u7684\u3002\u201d \u3000\u3000\u5b64\u5cf0\u5929\u738b\u4e5f\u4e0d\u7981\u663e\u5f97\u5f88\u60ca\u8bb6\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e01\u7075\u7433\u53c8\u53eb\u4e86\u8d77\u6765\uff1a\u201c\u56e0\u4e3a\u6211\u73b0\u5728\u5df2\u77e5\u9053\uff0c\u65e0\u8bba\u8c01\u53ea\u8981\u4e00\u4eba\u4e86\u4f60\u4eec\u7684\u6559\uff0c\u90fd\u4f1a\u53d8\u6210\u4e2a\u89c1 \u4e0d\u5f97\u4eba\u7684\u5351\u9119\u5c0f\u4eba\u3002\u201d \u3000\u3000\u5b64\u5cf0\u5929\u738b\u7684\u77b3\u5b54\u5728\u6536\u7f29\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u4f60\u4e0d\u60f3\u518d\u8003\u8651\u8003\u8651\uff1f\u201d \u3000\u3000\u4e01\u7075\u7433\u65ad\u7136\u9053\uff1a\u201c\u6211\u5df2\u4e0d\u5fc5\u518d\u8003\u8651\u3002\u201d \u3000\u3000\u5b64\u5cf0\u5929\u738b\u770b\u7740\u5979\uff0c\u5ffd\u7136\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u845b\u75c5\u3002\u201d \u3000\u3000\u845b\u75c5\u9053\uff1a\u201c\u5728\u3002\u201d \u3000\u3000\u5b64\u5cf0\u5929\u738b\u9053\uff1a\u201c\u5979\u8fd9\u6761\u547d\uff0c\u597d\u50cf\u662f\u4f60\u521a\u6551\u56de\u6765\u7684\u3002\u201d \u3000\u3000\u845b\u75c5\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u5b64\u5cf0\u5929\u738b\u9053\uff1a\u201c\u73b0\u5728\u4f60\u4e0d\u59a8\u518d\u628a\u5979\u8fd9\u6761\u547d\u62ff\u8d70\u3002\u201d \u3000\u3000\u845b\u75c5\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u4ed6\u6162\u6162\u5730\u653e\u4e0b\u4e07\u5b9d\u7bb1\uff0c\u53f3\u624b\u7684\u4e7e\u5764\u4f1e\uff0c\u5df2\u5411\u4e01\u7075\u7433\u7709\u5fc3\u70b9\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u4e07\u5b9d\u7bb1\u662f\u6551\u4eba\u7684\uff0c\u4e7e\u5764\u4f1e\u5374\u662f\u6740\u4eba\u7684\u3002 \u3000\u3000\u4ed6\u6740\u4eba\u7684\u52a8\u4f5c\u5feb\u800c\u51c6\u786e\uff0c\u5b8c\u5168\u4e0d\u50cf\u662f\u4e2a\u8001\u4eba\u51fa\u624b\uff0c\u4ed6\u6bd4\u5927\u591a\u6570\u4eba\u90fd\u4e86\u89e3\uff0c\u4e00\u4e2a\u4eba\u8eab\u4e0a\u6709 \u4e9b\u4ec0\u4e48\u5730\u65b9\u662f\u771f\u6b63\u81f4\u547d\u7684\u8981\u5bb3\u3002 \u3000\u3000\u7709\u5fc3\u4e4b\u95f4\u5c31\u662f\u771f\u6b63\u81f4\u547d\u7684\u8981\u5bb3\u3002 \u3000\u3000\u6ca1\u6709\u4eba\u80fd\u53d7\u5f97\u4e86\u4ed6\u8fd9\u4e00\u51fb\uff0c\u53ef\u662f\u4e01\u7075\u7433\u6ca1\u6709\u95ea\u907f\uff0c\u53cd\u800c\u51b7\u7b11\u7740\u8fce\u4e86\u4e0a\u53bb\uff0c\u5979\u77e5\u9053\u5df2\u65e0\u6cd5 \u95ea\u907f\u3002 \u3000\u3000\u5979\u7684\u624b\u8155\u8fd8\u88ab\u63e1\u5728\u5b64\u5cf0\u5929\u738b\u94a2\u94c1\u822c\u7684\u624b\u91cc\u3002 \u3000\u3000\u4e7e\u5764\u4f1e\u7684\u94c1\u5c16\uff0c\u5df2\u95ea\u7535\u822c\u5230\u4e86\u5979\u773c\u524d\uff0c\u5979\u770b\u89c1\u5bd2\u5149\u5728\u95ea\u52a8\uff0c\u5ffd\u7136\u53c8\u542c\u89c1\u201c\u5d29\u201d\u7684\u4e00\u58f0\u8f7b \u54cd\uff0c\u5c31\u4eff\u4f5b\u6709\u4e24\u6839\u94a2\u9488\u649e\u51fb\u3002 \u3000\u3000\u63a5\u4e0b\u53bb\u7684\u4e8b\uff0c\u5c31\u5feb\u5f97\u4f7f\u5979\u8fde\u770b\u90fd\u770b\u4e0d\u6e05\u3002 \u3000\u3000\u5979\u53ea\u611f\u89c9\u5230\u5b64\u5cf0\u5929\u738b\u7684\u624b\u7a81\u7136\u677e\u5f00\uff0c\u7a81\u7136\u51cc\u7a7a\u8dc3\u8d77\u7ffb\u8eab\uff0c\u5979\u8fd8\u4eff\u4f5b\u770b\u89c1\u5b64\u5cf0\u5929\u738b\u8eab\u5b50\u8dc3 \u8d77\u65f6\uff0c\u4f38\u624b\u5728\u845b\u75c5\u80cc\u4e0a\u4e00\u62cd\uff0c\u8fd9\u4e00\u62db\u5feb\u5982\u95ea\u7535\uff0c\u5979\u5b9e\u5728\u4e5f\u6ca1\u6709\u770b\u6e05\u695a\u3002 \u3000\u3000\u5979\u552f\u4e00\u770b\u6e05\u695a\u7684\u4e8b\uff0c\u662f\u5b64\u5cf0\u5929\u738b\u5df2\u8d70\u4e86\uff0c\u845b\u75c5\u5df2\u5012\u4e86\u4e0b\u53bb\uff0c\u4f46\u5979\u5374\u8fd8\u662f\u597d\u597d\u5730\u7ad9\u5728\u90a3 \u91cc\u3002 \u3000\u3000\u5979\u5b9e\u5728\u4e0d\u61c2\u8fd9\u7a76\u7adf\u662f\u600e\u4e48\u56de\u4e8b\uff1f \u3000\u3000\u591c\u8272\u66f4\u6df1\uff0c\u98ce\u66f4\u51b7\uff0c\u90a3\u7834\u65e7\u7684\u706f\u7b3c\uff0c\u8fd8\u5728\u67af\u679d\u4e0a\u6447\u6643\uff0c\u5439\u7b1b\u4eba\u7684\u5c38\u8eab\u8fd8\u5728\u67af\u679d\u4e0a\u6447\u6643\u3002 \u3000\u3000\u5b64\u5cf0\u5929\u738b\u5374\u5df2\u6d88\u5931\u5728\u9ed1\u6697\u4e2d\u3002 \u3000\u3000\u845b\u75c5\u6b63\u4f0f\u5728\u5730\u4e0a\uff0c\u4e0d\u505c\u5730\u54b3\u55fd\uff0c\u6bcf\u54b3\u4e00\u58f0\uff0c\u5c31\u6709\u4e00\u80a1\u9c9c\u8840\u6e85\u51fa\u3002 \u3000\u3000\u98ce\u5439\u8fc7\u4ed6\u80cc\u4e0a\u65f6\uff0c\u4ed6\u80cc\u4e0a\u7684\u8863\u670d\u7a81\u7136\u6709\u4e00\u7247\u88ab\u98ce\u5439\u6210\u4e86\u7070\uff0c\u9732\u51fa\u4e86\u4e00\u4e2a\u638c\u5370\u3002 \u3000\u3000\u9c9c\u7ea2\u7684\u638c\u5370\u3002 \u3000\u3000\u4e86\u7075\u7433\u4ece\u6765\u4e5f\u6ca1\u770b\u89c1\u8fd9\u4e48\u53ef\u6015\u7684\u638c\u529b\uff0c\u4f46\u5374\u5df2\u603b\u7b97\u660e\u767d\u4e86\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\u3002 \u3000\u3000\u5979\u8fd8\u6d3b\u7740\uff0c\u8fd8\u80fd\u597d\u597d\u7684\u7ad9\u5728\u8fd9\u91cc\uff0c\u53ea\u56e0\u4e3a\u845b\u75c5\u975e\u4f46\u6ca1\u6709\u6740\u5979\uff0c\u53cd\u800c\u6551\u4e86\u5979\u3002 \u3000\u3000\u4ed6\u5192\u7740\u751f\u547d\u7684\u5371\u9669\u6551\u4e86\u5979\uff0c\u800c\u845b\u75c5\u81ea\u5df1\u73b0\u5728\u5374\u5df2\u547d\u5982\u6e38\u4e1d\uff0c\u8fd9\u79cd\u6551\u547d\u7684\u6069\u60c5\uff0c\u4e5f\u50cf\u662f\u4e00 \u6839\u9488\uff0c\u5ffd\u7136\u523a\u75db\u4e86\u5979\u7684\u5fc3\u3002 \u3000\u3000\u65e0\u8bba\u662f\u60b2\u4f24\u4e5f\u597d\uff0c\u662f\u611f\u6fc0\u4e5f\u597d\uff0c\u4e00\u79cd\u611f\u60c5\u53ea\u8981\u592a\u5f3a\u70c8\uff0c\u5c31\u4f1a\u53d8\u5f97\u50cf\u5c16\u9488\u822c\u523a\u4eba\u3002 \u3000\u3000\u5979\u8e72\u4e0b\u6765\uff0c\u62b1\u4f4f\u4e86\u845b\u75c5\u3002 \u3000\u3000\u5979\u7684\u5fc3\u5728\u523a\u75db\uff0c\u80c3\u5728\u6536\u7f29\uff0c\u4f46\u5374\u5b8c\u5168\u4e0d\u77e5\u9053\u5e94\u8be5\u600e\u4e48\u5e2e\u52a9\u8fd9\u4e2a\u6551\u547d\u7684\u6069\u4eba\u3002 \u3000\u3000\u5979\u7684\u773c\u6cea\u5df2\u6ef4\u5728\u4ed6\u8eab\u4e0a\u3002 \u3000\u3000\u845b\u75c5\u5598\u606f\u7740\uff0c\u603b\u7b97\u5fcd\u4f4f\u4e86\u54b3\u55fd\uff0c\u5ffd\u7136\u9053\uff1a\u201c\u5feb\u2026\u2026\u5feb\u6253\u5f00\u6211\u7684\u7bb1\u5b50\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u7acb\u523b\u6293\u8d77\u4e86\u7bb1\u5b50\uff0c\u6253\u5f00\u3002 \u3000\u3000\u845b\u75c5\u9053\uff1a\u201c\u91cc\u9762\u662f\u4e0d\u662f\u6709\u4e2a\u9ed1\u8272\u7684\u6728\u74f6\uff1f\u201d \u3000\u3000\u91cc\u9762\u662f\u6709\u7684\u3002 \u3000\u3000\u4e01\u7075\u7433\u521a\u627e\u51fa\u6765\uff0c\u845b\u75c5\u5c31\u62a2\u8fc7\u53bb\uff0c\u54ac\u65ad\u74f6\u9888\uff0c\u628a\u4e00\u74f6\u836f\u5168\u90fd\u5012\u5728\u5634\u91cc\u3002 \u3000\u3000\u7136\u540e\u4ed6\u7684\u5598\u606f\u624d\u6e10\u6e10\u5e73\u606f\u3002 \u3000\u3000\u4e01\u7075\u7433\u4e5f\u677e\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u201c\u4e07\u5b9d\u7bb1\uff0c\u4e7e\u5764\u4f1e\uff0c\u960e\u738b\u6ca1\u6cd5\u7ba1\u3002\u201d\u8fde\u960e\u738b\u90fd\u6ca1\u6cd5\u7ba1\u7684\u4eba\uff0c\u5f53\u7136\u4e0b\u4f1a\u6b7b\u3001\u4ed6\u65e2\u7136\u80fd\u6551\u522b \u4eba\u7684\u547d\uff0c\u5f53\u7136\u4e5f\u80fd\u6551\u81ea\u5df1\u3002 \u3000\u3000\u53ef\u662f\u845b\u75c5\u7684\u8138\u8272\u8fd8\u662f\u90a3\u4e48\u53ef\u6015\uff0c\u8fde\u773c\u775b\u91cc\u7684\u795e\u91c7\u90fd\u5df2\u6d88\u5931\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u7684\u8138\u8272\u7edd\u4e0d\u6bd4\u90a3\u5439\u7b1b\u4eba\u7684\u8138\u8272\u597d\u770b\u591a\u5c11\u3002 \u3000\u3000\u4e01\u7075\u7433\u53c8\u4e0d\u7981\u4e3a\u4ed6\u5fe7\u8651\uff1a\u201c\u6211\u6276\u4f60\u56de\u5ba2\u6808\u53bb\u597d\u4e0d\u597d\uff1f\u201d \u3000\u3000\u845b\u75c5\u70b9\u70b9\u5934\uff0c\u521a\u7ad9\u8d77\u6765\uff0c\u53c8\u8dcc\u5012\uff0c\u53c8\u662f\u4e00\u53e3\u9c9c\u8840\u545b\u4e86\u51fa\u6765\u3002 \u3000\u3000\u4e01\u7075\u7433\u54ac\u7d27\u7259\uff0c\u6068\u6068\u9053\uff1a\u201c\u4ed6\u4e3a\u4ec0\u4e48\u8981\u5982\u6b64\u72e0\u5fc3\uff0c\u4e3a\u4ec0\u4e48\u8981\u4e0b\u8fd9\u79cd\u6bd2\u624b\uff1f\u201d \u3000\u3000\u845b\u75c5\u5ffd\u7136\u52c9\u5f3a\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u5bf9\u4ed6\u4e5f\u4e0b\u4e86\u6bd2\u624b\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u4e0d\u61c2\uff0c\u4ed6\u6839\u672c\u6ca1\u6709\u770b\u89c1\u845b\u75c5\u5411\u5b64\u5cf0\u5929\u738b\u51fa\u624b\u3002 \u3000\u3000\u845b\u75c5\u9053\uff1a\u201c\u4f60\u770b\u770b\u6211\u7684\u4f1e\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u770b\u89c1\u4e86\u3002 \u3000\u3000\u845b\u75c5\u9053\uff1a\u201c\u4f60\u770b\u770b\u4f1e\u67c4\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u8fd9\u624d\u53d1\u73b0\uff0c\u4f1e\u67c4\u662f\u7a7a\u7684\uff0c\u9876\u7aef\u8fd8\u6709\u4e2a\u5c16\u9488\u822c\u5927\u7684\u6d1e\u3002 \u3000\u3000\u5979\u7ec8\u4e8e\u660e\u767d\uff1a\u201c\u8fd9\u91cc\u9762\u85cf\u7740\u6697\u5668\uff1f\u201d \u3000\u3000\u845b\u75c5\u5728\u7b11\uff0c\u75db\u82e6\u5374\u4f7f\u5f97\u4ed6\u7684\u7b11\u770b\u6765\u6bd4\u54ed\u8fd8\u4ee4\u4eba\u60b2\u4f24\uff1a\u201c\u8fd9\u91cc\u4e0d\u4f46\u6709\u6697\u5668\uff0c\u800c\u4e14\u662f\u5f88\u6bd2\u7684 \u6697\u5668\u3002\u201d \u3000\u3000\u4ed6\u7684\u4e7e\u5764\u4f1e\uff0c\u672c\u5c31\u662f\u6740\u4eba\u7684\u3002 \u3000\u3000\u201c\u6211\u5bf9\u4f60\u51fa\u624b\u65f6\uff0c\u4f1e\u67c4\u6b63\u5bf9\u7740\u4ed6\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u5b8c\u5168\u660e\u767d\uff1a\u201c\u4f60\u7528\u4f1e\u5c16\u523a\u6211\u65f6\uff0c\u4f1e\u67c4\u91cc\u7684\u6697\u5668\u5c31\u5c04\u4e86\u51fa\u6765\u3002\u201d \u3000\u3000\u845b\u75c5\u70b9\u70b9\u5934\uff0c\u4eff\u4f5b\u60f3\u5927\u7b11\uff1a\u201c\u4ed6\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u6211\u4f1a\u5bf9\u4ed6\u51fa\u624b\u7684\uff0c\u4ed6\u6bd5\u7adf\u8fd8\u662f\u4e0a\u4e86\u6211\u7684 \u5f53\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u773c\u775b\u4eae\u4e86\uff1a\u201c\u4ed6\u5df2\u4e2d\u4e86\u4f60\u7684\u6697\u5668\uff1f\u201d \u3000\u3000\u845b\u75c5\u53c8\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6240\u4ee5\u4ed6\u7684\u638c\u529b\u867d\u53ef\u6015\uff0c\u6211\u4eec\u4e5f\u4e0d\u5fc5\u6015\u4ed6\u4e86\u3002\u201d \u3000\u3000\u559c\u5802\u91cc\u706f\u5149\u9634\u68ee\u800c\u9eef\u6de1\uff0c\u53ef\u662f\u9e3f\u5bbe\u5ba2\u6808\u91cc\uff0c\u5df2\u53ea\u5269\u4e0b\u8fd9\u5730\u65b9\u8fd8\u6709\u706f\u5149\u3002 \u3000\u3000\u6240\u4ee5\u4e01\u7075\u7433\u53ea\u6709\u628a\u845b\u75c5\u5e26\u5230\u8fd9\u91cc\u6765\uff0c\u8fd9\u91cc\u867d\u6ca1\u6709\u5e8a\uff0c\u5374\u6709\u684c\u5b50\u3002 \u3000\u3000\u5730\u4e0a\u7684\u8840\u6e0d\u5df2\u4e8e\u4e86\uff0c\u5979\u4ece\u5e10\u623f\u91cc\u627e\u6765\u51e0\u6761\u68c9\u88ab\uff0c\u57ab\u5728\u845b\u75c5\u8eab\u4e0b\u3002 \u3000\u3000\u4ed6\u7684\u8138\u8272\u8fd8\u662f\u5f88\u53ef\u6015\uff0c\u53ea\u8981\u4e00\u54b3\u55fd\uff0c\u5634\u89d2\u8fd8\u662f\u6709\u8840\u4e1d\u6c81\u51fa\u3002 \u3000\u3000\u5e78\u597d\u4ed6\u8fd8\u6709\u4e2a\u6551\u547d\u7684\u4e07\u5b9d\u7bb1 \u3000\u3000\u4e01\u7075\u7433\u770b\u7740\u4ed6\u8138\u4e0a\u7684\u75db\u82e6\u8868\u60c5\uff0c\u5fcd\u4e0d\u4f4f\u95ee\uff1a\u201c\u7bb1\u5b50\u91cc\u8fd8\u6709\u6ca1\u6709\u522b\u7684\u836f\u53ef\u4ee5\u8ba9\u4f60\u5403\u4e86\u8212\u670d \u4e9b\uff1f\u201d \u3000\u3000\u845b\u75c5\u6447\u6447\u5934\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u6551\u547d\u7684\u836f\u6709\u5f88\u591a\u79cd\uff0c\u53ef\u662f\u771f\u6b63\u80fd\u6551\u547d\u7684\u836f\uff0c\u901a\u5e38\u5374\u53ea\u6709\u4e00 \u79cd\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u4e5f\u52c9\u5f3a\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u7ba1\u600e\u4e48\u6837\uff0c\u4f60\u603b\u7b97\u5df1\u6551\u4e86\u4f60\u81ea\u5df1\u7684\u547d\u3002\u201d \u3000\u3000\u845b\u75c5\u770b\u4e86\u5979\u4e00\u773c\uff0c\u6162\u6162\u5730\u95ed\u4e0a\u773c\u775b\uff0c\u4eff\u4f5b\u60f3\u8bf4\u4ec0\u4e48\uff0c\u5374\u6ca1\u6709\u8bf4\u51fa\u6765\u3002 \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u4e00\u5b9a\u5f88\u5feb\u5c31\u4f1a\u597d\u7684\uff0c\u56e0\u4e3a\u4f60\u5b9e\u5728\u662f\u4e2a\u597d\u4eba\u3002\u201d \u3000\u3000\u845b\u75c5\u53c8\u7b11\u4e86\u3002 \u3000\u3000\u4e01\u7075\u7433\u5374\u60c5\u613f\u4ed6\u4e0d\u8981\u7b11\uff0c\u4ed6\u7684\u7b11\u5bb9\u8fde\u770b\u7684\u4eba\u90fd\u89c9\u5f97\u75db\u82e6\u3002 \u3000\u3000\u51b7\u98ce\u5982\u5200\u3002 \u3000\u3000\u4e01\u7075\u7433\u5df2\u5c06\u95e8\u7a97\u5168\u90fd\u5173\u4e86\u8d77\u6765\uff0c\u5200\u950b\u822c\u7684\u51b7\u98ce\uff0c\u5374\u8fd8\u662f\u4e00\u9635\u9635\u4ece\u95e8\u7f1d\u7a97\u91cc\u523a\u8fdb\u6765\u3002 \u3000\u3000\u5979\u5ffd\u7136\u9053\uff1a\u201c\u4f60\u77e5\u9053\u6211\u5728\u60f3\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u4f60\u60f3\u559d\u9152\uff1f\u201d \u3000\u3000\u4e01\u7075\u7433\u7b11\u4e86\uff0c\u8fd9\u6b21\u662f\u771f\u7684\u7b11\u4e86\uff0c\u56e0\u4e3a\u5979\u5df2\u770b\u89c1\u5c4b\u89d2\u91cc\u6446\u7740\u51e0\u575b\u9152\u3002 \u3000\u3000\u5979\u642c\u6765\u4e00\u575b\uff0c\u62cd\u788e\u4e86\u5c01\u6ce5\u3002 \u3000\u3000\u9152\u5f88\u9999\u3002\u4e01\u7075\u7433\u55c5\u5230\u4e86\u9152\u9999\uff0c\u5fc3\u91cc\u5374\u5ffd\u7136\u4e00\u9635\u523a\u75db\uff0c\u8fd9\u672c\u662f\u5979\u7684\u559c\u9152\uff0c\u73b0\u5728\u5462\uff1f \u3000\u3000\u9152\u867d\u9999\uff0c\u5979\u53c8\u600e\u4e48\u80fd\u5fcd\u5fc3\u559d\u4e0b\u53bb\u3002 \u3000\u3000\u5979\u60f3\u8d77\u4e86\u90ed\u5b9a\uff0c\u60f3\u8d77\u4e86\u53f6\u5f00\uff0c\u60f3\u8d77\u4e86\u4e3a\u53f6\u5f00\u53bb\u6253\u9152\u7684\u97e9\u8d1e\u3002 \u3000\u3000\u2014\u2014\u5979\u5f53\u7136\u8fd8\u4e0d\u77e5\u9053\u97e9\u8d1e\u5e76\u6ca1\u6709\u6b7b\u3002 \u3000\u3000\u5979\u53ea\u77e5\u9053\uff0c\u82e5\u4e0d\u662f\u5979\u523a\u4e86\u53f6\u5f00\u90a3\u4e00\u5200\uff0c\u97e9\u8d1e\u5c31\u4e0d\u4f1a\u6b7b\uff0c\u5979\u4e5f\u77e5\u9053\uff0c\u82e5\u4e0d\u662f\u9b54\u6559\u7684\u90aa\u6cd5\uff0c \u5979\u6b7b\u4e5f\u4e0d\u4f1a\u523a\u53f6\u5f00\u90a3\u4e00\u5200\u3002 \u3000\u3000\u201c\u9b54\u6559\u2026\u2026\u201d\u5979\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u50cf\u4f60\u8fd9\u79cd\u4eba\uff0c\u600e\u4e48\u4f1a\u4eba\u9b54\u6559\u7684\uff1f\u201d \u3000\u3000\u845b\u75c5\u6c89\u9ed8\u7740\uff0c\u7ec8\u4e8e\u957f\u957f\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u5c31\u56e0\u4e3a\u6211\u662f\u8fd9\u4e48\u6837\u4e00\u4e2a\u4eba\uff0c\u6240\u4ee5\u624d\u4f1a\u4eba \u9b54\u6559\u3002\u201d \u3000\u3000\u201c\u662f\u4f60\u81ea\u5df1\u5fc3\u7518\u60c5\u613f\u7684\uff1f\u201d \u3000\u3000\u201c\u662f\u3002\u201d \u3000\u3000\u201c\u6211\u60f3\u4e0d\u901a\u3002\u201d\u4e01\u7075\u7433\u4e5f\u53ea\u6709\u82e6\u7b11\uff1a\u201c\u6211\u5b9e\u5728\u60f3\u4e0d\u901a\u3002\u201d \u3000\u3000\u845b\u75c5\u9053\uff1a\u201c\u8fd9\u4e5f\u8bb8\u56e0\u4e3a\u4f60\u6839\u672c\u4e0d\u77e5\u9053\u6211\u662f\u4e2a\u4ec0\u4e48\u6837\u7684\u4eba\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u53ef\u662f\u6211\u77e5\u9053\u4f60\u7edd\u4e0d\u662f\u4ed6\u4eec\u90a3\u79cd\u72e0\u6bd2\u7684\u5c0f\u4eba\u3002\u201d \u3000\u3000\u845b\u75c5\u53c8\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u624d\u6162\u6162\u9053\uff1a\u201c\u6211\u5b66\u533b\uff0c\u672c\u662f\u4e3a\u4e86\u6551\u6211\u81ea\u5df1\uff0c\u56e0\u4e3a\u6211\u53d1\u73b0\u4e16\u4e0a\u7684\u540d\u533b \u4eec\uff0c\u5341\u4e2a\u4e2d\u6709\u4e5d\u4e2a\u662f\u8822\u624d\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u6211\u77e5\u9053\u3002\u201d \u3000\u3000\u845b\u75c5\u9053\uff1a\u201c\u53ef\u662f\u5230\u4e86\u540e\u6765\uff0c\u6211\u5b66\u533b\u5df2\u4e0d\u662f\u4e3a\u4e86\u6551\u81ea\u5df1\uff0c\u4e5f\u4e0d\u662f\u4e3a\u4e86\u6551\u4eba\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u4f60\u662f\u4e3a\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u845b\u75c5\u9053\uff1a\u201c\u5230\u540e\u6765\u6211\u5b66\u533b\uff0c\u53ea\u56e0\u4e3a\u6211\u5df2\u7ecf\u5b8c\u5168\u5165\u4e86\u9b54\u3002\u201d \u3000\u3000\u65e0\u8bba\u505a\u4ec0\u4e48\u4e8b\uff0c\u82e5\u662f\u592a\u6c89\u8ff7\uff0c\u90fd\u4f1a\u5165\u9b54\u7684\u3002 \u3000\u3000\u201c\u6240\u4ee5\u4f60\u5c31\u5165\u9b54\u6559\uff1f\u201d \u3000\u3000\u845b\u75c5\u9053\uff1a\u201c\u9b54\u6559\u4e2d\u867d\u7136\u6709\u5f88\u591a\u53ef\u6015\u7684\u6740\u4eba\u90aa\u672f\uff0c\u5374\u4e5f\u6709\u5f88\u591a\u795e\u5947\u7684\u6551\u547d\u79d8\u65b9\uff0c\u8b6c\u5982\u8bf4\uff0c \u4ed6\u4eec\u7684\u6444\u9b42\u5927\u6cd5\uff0c\u82e5\u662f\u7528\u5f97\u5f88\u6b63\u786e\uff0c\u5728\u7597\u4f24\u6cbb\u75c5\u65f6\uff0c\u5f80\u5f80\u53ef\u4ee5\u6536\u5230\u610f\u60f3\u4e0d\u5230\u7684\u7597\u6548\u3002\u201d \u3000\u3000\u6c34\u80fd\u8f7d\u821f\uff0c\u4e5f\u80fd\u8986\u821f\u3002\u65e0\u8bba\u4ec0\u4e48\u4e8b\u90fd\u662f\u8fd9\u6837\u5b50\u7684\u3002 \u3000\u3000\u201c\u4f60\u82e5\u662f\u7528\u5f97\u6b63\u786e\uff0c\u7812\u971c\u4e5f\u662f\u6551\u547d\u7684\u826f\u836f\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u4ed6\u4eec\u7684\u6444\u9b42\u5927\u6cd5\uff0c\u5bf9\u6cbb\u75c5\u53c8\u6709\u4ec0\u4e48\u7528\uff1f\u201d \u3000\u3000\u4e01\u7075\u7433\u8fd8\u662f\u4e0d\u61c2\u3002 \u3000\u3000\u845b\u75c5\u9053\uff1a\u201c\u533b\u8005\u610f\u4e5f\uff0c\u8fd9\u53e5\u8bdd\u4f60\u61c2\u4e0d\u61c2\uff1f\u201d \u3000\u3000\u201c\u4e0d\u61c2\u3002\u201d \u3000\u3000\u201c\u8fd9\u5c31\u662f\u8bf4\uff0c\u4e00\u4e2a\u4eba\u81ea\u5df1\u7684\u610f\u5fd7\u529b\u662f\u5426\u575a\u5f3a\uff0c\u5f80\u5f80\u53ef\u4ee5\u51b3\u5b9a\u4ed6\u7684\u751f\u6b7b\u3002\u201d \u3000\u3000\u4ed6\u8fd9\u79cd\u89e3\u91ca\u4e0d\u4f46\u6df1\u5965\uff0c\u800c\u4e14\u65b0\u9c9c\uff0c\u4ed6\u4e5f\u77e5\u9053\u4e01\u7075\u7433\u4e00\u5b9a\u8fd8\u662f\u542c\u4e0d\u61c2\u7684\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u53c8\u89e3\u91ca\uff1a\u201c\u8fd9\u4e5f\u5c31\u662f\u8bf4\uff0c\u4e00\u4e2a\u75c5\u91cd\u7684\u4eba\uff0c\u662f\u4e0d\u662f\u80fd\u6d3b\u4e0b\u53bb\uff0c\u81f3\u5c11\u6709\u4e00\u534a\u8981\u770b\u4ed6\u81ea \u5df1\u662f\u4e0d\u662f\u60f3\u6d3b\u4e0b\u53bb\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u7ec8\u4e8e\u61c2\u4e86\uff0c\u56e0\u4e3a\u5979\u5ffd\u7136\u60f3\u8d77\u4e86\u4e2a\u5f88\u597d\u7684\u4f8b\u5b50\uff0c\u5979\u60f3\u8d77\u4e86\u90ed\u5b9a\uff0c\u82e5\u4e0d\u662f\u5979\u6fc0\u53d1\u4e86\u90ed \u5b9a\u6c42\u751f\u7684\u610f\u5fd7\uff0c\u7528\u4e0d\u7740\u7b49\u9b54\u6559\u7684\u4eba\u4e0b\u624b\uff0c\u4ed6\u5c31\u65e9\u5df2\u6b7b\u4e86\u3002 \u3000\u3000\u5979\u7684\u5fc3\u53c8\u5728\u523a\u75db\uff0c\u5fcd\u4e0d\u4f4f\u6367\u8d77\u4e86\u9152\u575b\u5b50\uff0c\u559d\u4e86\u4e00\u5927\u53e3\u3002 \u3000\u3000\u845b\u75c5\u5ffd\u7136\u9053\uff1a\u201c\u8ba9\u6211\u4e5f\u559d\u4e00\u53e3\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u4f60\u7684\u4f24\u8fd9\u4e48\u91cd\uff0c\u8fd8\u80fd\u559d\u9152\uff1f\u201d \u3000\u3000\u845b\u75c5\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u65e2\u7136\u559d\u4e0d\u559d\u90fd\u662f\u4e00\u6837\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u559d\uff1f\u201d \u3000\u3000\u4e01\u7075\u7433\u7684\u5fc3\u5728\u5f80\u4e0b\u6c89\u3002 \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u559d\u4e0d\u559d\u90fd\u662f\u4e00\u6837\u7684\uff1f\u4f60\u521a\u624d\u5403\u7684\u836f\u96be\u9053\u6ca1\u6709\u6548\uff1f\u201d \u3000\u3000\u845b\u75c5\u6ca1\u6709\u56de\u7b54\uff0c\u4e5f\u4e0d\u5fc5\u56de\u7b54\u3002 \u3000\u3000\u4e01\u7075\u7433\u5ffd\u7136\u53d1\u73b0\u4ed6\u82cd\u767d\u7684\u8138\uff0c\u5df2\u53d8\u5f97\u901a\u7ea2\u6eda\u70ed\uff0c\u5c31\u50cf\u662f\u6709\u706b\u7130\u71c3\u70e7\u7740\u4e00\u6837\u3002 \u3000\u3000\u521a\u624d\u90a3\u74f6\u836f\uff0c\u663e\u7136\u5e76\u4e0d\u80fd\u6551\u4ed6\u7684\u547d\uff0c\u53ea\u4e0d\u8fc7\u6682\u65f6\u63d0\u4f4f\u4e86\u4ed6\u4e00\u53e3\u6c14\u800c\u5df2\u3002 \u3000\u3000\u770b\u7740\u4ed6\u8d8a\u6765\u8d8a\u53ef\u6015\u7684\u8138\u8272\uff0c\u4e01\u7075\u7433\u7684\u773c\u6cea\u53c8\u6025\u5f97\u6d41\u4e86\u4e0b\u6765\uff1a\u201c\u4f60\u2026\u2026\u4f60\u89c9\u5f97\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u201c\u6211\u89c9\u5f97\u5f88\u597d\u3002\u201d\u845b\u75c5\u95ed\u4e0a\u773c\u775b\uff1a\u201c\u6211\u8bf4\u8fc7\uff0c\u6211\u5df2\u662f\u4e2a\u8001\u4eba\uff0c\u5df2\u6ca1\u6709\u4ec0\u4e48\u53ef\u6015\u7684\u3002\u201d \u3000\u3000\u4ed6\u5e76\u4e0d\u6015\u6b7b\uff0c\u4e00\u70b9\u4e5f\u4e0d\u6015\u3002 \u3000\u3000\u4e01\u7075\u7433\u5ffd\u7136\u660e\u767d\uff0c\u521a\u624d\u4ed6\u62c5\u5fc3\u7684\u5e76\u4e0d\u662f\u4ed6\u81ea\u5df1\uff0c\u800c\u662f\u5979\u3002 \u3000\u3000\u8fd9\u60f3\u6cd5\u4e5f\u50cf\u4e00\u6839\u9488\u523a\u5165\u4e86\u5979\u7684\u5fc3\u3002 \u3000\u3000\u5979\u4e0d\u77e5\u9053\u8be5\u8bf4\u4ec0\u4e48\uff0c\u4e5f\u4e0d\u77e5\u9053\u8be5\u600e\u4e48\u6837\u624d\u80fd\u62a5\u7b54\u8fd9\u79cd\u6069\u60e0\u548c\u611f\u845b\u75c5\u5ffd\u53c8\u7b11\u4e86\u7b11\uff0c\u9053\uff1a \u201c\u6211\u4e5f\u8bf4\u8fc7\uff0c\u6211\u5bf9\u533b\u9053\u5df2\u5165\u4e86\u9b54\uff0c\u6240\u4ee5\u6211\u65e2\u6ca1\u6709\u670b\u53cb\uff0c\u4e5f\u6ca1\u6709\u4eb2\u4eba\uff0c\u56e0\u4e3a\u6211\u5bf9\u4efb\u4f55\u4eba\u90fd\u4e0d\u5173 \u5fc3\u3002\u201d \u3000\u3000\u53ef\u662f\u4ed6\u5bf9\u4e01\u7075\u7433\u5374\u662f\u5173\u5fc3\u7684\u3002 \u3000\u3000\u5979\u77e5\u9053\uff0c\u5979\u770b\u5f97\u51fa\uff0c\u4f46\u5374\u4e0d\u77e5\u9053\u662f\u4e3a\u4e86\u4ec0\u4e48\uff1f \u3000\u3000\u65e0\u8bba\u5982\u4f55\uff0c\u4ed6\u5df2\u662f\u4e2a\u8001\u4eba\uff0c\u4ed6\u4eec\u4e4b\u95f4\u7684\u5e74\u7eaa\u5b9e\u5728\u76f8\u5dee\u5927\u591a\uff0c\u5f53\u7136\u4e0d\u4f1a\u6709\u5979\u8fde\u60f3\u90fd\u4e0d\u6562\u60f3 \u7684\u90a3\u79cd\u611f\u60c5\u3002 \u3000\u3000\u4ed6\u5173\u5fc3\u5979\uff0c\u4e5f\u8bb8\u53ea\u4e0d\u8fc7\u50cf\u7236\u4eb2\u5bf9\u513f\u5973\u7684\u90a3\u79cd\u5173\u5fc3\u4e00\u6837\u3002 \u3000\u3000\u53ef\u662f\u845b\u75c5\u5df2\u7741\u5f00\u773c\u775b\uff0c\u6b63\u5728\u51dd\u89c6\u7740\u5979\u3002 \u3000\u3000\u4ed6\u7684\u8138\u66f4\u7ea2\uff0c\u773c\u6674\u91cc\u4e5f\u4eff\u4f5b\u6709\u706b\u7130\u5728\u71c3\u70e7\u7740\uff0c\u8fd9\u79cd\u706b\u7130\u5df2\u4f7f\u5f97\u4ed6\u5931\u53bb\u4e86\u5e73\u65f6\u7684\u51b7\u6f20\u4e0e\u9547 \u5b9a\u3002 \u3000\u3000\u4ed6\u5df2\u6e10\u6e10\u65e0\u6cd5\u63a7\u5236\u81ea\u5df1\u7684\u7406\u667a\u3002 \u3000\u3000\u4e01\u7075\u7433\u7adf\u4e0d\u7531\u81ea\u4e3b\u907f\u5f00\u4e86\u4ed6\u7684\u76ee\u5149\uff0c\u7ade\u4e0d\u6562\u518d\u53bb\u770b\u4ed6\u3002 \u3000\u3000\u845b\u75c5\u5ffd\u7136\u7b11\u4e86\u7b11\uff0c\u7b11\u5f97\u5f88\u51c4\u51c9\uff0c\u9053\uff1a\u201c\u6211\u5df2\u662f\u4e2a\u8001\u5934\u5b50\uff0c\u6211\u4eec\u7684\u5e74\u7eaa\u5b9e\u5728\u76f8\u5dee\u5927\u591a\u4e86\uff0c \u5426\u5219\u2026\u2026\u201d \u3000\u3000\u5426\u5219\u600e\u4e48\u6837\uff1f\u4ed6\u6ca1\u6709\u8bf4\u4e0b\u53bb\uff0c\u4e5f\u4e0d\u5fc5\u518d\u95ee\u4e0b\u53bb\u3002 \u3000\u3000\u4e01\u7075\u7433\u5df2\u660e\u767d\u4e86\u4ed6\u7684\u610f\u601d\uff0c\u4e5f\u5df2\u660e\u767d\u4e86\u4ed6\u7684\u611f\u60c5\u3002 \u3000\u3000\u8001\u4eba\u4e5f\u662f\u4eba\u3002\u53ea\u8981\u662f\u4eba\uff0c\u5c31\u6709\u53bb\u7231\u522b\u4eba\u7684\u6743\u5229\u3002 \u3000\u3000\u8001\u4eba\u4e5f\u548c\u5e74\u8f7b\u4eba\u4e00\u6837\uff0c\u662f\u6709\u611f\u60c5\u7684\uff0c\u6709\u65f6\u4ed6\u4eec\u7684\u60c5\u611f\u751a\u81f3\u6bd4\u5e74\u8f7b\u4eba\u66f4\u771f\u631a\uff0c\u66f4\u6df1\u523b\uff0c\u56e0 \u4e3a\u4ed6\u4eec\u5df2\u4e86\u89e3\u8fd9\u79cd\u611f\u60c5\u7684\u53ef\u8d35\uff0c\u56e0\u4e3a\u4ed6\u4eec\u5bf9\u8fd9\u79cd\u611f\u60c5\u5df2\u6709\u60a3\u5f97\u60a3\u5931\u4e4b\u5fc3\uff0c\u8fd8\u6ca1\u6709\u5f97\u5230\u65f6\uff0c\u5df2 \u552f\u6050\u5b83\u4f1a\u5931\u53bb\u3002 \u3000\u3000\u53ef\u662f\u845b\u75c5\u6bd5\u7adf\u4e0d\u662f\u5e73\u51e1\u7684\u4eba\uff0c\u6bd5\u7adf\u8fd8\u6ca1\u6709\u5b8c\u5168\u5931\u53bb\u7406\u667a\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u53ea\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u6de1\u6de1\u9053\uff1a\u201c\u4e0d\u7ba1\u600e\u4e48\u6837\uff0c\u4f60\u90fd\u4e0d\u5fc5\u4e3a\u6211\u62c5\u5fc3\uff0c\u6211\u521a\u624d\u8fd8\u8bf4\u8fc7\uff0c\u6211 \u65e2\u7136\u6ca1\u6709\u670b\u53cb\uff0c\u4e5f\u6ca1\u6709\u4eb2\u4eba\u2026\u2026\u6211\u7684\u6b7b\u6d3b\u8ddf\u522b\u4eba\u6839\u672c\u5b8c\u5168\u6ca1\u6709\u5173\u7cfb\u3002\u201d \u3000\u3000\u2014\u2014\u53ef\u662f\u8ddf\u6211\u6709\u5173\u7cfb\u2014\u2014\u4e01\u7075\u7433\u5fc3\u91cc\u88ab\u523a\u5f97\u66f4\u6df1\u3002 \u3000\u3000\u82e5\u4e0d\u662f\u4e3a\u5979\uff0c\u4ed6\u6839\u672c\u4e0d\u4f1a\u6b7b\uff1a\u82e5\u4e0d\u662f\u56e0\u4e3a\u4ed6\uff0c\u5979\u65e9\u5df2\u6b7b\u4e86\uff1b\u4ed6\u7684\u6b7b\u6d3b\uff0c\u600e\u4e48\u4f1a\u8ddf\u5979\u6ca1\u6709 \u5173\u7cfb\uff0c\u5979\u600e\u4e48\u80fd\u770b\u7740\u4ed6\u6b7b\uff1f\u53ef\u662f\u5979\u53c8\u6709\u4ec0\u4e48\u6cd5\u5b50\u80fd\u6551\u4ed6\u5462\uff1f \u3000\u3000\u2014\u2014\u4e00\u4e2a\u75c5\u91cd\u7684\u4eba\uff0c\u662f\u4e0d\u662f\u80fd\u6d3b\u4e0b\u53bb\uff0c\u81f3\u5c11\u6709\u4e00\u534a\u8981\u770b\u4ed6\u81ea\u5df1\u662f\u4e0d\u662f\u60f3\u6d3b\u4e0b\u53bb\u3002 \u3000\u3000\u8fd9\u4e9b\u8bdd\u4eff\u4f5b\u5ffd\u7136\u53c8\u5728\u4e01\u7075\u7433\u8033\u8fb9\u54cd\u8d77\uff0c\u5979\u77e5\u9053\u4ed6\u73b0\u5728\u5e76\u4e0d\u60f3\u6d3b\u4e0b\u53bb\uff0c\u4ed6\u5df2\u662f\u4e2a\u8001\u4eba\uff0c\u4ed6 \u6ca1\u6709\u670b\u53cb\uff0c\u4e5f\u6ca1\u6709\u4eb2\u4eba\uff0c\u751a\u81f3\u8fde\u5fc3\u91cc\u7684\u611f\u60c5\uff0c\u90fd\u4e0d\u6562\u5bf9\u4eba\u8bf4\u51fa\u6765\u3002 \u3000\u3000\u4f60\u82e5\u662f\u4ed6\uff0c\u4f60\u6d3b\u7740\u8fd8\u6709\u4ec0\u4e48\u610f\u601d\uff1f \u3000\u3000\u845b\u75c5\u7684\u773c\u775b\u53c8\u9616\u8d77\uff0c\u5ffd\u7136\u9053\uff1a\u201c\u4f60\u8d70\u5427\u2026\u2026\u5feb\u8d70\u2026\u2026\u201d \u3000\u3000\u201c\u4f60\u4e3a\u4ec0\u4e48\u8981\u6211\u8d70\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u6211\u4e0d\u559c\u6b22\u522b\u4eba\u770b\u89c1\u6211\u6b7b\u65f6\u7684\u6837\u5b50\u3002\u201d \u3000\u3000\u845b\u75c5\u7684\u8eab\u5b50\u5df2\u5f00\u59cb\u75c9\u631b\uff0c\u663e\u7136\u5728\u52c9\u5f3a\u63a7\u5236\u81ea\u5df1\uff1a\u201c\u6240\u4ee5\u4f60\u4e00\u5b9a\u8981\u8d70\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u7528\u529b\u63e1\u7d27\u4e86\u81ea\u5df1\u7684\u624b\uff0c\u5de6\u624b\u63e1\u4f4f\u4e86\u53f3\u624b\uff0c\u5c31\u50cf\u751f\u6015\u81ea\u5df1\u7684\u51b3\u5fc3\u4f1a\u6539\u53d8\u4e00\u6837\u7684\u3002 \u3000\u3000\u201c\u6211\u4e0d\u8d70\uff01\u201d\u5979\u5ffd\u7136\u5927\u58f0\u9053\uff1a\u201c\u7edd\u4e0d\u8d70\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e01\u7075\u7433\u7684\u624b\u63e1\u5f97\u66f4\u7528\u529b\uff1a\u201c\u56e0\u4e3a\u6211\u8981\u5ac1\u7ed9\u4f60\u3002\u201d \u3000\u3000\u845b\u75c5\u970d\u7136\u5f20\u5f00\u4e86\u773c\u775b\uff0c\u5403\u60ca\u5730\u770b\u7740\u5979\uff1a\u201c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u6211\u8bf4\u6211\u8981\u5ac1\u7ed9\u4f60\uff0c\u4e00\u5b9a\u8981\u5ac1\u7ed9\u4f60\u3002\u201d\u5979\u771f\u7684\u53c8\u4e0b\u4e86\u51b3\u5fc3\u3002 \u3000\u3000\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u5979\u5df2\u5fd8\u8bb0\u4e86\u90ed\u5b9a\uff0c\u5fd8\u4e86\u53f6\u5f00\uff0c\u5fd8\u4e86\u6240\u6709\u7684\u4eba\uff0c\u6240\u6709\u7684\u4e8b\u3002 \u3000\u3000\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u5979\u53ea\u77e5\u9053\u4e00\u4ef6\u4e8b\u3002 \u3000\u3000\u2014\u2014\u5979\u7edd\u4e0d\u80fd\u5c31\u8fd9\u4e48\u6837\u770b\u7740\u845b\u75c5\u6b7b\u5728\u5979\u9762\u524d\uff0c\u53ea\u8981\u80fd\u6551\u4ed6\uff0c\u5c31\u7b97\u8981\u5979\u53bb\u5ac1\u7ed9\u4e00\u53ea\u732a\uff0c\u4e00 \u6761\u72d7\uff0c\u5979\u4e5f\u4f1a\u6beb\u4e0d\u8003\u8651\u5c31\u7b54\u5e94\u3002\u5979\u672c\u5c31\u662f\u4e2a\u60c5\u611f\u4e30\u5bcc\u7684\u5973\u5b69\u5b50\uff0c\u5979\u505a\u4e8b\u672c\u5c31\u5e38\u5e38\u662f\u4e0d\u987e\u4e00\u5207 \u7684\u3002\u522b\u4eba\u6b3a\u8d1f\u4e86\u5979\u5bb3\u4e86\u5979\uff0c\u5979\u5f88\u5feb\u5c31\u4f1a\u5fd8\u8bb0\uff0c\u53ef\u662f\u4f60\u53ea\u8981\u5bf9\u5979\u6709\u4e00\u70b9\u597d\u5904\uff0c\u5979\u5c31\u4f1a\u6c38\u8fdc\u8bb0\u5728 \u5fc3\u91cc\u3002 \u3000\u3000\u5979\u505a\u7684\u4e8b\u4e5f\u8bb8\u5f88\u7cca\u6d82\uff0c\u751a\u81f3\u5f88\u8352\u8c2c\uff0c\u4f46\u5979\u5374\u7edd\u5bf9\u662f\u4e2a\u53ef\u7231\u7684\u4eba\uff0c\u56e0\u4e3a\u5979\u6709\u4e00\u9897\u7edd\u5bf9\u5584\u826f \u7684\u5fc3\u3002 \u3000\u3000\u201c\u4f60\u8981\u5ac1\u7ed9\u6211\uff1f\u201d\u845b\u75c5\u5728\u7b11\uff0c\u7b11\u5bb9\u4e2d\u5e26\u7740\u4e09\u5206\u8f9b\u9178\uff0c\u4e09\u5206\u611f\u6fc0\uff0c\u8fd8\u6709\u4e09\u5206\u662f\u4ec0\u4e48\uff1f\u4ed6\u81ea \u5df1\u4e5f\u4e0d\u77e5\u9053\uff0c\u81ea\u5df1\u4e5f\u5206\u4e0d\u6e05\uff0c\u4ed6\u4e0d\u662f\u4e2a\u5341\u5206\u6e05\u695a\u7684\u4eba\u3002 \u3000\u3000\u4e01\u7075\u7433\u8df3\u8d77\u6765\uff0c\u5979\u5ffd\u7136\u53d1\u73b0\u8fd9\u91cc\u552f\u4e00\u4eae\u7740\u7684\u706f\u706b\uff0c\u5c31\u662f\u90a3\u5bf9\u9f99\u51e4\u82b1\u70db\u3002\u8fd9\u672c\u662f\u4e3a\u5979\u548c\u90ed \u5b9a\u800c\u51c6\u5907\u7684\uff0c\u5c31\u5728\u8fd9\u5bf9\u9f99\u51e4\u82b1\u70db\u524d\uff0c\u90ed\u5b9a\u7a7f\u7740\u4e00\u8eab\u65b0\u90ce\u7684\u5409\u670d\uff0c\u5012\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u73b0\u5728\uff0c\u8fd9\u5bf9\u82b1\u70db\u8fd8\u6ca1\u6709\u71c3\u5c3d\uff0c\u5979\u5374\u5df2\u8981\u5ac1\u7ed9\u53e6\u5916\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u82e5\u662f\u522b\u4eba\u8981\u505a\u8fd9\u79cd\u4e8b\uff0c\u65e0\u8bba\u8c01\u90fd\u4f1a\u8ba4\u4e3a\u8fd9\u4e2a\u4eba\u662f\u4e2a\u8352\u5510\u65e0\u60c5\u7684\u75af\u5b50\u3002\u53ef\u662f\u4e01\u7075\u7433\u4e0d\u662f\u522b \u4eba\uff0c\u65e0\u8bba\u8c01\u5bf9\u5979\u90fd\u53ea\u6709\u601c\u60af\u548c\u540c\u60c5\uff0c\u56e0\u4e3a\u5979\u8fd9\u4e48\u505a\uff0c\u4e0d\u662f\u65e0\u60c5\uff0c\u800c\u662f\u6709\u60c5\uff0c\u4e0d\u662f\u62a5\u590d\uff0c\u800c\u662f \u727a\u7272\uff0c\u5979\u4e0d\u60dc\u727a\u7272\u81ea\u5df1\u4e00\u751f\u7684\u5e78\u798f\uff0c\u4e3a\u7684\u53ea\u8981\u62a5\u7b54\u522b\u4eba\u5bf9\u5979\u7684\u6069\u60c5\uff0c\u9664\u6b64\u4e4b\u5916\uff0c\u5979\u5b9e\u5728\u4e0d\u77e5 \u9053\u8fd8\u6709\u4ec0\u4e48\u522b\u7684\u6cd5\u5b50\u80fd\u6551\u845b\u75c5\u3002 \u3000\u3000\u8fd9\u6cd5\u5b50\u5f53\u7136\u5e76\u4e0d\u4e00\u5b9a\u6709\u6548\uff0c\u8fd9\u79cd\u60f3\u6cd5\u4e5f\u5f88\u8352\u8c2c\u5e7c\u7a1a\u3002\u53ef\u662f\u4e00\u4e2a\u4eba\u82e5\u662f\u80af\u727a\u7272\u81ea\u5df1\uff0c\u53bb\u6551 \u522b\u4eba\uff0c\u90a3\u4e48\u5979\u505a\u7684\u4e8b\u65e0\u8bba\u591a\u8352\u5510\uff0c\u591a\u5e7c\u7a1a\uff0c\u90fd\u503c\u5f97\u5c0a\u656c\u3002 \u3000\u3000\u56e0\u4e3a\u8fd9\u79cd\u727a\u7272\u624d\u662f\u771f\u6b63\u7684\u727a\u7272\uff0c\u624d\u662f\u522b\u4eba\u65e2\u4e0d\u80af\u505a\u3001\u4e5f\u505a\u4e0d\u5230\u7684\u3002 \u7b2c\u4e8c\u5341\u56db\u7ae0\u3000\u60b2\u6b22\u79bb\u5408>> \u53e4\u9f99\u300a\u4e5d\u6708\u9e70\u98de\u300b \u7b2c\u4e8c\u5341\u56db\u7ae0\u3000\u60b2\u6b22\u79bb\u5408 \u3000\u3000\u82b1\u70db\u5df2\u5c06\u71c3\u5c3d\uff0c\u70db\u6cea\u8fd8\u672a\u5e72\u3002 \u3000\u3000\u70db\u6cea\u4e00\u5b9a\u8981\u7b49\u5230\u8721\u70db\u5df1\u6210\u7070\u65f6\u624d\u4f1a\u5e72\uff0c\u8721\u70db\u5b81\u613f\u81ea\u5df1\u88ab\u70e7\u6210\u7070\u3002 \u3000\u3000\u4e5f\u53ea\u4e3a\u4e86\u7167\u4eae\u522b\u4eba\u3002 \u3000\u3000\u8fd9\u79cd\u505a\u6cd5\u5c82\u975e\u4e5f\u5f88\u611a\u8822\uff1f \u3000\u3000\u4f46\u4eba\u4eec\u82e5\u662f\u80af\u591a\u505a\u51e0\u4ef6\u8fd9\u79cd\u611a\u8822\u7684\u4e8b\uff0c\u8fd9\u4e16\u754c\u5c82\u975e\u66f4\u8f89\u714c\u707f\u70c2\uff1f \u3000\u3000\u4e01\u7075\u7433\u6276\u8d77\u845b\u75c5\uff0c\u7ad9\u5728\u82b1\u70db\u524d\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u73b0\u5728\u6211\u5c31\u8981\u5ac1\u7ed9\u4f60\uff0c\u505a\u4f60\u7684\u59bb\u5b50\uff0c\u7ec8\u751f\u4f9d\u9760 \u4f60\uff0c\u6240\u4ee5\u4f60\u4e00\u5b9a\u8981\u6d3b\u4e0b\u53bb\u3002\u201d \u3000\u3000\u845b\u75c5\u770b\u7740\u5979\uff0c\u4e00\u53cc\u7070\u9eef\u7684\u773c\u775b\uff0c\u5ffd\u7136\u53c8\u6709\u4e86\u5149\u91c7\uff0c\u8138\u4e0a\u7684\u7b11\u5bb9\uff0c\u4e5f\u5df2\u53d8\u5f97\u5b89\u8be6\u606c\u9759\u3002 \u3000\u3000\u4e01\u7075\u7433\u6cea\u75d5\u672a\u5e72\u7684\u8138\u4e0a\uff0c\u4e5f\u5df2\u9732\u51fa\u4e86\u5fae\u7b11\u3002 \u3000\u3000\u5979\u77e5\u9053\u4ed6\u5df2\u80fd\u6d3b\u4e0b\u53bb\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u5df2\u6709\u4e86\u5bb6\uff0c\u6709\u4e86\u4eb2\u4eba\uff0c\u4ed6\u5df2\u4e0d\u80fd\u6b7b\u3002 \u3000\u3000\u5979\u542b\u7740\u6cea\u7b11\u9053\uff1a\u201c\u8fd9\u91cc\u867d\u7136\u6ca1\u6709\u559c\u5b98\uff0c\u4f46\u6211\u4eec\u5374\u4e00\u6837\u8fd8\u662f\u53ef\u4ee5\u62dc\u5929\u5730\uff0c\u53ea\u8981\u6211\u4eec\u4e24\u4e2a\u4eba \u613f\u610f\uff0c\u6709\u6ca1\u6709\u522b\u4eba\u505a\u89c1\u8bc1\u90fd\u4e00\u6837\u3002\u201d \u3000\u3000\u8fd9\u5e76\u4e0d\u662f\u513f\u620f\uff0c\u66f4\u4e0d\u7b97\u8352\u5510\uff0c\u56e0\u4e3a\u5979\u7684\u786e\u662f\u771f\u5fc3\u8bda\u610f\u7684\u3002 \u3000\u3000\u845b\u75c5\u6162\u6f2b\u5730\u70b9\u4e86\u70b9\u5934\uff0c\u76ee\u4e2d\u5e26\u7740\u79cd\u5f02\u6837\u7684\u5149\u91c7\u770b\u7740\u5979\uff0c\u770b\u7740\u9762\u524d\u7684\u82b1\u70db\u3002 \u3000\u3000\u80fd\u548c\u81ea\u5df1\u559c\u7231\u7684\u5973\u5b50\u7ed3\u5408\uff0c\u5c82\u975e\u6b63\u662f\u6bcf\u4e2a\u7537\u4eba\u6700\u5927\u7684\u613f\u671b\u3002 \u3000\u3000\u4ed6\u5fae\u7b11\u7740\uff1a\u201c\u6211\u8fd9\u4e00\u751f\u4e2d\uff0c\u4e00\u76f4\u90fd\u5728\u76fc\u671b\u80fd\u6709\u8fd9\u4e48\u6837\u4e00\u5929\u2026\u2026\u6211\u672c\u6765\u4ee5\u4e3a\u6211\u6c38\u8fdc\u4e0d\u4f1a\u6709 \u8fd9\u4e48\u6837\u4e00\u5929\u4e86\uff0c\u53ef\u662f\u73b0\u5728\u2026\u2026\u201d \u3000\u3000\u73b0\u5728\u4ed6\u7ec8\u4e8e\u8fbe\u6210\u4e86\u4ed6\u7684\u613f\u671b\u3002 \u3000\u3000\u4ed6\u7684\u8bed\u58f0\u4e5f\u53d8\u5f97\u5b89\u8be6\u800c\u606c\u9759\uff0c\u53ef\u662f\u4ed6\u5e76\u6ca1\u6709\u8bf4\u5b8c\u8fd9\u53e5\u8bdd\uff0c\u4ed6\u5ffd\u7136\u5012\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u6b7b\u4ea1\u4e0b\u5f97\u6bd4\u95ea\u7535\u8fd8\u5feb\uff0c\u5ffd\u7136\u5c31\u51fb\u5012\u4e86\u4ed6\u3002 \u3000\u3000\u4ed6\u5b8c\u5168\u4e0d\u80fd\u62b5\u6297\u3002 \u3000\u3000\u6ca1\u6709\u4eba\u80fd\u62b5\u6297\u3002 \u3000\u3000\u9ece\u660e\u524d\u603b\u662f\u4e00\u5927\u6700\u9ed1\u6697\u7684\u65f6\u5019\u3002 \u3000\u3000\u4e01\u7075\u7433\u5df1\u8dea\u4e0b\uff0c\u8dea\u5728\u845b\u75c5\uff0c\u7684\u5c38\u4f53\u524d\uff0c\u773c\u6cea\u5c31\u50cf\u662f\u6cc9\u6c34\u822c\u6d8c\u51fa\u6765\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u540c\u4e00\u4e2a\u5730\u65b9\uff0c\u540c\u4e00\u5bf9\u82b1\u70db\u524d\uff0c\u5c31\u5728\u540c\u4e00\u5929\u665a\u4e0a\uff0c\u5df2\u6709\u4e24\u4e2a\u51c6\u5907\u8ddf\u4ed6\u7ed3\u5408\u7684\u7537\u4eba\u5012 \u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u8fd9\u6253\u51fb\u5b9e\u5728\u592a\u5927\u3002 \u3000\u3000\u4e5f\u8bb8\u4ed6\u4eec\u672c\u5c31\u8981\u6b7b\u7684\uff0c\u56e0\u4e3a\u5979\uff0c\u4ed6\u4eec\u4e5f\u8bb8\u53cd\u800c\u6b7b\u5f97\u66f4\u5feb\u3002 \u3000\u3000\u53ef\u662f\u5979\u81ea\u5df1\u5374\u5df1\u4e0d\u80fd\u4e0d\u8fd9\u4e48\u60f3\u3002\u5979\u5ffd\u7136\u89c9\u5f97\u81ea\u5df1\u662f\u4e2a\u4e0d\u6837\u7684\u5973\u4eba\uff0c\u53ea\u80fd\u4e3a\u522b\u4eba\u5e26\u6765\u707e\u7978 \u548c\u6b7b\u4ea1\u3002 \u3000\u3000\u90ed\u5b9a\u6b7b\u4e86\uff0c\u845b\u75c5\u6b7b\u4e86\uff0c\u53f6\u5f00\u4e5f\u51e0\u4e4e\u6b7b\u5728\u5979\u7684\u5200\u4e0b\u3002 \u3000\u3000\u5979\u81ea\u5df1\u5374\u504f\u504f\u8fd8\u6d3b\u7740\u3002 \u3000\u3000\u2014\u2014\u6211\u4e3a\u4ec0\u4e48\u8fd8\u8981\u6d3b\u7740\uff1f\u4e3a\u4ec0\u4e48\u8fd8\u8981\u6d3b\u5728\u8fd9\u4e16\u754c\u4e0a\uff1f \u3000\u3000\u8fd9\u662f\u4e2a\u4ec0\u4e48\u6837\u7684\u4e16\u754c\uff1f \u3000\u3000\u6bcf\u4e2a\u5979\u8ba4\u5f97\u7684\u4eba\uff0c\u7adf\u90fd\u53ef\u80fd\u662f\u9b54\u6559\u4e2d\u7684\u4eba\uff0c\u4ece\u94c1\u59d1\u5f00\u59cb\uff0c\u5230\u7389\u7bab\u9053\u4eba\uff0c\u845b\u75c5\uff0c\u8fd8\u6709\u90a3\u51b7 \u9177\u5982\u6076\u9b54\u7684\u5b64\u5cf0\u5929\u738b\uff0c\u6bcf\u4e2a\u4eba\u90fd\u662f\u5979\u60f3\u4e0d\u5230\u7684\u3002 \u3000\u3000\u5728\u8fd9\u4e16\u754c\u4e0a\uff0c\u8fd8\u6709\u4ec0\u4e48\u662f\u5979\u53ef\u4fe1\u8d56\u7684\uff1f \u3000\u3000\u53ea\u6709\u53f6\u5f00\uff01\u53ef\u662f\u53f6\u5f00\u53c8\u5728\u4f55\u5904\uff1f \u3000\u3000\u9152\u8fd8\u5728\u5979\u8eab\u65c1\uff0c\u70c8\u9152\u559d\u4e0b\u53bb\u65f6\uff0c\u5c31\u50cf\u662f\u559d\u4e0b\u4e86\u4e00\u56e2\u706b\u3002 \u3000\u3000\u5979\u559d\u4e86\u4e00\u53e3\uff0c\u53c8\u4e00\u53e3\u3002 \u3000\u3000\u201c\u53f6\u5f00\u4f60\u8bf4\u8fc7\uff0c\u53ea\u8981\u7b49\u4e00\u5207\u4e8b\u89e3\u51b3\uff0c\u4f60\u5c31\u4f1a\u6765\u627e\u6211\uff0c\u73b0\u5728\u4ec0\u4e48\u4e8b\u90fd\u5b8c\u4e86\uff0c\u4f60\u4e3a\u4ec0\u4e48\u8fd8\u4e0d \u6765\uff1f\u2026\u2026\u4e3a\u4ec0\u4e48\uff1f\u2026\u2026\u201d \u3000\u3000\u5979\u653e\u58f0\u5927\u53eb\uff0c\u5ffd\u7136\u5c06\u624b\u91cc\u7684\u9152\u575b\u5b50\u7528\u529b\u7838\u51fa\u53bb\uff0c\u7838\u5f97\u7c89\u788e\uff0c\u70c8\u9152\u9c9c\u8840\u822c\u6d41\u5728\u5730\u4e0a\u3002 \u3000\u3000\u684c\u4e0a\u5df2\u5c06\u71c3\u5c3d\u7684\u9f99\u51e4\u82b1\u70db\u4e5f\u88ab\u9707\u5012\u4e86\uff0c\u843d\u5728\u5730\u4e0a\uff0c\u7acb\u523b\u5c06\u5730\u4e0a\u7684\u70c8\u9152\u71c3\u70e7\u4e86\u8d77\u6765\u3002 \u3000\u3000\u706b\u4e5f\u662f\u65e0\u60c5\u7684\uff0c\u751a\u81f3\u6bd4\u6b7b\u4ea1\u66f4\u65e0\u60c5\uff0c\u751a\u81f3\u6bd4\u6b7b\u4ea1\u6765\u5f97\u66f4\u5feb\u3002 \u3000\u3000\u8fd9\u79cd\u731b\u70c8\u7684\u706b\u52bf\uff0c\u53c8\u6709\u8c01\u80fd\u62b5\u6297\u3002 \u3000\u3000\u6ca1\u6709\u4eba\u80fd\u62b5\u6297\uff01 \u3000\u3000\u4f46\u4e01\u7075\u7433\u5374\u8fd8\u662f\u75f4\u75f4\u5730\u8dea\u5728\u90a3\u91cc\uff0c\u8fde\u52a8\u90fd\u6ca1\u6709\u52a8\u3002 \u3000\u3000\u770b\u7740\u706b\u7130\u71c3\u70e7\uff0c\u5979\u5fc3\u91cc\u5ffd\u7136\u6cdb\u8d77\u79cd\u6b8b\u9177\u7684\u5feb\u610f\u3002 \u3000\u3000\u5979\u8981\u770b\u7740\u8fd9\u79cd\u706b\u7130\u71c3\u70e7\uff0c\u628a\u6240\u6709\u7684\u4e00\u5207\u5168\u90fd\u70e7\u5149\uff0c\u5979\u5df1\u4e0d\u518d\u6709\u4ec0\u4e48\u7559\u604b\u3002 \u3000\u3000\u6bc1\u706d\u5c82\u975e\u4e5f\u662f\u79cd\u53d1\u6cc4\uff1f \u3000\u3000\u5979\u9700\u8981\u53d1\u6cc4\u3002\u5979\u60f3\u6bc1\u706d\u3002 \u3000\u3000\u6728\u677f\u9694\u6210\u7684\u5385\u5802\uff0c\u8f6c\u773c\u95ee\u5c31\u5df2\u88ab\u706b\u7130\u541e\u6ca1\uff0c\u6240\u6709\u7684\u4e00\u5207\u4e8b\uff0c\u73b0\u5728\u771f\u7684\u5df2\u5168\u90fd\u89e3\u51b3\u4e86\u3002 \u3000\u3000\u53ef\u662f\u53f6\u5f00\u5462\uff1f \u3000\u3000\u53f6\u5f00\u3002\u4f60\u4e3a\u4ec0\u4e48\u8fd8\u4e0d\u6765\uff1f \u3000\u3000\u70c8\u706b\u7167\u7ea2\u4e86\u5927\u5730\u82cd\u7a79\u65f6\uff0c\u9ece\u660e\u7ec8\u4e8e\u6765\u4e86\u3002 \u3000\u3000\u53f6\u5f00\u5374\u8fd8\u662f\u6ca1\u6709\u6765\u3002 \u3000\u3000\u53f6\u5f00\u9189\u4e86\u3002 \u3000\u3000\u4ed6\u4e00\u5411\u5f88\u5c11\u9189\uff0c\u4ece\u6765\u4e5f\u6ca1\u6709\u4eba\u80fd\u704c\u9189\u4ed6\uff0c\u552f\u4e00\u80fd\u704c\u9189\u4ed6\u7684\u4eba\uff0c\u5c31\u662f\u4ed6\u81ea\u5df1\u3002 \u3000\u3000\u4ed6\u5f88\u60f3\u704c\u9189\u81ea\u5df1\u3002 \u3000\u3000\u559d\u9189\u9152\u5e76\u4e0d\u662f\u4ef6\u5f88\u6109\u5feb\u7684\u4e8b\uff0c\u5c24\u5176\u7b2c\u4e8c\u5929\u65e9\u4e0a\u66f4\u4e0d\u6109\u5feb\u2014\u2014\u8fd9\u4e00\u70b9\u4ed6\u6bd4\u8c01\u90fd\u77e5\u9053\u5f97\u6e05 \u695a\u3002 \u3000\u3000\u53ef\u662f\u6628\u5929\u665a\u4e0a\uff0c\u4ed6\u5374\u786c\u662f\u628a\u81ea\u5df1\u704c\u9189\uff0c\u9189\u5f97\u4eba\u4e8b\u4e0d\u7701\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u6bd5\u7adf\u4e0d\u662f\u5723\u4eba\u3002 \u3000\u3000\u77e5\u9053\u81ea\u5df1\u7684\u60c5\u4eba\u6b63\u5728\u62dc\u5929\u5730\uff0c\u65b0\u90ce\u5b98\u5374\u4e0d\u662f\u81ea\u5df1\uff0c\u53c8\u6709\u8c01\u8fd8\u80fd\u4fdd\u6301\u6e05\u6e05\u9192\u9192\uff0c\u9ad8\u9ad8\u5174\u5174 \u5730\u5728\u8857\u4e0a\u901b\u6765\u901b\u53bb\uff1f \u3000\u3000\u6240\u4ee5\u4ed6\u901b\u5230\u7b2c\u4e00\u4e2a\u5356\u9152\u7684\u5730\u65b9\u65f6\uff0c\u5c31\u505c\u4e86\u4e0b\u6765\uff0c\u505c\u4e86\u4e00\u4e2a\u591a\u65f6\u8fb0\u3002 \u3000\u3000\u53ef\u662f\u51fa\u6765\u7684\u65f6\u5019\u8fd8\u6ca1\u6709\u9189\u3002 \u3000\u3000\u4e00\u4e00\u8fd9\u5730\u65b9\u7684\u9152\u597d\u50cf\u592a\u6de1\u4e86\uff0c\u597d\u50cf\u5151\u4e86\u6c34\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u53c8\u901b\u5230\u7b2c\u4e8c\u4e2a\u5356\u9152\u7684\u5730\u65b9\uff0c\u7528\u4e00\u79cd\u4e0d\u7a33\u5b9a\u7684\u811a\u6b65\u901b\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u8fd9\u6b21\u4ed6\u662f\u600e\u4e48\u51fa\u6765\u7684\uff0c\u4ed6\u5df2\u8bb0\u4e0d\u6e05\u4e86\uff0c\u4ee5\u540e\u662f\u4e0d\u662f\u5230\u8fc7\u7b2c\u4e09\u4e2a\u5730\u65b9\uff1f\u4ed6\u66f4\u8bb0\u4e0d\u6e05\u4e86\u3002 \u3000\u3000\u4ed6\u552f\u4e00\u8bb0\u5f97\u7684\u4e8b\uff0c\u662f\u628a\u4e00\u4e2a\u5e26\u7740\u5a4a\u5b50\u53bb\u559d\u9152\u7684\u4e0a\u6d41\u6c13\u5934\u4e0a\u6253\u4e86\u4e2a\u6d1e\u3002 \u3000\u3000\u90a3\u4e2a\u6d1e\u7a76\u7adf\u6709\u591a\u5927\uff1f\u4ed6\u4e5f\u5df2\u5b8c\u5168\u4e0d\u8bb0\u5f97\u3002 \u3000\u3000\u4ed6\u9192\u6765\u7684\u65f6\u5019\uff0c\u53d1\u73b0\u81ea\u5df1\u7adf\u7761\u5728\u4e00\u6761\u6b7b\u5f04\u4e2d\u7684\u5783\u573e\u5806\u91cc\u3002 \u3000\u3000\u53c8\u810f\u53c8\u81ed\u7684\u5783\u573e\u5806\uff0c\u8fde\u91ce\u72d7\u90fd\u7edd\u4e0d\u80af\u5728\u8fd9\u79cd\u5730\u65b9\u7761\u4e00\u4e0b\u5b50\u3002 \u3000\u3000\u4ed6\u53ef\u4ee5\u4fdd\u8bc1\u8fd9\u7edd\u4e0d\u662f\u4ed6\u81ea\u5df1\u613f\u610f\u7684\uff0c\u4ed6\u4e00\u5411\u6ca1\u6709\u7761\u5728\u5783\u573e\u5806\u91cc\u7684\u4e60\u60ef\u3002 \u3000\u3000\u2014\u2014\u2014\u5b9a\u662f\u90a3\u4e2a\u5934\u4e0a\u6709\u6d1e\u7684\u4e0a\u6d41\u6c13\uff0c\u627e\u4e86\u4eba\u6765\u62a5\u4ec7\uff0c\u5148\u63cd\u4e86\u4ed6\u4e00\u987f\uff0c\u518d\u628a\u4ed6\u629b\u5230\u8fd9\u91cc \u6765\u3002 \u3000\u3000\u4ed6\u4e0d\u4e45\u5c31\u8bc1\u5b9e\u4e86\u8fd9\u4ef6\u4e8b\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u7ad9\u8d77\u6765\u7684\u65f6\u5019\u4e0d\u4f46\u5934\u75db\u6b32\u88c2\uff0c\u800c\u4e14\u5168\u8eab\u90fd\u53d1\u75bc\u3002 \u3000\u3000\u90a3\u4e00\u5b9a\u8981\u5f88\u91cd\u7684\u62f3\u5934\u624d\u80fd\u628a\u4ed6\u6253\u6210\u8fd9\u6837\u5b50\uff0c\u4ed6\u8fd8\u6ca1\u6709\u5b66\u4f1a\u6253\u4eba\u524d\u5c31\u5df2\u5148\u5b66\u4f1a\u6328\u6253\u7684\u3002 \u3000\u3000\u7136\u540e\u4ed6\u53c8\u53d1\u73b0\u5934\u75bc\u5e76\u4e0d\u662f\u5b8c\u5168\u56e0\u4e3a\u9152\u9189\uff0c\u4ed6\u5934\u4e0a\u4e5f\u591a\u4e86\u4e2a\u6d1e\u3002 \u3000\u3000\u65e0\u8bba\u8c01\u82e5\u662f\u53d1\u73b0\u81ea\u5df1\u88ab\u4eba\u629b\u5728\u5783\u573e\u5806\u91cc\uff0c\u88ab\u6574\u5f97\u4e00\u584c\u7cca\u6d82\uff0c\u90fd\u5154\u4e0d\u4e86\u8981\u5f88\u751f\u6c14\uff0c\u5f88\u96be\u53d7 \u7684\u3002 \u3000\u3000\u2014\u2014\u5076\u800c\u80fd\u88ab\u4eba\u75db\u63cd\uff0c\u5c82\u975e\u4e5f\u662f\u4ef6\u86ee\u6709\u8da3\u7684\u4e8b\u3002 \u3000\u3000\u4f55\u51b5\uff0c\u4ed6\u76f8\u4fe1\u63cd\u4ed6\u7684\u90a3\u4e9b\u5bb6\u4f19\u4eec\uff0c\u73b0\u5728\u4e00\u5b9a\u4e5f\u5f88\u75db\u3002 \u3000\u3000\u8d70\u51fa\u5df7\u5b50\uff0c\u662f\u6761\u659c\u8857\uff0c\u5c31\u50cf\u957f\u5b89\u57ce\u91cc\u5927\u591a\u6570\u8857\u9053\u4e00\u6837\uff0c\u53e4\u8001\u800c\u9648\u65e7\u3002 \u3000\u3000\u8857\u5bf9\u9762\u6709\u5bb6\u5c0f\u9152\u9986\uff0c\u95e8\u53e3\u6302\u7740\u4e2a\u5f88\u5927\u7684\u9152\u846b\u82a6\uff0c\u662f\u94c1\u94f8\u7684\u3002 \u3000\u3000\u53f6\u5f00\u5ffd\u7136\u60f3\u8d77\uff0c\u6628\u5929\u665a\u4e0a\u4ed6\u6253\u67b6\u559d\u9152\uff0c\u90fd\u662f\u5728\u8fd9\u5c0f\u9152\u94fa\u91cc\u3002 \u3000\u3000\u9152\u94fa\u540e\u9762\uff0c\u597d\u50cf\u5c31\u662f\u4e2a\u201c\u6697\u95e8\u5b50\u201d\uff0c\u90a3\u4e0a\u6d41\u6c13\u5e26\u51fa\u6765\u7684\uff0c\u5c31\u662f\u8fd9\u6697\u95e8\u5b50\u91cc\u7684\u5973\u4eba\u3002 \u3000\u3000\u4ece\u8fd9\u91cc\u5f80\u5de6\u8f6c\uff0c\u518d\u8f6c\u8fc7\u4e24\u6761\u8857\uff0c\u5c31\u662f\u9e3f\u5bbe\u5ba2\u6808\u3002 \u3000\u3000\u53f6\u5f00\u8fd9\u4e00\u8f88\u5b50\uff0c\u5927\u6982\u662f\u518d\u4e5f\u4e0d\u4f1a\u5230\u9e3f\u5bbe\u5ba2\u6808\u53bb\u4e86\uff0c\u90a3\u91cc\u7684\u4f24\u5fc3\u4e8b\u5b9e\u5728\u5927\u591a\u3002 \u3000\u3000\u73b0\u5728\u5e94\u8be5\u5230\u54ea\u91cc\u53bb\uff1f\u5e94\u8be5\u505a\u4e9b\u4ec0\u4e48\u4e8b\uff1f\u53f6\u5f00\u8fde\u60f3\u90fd\u6ca1\u6709\u60f3\u3002 \u3000\u3000\u4ed6\u51b3\u5b9a\u6682\u65f6\u4ec0\u4e48\u90fd\u4e0d\u53bb\u60f3\uff0c\u73b0\u5728\u4ed6\u8111\u5b50\u91cc\u8fd8\u662f\u660f\u6c89\u6c89\u7684\u3002 \u3000\u3000\u4ed6\u53ea\u77e5\u9053\u7edd\u4e0d\u80fd\u5f80\u5de6\u8fb9\u8d70\u3002 \u3000\u3000\u4eca\u5929\u5c45\u7136\u53c8\u662f\u6674\u5929\uff0c\u592a\u9633\u7167\u5728\u4eba\u8eab\u4e0a\uff0c\u6696\u6696\u548c\u548c\u7684\uff0c\u5f88\u8212\u670d\u3002 \u3000\u3000\u8857\u4e0a\u7684\u4eba\u90fd\u7a7f\u7740\u65b0\u8863\u670d\uff0c\u8138\u4e0a\u90fd\u5e26\u7740\u559c\u6c14\uff0c\u4e00\u89c1\u9762\u5c31\u4f5c\u63d6\uff0c\u4e0d\u505c\u4ed6\u8bf4\uff1a\u201c\u606d\u559c\u201d\uff0c\u53f6\u5f00 \u8fd9\u624d\u60f3\u8d77\u6765\uff0c\u4eca\u5929\u8fd8\u662f\u5927\u5e74\u521d\u4e8c\u3002 \u3000\u3000\u522b\u7684\u4eba\u5728\u5927\u5e74\u521d\u4e8c\u8fd9\u4e00\u5929\uff0c\u5e94\u8be5\u505a\u4e9b\u4ec0\u4e48\u4e8b\u5462\uff1f \u3000\u3000\u2014\u2014\u5e26\u7740\u5b69\u5b50\u5230\u4eb2\u621a\u670b\u53cb\u5bb6\u53bb\u62dc\u5e74\uff0c\u6536\u4e9b\u538b\u5c81\u94b1\uff0c\u7136\u540e\u518d\u56de\u5bb6\uff0c\u51c6\u5907\u4e9b\u91d1\u679c\u5143\u5b9d\uff0c\u7b49\u7740 \u522b\u4eba\u6765\u62dc\u5e74\uff0c\u628a\u538b\u5c81\u94b1\u518d\u8fd8\u7ed9\u522b\u4eba\u7684\u5b69\u5b50\u3002 \u3000\u3000\u8fd9\u4e00\u5929\u5927\u5bb6\u90fd\u4e0d\u8bb8\u8bf4\u4e0d\u5409\u5229\u7684\u8bdd\uff0c\u66f4\u4e0d\u8bb8\u5435\u67b6\u3001\u751f\u6c14\u3002 \u3000\u3000\u53ef\u662f\u65e2\u6ca1\u6709\u5bb6\u3001\u53c8\u6ca1\u6709\u670b\u53cb\u7684\u5f02\u4e61\u6d6a\u5b50\uff0c\u5728\u8fd9\u4e00\u5929\u53c8\u8be5\u5e72\u4ec0\u4e48\uff1f \u3000\u3000\u53f6\u5f00\u5728\u8857\u4e0a\u901b\u6765\u901b\u53bb\uff0c\u4e1c\u5f20\u897f\u671b\uff0c\u5176\u5b9e\u773c\u775b\u91cc\u4ec0\u4e48\u90fd\u6ca1\u6709\u770b\u5230\uff0c\u5fc3\u91cc\u4ec0\u4e48\u90fd\u6ca1\u6709\u53bb\u60f3\uff0c \u4e5f\u8bb8\u53ea\u5728\u60f3\u4e00\u4ef6\u4e8b\u3002 \u3000\u3000\u4e01\u7075\u7433\u73b0\u5728\u6b63\u5e72\u4ec0\u4e48\uff1f \u3000\u3000\u4ed6\u672c\u6765\u5df2\u51b3\u5b9a\uff0c\u6c38\u8fdc\u518d\u4e5f\u4e0d\u60f3\u5979\u4e86\uff0c\u4f46\u5374\u4e0d\u77e5\u4e3a\u4e86\u4ec0\u4e48\uff0c\u4ed6\u8fd9\u660f\u6c89\u6c89\u7684\u8111\u888b\u91cc\uff0c\u60f3\u6765\u60f3 \u53bb\uff0c\u504f\u504f\u90fd\u53ea\u6709\u5979\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u4ed6\u521a\u624d\u8fd8\u51b3\u5b9a\uff0c\u7edd\u4e0d\u518d\u5230\u9e3f\u5bbe\u5ba2\u6808\u53bb\uff0c\u53ef\u662f\u73b0\u5728\u4e00\u62fe\u8d77\u5934\uff0c\u5c31\u53d1\u73b0\u81ea\u5df1\u8fd8\u662f\u53c8\u8d70\u5230\u8fd9\u6761 \u8def\u4e0a\u6765\u4e86\u3002 \u3000\u3000\u5947\u602a\u7684\u662f\uff0c\u4ed6\u5e76\u6ca1\u6709\u770b\u89c1\u9e3f\u5bbe\u5ba2\u6808\u90a3\u5757\u9ad8\u9ad8\u6302\u7740\u7684\u91d1\u5b57\u62db\u724c\uff0c\u53ea\u770b\u89c1\u4e00\u5927\u5806\u4eba\uff0c\u56f4\u5728\u90a3 \u91cc\uff0c\u6709\u7684\u5728\u7a83\u7a83\u79c1\u8bae\uff0c\u6709\u7684\u5728\u6447\u5934\u53f9\u606f\uff0c\u751a\u81f3\u8fd8\u6709\u4e9b\u4eba\u6b63\u5728\u90a3\u91cc\u62b1\u7740\u5934\u653e\u58f0\u5927\u54ed\u7740\u3002 \u3000\u3000\u8fd9\u91cc\u7a76\u7adf\u51fa\u4e86\u4ec0\u4e48\u4e8b\uff1f \u3000\u3000\u53f6\u5f00\u5fcd\u4e0d\u4f4f\u901b\u4e86\u8fc7\u53bb\uff0c\u6324\u8fdb\u4eba\u4e1b\uff0c\u7136\u540e\u4ed6\u6574\u4e2a\u4eba\u5c31\u5ffd\u7136\u53d8\u5f97\u51b7\u51b7\u51b0\u51b0\uff0c\u5c31\u50cf\u662f\u4e00\u4e0b\u5b50\u6389 \u8fdb\u4e86\u6df1\u4e0d\u89c1\u5e95\u7684\u51b7\u6c34\u6f6d\u91cc\u3002 \u3000\u3000\u957f\u5b89\u57ce\u91cc\u6c14\u6d3e\u6700\u5927\u7684\u9e3f\u5bbe\u5ba2\u6808\uff0c\u73b0\u5728\u7ade\u5df2\u53d8\u6210\u4e86\u4e00\u7247\u74e6\u783e\u3002 \u3000\u3000\u9e3f\u5bbe\u5ba2\u6808\u6628\u591c\u7684\u60e8\u6848\uff1a\u76f4\u5230\u5929\u4eae\u624d\u6709\u4eba\u77e5\u9053\uff0c\u56e0\u4e3a\u6628\u5929\u662f\u4e2a\u5f88\u7279\u522b\u7684\u65e5\u5b50\uff0c\u662f\u5927\u5e74\u521d \u4e00\u3002 \u3000\u3000\u5927\u5e74\u521d\u4e00\u7684\u665a\u4e0a\uff0c\u5927\u5bb6\u901a\u5e38\u90fd\u662f\u5728\u5446\u5728\u5bb6\u91cc\u7684\uff0c\u8c01\u4e5f\u4e0d\u4f1a\u5230\u8857\u4e0a\u6765\u95f2\u901b\uff0c\u5c31\u7b97\u6709\u4eba\uff0c\u4e5f \u662f\u4e9b\u5df2\u8d4c\u5f97\u5934\u660f\u8111\u80c0\u7684\u4eba\uff0c\u8c01\u4e5f\u4e0d\u4f1a\u901b\u5230\u5ba2\u6808\u91cc\u53bb\u3002 \u3000\u3000\u5446\u5728\u5bb6\u91cc\u7684\u4eba\uff0c\u4e5f\u5927\u591a\u90fd\u5728\u559d\u9152\uff0c\u8d4c\u94b1\uff0c\u66f4\u4e0d\u4f1a\u5173\u5fc3\u5230\u5916\u9762\u7684\u4e8b\u3002 \u3000\u3000\u8001\u638c\u67dc\u8bf7\u53bb\u559d\u559c\u9152\u7684\uff0c\u5927\u90fd\u662f\u4e9b\u65e0\u5bb6\u53ef\u5f52\u7684\u5149\u68cd\uff0c\u6ca1\u6709\u4eba\u5173\u5fc3\u7684\u5149\u68cd\u3002 \u3000\u3000\u5c31\u56e0\u4e3a\u8fd9\u662f\u4e2a\u7279\u522b\u7684\u65e5\u5b50\uff0c\u6240\u4ee5\u624d\u4f1a\u53d1\u751f\u90a3\u4e9b\u7279\u522b\u7684\u4e8b\u3002 \u3000\u3000\u8fd9\u5e76\u4e0d\u662f\u5de7\u5408\u3002 \u3000\u3000\u6bcf\u4ef6\u4e8b\u7684\u53d1\u751f\u548c\u5b58\u5728\uff0c\u90fd\u4e00\u5b9a\u6709\u5b83\u7684\u539f\u56e0\u3002 \u3000\u3000\u201c\u8fd9\u91cc\u662f\u4ec0\u4e48\u5219\u3002\u5019\u8d70\u6c34\u7684\uff1f\u201d \u3000\u3000\u201c\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u6628\u5929\u591c\u91cc\u6211\u5728\u8d4c\u53f6\u5b50\u724c\uff0c\u5c31\u7b97\u5929\u584c\u4e0b\u6765\uff0c\u6211\u4e5f\u4e0d\u4f1a\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u542c\u8bf4\u6628\u5929\u665a\u4e0a\u6709\u4eba\u5728\u8fd9\u91cc\u505a\u559c\u4e8b\uff1f\u201d \u3000\u3000\u201c\u597d\u50cf\u662f\u7684\u3002\u201d \u3000\u3000\u201c\u90a3\u4e9b\u6765\u559d\u559c\u9152\u7684\u4eba\uff0c\u600e\u4e48\u8fde\u4e00\u4e2a\u90fd\u4e0d\u5728\uff1f\u201d \u3000\u3000\u201c\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u90a3\u5bf9\u65b0\u4eba\u5462\uff1f\u201d \u3000\u3000\u201c\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u8fd9\u5730\u65b9\u867d\u7136\u5df2\u88ab\u70e7\u6210\u4e86\u74e6\u783e\uff0c\u5374\u8fde\u4e00\u4e2a\u4eba\u7684\u9ab8\u9aa8\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u201c\u8fd9\u91cc\u7684\u8001\u638c\u67dc\u5462\uff1f\u201d \u3000\u3000\u201c\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u6628\u5929\u665a\u4e0a\u8fd9\u91cc\u7a76\u7adf\u51fa\u4e86\u4ec0\u4e48\u4e8b\uff0c\u7b80\u76f4\u8fde\u4e00\u4e2a\u77e5\u9053\u7684\u4eba\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u201c\u6211\u522b\u7684\u4e8b\u90fd\u4e0d\u5947\u602a\uff0c\u53ea\u5947\u602a\u90a3\u5bf9\u65b0\u4eba\u5c45\u7136\u4e5f\u4e0d\u5728\u8fd9\u6d1e\u623f\u91cc\uff0c\u8fde\u8001\u638c\u67dc\u90fd\u4e0d\u89c1\u4e86\u3002\u201d \u3000\u3000\u5927\u5bb6\u8bae\u8bba\u7eb7\u7eb7\uff0c\u8d8a\u8bf4\u8d8a\u5947\uff1a\u201c\u96be\u9053\u8fd9\u91cc\u6628\u5929\u665a\u4e0a\u51fa\u4e86\u72d0\u4ed9\uff1f\u51fa\u4e86\u9b3c\uff1f\u201d \u3000\u3000\u82e5\u4e0d\u662f\u6709\u9b3c\uff0c\u5ba2\u6808\u88ab\u70e7\u5149\uff0c\u90a3\u8001\u638c\u67dc\u603b\u8be5\u56de\u6765\u770b\u770b\u7684\u3002 \u3000\u3000\u53f6\u5f00\u77e5\u9053\u6ca1\u6709\u9b3c\uff0c\u4ed6\u4ece\u6765\u4e0d\u76f8\u4fe1\u8fd9\u79cd\u6d3b\u89c1\u9b3c\u7684\u4e8b\u3002\u4f46\u8fd9\u4ef6\u4e8b\u60c5\u5374\u771f\u7684\u597d\u50cf\u9047\u89c1\u4e86\u9b3c\uff0c\u4ed6 \u5c31\u662f\u628a\u8111\u888b\u6253\u51fa\u4e86\u4e2a\u6d1e\u6765\uff0c\u4e5f\u8fd8\u662f\u60f3\u4e0d\u901a\u7684\u3002\u201d \u3000\u3000\u4ed6\u53ea\u89c9\u5f97\u6574\u4e2a\u4eba\u90fd\u5df2\u53d8\u6210\u4e86\u4e00\u5927\u5757\u6728\u5934\uff0c\u4e00\u5757\u53c8\u51b7\u53c8\u786c\u7684\u6728\u5934\u3002 \u3000\u3000\u8fd9\u91cc\u7a76\u7adf\u600e\u4e48\u8d77\u7684\u706b\uff1f \u3000\u3000\u4e01\u7075\u7433\u548c\u90ed\u5b9a\u5230\u54ea\u91cc\u53bb\u4e86\uff1f \u3000\u3000\u4ed6\u4e00\u5b9a\u8981\u95ee\u51fa\u4ed6\u4eec\u7684\u884c\u8e2a\u6765\uff0c\u5374\u53c8\u4e0d\u77e5\u9053\u5e94\u8be5\u53bb\u95ee\u8c01\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u4eba\u4e1b\u91cc\u5ffd\u7136\u6709\u4e2a\u4eba\u5728\u62c9\u4ed6\u7684\u8863\u89d2\u3002 \u3000\u3000\u4ed6\u4e00\u4f4e\u5934\uff0c\u5c31\u770b\u89c1\u4e86\u4e00\u53ea\u67d4\u7f8e\u800c\u79c0\u6c14\u7684\u624b\u2014\u2014\u4e00\u53ea\u5973\u4eba\u7684\u624b\u3002 \u3000\u3000\u662f\u8c01\u5728\u62c9\u4ed6\uff1f \u3000\u3000\u662f\u4e0d\u662f\u4e01\u7075\u7433\uff1f \u3000\u3000\u53f6\u5f00\u62ac\u8d77\u5934\uff0c\u62c9\u4ed6\u7684\u4eba\u5df2\u8f6c\u8fc7\u8eab\uff0c\u5f80\u4eba\u4e1b\u5916\u8d70\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u5979\u8eab\u4e0a\u62ab\u7740\u4e4c\u9ed1\u7684\u51e4\u6c05\uff0c\u957f\u53d1\u5782\u843d\uff0c\u7528\u4e00\u679a\u7389\u73af\u675f\u4f4f\u3002 \u3000\u3000\u5979\u7a76\u7adf\u662f\u4e0d\u662f\u4e01\u7075\u7433\uff1f \u3000\u3000\u53f6\u5f00\u770b\u4e0d\u51fa\u3002 \u3000\u3000\u4ed6\u53ea\u597d\u8ddf\u7740\u5979\u8d70\u51fa\u4eba\u7fa4\uff0c\u770b\u7740\u5979\u8f7b\u76c8\u7684\u4f53\u6001\uff0c\u4ed6\u5fc3\u91cc\u5ffd\u7136\u6cdb\u8d77\u79cd\u8bf4\u4e0d\u51fa\u7684\u6ecb\u5473\uff0c\u53c8\u5e0c\u671b \u5979\u662f\u4e01\u7075\u7433\uff0c\u53c8\u5e0c\u671b\u5979\u4e0d\u662f\u3002 \u3000\u3000\u5979\u82e5\u662f\u4e01\u7075\u7433\uff0c\u4e24\u4eba\u76f8\u89c1\u540e\uff0c\u5fc3\u91cc\u53c8\u662f\u4ec0\u4e48\u6ecb\u5473\uff1f\u53c8\u6709\u4ec0\u4e48\u8bdd\u8bf4\uff1f \u3000\u3000\u629b\u82e5\u4e0d\u662f\u4e01\u7075\u7433\uff0c\u4f1a\u662f\u8c01\u5462\uff1f \u3000\u3000\u8fd9\u6b21\u53f6\u5f00\u5c45\u7136\u6ca1\u6709\u9000\u7f29\uff0c\u4e5f\u6ca1\u6709\u9003\u907f\uff0c\u4ed6\u77e5\u9053\u65e0\u8bba\u5979\u662f\u4e0d\u662f\u4e01\u7075\u7433\uff0c\u90fd\u4e00\u5b9a\u6709\u5f88\u591a\u8bdd\u8981 \u544a\u8bc9\u4ed6\u3002\u5979\u6162\u6162\u5730\u5728\u524d\u9762\u8d70\uff0c\u65e2\u6ca1\u6709\u505c\u4e0b\u6765\uff0c\u4e5f\u6ca1\u6709\u56de\u5934\uff0c\u8d70\u8fc7\u4e86\u8fd9\u6761\u957f\u8857\uff0c\u5ffd\u7136\u8f6c\u5165\u6761\u6a2a \u5df7\u3002 \u3000\u3000\u5df7\u5b50\u5f88\u7a84\u3002 \u3000\u3000\u53f6\u5f00\u8ffd\u8fc7\u53bb\u65f6\uff0c\u53ea\u770b\u89c1\u5979\u7684\u4eba\u5f71\u4e00\u95ea\uff0c\u8d70\u8fdb\u4e86\u4e00\u4e2a\u7a84\u95e8\u91cc\u3002 \u3000\u3000\u95e8\u662f\u865a\u63a9\u7740\u7684\u3002 \u3000\u3000\u4ece\u5916\u9762\u770b\u8d77\u6765\uff0c\u8fd9\u4e0d\u8fc7\u662f\u4e2a\u5f88\u5e73\u51e1\u7684\u4eba\u5bb6\uff0c\u95e8\u5916\u7684\u96ea\u79ef\u5f97\u5f88\u539a\uff0c\u4eff\u4f5b\u5df2\u5f88\u4e45\u6ca1\u6709\u6253\u626b\u3002 \u3000\u3000\u53f6\u5f00\u8d70\u5230\u95e8\u53e3\uff0c\u5fc3\u5c31\u8df3\u4e86\u8d77\u6765\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u60f3\u8d77\u8fd9\u5730\u65b9\u662f\u4ed6\u6765\u8fc7\u7684\uff0c\u73b0\u5728\u4ed6\u7528\u4e0d\u7740\u8d70\u8fdb\u53bb\uff0c\u4e5f\u77e5\u9053\u5979\u662f\u8c01\u4e86\u3002 \u3000\u3000\u5d14\u7389\u771f\u3002 \u3000\u3000\u8fd9\u6237\u4eba\u5bb6\u6b63\u662f\u5979\u5e26\u53f6\u5f00\u6765\u517b\u8fc7\u4f24\u7684\u5730\u65b9\u3002 \u3000\u3000\u60f3\u8d77\u4e86\u90a3\u4e24\u5929\u4e2d\u7684\u4e8b\uff0c\u53f6\u5f00\u5fc3\u91cc\u53c8\u6d8c\u8d77\u79cd\u8bf4\u4e0d\u51fa\u6765\u7684\u6ecb\u5473\uff0c\u5374\u4e0d\u77e5\u662f\u6b22\u559c\uff1f\u662f\u6005\u60d8\uff1f\u8fd8 \u662f\u5931\u671b\uff1f \u3000\u3000\u6b22\u559c\u7684\u662f\u5d14\u7389\u771f\u8fd8\u6d3b\u7740\u3002 \u3000\u3000\u6005\u60d8\u7684\u662f\u5f80\u4e8b\u5df2\u6210\u8fc7\u53bb\uff0c\u65e7\u68a6\u5df2\u65e0\u5904\u8ffd\u5bfb\u3002 \u3000\u3000\u5931\u671b\u7684\u662f\u4ec0\u4e48\u5462\uff1f \u3000\u3000\u96be\u9053\u4ed6\u5fc3\u5e95\u6df1\u5904\uff0c\u8fd8\u662f\u5728\u76fc\u671b\u7740\u5979\u5c31\u662f\u4e01\u7075\u7433\uff1f \u3000\u3000\u65e7\u68a6\u5e76\u4e0d\u662f\u5b8c\u5168\u65e0\u5904\u8ffd\u5bfb\uff0c\u81f3\u5c11\u5728\u8fd9\u5bd2\u51ac\u6e05\u6668\u7684\u51b7\u98ce\u91cc\uff0c\u8fd8\u53ef\u4ee5\u627e\u5230\u4e00\u70b9\u5f71\u5b50\u3002 \u3000\u3000\u51e4\u4ece\u540e\u9762\u7684\u53a8\u623f\u91cc\u5439\u8fc7\u6765\uff0c\u5439\u8fc7\u8fd9\u5c0f\u800c\u5e7d\u9759\u7684\u9662\u5b50\u3002 \u3000\u3000\u98ce\u4e2d\u5145\u6ee1\u4e86\u90c1\u90c1\u7684\u9999\u6c14\u3002 \u3000\u3000\u53f6\u5f00\u4e0d\u7981\u53c8\u60f3\u8d77\u90a3\u5929\u65e9\u4e0a\uff0c\u4ed6\u4e5f\u55c5\u5230\u4e86\u7ca5\u9999\uff0c\u6b63\u76fc\u671b\u7740\u4e00\u7897\u82b3\u9999\u6251\u9f3b\u7684\u70ed\u7ca5\uff0c\u7531\u5979\u4e00\u53cc \u67d4\u7f8e\u800c\u79c0\u6c14\u7684\u624b\u6367\u7ed9\u4ed6\u3002 \u3000\u3000\u8c01\u77e5\u7ca5\u7adf\u662f\u4ece\u95e8\u5916\u98de\u8fdb\u6765\u7684\u3002 \u3000\u3000\u4ed6\u6ca1\u6709\u770b\u89c1\u5979\u67d4\u7f8e\u7684\u624b\uff0c\u770b\u89c1\u7684\u5374\u662f\u4e00\u53ea\u6740\u4eba\u7684\u8840\u624b\u3002 \u3000\u3000\u4ece\u90a3\u5929\u5f00\u59cb\uff0c\u4ed6\u5c31\u4ece\u672a\u518d\u89c1\u8fc7\u5979\uff0c\u4e5f\u4ece\u672a\u60f3\u5230\u4ed6\u4eec\u8fd8\u6709\u518d\u89c1\u7684\u4e00\u5929\u3002 \u3000\u3000\u4ed6\u672c\u6765\u4ee5\u4e3a\u4ed6\u548c\u4e01\u7075\u7433\u4e00\u5b9a\u53ef\u4ee5\u6c38\u8fdc\u53ae\u5b88\u7684\uff0c\u8c01\u77e5\u73b0\u5728\u5374\u89c9\u5f97\u53ef\u80fd\u6c38\u4e0d\u518d\u89c1\u3002 \u3000\u3000\u4eba\u751f\u4e2d\u7684\u79bb\u5408\u60b2\u6b22\uff0c\u53c8\u6709\u8c01\u80fd\u9884\u6d4b\uff1f \u3000\u3000\u53f6\u5f00\u53f9\u606f\u7740\uff0c\u63a8\u5f00\u95e8\uff0c\u8d70\u8fdb\u5c4b\u5b50\uff0c\u90a3\u5f20\u5e8a\uff0c\u90a3\u4e2a\u5c0f\u5c0f\u7684\u8863\u67dc\uff0c\u90fd\u4f9d\u7136\u65e0\u6059\u3002 \u3000\u3000\u751a\u81f3\u8fde\u5c4b\u89d2\u7684\u9633\u5149\uff0c\u90fd\u8ddf\u90a3\u5929\u65e9\u4e0a\u5b8c\u5168\u4e00\u6837\u3002 \u3000\u3000\u53f6\u5f00\u4e5f\u4e0d\u77e5\u662f\u4eba\u5df2\u865a\u5f31\uff0c\u8fd8\u662f\u5fc3\u5728\u53d1\u8f6f\uff0c\u8d70\u8fdb\u53bb\uff0c\u8eba\u5728\u5e8a\u4e0a\u3002 \u3000\u3000\u6795\u4e0a\u7adf\u4eff\u4f5b\u4e5f\u8fd8\u7559\u7740\u53d1\u9999\u3002 \u3000\u3000\u65e0\u8bba\u5982\u4f55\uff0c\u90a3\u4e24\u5929\u5e73\u9759\u5b89\u9002\u7684\u65e5\u5b50\uff0c\u90fd\u662f\u4ed6\u6c38\u8fdc\u4e5f\u65e0\u6cd5\u5fd8\u8bb0\u7684\u3002 \u3000\u3000\u4ed6\u5fc3\u91cc\u751a\u81f3\u5728\u60f3\uff0c\u90a3\u5929\u5979\u82e5\u6ca1\u6709\u9047\u7740\u610f\u5916\uff0c\u4ed6\u662f\u4e0d\u662f\u76f4\u5230\u73b0\u5728\u8fd8\u5728\u8fd9\u91cc\u966a\u7740\u5979\uff1f \u3000\u3000\u95e8\u5916\u54cd\u8d77\u4e86\u4e00\u9635\u5f88\u8f7b\u7684\u811a\u6b65\u58f0\uff0c\u5979\u5df2\u6367\u7740\u7897\u70ed\u6c14\u817e\u817e\u7684\u7ca5\u8d70\u8fdb\u6765\uff0c\u7f8e\u4e3d\u7684\u8138\u4e0a\uff0c\u5e26\u7740\u751c \u871c\u800c\u6e29\u67d4\u7684\u5fae\u7b11\u3002 \u3000\u3000\u8fd9\u6b63\u662f\u90a3\u5929\u65e9\u4e0a\u53f6\u5f00\u5728\u5fc3\u91cc\u76fc\u671b\u7740\u7684\u60c5\u51b5\uff0c\u53ea\u4e0d\u8fc7\u73b0\u5728\u8ddd\u79bb\u90a3\u5929\u65e9\u4e0a\uff0c\u5df2\u4e0d\u77e5\u53c8\u8fc7\u4e86\u591a \u5c11\u5927\uff0c\u53c8\u53d1\u751f\u4e86\u591a\u5c11\u4e8b\u3002 \u3000\u3000\u73b0\u5728\u7684\u60c5\u51b5\u7eb5\u7136\u8fd8\u662f\u548c\u90a3\u5929\u65e9\u4e0a\u4e00\u6837\uff0c\u4f46\u5f7c\u6b64\u7684\u5fc3\u60c5\u5374\u5df2\u4e0d\u4e00\u6837\u3002 \u3000\u3000\u4e16\u4e0a\u53c8\u6709\u8c01\u90fd\u62c9\u5f97\u56de\u90a3\u4e00\u53bb\u6c38\u4e0d\u590d\u8fd4\u7684\u65f6\u5149\uff1f \u3000\u3000\u53f6\u5f00\u52c9\u5f3a\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u65e9\u3002\u201d \u3000\u3000\u201c\u65e9\u3002\u201d\u5d14\u7389\u771f\u7b11\u5f97\u66f4\u6e29\u67d4\uff1a\u201c\u7ca5\u5df2\u71ac\u597d\u4e86\uff0c\u4f60\u5c31\u8eba\u5728\u5e8a\u4e0a\u5403\uff1f\u201d \u3000\u3000\u53f6\u5f00\u70b9\u70b9\u5934\u3002 \u3000\u3000\u4e8e\u662f\u4e00\u7897\u9999\u6c14\u6251\u9f3b\u7684\u70ed\u7ca5\uff0c\u53c8\u7531\u5979\u4e00\u53cc\u67d4\u7f8e\u79c0\u6c14\u7684\u624b\u6367\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u7684\u786e\u5f88\u9700\u8981\u8fd9\u4e48\u6837\u4e00\u7897\u7ca5\u7684\uff0c\u4ed6\u7684\u80c3\u662f\u7a7a\u7684\uff0c\u6574\u4e2a\u4eba\u90fd\u662f\u7a7a\u7684\u3002 \u3000\u3000\u7ca5\u7684\u6ecb\u5473\uff0c\u4e5f\u8fd8\u662f\u8ddf\u4ee5\u524d\u4e00\u6837\uff0c\u53ef\u662f\u53f6\u5f00\u53ea\u559d\u4e86\u4eba\u53e3\uff0c\u5c31\u518d\u4e5f\u54bd\u4e0d\u4e0b\u53bb\u3002 \u3000\u3000\u5d14\u7389\u771f\u51dd\u89c6\u7740\u4ed6\uff0c\u8f7b\u8f7b\u9053\uff1a\u201c\u4f60\u6628\u5929\u665a\u4e0a\u4e00\u5b9a\u9189\u5f97\u5f88\u5389\u5bb3\u3002\u201d \u3000\u3000\u53f6\u5f00\u53c8\u52c9\u5f3a\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u9189\u5f97\u7b80\u76f4\u5c31\u50cf\u662f\u6761\u6b7b\u72d7\u3002\u201d \u3000\u3000\u5d14\u7389\u771f\u53c8\u770b\u4e86\u5f88\u4e45\uff0c\u624d\u8f7b\u8f7b\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u82e5\u662f\u4f60\uff0c\u6211\u4e5f\u8981\u9189\u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u77e5\u9053\u6628\u5929\u665a\u4e0a\u7684\u4e8b\uff1f\u201d \u3000\u3000\u201c\u672c\u6765\u6211\u8fd8\u4e0d\u77e5\u9053\u3002\u201d\u5979\u7f8e\u4e3d\u7684\u773c\u775b\u91cc\uff0c\u5ffd\u7136\u9732\u51fa\u79cd\u8bf4\u4e0d\u51fa\u7684\u5e7d\u6028\uff0c\u6162\u6162\u5730\u5f00\u59cb\u53d9\u8bf4\u5f80 \u4e8b\uff1a\u201c\u90a3\u5929\u65e9\u4e0a\u6211\u88ab\u4f0a\u591c\u54ed\u903c\u7740\u56de\u5230\u7389\u7bab\u9053\u4eba\u90a3\u91cc\u53bb\uff0c\u4ed6\u5c31\u2026\u2026\u5c31\u518d\u4e5f\u4e0d\u8bb8\u6211\u51fa\u6765\u3002\u201d \u3000\u3000\u53f6\u5f00\u9eef\u7136\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u5979\u4e00\u5b9a\u5403\u4e86\u4e0d\u5c11\u82e6\uff0c\u5979\u5c31\u7b97\u4e0d\u8bf4\uff0c\u4ed6\u4e5f\u770b\u5f97\u51fa\u3002 \u3000\u3000\u201c\u6211\u672c\u6765\u8fd9\u4e00\u8f88\u5b50\u5df2\u5b8c\u4e86\uff0c\u6211\u5b9e\u5728\u60f3\u4e0d\u5230\u90a3\u6076\u9b54\u4e5f\u6709\u6b7b\u5728\u522b\u4eba\u624b\u91cc\u7684\u4e00\u5927\u3002\u201d \u3000\u3000\u201c\u7389\u7bab\u9053\u4eba\u4e00\u6b7b\uff0c\u4f60\u5c31\u5230\u8fd9\u91cc\u6765\uff1f\u201d \u3000\u3000\u5d14\u7389\u771f\u9053\uff1a\u201c\u59d0\u59b9\u4eec\u4e00\u542c\u5230\u4ed6\u7684\u6b7b\u8baf\uff0c\u5c31\u50cf\u662f\u521a\u98de\u51fa\u7b3c\u5b50\u7684\u9e1f\uff0c\u90fd\u6068\u4e0d\u5f97\u98de\u5f97\u8fdc\u8fdc\u7684\uff0c \u6bcf\u4e2a\u4eba\u5206\u4e86\u4ed6\u4e00\u70b9\u4e1c\u897f\uff0c\u4e0d\u5230\u4e00\u4e2a\u65f6\u8fb0\u5c31\u5168\u90fd\u8d70\u4e86\uff0c\u53ea\u6709\u6211\u3002\u201d \u3000\u3000\u5979\u5782\u4e0b\u5934\uff0c\u6ca1\u6709\u518d\u8bf4\u4e0b\u53bb\u3002 \u3000\u3000\u2014\u2014\u53ea\u6709\u5979\u6ca1\u6709\u8d70\uff0c\u56e0\u4e3a\u5979\u5fd8\u4e0d\u4e86\u53f6\u5f00\uff0c\u6240\u4ee5\u53c8\u91cd\u5230\u8fd9\u91cc\uff0c\u60f3\u627e\u56de\u4e00\u70b9\u6614\u65e5\u7684\u65e7\u68a6\u3002 \u3000\u3000\u8fd9\u53e5\u8bdd\u5979\u7528\u4e0d\u7740\u8bf4\uff0c\u53f6\u5f00\u4e5f\u77e5\u9053\u3002 \u3000\u3000\u201c\u6211\u4e00\u4e2a\u4eba\u5728\u8fd9\u5c4b\u5b50\u91cc\u803d\u4e86\u4e00\u6574\u5929\uff0c\u65e2\u4e2a\u60f3\u51fa\u53bb\uff0c\u4e5f\u7761\u4e0d\u7740\u3002\u201d\u5979\u5728\u7b11\uff0c\u7b11\u5f97\u5374\u5f88\u8f9b \u9178\uff1a\u201c\u5176\u5b9e\u6211\u4e5f\u77e5\u9053\u4f60\u662f\u7edd\u4e0d\u4f1a\u518d\u56de\u5230\u8fd9\u91cc\u6765\u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u5fc3\u91cc\u53c8\u4f55\u5c1d\u4e0d\u662f\u9178\u9178\u7684\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u53d1\u89c9\u81ea\u5df1\u5b9e\u5728\u662f\u4e2a\u5f88\u65e0\u60c5\u7684\u4eba\uff0c\u5b9e\u5728\u6ca1\u6709\u60f3\u5230\u8fc7\u8981\u91cd\u56de\u8fd9\u91cc\u3002 \u3000\u3000\u201c\u76f4\u5230\u6628\u5929\u65e9\u4e0a\uff0c\u6211\u542c\u5230\u4e86\u5916\u9762\u7684\u7206\u7af9\u58f0\uff0c\u624d\u60f3\u8d77\u5df2\u7ecf\u662f\u5927\u5e74\u52ff\u4e00\u4e00\u3002\u201d\u5979\u6162\u6162\u5730\u63a5\u7740 \u9053\uff1a\u201c\u6211\u4e0d\u60f3\u4e00\u4e2a\u4eba\u518d\u95f7\u5728\u5c4b\u5b50\u91cc\uff0c\u53c8\u997f\u5f97\u53d1\u614c\u4e86\uff0c\u5fcd\u4e0d\u4f4f\u60f3\u5230\u5916\u9762\u53bb\u8d70\u8d70\uff0c\u53ef\u662f\u6211\u60f3\u4e0d\u5230 \u521a\u51fa\u53bb\uff0c\u5c31\u542c\u89c1\u4e2a\u5f88\u53ef\u6015\u7684\u6d88\u606f\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u6d88\u606f\uff1f\u201d \u3000\u3000\u201c\u6211\u542c\u8bf4\u4e01\u59d1\u5a18\u8981\u6210\u4eb2\u4e86\u3002\u201d\u53f6\u5f00\u7b11\u5f97\u5f88\u52c9\u5f3a\uff1a\u201c\u8fd9\u6d88\u606f\u5e76\u4e0d\u53ef\u6015\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u2026\u2026\u201d\u5d14\u7389\u771f\u53c8\u5782\u4e0b\u5934\uff1a\u201c\u90a3\u65f6\u5019\u6211\u8fd8\u4ee5\u4e3a\u5979\u2026\u2026\u5979\u8981\u5ac1\u7684\u4eba\u662f\u4f60\u3002\u201d \u3000\u3000\u4e00\u4e2a\u5973\u5b69\u5b50\uff0c\u82e5\u662f\u542c\u89c1\u81ea\u5df1\u5fc3\u7231\u7684\u7537\u4eba\u8981\u5a36\u4eb2\u7684\u6d88\u606f\uff0c\u5f53\u7136\u4f1a\u8ba4\u4e3a\u8fd9\u6d88\u606f\u53ef\u6015\u5f97\u5f88\uff1a\u53f6 \u5f00\u4e86\u89e3\u5979\u7684\u5fc3\u60c5\uff0c\u4ed6\u81ea\u5df1\u4e5f\u6709\u8fc7\u8fd9\u79cd\u5fc3\u60c5\u3002 \u3000\u3000\u4ed6\u5df2\u5fcd\u4e0d\u4f4f\u5728\u53f9\u606f\u3002 \u3000\u3000\u201c\u6211\u542c\u89c1\u4e01\u59d1\u5a18\u8981\u5ac1\u7684\u4eba\uff0c\u662f\u4e2a\u53d7\u4e86\u4f24\u7684\u4eba\uff0c\u6211\u66f4\u4ee5\u4e3a\u4ed6\u5c31\u662f\u4f60\u3002\u201d\u5d14\u7389\u771f\u5782\u7740\u5934\u9053\uff1a \u201c\u90a3\u65f6\u6211\u542c\u7f62\u867d\u7136\u96be\u53d7\uff0c\u5374\u53c8\u5e0c\u671b\u80fd\u5728\u559c\u7b75\u4e0a\u518d\u89c1\u4f60\u4e00\u6b21\uff0c\u6240\u4ee5\u6211\u5c31\u4e70\u4e86\u4efd\u793c\uff0c\u9001\u5230\u9e3f\u5bbe\u5ba2 \u6808\u53bb\u3002\u201d \u3000\u3000\u53f6\u5f00\u82e6\u7b11\u3002 \u3000\u3000\u4ed6\u4e5f\u9001\u4e86\u4efd\u793c\u53bb\uff0c\u4e00\u4efd\u5f88\u7279\u522b\u7684\u793c\u3002 \u3000\u3000\u77e5\u9053\u4e01\u7075\u7433\u7684\u5a5a\u8baf\u540e\uff0c\u4ed6\u5c31\u51b3\u5fc3\u8981\u60f3\u6cd5\u5b50\u5c06\u90ed\u5b9a\u7684\u4f24\u6cbb\u597d\u3002 \u3000\u3000\u53ef\u60dc\u4ed6\u81ea\u5df1\u6ca1\u6709\u6cbb\u4f24\u7684\u672c\u4e8b\uff0c\u6240\u4ee5\u4ed6\u5c31\u5728\u4e00\u591c\u95f4\uff0c\u6765\u56de\u8d76\u4e86\u4e03\u767e\u91cc\u8def\uff0c\u628a\u845b\u75c5\u627e\u6765\u3002 \u3000\u3000\u5d14\u7389\u771f\u54ac\u7740\u5634\u5507\uff0c\u53c8\u9053\uff1a\u201c\u53ef\u662f\u5230\u4e86\u665a\u4e0a\uff0c\u6211\u53c8\u4e0d\u6562\u53bb\u559d\u559c\u9152\u4e86\u3002\u201d \u3000\u3000\u201c\u4f60\u4e0d\u6562\uff1f\u201d\u53f6\u5f00\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u4f60\u6015\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u6211\u2026\u2026\u6211\u5ffd\u7136\u53c8\u6015\u89c1\u5230\u4f60\u3002\u201d \u3000\u3000\u201c\u90a3\u65f6\u4f60\u8fd8\u4e0d\u77e5\u9053\u65b0\u90ce\u5b98\u5e76\u4e0d\u662f\u6211\uff1f\u2019\u201d\u6211\u8fd8\u4e0d\u77e5\u9053\u3002\u201d\u5d14\u7389\u771f\u5e7d\u5e7d\u4ed6\u8bf4\u9053\uff1a\u201c\u6240\u4ee5\u6211 \u53c8\u628a\u81ea\u5df1\u5173\u5728\u8fd9\u5c4b\u5b50\u91cc\uff0c\u4e00\u4e2a\u4eba\u4e70\u4e86\u70b9\u9152\uff0c\u8eb2\u5728\u8fd9\u91cc\u559d\uff0c\u6211\u60f3\uff0c\u6211\u4e5f\u53ef\u4ee5\u7b97\u662f\u5728\u559d\u4f60\u4eec\u7684\u559c \u9152\u4e86\u3002\u201d \u3000\u3000\u53f6\u5f00\u770b\u7740\u5979\uff0c\u5fcd\u4e0d\u4f4f\u8f7b\u8f7b\u63e1\u4f4f\u4e86\u5979\u7684\u624b\u3002 \u3000\u3000\u4e16\u4e0a\u5c45\u7136\u8fd8\u6709\u4e2a\u8fd9\u4e48\u6837\u7684\u5973\u5b69\u5b50\uff0c\u5bf9\u4ed6\u6709\u8fd9\u4e48\u6837\u7684\u611f\u60c5\u3002 \u3000\u3000\u4ed6\u5c45\u7136\u4e00\u70b9\u90fd\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u53f6\u5f00\u53ea\u89c9\u5f97\u5fc3\u91cc\u4e00\u9635\u523a\u75db\uff0c\u201c\u6211\u82e5\u77e5\u9053\u4f60\u5728\u8fd9\u91cc\uff0c\u6211\u4e00\u5b9a\u6765\u966a\u4f60\u3002\u201d \u3000\u3000\u5d14\u7389\u771f\u7ec8\u4e8e\u5ae3\u7136\u4e00\u7b11\uff0c\u8fc7\u4e86\u5f88\u4e45\uff0c\u624d\u63a5\u7740\u9053\uff1a\u201c\u6211\u559d\u4e86\u4e00\u70b9\u9152\u540e\uff0c\u53c8\u5fcd\u4e0d\u4f4f\u60f3\u53bb\u770b\u770b\u4f60 \u4e86\u3002\u201d \u3000\u3000\u201c\u4f60\u53bb\u4e86\u6ca1\u6709\uff1f\u201d \u3000\u3000\u201c\u6211\u8fdf\u7591\u4e86\u5f88\u4e45\uff0c\u53cd\u53cd\u590d\u590d\u5730\u62ff\u4e0d\u5b9a\u4e3b\u610f\uff0c\u6211\u65e2\u6015\u770b\u89c1\u4f60\u4eec\u540e\u4f1a\u53d7\u4e0d\u4e86\uff0c\u53ef\u662f\u5c31\u8fd9\u4e48\u6837 \u6c38\u4e0d\u76f8\u89c1\uff0c\u6211\u4e5f\u4e0d\u7518\u5fc3\u3002\u201d \u3000\u3000\u53f6\u5f00\u4e5f\u4e86\u89e3\u8fd9\u79cd\u5fc3\u60c5\uff0c\u4e16\u4e0a\u4e5f\u8bb8\u6ca1\u6709\u4eba\u80fd\u6bd4\u4ed6\u66f4\u4e86\u89e3\u8fd9\u79cd\u5fc3\u60c5\u3002\u201d \u3000\u3000\u5d14\u7389\u771f\u9053\uff1a\u201c\u5230\u6700\u540e\u6211\u7ec8\u4e8e\u62ff\u5b9a\u4e3b\u610f\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u4e3b\u610f\uff1f\u201d \u3000\u3000\u201c\u6211\u5c31\u7b97\u4e0d\u53bb\u559d\u4f60\u4eec\u7684\u559c\u9152\uff0c\u4e5f\u5f97\u5728\u5916\u9762\u5077\u5077\u5730\u770b\u4f60\u4e00\u773c\u3002\u201d \u3000\u3000\u201c\u4f60\u53bb\u4e86\uff1f\u201d \u3000\u3000\u5d14\u7389\u771f\u70b9\u70b9\u5934\u9053\uff1a\u201c\u6628\u5929\u662f\u5927\u5e74\u521d\u4e00\uff0c\u5230\u4e86\u665a\u4e0a\uff0c\u8857\u4e0a\u51e0\u4e4e\u8fde\u4e00\u4e2a\u4eba\u90fd\u6ca1\u6709\uff0c\u6211\u5728\u8857\u4e0a \u901b\u4e86\u5f88\u4e45\uff0c\u624d\u9f13\u8d77\u52c7\u6c14\uff0c\u4ece\u5ba2\u6808\u540e\u9762\u6e9c\u4e86\u8fdb\u53bb\uff0c\u4e00\u8fdb\u53bb\u6211\u5c31\u77e5\u9053\u4e0d\u5bf9\u4e86\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4ec0\u4e48\u5730\u65b9\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u5d14\u7389\u771f\u9053\uff1a\u201c\u90a3\u4e48\u5927\u7684\u5ba2\u6808\u91cc\u7adf\u8fde\u4e00\u70b9\u58f0\u97f3\u90fd\u6ca1\u6709\uff0c\u975e\u4f46\u4e00\u70b9\u4e5f\u4e0d\u50cf\u6709\u4eba\u5728\u529e\u559c\u4e8b\uff0c\u5c31 \u662f\u529e\u4e27\u4e8b\u7684\u4eba\u5bb6\uff0c\u4e5f\u6ca1\u6709\u90a3\u4e48\u9759\u3002\u201d \u3000\u3000\u53f6\u5f00\u4e5f\u542c\u51fa\u4e0d\u5bf9\u4e86\uff0c\u7acb\u523b\u95ee\u9053\uff1a\u201c\u6211\u77e5\u9053\u53bb\u559d\u559c\u9152\u7684\u4eba\u4e0d\u5c11\uff0c\u600e\u4e48\u4f1a\u8fde\u4e00\u70b9\u58f0\u97f3\u90fd\u6ca1 \u6709\uff1f\u201d \u3000\u3000\u5d14\u7389\u771f\u9053\uff1a\u201c\u6211\u627e\u5230\u4e86\u529e\u559c\u4e8b\u7684\u90a3\u4e2a\u5927\u5385\uff0c\u4ece\u7a97\u53e3\u5f80\u91cc\u9762\u4e00\u770b\u2026\u2026\u201d \u3000\u3000\u5979\u8138\u4e0a\u5ffd\u7136\u9732\u51fa\u79cd\u53d7\u4e86\u6781\u5ea6\u60ca\u5413\u7684\u8868\u60c5\uff0c\u5c31\u597d\u50cf\u53c8\u770b\u5230\u4e86\u5f53\u65f6\u90a3\u79cd\u60e8\u4e0d\u5fcd\u7779\u7684\u60c5\u666f\u3002 \u3000\u3000\u53f6\u5f00\u7684\u5fc3\u4e5f\u5728\u5f80\u4e0b\u6c89\uff0c\u5fcd\u4e0d\u4f4f\u53c8\u95ee\u9053\uff1a\u201c\u4f60\u770b\u89c1\u4e86\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u5d14\u7389\u771f\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u2026\u2026\u201d \u3000\u3000\u5979\u7684\u58f0\u97f3\u4e5f\u5728\u53d1\u6296\uff0c\u8fc7\u4e86\u5f88\u4e45\uff0c\u624d\u80fd\u8bf4\u51fa\u8bdd\u6765\uff1a\u201c\u6211\u53ea\u770b\u89c1\u559c\u5802\u91cc\u5230\u5904\u5168\u662f\u8840\uff0c\u5168\u662f\u6b7b \u4eba\uff0c\u7adf\u8fde\u4e00\u4e2a\u6d3b\u7740\u7684\u90fd\u6ca1\u6709\u3002\u201d \u3000\u3000\u53f6\u5f00\u6014\u4f4f\uff0c\u6574\u4e2a\u4eba\u4eff\u4f5b\u5ffd\u7136\u53c8\u6c89\u4eba\u4e07\u52ab\u4e0d\u590d\u7684\u9ed1\u6697\u4e2d\u3002 \u3000\u3000\u201c\u5f53\u65f6\u6211\u8fd8\u4ee5\u4e3a\u4f60\u4e5f\u5728\u91cc\u9762\uff0c\u6240\u4ee5\u6211\u7acb\u523b\u5c31\u4e0d\u987e\u4e00\u5207\u51b2\u4e86\u8fdb\u53bb\u3002\u201d \u3000\u3000\u5979\u8f7b\u8f7b\u5410\u51fa\u53e3\u6c14\uff0c\u63a5\u7740\u9053\uff1a\u201c\u76f4\u5230\u90a3\u65f6\uff0c\u6211\u624d\u77e5\u9053\u4e01\u59d1\u5a18\u8981\u5ac1\u7684\u4eba\u5e76\u4e0d\u662f\u4f60\u3002\u201d \u3000\u3000\u201c\u4f60\u2026\u2026\u4f60\u770b\u89c1\u4e86\u90a3\u4e2a\u65b0\u90ce\u5b98\uff1f\u201d\u53f6\u5f00\u7684\u58f0\u97f3\u4e5f\u5728\u53d1\u6296\uff1a\u201c\u4ed6\u4e5f\u6b7b\u4e86\uff1f\u201d \u3000\u3000\u5d14\u7389\u771f\u70b9\u4e86\u70b9\u5934\uff0c\u9eef\u7136\u9053\uff1a\u201c\u4ed6\u6b7b\u5f97\u5f88\u60e8\u3002\u201d \u3000\u3000\u201c\u4e01\u7075\u7433\u5462\uff1f\u201d\u53f6\u5f00\u867d\u7136\u4e0d\u6562\u95ee\uff0c\u5374\u8fd8\u662f\u5fcd\u4e0d\u4f4f\u8981\u95ee\uff1a\u201c\u5979\u662f\u4e0d\u662f\u4e5f\u2026\u2026\u201d \u3000\u3000\u5d14\u7389\u771f\u9053\uff1a\u201c\u5979\u6ca1\u6709\u6b7b\uff0c\u5f53\u65f6\u5979\u6839\u672c\u4e0d\u5728\u90a3\u559c\u5802\u91cc\u3002\u201d \u3000\u3000\u53f6\u5f00\u4e5f\u4e0d\u7981\u5410\u51fa\u53e3\u6c14\uff0c\u5374\u53c8\u4e0d\u7981\u89c9\u5f97\u5947\u602a\uff0c\u4ed6\u548c\u4e01\u7075\u7433\u5206\u624b\u4e4b\u5507\uff0c\u96be\u9053\u5979\u7adf\u6ca1\u6709\u56de\u53bb\uff1f \u3000\u3000\u90ed\u5b9a\u4ed6\u4eec\u53c8\u662f\u600e\u4e48\u6b7b\u7684\uff1f\u662f\u8c01\u4e0b\u7684\u6bd2\u624b\uff1a\u5f53\u65f6\u5728\u559c\u5802\u4e2d\u7684\u4eba\u5e76\u4e0d\u5c11\uff0c\u80fd\u4e0b\u5f97\u4e86\u8fd9\u79cd\u6bd2\u624b \u7684\u4eba\u5e76\u4e0d\u591a\u3002 \u3000\u3000\u5d14\u7389\u771f\u9053\uff1a\u201c\u5f53\u65f6\u6211\u867d\u7136\u53c8\u5403\u60ca\uff0c\u53c8\u5bb3\u6015\uff0c\u53ef\u662f\u770b\u89c1\u4f60\u4e0d\u5728\u91cc\u9762\uff0c\u6211\u603b\u7b97\u677e\u4e86\u53e3\u6c14\u3002\u201d \u3000\u3000\u53f6\u5f00\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u4f60\u6709\u6ca1\u6709\u770b\u89c1\u56db\u4e2a\u9ec4\u8863\u4eba\u7684\u5c38\u4f53\uff1f\u201d \u3000\u3000\u5d14\u7389\u771f\u9053\uff1a\u201c\u6211\u6ca1\u6709\u6ce8\u610f\u522b\u4eba\uff0c\u4e5f\u4e0d\u6562\u4ed4\u7ec6\u53bb\u770b\u3002\u201d\u5979\u60f3\u4e86\u60f3\uff0c\u53c8\u9053\uff1a\u201c\u90a3\u4e9b\u5c38\u4f53\u91cc \u9762\uff0c\u597d\u50cf\u662f\u6709\u51e0\u4e2a\u7a7f\u7740\u9ec4\u8863\u670d\u7684\u4eba\u3002\u201d \u3000\u3000\u53f6\u5f00\u76b1\u8d77\u7709\uff1a\u201c\u4ed6\u4eec\u82e5\u662f\u4e5f\u6b7b\u4e86\uff0c\u51f6\u624b\u4f1a\u662f\u8c01\u5462\uff1f\u201d \u3000\u3000\u5d14\u7389\u771f\u9053\uff1a\u201c\u6211\u4e5f\u60f3\u4e0d\u900f\uff0c\u4e16\u4e0a\u600e\u4f1a\u6709\u8fd9\u4e48\u5fc3\u72e0\u624b\u8fa3\u7684\u4eba\uff0c\u5f53\u65f6\u6211\u53ea\u60f3\u8d76\u5feb\u79bb\u5f00\u90a3\u5730 \u65b9\uff0c\u8c01\u77e5\u6211\u521a\u60f3\u8d70\u7684\u65f6\u5019\uff0c\u5ffd\u7136\u542c\u89c1\u5916\u9762\u6709\u591c\u884c\u4eba\u7684\u8863\u8882\u5e26\u98ce\u58f0\u3002\u201d \u3000\u3000\u5979\u63a5\u7740\u53c8\u9053\uff1a\u201c\u56e0\u4e3a\u90a3\u5730\u65b9\u5b9e\u5728\u592a\u9759\uff0c\u6240\u4ee5\u6211\u542c\u5f97\u5f88\u6e05\u695a\uff0c\u6765\u7684\u4eba\u975e\u4f46\u8eab\u6cd5\u90fd\u5f88\u5feb\uff0c\u800c \u4e14\u8fd8\u4e0d\u6b62\u4e00\u4e2a\u4eba\u3002\u201d \u3000\u3000\u53f6\u5f00\u52a8\u5bb9\u9053\uff1a\u201c\u83ab\u975e\u662f\u90a3\u4e9b\u51f6\u624b\u53c8\u56de\u6765\u4e86\uff1f\u201d \u3000\u3000\u5d14\u7389\u771f\u9053\uff1a\u201c\u5f53\u65f6\u6211\u4e5f\u8fd9\u4e48\u60f3\uff0c\u6240\u4ee5\u5413\u5f97\u8fde\u8d70\u90fd\u4e0d\u6562\u8d70\u4e86\uff0c\u66f4\u4e0d\u6562\u7559\u5728\u90a3\u91cc\uff0c\u8ba9\u4ed6\u4eec\u770b \u89c1\uff0c\u5e78\u597d\u6211\u8fd8\u6709\u70b9\u6b66\u529f\uff0c\u60c5\u6025\u4e4b\u4e0b\uff0c\u6b66\u529f\u597d\u50cf\u53cd\u800c\u6bd4\u5e73\u65f6\u597d\u4e86\u4e9b\uff0c\u5c45\u7136\u4e00\u8df3\u5c31\u8df3\u8d77\u6765\u5f88 \u9ad8\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff0c\u201c\u4f60\u662f\u4e0d\u662f\u8df3\u4e0a\u4e86\u5927\u5385\u91cc\u7684\u90a3\u6839\u6a2a\u6881\uff1f\u201d \u3000\u3000\u5d14\u7389\u771f\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6211\u8eb2\u5728\u4e0a\u9762\uff0c\u8fde\u6c14\u90fd\u4e0d\u6562\u5598\uff0c\u5374\u53c8\u5fcd\u4e0d\u4f4f\u60f3\u5f80\u4e0b\u9762\u770b\u770b\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u770b\u89c1\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5d14\u7389\u771f\u9053\uff1a\u201c\u6211\u770b\u89c1\u4e86\u51e0\u4e2a\u7a7f\u7740\u9ec4\u8863\u670d\u7684\u4eba\uff0c\u4ece\u5916\u9762\u4e00\u7a9c\u8fdb\u6765\uff0c\u7acb\u523b\u5c31\u5c06\u5730\u4e0a\u7684\u6b7b\u4eba\uff0c \u4e00\u4e2a\u4e2a\u629b\u51fa\u4e86\u7a97\u5916\uff0c\u7a97\u5916\u597d\u50cf\u6709\u4eba\u5728\u7528\u4e1c\u897f\u63a5\u7740\uff0c\u4e0d\u5230\u7247\u523b\uff0c\u5c4b\u5b50\u91cc\u7684\u6b7b\u4eba\u5c45\u7136\u5168\u90fd\u88ab\u4ed6\u4eec \u642c\u7a7a\u4e86\u3002\u201d \u3000\u3000\u53f6\u5f00\u7684\u8138\u5df2\u53d1\u9752\uff1a\u201c\u4f60\u770b\u6e05\u695a\u4ed6\u4eec\u8eab\u4e0a\u7a7f\u7684\u662f\u9ec4\u8863\u670d\uff1f\u201d \u3000\u3000\u5d14\u7389\u771f\u9053\uff1a\u201c\u6211\u770b\u5f97\u5f88\u6e05\u695a\uff0c\u56e0\u4e3a\u4ed6\u4eec\u7684\u8863\u670d\u9ec4\u5f97\u5f88\u7279\u522b\uff0c\u5728\u706f\u5149\u4e0b\u770b\u8d77\u6765\uff0c\u5c31\u597d\u50cf\u6709 \u91d1\u5149\u5728\u95ea\u52a8\u7740\u4e00\u6837\u3002\u201d \u3000\u3000\u53f6\u5f00\u63e1\u7d27\u53cc\u62f3\uff1a\u9053\uff1a\u201c\u679c\u7136\u662f\u4ed6\u4eec\u4e0b\u7684\u6bd2\u624b\u3002\u201d\u5d14\u7389\u771f\u9053\uff1a\u201c\u53ef\u662f\u6211\u5e76\u6ca1\u6709\u770b\u89c1\u4ed6\u4eec\u6740 \u4eba\u3002\u201d \u3000\u3000\u53f6\u5f00\u51b7\u51b7\u9053\uff1a\u201c\u4eba\u82e5\u4e0d\u662f\u4ed6\u4eec\u6740\u7684\uff0c\u4ed6\u4eec\u4e3a\u4ec0\u4e48\u8981\u66ff\u522b\u4eba\u6536\u5c38\uff1f\u201d \u3000\u3000\u5d14\u7389\u771f\u9053\uff1a\u201c\u4ed6\u4eec\u6740\u4e86\u4eba\u540e\uff0c\u96be\u9053\u8fd8\u60f3\u6bc1\u5c38\u706d\u8ff9\uff1f\u201d \u3000\u3000\u53f6\u5f00\u6068\u6068\u9053\uff1a\u201c\u6740\u4eba\u706d\u53e3\uff0c\u6bc1\u5c38\u706d\u8ff9\uff0c\u672c\u5c31\u662f\u91d1\u94b1\u5e2e\u7684\u4e00\u8d2f\u4f5c\u98ce\u3002\u201d \u3000\u3000\u5d14\u7389\u771f\u9053\uff1a\u201c\u91d1\u94b1\u5e2e\uff1f\u2026\u2026\u91d1\u94b1\u5e2e\u53c8\u662f\u4e9b\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4ed6\u4eec\u4e0d\u662f\u4eba\u3002\u201d \u3000\u3000\u5d14\u7389\u771f\u770b\u7740\u4ed6\u8138\u4e0a\u7684\u6124\u6012\u4e4b\u8272\uff0c\u4e5f\u4e0d\u6562\u518d\u95ee\u4e0b\u53bb\uff0c\u8fdf\u7591\u4e86\u534a\u664c\u7ec8\u4e8e\u9053\uff1a\u201c\u540e\u6765\u6211\u53c8\u770b\u89c1 \u4e86\u4e01\u59d1\u5a18\u3002\u201d \u3000\u3000\u53f6\u5f00\u5931\u58f0\u9053\uff1a\u201c\u4f60\u5728\u54ea\u91cc\u770b\u89c1\u5979\u7684\uff1f\u201d \u3000\u3000\u5d14\u7389\u771f\u9053\uff1a\u201c\u5c31\u5728\u90a3\u91cc\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u5979\u53c8\u56de\u53bb\u4e86\uff1f\u201d \u3000\u3000\u5d14\u7389\u771f\u9053\uff1a\u201c\u90a3\u4e9b\u9ec4\u8863\u4eba\u628a\u5c38\u4f53\u642c\u7a7a\u4e4b\u540e\uff0c\u5979\u5c31\u53bb\u4e86\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u90a3\u65f6\u4f60\u8fd8\u6ca1\u6709\u8d70\uff1f\u201d \u3000\u3000\u5d14\u7389\u771f\u9053\uff1a\u201c\u90a3\u65f6\u5019\u6211\u6574\u4e2a\u4eba\u90fd\u5df2\u5413\u5f97\u53d1\u8f6f\uff0c\u5728\u5927\u6881\u4e0a\u803d\u4e86\u534a\u5929\uff0c\u521a\u5598\u8fc7\u4e00\u53e3\u6c14\uff0c\u4ed6\u4eec \u5c31\u6765\u4e86\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4ed6\u4eec\uff1f\u5979\u4e0d\u662f\u4e00\u4e2a\u4eba\u53bb\u7684\uff1f\u201d \u3000\u3000\u5d14\u7389\u771f\u9053\uff1a\u201c\u53bb\u7684\u6709\u4e24\u4e2a\u4eba\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u8fd8\u6709\u4e2a\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u5d14\u7389\u771f\u9053\uff1a\u201c\u662f\u4e2a\u5947\u5f62\u602a\u72b6\u7684\u8001\u5934\u5b50\uff0c\u534a\u591c\u91cc\u624b\u91cc\u8fd8\u62ff\u7740\u628a\u96e8\u4f1e\u3002\u201d \u3000\u3000\u53f6\u5f00\u604d\u7136\uff0c\u9053\uff1a\u201c\u662f\u845b\u75c5\u3002\u201d \u3000\u3000\u5d14\u7389\u771f\u9053\uff1a\u201c\u4f60\u8ba4\u5f97\u4ed6\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4e0d\u4f46\u8ba4\u5f97\uff0c\u800c\u4e14\u8fd8\u662f\u8001\u670b\u53cb\u3002\u201d \u3000\u3000\u5d14\u7389\u771f\u53c8\u4e0d\u7981\u53f9\u4e86\u53e3\u6c14\u3002\u9053\uff1a\u201c\u90a3\u4e48\u73b0\u5728\u4f60\u7684\u8001\u670b\u53cb\u53c8\u5c11\u4e86\u4e00\u4e2a\u3002\u201d \u3000\u3000\u53f6\u5f00\u53d8\u8272\u9053\uff1a\u201c\u4ed6\u4e5f\u6b7b\u4e86\uff1f\u201d \u3000\u3000\u5d14\u7389\u771f\u9eef\u7136\u9053\uff1a\u201c\u6b7b\u5f97\u4e5f\u5f88\u60e8\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u662f\u8c01\u6740\u4e86\u4ed6\uff1f\u662f\u8c01\u4e0b\u7684\u6bd2\u624b\uff1f\u201d \u3000\u3000\u5d14\u7389\u771f\u9053\uff1a\u201c\u4ed6\u4eec\u770b\u89c1\u5c38\u8eab\u88ab\u642c\u7a7a\uff0c\u4e5f\u89c9\u5f97\u5f88\u610f\u5916\uff0c\u53ef\u662f\u4ed6\u4eec\u5e76\u6ca1\u6709\u505c\u7559\uff0c\u4e5f\u6ca1\u6709\u53d1\u73b0 \u6881\u4e0a\u8fd8\u6709\u522b\u4eba\u5728\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u540e\u6765\u5462\uff1f\u201d \u3000\u3000\u5d14\u7389\u771f\u9053\uff1a\u201c\u4ed6\u4eec\u4e00\u8d70\uff0c\u6211\u5c31\u6e9c\u4e86\u4e0b\u53bb\uff0c\u5ffd\u7136\u542c\u5230\u5916\u9762\u6709\u4eba\u5728\u5439\u7b1b\u5b50\uff0c\u4ed6\u4eec\u542c\u4e86\u8fd9\u7b1b \u58f0\uff0c\u4e5f\u8d76\u4e86\u56de\u6765\uff0c\u5728\u9662\u5b50\u91cc\u770b\u4e86\u770b\uff0c\u5c31\u8d8a\u5899\u800c\u51fa\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u5462\uff1f\u201d \u3000\u3000\u5d14\u7389\u771f\u9053\uff1a\u201c\u6211\u6ca1\u6709\u8ddf\u8fc7\u53bb\uff0c\u53ea\u4e0d\u8fc7\u8eb2\u5728\u5899\u5934\u5f80\u5916\u9762\u770b\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u53c8\u770b\u89c1\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5d14\u7389\u771f\u9053\uff1a\u201c\u5916\u9762\u4e00\u68f5\u6811\u4e0a\uff0c\u597d\u50cf\u6302\u7740\u76cf\u706f\u7b3c\uff0c\u4e0b\u9762\u8fd8\u7ad9\u7740\u4e2a\u4eba\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u5d14\u7389\u771f\u9053\uff1a\u201c\u6211\u9694\u5f97\u592a\u8fdc\uff0c\u6839\u672c\u770b\u4e0d\u6e05\u695a\uff0c\u5e78\u597d\u5f53\u65f6\u56db\u4e0b\u4e00\u70b9\u58f0\u97f3\u90fd\u6ca1\u6709\uff0c\u6240\u4ee5\u4ed6\u4eec\u8bf4 \u8bdd\u7684\u58f0\u97f3\uff0c\u6211\u5012\u5168\u90fd\u542c\u89c1\u4e86\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4ed6\u4eec\u8bf4\u4e86\u4e9b\u4ec0\u4e48\u3002\u201d \u3000\u3000\u5d14\u7389\u771f\u9053\uff1a\u201c\u4e01\u59d1\u5a18\u8fc7\u53bb\u540e\uff0c\u597d\u50cf\u60ca\u53eb\u4e86\u4e00\u58f0\uff0c\u7136\u540e\u5c31\u95ee\u90a3\u4e2a\u4eba\uff0c\u662f\u4e0d\u662f\u5e03\u2026\u2026\u201d \u3000\u3000\u53f6\u5f00\u52a8\u5bb9\u9053\uff1a\u201c\u5e03\u8fbe\u62c9\uff1f\u201d \u3000\u3000\u5d14\u7389\u771f\u7acb\u523b\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5e03\u8fbe\u62c9\uff0c\u4e01\u59d1\u5a18\u8bf4\u7684\u5c31\u662f\u8fd9\u4e09\u4e2a\u5b57\u3002\u201d \u3000\u3000\u53f6\u5f00\u7acb\u523b\u8ffd\u95ee\uff1a\u201c\u90a3\u4e2a\u4eba\u600e\u4e48\u8bf4\uff1f\u201d \u3000\u3000\u5d14\u7389\u771f\u9053\uff1a\u201c\u4ed6\u627f\u8ba4\u4e86\uff0c\u8fd8\u8bf4\u81ea\u5df1\u662f\u5ea7\u5f88\u9ad8\u7684\u5c71\u5cf0\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u5b64\u5cf0\u5929\u7389\u3002\u201d \u3000\u3000\u5d14\u7389\u771f\u9053\uff1a\u201c\u540e\u6765\u6211\u624d\u77e5\u9053\uff0c\u90a3\u4e2a\u4eba\u5c31\u662f\u9b54\u6559\u4e2d\u7684\u56db\u5927\u5929\u738b\u4e4b\u4e00\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u845b\u75c5\u5c31\u662f\u6b7b\u5728\u4ed6\u624b\u91cc\u7684\uff1f\u5d14\u7389\u771f\u9053\uff1a\u201c\u845b\u8001\u5148\u751f\u662f\u4e3a\u4e86\u6551\u4e01\u59d1\u5a18\uff0c\u624d\u88ab\u4ed6\u638c \u529b\u6240\u4f24\uff0c\u53ef\u662f\u4ed6\u4e5f\u4e2d\u4e86\u845b\u8001\u5148\u751f\u7684\u6697\u5668\uff0c\u6211\u542c\u845b\u8001\u5148\u751f\u544a\u8bc9\u4e01\u59d1\u5a18\uff0c\u90a3\u662f\u79cd\u5f88\u5389\u5bb3\u7684\u6697 \u5668\u3002\u201d \u3000\u3000\u5979\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u53ef\u662f\u4ed6\u7684\u638c\u529b\u66f4\u53ef\u6015\uff0c\u845b\u8001\u5148\u751f\u53ea\u88ab\u4ed6\u8f7b\u8f7b\u62cd\u4e86\u4e00\u638c\uff0c\u5c31\u5df2\u65e0\u6551 \u4e86\u3002\u201d \u3000\u3000\u53f6\u5f00\u53c8\u6014\u4f4f\u3002 \u3000\u3000\u4ed6\u4e86\u89e3\u845b\u75c5\u7684\u6b66\u529f\uff0c\u4e5f\u4e86\u89e3\u845b\u75c5\u7684\u533b\u9053\u3002\u4ee5\u8fd9\u79cd\u6b66\u529f\u548c\u533b\u9053\uff0c\u5c31\u7b97\u6709\u4eba\u80fd\u51fb\u4f24\u4ed6\uff0c\u4ed6\u81ea \u5df1\u4e5f\u80fd\u6551\u5f97\u4e86\u81ea\u5df1\u7684\u3002 \u3000\u3000\u53f6\u5f00\u5b9e\u5728\u4e0d\u80fd\u76f8\u4fe1\uff0c\u4e16\u4e0a\u7ade\u6709\u5982\u6b64\u53ef\u6015\u7684\u638c\u529b\uff0c\u7adf\u80fd\u4e00\u638c\u5c31\u62cd\u6563\u845b\u75c5\u7684\u9b42\u9b44\u3002 \u3000\u3000\u201c\u53ef\u662f\u6211\u4eb2\u773c\u770b\u89c1\u845b\u8001\u5148\u751f\u5012\u4e0b\u53bb\u7684\uff0c\u5c31\u5012\u5728\u7b2c\u4e00\u4e2a\u65b0\u90ce\u5b98\u5012\u4e0b\u53bb\u7684\u5730\u65b9\u3002\u201d \u3000\u3000\u5979\u8bdd\u4e2d\u663e\u7136\u8fd8\u6709\u8bdd\u4e00\u4e00\u9664\u4e86\u7b2c\u4e00\u4e2a\u65b0\u90ce\u5b98\uff0c\u96be\u9053\u8fd8\u4f1a\u6709\u7b2c\u4e8c\u4e2a\uff1f \u3000\u3000\u8fd9\u4ef6\u4e8b\u522b\u4eba\u8fde\u505a\u68a6\u90fd\u4e0d\u4f1a\u60f3\u5230\u3002 \u3000\u3000\u53ef\u662f\u53f6\u5f00\u5374\u60f3\u5230\u4e86\uff0c\u4ed6\u4e86\u89e3\u4e01\u7075\u7433\uff0c\u5c31\u597d\u50cf\u4e86\u89e3\u81ea\u5df1\u7684\u624b\u638c\u4e00\u6837\uff0c\u6240\u4ee5\u5d14\u7389\u771f\u8bf4\u51fa\u4e86\u5979 \u6240\u770b\u89c1\u7684\u4e8b\uff0c\u53f6\u5f00\u5e76\u4e0d\u89c9\u5f97\u610f\u5916\u3002 \u3000\u3000\u610f\u5916\u7684\u53cd\u800c\u662f\u5d14\u7389\u771f\u3002\u5979\u672c\u6765\u4ee5\u4e3a\u65e0\u8bba\u8c01\u542c\u89c1\u8fd9\u79cd\u4e8b\uff0c\u90fd\u96be\u514d\u6709\u4e9b\u7279\u522b\u7684\u53cd\u5e94\u3002 \u3000\u3000\u4f46\u53f6\u5f00\u5374\u53ea\u662f\u8f7b\u8f7b\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u77e5\u9053\u5979\u4e00\u5b9a\u4f1a\u8fd9\u4e48\u6837\u505a\u7684\u3002\u201d \u3000\u3000\u5d14\u7389\u771f\u5fcd\u4e0d\u4f4f\u9053\uff1a\u201c\u4f60\u4e0d\u602a\u5979\uff1f\u201d \u3000\u3000\u53f6\u5f00\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u4f60\u82e5\u662f\u5979\uff0c\u6211\u76f8\u4fe1\u4f60\u4e00\u5b9a\u4e5f\u4f1a\u8fd9\u4e48\u6837\u505a\u7684\uff0c\u56e0\u4e3a\u4f60\u4eec\u90fd\u662f\u5fc3\u5730\u5584\u826f \u7684\u5973\u5b69\u5b50\uff0c\u4f60\u4eec\u90fd\u5b81\u613f\u727a\u7272\u81ea\u5df1\uff0c\u4e5f\u4e0d\u5fcd\u770b\u7740\u522b\u4eba\u53d7\u82e6\u3002\u201d \u3000\u3000\u4ed6\u7684\u58f0\u97f3\u5ffd\u7136\u53d8\u5f97\u5f88\u6e29\u67d4\uff0c\u56e0\u4e3a\u4ed6\u5fc3\u91cc\u53ea\u6709\u7231\u548c\u5173\u5207\uff0c\u5e76\u6ca1\u6709\u5ac9\u5992\u548c\u57cb\u6028\u3002 \u3000\u3000\u5d14\u7389\u771f\u5f53\u7136\u77e5\u9053\u90a3\u662f\u5bf9\u8c01\u7684\u7231\u548c\u5173\u5207\u3002 \u3000\u3000\u5979\u5fcd\u4e0d\u4f4f\u4e5f\u8f7b\u8f7b\u53f9\u4e86\u4e00\u58f0\uff0c\u5782\u4e0b\u5934\uff0c\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u6211\u4e0d\u662f\u5979\uff0c\u6211\u2026\u201d \u3000\u3000\u53f6\u5f00\u6ca1\u6709\u518d\u8ba9\u5979\u8bf4\u4e0b\u53bb\uff0c\u5df2\u6025\u7740\u95ee\u9053\uff1a\u201c\u4f60\u8d70\u7684\u65f6\u5019\uff0c\u5979\u8fd8\u7559\u5728\u706b\u7a9f\u91cc\uff1f\u201d \u3000\u3000\u5d14\u738b\u771f\u70b9\u70b9\u5934\uff0c\u52c9\u5f3a\u7b11\u9053\uff1a\u201c\u4f46\u662f\u4f60\u53ef\u4ee5\u653e\u5fc3\uff0c\u5979\u73b0\u5728\u4e00\u5b9a\u8fd8\u597d\u597d\u5730\u6d3b\u7740\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u56e0\u4e3a\u706b\u7a9f\u91cc\u5e76\u6ca1\u6709\u5979\u7684\u5c38\u9aa8\uff1f\u201d \u3000\u3000\u5d14\u7389\u771f\u9053\uff1a\u201c\u4e5f\u56e0\u4e3a\u5979\u662f\u4e2a\u5584\u826f\u7684\u5973\u5b69\u5b50\uff0c\u5409\u4eba\u81ea\u6709\u5929\u76f8\uff0c\u6211\u76f8\u4fe1\u4f60\u4eec\u5f88\u5feb\u5c31\u4f1a\u518d\u89c1 \u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u8f6c\u8fc7\u5934\uff0c\u4e0d\u5fcd\u518d\u770b\u5979\u7684\u8868\u60c5\u3002 \u3000\u3000\u7a97\u5916\u9633\u5149\u707f\u70c2\uff0c\u6674\u5929\u4eff\u4f5b\u5df2\u5c06\u6765\u4e34\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u7ad9\u8d77\u6765\uff0c\u8d70\u8fc7\u53bb\uff0c\u63a8\u5f00\u7a97\u6237\uff0c\u5583\u5583\u9053\uff1a\u201c\u4e0d\u7ba1\u600e\u4e48\u6837\uff0c\u73b0\u5728\u6211\u603b\u7b97\u5df2\u786e\u5b9a\u4e86\u4e24\u4ef6 \u4e8b\u3002\u201d \u3000\u3000\u5d14\u7389\u771f\u5728\u542c\u7740\u3002 \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4e0d\u7ba1\u90a3\u5e03\u8fbe\u62c9\u5929\u738b\u662f\u4ec0\u4e48\u4eba\uff0c\u73b0\u5728\u4ed6\u4e00\u5b9a\u5df2\u53d7\u4e86\u91cd\u4f24\uff0c\u6211\u5df2\u4e0d\u96be\u627e\u5230\u4ed6\u3002\u201d \u3000\u3000\u5d14\u7389\u771f\u9053\uff1a\u201c\u4f60\u4e00\u5b9a\u8981\u53bb\u627e\u4ed6\uff1f\u201d \u3000\u3000\u53f6\u5f00\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u53ef\u662f\u6211\u8fd8\u8981\u5148\u53bb\u627e\u53e6\u5916\u4e00\u4e2a\u4eba\u3002\u201d \u3000\u3000\u5d14\u738b\u771f\u9053\uff1a\u201c\u627e\u8c01\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u53bb\u627e\u90a3\u6740\u4eba\u7684\u51f6\u624b\u3002\u201d \u3000\u3000\u5d14\u7389\u771f\u53c8\u54ac\u8d77\u4e86\u5634\u5507\uff0c\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u73b0\u5728\u5c31\u8981\u53bb\uff1f\u201d \u3000\u3000\u53f6\u5f00\u786c\u8d77\u4e86\u5fc3\u80a0\uff0c\u9053\uff1a\u201c\u6211\u73b0\u5728\u5c31\u8981\u53bb\uff0c\u4f60\u2026\u2026\u4f60\u53ef\u4ee5\u5728\u8fd9\u91cc\u7b49\u6211\uff0c\u6211\u4f1a\u56de\u6765\u7684\u3002\u201d \u3000\u3000\u4ed6\u7684\u5fc3\u5e76\u4e0d\u592a\u786c\uff0c\u4ed6\u7684\u58f0\u97f3\u5df2\u5636\u54d1\u3002 \u3000\u3000\u5d14\u7389\u771f\u5782\u7740\u5934\uff0c\u770b\u7740\u81ea\u5df1\u811a\u5c16\uff0c\u8fc7\u4e86\u5f88\u4e45\uff0c\u5ffd\u7136\u9053\uff1a\u201c\u4f60\u7528\u4e0d\u7740\u56de\u6765\u4e86\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u6211\u2026\u2026\u6211\u4e0d\u4f1a\u5728\u8fd9\u91cc\u7b49\u4f60\u7684\u3002\u201d \u3000\u3000\u5979\u7684\u58f0\u97f3\u4e5f\u5df2\u5636\u54d1\u98a4\u6296\u3002 \u3000\u3000\u53f6\u5f00\u8fd8\u662f\u5fcd\u4e0d\u4f4f\u56de\u8fc7\u4e86\u5934\uff0c\u53c8\u95ee\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5d14\u7389\u771f\u5934\u5782\u5f97\u66f4\u4f4e\uff0c\u4e00\u5b57\u5b57\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u4e0d\u662f\u5979\uff0c\u6211\u2026\u2026\u201d \u3000\u3000\u5979\u6ca1\u6709\u518d\u8bf4\u4e0b\u53bb\uff0c\u5c31\u53ea\u8fd9\u4e00\u53e5\u8bdd\uff0c\u5df2\u4ee4\u5979\u7684\u5fc3\u90fd\u788e\u4e86\u3002 \u3000\u3000\u53f6\u5f00\u7684\u5fc3\u91cc\u4e5f\u5728\u523a\u75db\uff0c\u201c\u4f60\u8981\u5230\u54ea\u91cc\u53bb\uff1f\u201d \u3000\u3000\u201c\u6211\u6709\u5f88\u591a\u5730\u65b9\u53ef\u53bb\uff0c\u6211\u4e5f\u65e9\u5c31\u60f3\u5230\u5904\u53bb\u770b\u770b\uff0c\u5230\u5904\u53bb\u8d70\u8d70\uff0c\u5c06\u6765\u2026\u2026\u201d\u5979\u52c9\u5f3a\u5fcd\u4f4f\u4e86 \u773c\u6cea\uff0c\u4f5c\u51fa\u4e86\u7b11\u8138\uff1a\u201c\u6211\u8bf4\u4e0d\u5b9a\u4f1a\u627e\u4e2a\u8001\u5b9e\u7684\u7537\u4eba\uff0c\u5ac1\u7ed9\u4ed6\uff0c\u66ff\u4ed6\u751f\u5f88\u591a\u5f88\u591a\u513f\u5b50\uff0c\u4e5f\u8bf4\u4e0d \u5b9a\u4f1a\u5f00\u4e2a\u5c0f\u9152\u5e97\uff0c\u505a\u4e00\u4e2a\u5f53\u7089\u5356\u9152\u7684\u8001\u677f\u5a18\u2026\u2026\u201d \u3000\u3000\u5979\u7684\u5fc3\u5df2\u788e\u6210\u5343\u5343\u4e07\u4e07\u7247\uff0c\u6bcf\u8bf4\u4e00\u4e2a\u5b57\uff0c\u4e00\u7247\u53c8\u788e\u6210\u5343\u5343\u4e07\u4e07\u7247\u3002 \u3000\u3000\u53f6\u5f00\u7b11\u9053\uff1a\u201c\u5230\u90a3\u65f6\u6211\u4e00\u5b9a\u4f1a\u5230\u4f60\u7684\u9152\u5e97\u91cc\u53bb\u5927\u9189\u4e00\u573a\u3002\u201d \u3000\u3000\u4ed6\u5728\u7b11\uff0c\u4ed6\u4e0d\u80fd\u4e0d\u7b11\uff0c\u56e0\u4e3a\u4ed6\u751f\u6015\u81ea\u5df1\u4e00\u505c\u4e0b\u6765\uff0c\u773c\u6cea\u5c31\u4f1a\u6d41\u4e0b\u3002 \u3000\u3000\u5d14\u7389\u771f\u5fae\u7b11\u9053\uff1a\u201c\u5230\u90a3\u65f6\u5019\u6211\u4e00\u5b9a\u4f1a\u66ff\u4f60\u518d\u71ac\u4e00\u9505\u9e21\u7ca5\uff0c\u6709\u71d5\u7a9d\u7684\u9e21\u7ca5\u3002\u201d \u3000\u3000\u5979\u4e5f\u5728\u7b11\u3002\u53ef\u662f\u5979\u7b11\u7684\u65f6\u5019\uff0c\u773c\u6cea\u5df2\u6ef4\u4e0b\u9762\u988a\u2026\u2026 \u3000\u3000\u9633\u5149\u707f\u70c2\u3002 \u3000\u3000\u53f6\u5f00\u5927\u6b65\u8d70\u5728\u9633\u5149\u4e0b\u3002\u4ed6\u8138\u4e0a\u867d\u7136\u8fd8\u6709\u6cea\uff0c\u53ef\u662f\u4ed6\u77e5\u9053\u773c\u6cea\u5c31\u548c\u9c9c\u8840\u4e00\u6837\uff0c\u5728\u9633\u5149\u4e0b\u5f88 \u5feb\u5c31\u4f1a\u5e72\u7684\u3002 \u7b2c\u4e8c\u5341\u4e94\u7ae0\u3000\u60ca\u9b42\u4e00\u5200>> \u53e4\u9f99\u300a\u4e5d\u6708\u9e70\u98de\u300b \u7b2c\u4e8c\u5341\u4e94\u7ae0\u3000\u60ca\u9b42\u4e00\u5200 \u3000\u3000\u6cea\u5df2\u5e72\u4e86\uff0c\u8840\u4e5f\u5df2\u5e72\u4e86\u3002 \u3000\u3000\u6cea\u75d5\u662f\u770b\u4e0d\u89c1\u7684\uff0c\u53ef\u662f\u9c9c\u8840\u7559\u4e0b\u6765\u7684\u75d5\u8ff9\uff0c\u5374\u4e00\u5b9a\u7528\u8840\u6cea\u624d\u6d17\u5f97\u6e05\u3002 \u3000\u3000\u201c\u4ee5\u7259\u8fd8\u7259\uff0c\u4ee5\u8840\u8fd8\u8840\u3002\u201d \u3000\u3000\u53f6\u5f00\u4e00\u5411\u90fd\u662f\u5728\u7528\u201c\u5bbd\u6055\u201d\u6765\u4ee3\u66ff\u62a5\u4ec7\uff0c\u4ed6\u7684\u5200\u4e00\u5411\u4e0d\u662f\u6740\u4eba\u7684\u5200\uff0c\u4f46\u662f\u73b0\u5728\u4ed6\u7684\u5fc3\uff0c \u7ade\u4e5f\u5145\u6ee1\u4e86\u6124\u6012\u548c\u4ec7\u6068\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u53d1\u89c9\u81ea\u5df1\u5c31\u50cf\u662f\u4e00\u4e2a\u53ef\u7b11\u7684\u5c0f\u6728\u5076\uff0c\u4e00\u76f4\u90fd\u88ab\u4eba\u7528\u4e00\u6839\u770b\u4e0d\u89c1\u7684\u7ebf\uff0c\u63d0\u5728\u624b\u91cc\u3002 \u3000\u3000\u4ed6\u4e0d\u613f\u518d\u88ab\u4eba\u8fd9\u4e48\u6837\u611a\u5f04\u4e0b\u53bb\uff0c\u66f4\u4e0d\u613f\u518d\u53d7\u4eba\u5229\u7528\uff1b\u6ca1\u6709\u4eba\u613f\u610f\u505a\u6728\u5076\u7684\uff0c\u65e0\u8bba\u8c01\u7684\u5bb9 \u5fcd\u90fd\u6709\u9650\u5ea6\uff0c\u53f6\u5f00\u4e5f\u4e00\u6837\u3002 \u3000\u3000\u79ef\u96ea\u7684\u5927\u5730\uff0c\u6b63\u5728\u9633\u5149\u4e0b\u9732\u51fa\u5149\u79c3\u79c3\u7684\u9ec4\u571f\u3002\u957f\u5b89\u57ce\u5916\u7684\u5927\u8def\u4e0a\uff0c\u6ce5\u6cde\u5df2\u5e72\uff0c\u5374\u8fd8\u662f\u770b \u4e0d\u89c1\u8d76\u8def\u7684\u4eba\u3002 \u3000\u3000\u6ca1\u6709\u4eba\u613f\u610f\u5728\u5927\u5e74\u521d\u4e8c\u8fd9\u4e00\u5929\u8d76\u8def\u3002 \u3000\u3000\u53ea\u6709\u53f6\u5f00\u3002 \u3000\u3000\u4ed6\u627e\u4e86\u8f86\u8f66\uff0c\u5374\u627e\u4e0d\u5230\u8d76\u8f66\u7684\u4eba\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u4e0d\u5728\u4e4e\uff0c\u4ed6\u5c31\u8eba\u5728\u8fd9\u8f86\u8f7d\u7164\u7684\u5927\u677f\u8f66\u4e0a\uff0c\u4efb\u51ed\u62c9\u8f66\u7684\u9a74\u5b50\u6cbf\u7740\u5927\u8def\u5f80\u524d\u8d70\u3002 \u3000\u3000\u8f66\u4e0a\u7684\u7164\u78b4\u5b50\uff0c\u523a\u5f97\u4ed6\u5168\u8eab\u90fd\u5728\u53d1\u75db\uff0c\u53ef\u662f\u4ed6\u4e5f\u4e0d\u5728\u4e4e\u3002 \u3000\u3000\u62c9\u8f66\u7684\u9a74\u5b50\u8d70\u5f97\u5c45\u7136\u4e0d\u6162\uff0c\u540e\u9762\u6ca1\u6709\u4eba\u7528\u97ad\u5b50\u62bd\u5b83\uff0c\u5b83\u8d70\u5f97\u53cd\u800c\u6bd4\u5e73\u65f6\u66f4\u5e26\u52b2\u3002 \u3000\u3000\u9a74\u5b50\u672c\u5c31\u662f\u8fd9\u79cd\u813e\u6c14\u7684\u3002 \u3000\u3000\u5947\u602a\u7684\u662f\uff0c\u8fd9\u4e16\u4e0a\u6709\u5f88\u591a\u4eba\u7684\u813e\u6c14\uff0c\u4e5f\u8ddf\u9a74\u5b50\u5b8c\u5168\u4e00\u6837\u3002 \u3000\u3000\u53f6\u5f00\u5c45\u7136\u53bb\u4e70\u4e86\u5305\u82b1\u751f\uff0c\u8eba\u5728\u8f66\u4e0a\u6162\u6162\u5730\u5265\u7740\uff0c\u5265\u4e00\u9897\u629b\u8d77\u6765\uff0c\u624d\u7528\u5634\u63a5\u4f4f\uff0c\u6162\u6162\u5730\u5480 \u56bc\u3002 \u3000\u3000\u4ed6\u81ea\u5df1\u4e5f\u4e0d\u77e5\u9053\u8fd9\u662f\u5728\u4ec0\u4e48\u65f6\u5019\u517b\u6210\u7684\u4e60\u60ef\uff0c\u4e5f\u8bb8\u4ed6\u8fd8\u6ca1\u6709\u5fd8\u8bb0\u90a3\u4e2a\u5728\u6740\u4eba\u524d\uff0c\u4e00\u5b9a\u8981 \u5403\u51e0\u9897\u82b1\u751f\u7684\u8def\u5c0f\u4f73\u3002 \u3000\u3000\u53ea\u53ef\u60dc\u6ca1\u6709\u9152\uff0c\u4ed6\u5fd8\u4e86\u4e70\u9152\u3002 \u3000\u3000\u5927\u9189\u4e4b\u540e\uff0c\u7b2c\u4e8c\u5929\u80fd\u559d\u51e0\u676f\u201c\u8fd8\u9b42\u9152\u201d\uff0c\u4eba\u7acb\u523b\u5c31\u4f1a\u89c9\u5f97\u8212\u670d\u4e9b\u3002 \u3000\u3000\u4ed6\u60f3\u5230\u9152\u7684\u65f6\u5019\uff0c\u5c31\u770b\u89c1\u4e00\u89d2\u9752\u5e03\u9152\u65d7\uff0c\u4ece\u524d\u9762\u8def\u65c1\u7684\u67af\u6797\u91cc\u659c\u659c\u6311\u51fa\u3002 \u3000\u3000\u5c31\u7b97\u5728\u5927\u5e74\u521d\u4e8c\uff0c\u4e5f\u5e76\u4e0d\u662f\u7edd\u5bf9\u6ca1\u6709\u4eba\u60f3\u8d5a\u94b1\u7684\u3002 \u3000\u3000\u53f6\u5f00\u7b11\u4e86\uff0c\u5583\u5583\u81ea\u8bed\uff1a\u201c\u770b\u6765\u6211\u7684\u8fd0\u6c14\u5df2\u6e10\u6e10\u53d8\u597d\u4e86\u3002\u201d \u3000\u3000\u60f3\u559d\u9152\u7684\u65f6\u5019\uff0c\u7acb\u523b\u5c31\u53ef\u4ee5\u6709\u9152\uff0c\u8fd9\u79cd\u8fd0\u6c14\u786e\u5b9e\u4e0d\u9519\u3002 \u3000\u3000\u4ed6\u8df3\u8d77\u6765\uff0c\u5c06\u9a74\u8f66\u8d76\u5165\u4e86\u9053\u65c1\uff0c\u6162\u6162\u5730\u8d70\u5165\u90a3\u79ef\u96ea\u7684\u67a3\u6811\u6797\u3002 \u3000\u3000\u6811\u6797\u4e2d\u679c\u7136\u6709\u4e2a\u5c0f\u5c0f\u7684\u9152\u4ead\uff0c\u8fd8\u6709\u4e03\u516b\u4e2a\u4eba\u52a8\u4e5f\u4e0d\u52a8\u5730\u7ad9\u5728\u9152\u4ead\u5916\uff0c\u76f4\u7740\u773c\u775b\uff0c\u5f20\u7740 \u5634\uff0c\u5c31\u597d\u50cf\u4e00\u5806\u6ce5\u4eba\u3002 \u3000\u3000\u5176\u4e2d\u6709\u4e00\u4e2a\u4eba\uff0c\u5934\u4e0a\u7528\u767d\u5e03\u5305\u4f4f\uff0c\u4e00\u770b\u89c1\u53f6\u5f00\u8d70\u4e86\u8fc7\u6765\uff0c\u8138\u4e0a\u5c31\u9732\u51fa\u4e86\u60ca\u9a87\u4e4b\u8272\u3002 \u3000\u3000\u53f6\u5f00\u5374\u7b11\u4e86\u3002 \u3000\u3000\u4ed6\u8ba4\u5f97\u8fd9\u4e2a\u4eba\uff0c\u5c31\u662f\u6628\u5929\u665a\u4e0a\u4e00\u5b9a\u8981\u627e\u4ed6\u62fc\u5200\u7684\u571f\u6d41\u6c13\u3002 \u3000\u3000\u201c\u571f\u8c79\u5b50\uff0c\u571f\u5927\u54e5\u3002\u201d \u3000\u3000\u53f6\u5f00\u5ffd\u7136\u60f3\u8d77\u4e86\u522b\u4eba\u79f0\u547c\u4ed6\u7684\u540d\u5b57\uff0c\u5fae\u7b11\u7740\u8d70\u8fc7\u53bb\uff0c\u9053\uff1a\u201c\u571f\u5927\u54e5\uff0c\u4f60\u7684\u9152\u4e5f\u9192\u4e86\uff1f\u201d \u3000\u3000\u571f\u8c79\u4e8e\u8138\u8272\u53d1\u9752\uff0c\u60f3\u70b9\u70b9\u5934\uff0c\u53ef\u662f\u8116\u5b50\u5374\u4f3c\u5df2\u53d1\u786c\uff0c\u6574\u4e2a\u4eba\u90fd\u597d\u50cf\u786c\u5f97\u50cf\u4e8e\u6ce5\u5df4\u3002 \u3000\u3000\u4e0d\u4f46\u662f\u4ed6\uff0c\u5176\u4ed6\u7684\u516d\u4e03\u4e2a\u4eba\u4e5f\u4e00\u6837\u3002 \u3000\u3000\u53f6\u5f00\u5fae\u7b11\u9053\uff1a\u201c\u6328\u63cd\u7684\u4eba\u6ca1\u6709\u5bb3\u6015\uff0c\u63cd\u4eba\u7684\u4eba\u4e3a\u4ec0\u4e48\u53cd\u800c\u5bb3\u6015\u4e86\uff1f\u662f\u4e0d\u662f\u6211\u7684\u9aa8\u5934\u592a \u786c\uff0c\u628a\u5404\u4f4d\u7684\u624b\u6253\u75db\u4e86\uff1f\u90a3\u5c31\u5b9e\u5728\u62b1\u6b49\u5f97\u5f88\u3002\u201d \u3000\u3000\u4ed6\u6ca1\u6709\u731c\u9519\uff0c\u8fd9\u4e9b\u4eba\u7684\u624b\u679c\u7136\u90fd\u53c8\u9752\u53c8\u80bf\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u7684\u6b66\u529f\u82e5\u662f\u80fd\u7ec3\u5230\u53f6\u5f00\u8fd9\u6837\u5b50\uff0c\u7eb5\u7136\u5728\u70c2\u9189\u5982\u6ce5\u7684\u65f6\u5019\uff0c\u4e5f\u4e00\u6837\u6709\u9632\u8eab\u81ea\u536b\u7684\u672c \u80fd\u3002 \u3000\u3000\u53f6\u5f00\u7b11\u9053\uff1a\u201c\u53ef\u662f\u5404\u4f4d\u7528\u4e0d\u7740\u5bb3\u6015\uff0c\u6211\u5e76\u4e0d\u662f\u6765\u627e\u4f60\u4eec\u9ebb\u70e6\u7684\uff0c\u80fd\u5728\u5783\u573e\u5806\u4e0a\u7761\u4e00\u665a \u4e0a\uff0c\u4e5f\u662f\u86ee\u6709\u8da3\u7684\u4e8b\uff0c\u6211\u6b63\u60f3\u597d\u597d\u7684\u8c22\u8c22\u4f60\u4eec\u3002\u201d \u3000\u3000\u4ed6\u62cd\u4e86\u62cd\u571f\u8c79\u5b50\u7684\u80a9\uff0c\u9053\uff1a\u201c\u6765\uff0c\u8ba9\u6211\u6e05\u4f60\u4eec\u559d\u4e24\u676f\u3002\u201d \u3000\u3000\u571f\u8c79\u5b50\u8138\u4e0a\u7684\u8868\u60c5\u5374\u66f4\u6050\u60e7\u3002 \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u8fd8\u6015\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u571f\u8c79\u5b50\u7ec8\u4e8e\u9053\uff1a\u201c\u8001\u5927\uff0c\u6211\u4eec\u5df2\u77e5\u9053\u4f60\u6709\u79cd\uff0c\u53ea\u4e0d\u8fc7\u6211\u4eec\u6015\u7684\u5012\u4e0d\u662f\u4f60\u3002\u201d \u3000\u3000\u53f6\u5f00\u6014\u4f4f\u3002 \u3000\u3000\u5f04\u4e86\u534a\u5929\uff0c\u4eba\u5bb6\u6015\u7684\u539f\u6765\u5e76\u4e0d\u662f\u4ed6\u3002 \u3000\u3000\u53f6\u5f00\u82e6\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u6015\u7684\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u571f\u8c79\u5b50\u9053\uff1a\u201c\u6211\u4eec\u53ea\u6015\u4f60\u628a\u6211\u4eec\u5934\u4e0a\u7684\u4e1c\u897f\u78b0\u4e0b\u6765\uff0c\u6211\u4eec\u5c31\u771f\u7684\u662f\u6b7b\u8def\u4e00\u6761\u4e86\u3002\u201d \u3000\u3000\u53f6\u5f00\u8fd9\u624d\u53d1\u73b0\uff0c\u8fd9\u4e9b\u4eba\u7684\u5934\u9876\u4e0a\uff0c\u5168\u90e8\u7aef\u7aef\u6b63\u6b63\u7684\u6446\u7740\u4e00\u679a\u94dc\u94b1\u3002 \u3000\u3000\u94dc\u94b1\u5728\u592a\u9633\u4e0b\u95ea\u7740\u5149\uff0c\u5c31\u50cf\u662f\u9ec4\u91d1\u4e00\u6837\u3002 \u3000\u3000\u201c\u91d1\u94b1\u5e2e\u3002\u201d \u3000\u3000\u571f\u8c79\u5b50\u5410\u51fa\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u65e2\u7136\u4e5f\u77e5\u91d1\u94b1\u5e2e\u7684\u89c4\u77e9\uff0c\u6211\u5c31\u653e\u5fc3\u4e86\u3002\u201d \u3000\u3000\u53f6\u5f00\u7728\u4e86\u7728\u773c\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\u89c4\u77e9\uff1f\u201d \u3000\u3000\u5176\u5b9e\u4ed6\u5f53\u7136\u77e5\u9053\u91d1\u94b1\u5e2e\u7684\u89c4\u77e9\u3002 \u3000\u3000\u8fd9\u679a\u94dc\u94b1\uff0c\u5c31\u662f\u4ed6\u4eec\u7684\u4fe1\u7b26\uff0c\u4ed6\u4eec\u82e5\u662f\u628a\u94dc\u94b1\u653e\u5728\u4f60\u5934\u4e0a\uff0c\u4f60\u5c31\u8fde\u4e00\u52a8\u90fd\u4e0d\u80fd\u52a8\u4e86\u3002 \u3000\u3000\u571f\u8c79\u5b50\u9053\uff1a\u201c\u4f60\u771f\u7684\u4e0d\u77e5\u9053\uff1f\u53ea\u8981\u4f60\u628a\u6211\u4eec\u5934\u4e0a\u7684\u94dc\u94b1\u78b0\u4e0b\u6765\uff0c\u6211\u4eec\u5c31\u5f97\u6b7b\uff0c\u4f60\u4e5f\u5f97 \u6b7b\uff0c\u6211\u4eec\u5927\u5bb6\u5c31\u5168\u90fd\u662f\u6b7b\u8def\u4e00\u6761\u3002\u201d \u3000\u3000\u53f6\u5f00\u53c8\u7b11\u4e86\uff0c\u6447\u7740\u5934\uff0c\u7b11\u9053\uff1a\u201c\u54ea\u6709\u8fd9\u4e48\u5927\u7684\u89c4\u77e9\uff1f\u6211\u4e0d\u4fe1\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u4f38\u51fa\u624b\uff0c\u628a\u571f\u8c79\u5b50\u5934\u4e0a\u7684\u94dc\u94b1\u62ff\u4e86\u4e0b\u6765\uff0c\u5583\u5583\u9053\uff1a\u201c\u8fd9\u4e00\u6587\u94b1\u4e0d\u77e5\u9053\u80fd\u4e0d\u80fd\u4e70\u676f \u9152\u559d\u3002\u201d \u3000\u3000\u571f\u8c79\u5b50\u5374\u5df2\u9a87\u50bb\u4e86\uff0c\u5c31\u50cf\u662f\u5ffd\u7136\u88ab\u4eba\u62bd\u4e86\u4e00\u97ad\u5b50\uff0c\u4e24\u6761\u817f\u90fd\u5df2\u53d1\u8f6f\uff0c\u5ffd\u7136\u4e00\u4e0b\u5b50\u5c31\u8dea\u4e86 \u4e0b\u53bb\u3002 \u3000\u3000\u53f6\u5f00\u5374\u597d\u50cf\u6ca1\u770b\u89c1\uff0c\u53c8\u9053\uff1a\u201c\u4e00\u6587\u94b1\u60f3\u5fc5\u4e0d\u591f\u4e70\u9152\u7684\uff0c\u8fd8\u597d\u8fd9\u91cc\u8fd8\u6709\u3002\u201d \u3000\u3000\u4ed6\u8eab\u5b50\u5ffd\u7136\u63a0\u8d77\uff0c\u843d\u4e0b\u6765\u65f6\uff0c\u516d\u4e03\u4e2a\u4eba\u5934\u4e0a\u7684\u94dc\u94b1\uff0c\u5c31\u5168\u5df2\u90fd\u5230\u4e86\u4ed6\u624b\u91cc\u3002 \u3000\u3000\u8fd9\u4e9b\u4eba\u90fd\u9a87\u50bb\u4e86\uff0c\u4ed6\u4eec\u8fd9\u4e00\u8f88\u5b50\uff0c\u4ece\u6765\u4e5f\u6ca1\u770b\u89c1\u8fc7\u8fd9\u4e48\u5feb\u7684\u8eab\u624b\u3002 \u3000\u3000\u571f\u8c79\u5b50\u5ffd\u7136\u8dea\u5728\u5730\u4e0a\u5927\u53eb\uff1a\u201c\u8fd9\u662f\u4ed6\u5e72\u7684\uff0c\u5b8c\u5168\u4e0d\u5173\u6211\u4eec\u7684\u4e8b\u3002\u201d \u3000\u3000\u53f6\u5f00\u5fae\u7b11\uff1a\u201c\u8fd9\u672c\u6765\u5c31\u4e0d\u5173\u4f60\u4eec\u7684\u4e8b\u3002\u201d \u3000\u3000\u4ed6\u62c8\u8d77\u9897\u82b1\u751f\uff0c\u653e\u5728\u571f\u8c79\u5b50\u624b\u91cc\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u571f\u8c79\u5b50\u5f53\u7136\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u8fd9\u610f\u601d\u5c31\u662f\u8bf4\uff0c\u4f60\u4eec\u73b0\u5728\u5df2\u53ef\u4ee5\u7ad9\u8d77\u6765\u53bb\u559d\u9152\u4e86\uff0c\u968f\u4fbf\u5230\u54ea\u91cc\u53bb\u90fd\u884c\uff0c\u91d1\u94b1 \u5e2e\u7684\u4eba\u82e5\u6562\u53bb\u627e\u4f60\u4eec\u7684\u9ebb\u70e6\uff0c\u5c31\u53eb\u4ed6\u4eec\u6765\u627e\u82b1\u751f\u5e2e\u7684\u5e2e\u4e3b\uff0c\u5c31\u8bf4\u82b1\u751f\u5e2e\u7684\u5e2e\u4e3b\uff0c\u5df2\u63a5\u4e0b\u4e86\u8fd9 \u6863\u5b50\u4e8b\u3002\u201d \u3000\u3000\u571f\u8c79\u5b50\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u82b1\u2026\u2026\u82b1\u751f\u5e2e\u7684\u5e2e\u4e3b\u662f\u8c01\uff1f\u201d \u3000\u3000\u53f6\u5f00\u6307\u7740\u81ea\u5df1\u7684\u9f3b\u5b50\uff0c\u9053\uff1a\u201c\u5c31\u662f\u6211\u3002\u201d \u3000\u3000\u571f\u8c79\u5b50\u4e5f\u6014\u4f4f\u4e86\u3002 \u3000\u3000\u7a81\u542c\u4e00\u4e2a\u4eba\u51b7\u51b7\u9053\uff1a\u201c\u5f88\u597d\uff0c\u90a3\u4e48\u6211\u4eec\u73b0\u5728\u8981\u6211\u7684\u5c31\u662f\u4f60\u3002\u201d \u3000\u3000\u51b7\u51b0\u51b0\u7684\u58f0\u97f3\uff0c\u51b7\u51b0\u51b0\u7684\u53e3\u6c14\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u4e5f\u662f\u51b7\u51b0\u51b0\u7684\uff0c\u8721\u9ec4\u7684\u8138\uff0c\u9e5e\u773c\u9e70\u9f3b\uff0c\u8138\u4e0a\u6709\u6761\u5f88\u6df1\u7684\u5200\u75a4\uff0c\u4f7f\u5f97\u4ed6\u770b\u6765\u66f4\u662f\u6ee1 \u8138\u6740\u6c14\u3002 \u3000\u3000\u53f6\u5f00\u5374\u6ca1\u6709\u53bb\u770b\u4ed6\u7684\u8138\u2014\u2014\u53f6\u5f00\u6ce8\u610f\u7684\uff0c\u53ea\u4e0d\u8fc7\u662f\u4ed6\u7684\u8863\u88f3\u3002 \u3000\u3000\u4e00\u8eab\u5f88\u624e\u773c\u7684\u9ec4\u8863\u88f3\uff0c\u5728\u9633\u5149\u4e0b\u770b\u6765\uff0c\u4e5f\u50cf\u662f\u9ec4\u91d1\u4e00\u6837\u3002 \u3000\u3000\u4ed6\u5c31\u5728\u9152\u4ead\u7684\u77f3\u9636\u4e0a\uff0c\u8fd8\u6709\u4e09\u4e2a\u4eba\u7ad9\u5728\u4ed6\u8eab\u65c1\uff0c\u7a7f\u7684\u4e5f\u90fd\u662f\u540c\u6837\u7684\u8863\u88f3\u3002 \u3000\u3000\u53f6\u5f00\u53c8\u5728\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u8eab\u4e0a\u8fd9\u5957\u8863\u88f3\u5012\u4e0d\u9519\uff0c\u4e0d\u77e5\u9053\u80fd\u4e0d\u80fd\u8131\u4e0b\u6765\u7ed9\u6211\uff0c\u6211\u6b63\u597d\u62ff\u53bb \u7ed9\u6211\u90a3\u6761\u9a74\u4e8e\u7a7f\u4e0a\u3002\u201d \u3000\u3000\u9ec4\u8863\u4eba\u77aa\u7740\u4ed6\uff0c\u77b3\u5b54\u5df2\u6536\u7f29\uff0c\u5c45\u7136\u8fd8\u80fd\u6c89\u5f97\u4f4f\u6c14\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u672c\u5e2e\u7684\u89c4 \u77e9\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u521a\u624d\u542c\u8bf4\u3002\u201d \u3000\u3000\u9ec4\u8863\u4eba\u9053\uff1a\u201c\u56db\u5341\u5e74\u6765\uff0c\u6c5f\u6e56\u4e2d\u4ece\u6765\u4e5f\u6ca1\u6709\u4eba\u6562\u89e6\u72af\u8fc7\u672c\u5e2e\u7684\u89c4\u77e9\uff0c\u4f60\u77e5\u4e0d\u77e5\u9053\u662f\u4e3a\u4e86 \u4ec0\u4e48\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u8bf4\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u9ec4\u8863\u4eba\u9053\uff1a\u201c\u53ea\u56e0\u4e3a\u65e0\u8bba\u8c01\u6562\u89e6\u72af\u672c\u5e2e\u7684\u89c4\u77e9\uff0c\u5c31\u5fc5\u6b7b\u65e0\u7591\u3002\u201d \u3000\u3000\u53e6\u4e00\u4e2a\u9ec4\u8863\u4eba\u51b7\u7b11\u9053\uff1a\u201c\u65e0\u8bba\u4f60\u662f\u82b1\u751f\u5e2e\u7684\u5e2e\u4e3b\u4e5f\u597d\uff0c\u662f\u722a\u5b50\u5e2e\u7684\u5e2e\u4e3b\u4e5f\u597d\uff0c\u90fd\u4e00\u6837\u5fc5 \u6b7b\u65e0\u7591\u3002\u201d \u3000\u3000\u53f6\u5f00\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u53ef\u662f\u65e0\u8bba\u4ec0\u4e48\u89c4\u77e9\uff0c\u8fdf\u65e9\u603b\u662f\u8981\u4eba\u72af\u4e00\u72af\u7684\uff0c\u4e5f\u5c31\u597d\u50cf\u5904\u5973\u8fdf\u65e9 \u5f97\u5ac1\u7537\u4eba\u4e00\u6837\u3002\u201d \u3000\u3000\u9ec4\u8863\u4eba\u5bf9\u671b\u4e86\u4e00\u773c\uff0c\u6c89\u7740\u8138\uff0c\u4e00\u6b65\u6b65\u8d70\u4e0b\u53f0\u9636\uff0c\u8d70\u8fc7\u6765\u3002 \u3000\u3000\u56db\u4e2a\u4eba\u7684\u811a\u6b65\u90fd\u5f88\u6c89\u7a33\uff0c\u5c24\u5176\u662f\u90a3\u8138\u5e26\u5200\u75a4\u7684\u5927\u6c49\uff0c\u4e24\u65c1\u592a\u9633\u7a74\u9690\u9690\u51f8\u8d77\uff0c\u4e00\u53cc\u624b\u9752\u7b4b \u66b4\u73b0\uff0c\u663e\u7136\u662f\u5185\u529f\u5f88\u6df1\u7684\u6b66\u6797\u9ad8\u624b\u3002 \u3000\u3000\u53f6\u5f00\u770b\u7740\u4ed6\u7684\u624b\uff0c\u5ffd\u7136\u9053\uff1a\u201c\u9601\u4e0b\u83ab\u975e\u662f\u7ec3\u8fc7\u5927\u9e70\u722a\u529f\u7684\uff1f\u201d\u9ec4\u8863\u4eba\u51b7\u7b11\u3002 \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u770b\u9601\u4e0b\u8138\u4e0a\u8fd9\u6761\u5200\u75a4\uff0c\u83ab\u975e\u5c31\u662f\u6dee\u897f\u7684\u2018\u94c1\u9762\u9e70\u2019\uff1f\u201d \u3000\u3000\u9ec4\u8863\u4eba\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u7684\u773c\u529b\u5012\u4e0d\u9519\u3002\u201d \u3000\u3000\u53f6\u5f00\u5ffd\u7136\u6c89\u4e0b\u8138\uff0c\u9053\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u90ed\u5b9a\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u94c1\u9762\u9e70\u9053\uff1a\u201c\u597d\u50cf\u542c\u8bf4\u8fc7\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4ed6\u662f\u6211\u7684\u670b\u53cb\u3002\u201d \u3000\u3000\u94c1\u9762\u9e70\u9053\uff1a\u201c\u662f\u4f60\u7684\u670b\u53cb\u53c8\u5982\u4f55\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u82b1\u751f\u5e2e\u7684\u89c4\u77e9\uff1f\u201d \u3000\u3000\u94c1\u9762\u9e70\u9053\uff1a\u201c\u4ec0\u4e48\u89c4\u77e9\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u82b1\u751f\u5e2e\u7684\u89c4\u77e9\uff0c\u5c31\u662f\u4e0d\u8bb8\u522b\u4eba\u6740\u6211\u7684\u670b\u53cb\uff0c\u5426\u5219\u2026\u2026\u201d \u3000\u3000\u94c1\u9762\u9e70\u9053\uff1a\u201c\u5426\u5219\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u5c31\u662f\u8fd9\u6837\uff01\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u51fa\u624b\uff0c\u6325\u62f3\u75db\u51fb\u94c1\u9762\u9e70\u7684\u8138\u3002 \u3000\u3000\u94c1\u9762\u9e70\u5e76\u4e0d\u662f\u65e0\u540d\u4e4b\u8f88\uff0c\u4e5f\u4e0d\u662f\u65e0\u80fd\u4e4b\u8f88\uff0c\u4ed6\u4e0d\u4f46\u5728\u6dee\u897f\u4e00\u5e26\u7684\u540d\u5934\u6781\u54cd\uff0c\u5728\u6c5f\u6e56\u4e2d\u4e5f \u53ef\u4ee5\u7b97\u662f\u4e00\u7b49\u4e00\u7684\u597d\u624b\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u7684\u786e\u6709\u771f\u529f\u592b\u3002 \u3000\u3000\u4ed6\u7684\u9e70\u722a\u529f\uff0c\u7684\u786e\u5f97\u8fc7\u201c\u9e70\u722a\u738b\u201d\u95e8\u4e0b\u7684\u771f\u4f20\uff0c\u6614\u5e74\u5728\u5175\u5668\u8c31\u4e0a\u5217\u540d\u7684\u201c\u6dee\u897f\u5927\u5200\u201d\uff0c \u867d\u7136\u4e00\u5200\u780d\u5728\u4ed6\u8138\u4e0a\uff0c\u5c45\u7136\u6ca1\u6709\u780d\u6b7b\u4ed6\uff0c\u6dee\u897f\u5927\u5200\u53cd\u800c\u6b7b\u5728\u4ed6\u7684\u9e70\u722a\u529f\u4e0b\uff0c\u201c\u94e3\u9762\u9e70\u201d\u8fd9\u540d \u5b57\uff0c\u4e5f\u6b63\u662f\u56e0\u6b64\u800c\u6765\u3002 \u3000\u3000\u9e70\u722a\u5feb\uff0c\u9e70\u773c\u4e5f\u5feb\u3002\u53ef\u662f\u7b49\u4ed6\u770b\u5230\u53f6\u5f00\u6325\u62f3\uff0c\u62f3\u5934\u5df2\u75db\u51fb\u5728\u4ed6\u9f3b\u6881\u6b63\u4e2d\u3002 \u3000\u3000\u4ed6\u5e76\u4e0d\u89c9\u5f97\u75db\u3002\u8981\u80fd\u611f\u89c9\u5230\u75db\u82e6\uff0c\u5df2\u7ecf\u662f\u5f88\u4e45\u4ee5\u540e\u7684\u4e8b\u4e86\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u53ea\u89c9\u5f97\u773c\u524d\u5ffd\u7136\u4e00\u9635\u9ed1\u6697\uff0c\u5ffd\u7136\u6709\u65e0\u6570\u9897\u91d1\u661f\uff0c\u4ece\u773c\u524d\u6269\u5f20\u3002 \u3000\u3000\u4ed6\u5e76\u6ca1\u6709\u7acb\u523b\u5012\u4e0b\u53bb\uff0c\u76f4\u7b49\u5230\u5df2\u98de\u51fa\u53bb\u4e00\u4e08\u591a\u8fdc\uff0c\u649e\u5728\u9152\u4ead\u7684\u95e8\u6846\u4e0a\uff0c\u4ed6\u624d\u5012\u4e0b\u53bb\u3002 \u3000\u3000\u4ed6\u4e5f\u6ca1\u6709\u542c\u89c1\u81ea\u5df1\u8138\u4e0a\u9aa8\u5934\u788e\u88c2\u7684\u58f0\u97f3\uff0c\u53ef\u662f\u522b\u7684\u4eba\u5374\u5168\u90fd\u542c\u5f97\u6e05\u6e05\u695a\u695a\u3002 \u3000\u3000\u53f6\u5f00\u770b\u7740\u4ed6\u788e\u88c2\u7684\u8138\uff0c\u6de1\u6de1\u9053\uff1a\u201c\u539f\u6765\u4ed6\u5e76\u4e0d\u662f\u771f\u7684\u94c1\u9762\uff0c\u539f\u6765\u4ed6\u7684\u8138\u4e5f\u4e00\u6837\u53ef\u4ee5\u6253\u70c2 \u7684\u3002\u201d \u3000\u3000\u53e6\u5916\u7684\u4e09\u4e2a\u9ec4\u8863\u4eba\u54ac\u7740\u7259\uff0c\u8fde\u770b\u90fd\u6ca1\u56de\u5934\u53bb\u770b\u4ed6\u4eec\u7684\u540c\u4f34\u3002 \u3000\u3000\u5bd2\u5149\u95ea\u52a8\u7740\uff0c\u4e09\u4e2a\u4eba\u5df2\u540c\u65f6\u4eae\u51fa\u4e86\u5175\u5203\uff0c\u4e00\u628a\u5200\uff0c\u4e00\u53e3\u5251\uff0c\u4e00\u5bf9\u5224\u5b98\u7b14\u3002 \u3000\u3000\u4e09\u4e2a\u4eba\u56db\u4ef6\u5175\u5203\uff0c\u5ffd\u7136\u95f4\u5df2\u5168\u90fd\u5411\u53f6\u5f00\u8eab\u4e0a\u62db\u547c\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u4e24\u62db\u8fc7\u540e\uff0c\u53f6\u5f00\u5df2\u53d1\u73b0\u8fd9\u4e9b\u4eba\u4e2d\u6b66\u529f\u6700\u597d\u7684\uff0c\u5e76\u4e0d\u662f\u94c1\u9762\u9e70\uff0c\u4e5f\u4e0d\u662f\u7528\u5224\u5b98\u7b14\u7684\u8001\u8005\uff0c \u800c\u662f\u4e2a\u4f7f\u5251\u7684\u5e74\u8f7b\u4eba\u3002 \u3000\u3000\u4ed6\u7684\u5251\u6cd5\u8fc5\u6025\u800c\u7280\u5229\uff0c\u53d8\u5316\u5f88\u591a\uff0c\u4ed6\u7528\u7684\u5251\u4e5f\u662f\u7cbe\u54c1\u3002 \u3000\u3000\u5341\u4e09\u62db\u8fc7\u540e\uff0c\u53f6\u5f00\u8fd8\u662f\u6ca1\u6709\u51fa\u624b\u3002 \u3000\u3000\u4ed6\u4e00\u51fa\u624b\u5c31\u7edd\u4e0d\u843d\u7a7a\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u5df2\u51fa\u624b\uff0c\u53ea\u542c\u4e00\u58f0\u60ca\u547c\uff0c\u4e00\u9635\u808b\u9aa8\u6298\u65ad\u58f0\uff0c\u63a5\u7740\u201c\u683c\u201d\u7684\u4e00\u54cd\u3002\u7528\u5224\u5b98\u7b14\u7684\u8001\u8005 \u5df2\u88ab\u70b9\u4f4f\u7a74\u9053\uff0c\u4f7f\u5200\u7684\u5927\u6c49\u624b\u62b1\u808b\u9aa8\uff0c\u5012\u5728\u5730\u4e0a\uff0c\u4e00\u67c4\u5200\u5df2\u88ab\u6298\u65ad\u6210\u4e24\u6bb5\u3002 \u3000\u3000\u53ea\u6709\u4f7f\u5251\u7684\u5e74\u8f7b\u4eba\u6ca1\u6709\u5012\u4e0b\uff0c\u4f46\u8138\u4e0a\u5374\u5df2\u9a87\u5f97\u5168\u65e0\u8840\u8272\u3002 \u3000\u3000\u53f6\u5f00\u968f\u624b\u5c06\u4e24\u622a\u65ad\u5200\u7529\u6389\uff0c\u5ffd\u7136\u95ee\u8fd9\u5e74\u8f7b\u4eba\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u6211\u4e3a\u4ec0\u4e48\u8981\u6298\u65ad\u4ed6\u7684\u5200\uff1f\u201d \u3000\u3000\u5e74\u8f7b\u4eba\u6447\u5934\u3002 \u3000\u3000\u53f6\u5f00\u6de1\u6de1\u9053\uff1a\u201c\u56e0\u4ed6\u51fa\u624b\u592a\u9634\u6bd2\uff0c\u50cf\u4ed6\u8fd9\u79cd\u4eba\uff0c\u6839\u672c\u4e0d\u914d\u7528\u5200\u3002\u201d \u3000\u3000\u5e74\u8f7b\u4eba\u7d27\u63e1\u4ed6\u7684\u5251\uff0c\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u4f60\u4e5f\u7528\u5200\uff1f\u201d \u3000\u3000\u53f6\u5f00\u70b9\u70b9\u5934\u3002 \u3000\u3000\u4e16\u4e0a\u4e5f\u8bb8\u6ca1\u6709\u4eba\u6bd4\u4ed6\u66f4\u61c2\u5f97\u7528\u5200\uff0c\u4e5f\u6ca1\u6709\u4eba\u6bd4\u4ed6\u66f4\u4e86\u89e3\u5200\u7684\u4ef7\u503c\u3002 \u3000\u3000\u201c\u6211\u5bf9\u5200\u4e00\u5411\u5f88\u5c0a\u656c\u3002\u201d\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u82e5\u4e0d\u5c0a\u656c\u4f60\u7684\u5200\uff0c\u5c31\u6839\u672c\u4e0d\u914d\u7528\u5200\uff0c\u4f60\u82e5\u5c0a\u656c\u4f60 \u7684\u5200\uff0c\u7528\u7684\u65f6\u5019\u5c31\u5e94\u8be5\u7279\u522b\u8c28\u614e\u3002\u201d \u3000\u3000\u5e74\u8f7b\u4eba\u770b\u7740\u4ed6\uff0c\u773c\u6674\u91cc\u4e0d\u7981\u9732\u51fa\u60ca\u5f02\u4e4b\u8272\u3002 \u3000\u3000\u4ed6\u5df2\u770b\u51fa\u53f6\u5f00\u4e0d\u662f\u4e2a\u5e73\u51e1\u7684\u4eba\uff0c\u5e73\u51e1\u7684\u4eba\u7edd\u5bf9\u8bf4\u4e0d\u51fa\u8fd9\u79cd\u9053\u7406\u3002 \u3000\u3000\u4ed6\u5fcd\u4e0d\u4f4f\u95ee\uff1a\u201c\u4f60\u7a76\u7adf\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u6211\u59d3\u53f6\uff0c\u53eb\u53f6\u5f00\u3002\u201d \u3000\u3000\u5e74\u8f7b\u4eba\u8138\u8272\u53c8\u53d8\u4e86\uff1a\u201c\u53f6\u5f00\uff01\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u6728\u53f6\u7684\u53f6\uff0c\u5f00\u5fc3\u7684\u5f00\u3002\u201d \u3000\u3000\u5e74\u8f7b\u4eba\u7a81\u7136\u4e00\u4e2a\u5927\u7ffb\u8eab\uff0c\u51cc\u7a7a\u63a0\u8d77\uff0c\u5f80\u4ead\u5916\u7a9c\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u7684\u811a\u521a\u70b9\u5730\uff0c\u5c31\u5ffd\u7136\u542c\u89c1\u6025\u98ce\u4e00\u54cd\uff0c\u5200\u5149\u4e00\u95ea\u3002 \u3000\u3000\u95ea\u7535\u822c\u7684\u5200\u5149\uff0c\u5df2\u4ece\u4ed6\u5934\u9876\u98de\u8fc7\uff0c\u98de\u51fa\u4e94\u516d\u4e08\uff0c\u4f59\u52bf\u672a\u6b47\uff0c\u201c\u593a\u201d\u7684\u4e00\u58f0\u9489\u5728\u4e00\u68f5\u6811 \u4e0a\uff0c\u5200\u950b\u5165\u6728\uff0c\u76f4\u6ca1\u81f3\u67c4\u3002 \u3000\u3000\u5e74\u8f7b\u4eba\u4e00\u60ca\uff0c\u505c\u6b65\uff0c\u5934\u53d1\u5df2\u62ab\u6563\u4e0b\u6765\uff0c\u675f\u53d1\u7684\u91d1\u73af\uff0c\u5df2\u88ab\u524a\u65ad\u3002 \u3000\u3000\u4ed6\u5168\u8eab\u5374\u5df2\u50f5\u786c\u3002 \u3000\u3000\u4ed6\u4ece\u6765\u4e5f\u6ca1\u6709\u89c1\u8fc7\u8fd9\u6837\u5feb\u7684\u5200\u3002 \u3000\u3000\u98de\u5200\uff01 \u3000\u3000\u5200\u67c4\u72b9\u5728\u9707\u98a4\u3002 \u3000\u3000\u53f6\u5f00\u8d70\u8fc7\u53bb\uff0c\u62d4\u51fa\u6765\uff0c\u624b\u8155\u4e00\u7ffb\uff0c\u5200\u5df2\u4e0d\u89c1\u3002 \u3000\u3000\u5e74\u8f7b\u4eba\u8fd9\u624d\u957f\u957f\u5410\u51fa\u53e3\u6c14\uff1a\u201c\u4f60\u771f\u7684\u662f\u53f6\u5f00\uff1f\u201d \u3000\u3000\u201c\u6211\u672c\u6765\u5c31\u662f\u53f6\u5f00\u3002\u201d \u3000\u3000\u5e74\u8f7b\u4eba\u82e6\u7b11\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u65e9\u8bf4\uff1f\u201d \u3000\u3000\u53f6\u5f00\u7b11\u4e86\u7b11\uff0c\u5ffd\u7136\u53cd\u95ee\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u91d1\u575b\u6bb5\u5148\u751f\u7684\u95e8\u4e0b\uff1f\u201d \u3000\u3000\u5e74\u8f7b\u4eba\u53c8\u5403\u4e86\u4e00\u60ca\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\u7684\uff1f\u201d \u3000\u3000\u53f6\u5f00\u5fae\u7b11\u9053\uff1a\u201c\u94c1\u9762\u9e70\u521a\u624d\u5c82\u975e\u4e5f\u8bf4\u8fc7\uff0c\u6211\u7684\u773c\u529b\u4e00\u5411\u4e0d\u9519\u3002\u201d \u3000\u3000\u5e74\u8f7b\u4eba\u627f\u8ba4\uff1a\u201c\u9601\u4e0b\u5b9e\u5728\u662f\u597d\u773c\u529b\u3002\u201d \u3000\u3000\u53f6\u5f00\u53c8\u95ee\uff1a\u201c\u4f60\u662f\u6bb5\u5148\u751f\u7b2c\u51e0\u4e2a\u5f1f\u4e8e\uff1f\u201d \u3000\u3000\u201c\u7b2c\u4e09\u4e2a\u3002\u201d \u3000\u3000\u201c\u4f60\u59d3\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u59d3\u65f6\uff0c\u65f6\u94ed\u3002\u201d \u3000\u3000\u201c\u4f60\u6709\u6ca1\u6709\u8d76\u8fc7\u9a74\u8f66\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u201c\u6211\u4e5f\u77e5\u9053\u4f60\u6ca1\u6709\u3002\u201d \u3000\u3000\u53f6\u5f00\u6de1\u6de1\u5730\u7b11\u9053\uff1a\u201c\u53ef\u662f\u65e0\u8bba\u4ec0\u4e48\u4e8b\uff0c\u90fd\u6709\u7b2c\u4e00\u6b21\u7684\u3002\u201d \u3000\u3000\u201c\u5e26\u6211\u53bb\u89c1\u4f60\u4eec\u4e0a\u5b98\u5e2e\u4e3b\uff0c\u65e0\u8bba\u5979\u5728\u54ea\u91cc\uff0c\u90fd\u5f97\u5e26\u6211\u627e\u5230\u5979\u3002\u201d \u3000\u3000\u53f6\u5f00\u53c8\u5750\u4e0a\u4e86\u90a3\u8f7d\u7164\u7684\u9a74\u8f66\uff0c\u8eba\u4e0b\u53bb\uff0c\u751a\u81f3\u8fde\u773c\u775b\u90fd\u5df2\u95ed\u8d77\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u8fd9\u5e74\u8f7b\u4eba\u4e0d\u4f1a\u60f3\u9003\u8d70\uff0c\u4e5f\u4e0d\u4f1a\u4e0d\u542c\u8bdd\u7684\uff0c\u65e0\u8bba\u8c01\u770b\u89c1\u4e86\u4ed6\u7684\u98de\u5200\uff0c\u90fd\u7edd\u4e0d\u4f1a\u518d\u505a \u51fa\u611a\u8822\u7684\u4e8b\u6765\u3002 \u3000\u3000\u65f6\u94ed\u679c\u7136\u5df2\u5728\u8d76\u7740\u9a74\u672c\u4e0a\u8def\uff0c\u8fd9\u7684\u786e\u662f\u4ed6\u5e73\u751f\u7b2c\u4e00\u6b21\u3002 \u3000\u3000\u6709\u4eba\u5728\u540e\u9762\u97ad\u7b56\uff0c\u9a74\u5b50\u53cd\u800c\u8d70\u5f97\u6bd4\u521a\u624d\u6162\u4e86\u3002 \u3000\u3000\u53f6\u5f00\u53c8\u5265\u4e86\u9897\u82b1\u751f\uff0c\u629b\u8d77\uff0c\u7b49\u82b1\u751f\u843d\u8fdb\u4ed6\u7684\u5634\uff0c\u4ed6\u5ffd\u7136\u9053\uff1a\u201c\u542c\u8bf4\u91d1\u575b\u6bb5\u5148\u751f\uff0c\u662f\u4e2a\u6700 \u8bb2\u7a76\u996e\u98df\u8863\u7740\u7684\u4eba\u3002\u201d \u3000\u3000\u65f6\u94ed\u9053\uff1a\u201c\u55ef\uff01\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u542c\u8bf4\u4ed6\u6536\u7684\u5f1f\u5b50\uff0c\u4e5f\u5168\u90fd\u662f\u51fa\u8eab\u5f88\u597d\u7684\u4e16\u5bb6\u5b50\u3002\u201d \u3000\u3000\u65f6\u94ed\u9053\uff1a\u201c\u55ef\uff01\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u4e5f\u662f\uff1f\u201d \u3000\u3000\u65f6\u94ed\u9053\uff1a\u201c\u55ef\uff01\u201d \u3000\u3000\u4ed6\u663e\u7136\u4e0d\u613f\u8c08\u8bba\u8fd9\u4e2a\u8bdd\u9898\uff0c\u53f6\u5f00\u5374\u504f\u504f\u8981\u8c08\u4e0b\u53bb\u3002 \u3000\u3000\u201c\u4f60\u4e0d\u613f\u6211\u63d0\u8d77\u8fd9\u4ef6\u4e8b\uff0c\u662f\u4e0d\u662f\u4e5f\u89c9\u5f97\u4e0d\u597d\u610f\u601d\uff1f\u201d \u3000\u3000\u65f6\u94ed\u7ec8\u4e8e\u5fcd\u4e0d\u4f4f\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u4e0d\u597d\u610f\u601d\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u56e0\u4e3a\u4f60\u4e5f\u77e5\u9053\uff0c\u4ee5\u4f60\u7684\u5e08\u95e8\u548c\u5bb6\u4e16\uff0c\u672c\u4e0d\u8be5\u5728\u91d1\u94b1\u5e2e\u91cc\u505a\u5974\u624d\u7684\u3002\u201d \u3000\u3000\u65f6\u94ed\u7684\u8138\u53c8\u6da8\u7ea2\uff0c\u9053\uff1a\u201c\u6211\u4e0d\u662f\u5974\u624d\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u4e5f\u77e5\u9053\u4f60\u6295\u5165\u91d1\u94b1\u5e2e\uff0c\u672c\u662f\u4e3a\u4e86\u60f3\u6446\u8131\u4f60\u7684\u5bb6\u4e16\uff0c\u81ea\u5df1\u505a\u4e00\u756a\u4e8b\u4e1a\u51fa\u6765\uff0c \u6bcf\u4e2a\u5e74\u8f7b\u4eba\u5927\u90fd\u4f1a\u8fd9\u4e48\u60f3\u7684\u3002\u201d \u3000\u3000\u4ed6\u7b11\u4e86\u7b11\uff0c\u6de1\u6de1\u5730\u63a5\u7740\u9053\uff1a\u201c\u53ef\u662f\u4f60\u73b0\u5728\u505a\u7684\uff0c\u5374\u662f\u5974\u624d\u505a\u7684\u4e8b\u3002\u201d \u3000\u3000\u65f6\u94ed\u7ea2\u7740\u8138\u9053\uff1a\u201c\u8fd9\u662f\u56e0\u4e3a\u4f60\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u662f\u6211\u53eb\u4f60\u505a\u7684\uff0c\u4f46\u662f\u5f80\u522b\u4eba\u5934\u4e0a\u6446\u94dc\u94b1\uff0c\u96be\u9053\u8fd9\u4e0d\u662f\u5974\u624d\u505a\u7684 \u4e8b\uff1f\u201d \u3000\u3000\u65f6\u94ed\u95ed\u4e0a\u4e86\u5634\u3002 \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f55\u51b5\uff0c\u6211\u53eb\u4f60\u505a\u8fd9\u79cd\u4e8b\uff0c\u53ea\u56e0\u4e3a\u4f60\u672c\u5df2\u662f\u91d1\u94b1\u5e2e\u7684\u5974\u624d\uff0c\u5426\u5219\u6211\u60c5\u613f\u722c\u5728\u5730 \u4e0a\u505a\u9a74\u5b50\uff0c\u8ba9\u4f60\u9a91\u5728\u6211\u8eab\u4e0a\u3002\u201d \u3000\u3000\u65f6\u94ed\u7684\u8138\u66f4\u7ea2\uff0c\u76ee\u4e2d\u5374\u5df2\u4e0d\u7981\u9732\u51fa\u75db\u82e6\u4e4b\u8272\u3002 \u3000\u3000\u53f6\u5f00\u5ffd\u7136\u53c8\u95ee\u9053\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u6211\u521a\u624d\u4e3a\u4ec0\u4e48\u8981\u53d1\u51fa\u90a3\u4e00\u5200\uff1f\u201d \u3000\u3000\u65f6\u94ed\u8fdf\u7591\u7740\uff0c\u6162\u6162\u9053\uff1a\u201c\u6211\u4e5f\u542c\u8bf4\u8fc7\uff0c\u4f60\u7684\u5200\u4e0d\u662f\u6740\u4eba\u7684\uff0c\u800c\u662f\u6551\u4eba\u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u53d1\u51fa\u90a3\u4e00\u5200\uff0c\u5c31\u662f\u8981\u8ba9\u4f60\u77e5\u9053\uff0c\u4f60\u5728\u91d1\u94b1\u5e2e\u91cc\uff0c\u4e5f\u4e00\u6837\u505a\u4e0d\u51fa\u5927\u4e8b \u6765\u7684\u3002\u201d \u3000\u3000\u65f6\u94ed\u54ac\u7740\u7259\uff0c\u9053\uff1a\u201c\u90a3\u53ea\u56e0\u4e3a\u6211\u7684\u6b66\u529f\u2026\u2026\u201d \u3000\u3000\u53f6\u5f00\u6253\u65ad\u4e86\u4ed6\u7684\u8bdd\uff0c\u9053\uff1a\u201c\u4e00\u4e2a\u4eba\u662f\u4e0d\u662f\u53d7\u4eba\u5c0a\u656c\uff0c\u548c\u4ed6\u7684\u6b66\u529f\u5e76\u6ca1\u6709\u5173\u7cfb\uff0c\u4f60\u505a\u7684\u82e5 \u662f\u5149\u660e\u6b63\u5927\u7684\u4e8b\uff0c\u5c31\u7edd\u6ca1\u6709\u4eba\u4f1a\u770b\u4e0d\u8d77\u4f60\uff0c\u6211\u7684\u5200\u4e5f\u7edd\u4e0d\u4f1a\u98de\u5230\u4f60\u5934\u4e0a\u53bb\u3002\u201d \u3000\u3000\u4ed6\u53f9\u4e86\u53e3\u6c14\uff0c\u53c8\u9053\uff1a\u201c\u5426\u5219\u6211\u7eb5\u7136\u4e0d\u6740\u4f60\uff0c\u8fdf\u65e9\u4e5f\u4e00\u5b9a\u6709\u522b\u4eba\u4f1a\u6740\u4f60\u7684\u3002\u201d \u3000\u3000\u65f6\u94ed\u53c8\u95ed\u4e0a\u4e86\u5634\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u5df2\u660e\u767d\u53f6\u5f00\u7684\u610f\u601d\uff0c\u53f6\u5f00\u4e5f\u77e5\u9053\u4ed6\u4e0d\u662f\u4e2a\u611a\u8822\u7684\u4eba\u3002 \u3000\u3000\u201c\u6211\u76f8\u4fe1\u4f60\u4e00\u5b9a\u4e0d\u4f1a\u8ba9\u6211\u5931\u671b\u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u53c8\u5265\u4e86\u9897\u82b1\u751f\uff0c\u629b\u8d77\u6765\uff0c\u7b49\u7740\u5b83\u843d\u4e0b\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u8fd9\u9897\u82b1\u751f\u65e2\u7136\u5df2\u629b\u8d77\uff0c\u5c31\u4e00\u5b9a\u4f1a\u843d\u4e0b\u6765\u7684\u3002 \u3000\u3000\u9a74\u8f66\u5df2\u9a70\u5165\u4e86\u8857\u9053\uff0c\u2014\u2014\u548c\u957f\u5b89\u57ce\u91cc\u5b8c\u5168\u540c\u6837\u7684\u4e00\u6761\u8857\u9053\u3002 \u3000\u3000\u53ea\u4e0d\u8fc7\u8fd9\u6761\u8857\u7684\u9e3f\u5bbe\u5ba2\u6808\uff0c\u5e76\u6ca1\u6709\u88ab\u70e7\u6210\u4e00\u7247\u74e6\u783e\u3002 \u3000\u3000\u770b\u7740\u9e3f\u5bbe\u5ba2\u6808\u7684\u91d1\u5b57\u724c\u5728\u592a\u9633\u4e0b\u95ea\u7740\u5149\uff0c\u53f6\u5f00\u5fc3\u91cc\u53c8\u4e0d\u7981\u6709\u4e86\u79cd\u5f02\u6837\u7684\u611f\u89c9\uff0c\u5c31\u597d\u50cf\u770b \u89c1\u4e00\u4e2a\u6b7b\u4eba\u53c8\u590d\u6d3b\u4e86\u4e00\u6837\u3002\u4e8b\u5b9e\u4e0a\uff0c\u4ed6\u7684\u786e\u4e5f\u770b\u89c1\u8fc7\u6b7b\u4eba\u590d\u6d3b\u3002 \u3000\u3000\u4eba\u751f\u4e2d\u6709\u4e9b\u4e8b\uff0c\u7684\u786e\u5c31\u50cf\u662f\u68a6\u5883\uff0c\u662f\u771f\u662f\u5047\uff0c\u672c\u5c31\u5f88\u5c11\u6709\u4eba\u80fd\u5206\u5f97\u6e05\u3002 \u3000\u3000\u53f6\u5f00\u5fc3\u91cc\u5728\u53f9\u606f\uff0c\u8138\u4e0a\u5374\u5e26\u7740\u5fae\u7b11\uff0c\u4ed6\u77e5\u9053\u8857\u4e0a\u7684\u4eba\u90fd\u5728\u770b\u7740\u4ed6\u3002 \u3000\u3000\u73b0\u5728\u6b63\u662f\u4e2d\u5348\uff0c\u8857\u4e0a\u7684\u4eba\u5e76\u4e0d\u591a\uff0c\u4e5f\u6b63\u5982\u957f\u5b89\u57ce\u91cc\u7684\u60c5\u51b5\u4e00\u6837\uff0c\u5927\u591a\u6570\u4eba\u90fd\u7559\u5728\u5bb6\u91cc\u5403 \u996d\u3002 \u3000\u3000\u53ef\u662f\u5728\u8857\u4e0a\u8d70\u52a8\u7684\u4eba\uff0c\u6bcf\u4e2a\u4eba\u7684\u8868\u60c5\u90fd\u5f88\u4e25\u8083\uff0c\u770b\u6765\u90fd\u5f88\u7d27\u5f20\uff0c\u5c31\u50cf\u662f\u5df2\u77e5\u9053\u6709\u4ec0\u4e48\u5927 \u4e8b\u8981\u53d1\u751f\uff0c\u5fc3\u91cc\u90fd\u6709\u4e86\u79cd\u8bf4\u4e0d\u51fa\u7684\u9884\u5146\u3002 \u3000\u3000\u53f6\u5f00\u4e5f\u77e5\u9053\u8fd9\u91cc\u5c31\u8981\u6709\u4ef6\u5927\u4e8b\u53d1\u751f\u4e86\uff0c\u4ed6\u8fd8\u77e5\u9053\u8fd9\u4ef6\u5927\u4e8b\u5c31\u662f\u4ed6\u9020\u6210\u7684\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u5df2\u5230\u4e86\u8fd9\u91cc\uff0c\u4ed6\u5df2\u4e0d\u51c6\u5907\u50cf\u4e0a\u6b21\u90a3\u6837\uff0c\u5e73\u5e73\u5b89\u5b89\u5730\u8d70\u51fa\u53bb\u3002 \u3000\u3000\u9a74\u8f66\u53c8\u5728\u9e3f\u5bbe\u5ba2\u6808\u5916\u505c\u4e0b\uff0c\u53f6\u5f00\u4e00\u8d70\u8fdb\u53bb\uff0c\u5c31\u770b\u89c1\u4e0a\u5b98\u5c0f\u4ed9\u6b63\u5750\u5728\u67dc\u53f0\u91cc\uff0c\u6b63\u5728\u7ffb\u7740\u672c \u5e10\u7c3f\u3002 \u3000\u3000\u5979\u770b\u6765\u7684\u786e\u50cf\u662f\u4e2a\u8001\u677f\u5a18\u7684\u6837\u5b50\uff0c\u53ea\u4e0d\u8fc7\u6bd4\u5927\u591a\u6570\u8001\u677f\u5a18\u90fd\u6f02\u4eae\u5f97\u591a\u3002 \u3000\u3000\u542c\u89c1\u4e86\u53f6\u5f00\u7684\u811a\u6b65\u58f0\uff0c\u5979\u7acb\u523b\u62ac\u8d77\u5934\u6765\u5ae3\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u5c31\u77e5\u9053\u4f60\u4e00\u5b9a\u4f1a\u6765\u7684\uff0c\u6211\u6b63 \u5728\u7b49\u7740\u4f60\u3002\u201d \u3000\u3000\u53f6\u5f00\u7ad9\u5728\u67dc\u53f0\u524d\uff0c\u770b\u7740\u5979\uff0c\u4e5f\u4e0d\u77e5\u4e3a\u4e86\u4ec0\u4e48\uff0c\u5fc3\u91cc\u5ffd\u7136\u53c8\u89c9\u5f97\u4e00\u9635\u523a\u75db\u3002 \u3000\u3000\u65e0\u8bba\u5979\u662f\u771f\u662f\u5047\uff0c\u5979\u5bf9\u4ed6\u603b\u7b97\u4e0d\u9519\u3002\u4ed6\u4eec\u5728\u4e00\u8d77\u5171\u540c\u751f\u6d3b\u7684\u90a3\u51e0\u5929\uff0c\u4e5f\u662f\u4ed6\u6c38\u8fdc\u90fd\u5fd8\u4e0d \u4e86\u7684\u3002\u4ed6\u5b9e\u5728\u4e0d\u5e0c\u671b\u4ed6\u4eec\u4f1a\u53d8\u6210\u4ec7\u654c\uff0c\u65e0\u8bba\u600e\u4e48\u770b\uff0c\u4e0a\u5b98\u5c0f\u4ed9\u90fd\u7edd\u4e0d\u50cf\u662f\u4ed6\u7684\u4ec7\u654c\u3002 \u3000\u3000\u5979\u7b11\u5f97\u6e29\u67d4\u800c\u59a9\u5a9a\uff0c\u5c31\u50cf\u662f\u4e2a\u521a\u770b\u89c1\u8001\u677f\u56de\u6765\u7684\u8001\u677f\u5a18\uff1a\u201c\u6211\u5df2\u66ff\u4f60\u51c6\u5907\u4e86\u51e0\u6837\u4f60\u559c\u6b22 \u5403\u7684\u83dc\uff0c\u73b0\u5728\u60f3\u5fc5\u5c31\u5feb\u5f00\u996d\u4e86\u3002\u201d \u3000\u3000\u53f6\u5f00\u51b7\u51b7\u9053\uff1a\u201c\u6211\u4e0d\u662f\u6765\u5403\u996d\u7684\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u5ae3\u7136\u9053\uff1a\u201c\u53ef\u662f\u65e0\u8bba\u8c01\u90fd\u8981\u5403\u996d\u7684\uff0c\u4f60\u4e5f\u4e00\u6837\u4e0d\u80fd\u4f8b\u5916\u3002\u201d \u3000\u3000\u53f6\u5f00\u5e76\u4e0d\u60f3\u8ddf\u5979\u4e89\u8fa9\uff0c\u4e5f\u6ca1\u4e89\u8fa9\uff0c\u4ed6\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u4f60\u5728\u7b97\u5e10\uff1f\u201d \u3000\u3000\u201c\u55ef\u3002\u201d \u3000\u3000\u201c\u662f\u4e0d\u662f\u5728\u7b97\u4f60\u6628\u5929\u665a\u4e0a\u6740\u4e86\u591a\u5c11\u4eba\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u53c8\u7b11\u4e86\uff1a\u201c\u6211\u5c31\u7b97\u6740\u4e86\u4eba\uff0c\u4e5f\u4e0d\u4f1a\u8bb0\u5728\u5e10\u7c3f\u4e0a\u3002\u201d \u3000\u3000\u201c\u5e10\u7c3f\u8bb0\u7684\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u8fd9\u662f\u672c\u793c\u7c3f\u3002\u201d\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4e0a\u9762\u8bb0\u7740\u5f88\u591a\u5947\u602a\u7684\u4eba\uff0c\u9001\u4e86\u5f88\u591a\u5947\u602a\u7684\u793c\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u9001\u7ed9\u4f60\u7684\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u8fd8\u6ca1\u6709\u8fd9\u4e48\u597d\u7684\u798f\u6c14\u3002\u201d \u3000\u3000\u5979\u5ffd\u7136\u53c8\u7b11\u9053\uff1a\u201c\u4f60\u8981\u4e0d\u8981\u6211\u628a\u4e0a\u9762\u8bb0\u7684\u5ff5\u7ed9\u4f60\u542c\u542c\uff1f\u201d \u3000\u3000\u53f6\u5f00\u6ca1\u6709\u62d2\u7edd\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u5d14\u7389\u771f\uff0c\u9001\u7684\u662f\u4e00\u53ea\u8001\u6bcd\u9e21\uff0c\u4e00\u65a4\u71d5\u7a9d\uff1b\u5357\u5bab\u6d6a\uff0c\u9001\u7684\u662f\u4e00\u5e45\u753b\uff1b\u53f6 \u5f00\uff0c\u9001\u7684\u662f\u6d3b\u4eba\u4e00\u4e2a\u3002\u201d \u3000\u3000\u53f6\u5f00\u8138\u8272\u53d8\u4e86\uff0c\u4ed6\u5f53\u7136\u5df2\u77e5\u9053\u8fd9\u662f\u8c01\u7684\u793c\u8584\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u5403\u5403\u5730\u7b11\u7740\u9053\uff1a\u201c\u5d14\u7389\u771f\u4e3a\u4ec0\u4e48\u8981\u9001\u9e21\u5462\uff1f\u96be\u9053\u5979\u4ee5\u4e3a\u65b0\u90ce\u5b98\u662f\u4f60\uff0c\u60f3\u8ba9\u4f60\u716e \u4e00\u9505\u9e21\u7ca5\uff0c\u5728\u6d1e\u623f\u91cc\u5403\u5bb5\u591c\uff1f\u201d \u3000\u3000\u5979\u4e0d\u8ba9\u53f6\u5f00\u8bf4\u8bdd\uff0c\u53c8\u7b11\u9053\uff1a\u201c\u8fd9\u4e0a\u9762\u6700\u5947\u602a\u7684\u4e00\u4efd\u793c\uff0c\u6050\u6015\u5c31\u662f\u4f60\u9001\u7684\u4e86\uff0c\u53ef\u662f\u6700\u8d35\u91cd \u7684\u4e00\u4efd\u793c\uff0c\u4f60\u4e00\u5b9a\u731c\u4e0d\u51fa\u662f\u8c01\u9001\u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u5fcd\u4e0d\u4f4f\u95ee\uff1a\u201c\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u662f\u56db\u4e2a\u4eba\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u6162\u6162\u5730\u5ff5\u51fa\u4e86\u56db\u4e2a\u540d\u5b57\uff1a\u201c\u789f\u513f\u5e03\uff0c\u591a\u5c14\u7532\uff0c\u5e03\u8fbe\u62c9\uff0c\u73ed\u5bdf\u5df4\u90a3\u3002\u201d \u3000\u3000\u53f6\u5f00\u8138\u8272\u53c8\u53d8\u4e86\uff1a\u201c\u4ed6\u4eec\u9001\u7684\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u662f\u4e00\u888b\u73e0\u5b9d\uff0c\u91cc\u9762\u8fd8\u6709\u4e00\u5757\u7389\u724c\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u53c8\u9053\uff1a\u201c\u5c31\u662f\u8fd9\u5757\u7389\u724c\u3002\u201d \u3000\u3000\u5979\u5df2\u4ece\u67dc\u53f0\u91cc\u5c06\u90a3\u4e0a\u9762\u523b\u7740\u56db\u4e2a\u5929\u9b54\u7684\u7389\u724c\u62ff\u4e86\u51fa\u6765\u3002\u5979\u663e\u7136\u4e5f\u65e9\u5c31\u51c6\u5907\u8ba9\u53f6\u5f00\u770b\u7684\uff0c \u7389\u724c\u6676\u83b9\u800c\u7f8e\u4e3d\uff0c\u4e0a\u9762\u523b\u7740\u7684\u5929\u9b54\uff0c\u5374\u4ee4\u53f6\u5f00\u89e6\u76ee\u60ca\u5fc3\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u53c8\u5728\u95ee\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u8fd9\u7389\u724c\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u53f6\u5f00\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u201c\u8fd9\u662f\u590d\u4ec7\u7389\u724c\u3002\u201d\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u9b54\u6559\u7684\u5927\u5929\u738b\u590d\u4ec7\u65f6\uff0c\u4e00\u5b9a\u4f1a\u6709\u8fd9\u79cd\u7389\u724c\u51fa\u73b0\u3002\u201d \u3000\u3000\u53f6\u5f00\u7d27\u63e1\u53cc\u62f3\uff1a\u201c\u4ed6\u4eec\u662f\u4e0d\u662f\u4e3a\u7389\u7bab\u9053\u4eba\u590d\u4ec7\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u90a3\u888b\u73e0\u5b9d\uff0c\u5c31\u662f\u4ed6\u4eec\u4e70\u547d\u7684\u94b1\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u662f\u4e70\u547d\u7684\u94b1\uff1f\u201d \u3000\u3000\u201c\u56db\u5927\u5929\u738b\u5728\u6740\u4eba\u4e4b\u524d\uff0c\u4e00\u5b9a\u8981\u5148\u5c06\u90a3\u4e9b\u4eba\u7684\u547d\u4e70\u8fc7\u6765\uff0c\u56e0\u4e3a\u4ed6\u4eec\u4e0d\u613f\u6b20\u6765\u751f\u7684\u503a\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u4ed6\u4eec\u9001\u7684\u73e0\u5b9d\u5b9e\u5728\u4e0d\u5c11\uff0c\u6740\u7684\u4eba\u4e5f\u5b9e\u5728\u4e0d\u5c11\u3002\u201d \u3000\u3000\u53f6\u5f00\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u6740\u4eba\u7684\u96be\u9053\u662f\u4ed6\u4eec\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u53c8\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u5c31\u7b97\u662f\u5446\u5b50\uff0c\u4e5f\u8be5\u770b\u51fa\u6740\u4eba\u7684\u662f\u8c01\u4e86\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f46\u6536\u5c38\u7684\u5374\u662f\u4f60\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u6de1\u6de1\u9053\uff1a\u201c\u6740\u4eba\u662f\u574f\u4e8b\uff0c\u6536\u5c38\u5374\u662f\u505a\u7684\u597d\u4e8b\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u8981\u66ff\u4ed6\u4eec\u6536\u5c38\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u60f3\u67e5\u51fa\u4e00\u4ef6\u4e8b\u6765\u3002\u201d \u3000\u3000\u53f6\u5f00\u8ffd\u95ee\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6211\u8981\u67e5\u51fa\u591a\u5c14\u7532\u548c\u5e03\u8fbe\u62c9\u7a76\u7adf\u662f\u4ec0\u4e48\u4eba\u3002\u201d \u3000\u3000\u53f6\u5f00\u51b7\u51b7\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u6b7b\u4eba\u662f\u4e0d\u4f1a\u8bf4\u8bdd\u7684\uff0c\u4f60\u6536\u4e86\u4ed6\u4eec\u7684\u5c38\u4e5f\u6ca1\u6709\u7528\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6709\u7528\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6709\u7528\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6211\u7b97\u51c6\u4ed6\u4eec\u5f53\u65f6\u4e00\u5b9a\u4e5f\u5728\u90a3\u559c\u5802\u91cc\u3002\u201d \u3000\u3000\u53f6\u5f00\u627f\u8ba4\uff0c\u4ed6\u4eec\u82e5\u4e0d\u5728\u90a3\u559c\u5802\u91cc\uff0c\u53c8\u600e\u4e48\u80fd\u51fa\u624b\u6740\u4eba\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6240\u4ee5\u5f53\u65f6\u559c\u5802\u91cc\u82e5\u6709\u4e00\u767e\u4e2a\u4eba\uff0c\u6b7b\u7684\u4e00\u5b9a\u53ea\u6709\u4e5d\u5341\u516b\u4e2a\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6ca1\u6709\u6b7b\u7684\u4e24\u4e2a\uff0c\u4e00\u5b9a\u5c31\u662f\u591a\u5c14\u7532\u548c\u5e03\u8fbe\u62c9\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u5ae3\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u5c31\u77e5\u9053\u4f60\u5e76\u4e0d\u662f\u5446\u5b50\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u5c31\u5c06\u6b7b\u5c38\u5168\u6536\u56de\u6765\uff0c\u770b\u770b\u6b7b\u7684\u662f\u4e9b\u4ec0\u4e48\u4eba\uff1f\u6b7b\u4e86\u591a\u5c11\u4eba\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f46\u4f60\u5374\u8fd8\u662f\u67e5\u4e0d\u51fa\uff0c\u90a3\u6ca1\u6709\u6b7b\u7684\u4e24\u4e2a\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6240\u4ee5\u6211\u5c31\u628a\u793c\u7c3f\u4e5f\u62ff\u6765\u4e86\uff0c\u770b\u770b\u9001\u793c\u7684\u662f\u4e9b\u4ec0\u4e48\u4eba\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u9001\u793c\u7684\u4eba\u5e76\u4e0d\u4e00\u5b9a\u4f1a\u53bb\u559d\u559c\u9152\uff0c\u53bb\u559d\u559c\u9152\u7684\u4eba\uff0c\u5e76\u4e0d\u4e00\u5b9a\u9001\u4e86\u793c\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6211\u81f3\u5c11\u603b\u53ef\u4ee5\u770b\u51fa\u4e00\u70b9\u5934\u7eea\u6765\uff0c\u6211\u4e5f\u4e0d\u662f\u5446\u5b50\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u770b\u51fa\u6765\u4e86\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u4e00\u6765\uff0c\u6211\u7684\u5fc3\u5c31\u4e71\u4e86\uff0c\u600e\u4e48\u8fd8\u770b\u5f97\u4e0b\u53bb\uff1f\u201d \u3000\u3000\u5979\u7ad9\u8d77\u6765\uff0c\u8d70\u51fa\u67dc\u53f0\uff0c\u5ffd\u7136\u53c8\u9053\uff1a\u201c\u6211\u8fd8\u6709\u53e5\u8bdd\u8981\u95ee\u4f60\u3002\u201d \u3000\u3000\u53f6\u5f00\u53ea\u597d\u8ba9\u5979\u95ee\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4eba\u662f\u4e0d\u662f\u90fd\u8981\u5403\u996d\u7684\uff1f\u201d\u53f6\u5f00\u4e5f\u53ea\u597d\u627f\u8ba4\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u4eba\uff1f\u201d \u3000\u3000\u53f6\u5f00\u4e5f\u53ea\u6709\u627f\u8ba4\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u62c9\u8d77\u4ed6\u7684\u624b\uff0c\u5ae3\u7136\u9053\uff1a\u201c\u90a3\u6211\u4eec\u73b0\u5728\u5c31\u8be5\u5403\u996d\u53bb\u3002\u201d \u3000\u3000\u53f6\u5f00\u5728\u5403\u996d\u3002\u4ed6\u81ea\u5df1\u4e00\u5230\u4e86\u4e0a\u5b98\u5c0f\u4ed9\u9762\u524d\uff0c\u5c31\u597d\u50cf\u5ffd\u7136\u771f\u7684\u53d8\u6210\u4e86\u4e2a\u5446\u5b50\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u809a\u5b50\u5b9e\u5728\u5f88\u7a7a\uff0c\u8d70\u4e86\u534a\u5929\u8def\uff0c\u80c3\u53e3\u4e5f\u5f00\u4e86\uff0c\u4e0d\u5750\u4e0b\u5403\u996d\u5012\u4e5f\u6ca1\u4ec0\u4e48\uff0c\u4e00\u5750\u4e0b\u6765\uff0c \u62ff\u8d77\u4e86\u7b77\u5b50\uff0c\u5c31\u5f88\u96be\u518d\u653e\u4e0b\u6765\u3002 \u3000\u3000\u4f55\u51b5\u8fd9\u4e9b\u83dc\u4e5f\u7684\u786e\u90fd\u5bf9\u4ed6\u7684\u53e3\u5473\uff0c\u5c24\u5176\u662f\u4e00\u6837\u53c8\u9178\u53c8\u8fa3\u7684\u8c46\u8150\u4e73\uff0c\u4e0d\u4f46\u5f00\u80c3\uff0c\u800c\u4e14\u9192 \u9152\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u67d4\u58f0\u9053\uff1a\u201c\u6211\u6ca1\u6709\u66ff\u4f60\u51c6\u5907\u9152\uff0c\u56e0\u4e3a\u6211\u77e5\u9053\u4f60\u809a\u4e8e\u662f\u7a7a\u7684\uff0c\u5403\u5b8c\u4e86\u996d\uff0c\u6211\u518d\u966a \u4f60\u559d\u3002\u201d \u3000\u3000\u65e0\u8bba\u8c01\u6765\u770b\uff0c\u65e0\u8bba\u600e\u4e48\u6837\u770b\uff0c\u5979\u90fd\u662f\u4e2a\u53c8\u6e29\u67d4\u3001\u53c8\u4f53\u8d34\u7684\u5973\u4eba\uff0c\u4e00\u4e2a\u7537\u4eba\u82e5\u662f\u9047\u7740\u4e86\u8fd9 \u79cd\u5973\u4eba\uff0c\u5e94\u8be5\u600e\u4e48\u529e\u5462\uff1f\u53f6\u5f00\u5df2\u62ff\u5b9a\u4e86\u4e3b\u610f\u2014\u2014\u4e0d\u7406\u5979\uff0c\u5c31\u7b97\u5979\u80fd\u8bf4\u51fa\u4e00\u6735\u82b1\u6765\uff0c\u4e5f\u4e0d\u7406 \u5979\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u8f7b\u8f7b\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u5fc3\u91cc\u4e00\u5b9a\u5728\u6028\u6211\uff0c\u4e0d\u8be5\u628a\u4f60\u7559\u5728\u8fd9\u91cc\uff0c\u5426\u5219\u4e01 \u59d1\u5a18\u5c31\u7edd\u4e0d\u4f1a\u5ac1\u7ed9\u90ed\u5b9a\u7684\uff0c\u5979\u82e5\u4e0d\u5ac1\u7ed9\u90ed\u5b9a\uff0c\u4e5f\u673a\u4e0d\u4f1a\u5728\u90a3\u5929\u665a\u4e0a\u6709\u90a3\u4e9b\u4e8b\u53d1\u751f\u4e86\u3002\u201d \u3000\u3000\u8fd9\u6b63\u662f\u53f6\u5f00\u5fc3\u91cc\u60f3\u8bf4\u7684\u8bdd\uff0c\u81ea\u5df1\u8fd8\u6ca1\u6709\u8bf4\uff0c\u4e0a\u5b98\u5c0f\u4ed9\u53cd\u800c\u5148\u66ff\u4ed6\u8bf4\u4e86\u51fa\u6765\u3002 \u3000\u3000\u201c\u4f46\u662f\u4f60\u4e5f\u5e94\u66ff\u6211\u60f3\u60f3\uff0c\u6211\u4e5f\u662f\u4e2a\u5973\u4eba\uff0c\u5e76\u4e0d\u662f\u5996\u602a\u3002\u201d\u5979\u5e7d\u5e7d\u5730\u63a5\u7740\u9053\uff1a\u201c\u5973\u4eba\u559c\u6b22 \u4e0a\u4e00\u4e2a\u7537\u4eba\u65f6\uff0c\u603b\u4f1a\u5fcd\u4e0d\u4f4f\u60f3\u8981\u7559\u4f4f\u4ed6\u7684\uff0c\u65e0\u8bba\u4ec0\u4e48\u6837\u7684\u5973\u4eba\u90fd\u4e00\u6837\u3002\u201d \u3000\u3000\u53f6\u5f00\u5728\u51b7\u7b11\uff0c\u4f46\u662f\u4ed6\u5fc3\u91cc\u4e5f\u4e0d\u80fd\u4e0d\u627f\u8ba4\uff0c\u5979\u8bf4\u7684\u8bdd\u5e76\u4e0d\u662f\u6ca1\u6709\u9053\u7406\u3002\u7231\u5e76\u6ca1\u6709\u9519\uff0c\u4e5f\u4e0d \u662f\u7f6a\u6076\u3002 \u3000\u3000\u4e00\u4e2a\u5973\u4eba\u7231\u4e0a\u4e86\u4e00\u4e2a\u7537\u4eba\uff0c\u672c\u6765\u5c31\u662f\u5929\u7ecf\u5730\u4e49\u7684\u4e8b\uff0c\u4e00\u70b9\u9519\u90fd\u6ca1\u6709\u3002\u4e00\u4e2a\u5973\u4eba\u7231\u4e0a\u4e00\u4e2a \u7537\u4eba\u65f6\uff0c\u5f53\u7136\u5c31\u7edd\u4e0d\u4f1a\u5e0c\u671b\u4ed6\u8d76\u5feb\u8d70\u7684\u3002\u8fd9\u4e00\u70b9\u4e5f\u6ca1\u6709\u4eba\u80fd\u8bf4\u5979\u9519\u4e86\u3002 \u3000\u3000\u53f6\u5f00\u5ffd\u7136\u53d1\u89c9\u81ea\u5df1\u7684\u5fc3\u53c8\u5df2\u88ab\u5979\u6253\u52a8\uff0c\u7acb\u523b\u7ad9\u8d77\u6765\uff0c\u9053\uff1a\u201c\u4f60\u7684\u8bdd\u8bf4\u5b8c\u4e86\u6ca1\u6709\uff1f\u201d\u4e0a\u5b98 \u5c0f\u4ed9\u9053\uff1a\u201c\u8fd8\u6ca1\u6709\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u7684\u996d\u5374\u5df2\u5403\u5b8c\u4e86\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4f60\u4e0d\u60f3\u559d\u9152\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4e0d\u60f3\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4f60\u4e5f\u4e0d\u60f3\u67e5\u51fa\u591a\u5c14\u7532\u548c\u5e03\u8fbe\u62c9\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u81ea\u5df1\u4f1a\u53bb\u627e\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4f60\u5c31\u7b97\u771f\u7684\u80fd\u627e\u51fa\u6765\uff0c\u53c8\u600e\u4e48\u6837\uff1f\u96be\u9053\u4f60\u4e00\u4e2a\u4eba\u5c31\u80fd\u5bf9\u4ed8\u6574\u4e2a\u9b54\u6559\uff1f\u201d \u3000\u3000\u5979\u53c8\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u9b54\u6559\u4e2d\u6709\u591a\u5c11\u95e8\u4eba\u5b50\u5f1f\uff1f\u4f60\u77e5\u4e0d\u77e5\u9053\u4ed6\u4eec\u6709\u591a\u5927\u529b \u91cf\uff1f\u201d \u3000\u3000\u53f6\u5f00\u77e5\u9053\u9b54\u6559\u7684\u53ef\u6015\uff0c\u5f88\u5c11\u6709\u4eba\u80fd\u6bd4\u4ed6\u77e5\u9053\u5f97\u66f4\u6e05\u695a\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u4e5f\u5e94\u8be5\u77e5\u9053\uff0c\u8981\u5bf9\u4ed8\u9b54\u6559\u53ea\u6709\u4e00\u79cd\u6cd5\u5b50\u3002\u201d \u3000\u3000\u53f6\u5f00\u5fcd\u4e0d\u4f4f\u95f4\uff1a\u201c\u4ec0\u4e48\u6cd5\u5b50\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u8138\u4e0a\u6e29\u67d4\u7684\u7b11\u5bb9\u5df2\u6d88\u5931\uff0c\u7f8e\u4e3d\u7684\u773c\u775b\u91cc\uff0c\u5ffd\u7136\u95ea\u51fa\u4e00\u79cd\u903c\u4eba\u7684\u5149\u5f69\u3002 \u3000\u3000\u73b0\u5728\u5979\u5df2\u4e0d\u518d\u662f\u4e2a\u6e29\u67d4\u800c\u4f53\u8d34\u7684\u8001\u677f\u5a18\uff0c\u800c\u662f\u5a01\u9707\u6c5f\u6e56\u7684\u91d1\u94b1\u5e2e\u5e2e\u4e3b\u3002 \u3000\u3000\u5979\u51dd\u89c6\u7740\u53f6\u5f00\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u653e\u773c\u5929\u4e0b\uff0c\u80fd\u548c\u9b54\u6559\u5bf9\u6297\u7684\uff0c\u53ea\u6709\u6211\u4eec\u91d1\u94b1\u5e2e\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u7ecf\u8fc7\u591a\u5e74\u6765\u7684\u7b79\u5212\u51c6\u5907\uff0c\u73b0\u5728\u91d1\u94b1\u5e2e\u65e0\u8bba\u4eba\u529b\u7269\u529b\uff0c\u90fd\u5df2\u8fbe\u5230\u5dc5\u5cf0\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u5c11\u6797\u3001\u6b66\u5f53\u3001\u6606\u4ed1\u3001\u70b9\u82cd\u3001\u534e\u5c71\uff0c\u6bcf\u4e00\u4e2a\u95e8\u6d3e\u4e2d\uff0c\u73b0\u5728\u90fd\u5df2\u6709\u6211\u4eec\u7684 \u4eba\u2026\u2026\u201d \u3000\u3000\u53f6\u5f00\u6253\u65ad\u4e86\u5979\u7684\u8bdd\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u73b0\u5728\u53c8\u60f3\u6536\u4e70\u6211\uff1f\u201d \u3000\u3000\u201c\u4e0d\u662f\u6536\u4e70\u3002\u201d\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u53ea\u4e0d\u8fc7\u4f60\u82e5\u8981\u5bf9\u4ed8\u9b54\u6559\uff0c\u5c31\u53ea\u6709\u548c\u91d1\u94b1\u5e2e\u8054\u624b\u3002\u201d \u3000\u3000\u53f6\u5f00\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u53c8\u8981\u6211\u505a\u4f60\u4eec\u91d1\u94b1\u5e2e\u7684\u62a4\u6cd5\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u53ea\u8981\u4f60\u613f\u610f\uff0c\u6211\u751a\u81f3\u53ef\u4ee5\u5c06\u5e2e\u4e3b\u8ba9\u7ed9\u4f60\u505a\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u8981\u5982\u6b64\u727a\u7272\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u53f9\u4e86\u53e3\u6c14\uff0c\u773c\u6ce2\u53c8\u53d8\u5f97\u6625\u6c34\u822c\u6e29\u67d4\uff0c\u8f7b\u8f7b\u9053\uff1a\u201c\u4e00\u4e2a\u5973\u4eba\u4e3a\u4e86\u5979\u771f\u6b63\u559c\u6b22\u7684\u7537 \u4eba\uff0c\u672c\u6765\u5c31\u4e0d\u60dc\u727a\u7272\u4e00\u5207\u7684\uff0c\u4f55\u51b5\u2026\u2026\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f55\u51b5\u9b54\u6559\u672c\u6765\u5c31\u662f\u4f60\u4eec\u7684\u5bf9\u5934\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u975e\u4f46\u662f\u6211\u4eec\u7684\u5bf9\u5934\uff0c\u800c\u4e14\u662f\u8a93\u4e0d\u4e24\u7acb\u7684\u5bf9\u5934\uff0c\u5c24\u5176\u662f\u6700\u8fd1\u2026\u2026\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6700\u8fd1\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6700\u8fd1\u6211\u5c31\u7b97\u4e0d\u53bb\u627e\u4ed6\u4eec\uff0c\u4ed6\u4eec\u4e5f\u4f1a\u6765\u627e\u6211\u3002\u201d \u3000\u3000\u53f6\u5f00\u77e5\u9053\u8fd9\u4e0d\u662f\u8c0e\u8bdd\uff0c\u91d1\u94b1\u5e2e\u548c\u9b54\u6559\u6700\u8fd1\u90fd\u51c6\u5907\u91cd\u632f\u58f0\u5a01\uff0c\u79f0\u9738\u6c5f\u6e56\uff0c\u4ed6\u4eec\u4e4b\u95f4\u7684\u51b2 \u7a81\uff0c\u5f53\u7136\u4f1a\u8d8a\u6765\u8d8a\u5c16\u9510\u3002 \u3000\u3000\u9e6c\u868c\u76f8\u4e89\uff0c\u6e14\u7fc1\u5f97\u5229\uff0c\u8fd9\u5b9e\u5728\u662f\u4ed6\u7684\u597d\u673a\u4f1a\uff0c\u4ed6\u867d\u7136\u5e76\u4e0d\u60f3\u505a\u6e14\u7fc1\uff0c\u4f46\u81f3\u5c11\u53ef\u4ee5\u4e58\u8fd9\u4e2a \u673a\u4f1a\uff0c\u505a\u5f88\u591a\u4ed6\u65e9\u5df2\u60f3\u505a\u3001\u4e5f\u65e9\u5df2\u8be5\u505a\u7684\u4e8b\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u53c8\u9053\uff1a\u201c\u4f60\u7684\u60c5\u51b5\u4e5f\u4e00\u6837\uff0c\u73b0\u5728\u56db\u5927\u5929\u738b\u4e2d\uff0c\u5df2\u6709\u4e24\u4e2a\u4eba\u5230\u4e86\u957f\u5b89\uff0c\u4e3a\u7684\u7edd\u4e0d \u6b62\u662f\u8981\u5bf9\u4ed8\u91d1\u94b1\u5e2e\uff0c\u4e5f\u662f\u4e3a\u4e86\u8981\u5bf9\u4ed8\u4f60\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6240\u4ee5\u5c31\u7b97\u6211\u4e0d\u53bb\u627e\u4ed6\u4eec\uff0c\u4ed6\u4eec\u4e5f\u4e00\u6837\u4e0d\u4f1a\u653e\u8fc7\u6211\u7684\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4ed6\u4eec\u662f\u4f60\u7684\u5bf9\u5934\uff0c\u6211\u81f3\u5c11\u8fd8\u662f\u4f60\u7684\u670b\u53cb\uff0c\u6240\u4ee5\u4f60\u5e94\u8be5\u548c\u6211\u4eec\u8054\u5408\u8d77\u6765 \u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u5df2\u5750\u4e0b\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u73b0\u5728\u4f60\u5fc3\u91cc\u4e5f\u8bb8\u4f1a\u8ba4\u4e3a\u6211\u662f\u60f3\u5229\u7528\u4f60\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u4e0d\u662f\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u5c31\u7b97\u6211\u662f\u5728\u5229\u7528\u4f60\uff0c\u4f60\u5c82\u975e\u4e5f\u53ef\u4ee5\u540c\u6837\u5229\u7528\u6211\uff0c\u4e58\u8fd9\u4e2a\u673a\u4f1a\uff0c\u5c06\u9b54\u6559\u6d88 \u706d\uff1f\u201d \u3000\u3000\uff29\u53f6\u5f00\u5ffd\u7136\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u5b9e\u5728\u662f\u4e2a\u5f88\u4f1a\u8bf4\u8bdd\u7684\u5973\u4eba\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6211\u662f\u4e0d\u662f\u5df2\u7ecf\u8bf4\u52a8\u4e86\u4f60\uff1f\u201d \u3000\u3000\u53f6\u5f00\u82e6\u7b11\u9053\uff1a\u201c\u597d\u50cf\u662f\u7684\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u53c8\u7b11\u4e86\uff0c\u7b11\u5bb9\u53c8\u53d8\u5f97\u6e29\u67d4\u800c\u59a9\u5a9a\uff1a\u201c\u90a3\u4e48\u6211\u4eec\u73b0\u5728\u662f\u4e0d\u662f\u5df2\u5e94\u8be5\u559d\u676f\u9152\uff1f\u201d \u3000\u3000\u53f6\u5f00\u53f9\u9053\uff1a\u201c\u73b0\u5728\u6211\u53ea\u5947\u602a\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u7728\u7740\u773c\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u8981\u6211\u505a\u7684\u4e8b\uff0c\u6211\u4e3a\u4ec0\u4e48\u603b\u662f\u6ca1\u6cd5\u5b50\u62d2\u7edd\uff1f\u201d \u7b2c\u4e8c\u5341\u516d\u7ae0\u3000\u98ce\u6d41\u5be1\u5987>> \u53e4\u9f99\u300a\u4e5d\u6708\u9e70\u98de\u300b \u7b2c\u4e8c\u5341\u516d\u7ae0\u3000\u98ce\u6d41\u5be1\u5987 \u3000\u3000\u9152\u5df2\u6446\u4e0a\u6765\uff0c\u9189\u4eba\u7684\u5374\u4e0d\u662f\u9152\uff0c\u800c\u662f\u4e0a\u5b98\u5c0f\u4ed9\u3002 \u3000\u3000\u5979\u7684\u6e29\u67d4\uff0c\u5979\u7684\u4f53\u8d34\uff0c\u5979\u7684\u773c\u6cea\uff0c\u5979\u7684\u6bcf\u4e00\u6837\u90fd\u8db3\u4ee5\u4ee4\u7537\u4eba\u6c89\u9189\u3002 \u3000\u3000\u53f6\u5f00\u662f\u4e0d\u662f\u53c8\u9189\u4e86\uff1f\u4ed6\u6bd5\u7adf\u4e5f\u662f\u4e2a\u7537\u4eba\uff0c\u800c\u4e14\u5e76\u4e0d\u662f\u4ed6\u81ea\u5df1\u60f3\u8c61\u4e2d\u90a3\u4e48\u65e0\u60c5\u7684\u7537\u4eba\u3002\u4ed6 \u751a\u81f3\u5df2\u7ecf\u5728\u6000\u7591\u81ea\u5df1\uff0c\u662f\u4e0d\u662f\u65e9\u5df2\u88ab\u5979\u7684\u6e29\u67d4\u6c89\u9189\uff1f\u5979\u4e0d\u4f46\u662f\u4e2a\u5973\u4eba\uff0c\u800c\u4e14\u662f\u4e2a\u5973\u4eba\u4e2d\u7684\u5973 \u4eba\uff0c\u8fd9\u79cd\u5973\u4eba\u672c\u5c31\u662f\u7537\u4eba\u65e0\u6cd5\u6297\u62d2\u7684\u3002 \u3000\u3000\u5979\u4e5f\u8bb8\u6ca1\u6709\u4e01\u7075\u7433\u7684\u660e\u8273\uff0c\u4e5f\u6ca1\u6709\u5d14\u7389\u771f\u7684\u5a07\u5f31\uff0c\u53ef\u662f\u5979\u8fdc\u6bd4\u5979\u4eec\u66f4\u4e86\u89e3\u7537\u4eba\uff0c\u66f4\u61c2\u5f97 \u6349\u4f4f\u4e00\u4e2a\u7537\u4eba\u7684\u5fc3\u3002\u53f6\u5f00\u7684\u5fc3\u662f\u4e0d\u662f\u5df2\u88ab\u5979\u6349\u4f4f\uff1f \u3000\u3000\u201c\u4f60\u9189\u4e86\u6ca1\u6709\uff1f\u201d \u3000\u3000\u201c\u73b0\u5728\u867d\u7136\u8fd8\u6ca1\u6709\u9189\uff0c\u8fdf\u65e9\u603b\u662f\u4f1a\u9189\u7684\u3002\u201d \u3000\u3000\u201c\u4f60\u51c6\u5907\u9189\uff1f\u201d \u3000\u3000\u201c\u53ea\u8981\u4e00\u5f00\u59cb\u559d\uff0c\u5c31\u51c6\u5907\u9189\u3002\u201d \u3000\u3000\u201c\u6240\u4ee5\u6211\u82e5\u6709\u8bdd\u8bf4\uff0c\u5c31\u5f97\u4e58\u4f60\u8fd8\u6ca1\u6709\u9189\u7684\u65f6\u5019\u8bf4\u3002\u201d \u3000\u3000\u201c\u4e00\u70b9\u4e5f\u4e0d\u9519\u3002\u201d \u3000\u3000\u201c\u8fd9\u5e10\u7c3f\u4f60\u5df2\u770b\u8fc7\uff1f\u201d \u3000\u3000\u201c\u770b\u8fc7\u3002\u201d \u3000\u3000\u201c\u4f60\u770b\u51fa\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u6211\u53ea\u770b\u51fa\u91d1\u94b1\u5e2e\u7684\u51fa\u624b\uff0c\u597d\u50cf\u8fd8\u6ca1\u6709\u9b54\u6559\u5927\u65b9\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u7b11\u4e86\uff1a\u201c\u91d1\u94b1\u5e2e\u4e0d\u60f3\u4e70\u522b\u4eba\u7684\u547d\uff0c\u6240\u4ee5\u4e5f\u7528\u4e0d\u7740\u9001\u592a\u91cd\u7684\u793c\u3002\u201d \u3000\u3000\u53f6\u5f00\u51dd\u89c6\u7740\u676f\u4e2d\u7684\u9152\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u4e5f\u8bb8\u4f60\u65e9\u5df2\u770b\u51fa\u6765\uff0c\u65e0\u8bba\u9001\u591a\u91cd\u7684\u793c\uff0c\u4ed6\u4eec\u90fd\u6536\u4e0d\u5230 \u7684\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6211\u82e5\u771f\u7684\u80fd\u770b\u51fa\u6765\uff0c\u4e5f\u8bb8\u5c31\u4f1a\u591a\u9001\u4e9b\u4e86\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u65e0\u8bba\u9001\u4e86\u591a\u5c11\uff0c\u73b0\u5728\u90fd\u5df2\u6536\u56de\u6765\u3002\u201d \u3000\u3000\u53f6\u5f00\u4e5f\u7b11\u4e86\uff1a\u201c\u4f60\u770b\u51fa\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u53f9\u4e86\u53e3\u6c14\uff0c\u8f7b\u8f7b\u9053\uff1a\u201c\u6211\u770b\u51fa\u4f60\u5b9e\u5728\u662f\u4e2a\u5f88\u591a\u60c5\u7684\u4eba\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u7edd\u4e0d\u4f1a\u662f\u9b54\u6559\u4e2d\u7684\u56db\u5927\u5929\u738b\uff0c\u9b54\u6559\u4e2d\u5168\u90fd\u662f\u65e0\u60c5\u4eba\u3002\u201d \u3000\u3000\u53f6\u5f00\u82e6\u7b11\u9053\uff1a\u201c\u8fd9\u4e00\u70b9\u4f60\u73b0\u5728\u624d\u770b\u51fa\u6765\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u5ae3\u7136\u9053\uff1a\u201c\u73b0\u5728\u770b\u51fa\u6765\u8fd8\u4e0d\u8fdf\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u4ee5\u524d\u96be\u9053\u6000\u7591\u6211\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u627f\u8ba4\uff0c\u9053\uff1a\u201c\u56e0\u4e3a\u591f\u8d44\u683c\u505a\u9b54\u6559\u5929\u738b\u7684\u4eba\u5b9e\u5728\u4e0d\u591a\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u9664\u4e86\u6211\u4e4b\u5916\uff0c\u957f\u5b89\u57ce\u91cc\u8fd8\u6709\u51e0\u4e2a\u4eba\u591f\u8d44\u683c\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6700\u591a\u56db\u4e94\u4e2a\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u7b2c\u4e00\u4e2a\u5f53\u7136\u662f\u5415\u8fea\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4e0d\u9519\uff01\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u97e9\u8d1e\u5f53\u7136\u4e5f\u7b97\u4e00\u4e2a\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u5f53\u7136\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u8fd8\u6709\u5462\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u96be\u9053\u5df2\u5fd8\u4e86\u4f60\u90a3\u4e2a\u8001\u670b\u53cb\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6768\u5929\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u7b11\u9053\uff1a\u201c\u4e0d\u4f1a\u98de\u7684\u72d0\u72f8\u5df2\u7ecf\u591f\u53ef\u6015\u4e86\u3002\u4f55\u51b5\u4f1a\u98de\u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4ed6\u5c82\u975e\u662f\u4f60\u7684\u4eb2\u4fe1\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6211\u6ca1\u6709\u4eb2\u4fe1\u3002\u201d \u3000\u3000\u5979\u62ac\u8d77\u5934\uff0c\u51dd\u89c6\u7740\u53f6\u5f00\uff1a\u201c\u6211\u552f\u4e00\u4fe1\u4efb\u7684\u4eba\u5c31\u662f\u4f60\uff0c\u53ea\u53ef\u60dc\u2026\u2026\u201d \u3000\u3000\u53f6\u5f00\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u6211\u5374\u4e0d\u4fe1\u4efb\u4f60\uff0c\u4e5f\u8bb8\u6211\u552f\u4e00\u4e0d\u80fd\u4fe1\u4efb\u7684\u4eba\u5c31\u662f\u4f60\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u8f7b\u8f7b\u53f9\u606f\uff0c\u9053\uff1a\u201c\u6211\u5e76\u4e0d\u602a\u4f60\uff0c\u53ef\u662f\u603b\u6709\u4e00\u5929\uff0c\u4f60\u4f1a\u77e5\u9053\u81ea\u5df1\u9519\u4e86\u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u6ca1\u6709\u4e89\u8fa9\uff0c\u5fae\u7b11\u7740\u6539\u53d8\u8bdd\u9898\uff0c\u9053\uff1a\u201c\u5415\u8fea\u3001\u97e9\u8d1e\u3001\u6768\u5929\uff0c\u52a0\u8d77\u6765\u53ea\u6709\u4e09\u4e2a\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u4e2a\u4eba\u4e5f\u5f88\u53ef\u7591\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u8c01\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4e00\u4e2a\u6628\u5929\u624d\u5230\u957f\u5b89\u7684\u4eba\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u8ba4\u5f97\u4ed6\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4e0d\u8ba4\u5f97\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u77e5\u9053\u4ed6\u662f\u8c01\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u53f6\u5f00\u53c8\u7b11\u4e86\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u7684\u8868\u60c5\u5374\u5f88\u4e25\u8083\u3002\u9053\uff1a\u201c\u4f46\u6211\u5374\u77e5\u9053\u4ed6\u4e00\u5b9a\u6709\u8d44\u683c\u505a\u9b54\u6559\u7684\u5929\u738b\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u6d3e\u51fa\u53bb\u6253\u542c\u4ed6\u884c\u8e2a\u6765\u5386\u7684\u4eba\uff0c\u90fd\u5df2\u4e0d\u89c1\u4e86\u3002\u201d \u3000\u3000\u53f6\u5f00\u4e0d\u61c2\uff1a\u201c\u4e0d\u89c1\u4e86\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4e0d\u89c1\u4e86\u7684\u610f\u601d\uff0c\u5c31\u662f\u90a3\u4e9b\u4eba\u51fa\u53bb\u4e4b\u540e\uff0c\u5c31\u6ca1\u6709\u518d\u56de\u6765\u8fc7\uff0c\u751a\u81f3\u8fde\u6d88\u606f\u90fd \u6ca1\u6709\uff0c\u6211\u518d\u6d3e\u4eba\u51fa\u53bb\u627e\uff0c\u6211\u7684\u4eba\u4e5f\u6ca1\u6709\u56de\u6765\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u4e00\u5171\u6d3e\u51fa\u53bb\u591a\u5c11\u4eba\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4e00\u5171\u4e09\u6b21\uff0c\u7b2c\u4e00\u6b21\u4e24\u4e2a\uff0c\u7b2c\u4e8c\u6b21\u56db\u4e2a\uff0c\u7b2c\u4e09\u6b21\u516d\u4e2a\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u52a0\u8d77\u6765\u4e00\u5171\u662f\u5341\u4e8c\u4e2a\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u800c\u4e14\u662f\u5341\u4e8c\u4e2a\u597d\u624b\uff0c\u6700\u540e\u4e00\u6b21\u90a3\u516d\u4e2a\uff0c\u66f4\u662f\u597d\u624b\u4e2d\u7684\u597d\u624b\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u8fd9\u4e9b\u597d\u624b\u5168\u90e8\u4e0d\u89c1\u4e86\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u70b9\u70b9\u5934\uff0c\u9053\uff0c\u201c\u5341\u4e8c\u4e2a\u4eba\u51fa\u53bb\u4e86\u4e4b\u540e\uff0c\u5c31\u7acb\u523b\u65e0\u5f71\u65e0\u8e2a\uff0c\u5c31\u597d\u50cf\u5ffd\u7136\u4ece\u5730\u4e0a\u6d88 \u5931\u4e86\u4e00\u6837\u3002\u201d\u53f6\u5f00\u9053\uff1a\u201c\u4ed6\u4eec\u5c31\u7b97\u662f\u5341\u4e8c\u4e2a\u6728\u5934\u4eba\uff0c\u8981\u627e\u4e2a\u5730\u65b9\u628a\u4ed6\u4eec\u85cf\u8d77\u6765\uff0c\u4e5f\u4e0d\u662f\u4ef6\u5bb9 \u6613\u4e8b\u3002\u201d \u3000\u3000\u4e0a\u5b98\uff0c\u5c0f\u4ed9\u53f9\u9053\uff1a\u201c\u6240\u4ee5\u6211\u624d\u8ba4\u4e3a\u90a3\u4e2a\u4eba\u5f88\u53ef\u80fd\u6bd4\u5415\u8fea\u4ed6\u4eec\u66f4\u53ef\u6015\u3002\u201d \u3000\u3000\u53f6\u5f00\u7684\u8868\u60c5\u4e5f\u53d8\u5f97\u5f88\u4e25\u8083\uff0c\u9053\uff1a\u201c\u76f4\u5230\u73b0\u5728\uff0c\u4f60\u8fd8\u4e0d\u77e5\u9053\u4ed6\u662f\u4e2a\u4ec0\u4e48\u6837\u7684\u4eba\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6211\u53ea\u77e5\u9053\u4ed6\u662f\u6628\u5929\u624d\u51fa\u73b0\u7684\uff0c\u5728\u8fd9\u4e48\u51b7\u7684\u5929\u6c14\u91cc\uff0c\u4ed6\u8eab\u4e0a\u7a7f\u5f97\u5374\u5f88\u5355 \u8584\uff0c\u5934\u4e0a\u5c45\u7136\u8fd8\u5e26\u7740\u9876\u5927\u8349\u5e3d\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u8fd8\u6709\u5462\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6ca1\u6709\u4e86\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u96be\u9053\u8fde\u4ed6\u662f\u4ece\u54ea\u91cc\u6765\u7684\u90fd\u4e0d\u77e5\u9053\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u5979\u53f9\u4e86\u53e3\u6c14\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u5c31\u56e0\u4e3a\u6211\u4e0d\u77e5\u9053\uff0c\u6240\u4ee5\u624d\u6d3e\u4eba\u53bb\u6253\u542c\u3002\u201d \u3000\u3000\u53f6\u5f00\u4e5f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u770b\u6765\u4f60\u77e5\u9053\u7684\u4e8b\u4e5f\u5e76\u4e0d\u5927\u591a\u3002\u201d\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4f60\u77e5\u9053\u7684\u96be\u9053 \u6bd4\u6211\u591a\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u53ea\u591a\u4e00\u70b9\u3002\u201d \u3000\u3000\u201c\u4f60\u8fd8\u77e5\u9053\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u81f3\u5c11\u5df2\u6709\u70b9\u7ebf\u7d22\uff0c\u53ef\u4ee5\u627e\u5f97\u5230\u5e03\u8fbe\u62c9\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u5b64\u5cf0\u5929\u738b\uff1f\u201d \u3000\u3000\u53f6\u5f00\u70b9\u70b9\u5934\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4f60\u5df2\u77e5\u9053\u4ed6\u662f\u4e2a\u4ec0\u4e48\u6837\u7684\u4eba\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4ed6\u7684\u624b\u4e0a\u529f\u592b\u5389\u5bb3\uff0c\u800c\u4e14\u5df2\u53d7\u4e86\u91cd\u4f24\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u773c\u6674\u4eae\u4e86\uff0c\u9053\uff1a\u201c\u624b\u4e0a\u529f\u592b\u6700\u5389\u5bb3\u7684\u662f\u5415\u8fea\uff0c\u5374\u4e0d\u77e5\u9053\u4ed6\u662f\u4e0d\u662f\u5df2\u53d7\u4e86\u91cd \u4f24\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u8981\u67e5\u51fa\u8fd9\u4e00\u70b9\u5e76\u4e0d\u96be\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4f60\u51c6\u5907\u53bb\u627e\u4ed6\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u53cd\u5bf9\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u6211\u53ea\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u53f6\u5f00\u7b11\u4e86\u7b11\uff0c\u66ff\u5979\u8bf4\u4e86\u4e0b\u53bb\uff1a\u201c\u53ea\u4e0d\u8fc7\u6015\u6211\u4e5f\u50cf\u90a3\u4e9b\u4eba\u4e00\u6837\u5ffd\u7136\u4e0d\u89c1\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u4e5f\u7b11\u4e86\uff0c\u770b\u7740\u4ed6\u751c\u751c\u5730\u7b11\u7740\u9053\uff1a\u201c\u8fd9\u6b21\u6211\u7edd\u4e0d\u4f1a\u8ba9\u4f60\u53c8\u4e0d\u89c1\u4e86\uff0c\u6211\u2026\u2026\u201d \u3000\u3000\u8fd9\u6b21\u53f6\u5f00\u6ca1\u6709\u66ff\u5979\u8bf4\u4e0b\u53bb\uff0c\u4e5f\u6ca1\u6709\u8ba9\u5979\u8bf4\u4e0b\u53bb\uff0c\u5ffd\u7136\u8d77\u6765\uff0c\u9053\uff1a\u201c\u6240\u4ee5\u6211\u6700\u597d\u8fd8\u662f\u4e58\u6ca1 \u6709\u9189\u7684\u65f6\u5019\u8d76\u5feb\u8d70\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4f60\u73b0\u5728\u5c31\u8981\u53bb\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u8981\u6211\u7684\u4eba\uff0c\u4e0d\u6b62\u5415\u8fea\u4e00\u4e2a\uff0c\u6768\u5929\u548c\u97e9\u8d1e\u7684\u624b\u4e0a\u529f\u592b\u4e5f\u4e0d\u9519\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u83ab\u5fd8\u8bb0\u8fd8\u6709\u90a3\u4e2a\u51ac\u5929\u6234\u8349\u5e3d\u7684\u4eba\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u5927\u76f8\u56fd\u5bfa\u540e\u9762\uff0c\u8fd8\u6709\u4e2a\u5341\u65b9\u7af9\u6797\u5bfa\uff1f\u201d \u3000\u3000\u53f6\u5f00\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u542c\u8bf4\u90a3\u91cc\u7684\u7d20\u658b\u5f88\u4e0d\u9519\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4ed6\u6628\u5929\u665a\u4e0a\u5c31\u4f4f\u5728\u90a3\u91cc\u3002\u201d\u53f6\u5f00\u9053\uff1a\u201c\u6768\u5929\u5462\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4f60\u8981\u5148\u53bb\u627e\u4ed6\uff1f\u201d \u3000\u3000\u53f6\u5f00\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u83ab\u5fd8\u8bb0\u4ed6\u662f\u6211\u7684\u8001\u670b\u53cb\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u4e5f\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u65e2\u7136\u662f\u4ed6\u7684\u8001\u670b\u53cb\uff0c\u5c31\u8be5\u77e5\u9053\u4ed6\u6700\u559c\u6b22\u7684\u662f\u4ec0\u4e48\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u5973\u4eba\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u54ea\u79cd\u5973\u4eba\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u5be1\u5987\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u5fae\u7b11\u9053\uff1a\u201c\u8fd9\u6761\u8857\u8ddf\u957f\u5b89\u57ce\u91cc\u7684\u90a3\u6761\u8857\u5b8c\u5168\u4e00\u6837\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u8fd9\u6761\u8857\u4e0a\u4e5f\u6709\u4e2a\u738b\u5be1\u5987\u8c46\u8150\u5e97\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u7b11\u9053\uff1a\u201c\u8fd9\u6761\u8857\u4e0a\u7684\u738b\u5be1\u5987\u4e5f\u662f\u4e2a\u5f88\u98ce\u6d41\u7684\u5be1\u5987\u3002\u201d \u3000\u3000\u53f6\u5f00\u6545\u610f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u6768\u5929\u5df2\u7ecf\u5148\u53bb\u4e86\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u5ae3\u7136\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u73b0\u5728\u8d76\u7740\u53bb\u4e5f\u6ca1\u6709\u7528\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u5148\u5230\u9694\u58c1\u7684\u8336\u9986\u91cc\u53bb\u770b \u770b\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u8336\u9986\u91cc\u6709\u4ec0\u4e48\u597d\u770b\u7684\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6709\u4e2a\u5f88\u597d\u770b\u7684\u9525\u5b50\u3002\u201d \u3000\u3000\u53f6\u5f00\u5fae\u7b11\u7740\u8d70\u51fa\u53bb\uff0c\u9053\uff1a\u201c\u6211\u53ea\u5e0c\u671b\u8fd9\u9525\u5b50\u83ab\u8981\u628a\u6211\u9525\u51fa\u4e2a\u5927\u6d1e\u6765\u3002\u201d \u3000\u3000\u65e0\u8bba\u591a\u597d\u770b\u7684\u9525\u5b50\uff0c\u82e5\u662f\u9525\u5230\u4f60\u8eab\u4e0a\u65f6\uff0c\u4f60\u5c31\u4e0d\u4f1a\u89c9\u5f97\u5b83\u597d\u770b\u4e86\u3002 \u3000\u3000\u97e9\u8d1e\u65e2\u4e0d\u662f\u4e2a\u5f88\u597d\u770b\u7684\u9525\u5b50\uff0c\u4e5f\u4e0d\u80fd\u7b97\u662f\u4e2a\u5f88\u597d\u770b\u7684\u4eba\uff0c\u65e0\u8bba\u8c01\u7684\u9f3b\u5b50\u88ab\u4eba\u6253\u6241\u4e86\u4e4b \u540e\uff0c\u90fd\u4e0d\u4f1a\u5f88\u597d\u770b\u7684\u3002\u53ef\u662f\u4ed6\u4eca\u5929\u6c14\u8272\u770b\u6765\u5012\u4e0d\u9519\uff0c\u4e0d\u4f46\u7ea2\u5149\u6ee1\u9762\uff0c\u800c\u4e14\u7cbe\u795e\u6296\u64de\u3002\u65e0\u8bba\u8c01 \u90fd\u770b\u51fa\u4ed6\u7edd\u4e0d\u50cf\u662f\u4e2a\u53d7\u4e86\u91cd\u4f24\u7684\u4eba\u3002 \u3000\u3000\u4ed6\u770b\u89c1\u53f6\u5f00\uff0c\u7acb\u523b\u5c31\u7ad9\u8d77\u6765\uff0c\u5fae\u7b11\u7740\u62db\u547c\uff1a\u201c\u5750\u4e0b\u6765\u559d\u676f\u8336\u5982\u4f55\uff1f\u201d \u3000\u3000\u53f6\u5f00\u6447\u6447\u5934\u3002 \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u6765\u559d\u676f\u9152\uff1f\u201d \u3000\u3000\u53f6\u5f00\u53c8\u6447\u6447\u5934\u3002 \u3000\u3000\u97e9\u8d1e\u9053\uff1a\u201c\u8fd9\u91cc\u7684\u70b9\u5fc3\u4e5f\u4e0d\u9519\uff0c\u4f60\u60f3\u4e0d\u60f3\u5403\u70b9\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u53f6\u5f00\u5ffd\u7136\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u73b0\u5728\u6211\u552f\u4e00\u60f3\u5403\u7684\uff0c\u53ea\u6709\u8c46\u8150\u3002\u201d \u3000\u3000\u738b\u5be1\u5987\u8c46\u8150\u5e97\u5356\u7684\u5e76\u4e0d\u662f\u751f\u8c46\u8150\uff0c\u662f\u90a3\u79cd\u4e00\u5757\u5757\u716e\u719f\u4e86\u7684\uff0c\u716e\u5f97\u4e0a\u9762\u5df2\u6709\u4e86\u4e00\u4e2a\u4e2a\u5cf0\u7a9d \u822c\u5c0f\u6d1e\u7684\u8001\u8c46\u8150\uff0c\u738b\u5be1\u5987\u5374\u4e0d\u8001\uff0c\u8c46\u8150\u662f\u716e\u8001\u4e86\u7684\u597d\u5403\uff0c\u4eba\u5374\u662f\u534a\u8001\u7684\u98ce\u6d41\u3002 \u3000\u3000\u534a\u8001\u7684\u5f90\u5a18\uff0c\u5356\u719f\u900f\u4e86\u7684\u8001\u8c46\u8150\uff0c\u751f\u610f\u5f53\u7136\u4e0d\u9519\u3002\u53ea\u53ef\u60dc\u8fd9\u91cc\u5e76\u4e0d\u662f\u957f\u5b89\u57ce\uff0c\u738b\u5be1\u5987\u7a7f \u7740\u4e00\u8eab\u9ed1\u7f0e\u5b50\u7684\u5c0f\u68c9\u8884\uff0c\u6ee1\u5934\u9ed1\u6f06\u6f06\u7684\u5934\u53d1\uff0c\u677e\u677e\u5730\u633d\u4e86\u4e2a\u9afb\uff0c\u66f4\u663e\u5f97\u4e00\u5f20\u6e05\u6c34\u9e2d\u86cb\u8138\u767d\u91cc \u900f\u7ea2\uff0c\u7ea2\u91cc\u900f\u767d\uff0c\u5979\u770b\u6765\u4e00\u70b9\u4e5f\u4e0d\u8001\uff0c\u7b80\u76f4\u6bd4\u5ae9\u8c46\u8150\u8fd8\u8981\u5ae9\u5f97\u591a\u3002 \u3000\u3000\u6700\u8981\u547d\u7684\uff0c\u5374\u8fd8\u662f\u5979\u90a3\u53cc\u773c\u775b\uff0c\u5c0f\u5c0f\u7684\uff0c\u5f2f\u5f2f\u7684\uff0c\u7b11\u8d77\u6765\u7684\u65f6\u5019\u5c31\u50cf\u662f\u4e00\u5f2f\u65b0\u6708\uff0c\u53c8\u50cf \u662f\u4e2a\u94a9\u5b50\uff0c\u597d\u50cf\u4e00\u4e0b\u5b50\u5c31\u4f1a\u628a\u4f60\u7684\u9b42\u52fe\u8d70\u3002 \u3000\u3000\u73b0\u5728\u5979\u8fd9\u53cc\u773c\u775b\u6b63\u4e0a\u4e0b\u8198\u7740\u53f6\u5f00\uff0c\u5ae3\u7136\u9053\uff1a\u201c\u5ba2\u5b98\u7684\u4eec\u8150\u4e0a\u8981\u7528\u4ec0\u4e48\u4f5c\u6599\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u4e0d\u5403\u8c46\u8150\u3002\u201d \u3000\u3000\u738b\u5be1\u5987\u9053\uff1a\u201c\u8fd9\u8c46\u8150\u4e0d\u597d\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u8fd9\u8c46\u8150\u597d\u6781\u4e86\uff0c\u6211\u4e5f\u5f88\u60f3\u5403\u4e24\u5757\uff0c\u53ea\u53ef\u60dc\u6211\u4e0d\u6562\u3002\u201d \u3000\u3000\u738b\u5be1\u5987\u7b11\u5f97\u66f4\u5a9a\uff0c\u9053\uff1a\u201c\u8fd9\u4e48\u5927\u4e00\u4e2a\u5927\u7537\u4eba\uff0c\u8fde\u8c46\u8150\u90fd\u4e0d\u6562\u5403\uff1f\u201d \u3000\u3000\u53f6\u5f00\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u522b\u4eba\u7684\u8c46\u8150\u6211\u6562\u5403\uff0c\u4f60\u7684\u8c46\u8150\u6211\u5374\u4e0d\u6562\u5403\u3002\u201d \u3000\u3000\u738b\u5be1\u5987\u5ffd\u7136\u4e0d\u7b11\u4e86\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u662f\u6765\u627e\u6768\u5929\u7684\uff1f\u201d \u3000\u3000\u53f6\u5f00\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4ed6\u5728\u4e0d\u5728\uff1f\u201d \u3000\u3000\u738b\u5be1\u5987\u7528\u4e00\u6839\u6c34\u8471\u822c\u7684\u624b\u6307\u5f80\u540e\u9762\u70b9\u4e86\u70b9\uff0c\u597d\u50cf\u8fde\u770b\u90fd\u61d2\u5f97\u518d\u770b\u53f6\u5f00\u4e00\u773c\u3002 \u3000\u3000\u6709\u5f88\u591a\u5973\u4eba\u53ea\u559c\u6b22\u6709\u91ce\u5fc3\u7684\u7537\u4eba\uff0c\u4f60\u82e5\u5bf9\u5979\u6ca1\u6709\u91ce\u5fc3\uff0c\u5979\u5bf9\u4f60\u4e5f\u4e0d\u4f1a\u6709\u5174\u8da3\u3002 \u3000\u3000\u53f6\u5f00\u7b11\u4e86\uff0c\u4ed6\u5fae\u7b11\u7740\u8d70\u8fdb\u53bb\uff0c\u5ffd\u53c8\u56de\u8fc7\u5934\uff0c\u7b11\u9053\uff1a\u201c\u5176\u5b9e\u6211\u7684\u80c6\u5b50\u4e5f\u5e76\u4e0d\u662f\u4e00\u76f4\u90fd\u8fd9\u4e48 \u5c0f\u7684\u3002\u201d \u3000\u3000\u738b\u5be1\u5987\u53c8\u77aa\u4e86\u4ed6\u4e00\u773c\uff0c\u54ac\u7740\u5634\u5507\u9053\uff1a\u201c\u4eca\u5929\u4f60\u7684\u80c6\u5b50\u4e3a\u4ec0\u4e48\u7279\u522b\u5c0f\uff1f\u201d \u3000\u3000\u53f6\u5f00\u6068\u6068\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u4e0d\u60f3\u88ab\u72d0\u72f8\u54ac\u4e00\u53e3\u3002\u201d \u3000\u3000\u6768\u5929\u770b\u6765\u5e76\u4e0d\u50cf\u662f\u6761\u4f1a\u54ac\u4eba\u7684\u72d0\u72f8\uff0c\u65e0\u8bba\u591a\u53ef\u6015\u7684\u4eba\uff0c\u5728\u6d17\u6fa1\u7684\u65f6\u5019\uff0c\u90fd\u4f1a\u53d8\u5f97\u548c\u5584\u4e9b \u7684\u3002\u6768\u5929\u6b63\u5728\u6d17\u6fa1\uff0c\u4ed6\u6ce1\u5728\u4e00\u5927\u76c6\u70ed\u6c34\u91cc\uff0c\u5c3d\u91cf\u653e\u677e\u4e86\u56db\u80a2\uff0c\u770b\u6765\u6709\u70b9\u50cf\u662f\u6761\u61d2\u6d0b\u6d0b\u7684\u6c34 \u736d\u3002\u4ed6\u7684\u76ae\u80a4\u4e5f\u50cf\u662f\u6c34\u736d\u822c\u5149\u6ed1\uff0c\u5168\u8eab\u4e0a\u4e0b\u8fde\u4e00\u70b9\u4f24\u75d5\u90fd\u6ca1\u6709\uff0c\u53f6\u5f00\u5fcd\u4e0d\u4f4f\u53f9\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u6768\u5929\u770b\u7740\u4ed6\uff0c\u5fae\u7b11\u9053\uff1a\u201c\u597d\u670b\u53cb\u89c1\u9762\uff0c\u4f60\u4e3a\u4ec0\u4e48\u8981\u53f9\u6c14\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u56e0\u4e3a\u4f60\u6ca1\u6709\u53d7\u4f24\u3002\u201d \u3000\u3000\u6768\u5929\u9053\uff1a\u201c\u6211\u53d7\u4f24\u4e86\uff0c\u4f60\u624d\u9ad8\u5174\uff1f\u201d \u3000\u3000\u53f6\u5f00\u5ffd\u7136\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u60f3\u5403\u8c46\u8150\u3002\u201d \u3000\u3000\u6768\u5929\u5927\u7b11\uff0c\u9053\uff1a\u201c\u73b0\u5728\u6211\u6b63\u5728\u6d17\u6fa1\uff0c\u5c82\u975e\u6b63\u662f\u4f60\u7684\u597d\u673a\u4f1a\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u662f\u4ec0\u4e48\u597d\u673a\u4f1a\uff1f\u201d \u3000\u3000\u6768\u5929\u9053\uff1a\u201c\u73b0\u5728\u968f\u4fbf\u4f60\u5728\u5916\u9762\u5e72\u4ec0\u4e48\uff0c\u6211\u603b\u4e0d\u80fd\u8d64\u6761\u6761\u5730\u8dd1\u51fa\u53bb\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u670b\u53cb\u59bb\uff0c\u4e0d\u53ef\u620f\u3002\u201d \u3000\u3000\u6768\u5929\u9053\uff1a\u201c\u8981\u620f\u670b\u53cb\u59bb\uff0c\u8981\u7b49\u670b\u53cb\u6b7b\u540e\u620f\u3002\u201d \u3000\u3000\u53f6\u5f00\u53f9\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u4f60\u8fd8\u6ca1\u6709\u6b7b\u3002\u201d \u3000\u3000\u6768\u5929\u9053\uff1a\u201c\u90a3\u4e48\u6211\u4eec\u73b0\u5728\u662f\u670b\u53cb\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u672c\u6765\u4e0d\u662f\u7684\uff0c\u73b0\u5728\u53c8\u662f\u4e86\u3002\u201d \u3000\u3000\u6768\u5929\u76ef\u7740\u4ed6\uff0c\u773c\u775b\u91cc\u6e10\u6e10\u53d1\u51fa\u4e86\u5149\uff0c\u5200\u950b\u822c\u7684\u5149\u3002\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u4e5f\u6765\u4e0b\u6c34\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u60f3\u4e0d\u5230\uff1f\u201d \u3000\u3000\u6768\u5929\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u8981\u4e0b\u6c34\uff1f\u201d \u3000\u3000\u53f6\u5f00\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u4e0d\u8be5\u95ee\u6211\u7684\uff0c\u4f60\u81ea\u5df1\u5c82\u975e\u4e5f\u6ce1\u5728\u6c34\u91cc\uff1f\u201d \u3000\u3000\u6768\u5929\u9053\uff1a\u201c\u90a3\u53ea\u56e0\u4e3a\u6211\u5df2\u51fa\u4e0b\u53bb\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u82e5\u6709\u4eba\u6765\u62c9\u4f60\u4e00\u628a\u5462\uff1f\u201d \u3000\u3000\u6768\u5929\u9053\uff1a\u201c\u8c01\u80af\u62c9\u6211\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u3002\u201d \u3000\u3000\u4ed6\u679c\u7136\u4f38\u51fa\u4e86\u624b\u3002 \u3000\u3000\u6768\u5929\u5374\u6ca1\u6709\u63a5\u8fc7\u53bb\uff0c\u6de1\u6de1\u9053\uff1a\u201c\u51fa\u53bb\u592a\u51b7\uff0c\u8fd8\u662f\u6c34\u91cc\u6696\u548c\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u65e0\u8bba\u591a\u6696\u548c\u7684\u6c34\uff0c\u603b\u6709\u51b7\u7684\u65f6\u5019\u3002\u201d \u3000\u3000\u6768\u5929\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u5c31\u8be5\u4e58\u65e9\u8df3\u51fa\u53bb\u3002\u201d \u3000\u3000\u53f6\u5f00\u53c8\u7b11\u4e86\u7b11\u9053\uff1a\u201c\u4f60\u662f\u5728\u529d\u6211\uff0c\u8fd8\u662f\u5728\u8d76\u6211\u8d70\uff1f\u201d \u3000\u3000\u6768\u5929\u9053\uff1a\u201c\u4f60\u770b\u5462\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u5acc\u6c34\u91cc\u7684\u4eba\u5df2\u5927\u591a\uff0c\u592a\u6324\uff1f\u201d \u3000\u3000\u6768\u5929\u51b7\u7b11\uff0c\u9053\uff1a\u201c\u8d70\u4e0d\u8d70\u90fd\u968f\u4fbf\u4f60\uff0c\u53ea\u4e0d\u8fc7\u6211\u4eec\u603b\u7b97\u8fd8\u662f\u670b\u53cb\uff0c\u6709\u53e5\u8bdd\u6211\u4e0d\u80fd\u4e0d\u8bf4\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u8bf4\u3002\u201d \u3000\u3000\u6768\u5929\u9053\uff1a\u201c\u5343\u4e07\u4e0d\u8981\u53bb\u627e\u90a3\u4e2a\u6234\u8349\u5e3d\u7684\u4eba\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6768\u5929\u95ed\u4e0a\u4e86\u773c\u775b\uff0c\u4e0d\u518d\u5f00\u53e3\u3002 \u3000\u3000\u53f6\u5f00\u53c8\u95ee\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\u6211\u8981\u53bb\u627e\u4ed6\uff1f\u201d \u3000\u3000\u6768\u5929\u8fd8\u662f\u4e0d\u5f00\u53e3\uff0c\u6c34\u5f88\u70ed\uff0c\u70ed\u6c14\u817e\u817e\uff0c\u5c31\u597d\u50cf\u662f\u96fe\u4e00\u6837\u3002 \u3000\u3000\u53f6\u5f00\u5ffd\u7136\u53c8\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u7684\u786e\u8fd8\u662f\u6ce1\u5728\u6c34\u91cc\u7684\u597d\uff0c\u4ece\u8fd9\u4e48\u70ed\u7684\u6c34\u91cc\u51fa\u6765\uff0c\u4e00\u5b9a\u4f1a\u7740 \u51c9\u3002\u201d \u3000\u3000\u53f6\u5f00\u5df2\u8d70\u4e86\u3002 \u3000\u3000\u6768\u5929\u5374\u8fd8\u662f\u95ed\u7740\u773c\u775b\u6ce1\u5728\u6c34\u91cc\uff0c\u7b49\u5230\u6c34\u7684\u70ed\u6c14\u6d88\u6563\u65f6\uff0c\u624d\u770b\u51fa\u4ed6\u7684\u8138\u8272\u60e8\u767d\uff0c\u5c31\u597d\u50cf\u771f \u7684\u5df2\u6ca1\u6709\u529b\u6c14\u7ad9\u8d77\u6765\uff0c\u53ef\u662f\uff0c\u6c34\u5df2\u5feb\u51c9\u4e86\uff0c\u4ed6\u5df2\u4e0d\u80fd\u4e0d\u7ad9\u8d77\u6765\uff0c\u6c34\u4ece\u4ed6\u7684\u80a9\u5934\u6d41\u4e0b\uff0c\u6c34\u91cc\u7adf \u5e26\u7740\u8840\u4e1d\uff0c\u8840\u662f\u4ece\u54ea\u91cc\u6765\u7684\uff1f\u738b\u5be1\u5987\u5df2\u6084\u4fcf\u5730\u8d70\u8fdb\u6765\uff0c\u770b\u7740\u4ed6\uff0c\u773c\u775b\u91cc\u5145\u6ee1\u4e86\u601c\u60dc\u3002 \u3000\u3000\u6768\u5929\u7ad9\u8d77\u6765\u65f6\uff0c\u60e8\u81ea\u7684\u8138\u7ade\u5df2\u56e0\u75db\u82e6\u800c\u626d\u66f2\uff0c\u560e\u58f0\u9053\uff1a\u201c\u5916\u9762\u4f1a\u4e0d\u4f1a\u6709\u4eba\u95ef\u8fdb\u6765\uff1f\u201d \u3000\u3000\u738b\u5be1\u5987\u6447\u6447\u5934\uff0c\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u4f60\u7a76\u7adf\u662f\u600e\u4e48\u53d7\u7684\u4f24\uff1f\u4e3a\u4ec0\u4e48\u6015\u4eba\u770b\u89c1\uff1f\u201d \u3000\u3000\u6768\u5929\u54ac\u54ac\u7259\uff0c\u6ca1\u6709\u56de\u7b54\u8fd9\u53e5\u8bdd\uff0c\u5374\u4ece\u80a9\u5934\u4e0a\u6495\u4e0b\u4e00\u5c42\u76ae\u3002\u4e00\u5c42\u548c\u4ed6\u76ae\u80a4\u540c\u6837\u989c\u8272\u7684\u8584 \u76ae\uff0c\u4ed6\u6495\u4e0b\u6765\uff0c\u9c9c\u8840\u5c31\u6d41\u6ee1\u4e86\u4ed6\u7684\u80f8\u819b\u2026\u2026 \u3000\u3000\u4e00\u8f86\u5927\u8f66\u505c\u5728\u8def\u53e3\uff0c\u4e0a\u5b98\u5c0f\u4ed9\u501a\u5728\u8f6e\u4e0a\u7b49\u7740\u3002\u5979\u770b\u89c1\u53f6\u5f00\u8d70\u8fc7\u6765\u65f6\uff0c\u88ab\u9633\u5149\u6652\u5f97\u53d1\u7ea2\u7684 \u7b11\u8138\u66f4\u7f8e\u5982\u6625\u82b1\u3002\u4f60\u53ea\u8981\u770b\u89c1\u5979\uff0c\u5c31\u4f1a\u89c9\u5f97\u6625\u5929\u5df2\u4e0d\u8fdc\u4e86\u3002 \u3000\u3000\u53f6\u5f00\u5fc3\u91cc\u5728\u53f9\u606f\uff0c\u56e0\u4e3a\u4ed6\u5ffd\u7136\u60f3\u8d77\u4e86\u4ee5\u524d\u522b\u4eba\u63cf\u8ff0\u6797\u4ed9\u513f\u7684\u8bdd\u3002 \u3000\u3000\u2014\u2014\u2014\u4e2a\u4ed9\u5b50\u822c\u7f8e\u4e3d\u7684\u5973\u4eba\uff0c\u5374\u4e13\u95e8\u5f15\u8bf1\u7537\u4eba\u4e0b\u5730\u72f1\u3002 \u3000\u3000\u8fd9\u65ec\u8bdd\u82e5\u7528\u6765\u5f62\u5bb9\u4e0a\u5bab\u5c0f\u4ed9\uff0c\u662f\u4e0d\u662f\u4e5f\u540c\u6837\u6070\u5f53\uff1f \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u5728\u7b49\u7740\u95ee\uff1a\u201c\u4f60\u5df2\u627e\u5230\u4e86\u4ed6\u4eec\uff1f\u201d \u3000\u3000\u201c\u55ef\u3002\u201d \u3000\u3000\u201c\u4ed6\u4eec\u4e24\u4e2a\u4eba\u90fd\u6ca1\u6709\u53d7\u4f24\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u53f6\u5f00\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u81f3\u5c11\u6211\u770b\u4e0d\u51fa\u3002\u201d \u3000\u3000\u201c\u6240\u4ee5\u4ed6\u4eec\u90fd\u4e0d\u4f1a\u662f\u5b64\u5cf0\u3002\u201d \u3000\u3000\u53f6\u5f00\u70b9\u70b9\u5934\u3002\u4ed6\u7684\u786e\u6ca1\u6709\u770b\u51fa\u6768\u5929\u7684\u4f24\u53e3\uff0c\u8d34\u5728\u6768\u5929\u80a9\u4e0a\u7684\u90a3\u5c42\u76ae\u5728\u6c34\u4e2d\u770b\u6765\uff0c\u5c31\u8ddf\u8089 \u8272\u5b8c\u5168\u4e00\u6837\uff0c\u4ed6\u4e5f\u60f3\u4e0d\u5230\u4e00\u4e2a\u53d7\u4e86\u4f24\u7684\u4eba\uff0c\u8fd8\u4f1a\u6ce1\u5728\u6c34\u91cc\u3002 \u3000\u3000\u4e0a\u2018\u8a00\u5c0f\u4ed9\u9053\uff1a\u201c\u53ea\u4e0d\u8fc7\uff0c\u5c31\u7b97\u4ed6\u4eec\u6ca1\u6709\u53d7\u4f24\uff0c\u4e5f\u5e76\u4e0d\u80fd\u8bc1\u660e\u4ed6\u4eec\u4e0d\u662f\u9b54\u6559\u4e2d\u7684\u4eba\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4f46\u4f60\u5374\u5df2\u4e0d\u51c6\u5907\u518d\u8ffd\u67e5\u4e0b\u53bb\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4ed6\u4eec\u662f\u4f60\u7684\u4eba\uff0c\u8981\u8ffd\u67e5\u4e0b\u53bb\uff0c\u4e5f\u662f\u4f60\u7684\u4e8b\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u5df2\u51c6\u5907\u8d70\uff1f\u201d \u3000\u3000\u53f6\u5f00\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u5c82\u975e\u4e5f\u65e9\u5c31\u66ff\u6211\u51c6\u5907\u597d\u4e00\u8f86\u9a6c\u8f66\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u4e5f\u7b11\u4e86\uff0c\u7b11\u5f97\u5374\u6709\u4e9b\u5e7d\u6028\uff1a\u201c\u90a3\u53ea\u56e0\u4e3a\u6211\u4e5f\u77e5\u9053\u6211\u662f\u7559\u4e0d\u4f4f\u4f60\u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u8df3\u4e0a\u9a6c\u8f66\uff0c\u5ffd\u7136\u53c8\u9053\uff1a\u201c\u6768\u5929\u521a\u624d\u529d\u4e86\u6211\u4e00\u53e5\u8bdd\u3002\u201d \u3000\u3000\u4e0a\u2018\u81ea\u5c0f\u4ed9\u9053\uff1a\u201c\u4ec0\u4e48\u8bdd\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4ed6\u529d\u6211\u5343\u4e07\u4e0d\u8981\u53bb\u627e\u90a3\u4e2a\u6234\u8349\u5e3d\u7684\u4eba\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u73b0\u5728\u51c6\u5907\u5230\u54ea\u91cc\u53bb\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u53bb\u627e\u90a3\u4e2a\u6234\u8349\u5e3d\u7684\u4eba\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u522b\u4eba\u529d\u4f60\u7684\u8bdd\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4ece\u6765\u90fd\u4e0d\u542c\uff1f\u201d \u3000\u3000\u53f6\u5f00\u95ed\u4e0a\u8f66\u95e8\uff0c\u5374\u53c8\u4ece\u7a97\u5b50\u91cc\u4f38\u51fa\u5934\uff0c\u5fae\u7b11\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u8fd9\u4eba\u4e00\u5411\u90e8\u6709\u79cd\u75c5\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4ec0\u4e48\u75c5\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff0c\u201c\u7b28\u75c5\u3002\u201d \u3000\u3000\u9a6c\u8f66\u626c\u8d77\u4e86\u4e00\u7247\u6c99\u5c18\u3002\u8f66\u5df2\u8fdc\uff0c\u4e0a\u5b98\u5c0f\u4ed9\u8138\u4e0a\u5374\u8fd8\u5e26\u7740\u751c\u871c\u7684\u5fae\u7b11\u3002\u56e0\u4e3a\u53f6\u5f00\u7684\u5934\u8fd8\u4f38 \u5728\u7a97\u5b50\u5916\u9762\uff0c\u770b\u7740\u5979\u3002\u5979\u5fae\u7b11\u7740\uff0c\u626c\u8d77\u624b\u91cc\u7684\u4e1d\u4e2d\u3002\u5c31\u5728\u5979\u7684\u624b\u81c2\u62ac\u8d77\u65f6\uff0c\u5979\u7684\u7b11\u5bb9\u5ffd\u7136\u6d88 \u5931\uff0c\u88ab\u9633\u5149\u6652\u5f97\u53d1\u7ea2\u7684\u8138\uff0c\u4e5f\u7a81\u7136\u53d8\u5f97\u60e8\u767d\uff0c\u53ea\u53ef\u60dc\u8fd9\u65f6\u53f6\u5f00\u5df2\u8f6c\u8fc7\u5c71\u62d7\uff0c\u770b\u4e0d\u89c1\u4e86\u3002 \u7b2c\u4e8c\u5341\u4e03\u7ae0\u3000\u5bd2\u591c\u9ed1\u661f>> \u53e4\u9f99\u300a\u4e5d\u6708\u9e70\u98de\u300b \u7b2c\u4e8c\u5341\u4e03\u7ae0\u3000\u5bd2\u591c\u9ed1\u661f \u3000\u3000\u7985\u9662\u91cc\u6e05\u9759\u800c\u5e7d\u96c5\uff0c\u56e0\u4e3a\u9662\u5b50\u91cc\u6709\u7af9\u3002 \u3000\u3000\u7af9\u6797\u3002 \u3000\u3000\u6709\u7af9\u6797\u7684\u9662\u5b50\uff0c\u603b\u662f\u4f1a\u4ee4\u4eba\u89c9\u5f97\u5206\u5916\u5e7d\u96c5\u7684\u3002 \u3000\u3000\u5c24\u5176\u662f\u5728\u9ec4\u660f\u65f6\uff0c\u98ce\u5439\u7740\u7af9\u53f6\uff0c\u58f0\u97f3\u542c\u6765\u5c31\u4eff\u4f5b\u662f\u6d77\u6d6a\u3002 \u3000\u3000\u53f6\u5f00\u6b63\u5f98\u5f8a\u5728\u7af9\u6797\u524d\u3002 \u3000\u3000\u201c\u6211\u82e5\u65e9\u77e5\u9053\u957f\u5b89\u57ce\u91cc\u8fd8\u6709\u4e2a\u8fd9\u4e48\u5e7d\u9759\u7684\u5730\u65b9\uff0c\u6211\u4e5f\u4f1a\u4f4f\u5728\u8fd9\u91cc\u7684\u3002\u201d \u3000\u3000\u4ed6\u53f9\u606f\u7740\u9053\uff1a\u201c\u8fd9\u5730\u65b9\u7684\u4eba\u597d\u50cf\u662f\u4e0d\u5927\u591a\uff0c\u201d\u4ed6\u5e76\u4e0d\u662f\u4e00\u4e2a\u4eba\u5728\u81ea\u8a00\u81ea\u8bed\uff0c\u8fd9\u53e5\u8bdd\u4ed6\u662f \u5bf9\u82e6\u7af9\u8bf4\u7684\u3002 \u3000\u3000\u82e6\u7af9\u5c31\u662f\u5341\u65b9\u7af9\u6797\u5bfa\u7684\u77e5\u5ba2\u50e7\u3002 \u3000\u3000\u4ed6\u4eba\u5982\u5176\u540d\uff0c\u6e05\u7626\u5982\u7af9\uff0c\u867d\u65e0\u8089\uff0c\u5374\u4e0d\u4fd7\uff0c\u4ed6\u6b63\u5728\u5fae\u7b11\u7740\u4e89\u8fa9\uff1a\u201c\u5c0f\u5bfa\u7684\u65bd\u4e3b\u867d\u4e0d\u591a\uff0c \u4e5f\u4e0d\u592a\u5c11\u3002\u201d \u3000\u3000\u53f6\u5f00\u7b11\u4e86\u3002 \u3000\u3000\u4ece\u5916\u9762\u5230\u8fd9\u91cc\uff0c\u4ed6\u8fd8\u6ca1\u6709\u770b\u89c1\u4e00\u4e2a\u8fdb\u9999\u968f\u559c\u7684\u4eba\uff0c\u9662\u5b50\u91cc\u7684\u7985\u623f\u5c71\uff0c\u5bc2\u65e0\u4eba\u58f0\u3002 \u3000\u3000\u82e6\u7af9\u9053\uff1a\u201c\u8fd9\u4e03\u95f4\u7985\u623f\u90fd\u662f\u5ba2\u623f\uff0c\u672c\u6765\u5e76\u4e0d\u662f\u7a7a\u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u82e6\u7af9\u9053\uff1a\u201c\u6628\u5929\u665a\u4e0a\u4e4b\u524d\uff0c\u8fd8\u6709\u51e0\u4f4d\u65bd\u4e3b\u4f4f\u5728\u8fd9\u91cc\uff0c\u90fd\u662f\u5f88\u98ce\u96c5\u7684\u4eba\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u73b0\u5728\u5462\uff1f\u201d \u3000\u3000\u82e6\u7af9\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u73b0\u5728\u90fd\u5df2\u5230\u4e86\u5927\u76f8\u56fd\u5bfa\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4ed6\u4eec\u90fd\u662f\u6628\u5929\u665a\u4e0a\u8d70\u7684\uff1f\u201d \u3000\u3000\u82e6\u7af9\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u90a3\u4f4d\u6234\u8349\u5e3d\u7684\u767d\u65bd\u4e3b\u4e00\u6765\uff0c\u522b\u7684\u4eba\u5c31\u5168\u90e8\u8d70\u4e86\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u662f\u4ed6\u8d76\u8d70\u7684\uff1f\u201d \u3000\u3000\u82e6\u7af9\u82e6\u7b11\u9053\uff1a\u201c\u4ed6\u5e76\u6ca1\u6709\u8d76\u4eba\u8d70\uff0c\u53ef\u662f\u4ed6\u4e00\u6765\uff0c\u522b\u4eba\u5c31\u6ca1\u6cd5\u5b50\u518d\u4f4f\u4e0b\u53bb\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u82e6\u7af9\u53c8\u53f9\u4e86\u53e3\u6c14\uff0c\u6e05\u766f\u7684\u8138\u4e0a\uff0c\u5ffd\u7136\u9732\u51fa\u79cd\u5f88\u5947\u602a\u7684\u8868\u60c5\u3002 \u3000\u3000\u4ed6\u5e76\u6ca1\u6709\u76f4\u63a5\u56de\u7b54\u53f6\u5f00\u7684\u8bdd\uff0c\u5374\u6c89\u541f\u7740\u9053\uff1a\u201c\u6211\u5e26\u4f60\u5230\u4ed6\u623f\u91cc\u53bb\u770b\u770b\uff0c\u4f60\u5c31\u4f1a\u660e\u767d\u3002\u201d \u3000\u3000\u7985\u623f\u91cc\u56db\u58c1\u8427\u7136\uff0c\u4ec0\u4e48\u90fd\u6ca1\u6709\uff0c\u65e2\u6ca1\u6709\u684c\u6905\uff0c\u4e5f\u6ca1\u6709\u5e8a\u3002 \u3000\u3000\u8fd9\u4e48\u5927\u4e00\u95f4\u7985\u623f\u91cc\uff0c\u53ea\u6709\u4e24\u6839\u9489\u5b50\uff0c\u4e00\u6839\u9489\u5728\u5de6\u9762\u7684\u5899\u4e0a\uff0c\u4e00\u6839\u9489\u5728\u5bf9\u9762\u3002 \u3000\u3000\u53f6\u5f00\u53c8\u4e0d\u7981\u5728\u7b11\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u7684\u786e\u5df2\u660e\u767d\uff0c\u522b\u4eba\u4e3a\u4ec0\u4e48\u6ca1\u6cd5\u5b50\u5728\u8fd9\u91cc\u4f4f\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u201c\u5c31\u8fde\u6211\u4e5f\u4e00\u6837\u4f4f\u4e0d\u4e0b\u53bb\u3002\u201d \u3000\u3000\u4ed6\u5fae\u7b11\u7740\u9053\uff1a\u201c\u6211\u4e0d\u662f\u82cd\u8747\uff0c\u4e5f\u4e0d\u662f\u873b\u8712\uff0c\u603b\u4e0d\u80fd\u7761\u5728\u4e00\u6839\u9489\u5b50\u4e0a\u3002\u201d \u3000\u3000\u82e6\u7af9\u9053\uff1a\u201c\u8fd9\u91cc\u6709\u4e24\u6839\u9489\u5b50\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4e24\u6839\u9489\u5b50\u548c\u4e00\u6839\u9489\u5b50\u597d\u50cf\u4e5f\u6ca1\u6709\u4ec0\u4e48\u5206\u522b\u3002\u201d \u3000\u3000\u82e6\u7af9\u9053\uff1a\u201c\u6709\u5206\u522b\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u5374\u770b\u4e0d\u51fa\u5206\u522b\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u82e6\u7af9\u9053\uff1a\u201c\u4f46\u4f60\u5374\u5e94\u8be5\u60f3\u5f97\u5230\u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u82e6\u7af9\u9053\uff1a\u201c\u4e24\u6839\u9489\u5b50\uff0c\u5c31\u53ef\u4ee5\u6302\u6761\u7ef3\u5b50\u3002\u201d \u3000\u3000\u53f6\u5f00\u8fd8\u662f\u4e0d\u61c2\uff1a\u201c\u7ef3\u5b50\u6709\u4ec0\u4e48\u7528\uff1f\u201d \u3000\u3000\u82e6\u7af9\u9053\uff1a\u201c\u7ef3\u5b50\u4e0a\u53ef\u4ee5\u6302\u8863\u670d\uff0c\u4e5f\u53ef\u4ee5\u7761\u4eba\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u90a3\u4f4d\u6234\u8349\u5e3d\u7684\u767d\u65bd\u4e3b\uff0c\u665a\u4e0a\u5c31\u7761\u5728\u7ef3\u5b50\u4e0a\uff1f\u201d \u3000\u3000\u82e6\u7af9\u9053\uff1a\u201c\u800c\u4e14\u662f\u6761\u5f88\u7ec6\u7684\u7ef3\u5b50\u3002\u201d \u3000\u3000\u53f6\u5f00\u6014\u4f4f\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u82e5\u662f\u559c\u6b22\u7761\u5728\u7ef3\u4e86\u4e0a\uff0c\u90a3\u4e0d\u4f46\u813e\u6c14\u53e4\u602a\uff0c\u6b66\u529f\u4e5f\u4e00\u5b9a\u5f88\u53e4\u602a\u3002 \u3000\u3000\u82e6\u7af9\u9053\uff1a\u201c\u8fd9\u5c4b\u5b50\u91cc\u672c\u6765\u4e0d\u662f\u7a7a\u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u82e6\u7af9\u9053\uff1a\u201c\u8fd9\u91cc\u672c\u6765\u4e0d\u4f46\u6709\u684c\u6709\u5e8a\uff0c\u8fd8\u6709\u5f88\u591a\u58c1\u864e\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u684c\u6905\u662f\u4ed6\u8981\u642c\u51fa\u53bb\u7684\uff1f\u201d \u3000\u3000\u82e6\u7af9\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u58c1\u864e\u5462\uff1f\u201d \u3000\u3000\u201c\u82e6\u7af9\u8138\u4e0a\u53c8\u9732\u51fa\u90a3\u79cd\u5947\u602a\u7684\u8868\u60c5\uff0c\u9053\uff1a\u201c\u58c1\u864e\u5168\u90fd\u88ab\u4ed6\u5403\u4e86\u3002\u201d \u3000\u3000\u53f6\u5f00\u53c8\u6014\u4f4f\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u4e0d\u4f46\u559c\u6b22\u5728\u51ac\u5929\u6234\u8349\u5e3d\uff0c\u559c\u6b22\u7761\u5728\u7ef3\u5b50\u4e0a\uff0c\u8fd8\u559c\u6b22\u5403\u58c1\u864e\u3002 \u3000\u3000\u8fd9\u4e48\u53e4\u602a\u7684\u4eba\uff0c\u8fde\u53f6\u5f00\u90fd\u4ece\u672a\u770b\u89c1\u8fc7\u3002 \u3000\u3000\u4ed6\u8138\u4e0a\u4e5f\u4e0d\u7981\u9732\u51fa\u548c\u82e6\u7af9\u540c\u6837\u7684\u8868\u60c5\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u770b\u6765\u4ed6\u7684\u98df\u91cf\u597d\u50cf\u5e76\u4e0d\u5927\uff0c\u5403\u51e0\u6761\u58c1 \u864e\uff0c\u5c45\u7136\u5c31\u80fd\u5403\u70ae\u4e86\u3002\u201d \u3000\u3000\u82e6\u7af9\u9053\uff1a\u201c\u9664\u4e86\u58c1\u864e\u5916\uff0c\u4ed6\u5f53\u7136\u8fd8\u5403\u522b\u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u5403\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u82e6\u7af9\u9053\uff1a\u201c\u4f4f\u5728\u8fd9\u91cc\u7684\u65bd\u4e3b\u4eec\uff0c\u4e00\u5230\u665a\u4e0a\uff0c\u901a\u5e38\u90fd\u5f88\u5c11\u51fa\u53bb\u8d70\u52a8\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u82e6\u7af9\u9053\uff1a\u201c\u56e0\u4e3a\u5916\u9762\u6709\u86c7\uff0c\u6bd2\u86c7\u3002\u201d \u3000\u3000\u53f6\u5f00\u6115\u7136\u9053\uff1a\u201c\u86c7\u4e5f\u88ab\u4ed6\u5403\u5149\u4e86\uff1f\u201d\u82e6\u7af9\u9053\uff1a\u201c\u9664\u4e86\u86c7\u4e4b\u5916\uff0c\u8fd8\u6709\u8708\u86a3\u3002\u201d \u3000\u3000\u53f6\u5f00\u82e6\u7b11\u9053\uff1a\u201c\u539f\u6765\u4ed6\u7684\u98df\u91cf\u5e76\u4e0d\u5c0f\u3002\u201d \u3000\u3000\u82e6\u7af9\u9053\uff1a\u201c\u6240\u4ee5\u6211\u5df2\u7ecf\u5f00\u59cb\u5728\u62c5\u5fc3\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u82e6\u7af9\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8fd9\u91cc\u7684\u58c1\u864e\u548c\u6bd2\u86c7\u82e5\u662f\u5168\u90e8\u88ab\u4ed6\u5403\u5149\u4e86\uff0c\u90a3\u65f6\u4ed6\u5403\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u53f6\u5f00\u5fcd\u4e0d\u4f4f\u7b11\u9053\uff1a\u201c\u4f60\u96be\u9053\u6015\u4ed6\u5403\u4f60\uff1f\u201d \u3000\u3000\u82e6\u7af9\u53f9\u606f\u7740\uff0c\u8fd8\u6ca1\u6709\u5f00\u53e3\uff0c\u7a81\u542c\u4e00\u4e2a\u4eba\u51b7\u51b7\u9053\uff1a\u201c\u4eba\uff0c\u6709\u65f6\u6211\u4e5f\u5403\uff0c\u5374\u5f88\u5c11\u5403\u548c\u5c1a\u3002\u201d \u3000\u3000\u98ce\u5728\u5439\uff0c\u65e5\u5df2\u6c89\uff0c\u9ec4\u660f\u65f6\u7684\u7985\u9662\uff0c\u5c82\u975e\u603b\u662f\u4f1a\u663e\u5f97\u5206\u5916\u5bc2\u5bde\u5bd2\u51b7\u3002 \u3000\u3000\u8fd9\u7985\u9662\u91cc\u975e\u4f46\u5bd2\u51b7\uff0c\u800c\u4e14\u8fd8\u4eff\u4f5b\u6709\u79cd\u8bf4\u4e0d\u51fa\u7684\u8083\u6740\u8be1\u5f02\u4e4b\u610f\u3002 \u3000\u3000\u56e0\u4e3a\u9662\u5b50\u91cc\u5ffd\u7136\u51fa\u73b0\u4e86\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u4e00\u4e2a\u6234\u8349\u5e3d\u7684\u4eba\u3002 \u3000\u3000\u5728\u8fd9\u79cd\u9177\u5bd2\u7684\u5929\u6c14\u91cc\uff0c\u4ed6\u5c45\u7136\u8fd8\u7a7f\u7740\u4ef6\u5f88\u5355\u8584\u7684\u767d\u845b\u9ebb\u8863\uff0c\u5934\u4e0a\u7684\u8349\u5e3d\u5f62\u72b6\u66f4\u5947\u602a\uff0c\u770b \u6765\u5c31\u50cf\u662f\u4e2a\u6355\u9c7c\u7684\u7af9\u7bd3\u4e86\u3002 \u3000\u3000\u4ed6\u6234\u5f97\u5f88\u4f4e\uff0c\u51e0\u4e4e\u5df2\u5c06\u8138\u5168\u90fd\u63a9\u4f4f\uff0c\u53ea\u9732\u51fa\u4e00\u5f20\u8584\u8584\u7684\u5634\u3002\u4e0d\u8bf4\u8bdd\u7684\u65f6\u5019\u603b\u662f\u95ed\u5f97\u5f88 \u7d27\uff0c\u5c31\u50cf\u662f\u5200\u523b\u6210\u7684\u3002 \u3000\u3000\u53f6\u5f00\u5ffd\u7136\u7b11\u4e86\u3002 \u3000\u3000\u8d8a\u662f\u522b\u4eba\u7b11\u4e0d\u51fa\u7684\u65f6\u5019\uff0c\u4ed6\u53cd\u800c\u662f\u504f\u504f\u8981\u7b11\u3002 \u3000\u3000\u4ed6\u5fae\u7b11\u7740\u9053\uff1a\u201c\u4f60\u662f\u5f88\u5c11\u5403\u548c\u5c1a\uff1f\u8fd8\u662f\u4ece\u6765\u4e0d\u5403\uff1f\u201d \u3000\u3000\u6234\u8349\u5e3d\u7684\u767d\u8863\u4eba\u51b7\u51b7\u9053\uff1a\u201c\u6211\u901a\u5e38\u53ea\u5403\u4e00\u79cd\u4eba\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u54ea\u79cd\u4eba\uff1f\u201d \u3000\u3000\u767d\u8863\u4eba\u9053\uff1a\u201c\u8be5\u6b7b\u7684\u4eba\u3002\u201d \u3000\u3000\u53f6\u5f00\u82e6\u7b11\u9053\uff1a\u201c\u8fd9\u4e16\u4e0a\u7684\u786e\u6709\u79cd\u4eba\u5c31\u50cf\u6bd2\u86c7\u4e00\u6837\uff0c\u4f60\u82e5\u4e0d\u60f3\u62ab\u4ed6\u5403\u6389\uff0c\u5c31\u8981\u5148\u628a\u4ed6\u5403\u4e0b \u53bb\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u771f\u6b63\u8be5\u6b7b\u7684\u4eba\u5e76\u4e0d\u591a\u3002\u201d \u3000\u3000\u201c\u7684\u786e\u4e0d\u591a\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u4e5f\u50cf\u522b\u4eba\u4e00\u6837\uff0c\u5403\u4e9b\u6bd4\u8f83\u5bb9\u6613\u627e\u5230\u7684\u4e1c\u897f\uff1f\u201d \u3000\u3000\u81ea\u8863\u4eba\u9053\uff1a\u201c\u4f60\u5403\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u5403\u732a\u8089\uff0c\u4e5f\u5403\u725b\u8089\uff0c\u5c24\u5176\u662f\u7ea2\u70e7\u8089\uff0c\u5c0f\u8471\u7092\u725b\u8089\u4e1d\u4e5f\u4e0d\u9519\u3002\u201d \u3000\u3000\u767d\u8863\u4eba\u5ffd\u9053\uff1a\u201c\u5f20\u4e09\u662f\u4e2a\u6076\u6bd2\u72e1\u733e\u7684\u5c0f\u4eba\uff0c\u674e\u56db\u662f\u4e2a\u8bda\u5b9e\u523b\u82e6\u7684\u541b\u5b50\uff0c\u8fd9\u4e24\u4eba\u82e5\u662f\u4e00\u5b9a \u8981\u4f60\u6740\u4e00\u4e2a\uff0c\u4f60\u6740\u8c01\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u5f20\u4e09\u3002\u201d \u3000\u3000\u81ea\u8863\u4eba\u9053\uff1a\u201c\u73b0\u5728\u4f60\u6740\u7684\u5374\u662f\u674e\u56db\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u5df2\u6740\u4e86\u674e\u56db\uff1f\u201d \u3000\u3000\u767d\u8863\u4eba\u70b9\u70b9\u5934\u3002 \u3000\u3000\u53f6\u5f00\u82e6\u7b11\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u6211\u8fde\u4ed6\u7684\u4eba\u5728\u54ea\u91cc\u90fd\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u767d\u8863\u4eba\u9053\uff0c\u201c\u4f60\u5e94\u8be5\u77e5\u9053\uff0c\u4ed6\u5c31\u5728\u4f60\u7684\u809a\u5b50\u91cc\u3002\u201d \u3000\u3000\u53f6\u5f00\u4e0d\u61c2\uff0c\u8fd9\u767d\u8863\u4eba\u8bf4\u7684\u8bdd\uff0c\u5b9e\u5728\u6709\u70b9\u98a0\u4e09\u5012\u56db\uff0c\u83ab\u540d\u5176\u5999\u3002 \u3000\u3000\u767d\u8863\u4eba\u51b7\u7b11\u9053\uff1a\u201c\u6bd2\u7684\u662f\u86c7\uff0c\u4e0d\u662f\u725b\uff0c\u4f60\u6740\u7684\u5374\u662f\u725b\uff0c\u6740\u4e86\u5b83\u540e\uff0c\u8fd8\u5c06\u5b83\u7684\u5c38\u9ab8\u846c\u5728\u809a \u5b50\u91cc\u3002\u201d \u3000\u3000\u53f6\u5f00\u53ea\u89c9\u5f97\u80c3\u91cc\u53d1\u9175\uff0c\u51e0\u4e4e\u5df2\u5fcd\u4e0d\u4f4f\u8981\u5455\u5410\u3002 \u3000\u3000\u4ed6\u809a\u5b50\u91cc\u7684\u786e\u8fd8\u6709\u725b\u8089\uff0c\u4eca\u5929\u4e2d\u5348\u4ed6\u5403\u7684\u725b\u8089\u4e00\u5b9a\u8fd8\u6ca1\u6709\u5b8c\u5168\u6d88\u5316\u3002 \u3000\u3000\u53ef\u662f\u4e0b\u6b21\u5047\u5982\u518d\u6709\u4eba\u8bf7\u4ed6\u5403\u725b\u8089\u65f6\uff0c\u4ed6\u4e00\u5b9a\u96be\u54bd\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u767d\u8863\u4eba\u7684\u773c\u775b\u5728\u8349\u5e3d\u91cc\u76ef\u7740\u4ed6\uff0c\u9053\uff1a\u201c\u73b0\u5728\u4f60\u662f\u4e0d\u662f\u5df2\u660e\u767d\u4e86\u6211\u7684\u610f\u601d\uff1f\u201d \u3000\u3000\u53f6\u5f00\u53f9\u4e86\u53e3\u6c14\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u4f60\u7684\u8bdd\u542c\u6765\u5012\u4e5f\u4e0d\u662f\u5b8c\u5168\u6ca1\u6709\u9053\u7406\u3002\u201d \u3000\u3000\u767d\u8863\u4eba\u9053\uff1a\u201c\u8fd9\u9053\u7406\u4f60\u4ece\u6765\u6ca1\u6709\u542c\u8fc7\uff1f\u201d \u3000\u3000\u53f6\u5f00\u7b11\u9053\uff1a\u201c\u6211\u8fde\u60f3\u90fd\u6ca1\u6709\u60f3\u5230\u8fc7\u3002\u201d \u3000\u3000\u2014\u2014\u628a\u725b\u7684\u5c38\u9ab8\u846c\u5728\u809a\u91cc\uff0c\u8fd9\u79cd\u8bdd\u771f\u4e8f\u4ed6\u600e\u4e48\u60f3\u5f97\u51fa\u6765\u3002 \u3000\u3000\u767d\u8863\u4eba\u9053\uff1a\u201c\u770b\u6765\u4f60\u867d\u7136\u4e0d\u662f\u8bda\u5b9e\u523b\u82e6\u7684\u541b\u5b50\uff0c\u5374\u4e5f\u4e0d\u662f\u6076\u6bd2\u5351\u9119\u7684\u5c0f\u4eba\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u770b\u5f97\u51fa\uff1f\u201d \u3000\u3000\u767d\u8863\u4eba\u9053\uff1a\u201c\u5c31\u56e0\u4e3a\u6211\u770b\u5f97\u51fa\uff0c\u6240\u4ee5\u4f60\u73b0\u5728\u8fd8\u6d3b\u7740\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u5462\uff1f\u4f60\u662f\u4e2a\u4ec0\u4e48\u6837\u7684\u4eba\uff1f\u201d \u3000\u3000\u767d\u8863\u4eba\u9053\uff1a\u201c\u4f60\u770b\u4e0d\u51fa\uff1f\u201d \u3000\u3000\u53f6\u5f00\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u5f53\u7136\u5e76\u4e0d\u662f\u771f\u7684\u59d3\u767d\u3002\u201d\u767d\u8863\u4eba\u627f\u8ba4\u3002 \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u662f\u4ece\u9752\u57ce\u6765\u7684\u3002\u201d \u3000\u3000\u767d\u8863\u4eba\u4e5f\u6ca1\u6709\u5426\u8ba4\u3002 \u3000\u3000\u53f6\u5f00\u76ef\u7740\u4ed6\uff0c\u6162\u6162\u9053\uff1a\u201c\u636e\u8bf4\u9752\u57ce\u5c71\u91cc\uff0c\u6709\u4f4d\u9ad8\u4eba\uff0c\u540d\u5b57\u53eb\u58a8\u4e5d\u661f\u3002\u201d \u3000\u3000\u767d\u8863\u4eba\u6253\u65ad\u4e86\u4ed6\u7684\u8bdd\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u77e5\u9053\u7684\u4e8b\u597d\u50cf\u8fd8\u4e0d\u5c11\u3002\u201d \u3000\u3000\u53f6\u5f00\u5fae\u7b11\u9053\uff1a\u201c\u867d\u7136\u4e0d\u592a\u591a\uff0c\u5012\u4e5f\u4e0d\u592a\u5c11\u3002\u201d \u3000\u3000\u767d\u8863\u4eba\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u5e94\u8be5\u77e5\u9053\u7684\u4e8b\uff0c\u4f60\u53cd\u800c\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u767d\u8863\u4eba\u9053\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u591a\u5c14\u7532\u662f\u8c01\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u767d\u8863\u4eba\u9053\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u5e03\u8fbe\u4f4d\u662f\u8c01\uff1f\u201d \u3000\u3000\u53f6\u5f00\u53c8\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff0c\u201c\u770b\u6765\u6211\u77e5\u9053\u7684\u4e8b\u786e\u5b9e\u4e5f\u4e0d\u7b97\u591a\u3002\u201d\u767d\u8863\u4eba\u9053\uff1a\u201c\u4f60\u60f3\u4e0d\u60f3\u89c1\u89c1 \u4ed6\u4eec\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u80fd\u89c1\u5f97\u5230\u4ed6\u4eec\uff1f\u201d \u3000\u3000\u767d\u8863\u4eba\u9053\uff1a\u201c\u53ea\u8981\u4f60\u613f\u610f\u5728\u8fd9\u91cc\u7b49\uff0c\u5c31\u4e00\u5b9a\u80fd\u89c1\u5f97\u5230\u3002\u201d \u3000\u3000\u53f6\u5f00\u7684\u773c\u775b\u4eae\u4e86\u3002 \u3000\u3000\u4ed6\u5f53\u7136\u613f\u610f\u5728\u8fd9\u91cc\u7b49\uff0c\u201c\u5c31\u7b97\u8981\u6211\u7b49\u4e09\u5929\u4e09\u591c\uff0c\u6211\u4e5f\u613f\u610f\u3002\u201d \u3000\u3000\u767d\u8863\u4eba\u9053\uff1a\u201c\u4f60\u7528\u4e0d\u7740\u7b49\u4e09\u5929\u4e09\u591c\uff0c\u4f60\u6765\u5f97\u6b63\u5de7\u3002\u201d \u3000\u3000\u53f6\u5f00\u7cbe\u795e\u4e00\u632f\uff0c\u9053\uff1a\u201c\u96be\u9053\u4ed6\u4eec\u4eca\u5929\u4e5f\u4f1a\u5230\u8fd9\u91cc\u6765\uff1f\u201d \u3000\u3000\u767d\u8863\u4eba\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u65e2\u7136\u613f\u610f\u7b49\uff0c\u5c31\u4e0d\u5fc5\u591a\u95ee\uff0c\u4f60\u82e5\u4e0d\u613f\u7b49\uff0c\u4e5f\u6ca1\u6709\u4eba\u7559\u4f60\u3002\u201d \u3000\u3000\u53f6\u5f00\u7acb\u523b\u95ed\u4e0a\u4e86\u5634\uff0c\u773c\u775b\u5374\u5f20\u5f97\u66f4\u5927\u4e86\u3002 \u3000\u3000\u4ed6\u672c\u6765\u5c31\u4e0d\u662f\u591a\u5634\u7684\u4eba\u3002 \u3000\u3000\u767d\u8863\u4eba\u5ffd\u7136\u9053\uff1a\u201c\u548c\u5c1a\u672c\u4e0d\u8be5\u591a\u5634\u7684\u3002\u201d \u3000\u3000\u82e6\u7af9\u5782\u4e0b\u4e86\u5934\u3002 \u3000\u3000\u767d\u8863\u4eba\u9053\uff1a\u201c\u4f60\u8fd9\u548c\u5c1a\u8bf4\u7684\u8bdd\u5374\u592a\u591a\u3002\u201d \u3000\u3000\u82e6\u7af9\u4e5f\u95ed\u4e0a\u4e86\u5634\uff0c\u8fde\u4e00\u4e2a\u5b57\u90fd\u4e0d\u6562\u591a\u8bf4\u3002 \u3000\u3000\u767d\u8863\u4eba\u9053\uff1a\u201c\u548c\u5c1a\u4e0d\u4f46\u8981\u61c2\u5f97\u5e94\u8be5\u5728\u4ec0\u4e48\u65f6\u5019\u95ed\u4e0a\u5634\uff0c\u4e5f\u8be5\u61c2\u5f97\u5728\u4ec0\u4e48\u65f6\u5019\u95ed\u4e0a\u773c \u775b\u3002\u201d \u3000\u3000\u82e6\u7af9\u7acb\u523b\u95ed\u4e0a\u773c\u775b\uff0c\u6478\u7d22\u7740\u8d70\u51fa\u53bb\u3002 \u3000\u3000\u53f6\u5f00\u5fcd\u4e0d\u4f4f\u7b11\u9053\uff1a\u201c\u770b\u6765\u4ed6\u7684\u786e\u662f\u4e2a\u61c2\u4e8b\u7684\u548c\u5c1a\u3002\u201d \u3000\u3000\u767d\u8863\u4eba\u9053\uff1a\u201c\u771f\u6b63\u4e0d\u61c2\u4e8b\u7684\u548c\u5c1a\u53ea\u6709\u4e00\u79cd\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u54ea\u79cd\uff1f\u201d\u767d\u8863\u4eba\u9053\uff1a\u201c\u8be5\u6b7b\u7684\u548c\u5c1a\u3002\u201d \u3000\u3000\u53f6\u5f00\u53c8\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u4ece\u4f60\u773c\u91cc\u770b\u6765\uff0c\u5929\u4e0b\u7684\u4eba\u597d\u50cf\u4e00\u5171\u53ea\u6709\u4e24\u79cd\u3002\u201d \u3000\u3000\u767d\u8863\u4eba\u9053\uff1a\u201c\u672c\u6765\u5c31\u53ea\u6709\u4e24\u79cd\uff0c\u4e00\u79cd\u4e0d\u8be5\u6b7b\uff0c\u4e00\u79cd\u8be5\u6b7b\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4eca\u5929\u665a\u4e0a\u8981\u6765\u7684\u662f\u54ea\u79cd\u4eba\uff1f\u201d \u3000\u3000\u767d\u8863\u4eba\u9053\uff1a\u201c\u8be5\u6b7b\u7684\u4e00\u79cd\u3002\u201d \u3000\u3000\u591c\u3002 \u3000\u3000\u767d\u8863\u4eba\u7528\u4e00\u4e2a\u5f88\u5c0f\u7684\u6728\u74f6\u5b50\uff0c\u5728\u5730\u4e0a\u6d12\u4e86\u4e00\u5c42\u94f6\u8272\u7684\u7c89\u672a\uff0c\u5c31\u50cf\u662f\u7070\u5c18\u4e00\u6837\u3002 \u3000\u3000\u53ef\u662f\u7b49\u5230\u661f\u5149\u5347\u8d77\u7684\u65f6\u5019\uff0c\u8fd9\u4e9b\u7070\u5c18\u4e5f\u5f00\u59cb\u5728\u95ea\u52a8\u7740\u94f6\u5149\u3002 \u3000\u3000\u53f6\u5f00\u7b11\u9053\uff1a\u201c\u4eca\u5929\u665a\u4e0a\u4f60\u662f\u4e0d\u662f\u51c6\u5907\u5c06\u8fd9\u9662\u5b50\u5403\u4e0b\u53bb\uff0c\u6240\u4ee5\u5148\u5728\u4e0a\u9762\u6d12\u70b9\u80e1\u6912\uff1f\u201d \u3000\u3000\u767d\u8863\u4eba\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u7684\u8bdd\u8bf4\u5f97\u5927\u591a\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u767d\u8863\u4eba\u9053\uff1a\u201c\u4f60\u4e5f\u7b11\u5f97\u5927\u591a\u3002\u201d \u3000\u3000\u53f6\u5f00\u7b11\u9053\uff1a\u201c\u90a3\u53ea\u56e0\u6211\u5df2\u770b\u51fa\u4e86\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u767d\u8863\u4eba\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u770b\u5f97\u51fa\u4f60\u5e76\u4e0d\u662f\u4e2a\u51b7\u9177\u7684\u4eba\uff0c\u6709\u65f6\u4f60\u5fc3\u91cc\u4e5f\u60f3\u7b11\u4e00\u7b11\uff0c\u53ea\u4e0d\u8fc7\u603b\u52c9\u5f3a\u5fcd\u4f4f \u800c\u5df2\u3002\u201d \u3000\u3000\u767d\u8863\u4eba\u9053\uff1a\u201c\u6211\u4e3a\u4ec0\u4e48\u8981\u52c9\u5f3a\u5fcd\u4f4f\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u56e0\u4e3a\u4f60\u60f3\u53eb\u4eba\u6015\u4f60\u3002\u201d \u3000\u3000\u767d\u8863\u4eba\u8f6c\u8fc7\u8eab\uff0c\u63a8\u5f00\u4e86\u7a97\u6237\uff0c\u8fc7\u4e86\u5f88\u4e45\uff0c\u624d\u6162\u6162\u9053\uff1a\u201c\u4f60\u8fd8\u770b\u51fa\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u53f6\u5f00\u7b11\u9053\uff1a\u201c\u4f60\u82e5\u80af\u8ba9\u6211\u770b\u770b\u4f60\u7684\u8138\uff0c\u6211\u4e00\u5b9a\u8fd8\u53ef\u4ee5\u770b\u51fa\u5f88\u591a\u4e8b\u6765\u7684\u3002\u201d \u3000\u3000\u767d\u8863\u4eba\u970d\u7136\u56de\u5934\uff0c\u6380\u8d77\u4e86\u8349\u5e3d\u3002 \u3000\u3000\u4ed6\u7684\u8138\u672c\u6765\u4e5f\u8ddf\u522b\u4eba\u6ca1\u4ec0\u4e48\u4e0d\u540c\uff0c\u4f46\u5374\u6bd4\u522b\u4eba\u591a\u4e86\u4e5d\u9897\u661f\u3002 \u3000\u3000\u4e5d\u9897\u6f06\u9ed1\u7684\u661f\u3002 \u3000\u3000\u5728\u51ac\u5929\u7684\u665a\u4e0a\u770b\u6765\uff0c\u5929\u4e0a\u7684\u758f\u661f\u603b\u662f\u5206\u5916\u9065\u8fdc\uff0c\u5206\u5916\u660e\u4eae\u3002 \u3000\u3000\u8fd9\u767d\u8863\u4eba\u8138\u4e0a\u7684\u661f\u5374\u66f4\u9ed1\u51b7\uff0c\u66f4\u4eae\u3002 \u3000\u3000\u4e5d\u9897\u661f\u5728\u4ed6\u8138\u4e0a\u6392\u5217\u6210\u4e00\u79cd\u5947\u5f02\u800c\u8be1\u79d8\u7684\u56fe\u6848\uff0c\u6bcf\u9897\u661f\u90fd\u9489\u5b50\u822c\u5730\u9489\u5728\u8089\u91cc\u3002 \u3000\u3000\u53f6\u5f00\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u8fd9\u662f\u5728\u81ea\u5df1\u60e9\u7f5a\u81ea\u5df1\uff1f\u201d \u3000\u3000\u767d\u8863\u4eba\u5c45\u7136\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6bcf\u4e2a\u4eba\u90fd\u6709\u7f6a\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u4e5f\u4e0d\u4f8b\u5916\uff1f\u201d \u3000\u3000\u767d\u8863\u4eba\u9053\uff1a\u201c\u6211\u4e5f\u662f\u4eba\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u7684\u7f6a\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u767d\u8863\u4eba\u9053\uff1a\u201c\u6211\u53ea\u6068\u4e0d\u80fd\u6740\u5c3d\u8fd9\u4e16\u4e0a\u6076\u6bd2\u5351\u9119\u7684\u4e2a\u4eba\u3002\u201d \u3000\u3000\u53f6\u5f00\u53f9\u9053\uff1a\u201c\u8fd9\u5e76\u4e0d\u80fd\u7b97\u662f\u4f60\u7684\u7f6a\uff0c\u4f60\u53d7\u7684\u60e9\u7f5a\u672a\u514d\u592a\u91cd\u4e86\u4e9b\u3002\u201d \u3000\u3000\u767d\u8863\u4eba\u9053\uff0c\u201c\u82e5\u662f\u903c\u89c1\u7f6a\u66f4\u91cd\u7684\u4eba\uff0c\u8fd9\u4e5d\u9897\u661f\u5c31\u662f\u6740\u4eba\u7684\u5229\u5668\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6740\u4eba\u7684\u5229\u5668\uff1f\u201d \u3000\u3000\u767d\u8863\u4eba\u9053\uff1a\u201c\u4f60\u770b\u4e0d\u51fa\uff1f\u201d \u3000\u3000\u53f6\u5f00\u6447\u6447\u5934\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u6211\u4e5f\u8fde\u60f3\u90fd\u6ca1\u6709\u60f3\u5230\u3002\u201d \u3000\u3000\u767d\u8863\u4eba\u53c8\u7528\u8349\u5e3d\u63a9\u4f4f\u4e86\u8138\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u80fd\u770b\u5230\u6211\u8fd9\u5f20\u8138\u7684\u4eba\u5c31\u4e0d\u591a\uff0c\u80fd\u6d3b\u7740\u7684\u66f4\u5c11\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u8138\u4e0a\u672c\u6765\u662f\u4e0d\u662f\u53ea\u6709\u4e94\u9897\u661f\uff1f\u201d \u3000\u3000\u767d\u8863\u4eba\u9053\uff1a\u201c\u56e0\u4e3a\u4e16\u4e0a\u7684\u7f6a\u4eba\u8d8a\u6765\u8d8a\u591a\uff0c\u6211\u7684\u7f6a\u4e5f\u8d8a\u6765\u8d8a\u91cd\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6240\u4ee5\u58a8\u4e94\u661f\u53d8\u6210\u4e86\u58a8\u4e5d\u661f\u3002\u201d \u3000\u3000\u767d\u8863\u4eba\u9053\uff1a\u201c\u73b0\u5728\u5df2\u6ca1\u6709\u58a8\u4e94\u661f\uff0c\u53ea\u6709\u58a8\u4e5d\u661f\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u8fd9\u5c31\u96be\u602a\u5979\u4f1a\u5f04\u9519\u4e86\u3002\u201d \u3000\u3000\u58a8\u4e5d\u661f\u9053\uff1a\u201c\u5979\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u53f6\u5f00\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u731c\u4e0d\u51fa\uff1f\u201d \u3000\u3000\u58a8\u4e5d\u661f\u9053\uff1a\u201c\u662f\u4e0d\u662f\u4e0a\u5b98\u5c0f\u4ed9\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u4e5f\u77e5\u9053\u5979\uff1f\u201d \u3000\u3000\u58a8\u4e5d\u661f\u51b7\u7b11\u3002 \u3000\u3000\u53f6\u5f00\u906d\uff1a\u201c\u4f60\u77e5\u9053\u5979\u662f\u4e2a\u4ec0\u4e48\u6837\u7684\u4eba\uff1f\u201d \u3000\u3000\u58a8\u4e5d\u661f\u9053\uff1a\u201c\u8fd9\u6b21\u6211\u662f\u6765\u6740\u4eba\u7684\uff0c\u6740\u4e09\u4e2a\u4eba\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u5979\u4e5f\u662f\u5176\u4e2d\u4e4b\u4e00\uff1f\u201d \u3000\u3000\u58a8\u4e5d\u661f\u9053\uff1a\u201c\u5979\u672c\u6765\u662f\u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u73b0\u5728\u5462\uff1f\u201d \u3000\u3000\u58a8\u4e5d\u661f\u9053\uff1a\u201c\u73b0\u5728\u6211\u624d\u53d1\u73b0\uff0c\u8fd9\u4e16\u4e0a\u6bd4\u5979\u66f4\u8be5\u6b7b\u7684\u4eba\u8fd8\u6709\u5f88\u591a\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6700\u8be5\u6b7b\u7684\u662f\u54ea\u51e0\u4e2a\uff1f\u201d \u3000\u3000\u58a8\u4e5d\u661f\u9053\uff1a\u201c\u591a\u5c14\u7532\u548c\u5e03\u8fbe\u62c9\u3002\u201d \u3000\u3000\u53f6\u5f00\u53c8\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8981\u6740\u8fd9\u4e24\u4e2a\u4eba\uff0c\u53ea\u6015\u5f88\u4e0d\u5bb9\u6613\u3002\u201d \u3000\u3000\u58a8\u4e5d\u661f\u9053\uff1a\u201c\u6211\u672c\u5c31\u6ca1\u6709\u6253\u7b97\u6d3b\u7740\u56de\u53bb\u3002\u201d\u4ed6\u6162\u6162\u5730\u63a5\u7740\u9053\uff1a\u201c\u9b54\u6559\u4e2d\u7684\u56db\u5927\u5929\u738b\uff0c\u53ea \u8981\u8fd8\u6709\u4e00\u4e2a\u6d3b\u5728\u4e16\u4e0a\uff0c\u6211\u5c31\u7edd\u4e0d\u56de\u9752\u57ce\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u53ef\u662f\u4f60\u5c31\u662f\u6740\u4e86\u4ed6\u4eec\u4e24\u4e2a\uff0c\u4e5f\u8fd8\u6709\u4e24\u4e2a\u6d3b\u7740\u3002\u201d \u3000\u3000\u58a8\u4e5d\u661f\u9053\uff1a\u201c\u6ca1\u6709\u4e86\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u600e\u4e48\u6ca1\u6709\u4e86\uff1f\u201d \u3000\u3000\u58a8\u4e5d\u661f\u9053\uff1a\u201c\u73ed\u5bdf\u5df4\u90a3\u5df2\u6b7b\u5728\u90ed\u5b9a\u624b\u91cc\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u789f\u513f\u5e03\u5462\uff1f\u201d \u3000\u3000\u58a8\u4e5d\u661f\u5ffd\u7136\u4ece\u8eab\u4e0a\u62ff\u51fa\u5757\u7389\u724c\uff0c\u629b\u7ed9\u4e86\u53f6\u5f00\u3002\u6676\u83b9\u65e0\u7455\u7684\u7389\u724c\u4e0a\uff0c\u523b\u7740\u4e2a\u624b\u6267\u667a\u6167\u4e4b\u78d0 \u7684\u9b54\u795e\u3002 \u3000\u3000\u201c\u8fd9\u5c31\u662f\u789f\u513f\u5e03\u7684\u62a4\u8eab\u7b26\uff0c\u4ed6\u6d3b\u7740\u7684\u65f6\u5019\uff0c\u603b\u662f\u968f\u8eab\u5e26\u7740\u7684\u3002\u201d \u3000\u3000\u201c\u73b0\u5728\u600e\u5168\u4f1a\u5230\u4e86\u4f60\u8eab\u4e0a\uff1f\u201d \u3000\u3000\u58a8\u4e5d\u661f\u51b7\u51b7\u9053\uff1a\u201c\u56e0\u4e3a\u4ed6\u5df2\u662f\u4e2a\u6b7b\u4eba\u3002\u201d \u3000\u3000\u53f6\u5f00\u52a8\u5bb9\u9053\uff1a\u201c\u662f\u4f60\u6740\u4e86\u4ed6\uff1f\u201d \u3000\u3000\u58a8\u4e5d\u661f\u70b9\u70b9\u5934\u3002 \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u5728\u54ea\u91cc\u9047\u89c1\u4ed6\u7684\uff1f\u201d \u3000\u3000\u58a8\u4e5d\u661f\u9053\uff1a\u201c\u957f\u5b89\u57ce\u5916\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4ed6\u4e5f\u4e0b\u4e86\u9b54\u5c71\uff1f\u201d \u3000\u3000\u58a8\u4e5d\u661f\u9053\uff1a\u201c\u4ed6\u4eec\u7684\u9b54\u5c71\u672c\u5c31\u5728\u865a\u65e0\u7f25\u7f08\u95f4\uff0c\u4ed6\u4eec\u7684\u4eba\u5728\u54ea\u91cc\uff0c\u54ea\u91cc\u5c31\u662f\u4ed6\u4eec\u7684\u9b54 \u5c71\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6240\u4ee5\u73b0\u5728\u4ed6\u4eec\u7684\u9b54\u5c71\u5c31\u5728\u957f\u5b89\u57ce\uff1f\u201d \u3000\u3000\u58a8\u4e5d\u661f\u9053\uff1a\u201c\u4ed6\u4eec\u7684\u4eba\u82e5\u4e0d\u6b7b\uff0c\u4e5d\u4e5d\u516b\u5341\u4e00\u5929\u4e4b\u5185\uff0c\u8fd9\u957f\u5b89\u57ce\u5c31\u8981\u53d8\u6210\u5ea7\u9b54\u57ce\u3002\u201d \u3000\u3000\u53f6\u5f00\u5931\u58f0\u9053\uff1a\u201c\u9b54\u57ce\uff1f\u201d \u3000\u3000\u58a8\u4e5d\u661f\u9053\uff1a\u201c\u9b54\u57ce\u4e2d\u4e5f\u6709\u4e24\u79cd\u4eba\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u54ea\u4e24\u79cd\u4eba\uff1f\u201d \u3000\u3000\u58a8\u4e5d\u661f\u9053\uff1a\u201c\u4e00\u79cd\u662f\u4ed6\u4eec\u9b54\u6559\u7684\u5f1f\u5b50\uff0c\u8fd8\u6709\u4e00\u79cd\u662f\u6b7b\u4eba\u3002\u201d \u3000\u3000\u53f6\u5f00\u5410\u51fa\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5e78\u597d\u4ed6\u4eec\u7684\u79d8\u5bc6\u5df2\u88ab\u4f60\u53d1\u73b0\u4e86\u3002\u201d \u3000\u3000\u58a8\u4e5d\u661f\u50b2\u7136\u9053\uff1a\u201c\u5bf9\u6211\u8bf4\u6765\uff0c\u8fd9\u4e16\u4e0a\u6839\u672c\u6ca1\u6709\u79d8\u5bc6\u3002\u201d \u3000\u3000\u53f6\u5f00\u53f9\u9053\uff1a\u201c\u4f60\u77e5\u9053\u7684\u4e8b\u786e\u5b9e\u4e0d\u5c11\u3002\u201d \u3000\u3000\u58a8\u4e5d\u661f\u627f\u8ba4\u3002 \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u53ea\u5947\u602a\uff0c\u4f60\u600e\u4e48\u77e5\u9053\u8fd9\u4e48\u591a\u4e8b\u7684\uff0c\u4f60\u672c\u662f\u4e2a\u4e0d\u51fa\u5c71\u7684\u9690\u58eb\u3002\u201d \u3000\u3000\u58a8\u4e5d\u661f\u9053\uff1a\u201c\u4f60\u9519\u4e86\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u58a8\u4e5d\u661f\u9053\uff1a\u201c\u58a8\u5bb6\u7684\u7cbe\u795e\u5e76\u4e0d\u662f\u51fa\u4e16\u7684\uff0c\u800c\u662f\u5165\u4e16\u7684\uff0c\u4e3a\u4e86\u6025\u4eba\u4e4b\u96be\uff0c\u58a8\u5bb6\u5b50\u5f1f\u4e00\u5411\u4e0d \u501f\u6469\u9876\u653e\u7761\uff0c\u5200\u65a7\u52a0\u8eab\u3002\u201d \u3000\u3000\u53f6\u5f00\u770b\u7740\u4ed6\uff0c\u773c\u775b\u91cc\u9732\u51fa\u5c0a\u656c\u4e4b\u8272\u3002\u8fd9\u4e2a\u4eba\u770b\u6765\u867d\u51b7\u9177\u53e4\u602a\uff0c\u5176\u5b9e\u5374\u6709\u4e00\u9897\u5584\u826f\u7684\u5fc3\u3002 \u8fd9\u4e16\u4e0a\u771f\u6b63\u80fd\u4e3a\u522b\u4eba\u727a\u7272\u81ea\u5df1\u7684\u4eba\u5e76\u4e0d\u591a\uff0c\u53f6\u5f00\u4e00\u5411\u6700\u5c0a\u656c\u8fd9\u79cd\u4eba\u3002 \u3000\u3000\u7985\u623f\u91cc\u6ca1\u6709\u71c3\u706f\u3002\u58a8\u4e5d\u661f\u7684\u8349\u5e3d\u91cc\uff0c\u4e00\u76f4\u5728\u95ea\u95ea\u7684\u53d1\u5149\uff0c\u5374\u4e0d\u77e5\u9053\u662f\u4ed6\u7684\u773c\u775b\uff0c\u8fd8\u662f\u90a3 \u6740\u4eba\u7684\u661f\u3002 \u3000\u3000\u4ed6\u76ef\u7740\u53f6\u5f00\uff0c\u5ffd\u7136\u9053\uff1a\u201c\u6211\u4e5f\u65e9\u5c31\u77e5\u9053\u4f60\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u58a8\u4e5d\u661f\u9053\uff1a\u201c\u4f60\u59d3\u53f6\uff0c\u53eb\u53f6\u5f00\u3002\u201d \u3000\u3000\u53f6\u5f00\u5fae\u7b11\u9053\uff1a\u201c\u6728\u53f6\u7684\u53f6\uff0c\u5f00\u5fc3\u7684\u5f00\u3002\u201d \u3000\u3000\u58a8\u4e5d\u661f\u9053\uff1a\u201c\u4f60\u603b\u662f\u5f88\u5f00\u5fc3\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u5f88\u5c11\u53bb\u60f3\u90a3\u4e9b\u4e0d\u5f00\u5fc3\u7684\u4e8b\u3002\u201d \u3000\u3000\u58a8\u4e5d\u661f\u9053\uff1a\u201c\u636e\u8bf4\u4f60\u7684\u98de\u5200\uff0c\u73b0\u5728\u53ef\u7b97\u662f\u5f53\u4e16\u7b2c\u4e00\u3002\u201d \u3000\u3000\u53f6\u5f00\u82e6\u7b11\u9053\uff1a\u201c\u6211\u4e5f\u542c\u4eba\u8fd9\u6837\u8bf4\u8fc7\uff0c\u6240\u4ee5\u6211\u7684\u9ebb\u70e6\u4e5f\u603b\u662f\u5929\u4e0b\u7b2c\u4e00\u3002\u201d \u3000\u3000\u82e5\u8bba\u9ebb\u70e6\u4e4b\u591a\uff0c\u5012\u7684\u786e\u5f88\u5c11\u4eba\u80fd\u6bd4\u5f97\u4e0a\u4ed6\u3002 \u3000\u3000\u58a8\u4e5d\u661f\u6c89\u9ed8\u7740\uff0c\u8fc7\u4e86\u5f88\u4e45\uff0c\u624d\u7f13\u7f13\u9053\uff1a\u201c\u603b\u6709\u4e00\u5929\u6211\u4f1a\u77e5\u9053\u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u77e5\u9053\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u58a8\u4e5d\u661f\u9053\uff1a\u201c\u4f60\u7684\u98de\u5200\u7a76\u7adf\u662f\u4e0d\u662f\u5929\u4e0b\u7b2c\u4e00\u3002\u201d \u3000\u3000\u53f6\u5f00\u53f9\u9053\uff1a\u201c\u4f60\u82e5\u771f\u7684\u60f3\u77e5\u9053\uff0c\u6211\u7684\u9ebb\u70e6\u5c31\u53c8\u591a\u4e86\u4e00\u4ef6\u3002\u201d \u3000\u3000\u58a8\u4e5d\u661f\u9053\uff1a\u201c\u4f60\u4e0d\u60f3\u770b\u770b\u6211\u7684\u661f\u7a76\u7adf\u662f\u4e0d\u662f\u80fd\u6740\u4eba\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u4e0d\u60f3\u3002\u201d \u3000\u3000\u58a8\u4e5d\u661f\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u4eec\u5df2\u7ecf\u662f\u670b\u53cb\u3002\u201d \u3000\u3000\u58a8\u4e5d\u661f\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u7684\u670b\u53cb\u53ea\u6015\u592a\u591a\u4e86\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u670b\u53cb\u591a\u4e9b\uff0c\u603b\u6bd4\u6ca1\u6709\u670b\u53cb\u597d\u3002\u201d\u58a8\u4e5d\u661f\u9053\uff1a\u201c\u4e5f\u8bb8\u5c31\u56e0\u4e3a\u4f60\u7684\u670b\u53cb\u6bd4\u522b\u4eba \u591a\uff0c\u6240\u4ee5\u9ebb\u70e6\u4e5f\u6bd4\u522b\u4eba\u591a\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u9ebb\u70e6\u591a\u4e9b\uff0c\u4e5f\u6bd4\u6ca1\u6709\u9ebb\u70e6\u597d\u3002\u201d \u3000\u3000\u58a8\u4e5d\u661f\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u56e0\u4e3a\u771f\u6b63\u6ca1\u6709\u9ebb\u70e6\u7684\uff0c\u4e5f\u53ea\u6709\u4e00\u79cd\u4eba\u3002\u201d \u3000\u3000\u58a8\u4e5d\u661f\u9053\uff1a\u201c\u6b7b\u4eba\uff1f\u201d \u3000\u3000\u53f6\u5f00\u5fae\u7b11\u7740\u70b9\u70b9\u5934\u3002\u7a81\u7136\u201c\u8f70\u201d\u7684\u4e00\u54cd\uff0c\u9662\u5b50\u91cc\u7684\u77ed\u5899\u88ab\u6410\u7834\u4e86\u4e2a\u5927\u6d1e\uff0c\u4e00\u4e2a\u4eba\u80cc\u8d1f\u7740 \u53cc\u624b\uff0c\u65bd\u65bd\u7136\u8d70\u4e86\u8fdb\u6765\u3002 \u7b2c\u4e8c\u5341\u516b\u7ae0\u3000\u8eab\u5916\u5316\u8eab>> \u53e4\u9f99\u300a\u4e5d\u6708\u9e70\u98de\u300b \u7b2c\u4e8c\u5341\u516b\u7ae0\u3000\u8eab\u5916\u5316\u8eab \u3000\u3000\u5bd2\u661f\u5728\u5929\u3002 \u3000\u3000\u51b7\u6e05\u6e05\u7684\u661f\u5149\uff0c\u7167\u5728\u8fd9\u4eba\u8138\u4e0a\u3002 \u3000\u3000\u4ed6\u7684\u8138\u4e5f\u5728\u53d1\u7740\u5149\u3002 \u3000\u3000\u9752\u5149\uff01 \u3000\u3000\u6ca1\u6709\u4eba\u7684\u8138\u4e0a\u4f1a\u53d1\u51fa\u8fd9\u79cd\u9752\u5149\u7684\uff0c\u9664\u975e\u4ed6\u8138\u4e0a\u6234\u7740\u4e2a\u9752\u94dc\u9762\u5177\u3002 \u3000\u3000\u8fd9\u4eba\u7684\u8138\u4e0a\u5c31\u6234\u7740\u9752\u94dc\u9762\u5177\uff0c\u5728\u661f\u5149\u4e0b\u770b\u6765\uff0c\u663e\u5f97\u66f4\u72f0\u72de\u800c\u602a\u5f02\u3002 \u3000\u3000\u4ed6\u8eab\u4e0a\u7a7f\u7740\u7684\uff0c\u5374\u662f\u4ef6\u7f8e\u4e3d\u7684\u7ee3\u82b1\u957f\u888d\uff0c\u8170\u5e26\u4e0a\u659c\u63d2\u7740\u4e09\u67c4\u5f2f\u5200\u3002 \u3000\u3000\u60e8\u78a7\u8272\u7684\u5200\u9798\u4e0a\uff0c\u7f00\u6ee1\u4e86\u660e\u73e0\u7f8e\u7389\u3002 \u3000\u3000\u201c\u6765\u4e86\uff0c\u679c\u7136\u6765\u4e86\u3002\u201d \u3000\u3000\u53f6\u5f00\u8f7b\u8f7b\u5410\u51fa\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6765\u7684\u662f\u591a\u5c14\u7532\uff0c\u8fd8\u662f\u5e03\u8fbe\u62c9\uff1f\u201d \u3000\u3000\u201c\u4f60\u770b\u4e0d\u51fa\uff1f\u201d \u3000\u3000\u53f6\u5f00\u5df2\u770b\u51fa\u6765\uff0c\u8fd9\u4eba\u957f\u888d\u4e0a\u7ee3\u7740\u7684\uff0c\u662f\u8c61\u5f81\u6743\u6cd5\u7684\u9b54\u6756\u3002 \u3000\u3000\u201c\u591a\u5c14\u7532\uff0c\u4e5f\u8bb8\u4ed6\u8fd8\u4e0d\u662f\u591a\u5c14\u7532\u3002\u201d \u3000\u3000\u201c\u8fd8\u4e0d\u662f\uff1f\u201d \u3000\u3000\u201c\u591a\u5c14\u7532\u7684\u8eab\u5916\u5316\u8eab\u8fd8\u6709\u4e09\u4e2a\u3002\u201d \u3000\u3000\u4ec0\u4e48\u53eb\u8eab\u5916\u5316\u8eab\uff1f \u3000\u3000\u53f6\u5f00\u8fd8\u6ca1\u6709\u95ee\uff0c\u5df2\u770b\u89c1\u4e86\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u4e00\u9635\u98ce\u5439\u8fc7\uff0c\u4e00\u4e2a\u4eba\u968f\u7740\u98ce\u4ece\u5916\u98d8\u4e86\u8fdb\u6765\uff0c\u7ee3\u82b1\u7684\u957f\u888d\uff0c\u72f0\u72de\u7684\u9762\u5177\uff0c\u8170\u5e26\u4e0a\u4e5f\u659c\u63d2\u7740 \u4e09\u67c4\u7f00\u6ee1\u73e0\u7389\u7684\u5f2f\u5200\u3002 \u3000\u3000\u51e0\u4e4e\u5c31\u5728\u540c\u4e00\u77ac\u95f4\uff0c\u7af9\u6797\u540e\u548c\u5c4b\u6a90\u4e0b\u4e5f\u51fa\u73b0\u4e86\u4e24\u4e2a\u4eba\u3002 \u3000\u3000\u5b8c\u5168\u540c\u6837\u7684\u4e24\u4e2a\u4eba\u3002 \u3000\u3000\u53f6\u5f00\u6014\u4f4f\u3002 \u3000\u3000\u4ed6\u5b9e\u5728\u5206\u4e0d\u51fa\u8c01\u624d\u662f\u771f\u6b63\u7684\u591a\u5c14\u7532\u5929\u738b\u3002 \u3000\u3000\u201c\u4f60\u5c31\u7b97\u80fd\u6740\u4e86\u4ed6\u4eec\u4e09\u4e2a\uff0c\u90a3\u771f\u7684\u4e00\u4e2a\u8fd8\u662f\u4e00\u6837\u53ef\u80fd\u4f1a\u9003\u8d70\u3002\u201d \u3000\u3000\u58a8\u4e5d\u661f\u51b7\u7b11\u3002 \u3000\u3000\u201c\u4ed6\u65e2\u7136\u6765\u4e86\uff0c\u5c31\u4f11\u60f3\u518d\u8d70\u3002\u201d \u3000\u3000\u201c\u4f60\u600e\u4e48\u77e5\u9053\u4ed6\u771f\u7684\u6765\u4e86\uff0c\u4f60\u770b\u5f97\u51fa\uff1f\u201d \u3000\u3000\u201c\u6211\u770b\u5f97\u51fa\u3002\u201d\u58a8\u4e5d\u661f\u51b7\u51b7\u9053\uff1a\u201c\u6211\u53ea\u77e5\u9053\u4ed6\u975e\u6765\u4e0d\u53ef\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u6211\u5728\u8fd9\u91cc\u3002\u201d \u3000\u3000\u53f6\u5f00\u6ca1\u6709\u518d\u95ee\u4e0b\u53bb\uff0c\u4e5f\u4e0d\u80fd\u518d\u95ee\u4e0b\u53bb\uff0c\u4ed6\u5df2\u770b\u89c1\u4e00\u4e2a\u4eba\u8e0f\u7740\u661f\u5149\u8d70\u8fc7\u6765\u3002 \u3000\u3000\u94f6\u7c89\u4e5f\u5728\u53d1\u7740\u5149\u3002 \u3000\u3000\u4ed6\u6bcf\u8d70\u4e00\u6b65\uff0c\u5730\u4e0a\u5c31\u591a\u4e86\u4e2a\u6d45\u6d45\u7684\u811a\u5370\u3002 \u3000\u3000\u53ea\u51ed\u8fd9\u811a\u5370\uff0c\u96be\u9053\u5c31\u80fd\u5206\u5f97\u51fa\u4ed6\u662f\u4e0d\u662f\u771f\u7684\u591a\u5c14\u7532\uff1f \u3000\u3000\u53f6\u5f00\u53c8\u4e0d\u7981\u53f9\u606f\uff0c\u81f3\u5c11\u4ed6\u662f\u5206\u4e0d\u51fa\u7684\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u80cc\u8d1f\u7740\u53cc\u624b\u5728\u7985\u9662\u4e2d\u6f2b\u6b65\uff0c\u4e00\u4e2a\u4eba\u80cc\u8d1f\u7740\u53cc\u624b\u8d70\u8fc7\u6765\u3002 \u3000\u3000\u4ed6\u4eec\u4e0d\u4f46\u88c5\u675f\u6253\u626e\u5b8c\u5168\u76f8\u540c\uff0c\u8fde\u8d70\u8def\u7684\u59ff\u6001\u90fd\u5b8c\u5168\u4e00\u6837\u3002 \u3000\u3000\u58a8\u4e5d\u661f\u51ed\u4ec0\u4e48\u80fd\u5206\u8fa8\u51fa\u4ed6\u4eec\u7684\u8868\u60c5\uff1f \u3000\u3000\u591a\u5c14\u7532\u7ec8\u4e8e\u9053\uff1a\u201c\u9752\u57ce\u58a8\u4e5d\u661f\uff1f\u201d \u3000\u3000\u58a8\u4e5d\u661f\u70b9\u70b9\u5934\u3002 \u3000\u3000\u591a\u5c14\u7532\u9053\uff1a\u201c\u73b0\u5728\u6211\u5df2\u6765\u4e86\u3002\u201d \u3000\u3000\u58a8\u4e5d\u661f\u5ffd\u7136\u9053\uff1a\u201c\u6eda\u51fa\u53bb\u3002\u201d \u3000\u3000\u591a\u5c14\u7532\u51b7\u7b11\u9053\uff1a\u201c\u6211\u65e2\u7136\u5df2\u6765\u4e86\uff0c\u8981\u6211\u8d70\u53ea\u6015\u5c31\u5f88\u4e0d\u5bb9\u6613\u3002\u201d \u3000\u3000\u58a8\u4e5d\u661f\u9053\uff1a\u201c\u4f60\u4e00\u5b9a\u8981\u6b7b\u5728\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u591a\u5c14\u7532\u7684\u624b\u5df2\u63e1\u4f4f\u4e86\u5200\u67c4\u3002 \u3000\u3000\u58a8\u4e5d\u661f\u9053\uff1a\u201c\u4f60\u672c\u6765\u4e0d\u914d\u6211\u51fa\u624b\uff0c\u53ef\u662f\u73b0\u5728\u2026\u2026\u201d \u3000\u3000\u591a\u5c14\u7532\u9053\uff1a\u201c\u73b0\u5728\u4f60\u4e0d\u51fa\u624b\uff0c\u5c31\u6b7b\u3002\u201d \u3000\u3000\u5200\u5149\u4e00\u95ea\uff0c\u4ed6\u7684\u5200\u5df2\u51fa\u9798\uff0c\u60e8\u78a7\u8272\u7684\u5f2f\u5200\uff0c\u7728\u773c\u95f4\u5df2\u5288\u51fa\u4e09\u5200\u3002 \u3000\u3000\u58a8\u4e5d\u661f\u6ca1\u6709\u52a8\uff0c\u8fde\u6307\u5c16\u90fd\u6ca1\u6709\u52a8\u3002 \u3000\u3000\u4ed6\u5df2\u770b\u51fa\u8fd9\u4e09\u5200\u90fd\u662f\u865a\u62db\u3002 \u3000\u3000\u591a\u5c14\u7532\u624b\u8155\u4e00\u7ffb\uff0c\u7b2c\u56db\u5200\u5288\u4e0b\u53bb\uff0c\u5df2\u4e0d\u662f\u865a\u62db\u3002 \u3000\u3000\u5200\u5149\u524a\u7834\u58a8\u4e5d\u661f\u5934\u4e0a\u7684\u8349\u5e3d\uff0c\u64e6\u7740\u58a8\u4e5d\u661f\u7684\u9f3b\u5c16\u524a\u4e0b\uff0c\u53ea\u5dee\u534a\u5bf8\uff0c\u58a8\u4e5d\u661f\u7684\u8138\u5c31\u8981\u88ab\u8fd9 \u4e00\u5200\u524a\u6210\u4e24\u534a\u3002 \u3000\u3000\u53ea\u53ef\u60dc\u4ed6\u8fd8\u662f\u5dee\u4e86\u534a\u5bf8\u3002 \u3000\u3000\u58a8\u4e5d\u661f\u5c45\u7136\u8fd8\u6ca1\u6709\u51fa\u624b\uff0c\u5374\u76b1\u4e86\u76b1\u7709\u3002 \u3000\u3000\u7a81\u7136\u95f4\uff0c\u4e00\u70b9\u5bd2\u661f\u98de\u51fa\uff0c\u6253\u5728\u591a\u5c14\u7532\u5934\u4e0a\u3002 \u3000\u3000\u591a\u5c14\u7532\u5e76\u4e0d\u662f\u6ca1\u6709\u95ea\u907f\uff0c\u53ea\u53ef\u60dc\u8fd9\u4e00\u70b9\u5bd2\u661f\u6765\u5f97\u592a\u5feb\uff0c\u5927\u610f\u5916\u3002 \u3000\u3000\u4ed6\u770b\u89c1\u5bd2\u661f\u98de\u51fa\u65f6\uff0c\u60f3\u95ea\u907f\u5df2\u6765\u4e0d\u53ca\u4e86\uff0c\u7a81\u7136\u54ac\u4e86\u54ac\u7259\uff0c\u53cd\u624b\u4e00\u5200\uff0c\u523a\u5728\u81ea\u5df1\u809a\u5b50\u4e0a\u3002 \u3000\u3000\u8840\u5149\u98de\u6e85\uff0c\u4ed6\u4eba\u5df2\u5012\u4e0b\u3002 \u3000\u3000\u58a8\u4e5d\u661f\u8fd8\u662f\u6ca1\u6709\u52a8\uff0c\u8fde\u6307\u5c16\u90fd\u6ca1\u6709\u52a8\uff0c\u53ef\u662f\u7709\u5fc3\u4e4b\u95f4\u7684\u4e00\u70b9\u5bd2\u661f\uff0c\u5df2\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u8fd9\u79cd\u6697\u5668\u7adf\u7528\u4e0d\u7740\u52a8\u624b\uff0c\u5c31\u53ef\u4ee5\u53d1\u51fa\u6765\uff0c\u4ed6\u53ea\u8981\u76b1\u4e00\u76b1\u7709\u5c31\u53ef\u4ee5\u5236\u4eba\u4e8e\u6b7b\u5730\u3002\u53f6\u5f00\u53f9\u4e86 \u53e3\u6c14\uff0c\u9053\uff1a\u201c\u679c\u7136\u662f\u6740\u4eba\u7684\u5229\u5668\uff0c\u4e00\u70b9\u4e0d\u5047\u3002\u201d \u3000\u3000\u58a8\u4e5d\u661f\u9053\uff1a\u201c\u8fd9\u4e2a\u591a\u5c14\u7532\u5374\u662f\u5047\u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u770b\u5f97\u51fa\uff1f\u201d \u3000\u3000\u58a8\u4e5d\u661f\u70b9\u70b9\u5934\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u8fd9\u4eba\u7684\u6b7b\uff0c\u4e5f\u662f\u5047\u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u7b11\u9053\uff1a\u201c\u8fd9\u5c31\u8fde\u6211\u4e5f\u770b\u5f97\u51fa\u6765\u3002\u201d \u3000\u3000\u58a8\u4e5d\u661f\u9053\uff1a\u201c\u54e6\uff1f\u2019\u53f6\u5f00\u9053\uff1a\u201c\u8fd9\u79cd\u5200\u950b\u53ef\u4ee5\u7f29\u56de\u53bb\u7684\u9b54\u5200\uff0c\u6211\u5df2\u770b\u8fc7\u4e0d\u6b62\u4e00\u6b21\uff0c\u5374\u8fde \u4e00\u6b21\u90fd\u6ca1\u6709\u9a97\u8fc7\u6211\u3002\u201d \u3000\u3000\u58a8\u4e5d\u661f\u6de1\u6de1\u9053\uff1a\u201c\u8981\u9a97\u8fc7\u4f60\uff0c\u7684\u786e\u4e5f\u4e0d\u5bb9\u6613\u3002\u201d \u3000\u3000\u5012\u5728\u8840\u6cca\u4e2d\u7684\u201c\u591a\u5c14\u7532\u201d\u679c\u7136\u201c\u590d\u6d3b\u201d\u4e86\uff0c\u7a81\u7136\u62bd\u51fa\u4e86\u53e6\u4e00\u67c4\u5200\uff0c\u7ffb\u8eab\u7ad9\u8d77\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u8fd9\u4e00\u5200\u5e76\u6ca1\u6709\u5288\u8fc7\u6765\uff0c\u53c8\u662f\u4e00\u70b9\u5bd2\u661f\u98de\u6765\uff0c\u9489\u4eba\u4e86\u4ed6\u7684\u54bd\u5589\u3002 \u3000\u3000\u4ed6\u4eba\u53c8\u5012\u4e0b\u3002 \u3000\u3000\u53f6\u5f00\u53f9\u9053\uff1a\u201c\u770b\u6765\u8fd9\u6b21\u5df2\u4e0d\u662f\u5047\u7684\u3002\u201d \u3000\u3000\u58a8\u4e5d\u661f\u51b7\u51b7\u9053\uff1a\u201c\u4ed6\u672c\u6765\u4e0d\u5fc5\u6765\u9001\u6b7b\u3002\u201d\u53f6\u5f00\u9053\uff1a\u201c\u4ed6\u4e5f\u4e0d\u914d\u4f60\u51fa\u624b\u3002\u201d \u3000\u3000\u58a8\u4e5d\u661f\u9053\uff1a\u201c\u6211\u5e76\u6ca1\u6709\u51fa\u624b\u3002\u201d \u3000\u3000\u4ed6\u7684\u786e\u8fde\u6307\u5c16\u90fd\u6ca1\u6709\u52a8\u8fc7\uff0c\u65e0\u8bba\u8c01\u4e5f\u770b\u4e0d\u89c1\u8fd9\u79cd\u6697\u5668\u4f1a\u5728\u4ec0\u4e48\u65f6\u5019\u53d1\u51fa\uff0c\u5f53\u7136\u66f4\u6ca1\u6cd5\u95ea \u907f\u3002 \u3000\u3000\u53f6\u5f00\u53c8\u53f9\u9053\uff1a\u201c\u770b\u6765\u4e0a\u5b98\u5c0f\u4ed9\u679c\u7136\u6ca1\u6709\u8bf4\u9519\u3002\u201d \u3000\u3000\u58a8\u4e5d\u661f\u9053\uff1a\u201c\u5979\u8bf4\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u5979\u8bf4\u4f60\u662f\u4e16\u4e0a\u6700\u53ef\u6015\u7684\u4e09\u4e2a\u4eba\u4e4b\u4e00\uff0c\u751a\u81f3\u5c31\u662f\u6700\u53ef\u6015\u7684\u4e00\u4e2a\u3002\u201d \u3000\u3000\u58a8\u4e5d\u661f\u51b7\u51b7\u7b11\u9053\uff1a\u201c\u7684\u786e\u6ca1\u6709\u8bf4\u9519\u3002\u201d \u3000\u3000\u9662\u5b50\u91cc\u6709\u4eba\u5728\u51b7\u7b11\uff0c\u5374\u4e0d\u77e5\u662f\u8c01\u5728\u51b7\u7b11\u3002 \u3000\u3000\u4e09\u4e2a\u540c\u6837\u7684\u4eba\uff0c\u5168\u90e8\u80cc\u8d1f\u7740\u53cc\u624b\uff0c\u7ad9\u5728\u661f\u5149\u4e0b\u3002 \u3000\u3000\u58a8\u4e5d\u661f\u5200\u950b\u822c\u7684\u76ee\u5149\u5728\u4ed6\u4eec\u811a\u4e0b\u4e00\u8f6c\uff0c\u5ffd\u7136\u505c\u7559\u5728\u4e00\u4e2a\u4eba\u7684\u8138\u4e0a\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u4e0d\u5fc5\u518d \u8981\u522b\u4eba\u9001\u6b7b\u4e86\u3002\u201d \u3000\u3000\u8fd9\u4eba\u9053\uff1a\u201c\u6211\uff1f\u201d \u3000\u3000\u58a8\u4e5d\u661f\u9053\uff1a\u201c\u5c31\u662f\u4f60\u3002\u201d\u4ed6\u773c\u775b\u5728\u8349\u5e3d\u91cc\u53d1\u7740\u5149\uff0c\u8fd9\u4eba\u7684\u773c\u775b\u4e5f\u5728\u9752\u94dc\u9762\u5177\u91cc\u53d1\u7740\u5149\u3002 \u3000\u3000\u4e24\u4e2a\u4eba\u7684\u76ee\u5149\u76f8\u9047\uff0c\u5c31\u50cf\u662f\u5200\u5251\u76f8\u51fb\u3002 \u3000\u3000\u98ce\u4e5f\u51b7\u5982\u5200\u950b\u3002 \u3000\u3000\u8fd9\u4eba\u7a81\u7136\u5927\u7b11\uff0c\u7b11\u58f0\u6bd4\u5200\u950b\u66f4\u51b7\uff0c\u66f4\u5c16\u9510\uff1a\u201c\u597d\uff01\u597d\u773c\u529b\uff01\u4f60\u662f\u600e\u4e48\u770b\u51fa\u6765\u7684\uff1f\u201d \u3000\u3000\u58a8\u4e5d\u661f\u9053\uff1a\u201c\u4f60\u4eec\u7684\u4eba\u53ef\u4ee5\u4f5c\u5047\uff0c\u811a\u4e0b\u7684\u811a\u5370\u5374\u662f\u5047\u4e0d\u4e86\u7684\u3002\u201d \u3000\u3000\u201c\u4f60\u6709\u591a\u6df1\u7684\u529f\u592b\uff0c\u5c31\u4f1a\u7559\u4e0b\u591a\u6df1\u7684\u811a\u5370\uff0c\u529f\u592b\u8d8a\u6df1\uff0c\u811a\u5370\u8d8a\u6d45\uff0c\u8fd9\u7684\u786e\u662f\u5047\u4e0d\u4e86 \u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u8fd9\u624d\u660e\u767d\u58a8\u4e5d\u661f\u4e3a\u4ec0\u4e48\u8981\u5728\u9662\u5b50\u91cc\u904d\u9152\u94f6\u7c89\u7684\u7528\u610f\u3002 \u3000\u3000\u591a\u5c14\u7532\u4e5f\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u4f60\u5bf9\u672c\u95e8\u7684\u529f\u592b\u4e5f\u5f88\u719f\u6089\u3002\u201d \u3000\u3000\u58a8\u4e5d\u661f\u9053\uff1a\u201c\u5929\u9b54\u5341\u4e09\u5927\u6cd5\uff0c\u5728\u6211\u773c\u91cc\u770b\u6765\uff0c\u6839\u672c\u4e0d\u503c\u4e00\u6587\u3002\u201d \u3000\u3000\u591a\u5c14\u7532\u51b7\u7b11\u9053\uff1a\u201c\u597d\uff0c\u5f88\u597d\u3002\u201d \u3000\u3000\u4ed6\u6325\u4e86\u6325\u624b\uff0c\u53e6\u5916\u7684\u4e24\u4e2a\u4eba\u5c31\u9000\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u53f6\u5f00\u5ffd\u7136\u53d1\u73b0\u4ed6\u7684\u624b\u5728\u661f\u5149\u4e0b\u770b\u6765\uff0c\u4e5f\u50cf\u662f\u5200\u950b\u822c\u51b7\u5389\u3002 \u3000\u3000\u4ed6\u7684\u624b\u663e\u7136\u4e5f\u662f\u79cd\u6740\u4eba\u5229\u5668\u3002 \u3000\u3000\u80fd\u6740\u4eba\u7684\uff0c\u5c31\u662f\u6b66\u5668\u3002 \u3000\u3000\u8981\u547d\u7684\u6b66\u5668\u3002 \u3000\u3000\u4ed6\u4eec\u8eab\u4e0a\u90fd\u6709\u7edd\u5bf9\u81f4\u547d\u7684\u6b66\u5668\uff0c\u8fd9\u79cd\u6b66\u5668\u7adf\u5df2\u6210\u4e3a\u4ed6\u4eec\u8eab\u4f53\u7684\u4e00\u90e8\u5206\u3002 \u3000\u3000\u6ca1\u6709\u4eba\u80fd\u593a\u8d70\u4ed6\u4eec\u7684\u6b66\u5668\uff0c\u4ed6\u4eec\u7684\u6b66\u5668\u5df2\u7ecf\u4e0e\u751f\u547d\u7ed3\u5408\u3002 \u3000\u3000\u4f60\u6700\u591a\u4e5f\u4e0d\u8fc7\u80fd\u593a\u8d70\u4ed6\u4eec\u7684\u751f\u547d\u3002 \u3000\u3000\u8fd9\u5c31\u662f\u4ed6\u4eec\u6700\u53ef\u6015\u4e4b\u5904\u3002 \u3000\u3000\u751f\u547d\u7684\u529b\u91cf\uff0c\u5c82\u975e\u5c31\u662f\u4e16\u4e0a\u6700\u53ef\u6015\u7684\u529b\u91cf\u3002 \u3000\u3000\u53f6\u5f00\u53f9\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u4ed6\u867d\u7136\u77e5\u9053\u8fd9\u4e00\u6218\u5fc5\u5c06\u6539\u53d8\u6c5f\u6e56\u4e2d\u5f88\u591a\u4eba\u7684\u547d\u8fd0\uff0c\u5bf9\u8fd9\u4e00\u6218\u7684\u7ed3\u5c40\uff0c\u4ed6\u4e5f\u540c\u6837\u5173\u5fc3\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u51e0\u4e4e\u5df2\u4e0d\u5fcd\u770b\u4e0b\u53bb\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u4e5f\u77e5\u9053\uff0c\u8981\u9020\u6210\u4e00\u4ef6\u8fd9\u79cd\u6b66\u5668\uff0c\u4e5f\u4e0d\u77e5\u8981\u6d41\u591a\u5c11\u6c57\uff0c\u591a\u5c11\u8840\uff0c\u591a\u5c11\u6cea\u3002 \u3000\u3000\u4ed6\u5b9e\u5728\u4e0d\u5fcd\u770b\u7740\u5b83\u88ab\u6bc1\u706d\u3002 \u3000\u3000\u6bc1\u706d\u4e4b\u524d\uff0c\u603b\u662f\u5206\u5916\u5b89\u9759\u5e73\u548c\u3002 \u3000\u3000\u9662\u5b50\u91cc\u66f4\u9759\uff0c\u6740\u6c14\u5c82\u975e\u4e5f\u662f\u770b\u4e0d\u89c1\u3001\u542c\u4e0d\u89c1\u7684\u3002 \u3000\u3000\u80fd\u611f\u89c9\u8fd9\u79cd\u6740\u6c14\u7684\uff0c\u4ed6\u672c\u8eab\u7684\u611f\u89c9\u4e5f\u4e00\u5b9a\u6bd4\u522b\u4eba\u654f\u9510\u3002 \u3000\u3000\u53f6\u5f00\u5ffd\u7136\u89c9\u5f97\u5f88\u51b7\u3002 \u3000\u3000\u4e00\u7f15\u523a\u9aa8\u7684\u5bd2\u610f\uff0c\u5c31\u50cf\u662f\u5200\u950b\u822c\u523a\u5165\u4e86\u4ed6\u7684\u9aa8\u9ad3\u3002 \u3000\u3000\u8fd9\u5c31\u662f\u6740\u6c14\u3002 \u3000\u3000\u8349\u5e3d\u5df2\u7834\u88c2\uff0c\u5374\u8fd8\u6ca1\u6709\u6458\u4e0b\u6765\uff0c\u53f6\u5f00\u8fd8\u662f\u770b\u4e0d\u6e05\u58a8\u4e5d\u661f\u7684\u8138\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u53ef\u4ee5\u770b\u89c1\u591a\u5c14\u7532\u7684\u773c\u775b\u3002 \u3000\u3000\u591a\u5c14\u7532\u7684\u77b3\u5b54\u5728\u6536\u7f29\uff0c\u5ffd\u7136\u9053\uff1a\u201c\u73b0\u5728\u5df2\u53ea\u5269\u4e00\u4e2a\u4eba\u3002\u201d \u3000\u3000\u53e6\u5916\u7684\u4e24\u4e2a\u4eba\uff0c\u7684\u786e\u5df2\u9000\u51fa\u7985\u9662\u3002 \u3000\u3000\u591a\u5c14\u7532\u9053\uff1a\u201c\u4f60\u4eec\u6709\u4e24\u4e2a\u4eba\u3002\u201d \u3000\u3000\u53f6\u5f00\u62a2\u7740\u9053\uff1a\u201c\u51fa\u624b\u7684\u5374\u53ea\u6709\u4e00\u4eba\u3002\u201d\u591a\u5c14\u7532\u9053\uff1a\u201c\u4f60\u867d\u4e0d\u51fa\u624b\uff0c\u4e5f\u5df2\u5a01\u80c1\u5230\u6211\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u591a\u5c14\u7532\u9053\uff1a\u201c\u56e0\u4e3a\u4f60\u7684\u5200\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u7684\u5200\u5e76\u4e0d\u662f\u7528\u6765\u6697\u7b97\u522b\u4eba\u7684\u3002\u201d \u3000\u3000\u591a\u5c14\u7532\u9053\uff1a\u201c\u53ef\u662f\u53ea\u8981\u6709\u5200\u5728\uff0c\u5c31\u5df2\u5a01\u80c1\u5230\u6211\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u8981\u6211\u8d70\uff1f\u201d \u3000\u3000\u591a\u5c14\u7532\u9053\uff1a\u201c\u4f60\u4e5f\u4e0d\u80fd\u8d70\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u591a\u5c14\u7532\u51b7\u51b7\u9053\uff1a\u201c\u6211\u4eec\u4e09\u4e2a\u4eba\u65e2\u7136\u90fd\u5df2\u6765\u4e86\uff0c\u81f3\u5c11\u5c31\u5f97\u6709\u4e24\u4e2a\u4eba\u6b7b\u5728\u8fd9\u91cc\u3002\u201d \u3000\u3000\u53f6\u5f00\u7b11\u9053\uff1a\u201c\u4f60\u6740\u4e86\u4ed6\uff0c\u8fd8\u8981\u6740\u6211\uff1f\u201d \u3000\u3000\u591a\u5c14\u7532\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u4e0d\u80fd\u8d70\u3002\u201d \u3000\u3000\u53f6\u5f00\u7b11\u9053\uff1a\u201c\u96be\u9053\u4f60\u8981\u6211\u5148\u4ea4\u51fa\u6211\u7684\u5200\uff0c\u7136\u540e\u5750\u5728\u8fd9\u91cc\u7b49\u6b7b\uff1f\u201d \u3000\u3000\u591a\u5c14\u7532\u9053\uff1a\u201c\u6211\u53ea\u8981\u4f60\u7b54\u5e94\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u8bf4\u3002\u201d \u3000\u3000\u591a\u5c14\u7532\u9053\uff1a\u201c\u4f60\u5df2\u8bf4\u8fc7\uff0c\u4f60\u4eec\u7edd\u4e0d\u4f1a\u4e24\u4eba\u540c\u65f6\u51fa\u624b\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u591a\u5c14\u7532\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u8bdd\u6211\u76f8\u4fe1\uff0c\u4f60\u5e76\u4e0d\u662f\u8a00\u800c\u65e0\u4fe1\u7684\u5c0f\u4eba\u3002\uff0c\u53f6\u5f00\u5fae\u7b11\u9053\uff1a\u201c\u591a\u8c22\u3002\u201d \u3000\u3000\u591a\u5c14\u7532\u9053\uff1a\u201c\u6240\u4ee5\u4ed6\u6d3b\u7740\u65f6\uff0c\u4f60\u7684\u5200\u5c31\u7edd\u4e0d\u80fd\u51fa\u624b\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4ed6\u82e5\u6b7b\u4e86\u5462\uff1f\u201d \u3000\u3000\u591a\u5c14\u7532\u9053\uff1a\u201c\u53ea\u8981\u770b\u89c1\u6211\u4e00\u62db\u5f97\u624b\uff0c\u5c31\u53ef\u4ee5\u53d1\u4f60\u7684\u5200\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u600e\u4e48\u6837\u624d\u53eb\u505a\u4e00\u62db\u5f97\u624b\uff1f\u201d \u3000\u3000\u591a\u5c14\u7532\u9053\uff1a\u201c\u53ea\u8981\u6211\u7684\u624b\u5df2\u6253\u5728\u4ed6\u8eab\u4e0a\uff0c\u5c31\u53eb\u505a\u4e00\u62db\u5f97\u624b\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u53ea\u8981\u4f60\u7684\u624b\u6253\u5728\u4ed6\u8eab\u4e0a\uff0c\u4ed6\u5c31\u5df2\u5fc5\u6b7b\u65e0\u7591\uff1f\u201d \u3000\u3000\u591a\u5c14\u7532\u50b2\u7136\u9053\uff1a\u201c\u6211\u7684\u624b\u672c\u5c31\u662f\u6b66\u5668\uff0c\u80fd\u4e00\u62db\u6740\u4eba\u7684\u624d\u80fd\u7b97\u505a\u6b66\u5668\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u73b0\u5728\u6211\u660e\u767d\u4e86\u3002\u201d \u3000\u3000\u591a\u5c14\u7532\u9053\uff1a\u201c\u4f60\u7b54\u5e94\uff1f\u201d \u3000\u3000\u53f6\u5f00\u770b\u7740\u4ed6\uff0c\u773c\u775b\u91cc\u5e26\u7740\u5f88\u5947\u602a\u7684\u8868\u60c5\uff0c\u8fc7\u4e86\u5f88\u4e45\uff0c\u624d\u7f13\u7f13\u9053\uff1a\u201c\u6211\u7b54\u5e94\uff0c\u56e0\u4e3a\u6211\u6b20\u4f60 \u7684\u60c5\u3002\u201d \u3000\u3000\u591a\u5c14\u7532\u76ef\u7740\u4ed6\uff0c\u8fc7\u4e86\u5f88\u4e45\uff0c\u624d\u7f13\u7f13\u9053\uff1a\u201c\u4f60\u51e0\u65f6\u6b20\u6211\u7684\u60c5\uff1f\u201d \u3000\u3000\u53f6\u5f00\u7b11\u4e86\u7b11\u9053\uff1a\u201c\u90a3\u6b21\u7684\u4e8b\u6211\u65e2\u7136\u6ca1\u6709\u5fd8\u8bb0\uff0c\u4f60\u5f53\u7136\u4e5f\u4e0d\u4f1a\u5fd8\u8bb0\u3002\u201d \u3000\u3000\u591a\u5c14\u7532\u9053\uff1a\u201c\u6211\u6b20\u4e0d\u6b20\u4f60\u7684\uff1f\u201d \u3000\u3000\u53f6\u5f00\u6447\u6447\u5934\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u8fd9\u6b21\u82e5\u6740\u4e86\u6211\uff0c\u6211\u7edd\u4e0d\u602a\u4f60\u3002\u201d \u3000\u3000\u591a\u5c14\u7532\u9053\uff1a\u201c\u5f88\u597d\uff0c\u8fd9\u53e5\u8bdd\u6211\u7edd\u4e0d\u4f1a\u5fd8\u8bb0\u3002\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u8f6c\u8eab\uff0c\u76ef\u7740\u58a8\u4e5d\u661f\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u53ea\u4e0d\u8fc7\u7b2c\u4e00\u4e2a\u8981\u6b7b\u7684\u8fd8\u662f\u4f60\u3002\u201d \u3000\u3000\u58a8\u4e5d\u661f\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u597d\u50cf\u8fd8\u662f\u5fd8\u8bb0\u4e86\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u591a\u5c14\u7532\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u58a8\u4e5d\u661f\u9053\uff1a\u201c\u6211\u82e5\u6ca1\u6709\u628a\u63e1\u6740\u4f60\uff0c\u600e\u4e48\u4f1a\u7279\u5730\u7ea6\u4f60\u6765\uff1f\u201d \u3000\u3000\u591a\u5c14\u7532\u9053\uff1a\u201c\u4e5f\u8bb8\u4f60\u672c\u6765\u7684\u786e\u6709\u51e0\u5206\u628a\u63e1\uff0c\u53ea\u53ef\u60dc\u4f60\u4e5f\u5fd8\u8bb0\u4e86\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u58a8\u4e5d\u661f\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u591a\u5c14\u7532\u9053\uff1a\u201c\u4f60\u4e0d\u5e94\u8be5\u6cc4\u9732\u4e86\u4f60\u7684\u79d8\u5bc6\u3002\u201d \u3000\u3000\u58a8\u4e5d\u661f\u53c8\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\u79d8\u5bc6\uff1f\u201d \u3000\u3000\u591a\u5c14\u7532\u9053\uff1a\u201c\u6740\u4eba\u7684\u79d8\u5bc6\u3002\u201d \u3000\u3000\u58a8\u4e5d\u661f\u5728\u51b7\u7b11\uff0c\u5374\u4e0d\u7531\u81ea\u4e3b\u770b\u4e86\u5730\u4e0a\u7684\u6b7b\u4eba\u4e00\u773c\u3002 \u3000\u3000\u591a\u5c14\u7532\u9053\uff1a\u201c\u4f60\u4e0d\u8be5\u7528\u8fd9\u79cd\u6cd5\u4e8e\u6740\u4ed6\u7684\uff0c\u4f60\u672c\u8be5\u7559\u7740\u8fd9\u4e00\u62db\u6765\u5bf9\u4ed8\u6211\u3002\u201d \u3000\u3000\u58a8\u4e5d\u661f\u51b7\u7b11\u9053\uff1a\u201c\u6211\u4e0d\u7528\u8fd9\u6cd5\u5b50\uff0c\u4e5f\u53ef\u4ee5\u6740\u4f60\u3002\u201d \u3000\u3000\u591a\u5c14\u7532\u5927\u7b11\u3002 \u3000\u3000\u65e0\u8bba\u8c01\u5728\u7b11\u7684\u65f6\u5019\uff0c\u7cbe\u795e\u96be\u514d\u677e\u5f1b\uff0c\u6212\u5907\u90fd\u96be\u514d\u758f\u5ffd\u3002 \u3000\u3000\u4ed6\u4e00\u5f00\u59cb\u7b11\uff0c\u53f6\u5f00\u5df2\u53d1\u73b0\u4ed6\u9732\u51fa\u4e86\u7a7a\u95e8\u3002 \u3000\u3000\u201c\u7a7a\u95e8\u201d\u7684\u610f\u601d\uff0c\u5c31\u662f\u6b7b\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u58a8\u4e5d\u661f\u5df2\u6251\u8fc7\u53bb\u3002 \u3000\u3000\u4ed6\u7684\u8eab\u6cd5\u8f7b\u7075\u5982\u70df\u96fe\uff0c\u654f\u6377\u5982\u71d5\u5b50\uff0c\u4ed6\u7684\u51fa\u4e8e\u5374\u9510\u5982\u9e70\u5544\uff0c\u731b\u70c8\u5982\u96f7\u7535\u3002 \u3000\u3000\u4ed6\u5df2\u770b\u51c6\u4e86\u591a\u5c14\u7532\u7684\u7a7a\u95e8\u3002 \u3000\u3000\u591a\u5c14\u7532\u8fd8\u5728\u7b11\u3002 \u3000\u3000\u53ef\u662f\u7b49\u5230\u58a8\u4e5d\u661f\u6251\u8fc7\u53bb\u65f6\uff0c\u4ed6\u7684\u7a7a\u95e8\u5df2\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u8fd9\u95f4\u4e0d\u5bb9\u53d1\u7684\u4e00\u5239\u90a3\u95ee\uff0c\u4ed6\u7684\u7a7a\u95e8\u5df2\u5947\u8ff9\u822c\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u4ed6\u7684\u624b\u5728\u90a3\u91cc\u3002 \u3000\u3000\u522b\u4eba\u7684\u624b\uff0c\u53ea\u4e0d\u8fc7\u662f\u4e00\u53ea\u624b\uff0c\u4f46\u4ed6\u7684\u624b\u5374\u662f\u79cd\u81f4\u547d\u7684\u6b66\u5668\u3002 \u3000\u3000\u58a8\u4e5d\u661f\u4e00\u62db\u51fb\u51fa\uff0c\u5ffd\u7136\u53d1\u73b0\u8fd9\u4e00\u62db\u6253\u7684\u4e0d\u662f\u7a7a\u95e8\uff0c\u800c\u662f\u4ed6\u7684\u624b\u3002 \u3000\u3000\u2014\u2014\u662f\u591a\u5c14\u7532\u7684\u624b\uff0c\u53ea\u4e0d\u8fc7\u662f\u4e00\u53ea\u624b\u3002 \u3000\u3000\u6ca1\u6709\u4eba\u80fd\u7528\u4e00\u53ea\u624b\u53bb\u786c\u62fc\u4e00\u4ef6\u81f4\u547d\u7684\u6b66\u5668\u3002 \u3000\u3000\u58a8\u4e5d\u661f\u60f3\u6536\u56de\u8fd9\u4e00\u62db\uff0c\u5df2\u6765\u4e0d\u53ca\u4e86\u3002 \u3000\u3000\u4ed6\u7684\u624b\u63a5\u8fd1\u591a\u5c14\u7532\u7684\u624b\u65f6\uff0c\u5c31\u53ef\u4ee5\u611f\u89c9\u5230\u4e00\u79cd\u51b0\u51b7\u7684\u6740\u6c14\u3002 \u3000\u3000\u5c31\u50cf\u662f\u5251\u950b\u4e0a\u53d1\u51fa\u7684\u5251\u6c14\u4e00\u6837\u3002 \u3000\u3000\u591a\u5c14\u7532\u51b7\u7b11\u3002 \u3000\u3000\u53f6\u5f00\u5374\u4e0d\u7981\u53f9\u606f\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u65e0\u8bba\u8c01\u7684\u624b\u6253\u5728\u591a\u5c14\u7532\u8fd9\u53ea\u624b\u4e0a\uff0c\u90fd\u662f\u60b2\u5267\u3002 \u3000\u3000\u4ed6\u51e0\u4e4e\u5df2\u53ef\u60f3\u8c61\u5230\u58a8\u4e5d\u661f\u8fd9\u53ea\u624b\u7c89\u788e\u7684\u60c5\u51b5\u3002 \u3000\u3000\u53ea\u542c\u201c\u556a\u201d\u7684\u4e00\u58f0\uff0c\u53cc\u624b\u62cd\u51fb\u3002 \u3000\u3000\u58a8\u4e5d\u661f\u7684\u624b\u6ca1\u6709\u788e\u3002 \u3000\u3000\u4ed6\u7ade\u5728\u8fd9\u4e00\u5239\u90a3\u95f4\uff0c\u5c06\u624b\u4e0a\u7684\u529b\u91cf\u5b8c\u5168\u6d88\u6cc4\u4e86\u51fa\u53bb\u3002\u4ed6\u7adf\u5df2\u80fd\u5c06\u81ea\u5df1\u5168\u8eab\u7684\u529b\u91cf\uff0c\u6536\u653e \u81ea\u5982\u3002 \u3000\u3000\u8fd9\u7528\u529b\u7684\u4e00\u51fb\uff0c\u7adf\u53d8\u6210\u4e86\u8f7b\u8f7b\u4e00\u62cd\uff0c\u8f7b\u5f97\u51e0\u4e4e\u5c31\u50cf\u662f\u629a\u6478\u3002 \u3000\u3000\u629a\u6478\u662f\u7edd\u4e0d\u4f1a\u4f24\u4eba\u7684\uff0c\u65e2\u4e0d\u4f1a\u4f24\u5bb3\u522b\u4eba\uff0c\u4e5f\u4e0d\u4f1a\u4f24\u5bb3\u81ea\u5df1\u3002 \u3000\u3000\u53ea\u8981\u4f60\u7528\u7684\u529b\u91cf\u591f\u8f7b\uff0c\u5c31\u7b97\u53bb\u629a\u6478\u4e00\u67c4\u5229\u5251\uff0c\u4e5f\u4e0d\u4f1a\u4f24\u5bb3\u4e86\u4f60\u3002 \u3000\u3000\u591a\u5c14\u7532\u6014\u4f4f\u3002 \u3000\u3000\u8fd9\u8f7b\u8f7b\u7684\u4e00\u62cd\uff0c\u7adf\u4f3c\u6bd4\u91cd\u903e\u6cf0\u5c71\u7684\u4e00\u51fb\u66f4\u4ee4\u4ed6\u5403\u60ca\u3002 \u3000\u3000\u4ed6\u4ece\u6765\u4e5f\u6ca1\u6709\u63a5\u8fc7\u8fd9\u4e48\u8f7b\u7684\u4e00\u62db\u3002 \u3000\u3000\u9ad8\u624b\u8f83\u6280\uff0c\u5f80\u5f80\u53ea\u4e0d\u8fc7\u662f\u4e00\u62db\u4e4b\u4e89\u3002 \u3000\u3000\u8fd9\u4e00\u62db\u5374\u662f\u5343\u53d8\u4e07\u5316\uff0c\u65e0\u5947\u4e0d\u6709\u7684\u3002 \u3000\u3000\u58a8\u4e5d\u661f\u8fd9\u4e00\u62db\u7684\u5947\u5999\uff0c\u5e76\u4e0d\u5728\u4ed6\u7684\u53d8\u5316\u5feb\uff0c\u51fa\u624b\u91cd\u3002\u4ed6\u4e00\u62db\u80fd\u5236\u654c\uff0c\u53ea\u4e0d\u8fc7\u56e0\u4e3a\u4ed6\u7684\u51fa \u624b\u591f\u8f7b\u3002 \u3000\u3000\u53f6\u5f00\u4e5f\u4e0d\u7981\u53f9\u4e3a\u89c2\u6b62\u3002 \u3000\u3000\u76f4\u5230\u73b0\u5728\u624d\u660e\u767d\uff0c\u6b66\u529f\u4e2d\u7684\u53d8\u5316\u5965\u5999\uff0c\u7684\u786e\u662f\u4e0d\u53ef\u601d\u4eea\uff0c\u6c38\u65e0\u6b62\u5883\u7684\u3002 \u3000\u3000\u591a\u5c14\u7532\u4e00\u6014\u95f4\uff0c\u58a8\u4e5d\u661f\u7684\u624b\u5df2\u6cbf\u7740\u4ed6\u7684\u624b\u80cc\u6ed1\u8fc7\u53bb\uff0c\u6263\u6ce8\u4e86\u4ed6\u7684\u8109\u95e8\u3002 \u3000\u3000\u4ed6\u53c8\u4e00\u60ca\uff0c\u867d\u60ca\u800c\u4e0d\u4e71\u3002 \u3000\u3000\u4ed6\u7684\u53e6\u4e00\u53ea\u624b\u7a81\u7136\u4ece\u4e0b\u7ffb\u51fa\uff0c\u731b\u5207\u58a8\u4e5d\u661f\u7684\u65f6\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u53c8\u5fd8\u4e86\u4e00\u4ef6\u4e8b\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u7684\u8109\u95e8\u82e5\u662f\u88ab\u6263\u4f4f\uff0c\u7eb5\u7136\u6709\u5343\u65a4\u795e\u529b\uff0c\u4e5f\u4f7f\u4e0d\u51fa\u6765\u4e86\u3002 \u3000\u3000\u53f6\u5f00\u5df2\u542c\u89c1\u4e00\u9635\u9aa8\u5934\u788e\u88c2\u7684\u58f0\u97f3\u2014\u2014\u4e0d\u662f\u58a8\u4e5d\u661f\u7684\u9aa8\u5934\uff0c\u662f\u591a\u5c14\u7532\u7684\u3002 \u3000\u3000\u591a\u5c14\u7532\u5931\u58f0\u9ad8\u547c\uff1a\u201c\u4f60\u2026\u2026\u201d \u3000\u3000\u4ed6\u53ea\u8bf4\u51fa\u4e00\u4e2a\u5b57\u3002 \u3000\u3000\u201c\u4f60\uff01\u201d \u3000\u3000\u8fd9\u5c31\u662f\u4ed6\u8fd9\u4e00\u751f\u4e2d\uff0c\u8bf4\u51fa\u7684\u6700\u540e\u4e00\u4e2a\u5b57\u3002 \u3000\u3000\u4e00\u9897\u5bd2\u661f\u5df2\u6253\u5165\u4e86\u4ed6\u7684\u54bd\u5589\uff1a\u4e00\u9897\u6740\u4eba\u7684\u661f\uff01 \u3000\u3000\u6ca1\u6709\u58f0\u97f3\uff0c\u4e00\u70b9\u58f0\u97f3\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u751a\u81f3\u8fde\u98ce\u90fd\u5df2\u9759\u6b62\u3002 \u3000\u3000\u591a\u5c14\u7532\u5012\u5728\u8840\u6cca\u4e2d\uff0c\u4ed6\u4e00\u5012\u4e0b\u53bb\uff0c\u4ed6\u7684\u4eba\u5c31\u4f3c\u5df2\u5728\u4e8e\u762a\u6536\u7f29\u3002 \u3000\u3000\u4ed6\u6d3b\u7740\u65f6\u65e0\u8bba\u662f\u82f1\u96c4\u4e5f\u597d\uff0c\u662f\u9b54\u738b\u4e5f\u597d\uff0c\u73b0\u5728\u5374\u5df2\u53ea\u4e0d\u8fc7\u662f\u4e2a\u6b7b\u4eba\u3002 \u3000\u3000\u6b7b\u4eba\u5c31\u662f\u6b7b\u4eba\u3002 \u3000\u3000\u5c31\u7b97\u662f\u4e16\u4e0a\u6700\u53ef\u6015\u7684\u4eba\uff0c\u6b7b\u4e86\u540e\u770b\u6765\u4e5f\u8ddf\u522b\u7684\u4eba\u6ca1\u4ec0\u4e48\u4e0d\u540c\u3002 \u3000\u3000\u552f\u4e00\u4e0d\u540c\u7684\uff0c\u662f\u4ed6\u7684\u624b\u3002 \u3000\u3000\u4ed6\u7684\u624b\u8fd8\u662f\u5728\u591c\u7a7a\u4e0b\u95ea\u7740\u5149\uff0c\u4eff\u4f5b\u662f\u5728\u5411\u58a8\u4e5d\u661f\u793a\u5a01\u3002 \u3000\u3000\u201c\u4f60\u867d\u7136\u6740\u4e86\u6211\uff0c\u6bc1\u706d\u4e86\u6211\u8fd9\u4e2a\u4eba\uff0c\u5374\u8fd8\u662f\u6ca1\u6709\u6bc1\u706d\u6211\u8fd9\u53cc\u624b\uff01\u201d \u3000\u3000\u201c\u6211\u8fd9\u53cc\u624b\u8fd8\u662f\u5929\u4e0b\u65e0\u53cc\u7684\u6b66\u5668\uff01\u201d \u3000\u3000\u8fd8\u6ca1\u6709\u71c3\u706f\u3002 \u3000\u3000\u58a8\u4e5d\u661f\u7ad9\u5728\u661f\u7a7a\u4e0b\uff0c\u52a8\u4e5f\u4e0d\u52a8\u5730\u7ad9\u7740\u3002 \u3000\u3000\u6fc0\u6218\u8fc7\u540e\uff0c\u7eb5\u7136\u662f\u80dc\u5229\u8005\uff0c\u4e5f\u96be\u514d\u4f1a\u611f\u89c9\u5230\u4e00\u79cd\u8bf4\u4e0d\u51fa\u7684\u7a7a\u865a\u4e0e\u5bc2\u5bde\u3002 \u3000\u3000\u4ed6\u662f\u4e0d\u662f\u4e5f\u4e0d\u4f8b\u5916\uff1f \u3000\u3000\u8fc7\u4e86\u5f88\u4e45\uff0c\u4ed6\u624d\u8f6c\u8fc7\u5934\u3002 \u3000\u3000\u53f6\u5f00\u6b63\u8d70\u8fc7\u6765\u3002 \u3000\u3000\u58a8\u4e5d\u661f\u770b\u7740\u4ed6\uff0c\u5ffd\u7136\u9053\uff1a\u201c\u4f60\u4e0d\u60f3\u63ed\u5f00\u4ed6\u7684\u9762\u5177\u6765\u770b\u770b\uff1f\u2019\u53f6\u5f00\u53f9\u606f\u9053\uff1a\u201c\u4e0d\u5fc5\u3002\u201d \u3000\u3000\u58a8\u4e5d\u661f\u9053\uff1a\u201c\u4f60\u5df2\u77e5\u9053\u4ed6\u662f\u8c01\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u8ba4\u5f97\u8fd9\u53cc\u624b\u3002\u201d \u3000\u3000\u624b\u8fd8\u5728\u53d1\u7740\u5149\u3002 \u3000\u3000\u53f6\u5f00\u770b\u7740\u8fd9\u53cc\u624b\uff0c\u53c8\u4e0d\u7981\u53f9\u9053\uff1a\u201c\u8fd9\u7684\u786e\u662f\u5929\u4e0b\u65e0\u53cc\u7684\u6b66\u5668\u3002\u201d \u3000\u3000\u4e16\u4e0a\u7684\u786e\u6c38\u8fdc\u518d\u627e\u4e0d\u51fa\u8fd9\u4e00\u53cc\u624b\u3002 \u3000\u3000\u58a8\u4e5d\u661f\u6de1\u6de1\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u65e0\u8bba\u591a\u53ef\u6015\u7684\u6b66\u5668\uff0c\u672c\u8eab\u90fd\u4e0d\u80fd\u6740\u4eba\u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u660e\u767d\u3002 \u3000\u3000\u6740\u4eba\u7684\u5e76\u4e0d\u662f\u6b66\u5668\uff0c\u6740\u4eba\u7684\u662f\u4eba\u3002 \u3000\u3000\u58a8\u4e5d\u661f\u9053\uff1a\u201c\u4e00\u4ef6\u6b66\u5668\u662f\u5426\u53ef\u6015\uff0c\u4e3b\u8981\u5f97\u770b\u5b83\u662f\u5728\u4ec0\u4e48\u4eba\u624b\u91cc\u3002\u201d \u3000\u3000\u8fd9\u9053\u7406\u53f6\u5f00\u4e03\u7136\u4e5f\u660e\u767d\u3002 \u3000\u3000\u58a8\u4e5d\u661f\u9053\uff1a\u201c\u6211\u90a3\u4e00\u62db\u82e5\u662f\u51fa\u4e8e\u91cd\u4e86\u4e9b\uff0c\u6211\u7684\u624b\u5f88\u53ef\u80fd\u88ab\u4ed6\u6bc1\u4e86\u3002\u201d \u3000\u3000\u53f6\u5f00\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u5f88\u53ef\u80fd\u3002\u201d \u3000\u3000\u58a8\u4e5d\u661f\u9053\uff1a\u201c\u53ef\u662f\u6211\u90a3\u4e00\u62db\u51fa\u624b\u591f\u8f7b\uff0c\u8fd9\u5c31\u662f\u80dc\u8d1f\u7684\u5173\u952e\u3002\u201d \u3000\u3000\u53f6\u5f00\u82e6\u7b11\u9053\uff1a\u201c\u90a3\u4e00\u62db\u7684\u786e\u5999\u5f97\u5f88\u3002\u201d \u3000\u3000\u58a8\u4e5d\u661f\u9053\uff1a\u201c\u9ad8\u624b\u76f8\u6597\uff0c\u80dc\u8d1f\u7684\u5173\u952e\uff0c\u5f80\u5f80\u5c31\u5728\u8fd9\u4e00\u62db\u95ee\u3002\u201d \u3000\u3000\u53f6\u5f00\u6c89\u9ed8\u7740\uff0c\u5ffd\u7136\u4fef\u4e0b\u8eab\uff0c\u53bb\u63ed\u201c\u591a\u5c14\u7532\u201d\u8138\u4e0a\u7684\u9762\u5177\u3002 \u3000\u3000\u58a8\u4e5d\u661f\u9053\uff1a\u201c\u4f60\u65e2\u7136\u5df2\u77e5\u9053\u4ed6\u662f\u4ec0\u4e48\u4eba\uff0c\u73b0\u5728\u8fd8\u60f3\u518d\u770b\u770b\u4ed6\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u55ef\u3002\u201d \u3000\u3000\u58a8\u4e5d\u661f\u9053\uff1a\u201c\u6b7b\u4eba\u5e76\u6ca1\u6709\u4ec0\u4e48\u597d\u770b\u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f46\u6211\u5374\u60f3\u770b\u770b\uff0c\u4ed6\u4e34\u6b7b\u524d\u662f\u4e0d\u662f\u4e5f\u5df2\u660e\u767d\u8fd9\u9053\u7406\u3002\u201d \u7b2c\u4e8c\u5341\u4e5d\u7ae0\u3000\u9b54\u6559\u8840\u4e66>> \u53e4\u9f99\u300a\u4e5d\u6708\u9e70\u98de\u300b \u7b2c\u4e8c\u5341\u4e5d\u7ae0\u3000\u9b54\u6559\u8840\u4e66 \u3000\u3000\u9752\u94dc\u7684\u9762\u5177\uff0c\u5728\u661f\u7a7a\u4e0b\u53d1\u7740\u9752\u5149\u3002 \u3000\u3000\u5415\u8fea\u7684\u8138\u8272\u4e5f\u662f\u94c1\u9752\u7684\uff0c\u5374\u5df2\u626d\u66f2\uff0c\u4e00\u53cc\u51f8\u51fa\u7684\u773c\u775b\u91cc\uff0c\u5145\u6ee1\u4e86\u6050\u60e7\u548c\u4e0d\u4fe1\u3002 \u3000\u3000\u4ed6\u81f3\u6b7b\u4e5f\u4e0d\u80fd\u76f8\u4fe1\u4e00\u4ef6\u4e8b\u3002 \u3000\u3000\u4e00\u4ef6\u4ec0\u4e48\u4e8b\u5462\uff1f \u3000\u3000\u53f6\u5f00\u53f9\u9053\uff1a\u201c\u4ed6\u597d\u50cf\u81f3\u6b7b\u4e5f\u4e0d\u76f8\u4fe1\u4f60\u80fd\u6740\u4e86\u4ed6\u3002\u201d \u3000\u3000\u58a8\u4e5d\u661f\u51b7\u51b7\u9053\uff1a\u201c\u5c31\u56e0\u4e3a\u4ed6\u4e0d\u4fe1\uff0c\u6240\u4ee5\u4ed6\u624d\u4f1a\u6b7b\u3002\u201d \u3000\u3000\u53f6\u5f00\u53f9\u606f\u7740\uff0c\u5f90\u5f90\u9053\uff1a\u201c\u6709\u4e9b\u4e8b\u7684\u786e\u662f\u4e00\u4e2a\u4eba\u81f3\u6b7b\u4e5f\u4e0d\u4f1a\u660e\u767d\u7684\u2026\u2026\u201d \u3000\u3000\u53f6\u5f00\u4e5f\u6709\u4ef6\u4e8b\u8fd8\u4e0d\u660e\u767d\u3002 \u3000\u3000\u201c\u591a\u5c14\u7532\u201d\u65e2\u7136\u662f\u5415\u8fea\uff0c\u90a3\u4e48\u201c\u5e03\u8fbe\u62c9\u201d\u5b64\u5cf0\u5929\u738b\u662f\u8c01\u5462\uff1f \u3000\u3000\u6b7b\u4eba\u5df2\u642c\u8d70\uff0c\u5c4b\u5b50\u91cc\u5374\u8fd8\u6ca1\u6709\u71c3\u706f\u3002 \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u665a\u4e0a\u4f60\u81ea\u5df1\u4ece\u4e0d\u70b9\u706f\uff1f\u201d \u3000\u3000\u58a8\u4e5d\u661f\u53cd\u95ee\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u8981\u70b9\u706f\uff1f\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u95ee\u5f97\u5f88\u5999\uff0c\u53f6\u5f00\u7adf\u88ab\u95ee\u5f97\u6014\u4e86\u6014\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u6bcf\u4e2a\u4eba\u5230\u4e86\u665a\u4e0a\u90fd\u8981\u70b9\u706f\u7684\uff0c\u70b9\u8d77 \u706f\u6765\uff0c\u624d\u53ef\u4ee5\u770b\u6e05\u5f88\u591a\u4e8b\u3002\u201d \u3000\u3000\u58a8\u4e5d\u661f\u9053\uff1a\u201c\u4e0d\u70b9\u706f\u6211\u4e5f\u4e00\u6837\u53ef\u4ee5\u770b\u5f97\u5f88\u6e05\u695a\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u770b\u4e0d\u6e05\u695a\u3002\u201d \u3000\u3000\u58a8\u4e5d\u661f\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u968f\u65f6\u90fd\u53ef\u4ee5\u8d70\uff0c\u6211\u5e76\u6ca1\u6709\u7559\u4f60\u3002\u201d \u3000\u3000\u53f6\u5f00\u53c8\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u53ef\u662f\u4f60\u4e5f\u6ca1\u6709\u8d76\u6211\u8d70\u3002\u201d \u3000\u3000\u58a8\u4e5d\u661f\u9053\uff1a\u201c\u6211\u4e0d\u5fc5\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4e0d\u5fc5\uff1f\u201d \u3000\u3000\u58a8\u4e5d\u661f\u9053\uff1a\u201c\u8be5\u8d70\u7684\u65f6\u5019\uff0c\u4f60\u603b\u662f\u8981\u8d70\u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4ec0\u4e48\u65f6\u5019\u5bf9\u201d\u662f\u8be5\u8d70\u7684\u65f6\u5019\uff1f\u201d \u3000\u3000\u58a8\u4e5d\u661f\u9053\uff1a\u201c\u627e\u5230\u5b64\u5cf0\u7684\u65f6\u5019\u3002\u201d \u3000\u3000\u53f6\u5f00\u773c\u775b\u4eae\u4e86\uff0c\u7acb\u523b\u8ffd\u95ee\u9053\uff1a\u201c\u4f60\u4e5f\u77e5\u9053\u5b64\u5cf0\u662f\u8c01\uff1f\u201d \u3000\u3000\u58a8\u4e5d\u661f\u6ca1\u6709\u56de\u7b54\uff0c\u5374\u53c8\u53cd\u95ee\u9053\uff1a\u201c\u4f60\u4e00\u5b9a\u8ba4\u4e3a\u5415\u8fea\u662f\u5b64\u5cf0\uff1f\u201d \u3000\u3000\u53f6\u5f00\u4e0d\u80fd\u5426\u8ba4\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u56e0\u4e3a\u4ed6\u7684\u786e\u662f\u5b64\u9ad8\u9a84\u50b2\u7684\u4eba\u3002\u201d \u3000\u3000\u58a8\u4e5d\u661f\u9053\uff1a\u201c\u73b0\u5728\u4f60\u5df2\u80fd\u786e\u5b9a\u4ed6\u4e0d\u662f\u5b64\u5cf0\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u5b64\u5cf0\u5df2\u53d7\u4e86\u4f24\uff0c\u5415\u8fea\u5374\u6ca1\u6709\u3002\u201d \u3000\u3000\u4ed6\u5df2\u4ed4\u7ec6\u770b\u8fc7\uff0c\u5415\u8fea\u8eab\u4e0a\u552f\u4e00\u7684\u4f24\u75d5\uff0c\u5c31\u662f\u58a8\u4e5d\u661f\u7559\u4e0b\u7684\u3002 \u3000\u3000\u58a8\u4e5d\u661f\u9053\uff1a\u201c\u4f60\u80fd\u786e\u5b9a\u5b64\u5cf0\u5df2\u53d7\u4f24\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6709\u4eba\u4eb2\u773c\u770b\u89c1\u7684\u3002\u201d \u3000\u3000\u58a8\u4e5d\u661f\u9053\uff1a\u201c\u662f\u4ec0\u4e48\u4eba\u4eb2\u773c\u770b\u89c1\u7684\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4e00\u4e2a\u6211\u7edd\u5bf9\u4fe1\u4efb\u7684\u4eba\u3002\u201d \u3000\u3000\u58a8\u4e5d\u661f\u51b7\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u4fe1\u4efb\u7684\u4eba\u4e5f\u597d\u50cf\u4e0d\u5c11\u3002\u201d \u3000\u3000\u53f6\u5f00\u53f9\u9053\u201c\u6211\u4e5f\u77e5\u9053\u8fd9\u662f\u6211\u7684\u5927\u6bdb\u75c5\uff0c\u53ea\u53ef\u60dc\u6211\u603b\u662f\u6539\u4e0d\u4e86\u3002\u201d \u3000\u3000\u58a8\u4e5d\u661f\u4e0d\u518d\u8bf4\u8bdd\u3002 \u3000\u3000\u8349\u5e3d\u867d\u7136\u5df2\u7834\u4e86\uff0c\u5374\u8fd8\u662f\u6070\u597d\u80fd\u906e\u4f4f\u4ed6\u7684\u8138\uff0c\u8c01\u4e5f\u770b\u4e0d\u89c1\u4ed6\u8138\u4e0a\u7684\u8868\u60c5\u3002 \u3000\u3000\u4e5f\u8bb8\u4ed6\u8138\u4e0a\u6839\u672c\u5c31\u6ca1\u6709\u8868\u60c5\u3002 \u3000\u3000\u53f6\u5f00\u5fcd\u4e0d\u4f4f\u53c8\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u8fd8\u662f\u6234\u7740\u8fd9\u8349\u5e3d\uff1f\u201d \u3000\u3000\u58a8\u4e5d\u661f\u9053\uff1a\u201c\u56e0\u4e3a\u5916\u9762\u6709\u72d7\u5728\u53eb\u3002\u201d \u3000\u3000\u53f6\u5f00\u6014\u4e86\u6014\uff0c\u9053\uff1a\u201c\u5916\u9762\u6709\u72d7\u53eb\uff0c\u8ddf\u4f60\u6234\u8349\u5e3d\u53c8\u6709\u4ec0\u4e48\u5173\u7cfb\uff1f\u201d \u3000\u3000\u58a8\u4e5d\u661f\u51b7\u51b7\u9053\uff1a\u201c\u6211\u6234\u4e0d\u6234\u8349\u5e3d\uff0c\u8ddf\u4f60\u53c8\u6709\u4ec0\u4e48\u5173\u7cfb\uff1f\u201d \u3000\u3000\u53f6\u5f00\u7b11\u4e86\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u53d1\u73b0\u8fd9\u4eba\u770b\u6765\u867d\u6c89\u9ed8\u5be1\u8a00\uff0c\u5176\u5b9e\u5374\u662f\u4e2a\u5f88\u4f1a\u8bf4\u8bdd\u7684\u4eba\uff0c\u8bf4\u51fa\u6765\u7684\u8bdd\uff0c\u5f80\u5f80\u80fd\u4e00\u4e0b \u5b50\u5c31\u5c01\u4f4f\u522b\u4eba\u7684\u5634\uff0c\u4ee4\u4eba\u975e\u4f46\u65e0\u6cd5\u8fa9\u8bba\uff0c\u4e5f\u65e0\u6cd5\u518d\u95ee\u4e0b\u53bb\u3002 \u3000\u3000\u53f6\u5f00\u5374\u504f\u504f\u6709\u4e9b\u4e8b\u8981\u95ee\uff0c\u800c\u4e14\u975e\u95ee\u4e0d\u53ef\u3002 \u3000\u3000\u58a8\u4e5d\u661f\u5728\u9489\u5b50\u4e0a\u6302\u8d77\u4e86\u6761\u957f\u7ef3\uff0c\u7adf\u771f\u7684\u8eba\u5728\u7ef3\u5b50\u4e0a\uff0c\u800c\u4e14\u8fd8\u50cf\u662f\u5f88\u8212\u670d\u7684\u6837\u5b50\u4f3c\u7684\u3002 \u3000\u3000\u4ed6\u7761\u89c9\u7684\u65f6\u5019\u8fd8\u662f\u6234\u7740\u90a3\u9876\u8349\u5e3d\u3002 \u3000\u3000\u7985\u623f\u91cc\u8fde\u51f3\u5b50\u90fd\u6ca1\u6709\uff0c\u53f6\u5f00\u53ea\u6709\u7ad9\u7740\uff0c\u642d\u6c55\u7740\u9053\uff1a\u201c\u636e\u8bf4\u9752\u57ce\u662f\u9053\u5bb6\u7684\u4e09\u5341\u516d\u6d1e\u5929\u4e4b\u4e00 \u6d1e\u5929\u798f\u5730\uff0c\u98ce\u7269\u7f8e\u4e0d\u80dc\u6536\u3002\u201d \u3000\u3000\u58a8\u4e5d\u661f\u4e0d\u7406\u4ed6\u3002 \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u4eec\u9690\u5c45\u7684\u90a3\u4e2a\u5730\u65b9\uff0c\u4e00\u5b9a\u66f4\u662f\u4e2a\u4e16\u5916\u6843\u6e90\uff0c\u5374\u4e0d\u77e5\u6211\u662f\u4e0d\u662f\u6709\u798f\u6c14\u53bb\u770b\u4e00 \u770b\uff1f\u201d \u3000\u3000\u58a8\u4e5d\u661f\u8fd8\u662f\u4e0d\u7406\u4ed6\u3002 \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u90a3\u5730\u65b9\u636e\u8bf4\u4ece\u6765\u4e5f\u6ca1\u6709\u5916\u4eba\u53bb\u8fc7\uff0c\u4f60\u4eec\u4e5f\u4ece\u6765\u4e0d\u8ddf\u5916\u9762\u7684\u4eba\u6765\u5f80\uff0c\u53ef\u662f\u4f60\u4e00 \u51fa\u5c71\u5c31\u627e\u5230\u4e86\u591a\u5c14\u7532\uff0c\u4f60\u7684\u672c\u4e8b\u5012\u4e0d\u5c0f\u3002\u201d \u3000\u3000\u58a8\u4e5d\u661f\u95ed\u4e0a\u773c\u775b\uff0c\u4f3c\u5df2\u7761\u7740\u3002 \u3000\u3000\u53f6\u5f00\u5374\u8fd8\u4e0d\u6b7b\u5fc3\uff0c\u53c8\u95ee\u9053\uff1a\u201c\u4f60\u600e\u4e48\u4f1a\u77e5\u9053\u591a\u5c14\u7532\u5c31\u662f\u5415\u8fea\uff1f\u4f60\u600e\u4e48\u627e\u5230\u4ed6\u7684\uff1f\u201d \u3000\u3000\u58a8\u4e5d\u661f\u5ffd\u7136\u7ffb\u4e86\u4e2a\u8eab\uff0c\u4ece\u7ef3\u5b50\u4e0a\u8df3\u4e0b\u6765\uff0c\u5927\u6b65\u8d70\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u53f6\u5f00\u5f53\u7136\u4e5f\u4ece\u540e\u9762\u8ddf\u7740\uff0c\u9053\uff1a\u201c\u4f60\u8981\u5230\u54ea\u91cc\u53bb\uff1f\u201d \u3000\u3000\u58a8\u4e5d\u661f\u9053\uff1a\u201c\u53bb\u6211\u6837\u4e1c\u897f\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u53bb\u627e\u4ec0\u4e48\uff1f\u60ac\u4e0d\u662f\u6211\u5e03\u8fbe\u62c9\uff1f\u4f60\u80fd\u627e\u5f97\u5230\u4ed6\uff1f\u201d \u3000\u3000\u58a8\u4e5d\u661f\u9053\uff1a\u201c\u6211\u6211\u7684\u4e1c\u897f\uff0c\u4f60\u82e5\u60f3\u8981\uff0c\u6211\u53ef\u4ee5\u5206\u4e00\u534a\u7ed9\u4f60\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u60f3\u5230\u54ea\u91cc\u53bb\u627e\uff1f\u201d \u3000\u3000\u58a8\u4e5d\u661f\u9053\uff1a\u201c\u5c31\u5728\u8fd9\u91cc\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u8fd9\u91cc\u6709\u4ec0\u4e48\u597d\u627e\u7684\uff1f\u201d \u3000\u3000\u58a8\u4e5d\u661f\u4e0d\u518d\u56de\u7b54\uff0c\u5374\u53c8\u4ece\u8eab\u4e0a\u62ff\u51fa\u4e2a\u6728\u74f6\uff0c\u74f6\u5b50\u91cc\u88c5\u7684\u4e5f\u662f\u7c89\u672b\uff0c\u5374\u662f\u9ec4\u8272\u7684\u3002 \u3000\u3000\u4ed6\u5c06\u74f6\u91cc\u7684\u7c89\u672a\u6d12\u5728\u5730\u4e0a\uff0c\u6d12\u6210\u4e2a\u5706\u5708\uff0c\u5374\u53c8\u7559\u4e0b\u4e2a\u7f3a\u53e3\uff0c\u7136\u540e\u4ed6\u5c31\u7ad9\u5728\u65c1\u8fb9\uff0c\u7b49\u7740\u3002 \u3000\u3000\u53f6\u5f00\u770b\u4e0d\u61c2\uff1a\u201c\u4f60\u8fd9\u662f\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u58a8\u4e5d\u661f\u9053\uff1a\u201c\u6211\u5728\u505a\u996d\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u505a\u996d\uff1f\u201d \u3000\u3000\u4ed6\u66f4\u4e0d\u61c2\u3002 \u3000\u3000\u58a8\u4e5d\u661f\u9053\uff1a\u201c\u6bcf\u4e2a\u4eba\u90fd\u662f\u5403\u996d\u7684\u4eba\uff0c\u6211\u4e5f\u662f\u4eba\u3002\u201d \u3000\u3000\u53f6\u5f00\u8fd8\u60f3\u518d\u95ee\uff0c\u5ffd\u7136\u770b\u89c1\u9662\u5b50\u91cc\u51fa\u73b0\u4e86\u4e00\u70b9\u706f\u5149\uff0c\u4e00\u4e2a\u7626\u7626\u957f\u957f\u7684\u548c\u5c1a\uff0c\u5de6\u624b\u63d0\u7740\u4e00\u76cf \u706f\u7b3c\uff0c\u53f3\u624b\u7aef\u7740\u4e2a\u6728\u76d8\uff0c\u4ece\u524d\u9762\u8d70\u4eba\u4e86\u9662\u5b50\uff0c\u8138\u4e0a\u8fd8\u5e26\u7740\u4e09\u5206\u6050\u60e7\uff0c\u4e09\u5206\u72b9\u7591\uff0c\u60f3\u8fc7\u6765\uff0c\u53c8 \u4e0d\u6562\u3002 \u3000\u3000\u8fd9\u548c\u5c1a\u6b63\u662f\u82e6\u7af9\u3002 \u3000\u3000\u58a8\u4e5d\u661f\u9053\uff1a\u201c\u4f60\u6765\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u82e6\u7af9\u9053\uff1a\u201c\u6211\u662f\u9001\u4e1c\u897f\u6765\u7684\u3002\u201d \u3000\u3000\u58a8\u4e5d\u661f\u9053\uff1a\u201c\u9001\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u82e6\u7af9\u4e3e\u4e86\u4e3e\u624b\u91cc\u7684\u6728\u76d8\uff0c\u9053\uff1a\u201c\u5c38\u8eab\u6211\u5df2\u6536\u6b93\uff0c\u8fd9\u662f\u6211\u4ece\u4ed6\u4eec\u8eab\u4e0a\u627e\u5230\u7684\u4e1c\u897f\uff0c\u5168\u90fd\u5728 \u8fd9\u91cc\u3002\u201d \u3000\u3000\u58a8\u4e5d\u661f\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u8fd9\u548c\u5c1a\u5012\u8fd8\u8001\u5b9e\u3002\u201d \u3000\u3000\u82e6\u7af9\u82e6\u7b11\u9053\uff1a\u201c\u548c\u5c1a\u6709\u65f6\u867d\u7136\u4e5f\u8d2a\u8d22\uff0c\u5374\u8fd8\u4e0d\u81f3\u4e8e\u541e\u6ca1\u6b7b\u4eba\u8eab\u4e0a\u7684\u4e1c\u897f\u3002\u201d \u3000\u3000\u4ed6\u8d70\u8fc7\u6765\uff0c\u653e\u4e0b\u6728\u76d8\uff0c\u7acb\u523b\u5c31\u6e9c\u4e86\u3002 \u3000\u3000\u548c\u5c1a\u603b\u662f\u6015\u9ebb\u70e6\u7684\uff0c\u66f4\u4e0d\u60f3\u591a\u7ba1\u95f2\u4e8b\u3002 \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u770b\u6765\u4e00\u4e2a\u4eba\u53ea\u8981\u505a\u4e86\u548c\u5c1a\uff0c\u60f3\u4e0d\u8001\u5b9e\u4e5f\u4e0d\u884c\u4e86\u3002\u201d \u3000\u3000\u58a8\u4e5d\u661f\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u4e5f\u5e94\u8be5\u53bb\u505a\u548c\u5c1a\uff0c\u505a\u4e86\u548c\u5c1a\uff0c\u4f60\u81f3\u5c11\u53ef\u4ee5\u6d3b\u5f97\u4e45\u4e9b\u3002\u201d \u3000\u3000\u76d8\u5b50\u91cc\u6709\u4e94\u67c4\u5f2f\u5200\uff0c\u4e00\u5757\u7389\u724c\uff0c\u4e03\u516b\u9897\u73cd\u73e0\uff0c\u8fd8\u6709\u5c01\u5f00\u4e86\u53e3\u7684\u4fe1\u3002 \u3000\u3000\u7389\u724c\u4e0a\u523b\u7740\u7684\u679c\u7136\u662f\u6839\u6743\u6756\uff0c\u9b54\u6559\u4e2d\u7684\u5927\u65e0\u738b\uff0c\u6bcf\u4e2a\u4eba\u8eab\u4e0a\u597d\u50cf\u90fd\u6709\u5757\u8fd9\u6837\u7684\u7389\u724c\u7684\u3002 \u3000\u3000\u8fd9\u5e76\u4e0d\u5947\u602a\uff0c\u5947\u602a\u7684\u662f\u8fd9\u5c01\u4fe1\u3002 \u3000\u3000\u4fe1\u662f\u7528\u8840\u5199\u7684\uff0c\u53ea\u6709\u5341\u51e0\u4e2a\u5b57\uff1a\u201c\u521d\u4e09\u4e0b\u5348\u5165\u957f\u5b89\uff0c\u4f1a\u4e8e\u5ef6\u5e73\u95e8\uff0c\u8bf7\u76f8\u4fe1\u3002\u201d \u3000\u3000\u4e0b\u9762\u6ca1\u6709\u5177\u540d\uff0c\u5374\u753b\u4e86\u5ea7\u5c71\u5cf0\u3002 \u3000\u3000\u5b64\u5cf0\u3002 \u3000\u3000\u53f6\u5f00\u957f\u957f\u5410\u51fa\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u8fd9\u4e00\u5b9a\u662f\u5b64\u5cf0\u5199\u7ed9\u591a\u5c14\u7532\u7684\uff0c\u8981\u591a\u5c14\u7532\u5728\u5ef6\u5e73\u95e8\u7b49\u4ed6\u3002\u201d \u3000\u3000\u58a8\u4e5d\u661f\u9053\uff1a\u201c\u521d\u4e09\u5c31\u662f\u660e\u5929\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u660e\u5929\u4ed6\u771f\u7684\u4f1a\u6765\uff1f\u201d \u3000\u3000\u58a8\u4e5d\u661f\u9053\uff1a\u201c\u5f53\u7136\u4f1a\u6765\uff0c\u4ed6\u5e76\u4e0d\u77e5\u9053\u591a\u5c14\u7532\u5df2\u662f\u4e2a\u6b7b\u4eba\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u73b0\u5728\u4ed6\u5728\u4ec0\u4e48\u5730\u65b9\uff1f\u90a3\u5730\u65b9\u96be\u9053\u6ca1\u6709\u7b14\u58a8\uff1f\u4ed6\u4e3a\u4ec0\u4e48\u8981\u7528\u8840\u6765\u5199\u4fe1\uff1f\u201d \u3000\u3000\u58a8\u4e5d\u661f\u9053\uff1a\u201c\u8840\u4e66\u901a\u5e38\u53ea\u6709\u4e24\u79cd\u610f\u601d\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u54ea\u4e24\u79cd\uff1f\u201d \u3000\u3000\u58a8\u4e5d\u661f\u9053\uff1a\u201c\u4e00\u79cd\u662f\u4e34\u5371\u65f6\u7684\u7edd\u7b14\uff0c\u4e00\u79cd\u662f\u8868\u793a\u60c5\u51b5\u7684\u5371\u6025\u4e25\u91cd\u3002\u201d \u3000\u3000\u53f6\u5f00\u5ffd\u7136\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4e5f\u8bb8\u8fd9\u53ea\u4e0d\u8fc7\u56e0\u4e3a\u4ed6\u5df2\u53d7\u4e86\u4f24\uff0c\u672c\u5c31\u6709\u8840\u8981\u6d41\u51fa\u6765\u3002\u201d \u3000\u3000\u58a8\u4e5d\u661f\u9053\uff1a\u201c\u9b54\u6559\u4e2d\u4eba\u5199\u8840\u4e66\uff0c\u901a\u5e38\u90fd\u4e0d\u662f\u7528\u81ea\u5df1\u7684\u8840\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u8ba4\u4e3a\u8fd9\u5c01\u4fe1\u662f\u771f\u7684\uff1f\u201d \u3000\u3000\u58a8\u4e5d\u661f\u9053\uff1a\u201c\u7edd\u5bf9\u4e0d\u5047\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u600e\u4e48\u80fd\u786e\u5b9a\uff1f\u201d \u3000\u3000\u58a8\u4e5d\u661f\u53c8\u95ed\u4e0a\u4e86\u5634\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u7af9\u6797\u91cc\u5ffd\u7136\u54cd\u8d77\u4e86\u4e00\u9635\u5947\u5f02\u7684\u58f0\u97f3\uff0c\u4e00\u79cd\u65e0\u6cd5\u5f62\u5bb9\u3001\u4e0d\u53ef\u601d\u8bae\u7684\u58f0\u97f3\u3002 \u3000\u3000\u65e0\u8bba\u8c01\u542c\u89c1\u8fd9\u79cd\u58f0\u97f3\uff0c\u90fd\u4e00\u5b9a\u6bdb\u9aa8\u60ca\u7136\uff0c\u751a\u81f3\u4f1a\u5fcd\u4e0d\u4f4f\u5455\u5410\u3002 \u3000\u3000\u53f6\u5f00\u770b\u89c1\u7684\u4e8b\uff0c\u5374\u6bd4\u8fd9\u58f0\u97f3\u66f4\u53ef\u6015\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u770b\u89c1\uff0c\u4e5f\u4e0d\u77e5\u6709\u591a\u5c11\u6761\u5927\u5927\u5c0f\u5c0f\u7684\u6bd2\u86c7\u3001\u58c1\u864e\u3001\u8708\u86a3\u8815\u52a8\u7740\uff0c\u4ece\u7af9\u6797\u91cc\u722c\u4e86\u51fa \u6765\uff0c\u722c\u5165\u58a8\u4e5d\u661f\u7528\u7c89\u672a\u6d12\u6210\u7684\u5706\u5708\u3002 \u3000\u3000\u53f6\u5f00\u53ea\u89c9\u5f97\u80c3\u5728\u6536\u7f29\uff0c\u52c9\u5f3a\u5fcd\u8010\u4f4f\uff0c\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u4f60\u7684\u665a\u996d\uff1f\u201d \u3000\u3000\u58a8\u4e5d\u661f\u70b9\u70b9\u5934\uff0c\u5583\u5583\u9053\uff1a\u201c\u6211\u4e00\u4e2a\u4eba\u5403\u5df2\u591f\u4e86\uff0c\u4e24\u4e2a\u4eba\u5403\u5c31\u8fd8\u5c11\u4e86\u4e9b\u3002\u201d \u3000\u3000\u53f6\u5f00\u9a87\u7136\u9053\uff1a\u201c\u4e24\u4e2a\u4eba\u5403\uff1f\u8fd8\u6709\u8c01\u8981\u6765\uff1f\u201d \u3000\u3000\u58a8\u4e5d\u661f\u6de1\u6de1\u9053\uff1a\u201c\u6ca1\u6709\u522b\u4eba\u4e86\uff0c\u6211\u4e00\u5411\u5f88\u5c11\u8bf7\u5ba2\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u73b0\u5728\u4f60\u53ea\u6709\u4e00\u4e2a\u4eba\u3002\u201d \u3000\u3000\u58a8\u4e5d\u661f\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u4eba\uff1f\u201d \u3000\u3000\u65f6\u5f00\u5012\u62bd\u4e86\u53e3\u51c9\u6c14\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u8fd9\u4e48\u597d\u7684\u4e1c\u897f\uff0c\u8fd8\u662f\u7559\u7ed9\u4f60\u4e00\u4e2a\u4eba\u4eab\u53d7\u5427\uff0c\u6211\u4e0d\u6562\u5949 \u966a\u3002\u201d \u3000\u3000\u58a8\u4e5d\u661f\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u4e0d\u80af\u8d4f\u5149\uff1f\u201d\u3002 \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u8fd8\u6709\u7ea6\u4f1a\uff0c\u6211\u8981\u5230\u5916\u9762\u53bb\u5403\u996d\uff0c\u5403\u5b8c\u4e86\u6211\u5c31\u56de\u6765\u3002\u201d \u3000\u3000\u8bdd\u8fd8\u6ca1\u6709\u8bf4\u5b8c\uff0c\u4ed6\u5df2\u6e9c\u4e4b\u5927\u8a00\u3002 \u3000\u3000\u4ed6\u8fd9\u4e00\u751f\uff0c\u4ece\u6765\u4e5f\u6ca1\u6709\u88ab\u4eba\u9a87\u5f97\u9003\u8d70\u8fc7\uff0c\u53ef\u662f\u73b0\u5728\u5374\u9003\u5f97\u6bd4\u4e00\u53ea\u4e2d\u4e86\u7bad\u7684\u5154\u5b50\u8fd8\u5feb\u3002 \u3000\u3000\u58a8\u4e5d\u661f\u5ffd\u7136\u5927\u7b11\u9053\uff1a\u201c\u4f60\u82e5\u5728\u5916\u9762\u5403\u4e0d\u9971\uff0c\u4e0d\u59a8\u518d\u56de\u6765\u5403\u70b9\u5fc3\uff0c\u6211\u53ef\u4ee5\u7559\u4e24\u6761\u6700\u80a5\u7684\u8708 \u86a3\u7ed9\u4f60\u3002\u201d \u3000\u3000\u53f6\u5f00\u5df2\u8d8a\u5899\u800c\u51fa\uff0c\u8fde\u5934\u90fd\u4e0d\u6562\u56de\u3002 \u3000\u3000\u8fd9\u662f\u4ed6\u7b2c\u4e00\u6b21\u542c\u89c1\u58a8\u4e5d\u661f\u7684\u7b11\u58f0\uff0c\u4e5f\u662f\u6700\u540e\u4e00\u6b21\u3002 \u3000\u3000\u8fd9\u996d\u94fa\u5f88\u5c0f\uff0c\u5374\u5f88\u5e72\u51c0\u3002 \u3000\u3000\u73b0\u5728\u5df2\u8fc7\u4e86\u5403\u996d\u7684\u65f6\u5019\uff0c\u9664\u4e86\u4ed6\u4e4b\u5916\uff0c\u996d\u94fa\u91cc\u5df2\u6ca1\u6709\u522b\u7684\u5ba2\u4eba\u3002 \u3000\u3000\u53f6\u5f00\u8981\u4e86\u4e24\u6837\u83dc\uff0c\u4e00\u58f6\u9152\u3002 \u3000\u3000\u4ed6\u672c\u4e0d\u60f3\u559d\u9152\u7684\u3002 \u3000\u3000\u9152\u5165\u6101\u80a0\uff0c\u5316\u505a\u76f8\u601d\u6cea\u3002 \u3000\u3000\u4e5f\u8bb8\u53ea\u8981\u4e00\u676f\u9152\uff0c\u5c31\u80fd\u52fe\u8d77\u4ed6\u7684\u4f24\u5fc3\u4e8b\u3002 \u3000\u3000\u73b0\u5728\u4e0d\u662f\u4f24\u5fc3\u7684\u65f6\u5019\uff0c\u4ed6\u5c31\u7b97\u8981\u4f24\u5fc3\uff0c\u4e5f\u5f97\u7b49\u5230\u8fd9\u4ef6\u4e8b\u8fc7\u53bb\u4ee5\u540e\u3002 \u3000\u3000\u53ea\u53ef\u60dc\u4e00\u4e2a\u4eba\u8d8a\u662f\u60f3\u52c9\u5f3a\u63a7\u5236\u81ea\u5df1\u4e0d\u559d\u9152\u7684\u65f6\u5019\uff0c\u53cd\u800c\u5fcd\u4e0d\u4f4f\u8981\u53bb\u559d\u4e24\u676f\u7684\u3002 \u3000\u3000\u201c\u6211\u53ea\u559d\u4e24\u676f\u3002\u201d \u3000\u3000\u4ed6\u5728\u5fc3\u91cc\u8b66\u544a\u81ea\u5df1\uff0c\u7edd\u4e0d\u80fd\u591a\u559d\uff0c\u591c\u8fd8\u5f88\u957f\uff0c\u660e\u5929\u4e00\u5b9a\u662f\u975e\u5e38\u8270\u82e6\u7684\u4e00\u5929\uff0c\u53ef\u662f\u4e24\u676f\u9152 \u559d\u4e0b\u53bb\u4ee5\u540e\uff0c\u4ed6\u89c9\u5f97\u4e16\u754c\u4e0a\u6709\u5f88\u591a\u4e8b\u90fd\u6ca1\u6709\u521a\u624d\u60f3\u7684\u90a3\u4e48\u4e25\u91cd\u4e86\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u53c8\u559d\u4e86\u4e24\u676f\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u60f3\u8d77\u4e86\u4e86\u7075\u7433\u82e5\u662f\u5728\u8fd9\u91cc\uff0c\u4e00\u5b9a\u4e5f\u4f1a\u966a\u4ed6\u559d\u4e24\u676f\u7684\u3002 \u3000\u3000\u4ed6\u4eec\u5e38\u5e38\u5750\u5728\u8fd9\u79cd\u5c0f\u5e97\u91cc\uff0c\u559d\u4e24\u676f\u9152\uff0c\u5265\u51e0\u9897\u82b1\u751f\uff0c\u8fc7\u4e00\u4e2a\u5e73\u9759\u7684\u665a\u4e0a\u3002 \u3000\u3000\u5f53\u65f6\u4ed6\u603b\u662f\u89c9\u5f97\u8fd9\u79cd\u751f\u6d3b\u592a\u5355\u8c03\uff0c\u592a\u5e73\u9759\uff0c\u53ef\u662f\u73b0\u5728\u4ed6\u5df2\u77e5\u9053\u81ea\u5df1\u9519\u4e86\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u624d\u77e5\u9053\uff0c\u5e73\u9759\u5c31\u662f\u5e78\u798f\u3002 \u3000\u3000\u2014\u2014\u4eba\u4eec\u4e3a\u4ec0\u4e48\u603b\u662f\u8981\u7b49\u5230\u5e78\u798f\u5df2\u5931\u53bb\u4e86\u65f6\uff0c\u624d\u80fd\u771f\u6b63\u660e\u767d\u5e78\u798f\u662f\u4ec0\u4e48\uff1f \u3000\u3000\u98ce\u5f88\u51b7\uff0c\u5f88\u51b7\u3002 \u3000\u3000\u591c\u4e5f\u5f88\u51b7\u3002 \u3000\u3000\u5728\u5982\u6b64\u5bd2\u51b7\u7684\u51ac\u591c\u91cc\uff0c\u4e00\u4e2a\u5bc2\u5bde\u7684\u6d6a\u5b50\uff0c\u53c8\u600e\u4e48\u80fd\u4e0d\u5fc3\u9178\uff1f \u3000\u3000\u5bc2\u5bde\uff0c\u5200\u4e00\u6837\u7684\u5bc2\u5bde\u3002 \u3000\u3000\u5bf9\u4e00\u4e2a\u5e78\u798f\u7684\u4eba\u8bf4\u6765\uff0c\u5bc2\u5bde\u5e76\u4e0d\u53ef\u6015\uff0c\u6709\u65f6\u751a\u81f3\u53cd\u800c\u662f\u79cd\u4eab\u53d7\u3002 \u3000\u3000\u53ef\u662f\u7b49\u5230\u4ed6\u7684\u5e78\u798f\u5df2\u5931\u53bb\u65f6\uff0c\u4ed6\u5c31\u4f1a\u4e86\u89e3\u5bc2\u5bde\u662f\u4ef6\u591a\u4e48\u53ef\u6015\u7684\u4e8b\u4e86\u3002 \u3000\u3000\u6709\u65f6\u90a3\u751a\u81f3\u6bd4\u5200\u950b\u66f4\u5c16\u9510\uff0c\u4e00\u4e0b\u5b50\u5c31\u80fd\u523a\u5165\u4f60\u7684\u5fc3\u5e95\u6df1\u5165\u3002 \u3000\u3000\u53f6\u5f00\u7684\u5fc3\u5728\u523a\u75db\u3002 \u3000\u3000\u82e5\u4e0d\u662f\u5916\u9762\u7a81\u7136\u4f20\u6765\u4e00\u58f0\u60e8\u547c\uff0c\u4ed6\u4e00\u5b9a\u4f1a\u5fc3\u9178\u7684\u3002 \u3000\u3000\u4ed6\u5df2\u65e0\u6cd5\u63a7\u5236\u81ea\u5df1\u3002 \u3000\u3000\u53ef\u662f\u5c31\u5728\u4ed6\u7b2c\u4e03\u6b21\u4e3e\u676f\u7684\u65f6\u5019\uff0c\u5bd2\u98ce\u4e2d\u5ffd\u7136\u4f20\u6765\u4e00\u58f0\u60e8\u547c\u3002 \u3000\u3000\u547c\u58f0\u662f\u4ece\u5341\u65b9\u7af9\u6797\u5bfa\u90a3\u5904\u4f20\u6765\u7684\u3002 \u3000\u3000\u8fd9\u5c0f\u5e97\u94fa\u5c31\u5728\u7af9\u6797\u5bfa\u540e\u3002 \u3000\u3000\u60e8\u547c\u58f0\u54cd\u8d77\uff0c\u4ed6\u4eba\u5df2\u7bad\u4e00\u822c\u7a9c\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u7136\u540e\u4ed6\u5c31\u770b\u89c1\u4e86\u4e24\u4e2a\u4eba\u3002 \u3000\u3000\u4e24\u4e2a\u6b7b\u4eba\uff0c\u50cf\u9ebb\u888b\u822c\u642d\u5728\u7985\u9662\u5916\u7684\u77ed\u5899\u4e0a\uff0c\u7ee3\u82b1\u957f\u888d\uff0c\u9752\u94dc\u9762\u5177\uff0c\u6b63\u662f\u591a\u5c14\u7532\u7684\u8eab\u5916\u5316 \u8eab\u3002 \u3000\u3000\u53f6\u5f00\u677e\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u4ed6\u5e76\u4e0d\u662f\u4e2a\u6ca1\u6709\u540c\u60c5\u5fc3\u7684\u4eba\uff0c\u53ef\u662f\u5bf9\u8fd9\u4e24\u4e2a\u4eba\u7684\u6b7b\uff0c\u4ed6\u5b9e\u5728\u5e76\u4e0d\u592a\u540c\u60c5\u3002 \u3000\u3000\u4ed6\u4eec\u65e2\u7136\u5df2\u8d70\u4e86\uff0c\u4e3a\u4ec0\u4e48\u8fd8\u8981\u56de\u6765\u9001\u6b7b\uff1f \u3000\u3000\u4ed6\u4eec\u65e2\u7136\u8981\u56de\u6765\uff0c\u58a8\u4e5d\u661f\u5f53\u7136\u5c31\u4e0d\u4f1a\u8ba9\u4ed6\u4eec\u518d\u6d3b\u7740\u8d70\u51fa\u53bb\u3002 \u3000\u3000\u8fd9\u4e5f\u4e0d\u503c\u5f97\u5403\u60ca\u3002 \u3000\u3000\u53f6\u5f00\u53ea\u4e0d\u8fc7\u53f9\u4e86\u53e3\u6c14\u800c\u5df2\uff0c\u7b49\u5230\u4ed6\u770b\u89c1\u58a8\u4e5d\u661f\u65f6\uff0c\u624d\u771f\u7684\u5403\u4e86\u4e00\u60ca\u3002 \u3000\u3000\u4ed6\u5b9e\u5728\u60f3\u4e0d\u5230\u58a8\u4e5d\u661f\u7adf\u4e5f\u5df2\u662f\u4e2a\u6b7b\u4eba\u3002 \u3000\u3000\u9662\u5b50\u91cc\u8fd8\u662f\u6ca1\u71c3\u706f\u3002 \u3000\u3000\u58a8\u4e5d\u661f\u5c31\u5012\u5728\u9662\u5b50\u91cc\uff0c\u6574\u4e2a\u4eba\u90fd\u626d\u66f2\u6536\u7f29\uff0c\u5c31\u50cf\u662f\u4e2a\u7f29\u4e86\u6c34\u7684\u5e03\u5a03\u5a03\u3002 \u3000\u3000\u53f6\u5f00\u6014\u4f4f\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u5899\u5934\u4e0a\u7684\u4e24\u4e2a\u4eba\u662f\u6b7b\u5728\u58a8\u4e5d\u661f\u624b\u91cc\u7684\uff0c\u4f46\u4ed6\u5374\u60f3\u4e0d\u51fa\u58a8\u4e5d\u661f\u662f\u600e\u4e48\u6b7b\u7684\u3002 \u3000\u3000\u4ed6\u770b\u89c1\u8fc7\u58a8\u4e5d\u661f\u7684\u6b66\u529f\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u82e5\u5df2\u80fd\u5c06\u81ea\u5df1\u7684\u529f\u529b\u7ec3\u5f97\u6536\u653e\u81ea\u5982\uff0c\u522b\u4eba\u8981\u6740\u4ed6\uff0c\u5c31\u5f88\u4e0d\u5bb9\u6613\u3002 \u3000\u3000\u4f55\u51b5\u58a8\u4e5d\u661f\u7684\u6c89\u7740\u548c\u51b7\u9759\uff0c\u4e5f\u662f\u5f88\u5c11\u6709\u4eba\u80fd\u6bd4\u5f97\u4e0a\u7684\u3002 \u3000\u3000\u662f\u8c01\u6740\u4e86\u4ed6\uff0c\u6709\u8c01\u80fd\u6740\u4ed6\uff1f \u3000\u3000\u53f6\u5f00\u4fef\u4e0b\u8eab\u3002 \u3000\u3000\u8349\u5e3d\u8fd8\u5728\u58a8\u4e5d\u661f\u5934\u4e0a\uff0c\u53ef\u662f\u73b0\u5728\u4ed6\u5df2\u4e0d\u80fd\u518d\u62d2\u7edd\u522b\u4eba\u6458\u4e0b\u6765\u3002 \u3000\u3000\u53f6\u5f00\u6458\u4e0b\u8fd9\u9876\u8349\u5e3d\uff0c\u5c31\u770b\u89c1\u4e86\u4e00\u5f20\u60e8\u78a7\u8272\u7684\u3001\u5df2\u626d\u66f2\u53d8\u5f62\u7684\u8138\u3002 \u3000\u3000\u4ed6\u662f\u4e2d\u6bd2\u800c\u6b7b\u7684\u3002 \u3000\u3000\u662f\u8c01\u4e0b\u7684\u6bd2\uff1f \u3000\u3000\u53f6\u5f00\u52a8\u4e5f\u4e0d\u52a8\u5730\u7ad9\u7740\uff0c\u5200\u950b\u822c\u7684\u51b7\u98ce\u4e00\u9635\u9635\u523a\u5728\u4ed6\u8138\u4e0a\u3002 \u3000\u3000\u4ed6\u7ec8\u4e8e\u660e\u767d\u58a8\u4e5d\u661f\u662f\u600e\u4e48\u6b7b\u7684\u4e86\u3002 \u3000\u3000\u4f46\u4ed6\u5374\u8fd8\u662f\u4e0d\u660e\u767d\uff0c\u58a8\u4e5d\u661f\u4e3a\u4ec0\u4e48\u603b\u662f\u8981\u5c06\u8fd9\u9876\u8349\u5e3d\u6234\u5728\u5934\u4e0a\u3002 \u3000\u3000\u8fd9\u9876\u8349\u5e3d\u6ca1\u6709\u7279\u522b\u7684\u5730\u65b9\u3002 \u3000\u3000\u58a8\u4e5d\u661f\u7684\u8138\u4e0a\uff0c\u4e5f\u5e76\u6ca1\u6709\u4ec0\u4e48\u5730\u65b9\u662f\u53f6\u5f00\u770b\u4e0d\u5f97\u7684\u3002 \u3000\u3000\u9664\u4e86\u8138\u4e0a\u7684\u5bd2\u661f\u5916\uff0c\u4ed6\u4e5f\u662f\u4e2a\u5f88\u5e73\u51e1\u7684\u4eba\uff0c\u53ea\u4e0d\u8fc7\u6bd4\u53f6\u5f00\u60f3\u8c61\u4e2d\u82cd\u8001\u4e9b\u3002 \u3000\u3000\u4e00\u4e2a\u5f88\u5e73\u51e1\u7684\u4eba\uff0c\u4e00\u9876\u5f88\u5e73\u51e1\u7684\u8349\u5e3d\uff0c\u8fd9\u5176\u4e2d\u96be\u9053\u8fd8\u6709\u4ec0\u4e48\u4e0d\u5e73\u51e1\u7684\u79d8\u5bc6\uff1f \u3000\u3000\u53f6\u5f00\u6162\u6162\u5730\u653e\u4e0b\u8349\u5e3d\uff0c\u76d6\u4f4f\u4e86\u58a8\u4e5d\u661f\u7684\u8138\uff0c\u82e6\u7b11\u7740\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u4e5f\u50cf\u522b\u4eba\u4e00\u6837\u5403\u725b \u8089\u5462\uff1f\u81f3\u5c11\u725b\u8089\u603b\u662f\u6bd2\u4e0d\u6b7b\u4eba\u7684\u3002\u201d \u3000\u3000\u58a8\u4e5d\u661f\u7684\u5c38\u8eab\u4e5f\u5df2\u6536\u6b93\u3002 \u3000\u3000\u82e6\u7af9\u53cc\u638c\u5408\u5341\uff0c\u53f9\u606f\u7740\u9053\uff1a\u201c\u5929\u6709\u4e0d\u6d4b\u98ce\u4e91\uff0c\u4eba\u6709\u65e6\u5915\u7978\u798f\u3002\u6211\u4f5b\u6148\u60b2\u963f\u5f25\u9640\u4f5b\u3002\u201d \u3000\u3000\u4ed6\u5634\u91cc\u867d\u7136\u5728\u5ff5\u7740\u4f5b\u53f7\uff0c\u8138\u4e0a\u5374\u8fde\u4e00\u70b9\u60b2\u4f24\u7684\u6837\u5b50\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u5bf9\u58a8\u4e5d\u661f\u7684\u6b7b\uff0c\u4ed6\u663e\u7136\u4e5f\u5e76\u4e0d\u5927\u540c\u60c5\u3002 \u3000\u3000\u53f6\u5f00\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u51fa\u5bb6\u4eba\u4e0d\u8be5\u5e78\u707e\u4e50\u7978\u7684\u3002\u201d \u3000\u3000\u82e6\u7af9\u9053\uff1a\u201c\u8c01\u5e78\u707e\u4e50\u7978\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u3002\u201d \u3000\u3000\u82e6\u7af9\u82e6\u7b11\u9053\uff1a\u201c\u4eba\u5e94\u8be5\u6709\u597d\u751f\u4e4b\u5fb7\uff0c\u53ef\u662f\uff0c\u4ed6\u6b7b\u4e86\u6211\u7684\u786e\u4e0d\u592a\u96be\u53d7\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u8fd9\u548c\u5c1a\u867d\u7136\u591a\u8bdd\uff0c\u8bf4\u7684\u5012\u597d\u50cf\u90fd\u662f\u8001\u5b9e\u8bdd\u3002\u201d \u3000\u3000\u82e6\u7af9\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8001\u5b9e\u8bf4\uff0c\u82e5\u4e0d\u662f\u56e0\u4e3a\u6211\u6709\u591a\u8bdd\u7684\u6bdb\u75c5\uff0c\u73b0\u5728\u6211\u65e9\u5df2\u5f53\u4e86\u5927\u76f8\u56fd\u5bfa \u7684\u4e3b\u6301\u3002\u201d \u3000\u3000\u53f6\u5f00\u7b11\u4e86\uff0c\u4ed6\u89c9\u5f97\u8fd9\u548c\u5c1a\u975e\u4f46\u4e0d\u4fd7\uff0c\u800c\u4e14\u5f88\u6709\u8da3\u3002 \u3000\u3000\u82e6\u7af9\u53c8\u5f00\u59cb\u5728\u5ff5\u7ecf\uff0c\u8d85\u5ea6\u58a8\u4e5d\u661f\u7684\u4ea1\u9b42\u3002 \u3000\u3000\u53f6\u5f00\u5fcd\u4e0d\u4f4f\u53c8\u6253\u65ad\u4e86\u4ed6\u7684\u7ecf\u6587\uff0c\u9053\uff1a\u201c\u8fd9\u91cc\u505a\u6cd5\u4e8b\u7684\u53ea\u6709\u4f60\u4e00\u4e2a\u4eba\uff1f\u201d \u3000\u3000\u82e6\u7af9\u9053\uff1a\u201c\u522b\u7684\u548c\u5c1a\u90fd\u5df2\u7761\u7740\uff0c\u8fd9\u867d\u7136\u662f\u4e2a\u5e99\uff0c\u53ef\u662f\u5230\u8fd9\u91cc\u6765\u505a\u6cd5\u4e8b\u7684\u4eba\u5e76\u4e0d\u591a\uff0c\u5230\u8fd9 \u91cc\u6765\u7684\u65bd\u4e3b\u4eec\uff0c\u5927\u591a\u6570\u90fd\u662f\u4e3a\u4e86\u5403\u7d20\u658b\uff0c\u770b\u98ce\u666f\u7684\u3002\u201d \u3000\u3000\u4ed6\u53f9\u606f\u7740\u53c8\u9053\uff1a\u201c\u8001\u5b9e\u8bf4\uff0c\u8fd9\u4e2a\u5e99\u7b80\u76f4\u5c31\u8ddf\u996d\u9986\u5ba2\u6808\u5dee\u4e0d\u591a\u3002\u201d \u3000\u3000\u8fd9\u7684\u786e\u53c8\u662f\u8001\u5b9e\u8bdd\u3002 \u3000\u3000\u53f6\u5f00\u53c8\u7b11\u4e86\u7b11\uff0c\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u4ed6\u662f\u600e\u4e48\u6b7b\u7684\uff1f\u201d \u3000\u3000\u82e6\u7af9\u6447\u5934\u3002 \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u5c31\u662f\u56e0\u4e3a\u4f60\u592a\u591a\u8bdd\uff0c\u6240\u4ee5\u4ed6\u624d\u4f1a\u6b7b\u3002\u201d \u3000\u3000\u82e6\u7af9\u8138\u8272\u53d8\u4e86\u53d8\uff0c\u52c9\u5f3a\u7b11\u9053\uff1a\u201c\u65bd\u4e3b\u4e00\u5b9a\u662f\u5728\u5f00\u73a9\u7b11\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u4ece\u4e0d\u5728\u6b7b\u4eba\u9762\u524d\u5f00\u73a9\u7b11\u3002\u201d \u3000\u3000\u82e6\u7af9\u9053\uff1a\u201c\u65bd\u4e3b\u96be\u9053\u8fd8\u770b\u4e0d\u51fa\u4ed6\u662f\u88ab\u6bd2\u6b7b\u7684\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u770b\u5f97\u51fa\uff1f\u201d \u3000\u3000\u82e6\u7af9\u9053\uff1a\u201c\u8fd9\u91cc\u7684\u86c7\u4eba\u591a\u6570\u90fd\u6709\u6bd2\uff0c\u6709\u6bd2\u7684\u6bd2\u86c7\u4e5f\u6bd2\u4e0d\u6b7b\u4ed6\u3002\u201d \u3000\u3000\u4ed6\u53c8\u9053\uff1a\u201c\u53ef\u662f\u9664\u4e86\u4ed6\u81ea\u5df1\u6293\u7684\u90a3\u4e9b\u6bd2\u866b\u5916\uff0c\u4ed6\u5e76\u6ca1\u6709\u5403\u522b\u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u90a3\u4e9b\u6bd2\u866b\u65e2\u7136\u662f\u4ed6\u81ea\u5df1\u6293\u7684\uff0c\u600e\u4e48\u80fd\u6bd2\u5f97\u6b7b\u4ed6\uff1f\u201d \u3000\u3000\u82e6\u7af9\u6014\u4e86\u6014\uff0c\u5583\u5583\u9053\uff1a\u201c\u770b\u6765\u8fd9\u4ef6\u4e8b\u5012\u7684\u786e\u6709\u70b9\u53e4\u602a\u3002\u201d \u3000\u3000\u53f6\u5f00\u5374\u53c8\u7b11\u9053\uff1a\u201c\u5176\u5b9e\u8fd9\u4ef6\u4e8b\u5e76\u4e0d\u53e4\u602a\u3002\u201d \u3000\u3000\u82e6\u7af9\u4e0d\u61c2\u3002 \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4ed6\u7684\u786e\u662f\u88ab\u90a3\u4e9b\u6bd2\u866b\u6bd2\u6b7b\u7684\uff0c\u53ea\u56e0\u4e3a\u90a3\u4e9b\u6bd2\u866b\u8eab\u4e0a\uff0c\u53c8\u88ab\u4eba\u4e0b\u4e86\u79cd\u4ed6\u53d7\u4e0d\u4e86 \u7684\u6bd2\u3002\u201d \u3000\u3000\u82e6\u7af9\u9053\uff1a\u201c\u662f\u8c01\u4e0b\u7684\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6b7b\u5728\u5899\u5934\u4e0a\u7684\u90a3\u4e24\u4e2a\u4eba\u3002\u201d \u3000\u3000\u82e6\u7af9\u677e\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8fd9\u8ddf\u6211\u591a\u8bdd\u53c8\u6709\u4ec0\u4e48\u5173\u7cfb\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6709\u5173\u7cfb\u3002\u201d \u3000\u3000\u82e6\u7af9\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u82e5\u4e0d\u662f\u4f60\u591a\u8bdd\uff0c\u522b\u4eba\u600e\u4e48\u4f1a\u77e5\u9053\u4ed6\u5403\u7684\u662f\u4e94\u6bd2\uff1f\u201d \u3000\u3000\u2014\u2014\u522b\u4eba\u82e5\u4e0d\u77e5\u9053\u4ed6\u5403\u7684\u662f\u4e94\u6bd2\uff0c\u53c8\u600e\u4e48\u4f1a\u5728\u90a3\u4e9b\u6bd2\u866b\u8eab\u4e0a\u4e0b\u6bd2\uff1f\u82e6\u7af9\u8bf4\u4e0d\u51fa\u8bdd\u6765\u4e86\u3002 \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4e0b\u6bd2\u7684\u4eba\u60f3\u770b\u770b\u4ed6\u662f\u4e0d\u662f\u5df2\u7ecf\u88ab\u6bd2\u6b7b\uff0c\u60f3\u4e0d\u5230\u4ed6\u4e34\u6b7b\u4e4b\u524d\uff0c\u8fd8\u80fd\u628a\u4ed6\u4eec\u6740\u4e86 \u62a5\u4ec7\u3002\u201d \u3000\u3000\u8fd9\u89e3\u91ca\u7684\u786e\u5408\u60c5\u5408\u7406\u3002 \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u50cf\u4ed6\u8fd9\u79cd\u4eba\uff0c\u65e0\u8bba\u8c01\u5bf9\u4ed6\u4e0d\u8d77\uff0c\u4ed6\u65e0\u8bba\u6b7b\u6d3b\uff0c\u90fd\u4e00\u5b9a\u4e0d\u4f1a\u653e\u8fc7\u7684\u3002\u201d \u3000\u3000\u82e6\u7af9\u5583\u5583\u9053\uff1a\u201c\u6d3b\u7740\u65f6\u662f\u51f6\u4eba\uff0c\u6b7b\u4e86\u4e5f\u4e00\u5b9a\u662f\u6076\u9b3c\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u5343\u4e07\u8981\u5c0f\u5fc3\u4e9b\u3002\u201d \u3000\u3000\u82e6\u7af9\u53d8\u8272\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u5c0f\u5fc3\u4ec0\u4e48\uff1f\u201d\u53f6\u5f00\u76ef\u7740\u4ed6\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u5c0f\u5fc3\u4ed6\u5ffd\u7136\u4ece\u68fa\u6750\u91cc\u8dd1 \u51fa\u6765\uff0c\u5272\u4e0b\u4f60\u7684\u820c\u5934\uff0c\u8ba9\u4f60\u4ee5\u540e\u518d\u6ca1\u6cd5\u5b50\u8bf4\u8bdd\u3002\u201d \u3000\u3000\u82e6\u7af9\u8138\u8272\u53d8\u5f97\u66f4\u96be\u770b\uff0c\u5ffd\u7136\u9053\uff1a\u201c\u6211\u7684\u5934\u75bc\u5f97\u5f88\uff0c\u6211\u4e5f\u8981\u53bb\u7761\u4e86\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u4e0d\u80fd\u8d70\u3002\u201d \u3000\u3000\u82e6\u7af9\u4eff\u4f5b\u53c8\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u82e5\u8d70\u4e86\u8c01\u6765\u8d85\u5ea6\u4ed6\u7684\u4ea1\u9b42\uff1f\u201d \u3000\u3000\u82e6\u7af9\u9053\uff1a\u201c\u4ed6\u7528\u4e0d\u7740\u522b\u4eba\u8d85\u5ea6\uff0c\u8fd9\u79cd\u4eba\u53cd\u6b63\u4e00\u5b9a\u8981\u4e0b\u5730\u72f1\u7684\u3002\u201d \u3000\u3000\u661f\u5149\u95ea\u70c1\u3002\u5927\u6bbf\u91cc\u5145\u6ee1\u4e86\u4e00\u79cd\u8bf4\u4e0d\u51fa\u7684\u9634\u68ee\u8be1\u79d8\u4e4b\u610f\uff0c\u9ed1\u6697\u4e2d\u4eff\u4f5b\u771f\u7684\u6709\u4e9b\u542b\u51a4\u800c\u6b7b\u7684 \u6076\u9b3c\uff0c\u5728\u7b49\u7740\u5272\u4eba\u7684\u820c\u5934\u3002\u82e6\u7af9\u7b80\u76f4\u8fde\u7247\u523b\u4e5f\u5446\u4e0d\u4e0b\u53bb\u4e86\uff0c\u8fde\u624b\u91cc\u6572\u6728\u9c7c\u7684\u68d2\u7cd9\u90fd\u6765\u4e0d\u53ca\u653e \u4e0b\uff0c\u6389\u5934\u5c31\u8d70\uff0c\u8d70\u8fc7\u95e8\u69db\u65f6\uff0c\u51e0\u4e4e\u88ab\u7eca\u4e86\u4e2a\u8ddf\u6597\u3002\u53f6\u5f00\u770b\u5230\u4ed6\u8d70\u51fa\u53bb\uff0c\u773c\u775b\u91cc\u5ffd\u7136\u9732\u51fa\u79cd\u5f88 \u5947\u602a\u7684\u8868\u60c5\uff0c\u51fa\u5bb6\u4eba\u672c\u4e0d\u8be5\u6015\u9b3c\u7684\uff0c\u9664\u975e\u4ed6\u505a\u4e86\u4e9b\u89c1\u4e0d\u5f97\u4eba\u7684\u4e8f\u5fc3\u4e8b\uff0c\u4ed6\u505a\u4e86\u4ec0\u4e48\u4e8f\u5fc3\u4e8b\uff1f \u4ed6\u771f\u7684\u6015\u9b3c\uff0c\u8fd8\u662f\u6015\u522b\u7684\uff1f \u3000\u3000\u4e94\u53e3\u5d2d\u65b0\u7684\u68fa\u6750\uff0c\u5e76\u6392\u6446\u5728\u6bbf\u91cc\u3002 \u3000\u3000\u53f6\u5f00\u8fd8\u6ca1\u6709\u8d70\uff0c\u4ed6\u4e0d\u6015\u9b3c\uff0c\u4ed6\u6ca1\u6709\u505a\u8fc7\u4e8f\u5fc3\u4e8b\u3002 \u3000\u3000\u4ed6\u7ad9\u5728\u51b7\u98ce\u4e2d\uff0c\u770b\u7740\u8fd9\u4e94\u53e3\u5d2d\u65b0\u7684\u68fa\u6750\uff0c\u5583\u5583\u9053\uff1a\u201c\u8fd9\u5e99\u91cc\u867d\u7136\u5f88\u5c11\u505a\u6cd5\u4e8b\uff0c\u51c6\u5907\u7684\u68fa \u6750\u5012\u4e0d\u5c11\uff0c\u96be\u9053\u8fd9\u91cc\u7684\u548c\u5c1a\u90fd\u80fd\u672a\u535c\u5148\u77e5\uff0c\u65e9\u5df2\u77e5\u9053\u4eca\u5929\u665a\u4e0a\u4f1a\u6b7b\u5f88\u591a\u4eba\uff1f\u201d \u3000\u3000\u4ed6\u8bf4\u7684\u58f0\u97f3\u5f88\u8f7b\uff0c\u56e0\u4e3a\u4ed6\u77e5\u9053\u8fd9\u4e9b\u95ee\u9898\u8c01\u4e5f\u4e0d\u80fd\u7b54\u590d\uff0c\u4ed6\u672c\u662f\u8bf4\u7ed9\u81ea\u5df1\u542c\u7684\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u82e6\u7af9\u5ffd\u7136\u53c8\u4ece\u5916\u9762\u51b2\u4e86\u8fdb\u6765\uff0c\u5f20\u5927\u4e86\u5634\uff0c\u4f38\u51fa\u4e86\u820c\u5934\uff0c\u4eff\u4f5b\u60f3\u53eb\uff0c\u5374\u53eb\u4e0d\u51fa \u58f0\u97f3\u6765\u3002 \u3000\u3000\u53f6\u5f00\u5ffd\u7136\u53d1\u73b0\u4ed6\u4e0d\u4f46\u8138\u8272\u53d8\u4e86\uff0c\u5934\u7684\u989c\u8272\u4e5f\u53d8\u4e86\uff0c\u53d8\u6210\u79cd\u53ef\u6015\u7684\u6b7b\u9ed1\u8272\uff0c\u4ed6\u6307\u7740\u81ea\u5df1\u7684 \u820c\u5934\u597d\u50cf\u8981\u5bf9\u53f6\u5f00\u8bf4\u4ec0\u4e48\uff0c\u5374\u53c8\u8bf4\u4e0d\u51fa\u3002 \u3000\u3000\u53f6\u5f00\u51b2\u8fc7\u53bb\uff0c\u624d\u53d1\u73b0\u4ed6\u820c\u5934\u4e0a\u6709\u4e24\u4e2a\u7259\u5370\uff0c\u7adf\u663e\u7136\u662f\u6bd2\u86c7\u7684\u7259\u5370\u3002 \u3000\u3000\u4ed6\u7684\u820c\u5934\u5728\u5634\u91cc\uff0c\u6bd2\u86c7\u600e\u4e48\u4f1a\u54ac\u5230\u4ed6\u7684\u820c\u5934\u4e0a\u53bb\u7684\uff0c\u83ab\u975e\u8fd9\u91cc\u771f\u6709\u6076\u9b3c\u8981\u5c01\u4f4f\u4ed6\u7684\u5634\uff1f \u3000\u3000\u82e6\u7af9\u5ffd\u7136\u8bf4\u51fa\u4e86\u4e00\u4e2a\u5b57\uff1a\u201c\u5200\uff01\u201d \u3000\u3000\u201c\u4f60\u8981\u6211\u7528\u5200\u5272\u4e0b\u4f60\u7684\u820c\u5934\uff1f\u201d\u8fd9\u53e5\u8bdd\u8bf4\u51fa\uff0c\u53f6\u5f00\u4e5f\u4e0d\u7981\u6fc0\u7075\u7075\u6253\u4e86\u4e2a\u5bd2\u5664\u3002 \u3000\u3000\u53ea\u89c1\u82e6\u7af9\u7684\u820c\u5934\u8d8a\u80bf\u8d8a\u5927\uff0c\u547c\u5438\u8d8a\u6765\u8d8a\u6025\u4fc3\uff0c\u7a81\u7136\u7528\u5c3d\u5168\u8eab\u529b\u6c14\u4e00\u54ac\u3002\u4e00\u622a\u820c\u5934\u88ab\u4ed6\u81ea \u5df1\u54ac\u4e86\u4e0b\u6765\uff0c\u8840\u6e85\u51fa\uff0c\u8840\u4e5f\u662f\u9ed1\u7684\u3002 \u3000\u3000\u82e6\u7af9\u7ec8\u4e8e\u53d1\u51fa\u4e86\u4e00\u58f0\u60e8\u547c\uff0c\u53eb\u58f0\u7a81\u7136\u505c\u987f\u65f6\uff0c\u4ed6\u4eba\u4e5f\u5df2\u5012\u4e0b\uff0c\u4e34\u6b7b\u4e4b\u524d\uff0c\u7adf\u8fd8\u662f\u54ac\u4e0b\u4e86 \u81ea\u5df1\u7684\u820c\u5934\u3002 \u3000\u3000\u8fd9\u591a\u5634\u7684\u548c\u5c1a\uff0c\u65e0\u8bba\u6b7b\u6d3b\u90fd\u5df2\u4e0d\u80fd\u591a\u5634\u3002 \u7b2c\u4e09\u5341\u7ae0\u3000\u4e45\u522b\u91cd\u9022>> \u53e4\u9f99\u300a\u4e5d\u6708\u9e70\u98de\u300b \u7b2c\u4e09\u5341\u7ae0\u3000\u4e45\u522b\u91cd\u9022 \u3000\u3000\u98ce\u5f88\u51b7\u3002 \u3000\u3000\u53f6\u5f00\u8fce\u7740\u98ce\u8d70\u51fa\u53bb\uff0c\u8eab\u4e0a\u7684\u51b7\u6c57\u88ab\u51e4\u4e00\u5439\uff0c\u5c31\u50cf\u662f\u4e00\u7c92\u7c92\u51b0\u73e0\u4e00\u6837\u3002 \u3000\u3000\u4ed6\u5b9e\u5728\u4e5f\u4e0d\u6562\u5728\u90a3\u5927\u6bbf\u4e2d\u5446\u4e0b\u53bb\u3002 \u3000\u3000\u4ed6\u4e0d\u6015\u9b3c\u3002 \u3000\u3000\u53ef\u662f\u90a3\u5927\u6bbf\u91cc\u5374\u50cf\u662f\u9690\u85cf\u7740\u4e00\u4e9b\u6bd4\u9b3c\u66f4\u53ef\u6015\u7684\u4e8b\u3002 \u3000\u3000\u8fdc\u5904\u4f20\u6765\u66f4\u9f13\u3002 \u3000\u3000\u4e09\u66f4\u5df2\u8fc7\u3002 \u3000\u3000\u8fd9\u53e4\u8001\u7684\u57ce\u5e02\u91cc\uff0c\u706f\u706b\u5df2\u5be5\u843d\u3002\u65e0\u8bba\u8d70\u5230\u54ea\u91cc\uff0c\u90fd\u662f\u4e00\u7247\u9ed1\u6697\u3002 \u3000\u3000\u82e5\u662f\u5728\u590f\u5929\uff0c\u4e5f\u8bb8\u8fd8\u53ef\u4ee5\u627e\u5230\u4e00\u4e24\u5904\u559d\u9152\u5403\u5bb5\u591c\u7684\u5730\u65b9\u3002 \u3000\u3000\u53ea\u53ef\u60dc\u73b0\u5728\u8fd8\u662f\u6625\u5929\u3002 \u3000\u3000\u4e5f\u8bb8\u5c31\u56e0\u4e3a\u73b0\u5728\u7edd\u5bf9\u627e\u4e0d\u5230\u9152\u559d\uff0c\u6240\u4ee5\u53f6\u5f00\u5ffd\u7136\u89c9\u5f97\u5f88\u60f3\u559d\u4e24\u676f\u3002 \u3000\u3000\u4ed6\u53f9\u4e86\u53e3\u6c14\uff0c\u8d70\u51fa\u6a2a\u5df7\uff0c\u5b9e\u5728\u4e0d\u77e5\u9053\u8be5\u5230\u54ea\u91cc\u53bb\uff0c\u4eca\u5929\u665a\u4e0a\u4ed6\u751a\u81f3\u8fde\u7761\u89c9\u7684\u5730\u65b9\u90fd\u6ca1 \u6709\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\u5019\uff0c\u7a81\u542c\u6709\u4eba\u5e26\u7740\u7b11\u9053\uff1a\u201c\u6211\u77e5\u8fd9\u4e00\u4e2a\u5730\u65b9\u8fd8\u6709\u9152\u559d\uff0c\u4f60\u8ddf\u4e0d\u8ddf\u6211\u8d70\uff1f\u201d \u3000\u3000\u867d\u7136\u6709\u661f\u5149\uff0c\u5df7\u5b50\u91cc\u5374\u8fd8\u662f\u9ed1\u6697\u7684\uff0c\u4e00\u4e2a\u4eba\u5927\u8896\u98d8\u98d8\uff0c\u5728\u524d\u9762\u8d70\u3002 \u3000\u3000\u53f6\u5f00\u5728\u540e\u9762\u8ddf\u7740\u3002 \u3000\u3000\u524d\u9762\u7684\u4eba\u4e00\u76f4\u6ca1\u6709\u56de\u5934\uff0c\u53f6\u5f00\u4e5f\u4e00\u76f4\u6ca1\u6709\u95ee\uff0c\u66f4\u6ca1\u6709\u8d76\u4e0a\u53bb\u3002 \u3000\u3000\u524d\u9762\u7684\u4eba\u8d70\u5f97\u5e76\u4e0d\u5feb\uff0c\u4f46\u662f\u5bf9\u8fd9\u91cc\u7684\u8857\u9053\u5df7\u5f04\u5374\u5f88\u719f\u6089\u3002 \u3000\u3000\u53f6\u5f00\u8ddf\u7740\u4ed6\u516d\u8f6c\u516b\u8f6c\uff0c\u8fde\u65b9\u5411\u90fd\u5df2\u51e0\u4e4e\u65e0\u6cd5\u5206\u8fa8\uff0c\u53ea\u89c1\u524d\u9762\u4e00\u9053\u9ad8\u5899\uff0c\u91cc\u9762\u7684\u5ead\u9662\u4eff\u4f5b \u5f88\u6df1\uff0c\u8fd9\u4eba\u957f\u8896\u4e00\u62c2\uff0c\u5c45\u7136\u8f7b\u677e\u5730\u8d8a\u8fc7\u9ad8\u5899\u3002 \u3000\u3000\u8fd9\u4eba\u8f7b\u529f\u6781\u9ad8\uff0c\u8eab\u6cd5\u4e5f\u6781\u7f8e\u5999\uff0c\u8fde\u53f6\u5f00\u90fd\u5f88\u5c11\u89c1\u5230\u8f7b\u529f\u8fd9\u4e48\u9ad8\u7684\u4eba\u3002 \u3000\u3000\u9ad8\u5899\u5185\u4e5f\u662f\u4e00\u7247\u9ed1\u6697\uff0c\u51b7\u98ce\u4e2d\u6d6e\u52a8\u7740\u4e00\u9635\u9635\u6c81\u4eba\u5fc3\u813e\u7684\u6697\u9999\u3002 \u3000\u3000\u661f\u5149\u4e0b\u758f\u679d\u6a2a\u5f71\uff1a\u5c3d\u662f\u6885\u82b1\u3002 \u3000\u3000\u53f6\u5f00\u8ddf\u7740\u8d8a\u5899\u800c\u5165\uff0c\u624d\u53d1\u73b0\u8fd9\u5730\u65b9\u5c31\u662f\u4ed6\u521d\u5230\u957f\u5b89\u65f6\u6765\u8fc7\u7684\u51b7\u9999\u56ed\u3002 \u3000\u3000\u7ecf\u8fc7\u4e86\u90a3\u6b21\u8be1\u79d8\u60e8\u5389\u7684\u6076\u6218\u540e\uff0c\u8fd9\u6614\u65e5\u7684\u957f\u5b89\u7b2c\u4e00\u540d\u56ed\u4e2d\uff0c\u7adf\u5df2\u8352\u65e0\u4eba\u8ff9\u3002 \u3000\u3000\u8fde\u706f\u5149\u90fd\u6ca1\u6709\uff0c\u53ea\u6709\u5bd2\u98ce\u5439\u7740\u82b1\u679d\uff0c\u53d1\u51fa\u4e00\u9635\u9635\u4eff\u4f5b\u53f9\u606f\u4e00\u822c\u7684\u58f0\u97f3\u3002 \u3000\u3000\u662f\u8c01\u5728\u53f9\u606f\uff0c\u5728\u4e3a\u8c01\u53f9\u606f\uff1f \u3000\u3000\u662f\u4e0d\u662f\u4e3a\u4e86\u90a3\u4e9b\u5c48\u6b7b\u5728\u8fd9\u91cc\u7684\u9b3c\u9b42\uff1f \u3000\u3000\u51b7\u9999\u56ed\uff0c\u66f2\u5f84\u901a\u5e7d\u3002 \u3000\u3000\u524d\u9762\u7684\u4eba\u5bf9\u8fd9\u91cc\u7684\u5730\u52bf\u7adf\u4f3c\u4e5f\u5f88\u719f\u6089\uff0c\u53f6\u5f00\u53c8\u8ddf\u7740\u4ed6\u4e03\u8f6c\u516b\u8f6c\uff0c\u7a7f\u8fc7\u4e00\u9053\u95e8\uff0c\u6765\u5230\u4e00\u91cd \u5c0f\u9662\u3002 \u3000\u3000\u9662\u5b50\u91cc\u4e5f\u6ca1\u6709\u4eba\uff0c\u6ca1\u6709\u706f\u5149\uff0c\u6ca1\u6709\u58f0\u97f3\u3002 \u3000\u3000\u95e8\u662f\u5f00\u7740\u7684\uff0c\u8fd9\u4eba\u8d70\u8fc7\u53bb\u63a8\u5f00\u4e86\u95e8\uff0c\u81ea\u5df1\u5374\u95ea\u5230\u65c1\u8fb9\uff0c\u9053\uff1a\u201c\u8bf7\u8fdb\u3002\u201d \u3000\u3000\u53f6\u5f00\u6ca1\u6709\u8fdb\u53bb\u3002 \u3000\u3000\u8fd9\u4eba\u9053\uff1a\u201c\u4f60\u4e0d\u8fdb\u53bb\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u4e3a\u4ec0\u4e48\u8981\u8fdb\u53bb\uff1f\u201d \u3000\u3000\u8fd9\u4eba\u9053\uff1a\u201c\u91cc\u9762\u6709\u4eba\u5728\u7b49\u4f60\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u8c01\uff1f\u201d \u3000\u3000\u8fd9\u4eba\u9053\uff1a\u201c\u4f60\u8fdb\u53bb\u770b\u770b\u5c31\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u4e0d\u8fdb\u53bb\uff1f\u201d \u3000\u3000\u8fd9\u4eba\u9053\uff1a\u201c\u4eba\u5bb6\u7b49\u7684\u662f\u4f60\uff0c\u4e0d\u662f\u6211\u3002\u201d \u3000\u3000\u4ed6\u7684\u58f0\u97f3\u5f88\u5947\u602a\uff0c\u8138\u4e0a\u8499\u7740\u5757\u548c\u8863\u670d\u540c\u6837\u989c\u8272\u7684\u4e1d\u4e2d\u3002 \u3000\u3000\u53f6\u5f00\u76ef\u7740\u4ed6\uff0c\u5ffd\u7136\u7b11\u4e86\uff0c\u5fae\u7b11\u7740\u9053\uff1a\u201c\u4f60\u660e\u660e\u77e5\u9053\u6211\u80fd\u8ba4\u5f97\u51fa\u6765\uff0c\u4e3a\u4ec0\u4e48\u504f\u504f\u4e0d\u80af\u89c1 \u6211\uff1f\u201d \u3000\u3000\u8fd9\u4eba\u4eff\u4f5b\u5403\u4e86\u4e00\u60ca\uff0c\u5931\u58f0\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u8ba4\u5f97\u51fa\u6211\uff1f\u201d \u3000\u3000\u53f6\u5f00\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u82e5\u8ba4\u4e0d\u51fa\uff0c\u5c31\u4e0d\u4ec5\u662f\u4e2a\u778e\u5b50\uff0c\u800c\u4e14\u8fd8\u662f\u4e2a\u5446\u5b50\u3002\u201d \u3000\u3000\u8fd9\u4eba\u5782\u4e0b\u5934\uff0c\u8f7b\u8f7b\u5730\u95ee\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u4e0d\u77e5\u9053\uff1f\u201d \u3000\u3000\u8fd9\u4eba\u58f0\u97f3\u66f4\u8f7b\uff0c\u9053\uff1a\u201c\u662f\u4e0d\u662f\u56e0\u4e3a\u4f60\u5fc3\u91cc\u5df2\u6709\u4e86\u6211\uff1f\u201d\u53f6\u5f00\u6ca1\u6709\u53e3\u7b54\uff0c\u773c\u775b\u91cc\u7684\u8868\u60c5\u5ffd \u7136\u53c8\u53d8\u5f97\u5f88\u5947\u602a\u3002 \u3000\u3000\u65e0\u8bba\u8fd9\u79cd\u8868\u60c5\u662f\u4ec0\u4e48\u610f\u601d\uff0c\u81f3\u5c11\u4e0d\u662f\u5728\u5426\u8ba4\u3002 \u3000\u3000\u8fd9\u4eba\u7ec8\u4e8e\u62ac\u8d77\u5934\uff0c\u6380\u5f00\u4e86\u8138\u4e0a\u7684\u4e1d\u4e2d\uff0c\u661f\u5149\u5c31\u7167\u5728\u5979\u8138\u4e0a\u3002 \u3000\u3000\u5982\u6b64\u9759\u591c\uff0c\u5982\u6b64\u661f\u5149\uff0c\u5979\u7684\u8138\u770b\u6765\u7f8e\u4e3d\u5f97\u5c31\u50cf\u662f\u6885\u82b1\u7684\u7cbe\u7075\uff0c\u5929\u4e0a\u7684\u4ed9\u5b50\u3002 \u3000\u3000\u5979\u7684\u773c\u775b\u66f4\u7f8e\uff0c\u5374\u53c8\u4eff\u4f5b\u5e26\u7740\u79cd\u65e0\u6cd5\u5411\u4eba\u53d9\u8bf4\u7684\u5e7d\u6028\u548c\u611f\u4f24\u3002 \u3000\u3000\u5979\u51dd\u89c6\u7740\u53f6\u5f00\uff0c\u8f7b\u8f7b\u9053\uff1a\u201c\u6211\u7684\u786e\u5e94\u8be5\u77e5\u9053\u4f60\u80fd\u8ba4\u5f97\u51fa\u6211\u6765\u7684\uff0c\u56e0\u4e3a\uff0c\u4f60\u5c31\u7b97\u5316\u6210\u4e86 \u7070\uff0c\u6211\u4e5f\u8ba4\u5f97\u51fa\u4f60\u3002\u201d \u3000\u3000\u5979\u7684\u58f0\u97f3\u4e5f\u7f8e\uff0c\u7f8e\u5f97\u5c31\u50cf\u662f\u6625\u5929\u508d\u665a\u5439\u8fc7\u5927\u5730\u7684\u67d4\u98ce\u3002 \u3000\u3000\u5982\u6b64\u7f8e\u4e3d\u7684\u773c\u6674\uff0c\u5982\u6b64\u7f8e\u4e3d\u7684\u58f0\u97f3\uff0c\u9664\u4e86\u4e0a\u5b98\u5c0f\u4ed9\u8fd8\u6709\u8c01\uff1f \u3000\u3000\u53f6\u5f00\u4e5f\u5728\u51dd\u89c6\u7740\u5979\uff0c\u9053\uff1a\u201c\u4f46\u662f\u4f60\u5374\u5e0c\u671b\u6211\u8ba4\u4e0d\u51fa\u4f60\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u70b9\u70b9\u5934\u3002 \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u8fdf\u7591\u7740\uff0c\u9053\uff1a\u201c\u4f60\u8fdb\u53bb\u770b\u770b\uff0c\u5c31\u77e5\u9053\u662f\u4e3a\u4ec0\u4e48\u4e86\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u4e0d\u8fdb\u53bb\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6211\u53ef\u4ee5\u5916\u9762\u7b49\u7740\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u8981\u5728\u5916\u9762\u7b49\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u7b11\u4e86\u7b11\u9053\uff1a\u201c\u56e0\u4e3a\u4f60\u8fdb\u53bb\u4e86\u4e4b\u540e\uff0c\u4e00\u5b9a\u4e5f\u5e0c\u671b\u6211\u5728\u5916\u9762\u7b49\u7740\u3002\u201d \u3000\u3000\u5979\u7b11\u5f97\u4e0d\u4f46\u5f88\u51c4\u51c9\uff0c\u800c\u4e14\u5f88\u795e\u79d8\u3002 \u3000\u3000\u5979\u5b9e\u5728\u662f\u4e2a\u795e\u79d8\u7684\u5973\u4eba\uff0c\u603b\u662f\u4f1a\u505a\u51fa\u4e00\u4e9b\u4ee4\u4eba\u610f\u60f3\u4e0d\u5230\u7684\u4e8b\u3002 \u3000\u3000\u53f6\u5f00\u6ca1\u6709\u518d\u95ee\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u4e86\u89e3\u5979\uff0c\u5979\u4e0d\u80af\u8bf4\u7684\u4e8b\uff0c\u65e0\u8bba\u8c01\u4e5f\u95ee\u4e0d\u51fa\u6765\u7684\u3002 \u3000\u3000\u95e8\u5f00\u7740\uff0c\u88ab\u98ce\u5439\u5f97\u201c\u5431\u5431\u201d\u7684\u54cd\u3002 \u3000\u3000\u53f6\u5f00\u7ec8\u4e8e\u8d70\u4e86\u8fdb\u53bb\uff0c\u8d70\u4eba\u4e86\u9ed1\u6697\u4e2d\u2026\u2026 \u3000\u3000\u5916\u9762\u8fd8\u6709\u661f\u5149\uff0c\u5c4b\u5b50\u91cc\u66f4\u9ed1\u6697\u3002 \u3000\u3000\u53f6\u5f00\u4ec0\u4e48\u4e5f\u770b\u4e0d\u89c1\uff0c\u5374\u542c\u5230\u4e00\u9635\u9635\u5f88\u8f7b\u5f88\u8f7b\u7684\u547c\u5438\u58f0\u3002 \u3000\u3000\u5c4b\u5b50\u91cc\u679c\u7136\u6709\u4eba\u3002 \u3000\u3000\u201c\u662f\u8c01\uff1f\u201d \u3000\u3000\u6ca1\u6709\u4eba\u56de\u5e94\uff0c\u8fde\u547c\u5438\u58f0\u90fd\u4f3c\u5df2\u505c\u6b62\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u65e2\u7136\u662f\u5728\u5c4b\u5b50\u91cc\u7b49\u53f6\u5f00\uff0c\u4e3a\u4ec0\u4e48\u53c8\u4e0d\u80af\u56de\u7b54\u53f6\u5f00\u7684\u8bdd\uff1f \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u5e26\u53f6\u5f00\u6765\u7684\u65f6\u5019\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u80af\u4ee5\u771f\u9762\u76ee\u8ddf\u4ed6\u76f8\u89c1\uff1f \u3000\u3000\u5047\u5982\u662f\u522b\u4eba\uff0c\u8bf4\u4e0d\u5b9a\u65e9\u5df2\u9000\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u53ef\u662f\u53f6\u5f00\u6ca1\u6709\u3002 \u3000\u3000\u4ed6\u5fc3\u91cc\u5ffd\u7136\u6709\u4e86\u79cd\u8fde\u4ed6\u81ea\u5df1\u90fd\u65e0\u6cd5\u89e3\u91ca\u7684\u5947\u5f02\u611f\u89c9\u3002 \u3000\u3000\u4e00\u9635\u98ce\u5439\u8fc7\uff0c\u201c\u7830\u201d\u7684\u4e00\u58f0\uff0c\u95e8\u5ffd\u7136\u5173\u4e86\u8d77\u6765\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u5c31\u7b97\u60f3\u8d70\uff0c\u4e5f\u6ca1\u6cd5\u5b50\u8d70\u4e86\u3002 \u3000\u3000\u5c4b\u5b50\u91cc\u66f4\u6697\uff0c\u7684\u786e\u5df2\u4f38\u624b\u4e0d\u89c1\u4e94\u6307\uff0c\u4f46\u90a3\u547c\u5438\u58f0\u5374\u53c8\u54cd\u4e86\u8d77\u6765\u3002 \u3000\u3000\u547c\u5438\u58f0\u672c\u6765\u662f\u5728\u524d\u9762\u7684\uff0c\u73b0\u5728\u5df2\u9000\u5165\u4e86\u5c4b\u89d2\u3002 \u3000\u3000\u4ed6\u4e3a\u4ec0\u4e48\u8981\u9000\uff1f \u3000\u3000\u662f\u4e0d\u662f\u56e0\u4e3a\u4ed6\u4e5f\u5728\u5bb3\u6015\uff1f \u3000\u3000\u53f6\u5f00\u6c89\u4f4f\u4e86\u6c14\uff0c\u9053\uff1a\u201c\u4e0d\u7ba1\u4f60\u662f\u8c01\uff0c\u4f60\u65e2\u7136\u5728\u7b49\u6211\uff0c\u5c31\u8be5\u77e5\u9053\u6211\u662f\u8c01\u3002\u201d \u3000\u3000\u6ca1\u6709\u56de\u7b54\u3002 \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u5e76\u4e0d\u662f\u4e2a\u51f6\u6076\u7684\u4eba\uff0c\u6240\u4ee5\u4f60\u6839\u672c\u4e0d\u5fc5\u6015\u6211\u3002\u201d \u3000\u3000\u4ed6\u4e00\u9762\u5728\u8bf4\u8bdd\uff0c\u4e00\u9762\u5df2\u8d70\u8fc7\u53bb\u3002 \u3000\u3000\u4ed6\u8d70\u5f97\u5f88\u4f46\u3002 \u3000\u3000\u7a81\u7136\u95f4\uff0c\u4e00\u9635\u51b7\u98ce\u8fce\u9762\u5411\u4ed6\u5439\u8fc7\u6765\u3002 \u3000\u3000\u4ed6\u4ec0\u4e48\u90fd\u6ca1\u6709\u770b\u89c1\uff0c\u4f46\u662f\u4ed6\u53ef\u4ee5\u611f\u89c9\u5230\uff0c\u53ea\u6709\u5200\u98ce\u624d\u4f1a\u8fd9\u4e48\u51b7\u3002 \u3000\u3000\u8fd9\u67c4\u5200\u4ed6\u5374\u4e5f\u770b\u4e0d\u89c1\u3002 \u3000\u3000\u2014\u2014\u770b\u4e0d\u89c1\u7684\u5200\uff0c\u624d\u662f\u6740\u4eba\u7684\u5200\u3002 \u3000\u3000\u8fd9\u4eba\u662f\u8c01\uff0c\u4e3a\u4ec0\u4e48\u8981\u6740\u4ed6\uff1f \u3000\u3000\u5200\u98ce\u4e0d\u4f46\u51b7\uff0c\u800c\u4e14\u6025\u3002 \u3000\u3000\u53f6\u5f00\u8eab\u5f62\u4e00\u95ea\uff0c\u7a81\u7136\u95ea\u7535\u822c\u51fa\u624b\uff0c\u6263\u4f4f\u4e86\u8fd9\u4eba\u7684\u624b\uff0c\u624b\u51b0\u51b7\u3002 \u3000\u3000\u8fd9\u53ea\u624b\u4ed6\u5f53\u7136\u4e5f\u770b\u4e0d\u89c1\uff0c\u53ef\u662f\u4ed6\u5374\u80fd\u611f\u89c9\u5f97\u5230\uff0c\u6240\u4ee5\u80fd\u6293\u4f4f\u3002\u771f\u6b63\u7684\u6b66\u6797\u9ad8\u624b\u90fd\u6709\u79cd\u5947 \u5f02\u7684\u3001\u65e0\u6cd5\u89e3\u91ca\u7684\u611f\u89c9\uff0c\u5c31\u50cf\u662f\u91ce\u517d\u7684\u5352\u80fd\u4e00\u6837\u3002 \u3000\u3000\u8fd9\u4eba\u7684\u624b\u5728\u53d1\u6296\uff0c\u5374\u8fd8\u662f\u4e0d\u80af\u5f00\u53e3\u3002 \u3000\u3000\u53f6\u5f00\u7684\u624b\u4e5f\u7a81\u7136\u53d1\u6296\uff0c\u56e0\u4e3a\u4ed6\u5df2\u9690\u7ea6\u731c\u51fa\u4e86\u8fd9\u4e2a\u4eba\u662f\u8c01\u3002 \u3000\u3000\u4ed6\u55c5\u5230\u4e86\u8fd9\u4eba\u8eab\u4e0a\u7684\u6c14\u606f\u3002 \u3000\u3000\u6bcf\u4e2a\u4eba\u90fd\u6709\u4ed6\u81ea\u5df1\u7279\u6b8a\u7684\u6c14\u606f\uff0c\u8fd9\u4e2a\u4eba\u7684\u6c14\u606f\u4ed6\u6c38\u8fbd\u4e5f\u4e0d\u4f1a\u5fd8\u8bb0\u3002 \u3000\u3000\u6b7b\u4e5f\u4e0d\u4f1a\u5fd8\u8bb0\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u8fd9\u4e2a\u4eba\u5df2\u6446\u8131\u4e86\u4ed6\u7684\u624b\uff0c\u53c8\u9000\u4eba\u4e86\u5c4b\u89d2\u3002 \u3000\u3000\u8fd9\u6b21\u53f6\u5f00\u5e76\u6ca1\u6709\u518d\u903c\u8fc7\u53bb\uff0c\u4e8b\u5b9e\u4e0a\uff0c\u4ed6\u6574\u4e2a\u4eba\u90fd\u5df2\u50f5\u786c\uff0c\u5c31\u50cf\u662f\u5757\u6728\u5934\u822c\u6014\u4f4f\u3002 \u3000\u3000\u4ed6\u60f3\u4e0d\u5230\u8fd9\u4e2a\u4eba\u4f1a\u5728\u8fd9\u91cc\uff0c\u66f4\u60f3\u4e0d\u5230\u8fd9\u4e2a\u4eba\u4f1a\u6740\u4ed6\u3002 \u3000\u3000\u51b7\u6c57\u5df2\u5f00\u59cb\u4ece\u4ed6\u989d\u4e0a\u6d41\u4e0b\u3002 \u3000\u3000\u201c\u6211\u662f\u5c0f\u53f6\u3002\u201d\u4ed6\u5c3d\u529b\u63a7\u5236\u81ea\u5df1\uff1a\u201c\u96be\u9053\u4f60\u542c\u4e0d\u51fa\u6211\u7684\u58f0\u97f3\uff1f\u201d \u3000\u3000\u8fd8\u662f\u6ca1\u6709\u56de\u5e94\uff0c\u547c\u5438\u58f0\u5374\u5f88\u6025\u4fc3\uff0c\u4eff\u4f5b\u5145\u6ee1\u6050\u60e7\u3002 \u3000\u3000\u53f6\u5f00\u54ac\u4e86\u54ac\u7259\uff0c\u975e\u4f46\u6ca1\u6709\u518d\u5f80\u524d\u8d70\uff0c\u53cd\u800c\u4e00\u6b65\u6b65\u5411\u540e\u9000\uff0c\u9000\u5230\u95e8\u53e3\uff0c\u7a81\u7136\u8f6c\u8eab\uff0c\u7528\u529b\u62c9 \u95e8\u3002 \u3000\u3000\u95e8\u5c45\u7136\u4e00\u62c9\u5c31\u5f00\u4e86\u3002 \u3000\u3000\u4ed6\u51b2\u51fa\u53bb\uff0c\u4e0a\u5b98\u5c0f\u4ed9\u5c45\u7136\u771f\u7684\u8fd8\u662f\u5728\u9662\u5b50\u91cc\u7b49\u7740\u3002 \u3000\u3000\u770b\u5230\u4e86\u4ed6\u7684\u8868\u60c5\uff0c\u5979\u7684\u773c\u775b\u91cc\u5145\u6ee1\u4e86\u540c\u60c5\u548c\u5173\u5207\uff0c\u8fce\u4e0a\u6765\u95ee\u9053\uff1a\u201c\u4f60\u5df2\u77e5\u9053\u5c4b\u5b50\u91cc\u7684\u4eba \u662f\u8c01\uff1f\u201d\u53f6\u5f00\u70b9\u70b9\u5934\uff0c\u63e1\u7d27\u53cc\u62f3\uff0c\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u70b9\u8d77\u706f\u6765\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6211\u53c8\u4e0d\u5728\u5c4b\u5b50\u91cc\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u6ca1\u6709\u706b\u71a0\u4e8e\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6211\u6709\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u65e2\u7136\u6709\uff0c\u4e3a\u4ec0\u4e48\u521a\u624d\u4e0d\u7ed9\u6211\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u6ca1\u6709\u56de\u7b54\u8fd9\u53e5\u8bdd\uff0c\u53ea\u662f\u9ed8\u9ed8\u7684\u5c06\u706b\u71a0\u5b50\u4ea4\u7ed9\u4e86\u4ed6\u3002 \u3000\u3000\u53f6\u5f00\u7acb\u523b\u53c8\u51b2\u8fdb\u53bb\uff0c\u6253\u4eae\u4e86\u706b\u71a0\u4e8e\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u75f4\u75f4\u5730\u7ad9\u5728\u5c4b\u89d2\uff0c\u8d6b\u7136\u7adf\u662f\u4e01\u7075\u7433\u3002 \u3000\u3000\u53f6\u5f00\u7ec8\u4e8e\u770b\u89c1\u4e86\u5979\uff0c\u7ec8\u4e8e\u627e\u5230\u4e86\u5979\u3002 \u3000\u3000\u6ca1\u6709\u4eba\u80fd\u5f62\u5bb9\u4ed6\u6b64\u523b\u7684\u611f\u89c9\uff0c\u4e5f\u6ca1\u6709\u4eba\u80fd\u60f3\u8c61\u3002 \u3000\u3000\u53ef\u662f\u4e01\u7075\u7433\u5374\u7a81\u7136\u75af\u72c2\u822c\u5927\u53eb\u4e86\u8d77\u6765\uff0c\u6307\u7740\u4ed6\u624b\u91cc\u7684\u706b\u71a0\u5b50\uff0c\u5927\u53eb\u9053\uff1a\u201c\u706b\u2026\u2026 \u706b\u2026\u2026\u201d \u3000\u3000\u770b\u89c1\u4e86\u706b\u5149\uff0c\u5979\u5c31\u50cf\u662f\u7a81\u7136\u53d8\u6210\u4e86\u4e00\u53ea\u53d7\u60ca\u8d1f\u4f24\u7684\u91ce\u517d\uff0c\u5979\u6574\u4e2a\u4eba\u90fd\u7f29\u6210\u4e86\u4e00\u56e2\uff0c\u4e0d\u505c \u5730\u53d1\u6296\uff0c\u7f8e\u4e3d\u7684\u8138\u4e5f\u5df2\u56e0\u60ca\u9a87\u800c\u53d8\u4e86\u5f62\uff0c\u4e00\u76f4\u4e0d\u505c\u5730\u5927\u53eb\uff1a\u201c\u706b\u2026\u2026\u706b\u2026\u2026\u201d \u3000\u3000\u5979\u53ea\u770b\u89c1\u4e86\u706b\uff0c\u5374\u6ca1\u6709\u770b\u89c1\u53f6\u5f00\u3002\u5979\u7adf\u4f3c\u5df2\u4e0d\u8ba4\u5f97\u53f6\u5f00\u3002\u706b\u5149\u7acb\u523b\u7184\u706d\uff0c\u5c4b\u5b50\u91cc\u53c8\u662f\u4e00 \u7247\u9ed1\u6697\u3002 \u3000\u3000\u53f6\u5f00\u7684\u5fc3\u4e5f\u6c89\u5165\u4e86\u9ed1\u6697\u91cc\uff0c\u65e0\u8fb9\u65e0\u9645\u7684\u9ed1\u6697\u3002\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u4ed6\u53c8\u6084\u4fcf\u5730\u9000\u4e86\u51fa\u53bb\uff0c \u65e0\u8a00\u7684\u5c06\u706b\u71a0\u5b50\u8fd8\u7ed9\u4e86\u4e0a\u5b98\u5c0f\u4ed9\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u82e6\u7b11\u9053\uff1a\u201c\u4f60\u73b0\u5728\u662f\u4e0d\u662f\u5df2\u660e\u767d\uff0c\u521a\u624d\u6211\u4e3a\u4ec0\u4e48\u4e0d\u80af\u7ed9\u4f60\u706b\u71a0\u5b50\uff1f\u201d \u3000\u3000\u53f6\u5f00\u65e0\u8bed\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u53f9\u9053\uff1a\u201c\u5979\u662f\u4ece\u706b\u7a9f\u4e2d\u9003\u51fa\u6765\u7684\uff0c\u5979\u53d7\u7684\u60ca\u9a87\u592a\u5927\uff0c\u53ef\u662f\u2026\u53ef\u662f\u6211\u5b9e\u5728\u60f3\u4e0d \u5230\uff0c\u5979\u7adf\u5df2\u8fde\u4f60\u90fd\u4e0d\u8ba4\u5f97\u3002\u201d \u3000\u3000\u53f6\u5f00\u9eef\u7136\uff0c\u8fc7\u4e86\u5f88\u4e45\u624d\u95ee\u9053\uff1a\u201c\u4f60\u662f\u5728\u54ea\u91cc\u627e\u5230\u5979\u7684\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u5c31\u5728\u8fd9\u91cc\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u51e0\u65f6\u627e\u5230\u7684\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u5979\u9003\u51fa\u706b\u7a9f\u540e\uff0c\u60f3\u5fc5\u5c31\u5df2\u8eb2\u5230\u8fd9\u91cc\u6765\u3002\u53ef\u662f\u6211\u76f4\u5230\u4eca\u5929\u665a\u4e0a\u624d\u627e\u5230 \u5979\u3002\u201d \u3000\u3000\u5979\u5782\u4e0b\u5934\uff0c\u53c8\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u770b\u89c1\u5979\u8fd9\u6837\u5b50\uff0c\u4e00\u5b9a\u4f1a\u5f88\u96be\u53d7\uff0c\u53ef\u662f\u6211\u53c8\u4e0d\u80fd\u4e0d\u5e26\u4f60 \u6765\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u2026\u2026\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u6253\u65ad\u4e86\u4ed6\u7684\u8bdd\uff0c\u9053\uff1a\u201c\u6211\u672c\u4e0d\u60f3\u8ba9\u4f60\u77e5\u9053\u662f\u6211\u5e26\u4f60\u6765\u7684\uff0c\u56e0\u4e3a\u2026\u2026\u56e0\u4e3a\u2026\u2026\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u56e0\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u5782\u7740\u5934\uff0c\u6c89\u9ed8\u826f\u4e45\uff0c\u624d\u51c4\u7136\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u77e5\u9053\u7a76\u7adf\u4e3a\u4e86\u4ec0\u4e48\uff0c\u4e5f\u8bb8\u662f\u56e0\u4e3a\u6211\u4e0d \u613f\u8ba9\u4f60\u4e3a\u4e86\u8fd9\u4ef6\u4e8b\u800c\u611f\u6fc0\u6211\uff0c\u4e5f\u8bb8\u662f\u56e0\u4e3a\u6211\u5bb3\u6015\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u5bb3\u6015\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u795e\u60c5\u66f4\u60b2\u4f24\uff0c\u9053\uff1a\u201c\u5979\u53d8\u6210\u8fd9\u6837\u5b50\uff0c\u6211\u4e5f\u6709\u8d23\u4efb\uff0c\u6211\u6015\u4f60\u602a\u6211\uff0c\u6068\u6211\u2026\u2026\u6211\u66f4 \u6015\u4f60\u89c1\u4e86\u5979\u4e4b\u540e\uff0c\u4f1a\u4ece\u6b64\u4e0d\u7406\u6211\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f46\u4f60\u8fd8\u662f\u5e26\u6211\u6765\u4e86\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6240\u4ee5\u6211\u81ea\u5df1\u4e5f\u4e0d\u77e5\u9053\u7a76\u7adf\u662f\u5728\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u661f\u5149\u7167\u5728\u5979\u8138\u4e0a\uff0c\u5979\u6cea\u5df2\u6d41\u4e0b\uff0c\u65e0\u8bba\u8c01\u90fd\u5e94\u8be5\u80fd\u770b\u5f97\u51fa\uff0c\u5979\u5fc3\u91cc\u662f\u591a\u4e48\u77db\u76fe\uff0c\u591a\u4e48\u75db \u82e6\u3002 \u3000\u3000\u53f6\u5f00\u5374\u597d\u50cf\u770b\u4e0d\u89c1\uff0c\u5ffd\u7136\u8d70\u5230\u9662\u5b50\u4e2d\u592e\uff0c\u7ffb\u4e86\u4e09\u4e2a\u8ddf\u6597\uff0c\u7ad9\u8d77\u6765\uff0c\u7ad9\u5f97\u7b14\u76f4\uff0c\u957f\u957f\u5438\u4e86 \u53e3\u6c14\uff0c\u62c9\u5e73\u4e86\u8eab\u4e0a\u7684\u8863\u670d\uff0c\u5730\u4e0a\u7684\u79ef\u96ea\u672a\u6eb6\uff0c\u4e00\u679d\u6885\u82b1\u4e5f\u4e0d\u77e5\u88ab\u8c01\u6298\u65ad\uff0c\u843d\u5728\u79ef\u96ea\u4e0a\u3002 \u3000\u3000\u4ed6\u62fe\u8d77\u6765\uff0c\u6458\u4e0b\u4e00\u6735\uff0c\u63d2\u5728\u8863\u895f\u4e0a\uff0c\u7136\u540e\u518d\u8d70\u56de\u6765\uff0c\u5ffd\u7136\u5bf9\u4e0a\u5b98\u5c0f\u4ed9\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4f60 \u731c\u6211\u73b0\u5728\u60f3\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u5403\u60ca\u5730\u770b\u7740\u4ed6\uff0c\u4f3c\u5df2\u770b\u5f97\u53d1\u6014\u3002 \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u60f3\u53bb\u627e\u4e2a\u5730\u65b9\u7761\u4e00\u89c9\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u66f4\u5403\u60ca\uff0c\u9053\uff1a\u201c\u73b0\u5728\u4f60\u60f3\u53bb\u7761\u89c9\uff1f\u201d \u3000\u3000\u53f6\u5f00\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u660e\u5929\u4e2d\u5348\u6211\u8fd8\u6709\u4e8b\uff0c\u6211\u4e00\u5b9a\u8981\u517b\u8db3\u7cbe\u795e\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u7761\u5f97\u7740\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u4e3a\u4ec0\u4e48\u7761\u4e0d\u7740\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u53ef\u662f\u4e86\u7075\u7433\u2026\u2026\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4e0d\u7ba1\u600e\u4e48\u6837\uff0c\u6211\u4eec\u73b0\u5728\u603b\u7b97\u5df2\u627e\u5230\u4e86\u5979\uff0c\u522b\u7684\u4e8b\u53ef\u4ee5\u7b49\u5230\u4ee5\u540e\u518d\u8bf4\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u5979\u8fd9\u6837\u5b50\u4f60\u80fd\u653e\u5fc3\u5f97\u4e0b\uff1f\u201d \u3000\u3000\u53f6\u5f00\u5fae\u7b11\u9053\uff1a\u201c\u6709\u91d1\u94b1\u5e2e\u7684\u5e2e\u4e3b\u5728\u8fd9\u91cc\u4fdd\u62a4\u5979\uff0c\u6211\u8fd8\u6709\u4ec0\u4e48\u4e0d\u653e\u5fc3\u7684\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u770b\u7740\u4ed6\uff0c\u5c31\u597d\u50cf\u4ece\u6765\u6ca1\u6709\u770b\u89c1\u8fc7\u50cf\u4ed6\u8fd9\u79cd\u4eba\uff0c\u8fd9\u79cd\u4eba\u5b9e\u5728\u5c11\u89c1\u5f97\u5f88\u3002\u65e0\u8bba\u8c01\u9047 \u89c1\u8fd9\u79cd\u4e8b\uff0c\u90fd\u4e00\u5b9a\u4f1a\u5f88\u61ca\u607c\u5fe7\u8651\uff0c\u53ef\u662f\u4ed6\u7ffb\u4e86\u4e09\u4e2a\u8ddf\u6597\uff0c\u5c31\u5ffd\u7136\u5c06\u4e00\u5207\u4f18\u8651\u5168\u90e8\u8fdc\u8fdc\u5730\u629b\u5f00 \u4e86\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u53f9\u4e86\u53e3\u6c14\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u770b\u6765\u5c31\u7b97\u6709\u5929\u5927\u7684\u70e6\u607c\uff0c\u4f60\u4e5f\u80fd\u4e00\u4e0b\u5b50\u5c31\u629b\u5f00\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u8fd9\u4e16\u4e0a\u672c\u6ca1\u6709\u4ec0\u4e48\u503c\u5f97\u70e6\u607c\u7684\u4e8b\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u53f9\u9053\uff1a\u201c\u4f60\u5b9e\u5728\u662f\u4e2a\u5f88\u6709\u798f\u6c14\u7684\u4eba\u3002\u201d \u3000\u3000\u53f6\u5f00\u5c45\u7136\u6ca1\u6709\u5426\u8ba4\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u5fcd\u4e0d\u4f4f\u53c8\u95ee\u9053\uff1a\u201c\u660e\u5929\u4e2d\u5348\uff0c\u4f60\u6709\u4ec0\u4e48\u4e8b\u8981\u505a\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u6709\u4e2a\u7ea6\u4f1a\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4ec0\u4e48\u7ea6\u4f1a\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u5b64\u5cf0\u548c\u591a\u5c14\u7532\u7ea6\u597d\u4e86\u660e\u5929\u4e2d\u5348\u5728\u5ef6\u5e73\u95e8\u76f8\u89c1\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u76b1\u7709\u9053\uff1a\u201c\u8fd9\u662f\u4ed6\u4eec\u7684\u7ea6\u4f1a\uff0c\u4f60\u2026\u2026\u201d \u3000\u3000\u53f6\u5f00\u6253\u65ad\u4e86\u5979\u7684\u8bdd\uff0c\u9053\uff1a\u201c\u73b0\u5728\u591a\u5c14\u7532\u65e2\u7136\u5df2\u6b7b\u4e86\uff0c\u8fd9\u7ea6\u4f1a\u5c31\u53d8\u6210\u6211\u7684\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4f60\u60f3\u4e58\u6b64\u673a\u4f1a\uff0c\u627e\u5b64\u5cf0\u6765\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u55ef\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6bcf\u5929\u6b63\u5348\uff0c\u51fa\u5165\u5ef6\u5e73\u95e8\u7684\u4eba\u4e5f\u4e0d\u77e5\u9053\u6709\u591a\u5c11\uff0c\u4f60\u600e\u4e48\u77e5\u9053\u8c01\u662f\u5b64\u5cf0\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u603b\u6709\u6cd5\u5b50\u627e\u5230\u7684\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff0c\u201c\u4ec0\u4e48\u6cd5\u5b50\uff1f\u201d \u3000\u3000\u53f6\u5f00\u53c8\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u73b0\u5728\u8fde\u6211\u81ea\u5df1\u4e5f\u4e0d\u77e5\u9053\uff0c\u53ef\u662f\u5230\u65f6\u5019\u6211\u5c31\u80fd\u60f3\u51fa\u6765\u3002\u201d \u3000\u3000\u4ed6\u5fae\u7b11\u7740\uff0c\u53c8\u9053\uff1a\u201c\u8fd9\u4e16\u4e0a\u672c\u6ca1\u6709\u4ec0\u4e48\u4e0d\u80fd\u89e3\u51b3\u7684\u4e8b\uff0c\u5bf9\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u53ea\u6709\u82e6\u7b11\u3002 \u3000\u3000\u51b7\u9999\u56ed\u91cc\u53ef\u4ee5\u7761\u89c9\u7684\u5730\u65b9\u5f53\u7136\u5f88\u591a\uff0c\u53f6\u5f00\u5c45\u7136\u771f\u7684\u8bf4\u8d70\u5c31\u8d70\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u770b\u7740\u4ed6\u8d70\u51fa\u53bb\uff0c\u53c8\u5fcd\u4e0d\u4f4f\u5927\u58f0\u95ee\u9053\uff1a\u201c\u4f60\u81ea\u5df1\u53bb\u7761\u89c9\uff0c\u5374\u8981\u6211\u66ff\u4f60\u5728\u8fd9\u91cc\u4fdd\u62a4 \u5979\uff1f\u201d \u3000\u3000\u53f6\u5f00\u5fae\u7b11\u7740\u6325\u4e86\u6325\u624b\uff0c\u5df2\u8d70\u5f97\u4eba\u5f71\u4e0d\u89c1\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u4e0d\u7981\u53f9\u4e86\u53e3\u6c14\uff0c\u82e6\u7b11\u7740\u9053\uff1a\u201c\u73b0\u5728\u6211\u624d\u77e5\u9053\u4ed6\u4e3a\u4ec0\u4e48\u603b\u662f\u6ca1\u6709\u70e6\u607c\u4e86\uff0c\u56e0\u4e3a\u4ed6 \u603b\u662f\u80fd\u5c06\u4ed6\u81ea\u5df1\u7684\u70e6\u607c\u9001\u7ed9\u522b\u4eba\u7684\u3002\u201d \u3000\u3000\u8fd9\u7684\u786e\u662f\u53f6\u5f00\u7684\u672c\u4e8b\uff0c\u4ed6\u82e5\u6ca1\u6709\u8fd9\u79cd\u672c\u4e8b\uff0c\u73b0\u5728\u53ea\u6015\u65e9\u5df2\u4e00\u5934\u649e\u6b7b\u3002 \u3000\u3000\u521d\u4e09\u4e0a\u5348\u3002 \u3000\u3000\u53f6\u5f00\u5927\u6b65\u8d70\u8ff8\u4e86\u9662\u5b50\uff0c\u4ed6\u8eab\u4e0a\u7a7f\u7684\u8863\u670d\u53c8\u810f\u53c8\u76b1\uff0c\u81f3\u5c11\u5df2\u6709\u597d\u51e0\u5929\u6ca1\u6d17\u6fa1\uff0c\u4ed6\u7684\u53d1\u9afb\u84ec \u4e71\uff0c\u8863\u895f\u4e0a\u7684\u82b1\u4e5f\u5df2\u67af\u4e86\u3002 \u3000\u3000\u6700\u8fd1\u4ed6\u9047\u89c1\u7684\u4e8b\uff0c\u82e5\u6362\u4e86\u522b\u4eba\u65e9\u5df2\u6d3b\u4e0d\u4e0b\u53bb\u3002\u53ef\u662f\u4ed6\u8d70\u8fdb\u9662\u5b50\u6765\u7684\u65f6\u5019\uff0c\u5374\u663e\u5f97\u5bb9\u5149\u7115 \u53d1\uff0c\u7cbe\u795e\u6296\u64de\uff0c\u5c31\u50cf\u662f\u521a\u53d1\u4e86\u8d22\uff0c\u53c8\u4e2d\u4e86\u72b6\u5143\uff0c\u8981\u60f3\u518d\u627e\u4e2a\u6bd4\u4ed6\u795e\u6c14\u7684\u4eba\u5374\u5f88\u96be\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u6b63\u501a\u7740\u7a97\u6237\uff0c\u770b\u7740\u4ed6\u8138\u4e0a\u7684\u8868\u60c5\u4e5f\u4e0b\u77e5\u662f\u60f3\u54ed\uff0c\u8fd8\u662f\u60f3\u7b11\u3002 \u3000\u3000\u53f6\u5f00\u5927\u6b65\u8d70\u8fc7\u53bb\uff0c\u5fae\u7b11\u9053\uff1a\u201c\u65e9\uff01\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u54ac\u7740\u5634\u5507\u9053\uff1a\u201c\u73b0\u5728\u597d\u50cf\u5df2\u4e0d\u65e9\u4e86\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u867d\u7136\u4e0d\u65e9\uff0c\u4e5f\u4e0d\u592a\u665a\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u770b\u6765\u4f60\u4e00\u5b9a\u7761\u5f97\u5f88\u719f\u3002\u201d \u3000\u3000\u53f6\u5f00\u7b11\u9053\uff1a\u201c\u7761\u5f97\u7b80\u76f4\u5c31\u50cf\u6b7b\u4eba\u4e00\u6837\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u82e6\u7b11\u9053\uff1a\u201c\u6211\u5b9e\u5728\u60f3\u4e0d\u5230\u4f60\u5c45\u7136\u771f\u7684\u80fd\u7761\u7740\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u60f3\u7761\u65f6\uff0c\u5c31\u7b97\u5929\u584c\u4e0b\u6765\uff0c\u6211\u4e5f\u7167\u7761\u4e0d\u8bef\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u4e5f\u7761\u7740\u4e86\uff0c\u4e5f\u7761\u5f97\u5f88\u6c89\uff0c\u624b\u91cc\u5374\u8fd8\u662f\u63e1\u7740\u628a\u5200\u3002 \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u5979\u4ec0\u4e48\u65f6\u5019\u7761\u7684\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u5929\u4eae\u4e86\u624d\u7761\u3002\u201d \u3000\u3000\u684c\u4e0a\u6709\u4e2a\u6c64\u7897\uff0c\u662f\u7a7a\u7684\u3002 \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u770b\u6765\u5979\u597d\u50cf\u4e5f\u5403\u4e86\u70b9\u4e1c\u897f\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u5403\u4e86\u4e00\u7897\u7096\u9e21\u9762\uff0c\u5403\u5b8c\u4e86\u624d\u80af\u7761\u3002\u201d \u3000\u3000\u5979\u82e6\u7b11\u7740\uff0c\u53c8\u9053\uff1a\u201c\u5e78\u597d\u5979\u603b\u7b97\u7761\u4e86\uff0c\u5426\u5219\u6211\u4eec\u8fde\u95e8\u90fd\u8fdb\u4e0d\u6765\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u65e0\u8bba\u8c01\u4e00\u8d70\u8fdb\u6765\uff0c\u5979\u5c31\u62ff\u7740\u5200\u8981\u6740\u4eba\u3002\u201d \u3000\u3000\u53f6\u5f00\u7b11\u9053\uff1a\u201c\u4e0d\u7ba1\u600e\u4e48\u6837\uff0c\u80fd\u5403\u5f97\u4e0b\uff0c\u7761\u5f97\u7740\uff0c\u603b\u662f\u597d\u4e8b\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u53f9\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u6211\u5403\u4e5f\u5403\u4e0d\u4e0b\uff0c\u7761\u4e5f\u7761\u4e0d\u7740\uff0c\u6211\u5b9e\u5728\u6ca1\u6709\u4f60\u4eec\u8fd9\u4e48\u597d\u7684\u798f \u6c14\u3002\u201d \u3000\u3000\u5979\u773c\u73e0\u5b50\u8f6c\u4e86\u8f6c\uff0c\u5ffd\u53c8\u95ee\u9053\uff1a\u201c\u4f60\u60f3\u51fa\u6cd5\u5b50\u6765\u6ca1\u6709\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u8fd8\u6ca1\u6709\u5f00\u59cb\u60f3\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4f60\u51c6\u5907\u4ec0\u4e48\u65f6\u5019\u5f00\u59cb\u60f3\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u5230\u4e86\u57ce\u95e8\u518d\u60f3\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u82e6\u7b11\u9053\uff1a\u201c\u4f60\u5012\u662f\u4e00\u70b9\u4e5f\u4e0d\u7740\u6025\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u8239\u5230\u6865\u5934\u81ea\u7136\u76f4\uff0c\u8fd9\u53e5\u8bdd\u6211\u4e00\u76f4\u90fd\u5f88\u76f8\u4fe1\u3002\u2019\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u73b0\u5728\u4f60\u60f3\u5e72\u4ec0 \u4e48\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u60f3\u5403\u4e00\u5927\u7897\u6eda\u70eb\u7684\u7096\u9e21\u9762\u3002\u201d \u7b2c\u4e09\u5341\u4e00\u7ae0\u3000\u6f2b\u5929\u8981\u4ef7>> \u53e4\u9f99\u300a\u4e5d\u6708\u9e70\u98de\u300b \u7b2c\u4e09\u5341\u4e00\u7ae0\u3000\u6f2b\u5929\u8981\u4ef7 \u3000\u3000\u9633\u5149\u666e\u7167\uff0c\u4eca\u5929\u5c45\u7136\u53c8\u662f\u597d\u5929\u6c14\u3002 \u3000\u3000\u53f6\u5f00\u5927\u6b65\u8d70\u51fa\u4e86\u51b7\u9999\u56ed\uff0c\u770b\u6765\u66f4\u795e\u6c14\u5341\u8db3\uff0c\u56e0\u4e3a\u4e00\u5927\u7897\u6eda\u70eb\u7684\u7096\u9e21\u9762\u5df2\u4e0b\u4e86\u809a\u3002 \u3000\u3000\u9762\u662f\u5728\u51b7\u9999\u56ed\u91cc\u5403\u7684\u3002 \u3000\u3000\u4eca\u5929\u4e00\u5927\u65e9\uff0c\u4e0a\u5b98\u5c0f\u4ed9\u5c31\u53eb\u4eba\u5728\u53a8\u623f\u91cc\u5f00\u4e86\u4f19\u3002 \u3000\u3000\u2014\u2014\u6709\u94b1\u80fd\u4f7f\u9b3c\u63a8\u78e8\uff0c\u91d1\u94b1\u5e2e\u65e0\u8bba\u505a\u4ec0\u4e48\u4e8b\uff0c\u90fd\u50cf\u6bd4\u522b\u4eba\u5feb\u5f97\u591a\u3002 \u3000\u3000\u800c\u4e14\u90a3\u7897\u7096\u9e21\u9762\u7684\u6ecb\u5473\uff0c\u7adf\u6bd4\u53f6\u5f00\u6240\u5403\u8fc7\u7684\u4efb\u4f55\u4e00\u7897\u9762\u90fd\u8981\u597d\u5f97\u591a\u3002 \u3000\u3000\u8fd9\u5e76\u4e0d\u662f\u56e0\u4e3a\u4ed6\u809a\u5b50\u7279\u522b\u997f\uff0c\u800c\u662f\u56e0\u4e3a\u505a\u9762\u7684\u5e08\u5085\uff0c\u7adf\u662f\u7279\u5730\u4ece\u676d\u5dde\u594e\u65e0\u9986\u627e\u6765\u7684\u3002 \u3000\u3000\u2014\u2014\u91d1\u94b1\u5e2e\u91cc\u65e0\u8bba\u505a\u4ec0\u4e48\u4e8b\u7684\uff0c\u90fd\u7edd\u5bf9\u662f\u7b2c\u4e00\u6d41\u7684\u4eba\u624d\u3002 \u3000\u3000\u770b\u6765\u8fd9\u5e76\u4e0d\u662f\u5439\u5618\u3002 \u3000\u3000\u53f6\u5f00\u5403\u5149\u4e86\u90a3\u7897\u9762\uff0c\u5fc3\u91cc\u5374\u4e0d\u592a\u8212\u670d\u3002 \u3000\u3000\u4ed6\u8d8a\u6765\u8d8a\u770b\u4e0d\u900f\u91d1\u94b1\u5e2e\u7a76\u7adf\u6709\u591a\u5927\u7684\u529b\u91cf\uff0c\u4ed6\u751a\u81f3\u65e0\u6cd5\u60f3\u8c61\u3002 \u3000\u3000\u8f6c\u8fc7\u51e0\u6761\u8857\uff0c\u5c31\u662f\u5f88\u70ed\u95f9\u7684\u592a\u5e73\u574a\u3002 \u3000\u3000\u53f6\u5f00\u82b1\u4e86\u4e09\u5341\u6587\u94b1\u4e70\u4e86\u4e00\u5927\u5305\u82b1\u751f\uff0c\u53c8\u82b1\u4e86\u4e94\u5341\u6587\u94b1\u4e70\u4e86\u4e24\u6839\u957f\u7af9\u7aff\u3002 \u3000\u3000\u4ed6\u5df2\u5b66\u4f1a\u4e86\u5728\u7d27\u5f20\u7684\u65f6\u5019\u5265\u82b1\u751f\u3002 \u3000\u3000\u624b\u91cc\u6709\u4ef6\u4e8b\u505a\uff0c\u603b\u53ef\u4ee5\u4f7f\u4eba\u7684\u795e\u7ecf\u677e\u5f1b\u4e9b\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u4e70\u7af9\u7aff\u4e8e\u4ec0\u4e48\u5462\uff1f \u3000\u3000\u5ef6\u5e73\u95e8\u5728\u57ce\u5357\u3002 \u3000\u3000\u7a7f\u8fc7\u4e30\u6cfd\u574a\u548c\u5f85\u8d24\u574a\uff0c\u5c31\u662f\u5ef6\u5e73\u95e8\u3002 \u3000\u3000\u2014\u2014\u6bcf\u5929\u4e2d\u5348\uff0c\u4e5f\u4e0d\u77e5\u6709\u591a\u5c11\u4eba\u51fa\u5165\u5ef6\u5e73\u95e8\u3002 \u3000\u3000\u8fd9\u53e5\u8bdd\u4e5f\u4e0d\u5047\u3002 \u3000\u3000\u7ad9\u5728\u5f85\u8d24\u574a\u7684\u8857\u5934\u770b\u8fc7\u53bb\uff0c\u57ce\u95e8\u5185\u5916\uff0c\u4eba\u7fa4\u7199\u6765\u6518\u5f80\uff0c\u5404\u5f0f\u5404\u6837\u7684\u4eba\u90fd\u6709\u3002 \u3000\u3000\u2014\u2014\u4f60\u8fd8\u662f\u4e00\u6837\u770b\u4e0d\u51fa\u5b64\u5cf0\u662f\u8c01\u3002 \u3000\u3000\u53f6\u5f00\u7684\u786e\u770b\u4e0d\u51fa\u3002 \u3000\u3000\u4ed6\u5148\u5750\u5728\u8336\u9986\u91cc\u559d\u4e86\u58f6\u8336\uff0c\u95ee\u4f19\u8ba1\u8981\u4e86\u6839\u7ef3\u5b50\uff0c\u53c8\u8981\u4e86\u5f20\u7ea2\u7eb8\u3002 \u3000\u3000\u7136\u540e\u4ed6\u5c31\u7528\u67dc\u4e0a\u7684\u7b14\u58a8\uff0c\u5728\u7ea2\u7eb8\u4e0a\u5199\u4e86\u516b\u4e2a\u5927\u5b57\u3002 \u3000\u3000\u201c\u9ad8\u4ef7\u51fa\u552e\uff0c\u8d27\u5356\u8bc6\u5bb6\u3002\u201d \u3000\u3000\u867d\u7136\u5df2\u6709\u5f88\u4e45\u672a\u66fe\u63d0\u7b14\uff0c\u8fd9\u516b\u4e2a\u5b57\u5c45\u7136\u5199\u5f97\u8fd8\u4e0d\u9519\u3002 \u3000\u3000\u53f6\u5f00\u7528\u4e24\u6839\u7af9\u7aff\u5c06\u8fd9\u5f20\u7ea2\u7eb8\u5f20\u8d77\u6765\uff0c\u653e\u5728\u57ce\u95e8\u53e3\uff0c\u53c9\u770b\u4e86\u4e24\u904d\uff0c\u5bf9\u81ea\u5df1\u89c9\u5f97\u5f88\u6ee1\u610f\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u8981\u201c\u9ad8\u4ef7\u51fa\u552e\u201d\u7684\u7a76\u7adf\u662f\u4ec0\u4e48\uff1f \u3000\u3000\u96be\u9053\u662f\u4ed6\u81ea\u5df1\uff1f \u3000\u3000\u53f6\u5f00\u5f53\u7136\u4e0d\u4f1a\u51fa\u5356\u81ea\u5df1\u3002 \u3000\u3000\u65e5\u8272\u6e10\u9ad8\uff0c\u5df2\u8fd1\u6b63\u5348\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u4ece\u6000\u91cc\u62ff\u51fa\u4e86\u9752\u94dc\u9762\u5177\u548c\u4e00\uff0c\u5757\u7389\u724c\uff0c\u7528\u7ef3\u5b50\u7cfb\u8d77\u6765\uff0c\u6311\u5728\u7af9\u7aff\u4e0a\u3002 \u3000\u3000\u8fd9\u6b63\u662f\u591a\u5c14\u7532\u7684\u9057\u7269\u3002 \u3000\u3000\u72f0\u72de\u7684\u9752\u94dc\u9762\u5177\uff0c\u5728\u592a\u9633\u4e0b\u95ea\u95ea\u53d1\u7740\u9752\u5149\uff0c\u7389\u724c\u5374\u6676\u83b9\u5706\u6da6\uff0c\u73cd\u8d35\u53ef\u7231\u3002 \u3000\u3000\u8fdb\u51fa\u57ce\u95e8\u7684\u4eba\uff0c\u90fd\u4e0d\u514d\u8981\u591a\u770b\u4ed6\u4e24\u773c\uff0c\u5374\u6ca1\u6709\u4eba\u6765\u95ee\u6d25\u3002 \u3000\u3000\u8fd9\u9762\u5177\u5b9e\u5728\u592a\u53ef\u6015\uff0c\u8c01\u4e5f\u4e0d\u613f\u4e70\u8fd9\u4e48\u6837\u4e2a\u9762\u5177\u5e26\u56de\u53bb\u3002 \u3000\u3000\u53f6\u5f00\u5f53\u7136\u4e5f\u4e0d\u4f1a\u7740\u6025\u3002 \u3000\u3000\u8fd9\u9762\u5177\u53ea\u4e0d\u8fc7\u662f\u4ed6\u7684\u9c7c\u9975\uff0c\u4ed6\u8981\u9493\u7684\u662f\u6761\u5927\u9c7c\u3002 \u3000\u3000\u2014\u2014\u2014\u6761\u4f1a\u5403\u4eba\u7684\u5927\u9c7c\u3002\u5ffd\u7136\u95f4\uff0c\u4e00\u8f86\u9ed1\u6f06\u5927\u8f66\u5728\u524d\u9762\u505c\u4f4f\u3002 \u3000\u3000\u8fd9\u8f86\u8f66\u662f\u4ece\u57ce\u5916\u6765\u7684\uff0c\u672c\u8981\u9a70\u8fc7\u53bb\uff0c\u505c\u5f97\u5f88\u7a81\u7136\u3002 \u3000\u3000\u4e00\u4e2a\u670d\u9970\u5f88\u534e\u4e3d\u3001\u767d\u9762\u5fae\u987b\u7684\u4e2d\u5e74\u4eba\u4f38\u51fa\u5934\u76ef\u7740\u7af9\u7aff\u4e0a\u7684\u9762\u5177\u548c\u7389\u724c\u770b\u4e86\u4e24\u773c\uff0c\u5c31\u63a8\u5f00 \u8f66\u95e8\u8d70\u4e0b\u6765\u3002 \u3000\u3000\u7ec8\u4e8e\u6709\u751f\u610f\u4e0a\u95e8\u4e86\u3002 \u3000\u3000\u53f6\u5f00\u5374\u8fd8\u662f\u5f88\u6c89\u5f97\u4f4f\u6c14\u3002 \u3000\u3000\u8981\u60f3\u9493\u5927\u9c7c\uff0c\u5c31\u4e00\u5b9a\u8981\u6c89\u5f97\u4f4f\u6c14\u3002 \u3000\u3000\u8fd9\u4e2d\u5e74\u4eba\u80cc\u8d1f\u7740\u53cc\u624b\u8d70\u8fc7\u6765\uff0c\u4e00\u53cc\u770b\u6765\u5f88\u7cbe\u660e\u3001\u5f88\u9510\u5229\u7684\u773c\u775b\uff0c\u59cb\u7ec8\u76ef\u5728\u7af9\u7aff\u4e0a\uff0c\u5ffd\u7136 \u95f4\u9053\uff1a\u201c\u8fd9\u662f\u4e0d\u662f\u8981\u5356\u7684\uff1f\u201d \u3000\u3000\u53f6\u5f00\u70b9\u70b9\u5934\u3002 \u3000\u3000\u6307\u4e86\u6307\u7ea2\u7eb8\u4e0a\u7684\u516b\u4e2a\u5b57\u3002 \u3000\u3000\u4e2d\u5e74\u4eba\u6de1\u6de1\u9053\uff1a\u201c\u8fd9\u5757\u7389\u5012\u662f\u6c49\u7389\uff0c\u53ea\u53ef\u60dc\u96d5\u5de5\u5dee\u4e86\u70b9\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u975e\u4f46\u96d5\u5de5\u5dee\u4e86\u4e9b\uff0c\u7389\u4e5f\u4e0d\u597d\u3002\u201d \u3000\u3000\u4e2d\u5e74\u4eba\u9762\u4e0a\u9732\u51fa\u7b11\u5bb9\uff0c\u9053\uff1a\u201c\u4f60\u8fd9\u4eba\u505a\u751f\u610f\u5012\u8fd8\u5f88\u8001\u5b9e\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u8fd9\u4eba\u672c\u6765\u5c31\u8001\u5b9e\u3002\u201d \u3000\u3000\u4e2d\u5e74\u4eba\u9053\uff1a\u201c\u5374\u4e0d\u77e5\u4f60\u60f3\u5356\u4ec0\u4e48\u4ef7\u94b1\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u9ad8\u4ef7\u3002\u201d \u3000\u3000\u4e2d\u5e74\u4eba\u9053\uff1a\u201c\u9ad8\u4ef7\u662f\u591a\u5c11\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u4e0d\u59a8\u5148\u51fa\u4e2a\u4ef7\u94b1\u3002\u201d \u3000\u3000\u4e2d\u5e74\u4eba\u4e0a\u4e0a\u4e0b\u4e0b\u6253\u91cf\u4e86\u4ed6\u51e0\u773c\uff0c\u53c8\u770b\u4e86\u4e00\u773c\u7af9\u7aff\u4e0a\u7684\u7389\u724c\uff0c\u9053\uff1a\u201c\u4e09\u5341\u4e24\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u53f6\u5f00\u7b11\u4e86\u3002 \u3000\u3000\u4e2d\u5e74\u4eba\u4e5f\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u8fd9\u4ef7\u94b1\u6211\u867d\u5df2\u51fa\u5f97\u592a\u9ad8\u4e86\u4e9b\u3002\u53ef\u662f\u541b\u5b50\u4e00\u8a00\uff0c\u6211\u4e5f\u4e0d\u60f3\u518d\u6740\u4f60\u7684 \u4ef7\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4e09\u5341\u4e24\uff1f\u201d \u3000\u3000\u4e2d\u5e74\u4eba\u9053\uff1a\u201c\u5341\u8db3\u5341\u7684\u7eb9\u94f6\u4e09\u5341\u4e24\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u662f\u60f3\u4e70\u54ea\u4e00\u6837\uff1f\u3001\u4e2d\u5e74\u4eba\u9053\uff1a\u201c\u5f53\u7136\u662f\u8fd9\u5757\u7389\u724c\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4e09\u5341\u4e24\u5374\u53ea\u80fd\u4e70\u8fd9\u6839\u7af9\u7aff\u3002\u201d \u3000\u3000\u4e2d\u5e74\u4eba\u8138\u4e0a\u7684\u7b11\u5bb9\u4e00\u4e0b\u5b50\u5c31\u770b\u4e0d\u89c1\u4e86\uff0c\u6c89\u4e0b\u4e86\u8138\uff0c\u9053\uff1a\u201c\u4f60\u60f3\u8981\u591a\u5c11\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4e09\u4e07\u4e24\u3002\u201d \u3000\u3000\u4e2d\u5e74\u4eba\u51e0\u4e4e\u53eb\u4e86\u8d77\u6765\uff1a\u201c\u4e09\u4e07\u4e24\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u5341\u8db3\u5341\u7684\u7eb9\u94f6\u4e09\u4e07\u4e24\u3002\u201d \u3000\u3000\u4e2d\u5e74\u4eba\u5403\u60ca\u5730\u770b\u7740\u4ed6\uff0c\u5c31\u597d\u50cf\u5728\u770b\u75af\u5b50\u3002 \u3000\u3000\u53f6\u5f00\u60a0\u7136\u9053\uff1a\u201c\u8fd9\u5757\u7389\u724c\u7684\u7389\u8d28\u867d\u7136\u4e0d\u592a\u597d\uff0c\u96d5\u5de5\u4e5f\u5f88\u5dee\uff0c\u53ef\u662f\u4f60\u82e5\u8981\u4e70\uff1b\u5c31\u5f97\u51fa\u4e09\u4e07 \u4e24\uff0c\u5c11\u4e00\u6587\u6211\u90fd\u4e0d\u5356\u3002\u201d \u3000\u3000\u4e2d\u5e74\u4eba\u4e00\u53e5\u8bdd\u90fd\u4e0d\u518d\u8bf4\uff0c\u6389\u5934\u5c31\u8d70\u3002 \u3000\u3000\u53f6\u5f00\u53c8\u7b11\u4e86\uff0c\u5728\u65c1\u8fb9\u770b\u70ed\u95f9\u7684\u4eba\u4e5f\u5728\u7b11\u3002 \u3000\u3000\u201c\u4e00\u5757\u7389\u724c\u5c31\u60f3\u5356\u4e09\u4e07\u4e24\uff0c\u8fd9\u5c0f\u5b50\u83ab\u975e\u662f\u7a77\u75af\u4e86\uff1f\u201d \u3000\u3000\u201c\u8fd9\u79cd\u4ef7\u94b1\uff0c\u4e5f\u53ea\u6709\u75af\u5b50\u624d\u4f1a\u6765\u4e70\u3002\u201d \u3000\u3000\u5f53\u7136\u5df2\u6ca1\u70ed\u95f9\u53ef\u770b\uff0c\u90a3\u8f86\u9ed1\u6f06\u5927\u8f66\u5df2\u8f6c\u8fc7\u8857\u89d2\uff0c\u770b\u70ed\u95f9\u7684\u4eba\u4e5f\u5df2\u51c6\u5907\u8d70\u3002 \u3000\u3000\u8c01\u77e5\u8857\u89d2\u540e\u7a81\u7136\u53c8\u4f20\u6765\u9a6c\u5636\u58f0\uff0c\u90a3\u8f86\u9ed1\u6f06\u5927\u8f66\u5ffd\u7136\u53c8\u8d76\u4e86\u56de\u6765\uff0c\u6765\u65f6\u7adf\u6bd4\u53bb\u65f6\u8fd8\u5feb\u3002 \u3000\u3000\u8d76\u8f66\u7684\u9a6c\u97ad\u9ad8\u4e3e\uff0c\u547c\u54e8\u4e00\u58f0\uff0c\u9a6c\u8f66\u53c8\u5728\u524d\u9762\u505c\u4e0b\u3002 \u3000\u3000\u90a3\u4e2d\u5e74\u4eba\u53c8\u63a8\u95e8\u8d70\u4e86\u4e0b\u6765\uff0c\u4e00\u5f20\u767d\u767d\u51c0\u51c0\u7684\u8138\u4e0a\uff0c\u5e26\u7740\u79cd\u5f88\u5947\u602a\u7684\u8868\u60c5\uff0c\u5927\u6b65\u8d70\u5230\u53f6\u5f00 \u9762\u524d\uff0c\u9053\uff1a\u201c\u4f60\u521a\u624d\u8981\u4e09\u4e07\u4e24\uff1f\u201d \u3000\u3000\u53f6\u5f00\u70b9\u70b9\u5934\u3002 \u3000\u3000\u4e2d\u5e74\u4eba\u5ffd\u7136\u4ece\u8eab\u4e0a\u62ff\u51fa\u4e00\u53e0\u94f6\u7968\uff0c\u6570\u4e86\u53c8\u6570\uff0c\u6b63\u662f\u4e09\u5341\u5f20\u3002\u201c\u62ff\u53bb\u3002\u201d \u3000\u3000\u4ed6\u5c45\u7136\u5c06\u8fd9\u4e09\u5341\u5f20\u94f6\u7968\u5168\u90fd\u9012\u8fc7\u53bb\u7ed9\u53f6\u5f00\u3002 \u3000\u3000\u53f6\u5f00\u5374\u6ca1\u6709\u4f38\u624b\u63a5\uff0c\u53cd\u800c\u76b1\u4e86\u7709\uff0c\u95ee\u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e2d\u5e74\u4eba\u9053\uff1a\u201c\u8fd9\u662f\u94f6\u7968\uff0c\u5168\u662f\u4eac\u57ce\u56db\u5927\u6052\u51fa\u6765\u7684\uff0c\u4fdd\u8bc1\u5341\u8db3\u5151\u73b0\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4fdd\u8bc1\u5341\u8db3\u5151\u73b0\uff1f\u201d \u3000\u3000\u4e2d\u5e74\u4eba\u9053\uff1a\u201c\u6211\u59d3\u5b8b\uff0c\u57ce\u897f\u90a3\u5bb6\u4e13\u5356\u7389\u5668\u53e4\u73a9\u7684\u2018\u5341\u5b9d\u658b\u2019\u5c31\u662f\u6211\u5f00\u7684\uff0c\u8fd9\u91cc\u7684\u8857\u574a\u90bb \u5c45\u4eec\uff0c\u60f3\u5fc5\u4e5f\u6709\u4eba\u8ba4\u5f97\u6211\u3002\u201d \u3000\u3000\u201c\u5341\u5b9d\u658b\u201d\u662f\u591a\u5e74\u7684\u91d1\u5b57\u62db\u724c\uff0c\u5b8b\u8001\u677f\u4e5f\u662f\u57ce\u91cc\u6709\u6570\u7684\u5bcc\u7fc1\u3002 \u3000\u3000\u4eba\u4e1b\u4e2d\u7684\u786e\u6709\u4eba\u8ba4\u5f97\u4ed6\u3002 \u3000\u3000\u53ef\u662f\uff0c\u505a\u751f\u610f\u4e00\u5411\u6700\u7cbe\u7684\u5b8b\u8001\u677f\uff0c\u600e\u4e48\u80af\u82b1\u4e09\u4e07\u4e24\u94f6\u5b50\u4e70\u5757\u738b\u724c\uff1f\u83ab\u975e\u4ed6\u4e5f\u75af\u4e86\uff1f \u3000\u3000\u53f6\u5f00\u5374\u504f\u504f\u4e0d\u80af\u4f38\u624b\u53bb\u63a5\uff0c\u53c8\u95ee\u9053\uff1a\u201c\u8fd9\u94f6\u7968\u662f\u591a\u5c11\uff1f\u201d \u3000\u3000\u5b8b\u8001\u677f\u9053\uff1a\u201c\u5f53\u7136\u662f\u4e09\u4e07\u4e24\uff0c\u8fd9\u662f\u4e00\u5343\u4e24\u4e00\u5f20\u7684\u94f6\u7968\uff0c\u4e00\u5171\u4e09\u5341\u5f20\uff0c\u4f60\u4e0d\u59a8\u5148\u70b9\u70b9 \u6570\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4e0d\u5fc5\u70b9\u4e86\uff0c\u6211\u4fe1\u5f97\u8fc7\u4f60\u3002\u201d \u3000\u3000\u5b8b\u8001\u677f\u7ec8\u4e8e\u677e\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u73b0\u5728\u6211\u662f\u4e0d\u662f\u5df2\u53ef\u5c06\u8fd9\u5757\u7389\u724c\u62ff\u8d70\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4e0d\u884c\u3002\u201d \u3000\u3000\u5b8b\u8001\u677f\u6014\u4e86\u6014\uff0c\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u8fd8\u4e0d\u884c\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u56e0\u4e3a\u4ef7\u94b1\u4e0d\u5bf9\u3002\u201d \u3000\u3000\u5b8b\u8001\u677f\u7684\u767d\u8138\u5df2\u53d8\u9ec4\u4e86\uff0c\u5931\u58f0\u9053\uff1a\u201c\u4f60\u521a\u624d\u5c82\u975e\u8bf4\u597d\u7684\u4e09\u4e07\u4e24\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u90a3\u662f\u521a\u624d\u7684\u4ef7\u94b1\u3002\u201d \u3000\u3000\u5b8b\u8001\u677f\u9053\uff1a\u201c\u73b0\u5728\u5462\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u73b0\u5728\u8981\u4e09\u5341\u4e07\u4e24\u3002\u201d \u3000\u3000\u201c\u4e09\u5341\u4e07\u4e24\uff1f\u201d \u3000\u3000\u5b8b\u8001\u677f\u7ec8\u4e8e\u53eb\u4e86\u8d77\u6765\uff0c\u8138\u4e0a\u7684\u8868\u60c5\uff0c\u5c31\u597d\u50cf\u4e00\u6761\u5ffd\u7136\u88ab\u4eba\u8e29\u4f4f\u4e86\u5c3e\u5df4\u7684\u732b\u3002 \u3000\u3000\u65c1\u8fb9\u770b\u70ed\u95f9\u7684\u4eba\uff0c\u8868\u60c5\u4e5f\u8ddf\u4ed6\u5dee\u4e0d\u4e86\u591a\u5c11\u3002 \u3000\u3000\u53f6\u5f00\u8138\u4e0a\u5374\u8fde\u4e00\u70b9\u8868\u60c5\u4e5f\u6ca1\u6709\uff0c\u60a0\u7136\u9053\uff1a\u201c\u8fd9\u5757\u738b\u5e76\u4e0d\u597d\uff0c\u96d5\u5de5\u4e5f\u5dee\uff0c\u53ef\u662f\u73b0\u5728\u65e0\u8bba\u8c01 \u8981\u4e70\uff0c\u90fd\u5f97\u4e09\u5341\u4e07\u4e24\uff0c\u5c11\u4e00\u6587\u4e5f\u4e0d\u5356\u3002\u201d \u3000\u3000\u5b8b\u8001\u677f\u8dfa\u4e86\u8dfa\u811a\uff0c\u626d\u5934\u5c31\u8d70\uff0c\u8d70\u5f97\u5f88\u5feb\uff0c\u53ef\u662f\u8d70\u5230\u9a6c\u8f66\u524d\uff0c\u811a\u6b65\u53cd\u800c\u6162\u4e86\u4e0b\u6765\uff0c\u8138\u4e0a\u53c8 \u9732\u51fa\u90a3\u79cd\u5947\u602a\u7684\u8868\u60c5\uff0c\u7adf\u50cf\u662f\u5728\u6050\u60e7\u3002 \u3000\u3000\u4ed6\u6050\u60e7\u7684\u662f\u4ec0\u4e48\uff1f \u3000\u3000\u4ed6\u81ea\u5df1\u7684\u9a6c\u8f66\u91cc\uff0c\u6709\u4ec0\u4e48\u80fd\u4ee4\u4ed6\u6050\u60e7\u7684\u4e8b\uff1f \u3000\u3000\u6700\u5947\u602a\u7684\u4e00\u70b9\uff0c\u8fd8\u662f\u4e09\u4e07\u4e24\u8fd9\u4ef7\u94b1\u660e\u660e\u5df2\u5c06\u4ed6\u6c14\u8d70\u4e86\uff0c\u4ed6\u4e3a\u4ec0\u4e48\u53bb\u800c\u53c8\u590d\u8fd4\uff1f \u3000\u3000\u53f6\u5f00\u7684\u773c\u775b\u91cc\u5728\u53d1\u7740\u5149\uff0c\u4e00\u76f4\u76ef\u7740\u9a6c\u8f66\u7684\u7a97\u5b50\uff0c\u53ea\u53ef\u60dc\u8f66\u53a2\u91cc\u592a\u6697\uff0c\u4ece\u5916\u9762\u7684\u9633\u5149\u4e0b\u770b \u8fc7\u53bb\uff0c\u4ec0\u4e48\u4e5f\u770b\u4e0d\u89c1\u3002 \u3000\u3000\u5b8b\u8001\u677f\u5df2\u51c6\u5907\u53bb\u62c9\u8f66\u95e8\uff0c\u4f46\u5374\u4e5f\u4e0d\u77e5\u9053\u4e3a\u4e86\u4ec0\u4e48\uff0c\u521a\u4f38\u51fa\u624b\uff0c\u53c8\u6536\u4e86\u56de\u6765\u3002 \u3000\u3000\u8f66\u53a2\u91cc\u5374\u50cf\u662f\u6709\u4e2a\u4eba\u8f7b\u8f7b\u8bf4\u4e86\u53e5\u8bdd\uff0c\u8c01\u4e5f\u542c\u4e0d\u89c1\u4ed6\u8bf4\u7684\u4ec0\u4e48\u3002 \u3000\u3000\u5b8b\u8001\u677f\u5374\u542c\u89c1\u4e86\uff0c\u8138\u4e0a\u7684\u8868\u60c5\uff0c\u5c31\u50cf\u662f\u5ffd\u7136\u53c8\u88ab\u4eba\u8e22\u4e86\u4e00\u811a\u3002 \u3000\u3000\u662f\u8c01\u5728\u8f66\u53a2\u91cc\uff1f \u3000\u3000\u4e3a\u4ec0\u4e48\u4e00\u76f4\u8eb2\u5728\u91cc\u9762\u4e0d\u9732\u9762\uff1f\u4ed6\u5728\u8bf4\u4ec0\u4e48\uff1f \u3000\u3000\u5b8b\u8001\u677f\u542c\u4e86\u8fd9\u53e5\u8bdd\uff0c\u4e3a\u4ec0\u4e48\u4f1a\u5982\u6b64\u5403\u60ca\uff1f \u3000\u3000\u53f6\u5f00\u773c\u775b\u91cc\u5149\u8292\u95ea\u52a8\uff0c\u7ade\u597d\u50cf\u5df2\u627e\u51fa\u4e86\u4e9b\u95ee\u9898\u7684\u7b54\u6848\u3002 \u3000\u3000\u2014\u2014\u73b0\u5728\u8981\u4e70\u8fd9\u5757\u7389\u724c\u7684\uff0c\u5e76\u4e0d\u662f\u5b8b\u8001\u677f\uff0c\u800c\u662f\u8eb2\u5728\u8f66\u53a2\u91cc\u7684\u8fd9\u4e2a\u4eba\u3002 \u3000\u3000\u2014\u2014\u4ed6\u81ea\u5df1\u4e0d\u80af\u51fa\u9762\uff0c\u5c31\u903c\u7740\u5b8b\u8001\u677f\u6765\u4e70\u3002 \u3000\u3000\u5b8b\u8001\u677f\u663e\u7136\u88ab\u4ed6\u5a01\u80c1\u4f4f\u4e86\uff0c\u60f3\u4e0d\u4e70\u90fd\u4e0d\u884c\u3002 \u3000\u3000\u2014\u2014\u8fd9\u4eba\u662f\u7528\u4ec0\u4e48\u624b\u6bb5\u5a01\u80c1\u5b8b\u8001\u677f\u7684\uff1f\u4e3a\u4ec0\u4e48\u4e00\u5b9a\u8981\u4e70\u5230\u8fd9\u5757\u7389\u724c\uff1f \u3000\u3000\u9664\u4e86\u9b54\u6559\u4e2d\u7684\u4eba\u5916\uff0c\u8fd8\u6709\u8c01\u80af\u51fa\u8fd9\u4e48\u9ad8\u7684\u4ef7\u94b1\u6765\u4e70\u4e00\u5757\u7389\u724c\uff1f \u3000\u3000\u2014\u2014\u96be\u9053\u8fd9\u4eba\u5c31\u662f\u5b64\u5cf0\uff1f \u3000\u3000\u5bd2\u51ac\u65f6\u7684\u9633\u5149\uff0c\u5f53\u7136\u4e0d\u4f1a\u592a\u5f3a\u70c8\uff0c\u98ce\u5439\u5728\u4eba\u8eab\u4e0a\uff0c\u8fd8\u662f\u51b7\u5f97\u5f88\u3002 \u3000\u3000\u53ef\u662f\u5b8b\u8001\u677f\u5374\u5df2\u6ee1\u5934\u5927\u6c57\u3002 \u3000\u3000\u4ed6\u7ad9\u5728\u8f66\u95e8\u524d\u53d1\u7740\u6014\uff0c\u4e00\u53cc\u624b\u6296\u4e2a\u4e0d\u505c\uff0c\u5ffd\u7136\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u53c8\u8f6c\u8eab\u8d70\u4e86\u56de\u6765\uff0c\u8138\u4e0a\u7684 \u8868\u60c5\u770b\u6765\u53c8\u50cf\u662f\u4e2a\u88ab\u4eba\u7ed1\u4e0a\u6cd5\u573a\u7684\u6b7b\u5211\u72af\u3002 \u3000\u3000\u53f6\u5f00\u770b\u7740\u4ed6\u8d70\u8fc7\u6765\uff0c\u60a0\u60a0\u9053\uff1a\u201c\u4f60\u73b0\u5728\u5df2\u80af\u51fa\u4e09\u5341\u4e07\u4e24\uff1f\u201d \u3000\u3000\u5b8b\u8001\u677f\u7d27\u63e1\u4e86\u53cc\u62f3\uff0c\u5c45\u7136\u771f\u7684\u70b9\u4e86\u70b9\u5934\uff0c\u6ee1\u5934\u5927\u6c57\u6dcb\u6f13\u800c\u843d\uff0c\u54ac\u7740\u7259\u6068\u6068\u9053\uff1a\u201c\u4e09\u5341\u4e07 \u5c31\u4e09\u5341\u4e07\u3002\u201d \u3000\u3000\u53f6\u5f00\u7b11\u4e86\u3002 \u3000\u3000\u5b8b\u8001\u677f\u5403\u60ca\u5730\u770b\u7740\u4ed6\uff0c\u9053\uff1a\u201c\u4f60\u7b11\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u5728\u7b11\u4f60\u3002\u201d \u3000\u3000\u5b8b\u8001\u677f\u9053\uff1a\u201c\u7b11\u6211\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u5728\u7b11\u4f60\u521a\u624d\u4e3a\u4ec0\u4e48\u4e0d\u4e70\u3002\u201d \u3000\u3000\u5b8b\u8001\u677f\u9053\uff1a\u201c\u73b0\u5728\u2026\u2026\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u73b0\u5728\u7684\u4ef7\u94b1\u8ddf\u521a\u624d\u53c8\u4e0d\u4e00\u6837\u4e86\uff0c\u73b0\u5728\u8981\u4e09\u767e\u4e07\u4e24\uff0c\u5c11\u4e00\u6587\u90fd\u4e0d\u5356\u3002\u201d \u3000\u3000\u5b8b\u8001\u677f\u8df3\u4e86\u8d77\u6765\uff1a\u201c\u4e09\u767e\u4e07\u4e24\uff1f\u201d \u3000\u3000\u8fd9\u6c14\u6d3e\u5f88\u5927\u7684\u5927\u8001\u677f\uff0c\u73b0\u5728\u7adf\u50cf\u662f\u4e2a\u5b69\u5b50\u822c\u5927\u53eb\u5927\u8df3\uff1a\u201c\u4f60\u2026\u2026\u4f60\u2026\u2026\u4f60\u7b80\u76f4\u662f\u4e2a\u5f3a \u76d7\uff0c\u4f60\u597d\u9ed1\u7684\u5fc3\u3002\u201d \u3000\u3000\u53f6\u5f00\u6de1\u6de1\u9053\uff1a\u201c\u4f60\u82e5\u8ba4\u4e3a\u8fd9\u4ef7\u94b1\u592a\u9ad8\uff0c\u53ef\u4ee5\u4e0d\u4e70\uff0c\u6211\u5e76\u6ca1\u6709\u52c9\u5f3a\u4f60\u3002\u201d \u3000\u3000\u5b8b\u8001\u677f\u72e0\u72e0\u5730\u77aa\u7740\u4ed6\uff0c\u5c31\u50cf\u662f\u6068\u4e0d\u5f97\u54ac\u4ed6\u4e00\u53e3\uff0c\u5f20\u5927\u4e86\u5634\u60f3\u8bf4\u4ec0\u4e48\uff0c\u4e00\u53e3\u6c14\u5374\u5df2\u63a5\u4e0d \u4e0a\uff0c\u5ffd\u7136\u4e00\u8dcb\u8dcc\u5012\u5728\u5730\u4e0a\uff0c\u7adf\u88ab\u6c14\u5f97\u660f\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u770b\u70ed\u95f9\u7684\u4eba\u4e5f\u5728\u77aa\u7740\u53f6\u5f00\uff0c\u5927\u5bb6\u90fd\u89c9\u5f97\u8fd9\u4e2a\u4eba\u4e0d\u4f46\u662f\u4e2a\u5f3a\u76d7\uff0c\u7b80\u76f4\u6bd4\u5f3a\u76d7\u7684\u5fc3\u8fd8\u9ed1\u3002 \u3000\u3000\u53f6\u5f00\u5374\u4e00\u70b9\u4e5f\u4e0d\u5728\u4e4e\uff0c\u5ffd\u7136\u5bf9\u7740\u90a3\u8f86\u9a6c\u8f66\u7b11\u9053\uff1a\u201c\u9601\u4e0b\u65e2\u7136\u60f3\u8981\u8fd9\u4e1c\u897f\uff0c\u4e3a\u4ec0\u4e48\u81ea\u5df1\u4e0d \u6765\u4e70\uff1f\u201d \u3000\u3000\u9a6c\u8f66\u91cc\u6ca1\u6709\u52a8\u9759\u3002 \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u9601\u4e0b\u82e5\u80af\u81ea\u5df1\u51fa\u9762\uff0c\u6211\u4e5f\u8bb8\u4e00\u6587\u90fd\u4e0d\u8981\uff0c\u5c31\u5949\u9001\u7ed9\u9601\u4e0b\u3002\u201d \u3000\u3000\u4e00\u76f4\u5168\u65e0\u52a8\u9759\u7684\u9a6c\u8f66\u91cc\uff0c\u5ffd\u7136\u6709\u4eba\u53d1\u51fa\u4e86\u4e00\u58f0\u5200\u950b\u822c\u7684\u51b7\u7b11\u3002 \u3000\u3000\u201c\u771f\u7684\uff1f\u201d \u3000\u3000\u53f6\u5f00\u5fae\u7b11\u7740\u9053\uff1a\u201c\u6211\u662f\u4e2a\u8001\u5b9e\u4eba\uff0c\u6211\u4ece\u4e0d\u8bf4\u5047\u8bdd\u3002\u201d \u3000\u3000\u201c\u597d\uff01\u201d \u3000\u3000\u8fd9\u4e2a\u5b57\u521a\u8bf4\u51fa\u6765\uff0c\u7a81\u542c\u201c\u7a81\u201d\u7684\u4e00\u58f0\u5927\u9707\uff0c\u5d2d\u65b0\u7684\u9ed1\u6f06\u8f66\u53a2\uff0c\u7a81\u7136\u88ab\u649e\u5f97\u56db\u5206\u4e94\u88c2\u3002 \u3000\u3000\u8d76\u8f66\u7684\u51e0\u4e4e\u4e00\u4e2a\u8ddf\u6597\u8dcc\u4e0b\uff0c\u62c9\u8f66\u7684\u9a6c\u6602\u9996\u60ca\u5636\u2014\u2014\u8f66\u53a2\u91cc\u5df2\u51fa\u73b0\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u4e00\u4e2a\u94c1\u5854\u822c\u7684\u5de8\u4eba\uff0c\u8d64\u7740\u4e0a\u8eab\uff0c\u7a7f\u7740\u6761\u5927\u7ea2\u7684\u624e\u811a\u88e4\uff0c\u8170\u4e0a\u7cfb\u7740\u4e00\u6761\u6bd4\u5df4\u638c\u8fd8\u5bbd\u7684\u91d1\u677f \u5e26\uff0c\u4e00\u53cc\u94dc\u94c3\u822c\u7684\u773c\u6674\uff0c\u72e0\u72e0\u5730\u77aa\u7740\u53f6\u5f00\uff0c\u770b\u6765\u6d3b\u6d3b\u50cf\u662f\u4e2a\u521a\u8131\u6a0a\u7b3c\u7684\u5996\u9b54\u6076\u602a\u3002 \u3000\u3000\u4eba\u7fa4\u5927\u4e71\u3002 \u3000\u3000\u8fd9\u5de8\u4eba\u5df2\u63e1\u7d27\u4e86\u53cc\u6bd4\u918b\u74ee\u8fd8\u5927\u7684\u62f3\u5934\uff0c\u4e00\u6b65\u6b65\u5411\u53f6\u5f00\u8d70\u8fc7\u6765\u3002 \u3000\u3000\u65e0\u8bba\u662f\u4eba\u662f\u9a6c\uff0c\u7a81\u7136\u53d7\u5230\u60ca\u9a87\u4e4b\u540e\uff0c\u7b2c\u4e00\u4e2a\u53cd\u5e94\u901a\u5e38\u90fd\u662f\u540c\u6837\u7684\uff1a\u8dd1\u3002 \u3000\u3000\u8dd1\u5f97\u8d8a\u5feb\u8d8a\u597d\uff0c\u8d8a\u8fdc\u8d8a\u597d\u3002 \u3000\u3000\u53ef\u662f\u73b0\u5728\u62c9\u8f66\u7684\u4e24\u5339\u9a6c\u90fd\u6ca1\u6709\u8dd1\u51fa\u53bb\u3002 \u3000\u3000\u53ea\u4e0d\u8fc7\u60ca\u5636\u7740\uff0c\u4eba\u7acb\u800c\u8d77\u3002 \u3000\u3000\u56e0\u4e3a\u8fd9\u5de8\u4eba\u53cd\u624b\u4e00\u62c9\u8f66\u8f95\uff0c\u4e24\u5339\u9a6c\u5c31\u5df2\u8fde\u4e00\u6b65\u90fd\u8dd1\u4e0d\u51fa\u53bb\u3002 \u3000\u3000\u4eba\u7fa4\u867d\u4e71\uff0c\u5374\u6ca1\u6709\u8dd1\uff0c\u56e0\u4e3a\u5927\u5bb6\u90fd\u60f3\u770b\u770b\u8fd9\u4ef6\u4e8b\u7684\u7ed3\u5c40\u3002 \u3000\u3000\u4e0d\u7ba1\u600e\u4e48\u6837\uff0c\u8fd9\u90fd\u53ef\u4ee5\u7b97\u662f\u4ef6\u767e\u5e74\u96be\u9047\u7684\u602a\u4e8b\u3002 \u3000\u3000\u5927\u5bb6\u770b\u7740\u8fd9\u4e2a\u7528\u4e00\u53ea\u624b\u5c31\u53ef\u4ee5\u529b\u633d\u5954\u9a6c\u7684\u5de8\u4eba\uff0c\u518d\u770b\u7740\u53f6\u5f00\uff0c\u65e0\u8bba\u662f\u8c01\u90fd\u53ef\u4ee5\u770b\u5f97\u51fa\uff0c \u5012\u9709\u7684\u4e00\u5b9a\u662f\u53f6\u5f00\u3002 \u3000\u3000\u770b\u6765\u8fd9\u5de8\u4eba\u53ea\u8981\u7528\u4e00\u6839\u624b\u6307\uff0c\u5c31\u53ef\u4ee5\u628a\u53f6\u5f00\u7684\u8111\u888b\u6572\u6241\u3002 \u3000\u3000\u53f6\u5f00\u5374\u7b11\u4e86\u3002 \u3000\u3000\u4ed6\u5fae\u7b11\u7740\uff0c\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u4f60\u6709\u591a\u9ad8\uff1f\u201d \u3000\u3000\u8fd9\u79cd\u65f6\u5019\uff0c\u8fd9\u53e5\u8bdd\u867d\u7136\u95ee\u5f97\u5947\u602a\uff0c\u5de8\u4eba\u8fd8\u662f\u56de\u7b54\u9053\uff1a\u201c\u4e5d\u5c3a\u534a\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4e5d\u5c3a\u534a\u7684\u786e\u5df2\u4e0d\u80fd\u7b97\u77ee\u3002\u201d \u3000\u3000\u5de8\u4eba\u50b2\u7136\u9053\uff1a\u201c\u6bd4\u6211\u518d\u9ad8\u7684\u4eba\uff0c\u8fd9\u4e16\u4e0a\u53ea\u6015\u8fd8\u6ca1\u6709\u51e0\u4e2a\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u5175\u5668\u662f\u8bb2\u7a76\u4e00\u5bf8\u957f\uff0c\u4e00\u5bf8\u5f3a\uff0c\u4f60\u82e5\u662f\u6746\u67aa\uff0c\u4e00\u5b9a\u662f\u6746\u597d\u67aa\u3002\u201d \u3000\u3000\u5de8\u4eba\u9053\uff1a\u201c\u6211\u4e0d\u662f\u67aa\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u8fd8\u6709\u5f88\u591a\u522b\u7684\u4e1c\u897f\uff0c\u4e5f\u662f\u4ee5\u957f\u77ed\u6765\u5206\u8d35\u8d31\u7684\uff0c\u8b6c\u5982\u8bf4\uff0c\u957f\u7684\u7af9\u7aff\u5c31\u6bd4\u77ed\u7684 \u8d35\uff0c\u6240\u4ee5\u4f60\u82e5\u662f\u6839\u7af9\u7aff\uff0c\u4e00\u5b9a\u4e5f\u5f88\u503c\u94b1\u3002\u201d \u3000\u3000\u4ed6\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u4f60\u4e5f\u4e0d\u662f\u7af9\u7aff\u3002\u201d \u3000\u3000\u5de8\u4eba\u9053\uff1a\u201c\u6211\u662f\u4eba\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u5c31\u56e0\u4e3a\u4f60\u662f\u4eba\uff0c\u6240\u4ee5\u5b9e\u5728\u53ef\u60dc\u5f97\u5f88\u3002\u201d \u3000\u3000\u5de8\u4eba\u77aa\u8d77\u773c\uff0c\u9053\uff1a\u201c\u6709\u4ec0\u4e48\u53ef\u60dc\uff1f\u201d \u3000\u3000\u53f6\u5f00\u6de1\u6de1\u9053\uff1a\u201c\u53ea\u6709\u4eba\u662f\u4ece\u4e0d\u4ee5\u957f\u77ed\u8f7b\u91cd\u6765\u5206\u8d35\u8d31\u7684\uff0c\u4e00\u4e2a\u4eba\u7684\u56db\u80a2\u82e5\u662f\u53d1\u8fbe\uff0c\u5934\u8111\u5c31 \u5f80\u5f80\u4f1a\u5f88\u7b80\u5355\uff0c\u6240\u4ee5\u8d8a\u957f\u7684\u4eba\uff0c\u5f80\u5f80\u53cd\u800c\u8d8a\u4e0d\u503c\u94b1\u3002\u201d \u3000\u3000\u5de8\u4eba\u6012\u543c\u4e00\u58f0\uff0c\u5c31\u50cf\u662f\u53ea\u5927\u8c61\u822c\u51b2\u8fc7\u6765\uff0c\u770b\u6765\u4ed6\u6839\u672c\u7528\u4e0d\u7740\u51fa\u4e8e\uff0c\u5c31\u53ef\u4ee5\u628a\u53f6\u5f00\u6d3b\u6d3b\u649e \u6b7b\u3002 \u3000\u3000\u5c31\u7b97\u662f\u68f5\u5927\u6811\uff0c\u4e5f\u53d7\u4e0d\u4e86\u4ed6\u8fd9\u4e00\u649e\u7684\u3002 \u3000\u3000\u53ea\u53ef\u60dc\u53f6\u5f00\u4e5f\u4e0d\u662f\u68f5\u6811\u3002 \u3000\u3000\u8fd9\u5de8\u4eba\u5f53\u7136\u649e\u4e0d\u5012\u4ed6\u2014\u2014\u6ca1\u6709\u4eba\u80fd\u4e00\u4e0b\u5b50\u649e\u5012\u4ed6\u3002 \u3000\u3000\u53ef\u662f\u5c31\u5728\u8fd9\u5de8\u4eba\u649e\u8fc7\u6765\u7684\u65f6\u5019\uff0c\u672c\u6765\u5df2\u6c14\u5f97\u6655\u5012\u4e86\u7684\u5b8b\u8001\u677f\uff0c\u5374\u5ffd\u7136\u4ece\u5730\u4e0a\u7a9c\u4e86\u8d77\u6765\uff0c \u5c31\u50cf\u662f\u4e00\u6839\u7bad\u5c04\u51fa\u4e86\u5f26\u3002\u4ed6\u4e0d\u4f46\u51fa\u624b\u5feb\u5f97\u8981\u547d\uff0c\u51fa\u624b\u7684\u65f6\u5019\u66f4\u8981\u547d\u3002 \u3000\u3000\u53ef\u60dc\u4ed6\u4e95\u6ca1\u6709\u8981\u4e86\u53f6\u5f00\u7684\u547d\u3002 \u3000\u3000\u5de8\u4eba\u4ece\u524d\u9762\u6251\u8fc7\u6765\uff0c\u5b8b\u8001\u677f\u4ece\u53cd\u9762\u53d1\u51fa\u4e86\u8fd9\u81f4\u547d\u7684\u4e00\u51fb\u3002 \u3000\u3000\u53f6\u5f00\u4eba\u5df2\u5230\u4e86\u7af9\u7aff\u4e0a\u3002 \u3000\u3000\u6ca1\u6709\u4eba\u80fd\u60f3\u5230\u5b8b\u8001\u677f\u4f1a\u7a81\u7136\u51fa\u624b\uff0c\u66f4\u6ca1\u6709\u4eba\u60f3\u5f97\u5230\u53f6\u5f00\u80fd\u95ea\u907f\u5f00\u3002 \u3000\u3000\u4ed6\u7adf\u4f3c\u88ab\u98ce\u5439\u4e0a\u7af9\u7aff\u7684\uff0c\u7adf\u4f3c\u5df2\u53d8\u6210\u4e86\u7247\u98de\u4e91\uff0c\u4e00\u7247\u843d\u53f6\u3002 \u3000\u3000\u5b8b\u8001\u677f\u5403\u4e86\u4e00\u60ca\u3002 \u3000\u3000\u2014\u2014\u8fd9\u660e\u660e\u5df2\u662f\u5341\u62ff\u4e5d\u7a33\u7684\u4e00\u51fb\uff0c\u600e\u4e48\u4f1a\u5ffd\u7136\u843d\u7a7a\u7684\uff1f \u3000\u3000\u4ed6\u7684\u5de6\u65f6\u70b9\u5730\uff0c\u53f3\u624b\u5df2\u62bd\u51fa\u67c4\u5200\uff0c\u5200\u5149\u4e00\u95ea\uff0c\u76f4\u524a\u7af9\u7aff\u3002 \u3000\u3000\u5de8\u4eba\u5df2\u5f20\u5f00\u4e86\u4e00\u53cc\u84b2\u6247\u822c\u5927\u7684\u624b\u638c\uff0c\u5728\u4e0b\u9762\u7b49\u7740\u3002 \u3000\u3000\u7af9\u7aff\u4e00\u65ad\uff0c\u7af9\u7aff\u4e0a\u7684\u4eba\u5c31\u8981\u8dcc\u4e0b\u6765\u3002 \u3000\u3000\u53ea\u8981\u53f6\u5f00\u4e00\u8dcc\u4e0b\u6765\uff0c\u5c31\u5f97\u843d\u5165\u8fd9\u5de8\u4eba\u7684\u638c\u63e1\uff0c\u65e0\u8bba\u8c01\u843d\u5165\u4e86\u4ed6\u7684\u638c\u63e1\uff0c\u90fd\u65e0\u7591\u662f\u4ef6\u5f88\u60b2 \u60e8\u7684\u4e8b\u3002 \u3000\u3000\u4ed6\u8981\u634f\u788e\u4e00\u4e2a\u4eba\u7684\u5934\u9885\uff0c\u7b80\u76f4\u6bd4\u5b69\u5b50\u634f\u788e\u6ce5\u5a03\u5a03\u7684\u5934\u8fd8\u7b80\u5355\u3002 \u3000\u3000\u201c\u683c\u201d\u7684\u4e00\u58f0\uff0c\u7af9\u7aff\u6298\u65ad\u3002 \u3000\u3000\u6709\u7684\u4eba\u751a\u81f3\u5df2\u4e0d\u7531\u81ea\u4e3b\u53d1\u51fa\u4e86\u60ca\u547c\u2014\u2014\u53f6\u5f00\u679c\u7136\u5df2\u5411\u8fd9\u5de8\u4eba\u7684\u5b50\u638c\u843d\u4e0b\u3002 \u3000\u3000\u53ea\u542c\u53c8\u662f\u201c\u7830\u201d\u7684\u4e00\u54cd\uff0c\u4e00\u4e2a\u4eba\u5012\u4e86\u4e0b\u53bb\uff0c\u4e24\u4e2a\u4eba\u98de\u4e86\u8d77\u6765\u3002 \u3000\u3000\u5012\u4e0b\u53bb\u7684\u7adf\u662f\u90a3\u5de8\u4eba\uff0c\u98de\u8d77\u6765\u7684\u5374\u662f\u53f6\u5f00\u548c\u5b8b\u8001\u677f\u3002 \u3000\u3000\u53f6\u5f00\u521a\u843d\u4e0b\u6765\uff0c\u7a81\u7136\u53cd\u65f6\u4e00\u649e\uff0c\u819d\u76d6\u548c\u53f3\u65f6\u540c\u65f6\u649e\u5728\u5de8\u4eba\u8eab\u4e0a\u3002 \u3000\u3000\u5de8\u4eba\u5012\u4e0b\u65f6\uff0c\u4ed6\u5df2\u501f\u52bf\u98de\u8d77\u3002 \u3000\u3000\u5b8b\u8001\u677f\u4e5f\u5df2\u8ddf\u7740\u98de\u8d77\uff0c\u5200\u5149\u5982\u957f\u8679\u7ecf\u5929\uff0c\u6025\u524a\u53f6\u5f00\u7684\u8170\u3002 \u3000\u3000\u8c01\u77e5\u53f6\u5f00\u7684\u8170\u7a81\u53c8\u6c34\u86c7\u822c\u4e00\u6446\uff0c\u5de6\u624b\u5df1\u6263\u4f4f\u4e86\u5b8b\u8001\u677f\u7684\u53f3\u8155\u3002 \u3000\u3000\u5200\u843d\u4e0b\uff0c\u659c\u63d2\u5728\u9a6c\u8f66\u4e0a\u3002 \u3000\u3000\u4ed6\u4eec\u4eba\u4e5f\u843d\u5728\u9a6c\u8f66\u4e0a\uff0c\u9a6c\u8f66\u7684\u8f66\u53a2\u867d\u7136\u5df2\u788e\u88c2\uff0c\u5e95\u76d8\u5374\u6ca1\u4e0e\u88c2\u3002 \u3000\u3000\u4e24\u4e2a\u4eba\u540c\u65f6\u8dcc\u5728\u4e0a\u9762\uff0c\u62c9\u8f66\u7684\u9a6c\u53c8\u4e00\u60ca\uff0c\u60ca\u5636\u7740\u72c2\u5954\u51fa\u53bb\u3002 \u3000\u3000\u8fd9\u6b21\u6ca1\u6709\u4eba\u518d\u62c9\u5b83\u4eec\uff0c\u4e5f\u6ca1\u6709\u4eba\u80fd\u62c9\u5f97\u4f4f\u5b83\u4eec\u4e86\u3002 \u3000\u3000\u8f66\u592b\u65e9\u5df2\u5413\u5f97\u4e0d\u77e5\u53bb\u5411\uff0c\u4e24\u5339\u53d7\u4e86\u60ca\u5413\u7684\u5065\u9a6c\uff0c\u4e00\u8f86\u6ca1\u6709\u4eba\u8d76\u7684\u9a6c\u8f66\uff0c\u5728\u8857\u9053\u4e0a\u72c2\u5954\uff0c \u9664\u4e86\u75af\u5b50\u5916\uff0c\u8fd8\u6709\u8c01\u4f1a\u53bb\u6321\u4f4f\u5b83\u7684\u8def\uff0c\u8857\u4e0a\u7684\u4eba\u7eb7\u7eb7\u95ea\u907f\u3002 \u3000\u3000\u5b8b\u8001\u677f\u5728\u8f66\u4e0a\u6253\u4e86\u4e2a\u6eda\uff0c\u8fd8\u60f3\u8df3\u8d77\u6765\uff0c\u53ef\u662f\u4e00\u53ea\u62f3\u5934\u5df2\u5728\u773c\u524d\u7b49\u7740\u4ed6\u3002 \u3000\u3000\u4ed6\u521a\u8df3\u8d77\u6765\uff0c\u5c31\u770b\u89c1\u8fd9\u53ea\u62f3\u5934\uff0c\u63a5\u7740\uff0c\u5c31\u770b\u89c1\u4e86\u65e0\u6570\u9897\u91d1\u661f\uff0c\u8fd9\u6b21\u4ed6\u771f\u7684\u6655\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u53f6\u5f00\u8f7b\u8f7b\u5410\u51fa\u53e3\u6c14\uff0c\u4e0d\u7ba1\u8fd9\u4e2a\u5b8b\u8001\u677f\u7a76\u7adf\u662f\u4e2a\u4ec0\u4e48\u6837\u7684\u4eba\uff0c\u5374\u662f\u4e2a\u5f88\u4e0d\u7b80\u5355\u7684\u4eba\uff0c\u80fd\u53eb \u4ed6\u8eba\u4e0b\u6765\uff0c\u4e5f\u5e76\u4e0d\u662f\u4ef6\u5bb9\u6613\u4e8b\u3002 \u3000\u3000\u5065\u9a6c\u8fd8\u5728\u5f80\u524d\u5954\uff0c\u53f6\u5f00\u5e76\u6ca1\u6709\u62c9\u4f4f\u5b83\u7684\u610f\u601d\uff0c\u53cd\u800c\u5750\u4e0a\u524d\u9762\u8f66\u592b\u7684\u5ea7\u4f4d\uff0c\u6253\u9a6c\u524d\u884c\u3002 \u3000\u3000\u4ed6\u8981\u53bb\u8ffd\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u73b0\u5728\u5df2\u8fc7\u4e86\u6b63\u5348\uff0c\u53f6\u5f00\u5e76\u6ca1\u6709\u627e\u5230\u5e03\u8fbe\u62c9\uff0c\u4ed6\u8981\u8ffd\u7684\u4eba\u662f\u8c01\uff1f \u7b2c\u4e09\u5341\u4e8c\u7ae0\u3000\u98de\u72d0\u5f52\u5929>> \u53e4\u9f99\u300a\u4e5d\u6708\u9e70\u98de\u300b \u7b2c\u4e09\u5341\u4e8c\u7ae0\u3000\u98de\u72d0\u5f52\u5929 \u3000\u3000\u53e4\u8001\u7684\u57ce\u5e02\uff0c\u53e4\u8001\u7684\u8857\u9053\u3002\u8fd9\u6761\u8857\u662f\u7528\u9752\u77f3\u677f\u94fa\u6210\u7684\uff0c\u72ed\u7a84\u800c\u503e\u659c\u3002 \u3000\u3000\u524d\u9762\u6709\u8f86\u9a74\u8f66\uff0c\u8f66\u4e0a\u5806\u6ee1\u4e86\u9e21\u7b3c\uff0c\u7b3c\u5b50\u91cc\u88c5\u6ee1\u4e86\u9e21\uff0c\u663e\u7136\u662f\u4ece\u57ce\u5916\u9001\u9e21\u8fdb\u57ce\u6765\u5356\u7684\u3002 \u3000\u3000\u8d76\u8f66\u7684\u662f\u4e2a\u8001\u5934\u5b50\uff0c\u5582\u9e21\u7684\u662f\u4e2a\u8001\u592a\u5a46\uff0c\u4e24\u4e2a\u4eba\u5934\u53d1\u90fd\u81ea\u4e86\u3002\u8001\u592a\u5a46\u8e72\u5728\u9a74\u8f66\u4e0a\u5582\u9e21\uff0c \u8fde\u8170\u90fd\u76f4\u4e0d\u8d77\u6765\uff0c\u8001\u5934\u5b50\u5750\u5728\u524d\u9762\u8d76\u8f66\uff0c\u8fde\u97ad\u5b50\u90fd\u626c\u4e0d\u8d77\u3002 \u3000\u3000\u6bcf\u4e2a\u57ce\u5e02\u91cc\u90fd\u6709\u4eba\u5403\u9e21\uff0c\u5929\u5929\u90fd\u6709\u4eba\u5403\u9e21\u3002 \u3000\u3000\u65e2\u6709\u4eba\u5403\u9e21\uff0c\u5c31\u6709\u4eba\u5356\u9e21\uff0c\u8fd9\u672c\u662f\u5f88\u5e73\u5e38\u7684\u4e8b\u3002 \u3000\u3000\u8fd9\u8001\u5934\u5b50\u548c\u8001\u592a\u5a46\u770b\u6765\u66f4\u6ca1\u6709\u4e00\u70b9\u7279\u522b\u7684\u5730\u65b9\u3002 \u3000\u3000\u4f46\u53f6\u5f00\u8ffd\u7684\u597d\u50cf\u5c31\u662f\u4ed6\u4eec\u3002 \u3000\u3000\u770b\u89c1\u4ed6\u4eec\u5728\u524d\u9762\uff0c\u53f6\u5f00\u6253\u9a6c\u66f4\u6025\u3002 \u3000\u3000\u8001\u5934\u5b50\u56de\u5934\u770b\u4e86\u4ed6\u4e00\u773c\uff0c\u4e00\u53cc\u660f\u82b1\u7684\u8001\u773c\u91cc\uff0c\u7a81\u7136\u53d1\u51fa\u4e86\u5149\u3002 \u3000\u3000\u8001\u592a\u5a46\u5ffd\u7136\u63d0\u8d77\u4e2a\u9e21\u7b3c\uff0c\u5406\u559d\u4e00\u58f0\uff0c\u628a\u7b3c\u5b50\u91cc\u7684\u9e21\u5168\u90fd\u5012\u51fa\u6765\u3002 \u3000\u3000\u5927\u5927\u5c0f\u5c0f\u7684\u5341\u51e0\u53ea\uff0c\u6709\u7684\u98de\uff0c\u6709\u7684\u53eb\uff0c\u6709\u7684\u8df3\uff0c\u8def\u65c1\u7684\u91ce\u72d7\u4e5f\u51b2\u4e86\u51fa\u6765\uff0c\u53c8\u53eb\u53c8\u8df3\u3002 \u3000\u3000\u9e21\u98de\u72d7\u8df3\uff0c\u8857\u4e0a\u53c8\u4e71\u6210\u4e86\u4e00\u56e2\u3002 \u3000\u3000\u62c9\u8f66\u7684\u9a6c\u53c8\u60ca\u5636\u7740\u4eba\u7acb\u800c\u8d77\uff0c\u7b49\u5230\u53f6\u5f00\u518d\u6253\u9a6c\u51b2\u8fc7\u53bb\u65f6\uff0c\u524d\u9762\u7684\u9a74\u8f66\u5df2\u7ecf\u8f6c\u8fc7\u8857\u89d2\u3002 \u3000\u3000\u53f6\u5f00\u51b7\u7b11\uff0c\u7a81\u7136\u8dc3\u8d77\uff0c\u63a0\u4e0a\u5c4b\u810a\u3002 \u3000\u3000\u4ed6\u5df2\u4e0b\u4e86\u51b3\u5fc3\uff0c\u7edd\u4e0d\u8ba9\u90a3\u8001\u5934\u5b50\u6e9c\u8d70\u3002 \u3000\u3000\u4ed6\u4e3a\u4ec0\u4e48\u4e00\u5b9a\u8981\u8ffd\u4ed6\u4eec\uff1f \u3000\u3000\u4ed6\u4eec\u4e3a\u4ec0\u4e48\u8981\u9003\uff1f \u3000\u3000\u9a74\u8f66\u8fd8\u5728\u8dd1\uff0c\u9e21\u8fd8\u5728\u53eb\uff0c\u8f66\u4e0a\u7684\u4eba\u5374\u5df2\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u8fd9\u662f\u6761\u5f88\u7a84\u7684\u6a2a\u5df7\uff0c\u7a0d\u4e3a\u5927\u4e00\u70b9\u7684\u8f66\u5b50\uff0c\u6839\u672c\u5c31\u8d70\u4e0d\u8fdb\u6765\u3002 \u3000\u3000\u5df7\u5b50\u91cc\u5c45\u7136\u8fde\u4e00\u4e2a\u4eba\u90fd\u6ca1\u6709\uff0c\u4e24\u65c1\u7684\u95e8\u90fd\u5173\u7740\uff0c\u9662\u5b50\u91cc\u4e5f\u6ca1\u6709\u4eba\u3002 \u3000\u3000\u90a3\u8001\u5934\u5b50\u548c\u8001\u592a\u5a46\u600e\u4e48\u4f1a\u5ffd\u7136\u4e0d\u89c1\u4e86\uff1f \u3000\u3000\u4ed6\u4eec\u8eb2\u8fdb\u4e86\u54ea\u4e2a\u9662\u5b50\u91cc\uff1f \u3000\u3000\u53f6\u5f00\u5e76\u6ca1\u6709\u4e00\u5bb6\u5bb6\u53bb\u6211\uff0c\u4ed6\u8fd8\u662f\u53bb\u8ffd\u90a3\u8f86\u6ca1\u6709\u4eba\u7684\u9a74\u8f66\u3002 \u3000\u3000\u7a7f\u8fc7\u6a2a\u5df7\uff0c\u6709\u4e2a\u659c\u5761\u3002 \u3000\u3000\u9a74\u8f66\u867d\u7136\u6ca1\u6709\u4eba\u9a7e\u9a6d\uff0c\u5c45\u7136\u8fd8\u662f\u8f6c\u4e86\u4e2a\u5f2f\uff0c\u624d\u6cbf\u7740\u659c\u5761\u51b2\u4e0b\u53bb\u3002 \u3000\u3000\u53f6\u5f00\u7a81\u7136\u4e00\u63a0\u56db\u4e08\uff0c\u51cc\u7a7a\u7ffb\u8eab\uff0c\u843d\u4e0b\u6765\u65f6\uff0c\u6b63\u597d\u843d\u5728\u9a74\u4e88\u80cc\u4e0a\u3002 \u3000\u3000\u8fc7\u4e86\u659c\u5761\uff0c\u9a74\u8f66\u5c31\u6162\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u53f6\u5f00\u8fd8\u662f\u56db\u5e73\u516b\u7a33\u5730\u5750\u5728\u4e0a\u9762\uff0c\u5ffd\u7136\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u6211\u672c\u6765\u8ba4\u4e0d\u51fa\u4f60\u7684\uff0c\u53ea\u53ef\u60dc\u4f60\u6765\u7684 \u65f6\u5019\u592a\u5de7\u3002\u201d \u3000\u3000\u4ed6\u662f\u5728\u8ddf\u8c01\u8bf4\u8bdd\uff1f \u3000\u3000\u8f66\u4e0a\u6ca1\u6709\u522b\u7684\u4eba\uff0c\u53ea\u6709\u9e21\u548c\u9a74\u5b50\uff0c\u4e00\u4e2a\u6b63\u5e38\u7684\u4eba\uff0c\u662f\u7edd\u4e0d\u4f1a\u8ddf\u9a74\u5b50\u8bf4\u8bdd\u7684\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u5c45\u7136\u53c8\u63a5\u7740\u8bf4\u4e86\u4e0b\u53bb\uff1a\u201c\u4f60\u4eec\u8fdb\u57ce\u7684\u65f6\u5019\uff0c\u6b63\u662f\u6700\u4e71\u7684\u65f6\u5019\uff0c\u6211\u672c\u6765\u4e5f\u4e0d\u4f1a\u770b\u89c1 \u4f60\u4eec\uff0c\u53ef\u60dc\u90a3\u65f6\u6211\u6070\u5de7\u7ad9\u5728\u7af9\u7aff\u4e0a\uff0c\u90a3\u65f6\u5019\u8fdb\u57ce\u6765\u7684\u4eba\uff0c\u4e5f\u4e0d\u6b62\u4f60\u4eec\u4e24\u4e2a\uff0c\u672c\u6765\u6211\u5c31\u7b97\u770b\u89c1 \u4f60\u4eec\uff0c\u4e5f\u7edd\u4e0d\u4f1a\u7591\u5fc3\uff0c\u53ef\u60dc\u4f60\u4eec\u7684\u6837\u5b50\u5374\u8ddf\u522b\u7684\u4eba\u90fd\u4e0d\u4e00\u6837\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u5230\u8fd9\u91cc\uff0c\u9a74\u8f66\u4e0b\u9762\u5ffd\u7136\u6709\u4eba\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u4eec\u7684\u6837\u5b50\u6709\u54ea\u70b9\u8ddf\u522b\u4eba\u4e0d\u4e00\u6837\uff1f\u201d \u3000\u3000\u5345\u5f00\u53c8\u51b7\u7b11\uff1a\u201c\u4f60\u81ea\u5df1\u4e0d\u77e5\u9053\uff1f\u201d \u3000\u3000\u201c\u4e00\u70b9\u4e5f\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u9a74\u8f66\u4e0b\u9762\u7684\u4eba\u9053\uff1a\u201c\u6211\u89c9\u5f97\u6211\u4eec\u7684\u6837\u5b50\u8fde\u4e00\u70b9\u7279\u522b\u7684\u5730\u65b9\u90fd\u6ca1\u6709\u3002\u201d \u3000\u3000\u53f6\u5f00\u5fae\u7b11\u9053\uff1a\u201c\u4e5f\u5c31\uff48\u56e0\u4e3a\u4f60\u4eec\u7684\u6837\u5b50\u8fde\u4e00\u70b9\u7279\u522b\u7684\u5730\u65b9\u90fd\u6ca1\u6709\uff0c\u6240\u4ee5\u624d\u7279\u522b\u3002\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u975e\u4f46\u9a74\u8f66\u4e0b\u9762\u7684\u4eba\u542c\u4e0d\u61c2\uff0c\u9664\u4e86\u4ed6\u81ea\u5df1\u5916\uff0c\u80fd\u542c\u61c2\u7684\u4eba\u53ea\u6015\u8fd8\u4e0d\u591a\u3002 \u3000\u3000\u6240\u4ee5\u4ed6\u53c8\u89e3\u91ca\u7740\u9053\uff1a\u201c\u56e0\u4e3a\u90a3\u65f6\u5019\u522b\u4eba\u7684\u6837\u5b50\u90fd\u5f88\u7279\u522b\u2026\u2026\u201d \u3000\u3000\u90a3\u65f6\u6bcf\u4e2a\u4eba\u90fd\u5f88\u5403\u60ca\uff0c\u5f88\u7d27\u5f20\uff0c\u5f88\u5174\u594b\uff0c\u5c31\u7b97\u521a\u8fdb\u57ce\u6765\u7684\uff0c\u4e5f\u4e0d\u7981\u8981\u77aa\u5927\u4e86\u773c\u775b\uff0c\u5403\u60ca \u5730\u53bb\u770b\u53f6\u5f00\u548c\u90a3\u5de8\u4eba\u3002 \u3000\u3000\u53ef\u662f\u8fd9\u8001\u5934\u4e8e\u548c\u8001\u592a\u5a46\u5374\u597d\u50cf\u4ec0\u4e48\u90fd\u6ca1\u770b\u89c1\uff0c\u751a\u81f3\u8fde\u5934\u90fd\u6ca1\u6709\u56de\u3002 \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u4eec\u8fde\u770b\u90fd\u4e0d\u770b\u4e00\u773c\uff0c\u53ea\u56e0\u4e3a\u4f60\u4eec\u65e9\u5c31\u77e5\u9053\u90a3\u5730\u65b9\u4f1a\u53d1\u751f\u90a3\u4ef6\u4e8b\uff0c\u53ea\u56e0\u4e3a\u90a3 \u4ef6\u4e8b\u539f\u6765\u5c31\u662f\u4f60\u4eec\u5b89\u6392\u7684\uff0c\u597d\u63a9\u62a4\u4f60\u4eec\u8fdb\u57ce\u3002\u201d \u3000\u3000\u9a74\u8f66\u4e0b\u53c8\u6ca1\u6709\u58f0\u97f3\u4e86\u3002 \u3000\u3000\u53f6\u5f00\u4e5f\u4e0d\u518d\u5f00\u53e3\uff0c\u8d76\u7740\u9a74\u5b50\uff0c\u6162\u6162\u5730\u5f80\u524d\u8d70\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u4e0b\u9762\u7684\u4eba\u51b7\u7b11\u7740\u9053\uff1a\u201c\u6211\u770b\u9519\u4e86\u4f60\uff0c\u6211\u60f3\u4e0d\u5230\u4f60\u7adf\u662f\u8fd9\u4e48\u6837\u7684\u4e00\u4e2a \u4eba\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u662f\u600e\u4e48\u6837\u4e2a\u4eba\uff1f\u201d \u3000\u3000\u201c\u662f\u4e2a\u8be5\u6b7b\u7684\u4eba\u3002\u201d \u3000\u3000\u4e00\u53e5\u8bdd\u8fd8\u6ca1\u6709\u8bf4\u5b8c\uff0c\u9a74\u5b50\u7a81\u7136\u60ca\u5636\uff0c\u8df3\u4e86\u8d77\u6765\uff0c\u53f6\u5f00\u4e5f\u8ddf\u7740\u8df3\u4e86\u8d77\u6765\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u540c\u4e00\u5239\u90a3\u95f4\uff0c\u4e24\u4e2a\u4eba\u4ece\u9a74\u8f66\u4e0b\u7a9c\u51fa\uff0c\u4e00\u4e2a\u5f80\u4e1c\uff0c\u4e00\u4e2a\u5f80\u897f\u3002 \u3000\u3000\u4e24\u4e2a\u4eba\u7684\u8eab\u6cd5\u90fd\u6781\u5feb\uff0c\u9a87\u7136\u6b63\u662f\u90a3\u4e24\u4e2a\u8170\u90fd\u76f4\u4e0d\u8d77\u6765\u7684\u8001\u5934\u5b50\u548c\u8001\u592a\u5a46\u3002 \u3000\u3000\u53f6\u5f00\u8ffd\u7684\u662f\u8001\u5934\u5b50\u3002 \u3000\u3000\u8001\u5934\u5b50\u8f7b\u529f\u672c\u6781\u9ad8\uff0c\u672c\u6765\u4e5f\u672a\u5fc5\u80fd\u8ffd\u5f97\u4e0a\u7684\u3002 \u3000\u3000\u4f46\u662f\u73b0\u5728\u4ed6\u8eab\u624b\u5374\u50cf\u662f\u6709\u4e9b\u4e0d\u4fbf\uff0c\u663e\u7136\u53d7\u4e86\u5f88\u91cd\u7684\u4f24\u3002 \u3000\u3000\u96be\u9053\u4ed6\u5c31\u662f\u4f24\u5728\u845b\u75c5\u4f1e\u4e0b\u7684\u5b64\u5cf0\uff1f \u3000\u3000\u53f6\u5f00\u5e76\u6ca1\u6709\u7528\u4ed6\u7684\u5200\u3002 \u3000\u3000\u4e0d\u5230\u4e07\u4e0d\u5f97\u5df2\u65f6\uff0c\u4ed6\u7edd\u4e0d\u7528\u4ed6\u7684\u5200\uff0c\u4ed6\u7684\u5200\u5e76\u4e0d\u662f\u7528\u6765\u6740\u4eba\u7684\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u672c\u4eba\u5c31\u50cf\u662f\u4e00\u67c4\u5200\u3002 \u3000\u3000\u98de\u5200\uff01 \u3000\u3000\u4e09\u4e2a\u8d77\u843d\u540e\uff0c\u4ed6\u5df2\u8ffd\u4e0a\u4e86\u8fd9\u8001\u5934\u5b50\uff0c\u518d\u51cc\u7a7a\u4e00\u7ffb\uff0c\u5df2\u6321\u4f4f\u4e86\u8fd9\u8001\u5934\u5b50\u7684\u53bb\u8def\u3002 \u3000\u3000\u8001\u5934\u5b50\u8fd8\u60f3\u6251\u4e0a\u53bb\uff0c\u8eab\u5b50\u5374\u7a81\u7136\u4e00\u9635\u62bd\u7f29\uff0c\u5c31\u50cf\u662f\u7a81\u7136\u6709\u6761\u770b\u4e0d\u89c1\u7684\u97ad\u5b50\uff0c\u91cd\u91cd\u5730\u62bd\u5728 \u4ed6\u8eab\u4e0a\u3002 \u3000\u3000\u4ed6\u7684\u8138\u662f\u7ecf\u8fc7\u6613\u5bb9\u6539\u626e\u7684\uff0c\u5f53\u7136\u7edd\u4e0d\u4f1a\u6709\u4efb\u4f55\u8868\u60c5\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u773c\u91cc\u5374\u5145\u6ee1\u4e86\u75db\u82e6\u3001\u6124\u6012\u548c\u6028\u6bd2\uff0c\u6b63\u5200\u950b\u822c\u76ef\u7740\u53f6\u5f00\u3002 \u3000\u3000\u8fd9\u6b21\u53f6\u5f00\u5c45\u7136\u6ca1\u6709\u7b11\u3002 \u3000\u3000\u4ed6\u4e5f\u8bb8\u60f3\u7b11\u3002 \u3000\u3000\u5374\u7b11\u4e0d\u51fa\u53e3\uff0c\u56e0\u4e3a\u4ed6\u5df2\u8ba4\u51fa\u8fd9\u4e2a\u4eba\u3002 \u3000\u3000\u201c\u82e5\u4e0d\u662f\u4f60\u53d7\u4e86\u4f24\uff0c\u6211\u672c\u6765\u8ffd\u4e0d\u4e0a\u4f60\u7684\u3002\u201d\u4ed6\u53f9\u606f\u7740\u9053\uff1a\u201c\u4f60\u7684\u8f7b\u529f\uff0c\u679c\u7136\u662f\u5929\u4e0b\u65e0\u53cc \u7684\u8f7b\u529f\u3002\u201d\u8001\u5934\u5b50\u63e1\u7d27\u53cc\u62f3\uff0c\u9053\uff1a\u201c\u4f60\u5df2\u8ba4\u51fa\u4e86\u6211\uff1f\u201d\u53f6\u5f00\u70b9\u70b9\u5934\uff0c\u9eef\u7136\u9053\uff1a\u201c\u83ab\u5fd8\u8bb0\u6211\u4eec \u672c\u6765\u662f\u670b\u53cb\uff0c\u8001\u670b\u53cb\u3002\u201d \u3000\u3000\u8001\u5934\u51b7\u7b11\u9053\uff1a\u201c\u6211\u6ca1\u6709\u4f60\u8fd9\u79cd\u670b\u53cb\u3002\u201d \u3000\u3000\u4ed6\u8fd8\u60f3\u7528\u529b\u62b1\u8d77\u62f3\uff0c\u62b1\u7740\u80f8\uff0c\u53ea\u53ef\u60dc\u4ed6\u4eba\u5df2\u840e\u7f29\u3002 \u3000\u3000\u5c31\u8fde\u4ed6\u773c\u775b\u7684\u5149\u8292\u90fd\u5df2\u6d88\u5931\u3002 \u3000\u3000\u73b0\u5728\u8fd9\u53cc\u773c\u775b\u5c31\u7b97\u8fd8\u50cf\u662f\u4e00\u628a\u5200\uff0c\u4e5f\u5df2\u662f\u628a\u751f\u4e86\u9508\u7684\u5200\u3002 \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u7684\u4f24\u5f88\u91cd\u3002\u201d \u3000\u3000\u8001\u4eba\u54ac\u7d27\u7259\uff0c\u4e0d\u5f00\u53e3\u3002 \u3000\u3000\u53f6\u5f00\u53f9\u9053\uff1a\u201c\u4f60\u65e2\u7136\u53d7\u4e86\u91cd\u4f24\uff0c\u5c31\u4e0d\u8be5\u6ce1\u5728\u70ed\u6c34\u91cc\u7684\u3002\u201d \u3000\u3000\u4ed6\u679c\u7136\u5df2\u8ba4\u51fa\u4e86\u8fd9\u4e2a\u4eba\u3002 \u3000\u3000\u2014\u2014\u9664\u4e86\u201c\u98de\u72d0\u201d\u6768\u5929\u5916\uff0c\u8fd8\u6709\u8c01\u7684\u8f7b\u529f\u80fd\u4ee4\u53f6\u5f00\u4f69\u670d\u3002 \u3000\u3000\u2014\u2014\u4e00\u4e2a\u4eba\u82e5\u60f3\u9690\u7792\u81ea\u5df1\u7684\u4f24\u52bf\uff0c\u8fd8\u6709\u4ec0\u4e48\u5730\u65b9\u80fd\u6bd4\u6c34\u76c6\u91cc\u66f4\u597d\uff1f \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u53ef\u662f\u6c5f\u6e56\u4e2d\u7684\u4e8b\uff0c\u65e0\u8bba\u8c01\u90fd\u96be\u514d\u53d7\u4f24\u7684\uff0c\u8fd9\u5e76\u4e0d\u662f\u89c1\u4e0d\u5f97\u4eba\u7684\u4e8b\uff0c\u4f60\u4e3a\u4ec0\u4e48 \u8981\u7792\u6211\uff1f\u201d \u3000\u3000\u6768\u5929\u9053\uff1a\u201c\u56e0\u4e3a\u2026\u2026\u201d \u3000\u3000\u4ed6\u6ca1\u6709\u8bf4\u4e0b\u53bb\u3002 \u3000\u3000\u8fd9\u662f\u4e0d\u662f\u56e0\u4e3a\u4ed6\u6839\u672c\u6ca1\u6cd5\u5b50\u89e3\u91ca\uff1f\u6839\u672c\u6ca1\u6cd5\u5b50\u8bf4\u4e0b\u53bb\u4e86\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u8981\u7792\u7740\u6211\uff0c\u53ea\u56e0\u4e3a \u4f60\u7b97\u51c6\u6211\u4e00\u5b9a\u5df2\u77e5\u9053\u5b64\u5cf0\u53d7\u4e86\u4f24\uff0c\u4f60\u8981\u7792\u7740\u6211\uff0c\u662f\u56e0\u4e3a\u4f60\u5c31\u662f\u9b54\u6559\u4e2d\u7684\u2018\u5e03\u8fbe\u62c9\u5929\u738b\u2019\u3002\u201d \u3000\u3000\u6768\u5929\u7684\u8eab\u5b50\u5728\u98a4\u6296\uff0c\u5374\u8fde\u4e00\u4e2a\u5b57\u90fd\u6ca1\u6709\u8bf4\u3002 \u3000\u3000\u8fd9\u662f\u4e0d\u662f\u56e0\u4e3a\u4ed6\u81ea\u5df1\u4e5f\u77e5\u9053\u8fd9\u4ef6\u4e8b\u662f\u5426\u8ba4\u4e0d\u4e86\u7684\uff1f \u3000\u3000\u53f6\u5f00\u957f\u957f\u53f9\u606f\uff0c\u9053\uff1a\u201c\u4f60\u7684\u806a\u660e\u6211\u4e5f\u4e00\u76f4\u90fd\u5f88\u4f69\u670d\uff0c\u6240\u4ee5\u6211\u5b9e\u5728\u60f3\u4e0d\u901a\uff0c\u50cf\u4f60\u8fd9\u4e48\u6837\u4e00 \u4e2a\u4eba\uff0c\u4e3a\u4ec0\u4e48\u8981\u5165\u9b54\u6559\uff1f\u201d \u3000\u3000\u6768\u5929\u7ec8\u4e8e\u53d1\u51fa\u4e86\u58f0\u97f3\u3002 \u3000\u3000\u4e00\u79cd\u65e0\u8bba\u4ec0\u4e48\u4eba\u90fd\u6ca1\u6cd5\u5b50\u5f62\u5bb9\u7684\u7b11\u58f0\u3002 \u3000\u3000\u4ed6\u201c\u54af\u54af\u201d\u5730\u7b11\u7740\uff0c\u58f0\u97f3\u8d8a\u6765\u8d8a\u5927\uff0c\u53ef\u662f\u4ed6\u7684\u8eab\u4f53\u5374\u8d8a\u6765\u8d8a\u5c0f\u3002 \u3000\u3000\u4ed6\u7adf\u771f\u7684\u5728\u840e\u7f29\u3002 \u3000\u3000\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u4ed6\u4f3c\u4e4e\u5df2\u771f\u7684\u53d8\u6210\u4e86\u4e2a\u8001\u4eba\u3002 \u3000\u3000\u7a81\u7136\u7b11\u58f0\u65ad\u7edd\u3002 \u3000\u3000\u4ed6\u5012\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u9633\u5149\u4f9d\u65e7\u8f89\u714c\uff0c\u53ef\u662f\u53f6\u5f00\u5df2\u611f\u89c9\u4e0d\u5230\u5b83\u7684\u6e29\u6696\u3002 \u3000\u3000\u6768\u5929\u5f53\u7136\u66f4\u611f\u89c9\u4e0d\u5230\u3002 \u3000\u3000\u4ed6\u662f\u5e26\u7740\u7b11\u800c\u6b7b\u7684\uff0c\u4e00\u4e2a\u4eba\u4e34\u6b7b\u65f6\u8fd8\u80fd\u7b11\uff0c\u5e76\u4e0d\u662f\u4ef6\u5bb9\u6613\u4e8b\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u672c\u6765\u5c31\u6ca1\u6709\u7406\u7531\u7b11\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u7684\u79d8\u5bc6\u82e5\u88ab\u63ed\u7a7f\uff0c\u65e0\u8bba\u4ed6\u662f\u6b7b\u662f\u6d3b\uff0c\u90fd\u4e00\u5b9a\u7b11\u4e0d\u51fa\u3002 \u3000\u3000\u4ed6\u4e3a\u4ec0\u4e48\u8981\u7b11\uff1f\u4e3a\u4ec0\u4e48\u80fd\u7b11\uff1f \u3000\u3000\u53f6\u5f00\u7684\u624b\u51b0\u51b7\uff0c\u989d\u4e0a\u5374\u5728\u6d41\u7740\u6c57\uff0c\u51b7\u6c57\u3002 \u3000\u3000\u4ed6\u542c\u5f97\u51fa\u6768\u5929\u7684\u7b11\u58f0\u4e2d\uff0c\u4eff\u4f5b\u5e26\u7740\u79cd\u5947\u602a\u7684\u8ba5\u8bee\u4e4b\u610f\u3002 \u3000\u3000\u4f46\u4ed6\u731c\u4e0d\u51fa\u90a3\u7a76\u7adf\u662f\u4ec0\u4e48\u610f\u601d\uff1f \u3000\u3000\u65e0\u8bba\u90a3\u662f\u4ec0\u4e48\u610f\u601d\uff0c\u73b0\u5728\u90fd\u5df2\u53d8\u5f97\u6ca1\u6709\u610f\u4e49\uff0c\u4eba\u6b7b\u4e4b\u540e\uff0c\u4ed6\u62e5\u6709\u7684\u4e00\u5207\u5c31\u90fd\u5df2\u968f\u7740\u751f\u547d \u6d88\u5931\u3002 \u3000\u3000\u6b7b\u4eba\u552f\u4e00\u80fd\u5e26\u8d70\u7684\uff0c\u53ea\u6709\u4e00\u6837\uff1a\u79d8\u5bc6\u2014\u2014\u6768\u5929\u662f\u4e0d\u662f\u4e5f\u5e26\u8d70\u4e86\u4ec0\u4e48\u79d8\u5bc6\uff1f \u3000\u3000\u2014\u2014\u6b7b\u4eba\u6709\u65f6\u5019\u4e5f\u80fd\u8bf4\u8bdd\u7684\uff0c\u53ea\u4e0d\u8fc7\u8bf4\u8bdd\u7684\u65b9\u5f0f\u4e0d\u540c\u800c\u5df2\u3002 \u3000\u3000\u2014\u2014\u4ed6\u662f\u4e0d\u662f\u8fd8\u80fd\u5c06\u8fd9\u79d8\u5bc6\u8bf4\u51fa\u6765\uff1f \u3000\u3000\u7528\u4ed6\u7684\u4f24\u53e3\u3002 \u3000\u3000\u4f24\u53e3\u6e83\u70c2\uff0c\u6d41\u51fa\u6765\u7684\u8840\u90fd\u662f\u4e4c\u9ed1\u7684\uff0c\u53ef\u662f\u4f24\u53e3\u5e76\u4e0d\u5927\u3002 \u3000\u3000\u53f6\u5f00\u82e5\u4e0d\u662f\u4eb2\u773c\u770b\u89c1\uff0c\u5b9e\u5728\u5f88\u96be\u76f8\u4fe1\u8fd9\u9488\u5b54\u822c\u5927\u7684\u4e00\u70b9\u4f24\u53e3\uff0c\u5c31\u80fd\u8981\u4e86\u201c\u98de\u72d0\u201d\u6768\u5929\u7684 \u547d\u3002 \u3000\u3000\u98ce\u51b7\u5982\u5200\uff0c\u5c82\u975e\u4e5f\u603b\u662f\u6ca1\u6709\u58f0\u97f3\u7684\u3002 \u3000\u3000\u53f6\u5f00\u542c\u89c1\u7684\u58f0\u97f3\uff0c\u662f\u4e00\u4e2a\u4eba\u7684\u811a\u6b65\u58f0\uff0c\u4ed6\u6ca1\u6709\u56de\u5934\uff0c\u56e0\u4e3a\u4ed6\u77e5\u9053\u6765\u7684\u4eba\u662f\u8c01\u3002 \u3000\u3000\u6765\u7684\u662f\u521a\u624d\u4ece\u53e6\u4e00\u65b9\u5411\u9003\u8d70\u7684\u8001\u592a\u5a46\u3002 \u3000\u3000\u73b0\u5728\u5979\u8eab\u4e0a\u7a7f\u7684\uff0c\u5f53\u7136\u5df2\u4e0d\u662f\u90a3\u5957\u7d27\u8eab\u7684\u9ed1\u7f0e\u5b50\u5c0f\u68c9\u8884\u3002 \u3000\u3000\u5979\u90a3\u5f20\u767d\u751f\u751f\u7684\u6e05\u6c34\u9e2d\u86cb\u8138\uff0c\u73b0\u5728\u5f53\u7136\u5df2\u53d8\u4e86\u6837\u5b50\u3002 \u3000\u3000\u53d8\u4e0d\u4e86\u7684\uff0c\u662f\u5979\u7684\u773c\u775b\uff0c\u90a3\u53cc\u5c0f\u5c0f\u7684\u3001\u5f2f\u5f2f\u7684\uff0c\u7b11\u8d77\u5343\u65f6\u50cf\u94a9\u5b50\u822c\u7684\u773c\u775b\u3002 \u3000\u3000\u6768\u5929\u5c31\u5728\u5979\u9762\u524d\uff0c\u5979\u5374\u8fde\u770b\u90fd\u6ca1\u6709\u770b\u4e00\u773c\u3002 \u3000\u3000\u5979\u5728\u76ef\u7740\u53f6\u5f00\uff0c\u597d\u50cf\u4e00\u4e0b\u5b50\u5c31\u60f3\u628a\u53f6\u5f00\u7684\u9b42\u52fe\u8d70\u3002 \u3000\u3000\u53f6\u5f00\u5377\u8d77\u6b7b\u8005\u7684\u8863\u895f\uff0c\u7ad9\u8d77\u6765\uff0c\u8fc7\u4e86\u5f88\u4e45\uff0c\u624d\u8bf4\u51fa\u4e09\u4e2a\u5b57\uff1a\u201c\u4ed6\u6b7b\u4e86\u3002\u201d \u3000\u3000\u201c\u6211\u770b\u5f97\u51fa\u3002\u201d \u3000\u3000\u201c\u4ed6\u662f\u4f60\u7684\u7537\u4eba\uff1f\u201d \u3000\u3000\u201c\u4ed6\u6d3b\u7740\u65f6\u662f\u7684\u3002\u201d \u3000\u3000\u201c\u81ea\u5df1\u7684\u7537\u4eba\u6b7b\u4e86\uff0c\u65e0\u8bba\u4ec0\u4e48\u6837\u7684\u5973\u4eba\u90fd\u4f1a\u6709\u70b9\u96be\u53d7\u7684\u3002\u201d\u53f6\u5f00\u4e5f\u5728\u76ef\u7740\u5979\uff1a\u201c\u4f46\u6211\u5374 \u770b\u4e0d\u51fa\u4f60\u6709\u4e00\u70b9\u96be\u53d7\u7684\u6837\u5b50\u3002\u201d \u3000\u3000\u201c\u6211\u672c\u5c31\u662f\u5be1\u5987\u3002\u4ed6\u5e76\u4e0d\u662f\u6211\u7b2c\u4e00\u4e2a\u7537\u4eba\uff0c\u6211\u770b\u89c1\u8fc7\u7684\u6b7b\u4eba\uff0c\u4e5f\u4e0d\u6b62\u4ed6\u4e00\u4e2a\u3002\u201d \u3000\u3000\u738b\u5be1\u5987\u9053\uff1a\u201c\u65e0\u8bba\u4ec0\u4e48\u4e8b\uff0c\u53ea\u8981\u4e60\u60ef\u4e86\uff0c\u4e5f\u5c31\u4e0d\u4f1a\u96be\u53d7\u4e86\u3002\u201d \u3000\u3000\u5979\u663e\u7136\u5728\u53f9\u606f\uff0c\u53ef\u662f\u65e0\u8bba\u8c01\u90fd\u542c\u5f97\u51fa\uff0c\u5979\u7684\u53f9\u606f\u58f0\u4e2d\u5e76\u6ca1\u6709\u4ec0\u4e48\u60b2\u4f24\u4e4b\u610f\u3002 \u3000\u3000\u53f6\u5f00\u65e0\u8bdd\u53ef\u8bf4\u3002 \u3000\u3000\u5979\u8bf4\u7684\u81f3\u5c11\u662f\u771f\u8bdd\uff0c\u771f\u8bdd\u603b\u662f\u4ee4\u4eba\u65e0\u6cd5\u53cd\u9a73\u7684\u3002 \u3000\u3000\u738b\u5be1\u5987\u5ffd\u7136\u53c8\u95ee\u9053\uff1a\u201c\u662f\u4f60\u6740\u4e86\u4ed6\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u5e94\u8be5\u77e5\u9053\u4ed6\u65e9\u5df2\u53d7\u4e86\u4f24\u3002\u201d \u3000\u3000\u738b\u5be1\u5987\u9053\uff1a\u201c\u53ef\u662f\u4ed6\u521a\u624d\u8fd8\u662f\u6d3b\u751f\u751f\u7684\u4e00\u4e2a\u4eba\uff0c\u4e3a\u4ec0\u4e48\u73b0\u5728\u5ffd\u7136\u6b7b\u4e86\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u56e0\u4e3a\u4ed6\u53d7\u7684\u4f24\u5e76\u4e0d\u91cd\uff0c\u4e2d\u7684\u6bd2\u5374\u5f88\u91cd\u3002]\u738b\u5be1\u5987\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4ed6\u867d\u7136\u7528\u836f\u7269\u52c9\u5f3a\u538b\u5236\u4f4f\u6bd2\u6027\uff0c\u53ef\u662f\u4e00\u5954\u8dd1\u7528\u529b\uff0c\u6bd2\u52bf\u5c31\u53d1\u4f5c\u4e86\u3002]\u738b\u5be1\u5987 \u5ffd\u53c8\u51b7\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u4ed6\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u53f6\u5f00\u5f53\u7136\u77e5\u9053\u3002 \u3000\u3000\u738b\u5be1\u5987\u9053\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u2018\u98de\u72d0\u2019\u6768\u5929\u4e0d\u4f46\u8f7b\u529f\u9ad8\uff0c\u800c\u4e14\u8fd8\u6709\u5f88\u591a\u522b\u7684\u672c\u4e8b\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6cbb\u4f24\u7597\u6bd2\uff0c\u4e5f\u662f\u4ed6\u7684\u4e13\u957f\u4e4b\u4e00\u3002\u201d \u3000\u3000\u738b\u5be1\u5987\u9053\uff1a\u201c\u4f46\u662f\u4f60\u73b0\u5728\u5374\u8fd8\u8981\u8bf4\u4ed6\u662f\u88ab\u6bd2\u6b7b\u7684\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4e16\u4e0a\u53ea\u8981\u6709\u4e00\u79cd\u4ed6\u4e0d\u80fd\u89e3\u7684\u6bd2\uff0c\u4ed6\u5c31\u53ef\u80fd\u7834\u6bd2\u6b7b\u3002\u201d \u3000\u3000\u738b\u5be1\u5987\u9053\uff1a\u201c\u771f\u7684\u4e0d\u662f\u4f60\u6740\u4e86\u4ed6\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u4ece\u4e0d\u6740\u670b\u53cb\u3002\u201d \u3000\u3000\u738b\u5be1\u5987\u9053\uff1a\u201c\u4ed6\u771f\u7684\u662f\u4f60\u7684\u670b\u53cb\uff1f\u201d \u3000\u3000\u53f6\u5f00\u957f\u957f\u53f9\u606f\uff0c\u9eef\u7136\u9053\uff1a\u201c\u53ea\u8981\u4ed6\u505a\u8fc7\u6211\u4e00\u5929\u670b\u53cb\uff0c\u5c31\u6c38\u8fdc\u662f\u6211\u7684\u670b\u53cb\u3002\u201d \u3000\u3000\u738b\u5be1\u5987\u773c\u73e0\u5b50\u8f6c\u4e86\u8f6c\uff0c\u5ffd\u7136\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u6211\u4e5f\u542c\u8bf4\u8fc7\u4f60\u662f\u4ed6\u7684\u670b\u53cb\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u738b\u5be1\u5987\u9053\uff1a\u201c\u6211\u8fd8\u542c\u8fc7\u4e00\u53e5\u8bdd\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4ec0\u4e48\u8bdd\uff1f\u201d \u3000\u3000\u738b\u5be1\u5987\u9053\uff1a\u201c\u670b\u53cb\u59bb\uff0c\u4e0d\u53ef\u620f\uff0c\u8981\u620f\u670b\u53cb\u59bb\uff0c\u8981\u7b49\u670b\u53cb\u6b7b\u540e\u620f\u3002\u201d \u3000\u3000\u5979\u7b11\u65f6\u773c\u6674\u5a9a\u5982\u65b0\u6708\uff1a\u201c\u8fd9\u53e5\u8bdd\u6211\u597d\u50cf\u4e5f\u542c\u4f60\u8bf4\u8fc7\u3002\u201d \u3000\u3000\u53f6\u5f00\u82e6\u7b11\u3002 \u3000\u3000\u738b\u5be1\u5987\u9053\uff1a\u201c\u73b0\u5728\u4ed6\u5df2\u6b7b\u4e86\uff0c\u6211\u8fd8\u6d3b\u7740\uff0c\u4f60\u2026\u2026\u201d \u3000\u3000\u5979\u6ca1\u6709\u8bf4\u4e0b\u53bb\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u5979\u7684\u610f\u601d\uff0c\u53ea\u8981\u662f\u7537\u4eba\uff0c\u90fd\u5e94\u8be5\u660e\u767d\u7684\u3002 \u3000\u3000\u53f6\u5f00\u770b\u7740\u4ed6\uff0c\u5ffd\u7136\u9053\uff1a\u201c\u4f60\u89c1\u8fc7\u97e9\u8d1e\u6ca1\u6709\uff1f\u201d \u3000\u3000\u738b\u5be1\u5987\u5f53\u7136\u89c1\u8fc7\u3002 \u3000\u3000\u5979\u5e26\u7740\u7b11\u9053\uff1a\u201c\u90a3\u5c0f\u5b50\u672c\u6765\u4e5f\u5728\u6253\u6211\u7684\u4e3b\u610f\uff0c\u53ef\u60dc\u6211\u4e00\u770b\u89c1\u4ed6\u5c31\u60f3\u5410\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u738b\u5be1\u5987\u9053\uff1a\u201c\u56e0\u4e3a\u4ed6\u7684\u9f3b\u5b50\u3002\u201d \u3000\u3000\u53f6\u5f00\u4e5f\u7b11\u4e86\u3002 \u3000\u3000\u738b\u5be1\u5987\u9053\uff1a\u201c\u4ed6\u90a3\u9f3b\u5b50\u770b\u8d77\u6765\u7b80\u76f4\u5c31\u5ac1\u662f\u70c2\u8304\u5b50\u3002\u201d \u3000\u3000\u53f6\u5f00\u5fae\u7b11\u7740\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u4ed6\u90a3\u9f3b\u5b50\u600e\u4e48\u4f1a\u53d8\u6210\u90a3\u6837\u5b50\u7684\uff1f\u201d \u3000\u3000\u738b\u5be1\u5987\u9053\uff1a\u201c\u662f\u4e0d\u662f\u88ab\u4eba\u6253\u7684\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u5bf9\u4e86\u3002\u201d \u3000\u3000\u738b\u5be1\u5987\u9053\uff1a\u201c\u4f60\u77e5\u9053\u662f\u88ab\u8c01\u6253\u7684\uff1f\u201d \u3000\u3000\u53f6\u5f00\u7b11\u9053\uff1a\u201c\u6211\u4e0d\u4f46\u77e5\u9053\uff0c\u800c\u4e14\u77e5\u9053\u5f97\u6bd4\u8c01\u90fd\u6e05\u695a\u3002]\u738b\u5be1\u5987\u4e5f\u77e5\u9053\u4e86\uff0c\u7b11\u9053\uff1a\u201c\u4e00\u5b9a \u5c31\u662f\u88ab\u4f60\u6253\u7684\uff0c\u5bf9\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u5bf9\u3002\u201d \u3000\u3000\u4ed6\u6162\u6162\u5730\u63a5\u7740\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u73b0\u5728\u6700\u597d\u8d76\u5feb\u8d70\uff0c\u5e26\u7740\u4f60\u7684\u7537\u4eba\u8d70\uff0c\u597d\u597d\u7684\u66ff\u4ed6\u57cb\u846c\u3002\u201d \u3000\u3000\u738b\u5be1\u5987\u5f88\u610f\u5916\uff1a\u201c\u4f60\u8981\u6211\u8d70\uff0c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u56e0\u4e3a\u73b0\u5728\u6211\u7684\u624b\u5f88\u75d2\uff0c\u4f60\u82e5\u518d\u4e0d\u8d70\uff0c\u6211\u4fdd\u8bc1\u4f60\u7684\u9f3b\u5b50\u5f88\u5feb\u5c31\u4f1a\u53d8\u5f97\u8ddf\u97e9\u8d1e\u4e00 \u6837\u3002\u201d \u3000\u3000\u738b\u5be1\u5987\u6ca1\u6709\u518d\u8bf4\u8bdd\uff0c\u8fde\u4e00\u4e2a\u5b57\u90fd\u6ca1\u6709\u518d\u8bf4\u3002 \u3000\u3000\u5979\u81f3\u5c11\u8fd8\u7b97\u5f88\u8bc6\u76f8\u3002 \u3000\u3000\u7b49\u5979\u628a\u6768\u5929\u7684\u5c38\u4f53\u8f7d\u4e0a\u9a74\u8f66\uff0c\u53f6\u5f00\u624d\u6cbf\u7740\u539f\u6765\u7684\u8def\u8d70\u56de\u53bb\uff0c\u4ed6\u8d70\u5f97\u5f88\u6162\u3002 \u3000\u3000\u8d70\u51fa\u6a2a\u5df7\uff0c\u8d70\u4e0a\u5927\u8857\uff0c\u524d\u9762\u56f4\u7740\u4e00\u5806\u4eba\uff0c\u56f4\u7740\u4e00\u8f86\u7834\u9a6c\u8f66\u3002 \u3000\u3000\u5b8b\u8001\u677f\u5df2\u6b7b\u5e84\u9a6c\u8f66\u4e0a\uff0c\u8eab\u4e0a\u53ea\u6709\u4e00\u70b9\u9488\u5b54\u822c\u5927\u7684\u4f24\u53e3\u3002 \u3000\u3000\u4f24\u53e3\u5728\u4ed6\u7684\u7709\u5fc3\u3002 \u3000\u3000\u53f6\u5f00\u6324\u8fdb\u4eba\u4e1b\uff0c\u770b\u4e86\u770b\uff0c\u53c8\u6324\u51fa\u6765\uff0c\u8138\u4e0a\u5c45\u7136\u5e76\u6ca1\u6709\u5403\u60ca\u7684\u6837\u5b50\u3002 \u3000\u3000\u8fd9\u4ef6\u4e8b\u7adf\u4f3c\u65e9\u5df2\u5728\u4ed6\u610f\u6599\u4e4b\u4e2d\u3002 \u3000\u3000\u4ed6\u53c8\u8d70\u56de\u5ef6\u5e73\u95e8\uff0c\u90a3\u5de8\u4eba\u4e5f\u6b7b\u4e86\uff0c\u4e5f\u540c\u6837\u53ea\u6709\u4e00\u70b9\u4f24\u53e3\u3002 \u3000\u3000\u4e00\u70b9\u6bd4\u9488\u5b54\u5927\u4e0d\u4e86\u591a\u5c11\u7684\u4f24\u53e3\uff0c\u5374\u5df2\u5c06\u94c1\u5854\u822c\u7684\u5de8\u4eba\u7f6e\u4e4b\u4e8e\u6b7b\u5730\u3002 \u3000\u3000\u56f4\u7740\u4ed6\u770b\u7684\u4eba\u66f4\u591a\u3002 \u3000\u3000\u5219\u5f00\u6b63\u60f3\u6084\u6084\u5730\u6e9c\u8d70\uff0c\u5ffd\u7136\u95ee\uff0c\u4e00\u4e2a\u4eba\u63ea\u4f4f\u4e86\u4ed6\u7684\u8863\u895f\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u8d70\u4e0d\u4e86\u7684\u3002\u201d \u3000\u3000\u4e00\u4e2a\u4eba\u65e0\u8bba\u6709\u6ca1\u6709\u505a\u4e8f\u5fc3\u4e8b\uff0c\u82e5\u662f\u5ffd\u7136\u88ab\u4e2a\u5b98\u5dee\u4e00\u628a\u63ea\u4f4f\u4e86\u8863\u895f\uff0c\u90fd\u96be\u514d\u8981\u5413\u4e00\u8df3\u3002 \u3000\u3000\u6380\u4f4f\u53f6\u5f00\u8863\u895f\u7684\u8fd9\u4e2a\u4eba\uff0c\u6b63\u662f\u4e2a\u6234\u7740\u7ea2\u7f28\u5e3d\u3001\u63d0\u7740\u77ed\u68cd\u7684\u6355\u5feb\u3002 \u3000\u3000\u65c1\u8fb9\u5df2\u6709\u4eba\u5728\u53eb\uff1a\u201c\u521a\u624d\u8ddf\u5b8b\u8001\u677f\u6253\u67b6\u7684\u5c31\u662f\u4ed6\u3002\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\u662f\u4ed6\u3002\u201d \u3000\u3000\u8fd9\u6355\u5feb\u53c8\u6263\u4f4f\u4e86\u53f6\u5f00\u7684\u624b\u8155\uff0c\u7528\u7684\u5c45\u7136\u662f\u5c0f\u64d2\u62ff\u624b\u3002 \u3000\u3000\u4ed6\u51b7\u7b11\u7740\u9053\uff1a\u201c\u4f60\u4f24\u4e86\u4e24\u6761\u4eba\u547d\uff0c\u5c45\u7136\u8fd8\u6562\u9732\u9762\uff0c\u4f60\u7684\u80c6\u5b50\u5012\u4e0d\u5c0f\u3002]\u53f6\u5f00\u5f53\u7136\u5f88\u5bb9\u6613 \u5c31\u80fd\u7529\u8131\u8fd9\u53ea\u624b\uff0c\u5bf9\u201d\u4e03\u5341\u4e8c\u8def\u5c0f\u64d2\u62ff\u624b\u201d\uff0c\u4ed6\u81f3\u5c11\u6709\u4e00\u767e\u56db\u5341\u56db\u79cd\u7834\u6cd5\u3002\u53ef\u662f\u4ed6\u4e95\u6ca1\u6709\u8fd9 \u4e48\u6837\u505a\u3002 \u3000\u3000\u4ed6\u5e76\u4e0d\u662f\u6015\u8fd9\u4e2a\u6355\u5feb\uff0c\u800c\u662f\u5c0a\u656c\u3002 \u3000\u3000\u4e0d\u7ba1\u8fd9\u6355\u5feb\u662f\u4e2a\u4ec0\u4e48\u6837\u7684\u4eba\uff0c\u4ed6\u90fd\u540c\u6837\u5c0a\u656c\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u5c0a\u656c\u7684\u5e76\u4e0d\u662f\u8fd9\u4e2a\u4eba\uff0c\u800c\u662f\u8fd9\u4e2a\u4eba\u6240\u4ee3\u8868\u7684\u6cd5\u5f8b\u3002 \u3000\u3000\u4ed6\u751a\u81f3\u8fde\u5206\u8fa9\u90fd\u6ca1\u6709\u5206\u8fa9\u3002 \u3000\u3000\u8fd9\u79cd\u4e8b\u672c\u6765\u5c31\u4e0d\u662f\u8fd9\u79cd\u6355\u5feb\u80fd\u4e86\u89e3\u7684\uff0c\u4ed6\u6839\u672c\u6ca1\u6cd5\u5b50\u5206\u8fa9\u3002 \u3000\u3000\u8fd9\u91cc\u4e5f\u4e0d\u662f\u8bf4\u8bdd\u7684\u5730\u65b9\u3002 \u3000\u3000\u8fd9\u6355\u5feb\u5df2\u62bc\u7740\u4ed6\u4e0a\u4e86\u8f86\u9a6c\u8f66\uff0c\u5389\u58f0\u9053\uff1a\u201c\u4eba\u547d\u5173\u5929\uff0c\u738b\u6cd5\u5982\u7089\uff0c\u4f60\u5c31\u7b97\u6709\u5929\u5927\u7684\u80c6\u5b50\uff0c \u6211\u4e5f\u4e0d\u6015\u4f60\u4e0d\u62db\u3002\u201d \u3000\u3000\u53f6\u5f00\u5c31\u8ddf\u7740\u4ed6\u4e0a\u4e86\u9a6c\u8f66\uff0c\u7b49\u5230\u8f66\u5b50\u5f00\u59cb\u8d70\uff0c\u624d\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u4f60\u7a76\u7adf\u60f3\u628a\u6211\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u6355\u5feb\u9053\uff1a\u201c\u4e0d\u7ba1\u600e\u4e48\u6837\uff0c\u5148\u5173\u8d77\u6765\u518d\u8bf4\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u7136\u540e\u5462\uff1f\u201d \u3000\u3000\u6355\u5feb\u9053\uff1a\u201c\u7136\u540e\u518d\u7528\u4e0a\u597d\u7684\u4eba\u53c2\u7096\u4e00\u53ea\u9e21\uff0c\u505a\u56db\u4e94\u6837\u7cbe\u81f4\u7684\u4e0b\u9152\u83dc\uff0c\u70eb\u51e0\u58f6\u9648\u5e74\u7684\u7af9\u53f6 \u9752\uff0c\u8bf7\u4f60\u8fde\u9152\u83dc\u4e00\u8d77\u5403\u4e0b\u53bb\u3002\u201d \u3000\u3000\u201c\u4ed6\u201d\u7684\u773c\u775b\u5ffd\u7136\u5145\u6ee1\u7b11\u610f\uff0c\u58f0\u97f3\u4e5f\u53d8\u5f97\u6625\u98ce\u822c\u6e29\u67d4\u3002 \u3000\u3000\u53f6\u5f00\u53f9\u4e86\u53e3\u6c14\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u73b0\u5728\u6211\u603b\u7b97\u660e\u767d\u4e86\uff0c\u539f\u6765\u4f60\u60f3\u80c0\u6b7b\u6211\u3002\u201d \u7b2c\u4e09\u5341\u4e09\u7ae0\u3000\u60c5\u6df1\u4f3c\u6d77>> \u53e4\u9f99\u300a\u4e5d\u6708\u9e70\u98de\u300b \u7b2c\u4e09\u5341\u4e09\u7ae0\u3000\u60c5\u6df1\u4f3c\u6d77 \u3000\u3000\u7528\u4eba\u53c2\u7096\u7684\u9e21\uff0c\u8fd8\u5728\u5192\u7740\u70ed\u6c14\u3002 \u3000\u3000\u51e0\u6837\u4e0b\u9152\u83dc\u662f\u4e00\u5c0f\u789f\u7092\u732a\u5934\u8089\uff0c\u4e00\u789f\u871c\u7099\u817f\uff0c\u4e00\u789f\u6cb9\u7206\u9c9c\u867e\uff0c\u4e00\u789f\u65b0\u5207\u51ac\u7b0b\uff0c\u4e00\u789f\u98ce\u9e21 \u62cc\u9c7c\uff0c\u4e00\u789f\u5e72\u7206\u9ccd\u8721\u3002 \u3000\u3000\u7af9\u53f6\u9752\u4e5f\u6e29\u5f97\u6070\u5230\u597d\u5904\u3002 \u3000\u3000\u5317\u65b9\u4eba\u559d\u9152\u4e5f\u5f97\u6709\u5f88\u591a\u8bb2\u7a76\uff0c\u4e0d\u4f46\u9ec4\u9152\u3001\u82b1\u96d5\u6e29\u70ed\u4e86\u559d\uff0c\u767d\u5e72\u3001\u7af9\u53f6\u9752\u4e5f\u4e00\u6837\u3002 \u3000\u3000\u53f6\u5f00\u5df2\u4e09\u676f\u4e0b\u809a\uff0c\u6df1\u591c\u4e2d\u7684\u6fc0\u6218\uff0c\u4f24\u53e3\u4e2d\u7684\u6d53\u8840\uff0c\u4eff\u4f5b\u90e8\u5df2\u79bb\u4ed6\u5f88\u8fdc\u4e86\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u6b63\u5728\u770b\u7740\u4ed6\uff0c\u62bf\u7740\u5634\u7b11\u9053\uff1a\u201c\u8981\u80c0\u6b7b\u4f60\uff0c\u597d\u50cf\u5e76\u4e0d\u5bb9\u6613\u3002\u201d \u3000\u3000\u53f6\u5f00\u6ca1\u6709\u5f00\u53e3\uff0c\u4ed6\u7684\u5634\u6ca1\u7a7a\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4f60\u7684\u83dc\u867d\u7136\u5403\u5f97\u5f88\u5feb\uff0c\u9152\u5374\u559d\u5f97\u592a\u5c11\u3002\u201d \u3000\u3000\u53f6\u5f00\u7528\u773c\u775b\u8198\u4e86\u5979\u4e00\u773c\uff0c\u9053\uff1a\u201c\u4f60\u7a76\u7adf\u662f\u60f3\u80c0\u6b7b\u6211\uff0c\u8fd8\u662f\u60f3\u704c\u9189\u6211\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6211\u672c\u6765\u662f\u60f3\u5413\u6b7b\u4f60\u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4f60\u660e\u660e\u77e5\u9053\u90a3\u9644\u8fd1\u7684\u4eba\u5168\u90fd\u770b\u89c1\u4f60\u8ddf\u5b8b\u8001\u677f\u4ea4\u624b\uff0c\u5c45\u7136\u8fd8\u6562\u5728\u90a3\u91cc\u6e9c\u6765 \u6e9c\u53bb\uff0c\u4f60\u7684\u80c6\u4e8e\u4e5f\u672a\u514d\u592a\u5927\u4e86\u4e9b\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u6015\u6211\u88ab\u4eba\u8ba4\u51fa\u6765\uff0c\u6349\u5c06\u5b98\u91cc\u53bb\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4e0d\u7ba1\u600e\u4e48\u6837\uff0c\u591a\u4e00\u4e8b\u4e0d\u5982\u5c11\u4e00\u4e8b\uff0c\u4f60\u4f55\u5fc5\u53bb\u60f9\u90a3\u79cd\u9ebb\u70e6\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff09\u201c\u4f60\u4f2f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6015\u9047\u89c1\u771f\u6355\u5feb\u3002\u201d \u3000\u3000\u53f6\u5f00\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u60f3\u4e0d\u5217\u4e16\u4e0a\u5c45\u7136\u4e5f\u6709\u80fd\u4f7f\u4e0a\u5b98\u5e2e\u4e3b\u5bb3\u6015\u7684\u4e8b\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u4e5f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u5bb3\u6015\u7684\u4e8b\u53c8\u4f55\u6b62\u8fd9\u4e00\u4ef6\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u8fd8\u6015\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u8fd8\u6015\u53f6\u5e2e\u4e3b\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u53f6\u5e2e\u4e3b\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u5ae3\u7136\u9053\uff1a\u201c\u82b1\u751f\u5e2e\u7684\u53f6\u5e2e\u4e3b\u662f\u8c01\uff0c\u96be\u9053\u8fde\u4f60\u81ea\u5df1\u90fd\u5fd8\u4e86\uff1f\u201d \u3000\u3000\u53f6\u5f00\u5927\u7b11\u3002 \u3000\u3000\u4ed6\u5927\u7b11\u7740\u4e3e\u676f\uff0c\u4e00\u996e\u800c\u5c3d\uff0c\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u4ee5\u4f60\u770b\uff0c\u662f\u82b1\u751f\u597d\uff0c\u8fd8\u662f\u91d1\u94b1\u597d\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\uff0c\u6211\u53ea\u77e5\u9053\u4e00\u6587\u94b1\u5c31\u53ef\u4ee5\u4e70\u4e00\u5927\u5806\u82b1\u751f\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u53ef\u662f\u82b1\u751f\u81f3\u5c11\u6709\u4e00\u70b9\u6bd4\u91d1\u94b1\u5f3a\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u54ea\u4e00\u70b9\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u82b1\u751f\u53ef\u4ee5\u5403\u3002\u201d \u3000\u3000\u4ed6\u5265\u4e86\u9897\u82b1\u751f\uff0c\u629b\u8d77\u6765\uff0c\u7528\u5634\u63a5\u4f4f\uff0c\u6162\u6162\u5480\u56bc\uff0c\u53c8\u559d\u4e86\u53e3\u9152\uff0c\u9053\uff1a\u201c\u4f60\u82e5\u80fd\u7528\u4f60\u7684\u91d1\u94b1 \u6765\u4e0b\u9152\uff0c\u6211\u624d\u771f\u7684\u7b97\u4f60\u6709\u672c\u4e8b\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u5fae\u7b11\u7740\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u8bdd\u597d\u50cf\u603b\u662f\u5f88\u6709\u9053\u7406\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u5f53\u7136\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u53ef\u60dc\u4f60\u5fd8\u4e86\u4e00\u70b9\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6ca1\u6709\u94b1\uff0c\u9152\u4e5f\u6ca1\u6709\u4e86\uff0c\u82b1\u751f\u4e5f\u6ca1\u6709\u4e86\u3002\u201d \u3000\u3000\u53f6\u5f00\u60f3\u4e86\u60f3\uff0c\u7ec8\u4e8e\u627f\u8ba4\uff1a\u201c\u4f60\u8bf4\u7684\u8bdd\u597d\u50cf\u4e5f\u4e0d\u662f\u6ca1\u6709\u9053\u7406\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u7b11\u9053\uff1a\u201c\u5f53\u7136\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u53ef\u60dc\u4f60\u4e5f\u5fd8\u4e86\u4e00\u70b9\u3002\u53ea\u6709\u94b1\u8fd8\u662f\u4e0d\u591f\u7684\uff0c\u91d1\u94b1\u5e76\u4e0d\u80fd\u771f\u7684\u4f7f\u4eba\u5feb\u4e50\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u8fde\u60f3\u90fd\u6ca1\u6709\u60f3\u5c31\u5df2\u627f\u8ba4\uff1a\u201c\u6240\u4ee5\u6211\u4e00\u76f4\u90fd\u5728\u627e\u3002\u201d \u3000\u3000\u53f6\u5f00\u8fd9\uff1a\u201c\u627e\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u770b\u7740\u4ed6\uff0c\u7f8e\u4e3d\u7684\u773c\u775b\u6e29\u67d4\u5982\u6625\u6c34\uff1a\u201c\u627e\u4e00\u6837\u771f\u6b63\u80fd\u8ba9\u6211\u5feb\u4e50\u7684\u4e1c\u897f\u3002\u201d \u3000\u3000\u53f6\u5f00\u51b7\u51b7\u9053\uff1a\u201c\u9664\u4e86\u2018\u91d1\u94b1\u2019\u4e4b\u5916\uff0c\u8fd9\u4e16\u4e0a\u8fd8\u6709\u4ec0\u4e48\u80fd\u8ba9\u4f60\u5feb\u4e50\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u53ea\u6709\u4e00\u6837\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u82b1\u751f\u3002\u201d\u53f6\u5f00\u7b11\u4e86\u3002 \u3000\u3000\u4ed6\u53c8\u5265\u4e86\u9897\u82b1\u751f\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u53c8\u5fd8\u4e86\u4e00\u70b9\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u91d1\u94b1\u548c\u82b1\u751f\u5e76\u4e0d\u662f\u597d\u642d\u6863\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u9489\u5b50\u4e0e\u9489\u9524\u4e5f\u4e0d\u662f\u597d\u642d\u6863\u3002\u201d \u3000\u3000\u53f6\u5f00\u540c\u610f\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u53ef\u662f\u5b83\u4eec\u5728\u4e00\u8d77\u7684\u65f6\u5019\uff0c\u5f7c\u6b64\u90fd\u5f88\u5feb\u4e50\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u5f7c\u6b64\u90e8\u5f88\u5feb\u4e50\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u56e0\u4e3a\u6ca1\u6709\u9489\u9524\uff0c\u9489\u5b50\u5c31\u5b8c\u5168\u6ca1\u6709\u7528\uff0c\u6ca1\u6709\u9489\u5b50\uff0c\u9489\u9524\u4e5f\u4e0d\u80fd\u53d1 \u6325\u6240\u957f\u3002\u201d \u3000\u3000\u5979\u5fae\u7b11\u7740\u9053\uff1a\u201c\u4e00\u4e2a\u4eba\u82e5\u4e0d\u80fd\u53d1\u6325\u6240\u957f\uff0c\u5c31\u7b49\u4e8e\u662f\u4e2a\u5e9f\u7269\uff0c\u5e9f\u7269\u662f\u7edd\u4e0d\u4f1a\u5feb\u4e50\u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u4e5f\u540c\u610f\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6240\u4ee5\u5b83\u4eec\u53ea\u6709\u5728\u4e00\u8d77\uff0c\u624d\u80fd\u5f97\u5230\u5feb\u4e50\u3002\u201d \u3000\u3000\u5979\u51dd\u89c6\u7740\u53f6\u5f00\uff0c\u53f6\u5f00\u5374\u907f\u5f00\u4e86\u5979\u7684\u76ee\u5149\u3002 \u3000\u3000\u4ed6\u5728\u9003\u907f\uff1f \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u6162\u6162\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u4e00\u5b9a\u4e5f\u660e\u767d\uff0c\u6211\u8bf4\u7684\u8bdd\u7edd\u5bf9\u6709\u9053\u7406\u3002\u201d \u3000\u3000\u53f6\u5f00\u4e0d\u80fd\u5426\u8ba4\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u73b0\u5728\u591a\u5c14\u7532\u3001\u5e03\u8fbe\u62c9\u548c\u73ed\u5bdf\u5df4\u90a3\u90fd\u5df2\u6b7b\u4e86\uff0c\u56db\u5927\u65e0\u738b\u5df2\u53bb\u5176\u4e09\uff0c\u9b54\u6559\u7eb5 \u7136\u8fd8\u6ca1\u6709\u5b8c\u5168\u88ab\u6bc1\u706d\uff0c\u4e5f\u5df2\u4e00\u8e76\u4e0d\u632f\u3002\u201d \u3000\u3000\u5979\u6625\u6c34\u822c\u7684\u773c\u6ce2\uff0c\u53c8\u53d8\u5f97\u9489\u5b50\u822c\u5c16\u9510\u3002 \u3000\u3000\u4f46\u5979\u5374\u4e0d\u662f\u9489\u5b50\uff0c\u5979\u662f\u9489\u9524\u3002 \u3000\u3000\u201c\u9b54\u6559\u4e00\u5012\uff0c\u653e\u773c\u5929\u4e0b\uff0c\u8fd8\u6709\u54ea\u4e00\u5e2e\u3001\u54ea\u4e00\u6d3e\u80fd\u548c\u6211\u4eec\u4e89\u4e00\u65e5\u4e4b\u77ed\u957f\uff1f\u201d \u3000\u3000\u201c\u6211\u4eec\uff1f\u201d \u3000\u3000\u53f6\u5f00\u6ca1\u6709\u7b11\u3002 \u3000\u3000\u201c\u6211\u4eec\u3002\u201d\u4e0a\u5b98\u5c0f\u4ed9\u4e5f\u6ca1\u6709\u7b11\uff1a\u201c\u73b0\u5728\u91d1\u94b1\u52a0\u4e0a\u82b1\u751f\uff0c\u6240\u4ee3\u8868\u7684\u610f\u601d\u5df2\u4e0d\u6b62\u662f\u5feb\u4e50\u800c \u5df2\u3002\u201d \u3000\u3000\u53f6\u5f00\u5728\u5480\u56bc\u7740\u82b1\u751f\u3002 \u3000\u3000\u82b1\u751f\u662f\u88ab\u5480\u56bc\u7684\uff0c\u9489\u5b50\u662f\u88ab\u6572\u6253\u7684\u3002 \u3000\u3000\u53ef\u662f\uff0c\u82e5\u6ca1\u6709\u4eba\u5480\u56bc\uff0c\u82b1\u751f\u4e5f\u4e00\u6837\u4f1a\u8150\u70c2\uff0c\u82e5\u6ca1\u6709\u4eba\u6572\u6253\uff0c\u9489\u5b50\u4e5f\u4e00\u6837\u4f1a\u751f\u9508\u3002 \u3000\u3000\u751f\u547d\u7684\u4ef7\u503c\u662f\u4ec0\u4e48\uff1f \u3000\u3000\u82b1\u751f\u5c82\u975e\u4e00\u5b9a\u8981\u7ecf\u4eba\u5480\u56bc\uff0c\u9489\u5b50\u5c82\u975e\u4e00\u5b9a\u8981\u88ab\u4eba\u6572\u6253\uff0c\u7136\u540e\u5b83\u4eec\u7684\u751f\u547d\u624d\u6709\u4ef7\u503c\u3002 \u3000\u3000\u53f6\u5f00\u4f3c\u4e4e\u5df2\u88ab\u6253\u52a8\u4e86\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u67d4\u58f0\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u5fc3\u91cc\u4e00\u5b9a\u8ba4\u4e3a\u6211\u60f3\u8981\u4f60\u505a\u9489\u5b50\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u4e0d\u662f\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4f60\u5e94\u8be5\u770b\u5f97\u51fa\uff0c\u6211\u5e76\u4e0d\u662f\u4e2a\u5f88\u53ef\u6015\u7684\u9489\u9524\u3002\u201d \u3000\u3000\u5979\u4f38\u51fa\u624b\uff0c\u63e1\u4f4f\u4e86\u4ed6\u7684\u624b\u3002 \u3000\u3000\u5979\u7684\u624b\u67d4\u8f6f\u5982\u4e1d\u7f0e\u3002 \u3000\u3000\u53f6\u5f00\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u7684\u786e\u4e0d\u662f\uff0c\u53ea\u53ef\u60dc\u2026\u2026\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u82b1\u751f\u548c\u91d1\u94b1\u4e4b\u95f4\uff0c\u8fd8\u6709\u4e2a\u94c3\u5f53\uff1f\u201d \u3000\u3000\u53f6\u5f00\u82e6\u7b11\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4e01\u7075\u7433\u7684\u786e\u662f\u4e2a\u5f88\u597d\u7684\u5973\u5b69\u5b50\uff0c\u6211\u82e5\u662f\u7537\u4eba\uff0c\u6211\u4e5f\u4f1a\u559c\u6b22\u5979\u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u7537\u4eba\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6211\u81f3\u5c11\u5e76\u4e0d\u8ba8\u538c\u5979\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u771f\u7684\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6211\u82e5\u8ba8\u538c\u5979\uff0c\u4e3a\u4ec0\u4e48\u8981\u5e26\u4f60\u6765\u8ddf\u5979\u89c1\u9762\u3002\u201d \u3000\u3000\u53f6\u5f00\u76ef\u7740\u5979\uff0c\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u8f7b\u8f7b\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u73b0\u5728\u5df2\u660e\u767d\uff0c\u50cf\u4f60\u8fd9\u6837\u7684\u7537\u4eba\uff0c\u7edd\u4e0d\u662f\u4e00\u4e2a\u5973 \u4eba\u80fd\u5b8c\u5168\u5360\u6709\u7684\uff0c\u6211\u5df2\u6ca1\u6709\u8fd9\u79cd\u5962\u671b\u3002\u201d \u3000\u3000\u5979\u51dd\u89c6\u7740\u53f6\u5f00\uff0c\u773c\u775b\u66f4\u6e29\u67d4\uff1a\u201c\u91d1\u94b1\u53ef\u4ee5\u6253\u9020\u6210\u94c3\u5f53\uff0c\u94c3\u5f53\u4e5f\u53ef\u4ee5\u94f8\u6210\u94b1\uff0c\u6211\u8ddf\u5979\u4e3a\u4ec0 \u4e48\u4e0d\u80fd\u53d8\u6210\u4e00\u4e2a\u4eba\u5462\uff1f\u201d \u3000\u3000\u53f6\u5f00\u53c8\u907f\u5f00\u4e86\u5979\u7684\u76ee\u5149\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u5047\u5982\u4f60\u4e5f\u80fd\u628a\u6211\u8ddf\u5979\u770b\u6210\u4e00\u4e2a\u4eba\uff0c\u6211\u4eec\u5c31\u4e00\u5b9a\u90fd\u5f88\u5feb\u4e50\uff0c\u5426\u5219\u2026\u2026\u201d \u3000\u3000\u53f6\u5f00\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u5426\u5219\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u53f9\u9053\uff1a\u201c\u5426\u5219\u91d1\u94b1\u3001\u82b1\u751f\u548c\u94c3\u5f53\uff0c\u8bf4\u4e0d\u5b9a\u5168\u90fd\u4f1a\u75db\u82e6\u7ec8\u751f\u3002\u201d \u3000\u3000\u53f6\u5f00\u7ec8\u4e8e\u56de\u8fc7\u5934\uff0c\u770b\u7740\u5979\u3002 \u3000\u3000\u53c8\u662f\u9ec4\u660f\u3002 \u3000\u3000\u5915\u9633\u6b63\u7167\u5728\u7a97\u6237\u4e0a\uff0c\u8273\u4e3d\u5982\u6625\u971e\uff0c\u5c4b\u5b50\u91cc\u71c3\u7740\u706b\uff0c\u4e5f\u6e29\u6696\u5982\u6625\u5929\u3002 \u3000\u3000\u5979\u7684\u773c\u775b\u5374\u6bd4\u5915\u9633\u66f4\u8273\u4e3d\uff0c\u66f4\u6e29\u6696\u3002 \u3000\u3000\u4e5f\u8bb8\u6625\u5929\u5c31\u662f\u5979\u5e26\u6765\u7684\u3002 \u3000\u3000\u4e00\u4e2a\u80fd\u5c06\u6625\u5929\u5e26\u6765\u7684\u5973\u4eba\uff0c\u5c82\u975e\u5df2\u662f\u7537\u4eba\u4eec\u7684\u6700\u5927\u68a6\u60f3\uff1f \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u54ac\u7740\u5634\u5507\uff0c\u9053\uff1a\u201c\u4f60\u597d\u50cf\u4ece\u6765\u4e5f\u6ca1\u6709\u8fd9\u4e48\u6837\u770b\u8fc7\u6211\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u2026\u2026\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4f60\u5f88\u5c11\u770b\u6211\uff0c\u6240\u4ee5\u4f60\u6839\u672c\u6ca1\u6709\u770b\u6e05\u6211\u662f\u4e2a\u4ec0\u4e48\u6837\u7684\u5973\u4eba\uff0c\u5c31\u56e0\u4e3a\u4f60\u6839\u672c \u4e0d\u77e5\u9053\u6211\u662f\u4e2a\u4ec0\u4e48\u6837\u7684\u5973\u4eba\uff0c\u6240\u4ee5\u624d\u5f88\u5c11\u770b\u6211\u3002\u201d \u3000\u3000\u53f6\u5f00\u627f\u8ba4\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u7684\u773c\u6ce2\u4e2d\u53c8\u9732\u51fa\u5e7d\u6028\uff0c\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u4e00\u5b9a\u4f1a\u8ba4\u4e3a\u6211\u662f\u4e2a\u5f88\u968f\u4fbf\u7684\u5973\u4eba\uff0c\u6709\u8fc7 \u5f88\u591a\u7537\u4eba\uff0c\u5176\u5b9e\u2026\u2026\u5176\u5b9e\u4f60\u4ee5\u540e\u5c31\u4f1a\u77e5\u9053\u2026\u2026\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u77e5\u9053\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u5782\u4e0b\u5934\uff0c\u8f7b\u8f7b\u9053\uff1a\u201c\u4f60\u4ee5\u540e\u5c31\u4f1a\u77e5\u9053\uff0c\u4f60\u4e0d\u4f46\u662f\u6211\u7b2c\u4e00\u4e2a\u7537\u4eba\uff0c\u4e5f\u662f\u6211\u6700\u540e\u4e00 \u4e2a\u3002\u201d \u3000\u3000\u8fd9\u7edd\u4e0d\u662f\u8bf4\u8c0e\u3002 \u3000\u3000\u806a\u660e\u7684\u5973\u4eba\uff0c\u7edd\u4e0d\u4f1a\u8bf4\u8fd9\u79cd\u968f\u65f6\u90e8\u53ef\u80fd\u88ab\u63ed\u7a7f\u7684\u8c0e\u8bdd\u3002 \u3000\u3000\u5979\u5f53\u7136\u662f\u4e2a\u7edd\u9876\u806a\u660e\u7684\u5973\u4eba\u3002 \u3000\u3000\u53f6\u5f00\u7684\u5fc3\u4f3c\u5df2\u6eb6\u5316\uff0c\u60c5\u4e2a\u81ea\u7981\u53cd\u63e1\u4f4f\u5979\u7684\u624b\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u7528\u4e0d\u7740\u7b49\u5230\u4ee5\u540e\uff0c\u6211\u73b0\u5728\u5c31\u5df2 \u76f8\u4fe1\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u7684\u773c\u775b\u4eae\u4e86\uff0c\u5ffd\u7136\u8df3\u8d77\u6765\uff0c\u9053\uff1a\u201c\u8d70\uff0c\u6211\u4eec\u53bb\u627e\u94c3\u5f53\u53bb\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u5979\u2026\u2026\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u5979\u65e2\u7136\u8fd8\u77e5\u9053\u8eb2\u5230\u8fd9\u91cc\u6765\uff0c\u795e\u667a\u4e00\u5b9a\u8fd8\u6ca1\u6709\u5b8c\u5168\u4e27\u5931\uff0c\u53ea\u8981\u6211\u4eec\u597d\u597d\u7167 \u987e\u5979\uff0c\u5979\u4e00\u5b9a\u5f88\u5feb\u5c31\u4f1a\u590d\u539f\u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u76ee\u4e2d\u9732\u51fa\u611f\u6fc0\u4e4b\u8272\uff0c\u770b\u6765\u4ed6\u7684\u786e\u4e00\u76f4\u90fd\u6ca1\u6709\u8ba4\u9519\u5979\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u521a\u624d\u6211\u51fa\u53bb\u7684\u65f6\u5019\uff0c\u5979\u5df2\u7761\u89c9\u4e86\uff0c\u6211\u5c31\u53eb\u97e9\u8d1e\u5728\u90a3\u91cc\u770b\u7740\u5979\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u9525\u5b50\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u5ae3\u7136\u9053\uff1a\u201c\u53ea\u8981\u4f60\u4f1a\u7528\uff0c\u9525\u5b50\u7684\u7528\u5904\u5f88\u5927\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u5df2\u80fd\u4fe1\u4efb\u4ed6\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4ed6\u5e76\u4e0d\u662f\u4e2a\u597d\u4eba\uff0c\u53ef\u662f\u6211\u5df2\u7ecf\u770b\u51fa\u6765\uff0c\u4ed6\u7edd\u4e0d\u6562\u505a\u80cc\u53db\u6211\u7684\u4e8b\u3002\u201d \u3000\u3000\u4ed6\u4eec\u559d\u9152\u7684\u5730\u65b9\uff0c\u5f53\u7136\u5c31\u5728\u51b7\u9999\u56ed\u3002 \u3000\u3000\u7a7f\u8fc7\u89d2\u95e8\uff0c\u5c31\u662f\u4e86\u7075\u7433\u7684\u5c0f\u9662\u3002 \u3000\u3000\u66ae\u8272\u5df2\u6df1\u4e86\u3002 \u3000\u3000\u9662\u5b50\u91cc\u548c\u5e73\u800c\u5b89\u9759\uff0c\u95e8\u662f\u865a\u63a9\u7684\uff0c\u5c4b\u91cc\u8fd8\u6ca1\u6709\u71c3\u706f\u3002 \u3000\u3000\u4ed6\u4eec\u7a7f\u8fc7\u5bc2\u9759\u7684\u5c0f\u9662\uff0c\u8d70\u5230\u95e8\u53e3\uff0c\u4e0a\u5b98\u5c0f\u4ed9\u5c31\u653e\u5f00\u53f6\u5f00\u7684\u624b\u3002 \u3000\u3000\u5979\u4e0d\u4f46\u6e29\u67d4\uff0c\u800c\u4e14\u4f53\u8d34\u3002 \u3000\u3000\u5973\u4eba\u7684\u4f53\u8d34\uff0c\u603b\u662f\u80fd\u4ee4\u7537\u4eba\u611f\u52a8\u7684\u3002 \u3000\u3000\u201c\u5979\u4e00\u5b9a\u8fd8\u5728\u7761\u3002\u201d \u3000\u3000\u201c\u80fd\u7761\u5f97\u7740\u603b\u662f\u798f\u6c14\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u5fae\u7b11\u7740\uff0c\u8f7b\u8f7b\u63a8\u5f00\u4e86\u95e8\uff0c\u53f6\u5f00\u8ddf\u5728\u5979\u8eab\u540e\uff0c\u8fd8\u6ca1\u6709\u8d70\u8fdb\u95e8\uff0c\u5ffd\u7136\u53d1\u89c9\u5979\u6574\u4e2a\u4eba \u90fd\u5df2\u50f5\u786c\u3002 \u3000\u3000\u5c4b\u5b50\u91cc\u4e5f\u662f\u548c\u5e73\u800c\u5b89\u9759\u7684\uff0c\u5915\u9633\u7684\u6e29\u6696\u8fd8\u7559\u5728\u5c4b\u89d2\uff0c\u53ef\u662f\u4eba\u5df2\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u4e01\u7075\u7433\u4e0d\u89c1\u4e86\uff0c\u97e9\u8d1e\u4e5f\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u5403\u60ca\u5730\u770b\u7740\u7a7a\u5e8a\uff0c\u773c\u6cea\u90fd\u5df2\u6025\u5f97\u6d41\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u53f6\u5f00\u53cd\u800c\u6bd4\u8f83\u9547\u9759\uff0c\u5148\u71c3\u8d77\u4e86\u706f\uff0c\u624d\u95ee\u9053\uff1a\u201c\u4f60\u662f\u53eb\u97e9\u8d1e\u5b88\u5728\u8fd9\u91cc\u7684\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u70b9\u70b9\u5934\u3002 \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4ed6\u4f1a\u4e0d\u4f1a\u79bb\u5f00\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u7edd\u4e0d\u4f1a\uff0c\u6211\u5429\u5490\u8fc7\u4ed6\uff0c\u6ca1\u6709\u6211\u7684\u547d\u4ee4\uff0c\u4ed6\u7edd\u4e0d\u80fd\u79bb\u5f00\u534a\u6b65\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u6709\u628a\u63e1\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4ed6\u7edd\u4e0d\u6562\u4e0d\u542c\u6211\u7684\u8bdd\uff0c\u4ed6\u8fd8\u4e0d\u60f3\u6b7b\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u53ef\u662f\u73b0\u5728\u4ed6\u4eba\u5e76\u4e0d\u5728\u8fd9\u91cc\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u8138\u8272\u82cd\u767d\uff0c\u9053\uff1a\u201c\u6211\u60f3\u8fd9\u4e00\u5b9a\u6709\u539f\u56e0\uff0c\u4e00\u5b9a\u6709\u2026\u2026\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u60f3\u4ed6\u662f\u4e3a\u4e86\u4ec0\u4e48\u8d70\u7684\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u6ca1\u6709\u56de\u7b54\uff0c\u4e5f\u4e0d\u80fd\u56de\u7b54\u3002 \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4ed6\u4e0d\u4f46\u81ea\u5df1\u8d70\u4e86\uff0c\u8fd8\u628a\u4e01\u7075\u7433\u4e5f\u5e26\u8d70\u4e86\uff0c\u4ed6\u2026\u2026\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u6253\u65ad\u4e86\u4ed6\u7684\u8bdd\uff0c\u9053\uff1a\u201c\u4e01\u7075\u7433\u7edd\u4e0d\u662f\u4ed6\u5e26\u8d70\u7684\uff01\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u80fd\u786e\u5b9a\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u70b9\u70b9\u5934\uff0c\u5979\u5e76\u4e0d\u662f\u8f7b\u6613\u4e0b\u5224\u65ad\u7684\u4eba\uff0c\u5979\u7684\u5224\u65ad\u901a\u5e38\u90fd\u5f88\u51c6\u786e\uff1a\u201c\u5979\u53d7\u7684\u60ca\u9a87\u592a \u5927\uff0c\u6240\u4ee5\u4e00\u76f4\u90fd\u5f88\u7d27\u5f20\uff0c\u7edd\u4e0d\u80fd\u518d\u53d7\u5230\u4e00\u70b9\u523a\u6fc0\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u8ba4\u4e3a\u8fd9\u91cc\u53c8\u6709\u4ec0\u4e48\u4e8b\uff0c\u8ba9\u5979\u53d7\u4e86\u60ca\uff0c\u6240\u4ee5\u5979\u5ffd\u7136\u9003\u4e86\u51fa\u53bb\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4e00\u5b9a\u662f\u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u5979\u9003\u8d70\u4e86\uff0c\u97e9\u8d1e\u5f53\u7136\u8981\u8ffd\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6240\u4ee5\u4ed6\u4eec\u4e24\u4e2a\u4eba\u90fd\u4e0d\u5728\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4ed6\u53bb\u8ffd\u7684\u65f6\u5019\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u7559\u4e0b\u70b9\u6807\u8bb0\uff0c\u8ba9\u6211\u4eec\u77e5\u9053\u4ed6\u4eec\u7684\u53bb\u5411\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u5979\u7684\u9003\u8d70\u4e00\u5b9a\u5f88\u7a81\u7136\uff0c\u4ed3\u731d\u4e4b\u95f4\uff0c\u4ed6\u6765\u4e0d\u53ca\u3002\u201d \u3000\u3000\u53f6\u5f00\u53f9\u4e86\u53e3\u6c14\uff0c\u6ca1\u6709\u518d\u8bf4\u4ec0\u4e48\u3002 \u3000\u3000\u4ed6\u4e00\u5411\u4e0d\u662f\u90a3\u79cd\u4e00\u7740\u6025\u5c31\u4f1a\u516d\u795e\u65e0\u4e3b\u7684\u4eba\uff0c\u4ed6\u4e00\u5411\u5f88\u6c89\u5f97\u4f4f\u6c14\u53d7\u5230\u7684\u538b\u529b\u8d8a\u5927\uff0c\u4ed6\u53cd\u800c \u8d8a\u80fd\u6c89\u5f97\u4f4f\u6c14\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u54ac\u7740\u5634\u5507\uff0c\u9053\uff1a\u201c\u4ed6\u65e2\u7136\u5df2\u53bb\u8ffd\u4e86\uff0c\u4e0d\u7ba1\u8ffd\u4e0d\u8ffd\u5f97\u4e0a\uff0c\u90fd\u4e00\u5b9a\u4f1a\u6709\u6d88\u606f\u56de\u6765 \u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u55ef\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u73b0\u5728\u6211\u5c31\u7b97\u8981\u53bb\u627e\uff0c\u4e5f\u6ca1\u6cd5\u5b50\u627e\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u55ef\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u770b\u7740\u4ed6\uff0c\u5fcd\u4e0d\u4f4f\u53c8\u9053\uff1a\u201c\u4f60\u597d\u50cf\u5e76\u4e0d\u7740\u6025\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u7740\u6025\u6709\u6ca1\u6709\u7528\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u65e2\u7136\u6ca1\u6709\u7528\uff0c\u6211\u4e3a\u4ec0\u4e48\u8981\u7740\u6025\uff1f\u201d \u3000\u3000\u4ed6\u8bf4\u5f97\u867d\u4ece\u5bb9\uff0c\u8138\u8272\u8fd8\u662f\u5f88\u96be\u770b\uff0c\u6162\u6162\u5730\u5750\u4e0b\u6765\uff0c\u5750\u5728\u5e8a\u4e0a\u3002 \u3000\u3000\u2014\u2014\u65e2\u7136\u6709\u5730\u65b9\u5750\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u8eba\u4e0b\u53bb\uff1f \u3000\u3000\u4ed6\u7d22\u6027\u8eba\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u5374\u6025\u5f97\u8fde\u5750\u90fd\u5750\u4e0d\u4f4f\u4e86\uff0c\u76b1\u7740\u7709\u9053\uff1a\u201c\u8fd9\u5730\u65b9\u592a\u51b7\uff0c\u6211\u4eec\u4e0d\u5982\u2026\u2026\u201d \u3000\u3000\u706f\u5149\u7167\u5728\u4ed6\u8138\u4e0a\u3002\u4ed6\u7684\u8138\u770b\u6765\u4e5f\u50cf\u662f\u88ab\u4eba\u780d\u4e86\u4e00\u5200\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u4ece\u6765\u4e5f\u6ca1\u6709\u770b\u89c1\u4ed6\u5982\u6b64\u60ca\u9a87\u8fc7\uff0c\u5fcd\u4e0d\u4f4f\u95f4\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u53f6\u5f00\u6ca1\u6709\u5f00\u53e3\u3002\u4ed6\u7adf\u4f3c\u8fde\u5589\u5934\u7684\u808c\u8089\u90fd\u5df2\u50f5\u786c\uff0c\u8fde\u58f0\u97f3\u90fd\u5df2\u53d1\u4e0d\u51fa\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u8d70\u8fc7\u53bb\uff0c\u8d70\u5230\u5e8a\u5934\uff0c\u4e00\u5f20\u7f8e\u4e3d\u7684\u8138\uff0c\u5ffd\u7136\u4e5f\u53d8\u4e86\u989c\u8272\u3002 \u3000\u3000\u5979\u5ffd\u7136\u55c5\u5230\u4e00\u79cd\u5f88\u5947\u7279\u7684\u6c14\u5473\uff0c\u4e00\u79cd\u4eca\u4eba\u4f5c\u5455\u3001\u53c8\u4ee4\u4eba\u6218\u6817\u7684\u6c14\u5473\u3002 \u3000\u3000\u8840\u7684\u6c14\u5473\u3002\u4ed6\u4eec\u5e76\u6ca1\u6709\u6d41\u8840\uff0c\u8840\u8165\u6c14\u662f\u4ece\u54ea\u91cc\u6765\u7684\uff1f\u662f\u4ece\u5e8a\u4e0b\u6765\u7684\u3002 \u3000\u3000\u5e8a\u4e0b\u9762\u600e\u4e48\u4f1a\u6709\u8840\u8165\u6c14\uff0c\u96be\u9053\u5e8a\u4e0b\u4f1a\u6709\u4e2a\u6b7b\u4eba\uff1f\u6b7b\u7684\u662f\u4ec0\u4e48\u4eba\uff1f \u3000\u3000\u5e8a\u5e76\u4e0d\u91cd\uff0c\u4e00\u4f38\u624b\u5c31\u53ef\u4ee5\u6380\u8d77\u6765\uff0c\u8fd9\u4e9b\u95ee\u9898\u7acb\u523b\u5c31\u53ef\u4ee5\u5f97\u5230\u7b54\u6848\u3002 \u3000\u3000\u53ef\u662f\u53f6\u5f00\u6ca1\u6709\u4f38\u624b\u3002\u4ed6\u7684\u624b\u5df2\u50f5\u786c\uff0c\u8fde\u624b\u6307\u90fd\u5df2\u50f5\u786c\uff0c\u4ed6\u5b9e\u5728\u6ca1\u6709\u52c7\u6c14\u6380\u8d77\u8fd9\u5f20\u5e8a\u3002 \u3000\u3000\u2014\u2014\u5047\u5982\u771f\u6709\u4eba\u6b7b\u5728\u5e8a\u4e0b\uff0c\u6b7b\u7684\u4e0d\u662f\u4e01\u7075\u7433\u662f\u8c01\uff1f \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u5374\u5df2\u4f38\u51fa\u4e86\u624b\uff0c\u5e8a\u4e0b\u679c\u7136\u662f\u4e2a\u6b7b\u4eba\uff0c\u521a\u6b7b\u4e86\u4e0d\u4e45\uff0c\u8eab\u4e0a\u7684\u8840\u6e0d\u8fd8\u6ca1\u6709\u5e72\u900f\u3002 \u3000\u3000\u6b7b\u7684\u5374\u4e0d\u662f\u4e01\u7075\u7433\u3002\u662f\u97e9\u8d1e\u3002 \u7b2c\u4e09\u5341\u56db\u7ae0\u3000\u53cc\u91cd\u8eab\u4efd>> \u53e4\u9f99\u300a\u4e5d\u6708\u9e70\u98de\u300b \u7b2c\u4e09\u5341\u56db\u7ae0\u3000\u53cc\u91cd\u8eab\u4efd \u3000\u3000\u53f6\u5f00\u6014\u4f4f\uff0c\u4e0a\u5b98\u5c0f\u4ed9\u66f4\u5403\u60ca\u3002\u6b7b\u7684\u600e\u4e48\u4f1a\u662f\u97e9\u8d1e\uff1f\u53f6\u5f00\u60f3\u4e0d\u5230\uff0c\u4e0a\u5b98\u5c0f\u4ed9\u66f4\u89c9\u5f97\u610f\u5916\u3002 \u97e9\u8d1e\u65e2\u7136\u5df2\u6b7b\u5728\u8fd9\u91cc\uff0c\u4e01\u7075\u7433\u5462\uff1f \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u8f7b\u8f7b\u5730\u653e\u4e0b\u5e8a\uff0c\u6162\u6162\u5730\u8f6c\u8fc7\u8eab\uff0c\u8d70\u5230\u7a97\u524d\uff0c\u63a8\u5f00\u4e86\u7a97\u6237\u3002 \u3000\u3000\u7a97\u5916\u4e00\u7247\u9ed1\u6697\uff0c\u591c\u8272\u65e0\u60c5\uff0c\u5ffd\u7136\u53c8\u5df2\u6765\u4e34\u3002 \u3000\u3000\u5979\u9762\u5bf9\u7740\u8fd9\u65e0\u60c5\u7684\u591c\u8272\uff0c\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u624d\u957f\u957f\u5410\u51fa\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u539f\u6765\u5979\u5148\u6740\u4e86\u97e9\u8d1e\u624d\u8d70 \u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u8ba4\u4e3a\u662f\u5979\u6740\u4e86\u97e9\u8d1e\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4f60\u8ba4\u4e3a\u4e0d\u662f\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u7edd\u4e0d\u662f\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4f60\u80fd\u786e\u5b9a\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6b66\u529f\u4e5f\u6709\u5f88\u591a\u79cd\uff0c\u6700\u53ef\u6015\u3001\u6700\u6709\u6548\u7684\u5374\u53ea\u6709\u4e00\u79cd\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u54ea\u4e00\u79cd\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u53ea\u6709\u6740\u4eba\u7684\u6b66\u529f\uff0c\u624d\u662f\u771f\u6b63\u6709\u6548\u7684\u6b66\u529f\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u540c\u610f\u3002\u5979\u4e5f\u77e5\u9053\u6709\u5f88\u591a\u4eba\u7684\u6b66\u529f\u867d\u9ad8\uff0c\u5374\u4e0d\u80fd\u6740\u4eba\uff0c\u4e5f\u4e0d\u6562\u6740\u4eba\u3002 \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6740\u4eba\u7684\u6b66\u529f\uff0c\u4e01\u7075\u7433\u7edd\u5bf9\u6bd4\u4e0d\u4e0a\u97e9\u8d1e\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u65ad\u5b9a\u97e9\u8d1e\u7edd\u4e0d\u662f\u6b7b\u5728\u5979\u624b\u91cc\u7684\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u7edd\u4e0d\u662f\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u53ef\u662f\u73b0\u5728\u4e01\u7075\u7433\u5df2\u8d70\u4e86\uff0c\u97e9\u8d1e\u5374\u5df2\u6b7b\u5728\u8fd9\u91cc\u3002\u201d \u3000\u3000\u8fd9\u662f\u4e8b\u5b9e\uff0c\u4e8b\u5b9e\u662f\u8c01\u90fd\u4e0d\u80fd\u53cd\u9a73\u7684\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u82e5\u4e0d\u662f\u4e01\u7075\u7433\u6740\u4e86\u4ed6\uff1f\u662f\u8c01\u6740\u4e86\u4ed6\uff1f\u201d \u3000\u3000\u80fd\u6740\u97e9\u8d1e\u7684\u4eba\u4e5f\u4e0d\u591a\uff0c\u4f55\u51b5\uff0c\u8fd9\u5c4b\u5b50\u91cc\u9664\u4e86\u4ed6\u548c\u4e01\u7075\u7433\u5916\uff0c\u5e76\u6ca1\u6709\u7b2c\u4e09\u4eba\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4ed6\u82e5\u4e0d\u6b7b\uff0c\u7edd\u4e0d\u4f1a\u8ba9\u4e01\u7075\u7433\u8d70\uff0c\u96be\u9053\u6709\u4eba\u5148\u6740\u4e86\u4ed6\uff0c\u518d\u7ed1\u8d70\u4e86\u4e01\u7075 \u7433\uff1f\u201d \u3000\u3000\u8fd9\u4e9b\u95ee\u9898\u6709\u8c01\u80fd\u56de\u7b54\uff1f\u53f6\u5f00\u4e5f\u8d70\u8fc7\u6765\uff0c\u63a8\u5f00\u4e86\u53e6\u4e00\u6247\u7a97\u5b50\u3002\u7a97\u5b50\u867d\u4e0d\u540c\uff0c\u7a97\u5916\u7684\u591c\u8272\u5374 \u662f\u76f8\u540c\u7684\uff0c\u540c\u6837\u5bd2\u51b7\uff0c\u540c\u6837\u65e0\u60c5\u3002\u4ed6\u75f4\u75f4\u5730\u7ad9\u5728\u90a3\u91cc\uff0c\u52a8\u4e5f\u4e0d\u52a8\uff0c\u4ed6\u7684\u773c\u6674\u5c31\u5982\u540c\u7a97\u5916\u7684\u591c \u8272\u822c\u6df1\u6c89\u9ed1\u6697\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u5782\u7740\u5934\uff0c\u7ec8\u4e8e\u8f7b\u8f7b\u9053\uff1a\u201c\u6211\u521a\u624d\u4e0d\u8be5\u95ee\u90a3\u4e9b\u8bdd\u3002\u201d \u3000\u3000\u53f6\u5f00\u6c89\u9ed8\uff0c\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u73b0\u5728\u6700\u91cd\u8981\u7684\u4e00\u4ef6\u4e8b\uff0c\u662f\u8d76\u7d27\u60f3\u6cd5\u5b50\u53bb\u627e\u4e01\u7075\u7433\uff0c\u5979\u2026\u2026\u201d \u3000\u3000\u53f6\u5f00\u5ffd\u7136\u6253\u65ad\u4e86\u5979\u7684\u8bdd\uff0c\u9053\uff1a\u201c\u4e0d\u5fc5\u627e\u4e86\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u5f88\u610f\u5916\uff0c\u5979\u4ece\u672a\u60f3\u5230\u53f6\u5f00\u4f1a\u8bf4\u51fa\u8fd9\u79cd\u8bdd\uff0c\u5fcd\u4e0d\u4f4f\u8f6c\u8fc7\u5934\uff0c\u5403\u60ca\u5730\u770b\u7740\u4ed6\uff0c\u9053\uff1a \u201c\u4f60\u662f\u8bf4\uff0c\u4e0d\u5fc5\u53bb\u627e\u4e86\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u55ef\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u65e2\u7136\u5df2\u6709\u4eba\u77e5\u9053\u5979\u7684\u4e0b\u843d\uff0c\u53c8\u4f55\u5fc5\u518d\u53bb\u627e\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u8c01\u77e5\u9053\u5979\u7684\u4e0b\u843d\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u66f4\u5403\u60ca\uff0c\u9053\uff1a\u201c\u4f60\u662f\u8bf4\u6211\u77e5\u9053\u5979\u7684\u4e0b\u843d\uff1f\u201d \u3000\u3000\u53f6\u5f00\u6de1\u6de1\u9053\uff1a\u201c\u6211\u5df2\u8bf4\u5f97\u5f88\u6e05\u695a\uff0c\u4f60\u4e5f\u542c\u5f97\u5f88\u6e05\u695a\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u770b\u7740\u4ed6\uff0c\u6ca1\u6709\u52a8\uff0c\u6ca1\u6709\u5f00\u53e3\uff0c\u50cf\u662f\u5df2\u5b8c\u5168\u6014\u4f4f\u3002 \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u9b54\u6559\u4e2d\u7684\u56db\u5927\u5929\u738b\uff0c\u7684\u786e\u5df2\u6b7b\u4e86\u4e09\u4e2a\uff0c\u53ef\u662f\u5b64\u5cf0\u5e76\u6ca1\u6709\u6b7b\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6768\u5929\u8fd8\u6ca1\u6709\u6b7b\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6768\u5929\u4e0d\u662f\u5b64\u5cf0\uff0c\u5415\u8fea\u4e5f\u4e0d\u662f\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6768\u5929\u6ca1\u6709\u53d7\u4f24\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4ed6\u53d7\u4e86\u4f24\uff0c\u4f24\u5f97\u5f88\u91cd\uff0c\u53ef\u662f\u53d7\u4f24\u7684\u4eba\u5e76\u4e0d\u4e00\u5b9a\u5c31\u662f\u5b64\u5cf0\u3002\u201d \u3000\u3000\u2014\u2014\u7403\u662f\u5706\u7684\uff0c\u5706\u7684\u4e1c\u897f\u5e76\u4e0d\u4e00\u5b9a\u5c31\u662f\u7403\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4ed6\u82e5\u4e0d\u662f\u5b64\u5cf0\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u6562\u8ba9\u4eba\u77e5\u9053\u4ed6\u53d7\u4e86\u4f24\uff1f\u4e3a\u4ec0\u4e48\u8981\u7792\u7740\u4f60\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u56e0\u4e3a\u4ed6\u4ee5\u4e3a\u6211\u662f\u4f60\u7684\u5974\u624d\uff0c\u4ee5\u4e3a\u6211\u4e5f\u5165\u4e86\u91d1\u94b1\u5e2e\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u5ffd\u7136\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u8bdd\uff0c\u6211\u8fde\u4e00\u53e5\u4e5f\u4e0d\u61c2\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u5e94\u8be5\u61c2\u7684\uff0c\u4e5f\u53ea\u6709\u4f60\u624d\u61c2\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u56e0\u4e3a\u51fa\u624b\u4f24\u4ed6\u7684\u4eba\u5c31\u662f\u4f60\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u5728\u82e6\u7b11\uff0c\u9053\uff1a\u201c\u6211\u82e5\u4e0d\u662f\u5f88\u4e86\u89e3\u4f60\uff0c\u4e00\u5b9a\u4ee5\u4e3a\u4f60\u5df2\u9189\u4e86\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u4ece\u6765\u4e5f\u6ca1\u6709\u50cf\u73b0\u5728\u8fd9\u4e48\u6e05\u9192\u8fc7\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6768\u5929\u672c\u662f\u6211\u7684\u597d\u5e2e\u624b\uff0c\u6211\u4e3a\u4ec0\u4e48\u8981\u51fa\u624b\u4f24\u4ed6\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u56e0\u4e3a\u4ed6\u5148\u8981\u6740\u4f60\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u7b11\u4e86\u3002\u5979\u7684\u7b11\uff0c\u5c31\u8ddf\u53f6\u5f00\u5728\u65e0\u53ef\u5948\u4f55\u65f6\u90a3\u79cd\u7b11\u5b8c\u5168\u4e00\u6837\u3002 \u3000\u3000\u53f6\u5f00\u5374\u6ca1\u6709\u7b11\u3002\u4e8b\u5b9e\u4e0a\uff0c\u4ed6\u8138\u4e0a\u7684\u8868\u60c5\u4e5f\u4ece\u6765\u6ca1\u6709\u50cf\u73b0\u5728\u8fd9\u4e48\u4e25\u8083\u8fc7\u3002\u4ed6\u6c89\u7740\u8138\u9053\uff1a \u201c\u4ed6\u4e45\u5df2\u60f3\u6740\u4e86\u4f60\uff0c\u5374\u4e00\u76f4\u6ca1\u6709\u673a\u4f1a\uff0c\u53ea\u6709\u5192\u9669\u884c\u523a\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u884c\u523a\uff1f\u201d \u3000\u3000\u53f6\u5f00\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4e5f\u8bb8\u4ed6\u4f4e\u4f30\u4e86\u4f60\u7684\u6b66\u529f\uff0c\u4e5f\u8bb8\u4ed6\u5728\u65e0\u610f\u95f4\u53d1\u73b0\u4f60\u5df2\u53d7\u4e86\u4f24\uff0c\u6240\u4ee5\u51b3 \u5b9a\u4e58\u6b64\u673a\u4f1a\uff0c\u5192\u9669\u8bd5\u4e00\u8bd5\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u5728\u542c\u7740\uff0c\u5979\u4e0d\u518d\u8fa9\u9a73\uff0c\u597d\u50cf\u89c9\u5f97\u8fd9\u4ef6\u4e8b\u6839\u672c\u4e0d\u503c\u5f97\u8fa9\u9a73\u3002 \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4ed6\u51b3\u5b9a\u52a8\u624b\u7684\u65f6\u5019\uff0c\u60f3\u5fc5\u5c31\u5728\u521d\u4e00\u7684\u665a\u4e0a\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u5c45\u7136\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u5047\u5982\u8981\u6697\u4e2d\u53bb\u523a\u6740\u4e00\u4e2a\u4eba\uff0c\u5927\u5e74\u521d\u4e00\u7684\u665a\u4e0a\u7684\u786e\u662f\u597d\u65f6 \u5019\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4ed6\u53bb\u884c\u523a\u65f6\uff0c\u5f53\u7136\u662f\u8499\u7740\u8138\u7684\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u5f53\u7136\u3002\u201d \u3000\u3000\u65e0\u8bba\u8c01\u8981\u505a\u523a\u5ba2\u65f6\uff0c\u90fd\u7edd\u4e0d\u4f1a\u4ee5\u771f\u9762\u76ee\u793a\u4eba\u3002 \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4ed6\u672c\u6765\u4ee5\u4e3a\u81ea\u5df1\u8fd9\u4e00\u51fb\u5fc5\u5b9a\u5341\u62ff\u4e5d\u7a33\uff0c\u8c01\u77e5\u4f60\u7684\u6b66\u529f\u7adf\u6bd4\u4ed6\u60f3\u8c61\u4e2d\u8fd8\u8981\u597d\u5f97 \u591a\uff0c\u6240\u4ee5\u4ed6\u975e\u4f46\u6ca1\u6709\u5f97\u624b\uff0c\u53cd\u800c\u4f24\u5728\u4f60\u624b\u4e0b\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u53c8\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u8981\u6740\u6211\u7684\u786e\u4e0d\u662f\u4ef6\u5bb9\u6613\u4e8b\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u53ef\u662f\u4f60\u4e5f\u4f4e\u4f30\u4e86\u4ed6\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4ed6\u7684\u8f7b\u529f\u6781\u9ad8\uff0c\u867d\u7136\u6ca1\u6709\u5f97\u624b\uff0c\u5374\u8fd8\u662f\u9003\u8d70\u4e86\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u60f3\u8981\u6349\u4f4f\u4e00\u6761\u4f1a\u98de\u7684\u72d0\u72f8\uff0c\u5f53\u7136\u4e5f\u4e0d\u662f\u4ef6\u5bb9\u6613\u4e8b\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u4ee5\u4e3a\u4ed6\u65e2\u7136\u4e2d\u4e86\u4f60\u7684\u6bd2\u9488\uff0c\u5c31\u7b97\u80fd\u9003\u8d70\uff0c\u4e5f\u9003\u4e0d\u8fdc\u7684\uff0c\u4f46\u662f\u4ed6\u8fd8\u6709\u79cd\u4e13\u89e3 \u767e\u6bd2\u7684\u7075\u836f\uff0c\u5c45\u7136\u80fd\u6682\u65f6\u4fdd\u4f4f\u4e86\u4ed6\u7684\u6027\u547d\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u53ef\u662f\u6211\u53ea\u8981\u67e5\u51fa\u662f\u8c01\u53d7\u4e86\u4f24\uff0c\u5c31\u77e5\u9053\u523a\u5ba2\u662f\u8c01\u4e86\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6240\u4ee5\u4ed6\u624d\u4f1a\u7792\u7740\u6211\uff0c\u4e0d\u6562\u8ba9\u6211\u770b\u89c1\u4ed6\u7684\u4f24\u53e3\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4ed6\u4e00\u5b9a\u4ee5\u4e3a\u662f\u6211\u6d3e\u4f60\u53bb\u8c03\u67e5\u523a\u5ba2\u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4ed6\u5f53\u7136\u60f3\u4e0d\u5230\u4f60\u65e9\u5df2\u77e5\u9053\u523a\u5ba2\u5c31\u662f\u4ed6\u4e86\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6211\u600e\u4e48\u4f1a\u77e5\u9053\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4ed6\u4ee5\u4e3a\u738b\u5be1\u5987\u5df2\u6b7b\u5fc3\u584c\u5730\u8ddf\u7740\u4ed6\uff0c\u4ee5\u4e3a\u738b\u5be1\u5987\u4f1a\u66ff\u4ed6\u4fdd\u5b88\u79d8\u5bc6\uff0c\u60f3\u4e0d \u5230\u2026\u2026\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u738b\u5be1\u5987\u5374\u5c06\u8fd9\u79d8\u5bc6\u544a\u8bc9\u4e86\u6211\u3002\u201d \u3000\u3000\u53f6\u5f00\u53f9\u9053\uff1a\u201c\u65e0\u8bba\u591a\u7cbe\u660e\u7684\u7537\u4eba\uff0c\u90fd\u96be\u514d\u4f1a\u88ab\u5973\u4eba\u51fa\u5356\u7684\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u53c8\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8fd9\u4e5f\u8bb8\u53ea\u56e0\u4e3a\u7537\u4eba\u603b\u8ba4\u4e3a\u5973\u4eba\u90fd\u662f\u5f31\u8005\uff0c\u90fd\u662f\u50bb\u74dc\u3002\u201d \u3000\u3000\u53f6\u5f00\u540c\u610f\u8fd9\u53e5\u8bdd\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6211\u65e2\u7136\u5df2\u77e5\u9053\u4ed6\u5c31\u662f\u523a\u5ba2\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u6740\u4e86\u4ed6\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u56e0\u4e3a\u4f60\u6740\u4eba\u65f6\u603b\u559c\u6b22\u501f\u522b\u4eba\u7684\u5200\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u80fd\u501f\u522b\u4eba\u7684\u5200\uff0c\u53bb\u6740\u81ea\u5df1\u60f3\u6740\u7684\u4eba\uff0c\u5012\u7684\u786e\u662f\u4ef6\u5f88\u6109\u5feb\u7684\u4e8b\u3002\u201d\u53f6\u5f00 \u9053\uff1a\u201c\u4f60\u6109\u5feb\uff0c\u6211\u5c31\u4e0d\u6109\u5feb\u4e86\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u56e0\u4e3a\u8fd9\u6b21\u4f60\u60f3\u501f\u7684\uff0c\u662f\u6211\u7684\u5200\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u5b64\u5cf0\u53d7\u4e86\u4f24\uff0c\u6211\u5728\u627e\u5b64\u5cf0\uff0c\u6768\u5929\u53c8\u6070\u5de7\u53d7\u4e86\u4f24\uff0c\u800c\u4e14\u4e0d\u6562\u628a\u53d7\u4f24\u7684\u4e8b\u8bf4\u51fa \u6765\uff0c\u8fd9\u4ef6\u4e8b\u5c31\u597d\u50cf\u4e00\u52a0\u4e00\uff0c\u518d\u52a0\u4e00\uff0c\u5fc5\u5b9a\u662f\u4e09\u3002\u2026\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6240\u4ee5\u6211\u8ba4\u4e3a\u4f60\u53ea\u8981\u627e\u5230\u6768 \u5929\uff0c\u5c31\u4e00\u5b9a\u4f1a\u4ee5\u4e3a\u4ed6\u5c31\u662f\u5b64\u5cf0\u3002\u201d \u3000\u3000\u53f6\u5f00\u82e6\u7b11\u9053\uff1a\u201c\u6211\u672c\u6765\u51e0\u4e4e\u4ee5\u4e3a\u4ed6\u662f\u7684\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4f60\u7684\u89e3\u91ca\u542c\u6765\u597d\u50cf\u5f88\u5408\u7406\uff0c\u53ea\u53ef\u60dc\u4f60\u53c8\u5fd8\u4e86\u4e00\u70b9\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6740\u4eba\u90fd\u6709\u52a8\u673a\uff0c\u8981\u6740\u6211\uff0c\u66f4\u4e00\u5b9a\u8981\u6709\u5f88\u597d\u7684\u7406\u7531\uff0c\u56e0\u4e3a\u65e0\u8bba\u8c01\u90fd\u5e94\u8be5\u77e5 \u9053\u90a3\u7edd\u4e0d\u662f\u4ef6\u5bb9\u6613\u4e8b\u3002\u201d \u3000\u3000\u53f6\u5f00\u627f\u8ba4\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6768\u5929\u5f88\u4e86\u89e3\u6211\uff0c\u6211\u5bf9\u4ed6\u5e76\u4e0d\u574f\uff0c\u4ed6\u4e3a\u4ec0\u4e48\u8981\u5192\u9669\u6740\u6211\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u4e5f\u5f88\u4e86\u89e3\u4ed6\uff0c\u4ed6\u662f\u4e2a\u91ce\u5fc3\u5f88\u5927\u7684\u4eba\uff0c\u6240\u4ee5\u624d\u4f1a\u5165\u91d1\u94b1\u5e2e\u3002\u201d \u3000\u3000\u8fd9\u70b9\u4e0a\u5b98\u5c0f\u4ed9\u4e5f\u540c\u610f\u3002 \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4ed6\u8d8a\u6df1\u5165\uff0c\u8d8a\u4e86\u89e3\u91d1\u94b1\u5e2e\u52bf\u529b\u7684\u5e9e\u5927\uff0c\u91ce\u5fc3\u5c31\u8d8a\u5927\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u96be\u9053\u4ed6\u8fd8\u60f3\u505a\u91d1\u94b1\u5e2e\u7684\u5e2e\u4e3b\uff1f\u201d\u53f6\u5f00\u9053\uff1a\u201c\u4ed6\u4e00\u5b9a\u60f3\u5f97\u8981\u547d\uff0c\u53ea\u53ef \u60dc\u2026\u2026\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u53ef\u60dc\u53ea\u8981\u6211\u6d3b\u7740\uff0c\u4ed6\u5c31\u6c38\u8fdc\u6ca1\u6709\u8fd9\u4e00\u5929\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6240\u4ee5\u4ed6\u65e0\u8bba\u5192\u591a\u5927\u7684\u9669\uff0c\u4e5f\u8981\u6740\u4e86\u4f60\u3002\u201d \u3000\u3000\u91ce\u5fc3\u5c31\u50cf\u662f\u6d2a\u6c34\uff0c\u4e00\u65e6\u53d1\u4f5c\u8d77\u6765\uff0c\u5c31\u6ca1\u6709\u4eba\u80fd\u63a7\u5236\uff0c\u8fde\u4ed6\u81ea\u5df1\u90fd\u4e0d\u80fd\u3002\u6240\u4ee5\u91ce\u5fc3\u4e0d\u4f46\u80fd \u6bc1\u706d\u522b\u4eba\uff0c\u4e5f\u540c\u6837\u80fd\u6bc1\u81ea\u5df1\uff0c\u800c\u4e14\u5f80\u5f80\u5728\u6bc1\u706d\u522b\u4eba\u4e4b\u524d\uff0c\u5c31\u5df2\u5148\u6bc1\u4e86\u81ea\u5df1\u3002\u53ef\u662f\u4e00\u4e2a\u4eba\u5047\u5982 \u5b8c\u5168\u6ca1\u6709\u91ce\u5fc3\uff0c\u6d3b\u7740\u5c82\u975e\u4e5f\u5f88\u4e4f\u5473\uff1f\u8fd9\u5c82\u975e\u4e5f\u662f\u4eba\u7c7b\u7684\u60b2\u54c0\u4e4b\u4e00\uff1f \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u73b0\u5728\u4f60\u7684\u63a8\u6d4b\u597d\u50cf\u5df2\u6e10\u6e10\u53d8\u5f97\u5b8c\u6574\u4e9b\u4e86\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u8fd8\u4e0d\u7b97\u5b8c\u6574\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u7b11\u9053\uff1a\u201c\u4f60\u81ea\u5df1\u4e5f\u77e5\u9053\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u77e5\u9053\u7684\u4e8b\uff0c\u4e5f\u8bb8\u6bd4\u4f60\u60f3\u8c61\u4e2d\u8981\u591a\u4e9b\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6768\u5929\u4e00\u76f4\u4e0d\u6562\u5bf9\u4f60\u4e0b\u624b\uff0c\u4e3a\u4ec0\u4e48\u5ffd\u7136\u6709\u4e86\u52c7\u6c14\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u8fd9\u662f\u7b2c\u4e00\u70b9\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u7b49\u7684\u672c\u662f\u5b64\u5cf0\uff0c\u4ed6\u4e3a\u4ec0\u4e48\u4e5f\u6070\u5de7\u5728\u90a3\u65f6\u5165\u57ce\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u8fd9\u662f\u7b2c\u4e8c\u70b9\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6768\u5929\u82e5\u4e0d\u662f\u5b64\u5cf0\uff1f\u8c01\u624d\u662f\u5b64\u5cf0\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u8fd9\u662f\u7b2c\u4e09\u70b9\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u5b64\u5cf0\u82e5\u6ca1\u6709\u548c\u591a\u5c14\u7532\u7ea6\u597d\u5728\u5ef6\u5e73\u95e8\u76f8\u89c1\uff0c\u591a\u5c14\u7532\u8eab\u4e0a\u600e\u4e48\u4f1a\u6709\u90a3\u5f20\u8840\u4e66\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u8fd9\u662f\u7b2c\u56db\u70b9\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u58a8\u4e5d\u661f\u672c\u662f\u4e2a\u9690\u58eb\uff0c\u4e3a\u4ec0\u4e48\u4e00\u5230\u957f\u5b89\uff0c\u5c31\u80fd\u627e\u51fa\u591a\u5c14\u7532\u7684\u4e0b\u843d\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u8fd9\u662f\u7b2c\u4e94\u70b9\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u58a8\u4e5d\u661f\u65e2\u7136\u7ec8\u5e74\u5e38\u98df\u4e94\u6bd2\uff0c\u600e\u4e48\u4f1a\u90a3\u4e48\u5bb9\u6613\u5c31\u88ab\u6bd2\u6b7b\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u8fd9\u662f\u7b2c\u516d\u70b9\uff0c\u201d\u53f6\u5f00\u9053\uff1a\u201c\u82e6\u7af9\u672c\u662f\u4e2a\u5c40\u5916\u4eba\uff0c\u4e3a\u4ec0\u4e48\u4e5f\u4f1a\u5ffd\u7136\u60e8 \u6b7b\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u7b11\u9053\uff1a\u201c\u73b0\u5728\u4f60\u7684\u63a8\u6d4b\u597d\u50cf\u5df2\u6709\u4e86\u516d\u70b9\u6f0f\u6d1e\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u53ea\u6709\u516d\u70b9\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u65e0\u8bba\u8c01\u7684\u63a8\u6d4b\uff0c\u82e5\u662f\u6709\u4e86\u516d\u70b9\u6f0f\u6d1e\uff0c\u8fd9\u63a8\u6d4b\u6839\u672c\u4e0d\u80fd\u6210\u7acb\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u53ef\u662f\u6211\u8fd9\u63a8\u6d4b\u4e00\u5b9a\u80fd\u6210\u7acb\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u56e0\u4e3a\u8fd9\u516d\u70b9\u6f0f\u6d1e\uff0c\u6211\u90fd\u80fd\u89e3\u91ca\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4f60\u8bf4\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6f0f\u6d1e\u867d\u7136\u6709\u516d\u70b9\uff0c\u89e3\u91ca\u5374\u53ea\u6709\u4e00\u4e2a\uff0c\u53ea\u8981\u7528\u4e24\u53e5\u8bdd\u5c31\u80fd\u8bf4\u51fa\u6765\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6211\u5728\u542c\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u5b64\u5cf0\u5c31\u662f\u4f60\uff0c\u58a8\u4e5d\u661f\u4e5f\u662f\u4f60\uff01\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u53c8\u7b11\u4e86\u3002 \u3000\u3000\u2014\u2014\u4f60\u82e5\u5f88\u559c\u6b22\u4e00\u4e2a\u4eba\uff0c\u5e38\u5e38\u548c\u8fd9\u4e2a\u4eba\u89c1\u9762\uff0c\u4ed6\u7684\u6bdb\u75c5\uff0c\u4f60\u4e5f\u4e00\u5b9a\u4f1a\u4f20\u67d3\u4e0a\u7684\uff0c\u4e0a\u5b98\u5c0f \u4ed9\u663e\u7136\u5df1\u5b66\u4f1a\u4e86\u53f6\u5f00\u7684\u6bdb\u75c5\uff0c\u5230\u4e86\u65e0\u53ef\u5948\u4f55\u7684\u65f6\u5019\uff0c\u9047\u7740\u4e86\u56f0\u96be\u5371\u9669\u7684\u4e8b\uff0c\u5979\u4e5f\u4f1a\u7b11\uff0c\u53ea\u4e0d \u8fc7\u5979\u7b11\u5f97\u6bd4\u53f6\u5f00\u66f4\u751c\u3002 \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u5c31\u56e0\u4e3a\u4f60\u662f\u5b64\u5cf0\uff0c\u6240\u4ee5\u6768\u5929\u624d\u6562\u4e0b\u624b\uff0c\u56e0\u4e3a\u4ed6\u53d1\u73b0\u4f60\u5df1\u53d7\u4e86\u4f24\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u8fd9\u662f\u7b2c\u4e00\u4e2a\u89e3\u91ca\uff0c\u597d\u50cf\u8fd8\u5f88\u5408\u7406\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u5c31\u56e0\u4e3a\u4f60\u662f\u5b64\u5cf0\uff0c\u6240\u4ee5\u624d\u8981\u6768\u5929\u505a\u4f60\u7684\u66ff\u7f6a\u7f94\u7f8a\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u8fd9\u4e5f\u6709\u7406\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u53ea\u6709\u4f60\u624d\u77e5\u9053\u5415\u8fea\u662f\u591a\u5c14\u7532\uff0c\u4e5f\u53ea\u6709\u4f60\u624d\u80fd\u7ea6\u4ed6\u5230\u5341\u65b9\u7af9\u6797\u5bfa\u53bb\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6240\u4ee5\u58a8\u4e5d\u661f\u4e5f\u662f\u6211\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u6545\u610f\u5728\u8138\u4e0a\u5d4c\u8d77\u4e5d\u9897\u5bd2\u661f\uff0c\u59cb\u7ec8\u4e0d\u80af\u6458\u4e0b\u90a3\u9876\u8349\u5e3d\uff0c\u53ea\u56e0\u4e3a\u4f60\u7684\u6613\u5bb9\u672f\u867d \u7cbe\u5999\uff0c\u8fd8\u662f\u6015\u6211\u8ba4\u51fa\u4f60\u6765\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u53ef\u662f\u6211\u4e3a\u4ec0\u4e48\u8981\u626e\u6210\u58a8\u4e5d\u661f\u5462\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u56e0\u4e3a\u4f60\u8981\u6740\u591a\u5c14\u7532\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6211\u8981\u6740\u4ed6\uff1f\u4e3a\u4ec0\u4e48\u8981\u4f60\u53bb\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u56e0\u4e3a\u4f60\u8981\u8ba9\u6211\u4eb2\u773c\u770b\u89c1\u591a\u5c14\u7532\u7684\u6b7b\uff0c\u662f\u6b7b\u5728\u58a8\u4e5d\u661f\u624b\u91cc\u7684\u3002\u201d\u4ed6\u63a5\u7740\u53c8\u9053\uff1a \u201c\u591a\u5c14\u7532\u5f88\u53ef\u80fd\u4e5f\u77e5\u9053\u58a8\u4e5d\u661f\u662f\u4f60\uff0c\u6240\u4ee5\u4ed6\u90a3\u6700\u540e\u4e00\u7740\u6740\u624b\u5e76\u6ca1\u6709\u771f\u7684\u4f7f\u51fa\u6765\uff0c\u60f3\u4e0d\u5230\u4f60\u5374 \u4e58\u673a\u6740\u4e86\u4ed6\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u5728\u542c\u7740\u3002 \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u90a3\u672c\u662f\u6545\u610f\u6f14\u7ed9\u6211\u770b\u7684\u4e00\u51fa\u620f\uff0c\u591a\u5c14\u7532\u4e5f\u662f\u4e32\u901a\u597d\u4e86\u6f14\u620f\u7684\uff0c\u5c31\u8fde\u4f60\u4eec\u8bf4\u7684 \u90a3\u4e9b\u8bdd\uff0c\u4e5f\u50cf\u662f\u51fa\u620f\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4ed6\u4e3a\u4ec0\u4e48\u8981\u6765\u6f14\u8fd9\u51fa\u620f\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u56e0\u4e3a\u4f60\u4eec\u6f14\u8fd9\u51fa\u620f\u672c\u662f\u4e3a\u4e86\u8981\u6740\u6211\uff0c\u6240\u4ee5\u4ed6\u518d\u4e09\u8ddf\u6211\u7ea6\u5b9a\uff0c\u4e0d\u8bb8\u6211\u7684\u98de\u5200\u51fa \u624b\uff0c\u597d\u8ba9\u4f60\u6709\u673a\u4f1a\u6740\u6211\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6211\u5e76\u6ca1\u6709\u6740\u4f60\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u6ca1\u6709\uff0c\u56e0\u4e3a\u4f60\u771f\u6b63\u8981\u6740\u7684\u5e76\u4e0d\u662f\u6211\uff0c\u800c\u662f\u591a\u5c14\u7532\uff0c\u4ed6\u81f3\u6b7b\u4e5f\u60f3\u4e0d\u5230\u90a3\u51fa\u620f \u6700\u540e\u7684\u7ed3\u5c40\u7adf\u4f1a\u5ffd\u7136\u53d8\u4e86\u3002\u201d \u3000\u3000\u60f3\u5230\u591a\u5c14\u7532\u4e34\u6b7b\u65f6\u773c\u775b\u91cc\u7684\u60ca\u8bb6\u548c\u75db\u82e6\uff0c\u53f6\u5f00\u4e5f\u4e0d\u7981\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4ed6\u6b7b\u5f97\u5b9e\u5728\u5f88\u51a4 \u6789\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4f60\u540c\u60c5\u4ed6\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u53ea\u540c\u60c5\u4ed6\u7684\u6b7b\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u6de1\u6de1\u9053\uff1a\u201c\u6bcf\u4e2a\u4eba\u90fd\u8981\u6b7b\u7684\uff0c\u4ed6\u6b7b\u5f97\u51a4\u6789\uff0c\u53ea\u56e0\u4e3a\u4ed6\u672c\u5c31\u662f\u4e2a\u611a\u8822\u7684\u4eba\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4ed6\u611a\u8822\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u611a\u8822\u4e5f\u6709\u5f88\u591a\u79cd\uff0c\u50b2\u6162\u81ea\u5927\u5c82\u975e\u4e5f\u662f\u5176\u4e2d\u7684\u4e00\u79cd\u3002\u201d \u3000\u3000\u53f6\u5f00\u65e0\u6cd5\u8fa9\u9a73\u3002\u50b2\u6162\u81ea\u5927\u7684\u786e\u662f\u79cd\u611a\u8822\uff0c\u800c\u4e14\u5f88\u53ef\u80fd\u5c31\u662f\u6700\u4e25\u91cd\u7684\u4e00\u4e00\u79cd\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4f46\u662f\u6211\u5e76\u4e0d\u611a\u8822\uff0c\u73b0\u5728\u6211\u603b\u7b97\u5df2\u660e\u767d\u4f60\u7684\u610f\u601d\u4e86\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u5e94\u8be5\u660e\u767d\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4f60\u8bf4\u6211\u626e\u6210\u4e86\u58a8\u4e5d\u661f\uff0c\u518d\u5c06\u5415\u8fea\u627e\u53bb\uff0c\u8ba1\u5212\u6740\u4f60\uff0c\u5230\u6700\u540e\u5374\u53cd\u800c\u6740\u4e86 \u4ed6\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u542c\u8d77\u6765\u8fd9\u7684\u786e\u662f\u4ef6\u5f88\u8352\u8c2c\u7684\u4e8b\uff0c\u53ef\u662f\u8fd9\u8ba1\u5212\u5374\u7edd\u5bf9\u6709\u6548\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4e5f\u8bb8\u5c31\u56e0\u4e3a\u5b83\u4e0d\u53ef\u601d\u8bae\uff0c\u6240\u4ee5\u624d\u6709\u6548\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u90a3\u5c01\u8840\u4e66\u5f53\u7136\u4e5f\u662f\u8fd9\u8ba1\u5212\u7684\u4e00\u90e8\u5206\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6768\u5929\u81ea\u5df1\u5f53\u7136\u4e5f\u77e5\u9053\u4ed6\u7684\u79d8\u5bc6\u8fdf\u65e9\u4f1a\u88ab\u4f60\u53d1\u73b0\uff0c\u5df2\u51b3\u5b9a\u9003\u8d70\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u91d1\u94b1\u5e2e\u7684\u52bf\u529b\u904d\u5e03\u5929\u4e0b\uff0c\u4ed6\u80fd\u9003\u5230\u54ea\u91cc\u53bb\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4ed6\u5df2\u53d7\u8fc7\u8fd9\u4e00\u6b21\u6559\u8bad\uff0c\u8fd9\u6b21\u7684\u884c\u52a8\uff0c\u5f53\u7136\u7279\u522b\u5c0f\u5fc3\uff0c\u6240\u4ee5\u4ed6\u9009\u6765\u9009\u53bb\uff0c\u624d\u9009 \u4e86\u4e2a\u4f60\u6599\u60f3\u4e0d\u5230\u7684\u5730\u65b9\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4ec0\u4e48\u5730\u65b9\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u957f\u5b89\u57ce\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u8fd9\u91cc\u5c31\u662f\u957f\u5b89\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4ed6\u7b97\u51c6\u4f60\u4e00\u5b9a\u4f1a\u8ba4\u4e3a\u4ed6\u5df2\u9003\u5230\u4e86\u5f88\u8fdc\u7684\u5730\u65b9\u53bb\uff0c\u6240\u4ee5\u5c31\u504f\u504f\u9009\u4e86\u4e2a\u6700\u8fd1\u7684\u5730 \u65b9\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u4e5f\u627f\u8ba4\u8fd9\u5730\u65b9\u7684\u786e\u9009\u5f97\u4e0d\u9519\u3002 \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u4ed6\u53c8\u5c06\u8fd9\u8ba1\u5212\u544a\u8bc9\u4e86\u738b\u5be1\u5987\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4ed6\u4e0d\u80fd\u4e0d\u544a\u8bc9\u5979\uff0c\u4e00\u4e2a\u53d7\u4e86\u91cd\u4f24\u7684\u4eba\u8981\u8131\u9003\uff0c\u4e00\u5b9a\u8981\u4eba\u5e2e\u5fd9\u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4ed6\u544a\u8bc9\u4e86\u738b\u5be1\u5987\uff0c\u5c31\u7b49\u4e8e\u544a\u8bc9\u4e86\u4f60\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6211\u77e5\u9053\u4ed6\u9003\u4ea1\u7684\u8ba1\u5212\u540e\uff0c\u5c31\u4f2a\u9020\u4e86\u90a3\u5c01\u8840\u4e66\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u3002\u2018\u4f60\u7b97\u51c6\u6211\u770b\u5230\u90a3\u5c01\u8840\u4e66\u540e\uff0c\u5c31\u4e00\u5b9a\u4f1a\u5728\u5ef6\u5e73\u95e8\u7b49\u7740\u7684\u3002\u201d\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a \u201c\u8fd9\u5c01\u8840\u4e66\u53c8\u600e\u4e48\u4f1a\u5230\u4e86\u5415\u8fea\u8eab\u4e0a\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u8840\u4e66\u672c\u4e0d\u5728\u5415\u8fea\u8eab\u4e0a\uff0c\u662f\u82e6\u7af9\u7279\u5730\u9001\u6765\u7684\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u82e6\u7af9\u4e5f\u662f\u8fd9\u4ef6\u4e8b\u7684\u540c\u8c0b\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6240\u4ee5\u4ed6\u624d\u4f1a\u88ab\u4f60\u6740\u4e86\u706d\u53e3\uff0c\u6240\u6709\u8ddf\u8fd9\u4ef6\u4e8b\u6709\u5173\u7684\u4eba\uff0c\u90fd\u5df2\u88ab\u4f60\u6740\u4e86\u706d\u53e3\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u5b8b\u8001\u677f\u548c\u90a3\u5de8\u4eba\u5462\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4ed6\u4eec\u662f\u6768\u5929\u7684\u670b\u53cb\uff0c\u770b\u89c1\u6211\u5728\u5ef6\u5e73\u95e8\uff0c\u4e5f\u6545\u610f\u6f14\u4e86\u51fa\u620f\uff0c\u597d\u63a9\u62a4\u6768\u5929\u4eba\u57ce\uff0c \u6768\u5929\u662f\u600e\u4e48\u53d7\u4e86\u4f24\uff0c\u4ed6\u4eec\u5f53\u7136\u77e5\u9053\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u8fd9\u79d8\u5bc6\u5f53\u7136\u4e0d\u80fd\u8ba9\u4f60\u77e5\u9053\uff0c\u6240\u4ee5\u6211\u5c31\u5c06\u4ed6\u4eec\u4e5f\u6740\u4e86\u706d\u53e3\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u65e9\u5df2\u7b97\u51c6\u4f60\u6709\u8fd9\u4e00\u7740\uff0c\u6240\u4ee5\u4ed6\u6b7b\u4e86\uff0c\u6211\u5e76\u4e0d\u610f\u5916\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8fd9\u4e48\u6837\u4e00\u6765\uff0c\u6211\u6740\u7684\u4eba\u5012\u771f\u4e0d\u5c11\u3002\u201d \u3000\u3000\u53f6\u5f00\u75b2\u4e4f\u4ed6\u8bf4\uff1a\u201c\u7684\u786e\u4e0d\u5c11\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6211\u751a\u81f3\u8fd8\u4f1a\u81ea\u5df1\u6740\u81ea\u5df1\u3002\u201d \u3000\u3000\u5979\u53c8\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5047\u5982\u6211\u5c31\u662f\u58a8\u4e5d\u661f\uff0c\u5c82\u975e\u81ea\u5df1\u6740\u4e86\u81ea\u5df1\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6b7b\u7684\u58a8\u4e5d\u661f\u5e76\u4e0d\u662f\u4f60\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4e0d\u662f\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u77e5\u9053\u6211\u4e00\u5b9a\u4e0d\u4f1a\u6709\u90a3\u4e48\u597d\u7684\u80c3\u53e3\u966a\u4f60\u5403\u90a3\u7c97\u996d\uff0c\u6240\u4ee5\u65e9\u5df2\u51c6\u5907\u4e86\u66ff\u6b7b\u9b3c\uff0c \u7b49\u6211\u4e00\u8d70\uff0c\u4f60\u5c31\u6bd2\u6740\u4e86\u4ed6\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u56e0\u4e3a\u58a8\u4e5d\u661f\u4e00\u6b7b\uff0c\u8fd9\u4ef6\u4e8b\u5c31\u6b7b\u65e0\u5bf9\u8bc1\u4e86\u3002\u8fd9\u672c\u5c31\u662f\u4e2a\u6781\u5468\u5bc6\u7684\u8ba1\u5212\uff0c\u4e5f \u662f\u4e2a\u5f88\u597d\u542c\u7684\u6545\u4e8b\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u4e5f\u5e0c\u671b\u8fd9\u53ea\u4e0d\u8fc7\u662f\u4e2a\u6545\u4e8b\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u4eff\u4f5b\u5f88\u5403\u60ca\uff0c\u9053\uff1a\u201c\u96be\u9053\u8fd9\u4e0d\u662f\u6545\u4e8b\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u7684\u5de7\u5408\u592a\u591a\uff0c\u53ea\u6709\u771f\u5b9e\u7684\u4e8b\u624d\u4f1a\u6709\u8fd9\u4e48\u591a\u5de7\u5408\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u96be\u9053\u771f\u5b9e\u7684\u4e8b\u6bd4\u6545\u4e8b\u8fd8\u79bb\u5947\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u901a\u5e38\u90fd\u662f\u8fd9\u6837\u7684\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u5ae3\u7136\u9053\uff1a\u201c\u542c\u4f60\u8fd9\u4e48\u8bf4\uff0c\u8fde\u6211\u81ea\u5df1\u90fd\u6709\u70b9\u76f8\u4fe1\u8fd9\u4ef6\u4e8b\u662f\u771f\u7684\u4e86\u3002\u201d \u3000\u3000\u5979\u7b11\u5f97\u8fd8\u662f\u90a3\u4e48\u7eaf\u771f\u751c\u7f8e\uff1a\u201c\u53ef\u662f\uff0c\u6211\u7684\u8ba1\u5212\u65e2\u7136\u6781\u5468\u5bc6\uff0c\u600e\u4e48\u4f1a\u88ab\u4f60\u770b\u7834\u7684\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u65e0\u8bba\u591a\u5468\u5bc6\u7684\u8ba1\u5212\uff0c\u90fd\u96be\u514d\u6709\u6f0f\u6d1e\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u8fd9\u8ba1\u5212\u4e5f\u6709\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u63a8\u6d4b\u4e2d\u7684\u90a3\u4e9b\u6f0f\u6d1e\uff0c\u4e5f\u6b63\u662f\u4f60\u8ba1\u5212\u7684\u6f0f\u6d1e\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u56e0\u4e3a\u4f60\u82e5\u4e0d\u662f\u5b64\u5cf0\uff0c\u5c31\u7edd\u4e0d\u80fd\u6709\u8fd9\u4e48\u591a\u5de7\u5408\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u73b0\u5728\u4f60\u5df2\u5b8c\u5168\u786e\u5b9a\u4e86\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u76f4\u7b49\u5230\u6211\u770b\u5230\u4ed6\u4eec\u7684\u4f24\u53e3\u540e\uff0c\u624d\u5b8c\u5168\u786e\u5b9a\u7684\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4ed6\u4eec\u662f\u4e9b\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6768\u5929\u3001\u5b8b\u8001\u677f\u3001\u5de8\u4eba\u548c\u82e6\u7af9\uff0c\u4ed6\u4eec\u672c\u662f\u5907\u4e0d\u76f8\u5173\u7684\u4eba\uff0c\u672c\u4e0d\u53ef\u80fd\u6b7b\u5728\u540c\u4e00\u4e2a \u4eba\u624b\u91cc\uff0c\u53ef\u662f\u4ed6\u4eec\u81f4\u547d\u7684\u4f24\u53e3\u5374\u5b8c\u5168\u4e00\u6837\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8fd9\u5b9e\u5728\u5de7\u5f97\u5f88\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u5de7\u5408\u4e5f\u5c31\u662f\u6f0f\u6d1e\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6240\u4ee5\u6211\u4e0d\u4f46\u662f\u91d1\u94b1\u5e2e\u7684\u5e2e\u4e3b\uff0c\u4e5f\u662f\u9b54\u6559\u4e2d\u7684\u56db\u5927\u5929\u738b\u4e4b\u4e00\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u662f\u5b64\u5cf0\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u83ab\u5fd8\u8bb0\u91d1\u94b1\u5e2e\u548c\u9b54\u6559\u672c\u662f\u52bf\u4e0d\u4e24\u7acb\u7684\u5bf9\u5934\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u6ca1\u6709\u5fd8\u8bb0\u3002\u201d \u3000\u3000\u53f6\u5f00\u63a5\u7740\u9053\uff1a\u201c\u90a3\u4e48\u91d1\u94b1\u5e2e\u7684\u5e2e\u4e3b\u662f\u806a\u660e\u4eba\uff0c\u4ed6\u77e5\u9053\u5c06\u654c\u4eba\u6d88\u706d\u5e76\u4e0d\u662f\u6700\u597d\u7684\u6cd5\u5b50\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4ec0\u4e48\u6cd5\u5b50\u624d\u662f\u6700\u597d\u7684\u6cd5\u5b50\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6536\u670d\u4ed6\uff0c\u5229\u7528\u4ed6\uff0c\u5c06\u654c\u4eba\u7684\u529b\u91cf\uff0c\u53d8\u6210\u81ea\u5df1\u7684\u6b66\u5668\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u8fd9\u6cd5\u5b50\u7684\u786e\u4e0d\u9519\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u53ef\u662f\u9b54\u6559\u7684\u7ec4\u7ec7\u592a\u79d8\u5bc6\uff0c\u529b\u91cf\u592a\u5e9e\u5927\uff0c\u8981\u60f3\u6536\u670d\u4ed6\uff0c\u4e5f\u53ea\u6709\u4e00\u4e2a\u6cd5\u5b50\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4ec0\u4e48\u6cd5\u5b50\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u505a\u9b54\u6559\u7684\u6559\u4e3b\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u8981\u60f3\u505a\u9b54\u6559\u7684\u6559\u4e3b\uff0c\u5c31\u4e00\u5b9a\u8981\u5165\u9b54\u6559\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u5165\u4e86\u9b54\u6559\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u9b54\u6559\u81ea\u4ece\u8001\u6559\u4e3b\u53bb\u4e16\u540e\uff0c\u6743\u529b\u5c31\u88ab\u56db\u5927\u5929\u738b\u5206\u8d70\u4e86\uff0c\u8c01\u4e5f\u4e0d\u613f\u518d\u9009\u65b0\u7684 \u6559\u4e3b\uff0c\u628a\u81ea\u5df1\u5df2\u5f97\u5230\u7684\u6743\u529b\u518d\u4ea4\u56de\u53bb\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u56db\u5927\u65e0\u738b\u82e5\u662f\u5df2\u6b7b\u4e86\u4e09\u4e2a\u5462\uff1f\u201d \u3000\u3000\u4e0a\u5bab\u5c0f\u4ed9\u5ae3\u7136\u9053\uff1a\u201c\u90a3\u4e48\u5269\u4e0b\u7684\u4e00\u4e2a\uff0c\u5c31\u7b97\u60f3\u4e0d\u505a\u6559\u4e3b\uff0c\u53ea\u6015\u90fd\u56f0\u96be\u5f97\u5f88\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u50cf\u591a\u5c14\u7532\u4ed6\u4eec\u90a3\u79cd\u4eba\uff0c\u662f\u7edd\u4e0d\u4f1a\u6b7b\u5f97\u592a\u5feb\u7684\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u5f53\u7136\u4e0d\u4f1a\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u5f53\u7136\u4e5f\u4e0d\u80fd\u4eb2\u81ea\u51fa\u9762\u5bf9\u4ed8\u4ed6\u4eec\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6211\u505a\u4e8b\u4e00\u5411\u4e0d\u613f\u592a\u5192\u9669\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4ed6\u4eec\u4e5f\u8bb8\u81f3\u6b7b\u90fd\u4e0d\u77e5\u9053\u91d1\u94b1\u5e2e\u7684\u5e2e\u4e3b\u5c31\u662f\u4f60\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4ed6\u4eec\u8fde\u505a\u68a6\u90fd\u6ca1\u6709\u60f3\u5230\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u53ea\u6709\u7528\u4e00\u79cd\u6cd5\u5b50\u624d\u80fd\u6740\u5f97\u4e86\u4ed6\u4eec\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4f60\u8bf4\u7528\u4ec0\u4e48\u6cd5\u5b50\u6700\u597d\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u501f\u522b\u4eba\u7684\u5200\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u629a\u638c\u9053\uff1a\u201c\u5bf9\u4e86\uff0c\u8981\u6740\u4ed6\u4eec\u90a3\u6837\u7684\u4eba\uff0c\u4e00\u5b9a\u8981\u501f\u522b\u4eba\u7684\u5200\uff0c\u800c\u4e14\u8fd8\u8981\u501f\u4e00\u628a\u7279 \u522b\u7684\u5200\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u53ef\u662f\u4f60\u4e5f\u77e5\u9053\uff0c\u6211\u7684\u5200\u867d\u5feb\uff0c\u5374\u5f88\u5c11\u6740\u4eba\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6240\u4ee5\u6211\u624d\u8d39\u4e86\u90a3\u4e48\u591a\u7684\u5fc3\u601d\uff0c\u7ed5\u4e86\u90a3\u4e48\u591a\u5708\u5b50\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f60\u4e00\u5b9a\u4e5f\u8fde\u505a\u68a6\u90fd\u6ca1\u6709\u60f3\u5230\uff0c\u8fd8\u662f\u6709\u4e2a\u4eba\u770b\u7a7f\u4e86\u4f60\u7684\u79d8\u5bc6\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u76ef\u7740\u4ed6\uff0c\u8fc7\u4e86\u5f88\u4e45\uff0c\u53f9\u9053\uff1a\u201c\u4f60\u65e2\u7136\u4ec0\u4e48\u4e8b\u90fd\u80fd\u770b\u5f97\u7a7f\uff0c\u4e3a\u4ec0\u4e48\u770b\u4e0d\u7a7f\u6211\u7684 \u5fc3\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u2026\u2026\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6211\u5bf9\u4f60\u662f\u771f\u662f\u5047\uff0c\u4f60\u96be\u9053\u4e00\u70b9\u4e5f\u770b\u4e0d\u51fa\uff1f\u201d \u3000\u3000\u5979\u7f8e\u4e3d\u7684\u773c\u775b\u91cc\uff0c\u6709\u4e00\u79cd\u8bf4\u4e0d\u51fa\u7684\u5e7d\u6028\u548c\u60b2\u4f24\uff0c\u8fd9\u7a76\u7adf\u662f\u771f\u662f\u5047\uff1f \u7b2c\u4e09\u5341\u4e94\u7ae0\u3000\u4e00\u51b3\u80dc\u8d1f>> \u53e4\u9f99\u300a\u4e5d\u6708\u9e70\u98de\u300b \u7b2c\u4e09\u5341\u4e94\u7ae0\u3000\u4e00\u51b3\u80dc\u8d1f \u3000\u3000\u53f6\u5f00\u518d\u6b21\u8f6c\u8fc7\u5934\uff0c\u907f\u5f00\u4e86\u5979\u7684\u76ee\u5149\u3002 \u3000\u3000\u65e0\u8bba\u662f\u771f\u7684\u4e5f\u597d\uff0c\u662f\u5047\u7684\u4e5f\u597d\uff0c\u73b0\u5728\u90fd\u5df2\u4e0d\u91cd\u8981\u4e86\u3002 \u3000\u3000\u53f6\u5f00\u4e5f\u4e0d\u7981\u957f\u957f\u53f9\u606f\uff0c\u9053\uff1a\u201c\u6211\u6765\u7684\u65f6\u5019\uff0c\u8fd8\u4e0d\u60f3\u63ed\u7a7f\u8fd9\u4ef6\u4e8b\u7684\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u56e0\u4e3a\u2026\u2026\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u662f\u4e0d\u662f\u56e0\u4e3a\u4f60\u8fd8\u6709\u70b9\u4e0d\u5fcd\uff1f\u201d \u3000\u3000\u53f6\u5f00\u82e6\u7b11\u3002 \u3000\u3000\u4ed6\u4e0d\u80fd\u5426\u8ba4\uff0c\u4e5f\u5e76\u4e0d\u662f\u771f\u7684\u5b8c\u5168\u770b\u4e0d\u51fa\u5979\u5bf9\u4ed6\u7684\u611f\u60c5\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4f60\u975e\u4f46\u4e0d\u5fcd\uff0c\u4e5f\u4e0d\u6562\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4e0d\u6562\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u56e0\u4e3a\u4f60\u6839\u672c\u8fde\u4e00\u70b9\u8bc1\u636e\u90fd\u6ca1\u6709\uff0c\u53ea\u51ed\u63a8\u6d4b\uff0c\u662f\u4e0d\u80fd\u5b9a\u4eba\u7f6a\u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u4e5f\u4e0d\u80fd\u5426\u8ba4\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u53ef\u662f\u4e01\u7075\u7433\u51fa\u4e86\u4e8b\uff0c\u4f60\u5c31\u7acb\u523b\u4e0d\u987e\u4e00\u5207\u4e86\u3002\u201d \u3000\u3000\u5979\u773c\u775b\u91cc\u7684\u60b2\u4f24\uff0c\u5ffd\u7136\u53c8\u53d8\u6210\u4e86\u5992\u6068\uff1a\u201c\u5979\u7a76\u7adf\u4e3a\u4f60\u505a\u4e86\u4e9b\u4ec0\u4e48\u4e8b\uff0c\u80fd\u8ba9\u4f60\u8fd9\u4e48\u6b7b\u5fc3\u584c \u5730\u5bf9\u5979\uff1f\u6211\u53c8\u6709\u54ea\u70b9\u6bd4\u4e0d\u4e0a\u5979\uff1f\u201d \u3000\u3000\u53f6\u5f00\u6c89\u9ed8\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u5979\u5230\u5904\u95ef\u7978\u751f\u4e8b\uff0c\u5230\u5904\u60f9\u9ebb\u70e6\uff0c\u8fd8\u51e0\u4e4e\u4e00\u5200\u628a\u4f60\u6740\u6b7b\uff0c\u4f60\u4e0d\u5728\u7684\u65f6\u5019\uff0c \u5979\u8fde\u534a\u5929\u90fd\u7b49\u4e0d\u5f97\uff0c\u5c31\u6025\u7740\u8981\u5ac1\u4eba\uff0c\u5ac1\u4e00\u6b21\u4eba\u8fd8\u4e0d\u591f\uff0c\u4e00\u591c\u95f4\u5979\u5c31\u5ac1\u7ed9\u4e86\u4e24\u4e2a\u7537\u4eba\uff0c\u50cf\u8fd9\u6837 \u4e00\u4e2a\u5973\u4eba\uff0c\u6709\u54ea\u70b9\u503c\u5f97\u4f60\u4e3a\u5979\u5982\u6b64\u727a\u7272\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u4e5f\u60f3\u4e0d\u901a\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u2026\u2026\u201d \u3000\u3000\u53f6\u5f00\u6253\u65ad\u4e86\u5979\u7684\u8bdd\uff0c\u9053\uff1a\u201c\u6211\u53ea\u77e5\u9053\uff0c\u5c31\u7b97\u5979\u518d\u6740\u6211\u5341\u6b21\uff0c\u518d\u5ac1\u7ed9\u5341\u4e2a\u7537\u4eba\uff0c\u6211\u8fd8\u662f\u4e00 \u6837\u4f1a\u8fd9\u4e48\u6837\u5bf9\u5979\u7684\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u77e5\u9053\u5979\u5bf9\u6211\u662f\u771f\u5fc3\u7684\uff0c\u6211\u4fe1\u4efb\u5979\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u970d\u7136\u7ad9\u8d77\u6765\uff0c\u53c8\u6162\u6162\u5730\u5750\u4e0b\u3002 \u3000\u3000\u5979\u5750\u4e0b\u65f6\uff0c\u5df2\u4e0d\u518d\u662f\u4e2a\u60c5\u611f\u6fc0\u52a8\u7684\u5973\u4eba\u3002 \u3000\u3000\u5979\u7ad9\u8d77\u6765\u65f6\uff0c\u60c5\u611f\u4eff\u4f5b\u8981\u5d29\u6e83\uff0c\u53ef\u662f\u7b49\u5230\u5979\u5750\u4e0b\u65f6\uff0c\u5979\u5df2\u53d8\u6210\u4e86\u51b7\u9177\u5982\u51b0\u5c71\u3001\u9510\u5229\u5982\u5200 \u950b\u7684\u91d1\u94b1\u5e2e\u5e2e\u4e3b\u3002 \u3000\u3000\u4e5f\u8bb8\u5973\u4eba\u672c\u5c31\u662f\u591a\u53d8\u7684\uff0c\u5979\u53ea\u4e0d\u8fc7\u53d8\u5f97\u6bd4\u4efb\u4f55\u4eba\u90fd\u5feb\u800c\u5df2\u3002 \u3000\u3000\u4e5f\u8bb8\u5979\u6839\u672c\u6ca1\u6709\u53d8\uff0c\u53d8\u7684\u53ea\u4e0d\u8fc7\u662f\u5979\u7684\u4f2a\u88c5\u3002 \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u73b0\u5728\u4f60\u8fd8\u6709\u4ec0\u4e48\u8bdd\u8bf4\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6ca1\u6709\u4e86\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4f46\u6211\u5374\u8fd8\u6709\u4e00\u70b9\u4e0d\u80fd\u4e0d\u8bf4\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u7684\u786e\u8fde\u4e00\u70b9\u8bc1\u636e\u90fd\u6ca1\u6709\uff0c\u8fd9\u4e9b\u4e8b\u4f60\u672c\u4e0d\u5fc5\u627f\u8ba4\u7684\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u5fc5\u5426\u8ba4\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u51b7\u51b7\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u4e0d\u4f46\u662f\u91d1\u94b1\u5e2e\u7684\u5e2e\u4e3b\uff0c\u8fd8\u662f\u9b54\u6559\u7684\u6559\u4e3b\uff0c\u6211\u4e0d\u4f46\u638c\u63e1\u4e86\u5929\u4e0b \u6700\u53ef\u6015\u7684\u4e24\u5927\u5e2e\u6d3e\uff0c\u8fd8\u638c\u63e1\u4e86\u4e01\u7075\u7433\u7684\u6027\u547d\uff0c\u6211\u65e0\u8bba\u662f\u627f\u8ba4\u4e5f\u597d\uff0c\u662f\u5426\u8ba4\u4e5f\u597d\uff0c\u4f60\u90fd\u53ea\u6709\u542c \u7740\u3002\u201d \u3000\u3000\u53f6\u5f00\u5f81\u4f4f\u3002 \u3000\u3000\u4ed6\u5ffd\u7136\u53d1\u73b0\u81ea\u5df1\u7684\u786e\u6ca1\u6cd5\u5b50\u5bf9\u4ed8\u5979\uff0c\u8fde\u4e00\u70b9\u6cd5\u5b50\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u73b0\u5728\u4f60\u8fd8\u6709\u4ec0\u4e48\u8bdd\u8bf4\uff1f\u201d \u3000\u3000\u53f6\u5f00\u7684\u786e\u5df2\u65e0\u8bdd\u53ef\u8bf4\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u90a3\u4e48\u6211\u8bf4\u7684\u8bdd\uff0c\u4f60\u5c31\u8981\u542c\u7740\uff0c\u6bcf\u4e2a\u5b57\u90fd\u4ed4\u7ec6\u542c\u7740\u3002\u201d \u3000\u3000\u53f6\u5f00\u6ca1\u6709\u542c\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u5ffd\u7136\u542c\u89c1\u4e86\u53e6\u4e00\u4e2a\u4eba\u8bf4\u8bdd\u7684\u58f0\u97f3\uff1a\u201c\u5979\u8bf4\u7684\u8bdd\uff0c\u4f60\u8fde\u4e00\u4e2a\u5b57\u90fd\u4e0d\u5fc5\u542c\uff0c\u56e0\u4e3a\uff0c\u5979 \u6839\u672c\u5c31\u662f\u5728\u653e\u5c41\u3002\u201d \u3000\u3000\u58f0\u97f3\u662f\u4ece\u5e8a\u4e0b\u53d1\u51fa\u6765\u7684\u3002 \u3000\u3000\u5e8a\u4e0b\u9762\u660e\u660e\u53ea\u6709\u4e00\u4e2a\u4eba\uff0c\u4e00\u4e2a\u6b7b\u4eba\uff0c\u6b7b\u4eba\u600e\u4e48\u80fd\u8bf4\u8bdd\uff1f \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u662f\u4e2a\u7edd\u9876\u806a\u660e\u7684\u4eba\uff0c\u53f6\u5f00\u4e5f\u662f\u7684\uff0c\u4f46\u5374\u8fde\u4ed6\u4eec\u4e5f\u60f3\u4e0d\u901a\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f \u3000\u3000\u4e00\u4ef6\u4e8b\u82e5\u8fde\u4ed6\u4eec\u90fd\u60f3\u4e0d\u901a\uff0c\u8fd9\u4e16\u4e0a\u8fd8\u6709\u8c01\u80fd\u60f3\u5f97\u901a\u5462\uff1f \u3000\u3000\u5e8a\u4e0b\u9762\u660e\u660e\u53ea\u6709\u4e00\u4e2a\u6b7b\u4eba\uff0c\u4ed6\u4eec\u521a\u624d\u8fd8\u62ac\u8d77\u8fd9\u5f20\u5e8a\u6765\u770b\u8fc7\u3002 \u3000\u3000\u73b0\u5728\u8fd9\u5f20\u5e8a\u53c8\u88ab\u62ac\u4e86\u8d77\u6765\uff0c\u2014\u2014\u88ab\u4eba\u4ece\u4e0b\u9762\u5f80\u4e0a\u62ac\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u7684\u5fc3\u5374\u5728\u5f80\u4e0b\u6c89\u3002 \u3000\u3000\u2014\u2014\u521a\u624d\u8bf4\u8bdd\u7684\u4eba\uff0c\u7adf\u662f\u4e86\u7075\u7433\uff0c\u5979\u542c\u5f97\u51fa\u4e01\u7075\u7433\u7684\u58f0\u97f3\u3002 \u3000\u3000\u53ef\u662f\u4e01\u7075\u7433\u600e\u4e48\u4f1a\u5728\u5e8a\u4e0b\u7684\uff1f\u6b7b\u4e86\u7684\u97e9\u8d1e\u600e\u4e48\u4f1a\u53d8\u6210\u6d3b\u7684\u4e01\u7075\u7433\uff1f \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u5c31\u60f3\u4e0d\u901a\u4e86\u3002 \u3000\u3000\u53f6\u5f00\u4e5f\u60f3\u4e0d\u901a\u3002 \u3000\u3000\u2014\u2014\u4e00\u4ef6\u4e8b\u82e5\u8fde\u4ed6\u4eec\u4e5f\u60f3\u4e0d\u901a\uff0c\u4e16\u4e0a\u8fd8\u6709\u8c01\u80fd\u60f3\u5f97\u901a\uff1f \u3000\u3000\u53ea\u6709\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u5f53\u7136\u5c31\u662f\u4e01\u7075\u7433\u81ea\u5df1\u3002 \u3000\u3000\u4e01\u7075\u7433\u5e76\u6ca1\u6709\u771f\u75af\u3002 \u3000\u3000\u8fd9\u4e16\u4e0a\u4f1a\u88c5\u75f4\u7684\u5e76\u4e0d\u6b62\u4e0a\u5b98\u5c0f\u4ed9\u4e00\u4e2a\u4eba\uff0c\u4e01\u7075\u7433\u4e5f\u4f1a\u3002 \u3000\u3000\u4f60\u4f1a\u7684\u4e8b\uff0c\u6211\u90fd\u4f1a\u3002 \u3000\u3000\u5979\u4ece\u5e8a\u4e0b\u8d70\u51fa\u6765\uff0c\u770b\u7740\u4e0a\u5b98\u5c0f\u4ed9\uff0c\u773c\u775b\u91cc\u53d1\u7740\u5149\uff1a\u201c\u4f60\u4f1a\u9a97\u4eba\uff0c\u6211\u4e5f\u4f1a\uff0c\u4f60\u4f1a\u6740\u4eba\uff0c\u6211 \u4e5f\u4f1a\uff0c\u800c\u4e14\u7edd\u4e0d\u6bd4\u4f60\u5dee\u3002\u201d \u3000\u3000\u201c\u4f60\u8981\u97e9\u8d1e\u6765\u6740\u6211\uff0c\u518d\u60f3\u6cd5\u5b50\u8ba9\u5c0f\u53f6\u4ee5\u4e3a\u6211\u662f\u53d1\u75af\u800c\u6b7b\u7684\u3002\u201d \u3000\u3000\u201c\u4f60\u4e00\u5b9a\u60f3\u4e0d\u5230\u6211\u53cd\u800c\u6740\u4e86\u4ed6\u3002\u201d \u3000\u3000\u201c\u4f60\u4f1a\u5728\u6211\u7684\u7096\u9e21\u9762\u91cc\u4e0b\u8ff7\u836f\uff0c\u6211\u4e5f\u4f1a\u5728\u4ed6\u559d\u7684\u8336\u91cc\u4e0b\u8ff7\u836f\u3002\u201d \u3000\u3000\u201c\u4ed6\u5f53\u7136\u4e0d\u4f1a\u63d0\u9632\u4e00\u4e2a\u5df2\u53d1\u4e86\u75af\u7684\u5973\u4eba\uff0c\u5c31\u597d\u50cf\u6211\u4eec\u4ee5\u524d\u6ca1\u6709\u63d0\u9632\u4f60\u4e00\u6837\uff0c\u8fd9\u6cd5\u5b50\u672c\u662f \u6211\u4ece\u4f60\u90a3\u91cc\u5b66\u6765\u7684\u3002\u201d \u3000\u3000\u2014\u2014\u6b7b\u4e86\u7684\u97e9\u8d1e\u8fd8\u5728\u5e8a\u4e0b\uff0c\u8fd9\u6b21\u4ed6\u65e0\u7591\u662f\u771f\u7684\u6b7b\u4e86\u3002 \u3000\u3000\u201c\u6211\u5c06\u4ed6\u7684\u5c38\u4f53\u9001\u5230\u5e8a\u4e0b\u53bb\u7684\u65f6\u5019\uff0c\u624d\u53d1\u73b0\u5e8a\u4e0b\u9762\u6709\u4e2a\u5730\u7a96\uff0c\u662f\u85cf\u9152\u7684\u5730\u65b9\u3002\u539f\u6765\u51b7\u9999 \u56ed\u7684\u9152\u90fd\u662f\u85cf\u5728\u8fd9\u79cd\u5730\u7a96\u91cc\u7684\uff0c\u6240\u4ee5\u90a3\u5929\u6211\u4eec\u5728\u5916\u9762\u8fde\u4e00\u74f6\u9152\u90fd\u627e\u4e0d\u5230\u3002\u6211\u77e5\u9053\u4f60\u4eec\u4e00\u5b9a\u4f1a \u6765\uff0c\u6240\u4ee5\u6211\u5c31\u85cf\u5165\u5730\u7a96\u91cc\uff0c\u5374\u5c06\u5c38\u4f53\u6446\u5728\u5916\u9762\u3002\u6211\u7b97\u51c6\u4f60\u770b\u5230\u97e9\u8d1e\u6b7b\u4e86\u540e\uff0c\u4e00\u5b9a\u4f1a\u5927\u5403\u4e00 \u60ca\uff0c\u7edd\u4e0d\u4f1a\u518d\u6ce8\u610f\u5230\u4e0b\u9762\u8fd8\u6709\u4e2a\u5730\u7a96\u3002\u201d \u3000\u3000\u201c\u6211\u8fd8\u60f3\u542c\u542c\u4f60\u4eec\u5728\u4e0a\u9762\u8bf4\u4e9b\u4ec0\u4e48\uff0c\u770b\u4ed6\u662f\u4e0d\u662f\u4f1a\u88ab\u4f60\u9a97\u8d70\u3002\u201d \u3000\u3000\u5979\u770b\u7740\u53f6\u5f00\uff0c\u773c\u775b\u91cc\u5145\u6ee1\u4e86\u5e78\u798f\u7684\u5149\u8f89\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u5176\u5b9e\u6211\u4e5f\u77e5\u9053\u4f60\u8fd9\u6b21\u7edd\u4e0d\u4f1a\u518d\u4e0a\u5979 \u5f53\u7684\uff0c\u4f60\u679c\u7136\u6ca1\u6709\u8ba9\u6211\u5931\u671b\u3002\u201d \u3000\u3000\u5979\u8bf4\u5f97\u5f88\u7b80\u5355\u3002 \u3000\u3000\u65e0\u8bba\u591a\u66f2\u6298\u79bb\u5947\u7684\u4e8b\uff0c\u4e00\u8bf4\u7a7f\u4e86\uff0c\u4f60\u5c31\u4f1a\u53d1\u73b0\u5b83\u5e76\u4e0d\u50cf\u4f60\u60f3\u8c61\u4e2d\u90a3\u4e48\u590d\u6742\u3002 \u3000\u3000\u4e16\u4e0a\u672c\u5c31\u6709\u5f88\u591a\u4e8b\u90fd\u662f\u8fd9\u6837\u5b50\u7684\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u4e00\u76f4\u5728\u542c\u7740\uff0c\u82cd\u767d\u7f8e\u4e3d\u7684\u8138\u4e0a\uff0c\u5c45\u7136\u8fde\u4e00\u70b9\u8868\u60c5\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u7b49\u5230\u4e01\u7075\u7433\u8bf4\u5b8c\u4e86\uff0c\u5979\u624d\u6162\u6162\u5730\u62ac\u8d77\u624b\uff0c\u653e\u5728\u684c\u4e0a\u3002 \u3000\u3000\u5979\u90a3\u53cc\u7ea4\u67d4\u79c0\u6c14\u7684\u624b\uff0c\u7adf\u5ffd\u7136\u53d8\u5f97\u91d1\u5c5e\u822c\u575a\u786c\u3002 \u3000\u3000\u706f\u4e5f\u5728\u684c\u4e0a\u3002 \u3000\u3000\u5979\u7684\u624b\u5728\u706f\u4e0b\u53d1\u7740\u5149\u2014\u2014\u5e76\u4e0d\u662f\u5979\u7684\u624b\u5728\u53d1\u5149\uff0c\u662f\u4e00\u53cc\u91d1\u5c5e\u822c\u9510\u5229\u3001\u5374\u53c8\u50cf\u51b0\u4e00\u822c\u900f\u660e \u7684\u624b\u5957\u3002 \u3000\u3000\u90a3\u5929\u665a\u4e0a\uff0c\u5728\u9e3f\u5bbe\u5ba2\u6808\u7684\u540e\u5899\u5916\uff0c\u4e01\u7075\u7433\u770b\u89c1\u7684\u5c31\u662f\u8fd9\u53cc\u624b\u3002 \u3000\u3000\u5d14\u7389\u771f\u5728\u77ed\u5899\u5934\u8fdc\u8fdc\u770b\u89c1\u7684\u4e5f\u662f\u8fd9\u53cc\u624b\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u4f20\u8bf4\u4e2d\u7684\u91d1\u521a\u4e0d\u574f\uff0c\u5927\u641c\u795e\u624b\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u8fd9\u624b\u672c\u662f\u51c6\u5907\u7528\u6765\u5bf9\u4ed8\u5415\u8fea\u548c\u90ed\u5b9a\u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u770b\u5f97\u51fa\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u53ef\u60dc\u4ed6\u4eec\u5374\u8ba9\u6211\u637b\u4e86\u3002\u201d \u3000\u3000\u4ed6\u4eec\u6839\u672c\u6ca1\u6709\u7ed9\u5979\u673a\u4f1a\uff0c\u8ba9\u5979\u7528\u51fa\u8fd9\u79cd\u6b66\u5668\u3002 \u3000\u3000\u5979\u644a\u5f00\u624b\uff0c\u638c\u5fc3\u6709\u4e00\u679a\u6bd4\u7ee3\u82b1\u9488\u8fd8\u7ec6\u7684\u9488\uff1a\u201c\u8fd9\u662f\u6211\u7684\u4e0a\u5929\u5165\u5730\uff0c\u5927\u641c\u9b42\u9488\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6768\u5929\u4ed6\u4eec\u56db\u4e2a\u4eba\uff0c\u5c31\u662f\u6b7b\u5728\u6211\u8fd9\u79cd\u9488\u535e\u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u4e5f\u770b\u5f97\u51fa\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6614\u5e74\u6885\u82b1\u76d7\u7684\u6885\u82b1\u9488\uff0c\u5df2\u4eca\u5929\u4e0b\u6b66\u6797\u4e2d\u4eba\u4e27\u80c6\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u542c\u8bf4\u8fc7\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4f46\u662f\u6211\u53ef\u4ee5\u4fdd\u8bc1\uff0c\u6211\u8fd9\u79cd\u9488\u8fdc\u6bd4\u6885\u82b1\u9488\u66f4\u53ef\u6015\u3002\u201d \u3000\u3000\u53f6\u5f00\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u8fd9\u79cd\u9488\u60f3\u5fc5\u662f\u51c6\u5907\u7528\u6765\u5bf9\u4ed8\u6211\u7684\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u627f\u8ba4\u3002 \u3000\u3000\u76ef\u7740\u53f6\u5f00\uff0c\u5ffd\u53c8\u95ee\u9053\uff1a\u201c\u4f60\u7684\u5200\u5462\uff1f\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u5200\u5728\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u53f6\u5f00\u6ca1\u6709\u56de\u7b54\u3002 \u3000\u3000\u5929\u4e0a\u5730\u4e0b\uff0c\u4ece\u6765\u4e5f\u6ca1\u6709\u4eba\u77e5\u9053\u4ed6\u7684\u201c\u98de\u5200\u201d\u5728\u54ea\u91cc\uff0c\u4e5f\u6ca1\u6709\u4eba\u77e5\u9053\u5200\u662f\u600e\u4e48\u53d1\u51fa\u6765\u7684\u3002 \u3000\u3000\u5200\u672a\u51fa\u624b\u524d\uff0c\u8c01\u4e5f\u60f3\u8c61\u4e0d\u5230\u5b83\u7684\u901f\u5ea6\u548c\u529b\u91cf\u3002 \u3000\u3000\u5927\u5bb6\u53ea\u77e5\u9053\u4e00\u4ef6\u4e8b\u2014\u2014\u5200\u4e00\u5b9a\u5728\u5b83\u5e94\u8be5\u5728\u7684\u5730\u65b9\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u6162\u6162\u9053\uff1a\u201c\u6211\u4e5f\u77e5\u9053\u4f60\u7684\u5200\u662f\u65e0\u6240\u4e0d\u5728\uff0c\u65e0\u6240\u4e0d\u81f3\u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u5e76\u6ca1\u6709\u8c26\u865a\u3002 \u3000\u3000\u56e0\u4e3a\u5200\u867d\u7136\u662f\u4ed6\u7684\uff0c\u867d\u7136\u5728\u4ed6\u8eab\u4e0a\uff0c\u53ef\u662f\u8fd9\u79cd\u5200\u7684\u795e\u9ad3\uff0c\u5374\u8fd8\u662f\u522b\u4eba\u3002 \u3000\u3000\u4e00\u4e2a\u4f1f\u5927\u7684\u4eba\u3002 \u3000\u3000\u5929\u4e0a\u5730\u4e0b\uff0c\u4f60\u7edd\u5bf9\u627e\u4e0d\u5230\u4efb\u4f55\u4eba\u80fd\u4ee3\u66ff\u4ed6\u3002 \u3000\u3000\u82e5\u4e0d\u80fd\u4e86\u89e3\u4ed6\u90a3\u79cd\u4f1f\u5927\u7684\u7cbe\u795e\uff0c\u5c31\u7edd\u4e0d\u80fd\u53d1\u51fa\u90a3\u79cd\u53ef\u4ee5\u60ca\u5929\u52a8\u5730\u7684\u5200\u3002 \u3000\u3000\u98de\u5200\uff01\u98de\u5200\u8fd8\u672a\u5728\u624b\uff0c\u53ef\u662f\u5200\u7684\u7cbe\u795e\u5df2\u5728\u3002 \u3000\u3000\u90a3\u5e76\u4e0d\u662f\u6740\u6c14\uff0c\u4f46\u5374\u6bd4\u6740\u6c14\u66f4\u4ee4\u4eba\u80c6\u602f\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u7684\u77b3\u5b54\u5df2\u5728\u6536\u7f29\uff0c\u9053\uff1a\u201c\u4f60\u7684\u5200\u65e0\u6240\u4e0d\u5728\uff0c\u65e0\u6240\u4e0d\u81f3\uff0c\u6211\u7684\u9488\u4e5f\u4e00\u6837\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u54e6\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4f60\u4e5f\u6c38\u8fdc\u65e0\u6cd5\u60f3\u8c61\uff0c\u6211\u7684\u9488\u4f1a\u4ece\u4ec0\u4e48\u5730\u65b9\u53d1\u51fa\u6765\uff0c\u66f4\u65e0\u6cd5\u60f3\u8c61\u5b83\u662f\u600e\u4e48 \u53d1\u51fa\u6765\u7684\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u4e0d\u4f1a\u53bb\u60f3\uff0c\u4e5f\u4e0d\u5fc5\u60f3\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u51b7\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u82e5\u8ba4\u4e3a\u4f60\u80fd\u5c01\u4f4f\u6211\u7684\u51fa\u624b\uff0c\u4f60\u5c31\u9519\u4e86\u3002\u201d \u3000\u3000\u53f6\u5f00\u6c89\u9ed8\uff0c\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6211\u7684\u8ba1\u5982\u6052\u6cb3\u6c99\u6570\uff0c\u4f60\u7684\u5200\u5374\u6709\u9650\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u6211\u7684\u5200\u53ea\u8981\u4e00\u67c4\u5c31\u5df2\u8db3\u591f\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u8fde\u773c\u89d2\u90fd\u5728\u6536\u7f29\uff0c\u8fc7\u4e86\u5f88\u4e45\uff0c\u5ffd\u7136\u957f\u957f\u53f9\u606f\uff0c\u9053\uff1a\u201c\u4e5f\u8bb8\u8fd9\u5c31\u662f\u547d\u8fd0\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u547d\u8fd0\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u4e5f\u8bb8\u6211\u547d\u4e2d\u6ce8\u5b9a\uff0c\u8fdf\u65e9\u603b\u8981\u548c\u4f60\u4e00\u51b3\u80dc\u8d1f\u7684\u3002\u201d \u3000\u3000\u5979\u773c\u4e2d\u53c8\u9732\u51fa\u4e00\u62b9\u60b2\u4f24\uff1a\u201c\u6b63\u5982\u6614\u5e74\u7684\u4e0a\u5b98\u5e2e\u4e3b\uff0c\u662f\u547d\u4e2d\u6ce8\u5b9a\u4e86\u8981\u548c\u5c0f\u674e\u63a2\u82b1\u4e00\u51b3\u80dc\u8d1f \u4e00\u6837\u3002\u201d \u3000\u3000\u53f6\u5f00\u4e5f\u4e0d\u7981\u53f9\u606f\uff0c\u9053\uff1a\u201c\u6614\u5e74\u7684\u4e0a\u5b98\u5e2e\u4e3b\uff0c\u7684\u786e\u4e0d\u6127\u4e3a\u4e00\u4e16\u4e4b\u96c4\uff0c\u53ea\u53ef\u60dc\u73b0\u5728\u2026\u2026\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u6ca1\u6709\u8ba9\u4ed6\u8bf4\u4e0b\u53bb\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u6614\u5e74\u7684\u4e0a\u5b98\u5e2e\u4e3b\u867d\u5df2\u4e0d\u5728\uff0c\u4eca\u65e5\u7684\u4e0a\u5b98\u5e2e\u4e3b\u5374\u8fd8 \u5728\u3002\u201d \u3000\u3000\u53f6\u5f00\u9053\uff1a\u201c\u98de\u5200\u4e5f\u5728\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6614\u5e74\u4ed6\u4eec\u90a3\u4e00\u6218\uff0c\u867d\u8db3\u4ee5\u60ca\u5929\u5730\uff0c\u4f4d\u9b3c\u795e\uff0c\u5374\u6ca1\u6709\u4eba\u80fd\u4eb2\u773c\u770b\u5230\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u5fcd\u4e0d\u4f4f\u9053\uff1a\u201c\u4eca\u65e5\u4f60\u4eec\u8fd9\u4e00\u6218\uff0c\u5374\u4e00\u5b9a\u4f1a\u6709\u4eba\u4eb2\u773c\u770b\u5230\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u9053\uff1a\u201c\u6ca1\u6709\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff1a\u201c\u6709\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u970d\u7136\u8f6c\u5934\uff0c\u76ef\u7740\u5979\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u60f3\u770b\uff1f\u201d \u3000\u3000\u4e01\u7075\u7433\u9053\uff0c\u201c\u6211\u4e00\u5b9a\u80fd\u770b\u5f97\u5230\u3002\u201d \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u82e5\u5728\u8fd9\u91cc\uff0c\u6211\u7684\u98de\u9488\u51fa\u624b\uff0c\u7b2c\u4e00\u4e2a\u8981\u5bf9\u4ed8\u7684\u5c31\u662f\u4f60\uff0c\u4ed6\u82e5\u4e3a\u4f60\u5206 \u5fc3\u4ed6\u5c31\u53ea\u6709\u6b7b\u3002\u201d \u3000\u3000\u4e01\u7075\u7433\u6014\u4f4f\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u65e2\u6ca1\u6709\u518d\u8bf4\u4e00\u53e5\u8bdd\uff0c\u4e5f\u6ca1\u6709\u518d\u770b\u5979\u4e00\u773c\uff0c\u5979\u5374\u53ea\u6709\u8d70\u51fa\u53bb\u3002 \u3000\u3000\u5979\u8d70\u51fa\u53bb\u65f6\uff0c\u5168\u8eab\u90fd\u51b0\u51b7\u3002 \u3000\u3000\u95e8\u5173\u8d77\uff0c\u628a\u751f\u547d\u4e2d\u6240\u6709\u7684\u4e00\u5207\uff0c\u5168\u90fd\u5173\u5728\u95e8\u5916\u3002 \u3000\u3000\u95e8\u91cc\u5269\u4e0b\u7684\u53ea\u6709\u6b7b\uff1f \u3000\u3000\u6b7b\u7684\u662f\u8c01\uff1f\u4e01\u7075\u7433\u7684\u8170\u5f2f\u4e0b\uff0c\u51e0\u4e4e\u5df2\u5fcd\u4e0d\u4f4f\u8981\u5455\u5410\u3002 \u3000\u3000\u5979\u53c8\u6709\u4e86\u90a3\u79cd\u65e0\u53ef\u5948\u4f55\u7684\u611f\u89c9\uff0c\u8fd9\u79cd\u611f\u89c9\u624d\u771f\u7684\u80fd\u8ba9\u5979\u53d1\u75af\u3002 \u3000\u3000\u53ef\u662f\u53d1\u75af\u4e5f\u6ca1\u6709\u7528\u3002 \u3000\u3000\u6614\u5e74\u90a3\u4e00\u6218\uff0c\u5979\u867d\u7136\u6ca1\u6709\u89c1\u5230\uff0c\u5374\u542c\u8bf4\u8fc7\u3002 \u3000\u3000\u5c31\u8fde\u5c0f\u674e\u63a2\u82b1\u81ea\u5df1\u4e5f\u627f\u8ba4\uff0c\u4e0a\u5b98\u91d1\u8679\u7684\u786e\u6709\u5f88\u591a\u673a\u4f1a\u53ef\u4ee5\u6740\u4ed6\uff0c\u751a\u81f3\u8fd8\u53ef\u4ee5\u4ee4\u4ed6\u65e0\u6cd5\u8fd8 \u624b\u3002 \u3000\u3000\u4e0a\u5b98\u91d1\u8679\u6545\u610f\u5c06\u90a3\u4e9b\u673a\u4f1a\u5168\u90fd\u9519\u8fc7\u4e86\uff0c\u53ea\u56e0\u4e3a\u4ed6\u59cb\u7ec8\u60f3\u8d4c\u4e00\u8d4c\u3002 \u3000\u3000\u2014\u2014\u8d4c\u4ed6\u662f\u4e0d\u662f\u80fd\u8eb2\u5f97\u8fc7\u5c0f\u674e\u63a2\u82b1\u90a3\u201c\u4ece\u4e0d\u865a\u53d1\u201d\u7684\u51fa\u624b\u4e00\u5200\u3002 \u3000\u3000\u8fd9\u6b21\u4e0a\u5b98\u5c0f\u4ed9\u81ea\u7136\u7edd\u4e0d\u4f1a\u518d\u72af\u540c\u6837\u7684\u9519\u8bef\u3002 \u3000\u3000\u4e01\u7075\u7433\u5634\u91cc\u5728\u6d41\u7740\u82e6\u6c34\u3002 \u3000\u3000\u53f6\u5f00\u4e5f\u8bb8\u6b63\u5728\u8fd9\u6247\u95e8\u91cc\uff0c\u53d7\u7740\u6b7b\u7684\u6298\u78e8\uff0c\u5979\u5374\u53ea\u6709\u5728\u95e8\u5916\u7b49\u7740\u3002 \u3000\u3000\u5c31\u50cf\u5b59\u5c0f\u7ea2\u548c\u963f\u98de\u5728\u7b49\u674e\u5bfb\u6b22\u65f6\u4e00\u6837\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u4eec\u8fd8\u6709\u4e24\u4e2a\u4eba\u3002 \u3000\u3000\u5728\u4e0a\u5b98\u91d1\u8679\u7684\u5bc6\u5ba4\u5916\uff0c\u90a3\u6247\u95e8\u662f\u94c1\u94f8\u7684\uff0c\u65e0\u8bba\u8c01\u4e5f\u649e\u4e0d\u5f00\u3002 \u3000\u3000\u73b0\u5728\u5979\u9762\u524d\u7684\u8fd9\u6247\u95e8\uff0c\u5979\u968f\u65f6\u90fd\u53ef\u4ee5\u95ef\u8fdb\u53bb\uff0c\u5374\u504f\u504f\u4e0d\u6562\u95ef\u8fdb\u53bb\u3002 \u3000\u3000\u5979\u7edd\u4e0d\u80fd\u8ba9\u53f6\u5f00\u5206\u5fc3\u3002 \u3000\u3000\u5979\u5b9e\u5728\u5e0c\u671b\u9762\u524d\u7684\u8fd9\u6247\u95e8\uff0c\u4e5f\u662f\u6247\u649e\u4e0d\u5f00\u7684\u94c1\u95e8\uff0c\u90a3\u6837\u5979\u81f3\u5c11\u4e0d\u5fc5\u518d\u5fcd\u53d7\u8fd9\u79cd\u201c\u63a7\u5236\u81ea \u5df1\u201d\u7684\u75db\u82e6\u3002 \u3000\u3000\u6ca1\u6709\u4eb2\u81ea\u7ecf\u5386\u8fc7\u7684\u4eba\uff0c\u7edd\u5bf9\u60f3\u8c61\u4e0d\u5230\u8fd9\u79cd\u75db\u82e6\u6709\u591a\u4e48\u53ef\u6015\u3002 \u3000\u3000\u5979\u7b80\u76f4\u6068\u4e0d\u80fd\u5c06\u81ea\u5df1\u7684\u4e00\u53cc\u811a\u7528\u9489\u5b50\u9489\u8d77\u6765\u3002 \u3000\u3000\u591c\u5df2\u6df1\u4e86\u3002 \u3000\u3000\u4e01\u7075\u7433\u8fd8\u5728\u7b49\uff0c\u6574\u4e2a\u4eba\u90fd\u5df2\u56e0\u201c\u7b49\u5f85\u201d\u800c\u5d29\u6e83\uff0c\u60b2\u54c0\u7684\u662f\uff0c\u5979\u7adf\u4e0d\u77e5\u9053\u81ea\u5df1\u662f\u5728\u7b49\u4ec0 \u4e48\uff1f \u3000\u3000\u5979\u7b49\u7684\u4e5f\u8bb8\u53ea\u4e0d\u8fc7\u662f\u53f6\u5f00\u7684\u6b7b\u3002 \u3000\u3000\u60f3\u5230\u4e0a\u5b98\u5c0f\u4ed9\u7684\u673a\u667a\u548c\u6b66\u529f\uff0c\u5979\u5b9e\u5728\u4e0d\u77e5\u9053\u53f6\u5f00\u80fd\u6709\u51e0\u5206\u673a\u4f1a\u6d3b\u7740\u8d70\u51fa\u53bb\u3002 \u3000\u3000\u6240\u4ee5\u8fd9\u6247\u95e8\u6253\u5f00\u7684\u90a3\u4e00\u77ac\u95f4\uff0c\u5979\u51e0\u4e4e\u8fde\u5fc3\u8df3\u90fd\u5df2\u505c\u6b62\u3002 \u3000\u3000\u76f4\u5230\u5979\u53c8\u770b\u5230\u53f6\u5f00\u3002 \u3000\u3000\u53f6\u5f00\u770b\u6765\u5f88\u75b2\u5026\uff0c\u4f46\u5374\u662f\u6d3b\u7740\u7684\u3002 \u3000\u3000\u6d3b\u7740\uff0c\u8fd9\u624d\u662f\u6700\u91cd\u8981\u7684\u4e8b\u3002 \u3000\u3000\u4e01\u7075\u7433\u770b\u7740\u4ed6\uff0c\u773c\u6cea\u7ec8\u4e8e\u6162\u6f2b\u5730\u6d41\u4e86\u4e0b\u6765\u2014\u2014\u5f53\u7136\u662f\u6b22\u559c\u7684\u6cea\u3002 \u3000\u3000\u6b22\u559c\u65f6\u4e5f\u548c\u60b2\u54c0\u65f6\u4e00\u6837\uff0c\u9664\u4e86\u6d41\u6cea\u5916\uff0c\u4ec0\u4e48\u8bdd\u90fd\u8bf4\u4e0d\u51fa\uff0c\u4ec0\u4e48\u4e8b\u90fd\u4e0d\u80fd\u505a\uff0c\u751a\u81f3\u8fde\u52a8\u90fd \u4e0d\u80fd\u52a8\u3002 \u3000\u3000\u201c\u4e0a\u5b98\u5c0f\u4ed9\u5462\uff1f\u201d \u3000\u3000\u8fc7\u4e86\u5f88\u4e45\uff0c\u5979\u624d\u80fd\u95ee\u51fa\u8fd9\u53e5\u8bdd\u3002 \u3000\u3000\u56de\u7b54\u53ea\u6709\u4e09\u4e2a\u5b57\uff1a\u201c\u5979\u8d25\u4e86\u3002\u201d \u3000\u3000\u5979\u8d25\u4e86\u3002 \u3000\u3000\u8fd9\u662f\u591a\u4e48\u7b80\u5355\u7684\u4e09\u4e2a\u5b57\u3002 \u3000\u3000\u51b3\u5b9a\u80dc\u8d1f\uff0c\u4e5f\u53ea\u4e0d\u8fc7\u662f\u4e00\u5239\u90a3\u95ee\u7684\u4e8b\u3002 \u3000\u3000\u4f46\u662f\u53c8\u6709\u8c01\u80fd\u60f3\u8c61\uff0c\u8fd9\u4e00\u5239\u90a3\u95ee\u7684\u7d27\u5f20\u548c\u523a\u6fc0\u3002 \u3000\u3000\u8fd9\u4e00\u5239\u90a3\u5bf9\u6c5f\u6e56\u7684\u5f71\u54cd\uff0c\u53c8\u662f\u4f55\u7b49\u6df1\u5de8\u3002 \u3000\u3000\u4e00\u5239\u90a3\uff01 \u3000\u3000\u4e00\u5200\uff01 \u3000\u3000\u90a3\u4e00\u95ea\u7684\u5200\u5149\uff0c\u53c8\u662f\u4f55\u7b49\u60ca\u5fc3\uff0c\u4f55\u7b49\u58ee\u4e3d\uff01 \u3000\u3000\u4f60\u751a\u81f3\u4e0d\u5fc5\u4eb2\u773c\u53bb\u770b\uff0c\u53ea\u8981\u53bb\u60f3\u4e00\u60f3\uff0c\u4f60\u7684\u547c\u5438\u90fd\u4e0d\u7981\u8981\u505c\u987f\u3002 \u3000\u3000\u53ef\u662f\u4e86\u7075\u7433\u5e76\u6ca1\u6709\u60f3\u3002 \u3000\u3000\u6240\u6709\u7684\u4e00\u5207\u4e8b\uff0c\u5bf9\u5979\u90fd\u4e0d\u91cd\u8981\uff0c\u91cd\u8981\u7684\u662f\uff0c\u53f6\u5f00\u8fd8\u6d3b\u7740\u3002 \u3000\u3000\u53ea\u8981\u53f6\u5f00\u8fd8\u6d3b\u7740\uff0c\u5979\u5c31\u5df2\u5fc3\u6ee1\u610f\u8db3\u4e86\u3002 \u3000\u3000\u95e8\u91cc\u8fd8\u6709\u54ed\u6ce3\u58f0\uff0c\u6b7b\u4eba\u662f\u4e0d\u4f1a\u54ed\u7684\u3002 \u3000\u3000\u96be\u9053\u4e0a\u5b98\u5c0f\u4ed9\u8fd8\u6ca1\u6709\u6b7b\uff1f \u3000\u3000\u53f6\u5f00\u7684\u5200\uff0c\u672c\u4e0d\u662f\u6740\u4eba\u7684\u5200\u3002 \u3000\u3000\u4ed6\u8ba9\u5979\u6d3b\u4e0b\u53bb\uff0c\u662f\u4e0d\u662f\u56e0\u4e3a\u4ed6\u77e5\u9053\u5979\u4ee5\u540e\u5df2\u4e0d\u4f1a\u518d\u662f\u548c\u4ee5\u524d\u540c\u6837\u7684\u4e00\u4e2a\u4e0a\u5b98\u5c0f\u4ed9\u4e86\uff01 \u3000\u3000\u2014\u2014\u5bbd\u6055\u8fdc\u6bd4\u62a5\u590d\u66f4\u4f1f\u5927\u3002 \u3000\u3000\u4ee5\u7259\u8fd8\u7259\uff0c\u4ee5\u8840\u8fd8\u8840\uff0c\u8fd9\u53e5\u8bdd\u5bf9\u53f6\u5f00\u662f\u4e0d\u9002\u7528\u7684\u3002 \u3000\u3000\u4ed6\u7528\u7684\u662f\u5c0f\u674e\u98de\u5200\u3002 \u3000\u3000\u8fd9\u79cd\u5200\u7684\u529b\u91cf\u662f\u7231\uff0c\u4e0d\u662f\u6068\u3002 \u3000\u3000\u4e0a\u5b98\u5c0f\u4ed9\u662f\u4e0d\u662f\u4e5f\u80fd\u61c2\u5f97\u8fd9\u9053\u7406\uff1f \u3000\u3000\u4e01\u7075\u7433\u4e5f\u6ca1\u6709\u518d\u95ee\uff0c\u56e0\u4e3a\u73b0\u5728\u5979\u5fc3\u91cc\u53ea\u6709\u7231\uff0c\u6ca1\u6709\u6068\uff0c\u5979\u6b63\u5728\u770b\u7740\u53f6\u5f00\u7684\u773c\u775b\u2026\u2026 \u3000\u3000\u751f\u547d\u5982\u6b64\u7f8e\u597d\uff0c\u7231\u60c5\u5982\u6b64\u5947\u7092\uff0c\u4e00\u4e2a\u4eba\u82e5\u8fd8\u4e0d\u80fd\u5fd8\u8bb0\u4ec7\u6068\uff0c\u5c82\u975e\u611a\u8822\u5f97\u5f88\uff1f"}),showOnHomepage:!0}]}}]);